INSERT INTO "badkhashani" VALUES(1,1,'1:1','بنام الله بخشندۀ مهربان.');
INSERT INTO "badkhashani" VALUES(1,2,'1:2','تمامی سپاس و ستایش‌ها مخصوص ذاتی است که پروردگار تمام جهانیان است.');
INSERT INTO "badkhashani" VALUES(1,3,'1:3','بسیار بخشنده (و) بسیار مهربان است.');
INSERT INTO "badkhashani" VALUES(1,4,'1:4','مالک (و اختیار دار) روز جزا (قیامت) است.');
INSERT INTO "badkhashani" VALUES(1,5,'1:5','(پس ای الله! تنها) تو را عبادت می‌کنیم و (در کارهای خود) تنها از تو کمک می‌خواهیم.');
INSERT INTO "badkhashani" VALUES(1,6,'1:6','ما را به راهِ راست هدایت کن (و ثابت قدم دار).');
INSERT INTO "badkhashani" VALUES(1,7,'1:7','راه آنانِ که بر آنها نعمت‌ها بخشیده‌ای، نه (راه) آنانِ که بر آنها خشم گرفته‌ای، و نه راه گمراهان.');
INSERT INTO "badkhashani" VALUES(2,1,'2:1','الف. لام. میم. (مفهوم این حروف به الله متعال معلوم است).');
INSERT INTO "badkhashani" VALUES(2,2,'2:2','این کتابی است که هیچگونه شکی در (حقانیت) آن نیست، راهنمایی برای پرهیزگاران است.');
INSERT INTO "badkhashani" VALUES(2,3,'2:3','آنانکه به غیب ایمان می‌آورند و نماز را برپا می‌کنند و از آنچه به آنها روزی داده‌ایم (در راه الله) انفاق می‌کنند.');
INSERT INTO "badkhashani" VALUES(2,4,'2:4','و آنانکه به آنچه به‌سوی تو فرستاده شده است و به آنچه پیش از تو فرستاده شده است ایمان می‌آورند و آنان‌اند که به روز قیامت يقين دارند.');
INSERT INTO "badkhashani" VALUES(2,5,'2:5','همين کسان (که صفات‌شان ذکر شد) بر هدایتی از جانب پروردگارشان قرار دارند، و تنها ایشان رستگار و نجات یافته گان‌اند.');
INSERT INTO "badkhashani" VALUES(2,6,'2:6','البته کسانی که کفر ورزیدند برایشان برابر است، چه آنها را بترسانی و چه آنها را نترسانی، ایمان نمی‌آورند.');
INSERT INTO "badkhashani" VALUES(2,7,'2:7','(پس به سبب کفر شان) الله بر دلها و گوش‌هایشان مهر نهاده است، و بر چشم‌های آنها پردۀ است، و برای آنها عذاب بس بزرگ است.');
INSERT INTO "badkhashani" VALUES(2,8,'2:8','و از مردم کسی هست که می‌گوید به الله و به روز آخرت ایمان آورده‌ایم، و آنها (در واقع) هرگز مؤمن نیستند.');
INSERT INTO "badkhashani" VALUES(2,9,'2:9','(بلکه به گمان خود) الله و مؤمنان را فریب می‌دهند و (در حقیقت) فریب نمی‌دهند مگر خودشان را و (لیکن) درک نمی‌کنند.');
INSERT INTO "badkhashani" VALUES(2,10,'2:10','(چون) در دل‌هایشان بیماری (نفاق) است، پس (به سبب گناهانشان) الله شک آنها را زیاد کرد، و به سزای دروغی که می‌گفتند عذاب دردناک در پیش دارند.');
INSERT INTO "badkhashani" VALUES(2,11,'2:11','و چون به آنها گفته شود در زمین فساد مکنید، گویند: ما بجز اصلاح دیگر کاری نمی‌کنیم.');
INSERT INTO "badkhashani" VALUES(2,12,'2:12','آگاه باشید که آنها يقينا فسادگرانند، و ليكن (فساد خود را) درک نمی‌کنند.');
INSERT INTO "badkhashani" VALUES(2,13,'2:13','و چون به آنها گفته شود (از دل) ایمان آورید طوریکه مردم دیگر (صحابه) ایمان آورده‌اند، گویند: آیا ما هم مانند نادانان ایمان بیاوریم؟ آگاه باشید که خود آنها نادان‌اند، و لیکن (نادانی خود را) نمی‌دانند.');
INSERT INTO "badkhashani" VALUES(2,14,'2:14','و چون با مؤمنان روبرو شوند می‌گویند: ما (هم) ایمان آورده‌ایم، و چون با شیطان‌های خود در خلوت روند، گویند: البته ما باشماییم، جز این نیست که ما آنها (مؤمنان) را مورد استهزاء قرار می‌دهیم.');
INSERT INTO "badkhashani" VALUES(2,15,'2:15','الله آنان را مسخره می‌نماید و آنان را در سرکشی‌شان می‌گذارد تا (در فسق و فجور) حیران و سرگردان بمانند.');
INSERT INTO "badkhashani" VALUES(2,16,'2:16','این گروه کسانی‌اند که گمراهی را به عوض هدایت خریدند، پس تجارت آنها فائده نکرد (چون در این تجارت خود) راهیاب نبودند.');
INSERT INTO "badkhashani" VALUES(2,17,'2:17','حالت منافقان مانند حالت کسی است که آتش افروخت، پس چون آتش اطراف آن را روشن کرد، الله آن روشنی را از آنها بُرد، و آنها را در تاریکی‌های رها کرد که چیزی را دیده نمی‌توانند.');
INSERT INTO "badkhashani" VALUES(2,18,'2:18','آنها )در بارۀ دین اسلام( کرند (از شنیدن حق)، و گنگند (از نطق حق) و کورند (از دیدن نور هدایت)؛ پس (آنها از نفاق خود به راه راست) باز نمی‌گردند.');
INSERT INTO "badkhashani" VALUES(2,19,'2:19','یا حالت آنها مانند حالت کسانی است که به باران تندی گرفتار شده باشند که از آسمان فرو می‌ریزد و در آن، تاریکی‌ها و رعد و برق باشد و از ترس صدای صاعقه‌ها و مرگ انگشتان خود را در گوش‌هایشان می‌گذارند ولی الله بر کافران احاطه دارد.');
INSERT INTO "badkhashani" VALUES(2,20,'2:20','نزدیک است که برق چشم‌هایشان را برباید، و هر گاه بر آنها روشنی بخشد پیش روند، و چون راه‌شان را تاریک کند ایستاد شوند، و اگر الله می‌خواست چشم‌ها و گوش‌های آنها را می‌برد، چون الله به هر چیز قادر و تواناست.');
INSERT INTO "badkhashani" VALUES(2,21,'2:21','ای مردم! پروردگارتان را عبادت کنید که شما و کسانی را که پیش از شما بودند آفریده است، تا پرهیزگار شوید.');
INSERT INTO "badkhashani" VALUES(2,22,'2:22','آن ذاتی که زمین را برایتان فرشی و آسمان را سقفی قرار داد و از آسمان آب فرستاد و با وسیله آن انواع میوه‌ها را بیرون آورد تا برای تان روزی باشد، پس برای الله شریکانی مقرر نکنید در حالیکه شما میدانید (که الله شریک ندارد).');
INSERT INTO "badkhashani" VALUES(2,23,'2:23','و اگر شما در آنچه بر بندۀ خود نازل کرده‌ایم شک دارید، پس یک سوره مانند آن را بیارید، و مددگاران خود -غیر از الله- را طلب کنید، اگر راست می‌گویید.');
INSERT INTO "badkhashani" VALUES(2,24,'2:24','پس اگر چنین نکردید و هرگز نتوانید کرد، پس از آتشی که هیزم آن مردم و سنگ‌ها هستند بترسید که برای کافران آماده و مهیا کرده شده است.');
INSERT INTO "badkhashani" VALUES(2,25,'2:25','و مژده ده آنانی را که ایمان آوردند و اعمال نیک انجام دادند که برای آنها باغ‌هایی است که از زیر آن نهرها روان است، هرگاه به آنها میوه داده شود، گویند: این همان است که پیش‌تر به ما داده شد، و همانند آن برای آنها آورده شود، و برای آنها در جنت زنان پاکیزه است و آنها در آن برای همیشه‌اند.');
INSERT INTO "badkhashani" VALUES(2,26,'2:26','البته الله حیا نمی‌کند از اینکه هر مثلی را بزند، پشۀ باشد یا کوچک‌تر از آن، پس کسانی که ایمان آورده‌اند می‌دانند که آن حق است و از جانب پروردگارشان است. و اما کسانی که کفر ورزیده‌اند می‌گویند: الله از این مثال چه قصدی داشته است؟، (الله) با آن بسیاری را گمراه کرده و با آن بسیاری را هدایت می‌کند ولی جز فاسقان را با آن گمراه نمی‌کند.');
INSERT INTO "badkhashani" VALUES(2,27,'2:27','همان کسانی که عهد الله را پس از بستن آن می‌شکنند و آنچه را الله دستور به پیوستن آن داده است قطع کرده و در زمین فساد می‌کنند، ایشان زیان کاران‌اند.');
INSERT INTO "badkhashani" VALUES(2,28,'2:28','چگونه انکار می‌کنید (وجود و وحدانیت الله را؟) درحالیکه شما مرده و معدوم بودید پس زنده (و ایجاد) کرد شما را، باز شما را می‌میراند، و باز شما را زنده می‌کند، و باز بسوی او باز گردانیده می‌شوید.');
INSERT INTO "badkhashani" VALUES(2,29,'2:29','او ذاتی است که آنچه در زمین وجود دارد برایتان آفرید، باز به‌سوی آسمان قصد کرد و آن را هفت آسمان ساخت و او به هر چیزی داناست.');
INSERT INTO "badkhashani" VALUES(2,30,'2:30','و یادآور شو وقتی که پروردگارت به فرشتگان فرمود: من در زمین جانشینی قرار می‌دهم، (فرشتگان) گفتند: آیا در آن کسی را می‌گماری که در آن فساد می‌کند و خونها را می‌ریزد، در حالی که ما به حمد تو تسبیح می‌گوییم و ستایش و پاکی تو را بیان میداریم؟! گفت: من چیزی را می‌دانم که شما نمی‌دانید.');
INSERT INTO "badkhashani" VALUES(2,31,'2:31','و الله به آدم همه‌ای نامها را آموخت، باز آن را بر فرشتگان عرضه کرد و گفت: نام‌های این اشیا را به من خبر دهید اگر صادق هستید.');
INSERT INTO "badkhashani" VALUES(2,32,'2:32','فرشته‌ها گفتند: منزهی تو، ما نمی‌دانیم جز آنچه به ما آموخته ای، همانا تو دانا و حکیم هستی.');
INSERT INTO "badkhashani" VALUES(2,33,'2:33','الله فرمود: ای آدم! آنها را از نام‌های این چیزها خبر ده. و چون او نام‌های اشیاء را به آنان خبر داد، الله فرمود: آیا به شما نگفتم که من غیب آسمان‌ها و زمین را می‌دانم و آنچه را آشکار می‌سازید و آنچه را پنهان می‌کنید می‌دانم؟');
INSERT INTO "badkhashani" VALUES(2,34,'2:34','و یاد آور شو وقتی را که به فرشته‌ها گفتیم برای (تعظيم) آدم سجده کنید، پس فرشته‌ها همه سجده کردند بجز ابلیس که انکار کرد و تکبر ورزید، و از جملۀ کافران (منکران حكم الهی) شد.');
INSERT INTO "badkhashani" VALUES(2,35,'2:35','و گفتیم: ای آدم، تو با همسرت در بهشت سکونت گزین، واز نعمت‌های فراوان آن هر طور که می‌خواهید بخورید، اما به این درخت نزدیک نشوید، که از ظالمان خواهید شد.');
INSERT INTO "badkhashani" VALUES(2,36,'2:36','پس شیطان هر دو را از جنت لغزانید، پس بیرون کرد هر دو را از آن نعمتی که در آن بودند، (بعد از این﴾ برای آنها گفتیم که از جنت پایان شوید، (چون) شما دشمن یکدیگر اید، (البته) برای شما در زمین قرارگاه و جای بهره مندی است تا وقت مقرر.');
INSERT INTO "badkhashani" VALUES(2,37,'2:37','باز آدم از پروردگارش کلماتی را دریافت نمود، و (الله) توبۀ او را پذیرفت، چون الله بسیار توبه پذیر مهربان است.');
INSERT INTO "badkhashani" VALUES(2,38,'2:38','(تاکیداً) گفتیم همۀ شما از جنت فرود آیید، پس اگر هدایتی از جانب من به شما آمد، پس کسانی که از راهنمایی من پیروی کنند نه ترسی بر آنان خواهد بود و نه غمگین می‌شوند.');
INSERT INTO "badkhashani" VALUES(2,39,'2:39','و کسانی که کفر ورزیدند و آیات ما را تکذیب کردند همین گروه دوزخی‌اند و همیشه در دوزخ می‌باشند.');
INSERT INTO "badkhashani" VALUES(2,40,'2:40','ای فرزندان یعقوب! به یاد آورید نعمت من را که بر شما ارزانی داشتم، و به پیمان من وفا کنید تا به پیمان شما وفا کنم، و تنها از من بترسید.');
INSERT INTO "badkhashani" VALUES(2,41,'2:41','و به آنچه که من نازل کرده ام و تصدیق کننده چیزی است که با شما است، ایمان بیاورید، و نخستین کافر به آن نباشید، و آیات مرا به بهای حقیر نفروشید، و تنها از من بترسید.');
INSERT INTO "badkhashani" VALUES(2,42,'2:42','و حق را با باطل خلط نکنید و حق را کتمان نکنید در حالی که شما می‌دانید.');
INSERT INTO "badkhashani" VALUES(2,43,'2:43','و نماز را بر پا کنید و زکات را بدهید و با نمازگزاران نماز بخوانید.');
INSERT INTO "badkhashani" VALUES(2,44,'2:44','آیا مردم را به نیکی امر می‌کنید و خود را فراموش می‌کنید حال آنکه شما کتاب را می‌خوانید؟! آیا نمی‌اندیشید؟.');
INSERT INTO "badkhashani" VALUES(2,45,'2:45','و از صبر و نماز یاری بجویید و به راستی که آن دشوار است مگر بر فروتنان.');
INSERT INTO "badkhashani" VALUES(2,46,'2:46','آنانکه يقين دارند که پروردگارشان را ملاقات خواهند کرد و یقین دارند به‌سوی او باز می‌گردند.');
INSERT INTO "badkhashani" VALUES(2,47,'2:47','ای فرزندان يعقوب! یاد آور شوید نعمت مرا که بر شما ارزانی داشتم و نیز اینکه شما را بر جهانیان (هم عصرتان) فضیلت دادم.');
INSERT INTO "badkhashani" VALUES(2,48,'2:48','و از روزی بترسید که کسی نمی‌تواند برای کسی کاری را انجام دهد، و از کسی شفاعت پذیرفته نمی‌شود، و از کسی بدل و عوض گرفته نشود و نه یاری می‌شوند.');
INSERT INTO "badkhashani" VALUES(2,49,'2:49','و یادآور شوید هنگامی را که شما را از آل فرعون نجات دادیم که می‌چشاندند به شما عذابی سخت را، پسرانتان را می‌کشتند و زنانتان را زنده می‌گذاشتند و در این آزمایش بزرگ از جانب پروردگارتان بود.');
INSERT INTO "badkhashani" VALUES(2,50,'2:50','و یادآور شوید وقتی که دریا را برایتان شگافتیم و آل فرعون را غرق کردیم و شما نظر می‌کردید.');
INSERT INTO "badkhashani" VALUES(2,51,'2:51','و یادآور شوید این را که چون با موسی چهل شب وعده گذاشتیم باز بعد از رفتن او گوساله را معبود گرفتید در حالیکه شما ظالم بودید.');
INSERT INTO "badkhashani" VALUES(2,52,'2:52','باز شما را عفو کردیم تا شکر گزار باشید.');
INSERT INTO "badkhashani" VALUES(2,53,'2:53','و یاد آور شوید وقتی که موسی را کتاب آسمانی و فرقان دادیم تا هدایت شوید.');
INSERT INTO "badkhashani" VALUES(2,54,'2:54','و هنگامی که موسی به قومش گفت: ای قوم من! شما با پرستش گوساله بر خود ظلم کردید، پس به درگاه خالق تان توبه کنید، (باینطور که) خودتان را بکشید، این نزد خالق تان برایتان بهتر است، همانا او توبه پذیر مهربان است.');
INSERT INTO "badkhashani" VALUES(2,55,'2:55','و یادآور شوید هنگامی که شما گفتید: ای موسی! تا الله را آشکارا نبینیم هرگز به تو ایمان نخواهیم آورد، پس صاعقه شما را فرا گرفت در حالیکه شما نظر می‌کردید.');
INSERT INTO "badkhashani" VALUES(2,56,'2:56','باز شما را بعد از مردن تان زنده کردیم تا شكر الله را بجا آورید.');
INSERT INTO "badkhashani" VALUES(2,57,'2:57','و ابر را بر شما سایه بان کردیم و برایتان «من» و «سلوا» نازل کردیم، از نعمت‌های پاکیزۀ که به شما روزی داده‌ایم، بخورید، و بر ما ظلم نکردند بلکه آنها بر خود ظلم می‌کردند.');
INSERT INTO "badkhashani" VALUES(2,58,'2:58','و یادآور شوید چون گفتیم: وارد این قریه شوید و از هر کجای آن خواستید به خوشی و فراوانی بخورید و از دروازه آن سجده کنان وارد شوید و بگویید: گناهان ما را بیامرز، تا گناهان شما را بیامرزم و به نیکوکاران پاداش بیشتر خواهیم داد.');
INSERT INTO "badkhashani" VALUES(2,59,'2:59','پس تبدیل نمودند آن ظالمان (فرمودۀ الله را) به سخن دیگر غیر از آنچه به آنها گفته شده بود، پس بر آن ظالمان به سبب اینکه کارهای فاسقانه می‌کردند عذابی از آسمان نازل کردیم.');
INSERT INTO "badkhashani" VALUES(2,60,'2:60','و چون موسی برای قومش آب خواست، پس ما گفتیم: با عصایت به آن سنگ بزن، پس از آن دوازده چشمه روان شد که هر قبیله جای آب خود را می‌دانست. بخورید و بنوشید از روزی الله، و همچون تباهکاران در زمین فساد و تباهی نکنید.');
INSERT INTO "badkhashani" VALUES(2,61,'2:61','و چون گفتید: هرگز ما بر یک طعام صبر کرده نمی‌توانیم، پس از پروردگارت درخواست کن تا از آنچه زمین می‌رویاند از سبزی و خیار و سیر و عدس و پیاز برای ما بیرون بیاورد. گفت: آیا چیزی را که پست‌تر است جانشین چیزی می‌کنید که بهتر است؟! به قریه وارد شوید که آنچه می‌خواهید در آنجا برایتان است. و مهر خواری و فقر بر آنها زده شد و سزاوار خشم الله گردیدند. این به آن سبب بود که آنها به آیات الله كفر می‌ورزیدند، و پیغمبران را به ناحق می‌کشتند، این به آن خاطر بود که آنها نافرمان و متجاوز بودند.');
INSERT INTO "badkhashani" VALUES(2,62,'2:62','البته آنانکه ایمان آورده‌اند «مسلمانان» و کسانی که یهودی و صابئی و نصرانی بوده‌اند هر کس که به الله و روز قیامت ایمان آورد و عمل نیک انجام دهد اجرشان نزد پروردگارشان است، نه ترسی بر آنها است و نه غمگین می‌شوند.');
INSERT INTO "badkhashani" VALUES(2,63,'2:63','و چون از شما پیمان محکم گرفتیم و بالای شما کوه طور را برداشتیم (و گفتیم) بگیرید به قوت و کوشش آن کتابی را که به شما داده‌ایم و یاد کنید آنچه در آن است تا پرهیزگار شوید.');
INSERT INTO "badkhashani" VALUES(2,64,'2:64','باز شما بعد از آن پیمان رویگردان شدید، پس اگر فضل الله و مهربانی او بر شما نبود، البته از زیانکاران می‌شديد.');
INSERT INTO "badkhashani" VALUES(2,65,'2:65','و البته کسانی را از شما که در روز شنبه تجاوز کردند خوب شناختید، پس ما به آنها گفتیم: بوزینه‌های خوار و ذلیل باشید.');
INSERT INTO "badkhashani" VALUES(2,66,'2:66','پس آن عقوبت را عبرتی برای حاضران و آیندگان و پندی برای پرهیزگاران قرار دادیم.');
INSERT INTO "badkhashani" VALUES(2,67,'2:67','و یاد آور شوید هنگامی که موسی به قومش گفت: البته الله به شما دستور می‌دهد تا گاوی را ذبح کنید، گفتند: آیا ما را مورد استهزاء قرار می‌دهی؟ موسی گفت: پناه می‌برم به الله از اینکه از جاهلان باشم.');
INSERT INTO "badkhashani" VALUES(2,68,'2:68','گفتند: از پروردگارت درخواست کن که برای ما بیان کند آن گاو چگونه است؟ گفت: او می‌گوید آن گاوی است نه پیر و نه جوان بلکه میان آن دو است. پس آنچه به شما فرمان داده می‌شود، بجا آورید.');
INSERT INTO "badkhashani" VALUES(2,69,'2:69','(باز) گفتند: از پروردگارت درخواست کن که برای ما بیان کند رنگ آن چگونه است؟ گفت: او می‌فرماید: آن گاوی است زرد رنگ که بینندگان را شاد می‌کند.');
INSERT INTO "badkhashani" VALUES(2,70,'2:70','(بار دیگر به موسی) گفتند: از پروردگارت برای ما بخواه تا برای ما بیان کند که آن چگونه است، چون گاو بر ما مشتبه شده است و اگر الله بخواهد ما حتما راهیاب خواهیم شد.');
INSERT INTO "badkhashani" VALUES(2,71,'2:71','موسی گفت: الله می‌فرماید: آن گاوی است که نه رام است که زمین را قلبه کند، و نه آبیاری کند کشتزار را، بی‌عیب و یک رنگ است. گفتند: اکنون سخن حق را بیان کردی، پس آن را ذبح کردند و نزدیک بود این کار را نکنند.');
INSERT INTO "badkhashani" VALUES(2,72,'2:72','و (سبب امر به ذبح گاو این بود که) وقتی فردی را کشتید و در (بارۀ قاتل) آن اختلاف کردید و الله ظاهر کنندۀ چیزی بود که آنرا پنهان می‌کردید.');
INSERT INTO "badkhashani" VALUES(2,73,'2:73','پس حکم کردیم که بزنید جسم مقتول را به یک حصۀ از گوشت گاو، این چنین الله مرده‌ها را زنده می‌کند و به شما نشانه‌های قدرت خود را نشان می‌دهد تا بفهمید.');
INSERT INTO "badkhashani" VALUES(2,74,'2:74','پس از آن، دل‌های شما سخت شد، مانند سنگ یا سخت‌تر (از سنگ). و بعضی از سنگ‌هاست که از آن نهرها جاری می‌شود، و بعضی از آنها است که می‌شگافد و آب از آن بیرون می‌آید، و بعضی از آنها است که از ترس الله زیر می‌افتد، و الله از آنچه می‌کنید غافل نیست.');
INSERT INTO "badkhashani" VALUES(2,75,'2:75','پس آیا شما (مسلمانان) طمع دارید که یهودیان به شما ایمان آورند در حالیکه گروهی از ایشان کلام الله را می‌شنوند. باز آن را پس از فهمیدنش، تحریف می‌کنند با آنکه حق را می‌دانستند؟.');
INSERT INTO "badkhashani" VALUES(2,76,'2:76','و چون با مؤمنان روبرو شوند، گویند: ایمان آورده‌ایم و چون با همدیگر به خلوت روند، گویند: آیا آنچه را الله برایتان (در باره محمد در تورات) آشکار کرده است با آنان در میان می‌گذارید تا در پیشگاه الله با آن بر شما حجت آورند، آیا نمی‌فهمید؟');
INSERT INTO "badkhashani" VALUES(2,77,'2:77','آیا نمی‌دانند که الله آنچه را پنهان می‌کنند و آنچه را آشکار می‌کنند می‌داند؟.');
INSERT INTO "badkhashani" VALUES(2,78,'2:78','و بعضی از آنان ناخوان هستند که کتاب الله را جز آرزوهای خود نمی‌دانند و تنها از گمان کار می‌گیرند.');
INSERT INTO "badkhashani" VALUES(2,79,'2:79','پس وای بر کسانی که کتاب را با دست‌های خود می‌نویسند باز می‌گویند: این از جانب الله است، تا آن را به بهای اندکی بفروشند، پس وای بر آنها از آنچه دست‌هایشان نوشت و وای بر آنان از آنچه (از این راه) بدست می‌آورند.');
INSERT INTO "badkhashani" VALUES(2,80,'2:80','و گفتند: آتش دوزخ جز چند روزی به ما نمی‌رسد. بگو: آیا از الله عهدی گرفته‌اید که الله هرگز در وعده خود خلاف نخواهد کرد یا اینکه بر الله چیزهایی را نسبت می‌دهید که به آن علم ندارید؟.');
INSERT INTO "badkhashani" VALUES(2,81,'2:81','بلی! هر که کار بد کند و او را گناهانش احاطه کند، پس همین گروه یاران دوزخ‌اند که در آن تا ابد می‌باشند.');
INSERT INTO "badkhashani" VALUES(2,82,'2:82','و آنانکه ایمان آوردند و کارهای نیک انجام دادند، همین گروه اهل جنت‌اند که در آن همیشه می‌باشند.');
INSERT INTO "badkhashani" VALUES(2,83,'2:83','و یاد آور شو این را که از اولاد يعقوب عهد محکم گرفتیم که به جز الله چیزی را عبادت نکنید، و با پدر و مادر و با خویشاوندان و با یتیمان و ناتوانان نیکی کنید، و با مردم سخن نیک بگویید، و نماز را برپا کنید، و زکات را بدهید، اما شما (با آنکه عهد بستید) جز عده کمی از شما همه پشت گردانیدید.');
INSERT INTO "badkhashani" VALUES(2,84,'2:84','و یاد آور شوید هنگامی را که از شما پیمان گرفتیم که خون یکدیگر را نریزید و یکدیگر را از سر زمینتان بیرون نکنید باز شما (به این پیمان) اقرار کردید، در حالیکه خود بر آن گواهید.');
INSERT INTO "badkhashani" VALUES(2,85,'2:85','باز این شما هستید که یکدیگر را می‌کشید و گروهی از خودتان را از سرزمین و خانه‌هایشان بیرون می‌کنید و علیه آنها به گناه و تجاوز یکدیگر را مدد می‌کنید. و اگر به اسارت پیش شما بیایند فدیه می‌دهید و آنان را آزاد می‌کنید حال آنکه بیرون کردن آنها بر شما حرام است. آیا به بخشی از کتاب (تورات) ایمان می‌آورید و به بخشی دیگر آن کفر می‌ورزید؟ برای کسی از شما که در دنیا چنین کند جز خواری و رسوایی نیست و روز قیامت به سخت‌ترین شکنجه و عذاب برگردانیده می‌شوند، و الله از آنچه انجام می‌دهید غافل نیست.');
INSERT INTO "badkhashani" VALUES(2,86,'2:86','این گروه همان کسانی هستند که زندگی دنیا را به عوض آخرت خریدند، پس عذاب آنان تخفیف نمی‌یابد و مدد نخواهند شد.');
INSERT INTO "badkhashani" VALUES(2,87,'2:87','و البته به موسی کتاب دادیم و در پی او پیغمبرانی را فرستادیم، و به عیسی پسر مریم معجزات و دلایل روشنی دادیم و او را به وسیله روح القدس (جبرئیل) تایید کردیم، آیا هرگاه پیغمبری پیش شما (احکامی) به خلاف میل و آرزویتان آورد سرکشی کردید، پس گروهی (از پیغمبران) را تکذیب کردید و گروهی را کشتید؟.');
INSERT INTO "badkhashani" VALUES(2,88,'2:88','و گفتند که دل‌های ما دارای غلاف و پرده است، (که حق را نمی‌فهمیم؛ حال آنکه در دل‌هایشان پرده نیست) بلکه الله آنها را به سبب كفر‌شان از رحمت خود دور کرده است، پس بسیار کم از آنها ایمان می‌آرند.');
INSERT INTO "badkhashani" VALUES(2,89,'2:89','و چون کتابی از جانب الله پیش آنها آمد که تصدیق کنندۀ کتابی بود که با خود داشتند در حالیکه پیش از آن بر کافران طلب فتح می‌کردند، پس وقتی پیش آنها آمد، آنچه را که از قبل شناخته بودند انکار کردند، پس لعنت الله بر کافران باد.');
INSERT INTO "badkhashani" VALUES(2,90,'2:90','بد است آنچه که خود را به آن فروختند و آنچه را الله نازل کرده بود به سبب حسد انکار کردند، (و این به آن خاطر بود که می‌گفتند:) چرا الله فضل خود را بر هرکس که بخواهد نازل می‌کند؟ پس به خشمی بر خشم دیگر گرفتار شدند و برای کافران عذاب رسواکننده‌ای است.');
INSERT INTO "badkhashani" VALUES(2,91,'2:91','و چون به آنها گفته شود به آنچه الله نازل کرده است ایمان بیاورید، گویند: به آنچه بر ما نازل شده است ایمان می‌آوریم، و به غیر آن کفر می‌ورزند، حال آنکه آن (چه الله بر پیغمبر نازل کرده است) حق، و تصدیق کننده چیزی است که با آنهاست. بگو: پس چرا پیش از این پیغمبران الله را می‌کشتید، اگر مؤمن هستید؟.');
INSERT INTO "badkhashani" VALUES(2,92,'2:92','و البته موسی با معجزات روشن پیش شما آمد، باز پس از او گوساله را پرستیدید، در حالی که ظالم بودید.');
INSERT INTO "badkhashani" VALUES(2,93,'2:93','و یاد آور شوید وقتی که از شما پیمان محکم گرفتیم و کوه طور را بر سرتان بلند کردیم. آنچه را به شما داده‌ایم محکم بگیرید و بشنوید. گفتند: شنیدیم (مگر) نافرمانی کردیم و به سبب كفر‌شان محبت گوساله در دل‌هایشان جای گرفته بود. بگو: ایمانتان شما را به بد چیزی امر می‌کند اگر مؤمن هستید.');
INSERT INTO "badkhashani" VALUES(2,94,'2:94','بگو: (ای یهودیان!) اگر دار آخرت (جنت) در نزد الله خالص برای شما باشد بدون (شراکت) اهل دیگر ادیان، پس مرگ را آرزو کنید اگر در دعوای خود صادق هستید (تا که به جنت زود برسید).');
INSERT INTO "badkhashani" VALUES(2,95,'2:95','و هرگز مرگ را آرزو نکنند به سبب گناهانی که بدست خود تقدیم کرده‌اند، و الله به حال ظالمان داناست.');
INSERT INTO "badkhashani" VALUES(2,96,'2:96','و آنها (یهود) را حریص‌ترین مردم بر زندگی دنیا خواهی یافت، حتی حریص‌تر از مشرکین، هر یک از آنان دوست دارد که هزار سال عمر کند، با اینکه اگر چنین عمری به وی داده شود او را از عذاب نجات نمی‌دهد و الله به آنچه می‌کنند بیناست.');
INSERT INTO "badkhashani" VALUES(2,97,'2:97','بگو: هرکس که دشمن جبرئیل باشد، همانا او قرآن را به اذن الله بر قلب تو نازل کرده است، در حالیکه تأیید کننده کتاب‌هایی است که در پیش آنان است و برای مؤمنان هدایت و بشارت است.');
INSERT INTO "badkhashani" VALUES(2,98,'2:98','هرکس که دشمن الله و فرشته‌ها و پیغمبران و جبرئیل و میکاییل باشد پس البته الله دشمن کافران است.');
INSERT INTO "badkhashani" VALUES(2,99,'2:99','و البته آیت‌های روشن و بیانگر حق را بر تو نازل کردیم، و از آن آیات انکار نمی‌کند مگر فاسقان (نافرمانان).');
INSERT INTO "badkhashani" VALUES(2,100,'2:100','چرا هرگاه که عهد محکم بستند (باز) گروهی از آنها آن را به دور انداخته و شکستند؟ بلکه بیشتر ایشان ایمان نمی‌آرند.');
INSERT INTO "badkhashani" VALUES(2,101,'2:101','و چون پیغمبری از جانب الله نزد آنها آمد که تصديق کننده چیزی بود که با خود داشتند، گروهی از اهل کتاب، کتاب الله (قرآن) را پشت سرشان انداختند گویا (از آن هیچ) نمیدانند.');
INSERT INTO "badkhashani" VALUES(2,102,'2:102','و پیروی کردند از آنچه شیاطین در روزگار فرمانروایی سلیمان می‌خواندند، و سلیمان کفر نورزید، (و سحر و جادو نیاموخت) بلکه شیاطین کفر ورزیدند که به مردم جادو می‌آموختند و (نیز از) آنچه بر آن دو فرشته، هاروت و ماروت در بابل نازل شده بود پیروی می‌کردند. و این دو هیچ کسی را چیزی نمی‌آموختند مگر آنکه می‌گفتند: ما وسیله آزمایش شما هستیم، پس کافر مشو. و مردم از آنان چیزی می‌آموختند که با آن بین مرد و همسرش جدایی می‌افگندند و آنان به کسی زیانی نمی‌رساندند مگر به اذن الله و چیزی را می‌آموختند که به آنها زیان می‌رساند و به آنها سودی نمی‌رساند. و به خوبی می‌دانستند که هرکس خریدار آن باشد در آخرت بهرۀ ندارد، و چه بد بود آنچه به جان خریدند اگر میدانستند.');
INSERT INTO "badkhashani" VALUES(2,103,'2:103','و اگر آنها ایمان می‌آوردند و پرهیزگاری می‌کردند يقينا ثوابی که نزد الله هست برایشان بهتر بود، اگر این را می‌دانستند.');
INSERT INTO "badkhashani" VALUES(2,104,'2:104','ای کسانی که ایمان آورده‌اید، پیغمبر را به لفظ «راعنا» (ما را رعایت کن) خطاب نکنید (بلکه)، بگویید (در خطاب خود) «انظرنا» (ما را نظر کن) و بشنوید (گفته او را چون کلمه توهین آمیز در مقابل پیغمبر کفر است( و برای کفار عذاب دردناک است.');
INSERT INTO "badkhashani" VALUES(2,105,'2:105','کافران اهل کتاب و مشرکین دوست ندارند که خیری از جانب پروردگارتان بر شما نازل شود، و الله هر کسی را که بخواهد به رحمت خویش اختصاص می‌دهد و الله دارای فضل بزرگ است.');
INSERT INTO "badkhashani" VALUES(2,106,'2:106','هر آیه‌ای را که نسخ کنیم یا آن را (از حافظۀ تو) فراموش گردانیم بهتر از آن یا مانند آن را می‌آوریم، آیا نمی‌دانی که الله بر هر چیز قادر است؟.');
INSERT INTO "badkhashani" VALUES(2,107,'2:107','آیا نمیدانی (ای مخاطب) که پادشاهی آسمان‌ها و زمین از الله است و برای شما جز او هیچ کارساز و مددگاری نیست.');
INSERT INTO "badkhashani" VALUES(2,108,'2:108','آیا از پیغمبرتان همان را می‌خواهید که پیش از او از موسی خواسته شد؟ و هر کسی کفر را جانشین ایمان گرداند به راستی که از راه راست گمراه شده است.');
INSERT INTO "badkhashani" VALUES(2,109,'2:109','بسیاری از اهل کتاب به خاطر حسدی که در دل دارند دوست دارند که شما پس از ایمان آوردنتان کافر شوید پس از اینکه حق برایشان روشن گردید، پس گذشت و چشم پوشی کنید تا الله فرمان خود را بیاورد. البته الله بر هر چیزی تواناست.');
INSERT INTO "badkhashani" VALUES(2,110,'2:110','و (در اصلاح خود کوشیده) نماز را برپا کنید و زکات را بدهید و آنچه شما از خوبی و خیر برای خودتان پیش می‌فرستید، آن را نزد الله خواهید یافت. چون الله به آنچه می‌کنید بیناست.');
INSERT INTO "badkhashani" VALUES(2,111,'2:111','و گفتند: (یهود و نصارا) هرگز کسی وارد بهشت نمی‌شود مگر آنکه یهودی یا نصرانی باشد، این آرزوی آنهاست، بگو دلیل خود را بیاورید اگر در دعوای خود صادق هستید.');
INSERT INTO "badkhashani" VALUES(2,112,'2:112','بلی! هر کس که مخلصانه روی به الله کند (و منقاد اوامر او گردد) در حالیکه نیکوکار باشد، پس پاداش او نزد پروردگارش است، و نه ترسی بر آنهاست و نه اندوهگین می‌گردند.');
INSERT INTO "badkhashani" VALUES(2,113,'2:113','ویهودی‌ها گفتند که نصارا بر چیزی (از حق) نیستند (که قابل اعتماد باشد) و نصارا گفتند که یهودیها بر چیزی (از حق) نیستند (که قابل اعتماد باشد) در حالیکه هر دو کتاب الله را تلاوت می‌کنند و (می دانند که عقیدۀ هر دو خلاف كتاب الله است) همچنین آنانکه (کتاب آسمانی را) نمی‌دانند مانند سخن آنها را گفتند، پس الله حكم خواهد کرد در میان ایشان در روز قیامت در باره چیزی که در آن اختلاف می‌کردند.');
INSERT INTO "badkhashani" VALUES(2,114,'2:114','و کیست ظالم‌تر از کسیکه نگذارد در مساجد الله نام او برده شود و در ویرانی آن بکوشد؟ همان گروه را لایق نبود که داخل شوند در مسجدها مگر اینکه ترسان باشند (از عقوبت الهی)، (پس ) برای آنها در دنیا رسوائی است و در آخرت عذاب بزرگ در پیش دارند.');
INSERT INTO "badkhashani" VALUES(2,115,'2:115','(با بند کردن مساجد، عبادت الله بند نمی‌شود زیرا) برای الله است جانب مشرق و جانب مغرب، پس به هر طرف که روی کنید (برای عبادت الله) همانجاست روی الله؛ همانا الله دارای فضل گشاده و داناست.');
INSERT INTO "badkhashani" VALUES(2,116,'2:116','و گفتند: الله فرزندی برای خود برگزیده است، او پاک و منزه است. بلکه آنچه در آسمان‌ها و زمین است از اوست، و همه فرمانبردار او هستند.');
INSERT INTO "badkhashani" VALUES(2,117,'2:117','به وجود آورنده آسمان‌ها و زمین )از عدم و بدون هیچ نمونه( است و چون ایجاد چیزی را فیصله کند، به آن می‌گوید: «باش»، پس می‌شود.');
INSERT INTO "badkhashani" VALUES(2,118,'2:118','و آنان که نمی‌دانند، گفتند: چرا الله با ما سخن نمی‌گوید یا معجزۀ روشنی برای ما نمی‌فرستد؟ کسانی که پیش از آنها بودند مانند سخن آنها را گفتند، دل‌هایشان شبیه همدیگر است. به راستی که آیات را برای قومی روشن ساخته‌ایم که یقین دارند.');
INSERT INTO "badkhashani" VALUES(2,119,'2:119','يقينا ما تو را به حق مژده دهنده و بیم دهنده فرستادیم، و دربارۀ دوزخیان از تو پرسیده نخواهد شد.');
INSERT INTO "badkhashani" VALUES(2,120,'2:120','و یهود و نصارا هرگز از تو راضی نخواهند شد تا اینکه از دینشان پیروی کنی. بگو: البته هدایت الله هدایت حقیقی است. و اگر از خواهشات و آرزوهایشان پیروی کنی پس از اینکه علم و آگاهی به تو آمده است، هیچ مددگار و یاوری از جانب الله برایت نخواهد بود.');
INSERT INTO "badkhashani" VALUES(2,121,'2:121','کسانی که به آنها کتاب داده‌ایم آن را طوری که حق خواندنش است تلاوت می‌کنند، ایشان به آن ایمان می‌آورند و هر کس به آن کفر ورزد پس آنان زیانکاران‌اند.');
INSERT INTO "badkhashani" VALUES(2,122,'2:122','و ای فرزندان يعقوب! یاد کنید نعمت مرا که به شما ارزانی داشتم و اینکه شما را بر جهانیان (هم‌عصرتان) برتری دادم.');
INSERT INTO "badkhashani" VALUES(2,123,'2:123','و (نیز) بترسید از روزی که کسی به داد کسی نمی‌رسد و بدلی از آن پذیرفته نمی‌شود و شفاعت به او سودی نمی‌رساند، و نه آنها یاری خواهند شد.');
INSERT INTO "badkhashani" VALUES(2,124,'2:124','و چون ابراهیم را پروردگارش با کلماتی امتحان کرد و آن را به طور کامل انجام داد. الله فرمود: تو را پیشوای مردم قرار می‌دهم، گفت: و از اولاد من (نیز پیشوایان مقرر کن) فرمود: عهد من به ظالمان نمی‌رسد (یعنی: این منصب صرف به صالحان اولاد تو خواهد رسید).');
INSERT INTO "badkhashani" VALUES(2,125,'2:125','و باز یاد آور شوید وقتی که خانه کعبه را محل اجتماع مردم و جای امنی برای‌شان قرار دادیم و گفتیم از مقام ابراهیم جای نماز بگیرید. و به ابراهیم و اسماعیل سفارش کردیم تا خانه مرا برای طواف کنندگان و معتکفان و ركوع کنندگان و سجده کنندگان پاکیزه گردانید.');
INSERT INTO "badkhashani" VALUES(2,126,'2:126','و یاد آور شوید وقتی ابراهیم گفت: ای پروردگارم! اینجا (مکه) را شهر امن بگردان و از اهلش هر آن کس که به الله و روز آخرت ایمان آورد از میوه‌ها به او روزی ببخش. الله فرمود: و هر کس که کفر ورزد او را مدت اندکی بهره‌مند می‌گردانم، باز او را به عذاب آتش ناچار می‌سازم و بد جای بازگشت است دوزخ.');
INSERT INTO "badkhashani" VALUES(2,127,'2:127','(و یاد آور شوید) وقتی که ابراهیم و اسماعيل اساس و پایه‌های خانه کعبه را بلند می‌کردند گفتند: ای پروردگار ما! (بنای کعبه را) از ما قبول کن، چون تو شنوا و دانایی.');
INSERT INTO "badkhashani" VALUES(2,128,'2:128','ای پروردگار ما، ما را فرمانبردار خود بگردان، از فرزندان ما ملتی فرمانبردار پدید آور و به ما طرز عبادت ما را نشان ده، و توبه ما را قبول کن، چون تویی قبول کننده توبه (و) بسیار مهربان.');
INSERT INTO "badkhashani" VALUES(2,129,'2:129','ای پروردگار ما! در میان اولاد من پیغمبری از جنس خود‌شان بفرست که آیات را بر آنها تلاوت کند و به آنها کتاب آسمانی و حکمت (علم و دانش آن( را بیاموزد و آنها را پاک سازد، يقينا تویی بسیار غالب و با حکمت.');
INSERT INTO "badkhashani" VALUES(2,130,'2:130','و کیست که روی گرداند از دین ابراهیم بجز آنکه خود را خوار دانسته و نشناسد، و البته برگزیدیم ابراهیم را در دنیا، و يقينا او در آخرت نیز از نیکان خواهد بود.');
INSERT INTO "badkhashani" VALUES(2,131,'2:131','(به یاد آور وقتی را که) پروردگارش حکم کرد که فرمان بردار (اوامرم) شو، ابراهیم گفت: فرمان بردار شدم برای پروردگار جهانیان.');
INSERT INTO "badkhashani" VALUES(2,132,'2:132','و ابراهیم و يعقوب پسرانشان را به آن وصیت کردند که ای فرزندان! الله دین را برایتان برگزیده است پس نمیرید مگر اینکه مسلمان باشید.');
INSERT INTO "badkhashani" VALUES(2,133,'2:133','آیا شما حاضر بودید وقتی که مرگ يعقوب فرا رسید؟ وقتی به پسران خود گفت که بعد از من چه چیز را عبادت می‌کنید؟ گفتند: معبودت را، و معبود پدرانت ابراهیم و اسماعیل و اسحاق را می‌پرستیم که معبودی یگانه است و ما فرمانبردار او هستیم.');
INSERT INTO "badkhashani" VALUES(2,134,'2:134','آنها امتی بودند که در گذشتند و اعمالشان به سود خودشان است و اعمال شما، به سود خودتان است و شما مسؤول اعمال آنها نخواهید بود.');
INSERT INTO "badkhashani" VALUES(2,135,'2:135','(و اهل کتاب) گفتند: یهودی یا نصرانی باشید تا هدایت شوید، بگو: بلکه دین ابراهیم (را انتخاب می‌کنم) که از ادیان باطل رویگردان و یکتاپرست بود و از مشركان نبود.');
INSERT INTO "badkhashani" VALUES(2,136,'2:136','شما بگوید که ما ایمان آوردیم به الله و به آنچه نازل شده به ما، و به آنچه نازل شده به ابراهیم و اسماعیل و اسحق و يعقوب، و به نوادگان يعقوب، و ایمان آوردیم به آنچه که به موسی و عیسی داده شده، و به آنچه پیغمبران از جانب پروردگارشان دریافت کرده‌اند، و (در ایمان آوردن) میان هیچ یک از آنان فرق نمی‌گذاریم و ما تنها به او تعالی منقاديم و بس.');
INSERT INTO "badkhashani" VALUES(2,137,'2:137','پس اگر ایمان آورند (اهل کتاب) مثل ايمان شما (اصحاب پیغمبر) پس یقینا راهیاب شده‌اند، و اگر اعراض نمودند پس جز این نیست که آنها در مخالفت و ستیز قرار دارند، پس الله تو را (از شر آنها) کفایت می‌کند و الله شنوای داناست.');
INSERT INTO "badkhashani" VALUES(2,138,'2:138','(و بگویید) این است رنگ آمیزی الهی (که برترین رنگهاست)، و کیست از الله زیباتر باعتبار رنگ (دین فطرت)؟ و ما تنها او را عبادت می‌کنیم.');
INSERT INTO "badkhashani" VALUES(2,139,'2:139','(ای پیغمبر به اهل کتاب) بگو! آیا در باره الله با ما مجادله می‌کنید؟ در حالیکه که او رب ما و رب شما هم است، و اعمال ما به سود ماست و اعمال شما به سود شماست، و ما برای او تعالی اخلاص می‌ورزیم (او را با اخلاص می‌پرستیم).');
INSERT INTO "badkhashani" VALUES(2,140,'2:140','آیا (شما اهل کتاب می‌گویید که) ابراهیم و اسماعیل و اسحق و یعقوب و نبیرگان او (همه) یهودی یا نصرانی بودند؟ بگو: آیا شما داناتر هستید یا الله؟ و کیست ظالم‌تر از کسی که شهادتی را که از جانب الله نزد او است کتمان کند؟، و الله غافل نیست از چیزیکه شما می‌کنید.');
INSERT INTO "badkhashani" VALUES(2,141,'2:141','آنها امتی بودند که گذشتند، اعمال آنها به سود خود آنهاست و اعمال شما به سود خودتان است و شما دربارۀ اعمال آنها بازخواست نمی‌شوید.');
INSERT INTO "badkhashani" VALUES(2,142,'2:142','زودا که مردمان بی‌خرد بگویند چه چیزی باز گردانید مسلمانان را از قبلۀ که بر آن بودند؟ (یعنی از بیت المقدس) بگو: تنها برای الله است جانب مشرق و مغرب (و همه جوانب)، هر کسی را که بخواهد به راه راست هدایت می‌کند.');
INSERT INTO "badkhashani" VALUES(2,143,'2:143','و این چنین شما را امت میانه رو گردانیدیم تا شما گواه باشید بر مردمان و پیغمبر (نیز) گواه باشد بر شما، و ما لازم نگردانیدیم قبله را که تو بر آن بودی (بیت المقدس ) مگر اینکه بدانیم (با تغییر دادنش) چه کسی از پیغمبر پیروی می‌کند و چه کسی به عقب بر می‌گردد، و اگر چه آن خصلت (پیروی رسول) بسیار دشوار است مگر بر کسانی که الله آنان را هدایت نموده، و الله ایمانتان (یعنی نماز تان) را ضایع نمی‌گرداند، همانا الله نسبت به مردم (مسلمان) مشفق و مهربان است.');
INSERT INTO "badkhashani" VALUES(2,144,'2:144','البته ما می‌بینیم متوجه شدن ترا به طرف آسمان، پس حتما متوجه گردانیم ترا به‌سوی قبله که تو از آن راضی شوی، پس بگردان روی خود را (در نماز) به طرف مسجد الحرام، (کعبه) و شما (مسلمانان هم) هر جا که باشید پس بگردانید (در نماز) روی‌های خود را به طرف آن (کعبه) و البته آنان را که کتاب داده شده می‌دانند که آن (تحویل قبله) حق است از طرف رب شان، و الله از آنچه می‌کنند غافل نیست.');
INSERT INTO "badkhashani" VALUES(2,145,'2:145','واگر به اهل کتاب تمامی آیات و معجزات (مطلوبۀ آنها را( بیاوری باز هم از قبلۀ تو پیروی نمی‌کنند، و نه تو از قبلۀ آنها پیروی می‌کنی، و نه آنها متابعت می‌کنند قبلۀ یکدیگر را، و اگر تو (بالفرض) از خواهشات آنها پس از آگاهی و علمی که به تو رسید، پیروی کنی، البته در آن صورت از جمله ظالمان خواهی بود.');
INSERT INTO "badkhashani" VALUES(2,146,'2:146','آنانی که کتاب را به ایشان داده‌ایم، او (محمد) را می‌شناسند چنانکه فرزندانشان را می‌شناسند، و همانا گروهی از آنان حق را پنهان می‌کنند در حالی که می‌دانند.');
INSERT INTO "badkhashani" VALUES(2,147,'2:147','حق (در تحویل قبله تنها) از سوی پروردگارت می‌باشد، پس از شک کنندگان مباش.');
INSERT INTO "badkhashani" VALUES(2,148,'2:148','و برای هر یک جهت و قبلۀ است که به‌سوی آن روی می‌آورد، پس در کارهای خیر شتاب کنید، و هر جایی که باشید الله همۀ شما را یکجا جمع می‌نماید، همانا الله به هر چیز قادر است.');
INSERT INTO "badkhashani" VALUES(2,149,'2:149','از هر جا (و از هر شهر و نقطه) که بیرون شوی، پس (به هنگام نماز،( روی خود را به طرف مسجد الحرام بگردان، و البته (قبله بودن آن) حق است از جانب پرورد گار تو، و الله از آنچه که شما می‌کنید غافل نیست.');
INSERT INTO "badkhashani" VALUES(2,150,'2:150','و از هر جای که تو بیرون شوی (در سفر) پس بگردان روی خود را به‌سوی مسجد الحرام، و در هر جای که شما باشید (ای مردم) پس بگردانید روی‌های خود را به‌سوی آن (یعنی کعبه) تا نباشد برای مردم اعتراضی بر شما، مگر آنانکه ظلم کرده‌اند از آنها (اهل کتاب) پس از آنها نترسید و از من بترسید (تحویل قبله یکی برای خوشی پیغمبر بود و دیگر برای اینکه) تا تمام کنم نعمت خود را بر شما، و تا راهیاب شوید.');
INSERT INTO "badkhashani" VALUES(2,151,'2:151','همانطور که فرستادیم در میان شما (برای هدایت تان) پیغمبری از جنس خود شما که تلاوت می‌کند بر شما آیات ما را و پاک می‌سازد شما را )از عقائد و اخلاق فاسد) و به شما کتاب و حکمت را می‌آموزد و آنچه را که نمی‌دانستید به شما تعليم می‌دهد.');
INSERT INTO "badkhashani" VALUES(2,152,'2:152','پس شما مرا (به عبادت) یاد کنید، تا من شما را (به اجر و ثواب) یاد کنم، و شکر مرا را بجا آورید و (نعمت‌های) مرا کفران مکنید.');
INSERT INTO "badkhashani" VALUES(2,153,'2:153','ای کسانی که ایمان آورده‌اید، (در مشکلات و مصائب خود) به وسیلۀ صبر و نماز مدد جویید؛ که (رحمت و نصرت) الله با صابران است.');
INSERT INTO "badkhashani" VALUES(2,154,'2:154','و کسانی را که در راه الله کشته می‌شوند مرده نخوانید. بلکه آنان (در نزد الله) زنده‌اند، ولی شما (آن زندگی برزخی را) نمی‌دانید.');
INSERT INTO "badkhashani" VALUES(2,155,'2:155','البته شما را به چیزی از ترس و گرسنگی و نقصان مالی و جانی و نقصان میوه‌ها می‌آزماییم، و مژده بده صبر کنندگان را (در برابر این مصائب).');
INSERT INTO "badkhashani" VALUES(2,156,'2:156','آنانی را که چون برسد به آنها مصیبتی گویند: يقينا ما از الله هستیم (و به حکم او به دنیا آمده‌ایم) و ما به‌سوی او باز می‌گردیم.');
INSERT INTO "badkhashani" VALUES(2,157,'2:157','این گروه درود و رحمت الله شامل حال‌شان است و ایشان‌اند هدایت شدگان.');
INSERT INTO "badkhashani" VALUES(2,158,'2:158','يقينا صفا و مروه (که جای صبر هاجر است) یکی از نشانه‌های دین الله‌اند، پس هرکس که (اراده کند) حج بیت الله یا عمره را، پس نیست گناهی بر او که طواف (سعی) کند در میان این هر دو، و هرکسی (داوطلبانه) کاری نیک انجام دهد، پس یقینا الله قدركننده (نیکی او و) داناست.');
INSERT INTO "badkhashani" VALUES(2,159,'2:159','البته کسانی که دلایل روشن، و هدایتی را که ما فرستاده‌ایم پنهان می‌کنند، بعد از آن که آن را در کتاب برای مردم بیان نموده‌ایم، آن گروه مورد لعنت الله و لعنت کنندگان قرار می‌گیرند.');
INSERT INTO "badkhashani" VALUES(2,160,'2:160','مگر آنانکه توبه کرده‌اند، و (عمل خود را) اصلاح کرده‌اند، و دلائل و هدایت ما را بیان داشته‌اند، پس توبه آنها را قبول می‌کنم، و منم قبول کنندۀ توبه، (و) بی‌نهایت مهربان.');
INSERT INTO "badkhashani" VALUES(2,161,'2:161','البته آنانکه کفر ورزیدند و در حال کفر مردند، لعنت الله و فرشتگان و تمام مردم برایشان باد.');
INSERT INTO "badkhashani" VALUES(2,162,'2:162','در آن لعنت همیشه باقی می‌مانند، نه عذاب آنها تخفیف می‌گردد و نه به آنان مهلتی داده می‌شود (که از عذاب بیرون شوند).');
INSERT INTO "badkhashani" VALUES(2,163,'2:163','و معبود شما معبودی یگانه است، معبود به حقی جز او وجود ندارد، بخشندۀ مهربان است.');
INSERT INTO "badkhashani" VALUES(2,164,'2:164','البته (دليل وحدانیت الله این است که) در ایجاد کردن آسمان‌ها و زمین، و در اختلاف شب و روز، و در کشتی‌های که به نفع مردم در بحر می‌روند، و در آنچه که نازل کرده است از آسمان از آب، تا زمین را بعد از خشکیدنش با آن زنده کند و انواع جانوران را در آن منتشر سازد، و در به‌حرکت درآوردن بادها، و در ابری که مسخر کرده شده در بین آسمان و زمین، نشانه‌ها (و دلائل واضح) است (بر قدرت و وحدانیت او تعالی) برای مردمی که بفهمند (و از عقل کار گیرند).');
INSERT INTO "badkhashani" VALUES(2,165,'2:165','و از مردم کسانی هستند که غیر از الله شریکهایی می‌گیرند و آنها را دوست می‌دارند مانند دوست داشتن الله، و (اما) آنانکه ایمان آورده‌اند محبت‌شان به الله شدیدتر است (از محبت مشرکین با شریک‌های مقرر کرده خود)، و اگر ظالمان عذاب را ببینند در می‌یابند که همه قوت به دست الله است، و )این را هم در می‌یابند که( الله سخت عذاب دهنده است.');
INSERT INTO "badkhashani" VALUES(2,166,'2:166','وقتی عذاب را مشاهده کنند، پیشوایان (کفر) از پیروان خود بیزاری جویند و پیوند میانشان گسسته شود.');
INSERT INTO "badkhashani" VALUES(2,167,'2:167','و پیروان گویند: اگر بار دیگر به دنیا برگردانده شویم، تا از آقایان و رؤسای خود بیزاری جوییم، طوریکه اکنون از ما بیزار شده‌اند، این چنین الله نشان می‌دهد به آنها اعمال‌شان را که موجب حسرت ایشان باشد و آنها (پیروان و پیشوایان) از دوزخ بیرون نخواهند شد.');
INSERT INTO "badkhashani" VALUES(2,168,'2:168','ای مردم، از آنچه که در زمین حلال و پاکیزه است بخورید، و از گام‌های شیطان پیروی مکنید؛ همانا او برای شما دشمن آشکار است.');
INSERT INTO "badkhashani" VALUES(2,169,'2:169','جز این نیست که شیطان شما را به کارهای بد و به بی‌حیایی فرمان می‌دهد، و (فرمان می‌دهد به) اینکه آنچه را که (عاقبت بد آن را) نمی‌دانید به الله نسبت دهید.');
INSERT INTO "badkhashani" VALUES(2,170,'2:170','و هنگامی (به مشرکین) گفته شود که پیروی کنید آن (دینی را) که الله نازل کرده است، (در جواب) گویند: بلکه پیروی می‌کنیم از آنچه که پدران خود را بر آن یافته‌ایم، (آیا پیروی می‌کنند پدران خود را) اگر چه پدرانشان چیزی را نمی‌فهمیدند و راه یاب نبودند؟.');
INSERT INTO "badkhashani" VALUES(2,171,'2:171','مثال کفار مانند حیوانی است که وقتی صدایش کنند نشنود مگر فریاد و آواز را (گویا کافران) کران و گنگان و کوران‌اند، پس (حق و گمراهی را) نمی‌دانند.');
INSERT INTO "badkhashani" VALUES(2,172,'2:172','ای کسانی که ایمان آورده‌اید، بخورید از چیزهای پاکیزۀ که به شما روزی داده‌ایم، و شكر الله را بجا آورید، اگر تنها او را عبادت می‌کنید.');
INSERT INTO "badkhashani" VALUES(2,173,'2:173','جز این نیست که الله حرام کرده است بر شما (حیوان و پرندۀ) خود مرده و خون و خوک را و آنچه را که نام غیرالله بر او برده شده، پس هر که مجبور شود (به خوردن این چیزها) بدون آنکه (به خوردن آن) علاقه مند و تجاوزکار باشد، پس بر وی گناهی نیست، بدون شک الله آمرزندۀ مهربان است.');
INSERT INTO "badkhashani" VALUES(2,174,'2:174','همانا کسانی که آنچه را الله از کتاب (آسمانی) نازل کرده، پنهان می‌کنند و آن را به بهای حقیر (دنیا) می‌فروشند، در شکم‌هایشان جز آتش چیزی را فرو نمی‌برند و الله در روز قیامت با آنها سخن نمی‌گوید و آنها را پاکیزه نمی‌گرداند و برای آنها عذاب دردناک است.');
INSERT INTO "badkhashani" VALUES(2,175,'2:175','آنها همان کسانی هستند که گمراهی را به عوض هدایت و عذاب را به عوض آمرزش خریدند، پس چقدر در برابر آتش صبر و تحمل دارند!.');
INSERT INTO "badkhashani" VALUES(2,176,'2:176','این عقوبت به سبب آن است که الله كتاب را به حق نازل کرده است و کسانی که در کتاب اختلاف کردند در ستیزۀ عمیق قرار دارند.');
INSERT INTO "badkhashani" VALUES(2,177,'2:177','نیکی (تنها) این نیست که روی خود را به طرف مشرق و یا به طرف مغرب بگردانید، لیکن نیکوکار کسی است که به الله و روز قیامت و فرشتگان و کتاب‌های الله و پیغمبران ایمان آورد، و مال (خود) را (در راه الله) با وجود دوست داشتن آن به خویشاوندان و یتیمان و مسکینان و مسافران و گداها، و در آزاد نمودن برده‌ها (یا قرض داران) دهد، و نماز را برپا کند و زکات را بدهد، و وفا کنندگان به عهد خود چون عهد کنند، و صبر کنندگان در وقت تنگدستی و سختی و در وقت جهاد، این گروه کسانی‌اند که (در دعوای ایمان خود) راست گفتند و همین گروه پرهیزگارانند.');
INSERT INTO "badkhashani" VALUES(2,178,'2:178','ای کسانی که ایمان آورده‌اید! در مورد کشته شدگان، قصاص بر شما مقرر شده است، آزاد در مقابل آزاد، و برده در مقابل برده، و زن در مقابل زن، پس هرکسی که از جانب برادرش برای او چیزی بخشوده شد، به خوبی پیروی کند و به نیکی دیه را به او بپردازد. این تخفیف و رحمتی از جانب پروردگارتان برای شماست، پس هرکس بعد از این (پس از گرفتن خونبها) تجاوز کرد، پس برای او عذاب دردناک است.');
INSERT INTO "badkhashani" VALUES(2,179,'2:179','و برای شما در اجرای قصاص زندگانی است ای خردمندان، تا پرهیزگار شوید (و از قتل یکدیگر اجتناب ورزید).');
INSERT INTO "badkhashani" VALUES(2,180,'2:180','بر شما فرض شده است که هرگاه مرگ یکی از شما فرا رسید اگر مالی باقی بگذارد، برای پدر و مادر و خویشاوندان به طور شایسته وصیت کند، و این حقی بر پرهیزگاران است.');
INSERT INTO "badkhashani" VALUES(2,181,'2:181','پس هرکس آن (وصیت) را پس از شنیدنش تغییر داد، پس گناهش تنها بر کسانی است که آن را تغییر می‌دهند. يقينا الله شنوای داناست.');
INSERT INTO "badkhashani" VALUES(2,182,'2:182','پس کسیکه بخاطر ترس از خطا یا ظلم وصیت کننده (وصیت را تبدیل و اصلاح کند) تا صلح را در میان آنها برقرار کند پس بر وی گناهی نیست، بی‌گمان الله آمرزندۀ مهربان است.');
INSERT INTO "badkhashani" VALUES(2,183,'2:183','ای کسانی که ایمان آورده‌اید، روزه بر شما فرض شده است طوریکه فرض شده بود بر آنانکه پیش از شما بودند، تا پرهیزگار شوید.');
INSERT INTO "badkhashani" VALUES(2,184,'2:184','(روزه در) روزهای معدود (بر شما فرض است)، پس هر کسی از شما مریض یا در سفر باشد پس بر او شمردن روزهای دیگر (قضای روزه) لازم است، و بر آنانکه طاقت روزه گرفتن را ندارند، فديه لازم است به اندازه خوراک یک مسکین، پس هر کس به خوشی خود کار نیک بکند پس آن برای او بهتر است، و روزه داشتن برای شما بهتر است اگر بدانید (ثواب آن را).');
INSERT INTO "badkhashani" VALUES(2,185,'2:185','ماه رمضان ماهی است که در آن قرآن نازل شده است، کتابی که هدایتگر مردم است و متضمن نشانه‌های آشکار هدایت و فرقان است. پس هر کس از شما در ماه رمضان حاضر و مقيم باشد، آن را روزه بگیرد، و هر کس که بیمار یا مسافر باشد، پس تعدادی از روزهای دیگر را روزه بگیرد، الله برای شما آسانی می‌خواهد و برای شما دشواری نمی‌خواهد، تا شمار روزها را کامل کنید و الله را به سبب آنکه شما را هدایت کرده است به بزرگی یاد کنید، و باشد که شکر گزار باشید.');
INSERT INTO "badkhashani" VALUES(2,186,'2:186','و چون بندگانم درباره من از تو سوال کنند، پس (به ایشان بگو) من به آنها نزدیک ام و دعای دعاکننده را هرگاه مرا بخواند قبول می‌کنم، پس باید آنها (نیز) فرمان مرا قبول کننده، و باید به (وحدانیت) من یقین داشته باشند تا راه یابند (و کامیاب شوند).');
INSERT INTO "badkhashani" VALUES(2,187,'2:187','همبستری با زنانتان در شب روزه برای شما حلال شد، شما لباس آنها هستید و آنها لباس شما هستند، الله می‌دانست که شما در حق خود خیانت می‌کردید، پس توبه شما را پذیرفت و از شما در گذشت. پس اکنون با آنها همبستری کنید و آنچه را که الله برایتان مقرر نموده طلب نمایید، و بخورید و بیاشامید تا رشته سفید از رشته سیاه برایتان روشن شود، باز روزه را تا شب کامل کنید، و در حالیکه در مساجد به اعتکاف نشسته‌اید با زنان مقاربت نکنید. این حدود الله است، پس به آن نزدیک نشوید. این چنین الله آیات خود را برای مردم بیان می‌کند تا پرهیزگار شوند.');
INSERT INTO "badkhashani" VALUES(2,188,'2:188','و مال‌های یکدیگرتان را در میانتان به ناحق نخورید و آن را به حکام تقدیم نکنید تا بخشی از اموال مردم را به گناه بخورید، در حالیکه شما (هم) می‌دانید.');
INSERT INTO "badkhashani" VALUES(2,189,'2:189','دربارۀ (اوضاع مختلف) هلال‌های ماه از تو می‌پرسند، بگو: «اوضاع هلال‌ها) برای شناختن اوقات مردم است، و (خصوصا برای معرفت وقت) حج است، و نیکی این نیست که (مانند عهد جاهلیت در حالت احرام) از پشت خانه‌ها وارد منازل شوید و لیکن نیکی، نیکی کسی است که تقوی پیشه کند، و به خانه‌ها از دروازه‌های آن وارد شوید، و از الله بترسید (و کارها را بر عکس فرموده الله انجام ندهید) تا کامیاب شوید.');
INSERT INTO "badkhashani" VALUES(2,190,'2:190','و در راه الله با کسانی که با شما می‌جنگند، بجنگید و تجاوز نکنید، چرا که الله تجاوز کنندگان را دوست نمی‌دارد.');
INSERT INTO "badkhashani" VALUES(2,191,'2:191','و آنان (مشرکان) را در هر جایی که بیابید بکشید، و آنها را از همان جای که شما را بیرون کرده بودند بیرون کنید، و فتنه (کفر و شرک) از قتل بدتر است، و با مشرکان در نزد مسجد الحرام نجنگید تا آنکه ایشان با شما بجنگند، پس اگر با شما (در آنجا) جنگ کردند پس آنها را بکشید، اینچنین است سزای کافران.');
INSERT INTO "badkhashani" VALUES(2,192,'2:192','پس اگر (از شرک و جنگ) دست برداشتند (الله توبۀ آنها را قبول می‌کند) چون الله بسیار آمرزنده (و) مهربان است.');
INSERT INTO "badkhashani" VALUES(2,193,'2:193','و با آنها بجنگید تا فتنه (کفر و شرک) باقی نماند و دین همه برای الله باشد (و مردم تنها الله را عبادت کنند)، پس اگر از کفر و شرک خود دست برداشتند (با آنها کاری نداشته باشید چرا که) روا نیست تجاوز مگر بر ظالمان (و ایشان از ظلم خود دست برداشتند).');
INSERT INTO "badkhashani" VALUES(2,194,'2:194','(جنگ در) ماه حرام در مقابل (جنگ در) ماه حرام (جائز) است، چونکه از بی‌حرمتی مقدسات قصاص گرفته می‌شود، پس هرکس بر شما تجاوز کرد به مانند آنچه که به شما تجاوز کرده است به او تجاوز کنید و از الله بترسید، و بدانید که (مدد) الله با پرهیزگاران است.');
INSERT INTO "badkhashani" VALUES(2,195,'2:195','و در راه الله انفاق کنید و خود را با دستهایتان (به وسیله بخل) در هلاکت میندازید، و نیکی کنید، زیرا الله نیکی کنندگان را دوست می‌دارد.');
INSERT INTO "badkhashani" VALUES(2,196,'2:196','و حج و عمره را برای الله به اتمام برسانید، و اگر بازداشته شدید، هر آنچه از قربانی میسر است، ذبح کنید و سرهایتان را نتراشید تا وقتی که قربانی به جایگاه خود برسد، و هر کس از شما بیمار باشد یا آزاری در سر داشته باشد، و سر خود را بتراشد بر اوست که فدیه‌ای بدهد، از قبیل روزه یا صدقه یا قربانی، و چون امنیت و آسایش یافتید، پس هرکس پس از عمره قصد حج کند بر اوست که هر آنچه میسر است قربانی کند و کسی که قربانی نیافت سه روز در حج، و هفت روز وقتی که برگشتید روزه بگیرد؛ این ده روز کاملی است. این (حج تمتع) برای کسی است که خانواده او اهل مسجدالحرام نباشد. (یعنی ساکن مکه نباشد). و از الله بترسید و بدانید که الله عقوبت سخت دارد.');
INSERT INTO "badkhashani" VALUES(2,197,'2:197','(برای ادای) حج ماه‌های معلوم و معین است، پس هر که در آن ماهها ادای حج را بر خود فرض گرداند پس (بداند که) برای او جماع و گناه و جنگ و جدال در ایام حج جائز نیست (زیرا این‌ها منافی حج اند) و هر چه می‌کنید از کارهای نیک الله آن را می‌داند، و (در سفر حج با خود) توشه گیرید، و بهترین توشه پرهیزگاری است و از (عذاب) من بترسید ای صاحبان خرد.');
INSERT INTO "badkhashani" VALUES(2,198,'2:198','بر شما گناهی نیست که (در ایام حج) از پروردگار خود فضل و رزق طلب کنید (تجارت کنید)، پس چون از عرفات بازگشتید الله را نزد مشعر الحرام (مزدلفه) یاد کنید، او را یاد کنید چنانکه شما را هدایت کرده است، هرچند که پیش از آن از جملۀ گمراهان بودید.');
INSERT INTO "badkhashani" VALUES(2,199,'2:199','باز از همان جا که مردم روانه می‌شوند، شما نیز روانه شوید و از الله آمرزش بخواهید چرا که الله بسیار آمرزنده (و) مهربان است.');
INSERT INTO "badkhashani" VALUES(2,200,'2:200','پس چون مناسک حج تان را ادا کردید پس الله را یاد کنید همانطور که پدران تان را یاد می‌کنید، بلکه بیشتر از آن. پس، از مردمان کسانی هستند که می‌گویند: ای پروردگار ما! به ما در این دنیا نعمت و نیکی بده و در آخرت برای او هیچ سهمی نیست.');
INSERT INTO "badkhashani" VALUES(2,201,'2:201','و (نیز) از (میان) مردمان (حاجیان) کسانی هستند که می‌گویند: ای پروردگار ما در این دنیا به ما خیر و خوبی بده و در آخرت نیز خیر و خوبی بده، و ما را از عذاب دوزخ نگهدار.');
INSERT INTO "badkhashani" VALUES(2,202,'2:202','این گروه از آنچه که کرده‌اند حصه و بهره می‌برند، و الله زود حساب گیرنده (اعمال بندگان خود) است.');
INSERT INTO "badkhashani" VALUES(2,203,'2:203','و الله را در روزهای معین (شمرده شده در منی) یاد کنید، پس هرکسی عجله کند (و اعمال را) در دو روز انجام دهد بر او گناهی نیست، و کسیکه تاخیر کند (تا روز سوم) پس گناهی بر او نیست (بلکه او را ثواب زیاد است)، این (به جا آوردن ذكر الله) برای کسی است که تقوا اختیار کند، و از الله بترسید و بدانید که به دربار او جمع خواهید شد.');
INSERT INTO "badkhashani" VALUES(2,204,'2:204','و از مردمان کسی هست که سخنان او راجع به زندگانی دنیا تو را در تعجب می‌اندازد، و الله را بر آنچه که در دل دارد گواه می‌گیرد (قسم می‌خورد)، در حالیکه او سرسخت‌ترین دشمنان (و خصومت گران) است.');
INSERT INTO "badkhashani" VALUES(2,205,'2:205','و چون (از مجلس پیغمبر) باز گردد می‌کوشد تا در زمین فساد برپا کند، و زراعت و نسل‌های مردم را برباد سازد، و الله فساد (و فسادکاران را) دوست ندارد.');
INSERT INTO "badkhashani" VALUES(2,206,'2:206','و چون به او گفته شود (بطور نصیحت) که از الله بترس، غرور و تکبر می‌ورزد و به گناه (دیگر) وادارش می‌کند پس دوزخ برای او کافی است، و البته دوزخ بدجایی است.');
INSERT INTO "badkhashani" VALUES(2,207,'2:207','و از مردمان کسی هست که جان خود را برای حصول رضای الله می‌فروشد، والله به (همچنین) بندگان خود بسیار مشفق است.');
INSERT INTO "badkhashani" VALUES(2,208,'2:208','ای کسانی که ایمان آورده‌اید، بطور کامل در اسلام داخل شوید (در تطبيق همه احکام آن ادامه بدهید)، و از گام‌های شیطان پیروی نکنید، بی‌گمان او برای شما دشمن آشکار است.');
INSERT INTO "badkhashani" VALUES(2,209,'2:209','پس اگر بعد از اینکه دلائل واضح (و بیانگر حق) به شما آمد، بلغزید، پس بدانید که الله غالب است (بر همه مخلوقات و) حکیم است (در تأخیر عذاب مجرمان).');
INSERT INTO "badkhashani" VALUES(2,210,'2:210','آیا (کفار) انتظار دارند که الله با فرشتگان، در سایه بانهایی از ابر سفید به نزدشان بیاید و معاملۀ مردم فیصله شود (و قضاوت صورت گیرد)؟ و تمام امور (معاملات و قضایای بشری) به بارگاه الله باز گردانیده می‌شود.');
INSERT INTO "badkhashani" VALUES(2,211,'2:211','از بنی اسرائیل بپرس که چه بسیار نشانه‌های روشن به آنها دادیم. و هر کس که نعمت الله را - پس از آنکه پیش او آمد - (به كفران) بدل کند، پس البته الله سخت عقوبت دارد.');
INSERT INTO "badkhashani" VALUES(2,212,'2:212','زندگانی دنیا برای کافران آراسته شده است، و مؤمنان را مسخره می‌کنند. و پرهیزگاران در روز قیامت از آنها برترند، و الله کسی را که بخواهد بدون حساب روزی می‌دهد.');
INSERT INTO "badkhashani" VALUES(2,213,'2:213','مردم (در آغاز) امت واحد بودند، پس الله پیغمبران مژده دهنده و بیم دهنده را فرستاد، و با آنها کتاب را (به حق) نازل کرد تا در میان مردم در آنچه اختلاف کردند به حق حکم کنند، و در آن اختلاف نورزیدند جز کسانی که (کتاب) به آنها داده شد. (و این اختلاف شان) بعد از آمدن دلائل ظاهر بود و آنها از روی ستم و حسد در میان خود اختلاف ورزیدند، پس الله به فرمان خود مؤمنان را در آنچه مردم اختلاف کردند به حق هدایت کرد، و الله هرکس را که بخواهد، به راه راست هدایت می‌کند.');
INSERT INTO "badkhashani" VALUES(2,214,'2:214','آیا گمان می‌کنید که (به آسانی) وارد جنت میشوید؟! در حالیکه هنوز آنچه که بر سر گذشتگان آمده بود بر سر شما نیامده است. آنها دچار سختی‌ها و مشقتها شدند، و چنان متزلزل و مضطرب شدند، حتی که پیغمبر‌شان و آنانکه با او ایمان آورده بودند می‌گفتند: پس مدد الله کی خواهد آمد؟ (گفته شد) آگاه باشید که مدد الله نزدیک است.');
INSERT INTO "badkhashani" VALUES(2,215,'2:215','(مسلمانان) از تو می‌پرسند چه چیزی انفاق کنند (و به چه کسی بدهند؟) بگو: هر مالی را که انفاق می‌کنید (از مال حلال)، پس برای والدین و خویشاوندان و یتیمان و فقیران، و در راه ماندگان انفاق کنید، و هر نیکی انجام دهید پس یقینا الله به آن داناست.');
INSERT INTO "badkhashani" VALUES(2,216,'2:216','جنگ (جهاد) بر شما فرض شد و آن برای شما ناخوشایند (و مشکل) است و (لیکن) بسا (اوقات) چیزی را نمی‌پسندید در حالیکه به خیر شما می‌باشد و بسا چیزی را دوست دارید ولی به ضرر شما است، و الله میداند (نفع و زیان شما را) و شما نمی‌دانید.');
INSERT INTO "badkhashani" VALUES(2,217,'2:217','تو را دربارۀ جنگ در ماه حرام می‌پرسند، بگو جنگ در آن (گناه) بزرگ است و (لیکن) منع کردن از راه الله، و منع کردن از مسجد الحرام، و بیرون کردن اهل مسجد الحرام از آن، نزد الله (گناه) بزرگتر است، و فتنه (شرک و شکنجه کردن مسلمانان در ماه حرام) بزرگتر و بدتر است، و پیوسته کفار با شما می‌جنگند تا اگر بتوانند شما را از دین تان بازگردانند، و هر که از شما از دین خود بازگردد و در حالت کفر بمیرد پس اعمال او در دنیا و در آخرت ضائع و تباه می‌شود، و آن گروه اصحاب دوزخ‌اند و در آن جا همیشه خواهند بود.');
INSERT INTO "badkhashani" VALUES(2,218,'2:218','البته آنانکه ایمان آوردند و آنانکه (بعد از ایمان) هجرت کردند و در راه الله جهاد کردند، آن گروه امید رحمت الله را دارند، و الله بسیار آمرزنده (و) مهربان است (و از رحمت و مغفرت خود آنها را محروم نمی‌سازد).');
INSERT INTO "badkhashani" VALUES(2,219,'2:219','از تو دربارۀ شراب و قمار می‌پرسند، بگو: در هر دو گناه بزرگ است، و برای مردم منافعی (دنیوی) هم دارند، و گناهشان از نفعشان بیشتر است. و از تو می‌پرسند کدام مال خود را انفاق کنند؟ بگو: اضافه از ضرورت تان را انفاق کنید، این چنین الله آیات را برای شما بیان می‌کند تا تفکر کنید.');
INSERT INTO "badkhashani" VALUES(2,220,'2:220','(این چنین الله نشانه‌ها را برای شما بیان می‌کند تا ) در دنیا و آخرت (تفکر کنید) و از تو درباره اموال يتيمها می‌پرسند، بگو اصلاح نمودن (اموال) آنها بهتر است (برای شما و برای آنها)، و اگر خلط سازید مال آنها را با مال خود حرجی نیست؛ چون آنها برادران شما اند؛ و الله مفسد را از مصلح میداند، و اگر الله می‌خواست شما را در مشقت می‌انداخت، و یقینا الله غالب (و) باحکمت است.');
INSERT INTO "badkhashani" VALUES(2,221,'2:221','و نکاح نکنید با زنان مشرک تا آنکه ایمان آورند، و البته کنیز مؤمن بهتر است از زن آزادی که مشرک باشد، اگرچه (حسن و جمال) زن مشرک شما را در تعجب اندازد، و زنان مسلمان را به نکاح مردان مشرک ندهید، تا آنکه ایمان آورند، البته غلام مؤمن بهتر است از مرد آزاد مشرک اگرچه (مال و جمالش) شما را در تعجب اندازد؛ آنها (زنان و مردان مشرک) مردم را به طرف دوزخ دعوت می‌دهند، و الله به فرمان خود بندگان را به‌سوی بهشت و آمرزش دعوت می‌دهد، و آیات (و احکام) خود را برای مردم بیان می‌کند تا یادآور شوند.');
INSERT INTO "badkhashani" VALUES(2,222,'2:222','و از تو دربارۀ حیض می‌پرسند، بگو: آن رنج و زیان است، و در حالت حیض از (مقاربت با) زنان دوری جویید، و با آنها هم بستری مکنید تا آنکه پاک شوند، پس وقتی پاک شدند با آنان مقاربت کنید، از آنجا که الله شما را دستور داده است، چون الله توبه کنندگان و پاکیزگان را دوست دارد.');
INSERT INTO "badkhashani" VALUES(2,223,'2:223','زنان شما کشت زار شما‌اند، پس هر گونه که بخواهید به کشتزار خود در آیید، و برای خود (توشۀ) پیش بفرستید، و از الله بترسید، و بدانید که شما با او روبرو خواهید شد، و مژده بده (ای پیغمبر، به نجات آن روز) مؤمنان را.');
INSERT INTO "badkhashani" VALUES(2,224,'2:224','و نام الله را در معرض قسم‌های خود قرار ندهید به غرض اینکه از نیکی و تقوا و اصلاح در بین مردم اجتناب کنید (چون الله به این سه کار امر کرده است پس نام او را برای منع از این کارها چگونه استعمال می‌کنید) و الله شنوا و دانا است.');
INSERT INTO "badkhashani" VALUES(2,225,'2:225','الله شما را به قسم‌های غير قصدی (بیهوده) تان مؤاخذه نمی‌کند، بلکه شما را به آنچه دلهایتان قصد کرده است بازخواست می‌کند، و الله بسیار آمرزنده (و) بردبار است.');
INSERT INTO "badkhashani" VALUES(2,226,'2:226','برای کسانی که قسم می‌خورند با زنان خود آمیزش جنسی نکنند (ایلاء) چهار ماه انتظار و (مهلت) است، پس اگر (از قسم یاد شده) رجوع کنند (مورد عفو قرار می‌گیرند زیرا) به يقين الله آمرزنده (و) مهربان است.');
INSERT INTO "badkhashani" VALUES(2,227,'2:227','و اگر اراده طلاق کنند (یعنی رجوع نکردند) پس یقینا الله شنوای دانا است (بر اینکه به زبان چه گفتند و در دل چه داشتند).');
INSERT INTO "badkhashani" VALUES(2,228,'2:228','و زنان طلاق داده شده باید به مدت سه حیض (سه بار عادت ماهانه) انتظار بکشند، اگر به الله و روز آخرت ایمان دارند به آنان جایز نیست که آنچه الله در رحم‌هایشان آفریده است بپوشانند (یعنی در صورت حمل به دروغ نگویند که عدت من گذشته است)؛ و شوهرانشان در باز گردانیدن آنها (به نکاح خود در وقت عدت) سزاوارترند، اگر قصد اصلاح دارند، و زنان بر مردان حقی دارند مثل آن حقی که مردان بر زنان دارند که باید بطريقة شایسته ادا نمایند (و لیکن) مردان بر زنان درجه برتری دارند، والله غالب باحکمت است.');
INSERT INTO "badkhashani" VALUES(2,229,'2:229','طلاق (که بعد از آن امکان رجوع است) دوبار است، پس از آن نگاه داشتن (زن) یا به خوبی است، یا به نیکی رها کردن است، و برای شما حلال نیست که چیزی از آن مالی (مهری) که به زنان داده بودید بگیرید، مگر اینکه (زن و شوهر) بترسند از اینکه احكام الله را برپا نمی‌توانند (در صورت باقی داشتن نكاح)، پس اگر ترسیدید از اینکه (زن و شوهر) احکام الله را برپا نمی‌دارند پس گناهی بر آنها نیست که زن فديه دهد (به شوهر خود برای خلاصی از نکاح او)، این حدود الله است، پس از آن تجاوز نکنید، و هر کس که (با مخالفت فرمان الهی) از حدود الله تجاوز کند، پس همان گروه ظالمان‌اند.');
INSERT INTO "badkhashani" VALUES(2,230,'2:230','و اگر (بار سوم) آن را طلاق داد، پس برای او حلال نیست مگر اینکه با شوهری دیگر ازدواج کند، پس اگر (شوهر دوم) او را طلاق داد، گناهی بر آنها نیست که دوباره به یکدیگر باز گردند، البته در صورتی که مطمئن باشند که حدود الهی را رعایت خواهند کرد. این حدود الله است آن را برای قومی که می‌دانند بیان می‌کند.');
INSERT INTO "badkhashani" VALUES(2,231,'2:231','و چون زنان را طلاق دادید و به پایان عدۀ خود نزدیک شدند، پس یا آنها را به خوبی (طريقۀ شرعی) نگاه دارید، و یا به خوبی (طريقۀ شرعی) رها کنید و هرگز آنان را برای ضرر رسانیدن به آنها نزد خود نگاه نکنید تا (در حق آنها) ظلم و از حد گذری کنید، و هرکه چنین کند پس يقينا به خویشتن ظلم کرده است، و آیات (احکام) الله را مورد استهزاء و تمسخر قرار ندهید، و یاد کنید نعمتی را که الله بر شما انعام کرده است، و (یاد کنید) آنچه را که الله از کتاب و حکمت (سنت پیغمبر) بر شما نازل کرده است. الله شما را به وسیله آن (کتاب) پند می‌دهد و از الله بترسید و بدانید که الله به هر چیز داناست.');
INSERT INTO "badkhashani" VALUES(2,232,'2:232','و چون زنان را طلاق دادید و به پایان عدۀ خود رسیدند، آنها را از اینکه با شوهران (قبلی) خود ازدواج کنند - چنانچه در میانشان به طرز پسندیده توافق برقرار گردید - منع نکنید، این همان چیزی است که هرکس از شما که به الله و روز آخرت ایمان داشته باشد، به آن پند داده می‌شود. آن برایتان پاکتر و بهتر است، و الله می‌داند و شما نمی‌دانید.');
INSERT INTO "badkhashani" VALUES(2,233,'2:233','و مادران اولاد خود را تا دو سال کامل شیر دهند، این حکم برای کسی است که بخواهد مدت شیر خوارگی را تمام کند، و بر کسی که اولاد داده شده (پدر) انفاق زنان شیرده، و لباس آنها به طريقة مشروع و پسندیده لازم است، هیچ کس مکلف ساخته نمی‌شود مگر به قدر توان او، و هیچ مادری نباید به سبب فرزندش ضرر بیند، و نه هیچ پدری به سبب فرزندش ضررمند شود، و بر وارث فرزند چنین چیزی (نفقه) لازم است، پس اگر پدر و مادر به رضا و مشورۀ یکدیگر جدایی طفل را از شیر بخواهند پس گناهی بر آنها نیست، و اگر بخواهید که برای اولاد خود دایه‌ای بگیرید، پس گناهی بر شما نیست؛ به شرط اینکه آنچه را که مقرر کرده‌اید، بطور شایسته پرداخت کنید، و از الله بترسید و بدانید که الله به آنچه که می‌کنید بیناست.');
INSERT INTO "badkhashani" VALUES(2,234,'2:234','و کسانی از شما که وفات می‌کنند و زنانی را بجا می‌گذارند (باید آن زنان) تا مدت چهار ماه و ده روز انتظار بکشند، پس چون مهلت (عدت) خود را به پایان رساندند پس بر شما گناهی نیست در آنچه که آنان به طور شایسته دربارۀ خود انجام دهند، و الله به آنچه که شما می‌کنید آگاه است.');
INSERT INTO "badkhashani" VALUES(2,235,'2:235','و بر شما گناهی نیست که به طور تعریض یا کنایه از زنان خواستگاری کنید، و یا در دلهایتان پنهان نمایید، الله میداند که شما آنها را یاد می‌کنید، مگر به آنها وعده پنهانی (نکاح) ندهید به جز آنکه سخنی نیک گویید و قصد عقد نکاح نکنید تا آنکه میعاد عدت به پایان برسد و بدانید که الله آنچه را که در دل‌های شما است می‌داند، پس از او بترسید، و بدانید که الله آمرزنده (و) بردبار است.');
INSERT INTO "badkhashani" VALUES(2,236,'2:236','برشما گناهی نیست اگر زنان را قبل از اینکه با آنها همبستری نکرده باشید یا برای آنها مهری تعیین نکرده باشید، طلاق دهید، آنان (زنان مطلقه) را به طور پسندیده، (به نوعی) بهره مند سازید، ثروتمند به اندازۀ خودش و تنگدست به اندازه خودش (ایشان را بهره مند سازند و این کاری است) شایسته بر ذمۀ نیکوکاران.');
INSERT INTO "badkhashani" VALUES(2,237,'2:237','و اگر زنان را پیش از اینکه با آنها همبستری کرده باشید طلاق دادید در حالیکه مهری برای آنها مقرر کرده‌اید. پس (بر ذمۀ شما پرداخت) نیم آنچه مقرر کرده‌اید لازم است، مگر اینکه (آن را به شما) ببخشند یا کسی که عقد نکاح در دست اوست (آن را) ببخشد، و اگر ببخشید (عفو کنید) به تقوا نزدیک‌تر است، و احسان را در میان خود فراموش نکنید، بی‌گمان الله به آنچه می‌کنید بیناست.');
INSERT INTO "badkhashani" VALUES(2,238,'2:238','بر ادای نمازها محافظت (و پایبندی) کنید و (خصوصا) نماز میانه (نماز عصر)، و منقاد (فروتنانه) برای الله بایستید.');
INSERT INTO "badkhashani" VALUES(2,239,'2:239','پس اگر (در میدان جهاد از ایستادن نماز) ترسیدید (باز هم نماز را ترک نکنید بلکه) پیاده یا سواره نماز را ادا کنید، پس وقتی ایمن شديد، الله را یاد کنید (همانطور که) به شما چیزهایی آموخت که خود نمی‌دانستید');
INSERT INTO "badkhashani" VALUES(2,240,'2:240','و کسانی از شما که وفات می‌کنند و (بعد از وفات خود) زنانی را بر جای می‌گذارند (باید) برای همسران خود وصیت کنند که (وارثان شوهر) آنها را تا یکسال (در خانه شوهر شان) بگذارند تا نفع ببرند و آنها را از خانه بیرون نکنند، و اگر خودشان بیرون رفتند، پس در آنچه آنها به طور شایسته درباره خود انجام می‌دهند گناهی بر شما نیست، و الله غالب با حکمت است.');
INSERT INTO "badkhashani" VALUES(2,241,'2:241','و برای زنان طلاق داده شده هدیه شایسته است (یعنی نفقه و سکنی) این حقی است بر مردان پرهیزگار.');
INSERT INTO "badkhashani" VALUES(2,242,'2:242','این چنین الله آياتش را برای شما بیان می‌کند تا بفهمید.');
INSERT INTO "badkhashani" VALUES(2,243,'2:243','آیا نشنیدی داستان آنان را که از ترس مرگ از خانه‌های خود برآمدند در حالیکه هزارها نفر بودند؟ پس الله به آنها گفت: بمیرید (و مردند)، باز آنها را زنده کرد، واقعا که الله بر مردم احسان و کرم دارد، و لیکن اکثر مردم شكر (احسان) الله را به جا نمی‌آورند.');
INSERT INTO "badkhashani" VALUES(2,244,'2:244','و در راه الله بجنگید (جهاد کنید و از مرگ نترسید) و بدانید که الله شنوا و داناست.');
INSERT INTO "badkhashani" VALUES(2,245,'2:245','کیست آنکه به الله قرض نیکو بدهد (در راه او انفاق کند) تا (الله) آنرا برای او چندین برابر کند؟ و الله (در رزق بندگان) تنگی و گشایش می‌آورد و به بارگاه او باز می‌گردید.');
INSERT INTO "badkhashani" VALUES(2,246,'2:246','آیا از (داستان) اشراف بنی اسرائیل که بعد از موسی بودند خبر نیافتی، وقتی به پیغمبر خود گفتند که برای ما پادشاهی مقرر کن تا (در زیر فرمان او) در راه الله بجنگیم، پیغمبرشان گفت: ممکن است بر شما جنگ فرض گردد باز شما جنگ نکنید؟ گفتند: ما را چه شده که در راه الله جنگ نکنیم؟ در حالیکه از سرزمین و فرزندان ما بیرون رانده شدیم؟ (لیکن) وقتی جنگ بر آنها لازم شد، جز عدۀ اندکی اعراض کردند، و الله به (احوال) ظالمان داناست.');
INSERT INTO "badkhashani" VALUES(2,247,'2:247','و پیغمبر‌شان به آنها گفت که البته الله طالوت را پادشاه شما فرستاده است، گفتند: چگونه او پادشاه ما باشد در حالی که ما نسبت به او سزاوار فرمانروایی هستیم و به او مال فراوان داده نشده است؟ (پیغمبرشان) گفت: همانا الله او را بر شما برگزیده و در جسم و دانش بر شما برتری بخشیده است و الله ملک (سلطنت) خود را به هر کسی بخواهد می‌دهد، و الله گشایشگر داناست.');
INSERT INTO "badkhashani" VALUES(2,248,'2:248','و پیغمبرشان برای آنها گفت که البته علامت پادشاهی او این است که آن صندوق (عهد) پیش شما بیاید، که در آن سکون و آرامش از جانب پروردگارتان می‌باشد، و در آن (صندوق) باقی ماندۀ آنچه است که آل موسی و آل هارون بر جای گذاشته‌اند، در حالیکه فرشته‌ها آن صندوق را حمل می‌کنند، يقينا در این دلیل (حقانیت طالوت) است برای شما اگر مؤمن هستید.');
INSERT INTO "badkhashani" VALUES(2,249,'2:249','پس وقتی طالوت با لشکر خود (از مردم و منطقۀ خود) جدا شد گفت: البته الله شما را به وسيلۀ (آب) نهر آزمایش می‌کند، پس هر کس از آن بنوشد پس او از من نیست، و هر کس از آن نچشد پس او از من است، به جز کسیکه به اندازۀ یک کف دست گرفته بنوشد، پس همه جز تعداد کمی‌شان از آن نوشیدند، پس وقتی که طالوت و مؤمنان همراهان او از نهر عبور کردند، (افرادی از آنان) گفتند: امروز طاقت جنگ با جالوت و لشکر او را نداریم، و کسانی که یقین داشتند که الله را ملاقات خواهند کرد، گفتند: چه بسا گروه کم بر گروه زیاد به اذن الله غالب می‌گردد و الله با صابران است.');
INSERT INTO "badkhashani" VALUES(2,250,'2:250','وقتی (طالوت و همراهان او) با جالوت و لشکر او روبرو شدند، گفتند: ای پروردگار ما! بر ما صبر و شکیبایی فروریز و ما را ثابت قدم بدار و ما را بر قوم کافر پیروز گردان.');
INSERT INTO "badkhashani" VALUES(2,251,'2:251','پس جالوت و لشکر او را به اذن الله شکست دادند، و داود جالوت را کشت، و الله داود را بادشاهی و پیغمبری داد، و به او از آنچه میخواست چیزهایی آموخت؛ و اگر الله بعضی از مردم را بوسیلۀ بعضی دیگر دفع نمی‌کرد، یقینا زمین تباه (و از فساد پر) می‌شد، و لیکن الله لطف و احسان بسیار بر همه عالم دارد.');
INSERT INTO "badkhashani" VALUES(2,252,'2:252','این آیات، آیات (کتاب) الله است که آن را بر تو تلاوت می‌کنیم و یقینا تو از جملۀ پیغمبران هستی.');
INSERT INTO "badkhashani" VALUES(2,253,'2:253','اینها پیغمبرانی هستند که بعضی آنها را بر بعضی دیگر فضیلت داده‌ایم، از آنها کسی هست که الله با او سخن گفت، و مراتب برخی (از ایشان) را بلند کرده است، و به عیسی پسر مریم معجزات واضح (و بیانگر حق) دادیم، و او را به وسيله روح القدس (جبرئیل) تقویت نمودیم و اگر الله می‌خواست آنانکه بعد از آنها بودند بعد از اینکه دلائل روشن به آنها آمد، با یکدیگر جنگ نمی‌کردند و لیکن اختلاف کردند (که آن سبب قتال گردید)، پس برخی از آنان ایمان آوردند، و برخی از آنان کفر را اختیار کردند، و (تأكيداً گفته می‌شود که) اگر الله می‌خواست با یکدیگر جنگ نمی‌کردند، و ليكن الله آنچه را بخواهد انجام می‌دهد.');
INSERT INTO "badkhashani" VALUES(2,254,'2:254','ای کسانی که ایمان آورده‌اید، از آنچه که به شما روزی داده‌ایم انفاق کنید پیش از اینکه روزی بیاید که در آن نه خرید و فروشی است و نه دوستی و نه سفارشی، و کافران همان ظالمان حقیقی‌اند.');
INSERT INTO "badkhashani" VALUES(2,255,'2:255','الله آن ذاتی است که هیچ معبودی برحق غیر از او وجود ندارد (که) همیشه زنده و پایدار است (تدبیر تمام کائنات در دست اوست)، او را نه پینکی می‌آید و نه خواب (یعنی از کائنات یک لحظه غافل نمی‌باشد) هرچه در آسمان‌ها و هر چه در زمین است خاص از اوست، کیست که نزد او شفاعت کند مگر به اجازه او، آنچه را که پیش (روی) آنها است و آنچه را که پشت سر آنها است می‌داند (یعنی از احوال حاضر و آینده انسانها باخبر است) و مردم از علم او آگاهی نمی‌یابند، مگر آن مقداری که او بخواهد. کرسی او آسمان‌ها و زمین را فرا گرفته است، و نگهداری آنها (آسمانها و زمین) او را خسته نمی‌سازد، و او عالیتر و بزرگتر است.');
INSERT INTO "badkhashani" VALUES(2,256,'2:256','در (قبول کردن دین اسلام) اجباری نیست (چون) به راستی که هدایت از گمراهی جدا شده است، پس هر کس که به طاغوت كفر ورزد، و به الله ایمان بیاورد، به راستی که به دستاویز محکمی چنگ زده است که قطع شدنی نیست، و الله شنوای داناست (به اقوال و افعال مردم).');
INSERT INTO "badkhashani" VALUES(2,257,'2:257','(البته) الله کارساز مؤمنان است که بیرون می‌آرد آنها را از تاریکی‌های معاصی به نور اطاعت، و آنانکه کفر ورزیدند کارساز آنها شیطان سرکش است که بیرون می‌آرد ایشان را از نور فطرت به‌سوی تاریکهای کفر، این گروه یاران آتش‌اند و در آن همیشه خواهند بود.');
INSERT INTO "badkhashani" VALUES(2,258,'2:258','آیا از (حال) کسی که الله به او پادشاهی بخشیده بود و با ابراهیم در مورد پروردگارش مجادله کرد خبر داری؟ وقتی ابراهیم گفت: پروردگار من آنست که زنده می‌کند و می‌میراند. (آن کافر) گفت: من (هم) زنده می‌کنم (کسی را بخواهم زنده می‌گذارم) و می‌میرانم (کسی را بخواهم به قتل می‌رسانم)، ابراهیم گفت: الله آفتاب را (هر روز) از جانب مشرق می‌آرد پس (اگر راستگو هستی) تو آفتاب را از مغرب بیار؟ پس آن که کفر ورزیده بود، حیران شد. و الله قوم ظالم را هدایت نمی‌کند.');
INSERT INTO "badkhashani" VALUES(2,259,'2:259','(و) یا در داستان آن شخص بیاندیش که از کنار قريه گذر کرد در حالیکه سقف‌های آن افتیده بود، گفت: الله اهل این قریه را بعد از مردن (و پوسیده شدن) چطور زنده خواهد کرد؟ پس الله او را صد سال میراند، باز او را زنده کرد، و گفت: (در این حالت) چقدر گذرانیدی؟ گفت: یک روز یا بخشی از روز. الله گفت: بلکه صد سال در این حال بودی، پس نظر کن به خوراک و نوشیدنی خود که تا حال تغیر نکرده است، و نظر کن به خر خود (که چگونه زنده می‌کنیم آن را)؟ (و خواستیم) که تو را نشانۀ (قدرت خود) برای مردم بگردانیم و نظر کن به استخوان‌های (خر خود) که چطور آن را پیوند می‌دهیم باز آن را با گوشت می‌پوشانیم؟ پس وقتی (قدرت الله) به آن شخص روشن شد گفت: می‌دانم که الله بر هر چیزی تواناست.');
INSERT INTO "badkhashani" VALUES(2,260,'2:260','و به یاد آور وقتی را که ابراهیم گفت: ای پروردگارم! به من نشان ده که چطور مرده‌ها را زنده می‌کنی؟ الله گفت: آیا ایمان نداری؟ ابراهیم گفت: بلی (ایمان دارم)، و لیکن می‌خواهم که دلم مطمئن شود، الله گفت: پس چهار پرنده را بگیر باز آنها را نزد خود قطعه قطعه کن باز هر قطعه از آنها را بر کوهی بگذار، باز آنها را بخوان، پس (می‌بینی که) دویده به نزد تو می‌آیند، و بدان که الله غالب باحکمت است.');
INSERT INTO "badkhashani" VALUES(2,261,'2:261','مثال کسانی که مالهایشان را در راه الله انفاق می‌کنند مانند دانه ایست که برویاند هفت خوشه را و در هر خوشه آن صد دانه باشد، و الله برای کسی که بخواهد، چندین برابر می‌دهد و الله دارای فضل گشاده (و) دانا است.');
INSERT INTO "badkhashani" VALUES(2,262,'2:262','آنانکه مال‌های خود را در راه الله انفاق می‌کنند، باز بعد از (انفاق کردن) منت نمی‌گذارند و نه اذیت می‌رسانند، پاداششان نزد پروردگارشان است، و نه ترسی بر آنهاست و نه غمگین می‌گردند.');
INSERT INTO "badkhashani" VALUES(2,263,'2:263','سخن نیک (به محتاجان) و گذشت (از اشتباهات آنها) از صدقه که در پی آن آزار باشد بهتر است، و الله (از مخلوق خود) مستغنی (و) بردبار است (که به زودی مؤاخذه نمی‌کند).');
INSERT INTO "badkhashani" VALUES(2,264,'2:264','ای کسانی که ایمان آورده‌اید، صدقه‌های خود را با منت گذاشتن و آزار رسانی برباد نکنید، مانند کسی که مال خود را برای تظاهر و خود نمایی در برابر مردم انفاق می‌کند در حالیکه به الله و روز آخرت ایمان ندارد، پس مثال آن (شخص منافق) مانند سنگ صافی است که بر بالای آن خاک (نشسته) است، پس باران شدید بر آن بارید، پس آن سنگ را سخت و صاف بر جایش گذاشت، مردمان ریاکار نیز از آنچه بدست آورده‌اند (آنچه را انفاق کرده اند) هیچ سودی (ثوابی) نمی‌برند، و الله قوم کافر را هدایت نمی‌کند.');
INSERT INTO "badkhashani" VALUES(2,265,'2:265','و مثال آنان که مال‌های خود را برای خشنودی الله و به خاطر یقین و باوری که (در دل) دارند انفاق می‌کنند، مانند باغی است که در تپه‌ای قرار دارد، و باران تند بر آن ببارد، پس میوه خود را دو چند دهد، و اگر باران تند بر آن نبارد پس شبنم (یا باران كم) هم به او کافی است (که میوه دهد)، و الله به اعمالی که انجام می‌دهید بیناست.');
INSERT INTO "badkhashani" VALUES(2,266,'2:266','آیا کسی از شما دوست دارد (می خواهد) که باغی از درختان خرما و انگور داشته باشد و در زیر درخت‌های آن جوی‌ها روان باشد و در آن باغ از هرقسم میوه برایش باشد، و در حالی که به پیری رسیده و اولاد ضعیف و ناتوان دارد، پس ناگهان باد گرمی که در آن آتش است به آن (باغ) برسد و بسوزد، این چنین الله آیات خود را برای تان بیان می‌کند تا باشد که فکر کنید (زیرا آتش ریا باغ نیکی‌ها را می‌سوزاند).');
INSERT INTO "badkhashani" VALUES(2,267,'2:267','ای کسانی که ایمان آورده‌اید، از مال‌های پاکیزه که کسب کرده‌اید، و از آنچه که ما از زمین برایتان بیرون آورده‌ایم انفاق کنید، و به دادن چیزهای ناپاک قصد نکنید، در حالی که شما آن چیز ناپاک را نمی‌گیرید، مگر آنکه از بدی آن چشم پوشی کنید، و بدانید که الله بی‌نیاز و ستوده است.');
INSERT INTO "badkhashani" VALUES(2,268,'2:268','شیطان شما را وعدۀ فقر و تنگدستی می‌دهد (از تنگ دستی می‌ترساند)، و شما را به بی‌حیائی دستور می‌دهد، و (لیکن) الله به شما وعده آمرزش و بخشش از جانب خود می‌هد و الله گشایشگر (دارای فضل فراوان) و دانا است.');
INSERT INTO "badkhashani" VALUES(2,269,'2:269','حکمت (علم با عمل) را به کسی که بخواهد می‌بخشد، و هر کس که حکمت به او داده شود يقينا به او خیر بسیار داده شده است، و پند نمی‌گیرد مگر خردمندان.');
INSERT INTO "badkhashani" VALUES(2,270,'2:270','و هر مالی را که در راه الله انفاق کنید و یا نذری را که بر خود لازم کنید پس یقینا الله آن را می‌داند و برای ظالمان هیچ مدد گاری نیست.');
INSERT INTO "badkhashani" VALUES(2,271,'2:271','اگر صدقه‌های خود را علنا و آشکارا بدهید پس خوب است، و اگر دادن آن را پوشیده دارید و به فقیران بدهید برای تان خوب‌تر است، و الله (به سبب انفاق کردن) بعض گناهان شما را محو می‌کند، و الله به آنچه می‌کنید باخبر است.');
INSERT INTO "badkhashani" VALUES(2,272,'2:272','(ای محمد!) هدایت آنها بر ذمۀ تو نیست، بلکه الله هر کسی را که بخواهد هدایت می‌کند، و هرچه انفاق کنید به فائده خودتان است (چون) انفاق نمی‌کنید مگر برای طلب رضای الله، و هر چیز خوبی که انفاق کنید به طور کامل به شما بازگردانده می‌شود (ثواب آن به شما داده می‌شود) و به شما ظلمی نمی‌شود.');
INSERT INTO "badkhashani" VALUES(2,273,'2:273','(صدقه‌ها) برای فقیر‌های است که در راه الله بازمانده‌اند (لذا) قدرت سفر کردن در زمین (برای حصول رزق) را ندارند، و به سبب پاکی‌شان از سوال و گدائی، شخص نادان آنها را ثروت مند می‌پندارد، آنها را از سیمایشان می‌شناسی، با اصرار چیزی از مردم نمی‌خواهند و هر آنچه از خوبی (مال حلال) انفاق کنید پس يقينا الله به آن آگاه است.');
INSERT INTO "badkhashani" VALUES(2,274,'2:274','آنان که مال‌های خود را شب و روز، پوشیده و آشکارا (در راه الله) انفاق می‌کنند، پس ثواب‌شان در نزد پروردگارشان است، و بر آنها (در روز قیامت) نه ترسی است و نه غمگین می‌شوند.');
INSERT INTO "badkhashani" VALUES(2,275,'2:275','آنانکه مال سود را می‌خورند از قبر‌هایشان برنمی خیزند مگر مانند کسی که شیطان به سبب تماس او را دچار دیوانگی کرده است، این (عذاب) به سبب آنست که گفتند: بیع (خرید و فروش) مانند سود است در حالیکه الله بيع (به غیر سود) را حلال قرار داده است و سود را حرام کرده است، پس کسی که نصیحتی از طرف پروردگارش به او آمد، باز از سودخوری اجتناب کرد، پس آنچه در گذشته به دست آورده است از خود او می‌باشد و معامله او با الله است، و هر کس به سود خوری بازگشت، پس ایشان دوزخیان‌اند و در آن همیشه و جاودانه می‌باشند.');
INSERT INTO "badkhashani" VALUES(2,276,'2:276','الله مال سود را از بین می‌برد و صدقات را (مال حلال که از آن صدقه داده می‌شود) افزایش و نمو می‌دهد، و الله هر ناسپاس گنهگار را دوست ندارد.');
INSERT INTO "badkhashani" VALUES(2,277,'2:277','يقينا آنان که ایمان آوردند، و اعمال نیک انجام دادند، و نماز را برپا کردند، و زکات را دادند، ثواب آنها نزد پروردگارشان است، و هیچ ترسی بر آنها (در قیامت) نیست و نه غمگین می‌شوند.');
INSERT INTO "badkhashani" VALUES(2,278,'2:278','ای کسانی که ایمان آورده‌اید، از عذاب الله بترسید و باقی مانده (معاملات) سود را ترک کنید اگر مؤمن هستید.');
INSERT INTO "badkhashani" VALUES(2,279,'2:279','پس اگر چنین نکردید (معامله سود را ترک نکردید) آگاه شوید به جنگی از طرف الله و رسول او، و اگر (از سود خواری) توبه کردید پس (اصل) سرمایه اموال تان از خودتان است، نه شما به کسی ظلم کنید و نه بر شما ظلم می‌شود.');
INSERT INTO "badkhashani" VALUES(2,280,'2:280','و اگر قرض دار شما تنگ دست بود پس تا فرا رسیدن آسانی، مهلت داده می‌شود، و اگر ببخشید (قرضدارتان را معاف کنید) برای شما بهتر است اگر می‌دانید.');
INSERT INTO "badkhashani" VALUES(2,281,'2:281','و از روزی بترسید که در آن روز به بارگاه الهی باز گردانیده می‌شوید باز هر شخص (جزای) آنچه را که کسب کرده بود به طور کامل دریافت خواهد کرد، و بر آنها هیچگونه ظلمی نمی‌شود.');
INSERT INTO "badkhashani" VALUES(2,282,'2:282','ای کسانی که ایمان آورده‌اید، هرگاه با یکدیگر معامله دَین تا میعاد مقرر (معلوم) کردید پس آن را بنویسید، و باید نویسنده عادل و با انصاف در میان شما بنویسد، و نویسنده نباید از نوشتن آن، طوری که الله به او آموخته است ابا ورزد، پس باید بنویسید، و کسی که به ذمه او حق (دَین) است باید بنویسد و املاء کند، و از پروردگارش بترسد، و چیزی از دَین (مردم) را کم نکند، پس اگر کسی که بر ذمه او حق (دَین) است بی‌عقل یا ضعیف یا ناتوان یا نمی‌توانست املا کند، پس باید ولی او به عدل املاء کند، و دو شاهد از مردانتان گواه بگیرید، و اگر دو مرد نبودند پس یک مرد و دو زن از شاهدانی که می‌پسندید (گواه بگیرید)، تا اگر یکی از آن دو (زن) فراموش کرد دیگری به او یادآوری کند. و وقتی گواهان برای گواهی دادن طلب شوند نباید ابا ورزند، و از اینکه دَین را (خواه کم باشد یا بسیار) تا مدت مقرر آن بنویسید خسته نشوید، این (نوشتن) نزد الله به عدالت نزدیکتر است، و هم سبب محکم داشتن گواهی است، و هم برای آنکه (در مقدار دَین) شک نکنید، مگر این که معامله نقدی باشد که در میان خود دست به دست می‌کنید، پس بر شما گناهی نیست در این که نوشته نکنید، و وقتی قصد خرید و فروش کردید گواه بگیرید، و نباید نویسنده و گواه ضررمند شوند، و اگر )چنین( کردید (به یکی از ایشان ضرر رسانیدید) پس يقينا از فرمان الله سرباز زدید )و ضرر آن به شما می‌رسد)، و از الله بترسید و الله به شما (دین تان را) می‌آموزد، و الله به هر چیزی دانا است.');
INSERT INTO "badkhashani" VALUES(2,283,'2:283','و اگر در سفر بودید و نویسنده‌ای نیافتید، پس چیزی را از (قرضدار) به طور گرو بگیرید، پس اگر یکدیگر تان را امین دانستید (و نخواستید از او گرو بگیرید) پس کسی که امین قرار داده شده باید امانتش را ادا کند، و باید از پروردگار خود بترسد، و شهادت را پنهان نکنید، و هر کس که آن را پنهان کند يقينا دلش گناهکار است، و الله به آنچه می‌کنید دانا است.');
INSERT INTO "badkhashani" VALUES(2,284,'2:284','هر آنچه در آسمان‌ها و در زمین است خاص از الله است. و اگر آنچه را که در دل‌های شما است اظهار کنید یا بپوشانید البته الله درباره آن با شما محاسبه می‌کند، (بعد از حساب) پس هر کسی را که بخواهد می‌آمرزد و هرکسی را که بخواهد عذاب می‌دهد، و الله بر هر چیزی قادر است.');
INSERT INTO "badkhashani" VALUES(2,285,'2:285','پیغمبر به آنچه که به او از طرف پروردگارش نازل شد ایمان آورد، و مؤمنان، همگی به الله و فرشتگان وی و به کتاب‌های وی و به پیغمبران وی ایمان آوردند (و گفتند که در ایمان آوردن) در میان هیچ یک از پیغامبران او فرق نمی‌آوریم، و گفتند: شنیدیم و اطاعت کردیم (قبول کردیم احکام الله را)، ای پروردگار ما! ما از تو آمرزش می‌خواهیم! و بازگشت به‌سوی تو است (یعنی به روز آخرت نیز ایمان داریم).');
INSERT INTO "badkhashani" VALUES(2,286,'2:286','الله هیچ کسی را مکلف نمی‌سازد مگر به قدر طاقت و توان او، (انسان) هرچه (از خیر) انجام داده به نفع خودش است و هرچه (از شر) انجام داده به ضرر خودش است، ای پروردگار ما! اگر فراموش کردیم یا به خطا رفتیم ما را مواخذه مکن، و ای پروردگار ما! بر ذمۀ ما بار گران را مگذار چنانکه آن (بار گران) را بر آنان که پیش از ما بودند نهادی، ای پروردگار ما! آنچه تاب و توانش را نداریم بر ما مگذار و از ما در گذر و ما را مغفرت کن و بر ما رحمتت را نازل کن (زیرا که) توئی مولی و کارساز ما، پس ما را بر قوم کافر پیروز گردان.');
INSERT INTO "badkhashani" VALUES(3,1,'3:1','الم. (معنای این حروف به الله معلوم است).');
INSERT INTO "badkhashani" VALUES(3,2,'3:2','الله آن ذاتی است که هیچ معبودی بر حق جز او نیست، همیشه زنده و مدبر تمامی کائنات است.');
INSERT INTO "badkhashani" VALUES(3,3,'3:3','(الله) کتاب (قرآن) را به حق بر تو نازل کرد که (دارای احکام واقعی و) تصدیق کنندۀ آنچه که پیش از آن بوده می‌باشد، و تورات و انجیل را نازل کرد.');
INSERT INTO "badkhashani" VALUES(3,4,'3:4','پیش از این کتاب، برای هدایت مردم، و این کتاب را که فرق کنندۀ حق و باطل است (نیز برای هدایت) نازل کرد، يقينا آنان که به آیات الله كفر ورزیدند، آنها عذاب سخت در پیش دارند، و الله غالب (و) انتقام گیرنده است.');
INSERT INTO "badkhashani" VALUES(3,5,'3:5','بی‌گمان چیزی در زمین و در آسمان از الله پوشیده نمی‌ماند.');
INSERT INTO "badkhashani" VALUES(3,6,'3:6','(الله) آن ذاتی است که شما را در رحمهای (مادران تان) هر طوری که بخواهد صورت می‌بخشد، هیچ معبودی برحق به جز او وجود ندارد (و او) غالب باحکمت است.');
INSERT INTO "badkhashani" VALUES(3,7,'3:7','(الله) آن ذاتی است که قرآن را بر تو نازل کرد که بعضی آن آیاتی محکم است که مفهوم آن واضح و روشن است، و آنها اصل قرآن است (که دیگر آیات در روشنی آن فهمیده می‌شود) و بعضی از آن «متشابهات» هستند (که مفهوم آن مشتبه و مخفی است و احتمال معانی دیگری هم دارند)، اما کسانی که در دل‌هایشان کجی است برای فتنه انگیزی و تاویل نادرست از آیات متشابه پیروی می‌کنند، در حالیکه مفهوم متشابه را به جز الله کسی نمی‌داند. و علمای راسخ در علم می‌گویند: به آن ایمان آوردیم (چون) همۀ قرآن (محکم و متشابه) از نزد پروردگارما است، و پند نمی‌گیرد (از تعلیمات قرآن) مگر صاحبان عقل و خرد.');
INSERT INTO "badkhashani" VALUES(3,8,'3:8','(علمای راسخ در علم می‌گویند که) ای پروردگار ما! بعد ازینکه ما را هدایت کردی دل‌های ما را کج و منحرف مکن و از طرف خود به ما رحمتی ببخش، چون تو بسیار بخشش کننده‌ای.');
INSERT INTO "badkhashani" VALUES(3,9,'3:9','ای پروردگار ما! يقينا تو مردم را در روزی که در (حق بودن) آن شکی نیست، یکجا خواهی کرد، بی‌گمان الله در وعدۀ خود مخالفت نمی‌کند.');
INSERT INTO "badkhashani" VALUES(3,10,'3:10','بی‌گمان آنان که کافر شده‌اند مالها و اولادشان آنها را هرگز از عذاب الله نجات نخواهد داد (بلکه) آنان هیزم (آتش افروز) دوزخ‌اند.');
INSERT INTO "badkhashani" VALUES(3,11,'3:11','مانند شیوۀ (رفتار) آل فرعون و آنان که پیش از ایشان بودند آیات ما را تکذیب نمودند، پس الله آنها را به سبب گناهانشان گرفت، و الله سخت عذاب دهنده است.');
INSERT INTO "badkhashani" VALUES(3,12,'3:12','به کفار بگو که عنقریب (در دنیا) مغلوب خواهید شد و (در آخرت) به طرف دوزخ حشر خواهید شد و دوزخ بد جایگاهی است.');
INSERT INTO "badkhashani" VALUES(3,13,'3:13','(ای یهودیان) يقينا برای شما نشانه است (در شکست مشرکین و غلبۀ مسلمانان) در روبرو شدن دو گروه (در روز بدر)، یک گروه در راه الله می‌جنگید و گروه دیگر کافر بودند (و در عدد زیاد بودند و) مسلمانان را با چشم‌های خود دو چند می‌دیدند. و الله به کمک خود هر کسی را که بخواهد تأیید می‌کند (قوت می‌بخشد)، يقيناً در این (شکست كفار) عبرتی است برای صاحبان بصیرت.');
INSERT INTO "badkhashani" VALUES(3,14,'3:14','برای مردم محبت شهوات (چیزهای مرغوب) از قبیل زنان و پسران و مال‌های فراوان از (نوع) طلا و نقره و اسپهای نشان دار و چارپایان و کشتزارها، آراسته شده است (لیکن) آن (همه) وسائل زندگانی دنیا است، و سرانجام و بازگشت نیک به نزد الله می‌باشد.');
INSERT INTO "badkhashani" VALUES(3,15,'3:15','بگو: آیا شما را به چیزی که بهتر از متاع دنیا است خبر دهم؟ (آن خبر این است که) برای آنان که تقوا پیشه کرده‌اند نزد پروردگارشان باغ‌های است که از زیر درخت‌های آن جوی‌ها جاری است، آنها در آن (باغ‌ها) همیشه می‌باشند، و زنان پاکیزه و رضامندی الله را دارند، و الله به بندگان خود بیناست.');
INSERT INTO "badkhashani" VALUES(3,16,'3:16','کسانی‌اند که می‌گویند! ای پروردگار ما! يقينا ما ایمان آوردیم، پس گناهان ما را بیامرز و ما را از عذاب دوزخ نگاه دار.');
INSERT INTO "badkhashani" VALUES(3,17,'3:17','(اینها‌اند که در سختی‌ها) صبر کنندگان‌اند و (در اقوال و افعال خود) صادقانند و (به طاعت و نیکی‌ها) مداومت کنندگان‌اند و (در راه الله) انفاق کنندگان‌اند و در سحرگاهان (از الله) آمرزش طلبان‌اند.');
INSERT INTO "badkhashani" VALUES(3,18,'3:18','الله گواهی داد (به وسیله دلائل تکوینی و تشریعی) بر اینکه هیچ معبودی بر حق جز او تعالی نیست و (نیز گواهی داده اند) فرشته‌ها و صاحبان علم (بر توحيد او تعالی) در حالی که الله (مدبر امور مخلوقات) به عدل و انصاف است (پس) هیچ معبودی برحق جز او تعالی نیست، غالب (و) باحکمت است.');
INSERT INTO "badkhashani" VALUES(3,19,'3:19','بی‌گمان دین (معتبر) نزد الله اسلام است، و آنان که کتاب داده شده‌اند (یهود و نصارا) اختلاف نکردند مگر بعد از اینکه آگاهی و علم (به حق بودن اسلام) به آنها آمد و (اختلاف نکردند مگر) به سبب سرکشی و حسدی که در میان‌شان وجود داشت، و هر کس به آيات الله كفر بورزد (و آنرا انکار کند)، پس (بداند که) الله زود حساب گیرنده است.');
INSERT INTO "badkhashani" VALUES(3,20,'3:20','پس اگر (کفار) با تو (دربارۀ دین حق) مجادله کردند، پس بگو من و هرکسی که از من پیروی می‌کند خود را برای الله منقاد و تسلیم ساخته‌ایم، و از اهل کتاب (یهود و نصارا) و از ناخوانان (مشرکین عرب) بپرس که آیا شما به الله منقاد شده‌اید؟ (یعنی کتاب و دین او را قبول کرده‌اید؟) پس اگر منقاد و تسلیم شدند، البته راهیاب شده‌اند، و اگر رویگردان شدند پس تنها رسانیدن (تبلیغ) بر ذمۀ تو است، و الله به بندگان بینا است.');
INSERT INTO "badkhashani" VALUES(3,21,'3:21','البته آنان که به آیات الله کفر می‌ورزند و پیغمبران را به ناحق می‌کشند، و کسانی از مردم را که به عدل و انصاف امر می‌کنند، می‌کشند پس آنها را به عذاب دردناکی مژده بده.');
INSERT INTO "badkhashani" VALUES(3,22,'3:22','این گروه آنانی‌اند که اعمال‌شان در دنیا و آخرت نابود و ضائع گردیده است، و برای آنها هیچ مددگاری نیست.');
INSERT INTO "badkhashani" VALUES(3,23,'3:23','آیا آنان را که حصۀ از علم (کتاب سماوی) داده شده‌اند ندیدی که به‌سوی كتاب الله فراخوانده می‌شوند تا میان آنها قضاوت کند، باز گروهی از آنها در حالی که اعراض کنان‌اند رو می‌گرداند.');
INSERT INTO "badkhashani" VALUES(3,24,'3:24','(سبب روگردانی شان) به این خاطر است که گفتند آتش دوزخ به ما نخواهد رسید مگر چند روزی اندک، و افتراهای‌شان در دینشان آنان را فریب داد.');
INSERT INTO "badkhashani" VALUES(3,25,'3:25','پس حال آنها چگونه خواهد بود وقتی که آنها را در روزی جمع کنیم که هیچ شکی در )آمدن( آن نیست، و به هر شخص (جزای) آنچه کرده است کاملا داده شود، و بر آنها ظلم نمی‌شود.');
INSERT INTO "badkhashani" VALUES(3,26,'3:26','بگو: ای پروردگار! تو متصرف و صاحب بادشاهی (جهان) هستی، بادشاهی را به هر کسی که بخواهی میدهی، و بادشاهی را از هر کسی که بخواهی سلب می‌کنی، و کسی را که تو بخواهی عزت می‌دهی، و کسی را که تو بخواهی ذلیل می‌کنی، تمامی نیکی‌ها تنها به دست تو است، بی‌گمان تو بر هر چیز قدرت کامل داری.');
INSERT INTO "badkhashani" VALUES(3,27,'3:27','(یکی از علامات قدرت تو این است که) شب را در روز داخل می‌کنی، و روز را در شب داخل می‌کنی، و زنده را از مرده بیرون می‌کنی، و مرده را از زنده بیرون می‌آوری، و هر کسی را که بخواهی بیشمار روزی میدهی.');
INSERT INTO "badkhashani" VALUES(3,28,'3:28','نباید مؤمنان کافران را به جای مؤمنان به دوستی بگیرند، و هر کس این چنین کند پس در مقابل عهد الله در هیچ مرتبه‌ای نیست، مگر اینکه خود را از ضرر آنها نگاه دارید، و الله شما را از (عظمت و جلال) خود می‌ترساند، و بازگشت شما به طرف او است.');
INSERT INTO "badkhashani" VALUES(3,29,'3:29','بگو: اگر آنچه که در سینه‌های تان دارید، بپوشانید یا اظهار کنید (در هر دو صورت) الله آن را می‌داند و (بلکه) الله آنچه که در آسمان‌ها و آنچه که در زمین است می‌داند، و (مانند علم خود) الله به هرچیز قدرت کامل دارد.');
INSERT INTO "badkhashani" VALUES(3,30,'3:30','روزی که هرشخص هر آنچه از نیکی و بدی انجام داده است، حاضر می‌یابد (در آن روز) دوست دارد (و آرزو می‌کند) که ای کاش در بین او و آنچه از بدی انجام داده است مسافۀ دور می‌بود (که بدی‌های خود را نمی‌دید) و الله شما را از (عذاب) خود می‌ترساند، و الله به بندگان خود مشفق و مهربان است.');
INSERT INTO "badkhashani" VALUES(3,31,'3:31','بگو: اگر شما الله را دوست دارید پس از من پیروی کنید تا الله شما را دوست بدارد، و گناهان شما را بیامرزد، والله آمرزندۀ مهربان است.');
INSERT INTO "badkhashani" VALUES(3,32,'3:32','بگو: از الله و رسول او اطاعت کنید، پس اگر (از اطاعت الله و رسول او) رویگردان شدند، پس (بدانند که کافر شده‌اند و) الله کافران را دوست ندارد.');
INSERT INTO "badkhashani" VALUES(3,33,'3:33','بی‌گمان الله آدم و نوح و آل ابراهيم و آل عمران را (برای پیغامبری) برگزید (و بر دیگر اهل عالم برتری داد).');
INSERT INTO "badkhashani" VALUES(3,34,'3:34','(در حالیکه همگی از یک نسل‌اند که) بعضی آنها از بعضی دیگر است، و الله شنوا و دانا است.');
INSERT INTO "badkhashani" VALUES(3,35,'3:35','(به یاد آور که) چون زن عمران گفت: ای پروردگارم، البته آنچه را که در شکم من است خاص به رضای تو نذر کردم (بر خود لازم کردم) تا (از کارهای دنیا) آزاد باشد، پس از من قبول کن، بی‌گمان تو شنوا و دانائی.');
INSERT INTO "badkhashani" VALUES(3,36,'3:36','پس چون آن را زائید گفت: ای رب من! من دختر زاده ام، و الله بر آنچه او زاده بود داناتر بود (و به طور حسرت گفت) و مرد مانند زن (کمزور) نمی‌باشد و من آن را مریم نام نهادم و او و اولاد او را از شر شیطان رانده شده در پناه تو می‌سپارم.');
INSERT INTO "badkhashani" VALUES(3,37,'3:37','پس پروردگارش (مریم) را قبول نیک کرد و او را نیک رویانید (بزرگ کرد)، و زکریا را سرپرست او ساخت، هرگاه زکریا داخل عبادتگاه می‌شد نزد او (مریم) خوردنی می‌یافت، زکریا گفت: ای مریم! این رزق به تو از کجا می‌آید؟ مریم گفت: این رزق از جانب الله می‌آید: بی‌گمان الله کسی را که بخواهد بدون حساب روزی می‌دهد (بدون زحمت و تکلیف).');
INSERT INTO "badkhashani" VALUES(3,38,'3:38','در آنجا زکریا پروردگار خود را به دعا خواند (و) گفت: ای پروردگارم! برای من از نزد خود نسل پاکیزه ببخش؛ بی‌گمان تو شنوندۀ دعائی.');
INSERT INTO "badkhashani" VALUES(3,39,'3:39','در حالیکه او (زکریا) در عبادتگاه نماز می‌خواند فرشته‌ها او را صدا زدند که الله تو را (به پسری بنام) یحیی مژده می‌دهد، که تصدیق کنندۀ کلمه الله (عیسی) است، و سرور و پیشوا، و دور از گناهان، و پیغمبری از صالحان است.');
INSERT INTO "badkhashani" VALUES(3,40,'3:40','زکریا گفت: ای پروردگارم! چطور مرا پسر خواهد بود؟ در حالیکه به عمر پیری رسیده ام. و زنم نازای است، (الله) گفت: هم چنان است، الله چیزی که بخواهد انجام میدهد.');
INSERT INTO "badkhashani" VALUES(3,41,'3:41','(زکریا) گفت: ای پروردگارم! برای من نشانه‌ای تعین کن (که از آن قرار گرفتن حمل را بدانم) الله گفت: نشانۀ تو این است که تا سه روز با مردم سخن نگویی، مگر به اشاره، و (به خاطر شکر) پروردگار خود را بسیار یاد کن و صبح و شام او را تسبیح بگو،');
INSERT INTO "badkhashani" VALUES(3,42,'3:42','و (به یاد آور) هنگامی که فرشته‌ها گفتند: ای مریم! البته الله تو را برگزیده است (برای عبادت خود) و پاکیزه ات گردانیده و تو را بر دیگر زنان عالم برتری داده است.');
INSERT INTO "badkhashani" VALUES(3,43,'3:43','ای مریم! پروردگارت را باعاجزی و تواضع اطاعت و عبادت کن، و سجده کن و با رکوع کنندگان رکوع کن (با نماز خوانان نماز بخوان).');
INSERT INTO "badkhashani" VALUES(3,44,'3:44','(این داستان مریم و زکریا) از خبرهای غیبی است که آن را به تو وحی می‌کنیم، و تو نزد آنها حاضر نبودی وقتی که قلم‌های خویش را می‌انداختند تا کدامشان کفالت و سرپرستی مریم را به عهده گیرد، و تو نزد آنها نبودی وقتی (در مورد پرورش مریم) با یکدیگر مناقشه و کشمکش می‌کردند.');
INSERT INTO "badkhashani" VALUES(3,45,'3:45','(یاد آور شو) وقتی که فرشته‌ها گفتند: ای مریم! البته الله تو را به کلمه خود که نامش عیسی پسر مریم است مژده می‌دهد، که در دنیا و آخرت والا مقام، و از جملۀ مقربان (دربار الهی) است.');
INSERT INTO "badkhashani" VALUES(3,46,'3:46','(و از عجائب آن پسر این است که) با مردم در گهواره و در وقت بزرگ سالی (نیز) سخن می‌گوید، و از جملۀ صالحان است.');
INSERT INTO "badkhashani" VALUES(3,47,'3:47','مریم گفت: ای پروردگارم! چطور صاحب فرزند می‌شوم؟ در حالیکه دست هیچ انسانی به من نرسیده است؟ (جبرئیل) گفت: همچنین است، الله هر چیزی را که بخواهد بوجود می‌آرد (زیرا سنت الله این است که) چون آفریدن چیزی را فیصله کند می‌گوید: موجود شو پس فورا موجود می‌شود.');
INSERT INTO "badkhashani" VALUES(3,48,'3:48','(دیگر اوصاف آن پسر این است که الله) کتاب و حکمت و تورات و انجیل را به او می‌آموزد.');
INSERT INTO "badkhashani" VALUES(3,49,'3:49','و او را (به حیث) پیغمبر به‌سوی بنی اسرائیل می‌فرستد تا (به مردم بگوید) البته من با معجزه‌ای از طرف پروردگارتان پیش شما آمده ام، (آن معجزه این است که) من از گل مانند شکل پرنده برای شما می‌سازم، باز در آن می‌دمم که به امر الله پرنده می‌شود، و کور مادر زاد و شخص مبتلا به مرض برص (پیس) را به امر الله علاج می‌کنم، و مرده را به امر الله زنده می‌کنم، و شما را از چیزی که (روزانه) می‌خورید و از چیزی که در خانه‌های خود (برای آینده) ذخیره می‌کنید خبر می‌دهم، يقينا درین (معجزه‌ها) نشانه‌ای (دلیل نبوت من) برای شما است اگر یقین کننده هستید.');
INSERT INTO "badkhashani" VALUES(3,50,'3:50','و (آمده ام تا) تصدیق کننده تورات باشم که پیش از من آمده بود، و (آمده ام) تا برای شما حلال گردانم بعضی چیزهای که در تورات بر شما حرام است، و به شما نشانه و دلیلی از نزد پروردگارتان آوردم، پس از الله بترسید و مرا اطاعت کنید.');
INSERT INTO "badkhashani" VALUES(3,51,'3:51','بی‌گمان الله (متصرف و) معبود من و (متصرف و) معبود شما است، پس او را عبادت کنید که همین راه راست است (نه که من و مادرم را بپرستید).');
INSERT INTO "badkhashani" VALUES(3,52,'3:52','باز وقتی عیسی از آنها احساس کفر کرد گفت: کیست یاور من (در دعوت) به‌سوی (دین) الله؟ اصحاب مخلص عیسی (حواریون) گفتند: ما یاوران (دین) الله هستیم (که) به الله ایمان آورده‌ایم، (لذا) گواه باش که ما مسلمانیم.');
INSERT INTO "badkhashani" VALUES(3,53,'3:53','ای پرودگار ما! ما به آنچه که تو نازل کرده‌ای ایمان آوردیم و از این پیغمبر متابعت کردیم، پس ما را همراه گواهان (راه حق) بنویس.');
INSERT INTO "badkhashani" VALUES(3,54,'3:54','و (یهودی‌ها برای کشتن عیسی) حیله و چاره کردند و الله (هم برای نجات عیسی) چاره جویی کرد و الله بهترین چاره جویان است.');
INSERT INTO "badkhashani" VALUES(3,55,'3:55','(به یادآور حيلۀ نجات عیسی را) وقتی که الله گفت: ای عیسی! البته (در آینده) تو را می‌میرانم (ليكن فعلا) به‌سوی خود بالا می‌برم، و تو را از شر کافران پاک می‌کنم، و آنان را که از تو متابعت کردند بر آنان که کافر شده‌اند بالاتر می‌گردانم، (و این فوقیت) تا روز قیامت (خواهد بود)، باز بازگشت شما به طرف من است، باز (در میان شما) در چیزی که با هم اختلاف می‌کردید فیصله خواهم کرد.');
INSERT INTO "badkhashani" VALUES(3,56,'3:56','پس کسانی که کفر ورزیدند آنها را در دنیا و آخرت عذاب سخت خواهم داد، و هیچ یار و مددگاری ندارند (که به آنان امیدی داشته باشند).');
INSERT INTO "badkhashani" VALUES(3,57,'3:57','و اما آنان که ایمان آوردند و کارهای نیک کردند پس الله ثواب آنها را بطور کامل می‌دهد، و الله ظالمان را دوست ندارد.');
INSERT INTO "badkhashani" VALUES(3,58,'3:58','این کلام (قرآن) را بر تو تلاوت می‌کنیم (که این) از جملۀ آیات ربانی و سرشار از نصیحت‌های حکیمانه است.');
INSERT INTO "badkhashani" VALUES(3,59,'3:59','يقينا مثال عیسی (در آفریدن بدون پدر) نزد الله مانند مثال آدم است که او را از خاک آفرید باز به او فرمود: باش، پس شد.');
INSERT INTO "badkhashani" VALUES(3,60,'3:60','این (سخن) حق (بنده و رسول بودن عیسی) از نزد پروردگار توست پس (تو ای مخاطب) از جملۀ شک کنندگان مباش.');
INSERT INTO "badkhashani" VALUES(3,61,'3:61','پس هر کس در این باره پس از آنکه علم و دلیل قطعی به تو رسیده است با تو به مجادله پرداخت پس بگو )مخالفین را که( بیایید ما اولاد خود را، و شما اولاد خود را و ما زنان خود را و شما زنان خود را طلب کنیم و ما خود حاضر شویم و شما هم خود حاضر شوید پس دست دعا و عاجزی به‌سوی الله دراز می‌کنیم و لعنت الله را بر دروغگویان قرار می‌دهیم.');
INSERT INTO "badkhashani" VALUES(3,62,'3:62','يقينا همين است قصۀ راست (دربارۀ عیسی)، و هیچ معبودی برحق غیر از الله نیست، بی‌گمان الله غالب و باحکمت است.');
INSERT INTO "badkhashani" VALUES(3,63,'3:63','پس اگر روی گرداندند (از توحید، پرواه مکن چون) بی‌گمان الله به حال مفسدان دانا است.');
INSERT INTO "badkhashani" VALUES(3,64,'3:64','بگو: ای اهل کتاب بیایید به‌سوی سخنی که بین ما و شما مشترک است (آن سخن این است) که غیر از الله کسی را عبادت نکنیم، و چیزی را شریک او نسازیم، و بعض ما بعض دیگر را بجز الله، رب (مالک تحریم و تحلیل) قرار ندهد، پس اگر (ازین دعوت) رویگردان شدند پس (ای اهل کتاب) گواه باشید که ما مسلمانیم (منقاد حكم الله‌ایم و بس).');
INSERT INTO "badkhashani" VALUES(3,65,'3:65','ای اهل کتاب چرا دربارۀ ابراهيم مجادله می‌کنید؟ در حالیکه تورات و انجیل نازل نشده مگر بعد از ابراهیم، آیا نمی‌فهمید؟.');
INSERT INTO "badkhashani" VALUES(3,66,'3:66','آگاه باشید، شما همان گروهی هستید که دربارۀ چیزی که به آن علم داشتید مجادله کردید پس چرا دربارۀ چیزی که به آن علم ندارید مجادله می‌کنید؟ و الله (هرچیز را) می‌داند و شما نمی‌دانید.');
INSERT INTO "badkhashani" VALUES(3,67,'3:67','(حقیقت این است که) ابراهیم نه یهودی بود و نه نصرانی، و ليكن از شرک متنفر و تسلیم فرمان الله بود، و از جملۀ مشرکان نبود.');
INSERT INTO "badkhashani" VALUES(3,68,'3:68','يقينا نزدیک‌ترین مردمان (در راه توحید) به ابراهیم کسانی بودند که از او پیروی کردند، و (نیز) این پیغمبر و آنان که به او ایمان آوردند و الله کارساز و مددگار مؤمنان است.');
INSERT INTO "badkhashani" VALUES(3,69,'3:69','گروهی از اهل کتاب آرزو داشتند که شما را گمراه سازند، و (در حقیقت) گمراه نمی‌سازند مگر خود را و (لیکن) نمی‌فهمند.');
INSERT INTO "badkhashani" VALUES(3,70,'3:70','ای اهل کتاب! چرا به آيات الله كفر می‌ورزید (انکار می‌کنید؟) در حالیکه شما (نشانه‌های نبوت محمد ج را در کتب خودتان می‌بینید و در دل به حق بودن آن) گواهی می‌دهید.');
INSERT INTO "badkhashani" VALUES(3,71,'3:71','ای اهل کتاب، چرا حق را به باطل می‌پوشانید؟ و حق را (آگاهانه) کتمان می‌کنید؟ در حالیکه میدانید.');
INSERT INTO "badkhashani" VALUES(3,72,'3:72','و گروهی از اهل کتاب (به پیروان خود) گفتند: به آنچه بر مؤمنان نازل شده در اول روز ایمان آورید و در آخر روز به آن کفر ورزید (از ایمان خود برگردید)، تا شاید (این حال شما را دیده) از دین خود برگردند.');
INSERT INTO "badkhashani" VALUES(3,73,'3:73','(و می‌گویند) جز به کسی که از دین شما پیروی کند ایمان نیاورید، بگو: يقينا هدایت (صحیح) همان هدایت الله است (و نیز می‌گویند: ایمان نیاورید به) کسی که مثل آنچه به شما داده شده، داده شود، (یا اینکه کسی بتواند) با شما در نزد پروردگارتان مجادله کند، بگو: (ای پیغمبر)، فضل و رحمت بدست الله است، آن را به کسی که بخواهد می‌دهد، و الله دارای فضل گشاده (و) دانا است.');
INSERT INTO "badkhashani" VALUES(3,74,'3:74','و الله کسی را که بخواهد به رحمت خود مخصوص می‌گرداند، و الله دارای فضل بزرگ است.');
INSERT INTO "badkhashani" VALUES(3,75,'3:75','و از اهل کتاب کسی است که اگر بطور امانت مال بسیار به او بسپاری آن را به تو بر می‌گرداند، و از آنان کسی است که اگر بطور امانت یک دینار را به او بسپاری، آنرا دوباره به تو برنمی‌گرداند، مگر اینکه همیشه بالای سرش ایستاده باشی، این (خیانت) به سبب آن است که گفتند: ما در برابر بی‌سوادان مسئول نیستیم، و بر الله دروغ نسبت می‌دهند، در حالیکه آنان خود می‌دانند.');
INSERT INTO "badkhashani" VALUES(3,76,'3:76','بلی، هرکس به عهد خود وفا کند و پرهیزگار باشد (پس او نزد الله محبوب است و) بی‌گمان الله پرهیزگاران را دوست دارد');
INSERT INTO "badkhashani" VALUES(3,77,'3:77','يقينا آنان که عهد و پیمان الله و سوگندهایشان را به بهای حقیر دنیا می‌فروشند این گروه در آخرت هیچ سهمی ندارند، و الله با آنها سخن نمی‌گوید، و در روز قیامت به طرف آنها نظر نمی‌کند، و آنها را (از کفر) پاک نمی‌سازد، و برای آنها عذاب دردناک است.');
INSERT INTO "badkhashani" VALUES(3,78,'3:78','و البته گروهی از اهل کتاب هست که زبان‌های خود را در وقت خواندن کتاب (تورات) می‌پیچانند تا شما آن را از کتاب حساب کنید، حالیکه آن از تورات نیست، و می‌گویند: آن از جانب الله است، و در حقیقت از جانب الله نیست، و بر الله دروغ می‌بندند در حالیکه می‌دانند.');
INSERT INTO "badkhashani" VALUES(3,79,'3:79','برای هیچ بشری مناسب نیست که الله به او کتاب و حکم و پیغامبری بدهد باز به مردم بگوید الله را گذاشته بندگان من شوید، و ليكن )مناسب است که بگوید( به سبب آنچه می‌آموزید و می‌خوانید بندگان الله باشید.');
INSERT INTO "badkhashani" VALUES(3,80,'3:80','و به شما فرمان نمی‌دهد که فرشته‌ها و پیغامبران را متصرف و معبودان خود قرار دهید، آیا (معقول است که) شما را به کفر دستور دهد پس از اینکه شما مسلمان شدید؟!.');
INSERT INTO "badkhashani" VALUES(3,81,'3:81','و یادآور شو وقتی را که الله از پیغمبران عهد محکم گرفت که هرگاه به شما کتاب و حکمت دادم باز پیغمبری که تصدیق کننده آنچه با شما است نزد شما آید، حتما به او ایمان آرید و او را مدد کنید، فرمود:آیا اقرار کردید (به این عهد) و عهد محکم مرا در باره آن قبول کردید؟ گفتند: اقرار کردیم، (الله) فرمود: پس گواه باشید (بر یکدیگر) و من هم با شما از جملۀ گواهان می‌باشم.');
INSERT INTO "badkhashani" VALUES(3,82,'3:82','پس هرکس پس از آن (از دین اسلام) روی گرداند پس ایشان فاسقان‌اند.');
INSERT INTO "badkhashani" VALUES(3,83,'3:83','آیا دینی غیر از دین الله را می‌خواهند؟ در حالیکه هر چه در آسمان‌ها و زمین است به خوشی یا اجبار منقاد و تسلیم او‌اند، و به‌سوی او بازگردانیده می‌شوند.');
INSERT INTO "badkhashani" VALUES(3,84,'3:84','بگو: (ما دینی غیر از دین الله را نمی‌خواهیم بلکه) به الله و به آنچه بر ما، و به آنچه بر ابراهیم و اسماعیل و اسحاق و یعقوب و نبیره‌های یعقوب نازل شده، و آنچه به موسی و عیسی و دیگر پیغمبران از طرف پروردگارشان داده شده، ایمان آوردیم، بین هیچ یک از آنها (در ایمان آوردن) فرق نمی‌گذاریم، و ما تسلیم و منقاد الله هستیم و بس.');
INSERT INTO "badkhashani" VALUES(3,85,'3:85','و هرکه دینی غیر از اسلام اختیار کند، هرگز از او پذیرفته نمی‌شود و در آخرت از جملۀ زیان کاران خواهد بود.');
INSERT INTO "badkhashani" VALUES(3,86,'3:86','چگونه الله قومی را هدایت کند که بعد از ایمانشان کافر شدند (و بعد از اینکه) گواهی دادند که پیغمبر بر حق است (و بعد از اینکه) دلائل روشن به آنها آمد، و الله قوم ظالم را هدایت نمی‌کند (به منزل مقصود نمی‌رساند).');
INSERT INTO "badkhashani" VALUES(3,87,'3:87','(این گروه) سزایشان این است که لعنت الله و فرشته‌ها و همه مردمان بر آنها است.');
INSERT INTO "badkhashani" VALUES(3,88,'3:88','(در حالیکه) در آن (لعنت) همیشه‌اند پس عذاب از آنها تخفیف نخواهد شد، و به آنها مهلت داده نشده (که از عذاب بیرون شوند)، و به آنان نگاه کرده نمی‌شود.');
INSERT INTO "badkhashani" VALUES(3,89,'3:89','مگر آنان که بعد از آن توبه کردند، و عمل خود را اصلاح کردند، پس (امید است که آنها بخشیده شوند) بی‌گمان الله آمرزندۀ مهربان است.');
INSERT INTO "badkhashani" VALUES(3,90,'3:90','البته آنان که بعد از ایمان‌شان کافر شدند، باز بر کفر خود افزودند، هرگز توبه آنها قبول نمی‌شود، و این گروه، گمراهان‌اند.');
INSERT INTO "badkhashani" VALUES(3,91,'3:91','البته آنان که کافر شدند و در حالت کفر مردند، اگر زمین را پر از طلا کنند و آن را به عنوان فديه بپردازند، هرگز از هیچ کسی از آنها قبول نمی‌شود، و برایشان عذاب دردناک است، و هیچ مدد گاری ندارند (که به آنها امید داشته باشند).');
INSERT INTO "badkhashani" VALUES(3,92,'3:92','هرگز (مرتبۀ) نیکی را حاصل کرده نمی‌توانید تا از مالی که دوست می‌دارید (در راه الله) انفاق کنید، و الله از آنچه (در راه او) انفاق می‌کنید باخبر است (و اجر آن را به شما میدهد).');
INSERT INTO "badkhashani" VALUES(3,93,'3:93','تمام خوردنی‌ها برای بنی اسرائیل حلال بود، به جز آنچه يعقوب پیش از نازل شدن تورات بر خود ممنوع قرار داده بود، بگو: تورات را بیارید و آن را بخوانید؛ اگر راستگو هستید )که این‌ها در تورات حرام بوده است).');
INSERT INTO "badkhashani" VALUES(3,94,'3:94','پس هر کس بعد از این (تفصيل)، بر الله افترا ببندد، پس ایشان ظالمان‌اند.');
INSERT INTO "badkhashani" VALUES(3,95,'3:95','بگو: الله راست گفته است، پس از دین ابراهیم که یگانه پرست و به حق مایل و از شرک بیزار بود، و از جمله مشرکان نبود، پیروی کنید.');
INSERT INTO "badkhashani" VALUES(3,96,'3:96','بی‌گمان اولین خانۀ (عبادتگاهی) که برای مردمان بنا نهاده شد خانه‌ایست که در مکه قرار دارد، محل برکت و سبب هدایت برای عالميان است.');
INSERT INTO "badkhashani" VALUES(3,97,'3:97','در آن (خانه و گرد و پیش آن) علامت‌های واضح است (که یکی از آن) جای ایستادن ابراهیم (در وقت اعمار بيت الله) است و (دیگر این که) هرکه در آن (و حدود آن) داخل شود، در امان است، و الله حج خانۀ کعبه را بر مردمانی که توانایی رفتن به آنجا را دارند واجب گردانیده است (توان رسیدن به آن جا از لحاظ مالی و جانی و وسائل راه را داشته باشند) و هر کس کفر بورزد (حج و دیگر احکام الله را انکار کند، ضرر و نقصانی بر او تعالی نیست) زیرا او تعالی بدون شک از تمام جهانیان بی‌نیاز است.');
INSERT INTO "badkhashani" VALUES(3,98,'3:98','بگو: ای اهل کتاب! چرا به آیات الله كفر می‌ورزید (احکام واضح الله را انکار می‌کنید؟) در حالیکه الله به آنچه می‌کنید گواه (و عالم) است.');
INSERT INTO "badkhashani" VALUES(3,99,'3:99','بگو: ای اهل کتاب! (يهود و نصارا) چرا کسی را که ایمان می‌آورد از راه الله (دین اسلام) منع می‌کنید، (و) آنرا کج و غلط معرفی می‌کنید، و شما (بر این کار خود) گواه هستید، و الله از آنچه می‌کنید غافل نیست.');
INSERT INTO "badkhashani" VALUES(3,100,'3:100','ای کسانی که ایمان آورده‌اید! اگر از گروهی اهل کتاب اطاعت کنید، شما را بعد از ایمان تان به (حالت) كفر باز می‌گردانند.');
INSERT INTO "badkhashani" VALUES(3,101,'3:101','و چطور کفر می‌ورزید (کفر را قبول می‌کنید) در حالیکه آيات الله (قرآن) بر شما تلاوت می‌شود (تعليم داده می‌شوید)، و رسول او (سنت پیغمبر) در میان شما است، و هر کس دین الله را محکم بگیرد، پس یقینا به راه راست هدایت شده است.');
INSERT INTO "badkhashani" VALUES(3,102,'3:102','ای کسانیکه ایمان آورده‌اید! از الله بترسید آن طور که سزاوار ترسیدن از اوست، و نباید بمیرید مگر آنکه مسلمان باشید (و تا سرحد مرگ به احکام اسلام پابند باشید).');
INSERT INTO "badkhashani" VALUES(3,103,'3:103','وهمه یکجا به ریسمان (دین) الله چنگ بزنید (و آنرا محکم بگیرید) و متفرق نشوید و نعمتی را که الله بر شما انعام کرده بود، یاد کنید وقتی که دشمن یکدیگر بودید، پس الله میان دل‌های شما الفت و محبت ایجاد کرد و به نعمت الله (با هم) برادر گشتید، در حالیکه بر کناره گودالی از دوزخ بودید، پس الله شما را از آن نجات بخشید، این چنین الله آیات خود را به شما بیان می‌کند تا راه یاب شوید.');
INSERT INTO "badkhashani" VALUES(3,104,'3:104','و باید از شما گروهی باشند که مردم را به نیکی دعوت دهند، و مردم را به کارهای نیک (و درست) دستور دهند و از کارهای بد (و خلاف شرع) منع کنند، و این جماعت کامیاب‌اند.');
INSERT INTO "badkhashani" VALUES(3,105,'3:105','و ( شما مسلمانان) مانند کسانی مشوید که پراگنده شدند و اختلاف کردند بعد از اینکه دلائل واضح به آنها آمد، و (این گروه) برای‌شان عذاب بزرگ است.');
INSERT INTO "badkhashani" VALUES(3,106,'3:106','(آن عذاب در) روزی است که بعضی چهره‌ها سفید و درخشان می‌شود و چهره‌های (دیگر) سیاه می‌شود، پس کسانی که چهره‌هایشان سیاه گردیده است (برایشان گفته می‌شود) آیا پس از ایمان تان (و آمدن دلائل روشن ) کافر شدید؟ پس به سبب اینکه کفر می‌ورزیدید عذاب را بچشید.');
INSERT INTO "badkhashani" VALUES(3,107,'3:107','و اما کسانی که چهره‌هایشان سفید و درخشان گردیده است، پس آنها در رحمتی از جانب الله (جنت) هستند و آنان برای همیشه در آنجا می‌باشند.');
INSERT INTO "badkhashani" VALUES(3,108,'3:108','این آیات الله است که مشتمل بر حق و حقیقت‌اند، و آن را بر تو (ای پیغمبر) تلاوت می‌کنیم و الله هیچگونه ظلمی را برای مردمان عالم نمی‌خواهد.');
INSERT INTO "badkhashani" VALUES(3,109,'3:109','و (چگونه ظلم را بخواهد در حالیکه) هر چه در آسمان‌ها و هر چه در زمین است (از مخلوقات) تنها از الله است، و تمامی امور (معاملات بشر) به طرف الله باز گردانیده می‌شود.');
INSERT INTO "badkhashani" VALUES(3,110,'3:110','شما بهترین امتی هستید که برای (نفع) مردم آفریده شده‌اید (مردم را) به کارهای نیک امر می‌کنید، و از کارهای بد (و ناجائز) منع می‌کنید، و به الله ایمان دارید، و اگر اهل کتاب (مثل شما) ایمان می‌آوردند برایشان خیر (و بهتر) بود (لیکن) تعداد (کمی) از آنها مؤمن‌اند، و بیشترشان فاسق‌اند.');
INSERT INTO "badkhashani" VALUES(3,111,'3:111','(كفار اهل کتاب) ضرری به شما رسانیده نمی‌توانند به جز آزار (اندک) و اگر با شما جنگ کنند به شما پشت می‌گردانند (هزیمت می‌یابند) باز مدد نمی‌شوند.');
INSERT INTO "badkhashani" VALUES(3,112,'3:112','هر جایی که باشند بر آنها مهر خواری و حقارت زده شده است، مگر اینکه در پناه الله یا در پناه مردم باشند (دین اسلام را قبول کنند، و یا از قومی مدد طلب کنند) و به غضبی از جانب الله رجوع نموده‌اند، و مهر فقر و تنگدستی بر آنها زده شده است، این (همه) به سبب آن بود که به آيات الله كفر می‌ورزیدند، و پیغمبران را به ناحق قتل می‌کردند، این (کفر و تجاوز بر پیغمبران) به سبب آن بود که (از دستورات الهی) نافرمانی می‌نمودند و از حد تجاوز می‌کردند.');
INSERT INTO "badkhashani" VALUES(3,113,'3:113','آنان همه (اهل کتاب با هم) برابر نیستند (بلکه) از اهل کتاب گروهی هست که (برای عبادت الله) ثابت قدم ایستاده می‌باشند، و در اوقات شب سجدہ کنان آيات الله را تلاوت می‌کنند، (در تهجد مشغول اند).');
INSERT INTO "badkhashani" VALUES(3,114,'3:114','به الله و روز آخرت ایمان دارند، و (مردم را) به کارهای نیک دستور می‌دهند، و از کارهای بد (و خلاف شرع) منع می‌کنند، و در کارهای خیر شتاب می‌کنند، و این گروه (اهل کتاب) از جملۀ مردمان صالح‌اند.');
INSERT INTO "badkhashani" VALUES(3,115,'3:115','و هر آنچه از نیکی و کار خیر انجام دهند پس هرگز به هدر نخواهد رفت، و الله به پرهیزگاران داناست.');
INSERT INTO "badkhashani" VALUES(3,116,'3:116','البته آنان که کفر ورزیدند مال و اولادشان چیزی از عذاب الله را از آنها دفع نخواهد کرد، و ایشان یاران دوزخ‌اند و در آن همیشه می‌باشند.');
INSERT INTO "badkhashani" VALUES(3,117,'3:117','مثال آنچه کفار در زندگانی دنیا انفاق می‌کنند، مثال بادی است که در آن سرمای سخت است که بر کشت و زراعت قومی که بر خود ظلم کرده‌اند برسد، پس آنرا نابود کند، و الله بر آنها ظلم نکرده است، و لیکن (خود آنها) به جان‌های خویش ظلم می‌کنند.');
INSERT INTO "badkhashani" VALUES(3,118,'3:118','ای کسانی که ایمان آورده‌اید! از غیر خود (از غیر مسلمانان) محرم راز نگیرید (مردم دیگر را) که (ایشان) از رساندن هیچگونه ضرر (و فساد انداختن در میان شما) کوتاهی نمی‌کنند، و دوست دارند که شما به هر رنج و مشقت گرفتار شوید، چون دشمنی از دهان ایشان ظاهر شده است، و آنچه (از دشمنی که) در سینه‌هایشان پوشیده می‌دارند بزرگتر است، البته آیات را برای شما بیان کردیم اگر (عقل و خرد دارید) بیاندیشید.');
INSERT INTO "badkhashani" VALUES(3,119,'3:119','خبردار (ای مسلمانان) شما مردمی هستید که آنها را دوست دارید، و آنها شما را دوست ندارند (چون) شما به تمامی کتاب‌های الهی ایمان دارید، و هر وقت با شما روبرو شوند می‌گویند: ایمان آورده‌ایم، و چون در خلوت بروند از شدت قهر و غضب بر شما، انگشتان خود را با دندان می‌گزند، بگو: به (سبب) قهر و غضبی که دارید، بمیرید، بی‌گمان الله به راز سینه‌ها داناست.');
INSERT INTO "badkhashani" VALUES(3,120,'3:120','اگر به شما خوبی و راحتی برسد غمگین می‌شوند و اگر بدی و ناراحتی به شما برسد از آن خوشحال می‌شوند، و اگر صبر کنید و پرهیزگاری پیشه کنید، فریب و حیلۀ آنها هیچ ضرری به شما رسانیده نمی‌تواند، (چون) بی‌گمان الله به آنچه می‌کنند احاطه دارد.');
INSERT INTO "badkhashani" VALUES(3,121,'3:121','و به یاد آور وقتی را که صبحگاهان از میان خانواده ات بیرون شدی و جای مؤمنان را (در سنگرها به قصد جهاد با مشرکین) مشخص کردی، و الله شنوا و دانا است.');
INSERT INTO "badkhashani" VALUES(3,122,'3:122','چون دو گروهی از شما خواستند سستی کنند، و الله کارساز‌شان بود (که آنها را از سستی نگاه داشت) و مؤمنان باید بر الله توکل کنند.');
INSERT INTO "badkhashani" VALUES(3,123,'3:123','البته الله شما را در (جنگ) بدر نصرت داد، در حالیکه (در نظر دشمن) کم و حقیر بودید، پس از الله بترسید تا شکر (او را) به جا آرید.');
INSERT INTO "badkhashani" VALUES(3,124,'3:124','وقتی به مؤمنان می‌گفتی: آیا برای شما کافی نیست که پروردگارتان به وسیله سه هزار فرشتۀ فرود آورده شده از آسمان، شما را کمک کند؟.');
INSERT INTO "badkhashani" VALUES(3,125,'3:125','بلی، اگر صبر کنید و پرهیزگار شوید و کفار (با جوش و خروش خود) به مقابله شما بیایند، الله شما را با پنج هزار فرشتۀ نشاندار، مدد خواهد کرد.');
INSERT INTO "badkhashani" VALUES(3,126,'3:126','و الله آن (نصرت) را جز بشارتی برای شما نگردانید، و تا دل‌های تان با آن مطمئن شود؛ و نصرت و پیروزی (برای تان) نیست مگر از جانب الله غالب با حکمت (و اینکه نصرت الله به آمدن فرشته‌ها موقوف نیست).');
INSERT INTO "badkhashani" VALUES(3,127,'3:127','(الله شما را مدد کرد) تا (به وسيلۀ شما) طائفۀ از کافران را نابود یا سرنگون و مغلوب سازد، و ناکام و شکست خورده (از میدان جنگ) باز گردند.');
INSERT INTO "badkhashani" VALUES(3,128,'3:128','(اختیار) هیچ کاری بدست تو نیست، (الله) یا توبۀ آنها را قبول می‌کند، و یا آنها را عذاب می‌دهد، چرا که آنان ظالم‌اند.');
INSERT INTO "badkhashani" VALUES(3,129,'3:129','(قبول توبه یا عذاب به دست الله است زیرا) هر چه در آسمان‌ها و هر چه در زمین است (تنها) از الله است، هر کس را که بخواهد، عفو می‌کند، و هر کس را که بخواهد، عذاب می‌دهد، و الله آمرزندۀ مهربان است.');
INSERT INTO "badkhashani" VALUES(3,130,'3:130','ای کسانی که ایمان آورده‌اید! سود (ربا) را چندین برابر مخورید، و از الله بترسید، تا کامیاب و رستگار گردید.');
INSERT INTO "badkhashani" VALUES(3,131,'3:131','و از آتشی که برای کفار آماده شده است بترسید.');
INSERT INTO "badkhashani" VALUES(3,132,'3:132','و از الله و رسول او اطاعت کنید تا مورد رحمت (الهی) قرار گیرید.');
INSERT INTO "badkhashani" VALUES(3,133,'3:133','و بشتابید (به وسیلۀ توبه) به‌سوی مغفرت از جانب پروردگارتان و جنتی که پهنای آن ( برابر) آسمان‌ها و زمین است، (و) برای پرهیزگاران آماده شده است.');
INSERT INTO "badkhashani" VALUES(3,134,'3:134','آنان که مال‌هایشان را در حال ثروتمندی و تنگدستی (در راه الله) انفاق می‌کنند، و غضب خود را فرو می‌برند و از (اشتباهات) مردم می‌گذرند، و الله (اینچنین) خوبی کنندگان را دوست دارد.');
INSERT INTO "badkhashani" VALUES(3,135,'3:135','و آنان که چون عملی زشت (و گناهی) را مرتکب شوند یا بر خویش ظلم کنند، (فورا) الله را یاد می‌کنند پس برای (محو) گناهان خود (از الله) آمرزش می‌خواهند، و (می‌دانند که) چه کسی به جز الله می‌تواند گناهان مردم را بیامرزد؟ و به آنچه (از کردار بد) کرده‌اند اصرار (و مداومت) نمی‌ورزند، در حالیکه (انجام بد آن را) می‌دانند.');
INSERT INTO "badkhashani" VALUES(3,136,'3:136','این گروه جزایشان آمرزشی از طرف پروردگارشان، و باغ‌های که در زیر درختان و قصر‌های آن نهرها جاری است می‌باشد و در آن (باغ‌ها و قصرها) همیشه می‌باشند، و ثواب عمل کنندگان چه ثواب نیک و خوب است.');
INSERT INTO "badkhashani" VALUES(3,137,'3:137','پیش از شما سنت‌های الهی (راجع به نصرت مؤمنان و عذاب کفار) گذشته است پس در زمین سفر کنید و ببینید که سرانجام تکذیب کنندگان چطور بود.');
INSERT INTO "badkhashani" VALUES(3,138,'3:138','این (قرآن) بیانگر (همان سنت‌ها و بیان کنندۀ حقایق واقعی) است برای مردم، و هدایت و پندی برای پرهیزگاران است.');
INSERT INTO "badkhashani" VALUES(3,139,'3:139','و (شما ای مسلمانان) سست و غمگین مشويد؟ زیرا که غالب و برترید، اگر مؤمن باشید.');
INSERT INTO "badkhashani" VALUES(3,140,'3:140','اگر (در جنگ احد) به شما آسیب رسید (پریشان مشوید) چون به قوم کافر نیز مانند شما رسیده است، و این (حوادث) روزگار را در بین مردم می‌گردانیم (تا آنها را آزمایش کنیم) و تا الله مؤمنان (حقیقی) را مشخص سازد، و از شما شهیدانی بگیرد، و الله ظالمان را دوست ندارد.');
INSERT INTO "badkhashani" VALUES(3,141,'3:141','و تا الله مؤمنان را پاک و خالص گرداند (و ایشان را از منافقان متمایز سازد) و تا کافران را محو و نابود کند.');
INSERT INTO "badkhashani" VALUES(3,142,'3:142','آیا گمان دارید که به جنت می‌روید؟ در حالیکه الله (تا هنوز) مجاهدان و صابران شما را معلوم و مشخص نکرده است.');
INSERT INTO "badkhashani" VALUES(3,143,'3:143','والبته آرزوی موت (شهادت) را می‌کردید پیش از آنکه با آن روبرو شوید، پس آن (منظر موت) را دیدید، در حالیکه نگاه می‌کردید.');
INSERT INTO "badkhashani" VALUES(3,144,'3:144','و نیست محمد ج مگر پیغمبری که پیش از او پیغمبران (دیگر) گذشته است، پس آیا اگر وفات کند (به موت طبیعی) و یا کشته شود، پس به عقب باز می‌گردید (مرتد می‌شوید؟) و هرکس به عقب برگردد (مرتد شود) پس هرگز به الله ضرری رسانیده نمی‌تواند، و زود است که الله شکر گزاران را ثواب دهد.');
INSERT INTO "badkhashani" VALUES(3,145,'3:145','و (ممکن نیست) هیچکس جز به حكم الله نمی‌میرد و (الله) اجل (وقت مرگ هرکس ) را نوشته و معین نموده است، و هرکه ثواب دنیا را بخواهد آنرا به او می‌دهیم، و هرکه ثواب آخرت را بخواهد آنرا به او میدهیم، و زودا ثواب شاکران را می‌دهیم.');
INSERT INTO "badkhashani" VALUES(3,146,'3:146','و چه بسا پیغمبرانی گذشته‌اند که همراه آنها مردمان یکتاپرست بسیار (با کفار) قتال (جهاد) کردند، پس به سبب آنچه در راه الله به آنها رسید، سست و ناتوان نشدند، و (در مقابل دشمن) ذلیل نگشتند (و سر تسلیم خم نکردند بلکه صبر کردند) و الله صبر کنندگان را دوست دارد.');
INSERT INTO "badkhashani" VALUES(3,147,'3:147','وگفتار آنها جز این نبود که می‌گفتند: ای پروردگار ما! گناهان ما را بیامرز، و اسراف ما را معاف کن و قدم‌های ما را (در جهاد با دشمن) ثابت گردان، و ما را بر قوم کافر پیروز گردان.');
INSERT INTO "badkhashani" VALUES(3,148,'3:148','پس الله ثواب دنيا (فتح) و ثواب نیک آخرت (مغفرت و رحمت) را به ایشان بخشید و الله نیکوکاران را دوست دارد.');
INSERT INTO "badkhashani" VALUES(3,149,'3:149','ای کسانی که ایمان آورده‌اید! اگر از کسانیکه کافر شدند اطاعت کنید، شما را به عقب باز می‌گردانند (مرتد می‌سازند) پس به خسارت و زیانکاری باز خواهید گشت.');
INSERT INTO "badkhashani" VALUES(3,150,'3:150','(شما هیچ کارسازی ندارید) بلکه الله کارساز و مددگار شما است، و الله بهترین مددگاران است (لذا تنها او را اطاعت کنید).');
INSERT INTO "badkhashani" VALUES(3,151,'3:151','به زودی در دل‌های کافران ترس شما را خواهیم انداخت، به سبب اینکه چیزهایی را با الله شریک مقرر نمودند که هیچ دلیلی بر (صحت) آن نازل نکرده است، و جایگاه آنها دوزخ است، و جایگاه ستمگران چه بد جایگاهی است.');
INSERT INTO "badkhashani" VALUES(3,152,'3:152','و البته الله وعدۀ خود را (مبنی بر پیروزی شما در جنگ احد) محقق گرداند، وقتی که کفار را به حكم الله می‌کشتید تا آن که سست شدید و در مسأله (جنگ و تقسیم غنایم) نزاع کردید، و (از دستور پیغمبر) نافرمانی کردید، با آنکه به شما چیزی را نشان داد که دوست می‌داشتید آن را (نصرت و غنیمت را) و کسی از شما دنیا را می‌خواست، و از کسی از شما آخرت را می‌خواست، باز الله شما را از )تعقیب کردن( آنها منصرف ساخت تا شما را بیازماید، و البته الله (خطای شما) را معاف کرد، بی‌گمان الله بر مؤمنان منت و بخشش (و مهربانی) دارد.');
INSERT INTO "badkhashani" VALUES(3,153,'3:153','به یاد آورید هنگامی که (در جنگ احد در وقت فرار بر کوه) بالا می‌رفتید و به کسی توجه نمی‌کردید، (در حالیکه) پیغمبر شما را از پشت سر فریاد می‌کرد، پس الله (به سبب نافرمانی تان) غمی بر غمهای تان افزود و مجازات نمود (تا کوتاهی خود را بدانید، و) تا بر چیزی که از دست داده‌اید (مال غنیمت) و بر آنچه (از آسیب و زخم و شکست) بر شما رسید؛ غمگین نشوید، و الله به آنچه می‌کنید آگاه است.');
INSERT INTO "badkhashani" VALUES(3,154,'3:154','باز بعد از (آن) غم و اندوه، خواب پینکی را برای آرامش شما فرود آورد که گروهی از شما را فراگرفت، و گروه دیگر تنها در فکر خود بودند و درباره الله گمان ناحق، مانند گمان جاهلیت می‌کردند و می‌گفتند: آیا ما در این امر (جنگ) اختیاری داریم؟ بگو: البته همه کار و اختیار به دست الله است، در دل‌های خود چیزی را می‌پوشانند که آنرا به تو اظهار نمی‌کنند، می‌گویند: اگر در این کار چیزی اختیار داشتیم در اینجا کشته نمیشدیم، بگو: اگر در خانه‌های تان (هم) می‌بودید البته آنان که کشته شدن در مورد‌شان نوشته شده است به رزمگاه و جای قتل خود بیرون می‌شدند و تا الله آنچه را که در سینه‌های تان بود بیازماید، و تا آنچه که در دل‌های تان بود پاک و خالص کند، و الله به آنچه در سینه‌ها است داناست.');
INSERT INTO "badkhashani" VALUES(3,155,'3:155','يقينا آنان که از میان شما در روز روبرو شدن دو گروه (مسلمانان و کفار) پشت گردانیدند (فرار کردند) جز این نیست که شیطان آنها را به سبب بعضی گناهانی که کرده بودند، لغزانید. البته الله آنها را عفو کرد، بی‌گمان الله آمرزنده بردبار است.');
INSERT INTO "badkhashani" VALUES(3,156,'3:156','ای کسانی که ایمان آورده‌اید، مانند کسانی مشوید که کافر شدند و در باره برادران‌شان که به سفر (تجارت) یا برای جهاد رفتند، گفتند: اگر آنها نزد ما (در خانه) بودند، نه می‌مردند و نه کشته می‌شدند، (چنین گفتند) تا الله این سخن را سبب حسرت و افسوس در دل‌هایشان گرداند، در حالیکه الله زنده می‌کند و می‌میراند (زندگی و مرگ به دست الله است) و الله به آنچه می‌کنید بیناست.');
INSERT INTO "badkhashani" VALUES(3,157,'3:157','اگر در راه الله کشته شوید یا بمیرید، البته آمرزش و رحمت الله از آنچه (در زندگی خود) جمع می‌کنند، بهتر است.');
INSERT INTO "badkhashani" VALUES(3,158,'3:158','اگر بمیرید (به مرگ طبیعی) و یا کشته شوید، البته (در هر دو صورت) به‌سوی الله باز گردانده و جمع خواهید شد.');
INSERT INTO "badkhashani" VALUES(3,159,'3:159','پس به سبب رحمت الله است که در مقابل آنها نرمی کردی، و اگر درشت خوی و سخت دل بودی، حتما (مسلمانان) از اطراف تو دور می‌شدند، پس آنها را (در مورد حق خود) عفو کن و برای آنها (در بارۀ حق الله) مغفرت بخواه و در کارها با آنان مشورت کن، پس چون (بر کاری) تصمیم گرفتی پس بر الله توکل کن؛ بی‌گمان الله توکل کنندگان را دوست دارد.');
INSERT INTO "badkhashani" VALUES(3,160,'3:160','اگر الله شما را یاری و مدد کند، پس هیچکس بر شما غالب نخواهد شد، و اگر شما را خوار و ترک کند، کیست که بعد از او شما را مدد کند؟ و مؤمنان باید بر الله توکل کنند.');
INSERT INTO "badkhashani" VALUES(3,161,'3:161','و برای هیچ پیغمبری شایسته نیست که در مال غنیمت خیانت کند (زیرا) هرکه در مال غنیمت خیانت کند، در روز قیامت با خیانت خود حاضر می‌شود، باز به هر کس جزای آنچه کرده است، بطور کامل داده می‌شود، و بر آنها ظلم نمی‌شود.');
INSERT INTO "badkhashani" VALUES(3,162,'3:162','آیا کسی که از رضای الله پیروی می‌کند، مانند کسی است که به قهر و خشم الله بازگشته است (و گرفتار شده است) و جای او دوزخ است؟! و دوزخ بد جای بازگشت است.');
INSERT INTO "badkhashani" VALUES(3,163,'3:163','آنها (پیروان رضای الله) مراتب و مقام‌های مختلفی نزد الله دارند، و الله به آنچه می‌کنند، بیناست.');
INSERT INTO "badkhashani" VALUES(3,164,'3:164','به راستی که الله بر مؤمنان احسان نمود، آن وقت که در میان آنها پیغمبری از جنس خودشان فرستاد تا آيات الله را بر آنها تلاوت کند، و آنها را (از عقائد و اعمال باطل) پاک سازد و به ایشان (احکام) کتاب (قرآن) و حکمت (سنت) را تعلیم دهد، هرچند پیش از آن در گمراهی آشکارا واقع بودند.');
INSERT INTO "badkhashani" VALUES(3,165,'3:165','آیا وقتی که مصیبتی (در جنگ احد) به شما رسید، در حالیکه شما (در جنگ بدر) دوچند آن را (به دشمن تان) رسانیده بودید، (باز) گفتید: این مصیبت از کجا آمد؟ بگو: آن (مصیبت) از طرف خودتان است (وسبب آن خودتان هستید)، یقینا الله بر هرچیز قادر است.');
INSERT INTO "badkhashani" VALUES(3,166,'3:166','و هر چه (از مصائب) در روز روبرو شدن دو گروه (جنگ احد) به شما رسید، پس به حكم الله بود (تا شما را در برابر آن اجر دهد و) تا مؤمنان را مشخص سازد.');
INSERT INTO "badkhashani" VALUES(3,167,'3:167','و تا الله منافقان را آشکار و مشخص سازد، منافقانی که به آنها گفته شد: بیاید در راه الله بجنگید یا دشمن را (از خود) دفع کنید، (در جواب) گفتند: اگر می‌دانستیم این جنگی واقعی است حتما از شما پیروی می‌کردیم، آنان (منافقان) در آن روز، به کفر نزدیک‌تر بودند تا به ایمان، با دهان خویش سخنی را می‌گویند که در دل‌هایشان نیست، و الله به آنچه (در دل‌های خود) می‌پوشانند داناتر است.');
INSERT INTO "badkhashani" VALUES(3,168,'3:168','آنان که به برادران خود گفتند: و (خودشان در خانه‌ها) نشستند، اگر از ما اطاعت می‌کردند (و به جنگ نمی‌رفتند حالا) کشته نمی‌شدند، بگو: اگر در گفتۀ خود صادق هستید، پس مرگ را از خود دور کنید.');
INSERT INTO "badkhashani" VALUES(3,169,'3:169','و آنانی را که در راه الله کشته شده‌اند مرده گمان نکنید، بلکه (در جهان برزخ) زنده‌اند، (و) نزد پروردگارشان روزی (ونعمتها) داده می‌شوند.');
INSERT INTO "badkhashani" VALUES(3,170,'3:170','آنان به آنچه که الله از فضل و کرم خود به آنها داده است، خوشحال‌اند، و به کسانی که بعد از آنان می‌آیند و (لیکن) هنوز با آنها یکجا نشده‌اند، مژده می‌دهند (به این که) هیچ خوفی بر آنها نیست و نه غمگین می‌شوند.');
INSERT INTO "badkhashani" VALUES(3,171,'3:171','به نعمت‌های الله و فضل او مژده می‌دهند (و ابراز خوشی می‌کنند)، و به اینکه الله ثواب مؤمنان را ضائع نمی‌کند.');
INSERT INTO "badkhashani" VALUES(3,172,'3:172','آنها ( آنانی اند) امر الله و پیغمبر را اجابت کردند؛ بعد از اینکه زخمها به آنها رسیده بود، برای کسانی از ایشان که نیکی کردند و تقوی پیشه نمودند، ثواب بس بزرگ است.');
INSERT INTO "badkhashani" VALUES(3,173,'3:173','آنانی که مردم به ایشان گفتند: یقینا مردم (کفار برای قتال شما سپاه) جمع کرده‌اند، پس از آنها بترسید، پس (این سخن) ایمان آنها را زیاد کرد، و گفتند: ما را الله بس است، و او بهترین کارساز است.');
INSERT INTO "badkhashani" VALUES(3,174,'3:174','پس آنان با نعمت و فضل الله بازگشتند و هیچ زیانی به آنها نرسید و جویای رضای الله بودند، و الله دارای فضل بزرگ است.');
INSERT INTO "badkhashani" VALUES(3,175,'3:175','جز این نیست که آن (خبر دهنده) شیطان است که (مسلمانان را) از دوستان خود (کفار) می‌ترساند، پس از آنها مترسید، و از من بترسید، اگر مؤمن هستید.');
INSERT INTO "badkhashani" VALUES(3,176,'3:176','آنان که در قبول و نصرت کفر شتاب می‌کنند، تو را پریشان و غمگین نسازد، (چون) آنها هیچ ضرری به الله رسانده نمی‌توانند، الله می‌خواهد در آخرت هیچ سهمی برای آنها قرار ندهد (بلکه) برای آنها عذاب بزرگ است.');
INSERT INTO "badkhashani" VALUES(3,177,'3:177','يقينا آنان که کفر را به عوض ایمان اختیار نمودند، هرگز به الله ضرری رسانده نمی‌توانند، (بلکه) برایشان عذاب دردناک باشد.');
INSERT INTO "badkhashani" VALUES(3,178,'3:178','و کافران هرگز گمان نکنند که مهلت دادن ما به آنها به خیر‌شان است، (بلکه) جز این نیست که ما آنها را مهلت میدهم تا (به اختیار خود) به گناه خویش بیافزایند، و برای آنها عذاب اهانت آمیز (و رسواکننده) است.');
INSERT INTO "badkhashani" VALUES(3,179,'3:179','چنین نیست که الله مؤمنان را بر آن حالی که شما بر آن هستید بگذارد، تا آن که ناپاک (منافق و کافر) را از پاک (مؤمن) جدا سازد و چنین نیست که الله شما را از چیزهای پوشیده (از عقل و حواس) آگاه سازد، و لیکن الله (برای آگاه ساختن بعضی از امور غیبی) از پیغمبرانش هر که را بخواهد انتخاب می‌کند، پس به الله و به پیغمبران او ایمان بیاورید (ایمان خود را محکم بدارید)، و اگر ایمان بیاورید و پرهیزگاری کنید، پس برای شما ثواب بزرگ خواهد داد.');
INSERT INTO "badkhashani" VALUES(3,180,'3:180','و کسانی که الله از فضل خود به آنها داده است (و) بخل می‌کنند، گمان نبرند که آن بخل به خیر (و فائدۀ) آنها است، بلکه (آن بخل) به ضرر آنهاست، (چون) زود است که آن چه به آن بخل می‌ورزیدند، در روز قیامت طوق گردن آنها خواهد شد، و میراث آسمان‌ها و زمین تنها از الله است، و الله به هر آنچه می‌کنید، خبردار است.');
INSERT INTO "badkhashani" VALUES(3,181,'3:181','البته الله سخن کسانی را که گفتند: یقینا الله محتاج است، و ما غنى هستیم شنید، به زودی آنچه را گفته‌اند می‌نویسیم، و قضیه به ناحق کشتن پیغمبران را (نیز می‌نویسیم)، و (به ایشان) خواهیم گفت: عذاب سوزان را بچشید.');
INSERT INTO "badkhashani" VALUES(3,182,'3:182','این (عذاب سوزان) به سبب آن اعمالی است که به دست خود پیش فرستادید و (گرنه) الله نسبت به بندگان خود ظالم نیست.');
INSERT INTO "badkhashani" VALUES(3,183,'3:183','(این‌ها) آنانی‌اند که (به دروغ) گفتند: البته الله با ما عهد بسته است که به هیچ پیغمبری ایمان نیاوریم تا آن که قربانی ای برای ما بیاورد که آتش آن را بسوزاند، بگو: البته پیش از من پیغمبرانی بودند که معجزه‌های آشکار را با آن چه که گفتید برایتان آوردند، پس چرا آنها را کشتید، اگر راستگو هستید.');
INSERT INTO "badkhashani" VALUES(3,184,'3:184','پس اگر تو را تکذیب کردند پس (پروا مكن و بدان که) چندین پیغمبر قبل از تو، معجزه‌ها و صحیفه‌ها و کتاب روشن آورده بودند، (لیکن) تکذیب شدند.');
INSERT INTO "badkhashani" VALUES(3,185,'3:185','هر نفسی (جاندار) چشندۀ مرگ است، و البته جزای اعمالتان در روز قیامت بطور کامل به شما داده می‌شود، پس هر کس از دوزخ دور کرده و وارد بهشت کرده شد، یقینا کامیاب شده است، و زندگانی دنیا نیست مگر متاع غرور (سبب نفع و فریب بنده است).');
INSERT INTO "badkhashani" VALUES(3,186,'3:186','حتما در مالها و جانهایتان آزموده می‌شوید، و حتما از آنانی که پیش از شما کتاب داده شده‌اند (یهود و نصاری) و از مشرکین سخنان اذیت کننده (تمسخر آمیز و طعن) بسیار خواهید شنید، ولی اگر (در این امتحان و اذیت‌ها) صبر و تقوی اختیار کردید، پس یقینا این (خصلت) از چیزهای است که تصمیم را استوار و محکم می‌گرداند.');
INSERT INTO "badkhashani" VALUES(3,187,'3:187','و ياد کن وقتی را که الله از آنانی که کتاب داده شده بود پیمان محکم گرفت که حتما باید کتاب را برای مردم بیان کنید، و (احکام و دستورات) آن را نپوشانید، پس آنها عهد را پشت سر خود انداختند (و آن را پوشانیدند) و آنرا با متاع و عوض کم (و حقیر) فروختند، پس بد است آنچه خریدند.');
INSERT INTO "badkhashani" VALUES(3,188,'3:188','گمان مبر در مورد آنانی که به کرده خویش خرسند می‌شوند و دوست دارند بر آنچه که نکرده‌اند ستایش شوند، و گمان مبر که آنها از عذاب نجات می‌یابند (بلکه) آنها عذاب دردناکی در پیش دارند.');
INSERT INTO "badkhashani" VALUES(3,189,'3:189','بادشاهی آسمان‌ها و زمین تنها از الله است، و الله بر هر چیزی توانا است.');
INSERT INTO "badkhashani" VALUES(3,190,'3:190','يقينا در آفریدن آسمان‌ها و زمین (به هیئت خاص) و در اختلاف (پی در پی آمدن) شب و روز، دلائل واضح (به قدرت و وحدانیت الله) برای صاحبان خرد است.');
INSERT INTO "badkhashani" VALUES(3,191,'3:191','(خردمندان) آنانی‌اند که الله را (به حالت) ایستاده، و نشسته، و بر پهلو آرامیده یاد می‌کنند، و در (حکمت از) آفریدن آسمان‌ها و زمین تفکر می‌کنند (و در دعای خود می‌گویند:) ای پرودگار ما! این (عالم و موجودات) را بی‌فائده نه آفریده ای، پاکی تو (از آنچه لایق شأن تو نیست) پس ما را از عذاب دوزخ نگاه دار.');
INSERT INTO "badkhashani" VALUES(3,192,'3:192','ای پروردگار ما! یقینا کسی را که تو در دوزخ در آوری پس يقينا رسوایش کرده ای، و برای ظالمان هیچ مددگاری نیست (تا آنها را از این رسوائی نجات دهد).');
INSERT INTO "badkhashani" VALUES(3,193,'3:193','ای پروردگار ما! يقينا ما شنیدیم که دعوت دهندۀ (مردم را) به‌سوی ایمان دعوت می‌دهد، (و می‌گوید:) که به پروردگارتان ایمان آرید، پس ما ایمان آوردیم، پس ای پروردگار ما! گناهان ما را بیامرز، و از ما بدی‌های ما را دور کن، و ما را با نیکان بمیران.');
INSERT INTO "badkhashani" VALUES(3,194,'3:194','ای پروردگار ما! آنچه را که بر زبان پیغمبران خود (از نصرت در دنیا، و جنت در آخرت) به ما وعد کرده‌ای عطا کن، و ما را در روز قیامت رسوا مکن (زیرا ما ایمان داریم که) يقينا تو وعدہ خلافی نمی‌کنی.');
INSERT INTO "badkhashani" VALUES(3,195,'3:195','پس پروردگارشان دعای آنها را قبول کرد (به این طور) که من عمل هیچ عمل کننده‌ای شما را؛ خواه مرد باشد و خواه زن، ضائع نمی‌کنم، شما از جنس یکدیگر هستید (که در ایمان و عمل، مرد و زن فرق ندارند، و یکی از آن اعمال هجرت است) پس آنان که هجرت کردند و از خانه‌هایشان بیرون رانده شدند و در راه من (راه الله) اذیت‌ها دیدند و (با كفار) جنگ کردند و کشته شدند، حتما گناهان‌شان را محو می‌کنیم و حتما آنها را در باغ‌های که در زیر درختان و قصرهای آن نهرها جاری است، داخل کنیم، این ثواب از جانب الله داده می‌شود، و ثواب نیکو و خوب نزد الله است.');
INSERT INTO "badkhashani" VALUES(3,196,'3:196','گشت و گذار کافران در شهرها تو را نفریبد.');
INSERT INTO "badkhashani" VALUES(3,197,'3:197','(این متاع) چند روزه و ناچیز است، باز جایگاه‌شان دوزخ است، و دوزخ (برای کافران) چه بد آرامگاهی است.');
INSERT INTO "badkhashani" VALUES(3,198,'3:198','لیكن آنانی که (تقوی پیشه کردند و) از پروردگار خود ترسیدند برای آنها باغ‌هایی است که از زیر آن نهرها جاری می‌باشد، و در آن همیشه‌اند، (و این) مهمانی است از نزد الله، و آنچه نزد الله است، برای نیکوکاران (از هر چیزی) بهتر است.');
INSERT INTO "badkhashani" VALUES(3,199,'3:199','(همه اهل کتاب دوزخی نیستند) و يقينا از اهل کتاب کسانی هستند که به الله و به آنچه بر شما نازل شده و به آنچه بر خود آنها نازل شده است؛ در حالیکه در برابر الله متواضع‌اند، ایمان دارند، آیات (و احکام) الله را در برابر متاع حقیر (دنيا) عوض نمی‌کنند، این گروه است که نزد پروردگارشان اجر خود را دارند، بی‌گمان الله زود حساب گیرنده است.');
INSERT INTO "badkhashani" VALUES(3,200,'3:200','ای کسانی که ایمان آورده‌اید، صبر پیشه کنید، (و در مقابل دشمن) پایداری ورزید و برای جهاد آماده باشید (در انتظار جهاد وسایل جنگی خود را مهیا کنید) و از الله بترسید تا که کامیاب شوید.');
INSERT INTO "badkhashani" VALUES(4,1,'4:1','ای مردم! از پروردگارتان (مالک و متصرف تان) بترسید، آن (پروردگاری) که شما را از یک شخص آفرید، و همسر او (حوا) را (نیز) از همان شخص (آدم) آفرید، و (باز) از آن دو (آدم و حوا)، مرد و زن بسیاری را (در روی زمین) منتشر ساخت، و از الله بترسید که به نام او از یکدیگر سوال (درخواست) می‌کنید، همچنین از قطع صله رحمی (و تلف شدن حقوق) خویشاوندان بترسید؟ بی‌گمان الله بر (کردار و گفتار) شما نگهبان هست.');
INSERT INTO "badkhashani" VALUES(4,2,'4:2','و مال‌های یتیمان را به خودشان (بعد از بلوغ آنها) بسپارید، و (مال) ناپاک را با (مال) پاکیزه تبدیل نکنید و مال‌های آنان (یتیمان) را با مال‌های خود (یکجا کرده) نخورید، چون این (کار) گناه بزرگ است.');
INSERT INTO "badkhashani" VALUES(4,3,'4:3','و اگر ترسیدید از اینکه در حق دختران یتیم عدل کرده نمی‌توانید پس (در این صورت) از دیگر زنانی که مورد پسندتان است؛ دو زن، یا سه زن، یا چهار زن را نکاح کنید، پس اگر ترسیدید که عدل کرده نمی‌توانید پس (فقط) یک زن را نکاح کنید یا (اکتفا کنید به) کنیزی که مالک شده‌اید، این نزدیکتر به آن است که (با عیالمندشدن و فقیر شدن، در حق زنان) ظلم نکنید.');
INSERT INTO "badkhashani" VALUES(4,4,'4:4','و مهرهای زنان را به خوشی و رضایت، به ایشان بدهید، پس اگر از خلوص دل برای شما چیزی از مهر را (معاف کرده) بگذارند، پس آن را خوش و گوارا بخورید');
INSERT INTO "badkhashani" VALUES(4,5,'4:5','و به نادانان اموال تان را مسپارید که الله آن را سبب بقا و زندگیتان گردانیده است، و از آن مال، خوراک و پوشاک‌شان را بدهید، و با آنها سخن نیک و پسندیده بگویید.');
INSERT INTO "badkhashani" VALUES(4,6,'4:6','و یتیمان را امتحان کنید (که بالغ شده‌اند یا نه؟) تا وقتی که به عمر نکاح (بلوغ) برسند، پس اگر در آنها عقل و تدبیر را احساس کردید پس به آنها مالهایشان را بسپارید، و آن (مال‌ها) را به اسراف و شتاب مخورید (از ترس) اینکه بزرگ شوند (و مال خود را تصرف کنند) و هرکس توانمند (ثروتمند) بود، پس باید (از خوردن مال یتیم) پرهیزگاری و پاکی کند، و کسی که فقیر و تنگدست بود از مال یتیم به قدر ضرورت باید بخورد، پس وقتی خواستید مالهای‌شان را به آنها بسپارید، پس بر آنها گواه بگیرید، و الله بر محاسبه (بندگان) کافی است.');
INSERT INTO "badkhashani" VALUES(4,7,'4:7','مردان از آنچه که پدر و مادر و خویشاوندان به میراث می‌گذارند سهم دارند، و زنان از آنچه که پدر و مادر و خویشاوندان به میراث می‌گذارند سهم دارند، آن سهم (از اموال متروکه) کم باشد یا زیاد باشد (برای هریک) حصه معین مقرر گردیده است.');
INSERT INTO "badkhashani" VALUES(4,8,'4:8','و چون خویشاوندان (که وارث نیستند) و یتیمان و ناتوانان در تقسیم میراث حاضر شوند، پس به آنها چیزی از آن (مال به طور بخشش) بدهید، و با آنها سخن نیک و شایسته بگویید.');
INSERT INTO "badkhashani" VALUES(4,9,'4:9','و آنانی که اگر اولاد ضعیف از خود بجا بگذارند و بر (آینده) آنها ترس دارند (که ضائع نشوند) باید از الله بترسند، پس باید (در وصیت) از الله بترسند و باید سخن محکم و درست بگویند (که مخالف با عدل و حق نباشد).');
INSERT INTO "badkhashani" VALUES(4,10,'4:10','يقينا آنانی که مال‌های (چنين) يتيمان (ضعیف ) را به ظلم می‌خورند، جز این نیست که در شکم‌های خود آتش را می‌خورند، و زود است که در آتش شعله ور داخل شوند.');
INSERT INTO "badkhashani" VALUES(4,11,'4:11','(چون رعایت اولاد ضروری است لذا) الله شما را دربارۀ اولادتان سفارش می‌کند (که دارایی خود را برباد نکرده و آنها را فقیر نگذارید. و در وقت تقسیم) برای پسر مانند دو سهم دختر است، پس اگر وارثان زن باشند و اضافه از دو نفر باشند، پس برای آنها (از جمله سه حصه ترکه)، دو حصه سهم می‌رسد، و اگر یکی باشد، پس نصف ترکه از اوست، و برای هریک از پدر و مادر میت، یک ششم ترکه است اگر از میت فرزندی باشد، و اگر از میت فرزندی نباشد و پدر و مادر او وارث او گردند، پس برای مادر یک سوم است (و باقی از پدر است) و اگر از میت برادرانی مانده باشد، پس مادر او یک ششم ترکه را می‌برد، البته (تقسیم میراث) بعد از تنفيذ وصیتی است که میت به آن وصیت کرده است یا بعد از ادای دین است (اگر میت دیندار باشد) و شما نمی‌دانید که پدران و پسرانتان کدام یکی از آنها برای تان سودمندتر است، (این احکام) از طرف الله مقرر شده است؛ یقینا الله (به احوال ورثه) دانا و حکیم است.');
INSERT INTO "badkhashani" VALUES(4,12,'4:12','و برای شما (مردان) نصف از ترکۀ است که زنهایتان از خود بجا گذاشته‌اند، اگر آنها اولاد نداشته باشند، ولی اگر اولاد داشته باشند، سهم شما یک چهارم از ترکه است، البته بعد از تنفيذ وصیتی که زنان به آن وصیت می‌کنند یا بعد از ادای دین (اگر دیندار باشند)، و (اما اگر شوهر وفات کرد) سهم زنان یک چهارم از تركه شما است اگر فرزندی نداشته باشید، اما اگر فرزندی داشته باشید، سهم زنان شما یک هشتم تركه شما است (لیکن) بعد از تنفيذ وصیتی که شما به آن وصیت می‌کنید یا بعد از ادای دین (اگر دیندار باشید) و اگر مردی یا زنی که از او ارث برده شود کلاله باشد (یعنی والدین و فرزند نداشته باشد) و او برادر و خواهر داشته باشد، پس برای هریک از آنان یک ششم می‌رسد، پس اگر (خواهر و برادر) بیش از یکی باشند همه‌شان در یک سوم مساویانه شريک‌اند، البته بعد از تنفيذ وصیت یا بعد از ادای دین، (به شرطی که) وصیت (وصیت کننده) به وارث ضرر نرساند، (این حکم) سفارشی از جانب الله است، و الله دانا (و) بردبار است.');
INSERT INTO "badkhashani" VALUES(4,13,'4:13','آن (چه بیان شد) حدود و مقررات الله است، و هر کس از الله و رسول او اطاعت و پیروی کند، الله او را در باغ‌های داخل می‌کند که از زیر درختان و قصرهای آن نهرها جاری است، آنان برای همیشه در آن می‌باشند، و این است کامیابی بزرگ.');
INSERT INTO "badkhashani" VALUES(4,14,'4:14','و هرکس نافرمانی الله و رسول او را کند و از حدود الهی تجاوز کند الله او را در آتشی داخل می‌کند که همیشه در آن می‌ماند، و برای او عذاب رسواکننده (اهانت آمیز) است.');
INSERT INTO "badkhashani" VALUES(4,15,'4:15','و آنانی از زنان شما که مرتکب فاحشه (زنا) می‌شوند، پس بر آنها چهار مرد از خودتان (از مسلمانان عادل) را گواہ طلب کنید، پس اگر گواهی دادند (بر کار بد آنها) پس آنها را در خانه‌ها محبوس کنید تا آن که ملک الموت روح آنها را قبض کند، یا الله برای آنها (قانون و) راهی دیگری مقرر کند.');
INSERT INTO "badkhashani" VALUES(4,16,'4:16','و آن دو کس از شما که زنا می‌کنند (از زن و مرد) پس آنها را (بوسيلۀ حاكم) آزار دهید، پس اگر توبه کردند (از عمل فاحشه) و اصلاح کردند (عمل خود را) پس از آزارسانی‌شان دست بردارید؛ زیرا الله توبه پذیر (و) بسیار مهربان است.');
INSERT INTO "badkhashani" VALUES(4,17,'4:17','جز این نیست که (وعدۀ) قبول توبه بر ذمۀ الله است (الله متعال بر خود واجب گردانیده است). (این) تنها برای آنانی است که به نادانی کار بد می‌کنند، و باز به زودی (از گناه خویش) توبه می‌کنند. پس این گروه است که الله توبه‌شان را قبول می‌کند، و الله دانای باحکمت است.');
INSERT INTO "badkhashani" VALUES(4,18,'4:18','و (وعدۀ) قبول توبه برای کسانی نیست که گناه می‌کنند تا وقتی که مرگ یکی از آنها برسد می‌گوید: البته اکنون توبه کردم، و نیز توبه آنانی که در حالت کفر می‌میرند (قبول نمی‌گردد)، برای این (دو) گروه عذاب دردناک آماده کرده‌ایم.');
INSERT INTO "badkhashani" VALUES(4,19,'4:19','ای کسانی که ایمان آورده‌اید، برای شما حلال نیست که زنان را جبرا (بدون رضامندی آنها) به ارث ببرید، و نیز زنان (بیوه) را از نکاح (با مردی دیگر) منع مکنید، تا بخشی از مالی را که به آنها (به طور مهر) داده بودید، پس بگیرید مگر آنکه مرتکب عمل زشت آشکار شوند، و با زنان به طريقۀ نیک و پسندیده زندگانی کنید، پس اگر آنها را دوست ندارید (صبر کنید) زیرا چه بسا؛ چیزی را خوش ندارید و (لیکن) الله در آن خیر و برکت بسیاری گذاشته است.');
INSERT INTO "badkhashani" VALUES(4,20,'4:20','و اگر خواستید همسر (جدید) را به جای همسر (قدیم) قرار دهید (یعنی زن قدیم را طلاق دهید) و به یکی از آنها مال بسیار (به طور مهر) داده باشید، پس از آن (مال) چیزی نگیرید، آیا آن را به ستم و تجاوز آشکار پس می‌گیرید؟.');
INSERT INTO "badkhashani" VALUES(4,21,'4:21','و چطور آن را می‌گیرید حال آن که با یکدیگر آمیزش (جنسی) کرده‌اید (و هریک بر عورت دیگری اطلاع پیدا کرده است) در حالیکه زنان از شما عهد محکم (در عقد نکاح) گرفته‌اند.');
INSERT INTO "badkhashani" VALUES(4,22,'4:22','و با زنانی ازدواج نکنید که پدران تان با آنها نکاح کرده بودند، مگر آنچه در گذشته (قبل از نزول این آیت) رخ داده است، بی‌گمان این کار، بی‌حیائی و عمل ناشایست و منفور و راه بد است.');
INSERT INTO "badkhashani" VALUES(4,23,'4:23','بر شما مادران تان حرام شده است و دختران تان و خواهران تان و عمه‌هایتان و خاله‌هایتان و دختران برادر و دختران خواهر و (حرام شده است) آن مادران شما که شما را شیر داده‌اند و خواهران رضاعی تان و مادران زن‌هایتان، و دختران زنان تان که در کنار شما پرورش یافته اند؛ از بطن آن زنان تان که با آنها همبستر شده‌اید، پس اگر با آنها همبستر نشده‌اید، پس بر شما گناهی نیست (در نکاح کردن دختران آنها)، و (حرام شده است) زنان پسران شما که از پشت (نسل) خود تان باشند، و (حرام شده است) این که دو خواهر را در نکاح خود جمع کنید، مگر آنچه گذشته است، بی‌گمان الله آمرزنده (و) مهربان است.');
INSERT INTO "badkhashani" VALUES(4,24,'4:24','و زنان شوهردار بر شما حرام شده است مگر کنیزهای که مالک آنها شده‌اید، این فریضۀ الهی است که بر شما لازم شده است، و برای شما غیر این محرمات حلال است (به شرطی) که به مال‌های خویش طلب نکاح کنید به قصد پاکی، نه به قصد شهوت رانی و زنا، پس آنچه از زنان نفع گرفته‌اید (بوسيلۀ آمیزش جنسی) پس مهرهایشان را مطابق آنچه (از جانب الله) مقرر شده است بدهید، و بر شما در آنچه با یکدیگر بعد از مهر مقرر توافق نموده‌اید، گناهی نیست، (چون) يقينا الله دانای باحکمت است.');
INSERT INTO "badkhashani" VALUES(4,25,'4:25','و هر که از شما قدرت مالی نداشته باشد که (به وسیلۀ آن) با زنان مسلمان آزاد نکاح کند، پس باید با کنیزهای مسلمانی که مالک آنها شده‌اید نکاح کند، و الله به ایمان شما عالم‌تر است، همۀ شما از جنس یكدیگرید (همه تان فرزندان آدم هستید) پس کنیزان مسلمان را به اجازۀ اهل آنها نکاح کنید و مهرهایشان را به وجه معروف و نیک به آنان بدهید (به شرطی که) پاکدامن باشند و زانی نباشند و نه دوست پنهانی گیرنده باشند، پس وقتی شوهردار شدند، اگر مرتكب فاحشه (زنا) شدند پس بر آنها نیمی از عقوبتی (حدی) که بر زنان آزاد است (اجرای آن لازم است)، این (نکاح کنیزکان) برای کسی از شما است که از واقع شدن در گناه بترسد، و صبر کردن (نسبت به نکاح کنیزها) برایتان بهتر است، و الله آمرزندۀ مهربان است.');
INSERT INTO "badkhashani" VALUES(4,26,'4:26','الله می‌خواهد برای شما (قواعد دین تان را) بیان کند، و شما را به طریقه‌های (نیک) آنانی که پیش از شما بودند راهنمایی کند، و (می خواهد) توبۀ شما را قبول کند، والله دانای باحکمت است.');
INSERT INTO "badkhashani" VALUES(4,27,'4:27','و الله می‌خواهد شما را ببخشد (و به‌سوی رحمت خود باز گرداند)، و کسانی که از شهوات پیروی می‌کنند، می‌خواهند شما (از راه راست) به کجی بزرگ متمایل شوید (و حق را کاملا ترک گویید).');
INSERT INTO "badkhashani" VALUES(4,28,'4:28','الله می‌خواهد که (با آسان کردن احکام) بار تکلیف شما را سبک کند (زیرا) انسان ضعیف آفریده شده است.');
INSERT INTO "badkhashani" VALUES(4,29,'4:29','ای کسانی که ایمان آورده‌اید، مال‌های یکدیگر را در بین خودتان به ناحق مخورید، مگر این که از راه تجارت به رضامندی یکدیگر باشد، و خود را نکشید، بی‌گمان الله به شما همیشه مهربان است.');
INSERT INTO "badkhashani" VALUES(4,30,'4:30','و هرکه با تجاوز و ظلم چنین کند، پس به زودی او را در آتش سوزان داخل می‌کنیم، و این کار بر الله آسان است.');
INSERT INTO "badkhashani" VALUES(4,31,'4:31','اگر از گناهان بزرگی که از آن منع می‌شوید، پرهیز کنید (پس) گناهان کوچک تان را از شما دور (محو) می‌سازیم، و شما را در جای عزت و کرامت داخل می‌کنیم.');
INSERT INTO "badkhashani" VALUES(4,32,'4:32','و آنچه را که الله سبب فضیلت بعضی شما بر بعضی دیگر قرار داده است تمنی نکنید، برای مردان از آنچه خود کسب کرده‌اند سهمی است، و برای زنان از آنچه خود کسب کرده‌اند سهمی است، و (به جای آرزوهای بی‌مورد) از فضل الله بخواهید، يقينا الله به هر چیزی عالم است.');
INSERT INTO "badkhashani" VALUES(4,33,'4:33','و برای هریک از والدین و خویشان و آنانی که با ایشان پیمان بسته‌اید، در چیزی که از خود بجا می‌گذارند، ورثه و حقدار قرار داده‌ایم، پس سهم آنها را (از ارث) بدهید، زیرا الله بر هر چیز گواه است.');
INSERT INTO "badkhashani" VALUES(4,34,'4:34','مردان بر زنان ولایت دارند (صاحب اداره و حاکم اند) از آن جهت که الله بعضی (مردان) را بر بعضی دیگر (زنان) برتری داده است (از نگاه قدرت بدنی و ارادۀ محکم)، و اینکه از مال‌های خود (بر زنان) انفاق می‌کنند، پس زنان نیکو کار، فرمانبردار شوهران خود می‌باشند، و حقوق و اسرار شوهران خود را در غیاب آنها محافظت می‌کنند، به سبب اینکه الله آنها را (به وسیله شوهرها) محافظت می‌کند. و زنانی که از نافرمانی آنها می‌ترسید پس؛ (اولا) آنها را نصیحت کنید، و (ثانيا: اگر مؤثر نشد) در خوابگاه از آنها دوری کنید، و (ثالثا: اگر پند و جدایی فایده نکرد) پس آنها را بزنید، پس اگر از شما اطاعت کردند، باز بهانه‌ای (برای ظلم و ستم) بر آنها مجویید؛ بی‌گمان الله بلندمرتبه (و) بسیار بزرگ است.');
INSERT INTO "badkhashani" VALUES(4,35,'4:35','و اگر (شما اولیای امور) از اختلاف میان زن و شوهر ترسیدید، پس صلاح کاری (داور) از خانواده شوهر و صلاح کاری (داور) از خانواده زن بفرستید، اگر (صلاح کاران) اصلاح را بخواهند الله میان آن دو (زن و شوهر) موافقت (و الفت) پیدا می‌کند؛ بی‌گمان الله دانای باخبر است.');
INSERT INTO "badkhashani" VALUES(4,36,'4:36','و الله را عبادت کنید و با او چیزی را شریک نسازید، و با پدر و مادر نیکی و احسان کنید، و با خویشاوندان و یتیمان و ناداران، و با همسایۀ خویشاوند و با همسایۀ بیگانه و با همنشین در پهلو نشسته و با مسافران و با کنیزان؛ بی‌گمان الله کسی را که متكبر و خودستا (فخر فروش) باشد، دوست ندارد.');
INSERT INTO "badkhashani" VALUES(4,37,'4:37','(همچنان الله دوست ندارد) آنانی را که بخل می‌ورزند و مردم را (هم) به بخل امر می‌کنند، و آنچه را الله از فضل خود به آنها داده است، می‌پوشانند، و برای کافران عذاب خوار کننده مهیا نموده‌ایم.');
INSERT INTO "badkhashani" VALUES(4,38,'4:38','و آنانی که مال‌های خود را برای نمایش به مردم انفاق می‌کنند و (در حقیقت) به الله و به روز آخرت ایمان ندارند (در واقع شیطان همدم آنها است) و هر کس که شیطان همنشین او باشد، پس وی چه بد همنشینی است.');
INSERT INTO "badkhashani" VALUES(4,39,'4:39','و اگر به الله و به روز آخرت ایمان می‌آوردند، و (به اخلاص ) از آنچه الله به آنها روزی داده است، انفاق می‌کردند؛ چه زیانی بر آنها داشت؟ و الله به (احوال) آنها داناست.');
INSERT INTO "badkhashani" VALUES(4,40,'4:40','البته الله به وزن ذرۀ (بر کسی) ظلم نمی‌کند، و اگر آن (ذره) نیکی باشد، (الله) دو چندانش می‌کند، و از نزد خود (صاحب آن را) ثواب بزرگ میدهد.');
INSERT INTO "badkhashani" VALUES(4,41,'4:41','پس حال مردم چگونه باشد؛ وقتی از هر امتی گواهی بیاوریم، و تو را (نیز ای محمد) بر آنان (امت مسلمان) گواه بیاوریم.');
INSERT INTO "badkhashani" VALUES(4,42,'4:42','در آن روز، آنانی که کفر ورزیده‌اند و از پیغمبر نافرمانی کرده‌اند آرزو می‌کنند که ای کاش زمین بر آنها هموار گردد (در زمین فرو روند) و نمی‌توانند هیچ سخنی را از الله پنهان کنند.');
INSERT INTO "badkhashani" VALUES(4,43,'4:43','ای کسانی که ایمان آورده‌اید، در حال مستی و نشه به نماز نزدیک مشويد، تا وقتی که بدانید چه می‌گویید، و در حال جنابت به نماز نزدیک مشوید (و در حال جنابت در مسجد داخل نشوید) مگر اینکه عبور نمایید (از مسجد) تا آن که غسل کنید و اگر بیمار باشید، و یا مسافر باشید، یا یکی از شما از قضای حاجت آمد، یا با زنان هم بستری کرده باشید و آب نیافتید پس تیمم کنید بر زمین (خاک) پاک، پس مسح کنید به آن روی‌ها و دست‌های خود را، بی‌گمان الله عفو کننده (و) آمرزنده است.');
INSERT INTO "badkhashani" VALUES(4,44,'4:44','آیا به‌سوی آنانی که بهرۀ از (علم) کتاب داده شده‌اند ندیدی (یهود) که گمراهی را (در بدل هدایت) می‌خرند و می‌خواهند که شما (هم) راه حق را گم کنید.');
INSERT INTO "badkhashani" VALUES(4,45,'4:45','و (ای مؤمنان) الله به دشمنان شما داناتر است، و کافی است که الله دوست و کارساز شما باشد و کافی است که الله مددگار شما باشد.');
INSERT INTO "badkhashani" VALUES(4,46,'4:46','گروهی از یهودی‌ها ‌اند که کلمات را از جاهای آن تحریف می‌کنند و تغیر می‌دهند، و (به طور استهزاء) می‌گویند: شنیدیم (سخن ترا ای محمد) ولی نافرمانی کردیم، و بشنو (ای پیغمبر و) ناشنیده بگیر، و (می گویند:) حال ما را مراعات کن، و سخن را در زبانهای خود می‌پیچانند، تا در دین طعنه زنند، و اگر می‌گفتند: شنیدیم و اطاعت کردیم، و تو بشنو و به ما نظر كن، البته برای آنها خیر بود و درست‌تر بود، و لیکن الله آنها را به سبب كفر‌شان از رحمت خود محروم کرده است، پس ایمان نمی‌آرند مگر اندک.');
INSERT INTO "badkhashani" VALUES(4,47,'4:47','ای اهل کتاب! به آنچه نازل کرده‌ایم و تصدیق کنندۀ آن کتابی که با شما است، ایمان آورید، پیش از اینکه چهره‌ها را محو و مسخ سازیم و پیش از اینکه آنها را به شکل عقب (مانند پشت سر) برگردانیم، یا اصحاب آن چهره‌ها را از رحمت خود دور سازیم، طوری که صاحبان روز شنبه را از رحمت خود دور کردیم، و امر و ارادۀ الله شدنی (و یقینی) است.');
INSERT INTO "badkhashani" VALUES(4,48,'4:48','البته الله نمی‌بخشد این را که با او چیزی شریک قرار داده شود، و غیر از آن (شرک) را، برای کسی بخواهد می‌بخشد، و هر کس که (چیزی را) با الله شریک گرداند، پس در حقیقت به الله گناه بزرگی را افترا بسته است.');
INSERT INTO "badkhashani" VALUES(4,49,'4:49','آیا به‌سوی آنانی که خود را پاک می‌شمارند، ندیدی؟ بلکه الله هرکه را بخواهد پاک می‌سازد، و به قدر فتیلی (رشتۀ در وسط هسته خرما) ظلم کرده نشوند.');
INSERT INTO "badkhashani" VALUES(4,50,'4:50','ببین چطور بر الله دروغ و افترا می‌بندند؟ و کافی است که این گناه آشکار باشد.');
INSERT INTO "badkhashani" VALUES(4,51,'4:51','آیا آنانی را که بهره‌ای از علم كتاب (الله) داده شده‌اند، ندیدی که به بت و طاغوت (قوتهای سرکش) ایمان دارند، و راجع به کفار می‌گویند: این گروه نسبت به مؤمنان راه یاب‌تر‌اند.');
INSERT INTO "badkhashani" VALUES(4,52,'4:52','این گروه کسانی‌اند که الله آنها را لعنت کرده است، و کسی را که الله لعنت کند، پس برای او هیچ مددگاری نخواهی یافت.');
INSERT INTO "badkhashani" VALUES(4,53,'4:53','آیا برای آنها (یهودیان) سهمی در بادشاهی و حکومت است؟ که در آن صورت، به مردم (چیزی را) برابر شگاف هسته خرما نخواهند داد.');
INSERT INTO "badkhashani" VALUES(4,54,'4:54','یا اینکه (یهودیان) نسبت به مردمان (مسلمان) بخاطر اینکه الله به آنها از فضل خود (کتاب) داده است، حسد می‌ورزند؟ يقينا ما به اولاد ابراهیم (شما بنی اسرائیل) کتاب و حکمت و بادشاهی بزرگ را دادیم.');
INSERT INTO "badkhashani" VALUES(4,55,'4:55','پس برخی از آنها (یهودیان) به او (محمد) ایمان آوردند، و برخی از ایشان (ایمان نیاوردند بلکه) مانع ایمان آوردن مردم شدند، و آتش شعله ور دوزخ برای آنها کافی است.');
INSERT INTO "badkhashani" VALUES(4,56,'4:56','البته آنانی که به آیات ما كفر ورزیدند؛ حتما آنها را در آتش دوزخ در خواهیم آورد، هر قدر پوست‌های (بدن)‌شان بسوزد و پخته شود، پوست‌های دیگری به عوض پوست‌های (اولی)‌شان قرار می‌دهیم، تا عذاب را بچشند؛ بی‌گمان الله غالب باحکمت است.');
INSERT INTO "badkhashani" VALUES(4,57,'4:57','و آنانی که ایمان آوردند و کارهای نیک انجام دادند، البته آنها را به زودی در باغ‌های که در زیر آنها نهرها جاری است، داخل می‌کنیم، آنان برای همیشه در آن می‌باشند، برای آنها در آنجا زنان پاکیزه است و آنها را در سایه دوامدار درمی آریم.');
INSERT INTO "badkhashani" VALUES(4,58,'4:58','البته الله به شما دستور می‌دهد که امانت‌ها را به اهل آن (اصحاب آن) بسپارید و هرگاه میان مردم فیصله کردید، پس به عدل و انصاف حکم کنید، بی‌گمان الله شما را به آنچه نیک است پند و نصیحت می‌دهد؛ يقينا الله (به فیصله‌های شما) شنوا و بیناست.');
INSERT INTO "badkhashani" VALUES(4,59,'4:59','ای کسانی که ایمان آورده‌اید، از الله و پیغمبر و از صاحبان امرتان اطاعت کنید، و اگر در چیزی اختلاف کردید پس آن را به الله و رسول او (قرآن وسنت) برگردانید، اگر به الله و روز آخرت ایمان دارید، این (برای شما در دنیا) بهتر است و از نگاه عاقبت (آخرت) نیکوتر است');
INSERT INTO "badkhashani" VALUES(4,60,'4:60','آیا ندیدی به‌سوی آنانی که ادعا می‌کنند به کتابی که بر تو نازل شده و به کتابی که پیش از تو نازل شده ایمان آورده‌اند و می‌خواهند فيصله قضایای خود را پیش طاغوت ببرند؟ در حالیکه به آنها دستور داده شده که به طاغوت كفر بورزند (حکم طاغوت را قبول نکنند) و شیطان می‌خواهد آنها را به گمراهی دور (از حق) دچار کند.');
INSERT INTO "badkhashani" VALUES(4,61,'4:61','و چون به آنها گفته شود بیایید به طرف آنچه الله نازل کرده است، و (بیایید) به طرف رسول، منافقان را می‌بینی از تو سخت روی می‌گردانند.');
INSERT INTO "badkhashani" VALUES(4,62,'4:62','پس چگونه وقتی به سبب آنچه با دست‌های خود انجام داده‌اند، مصیبتی به آنها برسد، باز (بخاطر رهایی از آن) نزد تو می‌آیند و به الله قسم می‌خورند که (هدف ما از داوری نزد طاغوت) جز نیکی و موافقت (بین دو طرف در گیر) نبود.');
INSERT INTO "badkhashani" VALUES(4,63,'4:63','این گروه آنانی‌اند که الله آنچه را در دل (پنهان) دارند می‌داند، پس از (انتقام) آنها در گذر و آنها را وعظ و اندرز ده و به آنها سخنی بگو که به دل‌های آنها بنشیند، و مناسب حال‌شان باشد.');
INSERT INTO "badkhashani" VALUES(4,64,'4:64','و ما هیچ پیغمبری را نفرستادیم مگر اینکه به حكم الله اطاعت شود، و اگر آنها وقتی به جان خویش ظلم کردند نزد تو می‌آمدند، و از الله مغفرت می‌خواستند و پیغمبر هم برای آنها طلب مغفرت می‌کرد، البته الله را توبه پذیر (و) بسیار مهربان می‌یافتند.');
INSERT INTO "badkhashani" VALUES(4,65,'4:65','این چنین نیست، به پروردگارت قسم که آنها مؤمن (واقعی) نمی‌شوند تا آن که در حل اختلاف‌شان تو را محکم و داور مقرر کنند، باز در دل خود هیچگونه دلتنگی راجع به حکم (و قضاوت) تو نیابند، و (در برابر فیصلۀ تو) کاملا تسلیم گردند.');
INSERT INTO "badkhashani" VALUES(4,66,'4:66','و اگر ما به آنها (منافقین) دستور می‌دادیم که خود را بکشید یا از خانه‌های خود بیرون روید، جز عدد کم از آنها این امر (الله) را بجا نمی‌آوردند، و اگر آنها به آنچه پند داده می‌شوند عمل می‌کردند، البته برایشان بهتر می‌بود و در ثابت قدمی (آنها بر ایمان) محکم‌تر می‌بود.');
INSERT INTO "badkhashani" VALUES(4,67,'4:67','و البته در آن صورت به آنها از نزد خود ثواب بزرگ می‌دادیم.');
INSERT INTO "badkhashani" VALUES(4,68,'4:68','و البته آنها را به راه راست هدایت می‌کردیم.');
INSERT INTO "badkhashani" VALUES(4,69,'4:69','و هر کس که از الله و رسول اطاعت کند؛ پس این گروه (در روز قیامت) با آنانی همراه خواهند بود که الله به آنها نعمت‌ها بخشیده است، مانند پیغمبران و صادقان و شهیدان و نیکان، و این گروه چه رفقای خوبی‌اند.');
INSERT INTO "badkhashani" VALUES(4,70,'4:70','این مقام و مرتبه از نزد الله است، و الله (از نگاه دانائی) به احوال بندگان خود کافی است.');
INSERT INTO "badkhashani" VALUES(4,71,'4:71','ای کسانی که ایمان آورده‌اید، هوشیار و بیدار باشید و آمادگی تان را (برای رویارویی با دشمن) بگیرید، پس (برای جهاد) دسته دسته یا همه یکجا بیرون شوید.');
INSERT INTO "badkhashani" VALUES(4,72,'4:72','البته از شما کسانی هست که (در بیرون شدن برای جهاد) سستی و تأخیر می‌کند، پس اگر مصیبتی به شما برسد (در آن وقت) می‌گوید: الله بر من احسان و نعمت (خود را ارزانی کرد) که با آنها (در وقت جنگ ) حاضر نبودم.');
INSERT INTO "badkhashani" VALUES(4,73,'4:73','و اگر به شما نعمتی از طرف الله برسد، البته (این منافق) می‌گوید: چنانکه گویا در بین شما و او هیچ دوستی نبود، ای کاش با ایشان (مسلمانان) بودم تا به کامیابی بزرگ فائز میشدم.');
INSERT INTO "badkhashani" VALUES(4,74,'4:74','پس آنانی که زندگی دنیا را به آخرت می‌فروشند، باید در راه الله بجنگند، و هرکه در راه الله بجنگد، پس کشته یا (بر دشمن) غالب شود، پس به زودی ثوابی بزرگ به او خواهیم داد.');
INSERT INTO "badkhashani" VALUES(4,75,'4:75','و چه شده شما را که در راه الله و در راه نجات ضعیفان نمی‌جنگید؟ (که اعم) از مردان و زنان و کودکان‌اند، آنانی که می‌گویند: ای پروردگار ما! ما را از این قریه‌ای که اهل آن ظالم‌اند بیرون کن، و برای ما از جانب خود کار سازی مقرر کن، و برای ما از نزد خود مددگاری مقرر کن.');
INSERT INTO "badkhashani" VALUES(4,76,'4:76','آنانی که مؤمن‌اند در راه الله می‌جنگند و آنانی که کافر‌اند در راه طاغوت (شیطان) می‌جنگند، پس (ای مؤمنان) با دوستان شیطان بجنگید، چونکه حیلۀ شیطان ضعیف است.');
INSERT INTO "badkhashani" VALUES(4,77,'4:77','آیا ندیدی آنانی را که به ایشان گفته شد (فعلا) دست از جنگ بردارید، و نماز را برپا دارید و زکات بدهید، پس چون بر آنها جنگ فرض شد ناگهان گروهی از آنها، از مردم (کفار) می‌ترسند؛ مانند ترس‌شان از الله، بلکه سخت‌تر از آن، و گفتند: ای پروردگار ما! چرا بر ما جنگ را (حالا) فرض کردی؟ چرا جنگ را تا مدتی کوتاه به تأخیر نیانداختی؟ بگو: که بهره مندی دنیا کم است، (ولی کامیابی) آخرت برای کسی که تقوا پیشه کند، بهتر است. و بر شما به اندازۀ رشتۀ پشت خستۀ خرما هم ظلم نخواهد شد.');
INSERT INTO "badkhashani" VALUES(4,78,'4:78','در هر جائی باشید مرگ به سراغ تان می‌آید اگر چه در قلعه‌های مستحکم باشید، و اگر به آنها (منافقان) خوبی و پیروزی برسد می‌گویند: این از طرف الله است، و اگر خساره و بدیی به آنها برسد می‌گویند: این از طرف تو است، بگو: که همه از طرف الله است، پس چه شده است این گروه را که هیچ سخنی را (در موقع فهم و درک) نمی‌فهمند.');
INSERT INTO "badkhashani" VALUES(4,79,'4:79','(ای انسان) هر چه از خوبی و فائده بتو برسد از (فضل) الله است، و هر چه از بدی و مصیبت تو برسد، پس از نفس توست، و (ای پیغمبر، تو مسؤل نیکی و بدی آنها نیستی) چون ما تو را پیغام رسان رسالت خود به مردم فرستاده‌ایم، و گواهی الله در این مورد کافی است.');
INSERT INTO "badkhashani" VALUES(4,80,'4:80','و هرکس رسول (الله) را اطاعت کند، در حقیقت الله را اطاعت کرده است، و هرکس (از پیروی رسول الله) سرکشی کند، (پروا مکن) چون ما تو را بر آنان نگهبان (از گناه) نفرستادیم.');
INSERT INTO "badkhashani" VALUES(4,81,'4:81','و (منافقان در مجلس روزانۀ تو) می‌گویند: کار ما اطاعت و فرمانبرداری است، (لیکن) پس چون از نزد تو بیرون روند، گروهی از آنان در جلسه‌های شبانه بر خلاف سخنان تو عمل می‌کنند (تدبیر و چاره اندیشی می‌کنند) والله آنچه در جلسات شبانه (شان) می‌گذرد می‌نویسد، پس (از انتقام) آنها اعراض کن و بر الله توکل کن، (چون) کافی است که الله کارساز و پشتیبان باشد.');
INSERT INTO "badkhashani" VALUES(4,82,'4:82','آیا در مفهوم و معانی قرآن فکر و تدبر نمی‌کنند؟ و (در حالیکه) اگر قرآن از طرف غير الله بود البته در (احکام و معانی) آن اختلافات بسیاری می‌یافتند.');
INSERT INTO "badkhashani" VALUES(4,83,'4:83','و هرگاه خبری (شایعۀ) دربارۀ امن یا خوف (مسلمانان به منافقان) برسد (فورا) آن را نشر می‌کنند، و اگر آن شایعات را (قبل از پخش نمودن) به پیغمبر یا به صاحبان امر (رأی) برمی گرداندند البته آنانی که اهل فهم و درک و استنباط‌اند (نتیجه و حقیقت) آن خبر را می‌یافتند، و اگر فضل الله و رحمت او بر شما نبود، البته جز اندکی از شما؛ پیروی شیطان را می‌کردید.');
INSERT INTO "badkhashani" VALUES(4,84,'4:84','پس در راه الله بجنگ، که جز خودت کسی بر آن مکلف نیست و مؤمنان را (به جهاد) تشویق کن، امید است که الله از سختی (و گزند) کافران جلوگیری کند، و الله قوی تر، و سزایش (نیز) دردناکتر است.');
INSERT INTO "badkhashani" VALUES(4,85,'4:85','هرکس شفاعت (سفارش ) نیکی بکند، از آن سهمی می‌برد. و هرکس شفاعت بد (ناجائز) بکند، از آن سهمی می‌برد، و الله بر هر چیزی قادر است.');
INSERT INTO "badkhashani" VALUES(4,86,'4:86','و چون بر شما سلامی تقدیم شود، پس با کلمات بهتر از آن، یا همانند آن، جواب دهید، بی‌گمان الله بر هر چیز حسابگر است.');
INSERT INTO "badkhashani" VALUES(4,87,'4:87','الله ذاتی است که هیچ معبودی (برحق) جز او نیست، البته در روز قیامت که هیچ شکی در آن نیست، شما را جمع می‌کند. و صادق‌تر از الله در سخن کیست.');
INSERT INTO "badkhashani" VALUES(4,88,'4:88','پس شما را چه شده که دربارۀ منافقین دو گروه شده‌اید؟ در حالیکه الله آنها را به سزای آنچه کرده‌اند، سرنگون ساخته است، آیا می‌خواهید کسی را که الله گمراه ساخته است هدایت کنید؟ و حال آنکه هیچگاه برای کسی که الله او را گمراه ساخته راهی به‌سوی هدایت نخواهی یافت.');
INSERT INTO "badkhashani" VALUES(4,89,'4:89','و آنان دوست دارند که شما نیز کافر شوید طوری که خودشان کافر شده‌اند، تا شما و آنها (در کفر) برابر شوید، پس از آنها هیچ دوستی نگیرید، تا آن که (ایمان آورده) در راه الله هجرت کنند، پس اگر (از ایمان و هجرت) اعراض نمودند، پس هرجایی آنها را یافتید، آنها را به اسارت بگیرید و بکشید، و از آنها (کسی را) دوست و مددگار خود مگیرید.');
INSERT INTO "badkhashani" VALUES(4,90,'4:90','مگر آنانی که با قومی که بین شما و آنها پیمان است، پیوند (رابطه) دارند (نکشید)، یا آنانی که نزد شما بیایند در حالیکه سینه‌هایشان از جنگیدن با شما، یا از جنگیدن با قوم خود، به تنگ آمده است، و اگر الله می‌خواست آنها را بر شما مسلط می‌ساخت، باز حتما با شما می‌جنگیدند، پس اگر آنها از (جنگ با) شما دوری جستند و با شما نجنگیدند و به شما پیشنهاد صلح دادند، پس الله برای شما راهی (قتال عليه) آنها را قرار نداده است.');
INSERT INTO "badkhashani" VALUES(4,91,'4:91','عنقریب گروه دیگری (از مشرکین) را خواهید یافت که می‌خواهند هم از (ضرر) شما در امن باشند و هم از (ضرر) قوم خود. و هرگاه به فتنه (قتال عليه مسلمانان) باز گردانیده شوند، در آن سرنگون می‌شوند (در جنگ علیه مسلمانان سهم می‌گیرند)، پس اگر از جنگ با شما کناره گیری نکردند و به شما پیشنهاد صلح نکردند و از جنگیدن با شما دست نگرفتند، پس در هر جایی که آنها را یافتید به اسارت بگیرید و آنها را بکشید، و این گروهی است که ما برای شما عليه آنها دلیل ظاهر (بر جواز قتل ایشان) قرار داده‌ایم.');
INSERT INTO "badkhashani" VALUES(4,92,'4:92','و برای هیچ مؤمنی شایسته (جائز) نیست که مؤمن دیگری را بکشد، مگر اینکه به خطا (بدون قصد باشد)، و هر کس که مؤمنی را به خطا بکشد، پس بر او (لازم) است که برده مؤمنی را آزاد کند، و به ورثه مقتول دیت بسپارد، مگر اینکه (اولیای مقتول) صدقه (= معاف) کنند، پس اگر مقتول از گروه دشمن شما باشد (و ليكن خودش) مسلمان است، پس بر او (قاتل لازم) است که یک بردۀ مسلمان را آزاد کند، و اگر (مقتول) از قومی باشد که میان شما و آنها عهدی است، پس باید به خانواده وی (مقتول) دیت سپرده شود و برده مؤمنی را آزاد کند، واگر برده نیافت، پس بر او (لازم) است که دو ماه روزه پی در پی بگیرد، (این کفاره) بخاطر (قبولیت) توبه (قاتل) از جانب الله می‌باشد، و الله دانای باحکمت است.');
INSERT INTO "badkhashani" VALUES(4,93,'4:93','و هرکس که مؤمنی را قصداً (بدون هیچ سببی که موجب قتل باشد) بکشد، پس سزای او دوزخ است، که همیشه در آن می‌باشد و مورد غضب الله قرار می‌گیرد، و الله او را لعنت می‌کند، و برای او عذاب بس بزرگی را آماده کرده است.');
INSERT INTO "badkhashani" VALUES(4,94,'4:94','ای کسانی که ایمان آورده‌اید، وقتی در راه الله (برای جهاد) سفر کردید، پس به درستی تحقیق کنید (تا کافر را از مسلمان تشخیص دهید) و به کسی که به شما (كلمۀ) اسلام را تقدیم کرد، نگوید تو مؤمن نیستی (و قتل تو رواست) تا به وسیلۀ آن (کشتن مؤمن)، متاع زندگانی دنیا (مال غنیمت) را بدست آرید، و نزد الله غنيمت‌های بسیار است، شما نیز پیش از آن (اسلام) همانطور بودید، پس الله بر شما احسان کرد (که مسلمان شدید)، پس به درستی تحقیق (پرس و جو) کنید، بی‌گمان الله به آنچه می‌کنید آگاه است.');
INSERT INTO "badkhashani" VALUES(4,95,'4:95','مؤمنان خانه نشین غیر معذور (و نشسته از جهاد)، با مجاهدانی که در راه الله با مال و جان خود می‌جنگند، برابر نیستند، (بلکه) الله آنانی را که با مال و جانهای خود جهاد می‌کنند درجۀ بر نشستگان (از جهاد) فضیلت داده است و الله همه (ایشان) را وعده نیک داده است، و (لیکن) الله مجاهدان را بر نشستگان به ثواب بس بزرگ برتری داده است.');
INSERT INTO "badkhashani" VALUES(4,96,'4:96','و (نیز) مراتبی بس بزرگ از طرف او (الله)، و مغفرت و رحمت را (به ایشان می‌دهد)، و الله بسیار آمرزنده (و) بسیار مهربان است.');
INSERT INTO "badkhashani" VALUES(4,97,'4:97','البته آنان که فرشته‌ها ارواح‌شان را قبض می‌کنند در حالی که (به سبب ترک هجرت) به جانهای خویش ظلم کردند، قبض کنندگان ارواح به آنها می‌گویند: شما در چه حال بودید؟ (در جواب) گفتند: ما در زمین ضعیف بودیم، (فرشته‌ها) گویند: آیا زمین الله وسيع نبود تا در آن هجرت کنید؟، پس این گروه مسكن‌شان دوزخ است، و (دوزخ) چه بد سر انجامی است.');
INSERT INTO "badkhashani" VALUES(4,98,'4:98','مگر مستضعفان واقعی، از مردان و زنان و کودکانِ که هیچ چاره (و وسيلۀ) ندارند و هیچ راهی (برای هجرت) نمی‌یابند.');
INSERT INTO "badkhashani" VALUES(4,99,'4:99','پس این گروه امید است که الله آنها مورد عفو قرار دهد، و الله همیشه عفو کننده (و) آمرزنده است.');
INSERT INTO "badkhashani" VALUES(4,100,'4:100','و هر کس در راه الله هجرت کند، در زمین اقامتگاه‌های زیاد و کشادگی (در رزق) خواهد یافت، و هرکه از خانۀ خود به قصد هجرت به طرف الله و رسول او بیرون شود، باز مرگش فرا رسد، پس یقینا که ثواب او بر ذمۀ الله ثابت شده است، و الله آمرزندۀ مهربان است.');
INSERT INTO "badkhashani" VALUES(4,101,'4:101','چون در زمین سفر کردید، پس بر شما گناهی نیست که نماز را کوتاه کنید (نمازهای چهار رکعتی را دو ركعت بخوانید) اگر ترسیدید که کافران شما را در فتنه (و در بلا) می‌اندازند، چون کفار برای شما دشمن علنی و آشکار‌اند.');
INSERT INTO "badkhashani" VALUES(4,102,'4:102','و چون (تو ای محمد، در حالت خوف) در میان آنها (مؤمنان) بودی و برای آنها نماز برپا داشتی، پس باید گروهی از آنها همرای تو (در نماز) بایستند، و باید (هر دو گروه) سلاح خود را نیز بگیرند، پس چون این گروه سجده کردند (یک رکعت را ادا کردند)، پس آن گروه دیگر باید در پشت سر شما (در مقابل دشمن) قرار گیرند و باید آن گروه دیگر که (تاهنوز) نماز نخوانده‌اند، باید بیاید و با تو نماز بخوانند، و باید (آنها نیز) احتیاط و آمادگی و سلاح خود را بگیرند، چون کافران آرزو دارند که شما از سلاحها و سامان خود، غافل شوید، پس یکباره بر شما حمله کنند، و اگر به سبب باران یا بیماری به زحمت بودید، پس بر شما گناهی نیست که سلاح خود را (در نماز) بر زمین بگذارید و ليكن هوشیار جان خود باشید، یقینا الله برای کافران عذاب رسواکننده‌ای آماده کرده است.');
INSERT INTO "badkhashani" VALUES(4,103,'4:103','وقتی نماز را ادا کردید، پس الله را (بعد از نماز هم) ایستاده و نشسته و بر پهلوهای خود یاد کنید و چون آسوده خاطر و آرام شدید، پس نماز را (به طريقۀ قبل از جنگ و در اوقاتش) ادا کنید، زیرا نماز بر مؤمنان در اوقات معین فرض شده است.');
INSERT INTO "badkhashani" VALUES(4,104,'4:104','و (در وقت جهاد) در طلب و تعقیب قوم (کافر) سستی نکنید، اگر شما درد و رنج می‌بینید (در جنگ) پس آنان (کفار) نیز همانطور که شما درد و رنج می‌بینید، درد و رنج می‌بینند و (لیکن) شما (ثوابی را) از الله امید دارید که آنها امید ندارند، و الله دانای باحکمت است.');
INSERT INTO "badkhashani" VALUES(4,105,'4:105','البته ما این کتاب (قرآن) را به حق بر تو نازل کردیم (که دارای احکام حق و واقعی است)، تا بوسیلۀ آنچه الله به تو آموخته، میان مردم قضاوت کنی، و برای خائنان (بخاطر ایشان با اصحاب حق) جدال و خصومت مکن.');
INSERT INTO "badkhashani" VALUES(4,106,'4:106','و از الله آمرزش بخواه؛ چونکه الله آمرزندۀ مهربان است.');
INSERT INTO "badkhashani" VALUES(4,107,'4:107','و از طرف آنانی که به خویشتن خیانت می‌کنند جدال (دفاع) مکن؛ زیرا الله کسی را که خائن و گنهگار باشد، دوست نمی‌دارد.');
INSERT INTO "badkhashani" VALUES(4,108,'4:108','ایشان (اعمال زشت خود را) از مردم می‌پوشانند اما از الله نمی‌پوشانند، (چگونه از الله می‌پوشانند) در حالیکه او تعالی با آنهاست؛ وقتی که در شب (در میان خود) در باره نقشه‌ها و سخنانی که الله آن را نمی‌پسندد طرح‌ریزی می‌کنند، و الله به آنچه می‌کنند (با علم خود) احاطه دارد.');
INSERT INTO "badkhashani" VALUES(4,109,'4:109','آگاه! شما آن گروهی هستید که از خیانت کاران در زندگی دنیا دفاع می‌کردید، پس کیست که در روز قیامت به پیشگاه الله از آنها دفاع کند؟ یا کیست که برای (دفاع) از آنها (در روز قیامت) وکیل باشد؟');
INSERT INTO "badkhashani" VALUES(4,110,'4:110','و هر کس کار بدی را (در حق دیگران) انجام دهد، یا بر خود ظلم کند، باز از الله مغفرت بخواهد (توبۀ او قبول خواهد شد چون) الله را آمرزندۀ مهربان می‌یابد.');
INSERT INTO "badkhashani" VALUES(4,111,'4:111','و هرکس گناهی انجام دهد، تنها آن (گناه) را به ضرر خویش مرتکب شده است، و الله دانای باحکمت است.');
INSERT INTO "badkhashani" VALUES(4,112,'4:112','و هر کس خطا یا گناهی مرتکب شود (از روی قصد یا خطا) باز آن (گناه) را به گردن بی‌گناهی باندازد، البته بهتان و گناه آشکار را بر دوش کشیده است.');
INSERT INTO "badkhashani" VALUES(4,113,'4:113','و اگر فضل و مهربانی الله بر تو نبود، البته گروهی از آنها (منافقین یهود) قصد داشتند که تو را (از فیصلۀ حق) گمراه کنند، اما جز خود را گمراه نمی‌کنند و به تو هیچ ضرری رسانده نمی‌توانند و (فضل الله این است که) الله بر تو کتاب و حکمت را نازل کرد و چیزی را که تو نمی‌دانستی به تو آموخت، و فضل الله بر تو بسیار بزرگ است.');
INSERT INTO "badkhashani" VALUES(4,114,'4:114','در بسیاری از سخنان و مشوره‌های سری آنها خیر و فایده‌ای نیست به جز (سخن) کسی که به صدقه، یا کار نیک، یا صلح در بین مردمان حکم کند، و هرکس برای کسب رضای الله چنین (اعمال نیک) کند، پس به زودی او را ثواب بس بزرگ خواهیم داد.');
INSERT INTO "badkhashani" VALUES(4,115,'4:115','و هرکس بعد از اینکه هدایت برایش ظاهر شد، باز به مخالفت رسول الله پرداخت و راه غیر مؤمنان را (غیر دین اسلام را) پیروی کرد؛ او را به همان جهتی که روی آورده می‌سپاریم، و او را به دوزخ داخل می‌کنیم، و (دوزخ) بد سرانجامی است.');
INSERT INTO "badkhashani" VALUES(4,116,'4:116','البته الله از اینکه با او شریک قرار داده شود نمی‌بخشد، و پایین‌تر از شرک را برای کسی که بخواهد می‌بخشد، و هر که با الله شریک مقرر کند يقينا به گمراهی دور از حق گمراه شده است.');
INSERT INTO "badkhashani" VALUES(4,117,'4:117','(مشرکها در رفع حاجات خود) غیر از الله معبودانی را که مؤنث‌اند می‌خوانند (به نام‌های مؤنث یاد می‌شوند، و در حقیقت) شیطان سرکش را می‌خوانند.');
INSERT INTO "badkhashani" VALUES(4,118,'4:118','الله او (شیطان) را از رحمت خود دور کرده است؛ وقتی گفت: من حتما از بندگان تو حصه و مقدار معینی می‌گیرم (تابع خود می‌سازم).');
INSERT INTO "badkhashani" VALUES(4,119,'4:119','(شیطان گفت:) و حتما آنان (اولاد آدم) را گمراه می‌سازم و به آرزوهای (خام) مشغول‌شان می‌سازم، و حتما آنها را دستور می‌دهم که گوش‌های چارپایان را ببرند (برای مشخص ساختن آنها برای بتان و معبودان باطل) و حتما به آنها دستور می‌دهم که فطرت و آفرینش الله را تغیر دهند. و هر که شیطان را به جز الله دوست خود بگیرد، بیگمان زیان کردہ زیانی آشکار.');
INSERT INTO "badkhashani" VALUES(4,120,'4:120','شیطان به آنها وعده‌های خام می‌دهد و ایشان را در آرزوها غرق می‌کند و شیطان جز فریب، وعدۀ دیگر به آنها نمی‌دهد.');
INSERT INTO "badkhashani" VALUES(4,121,'4:121','این گروه ( پیرو شیطان) سرانجام‌شان دوزخ است، و از آن راه گریز نیابند.');
INSERT INTO "badkhashani" VALUES(4,122,'4:122','و آنانی که ایمان آورده‌اند و کارهای نیک انجام داده‌اند به زودی آنها را در باغ‌های که از زیر آنها نهرها جاری می‌باشد وارد می‌کنیم، در آن همیشه جاودانند، (این را الله وعده داده است و) وعدۀ الله راست است، و چه کسی در سخن از الله راستگوتر است؟');
INSERT INTO "badkhashani" VALUES(4,123,'4:123','(وعدۀ الله) بر وفق آرزوهای شما و آرزوهای اهل کتاب نیست، (بلکه) هر کس بدی کند در برابر آن سزا داده می‌شود، و برای خود غیر از الله هیچ کارساز و مددگاری نمی‌یابید.');
INSERT INTO "badkhashani" VALUES(4,124,'4:124','و هر کس که کارهای نیک کند، خواه مرد باشد یا زن، در حالیکه مؤمن باشند، پس ایشان داخل جنت می‌شوند و به اندازۀ شگاف خسته خرما مورد ظلم قرار نمی‌گیرند.');
INSERT INTO "badkhashani" VALUES(4,125,'4:125','و کیست خوب‌تر به اعتبار دین از آن کس که روی خود را برای الله منقاد گرداند، و نیکوکار باشد و از ملت ابراهیم پیروی کند در حالیکه از ادیان باطل نفرت داشت، و الله ابراهیم را دوست خود گرفت.');
INSERT INTO "badkhashani" VALUES(4,126,'4:126','و هرچه در آسمان‌ها و هر چه در زمین است، از الله است. و الله بر هر چیز (از نگاه علم و قدرت) احاطه دارد.');
INSERT INTO "badkhashani" VALUES(4,127,'4:127','و از تو دربارۀ زنان فتوا می‌طلبند، بگو: الله به شما درباره زنان فتوا می‌دهد و (فتوا می‌دهد در مورد) آنچه بر شما در قرآن (در اول این سوره) تلاوت می‌شود که دربارۀ زنان یتیم نازل شده که حق مقرر آنها را (از میراث و مهر) به ایشان نمی‌دهید، و (در عین حال) به نکاح آنها رغبت دارید. و شما را دربارۀ کودکان ضعیف (که میراث آنها را نخورید فتوا می‌دهد) و اینکه در حق يتيمان به عدل و انصاف رفتار کنید، و هر چه از کار نیک انجام دهید (ضایع نمی‌شود) بی‌گمان الله به آن داناست.');
INSERT INTO "badkhashani" VALUES(4,128,'4:128','و اگر زنی از بی‌میلی یا نفرت شوهر خود بیم داشت، پس بر آن دو گناهی نیست که در میان خویش صلح آورند به نوعی از (انواع) صلح، و صلح (و آشتی در هرکاری) بهتر است و (طبعا) بخل در هر نفس وجود دارد، و اگر (با زنان) خوبی کنید و (از حق تلفی آنها) پرهیز کنید، (این به خیر شما است) یقینا الله به آنچه می‌کنید آگاه است.');
INSERT INTO "badkhashani" VALUES(4,129,'4:129','و هرگز نمی‌توانید در بین زنان (تان) عدالت کنید، اگر چه بر عدالت (میان آنها) حریص باشید، پس به یک طرف کاملا تمایل نکنید تا آن زن دیگر را آویزان (و بلاتکلیف) بگذارید (که نه او را طلاق دهید و نه حقش را ادا کنید) و (اگر با زنان تان) صلح کنید و پرهیزگاری کنید (به نفع شماست)؛ بی‌گمان الله آمرزندۀ مهربان است.');
INSERT INTO "badkhashani" VALUES(4,130,'4:130','و اگر آن دو (زن و شوهر) از یکدیگر جدا شوند، پس الله هریک را از رحمت گشاده خود بی‌نیاز می‌گرداند، و الله گشایشگر باحکمت است.');
INSERT INTO "badkhashani" VALUES(4,131,'4:131','و هر چه در آسمان‌ها و هرچه در زمین است، از الله است، و به تاکید ما به آنانی که پیش از شما کتاب (آسمانی) داده شد، و نیز به شما حکم کردیم که از الله بترسید، و اگر کافر شوید (به الله هیچ زیانی نیست)، زیرا يقينا هر چه در آسمان‌ها و هر چه در زمین است از الله می‌باشد، و الله بی‌نیاز (و دارای صفات کمال) ستوده است.');
INSERT INTO "badkhashani" VALUES(4,132,'4:132','و هرچه در آسمان‌ها و هر چه در زمین است، برای الله است، و کافی است که الله کارساز مخلوقات (در تدبیر و ادارۀ آسمان‌ها و زمین) باشد.');
INSERT INTO "badkhashani" VALUES(4,133,'4:133','(ای مردم) اگر الله بخواهد شما را از بین می‌برد (هلاک می‌سازد)، و مردمان دیگر را می‌آورد (ایجاد می‌کند)، و الله بر این کار قادر است.');
INSERT INTO "badkhashani" VALUES(4,134,'4:134','و هرکس ثواب دنیا را بخواهد؛ پس (بداند که) ثواب دنیا و آخرت نزد الله است، و الله شنوای بیناست.');
INSERT INTO "badkhashani" VALUES(4,135,'4:135','ای کسانی که ایمان آورده‌اید، همیشه بر پا کنندۀ عدل باشید و برای الله گواهی دهید، اگر چه (آن گواهی) به ضرر خودتان، یا والدین، یا خویشاوندان تان باشد، و اگر (یک جانب دعوا) مال دار یا تنگ دست باشند، پس الله نسبت به آنها اولی‌تر است، پس از هوای نفس پیروی مکنید تا (تجاوز نکنید و) به انصاف رفتار کنید، و اگر (در گواهی دادن) زبان بپیچانید (و درست گواهی ندهید) یا (از گواهی دادن به حق) اعراض نمائید، پس (بدانید که) الله به آنچه می‌کنید خبردار است.');
INSERT INTO "badkhashani" VALUES(4,136,'4:136','ای کسانی که ایمان آورده‌اید، به الله و رسول او و به کتابی که بر پیغمبرش نازل کرده است، و به کتابی که پیشتر نازل کرده، ایمان محکم و جازم آورید. و هر که به الله و فرشته‌های او و کتاب‌های او و پیغمبران او و به روز آخرت کفر ورزد، پس یقینا به گمراهی دور از حق گمراه شده است.');
INSERT INTO "badkhashani" VALUES(4,137,'4:137','البته آنانی که ایمان آوردند باز کافر شدند، باز ایمان آوردند، باز کافر شدند، باز بر کفر خود افزودند؛ الله آنها را هرگز نمی‌بخشد، و آنها را (به راه حق) هدایت نمی‌کند (چون که منافق اند).');
INSERT INTO "badkhashani" VALUES(4,138,'4:138','منافقان را مژده ده که برای آنها عذاب دردناک است.');
INSERT INTO "badkhashani" VALUES(4,139,'4:139','کسانی که کفار را به جای مؤمنان دوست خود قرار می‌دهند، آیا عزت را نزد آنان می‌جویند؟ بی‌گمان همه عزت از الله است.');
INSERT INTO "badkhashani" VALUES(4,140,'4:140','و البته الله بر شما در قرآن (این حکم را) نازل کرده است که چون شنیدید که آیات الله مورد کفر و تمسخر قرار می‌گیرد پس با آنان منشینید (در مجالس‌شان شرکت نکنید) تا آن که به سخن دیگر شروع کنند، و گرنه شما هم (در کفر و استهزا) مثل آنها خواهید بود؛ البته الله همه منافقین و کفار را در دوزخ یکجا جمع می‌کند.');
INSERT INTO "badkhashani" VALUES(4,141,'4:141','آنانی که انتظار آمدن حوادثات را بر شما می‌کشند، پس اگر از طرف الله به شما فتحی برسد می‌گویند: آیا ما با شما نبودیم؟ واگر سهمی (از غلبه و پیروزی) نصيب کفار باشد، می‌گویند: آیا ما (برای محافظۀ شما) بر شما احاطه نکرده بودیم، و آیا شما را از ضرر مؤمنان منع نکردیم؟ پس الله در روز قیامت میان تان (شما مؤمنان و منافقان) فیصله خواهد کرد، و هرگز الله برای کافران، بر (ضرر) مؤمنان راه غلبه قرار نداده است.');
INSERT INTO "badkhashani" VALUES(4,142,'4:142','البته منافقین (به گمان خود) با الله نیرنگ می‌کنند، در حالیکه الله (با تدبیر خود) به آنها نیرنگ می‌زند، و چون (منافقان) به‌سوی نماز برخیزند؛ با کسالت برمی خیزند، به مردم (تظاهر نموده نیکی و صلاح خود را) نشان می‌دهند، و الله را یاد نمی‌کنند مگر بسیار کم.');
INSERT INTO "badkhashani" VALUES(4,143,'4:143','در میان ایمان و کفر حیران و متردد‌اند، نه به طرف اینها (مؤمنان)‌اند و نه به طرف آنان (کفار) و هرکس را که الله گمراه کند پس هرگز راهی (هدایت) برای او نخواهی یافت.');
INSERT INTO "badkhashani" VALUES(4,144,'4:144','ای کسانی که ایمان آورده‌اید، کافران را دوست نگیرید به جای مؤمنان، آیا (کفار را دوست گرفته) می‌خواهید در پیشگاه الله دلیلی واضح عليه خود قرار دهید؟.');
INSERT INTO "badkhashani" VALUES(4,145,'4:145','يقينا منافقین در طبقه زیرین دوزخ‌اند، و (ای مخاطب) برای آنها هیچ مددگاری نخواهی یافت.');
INSERT INTO "badkhashani" VALUES(4,146,'4:146','مگر آنانی که توبه کردند و (عمل خود را) اصلاح کردند و به دین الله چنگ زدند، و دین (عبادت) خود را برای الله خالص کردند، پس این گروه همرای کسانی‌اند که ایمان آورده‌اند، و زود است که الله به مؤمنان ثواب بزرگ دهد.');
INSERT INTO "badkhashani" VALUES(4,147,'4:147','اگر شکر (الله را) کنید و ایمان آورید، الله به عذاب (دادن) شما چه می‌کند؟ و الله قدردان (نیکی‌ها و) داناست (از نیکی بندگان آگاه می‌باشد).');
INSERT INTO "badkhashani" VALUES(4,148,'4:148','الله فریاد کشیدن به بدزبانی (دشنام و فحش ) را دوست ندارد مگر از کسی که بر او ظلم شده است، و الله شنوای داناست.');
INSERT INTO "badkhashani" VALUES(4,149,'4:149','اگر نیکی را ظاهر کنید، یا آن را بپوشانید، یا از بدی کسی بگذرید (عفو کنید)، پس یقینا الله عفو کننده قادر است.');
INSERT INTO "badkhashani" VALUES(4,150,'4:150','البته آنانی که به الله و پیغمبرانش کفر می‌ورزند و می‌خواهند که (در ایمان آوردن) بين الله و پیغمبرانش جدایی آورند، و می‌گویند: به بعضی (از پیغمبران) ایمان داریم، و بعضی دیگر را انکار می‌کنیم، و می‌خواهند در بین آن دو (کفر و ایمان) راه دیگری را برای خود بگیرند.');
INSERT INTO "badkhashani" VALUES(4,151,'4:151','این گروه به حق کافران (واقعی)‌اند و برای کافران عذاب رسواکننده آماده کرده‌ایم.');
INSERT INTO "badkhashani" VALUES(4,152,'4:152','و آنانی که به الله و پیغمبران او ایمان آورده‌اند و (در ایمان آوردن) بین یکی از آنها فرق نمی‌گذارند، این گروه زود است که الله اجر (و ثواب)‌شان را دهد، و الله آمرزندۀ مهربان است.');
INSERT INTO "badkhashani" VALUES(4,153,'4:153','اهل کتاب از تو می‌خواهند که یکباره (و یکجا) کتابی از آسمان برای آنها نازل کنی، (ليکن تو مرنج) چون از موسی بزرگتر از این را طلب کردند، و گفتند: الله را به ما آشکارا نشان بده، پس آوازی سخت آنها را گرفت به سبب ظلمشان (و هلاک شدند). باز گوساله را معبود خود قرار دادند، (و این) بعد از آن که معجزه‌های واضح برای آنها آمد، پس آن (جرم آنها) را هم معاف کردیم و به موسی حجت واضح و آشکار دادیم.');
INSERT INTO "badkhashani" VALUES(4,154,'4:154','و کوه طور را برای عهد گرفتن از ایشان، بالای سرشان برداشتیم، و به آنها گفتیم: سجده کنان از دروازه داخل شوید، و به آنها گفتیم: در روز شنبه تجاوز نکنید، (ماهی را شکار نکنید) و از آنها پیمان محکم گرفتیم.');
INSERT INTO "badkhashani" VALUES(4,155,'4:155','پس به سبب عهد شکنی و کفرشان به آیات الله و کشتن پیغمبران به ناحق و گفتن این سخن که بر دل‌های ما پرده است (لذا ما نمی‌فهمیم؛ ما آنان را عذاب کردیم) بلکه الله به سبب كفرشان، بر دل‌هایشان مهر نهاده است و ايمان نمی‌آرند مگر اندکی (از ایشان).');
INSERT INTO "badkhashani" VALUES(4,156,'4:156','و به سبب کفر آنها و سخنان آنها که بهتان بزرگی بر مریم بستند (مورد غضب ما قرار گرفتند).');
INSERT INTO "badkhashani" VALUES(4,157,'4:157','و به خاطری (مورد لعنت و عذاب ما قرار گرفتند) که گفتند: ما مسیح؛ عیسی پسر مریم را که رسول الله بود کشتیم، در حالیکه آنان او (عیسی) را نکشتند و نه (او را) به دار کشیدند، بلکه کار بر آنان مشتبه شد (و کسی مشابه عیسی را کشتند)، یقینا آنانی که در بارۀ او (عیسی) اختلاف کردند البته تا هنوز در شک‌اند، و هیچ دلیل و علم يقينی (راجع به قتل او) ندارند جز پیروی از گمان، و به یقین او را نکشتند.');
INSERT INTO "badkhashani" VALUES(4,158,'4:158','بلکه الله او را به نزد خود بالا برد، و الله غالب باحکمت است.');
INSERT INTO "badkhashani" VALUES(4,159,'4:159','وهیچ فردی از اهل کتاب نیست، مگر این که قبل از مرگش به (نبوت) عیسی ایمان می‌آورد، و او (عیسی) روز قیامت بر آنها گواه خواهد بود.');
INSERT INTO "badkhashani" VALUES(4,160,'4:160','پس به سبب ظلمی که یهودیان مرتکب آن شدند و (به سبب آن که) مردم را از راه الله باز می‌داشتند، چیزهای پاکیزه‌ای را که قبلا بر ایشان حلال بود، حرام کردیم.');
INSERT INTO "badkhashani" VALUES(4,161,'4:161','و به سبب سودخوردن آنها، در حالیکه از (خوردن) آن منع شده بودند، و خوردن‌شان مال مردم را به ناحق، (چیزهای پاکیزه را بر آنان حرام کردیم) و برای کافران ایشان عذاب دردناک را آماده کرده‌ایم.');
INSERT INTO "badkhashani" VALUES(4,162,'4:162','مگر آنانی که در علم راسخ‌اند از یهودیان و مؤمنان، به آنچه بر تو و آنچه قبل از تو نازل شده است، ایمان دارند. مخصوصا برپاکننده گان نماز و ادا کنندگان زکات و ایمان آورندگان به الله و به روز آخرت، این گروه را به زودی ثواب بزرگ می‌دهیم.');
INSERT INTO "badkhashani" VALUES(4,163,'4:163','(ای محمد) البته ما به تو وحی فرستادیم طوری که به نوح و پیغمبرانی که بعد از او بودند، وحی فرستادیم، (همچنین) به ابراهیم و اسماعیل و اسحاق و یعقوب، و به‌سوی نبیرگان اسحاق و به عیسی و ایوب و یونس و هارون و سلیمان، وحی کردیم. و به داود زبور دادیم.');
INSERT INTO "badkhashani" VALUES(4,164,'4:164','و به پیغمبرانی که قصۀ آنها را پیش از این (داستان) بر تو بیان کردیم، وحی کردیم، و به پیغمبرانی که قصۀ آنها را بر تو بیان نکرده‌ایم نیز وحی کردیم. و الله با موسی (به وسیلۀ وحی) سخن گفت؛ سخنی خاص.');
INSERT INTO "badkhashani" VALUES(4,165,'4:165','پیغمبرانی را (فرستادیم) که مژده دهنده و بیم دهنده بودند، تا بعد از آمدن پیغمبران برای مردم در برابر الله دلیلی (یعنی بهانه ای) نباشد، و الله غالب باحکمت است.');
INSERT INTO "badkhashani" VALUES(4,166,'4:166','(گرچه کافران بابهانه جویی‌هایشان قرآن را قبول نمی‌کنند) لیکن الله (به حق بودن) آنچه بر تو نازل کرده، گواهی می‌دهد، (چون) آن را به علم خود بر تو نازل کرده، و ملائکه (نیز) به (حق بودن آن) گواهی می‌دهند و گواهی الله (برای تو) کافی است.');
INSERT INTO "badkhashani" VALUES(4,167,'4:167','البته آنانی که کفر ورزیدند و دیگران را از راه الله منع نمودند، یقینا گمراه شدند به گمراهی دور (از حق).');
INSERT INTO "badkhashani" VALUES(4,168,'4:168','هر آئینه آنانی که کافر شدند و ظلم کردند، الله بر آن نیست که آنها را ببخشد و به راهی هدایت‌شان کند.');
INSERT INTO "badkhashani" VALUES(4,169,'4:169','به جز راه دوزخ، که در آن تا ابد برای همیشه بمانند، و این (عذاب) بر الله آسان است.');
INSERT INTO "badkhashani" VALUES(4,170,'4:170','ای مردم! یقینا پیغمبر از سوی پروردگارتان با (دین) حق پیش شما آمده است، پس (به آن) ایمان آورید که به خير شماست، و اگر کفر ورزید (پس بدانید که) هر چه در آسمان‌ها و در زمین است از الله است، و الله دانای حکیم است.');
INSERT INTO "badkhashani" VALUES(4,171,'4:171','ای اهل کتاب! در دین خود غلو نکنید و دربارۀ الله نگویید مگر سخن حق (و مناسب شأن او)، جز این نیست که مسیح؛ عیسی پسر مریم، رسول الله و (پیدا شده از) کلمه اوست (کلمۀ «کن») که به مريم القا کرد و روحی است از جانب الله، پس به الله و پیغمبران وی ایمان آورید و نگویید: (معبود کائنات) سه است؛ (از عقیده تثلیث) باز آیید که به خیر شماست، جز این نیست که معبود برحق فقط الله هست و بس، پاک و منزه است از اینکه فرزندی داشته باشد، هر چه در آسمان‌ها و هر چه در زمین است، تنها از اوست، و الله برای کارسازی و تدبیر عالم کافی است.');
INSERT INTO "badkhashani" VALUES(4,172,'4:172','(چرا از بنده بودن عیسی انکار می‌کنید؟ در حالیکه) مسیح هرگز از اینکه بندۀ الله باشد ابا (و تكبر) نورزیده، و نه فرشته‌های مقرب (درگاه الهی) از بندگی او ابا می‌ورزند. و هر کس از بندگی الله ابا داشته باشد و تكبر بورزد، پس به زودی الله همه (مردم) را یکجا به دربار خویش جمع خواهد کرد (و سزای متکبران را خواهد داد).');
INSERT INTO "badkhashani" VALUES(4,173,'4:173','پس آنانی که ایمان آورده و کارهای نیک انجام داده‌اند، ثواب آنها را تمام و کامل می‌دهد، و آنها را از فضل خود بیشتر خواهد داد، و اما آنانی که از عبادت الله ابا ورزیده و تکبر نمودند، پس به عذاب دردناک گرفتارشان می‌کند و غیر از الله برای خود هیچ دوست و مددگاری نمی‌یابند.');
INSERT INTO "badkhashani" VALUES(4,174,'4:174','ای مردم! یقینا به شما از طرف پروردگارتان برهانی (قاطع) آمده است و (آن برهان این است که) ما به‌سوی شما نوری تابناک (کتابی که ظاهر کنندۀ حق از باطل است) نازل کردیم.');
INSERT INTO "badkhashani" VALUES(4,175,'4:175','پس کسانی که به الله ایمان آوردند و به (دین) او چنگ زدند، پس زود است که آنها را در جوار رحمت و فضلی از جانب خود داخل کند، و آنها را به‌سوی خود؛ به راه راست هدایت کند.');
INSERT INTO "badkhashani" VALUES(4,176,'4:176','از تو فتوا می‌طلبند (دربارۀ کلاله) بگو: الله دربارۀ کلاله به شما چنین فتوا می‌دهد که اگر مردی وفات کند و فرزندی نداشته باشد (و نه پدر) و او خواهری داشته باشد، پس نصف ترکه به خواهر می‌رسد، و آن برادر از او (خواهر) میراث می‌برد اگر (خواهر کلاله باشد) او فرزند نداشته باشد (و نه پدر)، و اگر خواهران (در صورت کلاله بودن برادر) دو تن باشند، پس برای آنان دو سوم ترکه، از آنچه برادر از خود گذاشته می‌رسد، و اگر آنان (ورثۀ کلاله) برادر و خواهر باشند، پس برای هر مرد، سهمی مانند سهم دو زن می‌رسد، الله برای شما (احکام را) بیان می‌کند تا گمراه نشوید، و الله به هر چیزی داناست.');
INSERT INTO "badkhashani" VALUES(5,1,'5:1','ای کسانی که ایمان آورده‌اید، به عهد و پیمانهای خود (با الله و مردم) وفا کنید، (خوردن گوشت) چهار پایان برای شما حلال شده، مگر آنچه حرمت آن بر شما (در آینده) تلاوت می‌شود. و شکار را در حالت احرام حلال نشمارید، بی‌گمان الله هرچه که بخواهد حکم می‌کند.');
INSERT INTO "badkhashani" VALUES(5,2,'5:2','ای کسانی که ایمان آورده‌اید، حرمت شعایر (علامات دین) الله و ماه‌های حرام (ذوالقعده ذوالحجه و محرم و رجب) و قربانی‌های قلاده دار و عازمان بیت الله که از پروردگار‌شان فضل و رضایت می‌طلبند؛ حلال نشمارید (به آن بی‌حرمتی نکنید)، و چون از احرام بیرون آمدید پس (می‌توانید) شکار کنید. و عداوت قومی به علت این که شما را از مسجد الحرام منع کردند بر آن واندارد که از حد تجاوز کنید، و بر نیکی و تقوا با یکدیگر همکاری نمایید و (یکدیگر را) بر گناه و تجاوز همکاری نکنید، و از الله بترسید، چون الله سخت عذاب دهنده است.');
INSERT INTO "badkhashani" VALUES(5,3,'5:3','(طبق وعده) بر شما حیوان خود مرده (مردار) و خون و گوشت خوک و آنچه نام غير الله (در وقت ذبح کردن) بر آن یاد شده و خفه شده و به ضرب (چوب و سنگ) مرده و از بلندی افتاده و به شاخ زده شده و (مرده) و آنچه درندگان خورده باشند، مگر آنچه را که (زنده یابید) و ذبح (شرعی) کرده باشید. و (همچنان) آنچه برای بتان ذبح شده‌اند و آنکه با تیرهای فال تعیین قسمت کنید (همه) بر شما حرام شده است. اینها همه فسق است، امروز کفار از (مغلوب ساختن) دین شما ناامید شده‌اند، پس از آنها نترسید، و از من (از الله) بترسید. امروز دین تان را به شما کامل کردم، و نعمت خود را بر شما تمام نمودم، و اسلام را برای شما (به عنوان) دین پسندیدم، پس هر کس بی‌آنکه متمایل به گناه باشد در (حال) گرسنگی مجبور (به خوردن محرمات) گردد، پس (بداند) که الله آمرزندۀ مهربان است');
INSERT INTO "badkhashani" VALUES(5,4,'5:4','از تو می‌پرسند که چه چیزی برای آنها حلال شده است؟ بگو: همه چیزهای پاکیزه برای شما حلال شده است، و (نیز) آنچه از شکار حیوانات پاره کنندۀ (شکاری) در حالیکه (آنها را) برای شکار می‌پرورانید و از آنچه الله به شما آموخته است، به آنها (سگان) می‌آموزید پس از آنچه برای شما نگاه داشته‌اند، بخورید (و ليكن) نام الله را (در وقت ارسال) بر آن یاد کنید، و از الله بترسید، بی‌گمان الله زود حساب گیرنده است.');
INSERT INTO "badkhashani" VALUES(5,5,'5:5','امروز برای شما چیزهای پاکیزه حلال شده است، و طعام اهل کتاب برای شما حلال است، و طعام شما (نیز) به آنها حلال است و زنان پاکدامن از مؤمنات، و نیز زنان پاکدامن از کسانی که پیش از شما به آنان کتاب داده شده برای شما حلال است (به شرطی که) مهر آنها را بدهید در حالیکه پاکدامن باشید نه زناکار و نه دوست پنهانی گیرنده، و هرکه به ایمان (خود یا ارکان ایمان) کفر ورزد؛ البته عمل او تباه شده، و او در آخرت از (جملۀ) زیانکاران است.');
INSERT INTO "badkhashani" VALUES(5,6,'5:6','(از علامات ايمان طهارت و نماز است پس) ای کسانی که ایمان آورده‌اید، چون به (قصد) نماز ایستادید، پس روی و دست‌های تان را تا آرنج بشویید و سرهای خود را مسح کنید، و پاهای تان را تا شتالنگ بشوید، و اگر جنب باشید پس خود را پاک کنید (غسل کنید)، و اگر مریض یا مسافر باشید، یا کسی از شما از مکان قضای حاجت بیاید، یا با زنان تماس (آمیزش) کرده باشید و آبی (برای وضو یا غسل) نیابید پس قصد خاک پاک (تیمم) کنید، پس به آن خاک روی و دست‌های تان را مسح کنید، الله نمی‌خواهد بر شما مشقتی را لازم گرداند، و لیکن می‌خواهد که شما را پاک سازد، و نعمت خود را بر شما تمام کند، تا شکر او را به جا آرید.');
INSERT INTO "badkhashani" VALUES(5,7,'5:7','و یاد کنید نعمتی را که الله بر شما انعام کرده است و پیمانی را که با شما محکم بسته است، آن وقت که گفتید: شنیدیم و اطاعت کردیم، و از الله بترسید، زیرا الله به آنچه در دلهاست آگاه است.');
INSERT INTO "badkhashani" VALUES(5,8,'5:8','ای کسانی که ایمان آورده‌اید، برای (احقاق حق) الله (طبق عهدتان) برخیزید و به عدل و انصاف گواهی دهید و عداوت با قومی شما را به بی‌عدالتی وادار نکند (بلکه) عدل کنید که (عدل و انصاف) به تقوی نزدیکتر است، و از الله بترسید؛ بی‌گمان الله به آنچه می‌کنید آگاه است.');
INSERT INTO "badkhashani" VALUES(5,9,'5:9','الله به آنانی که ایمان آوردند و کارهای نیک انجام داده‌اند، وعده کرده است که آمرزش و پاداشی بزرگ خواهند داشت.');
INSERT INTO "badkhashani" VALUES(5,10,'5:10','و آنانی که کفر ورزیدند و آیات ما را دروغ پنداشتند، همان گروه دوزخیان‌اند.');
INSERT INTO "badkhashani" VALUES(5,11,'5:11','ای کسانی که ایمان آورده‌اید، آن نعمتی را که الله بر شما ارزانی نمود یادآور شوید، آن وقت که گروهی (از دشمنان) قصد کردند که به طرف شما دست تجاوز دراز کنند (و پیغمبر را به قتل برسانند) پس الله دست‌های آنها را از شما کوتاه کرد، و از الله بترسید، و مؤمنان باید بر الله توکل کنند.');
INSERT INTO "badkhashani" VALUES(5,12,'5:12','و البته الله از بنی اسرائیل (اولاد يعقوب) پیمان محکم گرفت و از میان آنها دوازده سرپرست (انتخاب کرده و) فرستادیم. و الله (به آنان) فرمود: یقینا من با شما ام؛ اگر نماز را برپا کنید و زکات را بدهید و به پیغمبران من ایمان آرید و ایشان را مدد و یاری کنید، و به الله قرض نیک دهید (در راه او انفاق کنید)، البته از شما گناهانتان را محو می‌کنم و شما را در باغ‌های که از زیر آنها نهرها جاری است، وارد می‌کنم. پس از این (عهد و وعده)، هر کس از شما کافر شود، به راستی که از راه راست منحرف گشته است.');
INSERT INTO "badkhashani" VALUES(5,13,'5:13','پس (یهود را) به خاطر پیمان شکنی‌شان لعنت کردیم و (از رحمت خود دور ساختیم) دل‌هایشان را سخت گردانیدیم؛ سخنان (الله) را از محل آن تغییر می‌دادند، و بخشی از آنچه را که به آن پند داده شده بودند فراموش کردند، و همیشه تو بر خیانت (تازۀ) از ایشان آگاه می‌شوی، مگر اندکی از آنها (که خیانت نمی‌کنند). پس از آنها در گذر و (از لغزشهای شان) روی بگردان، یقینا الله نیکوکاران را دوست دارد.');
INSERT INTO "badkhashani" VALUES(5,14,'5:14','از آنانی که گفتند ما نصارا هستیم، پیمان گرفتیم. پس (آنان نیز مانند يهود) بخشی از آنچه را که به آن پند داده شده بودند فراموش کردند، پس ما (نیز) دشمنی و کینه را در میان‌شان تا روز قیامت قرار دادیم، و الله به زودی آنها را به آنچه می‌کردند؛ آگاه خواهد ساخت.');
INSERT INTO "badkhashani" VALUES(5,15,'5:15','ای اهل کتاب! بی‌گمان فرستادۀ ما نزد شما آمد تا بسیاری از چیزهای از کتاب (تورات و انجیل) را که پنهان کرده‌اید برای شما بیان کند و از کوتاهی‌های شما در گذرد، البته از طرف الله نور و کتاب آشکار برای شما آمده است.');
INSERT INTO "badkhashani" VALUES(5,16,'5:16','الله به وسيلۀ آن (کتاب) کسانی را که از رضای او پیروی می‌کنند، به‌سوی راه‌های سلامتی هدایت می‌کند، و به حکم خود آنها را از تاریکی‌های (کفر) به روشنی (اسلام) بیرون می‌کند، و به راه راست هدایت می‌کند.');
INSERT INTO "badkhashani" VALUES(5,17,'5:17','هر آئینه آنان که گفتند: الله همان مسیح پسر مریم است یقینا کافر شدند. بگو: اگر الله بخواهد که مسیح پسر مریم و مادرش و همه مردمان روی زمین را یکجا به هلاکت رساند؟ پس چه کسی می‌تواند الله را (از این کار) باز دارد؟ و سلطنت آسمان‌ها و زمین و هر چه در بین آنهاست از الله است، هر چه بخواهد، می‌آفریند. و الله بر هر چیزی تواناست.');
INSERT INTO "badkhashani" VALUES(5,18,'5:18','و یهود و نصارا گفتند: که ما پسران الله و دوستان اوییم (و طوری که پدر پسرش را عذاب نمی‌دهد الله ما را نیز عذاب نمی‌دهد) بگو: پس چرا (الله) شما را (در دنیا) به سبب گناهان تان عذاب می‌دهد، بلکه شما هم بشری هستید از جمله کسانی که آفریده است، هر کسی را که بخواهد مغفرت می‌کند و هر کسی را که بخواهد، عذاب میدهد. و بادشاهی آسمان‌ها و زمین و آنچه در میان آنهاست، تنها از الله است، و بازگشت همه به‌سوی اوست.');
INSERT INTO "badkhashani" VALUES(5,19,'5:19','ای اهل کتاب! البته پیغمبر ما نزد شما آمده است که برای شما بیان کند (احکام ما را) بعد از انقطاع دوران پیغامبران تا نگویید که هیچ مژده دهنده و بیم دهنده‌ای نزد ما نیامد، پس يقينا مژده دهنده و بیم دهنده نزد شما آمد، و الله بر هر چیزی تواناست.');
INSERT INTO "badkhashani" VALUES(5,20,'5:20','و یاد آور شو زمانی را که موسی به قوم خود گفت: ای قوم من، نعمت الله را بر خود یاد کنید، وقتی در میان شما پیغمبرانی قرار داد؛ و شما را بادشاهانی گردانید، و به شما چیزی داد که به هیچ کسی از مردمان جهان نداده بود.');
INSERT INTO "badkhashani" VALUES(5,21,'5:21','(و موسی گفت:) ای قوم من، در سرزمین مقدس داخل شوید که الله برای شما (داخل شدن به آن را) لازم کرده است، و بر پشت خود برنگردید (و از فرمان ما اعراض نکنید) که زیانکار می‌شوید.');
INSERT INTO "badkhashani" VALUES(5,22,'5:22','(بنی اسرائیل در جواب) گفتند: ای موسی! یقینا در آن سرزمین قوم زور آور هستند، و ما داخل آن نمی‌شویم تا آنها از آنجا بیرون نروند، پس اگر از آنجا بیرون شدند، ما حتما داخل می‌شویم.');
INSERT INTO "badkhashani" VALUES(5,23,'5:23','دو تن از آنانی که می‌ترسیدند و الله بر آنان نعمت داده بود، گفتند: از راه دروازه بر آنها درآیید (و هجوم بیاورید)، پس اگر از آن داخل شدید پس حتما غالب خواهید شد (ليکن به این شرط که) بر الله توکل کنید، اگر مؤمن هستید.');
INSERT INTO "badkhashani" VALUES(5,24,'5:24','(بنی اسرائیل در جواب) گفتند: ای موسی! ما تا ابد در آن داخل نمی‌شویم تا وقتی که آنان در آنجا هستند، پس تو و پروردگارت بروید و بجنگید، ما در اینجا می‌نشینیم.');
INSERT INTO "badkhashani" VALUES(5,25,'5:25','(موسی) گفت: ای پروردگارم! يقينا من جز اختیار خودم و برادرم را ندارم، پس در بین ما و در بین این قوم نافرمان، جدایی بانداز (تا از یکدیگر جدا و متمایز شویم).');
INSERT INTO "badkhashani" VALUES(5,26,'5:26','الله (دعای موسی را قبول فرموده) گفت: پس آن سرزمین تا چهل سال بر آنها حرام است (و به سبب نافرمانی شان) حیران و سرگردان در زمین بگردند (و به جایی نرسند) پس (تو ای موسی) بر قوم فاسق و نافرمان اندوهگین مشو.');
INSERT INTO "badkhashani" VALUES(5,27,'5:27','(و نافرمانی مردمان در مقابل الله تاریخ قدیمی دارد لهذا اى محمد) بر آنها داستان دو پسر آدم را به راستی تلاوت کن، وقتی که هر یکی از آن دو (به دربار الهی) قربانی پیش کردند، پس از یکی آن دو (هابیل) پذیرفته شد و از دیگری (قابیل) پذیرفته نشد. گفت: (قابیل به هابیل) حتما تو را می‌کشم، (هابیل) گفت: الله تنها از پرهیزگاران می‌پذیرد.');
INSERT INTO "badkhashani" VALUES(5,28,'5:28','اگر دست به‌سوی من دراز کنی تا مرا بکشی، من دستم را به‌سوی تو دراز نمی‌کنم تا تو را بکشم (چون) من از الله، پروردگار جهانیان می‌ترسم.');
INSERT INTO "badkhashani" VALUES(5,29,'5:29','چون من می‌خواهم که گناه (قتل) من و گناه (سابقۀ) خودت را (به دربار الله) ببری، پس در نتیجه از جمله دوزخیان باشی، و این (دوزخ) سزای ظالمان است.');
INSERT INTO "badkhashani" VALUES(5,30,'5:30','پس نفس او کشتن برادرش را در نظرش آسان نمود، پس او را کشت و از جمله زیانکاران شد.');
INSERT INTO "badkhashani" VALUES(5,31,'5:31','پس الله زاغی را فرستاد که زمین را می‌کاوید تا به او (قابیل) نشان دهد که چگونه مرده برادرش را (در زمین) بپوشاند، گفت: (قابیل) ای وای بر من! آیا عاجزم از اینکه مانند این زاغ باشم و جسد برادرم را (در زمین) بپوشانم؟ پس از پشیمان شدگان گردید.');
INSERT INTO "badkhashani" VALUES(5,32,'5:32','به این جهت (قتل ناحق بود که) بر بنی اسرائیل حکم کردیم که هر کس کسی را بدون اینکه کسی را کشته باشد یا در زمین فساد کرده باشد، بکشد، پس چنان است که همۀ مردم را کشته باشد، و هر کس کسی را زنده بدارد (سبب حیات انسان گردد) پس چنان است که همۀ مردم را زنده داشته است. بی‌گمان پیغمبران ما دلائل واضح برای آنان (بنی اسرائیل) آوردند، باز )هم( بسیاری از آنها در زمین (با قتل ناحق) از حد تجاوز می‌کنند.');
INSERT INTO "badkhashani" VALUES(5,33,'5:33','جز این نیست که سزای آنانی که با الله و رسول او به جنگ می‌خیزند و در زمین برای فساد سعی می‌ورزند، این است که کشته شوند (اگر نفس بی‌گناه را قتل کرده باشند) یا به دار آویخته شوند (اگر قتل و غارت کرده باشند) یا دست و پایشان از جانب مخالف بریده شود (اگر فقط غارت کرده باشند) یا از زمین (وطن) تبعید شوند. این (سزا) ذلت و رسوایی برای آنها در دنیاست و در آخرت هم برایشان عذاب بزرگ خواهد بود.');
INSERT INTO "badkhashani" VALUES(5,34,'5:34','مگر آنانی که پیش از اینکه بر (گرفتن) آنها قادر شوید، توبه کرده باشند، پس بدانید که الله آمرزندۀ مهربان است.');
INSERT INTO "badkhashani" VALUES(5,35,'5:35','ای کسانی که ایمان آورده‌اید، از الله بترسید و به‌سوی او طلب وسیله کنید (هر نیکی که شما را به بارگاه الهی نزدیک می‌کند)، و در راه او جهاد کنید تا کامیاب شوید.');
INSERT INTO "badkhashani" VALUES(5,36,'5:36','يقينا آنانی که کفر ورزیدند، اگر (بالفرض) تمام آنچه در روی زمین است از آنها باشد، و مثل آن را نیز داشته باشند، تا به عوض عذاب روز قیامت آن را (به دربار الله) فديه دهند، هرگز از آنها پذیرفته نمی‌شود، و برای آنها عذاب دردناک است.');
INSERT INTO "badkhashani" VALUES(5,37,'5:37','(در آخرت کفار) می‌خواهند از دوزخ بیرون شوند در حالیکه از آن بیرون شدنی نیستند و برای آنها عذاب همیشه و پایدار است.');
INSERT INTO "badkhashani" VALUES(5,38,'5:38','و مرد دزد و زن دزد؛ دستهای‌شان را (بعد از ثبوت آن) به سزای آنچه کرده‌اند ببرید، (این سرزنش عبرت انگیز) عذابی از طرف الله است، و الله غالب (و) باحکمت است.');
INSERT INTO "badkhashani" VALUES(5,39,'5:39','پس هر کس پس از ظلم (و گناه) خویش توبه کند، و (عمل) خود را اصلاح و درست کند، پس یقینا الله توبۀ او را قبول می‌کند، چون الله آمرزندۀ مهربان است.');
INSERT INTO "badkhashani" VALUES(5,40,'5:40','آیا ندانسته‌ای (تو ای مخاطب) که پادشاهی آسمان‌ها و زمین از الله است؟ (لذا) هر که را بخواهد عذاب می‌دهد، و هر که را بخواهد می‌بخشد. و الله بر هر چیزی قادر است.');
INSERT INTO "badkhashani" VALUES(5,41,'5:41','ای پیغمبر! آنانی که در کفر شتاب می‌کنند تو را اندوهگین نسازند، (چه) از آنانی که به زبان گفتند: که ما ایمان آوردیم، ولی دل‌هایشان ایمان نیاورده است، و چه از یهود (باشند)، آنان به سخن‌های دروغ گوش می‌دهند، و (نیز) به سخنان قوم دیگر که هنوز به نزد تو نیامده‌اند، گوش می‌دهند و کلمات (تورات) را (بعد از درک آن) از محل آن تغییر می‌دهند و (به پیروان خود) می‌گویند: اگر این (حکم تحریف شده) به شما داده شد، بگیرید (قبول کنید)، و اگر آن به شما داده نشد پس دوری کنید (محتاط باشید)، و هر کس الله گمراهی او را خواسته باشد، هرگز در برابر الله برای او اختیاری نداری (که هدایت شود)، این گروه (منافقین و یهود) کسانی‌اند که الله نخواسته است دل‌هایشان را پاک سازد، برای آنها در دنیا رسوائی است، و برای آنها در آخرت (نیز) عذاب سخت خواهد بود.');
INSERT INTO "badkhashani" VALUES(5,42,'5:42','(آنها) شنوای سخن دروغ و بسیار رشوه خوار‌اند، پس اگر (برای فيصله) نزد تو آیند پس در بین آنها فيصله کن یا از آنها روی بگردان، و اگر روی گردانی (و فیصله نکردی) پس هرگز به تو ضرری رسانده نمی‌توانند، و اگر فیصله کردی؛ پس به عدل و انصاف فیصله کن، بی‌گمان الله عدل کنندگان را دوست می‌دارد.');
INSERT INTO "badkhashani" VALUES(5,43,'5:43','و (ليكن يهودیان) چگونه تو را داور مقرر می‌کنند در حالیکه تورات نزد ایشان است و در آن حكم الله است، آن گاه باز روی می‌گردانند؟ و ایشان مؤمن نیستند (به تورات خود).');
INSERT INTO "badkhashani" VALUES(5,44,'5:44','البته ما تورات را نازل کردیم که در آن هدایت و نور است (برای بشریت). پیغمبرانی که منقاد حكم الله بودند بر طبق آن برای یهودی‌ها حکم می‌کردند. (همچنین) علمای ربانی و دانشمندانی که به حفظ كتاب الله مامور بودند بر آن گواهی دادند. پس از مردم مترسید، از من بترسید و آیات مرا به بهای حقیر و اندک مفروشید و هر که مطابق آیاتی که الله نازل کرده است حكم نکند، پس آن گروه کافر است.');
INSERT INTO "badkhashani" VALUES(5,45,'5:45','و در آن (تورات) برای‌شان مقرر کردیم که نفس در برابر نفس و چشم در برابر چشم و بینی در برابر بینی و گوش در برابر گوش و دندان در برابر دندان و زخمها را نیز قصاص است و هر که از قصاص در گذرد، برای گناهش کفاره‌ای خواهد بود و هر که به آنچه الله (در حدود و قصاص) نازل کرده است حکم نکند، پس این گروه ستمکار است.');
INSERT INTO "badkhashani" VALUES(5,46,'5:46','و از پی آنان (پیغمبران پیشین) عیسی پسر مریم را فرستادیم که تصدیق کننده آنچه پیش از او بود (تورات)، و به عیسی انجیل را دادیم که در آن نور و هدایت بود، در حالیکه تصدیق کننده آنچه پیش از آن بود که تورات است، و هدایت و پند برای پرهیزگاران است.');
INSERT INTO "badkhashani" VALUES(5,47,'5:47','و باید معتقدان انجيل مطابق آنچه الله نازل کرده است حکم و فیصله کنند، و هر که مطابق آنچه الله نازل کرده است فیصله نکند، پس همان گروه فاسقان‌اند.');
INSERT INTO "badkhashani" VALUES(5,48,'5:48','و این کتاب را به راستی بر تو نازل کردیم، تصدیق کننده و حاکم بر کتاب‌هایی است که پیش از آن بوده است، پس مطابق آنچه الله نازل کرده است در بین آنان حکم کن، و از خواهشات (نفسانی) آنان (به جای) آنچه از حق به تو رسیده است، پیروی مکن، البته برای هر گروهی از شما شریعت و روشی مقرر کردیم، و اگر الله می‌خواست همۀ شما (مسلمانان، یهود و نصاری) را یک امت (پیرو توحيد) می‌گردانید، ولیکن (چنین نکرد) تا شما را در آنچه به شما داده است بیازماید، پس در کارهای نیک سبقت بگیرید، بازگشت همۀ شما بسوی الله است، تا شما را به آنچه در آن اختلاف می‌کردید؛ آگاه سازد.');
INSERT INTO "badkhashani" VALUES(5,49,'5:49','(و قرآن را بر تو نازل کردیم) برای این که در بین آنها مطابق آنچه الله نازل کرده است حکم کنی، و از خواهشات (نفسانی) آنها پیروی مکن، و محتاط باش از اینکه مبادا تو را از بعضی آنچه الله بر تو نازل کرده است منحرف سازند، باز اگر روی گرداندند، پس بدان که الله می‌خواهد آنها را به سزای بعضی از گناهانشان عذاب دهد، ويقينا بسیاری از مردم فاسق‌اند.');
INSERT INTO "badkhashani" VALUES(5,50,'5:50','آیا آنان حکم جاهلیت (ملت‌های جاهل) را می‌خواهند؟ برای آن مردمی که اهل یقین هستند چه حکمی از حكم الله بهتر است؟');
INSERT INTO "badkhashani" VALUES(5,51,'5:51','ای کسانی که ایمان آورده‌اید، یهود و نصاری را دوست نگیرید (زیرا آنها هرگز دوست شما نمی‌شوند، بلکه) بعضی از آنها دوستان بعضی دیگراند. و هر کس از شما با آنها دوستی کند يقينا او هم از آنهاست، بی‌گمان الله قوم ظالم را هدایت نمی‌کند.');
INSERT INTO "badkhashani" VALUES(5,52,'5:52','پس آنانی که در دل‌هایشان بیماری (نفاق) است می‌بینی که در دوستی آنها (کفار) شتاب می‌کنند؛ می‌گویند: (به طور بهانه) می‌ترسیم از اینکه مصیبتی (از طرف آنها) بما برسد، پس امید است که الله فتح (مبین) را بیارد و یا امری دیگری از نزد خود، پس از آنچه در دل‌های خود می‌پوشانیدند، پشیمان گردند.');
INSERT INTO "badkhashani" VALUES(5,53,'5:53','و مؤمنان می‌گویند: آیا اینها (منافقان) بودند که قسم‌های محکم به نام الله می‌خوردند که با شما‌اند، اعمال‌شان برباد شد، پس زیان کار گشتند.');
INSERT INTO "badkhashani" VALUES(5,54,'5:54','ای کسانی که ایمان آورده‌اید، هرکس از شما از دین خود بازگردد (ضرری به دین نمی‌رسد) پس به زودی الله قومی را به میان می‌آورد که ایشان را دوست می‌دارد و ایشان نیز او را دوست می‌دارند، با مؤمنان متواضع و با کفار سخت و شدید‌اند، در راه الله جهاد می‌کنند، و از ملامت، ملامت کننده نمی‌ترسند، این (صفات مذکور) فضل الله است که آنرا به هر کسی بخواهد می‌دهد، و الله گشایشگر داناست.');
INSERT INTO "badkhashani" VALUES(5,55,'5:55','جز این نیست که دوست شما تنها الله و رسول او و آن مؤمنانی است که نماز را برپا می‌کنند و زکات را می‌دهند؛ در حالیکه در رکوع‌اند (در برابر الله متواضع و عاجزاند).');
INSERT INTO "badkhashani" VALUES(5,56,'5:56','و هر کس که الله و رسول او و مؤمنان را دوست خود قرار دهد، پس (او از جملۀ مؤمنان واقعی است و) یقینا که حزب الله غالبند.');
INSERT INTO "badkhashani" VALUES(5,57,'5:57','(لذا) ای کسانی که ایمان آورده‌اید، آنانی را که دین شما را به استهزا و بازی گرفته‌اند، چه از آنانی که پیش از شما به آنها (یهود و نصارا) کتاب داده شده و چه از (دیگر) کافران، دوست نگیرید و از الله بترسید، اگر مؤمن هستید.');
INSERT INTO "badkhashani" VALUES(5,58,'5:58','و هرگاه به طرف نماز (مردم را) بخوانید، آن را به مسخره و بازی می‌گیرند، این (استهزا) به سبب آن است که آنها مردمی‌اند که تعقل نمی‌کنند (نادان اند).');
INSERT INTO "badkhashani" VALUES(5,59,'5:59','بگو: ای اهل کتاب! آیا بخاطر این بر ما عيب می‌گیرید که ما به الله و آنچه بر ما و آنچه پیش از ما نازل شده، ایمان آوردیم؟ و این بخاطر آنست که بیشتر شما فاسقید.');
INSERT INTO "badkhashani" VALUES(5,60,'5:60','بگو: آیا شما را به بدتر از آن (شخص عیب دار) به اعتبار جزا در نزد الله آگاه سازم؟ آن کسی که الله او را لعنت کرده است و بر او خشم گرفته و برخی از آنها را بوزینگان و خوکان و (نیز) طاغوت پرستان گردانیده است، این گروه‌اند که از نظر مرتبه بدتر و از راه راست گمراه‌تر‌اند.');
INSERT INTO "badkhashani" VALUES(5,61,'5:61','و هرگاه (منافقان) نزد شما آیند، می‌گویند: ایمان آوردیم و حال آنکه با کفر (نزد شما) در آمده و با کفر (از نزد شما) بیرون رفته‌اند، و الله به آنچه می‌پوشانند داناتر است.');
INSERT INTO "badkhashani" VALUES(5,62,'5:62','و بسیاری از آنها را می‌بینی که در گناه و تجاوز و خوردن حرام و رشوت خواری شان، شتاب می‌کنند، البته بد است آنچه می‌کنند.');
INSERT INTO "badkhashani" VALUES(5,63,'5:63','چرا خداپرستان و دانشمندان، آنها را از دروغ گفتن و حرامخواری‌شان باز نمی‌دارند؟، البته بد است آنچه می‌کردند.');
INSERT INTO "badkhashani" VALUES(5,64,'5:64','و یهودیها گفتند: دست الله بسته است، دست‌های خودشان بسته باد. و به سزای آن گفتار، لعنت شدند. بلکه دو دست الله گشاده است هر طوری که بخواهد انفاق می‌کند، و آنچه بر تو از جانب پروردگارت نازل شده است، طغیان و كفرشان را بیشتر می‌کند و ما تا روز قیامت دشمنی و کینه را در میان‌شان افگندیم، هرگاه آتش جنگ را افروختند الله خاموشش ساخت و آنان در روی زمین به فساد می‌کوشند، و الله مفسدان را دوست ندارد.');
INSERT INTO "badkhashani" VALUES(5,65,'5:65','و اگر اهل کتاب ایمان آورده و تقوی پیشه می‌کردند البته ما از آنها بدی‌هایشان را محو می‌کردیم و آنها را در باغ‌های پرنعمت بهشت درمی‌آوردیم.');
INSERT INTO "badkhashani" VALUES(5,66,'5:66','و اگر آنها تورات و انجیل و آنچه از طرف پروردگارشان (قرآن) بر آنها نازل شده برپا می‌نمودند (نافذ می‌کردند) البته از بالای سر‌شان و از زیر پاهای‌شان (نعمت‌های آسمان و زمین را) می‌خوردند، گروهی از آنها میانه رو هستند، و بسیاری از آنها آنچه می‌کنند بد است.');
INSERT INTO "badkhashani" VALUES(5,67,'5:67','ای پیغمبر! آنچه از پروردگارت بر تو نازل شده است به مردم برسان، اگر چنین نکردی پیغام او را نرسانده ای، الله تو را از (شر) مردم حفظ می‌کند، البته الله مردم کافر را هدایت نمی‌کند.');
INSERT INTO "badkhashani" VALUES(5,68,'5:68','بگو: ای اهل کتاب، شما در هیچ درجه‌ای نیستید تا آن که تورات و انجیل و آنچه از جانب پروردگارتان بر شما نازل شده است برپا دارید (نافذ کنید) و البته آنچه از جانب پروردگارت بر تو نازل شده است بر طغیان و كفر‌شان می‌افزاید، پس بر قوم کافر غمگین مشو.');
INSERT INTO "badkhashani" VALUES(5,69,'5:69','البته آنانی که ایمان آورده‌اند و یهودیان و صابئان (بی دینان) و نصارا، هر کس از این‌ها به الله و روز قیامت ایمان داشته باشد و کار شایسته کند (در آخرت) بیمی بر او نیست و غمگین نمی‌شود.');
INSERT INTO "badkhashani" VALUES(5,70,'5:70','البته ما از بنی اسرائیل پیمان گرفتیم و به‌سوی آنها پیغمبرانی فرستادیم، (اما) هرگاه پیغمبری پیامی می‌آورد که دلخواهشان نبود، گروهی (از پیغمبران) را تکذیب می‌کردند و گروهی را می‌کشتند.');
INSERT INTO "badkhashani" VALUES(5,71,'5:71','و (یهودیان) گمان کردند که هیچ عذاب و امتحانی برایشان نخواهد بود، لذا (از دیدن حقایق )کور و (از شنیدن حقایق) کر شدند، باز الله توبه‌شان را بپذیرفت باز بسیاری از آنها کور و کر شدند و الله به آنچه می‌کنند بیناست.');
INSERT INTO "badkhashani" VALUES(5,72,'5:72','البته آنانی که گفتند: الله، همان مسیح پسر مریم است، یقینا کافر شدند (در حالیکه) مسیح خود گفته بود که ای بنی اسرائیل! الله را عبادت کنید که رب من و رب شماست؛ يقينا هركس به الله شریک آرد البته الله جنت را بر او حرام کرده و جایگاه او دوزخ است. و برای ظالمان هیچ مدد گاری نیست.');
INSERT INTO "badkhashani" VALUES(5,73,'5:73','البته آنانی که گفتند: الله، سوم معبودهای سه گانه است، کافر شدند. حال آنکه هیچ معبود برحق نیست جز یک معبود برحق و یکتا، و اگر از آنچه می‌گویند (شرک) باز نیامدند، البته به کافران آنها عذاب دردناک خواهد رسید.');
INSERT INTO "badkhashani" VALUES(5,74,'5:74','آیا (بر شرک پایدار اند و) به دربار الله توبه نمی‌کنند و از وی آمرزش نمی‌خواهند؟ در حالیکه الله بخشندۀ مهربان است.');
INSERT INTO "badkhashani" VALUES(5,75,'5:75','مسیح پسر مریم نیست مگر پیغمبری که پیش از او نیز پیغمبرانی گذشته است، و مادر او زن صادق (و تصدیق کننده) بود، و هردو (مادر و پسر) طعام می‌خوردند (حال آنکه الله طعام نمی‌خورد)، بنگر چگونه ما آیات (دلائل توحید) را بیان می‌کنیم، باز بنگر چگونه (از راه حق) باز گردانیده می‌شوند؟.');
INSERT INTO "badkhashani" VALUES(5,76,'5:76','بگو: آیا غیر از الله چیزی را می‌پرستید که مالک هیچ ضرر و هیچ نفعی برای شما نیست؟، و یقینا الله شنوای داناست (پس تنها او را عبادت کنید).');
INSERT INTO "badkhashani" VALUES(5,77,'5:77','بگو: ای اهل کتاب! در دین خود به ناحق (و بی‌دلیل) از حد تجاوز مکنید و خواهشات مردمانی را که پیش از این گمراه شدند و بسیاری (از مردم) را گمراه کردند و خود هم (از راه راست) منحرف شدند، پیروی مکنید.');
INSERT INTO "badkhashani" VALUES(5,78,'5:78','کافران بنی اسرائیل بر زبان داود و عیسی پسر مریم، مورد لعنت قرار گرفتند. این (لعنت) به سبب آن بود که نافرمانی می‌کردند و از حد می‌گذشتند.');
INSERT INTO "badkhashani" VALUES(5,79,'5:79','(چون) آنان یکدیگر را از کارهای بدی که می‌کردند، منع نمی‌کردند. البته چه بد است آنچه می‌کردند.');
INSERT INTO "badkhashani" VALUES(5,80,'5:80','بسیاری از ایشان را می‌بینی که با کافران دوستی می‌کنند، البته چه بد است آنچه نفس‌هایشان برای آنها پیش فرستاده است، به سبب اینکه الله از آنها ناراض شده است و آنها در عذاب برای همیشه‌اند.');
INSERT INTO "badkhashani" VALUES(5,81,'5:81','و اگر آنها به الله و پیغمبر و به آنچه به‌سوی او نازل شده، ایمان می‌آوردند، آنان (کفار) را دوست نمی‌گرفتند، و ليكن بسیاری از آنها فاسق (و نافرمان)‌اند.');
INSERT INTO "badkhashani" VALUES(5,82,'5:82','البته یهودیان و مشرکان را سخت‌ترین دشمن مسلمانان می‌یابی، و البته مهربانترین مردم نسبت به مسلمانان آنانی را می‌یابی که گفتند: ما نصاری‌ایم. این (محبت نصاری با مسلمانان) به سبب این است که از آنان کشیشان و گوشه‌نشینان هستند و (نیز) به سبب آن است که آنان (نصاری) تکبر نمی‌ورزند (از پذیرفتن سخن حق).');
INSERT INTO "badkhashani" VALUES(5,83,'5:83','و چون (دانشمندان و منصفان نصاری) آنچه را که بر پیغمبر (محمد ج) نازل شده بشنوند، می‌بینی که چشمانشان پر از اشک می‌شود به سبب آنچه از حق شناخته‌اند، (و) می‌گویند: ای پروردگار ما! ما ایمان آورده ایم؛ پس ما را در زمرۀ گواهان (بر صدق محمد ج) بنویس.');
INSERT INTO "badkhashani" VALUES(5,84,'5:84','و (می‌گویند:) ما را چه شده که به الله و آنچه از حق به ما آمده، ایمان نیاوریم؟ در حالیکه طمع داریم که پروردگار ما، ما را با قوم نیکوکار يکجا داخل کند.');
INSERT INTO "badkhashani" VALUES(5,85,'5:85','پس به سبب آنچه گفتند، الله به آنها باغ‌های پاداش داد که از زیر آن نهرها جاری می‌باشد، در آن همیشه می‌مانند. و این است پاداش نیکوکاران.');
INSERT INTO "badkhashani" VALUES(5,86,'5:86','و آنانی که کافر شدند و آیات ما را دروغ شمردند، ایشان اهل دوزخ‌اند.');
INSERT INTO "badkhashani" VALUES(5,87,'5:87','ای کسانی که ایمان آورده‌اید، چیزهای پاکیزه را که الله بر شما حلال کرده است (بر خود) حرام نکنید، و از حد تجاوز نکنید؛ بی‌گمان الله تجاوزکاران را دوست نمی‌دارد.');
INSERT INTO "badkhashani" VALUES(5,88,'5:88','و از آنچه الله روزی حلال و پاکیزه به شما داده است، بخورید. و از الله، پروردگاری که شما به او ایمان دارید، بترسید.');
INSERT INTO "badkhashani" VALUES(5,89,'5:89','الله شما را به سبب سوگندهای لغوتان بازخواست نمی‌کند و لیکن به سبب، شکستن سوگندهایی که به قصد می‌خورید، مواخذه می‌کند و کفاره آن اطعام ده مسکین است از غذای متوسطی که به خانواده خویش می‌خورانید یا دادن لباس به آنها یا آزاد کردن یک غلام، و هر که (این موارد را) نیافت پس بر او سه روز روزه لازم است، این کفاره قسم‌های تان است، هرگاه که قسم خوردید (و آنرا شکستید). و قسمهای تان را نگاه دارید. الله اینچنین آیات خود را برای شما بیان می‌کند، باشد که شکر گزار شوید.');
INSERT INTO "badkhashani" VALUES(5,90,'5:90','ای کسانی که ایمان آورده‌اید، جز این نیست که شراب و قمار و بتان و تیرهای قرعه (فال‌بینی) همه پلید و از کار شیطان‌اند، پس از آن پرهیز کنید تا رستگار شوید.');
INSERT INTO "badkhashani" VALUES(5,91,'5:91','جز این نیست که شیطان می‌خواهد به وسیلۀ شراب و قمار میان شما دشمنی و کینه ایجاد کند، و شما را از یاد الله و نماز باز دارد، پس (با این همه مفاسد) آیا باز می‌آیید؟.');
INSERT INTO "badkhashani" VALUES(5,92,'5:92','(از اطاعت شیطان بر حذر باشید) و الله و رسول را اطاعت کنید، و (از نافرمانی ایشان) حذر کنید، پس اگر (از اطاعت الله و رسول ) رویگردان شدید، بدانید که وظیفۀ پیغمبر ما رسانیدن پیام روشن الهی است.');
INSERT INTO "badkhashani" VALUES(5,93,'5:93','بر آنانی که ایمان آورده و کارهای نیک و شایسته انجام داده‌اند، در مورد آنچه (پیش از تحریم) خورده‌اند، گناهی نیست؛ هرگاه تقوا پیشه کنند و ایمان بیاورند و کارهای نیک انجام دهند، باز تقوا پیشه کنند و ایمان بیاورند، باز تقوا پیشه کنند و نیکی نمایند. و الله نیکوکاران را دوست دارد.');
INSERT INTO "badkhashani" VALUES(5,94,'5:94','ای کسانی که ایمان آورده‌اید، البته الله شما را به شکاری که با دستهای تان می‌گیرید یا با نیزه شکار می‌کنید، می‌آزماید. تا معلوم کند که چه کسی در نهان از او می‌ترسد. و هر کس از این پس از حد بگذرد، عذاب دردناکی (در پیش) دارد.');
INSERT INTO "badkhashani" VALUES(5,95,'5:95','ای کسانی که ایمان آورده‌اید، هر گاه در احرام باشید شکار را مکشید و هر که شکار را به قصد بکشد جزای او قربانی کردن حیوانی است مانند آنچه کشته است به شرط آنکه دو عادل به آن گواهی دهند و قربانی را به کعبه رساند، یا برای جبران آن به مساكين طعام دهد، یا برابر آن روزه بگیرد، تا سزای کار خود را بچشد. الله از آنچه را در گذشته کرده‌اید عفو کرده است، (لیکن) هر که (به آن اشتباه) باز گردد، الله از او انتقام می‌گیرد، چون الله غالب انتقام گیرنده است.');
INSERT INTO "badkhashani" VALUES(5,96,'5:96','شکار دریا و خوردن آن، برای شما حلال شده که بهره‌ای (توشۀ) برای شما و مسافران است. و شکار صحرایی (خشکه) تا وقتی که در احرام هستید بر شما حرام شده است. و از الله بترسید، پروردگاری که به نزد او حشر می‌شوید.');
INSERT INTO "badkhashani" VALUES(5,97,'5:97','الله، کعبه بیت الحرام را (با) ماه حرام و قربانی بی‌قلاده و قربانی با قلاده وسیلۀ قوام (سبب بقای) مردم گردانید، تا بدانید که الله هر چه را که در آسمان‌ها و زمین است، می‌داند. و او به هر چیزی دانا است.');
INSERT INTO "badkhashani" VALUES(5,98,'5:98','بدانید که عقوبت الله سخت است و (این را نیز بدانید که) الله آمرزندۀ مهربان است.');
INSERT INTO "badkhashani" VALUES(5,99,'5:99','بر (ذمۀ) پیغمبر نیست مگر رسانیدن پیغام. و آنچه را که آشکار می‌کنید یا پنهان می‌دارید الله می‌داند.');
INSERT INTO "badkhashani" VALUES(5,100,'5:100','بگو: (ای مردم) پلید و پاکیزه برابر نیست، اگرچه فراوانی ناپاکان تو را به تعجب اندازد، پس ای صاحبان خرد از الله بترسید، باشد که رستگار و کامیاب گردید.');
INSERT INTO "badkhashani" VALUES(5,101,'5:101','ای کسانی که ايمان آورده‌اید، از چیزهای که اگر (حقیقت آنها) برای شما آشکار شود، شما را اندوهگین می‌سازد، سوال نکنید. و اگر در وقت نزول قرآن از آن سوال کنید، برای تان روشن می‌شود، و (در حالیکه) الله از آن چیزها، عفو کرده است. و الله آمرزندۀ بردبار است.');
INSERT INTO "badkhashani" VALUES(5,102,'5:102','البته مردمی که پیش از شما بودند از آن چیزها سؤال کردند باز به آن کافر شدند.');
INSERT INTO "badkhashani" VALUES(5,103,'5:103','الله در بارۀ بحيره (ماده شتری که به خاطر بتان گوشش بریده می‌شد و شیرش دوشیده نمی‌شد و کسی بر آن سوار نمی‌شد) و سائبه (ماده شتری که برای بتان گذاشته می‌شد و مورد استفاده قرار نمی‌گرفت) و وصیله (ماده شتری که پی در پی دو اولاد ماده می‌زائید باز به خاطر بتان آزاد گذاشته می‌شد) و حام (شتر نری که برای جفت گیری استعمال می‌شد باز به خاطر بتان آزاد گذاشته می‌شد) حکمی نکرده است و لیکن کافران بر الله دروغ می‌بندند و اکثر آنها تعقل نمی‌کنند (بی خرد اند).');
INSERT INTO "badkhashani" VALUES(5,104,'5:104','و چون به آنها گفته شود که به آنچه الله نازل کرده و به‌سوی پیغمبر بیایید، می‌گویند: آن رسم و رواجی که پدران خود را بر آن یافته‌ایم ما را بس است، اگرچه پدرانشان چیزی را نمی‌دانستند و (به حق) هدایت نشده بودند.');
INSERT INTO "badkhashani" VALUES(5,105,'5:105','ای کسانی که ایمان آورده‌اید، (پیش از همه) به (اصلاح) خود پردازید. اگر شما هدایت شوید، آنانی که گمراه‌اند به شما ضرری نمی‌رسانند. بازگشت همۀ شما به‌سوی الله است، و شما را به آنچه (در دنیا) می‌کردید، آگاه می‌سازد.');
INSERT INTO "badkhashani" VALUES(5,106,'5:106','ای کسانی که ایمان آورده‌اید، وقتی مرگتان فرا رسد به هنگام وصیت؛ دو تن عادل را از میان خودتان به گواهی بگیرید، یا اگر در سفر بودید و مصیبت مرگ برای تان فرا رسید، (شاهد مسلمان نیافتید) دو نفر از غیر خودتان را (به گواهی بطلبید)، اگر در بارۀ آن دو در شک افتادید پس تا بعد از نماز آن دو را نگاه دارید، تا به الله سوگند بخورند که این شهادت را به هیچ قیمتی عوض نمی‌کنیم اگر چه به سود خویشاوندان ما باشد و هرگز شهادت الهی را کتمان نمی‌کنیم، چون در آن صورت از گناهکاران خواهیم بود.');
INSERT INTO "badkhashani" VALUES(5,107,'5:107','پس اگر معلوم شد که آن دو گواه مرتکب گناه (خیانت) شده‌اند، پس دو شاهد دیگر که اولی‌تر از آن دو باشند جای ایشان را بگیرند پس به الله قسم بخورند که شهادت ما از شهادت آن دو درست‌تر است، و ما از حد تجاوز نکرده‌ایم، اگر چنین کنیم (در آن صورت) ما از ظالمان خواهیم بود.');
INSERT INTO "badkhashani" VALUES(5,108,'5:108','این روش نزدیک‌تر است بر اینکه گواهی را به طریقۀ درست آن ادا کنند، یا پس از سوگند خوردن، از رد شدن سوگندهایشان بترسند. و از الله بترسید و (فرمان او را) بشنوید چون الله قوم فاسق و نافرمان را هدایت نمی‌کند.');
INSERT INTO "badkhashani" VALUES(5,109,'5:109','روزی که الله پیغمبران را جمع کند، پس بگوید: به دعوت شما چه جواب داده شد؟ )در جواب( گویند: ما را هیچ علمی نیست، بی‌گمان دانای چیزهای پوشیده تویی.');
INSERT INTO "badkhashani" VALUES(5,110,'5:110','وقتی الله به عیسی پسر مریم گوید: نعمت مرا بر خویش و بر مادرت یاد کن، وقتی که تو را به روح القدس (جبرئیل) تائید کردم. با مردم در گهواره و در میانسالی سخن می‌گفتی. و وقتی که به تو کتاب و حکمت و تورات و انجیل آموختم. و وقتی که به حكم من از گل مانند شکل پرنده می‌ساختی، باز در آن می‌دمیدی، پس به حکم من پرنده‌ای می‌شد. و کور مادرزاد و پیس را به حكم من شفا میدادی. و وقتی که مردگان را به حكم من )زنده از گور( بیرون می‌کردی، و چون ضرر بنی اسرائیل را وقتی که برایشان معجزه‌ها آوردی، از تو منع کردیم. پس کافران‌شان (بنی اسرائیل) گفتند: این چیزی جز سحرى آشکار نیست.');
INSERT INTO "badkhashani" VALUES(5,111,'5:111','و چون به حواریان (اصحاب مخلص عیسی) وحی فرستادیم که به من و به پیامبر من (عیسی) ایمان بیاورید، گفتند: ایمان آوردیم، و گواه باش که ما تسلیم (و مسلمان) هستیم.');
INSERT INTO "badkhashani" VALUES(5,112,'5:112','یاد آور شو وقتی که حواریون (همراهان مخلص عیسی) گفتند: ای عیسی پسر مریم! آیا پروردگار تو می‌تواند که بر ما خوانی (پر از طعام) از آسمان فرود آرد؟ عیسی گفت: اگر مؤمن هستید، از الله بترسید.');
INSERT INTO "badkhashani" VALUES(5,113,'5:113','گفتند: می‌خواهیم که از آن بخوریم، و دل‌های ما (آرام گیرد و) مطمئن شود و بدانیم که به ما راست گفته ای، و بر نزول آن از گواهان باشیم.');
INSERT INTO "badkhashani" VALUES(5,114,'5:114','عیسی پسر مریم (در دعای خود) گفت: ای پروردگار ما! از آسمان خوانی (پر از طعام) بر ما نازل فرما تا عیدی برای ما باشد؛ برای اول و آخر ما. و (تا) دلیلی (نبوت من) از جانب تو باشد و ما را روزی ده که تویی بهترین روزی دهندگان.');
INSERT INTO "badkhashani" VALUES(5,115,'5:115','الله فرمود: من نازل کننده آن (خوان) بر شما هستم، ولی هرکس از شما بعد از آن کافر شود (ناشکری کند)، پس او را عذابی می‌دهم که هیچ کس از اهل عالم را چنین عذاب نداده باشم.');
INSERT INTO "badkhashani" VALUES(5,116,'5:116','و (یادآور شو) وقتی را که الله گوید: ای عیسی پسر مریم! آیا تو به مردم گفتی که من و مادرم را (دو معبود) غیر از الله گیرید؟ عیسی گفت: تو را به پاکی یاد می‌کنم (ای الله)، سزاوار من نیست آنچه را بگویم که لایق من نیست، اگر این سخن را گفته باشم، پس یقینا تو آن را دانسته ای، میدانی آنچه را در دل من است و نمی‌دانم آنچه در نفس توست، چون تنها توئی دانای رازهای پوشیده.');
INSERT INTO "badkhashani" VALUES(5,117,'5:117','جز آنچه تو مرا به آن فرمان داده بودی، برای آنها نگفته ام این که الله را که پروردگار من و شماست عبادت کنید، و تا وقتی در میان آنها بودم، بر آنها گواه بودم، پس وقتی مرا وفات دادی (به سوی خود بردی) تو خودت بر آنها نگهبان بوده ای، و توئی بر هر چیز گواه (و) نگهبان.');
INSERT INTO "badkhashani" VALUES(5,118,'5:118','(حالا( اگر آنها را عذاب دهی، پس آنها بندگان تو اند و اگر آنها را بیامرزی، پس یقینا تویی غالب باحکمت.');
INSERT INTO "badkhashani" VALUES(5,119,'5:119','الله فرمود: امروز روزیست که راستان را راستی‌شان نفع می‌رساند، برای آنها باغ‌هایی است که از زیر آنها نهرها جاری است، همیشه در آن‌اند تا ابد، الله از آنها راضی شد و آنها از الله راضی شدند، این است کامیابی بزرگ.');
INSERT INTO "badkhashani" VALUES(5,120,'5:120','فرمانروایی آسمان‌ها و زمین و هر چه در آنهاست برای الله است، و او بر همه چیز تواناست.');
INSERT INTO "badkhashani" VALUES(6,1,'6:1','همه ثنا و ستایش (همه خوبی‌ها) مخصوص الله است که آسمان‌ها و زمین را آفرید و تاریکی‌ها و روشنی را قرار داد. )اما( باز هم کافران به پروردگار خود (مخلوق را) برابر و همتا می‌گیرند.');
INSERT INTO "badkhashani" VALUES(6,2,'6:2','او (الله) ذاتی است که شما را از گل آفرید، باز (برای مرگ تان) اجلی مقرر کرد، و اجلى معين (برای زنده کردن شما در روز قیامت) نزد او است. باز هم شما (در قدرت) او شک می‌کنید.');
INSERT INTO "badkhashani" VALUES(6,3,'6:3','و او (الله) ذاتی است که معبود (حقیقی) در آسمان‌ها و در زمین است. (چون) او رازهای پوشیده شما را و (چیزهای) آشکارای شما را می‌داند و هرچه را بدست می‌آورید می‌داند.');
INSERT INTO "badkhashani" VALUES(6,4,'6:4','و هیچ آیتی از آیات پروردگارشان برایشان (کفار) نمی‌آید، مگر اینکه (به عوض تصدیق و ایمان) از آن رویگردان‌اند.');
INSERT INTO "badkhashani" VALUES(6,5,'6:5','(لذا) وقتی سخن حق به آنها آمد آنرا دروغ شمردند، پس به زودی، خبر چیزهایی که به آن تمسخر می‌کردند به آنها خواهد رسید.');
INSERT INTO "badkhashani" VALUES(6,6,'6:6','آیا ندیدند که پیش از آنها چه (بسیار) مردم را هلاک کرده ایم؟ مردمی که در زمین برای‌شان قوت داده بودیم، آنچنان قوتی که به شما نداده‌ایم و بر آنها از آسمان بارانهای بسیار (پی در پی) فرستادیم و نهرها را از زیر (باغها و کاخهای) شان جاری ساختیم. پس آن‌ها را به سبب گناهانشان هلاک کردیم و پس از آنها مردمی دیگر را آفریدیم.');
INSERT INTO "badkhashani" VALUES(6,7,'6:7','و اگر بر تو کتابی نوشته بر کاغذ نازل می‌کردیم که آن را با دست‌های خود لمس می‌کردند؛ (بازهم) کافران می‌گفتند: این (کتاب) جز جادویی آشکار نیست.');
INSERT INTO "badkhashani" VALUES(6,8,'6:8','و (کافران) گفتند: چرا فرشته‌ای بر او (برای تأیید پیغمبریش) نازل نشد؟ و (در حالیکه) اگر فرشته‌ای را نازل می‌کردیم، البته کار (آنها) به انجام می‌رسید (و هلاک می‌شدند) و هیچ مهلتی به آنان داده نمی‌شد.');
INSERT INTO "badkhashani" VALUES(6,9,'6:9','و اگر او (پیغمبر) را فرشته قرار می‌دادیم، البته او را به صورت مردی در می‌آوردیم، و البته آنها را دچار اشتباهی می‌کردیم که قبلا در آن (مشتبه) بودند.');
INSERT INTO "badkhashani" VALUES(6,10,'6:10','(ای پیامبر،) البته پیغمبرانی پیش از تو هم استهزا شدند، پس مسخره‌کنندگان را عذابی که به مسخره می‌گرفتند، احاطه کرد (و گرفتار عذاب شدند).');
INSERT INTO "badkhashani" VALUES(6,11,'6:11','بگو: در زمین بگردید باز ببینید که سر انجام (و سرنوشت) تکذیب کنندگان چه شد؟');
INSERT INTO "badkhashani" VALUES(6,12,'6:12','بگو: آنچه (از مخلوقاتی) که در آسمان‌ها و در زمین است برای کیست (و در تصرف کیست)؟ بگو: همه از الله (و در تصرف الله) است، رحمت را بر خود لازم کرده است (برای کسی که مستحق رحمت باشد) همۀ شما را در روز قیامت که در آن شکی نیست جمع خواهد کرد. (اما) آنانی که به زیان خود کار کرده‌اند ایمان نمی‌آرند.');
INSERT INTO "badkhashani" VALUES(6,13,'6:13','و تنها برای اوست هرچه در شب و روز قرار گرفته. و او شنوای داناست.');
INSERT INTO "badkhashani" VALUES(6,14,'6:14','بگو: آیا غیر از الله را کارساز خود قرار دهم؟ در حالیکه تنها او آفرینندۀ آسمان‌ها و زمین است. و او همه (مخلوقات) را روزی می‌دهد، و کسی به او روزی نمی‌دهد (و او تعالی به روزی نیازی ندارد) بگو: به من امر شده که اولین کسی باشم که منقاد و تسلیم اوست، و (نیز به من گفته شده که) هرگز از مشركان مباش.');
INSERT INTO "badkhashani" VALUES(6,15,'6:15','بگو: اگر من پروردگارم را نافرمانی کنم، از عذاب روزی بزرگ می‌ترسم.');
INSERT INTO "badkhashani" VALUES(6,16,'6:16','در آن روز عذاب از هر کسی دور گردانده شود يقينا مورد رحمت الله واقع شده است، و این کامیابی آشکاری است.');
INSERT INTO "badkhashani" VALUES(6,17,'6:17','اگر الله به تو محنتی برساند، هیچ کس جز خودش برطرف کنندۀ آن نیست، و اگر به تو خیری برساند، پس او بر هر چیزی تواناست.');
INSERT INTO "badkhashani" VALUES(6,18,'6:18','و اوست که بالای بندگان خود قرار داشته و بر آنها غالب و مسلط است، و اوست حکیم (و) آگاه.');
INSERT INTO "badkhashani" VALUES(6,19,'6:19','بگو: گواهی چه کسی بزرگتر و (قابل اعتمادتر) است؟ بگو: الله میان من و شما گواه است. و (گواهی دیگر این است که) این قرآن به من وحى شده، تا شما را و هر کس را که به او (این پیام الهی) برسد بیم دهم، آیا شما گواهی می‌دهید که با الله معبودهای دیگری است؟ بگو: من گواهی نمیدهم، بگو: جز این نیست که او معبودی است یکتا، و از آنچه با او شریک می‌سازید بیزارم.');
INSERT INTO "badkhashani" VALUES(6,20,'6:20','آنانی که کتاب (آسمانی) به ایشان داده‌ایم، او (پیغمبر) را مانند فرزندان خود می‌شناسند. (البته) آنانی که به خود زیان رساندند، ایمان نمی‌آورند.');
INSERT INTO "badkhashani" VALUES(6,21,'6:21','وكيست ظالم‌تر از آنکه بر الله دروغ بندد؟ یا اینکه آیات او را تکذیب کند؟، يقينا ظالمان رستگار نمی‌شوند.');
INSERT INTO "badkhashani" VALUES(6,22,'6:22','و روزی که همۀ آنها را جمع کنیم (معبودها و عبادت کنندگان آنها را) باز به کسانی که شرک آورده بودند می‌گوییم: شریک‌های شما که آنها را شریک الله گمان می‌کردید؛ کجا‌ اند؟');
INSERT INTO "badkhashani" VALUES(6,23,'6:23','باز فتنۀ آنان (جواب شان) جز این نیست که می‌گویند: قسم به الله، ای پروردگار ما، ما مشرک نبودیم.');
INSERT INTO "badkhashani" VALUES(6,24,'6:24','ببین، چگونه بر خود دروغ بستند و آنچه از (معبودهای که با الله شریک قرار داده و) افترا می‌کردند، از آنها گم گشت.');
INSERT INTO "badkhashani" VALUES(6,25,'6:25','و از آنها (مشرکان) کسانی هستند که به تو گوش می‌دهند و (لیکن) ما در دل‌های آنها پرده‌ها قرار داده‌ایم تا آن را نفهمند و در گوش‌های آنها سنگینی گذاشته‌ایم (تا نشنوند)، و اگر ببینند هر معجزه را (باز هم) ایمان نمی‌آورند، تا آنجا که وقتی نزد تو می‌آیند با تو مجادله می‌‌کنند. کافران می‌گویند: این (قرآن) نیست مگر افسانه‌های پیشینیان.');
INSERT INTO "badkhashani" VALUES(6,26,'6:26','و آنها (مردم را) از (پیروی) آن (محمد ج) منع می‌کنند و خود نیز از او دور می‌شوند، و هلاک نمی‌کنند مگر خود را، ولی نمی‌دانند.');
INSERT INTO "badkhashani" VALUES(6,27,'6:27','و اگر (تو ای مخاطب) آنان را که وقتی بر کنارۀ آتش (دوزخ) بازداشته شوند، ببینی، می‌گویند: ای کاش باز گردانیده شویم (به دنیا) و باز آیات پروردگار خود را تکذیب نکنیم، و از مؤمنان شویم.');
INSERT INTO "badkhashani" VALUES(6,28,'6:28','نه، بلکه چیزی را که پیش از این می‌پوشانیدند، بر آنها ظاهر شده است، و اگر (بالفرض) بازگردانیده هم شوند (به دنیا)، حتما به‌سوی آنچه از آن منع شده بودند، برمی‌گردند، و یقینا آنها دروغگویند (در این سخن شان).');
INSERT INTO "badkhashani" VALUES(6,29,'6:29','و گفتند: (منکران آخرت که) حیاتی جز همین زندگی این دنیای ما نیست، و ما (دوباره زنده و) برانگیخته نخواهیم شد.');
INSERT INTO "badkhashani" VALUES(6,30,'6:30','و اگر ببینی (تو ای مخاطب) وقتی به حضور پروردگارشان بازداشته شوند، (پس) پروردگار گوید: آیا این (رستاخيز و حساب و کتاب) حق نیست؟ گویند: بلی قسم به پروردگار ما که راست است. (الله) گوید: پس به سبب آنکه (در دنیا) کفر می‌ورزیدید، این عذاب را بچشید.');
INSERT INTO "badkhashani" VALUES(6,31,'6:31','يقينا آنانی که ملاقات الله را دروغ پنداشتند، (روز قیامت) زیان کار شدند. تا آن که قیامت ناگهانی بر آنها بیاید (پس ) گویند: ای افسوس بر ما، بر اینکه دربارۀ (ایمان به) قیامت کوتاهی کردیم، (در حالیکه) آنها بارهای (گناهان) خود را بر پشت‌هایشان بر می‌دارند، آگاه شوید! چه بد است آنچه بر می‌دارند.');
INSERT INTO "badkhashani" VALUES(6,32,'6:32','و زندگانی دنیا جز بازی و سرگرمی نیست. و البته خانۀ آخرت برای آنانی که پرهیزگاری می‌کنند، بهتر است. آیا نمی‌اندیشید (عقل ندارید)؟.');
INSERT INTO "badkhashani" VALUES(6,33,'6:33','البته می‌دانیم که آنچه آنها می‌گویند تو را اندوهگین می‌کند، پس (در حقیقت) آنها تو را تکذیب نمی‌کنند، و ليكن این ظالمان آيات الله را انکار می‌کنند.');
INSERT INTO "badkhashani" VALUES(6,34,'6:34','و البته پیغمبرانی پیش از تو نیز تکذیب شدند، ولی بر آنچه تکذیب شدند و بر اذیت‌های که دیدند، صبر کردند. تا آن که مدد ما به آنها رسید. و برای (مصداق) سخن‌های الله هیچ تغیر دهندۀ نیست، و البته (در این کتاب) از خبر پیغمبران (پیشین) به تو آمده است.');
INSERT INTO "badkhashani" VALUES(6,35,'6:35','و اگر اعراض آنها (از ایمان) بر تو گران تمام شود، پس اگر بتوانی راهی در زیر زمین بجویی، یا زینه‌ای (نردبانی) در آسمان بجویی، تا (از زمین یا آسمان) معجزۀ به آنها بیاوری (ولی چنین نمی‌توانی، پس اندوهگین مشو) و اگر الله می‌خواست البته همه را به راه راست جمع (و هدایت) می‌کرد، پس (به حکمت الله) از نادانان مباش.');
INSERT INTO "badkhashani" VALUES(6,36,'6:36','جز این نیست که تنها آنانی دعوت تو را می‌پذیرند که )سخن حق را( می‌شنوند. و الله مردگان را (روز قیامت) زنده می‌کند، باز به‌سوی او باز گردانیده می‌شوند.');
INSERT INTO "badkhashani" VALUES(6,37,'6:37','و (مشرکین به طور اعتراض) گفتند: چرا معجزۀ )به خواست آنها( از طرف پروردگارش بر او (پیغمبر) نازل نشد؟ بگو: یقینا الله قادر است بر اینکه معجزه نازل کند. و ليكن بیشتر آنها (حکمت از نازل نکردن معجزۀ فرمایشی را) نمی‌دانند.');
INSERT INTO "badkhashani" VALUES(6,38,'6:38','و هیچ جنبنده‌ای در زمین نیست، و هیچ پرنده‌ای که با دو بال خود می‌پرد، مگر اینکه آنها (نیز) امت‌های (گوناگونی) مانند شما هستند (که از هدف آفرینش خود تجاوز نمی‌کنند). و ما در کتاب (قرآن) از بیان هیچ چیزی کوتاهی نکردیم، باز همگی به‌سوی پروردگارشان محشور می‌شوند.');
INSERT INTO "badkhashani" VALUES(6,39,'6:39','و آنانی که آیات (و دلائل روشن) ما را تکذیب نمودند، آنها (در حقیقت) کر ‌اند و گنگ‌اند و (همیشه) در تاریکی‌ها‌اند (تاریکی‌های کفر و جهل). الله هر کسی را که بخواهد، گمراه می‌کند، و هر کسی را که بخواهد بر راه راست قرار می‌دهد.');
INSERT INTO "badkhashani" VALUES(6,40,'6:40','بگو: مرا آگاه سازید، اگر عذاب الله بر شما آید، یا قیامت بر شما برسد آیا (در آن وقت هم) کسی جز الله را می‌خوانید اگر (در این گمان تان) صادق هستید؟');
INSERT INTO "badkhashani" VALUES(6,41,'6:41','نه، بلکه (در آن وقت) فقط الله را می‌خوانید، پس اگر (الله) بخواهد آنچه را برای دفع آن دعا می‌کنید، از شما دفع می‌کند، و آنچه را که با او شریک مقرر کرده‌اید، (در آن وقت) فراموش می‌کنید.');
INSERT INTO "badkhashani" VALUES(6,42,'6:42','و البته ما به‌سوی امت‌های که پیش از تو بودند، پیغمبرانی فرستادیم (لیکن ایشان را تکذیب نمودند) پس آنها را به سختی‌ها (در اموال) و مصیبت‌ها (در ابدان) دچار کردیم، تا تضرع و زاری نمایند (و منقاد شوند).');
INSERT INTO "badkhashani" VALUES(6,43,'6:43','پس چرا وقتی که سختی و عذاب ما به ایشان آمد، زاری نکردند (و منقاد نشدند؟) و ليكن (حقیقت این است که) دل‌هایشان سخت شد. و شیطان آنچه (از اعمال زشت) را می‌کردند، در نظر‌شان مزین ساخت.');
INSERT INTO "badkhashani" VALUES(6,44,'6:44','پس وقتی آنچه را که به آن پند داده شده بودند، فراموش کردند، دروازه‌های هر چیز را بر آنها گشودیم، تا چون به آنچه که داده شده بودند خورسند شدند؛ ناگهان آنها را گرفتیم، پس یکباره ناامید شدند.');
INSERT INTO "badkhashani" VALUES(6,45,'6:45','لذا بنیان قوم ظالم ریشه کن شد. و تمامی ستایش مخصوص الله است که پروردگار جهانیان است.');
INSERT INTO "badkhashani" VALUES(6,46,'6:46','بگو: مرا آگاه سازید، اگر الله گوشهایتان (شنوائی تان) را و چشم‌های تان (بینائی تان) را بگیرد و بر دل‌های تان مهر زند، کیست معبودی به جز الله که آنها را به شما برگرداند؟ ببین که چطور آیات خود را به شیوه‌های مختلف بیان می‌کنیم؟ بازهم آنها (از این آیات) اعراض می‌کنند.');
INSERT INTO "badkhashani" VALUES(6,47,'6:47','بگو: مرا آگاه سازید، اگر عذاب الله ناگهانی یا آشکارا بر شما بیاید، (پس شما چه می‌توانید کرد؟ و) آیا جز قوم ظالم و ستمگار (کسی دیگر) هلاک می‌شود؟');
INSERT INTO "badkhashani" VALUES(6,48,'6:48','و ما پیغمبران را نمی‌فرستیم مگر مژده دهنده و بیم دهنده، لذا هرکس ایمان آورد و (اعمال خود را) اصلاح کرد، پس هیچ ترسی بر آنها (در روز آخرت) نخواهد بود و نه غمگین می‌شوند.');
INSERT INTO "badkhashani" VALUES(6,49,'6:49','و آنانی که آیات ما را دورغ پنداشتند، به آنها عذاب (ما) به سبب اعمال فاسقانۀ که می‌کردند، می‌رسد.');
INSERT INTO "badkhashani" VALUES(6,50,'6:50','بگو: من به شما نمی‌گویم که خزانه‌های الله نزد من است، و نه می‌گویم که غیب را میدانم، و به شما نمی‌گویم که من فرشته ام. من پیروی نمی‌کنم مگر آنچه را که به من وحی می‌شود، بگو: آیا بینا و نابینا (مسلمان و کافر) برابرند؟ آیا تفکر نمی‌کنید؟');
INSERT INTO "badkhashani" VALUES(6,51,'6:51','و به وسیلۀ قرآن آنانی را بترسان که از محشور شدن به دربار پروردگار خود می‌ترسند، (چون) برای آنها غیر از الله هیچ کارساز و شفاعت کننده‌ای نیست. (بترسان آنها را) تا ایشان پرهیزگاری کنند.');
INSERT INTO "badkhashani" VALUES(6,52,'6:52','و آنانی که پروردگار خود را صبح و شام می‌خوانند، در حالیکه خشنودی او را می‌طلبند، از خود مران. چیزی از حساب آنها بر تو نیست و چیزی از حساب تو بر آنها نیست. اگر آنها را از خود برانی، پس از ظالمان خواهی بود.');
INSERT INTO "badkhashani" VALUES(6,53,'6:53','و این گونه ما بعضی از آنها را (مستکبران را) به بعضی دیگر (مستضعفان) آزمودیم، تا (مستکبران) بگویند: آیا این‌ها (همان مردمی)‌اند که الله از میان ما بر آنها منت نهاده است؟ (بلی!) آيا الله به احوال شکر گزاران داناتر نیست؟.');
INSERT INTO "badkhashani" VALUES(6,54,'6:54','و چون آنانی که به آیات ما ایمان دارند، نزد تو بیایند، پس بگو: سلام بر شما (و اینکه) پروردگار تان بر خود رحمت را لازم گردانیده است. (به این طور که) هر کس از شما به نادانی کار بدی کند، باز بعد از آن توبه کند و (عمل خود را) اصلاح کند، پس بداند که يقينا الله آمرزندۀ مهربان است.');
INSERT INTO "badkhashani" VALUES(6,55,'6:55','و این طور آیات خود را با تفصيل و روشنی بیان می‌کنیم (تا بفهمند) و تا راه مجرمان آشکار گردد.');
INSERT INTO "badkhashani" VALUES(6,56,'6:56','بگو: من نهی شدم از این که آن کسانی را که شما به جز الله عبادت می‌کنید, عبادت کنم. بگو: من از هوا و خواهشات شما پیروی نمی‌کنم، در آن صورت گمراه شده باشم، و از راه یافتگان نباشم.');
INSERT INTO "badkhashani" VALUES(6,57,'6:57','بگو: البته من از طرف پروردگار خود بر حجت و برهان آشکاری قرار دارم، در حالیکه شما آن را تکذیب نموده‌اید، و آنچه (از عذاب الهی) را به شتاب از من می‌طلبید، در نزد من نیست. حکم (نزول عذاب یا رحمت) نیست مگر بدست الله. او بیان کنندۀ سخن حق است، و او بهترین فیصله کنندگان است.');
INSERT INTO "badkhashani" VALUES(6,58,'6:58','بگو: اگر آنچه را که با این شتاب می‌طلبید، نزد من می‌بود. البته کار میان من و شما فیصله می‌شد، زیرا الله (به احوال) ستمگاران داناتر است.');
INSERT INTO "badkhashani" VALUES(6,59,'6:59','کلیدهای غیب تنها نزد اوست. جز او کسی از غیب آگاهی ندارد. هر چه را که در خشکی و دریاست می‌داند. هیچ برگی از درختی نمی‌افتد مگر آنکه الله از آن آگاه است. و هیچ دانه‌ای در تاریکیهای زمین و هیچ ترى و خشکی نیست مگر اینکه (تفصیل آن) در کتاب مبين درج است.');
INSERT INTO "badkhashani" VALUES(6,60,'6:60','و او (الله) ذاتی است که شما را در شب می‌میراند و هر چه را در روز کسب کرده‌اید می‌داند، باز شما را در بامداد زنده می‌سازد، تا آن وقتی که مدت معین عمرتان به پایان رسد. باز بازگشت شما به نزد اوست. باز شما را از آنچه (در دنیا) کرده‌اید آگاه می‌کند.');
INSERT INTO "badkhashani" VALUES(6,61,'6:61','و الله بالای بندگان خود قرار داشته و بر آنها غالب است، و برای شما نگهبانانی می‌فرستد، تا چون مرگ یکی از شما فرا رسد، فرستادگان ما (فرشته‌ها) جان او را بگیرند و در حکم ما هیچ کوتاهی نکنند.');
INSERT INTO "badkhashani" VALUES(6,62,'6:62','باز به سوى الله، مولای حقیقی خویش باز گردانیده شوند. بدان که حکم، حکم اوست و او سریع‌ترین حسابگران است.');
INSERT INTO "badkhashani" VALUES(6,63,'6:63','بگو کیست که شما را از تاریکی‌های بیابان و دریا نجات دهد؟ در حالیکه او را به زاری و در نهان می‌خوانید که اگر از این تاریکی ما را نجات دهد، حتما از شکر گزاران خواهیم بود.');
INSERT INTO "badkhashani" VALUES(6,64,'6:64','بگو: الله شما را از این تاریکی‌ها (بلکه) از هر مصیبتی دیگر، نجات می‌دهد. باز هم شما (به جای شکر) شرک می‌ورزید؟.');
INSERT INTO "badkhashani" VALUES(6,65,'6:65','بگو: او قادر بر آن است که از بالای سرتان یا از زیر پاهایتان عذابی برشما بفرستد، یا شما را به صورت گروه‌های مختلف باهم درگیر کند و دشمنی و عذاب بعضی از شما را به بعضی دیگر بچشاند. بنگر که آیات را چگونه با شیوه‌های گوناگون بیان می‌کنیم، باشد که بفهمند.');
INSERT INTO "badkhashani" VALUES(6,66,'6:66','و قوم تو این (قرآن) را دروغ پنداشتند، در حالیکه سخن حق و راست است، بگو: من عهده دار و وكيل (ایمان آوردن و عذاب) شما نیستم.');
INSERT INTO "badkhashani" VALUES(6,67,'6:67','برای هر خبری وقت مقرر است، (از جمله فرود آمدن عذاب بر شما) و به زودی خواهید دانست.');
INSERT INTO "badkhashani" VALUES(6,68,'6:68','و چون ببینی که در آیات ما از روی تمسخر گفت و گو می‌کنند، پس از آنها روی بگردان تا به سخن دیگری بپردازند. و اگر شیطان تو را به فراموشی انداخت، پس بعد از یاد آمدن با آن مردم ستمکاره منشین.');
INSERT INTO "badkhashani" VALUES(6,69,'6:69','و پرهیزگاران به گناه آنها (کافران) بازخواست نخواهند شد. و ليكن باید آنان را پند دهند تا شاید پرهیزگار شوند.');
INSERT INTO "badkhashani" VALUES(6,70,'6:70','و بگذار آن کسانی را که دین خویش را به بازی و لهو گرفته‌اند و زندگانی دنیا فریبشان داده است. و به قرآن پندشان ده که به سزای اعمال خویش در جهنم محبوس خواهند ماند در حالیکه جز الله هیچ مددگار و شفیعی ندارند و اگر برای نجات خویش هر گونه فدیه دهند پذیرفته نخواهد شد. آنان به سبب آنچه کرده‌اند در جهنم محبوسند و به سزای آنکه کافر شده‌اند برایشان شرابی از آب جوشان و عذابی دردناک خواهد بود.');
INSERT INTO "badkhashani" VALUES(6,71,'6:71','بگو : آیا غیر از الله کسی را بخوانیم که نه به ما سود می‌رساند و نه زیان می‌رساند؟ و آیا پس از آنکه الله ما را هدایت کرده است، مانند آن کس که شیطان گمراهش ساخته و حیران بر روی زمین رهایش کرده، از دین باز گردیم؟ او یارانی (دلسوز) دارد که به‌سوی هدایت دعوتش می‌دهند که نزد ما بیا. بگو: هدایتی که از سوی الله باشد، هدایت واقعی است و به ما حكم شده که در برابر پروردگار جهانیان منقاد و تسلیم باشیم.');
INSERT INTO "badkhashani" VALUES(6,72,'6:72','و (نیز به ما حكم شده که) نماز را برپا کنید و از الله بترسید، و اوست آن ذاتی که به دربارش حشر می‌شوید.');
INSERT INTO "badkhashani" VALUES(6,73,'6:73','و او (الله) کسی است که آسمان‌ها و زمین را به حق (باعدل و تدبیر محكم) آفرید، و روزی که بگوید: موجود شو، پس بی‌درنگ موجود می‌شود؛ سخن او حق است، و روزی که در صور دمیده شود؛ پادشاهی تنها از اوست، دانای همه چیزهای غائب و آشکار است، و اوست ذات باحکمت (و) آگاه (از هرچیزی).');
INSERT INTO "badkhashani" VALUES(6,74,'6:74','و یادآور شو چون ابراهیم به پدر خود آزر گفت: آیا بت‌ها را معبود خود قرار میدهی؟ یقینا من تو و قومت را در گمراهی آشکار می‌بینم.');
INSERT INTO "badkhashani" VALUES(6,75,'6:75','و این گونه به ابراهیم پادشاهی عظیم آسمان‌ها و زمین را نشان دادیم تا (بفهمد و) از جملۀ یقین کنندگان باشد.');
INSERT INTO "badkhashani" VALUES(6,76,'6:76','پس چون بر وی شب تاریک شد، او ستاره‌ای را دید، (به طور تعجب) گفت: (آیا) این پروردگار من است! پس چون غروب کرد، گفت: غائب شوندگان را دوست ندارم.');
INSERT INTO "badkhashani" VALUES(6,77,'6:77','باز چون ماه را طلوع کنان دید، گفت: (آيا) این است پروردگار من! پس چون ماه (نیز) غروب کرد گفت: اگر پروردگارم مرا هدایت نکند البته از مردمان گمراه می‌شوم.');
INSERT INTO "badkhashani" VALUES(6,78,'6:78','باز چون آفتاب را طلوع کرده دید، گفت: (آيا) این پروردگار من است! (چونکه) این بزرگتر است، پس چون آفتاب (هم) غروب کرد، گفت: ای قوم من، البته من از آنچه (با الله) شریک می‌سازید بیزارم.');
INSERT INTO "badkhashani" VALUES(6,79,'6:79','بی‌گمان من روی خود را به‌سوی کسی گردانیده ام که آسمان‌ها و زمین را آفریده است (از عدم به وجود آورده است) در حالیکه من از شرک متنفرم، و از مشرکان نیستم.');
INSERT INTO "badkhashani" VALUES(6,80,'6:80','و قومش با او مجادله کردند. گفت: (ابراهیم) آیا با من دربارۀ (وحدانیت) الله مجادله می‌کنید؟ در حالیکه او مرا هدایت کرده است، و من از آنچه با او شریک میسازید بیمی ندارم، مگر این که پروردگارم (دربارۀ من) چیزی بخواهد. و علم پروردگار من به هر چیزی احاطه یافته است، پس آیا یادآور نمی‌شوید و پند نمی‌گیرید؟');
INSERT INTO "badkhashani" VALUES(6,81,'6:81','و چگونه از آن چیزی که شریک (الله) ساخته‌اید بترسم، در حالیکه شما چیزهایی را که الله هیچ دلیلی در بارۀ آنها نازل نکرده است می‌پرستید و بیمی به دل راه نمی‌دهید؟ (بگویید) که کدام یک از این دو گروه به ایمنی احق ترند، اگر می‌دانید؟');
INSERT INTO "badkhashani" VALUES(6,82,'6:82','آنانی که ایمان آورده‌اند و ایمان‌شان را با شرک آلوده نکرده‌اند، این گروه‌اند که از عذاب در امن‌اند و این گروه راه یاب‌اند.');
INSERT INTO "badkhashani" VALUES(6,83,'6:83','و این (نوع دلیل قطعی) حجت ماست که آن را به ابراهیم در برابر قومش دادیم، هرکه را بخواهیم به درجات بالا می‌بریم، یقینا پروردگار تو حکیم (و) داناست.');
INSERT INTO "badkhashani" VALUES(6,84,'6:84','و ما به او (ابراهیم) اسحاق و يعقوب را بخشیدیم. و هر یکی آنها را هدایت نمودیم، و نوح را (هم) پیش از آنها هدایت کرده بودیم، و از نسل او (ابراهیم) داود و سلیمان و ایوب و يوسف و موسی و هارون را (نیز هدایت کردیم) و ما اینگونه نیکوکاران را پاداش میدهیم،');
INSERT INTO "badkhashani" VALUES(6,85,'6:85','و زکریا و یحیی وعیسی و الیاس را (نیز هدایت نمودیم و) همه از صالحان بودند.');
INSERT INTO "badkhashani" VALUES(6,86,'6:86','و اسماعيل و يسع و يونس و لوط را (نیز هدایت نمودیم)، و همه را بر جهانیان فضیلت دادیم.');
INSERT INTO "badkhashani" VALUES(6,87,'6:87','و از پدرانشان و فرزندانشان و برادرانشان، کسانی را برگزیدیم و به راه راست هدایت کردیم.');
INSERT INTO "badkhashani" VALUES(6,88,'6:88','این است هدایت الله، هر که از بندگانش را که بخواهد به آن هدایت می‌کند. و اگر (بالفرض ) شرک ورزند البته آنچه انجام داده‌اند نابود می‌گردد.');
INSERT INTO "badkhashani" VALUES(6,89,'6:89','اینها کسانی‌اند که به آنها کتاب و فرمان (فيصله درست) و نبوت دادیم، اگر این قوم (مشرک) به آنان كفر ورزند (غمگین مباش چون ما) قوم دیگری را بر آن گمارده‌ایم که آن را انکار نمی‌کنند.');
INSERT INTO "badkhashani" VALUES(6,90,'6:90','آنان کسانی‌اند که الله آنها را (به راه حق) هدایت کرد، پس (تو ای محمد) به هدایت آنها اقتدا کن. و بگو: من هیچ مزدی از شما بر آن نمی‌طلبم، این کتاب جز اندرزی برای مردم جهان نیست.');
INSERT INTO "badkhashani" VALUES(6,91,'6:91','و (مشركان) الله را آن چنان که لایق اوست، نشناختند. چون گفتند: الله بر هیچ انسانی چیزی نازل نکرده است، بگو: کتابی را که موسی برای نور و هدایت مردم آورد، چه کسی بر او نازل کرده بود؟ (لیکن) آن را بر ورق‌ها نوشتید، بخشی از آن را آشکار می‌سازید ولی اکثر آن را پنهان می‌دارید. و به شما چیزهای آموخته شد که از این پیش نه شما می‌دانستید و نه پدرانتان می‌دانستند، بگو: آن الله است (که آن را بر من نازل کرده است) باز ایشان را بگذار تا در انکار و باطل گویی خود غوطه ور باشند.');
INSERT INTO "badkhashani" VALUES(6,92,'6:92','و (بگو) این است کتابی مبارک؛ که نازل کرده‌ایم، تصديق کننده کتابی است که پیش از آن نازل شده است. تا به وسيله آن مردم ام القرى (مکه) و مردم اطرافش را بترسانی. و آنانی که به روز قیامت ایمان می‌آورند، به آن نیز ایمان دارند. و ایشان بر نمازهای خویش محافظت دارند.');
INSERT INTO "badkhashani" VALUES(6,93,'6:93','و کیست ظالم‌تر از آن که بر الله دروغ بست، یا گفت: به من وحی شده است؟ در حالیکه چیزی بر او وحی نشده است، و (کیست ظالم‌تر از آن که) گفت: من نیز مانند آنچه که الله نازل کرده است، نازل می‌کنم! و اگر ببینی (تو ای مخاطب) وقتی که آن ظالمان در سختی‌های نزع گرفتار‌اند، و فرشتگان دست‌های خود را (به طرف آنها) گشوده‌اند (و می گویند) که جانهای خود را بیرون کنید، امروز شما به عذابی خوارکننده عذاب داده می‌شوید، و این سزا به سبب آن است که در بارۀ الله به ناحق سخن می‌گفتید و از آیات او تكبر (و سرکشی) می‌کردید.');
INSERT INTO "badkhashani" VALUES(6,94,'6:94','البته همان طور که در آغاز شما را آفریدیم، اکنون نیز نزد ما تک و تنها آمده‌اید، در حالیکه هر چه را که به شما داده بودیم (در دنیا) پشت سر گذاشته‌اید و هیچ یک از شفیعانتان را که می‌پنداشتید (در سرنوشت شما) شریکان الله‌اند، با شما نمی‌بینیم. البته روابط میان شما و آنها از هم بریده شده و آنچه (از شریکانی که به نفع خود) می‌پنداشتید، از شما گم گشته است.');
INSERT INTO "badkhashani" VALUES(6,95,'6:95','بی‌گمان الله شگافندۀ (رویانندۀ) دانه و خسته است، زنده را از مرده بیرون می‌آورد و بیرون آورندۀ (ایجاد کنندۀ) مرده از زنده است، آن است (الله) معبود شما، پس چگونه (از حق) گردانیده می‌شوید؟.');
INSERT INTO "badkhashani" VALUES(6,96,'6:96','(همان الله) شگافندۀ روشنی صبح است و شب را برای آرامش قرار داد و خورشید و ماه را برای حساب کردن اوقات. این است تقدیر پروردگار قدرتمند دانا.');
INSERT INTO "badkhashani" VALUES(6,97,'6:97','و الله آن ذاتی است که برای شما ستاره‌ها را آفرید، تا به وسیلۀ آن در تاریکی‌های خشکی و تاریکی‌های بحر، راه یاب شوید. واقعا ما آیات را برای آنانی که می‌دانند، به تفصیل بیان کرده‌ایم.');
INSERT INTO "badkhashani" VALUES(6,98,'6:98','و الله آن ذاتی است که شما را از یک نفس آفرید، پس برای شما قرارگاه است (در دنیا) و امانت گاه است (در قبر) واقعا آیات (توحید) را برای قومی که می‌فهمند، به تفصیل بیان کرده‌ایم.');
INSERT INTO "badkhashani" VALUES(6,99,'6:99','و الله آن ذاتی است که از آسمان آبی فرو آورد، پس به وسیلۀ آن آب هر گونه گیاه را بیرون آوردیم، و از آن گیاه، جوانه سبزی را بیرون آوردیم که از آن جوانۀ سبز دانه‌های به هم پیوسته را بیرون آوردیم، و از شگوفۀ درختان خرما خوشه‌هایی است نزدیک به زمین، و نیز (رویانیدیم به آن آب) باغ‌های از درخت‌های انگور و زیتون و انار را مشابه به همدیگر و غیر مشابه به همدیگر، به میوۀ آن درخت، چون ثمر آن ظاهر شود نگاه کنید، و (نیز) به پخته شدن آن، وقتی برسد (نگاه کنید). يقينا در این‌ها (نباتات) برای مردمی که ایمان می‌آورند نشانه‌هاست (که دلیل بر قدرت الله است).');
INSERT INTO "badkhashani" VALUES(6,100,'6:100','و (ليكن كافران به آن آیات یقین نکردند بلکه) با الله شریک‌های از جن مقرر کردند، در حالیکه (الله) آنان را (نیز) آفریده است. و برای الله پسران و دخترانی از روی نادانی خود قرار دادند، در حالیکه پاک و برتر است از آنچه (در حق او تعالی) وصف می‌کنند.');
INSERT INTO "badkhashani" VALUES(6,101,'6:101','ایجاد کنندۀ آسمان‌ها و زمین است (از عدم محض بدون هیچ نمونه ای) چطور برای او فرزندی باشد؟ در صورتی که برای او همسری نبوده و او همه چیز را آفریده است. و او به هر چیزی داناست.');
INSERT INTO "badkhashani" VALUES(6,102,'6:102','این است الله، مالک و متصرف شما، نیست هیچ معبودی برحق به جز او که آفرینندۀ همه چیز است، پس تنها او را عبادت کنید، و او بر هر چیز وکیل و نگهبان است.');
INSERT INTO "badkhashani" VALUES(6,103,'6:103','چشم‌ها (ذات) او را دریافته (و دیده) نمی‌توانند و او چشم‌ها را در می‌یابد، و او مهربان (و) آگاه است.');
INSERT INTO "badkhashani" VALUES(6,104,'6:104','البته دلايل بصیرت و روشنی از نزد پروردگار تان برای شما آمده است، پس هرکس به دیدۀ بصیرت (حق را) بنگرد، پس به سود خود اوست، و هر کس نابینا ماند، پس به ضرر خود اوست. بگو: من بر شما محافظ نیستم.');
INSERT INTO "badkhashani" VALUES(6,105,'6:105','و این گونه آیات (دلائل وحدانیت) خود را به اسلوب‌های مختلف بیان می‌کنیم (تا بفهمند)، و مبادا بگویند: تو (این قرآن را از کسی) درس خوانده ای، و تا اینکه آن (دین) را برای قومی که می‌دانند، بیان کنیم.');
INSERT INTO "badkhashani" VALUES(6,106,'6:106','پیروی کن آنچه را که از طرف پروردگارت به تو وحی شده، هیچ معبودی برحق به جز او نیست. و از مشرکان روی بگردان.');
INSERT INTO "badkhashani" VALUES(6,107,'6:107','و اگر الله می‌خواست آنها شرک نمی‌آوردند. و تو را بر آنها محافظ نگردانیده‌ایم، و تو وکیل آنها نیستی (که معاملۀ آنها به تو تفویض شده باشد).');
INSERT INTO "badkhashani" VALUES(6,108,'6:108','و (ای مسلمانان) آنانی را که غير الله را می‌خوانند دشنام ندهید. چرا که آنها از روی دشمنی و جهالت الله را دشنام خواهند داد، این گونه برای هر امتی عمل آنها را (در دنیا) زینت داده‌ایم. باز بازگشت آنها به‌سوی پروردگارشان است، پس او (الله) آنها را از (حقیقت) آنچه می‌کردند، آگاه خواهد ساخت.');
INSERT INTO "badkhashani" VALUES(6,109,'6:109','و (مشرکین) با سخت‌ترین قسم‌هایشان به الله قسم خوردند، که اگر معجزه‌ای به آنها بیاید حتما به آن ایمان می‌آورند، بگو آیات (معجزه‌ها) فقط در اختيار الله می‌باشد، و شما چه می‌دانید که اگر معجزۀ هم بیاید باز (به آن) ایمان نمی‌آورند.');
INSERT INTO "badkhashani" VALUES(6,110,'6:110','و دل‌ها و دیدگان آنها را برمی گردانیم، طوری که اولین بار به آن ایمان نیاوردند، و آنها را می‌گذاریم تا در سرکشی‌شان سرگردان و حیران بمانند.');
INSERT INTO "badkhashani" VALUES(6,111,'6:111','و اگر ما فرشته‌ها را بر آنها نازل می‌کردیم و مرده‌ها با ایشان سخن می‌گفتند و هر چیزی را یکجا نزد آنان جمع می‌کردیم، باز هم ایمان نمی‌آوردند، مگر اینکه الله بخواهد (با اجبار ایمان بیاورند). و ليكن بیشترشان جاهلند.');
INSERT INTO "badkhashani" VALUES(6,112,'6:112','و همچنین ما برای هر پیغمبری دشمنی از شیطان‌های انس و جن قرار دادیم. که بعضی از آنها به بعضی دیگر دروغهای مزین را (برای فریب دادن مردم) القا می‌کنند، و اگر پروردگارت می‌خواست این کار را نمی‌کردند، پس ایشان را با افترا و دروغشان بگذار.');
INSERT INTO "badkhashani" VALUES(6,113,'6:113','و (سخن‌های فریبنده را به یکدیگر می‌رسانند) تا دل‌های آنانی که به روز آخرت ایمان نمی‌آورند، به‌سوی آن (سخن‌ها) مایل شود، و تا آن را بپسندند (و به آن خوش شوند)، و تا بدست آورند آن بدی‌های را که در پی بدست آوردن آن‌اند.');
INSERT INTO "badkhashani" VALUES(6,114,'6:114','(بگو:) آیا (با این همه دلايل روشن) داور دیگری غیر از الله را طلب کنم؟ در حالیکه اوست که (برای حَکم ساختن) کتاب (قرآن) را به تفصیل به‌سوی شما نازل کرده است. و آنانی که (قبلا) به آنها کتاب داده‌ایم، می‌دانند که این کتاب (قرآن) از جانب پروردگارت به حق نازل شده است. پس هیچگاه از شک کنندگان مباش.');
INSERT INTO "badkhashani" VALUES(6,115,'6:115','و کلام پروردگارت در راستی و عدالت به حد کمال است، هیچ تغییر دهنده‌ای برای کلمات او نیست. و اوست شنوای دانا.');
INSERT INTO "badkhashani" VALUES(6,116,'6:116','و اگر از اکثر افراد روی زمین اطاعت کنی، تو را از راه الله گمراه می‌کنند (چون اینها) جز از گمان پیروی نمی‌کنند، و جز به دروغ سخن نمی‌گویند.');
INSERT INTO "badkhashani" VALUES(6,117,'6:117','يقينا پروردگار تو به کسانی که از راه او گمراه می‌شوند آگاه‌تر است. و به هدایت یافتگان (نیز) آگاه‌تر است.');
INSERT INTO "badkhashani" VALUES(6,118,'6:118','پس (مطابق هدایت پروردگارتان) بخورید از ذبحی که نام الله بر آن یاد شده، اگر به آيات الله ایمان دارید.');
INSERT INTO "badkhashani" VALUES(6,119,'6:119','و شما را چه شده که از آنچه نام الله بر آن برده شده است نمی‌خورید؟ در حالیکه الله چیزهایی را که بر شما حرام شده است به تفصیل بیان کرده است، مگر آنچه که (به خوردن آن) ناچار گردید. و البته بسیاری از مردم دیگران را از روی جهل و نادانی، با خواهشات خود، گمراه می‌کنند. یقینا پروردگارت به آنانی که از حد تجاوز می‌کنند داناترست.');
INSERT INTO "badkhashani" VALUES(6,120,'6:120','و گناه آشکار و پنهان را ترک کنید؛ البته آنانی که مرتکب گناه می‌شوند، به زودی سزای اعمالی را که مرتکب شده‌اند، خواهند دید.');
INSERT INTO "badkhashani" VALUES(6,121,'6:121','از ذبحی که نام الله بر آن یاد نشده است مخورید چون (خوردن آن) فسق و نافرمانی است، بی‌گمان شیاطین به دوستان خود القا می‌کنند تا با شما مجادله کنند، اگر از ایشان اطاعت کنید، يقينا شما هم مشرک می‌شوید.');
INSERT INTO "badkhashani" VALUES(6,122,'6:122','آیا آن کس که (با جهل و شرک) مرده بود، پس ما او را (با هدایت خود) زنده ساختیم و برای او نوری (ایمانی) بخشیدیم تا به وسيلۀ آن در میان مردم راه خود را بیابد، مانند کسی است که به تاریکی گرفتار است و راه بیرون شدن از آن را نمی‌داند؟ اینچنین اعمال کافران، در نظرشان مزین جلوه داده شده بود.');
INSERT INTO "badkhashani" VALUES(6,123,'6:123','و ما این چنین در هر قریه (دهی) مجرمان را بزرگان‌شان قرار دادیم تا در آن دهات، حيله (و فساد) کنند. اما جز به ضرر خودشان حیله سازی نمی‌کنند و (لیکن) نمی‌فهمند.');
INSERT INTO "badkhashani" VALUES(6,124,'6:124','هرگاه آیه‌ای بر آنها نازل شد، گفتند: هرگز ما ایمان نمی‌آوریم تا آن که آنچه به پیغمبران الله داده شده به ما نیز داده شود. (بگو:) الله داناتر است که رسالت خود را در کجا قرار دهد. به زودی به مجرمانی که گناه می‌کردند، سزای آن مكر و نیرنگی که می‌کردند، خواری و عذابی شدید از نزد الله خواهد رسید.');
INSERT INTO "badkhashani" VALUES(6,125,'6:125','پس الله هر که را بخواهد هدایت کند، سینۀ او را برای پذیرش اسلام می‌گشاید. و هر که را بخواهد گمراه کند، پس سینۀ او را (از پذیرفتن اسلام) تنگ می‌کند، گویا که به آسمان بالا می‌رود، این گونه الله به آنهایی که ایمان نمی‌آورند، پلیدی را قرار می‌دهد.');
INSERT INTO "badkhashani" VALUES(6,126,'6:126','و این است راه راست پروردگارت، البته ما آیات (خود) را برای قومی که عبرت می‌گیرند، به تفصیل بیان کرده‌ایم.');
INSERT INTO "badkhashani" VALUES(6,127,'6:127','برای آنها در نزد پروردگارشان، خانۀ سلامتی و آرامش است. و او دوست و کارساز‌شان است به سبب کارهایی که می‌کردند.');
INSERT INTO "badkhashani" VALUES(6,128,'6:128','و روزی که الله همۀ آنها را یکجا جمع کند (و بگوید:) ای گروه جن! از انسانها پیروان زیادی گرفتید. و دوستان آنها از انسانها گویند: ای پروردگار ما! بعضی ما از بعضی دیگر استفاده کرد، و به میعادی که برای ما مقرر کرده بودی، رسیدیم، (الله در جواب شان) گوید: آتش دوزخ جایگاه شماست همیشه در آن هستید، مگر چیزی را الله بخواهد، بی‌گمان پروردگار تو باحکمت (و) داناست.');
INSERT INTO "badkhashani" VALUES(6,129,'6:129','و این چنین بعضی ظالمان را بر بعضی دیگر به سبب آنچه می‌کردند، مسلط می‌کنیم.');
INSERT INTO "badkhashani" VALUES(6,130,'6:130','ای گروه جن و انس! آیا از میان شما پیغمبرانی برای شما نیامدند که آیات و احکام مرا بر شما بیان می‌کردند، و شما را از ملاقات این روز تان می‌ترسانیدند؟ گویند: بر علیه خود اعتراف کردیم. و زندگانی دنیا آنها را فریب داده بود، و بر خود گواهی دادند که کافر بودند.');
INSERT INTO "badkhashani" VALUES(6,131,'6:131','این (فرستادن پیغمبران) به سبب آن است که هرگز پروردگار تو هلاک کنندۀ قریه‌ها از روی ظلم نیست، در حالیکه اهل آن (از احکام الله) غافل و بی‌خبر باشند.');
INSERT INTO "badkhashani" VALUES(6,132,'6:132','و برای هر یک مطابق کردارشان مراتبی است، و پروردگار تو از آنچه می‌کنند، بی‌خبر نیست.');
INSERT INTO "badkhashani" VALUES(6,133,'6:133','و پروردگار تو بی‌نیاز و صاحب رحمت است. اگر بخواهد شما را می‌برد (هلاک می‌کند) و بعد از شما هر کسی را بخواهد جایگزین می‌کند، همان طوری که شما را از نسل قوم دیگر آفرید (و جایگرین آنها ساخت)،');
INSERT INTO "badkhashani" VALUES(6,134,'6:134','يقينا آنچه وعده داده می‌شوید آمدنی است. و شما نمی‌توانید (الله را) ناتوان سازید.');
INSERT INTO "badkhashani" VALUES(6,135,'6:135','بگو: ای قوم من، به جایگاه و منزلت خویش عمل کنید، که من هم (بر اساس جایگاه و منزلت خود) عمل می‌کنم، پس به زودی خواهید دانست که سرانجام سرای آخرت از که خواهد بود. يقينا ظالمان کامیاب نمی‌شوند.');
INSERT INTO "badkhashani" VALUES(6,136,'6:136','و برای الله از کشت‌ها و چار پایانی که آفریده است نصیبی مقرر کردند (در حالیکه همه از الله است) و (به گمان باطل خود) گفتند: که این قسمت برای الله است و این قسمت برای بتان ماست. پس آنچه سهم بتانشان بود به الله نمی‌رسید؛ ولی هر آنچه سهم الله بود به بتانشان می‌رسید. چه بد قضاوت (و داوری) می‌کنند.');
INSERT INTO "badkhashani" VALUES(6,137,'6:137','و این چنین برای بسیاری از مشرکان، شیاطین‌شان کشتن فرزندانشان را زیبا جلوه دادند، تا هلاکشان کنند و دینشان را بر آنان مشتبه گردانند، و اگر الله می‌خواست آنها چنین نمی‌کردند. پس آنان را با دروغ‌هایی که می‌بافند بگذار.');
INSERT INTO "badkhashani" VALUES(6,138,'6:138','و (به گمان فاسد خود) گفتند: اینها چهارپایان و کشتزار (های) ممنوع است، که جز کسی که ما می‌خواهیم نباید از آن بخورد، و چهار پایانی است که (سوار شدن بر) پشت آنها حرام شده است. و چهار پایانی (داشتند) که وقت ذبح نام الله را بر آن ذکر نمی‌کردند. (این احکام را) بر الله به دروغ نسبت دادند، (و الله) به زودی ایشان را به خاطر آنچه افترا می‌کردند، سزا خواهد داد.');
INSERT INTO "badkhashani" VALUES(6,139,'6:139','و گفتند: هر آنچه در شکم این چهار پایان است برای مردان ما حلال و برای زن‌های ما حرام است، و اگر مردار باشد (مرده به دنیا آمده باشد) زن و مرد در آن شریک می‌باشند، الله به سبب این گفتار آنها را سزا خواهد داد. چون او حكيم (و) داناست.');
INSERT INTO "badkhashani" VALUES(6,140,'6:140','البته خساره مند شد کسانی که اولاد خود را به نادانی کشتند، و چیزی را که الله به ایشان روزی داده بود با دروغ بستن بر الله حرام کردند، به راستی که گمراه شدند، و هدایت یافته نبودند.');
INSERT INTO "badkhashani" VALUES(6,141,'6:141','و (الله) اوست که باغهایی آفرید، که برخی درختان آن برداشته شده بر پایه‌ها است و برخی هم غیر برداشته شده بر پایه‌ها است، و درخت خرما و کشتزار، با طعمهای گوناگون، و زیتون و انار، (در شکل) مشابه به یکدیگر، و (در مزه و لذت) مشابهت ندارند. چون میوه دهند و (میوۀ آن) پخته شود پس از آنها بخورید. و در روز درو حق آن را (عشر آن را) نیز بپردازید. و اسراف مکنید چون که الله اسرافکاران را دوست ندارد.');
INSERT INTO "badkhashani" VALUES(6,142,'6:142','و نیز (آفرید)، چهار پایانی که بار می‌برند و بعضی که از پشم و موی آن فرش ساخته می‌شود (و یا حیوانی که مانند فرش در زمین خوابانیده و ذبح می‌شوند) از هر چه الله به شما روزی داده است بخورید و از قدم‌های شیطان پیروی مکنید، چون او دشمن آشکار شماست.');
INSERT INTO "badkhashani" VALUES(6,143,'6:143','(الله برای شما حلال کرده است) هشت جفت، از گوسفند، نر و ماده و از بز، نر و ماده. بگو: آیا، آن دو نر را حرام کرده است یا آن دو ماده را؟ یا آنچه را در شکم ماده‌ها است؟ با علم يقينی و دانش به من خبر دهید اگر (در دعوای تان) صادق هستید.');
INSERT INTO "badkhashani" VALUES(6,144,'6:144','و (الله آفرید) از شتر نر و ماده و از گاو نر و ماده، بگو: آیا آن دو نر را حرام کرده است یا آن دو ماده را با آنچه را که در شکم ماده‌ها است؟ آیا شما حاضر بودید وقتی که الله شما را به این کار امر کرد؟ پس کیست ظالم‌تر از کسی که بر الله افترا کند تا بدون علم مردم را گمراه سازد؟ یقینا الله قوم ظالم را هدایت نمی‌کند.');
INSERT INTO "badkhashani" VALUES(6,145,'6:145','بگو: در میان آنچه بر من وحى شده است چیزی را که خوردن آن حرام باشد، نمی‌یابم، جز مردار یا خون ریخته یا گوشت خوک که همۀ اینها ناپاک‌اند، یا حیوانی که (در وقت ذبح) نام غير الله بر آن برده شده باشد. پس هرکس ناچار به خوردن (این محرمات) گردد بی‌آنکه سرکشی کند و از حد بگذرد، پس بداند که پروردگارت آمرزنده (و) مهربان است.');
INSERT INTO "badkhashani" VALUES(6,146,'6:146','و بر یهود هر حیوان ناخن دار را حرام کردیم و از گاو و گوسفند، چربی آن دو را، بر آنها حرام کردیم مگر آن چربی که بر پشت آن دو، یا بر روده‌ها باشد، یا (آن چربی که) به استخوانها چسپیده باشد. این (تحریم) به سزای سرکشی‌شان (از دین) بود و يقينا ما صادق‌ایم.');
INSERT INTO "badkhashani" VALUES(6,147,'6:147','پس اگر تو را تکذیب کنند، پس بگو: (شما خورسند مشوید) چون پروردگار شما دارای رحمت گشاده است (لذا در عذاب شما تعجیل نمی‌کند ورنه) عذاب او از گروه مجرمان رد نمی‌شود.');
INSERT INTO "badkhashani" VALUES(6,148,'6:148','مشرکان (در بر حق بودن خود) خواهند گفت: اگر الله می‌خواست ما و پدران ما شرک نمی‌آوردیم و نه بر خود چیزی را حرام می‌کردیم، آنانی که پیش از ایشان بودند نیز همچنین (پیغامبران را) تکذیب کردند تا آن که عذاب ما را چشیدند. بگو: آیا نزد شما علمی (دلیلی قطعی) هست (بر این دعوی تان) تا آن را برای ما آشکار کنید؟ (بلکه) شما از گمان پیروی می‌کنید، و دروغگویانی بیش نیستید.');
INSERT INTO "badkhashani" VALUES(6,149,'6:149','بگو: دلیل محکم و رسا خاص برای الله است، اگر می‌خواست همه شما را یکجا هدایت می‌کرد.');
INSERT INTO "badkhashani" VALUES(6,150,'6:150','بگو: شاهدان خود را که گواهی دهند به این که الله اینها را حرام کرده است بیاورید؟ پس اگر گواهی دادند پس تو با آنها گواهی مده (یعنی گواهی آنها را قبول مكن) و از خواهشات آنانی که آیات ما را تکذیب کرده‌اند، و آنانی را که به روز قیامت ایمان ندارند پیروی مکن، و آنها با پروردگار خود همتا قرار می‌دهند.');
INSERT INTO "badkhashani" VALUES(6,151,'6:151','بگو: بیایید تا آنچه را که پروردگارتان بر شما حرام کرده است، برایتان تلاوت کنم اینکه به الله شریک قرار مدهید و به پدر و مادر نیکی کنید و از بیم فقر و تنگدستی اولاد خود را مکشید ما به شما و ایشان روزی می‌دهیم و به کارهای زشت (بی حیایی) چه ظاهر باشد و چه پوشیده نزدیک مشوید. و کسی را که الله کشتن او را حرام کرده است مگر به حق مکشید. این چیزهایی است که شما را به (ترک) آن سفارش می‌کند، تا درک کنید و دریابید.');
INSERT INTO "badkhashani" VALUES(6,152,'6:152','و به مال يتيم نزدیک مشوید مگر به طريقۀ نیک (و مشروع) تا اینکه به سن رشد (جوانی و بلوغ) برسد. و پیمانه و ترازو را به عدل و انصاف مراعات کنید، هیچکس را مکلف نمی‌سازیم مگر به قدر طاقت و توان او. و هرگاه سخن گفتید پس انصاف (را مراعات) کنید، و اگر چه (کسی که در بارۀ او سخن می‌گویید) خویشاوند شما باشد. و به عهد الله وفا کنید، این‌ها اموری است که (الله) شما را به آن سفارش کرده است، تا عبرت گیرید.');
INSERT INTO "badkhashani" VALUES(6,153,'6:153','و (بگو) این است راه راست من، پس از آن پیروی کنید و از راههای دیگر پیروی مکنید، که شما را از راه (مستقیم) پراکنده می‌سازد. این است آنچه (الله) شما را به آن سفارش می‌کند، تا پرهیزگار شوید.');
INSERT INTO "badkhashani" VALUES(6,154,'6:154','باز به موسی کتاب (تورات) دادیم تا نعمت را بر کسی که نیکی کرده است تمام کنیم، و تفصیل کننده هر چیزی باشد و نیز راهنمایی و رحمت برای مردم باشد. به این امید که (بنی اسرائیل) به ملاقات پروردگارشان ایمان بیاورند.');
INSERT INTO "badkhashani" VALUES(6,155,'6:155','و این (قرآن نیز) کتابی است که بابرکت آن را نازل کرده‌ایم، پس آن را پیروی کنید، و تقوی پیشه کنید. تا مستحق رحمت (الله) گردید.');
INSERT INTO "badkhashani" VALUES(6,156,'6:156','تا نگویید که کتاب تنها بر دو گروهی پیش از ما نازل شده است، و در حالیکه ما از آموختن آنها غافل بوده‌ایم.');
INSERT INTO "badkhashani" VALUES(6,157,'6:157','یا تا نگویید که اگر بر ما هم کتاب نازل می‌شد، بهتر از آنان راه یافته‌تر می‌بودیم. بدون شک بر شما هم از جانب پروردگارتان دلیل روشن و هدایت و رحمت آمده است. پس کیست ظالم‌تر از آن کس که آيات الله را تکذیب کند و از آن روی بگرداند؟ به زودی کسانی را که از آیات ما روی می‌گردانند، به سبب این اعراض‌شان به عذابی سخت سزا خواهیم داد.');
INSERT INTO "badkhashani" VALUES(6,158,'6:158','آیا (این اعراض کنندگان) انتظار می‌کشند که فرشتگان پیش آنها بیایند یا پروردگارت بیاید یا بعضی از نشانه‌های پروردگارت بیاید؟! روزی که بعضی از نشانه‌های پروردگارت بیاید، کسی که از پیش ایمان نیاورده و یا در وقت مومن بودن کار نیکی انجام نداده باشد، ایمانش به او سودی نمی‌رساند. بگو: انتظار بکشید ما هم با شما منتظریم.');
INSERT INTO "badkhashani" VALUES(6,159,'6:159','يقينا آنانی که دین خود را پراگنده کردند و گروه گروه شدند، تو به هیچ وجه از آنان نیستی (تو مسؤل آنان نیستی)، جزء این نیست که کار آنها با الله است. باز الله آنها را به آنچه می‌کردند، آگاه خواهد ساخت.');
INSERT INTO "badkhashani" VALUES(6,160,'6:160','هر کس کار نیکی انجام دهد پس ده برابر به او پاداش است، و هر که کار بدی انجام دهد تنها مانند آن سزا خواهد دید، و به آنها هرگز ظلم نخواهد شد.');
INSERT INTO "badkhashani" VALUES(6,161,'6:161','بگو: یقینا پروردگارم مرا به راه راست هدایت کرده است، و آن دین راست و استوار، دین ابراهیم است که از ادیان باطل متنفر بود و از مشرکان نبود.');
INSERT INTO "badkhashani" VALUES(6,162,'6:162','بگو: یقینا نماز من و عبادت من و زندگی من و مرگ من، همه برای الله؛ پروردگار جهانیان است.');
INSERT INTO "badkhashani" VALUES(6,163,'6:163','هیچ شریکی برای او نیست، و به این (توحید) امر شده ام، و من نخستین مسلمانم.');
INSERT INTO "badkhashani" VALUES(6,164,'6:164','بگو: آیا جز الله پروردگار دیگری بجویم؟ در حالیکه او پروردگار (مالک و متصرف) هر چیزی است. و هیچ کس عمل بد را جز به زیان خود انجام نمی‌دهد (عذابش فقط برای خودش است)، و هیچ کسی بار گناه کسی دیگر را بر نمی‌دارد. باز بازگشت همۀ شما به‌سوی پروردگارتان است، و او شما را به آنچه که در آن اختلاف می‌کردید، آگاه می‌سازد.');
INSERT INTO "badkhashani" VALUES(6,165,'6:165','و او (الله) آن ذاتی است که شما را جانشینان (اقوام دیگر) در زمین قرار داده است، و بعضی شما را بر بعضی دیگر در مراتب برتری داد، تا شما را در آنچه (از نعمت‌ها) که داده است بیازماید. یقینا پروردگار تو زود سزادهنده است، و بدون شک او آمرزندۀ مهربان است.');
INSERT INTO "badkhashani" VALUES(7,1,'7:1','المص. معنای این حروف به الله معلوم است.');
INSERT INTO "badkhashani" VALUES(7,2,'7:2','این کتابی است که به‌سوی تو نازل شده پس نباید در سینه ات به خاطر آن (بخاطر تكذیب مشرکان) تنگی باشد، (این کتاب بر تو نازل شد) تا به وسیلۀ آن (مردم را) بیم دهی، و (تا) برای مؤمنان پندی باشد.');
INSERT INTO "badkhashani" VALUES(7,3,'7:3','(پس) آنچه را از سوی پروردگارتان بر شما نازل شده، پیروی کنید. و غیر از او (الله) از دوستان دیگر پیروی مکنید، چه اندک پند می‌گیرید.');
INSERT INTO "badkhashani" VALUES(7,4,'7:4','و (به سبب پند نپذیرفتن) چه بسیار قریه‌های که (مردمش را) هلاک کردیم، پس عذاب ما در شب، یا در خواب نیم روز به‌سوی آنان آمد.');
INSERT INTO "badkhashani" VALUES(7,5,'7:5','پس چون عذاب ما به آنها آمد سخنشان جز این نبود که گفتند: ما ظالم بودیم.');
INSERT INTO "badkhashani" VALUES(7,6,'7:6','پس حتما از کسانی که پیغمبران به‌سوی آنها فرستاده شده خواهیم پرسید، و حتما از پیغمبران نیز خواهیم پرسید.');
INSERT INTO "badkhashani" VALUES(7,7,'7:7','و البته (از هر چه کرده اند) با علم و آگاهی برایشان بیان خواهیم کرد، چون ما (از احوال شان) غایب نبودیم.');
INSERT INTO "badkhashani" VALUES(7,8,'7:8','و در آن روز، وزن (اعمال) حق است. لذا هر کس که کفۀ (اعمال نیک ) وزن شدۀ او سنگین باشد پس آن گروه کامیاب‌اند.');
INSERT INTO "badkhashani" VALUES(7,9,'7:9','و هر کس که کفۀ (اعمال نیک) وزن شدۀ او سبک باشد، پس آن گروه آنانی‌اند که خود را خساره مند کرده‌اند، به سبب آنکه به آیات ما ظلم می‌کردند.');
INSERT INTO "badkhashani" VALUES(7,10,'7:10','و البته ما شما را در زمین جایگاه و اسقرار دادیم، و برای شما در آن وسائل معیشت (تان را) گذاشتیم. (اما) چه کم شکر گزاری (الله را ادا) می‌کنید.');
INSERT INTO "badkhashani" VALUES(7,11,'7:11','و البته شما را آفریدیم (پدر شما را از خاک)، باز شما را صورت دادیم (آدم را و به تبع او شما را)، باز به فرشته‌ها گفتیم: (امر کردیم) برای آدم سجده کنید، پس همه (فرشته‌ها) سجده کردند مگر ابلیس که از سجده کنندگان نبود.');
INSERT INTO "badkhashani" VALUES(7,12,'7:12','فرمود: (الله به ابلیس ) چه چیز تو را از سجده کردن بازداشت، وقتی تو را به سجده امر کردم؟ ابلیس گفت: زیرا من از آدم بهترم (چون) مرا از آتش آفریدی و او را از گل.');
INSERT INTO "badkhashani" VALUES(7,13,'7:13','(الله) فرمود: پس از آن (آسمان) پایین شو، تو را چه رسد که در آن تکبر ورزی، پس بیرون شو، یقینا تو از خوار شدگانی.');
INSERT INTO "badkhashani" VALUES(7,14,'7:14','گفت: (ابلیس ای الله!) مرا تا روزی که مردم برانگیخته می‌شوند مهلت ده.');
INSERT INTO "badkhashani" VALUES(7,15,'7:15','(الله) فرمود: یقینا تو از مهلت داده شدگانی.');
INSERT INTO "badkhashani" VALUES(7,16,'7:16','گفت: (ابلیس ای الله!) پس به سبب اینکه مرا گمراه نمودی، من هم (برای گمراه ساختن) آنها (اولاد آدم) در سر راه راست تو می‌نشینم.');
INSERT INTO "badkhashani" VALUES(7,17,'7:17','باز (برای گمراه ساختن آنها) از پیش روی آنها، و از پشت سر آنها، و از طرف راست شان، و از طرف چپ‌شان می‌آیم. و اکثر آنها را شکر گزار نخواهی یافت.');
INSERT INTO "badkhashani" VALUES(7,18,'7:18','(الله) فرمود: از آن نکوهیده و رانده شده بیرون رو، که البته هر کسی از آنها از تو پیروی کند، حتما دوزخ را از همۀ شما (تو و پیروان تو) پر می‌کنم.');
INSERT INTO "badkhashani" VALUES(7,19,'7:19','و (گفتیم:) ای آدم! تو و همسرت در جنت سکونت گزینید، پس از هر جا که می‌خواهید بخورید، ولی به این درخت نزدیک مشوید که از ظالمان می‌شوید.');
INSERT INTO "badkhashani" VALUES(7,20,'7:20','پس شیطان (در دل) هردو وسوسه انداخت تا (در نتیجۀ وسوسه) برای هردو آنچه از شرمگاه‌شان را که (از دیدن) مستور بود، آشکار گرداند. و گفت: (شیطان در وسوسه خود) پروردگارتان شما را از (خوردن) این درخت منع نکرده است، مگر اینکه مبادا (به سبب خوردن آن) دو فرشته گردید، یا از باشندگان دائمی جنت شوید.');
INSERT INTO "badkhashani" VALUES(7,21,'7:21','و برای آن دو قسم خورد که من برای شما از خیرخواهانم.');
INSERT INTO "badkhashani" VALUES(7,22,'7:22','پس آن دو را با فریب (از مرتبه شان) پایین کرد. پس چون آن دو از آن درخت چشیدند، عورتهای‌شان برای آنها آشکار شد، و شروع نمودند به چسپانیدن برگ درختان جنت بر جسم خود. و پروردگارشان ندا داد: آیا شما را از آن درخت منع نکرده بودم و نگفته بودم که بی‌گمان شیطان دشمن آشکار شماست؟');
INSERT INTO "badkhashani" VALUES(7,23,'7:23','(آدم و حوا به غفلت خود اعتراف نموده عرض کردند و) گفتند: ای پروردگار ما! ما بر خود ظلم کردیم، و اگر ما را نیامرزی و به ما رحم نکنی، البته از زیان کاران می‌شویم.');
INSERT INTO "badkhashani" VALUES(7,24,'7:24','(الله) فرمود: پایین شوید (که) شما دشمن یکدیگرید، و برای شما در زمین قرارگاه (و مسکن) است، و بهره مندی است تا مدتی معین.');
INSERT INTO "badkhashani" VALUES(7,25,'7:25','(باز الله) فرمود: در آن (زمین) زندگی می‌کنید، و در آن می‌میرید، و از آن (در روز قیامت) برانگیخته می‌شوید.');
INSERT INTO "badkhashani" VALUES(7,26,'7:26','ای اولاد آدم! البته ما برای شما لباسی را فرود آوردیم که عورت‌های شما را می‌پوشاند، و برای شما زینت است. و (لیکن) لباس پرهیزگاری و (عمل صالح) این (از همه) بهتر است. این از نشانه‌های الله (بر اکرام اولاد آدم) است. تا (نعمت او را) یادآور شوند.');
INSERT INTO "badkhashani" VALUES(7,27,'7:27','ای اولاد آدم! مبادا شیطان شما را فریب دهد (و در فتنه و گمراهی بیاندازد) طوری که پدر و مادر شما را از جنت بیرون کرد، لباس‌شان را از تنشان بیرون کشید تا شرمگاهشان را به ایشان نمایان کند. البته شيطان و قبیله اش از جایی که آنها را نمی‌بینید شما را می‌بینند. البته ما شيطانها را دوستان کسانی قرار دادیم که ایمان نمی‌آورند.');
INSERT INTO "badkhashani" VALUES(7,28,'7:28','(لذا) چون کار زشتی (بی حیائی) کنند، می‌گویند: ما پدران خود را بر آن یافته‌ایم، و الله ما را به آن امر کرده است، بگو: هرگز الله به کار زشت امر نمی‌کند، آیا آنچه را که نمی‌دانید به الله نسبت میدهید؟');
INSERT INTO "badkhashani" VALUES(7,29,'7:29','بگو: (بلکه) پروردگارم به عدل و انصاف امر کرده است، و اینکه چهره‌های تان را در هر مسجدی (در هر نماز به‌سوی قبله) راست کنید، و او را اطاعت کنید، در حالیکه دین خود را برای او خالص گردانیده‌اید. چنانکه شما را (در ابتدا) آفرید (بار دوم زنده کرده) به‌سوی او بر می‌گردید.');
INSERT INTO "badkhashani" VALUES(7,30,'7:30','گروهی را هدایت کرد، و گروهی گمراهی بر آنها ثابت شده است. (چون) آنها شیاطین را بجای الله کارساز و دوست خود گردانیده‌اند. و (باز هم) گمان می‌کنند که بر راه راست قرار دارند.');
INSERT INTO "badkhashani" VALUES(7,31,'7:31','ای اولاد آدم! زینت خود را (لباس خود را) در هر مسجدی (وقت هر نمازی) بگیرید، و بخورید و بنوشید ولی (در لباس و خورد و نوش ) اسراف (و حد گذری) مکنید. (چون) الله اسراف کنندگان را دوست ندارد.');
INSERT INTO "badkhashani" VALUES(7,32,'7:32','بگو: چه کسی زینت الله را که برای بندگانش بیرون آورده، و چیزهای پاک را حرام کرده است؟ بگو: آن (وسایل زینت و چیزهای پاک) در زندگانی دنیا برای مؤمنان است. و روز قیامت هم خالص از مؤمنان خواهد بود. اینچنین آیات را برای قومی که می‌دانند. بیان می‌کنیم.');
INSERT INTO "badkhashani" VALUES(7,33,'7:33','بگو: جز این نیست که پروردگارم فواحش (بی حیائی‌ها) را حرام کرده است، آنچه آشکار باشد از آن، و آنچه پنهان باشد. و (حرام کرده است) گناه و تجاوز و سرکشی به ناحق را (در حق مردم)، و (حرام کرده است) این که چیزی را با الله شریک مقرر کنید که بر حقانیت آن هیچ دلیلی نازل نکرده است. و (حرام کرده است) این که چیزی را که (بطلان آن را) نمی‌دانید، به الله نسبت دهید.');
INSERT INTO "badkhashani" VALUES(7,34,'7:34','و برای هر امتی اجلی (مقرر) است، پس چون اجلشان آید نه ساعتی پس می‌شود، و نه پیش می‌شود.');
INSERT INTO "badkhashani" VALUES(7,35,'7:35','ای اولاد آدم! اگر برای شما پیغامبران از جنس خود شما بیایند که آیات (و احکام مرا) بر شما بخوانند، پس هرکس از الله بترسد و (اعمال خود را) اصلاح کند، پس بر آنها (در روز قیامت) هیچ ترسی نخواهد بود و نه آنها غمگین می‌شوند.');
INSERT INTO "badkhashani" VALUES(7,36,'7:36','و (اما) آنانی که آیات ما را تکذیب کنند و از (قبول) آنها تكبر ورزند، این گروه اهل دوزخ‌اند و در آن همیشه‌اند.');
INSERT INTO "badkhashani" VALUES(7,37,'7:37','پس کیست ظالم‌تر از آن که بر الله دروغ ببندد یا آیات او را دروغ بشمارد؟ این گروه، به آنها حصه‌شان از آنچه در کتاب (نامۀ اعمال شان) بر آنان نوشته شده، خواهد رسید تا وقتی که چون نزد آنها فرستادگان ما بیایند که ارواح‌شان را قبض کنند، گویند: کجاست آنچه غیر از الله عبادت می‌کردید؟ (تا شما را از مرگ نجات دهند؟ در جواب) گویند: آنها از نظر ما گم شدند، و عليه خود گواهی دهند که کافر بودند.');
INSERT INTO "badkhashani" VALUES(7,38,'7:38','(الله) فرمود: به همراه امت‌هایی از جن‌ها و انسانها که پیش از شما گذشته‌اند داخل دوزخ شوید، هرگاه گروهی داخل دوزخ شود هم مانند خود را لعنت می‌کند، تا اینکه همه در آن یکدیگر را بیابند، آنگاه پیروان به پیشینیان (زعمای) خود می‌گویند: پروردگارا! اینها ما را گمراه کردند، پس به آنان دوچند از عذاب آتش بده. الله می‌فرماید: برای هر یک عذاب دو چندان است ليكن شما نمی‌دانید.');
INSERT INTO "badkhashani" VALUES(7,39,'7:39','و گروه پیشین از آنها به گروه پسین گویند: پس شما را بر ما هیچ فضلی نیست، لذا عذاب آتش را (مانند ما) بچشید به سبب آنچه می‌کردید.');
INSERT INTO "badkhashani" VALUES(7,40,'7:40','البته آنانی که آیات ما را دروغ شمردند و از قبول کردن آن تكبر نمودند، هرگز برای (اعمال و ارواح)‌شان دروازه‌های آسمان گشوده نمی‌شود، و به جنت داخل نمی‌شوند تا آن که شتر در سوراخ سوزن در آید، و این چنین مجرمان را سزا می‌دهیم.');
INSERT INTO "badkhashani" VALUES(7,41,'7:41','برای آنها (مجرمان) فرشهای از آتش دوزخ است، و از بالای‌شان هم پوشش‌ها (لحاف‌ها از آتش دوزخ) و ما این چنین ظالمان را سزا می‌دهیم.');
INSERT INTO "badkhashani" VALUES(7,42,'7:42','و آنانی که ایمان آوردند و کارهای نیک کردند (به قدر طاقت خود) هیچ کس را مکلف نمی‌سازیم مگر به قدر طاقتش. آن گروه اهل جنت‌اند (که) در آن همیشه می‌باشند.');
INSERT INTO "badkhashani" VALUES(7,43,'7:43','و (در جنت) هرگونه کینه‌ای را از سینه‌هایشان (مؤمنان) دور می‌کنیم، در حاليكه نهرها در زیر (قصرهای)‌شان جاری است و گویند: سپاس پروردگاری راست که ما را به این راه هدایت کرد و اگر ما را هدایت نمی‌کرد، هرگز راه خویش را نمی‌یافتیم. البته پیغمبران پروردگار ما به حق آمدند. و ایشان را ندا داده شود که این همان جنتی است که آنرا به پاداش کار‌هایی که می‌کردید، به میراث برده‌اید.');
INSERT INTO "badkhashani" VALUES(7,44,'7:44','و جنتیان دوزخیان را ندا دهند که ما آنچه را پروردگار ما به ما وعده داده بود، راست یافتیم، پس آیا شما هم آنچه را که پروردگارتان به شما وعده داده بود راست یافتید؟ گویند: آری! پس آواز دهندۀ در میان آنها آواز می‌دهد که لعنت الله بر ظالمان باد.');
INSERT INTO "badkhashani" VALUES(7,45,'7:45','(هم) آنانی که مردم را از راه الله (توحید) منع می‌کردند و برای آن کجی می‌خواستند، و آنها از روز آخرت هم منکر بودند.');
INSERT INTO "badkhashani" VALUES(7,46,'7:46','و در بین آن دو (جنت و دوزخ) پرده‌ای است. و بر اعراف مردمانی هستند که هریک (از اهل جنت و اهل دزوخ) را از سیمای آنان می‌شناسند، و اهل جنت را ندا می‌دهند که سلام بر شما باد، در حالیکه هنوز خودشان وارد جنت نشده‌اند، اما امید (داخل شدن) آن را دارند.');
INSERT INTO "badkhashani" VALUES(7,47,'7:47','و چون چشمان‌شان (اصحاب اعراف ) به‌سوی دوزخیان گردانیده شود، گویند: ای پروردگار ما! ما را همرای مردمان ظالم (در دوزخ) قرار مده.');
INSERT INTO "badkhashani" VALUES(7,48,'7:48','و اهل اعراف مردانی (دوزخی) را آواز دهند که آنها را به علامات‌شان می‌شناسند. گویند: جمعیت (و اتفاق) تان و آن همه تكبر (و سرکشی) که می‌کردید. شما را فایده‌ای نبخشید.');
INSERT INTO "badkhashani" VALUES(7,49,'7:49','آیا این‌ها (جنتیان) کسانی هستند، که قسم می‌خوردید که هرگز الله اینها (جنتیان) را شامل هیچ رحمتی قرار نمی‌دهد، (در حالیکه به ایشان گفته می‌شود) در جنت داخل شوید، هیچ ترسی بر شما نیست، و نه اندوهگین می‌شوید.');
INSERT INTO "badkhashani" VALUES(7,50,'7:50','و دوزخیان جنتیان را ندا می‌دهند که جرعه‌ای از آب یا از چیزی که الله به شما روزی داده است، بر ما بریزید. (در جواب) گویند: یقینا الله آن هر دو را بر کافران حرام ساخته است.');
INSERT INTO "badkhashani" VALUES(7,51,'7:51','آنانی که دین خود را به بازی و لهو گرفتند و زندگی دنیا آنها را فریب داد پس امروز آنان را فراموش می‌کنیم، چنانکه آنها ملاقات این روز را (در دنیا) فراموش کرده بودند و آیات ما را انکار می‌کردند.');
INSERT INTO "badkhashani" VALUES(7,52,'7:52','و البته به آنها کتابی آوردیم که مضمون آن را به تفصیل و از روی علم بیان کردیم، در حالیکه هدایت و رحمت برای مؤمنان است.');
INSERT INTO "badkhashani" VALUES(7,53,'7:53','آیا آنان جز در انتظار تأويل آنند (در انتظار انجام و نتیجه آن کتاب اند)؟ روزی که نتیجه و انجام آن بیاید (ظاهر شود) آنانی که نتیجه را پیش از آن (در دنیا) فراموش کرده بودند، می‌گویند: همانا پیغمبران پروردگار ما (دین) حق را آورده بودند (و ليكن ما سخنان آنها را نپذیرفتیم) پس آیا شفاعت کنندگانی هستند که ما را شفاعت کنند (و برای ما واسطه شوند)؟ و یا پس گردانیده شویم (به دنیا) باز عمل کنیم غير از آنچه که می‌کردیم (این آرزوی‌شان فایده ندارد) زیرا خود را زیانمند کردند، و گم شد از آنها آنچه (در دنیا) افترا می‌کردند.');
INSERT INTO "badkhashani" VALUES(7,54,'7:54','يقينا پروردگار شما الله است که آسمان‌ها و زمین را در شش روز آفرید سپس بر عرش قرار گرفت، شب را به روز می‌پوشاند كه به شتاب روز را می‌طلبد و آفتاب و ماه و ستارگان در فرمان او مسخر هستند. آگاه باشید! که آفرینش و فرمانروایی تنها برای الله است. الله بزرگوار و نهایت بزرگ و با برکت است، (چون او) پروردگار جهانیان است.');
INSERT INTO "badkhashani" VALUES(7,55,'7:55','(پس) پرودگارتان را به عاجزی و پنهانی بخوانید. البته الله از حد تجاوز کنندگان را دوست ندارد. (کسی که او را گذاشته دیگری را بخواند).');
INSERT INTO "badkhashani" VALUES(7,56,'7:56','و در زمین بعد از اصلاح آن فساد مکنید و او (الله) را با بیم و امید بخوانید، چون رحمت الله به نیکوکاران قریب است.');
INSERT INTO "badkhashani" VALUES(7,57,'7:57','و او (الله) همان ذاتی است که بادها را مژده دهنده پیشاپیش رحمت خود (پیش از باران) می‌فرستد، تا آن که چون ابر گران را بردارد، (پس) آن را به‌سوی زمین مرده (خشک) روان می‌کنیم، باز (به وسیلۀ آن) آب را می‌بارانیم، باز از آن از هر قسم میوه بیرون می‌کنیم، این گونه مرده‌ها را (زنده نموده) بیرون می‌کنیم. (این قیاس برای این است) تا شما پند گیرید.');
INSERT INTO "badkhashani" VALUES(7,58,'7:58','و شهر (زمین) پاک گیاهش به حکم پروردگارش می‌روید، و آن زمینی که ناپاک است گیاهش جز بی‌فایده و اندک نمی‌روید. این گونه آیات (قدرت) خود را برای مردمی که شکر گزاری می‌کنند بیان می‌کنیم.');
INSERT INTO "badkhashani" VALUES(7,59,'7:59','البته نوح را به‌سوی قومش فرستادیم، پس گفت: (در دعوت خود) ای قوم من! (فقط) الله را عبادت کنید (که) برای شما هیچ معبودی برحق غیر از او نیست، يقينا من بر شما از عذاب روز سخت و بزرگ می‌ترسم.');
INSERT INTO "badkhashani" VALUES(7,60,'7:60','رؤسای قوم نوح گفتند: يقينا ما تو را در گمراهی آشکار می‌بینیم.');
INSERT INTO "badkhashani" VALUES(7,61,'7:61','(نوح در جواب) گفت: ای قوم من! در من هیچگونه گمراهی نیست، و ليكن من پیغام رسان از جانب پروردگار عالمیان هستم.');
INSERT INTO "badkhashani" VALUES(7,62,'7:62','به شما پیام‌های پروردگارم را می‌رسانم و شما را نصیحت می‌کنم (پند و اندرز می‌دهم)، و از جانب الله چیزهایی را میدانم که شما نمی‌دانید.');
INSERT INTO "badkhashani" VALUES(7,63,'7:63','آیا تعجب کردید از اینکه به شما پندی از سوی پروردگارتان بر مردی از خودتان آمد؟ تا شما را (از عذاب الله) بترساند. و تا شما (از شرک) پرهیزگاری کنید و باشد که مورد رحمت (الهی) قرار گیرید.');
INSERT INTO "badkhashani" VALUES(7,64,'7:64','اما او را تکذیب کردند. پس او و کسانی را که با او (ایمان آورده و) در کشتی بودند، نجات دادیم، و آنانی را که آیات ما را دروغ شمردند، غرق کردیم. (چون) آنها قوم نابینا (و گمراه) بودند.');
INSERT INTO "badkhashani" VALUES(7,65,'7:65','و به‌سوی قوم عاد برادرشان هود را فرستادیم. گفت: ای قوم من فقط الله را بپرستید که برای شما معبودی برحق غیر از او نیست، آیا پرهیزگاری نمی‌کنید؟');
INSERT INTO "badkhashani" VALUES(7,66,'7:66','سرداران کافر قوم او گفتند: يقينا ما تو را در بیخردی می‌بینیم، و تو را از دروغگویان می‌پنداریم.');
INSERT INTO "badkhashani" VALUES(7,67,'7:67','(هود در جواب شان) گفت: ای قوم من! در من هیچ بیخردی نیست، بلکه من پیغمبری از جانب پروردگار جهانیانم.');
INSERT INTO "badkhashani" VALUES(7,68,'7:68','پیام‌های پروردگارم را به شما می‌رسانم، و من برای شما خیر خواهی امین‌ام.');
INSERT INTO "badkhashani" VALUES(7,69,'7:69','آیا شما از این تعجب می‌کنید که به شما پندی از جانب پروردگارتان به وسیلۀ مردی از خودتان آمد، تا شما را (از عذاب الله) بترساند؟ و به یاد آورید آن زمان را که شما را جانشین قوم نوح ساخت. و به جسم تان توانایی افزود. پس نعمت‌های الله را به یاد آورید، تا رستگار شوید.');
INSERT INTO "badkhashani" VALUES(7,70,'7:70','(قوم در جواب) گفتند: آیا نزد ما آمده‌ای تا تنها الله را عبادت کنیم و آنچه را که پدران ما عبادت می‌کردند، ترک کنیم؟ پس آنچه را به ما وعده می‌دهی (ما را از آن می‌ترسانی) بر ما بیاور اگر تو از راست گویانی.');
INSERT INTO "badkhashani" VALUES(7,71,'7:71','(هود) گفت: عذاب پروردگارتان بر شما حتمی شده است. آیا در بارۀ نامهایی که شما و پدرانتان (بر آن بتها) نهاده‌اید؛ با من مجادله می‌کنید؟ (در حالیکه) الله هیچ دلیلی بر (حقانیت) آنها نازل نکرده است، پس در انتظار (عذاب الهی) باشید، من هم با شما از انتظار کنندگانم.');
INSERT INTO "badkhashani" VALUES(7,72,'7:72','پس هود و همراهان (مؤمن) او را به رحمتی از جانب خود نجات دادیم، و آنانی که آیات ما را دروغ پنداشتند و مؤمن نبودند، ریشه کن کردیم.');
INSERT INTO "badkhashani" VALUES(7,73,'7:73','و به‌سوی قوم ثمود برادرشان صالح را (فرستادیم) گفت: ای قوم من، فقط الله را عبادت کنید، برای شما معبودی برحق جز الله نیست. همانا از طرف پروردگارتان معجزۀ آشکار برای شما آمده است، این ماده شتر (از جانب) الله برای شما معجزه است (که خلاف عادت آن را از سنگ آفریده است)، پس بگذارید در زمین الله بچرد، و به آن هیچ آزاری نرسانید که شما را عذاب دردناک خواهد گرفت.');
INSERT INTO "badkhashani" VALUES(7,74,'7:74','و به یاد آورید آن وقتی که شما را پس از قوم عاد جانشین (آنان) گردانید، و در زمین شما را جایگاه (خوب و مناسب) داد که بر قسمتهای هموار آن قصرها می‌ساختید و کوها را برای ساختن خانه‌ها می‌تراشیدید. پس نعمت‌های الله را یاد کنید و در زمین فساد مکنید.');
INSERT INTO "badkhashani" VALUES(7,75,'7:75','رؤسای متکبر قوم صالح به مستضعفانی که ایمان آورده بودند گفتند: آیا می‌دانید (یقین می‌کنید) که صالح از طرف پروردگارش فرستاده شده است؟ (مؤمنان مستضعف) گفتند: (بلی) بدون شک ما به آنچه به او فرستاده شده است، ایمان داریم.');
INSERT INTO "badkhashani" VALUES(7,76,'7:76','متکبرین (قوم صالح) گفتند: يقينا ما به آنچه شما به آن ایمان آورده‌اید، کافریم.');
INSERT INTO "badkhashani" VALUES(7,77,'7:77','پس ماده شتر را کشتند و از فرمان پروردگارشان سرکشی نمودند و گفتند: ای صالح! آن (عذاب) را که به ما وعده می‌دهی بیاور، اگر از پیغمبران هستی.');
INSERT INTO "badkhashani" VALUES(7,78,'7:78','پس آنها را زلزله شدید گرفت، پس در وقت صبح، در خانه‌هایشان به زانو افتاده مردند.');
INSERT INTO "badkhashani" VALUES(7,79,'7:79','پس (صالح) از آنها روی گرداند (آنها را مرده به حال‌شان گذاشت) و گفت: البته من پیام پروردگارم را به شما رساندم و شما را پند و اندرز دادم، ولی شما نصیحت کنندگان (خیر خواهان) را دوست ندارید.');
INSERT INTO "badkhashani" VALUES(7,80,'7:80','و لوط (را به حیث پیغمبر فرستادیم) وقتی به قوم خود گفت: آیا آن کار زشت (بی حیائی) را انجام می‌دهید، که هیچ کس از مردم عالم پیش از شما (آن کار قبيح را) نکرده است؟');
INSERT INTO "badkhashani" VALUES(7,81,'7:81','البته شما به جای زنان با مردان آمیزش (وشهوترانی) می‌کنید، بلکه شما مردم اسرافکار (و تجاوزکار) هستید.');
INSERT INTO "badkhashani" VALUES(7,82,'7:82','و جواب قوم لوط جز این نبود که (از روی تمسخر) گفتند: آنها را (لوط و پیروان او را) از قریۀ خود بیرون کنید، زیرا این‌ها مردمی هستند که پاکدامنی را می‌خواهند (و از کار ما بیزاری می‌جویند).');
INSERT INTO "badkhashani" VALUES(7,83,'7:83','پس ما لوط و خانواده اش را، به جز همسر او که از باقی ماندگان در عذاب بود، نجات دادیم.');
INSERT INTO "badkhashani" VALUES(7,84,'7:84','و ما بر آن قوم باران عجیبی (از سنگ‌ها) باراندیم، پس بنگر (ای شنونده) که سرانجام جنایت کاران چه شد؟');
INSERT INTO "badkhashani" VALUES(7,85,'7:85','و به‌سوی اهل مدین برادرشان شعیب را (به حیث پیغمبر) فرستادیم (پس شعیب) گفت: ای قوم من، تنها الله را عبادت کنید که برای شما معبودی برحق غیر از او نیست. همانا از نزد پروردگارتان دليل واضح به شما آمده است، پس (حق) پیمانه و ترازو را کامل ادا کنید، و حقوق و اموال مردم را کم نکنید، و در زمین بعد از اصلاح آن فساد نکنید، این (هدایات) به شما خير (و بهتر) است، اگر مؤمن هستید.');
INSERT INTO "badkhashani" VALUES(7,86,'7:86','و بر سر هر راهی منشینید تا کسانی را که به الله ایمان آورده‌اند بترسانید، و از راه الله (مردم را) منع کنید، و آن را کج می‌خواهید، و به یاد آورید (نعمت الله را) وقتی که شما کم بودید پس شما را زیاد کرد. و بنگرید که سرانجام فسادکنندگان چگونه بوده است.');
INSERT INTO "badkhashani" VALUES(7,87,'7:87','اگر گروهی از شما به آنچه که من از جانب الله به (تبلیغ) آن فرستاده شده ام، ایمان آورده‌اند و گروه دیگری هنوز ایمان نیاورده‌اند، پس صبر کنید تا الله میان ما فيصله کند و او بهترین فیصله کنندگان است.');
INSERT INTO "badkhashani" VALUES(7,88,'7:88','رؤسای متکبر قوم شعيب گفتند: ای شعیب، تو و آنانی را که به تو ايمان آورده‌اند از قریۀ خود بیرون می‌رانیم یا آنکه به دین ما باز گردید. گفت: اگر چه از آن کراهت داشته باشیم؟');
INSERT INTO "badkhashani" VALUES(7,89,'7:89','که اگر به دین شما باز گردیم بعد از اینکه الله ما را از آن نجات داد، همانا بر الله افترا بسته‌ایم. برای ما شایسته نیست که به آن باز گردیم مگر اینکه پروردگار ما بخواهد، علم پروردگار ما همه چیز را احاطه کرده است، بر الله توکل نموده‌ایم. ای پروردگار ما! میان ما و قوم ما به حق فیصله کن و توئی بهترین فیصله کنندگان.');
INSERT INTO "badkhashani" VALUES(7,90,'7:90','و رؤسای قومش که کافر بودند، گفتند: اگر از شعیب پیروی کنید (در آن صورت) سخت زیان خواهید کرد.');
INSERT INTO "badkhashani" VALUES(7,91,'7:91','پس آنها را زلزله سخت گرفت، پس در خانه‌های خود (مرده) و به زانو افتیده صبح کردند.');
INSERT INTO "badkhashani" VALUES(7,92,'7:92','آنانی که شعيب را دروغگوی شمردند (طوری شدند که) گویا هرگز در آن قریه سکونت نکرده بودند، آنانی که شعيب را دروغگوی شمردند، همان زیان کاران بودند.');
INSERT INTO "badkhashani" VALUES(7,93,'7:93','پس شعیب از نزد آنها روی گرداند و گفت: (به طور افسوس ) ای قوم من! البته من پیام‌های پروردگارم را به شما رساندم، و برای شما نصیحت (و خیرخواهی) کردم، پس چگونه بر (هلاکت شدن) قوم کافر غمگین شوم.');
INSERT INTO "badkhashani" VALUES(7,94,'7:94','و در هیچ شهری، پیغمبری را نفرستادیم مگر اینکه اهل آن شهر را (بعد از نافرمانی شان) به سختی‌ها و ضررها گرفتار کردیم تا باشد که (به الله) زاری و تضرع کنند.');
INSERT INTO "badkhashani" VALUES(7,95,'7:95','باز به جای بدی نیکی را قرار دادیم، تا آن که عددشان افزون شد (باز غافل شده) و گفتند: پدران ما هم (گاه) دچار سختی و (گاه دچار) خوشی و آسانی شدند (و این شیوۀ زمانه است)، پس آنها را (به خاطر سخن کفر آمیز شان) ناگهان هلاک کردیم، در حالیکه آنها (از آمدن عذاب) بی‌خبر بودند.');
INSERT INTO "badkhashani" VALUES(7,96,'7:96','و اگر اهل شهرها (که به آنها پیغمبر فرستاده شد) ایمان می‌آوردند و پرهیزگار می‌شدند، البته بر آنها (دروازۀ) برکات از آسمان و زمین را می‌گشودیم، و لیکن (به جای ایمان آوردن) تکذیب کردند. پس آنها را به سبب آنچه می‌کردند (به عذاب) گرفتار کردیم.');
INSERT INTO "badkhashani" VALUES(7,97,'7:97','آیا اهل قریه‌ها در امان شده‌اند از اینکه عذاب ما هنگام شب، در حالیکه به خواب رفته‌اند، به آنها برسد؟');
INSERT INTO "badkhashani" VALUES(7,98,'7:98','آیا اهل قریه‌ها در امان شده‌اند از اینکه عذاب ما در وقت چاشت، در حالیکه به بازی و تفریح مشغول‌اند، به آنها برسد؟');
INSERT INTO "badkhashani" VALUES(7,99,'7:99','آیا از مكر (تدبير) الله ایمن شده‌اند؟ زیرا از تدبير الله ایمن نمی‌شود مگر قوم زیانکار.');
INSERT INTO "badkhashani" VALUES(7,100,'7:100','آیا برای آنانی که زمین را بعد از (هلاکت) ساکنان آن به ارث می‌برند، سبب هدایت نگردید که اگر ما بخواهیم آنها را به سزای گناهانشان می‌رساندیم؟ (و نیز) بر دل‌هایشان مهر می‌زدیم؟ تا دیگر نشنوند.');
INSERT INTO "badkhashani" VALUES(7,101,'7:101','این قریه را (که داستان هلاک شدن‌شان ذکر شد) بعضی از اخبار آنها را بر تو بیان می‌کنیم، و البته پیغمبرانشان با دلائل واضح نزد آنها آمده بودند، ولی آنان به سبب اینکه (پیغمبران را) پیش از آن، تکذیب کرده بودند، به ایمان آوردن (به آنها) آماده نشدند، این طور الله بر دل‌های کافران مهر می‌زند.');
INSERT INTO "badkhashani" VALUES(7,102,'7:102','و در بیشتر آنها وفای به عهد نیافتیم، و يقينا بیشتر آنها را (عهدشکن و) نافرمان یافتیم.');
INSERT INTO "badkhashani" VALUES(7,103,'7:103','باز موسی را بعد از آن پیغمبران پیشین همراه با آیات (معجزه‌های) خود به‌سوی فرعون و رؤسای قوم او فرستادیم، اما آنان به (مقابل) آیات ما ظلم کردند. پس بنگر که انجام مفسدان چطور شد؟');
INSERT INTO "badkhashani" VALUES(7,104,'7:104','موسی گفت: (در دعوت خود) ای فرعون! يقينا من فرستاده‌ای از سوی پروردگار عالميان‌ام.');
INSERT INTO "badkhashani" VALUES(7,105,'7:105','سزاوار است که دربارۀ الله جز سخن راست و واقعی نگویم. البته من از طرف پروردگارتان برای شما معجزه (و دلیل) روشن آورده ام، پس بنی اسرائیل را (آزاد نموده) همرای من بفرست.');
INSERT INTO "badkhashani" VALUES(7,106,'7:106','(فرعون) گفت: اگر معجزه‌ای (با خود) آورده‌ای، آن را بیاور، اگر از راستگویانی.');
INSERT INTO "badkhashani" VALUES(7,107,'7:107','پس (به طور معجزه) موسی عصای خود را انداخت، پس ناگهان به صورت اژدهای ظاهر شد.');
INSERT INTO "badkhashani" VALUES(7,108,'7:108','و دست خود را (از گریبان) بیرون آورد، پس ناگهان دست او برای بینندگان درخشید.');
INSERT INTO "badkhashani" VALUES(7,109,'7:109','سران قوم فرعون (بعد از دیدن این دو معجزه) گفتند: یقینا این شخص جادو گری دانا است.');
INSERT INTO "badkhashani" VALUES(7,110,'7:110','می‌خواهد شما را از سرزمینتان بیرون کند، پس چه مشوره می‌دهید؟');
INSERT INTO "badkhashani" VALUES(7,111,'7:111','(مشاوران فرعون) گفتند: او (موسی) و برادرش را مهلت بده و نگاه دار، (و کسی را) به شهر‌ها بفرست تا همه مردم را جمع کند.');
INSERT INTO "badkhashani" VALUES(7,112,'7:112','تا هر جادو گر ماهر و دانا را نزد تو بیاورند.');
INSERT INTO "badkhashani" VALUES(7,113,'7:113','و جادوگران نزد فرعون آمدند (و) گفتند: اگر ما غالب شویم آیا برای ما پاداشی (جائزه و انعامی) هست؟');
INSERT INTO "badkhashani" VALUES(7,114,'7:114','(فرعون) گفت: بلی، بلکه يقينا شما از مقربان (دربار من) خواهید بود.');
INSERT INTO "badkhashani" VALUES(7,115,'7:115','(در آغاز مقابله جادو گران) گفتند: ای موسی! یا تو (عصایت را برای مقابله) بانداز، یا ما (سحر خود را) می‌اندازیم.');
INSERT INTO "badkhashani" VALUES(7,116,'7:116','(موسی) گفت: (شما) باندازید، پس وقتی (عصاها و ریسمان‌های خود را) انداختند، چشم‌های مردم را جادو کردند، و آنها را ترساندند، و جادوی بزرگی را پیش کردند.');
INSERT INTO "badkhashani" VALUES(7,117,'7:117','(و در عین حال) به موسی وحی فرستادیم که عصای خود را بانداز (چون انداخت) پس ناگهان جادوهای دروغین‌شان را فرو می‌برد.');
INSERT INTO "badkhashani" VALUES(7,118,'7:118','پس حق (معجزۀ موسی) به ثبوت رسید (روشن شد) و آنچه جادوگران می‌کردند، باطل شد.');
INSERT INTO "badkhashani" VALUES(7,119,'7:119','پس در آنجا مغلوب شدند (شکست خوردند جادوگران و اتباع فرعون)، و خوار و ذلیل برگشتند.');
INSERT INTO "badkhashani" VALUES(7,120,'7:120','جادوگران (بی‌اختیار) به سجده افتیدند.');
INSERT INTO "badkhashani" VALUES(7,121,'7:121','(و) گفتند: به پروردگار عالمیان ایمان آوردیم.');
INSERT INTO "badkhashani" VALUES(7,122,'7:122','پروردگار موسی و هارون (که به عبادت او دعوت می‌دهند).');
INSERT INTO "badkhashani" VALUES(7,123,'7:123','فرعون (جادوگران را عتاب کرده) گفت: آیا پیش از آنکه من به شما اجازه دهم به او ایمان آوردید؟ این حیله‌ای است که شما در بارۀ این شهر سنجیده‌اید تا اهل آن را از آن بیرون کنید. پس به زودی خواهید دانست (که در این شهر چه کسی می‌ماند؟).');
INSERT INTO "badkhashani" VALUES(7,124,'7:124','البته دستها و پای‌های شما را از جانب مخالف خواهم برید، باز همۀ شما را به دار می‌کشم.');
INSERT INTO "badkhashani" VALUES(7,125,'7:125','(جادوگران مؤمن) گفتند: البته ما به طرف پروردگار ما باز می‌گردیم.');
INSERT INTO "badkhashani" VALUES(7,126,'7:126','و تو از ما انتقام نمی‌گیری مگر اینکه چون آیات پروردگار ما آمد )و ما( به آن ایمان آورده‌ایم، ای پروردگار ما! بر ما صبر فرو ریز و ما را مسلمان بمیران.');
INSERT INTO "badkhashani" VALUES(7,127,'7:127','و سران قوم فرعون گفتند: که آیا موسی و قومش را می‌گذاری تا در زمین فساد کنند، و تو و معبودانت را رها کنند؟ (فرعون) گفت: (کجا می‌گذارم) به زودی پسران )و مردان‌) شان را قتل می‌کنیم و زنان آنها را زنده می‌گذاریم، چون ما بر آنها مسلط هستیم.');
INSERT INTO "badkhashani" VALUES(7,128,'7:128','موسی به قوم خود گفت: از الله کمک بخواهید و صبر (و مقاومت) کنید، (چون) زمین از الله است، هر که از بندگان خود را که بخواهد، وارث آن می‌گرداند، و انجام (نیک) از پرهیزگاران است.');
INSERT INTO "badkhashani" VALUES(7,129,'7:129','(بنی اسرائیل به موسی) گفتند: پیش از آنکه نزد ما بیایی و (هم) بعد از آنکه نزد ما آمدی اذیت شدیم، (پس چه باید کرد؟) موسی گفت: امید است که پروردگارتان دشمن شما را نابود کند و شما را در زمین (مصر) جانشین آنان سازد و ببیند که چگونه عمل می‌کنید؟');
INSERT INTO "badkhashani" VALUES(7,130,'7:130','و ما فرعونیان را با قحط سالی و کمبود میوه‌ها گرفتار کردیم تا پند گیرند. (و شاید به‌سوی الله برگردند).');
INSERT INTO "badkhashani" VALUES(7,131,'7:131','(ليكن عبرت نگرفتند) پس وقتی نیکیی به آنها دست می‌داد می‌گفتند: سزاوار ماست و چون بدیی به آنها می‌رسید، به موسی و همراهانش بدفالی می‌گرفتند. آگاه باشید که بدفالی (خوشبختی و بدبختی) آنها نزد الله است، لیکن بیشترینشان نمی‌دانند.');
INSERT INTO "badkhashani" VALUES(7,132,'7:132','و گفتند (ای موسی)! هر چه از معجزه (ها و دلایل) برای ما بیاوری تا ما را با آن جادو کنی، به تو ایمان نمی‌آوریم.');
INSERT INTO "badkhashani" VALUES(7,133,'7:133','پس ما بر آنها طوفان و (هجوم) ملخ و شپش و کوربقه‌ها و خون (روان) را بصورت آیات (و علامات) جدا جدا، فرستادیم. اما باز تكبر ورزیدند و قوم مجرم بودند.');
INSERT INTO "badkhashani" VALUES(7,134,'7:134','و هرگاه که عذاب بر آنها واقع شد، گفتند: ای موسی! با (توسل با) آن عهدی که الله نزد تو دارد، برای ما به پروردگارت دعا کن (که این عذاب را از ما بردارد) اگر عذاب را از ما دور کنی حتما به تو ایمان می‌آوریم، و (به گفتۀ تو) حتما بنی اسرائیل را همراه تو می‌فرستیم.');
INSERT INTO "badkhashani" VALUES(7,135,'7:135','پس چون تا آن وقت معین که قرار گذاشته بودند، عذاب را از آنها دور نمودیم، ناگهان پیمان خود را می‌شکستند.');
INSERT INTO "badkhashani" VALUES(7,136,'7:136','لذا از آنها انتقام گرفتیم پس آنها را در دریا غرق نمودیم به سبب این که آیات ما را دروغ شمردند، و از آنها (عقوبت و قهر الله) غافل بودند.');
INSERT INTO "badkhashani" VALUES(7,137,'7:137','و آن قوم مستضعف (بنی اسرائیل) را در مشارق و مغارب سرزمینی که در آن برکت نهادیم (سرزمین شام) وارث خزانه و سلطنت گردانیدیم. و سخن نیک (وعدۀ نیک) که پروردگار تو به بنی اسرائیل داده بود به سبب اینکه صبر ورزیده بودند، به کمال رسید (تحقق یافت). و آنچه را که فرعون و قومش می‌ساختند و آنچه از باغها را که بر پایه‌ها افراشته بودند، نابود کردیم.');
INSERT INTO "badkhashani" VALUES(7,138,'7:138','و بنی اسرائیل را از بحر گذراندیم، تا اینکه (در راه) بر قومی رسیدند که به عبادت بت‌های خویش مشغول بودند، گفتند: (بنی اسرائیل) ای موسی! برای ما هم معبودی مقرر کن همان طور که آنان را معبودانی هست، گفت: یقینا شما قوم نادان و جاهل هستید.');
INSERT INTO "badkhashani" VALUES(7,139,'7:139','چون آنچه که این مردم در آن قرار دارند، نابود و هلاک شده است، و عملی که می‌کنند باطل است.');
INSERT INTO "badkhashani" VALUES(7,140,'7:140','(موسی) گفت: آیا غیر از الله معبودی برای شما بخواهم؟ در حالیکه او شما را بر جهانیان (زمان تان) فضیلت داده است.');
INSERT INTO "badkhashani" VALUES(7,141,'7:141','و یادآور شوید هنگامی را که شما را از پیروان فرعون نجات دادیم که شما را سخت عذاب می‌کردند؛ پسران تان را قتل می‌کردند و زنان تان را زنده می‌گذاشتند، و در این برای شما از طرف پروردگارتان آزمایش بزرگ بود.');
INSERT INTO "badkhashani" VALUES(7,142,'7:142','و سی شب با موسی وعده گذاشتیم و آن را با ده شب دیگر تکمیل کردیم، تا وقت مقرر پروردگارش چهل شب کامل شد. و (در وقت رفتن به کوه طور) موسی به برادرش هارون گفت: در میان قوم من جانشین (نائب) من باش و در اصلاح (شان) بکوش و از راه مفسدان پیروی مکن.');
INSERT INTO "badkhashani" VALUES(7,143,'7:143','و چون موسی به وعده گاه ما آمد و پروردگارش با او سخن گفت، (موسی) گفت: ای پروردگارم! خودت را به من نشان ده تا تو را ببینم. (الله) فرمود: مرا هرگز نمی‌توان دید (توان دیدن مرا نداری). بلکه به کوه نگاه کن، اگر در جایش برقرار ماند تو هم مرا خواهی دید. وقتی پروردگارش به کوه تجلی کرد، آن کوه ریز ریز شد و موسی بیهوش به زمین افتید، باز وقتی به هوش آمد، گفت: پاکی ‌ترا است (ای الله)، به دربار تو توبه کردم و من اولین مؤمنان هستم.');
INSERT INTO "badkhashani" VALUES(7,144,'7:144','(الله) فرمود: ای موسی، البته من تو را به (رسانیدن) پیام‌ها و کلام (کتاب) خویش بر مردم برگزیده‌ام، پس چیزی را که من به تو داده ام )از وحی و کتاب( بگیر و از شکرگزاران باش.');
INSERT INTO "badkhashani" VALUES(7,145,'7:145','و در لوحه‌های تورات برای او (موسی) از هر چیزی نوشتیم تا عبرت و یادآوری، و تفصيل هرچیزی (در شرع موسوی) باشد، برای موسی نوشتیم، پس (به موسی گفتیم:) آن را با قدرت و عزم قوی بگیر، و به قومت دستور بده تا نیکوترین آن را اختیار کنند (که سبب هدایت و رحمت است)، به زودی سرای فاسقان را به شما نشان خواهم داد.');
INSERT INTO "badkhashani" VALUES(7,146,'7:146','به زودی کسانی را که به ناحق در زمین تکبر می‌کنند، از آیات خویش رویگردان می‌سازم، و اگر هر آیتی را ببینند ایمان نمی‌آورند و اگر راه هدایت را ببینند آنرا راه خود قرار نمی‌دهند و اگر راه گمراهی را ببینند آنرا راه خود قرار می‌دهند. زیرا آنها آیات ما را دروغ شمردند و از آن غافل بودند.');
INSERT INTO "badkhashani" VALUES(7,147,'7:147','و آنانی که آیات ما و ملاقات روز آخرت را دروغ شمردند، اعمال‌شان برباد شد، آیا جز در برابر کارهایی که می‌کردند، سزا داده می‌شوند؟!');
INSERT INTO "badkhashani" VALUES(7,148,'7:148','و قوم موسی بعد از او (رفتن او به کوه طور) از زیورات خود گوساله‌ای که آواز گاو داشت (ساختند و به عبادت) گرفتند. آیا نمی‌دیدند که آن پیکر (گوساله) با آنان سخن نمی‌گوید و آنان را به راهی رهنمایی نمی‌کند؟ باز هم آن را معبود قرار دادند؟ و واقعا که ظالم بودند.');
INSERT INTO "badkhashani" VALUES(7,149,'7:149','و چون ارزش گوساله از نظر‌شان سقوط کرد از آن کار پشیمان شدند و دیدند که گمراه شده‌اند، گفتند: اگر پروردگار ما به ما رحم نکند و ما را نیامرزد، البته از زیان کاران خواهیم بود.');
INSERT INTO "badkhashani" VALUES(7,150,'7:150','و چون موسی (از کوه طور) به طرف قوم خود بازگشت (و گوساله پرستی آنها را دید) در حالت خشم و افسوس گفت: پس از من بد جانشینانی برایم بودید، آیا در (مخالفت) امر پروردگارتان عجله کردید؟ و لوحه‌های تورات را انداخت و سر برادرش را گرفت و آن را به‌سوی خود کشید. (هارون) گفت: ای پسر مادرم! این قوم مرا ناتوان شمردند و تحقیرم نمودند، (بلکه) نزدیک بود مرا بکشند، پس دشمنان را به من شاد مگردان و مرا با قوم ظالم قرار مده.');
INSERT INTO "badkhashani" VALUES(7,151,'7:151','(وقتی خشم موسی فرو نشست) گفت: ای پروردگارم! من و برادرم را ببخش، و ما را در سایۀ رحمت خود داخل كن، و تو مهربان‌ترین مهربانانی.');
INSERT INTO "badkhashani" VALUES(7,152,'7:152','يقينا آنانی که گوساله را معبود خود قرار دادند قهری بزرگ از جانب پروردگارشان، و خواری در زندگی دنیا آنان را خواهد رسید. و این چنین افتراکنندگان را سزا می‌دهیم.');
INSERT INTO "badkhashani" VALUES(7,153,'7:153','و آنانی که کارهای بد انجام دادند (باز) پس از آن توبه کردند (و) ایمان آوردند (به وحدانیت الله) يقينا پروردگار تو آمرزنده (و) مهربان است.');
INSERT INTO "badkhashani" VALUES(7,154,'7:154','و چون خشم موسی فرو نشست، لوحه‌های تورات را گرفت، و در نوشته‌های آن راهنمائی و رحمت بود برای آنانی که ایشان از پروردگار‌شان بیم دارند.');
INSERT INTO "badkhashani" VALUES(7,155,'7:155','موسی برای وعده گاه ما از میان قوم خود هفتاد تن را انتخاب کرد. چون زلزله آنها را فرو گرفت، گفت: ای پروردگارم، اگر می‌خواستی آنها و مرا پیش از این هلاک می‌کردی. آیا به خاطر اعمالی که بیخردان ما انجام داده‌اند، ما را هلاک می‌کنی؟ و این (عقوبت) جز آزمایش تو نیست، هر کس را که بخواهی به آن گمراه می‌کنی و هر کس را که بخواهی هدایت می‌کنی. توئی مددگار ما، پس ما را بیامرز و بر ما رحم کن که تو بهترین آمرزندگانی.');
INSERT INTO "badkhashani" VALUES(7,156,'7:156','و برای ما در این دنیا نیکی بنویس و در آخرت (نیز نیکی بنویس)، البته ما به‌سوی تو رجوع نمودیم. (الله) فرمود: عذاب خود را به کسی که بخواهم می‌رسانم و رحمت من هر چیزی را در بر گرفته است، و آن را برای کسانی مقرر می‌دارم که پرهیز گاری می‌کنند و زکات را می‌دهند و به آیات ما ایمان می‌آورند.');
INSERT INTO "badkhashani" VALUES(7,157,'7:157','و کسانی که از پیغمبر ناخوان پیروی می‌کنند، پیغمبری که (اوصاف و سیرت) او را نزد خود در تورات و انجیل نوشته می‌یابند، آنان را به کارهای نیک امر می‌کند و از کارهای بد باز می‌دارد، چیز‌های پاکیزه را برایشان حلال می‌کند و چیزهای پلید را بر آنان حرام می‌نماید. و بار گران را از آنها بر می‌دارد و بند و زنجیره‌هایی را که بر آنان بود دور می‌کند، پس کسانی که به او ایمان آوردند و او را تعظیم نمودند و او را مدد کردند و از نوری که همراه وی فرو فرستاده شده است پیروی کردند، این گروه رستگار (و کامیاب)‌اند.');
INSERT INTO "badkhashani" VALUES(7,158,'7:158','(ای پیغمبر!) بگو: ای مردم (جهان)، من فرستادۀ الله به‌سوی همۀ شما هستم، آن پروردگاری که سلطنت آسمان‌ها و زمین خاص از اوست. و هیچ معبودی برحق جز او نیست، زنده می‌کند و می‌میراند. پس به الله و رسول عالیقدر او، که ناخوان است و به الله و سخن‌های او ایمان دارد، ایمان بیاورید و از او پیروی کنید، تا هدایت شوید.');
INSERT INTO "badkhashani" VALUES(7,159,'7:159','و از قوم موسی جماعتی هستند که )دیگران را( به حق راهنمائی می‌کنند، و به آن فيصلۀ عادلانه می‌کنند.');
INSERT INTO "badkhashani" VALUES(7,160,'7:160','و آنها را (بنی اسرائیل را) به دوازده سبط (طائفه و قبیله) تقسیم کردیم که هر یک امتی بودند، و به موسی وحی فرستادیم وقتی که قومش از او آب خواستند، که با عصایت به سنگ بزن، پس (از زدن عصا) از آن سنگ دوازده چشمه جاری شد که هر گروه جای آب نوشی و آب گیری خود را دانست، و ابر سفید را بر بالای آنها سایه بان ساختیم، و بر آنها من (شیرنی آسمانی) و سلوا (غذایی از پرنده‌ها) را نازل کردیم (و به آنان گفتیم) از چیزهای پاکیزه‌ای که به شما روزی داده‌ایم بخورید، (و آنها به سبب مخالفت امر الله) بر ما ظلم نکردند، بلکه بر خودشان ظلم می‌کردند.');
INSERT INTO "badkhashani" VALUES(7,161,'7:161','و یاد کن وقتی به آنان گفته شد که در این شهر (بیت المقدس ) سکونت کنید و در آن از هر جایی که بخواهید بخورید. و بگویید: گناهان ما را بیامرز. و سجده کنان از دروازه شهر داخل شوید، تا گناهانتان را بیامرزیم (بلکه) به زودی نیکوکاران را اجر بیشتر می‌دهیم.');
INSERT INTO "badkhashani" VALUES(7,162,'7:162','پس ظالمان آنها سخنی که به آنها گفته شده بود (فرمودۀ الله را) به سخن دیگری تبدیل نمودند، پس به سزای اینکه ظلم می‌کردند، عذابی از آسمان بر آنها فرستادیم.');
INSERT INTO "badkhashani" VALUES(7,163,'7:163','و بپرس از آنان (یهود) دربارۀ قریه‌ای که در کنار دریا بود، وقتی که در )روز( شنبه تجاوز می‌کردند، هنگامی که ماهی‌ها در روز شنبه به روی آب ظاهر می‌شدند، و روزهای غیر شنبه به سویشان نمی‌آمدند، اینگونه آنان را به سبب فسقی که می‌کردند، می‌آزمودیم.');
INSERT INTO "badkhashani" VALUES(7,164,'7:164','و چون جماعتی از ایشان (صالحان يهود) گفتند: چرا قومی را پند می‌دهید که الله، هلاک‌شان خواهد کرد، یا آنها را عذاب سخت خواهد داد؟ گفتند: تا پیش پروردگارتان معذور باشیم و شاید آنها پرهیزگار شوند.');
INSERT INTO "badkhashani" VALUES(7,165,'7:165','پس چون پندی را که به آنها داده شده بود فراموش کردند، آنانی را که از بدی منع می‌کردند نجات دادیم و ظالمان را به سبب نافرمانی‌شان به عذاب سخت گرفتار کردیم.');
INSERT INTO "badkhashani" VALUES(7,166,'7:166','پس وقتی از چیزی که از آن منع شده بودند سرکشی کردند، به آنها گفتیم: بوزینه‌های حقیر خوار شوید.');
INSERT INTO "badkhashani" VALUES(7,167,'7:167','و یاد آور شو وقتی را که پروردگار تو اعلام کرد که کسی را بر آنان بفرستد که تا روز قیامت به آنها عذاب سخت بچشاند. یقینا پروردگار تو زود سزا می‌دهد و نیز آمرزنده (و) مهربان است.');
INSERT INTO "badkhashani" VALUES(7,168,'7:168','و یهودی‌ها را در روی زمین به صورت گروه‌های مختلف متفرق نمودیم که برخی از آنها صالحان‌اند، و بعضی از آنها کمتر از آنها ‌اند، و آنها را به خوبی‌ها و بدی‌ها آزمودیم تا به دربار الله رجوع نمایند.');
INSERT INTO "badkhashani" VALUES(7,169,'7:169','بعد از آنها جانشینان بد آمدند و وارث آن کتاب گردیدند که متاع دنیوی پست را می‌گیرند و می‌گویند: به زودی آمرزیده می‌شویم. و اگر متاعی مانند آن بیابند باز هم آن را می‌گیرند آیا در کتاب (تورات) از آنها پیمان گرفته نشد که در بارۀ الله نگویند مگر سخن حق، حال آنکه آنچه در آن کتاب آمده بود خوانده بودند. و سرای آخرت برای کسانی که پرهیز گاری پیشه می‌کنند (به مراتب) بهتر است، آیا (پس از این بیان) تعقل (درک) نمی‌کنید؟');
INSERT INTO "badkhashani" VALUES(7,170,'7:170','و آنانی که به کتاب (قرآن) تمسک می‌کنند و نماز را برپا داشته‌اند (مستحق اجر ‌اند و بدانند که) ما ثواب اصلاح گران را ضائع نمی‌کنیم.');
INSERT INTO "badkhashani" VALUES(7,171,'7:171','و به یاد آور وقتی که کوه را بر سرشان چون سایبانی قرار دادیم و پنداشتند که اکنون، بر سرشان خواهد افتاد، (در آن وقت به ایشان گفتیم) کتابی را که به شما داده‌ایم با عزم و نیرومندی بگیرید و هر چه را که در آن آمده است یاد کنید، تا پرهیزگار شوید.');
INSERT INTO "badkhashani" VALUES(7,172,'7:172','و به یاد آور وقتی که پروردگار تو از پشت فرزندان آدم فرزندانشان را بیرون آورد، و آنان را بر خودشان گواه گرفت که آیا من پروردگار شما نیستم؟ گفتند: بلی! گواهی میدهیم. تا روز قیامت نگویید: ما از این (ربوبیت تو) غافل بودیم.');
INSERT INTO "badkhashani" VALUES(7,173,'7:173','یا نگویید که پدران ما پیش از این مشرک بودند و ما نسلی بودیم بعد از آنها (که ما به تقلید آنها رفتیم). آیا به سبب کاری که اهل باطل کرده است، ما را هلاک می‌کنی؟');
INSERT INTO "badkhashani" VALUES(7,174,'7:174','و ما این گونه آیات را به تفصیل بیان می‌کنیم (تا بفهمند) و (از کفر به فطرت پاک) باز گردند.');
INSERT INTO "badkhashani" VALUES(7,175,'7:175','و برای آنان خبر آن کس را بخوان که (علم) آیات خود را به او داده بودیم، پس از آن (از عمل کردن به آن) بیرون رفت، و شیطان او را تعقیب کرد (و بر او غالب شد)، و او از جملۀ گمراهان شد.');
INSERT INTO "badkhashani" VALUES(7,176,'7:176','و اگر ما می‌خواستیم مقام او را به سبب علم به آن آیات بالا می‌بردیم، ولی او به زمین متمایل شد و از هوی و هوس خود پیروی کرد. پس حالت او مانند سگ است، اگر او را به رفتن مجبور کنی زبان از دهان بیرون می‌آورد، و اگر آن را رها کنی باز زبان از دهان بیرون می‌آورد. این است حالت قومی که آیات ما را دروغ شمردند. پس داستان تکذیب کنندگان را حکایت کن تا تفکر نمایند (و از تکذیب باز آیند).');
INSERT INTO "badkhashani" VALUES(7,177,'7:177','بد است مثل مردمی که آیات ما را دروغ شمردند و (لكن) به خود ظلم می‌کردند.');
INSERT INTO "badkhashani" VALUES(7,178,'7:178','الله هرکس را که هدایت کند او راه یاب است، و هر کس را که گمراه سازد پس بی‌گمان ایشان زیانکارانند.');
INSERT INTO "badkhashani" VALUES(7,179,'7:179','البته برای دوزخ بسیاری از جن‌ها و انسانها را آفریدیم که (علامت آنها این است:) ایشان را دل‌هایی است که با آن (حق را) نمی‌فهمند و چشم‌هایی است که با آن نمی‌بینند و گوش‌هایی است که با آن (سخن حق را) نمی‌شنوند اینها مانند چهارپایانند، بلکه گمراه‌تر از آنها. ایشان غافلانند.');
INSERT INTO "badkhashani" VALUES(7,180,'7:180','و تنها الله دارای زیباترین و نیکوترین نام‌ها است، پس او را به آن نام‌ها بخوانید، و بگذارید آنانی را که در نام‌های الله کج روی می‌کنند (و آن را تحریف می‌کنند)، به زودی سزای آنچه را که می‌کردند، خواهند دید.');
INSERT INTO "badkhashani" VALUES(7,181,'7:181','(همه را برای دوزخ نه آفریده‌ایم بلکه) از آنانی که ما آفریده‌ایم جماعتی هستند که مردم را به حق راهنمایی می‌کنند، و به آن عادلانه حکم می‌کنند.');
INSERT INTO "badkhashani" VALUES(7,182,'7:182','و آنانی که آیات ما را دروغ شمردند به تدریج آنها را از جایی که ندانند، گرفتار خواهیم کرد.');
INSERT INTO "badkhashani" VALUES(7,183,'7:183','(اما) اکنون آنها را مهلت میدهم، چون تدبیر من استوار و متین است.');
INSERT INTO "badkhashani" VALUES(7,184,'7:184','آیا فکر نکردند که در همنشین‌شان هیچگونه دیوانگی نیست. و او بیم دهنده‌ای آشکار است.');
INSERT INTO "badkhashani" VALUES(7,185,'7:185','آیا در ملکوت آسمان‌ها و زمین و چیزهای دیگری که الله آفریده است (عقلمندانه) نظر نکردند؟ و شاید که مرگشان (یا مهلت عذاب آنها) نزدیک باشد. و پس از (انکار) آن به کدام سخن باور می‌کنند؟');
INSERT INTO "badkhashani" VALUES(7,186,'7:186','هر کس را که الله گمراه کند پس برای او هیچ هدایت کننده‌ای نیست، و آنها را در سر کشی‌شان حیران می‌گذارد.');
INSERT INTO "badkhashani" VALUES(7,187,'7:187','از تو دربارۀ قیامت می‌پرسند که چه وقت رخ می‌دهد؟ بگو: جز این نیست که علم آن نزد پروردگار من است، کسی جز او نمی‌تواند وقتش را ظاهر کند. (این واقعه) بر (اهل) آسمان‌ها و زمین سخت و دشوار است، نمی‌آید (قیامت) به سراغ تان مگر ناگهانی باز از تو دربارۀ قیامت می‌پرسند، گویا تو از آن آگاه هستی، بگو: جز این نیست که علم آن نزد الله است، و لیکن بیشتر مردم نمی‌دانند.');
INSERT INTO "badkhashani" VALUES(7,188,'7:188','بگو: (دانستن وقت قیامت چه) بلکه من مالک (جلب) نفع و (دفع) ضرری برای خود نیستم مگر چیزی را که الله (دربارۀ من) بخواهد، و اگر من غيب را می‌دانستم منافع زیادی (به نفع خود) می‌افزودم، و هیچ ضرری به من نمی‌رسید (بلکه) من نیستم مگر ترساننده و مژده دهنده برای قومی که ایمان می‌آورند.');
INSERT INTO "badkhashani" VALUES(7,189,'7:189','الله آن ذاتی است که شما را از یک شخص آفرید، و از تن او زنش را (نیز) آفرید تا به او آرامش یابد. پس (نظام تولد و تناسل آغاز شد) چون مرد با زن خود همبستر شد (آن زن) به بار سبک باردار شد (حامل شد) پس وقتی که بار وی سنگین شد (و به ولادت نزدیک شد) پس زن و شوهر (هردو) دعا کردند و از پروردگار خود خواستند که اگر به ما فرزند نیک (و سالمی) دهی، ما حتما از شکرگزاران خواهیم شد.');
INSERT INTO "badkhashani" VALUES(7,190,'7:190','پس وقتی (الله) به آن دو فرزند نیک داد، آنان در آنچه که به ایشان عطا نموده بود، برای الله شریکانی قرار دادند، پس الله برتر است از آنچه با وی شریک مقرر می‌کنند.');
INSERT INTO "badkhashani" VALUES(7,191,'7:191','آیا چیزی را شریک الله قرار می‌دهند که چیزی نمی‌آفرینند. در حالیکه خود‌شان نیز مخلوق‌اند (و به آفریننده محتاج اند).');
INSERT INTO "badkhashani" VALUES(7,192,'7:192','و (آن معبودها) نمی‌توانند یاریشان کنند و حتی خودشان را هم نه می‌توانند یاری کنند.');
INSERT INTO "badkhashani" VALUES(7,193,'7:193','و اگر آن معبودها را به هدایت طلب کنید از شما متابعت نکنند. (پس) بر شما برابر است که آنها را (به حق) بخوانید یا خاموش باشید.');
INSERT INTO "badkhashani" VALUES(7,194,'7:194','البته آنانی را که به جز الله (به عبادت) می‌خوانید، بندگانی مانند شما‌اند. آنها را بخوانید تا (دعای) شما را اجابت کنند، اگر (در دعوای خویش) راستگویید.');
INSERT INTO "badkhashani" VALUES(7,195,'7:195','(بلکه معبودهای تان با شما هیچ مشابهتی ندارند) آیا پاهایی دارند که با آن راه بروند؟ یا دست‌هایی دارند که با آن کاری را انجام دهند؟ آیا چشم‌هایی دارند که با آن ببینند؟ یا گوش‌هایی دارند که با آن بشنوند؟ بگو: شريكان الله را بخوانید، باز دربارۀ من حيله سازی و چاره جویی کنید و باز مرا مهلت ندهید.');
INSERT INTO "badkhashani" VALUES(7,196,'7:196','همانا یار و کارساز من الله است، پروردگاری که کتاب را نازل کرده است. و او صالحان را یاری می‌کند.');
INSERT INTO "badkhashani" VALUES(7,197,'7:197','و آنانی را که به جای الله (برای مدد خود) می‌خوانید، نمی‌توانند شما را یاری کنند و نه خود را یاری می‌کنند.');
INSERT INTO "badkhashani" VALUES(7,198,'7:198','و اگر آنان را به‌سوی (راه) هدایت بخوانید نمی‌شنوند، و آنان را می‌بینی که به‌سوی تو می‌نگرند در حالیکه چیزی را نمی‌بینند.');
INSERT INTO "badkhashani" VALUES(7,199,'7:199','(با وجود بی‌ادبی مشرکین، ادب دعوت را رعایت کن و) عفو را پیشه کن و به نیکی فرمان ده و از جاهلان اعراض کن.');
INSERT INTO "badkhashani" VALUES(7,200,'7:200','و اگر از جانب شیطان وسوسه‌ای به تو رسد، پس به الله پناه بجوی، همانا او شنواى داناست.');
INSERT INTO "badkhashani" VALUES(7,201,'7:201','کسانی که پرهیزگاری می‌کنند چون از شیطان وسوسه‌ای به آنها برسد، الله را یاد می‌کنند، آنگاه بینا می‌گردند (که آن وسوسه شیطانی است).');
INSERT INTO "badkhashani" VALUES(7,202,'7:202','و برادرانشان (شیطان صفتان گمراه) آنها (مشرکین) را در گمراهی‌شان مدد می‌کنند، باز کوتاهی نمی‌کنند.');
INSERT INTO "badkhashani" VALUES(7,203,'7:203','چون معجزه‌ای برایشان نیاوری، گویند: چرا تو معجزه‌ای انتخاب نکردی؟ بگو: من پیرو چیزی هستم که از پروردگارم به من وحی می‌شود. و این (قرآن) بصيرت‌هایی است از جانب پروردگارتان، و هدایت و رحمت است برای مردمی که ایمان می‌آورند.');
INSERT INTO "badkhashani" VALUES(7,204,'7:204','و هرگاه قرآن خوانده می‌شود، به آن گوش دهید و خاموش باشید (تا بشنوید) باشد که بر شما رحم شود.');
INSERT INTO "badkhashani" VALUES(7,205,'7:205','و پروردگارت را در دل خود به عاجزی و ترس، بی‌آنکه آوازت را بلند کنی، در وقت صباح و شامگاه یاد کن و از غافلان مباش.');
INSERT INTO "badkhashani" VALUES(7,206,'7:206','(زیرا) آنانی که نزد پروردگارت هستند از عبادت او تكبر نمی‌ورزند، و او را به پاکی یاد می‌کنند و برای او سجده می‌کنند.');
INSERT INTO "badkhashani" VALUES(8,1,'8:1','از تو دربارۀ مال‌های غنیمت می‌پرسند. بگو: مال‌های غنیمت متعلق به الله و پیغمبر است، پس از الله بترسید و در میان خویش صلح و صفا بیاورید و الله و رسول او را اطاعت کنید اگر شما مؤمن هستید.');
INSERT INTO "badkhashani" VALUES(8,2,'8:2','جز این نیست که مؤمنان کامل آنانی‌اند که هرگاه نام الله (نزد آنها) یاد شود، دل‌هایشان هراسان گردد، و چون آیات الله بر آنها تلاوت شود، ایمان‌شان افزون گردد و بر پروردگارشان توکل می‌کنند.');
INSERT INTO "badkhashani" VALUES(8,3,'8:3','آنانی که نماز را برپا می‌کنند و از آنچه به آنها روزی داده‌ایم انفاق می‌کنند.');
INSERT INTO "badkhashani" VALUES(8,4,'8:4','این جماعت در واقع مؤمنان کامل‌اند. برای آنها درجاتی است نزد پروردگارشان، و آمرزش و رزق نیکو است.');
INSERT INTO "badkhashani" VALUES(8,5,'8:5','همچنان که پروردگارت تو را از خانه ات به حق بیرون کرد، در حالیکه گروهی از مسلمانان (از بیرون شدن) سخت کراهت داشتند (باز راضی شدند).');
INSERT INTO "badkhashani" VALUES(8,6,'8:6','(آن مؤمنان) با تو در بارۀ حق؛ بعد از اینکه (حق بودن آن) واضح شد، مجادله می‌کردند، گویا به‌سوی مرگ سوق داده می‌شدند و ایشان )به آن( می‌نگرند.');
INSERT INTO "badkhashani" VALUES(8,7,'8:7','و یادآور شوید وقتی را که الله به شما یکی از دو طائفه را وعده کرد که (گروه مسلح) برای شما باشد، و شما می‌خواستید که طائفه بی‌سلاح برای شما باشد. در حالیکه الله می‌خواست که حق (دین و غلبۀ مسلمانان) را با سخنان (فرمان‌های) خود ثابت کند و کافران را ریشه کن کند.');
INSERT INTO "badkhashani" VALUES(8,8,'8:8','تا (دین) حق را ثابت کند و باطل (کفر) را نابود سازد، اگرچه مجرمان خوش نداشته باشند.');
INSERT INTO "badkhashani" VALUES(8,9,'8:9','یادآور شوید وقتی که از پروردگارتان یاری خواستید، پس دعای شما را قبول کرد که من شما را با هزار فرشته که پشت سر یکدیگر قرار دارند، مدد می‌کنم.');
INSERT INTO "badkhashani" VALUES(8,10,'8:10','و آن (مدد آسمانی) را الله جز برای بشارت و اینکه دلهایتان به آن مطمئن شود قرار نداد. و نصرت نیست مگر از جانب الله، چون او غالب حکیم است.');
INSERT INTO "badkhashani" VALUES(8,11,'8:11','(و به یاد آورید) وقتی را که خواب خفیف را بر شما مسلط ساخت، تا باعث امنیت و آرامش شما از جانب الله بگردد، و از آسمان آبی بر شما فرستاد تا با آن شما را پاک بگرداند و پلیدی شیطان را از شما دور سازد، و دل‌های شما را ثابت بگرداند، و به وسیلۀ آن قدم‌های شما را ثابت سازد.');
INSERT INTO "badkhashani" VALUES(8,12,'8:12','(و به یاد آورید) وقتی را که پروردگار تو به فرشته‌ها وحی کرد که من با شما ام، پس مؤمنان را ثابت قدم دارید (مدد کنید). به زودی در دل‌های کافران خوف را می‌اندازم، پس بر بالای گردنها بزنید و انگشتانشان را قطع کنید.');
INSERT INTO "badkhashani" VALUES(8,13,'8:13','این (حمایت الهی) به این سبب است که آنها از الله و رسول او مخالفت کردند، و هر کس که با الله و رسول او مخالفت کند، پس یقینا بداند که عقوبت الله سخت است.');
INSERT INTO "badkhashani" VALUES(8,14,'8:14','این است (عذاب دنیوی) پس بچشید آن را، (و بدانید که) برای کافران (در آخرت) دوزخ (آماده) است.');
INSERT INTO "badkhashani" VALUES(8,15,'8:15','ای کسانی که ایمان آورده‌اید، هرگاه با (لشکر) کفار روبرو شدید، پس به آنها پشت نگردانید (و فرار نکنید).');
INSERT INTO "badkhashani" VALUES(8,16,'8:16','و هر کس که در آن روز (به کفار) پشت گرداند، مگر کسی که (قصدش) گوشه گیری برای جنگ (مجدد) یا یاری به گروه (دیگر از مجاهدین) باشد، (در غیر آن) به غضب الله گرفتار می‌شود، و جایگاهش دوزخ است، و چه بدجای بازگشت است (دوزخ)!.');
INSERT INTO "badkhashani" VALUES(8,17,'8:17','(بعد از مدد الله به وسیلۀ فرشته‌ها بدانید که در حقیقت) شما (با نیروی خود) آنان را نکشتید، بلکه الله آنها را (با مدد‌های غیبی خود) به قتل رسانید. (و درحقیقت) تو (مشت ریگ را) بر آنها نه افگندی، بلکه الله افگند، (الله چنین کرد) تا مؤمنان را از طرف خود به آزمایش نیک بیازماید (که نصرت و غنیمت است) زیرا الله شنوا (و) دانا است.');
INSERT INTO "badkhashani" VALUES(8,18,'8:18','این است (آزمایش نیك)، و (بدانید که) الله، سست کننده حيله كافران است.');
INSERT INTO "badkhashani" VALUES(8,19,'8:19','(ای مشرکان!) اگر پیروزی (حق) را می‌طلبید، پس اینک فتح و پیروزی به‌سوی شما آمد، و اگر (از جنگ با مسلمانان) باز آیید برای شما بهتر است، و اگر (به کفر و عنادتان) باز گردید ما هم (به نصرت مسلمانان) باز می‌گردیم و (بدانید) که جماعت تان هرچند عدد آنها زیاد باشد، هرگز نمی‌توانند چیزی را از شما دفع کنند، و الله با مؤمنان است.');
INSERT INTO "badkhashani" VALUES(8,20,'8:20','(شرط نصرت الله این است) ای کسانی که ایمان آورده‌اید، الله و رسول او را اطاعت کنید، و از اطاعت رسول الله اعراض نکنید، در حالیکه امر الله را (در بارۀ اطاعت رسول) می‌شنوید.');
INSERT INTO "badkhashani" VALUES(8,21,'8:21','و مانند آنانی مشوید که گفتند: شنیدیم (سخن رسول را) ولی (در حقیقت) آنها نمی‌شنیدند.');
INSERT INTO "badkhashani" VALUES(8,22,'8:22','زیرا بدترین روندگان (جانوران در روی زمین) نزد الله کران و گنگانی‌اند که (حق را) درک نمی‌کنند.');
INSERT INTO "badkhashani" VALUES(8,23,'8:23','و اگر الله در آنان خیری می‌دانست، آنها را (به شنیدن سخن حق توفیق میداد و) شنوایشان می‌ساخت، و اگر آنان را (در صورت عدم توفيق) شنوا می‌ساخت، البته باز هم روی می‌گردانیدند، در حالیکه اعراض کنان می‌بودند.');
INSERT INTO "badkhashani" VALUES(8,24,'8:24','ای کسانی که ایمان آورده‌اید، چون الله و پیامبرش شما را به چیزی دعوت دهند که زندگی ابدی تان باشد (پس) دعوتشان را اجابت کنید و بدانید که الله میان شخص و (ارادۀ) قلبش حایل می‌شود. و همه به دربار او جمع کرده می‌شوید.');
INSERT INTO "badkhashani" VALUES(8,25,'8:25','و از فتنه‌ای بترسید که تنها به ظالمان تان نمی‌رسد، و بدانید که الله سخت عقوبت دهنده است.');
INSERT INTO "badkhashani" VALUES(8,26,'8:26','و یادآور شوید وقتی را که شما در زمین کم و مستضعف بودید (و) می‌ترسیدید که مردم شما را اختطاف کنند، پس الله به شما جای داد (در مدینه) و شما را به کمک خود تایید کرد (نصرت بخشید)، و از چیزهای پاکیزه به شما روزی داد، تا شکر گزاری کنید.');
INSERT INTO "badkhashani" VALUES(8,27,'8:27','و ای کسانی که ایمان آورده‌اید، به الله و رسول خیانت نکنید، و نه در امانت‌های خویش خیانت کنید، در حالیکه شما (سزای خیانت را) می‌دانید.');
INSERT INTO "badkhashani" VALUES(8,28,'8:28','و بدانید که مالها و فرزندانتان (سبب) آزمایشی (برای شما) است، و البته نزد الله ثواب بزرگ است.');
INSERT INTO "badkhashani" VALUES(8,29,'8:29','ای کسانی که ایمان آورده‌اید، اگر از الله بترسید، به شما قوت فرق کننده میان حق و باطل می‌دهد، و بدیهای شما را محو می‌کند و گناهان شما را می‌آمرزد (و شما را از فضل خود اجر و ثواب می‌دهد، چون) الله صاحب فضل بزرگ است.');
INSERT INTO "badkhashani" VALUES(8,30,'8:30','و یادآور شو زمانی که کافران (مکه) در مورد تو حیله سنجی (و نقشه کشی) می‌کردند، تا تو را زندانی کنند، یا تو را بکشند، یا تو را (از مکه) بیرون کنند، و آنها در مورد تو حیله می‌سنجیدند و الله هم (برای نجات تو) تدبیر می‌کرد و الله بهترین تدبیر کنندگان است.');
INSERT INTO "badkhashani" VALUES(8,31,'8:31','و چون آیات ما بر آنها تلاوت شود، گویند: شنیدیم، و اگر بخواهیم ما هم مانند آن (قرآن) می‌گوییم، این چیزی نیست مگر افسانه‌های مردمان پیشین.');
INSERT INTO "badkhashani" VALUES(8,32,'8:32','و یاد آور شو زمانی که گفتند: پروردگارا! اگر این (دین) حق از نزد تو است، پس از آسمان سنگهایی بر ما بباران، و یا برای ما عذابی دردناک بیار.');
INSERT INTO "badkhashani" VALUES(8,33,'8:33','و (لیكن این وقت رسیدن عذاب نیست چون) تا وقتی که تو در میان آنها هستی، الله عذاب دهندۀ آنها نیست، و الله عذاب دهندۀ آنها نیست در حالیکه از الله مغفرت می‌خواهند.');
INSERT INTO "badkhashani" VALUES(8,34,'8:34','و چه (مانعی) است که الله آنها را عذاب نکند، حال آن که (مؤمنان را) از مسجد الحرام منع می‌کنند، و آنها متولیان مسجد الحرام نیستند، و متولیان آن نیست مگر پرهیزگاران، و لیکن بیشتر آنها (این حقیقت) را نمی‌دانند.');
INSERT INTO "badkhashani" VALUES(8,35,'8:35','و نماز آنها (مشرکین) در نزد کعبه نبود مگر صفیر کشیدن و کف زدن (یعنی به جای نمازخواندن چنین تمسخری می‌کردند)، پس (به آنها گفته شد که) عذاب را به سزای کفرتان بچشید.');
INSERT INTO "badkhashani" VALUES(8,36,'8:36','البته آنانی که کفر ورزیدند مال‌های خود را برای منع نمودن مردم از راه و دين الله انفاق می‌کنند، پس زود است که آن را انفاق کنند، باز (عاقبت) آن انفاق‌شان سبب حسرت برایشان خواهد شد، و باز مغلوب می‌گردند. و آنانی که کفر ورزیدند به‌سوی دوزخ حشر می‌شوند.');
INSERT INTO "badkhashani" VALUES(8,37,'8:37','(این حشر نمودن کار برای این است) تا الله پلیدان (کافران) را از پاکان (مؤمنان) جدا سازد و بعضی پلیدان را بر بعض دیگر قرار دهد، پس آن همه (پلیدان) را توده سازد، باز آن (همه) را در دوزخ بیندازد، این جماعت همان زیانکارانند.');
INSERT INTO "badkhashani" VALUES(8,38,'8:38','به کافران بگو: اگر باز آیند (از کفر شان) آنچه گذشته است برای آنها آمرزیده می‌شود، و اگر باز گردند (به کفر و عداوت مسلمانان)، پس طريقۀ الله با کفار پیشین گذشته است.');
INSERT INTO "badkhashani" VALUES(8,39,'8:39','و (اگر به عداوت شما بازگشتند پس) با آنها بجنگید تا فتنۀ (شرک) باقی نماند، و دین، همه برای الله باشد. پس اگر باز آمدند (از كفر)، يقينا الله به آنچه می‌کنند، بیناست.');
INSERT INTO "badkhashani" VALUES(8,40,'8:40','و اگر روی گشتاندند (از ایمان، پس اندوهگین مشوید. بلکه) بدانید که الله کارساز شماست، و الله بهترین کارساز و بهترین مددگار است (پس با مشرکان بجنگید).');
INSERT INTO "badkhashani" VALUES(8,41,'8:41','و بدانید که هر آنچه را (از کافران) به غنیمت گرفتید، پس پنجم حصه آن از الله و پیغمبر و خویشاوندان (پیغمبر) و یتیمان و مسکینان و مسافران (در راه ماندگان) است، اگر به الله و به آنچه بر بنده خود در روز جدایی حق و باطل نازل کردیم ایمان دارید؛ روزی که آن دو گروه (مؤمن و کافر در روز بدر) روبرو شدند. و الله بر هر چیزی قادر است.');
INSERT INTO "badkhashani" VALUES(8,42,'8:42','یادآور شوید وقتی که شما در جانب نزدیکتر (به مدینه) بودید، و آنان (دشمنانتان) در جانب دورتر بودند و قافلۀ (تجاری آمده از شام) در پائین‌تر از شما (در کنار ساحل) قرار داشت. و اگر با یکدیگر وعدۀ (جنگ) می‌کردید (و ناگهان حکم جنگ صادر نمی‌شد) البته در وعده مخالفت می‌کردید، و لیکن (ناگهانی به شما دستور جنگ داده شد) تا الله کاری را که انجام دادنی بود، به انجام برساند، تا کسی که به دليل واضح هلاک شدنی بود، هلاک شود. و کسی که به دليل واضح زنده ماندنی بود، زنده بماند. و یقینا الله شنوای داناست.');
INSERT INTO "badkhashani" VALUES(8,43,'8:43','قابل یادآوری است وقتی که الله (عدد) آنها را در خوابت به تو کم نشان داد، و اگر آنها را به تو بسیار نشان می‌داد، البته سست می‌شدید و در کار (جنگ با مشرکین) نزاع می‌کردید، و ليكن الله (شما را از اختلاف و بزدلی) در امان داشت. چون الله از راز سینه‌ها آگاه است.');
INSERT INTO "badkhashani" VALUES(8,44,'8:44','و چون وقتی که با هم روبرو شديد (قبل از شروع جنگ)، الله آنها را در چشم شما کم نشان می‌داد، و شما را (نیز) در چشم آنها کم نشان می‌داد تا الله کاری را که شدنی بود (غلبۀ اسلام)، به انجام برساند و تمام کارها به سوى الله باز گردانیده می‌شود.');
INSERT INTO "badkhashani" VALUES(8,45,'8:45','ای کسانی که ایمان آورده‌اید، چون با جماعت (کفار در وقت جنگ) روبرو شديد، پس ثابت قدم باشید و الله را بسیار یاد کنید تا (به نصرت و پاداش الهی دست یابید و) رستگار شوید.');
INSERT INTO "badkhashani" VALUES(8,46,'8:46','و (دیگر اینکه در اثنای جنگ) الله و رسول او را اطاعت کنید و با یکدیگر اختلاف نکنید که سست و بزدل می‌شوید و قوت هیبت شما از بین می‌رود. (بلکه) صبر کنید چونکه (نصرت) الله با صبر کنندگان است.');
INSERT INTO "badkhashani" VALUES(8,47,'8:47','و مانند کسانی نباشید که به تکبر و غرور و برای خودنمایی (به مردم) از دیار خویش بیرون آمدند و (در حالیکه) دیگران را از راه الله باز می‌داشتند. و الله به هر کاری که می‌کنند احاطه دارد.');
INSERT INTO "badkhashani" VALUES(8,48,'8:48','و چون شیطان اعمالشان را به آنها زینت داد و گفت: امروز هیچ کس از مردم (مسلمان) بر شما غلبه کننده نیست و يقينا من پناه‌دهنده (و مددگار) شماام. (لیکن) پس چون هر دو گروه (کافر و مسلمان) با هم روبرو شدند، شیطان بر پاشنه‌های خود بازگشت و گفت: البته من از شما بیزارم، چون من چیزی را می‌بینم که شما نمی‌بینید، يقينا من از الله می‌ترسم. و الله سخت عذاب دهنده است.');
INSERT INTO "badkhashani" VALUES(8,49,'8:49','(شیطان اعمال‌شان را زینت داد آن) وقتی که منافقین و آنانی که در دل‌هایشان بیماری (شک) بود، می‌گفتند: این جماعت (مسلمانان) را دین‌شان فریفته است. و (ليکن به گفته‌های آنها پروا نکنید زیرا) هر کس بر الله توکل کند (الله او را کافیست)، چون الله غالب (و) باحکمت است.');
INSERT INTO "badkhashani" VALUES(8,50,'8:50','و اگر میدیدی (تو ای مخاطب، تعجب می‌کردی) وقتی که فرشته‌ها روح کافران را (در بدر) قبض می‌کردند، در حالیکه بر روی‌ها و پشت‌هایشان می‌زدند و (می‌گفتند:) عذاب سوزان را بچشید.');
INSERT INTO "badkhashani" VALUES(8,51,'8:51','این (عذاب سوزان) به سبب آن کرداری است که دست‌های شما پیش کرده است، و اینکه الله بر بندگان خود ستمگر نیست.');
INSERT INTO "badkhashani" VALUES(8,52,'8:52','مانند عادت فرعونیان و (عادت) آنانی که پیش از آنها بودند که به آيات الله كفر می‌ورزیدند، پس الله آنها را به سبب گناهانشان گرفتار کرد، و یقیناً الله توانا (و) سخت عذاب دهنده است.');
INSERT INTO "badkhashani" VALUES(8,53,'8:53','آن (عذاب سخت) به این سبب بود که الله تبدیل کننده نعمتی نیست که بر قومی بخشیده باشد مگر آن که آنان آنچه را که در دل اراده دارند تبدیل کنند (ارادۀ شکر را به ناشکری، و ارادۀ ایمان را به کفر، و ارادۀ اطاعت را به نافرمانی،) و الله شنوای دانا است.');
INSERT INTO "badkhashani" VALUES(8,54,'8:54','(باز به تاکید گفته می‌شود که) مانند عادت فرعونیان و (عادت) آنانی که پیش از آنها بودند که آیات (دلائل) پروردگار خود را دروغ شمردند، پس آنها را به سبب گناهانشان هلاک کردیم و فرعونیان را غرق نمودیم (چون که) همۀ آنها ظالم بودند.');
INSERT INTO "badkhashani" VALUES(8,55,'8:55','يقينا بدترین روندگان (جانوران روی زمین) نزد الله کسانی‌اند که کفر ورزیدند، پس ایمان نمی‌آرند.');
INSERT INTO "badkhashani" VALUES(8,56,'8:56','همان کسانی که از آنها عهد گرفتی باز عهد خود را در هر بار می‌شکنند و از (الله و عهدشکنی خویش) نمی‌ترسند.');
INSERT INTO "badkhashani" VALUES(8,57,'8:57','پس اگر آنها را در میدان جنگ بیابی، پس با (قتل و مجازات) آنان، کسانی را که پشتیبان آنان هستند، متفرق ساز (و بترسان)؛ تا عبرت گیرند.');
INSERT INTO "badkhashani" VALUES(8,58,'8:58','و اگر از قومی بیم خیانت (عهدشکنی) داری پس به طرف آنها عهدشان را (علنا و) به طور برابر بینداز (شما و آنها برابر بدانید که عهد شکسته است)، زیرا الله خیانت کاران را دوست ندارد.');
INSERT INTO "badkhashani" VALUES(8,59,'8:59','و کفار گمان نبرند که (از عذاب الهی) سبقت کرده‌اند (و جان به سلامت برده‌اند)، چون آنها (الله را) عاجز کرده نمی‌توانند.');
INSERT INTO "badkhashani" VALUES(8,60,'8:60','و (ای مسلمانان!) برای مقابله با كفار هر چه در توان دارید از انواع قوت، آماده سازید و (خصوصا) از اسپهای مهیا شده، تا با آن دشمن الله و دشمن خودتان را بترسانید. و (نیز) دشمنان دیگر غیر از آنها (مشرکین) را که شما آنها را نمی‌شناسید (و) الله آنها را می‌شناسد، و هر چیزی که در راه الله (برای آمادگی جهاد) انفاق می‌کنید، اجر آن به شما کاملا داده خواهد شد، و به شما ظلم نمی‌شود.');
INSERT INTO "badkhashani" VALUES(8,61,'8:61','و اگر کفار به صلح مائل و (آماده) شدند، پس تو نیز به آن میل کن (آماده شو)، و (لیکن) بر الله توکل کن، چون الله شنوای داناست.');
INSERT INTO "badkhashani" VALUES(8,62,'8:62','و اگر کفار بخواهند که (به بهانۀ صلح) تو را فریب دهند (پروا مكن)، زیرا الله تو را بس است. همان (الله) بود که تو را به کمک خود و مؤمنان قوت داد.');
INSERT INTO "badkhashani" VALUES(8,63,'8:63','و هم اوست که در بین دل‌هایشان (مسلمانان) الفت و محبت ایجاد کرد، اگر همه اموال روی زمین را انفاق می‌کردی نمی‌توانستی در بین دل‌های آنها الفت برقرار کنی، و ليكن الله در میان آنها الفت انداخت، يقينا او غالب باحکمت است.');
INSERT INTO "badkhashani" VALUES(8,64,'8:64','ای پیغمبر! الله و مؤمنان پیرو تو؛ تو را کافی است.');
INSERT INTO "badkhashani" VALUES(8,65,'8:65','ای پیغمبر! مؤمنان را بر قتال (کفار و جهاد در راه الله) ترغیب کن، اگر از شما بیست نفر صابر باشد، بر دو صد تن (کفار) غالب می‌آیند، و اگر از شما صد نفر باشد، بر هزار تن از کافران، غالب می‌شوند. زیرا کفار قومی‌اند، نادان.');
INSERT INTO "badkhashani" VALUES(8,66,'8:66','اکنون الله بر شما تخفیف نمود، چون دانست که در شما (به علّت کثرت دشمن) ضعفی است، پس اگر از شما صد نفر صابر باشد بر دو صد کافر غالب می‌آیند، و اگر از شما هزار نفر باشد (پس) بر دو هزار غالب می‌آیند، به اذن الله. و (نصرت) الله با صابران است.');
INSERT INTO "badkhashani" VALUES(8,67,'8:67','برای هیچ پیغمبری سزاوار (نبوده و) نیست که اسیران (جنگی) داشته باشد (و با گرفتن فدیه، آنان را آزاد کند مگر) تا اینکه در زمین قتل بسیار کند. (ای مؤمنان،) شما متاع (عارضی) دنیا را می‌خواهید و الله (برای شما ثواب) آخرت را می‌خواهد، و الله غالب با حکمت است.');
INSERT INTO "badkhashani" VALUES(8,68,'8:68','اگر حکم پیشین (نوشته شدۀ) الله نبود، البته به شما در مقابل فدیه‌ای که گرفتید، عذاب بزرگ می‌رسید.');
INSERT INTO "badkhashani" VALUES(8,69,'8:69','پس (بگذارید فدیه را و) از آنچه به غنیمت گرفته‌اید، حلال و پاکیزه بخورید، و از الله بترسید، همانا الله بسیار آمرزگار (و) مهربان است.');
INSERT INTO "badkhashani" VALUES(8,70,'8:70','ای پیغمبر! به اسیرانی که در دست شما‌اند بگو: اگر الله در دل‌های شما نیکی را معلوم کند البته به شما بهتر از آن فدیه‌ای که از شما گرفته شده می‌دهد، و شما را می‌آمرزد و الله بسیار آمرزگار (و) مهربان است.');
INSERT INTO "badkhashani" VALUES(8,71,'8:71','و اگر بخواهند به تو خیانت کنند، پس (تعجب مکن که) البته پیش از این نیز به الله خیانت کرده بودند، پس الله تو را (به خاطر انتقام گرفتن) بر آنها مسلط گردانید، و الله دانای حکیم است.');
INSERT INTO "badkhashani" VALUES(8,72,'8:72','يقينا آنانی که ایمان آوردند و هجرت کردند و با مالها و جانهای خود در راه الله جهاد کردند، و آنانی که (مهاجرین را) جای دادند و مدد کردند، این جماعت دوستان و خیرخواه یکدیگر‌اند. و آنانی که ایمان آوردند و (لیکن) هجرت نکرده‌اند، شما (هیچ‌گونه) دوستی (و مسؤولیّتی در برابر) آنها ندارید، تا آن که هجرت کنند (از مکه به مدینه). و اگر (همین مسلمانان غیر مهاجر) در کارهای دین از شما مدد طلب کنند، پس بر شماست مدد کردن آنها، مگر اینکه علیه قومی که میان شما و آنها عهد و پیمان باشد، و الله به آن چه می‌کنید بیناست.');
INSERT INTO "badkhashani" VALUES(8,73,'8:73','و آنانی که کفر ورزیدند، دوستان یکدیگر‌اند، (و) اگر (شما مؤمنان به این دستور در بارۀ دوستی با مؤمنان و دشمنی با کافران) عمل نکنید (یقیناً) در زمین فتنه و فساد بزرگی برپا خواهد شد.');
INSERT INTO "badkhashani" VALUES(8,74,'8:74','و آنانی که ایمان آورده‌اند و هجرت کرده‌اند و در راه الله جهاد کرده‌اند، و آنان که جای دادند (مهاجرین را) و مدد کردند. همین جماعت در حقیقت مؤمنان کامل‌اند (که) برایشان آمرزش و روزی عزت مندانه است.');
INSERT INTO "badkhashani" VALUES(8,75,'8:75','و آنانی که بعد (از نزول این آیت) ایمان آوردند و هجرت کردند و همرای شما جهاد کردند، پس این جماعت نیز از جملۀ شما‌اند. و در (حکمِ) كتاب الله، صاحبان قرابت نسبت به یکدیگر (در استحقاق میراث از سایر مؤمنان) نزدیکتراند. يقينا الله به هر چیزی داناست.');
INSERT INTO "badkhashani" VALUES(9,1,'9:1','(این) ابراز بیزاری است از طرف الله و رسول او نسبت به آن مشرکانی که با آنها عهد کرده‌اید.');
INSERT INTO "badkhashani" VALUES(9,2,'9:2','(به ایشان بگو:) پس در زمین (حدود مسلمانان) تا چهار ماه (از شوال تا آخر ماه محرم) بگردید و بدانید که شما الله را عاجز کرده نمی‌توانید (از عقوبت مجرمان)، و اینکه الله رسواکنندۀ کافران است.');
INSERT INTO "badkhashani" VALUES(9,3,'9:3','و اعلان است از طرف الله و رسول او برای مردم )جهان( در روز حج اكبر (حج فرضی) به اینکه الله از مشرکین بیزار است و رسول او (نیز از آنها بیزار است)، پس اگر توبه کنید (از شرک) پس آن برای شما خیر است، و اگر روی بگردانید (و بر شرک باقی بمانید)، پس بدانید که شما الله را عاجز کرده نمی‌توانید، و کافران را به عذاب دردناک مژده بده.');
INSERT INTO "badkhashani" VALUES(9,4,'9:4','مگر آن مشرکانی که با آنها عهد بسته‌اید و باز از آن (تعهدات خود) چیزی را نسبت به شما کم (نقض ) نکردند، و هیچ کس را علیه شما مدد نکردند، پس عهد آنان را تا پایان مدت مقرر‌شان تمام کنید. چون الله متقیان را دوست دارد.');
INSERT INTO "badkhashani" VALUES(9,5,'9:5','پس چون ماه‌های حرام (یعنی مهلت مشرکان) به پایان رسد، پس آن مشرکان را در هر جایی یافتید بکشید، و آنها را (اسیر) بگیرید و آنها را (از داخل شدن در حدود حرم) منع کنید و برای منع آنها در هر کمین گاهی بنشنید، پس اگر (از شرک) توبه کنند و نماز را برپا کنند و زکات بدهند، پس راه را بر آنها باز کنید، (و آنها را از مسجدالحرام و حدود حرم منع نکنید). چون الله آمرزگار (و) مهربان است.');
INSERT INTO "badkhashani" VALUES(9,6,'9:6','و اگر یکی از مشرکان از تو امان (پناه) خواست، پس او را امان ده تا کلام الله را بشنود، باز او را در جای امنش برسان، این (فرمان امان دادن) به سبب آنست که آنها قوم نادان‌اند (تا چیزی از دین ما را بدانند).');
INSERT INTO "badkhashani" VALUES(9,7,'9:7','(بعد از عهدشکنی خود آنها) چگونه برای مشرکین نزد الله و رسول او عهدی بماند؟ به جز آنانی که با آنها کنار مسجد الحرام عهد بسته بودید، پس تا وقتی که با شما (بر عهد و پیمان شان) وفادار باشند، شما نیز به عهدتان با آنان وفادار باشید. بی‌گمان الله پرهیزگاران را دوست دارد.');
INSERT INTO "badkhashani" VALUES(9,8,'9:8','چگونه (عهد و پیمانی داشته باشند) در حالیکه اگر بر شما غالب شوند، دربارۀ شما حق قرابت و عهد را مراعات نمی‌کنند. (بلکه) شما را با زبان‌شان راضی می‌سازند، و (از دوستی شما) دل‌های آنها ابا می‌ورزد، و بیشترین آنها فاسق (نافرمان و عهدشکن)‌اند.');
INSERT INTO "badkhashani" VALUES(9,9,'9:9','آيات الله را به بهای ناچیزی فروختند و (مردم را) از راه او باز داشتند (و خود‌شان نیز از آن اعراض کردند) و یقینا بد است آنچه می‌کنند.');
INSERT INTO "badkhashani" VALUES(9,10,'9:10','دربارۀ هیچ مؤمنی حق قرابت و عهد را رعایت نمی‌کنند، و این جماعت تجاوزکاران‌اند.');
INSERT INTO "badkhashani" VALUES(9,11,'9:11','پس اگر توبه کردند (از شرک) و نماز را برپا داشتند و زکات را دادند، پس آنها برادران شما در دین هستند، ما آیات خود را برای مردمی که می‌دانند، به تفصیل بیان می‌کنیم.');
INSERT INTO "badkhashani" VALUES(9,12,'9:12','ولی اگر (مشرکین) قسمهای خود را بعد از عهد خویش (با شما) شکستند، و به دین تان (دین اسلام) طعنه زدند، پس با پیشوایان کفر بجنگید‌، چرا که قسم‌های آنها قابل اعتبار نیست، تا (از عهدشکنی خود) دست بردارند.');
INSERT INTO "badkhashani" VALUES(9,13,'9:13','چرا نمی‌جنگید با قومی که قسم‌های خود را شکستند، و به بیرون کردن پیغمبر (از مدینه نیز) عزم کردند، و آنها (جنگ و عهدشکنی را) برای بار اول آغاز نمودند؟ آیا از آنها می‌ترسید؟ پس الله سزاوارتر به آن است که از او بترسید، اگر مؤمن هستید.');
INSERT INTO "badkhashani" VALUES(9,14,'9:14','با آنان (کفار عهدشکن) بجنگید الله آنها را به دست شما عذاب می‌دهد، و رسوایشان می‌سازد، و شما را بر آنها پیروز می‌گرداند، و سینه‌های مؤمنان را شفا می‌بخشد.');
INSERT INTO "badkhashani" VALUES(9,15,'9:15','و خشم دل‌های مؤمنان را می‌برد. و الله توبۀ هرکس را که بخواهد می‌پذیرد، و الله دانای باحکمت است.');
INSERT INTO "badkhashani" VALUES(9,16,'9:16','آیا گمان کرده‌اید که (بدون امتحان) گذاشته می‌شوید، در حالیکه تا هنوز الله (اخلاص ) آنانی از شما را که جهاد کرده‌اند، معلوم (ظاهر) نکرده است. و (نیز) کسانی را که غیر از الله و رسول او و مسلمانان دوست پنهانی (همراز) نگرفته‌اند (معلوم نکرده است). و الله به آنچه شما می‌کنید باخبر است.');
INSERT INTO "badkhashani" VALUES(9,17,'9:17','برای مشرکان روا نیست که مساجد الله را آباد کنند، در حالیکه بر کفر خود گواهی می‌دهند. این گروه کسانی‌اند که نیکی‌هایشان برباد شده و آنها در دوزخ برای همیشه‌اند.');
INSERT INTO "badkhashani" VALUES(9,18,'9:18','مساجد الله را تنها کسانی آباد می‌کنند که به الله و روز آخرت (قیامت) ایمان داشته و نماز را برپا کنند و زکات دهند و به جز الله از کسی نمی‌ترسند. پس امید است که این جماعت از راه یافتگان باشند.');
INSERT INTO "badkhashani" VALUES(9,19,'9:19','آیا (شما مشركان) تنها آب دادن حاجیان و آباد نمودن مسجد الحرام را مانند عمل کسی قرار داده‌اید که به الله و روز آخرت ایمان آورده و در راه الله جهاد کرده است؟ این دو عمل نزد الله برابر نیستند و الله قوم ظالم را هدایت نمی‌کند.');
INSERT INTO "badkhashani" VALUES(9,20,'9:20','(بلکه) آنانی که ایمان آورده و هجرت کرده‌اند و در راه الله با مالها و جانهای‌شان جهاد کرده‌اند، نزد الله مقام والاتر دارند، و این جماعت رستگاران‌اند.');
INSERT INTO "badkhashani" VALUES(9,21,'9:21','پروردگارشان آنها را به رحمتی از طرف خود و خشنودی و به باغ‌های که در آنها نعمت‌های دائمی دارند، مژده می‌دهد.');
INSERT INTO "badkhashani" VALUES(9,22,'9:22','همیشه‌اند در آن باغها تا ابد، يقينا نزد الله (برای نیکان) ثواب عظیم است.');
INSERT INTO "badkhashani" VALUES(9,23,'9:23','ای کسانی که ایمان آورده‌اید! اگر پدران و برادران شما کفر را بر ایمان ترجیح دادند، پس آنها را به دوستی نگیرید، و هر کسی که از شما با آنان (با پدران و برادران کافر خود) دوستی کند، پس آنان همان ظالمان‌اند.');
INSERT INTO "badkhashani" VALUES(9,24,'9:24','بگو: اگر پدران تان و پسرانتان و برادران تان و زنهایتان و خویشاوندانتان، و مال‌های که کسب کرده‌اید و تجارتی که از بی‌رواجی (و بی‌بازاری) آن می‌ترسید و عمارت‌های که آنرا می‌پسندید، نزد شما از الله و رسول او و از جهاد در راه الله محبوب‌تر است، پس انتظار کنید تا آن که الله حکمش (عقوبتش) را بیاورد. و الله مردم فاسق را هدایت نمی‌کند.');
INSERT INTO "badkhashani" VALUES(9,25,'9:25','البته الله شما را در مواضع بسیاری مدد کرد و (خصوصا) در روز (جنگ) حنين، وقتی که بسیاری تعداد تان شما را فریفته ساخته بود (لیکن) آن بسیاری تعداد چیزی را از شما دفع نکرد (بلکه) زمین با وجود فراخی آن بر شما تنگ گردید، باز در حالیکه پشت به دشمن گردانیده بودید، پا به فرار نهادید.');
INSERT INTO "badkhashani" VALUES(9,26,'9:26','و باز الله آرامش و اطمینان خود را بر رسولش و مؤمنان نازل کرد و (از آسمان) لشکرهای را فرستاد که شما نمی‌دیدید و کافران را عذاب داد. و این است سزای کافران.');
INSERT INTO "badkhashani" VALUES(9,27,'9:27','باز الله بعد از این، توبۀ هر کس را که بخواهد قبول می‌کند و الله آمرزگار (و) مهربان است.');
INSERT INTO "badkhashani" VALUES(9,28,'9:28','ای کسانی که ایمان آورده‌اید! جز این نیست که مشرکها پلیداند، پس نباید به مسجدالحرام نزدیک شوند بعد از این سال شان، و اگر از فقر (به سبب نا آمدن مشرکان) بترسید، پس الله به زودی شما را از فضل خود غنی می‌سازد، اگر بخواهد. چون الله دانای باحکمت است.');
INSERT INTO "badkhashani" VALUES(9,29,'9:29','(ای مسلمانان!) با آنانی (از اهل کتاب) بجنگید که به الله و روز آخرت ایمان نمی‌آورند، و چیزی را که الله و رسول او حرام قرار داده‌اند، حرام نمی‌دانند و از دین حق اطاعت نمی‌کنند، حال آنکه از کسانی‌اند که به آنها کتاب آسمانی داده شده است، (پس بجنگید با آنها) تا آن که (ایمان آورند یا) جزیه را به دست خود با كمال حقارت بدهند.');
INSERT INTO "badkhashani" VALUES(9,30,'9:30','(از دلایل ایمان نیاوردن‌شان این است که) یهودی‌ها گفتند: عُزیر پسر الله است. و نصارا گفتند: مسیح (عیسی بن مریم) پسر الله است. این است سخن آنها (که) در دهانهایشان (وجود دارد) به سخن آنانی که از پیش کفر ورزیده بودند مشابهت دارد. الله آنها را هلاک کند (پس ) چگونه (با وجود دلایل واضح) باز گردانیده می‌شوند؟');
INSERT INTO "badkhashani" VALUES(9,31,'9:31','(سبب گمراهی‌شان این بود که) علماء و گوشه نشینان (پیران) خود را به جای الله معبودهای خود گردانیدند، و مسیح پسر مریم را (نیز) معبود خود قرار دادند. حال آنکه امر نشده بودند مگر این که معبود یگانه را عبادت کنند که هیچ معبودی برحق غیر از او نیست. و پاک است او از آنچه با او شریک می‌سازند.');
INSERT INTO "badkhashani" VALUES(9,32,'9:32','می‌خواهند نور الله (چراغ توحید) را با دهانهایشان (با سخنهای دور از حقیقت) خاموش کنند و الله ابا می‌ورزد مگر اینکه نورش را کامل کند، اگر چه کافران دوست نداشته باشند.');
INSERT INTO "badkhashani" VALUES(9,33,'9:33','او (الله) همان ذاتیست که پیغمبر خود را با (اصول) هدایت و دین حق فرستاد، تا آن را بر تمامی ادیان عالم غالب سازد، اگر چه مشرکان خوش نداشته باشند.');
INSERT INTO "badkhashani" VALUES(9,34,'9:34','ای کسانی که ایمان آورده‌اید، البته بسیاری از علمای (یهود) و گوشه نشینان (نصارا) مال‌های مردم را به ناحق می‌خورند، و مردم را از راه (و دین) الله منع می‌کنند. و آنانی که طلا و نقره (مال دنیا) را ذخیره می‌کنند و آن را در راه الله انفاق نمی‌کنند، پس آنها را به عذاب دردناک مژده بده.');
INSERT INTO "badkhashani" VALUES(9,35,'9:35','روزی که آن گنجینه‌ها (طلا و نقره) در آتش دوزخ گداخته شود، پس به وسیلۀ آن پیشانی و پهلو و پشت‌های آنها داغ کرده شود )و گفته شود که( این است آنچه برای خود ذخیره کرده بودید، پس آنچه را ذخیره می‌کردید، بچشید.');
INSERT INTO "badkhashani" VALUES(9,36,'9:36','يقينا شمار ماهها نزد الله در کتابش از روزی که آسمان‌ها و زمین را آفریده است، دوازده ماه است، از آنجمله چهار ماه حرام (قابل احترام و بزرگداشت) است. دین راست و درست همین است، پس در آنها (چهار ماه حرام؛ به وسیلۀ جنگ) به خویش ظلم مکنید. و با مشرکان بجنگید، قسمی که آنها همه (به همکاری یکدیگر) با شما می‌جنگند، و بدانید که الله با پرهیزگاران است.');
INSERT INTO "badkhashani" VALUES(9,37,'9:37','جز این نیست که تأخیر نمودن ماهای حرام و بی‌جا کردن آن، فزونی در کفر است که کافران به سبب آن گمراه ساخته می‌شوند، چون که (جنگ در) آن را، یک سال حلال می‌شمارند، و یک سال دیگر آن را حرام قرار می‌دهند، تا شمارۀ ماه‌های که الله )جنگ را در آن( حرام قرار داده است موافق سازند، پس )در نتیجه( چیزی را که الله حرام گردانیده است، بر خود حلال گردانند. (در حقیقت) اعمال بدشان برای آنها مزين شده است. و الله قوم کافر را هدایت نمی‌کند.');
INSERT INTO "badkhashani" VALUES(9,38,'9:38','ای کسانی که ایمان آورده‌اید، شما را چه شده است که چون به شما گفته می‌شود در راه الله بیرون شوید، گران شده به زمین میل می‌کنید (تنبلی می‌کنید)؟ آیا به جای آخرت به زندگی دنیا راضی شده‌اید؟ پس متاع زندگی دنیا در برابر آخرت، اندکی بیش نیست.');
INSERT INTO "badkhashani" VALUES(9,39,'9:39','اگر بیرون نشوید (در راه الله) شما را عذاب دردناک می‌دهد، و به جای شما قوم دیگری را می‌آورد، و به او هیچ ضرری رسانده نمی‌توانید، و الله بر هرچیز قادر است.');
INSERT INTO "badkhashani" VALUES(9,40,'9:40','اگر او (پیغمبر) را یاری نکنید (هیچ پروایی نیست) چون او را الله یاری کرد، آن وقتی که او را کافران (از مکه( بیرون کردند در حالیکه او دوم آن دو تن بود، آن وقت که هر دو در غار (ثور) بودند، همان وقتی که به یار خود (ابوبکر صدیق) می‌گفت: غمگین مشو، چون الله با ماست. پس الله آرامش و اطمینان خود را بر پیغمبر نازل کرد و او را به لشکری که آنها را ندیدید کمک کرد، و کلمۀ کافران را پست گردانيد و كلمۀ الله بلند است، و الله غالب (و) باحکمت است.');
INSERT INTO "badkhashani" VALUES(9,41,'9:41','بیرون شوید (برای جهاد) در حالت سبکی و گرانی تان (بانشاط و بی‌نشاط)، و با مالها و جانهای خود در راه الله جهاد کنید، این برای شما خیر است، اگر بدانید.');
INSERT INTO "badkhashani" VALUES(9,42,'9:42','اگر منفعتی نزدیک و سفری آسان بود، حتما از تو پیروی می‌کردند، ولی مسافت طولانی راه برای آنان دشوار است. به الله سوگند می‌خورند که اگر می‌توانستیم با شما بیرون می‌شدیم، خود را هلاک می‌کنند و الله می‌داند که آنها دروغگو‌اند.');
INSERT INTO "badkhashani" VALUES(9,43,'9:43','(اگر چه) الله تو را معاف کرده است. (لیکن) چرا پیش از آنکه راستگویان بر تو معلوم گردند و دروغگویان را بشناسی، به آنها اجازه (نشست از جهاد را) دادی؟');
INSERT INTO "badkhashani" VALUES(9,44,'9:44','آنانی که به الله و روز آخرت ایمان دارند، دربارۀ اینکه با مالها و جانهای خود جهاد کنند، از تو اجازه نمی‌طلبند. و الله به احوال (همچون) پرهیزگاران داناست.');
INSERT INTO "badkhashani" VALUES(9,45,'9:45','تنها آنانی از تو اجازه می‌خواهند )تا به جهاد نروند( که (از زیر دل) به الله و روز آخرت ایمان ندارند، و دل‌هایشان از شک پر شده است، لذا آنها در شک خود (مبتلا بوده) سرگردان‌اند.');
INSERT INTO "badkhashani" VALUES(9,46,'9:46','و اگر آنها (منافقان) به راستی ارادۀ بیرون شدن (برای جهاد( را داشتند البته برای آن سامان و وسایل جنگ را آماده می‌کردند، و لیکن الله روان شدن آنها را (به جهاد) خوش نداشت پس آنان را از حرکت (با شما) منصرف گردانید و (گویا به آنها) گفته شد: بنشینید (در خانه‌های تان) با نشستگان (ضعیفان و اطفال).');
INSERT INTO "badkhashani" VALUES(9,47,'9:47','(الله آنها را از بیرون رفتن با شما منع کرد که) اگر آنان با شما بیرون می‌آمدند چیزی جز فساد (و اختلاف) به شما نمی‌افزودند، و البته با سرعت در میان شما به فساد انگیزی می‌پرداختند و در حق شما فتنه جویی می‌کردند، در حالیکه در میان شما کسانی هستند که سخن آنها را می‌شنوند. و الله به احوال ظالمان داناست.');
INSERT INTO "badkhashani" VALUES(9,48,'9:48','(دلیل فساد طلبی‌شان این است که) البته پیش از این فتنه را (در غزوۀ تبوک نیز) طلب کرده بودند و بر ضد تو نیرنگها و چاره اندیشی‌ها کردند، تا آن که حق (غلبۀ مسلمانان) آمد و امر (دین) الله آشکار شد، در حالیکه (منافقین) آن را خوش نداشتند.');
INSERT INTO "badkhashani" VALUES(9,49,'9:49','و از آنها (منافقین) کسی هست که می‌گوید: (به پیغمبر) مرا اجازه ده (به تخلف از جهاد) و مرا در فتنه مینداز، آگاه باش که همین اکنون در فتنه افتیده‌اند و یقینا دوزخ احاطه کننده کافران است.');
INSERT INTO "badkhashani" VALUES(9,50,'9:50','اگر به تو خیری برسد آنها را می‌رنجاند، و اگر به تو مصیبتی برسد، می‌گویند: البته ما از پیش احتياط خود را گرفتیم و (از نزد تو) شادمان بر می‌گردند.');
INSERT INTO "badkhashani" VALUES(9,51,'9:51','بگو: هرگز به ما نمی‌رسد مگر آنچه الله برای ما نوشته است، او کارساز ماست، و باید که مؤمنان به الله توکل کنند.');
INSERT INTO "badkhashani" VALUES(9,52,'9:52','بگو: آیا دربارۀ ما جز یکی از این دو خوبی را انتظار دارید ( پیروزی یا شهادت را)؟ در حالی که ما در بارۀ شما انتظار می‌کشیم که الله از نزد خود و یا به دستان ما به شما عذابی برساند. پس انتظار بکشید و ما هم با شما در انتظاریم.');
INSERT INTO "badkhashani" VALUES(9,53,'9:53','بگو: شما (منافقان) چه با خوشی (مال تان را در جهاد) انفاق کنید چه با کراهت، (در هردو صورت) هرگز از شما پذیرفته نمی‌شود، زیرا شما قوم فاسق بودید.');
INSERT INTO "badkhashani" VALUES(9,54,'9:54','و هیچ چیز مانع پذیرفته شدن انفاقهای‌شان نشد جز اینکه (در دل) به الله و رسول او کافر شده‌اند، و به نماز نمی‌آیند مگر در حال کسالت و انفاق نمی‌کنند (مال خود را) مگر با کراهت.');
INSERT INTO "badkhashani" VALUES(9,55,'9:55','پس مالها و اولاد آنها تو را در تعجب نیاندازد، چون الله می‌خواهد آنها را به وسیلۀ اینها (مال و اولاد) در زندگی دنیا عذاب دهد، و جانشان در حالی بیرون آید که کافر باشند.');
INSERT INTO "badkhashani" VALUES(9,56,'9:56','به الله قسم می‌خورند که آنها از شما (مسلمانان)‌اند، و حال آنکه از شما نیستند، و ليكن آنها قومی هستند که می‌ترسند (و تقیه می‌کنند).');
INSERT INTO "badkhashani" VALUES(9,57,'9:57','اگر پناهگاهی بیابند، یا غارهایی، یا سوراخی را، البته با سرعت به طرف آن روی می‌آورند (تا از شما مخفی شوند).');
INSERT INTO "badkhashani" VALUES(9,58,'9:58','و بعضی از آنها (منافقین) دربارۀ (تقسیم) صدقات بر تو عیب و ایراد می‌گیرند، پس اگر به آنها از آن داده شود، راضی می‌شوند، و اگر از آن داده نشوند، ناگهان ناراض (و خشمگین) می‌شوند.');
INSERT INTO "badkhashani" VALUES(9,59,'9:59','و اگر آنها به آنچه که الله و رسول او به آنها داده‌اند راضی می‌شدند، و می‌گفتند: الله ما را بس است، به زودی الله از فضلش و رسول او به ما می‌دهند، البته ما به سوى الله مشتاق و راغبیم (این برای‌شان بهتر بود).');
INSERT INTO "badkhashani" VALUES(9,60,'9:60','صدقه‌ها تنها برای فقیران و مسکین‌ها و وظیفه داران (و کارکنان) جمع آوری صدقات و برای آنانی که دل‌هایشان به طرف اسلام رغبت داده می‌شود و در راه آزادی بردگان (از غلامی) و دینداران و در راه الله (مجاهدان) و برای مسافران (بازماندگان در سفر) اختصاص دارد، فریضه‌ای از طرف الله است. و الله دانا و باحکمت است.');
INSERT INTO "badkhashani" VALUES(9,61,'9:61','و از منافقان کسانی‌اند که پیغمبر را آزار می‌دهند و می‌گویند: او سراپا گوش (خوش باور) است. بگو: (این) سراپا گوش بودن (او) خیری برای شماست (زیرا از شما درمی‌گذرد، و چونکه) به الله ایمان دارد و (سخن) مؤمنان را تصدیق می‌کند و برای آنانی از شما که ایمان آورده‌اند، رحمتی است. و آنانی که رسول الله را می‌آزارند، عذاب دردناک در پیش دارند.');
INSERT INTO "badkhashani" VALUES(9,62,'9:62','(آن منافقان) در نزد شما به الله قسم می‌خورند تا شما را راضی کنند، حال آنکه سزاوارتر این است که الله و رسولش را از خود راضی کنند، اگر مؤمن هستند.');
INSERT INTO "badkhashani" VALUES(9,63,'9:63','آیا (منافقان) ندانستند که هر کس با الله و رسول او مخالفت کند بی‌گمان برای او آتش دوزخ است که همیشه در آن می‌ماند، و این رسوایی بزرگ است.');
INSERT INTO "badkhashani" VALUES(9,64,'9:64','منافقان (به طور تمسخر آمیز) اظهار ترس می‌کنند از اینکه سوره‌ای دربارۀ آنان نازل شود که آنها را از آنچه که در دل‌هایشان است آگاه سازد، بگو: تمسخر کنید، يقينا الله آنچه را که از آن می‌ترسید، آشکار می‌سازد.');
INSERT INTO "badkhashani" VALUES(9,65,'9:65','و اگر از آنها بپرسی (چرا مسلمانان را مورد تمسخر قرار می‌دهید؟) البته می‌گویند: ما شوخی و بازی می‌کردیم، بگو: آیا به الله و آیات او و رسولش تمسخر می‌کردید؟');
INSERT INTO "badkhashani" VALUES(9,66,'9:66','(اکنون) عذر پیش نکنید، چون شما بعد از (اظهار) ایمان تان کافر شده‌اید، اگر گروهی از شما را عفو کنیم، گروهی دیگر را به سبب آنکه (توبه نکردند و) گناهکار بودند، عذاب میدهیم.');
INSERT INTO "badkhashani" VALUES(9,67,'9:67','مردان و زنان منافق (در نفاق و بی‌دینی مانند) یکدیگراند (که) به گناه امر و از نیکی نهی می‌کنند و دست‌های خود را (از انفاق کردن در راه الله) می‌بندند. الله را فراموش کردند، پس الله نیز آنها را (از رحمت خود) فراموش (محروم) کرد، واقعا که منافقان فاسق و نافرمان‌اند.');
INSERT INTO "badkhashani" VALUES(9,68,'9:68','(لذا) الله برای مردان و زنان منافق و کافران، دوزخ را وعده داده است که در آن همیشه‌اند، همان (دوزخ) برایشان کافی است. الله آنها را از رحمت خود دور نموده است، و برای آنها عذاب همیشگی است.');
INSERT INTO "badkhashani" VALUES(9,69,'9:69','(حال شما منافقان) مانند آنانی است که پیش از شما بودند، (بلکه) آنان از شما قوی‌تر و ثروتمندتر و دارای اولاد بیشتر بودند، پس از حصه خویش (چند روز) نفع بردند، و شما هم از حصه خود نفع بردید، قسمی که آنانی که پیش از شما بودند از حصه خود نفع بردند، و شما (در لذت‌های دنیا و تكذيب ما) فرو رفتید طوریکه (مردمان سابق) فرو رفتند. این گروه اعمال‌شان در دنیا و آخرت برباد شد، و این گروه زیانکارانند.');
INSERT INTO "badkhashani" VALUES(9,70,'9:70','آیا به آنها (مشرکین و منافقین) خبر آنانی که پیش از ایشان بودند نیامده است؟ (یعنی) قوم نوح و قوم عاد و قوم ثمود و قوم ابراهيم و باشندگان مدین و قریه‌های زیر و زبر شده که پیغمبرانشان دلائل واضح و روشن برایشان آوردند (ليكن كفر ورزیدند و به سبب آن بر ایشان عذاب آمد) پس الله هرگز به آنها ظلم نکرده بود، ولی آنها بر خود ظلم می‌کردند.');
INSERT INTO "badkhashani" VALUES(9,71,'9:71','مردان و زنان مؤمن دوستان یکدیگرند (که) به نیکی امر می‌کنند و از بدی منع می‌کنند، و نماز را برپا می‌کنند و زکات را می‌پردازند، و الله و رسول او را اطاعت می‌کنند، این گروه‌اند که الله آنها را بزودی مورد رحمت خویش قرار خواهد داد، چون الله غالب (و) باحکمت است.');
INSERT INTO "badkhashani" VALUES(9,72,'9:72','الله به مردان و زنان مؤمن باغ‌های را وعده کرده است که از زیر (درختان و قصر‌های) آن نهرها جاری است، در آن همیشه‌اند، و (نیز) قصرهای پاکیزه در بهشت‌های دائمی را (وعده کرده است)، و خشنودی الله از همه چیز بزرگ‌تر است. (در حقیقت) این است کامیابی بزرگ.');
INSERT INTO "badkhashani" VALUES(9,73,'9:73','ای پیغمبر! با کفار و منافقین جهاد کن و بر آنها شدت کن (این عذاب دنیا است و در آخرت) جایگاهشان دوزخ است، و چه بد جایگاهی است (دوزخ).');
INSERT INTO "badkhashani" VALUES(9,74,'9:74','(منافقان) به الله قسم می‌خورند که (سخن کفر) نگفته‌اند، (در حالیکه) بدون شک سخن کفر را گفتند، و پس از (اظهار) اسلامشان کافر گشته‌اند و عزم انجام کاری را نمودند که آنرا حاصل کرده نمی‌توانند، و به اعتراض نپرداختند مگر به سبب آن که الله و پیغمبرش آنان را از فضل خود بی‌نیاز ساخت. پس اگر (از نفاق) توبه کنند برایشان بهتر است، و اگر روی بگردانند الله در دنیا و آخرت آنان را به عذابی دردناک مبتلا می‌کند و آنان در روی زمین هیچ کارساز و مددکاری نخواهند داشت.');
INSERT INTO "badkhashani" VALUES(9,75,'9:75','و از آنان (منافقین) کسانی هستند که با الله عهد کردند که اگر از فضل خود به ما (مال و ثروت) دهد، حتما صدقه می‌دهیم و از نیکان خواهیم شد.');
INSERT INTO "badkhashani" VALUES(9,76,'9:76','پس چون (الله) از فضل خود به آنها داد، به آن بخل ورزيدند و اعراض کنان برگشتند.');
INSERT INTO "badkhashani" VALUES(9,77,'9:77','پس در عقب عهدشکنی‌شان (و به سزای آن، الله) نفاق را در دل‌هایشان باقی گذاشت تا روزی که با او ملاقات کنند، به سبب اینکه در چیزی که با او وعده کرده بودند وعدہ خلافی کردند و به سبب اینکه دروغ می‌گفتند.');
INSERT INTO "badkhashani" VALUES(9,78,'9:78','آیا ندانستند که الله رازهای پوشیده و سخنهای پنهانی آنها را می‌داند، و اینکه الله دانای غیبهاست.');
INSERT INTO "badkhashani" VALUES(9,79,'9:79','سانی که از مؤمنان داوطلب در صدقات عیبجویی می‌کنند، و از مردمانی که جز به اندازه توان‌شان چیزی (برای انفاق در راه الله) نمی‌یابند، عیبجویی می‌کنند و آنان را مسخره می‌کنند، الله ایشان را مورد تمسخر قرار می‌دهد و آنها را عذابی دردناک است.');
INSERT INTO "badkhashani" VALUES(9,80,'9:80','(لذا ای پیغمبر)، چه برای آنها آمرزش بخواهی چه آمرزش نخواهی (برابر است، چون) اگر هفتاد بار برای آنها مغفرت بخواهي الله هرگز آنها را نمی‌آمرزد، این (نیامرزیدن) به سبب آن است که آنها به الله و رسولش كفر ورزیدند و الله مردم فاسق (و کافر) را هدایت نمی‌کند.');
INSERT INTO "badkhashani" VALUES(9,81,'9:81','تخلف کنندگان از جهاد (غزوۀ تبوک) به نشستن خود (در خانه‌هایشان) بر خلاف رسول الله شادمان شدند، و خوش نداشتند که با مالها و جانهایشان در راه الله جهاد کنند، و گفتند: (به پیروان خود) در گرما (به جهاد) بیرون مشويد، بگو: آتش جهنم بسیار گرمتر و سوزانتر است اگر می‌دانستند.');
INSERT INTO "badkhashani" VALUES(9,82,'9:82','پس باید کم بخندند و بسیار گریه کنند، این جزای کارهایی است که می‌کردند.');
INSERT INTO "badkhashani" VALUES(9,83,'9:83','پس اگر الله تو را (از غزوۀ تبوک) به‌سوی طائفۀ از منافقین باز گرداند پس ایشان از تو برای بیرون شدن به جهاد اجازه خواستند، بگو: هرگز با من برای جهاد بیرون نخواهید شد، و هرگز همراه من با هیچ دشمنی نخواهید جنگید، چون شما اول بار به نشستن راضی شدید پس اکنون هم با خانه نشینان بنشینید.');
INSERT INTO "badkhashani" VALUES(9,84,'9:84','و هرگز بر هیچ یکی از آنها که بمیرد، نماز (جنازه) مخوان و بر قبر هیچ یکی از آنها مایست، چون ایشان به الله و رسول او کافر شدند و در حال فسق (و بی‌ایمانی) مردند.');
INSERT INTO "badkhashani" VALUES(9,85,'9:85','و اموال و اولاد آنان تو را (ای مخاطب)، در تعجب نه اندازد، چون الله می‌خواهد که آنها را به وسیلۀ آن در دنیا عذاب دهد، و جانشان در حال کفر بیرون رود.');
INSERT INTO "badkhashani" VALUES(9,86,'9:86','و چون سوره‌ای نازل شود که به الله ایمان آورید و همراه پیغمبرش جهاد کنید، ثروتمندان ایشان از تو اجازه می‌خواهند و می‌گویند: بگذار تا با خانه نشینان باشیم.');
INSERT INTO "badkhashani" VALUES(9,87,'9:87','(به جهاد نرفتند بلکه) راضی شدند که همرای تخلف کنندگان باشند، و بر دل‌هایشان مهر نهاده شده است، پس آنها نمی‌فهمند.');
INSERT INTO "badkhashani" VALUES(9,88,'9:88','ليكن پیغمبر و مؤمنانی که همرای وی‌اند، با مالها و جانهای خود جهاد کردند، و اینها برایشان همۀ نعمت‌ها است، و اینها کامیاب‌اند.');
INSERT INTO "badkhashani" VALUES(9,89,'9:89','الله برایشان باغهایی آماده کرده است که از زیر آنها نهرها می‌رود، در آن همیشه‌اند، این همان پیروزی بزرگ است.');
INSERT INTO "badkhashani" VALUES(9,90,'9:90','و عذرخواهان بادیه نشین (نزد تو) آمدند تا برایشان اجازه داده شود (به تخلف از جهاد). و آنانی که به الله و رسول او دروغ گفتند، در خانه نشستند. زود است که به کافران اعراب عذاب دردناک برسد.');
INSERT INTO "badkhashani" VALUES(9,91,'9:91','(در نشستن از جهاد) بر ضعیفان گناهی نیست و بر بیماران گناهی نیست و بر آنانی که چیزی نمی‌یابند تا انفاق کنند (در جهاد) گناهی نیست، به شرطی که برای الله و رسول او خیر خواهی کنند. (چون) بر نیکی کنند گان هیچ راهی (عتاب و عقاب) نیست. و الله آمرزندۀ مهربان است.');
INSERT INTO "badkhashani" VALUES(9,92,'9:92','و همچنین گناهی نیست بر کسانی که چون پیش تو آمدند تا آنان را بر مرکبی سوار کنی، گفتی: چیزی نمی‌یابم که شما را بر آن سوار کنم، و ایشان برگشتند در حالیکه چشمانشان از شدت اندوه پر از اشک بود از غم اینکه چیزی نمی‌یابند [در راه جهاد] انفاق کنند.');
INSERT INTO "badkhashani" VALUES(9,93,'9:93','البته راه مؤاخذه بر کسانی است که از تو اجازه می‌خواهند درحالیکه ثروتمند هستند. و راضی شدند به اینکه با خانه نشینان باشند، و الله بر دل‌هایشان مُهر نهاده است، پس آنها نمی‌دانند.');
INSERT INTO "badkhashani" VALUES(9,94,'9:94','چون باز گردید (از غزوه تبوک) به‌سوی آنها (منافقین) نزد شما عذر خواهی می‌کنند. بگو: عذر خواهی نکنید که هرگز سخن شما را باور نمی‌کنیم (چون) الله ما را از سخن‌های شما خبر داده است و الله و پیغمبرش عمل شما را خواهند دید، باز به‌سوی ذاتی که از پنهان و آشکار آگاه است باز گردانیده می‌شوید و شما را به آنچه انجام می‌دادید آگاه می‌سازد.');
INSERT INTO "badkhashani" VALUES(9,95,'9:95','چون به‌سوی آنان برگردید برای شما به الله قسم خواهند خورد تا از (جرم) آنان صرف نظر کنید، پس از آنها روی بگردانید، چون آنها پلیدند و به سزای آنچه می‌کردند، جایگاهشان دوزخ است.');
INSERT INTO "badkhashani" VALUES(9,96,'9:96','(منافقین) برای شما قسم میخورند تا از آنها راضی شوید، پس اگر شما (هم) از آنان راضی شوید الله از قوم نافرمان راضی نمی‌شود.');
INSERT INTO "badkhashani" VALUES(9,97,'9:97','(منافقان) اعراب (صحرانشینان) در کفر و نفاق سخت‌تر‌اند، و سزاوارتراند به اینکه شریعتی را که الله بر پیغمبر خود نازل کرده، ندانند، و الله دانا (و) حکیم است.');
INSERT INTO "badkhashani" VALUES(9,98,'9:98','و از اعراب (صحرانشینان) کسانی هستند که آنچه را انفاق می‌کنند تاوان می‌شمارند، و برای شما پیش آمد بد و ناگوار را انتظار می‌کنند، حال آنکه پیشامد بد بر خود آنها خواهد بود، و الله شنوای داناست.');
INSERT INTO "badkhashani" VALUES(9,99,'9:99','و از اعراب (صحرانشینان) کسانی هستند که به الله و روز آخرت ایمان دارند و آنچه را که در راه الله انفاق می‌کنند باعث تقرب به الله و وسیله‌ای برای دعاهای پیغمبر [در حق خویش] می‌دانند. آگاه باشید! بی‌گمان آن (انفاق مال) برایشان مایۀ تقرب نزد الله است. به زودی الله آنها را در جوار رحمت خود (جنت) داخل می‌کند. چون الله آمرزگار مهربان است.');
INSERT INTO "badkhashani" VALUES(9,100,'9:100','و سبقت کنندگان نخستین (در هجرت و نصرت) از مهاجرین و انصار و آنانی که با نیکوکاری از آنها (صحابه) پیروی کرده‌اند، الله از آنها راضی شده است و آنها از او راضی شده‌اند، و برای آنها باغهایی آماده کرده است که از زیر آن نهرها جاری است، همیشه در آن جاودانند، این است همان پیروزی بزرگ.');
INSERT INTO "badkhashani" VALUES(9,101,'9:101','و (بعضی) از صحرانشینان که در اطراف شما هستند و بعضی از اهل مدینه منافق‌اند که بر نفاق مهارت پیدا نموده‌اند، و تو (ای پیغمبر) آنها را نمی‌شناسی، (بلکه) ما آنها را می‌شناسیم، به زودی آنها را (در دنیا) دو مرتبه عذاب خواهیم داد، باز به طرف عذاب بزرگ (و سخت در آخرت) گردانیده می‌شوند.');
INSERT INTO "badkhashani" VALUES(9,102,'9:102','و دیگرانی‌اند که به گناهان خود اعتراف نموده‌اند (چون) عمل نیک را با عمل دیگر که بد است خلط نموده‌اند، امید است که الله توبۀ آنها را بپذیرد، چون الله آمرزنده (و) مهربان است.');
INSERT INTO "badkhashani" VALUES(9,103,'9:103','(ای پیغمبر!) از مال‌های آنها (مسلمانان) صدقه بگیر تا آنها را با آن پاک سازی، و برای آنها دعا کن، زیرا دعای تو سبب آرامش آنها است، و الله شنوای داناست.');
INSERT INTO "badkhashani" VALUES(9,104,'9:104','آیا نداسته‌اند که تنها الله توبه را از بندگانش می‌پذیرد، و زكات و صدقه را قبول می‌کند، و الله توبه پذیر (و) مهربان است؟');
INSERT INTO "badkhashani" VALUES(9,105,'9:105','و بگو: عمل (نیک ) کنید پس به زودی الله اعمال شما را می‌بیند، و همچنین پیغمبرش و مؤمنان اعمال شما را می‌بینند، و به‌سوی دانای پنهان و آشکار بازگردانیده خواهید شد، باز شما را به آنچه می‌کردید آگاه می‌سازد.');
INSERT INTO "badkhashani" VALUES(9,106,'9:106','و دیگرانی هستند که به امر الله واگذار شده‌اند، یا آنها را عذاب می‌دهد و یا توبه‌شان را قبول می‌کند، و الله دانای باحکمت است.');
INSERT INTO "badkhashani" VALUES(9,107,'9:107','و آنانی که به منظور زیان رساندن و (ایجاد و اشاعه) کفر و تفرقه اندازی بین مؤمنان مسجد ساختند تا کمینگاهی برای کسانی که پیش از این با الله و پیغمبرش جنگیده بودند باشد، و قسم می‌خورند که جز نیکی منظوری نداشته‌ایم، اما الله گواهی می‌دهد که آنها دروغگو هستند.');
INSERT INTO "badkhashani" VALUES(9,108,'9:108','هرگز در آن نایست (و نماز مگزار)، البته مسجدی که از روز اول بر اساس تقوا بنیان گذاری شده سزاوارتر است که در آن بایستی (و نماز بگزاری)، در آنجا مردانی‌اند که دوست دارند خود را پاکیزه دارند و الله پاکیزگان را دوست می‌دارد.');
INSERT INTO "badkhashani" VALUES(9,109,'9:109','آیا کسی که بنیاد خود را بر تقوای الله و خشنودی او بنیان نهاده است بهتر می‌باشد یا کسی که بنیاد (کار) خود را بر کنارۀ وادی نزدیک به سقوط بنیان گذاری کرده است و با آن در آتش جهنم می‌افتد؟ و الله قوم ظالم را هدایت نمی‌کند.');
INSERT INTO "badkhashani" VALUES(9,110,'9:110','همیشه آن عمارتی که (بر اساس نفاق) بنا کرده‌اند، در دل‌هایشان سبب شک و پریشانی است، مگر آن که دل‌هایشان پاره پاره شود، (واقعا که) الله دانای حکیم است.');
INSERT INTO "badkhashani" VALUES(9,111,'9:111','بی‌گمان الله از مؤمنان جانهایشان و اموالشان را به عوض آنکه بهشت برای آنان باشد خریده است. در راه الله می‌جنگند، پس می‌کشند (کفار را) و کشته می‌شوند، این وعده‌ای است که در تورات و انجیل و قرآن بر او (الله، مقرر) است، و چه کسی از الله به وعده خود وفا کننده‌تر است؟! پس (ای کسانی که ایمان آورده‌اید) به معامله‌ای که کرده‌اید خوش باشید و این همان پیروزی بزرگ است.');
INSERT INTO "badkhashani" VALUES(9,112,'9:112','آنان (مؤمنان فداکار) توبه کنندگان‌اند، عبادت کنندگان‌اند، حمد گویان‌اند، سفر کنندگان‌اند (در راه الله) رکوع و سجده کنندگان‌اند، به نیکی امرکنندگان‌اند، و از بدی منع کنندگان‌اند، و نگاه کنندگان حدود الله‌اند، و مؤمنان را (به این انجام نیک) مژده بده.');
INSERT INTO "badkhashani" VALUES(9,113,'9:113','پیغمبر و مؤمنان را روا نیست که برای مشرکان آمرزش بخواهند هر چند که خویشاوند باشند، پس از آنکه برایشان روشن شد که آنان اهل دوزخ‌اند.');
INSERT INTO "badkhashani" VALUES(9,114,'9:114','و طلب آمرزش ابراهیم برای پدرش نبود مگر به سبب وعده‌ای که به او داده بود، ولی هنگامی که برای او روشن شد که وی دشمن الله است از او ابراز بیزاری نمود، بی‌گمان ابراهیم بسیار دست به دعا و بردبار و مهربان بود.');
INSERT INTO "badkhashani" VALUES(9,115,'9:115','و الله هرگز قومی را پس از آنکه هدایتشان کرده است گمراه نمی‌سازد تا زمانی که آنچه را باید از آن بپرهیزند برایشان روشن نماید. چون الله به هر چیزی داناست.');
INSERT INTO "badkhashani" VALUES(9,116,'9:116','(قدرت الله نیز مانند علم او به همه کائنات احاطه دارد و) يقينا بادشاهی آسمان‌ها و زمین برای الله است، زندگی می‌بخشد و می‌میراند، و برای شما جز الله هیچ کارساز و مددگاری نیست.');
INSERT INTO "badkhashani" VALUES(9,117,'9:117','بی‌گمان الله بر پیغمبر و مهاجرین و انصار (به رحمت متوجه شد و) ببخشود، آنانی که در هنگام سختی (غزوۀ تبوک) از او پیروی کردند، بعد از آنکه نزدیک بود دل‌های گروهی از آنان منحرف شود، باز توبه آنان را پذیرفت. چرا که او نسبت به آنان مشفق (و) مهربان است.');
INSERT INTO "badkhashani" VALUES(9,118,'9:118','و (الله) توبۀ آن سه کس را پذیرفت که باز مانده بودند تا آنکه زمین با همۀ فراخی اش بر آنان تنگ شد و جانهایشان بر آنان تنگ آمد، و يقين کردند که هیچ پناهگاهی از الله جز به‌سوی او نیست، باز از آنان درگذشت (و آنها را به توبه توفیق داد) تا توبه نمایند. بی‌گمان الله بسیار توبه پذیر (و) مهربان است.');
INSERT INTO "badkhashani" VALUES(9,119,'9:119','ای کسانی که ایمان آورده‌اید، از الله بترسید و با صادقان باشید.');
INSERT INTO "badkhashani" VALUES(9,120,'9:120','برای اهل مدینه و آنانی که در اطراف آنها هستند از بادیه نشینان سزاوار نیست که از همراهی رسول الله پس مانند، و جانهای خود را از جان پیغمبر دوست‌تر داشته باشند، (این پس ماندن به خاطری سزاوارشان نیست) که هیچ تشنگی و رنج و گرسنگی در راه الله به آنان نمی‌رسد، و هیچ گامی بر نمی‌دارند که کافران را به خشم آورد و هیچ چیزی از دشمن حاصل نمی‌کنند، مگر اینکه به سبب آن ثواب عمل نیکی برای‌شان نوشته می‌شود، زیرا الله اجر نیکوکاران را ضایع نمی‌کند.');
INSERT INTO "badkhashani" VALUES(9,121,'9:121','و هیچ انفاقی، چه اندک و چه زیاد نمی‌کنند و از هیچ سرزمینی عبور نمی‌کنند مگر اینکه برایشان (عمل صالح) نوشته می‌شود، تا الله در عوض نیکوترین اعمالی که می‌کردند به آنان پاداش دهد.');
INSERT INTO "badkhashani" VALUES(9,122,'9:122','و شایسته نیست که مؤمنان همگی (برای طلب علم) بیرون شوند، پس چرا از هر قبیلۀ از ایشان یک گروهی بیرون نمی‌شود تا در دین دانشمند شوند و تا قوم خود را، وقتی به‌سوی آنها باز گردند، بترسانند. شاید که آنها ( پند گیرند و) از عذاب الله بترسند.');
INSERT INTO "badkhashani" VALUES(9,123,'9:123','ای کسانی که ایمان آورده‌اید، با کفاری که با شما متصل (و هم مرزاند) بجنگید، و باید که در شما سختی و قوت را بیابند (و محسوس کنند) و بدانید که (نصرت) الله با پرهیزگاران است.');
INSERT INTO "badkhashani" VALUES(9,124,'9:124','و چون سوره‌ای نازل شود پس از میان آنها (منافقان) کسی هست که (به طور استهزا) می‌گوید: ایمان کدام یک از شما را این (سوره) افزود؟ (بگو:) اما آنانی که ایمان آورده‌اند البته بر ایمان آنها افزوده است و آنها خوش و شادمان می‌شوند.');
INSERT INTO "badkhashani" VALUES(9,125,'9:125','و اما آنانی که در دل‌هایشان مرض (شک و نفاق) است، (آن سوره) پلیدیی بر پلیدی سابق‌شان افزود و (سرانجام) در حالت کفر می‌میرند.');
INSERT INTO "badkhashani" VALUES(9,126,'9:126','آیا (منافقان) نمی‌بینند که آنها در هر سال، یک یا دو بار آزموده می‌شوند؟ (ولی) باز هم توبه نمی‌کنند و پند و عبرت نمی‌گیرند.');
INSERT INTO "badkhashani" VALUES(9,127,'9:127','و هرگاه سوره‌ای نازل شود منافقان به یکدیگر نظر می‌کنند (از روی استهزاء، و از یکدیگر می‌پرسند که) آیا کسی (از مؤمنان در این مجلس ) شما را می‌بیند؟ باز (از مجلس پیغمبر) برمی گردند. الله دل‌های ایشان (را از قبول حق) باز گردانیده است، زیرا آنها قومی هستند که نمی‌فهمند.');
INSERT INTO "badkhashani" VALUES(9,128,'9:128','البته برای شما پیغمبری از خودتان آمده است. رنج و مصیبتی که به شما برسد بر او دشوار است، به شما حریص است و نسبت به مؤمنان مشفق (و) مهربان است.');
INSERT INTO "badkhashani" VALUES(9,129,'9:129','پس اگر روی گردانند (از تو ای محمد) بگو: الله مرا بس است، هیچ معبودی برحق نیست جز او، بر وی توکل کرده ام، و او مالک عرش بزرگ است.');
INSERT INTO "badkhashani" VALUES(10,1,'10:1','(الر) مفهوم این حروف به الله معلوم است، این آیات همان کتاب پر از حکمت است.');
INSERT INTO "badkhashani" VALUES(10,2,'10:2','آیا برای مردم (کفار) تعجب آور است که به‌سوی مردی از خودشان وحی فرستادیم که مردم را (از عذاب الله) بترسان؟ و آنانی را که ایمان آورده‌اند مژده بده به اینکه آنها مقام عالی نزد پروردگارشان دارند، (لیکن) کافران گفتند: همانا این شخص جادو گر آشکار است.');
INSERT INTO "badkhashani" VALUES(10,3,'10:3','(پیام آن شخص این بود که) البته پروردگار شما الله است که آسمان‌ها و زمین را در شش روز آفريد، باز (طوریکه شایستۀ شکوه و جلال اوست) بر عرش مستقر شد. (و) کار (کائنات) را تدبیر و اداره می‌کند. هیچ شفاعت کننده‌ای نیست مگر بعد از اجازه او، این است الله پروردگار شما، پس او را عبادت کنید، آیا عبرت نمی‌گیرید.');
INSERT INTO "badkhashani" VALUES(10,4,'10:4','بازگشت همۀ شما به‌سوی اوست، این وعدۀ راست الله است، چون او آفریدن مخلوقات را آغاز می‌کند، باز آن را باز می‌گرداند تا کسانی را که ایمان آورده و کارهای نیک انجام داده‌اند به عدل و انصاف پاداش دهد، و اما کسانی که کفر ورزیده‌اند نوشیدنی از آب داغ و سوزان دارند و عذابی دردناک در پیش دارند، به سبب آنکه کفر می‌ورزیدند.');
INSERT INTO "badkhashani" VALUES(10,5,'10:5','او (الله) ذاتی است که آفتاب را درخشان و ماه را تابان گردانیده و برای ماه منازلی (مسیرهایی) مقرر نموده است تا شمارۀ سالها و حساب (روز و ماه) را بدانید. الله آن را جز به حق نیافریده است. الله آیات را برای آنانی که می‌دانند به تفصيل (و روشنی) بیان می‌کند.');
INSERT INTO "badkhashani" VALUES(10,6,'10:6','البته در اختلاف (رفت و آمد) شب و روز و آنچه الله در آسمان‌ها و در زمین آفریده است، دلائلى واضح (بر توحيد او تعالی) است برای مردمی که پرهیزگاری می‌کنند.');
INSERT INTO "badkhashani" VALUES(10,7,'10:7','يقينا آنانی که ملاقات ما را امید ندارند و به زندگی دنیا راضی شده‌اند و به آن مطمئن شده‌اند، و نیز آنانی که از آیات ما غافل‌اند.');
INSERT INTO "badkhashani" VALUES(10,8,'10:8','این گروه جایگاه‌شان دوزخ است، به سبب آنچه که می‌کردند.');
INSERT INTO "badkhashani" VALUES(10,9,'10:9','البته آنانی که ایمان آورده‌اند و کارهای نیک انجام داده‌اند، پروردگارشان به سبب ایمان شان، آنها را به باغ‌های پر ناز و نعمت جنت هدایت می‌کند که جوی‌ها در زیر آن روان است.');
INSERT INTO "badkhashani" VALUES(10,10,'10:10','(ابتدای) دعای آنها در جنت این است که ای الله! پاکی تو راست، و کلمۀ ملاقات آنها در آنجا سلام (علیکم) است، و پایان دعایشان این است که شکر و سپاس مخصوص پروردگار جهانیان است.');
INSERT INTO "badkhashani" VALUES(10,11,'10:11','و اگر الله به همان شتابی که مردم خیر را می‌طلبند، شر را به آنها به شتاب می‌رسانید، حتما اجلشان به زودی می‌رسید، اما کسانی را که به ملاقات ما امید ندارند، رها می‌کنیم تا در سرکشی و نافرمانی خود سرگردان شوند.');
INSERT INTO "badkhashani" VALUES(10,12,'10:12','و چون به انسان رنجی برسد (خواه) به پهلو یا نشسته یا ایستاده باشد ما را به کمک می‌خواند، پس هرگاه رنج و مصیبت را از او دور کنیم چنان به راه خود می‌رود که گویا ما را به دور کردن رنجی که به او رسیده بود، به کمک نخوانده است، این طور برای اسراف کاران آنچه انجام می‌دادند، زینت داده شده است.');
INSERT INTO "badkhashani" VALUES(10,13,'10:13','و البته مردمان پیش از شما را هلاک کردیم وقتی ظلم کردند، پیغمبرانشان با معجزه‌های روشن نزد آنان آمدند، ولی هرگز آماده نبودند که ایمان بیاورند، این طور قوم مجرم را سزا می‌دهیم.');
INSERT INTO "badkhashani" VALUES(10,14,'10:14','باز شما را بعد از آنها در زمین جانشین گردانیدیم تا ببینیم چگونه عمل می‌کنید.');
INSERT INTO "badkhashani" VALUES(10,15,'10:15','و چون آیات روشن ما بر آنها خوانده شود کسانی که به ملاقات ما امید ندارند، می‌گویند: قرآنی دیگر غیر از این بیاور، یا آن را عوض کن، بگو: مرا نرسد که از پیش خود آن را تغییر دهم، مگر آنچه که به من وحی می‌شود از آن پیروی می‌کنم. من اگر نافرمانی پروردگارم را کنم از عذاب روزی بزرگ می‌ترسم.');
INSERT INTO "badkhashani" VALUES(10,16,'10:16','بگو: اگر الله می‌خواست آن را بر شما تلاوت نمی‌کردم، و خود او هم شما را به آن آگاه نمی‌کرد، البته پیش از این عمری را در میان شما سپری کرده ام، آیا نمی‌فهمید؟');
INSERT INTO "badkhashani" VALUES(10,17,'10:17','پس کیست ظالم‌تر از آن که بر الله دروغ ببندد، یا آیات او را دروغ بشمارد، يقينا مجرمان رستگار نمی‌شوند.');
INSERT INTO "badkhashani" VALUES(10,18,'10:18','و اینها غیر از الله چیزهایی را می‌پرستند که به آنان نه زیانی می‌رسانند و نه نفعی به آنان می‌رسانند، و می‌گویند: اینها شفاعت کنندگان ما نزد الله هستند. بگو: آیا الله را به چیزی که در آسمان‌ها و در زمین (به وجود آن) علم ندارد، خبر می‌دهید؟ (اگر اینها حقیقت می‌داشتند در علم الله حتما می‌بودند) او پاک و برتر است از آنچه با وی شریک می‌سازند.');
INSERT INTO "badkhashani" VALUES(10,19,'10:19','و مردم جهان نبودند مگر امت واحد (پیرو دین حق) پس اختلاف کردند، و اگر سخنی که سابقا از طرف پروردگار تو صادر شده است نبود، البته فیصله می‌کرد در میان آنها (با هلاک نمودن كفار و نجات مسلمانان) در چیزی که در آن اختلاف دارند.');
INSERT INTO "badkhashani" VALUES(10,20,'10:20','و می‌گویند: چرا معجزه‌ای از سوی پروردگارش بر او نازل نشده است (چنانکه ما می‌خواهیم)؟ بگو: جز این نیست که علم غیب مخصوص الله است، پس انتظار بکشید که من هم با شما از انتظار کنندگانم.');
INSERT INTO "badkhashani" VALUES(10,21,'10:21','و چون پس از مصیبت و سختی که به مردم رسیده است رحمتی به آنان بچشانیم، ناگهان در آیات ما مکر و حیله می‌کنند، بگو: الله در مكر (مجازات) سریعتر است. بی‌گمان فرستادگان ما مکر و حیله‌های شما را می‌نویسند.');
INSERT INTO "badkhashani" VALUES(10,22,'10:22','او (الله) آن ذاتیست که شما را در خشکی و دریا می‌گرداند تا آنکه در کشتی‌ها نشینید، و باد خوش آنان را پیش برد و سواران با آن باد موافق شاد شوند، و ناگهان بادی سخت به آنان رسد و از هر سو موج به سویشان آید، و آنان يقين کنند که گرفتار شده‌اند (آن وقت) الله را در حالیکه دین و عبادت را برایش پاک و خالص کرده‌اند، به فریاد می‌خوانند و (می‌گویند:) اگر ما را از این حال نجات دهی البته از شکرکنندگان خواهیم بود.');
INSERT INTO "badkhashani" VALUES(10,23,'10:23','و پس هرگاه آنها را نجات دهد، ناگهانی آنها در زمین به ناحق سرکشی می‌کنند (و به بیراهه می‌روند) ای مردم! غیر از این نیست که سرکشی شما تنها بر ضرر خودتان است، (تا چند روز) بهرۀ زندگانی دنیاست (که از آن استفاده می‌کنید)، باز بازگشت تان به نزد ما است، پس شما را به آنچه که می‌کردید آگاه خواهیم کرد.');
INSERT INTO "badkhashani" VALUES(10,24,'10:24','جز این نیست که مثل زندگانی دنیا مانند آبی است که از آسمان فرود آوردیم و با آن گیاهان زمین که انسانها و چهارپایان از آن می‌خورند درهم آمیخت و زمین درخشانی خود را به دست می‌آورد، و دارای زیب و زینت گردید و ساکنان آن گمان کردند که بر (استفاده از) آن توان دارند. ناگهان فرمان ما در شب یا روز آمد و آن را چون کشت درو شده گردانیدیم، گویی دیروز وجود نداشته است. این چنین آیات را برای مردمی که می‌اندیشند به تفصیل بیان می‌کنیم.');
INSERT INTO "badkhashani" VALUES(10,25,'10:25','و الله (شما را به وسیلۀ این آیات) به‌سوی سرای سلامتی (جنت) دعوت می‌دهد. و هر کسی را که بخواهد به راه راست هدایت می‌کند.');
INSERT INTO "badkhashani" VALUES(10,26,'10:26','(نعمت‌های سرای سلامتی) برای آنانی که کارهای نیک کرده‌اند، نیکویی (جنت) است و افزوده بر آن (دیدار الله) است. و چهره‌هایشان را سیاهی و ذلتی (گناه) نمی‌پوشاند، اینها اهل جنت‌اند که در آن همیشه خواهند بود.');
INSERT INTO "badkhashani" VALUES(10,27,'10:27','و آنانی که مرتکب بدی‌ها شده‌اند (بدانند) که سزای بدی مانند آن است، و بر آنها غبار ذلت و رسوائی نشیند، در برابر الله هیچ پناه دهنده‌ای ندارند، (طوری معلوم می‌شوند که) گویی چهره‌هایشان به قطعه‌های از شب تاریک پوشانیده شده است، اینها دوزخیانند که در آن همیشه می‌باشند.');
INSERT INTO "badkhashani" VALUES(10,28,'10:28','و یادآور شوید روزی را که همه مردم را جمع کنیم، باز به مشرکان می‌گوییم: شما و معبودانی که با الله شریک می‌ساختید در جای خود بایستید. باز در میانشان جدایی می‌آوریم و شریک‌هایشان می‌گویند: شما ما را نمی‌پرستیدید (بلکه شیاطین را پرستش می‌کردید).');
INSERT INTO "badkhashani" VALUES(10,29,'10:29','پس الله به عنوان گواه در بین ما و شما کافی است، البته ما از عبادت شما بی‌خبر بودیم.');
INSERT INTO "badkhashani" VALUES(10,30,'10:30','در آنجا هر کس آنچه را که قبلا فرستاده بود می‌آزماید (که آیا به نفع خود فرستاده یا به ضرر خود) و به دربار الله که کارساز و معبود حقیقی آنها است باز گردانیده شوند و آنچه به الله افترا می‌کردند، از آنها گم خواهد شد.');
INSERT INTO "badkhashani" VALUES(10,31,'10:31','بگو: (به مشرکان) کیست که از آسمان و زمین به شما روزی میدهد؟ یا کیست که مالک گوش و چشم‌ها است؟ و کیست که زنده را از مرده بیرون می‌آورد، و کیست که مرده را از زنده بیرون می‌آورد؟ و کیست که امور (عالم) را تدبیر (و حکم خود را در آن تنفيذ) می‌کند؟ (در جواب) خواهند گفت: «الله»، پس بگو: آیا (از الله) نمی‌ترسید؟!.');
INSERT INTO "badkhashani" VALUES(10,32,'10:32','پس این است الله، پروردگار حقیقی شما، پس بعد از حق جز گمراهی نیست، پس چگونه باز گردانیده می‌شوید؟');
INSERT INTO "badkhashani" VALUES(10,33,'10:33','این گونه سخن پروردگارت بر فاسقان ثابت شده است (و به حقیقت پیوست) که آنها ایمان نمی‌آرند (و اهلیت هدایت را ندارند).');
INSERT INTO "badkhashani" VALUES(10,34,'10:34','بگو: آیا از شریکان شما کسی هست که آفرینش را آغاز کند باز آن را برگرداند؟ بگو: تنها الله آفرینش را آغاز می‌کند باز آن را بر می‌گرداند. پس چگونه باز گردانیده می‌شوید؟!');
INSERT INTO "badkhashani" VALUES(10,35,'10:35','بگو: آیا از شریک‌های شما کسی هست که (مردم را) به‌سوی دین حق راهنمایی کند؟ بگو: الله (مردم را) به‌سوی دین حق راهنمایی می‌کند. آیا آن کس که به‌سوی حق هدایت می‌کند سزاوارتر است که پیروی شود یا کسی که خودش راه نمی‌یابد مگر اینکه راهنمایی گردد؟ پس شما را چه شده است؟ چگونه حکم (نادرست) می‌کنید؟');
INSERT INTO "badkhashani" VALUES(10,36,'10:36','و بیشتر‌شان (مشركان) جز از گمان پیروی نمی‌کنند، حال آنکه گمان، انسان را از حق بی‌نیاز نمی‌کند. البته الله به آنچه می‌کنند آگاه است.');
INSERT INTO "badkhashani" VALUES(10,37,'10:37','و چنان نیست که این قرآن از سوی غير الله ساخته شود، ليكن تصديق کنندۀ آن کتاب‌های آسمانی است که پیش از آن نازل شده‌اند، و تفصيل دهندۀ کتاب است که در آن هیچ شکی نیست و از سوی پروردگار جهانیان است.');
INSERT INTO "badkhashani" VALUES(10,38,'10:38','آیا (کفار مکه) می‌گویند: آن (قرآن) را به دروغ ساخته است؟ بگو: پس سوره‌ای مانند آن بیاورید و (برای کمک خود) هر کسی را که می‌توانید، غیر از الله، طلب کنید، اگر (در دعوای تان) راستگو هستید.');
INSERT INTO "badkhashani" VALUES(10,39,'10:39','(قرآن ساختۀ بشر نیست) بلکه چیزی را تکذیب کردند که به علم آن آگاهی نداشتند و هنوز تأویلش به آنان نیامده است، کسانی که پیش از آنان بودند نیز همینگونه تکذیب کردند، پس بنگر که سرانجام ظالمان چگونه شد؟!.');
INSERT INTO "badkhashani" VALUES(10,40,'10:40','و (مردم در مورد قرآن دو گروه اند) از ایشان بعضی است که به آن ایمان می‌آورند، و بعضی از آنها به آن ایمان نمی‌آورند، و پروردگار تو به حال مفسدان داناتر است.');
INSERT INTO "badkhashani" VALUES(10,41,'10:41','و اگر تو را تکذیب کردند پس بگو: عمل من به خودم است، و عمل شما به خود شما است، (من جوابگوی عمل خود و شما جوابگوی عمل خودتان هستید) شما از آنچه من می‌کنم بیزارید و من هم از آنچه شما می‌کنید بیزارم.');
INSERT INTO "badkhashani" VALUES(10,42,'10:42','و از آنان (تکذیب کنندگان) کسانی هستند که (به ظاهر) به تو گوش می‌دهند، آیا تو می‌توانی کران را بشنوانی؟ اگر چه عقل و خرد نیز نداشته باشند.');
INSERT INTO "badkhashani" VALUES(10,43,'10:43','و از آنها کسی هست که به تو می‌نگرد، آیا تو کوران را اگر چه نبینند راهنمایی کرده می‌توانی؟');
INSERT INTO "badkhashani" VALUES(10,44,'10:44','يقينا الله به هیچ وجه به مردم ظلم نمی‌کند، بلکه مردم به خویشتن ظلم می‌کنند.');
INSERT INTO "badkhashani" VALUES(10,45,'10:45','و در روزی که (در میدان محشر) مردم را جمع می‌کند (چنان معلوم خواهد شد که) گویا به قدر ساعتی از روز نمانده‌اند، (در آن وقت) باهم آشنا می‌شوند، (و ليكن به یکدیگر نفعی رسانده نتوانند). البته آنانی که دیدار الله را (در روز محشر) تکذیب کردند، زیانکار شدند و راه یافته نبودند.');
INSERT INTO "badkhashani" VALUES(10,46,'10:46','و اگر بعضی از آنچه را که به آنها وعده داده‌ایم به تو نشان دهیم، یا تو را بمیرانیم (در هر دو صورت) بازگشت‌شان به‌سوی ماست، باز الله بر آنچه می‌کنند گواه است.');
INSERT INTO "badkhashani" VALUES(10,47,'10:47','و برای هر امتی پیغمبری می‌باشد، پس وقتی پیامبرشان بیاید، به عدل و انصاف در میانشان فیصله می‌شود، و هیچ ظلمی به آنها نمی‌شود.');
INSERT INTO "badkhashani" VALUES(10,48,'10:48','و (مشرکان) می‌گویند: این وعده چه وقت است؟ اگر راست می‌گویید.');
INSERT INTO "badkhashani" VALUES(10,49,'10:49','بگو: (من چه می‌دانم؟ زیرا) من برای خود زیان و نفعی در اختیار ندارم، مگر چیزی را که الله بخواهد، برای هر امتی اجلی است، پس چون اجلشان برسد، نه ساعت تأخیر شود و نه (ساعتی) پیش افتد.');
INSERT INTO "badkhashani" VALUES(10,50,'10:50','بگو: (ای پیغمبر) مرا خبر دهید؛ اگر عذاب او شب یا روز به شما بیاید، پس مجرمان (کافران) بخاطر چه چیز عذاب را به زودی می‌خواهند؟');
INSERT INTO "badkhashani" VALUES(10,51,'10:51','(لذا گفته شود) باز آیا وقتی که (آن عذاب) تحقق یابد به آن ایمان می‌آورید؟ آیا اکنون (ایمان می‌آورید) در حالیکه رسیدن آن را به زودی می‌طلبیدید؟!');
INSERT INTO "badkhashani" VALUES(10,52,'10:52','باز (در روز قیامت) به ظالمان گفته شود عذاب ابدی را بچشید، آیا جز به آنچه کسب می‌کردید (در دنیا) سزا داده می‌شوید.');
INSERT INTO "badkhashani" VALUES(10,53,'10:53','و از تو می‌پرسند که آن (وعدۀ عذاب ابدی) راست است؟ بگو: آری، و قسم به پروردگارم که آن البته حق است، و شما (الله را از فرستادن آن عذاب) عاجزکننده نیستید.');
INSERT INTO "badkhashani" VALUES(10,54,'10:54','و اگر (بالفرض) برای هر کسی که ظلم کرده است، تمام آنچه در زمین است می‌بود، حتما آن را برای نجات خود می‌پرداخت. و چون عذاب الله را ببینند پنهانی اظهار ندامت می‌کنند و در میان آنان به عدل و انصاف فیصله می‌گردد، و بر آنها ظلم نمی‌شود.');
INSERT INTO "badkhashani" VALUES(10,55,'10:55','آگاه باشید آنچه که در آسمان‌ها و زمین است از الله است. آگاه باشید که وعدۀ الله حق است ولی اکثر آنها نمی‌دانند.');
INSERT INTO "badkhashani" VALUES(10,56,'10:56','او (الله) زندگی می‌بخشد و می‌میراند و به‌سوی او باز گردانیده می‌شوید.');
INSERT INTO "badkhashani" VALUES(10,57,'10:57','ای مردم (جهان)؛ يقينا برای شما از طرف پروردگارتان پندی آمده است و شفایی برای آنچه در سینه‌هاست و هدایت و رحمتی است برای کسانی که ایمان آورده‌اند.');
INSERT INTO "badkhashani" VALUES(10,58,'10:58','بگو: به فضل و رحمت الله است که (مسلمانان) باید به آن شادمان شوند. و این از هرچه (از مال و متاع دنیا) جمع می‌کنند بهتر است.');
INSERT INTO "badkhashani" VALUES(10,59,'10:59','بگو: به من خبر دهید آنچه از روزیی که الله برایتان نازل کرده است، پس (چرا) بعضی آن را حرام و بعضی آن را حلال قرار داده‌اید؟ آیا الله به شما اجازه داده است؟ یا بر الله دروغ می‌بندید؟!');
INSERT INTO "badkhashani" VALUES(10,60,'10:60','و آنانی که بر الله دروغ می‌بندند (در بارۀ) روز قیامت چه گمان دارند؟ یقینا الله نسبت به مردم دارای فضل و مهربانی است، لیکن بیشترشان شکرگزار نیستند.');
INSERT INTO "badkhashani" VALUES(10,61,'10:61','و نمی‌باشی (تو ای انسان) در هیچ حالتی و تلاوت نمی‌کنی هیچ آیه‌ای از قرآن را و هیچ کاری نمی‌کنید مگر اینکه ما بر شما گواهيم وقتی به آن کار شروع می‌کنید. و به اندازۀ ذره‌ای در زمین و آسمان از پروردگارت پنهان نمی‌ماند، و نه کوچک‌تر از آن و نه بزرگتر از آن مگر اینکه در کتابی روشن نوشته شده است.');
INSERT INTO "badkhashani" VALUES(10,62,'10:62','آگاه باشید که البته بر دوستان الله (مردمانی که اعمال نیک انجام می‌دهند) هیچ ترسی نیست و (در روز قیامت) آنها غمگین نمی‌شوند.');
INSERT INTO "badkhashani" VALUES(10,63,'10:63','(این دوستان الله) کسانی‌اند که ایمان آورده‌اند و از الله می‌ترسند و تقوی پیشه کرده‌اند.');
INSERT INTO "badkhashani" VALUES(10,64,'10:64','برای آنها در زندگانی دنیا و آخرت بشارت است، سخنان الله هیچ دگرگونی نمی‌یابد، این همان پیروزی بزرگ است.');
INSERT INTO "badkhashani" VALUES(10,65,'10:65','و سخن آنها ترا غمگین نکند، زیرا عزت کاملا از الله است و او شنوای داناست.');
INSERT INTO "badkhashani" VALUES(10,66,'10:66','آگاه باشید که هر که در آسمان‌ها و هر که در زمین است، از الله است. و کسانی که غیر از الله شریکانی را به کمک (خویش) می‌خوانند جز از گمان پیروی نمی‌کنند، و کارشان جز تخمین و دروغ نیست.');
INSERT INTO "badkhashani" VALUES(10,67,'10:67','اوست آن ذاتی که شب را برای شما آفریده است تا در آن آرام گیرید و روز را (برای دیدن شما) روشن گردانید، يقينا در این امر برای مردمانی که می‌شنوند (کلام الله را) دلائل واضح است.');
INSERT INTO "badkhashani" VALUES(10,68,'10:68','(مشركان) گفتند: الله برای خود فرزند گرفته است (پس فرزند الله را عبادت باید کرد) او پاک و منزه است و او (از هر مخلوقی) بی‌نیاز است. هر چه در آسمان‌ها و زمین است از الله است، هیچ دلیلی بر این (ادعا) ندارید، آیا به الله چیزی را نسبت می‌دهید که نمی‌دانید؟!.');
INSERT INTO "badkhashani" VALUES(10,69,'10:69','بگو: یقینا آنانی که بر الله دروغ می‌بندند کامیاب نخواهند شد.');
INSERT INTO "badkhashani" VALUES(10,70,'10:70','(بلکه چند روز) متاعی است در دنیا، باز بازگشت آنها به‌سوی ماست. باز عذاب سخت را به سبب کفری که می‌ورزیدند به آنها می‌چشانیم.');
INSERT INTO "badkhashani" VALUES(10,71,'10:71','و قصۀ نوح را برای آنان بخوان، وقتی که به قوم خود گفت: «ای قوم من! اگر ماندن من (در بین شما) و پند دادنم به آيات الله بر شما سخت می‌گذرد، پس بر الله توکل کرده ام. لذا شما تصمیم قاطع بگیرید همرای شریکان خویش، باز کارتان بر شما پوشیده نباشد. باز آنچه را که می‌خواهید در حق من انجام دهید، دریغ نکنید و مرا مهلت ندهید.');
INSERT INTO "badkhashani" VALUES(10,72,'10:72','پس اگر (از قبول دعوت من) روی گردانید (به من زیانی نیست و بدانید) که من از شما هیچ مزدی نخواسته ام. مزد من نیست مگر بر الله، و فرمان داده شده ام که از مسلمانان (فرمانبرداران) باشم.');
INSERT INTO "badkhashani" VALUES(10,73,'10:73','پس او را تکذیب کردند، باز او و آنانی را که با او در کشتی بودند نجات دادیم و آنها (نجات یافته گان) را جانشینان گردانیدیم، و آنانی را که آیات ما را تکذیب کردند غرق کردیم، پس ببین که سرانجام بیم داده شدگان چه شد؟');
INSERT INTO "badkhashani" VALUES(10,74,'10:74','باز پس از نوح پیغمبرانی را به‌سوی اقوام‌شان فرستادیم، پس به آنها دلائل واضح آوردند، اما ایشان هرگز آماده آن نبودند که به آنچه که قبلا تكذیب نموده بودند، ایمان بیاورند. این طور ما بر دل‌های تجاوزکاران مهر می‌نهیم.');
INSERT INTO "badkhashani" VALUES(10,75,'10:75','باز پس از آنها موسى وهارون را با آیات خود به‌سوی فرعون و سران قوم او فرستادیم، ولی آنها تكبر نمودند و قوم مجرم بودند.');
INSERT INTO "badkhashani" VALUES(10,76,'10:76','پس چون حق از جانب ما بسویشان آمد، گفتند: یقینا این جادوی آشکار است.');
INSERT INTO "badkhashani" VALUES(10,77,'10:77','موسی (در جواب) گفت: آیا وقتی حق به شما آمد، می‌گویید: (این جادو است؟) آیا این جادو است؟ حال آنکه جادو گران (در مقابل دین حق) کامیاب نمی‌شوند.');
INSERT INTO "badkhashani" VALUES(10,78,'10:78','گفتند: آیا پیش ما آمده‌ای تا ما را از چیزهایی که پدران و نیاکان خود را بر آن یافته‌ایم باز داری؟ و می‌خواهید در این سرزمین بزرگی و ریاست برای شما دو نفر باشد؟ و ما به شما دو تن ایمان نمی‌آوریم.');
INSERT INTO "badkhashani" VALUES(10,79,'10:79','و فرعون گفت: هر جادوگر دانا (و ماهر) را نزد من بیاورید.');
INSERT INTO "badkhashani" VALUES(10,80,'10:80','و چون جادوگران (برای مقابله) آمدند، موسی به آنها گفت: آنچه را می‌خواهید بافگنید، (در میدان) بیندازید.');
INSERT INTO "badkhashani" VALUES(10,81,'10:81','پس چون (عصاها و ریسمان‌های خود را) انداختند، موسی گفت: آنچه را که شما آورده‌اید همه جادوست، یقینا الله آن را به زودی باطل خواهد کرد، چون الله کار مفسدان را درست (و سودمند) نمی‌کند.');
INSERT INTO "badkhashani" VALUES(10,82,'10:82','و الله حق را به سخن‌های خود ثابت می‌کند، هر چند مجرمان نپسندند.');
INSERT INTO "badkhashani" VALUES(10,83,'10:83','پس هیچ کس به موسی ایمان نیاورد مگر جوانانی (اندک) از بنی اسرائیل (آن هم) با ترس از فرعون و سران قوم او که مبادا آنان را در فتنه اندازند (و از دین برگردانند). چون فرعون در زمین طغيان کرده بود و از زمرۀ تجاوزکاران بود.');
INSERT INTO "badkhashani" VALUES(10,84,'10:84','و موسی گفت: ای قوم من! اگر به الله ایمان آورده‌اید پس بر وی توکل کنید اگر مسلمان (فرمانبردار) هستید.');
INSERT INTO "badkhashani" VALUES(10,85,'10:85','پس گفتند: بر الله توکل کردیم، ای پروردگار ما! ما را فتنۀ برای قوم ظالم مگردان.');
INSERT INTO "badkhashani" VALUES(10,86,'10:86','و ما را به رحمت خود از قوم کافر نجات ده.');
INSERT INTO "badkhashani" VALUES(10,87,'10:87','و به‌سوی موسی و برادرش (هارون) وحی کردیم که برای قوم خود خانه‌هایی در مصر ترتیب دهید و خانه‌هایتان را رو به قبله (و محل برگزاری نماز) قرار دهید، و نماز را بر پا کنید و مؤمنان را مژده بده.');
INSERT INTO "badkhashani" VALUES(10,88,'10:88','و گفت موسی: ای پروردگار ما! يقينا به فرعون و اشراف قوم او زینت و مال‌های بسیار در زندگانی دنیا داده ای. پروردگارما! تا (مردم را) از راه تو گمراه سازند؟ ای پروردگار ما! مال‌هایشان را نابود کن و دل‌هایشان را سخت کن که ایمان نیارند، تا عذاب دردناک را بینند.');
INSERT INTO "badkhashani" VALUES(10,89,'10:89','(الله) گفت: البته دعای هردوی شما قبول شد، پس (در راه راست) استقامت کنید، و از راه نادانان پیروی نکنید.');
INSERT INTO "badkhashani" VALUES(10,90,'10:90','و بنی اسرائیل را از دریا عبور دادیم، پس فرعون و لشکر او از روی ستم و سرکشی آنها را تعقیب کردند، تا آن که غرقاب فرعون را احاطه کرد، گفت: يقين کردم که هیچ معبودی جز آن که بنی اسرائیل به او ایمان آورده‌اند، نیست. و من (هم اکنون) از مسلمانانم.');
INSERT INTO "badkhashani" VALUES(10,91,'10:91','(به او گفته شد) اکنون؟ در حالیکه پیش از این نافرمانی کردی و از جملۀ مفسدان بودی؟');
INSERT INTO "badkhashani" VALUES(10,92,'10:92','پس امروز تو را با جسد بی‌جانت نجات می‌دهیم تا برای کسانی که پس از تو می‌آیند نشانۀ (عبرت) باشی، و يقينا بسیاری از مردم از نشانه‌های (قدرت) ما غافل‌اند.');
INSERT INTO "badkhashani" VALUES(10,93,'10:93','و البته بنی اسرائیل را در جایگاه خوب و ستوده‌ای جای دادیم و از پاکیزه‌ها به آنها روزی دادیم. و اختلاف نورزیدند تا آن که دانش به آنان رسید. یقینا پروردگارت روز قیامت دربارۀ چیزی که در آن اختلاف می‌کردند، در میان آنها فیصله خواهد کرد.');
INSERT INTO "badkhashani" VALUES(10,94,'10:94','پس اگر (بالفرض) دربارۀ چیزی که بر تو نازل کرده‌ایم، در شک و تردید هستی، پس از آنانی که پیش از تو کتاب می‌خواندند بپرس. البته از سوی پروردگارت حق برای تو آمده است. پس، از شک کنندگان مباش.');
INSERT INTO "badkhashani" VALUES(10,95,'10:95','و از آنانی که آيات الله را دروغ شمردند مباش، که از زیانکاران می‌شوی.');
INSERT INTO "badkhashani" VALUES(10,96,'10:96','يقينا آنانی که حکم پروردگارت بر آنها ثابت شده است (که بر کفر از دنیا می‌روند)، ایمان نمی‌آورند.');
INSERT INTO "badkhashani" VALUES(10,97,'10:97','و اگر هر گونه نشانه‌ای به آنها بیاید، (بازهم ایمان نمی‌آورند) تا وقتی که عذاب دردناک را بینند.');
INSERT INTO "badkhashani" VALUES(10,98,'10:98','و مردم هیچ شهری ایمان نیاوردند که ایمانشان به آنان نفعی برساند مگر قوم یونس که چون ایمان آوردند عذاب رسواگر را در زندگی دنیا از آنان برطرف نمودیم و آنان را تا مدتی بهره مند ساختیم.');
INSERT INTO "badkhashani" VALUES(10,99,'10:99','و اگر پروردگار تو می‌خواست البته هرکه در روی زمین است، همه ایمان می‌آوردند (لیکن الله این را نخواست) پس آیا تو مردم را مجبور می‌سازی تا (همه) ایمان بیاورند؟');
INSERT INTO "badkhashani" VALUES(10,100,'10:100','و هیچ کس نمی‌تواند ایمان بیاورد مگر به اذن الله، و بر آنانی که نمی‌فهمند پلیدی قرار می‌دهد.');
INSERT INTO "badkhashani" VALUES(10,101,'10:101','بگو: بنگرید که چه چیزی در آسمان‌ها و زمین است؟ اما آیات و بیم دادنها به گروهی که ایمان نمی‌آورند سود نمی‌بخشد.');
INSERT INTO "badkhashani" VALUES(10,102,'10:102','پس آیا جز مانند روزهای پیشینیان را انتظار می‌کشند؟ بگو: پس انتظار بکشید، من نیز با شما از منتظرانم.');
INSERT INTO "badkhashani" VALUES(10,103,'10:103','باز پیغامبران خود و مؤمنان را نجات می‌دهیم، این گونه بر خود لازم گردانیدیم که مؤمنان را نجات می‌دهیم.');
INSERT INTO "badkhashani" VALUES(10,104,'10:104','بگو: ای مردم! اگر راجع به دین در شک و تردید هستید (از من امید چیزی نکنید) زیرا من کسانی را نمی‌پرستم که شما به غیر از الله می‌پرستید، بلکه الله را می‌پرستم که شما را می‌میراند. و به من دستور داده شده که از مؤمنان باشم.');
INSERT INTO "badkhashani" VALUES(10,105,'10:105','و (به من دستور داده شده) اینکه به‌سوی دینی روی آور که خالی از هرگونه شرک و انحراف است. و اینکه هرگز از مشرکان مباش.');
INSERT INTO "badkhashani" VALUES(10,106,'10:106','و (به من دستور داده شده) اینکه به غیر از الله کسی (و چیزی) را مخوان که هیچ سود و زیانی به تو نمی‌رساند، اگر چنین کنی در آن صورت يقينا از ظالمان خواهی شد.');
INSERT INTO "badkhashani" VALUES(10,107,'10:107','و اگر الله به تو ضرری برساند پس هیچ دور کننده‌ای برای آن جز خود او نیست و اگر خیری به تو بخواهد پس فضل او را هیچ ردکننده‌ای نیست، آن را به هر کس از بندگانش که بخواهد می‌رساند و او آمرزنده (و) مهربان است.');
INSERT INTO "badkhashani" VALUES(10,108,'10:108','بگو: ای مردم! حق از نزد پروردگارتان برای شما آمده است، پس هرکس هدایت یافت، به نفع خود هدایت یافته است، و هر کس گمراه شود پس به زیان خودش گمراه می‌شود، و من بر شما وکیل و نگهبان نیستم.');
INSERT INTO "badkhashani" VALUES(10,109,'10:109','و (ای محمد) از آنچه که بر تو وحی می‌شود، پیروی کن. و صابر و شکیبا باش تا آن که الله (در میان تو و مخالفان تو) فيصله کند و او بهترین فیصله کنندگان است.');
INSERT INTO "badkhashani" VALUES(11,1,'11:1','الر (معنای این حروف را الله می‌داند)، این (قرآن) کتابی است که آیات آن استوار و محکم گردیده، باز از جانب حکیم آگاه به تفصیل بیان شده است.');
INSERT INTO "badkhashani" VALUES(11,2,'11:2','برای اینکه چیزی را نپرستید مگر الله را، چون من از جانب او برای شما ترساننده و مژده دهنده هستم.');
INSERT INTO "badkhashani" VALUES(11,3,'11:3','و برای اینکه از پروردگارتان آمرزش بخواهید، باز به دربار او توبه کنید، تا شما را تا مدت معین به طرز نیکو بهره مند سازد و تا به هر صاحب فضل (در عقیده و عمل)، پاداش فضل او را بدهد. و اگر (از عبادت الله و توبه) روی بگردانید، پس من بر شما از عذاب روز بزرگ می‌ترسم.');
INSERT INTO "badkhashani" VALUES(11,4,'11:4','(چون) بازگشت شما به سوى الله است و او بر هرچیزی تواناست.');
INSERT INTO "badkhashani" VALUES(11,5,'11:5','آگاه باش که آنها (مشرکان) سینه‌های خود را می‌پیچند تا خود را از الله پنهان کنند، آگاه باش، وقتی که آنها خود را با جامه‌هایشان می‌پوشانند، الله آنچه را که پنهان می‌کنند و آنچه را آشکار می‌سازند، می‌داند. چرا که او به راز سینه‌ها داناست.');
INSERT INTO "badkhashani" VALUES(11,6,'11:6','هیچ رونده‌ای (جانداری) در زمین نیست مگر اینکه روزی آن بر عهدۀ الله است و محل استقرار و محل بازگشت آن را می‌داند، همه در کتاب بیانگر و روشن (لوح المحفوظ) ثبت است.');
INSERT INTO "badkhashani" VALUES(11,7,'11:7','و او (الله) همان ذاتیست که آسمان‌ها و زمین را در شش روز آفرید، در حالیکه عرش او بر آب بود (و شما را آفرید) تا شما را بیازماید که کدام تان نیکوکارترید. و اگر بگویی شما پس از مرگ زنده می‌شوید، کافران می‌گویند: این سخن جز جادوی آشکار نیست.');
INSERT INTO "badkhashani" VALUES(11,8,'11:8','و اگر عذاب را از آنها تا مدت شمرده شده به تأخیر اندازیم، البته (به طور تمسخر) می‌گویند: چه چیز مانع (آمدن آن عذاب) می‌شود؟ آگاه باش! روزی که عذاب به آنها برسد از آنها بازگشتنی نیست و چیزی را که به آن استهزاء می‌کردند، آنان را دربر می‌گیرد.');
INSERT INTO "badkhashani" VALUES(11,9,'11:9','و اگر از جانب خود به انسان رحمتی بچشانیم، باز آن را از وی بگیریم البته بسی ناامید و ناسپاس می‌گردد.');
INSERT INTO "badkhashani" VALUES(11,10,'11:10','و اگر بعد از رنجی که به او رسیده است نعمتی بچشانیم، می‌گوید: بدیها و سختی‌ها از من دور شد. يقينا او شادمان (و) فخر فروش است.');
INSERT INTO "badkhashani" VALUES(11,11,'11:11','مگر آنانی که صبر نمودند و کارهای نیک انجام دادند، اینها برای‌شان آمرزش و پاداش بزرگ است.');
INSERT INTO "badkhashani" VALUES(11,12,'11:12','مبادا بعضی از آنچه را که به سویت وحی می‌شود رها کنی و از اینکه می‌گویند: چرا گنجی بر او نازل نشد یا چرا فرشته‌ای با او نیامد (تا رسالت او را تصدیق کند) دلتنگ و ناراحت شوی، حال آنکه تو تنها بیم دهنده هستی و الله بر همه چیز مراقب و نگاهبان است.');
INSERT INTO "badkhashani" VALUES(11,13,'11:13','یا (مشرکین) می‌گویند: این قرآن خود ساخته و افترای پیغمبر است؟ بگو: پس شما ده سورۀ ساخته شده (دروغین) مانند آن را بیاورید و غیر از الله هرکس را که می‌توانید به یاری طلب کنید، اگر راستگو هستید.');
INSERT INTO "badkhashani" VALUES(11,14,'11:14','پس اگر سخن شما را اجابت نکردند، پس بدانید که قرآن به علم الله نازل شده است و (بدانید) که هیچ معبودی برحق جز او نیست، پس آیا مسلمان (فرمانبردار) می‌شوید؟');
INSERT INTO "badkhashani" VALUES(11,15,'11:15','کسانی که زندگانی دنیا و زینت آن را بخواهند، ثمرۀ اعمالشان را در همین دنیا کامل به آنها می‌دهیم، و به آنها کم داده نخواهد شد.');
INSERT INTO "badkhashani" VALUES(11,16,'11:16','(لیکن) ایشان کسانی‌اند که در آخرت (جزایی) جز آتش برایشان نیست و آنچه در دنیا کرده‌اند ضایع و هدر رفته است و کارهایشان باطل گردیده است.');
INSERT INTO "badkhashani" VALUES(11,17,'11:17','آیا کسی که دليل واضح از جانب پروردگار خود دارد و گواهی از طرف الله به دنبال آن باشد، و پیش از قرآن (هم)، کتاب موسی پیشوا و رحمت بوده است (مانند کسی است که در گمراهی قرار دارد)؟! ایشان به آن (حقایقی که پیغمبر آورده) ایمان می‌آورند، و هرکس از این احزاب و گروهها به آن کفر ورزد، پس آتش وعده‌گاه اوست. پس دربارۀ آن در شک و تردید مباش، بی‌گمان آن (قرآن) حق است و از سوی پروردگارت آمده است، ولی بسیاری از مردم ایمان نمی‌آورند.');
INSERT INTO "badkhashani" VALUES(11,18,'11:18','و کیست ظالم‌تر از آن کس که بر الله دروغ ببندد، ایشان بر پروردگارشان عرضه می‌شوند، و گواهان می‌گویند: اینها همان کسانی‌اند که بر پروردگار‌شان دروغ بستند، آگاه باشید! لعنت الله بر ظالمان است.');
INSERT INTO "badkhashani" VALUES(11,19,'11:19','آنانی که مردم را از راه الله منع می‌کنند، و آن را کج (و منحرف) می‌خواهند و آنها از آخرت هم منکرند.');
INSERT INTO "badkhashani" VALUES(11,20,'11:20','ایشان (نمی توانند الله را) در زمین ناتوان و عاجز کنند و آنان به غیر از الله کارساز و مددگاری ندارند، (در آخرت) عذاب برای آنها دو چندان خواهد شد (و آنان) توان شنیدن حق را نداشتند و حق را نمی‌دیدند.');
INSERT INTO "badkhashani" VALUES(11,21,'11:21','این گروه آنانی‌اند که به خود خساره و زیان زده‌اند و آنچه (بر الله) دروغ می‌بستند، از آنها گم و ناپدید گردید.');
INSERT INTO "badkhashani" VALUES(11,22,'11:22','بدون شک آنها در آخرت زیانکارترند.');
INSERT INTO "badkhashani" VALUES(11,23,'11:23','البته آنانی که ایمان آوردند و کارهای نیک کردند و برای پروردگارشان تواضع و فروتنی نمودند، آنها اهل جنت‌اند (که) در آن جاودانند.');
INSERT INTO "badkhashani" VALUES(11,24,'11:24','مثَل این دو گروه مانند کور و کر، و بینا و شنواست، آیا حال این دو گروه باهم برابر است؟ پس آیا پند نمی‌گیرید؟!');
INSERT INTO "badkhashani" VALUES(11,25,'11:25','و البته نوح را به‌سوی قومش فرستادیم (پس گفت: ای قوم من) يقينا من برای شما بیم دهندۀ آشکار هستم.');
INSERT INTO "badkhashani" VALUES(11,26,'11:26','که جز الله را عبادت نکنید؛ زیرا من از عذاب روز دردناک بر شما می‌ترسم.');
INSERT INTO "badkhashani" VALUES(11,27,'11:27','پس اشراف و سران قوم او که کافر بودند، گفتند: ما تو را جز بشری مانند خودما نمی‌بینیم و ما می‌بینیم که جز افراد حقیر و فرومایه و کوتاه فکر ما کسی از تو پیروی نکرده‌اند، و برای شما بر خود هیچ فضل و برتری نمی‌بینیم، بلکه شما را دروغگو می‌پنداریم.');
INSERT INTO "badkhashani" VALUES(11,28,'11:28','(نوح) گفت: ای قوم من! به من بگویید اگر دلیلی روشن از سوی پروردگارم داشته باشم و رحمتی از نزد خود به من داده باشد (و این رحمت الهی) بر شما پنهان مانده باشد، آیا می‌توانیم شما را به پذیرش آن ملزم کنیم در حالیکه شما آن را دوست نمی‌دارید؟');
INSERT INTO "badkhashani" VALUES(11,29,'11:29','و ای قوم من! در مقابل (تبلیغ) آن (دعوت) از شما چیزی نمی‌خواهم، چرا که پاداش من جز بر الله نیست. و من مؤمنان را (از خود) نمیرانم، آنان پروردگار خود را ملاقات می‌کنند ولی من شما را قومی (نادان) می‌بینم که جهالت می‌کنید.');
INSERT INTO "badkhashani" VALUES(11,30,'11:30','و ای قوم من! اگر آنها را طرد کنم، چه کسی مرا در برابر (عذاب) الله مدد خواهد کرد؟ آیا عبرت نمی‌گیرید؟');
INSERT INTO "badkhashani" VALUES(11,31,'11:31','و به شما نمی‌گویم که خزانه‌های الله نزد من است. و (نمی گویم) غیب نیز می‌دانم. و نمی‌گویم که من فرشته هستم. و دربارۀ آنانی که چشمان شما آنان را به حقارت می‌بیند نمی‌گویم الله خیری به آنان نخواهد داد. (بلکه) الله به آنچه در دل‌های آنان است آگاه‌تر است. در این صورت من از جملۀ ظالمان خواهم بود.');
INSERT INTO "badkhashani" VALUES(11,32,'11:32','گفتند: ای نوح، به راستی با ما مجادله کردی و بسیار هم جدال کردی، لذا آنچه را که به ما وعده می‌دهی بیار اگر از راستگویان هستی.');
INSERT INTO "badkhashani" VALUES(11,33,'11:33','(نوح) گفت: تنها الله است که اگر بخواهد آن را برای شما می‌آورد، و شما (الله را) عاجز کننده نیستید.');
INSERT INTO "badkhashani" VALUES(11,34,'11:34','و اگر بخواهم شما را اندرز بگویم، اندرز من به شما سودی نمی‌رساند، اگر الله بخواهد شما را گمراه کند. او پروردگار شماست و به‌سوی او باز گردانیده می‌شوید.');
INSERT INTO "badkhashani" VALUES(11,35,'11:35','آیا (مشرکین مانند قوم نوح) می‌گویند: آن (قرآن) را (محمد) از خود ساخته است؟ بگو: اگر آن را از خود ساخته باشم گناهش بر گردن من است، ولی من از جرمی که شما می‌کنید بیزارم.');
INSERT INTO "badkhashani" VALUES(11,36,'11:36','و به‌سوی نوح وحی فرستاده شد که از قوم تو جز آنانی که ایمان آورده‌اند، هرگز کسی ایمان نخواهد آورد، پس به سبب آنچه می‌کنند غمگین مشو.');
INSERT INTO "badkhashani" VALUES(11,37,'11:37','و زیر نظر ما و به حکم ما کشتی را بساز، و دربارۀ ظالمان با من سخن مگو؛ چرا که آنها غرق شدنی‌اند.');
INSERT INTO "badkhashani" VALUES(11,38,'11:38','و نوح کشتی را می‌ساخت و هر بار که اشرافی از قومش بر او می‌گذشتند، او را مسخره می‌کردند، گفت: اگر (امروز) ما را مسخره می‌کنید، ما نیز شما را همانگونه که مسخره می‌کنید، مسخره خواهیم کرد.');
INSERT INTO "badkhashani" VALUES(11,39,'11:39','پس به زودی خواهید دانست که بر چه کسی عذابی خواهد آمد که خوارش سازد. و بر او عذاب دائمی نازل خواهد شد.');
INSERT INTO "badkhashani" VALUES(11,40,'11:40','تا آن وقت که فرمان ما رسید و آب از تنور فوران کرد، گفتيم: از هر جنسی دو تن (نر و ماده) را در آن سوار کن و خانواده ات را، مگر کسانی که فرمان ما بر (هلاک آنان) از پیش صادر شده است. و کسانی را در آن بردار که ایمان آورده‌اند. و با او جز افراد کم ایمان نیاورده بودند.');
INSERT INTO "badkhashani" VALUES(11,41,'11:41','و (نوح به همراهان خود) گفت: در کشتی سوار شوید، به نام الله است روان شدنش و ایستادنش، البته پروردگار من آمرزندۀ مهربان است.');
INSERT INTO "badkhashani" VALUES(11,42,'11:42','و آن کشتی، آنها را در میان موجی مانند کوه‌ها (با خود) می‌برد و نوح (در وقت سوار شدن) پسرش را در حالیکه در کناری بود، صدا کرد که ای پسرک من! با ما سوار شو و با کافران مباش.');
INSERT INTO "badkhashani" VALUES(11,43,'11:43','(پسر در جواب) گفت: به زودی به‌سوی کوهی پناه می‌برم که مرا از آب نگاه دارد، نوح گفت: امروز هیچ نگاه دارنده‌ای از عذاب الله نیست، به جز کسی که الله به او رحم کند، (در این هنگام بود که) موج میان آن دو حائل شد، پس (پسر) از غرق شدگان شد.');
INSERT INTO "badkhashani" VALUES(11,44,'11:44','(و پس از انجامیدن امر الله، باز فرمان داد به زمین و آسمان و) گفته شد: ای زمین آب خود را فروبر و ای آسمان باران خود را ببند. و آب کم ساخته شد و حكم الله به جا آورده شد و کشتی بر کوه جودی قرار گرفت. و گفته شد: نابود باد قوم ستمکار.');
INSERT INTO "badkhashani" VALUES(11,45,'11:45','و نوح پروردگار خود را آواز داد و گفت: ای پروردگارم! یقینا پسرم از اهل بیت من است، و البته وعدۀ تو (به نجات اهل بیت من) راست است. و تو بهترین (عادل ترین) حکم کنندگانی.');
INSERT INTO "badkhashani" VALUES(11,46,'11:46','الله فرمود: ای نوح! در حقیقت او از اهل تو نیست، چون این (نمونۀ) عمل ناشایست است، پس چیزی را که به آن علم نداری از من مخواه، البته من به تو پند می‌دهم که مبادا از نادانان باشی.');
INSERT INTO "badkhashani" VALUES(11,47,'11:47','نوح گفت: ای پروردگارم، من به تو پناه می‌برم از اینکه چیزی را از تو بخواهم که به آن علم ندارم، و اگر مرا نیامرزی و بر من رحم نکنی از زیان کاران خواهم بود.');
INSERT INTO "badkhashani" VALUES(11,48,'11:48','گفته شد: ای نوح! فرود آی (از کشتی) با سلامتی از جانب ما با برکت‌های فرود آمده بر تو و بر امت‌هایی که با تو هستند و امت‌های دیگر‌اند که به زودی آنها را (در دنیا) بهره مند می‌کنیم باز به آنها از جانب ما (به خاطر ایمان نیاوردن شان) عذاب دردناک می‌رسد.');
INSERT INTO "badkhashani" VALUES(11,49,'11:49','این (داستان نوح) از خبرهای غیب (و خارج از علم مردم) است که آن را به تو وحی می‌کنیم، در حالیکه تو و قومت پیش از این آن را نمی‌دانستید، پس صبر کن، يقينا عاقبت نیک از پرهیزگاران است.');
INSERT INTO "badkhashani" VALUES(11,50,'11:50','و به‌سوی قوم عاد برادرشان هود را فرستادیم، (هود) گفت: ای قوم من! (تنها) الله را بپرستيد، جز او هیچ معبودی برحق برای شما نیست و شما (در گرفتن معبودها) جز افتراکنندگان (بر الله) نیستید.');
INSERT INTO "badkhashani" VALUES(11,51,'11:51','ای قوم من! بر (تبلیغ رسالت و بیان) آن مزدی از شما نمی‌خواهم، مزد من تنها بر عهدۀ کسی است که مرا آفریده است، پس آیا نمی‌فهمید؟');
INSERT INTO "badkhashani" VALUES(11,52,'11:52','ای قوم من! از پروردگار تان آمرزش بخواهید، باز به‌سوی او توبه کنید که آسمان را بر شما بارنده می‌کند، و قوتی بر قوت (سابق) تان می‌افزاید. و مجرمانه (از ایمان و توبه) روی نگردانید.');
INSERT INTO "badkhashani" VALUES(11,53,'11:53','گفتند: ای هود! برای ما دلیلی واضح نیاورده‌ای و ما به گفتۀ تو معبودان خود را ترک نخواهیم کرد و ما به تو ایمان نمی‌آوریم.');
INSERT INTO "badkhashani" VALUES(11,54,'11:54','دربارۀ تو چیزی نمی‌گوییم مگر این که بعضی معبودان ما به تو آسیبی رسانده است. هود گفت: من الله را گواه می‌گیرم و شما (هم) گواه باشید که من از آنچه (شما می‌پرستید و) شریک الله می‌سازید، بیزارم.');
INSERT INTO "badkhashani" VALUES(11,55,'11:55','به جز الله (از تمامی معبودانتان متنفرم، پس) همگی دربارۀ من به حیله و چاره جویی بپردازید، باز مرا مهلت ندهید.');
INSERT INTO "badkhashani" VALUES(11,56,'11:56','(چون) من بر الله، پروردگار خود و پروردگار شما توکل کرده‌ام، هیچ رونده‌ای نیست مگر اینکه او موی پیشانی او را در دست دارد (بر او مسلط است)، بی‌گمان پروردگارم بر راه راست است.');
INSERT INTO "badkhashani" VALUES(11,57,'11:57','پس اگر روی بگردانید، همانا من آنچه را که به رسانیدن آن به‌سوی شما فرستاده شده‌ام، رسانیدم. و پروردگارم (شما را هلاک نموده) قومی دیگر را به جای شما جانشین خواهد کرد و شما هیچ زیانی به او نمی‌رسانید. یقینا پروردگارم بر همه چیز (ناظر و) نگهبان است.');
INSERT INTO "badkhashani" VALUES(11,58,'11:58','و چون حکم ما (به هلاکت آنها) رسید، هود و کسانی را که با وی ایمان آورده بودند، به (واسطۀ) رحمتی از جانب خویش نجات دادیم و آنها را از عذاب سخت نجات دادیم.');
INSERT INTO "badkhashani" VALUES(11,59,'11:59','و این قبيلۀ عاد بودند که آیات پروردگار خود را انکار نمودند، و پیغمبرانش را نافرمانی کردند و از (دستور) هر متکبر و سرکش پیروی نمودند.');
INSERT INTO "badkhashani" VALUES(11,60,'11:60','و در این دنیا از پی آنها لعنت فرستاده شد. و در روز قیامت نیز، آگاه باشید که قوم عاد به پروردگار خود کفر ورزیدند، آگاه باشید که دوری (و هلاکت) بر عاد قوم هود است.');
INSERT INTO "badkhashani" VALUES(11,61,'11:61','و به‌سوی ثمود برادرشان صالح را فرستادیم، (صالح) گفت: ای قوم من! تنها الله را عبادت کنید (که) برای شما هیچ معبودی (برحق) نیست مگر الله (که) او شما را (در آغاز خلقت) از زمین آفرید و شما را آباد کنندگان زمین ساخت، پس از او آمرزش بخواهید، و باز به‌سوی او توبه کنید، که بی‌گمان پروردگار من نزدیک و قبول کنندۀ دعا است.');
INSERT INTO "badkhashani" VALUES(11,62,'11:62','گفتند: ای صالح! البته تو پیش از این در میان ما مایۀ امید بودی، آیا ما را از عبادت آنچه که پدران ما عبادت می‌کردند باز می‌داری؟ و يقينا ما راجع به چیزی که ما را به آن دعوت می‌دهی سخت در شکیم.');
INSERT INTO "badkhashani" VALUES(11,63,'11:63','(صالح) گفت: ای قوم من! آیا دیدید که اگر از سوی پروردگارم دليل واضح داشته باشم و از نزد خود رحمتی به من عطا کرده باشد، پس اگر از فرمان او نافرمانی کنم، چه کسی مرا در برابر (عقوبت) الله یاری خواهد کرد؟ (در نتیجه) شما جز بر زیان من نمی‌افزایید.');
INSERT INTO "badkhashani" VALUES(11,64,'11:64','ای قوم من! این ماده شتر (آفريدۀ) الله است که برای شما معجزه است، پس بگذارید آن را که در زمین الله بچرد، و هیچ آسیبی به او نرسانید که به عذاب زودرس گرفتار می‌شوید.');
INSERT INTO "badkhashani" VALUES(11,65,'11:65','پس آن ماده شتر را کشتند، پس (صالح) گفت: تا سه روز در خانه‌های خود برخوردار شوید، این وعدۀ بی‌دروغ است.');
INSERT INTO "badkhashani" VALUES(11,66,'11:66','پس وقتی که فرمان ما رسید صالح و کسانی را که همراه او ایمان آورده بودند به (سبب) رحمتی از سوی خویش نجات دادیم و از رسوایی آن روز رهایی بخشیدیم، البته پروردگارت توانای غالب است.');
INSERT INTO "badkhashani" VALUES(11,67,'11:67','و آنانی را که ظلم کرده بودند، آواز سخت فراگرفت، پس در خانه‌هایشان (مرده و) بر زانو افتیدند.');
INSERT INTO "badkhashani" VALUES(11,68,'11:68','گویی هرگز در آن جا سکونت نکردند، آگاه باشید که ثمود به پروردگارشان کفر ورزیدند، آگاه باشید! دوری (و هلاکت) بر ثمود باد.');
INSERT INTO "badkhashani" VALUES(11,69,'11:69','و البته فرستادگان (فرشتگان) ما برای ابراهیم مژده آوردند، و گفتند: سلام (بر تو ای ابراهیم)، گفت: سلام (بر شما باد)، پس دیری نپایید که ابراهیم گوسالۀ بریان آورد.');
INSERT INTO "badkhashani" VALUES(11,70,'11:70','پس چون دید که دست‌هایشان به آن (غذا) نمی‌رسد (و نمی‌خورند) این کار را از آنان ناپسند (و ناشناخته) دانست و از آنها در دل احساس خوف نمود، (فرشته‌ها) گفتند: نترس، چون ما به سوى قوم لوط فرستاده شده‌ایم.');
INSERT INTO "badkhashani" VALUES(11,71,'11:71','و زن ابراهیم ایستاده بود، خندید، پس ما او را به (تولد) اسحاق مژده دادیم، و از پی اسحاق به (تولد) يعقوب.');
INSERT INTO "badkhashani" VALUES(11,72,'11:72','گفت: (ساره) وای بر من! آیا فرزند می‌زایم حال آنکه من پیره زنم، و این شوهرم نیز پیر مرد است؟ واقعا این چیز بسیار عجیب است.');
INSERT INTO "badkhashani" VALUES(11,73,'11:73','فرشته‌ها گفتند: آیا از امر الله تعجب می‌کنی؟ (در حالیکه) رحمت الله و برکات او بر شماست ای اهل این خانه، بی‌گمان او ستوده (و) بزرگوار است.');
INSERT INTO "badkhashani" VALUES(11,74,'11:74','پس چون ترس از ابراهیم زایل شد و مژده (فرزند) به او رسید، شروع کرد به جدال کردن با ما دربارۀ (عذاب) قوم لوط.');
INSERT INTO "badkhashani" VALUES(11,75,'11:75','چون ابراهیم بسی بردبار، آه کشنده (دردمند) و توبه کار بود.');
INSERT INTO "badkhashani" VALUES(11,76,'11:76','(جواب آمد) ای ابراهیم! از این بحث و گفتگو بگذر، چون امر پروردگارت (به هلاک آنها) رسیده است و البته عذاب ردناشدنی بر آنها خواهد آمد.');
INSERT INTO "badkhashani" VALUES(11,77,'11:77','و چون فرستادگان ما نزد لوط آمدند از آمدنشان غمگین شد و بر آنها دل تنگ شد (از غم محافظت آن خوبرویان) و گفت: امروز، روزی سخت است.');
INSERT INTO "badkhashani" VALUES(11,78,'11:78','و قوم او دویده بسویش آمدند و پیش از آن هم کارهای زشت می‌کردند، لوط گفت: ای قوم من! این دختران (قوم) من‌اند، آنها برای شما (حلال و) پاکیزه ترند (پس با آنها نکاح کنید) و از الله بترسید و در بارۀ مهمانانم مرا رسوا و شرمنده نکنید، آیا در میان شما هیچ مرد رشیدی نیست (تا شما را از این کار زشت منع کند)؟');
INSERT INTO "badkhashani" VALUES(11,79,'11:79','(بدکاران قوم) گفتند: البته تو خوب دانسته‌ایی که ما به دخترانت هیچ حق و رغبتی نداریم (چون در نکاح ما نیستند) و يقينا تو خوب می‌دانی ما چه می‌خواهیم.');
INSERT INTO "badkhashani" VALUES(11,80,'11:80','لوط گفت: کاش در مقابل شما توانایی داشتم و یا پناهگاه محکمی (قوم و قبیلۀ نیرومند) داشتم که به آن پناه می‌بردم.');
INSERT INTO "badkhashani" VALUES(11,81,'11:81','(فرشته‌ها) گفتند: ای لوط! بی‌گمان ما فرستادگان پروردگار توییم، آنان هرگز به تو رسیده نمی‌توانند (تا گزندی بتو برسانند) پس در حصه‌ای از شب، خانواده ات را ببر و نباید کسی از شما به پشت سر خود بنگرد، مگر همسرت را (با خود نبر)، که هر آنچه به آنان برسد به او (هم) خواهد رسید، البته وعده گاه (نابودی) آنها صبح است، آیا صبح نزدیک نیست؟');
INSERT INTO "badkhashani" VALUES(11,82,'11:82','پس چون حكم ما آمد، آن (شهر و سرزمین) را زیر و رو کردیم. و آنجا را با سنگ گلی پیاپی از آسمان، سنگباران کردیم.');
INSERT INTO "badkhashani" VALUES(11,83,'11:83','سنگ‌هایی که نزد پروردگارت نشان دار بودند. و این چنین سنگباران (عذاب) از ظالمان دور نیست.');
INSERT INTO "badkhashani" VALUES(11,84,'11:84','و به‌سوی اهل مدین برادرشان شعیب را فرستادیم که گفت: ای قوم من الله را بپرستید برای شما غير از او (الله) هیچ معبودی (برحق) نیست، و پیمانه و ترازو را (در داد و گرفت) کم مکنید، چون من شما را در نعمت (مال و دارایی بسیار) می‌بینم، و در حقیقت بر شما از عذاب روز احاطه کننده (روز قیامت) می‌ترسم.');
INSERT INTO "badkhashani" VALUES(11,85,'11:85','و گفت، ای قوم من! پیمانه و ترازو را تمام و کمال به انصاف دهید و به مردم چیزهایشان را کم مدهید و در زمین فساد و تباهی نکنید.');
INSERT INTO "badkhashani" VALUES(11,86,'11:86','باقیماندۀ حلالی که الله می‌گذارد برایتان بهتر است، اگر ایمان دارید. و من بر شما محافظ نیستم (تا بر اعمال شما نظارت کنم).');
INSERT INTO "badkhashani" VALUES(11,87,'11:87','گفتند: ای شعیب! آیا نمازت به تو فرمان می‌دهد که آنچه را پدران ما می‌پرستیدند ترک کنیم؟ یا در اموال ما به میل خود تصرف نکنیم؟ البته تو بردبار و خردمند هستی!');
INSERT INTO "badkhashani" VALUES(11,88,'11:88','(شعيب) گفت: ای قوم من! به من خبر دهید اگر دليل واضحی از نزد پروردگارم داشته باشم و از نزد خود رزق و روزی خوبی به من داده باشد (آیا می‌توانم به خلاف دستورات او عمل کنم؟)، و نمی‌خواهم شما را از چیزی منع ‌کنم و خودم مرتکب آن شوم. من قصدی جز اصلاح ندارم. و توفيق من جز به تأیید الله نیست، تنها بر او توكل کرده ام و به‌سوی او باز می‌گردم.');
INSERT INTO "badkhashani" VALUES(11,89,'11:89','وای قوم من! جرم مخالفت شما با من، سبب نگردد که همان بلائی به شما برسد که به قوم نوح یا قوم هود یا قوم صالح رسید، و قوم لوط از شما دور نیست.');
INSERT INTO "badkhashani" VALUES(11,90,'11:90','و از پروردگار خود آمرزش بخواهید، باز به دربار او توبه کنید، همانا پروردگار من مهربان (و) دوستدار (نیکان) است.');
INSERT INTO "badkhashani" VALUES(11,91,'11:91','(به طور تمسخر) گفتند: ای شعیب! بسیاری از آنچه را که تو می‌گویی، نمی‌فهمیم و ما واقعا تو را در میان خود ناتوان می‌بینیم و اگر قبيلۀ تو نبود، حتما سنگسارت می‌کردیم. و تو بر ما گرانقدر نیستی.');
INSERT INTO "badkhashani" VALUES(11,92,'11:92','(شعيب) گفت: ای قوم من! آیا قبیله‌ام در نزد شما از الله گرامی‌تر است که او را پشت سر خویش قرار داده (و فراموشش کرده‌اید)، در حالیکه پروردگارم به آنچه می‌کنید احاطه دارد.');
INSERT INTO "badkhashani" VALUES(11,93,'11:93','وای قوم من! شما بر روش خود عمل کنید، من نیز بر روش خود عمل می‌کنم، به زودی خواهید دانست که عذاب رسوا کننده به چه کسی می‌رسد، (و خواهید دانست) چه کسی دروغگو است؟ انتظار بکشید، من هم با شما منتظرم.');
INSERT INTO "badkhashani" VALUES(11,94,'11:94','و چون امر ما (به هلاکت قوم) آمد، شعیب و آنانی را که با وی ایمان آورده بودند به رحمتی از جانب خود نجات دادیم و صدایی مرگبار ظالمان را گرفت، پس در خانه‌هایشان از پا در آمدند.');
INSERT INTO "badkhashani" VALUES(11,95,'11:95','گویا در آن قریه (قبلا) سکونت نکرده بودند، آگاه باش، دوری باد بر اهل مدین، همان طوری که ثمود دور افتادند.');
INSERT INTO "badkhashani" VALUES(11,96,'11:96','و البته موسی را با آیات (معجزات) خود و با دلیل ظاهر کنندۀ حق فرستادیم.');
INSERT INTO "badkhashani" VALUES(11,97,'11:97','به‌سوی فرعون و سران قوم او، ولی سران از امر فرعون پیروی کردند، حال آنکه امر فرعون راست و مایۀ هدایت نبود.');
INSERT INTO "badkhashani" VALUES(11,98,'11:98','او (فرعون) روز قیامت پیشاپیش قوم خود می‌رود، پس آنها را وارد آتش دوزخ خواهد ساخت. و دوزخ چه بدجای وارد شدن است برای واردان.');
INSERT INTO "badkhashani" VALUES(11,99,'11:99','در این دنیا و در روز قیامت (نیز) لعنت از پی آنان است، چه بد عطائی است که به آنها داده می‌شود!.');
INSERT INTO "badkhashani" VALUES(11,100,'11:100','این از خبرهای شهرهایی است که برای تو حکایت می‌کنیم، برخی از این شهرها هنوز بر سر پا باقی هستند و برخی (مانند کشت درو شده) از بیخ کنده شده‌اند.');
INSERT INTO "badkhashani" VALUES(11,101,'11:101','و ما بر آنها (ساکنان آن شهرها) ظلم نکردیم ولی آنها خود بر خویشتن ظلم کردند، پس معبودانشان را که به غیر از الله می‌خواندند وقتی که فرمان پروردگارت رسید چیزی را از آنان دفع نکردند و بر آنها جز نابودی و هلاکت نه افزودند.');
INSERT INTO "badkhashani" VALUES(11,102,'11:102','و گرفتن پروردگارت این چنین است؛ وقتی شهرهایی را عذاب دهد در حالیکه اهل آن ستمگر باشند، زیرا عذاب الله دردناک و سخت است.');
INSERT INTO "badkhashani" VALUES(11,103,'11:103','يقينا در این (داستانها) دلیل (عبرت) است برای کسی که از عذاب آخرت می‌ترسد، آن روز، روزیست که مردم برای آن جمع کرده می‌شوند و آن روز روزیست که همه مردم در آن حاضر کرده می‌شوند.');
INSERT INTO "badkhashani" VALUES(11,104,'11:104','و ما آن روز (قیامت) را به تأخیر نمی‌اندازیم، مگر تا مدت شمرده شده و معین.');
INSERT INTO "badkhashani" VALUES(11,105,'11:105','روزی که (قیامت) برسد هیچکس سخن نمی‌گوید مگر به اذن الله، پس (در آن روز) برخی از آنان بدبختند و برخی از آنان نیکبخت.');
INSERT INTO "badkhashani" VALUES(11,106,'11:106','اما آنانی که بدبخت شده‌اند پس در آتش دوزخ باشند، برای آنها در آنجا فریاد (نفس با آواز) و ناله (نفس بی‌آواز و پست) است.');
INSERT INTO "badkhashani" VALUES(11,107,'11:107','تا آسمان‌ها و زمین باقی است، ایشان در آن جاودانه می‌مانند مگر آنچه پروردگارت بخواهد، بی‌گمان پروردگار تو هر کاری را که بخواهد انجام می‌دهد.');
INSERT INTO "badkhashani" VALUES(11,108,'11:108','و اما کسانی که نیکبخت شده‌اند، پس در جنت همیشه به سر می‌برند و در آنجا تا زمانی که آسمان‌ها و زمین باقی است جاودانه‌اند، مگر آنچه پروردگارت بخواهد، این بخششی پیوسته و ناگسستنی است.');
INSERT INTO "badkhashani" VALUES(11,109,'11:109','پس در بارۀ (بطلان) آنچه می‌پرستند در شک مباش، آنها همان گونه (چیزهایی را) می‌پرستند که پدرانشان در گذشته می‌پرستیدند، و ما نصيب آنها (از عذاب) را کامل و بدون هیچ کم و کاستی به آنان خواهیم داد.');
INSERT INTO "badkhashani" VALUES(11,110,'11:110','و البته ما به موسی کتاب دادیم، پس در آن اختلاف واقع شد، و اگر سخن پروردگارت از پیش بر این نرفته بود حتما در میان آنان فيصله می‌شد و آنان سخت در شک و تردیدند.');
INSERT INTO "badkhashani" VALUES(11,111,'11:111','و البته پروردگارت (جزای) اعمال همگی را کامل و بدون کم و کاست می‌دهد و او به آنچه می‌کنند آگاه است.');
INSERT INTO "badkhashani" VALUES(11,112,'11:112','پس همانطور که دستور یافته‌ای استقامت کن و (نیز) کسی که همراه تو توبه کرده (و روی به الله آورده است باید چنین کند) و (از راه راست) سرکشی مکنید، بی‌گمان او به آنچه می‌کنید بیناست.');
INSERT INTO "badkhashani" VALUES(11,113,'11:113','و به‌سوی ظالمان (مشرکان) متمایل نشوید که آتش دوزخ به شما خواهد رسید، و جز الله دوست و کارسازی ندارید، باز نصرت داده نمی‌شوید.');
INSERT INTO "badkhashani" VALUES(11,114,'11:114','و در دو طرف روز (بامداد و مغرب) نماز را برپا دار و در ساعتی از شب، چون نیکی‌ها بدی‌ها را محو می‌سازند، این (قرآن) پندی است برای پندپذيران.');
INSERT INTO "badkhashani" VALUES(11,115,'11:115','و صبر پیشه کن که همانا (صبر عمل نیک است و) الله ثواب نیکوکاران را ضائع نمی‌کند.');
INSERT INTO "badkhashani" VALUES(11,116,'11:116','پس چرا در قرنهای پیش از شما صاحبان فضل نبودند که از فساد در روی زمین نهی می‌کردند، مگر اندکی (بودند) از کسانی که از میان آنها نجات‌شان دادیم. و ظالمان به دنبال خوشگذرانی و نعمتی که در آن بودند، رفتند. و آنان مجرمان بودند.');
INSERT INTO "badkhashani" VALUES(11,117,'11:117','و پروردگار تو هرگز بر آن نبوده است که شهرها را به ظلم هلاک سازد، در حالیکه اهل آن مصلح (و نیکوکار) باشند.');
INSERT INTO "badkhashani" VALUES(11,118,'11:118','و اگر پروردگار تو (اسلام اجباری مردم را) می‌خواست، البته همه مردم را امت واحد (تابع توحید) می‌گردانید، (ولی مردم) همیشه در اختلاف‌اند.');
INSERT INTO "badkhashani" VALUES(11,119,'11:119','مگر کسانی که پروردگارت بر آنها رحم کرده، و (الله) برای همین مردم را آفریده است و تمام شد سخن پروردگار تو که البته دوزخ را از جن و انس همگی پر خواهم کرد.');
INSERT INTO "badkhashani" VALUES(11,120,'11:120','و هر یک از سرگذشت پیغمبران را بر تو بیان می‌کنیم، آنچه که دلت را به آن استوار و محکم می‌گردانیم، و در این (سوره) برای تو حق آمده است، و برای مؤمنان پند و یادآوری آمده است.');
INSERT INTO "badkhashani" VALUES(11,121,'11:121','و به آنانی که ایمان نمی‌آورند بگو: شما بر شیوه و طریقۀ خود عمل کنید که ما نیز عمل می‌کنیم.');
INSERT INTO "badkhashani" VALUES(11,122,'11:122','و شما منتظر (نتیجۀ اعمال خود) باشید و ما هم منتظریم.');
INSERT INTO "badkhashani" VALUES(11,123,'11:123','و (دانستن) غیب آسمان‌ها و زمین خاص از الله است. و همه کارها به‌سوی او بازگردانده می‌شود، پس او را عبادت کن و بر او توکل کن. و پروردگارت از آنچه می‌کنید غافل نیست.');
INSERT INTO "badkhashani" VALUES(12,1,'12:1','الر (مفهوم این حروف به الله معلوم است)، این آیات، آیات کتاب واضح (و بیانگر حق و باطل) است.');
INSERT INTO "badkhashani" VALUES(12,2,'12:2','چون ما آن را قرآن )خواندنی و( به زبان عربی نازل کردیم تا (مفهوم آن را) درک کنید.');
INSERT INTO "badkhashani" VALUES(12,3,'12:3','ما بهترین داستان‌ها را، با وحی فرستادن و به وسیلۀ این قرآن بر تو حکایت می‌کنیم، حال آنکه پیش از این از بی‌خبران بودی.');
INSERT INTO "badkhashani" VALUES(12,4,'12:4','(آن داستان این است که) چون یوسف به پدرش گفت: ای پدر (بزرگوارم)! من یازده ستاره و آفتاب و ماه را در خواب دیدم، دیدم که آنها برایم سجده می‌کنند.');
INSERT INTO "badkhashani" VALUES(12,5,'12:5','(يعقوب) گفت: ای پسرک من! خوابت را به برادرانت حکایت مکن که برای تو حیله سازی می‌کنند، زیرا شیطان برای انسان دشمن آشکار است.');
INSERT INTO "badkhashani" VALUES(12,6,'12:6','و اینچنین پروردگارت تو را بر می‌گزیند و تعبیر خوابها را به تو می‌آموزد و نعمت خود را بر تو و فرزندان یعقوب کامل می‌کند همانطور که پیش از این بر پدرانت ابراهیم و اسحاق کامل نمود. بی‌گمان پروردگارت دانای باحکمت است.');
INSERT INTO "badkhashani" VALUES(12,7,'12:7','البته در سرگذشت یوسف و برادرانش برای پرسندگان نشانه‌هاست.');
INSERT INTO "badkhashani" VALUES(12,8,'12:8','وقتی که (برادران یوسف در مشورۀ خود) گفتند: یقینا یوسف و برادرش (بنیامین) نزد پدر ما نسبت به ما دوست داشتنی‌ترند، در حالیکه ما جمعی نیرومند هستیم، واقعا پدر ما در خطای آشکار است.');
INSERT INTO "badkhashani" VALUES(12,9,'12:9','(لذا) يوسف را بکشید یا او را در سرزمین (دور) بیندازید تا توجه پدرتان به شما معطوف گردد، و بعد از او (پس از قتل يوسف، با توبه کردن) مردمان نیک و شایسته باشید.');
INSERT INTO "badkhashani" VALUES(12,10,'12:10','گوینده‌ای از آنان گفت: یوسف را نکشید بلکه او را در قعر چاهی باندازید، تا کسی از مسافران او را بگیرد، اگر می‌خواهید کاری بکنید.');
INSERT INTO "badkhashani" VALUES(12,11,'12:11','گفتند: ای پدر (مهربان ما)! چرا در بارۀ یوسف به ما اطمینان نداری حال آنکه ما خیر خواه او هستیم؟');
INSERT INTO "badkhashani" VALUES(12,12,'12:12','(اگر ما را خیر خواه او میدانی) فردا او را با ما بفرست تا بخورد و بازی کند و البته ما نگهبان و مراقب او خواهیم بود.');
INSERT INTO "badkhashani" VALUES(12,13,'12:13','(پدر) گفت: اینکه او را با خود ببرید، مرا سخت غمگین می‌کند و (نیز) می‌ترسم از آن که او را گرگ بخورد و شما از او بی‌خبر باشید.');
INSERT INTO "badkhashani" VALUES(12,14,'12:14','(پسران) گفتند: اگر او را گرگ بخورد با اینکه ما (بر دفع گرگ) گروه نیرومند هستیم، در این صورت يقينا زیانکار خواهیم بود.');
INSERT INTO "badkhashani" VALUES(12,15,'12:15','پس وقتی او را بردند و تصمیم گرفتند که او را در قعر چاه بیندازند (در همان لحظه) به او (يوسف) وحی کردیم که البته آنها را از این کارشان آگاه خواهی ساخت، در حالیکه نمی‌فهمند.');
INSERT INTO "badkhashani" VALUES(12,16,'12:16','و شب گریان نزد پدر آمدند.');
INSERT INTO "badkhashani" VALUES(12,17,'12:17','گفتند: ای پدر! ما رفتیم که مسابقه دهیم، و يوسف را نزد سامان و اسباب ما گذاشتیم، پس گرگ او را خورد، ولی تو باور کنندۀ ما نیستی اگر چه راستگو باشیم.');
INSERT INTO "badkhashani" VALUES(12,18,'12:18','و پیراهن او را آلوده به خون دروغ آوردند، پدر گفت: نه، بلکه نفس‌های تان کار زشت را برای شما آراسته است، پس کار من صبر نیک است و راجع به آنچه بیان می‌کنید، از الله مدد (و یاری) می‌خواهم.');
INSERT INTO "badkhashani" VALUES(12,19,'12:19','و کاروانی (نزدیک چاه) آمد پس آب آور خود را فرستادند، پس دلو خود را انداخت (وقتی به جای آب، یوسف را دید) گفت: مژده باد! این یک پسر است، و او را به طور متاعی پنهان ساختند، و الله به آنچه می‌کردند داناست.');
INSERT INTO "badkhashani" VALUES(12,20,'12:20','و او را به پول ناچیزی (و تنها) به چند درهم شمرده شده فروختند و در بارۀ او بی‌علاقه بودند.');
INSERT INTO "badkhashani" VALUES(12,21,'12:21','و آن کس از اهل مصر که او را خریده بود به همسرش گفت: او را گرامی دار شاید به حال ما نفعی برساند، یا او را به فرزندی بگیریم. و این گونه ما یوسف را در سرزمین تمکین دادیم و تا تعبیر خوابها را به او بیاموزیم. و الله بر کار خود غالب است، اما بیشتر مردم نمی‌دانند.');
INSERT INTO "badkhashani" VALUES(12,22,'12:22','و چون به كمال جوانی خود رسید به او حكمت و دانایی دادیم، و این چنین به نیکوکاران پاداش می‌دهیم.');
INSERT INTO "badkhashani" VALUES(12,23,'12:23','و آن زنی که یوسف در خانه اش بود خواست تا از او کام گیرد (و از پاکدامنی او را بیرون آورد)، و درها را بست و گفت: پیش بیا که از توام، یوسف گفت: (از این کار زشت) به الله پناه می‌برم و او (عزیز مصر) آقای من است، (جای) مرا نیکو ساخت، بی‌گمان ظالمان رستگار نمی‌شوند.');
INSERT INTO "badkhashani" VALUES(12,24,'12:24','البته آن زن قصد (مقاربت با) یوسف کرد. (ولی) اگر یوسف برهان پروردگارش را نمی‌دید، او نیز قصد (مقاربت با) آن زن می‌کرد. ما این چنین کردیم تا بدی و فحشا را از او دور سازیم، چون که او از بندگان مخلص و برگزیدۀ ما بود.');
INSERT INTO "badkhashani" VALUES(12,25,'12:25','و هر دو به طرف دروازه بر یکدیگر سبقت کردند و آن زن پیراهن یوسف را از پشت پاره کرد. و شوهرش را نزدیک دروازه یافتند. (زن به شوهر خود) گفت: جزای کسی که قصد بدی به همسرت کرده چیست؟ جز این (نیست) که زندانی گردد یا عذاب دردناک (داده) شود.');
INSERT INTO "badkhashani" VALUES(12,26,'12:26','یوسف (در جواب) گفت: این زن مرا به سوی خود خواست، (ولی من انکار کردم و از وی فرار می‌نمودم) و شاهدی از خانوادۀ آن زن گواهی داد که اگر پیراهن او از پیش روی پاره شده باشد، پس آن زن راست گفته است و یوسف از دروغگویان است.');
INSERT INTO "badkhashani" VALUES(12,27,'12:27','و اگر پیراهنش از پشت پاره شده باشد، پس این زن دروغ گفته است و یوسف از راستگویان است.');
INSERT INTO "badkhashani" VALUES(12,28,'12:28','پس چون (شوهر) دید که پیراهن یوسف از پشت پاره شده، گفت: يقينا این (ماجرا) از مکر شما زنان است، زیرا مکر شما زنان بزرگ است.');
INSERT INTO "badkhashani" VALUES(12,29,'12:29','(عزیز مصر گفت:) ای یوسف! تو از افشای این کار در گذر و تو ای زن! برای گناه خود آمرزش بخواه، چون از خطاکاران بودی.');
INSERT INTO "badkhashani" VALUES(12,30,'12:30','و عده‌ای از زنان در شهر گفتند: زن عزیز از غلامش کام می‌خواهد تا او را از پاکدامنی غافل کند، به راستی محبت (یوسف) در دلش جای گرفته است، البته او را در گمراهی آشکار می‌بینیم.');
INSERT INTO "badkhashani" VALUES(12,31,'12:31','پس چون زن عزیز مکر زنان را شنید، کسی را به‌سوی زنان فرستاد و برای آنها مجلسی آماده کرد و به دست هر یک از آنها کاردی داد و گفت: (به يوسف) بر زنان بیرون آی. پس چون زنان او را دیدند او را (در حسن و جمال) بزرگ یافتند و دست‌های خود را (از شدت مشغول شدن به جمال او) بریدند و گفتند: سبحان الله (پاکی است الله را) این بشر نیست (بلکه) این جز فرشته بزرگوار نیست.');
INSERT INTO "badkhashani" VALUES(12,32,'12:32','زن عزیز گفت: این همان جوانی است که مرا دربارۀ محبت او ملامت کردید، و البته من او را بسوی خود طلب کردم، ولی خود را پاک داشت. و اگر آنچه را که به او دستور میدهم انجام ندهد، حتما زندانی می‌شود و حتما از خوار شدگان خواهد شد.');
INSERT INTO "badkhashani" VALUES(12,33,'12:33','گفت: پروردگارا! زندان برای من دوست داشتنی‌تر است از چیزی که مرا به‌سوی آن می‌خوانند. و اگر مکر آن زنان را از من باز نداری به‌سوی آنان خواهم گروید و از جملۀ نادانان می‌گردم.');
INSERT INTO "badkhashani" VALUES(12,34,'12:34','پس پروردگارش دعای او را قبول کرد و مکر زنان را از او باز داشت. يقينا او شنوای داناست.');
INSERT INTO "badkhashani" VALUES(12,35,'12:35','باز به نظرشان رسید پس از آنکه علامات (پاکی یوسف) را دیدند که البته او را تا مدتی زندانی کنند.');
INSERT INTO "badkhashani" VALUES(12,36,'12:36','و دو جوان با یوسف به زندان در آمدند، یکی از آن دو گفت: من در خواب دیدم که انگور (برای شراب) می‌فشارم، و دیگری گفت: من در خواب دیدم که بر سر خویش نانی برداشته ام که پرنده‌ها از آن می‌خورند، ما را از تعبیر این خواب آگاه کن که تو را از جملۀ نیکوکاران می‌بینیم.');
INSERT INTO "badkhashani" VALUES(12,37,'12:37','یوسف گفت: پیش از آنکه غذایتان به شما برسد، شما را از تعبیر خوابتان آگاه خواهم ساخت، این (تعبیر خواب) که به شما می‌گویم از چیزهایی است که پروردگارم به من آموخته است. همانا من آیین قومی را که به الله ایمان نمی‌آورند و منکر آخرت نیز هستند، ترک کرده‌ام.');
INSERT INTO "badkhashani" VALUES(12,38,'12:38','بلکه دین پدرانم ابراهیم و اسحق و یعقوب را پیروی کرده ام، برای ما شایسته نیست که با الله چیزی را شریک گردانیم، این (ایمان و توحید) از فضل الله بر ما و بر مردم است، ولی بیشتر مردم شکر گزاری نمی‌کنند.');
INSERT INTO "badkhashani" VALUES(12,39,'12:39','(یوسف گفت:) ای دوستان زندانی ام! آیا معبودهای پراگنده بهتر است یا الله یگانۀ غالب (بر همه مخلوقات)؟');
INSERT INTO "badkhashani" VALUES(12,40,'12:40','غير از او نمی‌پرستید مگر نام‌هایی را که شما و پدرانتان وضع کرده‌اید، الله (هیچ) دلیل و برهانی بر (صحت) آنان نفرستاده است. فرمانروایی (کائنات) تنها از الله است و امر کرده که جز او را نپرستید، این است دین راست و استوار، ولی بیشتر مردم نمی‌دانند.');
INSERT INTO "badkhashani" VALUES(12,41,'12:41','ای دوستان زندانی ام! اما یکی از شما (مثل سابق) به آقای خود شراب می‌نوشاند، اما آن دیگر به دار کشیده می‌شود و پرنده‌ها از سر او می‌خورند. (باز گفت:) امری که شما دوتن جویای (تعبير آن) از من شدید، فيصله شده است.');
INSERT INTO "badkhashani" VALUES(12,42,'12:42','و به یکی از آن دو که گمان می‌کرد نجات می‌یابد، گفت: مرا در نزد آقای خود یاد کن، ولی شیطان یادآوری (قضیۀ) یوسف را نزد آقایش از یاد آن (جوان) برد؛ در نتيجه، (یوسف) چند سال در زندان ماند.');
INSERT INTO "badkhashani" VALUES(12,43,'12:43','و پادشاه گفت: من در خواب هفت گاو فربه را دیدم که هفت گاو لاغر، آنها را می‌خوردند، و هفت خوشه سبز و هفت خوشه خشک دیگر را دیدم. ای سران قوم! در بارۀ خوابم به من نظر دهید، اگر خواب را تعبیر می‌کنید.');
INSERT INTO "badkhashani" VALUES(12,44,'12:44','(سران قوم) گفتند: این خواب‌های پریشان کننده و بی‌اساس است و ما به تعبیر خواب‌های پریشان و بی‌اساس آگاهی نداریم.');
INSERT INTO "badkhashani" VALUES(12,45,'12:45','و کسی که از آن دو نفر نجات یافته بود بعد از مدتها به یاد آورد (و) گفت: من شما را از تعبیر آن باخبر میسازم، پس مرا (نزد یوسف) بفرستید.');
INSERT INTO "badkhashani" VALUES(12,46,'12:46','(پس نزد یوسف رفته و گفت:) ای یوسف! ای مرد راستگو! دربارۀ هفت گاو فربه که هفت گاو لاغر آنها را می‌خورند، و (دربارۀ) هفت خوشۀ سبز و هفت خوشۀ خشک به ما نظر ده تا اینکه من به‌سوی مردم برگردم، امید است آنان بدانند.');
INSERT INTO "badkhashani" VALUES(12,47,'12:47','يوسف گفت: هفت سال پی در پی کشت کنید و آنچه را که درو نمودید جز اندکی که می‌خورید در خوشۀ آن بگذارید.');
INSERT INTO "badkhashani" VALUES(12,48,'12:48','پس از آن، هفت سال سخت می‌آید که آنچه را به خاطر آن ذخیره کرده‌اید می‌خورند مگر اندکی از آنچه که ذخیره می‌کنید.');
INSERT INTO "badkhashani" VALUES(12,49,'12:49','باز بعد از آن (سالهای خشک و سخت) سالی می‌آید که به مردم (در آن سال) باران می‌رسد، و در آن شیره (انگور و زیتون و دیگر میوه‌ها) را می‌فشارند.');
INSERT INTO "badkhashani" VALUES(12,50,'12:50','و پادشاه گفت: یوسف را پیش من بیاورید، چون قاصد نزد او آمد، گفت: پیش آقایت باز گرد و از او بپرس ماجرای زنانی که دست‌هایشان را بریدند چه بود؟ و یقینا پروردگارم به مکر آنان داناست.');
INSERT INTO "badkhashani" VALUES(12,51,'12:51','پادشاه (زنان را جمع کرده) گفت: جریان شما چه بود آنگاه که خواستید یوسف را به خود بخوانید؟ گفتند: پناه بر الله، هیچ گناهی از او سراغ نداریم. زن عزیز گفت: اکنون حق آشکار شد، این من بودم که او را به‌سوی خود خواندم (ولی او به من روی نداد) و يقينا او از راستگویان است.');
INSERT INTO "badkhashani" VALUES(12,52,'12:52','این (در خواست من) به آن خاطر است که او (عزیز مصر) بداند من در نهان به او خیانت نکرده ام و الله مكر خیانت کاران را به سر نمی‌رساند.');
INSERT INTO "badkhashani" VALUES(12,53,'12:53','و من نفس خود را تبرئه نمی‌کنم؛ چرا که نفس حتما بسیار به بدی امر می‌کند، مگر نفس کسی که پروردگارم به او رحم کند، چون پروردگارم آمرزندۀ مهربان است.');
INSERT INTO "badkhashani" VALUES(12,54,'12:54','و پادشاه (بار دوم) گفت: او را نزد من بیاورید تا وی را از مخصوص (مشورۀ خود و از افراد مقرب) خود گردانم. پس چون با او به سخن پرداخت، گفت: البته تو امروز نزد ما ارجمند و امانتدار (و قابل اعتماد) هستی.');
INSERT INTO "badkhashani" VALUES(12,55,'12:55','يوسف گفت: مرا بر خزانه‌های مصر مقرر کن، چون که من نگاهدارندۀ دانا هستم.');
INSERT INTO "badkhashani" VALUES(12,56,'12:56','این چنین یوسف را در این سرزمین قدرت و نعمت (و نفوذ) دادیم، که در آن هر جا که بخواهد قرار گیرد، ما نعمت خود را به هر کس که بخواهیم می‌رسانیم و مزد نیکوکاران را ضایع نمی‌گردانیم.');
INSERT INTO "badkhashani" VALUES(12,57,'12:57','و البته ثواب آخرت برای آنانی که ایمان آورده و پرهیزگاری می‌کردند (نسبت به پادشاهی دنیا) بهتر است.');
INSERT INTO "badkhashani" VALUES(12,58,'12:58','و برادران یوسف (برای حصول غله) به مصر آمدند، پس بر او وارد شدند، یوسف آنان را شناخت ولی آنها يوسف را نشناختند.');
INSERT INTO "badkhashani" VALUES(12,59,'12:59','و هنگامی که يوسف بارهای آنها را آماده کرد، گفت: (بار دیگر) برادر پدری خود را نزد من آورید، مگر نمی‌بینید که من پیمانه را به تمام و کمال میدهم و من بهترین میزبانان هستم؟');
INSERT INTO "badkhashani" VALUES(12,60,'12:60','پس اگر او را نزد من نیاوردید، پس برای شما هرگز پیمانۀ غله نزد من نیست و هرگز به من نزدیک نشوید.');
INSERT INTO "badkhashani" VALUES(12,61,'12:61','گفتند: دربارۀ او با پدرش گفتگو نموده تلاش می‌کنیم که (به هر وسیله ممکن او را نزد تو بیاوریم) و این کار را خواهیم کرد.');
INSERT INTO "badkhashani" VALUES(12,62,'12:62','و به جوانان (خدمتگذار خود) گفت: سرمایۀ (پول)‌شان را (که پرداخته اند) در بارهایشان بگذارید، تا چون به نزد خانواده‌شان بازگردند آن را بازیابند و شاید (دوباره) برگردند.');
INSERT INTO "badkhashani" VALUES(12,63,'12:63','پس چون به‌سوی پدر خویش باز گشتند، گفتند: ای پدر! پیمانۀ غله از ما منع شد، پس برادر ما را با ما بفرست تا پیمانه بگیریم و همانا ما نگهبان او هستیم.');
INSERT INTO "badkhashani" VALUES(12,64,'12:64','پدر گفت: آیا من دربارۀ او به شما اطمینان کنم همانگونه که دربارۀ برادرش (یوسف) پیش از این به شما اطمینان کردم؟! پس الله بهترین نگهبان و او مهربانترین مهربانان است.');
INSERT INTO "badkhashani" VALUES(12,65,'12:65','و چون بارشان را باز کردند، دیدند که سرمایۀ (پول)‌شان به آنان بازگردانده شده است. گفتند: ای پدر! ما دیگر چه می‌خواهیم؟ این سرمایۀ ماست که به ما بازگردانده شده است و (باز می‌رویم تا) برای خانوادۀ خود آذوقه بیاوریم و برادر خود را محافظت می‌کنیم و یک بار شتر اضافه خواهیم آورد، و این مقدار (برای ما کم و) ناچیز است.');
INSERT INTO "badkhashani" VALUES(12,66,'12:66','يعقوب گفت: هرگز او را با شما نمی‌فرستم تا اینکه عهدی به نام الله به من بدهید که او را حتما نزد من بر می‌گردانید، مگر اینکه گرفتار شوید (و از توان شما خارج گردد)، پس وقتی با پدر عهد بستند، يعقوب گفت: الله بر آنچه می‌گوییم وکیل (آگاه و ناظر) است.');
INSERT INTO "badkhashani" VALUES(12,67,'12:67','و (یعقوب از باب احتیاط) گفت: ای پسران من! از یک دروازه داخل نشوید، بلکه از دروازه‌های مختلف وارد شوید، و نمی‌توانم چیزی را که الله مقرر کرده است از شما دور کنم، (چون) حكم (نفع و ضرر) تنها و خاص به دست الله است، بر او توکل کرده ام و توکل کنندگان باید بر او توکل کنند.');
INSERT INTO "badkhashani" VALUES(12,68,'12:68','و چون همان طور که پدرشان به آنان دستور داده بود، وارد شدند، (چنین ورود) نمی‌توانست آنچه را که الله خواسته بود از آنان دفع کند، ولی آنچه را که در دل يعقوب بود برآورده کرد، يقينا يعقوب به خاطر اینکه ما به او آموخته بودیم، دارای علم بزرگی بود. ليكن بسیاری از مردم نمی‌دانند.');
INSERT INTO "badkhashani" VALUES(12,69,'12:69','و چون (بار دوم) بر يوسف وارد شدند، برادرش (بنیامین) را نزد خود جای داد. گفت: من برادر تو (یوسف) هستم، پس از آنچه کرده‌اند اندوهگین مباش.');
INSERT INTO "badkhashani" VALUES(12,70,'12:70','پس چون يوسف آنان را با آذوقه و سامان‌شان مجهز کرد، جام آبخوری (پادشاه) را در بار برادرش نهاد. باز ندا دهنده‌ای ندا داد: ای کاروان! حتما شما دزد هستید.');
INSERT INTO "badkhashani" VALUES(12,71,'12:71','روی به‌سوی ایشان کرده و گفتند: چه چیز را گم کرده‌اید؟');
INSERT INTO "badkhashani" VALUES(12,72,'12:72','گفتند: جام پادشاه را گم کرده‌ایم و برای هر کس که آنرا بیاورد بار شتر (از غله) خواهد بود و من ضامن این (وعده) هستم.');
INSERT INTO "badkhashani" VALUES(12,73,'12:73','(برادران یوسف) گفتند: به الله قسم که شما خوب دانسته‌اید که ما در سرزمین مصر برای فساد نیامده‌ایم و ما (پیش از این) دزد نبودیم.');
INSERT INTO "badkhashani" VALUES(12,74,'12:74','(مأموران) گفتند: پس سزای او (دزد) چیست اگر دروغگو باشید؟');
INSERT INTO "badkhashani" VALUES(12,75,'12:75','گفتند: سزایش این است که هر کس آن جام در بارش یافت شود پس به سزای دزدی اسیر گردد، ما این چنین ظالمان را سزا می‌دهیم.');
INSERT INTO "badkhashani" VALUES(12,76,'12:76','پس پیش از بار برادرش، به تفتیش نمودن بارهای دیگران آغاز کرد، باز پیمانه را از بار برادرش بیرون آورد، ما اینگونه برای یوسف تدبیر آموختیم، زیرا او نمی‌توانست در دین پادشاه برادرش را بگیرد، مگر اینکه الله بخواهد، درجات هر کس را که بخواهیم بالا می‌بریم و فوق هر دانایی دانایی بزرگتر هست.');
INSERT INTO "badkhashani" VALUES(12,77,'12:77','(برادرانش) گفتند: اگر او دزدی کند (بعید نیست) چون برادرش پیش از او دزدی کرده بود. پس یوسف آن را در دل خود پنهان نمود و آن را برایشان آشکار نکرد. (و در دل خویش) گفت: شما مقام بدتری دارید و الله به آنچه وصف می‌کنید داناتر است.');
INSERT INTO "badkhashani" VALUES(12,78,'12:78','گفتند: ای عزیز؛ او پدر پیر و سالخورده‌ای دارد، پس یکی از ما را به جای او بگیر، یقینا تو را از نیکوکاران می‌بینیم.');
INSERT INTO "badkhashani" VALUES(12,79,'12:79','يوسف گفت: به الله پناه می‌برم از اینکه غیر از کسی را که متاع خود را نزد او یافته‌ایم بگیریم، ما در آن صورت از ظالمان خواهیم بود.');
INSERT INTO "badkhashani" VALUES(12,80,'12:80','و هنگامی که از او ناامید شدند در خلوتی برای مشورت نشستند، بزرگ آنان گفت: آیا ندانسته‌اید که پدرتان از شما به نام الله عهد محکم گرفته است و پیش از این دربارۀ يوسف کوتاهی کرده‌اید؟ من هرگز از این سرزمین حرکت نمی‌کنم مگر اینکه پدرم به من اجازه دهد، یا الله در حق من حکم کند و او بهترین حکم کنندگان است.');
INSERT INTO "badkhashani" VALUES(12,81,'12:81','نزد پدرتان برگردید و بگویید: ای پدر! پسرت دزدی کرده است، و گواهی ندادیم جز به آنچه دانستیم و ما نگهبان علم غیب نبودیم.');
INSERT INTO "badkhashani" VALUES(12,82,'12:82','و از (مردم) دهی که در آن بودیم و از کاروانی که با آن آمدیم بپرس، و يقينا ما راستگوییم.');
INSERT INTO "badkhashani" VALUES(12,83,'12:83','يعقوب گفت: (نه) بلکه نفس‌های تان امری (زشتی) را برای شما آراسته است، پس کار من صبر نیک است، امید که الله همۀ آنها را یکجا نزد من بیارد، چون او دانای حکیم است.');
INSERT INTO "badkhashani" VALUES(12,84,'12:84','و از آنان روی گرداند و گفت: ای دریغ بر یوسف! و چشمانش از اندوه سفید و نابینا گردید درحالیکه بسیار غمگین بود.');
INSERT INTO "badkhashani" VALUES(12,85,'12:85','پسران گفتند: قسم به الله همیشه یوسف را یاد می‌کنی تا بیمار (و نزدیک به مرگ) شوی یا از هلاک شدگان گردی.');
INSERT INTO "badkhashani" VALUES(12,86,'12:86','يعقوب گفت: من پریشانی حال و اندوهم را تنها به الله شکایت می‌کنم و از سوی الله چیزهایی را می‌دانم که شما نمی‌دانید.');
INSERT INTO "badkhashani" VALUES(12,87,'12:87','ای پسران من! بروید و از یوسف و برادرش جستجو کنید و از رحمت الله ناامید مشوید، زیرا جز قوم کافر از رحمت الله ناامید نمی‌شود.');
INSERT INTO "badkhashani" VALUES(12,88,'12:88','و هنگامی که (بار سوم) پیش یوسف رفتند، گفتند: ای عزیز! به ما و خانواده ما سختی رسیده است و سرمایه اندک و ناچیز آورده‌ایم، پس پیمانه ما را کامل بده و بر ما (بيشتر) بخشش کن، چون الله صدقه دهندگان را پاداش می‌دهد.');
INSERT INTO "badkhashani" VALUES(12,89,'12:89','یوسف گفت: آیا به یاد دارید که با یوسف و برادرش وقتی که نادان بودید چه کردید؟');
INSERT INTO "badkhashani" VALUES(12,90,'12:90','گفتند: آیا واقعا تو یوسف هستی؟ گفت: بلی! من يوسف هستم و این برادر من است، الله بر ما منت نهاده است. چون هر کس تقوا و صبر پیشه کند، بی‌گمان الله پاداش نیکوکاران را ضایع نمی‌کند.');
INSERT INTO "badkhashani" VALUES(12,91,'12:91','(برادرانش) گفتند: قسم به الله که الله تو را بر ما برتری داده است و در حقیقت ما خطاکار بودیم.');
INSERT INTO "badkhashani" VALUES(12,92,'12:92','یوسف گفت: امروز بر شما هیچ سرزنش و ملامتی نیست. الله شما را می‌آمرزد و او مهربان‌ترین مهربانان است.');
INSERT INTO "badkhashani" VALUES(12,93,'12:93','(و به برادران خود گفت:) این پیراهن مرا با خود ببرید و آن را بر روی پدرم بیاندازید تا بینا گردد و اهل خویش را همه یکجا نزد من بیاورید.');
INSERT INTO "badkhashani" VALUES(12,94,'12:94','و چون کاروان (برادران یوسف به‌سوی شام) حرکت کرد، پدرشان گفت: اگر مرا به بی‌خردی نسبت ندهید همانا من بوی یوسف را می‌یابم.');
INSERT INTO "badkhashani" VALUES(12,95,'12:95','(اهل خانه) گفتند: قسم به الله که تو در همان خطای قدیمت هستی.');
INSERT INTO "badkhashani" VALUES(12,96,'12:96','پس چون مژده دهنده آمد، و پیراهن را بر روی یعقوب انداخت پس او بینا گشت، و گفت: آیا به شما نگفته بودم که از جانب الله چیزهایی را می‌دانم که شما نمی‌دانید؟');
INSERT INTO "badkhashani" VALUES(12,97,'12:97','(پس از رسیدن به کنعان) پسران گفتند: ای پدر! برای ما آمرزش گناهان ما را بخواه که ما خطاکار بودیم.');
INSERT INTO "badkhashani" VALUES(12,98,'12:98','گفت: به زودی از پروردگارم برای شما آمرزش می‌خواهم زیرا او آمرزگار مهربان است.');
INSERT INTO "badkhashani" VALUES(12,99,'12:99','پس چون بر یوسف در آمدند، پدر و مادر خود را در کنار خود جای داد و گفت: به خواست الله با امن و امان وارد سرزمین مصر شوید.');
INSERT INTO "badkhashani" VALUES(12,100,'12:100','و پدر و مادر خود را بر تخت نشاند و همۀ آنان برای او سجده کنان به زمین افتادند، و گفت: ای پدر! این تعبیر خوابی است که قبلا دیده بودم و پروردگارم آن را راست و درست گردانید، و حقا که به من احسان کرد وقتی که مرا از زندان بیرون ساخت و شما را پس از آنکه شیطان بین من و برادرانم اختلاف افگنده بود از دهات باز آورد. به راستی پروردگارم هر چه بخواهد (با لطف و مهربانی خود) سنجیده و دقیق انجام می‌دهد، زیرا او دانای حکیم است.');
INSERT INTO "badkhashani" VALUES(12,101,'12:101','ای پروردگار! واقعا (نصیبی بزرگ) از پادشاهی به من دادی و به من از تعبیر خوابها آموختی، ای آفرینندۀ آسمان‌ها و زمین! تو کارساز من در دنیا و آخرت هستی، مرا مسلمان بمیران و به صالحان ملحق بگردان.');
INSERT INTO "badkhashani" VALUES(12,102,'12:102','این داستان از خبرهای غیب است که آن را به تو وحی می‌کنیم. و تو نزد آنان نبودی وقتی که تصمیم گرفتند، در حالیکه آنان نیرنگ و توطئه چینی می‌کردند.');
INSERT INTO "badkhashani" VALUES(12,103,'12:103','و بیشتر مردم هر چند حرص بورزی، ایمان نمی‌آورند.');
INSERT INTO "badkhashani" VALUES(12,104,'12:104','و تو در برابر (این دعوت) از آنان مزدی نمی‌خواهی و آن (قرآن) جز پند و اندرزی برای عالمیان نیست.');
INSERT INTO "badkhashani" VALUES(12,105,'12:105','و چه بسیار دلائل قدرت و وحدانیت (الله) در آسمان‌ها و زمین است که بر آن می‌گذرند در حالیکه ایشان از آن روی می‌گردانند.');
INSERT INTO "badkhashani" VALUES(12,106,'12:106','و بیشترشان (که ادعای ایمان می‌کنند) به الله ایمان نمی‌آورند مگر اینکه مشرکند.');
INSERT INTO "badkhashani" VALUES(12,107,'12:107','آیا ایمن هستند از این که عذاب همه گیر از سوى الله بر آنان بیاید، یا ناگهان قیامت به آنها بیاید، در حالیکه غافل و بی‌خبر باشند؟!.');
INSERT INTO "badkhashani" VALUES(12,108,'12:108','بگو: این راه من است، با بصیرت و آگاهی به‌سوی الله دعوت می‌کنم و پیروان من هم (باید چنین باشند) و الله (از هر عیب) پاک است و من از مشرکان نیستم.');
INSERT INTO "badkhashani" VALUES(12,109,'12:109','و نفرستاده‌ایم پیش از تو مگر مردان را که وحی می‌فرستادیم به آنها در حالی که آنها از اهل شهرهای بزرگ ‌بودند، آیا (مشرکان) در زمین (همان پیغمبران) نرفته‌اند تا ببینند که چگونه شد انجام آنان که پیش از مشرکان بودند؟ (بلکه رفته‌اند اما عبرت نگرفته‌اند،) و البته سرای آخرت بهتر است برای پرهیزگاران، آیا نمی‌اندیشید؟');
INSERT INTO "badkhashani" VALUES(12,110,'12:110','(پیغمبران پیشین اقوام‌شان را دعوت دادند( تا آنکه پیغمبران (از ایمان آوردن مردم) ناامید شدند و (مردم) گمان کردند که (از طرف پیغامبران) به آنها دروغ گفته شده‌است. (در آن حالت،) یاری ما به پیغمبران رسید پس کسانی را که خواستیم نجات یافتند، و عذاب ما از گروه مجرمان برگشت ندارد.');
INSERT INTO "badkhashani" VALUES(12,111,'12:111','به راستی که در داستان‌های پیغمبران عبرتی برای صاحبان خرد است، (قرآن) سخنی نیست که به دروغ ساخته شده باشد بلکه تصدیق کنندۀ کتاب‌های است که پیش از آن بوده، و بیانگر هر ضرورت دینی است و هدایت و رحمتی است برای مردمی که (به آن) ایمان می‌آورند.');
INSERT INTO "badkhashani" VALUES(13,1,'13:1','المر. (مفهوم این حروف را الله می‌داند) این آیات کتاب (قرآن) است، و آنچه که از جانب پروردگارت به‌سوی تو نازل شده حق است، ولی بیشتر مردم ایمان نمی‌آورند.');
INSERT INTO "badkhashani" VALUES(13,2,'13:2','الله ذاتی است که آسمان‌ها را بدون ستونهایی که آنها را ببینید بلند کرد، باز بر عرش مستقر شد و آفتاب و ماه را مسخر کرد، هر یک تا مدت معین در حرکت است، الله کار هستی را تدبیر (و اداره) می‌کند و او آیات (علامات قدرت) خود را به تفصیل بیان می‌کند تا شما به ملاقات پروردگارتان یقین کنید.');
INSERT INTO "badkhashani" VALUES(13,3,'13:3','و اوست آن ذاتی که زمین را هموار و گسترده کرد و در آن کوه‌ها و نهرها گذاشت و از هر گونه میوه جفت دو گانه آفرید، شب را بر روز می‌پوشاند، يقينا در این (امور) نشانه‌هایی است برای آنان که (در وحدانیت و قدرت الله) می‌اندیشند.');
INSERT INTO "badkhashani" VALUES(13,4,'13:4','و در زمین قطعه‌های کنار هم (به هم پیوسته) و باغهایی از انگور و کشتزارها وجود دارد و درختان خرما چه از یک ریشه و چه از غیر یک ریشه که همه با یک آب آبیاری می‌شوند، اما برخی را از لحاظ طعم و لذت بر برخی دیگر برتری می‌دهیم، يقينا در این (اختلاف) نشانه‌هایی (قدرت الهی) است برای کسانی که می‌اندیشند.');
INSERT INTO "badkhashani" VALUES(13,5,'13:5','و اگر تعجب کنی (از تکذیب آنان) پس این سخن آنان عجیب‌تر است (که گفتند:) آیا وقتی خاک گردیم به راستی (دوباره زنده می‌شویم و) در آفرینش جدیدی می‌باشیم؟ اینها کسانی‌اند که به پرروردگارشان کفر ورزیدند و طوق و زنجیرها در گردنهایشان خواهد بود. این‌ها دوزخی‌اند و در آن جاودانه می‌مانند.');
INSERT INTO "badkhashani" VALUES(13,6,'13:6','و پیش از خوبی (رحمت الهی)، بدی (عذاب) را با شتاب از تو می‌خواهند، حال آنکه پیش از ایشان عقوبت‌ها (بر امثال شان) گذشته است و یقینا پروردگارت نسبت به مردم - با وجود ظلم‌شان - آمرزنده است و البته پروردگارت سخت عذاب دهنده است.');
INSERT INTO "badkhashani" VALUES(13,7,'13:7','و کافران می‌گویند: چرا معجزه‌ای از سوی پروردگارش بر او فرستاده نشده است؟ همانا تو تنها بیم دهنده هستی و هر قومی راهنمای دارد.');
INSERT INTO "badkhashani" VALUES(13,8,'13:8','الله می‌داند که هر (جاندار) ماده چه چیزی را در شکم حمل می‌کند و (نیز می‌داند) آنچه را که رحمها کم می‌کنند و آنچه را که می‌افزایند، و هر چیزی در نزد الله به اندازۀ معین است.');
INSERT INTO "badkhashani" VALUES(13,9,'13:9','(او) دانای همه پوشیده‌ها و آشکارا است، (و او) بزرگ بلند مرتبه است.');
INSERT INTO "badkhashani" VALUES(13,10,'13:10','برابر است (نزد او) هرکس از شما سخن را پنهان می‌دارد و کسی که آن را آشکار می‌سازد، و (برابر است نزد او) هرکس در شب پنهان است و هر کس در روز آشکارا حرکت می‌کند.');
INSERT INTO "badkhashani" VALUES(13,11,'13:11','برای او فرشتگانی است که پیاپی از روبرو و پشت سر به فرمان الله او را مراقبت (نگهبانی) می‌کنند، همانا الله احوال هیچ قومی را تغییر نمی‌دهد تا وقتی که آنان احوال خود را تغییر دهند و چون الله برای قومی بلائی بخواهد پس هیچ برگشتی برای آن نیست و برای آنان جز او هیچ کارسازی نیست.');
INSERT INTO "badkhashani" VALUES(13,12,'13:12','اوست که برق (آسمان) را برای بیم و امید به شما نشان می‌دهد و نیز ابرهای سنگین بار را ایجاد می‌کند.');
INSERT INTO "badkhashani" VALUES(13,13,'13:13','و رعد به ستایش او و فرشتگان نیز از ترس او تسبیح می‌گویند، و صاعقه‌ها را می‌فرستد و هرکس را که بخواهد به آنها گرفتار می‌کند، در حالیکه آنان دربارۀ الله مجادله می‌کنند، حال آنکه او (در رساندن عذاب) بسیار قدرتمند است.');
INSERT INTO "badkhashani" VALUES(13,14,'13:14','تنها الله است که شایستۀ عبادت و دعاست. و کسانی که جز الله را می‌خوانند به هیچ وجه دعایشان را اجابت نمی‌کنند، مگر مانند کسی که دو دستش را به‌سوی آب بگشاید تا به دهانش برسد در حالیکه هرگز آب به دهانش نخواهد رسید، و دعای کافران جز در گمراهی (و ناامیدی) نیست.');
INSERT INTO "badkhashani" VALUES(13,15,'13:15','و هر آنچه که در آسمان‌ها و زمین است خواسته و ناخواسته برای الله سجده می‌کنند، و همچنین سایه‌هایشان صبح و شام برای الله سجده می‌کنند.');
INSERT INTO "badkhashani" VALUES(13,16,'13:16','بگو: کیست پروردگار آسمان‌ها و زمین؟ (در پاسخ آنان)، بگو: «الله» بگو: آیا جز الله دوستانی گرفته‌اید که برای خودشان هم مالک نفع و زیانی نیستند؟ بگو: آیا کور و بینا برابرند؟ آیا تاریکی‌ها و روشنی برابرند؟ آیا شریکانی برای الله قرار داده‌اند که همچون آفریدن الله (چیزی را) آفریده باشند پس در نتیجه کار خلقت بر آنها مشتبه شده است؟ بگو: الله آفرینندۀ همه چیز است و اوست یگانۀ غالب.');
INSERT INTO "badkhashani" VALUES(13,17,'13:17','الله از آسمان آبی فرو فرستاد و رودخانه‌هایی به اندازۀ گنجایش خویش روان گشت، پس سیل کف بلندی روی خود برداشت. همچنین از آنچه در آتش می‌گذارید (از طلا و نقره و غیره) برای حصول زیورات و ابزار، از آن نیز کفی بالا می‌آید، الله برای حق و باطل این چنین مثل می‌زند (تا آنرا بیان و روشن کند)، اما آن کف از میان می‌رود و اما آنچه به مردم سود می‌بخشد بر روی زمین باقی می‌ماند، الله این چنین مثلها را می‌زند.');
INSERT INTO "badkhashani" VALUES(13,18,'13:18','برای آنانی که دعوت پروردگارشان را اجابت کرده‌اند حسنی (سرانجام نیک) است، و آنانی که دعوت پروردگار خود را اجابت نکرده‌اند (بالفرض) اگر همۀ آنچه که در زمین است و مانند آن را با خود داشته باشند البته همگی را برای رهایی خود (از عذاب الهی) فدیه خواهند داد، برای آنان سختی حساب است و جایگاهشان دوزخ است و (دوزخ) چه بد جایگاهی است!.');
INSERT INTO "badkhashani" VALUES(13,19,'13:19','آیا کسی که می‌داند آنچه از سوی پروردگارت بر تو نازل شده حق است، مانند کسی است که نابیناست؟ تنها خردمندانند که پند می‌گیرند.');
INSERT INTO "badkhashani" VALUES(13,20,'13:20','(خردمندان) آنانی‌اند که به عهدی که با الله بسته‌اند، وفا می‌کنند و عهد او را نمی‌شکنند.');
INSERT INTO "badkhashani" VALUES(13,21,'13:21','و (همچنان خردمندان) آنانی‌اند که آنچه را الله به پیوستنش امر کرده است می‌پیوندند و از پروردگار خویش می‌ترسند و از سختی حساب بیمناک هستند.');
INSERT INTO "badkhashani" VALUES(13,22,'13:22','و (همچنان خردمندان) آنانی‌اند که به خاطر پروردگارشان صبر و شکیبایی کردند و نماز را برپا داشتند و از آنچه که به آنها روزی دادیم به صورت پنهان و آشکار انفاق کردند و بدی را به نیکی دفع می‌کنند، این‌ها هستند که پاداش نیک آخرت از ایشان خواهد بود.');
INSERT INTO "badkhashani" VALUES(13,23,'13:23','همان باغ‌های بهشت که جای (اقامت) همیشگی است، آنان همراه پدران و فرزندان و همسران شایستۀ خود در آنجا وارد می‌شوند و فرشتگان (برای سلام و خوش آمدید) از هر دروازه‌ای بر ایشان وارد می‌شوند.');
INSERT INTO "badkhashani" VALUES(13,24,'13:24','(و می‌گویند:) سلامتی بر شما باد به پاداش صبر و شکیبایی که داشتید، پس آن سرای آخرت چه نیکو است!.');
INSERT INTO "badkhashani" VALUES(13,25,'13:25','و اما آنانی که عهد الله را پس از بستن آن می‌شکنند و چیزی را که الله به پیوستن آن امر کرده می‌برند و در زمین فساد می‌کنند، این گروه برایشان لعنت است و برایشان سختی آن سرای (نیز) است.');
INSERT INTO "badkhashani" VALUES(13,26,'13:26','الله برای هر کسی که بخواهد، رزق را می‌گشاید و تنگ می‌سازد و (کافران) به زندگی دنیا خورسند شدند، حال آنکه زندگانی دنیا در مقابل آخرت جز بهرۀ ناچیز (و چند روزه) نیست.');
INSERT INTO "badkhashani" VALUES(13,27,'13:27','و کافران می‌گویند: چرا بر او (محمد) معجزه‌ای از جانب پروردگارش نازل نشد؟ بگو: الله هرکس را بخواهد گمراه می‌کند و هر کس را که بسوی او رجوع کند، به راه خویش هدایت می‌کند.');
INSERT INTO "badkhashani" VALUES(13,28,'13:28','آنانی که ایمان آوردند و دل‌هایشان به یاد الله آرام می‌گیرد، آگاه باشید که دلها با یاد الله آرام می‌گیرند.');
INSERT INTO "badkhashani" VALUES(13,29,'13:29','آنانی که ایمان آورده‌اند و کارهای نیک انجام داده‌اند، خوشا به حال ایشان و آنان سرانجام نیک دارند.');
INSERT INTO "badkhashani" VALUES(13,30,'13:30','این چنین تو را در میان امتی فرستادیم که امتهای بسیاری پیش از آن گذشته‌اند، تا آنچه را به تو وحی کرده‌ایم بر آنان بخوانی. هر چند که به پروردگار مهربان کفر می‌ورزند. بگو: او (الله) پروردگار من است، جز او معبود به حقی نیست، بر او توکل کرده ام و بازگشت من به‌سوی اوست.');
INSERT INTO "badkhashani" VALUES(13,31,'13:31','و اگر قرآنی بود که کوه‌ها به وسیلۀ آن در حرکت آورده می‌شد و یا زمین به وسیلۀ آن پاره پاره کرده می‌شد و یا مردگان با آن به سخن گفتن در آورده می‌شد (باز هم ایمان نمی‌آوردند)، بلکه همۀ کارها الله را است. آیا مؤمنان ندانسته‌اند که اگر الله می‌خواست همۀ مردم را هدایت می‌کرد؟ و کافران همیشه به سزای آنچه می‌کنند مصیبت کوبنده‌ای به آنان می‌رسد. یا آن مصیبت نزدیک خانۀ‌شان فرود می‌آید، تا آنگاه که وعده الله برسد، بی‌گمان الله وعده خود را خلاف نمی‌کند.');
INSERT INTO "badkhashani" VALUES(13,32,'13:32','البته پیغمبران پیش از تو (نیز) مورد تمسخر قرار گرفتند، پس به کافران مهلت دادم، باز آنها را گرفتار ساختم، پس سزای من چگونه بود؟');
INSERT INTO "badkhashani" VALUES(13,33,'13:33','آیا کسی که بر هر شخصی به آنچه کرده حافظ و مراقب است (چون بتان است)؟! و برای الله شریکانی قرار داده‌اند. بگو: نام‌های آنها را بگیرید، آیا الله را به آنچه که در زمین نمیداند (و نمی‌شناسد) خبر می‌دهید؟ یا اینکه با سخنان ظاهری (مردم) فریفته می‌شوید (و آنها را عبادت می‌کنید)؟ بلکه برای کافران مكر (كفر)‌شان مزین شده است و از راه راست باز داشته شده‌اند، و هر کس را که الله گمراه کند هیچ هدایت کننده‌ای ندارد.');
INSERT INTO "badkhashani" VALUES(13,34,'13:34','برای این چنین مردم در زندگی دنیا عذاب سخت است. و البته عذاب آخرت سخت‌تر است، و برای آنها در برابر (عذاب) الله هیچ نگهدارنده‌ای نیست.');
INSERT INTO "badkhashani" VALUES(13,35,'13:35','صفت جنتی که به پرهیزگاران وعده داده شده است (چنین است که) نهرها از زیر قصرها و درختان آن جاری است، میوه‌هایش همیشگی و سایه اش (نیز) دایمی است. این است سرانجام متقیان و سرانجام کافران آتش (دوزخ) است.');
INSERT INTO "badkhashani" VALUES(13,36,'13:36','و (بعضی از) آنانی که به ایشان کتاب داده‌ایم از آنچه به‌سوی تو نازل شده است، خورسند می‌شوند، و از احزاب (کافران) کسانی هستند که بخشی از (احکام) آن را انکار می‌کند. بگو: به من این حکم شده که تنها الله را بپرستم و به او چیزی را شریک نسازم. به‌سوی او (مردم را) دعوت می‌دهم و بازگشت من به‌سوی اوست.');
INSERT INTO "badkhashani" VALUES(13,37,'13:37','و این چنین قرآن را به عنوان کتابی روشن و با زبان عربی نازل کردیم، و (بالفرض) اگر از خواهشات آنها پیروی کنی بعد از علمی که به تو رسیده است، تو را از (عذاب) الله هیچ حمایتگر و مددگاری نیست.');
INSERT INTO "badkhashani" VALUES(13,38,'13:38','و به راستی پیش از تو (نیز) پیغمبرانی را فرستادیم و برایشان زنان و فرزندانی قرار داده‌ایم، و برای هیچ پیغمبری ممکن نیست که معجزه‌ای بیاورد مگر به اجازه الله. و برای هر امری (هر وعدۀ) کتاب (قضای معین) نوشته شده است.');
INSERT INTO "badkhashani" VALUES(13,39,'13:39','الله هر چیزی را که بخواهد از میان می‌برد و هر چیزی را که بخواهد ثابت می‌دارد، و ام الکتاب (لوح محفوظ) در نزد اوست.');
INSERT INTO "badkhashani" VALUES(13,40,'13:40','و اگر برخی از آنچه را که به آنان وعده می‌دهیم به تو نشان دهیم و یا اینکه تو را بمیرانیم، در هر حال تنها رساندن (پیغام) بر عهدۀ تو است و حساب بر عهدۀ ما می‌باشد.');
INSERT INTO "badkhashani" VALUES(13,41,'13:41','آیا ندیدند که ما به این زمین می‌آئیم (و) از اطراف آن می‌کاهیم (و از تصرف کفار بیرون می‌آوریم)؟ و الله حكم می‌کند و فرمان و حکمش هیچ گونه رد و مانعی ندارد، و او زود حساب گیرنده است.');
INSERT INTO "badkhashani" VALUES(13,42,'13:42','البته آنانی که پیش از ایشان بودند مکر و حیله ورزیدند ولی تدبیر و نقشه‌ها همگی خاص (بدست الله و) از الله است، آنچه را که هر کس انجام میدهد می‌داند و کافران خواهند دانست که انجام نیکو از کیست.');
INSERT INTO "badkhashani" VALUES(13,43,'13:43','و کافران می‌گویند: تو رسول الله نیستی، بگو: کافی است که الله در بین من و شما گواه باشد، (و کافی است گواهی) کسی که نزد او علم کتاب است.');
INSERT INTO "badkhashani" VALUES(14,1,'14:1','الر. (مراد این حروف به الله معلوم است) کتابی است که آن را به‌سوی تو نازل کرده‌ایم تا (به وسيلۀ آن) مردم از تاریکی‌ها (کفر و گناه) به‌سوی روشنایی (ایمان و عمل صالح) به حکم پروردگارشان بیرون آوری، به‌سوی راه آن غالب ستوده.');
INSERT INTO "badkhashani" VALUES(14,2,'14:2','الله ذاتی است که آنچه در آسمان‌ها و زمین است خاص از اوست، و وای (هلاکت باد) بر کافران از عذاب سخت!');
INSERT INTO "badkhashani" VALUES(14,3,'14:3','آنانی که زندگی دنیا را (دوست داشته و) بر آخرت ترجیح می‌دهند و مردم را از راه الله منع می‌کنند و در آن کجی و انحراف می‌طلبند، این گروه کسانی‌اند که در گمراهی دور از حق قرار دارند.');
INSERT INTO "badkhashani" VALUES(14,4,'14:4','و ما هیچ رسولی را نفرستادیم مگر به زبان قومش، تا (دین الله را) برای‌شان بیان کند (ليكن باز هم قبول نکردند) پس الله هر کسی را که بخواهد گمراه می‌کند و هر کسی را که بخواهد، هدایت می‌کند و او غالب با حکمت است.');
INSERT INTO "badkhashani" VALUES(14,5,'14:5','البته موسی را با معجزه‌های خود فرستادیم که قوم خود را از تاریکیها (کفر و جهل) به‌سوی روشنی (ایمان و دانش) بیرون کن و به آنان روزهای (نعمت‌ها و مصیبت‌های) الله را یادآوری کن (تا پند گیرند). يقينا در این (امور) عبرتهایی برای هر صبر کنندۀ سپاسگزار است.');
INSERT INTO "badkhashani" VALUES(14,6,'14:6','و یاد کن برایشان وقتی را که موسی به قوم خود گفت: نعمت الله را بر خود به یاد آورید، آن زمان که شما را از فرعون نجات داد که بدترین عذاب را به شما می‌رساندند و پسرانتان را می‌کشتند و زنانتان را زنده نگاه می‌داشتند و در این امر آزمایش بزرگی از جانب پروردگارتان بود.');
INSERT INTO "badkhashani" VALUES(14,7,'14:7','و چون پروردگارتان اعلان کرد که اگر (نعمت‌هایم را) شکر کنید، البته بر نعمت‌های شما می‌افزایم و اگر کفر بورزید، بی‌گمان عذاب من بسیار سخت است.');
INSERT INTO "badkhashani" VALUES(14,8,'14:8','و موسی گفت: اگر شما و مردمان روی زمین همه یکجا کافر شوید (بدانید) که الله بی‌نیاز و ستوده است.');
INSERT INTO "badkhashani" VALUES(14,9,'14:9','آیا خبر آنانی که پیش از شما بودند (مانند) قوم نوح، عاد، ثمود، و کسان دیگری که پس از ایشان آمدند (و) به جز الله کسی از آنان خبر ندارد، به شما نرسیده است؟ پیغمبرانشان با معجزات (فراوان) به نزد آنان آمدند ولی دستانشان را در دهان‌هایشان گذاشتند و گفتند: ما به آنچه شما به آن فرستاده شده‌اید کافریم و در مورد آنچه ما را به‌سوی آن دعوت می‌دهید، سخت در شک هستیم.');
INSERT INTO "badkhashani" VALUES(14,10,'14:10','پیغمبران‌شان گفتند: آیا دربارۀ الله آفرینندۀ آسمان‌ها و زمین شکی است؟! او شما را دعوت می‌کند تا گناهانتان را بیامرزد و تا مدتی معین شما را مهلت می‌دهد. گفتند: شما جز بشری مانند ما نیستید، می‌خواهید ما را از آنچه پدران ما می‌پرستیدند منع کنید، پس (اگر در ادعای تان صادق هستید) برای ما دلیلی آشکار بیاورید.');
INSERT INTO "badkhashani" VALUES(14,11,'14:11','پیغمبرانشان به آنها گفتند: ما جز بشری مثل شما نیستیم، ولی الله بر هر کس از بندگان خود که بخواهد (با انتخاب او به نبوت) منت می‌نهد، و در توان ما نیست که جز به اذن الله برای شما حجتی بیاوریم، و مؤمنان باید تنها بر الله توکل کنند.');
INSERT INTO "badkhashani" VALUES(14,12,'14:12','و (پیغمبران گفتند:) چرا بر الله توکل نکنیم؟ حال آنکه ما را به راه‌های (زندگی و نجات) ما راهنمایی نموده است و البته ما بر آزار شما صبر خواهیم کرد و توکل کنندگان باید تنها بر الله توکل کنند.');
INSERT INTO "badkhashani" VALUES(14,13,'14:13','و (باز) کافران به پیغمبرانشان گفتند: البته شما را از سرزمین خود بیرون می‌کنیم یا اینکه به دین ما باز گردید. پس پروردگارشان به آنان وحی کرد که حتما ظالمان را هلاک می‌کنیم.');
INSERT INTO "badkhashani" VALUES(14,14,'14:14','و پس از آنها شما را در این سرزمین ساکن خواهیم کرد. این (وعده) برای کسی است که از ایستادن در حضور من بترسد و از تهدیدم بیم داشته باشد.');
INSERT INTO "badkhashani" VALUES(14,15,'14:15','و (پیغمبران) از الله فتح (نصرت) طلبیدند و (سر انجام) هر متکبر سرکشی ناکام شد.');
INSERT INTO "badkhashani" VALUES(14,16,'14:16','پشت سر او دوزخ است و از زرداب دوزخیان به او نوشانیده می‌شود.');
INSERT INTO "badkhashani" VALUES(14,17,'14:17','(به سبب تلخی و داغی) آن را جرعه جرعه می‌نوشد و نزدیک نیست که به آسانی از گلویش بگذرد و مرگ از هر سو به سراغ او می‌آید، ليكن نمی‌میرد، و به تعقیب آن، عذاب سختی در پیشروی خود دارد.');
INSERT INTO "badkhashani" VALUES(14,18,'14:18','مثال آنانی که به پروردگارشان کفر ورزیدند اعمال‌شان مانند خاکستری است که تند بادی در یک روز طوفانی بر آن وزیده باشد، به هیچ چیزی از آنچه (در دنیا) کسب کرده بودند، قدرت ندارند. این همان گمراهی دور (و درازی) است.');
INSERT INTO "badkhashani" VALUES(14,19,'14:19','آیا ندانسته‌ای (ای مخاطب) که الله آسمان‌ها و زمین را به حق آفریده است، اگر بخواهد شما را (از میان) می‌برد و خلق جدیدی می‌آورد؟!');
INSERT INTO "badkhashani" VALUES(14,20,'14:20','و این کار بر الله مشکل نیست.');
INSERT INTO "badkhashani" VALUES(14,21,'14:21','و (در روز قیامت) همه به دربار الله حاضر می‌شوند، پس ضعیفان به آنانی که خود را بزرگ می‌شمردند می‌گویند: ما پیرو شما بودیم، پس آیا می‌توانید چیزی از عذاب الله را از ما دفع کنید؟ می‌گویند: اگر الله ما را هدایت می‌کرد ما هم شما را هدایت می‌نمودیم، برای ما برابر است چه بی‌تابی کنیم یا صبر کنیم، هیچ پناهگاهی نداریم.');
INSERT INTO "badkhashani" VALUES(14,22,'14:22','و وقتی که کار (قضاوت) به پایان برسد (و کار از کار بگذرد) شیطان می‌گوید: الله به شما وعدۀ راست داد و من به شما وعده دادم و با شما وعدہ خلافی کردم. و من هیچ سلطه‌ای بر شما نداشتم جز اینکه شما را دعوت دادم و شما هم دعوتم را پذیرفتید. پس مرا ملامت نکنید و خود را ملامت کنید، نه من به فریاد شما می‌رسم و نه شما به فریاد من می‌رسید، البته به اینکه پیش از این شما مرا شریک الله قرار می‌دادید کافرم. بی‌گمان ظالمان برایشان عذاب دردناک است.');
INSERT INTO "badkhashani" VALUES(14,23,'14:23','و کسانی که ایمان آورده و کارهای نیک انجام داده‌اند به باغهایی در آورده می‌شوند که در زیر (درختان و قصرهای) آن نهرها روان است، در آنجا به حکم پروردگارشان همیشه‌اند، دعای‌شان در آنجا سلام گفتن است.');
INSERT INTO "badkhashani" VALUES(14,24,'14:24','آیا ندیدی که الله چگونه مثال زد؛ کلمۀ طیبه (و پاک) مانند درخت پاک (شیرین میوه‌ای است) که ریشه آن (در زمین) استوار و محکم است و شاخه‌های آن در آسمان است.');
INSERT INTO "badkhashani" VALUES(14,25,'14:25','که به حکم پروردگارش در هر وقتی میوۀ خود را می‌دهد. و الله این مثالها را برای مردم می‌زند تا یادآور شوند و عبرت گیرند.');
INSERT INTO "badkhashani" VALUES(14,26,'14:26','و مثال سخن پلید به درخت پلید می‌ماند که از روی زمین کنده شده و آن را هیچ قراری نباشد.');
INSERT INTO "badkhashani" VALUES(14,27,'14:27','الله مؤمنان را در زندگانی دنیا و آخرت به سبب سخن پایدار، ثابت قدم می‌دارد و ظالمان را گمراه می‌سازد، و الله هرچه بخواهد انجام میدهد.');
INSERT INTO "badkhashani" VALUES(14,28,'14:28','آیا ندیدی آنانی که نعمت الله را به ناسپاسی تبدیل کردند و قومشان را به سرای هلاکت در آورده‌اند؟!');
INSERT INTO "badkhashani" VALUES(14,29,'14:29','آن (سرای هلاکت) دوزخ است که داخل می‌شوند و چه بد قرارگاه است.');
INSERT INTO "badkhashani" VALUES(14,30,'14:30','و برای الله شریکانی مقرر نمودند تا (مردم را) از راه او گمراه کنند، بگو: برخوردار شوید (بهره و لذت ببرید)، به يقين بازگشت شما به‌سوی آتش است.');
INSERT INTO "badkhashani" VALUES(14,31,'14:31','به بندگان مؤمن من بگو: نماز را برپا کنند و از آنچه به ایشان روزی داده‌ایم (مقداری را) در پنهان و آشکار انفاق کنند، پیش از آنکه روزی برسد که در آن خرید و فروش و دوستی نیست.');
INSERT INTO "badkhashani" VALUES(14,32,'14:32','الله ذاتی است که آسمان‌ها و زمین را آفرید و از آسمان آبی نازل کرد و با آن آب میوه‌های را پیدا کرد و روزی شما گردانید و کشتی را برایتان رام ساخت تا به حکم او در بحر روان شود، و نهرها را برای شما مسخر کرد.');
INSERT INTO "badkhashani" VALUES(14,33,'14:33','و برای شما آفتاب و ماه را که همیشه در حرکت‌اند، تابع کرد و تابع کرد برای شما شب و روز را.');
INSERT INTO "badkhashani" VALUES(14,34,'14:34','و از هرچیزی که از او خواسته‌اید به شما داده است و اگر نعمت الله را بشمارید نمی‌توانید آنرا به شمار درآورید، واقعا انسان ظالم و ناشکر است.');
INSERT INTO "badkhashani" VALUES(14,35,'14:35','و یادآور شوید وقتی را که ابراهیم گفت: ای پروردگارم! این شهر (مکه) را جای امن بگردان و مرا و اولاد مرا از بت پرستی دور دار.');
INSERT INTO "badkhashani" VALUES(14,36,'14:36','پروردگارا! این بتان بسیاری از مردم را گمراه ساخته‌اند، پس هرکس از من پیروی کند او از من است و هر کس از من نافرمانی کند بی‌گمان تو آمرزندۀ مهربانی.');
INSERT INTO "badkhashani" VALUES(14,37,'14:37','ای پروردگار ما! من (یکی از) فرزندانم را در درۀ بی‌کشت و زرع (و) در کنار خانۀ تو که آن را حرام ساخته‌ای سکونت داده ام، پروردگارا! تا اینکه نماز را برپا دارند، پس چنان کن که دل‌های برخی از مردم به‌سوی آن مایل گردد و از میوه‌ها به آنان روزی ده، باشد که شکرگزاری کنند.');
INSERT INTO "badkhashani" VALUES(14,38,'14:38','ای پروردگار ما! يقينا تو می‌دانی آنچه را که پنهان می‌کنیم و آنچه را که آشکار می‌کنیم. و هیچ چیزی در زمین و در آسمان بر الله پوشیده نمی‌ماند.');
INSERT INTO "badkhashani" VALUES(14,39,'14:39','شكر و ستایش الله را که در سن پیری اسماعیل و اسحاق را به من بخشید، یقینا پروردگارم شنونده دعاست.');
INSERT INTO "badkhashani" VALUES(14,40,'14:40','ای پروردگارم! مرا پابند نماز گردان، و از ذرّیّه‌ام نیز (کسانی را نمازگذار گردان). پروردگارا! و دعای مرا بپذیر.');
INSERT INTO "badkhashani" VALUES(14,41,'14:41','ای پروردگار ما! مرا و پدر و مادرم و مؤمنان را بیامرز در آن روزی که حساب برپا می‌گردد.');
INSERT INTO "badkhashani" VALUES(14,42,'14:42','و الله را از آنچه ظالمان می‌کنند، غافل مپندار. جز این نیست که (عذاب) آنها را برای روزی که چشم‌ها در آن خیره شود، به تأخیر می‌اندازد.');
INSERT INTO "badkhashani" VALUES(14,43,'14:43','شتابانند؛ در حالیکه سرهای خود را بالا گرفته‌اند و چشمهای‌شان به سوی‌شان باز نمی‌گردد و دل‌هایشان (از فهم و ادراک) خالی است.');
INSERT INTO "badkhashani" VALUES(14,44,'14:44','و مردم را از روزی بترسان که عذاب به آنها می‌آید پس ظالمان می‌گویند: «ای پروردگار ما! ما را تا مدتی مهلت بده تا دعوتت را بپذیریم و از پیغمبران پیروی کنیم. (به آنان گفته می‌شود:) آیا پیش از این (در دنیا) قسم نخوردید که شما را هیچ زوالی نیست (و از بین نمی‌روید)؟');
INSERT INTO "badkhashani" VALUES(14,45,'14:45','و سکونت کردید در منازل آنانی که بر خویش ظلم کرده بودند و برای شما واضح شد که ما با آنها چه کردیم و برای شما مثال‌ها زدیم.');
INSERT INTO "badkhashani" VALUES(14,46,'14:46','و البته آنان دسیسه و مکرشان را به کار بردند و مکرشان با الله است، هر چند از مکرشان کوه‌ها از جای کنده می‌شد.');
INSERT INTO "badkhashani" VALUES(14,47,'14:47','پس گمان مبر که الله وعدۀ خود به پیامبرانش را خلاف می‌کند، بی‌گمان الله غالب (و) انتقام گیرنده است.');
INSERT INTO "badkhashani" VALUES(14,48,'14:48','(آن انتقام) در آن روزی (است) که زمین به غیر این زمین مبدل گردانیده می‌شود و آسمان‌ها به آسمان‌های دیگری تبدیل شده و بندگان در پیشگاه الله یگانۀ قهار حاضر می‌شوند.');
INSERT INTO "badkhashani" VALUES(14,49,'14:49','و جنایت کاران را در آن روز می‌بینی که با یکدیگر در زنجیره‌ها بسته شده‌اند.');
INSERT INTO "badkhashani" VALUES(14,50,'14:50','که پیراهن‌هایشان از سرب گداخته شده است و آتش چهره‌هایشان را می‌پوشاند.');
INSERT INTO "badkhashani" VALUES(14,51,'14:51','تا الله هر شخص را بر وفق آنچه کرده است، سزا دهد. يقينا الله زود حساب گر است.');
INSERT INTO "badkhashani" VALUES(14,52,'14:52','این (قرآن) ابلاغی است برای مردمان تا به آن بیم داده شوند و بدانند که تنها او معبود یگانه است و تا صاحبان خرد پند و عبرت گیرند.');
INSERT INTO "badkhashani" VALUES(15,1,'15:1','الر. (مفهوم این حروف به الله معلوم است) این آیات کتاب و قرآن بیان کنندۀ (حق و باطل) است.');
INSERT INTO "badkhashani" VALUES(15,2,'15:2','چه بسا کافران (در روز قیامت) آرزو کنند که کاش مسلمان بودند.');
INSERT INTO "badkhashani" VALUES(15,3,'15:3','(اکنون) بگذار آنها را تا بخورند و (چند روزی از لذتها) برخوردار شوند و آرزو(ها) آنها را غافل سازد، پس به زودی خواهند دانست.');
INSERT INTO "badkhashani" VALUES(15,4,'15:4','و ما هیچ (اهل) قریه‌ای را هلاک نکردیم مگر آنکه (برای هلاکت آنها) اجل نوشتۀ معلوم بود.');
INSERT INTO "badkhashani" VALUES(15,5,'15:5','و هیچ امتی از اجل خود نه پیش می‌افتد و نه پس می‌مانند.');
INSERT INTO "badkhashani" VALUES(15,6,'15:6','و (مشرکین به طور تمسخر و انکار) گفتند: ای کسی که قرآن بر او نازل شده است! یقینا تو دیوانه هستی.');
INSERT INTO "badkhashani" VALUES(15,7,'15:7','(اگر هوشیار باشی) و اگر از راستگویان هستی چرا فرشتگان را به نزد ما نمی‌آوری؟');
INSERT INTO "badkhashani" VALUES(15,8,'15:8','(در جواب بگو:) ما فرشته‌ها را جز به حق فرو نمی‌فرستیم و آنگاه (پس از فرود آمدن فرشته‌ها) به آنها مهلت داده نمی‌شود.');
INSERT INTO "badkhashani" VALUES(15,9,'15:9','بی‌گمان ما خود قرآن را نازل کرده‌ایم لذا ما خود نگهبان آن هستیم.');
INSERT INTO "badkhashani" VALUES(15,10,'15:10','(این رویۀ مشرکین تنها با تو نیست) و البته پیش از تو نیز در مردم‌های پیشین، پیغمبرانی فرستادیم (و ایشان نیز تکذیب شدند).');
INSERT INTO "badkhashani" VALUES(15,11,'15:11','و هیچ رسولی نزد ایشان نیامد مگر اینکه او را مسخره می‌کردند.');
INSERT INTO "badkhashani" VALUES(15,12,'15:12','این چنین آن (استهزاء) را در دل‌های مجرمان داخل می‌گردانیم.');
INSERT INTO "badkhashani" VALUES(15,13,'15:13','که به آن ایمان نمی‌آورند و البته راه و رسم (تکذیب نمودن کافران باز آمدن عذاب الهی) در پیشینان گذشته است.');
INSERT INTO "badkhashani" VALUES(15,14,'15:14','و اگر بر آنها دروازه‌ای از آسمان باز کنیم، پس همیشه در آن بالا می‌رفتند.');
INSERT INTO "badkhashani" VALUES(15,15,'15:15','حتما می‌گفتند: ما چشم بندی شده‌ایم، بلکه گروهی جادو شده هستیم.');
INSERT INTO "badkhashani" VALUES(15,16,'15:16','و بدون شک ما در آسمان برجهای آفریدیم و آن را برای بینندگان زینت دادیم.');
INSERT INTO "badkhashani" VALUES(15,17,'15:17','و آن را از هر شیطان رانده شده محفوظ و مصون داشتیم.');
INSERT INTO "badkhashani" VALUES(15,18,'15:18','به جز شیطانی که دزدیده گوش دهد که شهاب درخشان در پی او می‌افتند.');
INSERT INTO "badkhashani" VALUES(15,19,'15:19','و زمین را هموار کردیم و در آن کوه‌های محکم نهادیم و از هر چیز به اندازۀ معين و بطور سنجیده رویانیدیم.');
INSERT INTO "badkhashani" VALUES(15,20,'15:20','و برای شما در زمین اسباب معیشت قرار دادیم و برای کسانی که شما روزی دهندۀ آنها نیستید.');
INSERT INTO "badkhashani" VALUES(15,21,'15:21','و هیچ چیزی نیست مگر اینکه خزانه‌های آن پیش ماست و (لیکن) آن را جز به اندازۀ معین نازل نمی‌کنیم.');
INSERT INTO "badkhashani" VALUES(15,22,'15:22','و بادها را باردار کنندۀ ابر فرستادیم پس از آسمان آب باراندیم و آن را به شما نوشانیدیم و شما ذخیره کنندۀ آن نیستید.');
INSERT INTO "badkhashani" VALUES(15,23,'15:23','و يقينا ما هستیم که زندگی می‌بخشیم و ما می‌میرانیم و ما وارث نعمت‌های روی زمین هستیم.');
INSERT INTO "badkhashani" VALUES(15,24,'15:24','و البته ما (حال) گذشته گان شما را دانسته‌ایم (که چه عمل داشتند) و البته ما آیندگان شما را دانسته‌ایم (که چه عمل خواهند کرد).');
INSERT INTO "badkhashani" VALUES(15,25,'15:25','و البته پروردگار تو آنان را (در روز قيامت) محشور می‌کند چون او باحکمت داناست.');
INSERT INTO "badkhashani" VALUES(15,26,'15:26','و البته انسان را از گِل خشک از قسم لای سیاه بوی گرفته آفریدیم.');
INSERT INTO "badkhashani" VALUES(15,27,'15:27','و جن را پیش از آدم از آتش سوزان آفریدیم.');
INSERT INTO "badkhashani" VALUES(15,28,'15:28','و یادآور شو چون پروردگارت به فرشته‌ها گفت: که من بشری را از گِل خشک از قسم لای سیاه بوی گرفته خواهم آفرید.');
INSERT INTO "badkhashani" VALUES(15,29,'15:29','پس هرگاه (صورت و جسد) آن را برابر کردم و در آن از روح آفریدۀ خود دمیدم پس (شما فرشته‌ها) پیش او به سجده بیفتید.');
INSERT INTO "badkhashani" VALUES(15,30,'15:30','پس فرشته‌ها همه یکجا سجده کردند.');
INSERT INTO "badkhashani" VALUES(15,31,'15:31','مگر ابلیس که ابا ورزید از اینکه با سجده کنندگان باشد.');
INSERT INTO "badkhashani" VALUES(15,32,'15:32','الله فرمود: ای ابلیس! تو را چه شده است که همراه سجده کنندگان نیستی؟!');
INSERT INTO "badkhashani" VALUES(15,33,'15:33','ابلیس گفت: من آن نیستم که برای بشری که او را از گل خشک از قسم گل سیاه بوی گرفته آفریدی، سجده کنم.');
INSERT INTO "badkhashani" VALUES(15,34,'15:34','الله فرمود: پس بیرون شو از آنجا (از آن مقام والا) بی‌گمان تو رانده شده‌ای.');
INSERT INTO "badkhashani" VALUES(15,35,'15:35','و يقينا تا روز جزا بر تو لعنت (دوری از رحمت الله) است.');
INSERT INTO "badkhashani" VALUES(15,36,'15:36','ابلیس گفت: ای پروردگار! مرا تا روزی که مردم برانگیخته می‌شوند، مهلت ده.');
INSERT INTO "badkhashani" VALUES(15,37,'15:37','الله فرمود: همانا تو از جملۀ مهلت داده شده گانی.');
INSERT INTO "badkhashani" VALUES(15,38,'15:38','تا روز وقت (مقرر و) معلوم.');
INSERT INTO "badkhashani" VALUES(15,39,'15:39','(باز) ابلیس گفت: ای پروردگارم! به سبب اینکه مرا گمراه نمودی (چون از فرمان تو سرکشی کردم، پس گناهان را) در زمین برایشان زیبا جلوه می‌دهم و همگی آنان را گمراه می‌نمایم.');
INSERT INTO "badkhashani" VALUES(15,40,'15:40','به جز بندگان خالص تو از جملۀ آنان را (که بر آنها مسلط نمی‌توان شد).');
INSERT INTO "badkhashani" VALUES(15,41,'15:41','الله (در جواب) فرمود: این (دین اسلام) راهی است راست که بر من می‌رسد.');
INSERT INTO "badkhashani" VALUES(15,42,'15:42','البته تو بر بندگان (خالص ) من سلطه نداری مگر گمراهانی که از تو پیروی می‌کنند.');
INSERT INTO "badkhashani" VALUES(15,43,'15:43','و البته وعده گاه همۀ آنها دوزخ است.');
INSERT INTO "badkhashani" VALUES(15,44,'15:44','برای آن هفت در است و برای هر دری از آنان بخشی معین است.');
INSERT INTO "badkhashani" VALUES(15,45,'15:45','البته پرهیزگاران در باغها و چشمه سارها‌اند.');
INSERT INTO "badkhashani" VALUES(15,46,'15:46','(به ایشان گفته می‌شود) در آن با سلامتی و امنیت در آیید.');
INSERT INTO "badkhashani" VALUES(15,47,'15:47','و کینه‌ای را که در سینه‌های آنان است بیرون می‌کشیم (و) برادرانه بر تختها روبروی هم می‌نشینند.');
INSERT INTO "badkhashani" VALUES(15,48,'15:48','در آن جا رنجی به آنان نمی‌رسد، و از آنجا بیرون شدنی نیستند.');
INSERT INTO "badkhashani" VALUES(15,49,'15:49','بندگان مرا آگاه ساز که منم آمرزندۀ مهربان.');
INSERT INTO "badkhashani" VALUES(15,50,'15:50','و (نیز بندگان مرا آگاه کن) يقينا که عذاب من، عذاب دردناک است.');
INSERT INTO "badkhashani" VALUES(15,51,'15:51','و به آنها از مهمانان ابراهيم خبر ده (که هم برای رحمت آمده بودند و هم برای عذاب).');
INSERT INTO "badkhashani" VALUES(15,52,'15:52','وقتی که بر او وارد شدند، و سلام گفتند، ابراهیم گفت: البته ما از شما ترسان‌ایم.');
INSERT INTO "badkhashani" VALUES(15,53,'15:53','(فرشته‌ها) گفتند: نترس، چون ما تو را به پسر دانا (پیغمبر) مژده می‌دهیم.');
INSERT INTO "badkhashani" VALUES(15,54,'15:54','(ابراهیم) گفت: آیا مرا با وجود اینکه به سن پیری رسیده ام مژده میدهید؟ پس چه مژده‌ای به من می‌دهید؟');
INSERT INTO "badkhashani" VALUES(15,55,'15:55','گفتند: ما تو را به حق مژده دادیم، پس از ناامیدان مباش.');
INSERT INTO "badkhashani" VALUES(15,56,'15:56','ابراهیم گفت: جز گمراهان چه کسی از رحمت پروردگارش ناامید می‌شود؟');
INSERT INTO "badkhashani" VALUES(15,57,'15:57','(باز) گفت: پس کار مهم شما چیست ای فرستاده شدگان؟');
INSERT INTO "badkhashani" VALUES(15,58,'15:58','گفتند: همانا ما به‌سوی قوم مجرم (جنایتکار) فرستاده شده‌ایم.');
INSERT INTO "badkhashani" VALUES(15,59,'15:59','غیر از آل لوط (خانواده و پیروان او) که البته همۀ آنها را نجات می‌دهیم.');
INSERT INTO "badkhashani" VALUES(15,60,'15:60','به جز همسر او (چون الله فرموده است) ما فيصله کرده‌ایم که او از باقی ماندگان در عذاب باشد.');
INSERT INTO "badkhashani" VALUES(15,61,'15:61','پس وقتی فرشته‌های فرستاده شده نزد خاندان لوط آمدند.');
INSERT INTO "badkhashani" VALUES(15,62,'15:62','لوط گفت: البته شما مردم ناآشنائید.');
INSERT INTO "badkhashani" VALUES(15,63,'15:63','گفتند: (نه، ما نا آشنا نیستیم) بلکه برای تو خبر چیزی را آورده‌ایم که (قوم تو) در آن شک می‌کردند.');
INSERT INTO "badkhashani" VALUES(15,64,'15:64','و حق را برای تو آورده‌ایم، و البته ما راستگویانیم.');
INSERT INTO "badkhashani" VALUES(15,65,'15:65','لذا در بخشی از شب خانواده ات را همرایت ببر و خودت از پس ایشان برو و کسی از شما به پشت سر نگاه نکند و به همانجا که به شما دستور داده می‌شوید، بروید.');
INSERT INTO "badkhashani" VALUES(15,66,'15:66','و به‌سوی لوط وحی فرستادیم همان امر را که البته بیخ این گروه در وقت صباح کنده خواهد شد.');
INSERT INTO "badkhashani" VALUES(15,67,'15:67','و (همزمان) اهل شهر (سدوم) خوشی‌کنان آمدند (که مهمانان زیبا و خوبصورت آمده‌اند!).');
INSERT INTO "badkhashani" VALUES(15,68,'15:68','لوط گفت: چون اینها مهمانان من‌اند پس مرا شرمنده مکنید.');
INSERT INTO "badkhashani" VALUES(15,69,'15:69','و از الله بترسید و مرا خوار مکنید.');
INSERT INTO "badkhashani" VALUES(15,70,'15:70','(قوم) گفتند: آیا تو را از (میزبانی) مردم جهان منع نکرده بودیم؟');
INSERT INTO "badkhashani" VALUES(15,71,'15:71','لوط گفت: این‌ها دختران من‌اند (با آنها نکاح کنید) اگر می‌خواهید کاری بکنید.');
INSERT INTO "badkhashani" VALUES(15,72,'15:72','قسم به بخشندۀ عمر تو که آنها در مستی (و شهوت پرستی) خود حیران (و سرگردان) بودند.');
INSERT INTO "badkhashani" VALUES(15,73,'15:73','پس هنگام طلوع آفتاب آواز سخت مرگبار ایشان را فرا گرفت.');
INSERT INTO "badkhashani" VALUES(15,74,'15:74','پس آن شهر را زیر و زبر کردیم و بر آنها سنگهایی از سنگ گل بارانیدیم.');
INSERT INTO "badkhashani" VALUES(15,75,'15:75','البته در این (سرگذشت) برای هوشمندان (و عبرت گیرندگان) نشانه‌هایی است.');
INSERT INTO "badkhashani" VALUES(15,76,'15:76','و يقينا آن شهر (ویران شده) بر سر راه عبور و مرور موجود است.');
INSERT INTO "badkhashani" VALUES(15,77,'15:77','البته در این (عذاب) نشانۀ برای یقین کنندگان است.');
INSERT INTO "badkhashani" VALUES(15,78,'15:78','و البته باشندگان ایکه (منطقۀ خلاب نیز) ظالم بودند.');
INSERT INTO "badkhashani" VALUES(15,79,'15:79','پس از آنان نیز انتقام گرفتیم، و هر دو (قریۀ خراب شده) بر سر راهی واضح و آشکار است.');
INSERT INTO "badkhashani" VALUES(15,80,'15:80','و البته اهل حجر (قوم ثمود نیز) پیغمبران را تکذیب کردند.');
INSERT INTO "badkhashani" VALUES(15,81,'15:81','و آیات خود را (توسط پیغمبر شان) برای ایشان فرستادیم ولی آنان روی گردان بودند.');
INSERT INTO "badkhashani" VALUES(15,82,'15:82','و از کوه‌ها خانه‌ها می‌تراشیدند که همیشه در امان باشند.');
INSERT INTO "badkhashani" VALUES(15,83,'15:83','پس صبحدم آواز سخت و مرگبار آنها را فراگرفت.');
INSERT INTO "badkhashani" VALUES(15,84,'15:84','پس آنچه کسب می‌کردند (عذاب الهی را) از ایشان دفع نکرد.');
INSERT INTO "badkhashani" VALUES(15,85,'15:85','و ما آسمان‌ها و زمین و آنچه را که در بین آندو است جز به حق نه آفریدیم و البته قیامت آمدنی است پس (فعلا) به طريقۀ خوب از آنها در گذر (بدون انتقام گرفتن).');
INSERT INTO "badkhashani" VALUES(15,86,'15:86','چون پروردگارت همان آفریدگار داناست.');
INSERT INTO "badkhashani" VALUES(15,87,'15:87','و البته سبع المثانی (هفت آیت سورۀ الفاتحه که در نمازها مکرر تلاوت می‌شود) و قرآن بزرگ را به تو دادیم.');
INSERT INTO "badkhashani" VALUES(15,88,'15:88','و چشمانت را به آنچه که گروه‌هایی از آنان را از آن بهره‌مند ساخته‌ایم مدوز و بر آنان غمگین مشو و بال مهربانی خود را برای مؤمنان هموار کن.');
INSERT INTO "badkhashani" VALUES(15,89,'15:89','و بگو: یقینا من بیم دهندۀ آشکارم.');
INSERT INTO "badkhashani" VALUES(15,90,'15:90','همان طور که (عذاب را) بر تقسیم کنندگان فرود آوردیم (شما را نیز از آن بیم می‌دهیم).');
INSERT INTO "badkhashani" VALUES(15,91,'15:91','آنانی که کتاب آسمانی را بخش بخش ساختند.');
INSERT INTO "badkhashani" VALUES(15,92,'15:92','پس قسم به پروردگارت که البته از همۀ آنها خواهیم پرسید.');
INSERT INTO "badkhashani" VALUES(15,93,'15:93','از آنچه می‌کردند.');
INSERT INTO "badkhashani" VALUES(15,94,'15:94','پس آنچه را که به (دعوت و تبلیغ) آن امر می‌شوی، اعلان و آشکار کن و از مشرکان روی بگردان.');
INSERT INTO "badkhashani" VALUES(15,95,'15:95','چون ما تو را از شر استهزا کنندگان کفایت کننده‌ایم.');
INSERT INTO "badkhashani" VALUES(15,96,'15:96','آنانی که معبود دیگری را با الله قرار می‌دهند، پس به زودی (حقیقت را) خواهند دانست.');
INSERT INTO "badkhashani" VALUES(15,97,'15:97','و البته ما خوب می‌دانیم که سینۀ تو تنگ می‌شود به سبب آنچه می‌گویند.');
INSERT INTO "badkhashani" VALUES(15,98,'15:98','پس پروردگارت را به پاکی یاد کن و از سجده کنندگان (نماز گزاران) باش.');
INSERT INTO "badkhashani" VALUES(15,99,'15:99','و پروردگارت را پرستش کن تا وقتی که یقین تو رسد (مرگ به سراغت بیاید).');
INSERT INTO "badkhashani" VALUES(16,1,'16:1','امر الله رسید (غلبۀ توحید و شکست كفار) پس آن را به شتاب طلب نکنید، او پاک و بلند مرتبه است از چیزهایی که با او شریک می‌سازند.');
INSERT INTO "badkhashani" VALUES(16,2,'16:2','ملائکه را با وحی، به حکم خود بر هرکس از بندگانش که بخواهد فرود می‌آرد، که بترسانید (مردم را از مخالفت با پیغام الهی) که هیچ معبودی برحق به جز من (الله) نیست، پس از من بترسید (و خود را از عذابم نگاه دارید).');
INSERT INTO "badkhashani" VALUES(16,3,'16:3','آسمان‌ها و زمین را به حق آفریده است، بلندمرتبه است از آنچه با او شریک می‌سازند.');
INSERT INTO "badkhashani" VALUES(16,4,'16:4','انسان را از نطفه‌ای (قطرۀ منی) آفرید، پس ناگهان وی خصومت کنندۀ آشکار است.');
INSERT INTO "badkhashani" VALUES(16,5,'16:5','چهار پایان را برای شما آفریده است در آنها برای شما وسیلۀ گرم شدن و فائده‌های دیگر است. و از آنها (گوشت و چربی آنها را) می‌خورید.');
INSERT INTO "badkhashani" VALUES(16,6,'16:6','و برایتان در آنها زینت و زیبایی است وقتیکه آنها را (شام از چراگاه) می‌آورید و وقتی که آنها را (صبح به چراگاه) می‌برید.');
INSERT INTO "badkhashani" VALUES(16,7,'16:7','و بارهای شما را به شهر و سرزمینی حمل می‌کنند که جز با رنج و مشقت به آن نمی‌توانستید برسید، یقینا پروردگارتان رؤوف و مهربان است.');
INSERT INTO "badkhashani" VALUES(16,8,'16:8','و اسپ‌ها و قاطرها و الاغ‌ها را آفریده تا بر آنها سوار شوید و زینتی (برای شما) هست. و چیزهایی (دیگر) می‌آفریند که شما نمی‌دانید.');
INSERT INTO "badkhashani" VALUES(16,9,'16:9','و بر عهدۀ الله است كه راه راست را (به مردم) نشان دهد، و برخی از آن (راه‌ها) کج است. و اگر (الله) می‌خواست همۀ شما را هدایت می‌کرد.');
INSERT INTO "badkhashani" VALUES(16,10,'16:10','او ذاتی است که از آسمان برای شما آبی فرو فرستاده است که از آن می‌نوشید و با آن درختان و گیاهان می‌رویند (و) حیوانات خود را در آن می‌چرانید.');
INSERT INTO "badkhashani" VALUES(16,11,'16:11','الله به وسيلۀ آن آب، کشت و زیتون و خرما و انگور و از همه نوع میوه می‌رویاند، به يقين در این چیزها نشانۀ روشنی است برای کسانی که می‌اندیشند.');
INSERT INTO "badkhashani" VALUES(16,12,'16:12','و شب و روز و آفتاب و ماه را برایتان مسخر کرد و ستارگان به حکم او مسخر شده‌اند. بی‌گمان در این کار برای گروهی که تعقل می‌کنند، نشانه‌هاست.');
INSERT INTO "badkhashani" VALUES(16,13,'16:13','و چیزهایی را که در زمین با رنگهای مختلف و در انواع گوناگون آفریده برای شما مسخر و رام گردانیده است. البته در این امر برای گروهی که یادآور شوند، نشانۀ روشنی است.');
INSERT INTO "badkhashani" VALUES(16,14,'16:14','و اوست که دریا را مسخر ساخته است تا از آن گوشت تازه بخورید و از آن زیوراتی بیرون بیاورید که آن را می‌پوشید. و کشتی‌ها را در آن شگافنده می‌بینی، و تا از فضل او طلب معیشت کنید و تا شکر گزاری کنید.');
INSERT INTO "badkhashani" VALUES(16,15,'16:15','و در زمین کوه‌های استوار و پابرجایی قرار داد تا (زمین) شما را نجنباند. و نهرها و راههایی را آفرید تا شما راه خود را بیابید.');
INSERT INTO "badkhashani" VALUES(16,16,'16:16','و نشانه‌هایی دیگر نیز قرار داد، و آنان به وسیلۀ ستارگان راه می‌یابند.');
INSERT INTO "badkhashani" VALUES(16,17,'16:17','پس آیا کسی که می‌آفریند مانند کسی است که نمی‌آفریند؟ آیا یادآور نمی‌شوید؟');
INSERT INTO "badkhashani" VALUES(16,18,'16:18','و اگر نعمت‌های الله را بشمارید آن را احاطه کرده نمی‌توانید، بی‌گمان الله آمرزندۀ مهربان است.');
INSERT INTO "badkhashani" VALUES(16,19,'16:19','و الله هرچه را که می‌پوشانید و هرچه را که اظهار می‌کنید، می‌داند.');
INSERT INTO "badkhashani" VALUES(16,20,'16:20','و آنانی را که جز الله (در حاجات خود) می‌خوانند، چیزی نمی‌آفرینند، بلکه خودشان آفریده می‌شوند.');
INSERT INTO "badkhashani" VALUES(16,21,'16:21','(آن معبودهای خود ساخته) مرده‌اند (و بی‌روح اند)، و نمی‌دانند چه وقت زنده و برانگیخته می‌شوند.');
INSERT INTO "badkhashani" VALUES(16,22,'16:22','(پس) معبود واقعی شما معبود یکتا است، لذا آنانی که به روز آخرت ایمان نمی‌آورند، دل‌هایشان انکارگر است، و آنها مستکبران‌اند.');
INSERT INTO "badkhashani" VALUES(16,23,'16:23','شکی نیست که آنچه را پنهان می‌کنید و آنچه را آشکار می‌سازید، (الله) می‌داند، يقينا او مستکبران را دوست نمی‌دارد.');
INSERT INTO "badkhashani" VALUES(16,24,'16:24','و چون به آنان گفته شود: پروردگارتان چه چیز را نازل کرده است؟ می‌گویند: افسانه‌های پیشینیان را (نازل کرده است).');
INSERT INTO "badkhashani" VALUES(16,25,'16:25','تا در روز قیامت به طور کامل بار گناهان خویش و بخشی از بار گناهان کسانی را حمل کنند که ایشان را به نادانی گمراه ساخته‌اند. و چه بد باری را حمل می‌کنند!');
INSERT INTO "badkhashani" VALUES(16,26,'16:26','البته آنانی که پیش از ایشان بودند (برای انکار توحید) مکر ورزیدند، پس الله از اساس بر بنیان‌شان زد و آنرا انداخت و سقف از بالای سرشان بر آنان افتاد و عذاب (الله) از جایی که نمی‌دانستند به آنان رسید.');
INSERT INTO "badkhashani" VALUES(16,27,'16:27','(این عذاب دنیا بود) باز روز قیامت آنان را رسوا می‌کند و می‌فرماید: کجا‌اند شریک‌های من که شما به خاطر آنها (با پیغمبران) مخالفت می‌کردید؟ صاحبان علم می‌گویند: به یقین خواری و بدی امروز نصيب كافران است.');
INSERT INTO "badkhashani" VALUES(16,28,'16:28','آنانی که فرشتگان ارواح ایشان را قبض می‌کنند، در حالی که برخود ظالم بودند، پس تسلیم می‌شوند (و می‌گویند:) ما هرگز هیچ کار بدی نمی‌کردیم، بلی! یقینا که الله به آنچه می‌کردید، داناست.');
INSERT INTO "badkhashani" VALUES(16,29,'16:29','پس از دروازه‌های دوزخ درآید و همیشه در آن باشید. و حقا که جای متکبران چه بد است.');
INSERT INTO "badkhashani" VALUES(16,30,'16:30','و به پرهیزگاران گفته می‌شود: پروردگارتان چه چیزی را نازل کرده است؟ می‌گویند: خیر را (نازل کرده است لهذا) برای نیکوکاران در همین دنیا نیکی است و يقينا سرای آخرت بهتر خواهد بود. و چه خوب است سرای پرهیزگاران!');
INSERT INTO "badkhashani" VALUES(16,31,'16:31','باغ‌های همیشگی بهشت که در آن وارد می‌شوند نهرها در زیر درختان آن روان است، در آنجا هر چه بخواهند دارند. الله این چنین به متقیان پاداش می‌دهد.');
INSERT INTO "badkhashani" VALUES(16,32,'16:32','آنان که فرشتگان ارواح‌شان را قبض می‌کنند در حالی که پاک‌اند. می‌گویند: سلام بر شما باد به خاطر کارهایی که می‌کردید به بهشت وارد شوید.');
INSERT INTO "badkhashani" VALUES(16,33,'16:33','آیا کافران انتظار نمی‌کشند جز این که فرشتگان به سوی‌شان بیایند، یا اینکه فرمان پروردگارت برسد، همانطور کسانی که پیش از ایشان بودند این چنین کردند؟ و الله بر آنان ظلم نکرد بلکه خودشان به خود ظلم می‌کردند.');
INSERT INTO "badkhashani" VALUES(16,34,'16:34','پس سزای بدی‌هایی که می‌کردند به ایشان رسید و آنچه به آن تمسخر می‌کردند آنان را در بر گرفت.');
INSERT INTO "badkhashani" VALUES(16,35,'16:35','و مشرکان گفتند: اگر الله می‌خواست نه ما و نه پدران ما غیر از او هیچ چیزی را عبادت نمی‌کردیم، و چیزی را بدون حکم او حرام نمی‌کردیم، کسانی که پیش از ایشان بودند نیز این چنین کردند. پس آیا جز ابلاغ آشکار چیزی دیگری بر عهدۀ پیغمبران است؟');
INSERT INTO "badkhashani" VALUES(16,36,'16:36','و به یقین ما در میان هر امتی پیغمبری را فرستادیم (تا به مردم بگویند:) الله را بپرستید و از طاغوت اجتناب ورزید. پس از میان ایشان کسانی بودند که الله آنها را هدایت کرد، و گروهی دیگر نیز بودند که گمراهی بر آنها ثابت شد. پس در زمین سیر کنید و بنگرید که انجام تکذیب کنندگان چگونه شد.');
INSERT INTO "badkhashani" VALUES(16,37,'16:37','اگر بر هدایت آنان حریص باشی بدان که الله کسی را که گمراه نماید هدایت نمی‌کند، و آنان هیچ مددگارانی ندارند.');
INSERT INTO "badkhashani" VALUES(16,38,'16:38','و (مشرکان) به طور جدی به الله قسم خوردند که الله کسی را که می‌میرد، زنده نمی‌گرداند، بلی! این وعدۀ حق بر عهدۀ اوست، ولی بیشتر مردم نمی‌دانند.');
INSERT INTO "badkhashani" VALUES(16,39,'16:39','تا برای‌شان بیان کند آنچه را که در مورد آن اختلاف می‌ورزند و تا کافران بدانند که خود آنان دروغگو بوده‌اند.');
INSERT INTO "badkhashani" VALUES(16,40,'16:40','چون امر ما برای ایجاد چیزی که اراده اش را کرده‌ایم، جز این نیست که می‌گوییم: موجود شو؛ پس فورا موجود می‌شود.');
INSERT INTO "badkhashani" VALUES(16,41,'16:41','و آنان که در راه الله هجرت کردند پس از اینکه مورد ستم قرار گرفتند، البته آنان را در این دنیا به سرای نیک جای می‌دهیم و البته پاداش آخرت بزرگتر است، اگر می‌دانستند.');
INSERT INTO "badkhashani" VALUES(16,42,'16:42','(آن هجرت کنندگان) کسانی‌اند که صبر کردند (بر ترک وطن و دشواریهای هجرت) و بر پروردگارشان توکل می‌کنند.');
INSERT INTO "badkhashani" VALUES(16,43,'16:43','و پیش از تو جز مردانی را که به آنان وحی می‌کردیم، نفرستادیم، پس اگر نمی‌دانید از اهل علم بپرسید.');
INSERT INTO "badkhashani" VALUES(16,44,'16:44','آنان را با دلایل روشن و کتابها فرستادیم. و قرآن را بر تو نازل کردیم تا برای مردم چیزی را که برای آنان فرستاده شده است بیان کنی، و تا آنان تفکر کنند.');
INSERT INTO "badkhashani" VALUES(16,45,'16:45','آیا کسانی که مکرهای بد سنجیدند، ایمن شده‌اند از اینکه الله آنان را در دل زمین فرو بَرَد یا اینکه عذاب از آن جایی که نمی‌دانند بر آنان بیاید؟!');
INSERT INTO "badkhashani" VALUES(16,46,'16:46','یا الله در حال رفت و آمدشان آنان را بگیرد؟ پس عاجز کننده نیستند.');
INSERT INTO "badkhashani" VALUES(16,47,'16:47','ا آنان را در حالت ترس‌شان بگیرد، پس یقینا پروردگارتان رؤوف (و) مهربان است.');
INSERT INTO "badkhashani" VALUES(16,48,'16:48','آیا به چیزهایی که الله آفریده است ندیدند که چگونه سایه‌های آن از راست و چپ سجده کنان و فروتنانه برای الله می‌گردند.');
INSERT INTO "badkhashani" VALUES(16,49,'16:49','و هر چه در آسمان‌ها و هرچه در زمین است از جنبده‌ها، برای الله سجده می‌کنند و فرشته‌ها نیز سجده می‌کنند و آنها از عبادت الله سرکشی نمی‌کنند.');
INSERT INTO "badkhashani" VALUES(16,50,'16:50','از پروردگارشان که بالای سرشان است می‌ترسند و آنچه را که به آنان دستور داده می‌شود، انجام می‌دهند.');
INSERT INTO "badkhashani" VALUES(16,51,'16:51','و الله فرمود: دو معبود برای خود نگیرید، جز این نیست که او معبود حق و یکتاست، پس تنها از من بترسید.');
INSERT INTO "badkhashani" VALUES(16,52,'16:52','و آنچه در آسمان‌ها و زمین است خاص از اوست، و دین پایدار نیز خاص از اوست، پس آیا از غير الله می‌ترسید؟');
INSERT INTO "badkhashani" VALUES(16,53,'16:53','و آنچه از نعمت‌ها با شماست همه از سوى الله است، باز چون زیان و ضرری به شما رسد، به‌سوی او می‌نالید.');
INSERT INTO "badkhashani" VALUES(16,54,'16:54','باز هنگامی که الله زیان را از شما دور کند آنگاه گروهی از شما به پروردگارشان شرک می‌آرند.');
INSERT INTO "badkhashani" VALUES(16,55,'16:55','(بگذار) تا در آنچه به آنان داده‌ایم ناشکری کنند. پس اکنون (از نعمت‌های دنیا) برخوردار شوید، پس به زودی خواهید دانست (که چه سرنوشت سیاهی دارید)!');
INSERT INTO "badkhashani" VALUES(16,56,'16:56','و از آنچه به آنان روزی داده‌ایم نصیبی برای بت‌هایی که )چیزی) نمی‌دانند مقرر می‌کنند. قسم به الله از آنچه که به دروغ افترا می‌کردید، پرسیده خواهید شد.');
INSERT INTO "badkhashani" VALUES(16,57,'16:57','و برای الله دختران را قرار می‌دهند، پاک است او، و برای خود آنچه را که دوست دارند قرار می‌دهند.');
INSERT INTO "badkhashani" VALUES(16,58,'16:58','و هرگاه به یکی از آنها مژده تولد دختر داده شود؛ در حالیکه پر از غصه و اندوه است، چهره اش سیاه می‌گردد.');
INSERT INTO "badkhashani" VALUES(16,59,'16:59','به خاطر مژدۀ بدی که به او داده شده، خود را از قوم پنهان می‌کند، (و با خود می‌گوید): آیا او را با خواری نگاه دارد یا وی را زنده به گور کند؟ آگاه باشید! که چه بد قضاوت می‌کردند.');
INSERT INTO "badkhashani" VALUES(16,60,'16:60','برای آنانی که به آخرت ایمان ندارند، وصف زشت است. و صفت والا برای الله است و او غالب باحکمت است.');
INSERT INTO "badkhashani" VALUES(16,61,'16:61','و اگر الله مردم را به سبب ظلمشان گرفتار می‌کرد هیچ جانداری را بر روی زمین باقی نمی‌گذاشت، لیکن آنان را تا مدت معینی مهلت می‌دهد، پس چون وقت مقرر آنان برسد لحظه‌ای (اجلشان) به تاخیر نمی‌افتد، و لحظه‌ای نیز پیش نمی‌شود.');
INSERT INTO "badkhashani" VALUES(16,62,'16:62','و برای الله چیزی را قرار می‌دهند که خوش ندارند، و زبانهایشان سخن دروغ بیان می‌کند (و می‌گوید:) که نیکویی از ایشان است. بدون شک آتش دوزخ از ایشان است و آنان پیش از دیگران به آنجا رانده می‌شوند.');
INSERT INTO "badkhashani" VALUES(16,63,'16:63','قسم به الله که پیغمبران را به‌سوی امت‌هایی که پیش از تو بودند فرستادیم. پس شیطان کارهایشان را در نظرشان زیبا جلوه داد، لذا امروز هم شیطان یاور شان است و عذاب دردناکی دارند.');
INSERT INTO "badkhashani" VALUES(16,64,'16:64','و ما کتاب را بر تو نازل نکردیم مگر بخاطر اینکه چیزی را برای‌شان بیان کنی که در آن اختلاف کرده‌اند و تا هدایت و رحمتی برای مؤمنان باشد.');
INSERT INTO "badkhashani" VALUES(16,65,'16:65','و الله از آسمان آبی نازل کرد و با آن زمین را پس از مردنش (خشک شدن) زنده ساخت، به يقين در این دلیل روشنی است برای قومی که (سخن الله را) می‌شنوند.');
INSERT INTO "badkhashani" VALUES(16,66,'16:66','و البته در چهارپایان (شتر و گاو و گوسفند) برای شما عبرت است، از آنچه در شکمهایشان است از میان سرگین و خون، شیری خالص به شما می‌نوشانیم که برای نوشندگان خوشخور و گوارا است.');
INSERT INTO "badkhashani" VALUES(16,67,'16:67','و نیز از میوه‌های درختان خرما و انگور شراب می‌سازید و روزی پاک و حلال از آن می‌گیرید، البته در این نشانه‌ای است برای قومی که درک می‌کنند.');
INSERT INTO "badkhashani" VALUES(16,68,'16:68','و پروردگارت به زنبور عسل الهام کرد که از کوه‌ها و از درختان و از آنچه مردم بنا می‌کنند، برای خود خانه‌ها بسازد.');
INSERT INTO "badkhashani" VALUES(16,69,'16:69','باز از همۀ میوه‌ها بخور و رام شده راه‌های پروردگارت را در پیش گیر، از شکم‌های زنبوران نوشیدنی به رنگهای مختلف بیرون می‌آید. در آن برای مردمان شفا است، بی‌گمان در این، نشانه‌ای است برای مردمی که تفکر می‌کنند.');
INSERT INTO "badkhashani" VALUES(16,70,'16:70','و الله شما را آفرید، باز شما را می‌میراند و برخی از شما به خوارترین (دورۀ) پیری باز گردانیده می‌شود طوریکه پس از دانستن چیزی نمی‌داند (و همه چیز را فراموش می‌کند)، چون الله دانای تواناست.');
INSERT INTO "badkhashani" VALUES(16,71,'16:71','و الله بعضی از شما را بر بعضی دیگر در روزی برتری داده است، آنانی که (در روزی) برتری داده شده‌اند حاضر نیستند که روزی خود را به بردگان خود بدهند تا همۀشان در آن (دارایی) مساوی باشند، پس آیا نعمت الله را انکار می‌کنند؟');
INSERT INTO "badkhashani" VALUES(16,72,'16:72','الله از جنس خودتان برای شما همسرانی قرار داد و از همسرانتان برای شما فرزندان و نوادگان آفرید و از چیزهای پاکیزه به شما روزی داد، آیا (باز هم) به باطل ایمان می‌آورند و به نعمت الله كفر می‌ورزند؟');
INSERT INTO "badkhashani" VALUES(16,73,'16:73','و به جای الله چیزهایی را عبادت می‌کنند که مالک کمترین رزقی در آسمان‌ها و زمین برای آنان نیستند و توانایی ندارند.');
INSERT INTO "badkhashani" VALUES(16,74,'16:74','پس برای الله امثال (مانندها) قرار ندهید (مخلوق را به او تشبیه نکنید)، بی‌گمان الله می‌داند و شما نمی‌دانید.');
INSERT INTO "badkhashani" VALUES(16,75,'16:75','الله بردۀ مملوکی را به طور مثال ذکر کرده است که برای (انجام) هیچ کاری قدرت ندارد، و نیز کسی را مثل می‌زند که از سوی خویش به او روزی پاکیزه داده است، پس او در پنهان و آشکار از آن انفاق می‌کند، آیا این دو برابرند؟ الحمد لله (سپاسها مخصوص الله است)، بلکه بیشتر آنان نمی‌دانند.');
INSERT INTO "badkhashani" VALUES(16,76,'16:76','و الله دو مرد را مَثَل می‌زند که یکی از آنان گنگ است و بر انجام چیزی توانایی ندارد، و او باردوش آقای خویش بوده و هر جا او را بفرستد خیری به همراه نمی‌آورد. آیا او برابر با کسی است که به عدل فرمان می‌دهد و بر راه راست قرار دارد؟');
INSERT INTO "badkhashani" VALUES(16,77,'16:77','و علم غیب آسمان‌ها و زمین مخصوص الله است. و کار قیامت به اندازۀ یک چشم زدن یا کمتر از آن است، بی‌گمان الله بر هر کاری قادر است.');
INSERT INTO "badkhashani" VALUES(16,78,'16:78','و الله شما را از شکم‌های مادران تان بیرون آورد، در حالی که هیچ چیز نمی‌دانستید، و برای شما گوش و چشم و دل داد، تا شکر او را به جا آرید.');
INSERT INTO "badkhashani" VALUES(16,79,'16:79','آیا به‌سوی پرندگان رام شده در فضای آسمان ندیده‌اند؟ آنها را کسی جز الله (از افتیدن) نگاه نمی‌دارد، يقينا در این (نمونۀ قدرت الهی) برای کسانی که ایمان دارند نشانه‌هاست.');
INSERT INTO "badkhashani" VALUES(16,80,'16:80','و الله از خانه‌هایتان برای شما جای آرامش و آسایش قرار داد، و از پوست چهارپایان خانه‌هایی برایتان قرار داده است که آنها را در وقت سفرتان و در روز اقامت تان سبک می‌یابید، و از پشم گوسفند و موی شتر و بز وسائل منزل (و موجبات رفاه) شما را آماده کرده است، تا مدتی معین از آنها استفاده کنید.');
INSERT INTO "badkhashani" VALUES(16,81,'16:81','و الله از چیزهایی که آفریده سایه‌هایی را برایتان ایجاد کرد، و از کوه‌ها برای شما غارها قرار داد، و برایتان جامه‌هایی ایجاد کرد که شما را از گرمی (و سردی) حفظ می‌کند و جامه‌هایی که شما را از آسیب جنگ حمایت می‌کند، این چنین او نعمتش را بر شما تمام می‌کند تا تسلیم و منقاد شوید.');
INSERT INTO "badkhashani" VALUES(16,82,'16:82','پس اگر رویگردان شدند (از پذیرش اسلام و ایمان، اندوهگین مشو) چون بر تو رساندن آشکار است و بس.');
INSERT INTO "badkhashani" VALUES(16,83,'16:83','نعمت‌های الله را می‌شناسند (لیکن) باز آن را انکار می‌کنند و بیشتر آنها کافر‌اند.');
INSERT INTO "badkhashani" VALUES(16,84,'16:84','و روزی که از هر امتی گواهی برانگیزیم، باز به کافران اجازه (عذر خواهی) داده نمی‌شود و از آنان خواسته نمی‌شود که (با توبه و عمل صالح) پروردگارشان را راضی کنند.');
INSERT INTO "badkhashani" VALUES(16,85,'16:85','و چون ظالمان عذاب را بینند، پس از آنها کاسته نشود (این عذاب) و نه مهلت داده می‌شوند.');
INSERT INTO "badkhashani" VALUES(16,86,'16:86','و چون مشركان شریکان (معبودان) خود را ببینند، می‌گویند: ای پروردگار ما! این‌ها‌اند شریکان ما که آنان را به جای تو می‌خواندیم، (معبودان) این سخنان آنها را رد می‌کنند (و می‌گویند:) که يقينا شما دروغگوئید.');
INSERT INTO "badkhashani" VALUES(16,87,'16:87','و آن روز به‌سوی الله پیغام تسلیم و انقیاد را می‌افگنند و آنچه را افترا می‌کردند از آنان گم می‌شود.');
INSERT INTO "badkhashani" VALUES(16,88,'16:88','آنانی که کافر شدند و از راه (دین) الله منع نمودند، عذابی بر عذابشان می‌افزاییم به سبب اینکه )در زمین( فساد می‌کردند.');
INSERT INTO "badkhashani" VALUES(16,89,'16:89','و به یاد آور روزی را که در هر امت گواهی از خود آنها بر آنها می‌فرستیم و تو را هم بر ایشان گواه آوریم. و کتاب را (به تدریج) بر تو نازل کردیم که بیانگر هر چیز و وسيلۀ هدایت و رحمتی (است). و بشارتی‌ است برای مسلمانان.');
INSERT INTO "badkhashani" VALUES(16,90,'16:90','البته الله به عدل و احسان و بخشش به خویشاوندان امر می‌کند و از فحشا (کار زشت) و ناشایست و تجاوز منع می‌کند. و شما را پند می‌دهد تا عبرت گیرید.');
INSERT INTO "badkhashani" VALUES(16,91,'16:91','و به عهد الله چون عهد کرديد، وفا کنید. و قسم‌ها را پس از تأكید آن نشکنید، در حالیکه الله را بر خود کفیل (ضامن) گردانیده‌اید، بی‌گمان الله آنچه را که می‌کنید، می‌داند.');
INSERT INTO "badkhashani" VALUES(16,92,'16:92','و مانند آن (زن) نباشید که رشتۀ خود را پس از محکم تابیدن گسست، تا قسم‌های تان را در میان خود وسیلۀ فریب سازید، تا گروهی از گروهی دیگر (مال و امکانات) بیشتر بدست آورد. جز این نیست که الله شما را به وسيلۀ آن می‌آزماید، و به يقين روز قیامت آنچه را که در آن اختلاف می‌کردید برایتان بیان می‌کند.');
INSERT INTO "badkhashani" VALUES(16,93,'16:93','و اگر الله می‌خواست شما را امت واحد (مسلمان) می‌گردانید ولی کسی را که بخواهد گمراه می‌کند و کسی را که بخواهد هدایت می‌کند. و البته از آنچه در دنیا می‌کردید، پرسیده خواهید شد.');
INSERT INTO "badkhashani" VALUES(16,94,'16:94','و قسم‌هایتان را در میان خود وسیلۀ فریب قرار ندهید، تا (مبادا) قدمهایتان پس از محکم شدنش بلغزد، و به سبب منع کردنتان از راه الله عذاب را بچشید و در آخرت عذاب بزرگ داشته باشید.');
INSERT INTO "badkhashani" VALUES(16,95,'16:95','و عهد الله را به بهای حقیر (دنیا) نفروشید، زیرا آنچه نزد الله است همان برای شما بهتر است، اگر بدانید.');
INSERT INTO "badkhashani" VALUES(16,96,'16:96','(چون) آنچه نزد شماست فانی شدنی است و آنچه نزد الله است (برای همیشه) باقی می‌ماند. و البته صابران را (نظر) به نیکوترین آنچه عمل می‌کردند، ثواب خواهیم داد.');
INSERT INTO "badkhashani" VALUES(16,97,'16:97','هر کس خواه زن و خواه مرد، کار نیک انجام دهد و او مؤمن باشد، البته به زندگی پاک و خوشایند زنده اش می‌کنیم. و حتما (نظر) به نیکوترین آنچه عمل می‌کردند، ثوابشان خواهیم داد.');
INSERT INTO "badkhashani" VALUES(16,98,'16:98','پس هرگاه که خواستی قرآن بخوانی از (شر) شیطان رانده شده به الله پناه بخواه.');
INSERT INTO "badkhashani" VALUES(16,99,'16:99','بی‌گمان شیطان بر کسانی که ایمان آورده‌اند و بر پروردگارشان توکل می‌کنند، هیچ تسلطی ندارد.');
INSERT INTO "badkhashani" VALUES(16,100,'16:100','سلطۀ او تنها بر کسانی است که او را به دوستی می‌گیرند و (سلطۀ او) بر کسانی است که آنها به او (الله) شرک می‌آورند.');
INSERT INTO "badkhashani" VALUES(16,101,'16:101','و چون آیتی را به جای آیت دیگر بدل کنیم و الله به آنچه نازل می‌کند داناتر است. (دوستان شیطان) می‌گویند: جز این نیست که تو افتراکننده ای؛ بلکه اکثر آنها نمی‌دانند.');
INSERT INTO "badkhashani" VALUES(16,102,'16:102','بگو: روح القدس (جبرئیل) آن را به حق از سوی پروردگارت نازل کرده است، تا مؤمنان را ثابت قدم گرداند و تا هدایت و مژده‌ای باشد برای مسلمانان.');
INSERT INTO "badkhashani" VALUES(16,103,'16:103','و البته می‌دانیم که آنان می‌گویند: جز این نیست که بشری این قرآن را به او می‌آموزد. زبان کسی که این را به او نسبت می‌دهند، عجمی (غیر عربی) است. و این (قرآن) به زبان عربی واضح و روشن است.');
INSERT INTO "badkhashani" VALUES(16,104,'16:104','يقينا آنان که به آيات الله ایمان نمی‌آورند، الله آنها به راه راست نمی‌برد، (بلکه) برایشان عذاب دردناک است.');
INSERT INTO "badkhashani" VALUES(16,105,'16:105','جز این نیست که فقط کسانی دروغ می‌بندند که به آیات الله ایمان ندارند و ایشان خود دروغگویانند.');
INSERT INTO "badkhashani" VALUES(16,106,'16:106','کسی که به (وحدانیت) الله بعد از ایمان آوردن خود کافر شود، مگر آن کس که (به کفر گویی) مجبور شده ولی قلبش به ایمان آرام و استوار است. ولی کسی که به کفر سینه گشاده کند، پس غضبی از طرف الله بر آنان است. و برایشان عذاب بزرگ خواهد بود.');
INSERT INTO "badkhashani" VALUES(16,107,'16:107','این (غضب الهی) به سبب آنست که آنها زندگی دنیا را دوست داشتند و بر آخرت ترجیح دادند و اینکه الله قوم کافر را هدایت نمی‌کند.');
INSERT INTO "badkhashani" VALUES(16,108,'16:108','ایشان کسانی‌اند که الله بر دلها و گوشها و چشمانشان مهر زده است آنان غافلانند.');
INSERT INTO "badkhashani" VALUES(16,109,'16:109','شک نیست در اینکه آنها در آخرت خساره کنندگان‌اند.');
INSERT INTO "badkhashani" VALUES(16,110,'16:110','باز پروردگارت نسبت به کسانی که هجرت کردند پس از آنکه شکنجه شدند، باز جهاد کردند و صبر کردند، البته آمرزندۀ مهربان است.');
INSERT INTO "badkhashani" VALUES(16,111,'16:111','روزی که هر شخص جدال کنان از خود دفاع کند. و هر کسی به سزای آنچه کرده، بدون کم و کاست پاداش داده شود و به آنها ظلم نمی‌شود.');
INSERT INTO "badkhashani" VALUES(16,112,'16:112','الله (مردمان) قریه‌ای را مثال می‌زند که در امن و امان و آسودگی بود و از هر طرف روزیش فراوان به سویش می‌رسید، اما کفران نعمت الله را کردند و الله به سزای کاری که انجام دادند لباس گرسنگی و ترس را (به تن آنان کرد و طعم گرسنگی را) به ایشان چشانید.');
INSERT INTO "badkhashani" VALUES(16,113,'16:113','البته پیغمبر از خودشان به‌سوی آنان آمد ولی او را دروغگو شمردند، پس در حالی که ظالم بودند، عذاب آنها را فرا گرفت.');
INSERT INTO "badkhashani" VALUES(16,114,'16:114','پس از روزی حلال و پاکیزه‌ای بخورید که الله به شما روزی داده است و شکر نعمت الله را به جای آورید اگر تنها او را می‌پرستید.');
INSERT INTO "badkhashani" VALUES(16,115,'16:115','جز این نیست که مردار و خون و گوشت خوک و آنچه نام غير الله (به وقت ذبح) بر آن گفته شده باشد بر شما حرام کرده شده است، ولی اگر کسی به خوردن آنها مجبور شود، در صورتی که علاقمند نبوده و متجاوز نباشد (بر وی ایرادی نیست) يقينا الله آمرزندۀ مهربان است.');
INSERT INTO "badkhashani" VALUES(16,116,'16:116','و برای آنچه که زبانتان به دروغ بیان می‌کند نگویید این حلال است و این حرام، تا بر الله دروغ ببندید، چون کسانی که بر الله دروغ می‌بندند، کامیاب نمی‌شوند.');
INSERT INTO "badkhashani" VALUES(16,117,'16:117','(بلکه برایشان در دنیا) نفع اندک است و برای آنها عذاب دردناک است.');
INSERT INTO "badkhashani" VALUES(16,118,'16:118','و (ما) بر یهودیان چیزهایی را حرام کردیم که قبلا برای تو (در سورۀ انعام) حکایت کردیم، و ما به آنان ظلم نکردیم بلکه خودشان به خویشتن ظلم می‌کردند.');
INSERT INTO "badkhashani" VALUES(16,119,'16:119','باز (هم دروازۀ توبه برایشان باز است) چون پروردگار تو برای آنان که به نادانی کارهای بد کردند باز بعد از آن توبه کردند و اصلاح کردند (عقیده و عمل خود را)، البته پروردگار تو بعد از این (توبه و اصلاح) آمرزندۀ مهربان است.');
INSERT INTO "badkhashani" VALUES(16,120,'16:120','بی‌گمان ابراهیم یک امت (و) مطيع امر الله بود در حالی که او بیزار از دین باطل بود و از مشرکان نبود.');
INSERT INTO "badkhashani" VALUES(16,121,'16:121','شکر گزار نعمت‌های وی (الله) بود، او را برگزید و به راه راست هدایتش کرد.');
INSERT INTO "badkhashani" VALUES(16,122,'16:122','و در این دنیا به او حسنه (نبوت، فرزند صالح) دادیم و همانا او در روز آخرت نیز از نیکان و شایستگان است.');
INSERT INTO "badkhashani" VALUES(16,123,'16:123','باز به‌سوی تو وحی کردیم که از دین ابراهیم که از ادیان باطل بیزار بود، پیروی کن (زیرا) او از مشرکان نبود.');
INSERT INTO "badkhashani" VALUES(16,124,'16:124','جز این نیست که روز شنبه بر کسانی مقرر گردید که در بارۀ آن اختلاف ورزیدند، و بی‌گمان پروردگارت روز قیامت میان آنها دربارۀ آنچه در آن اختلاف می‌کردند، فيصله خواهد کرد.');
INSERT INTO "badkhashani" VALUES(16,125,'16:125','(مردم را) با حکمت و اندرز نیکو به راه پروردگارت دعوت بده و با آنها به شیوۀ که نیکوتر است مجادله کن. همانا پروردگارت به حال کسی که از راه او گمراه و منحرف می‌شود آگاهتر است، و به هدایت یافته گان داناتر است.');
INSERT INTO "badkhashani" VALUES(16,126,'16:126','و اگر خواستید عقوبت کنید به همان اندازه‌ای که مورد عقوبت قرار گرفته‌اید، عقوبت کنید (و عذاب دهید) و اگر صبر کنید البته صبر برای صابران بهتر است.');
INSERT INTO "badkhashani" VALUES(16,127,'16:127','و (تو ای محمد) صبر کن و صبر تو جز به توفيق الله نیست، و بر آنان (کفار) غمگین مشو، و از مکر و حیله آنها دل تنگ مشو.');
INSERT INTO "badkhashani" VALUES(16,128,'16:128','بی‌تردید، الله با پرهیزگاران است و با کسانی که آنها نیکوکار‌اند.');
INSERT INTO "badkhashani" VALUES(17,1,'17:1','پاک و منزه است آن ذاتی که بندۀ خود را در شبی از مسجد الحرام به مسجد الاقصی برد که اطراف آن را برکت داده‌ایم، تا برخی از نشانه‌های (قدرت) خود را به او نشان دهیم، چون اوست که شنوای بیناست.');
INSERT INTO "badkhashani" VALUES(17,2,'17:2','و به موسی کتاب دادیم و آن را برای بنی اسرائیل وسیلۀ هدایت گردانیدیم که غیر از من کارساز دیگری نگیرید.');
INSERT INTO "badkhashani" VALUES(17,3,'17:3','فرزندان کسانی که آنها را با نوح سوار کشتی کردیم، همانا او بندۀ شکر گزار بود.');
INSERT INTO "badkhashani" VALUES(17,4,'17:4','و به بنی اسرائیل در تورات خبر دادیم که البته در زمین دو بار فساد خواهید کرد و البته سرکشی و طغیان بزرگ خواهید کرد.');
INSERT INTO "badkhashani" VALUES(17,5,'17:5','پس (به یاد داشته باشید) وقتی وعدۀ اول آن دو برسد، بندگان سخت جنگجوی خود را بر شما می‌فرستیم، پس میان خانه‌ها به جستجو در آیند، و این وعده، آمدنی و تحقق یافتنی است.');
INSERT INTO "badkhashani" VALUES(17,6,'17:6','باز شما را بر آنان مسلط می‌گردانیم و شما را با اموال و فرزندان مدد می‌رسانیم و تعداد افرادتان را بیشتر می‌گردانیم.');
INSERT INTO "badkhashani" VALUES(17,7,'17:7','و اگر نیکی کنید برای خودتان نیکی کرده‌اید و اگر بدی کنید به خودتان بدی کرده‌اید. پس چون وعده دیگر (دوم) برسد (باز بندگان خود را می‌فرستیم) تا روی شما را ناخوش سازند و تا به مسجد وارد شوند طوری که بار اول در آن داخل شدند و تا نابود کنند بر هر چه دست یابند، نابودی کوبنده.');
INSERT INTO "badkhashani" VALUES(17,8,'17:8','(اگر توبه کنید بعد از بار دوم) امید است پروردگارتان به شما رحم کند. و اگر (به طغيان تان) باز گردید، ما نیز (برای مجازات تان) باز می‌گردیم. و دوزخ را برای کافران زندان ساخته‌ایم.');
INSERT INTO "badkhashani" VALUES(17,9,'17:9','همانا این قرآن به راهی هدایت می‌کند که آن پایدارتر است و به مؤمنانی که کارهای شایسته و پسندیده می‌کنند مژده می‌دهد که آنان پاداش بزرگ دارند.');
INSERT INTO "badkhashani" VALUES(17,10,'17:10','(و نیز خبر می‌دهد) کسانی که به آخرت ایمان نمی‌آورند برایشان عذاب دردناک آماده کرده‌ایم.');
INSERT INTO "badkhashani" VALUES(17,11,'17:11','و انسان دعای شر می‌کند، همان طوری که دعای خیر می‌کند، و انسان (همیشه) شتابکار است.');
INSERT INTO "badkhashani" VALUES(17,12,'17:12','و شب و روز را دو نشانه قرار دادیم، و نشانۀ شب را محو ساختیم و نشانۀ روز را روشنی بخش گردانیدیم تا فضل (رزق) پروردگارتان را بجویید و تا عدد سالها و حساب را بدانید. و ما هر چیزی را به تفصیل بیان نموده‌ایم.');
INSERT INTO "badkhashani" VALUES(17,13,'17:13','و ما اعمال هر کسی را در گردنش (تقدیرش) آویخته‌ایم و روز قیامت کتابی را برای وی بیرون می‌آوریم که آن را باز گشاده بیابد.');
INSERT INTO "badkhashani" VALUES(17,14,'17:14','(به او گفته شود) نامه ات را بخوان، کافی است که امروز خودت حسابگر خود باشی.');
INSERT INTO "badkhashani" VALUES(17,15,'17:15','هر کس که راه (حق را) یابد جز این نیست که به نفع خود راهیاب شده است، و هرکس گمراه شد جز این نیست که به زیان خود گمراه شده است، و هیچ کس بار گناه دیگری را بر نمی‌دارد، و ما عذاب کننده نیستیم مگر اینکه پیغمبری را بفرستیم.');
INSERT INTO "badkhashani" VALUES(17,16,'17:16','و چون بخواهیم که قریه‌ای (مجرم) را هلاک کنیم، سرکشان آن را فرمان می‌دهیم پس در آن نافرمانی می‌کنند، و در نتیجه عذاب بر آن قریه ثابت گردد، پس آن را کاملا زیر و زبر می‌کنیم.');
INSERT INTO "badkhashani" VALUES(17,17,'17:17','و چه بسا نسلها را بعد از نوح هلاک نمودیم و کافی است که پروردگارت از گناهان بندگانش آگاه باشد و (او) بس بیناست.');
INSERT INTO "badkhashani" VALUES(17,18,'17:18','کسی که (دنیای) زودگذر را بخواهد، در آن هرچه را بخواهیم زود به او می‌دهیم، باز جهنم را برایش مقرر می‌نماییم که در آن (آتش به صورت) سرزنش شده و رانده شده در آید.');
INSERT INTO "badkhashani" VALUES(17,19,'17:19','و هر کس که آخرت را بخواهد و برای آن طوری که سزاوار است کوشش کند، در حالی که مؤمن است. پس این جماعت کوشش‌شان (نزد الله) قابل تقدیر (و مورد قبول) است.');
INSERT INTO "badkhashani" VALUES(17,20,'17:20','و هر یک از اینها و آنها (دو گروه یاد شده) را از بخشش پروردگارت می‌دهیم. و بخشش پروردگارت (از هیچ کس) منع نشده است.');
INSERT INTO "badkhashani" VALUES(17,21,'17:21','ببین چطور بعضی از آنها را بر بعضی دیگر (در روزی و مال و متاع) برتری داده‌ایم. و البته درجات آخرت و فضیلت آن به مراتب بزرگ‌تر است.');
INSERT INTO "badkhashani" VALUES(17,22,'17:22','(ای انسان) معبود دیگری را با الله قرار مده که باز مذمت شده و بی‌مددگار بنشینی.');
INSERT INTO "badkhashani" VALUES(17,23,'17:23','و پروردگارت حکم کرد که جز او چیزی را عبادت نکنید و به پدر و مادر خوبی کنید. و اگر یکی از آنها یا هردو نزد تو به سن پیری برسند، پس به ایشان (حتی کلمۀ) اف مگو و بر آنها آواز بلند مکن، (بلکه) به آنها سخن نرم و محترمانه بگو.');
INSERT INTO "badkhashani" VALUES(17,24,'17:24','و از روی مهربانی بال تواضع را برای آنان هموار کن و بگو: ای پروردگارم! به آنان (پدر و مادرم) رحم کن طوری که آنان در خوردی مرا پرورش و بزرگ نمودند.');
INSERT INTO "badkhashani" VALUES(17,25,'17:25','پروردگار تان به آنچه در نفسهای شماست داناتر است، اگر نیکان (و توبه کنندگان) باشید، پس یقینا الله برای رجوع کنندگان به بارگاه او آمرزنده هست.');
INSERT INTO "badkhashani" VALUES(17,26,'17:26','و حق خویشاوند و مسکین و مسافر (در راه مانده) را بده. و هیچگاه (مالت را در گناه یا) بر وجه اسراف و تبذیر، خرج مكن.');
INSERT INTO "badkhashani" VALUES(17,27,'17:27','چون اسراف‌کنندگان (و انفاق کنندگان در گناه) برادران شیاطین هستند و شیطان در مقابل پروردگارش ناشکر است.');
INSERT INTO "badkhashani" VALUES(17,28,'17:28','و اگر از آنان (مستحقان) به خاطر طلب رحمت (رزق) پروردگارت که به آن امید داری، رویگردان می‌شوی، پس با آنان سخن ملایم بگو.');
INSERT INTO "badkhashani" VALUES(17,29,'17:29','و دست خود را به گردنت بسته مکن و بسیار هم آنرا گشاده مکن که آنگاه ملامت شده (و) درمانده بنشینی.');
INSERT INTO "badkhashani" VALUES(17,30,'17:30','البته پروردگارت روزی را برای هرکسی که بخواهد می‌گشاید و (روزی هرکسی را که بخواهد) تنگ می‌گرداند، چرا که الله به (مصالح) بندگان خود دانا (و) بیناست.');
INSERT INTO "badkhashani" VALUES(17,31,'17:31','و اولادتان را از ترس ناداری و تنگدستی نکشید، ما به آنان و (به) شما روزی می‌دهیم، چون کشتن آنها گناه بس بزرگ است.');
INSERT INTO "badkhashani" VALUES(17,32,'17:32','و به زنا نزدیک مشويد که يقينا آن گناه بسیار زشت و راه بد است.');
INSERT INTO "badkhashani" VALUES(17,33,'17:33','و نفسی را که الله کشتن آن را حرام کرده است نکشید مگر به حق، و هرکس به ظلم کشته شود برای ولی (صاحب خون او) سلطه‌ای داده‌ایم، پس او نباید در کشتن زیاده روی کند، يقينا که او یاری داده شده است.');
INSERT INTO "badkhashani" VALUES(17,34,'17:34','و به مال يتيم نزدیک مشوید مگر به طریقۀ نیکوتر، تا اینکه (یتیم) به (سن) رشدش برسد، و به عهد و پیمان خود وفا کنید، چرا که از پیمان و عهد پرسیده خواهد شد.');
INSERT INTO "badkhashani" VALUES(17,35,'17:35','و هرگاه پیمانه می‌کنید آن را به تمام و کمال پیمانه کنید و با ترازوی راست و درست وزن کنید که این کار سرانجام نیکوتر و بهتری دارد.');
INSERT INTO "badkhashani" VALUES(17,36,'17:36','و از پی چیزی مرو که به آن علم نداری، بی‌گمان گوش و چشم و دل هر یک مورد سوال واقع خواهند شد.');
INSERT INTO "badkhashani" VALUES(17,37,'17:37','و در زمین به ناز و تکبر راه مرو، چون تو زمین را شگافته نمی‌توانی و به بلندی کوه‌ها رسیده نمی‌توانی.');
INSERT INTO "badkhashani" VALUES(17,38,'17:38','همۀ این خصلت‌ها، بدی‌هایش نزد پروردگارت ناپسند است.');
INSERT INTO "badkhashani" VALUES(17,39,'17:39','این (سفارش‌های مذکور) از امور حکمت‌آمیزی است که پروردگارت به سویت وحی فرستاده است و با الله معبودی دیگر قرار مده که سرزنش شده و رانده شده در دوزخ افگنده خواهی شد.');
INSERT INTO "badkhashani" VALUES(17,40,'17:40','آیا پروردگارتان شما را به داشتن پسران برگزیده و برای خود از فرشته‌ها دختران گرفته است؟ واقعا شما سخن بسیار بزرگ می‌گویید.');
INSERT INTO "badkhashani" VALUES(17,41,'17:41','به يقين که ما در این قرآن (حقایق را به روشهای) گوناگون بیان کردیم تا پند و عبرت گیرند، (ولی) جز بر نفرت و گریز‌شان نمی‌افزاید.');
INSERT INTO "badkhashani" VALUES(17,42,'17:42','بگو: اگر با او چنان که می‌گویند معبودان دیگر بود، پس در آن صورت حتما به‌سوی صاحب عرش راهی تلاش می‌کردند (تا با او تعالی کشمکش کرده و بر او غلبه نمایند).');
INSERT INTO "badkhashani" VALUES(17,43,'17:43','(لیکن) او پاک و منزه و بلندمرتبه است از آنچه (مشرکین) می‌گویند به بلندی بس بزرگ.');
INSERT INTO "badkhashani" VALUES(17,44,'17:44','آسمان‌های هفت گانه و زمین و هر که در آنها هستند، او را به پاکی یاد می‌کنند. و هیچ چیزی نیست مگر اینکه او را در حال ستایش تسبیح می‌گویند، ولی شما تسبيح آنها را نمی‌فهمید، بی‌گمان او بردبار (و) آمرزنده است.');
INSERT INTO "badkhashani" VALUES(17,45,'17:45','و چون قرآن بخوانی، بین تو و آنانی که به آخرت ایمان ندارند، پردۀ پوشیده قرار می‌دهیم.');
INSERT INTO "badkhashani" VALUES(17,46,'17:46','و بر دل‌هایشان پرده‌هایی می‌نهیم تا آن (قرآن) را نفهمند و در گوش‌هایشان گرانی ایجاد می‌کنیم و هنگامی که پروردگارت را در قرآن به یگانگی یاد می‌کنی، با نفرت پشت کرده و بر می‌گردند.');
INSERT INTO "badkhashani" VALUES(17,47,'17:47','ما بهتر می‌دانیم که آنان به چه منظور به سخنان تو گوش می‌دهند، وقتی برای گوش کردن سخنانت می‌نشینند و هنگامی که با هم پنهانی صحبت می‌کنند، وقتی که ظالمان می‌گویند: (مسلمانان) جز مرد جادو شده را پیروی نمی‌کنید.');
INSERT INTO "badkhashani" VALUES(17,48,'17:48','ببین که چطور برای تو مثال‌ها زدند! پس گمراه شدند، لذا نمی‌توانند راه حق را پیدا کنند.');
INSERT INTO "badkhashani" VALUES(17,49,'17:49','و گفتند: آیا وقتی که ما (تبدیل به) استخوانها و اعضای پوسیده شدیم، آیا بار دیگر به آفرینش جدید برانگیخته می‌شویم؟');
INSERT INTO "badkhashani" VALUES(17,50,'17:50','بگو: (بلی، حتی اگر)، سنگ باشید یا آهن.');
INSERT INTO "badkhashani" VALUES(17,51,'17:51','یا مخلوقی دیگر که در نظر تان بزرگ (و سخت) معلوم می‌شود، (باز هم الله می‌تواند شما را دوباره زنده کند). پس خواهند گفت: چه کسی ما را دوباره زنده می‌کند؟ بگو: (همان) ذاتی که بار اول شما را آفرید. پس (از روی تمسخر) سرهایشان را به سویت تکان داده و می‌گویند: آن کی خواهد بود؟ بگو: شاید که نزدیک باشد.');
INSERT INTO "badkhashani" VALUES(17,52,'17:52','روزی که (الله) شما را بخواند، پس حمدگویان دعوت او را اجابت می‌کنید، و گمان می‌کنید درنگ نکرده بودید مگر مدت اندک.');
INSERT INTO "badkhashani" VALUES(17,53,'17:53','و (ای پیغمبر) به بندگانم بگو: سخنی را بگویند که بهتر است. چون شیطان در میان ایشان فساد و دشمنی می‌اندازد، همانا شیطان برای انسان دشمن آشکار است.');
INSERT INTO "badkhashani" VALUES(17,54,'17:54','پروردگارتان به حال شما آگاه‌تر است، اگر (الله) بخواهد به شما رحم می‌کند و اگر (الله) بخواهد شما را عذاب میدهد. و تو را وکیل و نگهبان بر آنان نفرستاده‌ایم.');
INSERT INTO "badkhashani" VALUES(17,55,'17:55','و پروردگارت به حال هر آنچه که در آسمان‌ها و زمین است آگاه‌تر است. و البته بعضی از پیغمبران را بر بعضی دیگر فضیلت دادیم و به داود زبور را دادیم.');
INSERT INTO "badkhashani" VALUES(17,56,'17:56','بگو: آنانی را که به جای الله معبود می‌پنداشتید بخوانید. آنها نه می‌توانند زیانی را از شما دفع کنند و نه )می‌توانند آن را( تغییر دهند.');
INSERT INTO "badkhashani" VALUES(17,57,'17:57','معبودهای را که مشرکین (به یاری و دعا) می‌خوانند، (مانند فرشتگان و عیسی و عزیر)، خود‌شان به‌سوی پروردگارشان وسیله‌ای (برای تقرب) می‌جویند که کدام یک از آنها به الله نزدیکترند و به رحمت وی امیدوارند و از عذاب او می‌ترسند، چرا که عذاب پروردگارت قابل حذر و ترسیدن است.');
INSERT INTO "badkhashani" VALUES(17,58,'17:58','هیچ قریه‌ای نیست )که اهل آن ظالم باشند( مگر اینکه ما پیش از روز قیامت آن را به هلاکت می‌رسانیم، یا آن را به عذاب سخت گرفتار می‌کنیم. این (وعده) در کتاب (لوح محفوظ) نوشته شده است.');
INSERT INTO "badkhashani" VALUES(17,59,'17:59','و (چیزی) ما را از فرستادن معجزات (مورد نظر مشرکین) بازنداشت، مگر اینکه گذشتگان آن را دروغ شمردند. و به ثمود ماده شتر دادیم تا نشانۀ پیش چشمان باشد، پس به آن ظلم کردند و ما معجزه‌ها را جز برای بیم دادن نمی‌فرستیم.');
INSERT INTO "badkhashani" VALUES(17,60,'17:60','و به یاد آور وقتی که به تو گفتیم: بدون شک پروردگارت به مردم احاطه کامل دارد. و رؤيا و خوابی را که به تو نشان دادیم و درخت نفرین شده در قرآن را جز (وسیلۀ) آزمایش برای مردم قرار ندادیم، ما ایشان را بیم می‌دهیم، ولی جز بر سرکشی شدید آنان نمی‌افزاید.');
INSERT INTO "badkhashani" VALUES(17,61,'17:61','و یادآور شو چون به فرشته‌ها گفتیم که برای آدم سجده کنید، پس (همۀ آنها) سجده کردند مگر ابلیس که (با وجود سجده نکردن) گفت: آیا برای کسی سجده کنم که (او را) از گل آفریده ای؟');
INSERT INTO "badkhashani" VALUES(17,62,'17:62','(و) گفت: (ای الله!) به من خبر ده آیا این همان کسی است که او را بر من برتری داده‌ای؟ اگر مرا تا روز قیامت مهلت دهی، حتما همه فرزندانش را -مگر اندکی- ریشه‌کن (و تابع خود) می‌کنم.');
INSERT INTO "badkhashani" VALUES(17,63,'17:63','(الله) فرمود: برو، هر کس از آنان از تو پیروی کند، سزای همۀ شما جهنم است، که سزای وافر (و تمام) است.');
INSERT INTO "badkhashani" VALUES(17,64,'17:64','و هرکس از آنان را که می‌توانی با آواز خویش تحریک کن و سواران و پیادگانت را علیه آنان جلب کن و در اموال و اولاد با آنان شریک شو و به آنان وعده بده. و شیطان جز نیرنگ و فریب به آنها وعده نمی‌دهد.');
INSERT INTO "badkhashani" VALUES(17,65,'17:65','در حقیقت تو را بر بندگان من هیچ تسلطی نیست و همین کافی است که پروردگارت حافظ و حامی (آنها) است.');
INSERT INTO "badkhashani" VALUES(17,66,'17:66','پروردگارتان آن ذاتی است که کشتی‌ها را در دریا برایتان روان می‌کند تا از فضل او طلب کنید، چرا که او همیشه بر شما مهربان است.');
INSERT INTO "badkhashani" VALUES(17,67,'17:67','و هرگاه در بحر سختی‌ها به شما رسد، جز الله همۀ کسانی را که می‌خوانید از نظرتان ناپدید می‌گردند، اما وقتی که (الله) شما را نجات دهد و به خشکی برساند روی گردان می‌شوید. و انسان بسیار ناشکر است.');
INSERT INTO "badkhashani" VALUES(17,68,'17:68','آیا مطمئن و ایمن شده‌اید از اینکه شما را در کناری از خشکی در زمین فرو برد، یا طوفان سنگریزه بر شما بباراند؟ باز برای خود یاری کننده‌ای نیابید.');
INSERT INTO "badkhashani" VALUES(17,69,'17:69','یا مگر ایمن شده‌اید از اینکه شما را به بحر بازگرداند، پس تند باد شکننده بر شما بفرستد، پس به سبب كفرتان شما را غرق کند؟! باز برای خود هیچ بازخواست کننده‌ای علیه ما به سبب آن غرق نیابید.');
INSERT INTO "badkhashani" VALUES(17,70,'17:70','و البته اولاد آدم را گرامی داشته و اکرام نمودیم و آنان را در خشکی و بحر (بر وسایل سفر) سوار کردیم و به آنان از چیزهای پاکیزه روزی دادیم و آنان را بر بسیاری از آفریدگان خویش برتری آشکار دادیم.');
INSERT INTO "badkhashani" VALUES(17,71,'17:71','یادآور شوید روزی را که هر گروهی را با پیشوای‌شان طلب کنیم، پس هر کس نامۀ اعمالش به دست راستش داده شود، پس این گروه نامۀ اعمال خود را می‌خوانند. و به اندازه رشتۀ (شگاف وسطی) خستۀ خرما بر آنها ظلم کرده نمی‌شود.');
INSERT INTO "badkhashani" VALUES(17,72,'17:72','و هر کس در این دنیا کور (دل و بی‌بصیرت) باشد، پس وی در آخرت نیز کور است و از راه راست گمراه‌تر است.');
INSERT INTO "badkhashani" VALUES(17,73,'17:73','و نزدیک بود که تو را از آنچه به سویت وحی کردیم در فتنه اندازند (و منصرف گردانند) تا غير آن را به دروغ به ما نسبت بدهی. و در آن صورت تو را به دوستی خود می‌گرفتند.');
INSERT INTO "badkhashani" VALUES(17,74,'17:74','و اگر تو را (بر وحی) ثابت قدم نمی‌داشتیم، نزدیک بود که به‌سوی آنها اندکی میل کنی.');
INSERT INTO "badkhashani" VALUES(17,75,'17:75','در آن صورت تو را دو چند عذاب زندگی دنیا و دوچند عذاب (پس از) مرگ می‌چشانیدیم، باز برای خود هیچ مددگاری در برابر ما نمی‌یافتی.');
INSERT INTO "badkhashani" VALUES(17,76,'17:76','والبته نزدیک بود که تو را از آن سرزمین (مکه) بلغزانند تا تو را از آن بیرون کنند، در این صورت بعد از تو جز مدت کوتاهی باقی نمی‌ماندند.');
INSERT INTO "badkhashani" VALUES(17,77,'17:77','این سنت، همان سنتی است که همیشه در میان آنانی که پیش از تو فرستاده‌ایم برقرار بوده است. و برای سنت و روش ما هیچ تبدیلی نخواهی یافت.');
INSERT INTO "badkhashani" VALUES(17,78,'17:78','و نماز را از وقت زوال آفتاب برپا کن تا نهایت تاریکی شب. و قرآن خواندن صبح را لازم گیر (در نماز صبح)، چون نماز صبح (توسط فرشتگان) مشاهده می‌گردد.');
INSERT INTO "badkhashani" VALUES(17,79,'17:79','و در بخشی از شب برخیز و نماز تهجد بخوان، این یک فریضۀ اضافی مخصوص توست، باشد که پروردگارت تو را به مقامی پسندیده برساند.');
INSERT INTO "badkhashani" VALUES(17,80,'17:80','و بگو: ای پروردگارم! مرا به طور پسندیده در (هر کاری) داخل کن و به طور پسندیده (از آن) بیرون آور. و از جانب خود قوتی به من عطا فرما که یار و مددگارم باشد.');
INSERT INTO "badkhashani" VALUES(17,81,'17:81','و بگو: حق آمد و باطل نابود شد، بی‌گمان باطل همیشه نابود شدنی است.');
INSERT INTO "badkhashani" VALUES(17,82,'17:82','و از قرآن آنچه را که سبب شفاست و برای مؤمنان رحمت است نازل می‌کنیم، (ولی) بر ظالمان جز خسارت و نقصان نمی‌افزاید.');
INSERT INTO "badkhashani" VALUES(17,83,'17:83','چون به انسان نعمت دهیم، روی می‌گرداند و به پهلو خود را کنار می‌کشد. و چون بوی شر برسد (از رحمت الله) ناامید می‌گردد.');
INSERT INTO "badkhashani" VALUES(17,84,'17:84','بگو هر کس به طریقۀ (پسندیدۀ) خود عمل می‌کند، پس پروردگارتان بهتر می‌داند که چه کسی راه یاب‌تر است.');
INSERT INTO "badkhashani" VALUES(17,85,'17:85','و (کفار) از تو دربارۀ روح می‌پرسند، بگو: روح از امر پروردگار من است، و از علم به شما داده نشده است، مگر اندکی.');
INSERT INTO "badkhashani" VALUES(17,86,'17:86','و اگر ما بخواهیم آنچه را که به تو وحی فرستاده‌ایم از بین می‌بریم، باز کسی را نخواهی یافت که در این مورد عليه ما از تو دفاع کند.');
INSERT INTO "badkhashani" VALUES(17,87,'17:87','مگر به سبب رحمت از طرف پروردگارت، چون فضل الله بر تو همواره بزرگ هست.');
INSERT INTO "badkhashani" VALUES(17,88,'17:88','بگو: اگر انس و جن جمع شوند (و متفق شوند) تا مانند این قرآن را بیاورند، هرگز نمی‌توانند مانند آنرا بیاورند، هرچند بعضی از ایشان پشتیبان و مددگار بعض دیگر باشند.');
INSERT INTO "badkhashani" VALUES(17,89,'17:89','البته ما در این قرآن هر مثلی را برای مردم به شیوه‌های گوناگون بیان کردیم، ولی بسیاری از مردم (از ایمان و عمل به آن) ابا ورزیدند و جز کفر و انکار (حق چیزی را نپذیرفتند).');
INSERT INTO "badkhashani" VALUES(17,90,'17:90','و گفتند: ما هرگز به تو ایمان نمی‌آوریم تا اینکه از زمین چشمه‌ای برای ما جاری سازی.');
INSERT INTO "badkhashani" VALUES(17,91,'17:91','یا اینکه باغی از درختان خرما و انگور داشته باشی و نهرها را در آن روان سازی.');
INSERT INTO "badkhashani" VALUES(17,92,'17:92','یا اینکه آسمان را طوری که ادعا می‌کنی، پاره پاره بر سر ما بیندازی. و یا الله و فرشتگان را رو به روی ما بیاوری.');
INSERT INTO "badkhashani" VALUES(17,93,'17:93','یا اینکه خانه‌ای از زر و زیورات داشته باشی و یا اینکه به‌سوی آسمان بالا روی. و به بالا رفتنت هم باور نداریم مگر اینکه کتابی برای ما بیاوری تا آن را بخوانیم. بگو: پروردگارم پاک و منزه است! آیا من جز بشری فرستاده شده هستم؟');
INSERT INTO "badkhashani" VALUES(17,94,'17:94','و هیچ چیزی مردم را از ایمان آوردن باز نداشت، وقتی که هدایت به‌سوی آنان آمد، مگر اینکه گفتند: آیا الله انسان را به عنوان پیغمبر فرستاده است؟!');
INSERT INTO "badkhashani" VALUES(17,95,'17:95','بگو: اگر در زمین فرشته‌های می‌بودند که با اطمینان راه می‌رفتند، البته بر آنها فرشته‌ای را از آسمان به عنوان پیغمبر نازل می‌کردیم.');
INSERT INTO "badkhashani" VALUES(17,96,'17:96','بگو: الله به عنوان گواه بین من و شما کافیست، چون او به بندگان خود دانا (و) بیناست.');
INSERT INTO "badkhashani" VALUES(17,97,'17:97','و هر کس را که الله هدایت کند پس او راه یاب است و هر کس را گمراه سازد پس هرگز برای آنها دوستانی به جز الله نیابی. و آنها را روز قیامت کور و گنگ و کر بر چهره‌هایشان افتاده حشر می‌کنیم. جایگاهشان دوزخ است، هربار که آتش آن فرو نشیند، بر شرارۀ آن می‌افزاییم.');
INSERT INTO "badkhashani" VALUES(17,98,'17:98','این است سزای آنان به خاطر اینکه به آیات ما کافر شدند و گفتند: آیا چون استخوانی چند و اعضای از هم پاشیده شدیم، آیا در آفرینش جدید برانگیخته می‌شویم؟!');
INSERT INTO "badkhashani" VALUES(17,99,'17:99','آیا ندیدند که الله همان ذاتی است که آسمان‌ها و زمین را آفرید، قادر است که مانند ایشان را بیافریند؟ و برای آنان اجلی مقرر کرده است که شکی در آن نیست، اما ظالمان ابا ورزیده، جز کفر و انکار (چیزی را نپذیرفتند).');
INSERT INTO "badkhashani" VALUES(17,100,'17:100','بگو: اگر شما مالک خزانه‌های رحمت پروردگارم بودید باز هم از ترس انفاق کردن آن، بخیلی می‌کردید، و انسان بخیل است.');
INSERT INTO "badkhashani" VALUES(17,101,'17:101','همانا ما موسی را نُه معجزه آشکار دادیم. پس از بنی اسرائیل بپرس وقتی که موسی نزدشان آمد، پس فرعون به او گفت: ای موسی! من تو را جادو شده می‌پندارم.');
INSERT INTO "badkhashani" VALUES(17,102,'17:102','(موسی به فرعون) گفت: یقینا دانسته‌ای که اینها (معجزه‌های روشن) را که باعث بینش و بصیرت مردم است، جز پروردگار آسمان‌ها و زمین نفرستاده است. و البته من تو را ای فرعون! هلاک شدنی می‌پندارم.');
INSERT INTO "badkhashani" VALUES(17,103,'17:103','پس (فرعون) خواست که آنان را از سرزمین (مصر) بیرون کند. پس ما او و کسانی را که همراه وی بودند، همه را یکجا غرق کردیم.');
INSERT INTO "badkhashani" VALUES(17,104,'17:104','و پس از (غرق شدن) او به بنی اسرائیل گفتیم: در آن سرزمین (شام) سکونت کنید، پس چون وعدۀ آخرت برسد، همۀ شما را یکجا خواهیم آورد.');
INSERT INTO "badkhashani" VALUES(17,105,'17:105','و آن (قرآن) را به حق نازل کردیم و به حق نازل شده است. و تو را جز مژده دهنده و بیم دهنده نفرستاده‌ایم.');
INSERT INTO "badkhashani" VALUES(17,106,'17:106','و قرآنی که آن را جدا جدا (به تدریج) نازل کردیم تا آن را با درنگ (اندک اندک) بر مردم بخوانی، و آن را به تدریج فرستاده‌ایم.');
INSERT INTO "badkhashani" VALUES(17,107,'17:107','بگو: (به قرآن) ایمان بیاورید، یا ایمان نیاورید (باکی نیست)، همانا آنانی که پیش از نازل شدن قرآن علم به ایشان داده شده است، هنگامی که قرآن بر آنان تلاوت می‌شود، سجده‌کنان به روی می‌افتند.');
INSERT INTO "badkhashani" VALUES(17,108,'17:108','و می‌گویند (پیروان حق) پاک است پروردگار ما، يقينا وعدۀ پروردگارما انجام شدنی است.');
INSERT INTO "badkhashani" VALUES(17,109,'17:109','و گریه‌کنان بر روی‌های خویش می‌افتند و بر تواضع آنان می‌افزاید.');
INSERT INTO "badkhashani" VALUES(17,110,'17:110','بگو: الله را بخوانید یا رحمن را بخوانید، او را به هر یک از نامهایش بخوانید (اشکالی ندارد، زیرا) او دارای نامهای نیکوست. و نمازت را نه با صدای بلند بخوان و نه با صدای آهسته، بلکه میان آن دو، راهی (وسط را) پیش گیر.');
INSERT INTO "badkhashani" VALUES(17,111,'17:111','و بگو: تمامی ستایش‌ها مخصوص الله است که نه فرزندی گرفته و نه در فرمانروایی عالم شریکی دارد و نه او را به سبب ناتوانی و ذلت، یار و مددگاری است. لذا او را طوری‌که سزاوار (شأن او) است، بزرگ بشمار.');
INSERT INTO "badkhashani" VALUES(18,1,'18:1','همه ستایش‌ها برای الله است ذاتی که کتاب را بر بندۀ خود نازل کرد و در آن هیچگونه کجی و انحراف نگذاشت.');
INSERT INTO "badkhashani" VALUES(18,2,'18:2','کتاب راست و درست، تا (کافران را) از عذاب سخت از جانب خود بترساند. و مؤمنانی را که کارهای نیک می‌کنند، مژده دهد به اینکه برای آنها پاداش نیک است.');
INSERT INTO "badkhashani" VALUES(18,3,'18:3','که باشند در آن تا ابد.');
INSERT INTO "badkhashani" VALUES(18,4,'18:4','و تا بترساند کسانی را که گفتند: الله فرزندی (برای خود) گرفته است.');
INSERT INTO "badkhashani" VALUES(18,5,'18:5','حال آنکه نه ایشان و نه پدرانشان به آن (فرزند داشتن الله) هیچ علمی ندارند، سخن بس بزرگ است که از زبانشان بیرون می‌آید، )در این باره( جز دروغ نمی‌گویند.');
INSERT INTO "badkhashani" VALUES(18,6,'18:6','پس مگر تو جان خود را از غم و اندوه هلاک می‌سازی در پی آنها که اگر به این سخن ایمان نیاورند.');
INSERT INTO "badkhashani" VALUES(18,7,'18:7','در حقیقت همۀ چیزهای که بر روی زمین است، ما برای آن زینت قرار داده‌ایم تا آنان را بیازماییم که کدام یک از آنها نیکوکارترند.');
INSERT INTO "badkhashani" VALUES(18,8,'18:8','و البته ما هر آنچه را که بر روی زمین است حتما میدان هموار و خشک خواهیم ساخت.');
INSERT INTO "badkhashani" VALUES(18,9,'18:9','آیا (گاهی) گمان کرده‌ای که اصحاب کهف (غار) و رقیم (لوحه نوشته) از عجایب و غرایب (دلائل قدرت) ما بودند؟');
INSERT INTO "badkhashani" VALUES(18,10,'18:10','وقتی که آن جوانان به غار پناه بردند. پس گفتند: ای پرودگار ما! از جانب خود رحمت به ما ببخش و برای ما در کار ما راه نجات وهدایت را آماده ساز.');
INSERT INTO "badkhashani" VALUES(18,11,'18:11','پس بر گوش‌هایشان در آن غار تا چندین سال پرده (خواب سنگین) نهادیم.');
INSERT INTO "badkhashani" VALUES(18,12,'18:12','باز ایشان را بیدار کردیم تا بدانیم که کدام یک از دو گروه مدت ماندن‌شان (در غار) را شمرده و بهتر به یاد دارد.');
INSERT INTO "badkhashani" VALUES(18,13,'18:13','ما داستان آنان را به راستی و درستی برای تو حکایت می‌کنیم. آنها جوانانی بودند که به پروردگارشان ایمان آوردند و ما بر هدایت آنان افزودیم.');
INSERT INTO "badkhashani" VALUES(18,14,'18:14','و دل‌هایشان را استوار گردانیدیم، وقتی که بر خواستند و گفتند: پروردگارما همان پروردگار آسمان‌ها و زمین است، هرگز جز او معبودی را نخواهیم پرستید، که در غیر این صورت سخن دروغ و دور از حق گفته‌ایم.');
INSERT INTO "badkhashani" VALUES(18,15,'18:15','(لیکن) این قوم ما به جز الله معبودهایی را گرفته‌اند. (پس) چرا بر حقانیت آنها دلیل روشن نمی‌آورند؟ پس کیست ظالم‌تر از کسی که به الله دروغ بندد؟!');
INSERT INTO "badkhashani" VALUES(18,16,'18:16','و (آن جوانان میان خود گفتند:) چون از آنان و از چیزهایی که به جز الله می‌پرستند کناره گرفتید، پس به آن غار جای گیرید تا پروردگارتان از رحمت خود بر شما بگستراند و در کارتان برای شما گشایش آماده سازد.');
INSERT INTO "badkhashani" VALUES(18,17,'18:17','و آفتاب را می‌بینی که چون طلوع کند به طرف راست غارشان مائل می‌شود، و چون غروب کند از طرف چپشان می‌گذرد، در حالیکه آنان در گشادگی غار قرار داشتند، این از نشانه‌های الله است، الله هر كس را هدایت کند پس او راه یافته است، و هر که را گمراه کند، پس برای او دوست و راهنمایی نمی‌یابی.');
INSERT INTO "badkhashani" VALUES(18,18,'18:18','و گمان می‌کنی که آنان بیدارند، حال آنکه آنان خفته‌اند. و ما آنان را به جانب راست و جانب چپ می‌گردانیدیم، و سگشان دو دستش در دهان غار گشاده بود، اگر به آنها می‌نگریستی از آنان می‌گریختی و البته از مشاهده آنها سینه ات از ترس و وحشت پر می‌شد.');
INSERT INTO "badkhashani" VALUES(18,19,'18:19','و این چنین ایشان را بیدار کردیم تا از یکدیگر بپرسند. گوینده‌ای از آنان گفت: چه مدتی (در این غار) بودید؟ گفتند: یک روز یا بخشی از روز؟ گفتند: پروردگارتان بهتر می‌داند که چقدر مانده‌اید، پس کسی از خودتان را با این سکه نقره به شهر بفرستید، و باید ببیند کدامیک از آنان غذای پاکتر دارد، پس از آن خوردنی برایتان بیاورد. و باید (در رفت و آمد خود) دقت و احتیاط کند و هیچ کسی را دربارۀ شما آگاه نسازد.');
INSERT INTO "badkhashani" VALUES(18,20,'18:20','زیرا اگر آنان بر شما قدرت یابند شما را سنگسار می‌کنند، یا اینکه به دین خود بر می‌گردانند. و در آن صورت هرگز رستگار نخواهید شد.');
INSERT INTO "badkhashani" VALUES(18,21,'18:21','و این چنین مردم را بر حال آنها آگاه ساختیم تا بدانند که وعدۀ الله حق و راست است، و اینکه (بدانند) قیامت (آمدنی است و) هیچ شکی در آن نیست، وقتی که میان خود نزاع کردند و گفتند: بر (غار) آنان بنایی بسازید. پروردگارشان از حال ایشان آگاه‌تر است، و کسانی که بر کار و بار‌شان غلبه یافتند، گفتند: حتما بر غار آنها مسجدی می‌سازیم.');
INSERT INTO "badkhashani" VALUES(18,22,'18:22','(بعضی از مردم) خواهند گفت: آنان سه نفر بودند که چهارم ایشان سگشان بود. و (بعضی دیگر) خواهند گفت: پنج نفر بودند که ششم ایشان سگشان بود. همه اینها از روی حدس و گمان است. و (بعضی) می‌گویند، هفت نفر بودند که هشتم ایشان سگشان بود. بگو: پروردگارم به تعداد آنها داناتر است. جز اندکی تعدادشان را نمی‌دانند، بنابراین جز بر اساس علم و یقین در مورد آنان جدال مکن و دربارۀ آنان از هیچکس سوال مکن.');
INSERT INTO "badkhashani" VALUES(18,23,'18:23','و هرگز دربارۀ هیچ کاری مگو که فردا آنرا انجام می‌دهم.');
INSERT INTO "badkhashani" VALUES(18,24,'18:24','(مگر اینکه بگویی:) اگر الله بخواهد (آن را انجام می‌دهم) و پروردگارت را وقتی که فراموش کردی یاد کن، و بگو: امید است که پروردگارم مرا به (راه) درست‌تر از این رهنمود کند.');
INSERT INTO "badkhashani" VALUES(18,25,'18:25','و (اصحاب کهف) سیصد سال در غارشان ماندند و نه سال دیگر نیز بر آن افزودند.');
INSERT INTO "badkhashani" VALUES(18,26,'18:26','بگو: الله به مدتی که در غار بودند داناتر است، غیب آسمان‌ها و زمین تنها به او اختصاص دارد، چقدر بینا و چقدر شنواست! آنان به جز او هیچ کارسازی ندارند و در حکم خود کسی را شریک نمی‌سازد.');
INSERT INTO "badkhashani" VALUES(18,27,'18:27','و آنچه را که از کتاب پروردگارت به تو وحی شده است، تلاوت کن، سخنان او را هیچ کس نمی‌تواند تغییر دهد، و هرگز پناهی جز او نیابی.');
INSERT INTO "badkhashani" VALUES(18,28,'18:28','و با آنانی که پروردگار خود را صبح و شام یاد می‌کنند صبر پیشه کن، در حالیکه خشنودی او را می‌خواهند. و نباید در طلب زینت زندگانی دنیا چشمانت از آنان دور شود. و از کسی اطاعت مکن که دل او را از یاد خود غافل ساخته‌ایم و او از هوای نفس خود پیروی کرده و کارش از حد گذشتن و کوتاهی است.');
INSERT INTO "badkhashani" VALUES(18,29,'18:29','و بگو: حق از سوی پروردگارتان است، پس هرکس که می‌خواهد، ایمان بیاورد و هر کس که می‌خواهد، کافر شود. به یقین ما برای ظالمان آتش را آماده کرده‌ایم که سرا پرده‌هایش آنان را در بر می‌گیرد. و اگر کمک بخواهند با آبی همچون مس گداخته شده به فریادشان رسند، که چهره‌ها را بریان می‌کند. چه بد نوشیدنی و چه بد جایگاهی است.');
INSERT INTO "badkhashani" VALUES(18,30,'18:30','بدون شک کسانی که ایمان آورده‌اند و کارهای شایسته کرده‌اند (بدانند که)، ما پاداش کسانی را ضایع نمی‌کنیم که کار نیک کرده‌اند.');
INSERT INTO "badkhashani" VALUES(18,31,'18:31','آنان کسانی‌اند که بهشت همیشگی برای ایشان است، که در زیر (قصرها و درختان) آنها نهرها روان است، در آن جا با دستبندهای از طلا آراسته می‌شوند و لباسهای ابریشم سبز و نازک و ضخیم می‌پوشند، در حالیکه بر تختها تكيه زده‌اند، چه پاداش خوب و چه نیکو تکیه گاهی است!');
INSERT INTO "badkhashani" VALUES(18,32,'18:32','و برای آنان، آن دو شخص را مثل بزن که به یکی از آنها، دو باغ انگور دادیم و گرداگرد باغ‌ها را با درختان خرما پوشاندیم و میان باغها کشتزار قرار دادیم.');
INSERT INTO "badkhashani" VALUES(18,33,'18:33','هر دو باغ، محصول و میوه‌هایش را (به وقتش) می‌داد و چیزی از محصول و میوه کم نمی‌کرد و ما در میان آن دو باغ جویبار روان کرده بودیم.');
INSERT INTO "badkhashani" VALUES(18,34,'18:34','و برای صاحب باغ میوه‌های (فراوانی) بود، پس به دوست خود در حالیکه با وی گفتگو می‌کرد، گفت: من از تو ثروتمندتر و از لحاظ افراد نسبت به تو نیرومندترم.');
INSERT INTO "badkhashani" VALUES(18,35,'18:35','و در باغش در آمد در حالی که بر خود ظالم بود گفت: گمان نمی‌کنم که هرگز این (باغ) نابود شود.');
INSERT INTO "badkhashani" VALUES(18,36,'18:36','و گمان نمی‌کنم که قیامت برپا شود. و اگر (هم) به‌سوی پروردگارم برگردانیده شوم، البته بهتر از این را در بازگشت خواهم یافت.');
INSERT INTO "badkhashani" VALUES(18,37,'18:37','رفیقش در حالیکه با او گفتگو می‌کرد به وی گفت: آیا به کسی کافر شده‌ای که تو را از خاک باز از نطفه آفرید و باز تو را برابر کرد (و به شکل) مرد کامل در آورد.');
INSERT INTO "badkhashani" VALUES(18,38,'18:38','ليكن من (می گویم) او (الله) پروردگار من است و کسی را با پروردگارم شریک نمیسازم.');
INSERT INTO "badkhashani" VALUES(18,39,'18:39','چرا وقتی که وارد باغت شدی، نگفتی: «ماشاءالله» هیچ قوت و قدرت جز به مشیئت الله نیست، اگر مرا از نگاه مال و اولاد کمتر از خود می‌بینی.');
INSERT INTO "badkhashani" VALUES(18,40,'18:40','(لیکن) امیدوارم که پروردگارم بهتر از باغت به من بدهد و بر آن (باغت) آفتی از آسمان بفرستد و به زمین بی‌گیاه و لغزنده تبدیل گردد.');
INSERT INTO "badkhashani" VALUES(18,41,'18:41','یا اینکه آب آن (باغت) در زمین فرو رود و هرگز نتوانی آن را به دست بیاوری.');
INSERT INTO "badkhashani" VALUES(18,42,'18:42','و بر میوه‌هایش (آفت آسمانی) احاطه کرده شد، پس چنان شد که از افسوس و تأسف بر آنچه در آن باغ انفاق کرده بود، دست بر دست می‌مالید، در حالیکه آن باغ بر ستونهای خود افتیده بود، و می‌گفت: ای کاش کسی را با پروردگارم شریک نمی‌ساختم!');
INSERT INTO "badkhashani" VALUES(18,43,'18:43','و برای او هیچ گروهی نبود که او را در برابر الله مدد کند و نه خود او انتقام گیر بود.');
INSERT INTO "badkhashani" VALUES(18,44,'18:44','در آن مقام (و در آن موقف معلوم شد که) نصرت و کمک به معبود حق تعلق دارد. او بهتر است از لحاظ پاداش و او بهتر است از لحاظ جزا دادن.');
INSERT INTO "badkhashani" VALUES(18,45,'18:45','و برای آنان زندگی دنیا را مثل بزن که مانند آبی است که آن را از آسمان نازل کردیم، باز گیاهان زمین به وسیلۀ آن باهم خلط شدند، باز چنان خشک شد که بادها آن را پراگنده کرد، و الله بر هر چیزی تواناست.');
INSERT INTO "badkhashani" VALUES(18,46,'18:46','مال و فرزندان زیب زندگانی دنیااند (که به زودی زائل خواهند شد) اما اعمال شایستۀ که پاداش آن پایدار است، بهترین ثواب نزد پروردگارت دارد و بهترین امید است.');
INSERT INTO "badkhashani" VALUES(18,47,'18:47','و یادآور شو روزی که کوه‌ها را روان کنیم و زمین را نمایان (هموار و برابر شده) می‌بینی و همۀ آنان را (در محشر) جمع می‌کنیم، پس هیچ کسی از آنها را نمی‌گذاریم.');
INSERT INTO "badkhashani" VALUES(18,48,'18:48','و (در آن روز مردم) صف بسته بر پروردگارت عرضه می‌شوند، البته طوریکه بار اول شما را آفریدیم به نزد ما آمدید، بلکه (شما) ادعا می‌کردید که هرگز وعد گاهی برای شما مقرر نخواهیم کرد.');
INSERT INTO "badkhashani" VALUES(18,49,'18:49','و کتاب (نامه‌های اعمال) نهاده می‌شود، و مجرمان را از دیدن آنچه در آن است، ترسان می‌بینی و می‌گویند: ای وای بر ما! این چه نامه‌ای است که هیچ عمل خورد و بزرگ را رها نکرده، مگر اینکه همۀ آن را به حساب آورده است؟ و آنچه را که کرده بودند حاضر و آماده می‌یابند و پروردگارت به هیچ کس ظلم نمی‌کند.');
INSERT INTO "badkhashani" VALUES(18,50,'18:50','و یادآور شو چون به فرشتگان گفتیم: برای آدم سجده کنید، پس آنها همگی سجده کردند، مگر ابلیس که از جن بود و از امر پروردگارش سرکشی کرد. آیا او و نسلش را به غیر از من دوستان خود می‌گیرید، حال آنکه آنها برای شما دشمن هستند؟ شیطان چه بد عوض برای ظالمان است!');
INSERT INTO "badkhashani" VALUES(18,51,'18:51','من آنان را در وقت آفرینش آسمان‌ها و زمین و در وقت آفرینش خودشان حاضر نکرده بودم. و من آن نیستم که گمراه کنندگان را مددکار خود بگیرم.');
INSERT INTO "badkhashani" VALUES(18,52,'18:52','و یادآور شو روزی را که الله می‌فرماید: شریک‌هایی را که برای من گمان می‌کردید فریاد کنید پس آنها (شریک‌ها را) فریاد کنند و (لیکن) آنها به فریاد‌شان پاسخ نمی‌دهند، و میانشان مهلكۀ قرار داده‌ایم (تا میان‌شان فاصله ایجاد شود).');
INSERT INTO "badkhashani" VALUES(18,53,'18:53','و مجرمان آتش دوزخ را می‌بینند و یقین می‌کنند که در آن می‌افتند و از آن جای بازگشتن نیابند.');
INSERT INTO "badkhashani" VALUES(18,54,'18:54','و البته در این قرآن برای مردمان هرگونه مثلی را به شیوه‌های گوناگون بیان کردیم. و (لیکن) انسان بیش از هر چیز مجادله گر است.');
INSERT INTO "badkhashani" VALUES(18,55,'18:55','و (چیزی) مانع مردم نشد از اینکه ایمان آورند وقتی که هدایت به سوی‌شان آمد و از پروردگارشان مغفرت بخواهند، مگر اینکه سنت (و سرنوشت) مردمان سابق برای آنان نیز بیاید و یا اینکه عذاب گوناگون (و رویاروی) به آنها برسد.');
INSERT INTO "badkhashani" VALUES(18,56,'18:56','و ما پیغمبران را جز مژده دهندگان و بیم دهندگان نمی‌فرستیم. و کافران به باطل مجادله می‌کنند تا به وسيلۀ آن حق را نابود گردانند و آیات مرا و چیزی را که به آن بیم داده شده‌اند به تمسخر گرفتند.');
INSERT INTO "badkhashani" VALUES(18,57,'18:57','و کیست ظالم‌تر از کسی که با آیات پروردگارش پند داده شود، پس از آن روی گرداند و آنچه را که دستهایش پیش فرستاده است فراموش کند؟ البته ما بر دل‌هایشان پرده‌ها کشیده‌ایم تا آن (حق و حقیقت) را نفهمند. و در گوش‌هایشان سنگینی قرار داده‌ایم (تا آنرا نشنوند)، و اگر آنها را به‌سوی هدایت دعوت دهی باز هرگز راه نیابند.');
INSERT INTO "badkhashani" VALUES(18,58,'18:58','و (حال آنکه) پروردگار تو بسیار آمرزگار و صاحب رحمت است، اگر آنان را به سبب آنچه بدست آورده‌اند مؤاخذه می‌کرد، عذاب را به شتاب برایشان می‌فرستاد، (ليكن الله زود عذاب نمی‌دهد) بلکه وعدۀ مقرر شده دارند که (در آن وقت) هرگز از آن پناهگاهی نمی‌یابند.');
INSERT INTO "badkhashani" VALUES(18,59,'18:59','و این همان قریه‌های (ویران شده) است که چون ظلم کردند، هلاک کردیم، و برای هلاکت‌شان وعدۀ مقرر کردیم.');
INSERT INTO "badkhashani" VALUES(18,60,'18:60','و یادآور شو وقتی را که موسی به نوجوان (همراه) خود گفت: به سفر خود ادامه می‌دهیم تا اینکه به محل جمع شدن دو دریا برسم، یا اینکه مدت دراز را می‌پیمایم.');
INSERT INTO "badkhashani" VALUES(18,61,'18:61','پس چون به محل جمع شدن دو دریا رسیدند، ماهی خود را فراموش کردند پس ماهی آب را شگافته راهش را در دریا پیش گرفت.');
INSERT INTO "badkhashani" VALUES(18,62,'18:62','پس وقتی (از آنجا) گذشتند، موسی به نوجوان خود گفت: نان چاشت ما را بیار که به راستی در این سفر ما رنج زیاد دیده‌ایم.');
INSERT INTO "badkhashani" VALUES(18,63,'18:63','(نوجوان) گفت: آیا دیدی؟ وقتی که به آن صخره آرام گرفتیم، البته من (در آنجا) ماهی را فراموش کردم. و جز شیطان کسی آن را از یاد من نبرد، و ماهی به طور عجیب راه خود را در دریا پیش گرفت.');
INSERT INTO "badkhashani" VALUES(18,64,'18:64','موسی گفت: این همان جایی بود که ما می‌خواستیم، پس جستجوکنان نقش پای خود را گرفته و بازگشتند.');
INSERT INTO "badkhashani" VALUES(18,65,'18:65','پس بنده‌ای از بندگان ما را یافتند که به او رحمتی از جانب خود داده بودیم و به او علم از جانب خود آموخته بودیم.');
INSERT INTO "badkhashani" VALUES(18,66,'18:66','موسی به او (خضر) گفت: آیا از تو پیروی کنم به این شرط که از رشد و هدایتی که به تو آموخته شده به من بیاموزی؟');
INSERT INTO "badkhashani" VALUES(18,67,'18:67','(خضر) گفت: همانا تو هرگز نمی‌توانی با من صبر کنی.');
INSERT INTO "badkhashani" VALUES(18,68,'18:68','و چگونه می‌توانی بر چیزی که به شناخت و حقیقت آن علم نداری صبر کنی؟');
INSERT INTO "badkhashani" VALUES(18,69,'18:69','موسی گفت: مرا (ان شاء الله) صابر خواهی یافت و در هیچ کاری از تو نافرمانی نمی‌کنم.');
INSERT INTO "badkhashani" VALUES(18,70,'18:70','خضر گفت: اگر واقعا از من پیروی می‌کنی، پس دربارۀ چیزی از من مپرس، تا آنکه خودم از آن با تو سخن آغاز کنم.');
INSERT INTO "badkhashani" VALUES(18,71,'18:71','پس هر دو روان شدند تا چون سوار کشتی شدند، (و خضر) آن را سوراخ کرد. (موسی) گفت: آیا آن را سوراخ کردی تا سرنشینان آن را غرق کنی؟ بی‌گمان چیزی ناگوار آوردی (کار ناروا کردی).');
INSERT INTO "badkhashani" VALUES(18,72,'18:72','(خضر) گفت: آیا نگفته بودم که تو هرگز نمی‌توانی با من صبر کنی؟');
INSERT INTO "badkhashani" VALUES(18,73,'18:73','(موسی) گفت: مرا به خاطر آنچه فراموش کردم مؤاخذه مکن و در کارم بر من سخت مگیر.');
INSERT INTO "badkhashani" VALUES(18,74,'18:74','باز هر دو به راه خود ادامه دادند، تا به نوجوانی روبرو شدند پس (خضر) او را کشت. (موسی) گفت: آیا انسان بی‌گناه را کشتی، بدون اینکه کسی را کشته باشد؟ البته کار ناپسندی را مرتکب شدی.');
INSERT INTO "badkhashani" VALUES(18,75,'18:75','خضر گفت: آیا به تو نگفته بودم که تو هرگز نمی‌توانی با من صبر کنی؟');
INSERT INTO "badkhashani" VALUES(18,76,'18:76','موسی گفت: اگر بعد از این از تو دربارۀ چیزی پرسیدم، دیگر با من همراهی نکن، همانا از سوی من به حد عذر رسیدی (و معذور هستی).');
INSERT INTO "badkhashani" VALUES(18,77,'18:77','باز رفتند تا به قریه‌ای رسیدند، از مردم آن غذا خواستند، ولی آنان از مهمان کردن آن دو خودداری کردند، پس در آن جا دیواری را یافتند که نزدیک بود بافتد، پس (خضر) آن را راست (و درست) کرد. (موسی) گفت: اگر می‌خواستی در مقابل این کار مزدی می‌گرفتی.');
INSERT INTO "badkhashani" VALUES(18,78,'18:78','خضر گفت: این است وقت جدائی میان من و تو، اکنون تو را از تأویل (و حکمت) آنچه نتوانستی بر آن صبر کنی آگاه می‌سازم.');
INSERT INTO "badkhashani" VALUES(18,79,'18:79','اما آن کشتی از مساکینی بود که در بحر کار می‌کردند، و (من) خواستم آن را معیوب سازم، چون در پشت سر آنان پادشاه (ظالم) بود که هر کشتی (سالم) را به زور می‌گرفت.');
INSERT INTO "badkhashani" VALUES(18,80,'18:80','و اما آن نوجوان، پدر و مادرش هردو مؤمن بودند، پس ترسیدیم که (اگر زنده بمانند) سرکشی و کفر را بر آنها تحمیل کند.');
INSERT INTO "badkhashani" VALUES(18,81,'18:81','پس خواستیم که پروردگارشان به عوض او، فرزند پاکتر و مهربان تری به ایشان دهد.');
INSERT INTO "badkhashani" VALUES(18,82,'18:82','و اما دیوار از دو پسر یتیمی بود که در شهر سکونت داشتند و در زیر آن گنجی متعلق به آن دو وجود داشت، و پدرشان (مردی) صالح بود و پروردگار تو خواست که آن دو به سن بلوغ برسند و گنج خود را بیرون بیاورند که این کار رحمتی از جانب پروردگار تو بود و من این (کارها) را از پیش خود نکردم. این بود تأویل (و حقیقت) آنچه که نتوانستی بر آن صبر کنی.');
INSERT INTO "badkhashani" VALUES(18,83,'18:83','و دربارۀ ذي القرنين از تو می‌پرسند، بگو: اکنون از سرگذشت او برایتان خبر می‌دهم.');
INSERT INTO "badkhashani" VALUES(18,84,'18:84','البته ما به او در زمین قدرت و نفوذ دادیم و از هر چیزی وسیله‌ای به او بخشیدیم.');
INSERT INTO "badkhashani" VALUES(18,85,'18:85','پس راهی را دنبال کرد (یعنی به راهی روان شد).');
INSERT INTO "badkhashani" VALUES(18,86,'18:86','تا آنکه به محل غروب آفتاب رسید، آن را چنین یافت که (گویی) در چشمه دارای گل سیاه فرو می‌رود، و در آن نزدیکی قومی را یافت. گفتیم: ای ذوالقرنین! (اختیار داری) یا عذاب(شان) می‌دهی و یا به آنها خوبی و احسان می‌کنی؟');
INSERT INTO "badkhashani" VALUES(18,87,'18:87','ذوالقرنین گفت: اما کسی که ظلم کند او را عذاب خواهیم داد باز در آخرت به‌سوی پروردگارش برگردانیده می‌شود و او (الله نیز) او را عذاب بسیار سخت می‌دهد.');
INSERT INTO "badkhashani" VALUES(18,88,'18:88','و اما کسی که ایمان آورد و کار شایسته انجام دهد، پاداش نيكو خواهد داشت و ما دستور آسانی به او خواهیم داد.');
INSERT INTO "badkhashani" VALUES(18,89,'18:89','باز راهی (دیگر) را دنبال نمود. (یعنی به راه دیگری روان شد).');
INSERT INTO "badkhashani" VALUES(18,90,'18:90','تا اینکه به محل طلوع آفتاب رسید (و) دید که آفتاب بر مردمانی می‌تابد که برای آنها در برابر تابش آن پوشش و لباسی قرار نداده‌ایم.');
INSERT INTO "badkhashani" VALUES(18,91,'18:91','چنین بود (داستان قدرت ذی القرنين) و ما به آنچه می‌کرد (و نزد او بود) کاملا آگاهی داشتیم.');
INSERT INTO "badkhashani" VALUES(18,92,'18:92','باز راهی (دیگر) را دنبال کرد.');
INSERT INTO "badkhashani" VALUES(18,93,'18:93','تا آن که در میان دو بند (کوه) رسید. و در ورای آن دو بند، قومی را یافت که هیچ سخنی را نمی‌فهمیدند.');
INSERT INTO "badkhashani" VALUES(18,94,'18:94','گفتند: ای ذوالقرنین! همانا يأجوج و مأجوج سخت در این زمین فساد می‌کنند، پس آیا (راضی هستی) که برای تو مالیاتی مقرر کنیم؛ به این شرط که میان ما و آنها سدی بسازی؟');
INSERT INTO "badkhashani" VALUES(18,95,'18:95','ذوالقرنین گفت: آنچه پروردگارم (از ثروت و نیرو و قدرت) در اختیار من گذاشته، بهتر است (از ثروت شما). پس مرا با نیروی (بازو) مدد کنید، تا میان شما و آنها بند بزرگ و محکمی بسازم.');
INSERT INTO "badkhashani" VALUES(18,96,'18:96','(وقتی به کار آغاز کرد) گفت: قطعه‌های آهن را برای من بیاورید، تا آنکه بین دو کوه را برابر کرد، گفت: در آن بدمید، تا آنکه (آهن) را آتش گردانید، گفت: مس گداخته شده برایم بیاورید تا بر آن بریزیم.');
INSERT INTO "badkhashani" VALUES(18,97,'18:97','پس نتوانستند بر آن بالا روند و (نیز) نتوانستند آن را سوراخ کنند.');
INSERT INTO "badkhashani" VALUES(18,98,'18:98','ذوالقرنین گفت: این رحمتی از سوی پروردگار من است و هرگاه وعدۀ پروردگارم برسد، آن (سدّ) را (ویران کرده و) با زمین هموار می‌کند، و وعدۀ پروردگارم حق (و بازگشت ناپذیر) است.');
INSERT INTO "badkhashani" VALUES(18,99,'18:99','و در آن روز (ویرانی سدّ) آنها را رها می‌کنیم تا موج‌آسا بعضی با بعضی دیگر خلط شوند، و (بعداً که) در صور دمیده شود، (همه) مردم را (در محشر) جمع کنیم.');
INSERT INTO "badkhashani" VALUES(18,100,'18:100','و دوزخ را در آن روز به طور خاص پیش روی كافران می‌آوریم.');
INSERT INTO "badkhashani" VALUES(18,101,'18:101','(پیش روی) آن کافرانی که چشم‌هایشان از (دیدن) آيات من در پرده‌ای بود و نمی‌توانستند (حق) را بشنوند.');
INSERT INTO "badkhashani" VALUES(18,102,'18:102','آیا کافران گمان کردند که به جز من، بندگان مرا اولياء بگیرند؟ يقينا ما دوزخ را منزلگاه برای کافران آماده کرده‌ایم.');
INSERT INTO "badkhashani" VALUES(18,103,'18:103','بگو: آیا شما را از کسانی که از نگاه عمل خسارمندترین مردمان‌اند، آگاه سازم؟');
INSERT INTO "badkhashani" VALUES(18,104,'18:104','آنانی‌اند که کوشش آنها در زندگی دنیا گم شده است و گمان می‌کنند که کار نیک می‌کنند.');
INSERT INTO "badkhashani" VALUES(18,105,'18:105','آنها کسانی هستند که به آیات پروردگارشان و ملاقات او کفر ورزیدند، در نتیجه اعمالشان نیست و نابود شد پس روز قیامت هیچ ارزش (و منزلتی) و وزنی برایشان نخواهیم نهاد.');
INSERT INTO "badkhashani" VALUES(18,106,'18:106','این جزای آنهاست به سبب آنکه کفر ورزیدند و آیات و پیامبران مرا به تمسخر گرفتند.');
INSERT INTO "badkhashani" VALUES(18,107,'18:107','البته آنانی که ایمان آوردند و کارهای نیک کردند، برای آنها باغ‌های جنت فردوس (باغ‌های انبوه) جایگاه مهمانی است.');
INSERT INTO "badkhashani" VALUES(18,108,'18:108','همیشه‌اند در آنجا و درخواست انتقال (به جای دیگر) نمی‌کنند.');
INSERT INTO "badkhashani" VALUES(18,109,'18:109','بگو: اگر بحر برای نوشتن سخنان پروردگارم (تبدیل به) رنگ (سیاهی) گردد، پیش از آنکه سخنان پروردگارم خاتمه یابد، بدون شک بحر تمام خواهد شد، هر چند (بحرهایی دیگر) مانند آن (بحر را نیز) به کمک آن بیاوریم.');
INSERT INTO "badkhashani" VALUES(18,110,'18:110','بگو: جز این نیست که من مانند شما بشری هستیم، به من وحی فرستاده می‌شود اینکه معبود شما معبود یگانه است (و بس)، پس هرکس که به دیدار پروردگار خود امید دارد، پس باید کار شایسته کند و در عبادت پروردگار خویش کسی را شریک نسازد.');
INSERT INTO "badkhashani" VALUES(19,1,'19:1','(كهيعص) مفهوم این حروف را الله میداند و بس.');
INSERT INTO "badkhashani" VALUES(19,2,'19:2','(این) یادی است از رحمت پروردگارت بر بنده‌اش زکریا.');
INSERT INTO "badkhashani" VALUES(19,3,'19:3','وقتی که پروردگارش را (در دعایش) آهسته (و آرام) ندا کرد.');
INSERT INTO "badkhashani" VALUES(19,4,'19:4','گفت: ای پروردگارم! استخوانم سست شده است. و موی سرم از پیری شعله‌ور شده و ای پروردگارم! هرگز در دعایت (وقتی چیزی از تو خواسته‌ام) ناامید نبوده‌ام.');
INSERT INTO "badkhashani" VALUES(19,5,'19:5','و من پس از خود از اقاربم بیمناکم. و همسرم بی‌فرزند است، پس از نزد خویش وارثی (در نبوت) به من ببخش.');
INSERT INTO "badkhashani" VALUES(19,6,'19:6','تا وارث من باشد و هم وارث اولاد يعقوب. و او را ای پروردگارم پسندیده بگردان.');
INSERT INTO "badkhashani" VALUES(19,7,'19:7','(الله دعای زکریا را قبول کرد و فرمود) ای زکریا ما تو را به پسری مژده می‌دهیم که نام او یحیی است و پیش از این هیچ هم نام او را پیدا نکردیم.');
INSERT INTO "badkhashani" VALUES(19,8,'19:8','زکریا گفت: ای پروردگارم! چطور مرا پسری خواهد بود، حال آنکه همسرم نازا است و خودم نیز به سبب پیری به نهایت ضعف رسیده‌ام.');
INSERT INTO "badkhashani" VALUES(19,9,'19:9','الله فرمود: همچنین است (فرمان من)، پروردگار تو گفته که این (کار) بر من آسان است چون پیش از این تو را در حالی آفریدم که هیچ چیزی نبودی.');
INSERT INTO "badkhashani" VALUES(19,10,'19:10','زکریا گفت: ای پروردگارم! علامتی برایم قرار ده، فرمود: نشانه‌ات آن است که سه شبانه روز نمی‌توانی با مردم سخن بگویی با وجود اینکه سالم و تندرستی.');
INSERT INTO "badkhashani" VALUES(19,11,'19:11','پس از محراب (جای عبادت) بر قوم خویش بیرون آمد، پس به آنان اشاره کرد که صبح و شام تسبیح گویید.');
INSERT INTO "badkhashani" VALUES(19,12,'19:12','ای یحیی! کتاب را با قوّت بگیر، و به او در کودکی حکمت دادیم.');
INSERT INTO "badkhashani" VALUES(19,13,'19:13','و از نزد خود به او مهر و شفقت دادیم. و برکت و پاکی نصیبش کردیم و او پرهیزگار بود.');
INSERT INTO "badkhashani" VALUES(19,14,'19:14','و با پدر و مادرش نیک رفتار بود، و متکبر و نافرمان نبود.');
INSERT INTO "badkhashani" VALUES(19,15,'19:15','و سلام بر او، روزی که تولد شد و روزی که می‌میرد و روزی که دوباره زنده می‌شود.');
INSERT INTO "badkhashani" VALUES(19,16,'19:16','و یادآور شو در کتاب (قرآن) مریم را، وقتی که در ناحیۀ شرقی (بیت المقدس) از خانواده‌اش به خلوت رفت.');
INSERT INTO "badkhashani" VALUES(19,17,'19:17','پس در مقابل آنان پرده‌ای گرفت، پس روح خود (جبرئیل) را به‌سوی او فرستادیم که در شکل بشر درست اندام بر او نمایان شد.');
INSERT INTO "badkhashani" VALUES(19,18,'19:18','مریم گفت: البته من از تو به پروردگار مهربان پناه می‌برم، اگر پرهیزگار باشی (به من نزدیک مشو).');
INSERT INTO "badkhashani" VALUES(19,19,'19:19','جبرئیل گفت: جز این نیست که من فرستاده پروردگار تو ام تا به تو پسری پاکیزه (از هر گناه) ببخشم.');
INSERT INTO "badkhashani" VALUES(19,20,'19:20','مریم گفت: چگونه پسری خواهم داشت حال آنکه بشری به من دست نزده و بدکار (هم) نبوده‌ام؟!');
INSERT INTO "badkhashani" VALUES(19,21,'19:21','جبرئیل گفت: فرمان حق چنین است. پروردگارت فرموده است که این (کار) برای من آسان است. و تا او را معجزه‌ای برای مردم گردانیم و رحمتی از سوی خود سازیم و (این تصمیم) کاری مقدر شده و انجام یافتنی است.');
INSERT INTO "badkhashani" VALUES(19,22,'19:22','پس مریم (از دمیدن فرشته) به او (عیسی) باردار شد و به سبب آن حمل، به مکانی دور (از مردم) کناره گرفت.');
INSERT INTO "badkhashani" VALUES(19,23,'19:23','پس درد ولادت او را به‌سوی تنۀ درخت خرما آورد. گفت: ای کاش پیش از این مرده بودم و یکباره از یادها فراموش شده بودم.');
INSERT INTO "badkhashani" VALUES(19,24,'19:24','پس (فرشته) از جانب پائین او ندا داد که اندوهگین مشو، چون پروردگارت پایین تو چشمه‌ای را جاری ساخته است.');
INSERT INTO "badkhashani" VALUES(19,25,'19:25','و تنۀ درخت خرما را بسوی خود بجنبان که بر تو خرمای تازه چیده شده می‌ریزاند.');
INSERT INTO "badkhashani" VALUES(19,26,'19:26','پس بخور و بنوش و چشم را روشن و آسوده دار. پس اگر کسی از آدمیان را دیدی، بگو: من برای پروردگار مهربان روزه نذر کرده‌ام بنابراین امروز با هیچ انسانی سخن نخواهم گفت.');
INSERT INTO "badkhashani" VALUES(19,27,'19:27','پس او را برداشت و پیش قوم خود برد، گفتند: ای مریم! به راستی عجب کار قبیحی آورده‌ای!');
INSERT INTO "badkhashani" VALUES(19,28,'19:28','ای خواهر هارون! پدرت مرد بد نبود و مادرت (نیز) زن بدکار نبود.');
INSERT INTO "badkhashani" VALUES(19,29,'19:29','پس مریم به‌سوی طفل (عیسی) اشاره کرد (که از او بپرسید) گفتند: ما چگونه با طفلی که در گهواره است سخن بگوییم؟!');
INSERT INTO "badkhashani" VALUES(19,30,'19:30','(عیسی) گفت: من بنده الله هستم، به من کتاب داده و مرا به پیغمبری انتخاب نموده است.');
INSERT INTO "badkhashani" VALUES(19,31,'19:31','و هر جا که باشم، مرا مبارک گردانیده است. و تا وقتی که زنده باشم، مرا به نماز و زكات سفارش کرده است.');
INSERT INTO "badkhashani" VALUES(19,32,'19:32','و (همچنان) به نیک رفتاری با مادرم (مرا سفارش نموده) و مرا متکبر و سرکش و بدبخت نگردانیده است.');
INSERT INTO "badkhashani" VALUES(19,33,'19:33','و سلام بر من، روزی که تولد شده‌ام و روزی که می‌میرم و روزی که دوباره زنده می‌شوم.');
INSERT INTO "badkhashani" VALUES(19,34,'19:34','این است (اوصاف) عیسی پسر مریم، سخن راست و درست که ایشان (اهل کتاب) در آن شک می‌کنند.');
INSERT INTO "badkhashani" VALUES(19,35,'19:35','الله را سزاوار نیست که فرزندی گیرد، او پاک و منزه است (از تهمت‌های ناروا)، چون کاری را اراده کند، تنها کافی است که به او بگوید: شو! پس بی‌درنگ موجود می‌شود!');
INSERT INTO "badkhashani" VALUES(19,36,'19:36','و بی‌گمان الله پروردگار من و پروردگار شماست. پس او را بپرستید، این است راه راست.');
INSERT INTO "badkhashani" VALUES(19,37,'19:37','اما (راه راست را گذاشته) گروه‌ها در میان خود اختلاف کردند. پس وای بر کافران از حاضر شدن در روزی بزرگ.');
INSERT INTO "badkhashani" VALUES(19,38,'19:38','چقدر شنوا و چقدر بینا می‌باشند روزی که به‌سوی ما می‌آیند، اما این ظالمان امروز در گمراهی آشکاری قرار دارند.');
INSERT INTO "badkhashani" VALUES(19,39,'19:39','و آنان را که (اکنون) در غفلت به سر می‌برند، از روز حسرت و پشیمانی بترسان، وقتی که کار به انجام رسانیده می‌شود و آنان ایمان نمی‌آورند.');
INSERT INTO "badkhashani" VALUES(19,40,'19:40','البته ماییم که زمین و همۀ کسانی را که بر روی آن هستند به میراث می‌بریم و به‌سوی ما برگردانیده می‌شوند.');
INSERT INTO "badkhashani" VALUES(19,41,'19:41','و یادآور شو در این کتاب ابراهیم را، بی‌گمان او بسیار راستگو و پیغمبر بود.');
INSERT INTO "badkhashani" VALUES(19,42,'19:42','وقتی که به پدر خود گفت: ای پدر جان! چرا چیزی را پرستش می‌کنی که نه می‌شنود و نه می‌بیند و نه چیزی را از تو دفع می‌کند؟');
INSERT INTO "badkhashani" VALUES(19,43,'19:43','ای پدر جان! البته از علم چیزی به من آمده که به تو نیامده است، لذا، از من پیروی کن تا تو را به راه راست هدایت نمایم.');
INSERT INTO "badkhashani" VALUES(19,44,'19:44','ای پدر جان! شیطان را پرستش مکن، به راستی که شیطان عصیانگر پروردگار مهربان است.');
INSERT INTO "badkhashani" VALUES(19,45,'19:45','ای پدرم! من از این می‌ترسم که عذابی از جانب پروردگار مهربان به تو برسد و آنگاه دوست و همنشین شیطان باشی.');
INSERT INTO "badkhashani" VALUES(19,46,'19:46','(پدر ابراهیم) گفت: ای ابراهیم! آیا تو از معبودان من روگردانی؟ اگر (از مخالفت و اعراض خود) بازنیایی حتما تو را سنگ‌سار خواهم کرد، و برو مدت دراز از من دور شو.');
INSERT INTO "badkhashani" VALUES(19,47,'19:47','(ابراهیم) گفت: سلام بر تو (با تو وداع می‌کنم)، به زودی از پروردگارم برایت آمرزش می‌خواهم، چون که او بر من مهربان است.');
INSERT INTO "badkhashani" VALUES(19,48,'19:48','و (حالا) از شما و از آنچه به جز الله می‌پرستید، کناره‌گیری می‌کنم. و پروردگارم را (به دعا) می‌خوانم، امید است که در خواندن پروردگارم ناامید و ناکام نباشم.');
INSERT INTO "badkhashani" VALUES(19,49,'19:49','پس وقتی از آنان و از آنچه به جای الله عبادت می‌کردند دوری کرد، اسحاق و یعقوب را به او بخشیدیم و هر یک را پیغمبر گردانیدیم.');
INSERT INTO "badkhashani" VALUES(19,50,'19:50','و از رحمت خویش به آنان بخشیدیم و ایشان را نیک نام و بلند آوازه (و بلند مرتبه) گردانیدیم.');
INSERT INTO "badkhashani" VALUES(19,51,'19:51','و یادآور شو در این کتاب موسی را، یقینا که او خالص کرده‌شده و فرستاده‌ای پیغمبر (بلندمرتبه) بود.');
INSERT INTO "badkhashani" VALUES(19,52,'19:52','و او را از جانب راست کوه طور ندا کردیم و او را رازگویان نزدیک خود ساختیم (با او سخن گفتیم).');
INSERT INTO "badkhashani" VALUES(19,53,'19:53','و ما از رحمت خود، برادرش هارون را که پیغمبر بود، (مددگار) به او بخشیدیم.');
INSERT INTO "badkhashani" VALUES(19,54,'19:54','و یادآور شو در این کتاب اسماعیل را، همانا او در وعده‌هایش صادق، و رسول و پیغمبر (الله) بود.');
INSERT INTO "badkhashani" VALUES(19,55,'19:55','و او خانواده‌اش را به نماز و زکات امر می‌کرد و در نزد پروردگارش پسندیده بود.');
INSERT INTO "badkhashani" VALUES(19,56,'19:56','و یادآور شو در این کتاب ادريس را، البته او بسیار راست کردار و پیغمبر بزرگ بود.');
INSERT INTO "badkhashani" VALUES(19,57,'19:57','و ما او را به مقام بلند ارتقا دادیم.');
INSERT INTO "badkhashani" VALUES(19,58,'19:58','آنان کسانی از پیغمبران بودند که الله برایشان نعمت داد (از جمله)، از نسل آدم و از نسل کسانی که با نوح بر کشتی سوار کردیم و از فرزندان ابراهیم و يعقوب و از کسانی که آنان را هدایت نمودیم و برگزیدیم، چون آیات پروردگار مهربان بر آنان خوانده می‌شد، سجده‌کنان و گریان (به زمین) می‌افتادند.');
INSERT INTO "badkhashani" VALUES(19,59,'19:59','پس از آنان فرزندان ناخلف جانشین شدند که نماز را ضائع کردند و از شهوات پیروی کردند، پس به زودی با زیان و ناکامی (در وادی غی) روبرو خواهند شد.');
INSERT INTO "badkhashani" VALUES(19,60,'19:60','مگر کسی که توبه کند و ایمان بیاورد و کارهای شایسته انجام دهد، پس ایشان وارد بهشت می‌شوند و هیچ ظلمی به آنان نمی‌شود.');
INSERT INTO "badkhashani" VALUES(19,61,'19:61','باغ‌های جاودان که پروردگار مهربان بندگانش را در غیب وعده داده است. و وعدۀ الله حقاً آمدنی است.');
INSERT INTO "badkhashani" VALUES(19,62,'19:62','در آن جا هیچ سخن بیهوده نمی‌شنوند، بلکه سلام (می‌شنوند)، روزی‌شان صبح و شام برایشان آماده است.');
INSERT INTO "badkhashani" VALUES(19,63,'19:63','این همان جنتی است که به هر کس از بندگان ما که پرهیزگار باشد به میراث می‌دهیم.');
INSERT INTO "badkhashani" VALUES(19,64,'19:64','و (ای جبرئیل به رسول ما بگو: ما فرشتگان) جز به فرمان پروردگارت فرود نمی‌آییم، آنچه پیش روی ماست و آنچه پشت سر ماست و آنچه میان این دو است، همه به او اختصاص دارد. و پروردگارت هرگز فراموش کار نیست.');
INSERT INTO "badkhashani" VALUES(19,65,'19:65','پروردگار آسمان‌ها و زمین و آنچه در میان این دو است، پس او را پرستش کن و بر عبادت او صابر باش، آیا همتایی برای او می‌شناسی؟');
INSERT INTO "badkhashani" VALUES(19,66,'19:66','و انسان می‌گوید: آیا وقتی بمیرم، راستی (دوباره از قبر) بیرون آورده خواهم شد؟');
INSERT INTO "badkhashani" VALUES(19,67,'19:67','آیا انسان به یاد نمی‌آورد که ما قبلا او را آفریدیم، در حالیکه چیزی نبود؟!');
INSERT INTO "badkhashani" VALUES(19,68,'19:68','پس قسم به پروردگارت! که البته آنان را با شیاطین جمع می‌کنیم، باز همه را در حالی که به زانو افتاده‌اند، گرد دوزخ حاضر خواهیم کرد.');
INSERT INTO "badkhashani" VALUES(19,69,'19:69','باز از میان هر گروه افرادی را بیرون می‌کنیم که در مقابل پروردگار مهربان از همه سرکش‌تر بوده‌اند.');
INSERT INTO "badkhashani" VALUES(19,70,'19:70','باز ما به آنان که برای درآمدن در دوزخ سزاوارترند داناتریم.');
INSERT INTO "badkhashani" VALUES(19,71,'19:71','و از شما هیچ کس نیست مگر اینکه وارد آن (دوزخ) می‌شود. این امر بر پروردگارت حتمی و فیصله قطعی است.');
INSERT INTO "badkhashani" VALUES(19,72,'19:72','باز پرهیزگاران را (از آتش) نجات می‌دهیم و ظالمان را به زانو افتاده در آن رها می‌کنیم.');
INSERT INTO "badkhashani" VALUES(19,73,'19:73','و چون آیات واضح ما بر آنان تلاوت شود، کافران به مؤمنان می‌گویند: کدام یک از دو گروه جایگاهش بهتر و مجلسش زیباتر است؟!');
INSERT INTO "badkhashani" VALUES(19,74,'19:74','و چه بسیار نسل‌ها را که پیش از آنان بودند، هلاک کردیم که نسبت به آنها مال بهتر و متاع زیباتر داشتند.');
INSERT INTO "badkhashani" VALUES(19,75,'19:75','بگو: هر کس که در گمراهی فرو رفته باشد، پروردگار مهربان به ایشان مهلت می‌دهد تا وقتی که آنچه را به آنان وعده داده شده است؛ یا عذاب، یا قیامت را ببینند، پس خواهند دانست که چه کسی جایگاهش بدتر و لشکرش ضعیف‌تر و ناتوانتر است.');
INSERT INTO "badkhashani" VALUES(19,76,'19:76','و الله بر هدایت کسانی که هدایت یافته‌اند، می‌افزاید. و نیکی‌های پایدار در نزد پروردگارت از لحاظ ثواب و عاقبت بهتر است.');
INSERT INTO "badkhashani" VALUES(19,77,'19:77','آیا دیدی آن کسی را که به آیات ما كفر ورزید و گفت: حتما به من مال و فرزند بسیار داده خواهد شد.');
INSERT INTO "badkhashani" VALUES(19,78,'19:78','آیا از غیب آگاه شده است؟ یا از پروردگار مهربان عهدی گرفته است؟');
INSERT INTO "badkhashani" VALUES(19,79,'19:79','نه، چنین نیست که او می‌گوید، به زودی آنچه را او می‌گوید، خواهیم نوشت و عذاب را برای او خواهیم افزود.');
INSERT INTO "badkhashani" VALUES(19,80,'19:80','و آنچه را می‌گوید، از وی به ارث می‌بریم و پیش ما تنها می‌آید.');
INSERT INTO "badkhashani" VALUES(19,81,'19:81','و به جای الله معبودانی گرفتند تا برایشان سبب عزت باشند.');
INSERT INTO "badkhashani" VALUES(19,82,'19:82','نه، چنین نیست، به زودی عبادت‌شان را انکار خواهند کرد و ضد و دشمن آنان می‌گردند.');
INSERT INTO "badkhashani" VALUES(19,83,'19:83','آیا ندانسته‌ای که ما شيطان‌ها را بر کافران فرستادیم تا سخت آنها را (بر انجام گناه) تحریک کنند؟');
INSERT INTO "badkhashani" VALUES(19,84,'19:84','پس بر (عذاب) آنها شتاب مکن جز این نیست که ما برای آنها مدت (عذاب را) می‌شماریم.');
INSERT INTO "badkhashani" VALUES(19,85,'19:85','روزی که پرهیزگاران را چون مهمانان گرامی به‌سوی رحمن حشر می‌کنیم.');
INSERT INTO "badkhashani" VALUES(19,86,'19:86','و مجرمان را در حال تشنگی به‌سوی دوزخ سوق دهیم.');
INSERT INTO "badkhashani" VALUES(19,87,'19:87','نمی‌توانند شفاعت بکنند، مگر کسی که از جانب پروردگار مهربان عهدی گرفته باشد.');
INSERT INTO "badkhashani" VALUES(19,88,'19:88','و گفتند: (مشرکین) پروردگار مهربان فرزند گرفته است (لذا ما او را عبادت می‌کنیم).');
INSERT INTO "badkhashani" VALUES(19,89,'19:89','به راستی که (شما مشرکان) سخن بسیار زشتی را (بر زبان) آوردید.');
INSERT INTO "badkhashani" VALUES(19,90,'19:90','نزدیک است که آسمان‌ها از این سخن پاره پاره شوند و زمین بشگافد و کوه‌ها به شدت فرو ریخته شوند.');
INSERT INTO "badkhashani" VALUES(19,91,'19:91','از اینکه به پروردگار مهربان فرزند قایل شدند.');
INSERT INTO "badkhashani" VALUES(19,92,'19:92','و برای پروردگار مهربان سزاوار نیست که فرزند گیرد.');
INSERT INTO "badkhashani" VALUES(19,93,'19:93','(چون) هر که در آسمان‌ها و زمین است، مگر اینکه بنده وار به‌سوی پروردگار مهربان می‌آید.');
INSERT INTO "badkhashani" VALUES(19,94,'19:94','يقينا همۀ آنها را به حصر و حساب آورده و تعدادشان را به شمارش خاص شمار کرده است.');
INSERT INTO "badkhashani" VALUES(19,95,'19:95','و روز قیامت همۀ آنان تک و تنها به دربار او خواهد آمد.');
INSERT INTO "badkhashani" VALUES(19,96,'19:96','البته آنانی که ایمان آوردند و کارهای نیک کردند، به زودی پروردگار مهربان در دل‌های آنان محبت و دوستی قرار می‌دهد.');
INSERT INTO "badkhashani" VALUES(19,97,'19:97','پس جز این نیست که ما (قرآن) را بر زبان تو آسان کردیم تا به وسیلۀ آن پرهیزگاران را مژده دهی و مردم ستیزه جو را با آن بترسانی.');
INSERT INTO "badkhashani" VALUES(19,98,'19:98','و چه بسیار هلاک کردیم پیش از آنها نسل‌ها و امت‌هایی را، آیا کسی از آنان را می‌یابی، یا (کمترین) صدایی از آنان می‌شنوی؟');
INSERT INTO "badkhashani" VALUES(20,1,'20:1','طه، مفهوم این حروف به الله معلوم است و بس. (نام پیغمبر ج نمی‌باشد).');
INSERT INTO "badkhashani" VALUES(20,2,'20:2','ما قرآن را بر تو نازل نکردیم تا خود را به رنج و مشقت اندازی.');
INSERT INTO "badkhashani" VALUES(20,3,'20:3','بلکه (آن را نازل کردیم) برای پند آموزی کسانی که از الله می‌ترسند.');
INSERT INTO "badkhashani" VALUES(20,4,'20:4','کتابی است نازل شده از سوی کسی که زمین و آسمان‌های بلند را آفریده است.');
INSERT INTO "badkhashani" VALUES(20,5,'20:5','همان پروردگار بخشنده که بر عرش قرار گرفت.');
INSERT INTO "badkhashani" VALUES(20,6,'20:6','هر آنچه در آسمان‌ها و هر آنچه در زمین است و هر آنچه در میان آن دو است و هر آنچه زیر خاک است؛ همه از الله است (و در تصرف او قرار دارند).');
INSERT INTO "badkhashani" VALUES(20,7,'20:7','و اگر سخن را آشکارا گویی (یا آن را پنهان کنی، بدان که) او سخن پنهان و پنهان‌تر را می‌داند.');
INSERT INTO "badkhashani" VALUES(20,8,'20:8','الله آن ذاتی است که هیچ معبودی برحق به جز او نیست، برای اوست نام‌های نیکو و زیبا.');
INSERT INTO "badkhashani" VALUES(20,9,'20:9','و آیا خبر موسی به تو رسیده است.');
INSERT INTO "badkhashani" VALUES(20,10,'20:10','هنگامی که آتشی دید، پس به خانواده‌اش گفت: توقف کنید، زیرا من آتشی دیدم، امیدوارم از آن آتش شعله‌ای برای شما بیاورم یا بر آن آتش راهنمایی بیابم.');
INSERT INTO "badkhashani" VALUES(20,11,'20:11','پس چون به آن آتش نزدیک شد، صدا کرده شد که ای موسی!.۔');
INSERT INTO "badkhashani" VALUES(20,12,'20:12','يقينا من پروردگار توام، پس نعل‌هایت را از پا بیرون آر، زیرا تو در وادی مقدس طوی هستی.');
INSERT INTO "badkhashani" VALUES(20,13,'20:13','و من تو را (به رسالتم) برگزیده‌ام، پس به آنچه که وحی می‌شود، گوش کن.');
INSERT INTO "badkhashani" VALUES(20,14,'20:14','البته من «الله» هستم، و معبودی بر حق جز من نیست، پس مرا عبادت کن و نماز را برای یاد کردن من برپا کن.');
INSERT INTO "badkhashani" VALUES(20,15,'20:15','بی‌گمان قیامت آمدنی است، (ولی) می‌خواهم (وقت) آن را (كاملاً) پنهان دارم تا هرکس برابر کوشش خود (خیر یا شر) جزا داده شود.');
INSERT INTO "badkhashani" VALUES(20,16,'20:16','پس نباید که تو را از آن (ایمان به روز قیامت و عمل نیک) کسی باز دارد که به قیامت ایمان ندارد و از هوی و هوس خود پیروی کرده است، پس در نتیجه هلاک شوی.');
INSERT INTO "badkhashani" VALUES(20,17,'20:17','(باز الله پرسید:) ای موسی! در دست راست تو چیست؟');
INSERT INTO "badkhashani" VALUES(20,18,'20:18','موسی گفت: این عصای من است، بر آن تکیه می‌کنم و با آن برای گوسفندانم برگ می‌تکانم و نیاز‌های دیگری (نیز) برای من از آن بر می‌آید.');
INSERT INTO "badkhashani" VALUES(20,19,'20:19','الله فرمود: ای موسی! آن را بانداز.');
INSERT INTO "badkhashani" VALUES(20,20,'20:20','پس موسی آن را انداخت که ناگهان ماری گردید که می‌دوید.');
INSERT INTO "badkhashani" VALUES(20,21,'20:21','الله فرمود: آن را بگیر و نترس، به زودی آن را به حالت اولی‌اش بر می‌گردانیم.');
INSERT INTO "badkhashani" VALUES(20,22,'20:22','و دست خود را در زیر بازویت ببر، تا سفید و درخشان، بدون هیچ عیبی به عنوان نشانۀ دیگر بیرون آید.');
INSERT INTO "badkhashani" VALUES(20,23,'20:23','تا از آیات بزرگ خود به تو نشان دهیم.');
INSERT INTO "badkhashani" VALUES(20,24,'20:24','(اکنون) به‌سوی فرعون برو، که او طغیان و سرکشی کرده است.');
INSERT INTO "badkhashani" VALUES(20,25,'20:25','موسی (درخواست نموده) گفت: ای پروردگارم! سینه‌ام را برایم بگشا.');
INSERT INTO "badkhashani" VALUES(20,26,'20:26','و کارم را برایم آسان کن.');
INSERT INTO "badkhashani" VALUES(20,27,'20:27','و گره را از زبانم بگشا.');
INSERT INTO "badkhashani" VALUES(20,28,'20:28','تا مردم سخنم را بفهمند.');
INSERT INTO "badkhashani" VALUES(20,29,'20:29','و برایم از خانواده‌ام وزیری قرار ده.');
INSERT INTO "badkhashani" VALUES(20,30,'20:30','برادرم هارون را.');
INSERT INTO "badkhashani" VALUES(20,31,'20:31','پشتِ مرا به او استوار کن.');
INSERT INTO "badkhashani" VALUES(20,32,'20:32','و او را در کارم (رسالت) شریک کن.');
INSERT INTO "badkhashani" VALUES(20,33,'20:33','تا تو را بسیار تسبیح گوییم.');
INSERT INTO "badkhashani" VALUES(20,34,'20:34','و بسیار تو را یاد کنیم.');
INSERT INTO "badkhashani" VALUES(20,35,'20:35','چون تو به احوال ما بینایی.');
INSERT INTO "badkhashani" VALUES(20,36,'20:36','الله فرمود: ای موسی! البته خواسته‌ات به تو داده شد.');
INSERT INTO "badkhashani" VALUES(20,37,'20:37','و به راستی بار دیگر (پیش از این) هم بر تو منت نهادیم.');
INSERT INTO "badkhashani" VALUES(20,38,'20:38','هنگامی که به مادرت آنچه را که باید الهام می‌شد، الهام کردیم.');
INSERT INTO "badkhashani" VALUES(20,39,'20:39','(آن الهام این بود) که او را در صندوقی قرار ده، پس او را در دریا بانداز تا دریا او را به ساحل افگند، و دشمن من و دشمن او، وی را بگیرد، و من محبتی از جانب خود بر تو افگندم، و تا زیر نظر من پرورش یابی.');
INSERT INTO "badkhashani" VALUES(20,40,'20:40','وقتی که خواهرت می‌رفت و می‌گفت: آیا کسی را به شما نشان دهم که پرورش او را به عهده گیرد؟ باز تو را به مادرت باز گردانیدیم تا چشمش روشن شود، و غمگین نگردد، و تو کسی را کشتی و ما تو را از غم و اندوه نجات دادیم، و تو را با آزمایش‌های سخت آزمودیم، پس چندین سال در میان اهل مدین ماندی، باز ای موسی! در زمان مقدر (به مصر) بازگشتی.');
INSERT INTO "badkhashani" VALUES(20,41,'20:41','تو را برای (رسانیدن پیغام) خود ساختم.');
INSERT INTO "badkhashani" VALUES(20,42,'20:42','برو تو و برادرت با معجزه‌های من (برای دعوت فرعون و قوم او) و در یاد کردن من سستی مکنید.');
INSERT INTO "badkhashani" VALUES(20,43,'20:43','به‌سوی فرعون بروید که البته او سرکشی و طغیان کرده است.');
INSERT INTO "badkhashani" VALUES(20,44,'20:44','و با او سخن نرم بگویید، شاید پند گیرد یا (از عذاب الله) بترسد.');
INSERT INTO "badkhashani" VALUES(20,45,'20:45','گفتند: (موسی و هارون) ای پروردگار ما! همانا از آن می‌ترسیم که او بر ما تجاوز کند یا آن که طغیان کند.');
INSERT INTO "badkhashani" VALUES(20,46,'20:46','(الله) فرمود: نترسید، يقينا من با شما هستم بی‌گمان می‌شنوم و می‌بینیم.');
INSERT INTO "badkhashani" VALUES(20,47,'20:47','پس نزد او بروید و بگویید: همانا ما دو فرستادۀ پروردگار تو هستیم، پس بنی اسرائیل را همرای ما بفرست و آنان را عذاب مکن. به درستی که ما معجزه‌ای از سوی پروردگارت برایت آورده‌ایم. و سلام بر کسی که از هدایت پیروی کند.');
INSERT INTO "badkhashani" VALUES(20,48,'20:48','چون به ما وحی شده است که عذاب بر کسی واقع می‌شود که (آیات آسمانی و معجزات را) تکذیب کند و روی بگرداند.');
INSERT INTO "badkhashani" VALUES(20,49,'20:49','فرعون گفت: ای موسی! پس پروردگار شما کیست (که به‌سوی او مرا دعوت می‌دهید)؟');
INSERT INTO "badkhashani" VALUES(20,50,'20:50','موسی (در جواب) گفت: پروردگار ما کسی است که هر چیزی را وجود بخشیده باز آن را (در راستای آن چیزی که برایش آفریده شده) هدایت کرده است.');
INSERT INTO "badkhashani" VALUES(20,51,'20:51','(فرعون) گفت: پس حال (و سرنوشت) نسل‌های پیشین چگونه است؟');
INSERT INTO "badkhashani" VALUES(20,52,'20:52','(موسی) گفت: علم آن در کتابی نزد پروردگارم است، پروردگارم نه خطا می‌کند و نه فراموش می‌کند.');
INSERT INTO "badkhashani" VALUES(20,53,'20:53','پروردگاری که زمین را برای شما فرشی (مانند گهواره) ساخت و در آن برای شما راه‌ها را مهیا کرد و از آسمان آبی فرود آورد، پس به وسیلۀ آن انواع مختلف از نباتات را جفت جفت بیرون آوردیم.');
INSERT INTO "badkhashani" VALUES(20,54,'20:54','بخورید و چهارپایانتان را بچرانید، يقينا در این امر برای صاحبان خرد نشانه‌هایی است.');
INSERT INTO "badkhashani" VALUES(20,55,'20:55','از این زمین شما را آفریدیم و در این زمین شما را باز می‌گردانیم و از آن بار دیگر شما را بیرون (زنده) می‌کنیم.');
INSERT INTO "badkhashani" VALUES(20,56,'20:56','البته همۀ آیات خود را به او (فرعون) نشان دادیم، ولی او تکذیب کرد و نپذیرفت.');
INSERT INTO "badkhashani" VALUES(20,57,'20:57','(فرعون) گفت: ای موسی! آیا آمده‌ای که ما را با جادوی خود از سرزمین خود ما بیرونی کنی؟');
INSERT INTO "badkhashani" VALUES(20,58,'20:58','پس بدان که ما نیز حتما جادویی مانند آن را برای تو می‌آوریم، پس بین ما و خودت وعده‌ای در مکان هموار بگذار که نه ما و نه تو از آن تخلف کنیم.');
INSERT INTO "badkhashani" VALUES(20,59,'20:59','موسی گفت: وعده گاه‌تان روز جشن است. و اینکه همۀ مردم در وقت چاشت جمع کرده شوند.');
INSERT INTO "badkhashani" VALUES(20,60,'20:60','پس فرعون برگشت و همۀ مکر و فریب خود را جمع کرد، باز (برای مقابله) آمد.');
INSERT INTO "badkhashani" VALUES(20,61,'20:61','موسی به آنان گفت: وای بر شما! بر الله دروغ نبندید، که الله شما را با عذاب سخت ریشه کن خواهد کرد، و کسی که بر الله دروغ بست حتما ناکام می‌گردد.');
INSERT INTO "badkhashani" VALUES(20,62,'20:62','پس (جادوگران) در میان خود اختلاف نمودند و مخفیانه باهم گفتگو کردند.');
INSERT INTO "badkhashani" VALUES(20,63,'20:63','(بالآخره به نتیجه رسیدند و) گفتند: البته این دو جادوگرند که می‌خواهند با جادوی خویش شما را از سرزمین‌تان بیرون کنند و آیین بهتر شما را از بین ببرند.');
INSERT INTO "badkhashani" VALUES(20,64,'20:64','لذا تمام حیله و تدبير خود را جمع کنید، باز صف بسته (به میدان) پیش آیید، بی‌گمان امروز هرکس غالب گردد، پیروز شده است.');
INSERT INTO "badkhashani" VALUES(20,65,'20:65','(جادوگران در آغاز مبارزه) گفتند: ای موسی! یا تو (اول عصایت را) می‌اندازی يا ما اولین کسی باشیم که می‌افگند؟');
INSERT INTO "badkhashani" VALUES(20,66,'20:66','موسی گفت: بلکه شما اول بافگنید، پس ناگهان بر اثر سحر آنان، ریسمان‌ها و عصاهایشان (در نظر موسی) چنان معلوم شد که می‌دوند.');
INSERT INTO "badkhashani" VALUES(20,67,'20:67','پس موسی در دل خود احساس ترس کرد (ولی آن را پنهان داشت).');
INSERT INTO "badkhashani" VALUES(20,68,'20:68','گفتیم: نترس، بدون شک تو غالب و برتری.');
INSERT INTO "badkhashani" VALUES(20,69,'20:69','و آنچه را که در دست راست داری بیانداز، تا هر چه را ساخته‌اند ببلعد، در حقیقت آنچه را که ساخته‌اند، نیرنگ جادوگر است. و جادوگر هرجا برود پیروز نمی‌شود.');
INSERT INTO "badkhashani" VALUES(20,70,'20:70','پس (با دیدنِ غلبۀ حق) جادوگران سجده‌کنان افتادند و گفتند: به پروردگار هارون و موسی ایمان آوردیم.');
INSERT INTO "badkhashani" VALUES(20,71,'20:71','(فرعون) گفت: آیا پیش از آنکه به شما اجازه دهم به او ایمان آوردید؟ بی‌گمان او بزرگ شماست که به شما جادو آموخته است، پس دست‌ها و پاهای شما را بر خلاف یکدیگر قطع می‌کنم، و شما را بر تنه‌های درخت خرما به دار می‌آویزم، و البته خواهید دانست کدام یک از ما و شما (در عذاب دادن) سخت‌تر و (در دنیا) پاینده‌تر است.');
INSERT INTO "badkhashani" VALUES(20,72,'20:72','(جادوگران به فرعون) گفتند: هرگز تو را بر دلایل روشنی که به ما رسیده و بر کسی که ما را آفریده است، ترجیح نمی‌دهیم. پس هر حکمی که (در حق ما) می‌خواهی، بکن. تو فقط در این زندگانی دنیاست که حکم می‌رانی.');
INSERT INTO "badkhashani" VALUES(20,73,'20:73','البته ما به پروردگار ما ایمان آوردیم تا گناهان ما را بیامرزد و آنچه از جادو را که ما را بر آن مجبور کردی (نیز بیامرزد)، و الله بهتر و پاینده‌تر است.');
INSERT INTO "badkhashani" VALUES(20,74,'20:74','و واقعا هرکس نزد پروردگارش مجرم بیاید، پس یقینا دوزخ برای اوست، که نه در آنجا می‌میرد و نه زنده می‌ماند.');
INSERT INTO "badkhashani" VALUES(20,75,'20:75','و هرکس نزد او مؤمن بیاید، در حالیکه اعمال نیک انجام داده باشد، پس این جماعت برای‌شان رتبه‌های بلند است.');
INSERT INTO "badkhashani" VALUES(20,76,'20:76','باغ‌های همیشگی، که از زیر (قصر‌ها و درختان) آن نهرها روان است، و همیشه در آن می‌مانند و این است پاداش کسی که پاکی اختیار کرده است.');
INSERT INTO "badkhashani" VALUES(20,77,'20:77','و همانا به موسی وحی فرستادیم که بندگانم را در شب ببر، و راه خشک برای آنان در دریا باز کن، که نه از رسیدن (لشکر دشمن) بترسی و نه (از غرق شدن در آن) بیمناک باشی.');
INSERT INTO "badkhashani" VALUES(20,78,'20:78','پس فرعون با لشکریانش آنان را تعقیب کرد، پس پوشانید از (امواج) دریا آنچه آنان را پوشانید.');
INSERT INTO "badkhashani" VALUES(20,79,'20:79','و (نتیجه این شد که) فرعون قومش را گمراه ساخت و (به راه راست) هدایت نکرد.');
INSERT INTO "badkhashani" VALUES(20,80,'20:80','ای بنی اسرائیل! در حقیقت ما شما را از دشمنتان نجات دادیم و در جانب راست کوه طور با شما وعده نهادیم و بر شما من و سلوی نازل کردیم.');
INSERT INTO "badkhashani" VALUES(20,81,'20:81','و از چیزهای پاکیزه‌ای که به شما روزی داده‌ایم بخورید و در آن از حد تجاوز نکنید که غضب من بر شما نازل خواهد شد، و هر کس که غضب من بر او نازل گردد یقینا که نابود شده است.');
INSERT INTO "badkhashani" VALUES(20,82,'20:82','و يقينا من بسیار آمرزنده‌ام برای کسی که توبه کند و ایمان آورد و عمل نیک انجام دهد، باز به راه مستقیم راهیاب شود.');
INSERT INTO "badkhashani" VALUES(20,83,'20:83','و ای موسی! چه چیز (سبب شد که) تو را از نزد قومت به شتاب آورد؟');
INSERT INTO "badkhashani" VALUES(20,84,'20:84','(موسی) گفت: آنان پی من‌اند، و ای پروردگارم! من به‌سوی تو شتافتم تا راضی شوی.');
INSERT INTO "badkhashani" VALUES(20,85,'20:85','الله فرمود: اما ما پس از (آمدن) تو قومت را آزمایش کردیم، و سامری آنها را گمراه ساخت.');
INSERT INTO "badkhashani" VALUES(20,86,'20:86','پس موسی خشمگین و با تأسف شدید به‌سوی قومش بازگشت و گفت: ای قوم من! آیا پروردگارتان وعدۀ نیک به شما نداد؟ آیا مدت بر شما دراز شد، یا خواستید که غضبی از سوی پروردگارتان بر شما نازل شود؟ چرا که با وعدۀ من مخالفت کردید.');
INSERT INTO "badkhashani" VALUES(20,87,'20:87','گفتند: ما به اختیار خود از وعدۀ تو مخالفت نکردیم، بلکه بارهای سنگین از زر و زیور قوم (قبط) بر ما تحمیل شده بود، پس آنها را دور انداختیم، و سامری نیز دور انداخت.');
INSERT INTO "badkhashani" VALUES(20,88,'20:88','پس (سامری) مجسمۀ گوساله‌ای را برای‌شان بیرون آورد که (مانند) صدای گوساله داشت، پس گفتند: این معبود شما و معبود موسی است و موسی آن را فراموش کرده است.');
INSERT INTO "badkhashani" VALUES(20,89,'20:89','پس (چگونه گوساله را معبود قرار دادند) آیا نمی‌بینند که پاسخ سخنان‌شان را نمی‌دهد و مالک هیچ زیان و سودی برایشان نیست؟');
INSERT INTO "badkhashani" VALUES(20,90,'20:90','و در حالیکه پیش از این هارون به آنان گفته بود: ای قوم من! شما با این (گوساله) مورد ابتلا و آزمون قرار گرفته‌اید، پروردگار شما الله مهربان است، پس از من پیروی کنید و امر مرا بجا آورید.');
INSERT INTO "badkhashani" VALUES(20,91,'20:91','(منحرفان بنی اسرائیل) گفتند: ما پیوسته به عبادت گوساله ادامه می‌دهیم تا آنکه موسی به نزد ما باز گردد.');
INSERT INTO "badkhashani" VALUES(20,92,'20:92','پس (وقتی موسی برگشت) گفت: ای هارون! وقتی آنان را دیدی که گمراه شدند چه چیزی تو را بازداشت؟');
INSERT INTO "badkhashani" VALUES(20,93,'20:93','که از من پیروی کنی؟ آیا دستور مرا نافرمانی کردی؟');
INSERT INTO "badkhashani" VALUES(20,94,'20:94','گفت: ای پسر مادرم! نه ریش مرا بگیر و نه موی سرم را، من از آن ترسیدیم که بگویی بین بنی اسرائیل تفرقه انداختی و سخن مرا رعایت نکردی.');
INSERT INTO "badkhashani" VALUES(20,95,'20:95','(موسی) گفت: ای سامری! مقصود و هدف تو چه بود؟');
INSERT INTO "badkhashani" VALUES(20,96,'20:96','(سامری) گفت: من چیزی را دیدم که (مردم) آن را نديدند، پس مشتی خاک از نقش پای فرستاده (الله) را گرفتم، پس آن را افگندم. این چنین نفس من (این کار را) در نظرم آراست.');
INSERT INTO "badkhashani" VALUES(20,97,'20:97','موسی گفت: پس برو، یقینا سزای تو در زندگی دنیا این باشد که بگویی: (به من) دست نزنید و نزدیک نشوید، البته وعدۀ دیگر داری که هرگز در آن با تو خلاف نشود. و به آن معبودت که برای عبادت وی معتكف بودی بنگر، که البته آن را می‌سوزانیم، باز آن را در دریا پراکنده می‌کنیم به پراگندگی خاص.');
INSERT INTO "badkhashani" VALUES(20,98,'20:98','بدون شک معبود شما تنها الله است که هیچ معبودی (بر حق) جز او نیست، علم او همه چیز را فرا گرفته است.');
INSERT INTO "badkhashani" VALUES(20,99,'20:99','این چنین اخبار گذشته را برای تو حکایت می‌کنیم، و يقينا از جانب خود پند (قرآن) به تو دادیم.');
INSERT INTO "badkhashani" VALUES(20,100,'20:100','هر کس که از آن روی گردان شود، پس او در روز قیامت بار گناه را بر می‌دارد.');
INSERT INTO "badkhashani" VALUES(20,101,'20:101','که همیشه در آن خواهند ماند و چه بد باری است که در روز قیامت خواهند داشت.');
INSERT INTO "badkhashani" VALUES(20,102,'20:102','روزی که در صور دمیده می‌شود و گناهکاران را در آن روز کبود چشم جمع می‌کنیم.');
INSERT INTO "badkhashani" VALUES(20,103,'20:103','در حالی که پنهانی در میان خویش می‌گویند: شما نمانده‌اید (در دنیا) جز ده شبانه روز.');
INSERT INTO "badkhashani" VALUES(20,104,'20:104','ما به آنچه می‌گویند داناتریم، وقتی نیک روش‌ترین‌شان می‌گوید: نمانده‌اید مگر یک روز.');
INSERT INTO "badkhashani" VALUES(20,105,'20:105','و از تو دربارۀ کوه‌ها می‌پرسند (که در روز قیامت چه خواهند شد) پس بگو: پروردگارم آنها را در هوا پراگنده خواهد کرد.');
INSERT INTO "badkhashani" VALUES(20,106,'20:106','پس آنها را به شکل میدان هموار می‌گذارد.');
INSERT INTO "badkhashani" VALUES(20,107,'20:107','که (تو ای مخاطب) در آن هیچ نشیب و فرازی را نمی‌بینی.');
INSERT INTO "badkhashani" VALUES(20,108,'20:108','در آن روز از دعوت دهنده‌ای پیروی می‌کنند که در آن هیچ کجی نباشد. و صداها در برابر پروردگار مهربان پست و خاموش می‌گردد و جز صدای آهسته نمی‌شنوی.');
INSERT INTO "badkhashani" VALUES(20,109,'20:109','در آن روز شفاعت (احدی) سود نمی‌بخشد، مگر کسی که پروردگار مهربان به او اجازه داده است و او را از روی گفتار بپسندد.');
INSERT INTO "badkhashani" VALUES(20,110,'20:110','الله می‌داند آنچه را که پیش روی مردم است و آنچه را که پشت سر آن‌هاست، ولی (مردم) از نگاه علم به او آگاهی ندارند.');
INSERT INTO "badkhashani" VALUES(20,111,'20:111','و چهره‌ها برای (الله) زنده پاینده که مدبر کائنات است اظهار تواضع می‌کنند و کسی که بار ظلم را بر دوش داشت، ناکام می‌گردد.');
INSERT INTO "badkhashani" VALUES(20,112,'20:112','و هرکس کارهای نیک کند و او مؤمن باشد، پس نه از ظلم (در حق خود) می‌ترسد و نه از کمی ثواب.');
INSERT INTO "badkhashani" VALUES(20,113,'20:113','و این چنین آن را قرآن عربی نازل کردیم و در آن بیم دادن و ترساندن را به (شیوه‌هایی گوناگون) آوردیم، تا پرهیزگار شوند، یا باعث یادآوری و پند ایشان شود.');
INSERT INTO "badkhashani" VALUES(20,114,'20:114','پس الله پادشاه حقیقی و برحق، بزرگ و بلند مرتبه است. و در (خواندن) قرآن عجله مکن پیش از آنکه وحی آن بر تو به انجام رسانده شود، و بگو: پروردگارم! بر علم و دانشم بیافزای.');
INSERT INTO "badkhashani" VALUES(20,115,'20:115','و به یقین پیش از این به‌سوی آدم حکم کردیم، پس (آن را) فراموش کرد و برای او عزم محکم نیافتیم.');
INSERT INTO "badkhashani" VALUES(20,116,'20:116','و یادآور شو وقتی را که به فرشتگان گفتیم: برای آدم سجده کنید، پس همه سجده کردند به جز ابلیس که (از سجده کردن) سرباز زد.');
INSERT INTO "badkhashani" VALUES(20,117,'20:117','پس گفتیم: ای آدم! در حقیقت این ابلیس برای تو و همسرت دشمن است، پس شما را از جنت بیرون نکند، که در رنج و مشقت افتی.');
INSERT INTO "badkhashani" VALUES(20,118,'20:118','و تو در آن (جنت) نه گرسنه می‌شوی و نه برهنه می‌مانی.');
INSERT INTO "badkhashani" VALUES(20,119,'20:119','و هم اینکه در آنجا نه تشنه می‌شوی و نه گرما زدۀ آفتاب.');
INSERT INTO "badkhashani" VALUES(20,120,'20:120','پس شیطان به‌سوی او وسوسه انداخت، گفت: ای آدم! آیا تو را به درخت همیشگی و ملکی که زایل نمی‌شود، راهنمایی کنم؟');
INSERT INTO "badkhashani" VALUES(20,121,'20:121','پس هر دو از آن درخت خوردند، در نتیجه، شرمگاه‌های‌شان بر آنها آشکار شد و شروع کردند به چسپانیدن برگ‌های جنت بر خود، و این گونه آدم به پروردگارش عصیان کرد و راه را گم کرد.');
INSERT INTO "badkhashani" VALUES(20,122,'20:122','باز پروردگارش او را برگزید، زیرا توبۀ او را قبول کرد و هدایتش کرد.');
INSERT INTO "badkhashani" VALUES(20,123,'20:123','(الله) فرمود: پس هر دو با هم از آن پایان شوید، در حالی که بعضی از (نسل) شما دشمن بعضی دیگر خواهند بود، پس اگر برای شما از جانب من هدایت رسد، هر کس از هدایتم پیروی کند، نه گمراه می‌شود و نه رنج می‌کشد.');
INSERT INTO "badkhashani" VALUES(20,124,'20:124','و هرکس از یاد من روی بگرداند زندگی تنگ و سخت خواهد داشت. و روز قیامت او را نابینا حشر خواهیم کرد.');
INSERT INTO "badkhashani" VALUES(20,125,'20:125','می‌گوید: ای پروردگارم! چرا مرا نابینا حشر کردی؟ حال آنکه (در دنیا) بينا بودم.');
INSERT INTO "badkhashani" VALUES(20,126,'20:126','(الله) فرماید: همان طور که آیات ما به تو رسید و آنها را فراموش کردی، همان‌گونه امروز فراموش می‌شوی.');
INSERT INTO "badkhashani" VALUES(20,127,'20:127','و این چنین سزا می‌دهیم کسی را که اسراف کرده و به آیات پروردگارش ایمان نیاورده است. و البته عذاب آخرت سخت‌تر و پاینده‌تر است.');
INSERT INTO "badkhashani" VALUES(20,128,'20:128','آیا برای آنان روشن نشده است که پیش از ایشان چه بسیار نسل‌ها را نابود کردیم که (اکنون آنان) در محل سکونت‌شان رفت و آمد می‌کنند؟ یقینا در این (امر) نشانه‌هایی برای خردمندان است.');
INSERT INTO "badkhashani" VALUES(20,129,'20:129','و اگر سخنی که سابقا از پروردگارت صادر شده، و موعدی مشخص مقرر نشده بود، بدون شک (عذاب آنها در همین دنیا) حتما لازم و واقع می‌شد.');
INSERT INTO "badkhashani" VALUES(20,130,'20:130','پس در مقابل آنچه می‌گویند صبر کن. و قبل از طلوع آفتاب و پیش از غروب آن و در بخشی از ساعات شب و در بخش‌هایی از روز پروردگارت را به پاکی یاد کن، تا راضی و خشنود گردی،');
INSERT INTO "badkhashani" VALUES(20,131,'20:131','و به‌سوی آنچه از زیب و زینت زندگی دنیا که برخی از آنان را بهره‌مند ساخته‌ایم چشم مدوز تا آنان را در آن بیازماییم، و روزی پروردگار تو بهتر و پایدارتر است.');
INSERT INTO "badkhashani" VALUES(20,132,'20:132','وخانواده‌ات را به نماز امر کن و (خودت نیز) بر آن صابر باش، ما از تو روزی نمی‌خواهیم، ما تو را روزی می‌دهیم و عاقبت از تقوا است.');
INSERT INTO "badkhashani" VALUES(20,133,'20:133','و (کافران) گفتند: چرا معجزه‌ای از سوی پروردگارش برای ما نمی‌آورد؟ آیا دليل واضح که در صحیفه‌های پیشین است، برایشان نیامده است؟!');
INSERT INTO "badkhashani" VALUES(20,134,'20:134','و اگر ما آنان را پیش از وی به عذابی هلاک می‌کردیم، می‌گفتند: پروردگارا! چرا پیغمبری را برای ما نفرستادی تا آیات تو را پیروی می‌کردیم، پیش از آنکه خوار و رسوا می‌شدیم؟');
INSERT INTO "badkhashani" VALUES(20,135,'20:135','بگو: همه منتظرند پس (شما نیز) در انتظار باشید، پس به زودی خواهید دانست که چه کسانی اهل راه راست هستند و چه کسی راه یافته است.');
INSERT INTO "badkhashani" VALUES(21,1,'21:1','برای مردم (وقت) حساب‌شان نزدیک شده است، در حالی که آنان در غفلت روی گردانند.');
INSERT INTO "badkhashani" VALUES(21,2,'21:2','هیچ پند (و هدایت) جدیدی از نزد پروردگارشان به آنها نمی‌رسد، مگر اینکه بازی کنان آن را می‌شنوند.');
INSERT INTO "badkhashani" VALUES(21,3,'21:3','(در حالیکه) دل‌هایشان (به دنیا) مشغول است و آنانی که ظلم کرده بودند در پنهان به نجوی و راز گویی پرداختند و گفتند که آیا این (شخص) انسانی مانند شما نیست، آیا به‌سوی جادو می‌روید؟ حال آنکه شما (حقیقت را) می‌بینید.');
INSERT INTO "badkhashani" VALUES(21,4,'21:4','(در جواب بگو:) پروردگارم هر سخنی را که در آسمان و زمین باشد، می‌داند، و او شنوای داناست.');
INSERT INTO "badkhashani" VALUES(21,5,'21:5','بلکه گفتند: خواب‌های پراگنده است، بلکه آن را از پیش خود ساخته است بلکه او شاعر است، (اگر پیغمبر در دعوای خود راست باشد) پس طوری که پیشینیان (با معجزه‌ها) فرستاده شدند، باید معجزه‌ای (حسّی) برای ما بیاورد.');
INSERT INTO "badkhashani" VALUES(21,6,'21:6','پیش از آنان (نیز مردمِ) هیچ قریه‌ای که (اهل) آن را هلاک کردیم ایمان نیاوردند، پس آیا ایشان ایمان می‌آورند؟!');
INSERT INTO "badkhashani" VALUES(21,7,'21:7','و پیش از تو نیز جز مردانی را که به آنان وحی می‌کردیم، نفرستادیم. پس اگر شما نمی‌دانید، از اهل کتاب بپرسید.');
INSERT INTO "badkhashani" VALUES(21,8,'21:8','پیغمبران را جسدی که غذا نخورند قرار ندادیم و جاودان هم نبودند.');
INSERT INTO "badkhashani" VALUES(21,9,'21:9','باز وعدۀ (خویش) به آنان را راست گردانیدیم، و آنان و هر کسی را که خواستیم، نجات دادیم، و اسرافکاران (تجاوزکاران) را نابود کردیم.');
INSERT INTO "badkhashani" VALUES(21,10,'21:10','البته ما بسوی شما کتابی نازل کردیم که ذكرتان در آن است، آیا نمی‌اندیشید؟');
INSERT INTO "badkhashani" VALUES(21,11,'21:11','و چه بسیار قریه‌ها را که اهل آن ظالم بودند، در هم شکستیم (و هلاک کردیم) و پس از آنان قومی دیگر را آفریدیم.');
INSERT INTO "badkhashani" VALUES(21,12,'21:12','پس چون عذاب ما را احساس کردند، ناگهان از آن می‌گریختند.');
INSERT INTO "badkhashani" VALUES(21,13,'21:13','(به آنان گفته می‌شود:) نگریزید و به‌سوی ناز و نعمتی که در آن متنعم بودید، باز گردید، شاید که از شما (چیزی) پرسیده شود.');
INSERT INTO "badkhashani" VALUES(21,14,'21:14','گفتند: ای وای بر ما، واقعا که ما ظالم بودیم.');
INSERT INTO "badkhashani" VALUES(21,15,'21:15','و همیشه سخن‌شان همین بود تا اینکه آنان را مانند کشت درو شده، بی‌حرکت و خاموش شده گردانیدیم.');
INSERT INTO "badkhashani" VALUES(21,16,'21:16','و آسمان و زمین و آنچه را که در میان آن دو است برای بازی و شوخی (بیهوده) نیافریده‌ایم.');
INSERT INTO "badkhashani" VALUES(21,17,'21:17','اگر می‌خواستیم بازیچه‌ای بگیریم حتما آن را از نزد خود انتخاب می‌کردیم.');
INSERT INTO "badkhashani" VALUES(21,18,'21:18','بلکه حق را بر باطل می‌اندازیم، پس آن را می‌شکند و سرکوب می‌کند، پس ناگهان باطل محو و نابود می‌شود. و وای بر شما به سبب آنچه که (الله را به آن) وصف می‌کنید.');
INSERT INTO "badkhashani" VALUES(21,19,'21:19','و از اوست هر که در آسمان‌ها و زمین است، و آنان که در نزد وی هستند از عبادت او تکبر نمی‌کنند و خسته نمی‌شوند.');
INSERT INTO "badkhashani" VALUES(21,20,'21:20','(بلکه) شب و روز تسبیح می‌گویند، بی‌آنکه سست شوند.');
INSERT INTO "badkhashani" VALUES(21,21,'21:21','آیا معبودهای از (موجودات) زمین گرفته‌اند که (ایشان مرده‌ها) را زنده می‌کنند؟!');
INSERT INTO "badkhashani" VALUES(21,22,'21:22','اگر در آن دو (آسمان‌ها و زمین) غیر از الله معبودهای وجود داشت، حتما آسمان‌ها و زمین هردو تباه می‌شد، پس پاک و منزه است الله پروردگار عرش، از آنچه وصف می‌کنند.');
INSERT INTO "badkhashani" VALUES(21,23,'21:23','(الله) از آنچه می‌کند پرسیده نمی‌شود، ولی آنها پرسیده می‌شوند.');
INSERT INTO "badkhashani" VALUES(21,24,'21:24','آیا به جای او معبودانی را به پرستش گرفته‌اند؟ بگو: دلیل خود را بیان دارید، این است کتاب کسانی که با من‌اند و (اینها هم) کتاب‌های امت‌های قبل از من است. بلکه اکثر آنان حق را نمی‌دانند، پس آنها روی گردان‌اند.');
INSERT INTO "badkhashani" VALUES(21,25,'21:25','و پیش از تو هیچ پیغمبری را نفرستادیم مگر اینکه به او وحی می‌فرستادیم که هیچ معبود برحق غیر از من (الله) نیست، لذا تنها مرا عبادت کنید.');
INSERT INTO "badkhashani" VALUES(21,26,'21:26','و (مشرکان) گفتند: پروردگار رحمن (برای خود از فرشته‌ها) فرزند گرفته است! او پاک و منزه است. بلکه آنها (فرشته‌ها) بندگان والاقدرِ مقرب هستند.');
INSERT INTO "badkhashani" VALUES(21,27,'21:27','در سخن گفتن از او پیشی نمی‌کنند، و آنان به حکم وی کار می‌کنند.');
INSERT INTO "badkhashani" VALUES(21,28,'21:28','الله حال و اعمال گذشته و حال و اعمال آیندۀ ایشان را می‌داند، و شفاعت نمی‌کنند مگر برای کسی که (الله) رضایت داده باشد. و آنان از بیم او ترسان‌اند.');
INSERT INTO "badkhashani" VALUES(21,29,'21:29','و هر کس از آنان (فرشته‌ها) بگوید: غیر از الله من نیز معبود هستم، پس او را به دوزخ سزا دهیم، این چنین به ظالمان سزا می‌دهیم.');
INSERT INTO "badkhashani" VALUES(21,30,'21:30','آیا کافران ندیدند که آسمان‌ها و زمین (ابتدا باهم) چسپیده و متصل بودند، باز آن دو را از هم جدا کردیم. و هر چیز زنده را از آب آفریدیم، آیا (به وحدانیت من) ایمان نمی‌آورند؟!');
INSERT INTO "badkhashani" VALUES(21,31,'21:31','و در زمین کوه‌های استوار و ریشه‌دار قرار دادیم تا مبادا زمین آنان را بجنباند. و در میان کوه‌ها راه‌های گشاده ایجاد کردیم تا که آنان راه یابند.');
INSERT INTO "badkhashani" VALUES(21,32,'21:32','و آسمان را سقف محفوظ قرار دادیم، (ولی) آنان از نشانه‌هایش روی می‌گردانند.');
INSERT INTO "badkhashani" VALUES(21,33,'21:33','و او پروردگاری است که شب و روز و آفتاب و ماه را آفرید و هر یک در مدار (معین) خود شناوراند.');
INSERT INTO "badkhashani" VALUES(21,34,'21:34','و برای هیچ انسانی پیش از تو زندگی دائمی مقرر نکردیم، آیا اگر تو بمیری ایشان جاودانه می‌مانند؟');
INSERT INTO "badkhashani" VALUES(21,35,'21:35','و هر نفس (جاندار) چشندۀ مرگ است. و شما را بخاطر امتحان به سختی و آسایش آزمایش می‌کنیم، و به‌سوی ما باز گردانیده می‌شوید.');
INSERT INTO "badkhashani" VALUES(21,36,'21:36','و چون کافران تو را ببینند حتما تو را به مسخره می‌گیرند (و می‌گویند:) آیا این همان کسی است که معبودانتان را (به بدی) یاد می‌کند؟ در حالیکه آنان خود یاد پروردگار رحمن را انکار می‌کنند.');
INSERT INTO "badkhashani" VALUES(21,37,'21:37','انسان از شتاب آفریده شده است. به زودی نشانه‌های خود را به شما نشان خواهیم داد، پس (عذاب و معجزه‌ها را) از من به شتاب طلب نکنید.');
INSERT INTO "badkhashani" VALUES(21,38,'21:38','و می‌گویند: این وعده کی می‌رسد، اگر راست می‌گویید؟');
INSERT INTO "badkhashani" VALUES(21,39,'21:39','اگر کافران می‌دانستند هنگامی که نمی‌توانند آتش را از چهره‌هایشان و نه از پس‌هایشان باز دارند و آنان نصرت داده نمی‌شوند، (در آمدن عذاب عجله نمی‌کردند).');
INSERT INTO "badkhashani" VALUES(21,40,'21:40','بلکه (آتش) ناگهان به آنان می‌رسد و آنها را حیران و سراسیمه می‌سازد و نمی‌توانند آن را برگردانند. و به آنها مهلت داده نمی‌شود.');
INSERT INTO "badkhashani" VALUES(21,41,'21:41','و البته پیغمبرانی پیش از تو نیز مورد تمسخر قرار گرفتند، پس کسانی که آنان را مسخره می‌کردند، آنچه که آن را به استهزا می‌گرفتند، ایشان را احاطه کرد.');
INSERT INTO "badkhashani" VALUES(21,42,'21:42','بگو: چه کسی شما را در شب و روز (از عذاب) پروردگار رحمان نگاه می‌کند؟ بلکه آنان از یاد پروردگارشان روی گردان‌اند.');
INSERT INTO "badkhashani" VALUES(21,43,'21:43','یا برای آنان معبودانی غیر از ما است که ایشان را (از عذاب) حمایت کنند؟! (این معبودان ساختگی) نه می‌توانند خود را مدد کنند، و نه آنان مدد کرده می‌شوند.');
INSERT INTO "badkhashani" VALUES(21,44,'21:44','بلکه ما ایشان و پدرانشان را (از انواع نعمت‌ها و آسایش) بر خوردار ساختیم، تا عمر بر آنان طولانی شد. آیا نمی‌بینند که ما می‌آییم و زمین را از جوانب آن می‌کاهیم (مسلمانان را بر سرزمین‌های کفار مسلط می‌سازیم)، آیا باز هم آنان پیروزند.');
INSERT INTO "badkhashani" VALUES(21,45,'21:45','بگو: من شما را با وحی بیم میدهم، ولی چون کران بیم داده شوند، دعوت را نمی‌شنوند.');
INSERT INTO "badkhashani" VALUES(21,46,'21:46','و اگر کم‌ترین چیزی از عذاب پروردگارت به آنان برسد، حتما (اعتراف می‌کنند و) می‌گویند: ای وای بر ما! واقعا ما ظالم بودیم.');
INSERT INTO "badkhashani" VALUES(21,47,'21:47','و ما ترازوهای عدل را در روز قیامت می‌نهیم، پس بر هیچ کسی در چیزی ظلم کرده نشود. و اگر هم به وزن دانه خردل باشد آن را (برای حساب) می‌آوریم. و بس است که ما حسابرس و حساب‌گر باشیم.');
INSERT INTO "badkhashani" VALUES(21,48,'21:48','و البته به موسی و هارون فرقان (فرق کننده بین حق و باطل و توحید و شرک) دادیم که روشنی و پند برای پرهیزگاران است.');
INSERT INTO "badkhashani" VALUES(21,49,'21:49','همان کسانی که در خفا از پروردگار‌شان می‌ترسند و آنان از (هول) قیامت هراس دارند.');
INSERT INTO "badkhashani" VALUES(21,50,'21:50','و این (قرآن نیز) پند با برکت است که آن را نازل کردیم، آیا شما منکر آن هستید؟');
INSERT INTO "badkhashani" VALUES(21,51,'21:51','و البته پیش از این (دادن تورات به موسی و هارون) به ابراهیم هدایت و رشد او را داده بودیم و به (حال) او دانا بودیم.');
INSERT INTO "badkhashani" VALUES(21,52,'21:52','وقتی که (ابراهیم) به پدر و قوم خود گفت: این تمثال‌هایی که شما ملازم آنها شده‌اید چیستند؟');
INSERT INTO "badkhashani" VALUES(21,53,'21:53','گفتند: پدران خود را برای آنها عبادت کنان یافتیم.');
INSERT INTO "badkhashani" VALUES(21,54,'21:54','ابراهیم گفت: البته شما و پدران‌تان همه در گمراهی آشکار بودید.');
INSERT INTO "badkhashani" VALUES(21,55,'21:55','گفتند ای ابراهیم! آیا حق را برای ما آورده‌ای یا تو از بازی کنندگانی؟');
INSERT INTO "badkhashani" VALUES(21,56,'21:56','(ابراهیم) گفت: بلکه پروردگارتان، پروردگار آسمان‌ها و زمین است، همان پررودگاری که آنها را آفرید، و من بر این (سخن) از گواهانم.');
INSERT INTO "badkhashani" VALUES(21,57,'21:57','و قسم به الله پس از آنکه پشت گردانیدید و رفتید، البته در مورد بت‌هایتان تدبیری خواهم سنجید.');
INSERT INTO "badkhashani" VALUES(21,58,'21:58','پس آنها را قطعه قطعه کرد، مگر بت بزرگ‌شان را، تا به‌سوی آن باز گردند.');
INSERT INTO "badkhashani" VALUES(21,59,'21:59','گفتند: چه کسی با معبودان ما چنین کار را کرده است؟ يقينا او از ظالمان است.');
INSERT INTO "badkhashani" VALUES(21,60,'21:60','(بعضی) گفتند: شنیدیم جوانی از آنها (به بدی) یاد می‌کرد که به او ابراهیم گفته می‌شود.');
INSERT INTO "badkhashani" VALUES(21,61,'21:61','(سران کفر) گفتند: او را در پیش چشم مردم بیاورید شاید که آنان (بر او) گواهی دهند.');
INSERT INTO "badkhashani" VALUES(21,62,'21:62','(بعد از حاضر کردن ابراهیم در مجلس) گفتند: آیا تو این کار را با معبودان ما کرده‌‌ای، ای ابراهیم؟');
INSERT INTO "badkhashani" VALUES(21,63,'21:63','(ابراهیم) گفت: بلکه این بت بزرگ چنین کاری را کرده است، پس اگر سخن می‌گویند از آنان بپرسید.');
INSERT INTO "badkhashani" VALUES(21,64,'21:64','پس به خود رجوع کردند، و گفتند: واقعا شما ستمگرید (که معبودهای خود ساخته را عبادت می‌کنید).');
INSERT INTO "badkhashani" VALUES(21,65,'21:65','باز سرافگنده شدند (و گفتند:) البته تو می‌دانی که آنها سخن نمی‌گویند.');
INSERT INTO "badkhashani" VALUES(21,66,'21:66','(ابراهیم) گفت: آیا به غیر از الله چیزی را می‌پرستید که هیچ سود و زیانی به شما نمی‌رساند؟');
INSERT INTO "badkhashani" VALUES(21,67,'21:67','وای بر شما و بر آنچه که به جای الله می‌پرستید! آیا (در قباحت این کارتان) نمی‌اندیشید؟!');
INSERT INTO "badkhashani" VALUES(21,68,'21:68','(اهل مجلس) گفتند: او را بسوزانید و معبودان‌تان را مدد کنید، اگر می‌خواهید کاری بکنید.');
INSERT INTO "badkhashani" VALUES(21,69,'21:69','(چون ابراهیم را در آتش انداختند) گفتیم: ای آتش! بر ابراهیم سرد و سلامت باش.');
INSERT INTO "badkhashani" VALUES(21,70,'21:70','و خواستند (در حق او) نیرنگ ورزند، پس ما آنان را زیانکارترین مردم گردانیدیم.');
INSERT INTO "badkhashani" VALUES(21,71,'21:71','و او و لوط را نجات دادیم و به سرزمینی بردیم که در آن برای جهانیان برکت نهاده‌ایم.');
INSERT INTO "badkhashani" VALUES(21,72,'21:72','و به او اسحاق را بخشیدیم و (افزون بر خواسته‌هایش) يعقوب را نیز به او بخشیدیم. و همه را از صالحان قرار دادیم.');
INSERT INTO "badkhashani" VALUES(21,73,'21:73','و آنان را پیشوایان مردم قرار دادیم که به فرمان ما هدایت می‌کردند و انجام دادن نیکی‌ها و برپا داشتن نماز و پرداخت زکات را به آنان وحی کردیم. و آنان تنها ما را عبادت می‌کردند.');
INSERT INTO "badkhashani" VALUES(21,74,'21:74','و به لوط حکم (نبوت) و علم عطا کردیم. و او را از دیاری که (مردمانش) کارهای زشت می‌کردند، نجات دادیم، بی‌گمان آنان مردمان بد و فاسق بودند.');
INSERT INTO "badkhashani" VALUES(21,75,'21:75','و او را به رحمت خود داخل کردیم، چون او از صالحان بود.');
INSERT INTO "badkhashani" VALUES(21,76,'21:76','و نوح را یاد کن وقتی که پیش از این دعا کرد، پس ما هم دعای او را پذیرفتیم، پس او و خانواده‌اش را از اندوه بزرگ نجات دادیم.');
INSERT INTO "badkhashani" VALUES(21,77,'21:77','و او را بر قومی که آیات ما را دروغ پنداشتند، مدد کردیم. چون که آنان قوم بد بودند، پس همۀ ایشان را غرق کردیم.');
INSERT INTO "badkhashani" VALUES(21,78,'21:78','و داود و سلیمان را یاد کن وقتی که دربارۀ کشتزار فیصله کردند چون شب گوسفندان قومی در آن چریده بودند، و ما بر فیصله آنها شاهد (و ناظر) بودیم.');
INSERT INTO "badkhashani" VALUES(21,79,'21:79','پس آن (قضاوت درست) را به سليمان فهماندیم. و به هر یک آن دو حکمت و علم داده بودیم. و کوه‌ها را برای داود مسخر ساختیم که با او تسبیح می‌گفتند و پرندگان را (نیز مسخر کردیم) و ما کنندۀ این کار بودیم.');
INSERT INTO "badkhashani" VALUES(21,80,'21:80','و به داود ساختن زره را آموختیم تا شما را از (آسیب) جنگ‌تان حفظ کند. پس آیا شکر گزار هستيد؟');
INSERT INTO "badkhashani" VALUES(21,81,'21:81','و ما باد سریع را برای سلیمان مسخر ساختیم که به حکم او به‌سوی سرزمینی روان می‌شد که در آن برکت نهاده‌ایم و ما به هر چیز دانا بودیم.');
INSERT INTO "badkhashani" VALUES(21,82,'21:82','و مسخر ساختیم برای سلیمان از شیاطین گروهی را که برای او (در بحر) غواصی می‌کردند (برای او مروارید را از بحر بیرون می‌آوردند) و کارهای دیگر غیر از آن می‌کردند، و ما نگهبان آنها بودیم.');
INSERT INTO "badkhashani" VALUES(21,83,'21:83','و ایوب را یاد کن وقتی که پروردگار خود را ندا کرد (و گفت): همانا به من رنج و آسیب رسیده است و تو مهربان‌ترین مهربانان هستی.');
INSERT INTO "badkhashani" VALUES(21,84,'21:84','پس دعای او را پذیرفتیم و رنج و آسیب را که به او رسیده بود، دور ساختیم، و خانواده‌اش و (نیز) مانندشان را همراه با آنان به او دادیم تا (اینها) رحمتی از جانب ما و پندی برای عبادت کنندگان باشد.');
INSERT INTO "badkhashani" VALUES(21,85,'21:85','و نیز یاد کن اسماعیل و ادریس و ذالکفل را که هر یک از صابران بودند.');
INSERT INTO "badkhashani" VALUES(21,86,'21:86','و آنها را در رحمت خود داخل کردیم، چون ایشان از صالحان بودند.');
INSERT INTO "badkhashani" VALUES(21,87,'21:87','و ذالنون (صاحب ماهی - یونس) را یاد کن وقتی که خشمگینانه (از میان قومش) بیرون رفت و گمان کرد که بر او تنگ نمی‌گیریم. پس در تاریکی‌های شکم ماهی فریاد نمود که (ای الله) معبود (برحق) جز تو نیست، تو پاک و منزهى، واقعا که من از ظالمان بودم.');
INSERT INTO "badkhashani" VALUES(21,88,'21:88','پس دعایش را اجابت کردیم و او را از غم نجات دادیم و مؤمنان را (وقتی به ما رجوع کنند) این چنین نجات می‌دهیم.');
INSERT INTO "badkhashani" VALUES(21,89,'21:89','و زکریا را یاد کن چون پروردگارش را ندا کرد که ای پروردگار من! مرا تنها مگذار و تو بهترین وارثان هستی.');
INSERT INTO "badkhashani" VALUES(21,90,'21:90','پس دعایش را اجابت کردیم و یحیی را به او بخشیدیم و همسرش را برای او شایسته (حمل) گردانیدیم، البته آنان در (انجام) کارهای خیر شتاب می‌کردند و با امید و بیم ما را می‌خواندند و برای ما متواضع بودند.');
INSERT INTO "badkhashani" VALUES(21,91,'21:91','و (یاد کن) زنی را که پاکدامنی خود را حفظ کرد (مریم)، پس از روح (مخلوق) خویش در او دمیدیم و او و پسرش را نشانۀ (قدرت خود) برای جهانیان قرار دادیم.');
INSERT INTO "badkhashani" VALUES(21,92,'21:92','البته این است امت شما، که امت یگانه است. و من پروردگار شما هستم، پس تنها بندگی مرا کنید.');
INSERT INTO "badkhashani" VALUES(21,93,'21:93','و (اما امت‌ها دربارۀ انبیا) در میان خویش متفرق شدند. و هریک آنها به‌سوی ما باز می‌گردند.');
INSERT INTO "badkhashani" VALUES(21,94,'21:94','پس هر کس از کارهای شایسته انجام دهد در حالیکه ایمان داشته باشد، برای کوشش او ناسپاسی نخواهد شد و ما (نیکی‌هایش) را برای او می‌نویسیم.');
INSERT INTO "badkhashani" VALUES(21,95,'21:95','و بر (مردم) دیاری که آنها را هلاک کردیم، محال است که (به سوی ما) باز نگردند.');
INSERT INTO "badkhashani" VALUES(21,96,'21:96','تا اینکه (سدّ) يأجوج و مأجوج گشوده شود و ایشان شتابان از هر بلندیی بیایند.');
INSERT INTO "badkhashani" VALUES(21,97,'21:97','و وعدۀ حق و راست نزدیک گردد، پس یکباره چشمان کافران خیره شود (و بگویند)، ای وای بر ما! ما از این (روز) در غفلت بودیم، بلکه ما ظالم بودیم.');
INSERT INTO "badkhashani" VALUES(21,98,'21:98','همانا شما و چیزهایی که به غیر از الله می‌پرستید، هیزم جهنم هستید، شما وارد آن خواهید شد.');
INSERT INTO "badkhashani" VALUES(21,99,'21:99','اگر اینها معبودهای واقعی بودند هرگز وارد آن نمی‌شدند و همۀ آنها (عابدان و معبودها) در آنجا جاودانه‌اند.');
INSERT INTO "badkhashani" VALUES(21,100,'21:100','برای آنان در دوزخ نالۀ زار است و در آن جا چیزی را نمی‌شنوند.');
INSERT INTO "badkhashani" VALUES(21,101,'21:101','البته آنان که قبلا از جانب ما به آنها وعدۀ نیک داده شده است، این‌ها از دوزخ دور داشته خواهند شد.');
INSERT INTO "badkhashani" VALUES(21,102,'21:102','(حتى) آواز دوزخ را نمی‌شنوند و آنان در آنچه نفس‌هایشان خواهش داشته باشد، همیشه خواهند بود.');
INSERT INTO "badkhashani" VALUES(21,103,'21:103','ترس بزرگ آن‌ها را غمگین نمی‌سازد و فرشتگان به استقبال‌شان می‌آیند (و می‌گویند:) این همان روزی است که به شما وعده داده می‌شد.');
INSERT INTO "badkhashani" VALUES(21,104,'21:104','روزی که آسمان را مانند پیچیدن صفحه نوشته شده کتاب‌ها می‌پیچیم طوری که بار اول آفرینش را آغاز کردیم (بار دیگر) آن را باز می‌گردانیم. وعدۀ لازمی بر عهدۀ ماست، يقينا ما انجام دهندۀ (آن) هستیم.');
INSERT INTO "badkhashani" VALUES(21,105,'21:105','البته در زبور (کتاب داود) بعد از ذکر (تورات) نوشتیم که زمین را بندگان نیک من به ارث می‌برند.');
INSERT INTO "badkhashani" VALUES(21,106,'21:106','بی‌گمان در این (قرآن) ابلاغی (کافی) برای عبادت گذاران است.');
INSERT INTO "badkhashani" VALUES(21,107,'21:107','و تو را (ای پیغمبر) نفرستادیم مگر رحمتی برای جهانیان.');
INSERT INTO "badkhashani" VALUES(21,108,'21:108','بگو: جز این نیست که به من وحی می‌شود که معبود شما پروردگار یکتا و یگانه است، پس آیا شما مسلمان هستید؟');
INSERT INTO "badkhashani" VALUES(21,109,'21:109','پس اگر روی گردان شدند، پس بگو: به همۀ شما برابر اعلام کردم. و نمی‌دانم که آنچه به شما وعده داده می‌شود نزدیک است یا دور.');
INSERT INTO "badkhashani" VALUES(21,110,'21:110','همانا الله هم سخن آشکار را می‌داند و هم آنچه را پنهان می‌کنید.');
INSERT INTO "badkhashani" VALUES(21,111,'21:111','و نمی‌دانم؛ شاید (دوری وعده) برای شما آزمایش باشد (و تأخیر شدن آن) برخوردار شدن (از نعمت‌ها برای شما باشد) تا مدت مقرر.');
INSERT INTO "badkhashani" VALUES(21,112,'21:112','(پیغمبر) گفت: ای پروردگارم! خودت به حق (میان ما و ایشان) فیصله کن. و پروردگار مهربان ما (آن ذاتی است که از او) مدد خواسته می‌شود بر آنچه شما وصف می‌کنید.');
INSERT INTO "badkhashani" VALUES(22,1,'22:1','ای مردم! از پروردگارتان بترسید، چون زلزلۀ قيامت چیز بس بزرگ است.');
INSERT INTO "badkhashani" VALUES(22,2,'22:2','روزی که آن را ‌ببینید، هر مادرِ شیردهنده‌ای از طفلی که او را شیر می‌دهد، غافل می‌شود و هر (موجودِ) بارداری بار (جنين) خود را می‌اندازد. و مردم را مست می‌بینی حال آنکه مست نیستند، بلکه عذاب الله بسیار سخت است.');
INSERT INTO "badkhashani" VALUES(22,3,'22:3','و از مردم کسی هست که بدون هیچ علم و دانشی دربارۀ الله مجادله می‌کند و از هر شیطان سرکش پیروی می‌کند.');
INSERT INTO "badkhashani" VALUES(22,4,'22:4','(در ازل) بر او نوشته شده است که هر کس با شیطان دوستی کند، پس شیطان گمراهش می‌کند و او را به‌سوی عذاب آتش سوزان راهنمائی می‌کند.');
INSERT INTO "badkhashani" VALUES(22,5,'22:5','ای مردم! اگر از دوباره زنده شدن در شک هستید پس (بدانید که) ما شما را از خاک آفریده‌ایم، باز از نطفه، باز از خون بسته، باز از پارۀ گوشت شکل داده شده و شکل داده نشده آفریده‌ایم، (همۀ این مراحل) به خاطر این است که برای شما (كمال قدرت خود را) روشن سازیم، و آنچه را بخواهیم تا زمان معین در رحم‌ها قرار می‌دهیم. باز شما را به صورت کودک بیرون می‌آوریم، باز تا به حد رشد (جوانی)تان برسید. و بعضی از شما جانش گرفته می‌شود. و بعضی از شما به پست‌ترین مرحلۀ عمر و پیری باز گردانیده می‌شود تا پس از (آن همه) دانستن چیزی نداند. و زمین را خشک شده می‌بینی اما وقتی که بر آن باران ببارانیم، به حرکت می‌آید و نمو می‌کند و از هر نوع گیاهان زیبا و خرم می‌رویاند.');
INSERT INTO "badkhashani" VALUES(22,6,'22:6','این (قدرت نمایی‌ها) به خاطر آن است که (بدانید) الله ذات حق است. و این که اوست که مرده‌ها را زنده می‌کند و هم اوست که بر هر چیزی تواناست.');
INSERT INTO "badkhashani" VALUES(22,7,'22:7','و اینکه قیامت آمدنی است هیچ شکی در آن نیست. و اینکه تنها الله است که آنانی را که در قبرهاند، دوباره زنده می‌گرداند.');
INSERT INTO "badkhashani" VALUES(22,8,'22:8','و از مردم کسی هست که بدون هیچ علم و بدون هیچ هدایت و بدون هیچ کتاب روشنی دربارۀ الله مجادله می‌کند.');
INSERT INTO "badkhashani" VALUES(22,9,'22:9','متکبرانه صفحۀ گردن خود را می‌پیچاند تا (مردم را) از راه الله گمراه سازد، در دنیا برای او رسوایی بزرگ است و در روز قیامت عذاب سوزان به او می‌چشانیم.');
INSERT INTO "badkhashani" VALUES(22,10,'22:10','این (عذاب) به سزای آن چیزهایی است که در دست تو پیش فرستاده بود. و (گرنه) الله هرگز بر بندگان خود ستمگر نیست.');
INSERT INTO "badkhashani" VALUES(22,11,'22:11','و از میان مردم کسی هست که الله را با دو دلی می‌پرستد، پس اگر خیر به او برسد به آن مطمئن می‌شود، و اگر رنج و پریشانی به او برسد، بر روی خود بر می‌گردد (و مرتد می‌شود)، (چنین شخص) در دنیا و آخرت زیانکار شده، و این همان زیان آشکار است.');
INSERT INTO "badkhashani" VALUES(22,12,'22:12','به جای الله چیزی را می‌خواند که نه نفعی به او می‌رساند و نه سودی به او می‌بخشد. این همان گمراهی دور (از حق) است.');
INSERT INTO "badkhashani" VALUES(22,13,'22:13','کسی را می‌خواند که زیانش از سودش نزدیک‌تر است، چه مددگار بد و چه یاران بد هستند!.');
INSERT INTO "badkhashani" VALUES(22,14,'22:14','البته الله کسانی را که ایمان آورده و کارهای نیک انجام داده‌اند در باغ‌هایی داخل می‌کند که نهرها از زیر (درختان و قصرهای) آن روان است. يقينا الله هر چه را بخواهد، انجام می‌دهد.');
INSERT INTO "badkhashani" VALUES(22,15,'22:15','هر که گمان کند که الله او را در دنیا و آخرت نصرت نمی‌دهد، پس باید ریسمانی به آسمان بیاویزد، باز آن را قطع کند، پس ببیند که آیا این تدبیرش چیزی را که سبب خشم او شده از میان می‌برد؟');
INSERT INTO "badkhashani" VALUES(22,16,'22:16','و این چنین آن (قرآن) را (به صورت) آیات واضح نازل کردیم و الله هر کس را که بخواهد هدایت می‌کند.');
INSERT INTO "badkhashani" VALUES(22,17,'22:17','البته آنانی که ایمان آوردند و آنانی که یهودی شدند و صابئين (بی‌دینان) و نصارا و آتش پرستان و آنانی که به الله شریک آوردند، يقيناً الله روز قیامت میان‌شان فيصله خواهد کرد، چون الله بر هرچیز گواه است.');
INSERT INTO "badkhashani" VALUES(22,18,'22:18','آیا ندیدی کسانی که در آسمان‌ها و زمین هستند و آفتاب و ماه و ستاره‌ها و کوه‌ها و درختان و چهارپایان و بسیاری از مردم، برای الله سجده می‌کنند؟ و بسیاری هم هستند که عذاب بر آنها لازم شده است. و هر که را الله خوار کند، او هیچ عزت دهنده‌ای ندارد. بی‌گمان الله هر چه بخواهد انجام می‌دهد.');
INSERT INTO "badkhashani" VALUES(22,19,'22:19','این دو فریق که دشمن یکدیگرند، دربارۀ پروردگارشان باهم اختلاف کردند. پس آنان که کفر ورزیده‌اند بر ایشان لباس‌هایی از آتش بریده شده است، از بالای سرشان آب جوشان و سوزان ریخته می‌شود.');
INSERT INTO "badkhashani" VALUES(22,20,'22:20','آنچه در شکم‌های آنهاست و (نیز) پوست‌هایشان به آن (آب جوشان) گداخته و ذوب می‌گردد.');
INSERT INTO "badkhashani" VALUES(22,21,'22:21','و برای (عذاب دادن) آنان، گرزهای از آهن آماده است.');
INSERT INTO "badkhashani" VALUES(22,22,'22:22','هرگاه بخواهند از شدت غم از آن بیرون آیند، در آن باز گردانیده می‌شوند (و به آنان گفته می‌شود:) بچشید عذاب سوزان را.');
INSERT INTO "badkhashani" VALUES(22,23,'22:23','البته الله آنان را که ایمان آورده‌اند و کارهای نیک انجام داده‌اند در باغ‌هایی داخل می‌کند که از زیر (درختان و قصرهای) آن نهرها جاری است، در آنجا با دستبندهای از طلا و مروارید آراسته می‌شوند، و لباس‌شان در آنجا ابریشم است.');
INSERT INTO "badkhashani" VALUES(22,24,'22:24','و (مؤمنان) به سخن پاکیزه راهنمائی کرده شدند و به راه ستوده راهنمائی کرده شدند.');
INSERT INTO "badkhashani" VALUES(22,25,'22:25','بی‌گمان آنان که کفر ورزیدند و (مردم را) از راه الله و از مسجد الحرام باز می‌دارند که ما آن را برای همۀ مردم اعم از مقیم در آنجا و بادیه نشین (آینده) یک برابر قرار داده‌ایم (برای آنان عذاب سخت و دردناک است)، و هر کس به قصد ظلم در آنجا مرتكب الحاد (شرک و قتل) گردد، او را از عذاب دردناک می‌چشانیم.');
INSERT INTO "badkhashani" VALUES(22,26,'22:26','و به یادآور وقتی که جایگاه خانه (کعبه) را برای ابراهیم معین ساختیم (و به او گفتیم:) که چیزی را با من شریک مکن، و خانه‌ام را برای طواف کنندگان و قیام کنندگان (در آن برای نماز) و رکوع کنندگان و سجده کنندگان پاک‌دار.');
INSERT INTO "badkhashani" VALUES(22,27,'22:27','و (به ابراهیم گفتیم که) در میان مردم برای حج اعلان کن، تا پیاده و (سوار) بر هر شتر لاغری که از هر راه دور می‌آیند، به‌سوی تو بیایند.');
INSERT INTO "badkhashani" VALUES(22,28,'22:28','(اعلان کن) تا شاهد منافع خویش باشند و در روزهای معلوم نام الله را بر ذبح چهار پایانی یاد کنند که الله روزیشان کرده است. پس از آنها بخورید و بینوایان و تنگدستان را نیز بخورانید.');
INSERT INTO "badkhashani" VALUES(22,29,'22:29','باز باید آلودگی‌های بدنشان را پاک کنند و باید به نذرهایشان وفا کنند و باید خانۀ قدیمی و گرامی را طواف نمایند.');
INSERT INTO "badkhashani" VALUES(22,30,'22:30','این است حكم الله، و هر کس مقدسات و شعائر الله را بزرگ و محترم بشمارد، پس آن برایش در نزد پروردگارش بهتر است، و چهار پایان برایتان حلال کرده شد مگر آنچه بر شما خوانده می‌شود، پس از پلیدی‌های که از بت‌ها ببار می‌آید، دوری کنید و از سخن دروغ اجتناب ورزید.');
INSERT INTO "badkhashani" VALUES(22,31,'22:31','در حالی که به‌سوی الله خالصانه گرویده باشید و از ادیان باطل بیزاری جویید و هیچ شریکی برای الله قرار ندهید. و هر کس به الله شرک ورزد، گویی از آسمان افتیده است و پرنده‌ها او را ربوده‌اند، یا اینکه باد او را به جای دور افگنده است.');
INSERT INTO "badkhashani" VALUES(22,32,'22:32','چنین است (سزای شرک)، و هر کس شعایر الله را بزرگ شمارد، پس در حقیقت این بزرگ‌داشت و تعظیم از تقوای دل‌هاست.');
INSERT INTO "badkhashani" VALUES(22,33,'22:33','برای شما در آن (قربانی) تا مدت معین فایده‌هاست، باز محل قربانی کردن آنها در جوار خانۀ قدیمی و گرامی (کعبه) است.');
INSERT INTO "badkhashani" VALUES(22,34,'22:34','و برای هر امتی جای قربانی کردن را مقرر نموده‌ایم، تا با نام الله چهارپایانی را ذبح کنند که الله به ایشان عطا نموده است. پس معبودتان معبود یگانه است، پس برای او منقاد شوید و تواضع کنندگان را مژده ده.');
INSERT INTO "badkhashani" VALUES(22,35,'22:35','(آنان) کسانی (هستند) که چون نام الله ياد شود دل‌هایشان ترسان گردد، و در برابر مصیبت‌هایی که به آنان می‌رسد صبر می‌کنند، و (نیز کسانی را مژده بده که) نماز را برپا می‌کنند و از آنچه روزی‌شان داده‌ایم انفاق می‌کنند.');
INSERT INTO "badkhashani" VALUES(22,36,'22:36','و (قربانی کردن) شتران (چاق و فربه) را برایتان از شعائر الله قرار دادیم، در آنها برای شما خير (منافع دینی و دنیوی) است، پس در حالی که بر پای ایستاده‌اند نام الله را بر آنها یاد کنید، پس چون پهلویشان بر زمین افتد از گوشت آنها بخورید، و به بینوایان سائل و غیر سائل بخورانید، اینگونه آنها را برایتان رام و مطيع کرده‌ایم تا که شکر کنید.');
INSERT INTO "badkhashani" VALUES(22,37,'22:37','هرگز گوشت‌ها و خون‌های قربانی‌ها به الله نمی‌رسد، بلکه تقوای شما به او می‌رسد، این چنین آنها را برایتان مسخر نمود تا الله را به خاطر آنکه شما را هدایت کرده است به بزرگی یاد کنید، و نیکوکاران احسان کننده را مژده بده.');
INSERT INTO "badkhashani" VALUES(22,38,'22:38','يقينا الله از مؤمنان دفاع می‌کند، چرا که الله هیچ خیانت‌کار ناشکر را دوست ندارد.');
INSERT INTO "badkhashani" VALUES(22,39,'22:39','به کسانی که کفار با آنان می‌جنگند، اجازۀ (جهاد) داده شد، به سبب این که مورد ظلم قرار گرفته‌اند. و البته الله بر نصرت دادن‌شان تواناست.');
INSERT INTO "badkhashani" VALUES(22,40,'22:40','همان کسانی که از سرزمین‌شان به ناحق بیرون رانده شدند، جز به سبب آن که می‌گفتند: پروردگار ما الله است. اگر الله بعضی از مردم را به وسیلۀ بعضی (دیگر) دفع نمی‌کرد، البته صومعه‌های (راهبان) و کلیساهای (نصارا) و عبادتگاه‌های (يهود) و مسجدهایی که نام الله در آنها بسیار یاد می‌شود، ویران می‌شد، و الله کسی را که (دین) او را یاری کند، یقینا یاری می‌کند. همانا الله قوی (و) غالب است.');
INSERT INTO "badkhashani" VALUES(22,41,'22:41','آن کسانی را که اگر در زمین آنها را قدرت (و حکمرانی) ببخشیم، نماز را برپا می‌دارند و زکات (اموال) را می‌دهند و به کار نیک فرمان می‌دهند و از کار زشت منع می‌کنند، و سر انجام کارها به الله باز می‌گردد.');
INSERT INTO "badkhashani" VALUES(22,42,'22:42','و اگر تو را تکذیب کنند، البته پیش از آنان قوم نوح و عاد و ثمود (نیز پیغمبران‌شان) را تکذیب نموده بودند.');
INSERT INTO "badkhashani" VALUES(22,43,'22:43','و (همچنین) قوم ابراهیم و قوم لوط');
INSERT INTO "badkhashani" VALUES(22,44,'22:44','و اصحاب مدین (قوم شعیب، نیز پیغمبر خود را تکذیب نمودند) و موسی (نیز) تکذیب شد، پس به کافران مهلت دادم، باز ایشان را گرفتار نمودیم. پس (بنگر) عقوبت من چگونه است؟');
INSERT INTO "badkhashani" VALUES(22,45,'22:45','پس چه بسیار قریه‌ها را که اهلش ظالم بودند، نابود کردیم. و (اینک) آن قریه‌ها بر سقف‌های خود افتاده است و چه بسیار چاه‌های معطل و متروک و کاخ‌های بلند و استواری که بی‌صاحب و متروک مانده است.');
INSERT INTO "badkhashani" VALUES(22,46,'22:46','آیا در زمین سیر و سفر نکرده‌اند تا دل‌هایی داشته باشند که با آن بفهمند و گوش‌هایی داشته باشند که با آن بشنوند، در حقیقت چشم‌ها کور نمی‌گردند، بلکه دل‌هایی که در سینه‌هاست کور می‌شوند.');
INSERT INTO "badkhashani" VALUES(22,47,'22:47','و با شتاب عذاب را از تو می‌طلبند. و با آن که الله هرگز وعده‌اش را خلاف نمی‌کند و در حقیقت یک روز نزد پروردگارت، مانند هزار سالی است که شما می‌شمارید.');
INSERT INTO "badkhashani" VALUES(22,48,'22:48','و چه بسیار قریه‌ها (و شهرها) که به (اهالی) آنها در حالیکه ظالم بودند مهلت دادم (اما اصلاح نشدند). سپس آنان را (در موقع مناسب) به عذاب گرفتار کردم، و بازگشت (همه) به‌سوی من است.');
INSERT INTO "badkhashani" VALUES(22,49,'22:49','بگو: ای مردم! جز این نیست که من برای شما تنها بیم دهندۀ آشکار هستم.');
INSERT INTO "badkhashani" VALUES(22,50,'22:50','پس آنان که ایمان آوردند و کارهای نیک کردند، برای آنها آمرزش است، و در جنت از روزی نیک برخوردار‌اند.');
INSERT INTO "badkhashani" VALUES(22,51,'22:51','و اما آنان که در (تكذيب) آیات ما سعی کردند، به قصد عاجز نمودن (پیغمبر) ما، آنان اهل دوزخ‌اند.');
INSERT INTO "badkhashani" VALUES(22,52,'22:52','و پیش از تو هیچ رسول و هیچ نبیی را نفرستادیم مگر اینکه چون آرزویی به خاطر می‌آورد شیطان چیزی را در آرزوی او القا می‌نمود، پس الله آنچه را که شیطان القا می‌کرد از بین می‌برد، باز الله آیاتش را محکم و استوار می‌کند، و الله دانای با حکمت است.');
INSERT INTO "badkhashani" VALUES(22,53,'22:53','تا الله آنچه را که شیطان القا می‌کند، برای کسانی که در دل‌هایشان مرض است، سبب فتنه بگرداند و (نیز) برای آنانی که دل‌هایشان سخت است. و يقينا ظالمان در مخالفت دور از حق‌اند.');
INSERT INTO "badkhashani" VALUES(22,54,'22:54','و تا آنان که به ایشان علم داده شده است بدانند که آن (وحی) از سوی پروردگارت حق است، و به آن ایمان آورند، تا دل‌هایشان به آن آرام و منقاد گردد، يقينا الله مؤمنان را به راه راست راهبر است.');
INSERT INTO "badkhashani" VALUES(22,55,'22:55','و کافران همیشه دربارۀ آن تردید دارند، تا اینکه ناگهان قیامت به آنان برسد، یا عذاب روز نامیمون و عقیم به سراغ‌شان بیاید.');
INSERT INTO "badkhashani" VALUES(22,56,'22:56','در آن روز ملک و سلطنت فقط از الله است، میان آنان فيصله می‌کند، پس کسانی که ایمان آورده و کارهای نیک انجام داده‌اند، در باغ‌های پرناز و نعمت خواهند بود.');
INSERT INTO "badkhashani" VALUES(22,57,'22:57','اما آنان که کفر ورزیدند و آیات ما را دروغ شمردند، پس برای ایشان عذاب اهانت کننده است.');
INSERT INTO "badkhashani" VALUES(22,58,'22:58','و کسانی که در راه الله هجرت کرده‌اند باز کشته شده یا مرده‌اند، بدون شک الله به آنان روزی نیک می‌دهد و به يقين الله بهترین روزی دهندگان است.');
INSERT INTO "badkhashani" VALUES(22,59,'22:59','الله حتما آنان را به جایگاهی که آن را می‌پسندند وارد خواهد کرد، و یقینا الله دانا (و) بردبار است.');
INSERT INTO "badkhashani" VALUES(22,60,'22:60','(حکم الهی) این است؛ و هر کس (ظالم را) به مانند آنچه خودش مورد ظلم (او) قرار گرفته بود عقوبت دهد، (و اگر) باز مورد ظلم قرار گیرد، حتما الله او را نصرت خواهد داد، چرا که الله بسیار بخشاینده (و) آمرزگار است.');
INSERT INTO "badkhashani" VALUES(22,61,'22:61','این (نصرت دادن مظلوم) به آن سبب است که الله (قادر به همه کار است که) شب را در روز و روز را در شب داخل می‌گرداند، و (نیز به آن سبب است) که الله شنوای بیناست.');
INSERT INTO "badkhashani" VALUES(22,62,'22:62','این (نصرت دادن مظلوم) به آن سبب است که الله همان معبود بر حق است و آنچه را که به جز او می‌پرستند همه باطل است، و (نیز به آن سبب است که) همان الله بلندمرتبه (و) بزرگ است.');
INSERT INTO "badkhashani" VALUES(22,63,'22:63','آیا ندیدی که الله از آسمان آب فرو آورد، پس زمین به سبب آن سرسبز و خرم می‌گردد، بی‌شک الله باریک بینِ آگاه است.');
INSERT INTO "badkhashani" VALUES(22,64,'22:64','آنچه در آسمان‌ها و آنچه در زمین است از اوست، و الله بی‌نیاز (و) ستوده است.');
INSERT INTO "badkhashani" VALUES(22,65,'22:65','آیا ندیدی که الله آنچه را در زمین است و کشتی‌ها را که به حکم او در بحر روان‌اند، برایتان مسخر کرده است. و (الله) آسمان را نگاه می‌دارد از آنکه بر زمین بیفتد، مگر به اذن خودش. زیرا الله نسبت به مردم مشفق (و) مهربان است.');
INSERT INTO "badkhashani" VALUES(22,66,'22:66','و او همان ذاتی است که به شما زندگی بخشید، باز شما را می‌میراند، باز شما را زنده می‌کند. واقعا انسان (در برابر پروردگارش) بسیار ناسپاس است.');
INSERT INTO "badkhashani" VALUES(22,67,'22:67','برای هر امتی شریعت خاص مقرر کرده‌ایم تا آنان به آن عمل کنند، پس نباید دربارۀ این امر با تو نزاع کنند. و به‌سوی پروردگارت دعوت کن، همانا تو بر راه راست قرار داری.');
INSERT INTO "badkhashani" VALUES(22,68,'22:68','و اگر با تو مجادله کردند، پس بگو: الله به آنچه می‌کنید داناتر است.');
INSERT INTO "badkhashani" VALUES(22,69,'22:69','الله میان شما در روز قیامت دربارۀ آنچه بایکدیگر در آن اختلاف می‌کردید، حکم و فیصله خواهد کرد.');
INSERT INTO "badkhashani" VALUES(22,70,'22:70','آیا ندانسته‌ای که الله (همۀ) آنچه را که در آسمان و زمین است می‌داند، بدون شک تمام اینها در کتابی ثبت و ضبط است. و البته این کار برای الله آسان است.');
INSERT INTO "badkhashani" VALUES(22,71,'22:71','(و مشرکان) غیر از الله چیزی را می‌پرستند که الله دلیلی بر (معبود بودن) آن نازل نکرده است، و چیزی را می‌پرستند که به آن علمی ندارند، و برای ظالمان هیچ یاری دهنده‌ای نیست.');
INSERT INTO "badkhashani" VALUES(22,72,'22:72','و چون آیات واضح ما بر آنان خوانده شود در چهرۀ کافران انکار و ناخوشی را می‌بینی، بلکه نزدیک است به کسانی حمله ور شوند که آیات ما را بر آنان می‌خوانند. بگو: آیا شما را به بدتر از این خبر دهم، به (آن) دوزخ که الله آن را به کافران وعده داده است. و چه بد سرانجامی است.');
INSERT INTO "badkhashani" VALUES(22,73,'22:73','ای مردم! مثلی زده شد، پس به آن گوش فرا دهید، کسانی را که غیر از الله به کمک می‌خوانید (و پرستش می‌کنید) هرگز نمی‌توانند مگسی بیافرینند، هر چند همه (برای آفرینش آن) جمع شوند، و اگر آن مگس چیزی را از آنان برباید، نمی‌توانند آن را از او دوباره بگیرند، طالب و مطلوب هردو ناتوانند.');
INSERT INTO "badkhashani" VALUES(22,74,'22:74','الله را آنچنان که سزاوار قدر اوست، قدر نگذاشتند، در حالیکه الله توانای غالب است.');
INSERT INTO "badkhashani" VALUES(22,75,'22:75','الله از میان فرشته‌ها رسولانی انتخاب می‌کند، و از میان مردم نیز، چون الله شنوای بیناست.');
INSERT INTO "badkhashani" VALUES(22,76,'22:76','آنچه پیش روی‌شان است و آنچه پشت سرشان است، می‌داند. و همه کارها به‌سوی الله باز گردانیده می‌شود.');
INSERT INTO "badkhashani" VALUES(22,77,'22:77','ای کسانی که ایمان آورده‌اید! رکوع و سجده کنید و پروردگارتان را عبادت کنید، و کار خیر انجام دهید تا رستگار شوید.');
INSERT INTO "badkhashani" VALUES(22,78,'22:78','و در راه الله جهاد کنید، چنان که شایستۀ جهاد در راه اوست. اوست که شما را برگزید. و در دین هیچ کار دشوار و سنگین را بر شما نگذاشت. آیین پدرتان ابراهیم (نیز چنین بوده است)، او شما را پیش از این مسلمان نام نهاده است، تا پیغمبر بر شما گواه باشد و (شما نیز) بر مردم گواه باشید، پس نماز را بر پا کنید و زکات را بدهید، و به دین الله تمسک جویید که او مددگار شماست. پس چه نیک مولایی، و چه نیک مددگاری!');
INSERT INTO "badkhashani" VALUES(23,1,'23:1','به راستی که مؤمنان کامیاب و رستگار شدند.');
INSERT INTO "badkhashani" VALUES(23,2,'23:2','آنانی که ایشان در نماز خود متواضع و ترسناک‌اند.');
INSERT INTO "badkhashani" VALUES(23,3,'23:3','و آنانی که ایشان از (کار و سخن) بیهوده روی گردان‌اند.');
INSERT INTO "badkhashani" VALUES(23,4,'23:4','و آنانی که ایشان زکات را می‌دهند.');
INSERT INTO "badkhashani" VALUES(23,5,'23:5','و آنانی که ایشان شرمگاه‌های خود را (از حرام) نگاه می‌دارند.');
INSERT INTO "badkhashani" VALUES(23,6,'23:6','مگر بر همسران یا کنیزان خود، که در این صورت سزاوار ملامتی و (مجازات) نیستند.');
INSERT INTO "badkhashani" VALUES(23,7,'23:7','پس هر کس غیر از این را بجوید، پس ایشان همان تجاوز کنندگان‌اند.');
INSERT INTO "badkhashani" VALUES(23,8,'23:8','و آنانی که ایشان امانت‌های خود را و عهد خود را رعایت می‌کنند.');
INSERT INTO "badkhashani" VALUES(23,9,'23:9','و آنانی که ایشان بر نماز‌های خود محافظت می‌کنند.');
INSERT INTO "badkhashani" VALUES(23,10,'23:10','این گروه ایشان وارثان‌اند.');
INSERT INTO "badkhashani" VALUES(23,11,'23:11','آنانی که فردوس را به ارث می‌برند، آنها در آن همیشه‌اند.');
INSERT INTO "badkhashani" VALUES(23,12,'23:12','و البته انسان (آدم) را از خلاصۀ از گِل آفریدیم.');
INSERT INTO "badkhashani" VALUES(23,13,'23:13','باز او را (آدم را به اعتبار نسل وی) نطفۀ در قرارگاه استوار و محفوظ (رحم) قرار دادیم.');
INSERT INTO "badkhashani" VALUES(23,14,'23:14','باز آن نطفه را به خون بسته تبدیل کردیم، پس آن خون بسته را به صورت پارۀ گوشت در آوردیم، پس آن پارۀ گوشت را استخوان‌ها ساختیم، پس از آن استخوان‌ها را با گوشت پوشانیدیم، باز او را به آفرینش دیگر (انسان زنده) آفریدیم، پس بسیار بزرگ است الله، بهترین آفرینندگان.');
INSERT INTO "badkhashani" VALUES(23,15,'23:15','باز شما بعد از این مراحل حتما می‌میرید.');
INSERT INTO "badkhashani" VALUES(23,16,'23:16','باز شما روز قیامت برانگیخته خواهید شد.');
INSERT INTO "badkhashani" VALUES(23,17,'23:17','و البته بالای شما هفت راه (آسمان) آفریدیم، و ما هرگز از آفرینش غافل نبوده‌ایم.');
INSERT INTO "badkhashani" VALUES(23,18,'23:18','و از آسمان آب را به اندازه فرود آوردیم و آن را در زمین جای دادیم. و همانا ما بر بردن آن تواناییم.');
INSERT INTO "badkhashani" VALUES(23,19,'23:19','پس ما به وسیلۀ آن آب باغ‌هایی از درختان خرما و انگور را برای شما ایجاد کردیم که برای شما در آنها میوه‌های بسیار است، و از آنها می‌خورید.');
INSERT INTO "badkhashani" VALUES(23,20,'23:20','و (همچنین با آن آب) درختی آفریدیم که در کوه طور سینا می‌روید (درخت زیتون و) روغن و طعام برای خورندگان است.');
INSERT INTO "badkhashani" VALUES(23,21,'23:21','و البته برای شما در چهارپایان عبرت است که شما را از آنچه در شکم آنهاست می‌نوشانیم، و برای شما در آن منافع فراوان است و از آن می‌خورید.');
INSERT INTO "badkhashani" VALUES(23,22,'23:22','و بر آنها و بر کشتی‌ها حمل می‌شوید.');
INSERT INTO "badkhashani" VALUES(23,23,'23:23','و البته نوح را به‌سوی قومش فرستادیم پس گفت: ای قوم من! الله را بپرستید، شما هیچ معبود برحق غیر از الله ندارید. آیا (از عذاب الله و زوال نعمت او) نمی‌ترسید؟');
INSERT INTO "badkhashani" VALUES(23,24,'23:24','پس اشراف و سرانی از قومش که کافر بودند، گفتند: این مرد مگر انسانی همچون شما نیست، می‌خواهد بر شما برتری یابد، اگر الله می‌خواست حتما فرشته‌ها را می‌فرستاد، ما چنین سخنی را از پدران پیشین خود نشنیده‌ایم.');
INSERT INTO "badkhashani" VALUES(23,25,'23:25','(و نیز گفتند) او نیست مگر مردی که در او اثر جنون است، پس تا مدتی دربارۀ او انتظار بکشید.');
INSERT INTO "badkhashani" VALUES(23,26,'23:26','(نوح) گفت: ای پروردگارم! مرا نصرت ده در قبال آنکه مرا تکذیب کردند.');
INSERT INTO "badkhashani" VALUES(23,27,'23:27','پس به او وحی فرستادیم که پیش چشم ما و وحی ما کشتی را بساز، پس وقتی فرمان ما رسید و تنور به فوران آمد، پس در آن از هر نوع حیوان، جفتی (نر و ماده) را سوار کن و خانواده ات را نیز سوار کن، مگر کسی از آنان که وعدۀ (حق) از پیش بر او مقرر شده است، و دربارۀ کسانی که ظلم کرده‌اند با من سخن مگو، زیرا آنان غرق شدنی‌اند.');
INSERT INTO "badkhashani" VALUES(23,28,'23:28','پس چون تو و همراهانت بر کشتی قرار گرفتید (سوار شدید) پس بگو: همه ستایش‌ها مخصوص الله است که ما را از قوم ظالم نجات داد.');
INSERT INTO "badkhashani" VALUES(23,29,'23:29','و (در وقت فرود آمدن) بگو: ای پروردگارم! مرا در جایی پر خیر و برکت فرود آور، و تو بهترین فرود آورندگانی.');
INSERT INTO "badkhashani" VALUES(23,30,'23:30','يقيناً در این (قصۀ نوح) البته نشانه‌هایی است، و بدون شک (ما بندگان خود را) آزمایش کننده بودیم.');
INSERT INTO "badkhashani" VALUES(23,31,'23:31','باز بعد از آنان قرنی دیگر را آفریدیم.');
INSERT INTO "badkhashani" VALUES(23,32,'23:32','پس در میان‌شان پیغمبری از خودشان فرستادیم که (بگوید) تنها الله را بپرستید، زیرا جز او معبود به حقی ندارید. پس آیا (از شرک) پرهیز نمی‌کنید؟');
INSERT INTO "badkhashani" VALUES(23,33,'23:33','و اشراف قومش که کافر شده بودند و ملاقات روز آخرت را تکذیب نموده بودند و در زندگانی دنیا به آنان آسودگی داده بودیم، گفتند: این شخص جز انسانی مانند شما نیست، از آنچه شما می‌خورید، می‌خورد. و از آنچه شما می‌نوشید، می‌نوشد.');
INSERT INTO "badkhashani" VALUES(23,34,'23:34','و (به پیروان خود گفتند:) اگر از انسانی مانند خود اطاعت کنید در آن صورت حتما زیانکار خواهید بود.');
INSERT INTO "badkhashani" VALUES(23,35,'23:35','آیا به شما وعده می‌دهد که چون مردید و خاک و استخوان شدید باز شما (بار دیگر زنده می‌گردید و از قبرها) بیرون آورده خواهید شد؟');
INSERT INTO "badkhashani" VALUES(23,36,'23:36','آنچه وعده داده می‌شوید، دور اندر دور است.');
INSERT INTO "badkhashani" VALUES(23,37,'23:37','جز زندگانی این جهان ما (هیچ زندگی و حیاتی) وجود ندارد (گروهی از ما) زنده می‌شویم، و ما برانگیخته نخواهیم شد.');
INSERT INTO "badkhashani" VALUES(23,38,'23:38','و او جز مردی نیست که بر الله دروغ بسته است و ما تصدیق کننده او نیستیم.');
INSERT INTO "badkhashani" VALUES(23,39,'23:39','گفت: ای پروردگارم! به سبب اینکه مرا دروغگوی شمردند، نصرتم ده.');
INSERT INTO "badkhashani" VALUES(23,40,'23:40','الله فرمود: بعد از مدت اندک (از کفر و عنادشان) سخت پشیمان خواهند شد.');
INSERT INTO "badkhashani" VALUES(23,41,'23:41','پس ناگهان صدای بلند (و مرگبار) نظر به وعدۀ راست، ایشان را فرا گرفت، پس آنان را (مانند) خس و خاشاک (که سیل بر روی آب حمل می‌کند) گردانیدیم، پس دوری و هلاکت باد بر قوم ظالم.');
INSERT INTO "badkhashani" VALUES(23,42,'23:42','باز بعد از ایشان نسل‌های (امت‌های) دیگر را آفریدیم.');
INSERT INTO "badkhashani" VALUES(23,43,'23:43','هیچ امتی نه از اجل مقرر خود سبقت می‌کند و نه پس می‌ماند.');
INSERT INTO "badkhashani" VALUES(23,44,'23:44','باز پیغمبران خود را یکی پس از دیگری فرستادیم، هرگاه پیغمبری برای (هدایت) امتی آمد، او را تکذیب کردند، ما هم یکی را (در هلاکت) تابع دیگری گردانیدیم و (به دیار نیستی فرستادیم و داستان عذاب) آنان را (برای مردم) افسانه ساختیم. پس دوری و هلاکت باد بر قومی که ایمان نمی‌آورند.');
INSERT INTO "badkhashani" VALUES(23,45,'23:45','باز موسی و برادرش هارون را با معجزه‌ها و دلایل روشن خود فرستادیم.');
INSERT INTO "badkhashani" VALUES(23,46,'23:46','به‌سوی فرعون و سران قوم او، ولی آنان تكبر ورزیدند و قوم سرکش و متکبر بودند.');
INSERT INTO "badkhashani" VALUES(23,47,'23:47','پس گفتند: آیا به دو انسانی که مانند خود ما هستند ایمان آوریم، حال آنکه قوم آنها بندگان (خدمت‌گذاران) ما هستند؟');
INSERT INTO "badkhashani" VALUES(23,48,'23:48','در نتیجه، آن دو (موسی و هارون) را تکذیب کردند، پس از جملۀ هلاک شدگان گشتند.');
INSERT INTO "badkhashani" VALUES(23,49,'23:49','و يقينا به موسی کتاب (تورات) دادیم تا راهیاب گردند.');
INSERT INTO "badkhashani" VALUES(23,50,'23:50','و پسر مریم و مادرش را آیتی گردانیدیم. و آن دو را در زمین بلند جای دادیم که برخوردار از آب روان و محل آرامی بود.');
INSERT INTO "badkhashani" VALUES(23,51,'23:51','ای پیغمبران! از چیزهای پاکیزه و حلال بخورید و کارهای نیک کنید، يقينا من به آنچه انجام می‌دهید، دانا هستم.');
INSERT INTO "badkhashani" VALUES(23,52,'23:52','در حقیقت این امت شما، امت یگانه است و من پروردگار شما هستم، پس تنها از من بترسید.');
INSERT INTO "badkhashani" VALUES(23,53,'23:53','(اما مردم) کار دین خود را در میان خود پراگنده ساختند. و هر گروه به آنچه نزد آنهاست (در حالیکه آن را از خود وضع کرده‌اند) شادمان هستند.');
INSERT INTO "badkhashani" VALUES(23,54,'23:54','پس آنان را تا مدتی در جهل و گمراهی که در آن غرقند، بگذار.');
INSERT INTO "badkhashani" VALUES(23,55,'23:55','آیا می‌پندارند که آنچه از مال و فرزندان به آنان مدد می‌کنیم،');
INSERT INTO "badkhashani" VALUES(23,56,'23:56','(به خاطر آن است که) ما به نفع‌شان شتاب می‌کنیم؟! نه، بلکه نمی‌فهمند (که ما ایشان را به تدریج می‌گیریم).');
INSERT INTO "badkhashani" VALUES(23,57,'23:57','بی‌گمان آنان که ایشان از ترس پروردگارشان هراسان‌اند.');
INSERT INTO "badkhashani" VALUES(23,58,'23:58','و آنان که ایشان به آیات پروردگار خویش ایمان می‌آورند.');
INSERT INTO "badkhashani" VALUES(23,59,'23:59','و آنان که ایشان به پروردگار خویش شریک مقرر نمی‌کنند.');
INSERT INTO "badkhashani" VALUES(23,60,'23:60','و آنان که ایشان آنچه را باید (در راه الله) بدهند، می‌دهند. در حالیکه دل‌هایشان ترسان است از اینکه به‌سوی پروردگارشان باز می‌گردند.');
INSERT INTO "badkhashani" VALUES(23,61,'23:61','آنان‌اند که برای کارهای نیک می‌شتابند و آنان در انجام آن پیشتاز‌اند.');
INSERT INTO "badkhashani" VALUES(23,62,'23:62','و هیچ کس را مکلف نمی‌سازیم مگر به قدر توانش. و نزد ما کتابی است که به حق سخن می‌گوید و به آنان هیچ ظلم کرده نمی‌شود.');
INSERT INTO "badkhashani" VALUES(23,63,'23:63','بلکه دل‌هایشان از این (قرآن) در غفلت غرق است و آنان غیر از این (کار زشت) کارهای زشت دیگری دارند که انجام دهندۀ آنند.');
INSERT INTO "badkhashani" VALUES(23,64,'23:64','(آنها در غفلت‌اند) تا آن که خوشگذرانان ایشان را به عذاب گرفتار می‌کنیم، پس ناگهان شور و فریاد می‌کنند.');
INSERT INTO "badkhashani" VALUES(23,65,'23:65','(به آنها گفته می‌شود) امروز شور و فریاد نکنید، چون شما از جانب ما قطعا یاری و مدد نمی‌شود.');
INSERT INTO "badkhashani" VALUES(23,66,'23:66','(زیرا) آيات من بر شما خوانده می‌شد، ولی شما بر عقب تان باز می‌گشتید.');
INSERT INTO "badkhashani" VALUES(23,67,'23:67','در حالیکه بر مردم (به سبب متولی بودن خانۀ کعبه) تکبر می‌ورزیدید، شب‌ها (در جلسات خود) در بارۀ قرآن بدگویی می‌کردید.');
INSERT INTO "badkhashani" VALUES(23,68,'23:68','آیا در این سخن تدبر نکردند، یا چیزی برایشان آمده که برای پدران پیشین آنها نیامده بود؟');
INSERT INTO "badkhashani" VALUES(23,69,'23:69','و یا اینکه پیغمبر خود را نشناختند، پس به این سبب او را انکار می‌کنند؟');
INSERT INTO "badkhashani" VALUES(23,70,'23:70','یا می‌گویند: او دیوانه است؟ (نه، چنین نیست) بلکه او حق را برایشان آورده است. لیکن بیشترشان حق را دوست ندارند.');
INSERT INTO "badkhashani" VALUES(23,71,'23:71','و اگر حق از هوی و هوس‌هایشان پیروی می‌کرد، حتما آسمان‌ها و زمین و هر که در آنهاست، تباه می‌شد. بلکه پند و عبرت‌شان را بر آنها آورده‌ایم، اما آنان از پند و نصیحت‌نامه خود روی گردان‌اند.');
INSERT INTO "badkhashani" VALUES(23,72,'23:72','یا از آنان مزد می‌طلبی؟ پس رزق و مزد پروردگارت بهتر است. و او بهترین روزی دهندگان است.');
INSERT INTO "badkhashani" VALUES(23,73,'23:73','(حال آنکه) به يقين تو آنها را به راه راست می‌خوانی.');
INSERT INTO "badkhashani" VALUES(23,74,'23:74','و یقینا کسانی که به آخرت ایمان نمی‌آورند، از راه راست (به راه‌های گمراهی) منحرف شده‌اند.');
INSERT INTO "badkhashani" VALUES(23,75,'23:75','و اگر بر آنها رحم کنیم و رنج و سختی که به آنان رسیده (از آنان) برطرف سازیم، باز هم حیران و سرگردان در سرکشی و طغیان خود پا فشاری می‌کنند.');
INSERT INTO "badkhashani" VALUES(23,76,'23:76','و البته آنان را به عذاب گرفتار کردیم، ولی برای پروردگارشان نه فروتنی و عاجزی کردند و نه به زاری افتادند.');
INSERT INTO "badkhashani" VALUES(23,77,'23:77','تا آن که دری از عذاب شدید بر آنان گشودیم، ناگهان در آن عذاب، ناامید (از نجات) شدند.');
INSERT INTO "badkhashani" VALUES(23,78,'23:78','و او ذاتی است که برای شما گوش و چشم‌ها و دل‌ها را آفرید، اما چه اندک شکر می‌گذارید.');
INSERT INTO "badkhashani" VALUES(23,79,'23:79','و او ذاتی است که شما را در زمین منتشر و پراگنده ساخت. و به‌سوی او محشور خواهید شد.');
INSERT INTO "badkhashani" VALUES(23,80,'23:80','و او ذاتی است که زنده می‌کند و می‌میراند، و اختلاف شب و روز از (فرمان) اوست. آیا نمی‌فهمید؟');
INSERT INTO "badkhashani" VALUES(23,81,'23:81','بلکه (کفار مکه) گفتند مانند آنچه پیشینیان گفته بودند.');
INSERT INTO "badkhashani" VALUES(23,82,'23:82','گفتند: آیا چون بمیریم و خاک و استخوان‌هایی چند شویم، آیا برانگیخته خواهیم شد؟');
INSERT INTO "badkhashani" VALUES(23,83,'23:83','البته این وعده به ما، و در گذشته به پدران ما (نیز) داده شده است، اما این، چیزی جز افسانه‌های پیشینیان نیست.');
INSERT INTO "badkhashani" VALUES(23,84,'23:84','بگو: زمین و کسانی که در آن هستند در تصرف کیست؟ اگر می‌دانید.');
INSERT INTO "badkhashani" VALUES(23,85,'23:85','زودا که گویند: همه (از الله و) در تصرف الله است. بگو: آیا پند نمی‌گیرید؟');
INSERT INTO "badkhashani" VALUES(23,86,'23:86','بگو: پروردگار آسمان‌های هفتگانه و پروردگار عرش بزرگ کیست؟');
INSERT INTO "badkhashani" VALUES(23,87,'23:87','خواهند گفت: این همه از الله است. بگو: پس چرا پرهیزگار نمی‌شوید؟');
INSERT INTO "badkhashani" VALUES(23,88,'23:88','بگو: کیست آن که پادشاهی هر چیزی در دست اوست؟ و او ذاتی است که پناه می‌دهد، و در برابر (عذاب) او (به کسی) پناه داده نمی‌شود، اگر می‌دانید.');
INSERT INTO "badkhashani" VALUES(23,89,'23:89','زودا که گویند: همه (از الله و) در تصرف الله است. بگو: پس از کجا فریب داده می‌شوید (مانند افسون شده)؟');
INSERT INTO "badkhashani" VALUES(23,90,'23:90','بلکه حق را برای آنها آورده‌ایم، و البته آنان دروغگو هستند.');
INSERT INTO "badkhashani" VALUES(23,91,'23:91','الله هیچ فرزندی نگرفته است و هیچ معبودی با او نبوده است، (اگر چنین می‌بود) هر معبودی آنچه را آفریده بود، با خود می‌برد، و حتما بعضی از آنان بر بعضی دیگر غالب می‌شدند، (لذا) منزه و پاک است الله از آنچه (کافران در حق او) وصف می‌کنند.');
INSERT INTO "badkhashani" VALUES(23,92,'23:92','(الله) دانای پنهان و آشکار است، پس برتر است الله از آنچه مشرکین با او شریک مقرر می‌کنند.');
INSERT INTO "badkhashani" VALUES(23,93,'23:93','بگو: ای پروردگارم! اگر آنچه را که به آنان وعده داده شده است به من نشان دهی.');
INSERT INTO "badkhashani" VALUES(23,94,'23:94','ای پروردگارم! پس مرا (در آن وقت) در جملۀ قوم ظالم قرار مده.');
INSERT INTO "badkhashani" VALUES(23,95,'23:95','و به راستی ما بر آن تواناییم که آنچه را به آنان وعده می‌دهیم، به تو نشان دهیم.');
INSERT INTO "badkhashani" VALUES(23,96,'23:96','بدی را به روشی که آن بهتر است دفع کن، ما به آنچه وصف می‌کنند داناتریم.');
INSERT INTO "badkhashani" VALUES(23,97,'23:97','و بگو: ای پروردگارم! از وسوسه‌های شیاطین به تو پناه می‌برم.');
INSERT INTO "badkhashani" VALUES(23,98,'23:98','و (ای پروردگارم) از این که شیاطین پیش من حاضر شوند، به تو پناه می‌برم.');
INSERT INTO "badkhashani" VALUES(23,99,'23:99','تا آن که مرگ به‌سوی یکی از آنان آید، گوید: ای پروردگارم! مرا (به دنیا) باز گردانید.');
INSERT INTO "badkhashani" VALUES(23,100,'23:100','تا در سرایی که (فرصت‌ها را) از دست داده بودم، کار نیک انجام دهم. نه، هرگز! چون آن سخنی است که او گویندۀ آن (تنها بر زبان) است، و پیش روی آنها حایلی است تا روزی که برانگیخته شوند.');
INSERT INTO "badkhashani" VALUES(23,101,'23:101','پس چون در صور دمیده شود، پس هیچگونه نسبت قرابت در میان‌شان نمی‌ماند و (در آن روز) از یکدیگر نمی‌پرسند.');
INSERT INTO "badkhashani" VALUES(23,102,'23:102','پس کسانی که میزان اعمال نیک‌شان سنگین باشد، پس این گروه، ایشان نجات یافته‌گان‌اند.');
INSERT INTO "badkhashani" VALUES(23,103,'23:103','و کسانی که کفّۀ اعمال نیک‌شان سبک باشد، اینان خویشتن را خساره مند نموده‌اند، و در جهنم همیشه خواهند ماند.');
INSERT INTO "badkhashani" VALUES(23,104,'23:104','و آتش دوزخ چهره‌هایشان را می‌سوزاند، و آنها در آنجا غمگین و ترش روی‌اند.');
INSERT INTO "badkhashani" VALUES(23,105,'23:105','آیا آیات من بر شما خوانده نمی‌شد، ولی شما آنها را دروغ می‌پنداشتید؟');
INSERT INTO "badkhashani" VALUES(23,106,'23:106','می‌گویند: ای پروردگار ما! بدبختی (لذت‌ها و شهوات) ما بر ما غالب شد، و مردم گمراه بودیم.');
INSERT INTO "badkhashani" VALUES(23,107,'23:107','ای پروردگار ما! ما را از این جا بیرون کن، پس اگر باز (به سوی کفر و شرک) برگشتیم، پس در این صورت ظالم خواهیم بود.');
INSERT INTO "badkhashani" VALUES(23,108,'23:108','(الله) فرماید: با ذلت و رسوایی داخل شوید و با من سخن نگویید.');
INSERT INTO "badkhashani" VALUES(23,109,'23:109','چون گروهی از بندگان من می‌گفتند: ای پروردگار ما! ایمان آوردیم، پس ما را بیامرز و به ما رحم کن، و تو بهترین رحم کنندگانی.');
INSERT INTO "badkhashani" VALUES(23,110,'23:110','پس شما آنها را به تمسخر گرفتید، تا جایی (که غافل شدید و) ذکر و عبادت مرا از یادتان برد، و شما بر آنان (بندگان مخلص من) می‌خندیدید.');
INSERT INTO "badkhashani" VALUES(23,111,'23:111','البته من هم امروز به سبب صبرشان (در برابر اذیت و تمسخر شما) به آنان این طور پاداش دادم و تنها آنان رستگاران‌اند.');
INSERT INTO "badkhashani" VALUES(23,112,'23:112','(الله از ایشان می‌پرسد:) چه مدت، به عدد سال‌ها در روی زمین ماندید؟.');
INSERT INTO "badkhashani" VALUES(23,113,'23:113','(در جواب) گویند: یک روز یا بخشی از یک روز ماندیم، پس از شمارکنندگان بپرس.');
INSERT INTO "badkhashani" VALUES(23,114,'23:114','الله می‌فرماید: جز اندکی درنگ نکردید، کاش شما می‌دانستید.');
INSERT INTO "badkhashani" VALUES(23,115,'23:115','آیا پنداشتید که شما را بیهوده آفریده‌ایم، و این که به‌سوی ما بازگردانده نمی‌شوید؟');
INSERT INTO "badkhashani" VALUES(23,116,'23:116','پس برتر است الله (از این که جهان را بیهوده خلق کند و او) پادشاه برحق است، هیچ معبودی برحق غير از او نیست، اوست پروردگار عرش بزرگ گرامی.');
INSERT INTO "badkhashani" VALUES(23,117,'23:117','و هر کس با الله معبود دیگری را بخواند که هیچ دلیلی بر حقانیت آن ندارد. پس حساب او تنها با الله است. يقينا کافران رستگار نمی‌شوند.');
INSERT INTO "badkhashani" VALUES(23,118,'23:118','و بگو: پروردگارم! (مرا) بیامرز و (بر من) رحم کن، زیرا تو بهترین رحم کنندگانی.');
INSERT INTO "badkhashani" VALUES(24,1,'24:1','این سوره‌ای است که آن را نازل کرده‌ایم و (عمل به احکام) آن را فرض گردانیدیم و در آن آیات روشن و واضح نازل کردیم، تا شاید یادآور شوید.');
INSERT INTO "badkhashani" VALUES(24,2,'24:2','زن و مرد زناکار (که هنوز ازدواج نکرده‌اند) به هر یک از آنان صد تازیانه بزنید. و اگر به الله و روز قیامت ایمان دارید، نباید در (تنفيذ حكم) دین الله درباره آنان دچار دلسوزی شوید. و باید گروهی از مؤمنان بر عذاب ایشان حاضر باشند.');
INSERT INTO "badkhashani" VALUES(24,3,'24:3','مرد زناکار مگر با زن زناکار یا مشرک ازدواج نمی‌کند. و زن زناکار را جز مرد زناکار و یا مرد مشرک به زنی نمی‌گیرد، این بر مؤمنان حرام شده است.');
INSERT INTO "badkhashani" VALUES(24,4,'24:4','و کسانی که به زنان پاکدامن مؤمن نسبت زنا می‌دهند، باز چهار گواه نمی‌آورند، به آنان هشتاد تازیانه بزنید، و هرگز شهادت آنان را قبول نکنید، و این گروه، البته فاسقان‌اند.');
INSERT INTO "badkhashani" VALUES(24,5,'24:5','مگر آنان که پس از این توبه کردند و (عمل خود را) اصلاح کردند. پس بدون شک الله آمرزگار (و) مهربان است.');
INSERT INTO "badkhashani" VALUES(24,6,'24:6','و کسانی که به زنان خود نسبت زنا می‌دهند و جز خودشان گواهانی دیگر ندارند، پس شهادت یکی از ایشان این است که چهار بار به نام الله قسم یاد کند که او از راستگویان است.');
INSERT INTO "badkhashani" VALUES(24,7,'24:7','و پنجمین شهادت او این است که (بگوید) لعنت الله بر او باد، اگر از دروغگویان باشد.');
INSERT INTO "badkhashani" VALUES(24,8,'24:8','و عذاب از آن (زن) ساقط می‌شود در صورتی که چهار بار (به الله سوگند بخورد و) گواهی دهد که شوهرش از دروغگویان است.');
INSERT INTO "badkhashani" VALUES(24,9,'24:9','و در مرتبۀ پنجم آن زن بگوید: غضب الله بر وی باد اگر او (شوهرش) از راستگویان باشد.');
INSERT INTO "badkhashani" VALUES(24,10,'24:10','و اگر فضل الله و رحمت او بر شما نمی‌بود (البته دچار عذاب می‌شدید) و اینکه الله توبه‌پذیرِ حکیم است.');
INSERT INTO "badkhashani" VALUES(24,11,'24:11','البته کسانی که تهمت بزرگ را در میان آورده‌اند، گروهی از خود شما هستند، این تهمت را برای خود شر نپندارید، بلکه آن برایتان خیر است، بر عهدۀ هر کدام از آنان که مرتکب آن شده است، سهمی از گناه است. و آن کس از ایشان که بخش بزرگ آن (تهمت) را عهده دار شده است، برایش عذاب بزرگ خواهد بود.');
INSERT INTO "badkhashani" VALUES(24,12,'24:12','چرا وقتی که این تهمت را شنیدید، مردان و زنان مؤمن در حق خویش گمان نیک نکردند (به خود قیاس نکردند) و نگفتند: این دروغ آشکار است؟');
INSERT INTO "badkhashani" VALUES(24,13,'24:13','چرا (افتراگران) برای اثبات آن (تهمت) چهار گواه نیاوردند؟ پس چون گواهان را نیاوردند، لذا آنان در نزد الله دروغگو هستند.');
INSERT INTO "badkhashani" VALUES(24,14,'24:14','و اگر فضل الله و رحمت او در دنیا و آخرت بر شما نبود، البته به سبب دخالت تان در کار تهمت (به عائشه)، عذاب بزرگ به شما می‌رسید.');
INSERT INTO "badkhashani" VALUES(24,15,'24:15','چون آن (شایعۀ زشت) را از زبان یکدیگر می‌گرفتید و با زبان‌های خویش چیزی می‌گفتید که علم و اطلاعی از آن نداشتید. و آن را سهل و ساده تلقی می‌کردید، در حالیکه آن (گناه و مجازاتش) نزد الله بسیار بزرگ است.');
INSERT INTO "badkhashani" VALUES(24,16,'24:16','چرا وقتی آن (بهتان بزرگ و ناروا) را شنیدید، نگفتید: برای ما سزاوار نیست که به آن سخن بگوییم، (ای الله) تو را به پاکی یاد می‌کنیم، این بهتانی بزرگ است؟');
INSERT INTO "badkhashani" VALUES(24,17,'24:17','الله به شما اندرز می‌دهد که هرگز به مانند آن بازنگردید، اگر مؤمن هستید.');
INSERT INTO "badkhashani" VALUES(24,18,'24:18','الله برای شما آیات خود را بیان می‌کند و الله دانای با حکمت است.');
INSERT INTO "badkhashani" VALUES(24,19,'24:19','البته کسانی که دوست دارند فاحشه و بی‌حیایی در میان مؤمنان شایع گردد، ایشان در دنیا و آخرت عذاب دردناکی دارند، و الله (رازهای سینه‌ها را) می‌داند و شما نمی‌دانید.');
INSERT INTO "badkhashani" VALUES(24,20,'24:20','و اگر فضل و رحمت الله بر شما نبود (حتما شما را در مقابل بهتان و شایعات تان مجازات می‌کرد) و اینکه الله مشفق (و) مهربان است.');
INSERT INTO "badkhashani" VALUES(24,21,'24:21','ای کسانی که ایمان آورده‌اید، از گام‌های شیطان پیروی مکنید. و هرکس از گام‌های شیطان پیروی کند (بداند) که او به فحشا و منکر دستور می‌دهد و اگر فضل الله و رحمت وی بر شما نبود، هرگز کسی از شما (از گناه) پاک نمی‌گردید، ولی الله هرکس را که بخواهد پاک می‌گرداند، و الله شنواى داناست.');
INSERT INTO "badkhashani" VALUES(24,22,'24:22','و کسانی از شما که صاحبان فضیلت (در دین) هستند و توانمندان شما (در ثروت)، نباید قسم بخورند که به خویشاوندان و بینوایان و مهاجران در راه الله چیزی ندهند، و باید که عفو کنند و باید گذشت کنند. آیا دوست ندارید که الله شما را بیامرزد؟ و الله آمرزنده (و) مهربان است.');
INSERT INTO "badkhashani" VALUES(24,23,'24:23','همانا کسانی که زنان پاکدامن و بی‌خبر )از بدکاری) و مؤمن را به زنا متهم می‌سازند، در دنیا و آخرت لعنت شده‌اند، و عذاب بزرگ در پیش دارند.');
INSERT INTO "badkhashani" VALUES(24,24,'24:24','روزی که زبان‌ها و دست‌ها و پاهایشان بر آنچه می‌کردند، گواهی می‌دهند.');
INSERT INTO "badkhashani" VALUES(24,25,'24:25','در آن روز، الله جزای شایستۀ آنان را بطور کامل و بی‌کم و کاست به ایشان می‌دهد، و خواهند دانست که الله همان (معبود) حق آشکار است.');
INSERT INTO "badkhashani" VALUES(24,26,'24:26','زنان پلید برای مردان پلیدند و مردان پلید برای زنان پلیدند. و زنان پاکیزه سزاوار مردان پاک‌اند و مردان پاکیزه سزاوار زنان پاک‌اند، این گروه از آنچه (منافقان) می‌گویند، مبرا و منزه هستند. برایشان آمرزش و رزق نیکوست.');
INSERT INTO "badkhashani" VALUES(24,27,'24:27','ای کسانی که ایمان آورده‌اید! وارد خانه‌هایی غیر از خانه‌های خود مشوید تا آنکه اجازه بگیرید و بر اهل آن خانه سلام گویید، این کار برای شما بهتر است، تا شاید پند گیرید و یادآور شوید.');
INSERT INTO "badkhashani" VALUES(24,28,'24:28','پس اگر کسی را در آن (خانه‌ها) نیافتید، پس داخل آن نشوید، تا اینکه به شما اجازه داده شود. و اگر به شما گفته شود: برگردید، پس برگردید که برای شما بهتر و پاکیزه‌تر است. و الله به آنچه می‌کنید داناست.');
INSERT INTO "badkhashani" VALUES(24,29,'24:29','بر شما گناهی نیست که به خانه‌های غیر مسکونی داخل شوید که در آنجا متاعی دارید. و الله می‌داند آنچه را آشکار می‌کنید و آنچه را می‌پوشانید.');
INSERT INTO "badkhashani" VALUES(24,30,'24:30','به مردان مؤمن بگو: چشمان خود را (از نگاه به نامحرمان) بپوشند و شرمگاه خود را حفظ کنند و پاکدامنی ورزند، این برایشان پاکیزه‌تر است، زیرا الله از آنچه انجام می‌دهند، آگاه است.');
INSERT INTO "badkhashani" VALUES(24,31,'24:31','و به زنان مؤمن بگو: چشمان خود را (از نامحرمان) بپوشند و شرمگاه خود را حفظ کنند و پاکدامنی ورزند. و زینت خود را ظاهر نسازند مگر آنچه که از آن آشکار است. و باید که چادرهای خود را بر گریبان‌های خود بیاندازند، و زینت خود را آشکار نسازند مگر برای شوهران خود، یا پدارن خود یا پدران شوهران خود، یا پسران خود یا پسران شوهران خود، یا برادرن خود یا پسران برادران خود، یا پسران خواهران خود، یا زنان مسلمان (هم جنس) خودشان، یا کنیزان خود، یا مردان بی‌رغبت (به زنان) که تابع خانوادۀ شمایند. و کودکانی که بر شرمگاه زنان اطلاع نیافته‌اند. و نباید زمین را با پاهای خود بکوبند تا آنچه از زینت‌شان را که پنهان می‌دارند، معلوم گردد. و ای مومنان! همگی به دربار الله توبه کنید تا رستگار شوید.');
INSERT INTO "badkhashani" VALUES(24,32,'24:32','و مردان و زنان بی‌همسر و بردگان و کنیزان صالح خود را (که سزاوار ازدواج هستند) به نکاح بدهید، اگر فقیر باشند الله آنان را از فضل خود غنی و بی‌نیاز خواهد کرد و الله دارای فضل گشاده (و) داناست.');
INSERT INTO "badkhashani" VALUES(24,33,'24:33','و آنان که اسباب ازدواج را نمی‌یابند باید پاکدامنی پیشه کنند، تا آنکه الله آنان را از فضل خود توانگر و بی‌نیاز گرداند. و کسانی از بردگان تان که خواستار قرار داد «کتابت» برای آزاد شدن هستند، اگر توانایی پرداخت قرار داد «کتابت» را در ایشان یافتید، پس با آنان قرارداد ببندید. و از مال الله که به شما ارزانی داشته است به آنان بدهید، و همچنین کنیزان خود را مجبور به زنا نکنید اگر آنان خواستند عفیف و پاکدامن باشند، برای این که بهرۀ زودگذر زندگانی دنیا را بدست آرید. و هر کس آنان را (بر زنا) اجبار کند، يقينا الله پس از اجبار کردنشان (نسبت به آنها) آمرزندۀ مهربان است.');
INSERT INTO "badkhashani" VALUES(24,34,'24:34','و بدون شک به‌سوی شما آیات روشن و مثلی از (حال) کسانی که پیش از شما گذشتند و پندی برای پرهیزگاران نازل کردیم.');
INSERT INTO "badkhashani" VALUES(24,35,'24:35','الله ایجادکنندۀ نور آسمان‌ها و زمین است. مثل نور او مانند چراغدانی است که در آن چراغی باشد و آن چراغ نیز در قندیلی قرار گیرد. آن قندیل گویی ستارۀ درخشان است که افروخته می‌شود (با روغنی) از درخت بابرکت زیتونی، که نه شرقی است و نه غربی، نزدیک است که روغنش روشنی بخشد، هرچند آتشی به آن نرسیده باشد. نوری است بر روی نور. الله هر کس را بخواهد با نور خود هدایت می‌کند. و الله برای مردم مثل‌ها می‌زند، و الله به همه چیز داناست.');
INSERT INTO "badkhashani" VALUES(24,36,'24:36','(و آن چراغ) در خانه‌هایی است که الله حکم کرده که (قدر و منزلت) آنها رفعت یابد و نام او در آنجا یاد شود و صبح و شام در آن (خانه‌ها)‌ او را تسبیح می‌گویند.');
INSERT INTO "badkhashani" VALUES(24,37,'24:37','مردانی که هیچ تجارت و خرید و فروشی آنان را از یاد الله، و برپا نمودن نماز، و دادن زکات غافل نمی‌سازد، از روزی می‌ترسند که دل‌ها و دیده‌ها در آن دگرگون می‌گردد.');
INSERT INTO "badkhashani" VALUES(24,38,'24:38','تا الله به عوض بهترینِ آنچه کرده‌اند، به آنان ثواب بدهد، و از فضل خویش بیشتر به آنان می‌دهد، و الله هر کسی را که بخواهد، بی‌حساب روزی می‌دهد.');
INSERT INTO "badkhashani" VALUES(24,39,'24:39','و آنان که کفر ورزیدند، اعمالشان مانند سراب در دشت هموار است که (فرد) تشنه آنرا آبی گمان می‌کند، تا چون به آن نزدیک شود آن را چیزی نیابد، و الله را نزد خود یابد و حسابش را تمام و کامل به او بدهد، و الله سريع الحساب است.');
INSERT INTO "badkhashani" VALUES(24,40,'24:40','یا (اعمال كفار) مانند تاریکی‌هایی در بحر عمیق است که موجی آن را می‌پوشاند که بر روی آن موج است و بر روی آن نیز ابر است، تاریکی‌هایی است که بعضی از آنها بر روی بعضی دیگر قرار گرفته است، چون (شخص مبتلا در تاریکی‌های داخل بحر) دستش را بیرون کند، نزدیک نیست آن را ببیند. و کسی که الله نوری برایش قرار نداده باشد، برای او نوری نیست.');
INSERT INTO "badkhashani" VALUES(24,41,'24:41','آیا ندانسته‌ای که همه کسانی که در آسمان‌ها و زمین هستند، الله را به پاکی یاد می‌کنند؟! و پرندگان که (در حال پرواز) بال گشوده‌اند نیز (او را تسبیح می‌گویند؟!) هریک از آنان نماز و تسبیح خود را دانسته است و الله به آنچه می‌کنند، داناست.');
INSERT INTO "badkhashani" VALUES(24,42,'24:42','و پادشاهی آسمان‌ها و زمین مخصوص الله است و بازگشت (همۀ شما) به‌سوی الله است.');
INSERT INTO "badkhashani" VALUES(24,43,'24:43','آیا ندیدی که الله ابرهایی را می‌راند، باز میان (اجزای) آن پیوند می‌دهد، باز آنها را متراکم می‌گرداند، پس می‌بینی که قطره‌های باران از لابلای آن بیرون می‌ریزد. و از ابرهایِ کوه مانند ژاله فرود می‌آورد، پس آن را به هر کس بخواهد می‌رساند و آن را از هر کس که بخواهد باز می‌دارد. نزدیک است درخشش برق آن چشم‌ها را برباید.');
INSERT INTO "badkhashani" VALUES(24,44,'24:44','الله شب و روز را می‌گرداند، البته در این (کار) عبرت بزرگی است برای کسانی که بصیرت داشته باشند.');
INSERT INTO "badkhashani" VALUES(24,45,'24:45','و الله هر جنبده‌ای را از آب آفریده است، پس بعضی از آنها بر شکم خود راه می‌روند و بعضی از آنها بر روی دو پا راه می‌روند و بعضی از آنها بر روی چهارپا راه می‌روند. الله هر چه بخواهد می‌آفریند. البته الله بر هر چیز قادر است.');
INSERT INTO "badkhashani" VALUES(24,46,'24:46','البته ما آیات روشنگر را نازل کردیم و الله هر که را بخواهد به راه راست هدایت می‌کند.');
INSERT INTO "badkhashani" VALUES(24,47,'24:47','و (منافقان) می‌گویند: به الله و به پیغمبر ایمان آوردیم و اطاعت کردیم، باز گروهی از آنها بعد از این (اعتراف) روی می‌گردانند و ایشان مؤمن نیستند.');
INSERT INTO "badkhashani" VALUES(24,48,'24:48','و چون به سوى الله و پیغمبرش دعوت داده شوند تا در میان‌شان حکم و فیصله کند، ناگهان گروهی از آنها رویگردانند.');
INSERT INTO "badkhashani" VALUES(24,49,'24:49','و اگر حق به جانب آنان باشد، گردن نهاده به‌سوی او می‌آیند.');
INSERT INTO "badkhashani" VALUES(24,50,'24:50','آیا در دل‌هایشان مرض (نفاق) است یا اینکه به شک افتاده‌اند؟ یا می‌ترسند که الله و پیغمبرش بر آنان ظلم کنند؟ (نه) بلکه خود ایشان ظالمان‌اند.');
INSERT INTO "badkhashani" VALUES(24,51,'24:51','سخن مؤمنان وقتی که به سوى الله و پیغمبرش خوانده شوند، تا میان‌شان فيصله کند تنها این است که می‌گویند: شنیدیم و اطاعت کردیم و این گروه همان رستگاران‌اند.');
INSERT INTO "badkhashani" VALUES(24,52,'24:52','و هر کس از الله و پیغمبرش اطاعت کند و از الله بترسد و از (عذاب) او خود را نگاه دارد، پس ایشانند که کامیاب‌اند.');
INSERT INTO "badkhashani" VALUES(24,53,'24:53','و (منافقان) به الله قسم خوردند به مؤكدترین قسم‌های خود که اگر به آنها فرمان دهی (که برای جهاد بیرون روند) البته بیرون می‌شوند، بگو: قسم مخورید، اطاعت پسندیده (از شما) مطلوب است، چون الله از آنچه می‌کنید، با خبر است.');
INSERT INTO "badkhashani" VALUES(24,54,'24:54','بگو: از الله اطاعت کنید و از پیغمبر (نیز) اطاعت کنید، پس اگر روی گردانند (بدانند که به رسول ما زیانی نرسانده‌اند) زیرا بر عهدۀ او تنها چیزی است که به آن مکلف شده است. و بر عهدۀ شما هم چیزی است که به آن مکلف هستید. اما اگر او را اطاعت کنید، راهیاب می‌شوید. و بر عهدۀ پیغمبر ما جز ابلاغ آشکار نیست.');
INSERT INTO "badkhashani" VALUES(24,55,'24:55','الله به کسانی از شما که ایمان آورده و کارهای شایسته کرده‌اند، وعده داده است که حتما آنان را در زمین جانشین خواهد کرد، طوری که آنانی را که پیش از ایشان بودند جانشین ساخت، و آن دینی را که برایشان پسندیده است به نفع‌شان پابرجا و برقرار خواهد ساخت، و بیم‌شان را به ایمنی تبدیل خواهد کرد؛ چون مرا پرستش می‌کنند و چیزی را با من شریک نمی‌سازند. و هر کس پس از این کافر شود، پس این گروه همان فاسقان‌اند.');
INSERT INTO "badkhashani" VALUES(24,56,'24:56','و نماز را برپا کنید و زکات را بدهید و از پیغمبر اطاعت کنید، تا بر شما رحم شود.');
INSERT INTO "badkhashani" VALUES(24,57,'24:57','گمان مبر که کافران عاجزکننده در زمین‌اند. و جایگاه‌شان دوزخ است و بد جای بازگشت است.');
INSERT INTO "badkhashani" VALUES(24,58,'24:58','ای کسانی که ایمان آورده‌اید! حتما باید غلامان و کنیزانتان و کسانی از شما که به (سن) بلوغ نرسیده‌اند، در سه وقت از شما اجازه بگیرند، پیش از نماز صبح و نیم روز، هنگامی که لباس‌های خود را بیرون می‌آورید، و بعد از نماز خفتن. (این) سه وقت برایتان (اوقات) خلوت است. غیر از این (سه وقت) بر شما و بر آنان گناهی نیست، چرا که ایشان در اطراف شما در رفت و آمدند، و شما نیز بر همدیگر وارد می‌شوید. الله این چنین آیات را برایتان روشن می‌سازد، و الله دانای باحکمت است.');
INSERT INTO "badkhashani" VALUES(24,59,'24:59','و چون اطفال شما به سن (بلوغ) رسیدند، باید از شما اجازه بگیرند، مانند کسانی که پیش از آنان بودند، اجازه می‌گرفتند. الله این چنین آیاتش را برایتان روشن می‌سازد، و الله دانای باحکمت است.');
INSERT INTO "badkhashani" VALUES(24,60,'24:60','و بر زنان بر جای نشسته که امید ازدواج ندارند گناهی نیست که لباس‌هایشان (چادرهایشان) را بگذارند به شرطی که زینتی را آشکار نکنند، و اگر پاکی و عفت ورزند، برایشان بهتر است، و الله شنواى داناست.');
INSERT INTO "badkhashani" VALUES(24,61,'24:61','بر نابینا گناهی نیست و بر لنگ (هم) گناهی نیست و بر بیمار (نیز) گناهی نیست و (نیز) بر خودتان گناهی نیست، که از خانه‌های خودتان و از خانه‌های فرزندانتان، یا خانه‌های پدرانتان، یا خانه‌های مادرانتان، یا خانه‌های بردارانتان، یا خانه‌های خواهرانتان، یا خانه‌های عموهای تان، یا خانه‌های عمه‌های تان، یا خانه‌های خالوهای تان و یا خانه‌های خاله‌های تان و یا آن (خانه‌هایی) که کلیدشان را در اختیار دارید، یا خانه‌های دوستانتان طعام بخورید. همچنین بر شما گناهی نیست که یکجا و یا جداگانه طعام بخورید. پس چون به خانه‌ها در آمدید، بر یکدیگر سلام گویید به سلام و درود مبارک و پاکیزه که از جانب الله (ثابت شده) است. الله این چنین آیات (خود) را برایتان بیان می‌کند تا بفهمید.');
INSERT INTO "badkhashani" VALUES(24,62,'24:62','مؤمنان واقعی تنها کسانی هستند که به الله و پیغمبرش ایمان آورده‌اند، و چون در کار جمع کننده (و مهمی که نیاز به جمع شدن مردم دارد) با او باشند به جایی نمی‌روند تا این که از وی کسب اجازه کنند، يقيناً آنان که از تو اجازه می‌خواهند، ایشان کسانی‌اند که به الله و پیغمبرش ایمان دارند. پس چون برای انجام بعضی از کارهای خود از تو اجازه خواستند، به هر کس از آنان که خواستی، اجازه بده و از الله برایشان آمرزش بخواه. بدون شک الله آمرزگار مهربان است.');
INSERT INTO "badkhashani" VALUES(24,63,'24:63','خطاب کردن پیغمبر را در میان خود، مانند خطاب کردن بعضی از خودتان به بعضی دیگر قرار ندهید، بدون شک الله کسانی از شما را که پناه جویان و پنهانی می‌گریزند، می‌شناسد. پس آنانکه با فرمان او مخالفت می‌کنند، باید از این بترسند که مصیبتی به آنان برسد، یا اینکه به عذاب دردناک گرفتار شوند.');
INSERT INTO "badkhashani" VALUES(24,64,'24:64','باید بدانید که يقينا آنچه در آسمان‌ها و زمین است، خاص از الله است، او می‌داند آنچه را که شما بر آن هستید و روزی که به‌سوی او بازگردانده می‌شوند. پس آنان را به آنچه که کرده‌اند خبر می‌دهد، و الله به هر چیز داناست.');
INSERT INTO "badkhashani" VALUES(25,1,'25:1','بسیار خجسته و بابرکت است آن ذاتی که فرقان (جداکنندۀ حق از باطل) را بر بنده خود (محمد) نازل کرد، تا برای جهانیان بیم ‌دهنده باشد.');
INSERT INTO "badkhashani" VALUES(25,2,'25:2','همان ذاتی که پادشاهی آسمان‌ها و زمین خاص از اوست. و (همان ذاتی که( هیچ فرزندی نگرفته است، و برای او در فرمانروایی شریکی نیست، و همه چیز را آفریده است، پس آنها را به اندازه معین مقرر کرده است.');
INSERT INTO "badkhashani" VALUES(25,3,'25:3','و غیر از او (الله) معبودانی را برای خود گرفته‌اند که چیزی را نمی‌آفرینند، بلکه خودشان آفریده می‌شوند و برای خود نه اختیار زیان را دارند و نه اختیار نفع را. و نه مالک مرگ‌اند و نه مالک حیات و نه رستاخیز (خویش).');
INSERT INTO "badkhashani" VALUES(25,4,'25:4','و کافران گفتند: این (قرآن) دروغی بیش نیست که خود (محمد) آن را ساخته است و گروهی دیگر او را بر آن یاری کرده‌اند. به يقين که ظلم و دروغ در میان آورده‌اند.');
INSERT INTO "badkhashani" VALUES(25,5,'25:5','و (باز) گفتند: افسانه‌های پیشینیان است که آن را برای خود نوشته است و صبح و شام بر او خوانده می‌شود.');
INSERT INTO "badkhashani" VALUES(25,6,'25:6','بگو: آن را ذاتی نازل کرده است که هر راز نهانی را که در آسمان‌ها و زمین است می‌داند. يقيناً او آمرزگار (و) مهربان است.');
INSERT INTO "badkhashani" VALUES(25,7,'25:7','و گفتند: این پیغمبر را چه شده است که غذا می‌خورد و در بازارها می‌رود؟! چرا فرشته‌ای به‌سوی او فرستاده نشده تا همراه او بيم‌دهنده باشد؟');
INSERT INTO "badkhashani" VALUES(25,8,'25:8','یا چرا گنجی (از آسمان) بر او انداخته نمی‌شود، یا چرا باغی ندارد که از (میوه‌های) آن بخورد؟ و ظالمان گفتند: شما جز از انسان جادوزده پیروی نمی‌کنید؟');
INSERT INTO "badkhashani" VALUES(25,9,'25:9','بنگر چگونه برای تو مثل‌ها زدند، پس گمراه شدند، در نتیجه هیچ راهی نمی‌یابند.');
INSERT INTO "badkhashani" VALUES(25,10,'25:10','بسیار خجسته و بابرکت است آن ذاتی که اگر بخواهد برایت بهتر از این می‌سازد، باغ‌هایی که از زیر (درختان) آن نهرها روان است و برای تو قصرها قرار می‌دهد.');
INSERT INTO "badkhashani" VALUES(25,11,'25:11','بلکه قيامت را دروغ شمردند و ما برای کسی که قیامت را دروغ بشمارد، دوزخ را آماده کرده‌ایم.');
INSERT INTO "badkhashani" VALUES(25,12,'25:12','و چون (دوزخ) آنان را از جای دور ببیند، (دوزخیان) خشم و خروشی از آن می‌شنوند.');
INSERT INTO "badkhashani" VALUES(25,13,'25:13','و چون در جای تنگ از آن انداخته شوند، در حالیکه دست و پا بسته‌اند، خواهان مرگ و نابودی می‌شوند.');
INSERT INTO "badkhashani" VALUES(25,14,'25:14','(برایشان گفته می‌شود:) امروز یکبار مرگ و نابودی مخواهید (بلکه برای خود) بارها نابودی را بخواهید.');
INSERT INTO "badkhashani" VALUES(25,15,'25:15','بگو: آیا این (عقوبت) بهتر است یا بهشت جاویدانی که به پرهیز گاران وعده داده شده است؟ که برایشان پاداش و بازگشت‌گاه است.');
INSERT INTO "badkhashani" VALUES(25,16,'25:16','هرچه بخواهند در آنجا دارند. و برای همیشه در آنجا خواهند ماند، (این) وعده‌ای است که تحقق آن بر عهدۀ الله است که از او درخواست شده است.');
INSERT INTO "badkhashani" VALUES(25,17,'25:17','و یادآور شو روزی را که الله آنان را و آنچه را بجای الله می‌پرستیدند جمع کند، و (به معبودان) می‌گوید: آیا شما این بندگانم را گمراه کرده‌اید، یا اینکه خود راه گم کرده‌اند؟');
INSERT INTO "badkhashani" VALUES(25,18,'25:18','آنان (معبودان با تعجب در پاسخ) می‌گویند: تو را به پاکی یاد می‌کنیم، برای ما سزاوار نیست که غیر از تو دوستانی بگیریم، بلکه آنان و پدرانشان را (از همه نعمت‌های دنیوی) برخوردار ساختی تا آنجا که یاد تو را فراموش کردند، و قوم هلاک شده بودند.');
INSERT INTO "badkhashani" VALUES(25,19,'25:19','(باز به مشرکان گفته شود:) به یقین ایشان سخنان شما را تکذیب کردند، پس در نتیجه نمی‌توانید عذاب را از خود دفع کنید و نه خود را یاری دهید، و هرکس از شما ظلم کند، عذاب بزرگ را به او می‌چشانیم.');
INSERT INTO "badkhashani" VALUES(25,20,'25:20','و ما پیش از تو پیغمبرانی را نفرستاده‌ایم، مگر اینکه غذا می‌خوردند و در بازارها راه می‌رفتند. و ما بعضی از شما را وسیلۀ امتحان بعضی دیگر قرار دادیم. آیا (در برابر آزمایش‌ها) صبر کرده می‌توانید؟ پروردگارت (به هر چیز) بیناست.');
INSERT INTO "badkhashani" VALUES(25,21,'25:21','و آنان که به ملاقات ما امید ندارند، می‌گویند: چرا فرشتگان بر ما فرود آورده نشد؟ یا چرا پروردگار خود را نمی‌بینیم؟ به راستی آنان خویشتن را بزرگ شمردند و سخت سرکشی کردند.');
INSERT INTO "badkhashani" VALUES(25,22,'25:22','روزی که فرشتگان را می‌بینند، آن روز برای مجرمان هیچ مژده‌ای نیست و (فرشتگان به کفار) می‌گویند: (از رحمت الهی) بازداشته شوید (و نصیبی جز محرومیت ندارید).');
INSERT INTO "badkhashani" VALUES(25,23,'25:23','و ما به (محاسبۀ) تمام اعمالی که آنان انجام داده بودند، می‌پردازیم. باز آن را چون غبار برباد رفته می‌گردانیم.');
INSERT INTO "badkhashani" VALUES(25,24,'25:24','جنتیان در آن روز منزلگاه و استراحت‌گاهشان بهتر و نیکوتر است.');
INSERT INTO "badkhashani" VALUES(25,25,'25:25','و (یادآور شو) روزی که آسمان با ابر شق گردد و فرشتگان پیاپی فرستاده شوند.');
INSERT INTO "badkhashani" VALUES(25,26,'25:26','آن روز سلطنت و پادشاهی حقیقی از پروردگار مهربان است. و آن روزی است که بر کافران بسیار دشوار خواهد بود.');
INSERT INTO "badkhashani" VALUES(25,27,'25:27','و (یادآور شو) روزی را که ظالم دست‌های خود را با دندان می‌گزد؛ می‌گوید: ای کاش من هم همرای پیغمبر، راه (ایمان و نجات را) در پیش می‌گرفتم.');
INSERT INTO "badkhashani" VALUES(25,28,'25:28','وای بر من! ای کاش فلان شخص (گمراه) را دوست خود نمی‌گرفتم.');
INSERT INTO "badkhashani" VALUES(25,29,'25:29','یقیناً که مرا از ذکر (الله و کتاب او) پس از آن که به‌سوی من آمد، گمراه ساخت. و شیطان همیشه خوارکنندۀ انسان است (در خواری و ذلت او را تنها می‌گذارد).');
INSERT INTO "badkhashani" VALUES(25,30,'25:30','و پیغمبر (شکوا کنان) می‌گوید: ای پروردگارم! قوم من این قرآن را رها نموده و از آن دوری گرفته‌اند.');
INSERT INTO "badkhashani" VALUES(25,31,'25:31','و این چنین برای هر پیغمبر دشمنی از مجرمان قرار دادیم و همین کافی است که پروردگار تو هدایتگر و مددکننده باشد.');
INSERT INTO "badkhashani" VALUES(25,32,'25:32','و کافران گفتند: چرا قرآن یکجا بر او نازل نشد؟ این طور (آن را به تدریج نازل کردیم) تا دل تو را به وسیلۀ آن محکم و استوار کنیم. و آن را آهسته و آرام بر تو خواندیم.');
INSERT INTO "badkhashani" VALUES(25,33,'25:33','و (کافران) نزد تو هیچ مثلی نمی‌آورند، مگر این که ما (جواب) برحق و نیک‌تر از روی تفسیر برایت می‌آوریم.');
INSERT INTO "badkhashani" VALUES(25,34,'25:34','آنان که بر چهره‌های خود افتاده به‌سوی دوزخ حشر می‌شوند، آنان بدترین جای و گم‌ترین راه را دارند.');
INSERT INTO "badkhashani" VALUES(25,35,'25:35','و به یقین ما به موسی کتاب (تورات) دادیم و برادرش هارون را همراه او وزیرش گردانیدیم.');
INSERT INTO "badkhashani" VALUES(25,36,'25:36','پس گفتیم: به‌سوی قومی بروید که آیات ما را دروغ شمردند، پس آنان را به سختی هلاک و نابود کردیم.');
INSERT INTO "badkhashani" VALUES(25,37,'25:37','قوم نوح را چون پیغمبران را تکذیب کردند، غرق ساختیم و ایشان را برای مردم عبرت گردانیدیم، و برای ظالمان عذاب دردناک آماده کرده‌ایم.');
INSERT INTO "badkhashani" VALUES(25,38,'25:38','و عاد و ثمود و اصحاب الرس و اهل قرن‌های زیادی که در این میان‌شان بودند (هلاک و نابود ساختیم).');
INSERT INTO "badkhashani" VALUES(25,39,'25:39','و برای هریک مثل‌ها آوردیم و هریک را (بعد از نافرمانی‌شان) به سختی نابود کردیم.');
INSERT INTO "badkhashani" VALUES(25,40,'25:40','و البته (مشرکان) بر قریه‌ای که باران بلا بر اهالی آنجا بارانده شد، گذشته‌اند، آیا آن را نمی‌دیدند؟ بلکه امیدی به زنده شدن نداشتند.');
INSERT INTO "badkhashani" VALUES(25,41,'25:41','و چون تو را بینند، جز این نیست که ترا به استهزا و تمسخر می‌گیرند (و می‌گویند): آیا این است همان کسی که الله او را به حیث پیغمبر فرستاده است؟!');
INSERT INTO "badkhashani" VALUES(25,42,'25:42','(و گفتند:) به راستی نزدیک بود که ما را از (پرستش) معبودان ما گمراه کند، اگر بر آن‌ها صبر و مقاومت نمی‌کردیم. و چون عذاب را ببینند به زودی خواهند دانست که چه کسی گمراهتر است.');
INSERT INTO "badkhashani" VALUES(25,43,'25:43','آیا دیده‌ای آن کسی را که هوای نفس خود را معبود خود گرفته است؟ آیا تو بر او وکیل می‌باشی؟');
INSERT INTO "badkhashani" VALUES(25,44,'25:44','یا می‌پنداری که بیشترشان می‌شنوند یا می‌فهمند؟ آنان نیستند مگر مانند چهارپایان (بی‌خرد)، بلکه ایشان گمراه‌ترند.');
INSERT INTO "badkhashani" VALUES(25,45,'25:45','آیا ندیده‌ای که پروردگارت چگونه سایه را گسترده است؟ و اگر می‌خواست آن را ساکن می‌کرد. باز آفتاب را دلیل (گسترش) آن قرار دادیم.');
INSERT INTO "badkhashani" VALUES(25,46,'25:46','باز آن را آهسته به‌سوی خود باز می‌گیریم.');
INSERT INTO "badkhashani" VALUES(25,47,'25:47','و اوست ذاتی که شب را برای‌تان لباس و خواب را مایۀ آرامش گردانید. و روز را (وقت) برخواستن شما (در زمین) قرار داد.');
INSERT INTO "badkhashani" VALUES(25,48,'25:48','و اوست ذاتی که پیش از رحمت خود (باران) بادها را مژده‌دهنده فرستاد و از آسمان آب پاکیزه نازل کردیم.');
INSERT INTO "badkhashani" VALUES(25,49,'25:49','تا به وسیلۀ آن (آب) سرزمین مرده را زنده کنیم و آن را به آنچه از چهارپایان و انسان‌های بسیاری که آفریده‌ایم، بنوشانیم.');
INSERT INTO "badkhashani" VALUES(25,50,'25:50','و یقیناً آن (آیات و اندرزها) را در میان‌شان (به صورت) گوناگون بیان کردیم تا پند گیرند، ولی بیشترِ مردم جز کفران را قبول نکردند.');
INSERT INTO "badkhashani" VALUES(25,51,'25:51','و اگر می‌خواستیم در هر قریه بیم‌دهنده‌ای می‌فرستادیم.');
INSERT INTO "badkhashani" VALUES(25,52,'25:52','پس، از کافران اطاعت مکن. و با آن (قرآن) با آنان جهاد کن، جهادی بزرگ.');
INSERT INTO "badkhashani" VALUES(25,53,'25:53','و او ذاتی است که دو دریا را در کنار هم روان ساخت، یکی شیرینِ خوشگوار و دیگری شورِ تلخ است. و در میان آن دو حایل و مانع محکم و استوار قرار داد.');
INSERT INTO "badkhashani" VALUES(25,54,'25:54','و او ذاتی است که از آب بشر را آفرید، پس برای او پیوند نسبی و سببی قرار داد، و پروردگارت همیشه تواناست.');
INSERT INTO "badkhashani" VALUES(25,55,'25:55','و غیر از الله چیزی را می‌پرستند که به آنان سود نمی‌بخشد و نه به آنان زیان می‌رساند. و کافر همیشه بر نافرمانی پروردگارش پشتیبان (شیطان) است.');
INSERT INTO "badkhashani" VALUES(25,56,'25:56','و تو را جز مژده‌دهنده و بیم‌دهنده نفرستادیم.');
INSERT INTO "badkhashani" VALUES(25,57,'25:57','بگو: بر آن (ابلاغ قرآن) هیچ مزدی از شما نمی‌خواهم، جز این که هرکس بخواهد (به این وسیله) به‌سوی پروردگارش راهی را در پیش گیرد.');
INSERT INTO "badkhashani" VALUES(25,58,'25:58','و بر آن زنده‌ای توکل کن که هرگز نمی‌میرد و با ستایش او را (به پاکی) یاد کن. و همین که او به گناهان بندگان خود آگاه می‌باشد، کافی است.');
INSERT INTO "badkhashani" VALUES(25,59,'25:59','آن ذاتی که آسمان‌ها و زمین و آنچه را که در میان آن‌هاست در شش روز آفرید، باز (طوری که شایستۀ شکوه و جلال اوست) بر عرش قرار گرفت. او دارای رحمت فراوان است، پس دربارۀ او از فرد دانا بپرس.');
INSERT INTO "badkhashani" VALUES(25,60,'25:60','و چون به کافران گفته شود: برای پروردگار بخشنده سجده کنید، گویند: پروردگار بخشنده چیست؟ آیا ما به چیزی سجده کنیم که تو به ما دستور می‌دهی؟ و این امر بر نفرت و گریزشان افزود.');
INSERT INTO "badkhashani" VALUES(25,61,'25:61','خجسته و بسیار بابرکت است ذاتی که در آسمان برج‌ها آفرید و در آن چراغ (خورشید) و ماه تابان را قرار داد.');
INSERT INTO "badkhashani" VALUES(25,62,'25:62','و اوست ذاتی که شب و روز را جانشین یکدیگر قرار داد، برای کسی که خواهان پند گرفتن یا خواهان شکرگزاری است.');
INSERT INTO "badkhashani" VALUES(25,63,'25:63','و بندگان پروردگار رحمان کسانی‌اند که بر روی زمین متواضع راه می‌روند و چون نادانان ایشان را مخاطب قرار دهند گویند: سلام بر شما.');
INSERT INTO "badkhashani" VALUES(25,64,'25:64','و آنان که شب را با سجده و قیام برای پروردگارشان می‌گذرانند.');
INSERT INTO "badkhashani" VALUES(25,65,'25:65','و آنان که می‌گویند: ای پروردگار ما! عذاب جهنم را از ما بگردان، چون عذابش دایمی و دوامدار است.');
INSERT INTO "badkhashani" VALUES(25,66,'25:66','یقیناً دوزخ بدترین قرارگاه و جایگاه است.');
INSERT INTO "badkhashani" VALUES(25,67,'25:67','و آنان که چون انفاق کنند، نه اسراف می‌کنند و نه بخل می‌ورزند، و شیوۀ‌شان بین این، (دو راه) وسط است.');
INSERT INTO "badkhashani" VALUES(25,68,'25:68','و آنانی که با الله معبود دیگری را نمی‌خوانند و نفسی را که الله (خونش را) حرام کرده است جز به حق نمی‌کشند و زنا نمی‌کنند. و هرکس چنین کند، مجازات آن را می‌بیند.');
INSERT INTO "badkhashani" VALUES(25,69,'25:69','برای او عذاب در روز قیامت دو چندان می‌گردد و خوار و زار و جاودان در آن می‌ماند.');
INSERT INTO "badkhashani" VALUES(25,70,'25:70','مگر کسی که توبه کند و ایمان آورد و عمل صالح انجام دهد، پس ایشان‌اند که الله بدی‌هایشان را به نیکی‌ها تبدیل می‌کند و الله آمرزندۀ مهربان است.');
INSERT INTO "badkhashani" VALUES(25,71,'25:71','و هرکس توبه کند و کار نیک انجام دهد حقا که او به‌سوی الله باز می‌گردد.');
INSERT INTO "badkhashani" VALUES(25,72,'25:72','و آنان که گواهی دروغ نمی‌دهند و چون بر (کار و سخن) بیهوده بگذرند، بزرگوارانه و شرافتمندانه می‌گذرند.');
INSERT INTO "badkhashani" VALUES(25,73,'25:73','و آنان که چون به آیات پروردگارشان پند داده شوند، بر آن کر و کور به سجده نمی‌افتند.');
INSERT INTO "badkhashani" VALUES(25,74,'25:74','و آنان که می‌گویند: ای پروردگار ما! به ما از همسران و فرزندان ما آن را عنایت کن که مایۀ روشنی چشمان باشد. و ما را پیشوای پرهیزگاران بگردان.');
INSERT INTO "badkhashani" VALUES(25,75,'25:75','ایشان‌اند که به خاطر آن که صبر کردند، مقام بلند را می‌یابند و در آن جا به سلام و درود روبرو می‌شوند.');
INSERT INTO "badkhashani" VALUES(25,76,'25:76','در آن جا همیشه می‌مانند، چه قرارگاه خوب و چه مقام نیکو است.');
INSERT INTO "badkhashani" VALUES(25,77,'25:77','بگو: اگر دعای‌تان نباشد، پروردگار من به شما هیچ اعتنایی ندارد، در حقیقت تکذیب کردید و (نتیجۀ بد) آن ملازم شما خواهد بود.');
INSERT INTO "badkhashani" VALUES(26,1,'26:1','طسم. (مراد از این حروف را الله می‌داند).');
INSERT INTO "badkhashani" VALUES(26,2,'26:2','این است آیات کتاب مبین.');
INSERT INTO "badkhashani" VALUES(26,3,'26:3','شاید از این که ایمان نمی‌آورند جان خود را هلاک کنی.');
INSERT INTO "badkhashani" VALUES(26,4,'26:4','اگر ما بخواهیم از آسمان معجزه‌ای بر آنان نازل می‌کنیم که گردن‌هایشان در مقابل آن خم و منقاد گردد.');
INSERT INTO "badkhashani" VALUES(26,5,'26:5','و به آن‌ها هیچ موعظه و اندرز تازه از سوی پروردگار بخشنده نمی‌آید، مگر این که از آن روی‌گردان هستند.');
INSERT INTO "badkhashani" VALUES(26,6,'26:6','به یقین که تکذیب کردند، پس به زودی اخبار چیزی که به آن تمسخر می‌کردند، به آن‌ها خواهد رسید.');
INSERT INTO "badkhashani" VALUES(26,7,'26:7','آیا به زمین ندیده‌اند که چقدر از هرگونه جفت ارزشمندی در آن رویانده‌ایم؟');
INSERT INTO "badkhashani" VALUES(26,8,'26:8','یقیناً در این کار نشانه‌ای است، ولی بیشترشان ایمان‌آرنده نیستند.');
INSERT INTO "badkhashani" VALUES(26,9,'26:9','و البته پروردگار تو همان غالبِ مهربان است.');
INSERT INTO "badkhashani" VALUES(26,10,'26:10','و یادآور شو وقتی که پروردگار تو به موسی ندا کرد (امر کرد) که به نزد آن قوم ظالم برو.');
INSERT INTO "badkhashani" VALUES(26,11,'26:11','قوم فرعون، آیا (از شرک و آمدن عذاب) نمی‌پرهیزند؟');
INSERT INTO "badkhashani" VALUES(26,12,'26:12','(موسی) گفت: ای پروردگارم! من می‌ترسم از این که مرا تکذیب کنند.');
INSERT INTO "badkhashani" VALUES(26,13,'26:13','و دلم (از غم تکذیب آن‌ها) تنگ می‌گردد و زبانم (به سبب لکنت) گشاده نمی‌شود، پس (وحی را) به‌سوی هارون بفرست.');
INSERT INTO "badkhashani" VALUES(26,14,'26:14','و (دیگر این که) آنان بر ذمۀ من گناهی دارند و می‌ترسم که مرا بکشند.');
INSERT INTO "badkhashani" VALUES(26,15,'26:15','الله فرمود: نه، چنین نیست. پس هر دوی شما با آیات ما بروید، البته ما با شما (هستیم و) شنونده‌ایم.');
INSERT INTO "badkhashani" VALUES(26,16,'26:16','پس به نزد فرعون بیایید و بگویید: همانا ما فرستادۀ پروردگار عالمیان هستیم.');
INSERT INTO "badkhashani" VALUES(26,17,'26:17','این که بنی اسرائیل را با ما بفرست.');
INSERT INTO "badkhashani" VALUES(26,18,'26:18','(فرعون) گفت: (ای موسی!) آیا ما تو را در طفولیت میان خود پرورش ندادیم؟ و سال‌ها از عمرت را در بین ما به سر نبردی؟');
INSERT INTO "badkhashani" VALUES(26,19,'26:19','و آن کاری که (با قبطی) انجام دادی از تو سر زد و تو از ناسپاسان هستی.');
INSERT INTO "badkhashani" VALUES(26,20,'26:20','(موسی) گفت: آن را وقتی مرتکب شدم که (به علم و دانایی نبوت) از بی‌خبران بودم.');
INSERT INTO "badkhashani" VALUES(26,21,'26:21','پس وقتی از شما ترسیدم، از شما گریختم، پس پروردگارم به من علم و فهم بخشید و مرا از پیغمبران گردانید.');
INSERT INTO "badkhashani" VALUES(26,22,'26:22','و آیا این نعمتی است که (آن را) بر من منت می‌نهی؟ در حالی که بنی اسرائیل را به بردگی گرفته‌ای.');
INSERT INTO "badkhashani" VALUES(26,23,'26:23','فرعون گفت: و پروردگار عالمیان چیست؟');
INSERT INTO "badkhashani" VALUES(26,24,'26:24','(موسی) گفت: پروردگار آسمان‌ها و زمین و آنچه میان هردو است، اگر یقین‌کنندگانید.');
INSERT INTO "badkhashani" VALUES(26,25,'26:25','(فرعون) به اطرافیان خود گفت: آیا نمی‌شنوید؟ (که موسی چه می‌گوید؟)');
INSERT INTO "badkhashani" VALUES(26,26,'26:26','(موسی) گفت: پروردگار شما و پروردگار نیاکان پیشین شماست.');
INSERT INTO "badkhashani" VALUES(26,27,'26:27','(فرعون) گفت: به یقین پیغمبرتان که به‌سوی شما فرستاده شده، دیوانه است.');
INSERT INTO "badkhashani" VALUES(26,28,'26:28','(موسی) گفت: اوست پروردگار مشرق و مغرب و آنچه میان آنهاست، اگر عقلمند هستید.');
INSERT INTO "badkhashani" VALUES(26,29,'26:29','(فرعون) گفت: اگر غیر از من معبودی اختیار کنی، حتماً تو را از جملۀ زندانیان می‌گردانم.');
INSERT INTO "badkhashani" VALUES(26,30,'26:30','(موسی) گفت: آیا اگر چیز آشکار هم برایت بیاورم؟');
INSERT INTO "badkhashani" VALUES(26,31,'26:31','(فرعون) گفت: اگر (در ادعایت) از راستگویان هستی، پس آن (چیز آشکار) را بیاور.');
INSERT INTO "badkhashani" VALUES(26,32,'26:32','پس موسی عصایش را انداخت و ناگهان اژدهای نمایان شد.');
INSERT INTO "badkhashani" VALUES(26,33,'26:33','و دست خود را از گریبانش بیرون آورد، پس ناگهان آن برای بینندگان سفید و درخشان معلوم شد.');
INSERT INTO "badkhashani" VALUES(26,34,'26:34','(فرعون) به اشراف و بزرگان دور و پیش خود گفت: یقیناً این مرد جادوگر بسیار داناست.');
INSERT INTO "badkhashani" VALUES(26,35,'26:35','می‌خواهد با جادوی خود شما را از دیارتان بیرون کند، اکنون شما چه رأی می‌دهید؟');
INSERT INTO "badkhashani" VALUES(26,36,'26:36','(اشراف قوم) گفتند: او و برادرش را متوقف ساز و افراد را به تمام شهرها بفرست تا جادوگران را جمع کنند.');
INSERT INTO "badkhashani" VALUES(26,37,'26:37','تا هر جادوگر ماهر و دانا را نزد تو بیاورند.');
INSERT INTO "badkhashani" VALUES(26,38,'26:38','پس جادوگران در وعده‌گاه روزی معین جمع کرده شدند.');
INSERT INTO "badkhashani" VALUES(26,39,'26:39','و به مردم گفته شد: آیا شما هم جمع می‌شوید؟');
INSERT INTO "badkhashani" VALUES(26,40,'26:40','تا اگر جادوگران غالب شوند، از آنان پیروی کنیم. ­');
INSERT INTO "badkhashani" VALUES(26,41,'26:41','پس وقتی که جادوگران آمدند، به فرعون گفتند: اگر ما غالب شویم آیا برای ما پاداشی خواهد بود؟');
INSERT INTO "badkhashani" VALUES(26,42,'26:42','(فرعون) گفت: بلی! (پاداش چه بلکه) شما در آن صورت از جمله (درباریان و) نزدیکان من خواهید بود.');
INSERT INTO "badkhashani" VALUES(26,43,'26:43','موسی به آنان گفت: آنچه را که می‌خواهید بیندازید، بیندازید.');
INSERT INTO "badkhashani" VALUES(26,44,'26:44','پس آنان ریسمان‌ها و عصاهای خود را انداختند و گفتند: به عزت فرعون قسم! ما حتما غالب و پیروزیم.');
INSERT INTO "badkhashani" VALUES(26,45,'26:45','پس موسی عصایش را انداخت، ناگهان (اژدهایی شد که) ساخته‌های دروغین آنان را بلعید. ­');
INSERT INTO "badkhashani" VALUES(26,46,'26:46','پس جادوگران سجده‌کنان بر زمین افتادند.');
INSERT INTO "badkhashani" VALUES(26,47,'26:47','گفتند: به پروردگار جهانیان ایمان آوردیم.');
INSERT INTO "badkhashani" VALUES(26,48,'26:48','پروردگار موسی و هارون.');
INSERT INTO "badkhashani" VALUES(26,49,'26:49','(فرعون) گفت: آیا پیش از اینکه به شما اجازه دهم به او ایمان آوردید؟ البته او بزرگترتان است که به شما جادو آموخته است. پس به زودی خواهید دانست، حتما دست‌ها و پاهای شما را بر خلاف جهت همدیگر خواهم برید و همۀ شما را به دار خواهم کشید.');
INSERT INTO "badkhashani" VALUES(26,50,'26:50','گفتند: باکی نیست، چون که ما به‌سوی پروردگار خود باز می‌گردیم.');
INSERT INTO "badkhashani" VALUES(26,51,'26:51','البته ما امیدواریم که پروردگارما گناهان ما را به خاطر اینکه نخستین ایمان‌آورندگان بوده‌ایم، بیامرزد.');
INSERT INTO "badkhashani" VALUES(26,52,'26:52','و به موسی وحی کردیم که بندگانم را در شب ببر، شما حتما تعقیب می‌شوید.');
INSERT INTO "badkhashani" VALUES(26,53,'26:53','پس فرعون جمع‌کنندگان (مردم) را به شهرها فرستاد.');
INSERT INTO "badkhashani" VALUES(26,54,'26:54','(و گفت:) البته این‌ها گروه اندک و ناچیز هستند.');
INSERT INTO "badkhashani" VALUES(26,55,'26:55','و به يقين که آنها ما را به خشم آورده‌اند.');
INSERT INTO "badkhashani" VALUES(26,56,'26:56','و در حالیکه ما گروه بیدار و آماده هستیم.');
INSERT INTO "badkhashani" VALUES(26,57,'26:57','پس ما آنان (فرعون و فرعونیان) را از میان باغ‌ها و چشمه‌ها بیرون کردیم.');
INSERT INTO "badkhashani" VALUES(26,58,'26:58','و (ایشان را) از گنج‌ها و منزلگاه‌های پرناز و نعمت بیرون کردیم.');
INSERT INTO "badkhashani" VALUES(26,59,'26:59','این چنین (آنها را به عذاب خود گرفتار کردیم) و آنها را به بنی اسرائیل به میراث دادیم.');
INSERT INTO "badkhashani" VALUES(26,60,'26:60','پس فرعون و لشکر او، موسی و بنی اسرائیل را در وقت طلوع آفتاب تعقیب کردند.');
INSERT INTO "badkhashani" VALUES(26,61,'26:61','پس چون هر دو گروه یکدیگر را دیدند، یاران موسی گفتند: يقينا ما گرفتار می‌شویم.');
INSERT INTO "badkhashani" VALUES(26,62,'26:62','(موسی) گفت: چنین نیست، زیرا پروردگارم با من است، به زودی مرا راهنمایی خواهد کرد.');
INSERT INTO "badkhashani" VALUES(26,63,'26:63','پس به موسی وحی کردیم که با عصایت به بحر بزن، پس (بحر) شگافت و هر بخشی مانند کوه بزرگ گردید.');
INSERT INTO "badkhashani" VALUES(26,64,'26:64','و دیگران را (نیز) در آنجا نزدیک کردیم.');
INSERT INTO "badkhashani" VALUES(26,65,'26:65','و موسی و همه همراهانش را نجات دادیم.');
INSERT INTO "badkhashani" VALUES(26,66,'26:66','باز دیگران را غرق کردیم.');
INSERT INTO "badkhashani" VALUES(26,67,'26:67','بی‌گمان در این (رویداد) نشانه‌ای است، و بیشترشان مؤمن نبودند.');
INSERT INTO "badkhashani" VALUES(26,68,'26:68','و یقینا پروردگارت توانا و مهربان است.');
INSERT INTO "badkhashani" VALUES(26,69,'26:69','و خبر ابراهیم را برای‌شان بخوان.');
INSERT INTO "badkhashani" VALUES(26,70,'26:70','وقتی که به پدر و قوم خود گفت: شما چه را می‌پرستید؟');
INSERT INTO "badkhashani" VALUES(26,71,'26:71','گفتند: بت‌ها را می‌پرستیم، و همیشه بر عبادت‌شان معتکف می‌باشیم.');
INSERT INTO "badkhashani" VALUES(26,72,'26:72','ابراهیم گفت: آیا وقتی که آنها را می‌خوانید سخن شما را می‌شنوند؟');
INSERT INTO "badkhashani" VALUES(26,73,'26:73','یا به شما فائده یا ضرری می‌رسانند؟');
INSERT INTO "badkhashani" VALUES(26,74,'26:74','گفتند: نه، بلکه پدران خود را دیدیم که چنین می‌کردند.');
INSERT INTO "badkhashani" VALUES(26,75,'26:75','ابراهیم گفت: آیا آنچه را که شما می‌پرستید، دیده‌اید (در آن تأمل کرده‌اید).');
INSERT INTO "badkhashani" VALUES(26,76,'26:76','و (آنچه را) پدران پیشین‌تان می‌پرستیدند؟');
INSERT INTO "badkhashani" VALUES(26,77,'26:77','پس بدانید که همۀ آنها دشمن من‌اند، غیر از پروردگار جهانیان.');
INSERT INTO "badkhashani" VALUES(26,78,'26:78','آن ذاتی که مرا آفریده است، پس او مرا هدایت می‌کند.');
INSERT INTO "badkhashani" VALUES(26,79,'26:79','آن ذاتی که مرا (غذا) می‌خوراند و (آشامیدنی) می‌نوشاند.');
INSERT INTO "badkhashani" VALUES(26,80,'26:80','و چون بیمار شوم، پس وی مرا شفا می‌دهد.');
INSERT INTO "badkhashani" VALUES(26,81,'26:81','و آن ذاتی که مرا می‌میراند، باز زنده‌ام می‌گرداند.');
INSERT INTO "badkhashani" VALUES(26,82,'26:82','و آن ذاتی که امیدوارم در روز جزا خطایم را بیامرزد.');
INSERT INTO "badkhashani" VALUES(26,83,'26:83','ای پروردگارم! به من حكم (علم و نبوت) ببخش و مرا با صالحان ملحق بگردان.');
INSERT INTO "badkhashani" VALUES(26,84,'26:84','و برای من در میان آیندگان نام نیک بگذار.');
INSERT INTO "badkhashani" VALUES(26,85,'26:85','و مرا از جملۀ وارثان جنت پرناز و نعمت بگردان.');
INSERT INTO "badkhashani" VALUES(26,86,'26:86','و پدرم را بیامرز، زیرا او از گمراهان بود.');
INSERT INTO "badkhashani" VALUES(26,87,'26:87','و مرا رسوا مکن روزی که (مردم) برانگیخته می‌شوند.');
INSERT INTO "badkhashani" VALUES(26,88,'26:88','روزی که مال و اولاد (هیچ) سودی نمی‌رسانند.');
INSERT INTO "badkhashani" VALUES(26,89,'26:89','مگر کسی (نجات می‌یابد و سود می‌بیند) که با دل پاک (از شرک و نفاق) نزد الله بیاید.');
INSERT INTO "badkhashani" VALUES(26,90,'26:90','و (در آن روز) جنت برای پرهیزگاران نزدیک آورده شود.');
INSERT INTO "badkhashani" VALUES(26,91,'26:91','و دوزخ برای گمراهان آشکار گردانیده شود.');
INSERT INTO "badkhashani" VALUES(26,92,'26:92','و به آنها گفته می‌شود: کجا هستند معبودانی که (آنها را) عبادت می‌کردید؟');
INSERT INTO "badkhashani" VALUES(26,93,'26:93','(معبودهایی) غیر از الله، آیا شما را مدد می‌کنند یا خودشان مدد می‌شوند؟');
INSERT INTO "badkhashani" VALUES(26,94,'26:94','پس، آنان و گمراهان یکی بعد از دیگری در آن (دوزخ) انداخته می‌شوند.');
INSERT INTO "badkhashani" VALUES(26,95,'26:95','و (نیز) لشکریان ابلیس همه (در دوزخ انداخته می‌شوند).');
INSERT INTO "badkhashani" VALUES(26,96,'26:96','آنان در آنجا (با معبودان خود) به نزاع می‌پردازند و می‌گویند:');
INSERT INTO "badkhashani" VALUES(26,97,'26:97','قسم به الله ما در گمراهی آشکار بودیم.');
INSERT INTO "badkhashani" VALUES(26,98,'26:98','آن وقت که شما را با پروردگار جهانیان برابر می‌ساختیم.');
INSERT INTO "badkhashani" VALUES(26,99,'26:99','و ما را گمراه نساخت مگر مجرمان.');
INSERT INTO "badkhashani" VALUES(26,100,'26:100','(اکنون) ما هیچ شفاعت‌کننده‌ای نداریم.');
INSERT INTO "badkhashani" VALUES(26,101,'26:101','و هیچ دوست صمیمی و دلسوزی (نداریم).');
INSERT INTO "badkhashani" VALUES(26,102,'26:102','پس ای کاش برای ما یکبار برگشت (به دنیا) بود تا از جملۀ مؤمنان می‌شدیم.');
INSERT INTO "badkhashani" VALUES(26,103,'26:103','البته در این (داستان ابراهيم) نشانه‌ای است، و بیشترشان مؤمن نبودند.');
INSERT INTO "badkhashani" VALUES(26,104,'26:104','و البته پروردگارت غالب (و) مهربان است.');
INSERT INTO "badkhashani" VALUES(26,105,'26:105','قوم نوح پیغمبران را تکذیب کردند.');
INSERT INTO "badkhashani" VALUES(26,106,'26:106','چون برادرشان نوح به آنان گفت: آیا از الله نمی‌ترسید؟');
INSERT INTO "badkhashani" VALUES(26,107,'26:107','يقينا من برایتان پیغمبر امین هستم.');
INSERT INTO "badkhashani" VALUES(26,108,'26:108','پس از الله بترسید و از من اطاعت کنید.');
INSERT INTO "badkhashani" VALUES(26,109,'26:109','من بر این (دعوت) هیچ مزدی از شما نمی‌خواهم، مزد من نیست مگر بر عهدۀ پروردگار جهانیان.');
INSERT INTO "badkhashani" VALUES(26,110,'26:110','پس از الله بترسید و از من اطاعت کنید.');
INSERT INTO "badkhashani" VALUES(26,111,'26:111','گفتند: آیا به تو ایمان بیاوریم حال آنکه فرومایگان از تو پیروی کرده‌اند؟');
INSERT INTO "badkhashani" VALUES(26,112,'26:112','نوح گفت: من از آنچه آنها می‌کنند چه می‌دانم؟');
INSERT INTO "badkhashani" VALUES(26,113,'26:113','حساب ایشان جز بر عهدۀ پروردگارم نیست اگر بدانید.');
INSERT INTO "badkhashani" VALUES(26,114,'26:114','و من طرد کنندۀ مؤمنان از خود نیستم.');
INSERT INTO "badkhashani" VALUES(26,115,'26:115','من جز بیم‌دهندۀ آشکار نیستم.');
INSERT INTO "badkhashani" VALUES(26,116,'26:116','گفتند: ای نوح! اگر (از دعوتت) دست برنداری، البته از جمله سنگسار شدگان خواهی بود.');
INSERT INTO "badkhashani" VALUES(26,117,'26:117','گفت: ای پروردگارم! همانا قومم مرا تکذیب کردند.');
INSERT INTO "badkhashani" VALUES(26,118,'26:118','پس میان من و آنها به حکمی فیصله کن و من و هر کس از مؤمنان را که با من است، نجات ده.');
INSERT INTO "badkhashani" VALUES(26,119,'26:119','پس او و آنانی را که با وی بودند در کشتی پر و گرانبار نجات دادیم.');
INSERT INTO "badkhashani" VALUES(26,120,'26:120','باز باقی ماندگان را غرق کردیم.');
INSERT INTO "badkhashani" VALUES(26,121,'26:121','البته در این (داستان) نشانه‌ای است، و اکثر‌شان مؤمن نبودند.');
INSERT INTO "badkhashani" VALUES(26,122,'26:122','واقعا پروردگارت غالب مهربان است.');
INSERT INTO "badkhashani" VALUES(26,123,'26:123','(قوم) عاد پیغمبران را تکذیب کردند.');
INSERT INTO "badkhashani" VALUES(26,124,'26:124','وقتی که برادرشان هود به ایشان گفت: آیا از الله نمی‌ترسید.');
INSERT INTO "badkhashani" VALUES(26,125,'26:125','البته من برای شما پیغمبر امین هستم.');
INSERT INTO "badkhashani" VALUES(26,126,'26:126','پس از الله بترسید و از من اطاعت کنید.');
INSERT INTO "badkhashani" VALUES(26,127,'26:127','و در مقابل دعوت خود مزدی از شما نمی‌طلبم و مزد من جز بر عهدۀ پروردگار جهانیان نیست.');
INSERT INTO "badkhashani" VALUES(26,128,'26:128','آیا بر هر بلندیی بنایی می‌سازید که (در آن) به عبث و بیهوده‌کاری مشغول شوید؟');
INSERT INTO "badkhashani" VALUES(26,129,'26:129','و قصرها و قلعه‌های محکم می‌سازید، به امید آنکه همیشه بمانید؟');
INSERT INTO "badkhashani" VALUES(26,130,'26:130','و چون (کسی را) مؤاخذه کنید، ظالمانه مؤاخذه می‌کنید.');
INSERT INTO "badkhashani" VALUES(26,131,'26:131','پس از الله بترسید و از من اطاعت کنید.');
INSERT INTO "badkhashani" VALUES(26,132,'26:132','و از آن ذاتی بترسید که شما را به آنچه خود می‌دانید، مدد کرده است.');
INSERT INTO "badkhashani" VALUES(26,133,'26:133','شما را با دادن چهارپایان و فرزندان مدد کرده است.');
INSERT INTO "badkhashani" VALUES(26,134,'26:134','و (نیز با دادن) باغ‌ها و چشمه‌ها (شما را مدد کرد).');
INSERT INTO "badkhashani" VALUES(26,135,'26:135','البته من از عذاب روزی بزرگ بر شما بیمناکم.');
INSERT INTO "badkhashani" VALUES(26,136,'26:136','گفتند: بر ما برابر است، خواه به ما پند دهی یا از پند دهندگان نباشی.');
INSERT INTO "badkhashani" VALUES(26,137,'26:137','این (تکذیب و طغيانگری) جز شیوۀ پیشینیان نیست.');
INSERT INTO "badkhashani" VALUES(26,138,'26:138','و ما عذاب داده نمی‌شویم.');
INSERT INTO "badkhashani" VALUES(26,139,'26:139','پس (قوم عاد)، هود را تکذیب کردند، در نتیجه آنها را هلاک کردیم. بدون شک در این نشانه است، و اکثر‌شان مؤمن نبودند.');
INSERT INTO "badkhashani" VALUES(26,140,'26:140','و البته پروردگارت همان غالبِ مهربان است.');
INSERT INTO "badkhashani" VALUES(26,141,'26:141','(قوم) ثمود نیز پیغمبران را تکذیب کردند.');
INSERT INTO "badkhashani" VALUES(26,142,'26:142','وقتی که برادرشان صالح به آنها گفت: آیا (از الله) نمی‌ترسید؟');
INSERT INTO "badkhashani" VALUES(26,143,'26:143','البته من برای شما پیغمبر امین هستم.');
INSERT INTO "badkhashani" VALUES(26,144,'26:144','پس از الله بترسید و از من اطاعت کنید.');
INSERT INTO "badkhashani" VALUES(26,145,'26:145','و بر تبلیغ این رسالت از شما مزد و پاداشی نمی‌خواهم، مزد و پاداش من جز بر عهدۀ پروردگار جهانیان نیست.');
INSERT INTO "badkhashani" VALUES(26,146,'26:146','آیا در آنچه (از نعمت‌ها که) اینجاست ایمن (از عذاب و مرگ) گذاشته می‌شوید؟');
INSERT INTO "badkhashani" VALUES(26,147,'26:147','در (میان) باغ‌ها و (در کنار) چشمه‌سارها؟');
INSERT INTO "badkhashani" VALUES(26,148,'26:148','و کشتزارها و نخلستان‌هایی که میوه و شگوفۀ آن‌تر و تازه است؟');
INSERT INTO "badkhashani" VALUES(26,149,'26:149','و با مهارت از کوه‌ها خانه‌هایی برای خود می‌تراشید.');
INSERT INTO "badkhashani" VALUES(26,150,'26:150','پس از الله بترسید و از من اطاعت کنید.');
INSERT INTO "badkhashani" VALUES(26,151,'26:151','و از فرمان اسراف کاران اطاعت مکنید.');
INSERT INTO "badkhashani" VALUES(26,152,'26:152','آنان که در زمین فساد می‌کنند و اصلاح نمی‌کنند.');
INSERT INTO "badkhashani" VALUES(26,153,'26:153','گفتند: جز این نیست که تو از جملۀ جادوشده‌گانی.');
INSERT INTO "badkhashani" VALUES(26,154,'26:154','تو انسانی مگر مانند ما نیستی، پس اگر از راستگویانی معجزه‌ای بیاور.');
INSERT INTO "badkhashani" VALUES(26,155,'26:155','صالح گفت: این ماده شتر است که یک نوبت آب خوردن برای اوست و روزی معین نوبت آب شماست.');
INSERT INTO "badkhashani" VALUES(26,156,'26:156','و به آن آسیب نرسانید، (که در آن صورت) عذاب روز بزرگ شما را فرا خواهد گرفت.');
INSERT INTO "badkhashani" VALUES(26,157,'26:157','پس آن شتر را کشتند، باز پشیمان شدند.');
INSERT INTO "badkhashani" VALUES(26,158,'26:158','پس عذاب آنان را فرا گرفت. البته در این نشانه است، ولی بیشتر‌شان ایمان‌آرنده نبودند.');
INSERT INTO "badkhashani" VALUES(26,159,'26:159','و البته پروردگارت غالب مهربان است.');
INSERT INTO "badkhashani" VALUES(26,160,'26:160','قوم لوط پیغمبران را تکذیب کردند.');
INSERT INTO "badkhashani" VALUES(26,161,'26:161','وقتی برادرشان لوط به آنها گفت: آیا از الله نمی‌ترسید؟');
INSERT INTO "badkhashani" VALUES(26,162,'26:162','البته من برای شما پیغمبر امین هستم.');
INSERT INTO "badkhashani" VALUES(26,163,'26:163','پس از الله بترسید و از من اطاعت کنید.');
INSERT INTO "badkhashani" VALUES(26,164,'26:164','و بر تبلیغ این رسالت از شما مزد و پاداشی نمی‌خواهم، مزد و پاداش من جز بر عهدۀ پروردگار جهانیان نیست.');
INSERT INTO "badkhashani" VALUES(26,165,'26:165','آیا از (فطرت پاک) مردم جهان (مخالفت می‌کنید و زنان را گذاشته) به سراغ مردان می‌روید.');
INSERT INTO "badkhashani" VALUES(26,166,'26:166','و همسران را که پروردگارتان برای شما آفریده است، می‌گزارید؟ بلکه شما مردم تجاوزکارید.');
INSERT INTO "badkhashani" VALUES(26,167,'26:167','گفتند: ای لوط! اگر (از عیبجویی ما) دست برنداری، حتما از بیرون رانده‌شدگان خواهی بود.');
INSERT INTO "badkhashani" VALUES(26,168,'26:168','(لوط) گفت: من از دشمنان (این) کارتان هستم.');
INSERT INTO "badkhashani" VALUES(26,169,'26:169','ای پروردگارم! مرا و خانواده‌ام را از شر آنچه می‌کنند نجات ده.');
INSERT INTO "badkhashani" VALUES(26,170,'26:170','پس او و خانواده‌اش همگی را یکجا نجات دادیم.');
INSERT INTO "badkhashani" VALUES(26,171,'26:171','به جز پیرزنی که در جملۀ باقی‌ماندگان (در عذاب) بود.');
INSERT INTO "badkhashani" VALUES(26,172,'26:172','باز دیگران را هلاک کردیم.');
INSERT INTO "badkhashani" VALUES(26,173,'26:173','و بر آنان باران سخت باراندیم، پس چه بد بود باران بیم داده شدگان.');
INSERT INTO "badkhashani" VALUES(26,174,'26:174','حقا که در این (عقوبت) نشانه‌ایست، و بیشتر‌شان ایمان‌آرنده نبودند.');
INSERT INTO "badkhashani" VALUES(26,175,'26:175','و البته پروردگارت همان غالب مهربان است.');
INSERT INTO "badkhashani" VALUES(26,176,'26:176','اصحاب ایکه (جنگل) پیغمبران را تکذیب کردند.');
INSERT INTO "badkhashani" VALUES(26,177,'26:177','وقتی که شعیب به آنان گفت: آیا (از الله) نمی‌ترسید؟');
INSERT INTO "badkhashani" VALUES(26,178,'26:178','البته من برای شما پیغمبر امین هستم.');
INSERT INTO "badkhashani" VALUES(26,179,'26:179','پس از الله بترسید و از من اطاعت کنید.');
INSERT INTO "badkhashani" VALUES(26,180,'26:180','و بر تبلیغ این رسالت از شما مزد و پاداشی نمی‌خواهم، مزد و پاداش من جز بر عهدۀ پروردگار جهانیان نیست.');
INSERT INTO "badkhashani" VALUES(26,181,'26:181','(شعیب گفت:) پیمانه را به تمام و کمال دهید و از کم‌کنندگان مباشید.');
INSERT INTO "badkhashani" VALUES(26,182,'26:182','و با ترازوی برابر و درست وزن کنید.');
INSERT INTO "badkhashani" VALUES(26,183,'26:183','و به مردم اشیای‌شان راکم ندهید و در زمین فسادکار نگردید.');
INSERT INTO "badkhashani" VALUES(26,184,'26:184','و بترسید از ذاتی که شما و امت‌های گذشته را آفریده است.');
INSERT INTO "badkhashani" VALUES(26,185,'26:185','گفتند: تو حتما از جملۀ جادوشدگانی.');
INSERT INTO "badkhashani" VALUES(26,186,'26:186','و تو جز بشری مانند ما نیستی، و یقینا تو را از دروغگویان می‌پنداریم.');
INSERT INTO "badkhashani" VALUES(26,187,'26:187','اگر از راستگویانی، پس قطعه‌ای از آسمان را بر ما بیفگن.');
INSERT INTO "badkhashani" VALUES(26,188,'26:188','(شعيب) گفت: پروردگارم به آنچه می‌کنید داناتر است.');
INSERT INTO "badkhashani" VALUES(26,189,'26:189','پس او را تکذیب کردند و عذاب روز ابر (آتشبار) آنان را فرو گرفت، البته آن عذاب روزی سخت و بزرگ بود.');
INSERT INTO "badkhashani" VALUES(26,190,'26:190','البته در این (هلاک ساختن) نشانه است. و بیشترشان ایمان‌آرنده نبودند.');
INSERT INTO "badkhashani" VALUES(26,191,'26:191','و يقيناً پروردگار تو غالب (و) مهربان است.');
INSERT INTO "badkhashani" VALUES(26,192,'26:192','و البته این (قرآن) فرو فرستادۀ پروردگار جهانیان است.');
INSERT INTO "badkhashani" VALUES(26,193,'26:193','که جبرئیل آن را فرود آورده است.');
INSERT INTO "badkhashani" VALUES(26,194,'26:194','(فرود آورده) بر دل تو تا از بيم‌دهندگان باشی،');
INSERT INTO "badkhashani" VALUES(26,195,'26:195','(آن قرآن) به زبان عربی واضح و روشن');
INSERT INTO "badkhashani" VALUES(26,196,'26:196','و البته (وصف) آن در صحیفه‌های پیشینیان آمده است.');
INSERT INTO "badkhashani" VALUES(26,197,'26:197','آیا برایشان نشانۀ (حقانیت قرآن) نیست که علمای بنی اسرائیل آن را می‌دانستند؟');
INSERT INTO "badkhashani" VALUES(26,198,'26:198','و اگر آن را بر بعضی از عجمیان نازل می‌کردیم.');
INSERT INTO "badkhashani" VALUES(26,199,'26:199','باز آن را برایشان می‌خواند، به آن ایمان نمی‌آوردند.');
INSERT INTO "badkhashani" VALUES(26,200,'26:200','ما این چنین آن را در دل‌های مجرمان راه دادیم.');
INSERT INTO "badkhashani" VALUES(26,201,'26:201','که به آن ایمان نمی‌آورند، تا آن که عذاب دردناک را ببینند.');
INSERT INTO "badkhashani" VALUES(26,202,'26:202','پس عذاب به طور ناگهانی بر سر آنان می‌آید، در حالیکه بی‌خبرند.');
INSERT INTO "badkhashani" VALUES(26,203,'26:203','پس می‌گویند: آیا به ما مهلت داده می‌شود؟');
INSERT INTO "badkhashani" VALUES(26,204,'26:204','پس آیا عذاب ما را به شتاب می‌طلبند؟');
INSERT INTO "badkhashani" VALUES(26,205,'26:205','مگر نمی‌دانی که اگر سال‌ها آنها را (مهلت دهیم و از نعمت‌ها) برخوردار سازیم.');
INSERT INTO "badkhashani" VALUES(26,206,'26:206','باز آنچه که به آن وعده داده می‌شوند، به ایشان برسد.');
INSERT INTO "badkhashani" VALUES(26,207,'26:207','آن برخورداریشان چیزی را از آنان دفع نمی‌کند؟ ­');
INSERT INTO "badkhashani" VALUES(26,208,'26:208','و (اهل) هیچ قریه را نابود نکردیم مگر اینکه ترسانندگانی (از پیامبران و نائبان آنها) داشتند.');
INSERT INTO "badkhashani" VALUES(26,209,'26:209','برای پند دادن و ما ظلم‌کننده نبودیم.');
INSERT INTO "badkhashani" VALUES(26,210,'26:210','و این قرآن را شیاطین فرود نیاورده‌اند.');
INSERT INTO "badkhashani" VALUES(26,211,'26:211','و سزاوارشان نیست، و نمی‌توانند (آن را نازل کنند).');
INSERT INTO "badkhashani" VALUES(26,212,'26:212','و چون آنها از شنیدن (قرآن و سخن فرشتگان) دور و محروم‌اند.');
INSERT INTO "badkhashani" VALUES(26,213,'26:213','پس با الله معبود دیگری را به دعا مخوان که از عذاب‌شدگان خواهی بود.');
INSERT INTO "badkhashani" VALUES(26,214,'26:214','و خویشاوندان نزدیکت را (از عذاب) بترسان.');
INSERT INTO "badkhashani" VALUES(26,215,'26:215','و بال مهربانی و شفقت خود را برای مؤمنانی که از تو پیروی می‌کنند، بگستران.');
INSERT INTO "badkhashani" VALUES(26,216,'26:216','پس اگر از تو نافرمانی کردند، بگو: البته من از آنچه می‌کنید بیزارم.');
INSERT INTO "badkhashani" VALUES(26,217,'26:217','و بر توانای مهربان توکل کن.');
INSERT INTO "badkhashani" VALUES(26,218,'26:218','آن ذاتی که چون (برای نماز شب) می‌ایستی، تو را می‌بیند.');
INSERT INTO "badkhashani" VALUES(26,219,'26:219','و (همچنین می‌بیند) حرکت (قیام و رکوع و نشست و برخاست) تو را در میان سجده‌کنندگان.');
INSERT INTO "badkhashani" VALUES(26,220,'26:220','يقينا او شنوای داناست.');
INSERT INTO "badkhashani" VALUES(26,221,'26:221','آیا شما را خبر دهم که شیاطین بر چه کسى نازل می‌شوند؟');
INSERT INTO "badkhashani" VALUES(26,222,'26:222','بر هر دروغگوی گناهکار نازل می‌شوند.');
INSERT INTO "badkhashani" VALUES(26,223,'26:223','گوش می‌دهند (به سخن فرشتگان) و بیشترشان دروغگو‌اند.');
INSERT INTO "badkhashani" VALUES(26,224,'26:224','و شاعران را گمراهان پیروی می‌کنند.');
INSERT INTO "badkhashani" VALUES(26,225,'26:225','آیا ندیدی که آنان در هر وادیی حیران و سرگردانند؟');
INSERT INTO "badkhashani" VALUES(26,226,'26:226','و آنها کسانی‌اند که چیزهایی می‌گویند که خود انجام نمی‌دهند.');
INSERT INTO "badkhashani" VALUES(26,227,'26:227','مگر آنان که ایمان آورده‌اند و کارهای نیک انجام داده‌اند و الله را بسیار یاد کرده‌اند و پس از آنکه مورد ظلم قرار گرفته‌اند، انتقام گرفته‌اند. و زود است که ظالمان بدانند که به کدام بازگشتگاه باز می‌گردند.');
INSERT INTO "badkhashani" VALUES(27,1,'27:1','طس. (معنای این حروف به الله معلوم است)، این آیه‌های قرآن و کتاب روشنگر است.');
INSERT INTO "badkhashani" VALUES(27,2,'27:2','هدایت و مژده‌ای برای مؤمنان است.');
INSERT INTO "badkhashani" VALUES(27,3,'27:3','آنان که نماز را برپا می‌کنند و زکات را می‌دهند و ایشان به آخرت یقین دارند.');
INSERT INTO "badkhashani" VALUES(27,4,'27:4','البته کسانی که به آخرت ایمان نمی‌آورند، کارهایشان را در نظر‌شان آراسته‌ایم، پس آنان (در گمراهی خود) متردد و حیران‌اند.');
INSERT INTO "badkhashani" VALUES(27,5,'27:5','آنان کسانی‌اند که برای‌شان عذاب بد است، و ایشان در آخرت زیانکارترین (مردم) می‌باشند.');
INSERT INTO "badkhashani" VALUES(27,6,'27:6','و یقینا تو قرآن را از سوی پروردگار حکیم و دانا دریافت می‌کنی.');
INSERT INTO "badkhashani" VALUES(27,7,'27:7','به یادآور وقتی که موسی به اهلش گفت: من آتشی دیدم، (به زودی) برای شما خبری یا شعله‌ای از آن می‌آورم تا خود را گرم کنید.');
INSERT INTO "badkhashani" VALUES(27,8,'27:8','پس چون موسی به نزدیک آن آمد، صدا کرده شد: بابرکت و خجسته گردید آن که در آتش است، و (نیز) آن که اطراف آن است، و الله پاک و منزه است، پروردگار جهانیان.');
INSERT INTO "badkhashani" VALUES(27,9,'27:9','(گفتیم) ای موسی! بدان که من الله، ذات توانای حکیم هستم.');
INSERT INTO "badkhashani" VALUES(27,10,'27:10','و عصای خود را بینداز و چون آن را دید که گویی ماری است که حرکت می‌کند، به عقب بازگشت و پشت خود را نظر نکرد. (فرمودیم) ای موسی! نترس، چونکه پیغمبران در پیشگاه من نمی‌ترسند.');
INSERT INTO "badkhashani" VALUES(27,11,'27:11','مگر کسی که ستم کرده باشد، باز بدی را به نیکی تبدیل کند، پس (بداند که) يقينا من آمرزندۀ مهربانم.');
INSERT INTO "badkhashani" VALUES(27,12,'27:12','و دست خود را در گریبانت داخل کن تا سفیدِ درخشان بی‌عیب بیرون آید. (این) از جملۀ نُه نشانه (است که به تو خواهیم داد) به‌سوی فرعون و قوم او (برو)، زیرا آنان قوم فاسقند.');
INSERT INTO "badkhashani" VALUES(27,13,'27:13','پس چون معجزه‌های ما به وضوح و روشنی (که سبب بصیرت و دانایی بود) به آنان رسید، گفتند: این جادوی آشکار است.');
INSERT INTO "badkhashani" VALUES(27,14,'27:14','و معجزات را در حالیکه دل‌هایشان به آن باور داشت، از روی ظلم و تکبر انکار کردند. پس بنگر که عاقبت فسادکاران چطور شد؟.');
INSERT INTO "badkhashani" VALUES(27,15,'27:15','و البته به داود و سلیمان (نیز) علم و دانش عطا کردیم و آن دو گفتند: ستایش الله راست که ما را بر بسیاری از بندگان مؤمنش برتری داد.');
INSERT INTO "badkhashani" VALUES(27,16,'27:16','و سلیمان (در علم و نبوت) وارث داود گردید، و گفت: ای مردم! به ما زبان پرندگان آموخته شده است، و از همۀ چیزها به ما داده شده است. یقینا این فضیلت آشکار است.');
INSERT INTO "badkhashani" VALUES(27,17,'27:17','و برای سلیمان لشکریانش از جن و انس و پرندگان جمع کرده شدند، پس (در صف‌های منظم) توقف داده شدند.');
INSERT INTO "badkhashani" VALUES(27,18,'27:18','تا آنکه به وادی مورچگان رسیدند، مورچه‌ای گفت: ای مورچگان! به لانه‌های خود در آیید تا سليمان و لشکریانش نادیده و نادانسته شما را پایمال نکنند.');
INSERT INTO "badkhashani" VALUES(27,19,'27:19','سلیمان از سخن آن مورچه تبسم کرد و گفت: ای پروردگارم! به من توفیق ده تا شکر نعمتی را که به من و پدرم عطا کرده‌ای بجا آورم، و (به من توفیق ده) تا کار نیک انجام دهم که آن را بپسندی، و مرا به رحمت خود در جملۀ بندگان صالحت درآور.');
INSERT INTO "badkhashani" VALUES(27,20,'27:20','و (سلیمان) جویای حال پرندگان شد و گفت: چرا هدهد را نمی‌بینم، یا اینکه از غایبان است؟');
INSERT INTO "badkhashani" VALUES(27,21,'27:21','حتماً او را به عذاب سخت سزا خواهم داد، یا سر او را می‌برم، یا (باید) دلیل آشکار برای من بیاورد (که چرا غایب بود).');
INSERT INTO "badkhashani" VALUES(27,22,'27:22','دیری نگذشت (که هدهد برگشت) و گفت: من از چیزی آگاهی یافتم که تو از آن آگاه نیستی و از (مردم) سبأ برایت خبری یقینی آورده‌ام.');
INSERT INTO "badkhashani" VALUES(27,23,'27:23','من زنی را یافتم که بر آنان فرمانروایی می‌کند و از همه چیز داده شده است و تخت بس بزرگ دارد.');
INSERT INTO "badkhashani" VALUES(27,24,'27:24','او و قومش را چنین یافتم که به جای الله به آفتاب سجده می‌کنند، و شیطان کارهایشان را برایشان آراسته است، و آنان را از راه (حق) بازداشته است. لذا آنها راهیاب نمی‌شوند.');
INSERT INTO "badkhashani" VALUES(27,25,'27:25','(شیطان این کار را برای‌شان آراست) تا سجده نکنند برای پروردگاری که نهان را در آسمان‌ها و زمین آشکار می‌کند و آنچه را پنهان می‌دارید و آنچه را آشکار می‌سازید می‌داند.');
INSERT INTO "badkhashani" VALUES(27,26,'27:26','الله آن ذاتی است که هیچ معبود بر حق به جز او نیست و او پروردگار عرش بزرگ است.');
INSERT INTO "badkhashani" VALUES(27,27,'27:27','(سلیمان به هدهد) گفت: به زودی خواهیم دید که آیا راست گفته‌ای یا از دروغ‌گویان بوده‌ای.');
INSERT INTO "badkhashani" VALUES(27,28,'27:28','(پس) این نامۀ مرا ببر و آن را به‌سوی آنان بینداز، باز از ایشان دور شو، و بنگر که چه (جواب) باز می‌دهند؟');
INSERT INTO "badkhashani" VALUES(27,29,'27:29','(پس از رسیدنِ نامه، ملكه بلقیس) گفت: ای بزرگان! البته نامۀ ارجمندی به‌سوی من انداخته شده است.');
INSERT INTO "badkhashani" VALUES(27,30,'27:30','آن نامه از سوی سلیمان است و (سرآغاز) آن چنین است: بنام الله بخشندۀ مهربان');
INSERT INTO "badkhashani" VALUES(27,31,'27:31','(مضمون نامه این است که ای اهل سبا!) بر من تكبر نورزید و تسلیم شده به نزد من آیید.');
INSERT INTO "badkhashani" VALUES(27,32,'27:32','(ملکه) گفت: ای سران قوم! در کارم به من نظر دهید، من هیچ کاری را بدون حضور و نظر شما انجام نداده‌ام.');
INSERT INTO "badkhashani" VALUES(27,33,'27:33','گفتند: ما دارای قوت و شوکت هستیم و ما صاحبان کارزار سختیم. ولی اختیار کار در دست توست. پس بنگر که چه فرمان می‌دهی.');
INSERT INTO "badkhashani" VALUES(27,34,'27:34','ملکه گفت: چون بادشاهان (فاتحانه) در شهری داخل شوند (پس) آن را ویران و برباد می‌کنند، و عزیز‌ترین مردمانش را خوارترین می‌گردانند، و اینگونه عمل می‌کنند.');
INSERT INTO "badkhashani" VALUES(27,35,'27:35','و من هدیه‌ای به‌سوی‌شان خواهم فرستاد تا ببینم فرستادگان با چه جواب باز می‌گردند.');
INSERT INTO "badkhashani" VALUES(27,36,'27:36','پس چون (فرستاده) به نزد سلیمان آمد، (سلیمان به او) گفت: آیا مرا به مال مدد می‌کنید؟ پس بدانید آنچه الله به من عطا کرده است، بهتر است از آنچه به شما داده است، بلکه این شما هستید که به هدیۀ خود شادمان و خورسند می‌شوید.');
INSERT INTO "badkhashani" VALUES(27,37,'27:37','(سلیمان به فرستاده گفت:) به‌سوی آنان باز گرد، ما حتما با لشکری به سوی‌شان خواهیم آمد که آنان توان مقابله با آن را ندارند، و ایشان را از آنجا با حالت خوار و زبون بیرون خواهیم کرد.');
INSERT INTO "badkhashani" VALUES(27,38,'27:38','(سلیمان) گفت: ای بزرگان! کدام یک از شما تخت او را - پیش از آنکه از در تسلیم نزد من آیند - برایم می‌آورد؟');
INSERT INTO "badkhashani" VALUES(27,39,'27:39','عفریتی (تنومندی) از جن گفت: من آن را پیش از آنکه از مجلست برخیزی برایت می‌آورم، و من بر آن (کار) توانا و امین هستم.');
INSERT INTO "badkhashani" VALUES(27,40,'27:40','کسی که نزد او علم و دانشی از کتاب الهی بود، گفت: من آن را پیش از چشم بر هم زدنت نزد تو می‌آورم. پس چون (سلیمان) تخت را پیش خود مستقر دید، گفت: این از فضل پروردگار من است، تا مرا بیازماید که آیا شکر می‌گزارم یا ناشکری می‌کنم؟ و هر کس شکر گزارد تنها به سود خود شکر می‌ورزد، و هر کس کفران (نعمت) کند، پس بدون شک پروردگارم بی‌نیاز کریم است.');
INSERT INTO "badkhashani" VALUES(27,41,'27:41','(سلیمان) گفت: تخت او را در نظرش ناشناس و دگرگون سازید، تا ببینیم که آیا پی می‌برد یا از آنانی می‌شود که راهیاب نمی‌گردند؟');
INSERT INTO "badkhashani" VALUES(27,42,'27:42','پس چون آمد، گفته شد: آیا تخت تو این چنین است؟. گفت: گویا این همان است. و پیش از این به ما علم داده شده بود و ما فرمانبردار و منقاد بودیم.');
INSERT INTO "badkhashani" VALUES(27,43,'27:43','و آنچه را که به غیر از الله پرستش می‌کرد، او را (از عبادت الله) باز داشته بود، چون او از جملۀ قوم کافر بود.');
INSERT INTO "badkhashani" VALUES(27,44,'27:44','به او (بلقیس) گفته شد: به قصر وارد شو، وقتی که آن را دید گمان کرد که حوض است، و دامن را از ساق‌هایش برکشید. (سلیمان) گفت: این قصری صاف و ساده از آبگینه‌هاست. گفت: پروردگارا! من به خود ظلم کردم و اکنون همرای سلیمان تسلیم پروردگار جهانیان شدم.');
INSERT INTO "badkhashani" VALUES(27,45,'27:45','و يقينا به‌سوی قوم ثمود برادرشان صالح را فرستادیم، که الله را بپرستید. پس ناگهان آنها به دو فريق متخاصم (کافر و مؤمن) تقسیم شدند.');
INSERT INTO "badkhashani" VALUES(27,46,'27:46','(صالح) گفت: ای قوم من! چرا بدی (عذاب) را پیش از نیکی (رحمت) به شتاب می‌طلبید؟ چرا از الله آمرزش نمی‌خواهید تا مورد رحمت قرار گیرید؟');
INSERT INTO "badkhashani" VALUES(27,47,'27:47','گفتند: ما به تو و به کسانی که با تو هستند، فال بد گرفته‌ایم. گفت: فال بدتان نزد الله است، بلکه شما گروهی هستید که آزموده می‌شوید.');
INSERT INTO "badkhashani" VALUES(27,48,'27:48','و در آن شهر نه نفر بودند که در زمین فساد می‌کردند و هیچ اصلاح نمی‌کردند.');
INSERT INTO "badkhashani" VALUES(27,49,'27:49','گفتند: به یکدیگر قسم بخورید (عهد ببندید) که بر صالح و خانواده‌اش در شب یورش می‌آوریم، باز به ولی او می‌گوییم: ما در وقت هلاکت خانواده‌اش حاضر نبودیم و البته ما راستگوییم.');
INSERT INTO "badkhashani" VALUES(27,50,'27:50','و حیلۀ بس‌بزرگ سنجیدند و ما هم (برای هلاکت آنها و نجات صالح) تدبیر نمودیم، در حالیکه آنها خبر نداشتند.');
INSERT INTO "badkhashani" VALUES(27,51,'27:51','پس بنگر سرانجام حیله‌سازی آنان چگونه بود؛ ما آنان و قوم‌شان را همگی نابود کردیم.');
INSERT INTO "badkhashani" VALUES(27,52,'27:52','پس این خانه‌های آنان است که به سبب ظلم‌شان به زمین افتاده است. بی‌گمان در این (ماجرا) برای مردمی که می‌دانند، دلیل و نشانه‌ای است.');
INSERT INTO "badkhashani" VALUES(27,53,'27:53','و کسانی را که ایمان آوردند و تقوا پیشه کردند، نجات دادیم.');
INSERT INTO "badkhashani" VALUES(27,54,'27:54','و لوط را (به یادآور) وقتی که به قوم خود گفت: آیا عمل فاحشه را انجام می‌دهید در حالیکه (قباحت آنرا) می‌دانید؟');
INSERT INTO "badkhashani" VALUES(27,55,'27:55','آیا شما برای شهوترانی، به جای زنان به سراغ مردان می‌آئید؟ بلکه شما قومی هستید که (عقوبت این جریمه را) نمی‌دانید.');
INSERT INTO "badkhashani" VALUES(27,56,'27:56','پس جواب قومش جز آن نبود که گفتند: خانواده (و پیروان) لوط را از دیار خود بیرون کنید که آنان مردم پاکدامن (و بیزار از ناپاکی‌ها) هستند.');
INSERT INTO "badkhashani" VALUES(27,57,'27:57','پس او و خانواده‌اش را نجات دادیم، به جز همسرش که او را از جملۀ باقیماندگان (در عذاب) مقدر کردیم.');
INSERT INTO "badkhashani" VALUES(27,58,'27:58','و بر آنان باران سخت (از سنگ) باراندیم، پس چه بد است باران بیم داده شدگان.');
INSERT INTO "badkhashani" VALUES(27,59,'27:59','بگو: ستایش برای الله است. و سلام بر آن بندگانش که (آنان را) برگزیده است. آیا الله بهتر است یا چیزهایی که (با الله) شریک می‌سازند؟');
INSERT INTO "badkhashani" VALUES(27,60,'27:60','یا کیست که آسمان‌ها و زمین را آفریده است و از آسمان آبی برایتان فرود آورده است، پس به وسیلۀ آن باغ‌های زیبا رویانیدیم در حالی که شما نمی‌توانستید درختانش را برویانید. آیا معبودی با الله هست؟ نه، بلکه در حقیقت آنها گروه کجرو هستند.');
INSERT INTO "badkhashani" VALUES(27,61,'27:61','یا کیست که زمین را قرارگاه گردانید و در میان آن نهرها قرار داد و برای آن کوه‌های پابرجا و استوار آفرید و میان دو دریا برزخ و پرده‌ای قرار داد؟ آیا معبودی با الله هست؟ نه، بلکه بیشترشان نمی‌دانند.');
INSERT INTO "badkhashani" VALUES(27,62,'27:62','یا کیست که دعای درمانده را - چون او را به دعا بخواند - اجابت می‌کند، و سختی را دور می‌کند، و شما را جانشین زمین می‌سازد. آیا معبودی با الله هست؟! شما بسیار کم پند می‌گیرید.');
INSERT INTO "badkhashani" VALUES(27,63,'27:63','یا کیست که شما را در تاریکی‌های بیابان و دریا هدایت می‌کند، و کیست که بادها را پیشاپیش رحمتش مژده‌دهنده می‌فرستد. آیا معبودی دیگر با الله هست؟ الله برتر است از آنچه شریک وی می‌سازند.');
INSERT INTO "badkhashani" VALUES(27,64,'27:64','یا کیست که آفریدن را آغاز می‌کند، باز آن را باز می‌گرداند؟ و چه کسی از آسمان و زمین به شما روزی می‌دهد؟ آیا معبودی با الله هست؟ بگو: دلیل و برهان خود را بیاورید، اگر راست می‌گویید.');
INSERT INTO "badkhashani" VALUES(27,65,'27:65','بگو: هرکه در آسمان‌ها و زمین است، جز الله غيب را نمی‌داند. و نمی‌دانند چه وقتی برانگیخته می‌شوند.');
INSERT INTO "badkhashani" VALUES(27,66,'27:66','بلکه علم ایشان دربارۀ آخرت نارسا است (و در آخرت تکامل خواهد یافت)، بلکه آنان در (وقوع) آن در شک هستند، بلکه آنان در مورد آن کورند.');
INSERT INTO "badkhashani" VALUES(27,67,'27:67','و کافران گفتند: آیا چون ما و پدران ما خاک گردیم، آیا بیرون آورده می‌شویم؟');
INSERT INTO "badkhashani" VALUES(27,68,'27:68','در واقع این را به ما و پیش از ما به پدران ما نیز وعده داده‌اند، این جز افسانه‌های گذشتگان نیست.');
INSERT INTO "badkhashani" VALUES(27,69,'27:69','بگو: در زمین بگردید، پس ببینید که سرانجام مجرمان چگونه شد؟');
INSERT INTO "badkhashani" VALUES(27,70,'27:70','و بر (كفر) آنان غم و اندوه مخور. و از آنچه مکر می‌ورزند، دل تنگ مباش.');
INSERT INTO "badkhashani" VALUES(27,71,'27:71','و می‌گویند: این وعده کی خواهد بود، اگر راستگویید؟');
INSERT INTO "badkhashani" VALUES(27,72,'27:72','بگو: شاید بعضی از آنچه را که به شتاب می‌طلبید، به شما نزدیک شده باشد.');
INSERT INTO "badkhashani" VALUES(27,73,'27:73','و البته پروردگارت بر مردم دارای فضل (فراوان) است، ولی بیشترشان شکر نمی‌گزارند.');
INSERT INTO "badkhashani" VALUES(27,74,'27:74','و يقينا پروردگارت می‌داند آنچه را در دل‌هایشان پنهان می‌کنند و آنچه را که آشکار می‌سازند.');
INSERT INTO "badkhashani" VALUES(27,75,'27:75','و هیچ پوشیده‌ای در آسمان و زمین نیست مگر اینکه در کتاب روشن (درج شده) است.');
INSERT INTO "badkhashani" VALUES(27,76,'27:76','یقینا این قرآن برای بنی اسرائیل بیشتر آنچه را که در آن اختلاف می‌کنند، بیان می‌کند.');
INSERT INTO "badkhashani" VALUES(27,77,'27:77','و البته آن (قرآن) برای کسانی که ایمان آورده‌اند، هدایت و رحمت است.');
INSERT INTO "badkhashani" VALUES(27,78,'27:78','به یقین پروردگارت به حکم خود در میان آنها فیصله خواهد کرد. و او غالبِ دانا است.');
INSERT INTO "badkhashani" VALUES(27,79,'27:79','پس بر الله توکل کن، زیرا تو بر حق آشکار هستی.');
INSERT INTO "badkhashani" VALUES(27,80,'27:80','البته تو نمی‌توانی به مردگان بشنوانی، و نمی‌توانی به کران آواز بشنوانی هنگامی‌که پشت‌کرده روی بگردانند.');
INSERT INTO "badkhashani" VALUES(27,81,'27:81','و تو نمی‌توانی نابینایان (حق) را از گمراهی‌شان (به‌سوی حق) هدایت کنی، تو تنها کسی را می‌توانی بشنوانی که به آیات ما ایمان می‌آورد، پس آنها منقاد و فرمانبردارند.');
INSERT INTO "badkhashani" VALUES(27,82,'27:82','و چون فرمان (عذاب) بر آنان تحقق یابد، جانوری را از زمین برای آنها بیرون می‌آوریم که با ایشان سخن می‌گوید، چرا که مردم به آیات ما یقین نداشتند.');
INSERT INTO "badkhashani" VALUES(27,83,'27:83','و آن روز (را به یادآور) که از هر امت، گروهی از آنان را که آیات ما را تکذیب می‌کردند، محشور کنیم. پس آنان توقف داده می‌شوند تا با هم دیگر یکجا شوند.');
INSERT INTO "badkhashani" VALUES(27,84,'27:84','تا آنکه همگی آمدند (الله) می‌فرماید: آیا آیات مرا تکذیب کردید درحالیکه در مورد آن از نظر علم احاطه نداشتید؟ یا (در طول زندگی‌تان) چه می‌کردید؟');
INSERT INTO "badkhashani" VALUES(27,85,'27:85','و به سبب ظلمی که کرده بودند، فرمان (عذاب الهی) بر آنان واقع شد، پس آنان (نمی‌توانند) سخن بگویند.');
INSERT INTO "badkhashani" VALUES(27,86,'27:86','آیا ندیدند که ما شب را آفریدیم تا در آن آرام گیرند، و روز را روشن ساختیم؟ البته در این کار برای قومی که ایمان می‌آوردند، علامات (قدرت الهی) است.');
INSERT INTO "badkhashani" VALUES(27,87,'27:87','و یادآور شو روزی که در صور دمیده می‌شود، پس آنانی که در آسمان‌ها و زمین‌اند هراسان شوند، به جز کسی‌که الله (امن‌شان را) بخواهد. و همگی عاجزانه به دربار او آیند.');
INSERT INTO "badkhashani" VALUES(27,88,'27:88','و کوه‌ها را می‌بینی (و) آنها را ساکن می‌پنداری، حال آنکه مانند ابرها در حرکت‌اند. این آفرینش الله است که همه چیز را استوار ساخت. یقینا او به آنچه می‌کنید باخبر است.');
INSERT INTO "badkhashani" VALUES(27,89,'27:89','(پس) هرکس که (به دربار الله) نیکی آورد، پاداشِ بهتر از آن خواهد داشت. و آنان از هراس آن روز ایمن‌اند.');
INSERT INTO "badkhashani" VALUES(27,90,'27:90','و هرکس (به دربار الله) بدی آورد، پس چهره‌هایشان در آتش نگونسار می‌شود. (و به ایشان گفته می‌شود) آیا جز در مقابل آنچه می‌کردید سزا داده می‌شوید؟');
INSERT INTO "badkhashani" VALUES(27,91,'27:91','جز این نیست که به من فرمان داده شده است که پروردگار این شهر (مکه) را بپرستم، پروردگاری که آن را حرام قرار داده است و همه چیز از اوست. و به من فرمان داده شده که از جملۀ تسلیم شدگان باشم.');
INSERT INTO "badkhashani" VALUES(27,92,'27:92','و (به من فرمان داده شده است) اینکه قرآن را بخوانم، پس هرکس راه یابد، جز این نیست که به نفع خود راه می‌یابد. و هرکس گمراه شود، بگو: من فقط از بیم دهندگان می‌باشم.');
INSERT INTO "badkhashani" VALUES(27,93,'27:93','و بگو: ستایش برای الله است، به زودی آیاتش را به شما نشان خواهد داد، پس آنها را خواهید شناخت. و پروردگارت از آنچه می‌کنید غافل نیست.');
INSERT INTO "badkhashani" VALUES(28,1,'28:1','طسم. (معنای این حروف به الله معلوم است).');
INSERT INTO "badkhashani" VALUES(28,2,'28:2','این است آیات کتاب (که خیر و برکت آن) روشن (است).');
INSERT INTO "badkhashani" VALUES(28,3,'28:3','بخشی از خبر موسی و فرعون را برای قومی که ایمان دارند، به حق بر تو می‌خوانیم.');
INSERT INTO "badkhashani" VALUES(28,4,'28:4','البته فرعون در زمین (مصر) تکبر ورزید، و مردمان آنجا را گروه گروه کرد، طایفه‌ای از ایشان را ضعیف و ناتوان می‌کرد. پسرانشان را می‌کشت، و زنانشان را زنده نگاه می‌داشت، بی‌گمان او از فسادکاران بود.');
INSERT INTO "badkhashani" VALUES(28,5,'28:5','و می‌خواهیم بر آنان که در زمین ضعیف و ناتوان قرار داده شده بودند، منت بگذاریم و ایشان را پیشوایان بگردانیم و (نیز) آنها را وارثان (سلطنت) بگردانیم.');
INSERT INTO "badkhashani" VALUES(28,6,'28:6','و ایشان را در زمین اقتدار (سلطنت و حکومت) می‌بخشیم، و به فرعون و هامان و لشکریان آن دو؛ آنچه را که از آن می‌ترسیدند، نشان دهیم.');
INSERT INTO "badkhashani" VALUES(28,7,'28:7','و به مادر موسی الهام کردیم که به او شیر بده. و هنگامی که بر او ترسیدی، او را (در صندوق گذاشته و) به دریا بینداز، و مترس و غمگین مباش، به یقین ما او را به نزد تو باز می‌گردانیم و او را از جملۀ پیغمبران می‌گردانیم.');
INSERT INTO "badkhashani" VALUES(28,8,'28:8','پس خانوادۀ فرعون او را گرفتند تا سرانجام دشمن آنان و سبب غم و اندوه‌شان گردد. یقینا فرعون و هامان و لشکریان آن دو خطاکار بودند.');
INSERT INTO "badkhashani" VALUES(28,9,'28:9','و همسر فرعون گفت: او برای من و برای تو نور چشم خواهد بود، او را نکشید، شاید به ما سود بخشد، و یا او را به فرزندی بگیریم و آنان خبر نداشتند (که عاقبت امر دشمن‌شان خواهد شد).');
INSERT INTO "badkhashani" VALUES(28,10,'28:10','و دل مادر موسی تهی (از صبر و قرار) شد، و اگر دل او را استوار نمی‌ساختیم تا از جملۀ یقین‌کنندگان (به وعدۀ الله) باشد، نزدیک بود که راز او را آشکار سازد.');
INSERT INTO "badkhashani" VALUES(28,11,'28:11','و (مادر موسی) به خواهرش گفت: در پی او برو. پس خواهرش او را از دور می‌دید، در حالی که آنان نمی‌دانستند.');
INSERT INTO "badkhashani" VALUES(28,12,'28:12','و شیر همۀ دایه‌ها را از قبل بر او حرام ساختیم، پس (خواهرش) گفت: آیا شما را به خانواده‌ای راهنمایی کنم که برای شما پرورش او را به عهده بگیرند، در حالیکه خیرخواه او باشند؟');
INSERT INTO "badkhashani" VALUES(28,13,'28:13','پس موسی را به مادرش باز گردانیدیم تا چشمش روشن شود، و غمگین نگردد، و بداند که وعدۀ الله حق است. ولی بیشترشان نمی‌دانند.');
INSERT INTO "badkhashani" VALUES(28,14,'28:14','و چون موسی به کمال رشد خود رسید و اندام او کامل شد، به او حکمت و دانش دادیم. و این چنین به نیکوکاران پاداش می‌دهیم.');
INSERT INTO "badkhashani" VALUES(28,15,'28:15','و روزی که مردم شهر غافل بودند وارد آن شد و دید که دو مرد می‌جنگند، یکی از قوم او، و آن دیگری از دشمنانش بود. آن که از قومش بود، از موسی علیه آنکه از دشمنش بود کمک خواست، و (موسی) مشتی به او زد و او را کشت. گفت این از کار شیطان است، چون او دشمن گمراه کنندۀ آشکار است.');
INSERT INTO "badkhashani" VALUES(28,16,'28:16','موسی گفت: ای پروردگارم! من به خود ظلم کردم، پس مرا بیامرز. و الله او را بخشید، یقینا او آمرزندۀ مهربان است.');
INSERT INTO "badkhashani" VALUES(28,17,'28:17','موسی گفت: ای پروردگارم! به سبب نعمت‌هایی که به من داده‌ای، من هرگز مددگار مجرمان نخواهم بود.');
INSERT INTO "badkhashani" VALUES(28,18,'28:18','و شب را ترسان و نگران در شهر روز کرد، ناگهان (دید) همان کسی که دیروز از او مدد خواسته بود، او را فریاد می‌کند. موسی به او گفت: بی‌گمان تو گمراه آشکاری.');
INSERT INTO "badkhashani" VALUES(28,19,'28:19','پس چون (موسی) خواست به شخصی که دشمن هردوی آنان بود، دست دراز کند، گفت: آیا می‌خواهی مرا بکشی، طوری که دیروز کسی را کشتی؟ تو می‌خواهی در زمین ستمگر باشی، و نمی‌خواهی از نیکوکاران باشی.');
INSERT INTO "badkhashani" VALUES(28,20,'28:20','و (در این اثنا) مردی از آخر شهر شتابان آمد، گفت: ای موسی! سران حکومت دربارۀ تو مشورت می‌کنند تا تو را بکشند، پس بیرون برو، یقینا من از خیرخواهان تو هستم.');
INSERT INTO "badkhashani" VALUES(28,21,'28:21','پس موسی از شهر بیرون شد، درحالیکه ترسان و نگران بود. گفت: پروردگارا! مرا از قوم ظالم نجات ده.');
INSERT INTO "badkhashani" VALUES(28,22,'28:22','و وقتی‌که به طرف مدین متوجه شد، گفت: امید است که پروردگارم مرا به راه راست هدایت نماید.');
INSERT INTO "badkhashani" VALUES(28,23,'28:23','و هنگامی که به آب مدین رسید، بر آن گروهی از مردم را دید که به (مواشی) آب می‌دهند، و آن طرف دیگر دو زن را یافت که مواشی خود را باز می‌دارند. گفت: منظورتان از این کار چیست؟ گفتند: ما (مواشی خود را) آب نمی‌دهیم تا اینکه چوپانان (مواشی‌شان را) باز گردانند، و پدر ما پیرمرد بزرگ سال است.');
INSERT INTO "badkhashani" VALUES(28,24,'28:24','پس موسی به (مواشی) آن دو آب داد، باز به سایه بازگشت و گفت: ای پروردگارم! من به آنچه از خیر که به سویم فرود آوردی، محتاجم.');
INSERT INTO "badkhashani" VALUES(28,25,'28:25','پس یکی از آن دو دختر درحالیکه با حیا گام برمی‌داشت به نزد او آمد (و) گفت: پدرم تو را دعوت می‌کند، تا مزد آب دادنِ (مواشی ما) را به تو دهد. پس چون موسی نزد او آمد و داستان را برای او حکایت کرد، گفت: نترس که از مردمان ظالم نجات یافتی.');
INSERT INTO "badkhashani" VALUES(28,26,'28:26','یکی از آن دو (دختر) گفت: ای پدر من! او را اجیر بگیر، بی‌گمان بهترین کسی است که به خدمت‌‌گیری (زیرا) هم شخص توانا (و هم) امین است.');
INSERT INTO "badkhashani" VALUES(28,27,'28:27','(آن مرد کهن سال) گفت: من می‌خواهم یکی از این دو دخترانم را به نکاح تو درآورم، به شرط اینکه هشت سال خدمت مرا کنی، پس اگر ده سال را تمام کنی پس از (احسان) توست. من نمی‌خواهم (بر تو سخت‌گیرم و) تو را در مشقت اندازم. اگر الله بخواهد مرا از نیکان خواهی یافت.');
INSERT INTO "badkhashani" VALUES(28,28,'28:28','(موسی) گفت: این پیمان میان من و تو است، البته هرکدام از این دو مدّت را ادا کردم، نباید تجاوزی بر من صورت گیرد. و الله بر آنچه می‌گوییم وکیل و گواه است.');
INSERT INTO "badkhashani" VALUES(28,29,'28:29','و چون موسی آن مدت را به پایان رسانید و با اهل خود روان شد، از سوی (کوه) طور آتشی را دید. به اهل خود گفت: بایستید که من آتشی را دیده‌ام، امیدوارم از آنجا خبری یا شعله‌ای از آتش برایتان بیاورم تا خود را (به آن) گرم کنید.');
INSERT INTO "badkhashani" VALUES(28,30,'28:30','پس وقتی نزدیک آتش آمد، از کنارۀ راست وادی، در جایگاه بابرکت، از درخت آواز کرده شد که ای موسی! من الله پروردگار جهانیانم.');
INSERT INTO "badkhashani" VALUES(28,31,'28:31','و عصای خود را بینداز، پس چون آنرا دید که حرکت می‌کند گویی مار است، موسی پشت گردانید و بازنگشت. (گفتیم) برگرد و نترس، یقینا تو از جملۀ کسانی هستی که در امانند.');
INSERT INTO "badkhashani" VALUES(28,32,'28:32','دستت را به گریبانت درآور تا سفید (و) بی‌عیب بیرون آید. و بخاطر دور کردن ترس، دست‌هایت را به‌سوی خود جمع کن این دو دلیل از سوی پروردگارت برای فرعون و اشراف (قوم) اوست. چون آنان قوم نافرمانِ بدکار بودند.');
INSERT INTO "badkhashani" VALUES(28,33,'28:33','(موسی) گفت: ای پروردگارم! من کسی از آنان را کشته‌ام، پس می‌ترسم که مرا بکشند.');
INSERT INTO "badkhashani" VALUES(28,34,'28:34','و برادرم هارون از من فصیح‌تر است، پس او را با من بفرست تا مددگار من باشد و مرا تصدیق نماید. چون من می‌ترسم از آنکه مرا تکذیب کنند.');
INSERT INTO "badkhashani" VALUES(28,35,'28:35','(الله) فرمود: ما بازوی تو را به وسیلۀ برادرت (هارون) تقویت و نیرومند خواهیم کرد و به شما سلطه و برتری خواهیم داد. پس آنان به شما دست یافته نمی‌توانند، به سبب معجزات ما، شما و کسانی که از شما پیروی می‌کنند غالب خواهید شد.');
INSERT INTO "badkhashani" VALUES(28,36,'28:36','پس وقتی موسی با معجزات روشن و آشکار ما به نزد آنان آمد، گفتند: این جز جادوی خودساخته نیست. و در میان پدران نخستین خود چنین چیزی را نشنیده‌ایم.');
INSERT INTO "badkhashani" VALUES(28,37,'28:37','و موسی گفت: پروردگار من داناتر است که چه کسی هدایت را از سوی او آورده است و چه کسی سرانجام نیک سرای آخرت را دارد، بی‌گمان ظالمان کامیاب نمی‌شوند.');
INSERT INTO "badkhashani" VALUES(28,38,'28:38','و فرعون گفت: ای سران و بزرگان قوم! من جز خودم معبودی را برای شما نمی‌شناسم! پس ای هامان! برای من بر گِل آتش بیافروز (و آنرا پخته کن) باز برای من برج بزرگی بساز، شاید من از معبود موسی اطلاع حاصل نمایم. و به راستی من او را از دروغگویان می‌پندارم.');
INSERT INTO "badkhashani" VALUES(28,39,'28:39','و فرعون و لشکر او به ناحق تکبر ورزیدند و گمان کردند که آنان به‌سوی ما بازگردانیده نمی‌شوند.');
INSERT INTO "badkhashani" VALUES(28,40,'28:40','پس او و سپاهیانش را گرفتیم، پس آنان را در دریا افگندیم. پس بنگر سرانجام ظالمان چگونه بود؟');
INSERT INTO "badkhashani" VALUES(28,41,'28:41','و آنان را پیشوایانی گردانیدیم که به‌سوی آتش (دوزخ) دعوت می‌دادند، و روز قیامت یاری نمی‌شوند.');
INSERT INTO "badkhashani" VALUES(28,42,'28:42','و در این دنیا لعنت را از عقب‌شان فرستادیم و روز قیامت هم از جملۀ طرد شدگان هستند.');
INSERT INTO "badkhashani" VALUES(28,43,'28:43','و البته به موسی کتاب آسمانی دادیم، بعد از آنکه اقوام نخستین را نابود کردیم، تا برای مردم سبب بصیرت و وسیله هدایت و رحمت باشد و تا ایشان یادآورد شوند و عبرت گیرند.');
INSERT INTO "badkhashani" VALUES(28,44,'28:44','و (ای محمد) تو در جانب غربی (کوه طور) نبودی، آن دم که امر نبوت را به موسی ابلاغ کردیم. و تو از حاضرین نبودی.');
INSERT INTO "badkhashani" VALUES(28,45,'28:45','و ليكن ما نسل‌هایی بوجود آوردیم، پس عمرشان طولانی شد. و تو در میان اهل مدين مقیم نبودی تا آیات ما را بر آنان بخوانی، لیکن ما فرستنده بودیم.');
INSERT INTO "badkhashani" VALUES(28,46,'28:46','و تو در کنار کوه طور نبودی وقتی که ما ندا دادیم، ولی رحمتی از سوی پروردگارت (آمد) تا قومی را بیم دهی که قبل از تو بیم دهنده‌ای به سوی‌شان نیامده بود، تا آنان پند بپذیرند.');
INSERT INTO "badkhashani" VALUES(28,47,'28:47','اگر (پیش از فرستادن پیغمبر) مصیبتی به خاطر اعمال‌شان به آنان می‌رسید، پس می‌گفتند: ای پروردگار ما! چرا به‌سوی ما پیغمبری نفرستادی تا از آیات تو پیروی می‌کردیم و از مؤمنان می‌بودیم؟');
INSERT INTO "badkhashani" VALUES(28,48,'28:48','پس چون حق از جانب ما به سوی‌شان آمد، گفتند: چرا مانند آنچه که به موسی داده شد به او داده نشده است؟ آیا به آنچه پیش از این به موسی داده شد کفر نورزیده‌اند؟ گفتند: این دو (قرآن و تورات) جادویی‌اند که یکدیگر را تأیید می‌کنند. و گفتند: ما همه را انکار می‌کنیم.');
INSERT INTO "badkhashani" VALUES(28,49,'28:49','بگو: پس اگر راست می‌گویید، کتابی از نزد الله بیاورید که از این دو (کتاب) هدایت کننده‌تر باشد، تا از آن پیروی کنم.');
INSERT INTO "badkhashani" VALUES(28,50,'28:50','پس اگر دعوت تو را نپذیرفتند (و ایمان نیاوردند)، بدان که تنها از خواهشات خود پیروی می‌کنند. و کیست گمراه‌تر از کسی که بدون راهنمایی از (سوی) الله، از هوی و هوس خود پیروی می‌کند؟! يقينا الله مردم ظالم را هدایت نمی‌کند.');
INSERT INTO "badkhashani" VALUES(28,51,'28:51','و البته (به وسیلۀ تورات و قرآن) سخن هدایت را به آنها پی در پی رسانیدیم، تا عبرت گیرند.');
INSERT INTO "badkhashani" VALUES(28,52,'28:52','آنانی را که پیش از این (قرآن) کتاب داده‌ایم به آن ایمان می‌آورند.');
INSERT INTO "badkhashani" VALUES(28,53,'28:53','و چون (قرآن) بر آنان خوانده شود، می‌گویند: به آن ایمان آوردیم، چون آن حق است و از سوی پروردگار ماست. ما پیش از (نزول) آن نیز مسلمان (منقاد و فرمانبردار) بودیم.');
INSERT INTO "badkhashani" VALUES(28,54,'28:54','ایشان به خاطر آنکه صبر کردند، اجر‌شان دو بار به آنان داده می‌شود و به خاطر آنکه بدی را با نیکی دفع می‌کنند. و از آنچه به آنان روزی داده‌ایم انفاق می‌کنند.');
INSERT INTO "badkhashani" VALUES(28,55,'28:55','و چون سخن لغو را بشنوند، از آن روی می‌گردانند و می‌گویند: اعمال ما برای ماست، و اعمال شما برای شماست. سلام بر شما ما خواهان (همنشینی با) جاهلان نیستیم.');
INSERT INTO "badkhashani" VALUES(28,56,'28:56','(ای پیامبر!) تو کسی را که دوست داری نمی‌توانی هدایت کنی، بلکه الله هر کس را که بخواهد هدایت می‌کند، و او به هدایت شدگان داناتر است');
INSERT INTO "badkhashani" VALUES(28,57,'28:57','و گفتند: اگر همراه تو از هدایت پیروی کنیم، از سرزمین خود ربوده می‌شویم. آیا آنان را در حرم امن جای ندادیم، که ثمرات هر چیزی به عنوان روزی از جانب ما به‌سوی آن رسانده می‌شود؟ لیکن بیشترشان نمی‌دانند.');
INSERT INTO "badkhashani" VALUES(28,58,'28:58','و چه بسیار قریه‌ها را نابود کردیم که زندگی خوش، آنها را مست و مغرور کرده بود. پس این خانه‌های ایشان است که بعد از آنان جز اندکی (در آن) سکونت نشده بود و ما خود ما وارث (دیار آنان) شدیم.');
INSERT INTO "badkhashani" VALUES(28,59,'28:59','و پروردگار تو هرگز هلاک کنندۀ شهرها نبوده است، مگر اینکه در اصل و مرکز آن پیغمبری بفرستد که آیات ما را بر آنان بخواند. و ما نابود کنندۀ قریه‌ها نیستیم مگر اینکه اهالی آنجا ظالم باشند.');
INSERT INTO "badkhashani" VALUES(28,60,'28:60','و هر آنچه داده شده‌اید، بهرۀ زندگانی دنیا و زینت آن است. ولی آنچه نزد الله است، بهتر و پاینده‌تر است. پس آیا از عقل‌تان کار نمی‌گیرید؟');
INSERT INTO "badkhashani" VALUES(28,61,'28:61','آیا کسی که او را به وعدۀ نیک وعده داده‌ایم و او دریابندۀ آن است، مانند کسی است که متاع زندگی این جهان را به او داده‌ایم، باز او روز قیامت از احضار شدگان (در عذاب) باشد؟');
INSERT INTO "badkhashani" VALUES(28,62,'28:62','و یادآور شو روزی را که (الله) آنان را ندا داده و می‌فرماید: کجایند آن شریکانی که آنها را (شفاعت‌گر) گمان می‌کردید؟');
INSERT INTO "badkhashani" VALUES(28,63,'28:63','کسانی که حكم عذاب بر آنها ثابت شده است می‌گویند: ای پروردگار ما! ایشان کسانی‌اند که گمراه‌شان کردیم، آنان را چنانکه خود گمراه بودیم گمراه کردیم. ما از آنها به‌سوی تو بیزاری می‌جوییم، ما را عبادت نمی‌کردند (بلکه خواهشات خود را عبادت می‌کردند).');
INSERT INTO "badkhashani" VALUES(28,64,'28:64','و گفته می‌شود: شریکان‌تان را (برای نجات) بخوانید، پس آنها را می‌خوانند، ولی (ندای)‌شان را پاسخ نمی‌دهند. و عذاب را می‌بینند، (و آرزو می‌کنند)ای کاش راه یافته می‌بودند.');
INSERT INTO "badkhashani" VALUES(28,65,'28:65','و یادآور شو روزی که (الله) آنها را ندا می‌دهد و می‌فرماید: به پیغمبران ما چه پاسخ دادید؟');
INSERT INTO "badkhashani" VALUES(28,66,'28:66','پس خبرها در آن روز بر آنان پوشیده می‌گردد و (از شدت دهشت) نمی‌توانند چیزی را از یکدیگر بپرسند.');
INSERT INTO "badkhashani" VALUES(28,67,'28:67','و اما کسی که توبه کرده و ایمان آورده و کار نیک انجام داده باشد، پس امید است که از جملۀ رستگاران گردد.');
INSERT INTO "badkhashani" VALUES(28,68,'28:68','و پروردگار تو هر چه را بخواهد می‌آفریند و (هر کس را بخواهد) اختیار می‌کند، و اختیاری برای آنان نیست. پاک است الله و بالاتر از آن است که برایش شریک قرار می‌دهند.');
INSERT INTO "badkhashani" VALUES(28,69,'28:69','و پروردگارت می‌داند آنچه را سینه‌هایشان پنهان می‌دارد و آنچه را آشکار می‌سازند.');
INSERT INTO "badkhashani" VALUES(28,70,'28:70','و اوست «الله»، معبودی به حق جز او نیست، ستایش در اول و آخر برای اوست و حکم در اختیار اوست و به‌سوی او باز گردانیده می‌شوید.');
INSERT INTO "badkhashani" VALUES(28,71,'28:71','بگو: آیا دیده‌اید اگر الله شب را بر شما تا روز قیامت پیوسته و همیشه بگرداند، کیست معبودی غیر از الله که برایتان روشنی بیاورد؟ آیا (حق را) نمی‌شنوید؟');
INSERT INTO "badkhashani" VALUES(28,72,'28:72','بگو: آیا دیده‌اید اگر الله روز را تا قیامت بر شما پیوسته و همیشه بگرداند، به جز الله معبودی است که بتواند برای شما شب را بیاورد تا در آن آرام گیرید؟ آیا نمی‌بینید؟');
INSERT INTO "badkhashani" VALUES(28,73,'28:73','و از رحمتش (به شماست که) شب و روز را برایتان آفرید تا در آن آرام گیرید و تا از فضل او روزی بجوئید و تا باشد که شکر بگزارید.');
INSERT INTO "badkhashani" VALUES(28,74,'28:74','و روزی که آنان را ندا میدهد، پس می‌گوید: آن شريكان من که شما می‌پنداشتید، کجایند (تا شما را از عذاب نجات دهند)؟');
INSERT INTO "badkhashani" VALUES(28,75,'28:75','و (در آن روز) از هر امتی گواهی بیرون کنیم، پس گوییم: دلیل خود را بیاورید. پس می‌دانند که حق با الله است، و آنچه که (به وحدانیت الله) افترا می‌کردند، از ایشان گم و ناپدید می‌شود.');
INSERT INTO "badkhashani" VALUES(28,76,'28:76','البته قارون از قوم موسی بود، پس بر آنان بغاوت کرد، و از گنج‌ها به اندازه‌ای به او دادیم که کلیدهای آن بر گروه توانمند سنگینی می‌کرد. وقتی قومش به او گفتند: شادی مکن، زیرا الله شاد شوندگان (به مال دنیا) را دوست نمی‌دارد.');
INSERT INTO "badkhashani" VALUES(28,77,'28:77','و در آنچه الله به تو داده است سرای آخرت را بجوی، و نصیب خود را از دنیا فراموش مکن، و چنانکه الله به تو نیکی کرده است (با دیگران) نیکی کن، و در زمین در تلاش فساد مباش که الله فسادکاران را دوست نمی‌دارد.');
INSERT INTO "badkhashani" VALUES(28,78,'28:78','قارون گفت: البته این (مال و ثروت) به خاطر علم و دانشی که دارم به من داده شده است. آیا ندانست که الله پیش از او از قرن‌ها (نسل‌های گذشته) کسانی را که از او تواناتر بودند و نسبت به او بیشتر مال اندوخته بودند، هلاک کرد؟ و مجرمان از گناهشان پرسیده نمی‌شوند (زیرا آنها از سیمای‌شان شناخته می‌شوند).');
INSERT INTO "badkhashani" VALUES(28,79,'28:79','پس (قارون) آراسته به زینت خود بر قومش بیرون شد، کسانی که زندگانی دنیا را می‌خواستند گفتند: ای کاش! ما نیز مانند آنچه به قارون داده شده است می‌داشتیم، بدون شک او از نصیب بزرگ (از نعمت‌های دنیا) برخوردار است.');
INSERT INTO "badkhashani" VALUES(28,80,'28:80','و کسانی که علم داده شده بودند، گفتند: وای بر شما! پاداش الله برای کسی که ایمان آورد و کار نیک انجام دهد، بهتر است. و این سخن را جز صابران نمی‌پذیرند.');
INSERT INTO "badkhashani" VALUES(28,81,'28:81','پس او و خانه‌اش را در زمین فرو بردیم. پس هیچ گروهی نداشت که او را در برابر (عذاب) الله مدد کنند، و خود او نیز از نصرت یافتگان نبود.');
INSERT INTO "badkhashani" VALUES(28,82,'28:82','و همان کسانی که دیروز مقام و منزلت او را آرزو می‌کردند، گفتند: وای! مثل اینکه الله روزی را برای هر کس از بندگانش که بخواهد فراخ و تنگ می‌گرداند، و اگر الله بر ما منت ننهاده بود، حتما ما را نیز (در زمین) فرو می‌برد. وای! گویا کافران رستگار نمی‌شوند.');
INSERT INTO "badkhashani" VALUES(28,83,'28:83','آن سرای آخرت را برای کسانی مقرر می‌گردانیم که در زمین خواهان برتری و فساد نیستند، و عاقبت (نیک) برای پرهیزگاران است.');
INSERT INTO "badkhashani" VALUES(28,84,'28:84','هر کس (در روز قیامت) کار نیک بیاورد، برای او (پاداش) بهتر از آن است، و هر کس بدی بیاورد (بداند) کسانی که مرتکب بدی‌ها شده‌اند، جز سزای آنچه کرده‌اند، (افزون بر آن) سزا داده نمی‌شوند.');
INSERT INTO "badkhashani" VALUES(28,85,'28:85','البته همان ذاتی که (عمل کردن به) این قرآن را بر تو فرض کرد، يقيناً او بازگردانندۀ تو به‌سوی بازگشت‌گاه است. بگو: پروردگارم بهتر می‌داند که چه کسی هدایت را (از سوی) او آورده است، و چه کسی در گمراهی آشکار قرار دارد.');
INSERT INTO "badkhashani" VALUES(28,86,'28:86','و تو امید نداشتی که کتاب بر تو نازل شود، بلکه این رحمتی از سوی پروردگارت بود (که این کار انجام شد)، پس هرگز مددگار کافران مباش.');
INSERT INTO "badkhashani" VALUES(28,87,'28:87','و هرگز کافران تو را از آيات الله باز ندارند، پس از آنکه (قرآن) بر تو نازل شده است. و به‌سوی پروردگارت دعوت کن و هرگز از مشركان مباش.');
INSERT INTO "badkhashani" VALUES(28,88,'28:88','و با الله معبود دیگری را مخوان، جز او معبود به حق نیست، همه چیز هلاک شونده است، جز وجه (ذات) او. سلطنت و حکم از او (نافذ) است و (همگی شما) به‌سوی او باز گردانیده می‌شوید.');
INSERT INTO "badkhashani" VALUES(29,1,'29:1','الم. (مفهوم این حروف به الله معلوم است).');
INSERT INTO "badkhashani" VALUES(29,2,'29:2','آیا مردم گمان کرده‌اند همین که بگویند: ایمان آوردیم (به حال خود) رها می‌شوند و مورد آزمایش قرار نمی‌گیرند؟');
INSERT INTO "badkhashani" VALUES(29,3,'29:3','و به يقين کسانی را که پیش از آنان بودند آزمودیم، پس البته الله آنان را که راست گفتند معلوم می‌دارد، و دروغگویان را (نیز) معلوم می‌دارد.');
INSERT INTO "badkhashani" VALUES(29,4,'29:4','آیا آنان که مرتکب بدی‌ها می‌شوند گمان می‌کنند که از ما پیشی می‌گیرند؟ چه بد قضاوت می‌کنند!');
INSERT INTO "badkhashani" VALUES(29,5,'29:5','هر کس که به لقای الله امید داشته باشد (بداند که) اجل مقرر الله آمدنی است و او شنوای داناست.');
INSERT INTO "badkhashani" VALUES(29,6,'29:6','و هرکس جهاد کنند جز این نیست که تنها برای خودش جهاد می‌کند. البته الله از جهانیان بی‌نیاز است.');
INSERT INTO "badkhashani" VALUES(29,7,'29:7','و آنان که ایمان آوردند و کارهای نیک انجام دادند، حتما گناهانشان را از آنان محو می‌سازیم و به آنان نیکوترین آنچه را که می‌کردند پاداش می‌دهیم.');
INSERT INTO "badkhashani" VALUES(29,8,'29:8','و به انسان سفارش کردیم که نسبت به پدر و مادرش نیکی کند. و اگر بکوشند تا چیزی را که به حقیقت آن علم نداری با من شریک گردانی، پس از آن دو اطاعت مکن، بازگشت تان به‌سوی من است، پس شما را به (حقیقت) آنچه می‌کردید، آگاه می‌سازم.');
INSERT INTO "badkhashani" VALUES(29,9,'29:9','و آنان که ایمان آوردند و کارهای نیک انجام دادند، آنان را در (زمرۀ) صالحان در می‌آوریم.');
INSERT INTO "badkhashani" VALUES(29,10,'29:10','و از مردم کسی هست که می‌گوید: به الله ایمان آورده‌ایم، اما وقتی در راه الله مورد اذیت و آزار قرار گیرد، فتنۀ (شکنجه) مردم را مانند عذاب الله قرار می‌دهد. و اگر نصرتی از سوی پروردگارت (به مؤمنان) رسد، حتما می‌گوید: البته ما با شما بودیم، آیا الله به آنچه در سینه‌های عالميان است، داناتر نیست؟');
INSERT INTO "badkhashani" VALUES(29,11,'29:11','و حتما الله مؤمنان را معلوم می‌دارد و حتما منافقان را (نیز) معلوم می‌دارد. ­');
INSERT INTO "badkhashani" VALUES(29,12,'29:12','و کافران به مؤمنان گفتند: از راه ما پیروی کنید حتما گناهان شما را به گردن می‌گیریم، در حالیکه آنان چیزی از گناهان ایشان را بردارنده نیستند. بدون شک آنان دروغگویانند.');
INSERT INTO "badkhashani" VALUES(29,13,'29:13','و البته بارهای (گناهان) خود و بارهایی دیگر را با بارهای (گناهان خود) بر می‌دارند و حتمی روز قیامت از آنچه افترا می‌کردند بازخواست می‌شوند.');
INSERT INTO "badkhashani" VALUES(29,14,'29:14','و به يقين که نوح را به‌سوی قومش فرستادیم پس نهصد و پنجاه سال در میان آنان باقی ماند، پس طوفان آنان را در حالیکه ظالم بودند، فرو گرفت.');
INSERT INTO "badkhashani" VALUES(29,15,'29:15','پس او و سواران کشتی را نجات دادیم و آن (کشتی) را پند و عبرتی برای جهانیان گرداندیم.');
INSERT INTO "badkhashani" VALUES(29,16,'29:16','(و یادآور شو) ابراهیم را وقتی که به قومش گفت: الله را بپرستید و از او بترسید، این برای شما بهتر است اگر می‌دانید.');
INSERT INTO "badkhashani" VALUES(29,17,'29:17','جز این نیست که شما غیر از الله بت‌هایی را می‌پرستید و (در عبادت الله) بهتان ایجاد می‌کنید، البته آنانی را که به جای الله می‌پرستید، به شما مالک روزی نیستند. پس نزد الله روزی بجویید و او را (به یگانگی) بپرستید و او را شکر گزارید که به‌سوی او باز گردانیده می‌شوید.');
INSERT INTO "badkhashani" VALUES(29,18,'29:18','و اگر (رسالت مرا) تکذیب کنید، پس امت‌های پیش از شما (نیز آن را) تکذیب کردند. و بر عهدۀ پیغمبر ما (وظیفه‌ای) جز ابلاغ آشکار نیست.');
INSERT INTO "badkhashani" VALUES(29,19,'29:19','آیا ندیدند که الله چگونه خلقت را آغاز می‌کند، باز آن را بر می‌گرداند؟! بی‌گمان این (اعادۀ خلقت) بر الله آسان است.');
INSERT INTO "badkhashani" VALUES(29,20,'29:20','بگو: در زمین بگردید و بنگرید که چگونه خلقت را آغاز کرد، باز پیدایش آخرت (بار دوم) را ایجاد می‌کند، یقینا الله بر هر کاری تواناست.');
INSERT INTO "badkhashani" VALUES(29,21,'29:21','هر کس را بخواهد عذاب می‌دهد و هر کس را بخواهد مورد رحمت قرار می‌دهد، و به‌سوی او باز گردانیده می‌شوید.');
INSERT INTO "badkhashani" VALUES(29,22,'29:22','و شما چه در زمین و چه در آسمان عاجزکننده (الله) نیستید و غیر از الله هیچ دوست و هیچ مددگاری ندارید.');
INSERT INTO "badkhashani" VALUES(29,23,'29:23','و آنان که به آیات الله و لقای او کفر ورزیدند، ایشان از رحمت من ناامید شده‌اند. و آن گروه برای‌شان عذاب دردناک است.');
INSERT INTO "badkhashani" VALUES(29,24,'29:24','پس جواب قومش جز این نبود که گفتند: او را بکشید یا بسوزانید! پس الله او را از آتش نجات داد. بی‌گمان در این (ماجرا) برای قومی که ایمان می‌آورند، نشانه‌هایی (قدرت الله) است.');
INSERT INTO "badkhashani" VALUES(29,25,'29:25','و (ابراهیم) گفت: جز این نیست که غیر از الله بتانی را معبود قرار داده‌اید که برای دوستی میان شما در زندگی دنیاست، باز در روز قیامت بعضی از شما بعضی دیگر را انکار می‌کند و بعضی از شما به بعضی دیگر لعنت می‌فرستد و جایگاه‌تان آتش دوزخ است و برای شما مددگارانی نیست.');
INSERT INTO "badkhashani" VALUES(29,26,'29:26','پس لوط به او (ابراهیم) ایمان آورد و گفت: من به‌سوی پروردگار خود هجرت می‌کنم، چون او غالب با حکمت است.');
INSERT INTO "badkhashani" VALUES(29,27,'29:27','و به (ابراهیم) اسحاق و یعقوب را بخشیدیم و در نسل او نبوت و کتاب قرار دادیم و در دنیا پاداشش را به او دادیم و البته او در آخرت از جملۀ صالحان است.');
INSERT INTO "badkhashani" VALUES(29,28,'29:28','و لوط (را نیز فرستادیم) وقتی به قومش گفت: شما عمل زشت را به عمل می‌آورید در حالی که هیچکس از جهانیان در (ارتکاب) آن بر شما سبقت نگرفته است.');
INSERT INTO "badkhashani" VALUES(29,29,'29:29','آیا شما با مردان آمیزش می‌کنید و راه (فطری و شرعی تناسل) را می‌بندید و در محافل خود مرتکب منکر می‌شوید؟ پس جواب قومش جز این نبود که گفتند: اگر از راستگویانی (پس) عذاب الله را برای ما بیاور.');
INSERT INTO "badkhashani" VALUES(29,30,'29:30','لوط گفت: ای پروردگارم! مرا بر قوم فساد کار پیروز گردان.');
INSERT INTO "badkhashani" VALUES(29,31,'29:31','و چون فرستادگان ما برای ابراهیم (آمدن فرزند را) مژده آوردند، گفتند: ما هلاک کنندۀ این شهریم، چرا که اهل آن ظالمان‌اند.');
INSERT INTO "badkhashani" VALUES(29,32,'29:32','ابراهیم گفت: لوط در آن شهر است! گفتند: ما به کسانی که در آنجا هستند، داناتریم، البته او و خانواده‌اش را نجات می‌دهیم، به جز همسرش که از باقی ماندگان (در عذاب) خواهد بود.');
INSERT INTO "badkhashani" VALUES(29,33,'29:33','و چون فرستادگان ما نزد لوط آمدند، لوط به خاطر آنان غمگین و دلتنگ شد، (فرشتگان) گفتند: مترس و غمگین مباش که ما نجات دهندۀ تو و خانواده‌ات هستیم، مگر همسرت که از باقی ماندگان (در عذاب) خواهد بود.');
INSERT INTO "badkhashani" VALUES(29,34,'29:34','البته ما بر اهل این قریه، به سبب آن که فسق می‌کردند، عذاب سخت را از آسمان فرود آورنده هستیم.');
INSERT INTO "badkhashani" VALUES(29,35,'29:35','و البته از آن (قریه) برای قومی که تعقل می‌کنند، نشانۀ روشن باقی گذاشتیم.');
INSERT INTO "badkhashani" VALUES(29,36,'29:36','و به‌سوی مدین برادرشان شعیب را فرستادیم و گفت: ای قوم من! الله را پرستش کنید و به روز باز پسین امید داشته باشید و در زمین برای فساد و تباهی سعی نورزید.');
INSERT INTO "badkhashani" VALUES(29,37,'29:37','پس او را تکذیب کردند در نتیجه زلزله آنان را گرفت و در خانه‌های خود از پای درآمدند.');
INSERT INTO "badkhashani" VALUES(29,38,'29:38','و عاد و ثمود را (نیز هلاک کردیم) و مسکن‌هایشان برای شما آشکار است و شیطان کارهایشان را برایشان آراسته نمود و آنان را از راه (راست) بازداشت در حالیکه صاحبان بصیرت و عقل بودند.');
INSERT INTO "badkhashani" VALUES(29,39,'29:39','و قارون و فرعون و هامان (را نیز هلاک کردیم) و البته موسی با دلائل و براهین روشن نزد آنان آمده بود، امّا ایشان در زمین استکبار و برتری جویی ورزیدند، ولی (از ما) سبقت گیرنده نبودند.');
INSERT INTO "badkhashani" VALUES(29,40,'29:40','پس هریک را به (سزای) گناهش گرفتار کردیم، پس برای بعضی از ایشان طوفان همراه با سنگریزه فرستادیم، و بعضی از ایشان را بانگ مرگبار فرا گرفت، و برخی از ایشان را در زمین فرو بردیم، و برخی دیگر را غرق کردیم. و الله نبود که بر آنان ظلم کند بلکه آنان خود به خویشتن ظلم می‌کردند.');
INSERT INTO "badkhashani" VALUES(29,41,'29:41','مثال آنانی که به جای الله دوستان گرفته‌اند چون وصف عنکبوت است که خانه‌ای برای خود ساخت، در حالیکه سست‌ترین خانه‌ها خانۀ عنکبوت است، اگر می‌دانستند.');
INSERT INTO "badkhashani" VALUES(29,42,'29:42','بی‌شک الله می‌داند که آنچه را غیر از او می‌خوانند، از هر چه باشد، و او غالب با حکمت است.');
INSERT INTO "badkhashani" VALUES(29,43,'29:43','و این مثال‌ها را برای مردم بیان می‌کنیم، ولی جز دانشمندان آنرا درک نمی‌کنند.');
INSERT INTO "badkhashani" VALUES(29,44,'29:44','الله آسمان‌ها و زمین را به حق آفرید، البته در این (خلقت) نشانۀ بزرگ برای اهل ایمان است.');
INSERT INTO "badkhashani" VALUES(29,45,'29:45','بخوان (ای پیغمبر!) آنچه از کتاب را که به تو وحی شده است و نماز را برپا دار، چون نماز (مسلمان را) از فحشا و منکر باز می‌دارد و البته ذكر الله بزرگ‌تر است. و الله آنچه را که می‌کنید می‌داند.');
INSERT INTO "badkhashani" VALUES(29,46,'29:46','و با اهل کتاب مجادله نکنید جز به روشی که بهتر است مگر با کسانی از ایشان که ظلم کرده‌اند، و بگویید: به آنچه بر ما نازل شده و به آنچه بر شما نازل شده ایمان داریم، و معبود ما و معبود شما یکی است و ما فرمانبردار او هستیم.');
INSERT INTO "badkhashani" VALUES(29,47,'29:47','و همچنین ما بر تو کتاب نازل کردیم، پس کسانی که به آنان کتاب (آسمانی) داده‌ایم، به آن ایمان می‌آورند. و از این گروه کسانی هستند که به آن ایمان می‌آورند و آیات ما را جز کافران انکار نمی‌کنند.');
INSERT INTO "badkhashani" VALUES(29,48,'29:48','و تو پیش از آن (قرآن) کتابی را نمی‌خواندی و با دست راست خود چیزی نمی‌نوشتی، وگرنه باطل اندیشان در شک می‌افتادند.');
INSERT INTO "badkhashani" VALUES(29,49,'29:49','بلکه آن (قرآن) آیات واضح در سینه‌های کسانی است که علم داده شده‌اند. و آیات ما را انکار نمی‌کند مگر ظالمان.');
INSERT INTO "badkhashani" VALUES(29,50,'29:50','و (مشرکان) گفتند: چرا بر او از جانب پروردگارش معجزاتی نازل نشده است؟ بگو: معجزات تنها نزد الله است و من فقط بیم دهندۀ آشکارم.');
INSERT INTO "badkhashani" VALUES(29,51,'29:51','آیا برای آنان کافی نیست که ما این کتاب را که بر آنان خوانده می‌شود، بر تو نازل کرده‌ایم؟ البته در این (کتاب اعجازگر) برای قومی که ایمان می‌آورند، رحمت و پند است.');
INSERT INTO "badkhashani" VALUES(29,52,'29:52','بگو: همین کافیست که الله بین من و شما گواه است، آنچه را که در آسمان‌ها و زمین است می‌داند. و کسانی که به (معبودان) باطل ایمان آورده‌اند و به الله كفر ورزیده‌اند، همین گروه زیانکاران‌اند.');
INSERT INTO "badkhashani" VALUES(29,53,'29:53','و (مشركان) عذاب را با شتاب از تو می‌طلبند. و اگر میعاد مقرر نبود، حتما عذاب به آنان می‌رسید و البته ناگهان در حالیکه نمی‌دانند عذاب به آنان می‌آمد.');
INSERT INTO "badkhashani" VALUES(29,54,'29:54','آنان عذاب را به شتاب از تو می‌طلبند درحالیکه جهنم بر کافران احاطه دارد.');
INSERT INTO "badkhashani" VALUES(29,55,'29:55','روزی که عذاب از بالای سر آنها و از زیر پاهایشان آنها را احاطه می‌کند و (الله) می‌گوید: بچشید سزای آنچه را که می‌کردید.');
INSERT INTO "badkhashani" VALUES(29,56,'29:56','ای بندگان من که ایمان آورده‌اید! يقينا زمین من فراخ است، پس تنها مرا عبادت کنید.');
INSERT INTO "badkhashani" VALUES(29,57,'29:57','هر نفسی چشندۀ مرگ است باز به‌سوی ما باز گردانیده می‌شوید.');
INSERT INTO "badkhashani" VALUES(29,58,'29:58','و کسانی که ایمان آورده‌اند و کارهای نیک کرده‌اند، ایشان را در غرفه‌های بهشت جای می‌دهیم که در زیر آنها نهرها روان است و در آنجا همیشه خواهند ماند. چه خوب است ثواب عمل کنندگان!');
INSERT INTO "badkhashani" VALUES(29,59,'29:59','همان کسانی که صبر کردند و بر پروردگارشان توکل می‌کنند.');
INSERT INTO "badkhashani" VALUES(29,60,'29:60','و چه بسیار است جاندارانی که (به سبب ناتوانی) نمی‌توانند روزی خود را بردارند، (بلکه) الله به آنها و به شما روزی می‌دهد، و او شنوای داناست.');
INSERT INTO "badkhashani" VALUES(29,61,'29:61','و اگر از آنان بپرسی که چه کسی آسمان‌ها و زمین را آفریده و آفتاب و ماه را مسخر کرده است؟ حتما می‌گویند: الله. پس چگونه به بیراهه برده می‌شوند؟');
INSERT INTO "badkhashani" VALUES(29,62,'29:62','الله روزی را برای هر کس از بندگانش که بخواهد گشاده می‌گرداند، و برای هرکس که بخواهد تنگ می‌گرداند. چون الله به هر چیزی داناست.');
INSERT INTO "badkhashani" VALUES(29,63,'29:63','و اگر از آنان بپرسی که چه کسی از آسمان آب را نازل کرد پس با آن زمین را بعد از مردنش زنده گردانید؟ حتما می‌گویند: الله، بگو: ستایش الله راست، اما بیشتر آنان نمی‌فهمند.');
INSERT INTO "badkhashani" VALUES(29,64,'29:64','و زندگی این دنیا جز سرگرمی و بازیچه نیست، و البته زندگی حقیقی سرای آخرت است، اگر می‌دانستند.');
INSERT INTO "badkhashani" VALUES(29,65,'29:65','و هنگامی که سوار کشتی شوند، الله را خالصانه و مخلصانه به دعا می‌خوانند، ولی چون آنان را به خشکی رسانده نجات دهد، ناگهان شرک می‌ورزند.');
INSERT INTO "badkhashani" VALUES(29,66,'29:66','تا (سرانجام) در آنچه به آنان داده‌ایم ناشکری کنند و تا (از نعمت‌های زودگذر دنیا) برخوردار گردند، پس به زودی (سزای عملکرد خویش را) خواهند دانست.');
INSERT INTO "badkhashani" VALUES(29,67,'29:67','آیا ندیدند که ما حرم ایمن (برای آنان) قرار داده‌ایم. حال آنکه مردم از اطراف آنان ربوده می‌شوند؟ آیا به باطل ایمان می‌آورند و به نعمت الله كفر می‌ورزند.');
INSERT INTO "badkhashani" VALUES(29,68,'29:68','و کیست ظالم‌تر از کسی که بر الله دروغ ببندد یا چون حق نزد او آید آنرا تکذیب کند؟ آیا در جهنم جایگاهی برای کافران نیست؟');
INSERT INTO "badkhashani" VALUES(29,69,'29:69','و کسانی که در راه ما سعی و کوشش کردند، البته آنان را به راه‌های (خشنودی) خویش رهنمود می‌گردانیم، و بی‌گمان الله با احسان کنندگان است.');
INSERT INTO "badkhashani" VALUES(30,1,'30:1','الم. (مفهوم این حروف به الله معلوم است).');
INSERT INTO "badkhashani" VALUES(30,2,'30:2','رومی‌ها مغلوب شدند (شکست خوردند).');
INSERT INTO "badkhashani" VALUES(30,3,'30:3','در نزدیک‌ترین سرزمین (به اعراب)، ولی آنها بعد از شکستشان به زودی غالب خواهند شد.');
INSERT INTO "badkhashani" VALUES(30,4,'30:4','در مدت چند سال (آینده). فرجام کار و فرمان، پیش از این و پس از این از الله است، و آن روز مؤمنان شادمان می‌شوند.');
INSERT INTO "badkhashani" VALUES(30,5,'30:5','به نصرت دادن الله (رومی‌ها را بر فارس). هر کس را که بخواهد نصرت می‌دهد و او غالبِ مهربان است.');
INSERT INTO "badkhashani" VALUES(30,6,'30:6','وعدۀ الله است (که رومی‌ها بر فارسیان غالب گردند و) الله وعدۀ خود را خلاف نمی‌کند، ولی بیشتر مردم نمی‌دانند.');
INSERT INTO "badkhashani" VALUES(30,7,'30:7','از زندگانی دنیا تنها ظاهر را می‌دانند، در حالیکه از آخرت غافل‌اند.');
INSERT INTO "badkhashani" VALUES(30,8,'30:8','آیا در ضمیر خود فکر نکردند که الله آسمان‌ها و زمین و آنچه را که در میان آن دو است، جز به حق و برای مدت معین نیافریده است؟! و البته بسیاری از مردم به ملاقات پروردگارشان (در آخرت) کافرند.');
INSERT INTO "badkhashani" VALUES(30,9,'30:9','آیا در زمین سیر نکرده‌اند تا بنگرند سرانجام کسانی که پیش از ایشان بوده‌اند چگونه بوده است؟! در حالیکه آنها از ایشان توانمندتر بودند و زمین را بهتر کاویدند و زیر و رو کردند و آن را بیشتر از آنچه (ایشان) آباد کرده‌اند، آباد نمودند. و پیغمبرانشان با معجزه‌ها به‌سوی آنان آمدند، پس الله بر آن نبود که بر آنان ظلم کند، بلکه آنها خود در حق خویش ظلم می‌کردند.');
INSERT INTO "badkhashani" VALUES(30,10,'30:10','باز عاقبت کسانی که مرتکب کارهای بد شدند، بدتر شد. چرا که آیات الله را تکذیب کردند و به آن استهزا می‌کردند.');
INSERT INTO "badkhashani" VALUES(30,11,'30:11','الله خلقت را آغاز می‌کند باز آن را دوباره برمی گرداند (و مجددا زنده می‌کند) باز (در روز قیامت) به‌سوی او باز گردانیده می‌شوید.');
INSERT INTO "badkhashani" VALUES(30,12,'30:12','و روزی که قیامت برپا شود، در آن روز مجرمان ناامید می‌گردند.');
INSERT INTO "badkhashani" VALUES(30,13,'30:13','و برای آنان از شریکان‌شان هیچ شفاعت کننده‌ای نیست و خود به شریکان‌شان کافر خواهند بود.');
INSERT INTO "badkhashani" VALUES(30,14,'30:14','و روزی که قیامت برپا شود، در آن روز مردم از یکدیگر جدا می‌شوند (اهل جنت به جنت و اهل دوزخ به دوزخ می‌روند)،');
INSERT INTO "badkhashani" VALUES(30,15,'30:15','اما آنان که ایمان آوردند و کارهای نیک انجام دادند، پس آنها در بوستان (های) جنت شاد و مسرور می‌گردند.');
INSERT INTO "badkhashani" VALUES(30,16,'30:16','و اما آنان که کفر ورزیدند و آیات ما و لقای آخرت را تکذیب کردند پس این گروه در عذاب احضار خواهند شد.');
INSERT INTO "badkhashani" VALUES(30,17,'30:17','پس چون در وقت شام و در وقت صبح وارد می‌شوید، الله را به پاکی یاد کنید.');
INSERT INTO "badkhashani" VALUES(30,18,'30:18','و ستایش در آسمان‌ها و زمین و به هنگام عصر و نیز زمانی که به وقت ظهر وارد می‌شوید، برای اوست.');
INSERT INTO "badkhashani" VALUES(30,19,'30:19','زنده را از مرده بیرون می‌آورد و مرده را از زنده بیرون می‌آورد و زمین را پس از خشک شدنش زنده و سبزه زار می‌گرداند. و این طور (در روز قیامت از قبرهای تان زنده شده) بیرون آورده می‌شوید.');
INSERT INTO "badkhashani" VALUES(30,20,'30:20','و از علامات قدرت الله این است که (اصل) شما (آدم) را از خاک آفرید، باز ناگهان شما انسان‌ها در زمین منتشر شدید.');
INSERT INTO "badkhashani" VALUES(30,21,'30:21','و از علامات قدرت الله این است که از جنس خودتان برایتان همسرانی آفرید تا در کنار آنان آرام گیرید و در میانتان دوستی و مهربانی نهاد، البته در این نشانه‌هایی است برای قومی که می‌اندیشند.');
INSERT INTO "badkhashani" VALUES(30,22,'30:22','و از علامات قدرت الله، آفرینش آسمان‌ها و زمین و مختلف بودن زبان‌ها و رنگ‌های شماست، البته در این (امر) برای اصحاب علم و بصیرت نشانه‌هایی (عبرت انگیز) است.');
INSERT INTO "badkhashani" VALUES(30,23,'30:23','و از علامات قدرت الله، خواب شما در شب و روز و تلاش‌تان برای برخورداری از فضل اوست. بی‌گمان در این (امر) برای قومی که (موعظه‌های ما را) می‌شنوند، عبرت‌ها و نشانه‌هاست.');
INSERT INTO "badkhashani" VALUES(30,24,'30:24','و از علامات قدرت الله این است که برق را برای ترس و امید به شما نشان می‌دهد، و از آسمان آب نازل می‌کند، پس زمین را بوسیلۀ آن پس از خشک شدنش زنده و سر سبز می‌گرداند. يقينا در این (امر) برای مردمی که عقل و خرد می‌ورزند، نشانه‌ها (و عبرت‌ها) است.');
INSERT INTO "badkhashani" VALUES(30,25,'30:25','و از علامات قدرت الله این است که آسمان و زمین به فرمان او برپا و قایم است. باز وقتی شما را از زمین با صدایی بخواند، ناگهان شما بیرون می‌شوید.');
INSERT INTO "badkhashani" VALUES(30,26,'30:26','و هر که در آسمان‌ها و زمین است، از او (و در تصرف او) است و همگی فرمانبردار او هستند.');
INSERT INTO "badkhashani" VALUES(30,27,'30:27','و اوست ذاتی که خلقت را آغاز می‌کند باز آن را دوباره ایجاد می‌کند، و این کار برایش آسان‌تر است، و هر صفت برتر و والاتر (بر زبان خلايق و دلایل) در آسمان‌ها و زمین برای اوست و او غالبِ باحکمت است.');
INSERT INTO "badkhashani" VALUES(30,28,'30:28','الله (در بارۀ وحدانیت خود) برای شما از خودتان مثلی زده است، آیا در آنچه به شما روزی داده‌ایم (می‌پسندید کسی) از غلامان تان شریک تان باشند که شما و ایشان هردو در آن یکسان و برابر باشید؟ و همانطور که شما (آزادگان) از یکدیگر می‌ترسید، از بندگان هم بیمناک باشید؟ این چنین آیات را برای قومی که خرد می‌ورزند به تفصیل و روشنی بیان می‌کنیم.');
INSERT INTO "badkhashani" VALUES(30,29,'30:29','بلکه ظالمان بدون علم از خواهشات خود پیروی کردند، پس کسی را که الله گمراه کرده است چه کسی هدایت می‌کند؟ و برای ایشان مددگارانی نیست.');
INSERT INTO "badkhashani" VALUES(30,30,'30:30','پس رویت را با پیروی از حق و بیزاری از ادیان باطل به‌سوی این دین راست کن، با همان فطرتی که الله مردم را بر آن آفریده است. (چون) در خلقت الله تبدیلی نیست. این است دین محکم و استوار، و لیکن بیشتر مردم نمی‌دانند.');
INSERT INTO "badkhashani" VALUES(30,31,'30:31','رجوع‌کنان به‌سوی الله برگردید و از او بترسید و نماز را برپا کنید و از جملۀ مشرکان نباشید.');
INSERT INTO "badkhashani" VALUES(30,32,'30:32','از کسانی که دینشان را پراکنده کردند و گروه گروه شدند. هر گروهی به آنچه که نزد خود دارند خرسند‌اند.');
INSERT INTO "badkhashani" VALUES(30,33,'30:33','و چون به مردم زیان و بلایی برسد پروردگارشان را رجوع کنان می‌خوانند، باز چون از سوی خود رحمتی به آنها بچشاند، ناگهان گروهی از آنان به پروردگارشان شرک می‌آورند.');
INSERT INTO "badkhashani" VALUES(30,34,'30:34','تا در نتیجه در آنچه که به آنان بخشیده‌ایم ناشکری کنند. پس (اندک زمانی) برخوردار شوید، اما خواهید دانست.');
INSERT INTO "badkhashani" VALUES(30,35,'30:35','آیا بر آنها دلیلی نازل کرده‌ایم پس آن (دلیل) به (صحت) آنچه به او شرک می‌آورند سخن می‌گوید؟');
INSERT INTO "badkhashani" VALUES(30,36,'30:36','و چون به مردم رحمتی (نعمت و عافیت) بچشانیم به آن خوش می‌شوند. و اگر رنج و سختی به خاطر کارهایی که کرده‌اند به آنان برسد، ناگهان آنها مأیوس و ناامید می‌شوند.');
INSERT INTO "badkhashani" VALUES(30,37,'30:37','آیا ندیدند که الله روزی را برای هر کس که بخواهد فراخ و (برای هر کس که بخواهد) تنگ می‌گرداند؟ البته در این (نظام) نشانه‌هایی است برای قومی که ایمان می‌آورند.');
INSERT INTO "badkhashani" VALUES(30,38,'30:38','پس به صاحب قرابت حقش را بده و (نیز) به مسكين و مسافر راه مانده (حق‌شان را بده)، این انفاق برای کسانی که رضایت الله را می‌جویند بهتر است. و این گروه نجات یافتگان‌اند.');
INSERT INTO "badkhashani" VALUES(30,39,'30:39','و آنچه به عنوان سود می‌دهید تا (سهم شما) در اموال مردم افزایش یابد، پس (بدانید که) نزد الله افزایش نخواهد یافت، و آنچه از زکات را که در طلب خشنودی الله پرداخت می‌کنید (و عوض آن را از کسی انتظار ندارید) پس همین گروه دارای پاداش چندین برابر خواهند بود.');
INSERT INTO "badkhashani" VALUES(30,40,'30:40','الله ذاتی است که شما را آفرید باز به شما روزی داد. باز شما را می‌میراند باز شما را دوباره زنده می‌کند. آیا از شریک‌هایتان کسی هست که چیزی از این (کارها) را انجام دهد؟! او پاک و منزّه و والاتر است از آنچه شریک او می‌سازند.');
INSERT INTO "badkhashani" VALUES(30,41,'30:41','فساد در خشکی و بحر ظاهر شد به سبب دست آوردهای مردم، تا (الله) سزای بعضی از آنچه را که کرده‌اند به آنان بچشاند، باشد که آنان باز گردند.');
INSERT INTO "badkhashani" VALUES(30,42,'30:42','بگو: در زمین بگردید، پس بنگرید که عاقبت کسانی که پیش از این بودند، چگونه شد، بیشتر آنها مشرک بودند.');
INSERT INTO "badkhashani" VALUES(30,43,'30:43','پس رویت را به‌سوی دین راست استوار بگردان پیش از آنکه روزی برسد که برای آن بازگشتی از (سوی) الله نیست. در آن روز (مردم) متفرق می‌گردند.');
INSERT INTO "badkhashani" VALUES(30,44,'30:44','هرکس کفر ورزد کفرش به زیان خود اوست و هر کس کار نیک انجام دهد پس (از اکنون زمینۀ عاقبت نیک را) برای خود‌شان آماده سازی می‌کنند.');
INSERT INTO "badkhashani" VALUES(30,45,'30:45','تا الله به کسانی که ایمان آورده‌اند و کارهای نیک انجام داده‌اند از فضل خود پاداش دهد، چون او كافران را دوست نمی‌دارد.');
INSERT INTO "badkhashani" VALUES(30,46,'30:46','و از علامات قدرت و وحدانیت الله این است که بادها را مژده رسان می‌فرستد تا از رحمت خود به شما بچشاند و کشتی‌ها هم با امر او به حرکت درآیند و تا از فضل او بجویید و تا شکر گزارید.');
INSERT INTO "badkhashani" VALUES(30,47,'30:47','و به تحقیق پیش از تو پیغمبرانی را به‌سوی اقوامشان فرستادیم و آنان دلايل واضح و آشکار برای‌شان آوردند، پس از کسانی که نافرمانی کردند انتقام گرفتیم. و نصرت دادن مؤمنان حق است بر (عهدۀ) ما.');
INSERT INTO "badkhashani" VALUES(30,48,'30:48','الله ذاتی است که بادها را می‌فرستد که ابر را در حرکت می‌آورد، باز هر طوری که بخواهد آن را می‌گستراند و آن را پاره پاره می‌گرداند. و می‌بینی که قطرات باران از لابلای آن بیرون می‌آید. و چون آن را به آن بندگانش که می‌خواهد، برساند، ناگهان آنها شادمان می‌شوند.');
INSERT INTO "badkhashani" VALUES(30,49,'30:49','و هر چند پیش از آن که بر آنان (باران) نازل گردد، سخت ناامید بودند.');
INSERT INTO "badkhashani" VALUES(30,50,'30:50','پس به آثار رحمت الله بنگر که چگونه زمین را پس از مردنش زنده می‌گرداند؟! بی‌گمان این (الله) زنده کننده مردگان است و او بر هرچیز تواناست.');
INSERT INTO "badkhashani" VALUES(30,51,'30:51','و اگر بادی بفرستیم پس آن (کشتزار) را زرد شده ببینند، حتما پس از آن (به جای توبه) ناشکری می‌کنند.');
INSERT INTO "badkhashani" VALUES(30,52,'30:52','پس تو نمی‌توانی به مردگان بشنوانی، و نمی‌توانی به کران آواز بشنوانی هنگامی که پشت کرده روی بگردانند.');
INSERT INTO "badkhashani" VALUES(30,53,'30:53','و تو هدایت کنندۀ کوران از گمراهی‌شان نیستی. و نمی‌توانی جز به کسانی که به آیات ما ایمان می‌آورند، بشنوانی، چون آنها منقاد و فرمانبردارند.');
INSERT INTO "badkhashani" VALUES(30,54,'30:54','الله ذاتی است که شما را ابتدا ضعیف و ناتوان آفرید، باز بعد از این ناتوانی قوت و قدرت بخشید، باز بعد از توانایی (برای شما) ناتوانی و پیری قرار دارد، هر چه بخواهد می‌آفریند و اوست دانای توانا.');
INSERT INTO "badkhashani" VALUES(30,55,'30:55','و روزی که قیامت برپا شود مجرمان قسم می‌خورند که جز ساعتی درنگ نکرده‌اند. این چنین (از راه حق) برگردانیده می‌شوند.');
INSERT INTO "badkhashani" VALUES(30,56,'30:56','و کسانی که به ایشان علم و ایمان داده شده است، می‌گویند: بدون شک طبق حکم در کتاب الله تا روز قیامت درنگ کرده‌اید، پس این روز قیامت است، و ليكن شما نمی‌دانستید.');
INSERT INTO "badkhashani" VALUES(30,57,'30:57','پس آن روز عذرخواهی ظالمان سودی به ایشان نمی‌بخشد و از آنان کسب رضایت (الله) خواسته نمی‌شود.');
INSERT INTO "badkhashani" VALUES(30,58,'30:58','و هر آئینه در این قرآن برای مردم از هر مثلی بیان کرده‌ایم. و اگر برای آنان آیتی بیاوری کافران حتما می‌گویند: شما نیستید مگر باطل اندیشان.');
INSERT INTO "badkhashani" VALUES(30,59,'30:59','این چنین الله بر دل‌های آنانی که نمی‌دانند مهر می‌نهد.');
INSERT INTO "badkhashani" VALUES(30,60,'30:60','پس صبر کن، چون وعدۀ الله حق است. و مبادا کسانی که ایمان ندارند تو را به خشم و سبکسری وا دارند.');
INSERT INTO "badkhashani" VALUES(31,1,'31:1','الم. (معنای این حروف به الله معلوم است).');
INSERT INTO "badkhashani" VALUES(31,2,'31:2','این آیات کتاب پر از حکمت است.');
INSERT INTO "badkhashani" VALUES(31,3,'31:3','هدایت و رحمت است برای محسنان (نیکو کاران مخلص).');
INSERT INTO "badkhashani" VALUES(31,4,'31:4','آنان که نماز برپا می‌کنند و زکات می‌دهند. و ایشان‌اند که به آخرت يقين دارند.');
INSERT INTO "badkhashani" VALUES(31,5,'31:5','ایشان‌اند که از جانب پروردگارشان بر هدایت هستند و همین ایشان رستگارانند.');
INSERT INTO "badkhashani" VALUES(31,6,'31:6','و از مردمان کسی است که سخنان لهو و بیهوده را می‌خرد تا به نادانی (مردم را) از راه الله گمراه سازد و (راه الله) را به استهزا گیرد. این گروه برایشان عذاب رسواکننده است.');
INSERT INTO "badkhashani" VALUES(31,7,'31:7','و چون آیات ما بر او تلاوت شود، تکبرکنان روی می‌گرداند، چنان که گویا آن را نشنیده است، گویی در گوش‌هایش سنگینی و پرده است، پس او را به عذاب دردناک مژده بده.');
INSERT INTO "badkhashani" VALUES(31,8,'31:8','البته آنان که ایمان آوردند و کارهای نیک انجام دادند، برای آنها باغ‌های پر از ناز و نعمت است.');
INSERT INTO "badkhashani" VALUES(31,9,'31:9','که در آن همیشه خواهند ماند، وعدۀ الله حق است، و او غالب باحمکت است.');
INSERT INTO "badkhashani" VALUES(31,10,'31:10','آسمان‌ها را بدون ستون‌هایی که آنها را ببینید آفریده است و در زمین کوه‌های استوار را افگنده است تا زمین شما را نلرزاند و در آن از هر نوع زنده جان‌ها منتشر کرد و از آسمان آب نازل کردیم، پس در زمین از هر گونه نباتات خوب و ارزشمند رویانیدیم.');
INSERT INTO "badkhashani" VALUES(31,11,'31:11','این است آفرینش الله، پس شما به من نشان دهید کسانی که غير از او ‌اند، چه چیز آفریده‌اند؟ بلکه ظالمان در گمراهی آشکار ‌اند.');
INSERT INTO "badkhashani" VALUES(31,12,'31:12','و يقينا به لقمان حکمت بخشیدیم (و به او دستور دادیم) که الله را شکر کن و هر کس (الله را) شکر گزارد در حقیقت به نفع خود شکر گزاری می‌کند و هر کس ناشکری کند (بداند که) الله بی‌نیاز ستوده است.');
INSERT INTO "badkhashani" VALUES(31,13,'31:13','و (یاد آور شو) وقتی را که لقمان پسر خود را وعظ می‌کرد (به او) گفت: پسر جانم! به الله شریک قرار مده. چرا که شرک ظلم بزرگ است.');
INSERT INTO "badkhashani" VALUES(31,14,'31:14','و به انسان دربارۀ پدر و مادرش سفارش کردیم، مادرش با ضعف بر بالای ضعف به او باردار شد. و جدا کردن او از شیر در طی دو سال است. (و به انسان سفارش کردیم) برای من و پدر و مادرت شکر گزار باش که بازگشت (همه) به‌سوی من است.');
INSERT INTO "badkhashani" VALUES(31,15,'31:15','و اگر سعی ورزیدند که تو را وادارند به این که چیزی را با من شریک قرار دهی که به آن علم نداری، پس از آنان اطاعت مکن، و در دنیا با آنان به خوبی رفتار کن. و از راه کسی پیروی کن که به‌سوی من بازگشته است، باز باز گشتتان به‌سوی من است، پس شما را از آنچه می‌کردید آگاه می‌سازم.');
INSERT INTO "badkhashani" VALUES(31,16,'31:16','(لقمان گفت): پسر جانم! بدان که اگر آن (عمل) به اندازه دانۀ خردلی باشد و در دل سنگی یا در آسمان‌ها و یا در زمین باشد، الله آن را می‌آورد (و مورد محاسبه قرار می‌دهد)، چون الله باریک بین (و) آگاه است.');
INSERT INTO "badkhashani" VALUES(31,17,'31:17','ای پسر جانم! نماز را برپا کن و به کار نیک دستور بده و از کار بد منع کن و در برابر آنچه به تو می‌رسد صابر باش که این (خصلت‌ها) از کارهای سترگ (و محوری) است.');
INSERT INTO "badkhashani" VALUES(31,18,'31:18','و با تکبر از مردم روی مگردان و در زمین خرامان راه مرو، چون الله هیچ متكبر فخر فروش را دوست ندارد.');
INSERT INTO "badkhashani" VALUES(31,19,'31:19','و در راه رفتن خود میانه روی (را پیشه) کن و صدایت را آهسته بدار، همانا بدترین آوازها، آواز خران است.');
INSERT INTO "badkhashani" VALUES(31,20,'31:20','آیا ندیده‌اید که الله آنچه را که در آسمان‌ها و آنچه را که در زمین است، برایتان مسخر گردانید و نعمت‌های ظاهری و باطنی خود را بر شما گسترده و فراوان ساخته است؟ و از مردم کسانی هستند که در بارۀ الله بدون هیچ علم و بدون هیچ هدایت و بدون هیچ کتاب روشن، مجادله می‌کند.');
INSERT INTO "badkhashani" VALUES(31,21,'31:21','و چون به آنان گفته شود. از آنچه پیروی کنید که الله نازل کرده است، می‌گویند: بلکه از چیزی پیروی می‌کنیم که پدران خود را بر آن یافته‌ایم، آیا (از پدران خود پیروی می‌کنند) حتی اگر شیطان آنان را به‌سوی عذاب سوزان فرا خواند.');
INSERT INTO "badkhashani" VALUES(31,22,'31:22','و هر که روی خویش را به سوى الله متوجه کند، در حالیکه محسن باشد به تحقیق به دستاویز بسیار محکمی چنگ زده است، و سرانجام کارها به‌سوی الله است.');
INSERT INTO "badkhashani" VALUES(31,23,'31:23','و هر که کافر شد، نباید کفر وی تو را غمگین سازد، (چراکه) بازگشت آنان به‌سوی ماست، و ایشان را از کارهایی که کرده‌اند آگاه می‌سازیم. البته الله به راز سینه‌ها آگاه است.');
INSERT INTO "badkhashani" VALUES(31,24,'31:24','و آنها را اندکی بهره مند می‌سازیم باز آنان را به (تحمل) عذاب سخت مجبور می‌سازیم.');
INSERT INTO "badkhashani" VALUES(31,25,'31:25','و اگر از آنان بپرسی که چه کسی آسمان‌ها و زمین را آفریده است؟ حتما می‌گویند: الله، بگو: الحمد لله (که اقرار نمودید) ولی بیشترشان نمی‌دانند.');
INSERT INTO "badkhashani" VALUES(31,26,'31:26','آنچه در آسمان‌ها و زمین است، از الله است. در حقیقت الله همان ذات بی‌نیاز و ستوده است.');
INSERT INTO "badkhashani" VALUES(31,27,'31:27','و اگر همۀ درختان روی زمین قلم شوند و دریا (دوات گردد) و هفت دریای دیگر به آن مدد برساند سخنان الله به پایان نرسد، يقينا الله غالب باحکمت است.');
INSERT INTO "badkhashani" VALUES(31,28,'31:28','آفریدن شما و برانگیختنتان جز مانند (آفریدن و برانگیختن) یک تن نیست، همانا الله شنوای بیناست.');
INSERT INTO "badkhashani" VALUES(31,29,'31:29','آیا ندیده‌ای (ای مخاطب) که الله شب را در روز و روز را در شب داخل می‌گرداند؟ و آفتاب و ماه را مسخر کرده است، هریک تا مدتی معین در حرکت است، و (آیا ندیدی که) الله به آنچه می‌کنید آگاه است.');
INSERT INTO "badkhashani" VALUES(31,30,'31:30','این به سبب آن است که الله حق است و آنچه را که به جای او می‌خوانند، باطل است. و الله بلند مرتبه و بزرگ است.');
INSERT INTO "badkhashani" VALUES(31,31,'31:31','آیا ندیده‌ای که کشتی‌ها در دریا به فضل الله حرکت می‌کنند تا (بعضی) از نشانه‌های (قدرت) خود را به شما نشان دهد؟! يقينا در این (کار) دلایل (وحدانیت الله) است برای هر صبر کنندۀ شکر گزار.');
INSERT INTO "badkhashani" VALUES(31,32,'31:32','و چون موجی مانند سایه بان آنها را فراگیرد، الله را خالصانه می‌خوانند و عبادت را خاص او می‌دانند، اما وقتی آنان را به‌سوی خشکه نجات دهد پس برخی از ایشان میانه رو هستند، و هیچ کس جز عهدشکن غدار ناشکر آیات ما را انکار نمی‌کند.');
INSERT INTO "badkhashani" VALUES(31,33,'31:33','ای مردم! از پروردگارتان بترسید، و (نیز) از روزی بترسید که نه هیچ پدری می‌تواند چیزی را از طرف فرزند خود ادا کند و نه هیچ فرزندی می‌تواند چیزی را از طرف پدرش ادا کند، يقينا وعدۀ الله حق است. پس زندگانی دنیا شما را نفریبد. و غرور (شیطان)، شما را نسبت به الله فریب ندهد.');
INSERT INTO "badkhashani" VALUES(31,34,'31:34','بی‌گمان علم (فرا رسیدن) قیامت فقط نزد الله است، و اوست که باران می‌باراند و آنچه که در رحمهاست می‌داند، و هیچ نفس نمی‌داند که فردا چه چیزی کسب می‌کند، و هیچ کسی نمی‌داند که در کدام سرزمین می‌میرد. البته الله دانای آگاه است.');
INSERT INTO "badkhashani" VALUES(32,1,'32:1','الم. (مفهوم این حروف به الله معلوم است).');
INSERT INTO "badkhashani" VALUES(32,2,'32:2','فرود آوردن این کتاب که شکی در آن نیست، از طرف پروردگار جهانیان است.');
INSERT INTO "badkhashani" VALUES(32,3,'32:3','آیا می‌گویند: خودش آن را از پیش خود ساخته است؟ بلکه قرآن حق است و از سوی پروردگارت آمده است تا گروهی را بترسانی که پیش از تو ترساننده‌ای نزد آنان نیامده است. باشد که هدایت شوند.');
INSERT INTO "badkhashani" VALUES(32,4,'32:4','الله ذاتی است که آسمان‌ها و زمین و آنچه را که در میان آن دو است، در شش روز آفرید، باز بر عرش قرار گرفت، جز او هیچ دوست و شفاعت کننده‌ای ندارید، آیا عبرت نمی‌گیرید؟');
INSERT INTO "badkhashani" VALUES(32,5,'32:5','امر را از آسمان تا زمین تدبیر می‌کند، باز (گزارش آن) در روزی که اندازۀ آن - از آنچه که شما می‌شمارید- هزار سال است، به‌سوی او بالا می‌رود.');
INSERT INTO "badkhashani" VALUES(32,6,'32:6','او ذاتی است آگاه از پنهان و آشکار (و) غالب (و) مهربان است.');
INSERT INTO "badkhashani" VALUES(32,7,'32:7','ذاتی که هر چیزی را آفریده و آن را حسن و زیبایی بخشیده است. و آفرینش انسان را از گِل آغاز کرد.');
INSERT INTO "badkhashani" VALUES(32,8,'32:8','باز نسل او را از خلاصۀ آب حقیر (منی) آفرید.');
INSERT INTO "badkhashani" VALUES(32,9,'32:9','باز اندام او را برابر کرد و از روح (آفریدۀ) خود در آن دمید و برای شما گوش و چشم‌ها و دل‌ها آفرید، ولی شما کمتر شکر می‌گزارید.');
INSERT INTO "badkhashani" VALUES(32,10,'32:10','و (کافران) می‌گویند: آیا وقتی (ما مُردیم و) در زمین ناپدید شویم، آیا آفرینش جدیدی خواهیم یافت؟ بلکه آنان به ملاقات پروردگارشان کافرند.');
INSERT INTO "badkhashani" VALUES(32,11,'32:11','بگو: ملک الموت (فرشتۀ مرگ) که بر شما گمارده شده است روح شما را می‌گیرد، باز به‌سوی پروردگارتان باز گردانیده می‌شوید.');
INSERT INTO "badkhashani" VALUES(32,12,'32:12','و اگر ببینی وقتی را که مجرمان نزد پروردگارشان سرهای‌شان را به زیر افگنده باشند و می‌گویند: ای پروردگار ما! دیدیم و شنیدیم پس ما را (به دنیا) باز گردان تا کار نیک انجام دهیم، اکنون ما يقين کرده‌ایم.');
INSERT INTO "badkhashani" VALUES(32,13,'32:13','و اگر ما می‌خواستیم حتما به هر کس هدایتش را می‌دادیم ولی از سوی من این قول فیصله شده است که جهنم را از جن و انس یکجا پر می‌کنم.');
INSERT INTO "badkhashani" VALUES(32,14,'32:14','پس به سبب آن که ملاقات امروزتان را فراموش کردید (عذاب دوزخ را) بچشید، همانا ما نیز فراموش تان کردیم، و بچشید عذاب همیشگی را به سبب آنچه می‌کردید.');
INSERT INTO "badkhashani" VALUES(32,15,'32:15','جز این نیست که کسانی به آیات ما ایمان می‌آورند که چون آن آیات به ایشان یادآوری شود، سجده کنان می‌افتند و پروردگارشان را با ستایش و به پاکی یاد می‌کنند و تکبر نمی‌ورزند.');
INSERT INTO "badkhashani" VALUES(32,16,'32:16','پهلوهایشان از بسترها دور می‌شود، با بیم و امید پروردگارشان را می‌خوانند و از آنچه به آنها روزی داده‌ایم انفاق می‌کنند.');
INSERT INTO "badkhashani" VALUES(32,17,'32:17','پس هیچ‌کس نمی‌داند که از آنچه سبب روشنی چشم‌ها است، چه چیزی (از نعمت‌های حیرت‌آور) برای آنها پنهان داشته شده است به پاداش آنچه می‌کردند.');
INSERT INTO "badkhashani" VALUES(32,18,'32:18','آیا کسی که مؤمن است، مانند کسی است که فاسق است؟ هرگز برابر نیستند.');
INSERT INTO "badkhashani" VALUES(32,19,'32:19','اما کسانی که ایمان آورده‌اند و کارهای نیک کرده‌اند، پس برای آنها باغ‌های جنت است که در آن جایگاه‌شان خواهد بود که بطور پذیرایی برای ایشان آماده شده است، به سبب آنچه می‌کردند.');
INSERT INTO "badkhashani" VALUES(32,20,'32:20','و اما کسانی که فاسق هستند پس جایگاهشان آتش دوزخ است، هرگاه که بخواهند از آن بیرون شوند، در آن باز گردانیده می‌شوند و به آنها گفته می‌شود: بچشید عذاب آتشی را که آن را دروغ می‌شمردید.');
INSERT INTO "badkhashani" VALUES(32,21,'32:21','و حتما عذاب نزدیک‌تر (دنیا) را پیش از عذاب بزرگتر (آخرت) به آنها می‌چشانیم، باشد که آنها (از کفر و شرک و گناه) باز گردند.');
INSERT INTO "badkhashani" VALUES(32,22,'32:22','و کیست ظالم‌تر از کسیکه به آیات پروردگارش پند داده شود ولی باز از آن روی بگرداند؟! بی‌گمان ما از مجرمان انتقام گیرنده‌ایم.');
INSERT INTO "badkhashani" VALUES(32,23,'32:23','و البته ما به موسی کتاب (تورات) دادیم. پس از اینکه موسی (تورات) را دریافت کرده است در شک مباش، و آن را برای بنی اسرائیل (سبب) هدایت گردانیدیم.');
INSERT INTO "badkhashani" VALUES(32,24,'32:24','و از آنان (بعضی از بنی اسرائیل) چون صبر کردند و به آیات ما يقين داشتند پیشوایانی قرار دادیم که به حکم ما راهنمایی می‌کردند.');
INSERT INTO "badkhashani" VALUES(32,25,'32:25','البته پروردگارت در روز قیامت میان آنان در آنچه که در آن اختلاف می‌کردند، فيصله خواهد کرد.');
INSERT INTO "badkhashani" VALUES(32,26,'32:26','آیا برایشان روشن نشده است که پیش از آنان چه بسیار از نسل‌ها را نابود کرده‌ایم و (ایشان هم اینک) در مسکن‌هایشان راه می‌روند؟! بی‌گمان در این دلایل (بزرگ و عبرت آموز) است، آیا نمی‌شنوند؟');
INSERT INTO "badkhashani" VALUES(32,27,'32:27','آیا ندیده‌اند که ما آب را به‌سوی زمین خشک و بی‌گیاه می‌رانیم و به وسیلۀ آن کشتزار را می‌رویانیم که هم چهارپایانشان و هم خودشان از آن می‌خورند؟! آیا نمی‌بینند؟');
INSERT INTO "badkhashani" VALUES(32,28,'32:28','و می‌گویند: این فیصله و قضاوت کی خواهد بود، اگر راستگوئید؟');
INSERT INTO "badkhashani" VALUES(32,29,'32:29','بگو: در روز فیصله و قضاوت ایمان کافران به آنان سودی نمی‌بخشد و نه به آنان مهلت داده می‌شود.');
INSERT INTO "badkhashani" VALUES(32,30,'32:30','پس از آنان روی برگردان و منتظر (روز حساب) باش، چراکه ایشان هم منتظر (شکست و نابودی شما) هستند.');
INSERT INTO "badkhashani" VALUES(33,1,'33:1','ای پیغمبر! از الله بترس و از کافران و منافقان اطاعت مکن، بی‌گمان الله دانای حکیم است.');
INSERT INTO "badkhashani" VALUES(33,2,'33:2','و آنچه را که از سوی پروردگارت به تو وحی می‌شود پیروی کن، البته الله از آنچه می‌کنید آگاه است.');
INSERT INTO "badkhashani" VALUES(33,3,'33:3','و بر الله توکل کن. و الله به عنوان کارساز کافی است.');
INSERT INTO "badkhashani" VALUES(33,4,'33:4','الله برای هیچ کسی دو دل در سینه‌اش ننهاده است، و آن زنانتان را که مورد ظهار قرار می‌دهید مادرانتان نگردانیده است و (همچنین) پسر خوانده‌های تان را پسران شما نگردانیده است. این سخنی شما به زبان‌های تان است و الله حق را می‌گوید و او به راه راست هدایت می‌کند.');
INSERT INTO "badkhashani" VALUES(33,5,'33:5','آنان (پسرخوانده‌ها) را به پدرانشان نسبت دهید، این کار پیش الله عادلانه‌تر است، و اگر پدران‌شان را نشناختید، در آن صورت برادران دینی و آزاد کردگان شما هستند، و در آنچه که اشتباه کرده باشید گناهی بر شما نیست، ليكن (گناه) در آنچه است که دل‌های تان قصد آن را کرده باشد و الله آمرزنده مهربان است.');
INSERT INTO "badkhashani" VALUES(33,6,'33:6','پیغمبر به مؤمنان از خودشان سزاوارتر است، و همسرانش مادران مؤمنان هستند، و خویشاوندان (در استحقاق میراث) نسبت به همدیگر در کتاب الله از مؤمنان و مهاجران اولویت بیشتری دارند، مگر اینکه در حق دوستانتان احسان بکنید. این در کتاب نوشته شده است.');
INSERT INTO "badkhashani" VALUES(33,7,'33:7','و یادآور شو وقتی که از پیغمبران عهد گرفتیم و (نیز) از تو و از نوح و ابراهیم و موسی و عیسی پسر مریم، و از آنها عهد محکم گرفتیم.');
INSERT INTO "badkhashani" VALUES(33,8,'33:8','تا راستگویان را از راستی‌شان بپرسد و (الله) برای کافران عذاب دردناک آماده کرده است.');
INSERT INTO "badkhashani" VALUES(33,9,'33:9','ای کسانی که ایمان آورده‌اید، نعمت الله را بر خود یاد کنید، وقتی که لشکرها به‌سوی شما آمدند پس بر سر آنان طوفانی را فرستادیم و (نیز) لشکرهایی را (به‌سوی‌شان روانه کردیم) که شما آنان را نمی‌دیدید، و الله به آنچه می‌کنید بیناست.');
INSERT INTO "badkhashani" VALUES(33,10,'33:10','وقتی که از طرف بالا و پایین شما به سویتان آمدند و آن هنگام که چشم‌ها خیره شد و دلها به گلوهگاهها رسید و در بارۀ الله گمان‌های گوناگونی می‌کردید.');
INSERT INTO "badkhashani" VALUES(33,11,'33:11','در آن وقت (بود که) مؤمنان آزموده شدند و سخت تکان خوردند و متزلزل شدند.');
INSERT INTO "badkhashani" VALUES(33,12,'33:12','(یاد آور شوید) وقتی را که منافقان و کسانی که در دل‌هایشان بیماری (شک و نفاق) بود می‌گفتند: الله و پیغمبرش جز فریب و وعده‌های دروغ به ما نداده‌اند.');
INSERT INTO "badkhashani" VALUES(33,13,'33:13','و (یاد آور شوید) چون گروهی از آنان گفتند: ای اهل يثرب! دیگر جای پاییدن شما در اینجا نیست، پس باز گردید. و گروهی از آنان از پیغمبر اجازه می‌طلبند و می‌گویند: خانه‌های ما مصئون و محکم نیست، حال آنکه آنها نامصئون و نامحکم نبود و جز فرار قصدی دیگر ندارند.');
INSERT INTO "badkhashani" VALUES(33,14,'33:14','و اگر (لشکر دشمن) بر آنان از اطراف مدینه در آورده می‌شدند، باز از آنان طلب فتنه (خیانت به مسلمانان) می‌شد، حتما این کار را انجام می‌دادند و مگر اندکی در آن درنگ نمی‌کردند.');
INSERT INTO "badkhashani" VALUES(33,15,'33:15','و البته ایشان بودند که پیش از این با الله عهد بسته بودند که (به دشمن) پشت نگردانند و عهد الله بازخواست شدنی است.');
INSERT INTO "badkhashani" VALUES(33,16,'33:16','بگو: اگر از مرگ یا کشته شدن بگریزید، هرگز سودی به شما نمی‌بخشد و در آن صورت جز اندکی بهره مند نمی‌شوید.');
INSERT INTO "badkhashani" VALUES(33,17,'33:17','بگو: چه کسی شما را از عذاب الله حفظ می‌کند اگر او در بارۀ شما بدی خواسته باشد یا برای شما رحمتی خواسته باشد؟! و غیر از الله هیچ کارساز و مددگاری برای خود نخواهند یافت.');
INSERT INTO "badkhashani" VALUES(33,18,'33:18','در حقیقت الله کسانی از شما را که مردم را از جنگ باز می‌داشتند می‌شناسد و (نیز) آنان را که به برادرانشان گفتند: نزد ما بیایید و جز اندکی به جنگ نمی‌آیند.');
INSERT INTO "badkhashani" VALUES(33,19,'33:19','در حالی که بر شما بخیلانند، پس چون وقت ترس رسد آنان را می‌بینی که به‌سوی تو می‌نگرند مانند کسی که از (سختی) مرگ بیهوش شده باشد (و) چشمانش (راست و چپ) می‌چرخد. و چون وقت ترس برود، بر شما با زبانهای تند و تیز زبان درازی می‌کنند (و) بر خیر (غنیمت) بخیلان‌اند. این گروه هرگز ایمان نیاورده‌اند، و الله اعمال ایشان را نابود گردانید. و این کار برای الله آسان است.');
INSERT INTO "badkhashani" VALUES(33,20,'33:20','گمان می‌کنند که احزاب (لشكر‌های کافران) نرفته‌اند و اگر احزاب باز آیند ایشان دوست دارند در میان اعراب بادیه نشین باشند و از اخبار شما سوال کنند. و اگر در میان شما بودند جز اندکی پیکار نمی‌کردند.');
INSERT INTO "badkhashani" VALUES(33,21,'33:21','البته برای شما در (سیرت) رسول الله سرمشق و الگویی نیکوست، برای آن کس که به الله و روز قیامت امید دارد و الله را بسیار یاد می‌کند.');
INSERT INTO "badkhashani" VALUES(33,22,'33:22','و چون مؤمنان احزاب (لشکرهای کافران) را دیدند، گفتند: این همان چیزی است که الله و پیغمبرش به ما وعده داده بودند، و الله و پیغمبرش راست گفتند، و این در حق آنان جز ایمان و تسلیم نیافزود.');
INSERT INTO "badkhashani" VALUES(33,23,'33:23','از مؤمنان مردانی هستند که به آنچه با الله بر آن پیمان بسته بودند، صادقانه وفا کردند، پس از آنان کسی است که قرار دادشان را به انجام رساندند و از آنان کسی است که در انتظارند و (در عهد و پیمان خود) هیچ گونه تغییر و تبدیلی نیاورده‌اند.');
INSERT INTO "badkhashani" VALUES(33,24,'33:24','تا الله راستگویان را به خاطر راستی‌شان پاداش بدهد و منافقان را اگر بخواهد عذاب کند یا بر آنان به رحمت باز گردد (ببخشد). به تحقيق الله آمرزندۀ مهربان است.');
INSERT INTO "badkhashani" VALUES(33,25,'33:25','و الله کافران را به خشم و غضبشان بازگردانید، بی‌آنکه به خیری رسیده باشند و الله (با فرستادن طوفان)، مؤمنان را از جنگ بی‌نیاز ساخت و الله توانای غالب است.');
INSERT INTO "badkhashani" VALUES(33,26,'33:26','و الله کسانی از اهل کتاب را که آنان را مدد کرده بودند، از قلعه‌هایشان فرود آورد و در دل‌هایشان ترس و هراس انداخت! (به طوری که) گروهی را می‌کشید و گروهی را اسیر می‌گرفتید.');
INSERT INTO "badkhashani" VALUES(33,27,'33:27','و زمین‌شان و دیارشان و دارایی شان، و همچنین زمینی را که هنوز گام به آنجا ننهاده بودید به شما میراث داد، و البته الله بر هرچیزی تواناست.');
INSERT INTO "badkhashani" VALUES(33,28,'33:28','ای پیغمبر! به همسران خود بگو: اگر خواهان زندگانی دنیا و زینت آن هستید، پس بیایید تا به شما هدیۀ مناسب بدهم و شما را به رها کردنی نیک رها سازم.');
INSERT INTO "badkhashani" VALUES(33,29,'33:29','و اگر الله و پیغمبرش و سرای آخرت را می‌خواهید، پس الله برای نیکوکاران شما پاداش بزرگی را آماده کرده است.');
INSERT INTO "badkhashani" VALUES(33,30,'33:30','ای همسران پیغمبر! هر کس از شما کار ناشایست آشکار را به عمل آورد، عذابش دو چندان خواهد بود. و این کار برای الله آسان است.');
INSERT INTO "badkhashani" VALUES(33,31,'33:31','و هر کس از شما که برای الله و پیغمبرش قانت و فرمانبردار باشد و کار نیک انجام دهد، پاداشش را دو بار به او می‌دهیم و برای او روزی نیک و ارزشمندی آماده کرده‌ایم.');
INSERT INTO "badkhashani" VALUES(33,32,'33:32','ای زنان پیغمبر! شما مانند هیچ یک از زنان دیگر نیستید اگر پرهیزگار باشید، پس در سخن گفتن نرمی و ملایمت نکنید تا مبادا کسی که در دلش بیماری است (در شما) طمع کند، و سخن شایسته بگویید.');
INSERT INTO "badkhashani" VALUES(33,33,'33:33','و در خانه‌های تان قرار گیرید و همچون زینت نمایی روزگار جاهلیت پیشین (در میان مردم ظاهر نشوید و) خودنمایی نکنید و نماز را برپا کنید و زکات را بدهید و از الله و پیغمبرش اطاعت کنید. جز این نیست که الله می‌خواهد پلیدی را از شما اهل بیت دور کند و شما را کاملاً پاکیزه گرداند.');
INSERT INTO "badkhashani" VALUES(33,34,'33:34','و آنچه را که از آيات الله و حکمت در خانه‌هایتان خوانده می‌شود یاد کنید، بی‌گمان الله لطف کننده آگاه است.');
INSERT INTO "badkhashani" VALUES(33,35,'33:35','بدون شک مردان مسلمان و زنان مسلمان، مردان با ایمان و زنان با ایمان، مردان فرمانبردار فرمان الله و زنان فرمان بردار فرمان الله و مردان و زنان راستگو، و مردان و زنان بردبار، و مردان فروتن و زنان فروتن و مردان صدقه دهنده، و زنان صدقه دهنده و مردان روزه دار و زنان روزه دار، و مردان پاکدامن و زنان پاکدامن، و مردان و زنانی که الله را بسیار یاد می‌کنند، الله برای آنان آمرزش و پاداش بزرگ آماده کرده است.');
INSERT INTO "badkhashani" VALUES(33,36,'33:36','و هیچ مرد مؤمن و زن مؤمنه در کاری که الله و پیغمبرش (در آن) فیصله کرده باشند اختیاری از خود ندارند و کسی که از الله و پیغمبرش نافرمانی کند در حقیقت به گمراهی آشکار گرفتار شده است.');
INSERT INTO "badkhashani" VALUES(33,37,'33:37','و یادآور شو چون به کسی که الله به او نعمت داده بود و تو (نیز) بر او انعام کرده بودی، می‌گفتی: همسرت را برای خود نگاه دار و از الله بترس و در نفس خود آنچه را که الله ظاهر کننده‌اش است پنهان می‌ساختی، و از مردم می‌ترسیدی و الله به آنکه از او بترسی سزاوارتر است. پس چون زید نیاز خود را از او برآورد، او را به ازدواج تو در آوردیم تا تنگی برای مؤمنان در ازدواج با همسران پسر خواندگانشان نباشد، وقتی که نیاز خود را از آنان به انجام رسانند، و حكم الله انجام یافتنی است.');
INSERT INTO "badkhashani" VALUES(33,38,'33:38','بر پیغمبر در آنچه الله برای او مقدر و معین گردانیده هیچ تنگی نیست، این سنت الهی در مورد پیغمبران پیشین نیز جاری بوده و حكم الله به اندازه مقدر و معین است.');
INSERT INTO "badkhashani" VALUES(33,39,'33:39','همان کسانیکه پیام‌های الله را ابلاغ می‌کنند و از او می‌ترسند و جز الله از کسی نمی‌ترسند. و الله از لحاظ محاسبه با بندگان خود کافی است.');
INSERT INTO "badkhashani" VALUES(33,40,'33:40','محمّد پدر هیچ یک از مردان شما نیست بلکه فرستادۀ الله و خاتم پیغمبران است و الله به همه چیز داناست.');
INSERT INTO "badkhashani" VALUES(33,41,'33:41','ای کسانی که ایمان آورده‌اید! الله را بسیار یاد کنید.');
INSERT INTO "badkhashani" VALUES(33,42,'33:42','و او را صبح و شام به پاکی یاد کنید.');
INSERT INTO "badkhashani" VALUES(33,43,'33:43','او (الله) ذاتی است که بر شما درود (و رحمت) می‌فرستد و فرشتگانش (نیز برای شما دعا می‌کنند) تا (الله) شما را از تاریکی‌ها به‌سوی نور بیرون کند و (الله) به مؤمنان بسیار مهربان است.');
INSERT INTO "badkhashani" VALUES(33,44,'33:44','درودشان در روزی که او را ملاقات می‌کنند سلام است و برای آنان پاداش نیک آماده کرده است.');
INSERT INTO "badkhashani" VALUES(33,45,'33:45','ای پیغمبر! ما تو را گواه و مژده دهنده و بیم دهنده فرستادیم.');
INSERT INTO "badkhashani" VALUES(33,46,'33:46','و (نیز) دعوتگر به‌سوی الله به حکم او و چراغ روشن هستی.');
INSERT INTO "badkhashani" VALUES(33,47,'33:47','و (ای پیغمبر!) به مؤمنان مژده بده که از (سوی) الله برای‌شان فضل بزرگ است.');
INSERT INTO "badkhashani" VALUES(33,48,'33:48','و از کافران و منافقان اطاعت مکن و اذیت و آزارشان را اعتبار مده (و استقامت داشته باش) و بر الله توکل کن و همین بس که الله کارساز باشد.');
INSERT INTO "badkhashani" VALUES(33,49,'33:49','ای مؤمنان! چون با زنان مؤمن ازدواج کردید باز پیش از آنکه با آنان آمیزش کنید طلاقشان دادید دیگر شما را بر این زنان عدّه‌ای نیست که آن را بشمارید، پس ایشان را از هدیۀ مناسب بهره مند سازید و به رها کردنی نیک رهایشان کنید.');
INSERT INTO "badkhashani" VALUES(33,50,'33:50','ای پیغمبر! همانا ما برای تو حلال کرده‌ایم (آن) همسرانت را که مهرشان را داده ای، و همچنین کنیزانی را که الله در جنگ بهرۀ تو ساخته است، و دختران عمویت و دختران عمه‌هایت و دختران خالوهایت و دختران خاله‌هایت که با تو هجرت کرده‌اند، و زن مؤمنی که خویشتن را به پیغمبر ببخشد (و) اگر پیغمبر بخواهد که او را به زنی گیرد که مخصوص توست نه دیگر مؤمنان، به راستی دانستیم آنچه را که بر آنان دربارۀ زنانشان و کنیزانشان مقرر کرده‌ایم تا بر تو هیچ حرج و تنگی نباشد و الله آمرزندۀ مهربان است.');
INSERT INTO "badkhashani" VALUES(33,51,'33:51','(نوبت) هر یک از زنانت را که بخواهی (می‌توانی) به تاخیر اندازی و هر کدام را بخواهی (می‌توانی) نزد خود جای دهی، و اگر یکی از آنان را که (از او) کناره گرفته‌ای باز (به بستر خود) طلب کنی، (در هر حال،) بر تو هیچ گناهی نیست، این نزدیک‌تر است به آن که چشمان‌شان روشن گردد و اندوهگین نشوند و همگی‌شان به آنچه به ایشان داده‌ای خشنود گردند و الله آنچه را که در دل‌های شماست می‌داند و الله دانای بردبار است.');
INSERT INTO "badkhashani" VALUES(33,52,'33:52','بعد از این دیگر گرفتن زنان برای تو حلال نیست و برایت حلال نیست که زنان دیگر را جایگزین آنها کنی هرچند زیبایی آنان تو را به شگفت آورد، به استثنای کنیزانی که در ملک تو هستند. و الله بر همه چیز مراقب است.');
INSERT INTO "badkhashani" VALUES(33,53,'33:53','ای مؤمنان! وارد خانه‌های پیغمبر نشوید مگر آنکه برای (صرف) طعام به شما اجازه داده شود، بی‌آن که منتظر (پخته شدن) آماده شدنش باشید، ولی هنگامی که دعوت شدید وارد شوید و زمانی که غذا را خوردید پراکنده شوید و به گفتگو ننشینید، این کار پیغمبر را رنج می‌دهد اما او شرم می‌کند ولی الله از بیان حق شرم نمی‌کند. و چون از زنان پیغمبر متاعی خواستید از پس پرده از ایشان بخواهید، این برای دلهایتان و دل‌های آنان پاکیزه‌تر است، و برای شما سزاوار نیست که پیغمبر الله را برنجانید و حق ندارید که پس از مرگ او همسرانش را به همسری خویش در آورید، البته این کار نزد الله گناهی بزرگ است.');
INSERT INTO "badkhashani" VALUES(33,54,'33:54','اگر چیزی را آشکار کنید، یا آن را پنهان دارید، پس (بدانید که) الله به هر چیز داناست.');
INSERT INTO "badkhashani" VALUES(33,55,'33:55','بر زنان گناهی نیست که از پدران‌شان و فرزندان‌شان و برادران‌شان و فرزندان برادران‌شان و فرزندان خواهران‌شان و زنان (هم‌جنس) خودشان و بردگان‌شان (حجاب نگیرند)، ای زنان! از الله بترسید، البته الله بر همه چیز گواه (حاضر و ناظر) است.');
INSERT INTO "badkhashani" VALUES(33,56,'33:56','همانا الله و فرشتگانش بر پیغمبر درود می‌فرستند، (پس) ای مؤمنان! شما هم بر او درود بفرستید و چنانکه باید درود بگویید.');
INSERT INTO "badkhashani" VALUES(33,57,'33:57','همانا کسانی که الله و پیغمبرش را می‌رنجانند الله آنها را در دنیا و آخرت لعنت کرده است و برایشان عذاب خوار کننده‌ای آماده کرده است.');
INSERT INTO "badkhashani" VALUES(33,58,'33:58','و کسانی که مردان و زنان مؤمن را بدون اینکه مرتکب عمل ناروا شده باشند، آزار می‌رسانند به راستی که (بار) بهتان و گناه آشکار بر گردن گرفته‌اند.');
INSERT INTO "badkhashani" VALUES(33,59,'33:59','ای پیغمبر! به زنانت و دخترانت و زنان مؤمنان بگو: چادرهایشان را بر خود بیاندازند، این (انداختن چادر) نزدیکتر است به آنکه (به پاکدامنی) شناخته شوند و آزار نبینند و الله آمرزندۀ مهربان است.');
INSERT INTO "badkhashani" VALUES(33,60,'33:60','اگر منافقان و کسانی که در دل‌هایشان بیماری است و شایعه سازان در مدینه (که باعث اضطراب می‌گردند) از کار خود دست نکشند به یقین تو را بر آنان مسلط می‌گردانیم باز جز اندک مدتی در آن شهر با تو مجاور نخواهند بود.');
INSERT INTO "badkhashani" VALUES(33,61,'33:61','در حالیکه لعنت شدگانند، هر کجا یافت شوند، اسیر گرفته شده و به سختی کشته شوند.');
INSERT INTO "badkhashani" VALUES(33,62,'33:62','این است سنت الهی در مورد کسانیکه پیش از این گذشته‌اند و هرگز برای سنت الله تغییر و تبدیلی نمی‌یابی.');
INSERT INTO "badkhashani" VALUES(33,63,'33:63','مردم از تو دربارۀ (وقت فرا رسیدن) قیامت می‌پرسند، بگو: علم آن تنها نزد الله است و تو چه دانی شاید که قیامت نزدیک باشد.');
INSERT INTO "badkhashani" VALUES(33,64,'33:64','به تحقیق الله کافران را لعنت کرده و برایشان آتش سوزان آماده کرده است. ­');
INSERT INTO "badkhashani" VALUES(33,65,'33:65','آنان جاودانه در آن خواهند ماند، (و) هیچ دوست و یاوری نمی‌یابند.');
INSERT INTO "badkhashani" VALUES(33,66,'33:66','روزی که چهره‌هایشان در آتش گردانده می‌شود، می‌گویند: ای کاش الله و پیغمبر را اطاعت می‌کردیم.');
INSERT INTO "badkhashani" VALUES(33,67,'33:67','و گویند: ای پروردگار ما! ما از پیشوایان و بزرگان خود اطاعت نمودیم و ما را از راه گمراه ساختند.');
INSERT INTO "badkhashani" VALUES(33,68,'33:68','ای پروردگار ما! آنان را دوچندان عذاب ده و آنان را لعنت کن لعنتی بزرگ.');
INSERT INTO "badkhashani" VALUES(33,69,'33:69','ای کسانی که ایمان آورده‌اید، مانند کسانی نباشید که موسی را آزار دادند پس الله او را از آنچه می‌گفتند پاک و مبرا ساخت، و موسی نزد الله آبرومند بود.');
INSERT INTO "badkhashani" VALUES(33,70,'33:70','ای کسانی که ایمان آورده‌اید، از الله بترسید و سخن حق و راست بگویید.');
INSERT INTO "badkhashani" VALUES(33,71,'33:71','اعمالتان را اصلاح می‌کند و گناهانتان را می‌آمرزد. و هر کس از الله و پیغمبرش اطاعت کند به راستی که به پیروزی و کامیابی بزرگی نایل آمده است.');
INSERT INTO "badkhashani" VALUES(33,72,'33:72','البته ما امانت را بر آسمان‌ها و زمین و کوه‌ها عرضه داشتیم ولی از برداشتن آن ابا ورزیدند و از آن ترسیدند و انسان آن را برداشت، يقينا او ظالم نادان است.');
INSERT INTO "badkhashani" VALUES(33,73,'33:73','تا (در نتیجه) الله مردان و زنان منافق و مردان و زنان مشرک را عذاب کند و بر مردان و زنان مؤمن به رحمت باز گردد و الله آمرزندۀ مهربان است.');
INSERT INTO "badkhashani" VALUES(34,1,'34:1','همه ستایش‌ها آن ذاتی راست که تمام آنچه در آسمان‌ها و زمین است خاص از اوست و در آخرت نیز ستایش سزاوار اوست و او باحکمت آگاه است.');
INSERT INTO "badkhashani" VALUES(34,2,'34:2','می‌داند آنچه را که در زمین فرو می‌رود و آنچه را که از آن بیرون می‌آید و آنچه را که از آسمان فرود می‌آید و آنچه را که در آن بالا می‌رود و او مهربان آمرزنده است.');
INSERT INTO "badkhashani" VALUES(34,3,'34:3','و کافران گفتند: قیامت برای ما فرا نمی‌رسد، بگو: آری! سوگند به پروردگارم، به یقین به سراغتان خواهد آمد، (پروردگار که) دانای غیب است. به اندازۀ ذره‌ای نه در آسمان‌ها و نه در زمین از او پنهان نمی‌ماند، و نه کمتر از آن و نه بزرگ‌تر از آن چیزی نیست مگر آنکه در کتابی روشن (ثبت) است.');
INSERT INTO "badkhashani" VALUES(34,4,'34:4','تا الله پاداش کسانی را بدهد که ایمان آورده و کارهای نیک کرده‌اند، ایشان‌اند که برایشان آمرزش و روزی گرامی است.');
INSERT INTO "badkhashani" VALUES(34,5,'34:5','و کسانی که سعی کردند آیات ما را تکذیب و انکار کنند و گمان کردند که عاجز کننده (پیغمبر ما)‌اند، آنان برایشان عذابی از بدترین و دردناکترین عذاب‌ها خواهد بود.');
INSERT INTO "badkhashani" VALUES(34,6,'34:6','و کسانی که علم داده شده‌اند می‌بینند که آنچه از جانب پروردگارت به‌سوی تو نازل شده حق است و این که به‌سوی راه ذات غالب و دارای صفات کمال و ستوده راهنمایی می‌کند.');
INSERT INTO "badkhashani" VALUES(34,7,'34:7','و کافران گفتند: آیا شما را بر مردی راهنمایی کنیم که به شما خبر می‌دهد: چون (بعد از مرگ) کاملاً متلاشی شدید (پس از آن) به آفرینش جدید آفریده می‌شوید؟');
INSERT INTO "badkhashani" VALUES(34,8,'34:8','آیا بر الله دروغ بسته؟ یا او دیوانگی دارد؟ (چنین نیست) بلکه کسانی که به آخرت ایمان نمی‌آورند در عذاب و گمراهی دور از حق هستند.');
INSERT INTO "badkhashani" VALUES(34,9,'34:9','آیا به‌سوی آنچه که پیش رو و پشت سر‌شان از آسمان و زمین قرار دارد ندیدند؟ اگر بخواهیم آنان را در زمین فرو می‌بریم یا پاره‌هایی از آسمان را بر آنان می‌افگنیم. بی‌گمان در این امر دلیل و نشانه‌ای است برای هر بنده‌ای که بخواهد خالصانه به‌سوی الله رجوع کند.');
INSERT INTO "badkhashani" VALUES(34,10,'34:10','و البته از سوی خود به داود فضل دادیم (و گفتیم) ای کوه‌ها! همراه او تسبیح گویید و (نیز) پرندگان را (گفتیم با او تسبیح گویید). و آهن را برای او نرم کردیم.');
INSERT INTO "badkhashani" VALUES(34,11,'34:11','(ما به داود دستور دادیم) که زره‌های کامل و پهن بساز و در بافتن (حلقه‌های آن) اندازه را مراعات کن. و کار شایسته کنید که البته من به آنچه می‌کنید بینا هستم.');
INSERT INTO "badkhashani" VALUES(34,12,'34:12','و برای سلیمان باد را (مسخر کردیم) که سیر صبحگاهان آن مسیر یک ماه و سیر شامگاهان آن (نیز) مسیر یک ماه بود. و چشمۀ مس را برای او روان ساختیم. و برخی از جنیان (را برای او رام کردیم که) به فرمان پروردگارش در نزد او کار می‌کردند، و هرکس از آنها از فرمان ما سرپیچی کند، از عذاب سوزان به او می‌چشانیم.');
INSERT INTO "badkhashani" VALUES(34,13,'34:13','آنان (جنیان) هرچه سلیمان می‌خواست برایش می‌ساختند از قبیل قلعه‌ها و مجسمه‌ها و کاسه‌های بزرگی چون حوض‌ها و دیگ‌های ثابت بر جای خود. (به آنان گفتیم:) ای آل داود، شکر [نعمت‌های مرا] به جای آورید که اندکی از بندگانم سپاسگزارند.');
INSERT INTO "badkhashani" VALUES(34,14,'34:14','پس چون مرگ را بر او مقرر کردیم چیزی جز موریانه که عصایش را می‌خورد آنان را از مرگ او مطلع نساخت، پس چون جسد سلیمان (به زمین) افتاد، جنیان پی بردند که اگر غیب را می‌دانستند، در عذاب خوار کننده باقی نمی‌ماندند.');
INSERT INTO "badkhashani" VALUES(34,15,'34:15','بی‌گمان برای (قوم) سبا در محل سکونت‌شان نشانه‌ای بود: دو باغ از جانب راست و از جانب چپ، (گفتيم) از روزی پروردگارتان بخورید و شکر او را به جای آورید، شهری است پاکیزه و پروردگاری است آمرزنده.');
INSERT INTO "badkhashani" VALUES(34,16,'34:16','اما آنان روی گرداندند، پس سیلی ویرانگر را بر آنان فرستادیم و به جای آن دو باغ‌شان دو باغ دیگر عوض دادیم که دارای میوه‌های تلخ و درخت گز و اندکی از درخت کنار بود.');
INSERT INTO "badkhashani" VALUES(34,17,'34:17','این عقوبت را به سزای کفرشان به آنان دادیم، و آیا جز ناسپاس را مجازات می‌کنیم؟');
INSERT INTO "badkhashani" VALUES(34,18,'34:18','و میان آنها و میان آبادی‌هایی که در آنها برکت نهاده بودیم آبادی‌هایی به هم پیوسته قرار داده بودیم و در میان آنها مسافت را به اندازه مقرّر داشته بودیم، (و گفتیم) شب‌ها و روزها را با کمال امن و امان در آنها سیر و سفر کنید.');
INSERT INTO "badkhashani" VALUES(34,19,'34:19','اما ایشان گفتند: ای پروردگار ما! فاصلۀ سفرهای ما را دور کن، و بر خود ظلم کردند. پس آنان را افسانه‌ها (بر سر زبان‌ها) گردانیدیم و آنان را سخت متلاشی و پاره پاره ساختیم، البته در این ماجرا برای هر صابر سپاسگزار نشانه‌های (عبرت انگیزی) است.');
INSERT INTO "badkhashani" VALUES(34,20,'34:20','و البته ابلیس گمانش را دربارۀ آنان راست یافت، پس از او پیروی کردند جز گروهی از مؤمنان.');
INSERT INTO "badkhashani" VALUES(34,21,'34:21','و شیطان را بر آنان هیچ سلطۀ نبود، مگر تا کسی را که به آخرت ایمان می‌آورد از کسی که او از آن در شک است معلوم بداریم، و پروردگار تو بر همه چیز مراقب و نگهبان است.');
INSERT INTO "badkhashani" VALUES(34,22,'34:22','(ای پیغمبر) بگو: آنان را که به جای الله (معبودتان) می‌پندارید بخوانید، آنها در آسمان‌ها و زمین به اندازۀ ذره‌ای را مالک شده نمی‌توانند، و در (تدبیر) آن دو، هیچ مشارکتی ندارند و برای الله در میانشان هیچ یاور و پشتیبانی نیست.');
INSERT INTO "badkhashani" VALUES(34,23,'34:23','و شفاعت در نزد او سود نمی‌بخشد مگر برای آن کس که الله به او اجازه دهد، تا چون که اضطراب و هراس از دل‌هایشان برطرف گردد می‌گویند: پروردگارتان چه گفت؟ می‌گویند: حق، و او والای بزرگ است.');
INSERT INTO "badkhashani" VALUES(34,24,'34:24','بگو: کیست که از آسمان‌ها و زمین شما را روزی می‌دهد؟ بگو: الله، و يقينا يا ما، یا شما بر هدایت یا گمراهی آشکار هستیم.');
INSERT INTO "badkhashani" VALUES(34,25,'34:25','بگو: شما از جرمی که ما کرده‌ایم باز خواست نمی‌شوید و ما نیز از آنچه شما می‌کنید بازخواست نمی‌شویم.');
INSERT INTO "badkhashani" VALUES(34,26,'34:26','بگو: پروردگار ما، ما و شما را جمع می‌کند باز در میان ما به حق حکم می‌کند و او فیصله‌کنندۀ داناست.');
INSERT INTO "badkhashani" VALUES(34,27,'34:27','بگو: آنان را که به عنوان شریک به او ملحق ساخته‌اید به من نشان دهید، چنین نیست، بلکه او الله است که غالب باحکمت است.');
INSERT INTO "badkhashani" VALUES(34,28,'34:28','و ما تو را جز مژده رسان و بیم دهنده برای تمام مردم نفرستادیم و لیکن اکثر مردم نمی‌دانند.');
INSERT INTO "badkhashani" VALUES(34,29,'34:29','و می‌گویند: این وعده کی فرا می‌رسد، اگر راست می‌گوئید؟');
INSERT INTO "badkhashani" VALUES(34,30,'34:30','بگو: وعده شما روز معینی است که نه ساعتی از آن پس می‌مانید و نه ساعتی از آن پیشی می‌گیرید.');
INSERT INTO "badkhashani" VALUES(34,31,'34:31','و کافران گفتند: به این قرآن و به آنچه پیش از آن است هرگز ایمان نمی‌آوریم. و اگر ببینی ظالمان را وقتی که نزد پروردگارشان نگاه داشته می‌شوند که چگونه بعضی از آنها سخن بعضی دیگر را باز میگرداند، مستضعفان به مستکبران می‌گویند: اگر شما نبوديد يقينا ما مؤمن می‌بودیم.');
INSERT INTO "badkhashani" VALUES(34,32,'34:32','(باز) مستکبران (در پاسخ) به مستضعفان می‌گویند: آیا پس از آنکه هدایت برایتان آمد، ما شما را از آن بازداشتیم؟ بلکه خود شما مجرم بودید.');
INSERT INTO "badkhashani" VALUES(34,33,'34:33','و (بار دیگر) مستضعفان به مستکبران می‌گویند: نه، بلکه مکر شب و روز (تان ما را از هدایت بازداشت) وقتی که به ما دستور می‌دادید که به الله كفر ورزیم و برای او همتایان مقرر کنیم، و وقتی عذاب را بینند، پشیمانی خود را پنهان کنند. و زنجیرها را به گردن‌های کافران می‌اندازیم. آیا جز در برابر کارهایی که می‌کردند، جزا داده می‌شوند؟');
INSERT INTO "badkhashani" VALUES(34,34,'34:34','و ما در هیچ قریه‌ای بیم دهنده‌ای نفرستادیم مگر آنکه سرکشان نازپرور آنجا گفتند: ما به آنچه شما به آن فرستاده شده‌اید، کافریم.');
INSERT INTO "badkhashani" VALUES(34,35,'34:35','و (نیز) گفتند: ما از نگاه اموال و فرزندان بیشتریم و ما عذاب شونده نیستیم.');
INSERT INTO "badkhashani" VALUES(34,36,'34:36','بگو: البته پروردگارم روزی را برای هرکس که بخواهد فراخ یا تنگ می‌گرداند، ولی بیشتر مردم نمی‌دانند.');
INSERT INTO "badkhashani" VALUES(34,37,'34:37','و اموال و اولادتان چیزی نیست که شما را به ما نزدیک گرداند به مرتبۀ قربت، مگر کسی (مقرب) است که ایمان آورد و کار نیک انجام دهد. پس برای آنان در برابر اعمالی که انجام داده‌اند پاداش دو چندان است و ایشان در غرفه‌های (بلند) ايمن‌اند.');
INSERT INTO "badkhashani" VALUES(34,38,'34:38','و کسانی که در (تکذیب) آیات ما سعی می‌کنند به این گمان که (پیغمبر ما را) عاجز می‌سازند، ایشان‌اند که در عذاب احضار خواهند شد.');
INSERT INTO "badkhashani" VALUES(34,39,'34:39','بگو: در حقیقت پروردگارم است که روزی را برای هر کس از بندگانش که بخواهد فراخ می‌گرداند، و برای هر کس که بخواهد تنگ می‌گرداند. و هرچه را انفاق کنید، الله عوضش را می‌دهد و او بهترین روزی دهندگان است.');
INSERT INTO "badkhashani" VALUES(34,40,'34:40','و (یادآور شو) روزی را که الله همۀ آنان را محشور می‌کند، باز به فرشتگان می‌گوید: آیا اینها بودند که شما را پرستش می‌کردند؟');
INSERT INTO "badkhashani" VALUES(34,41,'34:41','(فرشته‌ها) گویند: تو پاک و منزهی! و تنها تو باور و کارساز ما هستی! نه، بلکه آنها جنيان را پرستش می‌کردند؟ و بیشترشان به آنها ایمان داشتند.');
INSERT INTO "badkhashani" VALUES(34,42,'34:42','پس امروز هیچ یک از شما مالک نفع و ضرری برای دیگری نیستید، و به ظالمان می‌گوییم: بچشید عذاب آتشی را که آن را تکذیب می‌کردید. ­');
INSERT INTO "badkhashani" VALUES(34,43,'34:43','و چون آیات روشن ما بر آنان خوانده شود، می‌گویند: این (شخص) جز مردی نیست، که می‌خواهد شما را از آنچه پدران شما پرستش می‌کردند باز دارد و می‌گویند: این جز دروغ خودساخته نیست (که به الله نسبت داده شده است). و کافران (درباره) حق چون به آنان آمد گفتند: این جز جادویی آشکار نیست.');
INSERT INTO "badkhashani" VALUES(34,44,'34:44','و ما هیچ کتابی برای ایشان نداده بودیم که آن را بخوانند، و پیش از تو به‌سوی آنان هیچ بیم دهنده‌ای نفرستادیم.');
INSERT INTO "badkhashani" VALUES(34,45,'34:45','و کسانی که پیش از آنها بودند (وحی ما را) تکذیب کردند، حال آنکه آنان به یک دهم آنچه به پیشینیان داده بودیم نرسیده‌اند، پس پیغمبران ما را تکذیب کردند، پس (بنگر) عقوبت من چگونه بود؟');
INSERT INTO "badkhashani" VALUES(34,46,'34:46','بگو: من تنها شما را به یک (سخن) نصیحت می‌کنم و آن این است که خالصانه برای الله دونفر دونفر، و یا یک نفر یک نفر، برخیزید، باز اندیشه کنید که این همنشین شما هیچ دیوانگی ندارد، او برای شما جز بیم دهنده، پیش از آمدن عذاب سخت نیست.');
INSERT INTO "badkhashani" VALUES(34,47,'34:47','بگو: هر مزدی که از شما خواسته باشم، پس آن برای شما باشد، مزد من جز بر (عهدۀ) الله نیست و او بر هر چیز گواه است.');
INSERT INTO "badkhashani" VALUES(34,48,'34:48','بگو: پروردگار من حق را (در قلب هرکه بخواهد) می‌اندازد (او) دانای غيب‌هاست.');
INSERT INTO "badkhashani" VALUES(34,49,'34:49','بگو: حق آمد و دیگر باطل از سر نمی‌گیرد (سر از نو ایجاد نمی‌شود) و دوباره برنمی گردد.');
INSERT INTO "badkhashani" VALUES(34,50,'34:50','بگو: اگر گمراه شوم تنها به زبان خود گمراه می‌شوم و اگر راهیاب شوم پس این به سبب آن است که پروردگارم به من وحی می‌کند، بی‌گمان او شنوای نزدیک است.');
INSERT INTO "badkhashani" VALUES(34,51,'34:51','و اگر می‌دیدی وقتی را که مضطرب و هراسناک شوند، آنجا راه گریزی نیست. و از جایی نزدیک گرفتار شوند.');
INSERT INTO "badkhashani" VALUES(34,52,'34:52','و (وقتی عذاب را مشاهده کنند) می‌گویند: اکنون به او ایمان آوردیم، حال آنکه دسترسی به ایمان از راه دور چگونه ممکن است.');
INSERT INTO "badkhashani" VALUES(34,53,'34:53','و حال آنکه پیش از این به آن کفر ورزیدند. و از مکان دور به امور غیب (نسبت‌های باطل‌شان را) می‌افگنند.');
INSERT INTO "badkhashani" VALUES(34,54,'34:54','و میان آنان و میان آنچه خوش دارند حایلی افگنده شد چنان که پیش از این با (گروه‌های) امثال آنان انجام شده است، زیرا آنان سخت در شک بودند.');
INSERT INTO "badkhashani" VALUES(35,1,'35:1','همۀ ستایش‌ها سزاوار پروردگاری است که آفرینندۀ آسمان‌ها و زمین است (ذاتی که) فرشتگان را پیام آوران قرار داده است که دارای بالهای دوگانه و سه گانه و چهارگانه‌اند. هرچه بخواهد در آفرینش می‌افزاید، چون الله بر هر چیز قادر است.');
INSERT INTO "badkhashani" VALUES(35,2,'35:2','هر (درِ) رحمت را که الله برای مردم بگشاید پس بازدارنده‌ای برای آن نیست، و هر چیزی را که (الله) باز دارد، پس جز او گشاینده و باز کننده‌ای نیست. و او توانای باحكمت است.');
INSERT INTO "badkhashani" VALUES(35,3,'35:3','ای مردم! نعمت الله را بر خود یاد کنید، آیا آفریدگاری جز الله وجود دارد که به شما از آسمان و زمین روزی بدهد؟ جز او معبود بر حقی وجود ندارد، پس با این حال چگونه (از حق) برگردانیده می‌شوید.');
INSERT INTO "badkhashani" VALUES(35,4,'35:4','و اگر تو را تکذیب کنند (بدان که) پیغمبران پیش از تو نیز تکذیب شده‌اند. و کارها به سوى الله باز گردانیده می‌شود.');
INSERT INTO "badkhashani" VALUES(35,5,'35:5','ای مردم! البته وعدۀ الله (در بارۀ بازگشت به‌سوی او) راست است، پس زندگی دنیا شما را فریب ندهد و شیطان فریبکار شما را دربارۀ الله نفریبد.');
INSERT INTO "badkhashani" VALUES(35,6,'35:6','به تحقیق شیطان دشمن شماست، پس او را دشمن بگیرید. جز این نیست که او پیروان خود را دعوت می‌دهد تا از دوزخیان باشند.');
INSERT INTO "badkhashani" VALUES(35,7,'35:7','کسانی که (با پیروی از شیطان) کفر ورزیدند، عذاب سختی دارند. و کسانی که ایمان آورده و کارهای نیک انجام دادند، برای آنان آمرزش و پاداش بزرگ است.');
INSERT INTO "badkhashani" VALUES(35,8,'35:8','آیا کسی که زشتی عملش برای او آراسته جلوه داده شده است پس آن را نیک می‌بیند (مانند کسی است که چنین نمی‌باشد؟) بیشک الله هرکس را که بخواهد گمراه می‌کند و هرکس را که بخواهد هدایت می‌کند پس نباید خود را به سبب حسرت خوردن بر آنان هلاک سازی، البته الله به آنچه می‌کنند داناست.');
INSERT INTO "badkhashani" VALUES(35,9,'35:9','و الله ذاتی است که بادها را فرستاد پس آن (بادها) ابرها را بر می‌انگیزند و ما آنها را به‌سوی سرزمینی مرده راندیم و با آن زمین را پس از مردنش زنده ساختیم، برانگیختن مردم نیز چنین است.');
INSERT INTO "badkhashani" VALUES(35,10,'35:10','هرکس عزت می‌خواهد (بداند) که عزت همگی از الله است، سخنان پاکیزه به‌سوی او بالا می‌رود و عمل صالح آنرا بالا می‌برد و کسانی که با مکر و حیله بدیها را می‌اندیشند برای آنها عذاب سخت است و مکر و حیله سنجی‌شان خود برباد و تباه می‌گردد.');
INSERT INTO "badkhashani" VALUES(35,11,'35:11','و الله شما را از خاک باز از نطفه آفرید، باز شما را به صورت زوجهایی (نر و ماده) در آورد، و هیچ زنی باردار نمی‌شود و وضع حمل نمی‌کند مگر به علم او، و هیچ سالخورده‌ای عمر دراز نمی‌یابد و از عمرش کاسته نمی‌شود مگر اینکه در کتاب (لوح محفوظ) ثبت و ضبط شده است. به یقین این (کار) برای الله آسان است.');
INSERT INTO "badkhashani" VALUES(35,12,'35:12','و دو دریا برابر نیستند، این شیرین تشنگی بر است که نوشیدنش خوشگوار می‌باشد و آن دیگر شور (و) تلخ مزه است. و از هریک گوشت تازه می‌خورید و از هر دوی آنها وسائل زينت بیرون می‌آورید که آن را می‌پوشید. و کشتی‌ها را در آن (دریا) پاره کننده می‌بینی (که امواج دریا را شگافته می‌آیند) تا از فضل او روزی خود را طلب کنید و باشد که شکرگزارید.');
INSERT INTO "badkhashani" VALUES(35,13,'35:13','(الله) شب را در روز و روز را در شب داخل می‌کند، و آفتاب و ماه را مسخر کرده است که هریک (از آنها) تا مدت معین روان است. این است الله پروردگار شما، فرمانروایی او راست، و کسانی را که به جای او می‌خوانید حتی مالک پوست دانۀ خرمایی هم نیستند.');
INSERT INTO "badkhashani" VALUES(35,14,'35:14','اگر آنها را بخوانید صدای شما را نمی‌شنوند و اگر (به فرض محال) هم بشنوند شما را پاسخ نمی‌دهند. و روز قیامت شرک شما را انکار می‌کنند، و هیچ کس چون ذات آگاه تو را با خبر نمی‌سازد.');
INSERT INTO "badkhashani" VALUES(35,15,'35:15','ای مردم! شما به الله محتاجيد و الله است که (مطلقا) بی‌نیاز ستوده است.');
INSERT INTO "badkhashani" VALUES(35,16,'35:16','اگر بخواهد شما را از میان می‌برد و خلق نو به میان می‌آورد.');
INSERT INTO "badkhashani" VALUES(35,17,'35:17','و این کار بر الله دشوار نیست.');
INSERT INTO "badkhashani" VALUES(35,18,'35:18','و هیچ شخصی بار گناه دیگری را بر نمی‌دارد، و اگر گرانباری (کسی را) برای برداشتن بار (گناه) خود طلب کند، چیزی از آن برداشته نمی‌شود اگر چه (آن‌کس) خویشاوند باشد. جز این نیست که تو تنها کسانی را بیم می‌دهی که از پروردگارشان در نهان می‌ترسند و نماز را برپا داشته‌اند. و هر کس پاکیزگی جوید، تنها به نفع خویش پاکیزگی می‌جوید و بازگشت به سوى الله است.');
INSERT INTO "badkhashani" VALUES(35,19,'35:19','و نابینا و بینا برابر نیستند.');
INSERT INTO "badkhashani" VALUES(35,20,'35:20','و نه تاریکی‌ها و روشنایی (برابراند).');
INSERT INTO "badkhashani" VALUES(35,21,'35:21','و نه سایه و باد گرم (برابراند).');
INSERT INTO "badkhashani" VALUES(35,22,'35:22','و زندگان و مردگان برابر نیستند، البته الله هرکس را بخواهد می‌شنواند و تو شنوانندۀ کسانی که در گورها‌اند، نیستی.');
INSERT INTO "badkhashani" VALUES(35,23,'35:23','(چون) تو جز ترساننده نیستی.');
INSERT INTO "badkhashani" VALUES(35,24,'35:24','در حقیقت ما تو را به حق مژده رسان و بیم دهنده فرستادیم. و هیچ امتی نیست مگر این که در آن بیم دهنده‌ای گذشته است.');
INSERT INTO "badkhashani" VALUES(35,25,'35:25','و اگر تو را تکذیب کنند البته کسانی که پیش از آنان بوده‌اند نیز (پیغمبران را) تکذیب کردند. پیغمبرانشان معجزه‌های آشکار و کتاب‌های روشن برایشان آوردند.');
INSERT INTO "badkhashani" VALUES(35,26,'35:26','باز کافران را گرفتم، پس (بنگر) عقوبت من چگونه بود!.');
INSERT INTO "badkhashani" VALUES(35,27,'35:27','آیا ندیدی که الله از آسمان آب فرود آورد، پس به وسیلۀ آن (آب) میوه‌هایی مختلف و رنگارنگ بیرون آوردیم، و از کوه‌ها قطعه‌های است با رنگهای مختلف که برخی از آنها سفید و برخی سرخ و بعضی سیاهند در نهایت سیاهی.');
INSERT INTO "badkhashani" VALUES(35,28,'35:28','و از مردمان و جانوران و چهار پایان نیز رنگهای آنها مختلف و گوناگون است. جز این نیست که از بندگان الله تنها علماء‌اند که از او می‌ترسند. البته الله غالب (و) بس آمرزگار است.');
INSERT INTO "badkhashani" VALUES(35,29,'35:29','البته کسانی که کتاب الله را تلاوت می‌کنند و نماز را برپا داشته‌اند و از آنچه به آنها روزی داده‌ایم در پنهان و آشکار انفاق می‌کنند، (ایشان) امید به تجارتی دارند که هرگز زوالی ندارد.');
INSERT INTO "badkhashani" VALUES(35,30,'35:30','تا الله ثواب آنان را به تمام و کمال بدهد، و از فضل خود (پاداشی) به آنان بیفزاید بی‌گمان او آمرزگار سپاسگزار است.');
INSERT INTO "badkhashani" VALUES(35,31,'35:31','و آنچه از کتاب (قرآن) که به تو وحی کرده‌ایم حق و تصدیق کنندۀ کتاب‌های (آسمانی) پیش از خود است، یقینا الله از (احوال) بندگان خود باخبر (و) بیناست.');
INSERT INTO "badkhashani" VALUES(35,32,'35:32','باز این کتاب (قرآن) را به آن بندگانم که آنان را برگزیده بودیم، به میراث دادیم، پس بعضی از آنها به خویشتن ظالم و بعضی از ایشان میانه رو و بعضی از آنان به حکم الله به‌سوی نیکی‌ها پیشگامند. این است همان فضل بزرگ.');
INSERT INTO "badkhashani" VALUES(35,33,'35:33','(وعدۀ ما به پیشگامان به‌سوی نیکی‌ها) باغ‌های همیشگی است که در آنها داخل می‌شوند، در آنجا با دستبند‌های از طلا و مروارید آراسته می‌گردند و لباس‌شان در آنجا ابریشم است.');
INSERT INTO "badkhashani" VALUES(35,34,'35:34','و گویند: ستایش پروردگاری راست که اندوه (بدی‌ها و گناهان) را از ما دور کرد، البته پروردگار ما آمرزندۀ سپاسگزار است.');
INSERT INTO "badkhashani" VALUES(35,35,'35:35','همان ذاتی که از فضل خویش ما را در اقامتگاه ابدی فرود آورد، در آنجا به ما هیچ رنجی نمی‌رسد و در آنجا ما هیچ خستگی نمی‌بینیم.');
INSERT INTO "badkhashani" VALUES(35,36,'35:36','و کسانی که کفر ورزیدند برای آنان آتش دوزخ است، نه بر آنان (به مرگ دیگر) حکم می‌شود تا بمیرند، و نه (چیزی) از عذاب دوزخ از آنان کاسته می‌شود. و اینچنین هر ناسپاسی را سزا می‌دهیم.');
INSERT INTO "badkhashani" VALUES(35,37,'35:37','و آنان در دوزخ فریاد می‌کنند: ای پروردگار ما! ما را بیرون کن تا کارهای شایسته انجام دهیم، غیر از کارهایی که قبلا می‌کردیم، (می‌گوییم:) آیا آنقدر به شما عمر ندادیم که پند پذیر در آن پند و عبرت گیرد؟ و بیم دهنده (هم) نزد شما آمد، پس عذاب را بچشید که برای ظالمان هیچ مددگاری نیست.');
INSERT INTO "badkhashani" VALUES(35,38,'35:38','یقینا الله دانای (امور) پنهان آسمان‌ها و زمین است و البته او از چیزهایی که در داخل سینه‌هاست کاملا داناست.');
INSERT INTO "badkhashani" VALUES(35,39,'35:39','او ذاتی است که شما را در زمین جانشین‌ها گردانید، پس هرکس کفر ورزد، پس کفرش به زیان خود اوست. و کفر کافران در پیشگاه الله جز بر خشم (پروردگار) نمی‌افزاید. و کفر کافران جز زیان چیزی نصیب کافران نمی‌گرداند.');
INSERT INTO "badkhashani" VALUES(35,40,'35:40','بگو: آیا دیدید شریکان مقرر کردۀ خود را که به جای الله می‌خوانید؟ به من نشان دهید که آنان چه چیزی از زمین آفریده‌اند؟ و یا آنها در آفرینش آسمان‌ها شرکت داشته‌اند! یا به آنها کتابی داده‌ایم پس آنها بر حجّتی از آن قرار دارند؟ بلکه حق این است که ظالمان جز فریب به همدیگر وعده نمی‌دهند.');
INSERT INTO "badkhashani" VALUES(35,41,'35:41','البته الله آسمان‌ها و زمین را نگاه می‌دارد تا از جای خود نلغزند و اگر بلغزند هیچ کس بعد از الله نمی‌تواند آنها را نگاه بدارد. بی‌گمان او بردبار آمرزگار است.');
INSERT INTO "badkhashani" VALUES(35,42,'35:42','و با سخت‌ترین قسم‌های خود به الله قسم خوردند که اگر بیم دهنده‌ای نزد آنان بیاید، حتما نسبت به هر امتی راه یافته‌تر خواهند بود. امّا وقتی که بیم‌دهنده برایشان آمد، در حق آنان جز نفرت و گریز نه افزود.');
INSERT INTO "badkhashani" VALUES(35,43,'35:43','(نفرت و گریز‌شان از حق) به خاطر استکبار در زمین و حیله گری‌های زشتی بود که انجام می‌دادند. و حیله گری‌های زشت جز به اهلش بر نمی‌گردد. پس آیا جز سنت پیشینیان را انتظار دارند؟ پس هرگز برای سنت الهی تبدیلی نخواهی یافت. و هرگز برای سنت الهی تغییری نخواهی یافت.');
INSERT INTO "badkhashani" VALUES(35,44,'35:44','آیا در زمین نگشته‌اند تا ببینند که عاقبت پیشینیانشان چگونه شد؟ حال آنکه آنان نسبت به ایشان قوت و قدرت بیشتری داشتند، و هیچ چیزی در آسمان‌ها و در زمین الله را عاجز و ناتوان نخواهد کرد، چرا که او دانای تواناست.');
INSERT INTO "badkhashani" VALUES(35,45,'35:45','و اگر الله مردم را به (سزای) آنچه کرده‌اند گرفتار می‌کرد، بر پشت آن (زمین) هیچ جانوری را نمی‌گذاشت. بلکه آنان را تا وقت معیّنی به تأخیر می‌اندازد (و مهلت می‌دهد)، پس وقتی که مدت مشخص ایشان فرا رسید (هیچ چیزی از الله پوشیده نمی‌ماند) چون الله به بندگانش بیناست.');
INSERT INTO "badkhashani" VALUES(36,1,'36:1','يس. (معنای این حروف به الله معلوم است).');
INSERT INTO "badkhashani" VALUES(36,2,'36:2','قسم به قرآن پر از حکمت.');
INSERT INTO "badkhashani" VALUES(36,3,'36:3','به تحقیق که تو از جملۀ پیغمبران هستی.');
INSERT INTO "badkhashani" VALUES(36,4,'36:4','بر راه راست قرار داری.');
INSERT INTO "badkhashani" VALUES(36,5,'36:5','(این قرآن) فرو فرستادۀ الله غالبِ مهربان است.');
INSERT INTO "badkhashani" VALUES(36,6,'36:6','تا قومی را بیم دهی که پدرانشان بیم داده نشده‌اند پس آنان خود غافل و بی‌خبرند.');
INSERT INTO "badkhashani" VALUES(36,7,'36:7','بی‌گمان (وعدۀ عذاب) بر بیشتر آنان تحقّق یافته است، پس آنان ایمان نمی‌آورند.');
INSERT INTO "badkhashani" VALUES(36,8,'36:8','بدون شک ما در گردن‌هایشان طوق‌هایی تا زنخ‌هایشان نهاده‌ایم پس در نتیجه سرهایشان رو به بالا است (و زمین را دیده نمی‌توانند).');
INSERT INTO "badkhashani" VALUES(36,9,'36:9','و در پیش روی ایشان و در پشت سر ایشان سدّی قرار داده‌ایم پس بر چشمان آنان پرده‌ای انداخته‌ایم، در نتیجه آنان هیچ نمی‌بینند.');
INSERT INTO "badkhashani" VALUES(36,10,'36:10','و برای آنان برابر است چه آنها را بترسانی و چه آنها را نترسانی، ایمان نمی‌آورند.');
INSERT INTO "badkhashani" VALUES(36,11,'36:11','تو تنها کسی را می‌ترسانی (و این کار برای کسی فایده دارد) که از قرآن پیروی کند و غائبانه از پروردگار رحمان بترسد، پس او را به آمرزش و پاداش ارجمند مژده بده.');
INSERT INTO "badkhashani" VALUES(36,12,'36:12','بدون شک ماییم که مرده‌ها را زنده می‌سازیم و آنچه (از کارهای نیک و بد را که) پیش فرستاده‌اند و آثار (خوب و بد پس از مرگ)‌شان را می‌نویسیم. و همه چیز را در کتابی که پیشوای روشنگر است ضبط کرده‌ایم.');
INSERT INTO "badkhashani" VALUES(36,13,'36:13','و برای‌شان از اصحاب قريه مثل بزن وقتی که فرستادگان (الله) به‌سوی آنان آمدند.');
INSERT INTO "badkhashani" VALUES(36,14,'36:14','آن وقت که دو نفر (از فرستادگان خود) را به‌سوی ایشان فرستادیم، پس آندو را دروغگو شمردند، پس (آن دو را با شخص) سومی تقویت نمودیم، پس گفتند: بدون شک ما به‌سوی شما فرستاده شده‌ایم.');
INSERT INTO "badkhashani" VALUES(36,15,'36:15','گفتند: شما جز بشری همچون ما نیستید و پروردگار رحمن چیزی نازل نکرده است و شما جز دروغ نمی‌گوئید.');
INSERT INTO "badkhashani" VALUES(36,16,'36:16','(پیغمبران) گفتند: پروردگار ما می‌داند که البته ما به‌سوی شما فرستاده شده‌ایم.');
INSERT INTO "badkhashani" VALUES(36,17,'36:17','و بر عهدۀ ما جز رسانیدن آشکار (پیغام الهی، وظیفۀ دیگری) نیست.');
INSERT INTO "badkhashani" VALUES(36,18,'36:18','مردم گفتند: البته ما شما را به فال بد گرفته‌ایم، اگر دست برندارید حتما شما را سنگسار می‌کنیم و حتما از جانب ما عذاب دردناک به شما خواهد رسید.');
INSERT INTO "badkhashani" VALUES(36,19,'36:19','(پیغمبران) گفتند: شومی و فال بد تان همرای خودتان است. آیا چون اندرز داده شوید (آن‌را فال بد می‌شمارید) نه، بلکه شما قوم اسراف کار هستید.');
INSERT INTO "badkhashani" VALUES(36,20,'36:20','و از دورترین نقطۀ شهر مردی دوان دوان آمد، گفت: ای قوم من! از فرستادگان (الله) پیروی کنید.');
INSERT INTO "badkhashani" VALUES(36,21,'36:21','از کسی پیروی کنید که پاداشی از شما نمی‌خواهد و خود آنان راه یافته‌اند.');
INSERT INTO "badkhashani" VALUES(36,22,'36:22','و مرا چه شده که ذاتی را نپرستم که مرا آفریده است؟ و همه به‌سوی او بازگردانیده می‌شوید؟');
INSERT INTO "badkhashani" VALUES(36,23,'36:23','آیا غیر از او معبودهایی را به عبادت ‌گیرم که اگر پروردگار رحمن زیانی در حق من اراده کند، شفاعت آنان چیزی را از من دفع نمی‌کند و نه آنان می‌توانند مرا نجات دهند؟');
INSERT INTO "badkhashani" VALUES(36,24,'36:24','بدون شک من در آن صورت حتما در گمراهی آشکار خواهم بود.');
INSERT INTO "badkhashani" VALUES(36,25,'36:25','البته من به پروردگار شما ایمان آورده‌ام پس (اعلان توحید را) از من بشنوید.');
INSERT INTO "badkhashani" VALUES(36,26,'36:26','(پس از آنکه او را به شهادت رساندند) به او گفته شد: وارد بهشت شو، گفت: ای کاش قوم من این امر را میدانستند!');
INSERT INTO "badkhashani" VALUES(36,27,'36:27','که پروردگارم چگونه مرا آمرزیده و مرا از جملۀ گرامیان قرار داده است.');
INSERT INTO "badkhashani" VALUES(36,28,'36:28','و پس از (شهادت) او هیچ لشکری را از آسمان بر قوم وی نازل نکردیم (و پیش از این نیز) نازل کننده نبودیم.');
INSERT INTO "badkhashani" VALUES(36,29,'36:29','(سزای آنان) تنها یک بانگ مرگبار بود، پس به ناگهان خاموش شدند.');
INSERT INTO "badkhashani" VALUES(36,30,'36:30','ای افسوس بر این بندگان! هیچ پیغمبری به‌سوی ایشان نمی‌آمد مگر اینکه او را به مسخره می‌گرفتند.');
INSERT INTO "badkhashani" VALUES(36,31,'36:31','آیا ندیدند که پیش از آنان چه بسیار امتها را نابود کردیم که دیگر آنان به سویشان باز نمی‌گردند؟');
INSERT INTO "badkhashani" VALUES(36,32,'36:32','و حتما همگی‌شان در نزد ما احضار خواهند شد.');
INSERT INTO "badkhashani" VALUES(36,33,'36:33','و زمین مرده برای آنان نشانۀ (از قدرت الله در احضار نمودن‌شان) است که آن را زنده گردانیدیم و از آن دانه‌های بیرون آوردیم که از آن می‌خورند.');
INSERT INTO "badkhashani" VALUES(36,34,'36:34','و در آن باغ‌هایی از درختان خرما و درختان انگور قرار دادیم و از چشمه‌ها در آن روان کردیم.');
INSERT INTO "badkhashani" VALUES(36,35,'36:35','تا از میوه‌های آن درختان و از آنچه دست‌هایشان به عمل آورده بخورند. آیا باز هم شکر نمی‌گزارند؟');
INSERT INTO "badkhashani" VALUES(36,36,'36:36','پاک و منزه است ذاتی که همه نر و ماده‌ها را آفریده است، از (قبیل) آنچه زمین می‌رویاند و از خود آنان و از چیزهایی که ایشان نمی‌دانند.');
INSERT INTO "badkhashani" VALUES(36,37,'36:37','و نشانۀ دیگر برای آنها شب است که روز را از آن بر می‌گیریم، ناگهان تاریکی آنان را فرا می‌گیرد.');
INSERT INTO "badkhashani" VALUES(36,38,'36:38','و آفتاب بسوی (مسير) قرارگاه‌اش روان است، این اندازه گیری و تعیین الله غالب داناست.');
INSERT INTO "badkhashani" VALUES(36,39,'36:39','و برای ماه نیز منزل‌هایی تعیین کرده‌ایم که (پس از طی کردن آنها) چون شاخۀ خشکیدۀ کهنۀ خرما باز گردد.');
INSERT INTO "badkhashani" VALUES(36,40,'36:40','نه آفتاب را سزاوار است که به ماه برسد و نه شب را توان سبقت جستن از روز است. و هریک در مسیر خود شناورند.');
INSERT INTO "badkhashani" VALUES(36,41,'36:41','و نشانه‌ای (دیگر) است برای آنان که ما نسل آنها را در کشتی مملو (از انسان و حیوان) حمل کردیم.');
INSERT INTO "badkhashani" VALUES(36,42,'36:42','و برای ایشان مانند کشتی چیزهایی را آفریده‌ایم که سوار (آن) می‌شوند.');
INSERT INTO "badkhashani" VALUES(36,43,'36:43','و اگر بخواهیم آنها را غرق می‌کنیم پس برای آنان فریادرسی نیست و نه نجات داده می‌شوند.');
INSERT INTO "badkhashani" VALUES(36,44,'36:44','جز (به جهت) رحمتی از جانب ما و تا زمانی معین بهره‌مند شوند.');
INSERT INTO "badkhashani" VALUES(36,45,'36:45','و چون به آنان گفته شود از آنچه پیش روی شماست و از آنچه که پشت سرتان است بترسيد تا به شما رحم شود.');
INSERT INTO "badkhashani" VALUES(36,46,'36:46','و هیچ آیتی از آیات پروردگارشان به (سوی) آنان نمی‌آید مگر اینکه از آن روی گردان می‌شوند.');
INSERT INTO "badkhashani" VALUES(36,47,'36:47','و چون به آنان گفته شود: از آنچه که الله به شما روزی داده است انفاق کنید، کافران به مؤمنان می‌گویند: آیا به کسی طعام بدهیم که اگر الله می‌خواست به او طعام میداد، شما جز در گمراهی آشکار نیستید.');
INSERT INTO "badkhashani" VALUES(36,48,'36:48','و می‌گویند: این وعده کی فرا می‌رسد؟ اگر راست می‌گویید.');
INSERT INTO "badkhashani" VALUES(36,49,'36:49','جز یک بانگ تند را انتظار نمی‌کشند که ایشان را در حالی فرا می‌گیرد که با یکدیگر ستیزه و جدال می‌کنند.');
INSERT INTO "badkhashani" VALUES(36,50,'36:50','پس نه ‌وصیتی کرده می‌توانند و نه به‌سوی خانوادۀشان باز می‌گردند.');
INSERT INTO "badkhashani" VALUES(36,51,'36:51','و در صور دمیده می‌شود و ناگهان همۀ آنان از قبرها به‌سوی پروردگارشان می‌شتابند.');
INSERT INTO "badkhashani" VALUES(36,52,'36:52','گویند: ای وای بر ما! چه کسی ما را از خوابگاه ما برانگیخت؟ این همان چیزی است که الله مهربان وعده داده بود، و پیغمبران راست گفتند.');
INSERT INTO "badkhashani" VALUES(36,53,'36:53','این جز یک بانگ مرگبار نیست، پس ناگهان همگی آنان نزد ما احضار کرده شوند.');
INSERT INTO "badkhashani" VALUES(36,54,'36:54','پس امروز به هیچ کس کمترین ظلمی نمی‌گردد و جز به سزای آنچه می‌کردید جزا داده نخواهید شد.');
INSERT INTO "badkhashani" VALUES(36,55,'36:55','يقينا اهل بهشت امروز سخت سرگرم خوشی و شادمانی (در نعمت‌ها) هستند.');
INSERT INTO "badkhashani" VALUES(36,56,'36:56','آنان و همسرانشان در سایه‌های جنت بر تخت‌ها تکیه زده نشسته‌اند.');
INSERT INTO "badkhashani" VALUES(36,57,'36:57','در آنجا برای آنها میوه‌هایی است و هر چه بخواهند در اختیارشان خواهد بود.');
INSERT INTO "badkhashani" VALUES(36,58,'36:58','سلام بر شما، این سخنی است از جانب پروردگار مهربان.');
INSERT INTO "badkhashani" VALUES(36,59,'36:59','و (فرمان می‌دهیم) ای مجرمان! امروز (از مؤمنان) جدا شوید.');
INSERT INTO "badkhashani" VALUES(36,60,'36:60','ای فرزندان آدم! آیا به شما دستور ندادم که شیطان را عبادت نکنید، چرا که او دشمنی آشکار برای شماست؟');
INSERT INTO "badkhashani" VALUES(36,61,'36:61','و اینکه مرا عبادت کنید، این راه راست است.');
INSERT INTO "badkhashani" VALUES(36,62,'36:62','و هر آئینه او (شیطان) گروهی زیادی از شما را سخت گمراه کرد، پس آیا نمی‌فهمیدید.');
INSERT INTO "badkhashani" VALUES(36,63,'36:63','این جهنمی است که به شما وعده داده می‌شد.');
INSERT INTO "badkhashani" VALUES(36,64,'36:64','امروز به سبب کفری که می‌ورزیدید به آن در آیید.');
INSERT INTO "badkhashani" VALUES(36,65,'36:65','امروز بر دهان‌هایشان مهر می‌نهیم و دربارۀ آنچه می‌کردند دست‌هایشان با ما سخن می‌گویند و پاهایشان (بر آن) گواهی می‌دهند.');
INSERT INTO "badkhashani" VALUES(36,66,'36:66','و اگر بخواهیم چشمهای‌شان را نابود می‌کنیم، پس در راه (رهایی) بر یکدیگر پیشی می‌جویند، اما از کجا می‌توانند ببینند؟');
INSERT INTO "badkhashani" VALUES(36,67,'36:67','بلکه اگر بخواهیم آنان را بر جایشان مسخ می‌کنیم، پس نمی‌توانند پیش بروند و نه بر گردند.');
INSERT INTO "badkhashani" VALUES(36,68,'36:68','و هر که را که عمر دراز دهیم او را در خلقت دگرگون می‌سازیم، آیا درک نمی‌کنید؟');
INSERT INTO "badkhashani" VALUES(36,69,'36:69','و ما به او شعر نیاموخته‌ایم و (آموختن شعر) سزاوار او هم نیست، آن جز اندرز و قرآنی آشکار نیست.');
INSERT INTO "badkhashani" VALUES(36,70,'36:70','تا هر کس را که زنده باشد بیم دهد و سخن (عذاب) بر کافران ثابت گردد.');
INSERT INTO "badkhashani" VALUES(36,71,'36:71','آیا ندیدند که ما برای آنان از آنچه دستان ما ساخته است چهار پایانی آفریده‌ایم، پس ایشان مالک آنها‌اند؟');
INSERT INTO "badkhashani" VALUES(36,72,'36:72','و آنها را برایشان رام گردانیدیم، پس بعضی از آنها سواری و مرکب آنهاست، و از بعضی (دیگر) آنها می‌خورند.');
INSERT INTO "badkhashani" VALUES(36,73,'36:73','و برای آنان در چهارپایان منفعت‌ها و نوشیدنی‌هاست، پس آیا شکر نمی‌گزارند؟');
INSERT INTO "badkhashani" VALUES(36,74,'36:74','و غیر از الله معبودانی گرفتند تا اگر مدد کرده شوند.');
INSERT INTO "badkhashani" VALUES(36,75,'36:75','توانایی مدد‌شان را ندارند و آنها (کفار) برایشان (بتان) لشکری احضار شده هستند.');
INSERT INTO "badkhashani" VALUES(36,76,'36:76','پس گفتار آنان تو را غمگین نکند، چون ما می‌دانیم آنچه را پنهان می‌کنند و آنچه را آشکار می‌سازند.');
INSERT INTO "badkhashani" VALUES(36,77,'36:77','آیا انسان ندیده است که ما او را از نطفه‌ای آفریدیم، پس ناگهان او جدل کنندۀ آشکار شده است.');
INSERT INTO "badkhashani" VALUES(36,78,'36:78','و (این انسان منکر قیامت) برای ما مثالی آورد و آفرینش خود را فراموش کرد. گفت: چه کسی این استخوان‌های پوسیده و فرسوده را دوباره زنده می‌گرداند؟');
INSERT INTO "badkhashani" VALUES(36,79,'36:79','بگو: همان ذاتی که بار اول آنها را آفریده است دوباره زنده می‌گرداند و او به هر آفرینشی داناست.');
INSERT INTO "badkhashani" VALUES(36,80,'36:80','همان ذاتی که از درخت سبز برایتان آتش آفرید، پس شما از آن درخت آتش می‌افروزید.');
INSERT INTO "badkhashani" VALUES(36,81,'36:81','آیا کسی که آسمان‌ها و زمین را آفریده است قادر نیست بر این که مانند آنها را بیافریند؟ آری! (قادر است) و اوست آفرینندۀ دانا.');
INSERT INTO "badkhashani" VALUES(36,82,'36:82','هرگاه الله آفریدن چیزی را بخواهد کار او تنها این است که به او می‌گوید: موجود شو، پس بی‌درنگ موجود می‌شود.');
INSERT INTO "badkhashani" VALUES(36,83,'36:83','پس پاک و منزه است پروردگاری که مالکیت و حاکمیت همه چیز در دست اوست و به‌سوی او باز گردانیده می‌شوید.');
INSERT INTO "badkhashani" VALUES(37,1,'37:1','قسم به فرشته‌های صف بسته در صفهایی منظم');
INSERT INTO "badkhashani" VALUES(37,2,'37:2','قسم به آن فرشته‌هایی که (شیاطین را) سخت می‌رانند.');
INSERT INTO "badkhashani" VALUES(37,3,'37:3','قسم به آن گروه فرشته‌ها که ذكر الله (قرآن) را تلاوت می‌کنند.');
INSERT INTO "badkhashani" VALUES(37,4,'37:4','بی‌شک که معبود شما یگانه است.');
INSERT INTO "badkhashani" VALUES(37,5,'37:5','پروردگار آسمان‌ها و زمین و آنچه میان آن دو است، و پروردگار مشرقها (و مغرب‌ها) است.');
INSERT INTO "badkhashani" VALUES(37,6,'37:6','البته ما آسمان پایین (به شما) را به زینت ستارگان آراسته‌ایم.');
INSERT INTO "badkhashani" VALUES(37,7,'37:7','و آن را از هر شیطان سرکش به خوبی حفظ کرده‌ایم.');
INSERT INTO "badkhashani" VALUES(37,8,'37:8','آنان نمی‌توانند به (اسرار) عالم بالا گوش فرا دهند بلکه از هر سو به آنان (شهاب) پرتاب می‌شود.');
INSERT INTO "badkhashani" VALUES(37,9,'37:9','تا رانده شوند. و برای آنها عذاب دائمی است.');
INSERT INTO "badkhashani" VALUES(37,10,'37:10','مگر اینکه شیطانی به سرعت (خبری) را برباید که شعلۀ سوزنده او را دنبال (و نابود) می‌کند.');
INSERT INTO "badkhashani" VALUES(37,11,'37:11','پس از ایشان بپرس که آیا آفرینش آنان دشوارتر است یا آفرینش چیزهایی که (در کائنات) آفریده ایم؟ بدون شک ما آنان را از گل چسپنده آفریده‌ایم.');
INSERT INTO "badkhashani" VALUES(37,12,'37:12','بلکه تعجب می‌کنی (از انکار ایشان) و ایشان مسخره می‌کنند.');
INSERT INTO "badkhashani" VALUES(37,13,'37:13','و چون اندرز داده شوند، عبرت نمی‌گیرند.');
INSERT INTO "badkhashani" VALUES(37,14,'37:14','و چون معجزه‌ای را ببینند به آن تمسخر می‌کنند.');
INSERT INTO "badkhashani" VALUES(37,15,'37:15','و گفتند: این جز جادویی آشکار نیست.');
INSERT INTO "badkhashani" VALUES(37,16,'37:16','آیا وقتی مردیم (و تبدیل به) خاک و استخوانهای پوسیده شدیم، (دوباره) برانگیخته خواهیم شد؟');
INSERT INTO "badkhashani" VALUES(37,17,'37:17','و آیا پدران پیشین ما نیز (زنده خواهند شد).');
INSERT INTO "badkhashani" VALUES(37,18,'37:18','بگو: آری (همۀ شما زنده می‌شوید) در حالیکه شما ذلیل خواهید بود.');
INSERT INTO "badkhashani" VALUES(37,19,'37:19','همانا (قیامت) یک صدای مرگبار است، پس ناگهان آنان (صحنۀ قيامت را) می‌نگرند.');
INSERT INTO "badkhashani" VALUES(37,20,'37:20','و می‌گویند: وای بر ما این روز جزاست؟!');
INSERT INTO "badkhashani" VALUES(37,21,'37:21','(به آنها گفته می‌شود بلی،) این همان روز جدایی (حق از باطل) است که آن را دروغ می‌پنداشتید.');
INSERT INTO "badkhashani" VALUES(37,22,'37:22','(ندا می‌آید) ظالمان را با همراهان‌شان (و) با آنچه می‌پرستیدند، جمع کنید.');
INSERT INTO "badkhashani" VALUES(37,23,'37:23','غیر از الله (هر چه را پرستش می‌کردند) پس آنان را به راه دوزخ راهنمایی کنید.');
INSERT INTO "badkhashani" VALUES(37,24,'37:24','و آنان را متوقف کنید زیرا که آنها مورد بازخواست قرار می‌گیرند.');
INSERT INTO "badkhashani" VALUES(37,25,'37:25','شما را چه شده که یکدیگر را مدد نمی‌کنید؟');
INSERT INTO "badkhashani" VALUES(37,26,'37:26','بلکه آنان امروز تسلیم‌اند.');
INSERT INTO "badkhashani" VALUES(37,27,'37:27','و بعضی از آنان به بعضی دیگر روی کرده و از همدیگر می‌پرسند.');
INSERT INTO "badkhashani" VALUES(37,28,'37:28','(پیروان) می‌گویند: شما بودید که با قهر و غلبه به‌سوی ما می‌آمدید.');
INSERT INTO "badkhashani" VALUES(37,29,'37:29','(پیشوایان) گویند: بلکه (کوتاهی از خودتان بود و) مؤمن نبودید.');
INSERT INTO "badkhashani" VALUES(37,30,'37:30','و ما هیچ سلطه و غلبه بر شما نداشتیم بلکه شما خود قوم سرکش بودید.');
INSERT INTO "badkhashani" VALUES(37,31,'37:31','پس وعدۀ (عذاب) پروردگار ما بر ما لازم شد، بی‌گمان ما چشنده (طعم عذاب) خواهیم بود.');
INSERT INTO "badkhashani" VALUES(37,32,'37:32','چون ما خود گمراه بودیم پس شما را هم گمراه کردیم.');
INSERT INTO "badkhashani" VALUES(37,33,'37:33','پس یقینا آنها (همه) در آن روز در عذاب شریک‌اند.');
INSERT INTO "badkhashani" VALUES(37,34,'37:34','ما این چنین با مجرمان می‌کنیم.');
INSERT INTO "badkhashani" VALUES(37,35,'37:35','آنان چنان بودند که چون به آنان گفته می‌شد: معبود برحق جز الله (یگانه) نیست، تکبر می‌ورزیدند.');
INSERT INTO "badkhashani" VALUES(37,36,'37:36','و می‌گفتند: آیا معبودهای خود را به خاطر (سخن) شاعری دیوانه ترک گوییم؟');
INSERT INTO "badkhashani" VALUES(37,37,'37:37','(شاعر و دیوانه نیست) بلکه حق را آورده و پیغمبران را تصدیق کرده است.');
INSERT INTO "badkhashani" VALUES(37,38,'37:38','شما حتما عذاب دردناک را خواهید چشید.');
INSERT INTO "badkhashani" VALUES(37,39,'37:39','و جز (در برابر) آنچه می‌کردید، سزا داده نمی‌شوید.');
INSERT INTO "badkhashani" VALUES(37,40,'37:40','به جز بندگان مخلص الله.');
INSERT INTO "badkhashani" VALUES(37,41,'37:41','ایشان روزی معلوم و خاص دارند.');
INSERT INTO "badkhashani" VALUES(37,42,'37:42','(برایشان انواع) میوه‌ها است و ایشان گرامی‌اند.');
INSERT INTO "badkhashani" VALUES(37,43,'37:43','در باغ‌های پر نعمت (بهشت).');
INSERT INTO "badkhashani" VALUES(37,44,'37:44','بر تختها روبروی یکدیگر (نشسته اند).');
INSERT INTO "badkhashani" VALUES(37,45,'37:45','بر آنان جامی از شراب ناب گردانیده شود.');
INSERT INTO "badkhashani" VALUES(37,46,'37:46','که سفید رنگ و لذت بخش است برای نوشندگان.');
INSERT INTO "badkhashani" VALUES(37,47,'37:47','نه در آن زوال عقل است و نه از نوشیدن آن مست می‌شوند.');
INSERT INTO "badkhashani" VALUES(37,48,'37:48','و در نزدشان زنان بزرگ چشم که دیده از غیر می‌بندند خواهند بود.');
INSERT INTO "badkhashani" VALUES(37,49,'37:49','(از شدت سفیدی) گویا آنان تخم‌های (شتر مرغ) هستند که (در زیر پروبال آن) پنهان شده‌اند.');
INSERT INTO "badkhashani" VALUES(37,50,'37:50','پس بعضی رو به بعضی دیگر می‌کنند و از یکدیگر می‌پرسند.');
INSERT INTO "badkhashani" VALUES(37,51,'37:51','گوینده‌ای از آنان می‌گوید: البته من همنشینی داشتم.');
INSERT INTO "badkhashani" VALUES(37,52,'37:52','که می‌گفت: آیا تو (به روز آخرت) از باور کنندگان هستی؟');
INSERT INTO "badkhashani" VALUES(37,53,'37:53','آیا چون بمیریم و به خاک و استخوان‌ها تبدیل شویم (دوباره زنده شده) سزا و جزا می‌بینیم؟');
INSERT INTO "badkhashani" VALUES(37,54,'37:54','گوید: آیا شما (جنتیان) می‌توانید (از حال او) اطلاع یابید؟');
INSERT INTO "badkhashani" VALUES(37,55,'37:55','پس خودش نگریست و او را در وسط دوزخ دید.');
INSERT INTO "badkhashani" VALUES(37,56,'37:56','گفت: سوگند به الله نزدیک بود که مرا هلاک کنی.');
INSERT INTO "badkhashani" VALUES(37,57,'37:57','و اگر نعمت پروردگارم نبود حتما من هم از احضار شدگان (در دوزخ) بودم.');
INSERT INTO "badkhashani" VALUES(37,58,'37:58','(از شدت خوشی می‌گویند:) آیا ما دیگر نمی‌میریم؟!');
INSERT INTO "badkhashani" VALUES(37,59,'37:59','مگر مرگ نخستینی که داشتیم و ما عذاب نخواهیم دید.');
INSERT INTO "badkhashani" VALUES(37,60,'37:60','يقينا این کامیابی بزرگی است (که نصیب ما شده است).');
INSERT INTO "badkhashani" VALUES(37,61,'37:61','برای چنین (نعمتی) عمل کنندگان باید کار کنند.');
INSERT INTO "badkhashani" VALUES(37,62,'37:62','آیا این (نعمت جنت) برای پذیرایی بهتر است یا درخت زقوم.');
INSERT INTO "badkhashani" VALUES(37,63,'37:63','بدون شک ما آن را وسیلۀ شکنجه و رنج برای ظالمان قرار داده‌ایم.');
INSERT INTO "badkhashani" VALUES(37,64,'37:64','آن درختی است که از قعر دوزخ می‌روید.');
INSERT INTO "badkhashani" VALUES(37,65,'37:65','خوشه‌ها و شکوفه‌های آن گویا سرهای شیاطین است.');
INSERT INTO "badkhashani" VALUES(37,66,'37:66','آنان از آن می‌خورند و شکم‌ها را از آن پر می‌کنند.');
INSERT INTO "badkhashani" VALUES(37,67,'37:67','باز بر آن آب جوشان و آلوده را می‌نوشند.');
INSERT INTO "badkhashani" VALUES(37,68,'37:68','باز برگشت‌شان به‌سوی دوزخ است.');
INSERT INTO "badkhashani" VALUES(37,69,'37:69','بی‌گمان آنان پدرانشان را گمراه یافتند.');
INSERT INTO "badkhashani" VALUES(37,70,'37:70','پس (با این حال) آنان در پی پدرانشان شتابان می‌روند.');
INSERT INTO "badkhashani" VALUES(37,71,'37:71','البته پیش از آنان اکثر پیشینیان (نیز) گمراه شدند.');
INSERT INTO "badkhashani" VALUES(37,72,'37:72','و همانا ما در میانشان بیم دهندگانی فرستاده بودیم.');
INSERT INTO "badkhashani" VALUES(37,73,'37:73','پس بنگر که عاقبت بیم داده شدگان چطور شد؟');
INSERT INTO "badkhashani" VALUES(37,74,'37:74','به جز بندگان خالص شدۀ الله.');
INSERT INTO "badkhashani" VALUES(37,75,'37:75','و البته نوح ما را به فریاد خواند و ما هم چه خوب پاسخ دهندگان هستیم.');
INSERT INTO "badkhashani" VALUES(37,76,'37:76','و او و خانواده‌اش را از اندوه بزرگ نجات دادیم.');
INSERT INTO "badkhashani" VALUES(37,77,'37:77','و تنها نسل او را باقی گذاشتیم.');
INSERT INTO "badkhashani" VALUES(37,78,'37:78','و برای او در میان ملت‌های آینده یادگار و نام نیک به جا گذاشتیم.');
INSERT INTO "badkhashani" VALUES(37,79,'37:79','و در میان جهانیان بر نوح سلام باد.');
INSERT INTO "badkhashani" VALUES(37,80,'37:80','البته ما این چنین به نیکوکاران ثواب می‌دهیم.');
INSERT INTO "badkhashani" VALUES(37,81,'37:81','به يقين او از بندگان با ایمان ما بود.');
INSERT INTO "badkhashani" VALUES(37,82,'37:82','باز دیگران را غرق کردیم.');
INSERT INTO "badkhashani" VALUES(37,83,'37:83','و يقينا ابراهیم از پیروان او بود.');
INSERT INTO "badkhashani" VALUES(37,84,'37:84','وقتی که با دل سالم به پیشگاه پروردگارش آمد.');
INSERT INTO "badkhashani" VALUES(37,85,'37:85','وقتی که به پدر و قوم خود گفت: چه چیزی را می‌پرستید؟');
INSERT INTO "badkhashani" VALUES(37,86,'37:86','آیا به دروغ معبودهای دیگری غیر از الله را می‌خواهید؟');
INSERT INTO "badkhashani" VALUES(37,87,'37:87','پس گمانتان در بارۀ پروردگار جهانیان چیست؟');
INSERT INTO "badkhashani" VALUES(37,88,'37:88','پس نگاه خاصی به ستارگان کرد.');
INSERT INTO "badkhashani" VALUES(37,89,'37:89','پس گفت: من بیمارم.');
INSERT INTO "badkhashani" VALUES(37,90,'37:90','پس مردم از او روی گردانده برگشتند.');
INSERT INTO "badkhashani" VALUES(37,91,'37:91','پس شتابان و نهان به‌سوی معبودهایشان رفت و (مسخره کنان) گفت: آیا نمی‌خورید؟');
INSERT INTO "badkhashani" VALUES(37,92,'37:92','شما را چه شده است که حرف نمی‌زنید؟');
INSERT INTO "badkhashani" VALUES(37,93,'37:93','پس با دست راست بر آنها ضربۀ محکم وارد کرد.');
INSERT INTO "badkhashani" VALUES(37,94,'37:94','پس مردم شتابان به‌سوی او روی آوردند.');
INSERT INTO "badkhashani" VALUES(37,95,'37:95','(ابراهیم) گفت: آیا چیزهایی را می‌پرستید که خودتان می‌تراشید؟');
INSERT INTO "badkhashani" VALUES(37,96,'37:96','حال آنکه الله شما و آنچه را که انجام می‌دهید آفریده است.');
INSERT INTO "badkhashani" VALUES(37,97,'37:97','گفتند: برای او چهار دیوار بزرگی (مانند کوره) بسازید و او را در آتش بیاندازید.');
INSERT INTO "badkhashani" VALUES(37,98,'37:98','پس خواستند برای (نابودی) ابراهیم نیرنگ ورزند، ولی ما آنان را پست و مغلوب قرار دادیم.');
INSERT INTO "badkhashani" VALUES(37,99,'37:99','و ابراهیم گفت: البته من به‌سوی پروردگارم می‌روم، او مرا راهنمایی خواهد کرد.');
INSERT INTO "badkhashani" VALUES(37,100,'37:100','پروردگارم! به من (فرزندی) از صالحان عطا کن.');
INSERT INTO "badkhashani" VALUES(37,101,'37:101','پس او را به پسری بردبار مژده دادیم.');
INSERT INTO "badkhashani" VALUES(37,102,'37:102','وقتیکه (فرزندش) در کار و کوشش به سنی رسید که می‌توانست با او بایستد، (پدر) گفت: فرزندم! من در خواب دیده‌ام که من تو را ذبح می‌کنم، پس بنگر که چه می‌بینی و نظرت چیست؟ (فرزند) گفت: ای پدر! آنچه را مأمور شده‌ای انجام ده اگر الله بخواهد مرا از صابران خواهی یافت.');
INSERT INTO "badkhashani" VALUES(37,103,'37:103','پس چون (پدر و پسر) هردو (به امر الله) گردن نهادند و (ابراهيم) فرزندش را به پیشانی بر زمین خواباند.');
INSERT INTO "badkhashani" VALUES(37,104,'37:104','و او را ندا کردیم که ای ابراهیم!');
INSERT INTO "badkhashani" VALUES(37,105,'37:105','حقا که خواب (خود) را راست گرداندی البته ما این چنین نیکوکاران را پاداش می‌دهیم.');
INSERT INTO "badkhashani" VALUES(37,106,'37:106','بی‌گمان این یک آزمون آشکار بود.');
INSERT INTO "badkhashani" VALUES(37,107,'37:107','و ما قربانی بزرگ و ارزشمندی را فدای او کردیم.');
INSERT INTO "badkhashani" VALUES(37,108,'37:108','و برای او در ملت‌های آینده نام نیک باقی گذاشتیم.');
INSERT INTO "badkhashani" VALUES(37,109,'37:109','درود و سلام بر ابراهیم.');
INSERT INTO "badkhashani" VALUES(37,110,'37:110','ما این چنین نیکوکاران را ثواب می‌دهیم.');
INSERT INTO "badkhashani" VALUES(37,111,'37:111','چون او از بندگان با ایمان ما بود.');
INSERT INTO "badkhashani" VALUES(37,112,'37:112','و او را به (تولد) اسحاق که پیغمبر و از جملۀ شایستگان بود، مژده دادیم.');
INSERT INTO "badkhashani" VALUES(37,113,'37:113','و بر او و اسحاق خیر و برکت عطا کردیم. و از نسل آن دو (بعضی) نیکو کار و (بعضی) آشکارا بر خود ظالم بودند.');
INSERT INTO "badkhashani" VALUES(37,114,'37:114','و البته بر موسی و هارون منت نهادیم.');
INSERT INTO "badkhashani" VALUES(37,115,'37:115','و هردوی آنان و قومشان را از مصیبت بزرگ نجات دادیم.');
INSERT INTO "badkhashani" VALUES(37,116,'37:116','و آنان را مدد کردیم، پس ایشان بودند که غالب شدند.');
INSERT INTO "badkhashani" VALUES(37,117,'37:117','و به آن دو کتاب واضح و روشنگر دادیم.');
INSERT INTO "badkhashani" VALUES(37,118,'37:118','و آن دو را به راه راست هدایت کردیم.');
INSERT INTO "badkhashani" VALUES(37,119,'37:119','برای آن دو در ملت‌های آینده نام نیک باقی گذاشتیم.');
INSERT INTO "badkhashani" VALUES(37,120,'37:120','سلام بر موسی و هارون.');
INSERT INTO "badkhashani" VALUES(37,121,'37:121','ما این چنین نیکوکاران را ثواب می‌دهیم.');
INSERT INTO "badkhashani" VALUES(37,122,'37:122','چون آن دو از بندگان مؤمن ما بودند.');
INSERT INTO "badkhashani" VALUES(37,123,'37:123','و البته الياس (هم) از پیغمبران بود.');
INSERT INTO "badkhashani" VALUES(37,124,'37:124','وقتی که به قومش گفت: آیا (از الله) نمی‌ترسید؟');
INSERT INTO "badkhashani" VALUES(37,125,'37:125','آیا بت بعل را به دعا می‌خوانید و بهترینِ آفرینندگان را می‌گذارید؟');
INSERT INTO "badkhashani" VALUES(37,126,'37:126','الله را، که پروردگار شما و پروردگار پدران پیشین شماست.');
INSERT INTO "badkhashani" VALUES(37,127,'37:127','پس او را تکذیب کردند. پس بدون شک آنان (در دوزخ) احضار خواهند شد.');
INSERT INTO "badkhashani" VALUES(37,128,'37:128','مگر بندگان مخلص الله.');
INSERT INTO "badkhashani" VALUES(37,129,'37:129','و برای او در ملت‌های آینده نام نیک باقی گذاشتیم.');
INSERT INTO "badkhashani" VALUES(37,130,'37:130','سلام بر الياس.');
INSERT INTO "badkhashani" VALUES(37,131,'37:131','ما این چنین نیکوکاران را ثواب می‌دهیم.');
INSERT INTO "badkhashani" VALUES(37,132,'37:132','چون او از بندگان مؤمن ما بود.');
INSERT INTO "badkhashani" VALUES(37,133,'37:133','و يقينا لوط از پیغمبران بود.');
INSERT INTO "badkhashani" VALUES(37,134,'37:134','(یادآور شو) وقتی را که او و خانواده‌اش، همگی را نجات دادیم.');
INSERT INTO "badkhashani" VALUES(37,135,'37:135','به جز پیرزنی که از باقی ماندگان بود.');
INSERT INTO "badkhashani" VALUES(37,136,'37:136','باز دیگران را هلاک کردیم.');
INSERT INTO "badkhashani" VALUES(37,137,'37:137','و البته شما صبحگاهان بر سرزمین ایشان عبور می‌کنید.');
INSERT INTO "badkhashani" VALUES(37,138,'37:138','و نیز گاهی در شب عبور می‌کنید، آیا نمی‌فهمید؟');
INSERT INTO "badkhashani" VALUES(37,139,'37:139','و البته يونس از پیغمبران بود.');
INSERT INTO "badkhashani" VALUES(37,140,'37:140','وقتی که به‌سوی کشتی پر از مردم پناه برد.');
INSERT INTO "badkhashani" VALUES(37,141,'37:141','پس در قرعه کشی شرکت کرد و از بازندگان شد.');
INSERT INTO "badkhashani" VALUES(37,142,'37:142','پس ماهی او را بلعید، در حالیکه خود را سرزنش می‌کرد.');
INSERT INTO "badkhashani" VALUES(37,143,'37:143','پس اگر او از تسبیح گویان نبود.');
INSERT INTO "badkhashani" VALUES(37,144,'37:144','به يقين در شکمش تا روزی که (مردم) برانگیخته می‌شوند باقی می‌ماند.');
INSERT INTO "badkhashani" VALUES(37,145,'37:145','پس در حالیکه خسته و بیمار بود او را به سرزمین خشک (و خالی از گیاه) انداختیم.');
INSERT INTO "badkhashani" VALUES(37,146,'37:146','و بر او درختی از (نوع) کدو رویانیدیم.');
INSERT INTO "badkhashani" VALUES(37,147,'37:147','و او را به‌سوی یکصد هزار نفر یا بیشتر فرستادیم.');
INSERT INTO "badkhashani" VALUES(37,148,'37:148','پس ایمان آوردند پس آنان را تا مدتی معین بهره مند ساختیم.');
INSERT INTO "badkhashani" VALUES(37,149,'37:149','پس از آنان بپرس که آیا دختران برای پروردگارت و پسران برای آنان است؟');
INSERT INTO "badkhashani" VALUES(37,150,'37:150','یا ما فرشتگان را اناث آفریدیم و آنان حاضر بودند؟');
INSERT INTO "badkhashani" VALUES(37,151,'37:151','آگاه باشید! آنان از روی دروغ و تهمت (چنین( می‌گویند.');
INSERT INTO "badkhashani" VALUES(37,152,'37:152','الله فرزند زاده است! و البته ایشان دروغگویند.');
INSERT INTO "badkhashani" VALUES(37,153,'37:153','آیا الله دختران را بر پسران ترجیح داده است؟');
INSERT INTO "badkhashani" VALUES(37,154,'37:154','شما را چه شده است، چگونه حکم و فیصله می‌کنید؟');
INSERT INTO "badkhashani" VALUES(37,155,'37:155','آیا عبرت نمی‌گیرید؟');
INSERT INTO "badkhashani" VALUES(37,156,'37:156','آیا برای شما دلیل آشکار است؟');
INSERT INTO "badkhashani" VALUES(37,157,'37:157','اگر راست می‌گویید کتاب (سند) تان را بیاورید.');
INSERT INTO "badkhashani" VALUES(37,158,'37:158','و (کافران) بين الله و جنیان نسبتی قایل شدند، حال آنکه جنيان می‌دانند که (برای محاسبه) احضار خواهند شد.');
INSERT INTO "badkhashani" VALUES(37,159,'37:159','الله از آنچه (او را به آن) توصیف می‌کنند پاک و منزه است.');
INSERT INTO "badkhashani" VALUES(37,160,'37:160','به جز بندگان مخلص الله (که او را به شایستگی توصیف می‌کنند).');
INSERT INTO "badkhashani" VALUES(37,161,'37:161','پس شما و آنچه می‌پرستید.');
INSERT INTO "badkhashani" VALUES(37,162,'37:162','هرگز نمی‌توانید کسی را با فتنه و فساد از راه الله منحرف سازید.');
INSERT INTO "badkhashani" VALUES(37,163,'37:163','به جز کسی که (خودش) مایل باشد که وارد دوزخ گردد.');
INSERT INTO "badkhashani" VALUES(37,164,'37:164','(فرشتگان گویند) و کسی از ما نیست مگر آنکه جایگاه مشخصی دارد.');
INSERT INTO "badkhashani" VALUES(37,165,'37:165','و البته ما (برای عبادت الله) به صف ایستادگانیم.');
INSERT INTO "badkhashani" VALUES(37,166,'37:166','و البته این مائیم که تسبیح گویانیم.');
INSERT INTO "badkhashani" VALUES(37,167,'37:167','و هرچند مشرکان می‌گفتند.');
INSERT INTO "badkhashani" VALUES(37,168,'37:168','اگر پندی از پیشینیان در نزد ما بود.');
INSERT INTO "badkhashani" VALUES(37,169,'37:169','به يقين ما هم از بندگان مخلص الله می‌شدیم.');
INSERT INTO "badkhashani" VALUES(37,170,'37:170','پس به آن کفر ورزیدند و به زودی (نتیجۀ کفر خود را) خواهند دانست.');
INSERT INTO "badkhashani" VALUES(37,171,'37:171','و البته وعدۀ نصرت ما دربارۀ بندگان فرستادۀ ما قبلا ثبت و صادر شده است.');
INSERT INTO "badkhashani" VALUES(37,172,'37:172','البته ایشانند که مدد شدگانند.');
INSERT INTO "badkhashani" VALUES(37,173,'37:173','و يقينا لشکر ماست که پیروزمندانند.');
INSERT INTO "badkhashani" VALUES(37,174,'37:174','پس از آنان تا مدتی روی بگردان.');
INSERT INTO "badkhashani" VALUES(37,175,'37:175','و (عناد و سرکشی) آنان را بنگر، پس به زودی خودشان هم خواهند دید.');
INSERT INTO "badkhashani" VALUES(37,176,'37:176','پس آیا به (آمدن) عذاب ما عجله دارند؟');
INSERT INTO "badkhashani" VALUES(37,177,'37:177','پس چون (عذاب ما) به ساحت آنان نازل شود، پس بيم داده شدگان چه بامداد بدی خواهند داشت!');
INSERT INTO "badkhashani" VALUES(37,178,'37:178','پس از آنان تا مدتی روی بگردان.');
INSERT INTO "badkhashani" VALUES(37,179,'37:179','و بنگر و آنان خودشان هم به زودی خواهند دید.');
INSERT INTO "badkhashani" VALUES(37,180,'37:180','پاک و منزه است پروردگارت، پروردگار صاحب عزت، از توصیف‌هایی که کافران (در بارۀ او) می‌کنند.');
INSERT INTO "badkhashani" VALUES(37,181,'37:181','و سلام بر همۀ فرستادگان (الله).');
INSERT INTO "badkhashani" VALUES(37,182,'37:182','و همه ستایش‌ها سزاوار ذاتی است که پروردگار جهانیان است.');
INSERT INTO "badkhashani" VALUES(38,1,'38:1','ص. (مفهوم این حرف به الله معلوم است) قسم به قرآن والا و پند آموز.');
INSERT INTO "badkhashani" VALUES(38,2,'38:2','بلکه کافران در سرکشی سخت و مخالفت شدید قرار دارند.');
INSERT INTO "badkhashani" VALUES(38,3,'38:3','چه بسیار اقوام پیش از آنان را نابود کردیم که فریاد نمودند، ليكن فرصت (فرصت) نجات نبود.');
INSERT INTO "badkhashani" VALUES(38,4,'38:4','و تعجب کردند از اینکه بیم دهنده‌ای از خودشان به سویشان آمده است. و کافران گفتند: این جادوگر دروغگو است.');
INSERT INTO "badkhashani" VALUES(38,5,'38:5','آیا معبودان (متعدد) را معبود واحد ساخته است؟ البته که این چیز بسیار عجیب است.');
INSERT INTO "badkhashani" VALUES(38,6,'38:6','و رؤسای‌شان (از مجلس پیغمبر) برآمدند (و به دیگران نیز گفتند:) بروید و بر (پرستش) معبودان خود صبر کنید، بی‌گمان این (مقاومت) همان چیزی است که (از شما) خواسته می‌شود.');
INSERT INTO "badkhashani" VALUES(38,7,'38:7','و ما این مطلب را در آیین اخیر (دین قریش یا دین نصرانیت) نشنیده‌ایم، این آیین جز آیین دروغ و ساختگی نیست.');
INSERT INTO "badkhashani" VALUES(38,8,'38:8','آیا از بین همۀ ما این قرآن بر او نازل شده است؟ بلکه آنان از قرآن من در شک‌اند، بلکه هنوز عذاب مرا نچشیده‌اند.');
INSERT INTO "badkhashani" VALUES(38,9,'38:9','آیا نزد آنها خزانه‌های رحمت پروردگار غالب بخشاینده‌ات هست.');
INSERT INTO "badkhashani" VALUES(38,10,'38:10','آیا پادشاهی آسمان‌ها و زمین و چیزهایی که در میان آن دو است از ایشان است؟ پس اگر چنین است با وسایل و اسباب بالا روند.');
INSERT INTO "badkhashani" VALUES(38,11,'38:11','آنانکه اینجا هستند، لشكر ناچیزی از گروه‌های شکست خورده هستند.');
INSERT INTO "badkhashani" VALUES(38,12,'38:12','پیش از آنان قوم نوح و عاد و فرعون صاحب لشکر و قدرت (پیغمبران ما را) تکذیب کردند.');
INSERT INTO "badkhashani" VALUES(38,13,'38:13','و قوم ثمود و قوم لوط و اصحاب ایکه، آنان نیز احزابی بودند (که پیغمبران را تکذیب کردند).');
INSERT INTO "badkhashani" VALUES(38,14,'38:14','هریک از این گروه‌ها پیغمبران را تکذیب کردند، پس عذاب الهی (در مورد آنان) تحقق یافت.');
INSERT INTO "badkhashani" VALUES(38,15,'38:15','این کافران جز یک صدای مرگبار را انتظار نمی‌کشند که (پس از آن) هیچ مهلت و بازگشتی برای آن نیست.');
INSERT INTO "badkhashani" VALUES(38,16,'38:16','و گفتند: (با تمسخر و غرور) ای پروردگار ما! سهم ما را از (عذاب) پیش از روز حساب به ما بده.');
INSERT INTO "badkhashani" VALUES(38,17,'38:17','لذا (ای پیغمبر) بر آنچه می‌گویند صبر کن و بندۀ ما داود را یاد کن که صاحب قدرت بود، واقعا او (به سوی ما) بسیار رجوع کننده بود.');
INSERT INTO "badkhashani" VALUES(38,18,'38:18','البته ما کوه‌ها را مسخر کردیم تا شام و صبح همرای او تسبیح گویند.');
INSERT INTO "badkhashani" VALUES(38,19,'38:19','و پرندگان را نیز مسخر نمودیم در حالیکه با او جمع شده بودند، همگی فرمانبردار او بودند.');
INSERT INTO "badkhashani" VALUES(38,20,'38:20','و فرمانروایی و سلطنت او را استوار و محکم ساختیم و به او حکمت و قدرت قضاوت عادلانه دادیم.');
INSERT INTO "badkhashani" VALUES(38,21,'38:21','و آیا ماجرای دادخواهان به تو رسیده است، وقتی که بر دیوار عبادتگاه (داود) بالا شدند (و بر او فرود آمدند).');
INSERT INTO "badkhashani" VALUES(38,22,'38:22','چون بر داود (ناگهانی) وارد شدند پس او از آنان ترسید، گفتند: مترس! ما دو مدعی هستیم که یکی از ما بر دیگری ظلم کرده است، پس در میان ما به حق و عدل فيصله و داوری کن، و ظلم روا مدار و ما را به راه راست راهنمایی فرما.');
INSERT INTO "badkhashani" VALUES(38,23,'38:23','يقينا که این برادر من است و او نود و نه میش دارد و من تنها یک میش دارم و (وی به من) می‌گوید: آن را به من واگذار، و او در گفتگو بر من غلبه نموده است.');
INSERT INTO "badkhashani" VALUES(38,24,'38:24','(داود) گفت: بی‌شک او با درخواست (اینکه) میشت (را به او بدهی تا آن را) با میش‌های خود اضافه کند به تو ظلم کرده است. و حقا که بسیاری از شریکان به همدیگر ظلم می‌کنند، مگر آنانی که ایمان آورده و کارهای نیک انجام داده‌اند ولی چنین کسانی بسیار اندک و کم هستند. پس داود دانست که ما او را آزموده‌ایم، بنابراین، از پروردگار خویش آمرزش خواست و به سجده افتاد و توبه و انابه کرد.');
INSERT INTO "badkhashani" VALUES(38,25,'38:25','پس ما این (قضاوت عجولانه) را بر او بخشیدیم و البته برای او در نزد ما مقام قربت و عاقبت نیک است.');
INSERT INTO "badkhashani" VALUES(38,26,'38:26','ای داود! ما تو را در زمین خلیفه ساخته‌ایم، پس به حق در میان مردم حکم کن، و از هوای نفس پیروی مکن که تو را از راه الله منحرف می‌سازد، بی‌گمان آنان که از راه الله منحرف می‌شوند، برای‌شان عذاب سخت است به خاطر آنکه روز حساب را فراموش کردند.');
INSERT INTO "badkhashani" VALUES(38,27,'38:27','و ما آسمان‌ها و زمین و آنچه را که بین آن دو است بیهوده نه آفریدیم. این گمان کافران است، پس وای به حال کافران از آتش (دوزخ).');
INSERT INTO "badkhashani" VALUES(38,28,'38:28','آیا کسانی را که ایمان آورده و کارهای نیک انجام داده‌اند، مانند فساد گران در زمین قرار می‌دهیم؟ یا پرهیزگاران را مانند بدکاران قرار میدهیم؟');
INSERT INTO "badkhashani" VALUES(38,29,'38:29','(این) کتابی مبارکی است که آن را به‌سوی تو فرو فرستاده‌ایم تا در آیات آن تدبر کنند و خردمندان عبرت گیرند.');
INSERT INTO "badkhashani" VALUES(38,30,'38:30','و به داود سلیمان را عطا کردیم، او بهترین بنده‌ای بود، چون بسیار به دربار ما روی می‌آورد.');
INSERT INTO "badkhashani" VALUES(38,31,'38:31','وقتی که عصرگاه اسپهای چابک (و تندرو) به او عرضه شد.');
INSERT INTO "badkhashani" VALUES(38,32,'38:32','پس گفت: واقعا من این اسپان را به خاطر پروردگارم (و جهاد در راه او) دوست داشتم (و نگاه او به دیدن آنها ادامه داشت) تا آنکه اسپان از دیده‌های او پنهان شدند.');
INSERT INTO "badkhashani" VALUES(38,33,'38:33','(پس دستور داد که) آن (اسپها) را نزد من باز گردانید (تا بار دیگر به آنها نگاه کنم)، پس شروع به دست کشیدن بر ساق‌ها و گردنهای آنها کرد.');
INSERT INTO "badkhashani" VALUES(38,34,'38:34','و البته ما سلیمان را آزمودیم و بر تخت او جسدی افگندیم، باز به سوى (الله) روی آورد و توبه کرد.');
INSERT INTO "badkhashani" VALUES(38,35,'38:35','گفت: ای پروردگارم! مرا بیامرز، و به من پادشاهی عطا کن که بعد از من سزاوار هیچ کس نباشد، چون تو بسیار بخشنده ای.');
INSERT INTO "badkhashani" VALUES(38,36,'38:36','پس باد را برایش مسخر کردیم که به فرمان او هرجا که می‌خواست به آرامی سیر می‌کرد.');
INSERT INTO "badkhashani" VALUES(38,37,'38:37','و شیطان‌ها را (برای او مسخر کردیم) هر معمار و غوطه خور از آنها را.');
INSERT INTO "badkhashani" VALUES(38,38,'38:38','و گروه دیگری از شیاطین را (هم با دست و پاهای) به هم بسته در زنجیرها (مسخر او کردیم).');
INSERT INTO "badkhashani" VALUES(38,39,'38:39','(و به سلیمان گفتیم:) این بخشش بی‌حساب ماست، پس به هر کس می‌خواهی ببخش یا نگاه دار.');
INSERT INTO "badkhashani" VALUES(38,40,'38:40','و البته او در نزد ما مقامی ارجمند و سرانجامی نیک دارد.');
INSERT INTO "badkhashani" VALUES(38,41,'38:41','و بندۀ ما ایوب را یاد کن وقتی که پروردگارش را فریاد کرد که همانا شیطان به من رنج و عذاب رسانده است.');
INSERT INTO "badkhashani" VALUES(38,42,'38:42','(گفتیم) پای خود را به زمین بزن (تا از زیر پایت چشمه‌ای جاری شود)؛ این (چشمه)، جای شستشو، (و دارای آب) سرد و نوشیدنی است.');
INSERT INTO "badkhashani" VALUES(38,43,'38:43','و ما از رحمت خویش خانواده‌اش را به او بخشیدیم و مانند آنها را به ایشان افزودیم تا پندی برای خردمندان باشد.');
INSERT INTO "badkhashani" VALUES(38,44,'38:44','و (دستور دادیم) که دسته‌ای از شاخه‌های باریک را بگیر و (او را) با آن بزن (تا بدن همسرت آزرده نشود) و سوگند خود را مشكن، ما ایوب را صابر یافتیم، چه بندۀ خوبی بود بی‌گمان او بسیار توبه‌کار بود.');
INSERT INTO "badkhashani" VALUES(38,45,'38:45','و بندگان ما ابراهیم و اسحاق و یعقوب را یاد کن که دارای قوت و بصیرت بودند.');
INSERT INTO "badkhashani" VALUES(38,46,'38:46','البته ما آنان را با خلوصی خاصی که یاد آور سرای آخرت بود خالص کردیم.');
INSERT INTO "badkhashani" VALUES(38,47,'38:47','و البته آنها نزد ما از برگزیدگان نیک بودند.');
INSERT INTO "badkhashani" VALUES(38,48,'38:48','و اسماعیل و يسع و ذالکفل را یاد کن و همگی آنان از نیکان بودند.');
INSERT INTO "badkhashani" VALUES(38,49,'38:49','این یادی (از ایشان در دنیا) است و البته برای پرهیزگاران سرانجام نیک (در آخرت) خواهد بود.');
INSERT INTO "badkhashani" VALUES(38,50,'38:50','باغ‌های جاودان (در بهشت) در حالیکه درهایش به روی آنان گشوده است.');
INSERT INTO "badkhashani" VALUES(38,51,'38:51','در آنجا (بر تخت‌ها) تکیه می‌زنند و میوه‌های فراوان و نوشیدنی‌های زیادی را می‌طلبند.');
INSERT INTO "badkhashani" VALUES(38,52,'38:52','و نزد آنان زنانی هستند که تنها به شوهرانشان چشم می‌دوزند و همگی هم سن و سال هستند.');
INSERT INTO "badkhashani" VALUES(38,53,'38:53','این همان ثوابی است که برای روز حساب به شما وعده داده می‌شد.');
INSERT INTO "badkhashani" VALUES(38,54,'38:54','البته این عطا و روزی ماست که برای آن پایانی نیست.');
INSERT INTO "badkhashani" VALUES(38,55,'38:55','این (پاداش پرهیزگاران است) و البته برای سرکشان سرانجام بدی است.');
INSERT INTO "badkhashani" VALUES(38,56,'38:56','دوزخی که در آن وارد می‌شوند و (دوزخ) چه بد آرامگاهی است!');
INSERT INTO "badkhashani" VALUES(38,57,'38:57','این آب داغ و زردآب دوزخیان است که آن را باید بچشند.');
INSERT INTO "badkhashani" VALUES(38,58,'38:58','و عذاب‌های دیگری از همان نوع برای آنان است.');
INSERT INTO "badkhashani" VALUES(38,59,'38:59','(به سران کفر گویند:) این گروهی (از پیروان شما)‌اند که همرای شما وارد (دوزخ) می‌شوند، خوش آمدید برای آنان نیست؛ چون آنان وارد دوزخ شدند.');
INSERT INTO "badkhashani" VALUES(38,60,'38:60','(پیروان) گویند: بلکه شما را خوش آمدید مباد چون شما عذاب را بر ما پیش کردید، پس بد جای قرار است دوزخ.');
INSERT INTO "badkhashani" VALUES(38,61,'38:61','و گویند: ای پروردگار ما! هر کس ما را دچار این سرنوشت ساخته است، پس عذاب او را در آتش دو چند گردان.');
INSERT INTO "badkhashani" VALUES(38,62,'38:62','و (دوزخیان) گویند: ما را چه شده است کسانی را نمی‌بینیم که آنان را از اشرار می‌شمردیم؟');
INSERT INTO "badkhashani" VALUES(38,63,'38:63','آیا آنان را به مسخره می‌گرفتیم (و امروز اهل بهشت اند)؟ و یا این که چشمان ما ایشان را دیده نمی‌توانند.');
INSERT INTO "badkhashani" VALUES(38,64,'38:64','البته این مجادلۀ دوزخیان حق و واقع شدنی است.');
INSERT INTO "badkhashani" VALUES(38,65,'38:65','بگو: من تنها بیم دهنده هستم و بس، و هیچ معبود به حقی جز الله واحد و مقتدر وجود ندارد.');
INSERT INTO "badkhashani" VALUES(38,66,'38:66','(ذاتی که) پروردگار آسمان‌ها و زمین و آنچه میان آن دو است. (ذات) غالب آمرزگار.');
INSERT INTO "badkhashani" VALUES(38,67,'38:67','بگو: آن خبر بزرگ است.');
INSERT INTO "badkhashani" VALUES(38,68,'38:68','که شما از آن روی می‌گردانید.');
INSERT INTO "badkhashani" VALUES(38,69,'38:69','من از عالم بالا وقتی که با همدیگر گفتگو می‌کنند خبری ندارم.');
INSERT INTO "badkhashani" VALUES(38,70,'38:70','(چون) به من وحی نمی‌شود مگر به آن خاطر که بیم دهندۀ آشکار هستم.');
INSERT INTO "badkhashani" VALUES(38,71,'38:71','یادآور شو وقتی که پروردگارت به فرشتگان گفت: همانا من آفرینندۀ بشری از گل هستم.');
INSERT INTO "badkhashani" VALUES(38,72,'38:72','پس چون آن را سر و سامان داده و برابر کردم، و از روح (خلق کرده) خود در او دمیدم، پس (همه) سجده کنان به او بیفتید.');
INSERT INTO "badkhashani" VALUES(38,73,'38:73','پس همۀ فرشتگان جملگی (به آدم) سجده کردند.');
INSERT INTO "badkhashani" VALUES(38,74,'38:74','به جز ابلیس که تكبر ورزید و از کافران شد.');
INSERT INTO "badkhashani" VALUES(38,75,'38:75','(الله) فرمود: ای ابلیس! چه چیزی تو را بازداشت از اینکه برای آنچه با دو دست خود آفریده‌ام سجده کنی؟ تكبر ورزیدی یا از بلندمرتبه‌گان هستی؟');
INSERT INTO "badkhashani" VALUES(38,76,'38:76','(ابلیس) گفت: من از او بهتر هستم، مرا از آتش آفریده‌ای و او را از گل آفریده‌ای.');
INSERT INTO "badkhashani" VALUES(38,77,'38:77','(الله) فرمود: از آن بیرون شو چون که تو رانده شده ای.');
INSERT INTO "badkhashani" VALUES(38,78,'38:78','و البته تا روز جزا نفرین من بر تو خواهد بود.');
INSERT INTO "badkhashani" VALUES(38,79,'38:79','(ابلیس) گفت: پروردگارم! تا روزی که بندگان برانگیخته می‌شوند مرا مهلت بده.');
INSERT INTO "badkhashani" VALUES(38,80,'38:80','(الله) فرمود: همانا تو از مهلت داده شد گانی.');
INSERT INTO "badkhashani" VALUES(38,81,'38:81','تا روز و وقت معین (قیامت).');
INSERT INTO "badkhashani" VALUES(38,82,'38:82','(ابلیس) گفت: پس به عزت و عظمت تو قسم که همگی آنان را گمراه می‌کنم.');
INSERT INTO "badkhashani" VALUES(38,83,'38:83','مگر آن بندگان مخلص تو را (گمراه ساخته نمی‌توانم).');
INSERT INTO "badkhashani" VALUES(38,84,'38:84','(الله) فرمود: پس (این) حق است و حق را می‌گویم.');
INSERT INTO "badkhashani" VALUES(38,85,'38:85','که حتما دوزخ را هم از تو و هم از کسانی که از تو پیروی کنند پر خواهم کرد.');
INSERT INTO "badkhashani" VALUES(38,86,'38:86','بگو: من در مقابل (تبلیغ) قرآن هیچ مزدی از شما نمی‌طلبم، و از تكلف کنندگان نیستم (زیرا سخنانم روشن است و چیزی را بر شما تحمیل نمی‌کنم).');
INSERT INTO "badkhashani" VALUES(38,87,'38:87','این (قرآن) جز پندی برای جهانیان نیست.');
INSERT INTO "badkhashani" VALUES(38,88,'38:88','و حتما خبر (حق بودن) آن را بعد از مدتی خواهید دانست.');
INSERT INTO "badkhashani" VALUES(39,1,'39:1','فرود آوردن این کتاب از طرف الله غالب باحکمت است.');
INSERT INTO "badkhashani" VALUES(39,2,'39:2','البته ما کتاب را به حق بر تو نازل کردیم، پس الله را در حالیکه دین خود را برای او خالص گردانده ای، عبادت کن.');
INSERT INTO "badkhashani" VALUES(39,3,'39:3','بدان که دین خالص مخصوص الله است و کسانی که به جای او دوستانی گرفته‌اند (می گویند:) ما آنان را عبادت نمی‌کنیم مگر برای آنکه ما را به الله نزدیک کنند. البته الله میان آنها دربارۀ چیزی که در آن اختلاف می‌ورزند، فيصله خواهد کرد، یقینا الله دروغگوی ناشکر را هدایت نمی‌کند.');
INSERT INTO "badkhashani" VALUES(39,4,'39:4','اگر الله می‌خواست (برای خود) فرزندی بگیرد، حتما از آنچه که می‌آفریند، چیزی را که می‌خواست، اختیار می‌کرد. او پاک و منزه است (از این که فرزندی داشته باشد) و اوست الله که واحد قهر کننده است.');
INSERT INTO "badkhashani" VALUES(39,5,'39:5','او آسمان‌ها و زمین را به حق آفریده است، شب را بر روز و روز را بر شب می‌پیچد و آفتاب و ماه را مسخر کرده است و هر یک تا مدتی معین در حرکتند. بدان که اوست غالب آمرزگار.');
INSERT INTO "badkhashani" VALUES(39,6,'39:6','شما را از یک نفس آفرید، باز همسرش را از همان نفس آفرید و (از خزانۀ قدرت خود) از چهارپایان هشت نوع برای شما آفرید. او شما را در شکم‌های مادرانتان در میان تاریکی‌های سه گانه آفرینشی پس از آفرینش دیگر می‌آفریند. این است الله پروردگار شما، پادشاهی برای اوست، معبود به حقی به جز او نیست. پس چگونه برگردانیده می‌شوید؟');
INSERT INTO "badkhashani" VALUES(39,7,'39:7','اگر کفر ورزید (بدانید که) الله از شما بی‌نیاز است، لیکن کفر را برای بندگان خود نمی‌پسندد. و اگر شکر بگزارید آن را برایتان می‌پسندد. و هیچ کس بار گناه دیگری را بر نمی‌دارد، باز بازگشت همه شما به‌سوی پروردگارتان است، پس شما را به آنچه می‌کردید خبر می‌دهد. چون او به راز دلها داناست.');
INSERT INTO "badkhashani" VALUES(39,8,'39:8','و چون به انسان آسیبی برسد، پروردگارش را می‌خواند در حالیکه با توبه روی به درگاه او آورده است، باز چون نعمتی از سوی خود به او ببخشد آن (مصیبتی) را که به خاطر رفع آن به درگاه او دعا می‌کرد، فراموش می‌کند و برای الله شریکانی قرار می‌دهد تا (خود و دیگران را) از راه او گمراه سازد. بگو: مدتی اندک از کفر خود برخوردار شو، البته تو از دوزخیان هستی.');
INSERT INTO "badkhashani" VALUES(39,9,'39:9','آیا (مشرکی که اوصافش را بیان کردیم بهتر است یا) کسی که در اوقات شب سجده کنان و ایستاده به عبادت مشغول است در حالیکه از آخرت می‌ترسد و به رحمت پروردگارش امید دارد؟ بگو: آیا آنان که می‌دانند و آنان که نمی‌دانند برابرند؟. تنها صاحبان عقل و خرد پند می‌گیرند.');
INSERT INTO "badkhashani" VALUES(39,10,'39:10','(ای پیغمبر!) بگو: ای بندگان مؤمن من! از پروردگارتان بترسید، برای کسانی که نیکی کرده‌اند در این دنیا نیکی است، و زمین الله فراخ است، البته به صابران پاداششان به طور کامل و بی‌حساب داده می‌شود.');
INSERT INTO "badkhashani" VALUES(39,11,'39:11','بگو: به من حكم شده که الله را بپرستم، در حالیکه دین را برای او خالص گردانم.');
INSERT INTO "badkhashani" VALUES(39,12,'39:12','و (نیز) به من حكم شده که اولین (کس از) مسلمانان باشم.');
INSERT INTO "badkhashani" VALUES(39,13,'39:13','بگو: البته من اگر از پروردگارم نافرمانی کنم از عذاب روز بزرگ می‌ترسم.');
INSERT INTO "badkhashani" VALUES(39,14,'39:14','بگو: من تنها الله را می‌پرستم در حالیکه دینم را برای او خالص کرده ام.');
INSERT INTO "badkhashani" VALUES(39,15,'39:15','پس (شما مشرکان) هر چه را که می‌خواهید به جای او پرستش کنید. بگو: زیانکاران حقیقی کسانی هستند که خود و خانوادۀ خود را در روز قيامت خساره مند سازند. آگاه باش، این است همان زیان آشکار.');
INSERT INTO "badkhashani" VALUES(39,16,'39:16','از بالای سرشان سایبان‌هایی از آتش و از جانب زیر‌شان نیز سایبان‌هایی از آتش است، این چیزی است که الله به وسیلۀ آن بندگانش را می‌ترساند. پس ای بندگانم! از من بترسید.');
INSERT INTO "badkhashani" VALUES(39,17,'39:17','و کسانی که از عبادت طاغوت اجتناب ورزیدند و به‌سوی الله روی آوردند برای‌شان مژده است. پس بندگانم را مژده ده.');
INSERT INTO "badkhashani" VALUES(39,18,'39:18','آنانی که سخن را (به دقت) می‌شنوند باز بهترین آن را پیروی می‌کنند، ایشانند که الله هدایتشان کرده و ایشانند همان خردمندان.');
INSERT INTO "badkhashani" VALUES(39,19,'39:19','آیا کسی که حکم عذاب بر او حتمی شده است (امید نجات دارد؟) پس آیا تو می‌توانی کسی را که در آتش است نجات دهی؟');
INSERT INTO "badkhashani" VALUES(39,20,'39:20','اما آنان که از پروردگار‌شان ترسیدند، برای آنها قصرهای است که بالای یکدیگر ساخته شده‌اند و در زیر آن نهرها روان است، (این) وعدۀ الله است و الله وعده‌اش را خلاف نمی‌کند.');
INSERT INTO "badkhashani" VALUES(39,21,'39:21','آیا ندیدی که الله از آسمان آب فرود می‌آورد، پس آن را به صورت چشمه‌ها در زمین راه داد، باز به وسیلۀ آن انواع سبزه زارها و کشتزارها را با رنگ‌های گوناگون می‌رویاند، باز خشک می‌شود و آن را زرد رنگ می‌بینی، باز آنها را کاه و خاشاک می‌گرداند. البته در این امر برای خردمندان پند و عبرت است.');
INSERT INTO "badkhashani" VALUES(39,22,'39:22','آیا کسی که الله سینه‌اش را برای (پذیرش) اسلام گشوده است و او از سوی پروردگارش از نوری برخوردار است (چون سنگدلان است؟) پس وای بر کسانی که از شدت سنگدلی، الله را یاد نمی‌کنند! ایشان در گمراهی آشکارند.');
INSERT INTO "badkhashani" VALUES(39,23,'39:23','الله بهترین سخن را نازل کرده است، کتابی که آیات آن متشابه (و مشابه به همدیگر) است. (از شنیدن آن) پوست‌های آنانکه از پروردگارشان می‌ترسند به لرزه می‌افتد، باز پوست‌ها و دل‌هایشان با یاد الله نرم می‌شود. این هدایت الله است. و الله هرکس را بخواهد به آن هدایت می‌کند و هر کس را که الله گمراه سازد برای او هیچ راهنما و هدایتگری نیست.');
INSERT INTO "badkhashani" VALUES(39,24,'39:24','آیا کسی که (با ایمان و عمل نیک) روی خود را از عذاب بد روز قیامت دور نگه می‌دارد (مانند کسی است که از عذاب آن روز غافل است؟) و (در آن روز) به ظالمان گفته می‌شود: بچشید (سزای) آنچه را که کسب کردید.');
INSERT INTO "badkhashani" VALUES(39,25,'39:25','آنان که پیش از ایشان بودند (نیز پیغمبران را) تکذیب کردند، پس (در نتیجه) عذاب از جایی که نمی‌دانستند به سراغ‌شان آمد.');
INSERT INTO "badkhashani" VALUES(39,26,'39:26','پس الله خواری و رسوایی را در زندگانی دنیا به آنان چشانید و يقينا عذاب آخرت بزرگ‌تر است، اگر می‌دانستند.');
INSERT INTO "badkhashani" VALUES(39,27,'39:27','و البته ما در این قرآن برای مردم از هر مثلی آورده‌ایم، تا آنان پند گیرند.');
INSERT INTO "badkhashani" VALUES(39,28,'39:28','قرآن عربی، بدون هیچ کجی است، تا آنان (به وسیلۀ آن) پرهیزگار شوند.');
INSERT INTO "badkhashani" VALUES(39,29,'39:29','الله (برای توحید و شرک) مثالی بیان کرده است مردی را که بردۀ شریکان متعدد است که دربارۀ (مالكيت) او ناسازند و مردی را که مطیع یک شخص است، آیا این دو برابر‌اند؟ حمد و ستایش مخصوص الله است، بلکه بیشتر آنان نمی‌دانند.');
INSERT INTO "badkhashani" VALUES(39,30,'39:30','(ای پیغمبر) بدون شک تو خواهی مرد و آنان (نیز) مُردنی هستند.');
INSERT INTO "badkhashani" VALUES(39,31,'39:31','باز شما روز قیامت نزد پروردگارتان نزاع و مخاصمه خواهید کرد.');
INSERT INTO "badkhashani" VALUES(39,32,'39:32','پس کیست ظالم‌تر از کسی که بر الله دروغ بست و چون حقیقت برای او آمد، دروغ شمرد؟ آیا برای کافران در دوزخ جایگاه نیست؟');
INSERT INTO "badkhashani" VALUES(39,33,'39:33','و کسیکه سخن راست را با خود آورد و آن را تصدیق کرد، ایشانند همان پرهیزگاران.');
INSERT INTO "badkhashani" VALUES(39,34,'39:34','آنان هر آنچه را بخواهند نزد پروردگارشان است، این است پاداش نیکوکاران.');
INSERT INTO "badkhashani" VALUES(39,35,'39:35','تا الله بدترین کاری را که کرده بودند از (عملنامۀ) آنان بپوشاند. و آنان را به بهترین اعمالی که انجام داده بودند، اجر می‌دهد.');
INSERT INTO "badkhashani" VALUES(39,36,'39:36','آیا الله برای بنده‌اش کافی نیست؟ آنان تو را از غير الله می‌ترسانند. و هرکس را الله گمراه کند، پس هیچ راهنمایی برای او نخواهد بود.');
INSERT INTO "badkhashani" VALUES(39,37,'39:37','و هر کس را الله هدایت کند، پس هیچ گمراه کننده‌ای برای او نیست. آیا الله غالب انتقام گیر نیست؟');
INSERT INTO "badkhashani" VALUES(39,38,'39:38','و اگر از آنان بپرسی که چه کسی آسمان‌ها و زمین را آفرید؟ حتما می‌گویند: الله. بگو: پس آیا در آنچه که به جز الله را می‌خوانید اندیشه کرده‌اید؟ اگر الله بخواهد زیانی به من برساند، آنها می‌توانند آن زیان الهی را برطرف سازند؟ و یا اگر الله رحمتی را به من بخواهد، آنها می‌توانند مانع رحمت او شوند؟ بگو: الله برای من کافی است، توکل کنندگان تنها بر او توکل می‌کنند.');
INSERT INTO "badkhashani" VALUES(39,39,'39:39','بگو: ای قوم من! شما به وضع خود عمل کنید من (نیز به وضع و مأموریت خودم) عمل کننده ام، پس خواهید دانست.');
INSERT INTO "badkhashani" VALUES(39,40,'39:40','(خواهید دانست که) چه کسی عذاب خوار کننده بر وی خواهد آمد و عذاب پاینده بر او نازل خواهد شد.');
INSERT INTO "badkhashani" VALUES(39,41,'39:41','البته ما این کتاب را برای (هدایت) مردم به حق بر تو نازل کردیم. پس هرکس که هدایت یابد به نفع خود اوست و هر کس که گمراه گردد، تنها به زبان خود گمراه می‌شود. و تو بر آنان وکیل و نگهبان نیستی.');
INSERT INTO "badkhashani" VALUES(39,42,'39:42','الله است که جانها را در وقت مرگشان می‌گیرد، و نیز آن (جانی) که نمرده است (آنرا) وقت خوابش (می گیرد)، و (جانی را) که به مرگ آن حکم قطعی کرده است نگاه می‌دارد، و آن دیگر را تا زمان معینی باقی می‌گذارد، يقينا در این امر نشانه‌های روشن (و بزرگ الهی) برای کسانی است که (در قدرت الله) فکر می‌کنند.');
INSERT INTO "badkhashani" VALUES(39,43,'39:43','آیا غیر از الله معبودان شفاعتگر گرفته‌اند؟ بگو: اگر مالک هیچ چیزی نباشند و توان فهمیدن و خرد نداشته باشند (باز هم آنان را شفیعان خود می‌گیرید)؟');
INSERT INTO "badkhashani" VALUES(39,44,'39:44','بگو: همه شفاعت‌ها برای الله است، فرمانروایی آسمان‌ها و زمین از اوست، باز به‌سوی او باز گردانیده می‌شوید.');
INSERT INTO "badkhashani" VALUES(39,45,'39:45','و چون الله به تنهایی یاد شود، کسانی که به آخرت ایمان ندارند متنفر می‌شوند، ولی وقتی کسانی غیر از او یاد شوند، ناگهان شاد و خورسند می‌شوند.');
INSERT INTO "badkhashani" VALUES(39,46,'39:46','بگو: ای پروردگار! ای ایجاد کنندۀ آسمان‌ها و زمین! ای آگاه از پنهان و آشکار! تویی که میان بندگانت در آنچه همیشه اختلاف می‌کردند، فيصله خواهی کرد.');
INSERT INTO "badkhashani" VALUES(39,47,'39:47','و اگر هر آنچه در زمین است از ظالمان باشد و مانند آن (نیز) با آن باشد، به یقین به خاطر (نجات از) سختی عذاب روز قیامت آن را فدیه بدهند (ولی سودی نخواهد داشت) و از جانب الله چیزی برایشان آشکار شود که گمانش را نمی‌کردند.');
INSERT INTO "badkhashani" VALUES(39,48,'39:48','و (در آن روز) بدی آنچه (در دنیا) کسب کرده بودند برایشان آشکار می‌شود و آن عذابی را که به مسخره می‌گرفتند، آنان را فراخواهد گرفت.');
INSERT INTO "badkhashani" VALUES(39,49,'39:49','پس چون سختی و ضرری به انسان برسد، ما را می‌خواند. باز وقتی از سوی خود به او نعمتی عطا کنیم گوید: جز این نیست که آن نعمت بر اساس دانش و تدبیری که دارم به من داده شده! نه، چنین نیست. بلکه این نعمت وسیلۀ آزمایش است ولی بیشتر مردم نمی‌دانند.');
INSERT INTO "badkhashani" VALUES(39,50,'39:50','البته این سخن را کسانی هم گفتند که پیش از ایشان بودند، ولی آنچه را که (در دنیا) کسب می‌کردند، برایشان سودی نکرد.');
INSERT INTO "badkhashani" VALUES(39,51,'39:51','پس (سزای) بدی‌هایی که کرده بودند به آنان رسید، و کسانی از ایشان (کفار مکه) هم که ظلم کرده‌اند (سزای) بدی‌هایی که می‌کردند به آنان خواهد رسید، و هرگز نمی‌توانند (از عذاب الهی بگریزند و الله را) ناتوان کنند.');
INSERT INTO "badkhashani" VALUES(39,52,'39:52','آیا ندانستند که الله روزی را برای هر کس که بخواهد فراخ یا تنگ می‌سازد؟ البته در این امر نشانه‌هایی است برای کسانی که ایمان می‌آورند.');
INSERT INTO "badkhashani" VALUES(39,53,'39:53','بگو: ای بندگانم که بر خود اسراف (و ظلم) کرده‌اید! از رحمت الله ناامید نشوید، یقینا که الله همۀ گناهان را می‌آمرزد، چرا که او بسیار آمرزگار و بسیار مهربان است.');
INSERT INTO "badkhashani" VALUES(39,54,'39:54','و به‌سوی پروردگار خود رجوع کنید و تسلیم او شوید، پیش از اینکه عذاب به شما برسد باز مدد کرده نشوید.');
INSERT INTO "badkhashani" VALUES(39,55,'39:55','و از بهترین چیزی که از سوی پروردگارتان برای شما نازل شده پیروی کنید، پیش از اینکه ناگهان و در حالیکه بی‌خبرید، عذاب به شما برسد.');
INSERT INTO "badkhashani" VALUES(39,56,'39:56','تا (مبادا در قیامت) کسی بگوید: وای بر من به خاطر تقصیری که در حق الله کردم و بی‌گمان از مسخره کنندگان بودم.');
INSERT INTO "badkhashani" VALUES(39,57,'39:57','یا آنکه (از شدت اندوه) گوید: ای کاش الله مرا هدایت می‌کرد و از پرهیزگاران می‌بودم.');
INSERT INTO "badkhashani" VALUES(39,58,'39:58','یا چون عذاب را ببیند، بگوید: ای کاش برای من باز گشتی بود، تا از نیکوکاران می‌شدم.');
INSERT INTO "badkhashani" VALUES(39,59,'39:59','بلی، بی‌گمان آیات من به تو رسید، ولی آن را تکذیب کردی و تكبر ورزیدی و از جملۀ کافران شدی.');
INSERT INTO "badkhashani" VALUES(39,60,'39:60','و روز قیامت آنان را که بر الله دروغ بسته‌اند خواهی دید که چهره‌هایشان سیاه شده است. آیا برای متکبران جایگاهی در دوزخ نیست؟');
INSERT INTO "badkhashani" VALUES(39,61,'39:61','و الله پرهیزگاران را به سبب کامیابی‌شان نجات می‌دهد، در حالیکه هیچ رنج و عذاب به ایشان نمی‌رسد و غمگین نمی‌شوند.');
INSERT INTO "badkhashani" VALUES(39,62,'39:62','الله آفرینندۀ همه چیز است و او بر همه چیز وکیل و نگهبان است.');
INSERT INTO "badkhashani" VALUES(39,63,'39:63','کلیدهای آسمان‌ها و زمین از اوست و کسانی که به آيات الله کفر ورزیده‌اند، ایشان زیان کارانند.');
INSERT INTO "badkhashani" VALUES(39,64,'39:64','بگو: ای نادانان! آیا به من دستور میدهید که غیر از الله را عبادت کنم؟');
INSERT INTO "badkhashani" VALUES(39,65,'39:65','و يقينا به تو و به کسانی که پیش از تو بودند وحی شده است که اگر شرک ورزی بدون شک عمل تو تباه می‌شود و حتما از زیانکاران خواهی بود.');
INSERT INTO "badkhashani" VALUES(39,66,'39:66','بلکه تنها الله را عبادت کن و از جملۀ شکرگزاران باش.');
INSERT INTO "badkhashani" VALUES(39,67,'39:67','و الله را چنانکه سزاوار بزرگی اوست، قدردانی نکردند و روز قیامت سراسر زمین در قبضۀ او قرار دارد و آسمان‌ها پیچیده به دست راست او می‌باشد. او پاک و منزه است و برتر است از آنچه شریک (وی) می‌گردانند.');
INSERT INTO "badkhashani" VALUES(39,68,'39:68','و در صور دمیده خواهد شد، پس کسانی که در آسمان‌ها و زمین هستند (بی هوش شده) می‌میرند، مگر کسی را که الله بخواهد. باز دوباره در آن دمیده می‌شود، پس ناگهان همگی (زنده شده) می‌خیزند و نظر می‌کنند.');
INSERT INTO "badkhashani" VALUES(39,69,'39:69','و زمین به نور پروردگارش روشن می‌گردد و کتاب (عمل نامه‌ها) نهاده می‌شود و پیغمبران و گواهان آورده می‌شوند و به حق بین آنان فيصله می‌شود و به ایشان ظلم نمی‌شود.');
INSERT INTO "badkhashani" VALUES(39,70,'39:70','و به هرکسی (نتیجۀ) آنچه را که کرده است به تمام و کمال داده می‌شود. و او (الله) به آنچه می‌کنند داناتر است.');
INSERT INTO "badkhashani" VALUES(39,71,'39:71','و کافران به طرف دوزخ گروه گروه رانده می‌شوند و تا چون به نزدیک آن رسند درهایش گشوده شود و نگهبانانش به آنان گویند: آیا پیغمبرانی از خودتان به نزدتان نیامدند تا آیات پروردگارتان را برای شما بخوانند و شما را از ملاقات چنین روزی بترسانند؟ می‌گویند: بلی! ولی فرمان عذاب بر کافران ثابت و حتمی گردید.');
INSERT INTO "badkhashani" VALUES(39,72,'39:72','به ایشان گفته شود: از درهای دوزخ داخل شوید در حالیکه در آن جاودانه خواهید بود. پس چه بد است جایگاه متکبران.');
INSERT INTO "badkhashani" VALUES(39,73,'39:73','و کسانی که از پروردگارشان می‌ترسیدند گروه گروه به‌سوی بهشت سوق داده می‌شوند و تا چون به نزدیک آن رسند (مسرور می‌شوند) و درهایش گشوده شود و نگهبانانش به آنان می‌گویند: سلام بر شما باد! پاک و نیک بودید. پس خوش باشید و برای همیشه در آن در آیید.');
INSERT INTO "badkhashani" VALUES(39,74,'39:74','و (اهل بهشت) می‌گویند: شکر آن ذاتی راست که وعده‌اش را در حق ما محقق ساخت و ما را وارثان زمین (بهشت) گردانید، از بهشت هرجا که بخواهیم مأوى می‌گیریم، پس چه نیکوست پاداش عمل کنندگان.');
INSERT INTO "badkhashani" VALUES(39,75,'39:75','و فرشتگان را می‌بینی که گرد عرش حلقه زده‌اند و به تسبیح و ثنای پروردگار خود مشغولند و در میان آنان به حق فیصله شده و گفته می‌شود: ستایش ذاتی راست که پروردگار جهانیان است.');
INSERT INTO "badkhashani" VALUES(40,1,'40:1','حم. (مفهوم این حروف به الله معلوم است).');
INSERT INTO "badkhashani" VALUES(40,2,'40:2','فرود آوردن این کتاب از سوی الله غالب (و) دانا است.');
INSERT INTO "badkhashani" VALUES(40,3,'40:3','(پروردگاری که) آمرزندۀ گناه (و) قبول کنندۀ توبه (است). سخت سزادهنده و صاحب نعمت فراوان (می‌باشد). هیچ معبودی به حق جز او نیست، بازگشت (همه) به‌سوی اوست.');
INSERT INTO "badkhashani" VALUES(40,4,'40:4','جز کافران در آیات الله مجادله نمی‌کنند، پس آمد و رفت آنها در شهرها نباید تو را فریفته سازد.');
INSERT INTO "badkhashani" VALUES(40,5,'40:5','پیش از این (کفار) قوم نوح و گروه‌هایی که بعد از ایشان بودند (پیغمبران‌شان را) تکذیب کردند و هر امتی تصمیم گرفتند تا پیغمبر خود را به عقوبت بگیرند، و به وسیلۀ باطل مجادله کردند تا حق را نابود کنند، ولی من ایشان را (به قهر خود) گرفتار کردم، پس (بنگر که) عقوبت من چطور بود؟');
INSERT INTO "badkhashani" VALUES(40,6,'40:6','واینچنین سخن پروردگارت در بارۀ کسانی که کفر ورزیدند، تحقق یافت که آنان دوزخیانند.');
INSERT INTO "badkhashani" VALUES(40,7,'40:7','کسانی که عرش را حمل می‌کنند و آنان که در اطراف (عرش) هستند به ستایش پروردگارشان تسبیح می‌گویند و به او ایمان دارند و برای مؤمنان طلب آمرزش می‌کنند (و می‌گویند:) ای پروردگار ما! مهربانی و علم تو همه چیز را فرا گرفته است، پس آنان را که توبه کرده و از راه تو پیروی کرده‌اند، بیامرز و آنان را از عذاب دوزخ حفظ کن.');
INSERT INTO "badkhashani" VALUES(40,8,'40:8','ای پروردگار ما! و آنان را به باغ‌های دائمی بهشت داخل گردان که به ایشان وعده داده‌ای (و نیز) هرکس از پدران و همسران و فرزندانشان را که صالح و نیکوکار بودند (به همان بهشت داخل کن) بی‌گمان تو غالب باحکمت هستی.');
INSERT INTO "badkhashani" VALUES(40,9,'40:9','و آنها را از بدی‌ها نگه دار، و هرکه را در آن روز از (سزای) بدی‌ها نگه داری، واقعا به او رحم کرده‌ای و این است کامیابی بزرگ.');
INSERT INTO "badkhashani" VALUES(40,10,'40:10','البته کافران (در دوزخ) صدا زده می‌شوند (که) يقينا خشم الله (نسبت به شما) بزرگتر از خشم شما نسبت به خودتان است، چرا که به ایمان دعوت داده می‌شدید، ولی کفر می‌ورزیدید.');
INSERT INTO "badkhashani" VALUES(40,11,'40:11','(کفار) می‌گویند: ای پروردگار! ما را دوبار میراندی و دو بار زنده کردی، پس اکنون به گناهان خود اعتراف کردیم، پس آیا برای خارج شدن (از آتش) راهی هست؟');
INSERT INTO "badkhashani" VALUES(40,12,'40:12','(در جواب گفته شود: نه) این (عذاب) برای آنست که چون الله به تنهایی یاد می‌شد، (وحدانیت او را) انکار می‌کردید. و چون برای او شریکی قرار داده می‌شد، باور می‌کردید، پس (امروز) حکم و فیصله با پروردگار والا و بزرگ است.');
INSERT INTO "badkhashani" VALUES(40,13,'40:13','او ذاتی است که آیات خود را به شما نشان می‌دهد و از آسمان برای شما روزی نازل می‌کند، ولی پند و عبرت نمی‌گیرد مگر آن کسی که به درگاه او روی می‌آورد.');
INSERT INTO "badkhashani" VALUES(40,14,'40:14','پس الله را بخوانید در حالیکه عبادت و طاعت را برای او خالص کرده‌اید، هر چند کافران دوست نداشته باشند.');
INSERT INTO "badkhashani" VALUES(40,15,'40:15','الله دارای درجات رفيع و صاحب عرش است، او وحی را به حکم خود بر هرکس از بندگانش که بخواهد نازل می‌کند تا (بندگان را) از روز ملاقات بترساند.');
INSERT INTO "badkhashani" VALUES(40,16,'40:16','روزی که مردم ظاهر و آشکارند چیزی از آنان بر الله پنهان نمی‌ماند (ندا آید:) امروز فرمانروایی از چه کسی است؟ (پاسخ آید) از الله یکتای غالب تواناست.');
INSERT INTO "badkhashani" VALUES(40,17,'40:17','امروز هر کس در مقابل کاری که کرده است، پاداش داده می‌شود. امروز هیچ ظلمی نیست، بی‌گمان الله سريع الحساب است.');
INSERT INTO "badkhashani" VALUES(40,18,'40:18','و آنان را از روز نزدیک (قیامت) بترسان، وقتی که دلها به گلوه گاه می‌رسد، در حالیکه ساکت هستند و سخن نمی‌گویند. ظالمان هیچ دوست مخلص و دلسوز ندارند و هیچ سفارش کننده‌ای که سفارشش قابل پذیرش باشد نیست.');
INSERT INTO "badkhashani" VALUES(40,19,'40:19','(الله) خیانت چشمان و آنچه را که سینه‌ها پوشیده می‌دارند، می‌داند.');
INSERT INTO "badkhashani" VALUES(40,20,'40:20','و الله (خودش) به حق حکم می‌کند و کسانی را که به جای او می‌خوانند، به هیچ چیزی حکم کرده نمی‌توانند، يقينا الله شنوای بیناست.');
INSERT INTO "badkhashani" VALUES(40,21,'40:21','آیا در زمین سیر نکردند تا ببینند که سرانجام کسانی که پیش از ایشان بودند چگونه شد؟ آنان از ایشان قوی‌تر و آثارشان در زمین بیشتر بوده است و الله ایشان را به سبب گناهانشان گرفتار ساخت و آنان از عذاب الله هیچ پناه دهنده‌ای نداشتند.');
INSERT INTO "badkhashani" VALUES(40,22,'40:22','این (غضب الهی) به این خاطر بوده است که پیغمبران با دلایل واضح نزد آنها می‌آمدند و آنان کفر می‌ورزیدند پس در نتیجه الله آنان را گرفتار ساخت، يقينا او قوی و سخت عذاب دهنده است.');
INSERT INTO "badkhashani" VALUES(40,23,'40:23','و البته ما موسی را با معجزه‌های خود و دلیل روشن فرستادیم.');
INSERT INTO "badkhashani" VALUES(40,24,'40:24','به‌سوی فرعون و هامان و قارون، پس گفتند: او جادوگر دروغگوست.');
INSERT INTO "badkhashani" VALUES(40,25,'40:25','پس وقتی با (پیام) حق از سوی ما به نزد آنان آمد، گفتند: پسران آنان را که همراه با او ایمان آورده‌اند بکشید و زنانشان را زنده بگذارید، و (لیکن) حيلۀ كافران جز در گمراهی و تباهی نیست.');
INSERT INTO "badkhashani" VALUES(40,26,'40:26','و فرعون گفت: بگذارید موسی را بکشم و او پروردگار خود را به کمک طلبد، چون من می‌ترسم که دینتان را تغییر دهد، یا اینکه در این سرزمین فساد برپا کند.');
INSERT INTO "badkhashani" VALUES(40,27,'40:27','موسی گفت: البته من به پروردگار خود و پروردگار شما از (شر) هر متکبری که به روز حساب ایمان ندارد، پناه می‌برم.');
INSERT INTO "badkhashani" VALUES(40,28,'40:28','و مرد مؤمنی از خاندان فرعون که ایمان خود را پنهان می‌کرد گفت: آیا مردی را می‌کشید که می‌گوید: پروردگار من الله است. در حالیکه دلایل روشن و معجزات آشکاری از جانب پروردگارتان برایتان آورده است؟ اگر دروغگو باشد (گناه) دروغش بر (عهدۀ) اوست و اگر راستگو باشد پس بعضی از آنچه را که به شما وعده می‌دهد به شما می‌رسد، زیرا الله کسی را که اسرافکار و دروغگو باشد، هدایت نمی‌کند.');
INSERT INTO "badkhashani" VALUES(40,29,'40:29','(آن مرد مؤمن در ادامه سخن خود گفت:) ای قوم من امروز فرمانروایی در دست شماست و در این سرزمین غلبه دارید، اما اگر عذاب سخت الله بر ما بیاید، چه کسی ما را مدد خواهد کرد؟ فرعون گفت: به شما نشان نمی‌دهم جز آنچه را من در می‌یابم و شما را جز به راه رشد راهنمایی نمی‌کنم.');
INSERT INTO "badkhashani" VALUES(40,30,'40:30','و آن کس که ایمان آورده بود، گفت: ای قوم من! من از روزی مثل روز (هلاکت و سرنوشت بد) گروهها بر شما می‌ترسم.');
INSERT INTO "badkhashani" VALUES(40,31,'40:31','همچون سرنوشت قوم نوح و عاد و ثمود و کسانی که بعد از آنان آمدند. و الله بر بندگان ارادۀ ظلم نمی‌کند.');
INSERT INTO "badkhashani" VALUES(40,32,'40:32','(و نیز گفت) ای قوم من، البته من بر شما از روزی می‌ترسم که مردم یکدیگر را (برای نجات خود از عذاب) فریاد می‌کنند.');
INSERT INTO "badkhashani" VALUES(40,33,'40:33','آن روزی که پشت می‌گردانید و شما در برابر عذاب الله هیچ حمایتگری ندارید، و هر کس را که الله گمراه سازد دیگر او هیچ هدایتگری ندارد.');
INSERT INTO "badkhashani" VALUES(40,34,'40:34','و (آن مرد مؤمن نیز گفت:) البته یوسف پیش از این با دلایل روشن نزد شما آمد، ولی همیشه در مورد آنچه برایتان آورده بود در شک بودید تا اینکه از دنیا رفت (و) گفتید: الله بعد از او هرگز پیغمبری را نخواهد فرستاد. اینچنین الله اشخاص متجاوز و متردد را گمراه می‌سازد.');
INSERT INTO "badkhashani" VALUES(40,35,'40:35','آنان که دربارۀ آيات الله مجادله می‌کنند، بدون آنکه هیچ دلیلی برایشان آمده باشد، (مجادلۀ آنان) نزد الله و نزد مؤمنان خشم بزرگی را در پی دارد. اینچنین الله بر دل هر متكبر جبار مهر می‌نهد.');
INSERT INTO "badkhashani" VALUES(40,36,'40:36','فرعون گفت: ای هامان! بنای بلندی برای من بساز، شاید به راه‌ها دست یابم.');
INSERT INTO "badkhashani" VALUES(40,37,'40:37','راه‌های آسمان‌ها، تا از معبود موسی اطلاع یابم (و بنگرم)، چون من او را دروغگو می‌پندارم. اینچنین برای فرعون بدی کردارش زیبا جلوه داده شد و از راه (حق) باز داشته شد. و حيلۀ فرعون جز در تباهی نبود.');
INSERT INTO "badkhashani" VALUES(40,38,'40:38','و کسی که (از قوم فرعون به موسی) ایمان آورده بود گفت: ای قوم من! از من پیروی کنید تا شما را به راه درست هدایت کنم.');
INSERT INTO "badkhashani" VALUES(40,39,'40:39','ای قوم من! جز این نیست که زندگانی دنیا متاع ناچیزی است و البته آخرت سرای دائمی (و جاودانه) است.');
INSERT INTO "badkhashani" VALUES(40,40,'40:40','هرکس کار بدی انجام دهد جز به همان اندازه جزا داده نمی‌شود، ولی هرکس کار نیک انجام دهد، مرد باشد یا زن به شرط اینکه مؤمن باشد چنین کسانی وارد بهشت می‌شوند و در آنجا روزی بی‌حساب داده می‌شوند.');
INSERT INTO "badkhashani" VALUES(40,41,'40:41','و (نیز گفت:) ای قوم من! مرا چه شده است که شما را به‌سوی نجات دعوت می‌دهم و شما مرا به‌سوی آتش فرا می‌خوانید؟');
INSERT INTO "badkhashani" VALUES(40,42,'40:42','مرا فرا می‌خوانید تا به الله كافر شوم و چیزی را که من به (حقیقت) آن علم ندارم شریک او گردانم در حالیکه من شما را به سوى (الله) غالب آمرزگار فرا می‌خوانم.');
INSERT INTO "badkhashani" VALUES(40,43,'40:43','بدون شک آنچه مرا به‌سوی آن دعوت می‌کنید، در دنیا و در آخرت ارزش دعوت ما را ندارند، و به يقين بازگشت ما به‌سوی الله است و اسرافکاران، دوزخی و همدم آتش‌اند.');
INSERT INTO "badkhashani" VALUES(40,44,'40:44','به زودی آنچه را من به شما می‌گویم یاد خواهید کرد و من کار خود را به الله می‌سپارم، چون الله به (حال) بندگان بیناست.');
INSERT INTO "badkhashani" VALUES(40,45,'40:45','پس الله او را از سوء توطئه‌ها و حیله‌های ایشان مصئون و محفوظ داشت و عذاب سخت آل فرعون را فراگرفت.');
INSERT INTO "badkhashani" VALUES(40,46,'40:46','و آتش دوزخ است که هر صبح و شام بر آن عرضه می‌شوند و روزی که قیامت برپا شود (الله دستور می‌دهد) آل فرعون را در سخت‌ترین عذاب در آورید.');
INSERT INTO "badkhashani" VALUES(40,47,'40:47','و یادآور شو وقتی را که در آتش دوزخ با همدیگر به مجادله می‌پردازند و ناتوانان به مستکبران می‌گویند: البته ما پیرو شما بودیم، پس آیا می‌توانید بخشی از (عذاب) آتش (دوزخ) را از ما دفع کنید؟');
INSERT INTO "badkhashani" VALUES(40,48,'40:48','مستکبران (در جواب( می‌گویند: ما و شما همگی در آن (آتش) هستیم، یقینا که الله میان بندگان (به عدالت) حکم کرده است.');
INSERT INTO "badkhashani" VALUES(40,49,'40:49','و کسانی که در دوزخ‌اند به نگهبانان دوزخ می‌گویند: از پروردگارتان بخواهید که )فقط) یک روز عذاب را از ما بکاهد.');
INSERT INTO "badkhashani" VALUES(40,50,'40:50','(نگهبانان دوزخ) می‌گویند: آیا پیغمبران تان نبود که با معجزه‌های روشن نزدتان می‌آمدند؟ می‌گویند: آری. (نگهبانان دوزخ) می‌گویند: پس (خودتان) دعا کنید، ولی دعای کافران جز سردرگمی نتیجه‌ای ندارد.');
INSERT INTO "badkhashani" VALUES(40,51,'40:51','ما حتما پیغمبران خود و کسانی را که ایمان آورده‌اند، هم در زندگی دنیا و هم در روزی که گواهان )برای گواهی دادن) می‌ایستند، مدد می‌کنیم.');
INSERT INTO "badkhashani" VALUES(40,52,'40:52','(همان( روزی که عذرخواهی ظالمان سودشان ندهد و نفرین برای آنان خواهد بود و سرای بد جایگاه‌شان خواهد شد.');
INSERT INTO "badkhashani" VALUES(40,53,'40:53','و همانا ما به موسی هدایت عطا کردیم و بنی اسرائیل را وارث کتاب (تورات) گردانیدیم.');
INSERT INTO "badkhashani" VALUES(40,54,'40:54','(کتابی که( راهنما و اندرز گوی صاحبان عقل است.');
INSERT INTO "badkhashani" VALUES(40,55,'40:55','پس صبر کن، چون وعدۀ الله حق است. و برای گناهت آمرزش بخواه و شام و صبح با سپاس و ستایش پروردگارت را به پاکی یاد کن.');
INSERT INTO "badkhashani" VALUES(40,56,'40:56','البته کسانی که در آیات الله بدون دلیلی که (از سوی الله) برای‌شان آمده باشد به مجادله می‌پردازند، در سینه‌هایشان جز برتری جویی نیست که هرگز به آن نخواهند رسید، پس به الله پناه ببر، که بیشک او شنوای بیناست.');
INSERT INTO "badkhashani" VALUES(40,57,'40:57','البته آفرینش آسمان‌ها و زمین بزرگ‌تر از آفرینش مردم است، ولی بسیاری از مردم نمی‌دانند.');
INSERT INTO "badkhashani" VALUES(40,58,'40:58','و نابینا و بینا برابر نیستند و کسانی که ایمان آورده‌اند و کارهای نیک کرده‌اند، با کسانی که بدکارند، برابر نیستند. اما بسیار کم پند می‌گیرید.');
INSERT INTO "badkhashani" VALUES(40,59,'40:59','البته قیامت آمدنی است؛ شکی در آن نیست، ولی بیشتر مردم ایمان نمی‌آورند.');
INSERT INTO "badkhashani" VALUES(40,60,'40:60','و پروردگارتان می‌فرماید: مرا به دعا بخوانید تا (دعای) شما را اجابت کنم، بی‌گمان آنانکه از عبادت من تكبر می‌ورزند، به زودی با خواری و ذلت وارد دوزخ می‌شوند.');
INSERT INTO "badkhashani" VALUES(40,61,'40:61','الله آن ذاتی است که شب را برایتان آفرید تا در آن آرام گیرید و روز را روشنی بخش گرداند، البته الله نسبت به مردم لطف و مرحمت دارد، ولی بیشتر مردم شکر نمی‌گزارند.');
INSERT INTO "badkhashani" VALUES(40,62,'40:62','این است الله پروردگارتان آفریننده هر چیزی، معبودی به حق جز او نیست، پس چگونه )از راه حق( برگردانیده می‌شوید؟');
INSERT INTO "badkhashani" VALUES(40,63,'40:63','این چنین کسانی که آيات الله را انکار می‌کردند، به بیراهه گردانیده می‌شوند.');
INSERT INTO "badkhashani" VALUES(40,64,'40:64','الله ذاتی است که زمین را محل استقرار شما گردانید و آسمان را سقف ساخت و شما را شکل و صورت بخشید و شکل‌هایتان را زیبا و نیکو نمود و از پاکیزه‌ها به شما روزی داد. این است الله پروردگارتان، پس بابرکت و خجسته است ذاتی که پروردگار جهانیان است.');
INSERT INTO "badkhashani" VALUES(40,65,'40:65','اوست زندۀ ابدی که هیچ معبودی به حق جز او نیست، پس او را بخوانید در حالیکه دین را برای او خالص نموده‌اید، سپاس و ستایش برای پروردگاری است که پروردگار جهانیان می‌باشد.');
INSERT INTO "badkhashani" VALUES(40,66,'40:66','بگو: (ای پیغمبر) من منع شده‌ام از اینکه کسانی را بپرستم که شما آنها را غیر از الله عبادت می‌کنید، )آن هم( از زمانی که آیات روشن و دلایل اشکار از جانب پروردگار برایم آمده است. و به من فرمان داده شده است که تسلیم و منقاد پروردگار جهانیان باشم.');
INSERT INTO "badkhashani" VALUES(40,67,'40:67','الله ذاتی است که شما را از خاک باز از نطفه باز از خون بسته آفرید، باز شما را به شکل طفلی (از رحم‌ها) بیرون آورد. باز (شما را پرورانید) تا به کمال قوت خود برسید، باز تا پیر می‌شوید. و بعضی از شما پیش از رسیدن به مرحلۀ پیری می‌میرد و (بعضی از شما زنده می‌ماند) تا به مدتی که معین است برسید. و باشد که بفهمید.');
INSERT INTO "badkhashani" VALUES(40,68,'40:68','او ذاتی است که زندگی می‌بخشد و می‌میراند و هرگاه خواست کاری انجام پذیرد، تنها به آن می‌گوید: باش، پس بی‌درنگ موجود می‌شود.');
INSERT INTO "badkhashani" VALUES(40,69,'40:69','آیا ندیدی کسانی را که دربارۀ آيات الله مجادله و ستیز می‌کنند، چگونه (از حق) باز گردانیده می‌شوند؟');
INSERT INTO "badkhashani" VALUES(40,70,'40:70','آنان که کتاب (آسمانی) و آنچه را که پیغمبران ما را به آن فرستادیم تکذیب کردند، پس به زودی (حقیقت را) خواهند دانست.');
INSERT INTO "badkhashani" VALUES(40,71,'40:71','آن وقت که طوقها و زنجیرها در گردن‌هایشان باشد (و به طرف دوزخ) کشیده شوند.');
INSERT INTO "badkhashani" VALUES(40,72,'40:72','در آب جوشان و باز در آتش سوزانده می‌شوند.');
INSERT INTO "badkhashani" VALUES(40,73,'40:73','باز به ایشان گفته می‌شود: آن چه برای الله شریک قرار می‌دادید، کجا هستند؟');
INSERT INTO "badkhashani" VALUES(40,74,'40:74','(همان شریکانی که) غیر از الله پرستش (آنها را) می‌کردید؟ می‌گویند: از نزد ما گم شده‌اند، بلکه ما پیش از این چیزی را (غير از الله) نمی‌خواندیم، الله این طور کافران را گمراه می‌کند.');
INSERT INTO "badkhashani" VALUES(40,75,'40:75','این (عذاب) به سبب آن است که در زمین به ناحق شادی می‌کردید و به سبب آن است که به (کفر و سرکشی) خود می‌نازیدید.');
INSERT INTO "badkhashani" VALUES(40,76,'40:76','(اکنون) از درهای دوزخ وارد شوید که در آن جا جاویدان خواهید بود، پس چه بد است جایگاه متکبران.');
INSERT INTO "badkhashani" VALUES(40,77,'40:77','پس صبر کن. چون وعدۀ الله حق است، پس اگر بخشی از آنچه را که به آنها وعده می‌دهیم، به تو نشان دهیم، یا قبل از آن تو را بمیرانیم (در هر دو صورت) به‌سوی ما باز گردانیده می‌شوند.');
INSERT INTO "badkhashani" VALUES(40,78,'40:78','و البته پیش از تو پیغمبرانی را فرستادیم. سرگذشت بعضی از آنها را برای تو بیان کردیم و سرگذشت بعضی دیگر را برای تو بازگو نکردیم. و هیچ پیغمبری نمی‌تواند که معجزه‌ای جز به فرمان الله بیاورد، پس چون فرمان الله آید، به حق حکم و فیصله خواهد شد و باطل پرستان در آنجا زیانکار‌اند.');
INSERT INTO "badkhashani" VALUES(40,79,'40:79','الله همان ذاتی است که چهارپایان را برای شما قرار داد تا بر (بعضی) از آنها سوار شوید و از (بعضی) آنها می‌خورید.');
INSERT INTO "badkhashani" VALUES(40,80,'40:80','و برای شما در آنها منافع (دیگری هم) است. و تا با سوار شدن بر آنها به نیازی که در دل دارید برسید و بر آنها و بر کشتی‌ها حمل می‌شوید.');
INSERT INTO "badkhashani" VALUES(40,81,'40:81','و (الله) علامات قدرت خود را به شما نشان می‌دهد، پس کدام یک از علامات قدرت الله را انکار می‌کنید؟');
INSERT INTO "badkhashani" VALUES(40,82,'40:82','پس آیا در زمین سیر نکردند تا بنگرند عاقبت کسانی که پیش از آنها بوده‌اند چه شد؟ آنان نسبت به ایشان نیرومندتر و دارای آثار بیشتری در زمین بوده‌اند، پس آنچه می‌کردند (عذاب الله) را از آنان دفع نکرد.');
INSERT INTO "badkhashani" VALUES(40,83,'40:83','پس چون پیغمبرانشان با معجزه‌ها به‌سوی آنان آمدند، به آن مقدار علمی که داشتند خورسند و شادمان شدند (و حاضر نشدند سخنان انبیاء را بپذیرند) و آنچه را که به تمسخر می‌گرفتند، آنان را فراگرفت.');
INSERT INTO "badkhashani" VALUES(40,84,'40:84','پس وقتی عذاب ما را دیدند گفتند: تنها به الله ایمان آوردیم و به آنچه (قبلا) شرک می‌آوردیم، کافر شدیم.');
INSERT INTO "badkhashani" VALUES(40,85,'40:85','اما پس از آنکه عذاب ما را دیدند، دیگر ایمان‌شان برایشان سودی نرساند. این سنت همیشگی الله در مورد بندگانش جاری است. و كافران آنجا زیان می‌بینند.');
INSERT INTO "badkhashani" VALUES(41,1,'41:1','حم. (مفهوم این حروف به الله معلوم است).');
INSERT INTO "badkhashani" VALUES(41,2,'41:2','فرود آوردن این کتاب از جانب الله بخشندۀ مهربان است.');
INSERT INTO "badkhashani" VALUES(41,3,'41:3','کتابی که آیاتش واضح و روشن بیان شده است، قرآن عربی است، برای قومی که می‌دانند.');
INSERT INTO "badkhashani" VALUES(41,4,'41:4','در حالیکه (کتاب) مژده دهنده و بیم دهنده است، (لیکن) بیشترشان روی گردانند و آنان (ندای آن را) نمی‌شنوند.');
INSERT INTO "badkhashani" VALUES(41,5,'41:5','و گفتند: دل‌های ما از آنچه ما را به‌سوی آن دعوت می‌دهی در پرده است و در گوش‌های ما سنگینی است. و میان ما و تو حجاب است، پس تو عمل کن (و) البته ما نیز عمل می‌کنیم.');
INSERT INTO "badkhashani" VALUES(41,6,'41:6','بگو: (ای پیغمبر) جز این نیست که من بشری هستم مانند شما، به من وحی می‌شود که البته معبودتان معبود یگانه است، پس راست (و بدون انحراف) به‌سوی او روی بیاورید و از او آمرزش بخواهید، و وای به حال مشركان.');
INSERT INTO "badkhashani" VALUES(41,7,'41:7','آنان که زکات نمی‌دهند و آنها همان کسانی هستند که به آخرت کافر‌اند.');
INSERT INTO "badkhashani" VALUES(41,8,'41:8','البته آنان که ایمان آوردند و کارهای نیک انجام دادند، برای آنها ثواب بی‌پایان (و بی‌منت) است.');
INSERT INTO "badkhashani" VALUES(41,9,'41:9','بگو: آیا شما به کسی کفر می‌ورزید که زمین را در دو روز آفریده است و برای او همتایان قرار می‌دهید؟ حال آنکه او پروردگار جهانیان است.');
INSERT INTO "badkhashani" VALUES(41,10,'41:10','و در زمین کوههای استوار را قرار داد و در آن خیر و برکت نهاد و روزی (اهل) آن را در چهار روز به اندازۀ معین مقدر کرد، (بنابراین) برای سؤال کنندگان (واضح و) روشن گردید.');
INSERT INTO "badkhashani" VALUES(41,11,'41:11','باز قصد آفرینش آسمان را کرد، در حالیکه دود بود و به آسمان و زمین فرمود: با رغبت یا کراهت (شکل گرفته) بیایید. گفتند: فرمانبردار آمدیم.');
INSERT INTO "badkhashani" VALUES(41,12,'41:12','پس در دو روز آنها را هفت آسمان ساخت، و در هر آسمانی امرش را وحی کرد. و ما آسمان دنیا را با چراغهایی (ستارگان) زینت دادیم، و (آن را از دخالت شیاطین) محافظت نمودیم، این است تقدير ذات غالب دانا.');
INSERT INTO "badkhashani" VALUES(41,13,'41:13','پس اگر روی گردان شدند، بگو: همانا شما را از صاعقه‌ای مانند صاعقۀ عاد و ثمود می‌ترسانم.');
INSERT INTO "badkhashani" VALUES(41,14,'41:14','وقتی پیغمبران از پیش رو و از پشت سر به نزدشان آمدند (و گفتند:) جز الله را عبادت نکنید، گفتند: اگر پروردگارما می‌خواست (پیغمبری بفرستد) فرشتگان را می‌فرستاد، پس ما به آن چه شما به (تبلیغ) آن فرستاده شده‌اید، کافریم.');
INSERT INTO "badkhashani" VALUES(41,15,'41:15','پس عاد در زمین به ناحق تكبر ورزیدند و گفتند: کیست نیرومندتر از ما؟ آیا ندیده‌اند که الله همان ذاتی که آنان را آفریده است، توانمندتر از آنهاست؟ آنان آیات ما را انکار می‌کردند.');
INSERT INTO "badkhashani" VALUES(41,16,'41:16','پس در روزهای نحس و بی‌برکت، باد سرد و سختی را بر آنان فرستادیم تا عذاب رسوا کننده را در زندگی این دنیا به ایشان بچشانیم، و البته عذاب آخرت رسواکننده‌تر است، در حالیکه آنان مدد نمی‌شوند.');
INSERT INTO "badkhashani" VALUES(41,17,'41:17','و اما قوم ثمود، ما ایشان را هدایت کردیم لیکن کوری را بر هدایت ترجیح دادند. بنابراین، به سبب کارهایی که می‌کردند، صاعقۀ عذاب رسواکننده آنان را گرفت.');
INSERT INTO "badkhashani" VALUES(41,18,'41:18','و ما آنانی را که ایمان آوردند و پرهیزگار بودند، نجات دادیم.');
INSERT INTO "badkhashani" VALUES(41,19,'41:19','و روزی که دشمنان الله به‌سوی دوزخ جمع گشته و همه (توقف داده می‌شوند تا همه) يكجا شوند.');
INSERT INTO "badkhashani" VALUES(41,20,'41:20','تا چون به آن (دوزخ) بیایند، گوش و چشم‌ها و پوستهایشان به آنچه می‌کردند، گواهی می‌دهد.');
INSERT INTO "badkhashani" VALUES(41,21,'41:21','و آنها به پوست‌های خود می‌گویند: چرا علیه ما گواهی دادید؟ گویند: الله همان ذاتی که همه چیز را به نطق آورده ما را نیز به نطق آورده است. و او بار اول شما را آفریده است و به‌سوی او باز گردانیده می‌شوید.');
INSERT INTO "badkhashani" VALUES(41,22,'41:22','و نمی‌توانستید چیزی را پنهان کنید که (مبادا) گوش و چشم و پوستهایتان علیه شما گواهی دهند، بلکه گمان کردید که الله بسیاری از آنچه را که می‌کنید نمی‌داند.');
INSERT INTO "badkhashani" VALUES(41,23,'41:23','این گمان (بد) شما دربارۀ پروردگارتان بود که شما را هلاک کرد پس در نتیجه از زیانکاران شدید.');
INSERT INTO "badkhashani" VALUES(41,24,'41:24','پس اگر صبر کنند (چه فایده که) آتش دوزخ جایگاهشان است و اگر طلب عفو کنند پس از بخشودگان نیستند.');
INSERT INTO "badkhashani" VALUES(41,25,'41:25','و برای آنان همنشینانی گماشتیم که از پیش و پشت سرشان (کارهای ناشایست‌شان) را در نظرشان زیبا جلوه دادند، و فرمان عذاب الهی که قبل از آنان دربارۀ اقوام (گمراه) از جن و انس ثابت و صادر شده بود، بر آنان نیز ثابت شد. البته آنها زیانکار بودند.');
INSERT INTO "badkhashani" VALUES(41,26,'41:26','و کافران گفتند: به این قرآن گوش ندهید و در (هنگام تلاوت آن) سخن لغو و بیهوده گویید تا شاید غالب شوید.');
INSERT INTO "badkhashani" VALUES(41,27,'41:27','پس حتما به کافران عذاب سخت می‌چشانیم و حتما آنان را در برابر بدترین کاری که کرده‌اند، سزا خواهیم داد.');
INSERT INTO "badkhashani" VALUES(41,28,'41:28','این است سزای دشمنان الله که همان آتش است، که در آن سرای همیشگی دارند. این سزا به آن سبب است که آیات ما را انکار می‌کردند.');
INSERT INTO "badkhashani" VALUES(41,29,'41:29','و کافران گویند: ای پروردگار ما! آن دو تن از جن و انس را به ما نشان بده که ما را گمراه ساختند تا ایشان را زیر پاهای خود بگذاریم تا از پست‌ترین اشخاص شوند.');
INSERT INTO "badkhashani" VALUES(41,30,'41:30','بی‌گمان کسانی که گفتند: پروردگار ما الله است. باز (بر این عقیده) استقامت نمودند، فرشتگان بر آنان فرود می‌آیند (و به آنان مژده می‌دهند) که نترسید و غمگین نباشید و بشارت باد بر شما به بهشتی که وعده داده می‌شدید.');
INSERT INTO "badkhashani" VALUES(41,31,'41:31','ما در زندگانی دنیا و آخرت دوستان شما هستیم و (اکنون هم) در آخرت برای شما هر چه آرزو کنید و هر چه بخواهید هست.');
INSERT INTO "badkhashani" VALUES(41,32,'41:32','(اینها همه) پذیرایی از سوى الله آمرزگار مهربان است.');
INSERT INTO "badkhashani" VALUES(41,33,'41:33','و کیست خوش سخن‌تر از کسی که به‌سوی الله دعوت کند و کارهای نیک کند و گوید: من از جملۀ مسلمانان هستم؟');
INSERT INTO "badkhashani" VALUES(41,34,'41:34','و نیکی و بدی برابر نیست، (بدی دیگران را) به شیوه‌ای که بهتر است دفع کن، که (اگر چنین کنی) ناگاه کسی که میان تو و او دشمنی است، چون دوست گرم و صمیمی گردد.');
INSERT INTO "badkhashani" VALUES(41,35,'41:35','و جز صابران (این خصلت نیکو) را دریافت نمی‌کنند و این را جز کسانی که بهرۀ بزرگی (از اخلاق و ایمان) داشته باشند نخواهد یافت.');
INSERT INTO "badkhashani" VALUES(41,36,'41:36','و هرگاه وسوسه‌ای از شیطان به تو رسد، پس به الله پناه ببر، چون او شنوای داناست.');
INSERT INTO "badkhashani" VALUES(41,37,'41:37','از علامات قدرت او شب و روز و آفتاب و ماه است، پس برای آفتاب و ماه سجده نکنید، بلکه برای پروردگاری که آنها را آفریده است سجده کنید، اگر واقعا تنها او را عبادت می‌کنید.');
INSERT INTO "badkhashani" VALUES(41,38,'41:38','پس اگر (کافران) تكبر ورزیدند (باکی نیست زیرا) آنانکه نزد پروردگارت هستند شب و روز او را به پاکی یاد می‌کنند و آنان خسته نمی‌شوند.');
INSERT INTO "badkhashani" VALUES(41,39,'41:39','و از علامات قدرت الله این است که تو زمین را پژمرده و بی‌جان می‌بینی، اما چون آب را بر آن فرود آوریم به حرکت آید و سبز شود و نمو کند. البته ذاتی که این زمین را زنده می‌گرداند همان ذات زنده کنندۀ مردگان است بی‌گمان او بر هر چیزی قادر است.');
INSERT INTO "badkhashani" VALUES(41,40,'41:40','البته کسانی که در آیات ما به انحراف و باطل می‌روند بر ما پوشیده نیستند. آیا کسیکه در آتش انداخته می‌شود بهتر است یا کسی که در آرامش و آسایش در روز قیامت (به سوی حشر و حساب) می‌آید؟ شما هرچه می‌خواهید انجام دهید یقینا او به آنچه می‌کنید بیناست.');
INSERT INTO "badkhashani" VALUES(41,41,'41:41','يقينا آنانی که قرآن را چون به آنها رسید انکار کردند (به سبب كفرشان مجازات خواهند شد) در حالی که این کتاب ارجمند است.');
INSERT INTO "badkhashani" VALUES(41,42,'41:42','باطل از هیچ جهتی، نه از پیشرو و نه از پشت سر، در آن راه نمی‌یابد زیرا از (سوی) ذات باحکمت و ستوده فرستاده شده است.');
INSERT INTO "badkhashani" VALUES(41,43,'41:43','به تو گفته نمی‌شود جز آنچه به پیغمبران پیش از تو نیز گفته شده است، یقینا پروردگار تو صاحب آمرزش بزرگ و عذاب دردناک است.');
INSERT INTO "badkhashani" VALUES(41,44,'41:44','و اگر آن را قرآن عجمی قرار می‌دادیم حتما می‌گفتند: چرا آیت‌هایش به تفصيل (و روشن) بیان نشده؟ آیا (قرآن) به زبان غیر عربی (نازل می‌شود) و (پیغمبر مرد) عربی (باشد)؟ بگو: قرآن برای مؤمنان سبب هدایت و شفا است و کسانی که ایمان نمی‌آورند در گوش‌هایشان گرانی است و آن (قرآن) بر آنان سبب کوری است. (گویی) ایشان از دور ندا می‌شوند (ولی صدا را نمی‌شنوند).');
INSERT INTO "badkhashani" VALUES(41,45,'41:45','و البته به موسی کتاب (آسمانی) دادیم پس در آن اختلاف شد، و اگر سخن پروردگارت از پیش بر این صادر نشده بود، حتما در میان ایشان فیصله می‌گردید. و آنان در بارۀ آن (قرآن) در شک قوی و سوء ظن هستند.');
INSERT INTO "badkhashani" VALUES(41,46,'41:46','هرکس کار نیک انجام دهد به سود خود اوست و هرکس کار بدی کند به زیان خود می‌کند و پروردگار تو به بندگان ظالم نیست.');
INSERT INTO "badkhashani" VALUES(41,47,'41:47','علم به زمان وقوع قیامت به الله باز می‌گردد. و هیچ میوه‌ای از غلاف خود بیرون نمی‌آید و هیچ ماده‌ای باردار نمی‌شود و وضع حمل نمی‌کند مگر با علم و اطلاع او، و روزی که الله آنان را ندا کند: شرکایی که با من (شریک می‌پنداشتید) کجایند؟ گویند: به تو خبر دادیم (ای الله) که از ما کسی گواه شریک بودن آنها نیست.');
INSERT INTO "badkhashani" VALUES(41,48,'41:48','و آنچه را که از پیش می‌خواندند از نزد آنها ناپدید می‌شود و یقین پیدا می‌کنند که هیچ راه فرار ندارند.');
INSERT INTO "badkhashani" VALUES(41,49,'41:49','انسان از طلب خير خسته نمی‌شود. و اگر شری به او برسد مأیوس و ناامید می‌گردد.');
INSERT INTO "badkhashani" VALUES(41,50,'41:50','و اگر پس از سختی که به انسان رسیده است از طرف خود رحمتی به او بچشانیم، حتما خواهد گفت: این حق من و لايق من است (و چنان مغرور می‌شود که می‌گوید) گمان نمی‌کنم قیامت برپا شود و اگر به‌سوی پروردگارم باز گردانیده شوم، حتما در پیشگاه او دارای مقام و منزلت خوبی هستم. پس بی‌گمان کافران را به آنچه کرده‌اند خبر خواهیم داد و حتما عذاب بسیار سخت به آنها می‌چشانیم.');
INSERT INTO "badkhashani" VALUES(41,51,'41:51','و هرگاه به انسان نعمت دهیم روی گردان می‌شود و تکبر می‌ورزد و هرگاه رنج و بلایی به او برسد، بسیار دعای طولانی می‌کند.');
INSERT INTO "badkhashani" VALUES(41,52,'41:52','(ای پیغمبر) بگو: به من خبر دهید اگر این (قرآن) از سوى الله آمده باشد باز (شما) به آن کفر ورزید، کیست گمراه‌تر از آن که در مخالفت شدید با حق باشد؟');
INSERT INTO "badkhashani" VALUES(41,53,'41:53','به زودی نشانه‌های قدرت خود را در اطراف عالم و (هم) در نفس خودشان به آنها نشان خواهیم داد تا برای ایشان روشن و ثابت شود که يقينا او حق است. آیا کافی نیست که پروردگارت بر هر چیزی گواه باشد؟');
INSERT INTO "badkhashani" VALUES(41,54,'41:54','آگاه باش که آنان از ملاقات پروردگارشان (در قیامت) در شک عمیق اند! آگاه باش که او (الله) به همه چیز احاطه دارد.');
INSERT INTO "badkhashani" VALUES(42,1,'42:1','(حم).');
INSERT INTO "badkhashani" VALUES(42,2,'42:2','عين. سين. قاف. (مفهوم این حروف به الله معلوم است).');
INSERT INTO "badkhashani" VALUES(42,3,'42:3','این چنین (الله) به تو و به کسانی که پیش از تو بودند وحی می‌کند. الله غالب باحکمت است.');
INSERT INTO "badkhashani" VALUES(42,4,'42:4','آنچه در آسمان‌ها و آنچه در زمین است از اوست و او بلند مرتبه و بزرگ است. ­');
INSERT INTO "badkhashani" VALUES(42,5,'42:5','نزدیک است که آسمان‌ها (از عظمت وحی) از بالایشان بشکافند، در حالیکه فرشته‌ها با ستایش پروردگارشان را تسبیح می‌گویند و برای کسانی که در زمین هستند آمرزش می‌خواهند. آگاه باش که الله آمرزگار مهربان است.');
INSERT INTO "badkhashani" VALUES(42,6,'42:6','و آنان که جز الله برای خود دوستان و مددگاران گرفته‌اند، الله مراقب ایشان است و تو بر آنان وکیل و مسؤل نیستی (تا آنان را بر ایمان آوردن وادار کنی).');
INSERT INTO "badkhashani" VALUES(42,7,'42:7','و این چنین قرآن عربی به تو وحی کردیم تا مردم ام القرى (مكه مكرمه) و کسانی را که در اطراف آن هستند بترسانی و (آنان را) از روز جمع شدن (روز قیامت) که شکی در آن نیست بیم دهی. گروهی در بهشت و گروهی در دوزخ خواهند بود.');
INSERT INTO "badkhashani" VALUES(42,8,'42:8','و اگر الله می‌خواست آنان را یک امت می‌ساخت، ولی الله هرکه را بخواهد به رحمت خود وارد می‌کند و ظالمان هیچ دوست و مددگاری ندارند.');
INSERT INTO "badkhashani" VALUES(42,9,'42:9','آیا بجای الله دوستان دیگر گرفته‌اند؟ حال آنکه تنها الله کارساز است و اوست که مردگان را زنده می‌کند و اوست که بر همه چیز قادر است.');
INSERT INTO "badkhashani" VALUES(42,10,'42:10','و در هرچیزی که اختلاف کنید، حکم و فیصلۀ آن به الله بر می‌گردد. این است الله، پروردگار من، که بر او توکل کرده‌ام و به‌سوی او باز می‌گردم.');
INSERT INTO "badkhashani" VALUES(42,11,'42:11','آفرینندۀ آسمان‌ها و زمین است، از خودتان برای شما همسرانی قرار داده و چهارپایان را نیز جفت‌ها (نر و ماده) قرار داد و به این تدبیر شما را زیاد می‌گرداند. هیچ چیزی مانند او نیست و او شنوای بیناست.');
INSERT INTO "badkhashani" VALUES(42,12,'42:12','کلیدهای آسمان و زمین در تصرف اوست، روزی را برای هرکس که بخواهد می‌گشاید و (برای هرکس که بخواهد) تنگ می‌کند، يقينا او به هر چیز داناست.');
INSERT INTO "badkhashani" VALUES(42,13,'42:13','از (احکام) دین آنچه را به نوح فرمان داده بود و آنچه را که به تو وحی کرده‌ایم و آنچه را به ابراهیم و موسی و عیسی فرمان داده بودیم، برای شما نیز مقرر نمود که دین را بر پا دارید و در آن اختلاف نورزید. چیزی که مشرکان را به‌سوی آن دعوت می‌دهی، بر آنان سنگین است. الله هرکس را بخواهد به‌سوی خود انتخاب می‌کند و هر کس را که به درگاه او رو آورد به‌سوی خویش هدایت می‌کند.');
INSERT INTO "badkhashani" VALUES(42,14,'42:14','و پراگنده و متفرق نشدند مگر بعد از آنکه علم و آگاهی برایشان حاصل شد. و این تفرقه به سبب کینه و بغاوت در میان خودشان بود، و اگر سخن سابق از سوی پروردگارت صادر نشده بود که تا زمانی معین (مهلت) یابند، به يقين در میان آنان فيصله می‌شد و البته کسانیکه پس از آنان کتاب به آنها میراث داده شد در حقانیت آن دچار شک و تردید هستند.');
INSERT INTO "badkhashani" VALUES(42,15,'42:15','پس از این جهت (مردم را به‌سوی دین حق) دعوت بده و طوریکه به تو حکم شده پایداری کن، و از خواسته‌های آنان پیروی مکن. (و به آنان) بگو: به هر کتابی که از سوی الله نازل شده است ایمان آورده‌ام و به من دستور داده شده که در میان شما به عدالت حکم کنم، الله پروردگار ما و پروردگار شماست. اعمال ما برای ماست و اعمال شما برای شماست، هیچ حجتی میان ما و شما (ناگفته) نمانده است، الله میان ما جمع خواهد کرد و بازگشت همه به‌سوی اوست.');
INSERT INTO "badkhashani" VALUES(42,16,'42:16','و آنان که در بارۀ الله به مجادله و مخاصمه می‌پردازند، آن هم پس از آنکه (دین او) قبول کرده شده است، مجادلۀ آنان در نزد پروردگارشان باطل است و غضب (الله) بر آنان است و برای‌شان عذاب سخت است.');
INSERT INTO "badkhashani" VALUES(42,17,'42:17','الله آن ذاتی است که کتاب و میزان را به حق نازل کرده است. و تو چه میدانی شاید قیامت نزدیک باشد.');
INSERT INTO "badkhashani" VALUES(42,18,'42:18','کسانی که به قیامت ایمان ندارند، در رسیدن آن شتاب می‌ورزند، اما مؤمنان به سبب آن در خوف و هراسند و می‌دانند که قیامت حق است. بدانید آنانی که در بارۀ قیامت شک داشته و به مجادله می‌پردازند، در گمراهی دور از حق قرار دارند.');
INSERT INTO "badkhashani" VALUES(42,19,'42:19','الله نسبت به بندگانش مهربان و مشفق است، به هر کس که بخواهد روزی می‌دهد و او نیرومند غالب است.');
INSERT INTO "badkhashani" VALUES(42,20,'42:20','کسی که کشت آخرت را بخواهد در کشت او می‌افزاییم، کسی که کشت دنیا را بخواهد از آن به او می‌دهیم. و او در آخرت هیچ بهره‌ای ندارد.');
INSERT INTO "badkhashani" VALUES(42,21,'42:21','آیا (کافران) شریکانی دارند که برای ایشان دینی مقرر کرده است که الله به آن اجازه نداده است؟ و اگر سخن قطعی (دربارۀ مهلت دادن کافران) نبود، حتما بین آنان فيصله می‌شد، و بی‌گمان ظالمان برایشان عذاب دردناک است.');
INSERT INTO "badkhashani" VALUES(42,22,'42:22','ظالمان را در (قیامت) خواهی دید که از آنچه انجام داده‌اند، سخت بیمناک‌اند، در حالیکه آن عذاب حتما برایشان واقع شدنی است، و آنانکه ایمان آوردند و کارهای نیک انجام دادند، در باغ‌های بهشت هستند، برای آنان نزد پروردگارشان هر چه بخواهند میسر است، این است آن فضل بزرگ.');
INSERT INTO "badkhashani" VALUES(42,23,'42:23','این (ثواب بزرگ) همان چیزی است که الله به آن بندگان مؤمن و نیکوکار خود را مژده می‌دهد. بگو: من بر این از شما مزدی نمی‌خواهم، مگر دوستی به خاطر خویشاوندی. و هر کس که نیکی کند برای او در نیکی آن می‌افزاییم. یقینا الله آمرزندۀ قدردان است.');
INSERT INTO "badkhashani" VALUES(42,24,'42:24','آیا می‌گویند: (پیغمبر) بر الله دروغ بسته است؟، اگر الله بخواهد بر دل تو مهره می‌نهد، و الله با سخنانش باطل را محو می‌سازد و حق را محکم و پابرجا می‌کند. همانا او به راز سینه‌ها آگاه است.');
INSERT INTO "badkhashani" VALUES(42,25,'42:25','و اوست ذاتی که توبه را از بندگانش می‌پذیرد و از گناهان در می‌گذرد و آنچه را انجام می‌دهید، می‌داند.');
INSERT INTO "badkhashani" VALUES(42,26,'42:26','و (دعای) کسانی را که ایمان آورده و کارهای نیک انجام داده‌اند اجابت می‌کند، و از فضل خود به آنان می‌افزاید و برای کافران عذاب سخت است.');
INSERT INTO "badkhashani" VALUES(42,27,'42:27','و اگر الله روزی را برای بندگانش فراخ کند، حتما در زمین طغیان می‌کردند و ليكن به اندازۀ آنچه بخواهد نازل می‌کند، چرا که او به (احوال) بندگانش دانا (و) بیناست.');
INSERT INTO "badkhashani" VALUES(42,28,'42:28','و اوست که پس از ناامیدی مردم باران را نازل می‌کند و رحمت خود را می‌گستراند و اوست کارساز ستوده.');
INSERT INTO "badkhashani" VALUES(42,29,'42:29','و از نشانه‌های (قدرتش) آفرینش آسمان‌ها و زمین، و (نیز) جاندارانی است که در آن دو پراگنده کرده است. و او بر جمع نمودن آنها هنگامیکه بخواهد قادر است.');
INSERT INTO "badkhashani" VALUES(42,30,'42:30','و آنچه از مصیبت به شما رسد، به خاطر دست آورد خودتان است. و او از بسیاری (گناهان‌تان) در می‌گذرد.');
INSERT INTO "badkhashani" VALUES(42,31,'42:31','و شما هرگز نمی‌توانید (الله را از دستیابی به خود) در زمین عاجز سازید، و برای شما غیر از الله هیچ کارساز و مددگاری نیست.');
INSERT INTO "badkhashani" VALUES(42,32,'42:32','و از نشانه‌های (قدرت) او کشتی‌هایی است که مانند کوههای برجسته در بحر روانند.');
INSERT INTO "badkhashani" VALUES(42,33,'42:33','اگر بخواهد باد را ساکن می‌سازد و کشتی‌ها بر روی بحر متوقف می‌گردند، البته در این امر برای هر صبر کنندۀ شکر گذار نشانه‌هاست.');
INSERT INTO "badkhashani" VALUES(42,34,'42:34','یا اگر بخواهد کشتی‌ها را به خاطر آنچه (سرنشینان) آنها انجام داده‌اند، هلاک می‌سازد. و از بسیاری درمی‌گذرد.');
INSERT INTO "badkhashani" VALUES(42,35,'42:35','و تا آنانی که در آیات ما مجادله می‌کنند بدانند که برایشان راه گریز نیست.');
INSERT INTO "badkhashani" VALUES(42,36,'42:36','هر چه که به شما داده شده است بهرۀ چند روزۀ زندگانی دنیاست. و آنچه که نزد الله است بهتر و همیشگی است برای کسانی که ایمان آورده‌اند و بر پروردگار‌شان توکل می‌کنند.');
INSERT INTO "badkhashani" VALUES(42,37,'42:37','و (نیز) برای آنان که از گناهان بزرگ و اعمال زشت پرهیز می‌کنند. و چون خشم و غضب کنند، آنها می‌بخشند.');
INSERT INTO "badkhashani" VALUES(42,38,'42:38','و (نیز) آنانکه (دعوت) پروردگارشان را پذیرفتند و نماز را برپا داشتند و کارشان با یکدیگر به شیوۀ مشورت است. و از آنچه به ایشان روزی داده‌ایم، انفاق می‌کنند.');
INSERT INTO "badkhashani" VALUES(42,39,'42:39','و (نیز) برای آنان که چون تجاوزی به آنان برسد، انتقام می‌گیرند.');
INSERT INTO "badkhashani" VALUES(42,40,'42:40','و سزای بدی، بدیی مانند آن است، پس هرکس عفو کند و (عمل خود را) اصلاح نماید پس پاداش او بر الله است. يقينا الله ظالمان را دوست ندارد.');
INSERT INTO "badkhashani" VALUES(42,41,'42:41','و البته کسیکه پس از ستم دیدن خویش (برای دفع آن) انتقام بگیرد، بر آنان راهی (عتاب و عقاب) نیست.');
INSERT INTO "badkhashani" VALUES(42,42,'42:42','راه (عتاب و عقاب) تنها بر کسانی است که به مردم ظلم می‌کنند و در زمین به ناحق طغیان می‌کنند. آنانند که برایشان عذاب دردناک است.');
INSERT INTO "badkhashani" VALUES(42,43,'42:43','و کسیکه صبر کند و (مجرم را) عفو کند (بداند که) این (اوصاف) از کارهای استوار و سترگ است.');
INSERT INTO "badkhashani" VALUES(42,44,'42:44','و کسی را که الله گمراه سازد به غیر از او هیچ کارسازی برای او نیست. و ظالمان را خواهی دید که وقتی عذاب را می‌بینند می‌گویند: آیا راهی به‌سوی بازگشت (به دنیا) هست؟');
INSERT INTO "badkhashani" VALUES(42,45,'42:45','و آنان (ظالمان) را می‌بینی که بر آتش عرضه می‌شوند، در حالیکه از شدت ذلت و خواری ترسان‌اند، و از گوشۀ چشم مخفیانه (به آتش) می‌نگرند. و مؤمنان می‌گویند: البته زیانکاران کسانی‌اند که در روز قیامت خود و خانواده خود را به خسارت و زیان افگندند. آگاه باش! ظالمان در عذاب دائمی خواهند بود.');
INSERT INTO "badkhashani" VALUES(42,46,'42:46','و برای آنان جز الله ياران و دوستانی نیست که ایشان را یاری دهند، و هر کس را که الله گمراه سازد پس برای او هیچ راه (نجات) نیست.');
INSERT INTO "badkhashani" VALUES(42,47,'42:47','دعوت پروردگار خود را اجابت کنید، پیش از آنکه روزی فرا رسد که از (سوی) الله بازگشتی نباشد، در آن روز نه پناهگاهی دارید و نه می‌توانید انکار کنید.');
INSERT INTO "badkhashani" VALUES(42,48,'42:48','پس اگر روی بگردانند پس تو را بر آنان نگهبان نفرستاده‌ایم (تا آنان را بر ایمان مجبور سازی)، بر تو نیست مگر رسانیدن پیغام الله. و البته وقتی از جانب خویش رحمتی به انسان بچشانیم به آن شاد می‌شود (و ما را فراموش می‌کند). و اگر بلا و مصیبتی به خاطر کارهایی که کرده است به او برسد (نعمتها را فراموش می‌کند)، يقينا انسان ناسپاس است.');
INSERT INTO "badkhashani" VALUES(42,49,'42:49','فرمانروایی آسمان‌ها و زمین مخصوص الله است، هر چه بخواهد می‌آفریند، به هر کس که بخواهد دختران می‌بخشد و به هر کس که بخواهد پسران می‌بخشد.');
INSERT INTO "badkhashani" VALUES(42,50,'42:50','و یا اینکه هم پسر و هم دختر به آنان می‌دهد، و (الله) هرکه را بخواهد عقیم می‌کند چون او بس آگاه و تواناست.');
INSERT INTO "badkhashani" VALUES(42,51,'42:51','و هیچ انسانی را سزاوار نیست که الله با او سخن بگوید مگر از طریق وحی یا از پس پرده یا اینکه الله فرستاده‌ای (فرشته) بفرستد و آن (فرستاده) به حکم او (الله) هر چه را بخواهد وحی می‌کند، بی‌شک او والای باحکمت است.');
INSERT INTO "badkhashani" VALUES(42,52,'42:52','و این چنین روحی از حکم خود (قرآن) را به تو وحی کردیم، (قبل از آن) تو نمی‌دانستی که کتاب و ایمان چیست، ولی آن (قرآن) را نوری گردانیدیم که با آن هرکس از بندگان ما را که بخواهیم هدایت می‌کنیم و البته تو (مردم را) به راه راست هدایت می‌کنی.');
INSERT INTO "badkhashani" VALUES(42,53,'42:53','راه الله، پروردگاری که آنچه در آسمان‌ها و آنچه در زمین است، برای اوست. آگاه باشید! همۀ کارها به سوى الله باز می‌گردد.');
INSERT INTO "badkhashani" VALUES(43,1,'43:1','حم. (مفهوم این حروف به الله معلوم است)،');
INSERT INTO "badkhashani" VALUES(43,2,'43:2','قسم به این کتاب واضح (و بیانگر حق و باطل).');
INSERT INTO "badkhashani" VALUES(43,3,'43:3','به یقین ما این (کتاب) را قرآن عربی گردانیدیم تا شما بفهمید.');
INSERT INTO "badkhashani" VALUES(43,4,'43:4','و البته این کتاب در لوح محفوظ پیش ما ثبت است که بلند مرتبه و پر از حکمت است.');
INSERT INTO "badkhashani" VALUES(43,5,'43:5','آیا این قرآن را از شما باز گردانیم به خاطر آنکه شما گروه اسرافکارید؟');
INSERT INTO "badkhashani" VALUES(43,6,'43:6','و چه بسیار پیغمبران را در میان امت‌های پیشین فرستادیم.');
INSERT INTO "badkhashani" VALUES(43,7,'43:7','و هیچ پیغمبری به نزد آنان نمی‌آمد مگر آنکه او را مسخره می‌کردند.');
INSERT INTO "badkhashani" VALUES(43,8,'43:8','پس ما کسانی را هلاک کردیم که سخت‌تر از این اسرافکاران بودند. و نمونه‌هایی از عذاب پیشینیان گذشته است.');
INSERT INTO "badkhashani" VALUES(43,9,'43:9','و اگر از آنان بپرسی که چه کسی آسمان‌ها و زمین را آفریده است، حتما می‌گویند: آنها را پروردگار غالب دانا آفریده است.');
INSERT INTO "badkhashani" VALUES(43,10,'43:10','و آن ذاتی که زمین را برایتان محل آسایش گردانید و در آن برای شما راهها قرار داد تا شاید راه یابید.');
INSERT INTO "badkhashani" VALUES(43,11,'43:11','و آن ذاتی که از آسمان به اندازۀ معین آب فرود آورد، پس با آن آب زمین مرده را زنده کردیم. شما نیز این گونه (از قبرها) بیرون آورده می‌شوید.');
INSERT INTO "badkhashani" VALUES(43,12,'43:12','و آن ذاتی که همۀ جفت‌ها (نر و ماده) را آفریده است و برای شما از کشتی‌ها و چهارپایان چیزی آفرید که (بر آنها) سوار می‌شوید.');
INSERT INTO "badkhashani" VALUES(43,13,'43:13','تا بر پشت آنها قرار گیرید، و چون بر آن استقرار یافتید، نعمت پروردگارتان را یاد کنید، و بگویید: پاک و منزه است آن ذاتی که این را برای ما مسخر کرد و ما بر (رام کردن) آن قادر نبودیم.');
INSERT INTO "badkhashani" VALUES(43,14,'43:14','و البته ما به‌سوی پروردگار خود باز می‌گردیم.');
INSERT INTO "badkhashani" VALUES(43,15,'43:15','و برای او از بندگانش جزئی (مانند فرزند و شریک) قرار دادند، واقعا انسان ناسپاس آشکار است.');
INSERT INTO "badkhashani" VALUES(43,16,'43:16','آیا از آنچه می‌آفریند (برای خود) دختران را انتخاب کرد و برای شما پسران را برگزیده است؟');
INSERT INTO "badkhashani" VALUES(43,17,'43:17','در حالی که چون یکی از آنان را به آن چه برای رحمن مثل زده است، مژده دهند، چهره‌اش سیاه می‌شود و پر از غم می‌گردد.');
INSERT INTO "badkhashani" VALUES(43,18,'43:18','آیا کسی را که در زیور پرورش یافته و در وقت خصومت و مجادله نمی‌تواند مقصود خود را آشکار و اثبات کند (به الله نسبت می‌دهند؟).');
INSERT INTO "badkhashani" VALUES(43,19,'43:19','و فرشتگان را که بندگان پروردگار مهربان هستند، مؤنث قرار دادند. آیا در وقت آفریدن آنان حاضر بودند؟ گواهی آنان به زودی نوشته می‌شود و بازخواست خواهند شد.');
INSERT INTO "badkhashani" VALUES(43,20,'43:20','و گفتند: اگر پروردگار مهربان می‌خواست ما فرشتگان را عبادت نمی‌کردیم، آنان هیچ اطلاع و دلیلی به این ندارند. و ایشان جز از حدس و تخمین، سخنی نمی‌گویند.');
INSERT INTO "badkhashani" VALUES(43,21,'43:21','آیا پیش از این (قرآن) کتابی به آنان داده‌ایم که ایشان به آن چنگ زده‌اند؟');
INSERT INTO "badkhashani" VALUES(43,22,'43:22','بلکه گفتند: يقينا ما پدران خود را بر دینی یافته‌ایم و ما بر نقش قدم آنها روان و راه یافته‌ایم.');
INSERT INTO "badkhashani" VALUES(43,23,'43:23','و همین طور در هیچ دیاری پیش از تو بیم دهنده‌ای نفرستاده‌ایم مگر آنکه ثروتمندان متکبر‌شان گفتند: يقينا ما پدران خود را بر دینی یافته‌ایم و ما به راه و روش آنها اقتدا می‌کنیم.');
INSERT INTO "badkhashani" VALUES(43,24,'43:24','(پیغمبر‌شان به آنها) گفت: آیا اگر دینی را هم برای شما بیاورم که هدایت بخش‌تر از دینی باشد که پدرانتان را بر آن یافته‌اید (باز هم از آیین میراثی پیروی می‌کنید؟!) گفتند: ما به آنچه که به آن فرستاده شده‌اید کافریم.');
INSERT INTO "badkhashani" VALUES(43,25,'43:25','پس ما از آنها انتقام گرفتیم، پس ببین که سرانجام تکذیب کنندگان چگونه بود؟');
INSERT INTO "badkhashani" VALUES(43,26,'43:26','و یادآور شو چون ابراهیم به پدر و قومش گفت: من از آنچه شما می‌پرستید بیزارم.');
INSERT INTO "badkhashani" VALUES(43,27,'43:27','به جز آن معبودی که مرا آفریده است و او مرا هدایت خواهد کرد.');
INSERT INTO "badkhashani" VALUES(43,28,'43:28','و (الله) آن را در میان فرزندانش کلمۀ ماندنی قرار داد تا آنان (به توحید) باز گردند.');
INSERT INTO "badkhashani" VALUES(43,29,'43:29','بلکه من ایشان و پدرانشان را (از نعمت‌هایم) برخوردار ساختم، تا آنکه حق و پیغمبر آشکار به نزدشان آمد.');
INSERT INTO "badkhashani" VALUES(43,30,'43:30','و هنگامی که حق به نزد آنان آمد، گفتند: این جادو است و البته ما به آن کفر می‌ورزیم.');
INSERT INTO "badkhashani" VALUES(43,31,'43:31','و گفتند: چرا این قرآن بر مردی بزرگ (از نگاه جاه و مال) از دو شهر نازل نشد؟');
INSERT INTO "badkhashani" VALUES(43,32,'43:32','آیا آنها رحمت پروردگار تو را تقسیم می‌کنند؟ در حالی که ما معیشت آنان را در زندگی دنیا بین آنان تقسیم کرده‌ایم و بعضی را بر بعضی دیگر برتری‌هایی داده‌ایم تا بعضی از آنان بعضی دیگر را به خدمت گیرند و رحمت پروردگارت از آنچه جمع آوری می‌کنند بهتر است.');
INSERT INTO "badkhashani" VALUES(43,33,'43:33','اگر (این) نبود که مردم (در گمراهی) همه یک امت شوند، ما برای کسانی که به الله مهربان کفر می‌ورزند خانه‌هایی با سقف‌هایی از نقره می‌ساختیم و برای آنان زینه‌های از نقره قرار می‌دادیم که به وسیلۀ آنها بالا بروند.');
INSERT INTO "badkhashani" VALUES(43,34,'43:34','و برای خانه‌هایشان درهایی (از نقره قرار می‌دادیم) و تخت‌هایی نقره‌ای که بر آنها تکیه کنند (می‌ساختیم).');
INSERT INTO "badkhashani" VALUES(43,35,'43:35','و هر گونه اسباب زينت (برایشان قرار می‌دادیم)، اما همۀ اینها اسباب برخورداری زندگانی دنیاست و آخرت نزد پروردگارت برای پیرهیزگاران است.');
INSERT INTO "badkhashani" VALUES(43,36,'43:36','و هر کس که از یاد پروردگار مهربان غافل و روی گردان شود، برایش شیطانی می‌گماریم و آن (شیطان) همنشین او می‌گردد.');
INSERT INTO "badkhashani" VALUES(43,37,'43:37','و البته آنان (شیاطین) ایشان (انسانها) را از راه باز می‌دارند و گمان می‌کنند که ایشان هدایت یافته گان‌اند.');
INSERT INTO "badkhashani" VALUES(43,38,'43:38','تا وقتی که (چنین کسی) به پیش ما آید، (و به شیطان همنشین خود) گوید: ای کاش بین من و تو به اندازۀ مشرق و مغرب فاصله بود! چه بد همنشینی بودی.');
INSERT INTO "badkhashani" VALUES(43,39,'43:39','و امروز (آرزوی دوری از شیطان) برای شما سودی ندارد، چرا که شما ظلم کرده‌اید.');
INSERT INTO "badkhashani" VALUES(43,40,'43:40','آیا تو می‌توانی به کران بشنوانی؟ و یا اینکه کوران و کسانی را که در گمراهی آشکاری هستند، هدایت کنی؟');
INSERT INTO "badkhashani" VALUES(43,41,'43:41','پس وقتی تو را (از میان آنها) ببریم، پس حتمی از آنان انتقام خواهیم گرفت.');
INSERT INTO "badkhashani" VALUES(43,42,'43:42','یا آنچه را که به آنان وعده کرده‌ایم به تو نشان دهیم، زیرا ما بر آنان مسلط و مقتدریم.');
INSERT INTO "badkhashani" VALUES(43,43,'43:43','پس به آنچه به تو وحی شده است محکم چنگ بزن، چرا که تو به يقين بر راه مستقیم قرار داری.');
INSERT INTO "badkhashani" VALUES(43,44,'43:44','و چون قرآن برای تو و قومت پندی است و از شما سوال خواهد شد.');
INSERT INTO "badkhashani" VALUES(43,45,'43:45','و از (پیروان) پیغمبران پیشین ما بپرس، آیا به جز پروردگار مهربان معبودهایی قرار داده‌ایم که عبادت شوند؟');
INSERT INTO "badkhashani" VALUES(43,46,'43:46','و البته موسی را با نشانه‌های خود به‌سوی فرعون و سران (قوم) او فرستادیم (و موسی) گفت: یقیناً من فرستادۀ پروردگار عالمیان‌ام.');
INSERT INTO "badkhashani" VALUES(43,47,'43:47','وقتی که با نشانه‌های ما (به‌سوی) آنان آمد، پس آنان به آن (نشانه‌ها) می‌خندیدند.');
INSERT INTO "badkhashani" VALUES(43,48,'43:48','و ما هیچ معجزه‌ای را به ایشان نشان نمی‌دادیم مگر اینکه یکی از دیگری بزرگتر بود و آنان را به عذاب گرفتیم تا شاید باز گردند.');
INSERT INTO "badkhashani" VALUES(43,49,'43:49','و (به موسی) گفتند: ای جادوگر! پروردگارت را با آنچه که به تو عهد کرده است برای ما بخوان، ما حتما هدایت تو را خواهیم پذیرفت.');
INSERT INTO "badkhashani" VALUES(43,50,'43:50','پس وقتی عذاب را از ایشان دور کردیم، ناگهان همگی عهدشکنی کردند.');
INSERT INTO "badkhashani" VALUES(43,51,'43:51','و فرعون در میان قوم خود اعلان کرد و گفت: ای قوم من! آیا فرمانروایی مصر و این نهرها که از زیر (کاخهای) من روان است، از من نیست؟ آیا (عظمت مرا) نمی‌بینید؟');
INSERT INTO "badkhashani" VALUES(43,52,'43:52','نه، بلکه من بهترم از این شخص که خوار و حقیر است و نمی‌تواند روشن سخن بگوید.');
INSERT INTO "badkhashani" VALUES(43,53,'43:53','(اگر موسی راست می‌گوید) پس چرا دستبندهای از طلا بر او آویخته نشده است؟ یا چرا فرشتگان (برای یاری او) یکجا با او نیامده‌اند؟');
INSERT INTO "badkhashani" VALUES(43,54,'43:54','پس (فرعون) قوم خود را سبک و بی‌خرد شمرد، پس آنان از او اطاعت کردند. البته آنان قوم فاسق بودند.');
INSERT INTO "badkhashani" VALUES(43,55,'43:55','پس وقتی ما را به خشم آوردند، از آنان انتقام گرفتیم و همۀ آنان را غرق کردیم.');
INSERT INTO "badkhashani" VALUES(43,56,'43:56','پس آنان را پیشگامان (بد) و عبرت برای آیندگان قرار دادیم.');
INSERT INTO "badkhashani" VALUES(43,57,'43:57','و چون پسر مریم (عیسی) به طور مثال ذکر شد، ناگهان قوم تو از آن با فریاد به )تمسخر و( شور و هلهله پرداختند.');
INSERT INTO "badkhashani" VALUES(43,58,'43:58','و گفتند: آیا معبودان ما بهترند یا او؟ (آن مثال را) برای تو جز از روی جدال نیاوردند، بلکه آنان قوم جدل پیشه‌اند.');
INSERT INTO "badkhashani" VALUES(43,59,'43:59','در حالیکه عیسی جز بنده‌ای نیست که بر او انعام کردیم و او را نمونۀ هدایت برای بنی اسرائیل گردانیدیم.');
INSERT INTO "badkhashani" VALUES(43,60,'43:60','و اگر بخواهیم به جای شما در زمین فرشتگان قرار میدهیم تا جانشین شما شوند.');
INSERT INTO "badkhashani" VALUES(43,61,'43:61','و به يقين (عیسی) نشانه‌ای برای قیامت است پس هرگز دربارۀ آن (قیامت) شک نکنید و از من پیروی کنید که همین راه راست است.');
INSERT INTO "badkhashani" VALUES(43,62,'43:62','و شیطان هرگز شما را (از راه الله) باز ندارد، يقينا او برای شما دشمن آشکار است.');
INSERT INTO "badkhashani" VALUES(43,63,'43:63','و چون عیسی با معجزه‌های آشکار آمد، گفت: بدون شک برای شما حکمت آورده (و آمده‌ام) تا بعضی از امور را برای شما بیان و روشن کنم که در آنها اختلاف می‌ورزید، پس از الله بترسید و مرا اطاعت کنید.');
INSERT INTO "badkhashani" VALUES(43,64,'43:64','چون الله پروردگار من و پروردگار شماست پس او را عبادت کنید، این است راه راست.');
INSERT INTO "badkhashani" VALUES(43,65,'43:65','پس گروها بین خود اختلاف نمودند، پس وای به حال کسانی که ظلم کردند، از عذاب روز دردناک،');
INSERT INTO "badkhashani" VALUES(43,66,'43:66','آیا منتظر این هستند که قیامت ناگهانی و در حالیکه از آن بی‌خبرند به سراغ‌شان بیاید؟');
INSERT INTO "badkhashani" VALUES(43,67,'43:67','دوستان در آن روز دشمنان یکدیگر می‌شوند، مگر پرهیزگاران (که دوستی‌شان باقی می‌ماند).');
INSERT INTO "badkhashani" VALUES(43,68,'43:68','ای بندگان من! امروز بر شما هیچ ترسی نیست و نه شما غمگین می‌شوید.');
INSERT INTO "badkhashani" VALUES(43,69,'43:69','همان کسانی که به آیات ما ایمان آوردند و همیشه تسليم حق بودند.');
INSERT INTO "badkhashani" VALUES(43,70,'43:70','شما و همسران تان به بهشت در آئید در حالی که شادمان و مسرورید.');
INSERT INTO "badkhashani" VALUES(43,71,'43:71','بر ایشان ظرفها و جام‌های طلایی چرخانده می‌شود و هر چه دل بخواهد و هر چه چشم از آن لذت ببرد در بهشت موجود است و شما در آن جاودانه هستید.');
INSERT INTO "badkhashani" VALUES(43,72,'43:72','و این همان جنتی است که به سبب کارهایی که می‌کردید، به میراث برده‌اید.');
INSERT INTO "badkhashani" VALUES(43,73,'43:73','در آن برای شما میوۀ بسیار است که از آن می‌خورید.');
INSERT INTO "badkhashani" VALUES(43,74,'43:74','البته مجرمان در عذاب دوزخ همیشه می‌مانند.');
INSERT INTO "badkhashani" VALUES(43,75,'43:75','عذاب از آنان کاسته نمی‌شود و آنان در آن نا امید شده و خاموش هستند.');
INSERT INTO "badkhashani" VALUES(43,76,'43:76','و ما به آنان ظلم نکردیم بلکه آنان خود ظالم بودند.');
INSERT INTO "badkhashani" VALUES(43,77,'43:77','و فریاد می‌کشند: ای مالک (نگهبان دوزخ)! پروردگارت باید عليه ما حکم مرگ صادر کند. گویند: شما (در آن) ماندنی هستید.');
INSERT INTO "badkhashani" VALUES(43,78,'43:78','البته ما حق را برای شما آوردیم، ولی بیشتر شما با حق کراهت داشتید.');
INSERT INTO "badkhashani" VALUES(43,79,'43:79','آیا آنان تصمیم محکم (علیه پیغمبر ما) گرفتند؟ پس ما نیز ارادۀ محکم و تغییر ناپذیر (برای نجات او) گرفتیم.');
INSERT INTO "badkhashani" VALUES(43,80,'43:80','آیا گمان می‌کنند که ما اسرار پنهانی و سخنان در گوشی آنان را نمی‌شنویم؟! بلی! (می شنویم) و فرشتگان ما نزد آنها (همه چیز را) می‌نویسند.');
INSERT INTO "badkhashani" VALUES(43,81,'43:81','بلکه اگر برای پروردگار بخشنده فرزندی می‌بود، پس من اولین عبادت کنندگانم.');
INSERT INTO "badkhashani" VALUES(43,82,'43:82','پاک و منزه است پروردگار آسمان‌ها و زمین و پروردگار عرش، از آنچه او را توصیف می‌کنند.');
INSERT INTO "badkhashani" VALUES(43,83,'43:83','پس آنان را به حال خود‌شان واگذار تا در باطل خویش غوطه ور گردند و سرگرم بازی شوند تا روزی را که وعده داده می‌شوند ملاقات کنند.');
INSERT INTO "badkhashani" VALUES(43,84,'43:84','و او ذاتی است که در آسمان معبود است و در زمین (نیز) معبود می‌باشد و اوست حکیم دانا۔');
INSERT INTO "badkhashani" VALUES(43,85,'43:85','و خجسته و بابرکت است ذاتی که پادشاهی آسمان‌ها و زمین و هر آنچه میان آن دو است از اوست و علم قیامت مخصوص اوست و به‌سوی او باز گردانیده می‌شوید.');
INSERT INTO "badkhashani" VALUES(43,86,'43:86','و کسانی را که به جای او پرستش می‌کنند مالک هیچ شفاعت نیستند مگر کسانی که شهادت به حق داده باشند و آنان می‌دانند.');
INSERT INTO "badkhashani" VALUES(43,87,'43:87','و اگر از آنها بپرسی چه کسی آنان را آفریده است؟ حتما می‌گویند: الله! پس چگونه به انحراف کشانیده می‌شوند؟!');
INSERT INTO "badkhashani" VALUES(43,88,'43:88','و (الله فرمود:) سخن پیغمبر ما این است: پروردگارا! بدون شک اینها قومی هستند که ایمان نمی‌آورند.');
INSERT INTO "badkhashani" VALUES(43,89,'43:89','پس از آنان روی بگردان و بگو: سلام، (وداع با شما) در آینده خواهند دانست (که چه سرنوشتی دارند!).');
INSERT INTO "badkhashani" VALUES(44,1,'44:1','حم. (مفهوم این دو حرف به الله معلوم است).');
INSERT INTO "badkhashani" VALUES(44,2,'44:2','قسم به این کتاب واضح و بیانگر (حق و باطل).');
INSERT INTO "badkhashani" VALUES(44,3,'44:3','بدون شک ما آن را در شبی بابرکت و خجسته نازل کردیم و ما همیشه بیم دهنده بودیم.');
INSERT INTO "badkhashani" VALUES(44,4,'44:4','(چون) هر کار مهم و حکیمانه در آن (شب مبارک) بیان و مقرر می‌گردد.');
INSERT INTO "badkhashani" VALUES(44,5,'44:5','امری است از سوی ما،. بی‌گمان ما فرستنده بودیم.');
INSERT INTO "badkhashani" VALUES(44,6,'44:6','(این کار) رحمت بزرگ از سوی پروردگارت است، البته او شنوای داناست.');
INSERT INTO "badkhashani" VALUES(44,7,'44:7','پروردگار آسمان‌ها و زمین و آنچه میان آن دو است اگر یقین کننده هستید (پس ایمان آورید).');
INSERT INTO "badkhashani" VALUES(44,8,'44:8','هیچ معبودی به حق جز او نیست و اوست که زنده می‌کند و می‌میراند و پروردگار شما و پروردگار پدران پیشین شماست.');
INSERT INTO "badkhashani" VALUES(44,9,'44:9','بلکه آنان در شک (عمیق) بازی می‌کنند.');
INSERT INTO "badkhashani" VALUES(44,10,'44:10','پس منتظر روزی باش که آسمان دود آشکار را با خود می‌آورد.');
INSERT INTO "badkhashani" VALUES(44,11,'44:11','مردم را فرا می‌گیرد، این است عذاب دردناک.');
INSERT INTO "badkhashani" VALUES(44,12,'44:12','(مردم می‌گویند:) ای پروردگار ما! عذاب را از ما دور کن، همانا ما ایمان آرندگانیم.');
INSERT INTO "badkhashani" VALUES(44,13,'44:13','این پند برای آنان چه سود؟ در حالیکه پیغمبر روشنگر (حق) نزد آنان آمده بود.');
INSERT INTO "badkhashani" VALUES(44,14,'44:14','باز از وی روی گرداندند و گفتند: او دیوانۀ آموخته شده است.');
INSERT INTO "badkhashani" VALUES(44,15,'44:15','البته ما برای مدتی اندک برطرف کنندۀ عذاب هستیم (لیکن) شما باز (به شرک خود) رجوع کننده‌اید.');
INSERT INTO "badkhashani" VALUES(44,16,'44:16','روزی که به گرفتن سخت (شما را) بگیریم، البته ما انتقام گیرنده‌ایم.');
INSERT INTO "badkhashani" VALUES(44,17,'44:17','و بدون شک ما پیش از ایشان قوم فرعون را آزمودیم در حالیکه پیغمبر بزرگوار نزدشان آمد.');
INSERT INTO "badkhashani" VALUES(44,18,'44:18','(با این پیغام) که بندگان الله (بنی اسرائیل) را به من بسپارید، يقينا من برایتان پیغمبر امین هستم.');
INSERT INTO "badkhashani" VALUES(44,19,'44:19','و (اینکه) بر الله برتری نجویید (همانا) من برای شما دلیل آشکار می‌آورم.');
INSERT INTO "badkhashani" VALUES(44,20,'44:20','و البته من به پروردگار خود و پروردگار شما پناه می‌برم از اینکه مرا سنگسار کنید.');
INSERT INTO "badkhashani" VALUES(44,21,'44:21','و اگر به من ایمان نمی‌آورید، پس از من کناره‌گیری کنید.');
INSERT INTO "badkhashani" VALUES(44,22,'44:22','پس او (پیغمبر) پروردگار خود را به دعا خواند که يقينا آنها قوم مجرم‌اند.');
INSERT INTO "badkhashani" VALUES(44,23,'44:23','(به او گفتیم:) پس بندگانم را شبانه ببر (و بدانید که) حتما شما تعقیب می‌شوید.');
INSERT INTO "badkhashani" VALUES(44,24,'44:24','و بحر را آرام رها کن، زیرا ایشان لشکری‌اند غرق شده‌گان.');
INSERT INTO "badkhashani" VALUES(44,25,'44:25','(پس از غرق شدن) چه باغها و چشمه‌های بسیاری را گذاشتند.');
INSERT INTO "badkhashani" VALUES(44,26,'44:26','و چه کشتزارها و جایگاهی عالی (را بجا گذاشتند).');
INSERT INTO "badkhashani" VALUES(44,27,'44:27','و چه نعمتی که در آن شاد و خرم بودند.');
INSERT INTO "badkhashani" VALUES(44,28,'44:28','این طور (شد) و ما همه‌ای این نعمت‌ها را (از آنها گرفتیم و) به قوم دیگری دادیم.');
INSERT INTO "badkhashani" VALUES(44,29,'44:29','پس نه آسمان بر (حال) آنها گریست و نه زمین. و نه از مهلت داده شدگان بودند.');
INSERT INTO "badkhashani" VALUES(44,30,'44:30','و البته ما بنی اسراییل را از عذاب اهانت آمیز نجات دادیم.');
INSERT INTO "badkhashani" VALUES(44,31,'44:31','از فرعون که او سرکش و از اسرافکاران بود.');
INSERT INTO "badkhashani" VALUES(44,32,'44:32','و همانا ما بنی اسرائیل را عالمانه برگزیدیم و بر جهانیان (هم عصر شان) برتری دادیم.');
INSERT INTO "badkhashani" VALUES(44,33,'44:33','و از نشانه‌هایی به آنان دادیم که در آن آزمایش آشکار بود.');
INSERT INTO "badkhashani" VALUES(44,34,'44:34','البته این (مشرکان) می‌گویند.');
INSERT INTO "badkhashani" VALUES(44,35,'44:35','غیر از این مرگ اول ما (زندگی دیگر) نیست و ما بار دیگر زنده نخواهیم شد.');
INSERT INTO "badkhashani" VALUES(44,36,'44:36','پس اگر (شما پیغمبران) راستگو هستید، پدران (مردۀ) ما را بیاورید.');
INSERT INTO "badkhashani" VALUES(44,37,'44:37','آیا آنان (مشرکان مکه) بهترند یا قوم «تُبّع»؟ و کسانی که پیش از آنان بودند؟ ما آنان را به خاطر اینکه قوم مجرم بودند، نابود کردیم');
INSERT INTO "badkhashani" VALUES(44,38,'44:38','ما آسمان‌ها و زمین و آنچه را که در میان آن دو است بیهوده و بی‌هدف نیافریدیم.');
INSERT INTO "badkhashani" VALUES(44,39,'44:39','ما آن دو را جز به حق نیافریدیم لیکن بیشترشان نمی‌دانند.');
INSERT INTO "badkhashani" VALUES(44,40,'44:40','البته روز فیصله (میان توحید و شرک) وعده‌گاه همۀ آنهاست.');
INSERT INTO "badkhashani" VALUES(44,41,'44:41','روزی که هیچ دوستی از دوستی (دیگر) چیزی را دفع نکند و آنان مدد نمی‌شوند.');
INSERT INTO "badkhashani" VALUES(44,42,'44:42','به جز کسی که الله بر او رحم کند، چون او (الله) غالب مهربان است.');
INSERT INTO "badkhashani" VALUES(44,43,'44:43','البته درخت زقوم.');
INSERT INTO "badkhashani" VALUES(44,44,'44:44','غذای گنهگاران است.');
INSERT INTO "badkhashani" VALUES(44,45,'44:45','مانند مس گداخته شده در شکم‌ها می‌جوشد.');
INSERT INTO "badkhashani" VALUES(44,46,'44:46','چون جوشیدن آب گرم.');
INSERT INTO "badkhashani" VALUES(44,47,'44:47','(به مأموران دوزخ دستور داده می‌شود) او را بگیرید پس وی را به سختی به‌سوی قعر دوزخ بکشانید.');
INSERT INTO "badkhashani" VALUES(44,48,'44:48','باز بر بالای سرش از عذاب آب جوشیده بریزید.');
INSERT INTO "badkhashani" VALUES(44,49,'44:49','(به او گفته شود: عذاب را) بچش، چون تو بسیار عزت مند و (گرامی) و بزرگوار هستی.');
INSERT INTO "badkhashani" VALUES(44,50,'44:50','این همان چیزی است که همیشه دربارۀ آن شک و تردید داشتید.');
INSERT INTO "badkhashani" VALUES(44,51,'44:51','البته پرهیزگاران در جایگاه امن و امانی هستند.');
INSERT INTO "badkhashani" VALUES(44,52,'44:52','در (میان) باغها و (در کنار) چشمه سارها.');
INSERT INTO "badkhashani" VALUES(44,53,'44:53','لباس‌هایی از ابریشم نازک و ضخیم می‌پوشند و در مقابل یکدیگر نشسته‌اند.');
INSERT INTO "badkhashani" VALUES(44,54,'44:54','این چنین (است مقام پرهیزگاران) و حورهای گشاده چشم را همسر آنان قرار می‌دهیم.');
INSERT INTO "badkhashani" VALUES(44,55,'44:55','آنان در آنجا هر میوه‌ای را (که بخواهند) با آسودگی می‌طلبند.');
INSERT INTO "badkhashani" VALUES(44,56,'44:56','در جنت مرگ را نچشند، به جز مرگ نخستین را که (قبلا) چشیده بودند و الله آنان را از عذاب دوزخ حفظ نموده است.');
INSERT INTO "badkhashani" VALUES(44,57,'44:57','(این همه) فضل و بخششی است از سوی پروردگار تو، این همان کامیابی بزرگ است.');
INSERT INTO "badkhashani" VALUES(44,58,'44:58','جز این نیست که ما قرآن را با زبان تو آسان کردیم تا شاید پند گیرند.');
INSERT INTO "badkhashani" VALUES(44,59,'44:59','پس منتظر باش که آنان (نیز) منتظرند.');
INSERT INTO "badkhashani" VALUES(45,1,'45:1','حم. (مفهوم این حروف به الله معلوم است).');
INSERT INTO "badkhashani" VALUES(45,2,'45:2','فرود آوردن این کتاب از جانب الله غالب با حکمت است.');
INSERT INTO "badkhashani" VALUES(45,3,'45:3','بدون شک در آسمان‌ها و زمین برای مؤمنان نشانه‌هایی است.');
INSERT INTO "badkhashani" VALUES(45,4,'45:4','و (نیز) در آفرینش شما و (در آفرینش) آنچه از جنبنده‌ها (که در روی زمین) منتشر می‌سازد، نشانه‌هایی است برای قومی که یقین می‌کنند.');
INSERT INTO "badkhashani" VALUES(45,5,'45:5','و (در) اختلاف شب و روز و در آنچه از روزی که الله از آسمان نازل کرده و زمین را به وسیلۀ آن پس از خشک شدنش زنده گردانیده است و (نیز) در گردش بادها، برای قومی که خرد می‌ورزند نشانه‌هایی است.');
INSERT INTO "badkhashani" VALUES(45,6,'45:6','اینها آيات الله است که به حق بر تو می‌خوانیم، پس بعد از (پند) الله و آیات او به چه سخنی ایمان می‌آورند؟');
INSERT INTO "badkhashani" VALUES(45,7,'45:7','وای به حال هر دروغ پرداز گناهکار.');
INSERT INTO "badkhashani" VALUES(45,8,'45:8','آیات الله بر او خوانده می‌شود، باز تکبر کنان (بر کفر) اصرار می‌ورزد گویی که آن را نشنیده است. پس او را به عذاب دردناک مژده بده.');
INSERT INTO "badkhashani" VALUES(45,9,'45:9','و چون از آیات ما چیزی بفهمد، آن را به مسخره می‌گیرد، این گروه برایشان عذاب اهانت کننده است.');
INSERT INTO "badkhashani" VALUES(45,10,'45:10','پیش رویشان دوزخ است. و آنچه (از مال و متاع دنیا) را به دست آورده‌اند و آنچه غیر از الله را دوستان خود گرفته‌اند، چیزی از (عذاب را) از آنان دفع نمی‌کند و برای آنان عذاب بس بزرگ است.');
INSERT INTO "badkhashani" VALUES(45,11,'45:11','این (قرآن) راهنماست. و آنانی که به آیات پروردگارشان کفر ورزیده‌اند، برای‌شان عذاب دردناک، از عذاب نفرت انگیز است.');
INSERT INTO "badkhashani" VALUES(45,12,'45:12','الله ذاتی است که بحر را برایتان مسخر ساخت تا کشتی‌ها به حکم او در آن روان شوند و (نیز) تا شما از فضل او طلب کنید و تا شکرگزاری کنید.');
INSERT INTO "badkhashani" VALUES(45,13,'45:13','و آنچه که در آسمان‌ها و زمین است، همه از اوست که برایتان مسخر کرد، یقینا در این نشانه‌هایی است برای کسانی که می‌اندیشند.');
INSERT INTO "badkhashani" VALUES(45,14,'45:14','به مؤمنان بگو: از کسانی که به روزهای الله امید ندارند درگذرند تا (الله) هر قومی را به آنچه می‌کردند سزا دهد.');
INSERT INTO "badkhashani" VALUES(45,15,'45:15','هر کس که کار نیک انجام دهد به سود خود اوست و هر کس که کار بد کند به زیان خود اوست، باز به‌سوی پروردگارتان باز گردانیده می‌شوید.');
INSERT INTO "badkhashani" VALUES(45,16,'45:16','و البته به بنی اسرائیل کتاب و حکمت و پیغمبری دادیم و به آنان از پاکیزه‌ها روزی عطا کردیم و آنان را بر جهانیان (هم عصرشان) برتری دادیم.');
INSERT INTO "badkhashani" VALUES(45,17,'45:17','و به آنان دلائل واضح و روشن در بارۀ دین دادیم. پس اختلاف نکردند مگر بعد از آنکه حقیقت برای‌شان حاصل شد (آن اختلاف هم) از روی عداوت و حسادت بود که در میان‌شان وجود داشت، البته پروردگار تو روز قیامت در میانشان دربارۀ چیزی که در آن اختلاف می‌کردند، فيصله خواهد کرد.');
INSERT INTO "badkhashani" VALUES(45,18,'45:18','باز تو را بر آئین و راه روشنی از دین قرار دادیم پس از آن پیروی کن و از خواهشات آنان که نمی‌دانند پیروی مکن.');
INSERT INTO "badkhashani" VALUES(45,19,'45:19','آنان از (عذاب) الله چیزی را از تو دفع کرده نمی‌توانند و ظالمان دوستان یکدیگرند و الله هم یار و مددگار پرهیزگاران است.');
INSERT INTO "badkhashani" VALUES(45,20,'45:20','این (قرآن) وسيلۀ بینش و بصیرت بشریت و سبب هدایت و رحمت کسانی است که اهل یقین هستند.');
INSERT INTO "badkhashani" VALUES(45,21,'45:21','آیا کسانی که مرتکب بدی‌ها شدند، گمان کردند که آنان را مانند کسانی قرار می‌دهیم که ایمان آورده‌اند و کارهای نیک انجام داده‌اند؟ تا زندگانی و مرگ‌شان برابر باشد؟ چه بد حکم می‌کنند!');
INSERT INTO "badkhashani" VALUES(45,22,'45:22','و الله آسمان‌ها و زمین را به حق آفریده است تا هر کس به موجب آنچه کسب نموده سزا و جزا داده شود و آنان مورد هیچ گونه ظلمی واقع نمی‌شوند.');
INSERT INTO "badkhashani" VALUES(45,23,'45:23','پس آیا دیدی آن را که هوی و هوس خود را معبود خویش گردانیده و الله او را با وجود آگاهیش گمراه ساخته و بر گوش و دل او مهر نهاده، و بر چشمش پرده قرار داده است؟ پس چه کسی بعد از الله او را هدایت کند؟ آیا عبرت نمی‌گیرید؟');
INSERT INTO "badkhashani" VALUES(45,24,'45:24','و گفتند: جز همین زندگانی دنیوی، زندگی دیگری نداریم، می‌میرم و زنده می‌شویم و جز زمانه ما را هلاک نمی‌سازد. حال آنکه ایشان هیچ علمی به این (ادعا) ندارند، بلکه تنها گمان می‌کنند.');
INSERT INTO "badkhashani" VALUES(45,25,'45:25','و چون آیات واضح و روشن ما بر آنان تلاوت شود، دلیل و حجتی ندارند جز این که می‌گویند: اگر راست می‌گوئید پدران ما را (زنده کرده) بیاورید.');
INSERT INTO "badkhashani" VALUES(45,26,'45:26','بگو: الله شما را زنده می‌کند باز شما را می‌میراند باز برای روز قیامت که شکی در آن نیست شما را جمع می‌کند ولی اکثر مردم نمی‌دانند.');
INSERT INTO "badkhashani" VALUES(45,27,'45:27','پادشاهی آسمان‌ها و زمین مخصوص الله است. و روزی که قیامت برپا می‌شود در آن روز باطل گویان زیانکار می‌شوند.');
INSERT INTO "badkhashani" VALUES(45,28,'45:28','هر امتی را می‌بینی که بر سر زانوها نشسته است، هر امتی به‌سوی نامۀ اعمال خود فرا خوانده می‌شود، امروز پاداش کارهایی که می‌کردید به شما داده می‌شود.');
INSERT INTO "badkhashani" VALUES(45,29,'45:29','این است کتاب ما که به حق بر شما سخن می‌گوید، البته ما آنچه را که شما می‌کردید نسخه برداری کرده می‌نوشتیم.');
INSERT INTO "badkhashani" VALUES(45,30,'45:30','و اما آنانکه ایمان آورده و کارهای نیک کرده‌اند، پروردگارشان آنان را در رحمت خود داخل می‌کند، این است همان کامیابی بزرگ.');
INSERT INTO "badkhashani" VALUES(45,31,'45:31','و اما آنانکه کفر ورزیده‌اند (به آنان گفته می‌شود:) آیا آیات من بر شما خوانده نمی‌شد پس تکبر ورزیدید و قوم مجرم بودید؟');
INSERT INTO "badkhashani" VALUES(45,32,'45:32','و هنگامیکه گفته شود: وعدۀ الله حق است و شکی در (آمدن) قیامت نیست، می‌گفتید: ما نمی‌دانیم قیامت چیست، ما تنها گمانی (دربارۀ آن) داریم و به هیچ وجه يقين و باور نداریم.');
INSERT INTO "badkhashani" VALUES(45,33,'45:33','و بدی‌هایی اعمالشان برایشان آشکار شد و آنچه را به استهزاء می‌گرفتند، آنان را فرا گرفت.');
INSERT INTO "badkhashani" VALUES(45,34,'45:34','و گفته می‌شود: امروز شما را فراموش می‌کنیم، چنانکه شما دیدار این روزتان را فراموش کردید و جایگاهتان آتش دوزخ است و برای شما هیچ مددگاری نیست.');
INSERT INTO "badkhashani" VALUES(45,35,'45:35','این )عقوبت) به خاطر آن است که شما آيات الله را مورد تمسخر و استهزا قرار دادید و زندگانی دنیا مغرورتان کرد، پس امروز از آن (دوزخ) بیرون آورده نمی‌شوند و نه از آنها خواسته می‌شود که الله را خشنود سازند.');
INSERT INTO "badkhashani" VALUES(45,36,'45:36','پس ستایش تنها مخصوص الله است که پروردگار آسمان‌ها و پروردگار زمین و پروردگار جهانیان است.');
INSERT INTO "badkhashani" VALUES(45,37,'45:37','و بزرگی در آسمان‌ها و زمین مخصوص اوست و او غالب باحکمت است.');
INSERT INTO "badkhashani" VALUES(46,1,'46:1','حم. (مفهوم این حروف به الله معلوم است).');
INSERT INTO "badkhashani" VALUES(46,2,'46:2','فرود آوردن این کتاب از جانب الله توانمند باحکمت است.');
INSERT INTO "badkhashani" VALUES(46,3,'46:3','آسمان‌ها و زمین و آنچه را که در میان این دو است جز به حق و تا وقت معين نه آفریده‌ایم و کافران از آنچه که از آن بیم داده شده‌اند، روی گردانند.');
INSERT INTO "badkhashani" VALUES(46,4,'46:4','بگو: آنچه را به جای الله (به دعا) می‌خوانید، دیده‌اید؟ به من نشان دهید چه چیزی از زمین را (معبودان باطل‌تان) آفریده‌اند؟ یا اینکه در آفرینش آسمان‌ها شراکت داشته‌اند؟ اگر راستگو هستید پس کتابی پیش از این یا نشانه‌ای از علم (گذشتگان) را برای من بیاورید.');
INSERT INTO "badkhashani" VALUES(46,5,'46:5','و (وقتی دلیلی ندارید پس) کیست گمراه‌تر از آن که به جای الله کسی را (به دعا) می‌خواند که تا روز قیامت (دعای) او را اجابت کرده نتواند و آنان (معبودان باطل) از دعاهایشان غافل و بی‌خبر ‌اند؟!');
INSERT INTO "badkhashani" VALUES(46,6,'46:6','و چون مردم جمع کرده شوند (آن معبودها) دشمن آنان خواهند بود و عبادتشان را انکار می‌کنند.');
INSERT INTO "badkhashani" VALUES(46,7,'46:7','و هنگامیکه آیت‌های واضح ما بر آنان تلاوت شود، کسانی که کفر ورزیده‌اند دربارۀ حقی که به آنان آمده، می‌گویند: این جادوی آشکار است.');
INSERT INTO "badkhashani" VALUES(46,8,'46:8','یا اینکه می‌گویند: (پیغمبر) آن را از خود ساخته و به الله نسبت داده است؟ بگو: اگر من آن را به دروغ به الله نسبت داده باشم شما نمی‌توانید در برابر قهر الله کاری برای دفاع از من بکنید. او از آنچه شما در بارۀ آن (قرآن) گفتگو می‌کنید، داناتر است. کافی است که او در میان من و شما گواه باشد و اوست آمرزگار مهربان.');
INSERT INTO "badkhashani" VALUES(46,9,'46:9','بگو: من پیغمبر نو آمد و نخستین در میان پیغمبران نیستم و نمی‌دانم که با من و شما چگونه رفتار خواهد شد؟ من پیروی نمی‌کنم مگر آنچه را که به من وحی فرستاده می‌شود و من جز بیم دهندۀ آشکار نیستم.');
INSERT INTO "badkhashani" VALUES(46,10,'46:10','بگو: به من خبر دهید اگر این قرآن از سوى الله باشد و آن را انکار کنید، در حالیکه گواهی از بنی اسرائیل، بر مانند آن (تورات) گواهی داده و ایمان آورده، ولی شما همچنان تکبر می‌ورزید (پس آیا ظالم نیستید؟) حقا که الله قوم ظالم را هدایت نمی‌کند.');
INSERT INTO "badkhashani" VALUES(46,11,'46:11','و کافران دربارۀ مؤمنان گفتند: اگر (دین اسلام) چیز خوبی بود آنها در پذیرفتن آن از ما سبقت نمی‌کردند و چون خود‌شان به وسیلۀ آن هدایت نشده‌اند، می‌گویند: این دروغی قدیم است.');
INSERT INTO "badkhashani" VALUES(46,12,'46:12','و پیش از آن (قرآن) کتاب موسی پیشوا و رحمت بود. و این کتابی است تصدیق کنندۀ (آن) که به زبان عربی (نازل شده) است، تا ظالمان را بترساند و برای نیکوکاران مژده باشد.');
INSERT INTO "badkhashani" VALUES(46,13,'46:13','البته آنان که گفتند: الله پروردگار ماست، باز استقامت ورزیدند، نه ترسی بر آنان است و نه غمگین می‌شوند.');
INSERT INTO "badkhashani" VALUES(46,14,'46:14','ایشان اهل بهشت‌اند که در آن همیشه‌اند، به خاطر آنچه (از اعمال نیک) می‌کردند.');
INSERT INTO "badkhashani" VALUES(46,15,'46:15','و انسان را دربارۀ پدر و مادرش به نیکوکاری سفارش کردیم، مادرش او را به سختی حمل کرد و به سختی به دنیا آورد، و دوران بارداری و از شیر جدا کردن او سی ماه را در بر می‌گیرد، تا آنکه به كمال جوانی برسد و چهل ساله شود، می‌گوید: ای پروردگارم! به من توفیق ده تا نعمتی را که بر من و بر پدر و مادرم بخشیده‌ای شکر گزارم و کار نیک انجام دهم که از آن راضی شوی. و فرزندانم را نیز برایم نیک بگردان. همانا من به‌سوی تو باز گشته‌ام و از جملۀ مسلمانانم.');
INSERT INTO "badkhashani" VALUES(46,16,'46:16','آنان کسانی‌اند که بهترین کردارشان را از آنان می‌پذیریم و از بدی‌هایشان در می‌گذریم، در حالیکه در (میان) جنتیان خواهند بود، این همان وعدۀ راستی است که به آنان وعده داده می‌شد.');
INSERT INTO "badkhashani" VALUES(46,17,'46:17','و آنکه به پدر و مادرش گفت: اف بر شما! آیا به من وعده می‌دهید که من (از قبر) بیرون آورده خواهم شد، حال آنکه پیش از من اقوام و ملت‌هایی از جهان رفته‌اند (و زنده نشده‌اند) و آن دو (پدر و مادر) الله را می‌خوانند (و می‌گویند:) وای بر تو! ایمان بیاور، بی‌گمان وعدۀ الله حق است. اما او می‌گوید: این جز افسانه‌های مردمان گذشته نیست.');
INSERT INTO "badkhashani" VALUES(46,18,'46:18','اینها‌اند که وعدۀ عذاب بر آنان همراه با امت‌هایی از جن و انس که پیش از آنان گذشته‌اند محقق شده است. يقينا آنان زیان‌کار بودند.');
INSERT INTO "badkhashani" VALUES(46,19,'46:19','و برای هریک از آنچه عمل کرده‌اند، درجاتی است تا به این وسیله، الله سزای اعمالشان را به طور کامل به آنان بدهد و آنها مورد ظلم قرار نگیرند.');
INSERT INTO "badkhashani" VALUES(46,20,'46:20','و یادآور شو، روزی که کافران بر آتش عرضه شوند (در این وقت به آنان گفته می‌شود) شما لذت‌ها و خوشی‌های خود را در زندگانی دنیای خویش از بین بردید و از آن برخوردار شدید، امروز شما به سبب آن که به ناحق در زمین تکبر می‌ورزیدید و به (سزای) آنکه مرتکب گناه می‌شدید، به عذاب خوار کننده و ذلت بار سزا داده می‌شوید.');
INSERT INTO "badkhashani" VALUES(46,21,'46:21','و (هود) برادر (قوم) عاد را یاد کن وقتی که در منطقۀ ریگستان احقاف قوم خود را (از عذاب الله) بیم داد، و به راستی پیش از او و پس از او نیز بیم دهندگانی گذشته بودند (با این پیام) که جز الله را نپرستید. چون من از عذاب روز بزرگ بر شما می‌ترسم.');
INSERT INTO "badkhashani" VALUES(46,22,'46:22','گفتند: آیا آمده‌ای تا ما را از معبودان ما برگردانی؟ اگر از راستگویان هستی پس آنچه را که به ما وعده میدهی، برای ما بیار.');
INSERT INTO "badkhashani" VALUES(46,23,'46:23','گفت: علم (زمان رسیدن عذاب) تنها نزد الله است، من چیزی را به شما ابلاغ می‌کنم که به تبلیغ آن فرستاده شده ام، ولی شما را قومی جاهل می‌بینم.');
INSERT INTO "badkhashani" VALUES(46,24,'46:24','پس چون تودۀ ابر را دیدند که به‌سوی دره‌هایشان روی آورده است، گفتند: این ابری است که برای ما خواهد بارید، (نه)، بلکه آن چیزی است که آن را به شتاب می‌خواستید، بادی است که عذاب دردناک در آن نهفته است.');
INSERT INTO "badkhashani" VALUES(46,25,'46:25','همه چیز را به امر پروردگارش نابود می‌کند. پس چنان شدند که جز خانه‌هایشان چیزی دیده نمی‌شد، این چنین مجرمان را سزا می‌دهیم.');
INSERT INTO "badkhashani" VALUES(46,26,'46:26','و البته به آنان چنان امکانات داده بودیم که به شما ندادیم و برای آنها گوش و چشمان و دلها قرار داده بودیم، اما گوشها و چشم‌ها و دل‌هایشان به حال‌شان سودی نبخشید، چرا که آیات الله را انکار می‌کردند و آنچه که به آن تمسخر می‌کردند، آنان را احاطه کرد.');
INSERT INTO "badkhashani" VALUES(46,27,'46:27','و البته قریه‌های را که در اطراف شما قرار داشتند؛ هلاک کردیم و آیات خود را به صورت‌های مختلف بیان نمودیم، تا (از کفر خود) بازگردند.');
INSERT INTO "badkhashani" VALUES(46,28,'46:28','پس چرا کسانیکه (ایشان را) برای تقرب به الله معبود (خود) برگزیده بودند، آنها را یاری نکرد؟ بلکه از نزد آنان محو شدند و این بود (عاقبت) دروغ و افترای‌شان.');
INSERT INTO "badkhashani" VALUES(46,29,'46:29','و یادآور شو وقتی که گروهی از جن را به‌سوی تو روان کردیم تا قرآن را بشنوند، پس وقتی حاضر شدند، گفتند: خاموش باشید پس چون (تلاوت قرآن) تمام شد به‌سوی قومشان بازگشتند تا آنها را بیم دهند.');
INSERT INTO "badkhashani" VALUES(46,30,'46:30','گفتند: ای قوم ما! ما کتابی را شنیدیم که پس از موسی نازل شده است و تصدیق کنندۀ کتاب‌های پیش از خود است، (و) به حق و به راه راست هدایت می‌کند.');
INSERT INTO "badkhashani" VALUES(46,31,'46:31','ای قوم ما! سخنان دعوتگر الهی را بپذیرید و به او ایمان آورید تا الله گناهانتان را بیامرزد و شما را از عذاب دردناک امان دهد.');
INSERT INTO "badkhashani" VALUES(46,32,'46:32','و کسیکه (دعوت) دعوت دهندۀ الله را نپذیرد، پس نمی‌تواند (اسباب غضب الله) در زمین را عاجز کند و برای او جز الله هیچ ولی و یاوری نیست، (بلکه) ایشان در گمراهی آشکارند.');
INSERT INTO "badkhashani" VALUES(46,33,'46:33','آیا ندیده‌اند که الله همان ذاتی است که آسمان‌ها و زمین را آفریده و در آفریدن آنها عاجر و خسته نشده است، قادر است بر آنکه مرده‌ها را زنده کند؟! بلی؛ او بر هر کاری قادر است.');
INSERT INTO "badkhashani" VALUES(46,34,'46:34','و روزی که کافران بر دوزخ عرضه شوند (به آنان گفته می‌شود:) آیا این حق نیست؟ می‌گویند: بلی، قسم به پروردگارما. الله (به ایشان) می‌فرماید: پس به سبب آنکه کفر می‌ورزیدید عذاب را بچشید.');
INSERT INTO "badkhashani" VALUES(46,35,'46:35','(وقتی سزای‌شان چنین باشد) پس صبر کن طوری که پیغمبران اولوالعزم صبر کردند، و برای (عذاب) آنها شتاب مكن. روزی که آنچه را وعده داده می‌شوند، ببینند، گویی (در دنیا) جز ساعتی از روز نمانده‌اند، این (قرآن) ابلاغی است. پس آیا (قومی) جز گروه فاسقان هلاک می‌شود؟');
INSERT INTO "badkhashani" VALUES(47,1,'47:1','آنان که کفر ورزیدند و مردم را از راه (دین) الله منع کردند، (الله) اعمال‌شان را تباه کرد.');
INSERT INTO "badkhashani" VALUES(47,2,'47:2','و آنان که ایمان آوردند و کارهای نیک کردند و به آنچه که بر محمد نازل شده است، ایمان آوردند، چون که آن حق است و از سوی پروردگارشان آمده است، (الله) گناهانشان را از آنان محو می‌کند و حال و وضعشان را )برای کارهای نیک( اصلاح و سامان می‌بخشد.');
INSERT INTO "badkhashani" VALUES(47,3,'47:3','این (دو عاقبت) به آن خاطر است که کافران از باطل پیروی کردند و مؤمنان از همان حقى پیروی کردند که از سوی پروردگارشان آمده است. این چنین الله برای مردم مثال‌های ایشان را بیان می‌دارد.');
INSERT INTO "badkhashani" VALUES(47,4,'47:4','پس وقتی با کافران روبرو شدید گردنهایشان را بزنید، تا آنکه آنها را مغلوب سازید (و چون در دست شما اسیر شوند) پس آنان را محکم ببندید. باز آنان را به احسان یا در بدل فديه رها کنید تا جنگ بارهای سنگین خود را بگذارد و پایان یابد، این است (حكم الله) و اگر الله می‌خواست از آنان انتقام می‌گرفت ولی (می‌خواهد) برخی از شما را با برخی دیگر بیازماید. و کسانی که در راه الله کشته شوند الله هرگز اعمال آنان را ضایع نمی‌کند.');
INSERT INTO "badkhashani" VALUES(47,5,'47:5','به زودی الله آنان را هدایت خواهد کرد و حال و وضعشان را نیکو و اصلاح می‌سازد.');
INSERT INTO "badkhashani" VALUES(47,6,'47:6','و آنان را به جنتی داخل خواهد کرد که به آنان (در قرآن) توصیف کرده است.');
INSERT INTO "badkhashani" VALUES(47,7,'47:7','ای کسانی که ایمان آورده‌اید، اگر الله را یاری و مدد کنید؛ الله (هم) شما را یاری و مدد می‌کند و گام‌هایتان را ثابت و استوار می‌دارد.');
INSERT INTO "badkhashani" VALUES(47,8,'47:8','و آنان که کفر ورزیدند، مرگ و هلاکت بر آنان باد و الله اعمال‌شان را نابود و بی‌اثر گردانید.');
INSERT INTO "badkhashani" VALUES(47,9,'47:9','این (ذلت و هلاکت) به آن خاطر است که آنان آنچه را که الله نازل کرده است، مکروه پنداشتند، پس در نتیجه الله اعمال‌شان را تباه کرد.');
INSERT INTO "badkhashani" VALUES(47,10,'47:10','آیا در زمین سیر نکردند تا ببینند سرانجام کسانی که پیش از ایشان بودند؛ چطور شد؟ الله (همه چیز) آنان را نابود کرد و برای کافران نیز (سزای بد) مانند آن خواهد بود.');
INSERT INTO "badkhashani" VALUES(47,11,'47:11','این (مدد مؤمنان و هلاكت كافران) به این سبب است که الله کارساز مؤمنان است و کافران هیچ کارسازی ندارند.');
INSERT INTO "badkhashani" VALUES(47,12,'47:12','بی‌گمان الله کسانی را که ایمان آورده و کارهای نیک انجام داده‌اند به باغهایی داخل می‌کند که نهرها از زیر (قصرها و درختان) آن روان است. و كافران (در دنیا) برخوردار می‌شوند و می‌خورند (بدون فرق حلال و حرام) طوری که چهارپایان می‌خورند و در آخرت دوزخ جایگاه آنهاست.');
INSERT INTO "badkhashani" VALUES(47,13,'47:13','و چه بسیار (اهالی) قریه‌های که آنها از (اهالی) شهری که تو را از آن بیرون کردند قدرتمندتر بودند و ما آنان را نابود کردیم پس هیچ یار و مددگاری نداشتند.');
INSERT INTO "badkhashani" VALUES(47,14,'47:14','آیا کسی که از سوی پروردگارش بر دليل واضح قرار داشته باشد، مانند کسی است که کردار بدش برای او مزین شده و (ایشان) از هوی و هوس خود پیروی کردند؟');
INSERT INTO "badkhashani" VALUES(47,15,'47:15','صفت جنتی که به پرهیزگاران وعده داده شده (چنین است که) در آن نهرهایی از آب (زلال و خاص) است که بدبو شدنی نیست، و نهرهایی از شیری است که مزه‌اش تغییر نکرده و نهرهایی از شرابی است که برای نوشندگان لذت بخش است و نهرهایی از عسلی است که ناب و خالص است و در آن جا برای آنان هرگونه ميوه است و از آمرزش پروردگارشان برخوردارند. (آیا ایشان) مانند کسانی هستند که در آتش دوزخ دائمی‌اند؟ و از آب جوشان نوشانده می‌شوند که روده‌هایشان را پاره پاره می‌کند.');
INSERT INTO "badkhashani" VALUES(47,16,'47:16','و از آنان (منافقان) کسانی هستند که به تو (در ظاهر) گوش می‌دهند، اما وقتی که از نزد تو بیرون می‌روند به کسانی که به آنان علم و دانش بخشیده شده است (به طور تمسخر) می‌گویند: او هم اکنون چه گفت؟ ایشان کسانی‌اند که الله بر دل‌هایشان مهر نهاده است و از هوی و هوس‌هایشان پیروی کرده‌اند.');
INSERT INTO "badkhashani" VALUES(47,17,'47:17','و کسانی که هدایت یافته‌اند، الله بر هدایت‌شان افزود و اسباب تقوایشان را به آنها داد.');
INSERT INTO "badkhashani" VALUES(47,18,'47:18','پس آیا در انتظار قیامت هستند که ناگهان به آنان بیاید؟ به راستی که هم اکنون علایم و نشانه‌های آن ظاهر شده است پس چون قیامت به سراغشان بیاید، عبرت گرفتن‌شان چه سودی برای آنان خواهد داشت؟');
INSERT INTO "badkhashani" VALUES(47,19,'47:19','پس بدان که معبود به حق جز الله نیست و برای گناهانت و برای مردان و زنان مؤمن آمرزش بخواه و الله گردش و آرامیدن‌ تان را می‌داند.');
INSERT INTO "badkhashani" VALUES(47,20,'47:20','و مؤمنان (آرزوکنان) می‌گویند: چرا سوره‌ای (در بارۀ جهاد) فرستاده نشد؟ پس چون سوره روشن و محکم نازل شود و در آن جنگ ذکر گردد، آنان را که در دل‌هایشان مرض نفاق است می‌بینی مانند کسی به تو نگاه می‌کنند که به سبب مرگ بیهوش شده باشد، پس همان مرگ برایشان بهتر است.');
INSERT INTO "badkhashani" VALUES(47,21,'47:21','فرمانبردارای و گفتار نیک برای آنها بهتر است، پس چون امر (جنگ) قطعی شد، اگر به الله راست گویند (و صادقانه بجنگند) حتما برایشان خیر است.');
INSERT INTO "badkhashani" VALUES(47,22,'47:22','پس آیا اگر روی گردان شوید جز این از شما انتظار نمی‌رود که در زمین فساد کنید و پیوندهای خویشاوندی تان را قطع کنید؟');
INSERT INTO "badkhashani" VALUES(47,23,'47:23','ایشان کسانی‌اند که الله آنها را لعنت کرده است بنابر این گوش‌هایشان را کر و چشم‌هایشان را کور کرده است.');
INSERT INTO "badkhashani" VALUES(47,24,'47:24','آیا در قرآن نمی‌اندیشند یا بر دلها(یشان) قفل‌هایی زده شده است؟');
INSERT INTO "badkhashani" VALUES(47,25,'47:25','البته کسانی که پس از آنکه هدایت برایشان روشن شد به آن پشت کردند، شیطان (کارهایشان را) برای آنها آراسته جلوه داد و آنان را با آرزوهای طولانی فریفته است.');
INSERT INTO "badkhashani" VALUES(47,26,'47:26','این به خاطر آن است که آنان به کسانی که از دستورات نازل شدۀ الله ناخوش بودند، گفتند: در برخی از امور از شما اطاعت خواهیم کرد و الله پنهان کاری آنان را می‌داند.');
INSERT INTO "badkhashani" VALUES(47,27,'47:27','پس (حالشان) چگونه خواهد بود وقتی که فرشته‌ها جان‌هایشان را قبض کنند و روی‌ها و پشت‌های ایشان را می‌زنند؟');
INSERT INTO "badkhashani" VALUES(47,28,'47:28','این (گونه قبض نمودن ارواح‌شان) به آن جهت است که آنان از آنچه که الله را ناراض می‌سازد پیروی کردند و (کسب) رضایت او را ناخوش داشتند، پس (الله) اعمالشان را تباہ و برباد ساخت.');
INSERT INTO "badkhashani" VALUES(47,29,'47:29','آیا کسانی که در دل‌هایشان مرض نفاق است گمان کرده‌اند که الله کینه‌های (پنهانی)‌‌شان را آشکار نخواهد ساخت.');
INSERT INTO "badkhashani" VALUES(47,30,'47:30','و اگر بخواهیم، آنان را یک یک به تو نشان می‌دادیم پس آنان را از سیمایشان می‌شناختی. و حتما آنان را در طرز سخن گفتن می‌شناسی و الله اعمالتان را می‌داند.');
INSERT INTO "badkhashani" VALUES(47,31,'47:31','و بدون شک شما را می‌آزماییم تا مجاهدان و صابران شما را معلوم کنیم و اخبار شما را بیازماییم.');
INSERT INTO "badkhashani" VALUES(47,32,'47:32','یقینا کسانی که پس از این که هدایت بر ایشان روشن شد، کفر ورزیدند و (مردم را) از راه الله باز داشتند و با پیغمبر مخالفت کردند هرگز به الله کم‌ترین زیانی نمی‌رسانند و الله اعمالشان را تباه خواهد کرد.');
INSERT INTO "badkhashani" VALUES(47,33,'47:33','ای کسانی که ایمان آورده‌اید، از الله و پیغمبر اطاعت کنید و کارهای خود را (با مخالفت الله و رسولش) باطل و ضائع نسازید.');
INSERT INTO "badkhashani" VALUES(47,34,'47:34','البته آنانکه کفر ورزیدند و (مردم را) از راه الله باز داشتند باز مردند در حالیکه کافر بودند، پس هرگز الله ایشان را نخواهد بخشید.');
INSERT INTO "badkhashani" VALUES(47,35,'47:35','(وقتی که انجام کفر چنین باشد) پس سست مشوید و به سازش دعوت ندهید، چرا که شما برترید و الله با شماست و هرگز (اجر و پاداش) اعمالتان را کم نمی‌کند.');
INSERT INTO "badkhashani" VALUES(47,36,'47:36','بی‌گمان زندگی دنیا تنها بازی و سرگرمی است. و اگر ایمان بیاورید و پرهیزگاری کنید پاداش‌هایتان را به شما می‌دهد و مال‌هایتان را هم نمی‌خواهد.');
INSERT INTO "badkhashani" VALUES(47,37,'47:37','اگر الله مال‌هایتان را از شما بخواهد و (بر این امر) اصرار بورزد بخل خواهید ورزید و (این درخواست) کینه‌های شما را آشکار می‌کند.');
INSERT INTO "badkhashani" VALUES(47,38,'47:38','آگاه باشید این شما هستید که دعوت داده می‌شوید تا در راه الله انفاق کنید! (و ليكن) بعضی از شما بخل می‌ورزند و هر کس که بخل ورزد در حق خود بخل می‌ورزد. و الله بی‌نیاز است و شما نیازمندانید، و اگر روی بگردانید قوم دیگر را جایگزین شما می‌کند، باز آنان مانند شما (بخيل) نخواهند بود؟');
INSERT INTO "badkhashani" VALUES(48,1,'48:1','البته ما برای تو به فتحی آشکار حکم کردیم.');
INSERT INTO "badkhashani" VALUES(48,2,'48:2','تا (در نتیجه) الله همۀ گناهان گذشته و آیندۀ تو را بیامرزد و نعمتش را بر تو تمام سازد و تو را به راه راست هدایت کند.');
INSERT INTO "badkhashani" VALUES(48,3,'48:3','و تا الله تو را به نصرت قوی و غالب (شکست ناپذیر) مدد کند.');
INSERT INTO "badkhashani" VALUES(48,4,'48:4','اوست ذاتی که در دل مؤمنان سکون و اطمینان را نازل کرد تا ایمانی بر ایمان خود بیافزایند. و لشکرهای آسمان و زمین از الله است و الله دانای باحکمت است.');
INSERT INTO "badkhashani" VALUES(48,5,'48:5','تا مردان و زنان مؤمن را در باغ‌های در آورد که در زیر (درختان و قصرهای) آن نهرها جاری است و همیشه در آن خواهند بود، و اینکه بدی‌هایشان را محو سازد و این در نزد الله کامیابی بزرگ است.');
INSERT INTO "badkhashani" VALUES(48,6,'48:6','و تا مردان و زنان منافق و مردان و زنان مشرک را عذاب دهد که به الله گمان بد دارند، بدیها و بلاها تنها بر ایشان است و الله بر آنان غضب کرده و آنان را لعنت کرده است و دوزخ را برایشان آماده ساخته است که دوزخ بد جایگاه است.');
INSERT INTO "badkhashani" VALUES(48,7,'48:7','و لشکرهای آسمان و زمین از الله می‌باشد و الله غالب با حكمت است.');
INSERT INTO "badkhashani" VALUES(48,8,'48:8','البته ما تو را گواه (بر اعمال) و مژده رسان و بیم دهنده فرستادیم،');
INSERT INTO "badkhashani" VALUES(48,9,'48:9','تا به الله و پیغمبرش ایمان بیاورید و او را مدد کنید و او را بزرگ بدارید. و صبح و شام الله را به پاکی یاد کنید.');
INSERT INTO "badkhashani" VALUES(48,10,'48:10','البته کسانیکه با تو بیعت می‌کردند جز این نیست که با الله بیعت می‌کردند. دست الله بالای دست‌های آنان است، پس هرکس پیمان شکنی کند پس به زیان خود پیمان شکنی می‌کند و هر کس به آنچه که بر آن با الله عهد بسته است وفا کند، پس (الله) به زودی پاداش بزرگی به او خواهد داد.');
INSERT INTO "badkhashani" VALUES(48,11,'48:11','زود است که پس ماندگان عرب‌های بادیه نشین به تو بگویند: اموال و فرزندان ما، ما را مشغول ساخت، پس برای ما آمرزش بخواه، با زبانهایشان چیزی را می‌گویند که در دل‌هایشان نیست، بگو: اگر الله بخواهد به شما زیانی یا نفعی برساند چه کسی در برابر او برای شما اختیار چیزی را دارد؟ بلکه الله به آنچه می‌کنید آگاه است.');
INSERT INTO "badkhashani" VALUES(48,12,'48:12','(مشغول نبودید) بلکه گمان کردید که پیغمبر و مؤمنان هرگز به‌سوی خانواده‌هایشان باز نخواهند گشت و این در دل‌هایتان آراسته شد و گمان بد کردید و مردمان هلاک شده و بدبخت بودید.');
INSERT INTO "badkhashani" VALUES(48,13,'48:13','و هر کس به الله و پیغمبرش ایمان نیاورد پس (بداند که) ما برای کافران آتش شعله ور آماده کرده‌ایم.');
INSERT INTO "badkhashani" VALUES(48,14,'48:14','و فرمانروایی آسمان‌ها و زمین از الله است هر کسی را بخواهد می‌آمرزد و هر کسی را بخواهد عذاب می‌دهد و الله آمرزندۀ مهربان است.');
INSERT INTO "badkhashani" VALUES(48,15,'48:15','زود است همان پس ماندگان (غزوۀ حدیبیه) بگویند، وقتی که شما برای به دست آوردن غنیمتهای (خیبر) روان شوید، بگذارید که همراه شما برویم، می‌خواهند سخن الله را تغییر دهند. بگو: هرگز با ما نخواهید رفت، الله از پیش این چنین (دربارۀ شما) فرموده است، پس خواهند گفت: (الله نگفته است) بلکه شما نسبت به ما حسد می‌ورزید. (مسلمانان حسد نمی‌ورزند) ولی آنان جز اندکی نمی‌فهمند.');
INSERT INTO "badkhashani" VALUES(48,16,'48:16','به پس ماندگان بادیه نشین بگو: به زودی از شما دعوت خواهد شد که به‌سوی قوم جنگجو و قدرتمند بروید، تا با آنان بجنگید، یا اسلام آورند. پس اگر اطاعت کنید، الله پاداش نیک به شما خواهد داد. و اگر روی بگردانید، طوریکه پیش از این روی گرداندید، شما را به عذاب دردناک شکنجه خواهد کرد.');
INSERT INTO "badkhashani" VALUES(48,17,'48:17','بر نابینا گناهی نیست و بر لنگ گناهی نیست و بر بیمار گناهی نیست (که به جهاد نروند) و هرکس از الله و پیغمبرش اطاعت کند، الله او را به باغهایی وارد می‌سازد که از زیر (درختان و قصرهای) آن نهرها روان است. و هرکس روی بگرداند، او را به عذاب دردناک، عذاب خواهد داد.');
INSERT INTO "badkhashani" VALUES(48,18,'48:18','هر آئینه که الله از مؤمنان راضی شد، هنگامیکه زیر درخت با تو بیعت می‌کردند، پس آنچه را که در دل‌هایشان بود دانست، در نتیجه بر آنان سکون و آرامش نازل کرد و به آنان فتح نزدیک را پاداش داد.');
INSERT INTO "badkhashani" VALUES(48,19,'48:19','و غنیمت‌های بسیاری که آن را به دست خواهند آورد، و الله غالب باحکمت است.');
INSERT INTO "badkhashani" VALUES(48,20,'48:20','الله غنيمت‌های بسیار را به شما وعده کرده است که آنها را به دست می‌آورید و این (غنیمت) را زودتر به شما داد و دست تجاوز مردم را از شما باز داشت. تا نشانه‌ای (نصرت الهی) برای مؤمنان باشد و شما را به راه راست هدایت کند.');
INSERT INTO "badkhashani" VALUES(48,21,'48:21','و غنیمتهای دیگری (را نیز نصیب تان خواهد کرد) که هنوز قادر به گرفتن آن نشده‌اید، اما الله بر آن احاطه دارد. و الله بر هر چیزی قادر است.');
INSERT INTO "badkhashani" VALUES(48,22,'48:22','و اگر کافران (در حدیبیه) با شما می‌جنگیدند به یقین پشت‌هایشان را می‌گردانیدند، باز کارساز و مددگاری نمی‌یافتند.');
INSERT INTO "badkhashani" VALUES(48,23,'48:23','این سنت الله است که پیش از این (در مقابل كفار) گذشته است و هرگز برای سنت الله تغییر و تبدیل نخواهی یافت.');
INSERT INTO "badkhashani" VALUES(48,24,'48:24','او همان ذاتی است که در داخل مکه دست کافران را از شما و دست شما را از ایشان بازداشت، بعد از آنکه شما را بر آنان پیروز ساخت و الله به آنچه می‌کنید، بیناست.');
INSERT INTO "badkhashani" VALUES(48,25,'48:25','آنها کسانی هستند که کفر ورزیدند و شما را از مسجد الحرام منع کردند و نگذاشتند قربانی‌هایی نگاه داشته شده به جایگاهش برسد، و اگر مردان و زنان مؤمن (و مستضعف مکه) نبودند که آنان را نمی‌شناسید (و ممکن بود) آنها را پایمال کنید، پس نادانسته دربارۀ آنان رنج و آسیب به شما می‌رسید. (الله هرگز مانع این جنگ نمی‌شد) تا الله هرکسی را بخواهد در رحمت خود در آورد. اگر (مؤمنان و مشرکان) از هم جدا بودند حتما کافران را به عذاب دردناکی گرفتار می‌کردیم.');
INSERT INTO "badkhashani" VALUES(48,26,'48:26','وقتی که کافران تعصب و ننگ جاهلیت را در دل‌هایشان جای دادند، پس الله سکون و آرامش خود را بر پیغمبرش و (نیز) بر مؤمنان نازل کرد و آنان را به کلمۀ تقوا ملزم ساخت و آنان به آن سزاوارتر و اهل آن بودند و الله به هر چیز داناست.');
INSERT INTO "badkhashani" VALUES(48,27,'48:27','البته الله خواب پیغمبر خود را راست (به او نشان داد) و به حق تحقق بخشید که اگر الله بخواهد ایمن و در حالیکه موی سرتان را تراشیده و کوتاه کرده‌اید بدون ترس وارد مسجد الحرام خواهید شد. ولی الله چیزهایی را می‌دانست که شما نمی‌دانستید. پس، پیش از این فتحی نزدیک را مقرر گردانید.');
INSERT INTO "badkhashani" VALUES(48,28,'48:28','اوست ذاتی که پیغمبرش را با هدایت و دین حق فرستاد تا آن را بر همۀ ادیان غالب گرداند و کافی است که الله گواه باشد.');
INSERT INTO "badkhashani" VALUES(48,29,'48:29','محمد فرستادۀ الله است، و کسانی که با او هستند بر کافران سخت‌گیر و در میان خود مهربانند. آنان را در حال رکوع و سجده می‌بینی که فضل و خشنودی الله را می‌جویند. نشانۀ صلاح ایشان از اثر سجده در چهره‌هایشان نمایان است. این وصف آنان در تورات است، و اما توصیف‌شان در انجیل چنین است که مانند کشتزاری هستند که جوانه‌هایش را بیرون آورده و آنها را نیرو داده و محکم نموده، پس بر ساقه‌های خویش راست ایستاده باشد، طوریکه کشاورزان را در تعجب می‌اندازد تا کافران را به سبب آنان خشمگین کند. الله به کسانی از آنان که ایمان آورده و کارهای شایسته انجام داده‌اند آمرزش و پاداش بزرگ وعده داده است.');
INSERT INTO "badkhashani" VALUES(49,1,'49:1','ای کسانی که ایمان آورده‌اید، در مقابل الله و رسولش پیش دستی نکنید، و از الله بترسید، همانا الله شنوای داناست.');
INSERT INTO "badkhashani" VALUES(49,2,'49:2','ای کسانی که ایمان آورده‌اید، صدای خود را از صدای پیغمبر بلند نکنید و طوریکه با یکدیگر بلند گفتگو می‌کنید با پیغمبر به آواز بلند سخن نگویید تا ندانسته اعمالتان بی‌اجر و ضایع نشود.');
INSERT INTO "badkhashani" VALUES(49,3,'49:3','بی‌گمان کسانی که صدای خود را نزد پیغمبر الله پست می‌کنند، آنان کسانی‌اند که الله دل‌هایشان را برای (پذیرش) تقوا آزمایش و آماده کرده است. آمرزش و پاداش بزرگ مخصوص آنهاست.');
INSERT INTO "badkhashani" VALUES(49,4,'49:4','البته آنان که از پشت حجره‌ها تو را با صدای بلند می‌خوانند، بیش ترشان نمی‌فهمند.');
INSERT INTO "badkhashani" VALUES(49,5,'49:5','و اگر آنها صبر می‌کردند تا تو به‌سوی آنان بیرون آیی برایشان بهتر بود و الله آمرزندۀ مهربان است.');
INSERT INTO "badkhashani" VALUES(49,6,'49:6','ای کسانی که ایمان آورده‌اید! اگر فاسقی خبری را برایتان آورد دربارۀ آن تحقیق کنید تا (مبادا) به نادانی به گروهی زیان رسانید پس بر آنچه مرتکب شده‌اید پشیمان شوید.');
INSERT INTO "badkhashani" VALUES(49,7,'49:7','و بدانید که پیغمبر الله در میانتان است، اگر در بسیاری از کارها از شما اطاعت کند به مشقت خواهید افتاد، ولی الله ایمان را در نظرتان محبوب گرداند و آن را در دل‌هایتان زینت بخشید و کفر و فسق و نافرمانی را در نظرتان زشت و ناپسند جلوه داد. اینها‌اند که راه یافته‌اند.');
INSERT INTO "badkhashani" VALUES(49,8,'49:8','(این) به فضل و نعمتی از سوی الله است و الله دانای حکیم است.');
INSERT INTO "badkhashani" VALUES(49,9,'49:9','و اگر دو گروه از مؤمنان به جنگ با یک یگر پرداختند بین آن دو گروه آشتی و صلح برقرار کنید، پس اگر یکی از آن دو گروه بر دیگری تجاوز کرد با آن گروهی که تجاوز می‌کند بجنگید تا این که به حکم الله بر گردد، پس چون بازگشت در میان ایشان عادلانه صلح برقرار سازید و عدالت و انصاف کنید، بی‌گمان الله عادلان را دوست دارد.');
INSERT INTO "badkhashani" VALUES(49,10,'49:10','جز این نیست که مؤمنان با یکدیگر برادراند، پس در میان برادران تان صلح و آشتی برقرار کنید. و از الله بترسید تا مورد رحمت قرار گیرید.');
INSERT INTO "badkhashani" VALUES(49,11,'49:11','ای کسانی که ایمان آورده‌اید! نباید گروهی از شما گروهی دیگری را مسخره کند چه بسا که آنان از ایشان بهتر باشند و نباید زنانی زنانی دیگر را مسخره کنند چه بسا که آنان از ایشان بهتر باشند و در میان تان عیب جویی نکنید و یکدیگر را با لقب‌های زشت مخوانید. چه بد است نام و عنوان فسق بعد از ایمان آوردن! و کسانی که توبه نکنند پس ایشان همان ظالمان‌اند.');
INSERT INTO "badkhashani" VALUES(49,12,'49:12','ای کسانی که ایمان آورده‌اید! از بسیاری از گمانها اجتناب کنید، چون بعضی از گمان‌ها گناه می‌باشد. و جاسوسی مکنید و برخی از شما برخی دیگر را غیبت نکند، آیا کسی از شما دوست دارد که گوشت برادر مرده‌اش را بخورد؟ به يقين که آن را کریه و ناپسند می‌دانید، و از الله بترسید، يقينا الله توبه پذیر مهربان است.');
INSERT INTO "badkhashani" VALUES(49,13,'49:13','ای مردم! ما شما را از مرد و زن آفریدیم و شما را گروه‌ها و قبیله‌ها قرار دادیم تا یکدیگر را بشناسید. بی‌گمان گرامی‌ترین شما نزد الله باتقواترین شماست. همانا الله دانای باخبر است.');
INSERT INTO "badkhashani" VALUES(49,14,'49:14','بادیه نشینان اعراب گفتند: ایمان آورده‌ایم، بگو: شما ایمان نیاورده‌اید، بلکه بگوئید: مسلمان شده‌ایم و ایمان هنوز به دلهایتان وارد نشده است. و اگر از الله و پیغمبرش فرمانبرداری کنید، الله از (پاداش) کارهایتان چیزی نمی‌کاهد، به يقين الله آمرزندۀ مهربان است.');
INSERT INTO "badkhashani" VALUES(49,15,'49:15','مؤمنان (واقعی) تنها کسانی هستند که به الله و پیغمبرش ایمان آورده‌اند، باز هرگز شک به دل راه نداده‌اند، و با مال‌ها و جانهای‌شان در راه الله جهاد کرده‌اند. ایشان (در ایمان خود) همان راستگویانند.');
INSERT INTO "badkhashani" VALUES(49,16,'49:16','بگو: آیا الله را از دینتان آگاه می‌سازید حال آنکه الله آنچه در آسمان‌ها و آنچه در زمین است می‌داند و او به هر چیزی داناست؟');
INSERT INTO "badkhashani" VALUES(49,17,'49:17','بر تو منت و احسان می‌گزارند که اسلام آورده‌اند. بگو: با اسلام خود بر من منت مگذارید بلکه الله بر شما منت می‌گزارد که شما را به‌سوی ایمان هدایت کرده است، اگر راست می‌گوئید.');
INSERT INTO "badkhashani" VALUES(49,18,'49:18','البته الله غيب آسمان‌ها و زمین را می‌داند و الله به آنچه می‌کنید بیناست.');
INSERT INTO "badkhashani" VALUES(50,1,'50:1','ق، (مفهوم این حرف به الله معلوم است)، قسم به قرآن که دارای مجد و بزرگی است.');
INSERT INTO "badkhashani" VALUES(50,2,'50:2','بلکه تعجب کردند از اینکه بیم دهنده‌ای از خودشان به‌سوی آنان آمد. پس کافران گفتند: این (کلامی که از امور غیبی خبر می‌دهد) چیز عجیبی است.');
INSERT INTO "badkhashani" VALUES(50,3,'50:3','آیا وقتی مردیم و خاک شدیم (دوباره زنده می‌شویم؟) چنین برگشت بعید است!');
INSERT INTO "badkhashani" VALUES(50,4,'50:4','يقينا هر چه را که زمین از آنان می‌کاهد، می‌دانیم. و نزد ما کتابی است (که همه چیز را در خود) حفظ می‌کند.');
INSERT INTO "badkhashani" VALUES(50,5,'50:5','بلکه آنان وقتی حق برایشان آمد، آن را دروغ شمردند، پس آنان در کار (بعثت و نبوت) پریشان و حیران هستند.');
INSERT INTO "badkhashani" VALUES(50,6,'50:6','پس آیا به آسمان بالای سرشان نگاه نکرده‌اند که ما چگونه آن را بنا کرده‌ایم و با (ستارگان) آن را مزین نموده‌ایم که در آن هیچ شگافی نیست؟');
INSERT INTO "badkhashani" VALUES(50,7,'50:7','و زمین را هموار کردیم و کوه‌های محکم و استوار را در آن افگندیم و در آن از هر نوع (گیاه) پسندیده رویانیدیم.');
INSERT INTO "badkhashani" VALUES(50,8,'50:8','تا (این‌ها) برای بینش و پند دادن هر بندۀ توبه کننده باشد.');
INSERT INTO "badkhashani" VALUES(50,9,'50:9','و از آسمان آب پر برکت را فرود آوردیم و به وسيلۀ آن باغها و دانه‌های درو شدنی رویانیدیم.');
INSERT INTO "badkhashani" VALUES(50,10,'50:10','و درختان بلند خرما را (رویانیدیم) که آن میوۀ باهم پیوسته دارد.');
INSERT INTO "badkhashani" VALUES(50,11,'50:11','(آن را) برای روزی بندگان (آفریدیم) و با آن سرزمین مرده را زنده کردیم، خروج از قبر (نیز) این طور است.');
INSERT INTO "badkhashani" VALUES(50,12,'50:12','پیش از ایشان قوم نوح و اصحاب «رس» و قوم ثمود (پیغمبرانشان را) تکذیب کردند.');
INSERT INTO "badkhashani" VALUES(50,13,'50:13','و نیز قوم عاد و فرعون (با قومش) و برادران لوط.');
INSERT INTO "badkhashani" VALUES(50,14,'50:14','و اصحاب ایکه و قوم تُبّع هر یک پیغمبران را تکذیب کردند و وعدۀ عذاب من دربارۀ ایشان قطعی شد');
INSERT INTO "badkhashani" VALUES(50,15,'50:15','آیا ما از آفریدن اول درمانده و ناتوان شدیم؟ (نه) بلکه آنان دربارۀ آفرینش جدید شک و تردید دارند.');
INSERT INTO "badkhashani" VALUES(50,16,'50:16','و البته ما انسان را آفریدیم و می‌دانیم آنچه را که نفسش به او وسوسه می‌کند و ما از شاهرگ گردن به او نزدیکتریم.');
INSERT INTO "badkhashani" VALUES(50,17,'50:17','وقتی که دو فرشته (ملازم انسان) از راست و چپ (به مراقبت) نشسته (اعمال انسان را) دریافت می‌کنند.');
INSERT INTO "badkhashani" VALUES(50,18,'50:18','هیچ سخنی را بر زبان نمی‌آورد مگر آنکه نزدش نگهبان آماده (حضور) دارد.');
INSERT INTO "badkhashani" VALUES(50,19,'50:19','و (وقتی) بیهوشی و سختی مرگ به راستی بیاید، (به انسان گفته می‌شود:) این همان چیزی است که از آن می‌گریختی.');
INSERT INTO "badkhashani" VALUES(50,20,'50:20','و در صور دمیده می‌شود، این روز (تحقق) وعدۀ عذاب است.');
INSERT INTO "badkhashani" VALUES(50,21,'50:21','و هر کس در حالی (به میدان محشر) می‌آید که سوق دهنده و گواهی، با خود دارد.');
INSERT INTO "badkhashani" VALUES(50,22,'50:22','يقينا از این (روز) در غفلت بودی، پس پردۀ غفلت را از دیده ات برداشتیم و امروز چشمانت تیز شده است.');
INSERT INTO "badkhashani" VALUES(50,23,'50:23','و (فرشتۀ) همراهش گوید: این است آنچه در نزد من آماده است.');
INSERT INTO "badkhashani" VALUES(50,24,'50:24','هر کافر سرکشی را به دوزخ بیاندازید.');
INSERT INTO "badkhashani" VALUES(50,25,'50:25','آن که مانع هر خیر (و) تجاوزگر شک انداز است.');
INSERT INTO "badkhashani" VALUES(50,26,'50:26','آن که معبود دیگری را با الله مقرر کرده بود، پس او را در عذاب سخت (دوزخ) بیاندازید.');
INSERT INTO "badkhashani" VALUES(50,27,'50:27','(شیطان) هم نشین او می‌گوید: ای پروردگار ما! من او را گمراه نساخته بودم، بلکه (خود او) در گمراهی دور از حق بود.');
INSERT INTO "badkhashani" VALUES(50,28,'50:28','الله می‌فرماید: نزد من با یکدیگر مشاجره مکنید و بی‌گمان (پیش از این) برایتان بیم و وعدۀ عذاب داده بودم.');
INSERT INTO "badkhashani" VALUES(50,29,'50:29','سخن (فيصله شده) در نزد من تغییر داده نمی‌شود و من هرگز به بندگان ظالم نیستم.');
INSERT INTO "badkhashani" VALUES(50,30,'50:30','روزی که به دوزخ بگوییم: آیا پر شده ای؟ و (دوزخ) می‌گوید: آیا افزون بر این هم هست؟');
INSERT INTO "badkhashani" VALUES(50,31,'50:31','و جنت برای پرهیزگاران نزدیک کرده شود و از آنان دور نخواهد بود.');
INSERT INTO "badkhashani" VALUES(50,32,'50:32','گفته شود: این است آنچه به شما وعده داده می‌شد، که برای هر توبه‌کار و حافظ (حدود الهی) است.');
INSERT INTO "badkhashani" VALUES(50,33,'50:33','برای کسی که در نهان از پروردگار مهربان بترسد و با دل توبه کار پیش (به ‌سوی الله) آید.');
INSERT INTO "badkhashani" VALUES(50,34,'50:34','به سلامت وارد جنت شوید. این است روز زندگی دائمی.');
INSERT INTO "badkhashani" VALUES(50,35,'50:35','آنان در آنجا هرچه بخواهند دارند و نزد ما افزون (بر خواهشات آنها) است.');
INSERT INTO "badkhashani" VALUES(50,36,'50:36','و پیش از آنان اقوام زیادی را هلاک ساختیم که از ایشان نیرومندتر بودند و در شهرها سیر کردند و آنرا گشودند، اما آیا برای آنها راه گریزی بود؟');
INSERT INTO "badkhashani" VALUES(50,37,'50:37','و يقينا در این عبرت است برای کسی که دل (آگاه) داشته باشد و با حضور قلب (به سخنان) گوش فرا دهد.');
INSERT INTO "badkhashani" VALUES(50,38,'50:38','و البته آسمان‌ها و زمین و آنچه را که در بین آن دو است، در شش روز آفریدیم و هیچ خستگی به ما نرسید.');
INSERT INTO "badkhashani" VALUES(50,39,'50:39','پس بر آنچه می‌گویند صبر کن و پیش از طلوع خورشید و پیش از غروب آن به ستایش پروردگارت تسبیح گوی.');
INSERT INTO "badkhashani" VALUES(50,40,'50:40','و در بخشی از شب و در عقب نمازها نیز او را به پاکی یاد کن.');
INSERT INTO "badkhashani" VALUES(50,41,'50:41','و گوش به (زنگ) روزی باش که ندادهنده از مکان نزدیک ندا کند.');
INSERT INTO "badkhashani" VALUES(50,42,'50:42','روزی که بانگ مرگبار حقیقی را می‌شنوند، آن روز، روز بیرون آمدن (از قبرها) است.');
INSERT INTO "badkhashani" VALUES(50,43,'50:43','البته ما هستیم که زنده می‌کنیم و می‌میرانیم و بازگشت به‌سوی ماست.');
INSERT INTO "badkhashani" VALUES(50,44,'50:44','روزی که زمین به سرعت از (روی) آنان می‌شگافد؛ این جمع کردنی است که بر ما آسان است.');
INSERT INTO "badkhashani" VALUES(50,45,'50:45','ما به آنچه می‌گویند داناتریم و تو مأمور به اجبار مردم (به ایمان آوردن) نیستی. پس هر کسی را که از وعدۀ (عذاب) می‌ترسد به (وسیلۀ) قرآن پند ده.');
INSERT INTO "badkhashani" VALUES(51,1,'51:1','قسم به بادهایی که (هر چیز را) با شدت پراگنده می‌کنند.');
INSERT INTO "badkhashani" VALUES(51,2,'51:2','قسم به ابرهای گران بار.');
INSERT INTO "badkhashani" VALUES(51,3,'51:3','قسم به کشتی‌های که به آرامی می‌روند.');
INSERT INTO "badkhashani" VALUES(51,4,'51:4','و قسم به فرشتگانی که کارها را (به امر الله) تقسیم می‌کنند.');
INSERT INTO "badkhashani" VALUES(51,5,'51:5','بی‌گمان آنچه که وعده داده می‌شوید راست است.');
INSERT INTO "badkhashani" VALUES(51,6,'51:6','و حتما روز جزا (قیامت) واقع شدنی است.');
INSERT INTO "badkhashani" VALUES(51,7,'51:7','و قسم به آسمان که دارای راههای مختلف است.');
INSERT INTO "badkhashani" VALUES(51,8,'51:8','همانا شما در سخنان گوناگون سرگردان هستید.');
INSERT INTO "badkhashani" VALUES(51,9,'51:9','کسی که از آن (راه راست) منحرف شود (در آینده نیز از راه راست) باز گردانیده می‌شود.');
INSERT INTO "badkhashani" VALUES(51,10,'51:10','مرگ بر دروغگویان (که بدون دلیل و برهان در بارۀ قرآن و قیامت سخن می‌گویند).');
INSERT INTO "badkhashani" VALUES(51,11,'51:11','کسانی که ایشان در نادانی و بی‌خبری غرقند.');
INSERT INTO "badkhashani" VALUES(51,12,'51:12','می‌پرسند: روز جزا چه وقتی خواهد بود؟');
INSERT INTO "badkhashani" VALUES(51,13,'51:13','روزی که آنان بر آتش عذاب داده شوند.');
INSERT INTO "badkhashani" VALUES(51,14,'51:14','(گفته شود) بچشید عذاب تان را، این همان عذابی است که در بارۀ آن عجله داشتید.');
INSERT INTO "badkhashani" VALUES(51,15,'51:15','البته پرهیزگاران در باغها و چشمه سارها خواهند بود.');
INSERT INTO "badkhashani" VALUES(51,16,'51:16','چیزهایی را که پروردگارشان به آنان می‌دهد بدست می‌آرند. چون آنان پیش از این (در دنیا) نیکوکار بودند.');
INSERT INTO "badkhashani" VALUES(51,17,'51:17','آنان بودند که اندکی از شب را می‌خوابیدند.');
INSERT INTO "badkhashani" VALUES(51,18,'51:18','و در سحرگاهان (از پروردگارشان) آمرزش می‌خواستند.');
INSERT INTO "badkhashani" VALUES(51,19,'51:19','و در اموالشان برای گدا و بینوا حقی بود.');
INSERT INTO "badkhashani" VALUES(51,20,'51:20','و در زمین برای یقین کنندگان نشانه‌هایی (قدرت الهی) است.');
INSERT INTO "badkhashani" VALUES(51,21,'51:21','و در وجودتان (نشانه‌هایی قدرت الهی است) آیا نمی‌بینید؟');
INSERT INTO "badkhashani" VALUES(51,22,'51:22','و روزی شما و آنچه وعده داده می‌شوید، در آسمان (مقرر) است.');
INSERT INTO "badkhashani" VALUES(51,23,'51:23','به پروردگار آسمان و زمین قسم که البته این (وعده) حق است، مانند آنکه شما سخن می‌گویید.');
INSERT INTO "badkhashani" VALUES(51,24,'51:24','آیا داستان مهمانان گرامی ابراهیم به تو رسیده است؟');
INSERT INTO "badkhashani" VALUES(51,25,'51:25','وقتی که بر او وارد شدند پس گفتند: سلام (بر تو، و ابراهیم نیز) گفت: سلام (بر شما، و در دل گفت:) مردمان ناشناس هستید.');
INSERT INTO "badkhashani" VALUES(51,26,'51:26','پس آهسته به‌سوی خانواده‌اش رفت و گوساله فربه (و بریان شده) آورد.');
INSERT INTO "badkhashani" VALUES(51,27,'51:27','پس آن را نزدیک آنان نهاد (و) گفت: آیا نمی‌خورید؟!');
INSERT INTO "badkhashani" VALUES(51,28,'51:28','و (در دل) از ایشان احساس ترس کرد. گفتند: مترس، و او را به پسر دانا مژده دادند.');
INSERT INTO "badkhashani" VALUES(51,29,'51:29','پس همسرش (با شنیدن مژده فرزند) فریادکنان پیش آمد، و بر چهرۀ خود زد و گفت: پیرزنی نازا هستم (چگونه فرزندی به دنیا بیاورم؟!).');
INSERT INTO "badkhashani" VALUES(51,30,'51:30','فرشته‌ها گفتند: پروردگار تو این چینن گفته است، چون او باحکمتِ داناست.');
INSERT INTO "badkhashani" VALUES(51,31,'51:31','(ابراهیم) گفت: ای فرستادگان کار شما چیست؟.');
INSERT INTO "badkhashani" VALUES(51,32,'51:32','گفتند: همانا ما به‌سوی قوم مجرم و گناهکار فرستاده شده‌ایم.');
INSERT INTO "badkhashani" VALUES(51,33,'51:33','تا بر آنان سنگ‌هایی از گل بریزیم.');
INSERT INTO "badkhashani" VALUES(51,34,'51:34','که نزد پروردگارت برای (نابودی) اسرافکاران، نشان‌کرده شده است.');
INSERT INTO "badkhashani" VALUES(51,35,'51:35','پس هر کس از مؤمنان را که در آنجا بودند، بیرون کردیم.');
INSERT INTO "badkhashani" VALUES(51,36,'51:36','پس در آنجا جز یک خانه‌ای از مسلمانان نیافتیم.');
INSERT INTO "badkhashani" VALUES(51,37,'51:37','و در آن قریه برای کسانی که از عذاب دردناک می‌ترسند، نشانه‌ای )برای عبرت( گذاشتیم.');
INSERT INTO "badkhashani" VALUES(51,38,'51:38','و در قصۀ موسی (نیز نشانه‌ای هست) وقتی که او را با دلیلی آشکار به‌سوی فرعون فرستادیم،');
INSERT INTO "badkhashani" VALUES(51,39,'51:39','پس با لشكر خویش روی گردان شد و گفت: او جادوگر یا دیوانه است.');
INSERT INTO "badkhashani" VALUES(51,40,'51:40','پس ما او و لشکریانش را (به قهر خود) گرفتار کردیم و آنها را در دریا انداختیم در حالیکه او سزاوار سرزنش بود.');
INSERT INTO "badkhashani" VALUES(51,41,'51:41','و در (ماجرای) عاد (نیز عبرتهایی است) وقتی تند باد بی‌خیر و برکت را بر آنان فرستادیم.');
INSERT INTO "badkhashani" VALUES(51,42,'51:42','بر هر چیزی که می‌وزید آن را باقی نمی‌گذاشت مگر اینکه آن را چون استخوان پوسیده می‌گردانید.');
INSERT INTO "badkhashani" VALUES(51,43,'51:43','و در قصۀ قوم ثمود نیز علامت عبرت گذاشتیم، وقتی که به ایشان گفته شد: تا مدتی (از نعمت‌های دنیا) مستفید شوید.');
INSERT INTO "badkhashani" VALUES(51,44,'51:44','پس از حکم پروردگارشان سرکشی کردند در نتیجه صاعقه (حادثۀ بیهوش کننده) ایشان را فرا گرفت در حالیکه نگاه می‌کردند.');
INSERT INTO "badkhashani" VALUES(51,45,'51:45','پس نه توان برخواستن را داشتند و نه توانستند از کسی کمک بطلبند.');
INSERT INTO "badkhashani" VALUES(51,46,'51:46','و پیش از آنها قوم نوح را هلاک نمودیم؛ چون آنها قوم فاسق بودند.');
INSERT INTO "badkhashani" VALUES(51,47,'51:47','و آسمان را با قوت و توانمندی بنا نمودیم و يقينا ما وسعت دهنده‌ایم.');
INSERT INTO "badkhashani" VALUES(51,48,'51:48','و زمین را هموار کردیم پس چه خوب هموار کننده‌ایم.');
INSERT INTO "badkhashani" VALUES(51,49,'51:49','و از هر چیزی دو نوع (جفت) آفریدیم تا شما پند گیرید.');
INSERT INTO "badkhashani" VALUES(51,50,'51:50','پس (از شرک) به‌سوی (توحید) الله بگریزید، البته من از سوی او برایتان بیم دهندۀ آشکار هستم.');
INSERT INTO "badkhashani" VALUES(51,51,'51:51','و با الله معبود دیگر قرار ندهید، همانا من از سوی او برایتان بیم دهندۀ آشکار هستم.');
INSERT INTO "badkhashani" VALUES(51,52,'51:52','هم چنین هیچ پیغمبری به‌سوی مردمان پیش از ایشان نیامده است مگر اینکه گفتند: جادوگر یا دیوانه است.');
INSERT INTO "badkhashani" VALUES(51,53,'51:53','آیا یکدیگر را (به گفتن چنین سخنی) سفارش کرده‌اند؟! (نه) بلکه آنان قوم سرکش‌اند.');
INSERT INTO "badkhashani" VALUES(51,54,'51:54','لذا از آنان روی بگردان چون تو سزاوار نکوهش و ملامتی نیستی.');
INSERT INTO "badkhashani" VALUES(51,55,'51:55','و (به مردم) پند ده یقینا که پند دادن به مؤمنان نفع می‌رساند.');
INSERT INTO "badkhashani" VALUES(51,56,'51:56','و من جن و انس را نیافریدم مگر برای آنکه مرا عبادت کنند.');
INSERT INTO "badkhashani" VALUES(51,57,'51:57','من از آنها هیچ رزقی نمی‌خواهم. و نمی‌خواهم که به من طعام دهند.');
INSERT INTO "badkhashani" VALUES(51,58,'51:58','چون تنها الله روزی رسان و صاحب قدرت و نیرومند است.');
INSERT INTO "badkhashani" VALUES(51,59,'51:59','همانا برای کسانی که ظلم کردند نصیبی (از عذاب) مانند نصیب یارانشان (و هم مسلکان‌شان) دارند، پس نباید )عذاب را( به شتاب (از من) بخواهند.');
INSERT INTO "badkhashani" VALUES(51,60,'51:60','پس وای بر کافران از آن روزشان که وعده داده می‌شوند.');
INSERT INTO "badkhashani" VALUES(52,1,'52:1','قسم به کوه طور.');
INSERT INTO "badkhashani" VALUES(52,2,'52:2','و به کتاب نوشته شده.');
INSERT INTO "badkhashani" VALUES(52,3,'52:3','در ورق گشوده.');
INSERT INTO "badkhashani" VALUES(52,4,'52:4','و قسم به خانۀ معمور و آباد (در آسمان که به عبادت همیشگی ملایکه،‌ آباد است).');
INSERT INTO "badkhashani" VALUES(52,5,'52:5','و قسم به سقف بلند (آسمان).');
INSERT INTO "badkhashani" VALUES(52,6,'52:6','و قسم به بحر شعله ور و افروخته شده!');
INSERT INTO "badkhashani" VALUES(52,7,'52:7','که عذاب پروردگارت حتما واقع شدنی است.');
INSERT INTO "badkhashani" VALUES(52,8,'52:8','و برای آن هیچ دفع کننده‌ای نیست.');
INSERT INTO "badkhashani" VALUES(52,9,'52:9','روزی که آسمان به شدت پیچیده شود و بچرخد.');
INSERT INTO "badkhashani" VALUES(52,10,'52:10','کوه‌ها به شتاب روان شوند.');
INSERT INTO "badkhashani" VALUES(52,11,'52:11','پس در آن روز، وای بر تکذیب کنندگان.');
INSERT INTO "badkhashani" VALUES(52,12,'52:12','کسانی که در کفر و بیهوده گویی سرگرمند.');
INSERT INTO "badkhashani" VALUES(52,13,'52:13','روزی که به سختی به‌سوی آتش دوزخ رانده شوند.');
INSERT INTO "badkhashani" VALUES(52,14,'52:14','(گفته می‌شود) این همان دوزخی است که آن را دروغ می‌شمردید.');
INSERT INTO "badkhashani" VALUES(52,15,'52:15','آیا این جادو است یا این که شما نمی‌بینید؟.');
INSERT INTO "badkhashani" VALUES(52,16,'52:16','به آن وارد شوید، چه صبر کنید یا صبر نکنید بر شما برابر است، جز این نیست که در برابر آنچه می‌کردید جزا داده می‌شوید.');
INSERT INTO "badkhashani" VALUES(52,17,'52:17','البته پرهیزگاران در باغها و ناز و نعمت خواهند بود.');
INSERT INTO "badkhashani" VALUES(52,18,'52:18','به آنچه که پروردگارشان به آنان داده است شاد و مسرور‌اند، و پروردگارشان آنان را از عذاب دوزخ محفوظ و مصئون داشته است.');
INSERT INTO "badkhashani" VALUES(52,19,'52:19','(گفته شود) به پاداش کارهایی که کرده‌اید، گوارا بخورید و بنوشید.');
INSERT INTO "badkhashani" VALUES(52,20,'52:20','بر تختهای که کنار هم چیده شده تکیه کرده‌اند و زنان زیبا و سیاه چشم را همسران‌شان می‌گردانیم.');
INSERT INTO "badkhashani" VALUES(52,21,'52:21','و آنان که ایمان آوردند و فرزندانشان نیز در ایمان آوردن از آنان پیروی کرده‌اند، فرزندانشان را به آنان ملحق می‌کنیم و از عملشان چیزی نمی‌کاهیم. هر شخص در گرو کاری است که کسب کرده است.');
INSERT INTO "badkhashani" VALUES(52,22,'52:22','و آن‌ها را با میوه و گوشت از هر نوع که بخواهند، مدد می‌کنیم.');
INSERT INTO "badkhashani" VALUES(52,23,'52:23','در آنجا جامی را از دست هم دیگر می‌گیرند که در (نوشیدن) آن هیچ بیهودگی و گناهی نیست.');
INSERT INTO "badkhashani" VALUES(52,24,'52:24','و نوجوانانی همچون مروارید در صدف، برای (خدمت آنان) بر گردشان می‌گردند.');
INSERT INTO "badkhashani" VALUES(52,25,'52:25','و رو به یکدیگر نموده و از همدیگر می‌پرسند (که سبب کامیابی ما چیست؟).');
INSERT INTO "badkhashani" VALUES(52,26,'52:26','گویند: البته ما پیش از این در میان اهل خویش، تنها از الله می‌ترسیدیم.');
INSERT INTO "badkhashani" VALUES(52,27,'52:27','پس الله بر ما منت گذاشت و ما را از عذاب سوزان نگه داشت.');
INSERT INTO "badkhashani" VALUES(52,28,'52:28','چون ما بودیم پیش از این که الله را (در مشکلات خود) می‌خواندیم واقعا که او (بر بندگان مؤمن خود) احسان کننده و مهربان است.');
INSERT INTO "badkhashani" VALUES(52,29,'52:29','پس (به مردم) پند بده که تو به فضل پروردگارت کاهن و دیوانه نیستی.');
INSERT INTO "badkhashani" VALUES(52,30,'52:30','یا می‌گویند: (محمد) شاعری است و ما منتظر رسیدن مرگ ناخوش او هستیم؟');
INSERT INTO "badkhashani" VALUES(52,31,'52:31','بگو: انتظار بکشید که من هم با شما از منتظرانم.');
INSERT INTO "badkhashani" VALUES(52,32,'52:32','آیا عقل‌هایشان آنها را به این )سخنان( دستور می‌دهد یا آنها قوم سرکش‌اند؟!');
INSERT INTO "badkhashani" VALUES(52,33,'52:33','یا می‌گویند: قرآن را خود (محمد) ساخته (و به الله نسبت داده است)؟ نه، بلکه آنها ایمان نمی‌آورند.');
INSERT INTO "badkhashani" VALUES(52,34,'52:34','اگر راست می‌گویند پس سخنی مانند آن را بیاورند.');
INSERT INTO "badkhashani" VALUES(52,35,'52:35','آیا بدون هیچ خالقی آفریده شده‌اند یا خود آفریننده خود ‌اند؟');
INSERT INTO "badkhashani" VALUES(52,36,'52:36','یا آسمان‌ها و زمین را آفریده‌اند؟ نه، بلکه (حق این است که) يقين نمی‌کنند.');
INSERT INTO "badkhashani" VALUES(52,37,'52:37','آیا نزد آنها خزانه‌های پروردگار توست؟ یا اینکه (بر عالم) سیطره دارند؟');
INSERT INTO "badkhashani" VALUES(52,38,'52:38','آیا زینه‌ای دارند که به وسیلۀ آن (اسرار آسمان‌ها را) می‌شنوند؟ (اگر چنین است) پس باید شنوندۀ آنان دلیل آشکار بیاورد.');
INSERT INTO "badkhashani" VALUES(52,39,'52:39','آیا دختران سهم الله است و پسران سهم شما؟');
INSERT INTO "badkhashani" VALUES(52,40,'52:40','یا از آنها مزد می‌طلبی که پرداخت آن بر آنها دشوار است؟');
INSERT INTO "badkhashani" VALUES(52,41,'52:41','یا علم غیب نزد آنهاست که (هر چه بخواهند) می‌نویسند؟');
INSERT INTO "badkhashani" VALUES(52,42,'52:42','یا می‌خواهند حیله و مکری را (به خلاف پیغمبر) بکار برند؟ پس (بدانند که) کافران خود گرفتار حیله و مکر خواهند شد.');
INSERT INTO "badkhashani" VALUES(52,43,'52:43','آیا برای آنها معبودی غیر از الله هست؟ الله پاک و منزه است از آنچه برای او شریک قرار می‌دهند.');
INSERT INTO "badkhashani" VALUES(52,44,'52:44','اگر بینند که قطعه‌ای از آسمان می‌افتد (باز هم ایمان نمی‌آورند)، می‌گویند: ابری متراکم است (نه غضب الهی).');
INSERT INTO "badkhashani" VALUES(52,45,'52:45','پس آنها را (به حال خود‌شان) بگذار، تا آن روزشان را ببینند که در آن گرفتار صاعقه (عذاب الهی) شوند.');
INSERT INTO "badkhashani" VALUES(52,46,'52:46','روزی که حیلۀ‌شان چیزی (از عذاب الله) را از آنان دفع نمی‌کند و مدد و یاری نمی‌شوند.');
INSERT INTO "badkhashani" VALUES(52,47,'52:47','و البته برای آنان که ظلم کرده‌اند عذابی است غیر از این عذاب (دنیا و برزخ)، ولی بیشترشان نمی‌دانند. ­');
INSERT INTO "badkhashani" VALUES(52,48,'52:48','و بر (رساندن) حکم پروردگارت صبر کن که تو زیر نظر ما هستی. و هنگامی که (از خواب) برخواستی، با ستایش پروردگارت را تسبیح گوی.');
INSERT INTO "badkhashani" VALUES(52,49,'52:49','و در بخشی از شب و (نیز) بعد از پنهان شدن ستارگان، او را به پاکی یاد کن.');
INSERT INTO "badkhashani" VALUES(53,1,'53:1','قسم به ستاره وقتی پنهان شود.');
INSERT INTO "badkhashani" VALUES(53,2,'53:2','(که) هم صحبت شما نه گمراه شده و نه منحرف گشته است.');
INSERT INTO "badkhashani" VALUES(53,3,'53:3','و از روی هوی و هوس سخن نمی‌گوید.');
INSERT INTO "badkhashani" VALUES(53,4,'53:4','نیست این کلام مگر پیغام الهی که به او وحی می‌شود.');
INSERT INTO "badkhashani" VALUES(53,5,'53:5','(فرشتۀ) بس نیرومند آن را به او آموخته است.');
INSERT INTO "badkhashani" VALUES(53,6,'53:6','(فرشتۀ نیرومند برای رساندن و حی) راست و برابر ایستاد.');
INSERT INTO "badkhashani" VALUES(53,7,'53:7','در حالیکه او بر کنارۀ بلند آسمان بود.');
INSERT INTO "badkhashani" VALUES(53,8,'53:8','باز نزدیک شد، پس فرود آمد.');
INSERT INTO "badkhashani" VALUES(53,9,'53:9','تا جایی که فاصله‌اش به اندازۀ دو کمان یا کمتر گردید.');
INSERT INTO "badkhashani" VALUES(53,10,'53:10','پس (الله) آنچه را باید وحی می‌کرد (توسط آن فرشته) به بنده‌اش وحی کرد.');
INSERT INTO "badkhashani" VALUES(53,11,'53:11','دل (پیغمبر) آنچه را دید دروغ نشمرد.');
INSERT INTO "badkhashani" VALUES(53,12,'53:12','آیا با او دربارۀ چیزی که می‌بیند جدال می‌کنید؟');
INSERT INTO "badkhashani" VALUES(53,13,'53:13','و البته او (فرشته) را بار دیگر در فرود آمدنش هم دید.');
INSERT INTO "badkhashani" VALUES(53,14,'53:14','نزد سدرة المنتهى (درختی که علم مخلوقات به آن منتهی می‌شود).');
INSERT INTO "badkhashani" VALUES(53,15,'53:15','که آرامگاه متقیان در بهشت نزد آن درخت است.');
INSERT INTO "badkhashani" VALUES(53,16,'53:16','وقتی که (درخت) سدره را پوشاند آنچه که می‌پوشانید.');
INSERT INTO "badkhashani" VALUES(53,17,'53:17','چشم (پیغمبر) منحرف نشد و از حد تجاوز نکرد.');
INSERT INTO "badkhashani" VALUES(53,18,'53:18','البته او بخشی از نشانه‌های بزرگ پروردگار خود را مشاهده کرد.');
INSERT INTO "badkhashani" VALUES(53,19,'53:19','آیا (بت‌های) «لات» و «عزی» را دیده‌اید؟');
INSERT INTO "badkhashani" VALUES(53,20,'53:20','و «منات» آن بت سومی دیگر را؟');
INSERT INTO "badkhashani" VALUES(53,21,'53:21','آیا (گمان دارید که) پسر برای شما است و دختر برای او (الله)؟');
INSERT INTO "badkhashani" VALUES(53,22,'53:22','در این صورت این تقسیم ظالمانه است.');
INSERT INTO "badkhashani" VALUES(53,23,'53:23','این بتها جز نام‌هایی بیش نیستند که شما و پدرانتان آنرا نام نهاده‌اید که الله دربارۀ (حقانیت) آن دلیلی نازل نکرده است؟! جز از گمان و خواهشات نفس پیروی نمی‌کنند در حالیکه از (سوی) پروردگارشان برای آنان هدایت آمده است.');
INSERT INTO "badkhashani" VALUES(53,24,'53:24','آیا (گمان دارند که) انسان به هر چه آرزو کند می‌رسد؟');
INSERT INTO "badkhashani" VALUES(53,25,'53:25','پس آخرت و دنیا برای الله است.');
INSERT INTO "badkhashani" VALUES(53,26,'53:26','و چه بسا فرشتگانی در آسمانهاست (ولی) شفاعتشان سودی ندارد مگر بعد از آنکه الله برای هرکس که بخواهد و راضی شود، اجازه شفاعت دهد.');
INSERT INTO "badkhashani" VALUES(53,27,'53:27','البته آنان که به روز آخرت ایمان ندارند، فرشته‌ها را بنام دختران نام گذاری می‌کنند.');
INSERT INTO "badkhashani" VALUES(53,28,'53:28','در حالیکه آنان به آن هیچ علمی ندارند، جز از گمان پیروی نمی‌کنند و به یقین گمان، انسان را از (شناخت) حق بی‌نیاز نمی‌کند.');
INSERT INTO "badkhashani" VALUES(53,29,'53:29','لذا، از کسی روی بگردان که از یاد ما روی گردان شده و جز زندگی دنیوی نمی‌خواهد.');
INSERT INTO "badkhashani" VALUES(53,30,'53:30','این مُنتهای علم و شناخت آنان است، بی‌گمان پروردگارت به کسیکه از راه او گمراه شده داناتر است و او به کسیکه راهیاب شده داناتر می‌باشد.');
INSERT INTO "badkhashani" VALUES(53,31,'53:31','و آنچه در آسمان‌ها و زمین است برای الله است تا کسانی را که بدی کرده‌اند به (سزای) آنچه کرده‌اند جزا دهد و تا نیکوکاران را به بهترین وجه پاداش دهد.');
INSERT INTO "badkhashani" VALUES(53,32,'53:32','آنانی که از گناهان کبیره و زشتی‌ها جز گناهان صغيره اجتناب می‌کنند (بدانند که) البته پروردگارت (نسبت به آنان) آمرزشش وسیع است. او به (حال) شما وقتیکه شما را از زمین آفرید و هنگامی که شما در شکم‌های مادرانتان جنينها بودید داناتر است. پس از پاک بودن خود سخن مگویید. او به پرهیزگاران داناتر است.');
INSERT INTO "badkhashani" VALUES(53,33,'53:33','پس آیا دیدی آن شخصی را که اعراض کرد؟');
INSERT INTO "badkhashani" VALUES(53,34,'53:34','و اندکی (از مال) داد و سنگ دل شد (و از دادن آن دست کشید).');
INSERT INTO "badkhashani" VALUES(53,35,'53:35','آیا او علم غیب دارد پس او (همه چیز را) می‌بیند؟.');
INSERT INTO "badkhashani" VALUES(53,36,'53:36','یا به آنچه که در صحیفه‌های موسی آمده، خبر داده نشده است؟.');
INSERT INTO "badkhashani" VALUES(53,37,'53:37','و (نیز به آنچه در صحیفه‌های) ابراهیم که (در عهد خود با الله) وفا کرد.');
INSERT INTO "badkhashani" VALUES(53,38,'53:38','(مضمون همۀ کتب آسمانی این بود که) هیچ کس بار گناه دیگری را حمل نمی‌کند.');
INSERT INTO "badkhashani" VALUES(53,39,'53:39','و اینکه برای انسان جز آنچه تلاش کرده (نصیب دیگر) نیست.');
INSERT INTO "badkhashani" VALUES(53,40,'53:40','و اینکه تلاش و سعی او به زودی دیده خواهد شد.');
INSERT INTO "badkhashani" VALUES(53,41,'53:41','باز جزای کافی به او داده خواهد شد.');
INSERT INTO "badkhashani" VALUES(53,42,'53:42','و اینکه انتهای همه چیز به‌سوی پروردگار توست.');
INSERT INTO "badkhashani" VALUES(53,43,'53:43','و اینکه اوست که می‌خنداند و می‌گریاند.');
INSERT INTO "badkhashani" VALUES(53,44,'53:44','و اینکه اوست که می‌میراند و زنده می‌کند.');
INSERT INTO "badkhashani" VALUES(53,45,'53:45','و اوست که دو زوج نر و ماده را آفرید.');
INSERT INTO "badkhashani" VALUES(53,46,'53:46','از نطفه‌ای که (در رحم) ریخته می‌شود.');
INSERT INTO "badkhashani" VALUES(53,47,'53:47','و بر (عهدۀ) اوست آفریدن دوباره.');
INSERT INTO "badkhashani" VALUES(53,48,'53:48','اوست که غنی می‌سازد و راضی می‌کند.');
INSERT INTO "badkhashani" VALUES(53,49,'53:49','و اوست پروردگار ستارۀ شعری.');
INSERT INTO "badkhashani" VALUES(53,50,'53:50','و او عاد اول را نابود کرد.');
INSERT INTO "badkhashani" VALUES(53,51,'53:51','و قوم ثمود را (هلاک کرد) و کسی از ایشان را باقی نگذاشت.');
INSERT INTO "badkhashani" VALUES(53,52,'53:52','و (نیز) پیش از این قوم نوح را (هلاک کرد) چون آنان ظالم‌تر و سرکش‌تر بودند.');
INSERT INTO "badkhashani" VALUES(53,53,'53:53','و (دیار) مؤتفکه (زیر و زبر شدۀ قوم لوط) را سرنگون کرد.');
INSERT INTO "badkhashani" VALUES(53,54,'53:54','پس آن قریه را (با قهر و غضب خود) پوشانید آنچه پوشانید.');
INSERT INTO "badkhashani" VALUES(53,55,'53:55','پس به کدام یک از نعمت‌های پروردگارت شک و تردید می‌کنی؟');
INSERT INTO "badkhashani" VALUES(53,56,'53:56','این (پیغمبر) بیم دهنده‌ای از بیم دهندگان پیشین است.');
INSERT INTO "badkhashani" VALUES(53,57,'53:57','قیامت نزدیک گردیده است.');
INSERT INTO "badkhashani" VALUES(53,58,'53:58','کسی غیر از الله برطرف کننده‌ای (شدت و سختی آن روز) نیست.');
INSERT INTO "badkhashani" VALUES(53,59,'53:59','آیا از این سخن الهی تعجب می‌کنید؟');
INSERT INTO "badkhashani" VALUES(53,60,'53:60','و می‌خندید و گریه نمی‌کنید.');
INSERT INTO "badkhashani" VALUES(53,61,'53:61','در حالیکه شما غافليد و هوس رانید؟');
INSERT INTO "badkhashani" VALUES(53,62,'53:62','پس تنها برای الله سجده کنید و (او را) عبادت کنید.');
INSERT INTO "badkhashani" VALUES(54,1,'54:1','قیامت نزدیک شد و ماه شگافت.');
INSERT INTO "badkhashani" VALUES(54,2,'54:2','و اگر معجزه‌ای را ببیند روی می‌گردانند و می‌گویند: جادویی پی در پی است.');
INSERT INTO "badkhashani" VALUES(54,3,'54:3','و تکذیب کردند و از خواهشات (نفسانی) خود پیروی نمودند. و هر کاری هم جایگاه و قرارگاهی دارد.');
INSERT INTO "badkhashani" VALUES(54,4,'54:4','البته خبرهای مهم، آنچه که موجب بیزاری (از گناهان) شود، به اندازۀ کافی برای آنان آمده است.');
INSERT INTO "badkhashani" VALUES(54,5,'54:5','(قرآن) حکمتی رسا است، پس بیم دهندگان (برای سرکشان) سودی نبخشیدند.');
INSERT INTO "badkhashani" VALUES(54,6,'54:6','پس از ایشان روی بگردان (و منتظر باش تا) آن روزی که دعوت دهنده به‌سوی چیزی ناخوشایند فرا خواند.');
INSERT INTO "badkhashani" VALUES(54,7,'54:7','در حالیکه چشم‌هایشان (از ترس و وحشت) فروافتاده است. از گورها چنان بیرون می‌آیند که گویی آنها ملخ‌هایی پراگنده‌اند.');
INSERT INTO "badkhashani" VALUES(54,8,'54:8','شتابان به‌سوی دعوت دهنده می‌روند. کافران می‌گویند: این روزی بسیار سخت است.');
INSERT INTO "badkhashani" VALUES(54,9,'54:9','پیش از آنان قوم نوح تکذیب کردند و بندۀ ما را دروغگو شمردند و گفتند: دیوانه است. و (او) به خشونت (از دعوت دادن به‌سوی حق) منع شد.');
INSERT INTO "badkhashani" VALUES(54,10,'54:10','پس پروردگار خود را خواند که من مغلوب شده‌ام پس (از آنها) انتقام بگیر.');
INSERT INTO "badkhashani" VALUES(54,11,'54:11','پس درهای آسمان را با ریزش آب باز کردیم.');
INSERT INTO "badkhashani" VALUES(54,12,'54:12','و از زمین چشمه‌ها جاری کردیم، پس (هردو) آب برای انجام کاری که مقدر شده بود یکجا شد.');
INSERT INTO "badkhashani" VALUES(54,13,'54:13','و نوح را بر کشتی که دارای تخته‌ها و میخها بود، برداشتیم.');
INSERT INTO "badkhashani" VALUES(54,14,'54:14','(کشتی) زیر نظر چشم‌های ما روان بود. (این امر) پاداش کسی بود که (نبوتش) انکار شده بود.');
INSERT INTO "badkhashani" VALUES(54,15,'54:15','و البته آن را نشانه‌ای باقی گذاشتیم، پس آیا عبرت گیرنده‌ای هست؟!');
INSERT INTO "badkhashani" VALUES(54,16,'54:16','پس عذاب و ترساندن من چطور بود؟');
INSERT INTO "badkhashani" VALUES(54,17,'54:17','البته ما قرآن را برای تذکر و عبرت آسان گردانیده‌ایم، پس آیا عبرت گیرنده‌ای هست؟');
INSERT INTO "badkhashani" VALUES(54,18,'54:18','(قوم) عاد تکذیب کردند (پس ببین) عذاب من و بیم دادن‌هایم چطور بود؟');
INSERT INTO "badkhashani" VALUES(54,19,'54:19','البته که ما در روزی بی‌خیر و برکت (و) پی در پی تند باد سختی را بر آنان فرستادیم.');
INSERT INTO "badkhashani" VALUES(54,20,'54:20','آن باد مردم را (از زمین) بر می‌داشت، گویی که آنها تنه‌های درختان خرمای ریشه کن شده‌اند.');
INSERT INTO "badkhashani" VALUES(54,21,'54:21','پس عذاب من و بیم دادن‌هایم چطور بود؟.');
INSERT INTO "badkhashani" VALUES(54,22,'54:22','و البته قرآن را برای پند و عبرت گرفتن آسان گردانیده‌ایم، پس آیا عبرت گیرنده‌ای هست؟.');
INSERT INTO "badkhashani" VALUES(54,23,'54:23','ثمود بیم دادن (پیغمبر شان) را تکذیب کردند.');
INSERT INTO "badkhashani" VALUES(54,24,'54:24','پس گفتند: آیا از فردی پیروی کنیم که از خود ماست؟ يقينا ما در این صورت در گمراهی و جنون عمیق خواهیم بود.');
INSERT INTO "badkhashani" VALUES(54,25,'54:25','آیا از میان همۀ ما وحی بر او نازل کرده شد؟ بلکه او دروغگویی متکبر است.');
INSERT INTO "badkhashani" VALUES(54,26,'54:26','فردا خواهند دانست که دروغگوی و متکبر کیست؟');
INSERT INTO "badkhashani" VALUES(54,27,'54:27','البته ما ماده شتر را برای امتحان آنان خواهیم فرستاد، پس (ای صالح) در انتظار (هلاکت شان) باش و صبر کن.');
INSERT INTO "badkhashani" VALUES(54,28,'54:28','و به آنان خبر ده که آب در میان آنان تقسیم شده است. نوبت هر کدام که باشد بر سر آب حاضر شود.');
INSERT INTO "badkhashani" VALUES(54,29,'54:29','پس یارشان را صدا کردند و او دست درازی کرد پس شتر را از پای در آورد.');
INSERT INTO "badkhashani" VALUES(54,30,'54:30','پس عذاب من و بیم دادن‌هایم چطور بود؟');
INSERT INTO "badkhashani" VALUES(54,31,'54:31','همانا بر آنان صدای مرگبار را فرستادیم و ایشان همگی مانند گیاه خشک و ریزه شده شدند.');
INSERT INTO "badkhashani" VALUES(54,32,'54:32','و البته قرآن را برای پند و عبرت گرفتن آسان گردانیده‌ایم، پس آیا عبرت گیرنده‌ای هست؟');
INSERT INTO "badkhashani" VALUES(54,33,'54:33','قوم لوط بیم دهندگان را دروغ شمردند.');
INSERT INTO "badkhashani" VALUES(54,34,'54:34','(لذا) ما بر آنان سنگ باد فرستادیم، جز خانواده لوط که آنان را به هنگام سحر نجات دادیم. (همه نابود شدند).');
INSERT INTO "badkhashani" VALUES(54,35,'54:35','این (نجات) نعمتی بود از جانب ما، این چنین کسی را که شکر گزار باشد، جزای نیک می‌دهیم.');
INSERT INTO "badkhashani" VALUES(54,36,'54:36','و البته (لوط) آنان را از عقوبت ما بیم داده بود، اما آنان دربارۀ بیم دهندگان (پیغمبران) شک کردند.');
INSERT INTO "badkhashani" VALUES(54,37,'54:37','و البته (با لوط) دربارۀ (سوء قصد به) مهمانانش سخن گفتند، پس ما چشمهای آنها را کور کردیم (و گفتیم: لذت) عذاب من و ترسانیدن‌های مرا بچشید.');
INSERT INTO "badkhashani" VALUES(54,38,'54:38','و البته صبحگاهان عذاب پیاپی و پایدار بر ایشان آمد.');
INSERT INTO "badkhashani" VALUES(54,39,'54:39','(و گفتیم: لذت) عذاب من و ترسانیدن‌های مرا بچشید.');
INSERT INTO "badkhashani" VALUES(54,40,'54:40','و البته قرآن را برای پند و عبرت گرفتن آسان گردانیده‌ایم، پس آیا عبرت گیرنده‌ای هست؟');
INSERT INTO "badkhashani" VALUES(54,41,'54:41','و البته بیم دهندگان به نزد فرعون و پیروان او آمده بودند.');
INSERT INTO "badkhashani" VALUES(54,42,'54:42','آنان همه آیات ما را تکذیب کردند، لذا آنها را مانند گرفتن مقتدر و توانا (به قهر خود) گرفتیم.');
INSERT INTO "badkhashani" VALUES(54,43,'54:43','(بگو: ای اهل مکه!) آیا کافران شما از این‌ها (که ذکر شد) بهترند یا اینکه برائت شما از عذاب، در کتاب‌های (آسمانی سابق) درج است؟');
INSERT INTO "badkhashani" VALUES(54,44,'54:44','یا می‌گویند: ما گروه شکست ناپذیر و انتقام گیر هستیم و یکدیگر را پشتیبانی می‌کنیم؟');
INSERT INTO "badkhashani" VALUES(54,45,'54:45','زود است که این گروه شکست بخورند (و در مقابل مسلمانان) پشت بگردانند.');
INSERT INTO "badkhashani" VALUES(54,46,'54:46','بلکه وعدۀ عذاب آنها قيامت است. و (عذاب) قیامت سخت‌تر و تلخ‌تر است.');
INSERT INTO "badkhashani" VALUES(54,47,'54:47','يقينا مجرمان در گمراهی و آتش دوزخ‌اند.');
INSERT INTO "badkhashani" VALUES(54,48,'54:48','روزی که بر روی‌هایشان در دوزخ کشیده می‌شوند (و به آنان گفته شود:) طعم رسیدن آتش را بچشید.');
INSERT INTO "badkhashani" VALUES(54,49,'54:49','ما هر چیز را به اندازۀ معین آفریده‌ایم.');
INSERT INTO "badkhashani" VALUES(54,50,'54:50','و حكم ما نیست مگر مانند یک چشم زدن.');
INSERT INTO "badkhashani" VALUES(54,51,'54:51','و يقينا امثال شما (هم فکران و هم‌مسلکان شما) را نابود کردیم. پس آیا پند پذیری هست؟');
INSERT INTO "badkhashani" VALUES(54,52,'54:52','و هر چیزی را که انجام داده‌اند، در نامه‌های اعمال‌شان ثبت است.');
INSERT INTO "badkhashani" VALUES(54,53,'54:53','و هر خورد و بزرگ نوشته شده است.');
INSERT INTO "badkhashani" VALUES(54,54,'54:54','بی‌گمان پرهیزگاران در باغها و (در جوار) نهرها خواهند بود.');
INSERT INTO "badkhashani" VALUES(54,55,'54:55','در مقام و منزلت راستین، نزد فرمانروایی مقتدر.');
INSERT INTO "badkhashani" VALUES(55,1,'55:1','الله بخشنده (است).');
INSERT INTO "badkhashani" VALUES(55,2,'55:2','قرآن را (به محمد) آموخت.');
INSERT INTO "badkhashani" VALUES(55,3,'55:3','انسان را آفرید.');
INSERT INTO "badkhashani" VALUES(55,4,'55:4','به او نطق و بیان آموخت.');
INSERT INTO "badkhashani" VALUES(55,5,'55:5','آفتاب و ماه طبق حساب مقرر روانند.');
INSERT INTO "badkhashani" VALUES(55,6,'55:6','و گیاه و درخت برای او سجده می‌کنند.');
INSERT INTO "badkhashani" VALUES(55,7,'55:7','و آسمان را بلند برد و ترازو را مقرر کرد.');
INSERT INTO "badkhashani" VALUES(55,8,'55:8','تا در ترازو از حد تجاوز نکنید.');
INSERT INTO "badkhashani" VALUES(55,9,'55:9','و وزن را بر اساس عدل برپا دارید و در ترازو کم نگذارید.');
INSERT INTO "badkhashani" VALUES(55,10,'55:10','و زمین را برای خلق گسترانید.');
INSERT INTO "badkhashani" VALUES(55,11,'55:11','که در آن میوه (‌های فراوان) هست و درختهای خرمای غلافدار.');
INSERT INTO "badkhashani" VALUES(55,12,'55:12','(و در آن) دانۀ برگدار و گیاه خوشبو است.');
INSERT INTO "badkhashani" VALUES(55,13,'55:13','پس کدام نعمت‌های پروردگارتان را انکار می‌کنید؟');
INSERT INTO "badkhashani" VALUES(55,14,'55:14','انسان (آدم) را از گل خشک مانند سفال آفرید.');
INSERT INTO "badkhashani" VALUES(55,15,'55:15','و جن را از شعلۀ آتش آفرید.');
INSERT INTO "badkhashani" VALUES(55,16,'55:16','پس کدام نعمت‌های پروردگارتان را انکار می‌کنید؟');
INSERT INTO "badkhashani" VALUES(55,17,'55:17','(اوست) پروردگار دو مشرق و دو مغرب.');
INSERT INTO "badkhashani" VALUES(55,18,'55:18','پس کدام نعمت‌های پروردگارتان را انکار می‌کنید؟');
INSERT INTO "badkhashani" VALUES(55,19,'55:19','دو دریا (شور و شیرین) را (در کنار هم) روان کرد که با یکدیگر متصل می‌شوند.');
INSERT INTO "badkhashani" VALUES(55,20,'55:20','(اما) بين آن دو حجابی است که (به محدودۀ هم دیگر) تجاوز نمی‌کنند.');
INSERT INTO "badkhashani" VALUES(55,21,'55:21','پس کدام نعمت‌های پروردگارتان را انکار می‌کنید؟');
INSERT INTO "badkhashani" VALUES(55,22,'55:22','از آن دو (دریا) مروارید و مرجان بیرون می‌آید.');
INSERT INTO "badkhashani" VALUES(55,23,'55:23','پس کدام نعمت‌های پروردگارتان را انکار می‌کنید؟');
INSERT INTO "badkhashani" VALUES(55,24,'55:24','و او (الله) در بحر کشتی‌های ساخته شده مانند کوه دارد.');
INSERT INTO "badkhashani" VALUES(55,25,'55:25','پس کدام نعمت‌های پروردگارتان را انکار می‌کنید؟');
INSERT INTO "badkhashani" VALUES(55,26,'55:26','هر چه بر آن (زمین) است، فناپذیر است.');
INSERT INTO "badkhashani" VALUES(55,27,'55:27','و (تنها) پروردگارت صاحب صورت، که دارای شکوه و اکرام است، باقی می‌ماند.');
INSERT INTO "badkhashani" VALUES(55,28,'55:28','پس کدام نعمت‌های پروردگارتان را انکار می‌کنید؟');
INSERT INTO "badkhashani" VALUES(55,29,'55:29','هرکس که در آسمان‌ها و زمین است از او (کمک) می‌خواهد و هر روزی او در کاری است.');
INSERT INTO "badkhashani" VALUES(55,30,'55:30','پس کدام نعمت‌های پروردگارتان را انکار می‌کنید؟');
INSERT INTO "badkhashani" VALUES(55,31,'55:31','ای انس و جن! به زودی به حساب شما می‌پردازیم.');
INSERT INTO "badkhashani" VALUES(55,32,'55:32','پس کدام نعمت‌های پروردگارتان را انکار می‌کنید؟');
INSERT INTO "badkhashani" VALUES(55,33,'55:33','ای گروه‌های جن و انس! اگر می‌توانید از کناره‌های آسمان و زمین بگذرید، پس بگذرید، (اما) نمی‌توانید بگذرید مگر به واسطۀ قدرت (که شما آن قدرت را ندارید).');
INSERT INTO "badkhashani" VALUES(55,34,'55:34','پس کدام نعمت‌های پروردگارتان را انکار می‌کنید؟');
INSERT INTO "badkhashani" VALUES(55,35,'55:35','در آن روز شعله‌ای از آتش و دود بر شما فرستاده می‌شود. پس نمی‌توانید انتقام بگیرید.');
INSERT INTO "badkhashani" VALUES(55,36,'55:36','پس کدام نعمت‌های پروردگارتان را انکار می‌کنید؟');
INSERT INTO "badkhashani" VALUES(55,37,'55:37','پس وقتی آسمان بشکافد پس (مانند( گل سرخ، همچون روغن گداخته شود.');
INSERT INTO "badkhashani" VALUES(55,38,'55:38','پس کدام نعمت‌های پروردگارتان را انکار می‌کنید؟');
INSERT INTO "badkhashani" VALUES(55,39,'55:39','پس آن روز هیچ انس و جن از گناهش پرسیده نمی‌شود. (بلکه به علامت شناخته می‌شوند).');
INSERT INTO "badkhashani" VALUES(55,40,'55:40','پس کدام نعمت‌های پروردگارتان را انکار می‌کنید؟');
INSERT INTO "badkhashani" VALUES(55,41,'55:41','گناهکاران از سیما و قیافه‌هایشان شناخته می‌شوند، پس از موهای پیشانی و قدم‌هایشان گرفته می‌شوند )و در دوزخ انداخته می‌شوند).');
INSERT INTO "badkhashani" VALUES(55,42,'55:42','پس کدام نعمت‌های پروردگارتان را انکار می‌کنید؟');
INSERT INTO "badkhashani" VALUES(55,43,'55:43','گفته شود) این همان دوزخی است که مجرمان آن را دروغ می‌شمردند.');
INSERT INTO "badkhashani" VALUES(55,44,'55:44','بین آتش و آب گرم جوشان می‌گردند.');
INSERT INTO "badkhashani" VALUES(55,45,'55:45','پس کدام نعمت‌های پروردگارتان را انکار می‌کنید؟');
INSERT INTO "badkhashani" VALUES(55,46,'55:46','و برای کسیکه از ایستادن (در حضور) پروردگار خود ترسیده باشد، دو باغ است.');
INSERT INTO "badkhashani" VALUES(55,47,'55:47','پس کدام نعمت‌های پروردگارتان را انکار می‌کنید؟');
INSERT INTO "badkhashani" VALUES(55,48,'55:48','(آن دو باغ که درختانش) شاخه‌های بسیار دارد.');
INSERT INTO "badkhashani" VALUES(55,49,'55:49','پس کدام نعمت‌های پروردگارتان را انکار می‌کنید؟');
INSERT INTO "badkhashani" VALUES(55,50,'55:50','در آن دو (باغ) دو چشمه جاری است.');
INSERT INTO "badkhashani" VALUES(55,51,'55:51','پس کدام نعمت‌های پروردگارتان را انکار می‌کنید؟');
INSERT INTO "badkhashani" VALUES(55,52,'55:52','در آن دو (باغ) از هر میوه‌ای دو نوع است.');
INSERT INTO "badkhashani" VALUES(55,53,'55:53','پس کدام نعمت‌های پروردگارتان را انکار می‌کنید؟');
INSERT INTO "badkhashani" VALUES(55,54,'55:54','بر فرش‌هایی تکیه کرده‌اند که آسترهایش از ابریشم ضخیم است. و میوۀ آن دو باغ برای چیدن در دسترس است.');
INSERT INTO "badkhashani" VALUES(55,55,'55:55','پس کدام نعمت‌های پروردگارتان را انکار می‌کنید؟');
INSERT INTO "badkhashani" VALUES(55,56,'55:56','در آن (کاخ‌ها و باغ‌ها حوریان) همسران‌شان است که نگاه‌شان را از دیگران فرو می‌اندازند (و) پیش از آنان دست هیچ انس و جن به آنها نرسیده است.');
INSERT INTO "badkhashani" VALUES(55,57,'55:57','پس کدام نعمت‌های پروردگارتان را انکار می‌کنید؟');
INSERT INTO "badkhashani" VALUES(55,58,'55:58','(آن زنان چنان زیبا ‌اند که( گویا یاقوت و مرجان‌اند.');
INSERT INTO "badkhashani" VALUES(55,59,'55:59','پس کدام نعمت‌های پروردگارتان را انکار می‌کنید؟');
INSERT INTO "badkhashani" VALUES(55,60,'55:60','آیا پاداش نیکوکاری جز جزای نیکو است.');
INSERT INTO "badkhashani" VALUES(55,61,'55:61','پس کدام نعمت‌های پروردگارتان را انکار می‌کنید؟');
INSERT INTO "badkhashani" VALUES(55,62,'55:62','و غیر از آن دو باغ (برایشان( دو باغ دیگر هم است.');
INSERT INTO "badkhashani" VALUES(55,63,'55:63','پس کدام نعمت‌های پروردگارتان را انکار می‌کنید؟');
INSERT INTO "badkhashani" VALUES(55,64,'55:64','و آن دو باغ از شدت سبزی سیاه معلوم می‌شوند.');
INSERT INTO "badkhashani" VALUES(55,65,'55:65','پس کدام نعمت‌های پروردگارتان را انکار می‌کنید؟');
INSERT INTO "badkhashani" VALUES(55,66,'55:66','در آن دو باغ دو چشمه فواره کنان است.');
INSERT INTO "badkhashani" VALUES(55,67,'55:67','پس کدام نعمت‌های پروردگارتان را انکار می‌کنید؟');
INSERT INTO "badkhashani" VALUES(55,68,'55:68','در آن دو (باغ) میوه‌ها و درخت‌های خرما و انار است.');
INSERT INTO "badkhashani" VALUES(55,69,'55:69','پس کدام نعمت‌های پروردگارتان را انکار می‌کنید؟');
INSERT INTO "badkhashani" VALUES(55,70,'55:70','در آن باغها زنان نیک‌سیرت و خوبصورت است.');
INSERT INTO "badkhashani" VALUES(55,71,'55:71','پس کدام نعمت‌های پروردگارتان را انکار می‌کنید؟');
INSERT INTO "badkhashani" VALUES(55,72,'55:72','حوریان نگه داشته شده‌اند )برای نشستن همرای ازواج خود) در خیمه‌ها.');
INSERT INTO "badkhashani" VALUES(55,73,'55:73','پس کدام نعمت‌های پروردگارتان را انکار می‌کنید؟');
INSERT INTO "badkhashani" VALUES(55,74,'55:74','در حالیکه دست هیچ انس و جن پیش از آنان (مؤمنان) به آنان نرسیده است.');
INSERT INTO "badkhashani" VALUES(55,75,'55:75','پس کدام نعمت‌های پروردگارتان را انکار می‌کنید؟');
INSERT INTO "badkhashani" VALUES(55,76,'55:76','در حالیکه (جنتیان) بر بالش‌های سبز و بسترهای بی‌نظیر و زیبا تکیه زده‌اند.');
INSERT INTO "badkhashani" VALUES(55,77,'55:77','پس کدام نعمت‌های پروردگارتان را انکار می‌کنید؟');
INSERT INTO "badkhashani" VALUES(55,78,'55:78','بسیار بابرکت است نام پروردگارت که صاحب جلال و اکرام است.');
INSERT INTO "badkhashani" VALUES(56,1,'56:1','چون واقعۀ بزرگ (قیامت) واقع شود.');
INSERT INTO "badkhashani" VALUES(56,2,'56:2','که در واقع شدن آن هیچ دروغی نیست.');
INSERT INTO "badkhashani" VALUES(56,3,'56:3','(آن واقعه، گروهی را) پایین می‌آورد (و گروهی دیگر را) بالا می‌برد.');
INSERT INTO "badkhashani" VALUES(56,4,'56:4','آن وقت که زمین به شدت تکان داده می‌شود.');
INSERT INTO "badkhashani" VALUES(56,5,'56:5','و کوه‌ها کوبیده و به شدت متلاشی شوند.');
INSERT INTO "badkhashani" VALUES(56,6,'56:6','پس چون غبار پراگنده شوند.');
INSERT INTO "badkhashani" VALUES(56,7,'56:7','و شما (انسانها) به سه گروه تقسیم خواهید شد.');
INSERT INTO "badkhashani" VALUES(56,8,'56:8','پس (گروه اول) اهل سمت راست هستند. اما اهل سمت راست چه کسانی هستند؟.');
INSERT INTO "badkhashani" VALUES(56,9,'56:9','و (گروه دوم) اهل سمت چپ هستند! اما اهل سمت چپ چه کسانی هستند؟.');
INSERT INTO "badkhashani" VALUES(56,10,'56:10','و (گروه سوم) سبقت کنندگان، (که در کارهای نیک) سبقت کننده‌اند.');
INSERT INTO "badkhashani" VALUES(56,11,'56:11','آنان مقربان (دربار الهی)‌اند.');
INSERT INTO "badkhashani" VALUES(56,12,'56:12','که در باغ‌های نعمت خواهند بود.');
INSERT INTO "badkhashani" VALUES(56,13,'56:13','(آن مقربان) گروه زیادی از پیشینیان‌اند.');
INSERT INTO "badkhashani" VALUES(56,14,'56:14','و گروه اندکی از پسينيان‌اند.');
INSERT INTO "badkhashani" VALUES(56,15,'56:15','بر تخت‌های چیده شده و بافته شده از دُر و یاقوت (خواهند نشست).');
INSERT INTO "badkhashani" VALUES(56,16,'56:16','در حالیکه روبروی هم نشسته‌اند، بر این تخت‌ها تکیه می‌کنند.');
INSERT INTO "badkhashani" VALUES(56,17,'56:17','بر آنها پسرانی که همیشه جوان‌اند می‌گردند.');
INSERT INTO "badkhashani" VALUES(56,18,'56:18','با جام‌ها و کوزه‌ها و ظرف‌های از شراب جاری (که هیچ بیماری و آفتی ندارد بر آنان می‌گردند).');
INSERT INTO "badkhashani" VALUES(56,19,'56:19','نه از آن (شراب) سردرد شوند و نه مست و بیهوش.');
INSERT INTO "badkhashani" VALUES(56,20,'56:20','(و به گرد آنان می‌گردند) با میوه‌هایی که اختیار کنند.');
INSERT INTO "badkhashani" VALUES(56,21,'56:21','و گوشت پرنده‌ها از هر نوع که اشتها دارند.');
INSERT INTO "badkhashani" VALUES(56,22,'56:22','و زنان سفید روی و بزرگ چشم (گرد آنها می‌گردند).');
INSERT INTO "badkhashani" VALUES(56,23,'56:23','که همچون مروارید پنهان شده در صدف‌اند.');
INSERT INTO "badkhashani" VALUES(56,24,'56:24','(نعمت‌هایی که به ایشان داده می‌شود) به پاداش کارهایی است که می‌کرده‌اند.');
INSERT INTO "badkhashani" VALUES(56,25,'56:25','و در آنجا نه سخن بیهوده می‌شنوند و نه سخن گناه.');
INSERT INTO "badkhashani" VALUES(56,26,'56:26','مگر این سخن را که سلام و درود (بر شما) باد.');
INSERT INTO "badkhashani" VALUES(56,27,'56:27','و اصحاب دست راست چه (وضع و) حالی دارند، اصحاب دست راست؟.');
INSERT INTO "badkhashani" VALUES(56,28,'56:28','در (میان) درختان سدر (کنار) بی‌خار‌اند.');
INSERT INTO "badkhashani" VALUES(56,29,'56:29','و در باغ‌های درختان موز که میوه‌هایش چیده شده است.');
INSERT INTO "badkhashani" VALUES(56,30,'56:30','و در سایه‌های گسترده‌');
INSERT INTO "badkhashani" VALUES(56,31,'56:31','و در کنار آبشارها (و آب‌های روان)،');
INSERT INTO "badkhashani" VALUES(56,32,'56:32','و در میان میوه‌های فراوان‌ هستند');
INSERT INTO "badkhashani" VALUES(56,33,'56:33','که نه تمام می‌شود و نه از مصرف آن منع می‌گردند.');
INSERT INTO "badkhashani" VALUES(56,34,'56:34','و (بر) بسترهای بلند (نشسته اند).');
INSERT INTO "badkhashani" VALUES(56,35,'56:35','البته ما آن حوران را به طور خاص آفریدیم.');
INSERT INTO "badkhashani" VALUES(56,36,'56:36','پس ایشان را دوشیزه و باکره گردانیدیم.');
INSERT INTO "badkhashani" VALUES(56,37,'56:37','زنانی هم سن و سال و شوهر دوست‌اند.');
INSERT INTO "badkhashani" VALUES(56,38,'56:38','این نعمت‌ها برای اصحاب دست راست است.');
INSERT INTO "badkhashani" VALUES(56,39,'56:39','که گروهی از امتهای پیشین.');
INSERT INTO "badkhashani" VALUES(56,40,'56:40','و گروهی از امتهای پسین هستند.');
INSERT INTO "badkhashani" VALUES(56,41,'56:41','و اصحاب دست چپ چه (وضع و) حالی دارند، اصحاب دست چپ؟');
INSERT INTO "badkhashani" VALUES(56,42,'56:42','در باد گرم و آب جوش خواهند بود.');
INSERT INTO "badkhashani" VALUES(56,43,'56:43','و در سایه‌ای از دود سیاه.');
INSERT INTO "badkhashani" VALUES(56,44,'56:44','که نه سرد است و نه سودبخش.');
INSERT INTO "badkhashani" VALUES(56,45,'56:45','چون آنان پیش از این (در دنیا) در ناز و نعمت و خوشگذرانی بودند.');
INSERT INTO "badkhashani" VALUES(56,46,'56:46','و همیشه بر گناه بزرگ ادامه می‌دادند.');
INSERT INTO "badkhashani" VALUES(56,47,'56:47','و می‌گفتند: (راجع به انکار قیامت) آیا چون مردیم و خاک و استخوان گشتیم دوباره زنده خواهیم شد؟');
INSERT INTO "badkhashani" VALUES(56,48,'56:48','و آیا پدران نخستین ما (نیز دوباره زنده می‌شوند؟)');
INSERT INTO "badkhashani" VALUES(56,49,'56:49','بگو: البته تمام پیشینیان و پسینیان.');
INSERT INTO "badkhashani" VALUES(56,50,'56:50','به‌سوی وعده‌گاه روز معین جمع کرده می‌شوند.');
INSERT INTO "badkhashani" VALUES(56,51,'56:51','باز شما ای گمراهان تکذیب کنندۀ (توحید و قیامت).');
INSERT INTO "badkhashani" VALUES(56,52,'56:52','حتما از درختی که زقوم است، خواهید خورد.');
INSERT INTO "badkhashani" VALUES(56,53,'56:53','پس شکم‌ها را از آن پر خواهید کرد.');
INSERT INTO "badkhashani" VALUES(56,54,'56:54','باز بالای آن از آب جوش مینوشید.');
INSERT INTO "badkhashani" VALUES(56,55,'56:55','و مانند شتران تشنه می‌نوشید.');
INSERT INTO "badkhashani" VALUES(56,56,'56:56','این است پذیرایی از آنان در روز قیامت.');
INSERT INTO "badkhashani" VALUES(56,57,'56:57','ما شما را آفریده‌ایم پس چرا تصدیق نمی‌کنید؟');
INSERT INTO "badkhashani" VALUES(56,58,'56:58','آیا دربارۀ نطفه‌ای که در (رحم زنان) می‌ریزید، دقت کرده‌اید؟');
INSERT INTO "badkhashani" VALUES(56,59,'56:59','آیا شما آن را (بصورت انسان) می‌آفرینید یا ما آفریدگاریم؟');
INSERT INTO "badkhashani" VALUES(56,60,'56:60','ما در میان شما مرگ را مقدر کرده‌ایم و هرگز عاجز و ناتوان نیستیم و کسی بر ما پیشی نمی‌گیرد.');
INSERT INTO "badkhashani" VALUES(56,61,'56:61','از اینکه مانند شما را جاگزین کنیم و شما را در جهانی که نمی‌دانید دوباره آفرینش تازه‌ای ببخشیم.');
INSERT INTO "badkhashani" VALUES(56,62,'56:62','و البته شما آفریدن بار اول را دانسته‌اید، پس چرا متذکر نمی‌شوید؟');
INSERT INTO "badkhashani" VALUES(56,63,'56:63','آیا آنچه را کشت می‌کنید، دیده‌اید؟');
INSERT INTO "badkhashani" VALUES(56,64,'56:64','آیا شما آن را می‌رویانید یا ما می‌رویانیم؟');
INSERT INTO "badkhashani" VALUES(56,65,'56:65','اگر بخواهیم آن را گیاهی خشک و خاشاک می‌گردانیم پس تعجب خواهید کرد.');
INSERT INTO "badkhashani" VALUES(56,66,'56:66','(و می‌گوئید که) البته ما خسارمند شدیم.');
INSERT INTO "badkhashani" VALUES(56,67,'56:67','بلکه ما محروم و بدبختیم.');
INSERT INTO "badkhashani" VALUES(56,68,'56:68','آیا آبی را که می‌نوشید دیده‌اید؟');
INSERT INTO "badkhashani" VALUES(56,69,'56:69','آیا شما آن را از ابر پایین آورده‌اید یا ما آن را فرود می‌آوریم؟');
INSERT INTO "badkhashani" VALUES(56,70,'56:70','اگر بخواهیم آن را شور و تلخ می‌گردانیم پس چرا شكر نمی‌گزارید؟');
INSERT INTO "badkhashani" VALUES(56,71,'56:71','آیا آتشی را که می‌افروزید، دیده‌اید؟');
INSERT INTO "badkhashani" VALUES(56,72,'56:72','آیا شما درخت آن را آفریده‌اید یا ما آفریننده‌ایم؟');
INSERT INTO "badkhashani" VALUES(56,73,'56:73','(بلکه) ما آن را وسيلۀ تذكر (آتش آخرت) و منفعتی برای مسافران قرار داده‌ایم.');
INSERT INTO "badkhashani" VALUES(56,74,'56:74','پس نام پروردگار بزرگت را به پاکی یاد کن.');
INSERT INTO "badkhashani" VALUES(56,75,'56:75','پس قسم به جایگاه ستارگان.');
INSERT INTO "badkhashani" VALUES(56,76,'56:76','و البته اگر بدانید آن قسمی است، بزرگ.');
INSERT INTO "badkhashani" VALUES(56,77,'56:77','که این (کتاب) قرآنی گران قدر است.');
INSERT INTO "badkhashani" VALUES(56,78,'56:78','در کتاب پنهان (پوشیده از نظر انس و جن) قرار دارد.');
INSERT INTO "badkhashani" VALUES(56,79,'56:79','که به آن دست نمی‌رساند مگر پاکان (فرشتگان).');
INSERT INTO "badkhashani" VALUES(56,80,'56:80','از (سوی) پروردگار کائنات نازل شده است.');
INSERT INTO "badkhashani" VALUES(56,81,'56:81','آیا شما با این کلام (الهی) با سستی و سبکی برخورد می‌کنید؟!');
INSERT INTO "badkhashani" VALUES(56,82,'56:82','و (به جای شکر) روزی تان (روزی دهنده) را تکذیب می‌کنید؟');
INSERT INTO "badkhashani" VALUES(56,83,'56:83','پس چرا وقتی که جان به حلقوم می‌رسد (توانایی بازگرداندن آن را نداريد؟).');
INSERT INTO "badkhashani" VALUES(56,84,'56:84','و شما در آن وقت (سكرات موت را) می‌نگرید.');
INSERT INTO "badkhashani" VALUES(56,85,'56:85','و ما از شما به او (محتضر) نزدیک تریم، ولی شما نمی‌بینید.');
INSERT INTO "badkhashani" VALUES(56,86,'56:86','پس اگر شما (در برابر اعمال تان) جزاء داده نمی‌شوید.');
INSERT INTO "badkhashani" VALUES(56,87,'56:87','چرا روح را باز نمی‌گردانید اگر راست می‌گویید؟');
INSERT INTO "badkhashani" VALUES(56,88,'56:88','پس اما اگر از مقربان (دربار الهی) باشد.');
INSERT INTO "badkhashani" VALUES(56,89,'56:89','پس آسایش و ریحان و باغ پرناز و نعمت دارد.');
INSERT INTO "badkhashani" VALUES(56,90,'56:90','و اما اگر از اصحاب دست راست باشد.');
INSERT INTO "badkhashani" VALUES(56,91,'56:91','پس (برایش گفته می‌شود: چون تو) از اصحاب دست راست بودی، سلامتی (و امنیت) برای توست.');
INSERT INTO "badkhashani" VALUES(56,92,'56:92','و اما اگر از جملۀ تکذیب کنندگان گمراه باشد.');
INSERT INTO "badkhashani" VALUES(56,93,'56:93','پس با آب جوش از او پذیرایی می‌گردد.');
INSERT INTO "badkhashani" VALUES(56,94,'56:94','و (عاقبتش) افتادن در دوزخ است.');
INSERT INTO "badkhashani" VALUES(56,95,'56:95','بی‌گمان این (قرآن) خبر راست و یقین است.');
INSERT INTO "badkhashani" VALUES(56,96,'56:96','پس نام پروردگار بزرگت را به پاکی یاد کن.');
INSERT INTO "badkhashani" VALUES(57,1,'57:1','آنچه در آسمان‌ها و زمین است الله را به پاکی یاد می‌کنند و اوست غالب باحکمت.');
INSERT INTO "badkhashani" VALUES(57,2,'57:2','پادشاهی آسمان‌ها و زمین خاص از اوست و او زنده می‌کند و می‌میراند و او بر هر چیز تواناست.');
INSERT INTO "badkhashani" VALUES(57,3,'57:3','او «اول» و «آخر» و «ظاهر» و «باطن» است و او به هر چیز داناست.');
INSERT INTO "badkhashani" VALUES(57,4,'57:4','اوست ذاتی که آسمان‌ها و زمین را در شش روز آفريد آنگاه [آن گونه كه شايسته جلال و مقام اوست] بر عرش قرار گرفت. می‌داند آنچه را که در زمین داخل می‌شود و آنچه را که از آن خارج می‌گردد هر کجا که باشید او با شماست و الله به آنچه می‌کنید بیناست.');
INSERT INTO "badkhashani" VALUES(57,5,'57:5','فرمانروایی آسمان‌ها و زمین خاص از اوست و همه کارها به او بازگردانیده می‌شود.');
INSERT INTO "badkhashani" VALUES(57,6,'57:6','شب را در روز و روز را در شب داخل می‌کند. و او به راز سینه‌ها خوب داناست.');
INSERT INTO "badkhashani" VALUES(57,7,'57:7','به الله و پیغمبرش ایمان بیاورید و از آنچه شما را در (استفادۀ) آن جانشین قرار داده است، انفاق کنید. پس کسانی که از شما ایمان آورند و انفاق کنند، برایشان پاداش بزرگ است.');
INSERT INTO "badkhashani" VALUES(57,8,'57:8','و شما را چه شده که به الله ایمان نمی‌آورید در حالیکه پیغمبر شما را دعوت می‌دهد تا به پروردگارتان ایمان بیاورید. یقینا الله از شما پیمان گرفته است اگر باور دارید.');
INSERT INTO "badkhashani" VALUES(57,9,'57:9','اوست ذاتی که بر بندۀ خود (محمد) آیات واضح نازل می‌کند تا شما را از تاریکیها به‌سوی نور بیرون کند و همانا الله نسبت به شما مشفق و مهربان است.');
INSERT INTO "badkhashani" VALUES(57,10,'57:10','و شما را چه شده است که در راه الله انفاق نمی‌کنید، حال آنکه میراث آسمان‌ها و زمین برای اوست؟! کسانی از شما که پیش از فتح (مکه) انفاق کرده‌اند و جنگیده‌اند (با انفاق کنندگان و مجاهدان پس از فتح) برابر نیستند، آنان درجه و مقامشان برتر از درجه و مقام کسانی است که بعد از فتح بذل و انفاق نموده و جنگیده‌اند. اما به هرحال الله به همه وعدۀ نیک داده است و الله به آنچه می‌کنید آگاه است.');
INSERT INTO "badkhashani" VALUES(57,11,'57:11','کیست آن که به الله قرض نیک دهد تا آن را برایش دوچندان گرداند و برای او پاداشی ارزشمند است.');
INSERT INTO "badkhashani" VALUES(57,12,'57:12','روزی که مردان و زنان مؤمن را می‌بینی که نورشان از پیش روی آنان و در جانب راستشان شتابان می‌رود. امروز شما را مژده باد باغ‌هایی که (در زیر قصرها و درختان آن) نهرها جاری است و در آنجا جاودانه می‌باشید. این است کامیابی بزرگ.');
INSERT INTO "badkhashani" VALUES(57,13,'57:13','روزی که مردان و زنان منافق به مؤمنان می‌گویند: درنگی کنید (و به ما بنگرید) تا از نورتان سهمی حاصل کنیم. گفته می‌شود: به عقب برگردید و (در آنجا) نور بجویید. پس میان آنان دیواری زده می‌شود با دری که داخل آن رحمت و جانب بیرون آن رو به عذاب است.');
INSERT INTO "badkhashani" VALUES(57,14,'57:14','(منافقان) مؤمنان را ندا می‌دهند: آیا (در دنیا) با شما نبودیم؟ می‌گویند: بلی، و لیكن خویشتن را گرفتار فتنه کردید و چشم به راه (حوادث بد بر مؤمنان) بودید و (در دین حق) شک کردید و آرزوهای باطل شما را فریفته ساخت، تا آنکه حكم الله رسید و فریب‌گر بزرگ (شیطان) شما را در بارۀ الله فریب داد.');
INSERT INTO "badkhashani" VALUES(57,15,'57:15','پس امروز، از شما (منافقان) و از کسانی که کفر ورزیدند، بدل و فدیه پذیرفته نمی‌شود. جایگاه شما دوزخ است. آتش یاور شماست و بد جای بازگشت است.');
INSERT INTO "badkhashani" VALUES(57,16,'57:16','آیا برای مؤمنان وقت آن نرسیده است که دل‌هایشان به (هنگام) یاد الله و به (سبب یاد آوردن) آنچه از حق نازل شده است، نرم و متواضع گردد و مانند کسانی نباشند که پیش از این به آنان کتاب داده شد و زمان بر آنان طولانی شد؛ پس دل‌هایشان سخت شد و بسیار‌شان فاسق‌اند؟!');
INSERT INTO "badkhashani" VALUES(57,17,'57:17','بدانید که الله زمین را پس از مرگش زنده می‌کند. البته آیات (قدرت و وحدانیت خود) را به وضاحت برایتان بیان کردیم تا این که شما تعقل کنید.');
INSERT INTO "badkhashani" VALUES(57,18,'57:18','يقينا مردان و زنان صدقه دهنده و کسانی که به الله قرض نیک داده‌اند، (پاداش) آنان چندین برابر داده می‌شود و برای آنان اجر عزت‌مندانه است.');
INSERT INTO "badkhashani" VALUES(57,19,'57:19','و آنان که به الله و پیغمبران او ایمان آورده‌اند، ایشان راستان کامل‌اند. و شهیدان نزد پروردگارشان، پاداش و نورشان را دارند. و کسانی که کفر ورزیدند و آیات ما را تکذیب کردند آنان یاران دوزخ‌اند.');
INSERT INTO "badkhashani" VALUES(57,20,'57:20','بدانید که زندگانی دنیا چیزی جز بازی، سرگرمی و زینت و فخر فروشی در میانتان و افزون طلبی در اموال و اولاد نیست. مانند بارانی که گیاه (رویدۀ) آن کشاورزان را به تعجب می‌آورد باز خشک می‌شود و آن را زرد می‌بینی، باز کاه می‌گردد. و در آخرت عذاب سخت و (هم) مغفرت و رضامندی از جانب الله است. و زندگانی دنیا نیست مگر سبب و وسيلۀ غرور و فریب.');
INSERT INTO "badkhashani" VALUES(57,21,'57:21','(لذا) به‌سوی آمرزش از جانب پروردگارتان و جنتی که پهنایش مانند پهنای آسمان و زمین است؛ بشتابید که برای کسانی که به الله و پیغمبران او ایمان آورده‌اند، آماده شده است این فضل الله است، به هرکس که بخواهد آنرا می‌دهد و الله دارای فضل و بخشش بزرگ است.');
INSERT INTO "badkhashani" VALUES(57,22,'57:22','هیچ مصیبتی در زمین و نه در جان‌هایتان به شما نرسد مگر پیش از آنکه آن را ایجاد کنیم در کتاب (تقدیر) ثبت و نوشته است. چون این امر بر الله آسان است.');
INSERT INTO "badkhashani" VALUES(57,23,'57:23','این برای آنست تا بر آنچه از دستتان رفته است افسوس نخورید و به آنچه که (الله) به شما می‌دهد شاد نشوید و الله هیچ شخص متكبر فخر فروش را دوست نمی‌دارد.');
INSERT INTO "badkhashani" VALUES(57,24,'57:24','همان کسانی که بخل می‌ورزند و مردم را به بخل ورزيدن فرمان می‌دهند و هر کس که روی گردان شود (بداند که) الله بی‌نیاز و قابل ستایش است.');
INSERT INTO "badkhashani" VALUES(57,25,'57:25','البته ما پیغمبران خود را با دلایل واضح فرستادیم و با آنها کتاب و ترازو را نیز نازل کردیم تا مردم در میان خود عدالت را قایم کنند. و آهن را که در آن نیروی سخت و منافع برای مردم است نازل کردیم، تا الله بداند چه کسی او و پیغمبرانش را نادیده یاری می‌کند، زیرا الله قوی (و) غالب است.');
INSERT INTO "badkhashani" VALUES(57,26,'57:26','و البته نوح و ابراهیم را فرستادیم و در نسل هر دو نبوت و کتاب را قرار دادیم، پس بعضی از آنان هدایت یافتند ولی بسیاری از آنها فاسق‌اند.');
INSERT INTO "badkhashani" VALUES(57,27,'57:27','باز به تعقیب آنان پیغمبران خود را فرستادیم و عیسی پسر مریم را در پی آنان فرستادیم و به او انجیل دادیم و در دل‌های کسانی که از او پیروی کردند مهربانی و شفقت را قرار دادیم. و (اما) رهبانیتی (ترک دنیا) را که خودشان آن را اختراع نمودند، ما آن را بر آنان واجب نکرده بودیم، بلکه آنان برای به دست آوردن خشنودی الله (آن را ایجاد کرده بودند) ولی چنانکه باید (حقش را) رعایت نکردند. پس به مؤمنان‌شان پاداش آنان را دادیم و بسیاری از آنان فاسقان بودند.');
INSERT INTO "badkhashani" VALUES(57,28,'57:28','ای کسانی که ایمان آورده‌اید! از الله بترسید و به پیغمبر او ایمان بیاورید تا الله دو پاداش از رحمت خود را به شما بدهد و برایتان نوری قرار دهد که در روشنی آن حرکت کنید. و تا شما را بیامرزد و الله آمرزندۀ مهربان است.');
INSERT INTO "badkhashani" VALUES(57,29,'57:29','تا در نتیجه اهل کتاب بدانند که چیزی از فضل الله را در اختیار ندارند و اینکه فضل و رحمت در دست الله است، آن را به هر کس که بخواهد می‌دهد. و الله دارای فضل و بخشش بزرگ است.');
INSERT INTO "badkhashani" VALUES(58,1,'58:1','البته الله سخن زنی را شنید که دربارۀ همسرش با تو گفتگو می‌کرد و به الله شکایت می‌کرد. و الله گفتگوی شما دو نفر را می‌شنود. بی‌گمان الله شنوای بیناست.');
INSERT INTO "badkhashani" VALUES(58,2,'58:2','کسانی از شما که با زنانشان ظهار می‌کنند (و می‌گویند: تو به من مانند پشت مادرم هستی) آنان مادرانشان نیستند. بلکه مادرانشان تنها زنانی هستند که آنها را زاده‌اند و همانا آنان سخن زشت و دروغ می‌گویند و بدون شک الله عفو کنندۀ آمرزگار است.');
INSERT INTO "badkhashani" VALUES(58,3,'58:3','و آنانی که با زنان خود ظهار می‌کنند باز از آن چه گفته‌اند باز می‌گردند پیش از آن که با یکدیگر نزدیکی کنند باید برده‌ای را آزاد کنند. این درس و پندی است که به شما داده می‌شود و الله به آن چه می‌کنید آگاه است.');
INSERT INTO "badkhashani" VALUES(58,4,'58:4','پس اگر کسی (برده) نیابد باید دو ماه پی در پی روزه بگیرد پیش از آنکه زن و شوهر با همدیگر نزدیکی کنند. و کسی که نمی‌تواند پس باید شصت نفر مسکین را طعام بدهد. این برای آن است که به الله و پیغمبرش ایمان بیاورید. و این (احکام) حدود مقرر شدۀ الله است و کافران عذاب دردناک دارند.');
INSERT INTO "badkhashani" VALUES(58,5,'58:5','يقينا آنانی که با الله و پیغمبرش مخالفت می‌کنند خوار و ذلیل می‌شوند همان طور که پیشینیانشان خوار شدند و البته نشانه‌های واضح نازل کردیم و برای کافران عذاب رسواکننده است.');
INSERT INTO "badkhashani" VALUES(58,6,'58:6','روزی که الله همۀ آنان را برانگیزد تا به آنچه کرده‌اند خبر‌شان دهد؛ الله آن (اعمال) را شماریده و آنان فراموش کرده‌اند و الله بر همه چیز گواه است.');
INSERT INTO "badkhashani" VALUES(58,7,'58:7','آیا ندیدی که الله آنچه را که در آسمانهاست و آنچه را که در زمین است می‌داند؟ هیچ سه نفری نیست که با همدیگر رازگویی کنند مگر این که الله چهارم ایشان است و نه پنج نفری مگر این که الله ششم ایشان است و نه (رازگویانی) کمتر از این و نه بیشتر از این، مگر این که الله با ایشان است در هر کجا که باشند. باز روز قیامت از آنچه که کرده‌اند خبرشان می‌دهد چرا که الله به همه چیز داناست.');
INSERT INTO "badkhashani" VALUES(58,8,'58:8','آیا ندیده‌ای آنانی را که از نجوی (رازگویی) منع شده‌اند باز به آنچه که از آن منع شده‌اند باز می‌گردند و برای انجام گناه و سرکشی و نافرمانی پیغمبر با هم دیگر به نجوا می‌پردازند و چون نزد تو آیند طوری به تو سلام و تحیت می‌گویند که الله به تو آنطور سلام و تحیت نگفته است و در میان خود می‌گویند: پس چرا الله ما را به خاطر آنچه می‌گوییم عذاب نمی‌دهد؟ دوزخ برای آنان کافی است، وارد آن می‌شوند و (دوزخ) چه بد جا و بد سر انجامی است.');
INSERT INTO "badkhashani" VALUES(58,9,'58:9','ای کسانی که ایمان آورده‌اید! هرگاه با همدیگر به رازگویی پرداختید برای انجام گناه و تجاوزگری و نافرمانی از پیغمبر راز مگویید. و به نیکی و پرهیزگاری راز بگویید و از الله بترسید که به‌سوی او محشور می‌شوید.');
INSERT INTO "badkhashani" VALUES(58,10,'58:10','جز این نیست که راز گویی از (کارهای) شیطان است تا مؤمنان را اندوهگین سازد و هیچ ضرری به آنان رسانده نمی‌تواند مگر به ارادۀ الله و مؤمنان باید بر الله توکل کنند.');
INSERT INTO "badkhashani" VALUES(58,11,'58:11','ای کسانی که ایمان آورده‌اید، چون به شما گفته شود: در مجلس (برای دیگران) جای فراخ کنید، جای فراخ کنید، تا الله (رحمت خود را) برایتان فراخ گرداند. و چون گفته شود: برخیزید! پس برخیزید. الله به کسانی از شما که ایمان آورده‌اند و آنانی را که علم داده شده‌اند رفعت و بزرگی می‌بخشد، و الله به آنچه می‌کنید آگاه است.');
INSERT INTO "badkhashani" VALUES(58,12,'58:12','ای کسانی که ایمان آورده‌اید، هرگاه خواستید با پیغمبر نجوا (رازگویی) کنید، پیش از نجوایتان صدقه‌ای بدهید، این برایتان بهتر و پاکیزه‌تر است پس اگر (صدقه) نیابید (بدانید که) الله آمرزگار مهربان است.');
INSERT INTO "badkhashani" VALUES(58,13,'58:13','آیا می‌ترسید که پیش از نجوایتان صدقه‌هایی را بدهید؟ پس اگر صدقه ندادید و الله هم شما را بخشید، پس نماز را برپا کنید و زکات را بدهید و الله و پیغمبرش را اطاعت کنید و الله به آنچه می‌کنید آگاه است.');
INSERT INTO "badkhashani" VALUES(58,14,'58:14','آیا ندیدی به‌سوی کسانی که دوستی کردند با قومی که الله بر آنان غضب نموده است! آنها (منافقان) نه از شما‌اند و نه از آنان (یهود) و به دروغ سوگند می‌خورند در حالیکه می‌دانند.');
INSERT INTO "badkhashani" VALUES(58,15,'58:15','الله برای آنان عذابی سخت آماده کرده است، زیرا کاری انجام می‌دهند که بسیار بد است.');
INSERT INTO "badkhashani" VALUES(58,16,'58:16','آنان قسم‌هایشان را سپر ساخته‌اند و مردم را از راه الله منع نمودند لذا برایشان عذاب خوارکننده است.');
INSERT INTO "badkhashani" VALUES(58,17,'58:17','هرگز مال و اولادشان چیزی از عذاب الله را از آنها دفع نمی‌کند؟ (چون) این گروه دوزخیان‌اند که آنها در دوزخ جاودان‌اند.');
INSERT INTO "badkhashani" VALUES(58,18,'58:18','روزی که الله همۀ آنان را زنده می‌گرداند پس برایش قسم می‌خورند چنانکه برای شما قسم می‌خورند و گمان می‌کنند که ایشان بر چیزی هستند. آگاه باش! یقینا ایشان دروغگویانند.');
INSERT INTO "badkhashani" VALUES(58,19,'58:19','(چون که) شیطان بر آنان غالب شده است، پس یاد الله را از یادشان برده است، لذا آنها حزب شیطان هستند. آگاه باش که حزب شیطان زیانکارانند.');
INSERT INTO "badkhashani" VALUES(58,20,'58:20','يقينا آنانی که با الله و پیغمبرش دشمنی می‌کنند از جملۀ پست‌ترین و خوارترین (مردمان) خواهند بود.');
INSERT INTO "badkhashani" VALUES(58,21,'58:21','الله نوشته (و مقرر داشته) است که من و پیغمبرانم غالب می‌شویم. زیرا الله قویی غالب است.');
INSERT INTO "badkhashani" VALUES(58,22,'58:22','گروهی را که به الله و روز قیامت ایمان می‌آورند نخواهی یافت با کسی که با الله و پیغمبرش مخالفت ورزیده است دوستی کنند هرچند پدرانشان یا فرزندانشان یا برادرانشان یا خویشاوندانشان باشند. ایشان‌اند که (الله) در دل‌هایشان ایمان را نوشته است و آنان را به روحی از سوی خود تأیید نموده است و آنان را به باغهایی در می‌آورد که از زیر (قصرها و درختان) آنها نهرها روان است و جاودانه در آنجا می‌مانند. الله از آنان راضی است و آنان (نیز) از او راضی‌اند. ایشان حزب الله هستند. آگاه باشید! که تنها حزب الله رستگار می‌باشند.');
INSERT INTO "badkhashani" VALUES(59,1,'59:1','هر آنچه در آسمان‌ها و هر آنچه در زمین است، الله را به پاکی یاد می‌کند و او غالب باحکمت است.');
INSERT INTO "badkhashani" VALUES(59,2,'59:2','او ذاتی است که کافران اهل کتاب را در نخستین جمع شدن‌شان (علیه پیغمبر اسلام) از سرزمینشان بیرون کرد، شما گمان نمی‌کردید که آنان (از مدینه) بیرون شوند و آنان گمان می‌کردند که قلعه‌هایشان آنان را از (عذاب) الله محافظت می‌کند پس (عذاب) الله از راهی که گمان نمی‌کردند بر آنها وارد شد و در دل‌هایشان ترس و هراس انداخت طوری که با دستهای خود و دست‌های مؤمنان خانه‌های خویش را ویران می‌کردند. پس ای صاحبان بصیرت و آگاهی عبرت بگیرید.');
INSERT INTO "badkhashani" VALUES(59,3,'59:3','اگر الله آوارگی را بر آنان مقرر نمی‌کرد حتما آنان را در دنیا به عذاب گرفتار می‌کرد و برای آنها در آخرت عذاب دوزخ است.');
INSERT INTO "badkhashani" VALUES(59,4,'59:4','این عذاب به سبب آن است که آنان با الله و رسول او دشمنی و مخالفت نمودند و هر کس که با الله دشمنی و مخالفت کند (پس هلاک می‌شود) چون الله سخت عذاب دهنده است.');
INSERT INTO "badkhashani" VALUES(59,5,'59:5','آنچه از درختان خرما را بریدید یا آنرا بر پایه‌هایش قائم گذاشتید، پس به حكم الله بوده است و تا بدکاران را خوار و رسوا کند.');
INSERT INTO "badkhashani" VALUES(59,6,'59:6','و آنچه را الله از (اموال) آنان به پیغمبرش داده است شما برای حصول آن هیچ اسب و شتری را نتاختید بلکه الله پیغمبرانش را بر هر کس که بخواهد مسلط می‌سازد و الله بر هر چیزی قادر است.');
INSERT INTO "badkhashani" VALUES(59,7,'59:7','آنچه را الله از (اموال) اهالی آبادی‌های دیگر به پیغمبرش داده است متعلق به الله و پیغمبر و خویشاوندان (پیغمبر) و يتميان و مسکینان و مسافران (در راه ماندگان) است تا تنها در میان ثروتمندان شما دست به دست نگردد. و آنچه که پیغمبر به شما بدهد آن را بگیرید و از آنچه که شما را از آن منع می‌کند اجتناب ورزید. و از الله بترسید چون الله سخت عقوبت دهنده است.');
INSERT INTO "badkhashani" VALUES(59,8,'59:8','(همچنین غنائم) برای فقرای مهاجرین است که از خانه‌هایشان و اموالشان رانده شده‌اند فضل و رضایت الله را می‌جویند و الله و پیغمبرش را مدد می‌کنند. این گروه ایشان راستگویانند.');
INSERT INTO "badkhashani" VALUES(59,9,'59:9','و آنانی که پیش از مهاجرین خانه و کاشنه را آماده کردند و ایمان (نیز) در دلشان جای گرفت، کسانی را که به‌سوی آنان هجرت کنند دوست می‌دارند، و در دل‌های خود، از آنچه (به مهاجران) داده‌ شده احساس حسادت نمی‌کنند و (دیگران را) بر خودشان ترجیح می‌دهند هرچند که خود آنها سخت محتاج باشند. و آنانی که از حرص نفس خویش مصئون باشند، ایشان‌اند که رستگارند.');
INSERT INTO "badkhashani" VALUES(59,10,'59:10','و آنانی که پس از (مهاجرین و انصار) آمدند می‌گویند: ای پروردگار ما! ما و برادران ما را که در ایمان آوردن از ما سبقت نمودند بیامرز و در دل‌های ما هیچ کینه‌ای در حق کسانی که ایمان آورده‌اند قرار مده، ای پروردگار ما! یقینا تویی که بخشندۀ مهربانی.');
INSERT INTO "badkhashani" VALUES(59,11,'59:11','آیا منافقان را ندیدی که به برادران کافر خود از اهل کتاب (یهود) می‌گویند: هرگاه شما را بیرون کنند ما هم با شما بیرون خواهیم آمد و در حق شما هرگز از کسی فرمان نمی‌بریم، و اگر با شما جنگ و پیکار شود حتما شما را یاری می‌رسانیم. و الله گواهی میدهد که آنان دروغگو هستند.');
INSERT INTO "badkhashani" VALUES(59,12,'59:12','اگر (یهود) بیرون رانده شوند با آنان بیرون نمی‌روند و اگر با آنان جنگ صورت گیرد به آنان یاری نمی‌رسانند و اگر ارادۀ کمک کنند، حتما پشت می‌گردانند و می‌گریزند، باز یاری و مدد نخواهند شد.');
INSERT INTO "badkhashani" VALUES(59,13,'59:13','البته ترس شما (مسلمانان) در سینه‌های ایشان بیش از ترس آنان از الله است، این به آن خاطر است که آنان قومی‌اند که نمی‌فهمند.');
INSERT INTO "badkhashani" VALUES(59,14,'59:14','آنان هرگز با شما به صورت دسته جمعی جز در پس قلعه‌های محکم و یا از پشت دیوارها نمی‌جنگند. عداوت و جنگشان در میان خودشان سخت است، ایشان را متحد می‌پنداری حال آنکه دل‌هایشان پراگنده است. این (اختلاف) به آن خاطر است که آنان گروهی‌اند که تعقل نمی‌کنند.');
INSERT INTO "badkhashani" VALUES(59,15,'59:15','(حالت این یهودیان، در فایده نبردن از وعدۀ منافقان) مانند حالت مشرکانی است که چندی پیش از اینان بودند (که وعدۀ شیطان به آنها فایده نرساند؛ و) سزای کار (بد) خود را (در بدر) چشیدند. و آنان (در آخرت نیز) عذابی دردناک دارند.');
INSERT INTO "badkhashani" VALUES(59,16,'59:16','(حالت منافقان در وعدۀ خلافی) مانند (حالت) شیطان است که به انسان می‌گوید: کافر شو، اما وقتی که (انسان) کافر شود، (شیطان به او) می‌گوید: من از تو بیزارم، زیرا من از الله، که پروردگار جهانیان است می‌ترسم.');
INSERT INTO "badkhashani" VALUES(59,17,'59:17','پس عاقبت هردو (انسان کافر و شیطان) آن است که همیشه در دوزخ باشند و این عذاب ابدی سزای هر ظالم است.');
INSERT INTO "badkhashani" VALUES(59,18,'59:18','ای کسانی که ایمان آورده‌اید، از الله بترسید و هر شخص باید بنگرد که چه چیزی را برای (عاقبت) فردا پیش فرستاده است. و از الله بترسید، چون الله به آنچه می‌کنید باخبر است.');
INSERT INTO "badkhashani" VALUES(59,19,'59:19','و مانند کسانی نباشید که الله را فراموش کردند در نتیجه (الله نیز) خودشان را از یاد خودشان برد این گروه بدکاران‌اند.');
INSERT INTO "badkhashani" VALUES(59,20,'59:20','دوزخیان با بهشتیان برابر نیستند، چونکه بهشتیان رستگارند.');
INSERT INTO "badkhashani" VALUES(59,21,'59:21','اگر این قرآن را بر کوهی نازل می‌کردیم البته آن را از ترس الله خاشع و عاجز و از هم پاشیده می‌دیدی. و این مثال‌ها را برای مردم بیان می‌کنیم تا که ایشان بیانديشند.');
INSERT INTO "badkhashani" VALUES(59,22,'59:22','اوست الله که هیچ معبودی به حق جز او نیست، دانای پوشیده و آشکار. اوست بخشندۀ مهربان.');
INSERT INTO "badkhashani" VALUES(59,23,'59:23','اوست الله که هیچ معبودی به حق جز او نیست، او پادشاه، منزه و بی‌عیب و نقص، امان دهنده و امنیت بخشنده، محافظ و مراقب، قدرتمند و غالب، بزرگوار و شکوهمند و والا مقام و صاحب بزرگی کامل است. پاک است الله از آنچه (با او) شرک می‌آورند.');
INSERT INTO "badkhashani" VALUES(59,24,'59:24','اوست الله، آفریدگار، پدید آورنده، صورت دهنده. نام‌های نیک تنها برای اوست، آنچه در آسمان‌ها و زمین است او را به پاکی یاد می‌کند، و او غالب باحکمت است.');
INSERT INTO "badkhashani" VALUES(60,1,'60:1','ای کسانی که ایمان آورده‌اید، دشمنان من و دشمنان خود را به دوستی نگیرید شما با ایشان دوستی می‌کنید در حالی‌که آنها به حق و حقیقتی ایمان ندارند که برای شما آمده است. پیغمبر و شما را به خاطر اینکه به الله -پروردگارتان- ایمان دارید، (از شهر و دیارتان) بیرون می‌کنند. اگر شما برای جهاد در راه من و طلب رضایتم بیرون آمده‌اید (با آنان دوستی نکنید). شما مخفیانه با آنها رابطه دوستى برقرار می‌کنید در حالیکه من نسبت به هرچه پنهان می‌کنید یا آشکار می‌سازید داناتر هستم. و هر کس از شما چنین کاری را کند يقينا از راه راست گم گشته است.');
INSERT INTO "badkhashani" VALUES(60,2,'60:2','اگر بر شما مسلط شوند دشمنان سرسخت شما خواهند بود، و دستان و زبان‌های خود را به‌سوی شما به بدی دراز می‌کنند و دوست دارند که شما کافر شوید.');
INSERT INTO "badkhashani" VALUES(60,3,'60:3','در روز قیامت خویشاوندانتان و فرزندانتان سودی به شما نخواهند بخشید. در آن روز الله در میانتان جدایی خواهد آورد و الله به آنچه می‌کنید بیناست.');
INSERT INTO "badkhashani" VALUES(60,4,'60:4','البته برای شما در ابراهیم و آنانکه با او بودند سرمشقی نیکوست. وقتی به قوم خود گفتند: ما از شما و از آنچه به جای الله می‌پرستید بیزاریم، به شما کفر ورزیده‌ایم و بین ما و شما برای همیشه دشمنی و کینۀ ابدی است تا این که تنها به الله ایمان آورید، مگر گفتۀ ابراهیم به پدرش که (گفت:) برایت آمرزش خواهم خواست و در برابر الله برایت اختیار چیزی را ندارم. پروردگارا! بر تو توکل کرده‌ایم و به‌سوی تو روی آورده‌ایم و بازگشت به‌سوی توست.');
INSERT INTO "badkhashani" VALUES(60,5,'60:5','ای پروردگار ما! ما را محل آزمایشی برای کافران مگردان و ما را بیامرز، پروردگارا! تو غالب باحکمت هستی.');
INSERT INTO "badkhashani" VALUES(60,6,'60:6','البته در آنان برای شما سرمشق نیکوست برای کسی (از شما) که به ملاقات الله و روز قیامت امیدوار است و هر کس رویگردان شود (بداند که) الله بی‌نیاز و ستوده است.');
INSERT INTO "badkhashani" VALUES(60,7,'60:7','امید است که الله بین شما و کسانی که با آنان دشمنی داشته‌اید (به وسیلۀ اسلام آوردن شان) دوستی برقرار کند و الله تواناست و الله آمرزگار مهربان است.');
INSERT INTO "badkhashani" VALUES(60,8,'60:8','الله شما را از کسانی که با شما در (بارۀ) دین نجنگیده‌اند و شما را از خانه‌هایتان بیرون نکرده‌اند، منع نمی‌کند که به آنان نیکی کنید و در حق آنان به عدل و انصاف رفتار کنید، چون الله عادلان و با انصافان را دوست دارد.');
INSERT INTO "badkhashani" VALUES(60,9,'60:9','تنها شما را از کسانی منع می‌کند که در (بارۀ) دین با شما جنگیده‌اند و شما را از خانه‌هایتان بیرون کرده‌اند و در بیرون کردن تان (دیگران را) پشتیبانی کرده‌اند، از آن که با آنان دوستی کنید، و هر کس با آنان دوستی ورزد پس این گروه همان ظالمان‌اند.');
INSERT INTO "badkhashani" VALUES(60,10,'60:10','ای کسانی که ایمان آورده‌اید! چون زنان مؤمن هجرت کردند و به نزد شما آمدند آنان را بیازمائید - الله به ایمانشان داناتر است - پس اگر آنان را زنانی مؤمن دریافتید آنان را به کافران برنگردانید نه اینان (زنان مؤمن) برای آنان (کافران) حلالند و نه آنان (کافران) برای اینان (زنان مؤمن) حلالند و آنچه را مردان (کافر) انفاق کرده‌اند به آنان بدهید، و گناهی بر شما نیست که با آنان ازدواج کنید به شرطی که مهرهایشان را به آنان بدهید. و به (نکاح) زنان کافر (تان) پایبند نباشید (بلکه رهایشان کنید)؛ و آنچه را (به عنوان مهریه به زنان‌تان که مرتد شده و به کفار پیوسته‌اند) خرج کرده‌اید (از کافران) مطالبه کنید و (کافران نیز) آنچه را (که به زنان‌شان که اسلام آورده و به شما پیوسته‌‌اند) خرج کرده‌اند (از شما) مطالبه کنند. این حکم الله است که بین شما حکم می‌کند و الله دانای باحکمت است.');
INSERT INTO "badkhashani" VALUES(60,11,'60:11','و اگر کسی از همسرانتان به‌سوی کافران رفته و از دست شما برود، پس وقتی (کافران را) سزا دادید (و اموالشان را به غنیمت گرفتید) به آنان که همسرانشان (به سوی كفار) رفته‌اند به مقدار آن چه انفاق کرده‌اند بپردازید و از الله آن ذاتی بترسید که شما به او ایمان دارید.');
INSERT INTO "badkhashani" VALUES(60,12,'60:12','ای پیغمبر! هنگامی که زنان مؤمن پیش تو بیایند و بخواهند با تو بیعت کنند بر این که چیزی را شریک الله نسازند و دزدی نکنند و مرتکب زنا نشوند و فرزندانشان را نکشند و بهتانی را که با آن (از روی دروغ) فرزندی را به شوهرانشان نسبت دهند در میان نیاورند و در کارهای نیک (از فرمان) تو نافرمانی نکنند با آنان بیعت کن، و از الله برای آنان آمرزش بخواه. چون الله آمرزگار مهربان است.');
INSERT INTO "badkhashani" VALUES(60,13,'60:13','ای کسانی که ایمان آورده‌اید! قومی را دوست خود قرار ندهید که الله بر آنان غضب کرده است، در حالیکه آنان از آخرت ناامید‌اند، چنانکه کافران از گور‌خفتگان ناامید شده‌اند.');
INSERT INTO "badkhashani" VALUES(61,1,'61:1','آنچه در آسمان‌ها و آنچه در زمین است الله را به پاکی یاد می‌کند و او غالب با حکمت است.');
INSERT INTO "badkhashani" VALUES(61,2,'61:2','ای کسانی که ایمان آورده‌اید! چرا چیزی را می‌گوئید که آنرا انجام نمی‌دهید؟');
INSERT INTO "badkhashani" VALUES(61,3,'61:3','بسیار ناپسند است نزد الله؛ اینکه چیزی بگوئید که آنرا انجام نمی‌دهید.');
INSERT INTO "badkhashani" VALUES(61,4,'61:4','يقينا الله کسانی را دوست دارد که در راه او صف بسته می‌جنگند گویی بنایی استوار و به هم پیوسته‌اند.');
INSERT INTO "badkhashani" VALUES(61,5,'61:5','و یادآور شو آن وقت که موسی به قوم خود گفت : ای قوم من! چرا مرا آزار می‌دهید حال آنکه خوب می‌دانید که من فرستادۀ الله به‌سوی شما هستم؟! پس چون منحرف شدند الله دل‌هایشان را منحرف ساخت و الله گروه فاسقان را هدایت نمی‌کند.');
INSERT INTO "badkhashani" VALUES(61,6,'61:6','و یادآور شو وقتی که عیسی پسر مریم گفت: ای بنی اسرائیل! من فرستادۀ الله به‌سوی شما هستم و تورات را که پیش از من آمده تصدیق می‌کنم و به پیغمبری که بعد از من می‌آید و نام او احمد است مژده می‌دهم. پس چون با معجزه‌ها به‌سوی آنان آمد گفتند: این جادویی آشکار است.');
INSERT INTO "badkhashani" VALUES(61,7,'61:7','و کیست ظالم‌تر از آنکه بر الله دروغ می‌بندد در حالیکه به اسلام دعوت می‌شود و الله گروه ظالمان را هدایت نمی‌کند.');
INSERT INTO "badkhashani" VALUES(61,8,'61:8','(منافقان و اهل کتاب) می‌خواهند با دهانهای خود نور الله را خاموش کنند و الله کامل کنندۀ نور خود است هر چند کافران خوش نداشته باشند.');
INSERT INTO "badkhashani" VALUES(61,9,'61:9','اوست ذاتی که پیغمبر خود را با هدایت و دین حق فرستاد تا آن را بر همۀ ادیان غالب سازد، هر چند مشرکان خوش نداشته باشند.');
INSERT INTO "badkhashani" VALUES(61,10,'61:10','ای کسانی که ایمان آورده‌اید؟ آیا شما را بر تجارتی راهنمایی کنم که شما را از عذاب دردناک نجات دهد؟');
INSERT INTO "badkhashani" VALUES(61,11,'61:11','(آن تجارت این است که) به الله و پیغمبرش ایمان آورید و در راه الله با اموال و جان‌هایتان جهاد کنید، این برای شما بهتر است اگر بدانید.');
INSERT INTO "badkhashani" VALUES(61,12,'61:12','تا گناهان شما را برایتان بیامرزد و شما را به باغ‌هایی درآورد که از زیر (قصرها و درختان) آن نهرها روان است. و خانه‌های دلپسند در بهشت‌های جاودان به دست آورید، این است کامیابی بزرگ.');
INSERT INTO "badkhashani" VALUES(61,13,'61:13','و (نعمتی) دیگر (به شما می‌دهد) که آن را دوست دارید؛ پیروزی از سوی الله و فتحی نزدیک (فتح مکه) است و به مؤمنان مژده ده.');
INSERT INTO "badkhashani" VALUES(61,14,'61:14','ای کسانی که ایمان آورده‌اید، نصرت دهندگان (دین) الله باشید طوری که عیسی پسر مریم به حواریان گفت: مددگاران من (در دعوت) به‌سوی الله کیست؟ حواریان گفتند: ما مددگاران (دین) الله‌ایم، پس گروهی از بنی اسرائیل ایمان آوردند و گروهی کفر ورزیدند در نهایت مؤمنان را بر دشمنانشان توان دادیم پس (تا اینکه) پیروز شدند.');
INSERT INTO "badkhashani" VALUES(62,1,'62:1','آنچه در آسمان‌ها و آنچه در زمین است الله، پادشاه پاک (و) غالب (و) باحکمت را به پاکی یاد می‌کنند.');
INSERT INTO "badkhashani" VALUES(62,2,'62:2','اوست کسی که در میان ناخوانان پیغمبری از خود آنان فرستاد که آیاتش را بر آنان می‌خواند و آنان را پاک می‌دارد و به آنان کتاب و حکمت می‌آموزد، هرچند که پیش از این در گمراهی آشکار بودند.');
INSERT INTO "badkhashani" VALUES(62,3,'62:3','و (این بعثت خاص به مردم زمان پیغمبر نیست بلکه به) قومی دیگر از آنان (نیز است) که هنوز به ایشان نپیوسته‌اند (و بعدها به دنیا خواهند آمد)، و الله غالب با حکمت است.');
INSERT INTO "badkhashani" VALUES(62,4,'62:4','این (بعثت) فضل الله است، آن را به هر کس که بخواهد می‌دهد و الله دارای فضل بزرگ است.');
INSERT INTO "badkhashani" VALUES(62,5,'62:5','مثال آنان که مکلف به تورات شدند باز (چنانکه باید) رعایتش نکردند، مانند خری است که کتاب‌هایی را حمل می‌کند (اما از محتوای آن بی‌خبر است). چه بد است مثَل گروهی که آیات الله را دروغ شمردند! و الله قوم ظالم را هدایت نمی‌کند.');
INSERT INTO "badkhashani" VALUES(62,6,'62:6','بگو: ای یهودیان! اگر گمان می‌کنید که شما دوستان الله هستید نه مردمان دیگر، پس اگر راست می‌گویید آرزوی مرگ کنید.');
INSERT INTO "badkhashani" VALUES(62,7,'62:7','ولی آنان به خاطر آنچه پیش از این فرستاده‌اند، هرگز آرزوی مرگ نخواهند کرد و الله به (حال) ظالمان داناست.');
INSERT INTO "badkhashani" VALUES(62,8,'62:8','بگو: آن مرگی که از آن می‌گریزید (بدانید) که آن به شما رسیدنی است، باز به (نزد) دانای پنهان و آشکار بازگردانده می‌شوید پس شما را به (حقیقت) آنچه می‌کردید آگاه می‌سازد.');
INSERT INTO "badkhashani" VALUES(62,9,'62:9','ای کسانی که ایمان آورده‌اید! هرگاه در روز جمعه برای نماز ندا داده شد به یاد الله بشتابید و خرید و فروش را رها کنید، این برایتان بهتر است اگر بدانید.');
INSERT INTO "badkhashani" VALUES(62,10,'62:10','چون نماز ادا شد، در زمین پراگنده شوید و از فضل الله بجوئيد و الله را بسیار یاد کنید تا شما رستگار شوید.');
INSERT INTO "badkhashani" VALUES(62,11,'62:11','و چون تجارت و سرگرمی را ببینند به‌سوی آن پراگنده شده و تو را ایستاده رها می‌کنند. بگو: آنچه نزد الله است از سرگرمی و تجارت بهتر است و الله بهترین روزی دهندگان است.');
INSERT INTO "badkhashani" VALUES(63,1,'63:1','چون منافقان نزد تو آیند می‌گویند: گواهی می‌دهیم که تو پیغمبر الله هستی، و الله می‌داند که تو فرستادۀ الله می‌باشی و الله گواهی میدهد که منافقان دروغگویانند.');
INSERT INTO "badkhashani" VALUES(63,2,'63:2','قسم‌های خود را (برای پوشانیدن نفاق شان) سپر قرار داده‌اند پس مردم را از راه الله منع کردند، البته بد است آنچه آنها می‌کنند.');
INSERT INTO "badkhashani" VALUES(63,3,'63:3','این (نفاق) به آن خاطر است که ایشان ایمان آوردند، باز کفر ورزیدند پس بر دل‌هایشان مهر نهاده شد، پس آنها نمی‌فهمند.');
INSERT INTO "badkhashani" VALUES(63,4,'63:4','و هنگامی که آنها را می‌بینی قد و قامت‌شان تو را در تعجب می‌اندازد! و اگر سخن گویند به گفتۀ آنان گوش می‌دهی، آنان گویا تخته‌هائی هستند که تكيه داده شده‌اند. هر فریادی را علیه خود می‌پندارند، آنان دشمن‌اند. پس از آنان برحذر باش! الله آنان را بکشد، (از حق) به کجا گردانیده می‌شوند؟');
INSERT INTO "badkhashani" VALUES(63,5,'63:5','و چون به آنان گفته شود: بیایید تا رسول الله برایتان آمرزش بخواهد، سرهایشان را می‌گردانند و آنان را می‌بینی که دیگران را از راه الله منع می‌کنند در حالیکه تكبر می‌ورزند.');
INSERT INTO "badkhashani" VALUES(63,6,'63:6','برای آنان برابر است، چه برایشان آمرزش بخواهی و چه آمرزش نخواهی، هرگز الله ایشان را نمی‌آمرزد. زیرا الله مردمان فاسق را هدایت نمی‌کند.');
INSERT INTO "badkhashani" VALUES(63,7,'63:7','آنها کسانی‌اند که می‌گویند: بر آنانی که نزد رسول الله هستند انفاق مکنید تا منتشر و پراگنده شوند. حال آنکه خزانه‌های آسمان و زمین از الله است، ولی منافقان نمی‌فهمند.');
INSERT INTO "badkhashani" VALUES(63,8,'63:8','(و نیز) می‌گویند: اگر به مدینه باز گردیم به يقين عزتمندتر ذلیل‌تر را از آنجا بیرون می‌کند، حال آنکه عزت تنها مخصوص الله و رسول او و مؤمنان است، ولی منافقان نمی‌دانند.');
INSERT INTO "badkhashani" VALUES(63,9,'63:9','ای کسانی که ایمان آورده‌اید! اموال و اولادتان شما را از یاد الله غافل نکند و هر کس که چنین کند پس همان گروه زیانکارند.');
INSERT INTO "badkhashani" VALUES(63,10,'63:10','و از چیزهایی که به شما روزی داده‌ایم انفاق کنید پیش از آنکه مرگ یکی از شما برسد و بگوید: پروردگارا! چرا مرگ مرا تا مدتی اندک به تأخیر نیانداختی تا صدقه می‌دادم و از صالحان میشدم.');
INSERT INTO "badkhashani" VALUES(63,11,'63:11','و الله هرگز (مرگ) کسی را چون اجلش برسد به تأخیر نمی‌اندازد. و الله به آنچه می‌کنید باخبر است.');
INSERT INTO "badkhashani" VALUES(64,1,'64:1','آنچه در آسمان‌ها و آنچه در زمین است الله را به پاکی یاد می‌کنند، فرمانروایی (حقیقی) از اوست و ستایش (نیز) او راست و او بر هر کاری تواناست.');
INSERT INTO "badkhashani" VALUES(64,2,'64:2','او (الله) ذاتی است که شما را آفریده است پس بعضی از شما کافر و بعضی از شما مؤمن هستید و الله به آن چه می‌کنید بیناست.');
INSERT INTO "badkhashani" VALUES(64,3,'64:3','آسمان‌ها و زمین را به حق آفریده و شما را صورت بخشید و صورتهای شما را خوب و زیبا کرده است و بازگشت همه به‌سوی اوست.');
INSERT INTO "badkhashani" VALUES(64,4,'64:4','آنچه را که در آسمان‌ها و زمین است می‌داند و آنچه را که پنهان می‌کنید و آنچه را که آشکار می‌سازید، می‌داند و الله به آنچه در سینه‌ها است آگاهی دارد.');
INSERT INTO "badkhashani" VALUES(64,5,'64:5','آیا خبر آنان که پیش از این کافر بودند به شما نیامده است؟ آنان سزای کار خود را چشیدند و عذابی دردناک )در پیش( دارند.');
INSERT INTO "badkhashani" VALUES(64,6,'64:6','این (عذاب) به این جهت بود که پیغمبرانشان با معجزات (آشکار) نزد آنان آمدند و گفتند: آیا بشری (عادی) ما را هدایت می‌کند؟ پس کفر ورزیدند و روی گرداندند و الله اظهار بی‌نیازی نمود و الله بی‌نیاز ستوده است.');
INSERT INTO "badkhashani" VALUES(64,7,'64:7','کافران گمان کردند که دوباره زنده نخواهند شد، بگو: بلی! قسم به پروردگارم! به يقين دوباره زنده خواهید شد باز از آن چه می‌کردید به شما خبر خواهد داد و این کار برای الله آسان است.');
INSERT INTO "badkhashani" VALUES(64,8,'64:8','پس به الله و پیغمبر او و نوری که نازل کرده‌ایم ایمان بیاورید و الله به آن چه می‌کنید باخبر است.');
INSERT INTO "badkhashani" VALUES(64,9,'64:9','وقتی که (الله) شما را در روز اجتماع جمع کند، آن روز، روز زیان و نقصان است و کسی که به الله ایمان بیاورد و کار نیک کند بدی‌هایش را از او دور می‌سازد و به باغ‌هایی داخل می‌گرداند که از زیر (قصرها و درختان) آن نهرها جاری است که در آنجا همیشه و جاودانه‌اند. این است کامیابی بزرگ.');
INSERT INTO "badkhashani" VALUES(64,10,'64:10','و آنان که کفر ورزیدند و آیات ما را دروغ شمردند ایشان دوزخی‌اند، در آن جاودانه‌اند و این سر انجام بدی است.');
INSERT INTO "badkhashani" VALUES(64,11,'64:11','هیچ مصیبتی نمی‌رسد جز به حكم الله و هرکس به الله ایمان بیاورد الله دل او را هدایت می‌کند و الله به همه چیز داناست.');
INSERT INTO "badkhashani" VALUES(64,12,'64:12','از الله و پیغمبر اطاعت کنید و اگر رویگردان شوید (پس بدانید که) بر فرستادۀ ما جز رسانیدن پیغام آشکار نیست.');
INSERT INTO "badkhashani" VALUES(64,13,'64:13','الله است که جز او معبودی به حق نیست، پس مؤمنان باید بر الله توکل کنند.');
INSERT INTO "badkhashani" VALUES(64,14,'64:14','ای کسانی که ایمان آورده‌اید! به یقین بعضی از همسران و فرزندانتان دشمن شما‌اند، پس از آنان حذر کنید. و اگر عفو کنید و چشم پوشی نمائید و درگذرید (بدانید) که الله آمرزگار مهربان است.');
INSERT INTO "badkhashani" VALUES(64,15,'64:15','جز این نیست که اموالتان و فرزندانتان وسیلۀ امتحان شما‌اند و نزد الله پاداش بزرگی است.');
INSERT INTO "badkhashani" VALUES(64,16,'64:16','پس تا حد توان تان از الله بترسید و بشنوید و اطاعت کنید و انفاق نمائید که برای خودتان بهتر است و کسانی که از بخل و حرص نفس خویش در امان بمانند، آنانند که کامیابند.');
INSERT INTO "badkhashani" VALUES(64,17,'64:17','اگر به الله قرض حسنه بدهید آن را برایتان چندین برابر می‌سازد و شما را می‌آمرزد و الله سپاسگزار بردبار است.');
INSERT INTO "badkhashani" VALUES(64,18,'64:18','و او دانای همه پوشیده و آشکار است، غالب (و) باحکمت است.');
INSERT INTO "badkhashani" VALUES(65,1,'65:1','ای پیغمبر! وقتی که خواستید زنان را طلاق دهید آنان را در وقت عدۀ‌شان طلاق دهید و عده را بشمارید و از الله پروردگارتان بترسید. آنان را از خانه‌هایشان بیرون نکنید و آنان هم بیرون نروند مگر آنکه کار زشت آشکاری را مرتکب شوند و این حدود الله است و هر کس از حدود الله تجاوز کند در حقیقت بر خود ظلم کرده است. تو نمیدانی، چه بسا الله بعد از آن امری (دیگر) در میان آورد.');
INSERT INTO "badkhashani" VALUES(65,2,'65:2','و چون مدت عدۀ آنان به پایانش نزدیک شد آنان را به نیکی نگاه دارید یا به نیکی از آنان جدا شوید و دو مرد عادل از میانتان گواه بگیرید و گواهی را برای (رضای) الله ادا کنید. این طور، کسی که به الله و روز قیامت ایمان دارد به آن پند داده می‌شود و کسی که از الله بترسد برایش راه رهایی (از مشکلات) قرار خواهد داد.');
INSERT INTO "badkhashani" VALUES(65,3,'65:3','و او را از جایی روزی خواهد داد که او گمان نمی‌برد. و کسی که بر الله توکل کند او برایش کافی است. بی‌گمان الله امر خود را محقق می‌سازد. به تحقیق الله برای هر چیزی اندازه‌ای مقرر کرده است.');
INSERT INTO "badkhashani" VALUES(65,4,'65:4','و زنانی که از عادت ماهواری ناامید شده‌اند اگر (در عدۀ آنها) شک کردید، عدۀ آنان سه ماه است و (نیز) آنانکه به سن حیض نرسیده‌اند. و عدۀ زنان باردار وضع حمل است. و هرکس از الله بترسد و پرهیزگاری کند الله برایش در کارش آسانی می‌آورد.');
INSERT INTO "badkhashani" VALUES(65,5,'65:5','این حكم الله است که آن را به‌سوی شما نازل کرده است و هر کس از الله بترسد گناهانش را محو می‌کند و پاداش او را بزرگ می‌گرداند.');
INSERT INTO "badkhashani" VALUES(65,6,'65:6','آنان را در جایی سکونت دهید که خودتان سکونت دارید و در توانایی شماست و به آنان زیان نرسانید تا (عرصه را) بر آنان تنگ کنید. اگر باردار باشند به آنان نفقه دهید تا آنکه وضع حمل می‌کنند. پس اگر (فرزندتان) را برای شما شیر دهند مزدشان را بدهید و (در بارۀ نوزاد) میان خود به نیکی مشورت و توافق کنید. و اگر به دشواری افتادید (باید) زن دیگر (به درخواست) او (شوهر) شیر دهد.');
INSERT INTO "badkhashani" VALUES(65,7,'65:7','باید صاحب دارایی از دارایی خود نفقه دهد و کسی که روزی‌اش تنگ شده است باید از آن چه الله به او داده است انفاق کند، الله هیچ کس را جز به همان اندازه که به او داده است مكلف نمی‌کند. به زودی الله پس از سختی آسانی را می‌آورد.');
INSERT INTO "badkhashani" VALUES(65,8,'65:8','و چه بسا (اهالی) قریه‌ها از فرمان پروردگارشان و (فرمان) رسول او سرکشی کردند، پس به سختی از آنان حساب گرفتیم و به عذابی سخت ناشناخته عذابشان کردیم.');
INSERT INTO "badkhashani" VALUES(65,9,'65:9','پس سزای کار خود را چشیدند و سرانجام کارشان خساره و نقصان شد.');
INSERT INTO "badkhashani" VALUES(65,10,'65:10','الله برای آنان عذابی سخت آماده کرده است. پس ای خردمندان مؤمن! از الله بترسید، يقيناً الله به سوی شما ذکر (یعنی قرآن) را نازل کرده است.');
INSERT INTO "badkhashani" VALUES(65,11,'65:11','(و نیز) پیغمبری را (فرستاده) که برایتان آیات الله را که بیانگر (حق و باطل است) می‌خواند تا آنان را که ایمان آورده و کارهای شایسته کرده‌اند از تاریکی‌ها به‌سوی نور بیرون آورد، و کسی که به الله ایمان دارد و کار شایسته انجام دهد (الله) او را به باغهایی که از زیر (درختان و قصرهای) آن نهرها روان است وارد خواهد کرد که همیشه در آن جاودانه‌اند. یقینا الله روزی او را نیک ساخته است.');
INSERT INTO "badkhashani" VALUES(65,12,'65:12','الله ذاتی است که هفت آسمان را آفرید و از زمین نیز همانند آن را (آفرید)، فرمان الهی میان آنها نازل می‌شود تا بدانید که الله بر هر کاری قادر است وعلم او همه چیز را احاطه کرده است.');
INSERT INTO "badkhashani" VALUES(66,1,'66:1','ای پیغمبر! چرا چیزی را که الله برایت حلال کرده است به خاطر به دست آوردن خشنودی همسرانت بر خود حرام می‌کنی؟ و الله آمرزگار مهربان است.');
INSERT INTO "badkhashani" VALUES(66,2,'66:2','البته الله گشودن (شکستن) سوگندهایتان را برای شما مقرر کرده است و الله مددگار شماست و اوست دانای باحکمت.');
INSERT INTO "badkhashani" VALUES(66,3,'66:3','و وقتی که پیغمبر با یکی از زنانش سخنی نهانی گفت، پس چون وی آن (سخن) را برای (زن دیگر) بازگو کرد و الله (پیغمبر) را بر آن مطلع گردانید (پیغمبر) بخشی از آن را اظهار کرد و از بخشی (دیگر) اعراض نمود. و چون (موضوع) را به آن (زن) خبر داد، وی گفت: چه کسی این را به تو خبر داده است؟ گفت: (الله) دانای آگاه به من خبر داده است.');
INSERT INTO "badkhashani" VALUES(66,4,'66:4','(شما ای دو همسر پیغمبر) اگر به درگاه الله توبه کنید (برایتان بهتر است)، بی‌گمان دلهایتان منحرف گشته است و اگر بر ضد او همدست شوید (بدانید) که الله مددگار اوست و (نیز) جبرئیل و مؤمنان نیک و فرشتگان مددگار او هستند.');
INSERT INTO "badkhashani" VALUES(66,5,'66:5','اگر (پیغمبر) شما را طلاق دهد زود است که پروردگارش او را همسرانی بهتر از شما اعم از بیوه و دختر، عوض دهد که زنانی مسلمان، مؤمن، فرمانبردار، توبه‌گار، عبادت‌پیشه (و) روزه‌دار باشند.');
INSERT INTO "badkhashani" VALUES(66,6,'66:6','ای کسانی که ایمان آورده‌اید! خودتان و خانواده تان را از آتشی حفظ کنید که هیزمش مردم و سنگها خواهند بود. فرشتگانی بر آن مقرر است که خشن و سخت گیر و زورمند و توانا هستند، از الله در آن چه به آنان امر کند نافرمانی نمی‌کنند و آنچه را که فرمان یابند، انجام می‌دهند.');
INSERT INTO "badkhashani" VALUES(66,7,'66:7','ای آنانی که کافر شده‌اید! امروز عذرخواهی نکنید. جز این نیست که در برابر آنچه (در دنیا) می‌کردید، جزا داده می‌شوید.');
INSERT INTO "badkhashani" VALUES(66,8,'66:8','ای کسانی که ایمان آورده‌اید! به درگاه الله رجوع کنید و توبه خالصانه بکنید امید است پروردگارتان گناهانتان را محو کند و شما را به باغهایی در آورد که از زیر (درختان و قصرهای آن) نهرها روان است. روزی که الله پیغمبرش و آنانی را که با او ایمان آورده‌اند خوار و رسوا نمی‌سازد. نورشان پیش روی آنان و به سمت راستشان با شتاب روان است. می‌گویند: ای پروردگار ما! نور ما را برای ما کامل گردان و ما را بیامرز چون تو بر هر کاری توانایی.');
INSERT INTO "badkhashani" VALUES(66,9,'66:9','ای پیغمبر! با کافران و منافقان جهاد کن و بر آنان سخت گیر، و جایگاه آنان دوزخ است و بدجای بازگشت است.');
INSERT INTO "badkhashani" VALUES(66,10,'66:10','الله برای کافران، زن نوح و زن لوط را مثال زده است که (آن دو) در نکاح دو بنده از بندگان صالح ما بودند ولی به آنان خیانت کردند پس آن (دو پیغمبر) نتوانستند از (عذاب) الله چیزی را از آن (دو زن) دفع کنند و گفته شد: با واردشدگان به آتش (دوزخ) درآئید.');
INSERT INTO "badkhashani" VALUES(66,11,'66:11','و الله برای مؤمنان (نیز) زن فرعون را مثل زده است، هنگامی که گفت: ای پروردگار من! برای من در بهشت نزد خودت خانه‌ای بساز و مرا از فرعون و کارهایش نجات بده و مرا از (دست) گروه ظالم نیز نجات بده.');
INSERT INTO "badkhashani" VALUES(66,12,'66:12','و (الله) مریم دختر عمران (را نیز مثل زده) که شرمگاه خود را از حرام نگه داشت (و پاکدامنی ورزید)، پس از روح (آفریدۀ) خود در او دمیدیم و او سخنان پروردگارش و کتاب‌های او را تصدیق کرد و از اطاعت کنندگان بود.');
INSERT INTO "badkhashani" VALUES(67,1,'67:1','بابرکت (و بزرگوار) است آن ذاتی که پادشاهی (عالم) به دست اوست و او بر هر چیزی قادر است.');
INSERT INTO "badkhashani" VALUES(67,2,'67:2','آن ذاتی که مرگ و زندگی را آفرید تا شما را بیازماید که کدام یک از شما نیکوکارتر است و او غالب (و) آمرزگار است.');
INSERT INTO "badkhashani" VALUES(67,3,'67:3','آن ذاتی که هفت آسمان را طبقه طبقه (و هماهنگ) آفريد، در آفرینش پروردگار مهربان هیچ تفاوت و خللی نمی‌بینی، باز چشم بگردان آیا (در آن) نقص و خللی می‌بینی؟');
INSERT INTO "badkhashani" VALUES(67,4,'67:4','باز هم پی در پی چشم خود را برگردان، (خواهی دید که) چشم در حالیکه خسته و ناتوان است به سویت باز می‌گردد.');
INSERT INTO "badkhashani" VALUES(67,5,'67:5','البته آسمان دنیا را با چراغها زینت دادیم و آنها را (وسیلۀ) سنگسار کردن شیطان‌ها آماده کردیم و عذاب سوزان را برای آنها آماده کرده‌ایم.');
INSERT INTO "badkhashani" VALUES(67,6,'67:6','و برای آنانی که به پروردگارشان کفر ورزیدند عذاب دوزخ (آماده است) و بد بازگشتگاهی است.');
INSERT INTO "badkhashani" VALUES(67,7,'67:7','چون در آنجا افگنده شوند از آن آوازهایی (بد) می‌شنوند در حالیکه می‌جوشد.');
INSERT INTO "badkhashani" VALUES(67,8,'67:8','نزدیک است که (دوزخ) از خشم پاره پاره شود، هرگاه که گروهی (از کفار) در آن انداخته شوند، نگهبانانش از آنها می‌پرسند: آیا بیم دهنده‌ای به‌سوی شما نیامده بود؟');
INSERT INTO "badkhashani" VALUES(67,9,'67:9','گویند: بلی! بیم دهنده‌ای نزد ما آمد لیكن ما (دعوت او را) دروغ شمردیم و گفتیم: الله چیزی را نازل نکرده است. جز این نیست که شما در گمراهی بس بزرگ هستید.');
INSERT INTO "badkhashani" VALUES(67,10,'67:10','و گویند اگر می‌شنیدیم و یا تعقل می‌کردیم در (زمرۀ) دوزخیان قرار نمی‌گرفتیم.');
INSERT INTO "badkhashani" VALUES(67,11,'67:11','پس به گناه خود اعتراف کردند. پس نفرین بر دوزخیان باد!');
INSERT INTO "badkhashani" VALUES(67,12,'67:12','يقينا آنانی که در پنهانی از پروردگار خود می‌ترسند آمرزش و اجر بزرگ دارند.');
INSERT INTO "badkhashani" VALUES(67,13,'67:13','و سخنان را نهان دارید یا آن را آشکار سازید. (در هر صورت) او به راز دلها آگاه است.');
INSERT INTO "badkhashani" VALUES(67,14,'67:14','آیا کسی که (همه چیز را) آفریده است نمی‌داند؟ در حاليكه او باریک بین از هر چیز آگاه است.');
INSERT INTO "badkhashani" VALUES(67,15,'67:15','او ذاتی است که زمین را برایتان مسخر گردانیده است پس در اطراف آن بروید و از روزی الله بخورید و بازگشت (زنده شدن دوباره) در دست اوست.');
INSERT INTO "badkhashani" VALUES(67,16,'67:16','آیا از ذاتی که در آسمان است خود را در امان می‌دانید که شما را در زمین فرو برد در حالیکه آن می‌جنبد.');
INSERT INTO "badkhashani" VALUES(67,17,'67:17','آیا از ذاتی که در آسمان است ایمن شده‌اید که طوفانی از سنگریزه بر شما بفرستد؟! پس خواهید دانست که بیم دادن من چگونه است!');
INSERT INTO "badkhashani" VALUES(67,18,'67:18','و البته آنانی که پیش از اینها بودند (پیغمبران را) تکذیب کردند (پس ببین) عقوبت من چگونه بوده است!');
INSERT INTO "badkhashani" VALUES(67,19,'67:19','آیا پرندگان را بالای سرشان ندیدند که گاهی بالهای خود را گسترده و گاهی جمع می‌کنند؟! جز پروردگار مهربان آنها را (از افتیدن) نگاه نمی‌دارد، يقينا او هر چیزی را می‌بیند.');
INSERT INTO "badkhashani" VALUES(67,20,'67:20','و کیست آن که لشکر شما باشد و به جای پروردگار مهربان به شما کمک کند؟ کافران جز در فریب و گمراهی نیستند.');
INSERT INTO "badkhashani" VALUES(67,21,'67:21','یا کیست کسی که اگر الله روزی خود را قطع کند به شما روزی دهد؟ بلکه در سرکشی و نفرت لجاجت می‌کنند.');
INSERT INTO "badkhashani" VALUES(67,22,'67:22','پس آیا کسی که بر چهرۀ خود افتاده راه می‌رود هدایت یافته‌تر است یا کسی که ایستاده به راه راست می‌رود؟');
INSERT INTO "badkhashani" VALUES(67,23,'67:23','بگو: او ذاتی است که شما را آفریده و برایتان گوش و چشم‌ها و دل‌ها قرار داده است. (ولی) کمتر شکر گزاری می‌کنید.');
INSERT INTO "badkhashani" VALUES(67,24,'67:24','بگو: او ذاتی است که شما را در زمین منتشر ساخت و تنها به‌سوی او حشر می‌شوید.');
INSERT INTO "badkhashani" VALUES(67,25,'67:25','و می‌گویند: این وعده‌ای که می‌دهید کی خواهید بود اگر راست می‌گویید؟');
INSERT INTO "badkhashani" VALUES(67,26,'67:26','بگو: علم (آن) تنها نزد الله است و جز این نیست که من بيم دهندۀ آشکار هستم.');
INSERT INTO "badkhashani" VALUES(67,27,'67:27','پس هرگاه آن را (که وعده داده می‌شدند) نزدیک ببینند، چهره‌های کافران قبيح و گرفته می‌گردد و گفته می‌شود. این همان وعده‌ای است که می‌طلبیدید.');
INSERT INTO "badkhashani" VALUES(67,28,'67:28','بگو: به من خبر دهید اگر الله من و همراهان مرا هلاک کند و یا بر ما رحم کند پس چه کسی کافران را از عذاب دردناک پناه می‌دهد؟');
INSERT INTO "badkhashani" VALUES(67,29,'67:29','بگو: او پروردگار مهربان است، به او ایمان آورده‌ایم و تنها بر او توکل کرده‌ایم، پس خواهید دانست که چه کسی در گمراهی آشکار است.');
INSERT INTO "badkhashani" VALUES(67,30,'67:30','بگو: به من خبر دهید اگر آب شما در زمین فرو رود، چه کسی برایتان آبی روان (و شیرین) می‌آورد؟');
INSERT INTO "badkhashani" VALUES(68,1,'68:1','ن. (مفهوم این حرف به الله معلوم است)، قسم به قلم و به آنچه می‌نویسند.');
INSERT INTO "badkhashani" VALUES(68,2,'68:2','(که) تو به فضل پروردگارت دیوانه نیستی.');
INSERT INTO "badkhashani" VALUES(68,3,'68:3','و يقينا برای تو اجر (دائمی و) بی‌پایان است.');
INSERT INTO "badkhashani" VALUES(68,4,'68:4','و حقا که تو دارای اخلاق بس بزرگ هستی.');
INSERT INTO "badkhashani" VALUES(68,5,'68:5','پس خواهی دید و (آنان) هم خواهند دید.');
INSERT INTO "badkhashani" VALUES(68,6,'68:6','(و خواهند دید) که کدام یک از شما دیوانه است؟');
INSERT INTO "badkhashani" VALUES(68,7,'68:7','البته پروردگارت به کسی که از راهش گمراه شده داناتر است و او به راه یافتگان (هم) داناتر می‌باشد.');
INSERT INTO "badkhashani" VALUES(68,8,'68:8','پس از تکذیب کنندگان اطاعت مکن.');
INSERT INTO "badkhashani" VALUES(68,9,'68:9','آنان (مشرکین) آرزو دارند که نرمی و سازش کنی تا آنان هم نرمی و سازش از خود نشان دهند.');
INSERT INTO "badkhashani" VALUES(68,10,'68:10','و از هر حقیر و پستی که بسیار سوگند می‌خورد پیروی مکن.');
INSERT INTO "badkhashani" VALUES(68,11,'68:11','(آن که) بسیار عیب جوی است (و) همیشه سخن چینی می‌کند.');
INSERT INTO "badkhashani" VALUES(68,12,'68:12','بسیار منع کنندۀ خير (و) تجاوزگر گناهکار است.');
INSERT INTO "badkhashani" VALUES(68,13,'68:13','بد اخلاق است، (و نیز) بی‌اصل و نسب است.');
INSERT INTO "badkhashani" VALUES(68,14,'68:14','(این همه بدی‌ها در او به این سبب است) که دارای مال و اولاد (بسیار) است.');
INSERT INTO "badkhashani" VALUES(68,15,'68:15','چون آیات ما بر او خوانده شود می‌گوید: افسانه‌های پیشینیان است.');
INSERT INTO "badkhashani" VALUES(68,16,'68:16','به زودی بر بینی او داغ ذلت خواهیم نهاد (و او را رسوا خواهیم کرد).');
INSERT INTO "badkhashani" VALUES(68,17,'68:17','البته ما آنان را آزمودیم طوری که صاحبان آن باغ را آزمودیم، وقتی که قسم خوردند که میوه‌های باغ را صبحگاهان بچینند.');
INSERT INTO "badkhashani" VALUES(68,18,'68:18','و ان شاء الله نگفتند.');
INSERT INTO "badkhashani" VALUES(68,19,'68:19','پس بر آن باغ بلایی از سوی پروردگارت آمد در حالیکه آنان خوابیده بودند.');
INSERT INTO "badkhashani" VALUES(68,20,'68:20','پس (باغ‌شان چنان سوخت که) مانند شب تاریک گردید.');
INSERT INTO "badkhashani" VALUES(68,21,'68:21','باز (بی خبر از ماجرا) صبحگاهان همدیگر را ندا دادند.');
INSERT INTO "badkhashani" VALUES(68,22,'68:22','که اگر قصد چیدن میوه دارید، پس صبح به کشتزار برسید.');
INSERT INTO "badkhashani" VALUES(68,23,'68:23','پس روان شدند در حالیکه آهسته به یکدیگر می‌گفتند.');
INSERT INTO "badkhashani" VALUES(68,24,'68:24','که نباید امروز مسکینی در باغ نزد شما بیاید.');
INSERT INTO "badkhashani" VALUES(68,25,'68:25','و صبحگاهان قصد باغ کردند، در حالیکه خود را قادر بر جمع محصول (و منع مساکین) می‌پنداشتند.');
INSERT INTO "badkhashani" VALUES(68,26,'68:26','پس چون باغ را (سوخته) دیدند، گفتند: حتما ما راه را گم کرده‌ایم.');
INSERT INTO "badkhashani" VALUES(68,27,'68:27','(نه) بلکه ما از کشتزار خود محروم شده‌ایم.');
INSERT INTO "badkhashani" VALUES(68,28,'68:28','بهترین آنان گفت: آیا به شما نگفته بودم که چرا الله را به پاکی یاد نمی‌کنید؟');
INSERT INTO "badkhashani" VALUES(68,29,'68:29','گفتند: پروردگار ما پاک و منزه است، البته ما ظالم بودیم.');
INSERT INTO "badkhashani" VALUES(68,30,'68:30','پس ملامت کنان به یکدیگر روی آوردند.');
INSERT INTO "badkhashani" VALUES(68,31,'68:31','گفتند: ای وای بر ما! واقعا ما سرکش بودیم.');
INSERT INTO "badkhashani" VALUES(68,32,'68:32','امید است پروردگار ما بهتر از آن را برای ما عوض دهد، يقينا ما به پروردگار خویش رغبت و امید داریم.');
INSERT INTO "badkhashani" VALUES(68,33,'68:33','این چنین است عذاب (دنیا) و البته عذاب آخرت (از این) بزرگتر است، اگر می‌دانستند.');
INSERT INTO "badkhashani" VALUES(68,34,'68:34','البته برای پرهیزگاران در نزد پروردگارشان باغ‌های پر نعمت است.');
INSERT INTO "badkhashani" VALUES(68,35,'68:35','پس آیا مسلمانان (فرمانبرداران) را مانند مجرمان (گناهکاران) قرار می‌دهیم؟');
INSERT INTO "badkhashani" VALUES(68,36,'68:36','شما را چه شده؛ چگونه حکم می‌کنید؟');
INSERT INTO "badkhashani" VALUES(68,37,'68:37','آیا برای شما کتابی هست که در آن می‌خوانید؟');
INSERT INTO "badkhashani" VALUES(68,38,'68:38','و شما آنچه را که می‌پسندید در آن است؟');
INSERT INTO "badkhashani" VALUES(68,39,'68:39','آیا شما بر عهدۀ ما تا روز قیامت عهدهایی محکم دارید؟ که هر چه را حکم کنید برای شما باشد؟');
INSERT INTO "badkhashani" VALUES(68,40,'68:40','از آنها (کفار) سوال کن که کدام یک از ایشان ضامن چنین عهد است.');
INSERT INTO "badkhashani" VALUES(68,41,'68:41','یا برای آنها شریکهائی است، پس اگر راست می‌گویند، شریکان خود را بیاورند.');
INSERT INTO "badkhashani" VALUES(68,42,'68:42','روزی که ساق (پروردگار عالميان) آشکار ساخته می‌شود و به سجده فراخوانده می‌شوند ولی نمی‌توانند.');
INSERT INTO "badkhashani" VALUES(68,43,'68:43','این در حالی است که چشمانشان به زیر افتاده و خواری و ذلت ایشان را فرا گرفته است، در حالیکه (در دنیا) برای سجده کردن فراخوانده می‌شدند و آنها سالم (و تندرست) بودند (ولی سجده نمی‌کردند).');
INSERT INTO "badkhashani" VALUES(68,44,'68:44','پس مرا با کسی بگذار که این (قرآن) سخن الهی را تکذیب می‌کند، زود است که به تدریج آنها را از جایی که نمی‌دانند خواهیم گرفت.');
INSERT INTO "badkhashani" VALUES(68,45,'68:45','اما (فعلا) ایشان را مهلت می‌دهیم، چون تدبير من محکم و استوار است.');
INSERT INTO "badkhashani" VALUES(68,46,'68:46','آیا از ایشان مزدی طلب می‌کنی که آنان از پرداختن آن سنگین بارند؟');
INSERT INTO "badkhashani" VALUES(68,47,'68:47','یا اینکه نزدشان علم غیب وجود دارد که ایشان (از روی آن) می‌نویسند؟');
INSERT INTO "badkhashani" VALUES(68,48,'68:48','پس در رسیدن حکم پروردگارت صابر باش و مانند صاحب ماهی مباش که با دلی پر اندوه ندا (دعا) کرد.');
INSERT INTO "badkhashani" VALUES(68,49,'68:49','اگر نعمت پروردگارش نبود، در حالیکه قابل ملامتی بود به صحرایی بی‌آب و گیاه، افگنده می‌شد.');
INSERT INTO "badkhashani" VALUES(68,50,'68:50','(لیکن باز) پروردگارش او را برگزید و در زمرۀ صالحان قرار داد.');
INSERT INTO "badkhashani" VALUES(68,51,'68:51','و به يقين نزدیک بود کافران چون قرآن را شنیدند تو را با چشم‌های (تیز) خود بلغزانند و می‌گویند: يقينا این شخص (پیغمبر) دیوانه است.');
INSERT INTO "badkhashani" VALUES(68,52,'68:52','حال آنکه آن (قرآن) پندی برای جهانیان است.');
INSERT INTO "badkhashani" VALUES(69,1,'69:1','حاقه (حادثۀ ثابت و واقع شدنی).');
INSERT INTO "badkhashani" VALUES(69,2,'69:2','چیست آن حاقه (حادثۀ ثابت و واقع شدنی)؟');
INSERT INTO "badkhashani" VALUES(69,3,'69:3','و تو چه می‌دانی که آن حاقه (حادثۀ ثابت و واقع شدنی) چیست؟');
INSERT INTO "badkhashani" VALUES(69,4,'69:4','قوم ثمود و عاد آن حادثۀ کوبنده و واقع شدنی را تکذیب نمودند.');
INSERT INTO "badkhashani" VALUES(69,5,'69:5','اما ثمود به (وسیلۀ) آواز سخت و مرگبار هلاک شدند.');
INSERT INTO "badkhashani" VALUES(69,6,'69:6','و اما عاد به (وسیلۀ) تندبادی سرد و سوزنده هلاک شدند.');
INSERT INTO "badkhashani" VALUES(69,7,'69:7','که الله آن را هفت شب و هشت روز پی در پی بر آنان مسلط کرد آنگاه مردمان را می‌دیدی که روی زمین افتاده‌اند و گویی تنه‌های پوک درختان خرما‌اند.');
INSERT INTO "badkhashani" VALUES(69,8,'69:8','پس آیا می‌بینی که اثری از آنها باقی مانده باشد؟');
INSERT INTO "badkhashani" VALUES(69,9,'69:9','و فرعون و کسانی که پیش از او بودند و همچنین اهالی قریه‌های زیر و زبر شده (قوم لوط) مرتکب گناه و نافرمانی شدند.');
INSERT INTO "badkhashani" VALUES(69,10,'69:10','زیرا آنان از (فرمان) فرستادۀ پروردگارشان نافرمانی کردند، و الله ایشان را به سختی گرفت.');
INSERT INTO "badkhashani" VALUES(69,11,'69:11','البته وقتی که آب (به امر ما) طغیان کرد، شما را در کشتی روان برداشتیم.');
INSERT INTO "badkhashani" VALUES(69,12,'69:12','تا آن (عذاب) را برایتان پندی قرار دهیم و گوش‌هایی شنوا آن را به خاطر بسپارد.');
INSERT INTO "badkhashani" VALUES(69,13,'69:13','پس چون در صور دمیده شود یک دمیدنی.');
INSERT INTO "badkhashani" VALUES(69,14,'69:14','و زمین و کوه‌ها برداشته شوند، پس يکباره درهم کوبیده شوند.');
INSERT INTO "badkhashani" VALUES(69,15,'69:15','پس در آن روز واقعه (قیامت) رخ دهد.');
INSERT INTO "badkhashani" VALUES(69,16,'69:16','و آسمان (نیز) بشگافد و در آن روز سست گردد.');
INSERT INTO "badkhashani" VALUES(69,17,'69:17','و فرشته‌ها بر اطراف آن (آسمان) خواهند بود و عرش پروردگارت را در آن روز هشت فرشته بالای سر خود حمل می‌کنند.');
INSERT INTO "badkhashani" VALUES(69,18,'69:18','در آن روز شما (به بارگاه پروردگارتان) عرضه می‌شوید، هیچ پوشیدۀ شما (که آنرا انجام داده بودید) مخفی نماند.');
INSERT INTO "badkhashani" VALUES(69,19,'69:19','پس هر کس که نامۀ اعمالش به دست راستش داده شود (شادی کنان) می‌گوید: بگیرید نامۀ اعمال مرا بخوانید.');
INSERT INTO "badkhashani" VALUES(69,20,'69:20','چون من یقین داشتم که البته با حساب خودم روبرو خواهم شد.');
INSERT INTO "badkhashani" VALUES(69,21,'69:21','پس او در زندگانی خوش و رضایت بخش خواهد بود.');
INSERT INTO "badkhashani" VALUES(69,22,'69:22','در بهشت (بسیار) بلند خواهد بود.');
INSERT INTO "badkhashani" VALUES(69,23,'69:23','که میوۀ آن در دسترس است.');
INSERT INTO "badkhashani" VALUES(69,24,'69:24','(گفته می‌شود) گوارا بخورید و بنوشید به (پاداش) آنچه در روزگاران گذشته انجام میدادید.');
INSERT INTO "badkhashani" VALUES(69,25,'69:25','و اما کسی که نامۀ اعمالش به دست چپش داده شود، پس می‌گوید: ای کاش نامه‌ام بمن داده نمی‌شد.');
INSERT INTO "badkhashani" VALUES(69,26,'69:26','و (ای کاش) نمی‌دانستم که حساب من چیست.');
INSERT INTO "badkhashani" VALUES(69,27,'69:27','و ای کاش مرگ فیصله کننده و پایان کارم بود.');
INSERT INTO "badkhashani" VALUES(69,28,'69:28','دارایی من هیچ نفعی بمن نرساند.');
INSERT INTO "badkhashani" VALUES(69,29,'69:29','قدرت و پادشاهی من از دستم رفت.');
INSERT INTO "badkhashani" VALUES(69,30,'69:30','(گفته می‌شود) او را بگیرید، پس به زنجیرش کشید.');
INSERT INTO "badkhashani" VALUES(69,31,'69:31','باز او را در دوزخ داخل کنید.');
INSERT INTO "badkhashani" VALUES(69,32,'69:32','باز در زنجیری که درازی آن هفتاد گز باشد او را ببندید.');
INSERT INTO "badkhashani" VALUES(69,33,'69:33','چون او به الله بزرگ ایمان نمی‌آورد.');
INSERT INTO "badkhashani" VALUES(69,34,'69:34','و (مردم را) بر طعام دادن مسکین ترغیب نمی‌کرد.');
INSERT INTO "badkhashani" VALUES(69,35,'69:35','پس او امروز در این جا هیچ دوست مهربانی ندارد.');
INSERT INTO "badkhashani" VALUES(69,36,'69:36','و نه طعامی دارد جز زرداب دوزخیان.');
INSERT INTO "badkhashani" VALUES(69,37,'69:37','که جز گنهگاران آن را نمی‌خورند.');
INSERT INTO "badkhashani" VALUES(69,38,'69:38','پس قسم به آنچه می‌بینید.');
INSERT INTO "badkhashani" VALUES(69,39,'69:39','و به آنچه نمی‌بینید.');
INSERT INTO "badkhashani" VALUES(69,40,'69:40','که این (قرآن) سخن فرستاده (فرشته) بزرگوار است.');
INSERT INTO "badkhashani" VALUES(69,41,'69:41','و آن سخن شاعری نیست، اندکی ایمان می‌آورید.');
INSERT INTO "badkhashani" VALUES(69,42,'69:42','و (قرآن) گفتۀ غیبگو و کاهن نیست، اندکی عبرت می‌گیرید.');
INSERT INTO "badkhashani" VALUES(69,43,'69:43','(بلکه) از سوی پروردگار جهانیان نازل شده است.');
INSERT INTO "badkhashani" VALUES(69,44,'69:44','و اگر (بالفرض) پیغمبر بعضی از سخنان را به دروغ به ما نسبت می‌کرد.');
INSERT INTO "badkhashani" VALUES(69,45,'69:45','البته او را با قدرت می‌گرفتیم.');
INSERT INTO "badkhashani" VALUES(69,46,'69:46','باز رگ دلش را قطع می‌کردیم.');
INSERT INTO "badkhashani" VALUES(69,47,'69:47','پس هیچ کس از شما منع کننده‌ای (جزای ما) از او نیست.');
INSERT INTO "badkhashani" VALUES(69,48,'69:48','البته قرآن برای پرهیزگاران نصیحت و پند است.');
INSERT INTO "badkhashani" VALUES(69,49,'69:49','و البته ما به یقین می‌دانیم که برخی از شما تکذیب‌کننده‌اید.');
INSERT INTO "badkhashani" VALUES(69,50,'69:50','و البته آن (تکذیب) مایۀ حسرت بر کافران است.');
INSERT INTO "badkhashani" VALUES(69,51,'69:51','و البته آن (قرآن سخن) حق و یقینی است.');
INSERT INTO "badkhashani" VALUES(69,52,'69:52','پس نام پروردگار بزرگت را به پاکی یاد کن.');
INSERT INTO "badkhashani" VALUES(70,1,'70:1','درخواست کننده‌ای (به تمسخر) عذاب واقع شدنی را طلب کرد.');
INSERT INTO "badkhashani" VALUES(70,2,'70:2','بر کافران است (و) هیچ دفع‌کننده‌ای برای آن نیست.');
INSERT INTO "badkhashani" VALUES(70,3,'70:3','از سوی الله صاحب درجات و مراتب بلند (است).');
INSERT INTO "badkhashani" VALUES(70,4,'70:4','فرشتگان و (نیز) روح در روزی که مقدارش پنجاه هزار سال است به‌سوی او بالا می‌روند.');
INSERT INTO "badkhashani" VALUES(70,5,'70:5','پس صبر کن صبری نیکو (و بدون شکوه).');
INSERT INTO "badkhashani" VALUES(70,6,'70:6','البته آنان آن (روز) را دور می‌بیند.');
INSERT INTO "badkhashani" VALUES(70,7,'70:7','و ما آن را نزدیک می‌بینیم.');
INSERT INTO "badkhashani" VALUES(70,8,'70:8','(قیامت آن) روزی که آسمان مانند مس گداخته شود.');
INSERT INTO "badkhashani" VALUES(70,9,'70:9','و کوه‌ها مانند پشم حلاجی شده و رنگین گردد.');
INSERT INTO "badkhashani" VALUES(70,10,'70:10','و هیچ دوستی از (حال) دوست دیگر نمی‌پرسد.');
INSERT INTO "badkhashani" VALUES(70,11,'70:11','(دوستان و خویشاوندان) به ایشان نشان داده می‌شوند، گناهکار آرزو می‌کند که فرزندان خود را فديه (برای نجات) از عذاب آن روز قرار دهد.');
INSERT INTO "badkhashani" VALUES(70,12,'70:12','همچنین همسر و برادرش را.');
INSERT INTO "badkhashani" VALUES(70,13,'70:13','و قبیلۀ خود را که به او جای میدادند (و مدد می‌کردند).');
INSERT INTO "badkhashani" VALUES(70,14,'70:14','(بلکه می‌خواهد که) تمام انسان‌های روی زمین را (فدا کند) تا (این کار) او را نجات دهد.');
INSERT INTO "badkhashani" VALUES(70,15,'70:15','هرگز نه، دوزخ آتش سوزان و سراپا شعله است.');
INSERT INTO "badkhashani" VALUES(70,16,'70:16','که پوست بدن را به شدت جدا می‌کند.');
INSERT INTO "badkhashani" VALUES(70,17,'70:17','کسی را طلب می‌کند که پشت کرده و (از حق) روی گردانیده است.');
INSERT INTO "badkhashani" VALUES(70,18,'70:18','و (نیز کسی را که) مال جمع کرد و نگاه داشت.');
INSERT INTO "badkhashani" VALUES(70,19,'70:19','واقعا که انسان بی‌تاب و بی‌صبر (و حريص) آفریده شده است.');
INSERT INTO "badkhashani" VALUES(70,20,'70:20','چون به او مصيبت رسد بی‌تاب و پریشان می‌گردد.');
INSERT INTO "badkhashani" VALUES(70,21,'70:21','و چون خوبی به او رسد بخيل می‌گردد (و منع می‌کند).');
INSERT INTO "badkhashani" VALUES(70,22,'70:22','مگر نمازگزاران.');
INSERT INTO "badkhashani" VALUES(70,23,'70:23','آنانی که بر نماز‌شان (پایبندی و) مداومت دارند.');
INSERT INTO "badkhashani" VALUES(70,24,'70:24','و آنانی که در اموالشان سهمی معین و معلوم است.');
INSERT INTO "badkhashani" VALUES(70,25,'70:25','برای گدا و محروم.');
INSERT INTO "badkhashani" VALUES(70,26,'70:26','و آنانی که به روز جزا (قیامت) یقین کامل دارند.');
INSERT INTO "badkhashani" VALUES(70,27,'70:27','و آنانی که از عذاب پروردگارشان بیمناک و ترسناکند.');
INSERT INTO "badkhashani" VALUES(70,28,'70:28','چون از عذاب پروردگارشان ایمنی نیست.');
INSERT INTO "badkhashani" VALUES(70,29,'70:29','و آنانی که شرمگاه‌هایشان را (از حرام) محافظت می‌کنند.');
INSERT INTO "badkhashani" VALUES(70,30,'70:30','مگر بر زنان و کنیزان خود که (در این صورت) ملامتی برایشان نیست.');
INSERT INTO "badkhashani" VALUES(70,31,'70:31','پس هر کس فراتر از این را (برای قضای شهوت خود) بجوید آنانند که تجاوزکارند.');
INSERT INTO "badkhashani" VALUES(70,32,'70:32','و آنان که ایشان امانتها و عهدشان را رعایت می‌کنند.');
INSERT INTO "badkhashani" VALUES(70,33,'70:33','و آنان که به (ادای) گواهی‌هایشان قایم هستند (و از آن انکار نمی‌کنند).');
INSERT INTO "badkhashani" VALUES(70,34,'70:34','و آنان که ایشان بر نمازهای خود محافظت و مواظبت می‌کنند.');
INSERT INTO "badkhashani" VALUES(70,35,'70:35','ایشان‌اند که در باغ‌های جنت باعزت و گرامی داشته می‌شوند.');
INSERT INTO "badkhashani" VALUES(70,36,'70:36','پس کافران را چه شده که به‌سوی تو شتابان می‌آیند؟!');
INSERT INTO "badkhashani" VALUES(70,37,'70:37','گروه گروه از جانب راست و از جانب چپ.');
INSERT INTO "badkhashani" VALUES(70,38,'70:38','آیا هر یک از ایشان آرزو دارد که به بهشت پرناز و نعمت در آورده شود؟.');
INSERT INTO "badkhashani" VALUES(70,39,'70:39','نه، چنین نیست، يقينا ما آنان را از آنچه می‌دانند آفریده‌ایم.');
INSERT INTO "badkhashani" VALUES(70,40,'70:40','سوگند به پروردگار مغربها و مشرقها که ما توانائیم.');
INSERT INTO "badkhashani" VALUES(70,41,'70:41','بر اینکه بهتر از آنان را جایگزین کنیم و ما ناتوان نیستیم.');
INSERT INTO "badkhashani" VALUES(70,42,'70:42','پس آنان را به حال خود بگذار تا در باطل خود فرو روند و (به متاع دنیا) بازی کنند تا روزی را که وعده داده شده‌اند ملاقات کنند.');
INSERT INTO "badkhashani" VALUES(70,43,'70:43','روزی که شتابان از قبرهای خود بیرون می‌شوند. گویی آنان به‌سوی نشانه‌های (بت‌های) نصب شده می‌دوند.');
INSERT INTO "badkhashani" VALUES(70,44,'70:44','در حالیکه چشمانشان زیر افتاده و خواری و ذلت ایشان را فرا گرفته است، این است آن روزی که وعده داده می‌شدند.');
INSERT INTO "badkhashani" VALUES(71,1,'71:1','البته ما نوح را به‌سوی قومش فرستادیم که قوم خود را پیش از این که عذاب دردناک به سراغ آنها بیاید، بترسان.');
INSERT INTO "badkhashani" VALUES(71,2,'71:2','(نوح) گفت: ای قوم من! من برای شما بیم دهنده‌ای آشکارم.');
INSERT INTO "badkhashani" VALUES(71,3,'71:3','این که الله را عبادت کنید و از او بترسید و مرا اطاعت کنید.');
INSERT INTO "badkhashani" VALUES(71,4,'71:4','که الله گناهان شما را می‌آمرزد و اجل شما را تا وقتی معین به تأخیر می‌اندازد. يقيناً هرگاه اجل (و مهلت) مقرر الله برسد به تأخیر نمی‌افتد، اگر می‌دانید.');
INSERT INTO "badkhashani" VALUES(71,5,'71:5','نوح گفت: ای پروردگارم! من قومم را شب و روز (به ‌سوی توحید) دعوت دادم.');
INSERT INTO "badkhashani" VALUES(71,6,'71:6','ولی دعوت من جز بر فرار آنان نیفزود.');
INSERT INTO "badkhashani" VALUES(71,7,'71:7','و هرگاه من آنان را دعوت دادم تا (گناهان)‌شان را بیامرزی، انگشتان‌شان را به گوش‌های خود در آوردند و جامه‌هایشان را به چهره‌های خود پیچیدند و بر (طغیان خود) اصرار نمودند و سخت تکبر ورزیدند.');
INSERT INTO "badkhashani" VALUES(71,8,'71:8','باز ایشان را به آواز بلند دعوت دادم.');
INSERT INTO "badkhashani" VALUES(71,9,'71:9','باز (دعوتم را) برای آنان اعلان کردم و گاهی به صورت نهانی (هم) به ایشان دعوت دادم.');
INSERT INTO "badkhashani" VALUES(71,10,'71:10','پس گفتم: از پروردگارتان مغفرت بخواهید که او بسیار آمرزنده است.');
INSERT INTO "badkhashani" VALUES(71,11,'71:11','(اگر چنین کنید الله) از آسمان باران‌های پیاپی بر شما می‌باراند.');
INSERT INTO "badkhashani" VALUES(71,12,'71:12','و شما را با اموال و فرزندان مدد می‌کند و برای شما باغها قرار میدهد و برای شما نهرها جاری می‌سازد.');
INSERT INTO "badkhashani" VALUES(71,13,'71:13','شما را چه شده است که بزرگی و جلال برای الله قایل نمی‌شوید؟');
INSERT INTO "badkhashani" VALUES(71,14,'71:14','حال آنکه شما را در مراحل مختلف آفریده است.');
INSERT INTO "badkhashani" VALUES(71,15,'71:15','آیا نمی‌بینید که الله چگونه هفت آسمان را یکی بر بالای دیگر آفریده است؟!');
INSERT INTO "badkhashani" VALUES(71,16,'71:16','و ماه را در میان آنها تابان و خورشید را چراغ درخشان گردانید؟!');
INSERT INTO "badkhashani" VALUES(71,17,'71:17','و الله شما را از زمین (مانند گیاه) رویانید (آفرید).');
INSERT INTO "badkhashani" VALUES(71,18,'71:18','باز شما را به زمین باز می‌گرداند و (باز) شما را (از زمین) بیرون می‌آورد.');
INSERT INTO "badkhashani" VALUES(71,19,'71:19','و الله زمین را برای شما بستر هموار گردانید.');
INSERT INTO "badkhashani" VALUES(71,20,'71:20','تا در راه‌های وسيع آن بروید.');
INSERT INTO "badkhashani" VALUES(71,21,'71:21','نوح (در دعای خود) گفت: پروردگارا! بی‌گمان ایشان از من نافرمانی کردند و از کسی پیروی کردند که مال و اولادش جز زیان و نقصان بر او نیفزود.');
INSERT INTO "badkhashani" VALUES(71,22,'71:22','و آن (پیشوایان در بارۀ پیروان خود) دست به مکری بس بزرگ زدند.');
INSERT INTO "badkhashani" VALUES(71,23,'71:23','و گفتند: معبودان خود را ترک نکنید خصوصا (پنج بت را که عبارت‌اند از) ود و سواع و يغوث و يعوق و نسر را (ترک نکنید).');
INSERT INTO "badkhashani" VALUES(71,24,'71:24','واقعا که مردم بسیار را گمراه کردند (پس) ای الله! ظالمان را جز گمراهی نیفزا.');
INSERT INTO "badkhashani" VALUES(71,25,'71:25','به (سبب) گناهانشان غرق شدند باز به آتش در آورده شدند و برای خود در برابر الله مددگارانی نیافتند.');
INSERT INTO "badkhashani" VALUES(71,26,'71:26','و نوح گفت: ای پروردگار! هیچ کسی از کافران را بر روی زمین باقی مگذار.');
INSERT INTO "badkhashani" VALUES(71,27,'71:27','زیرا اگر آنها را زنده بگذاری بندگانت را گمراه می‌سازند و جز بد کار و کافر نمی‌زایند.');
INSERT INTO "badkhashani" VALUES(71,28,'71:28','ای پروردگارم! من و پدر و مادرم را و هر کسی را که مؤمن به خانه‌ام داخل شود و مردان و زنان مؤمن را بیامرز و ظالمان را جز نابودی نیفزا.');
INSERT INTO "badkhashani" VALUES(72,1,'72:1','بگو: به من وحی شده که گروهی از جن (به تلاوت من) گوش دادند، پس گفتند: ما قرآن عجیبی شنیدیم.');
INSERT INTO "badkhashani" VALUES(72,2,'72:2','که به راه راست هدایت می‌کند، پس به آن ایمان آوردیم و (بعد از این) هرگز کسی را با پروردگار خود شریک نمی‌سازیم.');
INSERT INTO "badkhashani" VALUES(72,3,'72:3','و (ایمان آوردیم به اینکه) ‌شأن و شکوه پروردگار ما بسیار بلند است، او زن و فرزندی (برای خود) نگرفته است.');
INSERT INTO "badkhashani" VALUES(72,4,'72:4','و (شگفت) اینکه نادان ما دربارۀ الله سخنان دروغ و ناحق می‌گفت (که او شریکی دارد).');
INSERT INTO "badkhashani" VALUES(72,5,'72:5','و ما گمان می‌کردیم که انسانها و جن‌ها هرگز بر الله دروغ نخواهند گفت.');
INSERT INTO "badkhashani" VALUES(72,6,'72:6','و اینکه مردانی از انسان به مردانی از جن پناه می‌بردند، پس به این وسیله بر سر کشی آنها افزودند.');
INSERT INTO "badkhashani" VALUES(72,7,'72:7','و اینکه آنها (انسانها) گمان کردند طوری که شما (جنیات) گمان کردید که هرگز الله کسی را (به پیغمبری) مبعوث نخواهد کرد.');
INSERT INTO "badkhashani" VALUES(72,8,'72:8','و اینکه ما به آسمان‌ها نزدیک شدیم و آن را چنان یافتیم که از محافظانی نیرومند و شهاب‌ها پر شده است.');
INSERT INTO "badkhashani" VALUES(72,9,'72:9','و اینکه ما (پیش از این) در گوشه‌های آسمان برای استراق سمع (شنیدن کلام عالم بالا) می‌نشستیم ولی اگر کسی اکنون گوش دهد، برای خود شهابی مراقب را می‌یابد.');
INSERT INTO "badkhashani" VALUES(72,10,'72:10','و اینکه ما نمی‌دانیم که آیا برای ساکنان زمین شر اراده شده یا این که پروردگارشان برای آنان خیر و هدایت خواسته است.');
INSERT INTO "badkhashani" VALUES(72,11,'72:11','و اینکه برخی از ما صالح و نیک و برخی غير آن (بد)‌اند. ما گروه‌های گوناگون هستیم.');
INSERT INTO "badkhashani" VALUES(72,12,'72:12','ما یقین کردیم که هرگز نمی‌توانیم بر ارادۀ الله در زمین غالب شویم و نمی‌توانیم با فرار از سلطۀ او خارج شویم.');
INSERT INTO "badkhashani" VALUES(72,13,'72:13','و اینکه چون (ندای حق و) هدایت را شنیدیم به آن ایمان آوردیم، پس هر کس که به پروردگارش ایمان بیاورد، نه از نقصان حقی می‌ترسد و نه از ستمی.');
INSERT INTO "badkhashani" VALUES(72,14,'72:14','و اینکه (اکنون) بعضی از ما فرمانبردار و بعضی از ما سرکش و ظالم‌اند، پس کسانی که اسلام آوردند، ایشان‌اند که راه راست را می‌طلبند.');
INSERT INTO "badkhashani" VALUES(72,15,'72:15','و اما ظالمان (و منحرفان) پس هیزم دوزخند.');
INSERT INTO "badkhashani" VALUES(72,16,'72:16','و اگر بر راه راست استقامت کنند البته آنها را با آب بسیار سیراب می‌کنیم.');
INSERT INTO "badkhashani" VALUES(72,17,'72:17','(و لیکن بر آنها تنگی نمودیم) تا ایشان را بیازماییم، و هر کس از ذکر و یاد پروردگارش روی گردان گردد او را به عذابی سخت داخل می‌گرداند.');
INSERT INTO "badkhashani" VALUES(72,18,'72:18','و اینکه مساجد خاص برای عبادت الله است، پس کسی دیگر را با الله مخوانید.');
INSERT INTO "badkhashani" VALUES(72,19,'72:19','و اینکه چون بنده الله بر خواست تا او را به (عبادت الله) بخواند نزدیک بود که بر او هجوم آورند.');
INSERT INTO "badkhashani" VALUES(72,20,'72:20','بگو: (ای محمد) من فقط پروردگار خود را می‌خوانم (عبادت میکنم) و کسی را با او شریک نمی‌گردانم،');
INSERT INTO "badkhashani" VALUES(72,21,'72:21','بگو: من مالک هیچ گونه زیانی برای شما نیستم و نمی‌توانم شما را هدایت کنم.');
INSERT INTO "badkhashani" VALUES(72,22,'72:22','بگو: هرگز کسی مرا از عذاب الله پناه داده نتواند و هرگز پشت و پناهی جز او نمی‌یابم.');
INSERT INTO "badkhashani" VALUES(72,23,'72:23','تنها (می‌توانم) پیغامی از جانب الله و رسالت‌های او را به انجام برسانم و هرکس از (فرمان) الله و رسول او نافرمانی کند بی‌گمان آتش دوزخ برای او خواهد بود که در آن جاودانه می‌ماند.');
INSERT INTO "badkhashani" VALUES(72,24,'72:24','(آنان در غفلت به سر می‌برند) تا آنکه آنچه را به آنها وعده داده می‌شود ببینند، پس آن وقت خواهند دانست که چه کسی مدد گارانش ناتوان‌تر و تعدادش کمتر است.');
INSERT INTO "badkhashani" VALUES(72,25,'72:25','بگو: نمی‌دانم آیا آنچه به شما وعده داده می‌شود نزدیک است یا اینکه پروردگارم مدت طولانی برای آن مقرر می‌کند؟');
INSERT INTO "badkhashani" VALUES(72,26,'72:26','(چون او) دانای همه رازهای پوشیده است، پس هیچ کسی را بر غیب خود آگاه نمی‌سازد.');
INSERT INTO "badkhashani" VALUES(72,27,'72:27','مگر کسی از رسولانی که خود بپسندد پس )در این صورت(پیشاپیش و پشت سر او نگهبانی می‌فرستد.');
INSERT INTO "badkhashani" VALUES(72,28,'72:28','تا بداند که پیغمبران پیام‌های پروردگارشان را رسانده‌اند و (الله) به آنچه که در نزد آنان است احاطه دارد و هر چیزی را با عدد شماره کرده است.');
INSERT INTO "badkhashani" VALUES(73,1,'73:1','ای جامه بخود پیچیده!');
INSERT INTO "badkhashani" VALUES(73,2,'73:2','شب برخیز مگر اندکی (از آن را).');
INSERT INTO "badkhashani" VALUES(73,3,'73:3','(برخیز) نیمی از شب یا اندکی از آن کم کن.');
INSERT INTO "badkhashani" VALUES(73,4,'73:4','یا اندکی بر آن بیافزای و قرآن را با تدبر و تأمل و شمرده بخوان.');
INSERT INTO "badkhashani" VALUES(73,5,'73:5','(چون) ما کلام نهایت گران (و موجب مسئولیت را) بر تو القا خواهیم کرد.');
INSERT INTO "badkhashani" VALUES(73,6,'73:6','به يقين شب زنده داری در تزکیه و اصلاح نفس مؤثرتر و در سخن استوارتر است.');
INSERT INTO "badkhashani" VALUES(73,7,'73:7','چون تو در روز مشغولیت و تلاش طولانی داری.');
INSERT INTO "badkhashani" VALUES(73,8,'73:8','و نام پروردگارت را یاد کن و (از هر چیز صرف نظر کرده و) به‌سوی او متوجه شو.');
INSERT INTO "badkhashani" VALUES(73,9,'73:9','(چون) او پروردگار مشرق و مغرب و (همه جهات) است که هیچ معبودی (بر حق) به جز او نیست، پس او را وکیل و کارساز خود قرار ده.');
INSERT INTO "badkhashani" VALUES(73,10,'73:10','و بر آنچه (کفار) می‌گویند صبر کن و به شیوۀ نیکو از ایشان دوری کن.');
INSERT INTO "badkhashani" VALUES(73,11,'73:11','و مرا با این تکذیب کنندگان برخوردار از ناز و نعمت تنها بگذار و آنها را اندکی مهلت ده.');
INSERT INTO "badkhashani" VALUES(73,12,'73:12','(که) نزد ما (سزاهایی چون) زنجیرهای سنگین و آتش سوزان دوزخ است.');
INSERT INTO "badkhashani" VALUES(73,13,'73:13','و (نزد ما) غذایی گلوگیر و عذاب دردناک است.');
INSERT INTO "badkhashani" VALUES(73,14,'73:14','(این عذاب) در روزی خواهد بود که زمین و کوه‌ها بجنبند و کوه‌ها به توده‌های پراگنده و تپه‌های ریگ روان تبدیل می‌گردند.');
INSERT INTO "badkhashani" VALUES(73,15,'73:15','البته ما پیغمبری را که بر شما گواه است به‌سوی شما فرستادیم، چنانکه به‌سوی فرعون پیغمبری فرستادیم.');
INSERT INTO "badkhashani" VALUES(73,16,'73:16','پس فرعون از آن رسول نافرمانی کرد (لذا) او را به سزای سخت گرفتیم.');
INSERT INTO "badkhashani" VALUES(73,17,'73:17','پس اگر کفر ورزید، چگونه خود را از (عذاب) روزی که کودکان را پیر می‌گرداند نگاه می‌دارید؟!');
INSERT INTO "badkhashani" VALUES(73,18,'73:18','آسمان به سبب (هول) آن روز شق می‌شود (چون) وعدۀ او حتما شدنی است.');
INSERT INTO "badkhashani" VALUES(73,19,'73:19','البته این (قرآن) پند است، پس هرکس که بخواهد راهی به‌سوی پروردگار خود در پیش بگیرد.');
INSERT INTO "badkhashani" VALUES(73,20,'73:20','همانا پروردگارت می‌داند که تو و گروهی از کسانی که با تو هستند نزدیک به دو سوم شب، یا نصف و یا یک سوم آن را به نماز می‌ایستید. و الله شب و روز را اندازه می‌کند. الله دانست که شما نمی‌توانید حساب آن را داشته باشید، لذا از شما درگذشت، پس آن مقدار از قرآن را (در نماز) بخوانید که برایتان میسر است و الله دانست که برخی از شما بیمار خواهند بود و دیگرانی هم برای به دست آوردن نعمت الله در زمین مسافرت می‌کنند و گروهی دیگر در راه الله جهاد می‌کنند. پس آنچه میسر باشد از آن بخوانید. و نماز را برپا کنید و زکات را بپردازید و به الله قرض الحسنه بدهید (در راه او به اخلاص انفاق کنید). و کارهای نیکی را که برای خودتان پیش می‌فرستید آن را نزد الله بهتر و با پاداش بیشتر خواهید یافت. و از الله مغفرت بخواهید که یقینا الله آمرزندۀ مهربان است.');
INSERT INTO "badkhashani" VALUES(74,1,'74:1','ای جامه بر خود کشیده.');
INSERT INTO "badkhashani" VALUES(74,2,'74:2','برخیز و (مردم را از عذاب الله) بترسان.');
INSERT INTO "badkhashani" VALUES(74,3,'74:3','و پروردگارت را به بزرگی یاد کن.');
INSERT INTO "badkhashani" VALUES(74,4,'74:4','و لباس‌هایت را پاکیزه دار.');
INSERT INTO "badkhashani" VALUES(74,5,'74:5','و از پلیدی دوری کن.');
INSERT INTO "badkhashani" VALUES(74,6,'74:6','و بخشش مکن تا بیش از آن بخواهی.');
INSERT INTO "badkhashani" VALUES(74,7,'74:7','و برای (رضایت) پروردگارت صبر کن.');
INSERT INTO "badkhashani" VALUES(74,8,'74:8','پس چون در صور دمیده شود.');
INSERT INTO "badkhashani" VALUES(74,9,'74:9','پس آن روز، روزی سخت است.');
INSERT INTO "badkhashani" VALUES(74,10,'74:10','بر کافران آسان نیست.');
INSERT INTO "badkhashani" VALUES(74,11,'74:11','مرا با آن که تنها آفریدمش بگذار.');
INSERT INTO "badkhashani" VALUES(74,12,'74:12','و به او مال فراوان دادم.');
INSERT INTO "badkhashani" VALUES(74,13,'74:13','و پسرانی که (هر وقت در کنارش) حاضرند.');
INSERT INTO "badkhashani" VALUES(74,14,'74:14','و برای او (وسائل زندگی را) از هر نظر آماده ساختم.');
INSERT INTO "badkhashani" VALUES(74,15,'74:15','(با این حال) باز هم طمع دارد که او را بیشتر دهم.');
INSERT INTO "badkhashani" VALUES(74,16,'74:16','هرگز نه، زیرا او در مقابل آیات ما عناد می‌ورزید.');
INSERT INTO "badkhashani" VALUES(74,17,'74:17','(بلکه) به زودی او را به بالا شدن بر گردنۀ سخت مجبور می‌سازم');
INSERT INTO "badkhashani" VALUES(74,18,'74:18','همانا او (برای مبارزه با قرآن) اندیشید و سنجيد.');
INSERT INTO "badkhashani" VALUES(74,19,'74:19','پس مرگ بر او باد چگونه سنجید!؟');
INSERT INTO "badkhashani" VALUES(74,20,'74:20','باز مرگ بر او باد چگونه سنجید؟');
INSERT INTO "badkhashani" VALUES(74,21,'74:21','باز نگریست.');
INSERT INTO "badkhashani" VALUES(74,22,'74:22','باز روی ترش کرد و پیشانی در هم کشید.');
INSERT INTO "badkhashani" VALUES(74,23,'74:23','باز پشت گردانید و تکبر کرد.');
INSERT INTO "badkhashani" VALUES(74,24,'74:24','پس گفت: این (قرآن) چیزی جز جادوی نقل شده (از پیشینیان) نیست.');
INSERT INTO "badkhashani" VALUES(74,25,'74:25','و این (قرآن) نیست مگر گفتۀ بشر.');
INSERT INTO "badkhashani" VALUES(74,26,'74:26','(به سزای این سخن) زود است که او را در سقر (دوزخ) داخل کنم.');
INSERT INTO "badkhashani" VALUES(74,27,'74:27','و تو چه می‌دانی که سقر چیست؟');
INSERT INTO "badkhashani" VALUES(74,28,'74:28','(سقر چیزی است که) نه باقی می‌گذارد و نه رها می‌کند.');
INSERT INTO "badkhashani" VALUES(74,29,'74:29','تغیر دهنده و سوزانندۀ پوست انسان است.');
INSERT INTO "badkhashani" VALUES(74,30,'74:30','بر آن (دوزخ) نوزده فرشته (محافظ) مقرراند.');
INSERT INTO "badkhashani" VALUES(74,31,'74:31','و ما محافظان دوزخ را جز از فرشتگان قرار ندادیم و شمار آنها را نیز جز وسيلۀ آزمایش کافران قرار ندادیم، تا اهل کتاب يقين کنند (که قرآن کتاب آسمانی است) و تا ایمان مؤمنان بیشتر شود و تا اهل کتاب و مؤمنان شک نکنند و تا بیماردلان (منافقان) و کافران بگویند: الله از این مثل چه خواسته است؟ الله این چنین هر کس را که بخواهد گمراه می‌سازد و هر کس را که بخواهد هدایت می‌کند. و لشکرهای پروردگارت را جز او کسی نمی‌داند و این جز اندرزی برای بشر نیست.');
INSERT INTO "badkhashani" VALUES(74,32,'74:32','نه چنین نیست (که کفار می‌پندارند)، قسم به ماه.');
INSERT INTO "badkhashani" VALUES(74,33,'74:33','قسم به شب چون پشت کند.');
INSERT INTO "badkhashani" VALUES(74,34,'74:34','قسم به صبح چون روشن شود.');
INSERT INTO "badkhashani" VALUES(74,35,'74:35','که دوزخ یکی از نشانه‌های بزرگ (قدرت الله) است.');
INSERT INTO "badkhashani" VALUES(74,36,'74:36','در حالیکه ترسانندۀ بشر است.');
INSERT INTO "badkhashani" VALUES(74,37,'74:37','(ترساننده است) برای هر کس از شما که بخواهد پیش رود (در گناه)، یا پس ماند (از نیکی).');
INSERT INTO "badkhashani" VALUES(74,38,'74:38','هر نفس (هر شخص) در گرو کاری است که انجام داده است.');
INSERT INTO "badkhashani" VALUES(74,39,'74:39','مگر اصحاب دست راست.');
INSERT INTO "badkhashani" VALUES(74,40,'74:40','که آنها در باغها‌اند و می‌پرسند.');
INSERT INTO "badkhashani" VALUES(74,41,'74:41','از مجرمان (کافران)');
INSERT INTO "badkhashani" VALUES(74,42,'74:42','که شما را چه چیز در دوزخ در آورد؟');
INSERT INTO "badkhashani" VALUES(74,43,'74:43','(در جواب) گویند: ما از نمازگزاران نبودیم.');
INSERT INTO "badkhashani" VALUES(74,44,'74:44','و مسکینان را طعام نمی‌دادیم.');
INSERT INTO "badkhashani" VALUES(74,45,'74:45','(بلکه) با آنانی که سخن باطل می‌گفتند (در کفر گویی) فرو می‌رفتیم.');
INSERT INTO "badkhashani" VALUES(74,46,'74:46','و روز جزا را دروغ می‌شمردیم،');
INSERT INTO "badkhashani" VALUES(74,47,'74:47','تا آن که مرگ به سراغ ما آمد.');
INSERT INTO "badkhashani" VALUES(74,48,'74:48','پس (این تکذیب کنندگان بی‌ایمان را) شفاعت شفاعت کنندگان نفع نرساند.');
INSERT INTO "badkhashani" VALUES(74,49,'74:49','پس آنان را چه شده است که از پند روی گردانند؟!');
INSERT INTO "badkhashani" VALUES(74,50,'74:50','گویا آنها گوره خرانی رمنده‌اند.');
INSERT INTO "badkhashani" VALUES(74,51,'74:51','که از شیر گریخته‌اند.');
INSERT INTO "badkhashani" VALUES(74,52,'74:52','بلکه هر فردی از آنها می‌خواهد که صحیفه‌های سرگشاده (از سوی الله) به او داده شود.');
INSERT INTO "badkhashani" VALUES(74,53,'74:53','چنین نیست، بلکه ایشان از روز آخرت نمی‌ترسند.');
INSERT INTO "badkhashani" VALUES(74,54,'74:54','واقعا که آن قرآن تذكر (و پند ارزشمند) است.');
INSERT INTO "badkhashani" VALUES(74,55,'74:55','پس هر کس بخواهد آن را یاد کند.');
INSERT INTO "badkhashani" VALUES(74,56,'74:56','و یاد نمی‌کنند مگر اینکه الله بخواهد (و به ایشان توفیق دهد). او (الله) سزاوار تقوی است و او سزاوار آمرزش است.');
INSERT INTO "badkhashani" VALUES(75,1,'75:1','قسم می‌خورم به روز قیامت.');
INSERT INTO "badkhashani" VALUES(75,2,'75:2','و قسم می‌خورم به نفس ملامت کننده (و پشیمان‌شونده).');
INSERT INTO "badkhashani" VALUES(75,3,'75:3','آیا انسان گمان می‌کند که استخوان‌هایش را جمع نخواهیم کرد؟');
INSERT INTO "badkhashani" VALUES(75,4,'75:4','بلی! (چنین خواهیم کرد) در حالیکه قادریم بر آن که (حتى) سرانگشتان او را (هم) برابر سازیم.');
INSERT INTO "badkhashani" VALUES(75,5,'75:5','بلکه انسان می‌خواهد (با انکار نمودن) راه فسق و فجور را پیش روی خود باز کند.');
INSERT INTO "badkhashani" VALUES(75,6,'75:6','(و به طور استهزا) می‌پرسد: روز قیامت چه وقت است؟');
INSERT INTO "badkhashani" VALUES(75,7,'75:7','پس وقتی که چشم‌ها خیره شود.');
INSERT INTO "badkhashani" VALUES(75,8,'75:8','و ماه بی‌نور و تاریک شود.');
INSERT INTO "badkhashani" VALUES(75,9,'75:9','و آفتاب و ماه یکجا جمع شود.');
INSERT INTO "badkhashani" VALUES(75,10,'75:10','و (وقتی روز قیامت برپا شود) انسان می‌گوید: راه گریز کجاست؟');
INSERT INTO "badkhashani" VALUES(75,11,'75:11','نه! هرگز جای (گریز و) پناه نیست.');
INSERT INTO "badkhashani" VALUES(75,12,'75:12','(بلکه) در آن روز قرارگاه تنها به‌سوی پروردگار توست.');
INSERT INTO "badkhashani" VALUES(75,13,'75:13','انسان در آن روز به آنچه پیش فرستاده و به آنچه پس گذاشته است خبر داده می‌شود.');
INSERT INTO "badkhashani" VALUES(75,14,'75:14','بلکه انسان به (خوب و بد) نفس خود بیناست.');
INSERT INTO "badkhashani" VALUES(75,15,'75:15','اگرچه (در آن روز) عذرهایی پیش کند.');
INSERT INTO "badkhashani" VALUES(75,16,'75:16','(ای پیغمبر! در وقت نزول وحی) زبانت را حرکت مده تا در یاد کردن آن شتاب کنی.');
INSERT INTO "badkhashani" VALUES(75,17,'75:17','بی‌گمان جمع کردن آن در سینۀ تو و خواندنش بر عهدۀ ماست.');
INSERT INTO "badkhashani" VALUES(75,18,'75:18','پس چون آن را (به زبان جبریل) خواندیم، پس قرائت آن را پیروی کن.');
INSERT INTO "badkhashani" VALUES(75,19,'75:19','باز بیان (حقایق و تفسیر) آن بر عهدۀ ماست.');
INSERT INTO "badkhashani" VALUES(75,20,'75:20','نه، چنین نیست (که می‌گویید) بلکه شما دنیای زودگذر را دوست می‌دارید.');
INSERT INTO "badkhashani" VALUES(75,21,'75:21','و آخرت را ترک می‌کنید.');
INSERT INTO "badkhashani" VALUES(75,22,'75:22','(حال آن که کامیاب و ناکام در آخرت معلوم شود) در آن روز چهره‌هایی شاداب (و خرم) هستند.');
INSERT INTO "badkhashani" VALUES(75,23,'75:23','(چرا که) به پروردگار خود می‌نگرند.');
INSERT INTO "badkhashani" VALUES(75,24,'75:24','و چهره‌هایی در آن روز گرفته (و غمگین) است.');
INSERT INTO "badkhashani" VALUES(75,25,'75:25','(چون) یقین دارند که به معصیت کمرشکن گرفتار می‌شوند.');
INSERT INTO "badkhashani" VALUES(75,26,'75:26','چنین نیست (که ایشان گمان دارند) هنگامی که جان به گلوگاه رسد.');
INSERT INTO "badkhashani" VALUES(75,27,'75:27','و گفته شود: آیا کسی هست که افسون بخواند؟');
INSERT INTO "badkhashani" VALUES(75,28,'75:28','و (محتضر) يقين کند که زمان جدایی (روح از بدن) فرا رسیده است.');
INSERT INTO "badkhashani" VALUES(75,29,'75:29','و ساق پا به ساق دیگر بپیچد (که دیگر حرکت نتواند).');
INSERT INTO "badkhashani" VALUES(75,30,'75:30','آن روز، روز سوق دادنش به‌سوی پروردگار توست.');
INSERT INTO "badkhashani" VALUES(75,31,'75:31','پس (انسان نافرمان) نه تصدیق کرد و نه نماز گزارد.');
INSERT INTO "badkhashani" VALUES(75,32,'75:32','بلکه دروغ شمرد و اعراض نمود.');
INSERT INTO "badkhashani" VALUES(75,33,'75:33','باز متکبرانه و خرامان به‌سوی خانواده‌اش رفت.');
INSERT INTO "badkhashani" VALUES(75,34,'75:34','وای بر تو، باز وای بر تو.');
INSERT INTO "badkhashani" VALUES(75,35,'75:35','باز وای بر تو، وای بر تو.');
INSERT INTO "badkhashani" VALUES(75,36,'75:36','آیا انسان گمان می‌کند که به حال خود گذاشته می‌شود (و با او حساب و کتاب صورت نمی‌گیرد)؟');
INSERT INTO "badkhashani" VALUES(75,37,'75:37','آیا او نطفه‌ای از منی که در رحم ریخته شده، نبود؟');
INSERT INTO "badkhashani" VALUES(75,38,'75:38','باز خون بسته‌ای بود که (الله آن را) آفرید باز (خلقت او را) استوار و برابر کرد.');
INSERT INTO "badkhashani" VALUES(75,39,'75:39','پس از آن (قطرۀ منی) دو جفت مرد و زن را آفرید.');
INSERT INTO "badkhashani" VALUES(75,40,'75:40','و آیا همین (آفرینندۀ انسان‌ها از منی) قادر نیست که (بار دوم) مُرده‌ها را زنده کند؟');
INSERT INTO "badkhashani" VALUES(76,1,'76:1','آیا بر انسان مدتی از زمان گذشت که چیزی قابل ذکر نبود.');
INSERT INTO "badkhashani" VALUES(76,2,'76:2','البته ما انسان را آمیخته از نطفه (مرد و زن) آفریده‌ایم، تا او را بیازمائیم (لذا) او را شنوای بینا گردانیدیم.');
INSERT INTO "badkhashani" VALUES(76,3,'76:3','البته ما به انسان راه راست را نشان دادیم، خواه شکرگزار باشد، خواه ناشکر.');
INSERT INTO "badkhashani" VALUES(76,4,'76:4','(لیکن) ما برای کفار زنجیرها و طوق‌ها و آتش سوزان را آماده کرده‌ايم.');
INSERT INTO "badkhashani" VALUES(76,5,'76:5','و البته نیکان از جامی می‌نوشند که با کافور آمیخته شده است.');
INSERT INTO "badkhashani" VALUES(76,6,'76:6','چشمه‌ای که بندگان نیک الله از آن می‌نوشد، هرگونه بخواهند آن را جاری می‌سازند.');
INSERT INTO "badkhashani" VALUES(76,7,'76:7','(علامت آن بندگان الله این است که) به نذرهای خود وفا می‌کنند و از روزی می‌ترسند که شرش عام و گسترده است.');
INSERT INTO "badkhashani" VALUES(76,8,'76:8','و طعام را با وجود محبت و حاجت خود، به بینوا و یتیم و اسیر می‌دهند.');
INSERT INTO "badkhashani" VALUES(76,9,'76:9','(و می‌گویند) ما برای رضای الله به شما طعام می‌دهیم و از شما هیچ پاداش و تشکری نمی‌خواهیم.');
INSERT INTO "badkhashani" VALUES(76,10,'76:10','البته ما از پروردگار ما می‌ترسیم در روزی که ترش روی و دشوار است.');
INSERT INTO "badkhashani" VALUES(76,11,'76:11','پس الله آن‌ها را از شر و ضرر آن روز حفظ کرد و به آن‌ها درخشانی چهره و خرمی بخشید.');
INSERT INTO "badkhashani" VALUES(76,12,'76:12','و در مقابل صبرشان (الله) باغ و (لباس‌های) ابریشمی به آنان پاداش می‌دهد.');
INSERT INTO "badkhashani" VALUES(76,13,'76:13','در حالی که بر تخت‌ها تکیه زده‌اند، نه (گرمی) آفتاب و نه (سوز) سرما را در آن جا می‌بینند.');
INSERT INTO "badkhashani" VALUES(76,14,'76:14','و سایه‌های آن (درختان جنت) به ایشان نزدیک است و میوه‌هایش در دسترس (شان) قرار داده شود.');
INSERT INTO "badkhashani" VALUES(76,15,'76:15','و بر آن‌ها ظرف‌های نقره‌ای و جام‌های بلورین گردانیده می‌شود.');
INSERT INTO "badkhashani" VALUES(76,16,'76:16','بلورهای از نقره، که آن را به اندازه‌های (متفاوت) اندازه‌گیری کرده‌اند.');
INSERT INTO "badkhashani" VALUES(76,17,'76:17','و در آن جا جام شرابی نوشانیده می‌شوند که آمیختۀ آن از چشمۀ زنجبیل است.');
INSERT INTO "badkhashani" VALUES(76,18,'76:18','چشمۀ است در جنت که سلسبیل نامیده می‌شود.');
INSERT INTO "badkhashani" VALUES(76,19,'76:19','و پسران همیشه جوان (برای خدمت) بر آنان می‌گردند و چون ایشان را ببینی، آنان را مرواریدهای پراگنده می‌پنداری.');
INSERT INTO "badkhashani" VALUES(76,20,'76:20','و چون آن جا را نگاه کنی، نعمت فراوان و پادشاهی بزرگ را می‌بینی.');
INSERT INTO "badkhashani" VALUES(76,21,'76:21','بر تن آن‌ها (جنتیان) لباس‌های ابریشم نازک و ابریشم ضخیم است و با دستبندهای از نقره آراسته شده‌اند، و پروردگارشان شراب پاکیزه به آن‌ها می‌نوشاند.');
INSERT INTO "badkhashani" VALUES(76,22,'76:22','البته این (نعمت‌ها) پاداش (اعمال) شما است و سعی‌تان مورد سپاس (و قابل تقدیر) است.');
INSERT INTO "badkhashani" VALUES(76,23,'76:23','البته ما قرآن را به تدریج بر تو نازل کرده‌ایم.');
INSERT INTO "badkhashani" VALUES(76,24,'76:24','پس بر حکم پروردگارت صابر باش و گناهکار یا ناسپاس آنان را اطاعت مکن.');
INSERT INTO "badkhashani" VALUES(76,25,'76:25','و نام پروردگارت را در هر صبح و شام یاد کن.');
INSERT INTO "badkhashani" VALUES(76,26,'76:26','و در حصه‌ای از شب برای الله نماز بگذار و در حصۀ طولانی از شب او را به پاکی یاد کن.');
INSERT INTO "badkhashani" VALUES(76,27,'76:27','بی‌گمان ایشان دنیای زودگذر را دوست می‌دارند و روز دشوار آخرت را پشت سر خویش می‌نهند.');
INSERT INTO "badkhashani" VALUES(76,28,'76:28','ما آن‌ها را آفریدیم و آفرینش آن‌ها را استوار و محکم کردیم و هر وقت بخواهیم مانند آن‌ها انسان‌های دیگری را جایگزین‌شان می‌کنیم.');
INSERT INTO "badkhashani" VALUES(76,29,'76:29','یقیناً این (تعلیمات قرآن)، یادآوری و پندی است؛ پس هرکس بخواهد راهی را به‌سوی پروردگارش برگزیند.');
INSERT INTO "badkhashani" VALUES(76,30,'76:30','شما نمی‌توانید (چیزی را) بخواهید مگر این که الله بخواهد، بی‌گمان الله دانای باحکمت است.');
INSERT INTO "badkhashani" VALUES(76,31,'76:31','(لذا) در رحمت خود کسی را که بخواهد داخل می‌کند و برای ظالم‌ها (که مستحق رحمت نیستند) عذاب دردناک را آماده کرده‌ایم.');
INSERT INTO "badkhashani" VALUES(77,1,'77:1','قسم به بادهای فرستاده شده‌ای پی در پی.');
INSERT INTO "badkhashani" VALUES(77,2,'77:2','(باز) قسم به بادهای تیز وزنده.');
INSERT INTO "badkhashani" VALUES(77,3,'77:3','و قسم به بادهای که ابرها را منتشر می‌کنند (برای باریدن باران).');
INSERT INTO "badkhashani" VALUES(77,4,'77:4','باز قسم به فرشتگان فرق‌کننده (بین حق و باطل).');
INSERT INTO "badkhashani" VALUES(77,5,'77:5','پس قسم به فرشتگان القا کنندۀ وعظ و وحی.');
INSERT INTO "badkhashani" VALUES(77,6,'77:6','برای رفع عذر یا بیم دادن (از عذاب الله).');
INSERT INTO "badkhashani" VALUES(77,7,'77:7','که البته آنچه وعده داده می‌شوید (از جزا و سزا) واقع شدنی است.');
INSERT INTO "badkhashani" VALUES(77,8,'77:8','پس وقتی که ستاره‌ها بی‌نور و محو شوند.');
INSERT INTO "badkhashani" VALUES(77,9,'77:9','و وقتی که آسمان شگافته شود.');
INSERT INTO "badkhashani" VALUES(77,10,'77:10','و وقتی که کوه‌ها ریشه کن و پراگنده شوند.');
INSERT INTO "badkhashani" VALUES(77,11,'77:11','و وقتی که برای (جمع شدن) پیغمبران وقت تعیین شود.');
INSERT INTO "badkhashani" VALUES(77,12,'77:12','برای کدام روزی پس انداخته شده‌اند؟');
INSERT INTO "badkhashani" VALUES(77,13,'77:13','برای روز فیصله (به دادگاه الهی پس انداخته شده‌اند).');
INSERT INTO "badkhashani" VALUES(77,14,'77:14','و تو چه می‌دانی که روز فیصله چیست؟');
INSERT INTO "badkhashani" VALUES(77,15,'77:15','در آن روز وای بر تکذیب‌کنندگان.');
INSERT INTO "badkhashani" VALUES(77,16,'77:16','آیا ما پیشینیان (مجرم) را هلاک نکردیم؟');
INSERT INTO "badkhashani" VALUES(77,17,'77:17','باز پسينيان [مجرم‌] را در پی آنان می‌آوریم.');
INSERT INTO "badkhashani" VALUES(77,18,'77:18','ما با مجرمان اینگونه رفتار می‌کنیم.');
INSERT INTO "badkhashani" VALUES(77,19,'77:19','در آن روز وای بر تکذیب‌کنندگان.');
INSERT INTO "badkhashani" VALUES(77,20,'77:20','آیا شما را از آب حقیر (منی) نیافریدیم؟');
INSERT INTO "badkhashani" VALUES(77,21,'77:21','باز آن را در قرارگاه محفوظ و استوار (رحم مادر) قرار دادیم.');
INSERT INTO "badkhashani" VALUES(77,22,'77:22','تا مدتی معین.');
INSERT INTO "badkhashani" VALUES(77,23,'77:23','پس اندازه کردیم (برای بودنِ آن در رحم مادر) پس ما چه خوب اندازه‌کنندگانیم.');
INSERT INTO "badkhashani" VALUES(77,24,'77:24','در آن روز وای بر تکذیب‌کنندگان.');
INSERT INTO "badkhashani" VALUES(77,25,'77:25','آیا زمین را جمع‌کننده نگردانیدیم؟');
INSERT INTO "badkhashani" VALUES(77,26,'77:26','(برای) زنده‌ها و مرده‌ها؟');
INSERT INTO "badkhashani" VALUES(77,27,'77:27','و در آن کوه‌های بلند قرار دادیم و به شما (از زمین) آب شیرین نوشانیدیم.');
INSERT INTO "badkhashani" VALUES(77,28,'77:28','در آن روز وای بر تکذیب‌کنندگان.');
INSERT INTO "badkhashani" VALUES(77,29,'77:29','(به مجرمان گویند) بروید به‌سوی آنچه که آن را دروغ می‌شمردید.');
INSERT INTO "badkhashani" VALUES(77,30,'77:30','بروید به‌سوی سایۀ سه شاخه (سایۀ آتش دوزخ).');
INSERT INTO "badkhashani" VALUES(77,31,'77:31','(آن سایه) نه سردی دارد و نه گرمی شعلۀ آتش را دفع می‌کند.');
INSERT INTO "badkhashani" VALUES(77,32,'77:32','آتش پاره‌هایی از خود بیرون می‌اندازد که مانند قصر (بزرگ) است.');
INSERT INTO "badkhashani" VALUES(77,33,'77:33','گویی شعله‌های آن (در بزرگی) مانند شترهای زردرنگ است.');
INSERT INTO "badkhashani" VALUES(77,34,'77:34','در آن روز وای بر تکذیب‌کنندگان.');
INSERT INTO "badkhashani" VALUES(77,35,'77:35','این روزی است که (کفار) نمی‌توانند سخن بگویند.');
INSERT INTO "badkhashani" VALUES(77,36,'77:36','و به آنان اجازه داده نمی‌شود تا عذرخواهی کنند.');
INSERT INTO "badkhashani" VALUES(77,37,'77:37','در آن روز وای بر تکذیب‌کنندگان.');
INSERT INTO "badkhashani" VALUES(77,38,'77:38','(و گفته شود) این روز روزِ فیصله است که شما و مردمان پیشین را جمع کردیم.');
INSERT INTO "badkhashani" VALUES(77,39,'77:39','پس اگر حیله‌ای دارید پس به کار گیرید.');
INSERT INTO "badkhashani" VALUES(77,40,'77:40','در آن روز وای بر تکذیب‌کنندگان.');
INSERT INTO "badkhashani" VALUES(77,41,'77:41','البته پرهیزگاران در سایه‌ها و چشمه‌ها قرار دارند.');
INSERT INTO "badkhashani" VALUES(77,42,'77:42','و از هر نوع میوه که بخواهند.');
INSERT INTO "badkhashani" VALUES(77,43,'77:43','(به آن‌ها گفته شود که) بخورید و بنوشید خوشگوار به پاداش آنچه (در دنیا) می‌کردید.');
INSERT INTO "badkhashani" VALUES(77,44,'77:44','همانا ما اینطور نیکوکاران را پاداش می‌دهیم.');
INSERT INTO "badkhashani" VALUES(77,45,'77:45','در آن روز وای بر تکذیب‌کنندگان.');
INSERT INTO "badkhashani" VALUES(77,46,'77:46','(به تکذیب‌کنندگان گفته می‌شود) بخورید و بهره گیرید اندک، یقیناً شما مجرمید.');
INSERT INTO "badkhashani" VALUES(77,47,'77:47','در آن روز وای بر تکذیب‌کنندگان.');
INSERT INTO "badkhashani" VALUES(77,48,'77:48','و چون به آنان گفته شود به رکوع بروید نمی‌توانند به رکوع بروند.');
INSERT INTO "badkhashani" VALUES(77,49,'77:49','در آن روز وای بر تکذیب‌کنندگان.');
INSERT INTO "badkhashani" VALUES(77,50,'77:50','پس بعد از این (انکار قرآن) به کدام سخن ایمان می‌آورند؟');
INSERT INTO "badkhashani" VALUES(78,1,'78:1','دربارۀ چه از یکدیگر می‌پرسند؟');
INSERT INTO "badkhashani" VALUES(78,2,'78:2','از خبر بزرگ (می‌پرسند).');
INSERT INTO "badkhashani" VALUES(78,3,'78:3','(خبری) که آن‌ها در آن اختلاف دارند.');
INSERT INTO "badkhashani" VALUES(78,4,'78:4','چنین نیست (آن خبر را عنقریب) خواهند دانست.');
INSERT INTO "badkhashani" VALUES(78,5,'78:5','(باز می‌گویم) چنین نیست (آن خبر را عنقریب) خواهند دانست.');
INSERT INTO "badkhashani" VALUES(78,6,'78:6','(برپا کردن قیامت به ما چه مشکل است؟) آیا ما زمین را بستر نگردانیده‌ایم؟');
INSERT INTO "badkhashani" VALUES(78,7,'78:7','و کوه‌ها را (همچون) میخ‌ها (نگردانیده‌ایم)؟');
INSERT INTO "badkhashani" VALUES(78,8,'78:8','و شما را جفت (نر و ماده) آفریدیم.');
INSERT INTO "badkhashani" VALUES(78,9,'78:9','و خواب‌تان را سبب آرامش قرار دادیم.');
INSERT INTO "badkhashani" VALUES(78,10,'78:10','و شب را (مانند لباس) پوششی گردانیدیم.');
INSERT INTO "badkhashani" VALUES(78,11,'78:11','و روز را وقتِ تلاش و معاش گردانیدیم.');
INSERT INTO "badkhashani" VALUES(78,12,'78:12','و بالای سر شما هفت آسمانِ محکم بنا نمودیم.');
INSERT INTO "badkhashani" VALUES(78,13,'78:13','و (آفتاب را) چراغ فروزان و درخشنده ساختیم.');
INSERT INTO "badkhashani" VALUES(78,14,'78:14','و از ابرهای متراکم و فشرده آبِ فراوان نازل کردیم.');
INSERT INTO "badkhashani" VALUES(78,15,'78:15','تا به وسیله آن دانه و گیاه را بیرون آریم.');
INSERT INTO "badkhashani" VALUES(78,16,'78:16','و باغ‌های درهم‌پیچیده و انبوه را نیز (آفریدیم).');
INSERT INTO "badkhashani" VALUES(78,17,'78:17','یقیناً روز فیصله، وقت مقرر و معین است.');
INSERT INTO "badkhashani" VALUES(78,18,'78:18','روزی که در صور دمیده شود، پس (به دربار پروردگارتان) گروه گروه بیایید.');
INSERT INTO "badkhashani" VALUES(78,19,'78:19','و آسمان گشوده شود و به صورت دروازه‌هایی باز درآید.');
INSERT INTO "badkhashani" VALUES(78,20,'78:20','و کوه‌ها روان شوند و مانند سراب گردند.');
INSERT INTO "badkhashani" VALUES(78,21,'78:21','البته دوزخ در انتظار (مردم سرکش) است.');
INSERT INTO "badkhashani" VALUES(78,22,'78:22','و برای سرکشان جای بازگشت است.');
INSERT INTO "badkhashani" VALUES(78,23,'78:23','در آن جا مدت طولانی می‌مانند.');
INSERT INTO "badkhashani" VALUES(78,24,'78:24','در آن جا نه سردی می‌چشند و نه نوشیدنی گوارائی می‌نوشند.');
INSERT INTO "badkhashani" VALUES(78,25,'78:25','مگر آب جوش و زرداب زخم دوزخیان را.');
INSERT INTO "badkhashani" VALUES(78,26,'78:26','این جزایی است مناسب و موافق (حال و وضعیت آن‌ها).');
INSERT INTO "badkhashani" VALUES(78,27,'78:27','(چون) آن‌ها (در دنیا) حساب روز قیامت را امید نداشتند.');
INSERT INTO "badkhashani" VALUES(78,28,'78:28','و آیات ما را به شدت تکذیب کردند.');
INSERT INTO "badkhashani" VALUES(78,29,'78:29','ولی ما هرچیز را با نوشتن حفظ نموده‌ایم.');
INSERT INTO "badkhashani" VALUES(78,30,'78:30','پس (برای‌شان گفته می‌شود که) بچشید عذاب را، چون هرگز به شما جز عذاب نخواهیم افزود.');
INSERT INTO "badkhashani" VALUES(78,31,'78:31','البته برای پرهیزگاران کامیابی بزرگ است.');
INSERT INTO "badkhashani" VALUES(78,32,'78:32','و باغ‌ها، و درختانِ انگور.');
INSERT INTO "badkhashani" VALUES(78,33,'78:33','و دختران نوجوان و هم‌سال.');
INSERT INTO "badkhashani" VALUES(78,34,'78:34','و پیاله‌ای پر از شراب.');
INSERT INTO "badkhashani" VALUES(78,35,'78:35','در آن جا سخن بیهوده و دروغ نمی‌شنوند.');
INSERT INTO "badkhashani" VALUES(78,36,'78:36','این پاداش از سوی پروردگار تو (است) پاداش و بخششی از روی حساب.');
INSERT INTO "badkhashani" VALUES(78,37,'78:37','پروردگار آسمان‌ها و زمین و هرچه در بین آن دو قرار دارد که او بسیار مهربان است، (با این وصف از عظمت او) نمی‌توانند با او سخنی بگویند.');
INSERT INTO "badkhashani" VALUES(78,38,'78:38','روزی که جبرئیل و دیگر فرشته‌ها (به دربار الله) صف‌بسته می‌ایستند، سخن نمی‌گویند، مگر کسی که پروردگار مهربان به او اجازه دهد و او نیز سخن حق و درست بگوید.');
INSERT INTO "badkhashani" VALUES(78,39,'78:39','آن روز، روز حق (و راست) است، پس هرکه بخواهد نجات یابد (عمل درست انجام دهد) که در روز قیامت به‌سوی او بازمی‌گردد.');
INSERT INTO "badkhashani" VALUES(78,40,'78:40','البته ما شما را از عذاب نزدیک ترسانیدیم، روزی که انسان آنچه را با دستان خویش پیش فرستاده است می‌بیند و کافر می‌گوید: ای کاش من خاک می‌بودم!.');
INSERT INTO "badkhashani" VALUES(79,1,'79:1','قسم به فرشته‌های که ارواح کافران را به سختی می‌گیرند.');
INSERT INTO "badkhashani" VALUES(79,2,'79:2','و قسم به فرشته‌های که ارواح مؤمنان را به آسانی می‌گیرند.');
INSERT INTO "badkhashani" VALUES(79,3,'79:3','و قسم به فرشتگانی که (در اجرای اوامر الهی) به سرعت شناورند.');
INSERT INTO "badkhashani" VALUES(79,4,'79:4','پس قسم به فرشتگانی که (در اجرای اوامر الهی) از یکدیگر سبقت می‌گیرند.');
INSERT INTO "badkhashani" VALUES(79,5,'79:5','باز قسم به فرشتگانی که همۀ کارها را تدبیر می‌کنند.');
INSERT INTO "badkhashani" VALUES(79,6,'79:6','روزی که زلزلۀ سخت همه چیز را به لرزه درمی‌آورد.');
INSERT INTO "badkhashani" VALUES(79,7,'79:7','به تعقیب آن لرزۀ دیگر واقع شود.');
INSERT INTO "badkhashani" VALUES(79,8,'79:8','برخی از دل‌ها (در آن روز) ترسان و لرزان است.');
INSERT INTO "badkhashani" VALUES(79,9,'79:9','(و) چشم‌های ایشان پایین افتاده است.');
INSERT INTO "badkhashani" VALUES(79,10,'79:10','می‌گویند: آیا ما دوباره بازگردانیده می‌شویم (به حالت نخستین؟)');
INSERT INTO "badkhashani" VALUES(79,11,'79:11','آیا (باز زنده می‌شویم که) استخوان‌های پوسیده شویم؟');
INSERT INTO "badkhashani" VALUES(79,12,'79:12','گویند (اگر چنین باشد) آن زندگی زیانبار خواهد بود.');
INSERT INTO "badkhashani" VALUES(79,13,'79:13','(الله در جواب آن‌ها فرمود): جز این نیست که آن تنها آواز بلند است.');
INSERT INTO "badkhashani" VALUES(79,14,'79:14','پس ناگهان در میدان محشر (که جای بی‌خوابی است) حاضر شوند.');
INSERT INTO "badkhashani" VALUES(79,15,'79:15','آیا داستانِ (دعوت) موسی به تو رسیده است؟');
INSERT INTO "badkhashani" VALUES(79,16,'79:16','وقتی که پروردگارش او را در وادی پاک طوی صدا کرد.');
INSERT INTO "badkhashani" VALUES(79,17,'79:17','(به او گفت): به‌سوی فرعون برو که سرکشی و طغیان کرده است.');
INSERT INTO "badkhashani" VALUES(79,18,'79:18','پس به او بگو: آیا می‌خواهی که (از کفر) پاک شوی؟');
INSERT INTO "badkhashani" VALUES(79,19,'79:19','و تو را به‌سوی پروردگارت راهنمائی کنم تا (بعد از شناختن پروردگارت) بترسی (و ایمان آوری و سرکشی نکنی).');
INSERT INTO "badkhashani" VALUES(79,20,'79:20','پس (موسی) معجزۀ بزرگ را به او نشان داد.');
INSERT INTO "badkhashani" VALUES(79,21,'79:21','اما او دروغ شمرد و نافرمانی کرد.');
INSERT INTO "badkhashani" VALUES(79,22,'79:22','باز پشت کرد (از مجلس موسی و برای فساد) سعی می‌نمود.');
INSERT INTO "badkhashani" VALUES(79,23,'79:23','پس (ساحران را) جمع کرد و اعلان نمود.');
INSERT INTO "badkhashani" VALUES(79,24,'79:24','پس گفت: من پروردگار بزرگتر شما هستم.');
INSERT INTO "badkhashani" VALUES(79,25,'79:25','پس الله او را به عذاب آخرت و دنیا گرفتار کرد.');
INSERT INTO "badkhashani" VALUES(79,26,'79:26','یقیناً در این (ماجرا) برای کسی که (از عذاب الله) بترسد، عبرت است.');
INSERT INTO "badkhashani" VALUES(79,27,'79:27','آیا (دوباره) آفریدن شما سخت‌تر است یا آفریدن آسمانی که او (الله) آن را بنا کرده؟');
INSERT INTO "badkhashani" VALUES(79,28,'79:28','و بلندی آن را بر افراشت، پس آن را استوار و برابر کرد.');
INSERT INTO "badkhashani" VALUES(79,29,'79:29','و شبش را تاریک و روزش را روشن گردانید.');
INSERT INTO "badkhashani" VALUES(79,30,'79:30','و بعد از آن زمین را هموار نمود.');
INSERT INTO "badkhashani" VALUES(79,31,'79:31','از آن (زمین) آب و چراگاهش را بیرون کرد.');
INSERT INTO "badkhashani" VALUES(79,32,'79:32','و کوه‌ها را (بر زمین) محکم و پابرجا کرد.');
INSERT INTO "badkhashani" VALUES(79,33,'79:33','(این همه را پیدا کرد) برای منفعت شما و چهارپایان‌تان.');
INSERT INTO "badkhashani" VALUES(79,34,'79:34','پس وقتی که بزرگ‌ترین حادثۀ (قیامت) بیاید.');
INSERT INTO "badkhashani" VALUES(79,35,'79:35','روزی که انسان آن کوشش خود را (در دنیا) به یاد می‌آورد.');
INSERT INTO "badkhashani" VALUES(79,36,'79:36','و دوزخ برای هر بیننده‌ای آشکار و نمایان شود.');
INSERT INTO "badkhashani" VALUES(79,37,'79:37','اما هر کسی طغیان و سرکشی کرد');
INSERT INTO "badkhashani" VALUES(79,38,'79:38','و زندگانی دنیا را (بر آخرت) ترجیح داد.');
INSERT INTO "badkhashani" VALUES(79,39,'79:39','پس یقیناً دوزخ جایگاه اوست.');
INSERT INTO "badkhashani" VALUES(79,40,'79:40','و اما کسی که از ایستادن در حضور پروردگارش ترسید و نفسش را از هوی و هوس باز داشت.');
INSERT INTO "badkhashani" VALUES(79,41,'79:41','پس یقیناً جنت جایگاه اوست.');
INSERT INTO "badkhashani" VALUES(79,42,'79:42','دربارۀ قیامت از تو می‌پرسند که چه وقتی برپا می‌شود؟');
INSERT INTO "badkhashani" VALUES(79,43,'79:43','تو را چه خبر از (وقت برپا شدن) آن.');
INSERT INTO "badkhashani" VALUES(79,44,'79:44','پایان و سرانجام آن با پروردگار توست.');
INSERT INTO "badkhashani" VALUES(79,45,'79:45','تو تنها بیم‌دهندۀ کسی هستی که از آن (قیامت) می‌ترسد.');
INSERT INTO "badkhashani" VALUES(79,46,'79:46','روزی که آن را می‌بینند، گویا که آنان جز (به قدر) شب یا چاشتی (در دنیا و برزخ) درنگ نکرده‌اند.');
INSERT INTO "badkhashani" VALUES(80,1,'80:1','روی ترش کرد و اعراض نمود.');
INSERT INTO "badkhashani" VALUES(80,2,'80:2','به این که نزد او نابینا آمد.');
INSERT INTO "badkhashani" VALUES(80,3,'80:3','و تو چه دانی که او شاید در پی پاکی باشد.');
INSERT INTO "badkhashani" VALUES(80,4,'80:4','یا پند گیرد، پس آن پند به او سودی رساند.');
INSERT INTO "badkhashani" VALUES(80,5,'80:5','اما کسی که خود را بی‌نیاز می‌داند.');
INSERT INTO "badkhashani" VALUES(80,6,'80:6','پس تو در پی او شدی؟');
INSERT INTO "badkhashani" VALUES(80,7,'80:7','و اگر او (شخص بی‌نیاز) پاک هم نشود، گناهی بر تو نیست.');
INSERT INTO "badkhashani" VALUES(80,8,'80:8','و اما کسی که شتابان نزد او آمد.');
INSERT INTO "badkhashani" VALUES(80,9,'80:9','در حالی که (از الله) می‌ترسید.');
INSERT INTO "badkhashani" VALUES(80,10,'80:10','پس تو (به دیگران مشغول شده) از وی روی‌گردان می‌شوی.');
INSERT INTO "badkhashani" VALUES(80,11,'80:11','چنین نیست (که آن‌ها می‌پندارند) یقیناً قرآن وسیله‌ای پند است.');
INSERT INTO "badkhashani" VALUES(80,12,'80:12','پس هرکس (پند) بخواهد باید آن را یاد کند.');
INSERT INTO "badkhashani" VALUES(80,13,'80:13','(همانا آیات قرآن) در صحیفه‌های قدرمند درج و محفوظ است.');
INSERT INTO "badkhashani" VALUES(80,14,'80:14','که بلندمرتبه و پاکیزه است.');
INSERT INTO "badkhashani" VALUES(80,15,'80:15','به دستِ نویسندگان.');
INSERT INTO "badkhashani" VALUES(80,16,'80:16','بزرگوار و نیک‌کردار.');
INSERT INTO "badkhashani" VALUES(80,17,'80:17','مرگ بر این انسان (کافر و سرکش)، چه ناشکر است!');
INSERT INTO "badkhashani" VALUES(80,18,'80:18','(دقت نکرده که) الله او را از چه چیز آفریده است؟');
INSERT INTO "badkhashani" VALUES(80,19,'80:19','از نطفه (قطرۀ منی در رحم مادر) او را آفرید، پس (اندازۀ ماندن او در رحم مادر را) مقرر کرد.');
INSERT INTO "badkhashani" VALUES(80,20,'80:20','باز راه را برای او آسان گردانید.');
INSERT INTO "badkhashani" VALUES(80,21,'80:21','باز (بعد از پایان‌یافتن عمر) او را می‌میراند و در قبر می‌نهد.');
INSERT INTO "badkhashani" VALUES(80,22,'80:22','باز هر وقتی که بخواهد او را زنده می‌کند.');
INSERT INTO "badkhashani" VALUES(80,23,'80:23','نه! آنچه را که به او فرمان داده بود انجام نداد.');
INSERT INTO "badkhashani" VALUES(80,24,'80:24','پس باید انسان به غذای خود بنگرد.');
INSERT INTO "badkhashani" VALUES(80,25,'80:25','(که) ما آب را به فراوانی فرو ریختیم.');
INSERT INTO "badkhashani" VALUES(80,26,'80:26','باز زمین را به خوبی شگافتیم.');
INSERT INTO "badkhashani" VALUES(80,27,'80:27','پس در زمین دانه‌ها را رویانیدیم.');
INSERT INTO "badkhashani" VALUES(80,28,'80:28','و (درخت‌های) انگور و سبزیجات را.');
INSERT INTO "badkhashani" VALUES(80,29,'80:29','و (درخت‌های) زیتون و خرما را.');
INSERT INTO "badkhashani" VALUES(80,30,'80:30','و باغ‌های پر درخت را.');
INSERT INTO "badkhashani" VALUES(80,31,'80:31','و میوه‌ها و علف‌زارها (چراگاه‌ها) را.');
INSERT INTO "badkhashani" VALUES(80,32,'80:32','(این همه را آفریدیم) برای نفع شما و حیواناتِ شما.');
INSERT INTO "badkhashani" VALUES(80,33,'80:33','پس چون آواز سخت و هولناک (قیامت) بیاید.');
INSERT INTO "badkhashani" VALUES(80,34,'80:34','روزی که انسان از برادر خود می‌گریزد.');
INSERT INTO "badkhashani" VALUES(80,35,'80:35','و از مادر و پدر خود.');
INSERT INTO "badkhashani" VALUES(80,36,'80:36','و از زن و فرزند خود.');
INSERT INTO "badkhashani" VALUES(80,37,'80:37','(چون) هرکس از آنان در آن روز حالتی دارد که برایش کفایت می‌کند.');
INSERT INTO "badkhashani" VALUES(80,38,'80:38','در آن روز چهره‌هایی شاد و درخشانند.');
INSERT INTO "badkhashani" VALUES(80,39,'80:39','و خوش و خندانند.');
INSERT INTO "badkhashani" VALUES(80,40,'80:40','و در آن روز چهره‌هایی است که بر آن‌ها غبار نشسته است.');
INSERT INTO "badkhashani" VALUES(80,41,'80:41','چهرۀ آن‌ها را سیاهی و تاریکی پوشانده است.');
INSERT INTO "badkhashani" VALUES(80,42,'80:42','این‌ها همان گروه کافرِ بدکار‌اند.');
INSERT INTO "badkhashani" VALUES(81,1,'81:1','وقتی که آفتاب پیچانیده شود (بی‌نور گردد).');
INSERT INTO "badkhashani" VALUES(81,2,'81:2','و وقتی که ستاره‌ها بی‌نور و تاریک شوند.');
INSERT INTO "badkhashani" VALUES(81,3,'81:3','و وقتی که کوه‌ها را روان سازند.');
INSERT INTO "badkhashani" VALUES(81,4,'81:4','و وقتی که ماده شترانِ باردار معطل گذاشته شوند.');
INSERT INTO "badkhashani" VALUES(81,5,'81:5','و وقتی که حیوانات وحشی حشر شوند.');
INSERT INTO "badkhashani" VALUES(81,6,'81:6','و وقتی که بحرها به جوش آیند.');
INSERT INTO "badkhashani" VALUES(81,7,'81:7','و وقتی که اجساد را با ارواح یکجا کنند.');
INSERT INTO "badkhashani" VALUES(81,8,'81:8','و وقتی که از دختر زنده به گورشده پرسیده شود.');
INSERT INTO "badkhashani" VALUES(81,9,'81:9','(که) به کدام گناه کشته شده است؟');
INSERT INTO "badkhashani" VALUES(81,10,'81:10','و وقتی که نامه‌های اعمال گشوده شود.');
INSERT INTO "badkhashani" VALUES(81,11,'81:11','و وقتی که آسمان از جای کنده شود.');
INSERT INTO "badkhashani" VALUES(81,12,'81:12','و وقتی که دوزخ شعله‌ور شود.');
INSERT INTO "badkhashani" VALUES(81,13,'81:13','و وقتی که جنت (برای اهلش) نزدیک آورده شود.');
INSERT INTO "badkhashani" VALUES(81,14,'81:14','هرکس آنچه (از نیکی و بدی) را که آماده (و حاضر) کرده است بداند.');
INSERT INTO "badkhashani" VALUES(81,15,'81:15','پس قسم به ستاره‌های که بازمی‌گردند.');
INSERT INTO "badkhashani" VALUES(81,16,'81:16','(ستارگان) حرکت کننده (باز در افق) نهان شونده.');
INSERT INTO "badkhashani" VALUES(81,17,'81:17','و قسم به شب چون برود.');
INSERT INTO "badkhashani" VALUES(81,18,'81:18','و قسم به صبح چون بدمد.');
INSERT INTO "badkhashani" VALUES(81,19,'81:19','یقیناً (قرآن) سخن رسول گرامی است.');
INSERT INTO "badkhashani" VALUES(81,20,'81:20','(که) او نیرومند است و در بارگاه صاحب عرش (الله) دارای منزلت والایی است.');
INSERT INTO "badkhashani" VALUES(81,21,'81:21','از او فرمان می‌برند، در آن جا (او) امین است.');
INSERT INTO "badkhashani" VALUES(81,22,'81:22','و هم صحبت شما (محمد) دیوانه نیست.');
INSERT INTO "badkhashani" VALUES(81,23,'81:23','و البته (او) آن (فرشته) را در کنارۀ آشکار آسمان دید.');
INSERT INTO "badkhashani" VALUES(81,24,'81:24','و او بر رسانیدن سخن‌های غیب بخیل نیست.');
INSERT INTO "badkhashani" VALUES(81,25,'81:25','و قرآن سخن شیطانِ رانده‌شده نیست.');
INSERT INTO "badkhashani" VALUES(81,26,'81:26','پس (قرآن را گذاشته) به کجا می‌روید؟');
INSERT INTO "badkhashani" VALUES(81,27,'81:27','آن قرآن جز پند و اندرز برای جهانیان نیست.');
INSERT INTO "badkhashani" VALUES(81,28,'81:28','برای کسی از شما که بخواهد (کردارش) به راه راست، مستقیم شود.');
INSERT INTO "badkhashani" VALUES(81,29,'81:29','و شما چیزی را نمی‌خواهید مگر آن که الله، پروردگار جهانیان آن را بخواهد.');
INSERT INTO "badkhashani" VALUES(82,1,'82:1','وقتی که آسمان شگافته شود.');
INSERT INTO "badkhashani" VALUES(82,2,'82:2','و وقتی که ستاره‌ها پراگنده (تاریک) شوند.');
INSERT INTO "badkhashani" VALUES(82,3,'82:3','و وقتی که دریاها جاری شوند.');
INSERT INTO "badkhashani" VALUES(82,4,'82:4','و وقتی که قبرها سر و زیر شوند (و مرده‌ها زنده شوند).');
INSERT INTO "badkhashani" VALUES(82,5,'82:5','هر شخص آنچه را پیش فرستاده و آنچه را که پس گذاشته است بداند.');
INSERT INTO "badkhashani" VALUES(82,6,'82:6','ای انسان! چه چیزی تو را در برابر پروردگار بزرگوارت مغرور ساخته (و فریب داده) است؟');
INSERT INTO "badkhashani" VALUES(82,7,'82:7','آن که تو را آفرید، پس (اندام) تو را برابر و معتدل کرد.');
INSERT INTO "badkhashani" VALUES(82,8,'82:8','به هر شکلی (و صورتی) که خواست تو را ترتیب کرد.');
INSERT INTO "badkhashani" VALUES(82,9,'82:9','چنین نیست (که می‌گویید) بلکه شما سزا را دروغ می‌شمارید.');
INSERT INTO "badkhashani" VALUES(82,10,'82:10','در حالی که بر شما نگهبانانی مقرر شده است.');
INSERT INTO "badkhashani" VALUES(82,11,'82:11','نویسندگان بزرگوار (اند).');
INSERT INTO "badkhashani" VALUES(82,12,'82:12','هرچه را می‌کنید، می‌دانند.');
INSERT INTO "badkhashani" VALUES(82,13,'82:13','البته نیکوکاران در نعمت‌ها و (جنت)‌اند.');
INSERT INTO "badkhashani" VALUES(82,14,'82:14','و البته بدکاران در دوزخ‌اند.');
INSERT INTO "badkhashani" VALUES(82,15,'82:15','که در روز جزا به آن داخل خواهند شد.');
INSERT INTO "badkhashani" VALUES(82,16,'82:16','و آنان هیچگاه از آن دور (بیرون) نمی‌شوند.');
INSERT INTO "badkhashani" VALUES(82,17,'82:17','تو چه می‌دانی که روز جزا چیست؟');
INSERT INTO "badkhashani" VALUES(82,18,'82:18','باز (گفته می‌شود که) تو چه می‌دانی که روز جزا چیست؟');
INSERT INTO "badkhashani" VALUES(82,19,'82:19','روزی که هیچ کسی برای دیگری نمی‌تواند کاری بکند و در آن روز فرمان، فرمانِ الله است (و بس).');
INSERT INTO "badkhashani" VALUES(83,1,'83:1','وای بر کم‌کنندگان (حقوق مردم)');
INSERT INTO "badkhashani" VALUES(83,2,'83:2','کسانی که وقتی از مردم پیمانه بگیرند به تمام و کمال می‌گیرند.');
INSERT INTO "badkhashani" VALUES(83,3,'83:3','و چون به ایشان پیمانه دهند یا وزن کنند، کم می‌کنند.');
INSERT INTO "badkhashani" VALUES(83,4,'83:4','آیا به این گمان نیستند (و نمی‌دانند) که دوباره زنده می‌شوند.');
INSERT INTO "badkhashani" VALUES(83,5,'83:5','برای روزی بس بزرگ؟');
INSERT INTO "badkhashani" VALUES(83,6,'83:6','روزی که مردم در پیشگاه پروردگار جهانیان (برای جواب دادن) می‌ایستند.');
INSERT INTO "badkhashani" VALUES(83,7,'83:7','چنین نیست (که آنان گمان دارند) البته عمل‌نامۀ بدکاران در سجّین (درج) است.');
INSERT INTO "badkhashani" VALUES(83,8,'83:8','و تو چه می‌دانی که سجّین چیست؟');
INSERT INTO "badkhashani" VALUES(83,9,'83:9','کتاب نوشته شده است.');
INSERT INTO "badkhashani" VALUES(83,10,'83:10','وای بر تکذیب‌کنندگان (منکران) در آن روز.');
INSERT INTO "badkhashani" VALUES(83,11,'83:11','آنانی که روز جزا را انکار می‌کنند.');
INSERT INTO "badkhashani" VALUES(83,12,'83:12','و تکذیب (و انکار) نمی‌کند روز جزا را مگر هر تجاوزگر گنهگار.');
INSERT INTO "badkhashani" VALUES(83,13,'83:13','چون آیات ما بر او خوانده شود می‌گوید: این افسانه‌های پیشینیان است.');
INSERT INTO "badkhashani" VALUES(83,14,'83:14','چنین نیست، بلکه آنچه می‌کردند (از کفر و معاصی) بر دل‌هایشان زنگ نهاده است.');
INSERT INTO "badkhashani" VALUES(83,15,'83:15','چنین نیست؛ بی‌گمان آنان آن روز از (دیدار) پروردگارشان در حجاب‌اند.');
INSERT INTO "badkhashani" VALUES(83,16,'83:16','باز بی‌گمان آن‌ها در دوزخ داخل خواهند شد.');
INSERT INTO "badkhashani" VALUES(83,17,'83:17','باز (برای‌شان) گفته خواهد شد که این همان عذابی است که آن را دروغ می‌دانستید.');
INSERT INTO "badkhashani" VALUES(83,18,'83:18','چنین نیست، حقا که عمل‌نامۀ نیکان در علیین (درج) است.');
INSERT INTO "badkhashani" VALUES(83,19,'83:19','و تو چه می‌دانی که علیین چیست؟');
INSERT INTO "badkhashani" VALUES(83,20,'83:20','کتاب نوشته شده است.');
INSERT INTO "badkhashani" VALUES(83,21,'83:21','مقربان (دربار الهی) در آن جا حضور می‌یابند.');
INSERT INTO "badkhashani" VALUES(83,22,'83:22','بی‌گمان نیکان در نعمت‌ها‌اند.');
INSERT INTO "badkhashani" VALUES(83,23,'83:23','بر تخت‌ها نشسته (تکیه زده و) می‌نگرند.');
INSERT INTO "badkhashani" VALUES(83,24,'83:24','(تو ای مخاطب) خوشی و خرمیِ نعمت را در چهره‌هایشان می‌بینی.');
INSERT INTO "badkhashani" VALUES(83,25,'83:25','از شراب خالص و مهر شده، نوشانیده می‌شوند.');
INSERT INTO "badkhashani" VALUES(83,26,'83:26','مهر آن مشک است. و رغبت‌کنندگان باید در (حصول) این نعمت رغبت کنند.');
INSERT INTO "badkhashani" VALUES(83,27,'83:27','و مخلوط آن از تسنیم است.');
INSERT INTO "badkhashani" VALUES(83,28,'83:28','(تسنیم) چشمه‌ای است که مقربان از آن می‌نوشند.');
INSERT INTO "badkhashani" VALUES(83,29,'83:29','بی‌گمان گناهکاران (در دنیا) به مؤمنان می‌خندیدند.');
INSERT INTO "badkhashani" VALUES(83,30,'83:30','و هرگاه مسلمانان از کنار آن‌ها می‌گذشتند (به طور استهزا با چشم و ابرو) به یکدیگر اشاره می‌کردند.');
INSERT INTO "badkhashani" VALUES(83,31,'83:31','و هنگامی که گناهکاران به خانوادۀ خود بازمی‌گشتند، شادمان برمی‌گشتند.');
INSERT INTO "badkhashani" VALUES(83,32,'83:32','و چون مؤمنان را می‌دیدند می‌گفتند: یقیناً این گروه گمراه‌اند.');
INSERT INTO "badkhashani" VALUES(83,33,'83:33','در حالی که بر آنان (مؤمنان) نگهبانان فرستاده نشده‌اند.');
INSERT INTO "badkhashani" VALUES(83,34,'83:34','پس امروز (روز قیامت) مؤمنان بر کافران می‌خندند.');
INSERT INTO "badkhashani" VALUES(83,35,'83:35','بر تخت‌ها نشسته (تکیه زده و) می‌نگرند.');
INSERT INTO "badkhashani" VALUES(83,36,'83:36','و (می‌پرسند) آیا به کافران سزای کارهایی که می‌کردند داده شده است؟');
INSERT INTO "badkhashani" VALUES(84,1,'84:1','وقتی که آسمان بشگافد (و پاره پاره شود)');
INSERT INTO "badkhashani" VALUES(84,2,'84:2','و حکم پروردگار خود را فرمان برد که چنین سزاوار است.');
INSERT INTO "badkhashani" VALUES(84,3,'84:3','و وقتی که زمین کشیده و هموار گردد.');
INSERT INTO "badkhashani" VALUES(84,4,'84:4','و آنچه در آن است بیرون اندازد و تهی گردد.');
INSERT INTO "badkhashani" VALUES(84,5,'84:5','و حکم پروردگار خود را فرمان برد که چنین سزاوار است.');
INSERT INTO "badkhashani" VALUES(84,6,'84:6','ای انسان! یقیناً تو با تلاش و رنج فراوان به‌سوی پروردگارت رهسپاری و سرانجام او را ملاقات خواهی کرد.');
INSERT INTO "badkhashani" VALUES(84,7,'84:7','(پس از ملاقات) کسی که نامۀ اعمالش به دست راستش داده شود.');
INSERT INTO "badkhashani" VALUES(84,8,'84:8','پس به آسانی با او حساب خواهد شد.');
INSERT INTO "badkhashani" VALUES(84,9,'84:9','و شادمان به‌سوی خانوادۀ خود بازمی‌گردد.');
INSERT INTO "badkhashani" VALUES(84,10,'84:10','اما کسی که نامۀ اعمالش از پشت سر (به دست چپ) او داده شود.');
INSERT INTO "badkhashani" VALUES(84,11,'84:11','پس با داد و فریاد هلاکت را می‌طلبد.');
INSERT INTO "badkhashani" VALUES(84,12,'84:12','و وارد دوزخ خواهد شد.');
INSERT INTO "badkhashani" VALUES(84,13,'84:13','چون او (در دنیا) در میان خانوادۀ خود شادمان (و غافل از آخرت) بود.');
INSERT INTO "badkhashani" VALUES(84,14,'84:14','و چون او گمان می‌کرد که هرگز (به سوی الله) باز نمی‌گردد.');
INSERT INTO "badkhashani" VALUES(84,15,'84:15','آری! (بازگشت به‌سوی الله حق است) به درستی که پروردگارش به او بیناست.');
INSERT INTO "badkhashani" VALUES(84,16,'84:16','پس قسم به شفق (سرخی بعد از غروب آفتاب).');
INSERT INTO "badkhashani" VALUES(84,17,'84:17','و قسم به شب و آنچه (در زیر سیاهی خود) جمع می‌کند.');
INSERT INTO "badkhashani" VALUES(84,18,'84:18','و قسم به ماه چون کامل گردد.');
INSERT INTO "badkhashani" VALUES(84,19,'84:19','که همیشه از یک حالت به حالت دیگر درآیید.');
INSERT INTO "badkhashani" VALUES(84,20,'84:20','پس کفار را چه شده که ایمان نمی‌آرند؟');
INSERT INTO "badkhashani" VALUES(84,21,'84:21','و هرگاه قرآن بر آن‌ها خوانده شود، سجده نمی‌کنند (منقاد نمی‌شوند).');
INSERT INTO "badkhashani" VALUES(84,22,'84:22','(سجده کردن چه که) بلکه کفار قرآن را تکذیب می‌کنند.');
INSERT INTO "badkhashani" VALUES(84,23,'84:23','و الله به آنچه در دل دارند عالم‌تر است.');
INSERT INTO "badkhashani" VALUES(84,24,'84:24','پس آن‌ها را به عذاب دردناک مژده ده.');
INSERT INTO "badkhashani" VALUES(84,25,'84:25','مگر آنانی که ایمان آورده‌اند و کارهای نیک انجام داده‌اند که برای آن‌ها اجر و ثوابی ابدی و قطع ناشدنی است.');
INSERT INTO "badkhashani" VALUES(85,1,'85:1','قسم به آسمانی که دارای برج‌هاست.');
INSERT INTO "badkhashani" VALUES(85,2,'85:2','و قسم به روز موعود (روز قیامت).');
INSERT INTO "badkhashani" VALUES(85,3,'85:3','و قسم به شاهد (روز جمعه)، و قسم به مشهود (روز عرفه).');
INSERT INTO "badkhashani" VALUES(85,4,'85:4','اهل خندق‌ها کشته شدند.');
INSERT INTO "badkhashani" VALUES(85,5,'85:5','خندق‌های پر از آتش و دارای هیزم فراوان.');
INSERT INTO "badkhashani" VALUES(85,6,'85:6','وقتی که بر آن (خندق‌ها) نشسته بودند.');
INSERT INTO "badkhashani" VALUES(85,7,'85:7','و آن‌ها بر آنچه با مؤمنان می‌کردند حاضر بودند.');
INSERT INTO "badkhashani" VALUES(85,8,'85:8','و آن‌ها از مؤمنان انتقام نگرفتند مگر این که به الله غالب و ستوده ایمان آورده بودند.');
INSERT INTO "badkhashani" VALUES(85,9,'85:9','آن ذاتی که فرمانروایی آسمان‌ها و زمین خاص از اوست و الله بر همه چیز گواه و آگاه است.');
INSERT INTO "badkhashani" VALUES(85,10,'85:10','یقیناً آنانی که مردان و زنان مؤمن را در فتنه انداختند (عذاب دادند) باز توبه نکردند، پس برای آن‌ها عذاب دوزخ و همچنین عذاب سوزنده است.');
INSERT INTO "badkhashani" VALUES(85,11,'85:11','البته آنانی که ایمان آوردند و کارهای نیک کردند باغ‌هایی دارند که از زیر آن جوی‌ها روان است. این است پیروزی بزرگ.');
INSERT INTO "badkhashani" VALUES(85,12,'85:12','(لیکن به یاد داشته باش که) البته گرفتِ پروردگار تو بسیار سخت است.');
INSERT INTO "badkhashani" VALUES(85,13,'85:13','چون او (آفرینش) را آغاز می‌کند و (بار دوم نیز) او (زندگی هرچیز را) بازمی‌گرداند.');
INSERT INTO "badkhashani" VALUES(85,14,'85:14','و او آمرزنده و دوستدار مؤمنان است.');
INSERT INTO "badkhashani" VALUES(85,15,'85:15','و اوست صاحب عرش دارای مجد و عظمت.');
INSERT INTO "badkhashani" VALUES(85,16,'85:16','هرچه را بخواهد حتماً انجام می‌دهد.');
INSERT INTO "badkhashani" VALUES(85,17,'85:17','آیا قصۀ آن لشکرها (که هلاک شدند) به تو رسیده است؟');
INSERT INTO "badkhashani" VALUES(85,18,'85:18','(لشکریان) فرعون و ثمود.');
INSERT INTO "badkhashani" VALUES(85,19,'85:19','(اما کفار از قصه‌های قرآن عبرت نمی‌گیرند)، بلکه کافران همیشه در پی تکذیب قرآن‌اند.');
INSERT INTO "badkhashani" VALUES(85,20,'85:20','و لیکن الله از هر طرف بر آن‌ها احاطه دارد.');
INSERT INTO "badkhashani" VALUES(85,21,'85:21','(قرآن قابل تکذیب نیست)، بلکه قرآن کتابی بزرگوار و عالی‌قدر است.');
INSERT INTO "badkhashani" VALUES(85,22,'85:22','که در لوح المحفوظ (لوحۀ که از دسترسِ مخلوقات محفوظ است) ثبت است.');
INSERT INTO "badkhashani" VALUES(86,1,'86:1','قسم به آسمان، و قسم به طارق (یعنی ستاره ظاهرشونده در شب).');
INSERT INTO "badkhashani" VALUES(86,2,'86:2','تو چه می‌دانی که طارق چیست؟');
INSERT INTO "badkhashani" VALUES(86,3,'86:3','آن ستارۀ درخشنده است.');
INSERT INTO "badkhashani" VALUES(86,4,'86:4','کسی نیست مگر آن که بر او نگهبانی است.');
INSERT INTO "badkhashani" VALUES(86,5,'86:5','پس باید انسان بنگرد که از چه آفریده شده است؟');
INSERT INTO "badkhashani" VALUES(86,6,'86:6','از آب (منی) زودریزنده آفریده شده است.');
INSERT INTO "badkhashani" VALUES(86,7,'86:7','که از میان استخوان پشت (مرد) و استخوان سینه (زن) بیرون می‌شود.');
INSERT INTO "badkhashani" VALUES(86,8,'86:8','لذا الله بر بازگردانیدن (یعنی دوباره زنده کردنِ) انسان‌ها قادر است.');
INSERT INTO "badkhashani" VALUES(86,9,'86:9','روزی که رازهای پوشیده ظاهر شود.');
INSERT INTO "badkhashani" VALUES(86,10,'86:10','پس در آن روز (برای انسان) هیچ قوتی و هیچ یاری‌دهنده‌ای نیست.');
INSERT INTO "badkhashani" VALUES(86,11,'86:11','قسم به آسمان باران‌دار.');
INSERT INTO "badkhashani" VALUES(86,12,'86:12','و قسم به زمین شگافنده (برای رویانیدن نباتات).');
INSERT INTO "badkhashani" VALUES(86,13,'86:13','که قرآن کلام فیصله‌کننده (بین حق و باطل) است.');
INSERT INTO "badkhashani" VALUES(86,14,'86:14','و سخن بیهوده و شوخی نیست.');
INSERT INTO "badkhashani" VALUES(86,15,'86:15','البته آن‌ها (کفار) سخت نیرنگ می‌کنند.');
INSERT INTO "badkhashani" VALUES(86,16,'86:16','و من نیز (برای مجازات‌شان) چاره‌اندیشی می‌کنم.');
INSERT INTO "badkhashani" VALUES(86,17,'86:17','پس کافران را مهلت ده (تا فکر کنند و) زمانِ اندک آن‌ها را رها کن.');
INSERT INTO "badkhashani" VALUES(87,1,'87:1','نام پروردگار بلندمرتبه‌ات را به پاکی یاد کن.');
INSERT INTO "badkhashani" VALUES(87,2,'87:2','کسی که آفرید (هرچیز را)، پس (آن را) استوار و برابر کرد.');
INSERT INTO "badkhashani" VALUES(87,3,'87:3','و کسی که اندازه را (برای هر چیز) مقرر کرد باز هدایت نمود.');
INSERT INTO "badkhashani" VALUES(87,4,'87:4','و کسی که (از زمین‌های علف‌زار) چراگاه را رویانید.');
INSERT INTO "badkhashani" VALUES(87,5,'87:5','باز آن را خشک و سیاه ساخت.');
INSERT INTO "badkhashani" VALUES(87,6,'87:6','ما بر تو (قرآن را) می‌خوانیم، پس تو دیگر فراموش نخواهی کرد.');
INSERT INTO "badkhashani" VALUES(87,7,'87:7','مگر آنچه را که الله خواسته باشد (از دلت فراموش می‌کند)، چون الله آشکار و آنچه که پوشیده است می‌داند.');
INSERT INTO "badkhashani" VALUES(87,8,'87:8','و تو را برای شریعت آسان آماده می‌کنیم.');
INSERT INTO "badkhashani" VALUES(87,9,'87:9','پس پند ده (مردم را) اگر پند دادن مفید افتد.');
INSERT INTO "badkhashani" VALUES(87,10,'87:10','کسی که از الله می‌ترسد به زودی پند خواهد گرفت.');
INSERT INTO "badkhashani" VALUES(87,11,'87:11','و بدبخت‌ترین انسان‌ها از آن اجتناب می‌ورزد.');
INSERT INTO "badkhashani" VALUES(87,12,'87:12','آن کس که در آتش بزرگ داخل خواهد شد.');
INSERT INTO "badkhashani" VALUES(87,13,'87:13','باز در آن جا نه می‌میرد و نه زنده می‌ماند.');
INSERT INTO "badkhashani" VALUES(87,14,'87:14','البته کسی که خود را از (پلیدی شرک) پاک کرد، رستگار شد.');
INSERT INTO "badkhashani" VALUES(87,15,'87:15','و نام پروردگار خود را یاد کرد و باز نماز خواند.');
INSERT INTO "badkhashani" VALUES(87,16,'87:16','ولی شما زندگانی دنیا را ترجیح می‌دهید.');
INSERT INTO "badkhashani" VALUES(87,17,'87:17','حال آن که زندگی آخرت بهتر و پاینده‌تر است.');
INSERT INTO "badkhashani" VALUES(87,18,'87:18','البته این مضمون در صحیفه‌های سابق هم آمده است.');
INSERT INTO "badkhashani" VALUES(87,19,'87:19','صحیفه‌های ابراهیم و موسی.');
INSERT INTO "badkhashani" VALUES(88,1,'88:1','آیا خبر حادثۀ احاطه‌کننده (قیامت) به تو رسیده است؟');
INSERT INTO "badkhashani" VALUES(88,2,'88:2','که چهره‌هایی در آن روز خوار و ذلیل‌اند.');
INSERT INTO "badkhashani" VALUES(88,3,'88:3','(با آن که) مشقت‌کشیده و رنج برده‌اند.');
INSERT INTO "badkhashani" VALUES(88,4,'88:4','(لیکن) در آتش شعله‌ور و سوزان داخل شوند.');
INSERT INTO "badkhashani" VALUES(88,5,'88:5','از چشمۀ داغِ جوشان نوشانیده شوند.');
INSERT INTO "badkhashani" VALUES(88,6,'88:6','خوراکی جز ضریع (خار تلخ) ندارند.');
INSERT INTO "badkhashani" VALUES(88,7,'88:7','که نه فربه می‌کند و نه از گرسنگی نجات می‌دهد.');
INSERT INTO "badkhashani" VALUES(88,8,'88:8','چهره‌هایی در آن روز خرم و تازه خواهند بود.');
INSERT INTO "badkhashani" VALUES(88,9,'88:9','و از کوشش خود راضی‌اند.');
INSERT INTO "badkhashani" VALUES(88,10,'88:10','در بهشت برین (و بلند جای دارند).');
INSERT INTO "badkhashani" VALUES(88,11,'88:11','در آن جا سخن بیهوده نشنوند.');
INSERT INTO "badkhashani" VALUES(88,12,'88:12','در آن جا چشمۀ جاری است.');
INSERT INTO "badkhashani" VALUES(88,13,'88:13','و در آن جا تخت‌های بلند است.');
INSERT INTO "badkhashani" VALUES(88,14,'88:14','و جام‌های چیده شده است.');
INSERT INTO "badkhashani" VALUES(88,15,'88:15','و بالش‌های در صف نهاده شده است.');
INSERT INTO "badkhashani" VALUES(88,16,'88:16','و فرش‌های گرانبها گسترده شده است.');
INSERT INTO "badkhashani" VALUES(88,17,'88:17','آیا به شتران نمی‌نگرند (منکران توحید) که چگونه آفریده شده‌اند؟');
INSERT INTO "badkhashani" VALUES(88,18,'88:18','و به‌سوی آسمان نگاه نمی‌کنند که چگونه برافراشته شده است؟');
INSERT INTO "badkhashani" VALUES(88,19,'88:19','و به طرف کوه‌ها (نمی‌نگرند) که چگونه نصب (برقرار) گردیده‌اند؟');
INSERT INTO "badkhashani" VALUES(88,20,'88:20','و به طرف زمین که چگونه هموار شده است؟');
INSERT INTO "badkhashani" VALUES(88,21,'88:21','پس پند و اندرز ده که وظیفۀ تو تنها پند دادن و یادآوری است.');
INSERT INTO "badkhashani" VALUES(88,22,'88:22','بر آن‌ها مسلط نیستی.');
INSERT INTO "badkhashani" VALUES(88,23,'88:23','مگر آن که روی گرداند و کفر پیشه کند.');
INSERT INTO "badkhashani" VALUES(88,24,'88:24','پس الله او را به عذاب سخت سزا می‌دهد.');
INSERT INTO "badkhashani" VALUES(88,25,'88:25','یقیناً بازگشت آن‌ها به‌سوی ماست.');
INSERT INTO "badkhashani" VALUES(88,26,'88:26','باز حساب آن‌ها با ما خواهد بود.');
INSERT INTO "badkhashani" VALUES(89,1,'89:1','قسم به فجر (صبح).');
INSERT INTO "badkhashani" VALUES(89,2,'89:2','و قسم به شب‌های ده‌گانه.');
INSERT INTO "badkhashani" VALUES(89,3,'89:3','و قسم به جفت و طاق.');
INSERT INTO "badkhashani" VALUES(89,4,'89:4','و قسم به شب چون سپری شود.');
INSERT INTO "badkhashani" VALUES(89,5,'89:5','آیا درین (امر) قسم کافی برای صاحبان خرد نیست؟ (بلی هست!).');
INSERT INTO "badkhashani" VALUES(89,6,'89:6','آیا ندیدی که پروردگارت با قوم عاد چه کرد؟');
INSERT INTO "badkhashani" VALUES(89,7,'89:7','قوم ارم که صاحب قامت‌های بلند و ستون مانند بودند.');
INSERT INTO "badkhashani" VALUES(89,8,'89:8','که مانند آن در (هیچ‌یک) از شهرها آفریده نشده بود.');
INSERT INTO "badkhashani" VALUES(89,9,'89:9','و با قوم ثمود؟ آنانی که سنگ‌ها را در وادی (القری) می‌تراشیدند.');
INSERT INTO "badkhashani" VALUES(89,10,'89:10','با فرعون؟ که میخ‌ها (برای سزا دادنِ مردم) داشت.');
INSERT INTO "badkhashani" VALUES(89,11,'89:11','(این سه قوم) همان کسانی بودند که در شهرها سرکشی کردند.');
INSERT INTO "badkhashani" VALUES(89,12,'89:12','پس در آن شهرها فساد بیشتر کردند.');
INSERT INTO "badkhashani" VALUES(89,13,'89:13','لهذا پروردگارت آنان را به عذاب سخت گرفتار کرد.');
INSERT INTO "badkhashani" VALUES(89,14,'89:14','یقیناً پروردگار تو در کمین‌گاه (مراقب اعمال بندگان) است.');
INSERT INTO "badkhashani" VALUES(89,15,'89:15','پس اما انسان هرگاه پروردگارش او را امتحان کند و به او مقام و نعمت دهد، می‌گوید: پروردگارم مرا عزت داد.');
INSERT INTO "badkhashani" VALUES(89,16,'89:16','و اما وقتی که پروردگارش او را امتحان کند و روزی‌اش را بر او تنگ سازد، می‌گوید: پروردگارم مرا اهانت کرده است.');
INSERT INTO "badkhashani" VALUES(89,17,'89:17','نه، چنین نیست، بلکه شما یتیم را گرامی نمی‌دارید.');
INSERT INTO "badkhashani" VALUES(89,18,'89:18','و همدیگر را بر طعام دادن مسکین ترغیب نمی‌کنید.');
INSERT INTO "badkhashani" VALUES(89,19,'89:19','و میراث دیگران را با مال خود خلط نموده (به طریقۀ نادرست) می‌خورید.');
INSERT INTO "badkhashani" VALUES(89,20,'89:20','و مال را بسیار (بیش از حد) دوست می‌دارید.');
INSERT INTO "badkhashani" VALUES(89,21,'89:21','چنین نیست، بلکه وقتی زمین به شدت درهم کوبیده شود.');
INSERT INTO "badkhashani" VALUES(89,22,'89:22','و پروردگارت بیاید و فرشتگان صف بسته حاضر شوند.');
INSERT INTO "badkhashani" VALUES(89,23,'89:23','و آن روز دوزخ در میان آورده شود، آن روز انسان (کافر) عبرت می‌گیرد، ولی چنین عبرت گرفتن چه سودی برای او دارد؟!');
INSERT INTO "badkhashani" VALUES(89,24,'89:24','می‌گوید: ای کاش برای این زندگی‌ام چیزی قبلاً می‌فرستادم.');
INSERT INTO "badkhashani" VALUES(89,25,'89:25','پس آن روز هیچ کسی چون عذاب الله عذاب نکند.');
INSERT INTO "badkhashani" VALUES(89,26,'89:26','و هیچ کسی هم چون الله او را به بند (زنجیرها) نمی‌بندد.');
INSERT INTO "badkhashani" VALUES(89,27,'89:27','(و در حالت نزع گفته شود که) ای نفس (شخص) آرام‌گیرنده (به ایمان)!');
INSERT INTO "badkhashani" VALUES(89,28,'89:28','به‌سوی پروردگارت بازگرد، (در حالی که) تو از او راضی و او از تو راضی است.');
INSERT INTO "badkhashani" VALUES(89,29,'89:29','پس در زمره بندگانم داخل شو.');
INSERT INTO "badkhashani" VALUES(89,30,'89:30','و در جنت من داخل شو.');
INSERT INTO "badkhashani" VALUES(90,1,'90:1','قسم به این شهر (مکه مکرمه).');
INSERT INTO "badkhashani" VALUES(90,2,'90:2','و در حالی که تو در این شهر (مکه) ساکن هستی.');
INSERT INTO "badkhashani" VALUES(90,3,'90:3','و قسم به پدر و به آنچه از او تولد شده است.');
INSERT INTO "badkhashani" VALUES(90,4,'90:4','که البته انسان را در (قعر) مشقت و تکلیف آفریدیم.');
INSERT INTO "badkhashani" VALUES(90,5,'90:5','آیا (انسان) گمان می‌کند که هرگز کسی بر او قادر نمی‌شود.');
INSERT INTO "badkhashani" VALUES(90,6,'90:6','و می‌گوید: مال بسیار انفاق کردم.');
INSERT INTO "badkhashani" VALUES(90,7,'90:7','آیا گمان می‌کند که او را کسی ندیده است؟');
INSERT INTO "badkhashani" VALUES(90,8,'90:8','آیا برای او دو چشم قرار ندادیم؟');
INSERT INTO "badkhashani" VALUES(90,9,'90:9','و یک زبان و دو لب؟');
INSERT INTO "badkhashani" VALUES(90,10,'90:10','و به او راه خیر و شر را نشان دادیم.');
INSERT INTO "badkhashani" VALUES(90,11,'90:11','پس در آن گردنۀ سخت قدم نگذاشت؟');
INSERT INTO "badkhashani" VALUES(90,12,'90:12','و چه می‌دانی که آن گردنۀ سخت چیست؟');
INSERT INTO "badkhashani" VALUES(90,13,'90:13','آزاد کردن گردن‌ها (برده‌ها) است.');
INSERT INTO "badkhashani" VALUES(90,14,'90:14','یا طعام دادن در روزِ گرسنگی است.');
INSERT INTO "badkhashani" VALUES(90,15,'90:15','به یتیمی که از اقارب باشد.');
INSERT INTO "badkhashani" VALUES(90,16,'90:16','یا به مسکین خاک‌آلود.');
INSERT INTO "badkhashani" VALUES(90,17,'90:17','باز از آنانی باشد که ایمان آورده‌اند و یکدیگر را به صبر نمودن (در راه دین) و نرمی و مهربانی سفارش کرده‌اند.');
INSERT INTO "badkhashani" VALUES(90,18,'90:18','این گروه اصحاب دست راست‌اند.');
INSERT INTO "badkhashani" VALUES(90,19,'90:19','و آنانی که به آیات ما کفر ورزیدند، آن‌ها اصحاب دست چپ‌اند.');
INSERT INTO "badkhashani" VALUES(90,20,'90:20','بر آنان (از هر سو) آتشی مسلط باشد (که راه فرار نداشته باشند).');
INSERT INTO "badkhashani" VALUES(91,1,'91:1','قسم به آفتاب و روشنی آن.');
INSERT INTO "badkhashani" VALUES(91,2,'91:2','و قسم به ماه چون از پی آفتاب رود (بعد از غروب آفتاب ظاهر شود).');
INSERT INTO "badkhashani" VALUES(91,3,'91:3','و قسم به روز چون ظاهر کند روشنی آفتاب را.');
INSERT INTO "badkhashani" VALUES(91,4,'91:4','و قسم به شب چون بپوشاند نور آفتاب را.');
INSERT INTO "badkhashani" VALUES(91,5,'91:5','و قسم به آسمان و به آن ذاتی آن را ساخته است.');
INSERT INTO "badkhashani" VALUES(91,6,'91:6','و قسم به زمین و به آن ذاتی که آن را هموار کرده است.');
INSERT INTO "badkhashani" VALUES(91,7,'91:7','و قسم به نفس انسانی و به آن ذاتی که برابر کرده است (قوت جسمی و روحی او را).');
INSERT INTO "badkhashani" VALUES(91,8,'91:8','باز به او راه بدی و نیکی او را الهام کرد.');
INSERT INTO "badkhashani" VALUES(91,9,'91:9','البته کامیاب شد هرکس که نفس خود را پاک کرد.');
INSERT INTO "badkhashani" VALUES(91,10,'91:10','و البته ناکام و ناامید شد آن که نفس خود را به گناه آلود.');
INSERT INTO "badkhashani" VALUES(91,11,'91:11','(مانند) قوم ثمود که به سبب سرکشی رسول الله را تکذیب نمودند.');
INSERT INTO "badkhashani" VALUES(91,12,'91:12','آن وقت که بدبخت‌ترین آن‌ها برخواست.');
INSERT INTO "badkhashani" VALUES(91,13,'91:13','پس رسول الله به آن‌ها گفت: شترِ (آفریدۀ) الله و آب‌خوردن آن را بگذارید.');
INSERT INTO "badkhashani" VALUES(91,14,'91:14','پس او را تکذیب نمودند و شتر را کشتند، پس به سزای گناهانشان، پروردگارشان بر آنان عذاب نازل کرد و ایشان را هلاک کرد و آن عقوبت همۀ آن‌ها را با خاک برابر کرد.');
INSERT INTO "badkhashani" VALUES(91,15,'91:15','و الله از عاقبت آن کار نمی‌ترسد.');
INSERT INTO "badkhashani" VALUES(92,1,'92:1','قسم به شب چون روز را (با تاریکی) بپوشاند.');
INSERT INTO "badkhashani" VALUES(92,2,'92:2','و قسم به روز چون روشن شود.');
INSERT INTO "badkhashani" VALUES(92,3,'92:3','و قسم به آن ذاتی که نر و ماده را آفریده است.');
INSERT INTO "badkhashani" VALUES(92,4,'92:4','همانا کوشش‌های شما گوناگون و مختلف است.');
INSERT INTO "badkhashani" VALUES(92,5,'92:5','پس اما کسی که (در راه الله مال خود را) بخشید و پرهیزگاری کرد.');
INSERT INTO "badkhashani" VALUES(92,6,'92:6','و شریعت نیک (و آسان) را تصدیق کرد.');
INSERT INTO "badkhashani" VALUES(92,7,'92:7','پس (راه رسیدن) به آسایش را برای او آسان می‌گردانیم.');
INSERT INTO "badkhashani" VALUES(92,8,'92:8','ولی کسی که بخل ورزید، و (خود را) مستغنی شمرد.');
INSERT INTO "badkhashani" VALUES(92,9,'92:9','و (شریعت) نیک (و آسان) را تکذیب کرد.');
INSERT INTO "badkhashani" VALUES(92,10,'92:10','پس زودا که راه سختی (راه دوزخ) را به او آسان کنیم.');
INSERT INTO "badkhashani" VALUES(92,11,'92:11','و وقتی (به گور) انداخته شود مال و دارایی‌اش به او سودی نخواهد کرد.');
INSERT INTO "badkhashani" VALUES(92,12,'92:12','البته راهنمائی (انسان‌ها) بر عهدۀ ماست.');
INSERT INTO "badkhashani" VALUES(92,13,'92:13','و البته (کامیابی) دنیا و آخرت در اختیار ماست.');
INSERT INTO "badkhashani" VALUES(92,14,'92:14','پس شما را از آتشی که شعله می‌زند ترسانیدم.');
INSERT INTO "badkhashani" VALUES(92,15,'92:15','جز بدبخت‌ترین انسان‌ها در آن درنیاید.');
INSERT INTO "badkhashani" VALUES(92,16,'92:16','آن که تکذیب کرد و روی گشتاند.');
INSERT INTO "badkhashani" VALUES(92,17,'92:17','و پرهیزگارترین (انسان‌ها) از آن دور داشته خواهد شد.');
INSERT INTO "badkhashani" VALUES(92,18,'92:18','آن که مال خود را می‌دهد تا پاک شود.');
INSERT INTO "badkhashani" VALUES(92,19,'92:19','و هیچ کسی نزد او نعمتی ندارد که پاداش (آن را) ببیند.');
INSERT INTO "badkhashani" VALUES(92,20,'92:20','(انفاق نمی‌کند آن متقی) مگر برای کسب رضایت پروردگار والاترش.');
INSERT INTO "badkhashani" VALUES(92,21,'92:21','و البته به زودی راضی شود.');
INSERT INTO "badkhashani" VALUES(93,1,'93:1','قسم به وقت چاشت.');
INSERT INTO "badkhashani" VALUES(93,2,'93:2','و قسم به شب چون (تاریک شود و) آرام گیرد.');
INSERT INTO "badkhashani" VALUES(93,3,'93:3','پروردگارت تو را رها نکرده و بر تو خشم نگرفته است.');
INSERT INTO "badkhashani" VALUES(93,4,'93:4','و البته آخرت برای تو بهتر از دنیاست.');
INSERT INTO "badkhashani" VALUES(93,5,'93:5','و البته پروردگارت به تو (نعمت) خواهد داد که راضی و شادمان شوی.');
INSERT INTO "badkhashani" VALUES(93,6,'93:6','آیا تو را یتیم نیافت پس تو را جای داد؟');
INSERT INTO "badkhashani" VALUES(93,7,'93:7','و تو را بی‌خبر (از احکام شرع) یافت پس هدایت کرد.');
INSERT INTO "badkhashani" VALUES(93,8,'93:8','و تو را فقیر یافت پس غنی گردانید.');
INSERT INTO "badkhashani" VALUES(93,9,'93:9','پس بر یتیم قهر مکن.');
INSERT INTO "badkhashani" VALUES(93,10,'93:10','و سائل و گدا را از خود مران.');
INSERT INTO "badkhashani" VALUES(93,11,'93:11','و اما نعمت‌های پروردگارت را بازگو کن.');
INSERT INTO "badkhashani" VALUES(94,1,'94:1','آیا سینه‌ات را برایت نکشادیم؟');
INSERT INTO "badkhashani" VALUES(94,2,'94:2','و بار سنگین را از تو برنداشتیم؟');
INSERT INTO "badkhashani" VALUES(94,3,'94:3','همان باری که پشت تو را سنگین کرده بود.');
INSERT INTO "badkhashani" VALUES(94,4,'94:4','و آوازه‌ات را برایت بلند گردانیدیم.');
INSERT INTO "badkhashani" VALUES(94,5,'94:5','پس یقیناً با هر سختی آسانی است.');
INSERT INTO "badkhashani" VALUES(94,6,'94:6','یقیناً با (همان) سختی آسانی (دیگر) است.');
INSERT INTO "badkhashani" VALUES(94,7,'94:7','پس هرگاه فارغ شدی (از دعوت و تبلیغ)، پس بکوش (برای عبادت پروردگارت).');
INSERT INTO "badkhashani" VALUES(94,8,'94:8','و (با رغبت و اشتیاق) به‌سوی پروردگارت روی آور.');
INSERT INTO "badkhashani" VALUES(95,1,'95:1','قسم به انجیر و زیتون.');
INSERT INTO "badkhashani" VALUES(95,2,'95:2','و قسم به طور سینا.');
INSERT INTO "badkhashani" VALUES(95,3,'95:3','و قسم به این شهر امن (مکه).');
INSERT INTO "badkhashani" VALUES(95,4,'95:4','بی‌گمان انسان را در بهترین صورت (و ترکیب) آفریدیم.');
INSERT INTO "badkhashani" VALUES(95,5,'95:5','باز او را به پست‌ترین مرحله (حقیرترین مرتبه؛ به سبب کفر) بازگردانیدیم.');
INSERT INTO "badkhashani" VALUES(95,6,'95:6','مگر آنانی که ایمان آوردند و کارهای نیک انجام دادند، پس برای آن‌ها اجریست قطع ناشدنی.');
INSERT INTO "badkhashani" VALUES(95,7,'95:7','پس چه چیز سبب می‌شود (ای انسان) که قیامت را تکذیب کنی.');
INSERT INTO "badkhashani" VALUES(95,8,'95:8','آیا الله بزرگ‌ترین فرمانروایان نیست؟ (بلی هست! و من بر آن از جملۀ گواهانم).');
INSERT INTO "badkhashani" VALUES(96,1,'96:1','بخوان به نام پروردگارت که (همه چیز را) آفرید.');
INSERT INTO "badkhashani" VALUES(96,2,'96:2','(و) انسان را از خون بسته آفرید.');
INSERT INTO "badkhashani" VALUES(96,3,'96:3','بخوان که پروردگارت کریم‌تر (و گرامی‌تر) است.');
INSERT INTO "badkhashani" VALUES(96,4,'96:4','آن که (انسان را) با قلم آموخت.');
INSERT INTO "badkhashani" VALUES(96,5,'96:5','آن چیزی را به انسان آموخت که نمی‌دانست.');
INSERT INTO "badkhashani" VALUES(96,6,'96:6','نه چنین نیست، بی‌گمان انسان طغیان می‌کند.');
INSERT INTO "badkhashani" VALUES(96,7,'96:7','چون خود را مستغنی (بی‌نیاز) می‌بیند.');
INSERT INTO "badkhashani" VALUES(96,8,'96:8','البته بازگشت همه به‌سوی پروردگار توست.');
INSERT INTO "badkhashani" VALUES(96,9,'96:9','آیا دیدی آن را که منع می‌کند.');
INSERT INTO "badkhashani" VALUES(96,10,'96:10','بنده‌ای را که نماز گزارد.');
INSERT INTO "badkhashani" VALUES(96,11,'96:11','آیا دیده‌ای (ای مخاطب) که (آن بندۀ نمازگزار) بر راه راست باشد.');
INSERT INTO "badkhashani" VALUES(96,12,'96:12','یا به پرهیزگاری سفارش کند (بازهم او را منع می‌کند)؟');
INSERT INTO "badkhashani" VALUES(96,13,'96:13','آیا دیده‌ای (ای مخاطب که) اگر (حق را) تکذیب کند و اعراض نماید (آیا الله او را به حالش می‌گذارد؟)');
INSERT INTO "badkhashani" VALUES(96,14,'96:14','آیا ندانست که الله می‌بیند؟');
INSERT INTO "badkhashani" VALUES(96,15,'96:15','چنین نیست (که او گمان دارد، اگر بازنیاید (آن منع‌کننده) البته پیشانی‌اش را خواهیم کشید.');
INSERT INTO "badkhashani" VALUES(96,16,'96:16','پیشانی دروغ‌گوی خطاکار.');
INSERT INTO "badkhashani" VALUES(96,17,'96:17','پس باید اهل مجلسش را بخواهد.');
INSERT INTO "badkhashani" VALUES(96,18,'96:18','ما هم به زودی فرشته‌های موظف دوزخ را طلب می‌کنیم.');
INSERT INTO "badkhashani" VALUES(96,19,'96:19','نه، چنین نیست، هرگز از او اطاعت مکن (بلکه) سجده کن (به دربار پروردگارت) و تقرب جوی.');
INSERT INTO "badkhashani" VALUES(97,1,'97:1','یقیناً ما قرآن را در شب قدر (شب دارای مرتبه و فضیلت) نازل کردیم.');
INSERT INTO "badkhashani" VALUES(97,2,'97:2','و تو چه می‌دانی که شب قدر چیست؟');
INSERT INTO "badkhashani" VALUES(97,3,'97:3','شب قدر بهتر است از هزار ماه.');
INSERT INTO "badkhashani" VALUES(97,4,'97:4','(خاصیت آن شب این است که) فرشتگان و جبرئیل در آن شب به حکم پروردگار خود فرود می‌آیند، برای انجام دادن هر کاری (که الله خواهد).');
INSERT INTO "badkhashani" VALUES(97,5,'97:5','آن شب محل نزول سلامتی و رحمت است تا طلوع صبح.');
INSERT INTO "badkhashani" VALUES(98,1,'98:1','کافران اهل کتاب (یهود و نصاری) و مشرکین (از دین و آئین خود) دست‌بردار نیستند تا آن که دلیل ظاهر به آن‌ها بیاید.');
INSERT INTO "badkhashani" VALUES(98,2,'98:2','پیغمبری از طرف الله که صحیفه‌های پاک را بر آن‌ها تلاوت می‌کند.');
INSERT INTO "badkhashani" VALUES(98,3,'98:3','که در آن نوشته‌های (احکام) راست و درست است.');
INSERT INTO "badkhashani" VALUES(98,4,'98:4','و اهل کتاب متفرق نشدند (اختلاف نکردند) مگر بعد از این که به آن‌ها دلیل روشن آمد.');
INSERT INTO "badkhashani" VALUES(98,5,'98:5','حال آن که مأمور نبودند مگر این که الله را خالصانه عبادت کنند که برای او دین و عبادت خود را خالص سازند، و متنفر از دین‌های باطل باشند، و نماز را برپا کنند و زکات بدهند، و این است دین راست و درست.');
INSERT INTO "badkhashani" VALUES(98,6,'98:6','یقیناً آنانی از اهل کتاب و مشرکان که کافر شدند در آتش دوزخ‌اند، در آن جاودانه‌اند، این گروه بدترین مخلوقات‌اند.');
INSERT INTO "badkhashani" VALUES(98,7,'98:7','البته آنانی که ایمان آورده‌اند و اعمال نیک انجام داده‌اند این‌ها بهترین مخلوقات‌اند.');
INSERT INTO "badkhashani" VALUES(98,8,'98:8','جزای (ثواب) آن‌ها نزد پروردگارشان باغ‌های جاودانه است که از زیر آن‌ها جوی‌ها می‌رود، جاودانه و برای همیشه در آن خواهند ماند. الله از آن‌ها راضی است و آن‌ها از الله راضی‌اند، این جزا برای کسی است که از پروردگارش بترسد.');
INSERT INTO "badkhashani" VALUES(99,1,'99:1','وقتی که زمین به لرزش شدید خود لرزانده شود.');
INSERT INTO "badkhashani" VALUES(99,2,'99:2','و زمین بارهای سنگین خود را (معادن و اموات را) بیرون کند.');
INSERT INTO "badkhashani" VALUES(99,3,'99:3','و انسان گوید: این زمین را چه شده است؟');
INSERT INTO "badkhashani" VALUES(99,4,'99:4','در آن روز زمین خبرهای خود را (راجع به اعمال بنی آدم) اظهار کند.');
INSERT INTO "badkhashani" VALUES(99,5,'99:5','چرا که پروردگارت به او (این کار را) وحی کرده است.');
INSERT INTO "badkhashani" VALUES(99,6,'99:6','در آن روز مردم (به دربار الهی) گروه‌های متفرق از قبرها بیرون می‌آیند، تا اعمال‌شان به آنان نشان داده شود.');
INSERT INTO "badkhashani" VALUES(99,7,'99:7','پس هرکس به اندازۀ ذره‌ای نیکی کرده باشد (پاداش) آن را می‌بیند.');
INSERT INTO "badkhashani" VALUES(99,8,'99:8','و هرکس به اندازۀ ذره‌ای بدی کرده باشد (پاداش) آن را (در آن روز) می‌بیند.');
INSERT INTO "badkhashani" VALUES(100,1,'100:1','قسم به اسپان دونده (در عرصۀ جهاد) که نفس‌زنان می‌دوند.');
INSERT INTO "badkhashani" VALUES(100,2,'100:2','باز قسم به اسپانی که (از سم شان) جرقه می‌افروزند.');
INSERT INTO "badkhashani" VALUES(100,3,'100:3','باز قسم به اسپانی که در وقت صبح بر دشمن یورش می‌برند.');
INSERT INTO "badkhashani" VALUES(100,4,'100:4','پس (به سبب تیز دویدن) گرد و غبار برمی‌انگیزند.');
INSERT INTO "badkhashani" VALUES(100,5,'100:5','باز در میان گروه دشمن داخل می‌شوند.');
INSERT INTO "badkhashani" VALUES(100,6,'100:6','همانا انسان (در مقابل پروردگار خود) بسیار ناشکر است.');
INSERT INTO "badkhashani" VALUES(100,7,'100:7','و او بر ناشکری خود گواه است.');
INSERT INTO "badkhashani" VALUES(100,8,'100:8','و او علاقۀ شدیدی به مال و دارایی دارد.');
INSERT INTO "badkhashani" VALUES(100,9,'100:9','آیا انسان نمی‌داند که وقتی آنچه در قبرهاست، برانگیخته شود.');
INSERT INTO "badkhashani" VALUES(100,10,'100:10','و آنچه در سینه‌هاست ظاهر شود.');
INSERT INTO "badkhashani" VALUES(100,11,'100:11','البته پروردگارشان در آن روز، از وضع و حال آن‌ها باخبر است.');
INSERT INTO "badkhashani" VALUES(101,1,'101:1','قارعه (حادثه کوبندۀ قیامت)');
INSERT INTO "badkhashani" VALUES(101,2,'101:2','چیست آن قارعه (حادثۀ کوبنده)؟');
INSERT INTO "badkhashani" VALUES(101,3,'101:3','و تو را چه چیز آگاه ساخت که آن قارعه (حادثه کوبنده) چیست؟');
INSERT INTO "badkhashani" VALUES(101,4,'101:4','(آن حادثه در) روزی (ظاهر گردد) که مردم مانند پروانه‌های پراگنده شوند.');
INSERT INTO "badkhashani" VALUES(101,5,'101:5','و کوه‌ها مانند پشم رنگین و حلاجی‌شده شوند.');
INSERT INTO "badkhashani" VALUES(101,6,'101:6','پس کسی که کفه ترازوی نیکی‌های او گران شود.');
INSERT INTO "badkhashani" VALUES(101,7,'101:7','پس او در زندگی رضایت‌بخشی باشد.');
INSERT INTO "badkhashani" VALUES(101,8,'101:8','اما کسی که کفه ترازوی نیکی‌های او سبک شود.');
INSERT INTO "badkhashani" VALUES(101,9,'101:9','پس مأوای او هاویه است.');
INSERT INTO "badkhashani" VALUES(101,10,'101:10','و تو را چه چیز آگاه ساخت که هاویه چیست؟');
INSERT INTO "badkhashani" VALUES(101,11,'101:11','آن آتشی است بزرگ و سوزان.');
INSERT INTO "badkhashani" VALUES(102,1,'102:1','طلب کثرت مال و اولاد شما را مشغول (غافل) ساخت.');
INSERT INTO "badkhashani" VALUES(102,2,'102:2','تا آن که به قبرستان رسیدید (یعنی دفن گردیدید).');
INSERT INTO "badkhashani" VALUES(102,3,'102:3','چنین نیست؛ خواهید دانست (در قبر).');
INSERT INTO "badkhashani" VALUES(102,4,'102:4','باز (می‌گویم) که چنین نیست، خواهید دانست (در روز قیامت).');
INSERT INTO "badkhashani" VALUES(102,5,'102:5','چنین نیست، اگر شما (حق‌بودنِ قیامت را) به یقین می‌دانستید (از آمدن آن غافل نمی‌شدید).');
INSERT INTO "badkhashani" VALUES(102,6,'102:6','البته دوزخ را (بعد از موت در برزخ) خواهید دید.');
INSERT INTO "badkhashani" VALUES(102,7,'102:7','باز البته آن (دوزخ) را به دیدۀ یقین (در روز قیامت) خواهید دید.');
INSERT INTO "badkhashani" VALUES(102,8,'102:8','باز البته در آن روز دربارۀ نعمت‌های که الله به شما داده است، پرسیده خواهید شد.');
INSERT INTO "badkhashani" VALUES(103,1,'103:1','قسم به زمانه.');
INSERT INTO "badkhashani" VALUES(103,2,'103:2','که البته انسان در خساره است.');
INSERT INTO "badkhashani" VALUES(103,3,'103:3','به جز آنانی که ایمان آوردند و اعمال نیک انجام دادند و یکدیگر را به حق توصیه کردند، و یکدیگر را به صبر (در راه حق) توصیه کردند.');
INSERT INTO "badkhashani" VALUES(104,1,'104:1','وای بر هر طعنه‌زنِ عیب‌جو.');
INSERT INTO "badkhashani" VALUES(104,2,'104:2','آن که مال را جمع کرد و آن را شمارید.');
INSERT INTO "badkhashani" VALUES(104,3,'104:3','گمان می‌کند که مالش او را جاودانه می‌دارد.');
INSERT INTO "badkhashani" VALUES(104,4,'104:4','نه، چنین نیست (بلکه) در حطمه انداخته خواهد شد.');
INSERT INTO "badkhashani" VALUES(104,5,'104:5','و تو را چه چیز آگاه ساخت که حطمه چیست؟');
INSERT INTO "badkhashani" VALUES(104,6,'104:6','(آن) آتش الله است که برافروخته شده.');
INSERT INTO "badkhashani" VALUES(104,7,'104:7','آتشی که بر دل‌ها مسلط می‌شود.');
INSERT INTO "badkhashani" VALUES(104,8,'104:8','البته (دروازۀ) آن آتش بر آن‌ها بسته شده است.');
INSERT INTO "badkhashani" VALUES(104,9,'104:9','(در حالی که بند و قید اند) در ستون‌های (آتشین) دراز.');
INSERT INTO "badkhashani" VALUES(105,1,'105:1','آیا ندیدی که پروردگارت با صاحبان فیل چه کرد؟');
INSERT INTO "badkhashani" VALUES(105,2,'105:2','آیا نیرنگ آن‌ها را تباه و باطل نگردانید؟');
INSERT INTO "badkhashani" VALUES(105,3,'105:3','و بر آن‌ها پرنده‌های (کوچک) را گروه گروه فرستاد.');
INSERT INTO "badkhashani" VALUES(105,4,'105:4','که بر آن‌ها سنگریزه‌های از گل انداختند.');
INSERT INTO "badkhashani" VALUES(105,5,'105:5','پس (الله) آن‌ها را مانند کاه جویده‌شده ساخت.');
INSERT INTO "badkhashani" VALUES(106,1,'106:1','برای الفت‌دادن قریش.');
INSERT INTO "badkhashani" VALUES(106,2,'106:2','الفت‌دادن آن‌ها به سفر زمستان و تابستان.');
INSERT INTO "badkhashani" VALUES(106,3,'106:3','پس باید صاحب این خانه را عبادت کنند.');
INSERT INTO "badkhashani" VALUES(106,4,'106:4','کسی که آن‌ها را در گرسنگی طعام داد و در وقت ترس، امن و امان بخشید.');
INSERT INTO "badkhashani" VALUES(107,1,'107:1','آیا دیدی آن شخصی را که جزای اعمال را تکذیب می‌کند.');
INSERT INTO "badkhashani" VALUES(107,2,'107:2','پس آن شخص همان است که یتیم را (اهانت کرده) از خود میراند.');
INSERT INTO "badkhashani" VALUES(107,3,'107:3','(و مردم را) بر طعام‌دادن مسکین ترغیب نمی‌کند.');
INSERT INTO "badkhashani" VALUES(107,4,'107:4','پس وای بر آن نمازگزارانی که،');
INSERT INTO "badkhashani" VALUES(107,5,'107:5','از نماز خود غافل‌اند.');
INSERT INTO "badkhashani" VALUES(107,6,'107:6','آنانی که خودنمایی می‌کنند.');
INSERT INTO "badkhashani" VALUES(107,7,'107:7','و از دادن وسایل (یا حاجت به همسایه) خودداری می‌کنند.');
INSERT INTO "badkhashani" VALUES(108,1,'108:1','البته (ای محمد! خیر بسیار که از جملۀ آن) حوض کوثر (می‌باشد) به تو دادیم.');
INSERT INTO "badkhashani" VALUES(108,2,'108:2','پس برای (رضای) پروردگارت نماز بگزار، و قربانی کن.');
INSERT INTO "badkhashani" VALUES(108,3,'108:3','یقیناً دشمن تو بی‌عقب و مقطوع النسل خواهد بود.');
INSERT INTO "badkhashani" VALUES(109,1,'109:1','بگو: ای کافران.');
INSERT INTO "badkhashani" VALUES(109,2,'109:2','من نمی‌پرستم چیزی را که شما می‌پرستید.');
INSERT INTO "badkhashani" VALUES(109,3,'109:3','و نه شما می‌پرستید آنچه را که من می‌پرستم.');
INSERT INTO "badkhashani" VALUES(109,4,'109:4','و نه من می‌پرستم آنچه را که شما پرستش کرده‌اید.');
INSERT INTO "badkhashani" VALUES(109,5,'109:5','و نه شما (در آینده) آنچه را که من می‌پرستم، می‌پرستید.');
INSERT INTO "badkhashani" VALUES(109,6,'109:6','(لذا) دین شما برای خودتان و دین من برای خودم است.');
INSERT INTO "badkhashani" VALUES(110,1,'110:1','چون مدد الله (فتح مکه) برسد.');
INSERT INTO "badkhashani" VALUES(110,2,'110:2','و مردم را ببینی که دسته دسته در دین الله (دین اسلام) داخل شوند.');
INSERT INTO "badkhashani" VALUES(110,3,'110:3','پس پروردگار خود را به پاکی و ثنا یاد کن و از او مغفرت بخواه، چون که او توبه‌پذیر است.');
INSERT INTO "badkhashani" VALUES(111,1,'111:1','بریده (و شکسته) باد دست‌های ابولهب، و (ابولهب) هلاک شد.');
INSERT INTO "badkhashani" VALUES(111,2,'111:2','(که) مال و آنچه به دست آورده است (از عزت و ریاست) به او سودی نکرد.');
INSERT INTO "badkhashani" VALUES(111,3,'111:3','به زودی در آتش شعله‌ور داخل شود (بعد از مرگ).');
INSERT INTO "badkhashani" VALUES(111,4,'111:4','و همسرِ او (نیز داخل شود با او) در حالی که هیزم‌کش است.');
INSERT INTO "badkhashani" VALUES(111,5,'111:5','و در گردن او ریسمانی از لیف خرما است.');
INSERT INTO "badkhashani" VALUES(112,1,'112:1','(ای پیغمبر)! بگو: او الله یگانه است.');
INSERT INTO "badkhashani" VALUES(112,2,'112:2','(و) الله از هرچیز بی‌نیاز (و برطرف کنندۀ نیازها) است.');
INSERT INTO "badkhashani" VALUES(112,3,'112:3','نه کسی را زاده و نه او زاده شده است.');
INSERT INTO "badkhashani" VALUES(112,4,'112:4','و هیچکس مانند و همتای او نیست.');
INSERT INTO "badkhashani" VALUES(113,1,'113:1','(ای پیغمبر)! بگو: پناه می‌برم به پروردگار صبح (سپیده دم).');
INSERT INTO "badkhashani" VALUES(113,2,'113:2','از شر هر آنچه آفریده است.');
INSERT INTO "badkhashani" VALUES(113,3,'113:3','و از شر تاریکی شب وقتی که همه جا را بپوشاند.');
INSERT INTO "badkhashani" VALUES(113,4,'113:4','و از شر زنان جادوگر که در گره‌ها می‌دمند.');
INSERT INTO "badkhashani" VALUES(113,5,'113:5','و از شر هر حسودی وقتی که حسد ورزد.');
INSERT INTO "badkhashani" VALUES(114,1,'114:1','(ای پیغمبر)! بگو: پناه می‌برم به پروردگار مردم.');
INSERT INTO "badkhashani" VALUES(114,2,'114:2','(و به) پادشاه (حقیقی) مردم.');
INSERT INTO "badkhashani" VALUES(114,3,'114:3','(و به) معبود (حقیقی) مردم.');
INSERT INTO "badkhashani" VALUES(114,4,'114:4','از شر وسوسه‌کننده‌ای که واپس می‌رود.');
INSERT INTO "badkhashani" VALUES(114,5,'114:5','آن که در دل‌های مردم وسوسه می‌اندازد.');
INSERT INTO "badkhashani" VALUES(114,6,'114:6','از جنس جن باشد و یا از جنس انسان.');
