INSERT INTO "bamoki" VALUES(1,1,'1:1','بەناوی خوای بەخشندەی میهرەبان');
INSERT INTO "bamoki" VALUES(1,2,'1:2','ھەموو سوپاس وستایشێك بۆ خوای پەروەردگاری جیھانیان');
INSERT INTO "bamoki" VALUES(1,3,'1:3','بەخشندەی میھرەبان (الرحمن) بەو مانایەی ڕەحم وبەزەیی پەروەردگار لەم جیھانەدا ھەمووانی گرتوەتەوە بەبێ باوەڕانیشەوە، وە (الرحیم) ڕەحم وبەزەیی پەروەردگار وەك زانایان دەڵێن تایبەتە بەبڕوادارانەوە لەڕۆژی دواییدا');
INSERT INTO "bamoki" VALUES(1,4,'1:4','تەنھا ئەوە خاوەنی ڕۆژی سزا وپاداشت دانەوە (باوەڕداران لە نوێژەکانیاندا لە ھەموو ڕکاتێکی ئەمە دەڵێنەوە، تاوەکو ھەمیشە ڕۆژی دواییان لەبیر بێت وبەکار وکردەوەی چاکە خۆیانی بۆ ئامادە بکەن)');
INSERT INTO "bamoki" VALUES(1,5,'1:5','(خوایە) تەنھا تۆدەپەرستین (تەنھا تۆیت شایستەی پەرستن وجگە لە تۆ ھیچ کەس شیاوی ئەوە نیە)، وە تەنھا لەتۆ داوای یارمەتی دەکەین (لەھەموو کار وکردەوە وپێداویستیەکماندا، چونکە ھەموو فەرمانێك بەدەست تۆیە، جگە لە تۆ ھیچ کەس بەئەندازەی گەردیلەیەک خاوەنی ھیچ شتێک نییە)');
INSERT INTO "bamoki" VALUES(1,6,'1:6','ڕێنموونیمان بکە بۆ ڕێگای ڕاستی (کە ڕێگای ڕاستی ئاینی پیرۆزی ئیسلامه، وە بەردەوام بین لەسەری ھەتا ئەو ڕۆژەی دەگەین بەدیداری زاتی ھەق تەعالای تۆ)');
INSERT INTO "bamoki" VALUES(1,7,'1:7','ڕێگای ئەوانەی کەچاکەت لەگەڵ کردوون (لەپێغەمبەران وڕاستگۆیان وشەھیدان وچاکەکاران)، نەك (ڕێگای) ئەوانەی خەشمی (تۆیان) لێگیراوە (وەک جولەکە وھاوکردەوەکانیان) وە نەك (ڕێگای) گومڕایان وەك (گاورەکان وھاوکردەوەکانیان)');
INSERT INTO "bamoki" VALUES(2,1,'2:1','بـە ( ئـەلیف، لام، میم ) دەخوێنرێتەوە، خوای گەورە زاناترە بە واتاکەی.ئەم پیتانەی کە لەسەرەتای ھەندێک لەسورەتەکانی قورئاندا ھاتوون، تەنھا خوای گەورە دەزانێت مەبەستی پێیان چیە، ئەمە ڕاسترین تەفسیرە بۆ ئەم پیتانە کرابێت، بەڵام بەچەند جۆرێکی تریش تەفسیرکراون لەوانە : ئەم پیتانە ناون بۆ ئەوسورەتانەی لەسەرەتایانەوە ھاتوون، یان ناون بۆ خوای گەورە، یان بۆ دەستەوسانکردنی عەرەبە بێباوەڕەکانە کە دەیانووت ئەم قورئانە لەلایەن خوداوە نەھاتوەتە خوارەوە بۆ موحەممەد ( صلى الله عليه وسلم ) پێیان دەڵێت : دەی ئەگەر وایە وێنە وھاوشێوەی ئەم قورئانە بھێنن، کە ھەر لەو پیتانە پێـکھاتووە کە ئێوە قسەی پێ دەکەن و بەکاریان دەھێنن .');
INSERT INTO "bamoki" VALUES(2,2,'2:2','ئەم قورئانە ھیچ گومانێکی تێدا نیە (کـە لەلایـەن خوای گـەورەوە نـێـرراوە) ڕێنیشاندەری پارێزکار و لەخواترسانە (پارێزکاران ولەخوا ترسان بەزانست وزانیاری بەسوود وکار وکردەوەی چاکە سوودی لێ وەردەگرن، چونکە ئەوانن لەپەروەردگاریان دەترسن وشوێن فەرمان وڕێنمایەکانی دەکەون)');
INSERT INTO "bamoki" VALUES(2,3,'2:3','ئەوانەی کەبڕوا دەھێنن بەشتە پەنھان ونادیارەکان (ئەو شتە پەنھانەی کە بە حەواس وعەقڵی مرۆڤ ھەستیان پێ ناکرێت، لە ڕێگەی وەحیەوە نەبێت، وەک باوەڕ بەفریشتەکان وبەھەشت ودۆزەخ وزۆر شتی تریش...) وە نوێژ دەکەن (بەڕێک وپێکی ولەکاتی خۆیدا ئەنجامی ئەدەن) و لەوەی کردوومانە بەڕزق وڕۆزیان دەیبەخشن (لەبەر ڕەزامەندی خوای گەورە)');
INSERT INTO "bamoki" VALUES(2,4,'2:4','وە ئەوانەی کەبڕوا دەھێنن بەو قورئانەی کە بۆ تۆ (ئەی موحەممەد نێرراوە) وە بەو کتێبانەشی کە بۆ (پێغەمبەران ونێرراوانی) پێش تۆ نێرراونەتە خوارەوە وەبڕوایان ھەیە بەڕۆژی دوایی');
INSERT INTO "bamoki" VALUES(2,5,'2:5','(خاوەنی ئەم صیفەتانە) لەسەر ڕێگه‌ی ڕاستن ( بەتەوفیق ویارمەتیدان) لە لایەن پەروەردگاریانەوە، وە ھەر ئەوانەن ڕزگار بووان (لە دوونیا ودواڕۆژدا)');
INSERT INTO "bamoki" VALUES(2,6,'2:6','بەڕاستی ئەوانەی کە بێ بڕوا بوون بۆ ئەوان چوون یەکە بیان ترسێنیت یان نەیان ترسێنیت هه‌ر بڕوا ناھێنن');
INSERT INTO "bamoki" VALUES(2,7,'2:7','خوا مۆری ناوە بەسەر دڵ و گوێیاندا، وە پەردە بەسەر چاویانەوەیە، وە سزای زۆرگەورەیان بۆ ھەیە');
INSERT INTO "bamoki" VALUES(2,8,'2:8','وەلەناو خەڵکیدا ھەندێ کەس ھەیە کەدەڵێ: بڕوامان ھێناوە بەخواو بەڕۆژی دوایی لەکاتێکدا ئەوان بڕوادار نین');
INSERT INTO "bamoki" VALUES(2,9,'2:9','(بەلای خۆیانەوە) خواو ئەوکەسانە دەخەڵەتێنن کە بڕوایان ھێناوە ! کەس ناخەڵەتێنن تەنھا خۆیان نەبێت بەڵام ھەستی پێ ناکەن');
INSERT INTO "bamoki" VALUES(2,10,'2:10','لە دڵیاندا نەخۆشی( ناپاکی) ھەیە خواش نەخۆشی یەکەی زۆرتر کردن (بەھاتنی قورئان) وە سزای سەختیان بۆ ھەیە بەھۆی ئەو درۆیانەوە کە دەیان کرد (و دەیان وت بڕوامان ھەیە)');
INSERT INTO "bamoki" VALUES(2,11,'2:11','وەکاتێک پێ یان بووترایە ئاشوب مەنێنەوە لە زەوی دا (ئاژاوە بەرپا مەکەن لە وڵاتدا) دەیان ووت: ئێمە ھەر چاکەکارین (ڕێکخەرین)');
INSERT INTO "bamoki" VALUES(2,12,'2:12','ئاگادار بن کەبەڕاستی ھەر ئەوانن ئاشوبگێڕ بەڵام ھەستی پێ ناکەن');
INSERT INTO "bamoki" VALUES(2,13,'2:13','وەکاتێ پێ یان بووترایە بڕوا بێنن وەک ئەوخەڵکانەی کە بڕوایان ھێناوە دەیان ووت: چۆن ئێمە بڕوابێنین وەک ئەوگێل ونەفامانەی کە بڕوایان ھێناوە بێدار بن کە بە ڕاستی ھەرخۆیان گێل و نەفامن بەڵام نازانن');
INSERT INTO "bamoki" VALUES(2,14,'2:14','وەکاتێ (ناپاکان) بگەیشتنایە بەوانەی کە بڕوایان ھێناوە دەیانووت : ئێمەش بڕوامان ھێناوە کاتێکیش بەتەنیا بوونایە لەگەڵ گەورە فێڵبازەکانیان دەیانووت : ئێمە ھەر لەگەڵ ئێوەین بێگومان ئێمە ھەر گاڵتە کەرین (بەوان)');
INSERT INTO "bamoki" VALUES(2,15,'2:15','خوا گاڵتە بەوان دەکات (لە بەرانبەر گاڵتە کردنەکەیان) وەماوەیان دەدات تا لەسەرکەشی خۆیاندا سەر لێشێواو بن');
INSERT INTO "bamoki" VALUES(2,16,'2:16','ئەمانە ئەوانەن کە گومڕاییان کڕی بە ڕێنمونیی جا نە بازرگانی یەکەیان قازانجی کردو نە ڕێبازی ڕاستیان گرت');
INSERT INTO "bamoki" VALUES(2,17,'2:17','(دوو ڕووەکان) وێنەیان وەک یەکێک وایە کە ( لەشەوێکی تاریکدا) ئاگرێک ھەڵگیرسێنێت کە لەگەڵ دەوروبەری ڕووناک کردەوە خوا ( خێرا) ڕووناکی یەکەیان لێ بکوژێنێتەوە, وە بیان ھێڵێتەوە لەناو چەند تاریکیەکی وادا کەھیچ نەبینن');
INSERT INTO "bamoki" VALUES(2,18,'2:18','کەڕو لاڵ و کوێرن بۆیە (لە گومڕایی خۆیان) ناگەڕێنەوە');
INSERT INTO "bamoki" VALUES(2,19,'2:19','یا وەک (کەسانێک لەچۆڵیەکدا) بارانێکی لێزمەو بەخوڕیان (لێ داکات) لەئاسمانەوە کە تاریکی زۆر و ھەورە تریشقەو بروسکەی تێدا بێت (بەجۆرێک کە) پەنجەکانیان دەکەن بە گوێیاندا لەبەر گرمەی ھەورە تریشقەکان بۆخۆپاراستن لەمردن وە خودا دەوری بێ بڕوایانی داوە');
INSERT INTO "bamoki" VALUES(2,20,'2:20','نزیک بێ ئەو بروسکەیە (بینایی) چاویان بڕفێنێ ھەرکاتێ ڕوناکیی بۆ بدانایەتەوە تیایدا بڕۆشتنایە وە کاتێ تاریکیشی بکردایە لێیان لەجێگەی خۆیان ڕادەمان( دەوەستان) ئەگەر خودا بیویستایە (بەو ھەورەگرمە و بروسکانە) کەڕو کوێری دەکردن بێگومان خوا بەسەر ھەموو شتێکدا دەسەڵاتدارە');
INSERT INTO "bamoki" VALUES(2,21,'2:21','ئەی خەڵکینە پەروەردگارتان بپەرستن کەئێوەو ئەوانەی پێش ئێوەشی دروست کردووە بەڵکو بەو پەرستنە خۆتان بپارێزن');
INSERT INTO "bamoki" VALUES(2,22,'2:22','ئەو( خوایەی) کەزەوی بۆ ڕاخستوون وە ئاسمانی بۆ کردوون بەکۆشک (ئاسمانی بۆگێڕاون بەگومەزی) وەئاوی (بارانی) لەئاسمانەوە ھێناوەتە خوارەوە ئەمجا دەری ھێناوە بەو ئاوە ھەموو جۆرە میوەو بەروبومێک بۆ ڕۆزیی ئێوە کەواتە ھاوتا بۆخوا پەیدامەکەن. کەئێوە خۆتان دەزانن (بێ ھاوتایە)');
INSERT INTO "bamoki" VALUES(2,23,'2:23','وەئەگەر ئێوە لەگومان دان لەم (قورئانەی) کەناردومانەتە خوارەوە بۆ بەندەی خۆمان(موحەممەد ) دە سورەتێک بێنن بەوێنەی ئەو وە بانگی ھاوکارەکانتان بکەن( لەڕەوان بێژان وشاعیران) بێجگەلە خوا ئەگەر ڕاست دەکەن، (بەفەرموودەی خوای نازانن)');
INSERT INTO "bamoki" VALUES(2,24,'2:24','ئەمجا ئەگەر ئەمەتان نەکرد؟ وە ھەرگیز نایشی کەن (ئیتر بێ بڕوامەبن) دەی خۆتان بپاریزن لەو ئاگرەی کە سوتەمەنیەکەی خەڵکی(بەدکار) وبەردە بۆبێ بڕوایان ساز کراوە (ئامادە كراوه)');
INSERT INTO "bamoki" VALUES(2,25,'2:25','(ئەی موحەممەد صلى الله عليه وسلم) مژدە بدە بەوانەی کە باوەڕیان ھێناوەو کردەوە باشەکانیان کردووە کە بێگومان بۆئەوان ھەیە بەھەشتانێک کە ڕووباری زۆر بەژێریاندا دەڕوات ھەرکاتێ لەوێ ڕۆزیان بدرێت لە میوەھات دەڵێن:ئەمە ئەوەیە کەلەمە و پێش پێمان درا (جیاواز لەتام وبۆندا) ئێستا وێنەیان ھێناوە, وەلەو بەھەشتانەدا بۆئەوان ھەیە ھاوسەرانی پاکیزە وەئەوان لەو (بەھەشتانە) دا ھەتا ھەتایین');
INSERT INTO "bamoki" VALUES(2,26,'2:26','بێگومان خوا شەرم ناکات بەوەی کە (بۆ تێگەیاندنی خەڵک) ھەر نموونەیەک بھێنێتەوە به مێشولەوبچوکتر (یاگەورەتر) لەویش ئەمجا ئەوانەی کەباوەڕدارن ئەوە چاک دەزانن کەئەو (نموونە ھێنانەوەیە) ڕاست ودروستە لە پەروەردگاریانەوە ئەوانەش کە بێ باوەڕن دەڵێن: خوا چی ویستووە بەم نموونە ھێنانەوەیە زۆر کەسی پێ گومڕادەکات وە ڕێنموونی زۆر کەسیشی پێ دەکات وە گومڕاناکات بەو نموونە ھێنانەوەیە مەگەر بەدکاران نەبێت');
INSERT INTO "bamoki" VALUES(2,27,'2:27','ئەوانەی کەپەیمانی خوا دەشکێنن و(ھەڵی دەوەشێننەوە) پاش بەستنی وە ئەیپچڕێنن ئەو (پەیوەندیانە) ی کەخوا فەرمانی داوە بەپەیوەستی کردنی وەلەزەویدا خراپە دەکەن ھەر ئەوانن زەرەرمەند و ڕەنجەڕۆ');
INSERT INTO "bamoki" VALUES(2,28,'2:28','چۆن بێ باوەڕ دەبن بەخوا!؟ لەکاتێکدا کەئێوە بێ گیان بوون، و گیانی بەبەردا کردن لەپاشان دەتان مرێنێ دوای ئەوەش زیندووتان دەکاتەوە ئەمجا بۆلای خۆی دەگەڕێنرێنەوە');
INSERT INTO "bamoki" VALUES(2,29,'2:29','خوا ئەو خوایەیەکەھەرچی لەزەویی دایە دروستی کردوە بۆئێوە پاشان ویستی ئاسمان دروست بکات ئەمجا ڕێکی خستن بەحەوت ئاسمان وە ئەو ئاگادارە بەھەموو شتێک');
INSERT INTO "bamoki" VALUES(2,30,'2:30','(بیریان بێنەوە) کاتێک کەپەروەردگاری تۆ بە فریشتەکانی ووت بەڕاستی من جێ نشینێک دروست دەکەم لەسەر زەوی (فریشتەکان) ووتیان: ئایا کەسێکی وای تیا دروست دەکەیت کە خراپەکاری تێدا بکات وە خوێنی تیا بڕێژێ؟ لە کاتێکدا کە ئێمە یادت دەکەین بەستایش کردنت وە بەپاک ڕات دەگرین (خوا) فەرموی: بێگومان ئەوەی من دەیزانم ئێوە نایزانن');
INSERT INTO "bamoki" VALUES(2,31,'2:31','خوا ناوی ھەموو شتێکی فێری ئادەم کرد لەپاشان ئەو شتانەی نیشانی فریشتەکاندا وە (خوا پێی) فەرموون: ناوی ئەمانەم پێ بڵێن ئەگەر ڕاست دەکەن، (کە ئێوە لە ئادەم باشترن)');
INSERT INTO "bamoki" VALUES(2,32,'2:32','(ھەموو) ووتیان: خوایە تۆ پاکیت لەھەڵە ئێمە ھیچ نازانین، مەگەر ئەوەی کە خۆت فێرت کردبین بەڕاستی ھەر تۆیت زاناو دانا');
INSERT INTO "bamoki" VALUES(2,33,'2:33','(خوا) فەرمووی: ئەی ئادەم ناوی ئەو شتانەیان پێ بڵێ جاکاتێک کە ناوەکانی پێ ووتن (خوا) فەرمووی: ئەی پێم نەووتن؟ بێگومان من ئاگادارم بەنھێنی ئاسمانەکان و زەوی وە ئاگادارم بەوەی کەئاشکرای دەکەن وە بەئەوەش کە دەیشارنەوە');
INSERT INTO "bamoki" VALUES(2,34,'2:34','(بێنەوە بیرت) کاتێک ووتمان بە فریشتەکان سوژدە ببەن بۆ ئادەم جا(ھەموو) سوژدەیان برد شەیتان نەبێت کە گوێی نەدایەو خۆی بەگەورە زانی وە لە بێ بڕوایان بوو');
INSERT INTO "bamoki" VALUES(2,35,'2:35','وە ووتمان: ئەی ئادەم خۆت و ژنەکەت نیشتەجێ بن لەم بەھەشتەدا وە لە ھەموو خواردەمەنیەکی بخۆن بەتێرو تەسەلی لەھەر شوێنێک کەحەز دەکەن وە نزیکی ئەم درەختە مەکەون تا لە ستەمکاران نەژمێردرێن تا نەبنە ستەمکار');
INSERT INTO "bamoki" VALUES(2,36,'2:36','ئەمجا شەیتان ھەڵی خەڵەتاندن لە بەھەشتدا وەلەو خۆشگوزەرانیەی کە تێیدابوون دەری پەڕاندن وە ووتمان: (بە ئادەم وحەواو شەیتان) بچنە خوارەوە (بۆ سەر زەوی) ھەندێکتان دوژمن ئەبن لەگەڵ ھەندێکتاندا وە بۆتان ھەیە جێگیربن لەسەر زەوی وە بەھرەمەند بن لە (ئارەزووی دونیا) تاماوەیەک');
INSERT INTO "bamoki" VALUES(2,37,'2:37','ئەمجا ئادەم چەند ووتەیەکی (پاڕانەوەی ) لە پەروەردگارییەوە وەرگرت (ئەویش) لێی گیراکرد بەڕاستی ھەر ئەو تەوبەگیراکەری میھرەبانە');
INSERT INTO "bamoki" VALUES(2,38,'2:38','ووتمان: ھەموو بچنە خوارەوە لە بەھەشت ئەمجا ئەگەر لەمنەوە ڕێنمونی یەکتان بۆ ھات ئەو کەسەی دوای ڕێنموونیی من کەوت نە ترسیان لەسەرەو نەخەم دەخۆن');
INSERT INTO "bamoki" VALUES(2,39,'2:39','وە ئەوانەی کەبێ بڕوابوون و نیشانە و ئایەتەکانی ئێمەیان بەدرۆ زانی ئەوانە ھاوەڵ و ھاودەمی ئاگرن تیایدا ئەمێننەوە بەھەتا ھەتایی وھەمیشەیی');
INSERT INTO "bamoki" VALUES(2,40,'2:40','ئەی بەنی ئیسرائییل (نەوەکانی یەعقوب) بیری ئەو چاکانە بکەنەوە کە ڕشتوومن بەسەرتاندا وە پەیمانی من ببەنەسەر تا منیش پەیمانی ئێوە ببەمەسەر وەھەر لە من بترسن');
INSERT INTO "bamoki" VALUES(2,41,'2:41','ھەروەھا بڕوا بھێنن بەو قورئانەی کەناردوومەتە خوارەوە کە پشتیوانی پەیامەکەی لای ئێوەیە (تەورات وئینجیلی دەستکاری نەکراو) وە ئێوە مەبن بەیەکەمین بێ بڕوا بەو قورئانە وە نیشانە و ئایەتەکانی من مەگۆڕنەوە بەشتێکی کەم نرخ وەھەر لە (بێ فەرمانی) من خۆتان بپارێزن');
INSERT INTO "bamoki" VALUES(2,42,'2:42','ڕاست و ڕەوا تێکەڵ مەکەن بەناڕەواو درۆ حەق و ڕاستی مەشارنەوە، لەکاتێکدا کە خۆتان ئەمە دەزانن');
INSERT INTO "bamoki" VALUES(2,43,'2:43','نوێژ بکەن و زەکات بدەن وەلەگەڵ کڕنوش بەراندا کڕنوش بەرن (بۆ خودا)');
INSERT INTO "bamoki" VALUES(2,44,'2:44','ئایا فەرمان بەسەر خەڵکیدا دەدەن بەچاکە؟ وە خۆتان فەرامۆش دەکەن (لەگەڵ ئەمەشدا) کەئێوە پەڕاوەکە (تەورات) دەخوێننەوە دەی ئایا ژیرنابن و تێ ناگەن؟');
INSERT INTO "bamoki" VALUES(2,45,'2:45','پەنا ببەنە بەرئارام گرتن ونوێژکردن (بۆ بە مەبەستەکانتان) وە بێگومان نوێژ (ئەرکێکی) گەورەو گرانە مەگەر لای ئەوانە نەبێ کە لەخوا ئەترسن و (خۆیان بەزل نازانن)');
INSERT INTO "bamoki" VALUES(2,46,'2:46','ئەوانەی کە دڵنیان بە خزمەت پەروەردگاریان دەگەن وە ئەوان دڵنیان بۆ لای ئەو دەگەڕێنەوە');
INSERT INTO "bamoki" VALUES(2,47,'2:47','ئەی بەنی ئیسرائیل (نەوەکانی یەعقوب) یادی ئەو بەخششانە بکەنەوە کە ڕشتوومە بەسەرتاندا وە من (کاتی خۆی ) ڕێزی ئێوەم دا بەسەر ھەمواندا');
INSERT INTO "bamoki" VALUES(2,48,'2:48','وە خۆتان بپارێزن لەڕۆژێک کە کەس ھیچ سوودێک بەکەسی تر ناگەیەنێت وە ھیج تکایەکی بۆ گیرا ناکرێت وە بارمتەی لێ وەرناگیرێت وە ئەمانە یارمەتی نادرێن');
INSERT INTO "bamoki" VALUES(2,49,'2:49','(بیربکەنەوە) کاتێک کەڕزگارمان کردن لەدەست و پێوەندی فیرعەون کەسزای سەختیان پێ ئەچەشتن کوڕەکانتانیان سەر دەبڕین وە کچەکانتانیان دەھێشتنەوە (بۆ کارەکەری) وە لەمەتاندا (لەم سزا سەختەی بەسەرتاندا درا) تاقی کردنەوەیەکی گەورە بوو لەپەروەردگارتانەوە بۆتان');
INSERT INTO "bamoki" VALUES(2,50,'2:50','(بیربکەنەوە) کاتێک کە دەریامان بۆ لەت کردن و ڕزگارمان کردن وە دەست وپێوەندی فیرعەونمان خنکاند و نوقمی (دەریامان) کردن لەکاتێکدا ئێوە تەماشاتان دەکردن');
INSERT INTO "bamoki" VALUES(2,51,'2:51','(بیربکەنەوە) کاتێک کەبەڵێنی چل شەومان دا بەمووسا لەدوای ئەو (ڕۆشتنی مووسا بۆ کێوی گور) گوێرەکەکەتان کرد بە (خوای خۆتان وپەرستتان) لەکاتێکدا ئێوە ستەمکار بوون');
INSERT INTO "bamoki" VALUES(2,52,'2:52','ئەمجا دوای ئەو (گوێرەکە پەرستیەتان) لێتان خۆش بووین تاسوپاسی (خوا) بکەن');
INSERT INTO "bamoki" VALUES(2,53,'2:53','بیربکەنەوە کاتێک تەورات وجیاکەرەوە (لە نێوان دروست ونادروست) مان دا بەمووسابۆ ئەوەی ڕێنمونیی پێ بکەن');
INSERT INTO "bamoki" VALUES(2,54,'2:54','بیربکەنەوە کاتێک کە مووسا بەگەلەکەی وت ئەی گەلەکەم بەڕاستی ئێوە ستەمتان کرد لەخۆتان بەگوێرەکە پەرستیەکەتان کەواتە بگەڕێنەوە بەپەشیمانی بۆ لای بەدی ھێنەرتان و خۆتان بکوژن (بێ تاوانتان تاوان بارتان بکوژێت) ئەمەتان باشترە بۆخۆتان لای بەدی ھێنەرتان ئەمجا (خواش) پەشیمانیەکەی لێ گیرا کرد بەڕاستی ھەر خوایە گیراکەری پەشیمانی و میھرەبانە');
INSERT INTO "bamoki" VALUES(2,55,'2:55','(بیربکەنەوە) کاتێ ووتتان ئەی مووسا بڕوات پێ ناھێنین تاخوانەبینین بەئاشکرا لەپڕ ھەورە تریشقەیەک لێ ی دان لەکاتێکدا ئێوە تەماشاتان دەکرد');
INSERT INTO "bamoki" VALUES(2,56,'2:56','لەپاشان زیندوومان کردنەوە لەدوای مردنتان بۆئەوەی سوپاسی (خوا) بکەن');
INSERT INTO "bamoki" VALUES(2,57,'2:57','وەبەھەور سێبەرمان کرد بەسەرتانەوە، وە گەزۆ و باڵندەی شێلاقەمان بەسەردا ڕشتن وبۆمان ناردن (وە پێمان ووتن) بخۆن لەو خواردنە پاکانەی کەکردمان بەڕۆزیتان (ئەوانەی ناسوپاسی یان کرد) ستەمیان لە ئێمە نەکرد بەڵکو ستەمیان لە خۆیان کرد');
INSERT INTO "bamoki" VALUES(2,58,'2:58','(بیربکەنەوە) کاتێک کەووتمان: بچنە ئەم شارە (کە قودسە) ئەمجا لەوێ (ھەرچی ئارەزوو دەکەن) بخۆن بەتێروتەسەلی لەھەر لایەک کەحەز دەکەن وە لەدەرگاوە بچنە ژوورەوە بە ملکەچی (بەکڕنوشەوە) وە بڵێن (خوایە) لەگوناھەکانمان خۆش ببە ئەوسا لەگوناھەکانتان خۆش دەبین وەلەئایندەدا پاداشتی چاکەکاران زۆرتر دەکەین');
INSERT INTO "bamoki" VALUES(2,59,'2:59','کەچی ئەوانەی ستەمکار بوون (ئەوەی پێیان ووترا بوو) گۆڕییان بەقسەیەک کەپێیان نەووترا بوو ئیتر ئێمەش رشتمان بەسەر ئەوانەی کەستەمیان کرد سزایەک لەئاسمانەوە بەھۆی ئەونافەرمانیەی کەکردیان');
INSERT INTO "bamoki" VALUES(2,60,'2:60','(بیربکەنەوە) کاتێک کە مووسا داوای باران بارینی کرد بۆگەلەکەی ئێمەش پێمان ووت: گۆچانەکەت بدە بەو (تاشە) بەردەدا (کەلێی دا) لەپڕ دوانزە کانیی لێ ھەڵقوڵا بێگومان ھەموو تیرەیەک چاوگەی (ئاوە خۆرەی) خۆی ناسی (ئەمجا ووتمان) بخۆن وبخۆنەوە لەڕۆزی خواوە بڵاو مەبنەوە لەزەویدا بەخراپەکردن');
INSERT INTO "bamoki" VALUES(2,61,'2:61','(بیربکەنەوە) کاتێک کەووتتان: ئەی مووسا ھەرگیز خۆمان راناگرین لەسەر یەک جۆر خۆراک کەواتە داوامان بۆبکە لەپەروەردگارت بۆمان دەربھێنێ لەوەی کەزەوی دەیڕوێنێ لەسەوزەوات و ترۆزی و خەیارو سیر ونیسک وپیاز (مووسا) فەرمووی: ئایا ئێوە شتی چاک دەگۆڕنەوە بە خراپ (واتە گەزۆ و گۆشتی باڵندە دەگۆڕنەوە بە سیر وپیاز) دابەزن بۆشارێک (لەم شارانەی فەلەستین) ئەوسا بەڕاستی چیتان داوا کردووە دەستتان دەکەوێت وە درابەسەریاندا ڕیسوایی و بێ نەوایی ( ھەژاری) وە خەشمی خوایان بۆخۆیان ھێنایەوە ئەمە لەبەر ئەوەبوو کەبێ بڕوابوون بەنیشانەو فەرمانەکانی خوا وە پێغەمبەرانیان دەکوشت بەناڕەوا ئەمە بەھۆی ئەوەوە بوو کەسەرپێچی و دەست درێژی یان دەکرد');
INSERT INTO "bamoki" VALUES(2,62,'2:62','بەڕاستی ئەوانەی ئیماندارن (موسوڵمانن) وەئەوانەی جوولەکەن وەگاورو ئەستێرە یان فریشتە پەرستەکان ھەرکامێک لەمانە بڕوای ھێنا بەخواو ڕۆژی دوایی وەکردەوەی چاکی کرد ئەوە پاداشتیان بۆھەیە لەلای پەروەردگاریانەوە نەترسیان لەسەرە و نەخەفەت دەخۆن');
INSERT INTO "bamoki" VALUES(2,63,'2:63','وە (بیربکەنەوە) کاتێک پەیمانمان لێ وەرگرتن وە (کێوی) طورمان بەرز کردەوە بەسەر سەرتانەوە (وە پێمان وتن) ئەوەی پێمان داون (لەتەورات) باش کاری پێبکەن وەبیرکەنەوە لەوەی کەتێیدایە بەڵکو خۆتان بپارێزن ولەخوا بترسن');
INSERT INTO "bamoki" VALUES(2,64,'2:64','پاشان پشتتان ھەڵکرد دوای پەیمان وەرگرتنەکە ئەمجا ئەگەر بەخشش و میھرەبانی خوا نەبوایە بەسەر تانەوە ئەوە ئێوە لەزەرەرمەندان دەبوون');
INSERT INTO "bamoki" VALUES(2,65,'2:65','سوێند بەخوا بەڕاستی ئێوە (دەنگ وباسی) ئەوانەتان زانیبوو لەخۆتان کەدەست درێژیان کرد لە ڕۆژی شەممەدا ئەمجا پێمان وتن ببن بەمەیمونی دوور خراوە لەبەزەیی خوا');
INSERT INTO "bamoki" VALUES(2,66,'2:66','ئەمجا ئەم سزایەمان کردە پەند بۆ خەڵکانی ئەو سەردەمەو ئەوانەی دوای ئەوان وەبەئامۆژگاری بۆ پارێزکاران');
INSERT INTO "bamoki" VALUES(2,67,'2:67','وە (بیربکەنەوە) کاتێک مووسا بەگەلەکەی ووت بێگومان خوا فەرمانتان پێ دەدات کە مانگایەک سەرببڕن وتیان: ئایا گاڵتەمان پێ دەکەیت؟ ووتی: پەنا ئەگرم بەخوا کەلە نەفامان بم');
INSERT INTO "bamoki" VALUES(2,68,'2:68','ووتیان: داوامان بۆ بکە لەپەروەردگارت پێمان بڵێ ئەو مانگایە کامەیە وچۆنە؟ ووتی: بێگومان خوا دەفەرموێ:مانگایەک بێت نەزۆر پیرو نەزۆر گەنج مام ناوەندی بێ لەنێوان ئەم دوانەدا زوو بیکەن ئەوەی فەرمانتان پێدراوە');
INSERT INTO "bamoki" VALUES(2,69,'2:69','ووتیان: داوامان بۆبکە لەپەروەردگارت پێمان بڵێ ڕەنگی مانگاکە چۆن بێ؟ ووتی: بەڕاستی خوا دەفەرموێ:مانگایەکی زەردی تێربێ (زەردێکی وا) دڵی تەماشاکاران خۆش بکات');
INSERT INTO "bamoki" VALUES(2,70,'2:70','ووتیان: داوامان بۆ بکە لەپەروەردگارت تەواو بۆمان ڕوون بکاتەوە چۆن بێ ئەومانگایە؟ بەڕاستی مانگاکەمان لێ تێک چووە وە بەڕاستی ئێمە ئەگەر خوا بیەوێ ڕێنمونی وەردەگرین');
INSERT INTO "bamoki" VALUES(2,71,'2:71','ووتی: خوا ئەفەرموێ بەڕاستی مانگایەک بێ ڕانەھێنرابێ نە بە کێڵانی زەوی وە نە بەئاودێری کشتوکاڵ بێ نەنگ وبێ پەڵەبێ ووتیان: ئائێستا ھەواڵی راستت ھێنا ئەمجا سەریان بڕی وخەریک بوو ئەو (سەربڕینە) نەکەن');
INSERT INTO "bamoki" VALUES(2,72,'2:72','(وە بیربکەنەوە) کاتێک کە یەکێکتان کوشت وەھەموو لەخۆتانتان دوور دەخستەوەو ئاژاوەتان بوو وە خواش ئاشکرای کرد ئەوەی ئەتان شاردەوە');
INSERT INTO "bamoki" VALUES(2,73,'2:73','ئەمجا ووتمان: بەپارچەیەکی (مانگا سەربڕاوەکە) لەکەسە کوژراوەکە بدەن ئا (بەو شێوەیە) خوا مردووەکان زیندوو دەکاتەوە وەنیشانەکانی خۆیتان پیشان دەدات بۆئەوەی ژیربن وتێ بگەن');
INSERT INTO "bamoki" VALUES(2,74,'2:74','لە پاشان دڵتان رەق بوو دوای ئەم (ھەموو) موعجیزانە دڵتان وەک بەردە بەڵکو زۆر رەقترە چونکە بەڕاستی بەردی وا ھەیە لەچەند لاوە جۆگە ئاوی لێ ھەڵ دەقوڵێ وە بێگومان ھی وایان ھەیە کەلەت لەت دەبێ و ئاوی لێ دێتەدەرێ وە بەڕاستی ھی وایان ھەیە لەترسی خوا (لەلوتکەی شاخەکان) بەرئەبێتەوە وە خوا بێ ئاگا نیە لەوەی کەدەیکەن');
INSERT INTO "bamoki" VALUES(2,75,'2:75','جا ئایا بەتەمان (ئەی موسوڵمانینە) کە (جوولەکە) بڕوا بێنن بەئایینەکەتان لەکاتێکدا دەستەیەک لەوان فەرموودەی خوایان ئەبیست (لەتەورات) کەچی لەپاش تێگەیشتنی دەست کاریان دەکرد وە ئەوان دەشیان زانی (کە کارێکی ناڕەوایە)');
INSERT INTO "bamoki" VALUES(2,76,'2:76','(جوولەکەکان) کاتێک تووشی موسڵمانەکان ببونایە دەیان وت: ئێمەش بڕوامان ھێناوە (بە پێغەمبەرەکەتان) بەڵام کاتێک لەپەنایەکەوە ھەندێکیان بەھەندێکیان بگەیشتنایە دەیان وت:ئەوە ئێوە ئەو شتانە بەموسوڵمانەکان دەڵێن کە خوا بۆئێوەی باسکردووە (لە تەوراتدا)؟ تائەو تەوراتە بکەنە بەڵگە بەسەرتانەوە لای پەروەردگارتان (لەڕۆژی دواییدا) جا ئایا ژیرنابن و تێ ناگەن؟');
INSERT INTO "bamoki" VALUES(2,77,'2:77','ئایا ئەوان نازانن کەبەڕاستی خوا ئاگادارە بە ئەوەی کەپەنھانی ئەکەن و ئەوەی ئاشکرای ئەکەن');
INSERT INTO "bamoki" VALUES(2,78,'2:78','وە ھەندێکیان نەخوێندەوارن لەپەڕاوەکە (تەورات) نازانن، تەنھا ئاوات و ھیوای ھیچ وپووچ نەبێت وە ئەوان ھەر دوای گومان دەکەون');
INSERT INTO "bamoki" VALUES(2,79,'2:79','ئەمجا ھاوارو تیاچوون بۆ ئەوانەی کەتەوراتە ( تێکدراوە) کە بەدەستی خۆیان دەنوسنەوە پاشان دەڵێن: ئەم (نامەیە) لە خواوەیە تاشتێکی کەمی پێ بکڕن ساھاوارو تیاچوون بۆ ئەوان لەوە کەدەستی ئەوان نوسیوێتیەوە وە ھاوار بۆئەوان لەوەی کە ئەنجامی ئەدەن');
INSERT INTO "bamoki" VALUES(2,80,'2:80','وە ( جوولەکەکان) وتیان: ئاگری دۆزەخمان لێ ناکەوێت مەگەر چەند ڕۆژێکی ژمێرراو نەبێت بڵێ: ئایا پەیمانتان لەخواوە وەرگرتووە؟ (بۆ ئەمەی کە دەیڵێن) تا خواش پێچەوانەی پەیمانی خۆی نەکات؟ یان شتێکی وا ھەڵدەبەستن بەدەم خـوداوە کەنایزانن');
INSERT INTO "bamoki" VALUES(2,81,'2:81','نەخێر وانیە ھەرکەسێک خراپەیەک بکات وە تاوانەکەی خۆی لەھەموو لایەکەوە دەوری دابێت ودایگرێتەوە ئا ئەوانە ھاوەڵی ئاگرن تیایدا دەمێننەوە بە ھەمیشەیی');
INSERT INTO "bamoki" VALUES(2,82,'2:82','وە ئەوکەسانەی کەبڕوایان ھێناو کردەوەی باشیان کرد ئا ئەوانە ھاوەڵی بەھەشتن تیایدا دەمێننەوە بەھەمیشەیی');
INSERT INTO "bamoki" VALUES(2,83,'2:83','(وە بیربکەنەوە) کاتێک کەپەیمانمان لەجوولەکە وەرگرت کە ھیچ شتێک نەپەرستن جگە لە خوای گەورە وە چاکە بکەن لەگەڵ دایک و باوک وە لەگەڵ خزمان وە لەگەڵ ھەتیوان و ھەژاراندا وە قسەی شیاو بکەن لەگەڵ خەڵکیدا وە نوێژ بکەن وە زەکات بدەن ئەمجا پشتتان ھەڵکرد کەمێکتان نەبێت وە ئێوە ھەر بەرھەڵستکار و (پشت ھەڵکەرن)');
INSERT INTO "bamoki" VALUES(2,84,'2:84','وە (بیربکەنەوە) کاتێک پەیمانمان لێ وەرگرتن کە خوێنی یەکتری نەڕێژن وە خۆتان دەربەدەر نەکەن لەووڵاتتان پاشان بڕیارتان دا (لەسەر ئەم پەیمانە) وە ئێوە (خۆتان) شایەتی لەسەر ئەدەن');
INSERT INTO "bamoki" VALUES(2,85,'2:85','ئەمجا ئێوە ھەرئەوانەن کەیەکتری دەکوژن وە ھەندێک لەخۆتان دەربەدەر دەکەن لە ووڵاتیان پشتیوانی (بێگانە) دەکەن دژی ئەوان بە بەدی و دەست درێژی وە ئەگەر دیلتان بۆ بێنن (لەخۆتان ئەیانکڕنەوە و) فدیەیان بۆ دەدەن لەگەڵ ئەمەش دا کەدەربەدەر کردنیان قەدەغە کراوە لێتان ( بۆچی کار بەوەیان دەکەن و بەمەیان ناکەن) جا ئایا بڕوا دەھێنن بەھەندێک لەتەورات و بێ بڕوا دەبن بەھەندێکی تری جاتۆڵەی ئەوکەسانە کەوا بکەن لەئێوە ھەر ڕیسوایی و بەدناویە لەژیانی دونیادا وە لەڕۆژی دوایشدا دەگەڕێنرێنەوە بۆناو سەختترین سزا وە خوا بێ ئاگا نیە لەوەی کەدەیکەن');
INSERT INTO "bamoki" VALUES(2,86,'2:86','ئەمانە ئەوانەن کە ژیانی جیھانیان کڕی بە دواڕۆژ ئیتر نە سزایان لەسەر سووک دەکرێت وە نە یارمەتی دەدرێن و (نەپشتگیریی دەکرێن)');
INSERT INTO "bamoki" VALUES(2,87,'2:87','سوێند بەخوا بەڕاستی ئێمە تەوراتمان دا بە مووسا وە دوا بەدوای ئەو پێغەمبەرانمان نارد وە چەند نیشانەیەکی ڕۆشنمان دا بە عیسای کوڕی مەریەم وە پشتیوانیمان کرد بە جیبرەیل جا ھەرکاتێ پێغەمبەرێکتان بۆ بھاتایە بە جۆرێک کە بەدڵی خۆتان نەبوایە لووت بەرز بوون و خۆتان بەگەورە گرتووە جا دەستەیەکتان (لە پێغەمبەران) بە درۆ زانیوە وە دەستەیەکتان کوشتوون');
INSERT INTO "bamoki" VALUES(2,88,'2:88','وە (جوولەکەکان) وتیان (بە پێغەمبەرصلى الله عليه وسلم) دڵمان داپۆشراوە ( قوفڵدراوە و قسەی تۆی ناگاتێ) (نەخێر وانیە) بەڵکو خوا نەفرەتی لێکردن بەھۆی بێ بڕواییانەوە جا کەمیان ھەیە بڕوا بھێنن');
INSERT INTO "bamoki" VALUES(2,89,'2:89','کاتێک کەنامەیەکیان بۆھات (کە قورئانە) لەلایەن خواوە کە پەسەندکەری تەوراتەکەی ئەوانە (پێش ئەوەی دەستکاری بکەن و بیگۆڕن) وە ئەوان (جوولەکەکان) لەوەوپێش داوای زاڵ بوونیان ئەکرد لەخوا بەسەر ئەوانەی کە بێ بڕوابوون کەچی کاتێک ئەو (پێغەمبەره) ی دەیان ناسی بۆیان ھات بێ بڕوابوون پێی کەواتە نەفرەتی خوا لەبێ بڕوایان بێت');
INSERT INTO "bamoki" VALUES(2,90,'2:90','شتێکی زۆر خراپ بوو کە خۆیانیان پێ فرۆشت (ئەو خراپەش) بێ بڕواییان بوو بەو قورئانەی کەخوا ناردویەتیە خوارەوە بەھۆی حەسودیانەوە بوو بەوەی کەخودا بڕێژێ لەبەخششی خۆی بەسەر ھەرکەسێکدا کەخۆی بیەوێ لەبەندەکانی (کە موحەممەدە صلى الله عليه وسلم) ئەمجا تووشی خەشم و ڕقی یەک لەدوای یەکی خوا بوون وە بۆ بێ بڕوایان ھەیە سزای ڕیسواکەر');
INSERT INTO "bamoki" VALUES(2,91,'2:91','کاتێک پێ یان بووترایە: بڕوا بھێنن بەوەی کەخوا ناردوویە تیە خوارەوە (کە قورئانە) ئەیانووت: ئێمە بڕوا بەوە دەھێنین کەبۆ خۆمان نێردراوەتە خوارەوە, وە بێ بڕوا ئەبوون بە ئەوانەی کە دوای (تەورات) نێراوە (وەک قورئان) لەکاتێکدا کە ئەو قورئانە ڕاستە وپشتیوانی تەوراتە ( بێ دەستکاری) یەکەی ئەوانەو بەڕاستی دەزانێت بڵێ :بۆچی لەمەوپێش پێغەمبەرانی خواتان دەکوشت ئەگەر بڕوادار بوون');
INSERT INTO "bamoki" VALUES(2,92,'2:92','سوێند بەخوا بەڕاستی مووساتان بۆھات بەچەند نیشانەیەکی ڕۆشنەوە کەچی لە دوای ئەو گوێرەکەتان پەرست بێگومان ئێوە ستەمکارن');
INSERT INTO "bamoki" VALUES(2,93,'2:93','(وەبیربکەنەوە) کاتێک کەپەیمانمان لێ وەرگرتن وە کێوی گورمان بەرز کردەوە بەسەرتاندا (وە ووتمان) ئەوەی پێمانداوون (تەورات) باش کاری پێ بکەن (وتووند دەستی پێوە بگرن) وە گوێ بگرن (بەزمان) وتیان : بیستمان و (بەکردەوە) بێ فەرمانیمان کردوە بەھۆی بی بڕوایی یانەوە دڵیان ئاو درابوو بەخۆشەویستی گوێرەکە کە بڵێ: بڕواکەتان چ فەرمانێکی بەدتان پێ دەدات ئەگەر ئێوە بڕوادارن');
INSERT INTO "bamoki" VALUES(2,94,'2:94','(ئەی موحەممەد صلى الله عليه وسلم) بڵێ: ئەگەر ماڵی دوایی (بەھەشت) بۆ ئێوەیە لای خوا (وەک خۆتان دەڵێن) بەتایبەتی بۆخۆتان بێت بەبێ ئەوەی کەس ببێ بە ھاوبەشتان کەواتە داوای مردن بکەن ئەگەر ڕاستگۆن');
INSERT INTO "bamoki" VALUES(2,95,'2:95','وە ھەرگیز داوای مردن ناکەن بەھۆی ئەو (گوناھانە) کەبەدەستی خۆیان پێشیان خستووە وە خوا ئاگادارە بە ستەمکاران');
INSERT INTO "bamoki" VALUES(2,96,'2:96','سوێندبێت ئەو (جوولەکانە) دەبینیت لەھەموو کەس زیاتر حەز لەژیان دەکەن وە لەوانەش کە ھاوبەش بۆ خوا پەیدادەکەن (لەبەرئەوە) ھەریەکێکیان ئاوات دەخوازێ کە ھەزار ساڵ بژیایە وە ئەو تەمەنە (کەبەسەری دەبات) رزگاری ناکات لەسزا وە خوا بینایە بەوەی کە دەیکەن');
INSERT INTO "bamoki" VALUES(2,97,'2:97','(ئەی موحەممەد صلى الله عليه وسلم) بڵێ: ھەر کەسێک دوژمنی جیبریل بێت (ئەوە دوژمنی خوایە) چونکە ئەو بە فەرمانی خوا قورئانی ھێناوەتە خوارەوە بۆ سەر دڵی تۆ پشتیوانی نامەکانی پێش خۆی دەکات و بەڕاستیان دەزانێت وە ڕێنمونی کارو مژدە دەرە بۆ بڕواداران');
INSERT INTO "bamoki" VALUES(2,98,'2:98','ھەر کەسێک دوژمنی خواو فریشتەکانی خوا و پێغەمبەرانی خوا و جیبرەیل و میکایل بێت (ئەوە بێ بڕوایە) وە بێگومان خواش دوژمنی بێ بڕوایانە');
INSERT INTO "bamoki" VALUES(2,99,'2:99','سوێند بەخوا بێگومان بۆمان ناردویت چەند نیشانەیەکی رون و ئاشکرا (کە قورئانە) وە کەس بێ بڕوا نابێت پێیان، مەگەر بەدکاران نەبێت');
INSERT INTO "bamoki" VALUES(2,100,'2:100','ئایا (بێ بڕوا ئەبن بەئایەتەکانی ئێمە) ھەر جارێک پەیمانێک ببەستن (لەگەڵ پێغەمبەر صلى الله عليه وسلم دا) دەستەیەک لەوانە پەیمانەکەیان ھەڵوەشاندۆتەوە (نەک ھەر ئەوەندە ) بەڵکو زۆربەیان ھەر بڕوا ناھێنن');
INSERT INTO "bamoki" VALUES(2,101,'2:101','کاتێک کە پێغەمبەرێکیان بۆ ھات لەلایەن خواوە بەپشتیوانی تەوراتە (دەستکاری نەکراوە) کەیان دەستەیەک لەوانەی کە خاوەن نامەبوون نامەکەی خوایان فڕێ دایەپشت خۆیان و(پشت گوێیان خست) وەک نەزانن (کەئەوە نامەی خودایە)');
INSERT INTO "bamoki" VALUES(2,102,'2:102','(جوولەکەکان) دوای ئەو (شتانە) کەوتن کەشەیتانەکان دەیان خوێندەوە (بۆ خەڵکی) لەسەردەمی پاشایەتی سولەیمان دا،وە سولەیمان بێ بڕوا نەبوو بەڵام شەیتانەکان بێ بڕوا بوون خەڵکیان فێری جادو دەکرد ھەروەھا ئەوەی نێردراوەتە خوارەوە لە بابل بۆ سەر ھارووت و مارووت (دوو فریشتە بوون) وە (ئەمانە) کەسیان فێر نەدەکرد تا پێیان نەوتایە ئێمە ھەر تاقیکەرەوەی (ئادەمیزاد) ین کەواتە بێ بڕوامەبە ئەمجا فێری جادوی وا دەبوون لێیانەوە کە ژن و مێردی پێ لەیەک جیا بکەنەوە ئەوان بەم جادوە ناتوانن زیان بەکەس بگەیەنن مەگەر بەویستی خوا نەبێت وە فێری شتێک دەبن کە زیانیان پێ دەگەیەنێ نەک قازانج بێگومان زانیان ھەرکەسێ جادو بکڕێ (وفێری ببێت) (بەفەرموودەی تەورات) لەڕۆژی دواییدا بێ بەش دەبێ بەڕاستی زۆر خراپە ئەو شتەی کە (بڕوای) خۆیانیان پێ فرۆشت ئەگەر بیانزانیایە');
INSERT INTO "bamoki" VALUES(2,103,'2:103','ئەگەر بەڕاستی ئەوان بڕوایان بھێنایە و پارێزکار بونایە ئەوا پاداشتێکی چاکتر لەلایەن خواوە بۆ ئەوان دەبوو ئەگەر بیانزانیایە');
INSERT INTO "bamoki" VALUES(2,104,'2:104','ئەی ئەوانەی کە بڕواتان ھێناوە مەڵێن ووشەی جوێن و بێ رێزیە بەلای جولەکەوە,وە بڵێن پەلەمان لێ مەکە و چاوەڕوانیمان لەسەر بکە و ببیستن وە بۆ بێ بڕوایان ھەیە سزای سەخت');
INSERT INTO "bamoki" VALUES(2,105,'2:105','حەزناکەن ئەوانەی کەبێ بڕوان لە خاوەن نامە و ھاوبەش پەیداکەرەکان کە ھیچ شتێکی باشتان لەخواوە بۆ بنێردرێتە خوارەوە بەڵام خواش بەخششی خۆی بۆ کەسێک کەبیەوێ تەرخان دەکات و خوا خاوەنی چاکەی گەورەیە');
INSERT INTO "bamoki" VALUES(2,106,'2:106','ئەگەر ئایەتێک (لە قورئان) بسڕینەوە یان فەرامۆشی بکەین و لەبیری ببەینەوە ئەوا یەکێکی تری باشتر لە ئەو، یان وەک ئەو دەھێنین ئایا نازانیت خوا بەسەر ھەموو شتێکدا بەدەسەڵاتە');
INSERT INTO "bamoki" VALUES(2,107,'2:107','ئایا نازانیت کە موڵکی ئاسمانەکان و زەوی ھەر بۆخوایە؟ وە ھیچ پشتیوان و یاریدە دەرێکتان نیە، خوا نەبێت');
INSERT INTO "bamoki" VALUES(2,108,'2:108','یا ئێوەش دەتانەوێ داوا ی (شتی نەشیاو) بکەن لەپێغەمبەرەکەتان؟ وەک لەمەوپێش داوا کرا لەمووسا (علیە السلام) ھەر کەسێک بڕوا بگۆڕێتەوە بە بێ بڕوایی ئەوە بێگومان ڕێی ڕاستی ونکردووە');
INSERT INTO "bamoki" VALUES(2,109,'2:109','زۆر لەخاوەن نامەکان ئاواتە خوازن کەبتانکەنەوە بە بێ بڕوا، دوای بڕوا ھێنانتان لەبەر حەسودییەک کە لەدەرونیانەوەیە (ئەمەش) دوای ئەوەی کەڕاستیەکەیان بۆ ڕون بوەتەوە بەڵام ئێوە لێیان خۆش بن و چاو پۆشیان لێ بکەن تا خوا فەرمانی خۆیتان بۆ دەنێرێت بێگومان خوا بە دەسەڵاتە بەسەر ھەموو شتێکدا');
INSERT INTO "bamoki" VALUES(2,110,'2:110','نوێژ بکەن وزەکات بدەن وەھەرچی پێشبخەن لەچاکە بۆخۆتان لای خوا دەستتان دەکەوێتەوە بێگومان خوا بینایە بەوەی کەدەیکەن');
INSERT INTO "bamoki" VALUES(2,111,'2:111','(جوولەکەو گاورەکان) وتیان: کەس ناچێتە بەھەشتەوە مەگەر یەکێک کە جوولەکە یا گاور بێت ئەمە ئاواتیانە پێیان بڵێ: بەڵگەتان بھێنن ئەگەر راست دەکەن');
INSERT INTO "bamoki" VALUES(2,112,'2:112','(نا وانییە) بەڵکو ھەر کەسێک بەڕاستی رووبکاتە خوا و لەھەمان کاتدا چاکەکار بێت ئەوە بۆی ھەیە پاداشت لەلای پەروەردگاریەوە نە ترسیان لەسەرە و نەخەمیش دەخۆن');
INSERT INTO "bamoki" VALUES(2,113,'2:113','جوولەکە ئەڵێن: گاور لەسەر ھیچ (ئاینێک ) نین وە گاوور دەڵێن: جوولەکە لەسەر ھیچ (ئایینێک) نین لەکاتێکدا ئەوان نامەکانیان دەخوێندەوە،(یەکێکیان تەورات و ئەوی تریش ئینجیل) ھەر بەم جۆرەیان دەوت، ئەوانەی کە نەزانن (لە بت پەرستەکان) ئەمجا خوا لە ڕۆژی دواییدا لە نێوانیاندا بڕیار دەدات لەوشتانەی کەتیایدا جیاواز و نـاکۆکن');
INSERT INTO "bamoki" VALUES(2,114,'2:114','کێ ستەمکارتـرە لەو کەسەی کە ڕێگری لـەمزگـەوتـەکـانی خوا دەکات لەوەی کە ناوی خوایان تێدا ببرێ وە ھەوڵ دەدات بۆ وێران کردنیان ئەوانە (کە قەدەغەی خواپەرستی دەکەن لە مزگەوتەکاندا) بۆیان نیە بچنە مزگەوتەکانەوە بە ترسەوە نەبێت لەژیانی دونیادا ریسوایی بۆ ئەوانە وە لەڕۆژی دوایشدا سزای گەورە');
INSERT INTO "bamoki" VALUES(2,115,'2:115','رۆژھەڵات و رۆژ ئاوا ھەر بۆخوایە کەواتە رووبکەنە ھەرلایەک (لە نوێژ کردندا بەفەرمانی خودا) ئەوە رووگەیە و رووتان لەخودا کردووە بێگومان خوا (بەزەیی و میھرەبانی) فراوانەو زانایە');
INSERT INTO "bamoki" VALUES(2,116,'2:116','(گاورو جوولەکە و بت پەرستەکان) وتیان: خوا منداڵی بۆخۆی داناوە ئەو (خوا) پاک و بەدوورە لەوە بەڵکو ھەرچی ھەیە لە ئاسمانەکان و زەویدا موڵکی ئەوە, ھەموو فەرمانبەردار وملکەچی ئەون');
INSERT INTO "bamoki" VALUES(2,117,'2:117','(خوا) بەدیھێنەری ئاسمانەکان و زەویە وە کاتێ کەویستی شتێ بکات ھەر ئەوەندەی پێ ئەوێ کە پێ ی بڵێ: ببە ئیتر ئەبێ');
INSERT INTO "bamoki" VALUES(2,118,'2:118','نەزانەکان وتیان: بۆچـی خوا خۆی قسەمان لەگەڵ ناکات یا نیشانەیەکمان بۆ نایەت ئەوانەی کەلەپێش ئەمانیشدا بوون ھەمـان شتیان دەوت دڵ و دەرونیان لەیەک دەچێت بێگومان نیشانەی زۆرمان باسکردوە بۆ کەسانێک بەشوێن دڵنیاییدا دەگەڕێن');
INSERT INTO "bamoki" VALUES(2,119,'2:119','بێگومان ئێمە تۆمان نارد بە ئاینێکی راست و درووستەوە بە مژدەدەر و ترسێنەر وە تۆ بەرپرسیار نیت لەیارانی دۆزەخ');
INSERT INTO "bamoki" VALUES(2,120,'2:120','ھەرگیز نە جوولەکە و نە گاور لێت ڕازی نابن ھەتا دوای ئاینیان نەکەویت (پێ یان) بڵێ: ھەر ڕێنموونیی خوایە ڕینموونی ڕاست وە ئەگەر دوای ئارەزووەکانی ئەوان بکەویت (ئەی موحەممەد صلى الله عليه وسلم) دوای ئەو زانینەی کە (لەخواوە ) بۆت ھاتووە (کە قورئانە) ھیچ سەرپەرشتیکارو یارییدەدەرێکت نابێ لەلایەن خواوە');
INSERT INTO "bamoki" VALUES(2,121,'2:121','ئەوانە کەنامەمان پێداون بەڕاستی دەیخوێننەوە ئەوانە بڕوا ئەھێنن بەڕێنمونی خوای گەورە (کە ئیسلامە) وە ئەوانەی بێ بڕوابن پێی ھەرئەوانن زەرەر مەند و خەسارۆمەند');
INSERT INTO "bamoki" VALUES(2,122,'2:122','ئەی بەنی ئیسرائیل (نەوەکانی یەعقوب) یادی ئەو بەخششانە بکەنەوە کە ڕشتوومە بەسەرتاندا وە من (کاتی خۆی ) ڕێزی ئێوەم دا بەسەر ھەمواندا');
INSERT INTO "bamoki" VALUES(2,123,'2:123','وە خۆتان بپارێزن لەڕۆژێک کە کەس لەبری کەس تۆڵەو پاداشت نادرێتەوە وەبارمتەی لێ وەرناگیرێت وە ھیچ تکایەک کەڵکی ناگرێ وەیارمەتیی نادرێن و سەرناخرێن');
INSERT INTO "bamoki" VALUES(2,124,'2:124','(بیربکەنەوە) کاتێک خوا ئیبراھیمی تاقی کردەوە بەچەند بڕیار وفەرمایشتێک کە ئەویش بەجێی ھێنان (خوا) فەرمووی: بێگومان من تۆ دەکەم بە پێشەوای خەڵکی (ئیبراھیم) وتی: لەنەوەشم ئەکەیت (بە پێشەوا)؟ فەرمووی: پێغەمبەریەتی من ناگات بەستەمکاران');
INSERT INTO "bamoki" VALUES(2,125,'2:125','(بیربکەنەوە) کاتێک کە کەعبەمان گێڕا بە پەناگەی خەڵکی و جێی ئارامی و ئاسایش (پێمان وتن) شوێنی ئەو بەردەی ئیبراھیم (علیە سلام) لە سەری ڕادەوەستا (کاتی دروست کردنی کەعبە) بکەنە جێگای نوێژ وە سپاردمان بە ئیبراھیم و ئیسماعیل کە ماڵەکەم پاک بکەنەوە بۆ ئەوانەی کە بەدەوری کەعبەدا دەسوڕێنەوە، وە بۆ ئەوانەی کە تیایدا ئەمێننەوە، ھەروەھا بۆ کڕنوش بەران و سوژدە بەران');
INSERT INTO "bamoki" VALUES(2,126,'2:126','(وە بیربکەنەوە) کاتێک کەئیبراھیم ووتی: خوایە ئەم شوێنە (کە مەککەیە) بکە بەشارێکی پڕ لە ئارامی و ئاسایش وە رۆزی دانیشتوانی بدە لـە بەروبوم و میوەھات ئەوانەیان کەبڕوایان بەخوا وبەڕۆژی دوایی ھەیە. (خوا) فەرمووی: ئەوەش بێ بڕوابێ رۆزی پێ ئەدەم بۆچەند ڕۆژێکی کەم لەپاشان ناچاری دەکەم بەسزای ئاگری دۆزەخ کە خراپترین سەرئەنجامە');
INSERT INTO "bamoki" VALUES(2,127,'2:127','(بیربکەرەوە) کاتێک کە ئیبراھیم و ئیسماعیل بناغەکانی کەعبەیان بەرز دەکردەوە (دەیانووت) خوایە ئەم کردەوەیەمان لێ گیرا بکە بەڕاستی ھەر تۆی بیسەر و زانا');
INSERT INTO "bamoki" VALUES(2,128,'2:128','خوایە ھەر دووکمان بکە بە گوێڕایەڵ وفەرمان بەرداری خۆت وە لەنەوەشمان گەلێک بکە بەفەرمانبەرداری خۆت وە رێگاکانی خوا پەرستیمان نیشان بدە وە پەشیمانیمان گیرابکە بەڕاستی ھەرتۆی گیراکەری پەشیمانی و میھرەبان');
INSERT INTO "bamoki" VALUES(2,129,'2:129','خوایە لە ناو نەوەکانماندا پێغەمبەرێک بنێرە لەخۆیان ئایەتەکانی تۆیان بەسەردا بخوێنێتەوە قورئان و داناییان فێربکات و پاکیان بکاتەوە (لە ھەموو گوناھێک) بێگومان ھەرتۆی بەتوانا و دانا');
INSERT INTO "bamoki" VALUES(2,130,'2:130','کێ ڕوو وەرئەگێڕێ لە ئایینی ئیبراھیم؟ مەگەر کەسێک کەخۆی گێل ونەفام کردبێت؟ سوێند بێ بێگومان ئیبراھیممان ھەڵبژارد لە دونیادا وە بێگومان ئەو لەڕۆژی دواییدا لەچاکەکارانە');
INSERT INTO "bamoki" VALUES(2,131,'2:131','(ھەڵی بژارد) لەکاتێکدا خوا پێی فەرموو: گوێڕایەڵ وفەرمانبەرداربە (ئیبراھیمیش) ووتی: وا خۆم سپارد بە پەروەردگاری جیھانیان');
INSERT INTO "bamoki" VALUES(2,132,'2:132','ئیبراھیم و یەعقوب ئامۆژگاری کوڕەکانی خۆیانیان کرد کە فەرمانبەرداری خوا بن (وە ھەریەکەیان پێیان وتن) ڕۆڵەکانم :خوا ئاینی یەکتاپەرستی بۆ ھەڵبژاردون کەواتە مەمرن بە موسوڵمانێتی نەبێت');
INSERT INTO "bamoki" VALUES(2,133,'2:133','(ئەی خاوەن نامەکان) ئایا ئێوە ئامادە بوون کاتێ یەعقوب لەسەرەمەرگدا بوو کاتێ بە کوڕەکانی ووت دوای من کێ دەپەرستن؟ (ئەوانیش) وتیان: خوای تۆو باو و باپیرانت دەپەرستین کە خوای ئیبراھیم و ئیسماعیل و ئیسحاقە کە خوایەکی تاک وتەنھایە وە ئێمەش فەرمانبەردار و گوێڕایەڵی ئەوین');
INSERT INTO "bamoki" VALUES(2,134,'2:134','ئەو پێغەمبەرانە دەستەیەک بوون تێپەڕیین ھەرچیان کرد بۆ خۆیانیان کردوە (ئێوەش) ھەرچی بکەن بۆخۆتانی دەکەن وە ئێوە بەرپرسیار نین لەکردەوەی ئەوان');
INSERT INTO "bamoki" VALUES(2,135,'2:135','(جوولەکە و گاوورەکان) ووتیان: (بەموسڵمانەکان) ببن بە جوولەکە یان بەگاوور تابکەونە سەر ڕێگای ڕاست بڵێ: بەڵکو پەیڕەویی ئایینی پاکی ئیبراھیم دەکەین کە لە ھاوبەش پەیداکاران نەبووە');
INSERT INTO "bamoki" VALUES(2,136,'2:136','بڵێن:(ئەی موسڵمانینە) بڕوامان ھێنا بە خواو بەو قورئانەی کەبۆخۆمان نێرراوەتە خوارەوە, وە بەو نامەیەی کە نێرراوە بۆ ئیبراھیم و ئیسماعیل و ئیسحاق و یەعقوب و کوڕەکانی یەعقوب وە بەوەی کەدراوە بە مووساو عیسا وە بەوەی کە دراوە بەھەموو پێغەمبەران لەلایەن پەروەدگاریانەوە جیاوازی ناکەین لە نێوان ھیچ کامێکیاندا وە ئێمە فەرمانبەردار وگوێڕایەڵی خواین');
INSERT INTO "bamoki" VALUES(2,137,'2:137','ئەمجا ئەگەر بڕوایان ھێنا بەو وێنەیەی کە ئێوە بڕواتان پێ ھێناوە ئەوا بێگومان ڕێی ڕاستیان دۆزیوەتەوە, وە ئەگەر پشتیان ھەڵکرد ئەوا ئەوان لە دوو بەرەکیدان وە خوا ئاگاداری تۆ ئەکات لەوان وە ھەر خوایە بیسەر و زانا');
INSERT INTO "bamoki" VALUES(2,138,'2:138','دەستبگرن بەو ئاینەی خواوە کەفیترەتی پاکی ئێوەی لەسەر خەلقکردوە بێگومان ھیچ فیترەتێکی بەوجۆرە پاک نیە (کەواتە چاک دەستی پێوە بگرن) وە ئێمەش پەرستن و عیبادەت ھەر بۆ ئەو دەکەین');
INSERT INTO "bamoki" VALUES(2,139,'2:139','بڵێ: ئایا دەمەقاڵێ و دەمەوەریمان لەگەڵ ئەکەن لەکاری خوا لە کاتێکدا ئەو پەروەردگاری ئێمە و ئێوەشە وە کردەوەی خۆمان بۆخۆمانە و کردەوەی خۆتان بۆ خۆتانە وە ئێمە تەنھا بەدڵسۆزی بەندایەتی خوا دەکەین');
INSERT INTO "bamoki" VALUES(2,140,'2:140','یاخود ئێوە ئەڵێن: ئیبراھیم و ئیسماعیل و ئیسحاق و یەعقوب وکوڕەکانی یەعقوب جوولەکە یا گاوور بوون؟ بڵێ: ئایا ئێوە زاناترن یا خوا؟ کێ ستەم کارترە لەو کەسەی کە شایەتیەکی لەخوا وەرگرتبێ و بیشارێتەوە خوا بێ ئاگا نی یە لەوەی کە ئێوە دەیکەن');
INSERT INTO "bamoki" VALUES(2,141,'2:141','ئەو پێغەمبەرانە دەستەیەک بوون تێپەڕیین ھەرچیان کرد بۆ خۆیانیان کردوە (ئێوەش) ھەرچی بکەن بۆخۆتانی دەکەن وە ئێوە بەرپرسیار نین لەکردەوەی ئەوان');
INSERT INTO "bamoki" VALUES(2,142,'2:142','ھەندێ نەزان و گێل ونەفام لەخەڵک دەڵێن: چی موسڵمانەکانی وەرگێڕا لەو ڕووگەیان کەجاران لەسەری بوون بڵێ ڕۆژھەڵات و ڕۆژئاوا ھەمووی موڵکی خوایە کێی بوێت ڕێنمونیی دەکات بۆ ڕێبازی ڕاست');
INSERT INTO "bamoki" VALUES(2,143,'2:143','ھەروەھا کردمانن بەگەلێکی میانەڕەو مامناوەندی (ھەڵبژاردە) تاببنە شاھید وچاودێر بەسەر خەڵکەوە، و (پێغەمبەریش صلى الله عليه وسلم) شاھید وچاودێر بێت بەسەر ئێوەوە ئەو ڕووگەیەی کەجاران لەسەری بوویت نەمانگێڕا (بەڕووگەو لەپاشان گۆڕیمان بەکەعبە) تەنھا بۆئەوەی دەربکەوێت کێ شوێن (پێغەمبەر صلى الله عليه وسلم) دەکەوێت (وەجیادەبێتەوە) لەوانەی کەپاش گەزدەبنەوە, وە بەڕاستی (ئەم گۆڕینە) کارێکی زۆر گەورەو گران بوو مەگەر لای ئەوانەی کەخوا ڕێنموونیی کردوون وەخوای گەورە بڕوا (و نوێژەکانی جاران) تانی بەفیڕۆ نەداوە بەڕاستی خوا لەگەڵ خەڵکیدا دڵسۆز و میھرەبانە');
INSERT INTO "bamoki" VALUES(2,144,'2:144','بەڕاستی ئێمە گەڕانی ڕووخسارت ڕوو بەئاسمان دەبینین دەی سوێند بەخوا ڕووت دەکەینە ڕووگەیەک کەپەسەندت بێ وڕازی بیت پێی ئیتر ڕووت وەرچەرخێنە بەرەو لای کەعبە (مسجد الحرام) ئێوەش ئەی موسڵمانان لەھەر لایەک بوون (لەنوێژدا) ڕووتان بکەنە ئەوێ، بەرەو (مسجد الحرام) بەڕاستی ئەوانەی پەڕاویان پێدراوە چاک ئەزانن کەئەمە شتێکی ڕاستە لە پەروەردگاریانەوە, وە خودا بێ ئاگا نیە لەوەی کەدەیکەن');
INSERT INTO "bamoki" VALUES(2,145,'2:145','سوێند بێت ئەگەر ھەرچی بەڵگەو نیشانە ھەیە بیھێنیتەوە بۆ ئەم خاوەن نامە وپەڕاوانە دوای ڕووگەی تۆ ناکەوون وە تۆش دوای ڕووگەی ئەوان ناکەویت وە ئەوان دوای ڕووگەی یەکدی ناکەون سوێند بێت ئەگەر تۆ دوای ئارەزووەکانی ئەوان بکەویت دوای ئەو زانینەی کە (بەسرووش) بۆت ھاتووە بەڕاستی ئەو کاتە تۆ لەستەمکاران دەبیت');
INSERT INTO "bamoki" VALUES(2,146,'2:146','ئەوانەی کە پەڕاومان پێ داون (پێغەمبەر صلى الله عليه وسلم) دەناسن وەک چۆن کوڕ ونەوەکانی خۆیان دەناسن بێگومان کۆمەڵێک لەوان (لەئەھلی کتاب) ئەو راستیە دەشارنەوە لەکاتێکدا ئەوان دەزانن (موحەممەد صلى الله عليه وسلم پێغەمبەرە)');
INSERT INTO "bamoki" VALUES(2,147,'2:147','راست ئەوەیە (کەبۆت ھاتووە) لەپەروەردگارتەوە کەواتە لە گومانداران مەبە');
INSERT INTO "bamoki" VALUES(2,148,'2:148','وە ھەموو گەلێک ڕووگەیەکی ھەیە کە ڕووی تێدەکات دەی دەست پێشکەری بکەن لە کردارە چاکەکاندا لەھەر کوێ ھەبن خودا ھەمووتان کۆدەکاتەوە بەڕاستی خوا بەدەسەڵاتە بەسەر ھەموو شتێکدا');
INSERT INTO "bamoki" VALUES(2,149,'2:149','لەھەر کوێ چوویتە دەرەوە (بەسەفەر) ڕووت وەربگێڕە بۆ لای کهعبەی پیرۆز وە ئەم ڕووکردنەت ڕاستەو لە پەروەردگارتەوەیە وە خودا بێ ئاگا نیە لەوەی کە دەیکەن');
INSERT INTO "bamoki" VALUES(2,150,'2:150','لەھەرکوێ چوویتە دەرێ (لەکاتی نوێژکردندا) ڕووت وەربگێڕە بۆ لای کەعبەی پیرۆ وە ئێوەش ( ئەی موسڵمانینە) لەھەر کوێ بوون ڕوو وەرگێڕن بۆلای ئەو (کەعبە) تا بیانوی خەڵکتان نەیەتەسەر مەگەر ستەم کارەکانیان جا لێیان مەترسن و لە من بترسن بۆ ئەوەی بەخششی خۆمتان بەتەواوی بەسەردا بڕێژم وە بەڵکو ڕێبازی ڕاست بگرن');
INSERT INTO "bamoki" VALUES(2,151,'2:151','وەک ڕەوانەمان کردلە ناوتاندا پێغەمبەرێک لە خۆتان کەئایەتەکانی ئێمەتان بەسەردا بخوێنێتەوە، وە دڵ ودەروونتان پاک بکاتەوە وەفێرتان کات فێری قورئان وە فێری دانایی (فەرموودەی خۆی) وە فێرتان کات ئەو شتانەی کەنەتان دەزانی');
INSERT INTO "bamoki" VALUES(2,152,'2:152','کەواتە یادم بکەن تامنیش یادتان بکەم و لەبیرتان نەکەم وە سوپاسم بکەن پێ نەزان و ناسوپاسیم مەکەن');
INSERT INTO "bamoki" VALUES(2,153,'2:153','ئەی ئەو کەسانەی کە بڕواتان ھێناوە پشت ببەستن بەخۆڕاگرتن و نوێژ (کردن) بەڕاستی خوا لەگەڵ خۆڕاگرانە');
INSERT INTO "bamoki" VALUES(2,154,'2:154','ھەرگیز مەڵێن بەو کەسانەی دەکوژرێن لەڕێگەی خوادا مردوون بەڵکو زیندوون بەڵام ئێوە ھەستی پێ ناکەن');
INSERT INTO "bamoki" VALUES(2,155,'2:155','سوێند بەخوا تاقیتان دەکەینەوە بە ھەندێک لەترس و برسێتی و کەم بوونی ماڵ و دارایی و خەڵکی (کەس وکارتان) و بەرووبووم جامژدە بدە بە خۆڕاگران');
INSERT INTO "bamoki" VALUES(2,156,'2:156','ئەوانەی کە لەگەڵ دووچاری سەختی ونەھامەتی وناخۆشیەک بوون دەڵێن ئێمە ھەر ھی خواین و ھەر بۆلای ئەویش دەگەڕێینەوە');
INSERT INTO "bamoki" VALUES(2,157,'2:157','ئا ئەوانە دروود و بەزەیی بەسەر ئەواندا دەبارێت لە لایەن پەروەردگاریانەوە، وە ئەوانە ئەو کەسانەن ھیدایەتیان وەرگرتووە');
INSERT INTO "bamoki" VALUES(2,158,'2:158','بەڕاستی صەفاو مەروە لەدروشمەکانی (دینی) خوان جا ھەر کەسێک حەجی ماڵی خوای کرد یا عومرەی کرد ھیچ خراپە و تاوانی ناگات کە ھاتوچۆ وسەعی نێوان سەفا و مەروە بکات وە ھەرکەسێک بە ئارەزووی خۆی چاکەیەکی کرد ئەوە بێگومان خودا سوپاسگوزارێکی زانایە');
INSERT INTO "bamoki" VALUES(2,159,'2:159','بێگومان ئەوانەی دەیشارنەوە ئەوەی کە ناردوومانەتە خوارەوە لە ئایەتانی ڕۆشن وڕێنمایی دوای ئەوەی کە ڕوونمان کردوەتەوە بۆ خەڵکی لە نامەی (تەورات و ئینجیل) دا ئەوانە خودا نەفرەتیان لێ دەکات وە نەفرەتکارانیش نەفرەتیان لێ دەکەن');
INSERT INTO "bamoki" VALUES(2,160,'2:160','مەگەر ئەوانەی کە پەشیمان بوبنەوە، وە کردەوەی چاکیان کردبێ و (ئەوەی شاردبویانەوە) ئاشکرایان کردبێ ئا ئەوانه پەشیمانیان لێ گیرادەکەم وە ھەر منم گیراکاری میھرەبان');
INSERT INTO "bamoki" VALUES(2,161,'2:161','بەڕاستی ئەوانەی بێباوەڕ بوون وە مردن لەکاتێکدا ئەوان بێ باوەڕ بوون ئەوانە لەسەریانە نەفرەتی خوا و فریشتەکان و خەڵکی بەتێکڕایی');
INSERT INTO "bamoki" VALUES(2,162,'2:162','تێیدا دەمێننەوە بەھەمیشەیی سزایان لەسەر سووک ناکرێت وە دواناخرێن لەسزا');
INSERT INTO "bamoki" VALUES(2,163,'2:163','پەرستراوی ئێوە پەرستراوێکی تاک و تەنھایە ھیچ پەرستراوێکی بە ھەق و ڕاست نیە بەتەنھا ئەو نەبێت کە بەخشندە و میھرەبانە');
INSERT INTO "bamoki" VALUES(2,164,'2:164','بەڕاستی ھەیە لە دروست کردنی ئاسمانەکان وزەوی و جیاوازی شەو وڕۆژ (بە کورتی و درێژی) و ئەو کەشتیانەی کەدێن ودەچن لەناو دەریادا بەوشتانە کەقازانج دەگەیەنن بەخەڵکی وە ئەوەی کە خوا لەئاسمانەوە باراندی لە ئاو (باران) پاشان زەوی پێ زیندوو کردەوە (پێ بووژاندەوە) پاش مردنی وە بڵاوی کردەوە تێیدا لەھەموو جۆرە زیندەوەرێک وە گێڕانی بایەکان (بە ھەموولایەکدا) وە ئەو ھەورانەی تەرخان کراون لەناوەندی ئاسمان و زەویدا چەند بەڵگەن بۆ کەسانێک کە تێبگەن');
INSERT INTO "bamoki" VALUES(2,165,'2:165','وە کەسانی واھەیە لە خەڵکی کەھاوبەشی زۆر بۆ خوا پەیدا دەکەن خۆشیان دەوێن وەک خۆشەویستی (ئیمانداران بۆ) خوا بەڵام ئەوانەی بڕوایان ھێناوە زۆر زیاتر خوایان خۆش دەوێت ئەگەر ببینن ئەوانەی ستەمیان کردوە (بە ھاوبەشی پەیدا کردن بۆخوا) کاتێک کە چاویان دەکەوێت بەسزای (خوا دەزانن) کە ھەرچی دەسەڵات ھەیە ھەمووی بۆ خوایە وە بێگومان سزای خوا زۆر سەختە');
INSERT INTO "bamoki" VALUES(2,166,'2:166','کاتێک کە ئەو کەسانەی خەڵک شوێنیان کەوتون لە دونیادا ( واتە سەرۆکە گومڕاکارەکان ) یەخە ھەڵئەتەکێنن لەوانەی (لە دونیادا) دوایان کەوتوون وە سزای (دۆزەخیان بەچاوی خۆیان) بینی وە ھەموو پەیوەندیەکیان بچڕایەوە');
INSERT INTO "bamoki" VALUES(2,167,'2:167','وە ئەوانەی کە دوایان کەوتبوون ووتیان خۆزگە ئێمە بگەڕاینایەتەوە (بۆ دونیا) تا یەخەمان لێ ھەڵبتەکاندنایە ھەر وەک (ئەوان) چۆن یەخەیان لەئێمە ھەڵتەکاند ھەر بەو شێوەیە خوا کردەوەکانیان نیشان ئەدات ئەیکات بەداخ لەسەر دڵیان وە ئەوان دەرچوونیان نییە لە ئاگر');
INSERT INTO "bamoki" VALUES(2,168,'2:168','ئەی خەڵکینە بخۆن لەوەی کەلە زەویدایە بەحەڵاڵی و پاکی وە دوای ھەنگاوەکانی شەیتان مەکەون بەڕاستی شەیتان دوژمنێکی ئاشکراتانە');
INSERT INTO "bamoki" VALUES(2,169,'2:169','بەڕاستی (شەیتان) فەرمانتان پێ دەدات بەتاوان و خراپە لەگەڵ ھەڵبەستنی شتی وا بەدەم خواوە کە نازانن');
INSERT INTO "bamoki" VALUES(2,170,'2:170','کاتێک پێتان بووترێت شوێن ئەو (قورئانه) بکەون کە خوا ناردویەتە خوارەوە دەڵێن: نەخێر بەڵکو شوێن ئەوە دەکەوین کە باوو باپیرانمان لەسەری بوون ئەگەر چی باوو باپیرانیشیان ھیچ تێ نەگەیشتبن و گومڕا بووبن (ڕێنمایش وەرنەگرن)');
INSERT INTO "bamoki" VALUES(2,171,'2:171','وە نموونەی ئەو بێ بڕوایانە وەک یەکێک وایە کە دەنگ لە ئاژەڵێک بکات تەنیا دەنگ و ھاوارەکە نەبێ ھیچ نابیستێ ئەمانە کەڕن لاڵن، (لە ووتنی قسەی دروست) کوێرن (لە بینینی ڕێ ی ڕاست) بۆیە تێ ناگەن و ژیر نین');
INSERT INTO "bamoki" VALUES(2,172,'2:172','ئەی ئەو کەسانەی کە بڕواتان ھێناوە بخۆن لەو خواردەمەنیە پاکانەی کردومانە بەڕۆزیتان وە سوپاسی خوا بکەن ئەگەر ئێوە ھەر ئەو دەپەرستن');
INSERT INTO "bamoki" VALUES(2,173,'2:173','ئەوەی (کە خوا) قەدەغەی کردووە لێتان تەنھا مردارەوە بوو و خوێنی (ڕۆشتوو) و گۆشتی بەراز و ئەوەی کە سەر بڕابێ (بەناوی) غەیری خواوە ئەمجا ھەر کەسێک ناچار بوو (بە خواردنی یەکێک لەمانە) بەبێ یاخی بوون و دەرچوون لەسنوور ئەوە ھیچ گوناھێکی لەسەر نی یە بێگومان خوا لێ خۆشبو ومیھرەبانە');
INSERT INTO "bamoki" VALUES(2,174,'2:174','بێگومان ئەوانەی ئەو ئایەتانە دەشارنەوە کەخودا ناردونیەتە خوارەوە لە تەورات و دەیگۆڕنەوە بەنرخێکی کەم ئەوانە ئاگر نەبێت ھیچی تر ناچێتە سکیانەوە, وە خوا لەڕۆژی دوایی دا قسەیان لەگەڵ ناکات و پاکیان ناکاتەوە و سزای سەختیان دەبێت');
INSERT INTO "bamoki" VALUES(2,175,'2:175','ئا ئەوانە کەسانێکن کە گومڕاییان کڕی بەڕێنمونی وە سزایان کڕی بەلێ خۆشبون ئای چەند خۆڕاگرن لەسەر ئاگر');
INSERT INTO "bamoki" VALUES(2,176,'2:176','ئەمە (ھەمووی) بەھۆی ئەوەوەیە کە خوا کتێبە (پیرۆزە) کانی ناردۆتە خوارەوە بەڕاستی وە بێگومان ئەوانەی دووبەرەکیان تێکەوت لە تەوراتدا لە دووبەرەکیەکی (وادان) کە دوورن (لەیەک کەوتن)');
INSERT INTO "bamoki" VALUES(2,177,'2:177','چاکە ئەوە نی یە کەڕوو بکەنە بەرەو ڕۆژھەڵات و ڕۆژئاوا بەڵکو چاکە ئەوەیە کە مرۆڤ بڕوای ببێ بەخوا وە بە ڕۆژی دوایی وە بە فریشتەکان وە بە کتێبەکان وە بە پێغەمبەران وە دارایی خۆی ببەخشێ لەگەڵ ئەوەی کەخۆشی دەوێت بەخزمان و بە ھەتیوان وبەبێ نەوایان (ھەژاران) و بەڕێبوارانی کەم دەست و بە سواڵکەران و لەڕێ ی ئازاد کردنی کۆیلەکاندا وە نوێژی بەچاکی کردبێت و زەکاتی دابێت و پەیمان بەجێ ھێنەران کاتێک کەپەیمانیان دابێت و خۆڕاگران لەکاتی تەنگدەستی و نەخۆشی و لەکاتی جەنگدا ئا ئەوانەن کەڕاستیان کرد وە ھەرئەوانەن پارێزکاران');
INSERT INTO "bamoki" VALUES(2,178,'2:178','ئەی ئەو کەسانەی کە بڕواتان ھێناوە پێویست کراوە لەسەر تان تۆڵە سەندنەوه لە کوژراواندا (بەیەکسانی) کەسی ئازاد بە کەسی ئازاد و بەندەش بە بەندە و ژن بە ژن (مێینە بە مێینە) ئەمجا ھەرکەسێک بەخشرا لەلایەن برایەکی دینیەوە بە بەشێک (لە خوێنەکە) ئەبێ (خزمەکانی تر) پەیڕەوی بکەن بەباشی (بکوژیش) بەچاکی خوێنەکە بدات ئەمە باریسهرشان سووککردنە لەپەروەردگارتانەوەو میھرە بانیە ئەمجا ھەرکەسێک دوای ئەمە دەستدرێژی کرد بۆئەو ھەیە سزای بەسوێ، (بەئازار)');
INSERT INTO "bamoki" VALUES(2,179,'2:179','لە کوشتنەوە وتۆڵەسەندنەوەدا ژیان بۆ ئێوە ھـەیـە ئەی ھۆشمەندان بەڵکو خۆتان بپارێزن');
INSERT INTO "bamoki" VALUES(2,180,'2:180','پێویست کراوە لەسەرتان کاتێک یەکێکتان لەسەرەمەرگدا بوو ئەگەر ماڵی لەپاش بەجێ ما وەسیەتکردن بەباشی بۆ دایک وباوک وخزمانێک (کە میراتیان بەرنەکەوێ) بەجوانی و ڕێکو پێکی؟ پێویست کراوە لەسەر پارێزکاران');
INSERT INTO "bamoki" VALUES(2,181,'2:181','ئەمجا ھەر کەسێک وەسیەتەکەی گۆڕی دوای بیستنی ئەوە گوناھی (تاوانی) ھەر لە ملی ئەو کەسانەدایە کە دەیگۆڕن بێگومان خواش بیسەر و زانایە');
INSERT INTO "bamoki" VALUES(2,182,'2:182','ئەمجا ھەرکەسێک ترسا لەوەسیەتکار کەھەڵە یان گوناھێک بکات جا (ئەو کەسە) میراتگرانی پێک بێنێ ئەوە ھیچ گوناھێکی لەسەر نی یە بەڕاستی خودا لێخۆش بوو بەبەزەییە');
INSERT INTO "bamoki" VALUES(2,183,'2:183','ئەی ئەوانەی کە بڕواتان ھێناوە ڕۆژووتان لەسەر نووسراوە وفەرزکراوە ھەر وەک چۆن فەرزکراوە لەسەر ئەو ئومەتانەی لەپێش ئێوە دا بوون بۆ ئەوەی خۆتان بپارێزن لەخودا بترسن');
INSERT INTO "bamoki" VALUES(2,184,'2:184','(ڕۆژووتان لەسەر فەرزکرا) چەند ڕۆژێکی دیاری کراوی ژمێرراوە جا ھەر کەسێک لەئێوە نەخۆش بوو یان لەسەفەردا بوو (وە بەڕۆژوو نەبوو) ئەوە پێویستە بەئەندازەی ئەو ڕۆژانەی (بەڕۆژوو نەبووە) قەزای بکاتەوە لەڕۆژانی تردا وە پێویستە لەسەر ئەوانە کە زۆر بە ناڕەحەتی ئەتوانن بەڕۆژوو بن و (بەڕۆژوو نابن) بریتی (یان لەسەرە)، کەخۆراکی ھەژارێکە ئەمجا ھەرکەسێک زۆرتر بریتی بدات ئەوە باشترە بۆی وئەگەر بەڕۆژوو بن بۆخۆتان باشترە ئەگەر بزانن');
INSERT INTO "bamoki" VALUES(2,185,'2:185','مانگی ڕەمەزان مانگێکە قورئانی تێدا نێرراوەتە خوارەوە بە ڕێنما بۆخەڵکی وە بەڵگە گەلێکی ڕۆشن بۆ ڕێنموونیی و جیاکردنەوەی (دروست ونادروست) ئەمجا ھەرکەسێک لەئێوە مانگی ڕەمەزانی بینی با بەڕۆژوو بێت وە ھەرکەسێک نەخۆش بوو لە سەفەردا بوو با بەئەندازەی ئەوڕۆژانە قەزای بکاتەوە لەچەند ڕۆژێکی تردا خوا ئاسانی ئەوێ بۆئێوە، وە گرانی ناوێت بۆتان (قەزای ڕەمەزانتان لەسەر دانرا) تا مانگەکە (بگرن) بەتەواوی وە بەگەورە ناوی خوا بەرن لەسەر ئەو ڕێنمونیەی کە کردوونی وە تا سوپاسی (خوا) بکەن');
INSERT INTO "bamoki" VALUES(2,186,'2:186','ھەر کارتێک بەندەکانی من پرسیاریان لێ کردیت دەربارەی من ئەوا بێگومان من لێیانەوە نزیکم گیرا دەکەم نزای نزاکاران ھەرکاتێک ھاوار و نزام لێ بکەن دەی با ئەوانیش بەدەنگ منەوە بێن وە بڕوام پێبھێنن بۆ ئەوەی ڕێی راست بدۆزنەوە');
INSERT INTO "bamoki" VALUES(2,187,'2:187','ڕێتان پێدراوە لەشەوانی ڕۆژووی (ڕەمەزان) دا کە بچەنە لای ژنەکانتان ئەوان بۆ ئێوە پۆشاکن وە ئێوەش بۆ ئەوان پۆشاکن خوا دەیزانی کەئێوە ناپاکیتان لەخۆتان دەکرد بۆیە پەشیمانی لێ گیراکردن وەلێتان خۆش بوو ئیتر لەئێستاوە (بە شەو) بچنە لایان وە بەدڵ بخوازن ئەوی کە خوا بۆی نوسیوون (لە منداڵ) وە بخۆن و بخۆنەوە ھەتا بۆتان جیا دەبێتەوە ڕوشتەی سپی (ڕۆژ) لە ڕوشتەی ڕەشی (شەو) لەشەبەقدا لەپاشان ڕۆژوەکەتان تەواو بکەن تاشەوی دوایی وە مەچنەلای ژنەکانتان لەکاتێکدا کە لەمزگەوتدا مابنەوە بۆ خواپەرستی ئەمانە سنوورانی خوان و نزیکیان مەکەونەوە ئاوا خوا باسی فرمانی خۆی دەکات بۆ خەڵکی تا خۆیان بپارێزن');
INSERT INTO "bamoki" VALUES(2,188,'2:188','ماڵی یەکتری مەخۆن لەناو خۆتاندا بەناڕەوا وە بە (بەرتییل) مەیدەن بەکاربەدەستان وفەرمانداران بۆ ئەوەی بەشێک لەماڵی خەڵکی بخۆن بەگوناە لە کاتێکدا ئێوەش دەزانن');
INSERT INTO "bamoki" VALUES(2,189,'2:189','پرسیارت لێ دەکەن دەربارەی نوێ بونەوەی مانگە نوێکان بڵێ ئەمانە نیشانەی کاتەکانن بۆ خەڵکی و بۆ حەج وەچاکە ئەوە نییە کەلەپشتی ماڵ وخانووەکانەوە بچنە ژوورەوە بەڵکو چاکە ئەوەیە ئەو کەسە پارێزکار و لە خوا ترس بێت وەبچنە ماڵەکان لە دەرگاکانیەوە وە لە خوا بترسن تا ڕزگارببن');
INSERT INTO "bamoki" VALUES(2,190,'2:190','جەنگ بکەن لە ڕێ ی خوادا لەگەڵ ئەوانەی جەنگتان لەگەڵدا دەکەن وە دەست درێژی مەکەن بەڕاستی خودا دەست درێژکارانی خۆش ناوێت');
INSERT INTO "bamoki" VALUES(2,191,'2:191','بیان کوژن (کافران) لەھەر شوێنێ دەستتان کەوتن و زاڵ بوون بەسەریاندا وە دەریان بکەن لەو شوێنانەی کە ئێوەیان لێ دەرکردوە لادان لە ڕێی یەکتاپەرستی خوای گەورە لە خەڵک کوشتن سەخت وگەورە ترە جەنگیان لەگەڵ مەکەن لەلای مزگەوتی کەعبەدا تا ئەوان لەوێدا نەجەنگن لەگەڵتان دا ئەمجا ئەگەر جەنگین لەگەڵتاندا ئێوەش بجەنگن لەگەڵیاندا ئەمەیە تۆڵەی بێ بڕوایان');
INSERT INTO "bamoki" VALUES(2,192,'2:192','بەڵام ئەگەر دەستیان ھەڵگرت (لە جەنگ) ئەوا خوای گەورە لێخۆشبوو میھرەبانە');
INSERT INTO "bamoki" VALUES(2,193,'2:193','جەنگیان لەگەڵ بکەن تا دوژمنایەتی بۆ ئاین وھاوەڵ بڕیاردان نەمێنێ وە ئاین و ملکەچی ھەر بۆ خوا دەبێت ئەمجا ئەگەر دەستیان ھەڵگرت ئیتر دەست درێژی نیە مەگەر لەسەر ستەمکاران نەبێت');
INSERT INTO "bamoki" VALUES(2,194,'2:194','رێزی مانگی حەرام بەرانبەر بەڕێزگرتنی ئەوانە بۆمانگی حەرام دەبێت شکاندنی ھەر رێزێکیش تۆڵەی ھەیە جا ھەر کەسێک دەست درێژی کردە سەرتان ئێوەش دەست درێژی بکەنە سەری بەوێنەی دەست درێژ کردنەکەی ئەو بۆ ئێوە, وە لەخوا بترسن وە بزانن کەخوا لەگەڵ پارێزکارانە');
INSERT INTO "bamoki" VALUES(2,195,'2:195','وە دارایی خۆتان بەخت بکەن لە ڕێی خوادا وە بەدەستی خۆتان خۆتان مەخەنە تیاچوونەوە, وە چاکە بکەن بەڕاستی خوا چاکەکارانی خۆش دەوێت');
INSERT INTO "bamoki" VALUES(2,196,'2:196','حەج و عومرەتان (کە دەست پێکرد) تەنھا بۆ خوا بە تەواوی بیکەن ئەمجا ئەگەر ھەر (قەدەغەکران) ئەوسا چیتان بۆ ھەڵسوڕا لەقوربانی (سەری بڕن) وە سەرتان مەتاشن تا قوربانیەکە دەگاتە جێ ی دیاری کراوی خۆی ئەمجا ھەر کەسێک لە ئێوە نەخۆش بوو یان ئازارێک لەسەریدا بوو (ئەگەر سەری تاشی) بریتیەک بدات کە ڕۆژووی (سێ ڕۆژە) یان خێرێک یان قوربانیەک بکات جا کاتێک دڵنیا بوون (ترس نەبوو) ئەمجا ئەو کەسەی بەھرەمەند بوو بە بەجێھێنانی عومرە تاکاتی حەج ئەوە چی بۆ ھەڵسوڕا لە قوربانی (بیکات) وە ئەگەر یەکێک نەیبوو با سێ ڕۆژ بەڕۆژوو بێت لە کاتی حەجدا وە حەوت ڕۆژی تریش کاتێک گەڕانەوە ئەمە دەبێتە دە ڕۆژی تەواو ئەمە بۆ کەسێکە کە (خۆی) و کەس و کاری دانیشتووی دەوروبەری (مسجد الحرام) نەبێت وە لەخوا بترسن وە بزانن کە بەڕاستی خوا خاوەنی تۆڵە و سزای سەختە');
INSERT INTO "bamoki" VALUES(2,197,'2:197','(کاتی) حەج لەچەند مانگێکی دیاری کراودایە ئەمجا ھەرکەسێک لەم مانگانەدا حەجی لەسەر خۆی پێویست کرد بائیتر نەچێتە لای ژن وە بەدکاری نەکات وە دەمەقاڵێ نەکات لەحەجدا وە ھەرچی بکەن لەچاکە خوا پێ ی دەزانێ وە توێشوو (ی پارێزکاری) و لەخواترسان ھەڵبگرن کەباشترین توێشوو لەخوا ترسانە وە لەمن بترسن ئەی ھۆشمەندان');
INSERT INTO "bamoki" VALUES(2,198,'2:198','گوناھتان لەسەر نی یە کەدوای ڕۆزی پەروەردگارتان بکەون ئەمجا کاتێک بڵاوەتان لێکرد وگەڕانەوە لە عەرەفات ناوی خوا بێنن لەلای (مشعر الحرام) دا وەیادی خوا بکەن کە ڕێنموونی کردن با لەپێش ئەوەشدا ئێوە لە گومڕایان بووبن');
INSERT INTO "bamoki" VALUES(2,199,'2:199','لە پاشان بگەڕێنەوە لەوشوێنەی خەڵکی لێ ی دەگەڕێتەوە وەداوای لێ خۆشبوون بکەن لەخوا بێگومان خوا لێ خۆشبوو میھرەبانە');
INSERT INTO "bamoki" VALUES(2,200,'2:200','ئەمجا کاتێک ئەرکان و واجباتی حەجتان تەواوکرد یادی خوا بکەن وەک ناوبردنتان بۆ باوو باپیرانتان یان زۆرتر لەناوی ئەوان ئەمجا ھی وا ھەیە لەخەڵکی (ھەر داوای دونیا دەکات و) دەڵێت خوایە بماندەرێ لەدونیادا وە (ئەوانە) ھیچ بەشێکیان نیە لەڕۆژی دواییدا');
INSERT INTO "bamoki" VALUES(2,201,'2:201','وەھی واش ھەیە دەڵێت خوایە ھەم لەدونیا و ھەم لە ڕۆژی دواییدا خێرو خۆشیمان پێ بدە وە بمان پارێزە لەسزای ئاگر');
INSERT INTO "bamoki" VALUES(2,202,'2:202','ئا ئەوانە بەشێکیان ھەیە لەوەی کە کردوویانە وە خوای گەوره زۆر خێرایە لە لێپرسینەوەدا');
INSERT INTO "bamoki" VALUES(2,203,'2:203','ناوی خوا بەرن (اللە اکبر بکەن) لەچەند ڕۆژێکی دیاری کراودا ھەرکەسێک پەلە بکات لە دوو ڕۆژدا (ھەرسێ جەمەرەکە بەردباران بکات) گوناھی ناگات وە ھەر کەسێکیش دوابکەوێت (سێ ڕۆژ ڕەجم بکات) گوناھی لەسەر نی یە بۆکەسێک پارێزکاری بکات و لەخوا بترسێت لەخوا بترسن و (لەسزای) خۆتان بپارێزن وە بزانن کە ئێوە بۆ لای ئەو (ئەگەڕێنەوەو) کۆدەکرێنەوە');
INSERT INTO "bamoki" VALUES(2,204,'2:204','لە ناوخەڵکی کەسانێک ھەن کە بە قسە سەرسامت دەکات لەم ژیانی دونیادا وە خوا دەکات بەشاھید لەسەر (ڕاستی) ئەوەی لە دڵیایەتی کەچی ئەو دوژمنێکی سەر سەختە');
INSERT INTO "bamoki" VALUES(2,205,'2:205','وەکاتێک پشتی ھەڵکرد ھەوڵ دەدات لە زەویدا (لە ووڵاتدا) بۆ ئەوەی خراپەی تێدا بکات وە کشتوکاڵ و وەچەو نەوە لەناو ببات وەخوا خراپەکاری خۆش ناوێت');
INSERT INTO "bamoki" VALUES(2,206,'2:206','وە کاتێک پێ ی بووترێت لەخوا بترسە دەمار دەیگرێ بۆ بەد کرداریی جا ئەو دۆزەخی بەسە کە بەدترین جێگایە');
INSERT INTO "bamoki" VALUES(2,207,'2:207','لە ناوخەڵکیش کەسانێک ھەن کە گیانی خۆی دەفرۆشێت بۆ ڕەزامەندی خوا وە خوا میھرەبانە بەبەندەکان');
INSERT INTO "bamoki" VALUES(2,208,'2:208','ئەی بڕواداران ھەموو بەتەواوی بچنە ناو ئیسلامەوە وەدوای ھەنگاوەکانی شەیتان مەکەون چونکە بێگومان ئەو دوژمنێکی ئاشکراتانە');
INSERT INTO "bamoki" VALUES(2,209,'2:209','ئەمجا ئەگەر لاتاندا (ھەڵخلیسکان) دوای ئەوەی کە نیشانە ئاشکراکانتان بۆھات ئەوە چاک بزانن کە خوا زاڵ و کار دروستە');
INSERT INTO "bamoki" VALUES(2,210,'2:210','ئایا ئەوانە چاوەڕوانن خوایان بۆ بێت لەناو چەند پارچە ھەورێکی سپی دا ھەروەھا فریشتەکان (یان بۆبێ) وە کارەکە کۆتایی پێ بێت وە ھەموو کارێک ھەر بۆلای خوا دەگەڕێتەوە');
INSERT INTO "bamoki" VALUES(2,211,'2:211','(ئەی موحەممەد صلى الله عليه وسلم) لەبەنی ئیسرائیل بپرسە کە چەند بەڵگەو نیشانەی ڕۆشنمان داوە بەوان وە ھەر کەسێک بیگۆڕێت بەھرەی (ڕێنموونی) خوا دوای ئەوەی کەدەستی کەوت ئەوە بێگومان خودا تۆڵەی زۆر سەختە');
INSERT INTO "bamoki" VALUES(2,212,'2:212','جوان کراوە بۆ بێ بڕوایان (ناسوپاس کەران) ژیانی دونیا(بە جۆرێک) کە گاڵتە دەکەن بە بڕواداران (بەڵام پایەی) ئەوانەی لەخوا ترسن گەلێ بەرزترە لەوان لە ڕۆژی دواییدا وە خودا ڕزق و ڕۆزی دەدات بەھەر کەسێک کەبیەوێت بەبێ شومار وحسابکردن');
INSERT INTO "bamoki" VALUES(2,213,'2:213','خەڵکی یەک کۆمەڵ بوون (بە سرووشت) ئەمجا خودا پێغەمبەرانی نارد بە مژدەدەر و ترسێنەر وە کتێبی ڕاستی و دروستی لەگەڵدا ناردونە خوارەوە تا بڕیار بدات و حوکم بکات لەناو خەڵکی دا لەو شتانەی کە جیاواز و دووبەرەک بوون تیایدا وە کەس جیاواز و دووبەرەکی نەکرد لە کتێبی ئاسمانیدا مەگەر ئەوانەی کە کتێبیان پێ درابوو دوای ئەوەی نیشانەو بەڵگەی ڕۆشنیان بۆ ھات بەھۆی حەسادەتی ناو خۆیانەوە ئەمجا خوا خۆی ڕێنموونی بڕوادارانی کرد بۆ ئەو ڕێگا ڕاستەی کە دووبەرەکیان تیادا دەکرد وە خوا کێ ی بوێت ڕێنموونی دەکات بۆ ڕێبازی ڕاست');
INSERT INTO "bamoki" VALUES(2,214,'2:214','ئایا واتان زانییوە کە دەچنە بەھەشت کەچی ھێشتا نەھاتۆتە ڕێتان ئەوەی لەناڕەحتی تووشی ئەوانە بوون کە لە پێش ئێوەدا تێپەڕین (کە لە ڕێی خوادا) دووچاری سەختی و زەرەر و زیان بوون وە زۆر بە توندی تەکان دران وزۆریان بوھات بەڕەنگێک کە پێغەمبەرەکە و ئەوانەی کەبڕوایان ھێنابوو لەگەڵیدا دەیان ووت سەرکەوتنی خوا کەی دێت ئاگادار بن و دڵنیابن بە ڕاستی سەرکەوتنی خوا نزیکە');
INSERT INTO "bamoki" VALUES(2,215,'2:215','پرسیارت لێ دەکەن چی ببەخشن (لە ڕێ ی خوادا) بڵێ: ئەوەی دەیبەخشن لە ماڵی پاک و حەڵاڵ با بۆ باوک و دایک و خزمانی نزیک و ھەتیوان و ھەژاران و ڕێبوارانی کەم دەست وە ھەرچی بکەن لە چاکە ئەوە بێگومان خوا ئاگادارە لێ ی');
INSERT INTO "bamoki" VALUES(2,216,'2:216','جەنگتان لەسەر پێویست کرا کەلاتان ناخۆشە وەئەگونجێ شتێکتان پێ ناخۆش بێ کەچی باش وخێر بێت بۆتان وەئەگونجێ شتێکتان پێ خۆش بێ و خراپ بێت بۆتان وە خوا ( چاک وخراپی ئێوە ) ئەزانێت و ئێوە نایزانن');
INSERT INTO "bamoki" VALUES(2,217,'2:217','پرسیارت لێ دەکەن دەربارەی مانگی حەرام جەنگ کردن تیایدا بڵێ جەنگ لەمانگی حەرام دا گوناھێکی گەورەیە بەڵام بەرھەڵستی ڕێبازی خوا وەبێ بڕوایی پێی وە ( ڕێگری موسوڵمانان) لەمزگەوتی حەرام ودەرکردنی خەڵکی ئەو مزگەوتە لێی گەورە ترە لای خوا (لە جەنگ کردن لە مانگی حەرامدا) وە ھاوەڵ پەیداکردن بو خودا و ھەوڵ دان بۆ پەشیمان کردنەوەی موسڵمان گەورەترە ل(پیاو) کوشتن (لەومانگەدا) بەردەوام دەست ھەڵناگرن و جەنگتان لەگەڵدا دەکەن تاپەشیمانتان بکەنەوە لەدینەکەتان ئەگەر بتوانن وەھەرکەسێک لەئێوە لەدینی خۆی ھەڵبگەڕێتەوە وە بەبێ بڕوایی بمرێ ئا ئەوانە ھەموو کردەوەکانیان پوچ ئەبێتەوە لە دونیا و دواڕۆژیشدا وەئەوانە یارو ھاودەمی ئاگرن تێیدا دەمێننەوە بەئێجگاری');
INSERT INTO "bamoki" VALUES(2,218,'2:218','بە ڕاستی ئەوانەی بڕوایان ھێنا وئەوانەی کۆچیان کرد و تێکۆشان لەڕێی خوادا ئائەوانە چاوەڕوانی میھرەبانی خودان وەخوا لێ خۆش بوو میھرەبانە');
INSERT INTO "bamoki" VALUES(2,219,'2:219','ئەی موحەممەد پرسیارت لێ دەکەن دەربارەی عەرەق و قومار بڵێ لەم دوانەدا گوناھی گەورە ھەیە و چەند قازانجێک ھەیە بۆ خەڵکی بەڵام گوناھەکەیان گەورە ترە لە سوود وقازانجەکەیان وە پرسیارت لێ دەکەن چی ببەخشن بڵێ ئەوەی لێتان زیادە ئاوەھا خوا بەڵگەکان ڕون دەکاتەوە بۆتان تا بیربکەنەوە');
INSERT INTO "bamoki" VALUES(2,220,'2:220','لەکاری دونیا ودواڕۆژتان وەپرسیارت لێ دەکەن لەبارەی ھەتیوان (کەچۆن بن لەگەڵیاندا) بڵێ چاکسازی (تێکەڵاوی کردن) بۆ ئەوان چاکترە وەئەگەر تێکەڵ ببن لەگەڵیاندا (قەیناکات چونکە) براتانن وەخوا دەزانێ کێ خراپەکار و کێ چاکەکارە (لەگەڵ ھەتیوان) ئەگەر خوا بیویستایە دوچاری ڕەنج و ناڕەحەتی دەکردن بەڕاستی خوا زاڵ و کار دروستە');
INSERT INTO "bamoki" VALUES(2,221,'2:221','ژنانی ھاوبەش پەیداکار مارە مەکەن تابڕوا دەھێنن کەنیزەکێکی بڕوادار باشترە لەژنە (ئازادێکی) ھاوبەش پەیداکار با (ھاوبەش پەیدا کەرەکە) سەرسامیشی کردبن (بەجوانی یان داراییان) وە ژنانی موسڵمان مارە مەکەن لەھاوبەش پەیداکەران تابڕوا دەھێنن وەکۆیلەیەکی بڕوادار باشترە له (ئازادێکی) ھاوبەش پەیداکەر با سەرسامیشی کردبن (بە دارایی یا جوانیان) ئەوانە بانگی (خەڵک) دەکەن بۆ ناو ئاگر بەڵام خوا (خەڵک) بانگ دەکات بۆ بەھەشت ولێخۆش بوون بەفەرمانی خۆی وە نیشانەکانی خۆی ڕۆشن دەکاتەوە بۆخەڵکی بەڵکو بیربکەنەوە');
INSERT INTO "bamoki" VALUES(2,222,'2:222','وەپرسیارت لێدەکەن دەربارەی حەیز (بێ نوێژی ئافرەتان) بڵێ ئەوە ئازارە کەواتە دوور بکەونەوە لەھاوسەرەکانتان لە کاتی حەیزا وە نزیکیان مەکەونەوە تا پاک دەبنەوە کەخۆیان شت (دوای پاک بوونەوە) ئەمجا بچنە لایان لەو شوێنەوە کە خوا فەرمانی پێداون بەڕاستی خوا تەوبەکارانی خۆش دەوێت وپاکانی خۆش دەوێت');
INSERT INTO "bamoki" VALUES(2,223,'2:223','ژنەکانتان کێڵگەی خۆتانن جا تۆوەکانتان بوەشێننە ناو کێڵگەکانتانەوە (مەبەست چوونە لایانە) بەھەرشێوەیەک دەتانەوێ (لەشوێنی تایبەتی خۆی کە پێشەوەیە نەک دواوە) کردەوەی باش پێش بخەن بۆ خۆتان لەخوا بترسن وە بزانن و دڵنیابن کەبەخزمەتی دەگەن وەمژدە بدە بەبڕواداران');
INSERT INTO "bamoki" VALUES(2,224,'2:224','نەکەن سوێند بەخوا خواردن بکەن بەبەرھەڵستی ئەوشتانەی سوێندتان لەسەری خواردووە کەچاکی و پارێزکاری و ڕێکخستنی نێوان خەڵکیە وە خوا بیسەر و زانایە');
INSERT INTO "bamoki" VALUES(2,225,'2:225','خوا سوێندی بێ ھوودە (لەدەم دەرچوون) تان لێ ناگرێ بەڵام سوێندێکتان لێ دەگرێت بە دڵ بتانەوێت ومەبەستتان بێت وەخوا لێخۆشبوو لەسەر خۆیە');
INSERT INTO "bamoki" VALUES(2,226,'2:226','بۆ ئەوانە ھەیە سوێند دەخۆن لەگەڵ ژنەکانیان دروست نەبن چاوەڕوانی چوار مانگ بکەن ئەمجا ئەگەر گەڕانەوە (بۆ لای ژنەکانیان) ئەوا خوا لێخۆشبوو میھرەبانە');
INSERT INTO "bamoki" VALUES(2,227,'2:227','وە ئەگەر بڕیاری تەڵاقدانیاندا ئەوا خوا بیسەرو زانایە');
INSERT INTO "bamoki" VALUES(2,228,'2:228','وە ژنە تەڵاقدراوەکان دان بەخۆیاندا دەگرن تا ماوەی سێ حەیز (لەخوێنا بوون) دروست نیە بۆ ئەوان کە بیشارنەوە ئەوەی خوا لە مناڵدانیاندا دروستیکردوه (لە حەیز یا منداڵ) ئەگەر بڕوایان ھەیە بەخوا و بەڕۆژیی دوایی وە مێردەکانیان شایستەن بە گێڕانەوەیان لە عیددەدا ئەگەر بیانەوێ بە باشی بژین وە بۆ ژنان ھەیە بەسەر مێردیانەوە وەکو ئەوەی کە بۆ پیاوان ھەیە بەسەر ژنانەوە بەپێ ی باوونەریتى (پەسەند) وە پیاوان پلەیەکیان ھەیە بەسەر ژنانیانەوە, وە خوا زاڵ و کار دروستە');
INSERT INTO "bamoki" VALUES(2,229,'2:229','تەڵاقێک (کە بردنەوەی لە دواوە بێت) دوو جارە دوای ئەوە ڕاگرتنە بەخۆشی یان ئازادکردن وتەڵاقدانیەتی بەباشیی وە دروست نیە بۆ ئێوە کە لێی بسەننەوە ھیچێک لەوشتانەی کەداوتاننێ مەگەر ھەردوولا بترسن (لەوەی) کە سنوری خوا ڕانەگرن جا ئەگەر ترسان لەوەی سنوری خوا ڕانەگرن ئەو پیاوو ئافرەتەکە گوناھیان ناگات بەوە کە ژنەکە لەبریتی خۆی بیدات بەپیاوەکەی ئەمانەی (باسکران) سنوری خوان لێیان دەرمەچن ودەستدرێژی مەکەنە سەریان وە ھەرکەسێک لە سنوری خوا دەربچێت ئا ئەوانە ستەمکارن');
INSERT INTO "bamoki" VALUES(2,230,'2:230','ئەمجا ئەگەر تەڵاقی (سێھەمی ) دا ئیتر ئەو ژنە بۆ ئەو پیاوە دروست نی یە ھەتا شوونەکات بە مێردێکی دیکە (بە ئێجگاری) ئەمجا ئەگەر (مێردی دووھەم) تەڵاقیدا (یا مرد) ئیتر گوناە نیە لەسەر ژن و مێردە (کۆنە) که کە بگەڕێننەوە لای یەکتر (بە مارەکردنیکی تازە) ئەگەر بزانن کە لە سنووری خوا دەرناچن ئەمانە سنووری خوان ڕونیان دەکاتەوە بۆ گەلێک کە تێ بگەن');
INSERT INTO "bamoki" VALUES(2,231,'2:231','کاتێک ژنانی ھاوسەرتان تەڵاقدا و گەیشتن بە کۆتایی عیددەیان ڕایان بگرنەوە بەخۆشی یان بەرەڵایان بکەن وتەڵاقیان بدەن بەباشی ڕایان مەگرنەوە بەئێش و ئازارەوە بۆئەوەی ستەمیان لێ بکەن ھەرکەسێک کاری وابکات ئەوەستەمی لەخۆی کردوە, وە گاڵتە بەفەرموودەکانی خوا مەکەن و بیربکەنەوە لە چاکەی خوا لەگەڵتاندا و ئەوەی کەبەسەریدا ڕشتوون لەقورئان و فەرموودەی بەجێ بەوە ئامۆژگاریتان دەکات وە لەخوا بترسن وەبزانن کەخوا بەھەموو شت زانایە');
INSERT INTO "bamoki" VALUES(2,232,'2:232','کاتێک ژنتان تەڵاقدا عیددەیان تەواو بوو ئیتر قەدەغەیان (بەرگرییان) مەکەن کە شووبکەن بەخەڵکانێکی دیکە کە دەبن بە مێردیان ئەگەر ڕێک کەوتن لەناو خۆیاندا بەباشی ئەمە پەند وئامۆژگاری تێدایە بۆئەو کەسەی کەبڕوای بەخواو بەڕۆژی دوایی بێت لە ئێوە ئەم پەند وەرگرتنەتان باشترەو پاکترە چونکە خوا دەزانێ و ئێوە نازانن');
INSERT INTO "bamoki" VALUES(2,233,'2:233','دایکان شیر بدەن بە منداڵەکانیان دوو ساڵی تەواو (ئەمە) بۆکەسێکە کە بیەوێت شیری تەواو بدات لەسەر باوکەکەیە ڕۆزی و کاڵای دایکەکان بەباشی ھیچ کەسێک لەتوانای خۆی زیاتر داوای لێ ناکرێت با زیان نەگەیەندرێ بەھیچ دایکێک بەھۆی منداڵەکەیەوە, وە بەھیچ باوکێک بەھۆی منداڵەکەیەوە, وه (ئەگەر باوکەکە مرد) لەسەر میراتگرە وەکو ئەوەی (پێشوونەفەقەی دایکی منداڵە کە بدات) ئەمجا ئەگەر ویستیان (منداڵەکە) لەشیر بیبڕنەوە (لەپێش دوو ساڵەکەدا) بەڕەزامەندی و ڕاوێژی ھەردوولایەکیان ئەوە گوناھیان ناگات وە ئەگەر (ئێوەی پیاوان) ویستان دایەن بگرن بۆ منداڵەکانتان ئەوە گوناھتان ناگات ئەگەر بیان دەنێ ئەوەی بۆتان داناون بەخۆشی وە لەخوا بترسن و بزانن بەڕاستی خوا بینایە بەوەی کە دەیکەن');
INSERT INTO "bamoki" VALUES(2,234,'2:234','ئەو پیاوانەی کەدەمرن لەئێوە و ھاوسەران جێ دەھێڵن ھاوسەرەکانیان دەبێت چاوەڕوانی بکەن چوار مانگ و دە شەو ئەمجا کە عیددەیان تەواو بوو ئیتر گوناھتان لەسەر نی یە (ئەی کەس وکاری ژن) لەوەی کە (ئەو ژنانە) دەیکەن لەخۆیاندا بەپێ ی باوی ناو کۆمەڵ وە خوا ئاگادارە بەوەی کە دەیکەن');
INSERT INTO "bamoki" VALUES(2,235,'2:235','وە گوناھتان ناگات لە خوازبێنی ژنانی (مێرد مردوو) بە ناڕاستەوخۆ پێیان بڵێن یا لەدڵی خۆتاندا (بڕیار بدەن) دەیانخوازن خوای گەورە زانیویەتی کە بێگومان ئێوە لەمەو دوا ناویان دەبەن بەڵام بەنھێنی بەڵێنیان پێ مەدەن مەگەر قسەیەک کەباش بێت وە بڕیاری مارەکردنیان مەدەن تا عیددەکەیان تەواو دەبێت وە بزانن بێگومان کە خوا دەزانێت بەوەی کە لە دڵتاندایە کەواتە خۆتانی لێ بپارێزن وە بزانن دڵنیابن کە خوا لێخۆشبووی لەسەر خۆیە');
INSERT INTO "bamoki" VALUES(2,236,'2:236','گوناھتان لەسەر نیە ئەگەر ژنانێک تەڵاق بدەن کە ھێشتا نەچووبنە لایان یا مارەییتان بۆ دانەنابن وە بەھرەمەندیان بکەن (شتێکیان لەماڵی خۆتان بدەنێ) دەوڵەمەند بەپێ ی خۆی وە ھەژار بەپێ ی خۆی بەھرەو یارمەتی دانێک کە باو و نەریت بێت. ئەوە پێویستە لەسەر چاکەکاران');
INSERT INTO "bamoki" VALUES(2,237,'2:237','وە ئەگەر ژنانتان تەڵاقدا پێش ئەوەی بچنە لایان وە مارەییتان بۆدانابون ئەوە نیوەی مارەیی یەکەیان دەکەوێ مەگەر ئەوان (لێتان) خۆش بن یان ئەو کەسە لە (مێردی) خۆش ببێت کە مارەبڕینەکەی بەدەستە وە لێ خۆشبونتان لە پارێزکاریەوە نزیکترە وە ئەو چاکەیەی کە لە نێوانتاندا بووە لەبیرتان نەچێتەوە بێگومان خوا بینایە بەوەی کەوا دەیکەن');
INSERT INTO "bamoki" VALUES(2,238,'2:238','ئاگاداری نوێژەکانتان بن وبیانپارێزن وە (بەتایبەتی) نوێژی ناوەڕاست (کە نوێژی عەسرە) وە لە نوێژدا بوەستن بۆ خوا بەمل کەچی و (بێ قسەکردن)');
INSERT INTO "bamoki" VALUES(2,239,'2:239','ئەمجا ئەگەر ترسان (نوێژ بکەن) بە پیادە یان بەسواری کەترستان نەما ئەوسا یادی خوا بکەن بەوڕەنگەی کەفێری کردون ئەوەی جاران نەتاندەزانی');
INSERT INTO "bamoki" VALUES(2,240,'2:240','ئەو پیاوانەی دەمردن لەئێوە پاش خۆیان ژنەکانیان بەجێ دەھێڵن (خوا فەرمانی کردووە) بە وەسیەت کردن بۆ ژنەکانیان تاساڵێک بەخێو بکرێن بێ ئەوەی دەربکرێن (لە ماڵی مێردیان) ئەمجا ئەگەر خۆیان چوونە دەرەوە ئیتر ھیچ گوناھتان لەسەر نیە (ئەی کەس وکاری مێرد) لەوەی کە ئەو ژنانە دەیکەن دەربارەی خۆیان بەباشیی وە خوا زاڵ وباڵادەست و کار دروستە');
INSERT INTO "bamoki" VALUES(2,241,'2:241','بۆ تەڵاق دراوان ھەیە (بەسەر مێردەکانیانەوە) بەھرەمەند بکرێن (بە بڕێک پارە) بەباشی (ئەوە) پێویستە لەسەر پارێزکاران');
INSERT INTO "bamoki" VALUES(2,242,'2:242','ئاوا خوا ئایەتەکانی خۆیتان بۆ رون دەکاتەوە تاتێبگەن و ژیربن');
INSERT INTO "bamoki" VALUES(2,243,'2:243','ئایا ووردنەبویتەوە لەوانەی کەلە وڵاتی خۆیان دەرچوون لەکاتێکدا کە ئەوان ھەزاران (کەس) بوون لەترسی مردن ئەمجا خوا پێی وتن بمرن لە پاشان زیندوی کردنەوە بەڕاستی خوا خاوەنی چاکەیە بەسەر خەڵکەوە بەڵام زۆربەی خەڵک سوپاسی خوا ناکەن');
INSERT INTO "bamoki" VALUES(2,244,'2:244','بجەنگن لەڕێی خوادا وە بزانن بەڕاستی خوا بیسەر و زانایە');
INSERT INTO "bamoki" VALUES(2,245,'2:245','کێیە ئەو کەسەی قەرز دەدات بەخوا بە قەرزێکی باش ئەمجا خوا بۆی زیاد دەکات بەزیاد کردنێکی زۆر وە خوا (ڕۆزی خۆی) ئەگرێتەوە وە فراوانی دەکات وە ھەر بۆلای ئەویش دەگەڕێنەوە');
INSERT INTO "bamoki" VALUES(2,246,'2:246','ئایا وورد نەبوویتەوە لەبەسەرھاتی دەستە و کۆمەڵێک لەکاربەدەست و ناودارانی بەنی ئیسرائیل (نەوەکانی یەعقوب) لەدوای مووسى کە بەپێغەمبەرێکی خۆیان ووت پاشایەکمان بۆ بنێرە تا جەنگ بکەین لەڕێی خوادا ووتی ئایا نزیک نییە ئێوە ئەگەر جەنگتان لەسەر فەرز و پێویست بکرێ جەنگ ناکەن (لە وەڵامدا) ووتیان چ سودێکمان پێ دەگات ئەگەر جەنگ نەکەین لەڕێی خوادا لەکاتێکدا کە دەرکراوین لە ووڵات و ماڵ و منداڵماندا کەچی کاتێک جەنگیان لەسەر پێویست کرا پشتیان ھەڵکرد کەمێکیان نەبێت خوایش ئاگادارە بەسەر ستەمکاراندا');
INSERT INTO "bamoki" VALUES(2,247,'2:247','وە پێغەمبەرەکەیان پێی ووتن بێگومان خوا طالوتی بۆ ناردن بە پادشا ووتیان ئەو چۆن ئەبێ بە پادشا بەسەر ئێمەوە کە ئێمە لەو شایستە ترین بەپادشایی وە دارایی زۆری نەدراوەتێ فەرمووی: بەڕاستی خوا ئەوی ھەڵبژاردووە بەسەر ئێوەدا وە بەشی زیاتری داوەتێ لە فراوانی لەزانین و توانایی جەستە وە خوا موڵک و پاشایی خۆی دەدات بەھەر کەس خۆی بیەوێ وە خوا (بەھرە) فراوان و زانایە');
INSERT INTO "bamoki" VALUES(2,248,'2:248','وە پێغەمبەرەکەیان پێی ووتن نیشانەی پادشاییەکەی ئەوەیە کە سندوق و دارەمەیتەکەتان بۆ دێتەوە کەمایەی ئارامیتانە لەپەروەردگارتانەوە لەگەڵ ھەندێک لە کەلەپوری خانەدانی موسا و ھارون فریشتە ھەڵیدەگرن بێگومان لەمەدا نیشانە و بەڵگە ھەیە بۆ ئێوە ئەگەرئێوە بڕوادار بن');
INSERT INTO "bamoki" VALUES(2,249,'2:249','ئەمجا کاتێک کە طالوت بەخۆی و لەشکرەکەیەوە (لەشار) دەرچوون پێی ووتن بێگومان خوا تاقیتان دەکاتەوە بەچەمێک (ڕوبارێک) ئەمجا ھەرکەسێک لێی خواردەوە ئەو لە (لەشکری) من نیە وە ھەرکەسێک لێی نەچەشت ئەوە لە منە مەگەر کەسێک پڕمشتێکی لێ بگرێت بە دەستی خۆی (و بیخواتەوە) جا ھەموو تێریان لێ خواردەوە کەمێکیان نەبێت کاتێک طالوت لێی پەڕیەوە ئەوانەش بڕوایان ھێنابوو لەگەڵیدا (بێ ئەوانەی کە تێریان لێ خواردەوە) ووتیان: ئەمڕۆ ئێمە دەرەقەتی جالوت و لەشکرەکەی نایەین وتیان: ئەوانەی کە پێیان وابوو بە خزمەت خوا دەگەن (لە ڕۆژی دواییدا) زۆر دەستەی کەم زاڵ بووە بەسەر دەستەی زۆردا بەفەرمانی خوا وە خوا لەگەڵ خۆڕاگرانە');
INSERT INTO "bamoki" VALUES(2,250,'2:250','کاتێک کە رووبەرووی جالووت و لەشکرەکەی وەستان ووتیان: خوایە خۆڕاگری بڕێژە بەسەرماندا وە پێمان جێگییر بکە وە زاڵمان بکە بەسەر گەلی بێ بڕوایاندا');
INSERT INTO "bamoki" VALUES(2,251,'2:251','ئەوسا بەفەرمانی خوا شکاندنیان وە داود (کە یەکێک بوو لەسەربازانی طالوت) جالوتی کوشت وە خوا پاشایەتی و پێغەمبەرێتی دایە وە ئەوەی ویستی فێری کرد ئەگەر خوا خەڵکی لەناو نەبردایە ھەندێکیان بەھەندێکی تریان ھەموو سەر زەوی پڕدەبوو لە خراپە و تاوان بەڵام خوا خاوەنی بەخششە بەسەر ھەموو جیھانیانەوە');
INSERT INTO "bamoki" VALUES(2,252,'2:252','ئەم (بەسەرھاتانە) نیشانەکانی خوان دەیان خوێنیینەوە بەسەرتدا بەڕاستی وە بێگومان تۆ لە پێغەمبەرانیت');
INSERT INTO "bamoki" VALUES(2,253,'2:253','ئەو پێغەمبەرانەی (کە باسکران) ڕێزمان داوە ھەندێکیان بەسەر ھەندێکیاندا لەو پێغەمبەرانە ھی وایان بووە کە خوا قسەی لەگەڵ کردووە (وەک موسی علیە السلام) وە ھەندێکیانی بەرز کردوەتەوە چەند پلەیەک وە دامان بە عیسای کوڕی مەریەم چەند بەڵگە و (موعجیزەیەکی) ئاشکرا وە پشتگیریمان کرد بە فریشتەی سرووش (کە جبریلە) ئەگەر خوا بیویستایە ئەو گەلانەی کەلە دوای پێغەمبەرەکانیان دەھاتن لەگەڵ یەکدا نە ئەجەنگین دوای ئەو نیشانە ڕۆشنانەی کەبۆیان ھاتبوو بەڵام دووبەرەکیان تێکەوت ئەمجا ھی وایان بوو بڕوای ھێنا وە ھی وایان بوو بێ بڕوابوو ئەگەر خوا بیویستایە یەکتریان نەدەکوشت و شەڕیان نەدەکرد بەڵام ئەوەی خوا بیەوێت دەیکات');
INSERT INTO "bamoki" VALUES(2,254,'2:254','ئەی ئەو کەسانەی بڕواتان ھێناوە ببەخشن لەو ڕۆزیەی کە پێمان داون پێش ئەوەی کە ڕۆژێ بێ کە نە کڕین و فرۆشتنی تێدایە و نە دۆستایەتی و ئاشنایەتی تێدایە وە نە تکای تێدایە (تا ڕزگارتان کات لە سزا) وە ھەر بێ بڕوایانن');
INSERT INTO "bamoki" VALUES(2,255,'2:255','خوا ئەو خوایەیە کە ھیچ پەرستراوێکی بەھەق و ڕاست نیە بێجگە لەو ھەمیشە زیندوی تەدبیرکار و ڕاگری (بوونەوەرە) وەنەوز و خەونایگرێت ھەر ھی ئەوە ھەر چی لە ئاسمانەکان و ھەرچی لە زەوی دایە کێیە ئەو کەسەی لەو ڕۆژەدا بتوانێ تکای لەلا بکات مەگەر بە ڕێ پێدانی خۆی نەبێت ئاگادارە بە ئەوەی لەبەردەمی (ئادەمی) یە وەیە (لە دونیا لە بەردەستیاندایە) وە ئەوەی لە دواوەیەتی (لە ڕۆژی دوایی بۆیان ئامادەکراوە) وە کەس ئاگای نیە لە ھیچ شتێک لە زانین و زانیاری ئەو (خوا) یە مەگەر بەوەی کە خۆی بیەوێ کورسیەکەی خودا ھەموو ئاسمانەکان و زەوی گرتۆتەوە, وە پارێزگاری کردنی ئاسمانەکان و زەوی بۆ خوا ئاسانە و (گران نیە) وە ھەر ئەوە بڵند پلەی پایە گەورە');
INSERT INTO "bamoki" VALUES(2,256,'2:256','بە ھیچ جۆرێک زۆر لێکردن لە دیندا نیە (بۆ ھاتنە ناو ئیسلام) چونکە بەڕاستی لە یەک جیابوەتەوە ڕێنمونی و شارەزایی لە گومڕایی و سەر لێ شێواوی (ئەم ئایەتە تایبەتە بە ئەوانەی ئاینی ئاسمانیان ھەیە) ئەمجا ھەر کەس لە ڕێی بێ بڕوایی بگەڕێتەوە (بێ باوەڕ بێت بە ھەر پەرستراوێک جگە لە خوا) وە بڕوا بھێنێ بەخوا ئەوە بێگومان بە توندی دەستی گرتووە بە دەستەگیرەیەکی قایمی بەھێزی وا (کە ئیسلام، یان ئیمانەکەیەتی) کە پچڕان و بەربونی بۆ نیە وە خوا بیسەر و زانایە');
INSERT INTO "bamoki" VALUES(2,257,'2:257','خوا دۆست و پشتیوانی ئەوانەیە کە بڕوادارن دەریان دەھێنێت لە تاریکستانی بێ بڕوایی بۆ ڕووناکی باوەڕ وڕێنمونی وە ئەوانەی کە بێ بڕوان شەیتان دۆستیانە دەریان دەھێنێ لە ڕوناکی (بیر و باوەڕە) وە بۆ تاریکستانی (گومڕایی) ئەوانە ھاوڕێی دۆزەخن تیایا ئەمێننەوە ھەتا ھەتایە');
INSERT INTO "bamoki" VALUES(2,258,'2:258','ئایا ئاوڕێک نادەیتەوە بەلای ئەو (نەمروودە) ی کە دەمەقاڵێی کرد لەگەڵ ئیبراھیم پێغەمبەردا (علیە السلام) دەرحەق بە پەروەردگاری لەبـەر ئەوەی خوا پادشایی دابویە کاتێ کە ئیبراھیم (لە وەڵامی نەمرود دا) ووتی: پەروەردگاری من ئەو زاتەیە ژیان ئەداو ژیان دەستێنێ و (دەمرێنێت) (نەمرود) ووتی: منیش ژیان ئەدەم وە ژیان دەستێنم ودەشمرێنم (دوو زیندانی کراوی ھێنا یەکێکیانی ئازاد کردو ئەوی دیکەیانی کوشت) ئیبراھیم ووتی: بێگومان خوا خۆر لە ڕۆژھەڵاتەوە ھەڵدەھێنێ دە (ئەگەر دەتوانیت) تۆ لە ڕۆژئاواوە ھەڵیبێنە ئەمجا دەم کوت بوو ئەو بێ بڕوایە (نەمروود) وە خوا ڕێنمونی گەلی ستەمکاران ناکات');
INSERT INTO "bamoki" VALUES(2,259,'2:259','یا وەک ئەو کەسە (عوزەیر) کە ڕابورد بەلای شارێکدا (بیت المقدس) لە کاتێکدا ئەو (شارە) دیوارەکانی ڕوخا بوو بەسەر سەربانەکانیدا (عوزەیر) ووتی: خوا چۆن ئەم شارە ئاوەدان دەکاتەوە پاش مردنی؟ (واتە پاش وێران بوونی) یەکسەر خوای گەورە (عوزەیری) سەد ساڵ مراند و پاشان زیندووی کردەوە (خوای گەورە) فەرمووی: چەندە ماویتەوە لێرەدا؟ ووتی ڕۆژێک یا بەشێ لەڕۆژێک ماومەتەوە (خوای گەورە) فەرمووی: نەخێر بەڵکو سەد ساڵ ماویتەوە جا تەماشای خواردن و خواردنەوەکەت بکە تێک نەچووە، وە تەماشای گوێدرێژەکەت بکە (وە زیندوومان کردیتەوە) تا بتگێڕین بە موعجیزە و (بەڵگە) بۆ خەڵکی وە تەماشای ئێسکەکانی (گوێدرێژەکەت) بکە چۆن بەرزیان دەکەینەوە (لە شوێنی خۆیان) و یەکیان دەخەین پاشان گۆشتی بەسەردا دەھێنین ئەمجا کاتێ ئەمانە دەرکەوت بۆی (عوزەیر) ووتی: بەچاکی تێگەیشتم بەڕاستی خوا خاوەن دەستەڵاتە بەسەر ھەموو شتێکدا');
INSERT INTO "bamoki" VALUES(2,260,'2:260','(ئەی موحهمەد بەیادیان بھێنە) کاتێک کە ئیبراھیم ووتی: ئەی پەروەردگارم نیشانم بدە چۆن مردوو زیندوو دەکەیتەوە؟ (خوا) فەرمووی: بڕوات پێ ی نەھێناوە؟ ووتی: بەڵێ بڕوام پێیەتی بەڵام (ئەمەوێ ببینم) بۆئەوەی دڵنیا بم (خوا) فەرمووی: کەواتە چوار مەل بگرە ئەمجا (سەریان ببڕە) و پارچە پارچەیان بکە لەپاشدا ھەر پارچەیەکیان لەسەر شاخێک دابنێ لە پاشان بانگیان بکە بەپەلە دێن بۆلات وە چاک بزانە و دڵنیابە بێگومان خوا باڵادەستی کار بەجێیە');
INSERT INTO "bamoki" VALUES(2,261,'2:261','وێنەی (خێری) ئەوانەی کە ماڵی خۆیان دەبەخشن لە ڕێی خوادا وەک دەنکە (تۆوێک) وایە (ڕوابێت) و حەوت گوڵی گرتبێت لەھەر گوڵێکدا سەد دانەی تێدا بێت وە خوا لەمەش، زیاتری دەکات بۆ ھەر کەسێک کە خۆی بیەوێت وە خوا خاوەن بەھرەی فراوانی ئاگادارە');
INSERT INTO "bamoki" VALUES(2,262,'2:262','ئەوانەی کە ماڵ و دارایی خۆیان دەبەخشن لە ڕێ ی خوادا دوای ئەوە ھیچ منەتێک نانێن و ئازارێک (بە دڵی ھەژاران) ناگەیەنن ئەو کەسانە بۆیان ھەیە پاداشتیان لای پەروەردگاریان وە ھیچ ترسێکیان لەسەر نیە وە ھیچ خەم و خەفەتێکیان نیە');
INSERT INTO "bamoki" VALUES(2,263,'2:263','قسە وگوفتاری جوان وشیرین و چاوپۆشی (لە ھەڵەدا) چاکترە لە خێرێک کە ئازار و دڵ ئێشاندنی بە دوادا بێت خوا دەوڵەمەند و بێ نیاز و لەسەر خۆیە');
INSERT INTO "bamoki" VALUES(2,264,'2:264','ئەی ئەوانەی بڕواتان ھێناوە پوچەڵی مەکەنەوە خێرو بەخشینەکانتان بە منەت نان و ئازاردان و دڵ ئێشان وەک ئەو کەسەی ماڵ و سامانی خۆی دەبەخشێت بۆ ڕووپامایی خەڵک وە باوەڕی نیە بەخوا و بە ڕۆژی دوایی جا وێنەی ئەو ناپاکە وەک بەردێکی ساف و لوس وایە کە گڵێکی کەمی بەسەرەوە بێت ئەمجا بارانێکی بە لێزمە لێی بدات وە بەردەکە بھێڵێتەوە بە ڕوتی ناتوانن دەست بخەنە ھیچ شتێک لەوەی کە کردوویانە وە خوا ڕێنمونی گەلی بێ بڕوا وخوا نەناس ناکات');
INSERT INTO "bamoki" VALUES(2,265,'2:265','نموونەی ئەوانەی کە ماڵ و دارایی خۆیان دەبەخشن لە پێناو ڕەزامەندی خوا وە بۆ دامەزراندنی دڵ و دەرونیان (لەسەر چاکە) وەک باخێک وایە کە بە شوێنێکی بەرزەوەبێت بارانی دڵۆپ گەورەی لێ بدات ئەمجا میوەی خۆی بدات بە دوو ھێندە وە ئەگەر بارانی دڵۆپ گەورەشی لێ نەدات بارانی وورد لێی ئەدات (واتە بێ بەرھەم نابێت) وە خوای گەورەش بینایە بەوەی کە دەیکەن');
INSERT INTO "bamoki" VALUES(2,266,'2:266','ئایا کەس لە ئێوە حەز دەکات کە باخێکی ببێت لەدرەختی خورما وە جۆرەھا درەختی ترێ و ڕەز چەند رووبار بڕوات بەژێریاندا بۆ خاوەن باخەکە ببێت لەناو ئەو باخەدا لە ھەموو جۆرە میوەیەک لەکاتێکدا (خاوەنەکەی) دووچاری ببێت پیری وە بیبێ چەند مناڵێکی ووردی بێ دەسەڵات ئەمجا گەردەلوولێکی لێ ھەڵ بکات گڕو سەموومی تێدا بێ یەکسەر باخەکە بسوتێ ئا بەم ڕەنگە خوا نیشانەکانی (میھرەبانی) خۆیتان بۆ ڕون دەکاتەوە بۆ ئەوەی بیر بکەنەوە');
INSERT INTO "bamoki" VALUES(2,267,'2:267','ئەی ئەو کەسانەی بڕواتان ھێناوە ببەخشن لە پاکترینی ئەوەی پەیداتان کردووە, وە لەوەی کـە بۆمـان دەرھێناون (ڕواندون) لە زەوی مەڕۆن لەوەی ببەخشن کە خراپە لە کاتێکدا کە خۆتان ئەو شتە خراپە وەرناگرن (ئەگەر بتان درێتێ) مەگەر چاوی لێ بپۆشن وە چاک بزانن کەخوا بێ نیازێکی شایستەی ستایشە');
INSERT INTO "bamoki" VALUES(2,268,'2:268','شەیتان دەتان ترسێنێت بە ھەژاری وەفەرمانتان پێدەدات بە خراپە وە خوا بەڵێنی لێخۆشبون و چاکەتان پێدەدات خوا بەھرە فراوان و میھرەبانی زانایە');
INSERT INTO "bamoki" VALUES(2,269,'2:269','ئەی بەخشێت ژیری ودانایی بە ھەر کەسێک کە بیەوێت وە ھەر کەسێک ژیری و دانایی پێدرا ئەوە بەڕاستی پێی دراوە خێر و چاکەیەکی زۆر پەند وەرناگرن تەنھا خاوەن ژیری و ھۆشمەندەکان نەبێت');
INSERT INTO "bamoki" VALUES(2,270,'2:270','ئەوەی بەخشیبێتتان لەچاکە یا نەزرێکتان بڕیار دابێ ئەوە بێگومان خوا ئاگای لێ یە وە بۆ ستەمکاران نیە ھیچ یارمەتی دەرێک');
INSERT INTO "bamoki" VALUES(2,271,'2:271','ئەگەر ئاشکرای بکەن خێرەکانتان ئەوە کارێکی باشە وە ئەگەر بیشارنەوە و بیدەن بە ھەژاران ئەوە باشترە بۆ ئێوە, وە خوا ھەڵی ئەوەرێنێت لەسەرتان ودەیسڕێتەوە ھێندێ لە گوناھەکانتان وە خوا بەوەی کە دەیکەن ئاگادارە');
INSERT INTO "bamoki" VALUES(2,272,'2:272','لەسەر تۆ نیە (ئەی موحەممەد صلى الله عليه وسلم) ڕێنمونی و موسوڵمان کردنی خەڵکی بەڵکو خوا ھەرکەسێکی بوێت ھیدایەتی دەدات وە ئەوەی ئێوە دەیبەخشن لە دارایی (لە چاکە) ئەوە (قازانجەکەی) بۆ خۆتانە ئێوە ھیچ نابەخشن مەگەر بۆ ڕەزای خوا نەبێ وە ئەوەی دەیبەخشن لەسامان و (چاکە) پاداشتەکەیتان دەست دەکەوێتەوە و ئێوە ستەمتان لێ ناکرێت');
INSERT INTO "bamoki" VALUES(2,273,'2:273','(ئەم خێرە) بۆ ئەو ھەژارانەیە کە دەست و پێ بەستراون لە ڕێی خوادا ناتوانن (بۆ ڕۆزی پەیدا کردن) بگەڕێن بە زەویدا نەزانی بێ ئاگا وا ھەست دەکات ئەوان کە دەوڵەمەندن بە ھۆی بێ نیازی یانەوە (سواڵ نەکردنیانەوە) تۆ (ئەی موحەممەد صلى الله عليه وسلم) دەیان ناسیت بە شێوەیاندا داوای ھیچ لەکەس ناکەن بەزۆر (وەک سواڵـکارانی دیکە) وە ئەوەی دەیبەخشن لە ماڵ و سامان بێگومان خوا ئاگادارە پێی');
INSERT INTO "bamoki" VALUES(2,274,'2:274','ئەوانەی کە ماڵ و دارایی خۆیان دەبەخشن بە شەو و بە ڕۆژ بە پەنھانی و بە ئاشکرا ئا ئەوانە ھەر پاداشتیان لای پەروەردگاریانە وە ھیچ ترسێکیان لە سەر نیە (لە داھاتوودا) وە ئەوان خەفەت بار نابن لە ڕۆژی دواییدا');
INSERT INTO "bamoki" VALUES(2,275,'2:275','ئەوانەی کە سوود (ڕیبا) دەخۆن (کاتی زیندوو بوونەوە) ڕاست نابنەوە (لە گۆڕەکانیاندا) مەگەر چۆن کەسێ ڕاست دەبێتەوە کە شەیتان دەستی لێ وەشاندبێت (وە تووشی فێ بووبێت) ئەوە بەھۆی ئەوەوەیە، سوود خۆران ووتیان: کڕین و فرۆشتن وەک سوود وایە لەکاتێکدا خوا ڕێی داوە بە کڕین و فرۆشتن وحەڵاڵی کردووە بەڵام سوود (ڕیبای) قەدەغە کردووە ئەمجا ئەو کەسەی پێی گەیشت ئامۆژگاری یەک لە پەروەردگاری یەوە, وە خێرا دەستی لێ ھەڵگرت ئەوا ئەوەی لەوەپێش وەری گرتووە بۆخۆیەتی (لە سوود خواردن) وە کاری ئەو حەواڵە بەخوایە وە ئەوەی گەڕایەوە (بۆ سوود خواردن) ئا ئەوانە ھاوڕێی ئاگرن تیا دەمێننەوە بە ھەمیشەیی وھەتاھەتایی');
INSERT INTO "bamoki" VALUES(2,276,'2:276','خوا فەڕ و بەرەکەتی سوود ناھێڵێت وە برەو بە خێرو بەخشین ئەدات وزیادی ئەکات وە خوا خۆشی ناوێت ھیچ بێباوەڕ و ناسوپاسگوزارێکی تاوانکار');
INSERT INTO "bamoki" VALUES(2,277,'2:277','بەڕاستی ئەوانەی کە بڕوایان ھێناوە, وە کردەوە باشەکانیان کردوە, وە نوێژیان بەھۆش و بیرەوە کردووە وە زەکاتیان داوە تەنھا پاداشتیان لای پەروەردگاریانە وە ھیچ ترسێکیان لەسەر نیە (لەڕۆژی دواییدا) وە ھیچ خەفەتێک ڕوویان تێ ناکات');
INSERT INTO "bamoki" VALUES(2,278,'2:278','ئەی ئەو کەسانەی بڕواتان ھێناوە (بە خوا) لە خوا بترسن وە دەست ھەڵگرن لەو سودەی کە لای خەڵک ماوتانە ئەگەر ئێوە بڕوادارن');
INSERT INTO "bamoki" VALUES(2,279,'2:279','ئەمجا ئەگەر دەستتان لێ ھەڵنەگرت کەواتە بزانن و ئاگادار بن جەنگ لەگەڵ خوا و پێغەمبەرەکەی ڕادەگەیەنن وە ئەگەر پەشیمان بوونەوە ئەوا تەنھا سەرمایەکانتان ھی ئێوەیە نەستەم بکەن (لە خەڵکی) وە نەستەمتان لێ دەکرێت');
INSERT INTO "bamoki" VALUES(2,280,'2:280','وە ئەگەر قەرزدار نەدار بوو ئەوە با مۆڵەتی بدرێت تا خوا دەسەڵاتی دەدا وە ئەگەر (لە قەرزەکە) یان خۆش بن باشترە بۆتان ئەگەر تێ بگەن');
INSERT INTO "bamoki" VALUES(2,281,'2:281','وە بترسن لـە ڕۆژێک دەگەڕێنرێنەوە بۆ لای خوا لەپاشان پاداشت ئەدرێتەوە بە ھەموو کەسێک (پاداشت و سزای) ئەوەی کردوەیەتی وە ئەوان ستەمیان لێ ناکرێ');
INSERT INTO "bamoki" VALUES(2,282,'2:282','ئەی ئەوانەی باوەڕتان ھێناوە ئەگەر قەرزێکتان لەیەکتری کرد بۆ ماوەیەکی ناوبراو (دیاریکراو) ئەوە بینووسن وە با بینوسێ لە نێوانتاندا نووسەرێک بەڕاستی و (دادوەری) وە ھیچ نووسەرێک با سەرپێچی نەکات لە نوسینی (سەنەدی قەرز) دا ھەروەک خوا ئەوی فێری نووسین کردووە با بینوسێ وە با ئەو کەسە دەقی سەنەدەکە بڵێ وبیخوێنێتەوە کە قەرزەکەی بەسەرەوەیە وە لە پەروەردگاری بترسێ وە ھیچی لێ کەم نەکاتەوە ئەمجا ئەگەر ئەو کەسەی قەرزەکەی بەسەرەوەیە بێ عەقڵ بوو یان ناتەواو بوو یا نەیدەتوانی خۆی دەقی سەنەدەکە بڵێت وبخوێنێتەوە ئەوە با گەورەکەی بیڵێت وبخوێنێتەوە بەڕاستی و (دادپەروەری) وە دوو شاھید بگرن لە پیاوانی خۆتان (لە موسوڵمانان) ئەمجا ئەگەر دوو پیاو نەبوو ئەوە با پیاوێک و دوو ژن بن لەوانەی کە پەسەندیان دەکەن لە شاھیدەکان بۆ ئەوەی ئەگەر یەکێکیان لەبیری چوو با ئەوی دیکەیان بیری بخاتەوه با شایەتەکان سەرپێچی نەکەن کاتێک بانگ کران (بۆ شایەتیدان) وە بێزار مەبن لەوەی کە بینوسن بچووک بێت یان گەورە تا کاتی خۆی ئەم نوسینەتان ڕاسترە بەلای خواوە، وە چەسپێنەر ترە بۆ شایەتی دانەکە وە نزیکترە لەوەی کە نەکەونە گومانەوە مەگەر کاتێک ئەو کردەوەیە بازرگانیەکی دەست بە دەست بێت و بیگێڕن لە ناو خۆتاندا ئەوە لەم کاتەدا گوناھتان ناگات ئەگەر نەینووسن وە شاھید بگرن کاتێک کڕین و فرۆشتنتان کرد و با زیان بە نووسەر و شاھید نەگەیەندرێت وە ئەگەر زیانتان گەیاند ئەوە بێگومان گوناھتان دەگات وە لەخوا بترسن وە خوا فێرتان دەکات وە خوا بە ھەموو شتێک زانا وئاگادارە');
INSERT INTO "bamoki" VALUES(2,283,'2:283','ئەگەر لەسەر سەفەر بوون و دەستتان نەکەوت نووسەرێک ئەوە بارمتەیەک وەرگرن جا ئەگەر ھەندێکتان لە ھەندێکتان دڵنیا بوون با ئەو کەسەی کە بە ئەمین زانراوە سپاردەکە بداتەوە, وە با لە پەروەردگاری بترسێت وە شایەتی مەشارنەوە ئەو کەسەیشی بیشارێتەوە ئەوە بەڕاستی بە دڵ گوناھبارە وە خوا ئاگادارە بە ھەموو کردەوەیەکتان');
INSERT INTO "bamoki" VALUES(2,284,'2:284','ھەر ھی خوایە ئەوەی لە ئاسمانەکانایە و ئەوەی لە زەویدایە ئەگەر ئاشکرای بکەن ئەوەی لە دڵتاندایە یان بیشارنەوە خوا محاسەبەتان دەکات لەسەری ئەوسا خۆش دەبێت لەو کەسەی کە بیەوێت وە سزای کەسێک دەدات کە بیەوێت وە خوا بەدەسەڵات و بەتوانایە بەسەر ھەموو شتێکدا');
INSERT INTO "bamoki" VALUES(2,285,'2:285','(پێغەمبەر صلى الله عليه وسلم) بڕوای ھێنا بەو نامەیەی کە بۆی نێرراوە لە پەروەردگاریەوە لەگەڵ بڕواداراندا ھەموو بڕوایان ھێناوە بەخوا وە بە فریشتەکانی وە بە نامەکانی وە بە پێغەمبەرانی جیاوازی ناکەین لە نێوان ھیچ یەکێک لە پەیامبەرانی (بڕواداران) ووتیان: (پێغەمبەر ئاینی خوای پێ گەیاندین ئێمەش) بیستمان فەرمانبەرداریمان کرد داوای لێخۆشبوونت لێدەکەین ئەی پەروەردگارمان وە ھەر بۆ لای تۆیە گەڕانەوە');
INSERT INTO "bamoki" VALUES(2,286,'2:286','خوا داوا لە ھیچ کەسێک ناکات مەگەر بە ئەندازەی توانای خۆی (سوودی) بۆ خۆی ھەیە ھەر چاکەیەک بکات (با ھەر بەدڵیش بێت) وە (زیانی) بۆ خۆیەتی ھەر خراپەیەک بکات ئەی پەروەردگارمان لێمان مەگرە ئەگەر کردەوەیەکی پێویستمان لەبیرچوو یا بەھەڵە گوناھێکمان کرد ئەی پەروەردگار لەسەرمان دامەنێ باری قورس وەک ئەوەی داتنا لەسەر ئەوانەی پێش ئێمە (ئوممەتانی پێشووتر) ئەی پەروەردگارمان بەڵای وامان بەسەردا مەدە کە بۆمان ھەڵنەگیرێت وە گوناھەکانمان بسڕەرەوە, وە لێمان خۆشبە وە میھرەبانیمان لەگەڵدا بکە ھەر تۆی یاریدەدەرو پشتیوانمان کەواتە سەرمان بخە بەسەر گەلی بێ بڕوایاندا');
INSERT INTO "bamoki" VALUES(3,1,'3:1','بـە ( ئـەلیف، لام، میم ) دەخوێنرێتەوە، خوای گەورە زاناترە بە واتاکەی.ئەم پیتانەی کە لەسەرەتای ھەندێک لەسورەتەکانی قورئاندا ھاتوون، تەنھا خوای گەورە دەزانێت مەبەستی پێیان چیە، ئەمە ڕاسترین تەفسیرە بۆ ئەم پیتانە کرابێت، بەڵام بەچەند جۆرێکی تریش تەفسیرکراون لەوانە: ئەم پیتانە ناون بۆ ئەوسورەتانەی لەسەرەتایانەوە ھاتوون، یان ناون بۆ خوای گەورە، یان بۆ دەستەوسانکردنی عەرەبە بێباوەڕەکانە کە دەیانووت ئەم قورئانە لەلایەن خوداوە نەھاتوەتە خوارەوە بۆ موحەممەد (صلى الله عليه وسلم) پێیان دەڵێت: دەی ئەگەر وایە وێنە وھاوشێوەی ئەم قورئانە بھێنن، کە ھەر لەو پیتانە پێـکھاتووە کە ئێوە قسەی پێ دەکەن و بەکاریان دەھێنن');
INSERT INTO "bamoki" VALUES(3,2,'3:2','ناردوویە تیە خوارەوە بۆت قورئان بەڕاستی و دروستی بەڕاست دانەری ھەموو ئەو (کتێبانەیە) لەپێشیدا بوون وە تەورات و ئینجیلی ناردە خوارەوە');
INSERT INTO "bamoki" VALUES(3,3,'3:3','ناردوویە تیە خوارەوە بۆت قورئان بەڕاستی و دروستی بەڕاست دانەری ھەموو ئەو (کتێبانەیە) لەپێشیدا بوون وە تەورات و ئینجیلی ناردە خوارەوە');
INSERT INTO "bamoki" VALUES(3,4,'3:4','لەپێش قورئاندا بەڕێنمونی خەڵکی وە ناردی یە خوارەوە فورقان (واتە: جیاکەرەوەی ڕاست و ناڕاست_ مەبەست پێی قورئانە) بێگومان ئەوانەی کە بێ بڕوابوون بە ئایەت و بەڵگەکانی خوا بۆیان ھەیە (لە قیامەتدا) سزای سەخت وە خوا باڵا دەست و بەتوانایە خاوەنی تۆڵە و سزایە');
INSERT INTO "bamoki" VALUES(3,5,'3:5','بەڕاستی خوا ھیچ شتێکی لێ وون نابێت. نە لەزەوی وە نە لەئاسماندا');
INSERT INTO "bamoki" VALUES(3,6,'3:6','ئەو خوایە زاتێکە شێوەتان ئەکێشێ لە منداڵدانەکاندا چۆنی بووێ ھیچ پەرستراوێکی بەھەق و ڕاست نیە تەنھا ئەو نەبێت زاڵی باڵادەستی کار بەجێیە');
INSERT INTO "bamoki" VALUES(3,7,'3:7','ئەو خوایە ناردوویەتی یە خوارەوە بۆت قورئان ھەندێکی لەئایەتەکانی (مەبەستەکانیان) ڕۆشنن ئەو ئایەتانە بنەڕەتی قورئانن وە ئەوانی تر (کۆمەڵە ئایەتێکن کە مەبەستەکانیان) ڕۆشن نین ئەمجا ئەوکەسانەی کە لە دڵیاندا لادان و چەوتی ھەیە دوای ئەو ئایەتانە دەکەون کە لە قورئاندا موتەشابیھن (ھاوشێوەن، واتاکانی ڕۆشن نین) بۆ تێکدانی بیروباوەڕی موسوڵمانان وە بۆ ڕاڤەکردنی بە ئارەزووی ناپاکی خۆیان لە حاڵەتێکدا کەس ڕاڤە کردنی نازانێت تەنیا خوا نەبێت وە زانایان و ئەوانەی دامەزراون لە زانینی عیلمی شەرعیدا ئەڵێن ئێمە بڕوامان ھێناوە بە (موتەشابیھەکان) ھەمووی لە پەروەردگارمانەوەن بیر لە قورئان ناکەنەوە تەنھا خاوەنانی ژیریی و بیر وھۆش نەبێت');
INSERT INTO "bamoki" VALUES(3,8,'3:8','ئەڵێن ئەی پەروەردگارمان لای مەدە (لە ڕێی ڕاست) دڵەکانمان دوای ئەوەی کە ڕێنمونیت کردین وە پێمان ببەخشە لە لای خۆتەوە بەزەییەک (میھرەبانیەک) بەڕاستی ھەر تۆی بەخشندە');
INSERT INTO "bamoki" VALUES(3,9,'3:9','ئەی پەروەردگارمان بێگومان تۆ کۆکەرەوەی خەڵکیت بۆ ڕۆژێک کە ھیچ گومانی تێدا نیە بەڕاستی خوا بەڵێنی خۆی ناشکێنێت و دەیباتە سەر');
INSERT INTO "bamoki" VALUES(3,10,'3:10','بێگومان ئەوانەی بێ بڕوا بوون ھەرگیز کەڵک و سوودیان پێ ناگەیەنێت نە ماڵیان وە نە منداڵیان لە (سزای) خوا بەھیچ شێوەیەک وە ئا ئەوانە سووتەمەنی دۆزەخن');
INSERT INTO "bamoki" VALUES(3,11,'3:11','(داب و باوی ئەم بێ بڕوایانە) وەک داب ونەریتی دەست و پێوەندەکانی فیرعەونە و ئەوانەی لەپێش ئەواندا بوون وایە کە نیشانەکانی ئێمەیان بە درۆزانی ئەمجا خواش گرتنی بەھۆی تاوانە کانیانەوە, وە خوا تۆڵەی سەختە');
INSERT INTO "bamoki" VALUES(3,12,'3:12','بڵێ: بەوانەی کە بێ بڕوان لەمەولا ئەشکێن و ژێر دەکەون وە کۆدەکرێنەوە بەرەو دۆزەخ کە ناخۆشترین شوێنە');
INSERT INTO "bamoki" VALUES(3,13,'3:13','بێگومان نیشانەیەکی گرنگ ڕویدا بۆ ئێوە (ئەی موسوڵمانینە یان ئەی کافرەکان) لەو دوو دەستەی (کە لە جەنگی بەدردا) دایان بەیەکدا دەستەیەکیان دەجەنگین لە ڕێی خوادا وە ئەوەی تریان بێ بڕوابوون موسوڵمانەکانیان ئەدی بەدوو ئەوەندەی خۆیان بە بینینی چاو وە خوا پشتگیری دەکات بە یارمەتی خۆی بۆ ھەرکەس بیەوێت بەڕاستی لەم یارمەتیدانەدا پەند وئامۆژگاری ھەیە بۆ کەسانی خاوەن ژیری و بیروھۆش');
INSERT INTO "bamoki" VALUES(3,14,'3:14','جوان کراوە وڕازێنراوەتەوە بۆ خەڵکی خۆشی و ئارەزوەکانی نەفس لە ئافرەتان و کوڕ وکاڵی زۆر و دارایی زۆری کەڵەکە کراو لە ئاڵتون و زێڕ و زیو و ئەسپ و ماینانی نیشانەدار (بەسپێتی ناوچەوان و ھەر چوار پەلی) ھەروەھا ئاژەڵ و چوارپێ و کشتوکاڵ ئەمانە بەھرە و (کەل و پەلی) (زیندەگانی(ژیانی) دونیان وە لای خوا ھەیە خۆشترین جێگا و پاشەڕۆژ');
INSERT INTO "bamoki" VALUES(3,15,'3:15','بڵێ: خەبەرتان بدەمێ بەباشتر لەمانەتان بۆ ئەوانەی کە پارێزیان کرد و لەخودا ترسان لای پەروەردگاریان چەند بەھەشتێک ھەیە کە بەژێریاندا دەڕوات چەند ڕووبارێک بەھەمیشەیی و ھەتا ھەتایی تێیدا دەمێننەوە, وە (بۆیان ھەیە) ژنانی پاکیزە وە ڕەزامەندی لە خواوە, وە خوا بینایە بە بەندەکان');
INSERT INTO "bamoki" VALUES(3,16,'3:16','(لە خوا ترسان) ئەوانەی کە دەڵێن ئەی پەروەردگارمان بەڕاستی ئێمە بڕوامان ھێنا دەی خۆش ببە لە گوناھەکانمان وە بمانپارێزە لە سزای ئاگری دۆزەخ');
INSERT INTO "bamoki" VALUES(3,17,'3:17','(ئەوانە بریتین لە) ئارام گران و ڕاستگۆیان و فەرمانبەردارانی خودا و دارایی وماڵبەخشن و ئەوانەی کە داوای لێ خۆشبوون دەکەن لە پێش بەیانیاندا');
INSERT INTO "bamoki" VALUES(3,18,'3:18','خوا شایەتی ئەدات (واتە ڕایدەگەیەنێت و بەبەڵگە دەیچەسپێنێت) کە بەڕاستی ھیچ پەرستراوێکی بەھەق وڕاست نیە ئەو نەبێت وە فریشتەکانیش شایەتی ئەدەن (واتە: ئیقرار ئەکەن) وە خاوەن زانستەکان (زانایان) یش شایەتی ئەدەن (واتە: باوەڕی چەسپاویان ھەیە) (خوا) ھەمیشە دادپەروەری ڕاگرتوە ھیچ پەرستراوێکی ڕاست نیە جگە لە خوا کە باڵادەستی کاربەجێیە');
INSERT INTO "bamoki" VALUES(3,19,'3:19','بەڕاستی دینی پەسەندکراو لای خوا (ھەر) ئیسلامە دوو بەرەکی نەکەوتنە ناو ئەوانەی کە نامەیان بۆ ھاتبوو دوای ئەوە نەبێت کە زانینی بەڕاستیان بۆ ھات (ئەم لادانەشیان) لە ڕوی ستەم و حەسادەتەوە بوو لە نێو خۆیاندا جا ھەر کەسێک بێ بڕوا بێت بە ئایەتەکانی خوا (کە قورئانە) ئەوە بەڕاستی خوا زوو حسابی لەگەڵ دەکات');
INSERT INTO "bamoki" VALUES(3,20,'3:20','ئەمجا ئەگەر(خاوەن نامەکان) دەمەقاڵێ یان لەگەڵ کردیت (ئەی موحەممەد صلى الله عليه وسلم) تۆش بڵێ ڕووی خۆم سپارد بەخوا (وملکەچی ئەوم) لەگەڵ ئەوانەی پەیڕەوی من ئەکەن وە (ئەی موحەممەد صلى الله عليه وسلم) بڵێ بەوانە کە نامەی تەورات و ئینجیلیان دراوەتێ(کە جولەکە و گاورەکانن) وە بە نەخوێندەوارەکان (ھاوبەش پەیداکەرەکانی گەلی عەرەب) ئایا ئێوە موسوڵمان بوون؟ ئەمجا ئەگەر موسوڵمان بوون ئەوە بەڕاستی مانای ئەوەیە کە ھیدایەتیان وەرگرتووە، وە ئەگەر ڕووشیان وەرگێڕا ئەوە بەڕاستی تۆ ھەر ڕاگەیاندنت لەسەرە وە خوا بینایە بە بەندەکان');
INSERT INTO "bamoki" VALUES(3,21,'3:21','بێگومان ئەوانەی بێ بڕوان بە ئایەتەکانی خوا (بە نیشانەکانی خوا) وە پێغەمبەران ئەکوژن بە ناڕەوا وە ئەو کەسانە ئەکوژن کە فەرمان ئەدەن بە دادگەری لە خەڵکی ئەوە مژدەیان بدەرێ (وەک سووکایەتی کردن وایە) بە سزای پڕ لە ئازار');
INSERT INTO "bamoki" VALUES(3,22,'3:22','ئەوانە کەسانێکن پووچ بووەتەوە کردەوەکانیان لە دونیا وە لە ڕۆژی دواییدا وە ئەوان نیانە ھیچ یارمەتیدەرێک');
INSERT INTO "bamoki" VALUES(3,23,'3:23','ئایا ورد نابیتەوە لەوانەی پێیان دراوە بەشێک لە تەورات بانگ دەکرێن بۆ لای کتێبی خوا (تەورات)بۆ ئەوەی بڕیاربدات لە نێوانیاندا لە پاشان دەستەیەک لەوان پشت ھەڵدەکەن لە حاڵێکدا ئەوان ڕوو وەرگێڕن');
INSERT INTO "bamoki" VALUES(3,24,'3:24','ئەمە بەھۆی ئەوەوەیە کە بێگومان ئەوان دەیانووت: ھەرگیز بەر لەشمان ناکەوێت ئاگر تەنھا چەند ڕۆژێکی ژمێررا و کەم نەبێت وە خەڵەتاندنی و (فریویدان) لە دینەکەیاندا ئەو درۆیانەی کە ھەڵیان دەبەست');
INSERT INTO "bamoki" VALUES(3,25,'3:25','ئەمجا دەبێ حاڵیان چۆن بێت ئەو کاتەی کە کۆیان دەکەینەوە لە ڕۆژێکدا کە گومانی تێدا نیە و پاداشتی تەواوی دەدرێتەوە ھەموو کەسێک ئەوەی کردویەتی وە ئەوان ھیچ ستەمێکیان لێ ناکرێت');
INSERT INTO "bamoki" VALUES(3,26,'3:26','بڵێ ئەی خوایە خاوەن دەسەڵات دەسەڵات دەدەیت بە ھەر کەسێک کە بتەوێت وە دەسەڵات ئەسێنیتەوە لە ھەر کەسێک کە بتەوێت وە بەڕێزی دەکەیت ئەوەی بتەوێت وە بێ ڕێزی دەکەیت ئەوەی بتەوێت تەنھا بەدەستی تۆیە ھەموو چاکەیەک بەڕاستی تۆ بەسەر ھەموو شتێکدا بە توانایت');
INSERT INTO "bamoki" VALUES(3,27,'3:27','شەو دەکەیت بەناو ڕۆژدا وە ڕۆژ دەکەیت بەناو شەودا وە زیندوو دەردێنیت لە مردوو وە مردوو دەردێنیت لەزیندوو وە ڕۆزی دەدەیت بە ھەر کەسێ کە بتەوێت بەبێ حساب و بێشومار');
INSERT INTO "bamoki" VALUES(3,28,'3:28','نابێت بڕواداران بێباوەڕان بگرن بە دۆستی خۆیان لەجیاتی بڕواداران وە ھەر کەسێک وابکات ئیتر پەیوەستی لەگەڵ خوادا نامێنێت مەگەر بۆ خۆپاراستنتان بێت لێیان (بەشێوەیەک کە بە دەم بیڵێن و بەدڵ باوەڕتان پێی نەبێت) وە خوا دەتان ترسێنێت لە سزای خۆی وە ھەر بۆ لای خوایە گەڕانەوە');
INSERT INTO "bamoki" VALUES(3,29,'3:29','(ئەی موحەممەد صلى الله عليه وسلم) بڵێ: ئەگەر بیشارنەوە ئەوەی لە دڵتاندایە یان ئاشکرای بکەن خوا پێی دەزانێت وە دەیزانێت ئەوەی لە ئاسمانەکان وە ئەوەی لەزەویدایە وە خوا بەسەر ھەموو شتێکدا بە توانایە');
INSERT INTO "bamoki" VALUES(3,30,'3:30','(بترسن) لەو ڕۆژەی ھەموو کەسێک ئەبینێت ھەر کارێکی باشی کردبێت ئامادەیە وە ھەر کارێکی خراپی کردبێت ئاوات دەخوازێت کە لە نێوان ئەو و کارە خراپەکەیدا ماوەیەکی زۆر دوور ببوایە وە خوا دەتان ترسینێت لە سزای خۆی وە خوا میھرەبانە بە بەندەکانی خۆی');
INSERT INTO "bamoki" VALUES(3,31,'3:31','(ئەی موحەممەد صلى الله عليه وسلم) بڵێ: ئەگەر ئێوە خواتان خۆش دەوێت کەواتە دوای من بکەون تا خواش ئێوەی خۆش بوێت وە لە گوناھەکانتان خۆش ببێت وە خوا لێخۆشبووی میھرەبانە');
INSERT INTO "bamoki" VALUES(3,32,'3:32','بڵێ: فەرمانبەردار وگوێڕایەڵی خوا و پێغەمبەر بن ئەمجا ئەگەر پشت ھەڵکەن ئەوە بێگومان خوا بێ بڕوایانی خۆش ناوێت');
INSERT INTO "bamoki" VALUES(3,33,'3:33','بێگومان خوا ھەڵی بژاردووە ئادەم و نوح وە خانەوادەی ئیبراھیم وە خانەوادەی عیمران بەسەر ھەموو جیھانیاندا');
INSERT INTO "bamoki" VALUES(3,34,'3:34','نەوەیەکن ھەندێکیان لە ھەندێکیان کەوتوونەتەوە, وە خوا بیسەر و زانایە');
INSERT INTO "bamoki" VALUES(3,35,'3:35','(بێنەوە بیر) کاتێک کە ژنی عیمران ووتی ئەی پەروەردگارم بێگومان من نەزرم کردوە بۆ تۆ ئەوەی کە لە سکم دایە ئازادکراو بێت (بۆ خزمەتی خانەی تۆ کە بیت المقدسە) دە تۆش (ئەم نەزرەم) لێ گیرا بکە بەڕاستی تۆ بیسەری زانایت');
INSERT INTO "bamoki" VALUES(3,36,'3:36','جا کاتێک کە سکەکەی دانا ووتی: ئەی پەروەردگارم خۆ من کچم بوو بەڕاستی خوا زاناترە بەوەی کە بوویەتی کوڕیش وەک کچ نیە (مەبەست لەوەیە لەو کاتەدا کوڕان دەکران بە نەزر بۆ خزمەتکردنی بیت المقدس) وە بێگومان منیش ناوم نا مەریەم وە من خۆی و نەوەی ئەپارێزم بە تۆ لە شەیتانی دەرکراو');
INSERT INTO "bamoki" VALUES(3,37,'3:37','ئەمجا پەروەردگار (نەزرەکەی) مەریەمی وەرگرت بەوەرگرتنێکی جوان وە ڕواندی (پەروەردەی کرد) بە ڕواندنێک (پەروەردەیەکی) جوان وە زەکەرییا دەستەبەریی کرد (ئەرکی ژیانی گرتە ئەستۆ و خودا کردی بەمامۆستای مەریەم) ھەر کات زەکەریا بڕۆشتایە بۆ لای مەریەم لە شوێنی خواپەرستیەکەیدا خواردەمەنی وڕزق و ڕۆزی لەلا دەبینی (زەکەریا) ووتی: ئەی مەریەم ئەمەت لە کوێ بوو؟ (مەریەم) ووتی: ئەوە لەلایەن خواوەیە بەڕاستی خوا ڕۆزی دەدات بە ھەر کەسێک کە بیەوێت بە بێ حساب و بێژمار');
INSERT INTO "bamoki" VALUES(3,38,'3:38','ئا لەو کاتەدا پاڕایەوە زەکەرییا لە پەروەردگاری ووتی: ئەی پەروەردگارم پێم ببەخشە لەلایەنی خۆتەوە کوڕێکی سروشت پاک بەڕاستی تۆ بیسەری پاڕانەوەیت');
INSERT INTO "bamoki" VALUES(3,39,'3:39','جا فریشتە بانگی کرد لەکاتێکدا کە زەکەرییا لە میحرابەکەدا وەستابوو نوێژی دەکرد بەڕاستی خوا مژدەت دەداتێ بە (کوڕێک ناوی) یەحیایە کە بڕواھێنەره بە عیسای ووتەی خوا و پێشەوایە وە جڵەوگری نەفسی خۆیەتی وە یەکێکە لە پێغەمبەرانی چاککردار');
INSERT INTO "bamoki" VALUES(3,40,'3:40','(زەکەرییا) ووتی: ئەی پەروەردگارم من چۆن کوڕم دەبێت لە حاڵێکدا بەڕاستی پیری یەخەگییرم بووە وە ژنەکەشم نەزۆکە؟ (خوای گەورە) فەرمووی ئیشی خوا وایە چی بوێت دەیکات');
INSERT INTO "bamoki" VALUES(3,41,'3:41','(زەکەرییا) ووتی ئەی پەروەردگارم نیشانەیەکم بۆ دابنێ (خوای گەورە)فەرمووی: نیشانەکەت ئەوەیە کە ناتوانیت قسە لەگەڵ خەڵکیدابکەیت سێ ڕۆژ مەگەر بە ئاماژە (ھێما) نەبێت وە زۆر یادی پەروەردگارت بکە و تەسبیحاتی خوا بکە بە ئێواران و بەیانیان');
INSERT INTO "bamoki" VALUES(3,42,'3:42','(یادی ئەوە بکە) کاتێک کە فریشتە (جیبریل) ووتی: ئەی مەریەم بێگومان خوا تۆی ھەڵبژارد و پاکی کردیتەوە (لە ھەمووخووڕەوشتێکی ناشیرین) وە ھەڵی بژاردوی بەسەر ھەموو ژنانی جیھاندا');
INSERT INTO "bamoki" VALUES(3,43,'3:43','ئەی مەریەم فەرمانبەرداری پەروەردگارت بکە وە سوژدەو کڕنووش بەرە لەگەڵ ئەوانەی کە سوژدەو کڕنوش دەبەن');
INSERT INTO "bamoki" VALUES(3,44,'3:44','ئەوەی ووترا لە ھەواڵە پەنھانەکانن بە وەحی بۆت دەنێریین (ئەی موحەممەد صلى الله عليه وسلم) وە تۆ لە لای ئەوان نەبویت کاتێک کە قەڵەمەکانیان ھاوێشت کامیان دەستەبەرێتی وسەرپەرشتی وبەخێوکردنی مەریەمیان بەردەکەوێت وە تۆ لەلایان نەبوویت کە دەمەقاڵێیان دەکرد (لەم ڕووەوە)');
INSERT INTO "bamoki" VALUES(3,45,'3:45','کاتێک کە فریشتەکە ووتی: ئەی مەریەم بێگومان خوا مژدەت دەداتێ بە ووتەیەک لەوەوە کە ناوی مەسیح (عیسا) ی کوڕی مەریەمە بەڕێزو پایەبەرزە لە ھەردوو جیھاندا وە لە نزیکانی خودایە');
INSERT INTO "bamoki" VALUES(3,46,'3:46','وە قسە دەکات لەگەڵ خەڵکیدا لە بێشکەداو لە (کاتی) گەورەیی دا وە لە پیاو چاکانیشە');
INSERT INTO "bamoki" VALUES(3,47,'3:47','مەریەم ووتی: ئەی پەروەردگارم من چۆن منداڵم دەبێت لە کاتێکدا کە پیاو نەھاتۆتە لام (لێم نەکەوتوە) ؟ (جیبریل) فەرمووی وایە (کە ئەڵێیت) خوا چی بوێت دروستی دەکات کاتێک کە بڕیاری شتێکی دا ھەر ئەوەندەی دەوێت پێ بڵێت ببە، ئیتر دەبێت');
INSERT INTO "bamoki" VALUES(3,48,'3:48','وە خوا فێری کتێب و دانایی وتەورات و ئینجیلی دەکات');
INSERT INTO "bamoki" VALUES(3,49,'3:49','وە پێغەمبەر (دەنێرێت) بۆ بەنو ئیسرائیل (نەوەکانی یەعقوب) (پێ یان دەڵێت) بەڕاستی من نیشانەیەکم بۆ ھێناون لە پەروەردگارتانەوە کە من بۆتان دروست دەکەم لە قوڕ (شتێک) لە وێنەی باڵندە ئەمجا فوی پیادەکەم یەکسەر دەبێت بە باڵندە بە فەرمانی خوا وە چاکی ئەکەمەوە کوێری زکماک و بەڵەک وە مردوانیش زیندوو دەکەمەوە بە فەرمانی خوا وە خەبەرتان دەدەمێ (لە خواوە) چی ئەخۆن چی بە زەخیرە ھەڵئەگرن لە ماڵەکانتاندا بەڕاستی ھەیە لەم موعجیزانەدا بەڵگە ونیشانەی بەھێز بۆ ئێوە (لەسەر ڕاستی پێغەمبەریەتی من) ئەگەر ئێوە بڕوادارن');
INSERT INTO "bamoki" VALUES(3,50,'3:50','وە (من ھاتووم) بە بڕوا ھێنەر بەو تەوراتەی کەلە بەردەستمدایە وە ھاتووم بۆ ئەوەی حەڵاڵ بکەم ھەندێک لەو شتانەی کە قەدەغە کراوە لێتان وە بەڵگەم بۆ ھێناون لەلای پەروەردگارتانەوە کەواتە بترسن لە (بێ فەرمانی) خوا وەفەرمانبەرداریم بکەن');
INSERT INTO "bamoki" VALUES(3,51,'3:51','بە ڕاستی خوا پەروەردگاری من و ئێوەیە دەی بیپەرستن ئا ئەمەیە ڕێگای ڕاست');
INSERT INTO "bamoki" VALUES(3,52,'3:52','ئەمجا کاتێک کە عیسا ھەستی کرد بە بێ بڕوایی گەلەکەی ووتی: کێ لەگە ڵ مندا یارمەتی دەری دینی خوایە شاگردەکانی (حەوارییەکانی) ووتیان ئێمە یارمەتی دەری دینی خواین بڕوامان بە خوا ھێناوە و تۆ ئەی عیسا شاھید بە کە ئێمە گوێڕایەڵ وملکەچی ئەوین');
INSERT INTO "bamoki" VALUES(3,53,'3:53','ئەی پەروەردگارمان ئێمە باوەڕمان ھێنا بەوەی کە ناردووتەتە خوارەوە, وە پەیڕەوی پێغەمبەری تۆمان کرد دەی خوایە ناومان بنووسە (بمانژمێرە) لە دەفتەری بڕوادارانی (یەکتاپەرست) دا');
INSERT INTO "bamoki" VALUES(3,54,'3:54','(جولەکەکان) پیلانیان گێڕا (بۆ کوشتنی عیسا) خواش لە بەرانبەردا پیلانی لێ گێڕان وەخواش چاکترین پیلان گێرە (لەبەرانبەر پیلان گێڕاندا بۆ پووچەڵکردنەوەی فڕوفێڵەکانیان)');
INSERT INTO "bamoki" VALUES(3,55,'3:55','(بیھێنەرەوە یادت) کاتێ خوا فەرمووی ئەی عیسا من تۆ دەبەمەوە و بەرزت دەکەمەوە بۆ لای خۆم وە پاک کەرەوەی تۆم لە (ھاودەمی) ئەوانەی کە بێ باوەڕ بوون وە پەیڕەوانی (ڕاستەقینەی) تۆ بەرز دەکەمەوە بەسەر بێ بڕوایاندا تاڕۆژی دوایی پاشان گەڕانەوەتان بۆ لای منە ئەمجا بڕیار دەدەم لە نێوانتاندا لەو شتانەی کە تیایاندا جیاوازو ناکۆکن');
INSERT INTO "bamoki" VALUES(3,56,'3:56','جا ئەوانەی بێ بڕوابوون سزایان دەدەم بەسزای سەخت لە دونیاو ڕۆژی دوایشدا وە ئەوانە ھیچ یارمەتیدەرێکیان نی یە');
INSERT INTO "bamoki" VALUES(3,57,'3:57','بەڵام ئەوانەی کە بڕوایان ھێناوە, وە کردەوە باشەکانیان کردووە ئەوە خوا بەتەواوی پاداشتیان دەداتەوە بێگومان خوا ستەمکارانی خۆش ناوێت');
INSERT INTO "bamoki" VALUES(3,58,'3:58','ئەم بەسەرھاتانەی کە ئەیان خوێنینەوە بەسەرتدا (ئەی موحەممەد صلى الله عليه وسلم) لە ئایەت وبەڵگە ونیشانەکان و قورئانی پڕ لە حیکمەتن');
INSERT INTO "bamoki" VALUES(3,59,'3:59','بەڕاستی وێنەی (دروست بوونی) عیسا لای خوا وەک وێنەی ئادەم وایە کە دروستی کرد لە گڵ لە پاشان پێی ووت ببە، ئەویش بوو (بەمرۆڤ)');
INSERT INTO "bamoki" VALUES(3,60,'3:60','قسەی ڕاست ئەوەیە کە لە خواوە بەتۆ گەیشت کەواتە لە گومانداران مەبە');
INSERT INTO "bamoki" VALUES(3,61,'3:61','ئەمجا ھەر کەسێک دەمە قاڵێی لەگەڵ کردیت (لە بارەی عیساوە) دوای ئەو زانینەی کە (بەوەحی) بۆت ھات تۆش پێیان بڵێ وەرن با بانگی کوڕەکانمان و کوڕەکانتان بکەین وە (بانگی) ژنەکانمان و ژنەکانتان بکەین وە (بانگی) خۆمان و خۆتان بکەین لە پاشاندا (ھەموومان) لەخوا بپاڕێینەوە ئەمجا نەفرەتی خوا بکەین لە وانەمان کە لەدرۆزنانین');
INSERT INTO "bamoki" VALUES(3,62,'3:62','بێگومان ھەر ئەمەیە چیرۆک و داستانی ڕاست و دروست وە ھیچ پەرستراوێکی بەڕاستی نی یە تەنھا خوا نەبێ بەڕاستی خوا تەنھا خۆی باڵادەستی کار دروستە');
INSERT INTO "bamoki" VALUES(3,63,'3:63','ئەمجا ئەگەر پشتیان ھەڵکرد. ئەوە بەڕاستی خوا ئاگادارە بە کردەوەی بەدکاران ( گەندەڵکاران)');
INSERT INTO "bamoki" VALUES(3,64,'3:64','(ئەی موحەممەد صلى الله عليه وسلم) بڵێ: ئەی خاوەن نامەکان وەرن بۆ لای ووتەیەکی ڕاست و (یەکسان) لە نێوان ئێمە و ئێوەدا کە جگە لەخوا ھیچ نەپەرستین وە ھیچ شتێک نەکەین بە ھاوبەشی وە ھەندێکمان ھەندێکمان بە پەروەردگار دانەنێین ئەمجا گەر پشتیان ھەڵکرد ئێوەش بڵێن: ئێوە شاھید بن کە ئێمە فەرمانبەردار وگوێڕایەڵ وملکەچی خوداین');
INSERT INTO "bamoki" VALUES(3,65,'3:65','ئەی خاوەن نامەکان بۆچی دەمەقاڵێ دەکەن دەربارەی ئیبراھیم لە کاتێکدا کە تەورات و ئینجییل نەنێرراونەتە خوارەوە لە دوای ئیبراھیم نەبێت دەی بۆ تێناگەن؟');
INSERT INTO "bamoki" VALUES(3,66,'3:66','ئاگاداربن ئێوە ئەوانەن کەدەمە قاڵێتان کرد لەوەی کە شتێکتان لێدەزانی ئەی بۆ دەمە قاڵێ دەکەن لە شتێکدا کە ھیچی لێ نازانن وە خوا دەیزانێ وە ئێوە نازانن');
INSERT INTO "bamoki" VALUES(3,67,'3:67','ئیبراھیم نەجولەکە بووەو نەگاور بەڵکو پاک و خواپەرست و گوێڕایەڵ وملکەچ بوو وە لە ھاوبەش پەیداکاران نەبوو');
INSERT INTO "bamoki" VALUES(3,68,'3:68','بێگومان نزیکترین کەس لە ئیبراھیم ئەوانەن کە وەختی خۆی پەیڕەویان کرد وە ئەم پێغەمبەرەو ئەوانەی کە بڕوایان ھێناوە, وە خوا دۆستی بڕوادارانە');
INSERT INTO "bamoki" VALUES(3,69,'3:69','حەزیان دەکرد دەستەیەک لە خاوەن نامەکان کە گومڕاتان بکەن لە کاتێکدا کەس گومڕا ناکەن خۆیان نەبێت بەڵام ھەستی پێ ناکەن (تێ ناگەن)');
INSERT INTO "bamoki" VALUES(3,70,'3:70','ئەی خاوەن نامەکان بۆ بێ بڕوا دەبن بە ئایەتەکانی خوا لە کاتێکدا کە شایەتی دەدەن (لە سەر ڕاستی ئەوان)');
INSERT INTO "bamoki" VALUES(3,71,'3:71','ئەی خاوەن نامەکان بۆ پۆشاکی ناڕەوا ئەکەن بە بەر ڕەوا و ڕاستیدا وە ڕەوا و ڕاستیەکە دەشارنەوە لە کاتێکدا خۆشتان ئەمە دەزانن');
INSERT INTO "bamoki" VALUES(3,72,'3:72','وە دەستەیەک لەخاوەن نامەکان ووتیان (بە ھەندێکیان) بڕوابێنن بەو قورئانە کە نێرراوەتە خوارەوە بۆ ئەوانەی کە بڕوایان ھێناوە سەرلە بەیانیان (بەفێڵ) وە بێ بڕوا ببنەوە دەمەو ئێوارە بەڵکو ئەوانیش پەشیمان ببنەوە');
INSERT INTO "bamoki" VALUES(3,73,'3:73','باوەڕتان نەبێت مەگەر بە کەسێک کە دوای دینی ئێوە کەوتبێت (ئەی موحەممەد صلى الله عليه وسلم) بڵێ: بەڕاستی ڕێنمونی ھەر ڕێنمونی خوایە (ئەی خاوەن نامەکان ئێوە پێتان گرانە) کە بدرێ بە یەکێکی دیکە وێنەی ئەو دینەی کە بە ئێوە دراوە یا دەمەقاڵێتان لەگەڵ بکەن (بۆڕتان بدەن) لای پەروەردگارتان (ئەی موحەممەد صلى الله عليه وسلم) بڵێ: بەڕاستی ئەو بەھرەیە (واتە پێغەمبەرایەتی) بەدەستی خوایە دەیدات بەھەر کەسێک کە بیەوێت وە خوا خاوەنی بەخششی زۆر و زانایە');
INSERT INTO "bamoki" VALUES(3,74,'3:74','ئەو (خوایە) ڕەحممەت و میھرەبانی خۆی تایبەت دەکات بۆ ھەرکەسێک بیەوێت (شیاوی بێت) وە خوا خاوەنی بەخشش و بەھرەی گەورەیە');
INSERT INTO "bamoki" VALUES(3,75,'3:75','وە لە ناو خاوەن نامەکانی ھی وا ھەیە ئەگەر خەزنەیەک بە سپاردە لای دابنێی دەتداتەوە, وە ھی واشیان تیایە ئەگەر دینارێکی لا دابنێیت ناتداتەوە مەگەر بەسەریەوە بوەستیت ئەم ناپاکی یە بەھۆی ئەوەوە بوو کە دەیانووت: گوناھمان ناگات لە خواردنی ماڵی ئەم نەخوێندەوارانە (واتە عەرەبەکان) وە درۆ بەدەم خواوە دەکەن وە خۆیان دەزانن (کە درۆ دەکەن)');
INSERT INTO "bamoki" VALUES(3,76,'3:76','نەخێر وانیە (وەک دەڵێن) ھەر کەسێک پەیمانی خۆی بەجێ بھێنێت وە پاریزکاری بکات ولەخودا بترسێت ئەوە بەڕاستی خوا پارێزکارانی خۆش دەوێت');
INSERT INTO "bamoki" VALUES(3,77,'3:77','بێگومان ئەوانەی دەیفرۆشن پەیمانی خوا و سوێندەکانی خۆیان بە نرخێکی کەم ئەوانە بێ بەشن (لە بەھرەی خوا) لە ڕۆژی دواییدا وە خوا قسەیان لەگەڵ ناکات وە تەماشاشیان ناکات لە ڕۆژی قیامەتدا و لە گوناھیان خۆش نابێت وپاکیان ناکاتەوە, وە بۆ ئەوان ھەیە سزای بە ئێش و ئازار');
INSERT INTO "bamoki" VALUES(3,78,'3:78','بە ڕاستی لە خاوەن نامەکان دەستەیەک ھەیە (ناو بەناو نامەکەیان تێکدەدەن و) زمانیان با ئەدەن و ئەپێچنەوە بە خوێندنەوەی کتێبەکەیان (تەورات) تا تۆ وا بزانیت لە نامەی خوایە (لە کاتێکدا) کە ئەوە لە نامەی خوا نی یە وە دەڵێن ئەوە لە خواوەیە لە کاتێکدا کە ئەوە لە خواوە نی یە وە درۆ بەدەم خواوە دەکەن و خۆشیان دەزانن (کە درۆیە)');
INSERT INTO "bamoki" VALUES(3,79,'3:79','نابێ بۆ ھیچ ئادەمیەک کە خوا نامەو شەریعەت و پێغەمبەرێتی دابێتێ پاشان بە خەڵکی بڵێت ببن بە بەندەی من لە جیاتی خوا بەڵکو (پێیان دەڵێت) خوا پەرست بن چونکە ئێوە خەڵک فێری نامەکەی خودا دەکەن وە ئێوە خۆشتان دەیخوێننەوە');
INSERT INTO "bamoki" VALUES(3,80,'3:80','وە خوا فەرمانتان پێ نادات کە فریشتە و پێغەمبەران بکەن بە پەرستراوی خۆتان چۆن (خودا) فەرمانتان پێ دەدات بێ بڕوابن (کافر بن) دوای ئەوەی کە ئێوە بڕوا دار بوون؟!');
INSERT INTO "bamoki" VALUES(3,81,'3:81','(بھێنەوە یادیان) کاتێک کە خوا پەیمانی وەرگرت لە پێغەمبەران کە ھەرچیم دانێ لە نامەو حیکمەت و (شەریعەت) لە پاشان پێغەمبەرێکتان بۆ ھات بە بڕوا ھێنەر بەو نامەی کەلە لاتانە دەبێ بڕوای پێ بھێنن وە یارمەتی بدەن خوا فەرمووی: ئایا دانتان نا بەم پەیمانەدا؟ وە لەسەر ئەوە پەیمانی منتان وەرگرت پێغەمبەران ووتیان: بەڵێ دانمان پێداناو پەیمانمان بەست خوا فەرمووی: کەواتە ئێوە بەشاھید بن وە منیش لەگەڵ ئێوە یەکێکم لە شاھیدان');
INSERT INTO "bamoki" VALUES(3,82,'3:82','جا ئەوەی پشتی ھەڵکرد (لەم پەیمانە) دوای ئەم دان پێدانانە کەواتە ھەر ئەوانن دەستەی دەرچوان لە فەرمانی خودا');
INSERT INTO "bamoki" VALUES(3,83,'3:83','ئایا (بێ بڕوایان) داوای غەیری دینی خوا دەکەن؟! لە کاتێکدا کە ھەرچی لەئاسمان و زەویدایە گوێرایەڵ وملکەچی خودان بیانەوێت (بە ویستی خۆیان) یا نەیانەوێت (بە ناچاری) وە ھەموو ھەر بۆ لای ئەو دەگەڕێنرێنەوە');
INSERT INTO "bamoki" VALUES(3,84,'3:84','(ئەی موحەممەد صلى الله عليه وسلم) بڵێ: ئێمە بڕوامان ھێناوە بە خوای گەورە, وە بەوەی نێرراوەتە خوارەوە بۆمان وە بەوەی کە نێرراوەتە خوارەوە بۆ ئیبراھیم وئیسماعیل وئیسحاق یەعقوب وکوڕانی یەعقوب وە بــەوەی کـە دراوە بە موساو عیسا وەپێغەمبەرەکانی تر لە پەروەردگاریانەوە جیاوازی ناکەین لەنێوان ھیچ کامێک لەم پێغەمبەرانە وە ئێمە بۆ خوای گەورە گەردن کەچین');
INSERT INTO "bamoki" VALUES(3,85,'3:85','وە ھـەر کەسێک دوای ھەر ئاینێک بکەوێت جگە لە ئاینی ئیسلام ئەوە لێی وەر ناگیرێت وە ئەو لە ڕۆژی دواییدا لە زەرەرمەندانە');
INSERT INTO "bamoki" VALUES(3,86,'3:86','چۆن خوا ڕێنمونی گەلێک دەکات بێ بڕوابووبن دوای بڕوا ھێنانیان وە شایەتییان دابێت کە ئەو پێغەمبەرە ڕاستە و (لەلایەن خوداوە ھاتووە) وە نیشانەی زۆریان بۆ ھاتبێ وە خوا ڕێنموونیی گەلی ستەمکاران ناکات');
INSERT INTO "bamoki" VALUES(3,87,'3:87','لەو (نەفرینە) دا دەمێننەوە بە ئێجگاری وھەمیشەیی سزایان لێ سووک ناکرێت وە مۆڵەت نادرێن');
INSERT INTO "bamoki" VALUES(3,88,'3:88','تێیدا دەمێننەوە بەھەمیشەیی سزایان لەسەر سووک ناکرێت وە دواناخرێن لەسزا');
INSERT INTO "bamoki" VALUES(3,89,'3:89','مەگەر ئەوانەی تۆبەیان کردبێ دوای ئەوە, وە کردەوەی چاکیان کردبێت ئەوە بێگومان خوا لێ خۆشبووی میھرەبانە');
INSERT INTO "bamoki" VALUES(3,90,'3:90','بە ڕاستی ئەوانەی کە بێ بڕوا بوون دوای بڕوا ھێنانیان پاشان زیاتر بێ باوەڕ بوون ھەرگیز تەوبەیان لێ گیرا ناکرێت ئا ئەوانە ئەوانـەن کە گومڕابوون');
INSERT INTO "bamoki" VALUES(3,91,'3:91','بە ڕاستی ئەوانەی بێ بڕوابوون وە بە بێ بڕوایی مردن ئەوە ھەرگیز (فدیە) لە ھیچ کامێکیان وەر ناگیرێت با پڕ بە ھەموو زەویش زێڕ بێت بیبەخشێت لە بریتی دا (بۆ سووک کردنی سزا لەسەری) وە ئەوانە سزای بە ئازاریان بۆ ھەیە وە ھیچ یاریدەدەر و سەرخەرێکیان نیە');
INSERT INTO "bamoki" VALUES(3,92,'3:92','ئێوە ھەرگیز بە خێرو چاکە ناگەن (وە دەسگیرتان نابێت) تا نەیبەخشن ھەندێ لەو داراییەی کە خۆشتان دەوێت وە ھەرچی ببەخشن ئەوە بێگومان خودا ئاگای لێیە (بە پێی دڵ و دەروونتان پاداشتان دەداتەوە)');
INSERT INTO "bamoki" VALUES(3,93,'3:93','ھەموو جۆرە خواردەمەنیەکان حەڵاڵ بوون بۆ نەوەی یەعقوب بێجگە لەوەی کە یەعقوب حەرامی کرد لەسەر خۆی لە پێش ناردنە خوارەوەی تەورات (ئەی موحەممەد صلى الله عليه وسلم) بڵێ: دەی تەورات بھێنن و بیخوێننەوە ئەگەر ئێوە ڕاست دەکەن');
INSERT INTO "bamoki" VALUES(3,94,'3:94','ئەمجا ھەر کەس درۆ بە دەم خواوە ھەڵبەستێ لە پاش ئەوە کەواتە ئەوانە خۆیان ستەمکارن');
INSERT INTO "bamoki" VALUES(3,95,'3:95','(ئەی موحەممەد صلى الله عليه وسلم) بڵێ: خوا ڕاستی فەرموو کەوا بوو پەیڕەوی ئاینی ئیبراھیم بکەن کە پاکە (بە ویستی خۆی لە بیرو باوەڕو کرداری ھەڵە لای داوە بۆ بیرو باوەڕ و کرداری ڕاست) وە ھیچ کاتێک لە ھاوبەش بڕیاردەران نەبووە');
INSERT INTO "bamoki" VALUES(3,96,'3:96','بە ڕاستی یەکەم ماڵێک دانراوە بۆ (خواپەرستی) خەڵکی ئەو ماڵەیە کە لەبەککە (مەککە) دایە پیرۆز کراوە, وە ڕێنمونیە بۆ جیھانیان');
INSERT INTO "bamoki" VALUES(3,97,'3:97','تیایدا ھەیە چەند بەڵگەو نیشانەی ڕوون (کە یەکێکیان ) شوێن وەستانەکەی ئیبراھیمە وە ھەر کەس چووبێتە ئەو شوێنە بێ ترس بووە (لە زیان و ئازاردان) خوا بڕیاریداوە لەسەرخەڵکی و فەرزی کردوە لەسەریان حەجی ماڵەکەی (خۆی) لەسەر ھەر کەسێک کە توانای گەیشتن و (ڕۆیشتنی) ھەبێت بۆی وە ھەر کەس باوەڕی نەبێت (یان توانای ھەبێت و نەچێت) ئەوە بە ڕاستی خوا بێ نیازە لە ھەموو جیھانیان');
INSERT INTO "bamoki" VALUES(3,98,'3:98','بڵێ: ئەی نامە داران (خاوەن کتێبەکان) لە بەر چی بێ بڕوان بە بەڵگەکانی خوا لە کاتێکدا خوا بیناو ئاگادارە بەئەوەی کە دەیکەن');
INSERT INTO "bamoki" VALUES(3,99,'3:99','بڵێ: ئەی خاوەن کتێبەکان لەبەر چی ڕێگری دەکەن لە ڕێی خوا کەسێک کە بڕوای ھێنابێ (مەبەست پێی پێغەمەرو باوەڕدارانە) دەتانەوێت بە ناڕاست بیخەنە بەرچاوی خەڵک لە کاتێکدا ئێوە زاناو ئاگادارن (بەوەی ڕێی خوایە) وە خوا بێ ئاگا نیە لەوەی کە ئێوە دەیکەن');
INSERT INTO "bamoki" VALUES(3,100,'3:100','ئەی ئەو کەسانەی کە بڕواتان ھێناوە ئەگەر ئێوە گوێڕایەڵی دەستەیەک بن لەوانەی کە نامەیان پێدراوە دەتانکەنەوە بە کافر دوای بڕوا ھێنانەکەتان');
INSERT INTO "bamoki" VALUES(3,101,'3:101','چۆن بێ بڕوادەبن؟ لە کاتێکدا دەخوێنرێتەوە بە سەرتاندا بەڵگەکانی خوا ( ئایەتەکانی قورئان) وە پێغەمبەرەکەی لە ناوتاندایە وە ھەرکەس دەست بگرێ بە ( ئایینی) خواوە ئەوە بەڕاستی ڕێنموونیی کراوە بۆ ڕێبازێکی ڕاست و دروست');
INSERT INTO "bamoki" VALUES(3,102,'3:102','ئەی ئەوانەی کە بڕواتان ھێناوە بترسن لە خوا ترسانێک کە شیاوێتی وە مەمرن ھەر بە موسوڵمانی نەبێ');
INSERT INTO "bamoki" VALUES(3,103,'3:103','ھەمووتان دەست بگرن بەھۆی پەیوەستیتان بەخواوە (کە قورئانە) چەند بەرەکە و (لێک جیا) مەبن بیر لە بەھرەو چاکەی خوا بکەنەوە بەسەرتانەوە لە کاتێکدا کە ئێوە دوژمنی یەکتر بوون بەڵام خوا ھۆگری خستە نێوان دڵەکانتان ئیتر بوون بە برای یەکتر بەھۆی ئەو چاکەی خواوە, وە ئێوە لەسەر لێواری چاڵێ بوون لەئاگر بەڵام خوا ڕزگاری کردن لێی ئا بەو جۆرە خوا بەڵگە ونیشانەکانی خۆی ڕوون دەکاتەوە بۆتان تا بەردەوام بن لەسەر ڕێنمونی (یان لەسەر شارەزایی)');
INSERT INTO "bamoki" VALUES(3,104,'3:104','ئەبێ لە ناو ئێوەدا کۆمەڵێک ببێت بانگەواز بکەن بۆ چاکە فەرمان بدەن بە ھەموو چاکەیەک وە ڕێگری بکەن لە ھەموو خراپەیەک ئەوانە خۆیانن سەر کەوتوو وسەرفرازن');
INSERT INTO "bamoki" VALUES(3,105,'3:105','بە وێنەی ئەوانە مەبن (وەک ئەھلی کتاب) کە بەش بەش و جیاواز بوون لە پاش ئەوەی ئەو بەڵگانەی بۆیان ھات وە ئەوانە سزایەکی گەورەیان بۆ ھەیە');
INSERT INTO "bamoki" VALUES(3,106,'3:106','ڕۆژێک دێت کە چەند ڕوویەک سپی دەبن وە چەند ڕوویەک ڕەش دەبن جا ئەوانەی کە ڕوویان ڕەش بووە (پێ یان دەوترێت) بۆچی بێ بڕوابوون لە پاش بڕوا ھێنانتان کەواتە بچێژن سزا بەھۆی بێ بڕواییتانەوە');
INSERT INTO "bamoki" VALUES(3,107,'3:107','وە ئەوانە کە ڕوویان سپی دەبێت ئەوە لەناو بەزەیی خوادان (لەبەھەشتدان) ئەوانە تێیدا دەمێننەوە بەھەمیشەیی');
INSERT INTO "bamoki" VALUES(3,108,'3:108','ئەمانە ئایەتەکانی خوان دەیان خوێنینەوە بەسەرتدا کە ڕاستن خواش نایەوێت ھیچ ستەمێک بۆ جیھانیان');
INSERT INTO "bamoki" VALUES(3,109,'3:109','تەنھا بۆ خوایە ھەرچی لە ئاسمانەکاندایە (وموڵکی خودایە) وە ھەرچی لەزەویدایە وە ھەر بۆلای خوا دەگەڕێتەوە ھەموو کارەکان');
INSERT INTO "bamoki" VALUES(3,110,'3:110','ئێوە (موسوڵمانان) باشترین گەلێک بوون بۆ خەڵکی ھێنرابێتە دی فەرمان دەدەن بەچاکە وە جڵەوگیری دەکەن لە خراپە وە بڕوا دەھێنن بەخوا ئەگەر نامە دارەکان (خاوەن کتێبەکان لە جولەکەو گاورەکان) بڕوایان بھێنایە ئەوە باشتر بوو بۆیان ھەندێک لەوان بڕوادارن و(باوەڕیان ھێناوە بە ئاینی ئیسلام) بەڵام زۆربەیان لە فەرمانی خوا دەرچوون');
INSERT INTO "bamoki" VALUES(3,111,'3:111','ھەرگیز ئەوان زیانتان پێ ناگەیەنن مەگەر زیانێکی کەم وە ئەگەر بجەنگێن لەگەڵتاندا پشتان تێدەکەن دواتریش سەرکەوتوو نابن');
INSERT INTO "bamoki" VALUES(3,112,'3:112','دراوە بەسەریاندا سەر شۆڕی و ڕیسوایی لەھەر شوێنێک ببن مەگەر بە ھۆی بەڵێن لەلایەن خوداوە نەبێت وە بەڵێنێک لەلای خەڵکەوە شایستەی خەشمێکی گەورەبوون لە لایەن خواوە, وە دراوە بەسەریاندا ھەژاری دەروون و چاو برسێتی ئەوە لەبەر ئەوە بوو ئەوان بێ بڕوابوون بە بەڵگەکانی خوا وە پێغەمبەرانیان دەکوشت بەناڕەوا ئەوەش بەھۆی ئەوەوە بوو کە ئەوانە سەرپێچی و دەست درێژیان دەکرد');
INSERT INTO "bamoki" VALUES(3,113,'3:113','ھەموو (نامە دارەکان) وەک یەک نین لەناو خاوەن نامەکاندا دەستەیەک ڕاست و ڕێک ھەن ئایەتەکانی خوا دەخوێنن (وباوەڕیان ھێناوە بە ئاینی ئیسلام) لە (کاتەکانی) شەودا وە لە(کاتەکانی) سوژدە بردنیاندا');
INSERT INTO "bamoki" VALUES(3,114,'3:114','بڕوایان ھەیە بەخوا وە بە ڕۆژی دوایی و فەرمان دەدەن بەھەرچی چاکەیە وە ڕێگری دەکەن لە ھەرچی خراپەیە وە پێشبڕکێ دەکەن لە ھەموو چاکەکاندا وە ئەوانە لە کۆمەڵی چاکانن');
INSERT INTO "bamoki" VALUES(3,115,'3:115','ھەر خێرو چاکەیەک بکەن ئەوە ھەرگیز بێ بەش ناکرێن لە پاداشتەکەی خواش بەئاگایە لە پارێزکاران ولەوانەی لێی دەترسن');
INSERT INTO "bamoki" VALUES(3,116,'3:116','بەڕاستی ئەوانەی کەبێ بڕوا بوون سوود بەوان ناگەیەنێت نەماڵەکانیان وە نە منداڵەکانیان ھیچێک لەلای خواوە ئا ئەوانە ھاوەڵ وھاوڕێی ئاگر دەبن ئەوان تێیدا دەمێننەوە بەھەمیشەیى');
INSERT INTO "bamoki" VALUES(3,117,'3:117','وێنەی ئەو ماڵ و داراییەی کەبەختی دەکەن لەم ژیانی دونیایەدا بە وێنەی بایەکە سەرما و سۆڵەی تێدابێت بدات لەکشتوکاڵی تاقمێک کەستەمیان لەخۆیان کردبێت جا ئەو (با) وسەرمایە کشتوکاڵەکەی لەناو بەرێت وە خوا ستەمی لێ نەکردوون بەڵکو خۆیان ستەمیان لەخۆیان دەکرد');
INSERT INTO "bamoki" VALUES(3,118,'3:118','ئەی ئەو کەسانەی کە بڕواتان ھێناوە مەگرن بۆ خۆتان دۆستی گیانی بەگیانی جگە لەخۆتان (واتە جگە لەباوەڕداران) درێغیتان لێ ناکەن لەھیچ شەڕ و خراپەیەک ئاواتە خوازی ناخۆشیتانن بێگومان دەرئەکەوێت خەشم و قین لە (قسەکانی) دەمیانەوە, وەئەوەی لەدڵیاندا شاردویانەتەوە گەورەترە بەڕاستی بەڵگە ونیشانەکانمان بۆ ڕوون کردونەتەوە ئەگەر ئێوە ژیربن');
INSERT INTO "bamoki" VALUES(3,119,'3:119','بێداربن ئا ئەوە ئێوەن کەئەوانتان خۆش دەوێت وەئەوان ئێوەیان خۆش ناوێ وەباوەڕتان ھەیە بەھەموو نامە ئاسمانیەکان کاتێک ئەوان پێتان بگەن دەڵێن بڕوامان ھێناوە, وە کاتێک تەنھا بونەوە سەرە پەنجەکانی خۆیان ئەگەزن لێتان لەڕقدا بڵێ بمرن بەداخ و رقی خۆتانەوە بەڕاستی خوا زانایە بەوەی لەناو دڵەکاندایە');
INSERT INTO "bamoki" VALUES(3,120,'3:120','ئەگەر چاکە و خۆشیەکتان پێ بگات غەمبار دەبن بەڵام ئەگەر ناخۆشیەکتان پێ بگات دڵخۆش دەبن پێی ئەگەر ئێوە ئارام بگرن (لەسەر گوێڕایەڵی خوا) و لەخوا بترسن پیلانی ئەوان ھیچ زیانێکتان پێ ناگەیەنێت بەڕاستی خوا تەواو ئاگادارە بە ھەرچی ئەوان دەیکەن');
INSERT INTO "bamoki" VALUES(3,121,'3:121','وە (بیھێنەوە بیریان) کاتێک کە بەیانی زوو دەرچوویت لەلای ماڵ و خێزانت بڕوادارەکانت نیشتەجێ ئەکرد لە چەند شوێنێکدا بۆ جەنگ کردن وە خوا بیسەر و زانایە');
INSERT INTO "bamoki" VALUES(3,122,'3:122','لەو کاتەدا کە دوو کۆمەڵ لە ئێوە خەریک بوون سست بن وتێک بشکێن و (بگەڕێنەوە) بەڵام خوا دۆست و یارمەتی دەریان بوو وە بڕوا داران با پشت تەنھا بە خوا ببەستن');
INSERT INTO "bamoki" VALUES(3,123,'3:123','سوێند بە خوا بێگومان خوا ئێوەی سەرخست لە (جەنگی) بەدردا لە کاتێکدا ئێوە کەم و لاواز بوون دەی لە خوا بترسن بەڵکو سوپاس گوزاری بن');
INSERT INTO "bamoki" VALUES(3,124,'3:124','(بیریان بخەرەوە) ئەو کاتەی دەتووت بە بڕوادارەکان ئایا بەستان نیە پەروەردگارتان کۆمەکتان بکات بە سێ ھەزار لە فریشتە دابەزێنراوەکان');
INSERT INTO "bamoki" VALUES(3,125,'3:125','بەڵێ، ئەگەر ئارام بگرن وە لە خوا بترسن پەلامارتان بدەن لەم کاتانەدا پەروەردگارتان کۆمەکتان بکات بە پێنج ھەزار لە فریشتە نیشانەدارەکان');
INSERT INTO "bamoki" VALUES(3,126,'3:126','وە خوا تەنھا ئەو (کۆمەکی) یەی کرد بە مژدە بۆتان وە بۆ ئەوەی دڵەکانتانی پێ ئارام ببێت (ئەوەش بزانن) سەرکەوتنیش تەنھا لەلایەن خوای باڵا دەستی داناوەیە');
INSERT INTO "bamoki" VALUES(3,127,'3:127','(وە ناردنی فریشتەکان) بۆ ئەوە بوو دابڕێت (لە ناوی بات) بەشێک لەوانەی کە بێ بڕوان یان خەفەت باریان بکات (بە تێشکانیان) ئەمجا بگەڕێنەوە بە نائومێدی');
INSERT INTO "bamoki" VALUES(3,128,'3:128','دەربارەی ھەڵھاتووەکانی شەڕی ئوحود بە ڕاستی بەدەست تۆ نیە (ئەی موحەممەد صلى الله عليه وسلم) ھیچ شتێک لەو کارەدا چ (خوا) تەوبەیان لێ وەرگرێت یان سزایان بدات چونکە بە ڕاستی ئەوانە ستەمکارن');
INSERT INTO "bamoki" VALUES(3,129,'3:129','ھەر بۆ خوایە ھەرچی لە ئاسمانەکاندایە وە ھەرچی لە زەویدایە خۆش ئەبێت لە ھەر کەس خۆی بیەوێت وە سزای ھەر کەسیش ئەدات خۆی بیەوێت وە خوا لێ بوردەی میھرەبانە');
INSERT INTO "bamoki" VALUES(3,130,'3:130','ئەی ئەوکەسانەی بڕواتان ھێناوە سوود مەخۆن چەند بەرامبەر و لە خوا بترسن بۆ ئەوەی ڕزگاربن');
INSERT INTO "bamoki" VALUES(3,131,'3:131','وە خۆتان بپارێزن لەو ئاگرەی کە ئامادە کراوە بۆ بێ بڕواکان');
INSERT INTO "bamoki" VALUES(3,132,'3:132','وەگوێ ڕایەڵی خودا و پێغەمبەر بن بێگومان میھرەبانیتان لەگەڵ ئەکرێت');
INSERT INTO "bamoki" VALUES(3,133,'3:133','پێشبڕکێ بکەن بۆ لێ بوردنێکی گەورەی (خودا) لەلایەن پەروەردگارتانەوە, وە بۆ بەھەشتێک کە پانییەکەی (بە ئەندازەی) ئاسمانەکان و زەوی یە کە ئامادە کراوە بۆ لە خوا ترسان');
INSERT INTO "bamoki" VALUES(3,134,'3:134','ئەوانەی ماڵ دەبەخشن (لە ڕێ ی خوادا) لە کاتی خۆشی (دەوڵەمەندی) و ناخۆشی دا و ڕقی بەتینی خۆیان دەخۆنەوە و دەبوورن لە خەڵکی خواش چاکەکارانی خۆش دەوێت');
INSERT INTO "bamoki" VALUES(3,135,'3:135','وە ئەوانەی کە کاتێک کردەوەیەکی ناشیرین دەکەن یاستەم لەخۆیان دەکەن کە یادی خوایان کردەوە ئەمجا داوای لێ بووردن دەکەن بۆ گوناھەکانیان ئایا کێ دەبوورێت لە گوناھەکان بێجگە لە خوا؟ وە بەردەوام نابن لەسەر ئەوەی کە کردوویانە لە کاتێکدا بزانن (گوناھە)');
INSERT INTO "bamoki" VALUES(3,136,'3:136','ئا ئەوانە پاداشتیان لێ بووردنە لەلایەن پەروەردگاریانەوە و چەند باخاتێک (بەھەشتێک) کەڕوبارەکان دەڕۆن بەژێریاندا ئەمێننەوە لەو بەھەشتانەدا بەھەمیشەیی چەند چاکە پاداشتی چاکەکاران');
INSERT INTO "bamoki" VALUES(3,137,'3:137','بێگومان رابورد لە پێش ئێوەوە چەند رێ و یاسایەک (باوێک) ئەمجا ئێوە بگەڕێن بەزەویدا و سەرنج بدەن ئایا چۆن بوو سەرئەنجامی بێ بڕواکان');
INSERT INTO "bamoki" VALUES(3,138,'3:138','ئەمە ڕوون کردنەوەیە بۆ ھەموو خەڵکی و ڕێنمونی و ئامۆژگاریە بۆ لە خوا ترسان');
INSERT INTO "bamoki" VALUES(3,139,'3:139','سست و دڵ لاواز مەبن وە غەمبار مەبن ھەر ئێوە بەرز و بڵند ترن ئەگەر بڕوادار بن');
INSERT INTO "bamoki" VALUES(3,140,'3:140','ئەگەر ئێوە (لە ئوحوددا) زام و برینتان تووش بووە ئەوە بێگومان ئەو ھۆزە (بتپەرستەکان لە بەدردا) زام و برینیان توش بووە بە وێنەی زامی ئێوە ئەو چەرخ و ڕۆژانەی (زاڵبوون و سەرکەوتنانە) دەیگێڕین لە ناو خەڵکیدا (جارێک بۆ ئێوە و جارێک بۆ ئەوان) لەبەر ئەوەی (لەدونیادا) خوا دەری بخات ئەوانەی بڕوایان ھێناوە و لە ناو ئێوەدا ھەڵبژێرێت چەند کەسێک بە شەھید وە خوا ستەمکارانی خۆش ناوێت');
INSERT INTO "bamoki" VALUES(3,141,'3:141','وە تا خوا (لە تاوان) پاک بکاتەوە ئەوانەی کە بڕوادارن وەبێ باوەڕان بنەبڕ بکات');
INSERT INTO "bamoki" VALUES(3,142,'3:142','ئایا ئێوە وا گومانتان دەبرد کە دەچنە بەھەشت لە کاتێکدا ھێشتا خوا دەری نەخستوە (واتە تاقی نەکردونەتەوە) ئەوانەی تێدەکۆشن لە ئێوە و ھێشتا دەری نەخستوون کەسانێک لە ئێوە کە خۆڕاگران');
INSERT INTO "bamoki" VALUES(3,143,'3:143','سوێند بێ بێگومان ئێوە ئاواتی (شەھیدی و) مردنتان ئەخواست لەپێش ئەوەی پێ ی بگەن خۆ بەڕاستی مردنتان بینی لە کاتێکدا ئێوە زەق سەیرتان دەکرد');
INSERT INTO "bamoki" VALUES(3,144,'3:144','(موحەممەد صلى الله عليه وسلم) تەنھا پێغەمبەرێکە (نەمر نیە) بێگومان لەپێش ئەو پێغەمبەرانی تر ڕابوردون جا ئایا ئەگەر وەفاتی کرد یان کوژرا دەگەڕێنەوە بۆ دواوەی خۆتان (پاشگەز دەبنەوە) ھەر کەس ھەڵگەڕێتەوە بۆ دواوەی خۆی (بۆ بت پەرستی) ئەوە ھەرگیز (ئەو کەسە) ھیچ زیانێک ناگەیەنێت بەخوا وە بەزوویی خوا پاداشتی سوپاس گوزاران دەداتەوە');
INSERT INTO "bamoki" VALUES(3,145,'3:145','ھیچ کەسێک نەبووە کە بمرێت مەگەر بە فەرمانی خوا نوسراوێکی کات دیاریکراوە ( لەو کاتەدا کەخوای گەورە دیاری کردوە) ھەر کەسێک دەیەوێت پاداشت و دەستکەوتی دونیایی لەو(پاداشتی دونیا) ھەندێکی پێ دەدەین وە ھەرکەسێک دەیەوێت پاداشتی پاشەڕۆژ لەو (پاداشتی پاشەڕۆژە) ھەندێکی پێ دەبەخشین وە بەزووی پاداشتی سوپاس گوزاران دەدەینەوە');
INSERT INTO "bamoki" VALUES(3,146,'3:146','زۆر لە پێغەمبەران جەنگ و کوشتاریان کردووە لەگاڵیاندا (شانبەشانیان) زۆر لە زانایان و خواناسان جا سست و دەستە پاچە نەبوون بەھۆی ئەوەی کە دووچاریان بووە لە ڕێی خوادا وە بێ ھێز و لاواز نەبوون وە سەریان شۆڕ نەکرد بۆ دوژمن وە خوا خۆڕاگرانی خۆش دەوێت');
INSERT INTO "bamoki" VALUES(3,147,'3:147','ھەر ئەمە ووتەی ئەوان بوو کەدەیانووت ئەی پەروەردگارمان لێمان ببورە لە گوناھەکانمان وە لە زیادە ڕەویمان لە کارووبارماندا و قاچەکانمان جێگیر بکە (لە جەنگدا) وە سەرمان بخە بەسەر کۆمەڵی بێ بڕوایاندا');
INSERT INTO "bamoki" VALUES(3,148,'3:148','بەھۆی ئەوەوە خوا پێی بەخشین پاداشتی دونیا وە پاداشتی چاک و شیرینی ڕۆژی دوایی خوا چاکەکارانی خۆش دەوێت');
INSERT INTO "bamoki" VALUES(3,149,'3:149','ئەی ئەوانەی بڕواتان ھێناوە ئەگەر ئێوە گوێڕایەڵی بکەن بۆ ئەوانەی بێ بڕوان ئەتان گێڕنەوە بۆ دواوەی خۆتان (بۆ بت پەرستی) ئەمجا ئەگەڕێنەوە بە زیانباری');
INSERT INTO "bamoki" VALUES(3,150,'3:150','(ئەوانە دۆستان نین) بەڵکو خوا یارمەتی دەر و دۆستی ئێوەیە وەخواباشترینی ھەموو یارمەتیدەرانە');
INSERT INTO "bamoki" VALUES(3,151,'3:151','بەزوویی دەخەینە دڵی ئەوانەی بێ بڕوان ترس و بیمی بەتین بەھۆی ئەوەی کرد بوویانە ھاوبەشی خوا شتانێک کە خوا ھیچ بەڵگەی نەناردۆتە خوارەوە لەسەری کە جێگا و شوێنی ئەوان ئاگرە وەچەند نیشتە جێیەکی خراپە (جێگەی) ستەمکاران');
INSERT INTO "bamoki" VALUES(3,152,'3:152','سوێند بێت بێگومان خوا بەجێی ھێنا بۆتان بەڵێنەکەی کاتێک (سەرەتای جەنگی ئوحود) ھەستتان لێ بڕین (دەتان کوشتن) بەفەرمانی خوا ھەتا کاتێک (لە ناکاو) سست بوون ئاژاوە و دووبەرەکیتان کرد لەکاری جەنگدا وەبێ فەرمانیتان کرد لەپاش ئەوەی کەخوا نیشانیدان ئەوەی (لە جەنگەکەدا) پێتان خۆش بوو (کەسەرکەوتن لەسەرەتایی جەنگەکەدا) ھەندێک لە ئێوە (دەسکەوتی) دونیایی دەویست وە ھەندێکتان پاداشتی ڕۆژی دوایی دەویست پاشان (خوا) ئێوەی گێڕایەوە لە (جەنگی) ئەوان و (تێشکان) تا تاقیتان بکاتەوە (لەخۆڕاگرتندا) سوێندبێت بێگومان خوا لێتان بوورا و خوا خاوەنی بەھرەو چاکەیە بەسەر بڕوادارانەوە');
INSERT INTO "bamoki" VALUES(3,153,'3:153','کاتێک دووردەکەوتنەوە (سەردەکەوتن بەکێوی ئوحوددا) وەبەڕێی کەس نەئەوەستان ولاتان نەدەکردەوە بەلایدا لەکاتێکدا پێغەمبەر بانگی لێ دەکردن لە دواتانەوە جا (خوا) پاداشتی غەم و خەفەتی ھەر بە غەم و خەفەت دانەوە بۆ ئەوەی غەمبار نەبن لەسەر ئەوەی لە دەستان چووە, وە (غەمبار نەبن بە) ئەوەی دووچارتان بووە خواش ئاگادارە بەوەی کەدەیکەن');
INSERT INTO "bamoki" VALUES(3,154,'3:154','پاشان دایبەزاند بە سەرتاندا (بۆی ناردن) لە پاش ئەو غەمەی (کە چەشتتان) ئارامیی کە وەنەوزێک بوو بەسەر دەستەیەک لە ئێوە داھات بەڵام دەستە وتاقمێکی دیکەتان( کە باوەڕیان ھێندە بەھێز نەبوو) بەڕاستی ھەر خۆیان مەبەست بوو گومانی ناڕاست و ناھەقیان بەخوا دەبرد وەک گومانی نەفامێتی ئەیان ووت ئایا بۆمان ھەیە (بە دەستمانە) لەم کارەدا (زاڵ بوون) ھیچ شتێک بڵێ بێگومان کاروبار ھەمووی بەدەست خوایە دەیشارنەوە لە دەروونیاندا ئەوەی کە دەری نابڕن بۆت دەڵێن: ئەگەر بە دەست ئێمە بوایە لەم کارەدا شتێک (لە سەرکەوتن) ئێمە لێرەدا نەدەکوژراین بڵێ ئەگەر ئێوە لە ماڵەکانی خۆشتاندا بوونایە ھەر دەردەچوون ئەوانەی کە کوژرانیان لەسەر نووسرا بوو بۆ جێگای کوژرانیان (ڕاکشانیان) بۆ ئەوەی خوا تاقی بکاتەوە ئەوەی کە لە سنگتاندایە وە بۆ ئەوەی پاڵفتە و پاکی بکاتەوە ئەو (بڕوایەی) کە لە دڵتاندایە وە خوا زانایە بەوەی لە سینەو دەرونەکاندایه');
INSERT INTO "bamoki" VALUES(3,155,'3:155','بەڕاستی ئەوانەی پشتیان ھەڵکرد لەناوتاندا لەو ڕۆژەی کە ھەردوو کۆمەڵ گەیشتن بەیەک (لە ئوحود دا) بێگومان تەنھا شەیتان ھەڵی خلیسکاندن ولەخشتەی بردن بەھۆی ھەندێ لەو گوناھانەی کەلەوەو پێش کردبوویان سوێند بێ بێگومان خوا لێ یان خۆشبوو چونکە بەڕاستی خوا لێبووردەی بەئارام و لەسەرخۆیە (لە تۆڵە سەندندا)');
INSERT INTO "bamoki" VALUES(3,156,'3:156','ئەی کەسانێ کە بڕواتان ھێناوە, وەک ئەوانە مەبن کە بێباوەڕ بوون بە برا بێباوەڕەکانیان دەووت کاتێک سەفەریان بکردایە یان غەزایان بکردایە وبچوونایە بۆ جەنگ ئەگەر لای ئێمە (نیشتەجێ) بوونایە نە دەمردن و نە دەکوژران تا خوا ئەوەبکات بە داخ و مەراق لە دڵیاندا وە خوا (مرۆڤ) دەژێنێ و دەشی مرێنێت وە خودا بینایە بەھەر کردەوەیەک کە ئێوە دەیکەن');
INSERT INTO "bamoki" VALUES(3,157,'3:157','سوێندبێت ئەگەر ئێوە بکوژرێن لە ڕێی خوادا یان بمرن ئەوە بەڕاستی لێبووردن و میھرەبانی لە لایەن خواوە چاکترە لەوەی کەکۆی دەکەنەوە');
INSERT INTO "bamoki" VALUES(3,158,'3:158','سوێند بێت ئەگەر ئێوە بمرن یان بکوژرێن ھەر لای خوا کۆدەکرێنەوە');
INSERT INTO "bamoki" VALUES(3,159,'3:159','بەھۆی بەزەیی خواوەیە بۆ ئەوان نەرم و نیانی بوێت ئەگەر تۆ بەدخوو قسە ڕەق و دڵ ڕەق بوویتایە ئەوە ھەموو بڵاوەیان دەکرد لەدەورت کەواتە لێیان ببورە و داوای لێ بوردونیان بۆ بکە لەخوا و ڕاوێژیان پێ بکه لە کاروباردا جا کاتێک (دوا ڕاوێژکردن) بڕیارتدا ئەوە پشت بە خوا ببەستە بەڕاستی خوا ئەوانەی خۆش دەوێت کەپشت بەخوا دەبەستن');
INSERT INTO "bamoki" VALUES(3,160,'3:160','ئەگەر خوا سەرتان بخات ئەوە ھیچ کەس زاڵ نابێت بەسەرتاندا وە ئەگەر یارمەتیتان نەدا (پشتتان بەردات) کێیە ئەو کەسەی؟ لەپاش خوا سەرتان دەخات وە بـا بـڕواداران پشت ھەر بەخوا ببەستن');
INSERT INTO "bamoki" VALUES(3,161,'3:161','وە بۆ ھیچ پێغەمبەرێک نەبوو کەخیانەت بکات وە ھەر کەس خیانەت بکات بەو خیانەتەوەی کردویەتی دێت (بەکۆڵیەوەیە) لە پاشەڕۆژدا (ڕۆژی ھەستانەوە) پاشان پاداشت دەدرێتەوە ھەموو کەسێک ھەرچی کردبێت وە ئەوان ستەمیان لێ ناکرێت');
INSERT INTO "bamoki" VALUES(3,162,'3:162','دەی ئایا کەسێک کەشوێنی ڕەزامەندی خوا کەوتبێت وەک کەسێک وایە بگەڕێتەوە بەخەشمێکەوە لەلایەن خواوە، وە جێگا و شوێنی ئەو دۆزەخ بێت کە سەرئەنجامێکی چەند خراپە؟');
INSERT INTO "bamoki" VALUES(3,163,'3:163','(چاکە کاران و خراپە کاران) خاوەنی چەند پلەی جیاوازن لای خوا وە خوا بینایە بە ئەوەی کەدەیکەن');
INSERT INTO "bamoki" VALUES(3,164,'3:164','سوێند بێت بێگومان خوا چاکە و منەتی نا بەسەر بڕواداراندا کاتێ کەڕەوانەی کرد لەناویاندا پێغەمبەرو نێرراوێک لەخۆیان کە دەیخوێنێتەوە بەسەریاندا بەڵگە ونیشانەکانی خودا و پاک و خاوێنیان دەکاتەوە وە قورئان و فەرموودەیان فێردەکات ئەگەر چی ئەوان لەپێشدا لەگومڕاییەکی ڕوون و ئاشکرادا بوون');
INSERT INTO "bamoki" VALUES(3,165,'3:165','ئایا بۆچی کاتێک دووچارتان بوو ناخۆشیەک (لەئوحود دا) بێگومان ئێوە دووقاتی ئەوەتان دووچاری ئەوان کرد (لەبەدردا) ووتتان ئەمە لە کوێوە ھات بڵێ (ئەی موحەممەد صلى الله عليه وسلم) ئەوە لەلایەن خۆتانەوە بوو (کە فەرمانی پێغەمبەرتان شکاند) بەڕاستی خوا بەسەر ھەموو شتێکدا بەتوانایە');
INSERT INTO "bamoki" VALUES(3,166,'3:166','ئەوەی کەدووچارتان بوو لەو رۆژەدا کە ھەردوو کۆمەڵ بەیەک گەیشتن (لەئوحوددا) ئەوە بەویستی خودا بوو بۆ ئەوەی بڕواداران دەربخات');
INSERT INTO "bamoki" VALUES(3,167,'3:167','و بۆ ئەوەیش دووڕوەکان ئاشکرا بکات وپێیان ووترا وەرن بجەنگن لەڕێی خوادا یابەرگری (لە وڵاتی خۆتان) بکەن ووتیان: ئەگەر بمانزانیایە جەنگ دەبێت شوێنتان دەکەوتین ئەوانە لەو رۆژەدا لە بێ بڕواییەوە نزیک تر بوون تا لە بڕواو ئیمانەوە ئەوانە بەدەمیان شتێک دەڵێن کەلەدڵیاندا نیە وەخوا (لەھەموو کەس) زاناترە بەوەی کە دەیشارنەوە');
INSERT INTO "bamoki" VALUES(3,168,'3:168','ئەوانەی کەووتیان (مەبەست پێی دووڕوەکانە) دەربارەی براکانیان ( باوەڕداران) لە کاتێکدا خۆیان دانیشتن و دەرنەچوون ئەگەر ئەوان گوێڕایەڵی ئێمە بوونایە نەدەکوژران بڵێ کەواتە ڕێگری مردن لە خۆتان دووربخەنەوە ئەگەر ئێوە ڕاست دەکەن');
INSERT INTO "bamoki" VALUES(3,169,'3:169','ھەرگیز وا گومان مەبەن ئەوانەی کە کوژران لەڕێی خوادا مردوون (نەخێر وانیە) بەڵکو ئەوانە زیندوون لای پەروەردگاریان رۆزی دەدرێن');
INSERT INTO "bamoki" VALUES(3,170,'3:170','شادمان و دڵخۆشن بەوەی کە خوا پێی بەخشیوون لەبەھرە و فەزڵی خۆی شادمانن بەوانە (بەبرا تێکۆشەرەکانیان) کە ھێشتا نەگەیشتوون پێیان لەدوای (شەھیدبوونی) ئەمان نەھیچ ترسێکیان لەسەرە و نە ئەوانە ھیچ غەم و خەفەتێک دەخۆن');
INSERT INTO "bamoki" VALUES(3,171,'3:171','شادمان و دڵخۆشن بەو بەھرەو زیادە فەزڵەی لەلایەن خواوە (پێیان دراوە) وە بەڕاستی خوا پاداشتی بڕواداران وون ناکات');
INSERT INTO "bamoki" VALUES(3,172,'3:172','ئەوانەی کە وەڵامی خواو پێغەمبەریان دایەوە لەپاش ئەوەی کە (لەئوحوددا) تووشی برینداری بوون بۆ ئەوانەیان ھەیە کەکردەوەی چاکیان کردوە, وەخۆیان پاراستووە پاداشتێکی گەورە');
INSERT INTO "bamoki" VALUES(3,173,'3:173','ئەوانەی کەخەڵکی پێیان دەووتن بە ڕاستی خەڵکی (بێ باوەڕ) لەشکر و کۆمەڵی زۆریان کۆکردۆتەوە لە دژتان کەواتە بترسن لێ یان کەچی ئەوان (ئیمانداران) بەمە باوەڕیان زیاد بوو وە ووتیان ھەر خوامان بەسە چەند پشت پێ بەستراوێکی چاکە');
INSERT INTO "bamoki" VALUES(3,174,'3:174','ئەمجا گەڕانەوە (بۆ مەدینە) بە بەھرەی گەورەوە (کەسەلامەتیە) وفەزڵ و پاداشت لەلایەن خواوە ھیچ زیان و ناخۆشیەکیان تووش نەبوو ئەوانە شوێنی ڕەزامەندی خوا کەوتن وەخوا خاوەنی بەھرە وفەزڵی گەورەیە');
INSERT INTO "bamoki" VALUES(3,175,'3:175','بەڕاستی ئەوەی ئێوە (سارد دەکاتەوە) تەنھا شەیتانە کەدۆست و (برادەرانی) خۆی دەترسێنێت جائێوە لێیان مەترسن وە لەمن بترسن ئەگەر ئێوە بڕوادارن');
INSERT INTO "bamoki" VALUES(3,176,'3:176','بادڵ غەمگینت نەکەن ئەوانەی پێشبڕکێ و پەلە دەکەن لەبێ بڕواییدا بێگومان ئەوانە ھەرگیز ھیچ زیانێک ناگەیەنن بەخوا خوا دەیەوێت کەھیچ بەشێکیان بۆ دانەنێت لەڕۆژی دوایی دا وەبۆ ئەوان سزایەکی گەورە ھەیە');
INSERT INTO "bamoki" VALUES(3,177,'3:177','بەڕاستی ئەوانەی کەبێ بڕواییان کڕی (لەم جیھانەدا) بەبڕواو ئیمان ھەرگیز ھیچ جۆرە زیانێک ناگەیەنن بەخوا بۆئەوانە ھەیە سزایەکی بەئێش و ئازار');
INSERT INTO "bamoki" VALUES(3,178,'3:178','ھەرگیز واگومان نەبەن ئەوانەی کەبێ باوەڕن ئەو مۆڵەت دانی ئێمە بۆ ئەوان چاکە بۆ خۆیان بەڕاستی ئێمە مۆڵەتی ئەوان ئەدەین بۆ ئەوەی گوناھی زیاتر بکەن وە بۆ ئەوانە ھەیە سزایەکی ڕیسواکەر');
INSERT INTO "bamoki" VALUES(3,179,'3:179','خوا واز لەبڕواداران ناھێنێت لەسەر ئەو بارەی کەئێوەی لەسەرن (کەجیا نەبوونەوەی موسڵمان و دووڕوو و کافرە) ھەتا جیا دەکاتەوە پیس لەپاک وەخوای گەورە ئێوە ئاگادار ناکات بەسەر نھێنی (غەیب) دا بەڵام خوای گەورە ھەڵی ئەبژێرێت لە نێرراوەکانیدا ھەرکەسێکی بوێت جا بڕوای ڕاستەقینە بێنن بەخوا وەبەنێرراوەکانی وە ئەگەر بەو جۆرە بڕوا بێنن وخۆتان بپارێزن ئەوە پاداشتێکی زۆر گەورەتان بۆ ھەیە');
INSERT INTO "bamoki" VALUES(3,180,'3:180','ھەرگیز واگومان نەبەن ئەوکەسانەی کەچروکی دەکەن بەوەی کە خوا پێی بەخشیوون لەڕێز و بەھرەی خۆی کەئەو (چرووکیە) چاکترە بۆیان (نەخێر وانیە) بەڵکو ئەوە خراپە بۆیان بە زوویی ئەوەی چروکیان پێ دەکرد وەک تەوق دەکرێتە ملیان لەپاشە ڕۆژدا (ڕۆژی ھەستانەوە) وە ھەر بۆخوایە وموڵکی ئەوە میراتی ئاسمانەکان و زەوی (ئەوەی لە ئاسمانەکان و زەویدایە) وە خوا ئاگادارە بە ھەرچی کە ئێوە دەیکەن');
INSERT INTO "bamoki" VALUES(3,181,'3:181','بەڕاستی خوا بیستی ووتەی ئەو(جولەکانەی) کە ووتیان بێگومان خوا ھەژارە و ئێمە دەوڵەمەندین (چونکە داوای قەرزمان لێ دەکات مەبەستیان لە ئایەتی - ومن یقرچ اللە قرچاً حسناً- یە) ئەوەی کە ووتیان دەینوسین (وە دەینووسین) کوشتنی ئەوانە بۆ پێغەمبەرەکان بە ناڕەوا وە (پێیان) دەڵێن بچەژن سزای ئاگری سووتێنەر');
INSERT INTO "bamoki" VALUES(3,182,'3:182','ئەو (سزا سەختە) بەھۆی کردەوەی (خراپی خۆتانەوەیە) کە پێشتان خستووە بێگومان خوا ھیچ جۆرە ستەمێک ناکات لە بەندەکان');
INSERT INTO "bamoki" VALUES(3,183,'3:183','ئەو (جولەکانەی) کە ووتیان بەڕاستی خوا پەیمانی داوە بە ئێمە کە بڕوا نەھێنین بەھیچ نێرراوێک ھەتا قوربانی و خێرێکمان بۆ نەھێنێت کە ئاگر (بێتە خوارەوەو) بیسوتێنێت بڵێ بێگومان ھاتن بۆلاتان چەند پێغەمبەرێک لە پێش منەوە بە بەڵگە ڕوونەکانەوە و بە ئەو (موعجیزەیەی) کە ووتتان دەی بۆچی ئەو (پێغەمبەرانەتان)کوشت ئەگەر ئێوە ڕاست دەکەن');
INSERT INTO "bamoki" VALUES(3,184,'3:184','جا ئەگەر باوەڕیان پێ نەکردی ئەوە بێگومان بڕوا نەکراوە (پێغەمبەرانی پێش تۆش بەدرۆزانران) بە پێغەمبەرانی لەپێش تۆوە ھێناویانە بەڵگە ڕوونەکان و نامەی ئاسمانی (نامەی پەند و تەمێکار) و نامەی ڕوون کەرەوە');
INSERT INTO "bamoki" VALUES(3,185,'3:185','ھەموو (خاوەن) گیانێک مردن دەچێژێت وە بەڕاستی بەتەواوی پێتان دەدرێت پاداشتەکانتان لە ڕۆژی دواییدا (ھەستانەوەدا) ئەمجا ھەرکەس دووربخرێتەوە لە ئاگری دۆزەخ و بخرێتە بەھەشتەوە ئەوە بەڕاستی ئەو کەسە سەرکەوتووە ژیانی دونیاش ھیچ شتێک نیە جگە لە چەند سات و ئارەزوویەکی فریودەر نەبێت');
INSERT INTO "bamoki" VALUES(3,186,'3:186','سوێند بێت بێگومان تاقی دەکرێنەوە لە سامان و دارایی و گیانی خۆتاندا سوێند بێت بێگومان دەیبیستن لەوانەی کە نامەیان پێدراوە لە پێش ئێوە, وە لەوانەی ھاوبەشیان بۆ خوا داناوە (ئازار و) قسەی ناخۆشی زۆر جا ئـەگـەر ئێوە ئارام بگرن و پارێزکاربن ئەوە بێگومان لە کارە بەھێز و گەورەکانە (لەو کارانەیە لێبڕانی دەوێت)');
INSERT INTO "bamoki" VALUES(3,187,'3:187','(بێنەوە بیریان) کاتێک کەخوا وەریگرت پەیمان لەوانەی کەنامەیان پێ بەخشرا بوو (لەجولەکە و گاورەکان) دەبێ ئەو نامەیە ڕوون بکەنەوە بۆ خەڵکی وە نەیشارنەوە بەڵام ئەو (پەیمانەیان) فڕێدایە پشت خۆیانەوە و کڕیان بەو نامەیە نرخێکی کەم ئای چەند خراپە ئەوەی کڕیان');
INSERT INTO "bamoki" VALUES(3,188,'3:188','وەگومان مەبە ئەوانەی کەشادمانن بەوەی کەکردیان و پێیان خۆشە کەسوپاس بکرێن بەشتێک کە نەیان کردووە ئەوە ھەرگیز گومانیان پێ مەبە بەڕزگار بوو لەسزا و بۆ ئەوانە ھەیە سزایەکی ئازاردەر');
INSERT INTO "bamoki" VALUES(3,189,'3:189','ھەر بۆخوایە پادشایەتی وموڵکی ئاسمانەکان و زەوی وە خوا بەسەر ھەموو شتێکدا ﴿﴾ دەسەڵاتدارە');
INSERT INTO "bamoki" VALUES(3,190,'3:190','بەڕاستی لەدروست کردنی ئاسمانەکان و زەویدا و لەجیاوازی و گۆڕانی شەو و ڕۆژدا چەندەھا بەڵگە ھەیە بۆ خاوەن بیرو ھۆشەکان');
INSERT INTO "bamoki" VALUES(3,191,'3:191','ئەوانەی کەیادی خوا دەکەن بەپێوە و بەدانیشتانەوە بەڕاکشانەوە (لەسەر تەنیشت) وەبییر دەکەنەوە لەدروست کردنی ئاسمانەکان و زەوی (ئەڵێن) ئەی پەروەردگارمان ئەم (جیھانەت) دروست نەکردووە بەبێ ھودە پاک و بێگەردی بۆ تۆ دەسا تۆش بمان پارێزە لە سزای ئاگری دۆزەخ');
INSERT INTO "bamoki" VALUES(3,192,'3:192','ئەی پەروەردگارمان ھەرکەس تۆ بیخەیتە ئاگری دۆزەخەوە ئەوە بێگومان ڕیسوات کردووە, وە ھیچ پشتیوانێک نیە بۆ ستەمکاران');
INSERT INTO "bamoki" VALUES(3,193,'3:193','ئەی پەروەردگارمان بەڕاستی ئێمە گوێمان لە بانگخوازێک بوو (مەبەست پێ پێغەمەمبەری خودایە) بانگی ئەکرد بۆ بڕواھێنان (دەی ووت) بڕوا بێنن بە پەروەردگارتان دەست بەجێ بڕوامان ھێنا ئەی پەروەردگارمان دەی لە گوناھەکانمان خۆش ببە وبسڕەوە لێمان خراپەکانمان و بمان مرێنە لەگەڵ کەسانی چاکەکاردا');
INSERT INTO "bamoki" VALUES(3,194,'3:194','ئەی پەروەردگارمان پێمان ببەخشە (لەپاش مردن) ئەوەی بەڵێنت پێ داین لەسەر زوبانی پێغەمبەرەکانت و ڕیسوامان مەکە لە ڕۆژی دوایی دا (ھەستانەوە) بێگومان تۆ بەڵێن ناشکێنیت');
INSERT INTO "bamoki" VALUES(3,195,'3:195','ئەمجا پەروەردگاریان وەڵامی (نزاکانی) دانەوە (فەرمووی) بێگومان من زایەی ناکەم کردەوەی (ھیچ) خاوەن کردەوەیەک لە ئێوە لە نێر یان مێ ھەندێکتان لە ھەندێکی ترتان بوون جا ئەوانەی کە کۆچیان کرد و دەرکران لە جێ و شوێنی خۆیان و ئەشکەنجەو ئازار دران لە ڕێ ی مندا و جەنگیان کرد و کوژران سوێند بەخوا دەیسڕمەوە لێیان خراپەکانیان وە بێگومان دەیان خەمە بەھەشتانێکەوە کە ڕووبارەکان دێن و دەچن بەژێریاندا ئەمە پاداشت دانەوەیەکە لە لای خواوە, وە لای خوایە جوانترین (چاکترین) پاداشت');
INSERT INTO "bamoki" VALUES(3,196,'3:196','لە خشتەت نەبات (فریوت نەدا) ھات و چۆی ئەوانەی بێ بڕوان (بەخۆشی و ئاسوودەییەوە) لە ووڵاتدا');
INSERT INTO "bamoki" VALUES(3,197,'3:197','ئەو ئاسوودەییەی ئەوان خۆشیەکی کەمە پاشان جێ و شوێنیان دۆزەخە ئای چ شوێنێکی خراپ و بەدە');
INSERT INTO "bamoki" VALUES(3,198,'3:198','بەڵام ئەوانەی لە پەروەردگاریان دەترسن بۆیان ھەیە بەھەشتانێک کە ڕووبارەکان دێن و دەچن بەژێریاندا تیایدا دەمێننەوە بە ھەمیشەیی ئامادە کراوە لە لایەن خواوە, وە ئەوەی کە لای خوایە چاکترە بۆ چاکەکاران');
INSERT INTO "bamoki" VALUES(3,199,'3:199','بێگومان لەناو نامە دارەکاندا ھەیە کەسانێک کە بڕوا دەھێنن بەخوا و بەو نامەیەی کە نێرراوەتە خوارەوە بۆ ئێوە (مەبەست پێ قورئانە) وە بەو نامەی کە نێرراوەتە خوارەوە بۆ خۆیان مل کەچن بۆ خوا بەڵگەکانی خوا نافرۆشن بە نرخێکی کەم ئەو نامە دارانە بۆیان ھەیە پاداشتی خۆیان لە لای پەروەردگاریانەوە بێگومان خوا لێپرسینەوە وموحاسەبەکردنی خێرایە');
INSERT INTO "bamoki" VALUES(3,200,'3:200','ئەی ئەوانەی کە بڕواتان ھێناوە خۆ گربن و (لە خۆگری دوژمن) خۆگرتر بن و لە سەنگەردا ئێشک بگرن و لە خوا بترسن بۆ ئەوەی ڕزگار ببن');
INSERT INTO "bamoki" VALUES(4,1,'4:1','ئەی خەڵکینە خۆتان بپارێزن لە (سزای) پەروەردگارتان ئەوەی کە درووستی کردوون لەیەک کەسەوە (کە ئادەمە) وە ھەر لە ئەویش ھاوسەرەکەی درووست کردووە و بڵاوی کردۆتەوە لەو دووانە پیاوانی زۆر ھەروەھا ئافرەتان خۆتان بپارێزن لەو خوایەی کە بەناوی ئەوەوە داوا دەکەن لە یەکتر وە (خۆتان بپارێزن لە بچڕاندنی) خزمایەتی بەڕاستی خوا ھەمیشە چاودێرە بەسەرتانەوە');
INSERT INTO "bamoki" VALUES(4,2,'4:2','بیدەنەوە بە ھەتیوان (دوای باڵغ بونیان) ماڵ و سامانیان وە پیس (لە ماڵی خۆتان) مەگۆڕنەوە بە پاک (لە ماڵی ئەوان) وە ماڵەکانیان مەخۆن لەگەڵ ماڵەکانی خۆتان بێگومان ئەو خواردنە گوناھێکی گەورەیە');
INSERT INTO "bamoki" VALUES(4,3,'4:3','وە ئەگەر ترسان دادپەروەر نەبن لە (مارەیی) ئەو ئافرەتە بێ باوکانەدا (مارەیان مەکەن) ئەوە مارە بکەن لەو ئافرەتانەی حەڵاڵن بۆتان دوو دوو یا سیان سیان یا چوار چوار (واتە تا چوار ژن درووستە لەیەک کاتدا کۆی بکەیتەوە) جا ئەگەر ترسان دادگەر نەبن ئەوە یەک ئافرەت (مارە بکەن لەوانەی ئازادن) یان ئەو کەنیزەکانەی کە موڵکتانن ئەوە نزیکترە کە ستەم نەکەن (یا ھەژار نەکەون)');
INSERT INTO "bamoki" VALUES(4,4,'4:4','بیدەن بە ئافرەتان (نەک کەس وکاریان) مارەییەکانیان وەک بەخشین نەک وەک کرێ ئەمجا ئەگەر بە دڵ پێیان خۆش بوو بەخشینی شتێک لەو (مارەییە) بە ئێوە ئەوە ئێوە بیخۆن بە خۆشی و حەڵاڵی (نۆشی گیانتان بێ)');
INSERT INTO "bamoki" VALUES(4,5,'4:5','مەیدەنە گێل وکەم عەقڵەکان ماڵەکانتان لە حاڵەتێکدا خوا ئەو (ماڵانەی) بۆ کردوون بەھۆی ڕاوەستانی ژیانتان وە (بێ عەقڵەکان) ڕۆزی و خۆراکیان بدەنێ لەو ماڵە و پۆشاکیان بۆ بکەن و گفتوگۆیان لەگەڵ بکەن بە گفتوگۆیەکی باش');
INSERT INTO "bamoki" VALUES(4,6,'4:6','ھەتیوان تاقی بکەنەوە ھەر کاتێک گەیشتنە (تەمەنی) ژن ھێنان و شووکردن ئەمجا ئەگەر ھەستان کرد لەواندا ژیری (و پێگەیشتن) ئەوە سامان و ماڵەکانیان پێ بدەنەوە و ماڵەکانیان مەخۆن بە زیادەڕەوی و بەپـەلـە نەوەک گەورە ببن (ولێتان وەرگرنەوە) وە ھەر کەس (لەسەرپەرشتیاری ھەتیوان) دەوڵەمەند بوو با خۆی بگرێتەوە (لە خواردنی ماڵیان) وە ھەر کەس لەوان ھەژار بوو با (لێی) بخوات بە ئەندازەی پێویست ئەمجا ھەر کات ماڵی ھەتیوەکانتان دایەوە پێیان ئەوە شایەتیان لەسەر بگرن بەسە خوا بۆ لێپرسینەوە (بەسەرتانەوە)');
INSERT INTO "bamoki" VALUES(4,7,'4:7','بۆ پیاوان ھەیە بەشێک لە و (میراتەی) دایک و باوک بەجێی دەھێڵن و خزمە نزیکەکان وە بۆ ئافرەتانیش ھەیە بەشێک لە و (میراتەی) دایک و باوک بەجێی دەھێڵن و خزمە نزیکەکان لە ئەوەی (بەجێی دەھێڵن) کەم بێت یان زۆر بەبەشێکی بڕیار دراو');
INSERT INTO "bamoki" VALUES(4,8,'4:8','جا ئەگەر کاتی دابەش کردنی (میراتەکە) ئامادەبوون خزمە نزیکەکان (کە میرات ناگرن لە مردوو) و ھەتیوو و ھەژارە (بێگانەکان) ئەو لەو کەلەپوورە بەشیان بدەن (ئەم بەش دانە سوننەتە نەک واجب) وە قسەیان لەگەڵ بکەن بە قسەکردنێکی جوان و چاک');
INSERT INTO "bamoki" VALUES(4,9,'4:9','ئەوانەی (سەرپەرشتیارن) با بترسن ئەگەر بەجێی بھێڵن لەپاش خۆیانەوە (پاش مردن) منداڵانی لاواز (بچوک) دەترسن لێیان (بفەوتێن) دەبا لەخوا بترسن و قسەی ڕاست و ڕەوا بکەن (مەبەست پێی ئەوەیە وەسیەتێک نەکەن زیان بگەیەنن بەمنداڵەکانیان پاش خۆیان بەھەژاری بەجێیان بھێڵین وھەموو ماڵ وسەروەتەکەیان بکەن بەخێر و سەدەقە)');
INSERT INTO "bamoki" VALUES(4,10,'4:10','بەڕاستی ئەو کەسانەی کە ماڵی ھەتیوەکان دەخۆن بەستەم و ناڕەوا بەڕاستی ئەوانە بەس لە سکی خۆیان ئاگر دەخۆن. وە لەمەوپاش دەچنە ئاگرێکی بەتین و ھەڵگیرساوەوە');
INSERT INTO "bamoki" VALUES(4,11,'4:11','خوا ئامۆژگاریتان دەکات دەربارەی (میراتی) ڕۆڵەکانتان بۆ نێرێک ھەیە (لە میرات) بە وێنەی بەشی دوو مێینە خۆ ئەگە (منداڵەکانتان) تەنھا ئافرەت بوون (دووان) و زیاد لەدووان ئەوا بۆیان ھەیە دوو بــەش لـە سـێ بەشـی ئەوەی (مردووەکە) بەجێ ی ھێشتووە بەڵام ئەگەر یەک ئافرەت بوو ئەوە نیوەی (میراتەکەی) بۆ ھەیە وە بۆ ھەر کام لە دایک و باوکی مردووەکە ھەیە شەش یەک لەوەی کە بەجێی ھێشتووە ئەگەر مردووەکە منداڵی ھەبوو بەڵام ئەگەر مردووەکە منداڵی نەبوو و دایک و باوکی میراتگری بوون ئەوە سێ یەک بۆ دایکەکەیەتی بەڵام ئەگەر (مردووەکە) چەند برایەکی ھەبوو ئەوا شەش یەک بۆ دایکەکەیەتی لەپاش (دەرکردنی) وەسیەتێک وەسێتی پێ کرابێ (لە لایەن مردووەکەوە) یان دانەوەی قەرزێک (کە لەسەری بێت) باوکەکانتان و کوڕەکانتان نازانن کامەیان بەسوود ترن بۆتان (ئەو دابەش کردنە) لە لایەن خواوە پێویست کراوە بەڕاستی خوا ھەمیشە زانای دانایە');
INSERT INTO "bamoki" VALUES(4,12,'4:12','بۆ ئێوە ھەیە نیوەی ئەو (میراتەی) کەژنەکانتان بەجێ یان ھێشتووە ئەگەر ئەو ژنانە منداڵیان نەبوو جا ئەگەر منداڵیان ھەبوو بۆ ئێوە ھەیە چوار یەک لەوەی (ژنەکانتان) بەجێیان ھێشتووە لەپاش (دەرکردنی) وەسیەتێک کەکردبێتیان یان قەرزێک (کەلەسەریان بێت) بۆ (ژنەکانتان) ھەیە چوار یەک لەوەی بەجێتان ھێشتووە ئەگەر ئێوە منداڵتان نەبوو جا ئەگەر منداڵتان ھەبوو ئەوەبۆ ئەوان ھەیە ھەشت یەک لەوەی بەجێتان ھێشتووە لەپاش (دەرکردنی) وەسیەتێک کەکردبێتتان یان دانەوەی قەرزێک (کەلەسەرتان بێت) وە ئەگەر پیاوێک یان ئافرەتێک کەمیراتی لێ ئەگیرا (مردبوو) نەباوان (دایک وباوکی) ھەبوو نە منداڵ وە تەنھا برایەک یان خوشکێکی (لەدایکەوە) ھەبوو ئەوە بۆ ھەر یەک لەو دووانە ھەیە شەش یەک بەڵام ئەگەر ئەوان زیاتر بوون لەبرایەک یان خوشکێکی دایکی ئەو کاتە ئەوانە ھەموویان ھاوبەشن لەسێ یەکی (میراتەکە) دا لەپاش (دەرکردنی) وەسیەتێک کەکرابێت یان قەرزێک کە (وەسێتەکە یاقەرزەکە بۆ میراتگران) بێزیان بێت ئەمە بڕیارە لەلایەن خواوە خواش زانای لەسەرخۆیە');
INSERT INTO "bamoki" VALUES(4,13,'4:13','ئەو (یاسایانە) سنورەکانی خودان ھەر کەسێک گوێڕایەڵی خوا و پێغەمبەرەکەی بێت (خوا) دەیخاتە ناو بەھەشتانێک کەڕووبارەکان دێن و دەچن بەژێریاندا تیایدا دەمێننەوە بەھەمیشەیی وەئەوەیە سەرکەوتنی زۆر گەورە');
INSERT INTO "bamoki" VALUES(4,14,'4:14','ھەر کەسێکیش سەرپێچی خواو پێغەمبەرەکەی بکات و سنورەکانی (خوا) ببەزێنێت دەیخاتە ئاگرێکەوە ھەمیشە دەمێنێتەوە تیایدا وەسزایەکی ڕیسواکەری بۆ ھەیە');
INSERT INTO "bamoki" VALUES(4,15,'4:15','ئەوانەی کە کاری زۆر پیس و ناشرین (زینا) دەکەن لە ئافرەتانی (بڕوادارتان) ئەوە چوار شایەت لە خۆتان لەسەریان بگرن ئەمجا ئەگەر شایەتییاندا ئەوە ئەو (ژنە زیناکارانە) بەند بکەن لە ماڵەکانتاندا ھەتا (فریشتەی) مەرگ گیانیان دەکێشێت یان خوا ڕێگایەکی (ڕزگار بوونیان) بۆ دائەنێت (ئەمە حوکمی زیناکردن بوو پێش ھاتنە خوارەوەی ئایەتەکانی سورەتی النور واتە حومکی ئەم ئایەتەی سورەتی (النساء) بە ئایەتەکانی سورەتی النور سڕراوەتەوە)');
INSERT INTO "bamoki" VALUES(4,16,'4:16','وە ئەو دووانەی (کوڕو کچەی) ئەو کارە پیسە (زینا) دەکەن لە ئێوە ئەوە ئازاریان بدەن جا ئەگەر گەڕانەوە و تەوبەیان لێکرد وە چاک بوون ئەوا وازیان لێ بێنن چونکە بەڕاستی خوا ھەمیشە لێبووردەی میھرەبانە');
INSERT INTO "bamoki" VALUES(4,17,'4:17','بێگومان وەرگرتنی تەوبە لای خوا تەنھا بۆ ئەوانەیە کە گوناە دەکەن بە نەفامی پاشان پەشیمان دەبنەوە بە زوویی (پێش مردن) ئا ئەوانە خوا گەڕانەوەیان لێ وەردەگرێت خواش ھەمیشە زانا و دانایە');
INSERT INTO "bamoki" VALUES(4,18,'4:18','پەشیمانی (وەرگرتن) نیە بۆ ئەوانەی تاوانی زۆر دەکەن ھەتا کاتی سەرە مەرگی یەکێکیان دێت دەڵێت: بەڕاستی من ئێستا تەوبەم کرد وە بۆ ئەوانەش نیە کە دەمرن بە بێ بڕوایی ئەوانەی (باسکران) ئامادەمان کردووە بۆیان سزایەکی ئازار دەر');
INSERT INTO "bamoki" VALUES(4,19,'4:19','ئەی ئەوانەی کە بڕواتان ھێناوە حەڵاڵ نیە بۆتان کە ئافرەتان بە زۆر (بە ناچاری) بە میراتی بەرن (بۆ خۆتان) وە ڕێگری شوکردنیان لێ مەکەن بۆ ئەوەی ھەندێک لەو (مارەییەی) کە پێتان داون بیبەن (بۆ خۆ تان) مەگەر کاتێک کە کردەوەیەکی زۆر ناشرین (زینا) بە ئاشکرا بکەن وە ڕەفتاریان لەگەڵ بکەن بەچاکی (و ژیانیان لەگەڵ ببەنە سەر) جا ئەگەر حەزتان لێیان نەبوو (تەڵاقیان مەدەن) چونکە لەوانەیە ئێوە حەز نەکەن لە شتێک و خوا لەوەدا داینابێت چاکە وخێرێکی زۆر');
INSERT INTO "bamoki" VALUES(4,20,'4:20','ئەگەر ویستتان گۆڕینی ژنێک لەشوێن ژنێک (کەحەزی لێ ناکەن) وە دابێتتان بەیەکێکیان ماڵێکی زۆر (بەمارەیی) ئەوە مەی سەننەوە لێی ھیچ شتێک (لەوماڵە زۆرە) ئایا ئەو (مارەییەیان) لێ ئەسەننەوە بەبوختان کردن و تاوانێکی ئاشکرا (بەرامبەری ئافرەتەکە)');
INSERT INTO "bamoki" VALUES(4,21,'4:21','جا چۆن وەری دەگرنەوە؟ لەکاتێکدا چوونەتەلای یەکتری (تێکەڵاوی ژن و مێردایەتیتان لەگەڵ یەک کردووە) وەئەو ئافرەتانە وەریان گرتووە لێتان پەیمانێکی پتەو بەھێز (کەوا بەستنی نیکاحە)');
INSERT INTO "bamoki" VALUES(4,22,'4:22','(ئەی پیاوان) مارە ی مەکەن ئەو ئافرەتانەی کەباوکەکانتان مارەیان کردوون (واتە باوەژنەکانتان) مەگەر ئەوەی پێش لەم (حەرام کردنە) کرابێت بەڕاستی ئەو مارەکردنە کردەوەیەکی زۆر پیس وچەپەڵە زۆر بێزراو وڕێبازێکی زۆر خراپ و بەدە (کەگیراوەتە بەر)');
INSERT INTO "bamoki" VALUES(4,23,'4:23','قەدەغە کراوە لەسەرتان (حەرام کراوە) (مارەکردنی) دایکانتان و کچەکانتان و خوشکەکانتان و پورەکانتان (کەخوشکی باوکتانن) و پورەکانتان (کەخوشکی دایکتانن) و کچانی برا و کچانی خوشک ئەو دایکانەتان کەشیریان پێداون وخوشکەکانتان لەشیرەوە و دایکی ژنەکانتان (خەسوو) و کچانی ژنانتان کە لەمێردی تریان بن ئەوانەی کەلە ماڵی ئێوەدا دەژین لەو ژنانەتانن کەچوونەتە لایان جا ئەگەر نەچووبونە لایان (لای دایکی کۆڵەبەسەکان) ئەوە ھیچ گوناھێکتان لەسەر نیە (لەمارە کردنی کچەکانیاندا) وە (حەرامە بۆتان) ھاوسەری کوڕەکانتان (بووکەکانتان) ئەوانەی کەلە پشتی خۆتانن وە (قەدەغە وحەرامکراوە لەسەرتان) کۆکردنەوەی نێوان دوو خووشک لەیەک کاتدا مەگەر ئەوەی پێش ئەم (حوکمە لەنەفامیدا) کرابێت بێگومان خوا ھەمیشه لێبوردە و میھرەبانە');
INSERT INTO "bamoki" VALUES(4,24,'4:24','(ھەروەھا حەرام کراوەلێتان) ئەوانەی کەمێردیان ھەیە لە ئافرەتان جگە لەوانەی کە بوونەتە موڵکتان (کەنیزەک) (ئەو حوکمانە باسکران) پێویست کراوو نوسراوی خوایە لەسەرتان وە بۆتان حەڵاڵکراوە بێجگە لەوانە (کە باسکران ھەمووی حەڵاڵە بۆتان تا چوار ژن بەیەکەوە کۆ بکەنەوە) کە بەماڵ وسامانتان خوازبێنی (ئافرەتان) بکەن (بەمەرجێک) کە داوێن پاک بن و داوێن پیس نەبن جا ئەوەی کە لەزەتتان لێوەرگرت لەو ئافرەتانە ی (حەڵاڵن) ئەوا مارەییەکەیان پێ بدەن کەفەرزکراوە لەسەرتان تاوانتان لەسەر نیە لەوەی کە لەسەری پێک ھاتن (لەگەڵ ژنەکانتان دا لەکەمکردنەوەی مارەیی) لە پاش دیاریکردنی مارەیی بەڕاستی خودا ھەمیشە زانا و کاردروستە');
INSERT INTO "bamoki" VALUES(4,25,'4:25','وە ئەوەی لە ئێوە نەیتوانی لەبەر ھەژاری کە ئافرەتانی ئازادی ئیماندار مارەبکات ئەوە با لەوانە مارە بکات کەبوونەتە موڵکتان لە کەنیزەکە ئیماندارەکانتان وە خوا زاناترە بەباوەڕەکەتان ھەندێکتان لەھەندێکی ترتانن (ھەمووتان نەوەی ئادەمن) لەبەرئەوە مارەیان بکەن بەڕەزامەندی کەسوکاریان و مارەییەکەیان پێ بدەن بەشێوازێکی چاک لەکاتێکدا ئەو کەنیزەکانە پاک داوێن بن داوێن پیس (و زیناکاری بەئاشکرا) نەبن وە دۆستیان نەبێت (بەنھێنی بۆ زینا کردن) جا ئەگەر ئەو (کەنیزەکانە) شوویان کرد پاشان خراپەیەکی ئاشکرایان کرد (زینا) ئەوە نیوەی سزای ئافرەتە ئازادەکانیان لەسەرە (لێیان بدرێت کە پەنجا جەڵدە) ئەو (مارەکردنی کەنیزەکە) بۆ کەسێکە لەئێوە لە تاوان کردن (زینا) بترسێت بەڵام ئەگەر ئارام بگرن (و کەنیزەک مارە نەکەن) چاکترە بۆتان خوا لێبوردەی بەبەزەییە');
INSERT INTO "bamoki" VALUES(4,26,'4:26','خوا دەیەوێت بۆتان ڕوون بکاتەوە, وە ڕێنمونیتان بکات بۆ ڕێ وشوێنی ئەوانەی لە پێش ئێوەوە بوون (لەخواناسان) و لێتان خۆش ببێت وە خوا زانای کاردروستە');
INSERT INTO "bamoki" VALUES(4,27,'4:27','وەخوا دەیەوێت کەلێتان ببورێت بەڵام ئەیانەوێت ئەوانەش کەشوێن ئارەزو و ھەواو ھەوەسەکانیان کەوتوون کە لاتان بدەن بەلادانێکی گەورە');
INSERT INTO "bamoki" VALUES(4,28,'4:28','خوا دەیەوێت کەئەرکتان لەسەر سووک بکات چونکەئادەمی بە لاواز دروست کراوە');
INSERT INTO "bamoki" VALUES(4,29,'4:29','ئەی ئەوانەی باوەڕتان ھێناوە سامانی یەکتری مەخۆن لەنێوانتاندا بەناڕەوا مەگەر بازرگانیەک بێت بەڕەزامەندی لەنێوان خۆتان دا وە خۆتان مەکوژن (یان خۆتان مەفەوتێنن بەتاوان) بەڕاستی خواھەمیشە بەبەزەییە پێتان');
INSERT INTO "bamoki" VALUES(4,30,'4:30','وە ھەرکەسێک ئەوە بکات (واتە یەکێک بکوژێت) بەدەست درێژی و ستەم ئەو لەمەودوا دەیخەینە ناو ئاگرەوە, وە ئەوەش ھەمیشە بۆ خوا ئاسانە');
INSERT INTO "bamoki" VALUES(4,31,'4:31','ئەگەر دوور بکەونەوە لەو تاوانە گەورانەی کەلێتان قەدەغەکراوە ئەوە تاوانە بچووکەکانتان بۆ دەپۆشین وە دەتانخەینە شوێنێکی چاک و ڕێزدارەوە');
INSERT INTO "bamoki" VALUES(4,32,'4:32','وە ئاوات مەخوازن بۆ ئەو پلەیەی کە خوا فەزڵی ھەندێکتانی داوە بەسەر ھەندێکی ترتاندا بۆ پیاوان بەش ھەیە لەوەی بەدەستی ئەھێنن وە بۆ ئافرەتانیش بەش ھەیە لەوەی بەدەستی ئەھێنن وە داوا لەخوا بکەن لەبەھرە و بەخششی خۆی بەڕاستی خوا ھەمیشە بەھەموو شتێک زانایە');
INSERT INTO "bamoki" VALUES(4,33,'4:33','وە دامان ناوە بۆ ھەموو (مردوویەک یان میراتێک) چەند میرات گرێک (کە بەشێ ئەگرن) لەوەی کە دایک و باوک و خزمانیان بەجێیان ھێشتووە, وە ئەوانەش کەپەیمانتان لەگەڵ گرێ داون (کە میرات لەیەکتربەرن) ئەوە بەشی خۆیانیان بدەنێ بەڕاستی خوا ھەمیشە بەسەر ھەموو شتێکدا ئاگادارو چاودێرە');
INSERT INTO "bamoki" VALUES(4,34,'4:34','پیاوان سەرپەرشتیارن (کاربەدەستن) بەسەر ئافرەتانەوە بەھۆی ئەوەوە خوا فەزڵی ھەندێکیانی داوە بەسەر ھەندێکیاندا ھەروەھا بەھۆی ئەوەی کە (پیاوان) لەسامانەکانیان خەرج دەکەن جا ئافرەتە چاکەکان (ئەوانەن) گوێرایەڵانی بەردەوام (بۆ خواو پاشان مێردەکانیان) پارێزەرانی (داوێنی خۆیان وسامانی مێردەکانیانن) کە مێردەکانیان لەماڵەوە نەبن بەھۆی ئەوەی خوا فەرمانی پاراستنی داوە, وەئەو ئافرەتانەش کەئێوە دەترسن سەرپێچیتان بکەن ئەوە ئامۆژگاریان بکەن و جێگەیان لێ جیابکەنەوە (و لەگەڵیان مەخەون) وە لێیان بدەن بە (سوکی بەشێوەیەک کەلەشی عەیبدار نەکات) جا ئەگەر گوێرایەڵیان کردن ئەوە ئیتر ھیچ رێگەیەک لەدژیان مەگرنەبەر بەڕاستی خوا ھەمیشە بەرز وبڵند و گەورەیە');
INSERT INTO "bamoki" VALUES(4,35,'4:35','وە ئەگەر ترسان لە تێکچونی نێوانیان (ژن و مێرد) ئەوە ناوبژیوانێک بنێرن لەخزمی پیاوەکە و ناو بژیوانێک لە خزمی ئافرەتەکە ئەگەر (دوو ناوبژیوانەکە) ئاشتییان بوێت خوا نێوان ژن و مێردەکە چاک دەکاتەوە بەڕاستی خوا ھەمیشە زانا و ئاگادارە');
INSERT INTO "bamoki" VALUES(4,36,'4:36','بەس خوا بپەرستن، و ھیچ شتێک مەکەن بەھاوبەشی ھەروەھا لەگەڵ دایک و باوکتاندا چاک بن وە لەگەڵ خزم و نزیکان و ھەتیوان و ھەژاران وھاوسێی خزم و ھاوسێی بێگانە یا دوور و ھاوڕێی ھەمیشەیی یا ھاوڕێی سەفەر و ڕێبوار و ئەوانەی بوون بەموڵکتان (چاک بن لەگەڵیاندا) بەڕاستی خوا خۆشی ناوێت ئەو کەسەی کە خۆ بەزل زان و شانازیکار بێت');
INSERT INTO "bamoki" VALUES(4,37,'4:37','ئەوانەی کە چرووکن فەرمانیش دەکەن بەخەڵکی بەچرووکی وە دەیشارنەوە ئەوەی خوا پێی بەخشیوون لە بەخششی خۆی وە ئامادەمان کردوە بۆ بێباوەڕان سزایەکی ریسواکەر');
INSERT INTO "bamoki" VALUES(4,38,'4:38','وە ئەوانەی ماڵەکانیان دەبەخشن لەبەر ڕوپامایی خەڵکی باوەڕیش ناھێنن نەبەخوا و نەبەڕۆژی دوایی وە ھەرکەس شەیتان ھاورێی بێت ئەوە (شەیتان) ھاورێیەکی زۆر خراپە');
INSERT INTO "bamoki" VALUES(4,39,'4:39','وە ئەوانە چ زیانێکیان دەکەوتە سەر؟ ئەگەر باوەڕیان بھێنایە بەخوا وە بەڕۆژی دوایی وەبیان بەخشییایە لەوەی کەخوا پێ ی بەخشیبوون وەخوا ھەمیشە زانایە پێیان');
INSERT INTO "bamoki" VALUES(4,40,'4:40','بەڕاستی خوا بە ئەندازەی گەردیلەیەک ستەم ناکات وە ئەگەر چاکەیەک ھەبێت (خودا) پاداشتەکەی چەندجارە دەکاتەوە و دەیبەخشێت لەلایەن خۆیەوە پاداشتێکی گەورە');
INSERT INTO "bamoki" VALUES(4,41,'4:41','ئەمجا (حاڵی کافران) چۆن بێت کاتێک ھێنامان لە ھەموو ئومەتێکدا شایەتێک (کە پێغەمبەرەکەیانە) تۆیشمان ھێنا (ئەی موحەممەد صلى الله عليه وسلم) بەشایەت بەسەر ئائەوانەوە');
INSERT INTO "bamoki" VALUES(4,42,'4:42','لەو ڕۆژەدا ئاوات دەخوازن ئەوانەی کافر بوون و سەرپێچی پێغەمبەریان کرد کە زەویان بەسەردا تەخت بکرێت (واتە بخرێنە ژێر زەوی و نەھێنرێنە دەرەوە) وە (لەو ڕۆژەدا) ھیچ قسەو باسێک لەخوا ناشارنەوە');
INSERT INTO "bamoki" VALUES(4,43,'4:43','ئەی ئەو کەسانەی کەبڕواتان ھێناوە نزیکی نوێژ مەبنەوە کاتێک ئێوە سەرخۆش بن ھەتا بزانن چی دەڵێن وەھەروەھا نزیکی (جێگای) نوێژ مەبنەوە لەکاتێکدا لەشتان بێ نوێژە (لەشتان پیسە) مەگەر تێپەڕبن (بە مزگەوتدا) ھەتا خۆتان دەشۆن وە ئەگەر ئێوە نەخۆش بوون یان لەسەفەردا بوون یان کەسێک لەئێوە ھاتەوە لەشوێنی دەست بەئاو گەیاندنەوە یان جووت بوون لەگەڵ خێزانەکانتاندا (یالە یەک کەوتن لەگەڵ ئافرەتانی نامەحرەمدا) ئەمجا ئاوتان دەست نەکەوت ئەوە بەخۆڵێکی پاک تەیەموم بکەن جا بەو تۆزو خۆڵە ڕووخسار و دەستەکانتان تەیەموم بکەن بەڕاستی خوا ھەمیشە سڕەرەوەی تاوانە و لێبووردەیە');
INSERT INTO "bamoki" VALUES(4,44,'4:44','ئایا سەیری ئەوانەت نەکردووە کە بەشێک لەنامەکەیان پێدراوە (کە تەوراتە) گومڕایی ھەڵدەبژێرن (دەکڕن) وە دەیانەوێت ئێوەش لەڕێبازی ڕاست گومڕاببن');
INSERT INTO "bamoki" VALUES(4,45,'4:45','وەتەنھا خوا چاک ئاگادارە بەدوژمنەکانتان وە تەنھا خوا بەسە دۆست بێت تەنھا خواش بەسە یاریدەدەر بێت');
INSERT INTO "bamoki" VALUES(4,46,'4:46','ھەندێک لە جوولەکەکان فەرموودەکانی خوا دەگۆڕن لەشوێن و مەبەستی خۆی وە دەڵێن: بیستمان و سەرپێچیمان کرد!! گوێ بگرە قەت گوێت لێ نەبێ!! وە دەڵێن (راعنا) چاک گوێمان بۆ بگرە (بەڵام بەزمانی جولەکە واتە تۆ خراپترینمانی ئەوان ئەم مانایان مەبەست بوو) دەمیان خوار دەکردەوە (بۆ ئەوەی مەبەستە خراپەکە بگەیەنێت) وە بۆتانەو تەشەردان لەئایینی (ئیسلام) وە ئەگەر ئەوان بیان ووتایە بیستمان و گوێرایەڵین و (قسەمان) ببیستەو پەلەمان لێ مەکە ئەوە چاکترو ڕاست تر بوو بۆیان بەڵام خوا نەفرەتی لێکردن بەھۆی بێ باوەڕییەکەیانەوە بۆیە بڕوا ناھێنن کەمێکیان نەبێت');
INSERT INTO "bamoki" VALUES(4,47,'4:47','ئەی ئەوانەی کەنامەتان پێ بەخشراوە بڕوا بھێنن بەو (قورئانە) ی ناردوومانەتە خوارەوە کە بەڕاست دانەری ئەو (تەوراتەی) ئێوەیە پێش ئەوەی ڕوخسارانێک ساف بکەین (لەپێشەوە) ئەمجا بیبەینە پشت سەریان یان نەفرەتیان لێ بکەین وەک چۆن نەفرەتمان کرد لە خەڵکانی شەممە (ئەوانەی کەڕاوە ماسییان لێ قەدەغە کرا لە ڕۆژی شەممەدا کەچی دەھاتن لەڕۆژی ھەینیدا تۆڕەکانیان دادەخست و ڕۆژی یەک شەممە ھەڵیان دەگرتەوە) وەبڕیاری خوا ھەمیشە بەدی دێت');
INSERT INTO "bamoki" VALUES(4,48,'4:48','بەڕاستی خوا خۆش نابێت گەر ھاوبەشی بۆ دابنرێ بێجگە لەوە خۆش ئەبێت لەھەر گوناھێکی تر بۆ ھەرکەسێک کە بیەوێت وە ھەرکەسێک ھاوبەش بۆ خوا دابنێت ئەوە بێگومان گوناھێکی زۆر گەورەی ھەڵبەستووە');
INSERT INTO "bamoki" VALUES(4,49,'4:49','ئایا سەیری ئەوانەت نەکردووە کەخۆیان بەپاک و چاک دەزانن (وانیە) بەڵکو خوا ھەر کەسێکی بوێت پاک و خاوێنی دەکات و کەمترین ستەمیان لێ ناکرێت (بابەئەندازەی ئەو دەزوە باریکەی ناوکی خورماش بێت)');
INSERT INTO "bamoki" VALUES(4,50,'4:50','سەیری بکە چۆن بەدەم خواوە درۆ ھەڵدەبەستن وە بەسە ئەو درۆیەکە گوناھێکی ڕوون و ئاشکرایە');
INSERT INTO "bamoki" VALUES(4,51,'4:51','ئایا ئاوڕێکت نەداوەتەوە لەوانەی بەشێک لەنامەی خواییان پێدراوە بڕوا ئەھێنن بەشتی بێ نرخ و بەرنامەی غەیری خوا (بە جادووگەران و بەشەیتان) وە ئەڵێن بەوانەی کەبێ بڕوان ئەوانە رێبازیان ڕاسترە لەوانەی کەباوەڕیان ھێناوە');
INSERT INTO "bamoki" VALUES(4,52,'4:52','ئا ئەوانە کەسانێکن کە خوا نەفرینی لێ کردوون ھەرکەسێکیش خوا نەفرینی لێ بکات ئەوە ھەرگیز دەست ناکەوێت بۆی ھیچ یارمەتی دەرێ');
INSERT INTO "bamoki" VALUES(4,53,'4:53','ئایا ئەوانە (جولەکەکان) بەشیان ھەیە لە دەسەڵاتی پاشایەتی (خاوەنێتی بوونەوەر)؟ (جا ئەگەر بیانبوایە) ئەو کاتە بەخەڵکیان نەدەبەخشی (بە ئەندازەی) توێکڵی دەنکە خورمایەک');
INSERT INTO "bamoki" VALUES(4,54,'4:54','یان ئێرەیی (حەسوودی) دەبەن بەخەڵکی؟ لەسەر ئەوەی خوا پێی بەخشیوون (کە پێغەمبەرێتیە) لە فەزڵ و بەھرەی خۆی ئەوە بێـگومان داومانە بەنەوەی ئیبراھیم کتێب و پێغەمبەرێتی ھەروەھا پێمان بەخشین دەسەڵاتێکی گەورە');
INSERT INTO "bamoki" VALUES(4,55,'4:55','جالەو خەڵکەدا ھەبوو بڕوایان بە (موحەممەد صلى الله عليه وسلم) ھێنا ھەشیان بوو بەرگری لێ کردووە, وە دۆزەخ بەسە(بۆیان) کە ئاگرێکی ھەڵگیرساوە');
INSERT INTO "bamoki" VALUES(4,56,'4:56','بەڕاستی ئەوانەی بێ بڕوا بوون بە ئایەتەکانی ئێمە لەمەولا دەیانخەینە ناو ئاگرێک ھەر کاتێک کە پێستیان سووتا (وبرژا) بۆیان دەگۆڕێن بە پێستێکی تر بۆ ئەوەی سزاکە (بەچاکی) بچەژن بەڕاستی خوا ھەمیشە زاڵ و کار دروستە');
INSERT INTO "bamoki" VALUES(4,57,'4:57','وە ئەوانەی باوەڕیان ھێنا و کاری چاکەیان کرد لەمەولا دەیانخەینە چەند بەھەشتێ کە ڕووبارەکان دەچن بەژێریاندا دەمێننەوە تیایدا بەھەمیشەیی بۆیان ھەیە تیایدا ھاوسەرانی پاک وخاوێن وە دەیانخەینە ژێر سێبەری چڕو بەردەوامەوە');
INSERT INTO "bamoki" VALUES(4,58,'4:58','بەڕاستی خوا فەرمانتان پێ دەکات بەدانەوەی گشت سپاردەکان بۆ خاوەنەکانیان وە کاتێک دادوەریتان کرد لەنێوان خەڵکیدا (خوا فەرمان دەکات) بەدادگەری حوکم بکەن بەڕاستی خوا بە چاکی ئامۆژگاریتان دەکات بەڕاستی خوا ھەمیشە بیسەر و بینایە');
INSERT INTO "bamoki" VALUES(4,59,'4:59','ئەی کەسانێک کەباوەڕتان ھێناوە گوێڕایەڵی خوا و گوێڕایەڵی پێغەمبەر بن وە پاشانیش گوێڕایەڵی کاربەدەستە (عادیلەکانی) خۆتان بن جا ئەگەر دووبەرەکیتان ھەبوو لە شتێکدا ئەوە بیگێڕنەوە بۆ لای خواو (پێغەمبەر صلى الله عليه وسلم) ئەگەر ئێوە باوەڕتان ھەیە بەخوا و بەڕۆژی دوایی ئەوە باشترە وە ئاکام و ئەنجامی چاکترە');
INSERT INTO "bamoki" VALUES(4,60,'4:60','ئایا ئاوڕێکت نەداوەتەوە بەلای ئەوانەی وا خۆ نیشان دەدەن کە بەڕاستی ئەوان باوەڕیان ھێناوە بەوەی دابەزیوە بۆتۆ (کە قورئانە) و بەوەش کە پێش تۆ دابەزیوە (کە تەوراتە) دەیانەوێت کە دادگایی وحوکمیان ببەن بۆلای یەکێک کە حوکم بەبەرنامەی خوا ناکات لە کاتێکدا بەڕاستی فەرمانیان پێ کراوە کە بڕوایان پێی نەبێت وە شەیتان دەیەوێت کە گومڕایان بکات بەگومڕاییەکی دوور لە ڕاستی');
INSERT INTO "bamoki" VALUES(4,61,'4:61','وە ئەگەر پێیان بوترێت: وەرن بۆلای ئەوەی خوا ناردوویەتیە خوارەوە, وە بۆ لای (پێغەمبەر صلى الله عليه وسلم) دەبینی (ئەی موحەممەد صلى الله عليه وسلم) دوو ڕووەکان ڕووت لێ وەردەگێرن بەوەرگەڕانێکی تەواو');
INSERT INTO "bamoki" VALUES(4,62,'4:62','ئایا حاڵیان چۆن ئەبێت کاتێک بەسەرھاتێکی ناخۆشیان تووش بێت؟ بەھۆی ئەوەی دەستیان پێش خستووە (کردوویانە) پاشان دێنەوە بۆ لات و سوێند دەخۆن بەخوا تەنھا نیازی چاکە و یەک خستنەوەمان بووە (کاتێک کە حوکم دەبەین بۆلای غەیری خوا)');
INSERT INTO "bamoki" VALUES(4,63,'4:63','ئەوانە کەسانێکن خوا دەزانێت چی لە دڵیاندا ھەیە کەواتە ڕوویان لێ وەرگێڕە وە ئامۆژگارییان بکەوە بڵێ پێیان دەربارەی خۆیان ووتەیەکی ڕەوان و کاریگەر');
INSERT INTO "bamoki" VALUES(4,64,'4:64','وە ھیچ پێغەمبەرێکمان ڕەوانە نەکردووە تەنھا بۆ ئەوە نەبێت گوێڕایەڵی بکرێت بەفەرمانی خوا وە بێگومان ئەگەر ئەوان کاتێک ستەمیان لە نەفسی خۆیان کرد بھاتنایە بۆلات وە داوای لێ بوردونیان بکردایە لە خوا پێغەمبەریش داوای لێ خۆش بوونی بۆ بکردنایە بێگومان دەیـان بینی خوا پەشیمانی وەرگر و بەبەزەییە');
INSERT INTO "bamoki" VALUES(4,65,'4:65','نەخێر (وانیە کەخۆیان بە باوەڕدار نیشیان دەدەن) سوێند بەپەروەردگارت بڕوا دارنین تاتۆ نەکەنە دادوەر لەھەر ئاژاوە و دووبەرەکیەکی نێوانیان دا لە پاشان ھەست نەکەن لە دەرونیاندا بەھیچ بێزارییەک لەو بڕیارەی تۆ داوتە و مل کەچ نەبن بەملکەچیەکی تەواو');
INSERT INTO "bamoki" VALUES(4,66,'4:66','وە بێگومان ئەگەر ئێمە پێویستمان بکردایە لەسەریان کە یەکتری بکوژن (واتە باوەڕداران بێ باوەڕان بکوژن) یان لەماڵەکانتان دەربچن ئەوەیان نەدەکرد کەمێکیان نەبێت وە ئەگەر بەڕاستی ئەوان کاریان بکردایە بە پێی ئەو ئامۆژگارییانەی دەکران ئەوە باشتر بوو بۆیان وە دامەزراوتر دەبوون (لەسەر ئایینەکەیان)');
INSERT INTO "bamoki" VALUES(4,67,'4:67','جا ئەو کاتە بێگومان پێمان دەبەخشین لەلایەن خۆمانەوە پاداشتێکی گەورە');
INSERT INTO "bamoki" VALUES(4,68,'4:68','وە ڕێنموونیمان دەکردن بۆ ڕێگای ڕاست');
INSERT INTO "bamoki" VALUES(4,69,'4:69','وە ھەر کەسێک گوێڕایەڵی خوا و پێغەمبەر بکات جا ئەوانە لەگەڵ کەسانێکن کە خوا فەزڵ و بەھرەی (خۆی) ڕشتووە بەسەریاندا لە پێغەمبەران و ڕاستگۆیان و شەھیدان و پیاوچاکان وە ئەوانە چ ھاوڕێ و ھاوەڵێکی چاکن');
INSERT INTO "bamoki" VALUES(4,70,'4:70','ئەو فەزڵ و بەھرەیە تەنھا لەلایەن خواوەیە وە بەسە کە خوا زانایە (بەھەموو شتێک)');
INSERT INTO "bamoki" VALUES(4,71,'4:71','ئەی ئەو کەسانەی باوەڕتان ھێناوە ئاگاداریی خۆتان وەرگرن ئەوسا تاقم تاقم ھێرش بەرن (دەرچن بۆ جیھاد) یان ھەموو پێکەوە ھێرش بەرن (دەرچن بۆ جیھاد)');
INSERT INTO "bamoki" VALUES(4,72,'4:72','وە بێگومان لە ناوتاندایە کەسی وا بەڕاستی خۆی دوا دەخات جا ئەگەر تووشی بەسەرھاتێکی خراپ ھاتن دەڵێت بەڕاستی خوا فەزڵ و بەھرەی ڕشت بەسەرمدا کە لەگەڵیاندا ئامادە نەبووم');
INSERT INTO "bamoki" VALUES(4,73,'4:73','وە ئەگەر تووشی فەزڵ و بەھرەیەک بوون لە خواوە بێگومان دەڵێت وەک لەنێوان ئێوە و ئەوا ھیچ دۆستایەتی یەک نەبوو بێت ئەی خۆزگە لەگەڵ ئەواندا بوومایە تا قازانجێکی زۆر گەورەم بکردایە');
INSERT INTO "bamoki" VALUES(4,74,'4:74','کەواتە با بجەنگن لەڕێی خوادا ئەو کەسانەی کە ژیانی دونیا دەفرۆشن بەڕۆژی دوایی جا ھەرکەسێک بجەنگێت لەڕێی خوادا ئەمجا بکوژرێ (شەھید ببێت) یان زاڵ ببێت ئەوە دواتر پێی دەدەین پاداشتێکی گەورە');
INSERT INTO "bamoki" VALUES(4,75,'4:75','ئەوە چیتانە (بەچ بیانوویەک) جەنگ ناکەن لەپێناوی خوادا و لەپێناوی چەوساوەکان لەپیاوان و ئافرەتان و منداڵان ئەوانەی کە دەڵێن ئەی پەروەردگارمان ڕزگارمان کەی لەم شارە (مەبەست پێی شاری مەککەیە) کە خەڵکەکەی ستەمکارن وە لەلایەن خۆتەوە پشتیوانێکمان بۆ بەدی بھێنە وە لەلایەن خۆتەوە بەدی بھێنە بۆمان یارمەتیدەرێک');
INSERT INTO "bamoki" VALUES(4,76,'4:76','ئەو کەسانەی باوەڕیان ھێناوە لەپێناوی خوادا دەجەنگن وە ئەوانەی بێ باوەڕن لە پێناوی نارەوا (یان شەیتان) دا دەجەنگن کەواتە شەڕبکەن لەگەڵ دۆستەکانی شەیتاندا بەڕاستی پیلانی شەیتان ھەر لاواز بووە');
INSERT INTO "bamoki" VALUES(4,77,'4:77','ئایا سەیری ئەو کەسانەت نەکردووە کەپێیان ووترا دەستان بگێڕنەوە (لەجەنگ و کوشتار) و نوێژ بکەن و زەکات بدەن؟ کەچی کاتێک جەنگیان لەسەر پێویست کرا یەکسەر دەستەیەکیان لە خەڵکی دەترسان وەک ترسان لەخودا بگرە زیاتریش دەترسان دەیانووت ئەی پەروەردگاری ئێمە بۆچی جەنگت لەسەرمان پێویست کردوە خۆزگە دوات دەخستین (لەجەنگ کردن) بۆ ماوەیەکی کەم بڵێ خۆشی و لەزەتی دونیا زۆر کەمە و ڕۆژی دوایی باشترە بۆ کەسێک لەخوا بترسێت وەستەمتان لێ ناکرێت بە (ئەندازەی) تاڵی ناوکە خورمایەک');
INSERT INTO "bamoki" VALUES(4,78,'4:78','لەھەرشوێنێک بن مردن پێتان دەگات بالەناو قەڵا و کۆشکی بەرزو قایمشدابن وە ئەگەر (ناپاکان) خۆشی و چاکە ھاتە رێیان دەڵێن ئەمە لەلایەن خواوەیە وە ئەگەر تووشی نا خۆشی و خراپەیەک ھاتن دەڵێن ئەمە لەلایەن تۆوەیە (ئەی موحەممەد صلى الله عليه وسلم) بڵێ: ھەمووی لەلایەن خواوەیە ئەوە ئەو خەڵکە چیان بەسەر ھاتووە؟ وەکو لە ھیچ قسەیەک تێنەگەن وایه');
INSERT INTO "bamoki" VALUES(4,79,'4:79','ھەر چاکەیەکت تووش بوو ئەوە لەخواوەیە وە ھەر خراپەیەکت تووش بوو ئەوە لە خۆتەوەیە و ئێمە تۆمان ناردووە بەپێغەمبەر بۆخەڵکی وە خوا بەسە شایەتی ئەوە بدات');
INSERT INTO "bamoki" VALUES(4,80,'4:80','ھەر کەسێک گوێرایەڵی (پێغەمبەر صلى الله عليه وسلم) بکات ئەوە بەڕاستی گوێرایەڵی خوای کردووە ھەرکەسێکیش پشت ھەڵبکات ئەوە ئێمە تۆمان ڕەوانە نەکردووە بە چاودێر و پارێزەر بەسەریانەوە');
INSERT INTO "bamoki" VALUES(4,81,'4:81','(دووڕوەکان) دەڵێن گوێرایەڵین (مل کەچ و فەرمانبەردارین) بەڵام کاتێک لەلای تۆ دەرچوون کۆمەڵێک لەوان شەو دەکەنەوە بەپیلان گێران (لەموسڵمانان) بەپێچەوانەی قسەکانی تۆ وەخوا دەینووسێت ئەو (پیلانانەی) شەو کردویانە کەواتە وازیان لێ بھێنە و پشت بەخوا ببەستە خوا بەسە پشتیوانت بێت');
INSERT INTO "bamoki" VALUES(4,82,'4:82','ئەوەبۆ بییر ناکەنەوە لەقورئان؟ خۆ ئەگەر لەلایەن کەسێکی ترەوە بوایە جگەلە خوا بێگومان دەیانبینی تیایدا جیاوازیی زۆر');
INSERT INTO "bamoki" VALUES(4,83,'4:83','وە کاتێک ھەواڵێکیان پێ بگات دەربارەی ئاسایش و ھێمنایەتی (نێوانتان) یان لەبارەی ترسەوە بڵاوی دەکەنەوە بەڵام ئەگەر بیانگەڕانایەتەوە بۆ لای (پێغەمبەر صلى الله عليه وسلم) و بۆ لای کاربەدەستانی خۆیان ئەوە ئەوانەی ڕاستی ھەواڵەکان دەردەھێنن دەزانن ھەواڵەکە (ڕاستە یان درۆیە) وە ئەگەر چاکەو بەزەیی خوا نەبوایە بەرامبەر بەئێوە بێگومان شوێن شەیتان دەکەوتن کەمێکتان نەبێت');
INSERT INTO "bamoki" VALUES(4,84,'4:84','(ئەی موحەممەد صلى الله عليه وسلم) لەپێناوی خوادا بجەنگێ تۆ تەنھا ئەرکی خۆت لێ داوا کراوە (یا تۆ تەنھا ئەرکی خۆتت لەسەرە) ھانی بڕوادارانیش بدە (بۆ جیھاد کردن) ئومێدە خوا ڕێگری لە ھێزی بێ باوەڕان بکات وە خوا بەھێزترە و تۆڵەی بەتینترە');
INSERT INTO "bamoki" VALUES(4,85,'4:85','ھەرکەسێک تکایەکی چاکە بکات بەشێکی لە (پاداشتی) ئەو چاکەیە بۆ ھەیە وە ھەرکەسێک تکایەکی خراپە بکات ئەویش بەشێکی دەبێت لە (سزای) ئەو خراپەیە وەخوا ھەمیشە بەسـەر ھەموو شتێکدا بەتوانـایە (چاودێرە)');
INSERT INTO "bamoki" VALUES(4,86,'4:86','ھەر کاتێک سڵاوێکتان لێکرا ئەوە بەچاکتر لەو سڵاوە سڵاو بکەنەوە (لێیان) یان وەکو خۆی وەڵامی بدەنەوە بێگومان خوا ھەمیشە پرساو چاودێرە بەسەر ھەموو شتێکدا');
INSERT INTO "bamoki" VALUES(4,87,'4:87','ھیچ پەرستراوێکی ڕاست نیە جگە لەخوا سوێند بەخوا بێگومان کۆتان دەکاتەوە بۆ (لێپرسینەوەی) ئەو ڕۆژی دواییەی کە ھیچ گومانی تێدا نیە جا کێ لە خوا قسە (و بەڵێنی) ڕاستترە');
INSERT INTO "bamoki" VALUES(4,88,'4:88','ئەوە چیتانە دەربارەی دوو ڕووەکان بوونەتە دوو دەستە لەکاتێکدا خوا ئەوانەی گەڕاندۆتەوە بۆ بێ باوەڕی بەھۆی کردەوەکانیانەوە ئایا دەتانەوێت ڕێنمونی کەسێک بکەن کە خوا گومڕای کردووە؟ وە ھەرکەسێک خوا گومڕای بکات ئەوە ھەرگیز ناتوانی ڕێگای ڕاستی بۆ بدۆزیتەوە');
INSERT INTO "bamoki" VALUES(4,89,'4:89','ئەوان ئاواتە خوازن ئێوەش بێ باوەڕبن وەک چۆن خۆیان بێ باوەڕ بوون تا ئێوەو ئەوان یەکسان بن کەواتە ئەوان بە دۆست و یارمەتی دەر ھەڵمەبژێرن ھەتا (پەشیمان دەبنەوەو) کۆچ دەکەن لەڕێی خوادا ئەمجا ئەگەر ڕوویان وەرگێڕاو پشتیان ھەڵکرد ئەوە (بەدیل) بیانگرن و بیانکوژن لەھەر شوێنێک تووشیان بوون وە ھیچ دۆست و یارمەتیدەرێک لەوان ھەڵمەبژێرن');
INSERT INTO "bamoki" VALUES(4,90,'4:90','جگە لەوانەی پەیوەندی دەکەن بە کەسانێکەوە کە لەنێوان ئێوەو ئەواندا پەیمان ھەیە یان ھاتوون بۆ لاتان لەسەر دڵیان ناڕەحەتە (حەزناکەن) لەوەی کە جەنگتان لەدژ بکەن یاجەنگ لەدژی خزمەکانیان بکەن وە ئەگەر خوا بیویستایە زاڵی دەکردن بەسەرتاندا وە جەنگیان دژتان دەکرد جا ئەگەر ئەوان کەنارگیر بوون لە ئێوە، وە جەنگیان لە دژتان نەدەکرد و پێشنیاری ئاشتیان کرد بۆتان (ملکەچ بوون) ئەوە خوا ڕێگەی نەداوون کە دەستیان بۆ بەرن و (نابێ شەڕیان لەدژ بکەن)');
INSERT INTO "bamoki" VALUES(4,91,'4:91','لە مەولا کەسانێکی تر دەبینن دەیانەوێت لەئێوە ئەمین بن وە لە ھۆزەکەی خۆشیان ئەمین بن ھەر کاتێک بانگ کرابن بۆ بـێ بــاوەڕی و بت پەرستی گەڕاونەتەوە بۆی (واتە بەئاشتی ڕازی نەدەبوون) جا ئەگەر کەنارگیر نەبوون لێتان وە جڵەوی ئاشتیان نەدایە دەستتان وە دەستیان نەکێشایەوە (لەجەنگ کردن) ئەوە بەدیل بیانگرن و بیانکوژن لەھەر شوێنێک کە بەردەستتان کەوتن ئەوانەی (باسمان کرد) داومانەبە ئێوە لەدژیان دەسەڵاتی ئاشکرا');
INSERT INTO "bamoki" VALUES(4,92,'4:92','بۆ ھیچ باوەڕدارێک ڕەوا نیە باوەڕدارێک بکوژێت مەگەر بەھەڵە نەبێت وە ھەر کەسێک ئیماندارێک بە ھەڵە بکوژێت ئەوە (لەسەریەتی) ئازادکردنی کۆیلەیەکی باوەڕدار و (نرخی) خوێنێکیش بدات بە کەس و کاری کوژراوەکە مەگەر بیبەخشن (لە خوێنەکە بەخێری خۆیان) بەڵام ئەگەر کوژراوەکە لەگەلێک بوو کە دوژمنتان بوون (کافری شەڕکار بوون) وە بەڵام کوژراوەکە ئیماندار بوو ئەوە (ھەر لەسەریەتی) ئازادکردنی بەندەیەکی ئیماندار وە ئەگەر (کوژراوە باوەڕدارەکە) لەگەلێک بوو کە لەنێوان ئێوە وئەواندا پەیمان ھەبوو ئەوا (نرخی) خوێنێک بدەنەکەسی کوژراوەکە لەگەڵ ئازادکردنی کۆیلەیەکی ئیماندار جا ئەو (بکوژەی) دەستی نەکەوت (یان نەی بوو) ئەوە (لەسەریەتی) ڕۆژووگرتنی دوو مانگ لەسەریەک (ئەم بڕیاڕە) لێبووردنە لەلایەن خواوە، وە خوا ھەمیشە زانا و کاردروستە');
INSERT INTO "bamoki" VALUES(4,93,'4:93','وەھەرکەسێک ئیماندارێک بەدەس ئەنقەس بکوژێت ئەوە تۆڵەی ئەو کەسە دۆزەخە بە ھەمیشەیی تیایدا ئەبێت وخوا خەشمی لێگرتووە و نەفرەتی لێکردووە و سزایەکی گەورەی بۆئامادە کردووە');
INSERT INTO "bamoki" VALUES(4,94,'4:94','ئەی کەسانێک کەبڕواتان ھێناوە کاتێک دەرچوون (بۆ جیھاد کردن) لەڕێگەی خوادا ھەموو شتێک ڕوون بکەنەوە, وە نەڵین بەو کەسانەی کە سڵاوی (موسڵمانانە) تان لێدەکەن تۆ بڕوادار نیت؟ بەدوای شتوومەک و سامانی دونیاییدا بگەڕێن ئەوە لای خوا دەسکەوتی زۆر ھەیە ئێوەش لەمەو پێش ھەروەھا بوون (ئیمانەکەتان دەشاردەوە) ئەمجا خوا منەتی نا بەسەرتاندا (بە ھێز و زاڵی کردن) دەی کەواتە ھەموو شتێک ڕوون بکەنەوە بەڕاستی خوا ھەمیشە ئاگادارە بەوەی ئێوە دەیکەن');
INSERT INTO "bamoki" VALUES(4,95,'4:95','یەکسان نین ئەو باوەڕدارانەی دانیشتوون و جیھاد ناکەن جگە لە خاوەن عوزرەکان نەبێت (وەک نەخۆش، و کوێر، وشەل) لەگەڵ تێکۆشەرانی ڕێگەی خوادا بەماڵ و گیانیان خوا فەزڵی تێکۆشەران بە ماڵ و گیانیانی داوە بە سەر دانیشتوان (لە جیھاد) دا بە پلەیەک وە ھەر یەک لەو دوو تاقمە خوا بەڵێنی پاداشتی زۆر چاکی پێی داون (کە بەھەشتە). وە خوا ڕێزی تێکۆشەرانی داوە بەسەر دانیشتووەکاندا بە پاداشتێکی گەورە');
INSERT INTO "bamoki" VALUES(4,96,'4:96','لەلای (خوا) وە پلەی زۆر و لێخۆش بوون و میھرەبانیان بۆ ھەیە وە خوا ھەمیشە لێبوردەی میھرەبانە');
INSERT INTO "bamoki" VALUES(4,97,'4:97','بە ڕاستی ئەوانەی کە فریشتەکان گیانیان دەکێشن لە کاتێکدا کەستەمیان لەخۆیان دەکرد (بە کۆچ نەکردنیان) (بە تەشەرەوە پێیان) دەڵێن (دەربارەی ئایینەکەتان) لەچیدا بوون؟! دەڵێن ئێمە چەوساوە بووین لەسەر زەویدا (پێیان) دەڵێن مەگەر زەوی خوا فراوان نەبوو؟ تا کۆچتان بکردایە تیایدا جا ئەوانە شوێنیان دۆزەخە ئای چ پاشە ڕۆژێکی خراپە');
INSERT INTO "bamoki" VALUES(4,98,'4:98','بێجگە لەو چەوساوانەی کە لە پیاوان و ئافرەتان و لە مناڵانن کە توانای ھیچ چارەیەکیان نیە (لەبەر بێ ھێزی و پیرییان) وە بەھیچ شێوازێک ڕێ دەرناکەن');
INSERT INTO "bamoki" VALUES(4,99,'4:99','ئا ئەوانە ئومێد ھەیە خوا لێیان خۆش بێت وە خوا ھەمیشە چاوپۆشیکار و لێخۆشبووە');
INSERT INTO "bamoki" VALUES(4,100,'4:100','وە ھەر کەسێک کۆچ بکات لە ڕێی خوادا دەستی دەکەوێت لە زەویدا جێگە و ڕێگەی زۆر (کە دوژمن پێی ناخۆشە) وە فراوانی (لە ڕۆزی ژیاندا) وە ھەرکەسێک لە ماڵی خۆی دەرچێت وە کۆچ بکات بۆ لای خوا و پێغەمبەرەکەی پاشان مردن پێی بگات ئەوە بێگومان پاداشتی ئەو کەسە کەوتۆتە سەر خوا وە خوا ھەمیشە لێبوردەی بە بەزەییە');
INSERT INTO "bamoki" VALUES(4,101,'4:101','ھەر کاتێک سەفەرتان کرد و گەڕان بەسەر زەویدا ئەوە ھیچ گوناھێکتان لەسەر نیە (دروستە بۆتان) کە نوێژەکان کورت بکەنەوە ئەگەر ترسان ئەوانەی بێ باوەڕن جەنگتان لەگەڵ بکەن بە ڕاستی بێ باوەڕان ھەمیشە بۆ ئێوە دوژمنێکی ڕوون و ئاشکران');
INSERT INTO "bamoki" VALUES(4,102,'4:102','کاتێک تۆ (ئەی موحەممەد صلى الله عليه وسلم) لەجەنگدا) لەناو بڕواداراندا بوویت و پێش نوێژیت بۆ کردن ئەوە با تاقمێک لەوان لەگەڵ تۆ نوێژ بکەن و با چەکەکانیان پێ بێت جا کاتێک سوژدەیان برد (رکاتێکیان تەواو کرد) با (بچنە دواوە) لە پشتانەوە بن (پاسەوانتان بن) وە باتاقمەکەی تر بێن کە نوێژیان نەکردوە (وپاسەوان بوون) و لەگەڵ تۆ نوێژ بکەن وە با ووریای خۆیان بن و چەکەکانیشیان پێ بێت بێ باوەڕان ئاواتە خوازن کە ئێوە بێ ئاگابن لە چەکەکانتان و شت و مەکەکانتان تا پەلامارتان بدەن بە پەلامارێکی یەکجارەکی (بدەن بە سەرتاندا) وە تاوانتان لەسەر نیە ئەگەر بەھۆی بارانەوە ناڕەحەت بوون یان نەخۆش بوون کە چەکەکانتان دانێن بەڵام ووریای خـۆتان بن بەڕاستی خوا ئامادەی کردوە بۆ بێ باوەڕان سزایەکی ڕیسواکەر');
INSERT INTO "bamoki" VALUES(4,103,'4:103','ئەمجا کاتێک نوێژەکانتان بەجێ ھێنا ئەوسا یادی خوا بکەن بەپێوە و بەدانیشتنەوە وە لە سەرتەنیشتان (بە پاڵکەوتنەوە) جا ھەر کاتێک دڵ ئارام بوون (و ترستان نەما) ئەو کاتە نوێژەکانتان بە ڕێک و پێکی بکەن بەڕاستی نوێژ ھەمیشە لەسەر باوەڕداران فەرز کراوێکی کات بۆ دیاری کراوە');
INSERT INTO "bamoki" VALUES(4,104,'4:104','سست مەبن لە بەدوا گەڕانی دوژمنتاندا (یان لە بەدوا گەڕانی ئەو قەومە کافرانەدا) ئەگەر ئێوە ئێش و ئازارتان پێ بگات بێگومان ئەوانیش ئێش و ئازاریان پێدەگات وەک چۆن ئێوە ئێش و ئازارتان پێدەگات وە ئێوە ئومێد دەکەن لەخوا ئەوەی ئەوان ئومێدی ناکەن (کە پاداشت و سەرکەوتنە) وە بەڕاستی خوا ھەمیشە زانا و کار دروسته');
INSERT INTO "bamoki" VALUES(4,105,'4:105','بێگومان ئێمە دامانبەزاندۆتە سەر تۆ ئەم قورئانە بەڕاستی بۆ ئەوەی بڕیار بدەی لەنێوان خەڵکیدا بەو بڕیارەی کەخوا نیشانی داویت وە بۆ ناپاکان مەبە بەپارێزەر و پشتیوان ولایەنگر');
INSERT INTO "bamoki" VALUES(4,106,'4:106','داوای لێخۆشبوون لەخوا بکە بەڕاستی خوا لێخۆش بووی بە بەزەیی یە');
INSERT INTO "bamoki" VALUES(4,107,'4:107','بەرگری مەکە لەوانەی ناپاکی دەکەن بەرامبەر خۆیان بێگومان خوا خۆشی ناوێت ئەوانەی کە بەردەوام زۆر ناپاک و گوناھبارن');
INSERT INTO "bamoki" VALUES(4,108,'4:108','(تاوانەکانی) خۆیان دەشارنەوە لەخەڵکی (لەترساندا) و نایشارنەوە لە خوا (و شەرمی لێ ناکەن) لەکاتێکدا خوا لەگەڵیاندایە کاتێک (کە بەنھێنی) لەشەودا تەگبیر دەکەن و قسەیەک دەکەن کە خوا پێی ڕازی نیە وە خوا ھەمیشە زاناو ئاگادارە بەھەر کردەوەیەک کە ئەوان دەیکەن');
INSERT INTO "bamoki" VALUES(4,109,'4:109','(ئیمانداران) بێداربن وا ئێوە بەرگریتان لەناپاکان کرد لەژیانی دونیادا دەی ئایا کێ بەرگرییان لێ دەکات (لە سزای خوا) لە ڕۆژی دواییدا یان کێ کارو باریان دەگرێتە دەست');
INSERT INTO "bamoki" VALUES(4,110,'4:110','وە ھەرکەس کردەوەیەکی خراپ بکات یان ستەم لەخۆی بکات پاشان داوای لێ خۆش بوون لەخوا بکات دەبینێت خوا لێبوردە و میھرەبانە');
INSERT INTO "bamoki" VALUES(4,111,'4:111','وە ھەرکەسێک گوناھێکی (گەورە) بکات بێگومان (زیانی) ئەو گوناھە ھەر بۆ خۆیەتی وەخوا ھەمیشە زانای کاردروستە');
INSERT INTO "bamoki" VALUES(4,112,'4:112','وە ھەرکەسێک گوناھێک بکات (بەھەڵە) یان گوناھێک بکات (بەقەست) پاشان (ئەو تاوانە) بخاتە سەر بێ تاوانێک ئەوە بێگومان خستویەتیە ئەستۆی خۆی (سزای) بوھتانێک وە تاوانێکی ئاشکرا');
INSERT INTO "bamoki" VALUES(4,113,'4:113','وە ئەگەر بەخشش و بەزەیی خوا بەسەر تەوە نەبوایە (ئەی موحەممەد صلى الله عليه وسلم) بێگـومان دەستەیەک لەو ناپاکانە بە نیازبوون کە گومڕات بکەن بەڵام ئەوانە بەس خۆیان گومڕا دەکەن وە ئەوانە ھیچ زیانێک ناگەیەنن بە تۆ وەخوا ناردوویەتیە خوارەوە بۆ سەرت قورئان و دانایی وە ئەوەی کەنەتئەزانی فێری کردی وە ھەمیشە بەخششی خوا بەسەر تۆوە زۆر گەورەیە');
INSERT INTO "bamoki" VALUES(4,114,'4:114','ھیچ چاکەیەک نیە لەزۆربەی چپەو سرتە کردنەکانیاندا مەگەر یەکێک فەرمان بکات بەماڵ بەخشین لەرێی خوادا یان فەرمانکردن بە چاکە یان چاککردنی نێوان خەڵکی جا ھەر کەسێک ئەوە بکات بەنیازی ڕەزامەندی خوا ئەوە لە داھاتوودا پێی دەبەخشین پاداشتی زۆر گەورە');
INSERT INTO "bamoki" VALUES(4,115,'4:115','وەھەرکەسێک دژایەتی (پێغەمبەر صلى الله عليه وسلم) بکات پاش ئەوەی کە ڕێبازی ڕاستی بۆ ڕوون بوویەوە وشوێنی ڕێگایەکی تر بکەوێت بێجگە لەڕێگای بڕواداران ڕووی وەردەگێڕین بۆئەو ڕێبازەی کەھەڵی بژاردووە و دەیخەینە دۆزەخ ئای چ پاشەڕۆژێکی خراپە');
INSERT INTO "bamoki" VALUES(4,116,'4:116','بەڕاستی خوا خۆش نابێت لەوەی کەھاوەڵی بۆ دابنرێت بەڵام جگە لەوە ھەرچی بێت لێی خۆش دەبێت لە ھەرکەسێک کە (خوا) بیەوێت وە ھەرکەسێک ھاوەڵ بۆ خوا دابنێت ئەوە بەڕاستی گومڕا بووە بە گومڕابونێکی دوور لەڕاستی');
INSERT INTO "bamoki" VALUES(4,117,'4:117','جگە لە خوا نزاو ھاوار ناکەن (نایپەرستن جگە لەخوا) تەنھا لەچەند (بتێکی ناولێنراوی) مێینە نەبێت (وەک لات ومەنات وعوزا) وە نایپەرستن جگە لە شەیتانی یاخی و سەرکەش نەبێت');
INSERT INTO "bamoki" VALUES(4,118,'4:118','کە خوا نەفرەتی لێکردووە. وە (شەیتان) ووتوویەتی سوێند بێت ھەڵدەبژێرم (بەبەندەی خۆم) لەبەندەکانتدا بەشێکی دیاریکراو');
INSERT INTO "bamoki" VALUES(4,119,'4:119','سوێند بێت گومڕایان دەکەم و بەئاوات و خەیاڵی درۆینە سەرگەرمیان دەکەم وە فەرمانیان پێ دەدەم جا بێگومان ئەوانیش گوێچکەی ئاژەڵەکان دەبڕن (لەسەردەمی نەفامیدا ھەر ووشترێک پێنج سکی بکردایە گوێیان دەبڕی ولەسەر خۆیان حەرام دەکرد) وە فەرمانیان پێ دەدەم ئەوسا ئەوانیش دروستکراوی خوا دەگۆڕن وە ھەرکەسێک شەیتان بکات بەدۆستی خۆی بێجگە لەخوا ئەوە بێگومان زیانی لێ کەوتووە بەزیانێکی دیارو ئاشکرا');
INSERT INTO "bamoki" VALUES(4,120,'4:120','(شەیتان) بەڵێنیان پێ دەدات و ئاوات و ئومێدیان دەخاتەبەر دڵنیابن شەیتان ھیچ بەڵێنێکیان پێ نادات جگە لەخەڵەتاندن نەبێت');
INSERT INTO "bamoki" VALUES(4,121,'4:121','ئەوانە شوێن و جێگەیان دۆزەخە و ھیچ شوێنی ڕاکردن و لادانیان دەست ناکەوێت لێ ی');
INSERT INTO "bamoki" VALUES(4,122,'4:122','وە ئەو کەسانەی باوەڕیان ھێناوە و کردەوە چاکەکانیان کردووە لە داھاتوویەکی نزیکدا دەیانخەینە بەھەشتانێک کە ڕووبارەکان بە ژێر درەختەکانیان دا دەڕۆن دەمێننەوە تیایدا بە ھەمیشەیی بەڵێنی خوایە و ڕاستە (بەدی دەھێنرێت) وە ئایا کێ لە خوا ڕاست بێژترە');
INSERT INTO "bamoki" VALUES(4,123,'4:123','(بەرزی و پاداشت) نەبە ئارەزووی ئێوەیە و نەبەئارەزووی خاوەن کتێبەکانە ھەر کەسێک خراپەیەک بکات پاداشتی دەدرێتەوە, وە دەستی ناکەوێت بێجگە لەخوا ھیچ دۆست و یارمەتیدەرێک');
INSERT INTO "bamoki" VALUES(4,124,'4:124','کەسێکیش کردەوە چاکەکان جێ بەجێ بکات لە نێر یان لە مێ لە ھەمان کاتدا ئەو کەسە بڕوادار بێت ئەوە ئەوانە دەچنە بەھەشتەوە و بچووکترین ستەمیان لێ ناکرێت (ھێندەی توێکڵی ناوکە خورمایەک)');
INSERT INTO "bamoki" VALUES(4,125,'4:125','ئایا کێ ئایینی چاکترو جوانترە (وە کێ دیندارترە) لە کەسێک کە ڕووی خۆی یەکلا کردبێتەوە بۆ خوا لە ھەمان کاتدا ئەو کەسە چاکەکار بێت و شوێنی ئاینی ئیبراھیم کەوتبێت بەڕاستی و پاکی (کە بە وییستی خۆی لە بەتاڵ لای داوە بۆ لای ڕاست) وە خوا ئیبراھیمی ھەڵبژارد بەدۆست و خۆشەویستی خۆی');
INSERT INTO "bamoki" VALUES(4,126,'4:126','تەنھا بۆ خوایە ھەرچی لە ئاسمانەکان و زەوی دا ھەیە وە خوا بەردەوام ئاگاداری ھەموو شتێکە');
INSERT INTO "bamoki" VALUES(4,127,'4:127','وە (ئەی موحەممەد صلى الله عليه وسلم) پرسیارت لێدەکەن دەربارەی ئافرەتان بڵێ خوا وەڵامتان دەداتەوە دەربارەیان وە ئەوەی بەسەرتاندا دەخوێنرێتەوە لە قورئاندا (وەڵامێکە) لەبارەی کچانی ھەتیوەوە ئەوانەی پێیان نادەن ئەوەی نووسراوە بۆیان (واتە میراتی باوکیان) وە حەز ناکەن مارەیان بکەن (لەبەر میراتەکەشیان ناھێڵن شوو بکەن) وە لە بارەی ئەو مناڵانەی کە بێ دەسەڵاتن وە (خوا فەرمان دەدات) بە دادگەری لەگەڵ ھەتیوان دا بجوڵێنەوە, وە ھەر کردەوەیەکی باش بکەن ئەوە بێگومان خوا بەئاگایە لێی');
INSERT INTO "bamoki" VALUES(4,128,'4:128','وە ئەگەر ئافرەتێک ترسا لە گوێ پێ نەدان یان پشت ھەڵکردنی مێردەکەی ئەوە ھەردووکیان ھیچ گوناھێکیان لەسەر نیە کە ئاشتەواییەکی چاک بکەن لە نێوان خۆیاندا ھەر ئاشتەوایی و یەک کەوتن چاکترە ھەر چەندە چروکی و ڕەزیلی تێکەڵاوی دەرونەکان کراوە, وە ئەگەر چاکە کارو پارێزکاربن ئەوە دڵنیابن بەڕاستی خوا ھەمیشە بە ئاگایە لەوەی کە دەیکەن');
INSERT INTO "bamoki" VALUES(4,129,'4:129','ئێوە ھەرگیز ناتوانن داد پەروەربن لە نێوان ژنەکانتان دا ھەر چەندە سوور بن (ھەوڵ بدەن) جا کەواتە بەتەواوی خوار مەبنەوە (لە ھاوسەری یەکێکیان) بەو ھۆیەوە واز بھێنن لەوی تریان وەک ھەڵبەستراو (نە ئەوەیە مێردی ھەبێت نەبەرەڵاش کرا بێت وتەڵاق درا بێت) وەئەگەر بسازێن و پارێزکار بن ئەوە بەڕاستی خوا ھەمیشە لێبوردە و میھرەبانە');
INSERT INTO "bamoki" VALUES(4,130,'4:130','وە ئەگەر لێک جیابوونەوە پەروەردگار ھەریەکەیانی (لەوی تر) بێ نیاز و پێویستی گرت بەڕەحمی خۆی وە خوا ھەمیشە بەھرە فراوان و کاردروستە');
INSERT INTO "bamoki" VALUES(4,131,'4:131','وە بۆ خوایە ھەرچی لە ئاسمانەکان و زەویدایە سوێند بەخوا بەڕاستی ئێمە ئامۆژگاریمان کردوون ئەوانەی کەنامەیان پێدراوە لەپێش ئێوەدا ھەروەھا ئێوەش کە لەخوا بترسن وە ئەگەر بێ باوەڕ بن ئەوە بەڕاستی ھەر ھی خوایە ھەرچی لەئاسمانەکان و زەویدایە وە خوا ھەمیشە دەوڵەمەند و سوپاس کراوە');
INSERT INTO "bamoki" VALUES(4,132,'4:132','وە تەنھا بۆ خوایە موڵکی ئەوە ھەرچی لەئاسمانەکان و زەویدایە تەنھا خوا بەسە پێ سپێراو و چاودێر بێت');
INSERT INTO "bamoki" VALUES(4,133,'4:133','ئەگەر خوا بیەوێت ناتانھێڵێت و لەناوتان دەبات ئەی خەڵکینە وە دەستەیەکی تر دەھێنێت وەخوا ھەردەم بەسەر ئەوەدا بەتوانایه');
INSERT INTO "bamoki" VALUES(4,134,'4:134','ھەر کەسێک پاداشتی دونیای دەوێت ئەوە لای خوایە پاداشتی دونیا و دواڕۆژ وە ھەمیشە خوا بیسەر و بینایە');
INSERT INTO "bamoki" VALUES(4,135,'4:135','ئەی کەسانێک کەباوەڕتان ھێناوە بەردەوام زۆر دادگەربن لەبەر خوا شایەتی بدەن باشایەتی یەکە دژ بەخۆشتان بێت یان دژی باوک و دایک و دژی خزمە نزیکەکانتان (بێت) ئەگەر (شایەتی لەسەر دراوەکە) دەوڵەمەند بێت یان ھەژار (شایەتیەکە ھەر بدەن) چونکە خوا بەوان لە پێشترە (و قازانجیان چاکتر دەزانێت) کەواتە شوێن ئارەزوو مەکەون بۆ ئەوەی لەھەق لانەدەن وە ئەگەر(شایەتیەکە) پێچەوانە بدەن یان نەیدەن و گوێی نەدەنێ ئەوە بەڕاستی خوا ھەمیشە بەکارەکانتان ئاگادارە');
INSERT INTO "bamoki" VALUES(4,136,'4:136','ئەی کەسانێک کە باوەڕتان ھێناوە بەردەوام بن لەسەر باوەڕ بەخوا و پێغەمبەرەکەی و بەو قورئانەی ناردوویەتیە خوارەوە، بۆ پێغەمبەرەکەی وە بەو کتێبانەش کەپێشتر ناردوویەتیە خوارەوە, وە ھەر کەسێک بێ باوەڕ بێت بەخوا و بە فریشتەکانی و بە کتێبەکانی و پێغەمبەرەکانی وە بە ڕۆژی دوایی ئەوە بەڕاستی گومڕا بووە بەگومڕاییەکی زۆر دوور (لەڕاستی)');
INSERT INTO "bamoki" VALUES(4,137,'4:137','بێگومان ئەوانەی بڕوایان ھێناوە پاشان بێ باوەڕ بوونەتەوە دواتر باوەڕیان ھێناوە ئەوسا بێ باوەڕبوونەتەوە پاشان لە بێ بڕواییدا زیادتر ڕۆچوون خوا ھەرگیز لێیان خۆش نابێت وە ڕێنماییان ناکات بۆ ڕێگای ڕاست');
INSERT INTO "bamoki" VALUES(4,138,'4:138','موژدەبدە بەدوو ڕوەکان بەوەی کەبەڕاستی سزایەکی بەئازاریان بۆھەیە');
INSERT INTO "bamoki" VALUES(4,139,'4:139','ئەوانەی کە بێ باوەڕان بەدۆست و خۆشەویستی خۆیان دادەنێن بێجگە لە ئیمانداران ئایا بەتەمان سەربەرزیان لای ئەوان دەس کەوێت؟ (بۆچونێکی ھەڵەیە) چونکە بەڕاستی سەربەرزی و باڵا دەستی ھەمووی بۆ خوایە');
INSERT INTO "bamoki" VALUES(4,140,'4:140','وە بێـگومان خوا ناردوویەتیە خوارەوە بۆتان لە قورئاندا ھەر کاتێک بیستان کە ئایەتەکانی (بەڵگەکانی) خوا بڕوایان پێ ناکرێت و گاڵتەیان پێ دەکرێت ئەوە دامەنیشن لەگەڵیاندا (لەگەڵ ئەوانەی گاڵتە بە ئایەتەکانی خوا دەکەن) تا دەچنە ناو باسێکی ترەوە (و باسەکە دەگۆڕن) بێگومان ئەگەر دانیشتن لەگەڵیاندا (و ھاوبەشیان بکەن) ئەوە ئێوەش وەک ئەوانن بە ڕاستی خوا کۆکەرەوەی دووڕوەکان و بێ باوەڕەکانیشە بە تێکڕا لە دۆزەخدا');
INSERT INTO "bamoki" VALUES(4,141,'4:141','(دووڕووەکان) ئەوانەی چاوەڕوانی (سەر ئەنجامی) ئێوە دەکەن جا ئەگەر بۆ ئێوە بوو سەرکەوتن لەلای خواوە دەڵێن مەگەر ئێمە لەگەڵ ئێوە نەبووین؟ بەڵام ئەگەر بێ بڕوایان بەشێکیان (لەسەرکەوتن) ھەبوو ئەڵێن ئایا ئێمە زاڵمان نەکردن بەسەر ئەواندا؟ و ئێوەمان نە پاراست لەئیمانداران ؟ (کەواتە بەشمان بدەن) جا خوا لەڕۆژی دواییدا بڕیاری یەکجاری دەدات لە نێوانتاندا وە ھەرگیز خوا (بەڵگەی) کافران بەسەر ئیماندارانی (ڕاستەقینە) دا سەرناخات (لەم جیھانەدا) ودروست نیە باوەڕداران ملکەچ وسەر شۆڕی بێباوەڕان بن وحوکمی ئەوان جێبەجێ بکەن');
INSERT INTO "bamoki" VALUES(4,142,'4:142','بە ڕاستی دووڕووەکان دەیانەوێت خوا بخەڵەتێنن کەچی (لەبەرامبەردا) خوا خەڵەتێنەری ئەوانە وفڕ وفێڵەکانیان پووچەڵ دەکاتەوە) ھەر کاتێکیش ھەستن بۆ نوێژ بە تەمەڵی و سستی ھەڵدەستن ڕوپامایی بۆ خەڵکی ئەکەن وە یادی خوا ناکەن بەکەمی نەبێت');
INSERT INTO "bamoki" VALUES(4,143,'4:143','دوودڵ ودڵەڕاوکێیانە لـە نێوان (کوفر وباوەڕ) دا نەلای بڕوادارانن وەنە لای بێ باوەڕانن وەھەر کەسێک خوا گومڕای بکات ئەوە ھەرگیز ڕێگەیەکی ڕاستی بۆ نادۆزێتەوە (بیگەیەنێت بەمەبەست)');
INSERT INTO "bamoki" VALUES(4,144,'4:144','ئەی ئەوانەی کە بڕواتان ھێناوە بێ بڕواکان مەکەنە دۆستی خۆتان لەبری ئیمانداران ئایا (بەم کارە) دەتانەوێت بیدەن بە خوا بەڵگەیەکی ڕوون لەدژی خۆتان؟');
INSERT INTO "bamoki" VALUES(4,145,'4:145','(بابزانن) بەڕاستی دوو ڕووەکان لە خانە و تەبەقی ھەرە ژێرەوەی ئاگردان وە ھەرگیز ڕزگار کەرێکت بۆ ئەوان دەست ناکەوێت');
INSERT INTO "bamoki" VALUES(4,146,'4:146','جگە لەوانەی کە تەوبە دەکەن و خۆیان چـاک دەکەن ودەسـت دەگـرن بە (ئاینی) خـواوە, وە ئاینەکەیان پاک وخاوێن دەکەن بۆ خوا جائەوانەی (ئەو مەرجانەیان بەجێ ھێنا) لەگەڵ ئیمانداراندا دەبن بێگومان لەداھاتوودا خوا دەیبەخشێت بەئیمانداران پاداشتێکی زۆر گەورە');
INSERT INTO "bamoki" VALUES(4,147,'4:147','ئایا خوا چ ئیشێکی ھەیە بە سزادانتان ئەگەر سوپاسی بکەن وە باوەڕی پێ بھێنن وە خوا ھەمیشە سوپاسگوزاری بەندە چاکەکانیەتی و زانایە پێیان');
INSERT INTO "bamoki" VALUES(4,148,'4:148','خوا پێی خۆش نیە ئاشکرا کردنی قسەی نابەجێ و خراپ مەگەر کەسێک ستەمی لێکرا بێت (بۆی دروستە خراپەی ستەمکار ئاشکرا کات) وە خوا ھەمیشە بیسەر و زانایە');
INSERT INTO "bamoki" VALUES(4,149,'4:149','ئەگەر خێر و چاکە دەرخەن (بەئاشکرا بیکەن ) یان بیشارنەوە یان ببورن لەخراپە یەک ئەوە بێگومان خوا ھەمیشە لێبوردەی بەتوانایە');
INSERT INTO "bamoki" VALUES(4,150,'4:150','بەڕاستی ئەوانەی بێ باوەڕن بەخواو پێغەمبەرەکانی وەدەیانەوێت جیاوازی بخەنە نێوان خوا و پێغەمبەرەکانی وە دەڵێن باوەڕ بەھەندێک (لەپێغەمبەران) دەھێنین وە باوەر ناھێنن بەھەندێکی تریان و دەیانەوێت لەو نێوانەدا ڕێگەیەک بدەنەبەر');
INSERT INTO "bamoki" VALUES(4,151,'4:151','ئائەوانە بەڕاستی بێ باوەڕی تەواون وە ئامادەمان کردووە بۆ بێ باوەڕان سزایەکی ڕسواکەر');
INSERT INTO "bamoki" VALUES(4,152,'4:152','وە ئەوانەی بڕوایان ھێناوە بەخواو پێغەمبەرەکانی وە ھیچ جیاوازیەکیان نەخستە نێوان ھیچ کام لەو پێغەمبەرانە ئا ئەوانە لە داھاتوودا خوا پاداشتیان دەداتەوە, وە خوا ھەمیشە لێ بووردە و میھرەبانە');
INSERT INTO "bamoki" VALUES(4,153,'4:153','(ئەی موحەمەد صلى الله عليه وسلم) خاوەن نامەکان داوات لێ دەکەن کە بۆیان بھێنیتە خوارەوە پەڕاوێک لەئاسمانەوە جا بەڕاستی داوایان کرد لە موسا گەورەتر لەوە ووتیان خوامان نیشان بدە بەئاشکرا جابروسکەیەک لێی دان وگرتنی بەھۆی ستەم کردنیانەوە پاشان گوێرەکەیەکیان کرد بە پەرستراوی خۆیان پاش ئەوەی بۆیان ھات بەڵگە روون و ئاشکراکان ئەمجا ئێمە چاوپۆشیمان لەویش کرد (لە گوێرەکە پەرستیەکەیان) وە بەخشیمان بەموسا موعجیزە و بەڵگەی ئاشکرا (تا باوەڕ بھێنن)');
INSERT INTO "bamoki" VALUES(4,154,'4:154','وە بەرزمان کردەوە بەسەر سەریانەوە (کێوی گور) بەھۆی پەیماندانیانەوە, وە ووتمان پێیان بچنە ژوورەوە لەدەرگای (قودسەوە) بەمل کەچی وسوژدە بردنەوە, وە ووتمان پێیان دەست درێژی مەکەن لە رۆژی شەمماندا (بەڕاو کردنی ماسی) و وەرمان گرت لێیان پەیمانێکی بەھێز و بتەو');
INSERT INTO "bamoki" VALUES(4,155,'4:155','جا بەھۆی ھەڵوەشاندنەوەی پەیمانەکەیان وە بەھۆی بڕوا نەھێنانیان بە ئایەتەکانی خوا وە کوشتنی پێغەمبەرەکانیان بەناڕەوا (وەک زەکەریاو یەحیا) وە بەھۆی ئەوەی کە ووتیان: ئێمە دڵمان داپۆشراوە (نەخێر وانیە) بەڵکو خودا مۆری ناوە بەسەر دڵەکانیاندا بەھۆی باوەڕ نەھێنانیانەوە کەواتە باوەڕ ناھێنن مەگەر کەمێکیان نەبێت');
INSERT INTO "bamoki" VALUES(4,156,'4:156','وە بەھۆی بڕوانەھێنانیانەوە, وە قسەکردنیان لەدژی مەریەم کە بوختانێکی زۆر گەورە بوو');
INSERT INTO "bamoki" VALUES(4,157,'4:157','وە بەھۆی ئەوەی کەووتیان بێگومان ئێمە مەسیح عیسای کوڕی مەریەممان کوشت کە پێغەمبەری خوایە لەکاتێکدا نەشیان کوشتبوو وە ھەڵیشیان نەواسی بوو بەڵکو لێیان گۆڕدرا وە بێگومان ئەوانەی دووبەرەکیان تێکەوت دەربارەی عیسا لەگوماندان لە کوشتنەکەیدا ھیچ زانینێکیان دەربارەی نیە جگە لەشوێن کەوتنی گومان نەبێت وە بەڕاستی وبێگومان نەیان کوشتبوو');
INSERT INTO "bamoki" VALUES(4,158,'4:158','(وانیە) بەڵکو خوا بەرزی کردەوە بۆلای خۆی وەخوا ھەمیشە زاڵ و کار دروستە');
INSERT INTO "bamoki" VALUES(4,159,'4:159','وە ھیچ کەسێک نیە لەنامەدار و (خاوەن کتێب) ـەکان مەگەر بڕوای پێ دێنێت (بەعیسا) پێش مردنی وە لەڕۆژی دوایشدا ( عیسا) دەبێت بەشایەت بەسەریانەوە');
INSERT INTO "bamoki" VALUES(4,160,'4:160','بەھۆی ستەم کردنەوە لەلایەن جولەکەکانەوە حەراممان کرد لێیان چەند خۆراکێکی پاک و خاوێن کە حەڵاڵ کرابوو بۆیان وە بەھۆی زۆر دژایەتی کردن و بەرھەڵستی کردنیان لەڕێگەی خوا');
INSERT INTO "bamoki" VALUES(4,161,'4:161','وە بەھۆی سوود و (ریبا) وەرگرتنیانەوە کەبێگومان لێیان قەدەغە کرا بوو وە بەھۆی سامان و ماڵی خەڵکی خواردنیانەوە بەناڕەوا وە ئامادەمان کردووە بۆ بێ بڕواکانیان سزایەکی زۆر بەئازار');
INSERT INTO "bamoki" VALUES(4,162,'4:162','بەڵام دامەزراوان وشارەزایان لەزانیاریدا لەوان (لە جولەکەکان) و لە باوەڕدارەکان بڕوایان ھەیە بەوەی نێردراوەتە خوارەوە بۆ سەر تۆ وەبەوەش پێش تۆ نێردراوەتە خوارەوە, وە بەجێ ھێنەرانی نوێژ وزەکات دەران وباوەڕداران بەخوا و بە ڕۆژی دوایی ئەوانە لە داھاتوویەکی نزیکدا پێیان دەبەخشین پاداشتێکی زۆر گەورە');
INSERT INTO "bamoki" VALUES(4,163,'4:163','بەڕاستی ئێمە نیگامان بۆ کردویت وەک چۆن نیگامان کرد بۆ نوح و بۆ پێغەمبەرەکانی پاش ئەو وە نیگامان کرد بۆ ئیبراھیم و ئیسماعیل و ئیسحاق و یەعقوب و نەوەکانی یەعقوب وە بۆ عیساو ئەیوب و یونس و ھارون وسولەیمان وە زەبوریشمان بەخشی بەداود');
INSERT INTO "bamoki" VALUES(4,164,'4:164','وە پێغەمبەرانێک بێگومان بەسەرھاتەکانیانمان گێڕاوەتەوە بۆت لەمەو پێش وە پێغەمبەرانێک بەسەرھاتەکانیانمان نەگێراوەتەوە بۆت وە خوا قسەی کرد لەگەڵ موسـا بەقسە کردن');
INSERT INTO "bamoki" VALUES(4,165,'4:165','پێغەمبەران (مان ناردووە) کە موژدەدەرن و ترسێنەر بن بۆ ئەوەی بۆ خەڵکی نەبێت بەڵگە بەسەر خواوە دوای (ناردنی) پێغەمبەران وە خوا ھەمیشە زاڵ وکار دروستە');
INSERT INTO "bamoki" VALUES(4,166,'4:166','بەڵام خوا شایەتی دەدات بە (ڕاستیەتی) ئەو قورئانەی ناردویەتیە خوارەوە بۆت بە زانینی خۆی ناردویەتیە خوارەوە و فریشتەکانیش شایەتی ئەدەن وە خوا بەسە شایەت بێت');
INSERT INTO "bamoki" VALUES(4,167,'4:167','بەڕاستی ئەوانەی کەبێ باوەڕبوون و بەرھەڵستی ڕێگای خوایان کرد بێگومان ئەوانە گومڕابوون بە گومڕابوونێکی دوور و لەڕادە بەدەر');
INSERT INTO "bamoki" VALUES(4,168,'4:168','بەڕاستی ئەوانەی بڕوایان نەھێنا وەستەمیان کرد خوا لێیان خۆش نابێت ڕێنمونیشیان ناکات بۆ ھیچ ڕێگایەک');
INSERT INTO "bamoki" VALUES(4,169,'4:169','بێجگە لەڕێگەی دۆزەخ ئەمێننەوە تیایدا بەھەمیشەیی و بێ بڕانەوە, وە ئەوەی باسکرا لای خوا ئاسانە');
INSERT INTO "bamoki" VALUES(4,170,'4:170','ئەی خەڵکینە بێگومان پێغەمبەر ھێناویەتی بۆتان ڕاستی و ڕەوا لەلایەن پەروەردگارتانەوە کەواتە ئێوەش باوەڕ بھێنن چاکترە بۆتان وە ئەگەر باوەڕ نەھێنن ئەوا بێگومان ھەرچی لە ئاسمانەکان و زەویدا ھەیە ھی خوایە وە خوا ھەمیشە زانا و دانا و (کاردروستە)');
INSERT INTO "bamoki" VALUES(4,171,'4:171','ئەی خاوەن پەڕاوە ئاسمانیەکان زیادە ڕەوی مەکەن لەئاینەکەتاندا وە شتێک مەڵێن لە بارەی خواوە بێجگە لەڕاستی بێگومان مەسیح عیسای کوڕی مەریەم تەنھا پێغەمبەری خوایە (دروستی کردوە بە) ووتەی خۆی (ببە) بە (جیبریلدا) ناردی بۆ مەریەم و (عیسا) گیانێکە لەلایەن خواوە بەخشراوە کەواتە بڕوا بھێنن بەخواو پێغەمبەرانی وە مەڵێن (خواکانمان) سیانن (اللە و عیسی و مەریەم) (لەو ووتەیە) واز بھێنن چاکترە بۆتان بێگومان خوا پەرستراوێکی تەنھایە پاک و بێگەردی بۆ خوا لەوەی ڕۆڵەی ھەبێت ھەر ھی خوایە ھەرچی لە ئاسمانەکان و ھەرچی لەزەویدایە وە خوا بەسە چاودێربێت (و ھەموو شتێکی بدرێتە دەست)');
INSERT INTO "bamoki" VALUES(4,172,'4:172','ھەرگیز مەسیح لاریی نیە (ناڕازی نیە) لەوەی بەندەی خوا بێت ھەروەھا فریشتە نزیکەکانیش وە ھەر کەس بە بەندایەتی خوا ڕازی نەبێت وە خۆی بەگەورە دابنێت ئەوە لەداھاتوودا ھەموویان کۆدەکاتەوە لای خۆی');
INSERT INTO "bamoki" VALUES(4,173,'4:173','جا ئەوانەی کەبڕوایان ھێناوە و کردەوە چاکەکانیان کردوە ئەوا پاداشتیان دەداتەوە و بۆیان زیادیش دەکات لەبەھرەی خۆی بەڵام ئەوانەی لاریان کرد (لەبەندایەتی ئەو) وە خۆیان بەگەورە زانی ئەوە خوا سزایان دەدات بەسزایەکی بەئازار وە جگە لەخوا دەستیان ناکەوێت ھیچ دۆست و پشت گیرێک (سەرخەرێک)');
INSERT INTO "bamoki" VALUES(4,174,'4:174','ئەی خەڵکینە بەڕاستی بۆتان ھات بەڵگەی ئاشکرا لەپەروەردگارتانەوە، وە ناردمانە خوارەوە بۆتان ڕووناکییەکی ئاشکراکەر (کەقورئانە)');
INSERT INTO "bamoki" VALUES(4,175,'4:175','جا ئەوانەی باوەڕیان بەخوا ھێناوە و پشتیان بەو بەستووە ودەستیان گرت بە ئاینەکەیانەوە ئەوە بێگومان دەیانخاتە ناو بەزەیی خۆیەوە و بەھرەو چاکەی (خۆیەوە) وە ڕێنماییان دەکات بۆلای خۆی کە ڕێگای ڕاستە');
INSERT INTO "bamoki" VALUES(4,176,'4:176','(ئەی موحەممەد صلى الله عليه وسلم) داوای فتوات لێ دەکەن ولێت دەپرسن بڵێ خوا فتواتان بۆدەدات دەربارەی کەلالە (کەسێکە نەوەو کوڕو باوک و باپیری نەبێت) ئەگەر پیاوێک مردو منداڵی نەبوو تەنھا خوشکێکی ھەبوو ئەوە نیوەی میراتی برا مردووەکەی دەکەوێت وە (ئەگەر ئافرەتێ بمرێت و تەنھا برایەکی ھەبێت) ئەو (برایە) میراتی خوشکە مردووەکەی ھەموو دەبات ئەگەر خوشکە مردوەکەی منداڵی نەبێت بەڵام گەر دوو خوشک بوون (دوای برا مردووەکەیان) ئەوا ( دوو خوشکەکە) دوو سێیەکی میراتی براکەیـان دەبەن وە ئەگەر میراتگرەکان چەند خوشک و برایەک بوون، پیاوان و ئافرەتان ئەوە بۆ ھەر نێرینەیەک ئەندازەی بەشی دوو مێینە ھەیە خوا بۆتان ڕوون دەکاتەوە تا گومڕا نەبن وەخوا بەھەموو شتێک زانایە');
INSERT INTO "bamoki" VALUES(5,1,'5:1','ئەی بڕواداران بەڵێنەکانتان ببەنەسەر حەڵاڵ کراوە بۆتان (گۆشتی) ئاژەڵ و چوارپێ (مەڕو بزن و وشترو ڕەشە ووڵاغ ) بێجگەلەوەی کە دەخوێنرێتەوە بەسەرتان دا (حەرامن) دروست نیە ڕاو بکەن لەکاتێکدا ئێوە لە ئیحرام دابن بەڕاستی خوا ھەرچی بوێت بڕیاری بۆ دەدات');
INSERT INTO "bamoki" VALUES(5,2,'5:2','ئەی ئەوانەی بڕواتان ھێناوە ڕێ مەگرن لەدروشمەکانی خوا (ھەڵی مەوەشێننەوە) وجەنگ مەکەن لەمانگە حەرامەکاندا وە ڕێ مەگرن لەو قوربانیانەی بەدیاری دەھێنرێن بۆ مەککە و (ڕێ مەگرن و زیان مەدەن) لەو قوربانیانەی ملوانکەیان لەمل کراوە (دیاری کراون بۆ نەزر وقوربانی کردن) وە مەشکێنن ڕێزی ئەوانەی نیازی ماڵی خوا دەکەن بەتەمای بەھرەمەندی و ڕەزامەندی پەروەردگاریانن وە کاتێک ئیحرامتان شکاند ئەوە (دروستە) ڕاو بکەن باخەشم و قینتان لە کۆمەڵێک واتان لێنەکات لەبەر ئەوەی بەرگرییان کردن لە مزگەوتی حەرام (کەعبە) کە دەست درێژی بکەن وە کۆمەکی یەکتری بکەن لەسەر چاکە و پارێزگاری و لەخواترسان وە کۆمەکی یەکتر مەکەن لەسەر تاوان و دەست درێژی و لە خوا بترسن بێگومان خوا تۆڵەی بەتینە');
INSERT INTO "bamoki" VALUES(5,3,'5:3','لەسەرتان یاساغ کراوە (گۆشتی) مردارەوە بوو وە خوێن وە گۆشتی بەراز و ئەو ئاژەڵەی لەکاتی سەر بڕیندا ناوی غەیری خوای لێ برابێت و ئاژەڵی خنکاو (و خنکێنراو) وە ئەوەی کەلێی درابێت تاگیانی دەرچوبێت وە ئەوەی لەبەرزی کەوتبێتە خوارەوەو مرد بێت وە شۆخ لێدراو (کەبەو شۆخ لێدانە مردبێت) وە ئەوەی دڕندە لێی خواردبێت بێجگە ئەوەی (پێش مردن) سەرتان بڕی بێت ھەروەھا (یاساغ کراوە لەسەرتان) ئەو ئاژەڵەی لەسەر بەرد سەربڕابێت (نێب: چەند بەردێک بوون لە دەوری کەعبە بەپیرۆز سەیر دەکران و لەلایاندا ئاژەڵیان سەر دەبڕی) و داواکردنی بەش بەخت (و نیاز گرتنەوە) بەتیرو پشک (کە ھەموو جۆرە یانێیب ھاوشێوەکانی دەگرێتەوە) (ئەنجام دانی) ئەمانە ھەمووی لەفەرمان دەرچوون و تاوانە ئەوانەی بڕوایان نەھێناوە ئەمڕۆ نائومێد بوون لەئاینەکەتان کەواتە لێیان مەترسن تەنھا لە من بترسن ئەمڕۆ ئایینەکەتانم تەواو (کامڵ) کردوە بۆتان و چاکەو بەھرەی خۆم بەتەواوی ڕشتووە بەسەرتاندا و ڕازی بووم کەئیسلام ئایینتان بێت جا ھەرکەس ناچار بوو لەبرسیەتیدا بێ ئەوەی نیەتی گوناھـ کردنی ھەبێت (لەو گۆشتانە بخوات گوناھی ناگات) ئەوە بەڕاستی خوا لێخۆش بووی بەبەزەییە');
INSERT INTO "bamoki" VALUES(5,4,'5:4','(ئەی موحەممەد صلى الله عليه وسلم) پرسیارت لێدەکەن چی حەڵاڵ کراوە بۆیان؟ بڵێ حەڵاڵ کراوە بۆتان ھەموو خۆراکە پاکەکان وە (نێچیری) ئەو باڵندە و تانجی و سەگە ڕاو کەرانەی کەفێری ڕاوتان کردوون فێری ڕاویان ئەکەن لەوەی خوافێری کردوون دەبخۆن لەوانەی بۆتان دەگرن و ناوی خوای لەسەر بھێنن (کاتێک تانجیەکەی تێبەرئەدەن وکاتێ دەیخۆن) و لەخوا بترسن بێگومان خوا لێپرسینەوەی خێرایە');
INSERT INTO "bamoki" VALUES(5,5,'5:5','ئەمڕۆ حەڵاڵ کراوە بۆتان خۆراکە پاکوخاوێنەکان وە خۆراکی ئەوانەی نامەیان پێدراوە (ئەھلی کتاب) حەڵاڵە بۆتان وە خۆراکی ئێوەش حەڵاڵە بۆ ئەوان وە ئافرەتانی (ئازادی) داوێن پاک لەئیمانداران (حەڵاڵە بۆتان) ھەروەھا ئافرەتانی (ئازادی) داوێن پاک لەنامەدارانی (گاور وجولەکە) پێش ئێوە ئەگەر ئێوە مارەیی یەکانیان بدەنێ (حەڵاڵن بۆتان) ئێوە دەبێ داوێن پاک بن داوێن پیس نەبن (بەئاشکرا) و دۆستی ژێر بەژێرتان نەبێت (بۆ داوێن پیسی) وە ئەوەی بێ باوەڕ بێت بەئیمان و شەریعەت ئەوە بێگـومان کردەوەکانی ھەڵدەوەشێتەوە، وە لەڕۆژی دواییدا یەکێکە لە خەسارۆمەندان');
INSERT INTO "bamoki" VALUES(5,6,'5:6','ئەی ئەوانەی باوەڕدارن ئەگەر ھەستان بۆ نوێژ کردن ئەوە دەم و چاوتان بشۆن ھەروەھا دەستەکانتان لەگەڵ ئانیشکەکانتان وە دەستی تەڕ بھێنن بەسەرتاندا وە پێکانتان (بشۆن) لەگەڵ ھەردوو قولەپێتان وە ئەگەر لەش گران بوون ئەوەخۆتان خاوێن بکەنەوە (خۆتان بشۆن) وە ئەگەر نەخۆش بوون یان لەسەفەردا بوون یان کەسێکتان ھاتەوە لەشوێنی پێویستی سەرئاوکردن یان جووت بوون (یاپێستان بەریەک کەوت) لەگەڵ خێزانەکانتاندا و ئاوتان دەست نەکەوت ئەوە تەیەموم بکەن بەخۆڵێکی پاک جادەست بھێنن بەدەم و چاوو دەستەکانتان دا بەو تۆز و خۆڵە پاکە خوا نایەوێت ھیچ جۆرە تەنگی و ناڕەحەتیەک لەسەرتان دابنێت بەڵکو ئەیەوی پاکتان بکاتەوە و چاکە و بەھرەی خۆی تەواو بکات بەسەرتانەوە بەڵکو سوپاسی بکەن');
INSERT INTO "bamoki" VALUES(5,7,'5:7','یادی چاکەو فەزڵی خوا بکەنەوە بەسەرتانەوە. وە ئەو پەیمانەی کەدایمەزراندن پێی کاتێک کەووتتان بیستمان وگوێڕایەڵ بووین لەخوا بترسن بەڕاستی خوا چاک ئاگادارە بەوەی لە سینگەکاندایە');
INSERT INTO "bamoki" VALUES(5,8,'5:8','ئەی ئەوانەی باوەڕتان ھێناوە زۆر بەرێکوپێکی ھەستن بەئەنجامدانی فەرمانەکانی خوا شایەتی دەربن بەداد و ڕاستی باڕق و کینەتان لەدەستەو کۆمەڵێک ھانتان نەدات لەسەر ئەوەی ڕاست نەڕۆن و دادگەر نەبن دادگەربن ئەوە نزیکترە لەپارێزکاری وخواپەرستی یە وە، وە لەخوا بترسن بێگومان خوا ئاگایە بەکردەوەکانتان');
INSERT INTO "bamoki" VALUES(5,9,'5:9','خوا بەڵێنی داوە بەوانەی بڕوایان ھێناوە و کردەوەی چاکەکانیان کردوە بۆیان ھەیە لێخۆش بوون و پاداشتێکی گەورە');
INSERT INTO "bamoki" VALUES(5,10,'5:10','وە ئەوانەی بڕوایان نەھێنا وە ئایەتەکانی ئێمەیان بەدرۆ دائەنا ئەوانە ھاوڕێی ئاگری دۆزەخن');
INSERT INTO "bamoki" VALUES(5,11,'5:11','ئەی ئەوانەی بڕواتان ھێناوە یادی چاکە و بەھرەی خوا بکەنەوە بەسەرتانەوە کاتێک کۆمەڵێک نیازیان کرد کەدەست درێژ بکەن بۆتان خوایش بەرگری دەست درێژی ئەوانی لێکردن');
INSERT INTO "bamoki" VALUES(5,12,'5:12','سوێند بەخوا، بەڕاستی خوا پەیمانی بەھێزی وەرگرت لەنەوەی ئیسرائیل و ھەڵمان بژارد لەوان دوانزە چاودێر وە خوا فەرمووی بێگومان من لەگەڵتانم سوێند بەخوا ئەگەر بەڕێک وپێَکی نوێژ بەجێ بھێنن و زەکات بدەن و باوەڕ بھێنن بە نێرراوەکانم وە یارمەتیان بدەن وڕێزیان بگرن وە قەرز بدەن بە (بەندەکانی) خوا وخێریان پێ بکەن بەقەرزدان و خێرێکی جوان وچاک ئەوە بێگومان گوناھو خراپەکانتان ئەسڕمەوە، وە ئەتان خەمە بەھەشتانێک کەڕوبارەکان دێن و دەچن بەژێریاندا ئەمجا لەپاش ئەو مەرج و پەیمانە ھەرکەس لەئێوە بێ بڕوا ببێت ئەوە بێگومان ڕێگای ڕاستی وون کردوە');
INSERT INTO "bamoki" VALUES(5,13,'5:13','ئەمجا بەھۆی (پاش گەز بوونەوەو) ھەڵوەشاندنەوەیان بۆ پەیمانەکەیان نەفرەتمان لێ کردن وەدڵەکانیانمان گێڕا بەدڵێکی ڕەق و ووشک ووتە و فەرموودەی خوا لادەدەن ودەیگۆڕن لە شوێنەکانی خۆی وە لەبیریان کرد بەشێکی زۆرلەوەی کەئامۆژگاری کرابوون پێی بێ کۆتایی ودواھاتن (بەردەوام) ئاگادار ئەبی بەسەر ناپاکی وکردەوەی ناپاکیانەوە (وبەردەوام خەریکی فڕ وفێڵ وناپاکین) مەگەر کەمێک لەوان نەبێت ( کە ئەمە پیشەیان نیە) جا ببورە لێیان و چاو پۆشی بکە بەڕاستی خوا چاکەکەرانی خۆش دەوێت');
INSERT INTO "bamoki" VALUES(5,14,'5:14','وە لەوانەی کەدەیان ووت ئێمە گاورین بەڵێن و پەیمانی بەھێزمان لێ وەرگرتن بەڵام بەشێکی گەورە لەوەی کەئامۆژگاری کران پێی (لەسەر زمانی عیسی) لەبیریان چوو بەھۆی ئەوەوە دوژمنایەتی وکینەبەریمان خستە نێوانیان تاڕۆژی دوایی وە لەمەودوا خوا ھەواڵیان پێدەدات بەوەی کەدەیانکرد (لەجیھاندا)');
INSERT INTO "bamoki" VALUES(5,15,'5:15','ئەی نامەدارەکان (ئەی ئەھلی کتاب) بێگومان نێرراومان ھاتووە بۆ ئێوە ڕوونی دەکاتەوە بۆتان زۆری لەوەی کەئێوە دەتان شاردەوە لەکتێبی (تەورات و ئینجیل) دا وەچاو پۆشی دەکات لەزۆریک لەوانە بێگومان بۆتان ھاتووە لەلایەن خواوە ڕووناکی یەکی گەورە و نامەیەکی ڕوونکەرەوە');
INSERT INTO "bamoki" VALUES(5,16,'5:16','خوا ڕێنمونی کەسێکی پێدەکات شوێن ڕەزامەندی ئەو بکەوێت بۆڕێگەکانی ئارامی و ئاشتی وەدەریان دەھێنێت لەتاریکیەکانی (بێ باوەڕیەوە) بۆ ناو ڕووناکی (ئیمان و ئیسلام) بە فەرمانی خۆی وە ڕێنمونیان دەکات بۆ ڕێگای ڕاست');
INSERT INTO "bamoki" VALUES(5,17,'5:17','سوێند بەخوا بەڕاستی بێ باوەڕبوون ئەوانەی کەووتیان بەڕاستی خوا ھەر مەسیحی (عیسی) کوڕی مەریەمە (ئەی موحەممەد صلى الله عليه وسلم) بڵێ ئەوە کێ دەتوانێت ڕێی ھیچ شتێک بگرێت لەخوا ئەگەر بیەوێت مەسیحی کوڕی مەریەم لەناو ببات ھەروەھا دایکی و ھەرچی کەسیش لەزەویدا ھەیە وەھەر بۆ خوایە خاوەن دارێتی ئاسمانەکان و زەوی وموڵکی ئەون و ھەرچی وا لە نێوانیاندا دروست ئەکاو دایئەھێنێت ھەرچی بیەوێت وەخـوا بەسەر ھەموو شتێکدا بەتوانایە');
INSERT INTO "bamoki" VALUES(5,18,'5:18','جولەکە وگاورەکان دەیانووت ئێمە کوڕان و خۆشەویستانی خواین بڵێ دەی بۆچی سزاتان دەدات بەھۆی گوناھە کانتانەوە (نەخێر ڕاست نیە) بەڵکو ئێوە مرۆڤێکن لەو مرۆڤانەی کەخوا دروستی کردون خۆش ئەبێت لەھەر کەس بیەوێت و سزای ھەرکەسیش ئەدات کەبیەوێت و ھەر بۆ خوایە موڵکی ئاسمانەکان و زەوی وەھەرچی والە نێوانیاندا وە ھەر بۆ لای ئەویشە گەڕانەوە');
INSERT INTO "bamoki" VALUES(5,19,'5:19','ئەی نامەدارەکان بێگومان نێرراوەکەمان ھات بۆ ناوتان (بەرنامەی خوا) ڕوون دەکاتەوە بۆتان پاش ماوەیەک لەناردنی پێغەمبەران لەبەر ئەوەی نەڵێن: نەھات بۆ لامان ھیچ مژدەدەرو ترسێنەرێک ئەوەتا بێگومان مژدە دەرو ترسێنەرتان بۆ ھاتووە, وەخوا بەتوانا و دەسەڵاتدارە بەسەر ھەموو شتێکدا');
INSERT INTO "bamoki" VALUES(5,20,'5:20','باسی ئەو کاتە بکە کەموسا فەرمووی بەگەلەکەی ئەی گەلەکەم بیری چاکەو بەھرەی خوا بکەنەوە بەسەرتانەوە کاتێک کەپێغەمبەرانی تێدا ھەڵخستوون وە چەندینی کردوون بەپادشا ومەلیک وە شتێکی پێ بەخشیوون کە نەی بەخشیووە بەھیچ کەس لەجیھانیان');
INSERT INTO "bamoki" VALUES(5,21,'5:21','ئەی گەلەکەم بچنە ناو ئەو خاکە پیرۆزەی (پاکراوەتەوە لەبت پەرستی کە مەبەست پێی بیت المقدسە) کەخوا بڕیاری داوە بۆتان وە پاشەوپاش وەرنەگەڕێنەوە لەئاینەکەتان (ئەگەر بگەڕێنەوە) ئەوە بەزیانباری و نائومێدی ئەگەڕێنەوە');
INSERT INTO "bamoki" VALUES(5,22,'5:22','ووتیان ئەی موسا بەڕاستی گەلێکی زۆر دارو ستەمکار لەو شارە دایە وە ئێمە ھەرگیز ناچینە ناوی تا ئەوان لێی دەرنەچن ئەمجا ئەگەر ئەوان لێی دەرچوون ئەو کاتە بێگومان ئێمە تێی ئەچین');
INSERT INTO "bamoki" VALUES(5,23,'5:23','دوو پیاو ووتیان: لەوانەی لەخوا دەترسان وەخوا چاکەی ئیمانی ڕشت بوو بەسەریاندا بدەن بەسەریاندا لەقاپی شارەوە بچنە ژورەوە جا ئەگەر چوونە ناویەوە ئەوە بەڕاستی ئێوە زاڵ دەبن بەسەریاندا وەپشت ھەر بەخوا ببەستن ئەگەر ئێوە باوەڕِدارن');
INSERT INTO "bamoki" VALUES(5,24,'5:24','ووتیان ئەی موسا بەڕاستی ئێمە ھەرگیز ناچینە ناو ئەوشارە تائەوان تێیدابن کەواتە بڕۆ خۆت و پەروەردگارت لەدژیان جەنگ بکەن بێگومان ئێمە لێرە دانیشتووین');
INSERT INTO "bamoki" VALUES(5,25,'5:25','(موسا) ووتی ئەی پەروەردگارم بەڕاستی من خاوەنی ھیچ کەسێک نیم بێجگە لەخۆم و براکەم نەبێت کەواتە جیایی بخەرە نێوان ئێمە و ئەوگەلەی کە لەسنووری خوا دەرچوون');
INSERT INTO "bamoki" VALUES(5,26,'5:26','(خوا) فەرمووی بەڕاستی ئەو (خاکە پیرۆزەی بیت المقدس) قەدەغە کراوە لەسەریان ماوەی چل ساڵ بەسەر گەردانی دەخولێنەوە بەسەر زەویدا بۆیە خەم مەخۆ لەسەر ئەو گەلەی کەلە سنووری خوا دەرچوون');
INSERT INTO "bamoki" VALUES(5,27,'5:27','(ئەی موحەممەد صلى الله عليه وسلم) بخوێنەرەوە بەسەریاندا ھەواڵی دوو کوڕەکەی ئادەم کە حەق و ڕاستە کاتێک ھەردووکیان دوو جۆرە قوربانیان کرد ئەمجا لەیەکێکیان وەرگیرا وە لەوی تریان وەرنەگیرا (ئەوەیان لێی گیرا نەکرا) ووتی بێگومان دەبێ ھەر بتکوژم ئەویش ووتی خوا (قوربانی) تەنھا لەپارێزکاران و لەخواترسان گیرا دەکات');
INSERT INTO "bamoki" VALUES(5,28,'5:28','سوێند بەخوا ئەگەر تۆ دەستم بۆ درێژکەی بۆ ئەوەی بمکوژی من دەست درێژ ناکەم بۆ تۆ بۆ ئەوەی بتکوژم بەڕاستی من لەخوا ئەترسم کە پەروەردگاری ھەموو جیھانیانە');
INSERT INTO "bamoki" VALUES(5,29,'5:29','بەڕاستی من دەمەوێت کە تۆ بگەڕێیتەوە (بۆلای خوا) بەگوناھی من و خۆیشتەوە ئەوسا تۆ لەھاوەڵانی دۆزەخ دەبیت ھەر ئەوەش پاداشتی ستەمکارانە');
INSERT INTO "bamoki" VALUES(5,30,'5:30','ئەمجا دەرونی(ئەو تاوانبارە) ھانیدا بۆ کوشتنی براکەی وە کوشتی بەھۆی یەوە چووە ڕیزی زیان باران وخەسارومەندان');
INSERT INTO "bamoki" VALUES(5,31,'5:31','پاشان خوا قەلە ڕەشێکی نارد لەزەویدا چاڵی ھەڵئەکەند (بۆ قەلە ڕەشێکی مردارەوە بوو) بۆ ئەوەی نیشانی بدات کەچۆن لەشی براکەی بشارێتەوە ووتی ئای داخی سەختم وتیاچوون بۆ من بۆ من دەستە پاچە بووم لەوەی وەک ئەم قەلەڕەشەبم؟ تا تەرم و عەورەتی براکەم بشارمەوە ئەوسا چووە ڕیزی پەشیمان بوانەوە');
INSERT INTO "bamoki" VALUES(5,32,'5:32','ھەر لەبەر ئەوەی (کەلەو داستانەدا باسکرا) پێویستمان کرد لەسەرنەوەی ئیسرائیل کەبێگومان بڕیار ئەمەیە ئەوەی تەنھا کەسێک بکوژێت لەتۆڵەی کەسێکی کوژراودا نەبێت یان تۆڵەی خراپە کاری و ئاژاوە بنێتەوە لەسەر زەویدا ئەوە وەک ئەوەوایە ھەموو خەڵکی کوشتبێت ئەوەش بێ تاوانێک ڕزگار کات لەکوژران ئەوە وەک ئەوەوایە ھەموو ئادەمی ڕزگار کردبێت و زیندووی کردبنەوە سوێند بەخوا بێگومان، پێغەمبەرەکانمان ھاتن بۆلایان بەبەڵگە ڕوونەکانەوە کەچی لەپاش ئەوە زۆرێک لەوانە زیادە ڕەویان کرد لەزەویدا');
INSERT INTO "bamoki" VALUES(5,33,'5:33','بەڕاستی تۆڵەی ئەوانەی کە جەنگ و دوژمنایەتی ئەکەن لەگەڵ خواو پێغەمبەرەکەی دا وە ھەوڵ ئەدەن لەزەویدا خراپە بڵاوبکەنەوە (ئەوە تۆڵەیان) ئەوەیە بکوژرێن یا ھەڵبواسرێن بەدارا یــان دەسـت وقاچیان ببڕدرێت بەپێچەوانەوە (ڕاسـت وچەپ) یان دوور بخرێنەوە لەووڵات ئەمە زەبونی و ئـابـڕو بردنیانە لە جیھاندا لە دواڕۆژیشدا بۆیان ھـەیــە ســزایـەکی گــەورە');
INSERT INTO "bamoki" VALUES(5,34,'5:34','بێجگەلەوانەی تەوبەیان کردووە لەپێش ئەوەی دەسەڵاتتان ببێت بەسەریاندا تا بزانن کەبەڕاستی خوا لێ بوردە و میھرەبانە');
INSERT INTO "bamoki" VALUES(5,35,'5:35','ئەی ئەوانەی بڕواتان ھێناوە لەخوا بترسن بگەڕێن بەدوای ھەموو ھۆکارێکدا کەنزیکتان بخاتەوە لەخوا وەتێ بکۆشن وخەبات بکەن لەڕێگەی خوادا بەئومێدی ڕزگار بونتان');
INSERT INTO "bamoki" VALUES(5,36,'5:36','بەڕاستی ئەوانەی کە لەجیھاندا بێ بڕوا بوون ئەگەر ببنە خاوەنی ھەرچی لەزەویدایە ھەمووی ئەوەندەی تریشی لەگەڵ بێت بۆ ئەوەی خۆیانی پێ بکڕنەوە و رزگارکەن لەسزای ڕۆژی دوایی وبیبەخشن بۆ ڕزگار بوونیان لێیان وەرناگیرێت وە بۆیان ھەیە سزایەکی بە ئازار');
INSERT INTO "bamoki" VALUES(5,37,'5:37','دەیانەوێت لە ئاگری دۆزەخ بێنەدەرەوە بەڵام ھەرگیز دەرچوونیان لە (ئاگری دۆزەخ) نیە و سزای بەردەوامیان بۆ ھەیە');
INSERT INTO "bamoki" VALUES(5,38,'5:38','پیاو و ئافرەتی دز دەستیان ببڕن لەتۆڵەی ئەو دزیەی کردویانە ئەمە تۆڵە و تەمێ کردنێکە لەلایەن خواوە وە خوا باڵا دەست وکار دروستە');
INSERT INTO "bamoki" VALUES(5,39,'5:39','جا ھەر کەس تۆبەی کرد لەپاش ستەمەکەی (دزیەکەی) وە کردەوەی چاکی کردو باش بوو ئەوە بێگومان خوا تۆبەکەی لێ وەردەگرێت بەڕاستی خوا لێبوردە و میھرەبانە');
INSERT INTO "bamoki" VALUES(5,40,'5:40','ئایا نەتزانیوە کەھەر بۆ خوایە خاوەنێتی ئاسمانەکان و زەوی سزای ھەر کەسێک دەدات کەبیەوێت وە ئەبوورێت لەھەر کەس خۆی بیەوێت وەخوا بەسەر ھەموو شتێکدا بەتوانا و دەسەڵاتدارە');
INSERT INTO "bamoki" VALUES(5,41,'5:41','ئەی (پێغەمبەر صلى الله عليه وسلم) خەم بارت نەکات ئەوانەی کە پێشبڕکێ دەکەن لەبێ بڕواییدا لەوانەی بەدەم ئەڵێن بڕوامان ھێناوە کەچی دڵە کانیان بڕوای نەھێناوە ھەروەھا لەوانەش کە جولەکەن زۆر گوێ قوڵاخن بۆ درۆ زۆر گوێ لە مشتی دەستەیەکی ترن کەنەھاتون بۆلای تۆ ووتەکانی (تەورات) دەگۆڕن لە پاش ئەوەی خوا لەشوێنی خۆی دایناون ئەڵێن ئەگەر ئەمە ی (ئێمە) تان پێ گوترا (لەلایەن موحەممەد صلى الله عليه وسلم) ئەوە وەری گرن ئەگەر لەئەمەی (ئێمە) تان پێ نەگوترا ئەوا خۆتان بپارێزن وەھەر کەسێک خوا بیەوێ کافرو گومڕای بکات ئەوە تۆ لەبەرامبەر خوادا ھیچت پێ ناکرێت بۆی ئەوانە کەسانێکن خوا نەیویستووە دڵەکانیان پاک بکاتەوە لەدونیادا سەرشۆڕدەبن وە لەڕۆژی دواییشدا بۆیان ھەیە سزایەکی زۆرگەورە');
INSERT INTO "bamoki" VALUES(5,42,'5:42','ئەو (جولەکانە) زۆر گوێ قوڵاخی درۆن ( ھەمیشە گوێ ھەڵدەخەن بۆ درۆ) ماڵی حەرام زۆر دەخۆن جا ئەگەر ئەوانە ھاتن بۆلات ئەوە (بەویستی خۆت) بڕیار بدە لە نێوانیاندا یان وازیان لێ بھێنە خۆ ئەگەر وازیان لێ بھێنیت ئەوە ھیچ زیانێکت پێ ناگەیەنن وە ئەگەر بڕیارت دا لەنێوانیاندا ئەوا بە دادوەری بڕیار بدە چونکە بەڕاستی خوا دادگەرانی خۆش دەوێت');
INSERT INTO "bamoki" VALUES(5,43,'5:43','وە ئەوانە چۆن تۆدەکەن بەدادوەر لەکاتێکدا تەوراتیان لایە کە حوکم و بڕیاری خوای تیایە ئەمجا لەپاش ئەوەی (کەکردیتیان بەدادوەر) ڕوو وەر دەگێڕنؤ بێگومان ئەوانە بڕوادار نین');
INSERT INTO "bamoki" VALUES(5,44,'5:44','بێگومان ئێمە تەوراتمان ناردە خوارەوە کەڕێنمونی و ڕووناکی تێدا بوو ئەو پێغەمبەرانە حوکمیان پێ دەکرد کەموسڵمان و ملکەچ بوون بۆ ئەوانەی کە جولەکە بوون ھەروەھا زانا خوا ناسەکان و زانا شەرع زانەکان (حوکمیان پێ دەکرد) بەھۆی ئەوەی فەرمانی پارێزگاری کتێبی خوا (تەورات) یان پێ کرا بوو (لەلایەن پێغەمبەرەکانیانەوە) وەشایەتیش بوون لەسەری کەواتە ئەی (زانایانی جولەکە) لەخەڵکی مەترسن بەڵکو تەنھا لەمن بترسن بەڵگەو فەرمانەکانی من مەگۆڕنەوە (مەفرۆشن) بە نرخێکی کەم وە ھەر کەس حوکم نەکات بەو بڕیارانەی کەخوا ناردوویەتیە خوارەوە ئا ئەوانە بێ باوەڕن و موسڵمان نین');
INSERT INTO "bamoki" VALUES(5,45,'5:45','لەتەوراتدا نوسیمان (پێویستمان کرد) لەسەریان کەبێگومان ھەر کەسێک بەدەستی ئەنقەست کەسێک بکوژێت ئەکوژرێتەوە لە بەرامبەریدا وە چاو لەبری چاو وە لووت لەبری لووت وە گوێچکە لەبری گوێچکە وە ددان لەبری ددان وەزامەکانیش تۆڵەیان زامدار کردنی دەست درێژی کەرانە جا ھەر کەس لەتۆڵە سەندن خۆش بێت ئەوە ئەبێتە ھۆی سڕینەوەی گوناھی وە ھەر کەسێک حوکم نەکات بەوەی کەخودا ناردویەتیە خوارەوە ئەوا ھەر ئەو کەسانە ستەم کاری تەواون');
INSERT INTO "bamoki" VALUES(5,46,'5:46','ئەمجا بە دوای پێغەمبەرانی(جولەکەدا) ڕەوانەمان کرد عیسای کوڕی مەریەم باوەڕی ھەیە بەئەو تەوراتەی کەلەپێش خۆیەوە ھاتووە و ( ڕاستی دەردەخات وباوەڕی پێیەتی) وەئینجیلمان پێ بەخشی کەڕێنمونی و ڕووناکی تێدایە وە ئەو تەوراتە بەڕاست دەزانێت کەلە پێش ئەوەوە ھاتووە, وە ڕێنمونی و ئامۆژگاری یە بۆ پارێزکاران ولەخواترسان');
INSERT INTO "bamoki" VALUES(5,47,'5:47','دەبا پەیڕەوانی ئینجیل حوکم بکەن بەوەی کەخوا ناردوویەتیە خوارەوە تیایدا وە ھەرکەس حوکم نەکات بەوەی کەخوا ناردویەتیە خوارەوە ئا ئەوانە بەتەواوی یاخین و لەفەرمانی خوا دەرچوون');
INSERT INTO "bamoki" VALUES(5,48,'5:48','وە ئەم قورئانەمان ناردە خوارەوە بۆتۆ بەحەق و ڕاستی و کتێبەکانی پێش خۆی بەڕاست دەزانی و پشتیوانیان لێ دەکات (پێش دەست تێخستن و گۆڕینیان لەلایەن جولەکە وگاورەکانەوە) و چاودێریانە کەواتەحوکم و بڕیاربدە لەناو نامەدارەکاندا بەوەی خوا ناردویەتیە خوارەوە, وە شوێن ھەواو ھەوەسی ئەوان مەکەوە لانەدەی لەو حوکم و بڕیارە ھەق و ڕاستەی کەبۆت ھاتووە دامان ناوە بۆ ھەرئوممەتێک لەئێوە شەریعەت و ڕێگەیەکی ڕوون وە ئەگەر خوا بیویستایە ئێوەی ئەکرد بەیەک ئوممەت و کۆمەڵ بەڵام (نەیکردوون) بۆئەوەی تاقیتان بکاتەوە لەو شەریعەت و یاسایانەی کەپێی بەخشیوون کەواتە پێش بڕکێ بکەن بۆ ئەو شتانەی کەچاکن گەڕانەوەتان ھەر بۆلای خوایە بەگشتی ئەمجا ھەواڵتان پێدەدا بەوەی کەلەجیھاندا جیاوازبوون تیایدا');
INSERT INTO "bamoki" VALUES(5,49,'5:49','وە (فەرمانمان پێکردیت) کەحوکم بکە لەنێوانیاندا بەوەی کەخوا ناردویەتیە خوارەوە و شوێن حەزو ئارەزویان نەکەویت وە ووریابە لێیان لەوەی بت ترازێنن لە ھەندێک لەوەی خوا ناردویەتیە خوارەوە بۆت ئەمجا ئەگەر ئەوانە ڕویان وەرگێڕاو سەرپێچی یان کرد ئەوە بزانە و دڵنیابە خوا ئەیەوێت بەھۆی ھەندێک لە تاوانەکانیانەوە سزایان بدات و تووشی زیانیان بکات بێگومان زۆر لە ئادەمی یەکان سەرکەشن و لەسنووری خوا دەرچوون');
INSERT INTO "bamoki" VALUES(5,50,'5:50','ئایا ئەوان داوای حوکمی نەفامی دەکەن؟ ئایا کێ حوکم و بڕیاری لە ھی خوا جوان و چاکترە؟ بۆکەسانێک کە بڕوای تەواویان ھەیە');
INSERT INTO "bamoki" VALUES(5,51,'5:51','ئەی ئەوانەی باوەڕتان ھێناوە جولەکەو گاور مەکەن بەپشتیوان و خۆشەویستی خۆتان ئەوانە ھەندێکیان دۆستی یەکتـرن ئەوەی لە ئێوە ئەوان بکات بەپشتیوان و دۆستی خوی ئەوە بەڕاستی ئەوکەسە لەوانە بێگومان خوا ڕێنمونی گەلی ستەمکاران ناکات');
INSERT INTO "bamoki" VALUES(5,52,'5:52','بۆیە ئەبینی ئەوانەی دڵیان نەخۆشی (دوو ڕووی) تیایە پێش برکێ ئەکەن لەدۆستایەتی ئەو (جولەکەو گاور) انەدا ئەڵێَََن ئەترسین کەبەسەر ھاتی ناخۆشی ڕۆژگار دووچارمان ببێت دەی نزیکە خوا سەرکەوتن و زاڵ کردنی تەواو بنێرێت (بۆ پێغەمبەر صلى الله عليه وسلم) یان لەلایەن خۆیەوە کارێکی تر بکات بەھۆی ئەوەوە وایان لێدێت لەوەی پەنھانیان کردبوو لەدەروونی خۆیاندا پەشیمان دەبنەوە');
INSERT INTO "bamoki" VALUES(5,53,'5:53','ئەوسا ئەوانەی بڕوایان ھێناوە دەڵێن ئایا ئەوکەسانە ئەوانەن کەسوێندیان بەخوا ئەخوارد بەتوندترین سوێندیان کەبەڕاستی ئەوان لەگەڵتانن کردەوەکانیان پوچەڵ بوویەوە بەھۆی ئەوەوە زەرەرمەندو ڕەنجە ڕۆبوون');
INSERT INTO "bamoki" VALUES(5,54,'5:54','ئەی ئەوانەی کەباوەڕتان ھێناوە ھەر کەس لەئێوە ھەڵگەڕێتەوە لەئاینەکەی ئەوە بەزووی خوا لەباتی ئەوان گەل و کۆمەڵێَکی تر ئەھێنێت خوا ئەوانی خۆش ئەوێت و ئەوانیش خوایان خۆش ئەوێت بەسۆزو بەزەیین بەرانبەر بڕوا داران بەھێزو توندن بەرانبەر بێ بڕوایان تێ کۆشان دەکەن لەڕێی خوادا و ناترسن لەسەرزەنشتی سەرزەنشت کاران ئەوەی باسکرا چاکەو بەھرەی خوایە ئەیبەخشێت بەھەر کەس کەخۆی بیەوێت وەخوا دەستەڵات فراوان و زانایە');
INSERT INTO "bamoki" VALUES(5,55,'5:55','بێگومان دۆست و یارمەتیدەری ئێوە تەنھا خواو پێغەمبەرەکەیی و بڕوا دارانن ئەوانەی کەبەڕێک و پێکی نوێژەکانیان بەجێ ئەھێنن و زەکات ئەدەن بەردەوام مل کەچن بۆ فەرمانی خوا');
INSERT INTO "bamoki" VALUES(5,56,'5:56','وە ھەر کەس خواو پێغەمبەرو ئیمانداران بکات بەپشتیوانی خۆی ئەوە بێگومان ھەر کۆمەڵی خودا زاڵ و سەرکەوتوون');
INSERT INTO "bamoki" VALUES(5,57,'5:57','ئەی کەسانێک کەبڕواتان ھێناوە دامەنێن ئەوانەی کەئاینەکەی ئێوە دادەنێن (بەشتێکی) گاڵتەو لاقرتی پێکراو لەوانەی نامەیان پێ بەخشراوە لەپێش ئێوەدا وە لەبێ بڕواکانیش (دایان مەنێن) بەدۆست و خۆشەویستی خۆتان وە لەخوا بترسن ئەگەر ئێوە بڕوادارن');
INSERT INTO "bamoki" VALUES(5,58,'5:58','کاتێک ئێوە بانگ دەدەن بۆ نوێژ ئەو (دوو ڕووانە) گاڵتە ولاقرتی پێ ئەکەن ئەمەش بەھۆی ئەوەوە یە کەئەوان گەلێکی نەفام و نەزانن');
INSERT INTO "bamoki" VALUES(5,59,'5:59','بڵێ ئەی نامەدارەکان ئایا ھیچ ڕەخنە و ڕقێکتان ھەیە لەئێمە بێجگە لەوەی کەبڕوامان ھێناوە بەخوا وە بەو قورئانەی ھێنراوەتە خوارەوە بۆمان وە بەوەی لەمەوپێش ھێنراوەتە خوارەوە بێگومان زۆربەتان سەرکەش و لەسنوور دەرچوون');
INSERT INTO "bamoki" VALUES(5,60,'5:60','ئەی (موحەممەد صلى الله عليه وسلم) بڵێ ئایا ھەواڵتان بدەمێ کێ شوێن و پاداشتی خراپترە لای خوا لەوە ئەوە کەسێکە خوا نەفرەتی لێ کردبێت وخەشمی لێگرتبێت وە ھەندێکی لێ کردبن بەمەیمون و بەراز وە بت وتاغوتی پەرستبێت ئەوانەی باسکران خراپترین شوێنیان ھەیە و گومڕاترین کەسن لەڕێگای ڕِاست');
INSERT INTO "bamoki" VALUES(5,61,'5:61','وەکاتێک دێن بۆلاتان دەڵێن بڕوامان ھێناوە سوێند بێت بێگومان بەبێ بڕواییەوە ھاتوون وەبێگومان ھەر بەبێ بڕواییشەوە دەرچوون وە خوا زۆر چاک دەزانێت بەوەی کەدەیان شاردەوە');
INSERT INTO "bamoki" VALUES(5,62,'5:62','وەئەبینی زۆرێک لەوانە پێشبڕکێ ئەکەن لەگوناھو دەست درێژی کردن و حەرام خواردندا (وەک بەرتیل) بێگومان ئەوەی کەدەیانکرد زۆر بەدو خراپ بوو');
INSERT INTO "bamoki" VALUES(5,63,'5:63','ئەوە بۆچی رێگریان لێ نەکردن خواپەرست و زاناکانیان لەگوفتاری گوناھیان و لەحەرام خواردنیان (وەک بەرتیل) بەڕاستی شتێکی بەدبوو ئەوەی دەیانکرد');
INSERT INTO "bamoki" VALUES(5,64,'5:64','جولەکەکان ووتیان خوا دەستی بەستراوە (پاکی بۆخودا ) یاخوا ئەوان خۆیان چنگ قوچراو و دەست بەستراوبن وە نەفرینیان لێکرا بەھۆی ئەوەی کە ووتیان (نەخێر وانیە) بەڵکو ھەردوو دەستی خودا ھەمیشە کراوەیە (لەم ئایەتەدا جێگیرکردن و باسکردنی سیفەتی دەست بۆ خودا ھاتووە ئێمە باوەڕمان پێیەتی بەبێ لێچواندن و شێوەکەی نازانین و تەنھا خوا دەیزانێت) چۆنی بیەوێت دەیبەخشێت وەسوێند بەخوا زیاد دەکات بۆ زۆر لەوان (بۆ زانا و سەرۆکەکانیان) ئەو قورئانەی ھێنراوتە خوارەوە بۆ تۆ لەلایەن پەروەردگارتەوە سەرکەشی و بێ بڕوای (زیاد ئەکات بۆیان) وەخستومانەتە نێوانیان دوژمنایەتی و کینە و ڕِقەبەری تاڕۆژی دوایی ھەر جارێک ئاگرێکیان ھەڵگیرساندبێت بۆ ئاژاوە نانەوەو جەنگ خوا ئەو ئاگرەی کوژاندۆتەوە, وە تێکۆشان ئەکەن لە زەویدا بۆ خراپەو تێکدان خواش خراپەکارانی خۆش ناوێت');
INSERT INTO "bamoki" VALUES(5,65,'5:65','خۆ ئەگەر بەڕاستی نامەدارەکان ( ئەھلی کتاب لەگاور و جولەکەکان) بڕوایان بھێنایە و خۆیان بپاراستایە و پارێزکار ولەخواترس بوونایە بێگومان گوناھو خراپەکانیانمان دەسڕی یەوە لێیان وە دەمانخستنە ناو بەھەشتانی پڕ ناز و نیعمەت');
INSERT INTO "bamoki" VALUES(5,66,'5:66','وە بێگومان ئەگەر ئەو نامەدارانە تەورات و ئینجیلیان کار پێ بکردایە ھەروەھا ئەو قورئانەی کەھێنراوەتە خوارەوە بۆیان لەلایەن پەروەردگاریانەوە بێگومان ڕۆزیان دەخوارد لەلای سەرویانەوە (لە ئاسمانەوە) وە لە ژێرپێیانەوە (لە زەویەوە) تێیاندایە دەستە و کۆمەڵێکی ڕێک و پێک بەڵام زۆرێک لەوان ئەوەی دەیکەن زۆر بەد و خراپە');
INSERT INTO "bamoki" VALUES(5,67,'5:67','ئەی (پێغەمبەر صلى الله عليه وسلم) ڕایگەیەنە (بەخەڵک) ئەوەی ھێنراوەتە خوارەوە بۆت لەلایەن پەروەردگارتەوە, وە ئەگەر وانەکەیت ئەوە ڕاسپێریەکەی خوات ڕانەگەیاندوە و بەجێت نەھێناوە, وە خوا تۆ دەپارێزێت لەخەڵکی (کەبتکوژن) بێگومان خوا ڕێنمونی بێ بڕوایان ناکات');
INSERT INTO "bamoki" VALUES(5,68,'5:68','(ئەی موحەممەد صلى الله عليه وسلم) بڵێ ئەی نامەدارەکان (ئەی ئەھلی کتاب) ئێوە لەسەر ھیچ شتێک نین ھەتا بەڕێک و ڕاستی پەیڕەوی تەورات و ئینجیل نەکەن ھەروەھا ئەو قورئانەی کەھێنراوەتە خوارەوە بۆتان لەلایەن پەروەردگارتانەوه سوێند بەخوا زیاد ئەکات بۆ زۆریان ئەوەی ھێنراوەتە خوارەوە بۆت لەلایەن پەروەردگارتەوە سەرکەشی و بێ باوەڕییان (زیاد دەکات) کەواتە خەم مەخۆ لەسەر کۆمەڵی بێ بڕوایان');
INSERT INTO "bamoki" VALUES(5,69,'5:69','بەڕاستی ئەوانەی بڕوایان ھێناوە و ئەوانەی بوون بەجولەکە وە ئەستێرە پەرست یان فریشتە پەرستەکان وە شوێن کەوتوانی عیسی پێغەمبەر ھەر کەس لەوانە بڕوایان ھێنا بێت بەخوا و بە ڕۆژی دوایی و کردەوەی چاکی کردبێت ئەوە نە ھیچ مەترسیەکیان لەسەرە وە نەخەفەت بار ئەبن (لەڕۆژی دواییدا');
INSERT INTO "bamoki" VALUES(5,70,'5:70','سوێند بەخوا پەیمانی بەھێزمان وەرگرت لە بەنو ئیسرائیل و چەند پێغەمبەرێکمان نارد بۆیان ھەر جارێک پێغەمبەرێکیان بۆ بھاتایه بە ھەر فەرمانێ کە بەدڵیان نەبوایە دەستەیەکیان بەدرۆزن دائەنان وە دەستەیەکیشیان دەکوشتن');
INSERT INTO "bamoki" VALUES(5,71,'5:71','وایان دەزانی کەتۆڵە و سزا دووچاریان نابێت جا بۆیە کوێرو کەڕبوون (لەئاست بیستن و بینینی ھەق) لە پاشان خوا تەوبە و گەڕانەوەی لێ وەرگرتن ئەمجا لەپاش ئەوە زۆر بەیان کوێرو کەڕبوون (لەبیستن و بینینی بەڵگەو ئایەتەکانی خوا وە خوا بینەرە بەھەر کردەوەیەک کەدەیکەن');
INSERT INTO "bamoki" VALUES(5,72,'5:72','سوێند بێت بێ باوەڕبوون ئەوانەی کەووتیان بێگومان خوا مەسیحی کوڕی مەریەمە لەکاتێکدا مەسیح (کاتێک ڕەوانەکرا) ووتی ئەی بەنو ئیسرائیل تەنھا خوا بپەرستن کە پەروەردگاری من و ئێوەشە بەڕاستی ھەر کەس ھاوبەش بۆخوا دابنێت ئەوە بێگومان خوا بەھەشتی لێ حەرام کردووە، وە جێگا و شوێنی ئاگر دەبێت وە بۆ ئەو ستەمکارانە ھیچ یارمەتیدەرێک نیە');
INSERT INTO "bamoki" VALUES(5,73,'5:73','سوێند بەخوا بەڕاستی بێ باوەڕبوون ئەوانەی کەووتیان بێگومان خوا یەکێکە لەو سێ خوا (کەبریتین لەپەروەردگارو عیسی و دایکی) (لەکاتێکدا) ھیچ پەرستراوێکی ڕاست نیە بێجگە لەتەنھا خوایەک وە ئەگەر واز نەھێنن لەو ووتە ناڕەواو بەدانە سوێند بەخوا دووچاری ئەوانەیان ئەبێت کەبێ باوەڕبوون سزایەکی بەئازارو سەخت');
INSERT INTO "bamoki" VALUES(5,74,'5:74','دەی ئایا بۆ تۆبە ناکەن و ناگەڕێنەوە بۆلای خوا؟ و داوای لێبوردنی لێ ناکەن؟ لەکاتێکدا خوا لێبوردەی میھرەبانە');
INSERT INTO "bamoki" VALUES(5,75,'5:75','مەسیحی کوڕی مەریەم ھیچ نیە جگە لەپێغەمبەری خوا بێگومان لەپێش ئەوەوە چەندەھا پێغەمبەر بوون دایکیشی ئافرەتێکی زۆر ڕاستگۆیە ھەردووکیان خواردنیان دەخوارد (واتە خوانین) سەرنج بدە چۆن ئایەت و بەڵگەکان ڕوون دەکەینەوە بۆیان پاشان سەیرکە چۆن لا دەدرێن لەحەق بەرەو بەتاڵ دەڕۆن');
INSERT INTO "bamoki" VALUES(5,76,'5:76','(ئەی موحەممەد صلى الله عليه وسلم) بڵێ ئایا ئێوە بێجگە لەخوا شتێک دەپەرستن؟ کە ھیچ زەرەرو سوودێکی نیە بۆتان وە خوا ھەر خۆی بیسەر و زانایە');
INSERT INTO "bamoki" VALUES(5,77,'5:77','(ئەی موحەممەد صلى الله عليه وسلم) بڵێ ئەی نامەدارەکان زیادە ڕۆی مەکەن لەئاینەکەتاندا جگە لەوە نەبێت کەڕاست وحەقە و شوێن ئارەزووی کەسانێک مەکەون کەبەڕاستی پێشتر گومڕاو سەرلێ شێواو بوون وە زۆر کەسیشیان گومڕاکرد وە خۆشیان گومڕابوون و لایاندا لەڕێگای ڕاست');
INSERT INTO "bamoki" VALUES(5,78,'5:78','نەفرین کرا لەوانەی بێ بڕوابوون لەبەرەو نەوەی ئیسرائیل لەسەر زمانی داود ولەسەر زمانی عیسای کوڕی مەریەمیش ئەو نەفرینەش لەبەر ئەوە بوو سەرپێچیان کرد و ھەمیشە دەست درێژیان دەکرد');
INSERT INTO "bamoki" VALUES(5,79,'5:79','ئەوانە رێگری یەکترییان نەدەکرد لەھەر کارێکی بێزراو ناشەرعی کەدەیانکرد بێگومان زۆر بەدبوو ئەوەی کەئەیانکرد');
INSERT INTO "bamoki" VALUES(5,80,'5:80','ئەبینی (ئەی موحەممەد صلى الله عليه وسلم) زۆر بەیان دۆستایەتی ئەکەن لەگەڵ ئەوانەی بێ باوەڕ بوون بێگومان زۆر بەد و خراپە ئەوەی کەپێشیان خست بۆخۆیان (وکردویانە) کە ھۆی خەشم و قینی خوا بوو لێیان وە ئەوانە لەسزای دۆزەخدا بێ بڕانەوە دەمێننەوە');
INSERT INTO "bamoki" VALUES(5,81,'5:81','وە ئەگەر ئەوانە بڕوایان بھێنایە بەخوا وە بە پێغەمبەرو بەو بەرنامەیەی کەنێراوەتە خوارەوە بۆ سەری ئەو بێ بڕوایانەیان نەدەکرد بەدۆست و خوشەویستی خۆیان بەڵام زۆر بەیان لەئاین دەرچوون و یاخین لەخوا');
INSERT INTO "bamoki" VALUES(5,82,'5:82','سوێند بێت دەبینیت (ئەی موحەممەد صلى الله عليه وسلم) خراپترینی خەڵکی بۆ دوژمنایەتی لەگەڵ بڕواداراندا جولەکە و ھاوبەشدانەرانن وە سوێند بێت دەبینیت نزیکترینیان لەخۆشەویستی بۆ بڕواداران ئەوانەن کەدەیانووت بێگومان ئێمە گاوریین ئەوەش لەبەر ئەوەیە بێگومان لە ناویاندا قەشەو گۆشەگیرانێک ھەن کە بەڕاستی ئەوان خۆیان بەزل و گەورە دانانێن');
INSERT INTO "bamoki" VALUES(5,83,'5:83','ھەر کاتێک گوێیان لێ بووایە ئەو قورئانەی کە نێراوەتە خوارەوە بۆ (پێغەمبەر صلى الله عليه وسلم) دەبینی چاوەکانیان لە فرمێسک (پڕدەبێت و) دەڕژێت لەبەر ئەو ڕاستیەی کە زانیویانە (و بۆیان دەرکەوتوە) ئەڵێن ئەی پەروەردگارمان ئەوا ئێمە باوەڕمان ھێنا (بەو قورئانەی ناردووتە خوارەوە بۆ موحەممەد) دەتۆش لەگەڵ شایەتیدەران ناومان بنوسە و (لەگەڵ ئەواندا بمانژمێرە)');
INSERT INTO "bamoki" VALUES(5,84,'5:84','بۆچی دەبێت ئێمە باوەڕ نەھێنین بە خوا و بەوەی کە بۆمان ھاتووە لە حەق و ڕاستی لە کاتێکدا ئومێدمان وابێت پەروەردگارمان بمانخاتە ڕیزی پیاو چاکانەوە');
INSERT INTO "bamoki" VALUES(5,85,'5:85','جا خوا پاداشتیان ئەداتەوە بەھۆی ئەوەوە کە ووتیان بەچەند باخێک کە ڕووبار بە ژێریاندا دەڕوات دەمێننەوە تیایدا بەھەمیشەیی وە ئەوەیە پاداشتی چاکە کاران');
INSERT INTO "bamoki" VALUES(5,86,'5:86','وە ئەوانەی بڕوایان نەھێنا وە ئایەتەکانی ئێمەیان بەدرۆ دائەنا ئەوانە ھاوڕێی ئاگری دۆزەخن');
INSERT INTO "bamoki" VALUES(5,87,'5:87','ئەی ئەوانەی بڕواتان ھێناوە ئەو شتە پاک و خۆشانەی کە خوا حەڵاڵی کردوە بۆتان یاساغی مەکەن لە خۆتان و سنوور مەشکێنن چونکە بێگومان خوا سنوور شکێنەرانی خۆش ناوێت');
INSERT INTO "bamoki" VALUES(5,88,'5:88','بخۆن لەو ڕزق و رۆزیانەی کە خوا پێی بەخشیوون کە حەڵاڵ و پاکن خۆتان بپارێزن و بترسن لەو خوایەی کە ئێوە باوەڕتان پێیەتی');
INSERT INTO "bamoki" VALUES(5,89,'5:89','خوا لێتان ناگرێت ئەو سوێندانەتان کە نیازی دڵی لەگەڵدا نیە بەڵام لێتان ئەگرێت (بە گوناھبارتان دادەنێت) بە (شکاندنی) سوێندێک کە بەنیازی دڵ داتان بەستبێت جا کەفارەتی (سڕینەوەی شکاندنی) ئەو سوێندانە خۆراک پێدانی (دە) ھەژارە لە خۆراکێک مامناوەندی کە بە منداڵ وخێزانی خۆتانی دەدەن یا پۆشاکی (دە) ھەژار یا ئازاد کردنی بەندەیەک جائەوەی دەستی نەکەوت ھیچ کام لەمانە ئەوە سێ ڕۆژ بە ڕۆژوو بێت ئەوەی (باسکرا) کەفارەتی سوێندەکانتانە کاتێک کە سوێندتان خوارد (و سوێندەکەتان شکاند) وە سوێندەکانتان بپارێزن بەو جۆرە خوا ئایەتەکانی خۆی بە ڕوونی باسی ئەکات بۆتان بۆ ئەوەی سوپاسی بکەن');
INSERT INTO "bamoki" VALUES(5,90,'5:90','ئەی ئەوانەی بڕواتان ھێناوە بێگومان ئارەق و قومار و ئەو بەردانەی کە کراون بە بت و ئەو پارچە دارانەی (کە بەکارئەھێنرێن بۆ دیاریکردنی بەش و چارەنووس) ئەوانە پیسن و لەکردەوەی شەیتانن کەواتە دوورکەونەوە لێی تاڕزگاربن');
INSERT INTO "bamoki" VALUES(5,91,'5:91','بێگومان شەیتان ھەر ئەوەی ئەوێت کە دوژمنایەتی و کینەو ڕق بخاتە ناوتانەوە بەھۆی ئارەق و قومارەوە وە رێگریتان بکات لەیادی خودا و لەنوێژ کردن دەی ئایا ئێوە وازدەھێنن (لەو خراپانە)؟');
INSERT INTO "bamoki" VALUES(5,92,'5:92','وە فەرمانبەر وگوێڕایەڵی خوابن وە فەرمانبەرو گوێڕایەڵی نێرراوە کەی بن و بترسن (ئاگاداربن) ئەمجا ئەگەر ئێوە ڕوو وەرگێڕن و فەرمانبەری نەکەن ئەوا بزانن بێگومان ئەوەی کە لەسەر نێردراوەکەی ئێمە بێت تەنھا ڕاگەیاندنی ڕوون و ئاشکرای ئایینەکەیە');
INSERT INTO "bamoki" VALUES(5,93,'5:93','نەبووە و نیە لەسەر ئەوانەی کەبڕوایان ھێناوە, وە کردەوە چاکەکانیان کردووە ھیچ گوناھێک لەوەی خواردویانە (لە ئارەق و پارەی قومار پێش حەرام کردنیان) ئەگەر خۆیان پاراستبێت (لەو چەرخەدا لەوەی کە حەرام بووە لە سەریان) وە باوەڕداربووبن و کردەوە چاکەکانیان کردبێت پاشان خۆیان پاراستبێت (لەوەی کە حەرام کرابێت) و بڕواشیان ھەبوبێت (بەوەی کە ھاتبێت لە لایەن خواوە) پاشان بەردەوام بووبن لەسەر خۆ پارێزی و چاکەکانیشیان بەچاکی کردبێت وە خوا ئەو چاکەکارانەی خۆش دەوێت');
INSERT INTO "bamoki" VALUES(5,94,'5:94','ئەی ئەوانەی بڕواتان ھێناوە سوێند بە خوا، خوا تاقیتان دەکاتەوه بە ھەندێک لە نێچیر کە بەدەست دەیانگرن و بە ڕمەکانتان ڕاویان دەکەن تا خوا دەری بخات کێ لێی دەترسێت بە پەنھانی ئەمجا ھەرکەس دەست درێژی بکات دوای ئەو (ھەواڵ پێدانە) ئەوە بۆی ھەیە سزایەکی بەئازار');
INSERT INTO "bamoki" VALUES(5,95,'5:95','ئەی ئەوانەی بڕواتان ھێناوە نێچیر مەکوژن لە کاتێکدا ئێوە لە ئیحرامدان وە ئەوەتان بەدەستی ئەنقەست بیکوژێت ئەوە لەسەریەتی سەر بڕینی وێنەی ئەوەی کە کوشتویەتی لە ئاژاڵ و ماڵات ئەو بڕیارە بدەن (نێچیرەکە لە بەرانبەر چ ئاژەڵێکدایە) دوو پیاوی موسڵمانی چاک لەخۆتان بە شێوەی قوربانی بگاتە کەعبە (و لەوێ بیکات بەقوربانی) یان بە ئەندازەی نرخی قوربانیەکە خۆراک بدات بە چەند ھەژارێک یان بەقەدەر ئەو خۆراکدانە ڕۆژوو بگرێت بۆ ئەوەی بچێژێت ئۆباڵی کارەکەی خوا خۆش بووە لەوەی کە تێپەڕی و بەسەرچوو بەڵام ھەرکەسێک بگەرێتەوە (بۆ سەر ئەو کردەوەیە) ئەوا خوا تۆڵەی بە تینی لێ دەسێنێت وەخوا زاڵە و خاوەنی تۆڵەی بەتینە');
INSERT INTO "bamoki" VALUES(5,96,'5:96','ڕاوی (نێچیر) و خواردەمەنی دەریایی حەڵاڵکراوە بۆتان تا ئێوە و گەشتیارەکان سوودی لێ وەربگرن وڕاو (نێچیری) کێوی و ووشکانی یاساغ کراوە لێـتان تا لە ئیحرامدا بن لە (بێفەرمانی) ئەو خوایە بترسن کە ھەر بۆ لای ئەو کۆدەکرێنەوە (لە ڕۆژی دواییدا)');
INSERT INTO "bamoki" VALUES(5,97,'5:97','خوا کەعبە کە (بیت الحرام) ـە دایناوە تاوەکو (جێگای) ڕاوەستانی کاروباری دین ودونیای خەڵکی بێت وە ھەروەھا مانگە یاساغەکان (کە جەنگ تێیاندا حەرامە) و قوربانیە دیاری نەکراوەکان و قوربانیە دیاری کراوەکانیشی داناوە ئەوەش بۆ ئەوەی کە بزانن بەڕاستی خوا ئەزانێت و بە ئاگایە کە چی وا لە ئاسمانەکان و زەویدا وە بێگومان خوا بە ھەموو شتێک زانایە');
INSERT INTO "bamoki" VALUES(5,98,'5:98','باش بزانن کە بەڕاستی خوا تۆڵەی زۆر بە تینە (بۆ یاخیان) وە بەڕاستی خوا لێبووردە و میھرەبانە (بۆ تۆ بەکاران)');
INSERT INTO "bamoki" VALUES(5,99,'5:99','نێردراوەکەی خوا (موحەممەد) ھیچی لەسەر نیە بێجگە لە ڕاگەیاندن نەبێت وە خوا دەزانێت بەوەی دەریدەخەن بەوەش دەیشارنەوە');
INSERT INTO "bamoki" VALUES(5,100,'5:100','(ئەی موحەممەد صلى الله عليه وسلم) بڵێ پیس و پاک وەک یەک نین ھەرچەند سەرسامت بکات زۆری پیسەکە کەواتە لەخوا بترسن ئەی خاوەنانی ژیری و ھۆشمەندان تا ڕزگار ببن');
INSERT INTO "bamoki" VALUES(5,101,'5:101','ئەی ئەوانەی بڕواتان ھێناوە پرسیار مەکەن لە شتانێک ئەگەر ئاشکرا بکرێن بۆتان ناخۆش و خراپ دەبێت بۆتان وە ئەگەر پرسیار بکەن دەربارەیان کاتێک قورئان دەنێردرێتە خوارەوە، بۆتان ئاشکرا دەکرێن خوا خۆش بووە لەو پرسیارانەی لەوە و پێش وە خوا زۆر لێبوردەی لەسەرخۆیە');
INSERT INTO "bamoki" VALUES(5,102,'5:102','بێگومان ئەو (جۆرە) پرسیارانەیان کرد کۆمەڵێك پێش ئێوە پاشان بێ بڕوا بوون بەھۆی ئەو پرسیارانەوە');
INSERT INTO "bamoki" VALUES(5,103,'5:103','خوا دای نەناوە و بڕیاری نەداوە (بۆ بتپەرست وھاوەڵدانەران کە حەرامی بکەن لەسەرخۆیان وجێی بھێڵن بۆ بتەکانیان وسوودیان لێ وەرنەگرن) لەبەحیرە: (ووشترێک بوو پێنج سکی بکردایەو پێنجەم نێر بوایە، ئەو کاتە گوێچکەیان لەت دەکرد و بەرەڵایان دەکرد) و سائیبە: (ووشترێک بوو کە دە تا دوانزە سکی بکردایە ھەموو مێینە بوایە) و وەێیلە: (ووشترێک بوو یەکەم و دووەم سکی مێینە بوایە) و حام: (ووشترێکی نێر بوو کە لە پشتی ئەو دە سک پەیدا ببوایە) بەڵام ئەوانەی بێ باوەڕبوون درۆ بە دەم خواوە ھەڵئەبەستن و زۆربەیان ژیر نین (نەزانن)');
INSERT INTO "bamoki" VALUES(5,104,'5:104','وەکاتێک کە بووترێت پێیان وەرن بۆ لای ئەو قورئانەی کە خوا ناردویەتیە خوارەوە و بۆ لای (پێغەمبەر صلى الله عليه وسلم) دەڵێن بەسمانە ئەو (ئاینەی) کەباوک و باپیرانمان لەسەری بوون ئایا (چاویان لێدەکەن) ئەگەر لە حاڵێکدا باو باپیرانیشیان ھیچ شتێکیان نەزانیبێ وگومڕابووبن');
INSERT INTO "bamoki" VALUES(5,105,'5:105','ئەی ئەوانەی بڕواتان ھێناوە ھۆشتان بەخۆتانەوە بێت زیانتان پێناگەیەنێت ئەوەی گومڕا بووە ئەگەر ئێوە لەسەر ڕێی ڕاست بن (رێگری کردن لەخراپە بەشێکە لە ڕێنمونی وەرگرتن) ھەر بۆلای خوایە گەڕانەوەی ھەمووتان بەگشتی ئەمجا ھەواڵتان پێ ئەدا بەھەر کردەوەیەک کە کردووتانە');
INSERT INTO "bamoki" VALUES(5,106,'5:106','ئەی ئەوانەی کە بڕواتان ھێناوە شایەتی بدەن لە نێوانتاندا کاتێک کەمردنی ھەرکامتان نزیک بوویەوە لەکاتی وەسیەت کردنتاندا دوو کەسی دادپەروەر لەخۆتان (موسوڵمان بن) یان دووکەسی تر جگەلە خۆتان (کافربن) ئەگەر ئێوە بەسەفەر ڕۆشتبوون بە ووڵاتدا و بەڵای مردنتان تووش ھات (دوو شایەتەکە) ڕاگرن لەپاش نوێژی (عەسر) دەبێ سوێند بخۆن بەخوا ئەگەر گومانتان بوو (لە ڕاستی و پاکیان) ئێمە (سوێند خواردنمان) بەخوا ناگۆڕینەوە بەھیچ نرخێک باشایەتی بۆدراوەکە خزم و نزیکیش بێت وە شایەتی (دان بۆ) خوا ناشارینەوە بێگومان (ئەگەر شایەتیەکەمان شاردەوە) کەواتە بەڕاستی لە گوناھباران دەبین');
INSERT INTO "bamoki" VALUES(5,107,'5:107','ئەمجا ئەگەر زانرا (پاش سوێند خواردنیان) بەوەی کە ئەو دوو شایەتە درۆیان کردوە و خۆیان تاوانبارکردووە ئەوە دووکەسی تر لە شوێنی ئەواندا ھەڵدەستن (بەسوێند خواردن) لەوانەی کەشیاو و نزیکتربن (لەمردووەکە) لەو دووانە ئەمجا ھەردووکیان سوێند بخۆن بە خوا کەبەڕاستی شایەتیەکەی ئێمە ڕاستترە لە شایەتیەکەی ئەو دووانەی پێشوو وە لەڕاستی لامان نەداوە (لەسوێند خواردنەکەماندا) بێگومان (ئەگەر لابدەین) ئەوکاتە ئێمە لەستەمکاران دەبین');
INSERT INTO "bamoki" VALUES(5,108,'5:108','ئەو (جۆرە شایەتیدانە) نزیکترە کە شایەتەکان ھەڵسن بە شایەتیەکەیان بەشێوە ڕاستەکەی یان بترسن لە ڕەدکردنەوەی سوێندەکان (بۆخزمی نزیکی مردووەکە) دوای سوێند خواردنی ئەمان (واتە بە درۆ بخرێنەوە) بترسن لە خوا و خۆتان بپارێزن و گوێڕایەڵی فەرمانی خوا بن خوا ڕێنمونی کۆمەڵی لەسنوور دەرچوو ناکات');
INSERT INTO "bamoki" VALUES(5,109,'5:109','لە ڕۆژێک (بترسن) خوا ھەموو پێغەمبەران کۆدەکاتەوە ئەوسا پێیان دەفەرموێت چ وەڵامێکتان درایەوە؟ دەڵێن ھیچ زانیارییەکمان نیە بێگومان تەنھا تۆ ئاگاداری ھەموو نھێنیەکانیت');
INSERT INTO "bamoki" VALUES(5,110,'5:110','(بیر بکەرەوە) کاتێک خوا بە عیسای کوڕی مەریەمی فەرموو بیربکەرەوە لەچاکە و بەھرەی من بەسەر خۆت و دایکتەوە کاتێک کە پشتیوانیم لێ کردیت بە گیانی ھەرە پیرۆز ( بە جوبرەیل) کاتێک لەبێشکەدا بوویت و بەگەورەییش قسەت لەگەڵ خەڵکیدا دەکرد وە کاتێکیش فێری کتێب (ونووسین) و حیکمەت و تەورات و ئینجیلم کردیت وەبیربکەرەوە کاتیێک بەفەرمانی من لەقوڕ شتێکت لە شێوەی باڵندە دروست دەکرد دەست بەجێ بەفەرمانی من ئەبوو بە باڵندە وە بەفەرمانی من کوێری زکماک و بەڵەکیت چاک دەکردەوە, وەکاتێک بەفەرمانی من مردووانت لە گۆڕ دەردەھێنا (بەزیندوو کراوەیی) وە بیربکەرەوە کاتێک بەرگری نەوەکانی ئیسرائیلم کرد لێت (بتکوژن) کاتێکیش موعجیزە ڕوونەکانت بۆھێنان جا ئەوانەیان کە بێ باوەڕبوون ووتیان بەڕاستی ئەمە ھیچ نیە جگەلە جادویەکی ئاشکرا نەبێت');
INSERT INTO "bamoki" VALUES(5,111,'5:111','وە (بیربکەرەوە) کاتێک خستمە دڵی ھاوەڵەتایبەتیەکانت کەبڕوا بھێنن بە من و بە پێغەمبەرەکەم (عیسی) ئەوانیش ووتیان: ئەوا بڕوامان ھێنا و تۆش شایەت بە کە بێگومان ئێمە موسڵمانین');
INSERT INTO "bamoki" VALUES(5,112,'5:112','کاتێک حەواریەکان ووتیان: ئەی عیسای کوڕی مەریەم ئایا پەروەردگاری تۆ دەتوانێت لەئاسمانەوە خوانێکمان بۆ بنێرێتە خوارەوە (عیسا) ووتی لەخوا بترسن ئەگەر ئێوە باوەڕدارن');
INSERT INTO "bamoki" VALUES(5,113,'5:113','(حەواریەکان) ووتیان دەمانەوێت لێی بخۆین ودڵمان ئارام ببێ و دڵنیابین (کەپێغەمبەریت) وەبزانین کە بێگومان ڕاستت لەگەڵ کردوین (لەوەی کەپێت ڕاگەیاندین) ئێمەش ببین بەشایەت لەسەری (بۆئەوانەی ئامادە نین)');
INSERT INTO "bamoki" VALUES(5,114,'5:114','عیسای کوڕی مەریەم فەرمووی ئەی خوای پەروەردگارمان لەئاسمانەوە خوانێکمان بۆ بنێرە خوارەوە تا ببێت بە جەژن بۆ ئێستامان و نەوەکانی داھاتووشمان وەموعجیزە و بەڵگەیەک بێت لەلایەن تۆوە, وە ڕزق و ڕۆزیمان پێ ببەخشە چونکە تۆ باشترینی ھەموو ڕۆزی دەرانی');
INSERT INTO "bamoki" VALUES(5,115,'5:115','خوای گەورە فەرمووی: بە ڕاستی من ئەو خوانە دەنێرمە خوارەوە بۆتان ئەمجا ھەر کەس لەئێوە بێ بڕوا و نا سوپاس بێت لە پاش ئەوە ئەوە بێگومان سزایەکی ئەدەم کە سزای ھیچ کەسی پێ نادەم لەجیھانیان');
INSERT INTO "bamoki" VALUES(5,116,'5:116','وە (بیر بکەرەوە) کاتێک خوا دەفەرمووێت: (لەڕۆژی دواییدا) ئەی عیسای کوڕی مەریەم ئایا تۆ ووتووتە بە خەڵکی کەمن و دایکم بەدوو پەرستراو دابنێن (وبمانپەرستن) لەجیاتی خوا؟ (عیسای) ووتی: پاک و بێگەردی بۆ تۆ ئەی خوایە نابێت و ناگونجێت بۆ من شتێک بڵێم کە بە ھیچ شێوەیەک مافی من نیە ئەگەر من ئەو قسەیەم کردبێت ئەوە بەڕاستی تۆ ئاگات لێیە چونکە تۆ ئەوەی لە نەفسم دایە دەیزانی بەڵام من نازانم و بێ ئاگام لەوەی کە لەنەفسی تۆدایە بەڕاستی تەنھا تۆ زانای تەواوی بە ھەموو نھێنیەک');
INSERT INTO "bamoki" VALUES(5,117,'5:117','ھیچم پێ نەوتوون بێجگە لەوە نەبێت کە تۆ فەرمانت پێکردووم کە بەندایەتی خوا بکەن کەپەروەردگاری من و ئێوەیشە و من شایەت و چاودێر بووم بە سەریانەوە تا لە ناویاندا بووم ئەمجا کاتێک تۆ منت بردەوە (و بەرزت کردمەوە بۆ ئاسمان) ھەر خۆت چاودێر بووی بەسەریانەوە, وەخۆت ئاگادار و شایەتی بەسەر ھەموو شتێکەوە');
INSERT INTO "bamoki" VALUES(5,118,'5:118','ئەگەر سزایان بدەیت، ئەوە بێگومان بەندەی خۆتن وەئەگەر لێشیان خۆش ببیت ئەوە بێگومان تەنھا خۆتی بەدەستەڵات و کاردروست');
INSERT INTO "bamoki" VALUES(5,119,'5:119','خوای گەورە فەرمووی: ئەمە رۆژێکە ڕاستبێژان ڕاستیەکەیان کەڵکیان پێدەگەیەنێت بەھەشتانێکیان بۆ ھەیە کە ڕووبارەکان بەژێریاندا دەڕۆن ئەمێننەوە تیایدا بە ھەمیشەیی خوا ڕازی بووە لێیان و ئەوانیش لە خوا رازی بوون ئەوەیە سەرکەوتنی زۆر گەورە');
INSERT INTO "bamoki" VALUES(5,120,'5:120','ھەر بۆ خوایە خاوەندارێتی ئاسمانەکان و زەوی و ھەرچیشیان تێدایە وە تەنھا ئەو بەسەر ھەموو شتێکدا بە توانا و دەسەڵاتدارە');
INSERT INTO "bamoki" VALUES(6,1,'6:1','ھەموو سوپاس و ستایشێک ھەربۆ ئەو خوایەی ئاسمانەکان و زەوی دروستکردووە, وە تاریکیەکان و ڕووناکی داناوە ئەمجا ئەوانەی بێ باوەڕ بوون ھاوتا و ھاوبەش بۆ پەروەردگاریان بڕیار دەدەن');
INSERT INTO "bamoki" VALUES(6,2,'6:2','خوا ئەو زاتەیە کەئێوەی لەقوڕ دروست کردووە پاشان کاتێکی دیاری کردووە (بۆ مردن) وە کاتێکی دیاری کراویش لای خوای گەورە دانراوە (بۆ قیامەت) کەچی دوا ئەوەش ئێوە بەگومانن');
INSERT INTO "bamoki" VALUES(6,3,'6:3','وەتەنھا ئەو خوایە (باڵادەست و پەرستراوە) لەئاسمانەکان و لەزەویدا نھێنی و ئاشکراتان دەزانێ و بە ھەموو کردەوەیەکیش کەدەیکەن ئاگادارە');
INSERT INTO "bamoki" VALUES(6,4,'6:4','وە ھەر بەڵگەو نیشانەیەک لەبەڵگە ونیشانەکانی پەروەردگاریان بۆ بێت ئەوان ڕووی لێ وەردەگێڕن');
INSERT INTO "bamoki" VALUES(6,5,'6:5','بەڕاستی کاتێک قـورئانیان بۆ ھات بە درۆیان دانـا جا لەمەو پاش بۆیان دێت ھەواڵی ئەو شتانەی کەئەوان گاڵتەیان پێدەکرد');
INSERT INTO "bamoki" VALUES(6,6,'6:6','ئایا نەیان دیوە (نەیان زانیوە) لەپێش ئەوانەوە چەند گەل و نەوەمان لەناو بردووە دەسەڵاتمان پێدا بوون لەزەویدا بەشێوەیەک کە بە ئێوەمان نەدابوو و بارانی پەیتاپەیتامان بەسەردا باراندن وە جۆگەو ڕووبارەکانمان وا لێ کرد بوو کە بەبن باخ و کۆشکەکانیاندا دەڕۆیشت لەناو مان بردن بە ھۆی گوناھەکانیانەوە, وە لە دوای ئەوان دروستمان کرد گەل و نەوەیەکی تر');
INSERT INTO "bamoki" VALUES(6,7,'6:7','وە ئەگەر نامەیەکی نوسراومان بناردایە تەخوارەوە بۆت لەناو کاغەزێکدا بوایە و دەستیشیان لێ بدایە بێـگومان ئەوانەی بێ باوەڕ بوون دەیان ووت ئەمە تەنھا جادویەکی ئاشکرایە');
INSERT INTO "bamoki" VALUES(6,8,'6:8','وە دەیانووت: بۆچی فریشتەیەک نەنێنرراوەتە خوارەوە بۆسەری (بیبینین) ئەگەر فریشتەیەکمان بناردایەتە خوارەوە بێگومان کارتەواو دەبوو (لەناو بردنیان جێ بەجێ دەکرا) پاشان مۆڵەت نەئەدران');
INSERT INTO "bamoki" VALUES(6,9,'6:9','ئەگەر ئەو (پێغەمبەرە) مان بکردایە بەفریشتەیەک بێگومان دەمانگۆڕا بە وێنەی پیاوێک (تابیبینن وقسەی لەگەڵ بکەن) و بێگومان لێمان دەشێواندن وەک چۆن خۆیان لە خۆیان دەشێواند');
INSERT INTO "bamoki" VALUES(6,10,'6:10','سوێند بێت بێگومان گاڵتە کراوە بە پێغەمبەرانی پێش تۆیش سەرئەنجام بۆ ئەوانەیان گاڵتەیان دەکرد دابەزی و دەوریدان تۆڵەی ئەوەی گاڵتەیان پێدەکرد (لەسزای سەخت)');
INSERT INTO "bamoki" VALUES(6,11,'6:11','(ئەی موحەممەد صلى الله عليه وسلم) بڵێ بڕۆن و بگەڕێن بە زەویدا پاشان سەرنج بدەن سەر ئەنجامی ئەوانەی بەڵگەکانی خوایان بەدرۆ دادەنا چۆن بووە');
INSERT INTO "bamoki" VALUES(6,12,'6:12','(ئەی موحەممەد صلى الله عليه وسلم) بڵێ ئایا ھی کێیە ئەوەی لە ئاسمانەکان و زەویدا یە بڵێ تەنھا بۆ خوایە کە بەزەیی و میھرەبانی پێویستی کردوە لەسەرخۆی سوێند بەخوا بێگومان کۆتان دەکاتەوە لە ڕۆژی دواییدا کە ھیچ گومان و دوودڵی تێدا نیە ئەوانەی خۆیان دۆڕاندووە ئەوە ئەوانن کە بڕوا ناھێنن');
INSERT INTO "bamoki" VALUES(6,13,'6:13','وە ھەر بۆ خوایە ئەوەی ئارامی گرتووە لەشەو و ڕۆژدا وە ھەر ئەو بیسەر و زانایە');
INSERT INTO "bamoki" VALUES(6,14,'6:14','(ئەی موحەممەد صلى الله عليه وسلم) بڵێ ئایا؟ بێجگە لە خوا دۆست و پەرستراوێک تر بۆخۆم دانێم؟ کەئەو بەدیھێنەری ئاسمانەکان و زەویە ئەو خوایە ڕۆزی دەدات و ڕۆزی پێ‎ نادرێ بڵێ بەڕاستی من فەرمانم پێدراوە کە یەکەم کەس بم مل کەچ کەم (بۆ فەرمانی خوا) وە (فەرمانم پێدراوە لەلایەن خوداوە) ھەرگیز لەھاوبەش دانەران مەبە');
INSERT INTO "bamoki" VALUES(6,15,'6:15','پێیان بڵێ: بێگومان من دەترسم ئەگەر سەرپێچی لە فەرمانی پەروەردگارم بکەم لە سزای ڕۆژێکی گەورە');
INSERT INTO "bamoki" VALUES(6,16,'6:16','ھەرکەس ئەو سزایەی لێ لادرێت لەو ڕۆژەدا ئەوە بێگومان خوا بەزەیی پێیدا ھاتۆتەوە و ئەوەیە سەرکەوتنی ئاشکراو تەواو');
INSERT INTO "bamoki" VALUES(6,17,'6:17','وە ئەگەر خوا دووچاری بەڵاو زیانێکت بکات ئەوە جگە ئەو (خوا) کەس نیە بتوانێت لایبات وە ئەگەر خێر و خۆشیەکیشت پێ بگەیەنێت ئەوە خودا دەسەڵاتدارە بەسەر ھەموو شتێکدا');
INSERT INTO "bamoki" VALUES(6,18,'6:18','وە ھەر ئەوە دەست ڕۆیشتوو زاڵە بەسەر بەندەکانیدا ھەر ئەویش کار دروست و ئاگادارە');
INSERT INTO "bamoki" VALUES(6,19,'6:19','(ئەی موحەممەد صلى الله عليه وسلم) بڵێ: شایەتیدانی کێ لە ھەموو شایەتی دانێک گەورەترە (لەسەر پێغەمبەرێتی من) بڵێ: خوا گەورەترین شایەتە لە نێوان من و ئێوەدا وە ئەم قورئانە وەحی کراوە بۆ من بۆ ئەوەی ئێوەی پێ بترسێنم و ھەر کەسێکی کەش کە پێی دەگات ئایا بەڕاستی ئێوە شایەتی ئەدەن بە دڵنیاییەوە کە لەگەڵ خوادا چەند پەرستراوێکی تر ھەبێت؟! بڵێ: من شایەتی (وا ناڕەوا) نادەم بڵێ: بێگومان ھەر ئەو پەرستراوی تاک و تەنھایە وە بەڕاستی من بەریم لەوەی کە ئێوە دەیکەنە ھاوبەش بۆ خوا');
INSERT INTO "bamoki" VALUES(6,20,'6:20','ئەوانەی کە نامەمان پێ بەخشین (لەگاورو جولەکەکان) ئەم پێغەمبەرە دەناسن (کە پێغەمبەری خوایە بەھۆی باسکردنی نیشانەکانی لە تەورات و ئینجیلدا) وەک چۆن کوڕەکانیان ئەناسن ئەوانەی خۆیان دۆڕاندو خەسارۆمەند بوون ئەوە ئەوانن بڕوا ناھێنن');
INSERT INTO "bamoki" VALUES(6,21,'6:21','کێ ستەمکارترە لەو کەسەی درۆ ھەڵبەستێ بەدەم خواوە یان بێ باوەڕ بوو بێت بە ئایەتەکانی (بە بەڵگەکانی) خوا بەڕاستی ستەمکاران ڕزگاریان نابێت');
INSERT INTO "bamoki" VALUES(6,22,'6:22','وە ڕۆژێ ھەموویان کۆ دەکەینەوە (لەقیامەتدا) پاشان ئەڵێین بەوانەی ھاوەڵیان بۆ خوا دانابوو کوان ئەو ھاوەڵانەی کە بە خەیاڵی خۆتان بەھاوەڵی خواتان دانابوون');
INSERT INTO "bamoki" VALUES(6,23,'6:23','پاشان بەھانەیان ھیچ نەبوو بێجگە لەوەی کە ووتیان سوێند بە خوای پەروەردگارمان ئێمە بەھیچ جۆرێک ھاوبەشدانەر نەبووین بۆخوا');
INSERT INTO "bamoki" VALUES(6,24,'6:24','سەیرکە چۆن درۆ لە دژی خۆشیان دەکەن و لێیان وون بوون ئەو بتانەی خۆیان بە درۆ ھەڵیان بەستبوون');
INSERT INTO "bamoki" VALUES(6,25,'6:25','وە لەو (کافرانە) کەسانێک ھەیە کە گوێت بۆ دەگرن کەقورئان دەخوێنیت بەڵام ئێمە (بەھۆی تاوان گوناھو ھاوەڵدانانیانەوە) چەند پەردەیەکمان داوە بەسەر دڵەکانیاندا بۆ ئەوەی تێی نەگەن وە کەڕی و گوێ گرانیمان خستۆتە ناو گوێچکەیانەوە, وە ئەگەر ھەموو بەڵگە و نیشانەیەکی ڕوون ببینن بڕوای پێ ناھێنن تا ئەو ڕادەیە کاتێک بێن بۆ لات دەمە دەمێ و بەربەرەکانیت لەگەڵ دەکەن ئەوانەی بێ باوەڕن ئەڵێن ئەم قورئانە ھیچ نیە بێجگە لە ئەفسانەی پێشینان');
INSERT INTO "bamoki" VALUES(6,26,'6:26','ئەوان رێگری (خەڵک) ئەکەن لەو (قورئانە) و خۆشیان لێی دوور دەکەنەوە ھیچ کەسیش لە ناو نابەن خۆیان نەبێت و ھەستیش ناکەن (کە لەچی خەسارۆمەندیەکدان)');
INSERT INTO "bamoki" VALUES(6,27,'6:27','وە ئەگەر بیان بینیت کاتێک کە لەسەر قەراغی دۆزەخ وە ستێنراوون کە دەڵێن خۆزگە دەگەڕێنڕِاینەوە بۆ دونیا و بەڵگەکانی پەروەردگارمان بەدرۆ نەدەزانی وە لە بڕواداران ئەبووین');
INSERT INTO "bamoki" VALUES(6,28,'6:28','(نەخێر بەڕاستیان نیە) بەڵکو ئەوەی کە لەوەو پێش دەیانشاردەوە بۆیان دەرکەوتووە, وەئەگەر بگەڕێنرێنەوە (بۆ دونیا) بێگومان دووبارە دەگەڕێنەوە بۆ ئەو کارانەی کەلێیان قەدەغە کرابوو وەبەڕاستی ئەوانە درۆزنن');
INSERT INTO "bamoki" VALUES(6,29,'6:29','وە ووتیان بەڕاستی ھەر ئەم ژیانەی (دونیا) مانە وئێمە زیندوو ناکرێینەوە');
INSERT INTO "bamoki" VALUES(6,30,'6:30','وەئەگەر ئەوانە دەبینی ئەوکاتەی کەلە حزووری پەروەردگاریاندا وەستێنراوون (پەروەردگار) دەفەرموێت ئایا ئەمە ڕاست نیە؟ ئەڵێن: بەڵێ ڕاستە سوێند بە پەروەردگارمان دەفەرموێت دەی بچەژن ئەو سزایە بە ھۆی ئەوەی کەبڕواتان نەھێناوە (ئینکارتان دەکرد)');
INSERT INTO "bamoki" VALUES(6,31,'6:31','بەڕاستی ئەوانەی کە دیداری پەروەردگاریان بە درۆ دەزانی زەرەریان کرد ھەتا کاتێک لەناکاو ڕۆژی دواییان بەسەردا دێت ئەڵێن ئای داخ و پەشیمانی سەخت بۆ ئێمە چەند کەم تەرخەمیمان کردلە دونیادا و لەدەستمان چوو لەکاتێکدا تاوانەکانیان ھەڵگرتووە بەکۆڵیانەوەیە ئای چەند بەد و خراپە ئەو بارەگرانەی ھەڵیدەگرن');
INSERT INTO "bamoki" VALUES(6,32,'6:32','وەژیانی دونیا ھیچ نیە بێجگە گاڵتە و سەرقاڵیی نەبێت وە بێگومان ماڵی دوا ڕۆژ چاکترە بۆئەوانەی خۆیان دەپارێزن و لە خوا دەترسن دەی ئایا بۆ ژیرنابن');
INSERT INTO "bamoki" VALUES(6,33,'6:33','بەڕاستی ئێمە دەزانین کەبێگومان ئەوەی کەئەوان دەیڵێن غەمبارت دەکات بەڵام بێگومان ئەوان تۆ بەدرۆزن دانانێن بەڵکو ئەو ستەمکارانە ئایەتەکانی خوا﴿﴾ ئینکار دەکەن (وبەدرۆی دەزانن)');
INSERT INTO "bamoki" VALUES(6,34,'6:34','سوێند بە خوا بێگومان پێغەمبەرانی پێش تۆش (ئەی موحەممەد صلى الله عليه وسلم) باوەڕیان پێ نەکراوە بەڵام ئارامیان گرت لەسەر ئەو باوەڕ پێ نەکرانەیان وە ئەزیەت و ئازار دران تا یارمەتی و سەرخستنی ئێمەیان بۆ ھات وەفەرمانەکانی (بەڵێنەکانی) خوا بەھیچ کەسێک ناگۆڕدرێت سوێند بێت بێگومان ھەواڵی ھەندێ لەو پێغەمبەرانەت بۆ ھاتووە');
INSERT INTO "bamoki" VALUES(6,35,'6:35','خۆ ھەرچەند ڕوو وەرگێڕانی ئەوانە سەخت و گرانە لەسەرت جا ئەگەر توانیت بە ناخی زەویدا تونێلێک لێ دەیت یان پەیژەیەک بۆ ئاسمان دانێیت بەھۆیەوە بەڵگەیەکیان بۆ بھێنیت وە ئەگەر خوا بیویستایە ئەوانەی کۆدەکردەوە لەسەر رێنمونی و بڕوا ھێنان کەواتە ھەرگیز لەوانە مەبە کە نەزانن');
INSERT INTO "bamoki" VALUES(6,36,'6:36','بەڕاستی تەنھا ئەوانە وەڵامی تۆ ئەدەنەوە کە (بەدڵ) ئەبیسن مردووەکانیش (ئەوانەی باوەڕ ناھێنن) خوا زیندوویان دەکاتەوە پاشان ھەر بۆلای خۆی دەگەڕێنەوە');
INSERT INTO "bamoki" VALUES(6,37,'6:37','ووتیان بۆچی لەلایەن پەروەردگاریەوە موعجیزەیەکی بۆ نەھاتۆتە خوارەوە بڵێ بێگومان خوا بە توانایە کە بەڵگە و نیشانەیەک بھێنێتە خوارەوە بەڵام زۆربەیان نازانن');
INSERT INTO "bamoki" VALUES(6,38,'6:38','ھیچ گەڕۆکێکی گیانلەبەر نیە لەزەویدا وە ھیچ باڵندەیەک نیە کە دەفڕێ بە ھەردوو باڵەکەی مەگەر ئەوانیش وەک ئێوە ئومەتانێکن ھیچ شتێکمان فەرامۆش نەکردووە لەم کتێبەدا (لوح المحفوڤ یا قورئاندا) پاشان ئەوانە (لە ڕۆژی دواییدا) لای پەروەردگاریان کۆدەکرێنەوە');
INSERT INTO "bamoki" VALUES(6,39,'6:39','وە ئەوانەی بێ باوەڕ بوون بەبەڵگەکانی ئێمە کەڕ و لاڵن لەناو تاریکستاندان ھەرکەس خوا بیەوێ گومڕای دەکات ھەرکەسیش خوا بیەوێت دەیخاتە سەر رێگای ڕاست');
INSERT INTO "bamoki" VALUES(6,40,'6:40','(ئەی موحەممەد صلى الله عليه وسلم) بڵێ ھەواڵم پێ بدەن ئەگەر سزای خواتان بۆ بێت یان ڕۆژی دوایی یەخەتان بگرێت ئایا ھاوار دەکەنە ھیچ شتێک بێجگە لە خوا ئەگەر ئێوە ڕاستگۆن');
INSERT INTO "bamoki" VALUES(6,41,'6:41','(نا) بەڵکو تەنھا بانگ و ھاوار لەخوا دەکەن ئەمجا ئەویش ئەگەر ویستی ئەوەی ھاواری (لە دەست) ئەکەن نایھێڵێ و ئەو شتانەتان لەبیر دەچێتەوە کە دەتانکردن بە ھاوبەش بۆ خوا');
INSERT INTO "bamoki" VALUES(6,42,'6:42','سوێند بێت بێگومان ئێمە پێغەمبەرانمان نارد بۆ گەلانی پێش تۆ (ئەی موحەممەد صلى الله عليه وسلم) وەتووشی نەبوونی و ھەژاری وەنەخۆشی و ترس و ناڕەحەتیمان کردن بۆ ئەوەی بگەڕێنەوە و پەشیمانی دەرببڕن (لاوازی وکزی بنوێنن)');
INSERT INTO "bamoki" VALUES(6,43,'6:43','دەی بۆ نەپاڕانەوە کاتێک سزای ئێمەیان بۆھات؟ بەڵام دڵیان ڕەق بووە وەشەیتان ئەو کردەوە خراپانەی بۆ ڕازاندبوونەوە کەدەیانکرد');
INSERT INTO "bamoki" VALUES(6,44,'6:44','جا کاتێک لەبیریان چویەوە ئەوەی کە پێی ئامۆژگاری کران دەرگای ھەموو شتێکمان لەسەر کردنەوە تاکاتێک دڵخۆش بوون بەوەی پێیان درابوو لە ناکاودا تۆڵەمان لێ سەندن ئیتر ئەوانیش نائومێد و سەرگەردان بوون');
INSERT INTO "bamoki" VALUES(6,45,'6:45','ئەمجا ئەو گەلەی کە ستەمیان کرد دوا بڕاو کران و ھەموو سوپاس و ستایشێ ھەر بۆ خوای پەروەردگاری جیھانیانە');
INSERT INTO "bamoki" VALUES(6,46,'6:46','(ئەی موحەممەد صلى الله عليه وسلم) بڵێ پێم بڵێن ئەگەر خوا ھەستی بیستن و بینینتان لێ بسەنێتەوە مۆریش بنێت بەسەر دڵەکانتاندا چ پەرستراوێک ھەیە بێجگە خوا بۆتان بھێنێتەوە تەماشابکە چۆن بەڵگەکان دەگۆڕین و چەند بارەیان دەکەینەوە پاشان ئەوان پشتی تێدەکەن و ڕوو وەردەگێڕن');
INSERT INTO "bamoki" VALUES(6,47,'6:47','ئەی (موحەممەد صلى الله عليه وسلم) بڵێ پێم بڵێن ئەگەر سزای خوا لە ناکاو دا یا بە ئاشکرا بێت بۆتان ئایا لەناو دەبرێت (بە سزای خوایی) بێجگە لە کۆمەڵی ستەمکاران نەبێت؟');
INSERT INTO "bamoki" VALUES(6,48,'6:48','وە نێرراوەکانمان نەناردووە مەگەر بە موژدەدەر و ترسێنەرنەبێت جا ھەرکەس بڕوای ھێناو کردەوەی چاکی کرد ئەوە ھیچ مەترسی یەکیان لەسەر نابێ و غەمباریش نابن');
INSERT INTO "bamoki" VALUES(6,49,'6:49','ئەوانەیش بێ بڕوا بوون بە بەڵگەکانی ئێمە بە ھۆی دەرچوونیان لە سنووری(فەرمانی خوا) سزایان لێ دەکەوێَت و دەیانگرێت');
INSERT INTO "bamoki" VALUES(6,50,'6:50','(ئەی موحەممەد صلى الله عليه وسلم) بڵێ من پێتان ناڵێم خەزێنەکانی خوا لای منە وە شتی نھێنیش نازانم وە پێتان ناڵێم بێگومان من فریشتەم شوێنی ھیچ ناکەوم ئەوە نەبێت کە بۆم نیگا کراوە بڵێ ئایا کوێر و بینا یەکسانن؟ دەی ئایا بۆ بیرناکەنەوە؟');
INSERT INTO "bamoki" VALUES(6,51,'6:51','بەم قورئانە بترسێنە ئەوانەی دەترسن (لەوەی کە) کۆبکرێنەوە لای پەروەردگاریان لە کاتێکدا بێجگە لە خوا ھیچ یارمەتیدەر و تکا کارێکیان نیە بەڵکو خۆیان بپارێزن ولەخوا بترسن');
INSERT INTO "bamoki" VALUES(6,52,'6:52','وە ئەوانە دەرنەکەیت کە پەروەردگاریان دەپەرستن (وداوای لێدەکەن) بەبەیانیان و ئێواراندا (ڕەزامەندی) خوایان دەوێت و لێپرسینەوەی ھیچ شتێکیان لەسەر تۆ نیە وە لێپرسینەوەی ھیچ شتێکی تۆش لەسەر ئەوان نیە جا ئەگەر دەریانکەیت (دور یان خەیتەوە) ئەوە لەستەمکاران دەبیت');
INSERT INTO "bamoki" VALUES(6,53,'6:53','وە بەوجۆرە ھەندێکیانمان بەھەندێکی تریان تاقی کردەوە بۆ ئەوەی بڵێن ئایا ئەوانە خوا رێزو بەھرەی پێ بە خشیوون لە ناو ئێمەدا؟ ئایا خوا لە ھەموو کەس زاناتر نیە بەوانەی شوکرانە بژێرن؟');
INSERT INTO "bamoki" VALUES(6,54,'6:54','وەکاتێک ئەوانەی کە بڕوایان ھەیە بە ئایەتەکانی ئێمە ھاتن بۆلات ئەوە پێیان بڵێ سڵاوتان لێ بێت پەروەردگارتان بەزەیی و میھرەبانی لەسەر خۆی پێویست کردووە بێگومان ھەر کەس لە ئێوە کارێکی خراپ بکات بەنەزانی پاشان بگەڕێتەوەو تەوبە بکات وخۆی چاک بکات ئەوە بەڕاستی خوا لێبوردە و بەبەزەییە');
INSERT INTO "bamoki" VALUES(6,55,'6:55','ئا بەو جۆرە ئایەتەکان ڕوون دەکەینەوە بۆ ئەوەی ڕێگای تاوانباران ڕوون بێتەوە');
INSERT INTO "bamoki" VALUES(6,56,'6:56','بڵێ: بێگومان من قەدەغەم لێکراوە لەوەی کەبەندایەتی بکەم بۆ ئەوانەی ئێوە ھاواریان لێدەکەن و دەیان پەرستن بێجگە لە خوا بڵێ من شوێنی ئارەزووەکانی ئێوە ناکەوم ئەگەر وا بکەم بێگومان من گومڕا دەبم وەلە ڕیزی ئەو کەسانەدا نابم کە ڕێنمونیان وەرگرتووە');
INSERT INTO "bamoki" VALUES(6,57,'6:57','بڵێ بێگومان من لەسەر بەڵگەیەکی ئاشکرام کە لەلایەن پەروەردگارمەوەیە کەچی ئێوە بڕواتان پێ نەھێنا ئەو (سزای) ئێوە پەلەی ھاتنی دەکەن بە دەست من نیە فەرمان ڕەوایی تەنھا بۆ خوایە حەق و ڕاستی باسی دەکات وە ئەو چاکترینی بڕیاردەر و جیاکەرەوەیە');
INSERT INTO "bamoki" VALUES(6,58,'6:58','بڵێ ئەگەر لام بوایە ئەوەی ئێوەپە لە دەکەن لە ھاتنی ئەوا کاری نێوان من و ئێوە کۆتایی پێ دەھێنرا وە خوا چاک ئاگادارە بە ستەمکاران');
INSERT INTO "bamoki" VALUES(6,59,'6:59','وە کلیلی ھەموو نھێنیەکان ھەرلای خوایە کەس نایانزانێت جگە لە (خوا) وە (خوا) دەزانێ چی لە ووشکانی و دەریادا ھەیە وە ھەر گەڵایەک بکەوێتە خوارەوە خوا پێی دەزانێت و ھەر دانەوێڵەیەک لە تاریکیەکانی زەویدا بێت و ھەر تەڕو ووشکێک ھەبێت ھەر ھەموویان لە کتێبی ئاشکرا لە (لوح المحفوڤ) دان');
INSERT INTO "bamoki" VALUES(6,60,'6:60','وە ھەر ئەو خوایەیە کە دەتان خەوێنێت لەشەودا (لێرە خەو بەمانای مردن ھاتووە) وە ئەشزانێت (لە ڕۆژ دا) چ کارێکتان ئەنجامداوە پاشان لە ڕۆژدا لە خەوبێدارتان دەکاتەوە بۆ ئەوەی تەواو ببێت ماوەی دیاریکراوی (ھەمووکەسێک) ئەوسا ھەر بۆلای ئەوە گەڕانەوەتان پاشان ھەواڵتان پێدەدات بەھەر کردەوەیەک کە کردوتانە');
INSERT INTO "bamoki" VALUES(6,61,'6:61','ئەو (خوا) زاڵ و دەسە ڵاتدارە بەسەر بەندەکانیدا وە کۆمەڵە فریشتەیەکتان بۆ دەنێرێت (کە چاودێر و پارێزەر و نوسەری کردەوەکانتانن) تاکاتی مردنی یەکێکتان دێت نێرراوەکانمان گیانی دەکێشن کەھیچ سستی و کەمتەر خەمیەک ناکەن (لەکارەکانیاندا)');
INSERT INTO "bamoki" VALUES(6,62,'6:62','پاشان دەگەڕێنرێنەوە بۆلای خوا کەسەروەری ڕاستەقینەیانە ئاگاداربن فەرمان ڕەوایی و بڕیار ھەر بۆئەوە ھەر خوایشە لێپرسینەوەی خێراترە لە ھەموو کەس');
INSERT INTO "bamoki" VALUES(6,63,'6:63','(ئەی موحەممەد صلى الله عليه وسلم) بڵێ: ئایا کێ ئێوە ڕزگار دەکات لەتاریکیەکانی ووشکانی و دەریا کە ھاوار و نزای لێدەکەن بە ئاشکراو نھێنی (ئەڵێن) سوێند بە خوا ئەگەر لەم مەترسیە رزگارمان بکات ئەوە بێگومان لە سوپاس کارانی خوا دەبین');
INSERT INTO "bamoki" VALUES(6,64,'6:64','بڵێ: ھەر خوا ئێوە رزگار دەکات لەو مەترسیە و لە ھەموو نارەحەتی و غەم و پەژارەیەک (لەگەڵ ئەوەشدا) پاشان ئێوە ھاوبەش بۆ خوا بڕیار دەدەن');
INSERT INTO "bamoki" VALUES(6,65,'6:65','بڵێ: ھەر ئەو خوایە بەتوانا و دەستەڵاتدارە کەسزایەکتان بۆ بنێرێت لە سەروتانەوە یان لە ژێرپێتانەوە یان سەرتان لێ بشێوێنێت و بتان کات بەچەند دەستە و تاقمێکەوە جا ھەندێکتان بە ھەندێکی ترتان سزاو ئازار بدات سەرنج بدە چۆن ئایەتەکان دەگۆڕین (لە جۆرێکەوە بۆ جۆرێکی تر) تا ئەوان تێبگەن و بیربکەنەوە');
INSERT INTO "bamoki" VALUES(6,66,'6:66','گەلەکەت باوەڕیان بەم قورئانە نەکرد کە ھەر ئەوە حەق و ڕاست بڵێ: من پارێزەر و بەرپرستان نیم');
INSERT INTO "bamoki" VALUES(6,67,'6:67','بۆ ھەموو ھەواڵێک کاتێکی دیاریکراو ھەیە (کە ڕاست و درۆی تیادا ڕوون دەبێتەوە) وە بەزووی دەزانن');
INSERT INTO "bamoki" VALUES(6,68,'6:68','ھەر کاتێک ئەوانەت دی قسەی ناشایستە دەکەن دەربارەی ئایەتەکانی ئێمە ئەوە ڕوو وەرگێڕە لێیان تا دەچنە سەر باسێکی تر وە ئەگەر شەیتان لە بیری بردیتەوە ئیتر لە پاش بیرکەوتنەوەت لەگەڵ کۆمەڵی ستەمکاران دامەنیشە');
INSERT INTO "bamoki" VALUES(6,69,'6:69','(ھەرچەندە) ئەوانەی خۆیان دەپارێزن ھیچ پرسیارو تاوانی ئەوانیان لەسەر نیە بەڵام بیرخستنەوەیە تا خۆیان بپارێزن');
INSERT INTO "bamoki" VALUES(6,70,'6:70','(ئەی موحەممەد صلى الله عليه وسلم) واز بێنە لەوانەی ئاینەکەیان کردووە بە گەمە و گاڵتەجاڕی و ژیانی دونیا ھەڵی خەڵەتاندون وە بە قورئان ئامۆژگاریان بکە بۆ ئەوەی ھیچ کەس تیا نەچێ بەھۆی ئەو (خراپانە) ی کردویەتی کە جگە لە خوا ھیچ دۆست و تکاکارێکی نیە ئەگەر ھەموو فیدیە و بریتیەک بدات لێی وەرناگیرێت ئەوانە کەسانێکن بەھۆی ئەو (خراپانە) ی کە کردوویانە تیاچوون بۆ ئەوانەیە خواردنەوەیەک لە ئاوی زۆر گەرم و بەکوڵ وسزای زۆر بە ئازاریش بە ھۆی ئەوەی کەبێ بڕوابوون');
INSERT INTO "bamoki" VALUES(6,71,'6:71','بڵێ ئایا بێجگە لەخوا ھاوار بکەین (و بپەرستین) شتێک کەنەسوودمان پێ دەگەیەنێت و نە زیان وە بەرەو دوا بگەڕێینەوە (پاشگەزبینەوە لە ئاینەکەمان) لە پاش ئەوەی کە خوا ڕێنمونی کردین (بۆ ڕێگای ڕاست)؟ بەوێنەی ئەوکەسەی کە شەیتانەکان سەریان لێ شێواند بێت لەزەویدا سەرگەردان بێت و چەند ھاوڕێیەکی ھەبێت بانگی کەن بۆ رێگای ڕاست (بڵێن) وەرە بۆلای ئێمە (کەچی بەگوێیان نەکات) بڵێ بەڕاستی ھەر ڕێنمونی خوا ڕێ نمونی ڕاستە و فەرمانمان پێدراوە کە مل کەچ و گوێڕایەڵ بین بۆ پەروەردگاری جیھانیان');
INSERT INTO "bamoki" VALUES(6,72,'6:72','وە (فەرمانمان پێدراوە) کە بەڕێکی نوێژ بەجێ بھێنن و لەو (پەروەردگارە) بترسین وە ھەمووتان ھەر بۆلای ئەو زاتە کۆ دەکرێنەوە (لەڕۆژی دواییدا)');
INSERT INTO "bamoki" VALUES(6,73,'6:73','وەھەر ئەو خوایە یە کەئاسمانەکان و زەوی بەدیھێناوە بەحەق و ڕاستی وە ھەر ڕۆژێک بەشتێک بفەرمووێت ببە ئەویش دەست بەجێ دەبێت فەرمان و ووتەی ئەو ڕاستە ھەر بۆئەوە موڵک و خاوەنیەتی (ھەموو شتێک) لەو ڕۆژەدا کەفوو دەکرێت بە (ێور) کەڕەنادا زانا و ئاگایە بەھەموو نھێنی و ئاشکرایەک و ھەر ئەوە کاردروست و بە ئاگا');
INSERT INTO "bamoki" VALUES(6,74,'6:74','وە (بیربکەرەوە) کاتێک کە ئیبراھیم ووتی بە ئازەری باوکی ئایا تۆ بتانێک دەکەیتە پەرستراوی خۆت؟ بەڕاستی من تۆو گەلەکەت لە گومڕاییەکی تەواو و ئاشکرادا دەبینم');
INSERT INTO "bamoki" VALUES(6,75,'6:75','بەو شێوەیە نیشانی ئیبراھیم ماندا دەسەڵات و خاوەنێتی (خوا بۆ) ئاسمانەکان و زەوی بۆ ئەوەی لەڕیزی باوەڕدارە ڕاستەقینەکاندا بێت');
INSERT INTO "bamoki" VALUES(6,76,'6:76','کاتێک شەوی بەسەردا ھات ئەستێرەیەکی بینی ووتی ئەمە پەروەردگاری منە بەڵام کاتێ ئاوابوو ووتی من ھەرچی ئاوا بێ خۆشم ناوێت');
INSERT INTO "bamoki" VALUES(6,77,'6:77','ئەمجا کاتێک مانگی بینی ھەڵھات ووتی ئەمە پەروەردگاری منە بەڵام کاتێک کە ئاوابوو ووتی سوێند بەخوا ئەگەر پەروەردگارم ڕێنمونیم نەکات بێگومان لەکۆمەڵی گومڕایان دەبم');
INSERT INTO "bamoki" VALUES(6,78,'6:78','ئەمجا کاتێک خۆری بینی ھەڵھات ووتی ئەمە پەروەردگاری منە ئەمە گەورەترە (لەوان) جا کاتێک خۆریش ئاوا بوو ووتی ئەی گەلەکەم بەڕاستی من بەریم لە ھەرشتێک کەئێوە دەیکەن بەھاوبەش بۆ خوا');
INSERT INTO "bamoki" VALUES(6,79,'6:79','بەڕاستی من ڕووی خۆم وەرئەگێڕم بۆلای زاتێک کە ئاسمانەکان و زەوی بەدیھێناوە جگە لەخوا ڕووم لەھیچی تر ناکەم وە بێگومان من لەھاوبەش پەیداکەران نیم');
INSERT INTO "bamoki" VALUES(6,80,'6:80','(بەڵام ئیبراھیم) گەلەکەی دەمەقاڵێیان لەگەڵدا کرد فەرمووی ئایا ئێوە دەمەدەمێ و موجادەلەم لەگەڵدا دەکەن؟ لەبارەی خواوە کە بەڕاستی ڕێنمونی منی کردووە من ناترسم لەوەی کە ئێوە دەیکەن بەھاوبەش بۆ خوا (چونکە زیان ناگەیەنن) مەگەر شتێک کەپەروەردگارم بیەوێت بیکات زانست و ئاگاداری پەروەردگارم ھەموو شتێکی گرتۆتەوە دەی ئایا بۆ بیرناکەنەوە');
INSERT INTO "bamoki" VALUES(6,81,'6:81','وە من چۆن دەترسم لەو بتانەی کە ئێوە کردووتانە بەھاوبەش بۆ خوا کەئێوە ناترسن بێگومان بتانێکتان کردۆتە ھاوبەش بۆ خوا کە ھیچ بەڵگەیەک نەنێرراوە بۆتان (کە ئەوان بپەرستن) دەی کەواتەکام لەم دوو دەستەیە شایستە ترە بە نەترسان (و ئارامی) ئەگەر ئێوە ھیچ دەزانن');
INSERT INTO "bamoki" VALUES(6,82,'6:82','ئەوانەی کە بڕوایان ھێناوە و بڕواکەیان تێکەڵ بەستەم (ھاوەڵ دانان) نەکردووە ھێمنی و ئاسایش تەنھا بۆ ئەوانەیە ھەر ئەوانەش ڕێنمون کراوون');
INSERT INTO "bamoki" VALUES(6,83,'6:83','ئەو (بەڵگانە) بەڵگەی ئێمە بوون کە دابوومان بە ئیبراھیم لەبەرانبەر گەلەکەیەوە ھەرکەسێک بمانەوێت پلەوپایەی بەرز دەکەینەوە بەڕاستی پەروەردگارت کاردروست و زانایە');
INSERT INTO "bamoki" VALUES(6,84,'6:84','وە ئیسحاق و (لەئیسحاقیش) یەعقوبمان پێ بەخشی ڕێنمونی ھەموویانمان کرد وە لەوەو پێش ڕێنمونی نوحیشمان کرد ولەنەوەکانی نوح داود و سولەیمان و ئەیوب و یوسف و موساو ھارونمان (ڕێنمونی کرد) ئا بەو چەشنە پاداشتی چاکەکاران دەدەینەوە');
INSERT INTO "bamoki" VALUES(6,85,'6:85','وە زەکەریا و یەحیا و عیسا و ئیلیاس ھەریەک لەمانە لە چاکەکاران بوون');
INSERT INTO "bamoki" VALUES(6,86,'6:86','وە ئیسماعیل و یەسەع و یونس و لووط ھەریەکە لەمانە ڕێزمانداون بەسەر ھەموو خەڵکیدا');
INSERT INTO "bamoki" VALUES(6,87,'6:87','ھەروەھا ھەندێک لەباوک و نەوەو و براکانیان ھەڵمان بژاردوون و ڕێنمونیمان کردوون بۆ ڕێگەی ڕاست');
INSERT INTO "bamoki" VALUES(6,88,'6:88','ئەوە ڕێنمونی خوایە ڕێنمونی ھەربەندەیەکی پێدەکات لەبەندەکانی کە خۆی بیەوێت وە ئەگەر ھاوبەشیان ڕەوا بدایە بۆ خوا بێگومان ئەو چاکانەی کردبویان ھەمووی بێ پاداشت دەردەچوو');
INSERT INTO "bamoki" VALUES(6,89,'6:89','ئەوانە کە سانێکن پەیام و زانین و حوکمڕانی و پێغەمبەرێتیمان پێ بەخشیوون جا ئەگەر ئەوانە (قوڕەیش) بڕوای پێ نەھێنن ئەوە بێگومان بەدەستەیەکی ترمان سپاردووە (چاودێری بکەن و) بێ بڕواش نابن پێی');
INSERT INTO "bamoki" VALUES(6,90,'6:90','ئەوانە (ئەو پێغەمبەرانەی باسکران) کەسانێک بوون خوا ڕێنمونی کردن دەی پەیڕەوی ڕێگە وڕێبازی ئەوان بکە (ئەی موحەممەد صلى الله عليه وسلم) (ئەی موحەممەد صلى الله عليه وسلم) بڵێ داوای ھیچ کرێ و پاداشتێکتان لێناکەم لەسەری ئەم قورئانە تەنھا ئامۆژگاری یە بۆ ھەموو جیھانیان');
INSERT INTO "bamoki" VALUES(6,91,'6:91','وەک پێویست خوایان نەناسی و ڕێزیان نەگرت کاتێ ووتیان خوا ھیچ شتێکی نەناردۆتە خوارەوە بۆ ھیچ ئادەمیەک بڵێ ئەی کێ ئەو کتێبەی ناردە خوارەوە کە موسا ھێنای (ئەو کتێبە) ڕووناکی و ڕێنمونی بوو بۆ خەڵک (پێش دەستکاری کردنی لەلایەن جولەکەکانەوە) ئێوە ئەو (کتێبە) دەکەنە چەند پەڕە و کاغەزێک بەشێکی (کە قازانجتانە) ئاشکرای دەکەن و زۆریشی (کەزیانی خۆتانە) دەیشارنەوە شتانێک فێر کراوون کەئێوە نەتان دەزانی نەخۆتان و نە باو و باپیرانتان بڵێ ھەرخوا (کتێبی بۆ موسا ناردووە) پاشان لێیان گەڕێ لەناو بەتاڵی خۆیاندا گاڵتەو یاری بکەن و ڕۆبچن');
INSERT INTO "bamoki" VALUES(6,92,'6:92','ئەم قورئانە ئێمە ناردوومانەتە خوارەوە پیرۆز و فەڕدارە ئەو کتێبانەش پێش ئەو ھاتوون بەڕاستیان دادەنێت (پێش ئەوەی گاور وجولەکەکان بیشێوێنن) ھەروەھا بۆ ئەوەی دانیشتوانی مەککە ودەوروبەری ئاگادار کەیتەوە و بیانترسێنیت جا ئەوانەی بڕوایان بە دواڕۆژ ھەبێت بڕوا بەو (قورئانە) دەھێنن ئەوان پارێزەرن بۆ نوێژەکانیان و بەچاکی لەکاتی خۆیاندا دەیانکەن');
INSERT INTO "bamoki" VALUES(6,93,'6:93','کێ لەوکەسە ستەمکارترەکە درۆ ھەڵبەستێ بەدەم خواوە یان بڵێ نیگا وسروشم بۆکراوە بەڵام ھیچ نیگا و سروشێکیشی بۆ نەکرا بێت ھەروەھا کەسێ کە بڵێ منیش وێنەی ئەو قورئانەی خوا دایبەزاندووە دادەبەزێنم خۆزگە ستەمکارانت دەبینی کاتێک وان لە ناڕەحەتی و سەختی سەرە مەرگدا و فریشتەکان دەستیان بۆ دەبەن (بۆ گیان کێشانیان) (پێیان دەڵێن) خۆتان دەربازکەن (لەم ناڕەحەتی گیان کێشانە ئەگەر ڕاست دەکەن) ئەمڕۆ پاداشتی ڕیسواکەر دەدرێنەوە بەھۆی ئەوەی کەقسەی ناڕاستان ھەڵئەبەست بەدەم خواوە و خۆتان بەگەورە دەزانی لەبەرانبەر ئایەتەکانی خوادا');
INSERT INTO "bamoki" VALUES(6,94,'6:94','سوێند بێت بێگومان ئێوە بە تەنیا و یەک یەک گەڕانەوە بۆ لامان ھەر وەکو یەکەم جار دروستمان کردن و ئەوەی پێمان دابوون بەجێتان ھێشت لەدوای خۆتان (لەماڵ وسەروەت وسامان) وە نابینین لەگەڵتاندا ئەو تکاکارانەی کە لاتان وابوو ھاوبەشی خوان لە تکا کردن بۆتان سوێند بە خوا بێگومان پچڕا پەیوەستی نێوانتان وە ئەوەی بە پشتیوانی خۆتان دادەنا وون بوو لێتان');
INSERT INTO "bamoki" VALUES(6,95,'6:95','بەڕاستی خوا لەتکەری دانەوێڵە و ناوکە تۆوە (کاتێک کە دەڕوێنرێت لە زەویدا) زیندوو دەرئەھێنێت لە مردوو, وە (خوا) دەرھێنەری مردوانە لە زیندوو ئەوەیە خوای ئێوە دەی کەواتە ئێوە چۆن (لەپەرستنی ئەو خوایە) لا دەدرێن؟');
INSERT INTO "bamoki" VALUES(6,96,'6:96','(خوا) بەدی ھێنەری شەبەق و ڕووناکی ڕۆژ بوونەوەیه و شەوی کردووە بەکات و ھۆی ئارامگرتن و خۆر و مانگی کردۆتە ھۆی حساب دیاریکردن و زانینی کات ئەوەش ئەندازەگرتنی خوای بەدەسەڵات و زانایە');
INSERT INTO "bamoki" VALUES(6,97,'6:97','وە ھەر خوایە کەئەستێرەی داناوە بۆتان بۆ ئەوەی ڕێیان پێدەرکەن لە تاریکیەکانی وشکانی و دەریادا بەڕاستی ئایەتەکانمان ڕوون کردۆتەوە بۆ کۆمەڵێ کەبزانن و تێ بگەن');
INSERT INTO "bamoki" VALUES(6,98,'6:98','وە ئەو خوایە زاتێکە کە ئێوەی دروستکردوە لەیەک کەس کە (ئادەم) ـە ئەمجا (لە پشتی باوکتانا) نیشتەجێ کران (دواتریش لەمناڵدانی دایکتاندا) دانران بە شێوەی کاتی بە ڕاستی ئایەتەکانمان ڕوون کردۆتەوە بۆ کەسانێک بەووردی بیر بکەنەوە');
INSERT INTO "bamoki" VALUES(6,99,'6:99','وە ئەو خوایە زاتێکە کە بارانی لە ئاسمانەوە باراندووە جا بە ھۆی ئەو (بارانەوە) ھەموو جۆرە چەکەرەیەکمان ڕواند ئەمجا ڕووەکی سەوزمان لێ ڕوواندووە, وە لەو (ڕووەکە سەوزە) دانەوێڵەی لەسەریەک کەڵەکە بوومان دەرھێناوە (وەک گوڵی جۆ و گەنم) وە لە دار خورما لە چەند پەلێکی ھێشووی شۆڕە وە بوو دەردەھێنین وە (دەڕوێنین بەو بارانە) باخاتی زۆرلە ترێی ھەمە جۆر و زەیتون و بنە ھەناری ھاو وێنە و لەیەکتر جیا جیا (لەتامدا) سەرنجی بەرھەمەکەی بدەن کاتێک بەر دەگرێت و پێدەگات بێگومان لەم سەرنجدانەدا چەند بەڵگەی گەورە ھەیە (لەسەر گەورەی دەسەڵاتی خودا) بۆکەسانێک کە بڕوا دەھێنن');
INSERT INTO "bamoki" VALUES(6,100,'6:100','ھاوبەشانێکیان بڕیارداوە بۆ خوا کە جنۆکەن لەکاتێکدا خوا ئەو (جنۆکانەی) دروست کردووە بەدرۆ و بوختان چەند کوڕ و کچیان بڕیاردا بۆ خوا بەبێ زانیاری پاک و بێگەردی بۆ خوا لەوەی کە ئەو بێ بڕوایانە بڕیاری ئەدەن بۆ خوا');
INSERT INTO "bamoki" VALUES(6,101,'6:101','(خوا) داھێنەری ئاسمانەکان و زەویە لە نەبوون چۆن مناڵی دەبێت کە ژن و ھاوسەری نەبووە خۆی ھەموو شتێکی دروستکردووە و ئەو بە ھەموو شتێ زانا و بە ئاگایە');
INSERT INTO "bamoki" VALUES(6,102,'6:102','ھەر ئەو خوایەیە کە پەروەردگاری ئێوەیە ھیچ پەرستراوێکی ڕاستەقینە نیە جگە لە ئەو دروستکەری ھەموو شتێکە کەواتە ئەو بپەرستن وە ئەو خوایە چاودێرە بەسەر ھەموو شتێکەوە');
INSERT INTO "bamoki" VALUES(6,103,'6:103','چاوەکان پەی پێ نابەن و ناتوانن بیبینن بەڵام ئەو ھێزی بینین و چاوەکان دەبینێت و ئەو بە ئاگا و ورد بینە');
INSERT INTO "bamoki" VALUES(6,104,'6:104','بەڕاستی لەلایەن پەروەردگارتانەوە چەندین بەڵگەی ڕوونتان بۆ ھاتووە ئەمجا ھەر کەس بیر لەڕاستی دەکاتەوە و دەیبینێت ئەوە (سوودی) بۆ خۆیەتی ھەر کەسیش بیر ناکاتەوە و کوێر بووە ئەوە (زیانی) ھەر بۆ خۆیەتی وە من چاودێر و پارێزەرنیم بە سەرتانەوە (بەس ڕاگەیەنەرم)');
INSERT INTO "bamoki" VALUES(6,105,'6:105','ئاوا جۆر و شێوەی بەڵگەکان دەگۆڕین دەبا ھەر بڵێن خوێندووتە وپێتووتراوە, وە بۆ ئەوەی چاک ڕوونی بکەینەوە بۆ کۆمەڵێک کەتێدەگەن و شارەزان');
INSERT INTO "bamoki" VALUES(6,106,'6:106','شوێنی ئەوە بکەوە کە لەلایەن پەروەردگارتەوە وەحیت بۆ کراوە ھیچ شتێک شایستەی پەرستن نیە جگە لەو (لە خوا) وە ڕوو وەرگێڕە لە ھاوبەش بڕیاردەران');
INSERT INTO "bamoki" VALUES(6,107,'6:107','وە ئەگەر خوا بیویستایە ھاوەڵیان دا نەدەنا و تۆمان نەدەکرد بەچاو دێرو پارێزەر بەسەریانەوە و تۆ زۆردارو دەسەڵاتدار نیت بەسەریانەوە');
INSERT INTO "bamoki" VALUES(6,108,'6:108','(ئەی موسڵمانان) جنێو و قسەی ناشرین مەڵێن بەوبت و پەرستراوانەی کەئەوان ھاواریان لێدەکەن و دەیانپەرستن بێجگە لەخوا بەو ھۆیەوە ئەوانیش قسەی نەشیاو بڵێن بەخوا بەو شێوەیە ڕازاندومانەتەوە بۆ ھەموو ئوممەتێک کردەوەکەیان پاشان گەڕانەوەیان ھەر بۆلای پەروەردگاریانە ئەمجا ھەواڵیان پێ دەدات بەو کردەوانەی ئەنجامیان دەدا');
INSERT INTO "bamoki" VALUES(6,109,'6:109','وە بەھێزترین سوێندیان بەخوا دەخوارد سوێند بەخوا ئەگەر موعجیزەیەکیان بۆ بێت (لەوانەی کەخۆیان داوایان دەکرد) بێگومان بڕوا دەھێنن پێی بڵێ بەڕاستی موعجیزەکان ھەر لای خوایە وە ئێوە نازانن ئەی بڕواداران ئەگەر ئەو بەڵگانەش بێتەکایەوە ھەر بڕوا ناھێنن');
INSERT INTO "bamoki" VALUES(6,110,'6:110','وە (کاتی ھاتنی موعجیزەکان بەھۆی بێ بڕواییانەوە) دڵ و چاویان وەردەگێڕین و لایدەدەین (لە باوەڕ ھێنان) ھەر وەک لە سەرەتاوە بڕوایان بەقورئان نەھێنا وەلێیان دەگەڕێین لەسەر کەشی خۆیاندا بن (دەبا) ھەر سەرگەردان بن');
INSERT INTO "bamoki" VALUES(6,111,'6:111','وە بێگومان ئەگەر ئێمە فریشتەمان بۆ لای ئەو (بێباوەڕ) انە بناردایە و مردووەکان قسەیان لەگەڵ بکردنایە و ھەموو شتێکمان بۆ کۆ بکردنایەتەوە ڕوو بەڕوو بەپێش چاویانەوە (یان کۆمەڵ کۆمەڵ) بڕوایان نەدەھێنا مەگەر خوا بیویستایە بەڵام زۆربەیان نازانن و بێ ئاگان');
INSERT INTO "bamoki" VALUES(6,112,'6:112','ھەر بەو شێوە بۆ ھەموو پێغەمبەرێک دوژمنێکمان داناوە لەشەیتانەکانی مرۆڤ و جنۆکە کەبەنھێنی بەیەکتری ڕادەگەیەنن ووتەی ڕازاوە و بریقەدار بۆ فریودانیان وە ئەگەر پەروەردگارت بیویستایە ئەوەیان نەدەکرد دە لێیان گەڕێ بەخۆیان و ئەو درۆیـانـەی کەھەڵی دەبەستن');
INSERT INTO "bamoki" VALUES(6,113,'6:113','وە بۆ ئەوەی گوێ بگرێت بۆ ئەو (ووتە بریقەدارو ڕازاوەی بۆ یەکتری دەکەن) دڵی ئەو کەسانەی بڕوایان نیە بەڕۆژی دوایی بۆ ئەوەی ڕازی ببن پێی وەتا ئەنجامی بدەن ئەو گوناھانەی ئەنجامی ئەدەن');
INSERT INTO "bamoki" VALUES(6,114,'6:114','جا ئایا جگە لەخوا دادوەرێکی تر بۆ خۆم بڕیار بدەم؟ لەکاتێکدا (خوا) ئەو زاتەیە کەناردویەتیە خوارەوە بۆ ئێوە ئەم قورئانە کە ڕوونکەرەوەی ھەموو باسێکە بەدرێژی ئەوانەش کە نامەمان پێ بەخشین چاک دەزانن بێگومان ئەم قورئانە لەلایەن پەروەردگارتەوە بەحەق و ڕاستی نێرراوە کەواتە ھەرگیز لەگومانداران مەبە');
INSERT INTO "bamoki" VALUES(6,115,'6:115','وە فەرمان و ووتەی پەروەردگارت تەواو بووە و بەئەنجام گەیشت بەڕاستی و دروستی کەھیچ کەس ناتوانێت بڕیارو ووتەکانی بگوڕێت ھەر خوا بیسەر و زانایە');
INSERT INTO "bamoki" VALUES(6,116,'6:116','وە ئەگەر تۆ گوێڕایەڵی زۆربەی ئەوانە بیت لەسەر زەویدان لەڕێگەی خوا گومڕات دەکەن ئەوان ھەر شوێنی گومان کەوتوون وە ھەمیشە درۆ و گۆترەکاری دەکەن');
INSERT INTO "bamoki" VALUES(6,117,'6:117','بێگومان ھەر پەروەردگارت چاک دەزانێت کێ گومڕا بووە و لایداوە لەڕێگای ئەو وە ھەر ئەو زاناترە بەوانەی ڕێبازی ڕاستیان گرتووە');
INSERT INTO "bamoki" VALUES(6,118,'6:118','دەبخۆن لەو (سەربڕاوە) ی کەناوی خوای لەسەر ھێنراوە ئەگەر ئێوە بەئایەتەکانی خوا بڕوادارن');
INSERT INTO "bamoki" VALUES(6,119,'6:119','بۆچی ئێوە ناخۆن؟ لە (گۆشتی) ئەو (ئاژەڵان) ـەی کەناوی خوای لەسەر ھێنراوە (لەکاتی سەربڕیندا) کەبێگومان خوا بۆتانی ڕوون کردۆتەوە ئەوەی حەرامی کردوە لەسەرتان جگە لەوەی کەناچار دەبن بۆی (بیخۆن لەو خواردنانەی کە حەرامن) بەڕاستی زۆر کەس خەڵکی گومڕا دەکەن بە ھەوا و ئارەزوی خۆیان بەبێ ھیچ زانیاری یەک (کە لەخواوە وەرگیرا بێت) بێگومان پەروەردگارت دەست درێژ کاران و سەرکەشان چاک دەناسێت');
INSERT INTO "bamoki" VALUES(6,120,'6:120','لەگوناھی ئاشکراو نھێنی واز بھێنن بەڕاستی ئەوانەی کەگوناھـ دەکەن بە دەستی ئەنقەست بەزوویی تۆڵەیان لێ دەسەنرێت بەھۆی ئەو تاوانانەی دەیانکرد');
INSERT INTO "bamoki" VALUES(6,121,'6:121','لەو (سەربڕاوە) مەخۆن کەناوی خوای لەسەر نەھێنراوە (لەکاتی سەربڕیندا) بێگومان (خواردنی) ئەوە تاوان و لەسنوور دەرچوونە وە بێگومان شەیتانەکان بەنھێنی شت بەدۆستانیان دەڵێن بۆ ئەوەی موجادەلە و دەمە دەمێتان لەگەڵدا بکەن جائەگەر بەگوێیان بکەن ئەوە بێگومان لەھاوبەش دانەران دەبن');
INSERT INTO "bamoki" VALUES(6,122,'6:122','ئایا کەسێک کە مردوو بێت ( واتە بێ بڕوا بێت) و ئێمە زیندوومان کردەوە ( ھیدایەتماندا) و ڕووناکیەکمان بۆ فەراھەم ھێنا (کەقورئانە) بە ھۆی ئەوەوە لەناو خەڵکیدا دەگەڕێ و دەڕوات ئایا وەک ئەوە وایە کە لەناو تاریکستانێکدا بێت کە بەھیچ جۆرێک دەرچوونی نیە لێی بەو شێوەیە ڕازێنراوەتەوە بۆ بێ بڕوایان ئەو کردەوە (خراپانەی) دەیان کرد');
INSERT INTO "bamoki" VALUES(6,123,'6:123','بەو شێوەیە لە ھەموو شارێکدا گەورەترینی تاوانباریمان کرد (بە دەسەڵاتدار) بۆ ئەوەی پیلان و فێڵ بکەن لەو شارانەدا وە فێڵ وپیلان ناکەن (لە کەس) بێجگە لەخۆیان بەڵام ئەوان ھەستی پێ ناکەن');
INSERT INTO "bamoki" VALUES(6,124,'6:124','وە کاتێک ئایەتێکیان بۆ بێت دەڵێن ئێمە ھەرگیز باوەڕ ناھێنین ھەتا وێنەی ئەوەی بەپێغەمبەرانی خوا دراوە بەئێمەش نەدرێت خوا چاک دەزانێت پەیام و پێغەمبەرایەتی خۆی بۆ کوێ دەنێرێت (و کێ شیاو ترە بۆ ئەو کارە) بەزویی ئەوانەی تاوانیان کردووە دووچاریان دەبێت سووکی وڕیسوایی لای خوا وسزای سەخت بەھۆی ئەو پیلان وفێڵانەوەی کەدەیانکرد (لە بڕواداران)');
INSERT INTO "bamoki" VALUES(6,125,'6:125','جا ھەرکەسێک خوا بیەوێت ڕێنمونی بکات سینگی فراوان دەکات بۆ ئیسلام (و بڕوا ھێنان) وە ھەر کەس خوا بیەوێت گومڕای کات (لەبەر نەشیاوی وسەرکەشی خۆی) سنگی زۆر تەنگ دەکات وەک ئەوەی (بیەوێت) بەرز ببێتەوە بەرەو ئاسمان ( چونکە مرۆڤ تا بەرەو ئاسمان بەرز ببێتەوە ھەست بە تەنگە نەفەسی دەکات وبەناڕەحەت ھەناسەی بۆ دەدرێت) بەو جۆرە خوا پیسی و ناپاکی دادەنێت لەسەر ئەوانەی کەبڕوا ناھێنن');
INSERT INTO "bamoki" VALUES(6,126,'6:126','ئەم ئیسلامە ڕێگای پەروەردگارتە کەڕێک و ڕاستە بێگومان بەڕوونی ئایەتەکانمان باسکردووە بۆ کۆمەڵێک کەبیربکەنەوە');
INSERT INTO "bamoki" VALUES(6,127,'6:127','بۆ ئەوانەیە لای پەروەردگاریان ماڵی ئاشتی کە (بەھەشتە) تەنھا ئەویش دۆستیانە بەھۆی ئەو کردەوە چاکانەی دەیان کرد');
INSERT INTO "bamoki" VALUES(6,128,'6:128','وە ڕۆژێک (خوا) تێکڕا کۆیان دەکاتەوە و (دەفەرموێت) ئەی دەستەو کۆمەڵی جنۆکە بێگومان ئێوە زۆر کەستان لەئادەمیەکان گومڕا کرد وە دۆستەکانیان لەئادەمی دەڵێن ئەی پەروەردگارمان ھەندێکمان سوود و کەڵکمان لەھەندێکمان وەرگرت وە بەو کاتەو مۆڵەتە گەیشتین کەبۆت دیاری کرد بووین خوا فەرمووی ئاگر شوێنتانە بەھەمیشەیی و بێ بڕانەوە دەمێننەوە تیایدا جگە لەو کاتەی کەخوا بیەوێت بەڕاستی پەروەردگارت کار دروست و زانایە');
INSERT INTO "bamoki" VALUES(6,129,'6:129','بەو شێوەیە ھەندێک لەستەمکاران دەکەین بەدۆست و پشتیوانی ھەندێکی تریان بەھۆی ئەو کردەوانەوەی ئەنجامیان دەدا');
INSERT INTO "bamoki" VALUES(6,130,'6:130','ئەی دەستە و کۆمەڵی جنۆکە و ئادەمی ئایا پێغەمبەرانتان لەخۆتان بۆ نەھات کەئایەتەکانی منتان بۆ بخوێننەوە, وە لەگەیشتن و دووچار بوونی ئەم ڕۆژەیان نەترساندن دەڵێن (ڕاستە و) شایەتی لەسەر خۆمان دەدەین بەڵام ژیانی دونیا خەڵەتاندینی و شایەتیان لەسەر خۆیاندا کەبێگومان ئەوان بێ بڕوا بوون');
INSERT INTO "bamoki" VALUES(6,131,'6:131','ئەوەی باسکرا لەبەر ئەوەیە کەپەروەردگارت لەناو بەری (خەڵکی) شارو گوندەکان نەبووە و زوڵم و ستەمی لێ نەکردوون لەکاتێکدا خەڵکی ئەو شارو گوندانە بێ ئاگابووبن');
INSERT INTO "bamoki" VALUES(6,132,'6:132','بۆ ھەر یەکێک (لەجنۆکەو ئادەمی) چەند پلە و پایە ھەیە لەبەرانبەر کردەوەکانیاندا وە پەروەردگارت لەوەی کەدەیکەن (ئەو ستەمکارانە) بێ ئاگا نیە');
INSERT INTO "bamoki" VALUES(6,133,'6:133','ھەر پەروەردگاری تۆ بێ نیازە و خاوەنی میھرەبانیە ئەگەر بیەوێت لەناوتان دەبات وە کێی بوێت دوای ئێوە دەیکاتە جێنشینی(ئێوە) وەک چۆن ئێوەی لەنەوەی کۆمەڵێکی تر بەدی ھێنا');
INSERT INTO "bamoki" VALUES(6,134,'6:134','بێگومان ھەر بەڵێنێکتان پێدەدرێت ھەر دێت و ئێوە لەدەس خوا دەرناچن');
INSERT INTO "bamoki" VALUES(6,135,'6:135','(ئەی موحەممەد صلى الله عليه وسلم) بڵێ ئەی گەلەکەم لەسەر ڕێبازی خۆتان ئەوەی لەدەستان دێت بیکەن بێگومان منیش کارو کردەوەی خۆم دەکەم جا لەمەو پاش دەزانن سەرئەنجامی ئەو دونیا بۆکێ دەبێت بێگومان (بێ باوەڕان) وستەمکاران ڕزگاریان نابێت');
INSERT INTO "bamoki" VALUES(6,136,'6:136','وە بڕیاریاندا بۆ خوا لەوەی کە (خوا) دروستی کردو دایھێنا بەشێک لەکشتوکاڵ و ئاژەڵ و ماڵات جا بەگومانی خۆیان ووتیان ئەمە بۆ خوایە وە ئەوەش بۆ ھاوبەشەکانمان (بتەکانمانە) جا ئەوەی بۆ ھاوبەشەکانیانە ئەوە بە خوا ناگات وە ئەوەش بۆ خوایە ئەوە دەگات بە ھاوبەشەکانیان (واتە: خوا نایەوێت و ئەیداتەوە بەسەریاندا) ئای چ خراپە ئەوەی بڕیاری دەدەن');
INSERT INTO "bamoki" VALUES(6,137,'6:137','ئا بەو شێوەیە شەیتانەکان کوشتنی ڕۆڵەکانیان بۆ زۆر لەھاوەڵـدانەرەکان ڕازاندەوە بۆ ئەوەی لەناویان بەرن (بەکوشتنی کەسانی بێ تاوان)وە بۆ ئەوەی ئاینەکەیان لێ بشێوێنن وە ئەگەر خوا بیویستایە ئەوەیان نەدەکرد کەواتە لێیان گەڕێ با لەگەڵ ئەو درۆ و دەلەسەیەی کە ھەڵی دەبەستن (بێ ئاگا و گومڕا بن)');
INSERT INTO "bamoki" VALUES(6,138,'6:138','وە (بە گومانی خۆیان) دەیانووت ئەمانە ئاژەڵ وماڵات وکشتوکاڵێکن کە قەدەغە کراون نابێت لێیان بخوات کەسانێک نەبێت خۆمان بمانەوێت بەگومانی خۆیان ماڵاتێکیش پشتیان قەدەغە کرابوو (نابیێت باریان لێ بنرێت یا سواریان ببن) ھەروەھا ئاژەڵ و ماڵات ھەیە (لەکاتی سەربڕیندا) ناوی خوایان لەسەر نەدەبرد بەدرۆ ھەڵبەستن بەدەم خواوە لەمەولا خوا تۆڵەی ئەو درۆ ھەڵبەستنەیان لێ دەستەنێت');
INSERT INTO "bamoki" VALUES(6,139,'6:139','وە دەیانووت ئەوەی لەسکی ئەم ئاژەڵ و ماڵاتانە دایە تایبەتە بەنێرینەکانمانەوە (پیاوەکانمان) وە قەدەغە کراوە لەسەر ژنەکانمان وە ئەگەر مردارەوە بوو بوون ئەوا ھەموو تیایدا ھاوبەشو بەشدارن بەزوویی خوا سزای ئەم وەێفە (نەفامانە)یان دەداتەوە بێگومان خوا کار دروست و زانایە');
INSERT INTO "bamoki" VALUES(6,140,'6:140','بەڕاستی زیانباربوون ئەوانەی ڕۆڵەکانی خۆیان کوشت بەنەفامی ونەزانی و ئەوەی کەخوا پێی بەخشیبوون لەخۆیان قەدەغەکرد بەدەست ھەڵبەست و درۆ بەدەم خواوە بەڕاستی ئەوانە گومڕا بوون و ڕێنمونی کراو نەبوون');
INSERT INTO "bamoki" VALUES(6,141,'6:141','وە تەنھا خوایە کەبەدی ھێناوە باخاتی کەپرو ھەرزاڵە بۆ کراو (یا ساق بەرز) وە کەپرو ھەرزاڵە بۆ نەکراو (ساق کورت) وە دار خورما و کشتوکاڵی جیاواز لەخواردن و تامدا (بەدیھێناوە) زەیتون و ھەناریشی (بەدی ھێناوە) لەیەک چوو (لەوێنەو دیمەندا) و لەیەک نەچوو (لەتامدا) بخۆن لەبەروبوومی کاتێک ھاتەبەر وە لەڕۆژی دروێنە و چنینیدا مافەکەی (زەکاتەکەی) لێ بدەن و زیادە ڕۆی مەکەن بێگومان خوا زیادەڕۆیانی خۆش ناوێت');
INSERT INTO "bamoki" VALUES(6,142,'6:142','لەئاژەڵ و ماڵاتیش ھەندێکی بارھەڵگرە و ھەندێکی بۆ کوڵک و خوریە (کەڕاخەرو فەرشی لێ دروست دەکرێت) لەوەی کەخوا پێی بەخشیوون بخۆن وە شوێنی ھەنگاوەکانی شەیتان مەکەون چونکە بێگومان شەیتان دوژمنێکی ئاشکرای ئێوەیە');
INSERT INTO "bamoki" VALUES(6,143,'6:143','(خوا) ھەشت جۆر ئاژەڵ و ماڵاتی (دروستکردووە) لەمەڕ دووان (مەڕ و بەران) و لەبزن دووان (بزن و سابرێن) (ئەی موحەممەد صلى الله عليه وسلم) بڵێ: ئایا خوا ئەو دوو نێرەی حەرام کردووە؟ یان ئەو دوو مێیە؟ یا ئەو بێچوانەی کە لەسکی ئەو دوو (جۆرە ئاژەڵە) مێینەدایە ئەگەر ئێوە ڕاست دەکەن ھەواڵم بدەنێ بەزانیاریەک (لەخواوە)');
INSERT INTO "bamoki" VALUES(6,144,'6:144','وە لەحوشتر دووان و لەڕەشەوڵاخ دووان (نێرو مێیەک) (ئەی موحەممەد صلى الله عليه وسلم) بڵێ ئایا خوا ئەو دوو نێرەی حەرام کردووە یان ئەو دوو مێیە؟ یا ئەو بێچوانەی کە لەسکی ئەو دوو (جۆرە ووڵاخە) مێینەدایە یان ئێوە ئامادە و شایەت بوون ئەو کاتە کەخوا فەرمانی پێکردن (بەم قەدەغەکردنە) کەواتە کێ لەو کەسە ستەمکارترە کەدرۆ بەدەم خواوە ھەڵدەبەستێت بۆ ئەوەی خەڵکی گومڕا بکات بەبێ ھیچ زانیاریەک بەڕاستی خوا ڕێنمونی کۆمەڵی ستەمکاران ناکات');
INSERT INTO "bamoki" VALUES(6,145,'6:145','(ئەی موحەممەد صلى الله عليه وسلم) بڵێ نابینم لەوەی کە (وەحی) وسرووشم بۆ کراوە ھیچ خۆراکێک حەرام بێت بۆ کەسێک کە بیخوات جگە لە مردارەوە بوو یان خوێنێکی ڕژێنراو نەبێت یا گۆشتی بەراز چونکە بێگومان ئەوەش پیس (وحەرامە) یان سەربڕاوێک (کاتی سەربڕین) ناوی غەیری خوای لەسەر ھێنرا بێت جا ھەر کەس ناچار بوو (کە ئەو حەرامانە بخوات لەبەر برسیەتی وخواردنی حەڵاڵی دەست نەکەوت) با زیاد لەپێویست نەخوات و مەبەستی سنوور بەزاندن نەبێت ئەوە بێگومان پەروەردگارت لێبوردەی میھرەبانە');
INSERT INTO "bamoki" VALUES(6,146,'6:146','تەنھا لەسەر ئەوانەی بوونە جولەکە حەراممان کرد ھەموو چڕنوکدارێک (باڵدار بێت یا ئاژەڵ) وە لە (گۆشتی) ڕەشەوڵاخ و مەڕو بزنیش پیو و بەزەکانیمان لێ حەرام کردن مەگەر ئەوەی کەبەسەر پشتیانەوە بێت یا ئەوەی بەڕیخۆڵەکانیانەوە بێت یا تێکەڵ بووبێت و لکا بێت بەئێسکەوە بەھۆی سەرکەشیانەوە ئەو تۆڵە و سزامان دان وە بێگومان ئێمە ڕاست گۆین');
INSERT INTO "bamoki" VALUES(6,147,'6:147','ئەمجا ئەگەر باوەڕیان پێ نەکردیت ئەوا (پێیان) بڵێ پەروەردگارتان خاوەنی میھرەبانی فراوانە وە سزاو تۆڵەی ئەو (خوا کەھات) لەھۆزی تاوانباران ناگێڕدرێتەوە');
INSERT INTO "bamoki" VALUES(6,148,'6:148','ئەوانەی کەھاوبەشیان بۆ خوا دانا بوو لەمەو دوا دەڵێن: ئەگەر خوا بیویستایە ئێمەو باوو باپیرانمان ھاوبەشمان (بۆخوا) دا نەدەنا وە ھیچ شتێکمان حەرام نەدەکرد ھەر بەو شێوەیە ئەوانەی پێش ئەمانیش درۆیان کرد ھەتا سزاو تۆڵەی ئێمەیان چەشت (ئەی موحەممەد صلى الله عليه وسلم) بڵێ ئایا ھیچ زانیاریەکتان لایە تا دەری بخەن بۆمان و دەری ببڕن ئێوە شوێنی ھیچ شتێک ناکەون بێجگە لەگومان نەبێت و ئێوە ھەر لەخۆتانەوە بەگۆترە شت دەڵێن');
INSERT INTO "bamoki" VALUES(6,149,'6:149','بڵێ دەی کەواتە بەڵگەی بەھێز و (دەسەڵات) ھەر بۆ خوایە جا ئەگەر (خوا) بیویستایە ھەر ھەمووتانی دەخستە سەر ڕێی ڕاست');
INSERT INTO "bamoki" VALUES(6,150,'6:150','(ئەی موحەممەد صلى الله عليه وسلم) بڵێ ئەو شایەتانەتان بھێنن کە شایەتی دەدەن کە خوا ئەمەی حەرام کردووە جا ئەگەر (بە درۆ) شایەتیان دا تۆ لەگەڵیاندا شایەتی مەدە وە شوێن ھەواو ھەوەس وئارەزواتی ئەوانە مەکەوە کەباوەڕیان نەھێنا بەئایەتەکانی ئێمە وە شوێن ئەوانەش مەکەوە کەبڕوایان نیە بەڕۆژی دوایی ئەوانەش کەھاوبەش و ھاوتا بۆ پەروەردگاریان بڕیار دەدەن');
INSERT INTO "bamoki" VALUES(6,151,'6:151','(ئەی موحەممەد صلى الله عليه وسلم) بڵێ وەرن بابیخوێنمەوە (بۆتان) ئەوەی پەروەردگارتان (حەرام) و یاساغی کردووە لەسەرتان (فەرمانیداوە کەوا) ھیچ شتێک نەکەن بە ھاوبەش بۆی و لەگەڵ باوک و دایکدا ئاکار چاک و ڕەفتار جوانبن و مناڵەکانتان مەکوژن لەبەر ھەژاری و نەبوونی ئێمە ڕۆزی ئێوەو ئەوانیش دەدەین وە نزیکی گوناھو کردەوە زۆر بەدەکان مەکەونەوە ئەوەی بە ئاشکرا دەکرێت لەو (خراپانە) و ئەوەش بە پەنھان وە ئەو کەسە مەکوژن کە خوا کوشتنی حەرام کردووە مەگەر ھەق بێت (و بکوژرێتەوە لەتۆڵەدا) خوا ئەمانەی بەئێوە ڕاسپاردوە بۆ ئەوەی تێ بگەن (و ژیری خۆتان بەکاربھێنن)');
INSERT INTO "bamoki" VALUES(6,152,'6:152','وە نزیکی ماڵی ھەتیوان مەکەونەوە مەگەر بەو شێوەی کەجوانترو چاکتر بێت (بۆ ئەوان) تا باڵغ دەبێت و دەگاتە ھێزی خۆی وە پێوانەو کێشانە بێ زیاد و کەم ئەنجام بدەن بەدادگەری داوای ھیچ ئەرک و فەرمانێک لەکەس ناکەین بەئەندازەی توانای خۆی نەبێت وەکاتێک (بۆشایەتیدان و شتی تر) قسەتان کرد دادوەرو ڕاست بن با (لەدژی) خزمی نزیکی خۆشتان بێت وەبەڵێن و پەیمانی خوا بەچاکی ببە نەسەر خوا ئەو شتانەی بەئێوە ڕاسپاردوە بۆ ئەوەی بیر بکەنەوە');
INSERT INTO "bamoki" VALUES(6,153,'6:153','(فەرمانی داوە کە) بێگومان ئەمە ڕێگای ڕاستی منە کەڕاست و دروستە کەواتە شوێنی بکەون و شوێنی کوێرە ڕێگا وڕێچکەکانی تر مەکەون چونکە دوورتان دەخەنەوە و لاتان دەدەن لەڕێگەی خوا خوا ئەو شتانەی بەئێوە ڕاسپاردوە بۆ ئەوەی پارێزکار و لە خواترس بن');
INSERT INTO "bamoki" VALUES(6,154,'6:154','لەپاش ئەوە تەوراتمان بەخشی بەموسا بۆ تەواو کردنی چاکەو بەھرەمان بەرانبەر کەسێک کە بە چاکی پێی ھەڵساوە, وە ڕوونکەرەوەی ھەموو شتێک بێت وە ڕێنیشاندەر و ڕێنمونی و میھرەبانی بێت بۆ ئەوەی باوەڕ بھێنن بەگەیشتنیان بەحوزوری پەروەردگاریان (لە ڕۆژی دواییدا)');
INSERT INTO "bamoki" VALUES(6,155,'6:155','وە ئەم قورئانە پیرۆزە بەفەڕ وپیتەمان ناردۆتە خوارەوە کەواتە ئێوەش شوێنی بکەون و خۆتان بپارێزن و (پارێزکاربن) تا ڕەحمتان پێ بکرێت');
INSERT INTO "bamoki" VALUES(6,156,'6:156','لەبەر ئەوەی نەڵێن بێگومان کتێب تەنھا بۆ سەر دوو کۆمەڵی (گاورو جولەکە) نێراوەتە خوارەوە لەپێش ئێمەدا کەبەڕاستی ئێمە لەخوێندنەوە و لێکۆڵینەوەی کتێبەکانیان بێ ئاگابووین');
INSERT INTO "bamoki" VALUES(6,157,'6:157','وە بۆ ئەوەی نەڵێن بەڕاستی ئەگەر کتێبی ئاسمانی بۆ ئێمەش بھاتایە ئەوە ئێمە لەوان ڕێنمونتر دەبووین ئێوە بێگومان بەڵگەو نیشانە (قورئانی) ڕوونتان لە پەروەردگارتانەوە بۆ ھاتووە کەڕێنمونی و میھرەبانیەکی گەورەیە کەواتە کێ ستەمکارترە لەو کەسەی بێ باوەڕ بێت بەئایەتەکانی خواو بەدرۆی بزانێت وە ڕوویان لێ وەرگێڕێت لەمەو دوا بەزوویی تۆڵە دەسێنین لەوانەی سەرپێچی دەکەن لە ئایەتەکانی ئێمە بەسزایەکی بەدو سەخت بەھۆی ئەو لادانەوە کەدەیانکرد');
INSERT INTO "bamoki" VALUES(6,158,'6:158','ئاخۆ (ئەی موحەممەد صلى الله عليه وسلم) ئەوان چاوەڕوانی چ شتێک دەکەن بێجگە لەوەی فرشتەی (گیان کێشان) یان بۆ بێت یان پەروەردگارت بێت یان ھەندێک لە موعجیزە و نیشانە ئاشکراکانی پەروەردگارت بەدی بێت ئەو ڕۆژەی ھەندێک لەنیشانە و بەڵگە ڕوونەکانی پەروەدگارت بەدی دێت باوەڕھێنان سوود ناگەیەنێت بەھیچ کەس کەپێشتر بڕوای نەھێنا بێت یان ھیچ خێروچاکەیەکی نەکردبێت لەگەڵ ئیمانەکەیدا بڵێ: چاوەڕوان بن (ھەڕەشەیە) ئێمەش چاوەڕوانی دەکەین');
INSERT INTO "bamoki" VALUES(6,159,'6:159','بەڕاستی ئەوانەی ئاینەکەیان بەش بەش کرد و بوون بەچەند دەستەو تاقمێکەوە تۆ لەھیچ شتێکدا لەئەوان نیت بێگومان کاری ئەوان تەنھا بۆ لای خوا دەگەڕێتەوە پاشان خوا ھەواڵیان پێدەدات بەھەر کردەوەیەک کەکردویانە');
INSERT INTO "bamoki" VALUES(6,160,'6:160','ھەر کەسێک چاکە بکات ئەوە (لەڕۆژی دواییدا) دە ھێندەی چاکەکەی پاداشتی بۆ ھەیە وە ھەر کەسێک خراپە بکات ئەوە تۆڵەی لێ ناسێنرێت تەنھا بەوێنەی خۆی نەبێت و ئەوانە ستەمیان لێ ناکرێت');
INSERT INTO "bamoki" VALUES(6,161,'6:161','(ئەی موحەممەد صلى الله عليه وسلم) بڵێ بێگومان من پەروەردگارم ڕێنمونی کردووم بۆ ڕێگای ڕاست ئاینێکی زۆر ڕاست و پاک (کەھیچ لاریەکی تیا نیە) ئایینی ئەو ئیبراھیمەیە کە لەھەموو ئایینە ناڕاستەکان لایداوە بۆ پەرستنی خوا بەتەنھا وە بە ھیچ جۆرێک لە ھاوبەش پەیداکەران نەبووە');
INSERT INTO "bamoki" VALUES(6,162,'6:162','(ئەی موحەممەد صلى الله عليه وسلم) بڵێ بێگومان نوێژو (بەندایەتی) و قوربانی و ژیان و مردنم ھەر بۆ خوای پەروەردگاری ھەموو جیھانیانە');
INSERT INTO "bamoki" VALUES(6,163,'6:163','ھیچ ھاوبەشێکی نیە و ھەر بەوە فەرمانم پێدراوە, وە من یەکەمی ناو ھەموو موسڵمانان و فەرمانبەردارانم (بۆ خوا)');
INSERT INTO "bamoki" VALUES(6,164,'6:164','(ئەی موحەممەد صلى الله عليه وسلم) بڵێ: ئایا بێجگە لە خوا پەرستراوێکی تر بۆ خۆم ھەڵبژێرم لەکاتێکدا ئەو پەروەردگاری ھەموو شتێکە و ھەر کەسێک ھەر گوناھو خراپەیەک بکات تەنھا لەسەر خۆیەتی وە ھیچ کەسێکی گوناھکار گوناھی کەسێکی تر ھەڵناگرێت پاشان گەڕانەوەتان ھەر بۆلای پەروەردگارتانە ئەمجا ھەواڵتان پێ ئەدا بەوەی کەئێوە تیایدا جیاواز بوون');
INSERT INTO "bamoki" VALUES(6,165,'6:165','وە خوا ئەو زاتەیە کە ئێوەی کردووە بەجێنشینانی زەوی (کۆمەڵ لەدوای کۆمەڵ دێن) چەند پلە ھەندێکتانی بەرز کردۆتەوە بەسەر ھەندێکی ترتاندا بۆ ئەوەی تاقیتان بکاتەوە لەوەدا کەپێی بەخشیوون بەڕاستی پەروەردگارت تۆڵە سەندنی خێرایە (بۆ بەدکاران) وە بەڕاستی ئەو لێبوردە و میھرەبانە (بۆ چاکەکاران)');
INSERT INTO "bamoki" VALUES(7,1,'7:1','بە (ئەلیف، لام، میم، ێاد) دەخوێنرێتەوە، خوا زاناترە بەماناو مەبەست پێی، بۆ زانیاری زیاتر سەیری سەرەتای سورەتی (البقرە) بکە');
INSERT INTO "bamoki" VALUES(7,2,'7:2','(ئەم قورئانە) نامەیەکە نێرراوەتە خوارەوەبۆ تۆ (ئەی موحەممەد صلى الله عليه وسلم) کەواتە دڵ تەنگ مەبە (بەپەیڕە و نەکەرانی) بۆ ئەوەی (ئەوان)بەم قورئانە بترسێنیت و بیرخستنەوە و ئامۆژگاریش بێ بۆ بڕواداران');
INSERT INTO "bamoki" VALUES(7,3,'7:3','پەیڕەوی ئەوە بکەن کە لەلایەن پەروەردگارتانەوە دابەزێنراوەتە خوارەوە بۆتان وە بێجگە لەئەو (لە خوا) شوێن ھیچ پەرستراوانێکی تر مەکەون و مەیکەن بە پشتیوانی خۆتان ئێوە زۆر کەم بیر دەکەنەوە');
INSERT INTO "bamoki" VALUES(7,4,'7:4','وە چەندەھا شار و گوندمان وێرانکرد و سزای ئێمەیان بۆ ھات لەشەودا یان ئەوان لەخەو و پشووی نیوەڕۆدا بوون');
INSERT INTO "bamoki" VALUES(7,5,'7:5','جا کاتێک سزای ئێمەیان بۆھات ھاوار و نزایان ھیچ نەبوو بێجگە لەوەی کەدەیانووت: بەڕاستی ئێمە ستەمکار بووین');
INSERT INTO "bamoki" VALUES(7,6,'7:6','سوێند بەخوا بەڕاستی پرسیار دەکەین لەوانەی کەپێغەمبەرانیان بۆ نێردرا (کە چۆن پێشوازییان لێکردن) وە بێگومان پرسیاریش دەکەین لەپێغەمبەران (کە چۆن وەڵام درانەوە)');
INSERT INTO "bamoki" VALUES(7,7,'7:7','جا سوێند بەخوا بەڕاستی (ئەوەی کردوویانە) بۆیان دەگێڕینەوە لەڕووی زانیاریەوە وەئێمە ھیچ کاتێک بێ ئاگا نەبووین (لێیان)');
INSERT INTO "bamoki" VALUES(7,8,'7:8','کێشانەی ڕاست (بۆ کردەوەکانتان) لەو ڕۆژە دا دەبێت و گومانی تێدا نیە جاھەر کەس کێشراوی (چاکـەکانی) قورس بێت ئەوە ئەوانەن ڕزگار و گەیشتوو بەئامانج');
INSERT INTO "bamoki" VALUES(7,9,'7:9','وە ھەرکەس کێشراوی (چاکەکانی) سووک بێت ئەوانە کەسانێکن کەخۆیانیان دۆڕاندووەو زەرەر مەندبوون بەھۆی ئەوەی کەستەمیان کرد بەرانبەر بەڵگەو نیشانەکانی ئێمە (و بەدرۆیان دانا)');
INSERT INTO "bamoki" VALUES(7,10,'7:10','سوێند بەخوا بێگومان ئێمە ئێوەمان نیشتەجێ و جێگیرکرد لەزەویدا و جۆرەھا ھۆی ژیانمان تێیدا بۆ ڕەخساندن (بەڵام) ئێوە زۆر کەم سوپاس گوزارن');
INSERT INTO "bamoki" VALUES(7,11,'7:11','سوێند بێت بێگومان ئێوەمان بەدی ھێنا و پاشان شکڵ و شێوەمان کێشان پاشان ووتمان بەفریشتەکان سوژدە ببەن بۆ ئادەم ھەموویان یەکسەر سوژدەیان برد بێجگە لەئیبلیس (لەشەیتان) کە لەسوژدە بەران نەبوو');
INSERT INTO "bamoki" VALUES(7,12,'7:12','(خوا) فەرمووی چی ڕێگری لێ کردیت کەسوژدە نەبەیت کاتێک فەرمانم پێکردیت؟ (شەیتان) ووتی: من لە (ئادەم) چاکترم چونکە منت لە ئاگر دروست کردووەو ئەوت لەقوڕ دروست کردووە');
INSERT INTO "bamoki" VALUES(7,13,'7:13','(خوا) فەرمووی: (لە بەھەشت) بڕۆرە خوارەوە چونکە بۆت نیە لەو (بەھەشتە) دا خۆت بەگەورە و زل بزانی یەکسەر دەرچۆ بەڕاستی تۆ لە بێ نرخ و ڕیسوایانی');
INSERT INTO "bamoki" VALUES(7,14,'7:14','ووتی مۆڵەتم بدە (مەممرێنە) تا ئەو ڕۆژەی زیندوو دەکرێنەوە');
INSERT INTO "bamoki" VALUES(7,15,'7:15','(خواش) فەرمووی: بێگومان تۆ لەمۆڵەت دراوانیت');
INSERT INTO "bamoki" VALUES(7,16,'7:16','ووتی ئەی پەروەردگار بەھۆی ئەوەوە کە گومڕات کردم بێگومان بۆیان (بۆ ئادەم و نەوەکانی) دادەنیشم لەڕێگە ڕاستەکەی تۆدا');
INSERT INTO "bamoki" VALUES(7,17,'7:17','ئەمجا سوێندبێ بێگومان بۆیان دەچم لەپێشیانەوە و لەدوایانەوە, وە لەلای ڕاستیان و لەلای چەپیانەوە ئەوسا نابینی زۆربەیان سوپاس گوزاربن');
INSERT INTO "bamoki" VALUES(7,18,'7:18','(خوا) فەرمووی: دەرچۆ تێیدا بە لۆمەکراوی و دەرکراوی سوێندبێت بێگومان ھەرکەس شوێنی تۆ کەوتبێت لەوان ئەوە بێگومان دۆزەخ پڕ دەکەم لەئێوە ھەمووتان');
INSERT INTO "bamoki" VALUES(7,19,'7:19','وە ووتمان: ئەی ئادەم خۆت و ژنەکەت نیشتەجێ بن لەم بەھەشتەدا وە لە ھەموو خواردەمەنیەکی بخۆن بەتێرو تەسەلی لەھەر شوێنێک کەحەز دەکەن وە نزیکی ئەم درەختە مەکەون تا لە ستەمکاران نەژمێردرێن تا نەبنە ستەمکار');
INSERT INTO "bamoki" VALUES(7,20,'7:20','وە (ووتمان) ئەی ئادەم تۆو ھاوسەرەکەت نیشتەجێی بەھەشت بن بخۆن لەھەرچی کەویست و حەزتان لێ بوو بەڵام نزیکی ئەو درەختە مەکەونەوە ئەگینا لەستەمکاران دەبن');
INSERT INTO "bamoki" VALUES(7,21,'7:21','وە سوێندی بۆ خواردن ووتی بێگومان من لە ئامۆژگاریکەرانی دڵسۆزی ئێوەم');
INSERT INTO "bamoki" VALUES(7,22,'7:22','سەرئەنجام بەخەڵەتاندن ھانیدان جا کاتێک (لەبەرو بوومی) ئەو درەختەیان چەشت ھەردووکیان عەیب و عەورەتیان دەرکەوت وە دەستیان کرد بەداپۆشینی خۆیان بەگەڵای (درەختانی) بەھەشت وە پەروەردگاریان بانگی ھەردووکیانی کرد ئایا ئەو درەختەم قەدەغە نەکرد لێتان وە ئایا پێم نەووتن کە بێگومان شەیتان بۆ ئێوە دوژمنێکی ئاشکرایە');
INSERT INTO "bamoki" VALUES(7,23,'7:23','ھەردووکیان ووتیان ئەی پەروەردگارمان ئێمە ستەممان لەخۆمان کرد جا ئەگەر لێمان نەبوری و میھرەبانیمان لەگەڵ نەکەیت سوێند بەخوا بێگومان ئێمە لەزیانباران دەبین');
INSERT INTO "bamoki" VALUES(7,24,'7:24','(خوا) فەرمووی بڕۆنە خوارەوە (لەبەھەشت) ھەندێکتان دوژمنی ھەندێکی ترتانن وە بۆتان ھەیە لەزەویدا نیشتەجێ بوون و ھۆی ڕابواردن و ژیان ھەتا ماوەیەک');
INSERT INTO "bamoki" VALUES(7,25,'7:25','(خوا) فەرمووی: لەزەویدا دەژین و لەویشدا دەمرن و ھەر لەویش دەردەھێنرێن (و زیندوو دەکرێنەوە)');
INSERT INTO "bamoki" VALUES(7,26,'7:26','ئەی نەوەی ئادەم بێگومان پۆشاکمان بۆ ناردوون کەعەیب وعەورەتتان دادەپۆشێ وە پۆشاکی جوان و ڕازاوەیش (مان بۆ ناردوون) بەڵام پۆشاکی ترسان لەخوا و پارێزکاری چاکترو باشترە ئەوەی باسکرا ھەندێکە لەبەڵگە و نیشانەکانی خوا بۆ ئەوەی بیربکەنەوەو ئامۆژگاری وەرگرن');
INSERT INTO "bamoki" VALUES(7,27,'7:27','ئەی نەوەی ئادەم با شەیتان فریوتان نەدات و نەتانخەڵەتێنێت ھەرچۆنێک باوک و دایکتانی لەبەھەشت دەرکرد لەحاڵێََََکدا پۆشاکەکەیانی لێداکەندن بۆ ئەوەی عەیب و عەورەتیان (دەربخات) و نیشانیان بدات بەڕاستی شەیتان خۆی و دەستەکەی دەتانبینن لەجێگایەکەوە کەئێوە ئەوان نابینن بەڕاستی ئێمە شەیتانەکانمان گێڕاوە بەدۆست وخۆشەویست بۆ ئەوانەی کەبڕوا ناھێنن');
INSERT INTO "bamoki" VALUES(7,28,'7:28','وە کاتێک کردەوەیەکی زۆر بەدو ناشیرینیان دەکرد (کە بەڕووتی تەواف کردن نموونەیەکەیەتی) دەیانووت: لە باوو باپیرانمانەوە بۆمان ماوەتەوە, وە خوا فەرمانی ئەو (کردەوەی) پێ داوین (ئەی موحەممەد صلى الله عليه وسلم) بڵێ بێگومان خوا فەرمان نادات بەکردەوەی بەد و خراپ ئایا بەدەم خواوە شتێک دەڵێن کەنایزانن و بێ ئاگان لێی؟');
INSERT INTO "bamoki" VALUES(7,29,'7:29','بڵێ پەروەردگارم فەرمانی بە دادپەروەری داوە, وە لەکاتی ھەموو نوێژێکدا ڕاست ڕوو بکەنە خوا و دڵسۆزانە ھاواری لێ بکەن و بیپەرستن ھەرچۆنێ لە یەکەم جارەوە (خوا) دروستی کردون بەو شێوەیە دەگەڕێنەوە (بۆ لای خوا)');
INSERT INTO "bamoki" VALUES(7,30,'7:30','دەستەیەکی ڕێنمونی کردوە دەستەیەکیش گومڕایی بەسەریاندا چەسپا بەڕاستی ئەوانە شەیتانەکانیان کرد بەدۆست و پشتیوانی خۆیان لەجیاتی خوا وە وا گومان دەبەن کەڕێنمونی کراوون');
INSERT INTO "bamoki" VALUES(7,31,'7:31','ئەی نەوەی ئادەم، پۆشاکی جوان و ڕازاوەتان بپۆشن لەکاتی ھەموو نوێژ (چوونە مزگەوتێکتاندا) وە بخۆن و بخۆنەوە (لەوەی کە خوا حەڵاڵی کردووە بۆتان) و زیادە ڕۆی مەکەن بێگومان خوا زیادەڕۆیانی خۆش ناوێت');
INSERT INTO "bamoki" VALUES(7,32,'7:32','(ئەی موحەممەد صلى الله عليه وسلم) بڵێ کێ ڕازاندنەوە وجوانی و (نازونیعمەتە) خواییەکانی حەرام کردووە کەخوا بۆ بەندەکانی دروستی کردووە ھەروەھا ڕۆزی وخۆراکە پاک و خۆشەکان (کێ حەرامی کردوون) (ئەی موحەممەد صلى الله عليه وسلم) بڵێ ئەوانە بۆ بڕوادارانە لەژیانی دونیادا (بێ بڕوایانیش لەپەنای ئەواندا بەشداری دەکەن) (بەڵام) لەڕۆژی دواییدا تایبەتە بەبڕوادارانەوە بەم شێوەیە ئایەتەکانمان باس دەکەین و ڕوونی دەکەینەوە بۆ گەل و کۆمەڵێک کە دەزانن و تێدەگەن');
INSERT INTO "bamoki" VALUES(7,33,'7:33','بڵێ بێگومان پەروەردگارم تەنھا ئەو کردەوانەی حەرام کردوە کەزۆر بەد و خراپن ئەوانەی کە ئاشکران و ئەوانەش کە نھێنین وە گوناھی (حەرام کردووە) وە دەست درێژی بەناھەق وە (حەرامی کردوە) شتێک بکەنە ھاوبەش بۆخوا کەبەڵگەی بۆ نەھاتووە, وە (حەرامی کردووە) کەبەدەم خواوە شتانێک بڵێن کەنایزانن');
INSERT INTO "bamoki" VALUES(7,34,'7:34','وە بۆ ھەموو ئوممەتێ کاتێکی دیاری کراو ھەیە (بۆ مردن و سزادانیان) ئەمجا کاتێک ماوەی (مردن یان سزا) کەیان ھات ساتێک دواناکەون و پێشیش ناکەون');
INSERT INTO "bamoki" VALUES(7,35,'7:35','ئەی نەوەی ئادەم ئەگەر بۆتان ھات پێغەمبەرانێک لەخۆتان ئایەتەکانی منیان بۆ دەخوێندنەوە جا ھەر کەس خۆی پاراست (لە نا فەرمانی خوا) و کردەوەی چاکی کرد ئەوە مەترسیان لەسەر نیە وخەم و خەفەتیش ناخۆن');
INSERT INTO "bamoki" VALUES(7,36,'7:36','وە ئەوانەش کە بێ بڕوا بوون بەئایەتەکانی ئێمە و خۆیان بەزل دەزانی لەبەرانبەر ئەو ئایەتانەدا ئەوانە ھاوەڵانی ئاگرن ئەوانە بەھەمیشەیی تیایدا دەمێننەوە');
INSERT INTO "bamoki" VALUES(7,37,'7:37','جا کێ لەوە ستەمکارترە کە درۆ بەدەم خواوە ھەڵبەستێ یان بێ بڕوا بێت بەئایەتەکانی خوا ئەوانە ئەو بەشەی بۆیان نوسراوە دوو چاریان دەبێت ھەتا ئەو کاتەی نێردراوەکانمان (بۆ گیان کێشان) دێن بۆیان دەیانمرێنن و (گیانیان دەکێشن) پێیان دەڵێن ئەوانەی لەجیاتی خوا ئێوە ھاوارو نزاتان لێ ئەکردن لەکوێن (لەوەڵامدا) دەڵێن دیار نین و وون بوون لێمان وە شایەتی دەدەن لەسەر خۆیان کە بێگومان ئەوانە لە دونیادا بێ بڕوا بوون');
INSERT INTO "bamoki" VALUES(7,38,'7:38','(خوا پێیانی) فەرموو ئێوە لەگەڵ ئەو ئوممەتانەی کەلە پێش ئێوە وە بوون لە جنۆکەو ئادەمیزاد بچنە ئاگری دۆزەخەوە ھەر جارێک دەستەیەک دەچنە ناویەوە نەفرین لەھاوبیرانی دەکات ھەتا ھەموو دەگەن بەیەک و کۆدەبنەوە یەک دەگرن دوایینەکانیان (کەشوێن کەوتوو بوون) دەربارەی پێشوەکانیان (کەپێشەوا بوون) دەڵێن ئەی پەروەردگارمان ئەوانە ئێمەیان گومڕا کرد دە تۆش لەئاگردا دووقات سزایان بدە (پەروەردگار) دەفەرموێ بۆ ھەرکامتان (سزا) دوو بەرامبەرە بەڵام ئێوە نازانن و بێ ئاگان');
INSERT INTO "bamoki" VALUES(7,39,'7:39','سەرۆک و چاو لێکراوەکان بە شوێن کەوتووەکانیان دەڵێن ئێوەش ھیچ زیادە ڕێزێکتان بەسەر ئێمەدا نیە دە ئێوەش سزا بچەژن بەھۆی ئەو (خراپانەی) دەتانکرد');
INSERT INTO "bamoki" VALUES(7,40,'7:40','بەڕاستی ئەوانەی بێ بڕوا بوون بەئایەتەکانی ئێمە وە خۆیان بەگەورە و زل دادەنا لە بەرامبەریاندا ئەوانە دەرگاکانی ئاسمانیان بۆ ناکرێتەوە و ناچنە بەھەشتەوە ھەتا حوشتر نەچێت بەکونی دەرزی دروماندا (ئەمەش مەرجێکە ھەرگیز نایەتە دی) ئا بەو جۆرە تۆڵە لە تاوانباران دەسێنین');
INSERT INTO "bamoki" VALUES(7,41,'7:41','ئەوانەی لەدۆزەخدا فەرش و ڕاخەریان (لە ئاگرە) وە لەلای سەروشیانەوە لێفە و داپۆشەریان (لەئاگرە) ئا بەو جۆرە تۆڵە لەستەمکاران دەستێنین');
INSERT INTO "bamoki" VALUES(7,42,'7:42','وە ئەوانەی کە بڕوایان ھێناوە وکردەوەی چاکیشیان کردووە داوا لە ھیچ کەس ناکەین زیاد لەتوانای خۆی ئا ئەوانە ھاوەڵانی بەھەشتن ئەوان ھەمیشەیین تێیدا');
INSERT INTO "bamoki" VALUES(7,43,'7:43','ئەوەی لەدڵیاندا بووبێت دامانڕنی لەکینە و ڕق (دەربارەی یەکتری) ڕووبارەکان دەڕۆن بەژێر (کۆشک وباخەکان) یاندا وە دەڵێن سوپاس بۆ ئەو خوایەی کەڕێنمونی کردین بۆ ئەم ڕێگایە کە ئێمە ھەرگیز ڕێبازی ڕاست شارەزا نەدەبووین ئەگەر خوا ڕێنمونی نەکردینایە سوێند بەخوا بێگومان نێرراوەکانی پەروەردگارمان ڕاستیان ھێنا وە بانگیان لێ دەکرێت بەمەی کە ئەو بەھەشتە پێتان بەخشرا بەھۆی ئەو کردەوە (چاکانە وە بوو) کە دەتان کردن');
INSERT INTO "bamoki" VALUES(7,44,'7:44','وە بەھەشتیەکان بانگ دەکەن لەدۆزەخیەکان بێگومان ئێمە ئەوەی کەپەروەردگارمان بەڵێنی پێداین ڕاست بوو پێی گەیشتین جا ئایا ئێوەش ئەوەی کەپەروەردگارتان بەڵێنی پێدان ڕاست بوو پێی گەیشتن؟ دەڵێن بەڵێ پاشان جاڕچیەک جاڕی دەدا لەنێوانیاندا کەنەفرەتی خوا لەسەر ستەمکاران بێت');
INSERT INTO "bamoki" VALUES(7,45,'7:45','ئەوانەی بەرھەڵستی لە ڕێی خوا دەکەن و دەیانویست ئەو (ڕێگایە) بەخوارو ناڕێک نیشان بدەن لەکاتێکدا ئەوان بێ بڕوا بوون بەڕۆژی دوایی');
INSERT INTO "bamoki" VALUES(7,46,'7:46','و لەنێوانیاندا پەردە و جیاکەرەوەیەک ھەیە بەسەر ئەعرافەوە (کەبەرزاییەکە لەنێوان بەھەشت و دۆزەخدا) پیاوانێک ھەن ھەر یەکە لەبەھەشتی ودۆزەخیەکان دەناسنەوە بەھۆی نیشانە و (شێوەی تایبەتی) یانەوە جا بەھەشتیەکان بانگ دەکەن و دەڵێن: سەلامتان لێ بێت نەچوونەتە ناویەوە بەڵام ئومێدیان ھەیەو بەتەمان بچنە ناوى');
INSERT INTO "bamoki" VALUES(7,47,'7:47','ھەر کاتێک ڕوویان لادرێ و وەرگێڕدرێت بەرەو لای دۆزەخیەکان دەڵێن ئەی پەروەردگارمان مەمانخەنە ڕیزی گەلی ستەمکارانەوە');
INSERT INTO "bamoki" VALUES(7,48,'7:48','وە ئەوانەی وان بەسەر ئەعرافەوە پیاوانێک بانگ دەکەن کەبەنیشانەی تایبەتیاندا دەیان ناسن دەڵێن کۆمەڵەکەتان سوودی پێ نەگەیاندن ھەروەھا خۆبەزل زانین و لەخۆبایی بوونتان بەکەڵکتان نەھات');
INSERT INTO "bamoki" VALUES(7,49,'7:49','ئایا ئەوانە (ھەژارە باوەڕدارەکان) ھەر ئەوانە نین کەئێوە (لەدونیادا) سوێندتان دەخوارد کەخوا بەر میھرەبانی خۆیان ناخات (خوا بەھەژارە باوەڕدارەکان ئەڵێت) بچنە بەھەشت ھیچ مەترسیەکتان لەسەر نیە و ئێوە خەم ناخۆن');
INSERT INTO "bamoki" VALUES(7,50,'7:50','وە دۆزەخیەکان بانگ و ھاوار لەبەھەشتیەکان دەکەن (دەڵێن) ھەندێ ئاومان پێ ببەخشن یان ھەندێک لەو ڕۆزیەی کەخوا پێی بەخشیوون (بماندەنێ) (لەوەڵامیاندا) دەڵێن: خوا ھەردووکیانی لەسەر بێ بڕوایان حەرام کردووە');
INSERT INTO "bamoki" VALUES(7,51,'7:51','ئەوانەی کەئاینەکەی خۆیان کردبووە سەرقاڵی بێ سوود و گاڵتە جاڕی وە ژیانی دونیا ھەڵیخەڵەتاندن بۆیە ئەمڕۆ ئەوان لەبیرخۆمان دەبەینەوە ھەرچۆنێک ئەوان دیداری ئەم ڕۆژەیان لەبیری خۆیان بردبویەوە, وە (لەبەرئەوەی) ئایەتەکانی ئێمەیان ئینکار دەکرد (دانیان پێدا نەدەھێنا)');
INSERT INTO "bamoki" VALUES(7,52,'7:52','سوێند بەخوا بێگومان ئێمە کتێبێکمان بۆھێناون لەڕووی زانستەوە بەڕوونی شیمان کردوەتەوە کەڕێنمونی و میھرەبانیە بۆ کەسانێک بڕوا دەھێنن');
INSERT INTO "bamoki" VALUES(7,53,'7:53','ئایا چاوەڕوانی چی دەکەن بێجگە لە سەرئەنجامی ھەڕەشەکانی ئەو (قورئانە)؟ ئەو ڕۆژەی کە سەرئەنجامی (ھەڕەشەکانی) بەدی دێت ئەوانەی لەوە و پێش لەبیریان کردبوو دەڵێن بەڕاستی نێرراوەکانی پەروەردگارمان بەڕاستیەوە ھاتن جا ئایا (ئەمڕۆ) تکا کارانێک بۆ ئێمە ھەیە تکامان بۆ بکەن یان بگەڕێنرێینەوە بۆ دونیا ھەتا کردەوەی چاک بکەین نەک وەک ئەوەی دەمانکرد بێگومان خۆیانیان دۆڕاند ئەوەی ھەڵیان دەبەست وون بوو لێیان');
INSERT INTO "bamoki" VALUES(7,54,'7:54','بەڕاستی پەروەردگارتان ئەو خوایەیە کە ئاسمانەکان و زەوی لەشەش ڕۆژدا دروستکردووە پاشان (خوا) بەرز بووەوە سەر عەرش و قەراری گرت (ئێمە ئیمانمان پێیەتی بەڵام شێوەکەی نازانین پرسیار کرا لە ئیمامی مالیک دەربارەی (استواء) فەرمووی وەستانەکە حەقیقەتە و چۆنێتیەکەی نەزانراوە و پرسیارکردن دەربارەی بیدعـەتـە وباوەڕبوون پێی واجبە) بە (تاریکایی) شەو (ڕۆشنایی) ڕۆژ دادەپۆشێت (بەپێچەوانەشەوە) (ڕِۆژ بەشوێن شەوەوەیە بەخێرایی) وشـەو بەشوێن ڕۆژەوەیە بەخێرایی خۆر و مانگ و ئەستێرەکانیش ژێر بار کراوون بەفەرمانی خۆی بێداربن کەبەدیھێنان و فەرماندان ھەر بۆ خوایە پاک و بێگەردە (لەھەموو کەم و کوڕیەک) خوای پەروەردگاری ھەموو جیھانیان');
INSERT INTO "bamoki" VALUES(7,55,'7:55','لەپەروەردگارتان بپاڕێنەوە بەزەلیلی (و کڕوزانەوە) و بەنھێنی بێگومان خوا دەستدرێژی کارانی خۆش ناوێت');
INSERT INTO "bamoki" VALUES(7,56,'7:56','خراپەکاری مەکەن لەزەویدا پاش ئەوەی کەخوا چاکی کردووە, وە ھاوار و نزا بەرن بۆ خوا بەترس و ئومێدەوە بەڕاستی میھرەبانی خوا لەچاکەکارانەوە نزیکە');
INSERT INTO "bamoki" VALUES(7,57,'7:57','وە ئەو (خوا) زاتێکە بایەکان دەنێرێت بەموژدەدەر پێش (بارانی) رەحمەتی پەروەردگار ھەتا کاتێک ھەوری قورسی(باراناوی) ھەڵدەگرن دەینێرین بۆ ووڵاتێکی مردوو (وشکی بێ گیا) جا بەو (ھەورە) باران دەبارێنین بەھۆی ئەوەوە ھەموو بەرووبومێک دەردەھێنین (لە زەویدا) ھەر بەو وێنەیە مردووەکان لە زەوی دەردەھێنین (و زیندوویان دەکەینەوە) ھەتا ئێوە بیربکەنەوە');
INSERT INTO "bamoki" VALUES(7,58,'7:58','زەوی وخاکی پاک و بەپیت ڕووەک و شیناییەکەی بەفەرمانی پەروەردگاری دەڕوێت وە ئەوەی پیس و بێ خێرە دڕک و داڵ نەبێ لێی ناڕوێت و دەرنایەت ئا بەو جۆرە ئایەتەکانی خۆمان باس دەکەین بۆ گەلێک کە سوپاسگوزاربن');
INSERT INTO "bamoki" VALUES(7,59,'7:59','سوێند بەخوا بێگومان ئێمە نوحمان ڕەوانە کرد بۆ لای گەلەکەی ووتی: ئەی گەلەکەم تەنھا خوا بپەرستن ئێوە ھیچ پەرستراوێکتان نیە بێجگە لەو (لە خوا) بەڕاستی من دەترسم (دووچاری) ئێوە بێت سزای ڕۆژێکی گەورە (کە ڕۆژی دواییە)');
INSERT INTO "bamoki" VALUES(7,60,'7:60','کاربەدەست و دەسەڵاتدارانی گەلەکەی ووتیان بەڕاستی ئێمە تۆ لەگومڕاییەکی ئاشکرادا دەبینین');
INSERT INTO "bamoki" VALUES(7,61,'7:61','فەرمووی: ئەی گەلەکەم ھیچ گومڕاییەک لەمندا نیە بەڵکو من نێرراوم لەلایەن پەروەردگاری جیھانیانەوە');
INSERT INTO "bamoki" VALUES(7,62,'7:62','پەیامەکانی پەروەردگارم ڕادەگەیەنم پێـتان وە ئامۆژگاریتان دەکەم و ئەوەی من لەلایەن خواوە دەیزانم ئێوە نایزانن');
INSERT INTO "bamoki" VALUES(7,63,'7:63','ئایا سەرتان سوڕماوە لەھاتنی پەیام و یادخەرەوەیەک لەلایەن پەوەردگارتانەوە بۆ پیاوێک لەخۆتان (کەنوحە) بۆ ئەوەی ئاگادارتان کاتەوەو بتانترسێنێت و خۆتان بپارێزن (لەنا فەرمانی خوا) بۆ ئەوەی ڕەحمتان پێ بکرێت');
INSERT INTO "bamoki" VALUES(7,64,'7:64','دوای ئەوە بڕوایان پێ نەھێنا ئێمەش (نوح) مان ڕزگار کرد ھەروەھا ئەوانەش لە گەڵیدا بوون لەکەشتیەکەدا پاشان ئەوانەی بڕوایان نەبوو بە ئایەتەکانمان نوقممان کردن و خنکاندمانن بەڕاستی ئەوانە کۆمەڵی (دڵ) کوێران بوون');
INSERT INTO "bamoki" VALUES(7,65,'7:65','وە ناردمان بۆ لای گەلی عاد براکەیان کە ھوود (پێغەمبەرە) فەرمووی: ئەی گەلەکەم بەتەنھا خوا بپەرستن ھیچ پەرستراوێکتان نیە بێجگە لەو دەی ئایا ئێوە پارێزکار نابن ولەخودا ناترسن؟!');
INSERT INTO "bamoki" VALUES(7,66,'7:66','ئەو کاربەدەست و دەسەڵاتدارانەی کە بێ بڕوابوون لە گەلەکەی ووتیان بە ڕاستی ئێمە تۆ لەگێلی و نەفامیدا دەبینین وە بە ڕاستی وا گومان دەبەین کە تۆ لەدرۆزنان بیت');
INSERT INTO "bamoki" VALUES(7,67,'7:67','فەرمووی ئەی گەلەکەم ھیچ گێلی و نەفامی یەک لە مندا نیە بەڵکو من نێرراوێکم لەلایەن پەروەردگاری ھەموو جیھانیانەوە');
INSERT INTO "bamoki" VALUES(7,68,'7:68','پەیامەکانی پەروەردگارم بەئێوە ڕادەگەیەنم وە من بۆ ئێوە ئامۆژگاریکەرێکی دەست پاک و ئەمینم');
INSERT INTO "bamoki" VALUES(7,69,'7:69','ئایا سەرتان سوڕماوە لەھاتنی (نیگاو) ئامۆژگاری لەلایەن پەروەردگارتانەوە بۆ پیاوێک لەخۆتان ھەتا بتانترسێنێت وە بیری (چاکەکانی خوا) بکەنەوە کاتێ ئێوەی کرد بەجێنشین لەپاش گەلی نوح وە لەش وقەڵافەتی ئێوەی لەوان گەورەتر دروستکرد کەواتە بیری چاکەو بەھرەکانی خوا بکەنەوە بۆ ئەوەی ڕزگارتان ببێت');
INSERT INTO "bamoki" VALUES(7,70,'7:70','ووتیان ئایا بۆ ئەوە ھاتووی بۆ ناومان کە بەتەنھا خوا بپەرستین وە وازبھێنین لەوەی باوک و باپیرانمان دەیانپەرستن کەواتە ئەو (سزایەی) کەبەڵێنت پێ داوین بۆمان بێنە دی ئەگەر ڕاست دەکەیت و لەڕاست گۆیانی');
INSERT INTO "bamoki" VALUES(7,71,'7:71','فەرمووی: بێگومان لەلایەن پەروەردگارتانەوە تووشتان بووە سزاو خەشم و توڕەیی ئایا دەمە قاڵێم لەگەڵدا دەکەن دەربارەی چەند ناوێک کەئێوەو باو و باپیرانتان ناوتانە لەبتانێک کەخوا ھیچ بەڵگەیەکی لەسەریان نەناردۆتە خوارەوە جا کەواتە چاوەڕوانی بکەن منیش لەگەڵتان لەچاوەڕوانانم');
INSERT INTO "bamoki" VALUES(7,72,'7:72','ئەوسا ئەو و ئەوانەش لەگەڵی بوون و ( باوەڕیان پێ ھێنا بوو) بەمیھرەبانیەک لەلایەن خۆمانەوە ڕزگارمان کردن ئەوانەشمان لەڕیشە ھێنا کەئایەتەکانی ئێمەیان بەدرۆ دادەنا و باوەڕدار نەبوون');
INSERT INTO "bamoki" VALUES(7,73,'7:73','وە بۆ گەلی سەمودیش ساڵحی برایانمان نارد فەرمووی: ئەی گەلەکەم خوا بەتەنھا بپەرستن جگە لەو پەرستراوێکی ترتان نیە بێگومان موعجیزەیەکی ڕوونتان لەپەروەردگارتانەوە بۆ ھاتووە ئەمە حوشتری خوایە کە موعجیزەیەکە بۆ ئێوە جا لێی گەڕێن با بخوا و بلەوەڕێت لەزەوی خوادا و ئازاری مەدەن و دەستی بۆ مەبەن بەخراپە (ئەگەر وابکەن) تووشی سزایەکی سەخت و بەئازار دەبن');
INSERT INTO "bamoki" VALUES(7,74,'7:74','بیر بکەنەوە لەو کاتەی کەخوا ئێوەی گێڕا بەجێنشین لەپاش ھۆزی عاد لەزەویدا دایمەزراندن و نیشتەجێی کردن لەدەشتەکانیدا کۆشکی بەرز دروست دەکەن وە لەشاخەکانیدا، ماڵ و خانوو دائەتاشن دە بیربکەنەوە لەچاکە گەورەکانی خوا و (سوپاسی بکەن) و خراپەکاری مەکەن لەوڵاتدا و (مەبن بەخراپەکار و گێرە شێوێن)');
INSERT INTO "bamoki" VALUES(7,75,'7:75','دەسەڵاتدارە لووت بەرزەکانی گەلەکەی ووتیان بەوانەی لاوازو ژێر دەستەکرا بوون ئەوانەی بڕوایان ھێنا بوو لەناویاندا ئایا دەزانن کەێاڵح ڕەوانە کراوە لەلایەن پەوەردگاریەوە ووتیان بێگومان ئێمە بڕوادارین بەوەی کەێالح پێی ڕەوانە کراوە');
INSERT INTO "bamoki" VALUES(7,76,'7:76','ئەوانەی خۆیان بەزل دەزانی ووتیان بەڕاستی ئێمە بێ باوەڕین بەوەی کەئێوە بڕواتان پێ ھێناوە');
INSERT INTO "bamoki" VALUES(7,77,'7:77','ئەمجا حوشترەکەیان سەربڕی وە سەرکەشیان کرد لەفەرمانی پەروەدگاریان وە ووتیان: ئەی ساڵح ئەو بەڵێنی (سزایەی) بە ئێمەی دەدەیت بۆمان بێنەدی ئەگەر تۆ لەپێغەمبەرانی');
INSERT INTO "bamoki" VALUES(7,78,'7:78','بەھۆی ئەوەوە بومە لەرزەیەکی بەتین لێی دان ئیتر ھەموویان لەماڵ وخانووەکانیان دا بوون بە (لاشەی) بێ گیان و بێ جوڵە');
INSERT INTO "bamoki" VALUES(7,79,'7:79','جا ساڵح ڕووی لێ وەرگێڕان و (وازی لێ ھێنان) وە فەرمووی ئەی گەلەکەم سوێند بەخوا بەڕاستی پەیامی پەروەردگارم پێ ڕاگەیاندن وە ئامۆژگاریم کردن و دڵسۆز بوم بۆتان بەڵام ئێوە ئامۆژگارو دڵسۆزانتان خۆشناوێت');
INSERT INTO "bamoki" VALUES(7,80,'7:80','وە لوطمان نارد، کاتێک بەگەلەکەی ووت چۆن ئێوە ئەو کارە زۆر بەد و خراپە دەکەن کە (نێر بازیە) ھیچ کەس لەجیھاندا پێشتان نەکەوتوە لەو (کارەپیسە) دا');
INSERT INTO "bamoki" VALUES(7,81,'7:81','بەڕاستی ئێوە بەپیاوان ئارەزوتان دەشکێنن؟ لەجیاتی ئافرەتان؟ بەڵکو ئێوە دەستەیەکی زیادە ڕۆن');
INSERT INTO "bamoki" VALUES(7,82,'7:82','وە وەڵامی گەلەکەی ھیچ نەبوو بێجگە لەوەی کەووتیان لەشار و ووڵاتەکەتان (لوط و ئیماندارەکان) دەربکەن بەڕاستی ئەوانە کەسانێکن کەخۆیان پاک و تەمیز ڕادەگرن');
INSERT INTO "bamoki" VALUES(7,83,'7:83','ئەمجا ئێمە خۆی وشوێنکەوتوانیمان ڕزگار کرد جگە لەخێزانەکەی کە لەتیاچووان بوو');
INSERT INTO "bamoki" VALUES(7,84,'7:84','جا بارانيَكمان بةسةرا باراندن (بةرد بارانمان كردن) جا سةيركة سةرئةنجامى تاوان باران ضؤن بوو (وضيان ليَهات)');
INSERT INTO "bamoki" VALUES(7,85,'7:85','وە بۆ گەلی مەدیەنیش شووعەیبی برایانمان نارد فەرمووی ئەی گەلەکەم بەتەنھا خوا بپەرستن ھیچ پەرستراوێکتان نیە بێجگە لەو بێگومان لەلایەن پەروەردگارتانەوە بەڵگەو موعجیزەیەکی ڕوون و ئاشکراتان بۆ ھاتووە کەواتە لە پێوانە و کێشانەتان دا ڕێک و ڕاست بن و لەشت و مەکی خەڵکی کەم مەکەنەوە (بەدزی) وە لەزەویدا خراپەکاری مەکەن لەپاش چاکردنی ئەوەی باسکرا چاکترە بۆتان ئەگەر ئێوە ئیماندارن');
INSERT INTO "bamoki" VALUES(7,86,'7:86','وە لەسەر ھەموو ڕێگایەک دامەنیشن (رێگە مەگرن لەخەڵکی) ھەڕەشە بکەن و بەرھەڵستی ڕێگەی خوا بکەن لەو کەسەی باوەڕی پێ ھێنا بێت و بتانەوێت ڕێگای خوا بەلاری وچەوتی بخەنەڕوو وە بیری ئەو کاتە بکەنەوەکە ئێوە کەم بوون پاشان خوا زۆری کردن دەی سەرنج بدەن سەرئەنجامی خراپەکاران چۆن بوو و(چیان لێھات)');
INSERT INTO "bamoki" VALUES(7,87,'7:87','وە ئەگەر کۆمەڵێک لە ئێوە باوەڕیان ھێنا بێت بەو پەیامەی بەمندا نێردراوە، وە کۆمەڵێکی ترتان باوەڕیان نەھێنا بێت ئەوە ئارام بگرن ھەتا خوا بڕیار دەدات لەنێوانماندا وە ئەو (خوای گەورە) باشترینی بڕیاردەرانە');
INSERT INTO "bamoki" VALUES(7,88,'7:88','ئەوکاربەدەست ودەسەڵاتدارانەی کەخۆیان بەگەورە دائەنا لەناو گەلەکەی (شوعەیب) دا ووتیان ئەی شوعەیب بێگومان دەرت دەکەین وە ئەوانەش کە بڕوایان ھێناوە لەگەڵ تۆدا لە شارە کەمان یان دەبێ بگەرێنەوە سەر ئاینی ئێمە (شوعەیب) فەرمووی: ئەگەر حەزیش نەکەین و پێشمان ناخۆش بێت');
INSERT INTO "bamoki" VALUES(7,89,'7:89','بێگومان درۆیەکی گەورەمان بۆ خوا ھەڵبەستوە ئەگەر بگەڕێینەوە ناو ئاینەکەتان (کە بت پەرستیە) پاش ئەوەی کەخوا ڕزگاری کردین لێی ڕەوانیە بۆمان بگەڕێینەوە بۆ ناو ئەو (بت پەرستیە) مەگەر خوای پەروەردگارمان بیەوێت زانست و ئاگاداری پەروەردگارمان ھەموو شتێکی گرتۆتەوە تەنھا پشتمان بە خوا بەستووە (ووتیان): ئەی پەروەردگارمان، لەنێوان ئێمە و گەلەکەماندا حوکم و بڕیار بدە بەھەق تۆ باشترینی بڕیار دەرانی');
INSERT INTO "bamoki" VALUES(7,90,'7:90','دەستەو ئەشرافی ھۆزەکەی کە بێ بڕوا بوون ووتیان سوێند بە خوا ئەگەر ئێوە شوێن شوعەیب بکەون بەڕاستی ئەو کاتە زیان لێکەوتوان خۆتانن');
INSERT INTO "bamoki" VALUES(7,91,'7:91','جا بەھۆی ئەوە, وە بومەلەرزەیەکی توند لێیدان ھەموویان لەماڵەکانیاندا لەناوچوون (بێ گیان و جووڵە کەوتن)');
INSERT INTO "bamoki" VALUES(7,92,'7:92','ئەوانەی کە شوعەیبیان بەدرۆزانی (باوەڕیان پێ نەھێنا) (وا تیا چوون) وەک ھەر لەو ماڵانەدا نە بووبن (وتێیدا نەژیابن) ئەوانەی کە شوعەیبیان بە درۆزانی (باوەڕیان پێ نەھێنا) ھەر ئەوان خەسارۆمەند بوون');
INSERT INTO "bamoki" VALUES(7,93,'7:93','ئیتر (شوعەیب) ڕووی لێ وەرگێڕان فەرمووی پێیان: ئەی گەلەکەم سوێند بەخوا بەڕاستی من پەیامی پەروەردگارم بە ئێوە گەیاند و ئامۆژگاریم کردن جا چۆن بۆ ھۆزی بێ بڕواو کافر غەم و خەفەت بخۆم');
INSERT INTO "bamoki" VALUES(7,94,'7:94','ئێمە بۆ ھەر گوند و شارێک پێغەمبەرێکمان ڕەوانە کردبێت دانیشتوانیمان تووش کردووە بەنەبوونی و کەم دەرامەتی (بەزیان و نەخۆشی) تا لە پەروەردگار بپاڕێنەوە و پەشیمان ببنەوە');
INSERT INTO "bamoki" VALUES(7,95,'7:95','لەدوایدا خراپە و ناخۆشیەکەمان گۆڕی بەچاکە و خۆشی ھەتا زۆر بوون و گەشەیان کرد لەماڵ و سەروەتدا وە ووتیان: بێگومان باو و باپیرانمان تووشی زیان و ناخۆشی و نەخۆشی بوون جا لەناکاودا سزامان گرتنی لەکاتێکدا کە ھەستیان نەدەکرد');
INSERT INTO "bamoki" VALUES(7,96,'7:96','وە ئەگەر دانیشتوانی شار وگوندەکان باوەڕیان بھێنایە و خۆیان بپاراستایە (لە قەدەغە کراوەکانی خوا) بەڕاستی دەرگای بەرەکاتی ئاسمان و زەویمان بۆ دەکردنەوە بەڵام بڕوایان نەھێنا (بەپێغەمبەرەکەیان) ئەمجا ئێمەش ئەوانمان گرت و سزامان دان بەھۆی ئەو خراپانەوەی دەیانکرد');
INSERT INTO "bamoki" VALUES(7,97,'7:97','ئایا خەڵکی ئەو شار وگوندانە ترسیان نەبوو لەوەی سزای ئێمەیان بۆبێت لە شەودا کاتێک ئەوان خەوتوون');
INSERT INTO "bamoki" VALUES(7,98,'7:98','وە ئایاخەڵکی ئەو شار وگوندانە ترسیان نەبوو لەوەی کەسزای ئێمەیان بۆ بێت لە چێشتەنگاودا لەکاتێکدا یاری و گاڵتە دەکەن');
INSERT INTO "bamoki" VALUES(7,99,'7:99','جا ئایا لە پیلانی خوا نەترسان و بێ باک بوون دەی کەس لەپیلانی خوا دڵنیا نابێت جگە لەکەسانی زەرەرمەند نەبێت');
INSERT INTO "bamoki" VALUES(7,100,'7:100','ئایا ڕوون نەبۆتەوە بۆ ئەوانەی زەویان بە میرات بۆ دەمێنێتەوە دوای (تیا چوونی) خاوەنە پێشوەکانیان ئەگەر بمانەوێت بەھۆی گوناھەکانیانەوە دووچاری بەڵایان دەکەین وە مۆر دەنێین بەسەر دڵیاندا ئیتر ئەوان (ھیچ پەند وئامۆژگارییەک) نابیستن');
INSERT INTO "bamoki" VALUES(7,101,'7:101','ئەو شار وگوندانانەی (کە وێرانمان کردن) ھەندێک لە ھەواڵەکانی دەگێڕینەوە بۆت سوێند بەخوا بەڕاستی پێغەمبەرانیان بۆھات بە بەڵگە و نیشانەی ڕوونەوە بەڵام باوەڕیان نەھێنا بەوەی کەلەپێش دا بە درۆیان دەزانی ئا بەو جۆرە خوا مۆر دەنێت بەسەر دڵی بێ باوەڕاندا');
INSERT INTO "bamoki" VALUES(7,102,'7:102','زۆربەی ئەوانمان نەدی خاوەنی ھیچ پەیمانێک بن بەڕاستی زۆربەیانمان دی سەرپێچیکار و (تاوانبار) بوون');
INSERT INTO "bamoki" VALUES(7,103,'7:103','پاشان دوای ئەوانەی (کەباسمان کردن) موسامان نارد بە بەڵگە و موعجیزەکانمانەوە بۆ لای فیرعەون و دارو دەستەکەی بەڵام ئەوان بەنیشانەکان (بێ بڕوا بوون) و ستەمیان کرد جا بڕوانە و سەرنج بدە کە سەرەنجامی لە فەرمان دەرچووان و خراپەکاران چۆن بوو');
INSERT INTO "bamoki" VALUES(7,104,'7:104','موسا فەرمووی: ئەی فیرعەون بەڕاستی من پێغەمبەرێکم لەلایەن پەروەردگاری جیھانیانەوە');
INSERT INTO "bamoki" VALUES(7,105,'7:105','وا شیاوە و (سوورم) کە ھیچ شتێک نەڵێم بە ناوی خواوە جگە لە ھەق و ڕاستی بە ڕاستی لەلایەن پەروەردگارتانەوە بەڵگەی ڕوون و ئاشکرام بۆ ھێناون کەواتە بەنو ئیسرائیل لەگەڵ من بنێرە (بەرەڵایان کە، لە کۆیلەیی و چەوسانەوەت)');
INSERT INTO "bamoki" VALUES(7,106,'7:106','(فیرعەون) ووتی: ئەگەر بەڵگە و موعجیزە یەکت ھێناوە (ئەی موسی) ئەوە نیشانی بدە ئەگەر تۆ لە ڕاست بێژانی');
INSERT INTO "bamoki" VALUES(7,107,'7:107','ئەمجا موسا گۆچانەکەی فڕێدا یەکسەر گۆچانەکە بوو بەمارێکی گەورەی ئاشکرا');
INSERT INTO "bamoki" VALUES(7,108,'7:108','دەستی (لە باخەڵیەوە یان لە بنھەنگڵیەوە) دەرھێنا لە پڕ ئەو (دەستە) سپی و پرشنگداربوو بۆ بینەران');
INSERT INTO "bamoki" VALUES(7,109,'7:109','ھەندێک لە کاربەدەست ودەسەڵاتدارانی گەلەکەی فیرعەون ووتیان: بە ڕاستی ئەم (موسایە) جادوگەرێکی زۆر زانە');
INSERT INTO "bamoki" VALUES(7,110,'7:110','دەیەوێت لە خاک و ووڵاتی خۆتان دەرتان کات جا ئێوە فەرمان و راتان چیە (لەم بارەیەوە)');
INSERT INTO "bamoki" VALUES(7,111,'7:111','ئەوانیش ووتیان: کاری (موسا) وبراکەی دوابخە و کەسانێک بنێرە له شارانەكاندا ههموو (جادوگەرهكان) كۆبكهنهوه');
INSERT INTO "bamoki" VALUES(7,112,'7:112','ھەموو جادووگەرێکی شارەزات بۆ دەھێنن');
INSERT INTO "bamoki" VALUES(7,113,'7:113','جادوگەران ھاتن بۆ لای فیرعەون ووتیان: بێگومان ئێمە پاداشت و خەڵاتی گەورەمان دەبێت ئەگەر ھاتوو ئێمە سەرکەوتین (بەسەر موسا دا)؟');
INSERT INTO "bamoki" VALUES(7,114,'7:114','(فیرعەون) ووتی: بەڵێ پاداشتی گەورەتان دەبێت: و بێگومان ئێوە لەنزیکانی (خۆم) دەبن');
INSERT INTO "bamoki" VALUES(7,115,'7:115','(جادوگەرەکان) ووتیان ئەی موسا یاتۆ (ئەوەی پێتە) فڕێی دە یان ئێمە دەیھاوێژین و فڕێی دەدەین');
INSERT INTO "bamoki" VALUES(7,116,'7:116','(موسا) ووتی: ئێوە بیھاوێژن (ئەوەی پێتانە) ئەمجا کاتێ (ئەوەی پێیان بوو لە جادو) فڕێیاندا چاوبەستیان لە خەڵک کرد وترسێکی گەورەیان خستە دڵیانەوە, وە جادویەکی گەورەیان نواند');
INSERT INTO "bamoki" VALUES(7,117,'7:117','وەحیمان کرد بۆ موسا کە گۆچانەکەت فڕێ بدە ئیتر یەکسەر ئەو (گۆچانە بوو بەمار) و ھەرچی جادوی درۆینەیان کردبوو ھەڵی لووشی');
INSERT INTO "bamoki" VALUES(7,118,'7:118','ئیتر ڕاستی دەرکەوت و ڕوون بوویەوە ئەوانیش ھەرچی (سیحر) یان کرد بوو پووچەڵ بوویەوە');
INSERT INTO "bamoki" VALUES(7,119,'7:119','ھەر لەوێدا (فیرعەون و شوێنکەوتوانی) ژێر خران و دۆڕان و بە ڕسوایی گەڕانەوە');
INSERT INTO "bamoki" VALUES(7,120,'7:120','وە جادوگەران ھەموویان سوژدەیان برد (بۆ پەروەردگاریان)');
INSERT INTO "bamoki" VALUES(7,121,'7:121','(ساحیرەکان) ووتیان: ئێمە بڕوامان ھێنا بەپەروەردگاری جیھانیان');
INSERT INTO "bamoki" VALUES(7,122,'7:122','کە پەروەردگاری موسا و ھارونە');
INSERT INTO "bamoki" VALUES(7,123,'7:123','فیرعەون ووتی: پێش ئەوەی مۆڵەتان بدەم باوەڕتان پێ ھێنا! بەڕاستی ئەمە فێڵ و پیلانێکە (ئێوە و موسا) نەخشەتان بۆ کێشاوە لەم شارەدا تا دانیشتوانەکەی لێ دەربکەن جا لە دواییدا دەزانن (چیتان لێ دەکەم)');
INSERT INTO "bamoki" VALUES(7,124,'7:124','سوێند بێت دەست و قاچتان ببڕم بەپێچەوانەوە (چەپ و ڕاست) ئەمجا سوێند بێت گشتان ھەڵدەواسم');
INSERT INTO "bamoki" VALUES(7,125,'7:125','ووتیان: (بەفیرعەون) ئێمە دڵنیاین کە بۆ لای پەروەردگارمان دەگەڕێینەوە');
INSERT INTO "bamoki" VALUES(7,126,'7:126','تۆ ھیچ ڕەخنەیەکت لەئێمە نیە جگە لەوەی کە ئێمە باوەڕمان بەبەڵگە و نیشانەکانی پەروەردگارمان ھێناوە کاتێک بۆ مان ھات ئەی پەروەردگارمان ئارامیمان بەسەردا بڕێژە و بە موسوڵمانیەتی بمان مرێنە');
INSERT INTO "bamoki" VALUES(7,127,'7:127','ھەندێک لە دەسەڵاتداران وکاربەدەستانی گەلی فیرعەون ووتیان ئایا واز لە موسا و ھۆزەکەی دەھێنی بۆ ئەوەی لە ووڵاتدا (لە خاکی میسردا) خراپەکاری بکەن و دەست لەخۆت و پەرستراوەکانت ھەڵبگرن (فیرعەون) ووتی: لەمەودوا کوڕەکانیان دەکوژین و ئافرەتانیان دەھێڵینەوە بەزیندوویی (بۆ خزمەت کردن) وە بێگومان ئێمە دەسەڵاتدارین بەسەریاندا');
INSERT INTO "bamoki" VALUES(7,128,'7:128','موسا بەگەلەکەی ووت داوای یارمەتی لە خوا بکەن و خۆ ڕاگر بن بێگومان ھەموو زەوی موڵکی خوایە دەیدا بەھەرکەس لەبەندە (شیاوە) کانی کەخۆی بیەوێت و سەرئەنجامی چاک بۆ خواناسانە');
INSERT INTO "bamoki" VALUES(7,129,'7:129','(گەلەکەی) ووتیان: پێش ھاتنی تۆ بۆ ناومان ئازار دراین ھەروەھا پاش ھاتنی تۆ بۆ ناومان (بەپێغەمبەرایەتی) (موسا) فەرمووی: بەڵکو پەروەردگارتان دوژمنەکەتان لەناو بەرێت و ئێوە بکات بەجێنشینی (دوژمنەکانتان) لەسەر زەوی جا تەماشا دەکات ئێوە چۆن کار دەکەن');
INSERT INTO "bamoki" VALUES(7,130,'7:130','سوێند بەخوا بەڕاستی ئێمە دارودەستەی فیرعەونمان سزادا بە ووشکە ساڵی وقات وقڕی و کەم بوونەوەی بەرو بوومی میوە و کشتوکاڵەکانیان بۆ ئەوەی بیر بکەنەوەو (پەند وەربگرن)');
INSERT INTO "bamoki" VALUES(7,131,'7:131','جا کاتێک چاکەیان بۆ بھاتایە (لە سەروەت و بەروبووم) دەیانووت: ئەمە ھەر ھی ئێمەیە (شایستەی ئێمەیە) بەڵام کاتێک تووشی بەڵاو خراپە بوونایە (دەیانووت) بەھۆی شوومی موسا و شوێن کەوتووانیەوەیە بێداربن بێگومان ئەو (ناخۆشیانەی) بەلایانەوە شووم و خراپە لەلایەن خواوەیە (نەک موسا و ئیمانداران) بەڵام زۆربەیان بێ ئاگان ونازانن');
INSERT INTO "bamoki" VALUES(7,132,'7:132','دەیانووت (بە موسا) ھەر موعجیزەیەکمان بۆ بھێنی بۆ ئەوەی جادومان لێ بکەی پێی ئەوە ئێمە باوەڕت پێ ناھێنین');
INSERT INTO "bamoki" VALUES(7,133,'7:133','ئەوسا ئێمەش ناردمانە سەریان لافاو وکوللە و ئەسپێ جۆرەھا بۆق و خوێن کەچەند بەڵگەو موعجیزەیەکی ڕوون بوون کەچی خۆیان بە گەورە زانی (لەوەی کە بڕوا بھێنن) بێگومان ئەوانە گەلێکی تاوانبار بوون');
INSERT INTO "bamoki" VALUES(7,134,'7:134','کاتێکیش ئەو سزاو بەڵایان بەسەردا ھات ووتیان: ئەی موسا لە پەروەردگارت بپاڕێرەوە بۆمان وەک بەڵێنی بەتۆ داوە سوێندبێ گەر ئەم بەڵاو سزایەمان لەسەر لابەری بێگومان باوەڕت پێ دەھێنین و بەنو ئیسرائیلت لەگەڵدا دەنێرین');
INSERT INTO "bamoki" VALUES(7,135,'7:135','بەڵام کاتێک سزاکەمان لەسەریان لابرد تاگەیشتنە ماوەی دیاری کراو (بۆ تیاچوونیان) دەست بەجێ پەیمانەکەیان ھەڵوەشاندەوە');
INSERT INTO "bamoki" VALUES(7,136,'7:136','جا تۆلەمان لێ سەندن وە لە دەریادا خنکاندمانن لەبەر ئەوەی ئەوان باوەڕیان بە نیشانەکانی ئێمە نەبوو وھەمیشە لێیان بێ ئاگا بوون');
INSERT INTO "bamoki" VALUES(7,137,'7:137','ئیتر (لەجێگای گەلی فیرعەون) ئەو گەلەمان نیشتەجێ کرد کە ھەمیشە دەچەوسێنرانەوە لەڕۆژھەڵات و ڕۆژئاوای ئەو ووڵاتەدا کەپیت و بەرەکەتمان تێ خست بوو و بەڵێنی ھەرەجوانی پەروەردگارت بەتەواوی ھاتەدی بۆ نەوەی ئیسرائیل بەھۆی ئارامگریانەوە (لەسەر ناڕەحەتیەکان) وە ڕووخاندمان وتێکمان دا ئەو کۆشک و تەلارانەی فیرعەون و گەلەکەی دروستیان کردبوو ھەروەھا ئەو (مێوە و ڕەز وباخانەی) کە کەپریان بۆ کرد بوو');
INSERT INTO "bamoki" VALUES(7,138,'7:138','ئەمجا نەوەی ئیسرائیلمان لەو دەریا پەڕاندەوە (فیرعەون و گەلەکەیمان تێدا خنکاند) ئەمجا تووشی خەڵکانێک بوون بەردەوام خەریکی پەرستنی بتەکانیان بوون (جولەکەکانیش) ووتیان: ئەی موسا ھەروەک ئەو پەرستراوانەی ئەوان تۆیش پەرستراوێکمان بۆ دابنێ موسا فەرمووی: بەڕاستی ئێوە ھۆزێکی نەفامن');
INSERT INTO "bamoki" VALUES(7,139,'7:139','بێگومان ئەوانە ئەو (بت پەرستیەی) ئەوانی لەسەرە لە ناودەچێت وە ھەرچی دەکەن بەتاڵ و ھەڵوەشاوەیە');
INSERT INTO "bamoki" VALUES(7,140,'7:140','موسا) فەرمووی: ئایا جگە لە خوا داوای پەرستراوێکی ترتان بۆ بکەم؟ کە (ئەو خوایە) ڕێزی داوون بەسەر جیھانیاندا');
INSERT INTO "bamoki" VALUES(7,141,'7:141','وە (بیربکەنەوە) لەو وەختەی رزگارمان کردن لە دەست و پەیوەندەکەی فیرعەون کە ئەشکەنجە و ئازاری سەختیان دەدان کوڕانی ئێوەیان دەکوشت و ئافرەتانی ئێوەیان دەھێشتەوە (بۆ خزمەتی خۆیان) وە لەو (ئازاردانە) تاندا تاقی کردنەوەیەکی گەورە بوو لەلایەن پەروەردگارتانەوە');
INSERT INTO "bamoki" VALUES(7,142,'7:142','وە بەڵێنمان بە موسا دا سی شەو بەدە شەوی تر ماوەکەمان تەواوکرد ئەوسا وەختی دیداری پەروەردگاری بوو بە چل شەوی تەواو موسا بە ھارونی برای ووت تۆ لە ناو گەلەکەمدا جێنشینم بە و کاری چاک بکە و شوێنی ڕێبازی خراپەکاران مەکەوە');
INSERT INTO "bamoki" VALUES(7,143,'7:143','جا کاتێک موسا ھات بۆ کات و شوێنی دیاری کراومان (بۆ گفتوگۆکردن لەگەڵمان) و پەروەردگار ووتووێژی لەگەڵدا کرد ووتی: ئەی پەروەردگارم خۆتم نیشان بدە تا بت بینم (پەروەردگار) فەرمووی: تۆ (لە دونیادا) من نابینی بەڵام سەیری (لای) شاخەکە بکە جا ئەگەر ئەو شاخە لە شوێنی خۆی دا نەجوڵا ئەوە دوای کەمێک تۆش دەمبینی جا کاتێک پەروەردگاری خۆی دەرخست بۆ شاخەکە شاخەکەی وورد و ھان کرد و (لەگەڵ زەویدا تەختی کرد) و موساش بەبێ ھۆشی کەوت بەڕوودا جا وەختێ ھۆشی بەخۆیدا ھاتەوە (چاک بوویەوە) فەرمووی: پاکی و بێگەردی بۆ تۆ (ئەی پەروەردگار) بە پەشیمانی گەڕامەوە بۆ لات وە من یەکەمی بڕوا دارنم');
INSERT INTO "bamoki" VALUES(7,144,'7:144','(پەروەردگار) فەرمووی: ئەی موسا بێگومان من تۆم ھەڵبژاردوە بەسەر ھە موو خەڵکیدا بەپەیامەکانم و قسەکردنم (لەگەڵت ڕاستەوخۆ) کەواتە ئەوەی پێم بەخشیوی توند دەستی پێوە بگرە وە لە سوپاسگوزاران بە');
INSERT INTO "bamoki" VALUES(7,145,'7:145','و لەو پارچە تەختانەدا بۆمان نووسی لە ھەموو جۆرە باسێک پەند و ئامۆژگاری و باسی ھەموو شتێک بەڕوونی (کە پێویستیان پێی بێت) کەواتە (موسا) بەتوندی بیانگرە و ئاگاداریان بە وە فەرمان بە گەلەکەت بکە تا دەست بگرن و ڕەفتار بکەن بە جوانتر و چاکترینیان بەزوویی جێ و شوێنی لەسنوور دەرچووانتان نیشان دەدەم');
INSERT INTO "bamoki" VALUES(7,146,'7:146','لایان ئەدەم لە بەڵگە و نیشانە و ئایەتەکانم ئەوانەی کە خۆیان بە گەورە دادەنێن لەسەر زەویدا بەناھەق و ناڕەوا ئەوانە ئەگەر ھەموو ئایەت و موعجیزەیەک ببینن باوەڕی پێ ناھێنن وە ئەگەر ئایینی ڕاست و دروست ببینن نایکەن بە رێبازو ئایینی خۆیان وە ئەگەر ڕێگەی گومڕایی و ئاینی چەوت ببینن دەیکەن بە ئاین و ڕێباز بۆ خۆیان ھەموو ئەمانە لەبەر ئەوەیە کە بەڵگەو نیشانەکانی ئێمەیان بەدرۆ دەزانی وە ھەمیشە بێ ئاگا بوون لێیان');
INSERT INTO "bamoki" VALUES(7,147,'7:147','وە کەسانێک کە بەڵگە و نیشانەکانی ئێمەیان بە درۆزانی ھەروەھا دیداری ڕۆژی دواییان (بە درۆ زانی) کارو کردەوەی (چاکیان) بە ھیچ دەرچوو (پوچەڵ بوویەوە) ئایا جگە لەو (خراپانەی) کە دەیان کرد سزا دەدرێن؟');
INSERT INTO "bamoki" VALUES(7,148,'7:148','وە ھۆزی موسا پاش (رۆیشتنی) موسا دروستیان کرد لە خشڵ و زێڕەکانیان پەیکەری گوێرەکەیەک کە قۆڕەی (وەک گوێرەکەی) ھەبوو ئایا ئەوانە نەیانبینی کە بێگومان ئەو (گوێرەکەیە) قسەیان لەگەڵدا ناکات و ھیچ رێبازێکیشیان نیشان نادات کردیان بە (پەرستراو) و ئەوانە ستەمکاربوون');
INSERT INTO "bamoki" VALUES(7,149,'7:149','وە کاتێک لە دەستیان دەرچوو (زۆر پەشیمان بوونەوە) وە تێگەیشتن و بینیان کە بێگومان گومڕابوون ووتیان: سوێند بە خوا ئەگەر پەروەردگارمان بەزەیی پێماندا نەیەتەوە، وە لە گوناھەکانمان خۆش نەبێت ئەوەبەڕاستی ئێمە لە زیان لێ کەوتوان دەبین');
INSERT INTO "bamoki" VALUES(7,150,'7:150','جا کاتێک موسا گەڕایەوە ناو ھۆزەکەی بەڕق و توڕەییەکی زۆرەوە (موسا) ووتی (بە گوێرەکە پەرستەکان) لە دوای من چ جێنشینێکی بەد فەڕو خراپ بوون ئایا پەلەتان کرد لە فەرمانی پەروەردگارتان؟ (موسا) تەختەکانی (تەوراتی) فڕێدا وە (قژی) سەری (ھارونی) برای گرت و بەرەو خۆی ڕایکێشا (ھارون) ووتی: ئەی کوڕی دایکم بەڕاستی ئەم گەلە لاوازیان کردم نزیک بوو بمکوژن کەواتە دوژمنانم پێ دڵخۆش مەکە و لەگەڵ ھۆزی ستەم کاراندا داممەنێ');
INSERT INTO "bamoki" VALUES(7,151,'7:151','(موسا) فەرمووی: ئەی پەروەردگارم لە خۆم و براکەم خۆش بە وە بمانخەرە ناو بەزەیی و میھرەبانی خۆتەوە تۆ میھرەبان ترینی ھەموو میھرەبانانیت');
INSERT INTO "bamoki" VALUES(7,152,'7:152','بەڕاستی ئەو کەسانەی کە گوێرەکە پەرست بوون لەمەودوا دەکەونەبەر ڕق و خەشمی پەروەردگاریان ھەروەھا ڕسوایی و زەلیلی لە ژیانی دونیادا ھەر بەو جۆرە سزای درۆ ھەڵبەستان دەدەین');
INSERT INTO "bamoki" VALUES(7,153,'7:153','وە ئەوانەی گوناھیان کرد پاشان دوای ئەوە تەوبەیان کرد و باوەڕیان ھێنا بێگومان پەروەردگارت پاش ئەو (باوەڕھێنان و تۆبە کردنە) لێبوردە و میھرەبانە');
INSERT INTO "bamoki" VALUES(7,154,'7:154','جا کاتێ موسا توڕەییەکەی نیشتەوە تەختەکانی (تەوراتی) ھەڵگرتەوە و لە ڕەنووس و نــوسراوی ئەو (تەختانە) دا ڕێنمونی و بەزەیی گەورە ھەبوو بۆ کەسانێک کە لە پەروەردگاریان دەترسن');
INSERT INTO "bamoki" VALUES(7,155,'7:155','وە موسا لەگەلەکەی خۆی ھەڵیبژارد حەفتا پیاو بۆ کات و شوێنی دیاری کراو لەگەڵ ئێمە (بۆ ئەوەی لە کێوی طور تەوبە بکەن لە تاوانی گوێرەکە پەرستیەکەیان، جا کاتێک ھاتنە ئەوێ ووتیان بە موسا بڕوات پێ ناھێنین ھەتا خوا نەبینین تۆ خۆت قسەت لەگەڵ کرد با ئێمەش بیبینین) (خوا سزای دان وبومەلەرزەی بۆ ناردن) جا کاتێک کە بوومەلەرزەکە دایگرتن (موسا) فەرمووی: ئەی پەروەردگارم گەر بتویستایە لەمەو پێش ئەوان و منیشت لەناو دەبرد ئایا تۆ ھەموومان لەناو دەبەیت بەھۆی ئەوەی کە گێل ونەفامەکانمان ئەنجامیانداوە ئەمە تەنھا تاقی کردنەوەیەکی خۆتە ھەر کەسێکت بوێت بەو ھۆیەوە گومڕای دەکەیت وە ھەر کەسێکت بوێت ڕێنمونی دەکەیت ھەر تۆ پشتیوان و (پەرستراومانی) کەواتە لێمان خۆش ببەو ڕەحمان پێ بکە چونکە باشترین لێبوردە ھەر تۆی');
INSERT INTO "bamoki" VALUES(7,156,'7:156','(خوایە) لەم دونیادا چاکەمان بۆ بنووسە ھەروەھا لەڕۆژی دوایشدا بەڕاستی ئێمە بۆ لای تۆ گەڕاوینەتەوە (پەروەردگار) فەرمووی: سزاو ئازارم دووچاری ھەرکەسێک دەکەم کەبمەوێت وە بەزەیی و رەحمەتیشم ھەموو شتێکی گرتوەتەوە جا لەدواییدا ئەو (بەزەییەم) دەنووسم بۆ ئەوانەی پارێزکار و لەخوا ترسن و زەکات دەدەن ئەوانەش کە بڕوا بەبەڵگە و نیشانەکانی ئێمە دێنن');
INSERT INTO "bamoki" VALUES(7,157,'7:157','(ئەم ڕەحمەتەی پەروەردگار) بۆ ئەوانەیە کە شوێن (پێغەمبەرصلى الله عليه وسلم) کەوتوون کە پێغەمبەرێکی نەخوێندەوارە و (لەلایەن پەروەردگارەوە ھاتووە) ئەو کەسە (پێغەمبەرێکە ئەھلی کتاب سیفەتی دەزانن) چونکە بە نووسراوی لایانە لە تەورات و ئینجیلدا کە فەرمانیان پێ دەکات بە چاکە و ڕێگرییان لێ دەکات لە خراپە شتە خاوێن و پاکەکانیان بۆ حەڵاڵ دەکات وە شتە پیسەکانیان لێ حەرام و (قەدەغە) دەکات و بارە قورس وگرانەکانیان لەسەر لادەبات و ئەو کۆت و زنجیرانەش کە بەسەریانەوە بوون ئەمجا ئەو کەسانەی کە باوەڕیان بەو (پێغەمبەرە) ھێنابێت و ڕێزو قەدریان بۆ دانابێت یارمەتیان دابێت و پشتیوانیان لێ کردبێت و پەیڕەوی ئەو نوورەیان کردبێت (کە قورئانە) کە لەگەڵیدا نێرراوەتە خوارەوە ئەوان ھەموو (کە باسکران) لە ڕزگار بووانن');
INSERT INTO "bamoki" VALUES(7,158,'7:158','(ئەی مەحەممەد صلى الله عليه وسلم) بڵێ: ئەی خەڵکینە بەڕاستی من پێغەمبەری خوام بۆ ئێوە ھەمووتان ئەو (خوایەی) کەتەنھا بۆ ئەوە، موڵکی ھەموو ئاسمانەکان و زەوی ھیچ پەرستراوێکی ڕاست نیە جگە لەئەو دەژێنێت و دەمرێنێت کەواتە بڕوا بەخواو پێغەمبەرەکەی بھێنن کە نێراوێکی نەخوێندەوارە ئەویش باوەڕی بەخوا و بەووتەکانی ھەیە ئێوەش شوێنی بکەون و پەیڕەوی بکەن تا بکەونە سەر ڕێگای ڕاست');
INSERT INTO "bamoki" VALUES(7,159,'7:159','وەلەھۆزەکەی موسادا تاقمێکی گەورە ھەن کەڕێنمونی خەڵکی دەکەن بەحەق وڕاستی و ھەر بەحەقیش دادوەری دەکەن');
INSERT INTO "bamoki" VALUES(7,160,'7:160','ئەوان (جولەکە) مان کرد بە دوانزە تیرە و تایفەوە, وە وەحیمان بۆ موسا نارد کاتێک کە ھۆزەکەی داوای ئاویان لێ کرد (لە تینواندا) کە بە گۆچانەکەت بدە لەبەردەکە ئیتر دەست بەجێ لێی ھەڵقوڵا دوازدە کانی وسەرچاوە بێگومان ھەر تیرە و تایفەیەک جێگای ئاو خواردنی خۆی دەزانی وە ھەورمان کرد بە سێبەر بە سەریانەوە و گەزۆ و باڵندەی (شیلاقە) مان بۆ ناردن لەوڕزق و ڕۆزیە پاکانە بخۆن کە پێمان داوون وە ئەوانە (بەو تاوانانە) ھیچ ستەمێکیان لە ئێمە نەکرد بەڵکو ھەر ستەمیان لە خۆیان دەکرد');
INSERT INTO "bamoki" VALUES(7,161,'7:161','(بیربکەرەوە) کاتێک بەوان ووترا لەناو ئەو شارەدا (بیت المقدس) نیشتە جێ ببن لە (خواردنەکانی) ئەوێ بخۆن لە ھەر لایەک حەزتان کرد و بڵێن: (داوای) ئێمە سڕینەوەی گوناھەکانمانە و لە دەروازەی (شارەوە) بەسوژدە بردنەوە بڕۆنە ژوورەوە ئەوسا لە خراپەو تاوانەکانتان دەبوورێین و خۆش دەبین لەئایندەدا (پاداشتی) چاکەکاران زیاد دەکەین');
INSERT INTO "bamoki" VALUES(7,162,'7:162','کەچی ئەوانەیان کەستەمیان کرد گۆڕییان ئەو (ووتەی) کە پێیان ووترا بوو بەووتەیەکی تر ئێمەش (دوای ئەوە) سزایەکمان ناردە سەریان لە ئاسمانەوە بە ھۆی ئەوە, وە کە بەردەوام ستەمیان دەکرد');
INSERT INTO "bamoki" VALUES(7,163,'7:163','وە پرسیاریان لێ بکە دەربارەی ئەو شارەی کە لە نزیک دەریای (سور) بوو کە لە ڕۆژی شەممەدا دەست درێژی یان دەکرد (بەوەی ڕاوە ماسیان دەکرد لەکاتێکدا خوای گەورە لەو ڕۆژەدا ڕاوە ماسی لێ قەدەغە کردبوون) چونکە لە ڕۆژی شەممەکەیاندا ماسیە گەورەکان دەھاتن ودەردەکەوتن بە سەر ئاوەوە (خوای گەورو بەمە تاقی کردنەوە) وە ڕۆژێک کە شەممە نەبوایە ماسیە گەورەکان نەدەھات بۆیان ئا بەو جۆرە تاقیمان کردنەوە چونکە لە فەرمانی خوا دەردەچوون وسەر پێچیان دەکرد (بەوەی فێڵیان کرد، دەچوون لە ڕۆژی ھەینیدا تۆڕەکانیان دادەنا ، وڕۆژی یەک شەممەش ھەڵیان دەگرتەوە بەمەش فەرمانەکەی خوایان شکاند)');
INSERT INTO "bamoki" VALUES(7,164,'7:164','کاتێک تاقمێ لەوان (بەتاقمێکی تریانی) ووت بۆچی ئامۆژگاری کۆمەڵێک دەکەن کەخوا لەناویان دەبات یان بەسزایەکی سەخت سزایان دەدات ووتیان: تا لای پەروەردگارتان بەھانە و عوزر بێت و (تاوانبار نەبین لە سەر بێ دەنگیمان) و بەڵکو ئەوان لەخوا بترسن');
INSERT INTO "bamoki" VALUES(7,165,'7:165','ئەمجا کاتێک ئەوەی پێی ئامۆژگاری کرابوون لەبیریان چوو ئەوانەی بەرگرییان دەکرد لە خراپە وتاوان رزگارمان کردن ئەوانەش کە ستەمیان کرد سزامان دان بەسزایەکی زۆر خراپ بەھۆی دەرچوونیان لە فەرمانی خوا');
INSERT INTO "bamoki" VALUES(7,166,'7:166','جاکاتێک یاخی بوون لەوەی قەدەغە کرابوون لێی ئەوسا پێمان ووتن: ببنە چەند مەیمونێکی دوورخراوەی بێ نرخ');
INSERT INTO "bamoki" VALUES(7,167,'7:167','وە (ئەی موحەممەد صلى الله عليه وسلم) بیربکەرەوە کاتێک پەروەردگارت ڕایگەیاند و دای بە گوێچکەیاندا سوێندبێ بێگومان دەینێرێت بۆیان تا ڕۆژی دوایی کەسانێ کە سزای توند و بەدیان پێ بچێژێت بەڕاستی پەروەردگارت تۆڵەی خێرایە بەڕاستی لێ خۆش بوو و میھرەبانیشە');
INSERT INTO "bamoki" VALUES(7,168,'7:168','وە ئەوانمان لەسەر زەویدا بە چەند کۆمەڵێک دابەش کرد ھەندێکیان چاکەکارن وە ھەندێکیشیان وەک ئەوان نەبوون (بێ باوەڕ بوون) وە تاقیمان کردنەوە بەچەندەھا چاکەو خۆشی و بەچەندەھا خراپە و ناخۆشی بەئومێدی ئەوەی بگەڕێنەوە (بۆ لای خوا)');
INSERT INTO "bamoki" VALUES(7,169,'7:169','دوای ئەوان جێ نشینانێکی خراپ ھاتنە جێگەیان کە میراتگری تەورات بوون کە ھەر بۆ ماڵ وسامانی (حەرامی) ئەم دونیایە ھەوڵیان ئەدا ئەوەشیان ھەڵبژارد وە دەیانووت بێگومان ئێمە عەفو دەکرێین لەکاتێکدا ئەگەر بۆیان بێت کاڵایەکی وەک ئەوی تر ئەگەر چی خراپیش بوایە وەریان دەگرت ئایا لێیان وەرنەگیراوە و پێویست نە کراوە لەسەریان پەیمان لە تەوراتدا کە بەدەم خواوە ھیچ نەڵێن بێجگە لە ڕاستی نەبێت ئەوەی لە تەوراتدا ھەبوو خوێندویانەتەوە, وە ڕۆژی دوایی و (دەست کەوتی ئەو ڕِۆژە) چاکتر و باشترە بۆ ئەوانەی کە خۆ پارێزو لە خواترسن ئایا ھێشتا ژیر نابن؟');
INSERT INTO "bamoki" VALUES(7,170,'7:170','وە ئەوانەی بەتوندی دەست دەگرن بەتەوراتەوە (و پەیڕەویان دەکرد پێش ھاتنی ئیسلام) وە نوێژەکانیان بەجێ دەھێنن بێگومان ئێمە وونی ناکەین پاداشتی چاکەکاران');
INSERT INTO "bamoki" VALUES(7,171,'7:171','(بھێنەوە بیریان) کاتێک کە کێوی (طور) مان بەرزکردەوە بەسەریاندا وەکو ھەورێک (سێبەرێک) بوو وە لایان وابوو کە دەکەوێت بە سەر یاندا (پێمان ووتن) ئەوەی کە پێمان داوون بەتوندی بیگرن وە ئەوەی تێدایە (لە ئامۆژگاری و حوکم) بەردەوام لە بیرتان بێت بەڵکو ببنە پارێزکار و لەخواترس');
INSERT INTO "bamoki" VALUES(7,172,'7:172','وە کاتێ کە پەروەردگارت وەریگرت (دەری ھێنا) لە پشتی ئادەمیەکان وەچە و نەوەکانیان وەکردنی بە شایەت بەسەر خۆیانەوە (پێی ووتن) ئایا من پەروەردگاری ئێوە نیم ؟ (لە وەڵامدا) ووتیان: بەڵێ شایەتیماندا تا نەڵێن لە ڕۆژی دواییدا بەڕاستی ئێمە بێ ئاگا بووین لەمە (کەخوا پەروەردگارمانە)');
INSERT INTO "bamoki" VALUES(7,173,'7:173','یا نەوەکو بڵێن: بێگومان باوو باپیرانمان ھاوبەشیان بڕیار داوە لەپێش (ئێمەوە) وەئێمەش وەچە و نەوەی دوای ئەوان بووین دەی ئایا لەناومان دەبەیت بەوەی کە بێ فەڕو خراپەکاران کردوویانە');
INSERT INTO "bamoki" VALUES(7,174,'7:174','ئا بەو جۆرە نیشانەکانی خوا ڕوون دەکەینەوە بەڵکو (بەرەو ھەق) بگەڕێنەوە');
INSERT INTO "bamoki" VALUES(7,175,'7:175','وە (ئەی موحەممەد صلى الله عليه وسلم) بخوێنەرەوە بەسەریاندا ھەواڵی ئەو کەسەی کە نیشانەکانی خۆمان پێ بەخشی کەچی خۆی لێ داڕنی (و دەرچوو لە فەرمانی خوا) ئەمجاشەیتان دوای کەوت بەھۆی ئەوەوە بو بەیەکێک لە گومڕایان');
INSERT INTO "bamoki" VALUES(7,176,'7:176','ئەگەر بمان ویستایە بەو ئایەتانە پلەیمان بەرز دەکردەوە بەڵام ئەو خۆی نوساند بە زەوییەوە و دوای ئارەزووی خۆی کەوت نموونەی ئەو وەک ئەو سەگەیە ئەگەر لێی بدەیت و دەریکەیت (مۆڕەت لێ دەکات) و زمانت لێ دەردەھێنێ وە ئەگەر وازیشی لێ بھێنی ھەر زمان دەردەھێنێ ئەو (سیفەتانە) نموونەی ئەو کەسانەیە کە نیشانەکانی ئێمەیان بەدرۆ دانا دەی ئەو داستان و چیرۆکانە باس بکە تا بچنەوە بەخۆیاندا و بیر بکەنەوە');
INSERT INTO "bamoki" VALUES(7,177,'7:177','بەدو خراپە وێنە و (چیرۆکی) ئەو کەسانەی کە نیشانەکانی ئێمەیان بەدرۆ دانا ئەوانە ھەمیشە ستەمیان لە خۆیان دەکرد');
INSERT INTO "bamoki" VALUES(7,178,'7:178','ھەر کەس خوا ڕێنمونی بکات ھەر ئەو ڕێنمونی کراوە ھەر کەسیش (خوا بەھۆی کردەوەکانیەوە) گومڕای بکات ھەر ئەوانە زیان لێ کەوتوو رەنجەڕۆن');
INSERT INTO "bamoki" VALUES(7,179,'7:179','وە سوێند بەخوا بەڕاستی دروستمان کردووە بۆ دۆزەخ زۆرێک لە جنۆکە و ئادەمی دڵیان ھەیە بەڵام پێی تێناگەن (لە ڕاستی) و چاویان ھەیە (ھەقی) پێ نابینن و گوێیان ھەیە (ھەقی پێ) نابیستن ئەوانە وەک ئاژەڵ (وچوار پێ) وان بگرە خراپتر و گومڕا ترن (لە ئاژەڵ) ھەر ئەوانەن بێ ئاگایان');
INSERT INTO "bamoki" VALUES(7,180,'7:180','ھەر بۆ خوایە جوانترین و چاکترینی ناوەکان جا ئێوەش بەو ناوانە بانگ و ھاواری لێ بکەن واز بھێنن و نرخ دامەنێن بۆ ئەوانەی کە لەناوەکانی (خوا) دا گۆڕان و لادان دەکەن (دەیکەنە ناو بۆ بتەکانیان) بەزوویی تۆڵەی ئەوەی دەیانکرد لێیان دەسەنرێت');
INSERT INTO "bamoki" VALUES(7,181,'7:181','وە لەوانەی کە دروستمان کردووە کۆمەڵێک ھەیە ڕێنمونی خەڵک دەکەن بە (ئاینی) ڕاست و داد وەریشی پێدەکەن');
INSERT INTO "bamoki" VALUES(7,182,'7:182','وە ئەوانەی کە نیشانەکانی ئێمەیان بەدرۆ دانا ووردە ووردە (پلە بە پلە) نزیکیان دەکەینەوە لە تیاچوون بەجۆرێک کە خۆیشیان بەخۆیان نازانن');
INSERT INTO "bamoki" VALUES(7,183,'7:183','ماوەیەک مۆڵەتیان دەدەم بەڕاستی پیلانی من زۆر پتەو و بەھێزە');
INSERT INTO "bamoki" VALUES(7,184,'7:184','ئایا ئەوان بیریان نەکردوەتەوە کەھاوڕێ کەیان (موحەممەد صلى الله عليه وسلم) ھەرگیز تووشی شێتی نەبووە ئەو تەنھا ترسێنەرێکی ئاشکرایە (بۆ ئێوە)');
INSERT INTO "bamoki" VALUES(7,185,'7:185','ئایا ئەوانە ناڕوانن؟ لەو موڵکە گەورە و فراوانەی لەئاسمانەکان و زەوی دا ھەیە وە لەو شتانەی خوای گەورە دروستی کردوون وەلەوانەیە نزیک بوو بێـتەوە کۆتایی (ژیان) یان جا بە چ قسەیەک لە پاش قورئان بڕوا دەھێنن');
INSERT INTO "bamoki" VALUES(7,186,'7:186','ھەر کەس خوا گومڕای بکات (بەتاوانی کردەوەی خراپی) ئەو ھیچ ڕێنمونی کەرێکی بۆ نیە (خوا) وازیان لێ دەھێنێت لە گومڕایی و یاخی بوونی خۆیاندا گێژ بخۆن');
INSERT INTO "bamoki" VALUES(7,187,'7:187','(ئەی موحەممەد صلى الله عليه وسلم) پرسیارت لێ دەکەن لەبارەی ڕۆژی دواییەوە کەی دەبێت بڵێ: زانینی ئەوە تەنھا لای پەروەردگارمە ھیچ کەس (ناتوانێت) کاتەکەی ئاشکرا بکات جگە لەپەروەردگار نەبێت (ھاتنی قیامەت) بۆ ئاسمانەکان و زەویش گران و قورسە نایەت بەسەرتاندا (ڕوونادات) لەناکاودا نەبێت لە تۆ دەپرسن وەک ئەوەی کە تۆ لە (ھاتنی) ڕۆژی دوایی ئاگادار بیت (ئەی موحەممەد صلى الله عليه وسلم) بڵێ: بێگومان زانینی (ھاتنی) ئەو ڕۆژە تەنھا لای خوایە بەڵام زۆرێک لە خەڵکی نایزانن');
INSERT INTO "bamoki" VALUES(7,188,'7:188','(ئەی موحەممەد صلى الله عليه وسلم) بڵێ: من خاوەنی ھیچ سوود و زیانێک نیم بۆخۆم مەگەر ئەوەی خوا بیەوێت ئەگەر من غەیب و شتە نادیارەکانم بزانیایە خێرو خۆشی زۆرم بەدەست دەھێنا وەھیچ خراپەو زیانێکم پێ نە دەگەیی من تەنھا ترسێنەرو مژدەدەرم بۆ خەڵکانێک کە باوەڕ دەھێنن');
INSERT INTO "bamoki" VALUES(7,189,'7:189','(خوا) ئەو زاتەیە کە ئێوەی دروستکردوە لەیەک کەس (کە ئادەمە) ھەر لە خۆی ھاوسەری بەدیھێنا تا لەلای بحەسێتەوە (ئارام بگرێت) جا کاتێک (پیاوەکە) لەگەڵ ھاوسەرەکەی جووت بوو (دروست بوو) باری سووکی ھەڵگرت (سکی پڕبوو) ئەمجا لەسەر ئەم (سک پڕیە) ماوەیەک تێپەڕی جا کاتێک (سکەکەی) قورس بوو (ژن و مێردەکە) ھەردوو لە خوا پاڕانەوە سوێند بێت ئەگەر تۆ منداڵێکی چاک و تەواومان پێ بدەیت بێگومان لە سوپاس گوزاران دەبین');
INSERT INTO "bamoki" VALUES(7,190,'7:190','بەڵام کاتێک (نەوەکانی ئادەم وحەوا پاش خۆیان) پەروەردگار مناڵێکی چاکی پێدان چەند ھاوبەشێکیان بۆخوا دانا (وەک ئەوەی بتپەرستەکان دەیانکرد) لەوەی کەپێی دابوون خوا لەوە بەرزو بڵند ترە کە ھاوبەشی بۆ دابنێن');
INSERT INTO "bamoki" VALUES(7,191,'7:191','ئایا شتانێک دەکەن بە ھاوبەشی خوا کە ناتوانن ھیچ شتێک دروست بکەن لەکاتێکدا خۆشیان دروست کراون');
INSERT INTO "bamoki" VALUES(7,192,'7:192','وە (ئەو بت و پەرستراوانە) ناتوانن ھیچ یارمەتیەکیان بدەن ھەروەھا ناتوانن یارمەتی خۆیشیان بدەن');
INSERT INTO "bamoki" VALUES(7,193,'7:193','وە ئەگەر ئێوە ئەوان (بتەکان) بانگ بکەن بۆ ڕێی ڕاست شوێنتان ناکەون بۆ ئێوە یەکسانە چ بانگیان بکەن یان بێ دەنگ بن (سوودتان پێ ناگەیەنن)');
INSERT INTO "bamoki" VALUES(7,194,'7:194','بەڕاستی ئەوانەی کە ئێوە ھاوار و داوایان لێ دەکەن جگە لە خوا ئەوانیش چەند بەندەیەکن وەک خۆتان دەی بانگ وھاواریان لێ بکەن با وەڵامتان بدەنەوە (ویستەکانتان بە جێ بھێنن) ئەگەر ئێوە ڕاست گۆن');
INSERT INTO "bamoki" VALUES(7,195,'7:195','ئایا ئەوانە (ھیچ نەبێ وەک خۆتان) قاچیان ھەیە کە پێی بڕۆن بەڕێدا یان دەستیان ھەیە کەدەستی پێ بوەشێنن یان چاویان ھەیە کەپێی ببینن یان گوێیان ھەیە کەپێی ببیستن (ئەی موحەممەد صلى الله عليه وسلم) بڵێ: بانگی ھاوبەشەکانتان بکەن پاشان نەخشە وپیلانم بۆدانێن ومۆڵەتیشم مەدەن');
INSERT INTO "bamoki" VALUES(7,196,'7:196','بەڕاستی پشتیوان و دۆستی من ھەر خوایە ئەو (خوایەی) کەقورئانی ناردە خوارەوە, وە ھەر ئەویش پشتیوانی چاکەکاران دەکات');
INSERT INTO "bamoki" VALUES(7,197,'7:197','وە ئەوانەی ئێوە جگە لە خوا ھانایان بۆ دەبەن ناتوانن یارمەتیتان بدەن ناشتوانن یارمەتی خۆیان بدەن');
INSERT INTO "bamoki" VALUES(7,198,'7:198','وە ئەگەر بانگیان بکەن بۆ ڕێگای ڕاست ئەوان نابیستن وە وادەزانیت بۆ تۆ دەڕوانن لەکاتێکدا ئەوان ھیچ نابینن');
INSERT INTO "bamoki" VALUES(7,199,'7:199','دەست بگرە بە لێبوردەییەوە (چاوو پۆشی بکە) فەرمانیش بدە بە چاکە و چاکە کردن و ڕوو وەرگێڕە لە نەفامان و نەزانان (وازیان لێ بھێنە)');
INSERT INTO "bamoki" VALUES(7,200,'7:200','وە ئەگەر خەتەرە و خەیاڵ و وەسوەسەیەکت بۆ ھات لە لایەنی شەیتانەوە یەکسەر پەنا بگرە بەخوا بەڕاستی ھەر ئەو بیسەری زانایە');
INSERT INTO "bamoki" VALUES(7,201,'7:201','بەڕاستی ئەوانەی خواناس و پارێزکار بوون کاتێک خەتەرو وەس وەسەیەکیان لەشەیتانەوە پێ بگات یادی خوا دەکەن و خوایان بیر دەکەوێتەوە جا کتوپڕ بەرچاویان ڕۆشن دەبێتەوە (ڕاستی دەدۆزنەوە)');
INSERT INTO "bamoki" VALUES(7,202,'7:202','وە برا (شەیتانە) کانیان یارمەتیان دەدەن لەگومڕایی بووندا پاشان کۆتایی ناھێنن و واز ناھێنن (بۆ گومڕا کردنیان)');
INSERT INTO "bamoki" VALUES(7,203,'7:203','وە ھەر کاتێک (وەحی دوا بکەوێت) ئایەتێکیان بۆ نەھێنی دەیانووت: بۆچی لەخۆتەوە ڕێکی ناخەیت (بۆ خۆت ھەڵی نابەستی) (ئەی موحەممەد صلى الله عليه وسلم) بڵێ: بێگومان من ھەر شوێنی ئەوە دەکەوم کە لەلایەن پەروەردگارمەوە وەحیم بۆ دەکرێت ئەم (قورئانە) بەرچاو ڕۆشنی و بەڵگەیە لەلایەن پەروەردگارتانەوە و ڕێنمونی و نیشاندەرە و میھرەبانی و بەزەیی خوایە بۆ گەل و ھۆزێک کە باوەڕ دەھێنن');
INSERT INTO "bamoki" VALUES(7,204,'7:204','وە کاتێک کە قورئان خوێندرا دەبێت گوێی لێ بگرن و بێ دەنگیش بن بۆ ئەوەی خوا ڕەحمتان پێ بکات');
INSERT INTO "bamoki" VALUES(7,205,'7:205','وە لە (دڵ) و دەروونتدا یادی پەروەردگارت بکە بە پاڕانەوە و ترس و بیمەوە بەبێ ئەوەی دەنگ بەرزبکەیتەوە لە بەرەبەیانی و دەماو دەمی ئێواراندا وە لە بێ ئاگایان مەبن');
INSERT INTO "bamoki" VALUES(7,206,'7:206','بە ڕاستی ئەو فریشتانەی کە لای پەروەردگارتن خۆ بە زل نازانن لە پەرستنی ئەو (خوا) و ھەمیشە ستایشی دەکەن (کڕنووشی بۆ دەبەن) و تەنھا سوژدە بۆ ئەو دەبەن');
INSERT INTO "bamoki" VALUES(8,1,'8:1','(ئەی موحەممەد صلى الله عليه وسلم) پرسیارت لێ دەکەن سەبارەت بە دەسکەوتەکانی (جەنگی بەدر) تۆش بڵێ: ئەو دەست کەوتانە ھەر بۆ خوا و پێغەمبەرن صلى الله عليه وسلم کەواتە خۆتان بپارێزن و لە خوا بترسن وە پەیوەندی نێوان خۆتان چاک بکەنەوە وگوێڕایەڵی خواو پێغەمبەرەکەی بن ئەگەرئێوە بڕوادارن');
INSERT INTO "bamoki" VALUES(8,2,'8:2','بێگومان بڕواداران تەنھا کەسانێکن کاتێک کە باسی خوا کرا و ناوی ھێنرا دڵیان دەترسێت و ترسی لێ دەنیشێت کاتێکیش ئایەتەکانی خوا بخوێنرێتەوە بەسەریاندا بڕوایان (پێی) زیاد دەبێت و ھەر پشت بە پەروەردگاری خۆیان دەبەستن');
INSERT INTO "bamoki" VALUES(8,3,'8:3','ئەوانەن کە بەڕاستی نوێژەکانیان بەجێ دەھێنن وە لەو ڕزق و ڕِۆزیەی پێمان داوون (لە پێناوی خوادا) دەیبەخشن');
INSERT INTO "bamoki" VALUES(8,4,'8:4','ھەر ئەوانەن بڕوادارانی ڕاستەقینە پلەی بەرزیان بۆ ھەیە لای پەروەردگاریان و لێ خۆشبوونی (تاوانەکانیان) و ڕۆزی پەسەند و چاکیان بۆ ھەیە');
INSERT INTO "bamoki" VALUES(8,5,'8:5','ھەر چۆنێک کە پەروەردگارت لە ماڵەکەت ( لەشاری مەدینە) دەریھێنایت (بۆ جەنگی بەدر) بەھەق و ڕاستی لەکاتێکدا بێگومان دەستەیەک لەبڕواداران بێزاربوون و پێیان ناخۆش بوو');
INSERT INTO "bamoki" VALUES(8,6,'8:6','دەمەقاڵێت لەگەڵدا دەکەن لە (شتی) ھەق و ڕاستدا دوای ئەوەش کە بۆیان ڕوون بوەتەوە (کە ڕاستە ھێشتا جەنگیان لا گرانە) وەک ئەوەی بە توندی بەرە و مردن ببرێن و بەچاوی خۆیان (مردن) ببینن');
INSERT INTO "bamoki" VALUES(8,7,'8:7','(بیری ئەو کاتە بکەنەوە) کەخوا بەڵێنی بەئێوەدا دەستکەوتی یەکێک لەو دوو کۆمەڵە (کاروانە بازرگانیەکە یان سەرکەوتن بە سەریاندا) بەڕاستی دەبێتە ھی ئێوە, وە ئێوە ئەوەتان پێ خۆشە کە ئەو دەستکەوتە بێ چەکە، (کاروانە بازرگانیەکە) بۆ ئێوە بێت بەڵام خوا دەیەوێت ووتەکانی خۆی بەڕاست دەرچێت و (حەق سەرکەوێت) و ڕەگ و ڕیشەی بێ باوەڕان لەبن بھێنێت');
INSERT INTO "bamoki" VALUES(8,8,'8:8','تا ھەق جێگیر بکات و ناحەقیش لەناو بەرێت و پوچەڵی بکاتەوە با تاوانبارانیش پێیان ناخۆش بێت');
INSERT INTO "bamoki" VALUES(8,9,'8:9','(بیربکەنەوە) کاتێک (لە بەدردا) ھاناتان بۆ پەروەردگارتان دەبرد خواش ھات بەھاناتانەوە ووتی من دەینێرم بۆ یارمەتیان ھەزار فریشتە بە دوای یەکدا (دێنە خوارێ)');
INSERT INTO "bamoki" VALUES(8,10,'8:10','وە خوا ئەوەی تەنھا بۆ شادی و موژدە نارد تا دڵەکانتان پێی ئارام بگرێت وە سەر کەوتنیش تەنھا لای خواوەیە و بەس بەڕاستی خوا بەدەستەڵاتی کاربەجێیە');
INSERT INTO "bamoki" VALUES(8,11,'8:11','(بیربکەنەوە) کاتێک (خوا) وەنەوزی بەسەرتاندا ھێنا (بووە ھۆی) دڵنیایی ئێوە لە لایەن (پەروەردگار) خۆیەوە بارانیشی لە ئاسمانەوە بۆ باراندن تا پاکتان بکاتەوە پێی و پیسی شەیتان (دوو دڵی و گومان) لەئێوە دوور بخاتەوە و پەیوەندی دڵەکانتان پتەو بکات وە بەو (بارانە) پێکانتان دامەزراو و جێگیر بکات (لەگۆڕەپانی جەنگدا)');
INSERT INTO "bamoki" VALUES(8,12,'8:12','(بیھێنەوە بیر) کاتێک کە پەروەردگارت نیگای نارد بۆ فریشتەکان (کە بە پێغەمبەر وباوەڕداران ڕابگەیەنن) کە بێگومان من (خوای گەورە) لەگەڵتانم دەی دڵی کەسانێک قایم و دامەزراوکەن کە باوەڕیان ھێناوەبە زوویی ترس و بیم دەخەمە دڵی ئەو کەسانەی کە بێ باوەڕ بوون جا ئێوەش لەسەرووی گەردنیان بدەن (واتە: سەریان) وە لە ھەموو سەر پەنجەکانیان بدەن (واتە لە ھەموو لاشەیان بدەن لەسەرەوە تا خوارەوە)');
INSERT INTO "bamoki" VALUES(8,13,'8:13','ئەوە لەبەر ئەوەی کە بەڕاستی ئەوانە دژایەتی خوا و پێغەمبەرەکەیان کرد ھەر کەسێکیش دژایەتی خوا و پێغەمبەرەکەی بکات ئەوە بێگومان تۆڵە و سزای خوا زۆر سەخت وبەتینە');
INSERT INTO "bamoki" VALUES(8,14,'8:14','(حوکم و سزای ئێوە) ئەوەیە دەی بیچەژن وە بەڕاستی بۆ بێ بڕوایان سزای ئاگری دۆزەخ ھەیە');
INSERT INTO "bamoki" VALUES(8,15,'8:15','ئەی ئەو کەسانەی کە بڕواتان ھێناوە کاتێک (لەجەنگدا) ڕووبەڕووی زۆرێک لەبێ باوەڕان بوونەوە قەت پشتیان تێ مەکەن و ھەڵمەیەن');
INSERT INTO "bamoki" VALUES(8,16,'8:16','وە ھەر کەس لەو ڕۆژەدا پشتیان تێ بکات و ھەڵبێت مەگەر (مەبەستی ئەوەبێت) بۆ ھێرش کردن بگەڕێتەوە سەریان یان بڕوات بۆ لای کۆمەڵێکی تر و (یارمەتیان بدات) ئەوە بێگومان بەڕق و خەشمی خواوە دەگەڕێتەوە, وە جێگای دۆزەخ دەبێت کە جێگـایەکی زۆر خراپ و بەدە');
INSERT INTO "bamoki" VALUES(8,17,'8:17','دە کەواتە ئێوە ئەوانتان نەکوشت بەڵکو خوا ئەوانی کوشت وەکاتێ کە (مشتێ خۆڵ و زیخت ڕوو بەکافران) ھاوێشت بەتەنھا تۆ نەتھاوێشت بەڵکو خوا ھاوێشتی و (لێیدان) بۆ ئەوەی خوا بڕواداران تاقی بکاتەوە لەلایەن خۆیەوە بەتاقی کردنەوەیەکی چاک بەڕاستی خوا بیسەر و زانایە');
INSERT INTO "bamoki" VALUES(8,18,'8:18','(سەر ئەنجامی باوەڕداران و کافران) ئەوە بوو (کەڕوویدا) وە بەڕاستی خوا پیلانی بێ بڕوایان پوچەڵ دەکاتەوە');
INSERT INTO "bamoki" VALUES(8,19,'8:19','ئەگەر ئێوە ئەی (بێ باوەڕان) داوای یارمەتی و سەرکەوتن دەکەن ئەوە بەڕاستی سەرکەوتنتان بۆ ھات (گاڵتەیان پێ دەکات) ئەگەر کۆتایی بھێنن (لەدوژمنایەتی پێغەمبەر یان کۆتای بھێنن بە بت پەرستی) ئەوە چاکترە بۆتان وە ئەگەر بگەڕێنەوە (بۆ سەر دژایەتی یان بت پەرستی) ئەوە ئێمەش دەگەڕێینەوە (بۆ سەر خستی باوەڕداران) ھەر گیز دەستە و کۆمەڵەکەتان ھیچ سوودێکتان پێ ناگەیەنن ھەر چەند زۆریش بن وەبێگومان خوا لەگەڵ بڕواداران دایە');
INSERT INTO "bamoki" VALUES(8,20,'8:20','ئەی ئەو کەسانەی بڕواتان ھێناوە گوێڕایەڵی خواو پێغەمبەرەکەی بن وپشتی تێمەکەن لەکاتێکدا ئێوە تێدەگەن و دەبیستن');
INSERT INTO "bamoki" VALUES(8,21,'8:21','وەکو ئەوانە مەبن کە (بەسەر زارەکی) ووتیان :بیستمان لەکاتێکدا کە ئەوانە ھیچ نابیستن و تێناگەن');
INSERT INTO "bamoki" VALUES(8,22,'8:22','بەڕاستی خراپترینی ھەموو گیان لەبەران لە لای خوا ئەو کەڕولاڵانەن (مەبەست پێی بێ باوەڕانە) کە بیرناکەنەوە و ژیر نابن');
INSERT INTO "bamoki" VALUES(8,23,'8:23','وە ئەگەر خوا بیزانیایە چاکەیەک لەو (بێ باوەڕانەدا) ھەیە بێگومان وای لێ دەکردن ببیستن ئەگەر خوا (فەرمانی خۆی) پێ ببیستنایە ھەر ڕوویان وەردەگێڕا وە ئەوان ھەر گوێ پێ نەدەرن');
INSERT INTO "bamoki" VALUES(8,24,'8:24','ئەی ئەو کەسانەی باوەڕتان ھێناوە وەڵامی خوا و پێغەمبەر بدەنەوە کاتێک کە بانگتان دەکەن بۆ لای ئەو (ئاینەی) کەزیندووتان دەکاتەوە بشزانن بەڕاستی (ویستی) خوا دەبێتە بەربەست لەنێوان ئادەمی و دڵیدا (واتە ئەو پەڕی دەستەڵاتی ھەیە بەسەر دڵی بەندەکانیدا) وە بەڕاستی ھەر لای ئەو کۆ دەکرێنەوە (لەڕۆژی دواییدا)');
INSERT INTO "bamoki" VALUES(8,25,'8:25','وەخۆتان بپارێزن لە ئاشوب و بەڵایەک کە تووشی ستەم کارانتان نابێت بە تایبەتی (ھەمووتان دەگرێتەوە) وە بشزانن کە بەڕاستی تۆڵە و سزای خوا زۆر سەختە');
INSERT INTO "bamoki" VALUES(8,26,'8:26','بیر بکەنەوە کاتێک کە ئێوە کەم و لاوازکراو و ژێر دەستە بوون لە زەوی (لە مەککە) دا دەترسان کەخەڵکی بتان فڕێنن وبدەن بەسەرتاندا ئەمجا خوا پەنای دان وە بەیارمەتی خۆی پشتیوانی کردن وە ڕزق و ڕۆزی لە شتە پاک (و حەڵاڵە) کان پێ بەخشین بۆ ئەوەی سوپاس گوزاری خوابن');
INSERT INTO "bamoki" VALUES(8,27,'8:27','ئەی ئەو کەسانەی باوەڕتان ھێناوە ناپاکی مەکەن لەگەڵ خوا و پێغەمبەر صلى الله عليه وسلم دا وە ناپاکی مەکەن لە سپاردەکانی ناو خۆتان لەکاتێکدا کە ئێوە دەزانن (ناپاکیە)');
INSERT INTO "bamoki" VALUES(8,28,'8:28','وە بزانن بەڕاستی ماڵ و منداڵتان ھۆی تاقیکردنەوەی (ئێوەن) وە بەڕاستی لای خوا پاداشتی زۆر گەورە ھەیە');
INSERT INTO "bamoki" VALUES(8,29,'8:29','ئەی ئەو کەسانەی کە باوەڕتان ھێناوە ئەگەر ئێوە خواناس بن و خۆتان بپارێزن ھۆی جیاکەرەوەی (ڕاست و ناڕاست) تان بۆ فەراھەم دەھێنێت و لە گوناھەکانتان دەبورێت و لێتان خۆش دەبێت وە خوا خاوەن بەھرە و چاکەی زۆر گەورەیە');
INSERT INTO "bamoki" VALUES(8,30,'8:30','وە (بیر بکەرەوە) کاتێک ئەوانەی بێ باوەڕ بوون پیلانیان لە دژت دەگێڕا تا بەند و کۆتت بکەن یان بتکوژن یان (لە مەککە) دەرت بکەن وە ئەوانە (بەردەوام) پیلان دەگێڕن و فڕو فێڵ دەکەن وە خوایش لەبەرامبەر ئەواندا پیلان دەگێڕێت بۆ پووچەڵ کردنەوەی پیلان و فڕ و فێڵەکانیان وە خوا چاکترینی پیلان گێڕانە (بەرامبەر پیلان گێڕان)');
INSERT INTO "bamoki" VALUES(8,31,'8:31','وە کاتێک کە ئایەتەکانی ئێمەیان بەسەردا دەخوێنرێتەوە دەڵێن :بیستمان (واتە گرنگ نیە) ئەگەر بمان ویستایە وێنەی ئەو ئایەتانەمان دەووت ئەمە ھیچ نیە جگە لە ئەفسانەی پێشینان');
INSERT INTO "bamoki" VALUES(8,32,'8:32','(بیر بکەرەوە) کاتێک (کە بێ باوەڕەکان) دەیانووت ئەی پەروەردگار ئەگەر ئەم قورئانە ڕاستە و لەلایەن تۆوە ھاتووە دەی بەرد بەسەرماندا ببارێنە لە ئاسمانەوە یان سزای سەختی پڕ ئازارمان بۆ بنێرە');
INSERT INTO "bamoki" VALUES(8,33,'8:33','وە بەڕاستی خوا سزایان نادات لەکاتێک دا تۆ (ئەی موحەممەد صلى الله عليه وسلم) لەناویاندا بیت ھەروەھا خوا تووشی سزایان ناکات لە کاتێکدا ئەوان داوای لێبوردن لە خوا بکەن');
INSERT INTO "bamoki" VALUES(8,34,'8:34','خوا چۆن سزایان نادات؟! لە کاتێکدا ئەوان ڕێگری (موسوڵمانان) دەکەن لە چوون بۆ مزگەوتی بەڕێزو پیرۆزی (کەعبە) لەکاتێکدا ئەوان خۆشەویست و سەر پەرشتیاری ئەو (مزگەوتە) نین بەڕاستی سەر پەرشتیار و خۆشەویستی ئەو (مزگەوتە) خوا ناسان و پارێزکارانن بەڵام زۆربەیان نازانن');
INSERT INTO "bamoki" VALUES(8,35,'8:35','وە نوێژی ئەوان (بت پەرستەکان) لەماڵی خوادا ھیچ نیە جگە لە فیکە کردن و چەپڵە لێدان نەبێت کەواتە بچەژن سزای سەخت (لە بەدردا) بە ھۆی بڕوا نەھێنانتانەوە');
INSERT INTO "bamoki" VALUES(8,36,'8:36','بەڕاستی ئەوانەی کەبێ باوەڕن ماڵ و سامانیان خەرج دەکەن بۆ ئەوەی بەر ھەڵستی رێگەی خوا بکەن جا لە مەودوا (ماڵ و سامانیان) بەخت دەکەن و دەیبەخشن لەپاشان دەبێت بەداخ و پەژارە بۆیان پاشان ژێر دەکەون وە ئەوانەی کەبێ باوەڕن ھەر لەدۆزەخ دا کۆ دەکرێنەوە');
INSERT INTO "bamoki" VALUES(8,37,'8:37','بۆ ئەوەی خوا دەستەی پیس لە دەستەی پاک جیا بکاتەوە و پیسەکانیش لەسەر یەک ھەڵچنێت ئەمجا ھەمووی پێکەوە کۆ بکاتەوە و بیخاتە دۆزەخەوە ھەر ئەوانە زیانبار و خەسارۆ مەندی تەواون');
INSERT INTO "bamoki" VALUES(8,38,'8:38','(ئەی موحەممەد صلى الله عليه وسلم) بڵێ: بە ئەوانەی کە بێ باوەڕ بوون ئەگەر کۆتایی بھێنن (بەبێ بڕواییەکەیان) بەڕاستی (خوا) دەبورێت لەھەرچی لەوەو پێش کردوویانە بەڵام ئەگەر بگەڕێنەوە (سەر کردەوەی پێشوویان) ئەوە بەڕاستی باوو یاسای پێشینە (خراپە)کان چی بوو (بۆ ئەمانیش دووبارە دەبێـتەوە)');
INSERT INTO "bamoki" VALUES(8,39,'8:39','(باوەڕداران) جەنگ بکەن لەگەڵ بێ باوەڕاندا ھەتا ئاژاوە (بێ باوەڕی و بت پەرستی) نامێنێت و ئاین و ملکەچی ھەمووی بۆ خوا دەبێت جا ئەگەر کۆتاییان ھێنا ئەوەبەڕاستی خوا بەھەر کردەوەیەک کە (بێ باوەڕان) دەیکەن بینایە');
INSERT INTO "bamoki" VALUES(8,40,'8:40','وە ئەگەر ڕوویان وەرگێڕاو پشتیان ھەڵکرد ئەوە بزانن بەڕاستی خوا پشتیوان و یارمەتیدەرتانە ئەو (خوا) باشترین پشتیوانە وە باشترین یاریدەدەر و سەرخەرتانە');
INSERT INTO "bamoki" VALUES(8,41,'8:41','وە بزانن بێگومان ھەر چیەکتان لەغەنیمەت (دەسکەوت) وگرت ئەوە بەڕاستی پێنجیەکی ئەو (دەسکەوتە) بۆ خوا و بۆ پێغەمبەر صلى الله عليه وسلم و بۆ خزمانی (پێغەمبەر صلى الله عليه وسلم) و بۆ ھەتیوان و بۆ ھەژاران و بۆ ڕێبوارانە ئەگەر ئێوە باوەڕتان ھێناوە بەخوا وە بەوەی ناردوومانەتە خوارەوە بۆسەر بەندەکەمان (کەموحەممەد پێغەمبەرە صلى الله عليه وسلم) لەڕۆژی جیاکردنەوەدا (واتە:لە جەنگی بەدردا) لەو ڕۆژەی کەدوو کۆمەڵ بەیەک گەیشتن (دژ بەیەک جەنگان) وە خوا بەسەر ھەموو شتێکدا دەسەڵاتدارە');
INSERT INTO "bamoki" VALUES(8,42,'8:42','وەکاتێک ئێوە لای خواری (شیوەکەی) نزیک (مەدینە) دا بوون و ئەوانیش لەلای سەروی (شیوەکەی) دوورتر (لەمەدینە) دا بوون کاروانەکەش لەخوارتری ئێوە بوو وە ئەگەر بەڵێنتان بەیەکتر بدایە بێگومان جیاواز دەبوون لەبەڵێنەکەدا (جەنگتان نەدەکرد) بەڵام (کۆی کردونەتەوە) بۆ ئەوەی خوا کارێک بەدی بھێنێت ھەر بەدی دێت بۆ ئەوەی لەناو بچێت ئەو کەسەی (بەلای خواوە) لەناو چووە لەڕووی بەڵگەوە, وە بۆ ئەوەی بژی ئەوەی (بەلای خواوە) دەژی لەڕووی بەڵگەوە, وە بەڕاستی خوا بیسەری زانایە');
INSERT INTO "bamoki" VALUES(8,43,'8:43','(بیر بکەرەوە) کاتێک کەخوا ئەو (بێ بڕوایانەی ) نیشان دەدایت لەخەوتدا کەم بوون خۆ ئەگەر (خوا ژمارەی) ئەوانەی بەزۆر نیشان بدایتایە ئەوە بێگومان دەترسان و دەشکان وە جیاواز دەبوون لەبڕیاری جەنگدا بەڵام خوا پاراستنی بەڕاستی ئەو (خوا) زانایە بە ئەوەی لەناو دڵاندایە');
INSERT INTO "bamoki" VALUES(8,44,'8:44','(بیربکەنەوە) کاتێک خوا ئەوانی نیشاندان کاتێک ڕووبەڕوو بوونەوە لەبەرچاوتان بەکەم دەبینران ئێوەشی لەبەر چاوی ئەوان بەکەم نیشاندا بۆ ئەوەی خوا کارێک بەدی بھێنێت ھەر بەدی دێت وە ھەمووکارەکان ھەر بۆلای خوا دەگێڕێنرێنەوە');
INSERT INTO "bamoki" VALUES(8,45,'8:45','ئەی ئەوانەی کەباوەڕتان ھێناوە کاتێک (لەجەنگدا) لەگەڵ کۆمەڵێکدا ڕووبەڕوو بوونەوە خۆگربن (ھەڵنەیەن) و یادی خوا زۆر بکەن بۆ ئەوەی سەرکەوتووبن');
INSERT INTO "bamoki" VALUES(8,46,'8:46','(ئەی باوەڕداران) گوێڕایەڵی خوا و پێغەمبەرەکەی بکەن وە جیاوازمەبن و دووبەرەکی مەکەن بەھۆیەوە بشکێن و لاوازببن و ھێزتان بڕواو نەمێنێت وەخۆ ڕاگر بن بەڕاستی خوا لەگەڵ خۆگراندایە');
INSERT INTO "bamoki" VALUES(8,47,'8:47','وەک ئەوانە (وەک بت پەرستەکان) مەبن دەرچوون لەماڵ و وڵاتی خۆیان بەفیزو و ڕووپامایی بۆ خەڵک کردن وە ڕێگری (خەڵکیان) دەکرد لەڕێگەی خوا وە خوا بەھەموو ئەو کارانەی دەیکەن ئاگادارە');
INSERT INTO "bamoki" VALUES(8,48,'8:48','(بیربکەرەوە) کاتێک کەشەیتان کردەوەکانیانی (بۆ بت پەرستەکان) ڕازاندنەوە, وە ووتی: ئەمڕۆ ھیچ کەس بەسەرتاندا زاڵ نابێت و بێگومان من پەنادەر و (پارێزەر) ی ئێوەم ئەمجا کاتێک ھەردوو دەستەکە ڕوو بەڕووی یەکتر بوونەوە (شەیتان) پاشەو پاش بەدوای خۆی دا گەڕایەوە و ووتی: بەڕاستی من بەریم لەئێوە چونکە بەڕاستی ئەوەی من دەبینم ئێوە نایبینن بەڕاستی من لەخوا دەترسم و خوا زۆر تۆڵەی بەتین و سەختە');
INSERT INTO "bamoki" VALUES(8,49,'8:49','(بیربکەرەوە) کاتێک دووڕووەکان و ئەوانەی نەخۆشی لەدڵیاندابوو دەیانووت ئەوانە (موسوڵمانەکان) ئاینەکەیان فریوی داوون ھەرکەسێک پشت بەخوا ببەستێت ئەوە بەڕاستی خوا بە دەسەڵاتی کار دروستە');
INSERT INTO "bamoki" VALUES(8,50,'8:50','وە ئەگەر دەتبینی کاتێک فریشتەکان گیانی بێ باوەڕان دەکێشن لەدەموچاوو پاشیان دەدەن و (پێیان دەڵێن) بچەژن سزای ئاگری سوتێنەر');
INSERT INTO "bamoki" VALUES(8,51,'8:51','ئەو (ئازار و سزایە) بەھۆی ئەوەوەیە دەستی خۆتان پێشی خستووە, وە بەڕاستی خوا بە ھیچ شێوەیەک ستەمکار نیە بۆ بەندەکانی');
INSERT INTO "bamoki" VALUES(8,52,'8:52','(ئەم بێ باوەڕانە باوو نەریتیان) ھەر وەکو باو و نەریتی دەستەی فیرعەون و ئەوانەیە لەپێش ئەوانەوە بوون کە بێ بڕوا بوون بە نیشانەکانی خوا ئیتر (سزای) خوایش گرتنی بەھۆی گوناھەکانیانەوە بەڕاستی خوا بەتوانا و تۆڵە بەتینە');
INSERT INTO "bamoki" VALUES(8,53,'8:53','ئەوە بەھۆی ئەوەوەیە کەبەڕاستی خوا ھیچ نیعمەتێک ناگۆڕێت کە بەخشیبێتی بەھەر گەل و ھۆزێک ھەتا ئەوەی لەخۆیاندایە نەیگۆڕن (واتە ئیمانەکەیان بگۆڕن بۆ بێ باوەڕی) وە بەڕاستی خوا بیسەر و زانایە');
INSERT INTO "bamoki" VALUES(8,54,'8:54','(ئەم کردەوەی ئەمان) وەکو باوو نەریتی دەستەی فیرعەون وئەوانەی لەپێش ئەوان بوون وایە بەڵگەکانی پەروەردگاریان بەدرۆزانی ئێمەش لەناومان بردن بەھۆی گوناھەکانیانەوە, وە دەست و پێوەندەکەی فیرعەونمان خنکاند چونکە ھەموویان ستەمکار بوون');
INSERT INTO "bamoki" VALUES(8,55,'8:55','بەڕاستی بەدترینی گیان لەبەران (کەسانێکن) بەلای خواوە ئەوانەن بێ باوەڕبوون لەبەر ئەوەی ئەوان باوەڕ ناھێنن');
INSERT INTO "bamoki" VALUES(8,56,'8:56','ئەوانەی کەپەیمانت لەگەڵیان بەست لەپاش ئەوە پەیمانەکەیان دەشکاند لەھەموو جارێکدا و ئەوان خۆیان ناپارێزن ولە خوا ناترسن');
INSERT INTO "bamoki" VALUES(8,57,'8:57','جا ئەگەر تووشی ئەوانە بوویت (زاڵ بوویت بەسەریاندا) لە جەنگدا (سزاو کوشتن و بڕینێکیان پێ بکە) کەببێتە ھۆی ترس لەدڵی ئەو دوژمنانەی لەپشتیانەوەن بەڵکو ئەوان بیربکەنەوە');
INSERT INTO "bamoki" VALUES(8,58,'8:58','وە ئەگەر بەڕاستی مەترسیت کرد لەناپاکی دەستەیەک (کەپەیمانەکەیان بشکێنن) ئەو کاتە (پەیمانەکە) فڕێ بدەرەوە بۆیان بەڕێک وپێکی تا بەرامبەر بن بەڕاستی خوا ناپاکانی خۆش ناوێت');
INSERT INTO "bamoki" VALUES(8,59,'8:59','وە باگومان نەبەن ئەوانەی بێ باوەڕ بوون پێشکەوتوون بێگومان ئەوانە خوا دەستە پاچە ناکەن (لە دەستی دەرناچن)');
INSERT INTO "bamoki" VALUES(8,60,'8:60','(ئەی ئیمانداران) ئامادەی بکەن بۆیان ئەوەی لەتواناتاندا ھەیە لەھێز وە لەئەسپی دابەستراو تا بترسێنن پێی دوژمنی خواو دوژمنی خۆتان وە کەسانێکی تریش بێجگە لەوان ئێوە نایانناسن خوا دەیانناسێت وە ھەرشتێک ببەخشن لەڕێی خوادا (پاداشەکەی) بەتەواوی دەدرێتەوە پێتان وە ئێوە ھیچ ستەمتان لێ ناکرێت');
INSERT INTO "bamoki" VALUES(8,61,'8:61','وە ئەگەر ئەوانە مەیلی ئاشتیان ھەبوو تۆش (ئەی موحەممەد صلى الله عليه وسلم) مەیلی ئەوە بکە وە پشت بەخوا ببەستە بەڕاستی ھەر ئەو زاتە بیسەری زانایە');
INSERT INTO "bamoki" VALUES(8,62,'8:62','وە ئەگەر ویستیان فێڵت لێ بکەن ئەوە بەڕاستی خوات بەسە (پشت و پەنات بێت) خوا ئەو زاتەیە کە بەھێزی کردیت بەیارمەتی خۆی وە بەبڕواداران');
INSERT INTO "bamoki" VALUES(8,63,'8:63','وە ھۆگری خستە نێوان دڵەکانیان ئەگەر بتبەخشیایە ھەرچی لەزەویدا ھەیە ھۆگریت نەدەخستە نێوان دڵەکانیانەوە بەڵام خوا ھۆگری خستە نێوانیان بەڕاستی ھەر ئەو زاڵ و کاردروستە');
INSERT INTO "bamoki" VALUES(8,64,'8:64','ئەی پێغەمبەر صلى الله عليه وسلم خوات بەسە ھەروەھا ئەو بڕوادارانەش کەشوێنت کەوتوون');
INSERT INTO "bamoki" VALUES(8,65,'8:65','ئەی پێغەمبەر صلى الله عليه وسلم ھانی بڕواداران بدە لەسەر جەنگ کردن (لەگەڵ دوژمناندا) ئەگەر لەئێوەدا ببێت بیست کەسی خۆڕاگر زاڵ دەبن بەسەر دووسەد کەسی (بێ باوەڕ) دا وە ئەگەر سەد کەس لەئێوە ھەبێت زاڵ دەبن بەسەر ھەزار کەسدا لەئەوانەی بێ باوەڕن بەھۆی ئەوەی بەڕاستی ئەوانە کۆمەڵێکن نافامن');
INSERT INTO "bamoki" VALUES(8,66,'8:66','ئێستا خوا فەرمانەکەی سووک کرد لەسەرتان وەزانی کە لاوازیتان تێدایە جا ئەگەر لەئێوەدا ببێت سەد کەسی خۆڕاگر زاڵ دەبن بەسەر دووسەد کەس (لە بێ باوەڕان) دا وەئەگەر ھەزار کەس لەئێوە ھەبن زاڵ دەبن بەسەر دوو ھەزار کەسدا بەفەرمانی خوا وەخوا لەگەڵ خۆگراندایە');
INSERT INTO "bamoki" VALUES(8,67,'8:67','بۆ ھیچ پێغەمبەرێک ڕەوا نیە کە دیلی (لە دوژمنان) ھەبێت و ڕایان بگرێت ھەتا کوشتاری زۆریان لێ نەکات ودەسەڵاتی نەچەسپێت لەزەویدا ئایا ئێوە ماڵی دونیاتان دەوێت (بە وەرگرتنی فیدیە)؟ (لەکاتێکدا) خوای گەورە (پاداشتی) ڕۆژی دوایی دەوێت (بۆ ئێوە) وە خوا بەتوانا و زاڵ و کار دروستە');
INSERT INTO "bamoki" VALUES(8,68,'8:68','ئەگەر نووسراو (و بڕیار) ێک لەلایەن خواوە پێش نەکەوتایە (کە حەڵاڵ بوونی دەستکەوت و غەنیمەتی جەنگە) بێگومان لەبەر ئەو (فیدیە) ی کە وەرتان گرت (لەبەرامبەر ئازادکردنی دیلەکاندا) تووشتان دەبوو سزایەکی گەورە');
INSERT INTO "bamoki" VALUES(8,69,'8:69','کەواتە بخۆن لەوەی کە بە غەنیمەت گرتان حەڵاڵ و پاکە وە لە خوا بترسن بەڕاستی خوا لێ بوردە و میھرەبانە');
INSERT INTO "bamoki" VALUES(8,70,'8:70','ئەی پێغەمبەر صلى الله عليه وسلم بڵێ بەوانەی کە لەژێر دەستاندان لەدیلەکان ئەگەر خوا بزانێت لەدڵەکانتاندا چاکەیەک ھەیە پێتان دەبەخشێت چاکتر لەو (فیدیەی) لێتان وەرگیراوە, وە لێتان دەبورێت وە خوا لێ بووردە ومیھرەبانە');
INSERT INTO "bamoki" VALUES(8,71,'8:71','وەئەگەر (ئەو دیلانە) بیانەوێت ناپاکیت لەگەڵدا بکەن ئەوە بێگومان ناپاکیان لەگەڵ خوادا کرد لەپێش (ئەمەدا) خواش (تۆی) زاڵ کرد بەسەریاندا وەخوا زانای دانایە');
INSERT INTO "bamoki" VALUES(8,72,'8:72','بەڕاستی ئەوانەی بڕوایان ھێنا و کۆچیان کرد و تێکۆشان بەماڵیان و بەگیانیان لەڕێگای خوادا وە ئەوانەی (کۆ چەرەکانیان) گرتە خۆیان و کۆمەکیان کردن ئەوانە پشتیوان و دۆستی یەکترین وە ئەوانەی کەبڕوایان ھێناوە وکۆچیان نەکردووە بۆ ئێوە نیە ھیچ جۆرە دۆستایەتیەکیان بکەن ھەتا کۆچ نەکەن وەئەگەر داوای یارمەتیان لێ کردن لە (پارێزگاری) ئاین ئەوە پێویستە لەسەرتان یارمەتی دانیان مەگەر لەدژی گەلێک لەنێوان ئێوە و ئەواندا پەیمان ھەبێت (نابێت یارمەتیان بدەن) وەخوا بینایە بەوەی کەدەیکەن');
INSERT INTO "bamoki" VALUES(8,73,'8:73','وە ئەوانەی کە بێ بڕوان ھەندێکیان دۆستی ھەندێکیانن ئەگەر ئەو (پشتیوانیە) نەکەن لەزەویدا ئاژاوەو فەساد و بەد خویەکی گەورە بەرپا دەبێت');
INSERT INTO "bamoki" VALUES(8,74,'8:74','وەئەوانەی کەبڕوایان ھێنا وکۆچیان کرد و تێکۆشان لەڕێگەی خوادا ئەوانەش (کۆچەرەکان) یان گرتە خۆیان و کۆمەکیان کردن بەڕاستی ھەر ئەوانەن بڕوادارانی ڕاستەقینە بۆ ئەوانەیە لێ بووردن و ڕۆزیەکی چاک و بەنرخ');
INSERT INTO "bamoki" VALUES(8,75,'8:75','وە ئەوانەی لەدوایدا بڕوایان ھێنا و کۆچیان کرد و لەگەڵ ئێوەدا تێکۆشان ئا ئەوانە لەئێوەن وە ئەوانەی خزمی یەکترن ھەندێکیان (بۆ میرات گرتن) شایستە ترن بەھەندێکی تریان لەکتێبی خوادا (لە قورئان یان لە لوح المحفوڤ) دا بەڕاستی خوا بەھەموو شتێک زانایە');
INSERT INTO "bamoki" VALUES(9,1,'9:1','ئەمە بەری بوونێکی گەورەو گرنگە لەلایەن خواو پێغەمبەرەکەیەوە بۆ ئەوانەی کەپەیمانتان لەگەڵ بەستوون لە ھاوبەش دانەران');
INSERT INTO "bamoki" VALUES(9,2,'9:2','کەواتە (ئەی بێ بڕواکان) لەزەویدا بگەڕێن ماوەی چوار مانگ وە بزانن بێگومان ئێوە لەدەستی خوا دەرناچن وە بەڕاستی خوا ڕیسواکەری بێ بڕوایانە');
INSERT INTO "bamoki" VALUES(9,3,'9:3','ھەروەھا ئەمە ڕاگەیاندنێکە لەلایەن خوا و پێغەمبەرەکەیەوە بۆ گشت خەڵکی لەڕۆژی حەجی گەورەدا بەڕاستی خواو پێغەمبەرەکەی بەرین لەھاوبەش دانەرەکان جا ئەگەر پەشیمان ببنەوە ئەوە چاکترە بۆتان وە ئەگەر پشتی تێ بکەن ئەوە بزانن بێگومان ئێوە لە دەستی خوا دەرناچن مژدەش بدە بەوانەی کە بڕوایان نەھێناوە بەسزایەکی ئازاردەر');
INSERT INTO "bamoki" VALUES(9,4,'9:4','بێجگە لەوانەی کەپەیمانتان لەگەڵ بەستوون لە ھاوبەش دانەرەکان پاشان ھیچ کەم و کوڕییان لێ نەکردن وە پشتیوانی ھیچ کەسێکیان نەکردووە لەدژتان ئەوە ئێوەش پەیمانەکەیان بۆ تەواو بکەن (لەگەڵیان دا بیبەنەسەر) تاماوەی دیاری کراویان بەڕاستی خوا پارێزکاران و لەخواترسانی خۆش دەوێت');
INSERT INTO "bamoki" VALUES(9,5,'9:5','ئەمجا کاتێک مانگە حەرامەکان (ژو القعدە ، ژو الحجە، محرم، رجب) تەواو بوون ئەوە ھاوبەش دانەرەکان بکوژن لە ھەر شوێنێک دەستان کەوتن وە (بەدیل) بیانگرن وە ڕێگریان بکەن لە دەرچوون وە بۆیان دانیشن لە ھەموو جێگایەکی چاوەڕوانیدا جا ئەگەر پەشیمان بوونەوە و نوێژیان کرد وە زەکاتیاندا ئەوە ڕێیان بەرەڵاکەن و وازیان لێ بھێنن بەڕاستی خوا لێ بووردە و میھرەبانە');
INSERT INTO "bamoki" VALUES(9,6,'9:6','وە ئەگەر کەسێک لە ھاوبەشدانەرەکان داوای ھاوسێیەتی و پەنادانی لێ کردیت ئەوە پەنای بدە بۆ ئەوەی ووتەی خوای گەورە (قورئان) ببیستێت لە پاشان بیگەیەنە جێگای ھێمنی خۆی ئەوە لەبەر ئەوەیە کە بێگومان ئەوانە دەستە و کۆمەڵێکن ھیچ نازانن');
INSERT INTO "bamoki" VALUES(9,7,'9:7','چۆن عەھد و پەیمان دەبێت بۆ ھاوبەش دانەرەکان لای خوا و پێغەمبەرەکەی صلى الله عليه وسلم بێجگە لەوانەی کەپەیمانتان لەگەڵبەستن لای مزگەوتی حەرام (کە کەعبەی پیرۆزە) جاچەند ئەوانە رێک و ڕاست بوون لەگەڵتان ئێوەش ڕێک و ڕاست بن لەگەڵیان بەڕاستی خوا خۆ پارێزەران وخواناسانی خۆش دەوێت');
INSERT INTO "bamoki" VALUES(9,8,'9:8','چۆن (پەیمان دەبەنە سەر) لەکاتێکدا ئەگەر زاڵ ببن بەسەرتاندا لەبەر چاو ناگرن دەربارەی ئێوە ھیچ خزمایەتی و سوێند و بەڵێنێک بەووتەی دەمیان ڕازیتان دەکەن بەڵام دڵەکانیان دژتانن و ڕقیان لێتان دەبێتەوە ئا ئەوانە زۆربەیان سنوورەکانی خودا دەبەزێنن');
INSERT INTO "bamoki" VALUES(9,9,'9:9','بەڵگە و ئایەتەکانی خوایان گۆڕییەوە بەنرخێکی زۆر کەم ئەمجا ڕێگریان لەڕێگەی خوا کرد بەڕاستی زۆر خراپ بوو ئەوەی ئەوان دەیانکرد');
INSERT INTO "bamoki" VALUES(9,10,'9:10','لەبەر چاو ناگرن دەربارەی ھیچ بڕوادارێک ھیچ خزمایەتی و سوێند و بەڵێنێک وە ھەر ئەوانە دەست درێژی کەرن');
INSERT INTO "bamoki" VALUES(9,11,'9:11','ئەمجا ئەگەر پەشیمان بوونەوە و نوێژیان بەجێ ھێنا وە زەکاتیاندا ئەوە براتانن لەئاییندا وە ئێمە بەڵگەکان ڕوون دەکەینەوە بۆ گەلێک کەدەیزانن');
INSERT INTO "bamoki" VALUES(9,12,'9:12','بەڵام ئەگەر سوێند و پەیمانەکەیان ھەڵوەشاندەوە پاش بەستنی پەیمانەکەیان وە تەشەرو تانەیاندا لەئاینەکەتان ئەوە بجەنگن لەدژی پێشەوایانی بێ باوەڕ بەڕاستی ئەوانە ھیچ جۆرە پەیمانێکیان نیە بەڵکو کۆتایی بھێنن (بەبێ باوەڕی)');
INSERT INTO "bamoki" VALUES(9,13,'9:13','ئایا ئێوە جەنگ ناکەن لەگەڵ گەلێکدا کەپەیمانەکەیان ھەڵوەشاندەوە (پەیمانی حودەیبیە) و نیازی دەرکردنی پێغەمبەرصلى الله عليه وسلم یان کرد ھەر ئەوانیش لەیەکەم جارەوە دەستیان کرد بەجەنگ لەگەڵ ئێوەدا ئایا لێیان دەترسن (لەکاتێکدا) ھەر خوا شایستەترە کە لێی بترسن ئەگەر ئێوە بڕوادارن');
INSERT INTO "bamoki" VALUES(9,14,'9:14','(ئەی باوەڕداران) بیان کوژن خوا بەدەستی ئێوە سزایان دەدات و ڕیسوایان دەکات و سەرتان دەخات بەسەریاندا و (خوا) سینە و دڵی کۆمەڵی بڕوادار خۆش دەکات');
INSERT INTO "bamoki" VALUES(9,15,'9:15','و ڕق و خەشمی دڵەکانیان دادەمرکێنێ و ناھێڵێت وە خوای گەورە تەوبە وەردەگرێت لە ھەر کەسێک کەبیەوێت وە خوا زانای دانایە');
INSERT INTO "bamoki" VALUES(9,16,'9:16','ئایا گومانتان وابوو کەوازتان لێ دەھێنرێت لەکاتێکدا ھێشتا خوا ئاشکرای نەکردووە ئەوانەی تێکۆشان لەئێوە (لە پێناو خوادا) وە نەیان کردووە و ھەڵیان نەبژاردووە جگە لەخوا و پێغەمبەرەکەی و موسوڵمانان بە ھاوڕاز و جێی نھێنیان (دڵسۆزیان) وە خوا ئاگایە بەھەرچی کەئێوە دەیکەن');
INSERT INTO "bamoki" VALUES(9,17,'9:17','نابێت ھاوبەشدانەران مزگەوتەکانی خوا ئاوەدان بکەنەوە (و سەرپەرشتیان بکەن) لەکاتێکدا خۆیان شاھیدن لەسەر خۆیان کە بێ باوەڕن ئەوانە کردەوەکانیان ھەڵوەشاوەتەوە، وە ئەوانە لەناو ئاگردا ھەمیشەیین');
INSERT INTO "bamoki" VALUES(9,18,'9:18','بەڕاستی مزگەوتەکانی خوا ئاوەدان دەکاتەوە کەسێک کەبڕوای ھێنابێت بەخوا و بەڕۆژی دوایی و نوێژەکانی بەجێ ھێنابێت و زەکاتی دابێت و لە کەس نەترسا بێت بێجگە لەخوا جا بەڵکو ئەوانە لەو کەسانە بن کە ڕێنمونی کراون');
INSERT INTO "bamoki" VALUES(9,19,'9:19','(ئەی بێ باوەڕان) ئایا پێتان وایە ئاودان بەحاجییان و (سەرپەرشتی) و ئاوەدان کردنەوەی مزگەوتی حەرام (کەعبەی پیرۆز) وەکو کەسێکە کەباوەڕی ھێنا بێت بەخوا و بەڕۆژی دوایی و تێکۆشا بێت لە ڕێی خوادا؟ چوون یەک نین بەلای خوداوە و (یەکسان نین) وە خوا ڕێنمونی کۆمەڵی ستەمکاران ناکات');
INSERT INTO "bamoki" VALUES(9,20,'9:20','ئەوانەی کەبڕوایان ھێناوە و کۆچیان کردووە و تێکۆشاون لەڕێگای خوادا بەماڵیان و بەگیانیان پلەیان بەرزترە لای خوا وەھەر ئەوانە سەرکەوتوون (لە دونیا و دوا ڕۆژدا)');
INSERT INTO "bamoki" VALUES(9,21,'9:21','پەروەردگاریان موژدەیان دەداتێ بەمیھرەبانی و ڕەزامەندی لەلایەن خۆیەوە ھەروەھا بەھەشتانێکیان بۆ ھەیە نازو نیعمەتی بەردەوام و ھەمیشەیی تێدایە');
INSERT INTO "bamoki" VALUES(9,22,'9:22','دەمێننەوە تیایدا بەھەمیشەیی بەڕاستی خوا پاداشتی ھەرە گەورەی لایە');
INSERT INTO "bamoki" VALUES(9,23,'9:23','ئەی ئەوانەی کەبڕواتان ھێناوە باوک و براکانتان دامەنێن (ھەڵمەبژێرن) بەدۆست و خۆشەویست ئەگەر بێ باوەڕییان لەباوەڕ لا خۆشەویسترو چاکتربوو وە ھەر کەس لەئێوە ئەوانە بکات بەدۆستی خۆی ئەوە ھەر ئەوانە ستەمکارن');
INSERT INTO "bamoki" VALUES(9,24,'9:24','(ئەی موحەممەد صلى الله عليه وسلم) بڵێ: ئەگەر باوکانتان وکوڕەکانتان وبراکانتان وھاوسەرەکانتان وخزم وعەشرەتتان وماڵ و دارایی یەک (بە زەحمەت) پەیداتان کردووە و بازرگانیەک کەدەترسن برەوی نەبێت (بێ بازاڕ بێت) و خانوو بەرەیەک کەپێی ڕازین خۆشەویست ترە لاتان لەخوا و پێغەمبەرەکەی و تێکۆشان لەڕێگەکەیدا ئەوە چاوەڕوان بکەن ھەتا خوا فەرمان و (تۆڵەی) خۆی دەھێنێت (بۆتان) وە خوا ڕێنمونی کەسانی تێپەڕ لەسنوور ناکات');
INSERT INTO "bamoki" VALUES(9,25,'9:25','سوێند بەخوا بەڕاستی خوا سەری خستن لە زۆر جێگا و شوێندا و لەڕۆژی حونەینیشدا (یارمەتی دان) کەبە زۆری خۆتاندا دەنازین بەڵام (ئەو زۆریە) ھیچ سوودێکی نەبوو بۆتان و زەویتان لەسەر تەنگ بوویەوە بەو فراوانیەی کەھەیەتی پاشان پشتان کردە دوژمن و ھەڵھاتن');
INSERT INTO "bamoki" VALUES(9,26,'9:26','لەپاش ئەوە خوا ئارامی خۆی نارد بۆ سەر پێغەمبەرەکەی بۆ سەر بڕواداران وە سەربازانێکی (لەئاسمانەوە) ناردە خوارەوە کەئێوە نەتاندەبینین وەسزای بێ بڕواکانیشی دا (بەکوشتن و برینداری و بەدیل گرتن) ئەوەش سزای بێ باوەڕانە');
INSERT INTO "bamoki" VALUES(9,27,'9:27','پاشان خوا خۆش دەبێت لەپاش ئەوە لە ھەر کەسێک کە بیەوێت وە خوا لێ بوردە و میھرەبانە');
INSERT INTO "bamoki" VALUES(9,28,'9:28','ئەی ئەوانەی کەبڕواتان ھێناوە بێگومان ھاوبەش دانەران پیسن کەواتە با نزیکی مزگەوتی حەرام نەکەونەوە پاش ئەم ساڵەیان (ئەو ساڵەی فەتحی مەککە کرا) وەئەگەر لەنەبوونی ترسان ئەوە لەمەو پاش خوا دەوڵەمەندتان دەکات لە چاکە و فەزڵ و بەھرەی خۆی ئەگەر ویستی بەڕاستی خوا زانای کاردروستە');
INSERT INTO "bamoki" VALUES(9,29,'9:29','جەنگ بکەن لەگەڵ ئەوانەی کەبڕوا ناھێنن بەخوا وە بەڕۆژی دوایی وە بەحەرامی دانانێن ئەوەی کە خوا و پێغەمبەرەکەی حەرامیان کردووە، وە ملکەچ ناکەن بۆ ئاینی ڕاست و دروست (ئاینی ئیسلام) لە ئەوانەی کتێبیان پێ بەخشراوە (مەبەست پێی گاور وجولەکەکانە) ھەتا باج و سەرانە دەدەن (بەدەوڵەتی ئیسلامی) بەدەستی خۆیان و بەملکەچی لەکاتێکدا ئەوان سەرشوڕو ڕیسوان');
INSERT INTO "bamoki" VALUES(9,30,'9:30','جوولەکەکان ووتیان عوزەیر کوڕی خوایە و گاورەکانیش ووتیان مەسیح کوڕی خوایە ئەوە ووتەی ئەوانە بەدەمی خۆیان دەیڵێن وەک ووتەی ئەوانە دەڵێنەوە کەپێشتر بێ بڕوا بوون خوا لەناویان بەرێت چۆن درۆ دەکەن و لەھەق و ڕاستی لادەدەن');
INSERT INTO "bamoki" VALUES(9,31,'9:31','زانا و قەشەکانیان کردوە بەپەروەردگار لەجیاتی خوا ھەروەھا مەسیحی کوڕی مەریەمیش (یان کردوە بە خوا) لە کاتێکدا فەرمانیان پێ نەدراوە بێجگە لەوەی کەیەک خوا بپەرستن کە ھیچ پەرستراوێکی ڕاست نیە بێجگە لەئەو پاک و بێگەردی بۆ ئەو (خوا) یەی لەوەی دەیکەنە ھاوبەشی');
INSERT INTO "bamoki" VALUES(9,32,'9:32','دەیانەوێت ڕِووناکی (ئاینی) خوا بکوژێننەوە بەفووی دەمیان وە خوا نایەوێت و ناھێلێت و ھەر ڕووناکی خۆی (کە ئاینی پیرۆزی ئیسلامە) تەواو دەکات سەری دەخات (بەسەر تەواوی بێ باوەڕاندا) ھەر چەندە بێ باوەڕان پێیان ناخۆش بێت');
INSERT INTO "bamoki" VALUES(9,33,'9:33','خوا زاتێکە کەپێغەمبەرەکەی ناردووە بە ڕێنمونی و ئاینی ڕاست بۆ ئەوەی زاڵی بکات بەسەر ھەموو ئاینەکاندا بە گشتی ھەر چەندە بت پەرستان پێیان ناخۆش بێت');
INSERT INTO "bamoki" VALUES(9,34,'9:34','ئەی ئەوانەی کەبڕواتان ھێناوە بەڕاستی زۆر لەزانا و قەشەکانی (جولەکەو گاور) سامان و ماڵی خەڵکی دەخۆن بەناھەق و ڕێگری (خەڵک) دەکەن لەڕێگەی خوا وە ئەوانەی ئاڵتون و زیو سەریەک دەنێن و کۆدەکەنەوە و نایبەخشن و زەکاتەکەی دەرناکەن لە پێناو خودا ئەوە موژدەیان پێ بدە بەسزایەکی ئازاردەر');
INSERT INTO "bamoki" VALUES(9,35,'9:35','لە ڕۆژێکدا سوور دەکرێتەوە لەئاگری دۆزەخدا ئەوسا بەو (ئاڵتون و زیوە سوورەوە بووە) داخ دەکرێت ڕوویان و تەنیشتیان و پشتیان (پێیان دەووترێت) ئەمە ئەو (سامانەیە)کۆتان دەکردەوە بۆ خۆتان دەبچێژن (تۆڵەی) ئەوەی کۆتان دەکردەوە');
INSERT INTO "bamoki" VALUES(9,36,'9:36','بەڕاستی ژمارەی مانگەکانی (ساڵ) لای خوا دوانزە مانگە لە (لوح المحفوظ) وا دانراوە لەڕۆژێکەوە (خوا) ئاسمانەکان و زەوی دروست کردووە چوار لەو مانگانە گەورەو بەڕێزن (شەڕیان تێدا قەدەغەکراوە) ئەوە ئاینی رێک و ڕاستە کەواتە لەو چوار مانگەدا ستەم مەکەن لەخۆتان (وکوشتاریان تێدا مەکەن) وە بجەنگن لەدژی تەواوی بت پەرستان بەتێکڕا ھەروەک ئەوان دژی ئێوە دەجەنگن بە تێکڕا و (بێ جیاوازی) ئەوەش بشزانن کە بێگومان خوا لەگەڵ پارێزکاران وخواناساندایە (بە چاودێری و پاراستن)');
INSERT INTO "bamoki" VALUES(9,37,'9:37','بەڕاستی دواخستن و گۆڕینی (مانگە حەرامەکان) ڕۆچوونە لە بێ باوەڕیدا بێ باوەڕانی پێ گومڕا دەکرێت (دواخستنەکە) ساڵێک حەڵاڵی دەکەن و ساڵێک حەرامی دەکەن بۆ ئەوەی ڕێک ببن لەگەڵ ژمارەی ئەو چوار مانگەی کە خوا ڕێزداری کردوون (جەنگی تێیاندا حەرام کردووە) سەر ئەنجام ئەوەی خوا حەرامی کردبوو حەڵاڵی دەکەن جوان کراوە بۆیان کردەوە ناشیرینەکانیان خواش ڕێنمونی کەسانی بێ باوەڕ ناکات');
INSERT INTO "bamoki" VALUES(9,38,'9:38','ئەی ئەوانەی کەباوەڕتان ھێناوە ئێوە چیتانە کاتێک پێـتان بووترێت دەرچن (بۆ جیھاد) لەڕێی خوادا لەشوێنی خۆتان ناجوڵێن (خۆتان دوا دەخەن) ئایا ئێوە ڕازین بەم ژیانەی دونیا لەبری پاشەڕۆژ دەی کامەیە خۆشی و شادیی ئەم ژیانەی دونیا لەبەرامبەری خۆشی پاشە ڕۆژدا زۆر کەمە');
INSERT INTO "bamoki" VALUES(9,39,'9:39','ئەگەر نەڕۆن و دەرنەچن (بۆ جیھادکردن) (خوا) سزاتان دەدا بەسزایەکی سەخت و ئازاردەر وە لەبری ئێوە گەلێکی تر دەھێنێت (جیاواز لەئێوە) و ئێوە ھیچ زیانێکی پێ ناگەیەنن وە خوا بەتوانایە بەسەر ھەموو شتێکدا');
INSERT INTO "bamoki" VALUES(9,40,'9:40','ئەگەر ئێوە یارمەتی ئەو (پێغەمبەرە صلى الله عليه وسلم) نەدەن ئەوە بێگومان خوا یارمەتی داوە کاتێک بێ بڕوایان دەریان کرد لە مەککە پێغەمبەر یەکێک بوو لەو دوو کەسە کاتێک ئەو دووانە (پێغەمبەر و ئەبو بەکر) لەئەشکەوتی (پور) دا بوون ئەو کاتە بەھاوەڵەکەی (بە ئەبوبەکری صدیقی) ووت خەم مەخۆ بێگومان خوا لەگەڵمانە ئەمجا خوا ئارامی و ئاسایشی بۆ ناردە خوارەوە، وە یارمەتیدا بە سەربازانێک کە نەتاندەبینین ووتەی بێ باوەڕانی گێڕا بەژێر کەوتوو و ووتەی خوا ھەر بەرز و بڵندە وە خوا زاڵ و باڵادەست و کاردروستە');
INSERT INTO "bamoki" VALUES(9,41,'9:41','بڕۆن و دەرچن (بۆ جیھاد) چ سووک بن (لەش ساغ و دەوڵەمەند و دەسەڵاتداربن) چ قورس و گران بن (نەخۆش و ھەژاربن) و تێبکۆشن بەماڵ و گیانتان لەڕێگەی خوادا ئەوەتان چاکترە بۆتان ئەگەر ئێوە بزانن');
INSERT INTO "bamoki" VALUES(9,42,'9:42','ئەگەر دەست کەوتێکی نزیک و سەفەرێکی ئاسان بوایە ئەوا شوێنت دەکەوتن (دەردەچوون لەگەڵتدا) بەڵام (بڕینی) ڕێگەی دوورو دژواری (جەنگی تەبووک) بۆ ئەوان ناڕەحەتە وە بێگومان (پاش گەڕانەوەتان لەتەبووک) سوێند دەخۆن بەخوا ئەگەر بمانتوانیایە لەگەڵتان دەردەچووین (بۆ جیھاد) ئەوانە خۆیان دەفەوتێنن وە خوا دەزانێت بەڕاستی ئەو (دوو ڕووانە) درۆزنن');
INSERT INTO "bamoki" VALUES(9,43,'9:43','(ئەی موحەممەد صلى الله عليه وسلم) خوا لێت ببورێت لەبەرچی مۆڵەتی ئەو (دوو ڕوانەت) دا ھەتا بۆت دەرکەوێت ئەوانەی کەڕاستیان کرد وە درۆزنەکان بناسیت');
INSERT INTO "bamoki" VALUES(9,44,'9:44','(ئەی موحەممەد صلى الله عليه وسلم) مۆڵەتت لێ وەرناگرن (بۆ ئەوەی خۆیان دوا بخەن لە جیھادکردن) ئەوانەی کە بڕوایان ھێناوە بەخوا و بەڕۆژی دوایی کە تێبکۆشن بەماڵ و گیانیان وە خوا بە ئاگاو زانایە بەخۆ پارێزەران');
INSERT INTO "bamoki" VALUES(9,45,'9:45','بەڕاستی ئەوانە مۆڵەتت لێ وەردەگرن (کە نەیەن بۆ جیھاد کردن) کە بڕوایان نیە بەخوا و بە ڕۆژی دوایی و دڵەکانیان لە گومان و دوو دڵیدایە بۆیە ئەوانە لە گومان و دوودڵی خۆیاندا سەرگەردانن');
INSERT INTO "bamoki" VALUES(9,46,'9:46','ئەگەر بیان ویستایە دەربچن (بۆ جیھادکردن) ئەوە ھۆ و کەرەستەی پێویستیان بۆ ئامادە دەکرد (لەچەک و ووڵاخ وتفاقی جەنگ) بەڵام خوا دەرچوونی ئەوانی پێ ناخۆش بوو بۆیە (خوا) خاوی کردنەوە (لەدەرچوون بۆ جیھاد) پێیان ووترا دانیشن لەگەڵ جێماواندا');
INSERT INTO "bamoki" VALUES(9,47,'9:47','ئەگەر لەگەڵتاندا دەرچوونایە ھیچ شتێکیان بۆ زیاد نەدەکردن بێجگە لە تێکدان و خراپەکاری نەبێت و نێوان ئێوەیان تێک دەدا (بەدوو زمانی و ھاتو ھوت) ئاژاوەیان بۆ دەنانەوە، وە لەناو ئێوەدا خەڵکانێک ھەن چاک گوێیان لێ دەگرن وە خوا بەئاگا و زانایە بەستەمکاران');
INSERT INTO "bamoki" VALUES(9,48,'9:48','سوێند بەخوا بەڕاستی دەستیان دابوویە ئاژاوە و (بشێوی) لەمەو پێش و جۆرەھا فەرمان و پیلانیان بۆ (لاوازکردنت) بەکارھێنا ھەتا ڕاستی ھات وە کارو فەرمانی خوا سەرکەوت کە ئەوان پێیان ناخۆش بوو');
INSERT INTO "bamoki" VALUES(9,49,'9:49','وە ھەندێک کەس لەوانە دەڵێن (ئەی موحەممەد صلى الله عليه وسلم) ڕوخسەتم بدە (با نەیەم بۆ جیھادکردن) و تووشی گوناھو خراپەم مەکە ئاگاداربن (لەئێستەوە) کەوتوونەتە ناو گوناھو خراپەوە و بێگومان دۆزەخ دەوری بێ باوەڕانی داوە');
INSERT INTO "bamoki" VALUES(9,50,'9:50','ئەگەر بەھرە و چاکە و سەرکەوتن بێـتە ڕێگات ناڕەحەت و غەمبار دەبن وە ئەگەر ناڕەحەتی و تێشکانت تووش بێت (بەکەشخەوە) دەڵێن بەڕاستی ئێمە کاری خۆمان کرد لەمەو پێش جا ڕوو وەردەگێڕن بەخۆشحاڵی و شادمانیەوە');
INSERT INTO "bamoki" VALUES(9,51,'9:51','(ئەی موحەممەد صلى الله عليه وسلم) بڵێ ھیچمان تووش نابێت جگە لەوەی کەخوا نوسیویەتی بۆمان خوا پشتیوانی ئێمەیە وە بڕواداران با تەنھا پشت بەخوا ببەستن');
INSERT INTO "bamoki" VALUES(9,52,'9:52','(پێیان) بڵێ: ئایا چاوەڕوانی چی دەکەن بۆ ئێمە بێجگە لە یەکێک لە دوو شتە زۆر چاکە کە (سەرکەوتن یان شەھید بوونە لە پێناو خوادا) ئێمەش چاوەڕوانین بۆ ئێوە کەخوا دووچارتان بکات بەسزایەک لەلایەن خۆیەوە یان بەدەستی ئێمە کەواتە چاوەڕوانی بکەن بێگومان ئێمە لەگەڵ ئێوەدا چاوەڕوانین');
INSERT INTO "bamoki" VALUES(9,53,'9:53','(ئەی موحەممەد صلى الله عليه وسلم) بڵێ: ماڵ ببەخشن بەخۆشی خۆتان یان بەزۆر و ناچاری ھەرگیز لێـتان وەرناگیرێت چونکە بەڕاستی ئێوە کۆمەڵێکی لەسنوور دەرچوو بوون');
INSERT INTO "bamoki" VALUES(9,54,'9:54','وە ھیچ شتێک ڕێگری ئەوانەی نەکردووە کە گیرا بکرێت لێـیان ئەوەی کە بەخشییان بێجگە لەوەی کە بێگومان ئەوانە بێ باوەڕبوون بەخوا و پێغەمبەرەکەی وە نایەن بۆ نوێژکردن بەتەمەڵی نەبێت وە (ھیچێک) نابەخشن ئیللا پێـیان ناخۆشە و ناچارن');
INSERT INTO "bamoki" VALUES(9,55,'9:55','جا با سەرنجت ڕانەکێشێت (سەرسامت نەکات) (زۆری) ماڵ و دارایی و منداڵیان بێگومان خوا دەیەوێت سزایان بدات بەھۆی ئەو (ماڵ و منداڵەوە) لەم ژیانی دونیایەدا وە بەسەختی گیانیان دەربچێت لەکاتێکدا کەئەوان بێ باوەڕن');
INSERT INTO "bamoki" VALUES(9,56,'9:56','وە سوێند دەخۆن بەخوا کەبەڕاستی ئەوان لەئێوەن (لە باوەڕدارانن) کەچی لەئێوە نین بەڵکو ئەوانە کۆمەڵێکن زۆر دەترسن (زراویان چووە)');
INSERT INTO "bamoki" VALUES(9,57,'9:57','ئەگەر پەنایەکیان دەست کەوێت یان ئەشکەوتانێک یان کون و قوژبن و نەفەقێک پەنای بۆ دەبەن و (ڕووی تێدەکەن) لەکاتێکدا زۆر بەپەلە ڕادەکەن (یان ھیچ شتێک جڵەویان ناگرێت)');
INSERT INTO "bamoki" VALUES(9,58,'9:58','وەھەندێ کەس لەوانە ڕەخنەو توانجت لێ دەگرن لەدابەش کردنی خێرەکاندا (یان زەکاتدا) خۆ ئەگەر لەو خێر و سەدەقەیان پێ بدرێت ڕازی دەبن وە ئەگەر پێیان نەدرێ لێی ئەو کاتە ئەوان تووڕە و ناڕازی دەبن');
INSERT INTO "bamoki" VALUES(9,59,'9:59','وە بەڕاستی ئەگەر ئەوانە ڕازی بوونایە بەوەی خواو پێغەمبەرەکەی صلى الله عليه وسلم پێی دابوون وە بیانووتایە خوامان بەسە لەمەو پاش خوا پێمان دەبەخشێت لە فەزڵ ومیھرەبانی خۆی ھەروەھا پێغەمبەرەکەشی (بەشمان دەدات) بەڕاستی ئێمە بەسۆز و پەرۆشین بۆ لای خوا');
INSERT INTO "bamoki" VALUES(9,60,'9:60','بەڕاستی (زەکات) دان تەنھا (بۆ ئەم کومەڵانەیە کە خوای گەورە باسی کردوون) ئەوانیش بۆ بێ نەوایان وە بۆ ھەژاران وە بۆکرێکارانێک کەبەسەریەوەن (زەکات کۆدەکەنەوە) وە ئەوانەی دڵیان دا ئەھێنرێت (بۆ خۆشویستنی ئیسلام) وە بۆ ئازاد کردنی بەندە و کۆیلەکان وە بۆ قەرزاران وە بۆ جەنگاوەرانی ڕێی خوا و بۆ ڕێبوارانی پەککەوتە ئەمە پێویست کراوە وفەرزکراوە لەلایەن خواوە، وە خوا زانا و کاردروستە');
INSERT INTO "bamoki" VALUES(9,61,'9:61','وە لەو ناپاکانەدا کەسانێک ھەیە کە ئازاری پێغەمبەر صلى الله عليه وسلم دەدەن و دەڵێن ئەو خۆش باوەڕ و گوێگرە بۆ ھەموو شت تۆش بڵێ گوێگری چاکەیە (کە قازانجە) بۆتان باوەڕی ھەیە بەخوا و باوەڕی بەئیمانداران ھەیە و میھرەبانیە بۆ ئەوانەی بڕوایان ھێناوە لە ئێوه، وە ئەوانەی کە ئازاری پێغەمبەری خوا صلى الله عليه وسلم دەدەن سزایەکی ئازاردەریان بۆ ھەیە');
INSERT INTO "bamoki" VALUES(9,62,'9:62','(ناپاکان) بەخوا سوێندتان بۆ دەخۆن بۆ ئەوەی لێیان ڕازی بن وە خواو پێغەمبەرەکەی شایستە ترە بەوەی کەڕازی بکەن ئەگەر ئەوانە بڕوادارن');
INSERT INTO "bamoki" VALUES(9,63,'9:63','ئایا ئەوانە نازانن بێگومان ھەرکەس دژایەتی خواو پێغەمبەرەکەی بکات ئەوە بێگومان (تۆڵەی) ئەو کەسە ئاگری دۆزەخە بۆ ھەمیشەیی تیایدا دەمێنێتەوە ئەوەیە ڕیسوایی و زەبوونی زۆر گەورە');
INSERT INTO "bamoki" VALUES(9,64,'9:64','دوو ڕووەکان مەترسی دەکەن لەوەی کە سورەتێک دەربارەیان بنێررێتە خوارەوە کە ھەواڵیان لەسەربدات بەوەی کەلە دڵیاندایە (ئەی موحەممەد صلى الله عليه وسلم بڵێ: گاڵتە و لاقرتی بکەن بەڕاستی خوا دەرخەر و ئاشکراکاری ئەوەیە کەمەترسی لێدەکەن');
INSERT INTO "bamoki" VALUES(9,65,'9:65','سوێندبێت ئەگەر پرسیاریان لێ بکەیت بەڕاستی دەڵێن دڵنیابن ئێمە ھەر دەمەتەقێ و گاڵتە دەکەین تۆش بڵێ: ئایا بە خواو بەڵگەکانی و پێغەمبەرەکەی ئێوە گاڵتە و لاقرتیتان دەکرد؟');
INSERT INTO "bamoki" VALUES(9,66,'9:66','بەھانە مەھێننەوە بەڕاستی ئێوە بێ بڕوا بوونەوە پاش بڕوا ھێنانتان ئەگەر ببورین و چاوپۆشی بکەین لە کۆمەڵ و تاقمێکتان (بەھۆی گەڕانەوەتانەوە) سزای کۆمەڵ و تاقمێکی تر دەدەین لەبەرئەوەی بێگومان ئەوانە تاوانبار بوون');
INSERT INTO "bamoki" VALUES(9,67,'9:67','پیاوانی دوو ڕوو و ئافرەتانی دوو ڕوو ھەندێکیان پشتیوانی ھەندێکیانن فەرمان دەکەن بە خراپە و ڕێگری دەکەن لە چاکە و دەستیان دەقوچن (وخێر ناکەن) خوایان لە بیر چۆتەوە بۆیە خواش لەبیری کردوون و وازی لێ ھێناون بەڕاستی دووڕووەکان ھەر ئەوانن کەدەرچوون لەسنوور');
INSERT INTO "bamoki" VALUES(9,68,'9:68','خوا بەڵێنی داوە بە پیاوو ئافرەتە دوو ڕووەکان و بەبێ باوەرانیش بە ئاگری دۆزەخ دەمێننەوە تیایدا بە ھەمیشەیی ئاگری دۆزەخیان بەسە و خوا نەفرەتی لێ کردوون و سزای ھەمیشەییان بۆ ھەیە');
INSERT INTO "bamoki" VALUES(9,69,'9:69','(ئێوە ئەی دوو ڕووەکان) وەک ئەوانەن لەپێش ئێوەدا بوون ئەوانە زۆر بەھێز تر بوون لەئێوە و ماڵ و سامان و منداڵیشیان زۆرتر بوو (لەئێوە) ئەمجا ئەوان شادمان بوون بەشی خۆیان ڕایان بوارد وە ئێوەش ڕاتان بواردو شادمان بوون بە بەشەکەی خۆتان ھەر چۆنێک ئەوانەی لەپێش ئێوە شادمان بوون بە بەشی خۆیان وە ئێوە ڕۆچوون لەناھەق و بەتاڵدا بەوێنەی ڕۆچوونی ئەوان ئەوانە کردەوەکانیان پوچەڵ و بێسوود بوو لەدونیاو ڕۆژی دواییشدا وە ھەر ئەوانەن زەرەرمەندو ڕەنجەڕۆن');
INSERT INTO "bamoki" VALUES(9,70,'9:70','ئایا نەھاتووە بۆیان و پێیان نەگەیشتووە ھەواڵی ئەوانەی کەلەپێش ئەمانەوە بوون لەگەلی نوح وعاد و سەموود (ھۆزی صالح) وە گەلی ئیبراھیم و خەڵکی مەدیەن و شارەکانی ژێرو زەبەر کراو (ھۆزی لوط) پێغەمبەرەکانیان ھاتن بۆ ناویان بە بەڵگە ڕوونەکانەوە جا خوا ستەمی لێ نەکردن بەڵکو ئەوانە خۆیان ستەمیان لەخۆیان کرد');
INSERT INTO "bamoki" VALUES(9,71,'9:71','پیاوان و ئافرەتانی باوەڕدار ھەندێکیان پشتیوان و یارمەتیدەر و خۆشەویستی ھەندێکی تریانن فەرمان دەدەن بەچاکە و ڕێگری دەکەن لەخراپە و بەڕێک و پێکی نوێژ جێبەجێ دەکەن و زەکاتی (سامانیان) دەدەن وە گوێڕایەڵی خواو پێغەمبەرەکەی دەکەن ئەوانە لەداھاتوودا خوا ڕەحمیان پێدەکات بەڕاستی خوا بەدەسەڵات و کار دروسته');
INSERT INTO "bamoki" VALUES(9,72,'9:72','خوا بەڵێنی داوە بە پیاوان و ئافرەتانی بڕوادار بە بەھەشتانێک کەڕوبارەکان دەڕۆن بەژێریاندا تێیدا دەمێننەوە بەھەمیشەیی و (بەڵێنی پێداوون) بەچەند کۆشکی چاک و ڕازاوە لە بەھەشتانێکی ھەمیشەییدا و ڕەزامەندیش لەلایەن خواوە کە گەورەترە (لە ھەموو شتێک) ھەر ئەوەیە سەرکەوتنی زۆر گەورە');
INSERT INTO "bamoki" VALUES(9,73,'9:73','ئەی پێغەمبەر صلى الله عليه وسلم جیھاد بکە دژی بێ باوەڕان ودووڕوەکان وە توند و تیژ بەلەدژیان شوێنی حەوانەوەی ئەوانە دۆزەخە کە دواڕۆژ و چارە نوسێکی خراپە');
INSERT INTO "bamoki" VALUES(9,74,'9:74','(دوو ڕووەکان) سوێند دەخۆن بەخوا (ئەو ووتانەی دراوەتە پاڵیان) نەیانوتووە سوێند بەخوا بێگومان ووتەی بێ باوەڕییان ووتووە وە بێ بڕوا بوونەتەوە لەپاش موسوڵمان بوونیان و نیازی شتێکیان بوو بەڵام بۆیان نەکرا (کوشتنی پێغەمبەر صلى الله عليه وسلم کاتی گەڕانەوەی لەتەبووک) وە ھیچ ڕەخنەو ڕقێکیان نەبوو تەنھا لەبەر ئەوە نەبێت کە خواو پێغەمبەرەکەی و (بڕوادارانیان) دەوڵەمەند کردوە لە فەزڵ و بەھرەی خۆی ئەمجا ئەگەر ئەوان بگەڕێنەوە وپەپشیمان ببنەوە ئەوە چاکترە بۆیان وە ئەگەر ڕوو وەرگێڕن و (نەگەڕێنەوە) ئەوە خوا سزایان دەدات بەسزایەکی سەخت لە دونیا و پاشەڕۆژدا وە لەزەویدا نیە بۆ ئەوان ھیچ دۆست و یارمەتیدەرێک');
INSERT INTO "bamoki" VALUES(9,75,'9:75','وە لەناپاکان کەسانێک ھەن کەپەیمانیان داوە بەخوا سوێند بەخوا ئەگەر خوا لە فەزڵ و بەھرەی خۆی پێمان ببەخشێت ئەوە بێگومان خێر وچاکە دەکەین وە بێگومان لە چاکەکاران دەبین');
INSERT INTO "bamoki" VALUES(9,76,'9:76','کەچی کاتێک خوا لە فەزڵ و بەھرەی خۆی پێی بەخشین ڕژدی و چروکیان تێداکرد و پشتیان ھەڵکرد لەکاتێکدا ئەوانە ڕوو وەرگێڕن');
INSERT INTO "bamoki" VALUES(9,77,'9:77','ئەمجا خواش سەرئەنجامی ئەوانی گێڕا بەدوو ڕوویی لەدڵیاندا ھەتا ئەو ڕۆژەی بەدیداری (خوا) دەگەن بەھۆی نەبرد نەسەری ئەو بەڵێنەی کەدابوویان بەخوا و بەھۆی ئەوە شەوە درۆیان دەکرد');
INSERT INTO "bamoki" VALUES(9,78,'9:78','ئایا نەیان زانیوە کەبێگومان خوا ئاگادارە بەنھێنی و چپەیان بەڕاستی خوا ئەو پەڕیڕادە ئاگاو زانایە بەشاراوە و پەنھانەکان');
INSERT INTO "bamoki" VALUES(9,79,'9:79','ئەو کەسانەی کەتانە و تەشەر دەدەن لەو باوەڕدارانەی جگە لە زەکات ماڵ و سامانیان دەبەخشن وە (تانە وتەشەر دەدەن) لەوانەی کەتەنھا کەمێکیان ھەیە بەزەحمەت پەیدایان کردووە ئەوسا ھەر گاڵتەشیان پێدەکەن خوا گاڵتە بەوان دەکات (لەبەرامبەر گاڵتەی ئەواندا) و سزای سەخت بۆ ئەوانە');
INSERT INTO "bamoki" VALUES(9,80,'9:80','(ئەی موحەممەد صلى الله عليه وسلم) داوای لێخۆشبوونیان بۆ بکەیت یان داوای لێخۆشبوونیان بۆ نەکەیت (یەکسانە) ئەگەر داوای لێخۆشبوونیان بۆ بکەیت حەفتا جار ئەوە خوا ھەرگیز لێیان خۆش نابێت ئەوەش بەھۆی ئەوەیە بێگومان ئەوانە بێ باوەڕ بوون بەخوا و پێغەمبەرەکەی وە خوا ڕێنمونی خەڵکانی دەرچوو لەئاین ناکات');
INSERT INTO "bamoki" VALUES(9,81,'9:81','(ئەو دوو ڕووانەی) دواکەوتن لە جیھاد کردن و (لەمەدینەدا مابوونەوە) دڵخۆش بوون بەدانیشتن و بەجێمانیان لە پێغەمبەری خوا (لەجەنگی تەبوکدا) وە پێیان ناخۆش بوو کەجیھاد بکەن و تێبکۆشن بەماڵ وگیانیان لەڕێی خوادا (بەئیماندارانیان) دەووت لەم (وەرزە) گەرمایەدا دەرمەچن بۆ جیھاد (ئەی موحەممەد صلى الله عليه وسلم) بڵێ: ئاگری دۆزەخ زۆر گەرمترە (لەم گەرمایە) ئەگەر ئەوانە تێبگەن و بزانن');
INSERT INTO "bamoki" VALUES(9,82,'9:82','دەی با کەم پێ بکەنن و زۆر بگرین (ئەو سزایە) تۆڵەو پاداشتی ئەو کردەوانەیە کەدەیان کرد');
INSERT INTO "bamoki" VALUES(9,83,'9:83','ئەوسا ئەگەر خوا تۆی گەڕاندەوە (ئەی موحەممەد صلى الله عليه وسلم لەتەبوک) بۆلای کۆمەڵێک لەو بەجێماوانە و داوایان لێ کردیت بۆ دەرچوون (بۆ غەزایەکی تر) تۆش بڵێ: ھەرگیز ئێوە لەگەڵ مندا دەرناچن بۆ جیھاد و ھەرگیز ئێوە لەگەڵ مندا جەنگ ناکەن لەگەڵ ھیچ دوژمنێکدا چونکە بێگومان ئێوە ڕازی بوون بەدانیشتن لەیەکەم جاردا کەواتە ھەر دانیشن لەگەڵ بەجێماواندا');
INSERT INTO "bamoki" VALUES(9,84,'9:84','وە (ئەی موحەممەد صلى الله عليه وسلم) تۆ نوێژ مەکە لەسەر ھیچ کەسێک لەوانە کەمرد ھەتا ھەتایە وە ڕامەوەستە لەسەر گۆڕەکەی بێگومان ئەوانە بێ باوەڕ بوون بەخوا و پێغەمبەرەکەی و مردن لەکاتێکدا ئەوانە بەردەوام سنوورشکێن بوون');
INSERT INTO "bamoki" VALUES(9,85,'9:85','وە(ئەی موحەممەد صلى الله عليه وسلم) باسەرت سوڕنەھێنێت ماڵ و منداڵی زۆریان بەڕاستی خوا دەیەوێت بەوە (بەو ماڵ و منداڵ زۆریەیان) سزایان بدات لەدونیادا وە گیانیان دەربچێت لەکاتێکدا ئەوانە بێ باوەڕن');
INSERT INTO "bamoki" VALUES(9,86,'9:86','و ھەر کاتێک سورەتێک بنێردرێتە خوارەوە بەمەی کەباوەڕ بەخوا بھێنن و جیھاد و تێکۆشان بکەن لەگەڵ پێغەمبەرەکەیدا مۆڵەتت لێ وەردەگرن ئەوانەیان کەخاوەن دەسەڵات و سامانن و دەڵێن لێمان گەڕێ و وازمان لێ بھێنە با لەگەڵ دانیشتووەکاندا بین (وەک ئافرەت و منداڵ وپەککەوتەکان)');
INSERT INTO "bamoki" VALUES(9,87,'9:87','ڕازی بوون بەوەی کەلەگەڵ بەجێ ھێڵراوان دا بن (وەک ئافرەت و منداڵ و پەککەوتەکانان) وە مۆر نراوە بەسەر دڵیاندا بۆیە ئەوان ھیچ تێناگەن و نازانن');
INSERT INTO "bamoki" VALUES(9,88,'9:88','بەڵام پێغەمبەر صلى الله عليه وسلم و ئەوانەی کەبڕوایان ھێناوە لەگەڵیدا تێکۆشان بەماڵ و گیانیان ھەموو خێر و خۆشی ھەر بۆ ئەوانە وە ھەر ئەوانە سەرفرازن');
INSERT INTO "bamoki" VALUES(9,89,'9:89','خوا بەھەشتانێکی بۆ ئامادە کردوون کەڕووبارەکان دێن و دەچن بەژێریاندا بەھەمیشەیی تیایدا دەمێننەوە ئەوەیە سەرکەوتنی زۆر گەورە');
INSERT INTO "bamoki" VALUES(9,90,'9:90','بیانوگران لەعەرەبە دەشتەکییەکان ھاتن (بۆ لای پێغەمبەر صلى الله عليه وسلم) بۆ ئەوەی ڕێگەیان پێ بدرێت (نەڕۆن بۆ جیھاد کردن) وە دانیشتن ئەوانەی کەدرۆیان کرد لەگەڵ خوا و پێغەمبەرەکەی بێگومان ئەوانەیان کە بێ بڕوا بوون لە داھاتودا دوو چاریان دەبێت سزایەکی سەختی ئازاردەر');
INSERT INTO "bamoki" VALUES(9,91,'9:91','(گوناھێک) لەسەر بێ ھێز و پەککەوتە ونەخۆشەکان نیە ئەوانەش کەھیچیان نیە خەرجی بکەن (بۆ پێویستی جیھاد) ھیچ گوناھێکیان لەسەر نیە (کەنەڕۆشتوون بۆ جیھاد کردن) کاتێک دڵسۆز و دروست بن بۆ خوا و پێغەمبەرەکەی ھیچ ڕێگەیەک نیە بۆ (تاوانبار کردنی) ئەو چاکەکارانە وە خوا لێخۆشبوو و میھرەبانە');
INSERT INTO "bamoki" VALUES(9,92,'9:92','وە تاوان لەسەر ئەو کەسانەش نیە کە ھاتنە لات (ئەی موحەممەد صلى الله عليه وسلم) بۆ ئەوەی (ووڵاخیان بۆ دابین کەیت) و سواریان کەیت ووتت ووڵاخم دەست ناکەوێت سوارتان بکەم (بۆ جیھاد کردن) ئەوانیش گەڕانەوە لەکاتێکدا چاویان فرمێسکی دەڕشت لەداخ و خەفەتدا کەھیچیان نەبوو خەرجی بکەن (بۆ پێداویستی جیھاد)');
INSERT INTO "bamoki" VALUES(9,93,'9:93','تەنھا ڕێگە (بۆ تاوانبارکردن) لەسەر ئەوانەیە داوای مۆڵەتت لێ دەکەن لەکاتێکدا دەوڵەمەندن و (توانای ڕۆیشتن بۆ جیھادیشیان ھەیە) ڕازی بوون بەوەی کە لەگەڵ بەجماوەکاندا بن (لە جیھاد وەک ئافرەت و منداڵ و نەخۆش) وە خوا مۆری ناوە بەسەر دڵیاندا بەھۆیەوە ھیچ نازانن');
INSERT INTO "bamoki" VALUES(9,94,'9:94','عوزر و بەھانەتان بۆ دەھێننەوە کاتێک (لەجیھاد وغەزا) گەڕانەوە بۆلایان بڵێ بەھانە و عوزر مەھێننەوە ئێمە ھەرگیز باوەڕتان پێ ناکەین چونکە بەڕاستی خوا ئاگاداری کردووین لە ھەواڵەکانتان (کە ئێوە ناپاک و دوو ڕوون) وە لە داھاتوودا خوا و پێغەمبەرەکەی کردەوەکانتان دەبینن پاشان (لەڕۆژی دواییدا) دەگەڕێنرێنەوە بۆ لای زانای ھەموو نھێنی و ئاشکراکان (کە خوای گەورەیە) ئەمجا ھەواڵتان پێدەدات بەوەی لە دونیادا دەتان کرد');
INSERT INTO "bamoki" VALUES(9,95,'9:95','بەزوویی سوێندتان بۆدەخۆن بەخوا کاتێک گەڕانەوە بۆلایان بۆ ئەوەی چاوپۆشیان لێ بکەن و سەرزەنشتیان نەکەن ئێوەش ڕوویان لێوەرگێڕن بەڕاستی ئەو (دوو ڕووانە) پیس و (نەگریسن) وە جێگایان دۆزەخە (لە ڕۆژی دواییدا) تۆڵەیان لێ دەسەنرێت بەھۆی ئەو تاوان وخراپەی لەدونیادا دەیان کرد');
INSERT INTO "bamoki" VALUES(9,96,'9:96','(بە درۆ) سوێندتان بۆ دەخۆن بۆ ئەوەی لێیان ڕازی بن جا ئەگەر ئێوەش لێیان ڕازی بن ئەوە بێگومان خوا ڕازی نابێت لەکەسانی دەرچوو لەسنوور (تاوانبار)');
INSERT INTO "bamoki" VALUES(9,97,'9:97','عەرەبە دەشتەکیەکان بێ بڕوایی و دوو ڕووییان بەھێز ترە و شایستەترن کە نەزانن ئەو سنوورانەی خوا ناردوویەتیە خوارەوە بۆ سەر پێغەمبەرەکەی صلى الله عليه وسلم وە خوا زانا وکاردروستە');
INSERT INTO "bamoki" VALUES(9,98,'9:98','وە لەو عەرەبە دەشتەکیانەدا ھەندێک ھەیە ئەوەی لێی وەردەگیرێت و دەی بەخشێت بەباج و سەرانەی دادەنێت وە چاوەڕوانی دەکەن بەسەر ھاتی خراپتان بەسەردا بێت بەڵاو بەسەر ھاتی خراپ بە سەر خۆیاندا بێت خواش بیسەر و زانایە');
INSERT INTO "bamoki" VALUES(9,99,'9:99','وە ھەندێکی تر لەو عەرەبە دەشتەکیانە ھەیە کە باوەڕیان بەخوا و بەڕۆژی دوایی ھەیە وە وای دادەنێت ئەوەی دەی بەخشێت ھۆی نزیک بوونەوەیە لە خوا وە دوعا کردنی پێغەمبەر صلى الله عليه وسلم بێداربن بێگومان ئەوە ھۆی نزیک بوونەوەیانە لەخواوە لە ئایندەدا خوا دەیانخاتە ناو میھرەبانی و ڕەحمەتی خۆیەوە بێگومان خوا لێبوردە و میھرەبانە');
INSERT INTO "bamoki" VALUES(9,100,'9:100','وە پێشکەوتووە یەکەمەکان (ئەوانەی لەسەرەتاوە باوەڕیان ھێنا) لەکۆچەران و یاریدەدەران ئەوانەش زۆر بە چاکی پەیڕەوی ئەوانیان کردووە و شوێنیان کەوتوون خوا لێیان ڕازیە ئەوانیش لەخوا ڕازین وە (خوا) بەھەشتانێکی بۆ ئامادە کردوون کە ڕووبارەکان دێن و دەچن بەژێریاندا تێیدا دەمێننەوە بە ھەمیشەیی و بێ بڕانەوە ئەوەیە گەیشتن بە ئامانجی گەورە');
INSERT INTO "bamoki" VALUES(9,101,'9:101','و لەو عەرەبە دەشتەکیانەی کە لە دەورو بەرتاندان ناپاکانێک ھەن ھەندێک لەخەڵکی مەدینەش لەسەر ناپاکی ڕاھاتوون و خۆیان بۆ تەرخان کردووە تۆ (ئەی موحەممەد صلى الله عليه وسلم) نایانناسیت ئێمە چاک دەیا نناسین لە ئایندەدا دووجار سزایان دەدەین دواتریش دەگەڕێنرێنەوە بۆ ناو سزایەکی گەورە (بۆ دۆزەخ)');
INSERT INTO "bamoki" VALUES(9,102,'9:102','وە کەسانێکی تر ھەن دانیان ناوە بەگوناھەکانیاندا کردەوەی چاکیان تێکەڵ کردووە لەگەڵ کردەوەی تری خراپدا ئومێد وایە خوا تۆبەیان لێ وەرگرێت بەڕاستی خوا لێبوردە و میھرەبانە');
INSERT INTO "bamoki" VALUES(9,103,'9:103','وەربگرە لەماڵ و داراییان (زەکات و سەدەقە) تا بەھۆیەوە پاکیان بکەیتەوە (لە گوناھو تاوان) و (دڵیان) پوخت و خاوێن دەکەیت بەو ماڵ بەخشینە و دوعا و نزای خێریان بۆبکە بەڕاستی دوعای تۆ ئارامی و دڵ خۆشیە بۆ ئەوان وەخوا بیسەر و زانایە');
INSERT INTO "bamoki" VALUES(9,104,'9:104','ئایا نەیان زانیوە کە بێگومان ھەر خوا تەوبە و پەشیمانی لە بەندەکانی وەردەگرێت وە ھەر ئەو خێر وبەخشینەکان وەردەگرێت وە بەڕاستی ھەر خوایە وەرگری تەوبە یە و میھرەبانە (لەگەڵ بەندەکانیدا)');
INSERT INTO "bamoki" VALUES(9,105,'9:105','(ئەی موحەممەد صلى الله عليه وسلم بەوانە) بڵێ: کارو کردەوە بکەن جا بێگومان خوا و پێغەمبەرەکەی و بڕواداران کردەوەکانتان دەبینن لەپاشان دەگەڕێنرێنەوە بۆلای (خوای) زانا بە ھەموو نھێنی و ئاشکراکان ئەمجا ھەواڵتان دەداتێ بەھەرچی کە ئێوە لە دونیادا دەتان کرد');
INSERT INTO "bamoki" VALUES(9,106,'9:106','(لە بە جێماوەکاندا) کەسانێکی تر ھەبوون کە دواخران بۆ فەرمان و بڕیاری خوا یان سزایان دەدات یان پەشیمانیان لێ وەردەگرێت وە خوا زانا و کار دروستە');
INSERT INTO "bamoki" VALUES(9,107,'9:107','وە (لەناپاکان) ئەوانەی مزگەوتێکیان دروست کرد بۆ زیان گەیاندن (بە موسڵمانان) و خوانەناسی و جیاوازی خستنە نێوان ئیمانداران و بۆ چاوەڕوانی کردنی کەسانێک کە دژایەتی خوا و پێغەمبەرەکەیان کردوە لەوەو پێش بێگومان (ئەو دوو ڕووانە) سوێند دەخۆن (دەڵێن) تەنھا چاکەمان ویستووە بەڵام خوا شایەتی دەدات کە بەڕاستی ئەوانە درۆزنن');
INSERT INTO "bamoki" VALUES(9,108,'9:108','ھەرگیز نوێژ (لەو مزگەوتەدا) نەکەی (ئەی موحەممەد صلى الله عليه وسلم) بێگومان مزگەوتێک کە لەسەر بناغەی تەقوا و لە خوا ترسان دامەزرا بێت لە یەکەم ڕۆژەوە (یەکەم ڕۆژ ھاتنت بۆ شاری مەدینە) شایستەترە کە نوێژی تێدا بکەیت پیاوانێکی تێدایە پێیان خۆشە کە خۆیان خاوێن و پاک بکەنەوە (لەژەنگ وژاری سەردەمی نەفامی) خوایش ئەوانەی خۆش دەوێت کە دەیانەوێت خۆیان پاک بکەنەوە');
INSERT INTO "bamoki" VALUES(9,109,'9:109','جا ئایا کەسێک بناغەی مزگەوتەکەی دامەزراند بێت لەسەر خواناسی و بەدەست ھێنانی ڕەزامەندی خوا چاکترە یان کەسێک بناغەی مزگەوتەکەی دامەزراند بێت لەسەر لێواری کەندەڵانێکی فشەڵ و ڕووخەک جا بەخۆیەوە بڕوخێتە ناو ئاگری دۆزەخەوە، وە خوا ڕێنمونی کەسانی ستەمکار ناکات');
INSERT INTO "bamoki" VALUES(9,110,'9:110','بەردەوام ئەو دروست کراوەیان کە دروستیان کرد ھۆی بێ ئارامی و گومانە لە دڵیاندا (واتە ھەرگیز لە دڵیان دەرناچێت) مەگەر دڵیان پارچە پارچە بکرێت و (بمرن) وە خوا زانا و کار دروستە');
INSERT INTO "bamoki" VALUES(9,111,'9:111','بەڕاستی خوا کڕیویەتی لە باوەڕداران گیان و ماڵیان بەوەی کە بەھەشتیان دەداتێ لە بریتیدا (ئەو بڕوادارانە) دەجەنگن لە ڕێی خوادا ئەمجا (بێ باوەڕان) دەکوژن و (خۆشیان) دەکوژرێن (شەھید دەبن) ئەمە بەڵێنێکی ڕاستە (و خـوا بـڕیـاری داوە) لەسەرخۆی لە تەورات و ئینجیل و قورئاندا جا کـێ بە بەڵێن و پەیمانی خۆی وەفادارترە وەک خوای گەورە؟ کەواتە مژدە بەخشن و شادمان بن بەو فرۆشتنەتان کە مامەڵەتان پێوە کرد (بە ڕاستی) ھەر ئەوەیە گەیشتن بە ئامانجی زۆر گەورە');
INSERT INTO "bamoki" VALUES(9,112,'9:112','(موجاھیدانی ڕێگای خوا، ئەمانە سیفەتیانە) تۆبەکارن خواپەرستن سوپاسگوزار وگەشتیارن (کۆچەرن لەپێناو خوادا) کـڕنوش و سـوژدە بەرن (نوێژ خوێنن) فەرمـاندەرن بەچاکە و ڕێگریکەرن لـە خراپە پـارێــزەران بۆ سنوورەکانی خوا جا (ئەی موحەممەد صلى الله عليه وسلم) موژدە بدە بەو باوەڕدارانەی (ئەو سیفاتانەیان تێدایە)');
INSERT INTO "bamoki" VALUES(9,113,'9:113','ڕەوا وشایستە نیە بۆ پێغەمبەر وباوەڕداران کە داوای لێخۆشبوون بکەن بۆ ھاوبەشدانەران با خزمی نزیکیشیان بن لە پاش ئەوەی کە بۆیان دەرکەوتووە کە بێگومان ئەو (ھاوبەشدانەرانە) ھاوەڵ و پێڕی دۆزەخن');
INSERT INTO "bamoki" VALUES(9,114,'9:114','وە داوای لێخۆشبوونی ئیبراھیم بۆ باوکی تەنھا لەبەر وادە و بەڵێنێک بوو کە بە باوکی دابوو (کە داوای لێخشبوونی بۆ بکات) جا کاتێک بۆی ڕوون بوویەوە کە بێگومان باوکی دوژمنی خوایە خۆی لێبەری کرد بەڕاستی ئیبراھیم زۆر لە خوا ترس و لەسەر خۆ بوو');
INSERT INTO "bamoki" VALUES(9,115,'9:115','وە خوا ھیچ گەل وھۆزێک گومڕا ناکات دوای ئەوەی ڕێنمونی کردوون ھەتا بۆیان ڕوون نەکاتەوە ئەوەی پێویستە خۆیانی لێ بپارێزن بێگومان خوا زانا و ئاگادارە بەھەموو شتێک');
INSERT INTO "bamoki" VALUES(9,116,'9:116','بێگومان ھەر بۆ خوایە پادشایەتی وموڵکی ھەموو ئاسمانەکان و زەوی (خوا) دەژێنێت و دەشمرێنێت وە بێجگە لەخوا بۆ ئێوە نیە ھیچ دۆست و فریادڕەس و یارمەتیدەرێک');
INSERT INTO "bamoki" VALUES(9,117,'9:117','بێگومان خوا تەوبەی وەرگرت لە پێغەمبەر صلى الله عليه وسلم و لەو کۆچەر و یاریدەرانەی کە شوێنی (پێغەمبەر صلى الله عليه وسلم) کەوتن لە کاتی سەختی و ناڕەحەتیدا (مەبەست پێی جەنگی تەبوکە) پاش ئەوەی نزیک بوو دڵی کۆمەڵێکیان لە ھەق لابدات پاشان خوا تەوبەی لێ وەرگرتن بە ڕاستی خوا لەگەڵیان دڵسۆز و میھرەبانە');
INSERT INTO "bamoki" VALUES(9,118,'9:118','ھەروەھا (خوا تەوبەی وەرگرت) لەو سێ کەسەش کە دواخران (خوا دەربارەیان بڕیاری نەدابوو) ھەتا ئەو کاتەی لێیان تەسک بوویەوە زەوی بەو فراوانیەی کەھەیەتی و بێزاربوون لەگیانی خۆشیان و زانیان ھیچ پەناگەیەک نیە لە (سزای) خوا مەگەر پەنایی خۆی نەبێت پاشان پەشیمانی لێ وەرگرتن (بە دابەزاندنی چەند ئایەتێک) بۆ ئەوەی تۆبە بکەن بەڕاستی خوا ھەمیشە تۆبە وەرگری میھرەبانە');
INSERT INTO "bamoki" VALUES(9,119,'9:119','ئەی ئەوانەی بڕواتان ھێناوە خۆتان بپارێزن لە (تۆڵەی) خوا وەلەگەڵ ڕاست گۆیاندا بن و لەڕیزی ئەواندابن');
INSERT INTO "bamoki" VALUES(9,120,'9:120','نەدەبوو خەڵکی مەدینە و ئەو عەرەبە دەشتەکیانەی بەدەوور و بەریاندان لە پێغەمبەری خوا صلى الله عليه وسلم بەجێ بمێنن و دوابکەون (لەجیھادکردن) و گیانی خۆیان لە گیانی پێغەمبەر صلى الله عليه وسلم خۆشتربوێت ئەوە لەبەر ئەوەیە کە بێگومان ئەوان دووچاریان نابێت ھیچ تینوویەتی و ماندوو بوون و برسێتیەک لەڕێی خوادا ھەروەھا پێ ناخەنە ھیچ شوێنێک (لەخاکی کافران) کە ڕقی کافرانی پێ ھەڵسێ و توڕەیان کات وە ھیچ زیانێک لە دوژمن نادەن (دیل کردن و کوشتنیان یا گرتنی ماڵ و سامانیان) ئیلا بەم کردەوانە بۆیان دەنوسرێت کردەوەیەکی چاک بێگومان خوای گەورە پاداشتی چاکە کاران وون ناکات');
INSERT INTO "bamoki" VALUES(9,121,'9:121','وە ھیچ شتێک (لەڕێگەی خودا) نابەخشن بچووک بێت یان گەورە (کەم بێت یان زۆر) و ھیچ دۆڵ وشیوێک نابڕن ئیلا بۆیان دەنووسرێت بۆ ئەوەی خوا پاداشتیان بداتەوە بەچاکترین پاداشتی ئەوەی کە دەیانکرد');
INSERT INTO "bamoki" VALUES(9,122,'9:122','وە نەدەبوو باوەڕداران تێکڕا ھەموو دەرچن و بڕۆن (بۆجیھاد کردن) ئەگەر دەربچوونایە لە ھەموو کۆمەڵێکیان چەند کەسێک بۆ ئەوەی لە ئاینەکەیان تێبگەن و شارەزابن بۆ ئەوەی گەل و ھۆزەکەیان (شارەزا بکەن لەوەی کە فێری بوون) بیشیان ترسێنن (لەئاگری دۆزەخ) کاتێک گەڕانەوە (لەجیھاد کردن) بۆلایان بۆ ئەوەی ئەوانیش (شارەزاببن) وبێدار ببنەوە (لەنەزانی)');
INSERT INTO "bamoki" VALUES(9,123,'9:123','ئەی ئەوانەی کە ئیمانتان ھێناوە بجەنگن لەدژی ئەوانەی کە نزیکن لێتانەوە لە بێ بڕوایان وە با بێ باوەڕان لە ئێوەدا توند و تیژی ببینن و بزانن بێگومان خوا لەگەڵ پارێزکاران و لەخوا ترساندایە');
INSERT INTO "bamoki" VALUES(9,124,'9:124','وە کاتێک سورەتێک (لەقورئان) بنێردرێتە خوارەوە (دوو ڕووەکان) ھی وایان ھەیە دەڵێت کام لە ئێوە باوەڕی بەم (سورەتە) زیادی کرد جا ئەوانەی کە باوەڕیان ھێناوە ئەمە ئیمانیان زیاد دەکات (و بەھێزتر دەبێت) وە ئەوان مژدە بەخشن و خۆش حاڵن');
INSERT INTO "bamoki" VALUES(9,125,'9:125','بەڵام ئەوانەی لەنێو دڵیاندا نەخۆشی (و ناپاکی) ھەیە (وەک دوو ڕوەکان) ئەوە سورەتەکە بۆیان زیاد دەکات سەرەڕای پیسی و ناپاکی خۆیان پیسی و ناپاکی تر ئەو (ناپاکانە) دەمرن لەکاتێکدا کە بێ بڕوان');
INSERT INTO "bamoki" VALUES(9,126,'9:126','ئایا ئەوانە نازانن و نابینن بێگومان ئەوان تاقی دەکرێنەوە لەھەموو ساڵێکدا جارێک یان دووجار پاشان ئەوان ناگەڕێنەوە و تۆبەناکەن و پەند وەرناگرن');
INSERT INTO "bamoki" VALUES(9,127,'9:127','وە کاتێک سورەتێک بنێردرێتە خوارەوە (ئەو دووڕوانە) دەڕوانن بۆ یەکتری (دەڵێن) ئایا ھیچ کەس دەتان بینێت؟ پاشان (بە دزیەوە) دەگەڕێنەوە خوا دڵیانی وەرگێڕا (لە بڕواھێنان) چونکە بەڕاستی ئەوانە کۆمەڵێکن ھیچ تێ ناگەن');
INSERT INTO "bamoki" VALUES(9,128,'9:128','سوێند بەخوا بەڕاستی بۆتان ھاتووە پێغەمبەرێک لە خۆتان قورس و گرانە بەلایەوە ناڕەحەتی و ناخۆشی ئێوە زۆر سوورە لەسەر (باوەڕھێنان) تان بۆ بڕواداران زۆر دڵسۆز و میھرەبانە');
INSERT INTO "bamoki" VALUES(9,129,'9:129','جا ئەگەر ڕوویان وەرگێڕاو گوێیان پێ نەدایت ئەوسا بڵێ من تەنھا خوام بەسە کە ھیچ پەرستراوێکی ڕاست نیە جگە لە ئەو تەنھا پشت بەو دەبەستم ئەویش پەروەردگاری عەرشی گەورەیە');
INSERT INTO "bamoki" VALUES(10,1,'10:1','بە (ئەلیف، لام، ڕا) دەخوێنرێتەوە خوا زاناترە بە ماناکەی بۆ زانیاری زیاتر سەیری سەرەتای سورەتی (البقرە) بکە ئەمە ئایەتەکانی قورئانی نەگۆڕاوی پڕ حیکمەتە');
INSERT INTO "bamoki" VALUES(10,2,'10:2','ئایا بەلای خەڵکەوە سەیرە کە ئێمە نیگامان کردوە بۆ پیاوێک لەخۆیان (پێمان ووتووە) کە ئادەمی بترسێنە وە موژدە بدە بەوانەی باوەڕیان ھێناوە کە بێگومان بۆیان ھەیە پلە و پایەی بەرز لای پەروەردگاریان بەڵام بێ باوەڕان ووتیان بێگومان ئەم (پیاوە) جادوگەرێکی ئاشکرایە');
INSERT INTO "bamoki" VALUES(10,3,'10:3','بەڕاستی پەروەردگاری ئێوە ئەو زاتەیە کە ھەموو ئاسمانەکان و زەوی دروستکردووە لەشەش ڕۆژدا پاشان بەرزبویەوە سەر عەرش و قەراری گرت (باوەڕمان پێیەتی بەڵام چۆنیەتێکەی نازانین) (خوا) ھەموو کاروبارەکان ھەڵدەسوڕِێنێت ھیچ تکا کارێک نیە (بۆ کەس) مەگەر لەپاش رێگەدانی ئەو (خوا) نەبێت ئەوزاتە پەروەردگاری ئێوەیە کەواتە ھەر ئەو بپەرستن دەی ئایا ئێوە بیرناکەنەوە');
INSERT INTO "bamoki" VALUES(10,4,'10:4','گەڕانەوەی ھەمووتان ھەر بۆلای ئەوە بەڵێنی خوا حەق وڕاستە بێگومان خوا لەسەرەتاوە دروستکراوان دروست دەکات دوای (مردنیان) دووبارە دروستیان دەکاتەوە بۆ ئەوەی پاداشتی ئەوانە بداتەوە کە بڕوایان ھێناوە و کردەوەی چاکیان کردوە بە دادگەرانە وە ئەوانەی باوەڕیان نەھێناوە بۆیان ھەیە خواردنەوەی کوڵاو کوڵی زۆر گەرم وە سزای سەختی ئازاردەر لەبەر ئەوەی لەدونیادا بێ باوەڕبوون');
INSERT INTO "bamoki" VALUES(10,5,'10:5','خوا زاتێکە کە خۆری گێڕاوە بە تیشک مانگیشی گێراوە بەڕووناکی بۆ (گەڕانی) مانگ چەند قۆناغێکی دیاری کردوە بۆ ئەوەی ژمارەی ساڵەکان و حیسابی (مانگ و ڕۆژی) پێ بزانن خوا ئەوانەی دروست نەکردوە بەحەق و ڕاستی نەبێت ئایەتەکان شیدەکاتەوە بۆ کۆمەڵێک کە زانان و تێدەگەن');
INSERT INTO "bamoki" VALUES(10,6,'10:6','بێگومان لە جیاوازی شەو و ڕۆژدا و ھەرچی خوا دروستیکردوە لە ئاسمانەکان و زەویدا چەند نیشانە و بەڵگە ھەیە بۆ ئەوانەی کە خۆیان دەپارێزن و لە خوا دەترسن');
INSERT INTO "bamoki" VALUES(10,7,'10:7','بەڕاستی ئەوانەی بەھیوای دیداری ئێمە نین (لە دواڕۆژدا) وڕازی بوون بەژیانی دونیا ودڵنیا بوون پێی وە ئەوانەی بێ ئاگان لە ئایەتەکانی ئێمە');
INSERT INTO "bamoki" VALUES(10,8,'10:8','ئەوانە جێگایان دۆزەخە لەبەر ئەو کردەوانەی کە (لەدونیادا) دەیان کرد');
INSERT INTO "bamoki" VALUES(10,9,'10:9','بەڕاستی ئەوانەی بڕوایان ھێناوە و کردەوە چاکەکانیان کردوە پەروەردگاریان ڕێنمونیان دەکات بەھۆی باوەڕەکەیانەوە ڕوبارەکان دێن و دەچن بەژێریاندا لە باخ وبەھەشتانی پڕ نازو نیعمەتدا');
INSERT INTO "bamoki" VALUES(10,10,'10:10','دوعایان تێیدا (ئەوەیە کەدەڵێن) پاکو بێگەردی بۆتۆ ئەی پەروەردگارمان سڵاو کردن لێیان لەو (بەھەشتەدا) بەووشەی سەلامە وە دواھەمین دوعایان (ئەوەیەکە دەڵێن) بێگومان سوپاس و ستایش ھەر بۆ خوایە کە پەروەردگاری ھەموو جیھانە');
INSERT INTO "bamoki" VALUES(10,11,'10:11','وەئەگەر خوا پەلەی بکردایە لە (گیرا کردنی دوعای) شەڕ و خراپەدا بۆ خەڵکی وەک پەلەکردنی (گیرا کردنی دوعای) خێر وخۆشی ئەوە ماوەکەیان دوایی پێ دەھێنرا و (کاتی مردنیان دەھات) بۆیە مۆڵەتی ئەوانە دەدەین بەھیوای دیداری ئێمە نین (لەڕۆژی دواییدا) لە نێو گومڕایی و سەرکەشیی خۆیان دا بسوڕێنەوە و بتلێنەوە');
INSERT INTO "bamoki" VALUES(10,12,'10:12','وەکاتێک ئادەمی زیانێکی لێ بکەوێت بەڕاکشان یان بەدانیشتن یان بەپێوە لێمان دەپاڕێتەوە ئەمجا کاتێک زەرەر و زیانەکەمان لەسەر لابرد و نەمان ھێشت دەڕوات (دەچێتە سەر حاڵی پێشووی و دەگەڕێتەوە سەر کاری ناپەسەندی) وەک ھاوار و دوعای نەکردبێت لێمان نەپاڕابێـتەوە بۆ لابردنی زیانێک کە دووچاری بوو بوو بەو جۆرە ڕازێنراوەتەوە بۆ زیادە ڕەۆیان ئەو کردەوانەی کە دەیان کرد');
INSERT INTO "bamoki" VALUES(10,13,'10:13','سوێند بەخوا بەڕاستی لەناومان برد چەند گەل و نەتەوە لەپێش ئێوەدا کاتێک ناھەقیان کرد لەکاتێکدا کەپێغەمبەرانیان بەبەڵگەی ڕوونەوە بۆ ھات ھەر باوەڕیان نەھێنا ئا بەو جۆرە تۆڵە لە کۆمەڵی تاوانباران دەستێنین');
INSERT INTO "bamoki" VALUES(10,14,'10:14','لەپاشان ئێوەمان کرد بەجێگر و جێنشین لەزەویدا لەپاش ئەوان بۆ ئەوەی سەرنج بدەین ئێوە چۆن ڕەفتار وکردەوە دەکەن');
INSERT INTO "bamoki" VALUES(10,15,'10:15','وەکاتێک بخوێنرێتەوە بەسەریاندا ئایەتە روونەکانی ئێمە ئەوانەی بەتەمای دیداری ئێمە نین (لەدوارۆژ دا) دەڵێن قورئانێکی تر جگەلەم قورئانە بھێنە یان بیگۆڕە (ئەی موحەممەد صلى الله عليه وسلم) پێیان بڵێ بۆ من نیە کە لەلایەن خۆمەوە بیگۆڕم من شوێن ھیچ شتێ ناکەوم جگەلەوەی وەحی و سرووش دەکرێت بۆم بەڕاستی من دەترسم ئەگەر سەرپێچی پەروەردگارم بکەم لەسزای ڕۆژێکی گەورە (کە ڕۆژی دواییە)');
INSERT INTO "bamoki" VALUES(10,16,'10:16','(پێیان) بڵێ ئەگەر خوا بیویستایە ئەم قورئانەم نەدەخوێندەوە بۆتان وە لەو (قورئانە) ئاگادارم نەدەکردن چونکە بێگومان من مامەوە لەناوتاندا ماوەی تەمەنێکی درێژ پێش ھاتنی ئەم قورئانە دەی ئایا ئێوە بۆ ژیر نابن و بیرناکەنەوە؟');
INSERT INTO "bamoki" VALUES(10,17,'10:17','جا کێ ستەمکارترە لەو کەسەی درۆ بەدەم خواوە ھەڵبەستێ یان ئایەتەکانی قورئان بەدرۆ دابنێت بەڕاستی تاوان باران ڕزگاریان نابێت');
INSERT INTO "bamoki" VALUES(10,18,'10:18','ئەوانە بێجگە لەخوا شتانێک دەپەرستن کە نەزیانیان پێ دەگەیەنن نەسوود و قازانج وە دەڵێن ئەو بتانە تکا کارمانن لای خوا (ئەی موحەممەد صلى الله عليه وسلم بەوانە) بڵێ ئایا ئێوە ھەواڵ دەدەن بەخوا بەشتێک کەبێ ئاگایە لێی لەئاسمانەکان و زەویدا خوا پاک و بێگەرد و بڵندە لەوەی کە دەیکەنە ھاوبەشی');
INSERT INTO "bamoki" VALUES(10,19,'10:19','خەڵکی لەرابوردودا ھەریەک ئوممەت بوون (بڕوا دار بوون بەخوا) ئەمجا جیاواز بوون وە ئەگەر بڕیارێک پێشتر نەدرایە لەلایەن پەروەردگارتەوە ئەوا بڕیار دەدرا لەنێوانیاندا لەوەی کە تێیدا جیاواز بوون');
INSERT INTO "bamoki" VALUES(10,20,'10:20','وە ئەوانە دەڵێن با بھێنرایەتە خوارەوە بۆی بەڵگەو نیشانەیەک لەلایەن پەروەردگاریەوە تۆش بڵێ بێگومان (زانینی) نھێنی و پەنھانی تایبەت بە خوایە کەواتە چاوەڕوانی بکەن بەڕاستی منیش لەگەڵ ئێوەدا یەکێکم لە چاوەڕوانکاران');
INSERT INTO "bamoki" VALUES(10,21,'10:21','وە کاتێک کە نیعمەت و ڕەحمەتی خۆمان دەدەین بەخەڵکی و تامی ئەو نیعمتەیان پێدەچێژین لەپاش زیان و ناخۆشیەک کە لێیان کەوتووە دەست بەجێ ئەوان پیلان دەگێڕن دەربارەی بەڵگە و ئایەتەکانی ئێمە (ئەی موحەممەد صلى الله عليه وسلم) بڵێ خوا خێراترە لە پیلان کردندا (لە بەرامبەر پیلانی ئەواندا) بێگومان فریشتە و چاودێرەکانمان ھەر پیلان و فڕو فێڵێک (ئەو بێ باوەڕانە )دەیکەن دەینوسن');
INSERT INTO "bamoki" VALUES(10,22,'10:22','خوا زاتێکە ھاتو چۆتان پێ دەکات لەووشکایی و لەدەریادا ھەتا کاتێک ئێوە لەکەشتیدان (کەشتیەکان) دەیان گێڕێت بەھۆی شنە بایەکی خۆشەوە و خۆش حاڵ دەبن پێی (لەپڕ) ڕەشە بایەکی زۆر بەھێز لەو (کەشتییانە) ھەڵکات وشەپۆلیان بۆ بێت لەھەموو لایەکەوە، وە دڵنیا بوون لەوەی کە ئەوان دەورە دراون و (لەناو دەچن) ھاوار دەکەنە خوا ی گەورە زۆر بەدڵسۆزی وگەردنی بۆ کەچ دەکەن (و دەڵێن) سوێند بەخوا ئەگەر ڕزگارمان بکەیت لەم سەختی وناڕەحەتیە بێگومان ئێمە لە سوپاسگوزاران دەبین (بۆتۆ ئەی خوایە)');
INSERT INTO "bamoki" VALUES(10,23,'10:23','ئەمجا کاتێک خوا ڕزگاری کردن ( لەو ناڕەحەتیە) کوتوپڕ دەست دەکەنەوە بە خراپەکاری و بێ باوەڕی لەزەویدا بە ناڕەوا ئەی ئادەمیە (خراپەکارەکان) لەڕاستیدا ستەم و ناحەقیەکانتان زیانی ھەر بۆ خۆتانە لەزەتی ژیانی دونیاش بێ نرخە پاشان گەڕانەوەتان ھەر بۆلای ئێمەیە ئەمجا ھەواڵتان پێ دەدەین بەوەی کە (لەدونیادا) دەتان کرد');
INSERT INTO "bamoki" VALUES(10,24,'10:24','بێگومان وێنەی ژیانی دونیا وەک بارانێک وایە لەئاسمانەوە باراندومانە جا بەھۆی ئەو (بارانەوە ڕوەکی زۆر ڕواوە و) سەوزی و ڕوەکی زەوی تێکەڵ بەیەک بوون وبە یەکدا چوون لەوەی کە خەڵکی و ئاژەڵیش دەیخۆن ھەتا کاتێک زەوی (بەسەوزی و ڕوەک) ڕەونەق و جوانی خۆی وەردەگرێت و خۆی دەڕازێنێتەوە دانیشتوان و خەڵکی ئەو (زەویە) وا دەزانن کەبە ڕاستی ئەوان بەتوانان بەسەر (دروێنە کردن) یدا (لەناکاوێکدا) فەرمانی ئێمە بێت بۆ ئەو (زەوی و کشتوکاڵە) لەشەو یان لەڕۆژدا ئەمجا وەک دروێنە کراوی لێ بکەین (وھیچیان بۆ نەمێنێتەوە) وەک لەوەو پێش ھەر نەبووبێت ئابەو جۆرە ڕوونی دەکەینەوە ئایەت و بەڵگەو نیشانەکانمان بۆ گەلێک کە بیربکەنەوە');
INSERT INTO "bamoki" VALUES(10,25,'10:25','وە خوا (خەڵک) بانگ دەکات بۆ ماڵی ئاشتی و ئارامی (بەھەشت) وە ڕێنمونی ھەر کەسێک دەکات کە بیەوێت بۆ ڕێگەی ڕاست');
INSERT INTO "bamoki" VALUES(10,26,'10:26','بۆ ئەوانەی چاکەیان کردوە پاداشتی چاکتر (بەھەشت) و زیاتریان ھەیە (کەبینینی خوایە) دەم و چاویان دایناگرێت (لێی نانیشێت) ھیچ ڕەشی وتەپ و تۆزو ڕیسواییەک ئەوانە ھاوڕێی بەھەشتن ئەوانە لەو (بەھەشتە) دان بەھەمیشەیی');
INSERT INTO "bamoki" VALUES(10,27,'10:27','ئەوانەش کەکردەوەی خراپیان کردووە تۆڵەی ھەر خراپەیەک بەئەندازەی ئەو (خراپەیە تۆڵەی لێ دەسێندرێت) وە ڕیسوایی دایان دەگرێت بۆیان نیە ھیچ پەنادەرێک لە (سزای) خودا (بەڕادەیەک ڕوویان ڕەش بووە) دەڵێی دەم و چاوویان داپۆشراوە بە چەند بەشێک لەشەوە زەنگ ئەوانەی (کەباس کران) ھاوڕێی ئاگری دۆزەخن ئەوان تێیدا دەمێننەوە بەھەمیشەیی');
INSERT INTO "bamoki" VALUES(10,28,'10:28','(ئەی موحەممەد صلى الله عليه وسلم باسی بکە بۆیان) ئەو ڕۆژەی ھەموویان کۆدەکەینەوە ئەمجا دەڵێین بەوانەی ھاوبەشیان بۆ خوا بڕیار داوە لەشوێنی خۆتاندا نەجوڵێن (ڕاوەستن) خۆتان و بت و پەرستراوەکانتان ئەمجا لێکیان جیادەکەینەوە پەرستراوەکانیان دەڵێن ئێوە ئێمەتان نەدەپەرست');
INSERT INTO "bamoki" VALUES(10,29,'10:29','جا خوا بەسە کە شاھید بێت لەنێوان ئێمە و ئێوەدا بێگومان ئێمە بێ ئاگابووین لەبەندایەتی کردنتان بۆ ئێمە');
INSERT INTO "bamoki" VALUES(10,30,'10:30','لەوێدا دەردەکەوێت ھەموو کەسێک چی پێش خۆی خستووە، وە ھەموو دەگەڕێنرێنەوە بۆلای خوا کە کۆمەکیکار وپەروەردگاری حەق و ڕاستیانە وە لێیان وون دەبێت ئەو (پەرستراوە درۆیانەی) ھەڵیان بەستبوون');
INSERT INTO "bamoki" VALUES(10,31,'10:31','(ئەی موحەممەد صلى الله عليه وسلم) بڵێ ئایا کێ ڕزق و ڕۆزیتان دەدات لەئاسمان (بە باران) و لەزەویەوە (بە ڕووەک) یان کێ خاوەنی (دروستکەری) گوێ و چاوانە وە کێ زیندوو لە مردوو دەردەھێنێت وە مردوو لەزیندوو دەردەھێنێت ھەروەھا کێ کارەکان ھەڵدەسوڕێنێ بێگومان یەکسەر دەڵێن خوا کەواتە (ئەی موحەممەد صلى الله عليه وسلم) پێیان بڵێ دەی بۆ خۆتان ناپارێزن (لەسزای خوا)؟');
INSERT INTO "bamoki" VALUES(10,32,'10:32','ھەر ئەو خوایە پەروەردگاری ڕاستی ئێوەیە دەی لەپاش ڕاستی ھیچ ھەیە جگە لەگومڕایی دەی چۆن لەڕێگەی ڕاست لادەدرێن؟!');
INSERT INTO "bamoki" VALUES(10,33,'10:33','بەو جۆرە چەسپاوە بڕیاری پەروەردگارت لەسەر ئەوانەی لەسنوور لایانداوە بێگومان ئەوانە بڕوا ناھێنن');
INSERT INTO "bamoki" VALUES(10,34,'10:34','(ئەی موحەممەد صلى الله عليه وسلم) بڵێ لەوانەی کردوتانە بەھاوبەشی خوا کەسێک ھەیە شتێک لەنەبوونەوە دروست بکات ئەمجا (لەپاش مردنی) دووبارەی بکاتەوە بڵێ خوا لە نەبوو شت دروست دەکات ئەمجا (لە پاش مردنی) دروستی دەکاتەوە دەی چۆن لە ڕاستی لا دەدرێن');
INSERT INTO "bamoki" VALUES(10,35,'10:35','بڵێ ئایا لە ناو پەرستراوەکانتاندا کەسێ ھەیە ڕێنمونی بکات بۆ ڕێگای ڕاست بڵێ خوا ڕێنمونی دەکات بۆ ڕێگای ڕاست جا ئایا کەسێک کە ڕێنمونی دەکات بۆ ڕاستە ڕێ شایان ترە بەوەی پەیڕەوی بکرێت وشوێنی بکەویت یان کەسێک ڕێبازی ڕاست شارەزا نابێ مەگەر شارەزا بکرێت ئەوە چیتانە چۆن ئەو بڕیارە دەدەن؟');
INSERT INTO "bamoki" VALUES(10,36,'10:36','وە زۆربەیان شوێن ھیچ ناکەون جگە لەگومان بەڕاستی گومان لە ئاست ھەقدا ھیچ سوودێک (بەخاوەنەکەی) ناگەیەنێت بەڕاستی خوا زانایە بەوەی کە دەیکەن');
INSERT INTO "bamoki" VALUES(10,37,'10:37','ڕەوا نیە و ناگونجێت ئەم قورئانە ھەڵبەسترا بێ (لەلایەن کەسێکەوە) جگە لەخوا بەڵکو بەڕاست دانەری ئەو (کتێبانەی) پێش خۆیەتی (پێش دەستکاری کردنیان) وە ڕوون کەرەوەی کتێبە ئاسمانیەکانە (ئەم قورئانە) ھیچ گومانی تێدا نیە لەلایەن پەروەردگاری جیھانیانەوە ھاتووە');
INSERT INTO "bamoki" VALUES(10,38,'10:38','یان دەڵێن (موحەممەد صلى الله عليه وسلم) خۆی ئەم قورئانەی ھەڵبەستووە بەدەم خواوە بەوانە بڵێ سورەتێک بھێنن وەک ئەو و ھەر کەسێک کەدەتوانن بانگی بکەن بێجگە لەخوا ئەگەر ئێوە ڕاست دەکەن');
INSERT INTO "bamoki" VALUES(10,39,'10:39','(ئەوە واز لێ بھێنە) بەڵکو ئەوانە بڕوایان بەقورئان نەھێناو بە درۆیان دانا کە ھیچ زانیاریان دەربارەی نەبوو کە ھێشتا سەرئەنجامی (سزاو تۆڵەی) قورئانیان بۆ نەھاتووە بە ھەمان شێوە (پەیامی پێغەمبەرانیان) بەدرۆ زانی ئەوانەی لەپێش ئەمانەوە بوون ئەمجا سەرنج بدە کە چۆن بوو دواڕۆژ و ئاکامی ستەمکاران');
INSERT INTO "bamoki" VALUES(10,40,'10:40','لەوانیشدا کەسی وا ھەیە کەبڕوا بەقورئان دەھێنێت کەسی واشیان ھەیە کەبڕوای پێ ناھێنێت وە پەروەردگارت لەھەموو کەس زاناترە بە خراپەکاران');
INSERT INTO "bamoki" VALUES(10,41,'10:41','ئەگەر بڕوایان پێ نەھێنای ئەوە پێیان بڵێ (پاداشتی) کردەوەی خۆم بۆ خۆم و (پاداشتی) کردەوەی خۆتان بۆ خۆتان ئێوە بەرین (بەرپرسیارنین) لەوەی من دەیکەم منیش بەریم (بەرپرسیارنیم) لەوەی ئێوە دەیکەن');
INSERT INTO "bamoki" VALUES(10,42,'10:42','(کافران) کەسی وایان ھەیە گوێت لێ دەگرن (کەقورئان دەخوێنیت) جا ئایا تۆ گوفتارت بەکەڕان دەژنەوێنی؟ ئەگەر چی ئەو کەڕانە ھیچیش تێنەگەن');
INSERT INTO "bamoki" VALUES(10,43,'10:43','وە ھی وایان ھەیە دەڕوانێت بۆت دەی ئایا تۆ ڕێنمونی ئەو کوێرانە دەکەیت (مەبەست پێی بێ باوەڕانە کە کوێرن لە ئاست بینینی ھەق وڕاستیدا) ئەگەر چی ئەو کوێرانە نابیناش بن؟');
INSERT INTO "bamoki" VALUES(10,44,'10:44','بەڕاستی خوا ھیچ ستەم لەخەڵکی ناکات بەڵکو خەڵکی خۆیان ستەم لەخۆیان دەکەن');
INSERT INTO "bamoki" VALUES(10,45,'10:45','وە (بیریان بخەنەوە) ئەو ڕۆژەی کە (خوا) ھەموویان کۆ دەکاتەوە دەڵێی لە دونیادا نەماونەتەوە ساتێکی کەم نەبێت لە ڕۆژێک خۆیان بەیەکتر دەناسێنن بەڕاستی زەرەرمەند بوون ئەوانەی کە بڕوایان نەھێنا بە (زیندووبونەوە) و دیداری خوا وە ئەوانە ڕێنمون وەرگرنەبوون');
INSERT INTO "bamoki" VALUES(10,46,'10:46','بەڕاستی ئەگەر نیشانت بدەین ھەندێک لەوەی بەڵێنمان داوە پێیان (زاڵبوونت بەسەریاندا) یان تۆ بمرێنین (پێش بەدی ھاتنی) ئەوە گەڕانەوەیان ھەر بۆلای ئێمەیە پاشان خوا شایەتە لەسەر ئەو کردەوانەی کە دەیکەن (لەپاش تۆ)');
INSERT INTO "bamoki" VALUES(10,47,'10:47','وە بۆ ھەموو گەلێک پێغەمبەرێک ھەبووە جا کاتێک پێغەمبەرەکەیان (لەقیامەتدا) ھات بۆلایان بڕیار دەدرێت لەنێوانیاندا بەدادگەریی و ڕاستی وە ئەوان ھیچ ستەمیان لێ ناکرێت');
INSERT INTO "bamoki" VALUES(10,48,'10:48','دەڵێن کەی دەبێت ئەو بەڵێن و ھەڕەشەیە ئەگەر ئێوە ڕاست دەکەن');
INSERT INTO "bamoki" VALUES(10,49,'10:49','(ئەی موحەممەدصلى الله عليه وسلم) بڵێ بەدەستم نیە بۆ خودی خۆم ھیچ زیان و سوودێک مەگەر ئەوەی خوا بیەوێت بۆ ھەموو گەل و نەتەوەیەک کاتێکی دیاری کراو ھەیە ئەگەر کاتەکەیان ھات ئەوا نەساتێک دوا دەکەون نەپێشیش دەکەون');
INSERT INTO "bamoki" VALUES(10,50,'10:50','تۆ بڵێ (بەوانە) پێم بڵێن ئەگەر سزای خواتان بۆھات لەشەودا یان لەڕۆژدا ئایا تاوانباران بۆچی (سزایەک) پەلە دەکەن');
INSERT INTO "bamoki" VALUES(10,51,'10:51','ئایا پاش ئەوەی ڕوویدا و بەدی ھات ئێوە بڕوای پێ دەھێنن؟!! ئا ئێستا (بڕوای پێ دەھێنن)؟ خۆ بەڕاستی ئێوە پەلەی ھاتنی (ئەو سزا) یەتان دەکرد');
INSERT INTO "bamoki" VALUES(10,52,'10:52','ئەمجا پێـیان دەووترێت بەوانەی کە ستەمیان کردوە بچەژن سزای ھەمیشەیی ئایا ئێوە تۆڵەتان لێ دەسەندرێت مەگەر بەھۆی ئەو (کارانەوە) نەبێت کەخۆتان دەتان کرد');
INSERT INTO "bamoki" VALUES(10,53,'10:53','(ئەی موحەممەد صلى الله عليه وسلم) لێت دەپرسن ئایا ئەو سزایە ڕاستە (بەدی دێت)؟ پێیان بڵێ بەڵێ سوێند بە پەروەردگارم بێگومان ئەوە ڕاستە وە ئێوە ناتوانن خۆتانی لێ قوتار بکەن');
INSERT INTO "bamoki" VALUES(10,54,'10:54','ئەگەر ھی ھەرکەسێک بێت کە ستەمی کردووە (لەدونیادا) یان بێ باوەڕ بوو بێت ھەرچی لە زەویدایە لەبریتی خۆی دەیدات (تەنھا بۆ ئەوەی ڕزگاری ببێت) وە (بێ باوەڕان) پەشیمانی خۆیان دەشارنەوە (لەو ڕۆژەدا) کاتێک کە ئەو سزایە دەبینن جا بڕیار دەدرێت لە نێوانیاندا بەدادوەری و ئەوانە ھیچ ستەمیان لێ ناکرێت');
INSERT INTO "bamoki" VALUES(10,55,'10:55','ئاگاداربن بەڕاستی موڵکی خوایە ئەوەی لە ئاسمانەکان و زەویدایە ئاگاداربن بەڕاستی بەڵێنی (ھەڕەشەی) خوا ڕاستە بەڵام زۆربەیان نازانن');
INSERT INTO "bamoki" VALUES(10,56,'10:56','ئەو (خوایە) دەژێنێت ودەمرێنێت و ھەر بۆلای ئەو دەگەڕێنرێنەوە (لەڕۆژی دواییدا)');
INSERT INTO "bamoki" VALUES(10,57,'10:57','ئەی خەڵکینە بێگومان بۆتان ھاتووە ئامۆژگاری (قورئان) لەلایەن پەروەردگارتانەوە, وە شیفا و چارەیە بۆ ئەوەی لە ناو دڵ و دەرونتاندایە وە ڕێنمونی و میھرەبانیە بۆ بڕواداران');
INSERT INTO "bamoki" VALUES(10,58,'10:58','(ئەی موحەممەد صلى الله عليه وسلم) بڵێ ھەر بەھۆی چاکە و میھرەبانی خواوەیە جا کەواتە بابەوە دڵخۆش بن ئەوە چاکترە (بۆیان) لەوەی کۆی دەکەنەوە (لەشتی ئەم دونیایە)');
INSERT INTO "bamoki" VALUES(10,59,'10:59','بڵێ (بەو بێ بڕوایانە) پێم بڵێن ئەوەی خوا ناردوویەتیە خوارەوە بۆتان لە ڕزق و ڕۆزی ھەندێکیتان کردووە بە حەرام و (ھەندێکی) بە حەڵاڵ بڵێ ئایا خوا مۆڵەتی (ئەوەی) پێ داوون (لەخۆتانەوە شتانێک حەڵاڵ و شتانێکیش حەرام بکەن) یان درۆ دەکەن بەناوی خواوە');
INSERT INTO "bamoki" VALUES(10,60,'10:60','وە گومانی ئەوانە چیە کە درۆ ھەڵئەبەستن بەناوی خواوە لەڕۆژی دواییدا بەڕاستی خوا خاوەنی چاکەو فەزڵ و بەھرەیە بەسەر خەڵکی یەوە بەڵام زۆربەی خەڵکی سوپاسی (ئەو چاکەو بەھرانە) ناکەن');
INSERT INTO "bamoki" VALUES(10,61,'10:61','وە (ئەی موحەممەد صلى الله عليه وسلم) تۆ لە ھەر مەبەست و کارێکدا بیت وە ھەرچی لە قورئان بخوێنی و (ئێوە) ھەر کردەوەیەک بکەن بێگومان ئێمە بەسەرتانەوە شایەتین و ئاگادارین کاتێک دەڕۆنە ناویەوە و پێوەی خەریکن وە وون نابێت لەپەروەردگارت بەئەندازەی گەردیلەیەک نە لەزەوی و نە لەئاسماندا وە نەبچوکتر لەو گەردیلەیە و نە گەورەتر لەو (گەردیلەیە) مەگەر ئەوەتا لە ناو پەڕاوێکی ئاشکرادا (کەمەبەست پێی لوح المحفوظ) ـە');
INSERT INTO "bamoki" VALUES(10,62,'10:62','ئاگادار بن بێگومان دۆستانی خوا ترسیان لـەسەر نیە خەفەتیش ناخۆن');
INSERT INTO "bamoki" VALUES(10,63,'10:63','ئەوانەی باوەڕیان ھێناوە, وە ھەمیشە خۆیان دەپارێزن و لەخوا دەترسن');
INSERT INTO "bamoki" VALUES(10,64,'10:64','ھەر بۆ ئەوانە مزگێنی وموژدە لەژیانی دونیادا و لە دواڕۆژیشدا گۆڕان نیە بۆ ووتە (و بەڵێنە) کانی خوا ھەر ئەوەیە سەرکەوتنی زۆر گەورە');
INSERT INTO "bamoki" VALUES(10,65,'10:65','باخەمبارت نەکات ووتەی ئەوانە چونکە بەڕاستی ھێز و دەسەڵات ھەمووی بۆ خوایە خوایش بیسەر و زانایە');
INSERT INTO "bamoki" VALUES(10,66,'10:66','ئاگادار بن بەڕاستی ھەر بۆ خوایە ھەر چی لە ئاسمانەکان ھەرچیش لە زەویدایە پەیڕەویان نەکردووە ئەوانەی بێجگە لە خوا دەپەرستن پەرستراوانێک (ھاوبەشانێ بۆ خوا) ئەوانە تەنھا شوێن گومان کەوتوون وە ئەوانە تەنھا درۆدەکەن (بەگومان و گۆترە شت دەڵێن)');
INSERT INTO "bamoki" VALUES(10,67,'10:67','ئەو خوایە زاتێکە شەوی بۆ داناون تا تێیدا بحەسێنەوە ڕۆژیشی داناوە بە ڕووناککەرەوە (کەمایەی ژیان وگوزەرانتان بێت) بەڕاستی لەوەدا (کە باس کرا) چەند بەڵگە و نیشانە ھەیە بۆ کەسانێک کە دەبیستن و ژیرن');
INSERT INTO "bamoki" VALUES(10,68,'10:68','ئەوانە دەڵێن خوا منداڵی بۆخۆی ھەڵبژاردوە!! پاکی و بێگەردی بۆ خوا ئەو بێ نیازە تەنھا ئەو خاوەنیانە ھەرچی وا لە ئاسمانەکان و ھەرچی وا لە زەویدا ھیچ بەڵگەیەکتان لا نیە بەمەی کە دەیڵێن. ئایا شتێک دەڵێن بە دەم خواوە کە نایزانن و بێ ئاگان لێی');
INSERT INTO "bamoki" VALUES(10,69,'10:69','(ئەی موحەممەد صلى الله عليه وسلم) بڵێ بەڕاستی ئەوانەی کە درۆ ھەڵ دەبەستن بە ناوی خواوە (ھەرگیز) ڕزگارنابن');
INSERT INTO "bamoki" VALUES(10,70,'10:70','(چونکە) بەھرە و ڕابواردنێکی کەم لە دونیادا (بەسەر دەبەن) پاشان بۆ لای ئێمەیە گەڕانەوەیان ئەمجا پێیان دەچێژین سزای زۆر توند بەھۆی ئەوەی کە بڕوایان نەھێنا (تامردن)');
INSERT INTO "bamoki" VALUES(10,71,'10:71','(ئەی موحەممەد صلى الله عليه وسلم) دەنگوباس و ھەواڵی نوح (علیە السلام) یان بۆ بخوێنەرەوە کاتێک بەگەلەکەی ووت ئەی گەلەکەم ئەگەر بەلاتانەوە قورس و گرانە مانەوەم لەناوتاندا و ئامۆژگاری کردنم (بۆتان) بەئایەتەکانی خوا ئەوامن پشتم بەخوا بەستووه ئێوەش کارو بڕیاری خۆتان کۆکەنەوە لەگەڵ ھاوبەشەکانتاندا ئەمجا با کار و بڕیارەکانتان شاراوە نەبێت بە لاتانەوە (بەڵکو ئاشکرابێت) پاشان پەلامارم بدەن و ماوەم مەدەن');
INSERT INTO "bamoki" VALUES(10,72,'10:72','جا ئەگەر سەرپێچیتان کردم (پشتم تێ دەکەن) ئەوە من داوام نەکردووە لێتان ھیچ پاداشتێک پاداشتی من ھەر لەسەر خوایە وە فەرمانم پێدراوە کە لە فەرمانبەردارانی خودا و موسوڵمانان بم');
INSERT INTO "bamoki" VALUES(10,73,'10:73','ئەمجا بەدرۆیان زانی ئەوسا ئێمەش ئەو (نوح) و ئەوانەش کەلەگەڵی بوون ڕزگارمان کردن لەکەشتیەکەدا وە کردمانن بەجێنشینانی (ئەوان لەزەویدا) ئەوانەشمان ژێر ئاو خست کەبڕوایان بەئایەتەکانی ئێمە نەھێنا ئەمجا سەرنج بدە و بڕوانە بزانە چۆن بوو دواڕۆژ و ئاکامی ترسێنراوان');
INSERT INTO "bamoki" VALUES(10,74,'10:74','ئەمجا لەدوای نوح ناردمان پێغەمبەرانێک بۆ ناو گەلەکانیان ئەوانیش ھاتن بۆ لایان بەبەڵگە ڕوونەکانەوە کەچی ئەوان بڕوایان نەھێنا بەوەی کەلە ڕابردوودا بڕوایان پێنەھێنا بوو بەو جۆرە مۆر دەنێین بەسەر دڵی دەست درێژ کاراندا (تاتێبگەن)');
INSERT INTO "bamoki" VALUES(10,75,'10:75','پاشان ناردمان لەپاش ئەو نێرراوانە موسا و ھارون بۆلای فیرعەون و ە بۆ لای دەستە و تاقمەکەی بە بەڵگە و موعجیزەکانمانەوە بەڵام ئەوان خۆیان بە گەورە زانی و ئەوان ھۆزێکی تاوان باربوون');
INSERT INTO "bamoki" VALUES(10,76,'10:76','جا کاتێک بۆیان ھات ھەق و ڕاستی لەلایەن ئێمەوە ووتیان بێگومان ئەمە جادوویەکی ڕوون و دیارە!');
INSERT INTO "bamoki" VALUES(10,77,'10:77','موسا ووتی (پێیان) ئایا ئێوە بەم ھەق و ڕاستیە دەڵێن (جادوە) کاتێک بۆتان ھاتووە ئایا ئەوە جادوە کە جادوگەران ڕزگاریان نابێت');
INSERT INTO "bamoki" VALUES(10,78,'10:78','ووتیان ئایا ھاتووی بۆلامان بۆ ئەوەی لاماندەیت لەوەی باوو باپیرانمان لەسەری بوون تا بۆ ھەر دووکتان (موسا و ھارون) بێت گەورەیی (وسەرۆکایەتی بەنو ئیسرائیل) لەزەویدا وە ئێمە ھەرگیز باوەڕتان پێ ناکەین');
INSERT INTO "bamoki" VALUES(10,79,'10:79','فیرعەون ووتی (بەکۆمەڵەکەی) بۆم بھێنن و (کۆی بکەنەوە) ھەموو جادوگەرێکی زۆر زانا و لێزان');
INSERT INTO "bamoki" VALUES(10,80,'10:80','جا کاتێک جادوگەرەکان ھاتن موسا پێی ووتن: ئەوەی دەتانەوێ بیھاوێژن (فڕێی دەن)');
INSERT INTO "bamoki" VALUES(10,81,'10:81','ئەمجا کاتێک (جا دوەکانیان) فڕێدا مووسا ووتی ھەرچی ئێوە ھێناوتانە جادوە بێگومان خوا بەزوویی پوچەڵی دەکاتەوە چونکە بەڕاستی خوا چاکی ناکات کردەوەی خراپەکاران');
INSERT INTO "bamoki" VALUES(10,82,'10:82','خوا حەق و ڕاستی سەردەخات (دەیچەسپێنێت) بەفەرمان و بەڵێنەکانی با تاوانبارانیش پێیان ناخۆش بێت');
INSERT INTO "bamoki" VALUES(10,83,'10:83','جا (لەسەرەتاوە) بڕوای نەھێنا بەموسا چەند کەسێک نەبێت لە گەلەکەی بەھۆی ترس و بیمیان لە فیرعەون و کاربەدەست وگەورەکانیان نەوەک بیانگێڕێتەوە بۆ بێ دینی بەڕاستی فیرعەون خۆبە زلزان و زاڵ بوو لە زەوی (میسر) دا وە بەڕاستی لە سنوور بەزێنەران بوو');
INSERT INTO "bamoki" VALUES(10,84,'10:84','وە موسا ووتی ئەی گەلەکەم گەر ئێوە بڕواتان بەخوا ھێناوە ئەوە تەنھا پشت بەخوا ببەستن ئەگەر ئێوە فەرمانبەر و مل کەچن');
INSERT INTO "bamoki" VALUES(10,85,'10:85','ئەوانیش (باوەڕدارەکان) ووتیان تەنھا پشتمان بەخوا بەستووە ئەی پەروەردگارمان مەمانگێڕە بە جێگەی ئەشکەنجە وئازار بۆ گەلی ستەمکاران');
INSERT INTO "bamoki" VALUES(10,86,'10:86','وە ڕزگارمان بکە بەبەزەیی خۆت لەدەست کۆمەڵی بێ بڕوایان');
INSERT INTO "bamoki" VALUES(10,87,'10:87','وە نیگامان کرد بۆ موسا و براکەی (ھارون) کە پێکی بھێنن و ئامادەی بکەن بۆ گەلەکەتان چەند خانوویەک لەمیسردا (کەتێیدا بحەوێنەوە) وە خانووەکانتان بکەن بە قیبلە (واتە بیکەن بەجێگای پەرستنی خودا) و بەڕێکو پێکی نوێژ بەجێ بھێنن مژدەش بدە بەبڕواداران (بەبەھەشت و سەرکەوتن)');
INSERT INTO "bamoki" VALUES(10,88,'10:88','و موسا ووتی ئەی پەروەردگارمان بەڕاستی تۆ بەخشیووتە بەفیرعەون و کۆمەڵەکەی ھۆکاری ڕازانەوە و جوانی و ماڵ و سامانی زۆر لەژیانی دونیادا ئەی پەروەردگارمان بۆ ئەوەی خەڵک گومڕا بکەن لەڕێگای تۆ ئەی لە ناوی بەرە و مەیھێڵە ماڵ و سامانیان دڵەکانیان ڕەق وڕەش بکە چونکە بڕوا ناھێنن ھەتا نەبینن (بەچاوی خۆیان) سزای سەختی ئازاردەر');
INSERT INTO "bamoki" VALUES(10,89,'10:89','(خوای گەورە) فەرمووی بیگومان داوای ھەردووکـتان وەرگیرا جا خۆ ڕاگر و بەردەوام بن (لەسەر ئاینی خوا) وە مەکەونە شوێن ڕێگای ئەوانەی کەنازانن');
INSERT INTO "bamoki" VALUES(10,90,'10:90','ئەمجا نەوەی ئیسرائیلمان لە دەریاکە (دەریای سوور) پەڕاندەوە ئەمجا فیرعەون و لەشکرەکەی شوێنیان کەوتن بە ستەم و دەست درێژی کردن ھەتا ئەو کاتەی خەریک بوو (فیرعەون) بخنکێت ئەوسا ووتی بڕوام ھێنا کە ھیچ پەرستراوێکی ڕاست نیە جگە لەئەو (خوایە) نەبێت کە بڕوایان پێ ھێناوە نەوەی ئیسرائیل وە منیش لە موسڵمانانم');
INSERT INTO "bamoki" VALUES(10,91,'10:91','(ئەی فیرغەون) ئێستا (باوەڕ دەھێنێت؟) کە بێگومان لەوەو پێش سەرپێچیت کرد وەتۆ لەخراپەکاران و تێکدەران بوویت');
INSERT INTO "bamoki" VALUES(10,92,'10:92','بەڵام ئەمڕۆ لاشەکەت (لە دەریا) ڕزگار دەکەین بۆ ئەوەی ببیت بەپەند بۆ ئەوەی لەدوای تـۆ دێت وە بـەڕاستی زۆرێک لـەخەڵکی بێ ئاگان لە بەڵگە و نیشانەکانی ئێمە');
INSERT INTO "bamoki" VALUES(10,93,'10:93','سوێند بەخوا بێگومان نیشتە جێمان کردن نەوەی ئیسرائیل لەشوێنێکی چاک و شایستەدا و ڕۆزی چاک و پاکمان پێدان جا ڕاجیاو دووبەرەک نەبوون ھەتا زانین و شارەزایی (تەورات) یان بۆ ھات بەڕاستی پەروەردگارت بڕیار دەدات لەنێوانیاندا لەڕۆژی دوایدا لەوەی ئەوان تێیدا ڕاجیا بوون');
INSERT INTO "bamoki" VALUES(10,94,'10:94','جائەگەر تۆ لەگوماندایت لەوەی کە ناردومانەتە خوارەوە بۆت ئەوە پرسیار لەوانە بکە کە کتێبی ئاسمانی شارەزان و دەیخوێننەوە لەپێش تۆ سوێند بەخوا بەڕاستی بۆت ھاتووە ھەق و ڕاستی لەپەروەردگارتەوە کەواتە لەدەستەی گومانداران مەبە');
INSERT INTO "bamoki" VALUES(10,95,'10:95','و لەوانەش مەبە کە نیشانەکانی خوایان بە درۆزانی (و باوەڕیان پێ نەھێنان) (ئەگەر وابکەیت) ئەوە دەچیتە ڕیزی زەرەرمەندانەوە');
INSERT INTO "bamoki" VALUES(10,96,'10:96','بەڕاستی ئەوانەی دەرچووە و چەسپاوە بۆیان بڕیاری پەروەردگارت بڕوا ناھێنن');
INSERT INTO "bamoki" VALUES(10,97,'10:97','(بڕوا ناھێنن) ھەرچەند بۆیان بێت ھەموو بەڵگەو نیشانەیەک ھەتا نەبینن (بە چاوی خۆیان) سزای سەختی ئازاردەر');
INSERT INTO "bamoki" VALUES(10,98,'10:98','(خەڵکی) ھیچ شار وگوندێک (لەوانەی کە ھەڕەشەیان لێکرا) وباوەڕیان ھێنا (کاتی بینین و دابەزینی سزا ی پەروەردگار) باوەڕەکەیان ھیچ سوودی پێ نەگەیاندن جگە لە گەلی یونس (علیە السلام) نەبێت کاتێک ھەموو بڕوایان ھێنا وتەوبەیان کرد (کە دڵنیا بوون سزای پەروەردگار دابەزیوە بۆ سەریان) لامان برد لەسەریان سزای سەرشۆڕی و ڕیسوایی لە ژیانی دونیادا و بەھرە و خۆشیمان پێدان تا ماوەیەک (تاکاتی مردنیان)');
INSERT INTO "bamoki" VALUES(10,99,'10:99','وە ئەگەر پەروەردگارت بیویستایە بڕوای دەھێنا ئەو (خەڵکەی) وا لەزەویدان ھەموو بە تێکڕا دەی ئایا تۆ (ئەی موحەممەد صلى الله عليه وسلم) زۆر لەخەڵکی دەکەیت ھەتا ببنە بڕوادار');
INSERT INTO "bamoki" VALUES(10,100,'10:100','وە بۆ ھیچ کەسێک نیە کەبڕوا بھێنێت مەگەر بە ویستی خوا نەبێت وە پیسی (وبێ باوەڕی) دەخاتە سەر ئەوانەی کە تێ ناگەن و بیرناکەنەوە');
INSERT INTO "bamoki" VALUES(10,101,'10:101','(ئەی موحەممەد صلى الله عليه وسلم) بڵێ: سەرنج بدەن چی لەئاسمانەکان و زەویدایە کەچی نیشانە و ترسێنەرەکان ھیچ سوود ناگەیەنن بەکەسانێک بڕوا نەھێنن');
INSERT INTO "bamoki" VALUES(10,102,'10:102','دەی چاوەڕوانی چی دەکەن بێجگە لە ڕووداوێک وەک ڕووداوەکانی ئەوانەی کە لە پێشیانەوە ڕابوردن بڵێ دەی چاوەڕوان بکەن بەڕاستی منیش لەگەڵ ئێوەدا لە چاوەڕوانکارانم');
INSERT INTO "bamoki" VALUES(10,103,'10:103','پاشان ئێمە ڕزگاری دەکەین (لە کاتی ناڕەحەتیدا) پێغەمبەرانمان و ئەوانەی بڕوایان ھێناوە ئابەو شێوە پێویستە لەسەرمان ئیمان داران ڕزگار بکەین');
INSERT INTO "bamoki" VALUES(10,104,'10:104','بڵێ ئەی خەڵکینە ئەگەر ئێوە لەگوماندان لە (ڕاستی) ئاینەکەم ئەوە من ناپەرستم ئەوانەی کە ئێوە دەی پەرستن بێجگە لەخوا بەڵکو ئەو خوایە دەپەرستم کەئێوە دەمرێنێ وە من فەرمانم پێکراوە کە لەبڕواداران بم');
INSERT INTO "bamoki" VALUES(10,105,'10:105','وە ڕووت ڕێک و ڕاست بکە بۆ ئایینێک کەڕاستە وە بەھیچ جۆرێک لە ھاوبەشدانەران مەبە');
INSERT INTO "bamoki" VALUES(10,106,'10:106','ھاوارمەکە (مەپەرستە) بێجگە لەخوا شتێ نەسوودت پێ دەگەیەنێت و نەزیانت لێ دەدات جا ئەگەر وات کرد ئەوە بێگومان ئەوکاتە تۆ لەستەمکارانی');
INSERT INTO "bamoki" VALUES(10,107,'10:107','وە ئەگەر خوا بە تۆ بگەیەنێت زیانێک ھیچ لادەرێک نیە بۆ ئەو (زیانە) بێجگە لە خوا وە ئەگەر خێر و خۆشیەکی بوێت بۆت ئەوە ھیچ ڕێگریکەرێک نیە بۆ چاکەی خوا بەھرەمەندی دەکات ئەو کەسەی خۆی بیەوێت لە بەندەکانی وە ھەر ئەو لێبوردە و بەبەزەییە');
INSERT INTO "bamoki" VALUES(10,108,'10:108','(ئەی موحەممەد صلى الله عليه وسلم) بڵێ ئەی خەڵکینە بەڕاستی (قورئانی) حەقتان بۆ ھاتووە لەلایەن پەروەردگارتانەوە جا ھەر کەسێک ڕێنمونی وەرگرتبێت ئەوە بێگومان (قازانجی) ڕێنمونیەکەی بۆ خۆیەتی ھەر کەسیش گومڕا بووبێت ئەوە بێگومان (زیانی) گومڕابوونەکەی بۆ خۆیەتی وە من لە ئێوە بەر پرسیارنیم');
INSERT INTO "bamoki" VALUES(10,109,'10:109','شوێن ئەوە بکەوە کە سرووش وەحی کراوە بۆت وە بە ئارام و خۆگر بە ھەتا خوا بڕیارێک دەدات (لە نێوانی تۆ و ئەواندا) وە ئەو (خوایە) چاکترینی ھەموو دادوەر و بڕیاردەرانە');
INSERT INTO "bamoki" VALUES(11,1,'11:1','بە (ئەلیف، لام، ڕا) دەخوێنرێتەوە خوا زاناترە بەماناکەی، بۆ زانیاری زیاتر سەیری سەرەتای سورەتی (البقرە) بکە ئەمە کتێبێکە بتەو داڕێژراوە ئایەتەکانی پاشان ڕوون کراونەتەوە لەلایەن (خوای) کاردروست و زاناوە');
INSERT INTO "bamoki" VALUES(11,2,'11:2','بۆ ئەوەی ھیچ شتێک نەپەرستن بێجگە لەخوا بەڕاستی من لەلایەن ئەو (خوا) وە بۆ ئێوە ترسێنەر و موژدەدەرم');
INSERT INTO "bamoki" VALUES(11,3,'11:3','ھەروەھا بۆ ئەوەی داوای لێخۆشبوون بکەن لە پەروەردگارتان پاشان تۆبە بکەن و بگەڕێنەوە بۆلای دەتانژێنێت بەژیانێکی چاک و خۆش تاماوەی دیاریکراو (بۆتان) وە دەبەخشێت بەھەموو خاوەن چاکە و بەھرەیەک (پاداشتی) چاکە و بەھرەی خۆی وە ئەگەر ڕوو وەرگێڕن و سەرپێچی بکەن ئەوە بەڕاستی من مەترسی دەکەم یەخەتان بگرێت سزای ڕۆژێکی گەورە (کە ڕۆژی دواییە)');
INSERT INTO "bamoki" VALUES(11,4,'11:4','ھەر بۆلای خوایە گەڕانەوەتان وە ئەو (خوایە) دەسەڵاتدارە بەسەر ھەموو شتێکدا');
INSERT INTO "bamoki" VALUES(11,5,'11:5','ئاگادار بن بێگومان ئەوان سنگیان دەپێچنەوە (لەسەر کینە و دژایەتی) بۆ ئەوەی خۆیان بشارنەوە لە خوا بێداربن کاتێک ئەوانە خۆیان دادەپۆشن بە پۆشاکەکانیان (بۆ ئەوەی پیلانەکانیان دەرنەکەون) (خوا) دەزانێت بەوەی دەیشارنەوەو ئەوەی ئاشکرای دەکەن بێگومان ئەو (خوایە) بە ئاگا و زانایە بەو (نھێنیانەی) لە سینە و دڵەکاندایە');
INSERT INTO "bamoki" VALUES(11,6,'11:6','ھیچ گیان لەبەرێک نیە لەزەویدا ئیلا لەسەر خوایە ڕزق و ڕۆزیەکەی وە (خوا) دەزانێت شوێنی مانەوەی وە شوێنی لێ دەرچوونی ھەمووی نوسراوە لەکتێبی ڕوون کەرەوە (لوح المحفوظ)دا');
INSERT INTO "bamoki" VALUES(11,7,'11:7','ئەو (خوایە) زاتێکە کە ئاسمانەکان وزەوی دروستکردوە لە شەش ڕۆژدا و عەرشەکەی لەسەر ئاو بوو بۆ ئەوەی تاقیتان بکاتەوە کامتان کردەوەی چاکتر (دەکەن) سوێندبێ ئەگەر بڵێیت (بەوانە) بەڕاستی ئێوە زیندوو دەکرێنەوە لەپاش مردن بێگومان ئەوانەی کە بێ بڕوان دەڵێن ئەمە ھیچ نیە بێجگە لە جادویەکی ڕوون و دیارنەبێت');
INSERT INTO "bamoki" VALUES(11,8,'11:8','وە سوێند بەخوا ئەگەر ئەو سزایان دوا بخەین تاماوەیەکی کەمی دیاریکراو ئەوە بەڕاستی دەڵێن چی ئەو سزای ڕاگرتووە (بۆچی بۆمان نایەت) بێدار بن ڕۆژێ ئەو سزایەیان بۆ دێت لێیان لا نادرێت وە دەوریان دەدات ئەوەی کە ئەوانە گاڵتە و لاقرتیان پێدەکرد');
INSERT INTO "bamoki" VALUES(11,9,'11:9','سوێند بەخوا ئەگەر ئێمە بچێژین بە ئادەمی چاکە و فەزڵ و بەھرەیەک لای خۆمانەوە پاشان ئەو (چاکەو بەھرەیەی) لێ بگرینەوە بێگومان ئەو زۆر نائومێد و سپڵەیە (لەو کاتەدا کوفری نیعمتەکانی خوا دەکات)');
INSERT INTO "bamoki" VALUES(11,10,'11:10','سوێند بەخوا ئەگەر پێی بچێژین وبیدەینێ بەھرە و چاکە دوای ئەو سەختی و ناڕەحتیانەی تووشی بووە بێگومان دەڵێت گشت سەختی و زیانەکانم لە مل بوویەوە (بەرۆکی بەردام) بێـگومان (لەوکاتەدا) زۆر دڵخۆش و لوت بەرزو خۆ بەزلزانە');
INSERT INTO "bamoki" VALUES(11,11,'11:11','جگە لەوانەی کە بائارام و خۆگر بوون و کردەوە چاکەکانیان بەجێھێنا ئەوانە بۆیان ھەیە لێبوردن و پاداشتی گەورە');
INSERT INTO "bamoki" VALUES(11,12,'11:12','ئایا تۆ وازھێنەری لە (ڕاگەیاندنی) ھەندێک لەوەی نیگات بۆ کراوە و دڵ تەنگ دەبیت پێی لەبەر ئەوەی کە دەڵێن بۆچی گەنج و خەزێنەی بۆ نانێردرێتە خوارەوە یان بۆچی فریشتەیەکی لەگەڵدا نەھاتووە بێگومان تۆ تەنھا ترسێنەری و خـوا بۆ ھەموو شتێک سەرپەرشتیکارو چاودێرە');
INSERT INTO "bamoki" VALUES(11,13,'11:13','یان ئەوانە دەڵێن پێغەمبەر صلى الله عليه وسلم ئەو (قورئانە) ی ھەڵبەستووە بڵێ دەی ئێوەش دە سورەتی وەکو ئەو (قورئانە) بھێنن ھەڵبەستراوبن وەبانگ بکەن (بۆ پشتیوانیتان) ھەرکەس دەتوانن بێجگە لە خوائەگەر ئێوە ڕاست دەکەن');
INSERT INTO "bamoki" VALUES(11,14,'11:14','جا ئەگەر وەڵامیان نەدانەوە ئەوە بزانن کەبێگومان ئەم (قورئانە) نێردراوەتە خوارەوە بەئاگاداری وزانینی خوا ھەروەھا بزانن ھیچ پەرستراوێکی ڕاست نیە بێجگە لەئەو دەی ئایا ئێوە ملکەچ و موسڵمانن؟');
INSERT INTO "bamoki" VALUES(11,15,'11:15','ھەرکەس دەیەوێت ژیانی دونیا و ڕازاوە و جوانی ئەو ئێمە (بەرھەمی) کردەوەکانیان بەتەواوی پێ دەدەین تیایدا وە ئەوان لەم ژیانەدا کەموکوڕیان لەگەڵ ناکرێت');
INSERT INTO "bamoki" VALUES(11,16,'11:16','ئەوانە کەسانێکن بۆیان نیە لەڕۆژی دواییدا جگە لەئاگر وە ئەوەی کردیان تێیدا پوچ و بێ بەرھەم بووە وە ھەرچیان کرد بەردەوام بوو بەھیچ');
INSERT INTO "bamoki" VALUES(11,17,'11:17','جا ئایا کەسێک بەڵگەیەکی ئاشکرای ببێ لەلایەن پەروەردگاریەوە و شایەتێک لای ئەوەوە بۆی بخوێنێتەوە, وە لەپێش ئەم (شایەتیەی خۆیەوە) پەڕاوی موساش (شایەتە بۆی) کەپەیڕەو پێشەواو میھرەبانیە ئەوانە بڕوایان ھەیە بەو (شایەتە کەقورئانە) وە ھەرکەس باوەڕی پێی نەبێت لەو کۆمەڵ و تاقمانە (ئەوانەی کۆبونەوە لەسەر دژایەتی ئیسلام) ئەوە ئاگر جێگایەتی کەواتە ھیچ جۆرە گومان و دوو دڵیەکت نەبێت لەو (قورئانە) بەڕاستی ئەو قورئانە ڕاستە لەلایەن پەروەردگارتەوە ھاتووە بەڵام زۆرێک لەخەڵکی بڕواناھێنن');
INSERT INTO "bamoki" VALUES(11,18,'11:18','وەئایا کێ ستەمکارترە لەو کەسەی کەدرۆ ھەڵبەستێ بەدەم خواوە؟ ئەوانە دەبرێنە حوزوری پەروەردگاریان (ووتەکانیان دەخرێتە ڕوو) وە شایەتەکان (لەپێغەمبەران وفریشتەکان) دەڵێن ئا ئەوانە ئەو کەسانەن (لەدونیادا) درۆیان ھەڵدەبەست بەدەم پەروەردگاریانەوە بێداربن نەفرەتی خوا بۆ ستەمکارانە');
INSERT INTO "bamoki" VALUES(11,19,'11:19','ئەوانەی بەرھەڵستی ڕێگەی خوا دەکەن و دەیانەوێ بەلارو چەوتی نیشاندەن لەکاتێکدا ئەوان بەڕۆژی دوایی بێ باوەڕن');
INSERT INTO "bamoki" VALUES(11,20,'11:20','ئەو (نەفرەت لێکراوانە) ناتوانن (لەدەستی خوا) خۆیان قوتار بکەن لەوڵات و زەویدا و نیانە بێجگە لەخوا ھیچ پشتیوان و دۆستێک بۆیان دووقات دەکرێتەوە سزا توانای بیستنی (ئەو قورئانە) یان نەبوو (ھەقیشیان) نەدەبینی');
INSERT INTO "bamoki" VALUES(11,21,'11:21','ئەوانە کەسانێکن کەخۆیان دۆڕاند وە لێیان وون بوو ئەو درۆ و دەلەسانەی لەدونیادا دەیان کرد');
INSERT INTO "bamoki" VALUES(11,22,'11:22','بەڕاستی و بێگومان ئەوانە لەڕۆژی دواییدا زەرەرمەند ترین کەس خۆیانن');
INSERT INTO "bamoki" VALUES(11,23,'11:23','بەڕاستی ئەوانەی بڕوایان ھێناوە و کردەوە چاکەکانیان بەجێھێناوە و گەردن کەچ و دڵ ئارام بوون بۆ (فەرمانەکانی) پەروەردگاریان ئەوانە ھاوەڵانی بەھەشتن تێیدا دەمێننەوە بەھەمیشەیی');
INSERT INTO "bamoki" VALUES(11,24,'11:24','وێنەی ئەو دوو دەستەیە وەک کوێر و کەڕ و بیسەر و بیناوان ئایا ئەودوو دەستەیە یەکسانن دە بۆ بیرناکەنەوە');
INSERT INTO "bamoki" VALUES(11,25,'11:25','سوێند بە خوا بێـگومان ناردمان نوح بۆ لای گەلەکەی (پێی ووتن) دڵنیابن من ترسێنەرێکی ئاشکرام بۆتان');
INSERT INTO "bamoki" VALUES(11,26,'11:26','کە جگە لە خوا نەپەرستن (چونکە) بەڕاستی من دەترسم یەخەتان بگرێ سزای ڕۆژێکی سەخت و بەئازار');
INSERT INTO "bamoki" VALUES(11,27,'11:27','جا کاربەدەست ودەسەڵاتدارەکان ووتیان ئەوانەی کەبڕوایان نەھێنا بوو لە گەلەکەی (نوح) ئێمە تۆ نابینین جگە لەئادەمیەکی وەک خۆمان وە نابینین تۆ ھیچ کەسێک شوێنت کەوتبێت جگە لەوانەی کە ھەژار و ڕیسوا و پەستەکانمانن بێ ئاوەزا و ساویلکەن (ڕواڵەت بینن) وە نابینن ئێوە ھیچ شتێکتان لە ئێمە زیادبێت بەڵکو بەدرۆزنتان دەزانین');
INSERT INTO "bamoki" VALUES(11,28,'11:28','(نوح) ووتی ئەی گەلەکەم ھەواڵم بدەنێ ئەگەر من بەڵگەی ئاشکرام ھەبێت لەلای پەروەردگارمەوە و ڕەحمەتێکی پێ بەخشیبم لەلایەن خۆیەوە بەڵام ئەوە لەئێوە شاردرابێتەوە ئایا بەزۆر پابەندتان بکەین پێیەوە لە کاتێکدا ئێوە حەزی لێ ناکەن و پێتان ناخۆشە');
INSERT INTO "bamoki" VALUES(11,29,'11:29','وە ئەی گەلەکەم لەسەر ئەم (گەیاندنی پەیامە) داوای ھیچ ماڵ و سامانێکتان لێ ناکەم پاداشتی من ھەر لەسەر خوایە وە من ئەوانە دەرناکەم بڕوایان ھێناوە بەڕاستی ئەوانە بەدیداری پەروەردگاریان دەگەن (لەڕۆژی دواییدا) بەڵام من ئێوە دەبینم کە کۆمەڵێکی نەزانن');
INSERT INTO "bamoki" VALUES(11,30,'11:30','ئەی گەلەکەم کێ یارمەتیم دەدات لەبەرانبەر خوادا ئەگەر ئەوانە بتارێنم و (دەریان بکەم) دەی ئایا بۆ بیرناکەنەوە؟');
INSERT INTO "bamoki" VALUES(11,31,'11:31','من پێتان ناڵێم خەزێنەکانی خوا لای منە وە ناشڵێم غەیب و شتە نادیارەکان دەزانم ھەروەھا ناڵێم من فریشتەم بەوانەش ناڵێم کە لەبەر چاوی ئێوە سووک و بێ ڕێزن خوا ھیچ چاکەیەکیان ناداتێ خوا چاک دەزانێ');
INSERT INTO "bamoki" VALUES(11,32,'11:32','ووتیان ئەی نوح بێگومان دەمەقاڵێت کرد لەگەڵماندا زۆریش دەمەقاڵێت کرد لەگەڵماندا دەی ئەوەی بەڵێنمان پێ دەدەیت (لەسزا) بۆمان بھێنە ئەگەر تۆ لە ڕاست گۆیانیت');
INSERT INTO "bamoki" VALUES(11,33,'11:33','ووتی بێگومان خوا بۆتان دەھێنێت ئەگەر بیەوێت وە ئێوە ناتوانن (لەسزای خوا) خۆتان قوتار بکەن');
INSERT INTO "bamoki" VALUES(11,34,'11:34','وە ئامۆژگاری من سوودتان پێ ناگەیەنێت گەر بمەوێت ئامۆژگاریتان بکەم ئەگەر خوا بیەوێت گومڕاتان بکات ئەو پەروەردگارتانە وە ھەر بۆلای ئەویش دەگەڕێنەوە');
INSERT INTO "bamoki" VALUES(11,35,'11:35','یان ئەوانە دەڵێن موحەممەد صلى الله عليه وسلم ئەم (قورئانەی) بەناوی خواوە ھەڵبەستووە بڵێ ئەگەر من ھەڵم بەستبێت ئەوە گوناھو تاوانەکەی تەنھا لەسەر خۆمە وە منیش بەریم لەتاوانی ئێوە');
INSERT INTO "bamoki" VALUES(11,36,'11:36','وەحی کرا بۆ نوح (علیە السلام) کەوا کەسی تر بڕوا ناھێنێت لەگەلەکەت بێجگە لەوەی پێشتر بڕوای ھێناوە کەواتە ناڕەحەت وغەمبار مەبە بەوەی ئەوان دەیکەن');
INSERT INTO "bamoki" VALUES(11,37,'11:37','وە کەشتیەکە دروست بکە بە پێش چاو و چاودێری وفەرمان و نیگای ئێمە (لەم ئایەتەدا باسی سیفەتی -چاو، عەین - ھاتووە بۆ خوای گەورە، بڕوامان پێیەتی بەبێ ھیچ لێچواندن ولێکدانەوەیەک، شێوەکەی نازانین، وە تەنھا خوا خۆی دەزانێت) وە ھیچ قسەم لەگەڵدا مەکە لە بارەی ئەوانەی ستەمیان کردووە (وبێ باوەڕ بوون) چونکە بێگومان ئەوانە دەخنکێنرێن');
INSERT INTO "bamoki" VALUES(11,38,'11:38','وە دەستی کرد بە دروستکردنی کەشتیەکە ھەر کاتێک تێ پەڕینایە بەلایدا کۆمەڵێک لەگەلەکەی گاڵتەیان پێدەکرد (نوح) دەیووت ئەگەر ئێوە ئێستە گاڵتە و لاقرتی بەئێمە دەکەن ئەوە بێگومان ئێمەش لاقرتی بەئێوە دەکەین ھەر چۆن ئێوە لاقرتی بەئێمە دەکەن');
INSERT INTO "bamoki" VALUES(11,39,'11:39','جا لەمەوپاش دەزانن کێ سزای بۆ دێت سەر شۆڕی دەکات (لەدونیاداو لەقیامەتیشدا) سزای ھەمیشەیی بەسەردادێت');
INSERT INTO "bamoki" VALUES(11,40,'11:40','(نوح) (کەشتیەکەی دروستکرد) ھەتا کاتێک فەرمانی ئێمە ھات (بۆ لەناوبردنیان) و تەنورەکە ئاوی لێ ھەڵقوڵا ووتمان (بەنوح) لەو (کەشتیە) دا ھەڵی بگرە لەھەموو نێرو مێیەک دووان ژن و منداڵەکانیشت ھەڵبگرە ئەوەیان نەبێت بڕیاری خوای لەسەر دراوە (بەخنکاندن) ھەر ئەوانەش (ھەڵ بگرە کە) بڕوایان ھێنا (لەگەلەکەت) وە خەڵکێکی کەم نەبێت بڕوایان نەھێنا لەگەڵیدا');
INSERT INTO "bamoki" VALUES(11,41,'11:41','(نوح) ووتی ھەمووتان سواربن لەو (کەشتیە) دا بەناوی خواوە لەکاتی ڕۆیشتن و وەستانیدا بەڕاستی پەروەردگارم زۆر لێ بوردە و میھرەبانە');
INSERT INTO "bamoki" VALUES(11,42,'11:42','کەشتییەکە دەیبردن بەناو شەپۆلی وەک کێو و شاخدا نوحیش (علیە السلام) کوڕەکەی بانگ کرد (کەناوی کەنعان بوو) کە لەشوێنێکی کەناردا بوو (ووتی) ئەی کوڕی خۆشەویستم لەگەڵمان دا سواربە و لەگەڵ بێ بڕوایان مەبە');
INSERT INTO "bamoki" VALUES(11,43,'11:43','(کوڕەکەی) ووتی پەنا بۆ شاخێک دەبەم کە بمپارێزێت لەم لافاوە (نوح) ووتی: ئەمڕۆ ھیچ پارێزەرێک (پەنادەرێک) نیە لەفەرمانی خوا (کەخنکاندنە) مەگەر کەسێک خوا ڕەحمی پێ بکات (و باوەڕدار بێت) لەوکاتەدا شەپۆلی لافاو کەوتە نێوانیان جا ئەو (کوڕە خنکاو) لەخنکاوان بوو');
INSERT INTO "bamoki" VALUES(11,44,'11:44','ئەوسا ووترا ئەی زەوی ئاوەکەت ھەڵمژە و ئەی ئاسمان باران مەبارێنە ئیتر لافاوەکە رۆچوو بەناخی زەویدا کارەکە (خنکانیان) تەواو کرا و (کەشتیەکە) وەستا بەسەر (کێوی) جودیەوە ووترا دووری لەڕەحمی خوا بۆ گەلی ستەمکاران');
INSERT INTO "bamoki" VALUES(11,45,'11:45','نوح ھاواری کردە پەروەردگاری ووتی ئەی پەروەردگارم بەڕاستی کوڕەکەم لە کەس و کاری منە و بێگومان بەڵێنی تۆش ڕاستە وە تۆش دادوەرترینی ھەموو فەرمان ڕەوایانی');
INSERT INTO "bamoki" VALUES(11,46,'11:46','(خوا) فەرمووی ئەی نوح بەڕاستی ئەو (کەنعان) لەکەس و کاری تۆ نیە بێگومان ئەو (کوڕە) کردەوەی خراپ و ناپەسەند بوو جا کەواتە داوای شتێکم لێ مەکە کەزانیاریت دەربارەی نیە بەڕاستی من ئامۆژگاریت دەکەم تا لەنەفامان نەبیت');
INSERT INTO "bamoki" VALUES(11,47,'11:47','(نوح) ووتی پەروەردگارم بەڕاستی من پەنات پێ دەگرم (لەوەی) داوای شتێکت لێ بکەم کەنایزانم وە ئەگەر نەبوریت لێم وڕەحمم پێ نەکەی لەزیان لێکەوتوان دەبم');
INSERT INTO "bamoki" VALUES(11,48,'11:48','ووترا ئەی نوح دابەزە (لەکەشتیەکە) بەھێمنی و بێوەی لەلایەن ئێمەوە, وە بەپیت و فەڕی زۆر بەسەرخۆتەوە, وەبەسەر گەلانێک کەپەیدا ئەبن لەوانەی کە لەگەڵ تۆ دان (یا بەسەر ئەو خەڵکانەی لەگەڵ تۆدان) چەند نەتەوە و گەل دواتر دێن خۆشی و نازو نیعمەتیان پێ دەدەین پاشان لەلایەن ئێمەوە دووچاریان دەبێت سزای سەختی ئازاردەر');
INSERT INTO "bamoki" VALUES(11,49,'11:49','ئەمانەی (باسکران) لەھەواڵە گرنگە نادیارەکانە بە نیگا بۆمان ناردی نەتۆ و نەگەلەکەت نەتان دەزانین لەپێش ئەم (نیگای ئێمە) کەواتە خۆڕاگربە بەڕاستی سەرئەنجام بۆ لەخوا ترسانە');
INSERT INTO "bamoki" VALUES(11,50,'11:50','وە بۆلای (گەلی) عاد ھودی برایانمان نارد ووتی ئەی گەلەکەم خوا بپەرستن ھیچ پەرستراوێکتان نیە جگە لە ئەو ئێوە تەنھا درۆ ھەڵئەبەستن');
INSERT INTO "bamoki" VALUES(11,51,'11:51','ئەی گەلەکەم لەسەر ئەم (گەیاندنی پەیامە) داوای ھیچ پاداشتێکتان لێ ناکەم پاداشتی من ھەر لەسەر ئەو (خوا) یە کەدروستی کردوم دەی ئایا ژیر نابن');
INSERT INTO "bamoki" VALUES(11,52,'11:52','وە ئەی گەلەکەم داوای لێخۆشبون لەپەروەردگارتان بکەن دواتر بگەڕێنەوە بۆلای ئەو (ئەگەر وابکەن خوا) لەئاسمانەوە (باران) دەبارێنێت بەسەرتاندا بەلێزەمە و ھێزی تر زیاد دەکات بۆ ھێزەکەتان وەڕوو وەرمەگێڕن (لەبانگەوازەکەم) بەتاوان باری');
INSERT INTO "bamoki" VALUES(11,53,'11:53','ووتیان ئەی ھود تۆ ھیچ بەڵگەیەکی ڕوونت بۆ نەھێناوین بۆیە ئێمەش واز لەپەرستراوەکان (خواکان) مان ناھێنین لەبەر قسەی تۆ وە ئێمە باوەڕت پێ ناکەین');
INSERT INTO "bamoki" VALUES(11,54,'11:54','ئێمە تەنھا (ئەوە بەتۆ) دەڵێین ھەندێک لە پەرستراوەکانمان تووشیان کردووی بەشێتی ووتی بەڕاستی من خوا دەکەم بەشایەت و ئێوەش شایەت بن کەبەڕاستی من بەریم و دوورم لەوەی کەئێوە دەیکەنە ھاوبەش بۆخوا');
INSERT INTO "bamoki" VALUES(11,55,'11:55','جگە لەخوا جا ھەمووتان پیلانم لێ بگێڕن لەپاشان مۆڵەتیشم مەدەن (و لێم مەوەستن)');
INSERT INTO "bamoki" VALUES(11,56,'11:56','بەڕاستی من پشتم بەخوا بەستووە کەپەروەردگاری من و پەروەردگاری ئێوەیشە ھیچ گیان لەبەرێک نیە لەژێر ڕکێفی خوای گەورەدا نەبێت (بگری پێشە سەریەتی) بەڕاستی پەروەردگارم لەسەر ڕێگەیەکی ڕاستە');
INSERT INTO "bamoki" VALUES(11,57,'11:57','ئەوسا ئەگەر ئێوە ڕوو وەرگێڕن ئەوە بێگومان من پێم ڕاگەیاندون ئەوەی بەمندا نێردراوە بۆتان وە پەروەردگارم کۆمەڵێکی تر لەشوێنی ئێوە دادەنێت ئێوەش ھیچ زیانێک بەخوا ناگەیەنن بەڕاستی پەروەردگارم بەسەر ھەموو شتێکدا چاودێرو پارێزەرە');
INSERT INTO "bamoki" VALUES(11,58,'11:58','وە کاتێک فەرمانی ئێمە ھات (بۆ لەناوبردنیان) ھودمان ڕزگار کرد ئەوانەش باوەڕیان ھێنابوو لەگەڵیدا بەمیھرەبانی خۆمان وە ئەوانمان لەسزایەکی توند و بەھێز ڕزگار کرد');
INSERT INTO "bamoki" VALUES(11,59,'11:59','ئەوەش گەلی عاد بوو باوەڕیان نەھێنا بە بەڵگە و موعجیزەکانی پەروەردگاریان و سەرپێچی نێرراوەکانی خوایان کرد و شوێن فەرمانی ھەموو زۆردارێکی سەرکەش کەوتن');
INSERT INTO "bamoki" VALUES(11,60,'11:60','بەشوێنیاندا نێررا نەفرەت لەم دونیایەدا و لەڕۆژی دواییشدا بێدار بن بەڕاستی گەلی عاد بڕوایان بەپەروەردگاریان نەھێنا بێداربن دووری و لەناوچوون بۆ عادی گەلی ھود');
INSERT INTO "bamoki" VALUES(11,61,'11:61','بۆ گەلی سەمودیش ناردمان صاڵح پێغەمبەر (علیە السلام) ی برایان ووتی ئەی گەلەکەم خوا بپەرستن ھیچ پەرستراوێکتان نیە بێجگە لە ئەو ئەو دروستی کردوون لەزەویدا و کردونی بەئاوەدانکەرەوە تێیدا کەواتە داوای لێبوردنی لێ بکەن پاشان بگەڕێنەوە بۆلای بەڕاستی پەروەردگارم زۆر نزیک و نزا ودوعا وەرگرە');
INSERT INTO "bamoki" VALUES(11,62,'11:62','ووتیان ئەی صاڵح بەڕاستی تۆ لەناوماندا جێی ئومێد بووی پێش ئەمە ئایا ڕێگریمان دەکەیت لەپەرستنی ئەوەی کەباو و باپیرانمان پەرستوویانە و بەڕاستی ئێمە لە گومانداین لەوەی تۆ بانگمان دەکەیت بۆلای و دوو دڵین');
INSERT INTO "bamoki" VALUES(11,63,'11:63','صاڵح ووتی ئەی گەلەکەم پێم بڵێن ئەگەر من بەڵگەیەکی ئاشکرام ھەبێ لەلایەن پەروەردگارمەوە, وە لەلایەن خۆیەوە پێغەمبەرێتی پێ بەخشیبم جا کێ یارمەتیم دەدات لەبەرامبەر خوادا ئەگەر سەرپێچی ئەو بکەم کەواتە ئێوە ھیچ بۆمن زیاد ناکەن زەرەرو زیان نەبێت');
INSERT INTO "bamoki" VALUES(11,64,'11:64','ئەی گەلەکەم ئەمە ووشترەکەی خوایە کە موعجیزەیەکی ڕوونە بۆ ئێوە (لەسەر ڕاستی پێغەمبەرێتیەکەم) جا ئێوەش وازی لێ بھێنن با لە زەوی خوا بخوات (بلەوەڕێت) بەخراپە دەستی بۆ مەبەن ئەگینا سزایەکی نزیک تووشتان دەبێت');
INSERT INTO "bamoki" VALUES(11,65,'11:65','کەچی ئەوان ووشترەکەیان سەربڕی جا (صاڵح) ووتی ڕابوێرن لەماڵی خۆتاندا سێ ڕۆژ ئەوە بەڵێنێکە درۆ نیە');
INSERT INTO "bamoki" VALUES(11,66,'11:66','جا کاتێک فەرمانمان ھات (بۆ لەناو بردنیان) صاڵحمان ڕزگار کرد ئەوانەش بڕوایان ھێنابوو لەگەڵیدا بە بەزەییەک لەلایەن خۆمانەوە لە سەرشۆڕی و ڕیسوایی ئەو ڕۆژە (پەناماندان) بەڕاستی پەروەردگارت ھەر خۆی بەتواناو بەدەسەڵاتە');
INSERT INTO "bamoki" VALUES(11,67,'11:67','ئەوانەش کەستەمیان کرد شریخەیەک گرتنی ئیتر لەناو ماڵەکانیاندا کەوتن بەڕووداو جووڵەیان لێ بڕا (مردن)');
INSERT INTO "bamoki" VALUES(11,68,'11:68','وەک ئەوەی لەوێدا نەبووبن بێدار بن بەڕاستی گەلی سەمود بێ بڕوابوون بەپەروەردگاریان بێداربن دووری و لەناوچوون بۆ گەلی سەمود');
INSERT INTO "bamoki" VALUES(11,69,'11:69','سوێند بەخوا بێگومان نێرراوەکانمان ھاتن بۆلای ئیبراھیم بە مژدەوە ووتیان سەلامی خوات لێ بێت (ئەویش) ووتی سەلامی خوا لەئێوەش بێت ئەمجا ئەوەندەی نەبرد (ئیبراھیم) خێرا گوێرەکەیەکی برژاوی ھێنا بۆیان');
INSERT INTO "bamoki" VALUES(11,70,'11:70','جا کاتێک بینی دەستیان درێژ ناکەن بۆی (و لێی ناخۆن) نائاسایی ھاتنە بەرچاوی و لەدەروونی خۆیدا مەترسی لێکردن (کەنیازی خراپیان ھەبێت) ووتیان مەترسە بەڕاستی ئێمە نێرراوین بۆ سەر گەلی لوط');
INSERT INTO "bamoki" VALUES(11,71,'11:71','لەوکاتەدا خێزانەکەی وەستابوو و پێکەنی ئێمەش مژدەمان پێ دا بەئیسحاق وە لەپاش ئیسحاق (مژدەمان پێدا) بەیەعقوب (کە کوڕی ئیسحاقە)');
INSERT INTO "bamoki" VALUES(11,72,'11:72','(خێزانی ئیبراھیم) ووتی وەیڕۆ (وای) چۆن منداڵم دەبێت کەمن پیرە ژنێکم ئەوەش مێردەکەمە کە پیربووە بەڕاستی ئەمە شتێکی سەیرە');
INSERT INTO "bamoki" VALUES(11,73,'11:73','(فریشتەکان) ووتیان تۆ سەرت سوڕدەمێنێت؟ لەکار و فەرمانی خوا رەحمەت و بەرەکەتی زۆری خواتان لەسەربێت ئەی ئال و بەیتی ئیبراھیم بەڕاستی خوا سوپاسکراوی چاکە فراوانە');
INSERT INTO "bamoki" VALUES(11,74,'11:74','ئەمجا کاتێک ترس و بیمی ئیبراھیم لاچوو نەما وە مژدەکەشی بۆ ھات لەبارەی گەلەکەی لوط گفتوگۆی لەگەڵ دەکردین');
INSERT INTO "bamoki" VALUES(11,75,'11:75','(چونکە) بەڕاستی ئیبراھیم لەسەرخۆو دڵنەرم و ڕوو لەخوابوو');
INSERT INTO "bamoki" VALUES(11,76,'11:76','ئەی ئیبراھیم وازبھێنە لەم کارە بەڕاستی بێگومان دەرچووە (بۆ ئەوکارە) فەرمانی پەروەردگارت وە بەڕاستی بۆ ئەوانە دێت سزایەکی نەگەڕاوە');
INSERT INTO "bamoki" VALUES(11,77,'11:77','کاتێک کە ھاتن فریشتەکانمان بۆلای لوط ھاتنیانی پێ ناخۆش بوو وە دڵگران بوو پێیان وە ووتی ئەم ڕۆژە ڕۆژێکی سەختە');
INSERT INTO "bamoki" VALUES(11,78,'11:78','وە گەلەکەی ھاتن بۆلای پەلەیان دەکرد (لەھاتن) بۆ لای لەکاتێکدا ئەوان لەوەو پێش ھەمیشە کردەوە بەدەکانیان ئەنجام دەدا (کە نێربازی بوو) ووتی ئەی گەلەکەم ئەوانە کچەکانی منن (مەبەستی کچەکانی ھۆزەکەی بوو) ئەوانە پاک وحەڵاڵن بۆتان دەی لەخوا بترسن و سەرشۆڕم مەکەن لەوەی دەتانەوێت بەرامبەر میوانەکانم بیکەن ئاخۆ پیاوێکی ژیرو تێگەیشتووتان تێدا نیە');
INSERT INTO "bamoki" VALUES(11,79,'11:79','ووتیان سوێندبێت بێگومان تۆ دەزانیت ئێمە ھیچ ھەق (و ئارەزوو) ێکمان لەکچەکانتدا نیە وە بەڕاستی تۆ دەزانی ئێمە چیمان دەوێت');
INSERT INTO "bamoki" VALUES(11,80,'11:80','ووتی ئای خۆزگە لەبەرانبەر ئێوەدا ھێزێکم ببوایە یان پەنایەکی قایم ببوایە پەنام پێ ببردایە (ڕێگریم دەکردن)');
INSERT INTO "bamoki" VALUES(11,81,'11:81','(فریشتەکان) ووتیان ئەی لوط بەڕاستی ئێمە نێرراوی پەروەردگاری تۆین ھەرگیز ناگەنەلای تۆ جا تۆ وماڵ ومنداڵت و ئەوەی باوەڕی پێ ھێناویت لەبەشێکی شەودا بڕۆن (وئەم شارە بەجێ بھێڵن) وە ھیچ کەس لەئێوە لانەکاتەوە بەدوای خۆیدا جگە لەژنەکەت بەڕاستی ئەو تووشی دەبێت ئەوەی کەتووشی ئەوان (گەلەکەت) دەبێت (چونکە بێ باوەڕ بوو) بێگومان وەختی لەناو بردنیان بەرەبەیانە ئایا بەرەبەیان نزیک نیە');
INSERT INTO "bamoki" VALUES(11,82,'11:82','جا کە فەرمانی ئێمە ھات (بۆ لەناوبردنیان) ئەو (شوێنە) مان سەرو ژێر کرد و باراندمان بەسەریاندا ووردەزیخـی گڵین بەدوای یەکداو بێ بچڕِان (دەباری)');
INSERT INTO "bamoki" VALUES(11,83,'11:83','ھەموو ئەو (ووردە زیخانە) دیاری کرابوون لای پەروەردگارتەوە, وە ئەو (سزایە) دوور نیە لەستەمکارانەوە');
INSERT INTO "bamoki" VALUES(11,84,'11:84','وە (ناردمان) بۆ (خەڵکی) مەدیەنیش شوعەیبی برایان ووتی ئەی گەلەکەم خوا بپەرستن ھیچ پەرستراوێکتان نیە بێجگە ئەو وە کەم و کوڕی مەکەن لەپێوانە و کێشانەدا بەڕاستی من دەتان بینم بەماڵ و سامانی زۆرەوە, وە بەڕاستی من دەترسم لێتان سزایەک گەمارۆتان بدات و لێی رزگار نەبن');
INSERT INTO "bamoki" VALUES(11,85,'11:85','جا ئەی گەلەکەم پێوانەو کێشانە ڕاست و دروست ئەنجام بدەن وە کەم و کوڕی مەکەن لەشت ومەکی خەڵکیدا و خراپەکاری مەکەن (یاخراپە بڵاومەکەنەوە) لەزەویدا');
INSERT INTO "bamoki" VALUES(11,86,'11:86','ئەوەی خوا دەیھێڵتەوە (لەقازانجی حەڵاڵ) چاکترە بۆتان ئەگەر ئێوە بڕوادارن و من پارێزەر نیم بەسەرتانەوە');
INSERT INTO "bamoki" VALUES(11,87,'11:87','ووتیان ئەی شوعەیب ئایا نوێژەکەت فەرمانت پێ دەدات کەئێمە وازبھێنین لەوەی باوو باپیرانمان پەرستوویانە یان وازبھێنین لەوەی بەویستی خۆمان لەماڵ و سامانماندا دەیکەن بەڕاستی تۆ پەلەنەکەری لێزان و ھۆشمەندیت');
INSERT INTO "bamoki" VALUES(11,88,'11:88','ووتی ئەی گەلەکەم ھەواڵم بدەنێ ئەگەر من بەڵگەیەکی ڕوونم لەلایەن پەروەردگـارمـەوە پێ بێت وە ڕۆزیەکی زۆرو باشی پێدابم لەلایەن خۆیەوە (چ مەبەستێکم ھەیە درۆ بکەم) وە من ھەرگیز نامەوێت پێچەوانەی ئێوە بکەم لەو (کارەی) ڕێگری ئێوەی لێ دەکەم (خۆم بیکەم) من ھیچ مەبەستێکم نیە بێجگە لەچاککردن وچاکسازی نەبێت ئەوەندەی بتوانم ولەتوانامدا بێت سەرکەوتنیشیم تەنھا بە (پشتیوانی) خوایە پشتم ھەر بەو بەستووە ھەر بۆلای ئەویش دەگەڕێمەوە');
INSERT INTO "bamoki" VALUES(11,89,'11:89','ئەی گەلەکەم دژایەتیتان لەگەڵم نەبێتە ھۆی ئەوەی تووشتان ببێت وێنەی ئەو (سزایەی) تووشی گەلی نوح یان گەلی ھود یان گەلی صاڵح بوو خۆ گەلەکەی لوط دوور نیە لێتانەوە (چییان بەسەر ھات)');
INSERT INTO "bamoki" VALUES(11,90,'11:90','وە داوای لێخۆش بونیش لەپەروەردگارتان بکەن پاشان بگەڕێنەوە بۆلای بەڕاستی پەروەردگارم میھرەبان و دڵسۆزە');
INSERT INTO "bamoki" VALUES(11,91,'11:91','ووتیان ئەی شوعەیب ئێمە تێناگەین لەزۆرێک لەوەی تۆ دەیڵێیت بەڕاستی ئێمە تۆ بە لاواز دەبینین لەناوماندا خۆ ئەگەر (لەبەر) خزمەکانت نەبوایە دڵنیابە بەرد بارانمان دەکردیت (چونکە) تۆ خۆت ھیچ دەسەڵاتێکت نیە لەبەرامبەر ئێمەدا');
INSERT INTO "bamoki" VALUES(11,92,'11:92','ووتی ئەی گەلەکەم ئایا خزمەکانم بەدەسەڵات ترن لەبەرامبەر ئێوەدا لە خوا لەکاتێکدا فەرمانی ئەو (خوا) تان پشت گوێ خستووە بەڕاستی پەروەردگارم ئاگادارە بەھەرچی ئێوە دەیکەن');
INSERT INTO "bamoki" VALUES(11,93,'11:93','ئەی گەلەکەم کردەوە بکەن لەسەر بار و حاڵەتی خۆتان بەڕاستی منیش کارو کردەوە دەکەم لەمەودوا دەزانن کێ سزای بۆ دێت ڕیسوا و سەر شۆڕی دەکات وەکێش درۆزنە جا چاوەڕوانی بکەن بەڕاستی منیش لەگەڵتان چاوەڕوانم');
INSERT INTO "bamoki" VALUES(11,94,'11:94','کاتێک فەرمانی ئێمە ھات (بۆ لەناوبردنیان) شوعەیبمان ڕزگار کرد و ئەوانەش بڕوایان ھێنا بوو لەگەڵیدا بە بەزەیی لەلایەن خۆمانەوە, وە ئەوانەی ستەمیان کرد بوو ناڵە و شریخە کە (نەعرەتەکە) گرتنی ئیتر ھەموویان لەماڵ و جێگاکانیاندا دەمەو ڕووکەوتن و جووڵەیان نەما (مردن)');
INSERT INTO "bamoki" VALUES(11,95,'11:95','(وایان لێھات) وەک ھەر تێیدا نەبووبن بێداربن دووری و تیاچوون بۆ خەڵکی مەدیەن وەک چۆن گەلی سەمود تیاچوون و دوور بوون لەڕەحمی خوا');
INSERT INTO "bamoki" VALUES(11,96,'11:96','سوێند بەخوا بێگومان ناردمان موسا بە موعجیزەکانمانەوە, وە بەڕابەرو بەڵگەی ڕوونەوە');
INSERT INTO "bamoki" VALUES(11,97,'11:97','بۆ لای فیرعەون و دەست وپێوەندەکەی بەڵام ئەوان شوێن فەرمانی فیرعەون کەوتن لەکاتێکدا فەرمانی فیرعەون ڕاست و ڕەوا نەبوو');
INSERT INTO "bamoki" VALUES(11,98,'11:98','پێش گەلەکەی دەکەوێت لەڕۆژی دواییدا ئەمجا ھەموویان دەباتە ناو ئاگری دۆزەخ ئای چەند خراپە ئەو شوێنەی ئەوانی بۆ دەبرێت');
INSERT INTO "bamoki" VALUES(11,99,'11:99','وە لەم (دونیا) دا نەفرەتیان بەشوێندا نێرراوە ھەروەھا لەڕۆژی دواییشدا ئای چەند خراپە ئەو بەخشراوەی پێیان بەخشرا');
INSERT INTO "bamoki" VALUES(11,100,'11:100','ئەوەی (باسکرا بۆت چەند ھەواڵێکە) لە ھەواڵی شارەکانی ڕابوردوو دەیگێڕینەوە بۆت (لەم سورەتەدا) ھەندێکیان ماون وھەندێکی تریان نەماون (وێران بوون)');
INSERT INTO "bamoki" VALUES(11,101,'11:101','وە ئێمە ستەممان لێ نەکردن بەڵکو خۆیان ستەمیان کرد لەخۆیان جا ھیچ سوودێکی پێ نەگەیاندن ئەو پەرستراوانەیان کەدەیان پەرستن و ھاواریان لێکردن لەجیاتی خوا بە ھیچ شتێک (سودیان پێ نەگەیاندن) کاتێ فەرمانی پەروەردگارت ھات (بۆ لەناو بردنیان) ئەوانە (ئەو بتانە) ھیچیان زیاد نەکرد بۆ ئەوانەی (دەیان پەرستن) بێجگە زیان و لەناوچوون نەبێت');
INSERT INTO "bamoki" VALUES(11,102,'11:102','ئا بەو شێوەیە سزادانی پەروەردگارت کاتێک خەڵکی شارەکان سزا دەدات لە کاتێکدا ستەمکاربوون بەڕاستی تۆڵە سەندنی ئەو ئازار دەر و بەتینە');
INSERT INTO "bamoki" VALUES(11,103,'11:103','بەڕاستی لەوەی (باسکرا) پەندو ئامۆژگاری ھەیە بۆ کەسێک بترسێت لەسزای دوا ڕۆژ ئەو ڕۆژە ڕۆژێکە کەئادەمی بۆ کۆدەکرێتەوە و ئەوە ڕۆژێکی دیارو بەرچاوە (ھەموان ئامادەن تێیدا)');
INSERT INTO "bamoki" VALUES(11,104,'11:104','وە ئێمە دوای ناخەین مەگەر بۆ کاتێکی دیارو بەرچاو نەبێت');
INSERT INTO "bamoki" VALUES(11,105,'11:105','ئەو ڕۆژە کەدێت ھیچ کەس قسە ناکات بەمۆڵەتی خوا نەبێت ئەوسا لەناویاندا ھەیە بێ بەخت (وچارە ڕەش) و ھەیە بەختەوەر (وئاسوودە)');
INSERT INTO "bamoki" VALUES(11,106,'11:106','جا ئەوانەی بێ بەخت وچارە ڕەش بوون ئەوە لەناو ئاگردان لەناو ئەو (ئاگرە) دا ھەر ئاخ وئۆف و ھەنیسکیانە');
INSERT INTO "bamoki" VALUES(11,107,'11:107','تیایدا دەمێننەوە بەھەمیشەیی تا ئاسمانەکان و زەوی بمێنێت (مەبەست پێی ئاسمان وزەویە لە قیامەتدا کە ئەوانیش ھەر دەمێنن) مەگەر ئەو کەسەی پەروەردگارت بیەوێت (ڕزگاری بکات) بەڕاستی پەروەردگارت ئەوەی کەبیەوێت دەیکات');
INSERT INTO "bamoki" VALUES(11,108,'11:108','بەڵام ئەوانەی کەبەختەوەر و ئاسوودەکراون ئەوە لەبەھەشتدان بەھەمیشەیی دەمێننەوە تیایدا تا ئاسمان و زەوی بمێنێت مەگەر ئەو کەسەی پەروەردگارت بیەوێت ئەوە بەخشینێکە بێ بڕانەوە');
INSERT INTO "bamoki" VALUES(11,109,'11:109','کەواتە ھیچ جۆرە گومان و دودڵیەکت نەبێت لە (ناڕاستی) ئەوەی ئەوانە دەیپەرستن تەنھا ئەوە دەپەرستن کەباوو باپیرانیان دەیان پەرستن لەوەو پێش بەڕاستی ئێمە پێیان دەدەین بەتەواوی بەشی خۆیان (لەسزا) بەبێ لێ کەمکردنەوە (پڕاوپڕ)');
INSERT INTO "bamoki" VALUES(11,110,'11:110','سوێند بەخوا بەڕاستی ئێمە بەخشیمان بەموسا پەڕاوی (تەورات) ئەمجا جیاوازی تێداکرا ئەگەر لەبەر بڕیارێک نەبوایە کەپێشتر دراوە لەلایەنی پەروەردگارتەوە دادوەری دەکرا لەنێوانیاندا و بەڕاستی ئەوانە لەگومان و دوو دڵیدان لەو (قورئانە)');
INSERT INTO "bamoki" VALUES(11,111,'11:111','بێگومان ھەموو (ئەوانەی جیاوازن) پەروەردگارت (پاداشتی) کردەوەکانیان بەتەواوی پێ دەدات بەڕاستی ئەو ئاگادارە بەھەر کردەوەیەک کەدەیکەن');
INSERT INTO "bamoki" VALUES(11,112,'11:112','کەواتە ڕاستبە بەو شێوەی فەرمانت پێدراوە, وە ئەوانەش تۆبەیان کردوە لەگەل تۆدا و (گەڕاونەتەوە) زیادە ڕەوی مەکەن بەڕاستی ئەو (خوایە) بینایە بەھەر کردەوەیەک کەدەیکەن');
INSERT INTO "bamoki" VALUES(11,113,'11:113','پاڵ مەدەن بەوانەوە کەستەمیان کردوە چونکە دووچاری ئاگری دۆزەخ دەبن و بێجگە لەخوا ھیچ پشتیوانێکتان نابێت پاشانیش یارمەتی نادرێن');
INSERT INTO "bamoki" VALUES(11,114,'11:114','وە نوێژ ئەنجام بدە لەم سەرو ئەوسەری ڕۆژدا (لەبەیانیان و ئێواراندا) و لە بەشێک لەشەودا چونکە بەڕاستی (کردەوە) چاکەکان (کردەوە) خراپەکان لادەبەن ودەیانسڕنەوە ئەوە پەند و ئامۆژگاریە بۆ پەند وەرگران');
INSERT INTO "bamoki" VALUES(11,115,'11:115','وە خۆڕاگر بە چونکە بەڕاستی خوا پاداشتی چاکە کاران وون ناکات');
INSERT INTO "bamoki" VALUES(11,116,'11:116','دەی باببوایە لەناو ئەو ئوممەتانەی کەلە پێش ئێوە دابوون کەسانی خاوەن ئاین و ژیری کە ڕێگریان بکردایە لەخراپەکاری لەزەویدا مەگەر کەمێک لەوانەی ڕزگارمان کردن لەو گەلانەدا وە ئەوانەی کەستەمیان کرد شوێنی ژیان خۆشی و ڕابواردن کەوتن و بەردەوام بوون لە خراپەکاری');
INSERT INTO "bamoki" VALUES(11,117,'11:117','ھەرگیز پەروەردگارت بەستەم خەڵکی شارو گوندەکان لەناو نابات (واتە خوای گەورە ستەمکار نیە) لە کاتێکدا دانیشتوانی (ئەو شارانە) موصڵح و چاکەکاربووبن');
INSERT INTO "bamoki" VALUES(11,118,'11:118','گەر پەروەردگارت بیویستایە گشت خەڵکی دەکرد بە (پەیڕەوی) یەک ئاین بەڵام ئەوان بەردەوام جیاوازدەبن');
INSERT INTO "bamoki" VALUES(11,119,'11:119','مەگەر کەسێک پەروەردگارت میھرەبانی لەگەڵ کردبێت وە ھەر لەبەر ئەوەش دروستی کردوون بڕیاری پەروەردگارت دەرچوو (کۆتایی ھات) کە سوێند بەخوا دۆزەخ پڕ دەکەم لە جنۆکە و لە ئادەمی ھەموو پێکەوە');
INSERT INTO "bamoki" VALUES(11,120,'11:120','ھەمووت بۆ دەگێڕینەوە لە ھەواڵی پێغەمبەران (علیھم السلام) بۆ ئەوە کە دڵی تۆی پێ دامەزراو بکەین وە لەم (سورەتە) دا حەق و ڕاستیت بۆ ھاتووە, وە ئامۆژگاری و پەندیشی تیایە بۆ بڕواداران');
INSERT INTO "bamoki" VALUES(11,121,'11:121','وە بڵێ بەوانەی کەبڕوا ناھێنن ئێوە کار وکردەوە بکەن لەسەر ئەو شێوەی خۆتان (کە ئەوەتان لەسەری) بێگومان ئێمەش کردەوە دەکەین (بەگوێرەی ئەوەی کە خودا فەرمانی پێ کردووین)');
INSERT INTO "bamoki" VALUES(11,122,'11:122','ئەوسا چاوەڕوانی بکەن (بۆ بەسەرھاتی خراپ) بەڕاستی ئێمەش چاوەڕوانین');
INSERT INTO "bamoki" VALUES(11,123,'11:123','وە تەنھا بۆ خوایە (زانینی) نھێنی ئاسمانەکان وزەوی وتەنھا ھەر ئەویش پێی ئاگادارە ھەر بۆلای ئەویش دەگەڕێنرێتەوە ھەموو کار و بارێک کەواتە ئەو بپەرستە و ھەرپشت بەو ببەستە وە بێگومان پەروەردگارت بێ ئاگا نیە لەھەر کردەوەیەک ئێوە دەیکەن');
INSERT INTO "bamoki" VALUES(12,1,'12:1','بە (ئەلیف، لام، ڕا) دەخوێنرێتەوە خوا زاناترە بە ماناکەی بۆ زانیاری زیاتر سەیری سەرەتای سورەتی (البقرە) بکە ئەمانە نیشانەکانی کتێبی ئاشکران');
INSERT INTO "bamoki" VALUES(12,2,'12:2','بەڕاستی ئێمە ناردومانەتە خوارەوە قورئانێک بە عەرەبی بۆ ئەوەی ژیربن و لێی تێ بگەن');
INSERT INTO "bamoki" VALUES(12,3,'12:3','ئێمە دەگێڕینەوە بۆ تۆ جوانترین چیرۆک و بەسەرھات لە ڕێگای ئەم قورئانەوە نیگامان کردووە بۆت ھەر چەندە تۆ لەپێش (ھاتنی) ئەم قورئانەدا لەم بەسەرھاتە بێ ئاگا بوویت');
INSERT INTO "bamoki" VALUES(12,4,'12:4','(بیریان خەرەوە) کاتێک یوسف بە باوکی ووت ئەی بابە گیان بە ڕاستی من لە خەومدا بینیم یازدە ئەستێرە و مانگ و ڕۆژ بینیمن سوژدەیان بۆ دەبردم');
INSERT INTO "bamoki" VALUES(12,5,'12:5','ووتی ئەی کوڕی شیرینم خەوەکەت مەگێڕەرەوە بۆ براکانت نەوەک بەھۆیەوە فێڵ و پیلانێکت لێ بگێڕن بەڕاستی شەیتان دوژمنێکی ئاشکرایە بۆ ئادەمی');
INSERT INTO "bamoki" VALUES(12,6,'12:6','ئابەو شێوەیە ھەڵت دەبژێرێت (بۆ پێغەمبەرایەتی) پەروەردگارت وە فێری لێکدانەوەی خەونەکانیشت دەکات وە نیعمەت و بەھرەی خۆی تەواو دەکات بۆت وە بۆ بنەماڵەی یەعقوبیش ھەر چونێک تەواوی کرد بۆ ھەردوو باپیرەت لەپێشدا کەئیبراھیم و ئیسحاقن بەڕاستی پەروەردگارت زانا و کاردروستە');
INSERT INTO "bamoki" VALUES(12,7,'12:7','سوێند بەخوا بەڕاستی لە بەسەر ھاتی یوسف و براکانیدا پەندو ئامۆژگاری ھەیە بۆ پرسیارکەران');
INSERT INTO "bamoki" VALUES(12,8,'12:8','کاتێک (براکانی یوسف) ووتیان بێگومان یوسف و براکەی خۆشەویسترن لە ئێمە بەلای باوکمانەوە لەکاتێکدا ئێمە کۆمەڵێکی بەھێز و بەتواناین بەڕاستی باوکمان لە ھەڵەیەکی ڕوون و ئاشکرادایە');
INSERT INTO "bamoki" VALUES(12,9,'12:9','(ھەندێکیان ووتیان) یوسف بکوژن یافڕێی بدەنە زەویەکی دوور (ئەو کاتە) سەرنجی باوکتان تەنیا بۆلای ئێوە دەبێت و لەدوای ئەوەوە دەبن بەکۆمەڵێکی چاکەکار (بەتۆبەکردن)');
INSERT INTO "bamoki" VALUES(12,10,'12:10','قسەکەرێک لەوان ووتی یوسف مەکوژن بەڵکو فڕێی بدەنە بنی بیرێکەوە ھەندێک لە کاروانیەکان (دەیدۆزنەوە) وھەڵیدەگرن ئەگەر ئێوە (کارەکە) ھەر دەکەن');
INSERT INTO "bamoki" VALUES(12,11,'12:11','(پاش کێشانی نەخشە وپیلانەکەیان) ووتیان ئەی باوکی بەڕێزمان لەبەرچی تۆ لە یوسف لێمان دڵنیانیت و (بەگومانیت لێمان) لەکاتێکدا کەئێمە دڵسۆزو خەمخۆرین بۆی');
INSERT INTO "bamoki" VALUES(12,12,'12:12','سبەینێ لەگەڵمان بینێرە (بۆ دەرو دەشت) (بە ئارەزووی خۆی) خواردن بخوات و یاری بکات و بێـگومان ئێمە پارێزگاری دەکەین');
INSERT INTO "bamoki" VALUES(12,13,'12:13','(باوکیان) ووتی بەڕاستی دڵتەنگ و خەمباردەبم کەئێوە یوسف بەرن (لەگەڵ خۆتان) وە دەترسم کە ئێوە لێی بێ ئاگابن وگورگ بیخوات');
INSERT INTO "bamoki" VALUES(12,14,'12:14','ووتیان چۆن گورگ دەیخوات؟! لەکاتێکدا ئێمە کۆمەڵێکی بەھێزین بەڕاستی ئەو کاتە ئێمە زەرەر مەندین (ڕوو زەردی تۆ دەبین)');
INSERT INTO "bamoki" VALUES(12,15,'12:15','ئەمجا کاتێک یوسفیان برد لەگەڵ خۆیان وە ھەموو بڕیاریاندا بیخەنە بنی بیرەکەوە, وە ئێمە وەحی و سرووشمان بۆ (یوسف) کرد سوێند بەخوا ھەواڵی ئەم کارەیان پێ دەدەیت (کەپێیان کردی) لەکاتێکدا ئەوان ھەست ناکەن');
INSERT INTO "bamoki" VALUES(12,16,'12:16','جا ئێوارە درەنگانێک (براکانی یوسف) بەگریانەوە ھاتنەوە بۆلای باوکیان');
INSERT INTO "bamoki" VALUES(12,17,'12:17','ووتیان ئەی باوکی ئێمە بەڕاستی ئێمە ڕۆیشتین پێشبڕکێمان دەکرد و یوسفمان لای شتومەکەکانمان بەجێ ھێشت بۆیەگورگ خواردی وە تۆ قەت باوەڕ بەئێمە ناکەیت ھەرچەند ئێمە ڕاستگۆش بین');
INSERT INTO "bamoki" VALUES(12,18,'12:18','(براکانی) کراسەکەی (یوسف) یان ھێنایەوە کە خوێنی درۆینەی پێوە بوو (باوکیان) ووتی بەڵکو دڵ و دەروونی خۆتان ئەم کارە (ناپەسەندەی) بۆ ئاسان و جوان کردوون (ووتی جا ئارام دەگرم) بەئارام گرتنێکی چاک و پەسەند خواش یارمەتیدەرمە لەسەر ئەوەی باسی دەکەن');
INSERT INTO "bamoki" VALUES(12,19,'12:19','جا کاروانێک ھات و ئاو ھێنەرەکەیان نارد (بۆ ئاو) ئەویش دۆڵچەکەی داھێڵا بۆ ناو بیرەکە (کە دۆڵچەکەی ھێنایە سەرەوە) ووتی ئەی خەڵکینە موژدە بێت ئەمە کوڕێکە وە ئەوان (یوسف) یان شاردەوە کردیانە کاڵای بازرگانی وە خوا زانایە بەوەی ئەوانە دەیکەن');
INSERT INTO "bamoki" VALUES(12,20,'12:20','و فرۆشتیان بەنرخێکی کەم کە چەند دەرھەمێک بوو و ئەوان بێ نیاز بوون لێی (چونکە دەترسان ڕازیان دەرکەوێت)');
INSERT INTO "bamoki" VALUES(12,21,'12:21','وە ئەو کەسەی لە میسر یوسفی کڕی ووتی بە ژنەکەی ڕێزی لێ بگرە (شوێنی حەوا نەوەی چاکی بۆ ئامادە بکە) بەڵکو سوودمان پێ بگەیەنێت یان بیکەین بەکوڕی خۆمان ئابەم جۆرە یوسفمان جێ گیرکرد (و پلەو پایەمان پێدا) لە زەوی (میسر) دا (ئەمەمان کرد) تا فێری خەو لێکدانەوەی بکەین وە خوا بەسەرکاری خۆیدا زاڵ و بەدەسەڵاتە بەڵام زۆربەی خەڵکی نازانن');
INSERT INTO "bamoki" VALUES(12,22,'12:22','وە کاتێک (یوسف) بوو بەپیاو و پێگەیی فەرمان ڕەوایی و زانیاریمان پێدا (وکردمان بە پێغەمبەر) وە ئابەم جۆرە پاداشتی چاکەکاران دەدەینەوە');
INSERT INTO "bamoki" VALUES(12,23,'12:23','ئەو ئافرەتەی یوسف لەماڵیدا بوو دەست تێکەڵاوی (وخیانەتی) لەیوسف ویست و ھەموو دەرگاکانی داخست ووتی دەی وەرە پێشەوە بۆ ئەوەی بۆت ئامادەکراوە (یوسف) ووتی من پەنا دەگرم بەخوا بەڕاستی ئەو (خوا) پەروەردگاری منە ڕێزی لێ گرتووم بەڕاستی ستەمکاران ڕزگاریان نابێت');
INSERT INTO "bamoki" VALUES(12,24,'12:24','سوێند بەخوا بێگومان ئەو ژنە بەتەواوی نیازی (تێکەڵی ژن و مێردی) یوسفی کرد، یوسفیش نیازی ئەو ژنەی دەکرد بەخەتەرەی نەفسی بەشەری ئەگەر ئەو بەڵگە ڕوونەی پەروەردگاری نەدیبایە ئا بەو جۆرە (بەڵگەمان نیشانی یوسف دا) بۆ ئەوەی خراپەو داوێن پیسی لێ دوور بخەینەوە چونکە بەڕاستی ئەو (یوسف) لەبەندە پاک وھەڵبژاردەکانی ئێمەیە');
INSERT INTO "bamoki" VALUES(12,25,'12:25','(یوسف و ئافرەتەکە) ھەر دووکیان بەڕاکردن گەشتنە لای دەرگاکە (یوسف بۆ خۆ ڕزگارکردن و ژنەکەی (عەزیزی میێر) یش بۆ ئەوەی بگات بە یوسف (بۆ بەجێھێنانی مەبەستی خۆی) (ئافرەتەکە) کراسەکەی (یوسفی) دڕاند لەدواوە جا ھەردووکیان تووشی مێردی (ئافرەتەکە) بوون لەبەردەم دەرگاکەی (دەرەوە) دا (ئافرەتەکە بۆ ئەوەی خۆی لەو تاوانە بەدوور بگرێت وخیانەتەکەی دەرنەکەوێت) بەپەلە ووتی: سزای ئەو کەسە چیە کە ویستبێتی خراپە (داوێن پیسی) لەگەڵ خێزانەکەتدا بکات جگە لەوەی بەند بکرێت یان سزایەکی سەخت بدرێت');
INSERT INTO "bamoki" VALUES(12,26,'12:26','(یوسف) ووتی ئەو (ژنە) داوێن پیسی لەمن وویست ئەوسا شایەتێک لەکەسوکاری ئەو (ئافرەتە) شایەتیدا (ووتی) ئەگەر کراسەکەی (یوسف) لە پێشەوە دڕێنرا بێت ئەوە ئافرەتەکە ڕاست دەکات وە (یوسف) لە درۆزنان (خراپەکارانە)');
INSERT INTO "bamoki" VALUES(12,27,'12:27','وە ئەگەر کراسەکەی لەدواوە دڕێنرا بێت ئەوە ئافرەتەکە درۆ دەکات و (یوسف) لەڕاست گۆیانە');
INSERT INTO "bamoki" VALUES(12,28,'12:28','ئەمجا کاتێک (عەزیز) بینی کراسەکەی یوسف لەدواوە دڕاوە ووتی بەڕاستی ئەمە لەفڕو فێڵی ئێوەی (ئافرەتانە) بەڕاستی فڕوفێڵی ئێوە زۆر گەورەیە');
INSERT INTO "bamoki" VALUES(12,29,'12:29','(عەزیزی میێر ووتی) یوسف وازبھێنە لەم کارەو باسی مەکە و (ژنەکە) تۆش بۆ گوناھەکەت داوای لێخۆشبون بکە چونکە بەڕاستی تۆ لەپێڕی تاوانبارانی');
INSERT INTO "bamoki" VALUES(12,30,'12:30','(دەستەیەک) لەژنانی شار (پاش ئەوەی ھەواڵەکەیان بیست بەتەشەرەوە) ووتیان ژنەکەی عەزیزی (میێر) دەیەوێت (ھەوڵ دەدات) لەگەڵ غوڵامەکەی داوێن پیسی بکات بەڕاستی خۆشـەویسـتی (ئـەو غوڵامـە) چووەتە ناو پەردەی دڵی بەڕاستی ئێمە ئەو (ژنە) دەبینین لە گومڕاییەکی ڕووندایە');
INSERT INTO "bamoki" VALUES(12,31,'12:31','جا کاتێک ژنەکەی (عەزیزی میێر) تانەو تەشەری ئەوانی بیست ناردی بەدوایاندا ئامادەی کرد بۆیان شوێنی (خۆشی دانیشتن و) شان دادان و ھەریەک لەوان چەقۆیەکی پێدان (بۆ میوەلەت کردن) ئەوسا ووتی (بەیوسف) وەرە بۆ ناویان (بابتبینن) ھەر کە ئەوان (یوسف) یان بینی حەپەسان (لەجوانی ئەودا) و ھەموویان (لەکاتی میوە لەتکردندا) دەستی خۆیانیان بڕی ووتیان پاکی وبێگەردی بۆ خوا ئەمە ئادەمی نیە ئەمە تەنھا فریشتەیەکی بەرزو بەڕێزە');
INSERT INTO "bamoki" VALUES(12,32,'12:32','(ژنەکە) ووتی پێیان ئەمە ئەو کەسەیە کە ئێوە سەرزەنشتی منتان دەکرد لەسەری وە سوێند بەخوا بێگومان ویستم دەستم لەگەڵ تێکەڵ کات بەڵام ئەو خۆی گرتووە (نەیکرد) وە سوێند بەخوا ئەگەر ئەوەی فەرمانی پێ دەکەم نەیکات ئەوە بێگومان بەند دەکرێت و لەڕیسوایان دەبێت');
INSERT INTO "bamoki" VALUES(12,33,'12:33','(یوسف) ووتی ئەی پەروەردگارم بەندکردنم لا خۆشترە لەوەی کەئەوانە داوای دەکەن لێم (کە بیکەم) وە ئەگەر پیلانی ئەو ژنانەم لێ لانەدەی لەوانەیە دڵم بۆلایان بڕوا (حەزیان لێ بکەم) پاشان لەنەفامان دەبم و دەچمە ڕیزیانەوە');
INSERT INTO "bamoki" VALUES(12,34,'12:34','ئەوسا پەروەردگاری نزاکەی لێ وەرگرت و پیلانەکەی ئەو (ژنانەی) لێ لادا بەڕاستی ھەر خوا بیسەر و زانایە');
INSERT INTO "bamoki" VALUES(12,35,'12:35','ئەمجا بۆیان دەرکەوت و پێیان چاک بوو پاش ئەوەی ئەو ھەموو بەڵگەیان (لێ) بینی تاماوەیەک بەندی بکەن');
INSERT INTO "bamoki" VALUES(12,36,'12:36','ئەوسا دوو کوڕی لاو (خزمەتکار) لەگەڵی چوونە بەندیخانە یەکێکیان ووتی (بەیوسف) بەڕاستی لەخەومدا خۆمم بینی ترێم دەگوشی کەدەبێ بەعەرەق وە ئەوی تریان ووتی بێگومان لەخەودا خۆمم بینی نانم ھەڵدەگرت بەسەر سەرمەوە باڵندە لێی دەخوارد لێکدانەوەی ئەو (خەوە) مان پێ بڵێ بەڕاستی ئێمە تۆ لەچاکەکاران دەبینین (کەچاک خەو لەیەک دەدەیتەوە و پەرژینی دەکەیت)');
INSERT INTO "bamoki" VALUES(12,37,'12:37','(یوسف) ووتی: (سبەینێ) ھیچ خۆراکێکتان پێ نادرێت کە بیخۆن بەھەر دووکتان دەڵێم چی وچیتان بۆ دەھێنن پێش ئەوەی بگاتە لاتان ئەمەش لەو بەھرانەیە کەپەروەردگارم فێری کردوم بەڕاستی من وازم ھێناوە لەئاینی گەلێک کەبڕوایان بە خوا نەھێنا وە ئەوان بێ بڕواش بوون بە ڕۆژی دوایی');
INSERT INTO "bamoki" VALUES(12,38,'12:38','وە شوێنی ئاینی باوو باپیرانم کەوتووم کە (ئاینی) ئیبراھیم و ئیسحاق و یەعقوبە شایستە و شیاونیە بۆمان (بۆ شوێنکەوتوانی ئیبراھیم) کەھیچ شتێک بکەین بەھاوبەش بۆ خوا ئەمەش چاکە و بەھرەی خوایە بەسەر ئێمەوە, وە بەسەر خەڵکیشەوە بەڵام زۆربەی خەڵکی سوپاسی خوا ناکەن');
INSERT INTO "bamoki" VALUES(12,39,'12:39','ئەی دوو ھاوڕێکەی بەندینخانەم ئایا چەند خوایەکی جیاجیا باشترن یان خوایەکی تاکی بەدەسەڵات');
INSERT INTO "bamoki" VALUES(12,40,'12:40','ئێوە بێجگە لەخوا ھیچ شتێک ناپەرستن جگە لە چەند ناوێک نەبێت کە ئێوە و باوو باپیراتان ناوتان ناوون (خودا) خوا ھیچ بەڵگەیەکی دەربارەیان نەناردۆتە خوارەوە (کە ئەوانە بپەرستن) بێگومان بڕیار تەنھا بۆ خوایە فەرمانی داوە بێجگە لەو ھیچ شتێک نەپەرستن ئەوەیە ئاینی ڕاستی دامەزراوی نەگۆڕاو بەڵام زۆربەی خەڵکی نازانن');
INSERT INTO "bamoki" VALUES(12,41,'12:41','ئەی دوو ھاوڕێکەی بەندینخانەم یەکێکتان دەبێت بە مەی گێڕی گەورەکەی (ھی پاشا) بەڵام ئەوی ترتان (دەکوژرێت و) ھەڵدەواسرێت ئەوسا باڵندە لەسەر (و دەم چاوی) دەخوات ئەو کارەی کەئێوە دەربارەی پرسیار دەکەن (لەلایەن خوداوە) بڕیاری لەسەر دراوە (وناگۆڕێت)');
INSERT INTO "bamoki" VALUES(12,42,'12:42','وە (یوسف) ووتی بەوەی کەدەیزانی ڕزگاری دەبێت لەدوو بەندکراوەکە باسم بکە لای گەورە و پادشاکەت (کە بەناھەق گیراوم) بەڵام شەیتان لەبیری بردەوە کەباسی (یوسف) بکات لای پادشاکەی بۆیە ماوەی چەند ساڵێک (یوسف) لەبەندیخانەدا مایەوە');
INSERT INTO "bamoki" VALUES(12,43,'12:43','وە پادشا (خەوێکی بینی) ووتی بێگومان من لەخەومدا حەوت مانگای قەڵەوم بینی کەحەوت (مانگا) ی لەڕ ولاواز دەیخواردن وە حەوت گوڵە دانەوێڵەی سەوز و حەوتی تری وشکم بینی ئەی کاربەدەست ودونیا دیدەکان ئەم خەوەم بۆ لێک بدەنەوە ئەگەر ئێوە مانای خەو دەکەن و دەزانن خەو پەرژین بکەن');
INSERT INTO "bamoki" VALUES(12,44,'12:44','ووتیان خەوێکی تێکەڵ و پێکەڵ (و پڕوپوچە) و ئێمەش لێکدانەوەی خەوی تێکەڵ و پێکەڵ نازانین');
INSERT INTO "bamoki" VALUES(12,45,'12:45','ئەوسا ئەوەیان کە ڕزگاری بوو لەدوو (بەندکراوە) کە لەپاش چەند ساڵێک بیری کەوتەوە ووتی من بۆتان لێکدەدەمەوە مانای ئەو خەوە جا (فەرموو) بمنێرن (بەدوای یوسفدا کە لەبەندینخانەدایە)');
INSERT INTO "bamoki" VALUES(12,46,'12:46','(ھات ووتی) یوسف ئەی ھاوڕێی ڕاستگۆ مانای ئەو خەوەمان پێ بڵێ چی یە؟ حەوت مانگای قەڵەو کەحەوت مانگای لەڕو لاواز بیانخوات ھەروەھا حەوت گوڵی دانەوێڵەی سەوز و حەوت گوڵی ووشک تا من بگەڕێمەوە بۆلای خەڵکی بۆ ئەوەی ئەوان ئەوە بزانن');
INSERT INTO "bamoki" VALUES(12,47,'12:47','(یوسف) ووتی حەوت ساڵ کشتوکاڵ دەکەن بەدوای یەکداو بەبەردەوامی جائەوەی کەدروێنەتان کرد بیھێڵنەوە لەگوڵەکەیدا کەمێک نەبێت لەوەی کەدەیخۆن');
INSERT INTO "bamoki" VALUES(12,48,'12:48','ئەمجا لەپاش ئەو (حەوت ساڵە) حەوت ساڵی تردێت کەسەخت وقات و قڕییە ھەرچیتان پاشە کەوت کردووە بۆ ئەم (حەوت ساڵە سەختە) دەیخۆن مەگەر کەمێک لەوەی قایم ھەڵی دەگرن (بۆ بنە تۆو وناشتنەوەی ساڵێکی تر)');
INSERT INTO "bamoki" VALUES(12,49,'12:49','پاشان دوای ئەو (حەوت ساڵە سەختە) ساڵێک دێت خەڵکی تێیدا (ڕزگار دەکرێن) بارانیان بۆ دەبارێنرێت و تێیدا دۆشاوی (میوەھات) دەگرن');
INSERT INTO "bamoki" VALUES(12,50,'12:50','پاشا ووتی ئەو (پیاوە) م بۆ بھێنن جا کاتێک نێردراوەکەی (پاشا) ھاتە لای یوسف (یوسف) ووتی بگەڕێرەوە بۆلای گەورەکەت (پادشاکەت) و پرسیاری لێ بکە ھۆی چی بوو کەئەو ژنانە دەستی خۆیانیان بڕی بێگومان پەروەردگارم بەپیلانی ئەو (ژنانە) ئاگادارو زانایە');
INSERT INTO "bamoki" VALUES(12,51,'12:51','ووتی (بەژنەکان) مەبەستان چی بوو کاتێک ویستتان یوسف بخەڵەتێنن و کاری خراپی پێ بکەن (ئافرەتەکان) ووتیان پاک و بێگەردی بۆ خوا ئێمە ھیچ خراپەیەکمان لێ نەدیوە ژنەکەی عەزیزی (میێر) ووتی ئا ئێستا حەق و ڕاستی دەرکەوت من دەمویست (یوسف) دەستم لەگەڵ تێکەڵ بکات و بێگومان ئەو لەڕاست گۆیانە');
INSERT INTO "bamoki" VALUES(12,52,'12:52','(یوسف ووتی) ئەوە بۆ ئەوەی (پادشا) بزانی بەڕاستی من بەپەنھانی ناپاکی وخیانەتم نەکردووە لێی وە بەڕاستی خواش پیلانی ناپاکان ناگەیەنێتە ئەنجام');
INSERT INTO "bamoki" VALUES(12,53,'12:53','وە (یوسف ووتی) من ھەرگیز پاکانە بۆ خۆم ناکەم (چونکە) بەڕاستی نەفس زۆر فەرمان دەرە بەخراپە بێجگە ئەو نەفسە کە پەروەردگارم ڕەحمی پێ کردبێت بەڕاستی پەروەردگارم لێبوردە و میھرەبانە');
INSERT INTO "bamoki" VALUES(12,54,'12:54','جا پادشا ووتی (بەژێردەستەکانی) ئەو یوسفەم بۆ بھێنن دەیکەمە (کەسێکی) تایبەت بەخۆم جا کاتێ کە (پادشا) قسەی لەگەڵ کرد ووتی بەڕاستی تۆ ئەمڕۆ لای ئێمە خاوەنی پلەو پایە و باوەڕپێکراویت');
INSERT INTO "bamoki" VALUES(12,55,'12:55','(یوسف) فەرمووی (بەپادشا) لەسەر خەزنە و دارایی زەوی (میسر) دابمنێ بەڕاستی من پارێزەرێکی شارەزام');
INSERT INTO "bamoki" VALUES(12,56,'12:56','ئا بەو جۆرە یوسفمان پایەدارو جێگیر کرد لەخاکی (میسر) دا لەھەر شوێنێکی (میسردا) بیویستایە دەژیا ھەر کەسمان بوێت بەرمیھرەبانی خۆمانی دەخەین (لەدونیادا) و پاداشتی چاکە کاران وون ناکەین');
INSERT INTO "bamoki" VALUES(12,57,'12:57','وە بەڕاستی پاداشتی ڕۆژی دوایی چاکترە بۆ ئەو کەسانەی بڕوایان ھێناوە و بەھەمیشەیی خۆپارێز بوون (لە ھاوبەش بڕیاردان و تاوان)');
INSERT INTO "bamoki" VALUES(12,58,'12:58','وە (لە کاتی گرانیدا) براکانی یوسف ھاتن (بۆ میسر) ئەوسا چوونە لای (یوسف) دەست بەجێ (یوسف) ئەوانی ناسیەوە بەڵام ئەوان ئەمیان نەناسیەوە');
INSERT INTO "bamoki" VALUES(12,59,'12:59','جا کاتێک کە ڕێکی خستن وئەوەی کە ویستیان پێی دان ووتی برایەکی باوکیتان ھەیە بۆم بھێنن ئایا نابینن کەمن پێوانەم تێرو تەواوە (بۆ کڕیارەکان) وە من خانە خوێی چاکم');
INSERT INTO "bamoki" VALUES(12,60,'12:60','جا ئەگەر ئەو (برایەتانم) بۆ نەھێنن ئەوە پێوانەی (دانەوێڵە) تان لای من نیە و نزیک (ئەم ووڵاتەش) مەکەونەوە');
INSERT INTO "bamoki" VALUES(12,61,'12:61','ووتیان ھەوڵ و تەقەلا دەدەین لەگەڵ باوکیــدا و دڵنیابە ئێمە ئەو کارە دەکەین');
INSERT INTO "bamoki" VALUES(12,62,'12:62','وە (یوسف) ووتی بە بەردەست و خزمەتکارەکانی دراوەکانیان کە داویانە (بە دزیەوە) بیخەنە ناو بارەکانیان بەڵکو بیناسنەوە کاتێک کە گەڕانەوە بۆ ناو ماڵ و منداڵیان تا (ببێتە ھۆی ئەوەی) بگەڕێنەوە و بێنەوە (جارێکی تر)');
INSERT INTO "bamoki" VALUES(12,63,'12:63','ئەمجا کاتێک گەڕانەوە بۆ لای باوکیان ووتیان ئەی باوکی بەڕێزمان (بۆ ساڵی داھاتوو) پێوانەمان لێ قەدەغە کراوە (شتمان پێ نافرۆشن ئەگەر بنیامین نەنێریت لەگەڵماندا) جا کەواتە براکەمان لەگەڵدا بنێرە، پێوانەش دەکەین (دانەوێڵە دەکڕین) و دڵنیابە ئاگاداریشی دەکەین');
INSERT INTO "bamoki" VALUES(12,64,'12:64','(یەعقوب) ووتی (پێیان) ئایا دڵنیابم لێـتان دەربارەی ھەروەک پێشتر دڵنیا بووم لێتان دەرباری (یوسفی) برای کەواتە خوا چاکترین پارێزەرە وە ئەو بەزەیدارترینی ھەموو بەزەیدارانە');
INSERT INTO "bamoki" VALUES(12,65,'12:65','جا کاتێک بارەکانیان کردەوە بینیان دەستمایەکەیان بۆ گەڕێنراوەتەوە ووتیان ئەی باوکی بەرێزمان چیمان دەوێت ئەمە دراوەکەمانە بۆمان نێردراوەتەوە بژێویش بۆ خاو خێزانمان دەھێنین و براکەشمان دەپارێزین و باری ووشترێکیش (دانەوێڵە) زیاد دەھێنین ئەم (بارەش) پێوانەیەکی کەمە (و بە ئاسانی دەمان دەنێ)');
INSERT INTO "bamoki" VALUES(12,66,'12:66','(یەعقوب) ووتی ھەرگیز ناینێرم لەگەڵتان ھەتا بەڵێنێکی بتەوم پێ نەدەن لەلایەن خواوە (تا سوێندم بۆ نەخۆن بەخوا) کەبێگومان (بە بێوەی) ئەیھێننەوە بۆم مەگەر دەور بدرێن وچارتان نەمێنێت ئەمجا کاتێک پەیمانیان پێدا (سوێندیان خوارد) (یەعقوب) ووتی خوا ئاگادار و چاودێرە سەبارەت بەوەی کەدەیڵێین');
INSERT INTO "bamoki" VALUES(12,67,'12:67','وە ووتی ئەی کوڕەکانم ھەمووتان لە دەرگایەکەوە مەچنە ژوورەوە بەڵکو لەچەند دەرگایەکی جیاجیاوە بچنە ژوورەوە, وە من ھیچ سوودتان پێ ناگەیەنم لەبەرانبەر (پێشھاتی) خوادا ھەموو حوکم و بڕیارێک تەنھا بۆ خوایە پشت ھەر بەو دەبەستم و خۆسپێرانیش با خۆیان ھەر بەو بسپێرن');
INSERT INTO "bamoki" VALUES(12,68,'12:68','جا کاتێک چوونە ژوورەوە لەو شوێنەوەی کە باوکیان فەرمانی پێدابوون ئەو (چوونە بەو جۆرە) ھیچ سوودی پێ نەگەیاندن لە بەرامبەر (قەدەری) خوادا بەڵکو ئەوە مەبەستێک بوو لە دەروونی یەعقوبدا بوو (بەم شێوە) ئەنجامیدا وە بەڕاستی یەعقوب خاوەنی زانست و زانیاری بوو چونکە ئێمە فێرمان کرد بوو بەڵام زۆربەی خەڵکی نازانن');
INSERT INTO "bamoki" VALUES(12,69,'12:69','کاتێکیش کە چوونە ژوورەوە بۆ لای یوسف (یوسف) براکەی بردە پەنا خۆیەوە ووتی بەڕاستی من براکەی تۆم کەواتە ھیچ غەمبار مەبە بەوەی کە ئەوان دەیکەن (لەگەڵت) دا');
INSERT INTO "bamoki" VALUES(12,70,'12:70','جـا کـاتێک کـەڕێـکی خسـتن و ئەوەی ویستیـان پێی دان ئاوخۆری (پێوانەکەی) مەلیکی خستە ناو باری براکەی پاشان جاڕچیەک جاڕیدا ئەی کاروانەکە بەڕاسـتی ئێـوە دزن');
INSERT INTO "bamoki" VALUES(12,71,'12:71','ڕوویـان تێکـردن و ووتـیـان چـیتـان گـوم کـردوە؟');
INSERT INTO "bamoki" VALUES(12,72,'12:72','ووتیان ئاوخۆرەکەی (پێوانەکەی) پادشامان دیارنیە ھەر کەسێک بیھێنێتەوە باری ووشترێک (خۆراکی) دەدرێتێ وەمنیش دەستەبەری ئەو (بارە) دەبم');
INSERT INTO "bamoki" VALUES(12,73,'12:73','ووتیان سوێند بەخوا بەڕاستی ئێوە دەزانن کەئێمە نەھاتووین بۆ ئەوەی خراپە بکەین لەووڵاتدا وەئێمە پێشتر دز نەبووین');
INSERT INTO "bamoki" VALUES(12,74,'12:74','ووتیان دەی ئایا تۆڵەی ئەودزە چیە ئەگەر ئێوە درۆزن بوون');
INSERT INTO "bamoki" VALUES(12,75,'12:75','ووتیان تۆڵەی ئەوەیە لەباری ھەرکەسێکدا دۆزرایەوە ھەر ئەوکەسە خۆی تۆڵەی بێت (واتە: ئەوەی دزیەکەی کردووە ئەبێتە عەبد و کۆیلەی ئەوەی دزیەکەی لێکراوە) ئا بەو جۆرە تۆڵە لەستەمکاران دەسێنین');
INSERT INTO "bamoki" VALUES(12,76,'12:76','ئەوسا (یوسف) دەستی کرد بەبشکنینی جەواڵەکانیان (بارەکانیان) لەپێش بشکنینی جەواڵی (بنامین) برای (دایک و باوکی یوسف) لەپاشان (ئاو خۆر - پێوانەکەی) دەرھێنا لە جەواڵی براکەیدا ئا بەو جۆرە پیلانمان نیشانی یوسف دا ئەو نەیدەتوانی براکەی بگرێت بەپێی ئاینی و یاسایی پادشا (بەڵکو بەپێی ئەو قسە بوو براکانی خۆیان کردیان) مەگەر (کاتێک) کە خوا بیەوێت ھەر کەسێک بمانەوێت پلەی بەرز دەکەینەوە و لەسەر ھەموو خاوەن زانستێکەوە زاناتر ھەیە (مەبەست پێ خودایە)');
INSERT INTO "bamoki" VALUES(12,77,'12:77','ووتیان ئەگەر ئەو (بنیامین) دزیبێتی ئەوە بێگومان براکەشی پێشتر دزی کردووە (واتە یوسفیش دز بووە) بەڵام یوسف (پێناخۆشبوونی خۆی) بەم (تۆمەتە) لەدەرونی خۆیدا شاردەوە دەری نەبڕی بۆ ئەوان (تەنھا) ووتی ئێوە لەوان خراپترن خواش چاک دەزانێ بەوەی کەئێوە دەیڵێن');
INSERT INTO "bamoki" VALUES(12,78,'12:78','ووتیان ئەی عەزیز (ئەی پادشا) بەڕاستی ئەو باوکێکی زۆر پیری بەتەمەنی ھەیە جایەکێک لە ئێمە لە جیاتی ئەو بگرە بەڕاستی ئێمە تۆ لەچاکەکاران دەبینین');
INSERT INTO "bamoki" VALUES(12,79,'12:79','(یوسف) ووتی پەنا بەخوا لەوەی کە ھیچ کەسێ بگرین بێجگە لەوەی کە ئاو خۆر (پێوانە) کەمان لا دۆزیوەتەوە (کەدزرا بوو) بەڕاستی ئەگەر وا بکەین ئێمە لەستەم کاران دەبین');
INSERT INTO "bamoki" VALUES(12,80,'12:80','جا کاتێک بێ ھیوا بوون لەوەی (وەزیر بەرەڵای بکات) چوونە کەنارێک بەچپە ڕاوێژیان دەکرد برا گەورەکەیان ووتی ئەی نازانن کە بێگومان باوکتان سوێند و پەیمانێکی بەناوی خواوە لێ وەرگرتن پێشتریش (لەو بەڵێنەی پێتان دابوو) دەربارەی یوسف کەمتەرخەمیتان کرد کەواتە من ئەم زەویە بەجێ نایەڵم ھەتا باوکم مۆڵەتی(گـەڕانـەوەم) پێ نەدات یان خوا بڕیارم بۆ نەدات ھەر ئەویش باشترینی دادوەرانە');
INSERT INTO "bamoki" VALUES(12,81,'12:81','ئێوە بگەڕێنەوە بۆلای باوکتان وپێی بڵێن ئەی باوکی خۆمان بەڕاستی کوڕەکەت دزی کرد وئێمەش بێجگە لەوەی دەمانزانی شایەتیمان نەداوە خۆ ئێمە ئاگاداری شاراوە ونھێنی یەکان نەبووین');
INSERT INTO "bamoki" VALUES(12,82,'12:82','(خۆ ئەگەر بڕوا ناکەیت) لەخەڵکی ئەو شارەش بپرسە کەئێمەی لێ بووین ولەو کاروانەش (بپرسە) لەگەڵیدا گەڕاینەوە, وەبەڕاستی ئێمە ڕاست گۆین');
INSERT INTO "bamoki" VALUES(12,83,'12:83','(یەعقوب علیە السلام) ووتی وانیە بەڵکو نەفسی خۆتان کارێکی بۆ جوان (و ئاسان) کردون بۆیە چارم دانبەخۆدا گرتنە بەجوانی بەو ئومێدەی خوا ھەموویان ( یوسف و بنیامینیش) م بۆ بھێنێتەوە بەڕاستی ھەر ئەو زانای کاردروستە');
INSERT INTO "bamoki" VALUES(12,84,'12:84','ئەوسا ڕوی لێ وەرگێڕان ووتی ئاخ وداخ بۆ یوسف وە ھەردوو چاوی سپی (و کوێر) بوون لەداخ وخەفەتدا چونکە ھەر داخ و خەفەتی خۆی دەخواردەوە');
INSERT INTO "bamoki" VALUES(12,85,'12:85','ووتیان سوێند بەخوا وازناھێنی ھەربیری یوسف دەکەیتەوە ھەتا پەکت دەکەوێت بەنەخۆشی وخەفەت خواردن یان دەمریت و دەچیتە ڕیزی لە ناوچوان');
INSERT INTO "bamoki" VALUES(12,86,'12:86','ووتی من سکاڵا و دەردی دڵ و خەفەت و پەژارەم ھەر لای خوادەکەم وە من لەلایەن خواوە شتانێک دەزانم کە ئێوە نایزانن');
INSERT INTO "bamoki" VALUES(12,87,'12:87','(یەعقوب ووتی) ئەی کوڕەکانم بڕۆن جا بگەڕێن و ھەواڵی یوسف و براکەی بزانن نائومێدیش مەبن لەبەزەیی و میھرەبانی خوا بەڕاستی نائومێد نابێت لەڕەحم و میھرەبانی خوا جگە لەوانەی کەبێ بڕوان');
INSERT INTO "bamoki" VALUES(12,88,'12:88','جا کاتێک کە چوونە لای ئەو (یوسف) ووتیان ئەی عەزیز (ئەی پادشا) زیان و برسێتی تووشی خۆمان و ماڵ و منداڵمان بووە و دراوێکی کەممان ھێناوە (بۆ کڕینی خۆراک) تۆش پێوانەکەمان بە تەواوی بدەرێ خێریشمان پێ بکە بەڕاستی خواپاداشتی خێرەومەندان دەداتەوە');
INSERT INTO "bamoki" VALUES(12,89,'12:89','ووتی ئێوە دەزانن چیتان کرد بەیوسف و براکەی لەوکاتەدا کەئێوە نەزان بوون');
INSERT INTO "bamoki" VALUES(12,90,'12:90','ووتیان بەڕاست تۆ یوسفیت؟ ووتی (پێیان) من یوسفم و ئەمەش برامە بەڕاستی خوا منەتی نابەسەرماندا بەڕاستی ھەرکەس خۆی بپارێزێت ولەخوا بترسێت ودان بەخۆیدا بگرێت ئەوسا بەڕاستی خوا پاداشتی چاکەکاران وون ناکات');
INSERT INTO "bamoki" VALUES(12,91,'12:91','ووتیان (بەیوسف) سوێند بەخوا بەڕاستی خوا تۆی ھەڵ بژارد بەسەر ئێمەدا وە بەڕاستی ئێمە لە خەتاکاران بووین');
INSERT INTO "bamoki" VALUES(12,92,'12:92','(یوسف) ووتی ئەمڕۆ ھیچ سەرزەنشت و تەشەرێکتان لەسەر نیە (لەلایەن منەوە) خوا لێتان خۆش بێت وە ئەو (خوا) لەھەموو میھرەبانان میھرەبانترە');
INSERT INTO "bamoki" VALUES(12,93,'12:93','ئەم کراسەی من ببەن و بیدەن بەسەر دەم و چاوی باوکمدا (دەست بەجێ) چاوی چاک دەبێتەوە ئەوسا ھەموو کەس و کارتان بھێنن بۆ (ئەم وڵاتەی) من');
INSERT INTO "bamoki" VALUES(12,94,'12:94','جا کاتێک کاروانەکە (لەمیسر) دەرچوو باوکیان ووتی بەڕاستی من بۆنی یوسف ھەست پێدەکەم ئەگەر بە خەڵەفاو و بێ عەقڵم دانەنێن');
INSERT INTO "bamoki" VALUES(12,95,'12:95','ووتیان سوێند بەخوا تۆ ھەر لەسەر ھەڵەکەی کۆنی خۆتی');
INSERT INTO "bamoki" VALUES(12,96,'12:96','جا کاتێک مژدە دەرەکە ھات ئەو (کراسەی) دابەسەر دەم و چاویدا یەکسەر بینایی گەڕایەوە (کەپێشتر نابینا بوو بوو) (یەعقوب) ووتی من بەئێوەم نەووت؟ دڵنیابن من لەلایەن خواوە شتانێک دەزانم ئێوە نایزانن');
INSERT INTO "bamoki" VALUES(12,97,'12:97','ووتیان ئەی بابە داوای لێخۆشبوونی گوناھەکانمان بۆ بکە (لای پەروەردگار) بەڕاستی ئێمە گوناھباربووین');
INSERT INTO "bamoki" VALUES(12,98,'12:98','(یەعقوب) ووتی پێیان دواتر داوای لێبوردنتان بۆ دەکەم لەپەروەردگارم بەڕاستی ھەر ئەو لێبوردە و میھرەبانە');
INSERT INTO "bamoki" VALUES(12,99,'12:99','جا کاتێک چوونە لای یوسف (لە وڵاتی میسر) دایک و باوکی بردە نزیکی خۆی (و دەستی کردە ملیان) و فەرمووی پێیان وەرنە میسر ئەگەر خوا بیەوێت ئارام دەبن (ھیچ زیانێکتان تووش نایەت)');
INSERT INTO "bamoki" VALUES(12,100,'12:100','وە باوک و دایکی لەسەر تەختی (حوکمڕانی خۆی) دانا وە ھەموو کەوتن بەڕوودا بۆی بەسوژدە بردنەوە (یوسف) ووتی ئەی باوکی خۆشەویستم ئەمە لێکدانەوەی خەوەکەم بوو کەلەمەو پێش (بینیم) بێگومان پەروەردگارم بەڕاستی گێڕا وە بەڕاستی خوا چاکەی لەگەڵ کردم کاتێک لە زیندان دەری ھێنام وە ئێوەشی لە لادێ وبیابانەوە ھێنا (بۆ ناوشار) دوای ئەوەی شەیتان ناخۆشی و ناکۆکی خستە نێوان من و براکانم بەڕاستی پەروەردگارم میھرەبانە بەرامبەر ئەوەی بیەوێت بەڕاستی ھەر ئەو زانا و کاردروستە');
INSERT INTO "bamoki" VALUES(12,101,'12:101','ئەی پەروەردگارم بەڕاستی پێت بەخشیووم ھەندێک لەدەسەڵات و پاشایەتی وە ھەندێکیش لە لێکدانەوەی خەونت فێرکردوم ئەی دروستکەری ئاسمانەکان وزەوی تۆ پشتیوان و یارمەتیدەری منی لەدونیا و لەڕۆژی دواییدا بە موسوڵمانێتی بممرێنە و بمخەرە ڕیزی پیاو چاکانەوە (لەدوا ڕۆژدا)');
INSERT INTO "bamoki" VALUES(12,102,'12:102','ئەوەی (کە باس کرا) لە ھەواڵە نەزانراو و پەنھانەکانە کە بە وەحی بۆت دەنێرین (ئەی موحەممەد صلى الله عليه وسلم) تۆ لای ئەوان (براکانی یوسف) نەبوویت کاتێک کەھەموو بڕیاری خۆیان دا (بەنھێنی) و پیلانیان داڕشت');
INSERT INTO "bamoki" VALUES(12,103,'12:103','وەزۆربەی خەڵکی ھەرچەند تۆ سووربیت (لەسەر باوەڕ ھێنانیان) باوەڕ ناھێنن');
INSERT INTO "bamoki" VALUES(12,104,'12:104','(ئەی موحەممەدصلى الله عليه وسلم) تۆ داوای ھیچ پاداشتێک لەوانە ناکەیت لەسەر (گەیاندنی) ئەو (قورئانە) ئەو قورئانە ھەر پەند و ئامۆژگاریە بۆ جیھانیان');
INSERT INTO "bamoki" VALUES(12,105,'12:105','وە زۆر بەڵگەو نیشانە ھەیە لەئاسمانەکان و زەویدا بە لایاندا دەڕۆن بەڵام ئەوان ڕوویان لێوەردەگێڕن');
INSERT INTO "bamoki" VALUES(12,106,'12:106','جا زۆربەیان باوەڕناھێنن بەخوا مەگەر ئەوان لەوکاتەدا ھاوبەش بڕیاردەرن بۆ خوا');
INSERT INTO "bamoki" VALUES(12,107,'12:107','جا ئایا ئەوانە ناترسن یەخەیان بگرێت سزایەکی داپۆشەری خوا یان لەناکاودا ڕۆژی دواییان بەسەردا بێت لە کاتێکدا ئەوان ھەستی پێ نەکەن');
INSERT INTO "bamoki" VALUES(12,108,'12:108','بڵێ ئەمە ڕێگای منە بانگی (خەڵکی) دەکەم بۆلای خوا لەسەر بەڵگەی ڕوون و چاوڕۆشنی من و ئەوانەش شوێنم کەوتوون وخواش پاک و بێگەردە وە من ھیچ کات لە ھاوبەشدانەران نیم');
INSERT INTO "bamoki" VALUES(12,109,'12:109','وە ئێمە پێش تۆ (پێغەمبەرمان) نەناردووە جگە لەپیاوانێک کەنیگامان بۆ دەکردن لەخەڵکی شار وگوندەکان دەی ئایا ئەو (بێ بڕوایانە) نەگەڕاون بەسەر زەویدا تا سەرنج بدەن چۆن بووە سەرئەنجامی ئەوانەی کە لەپێش ئەمانەوە بوون وە بێگومان ژیانی ڕۆژی دوایی چاکترە بۆ ئەوانەی خۆیان پاراستووە و لەخواترس بوون (لەدونیادا) دەی ئێوە ژیرنابن و تێ ناگەن');
INSERT INTO "bamoki" VALUES(12,110,'12:110','ھەتا کاتێک پێغەمبەرەکانیان نا ئومێد بوون (لەسەرکەوتن) وە گەلەکەیان وایانزانی کە (پێغەمبەران) درۆیان لەگەڵدا کردون (لەو کاتەدا) سەرکەوتن ویارمەتی ئێمەیان بۆ ھات جا ئەوانەی دەمانویست ڕزگارکران سزاشمان ناگەڕێنرێتەوە لە گەل و ھۆزی تاوانبار');
INSERT INTO "bamoki" VALUES(12,111,'12:111','سوێندبێت بێگومان لە داستانی (پێغەمبەراندا علیھم السلام) پەندو ئامۆژگاری ھەیە بۆ خاوەن بیر وھۆشان ئەم (قورئانە) چیرۆک و قسەیەک نیە ھەڵبەسترابێت بەڵکو بەڕاستدانەر و پشتگیری ئەو (کتێبانە) یە کە لەپێش خۆیەوە (ھاتوون پێش دەستکاری کردنیان لەلایەن جولەکە وگاورەکانەوە) ھەروەھا ڕوونکەرەوەی ھەموو شتێکە وە ڕێنمونی و میھرەبانیە بۆ ئەوانەی کەبڕوا دەھێنن');
INSERT INTO "bamoki" VALUES(13,1,'13:1','بە (ئەلیف، لام، ڕا) دەخوێنرێتەوە خوا زاناترە بە ماناکەی، بۆزانیاری زیاتر سەیری سەرەتای سورەتی (البقرة) بکە ئەمانە ئایەتەکانی کتێبی (ئاسمانین) وە ئەوەی نێرراوەتە خوارەوە بۆتۆ لەلایەن پەروەردگارتەوە ھەق و ڕاستە بەڵام زۆربەی خەڵکی باوەڕ ناھێنن');
INSERT INTO "bamoki" VALUES(13,2,'13:2','ئەو خوایەی ئاسمانەکانی بەرزکردۆتەوە بەبێ ھیچ پایە و ستونێک وەک دەیبینن پاشان بەرز بوەوە بۆ سەر عەرش و قەراری گرت (بەو شێوەی شایستەی خوا بێت کەئێمە چونێتیەکەی نازانین) وە خۆر و مانگی ڕام ھێناوە (بۆ خزمەتکردنی خەڵکی) ھەر یەکەیان دەگەڕێت تاکاتی دیاریکراو ھەموو کاروبارێک ھەڵدەسوڕێنێ و ئایەتەکان ڕوون دەکاتەوە (بۆتان) ھەتا لە (زیندوو بوونەوە و) گەیشتن بەخزمەت پەروەردگارتان دڵنیابن');
INSERT INTO "bamoki" VALUES(13,3,'13:3','(خوا) ئەو زاتەیە کە زەوی ڕاخستووە (بەپانی و درێژی) و چەندھا چیا و ڕووباری تێدا داناوە (دروستکردووە) وەلە ھەموو جۆرە میوەیەک جوتێک نێر و مێی تێدا دروستکردووە شەو دەدات بەسەر ڕۆژدا بەڕاستی لەوانەدا بەڵگە و نیشانە ھەیە بۆ ئەو کەسانەی کەبیردەکەنەوە');
INSERT INTO "bamoki" VALUES(13,4,'13:4','وە لەزەویدا چەند پارچەی لە پاڵ یەکتریدا ھەن ھەروەھا باخاتی ترێ و ڕەز و کشتوکاڵ و(باخاتی) دارخورمای دوو قەدی (یەک بنج) و یەک قەد ھەموویان بەیەک ئاو ئاودەدرێن کەچی فەزڵی ھەندێکیمان داوە بەسەر ھەندێکی تریاندا لەخواردندا (بەجۆرێک تامی ھەریەکەیان جیاوازە لەوی دیکە) بەڕاستی لەوانەدا چەند نیشانەی ڕوون ھەیە بۆ ئەوانەی تێدەگەن و بیردەکەنەوە');
INSERT INTO "bamoki" VALUES(13,5,'13:5','جا ئەگەر تۆ سەرت سوڕ بمێنێ ئەوە قسەی ئەوانە جێی سەر سوڕمانە کە دەڵێن ئایا کاتێک (کەمردین) وبوینەوە بەخاک ئایا ئێمە سەر لە نوێ دروست ئەکرێینەوە ئەوانە کەسانێکن کە بێ باوەڕ بوون بە پەروەردگاریان جا ئەوانە کۆت و زنجیر لە گەردنیاندایە وە ئەوانە ھاوەڵانی ئاگرن کە بە ھەمیشەیی دەمێننەوە تێیدا');
INSERT INTO "bamoki" VALUES(13,6,'13:6','بەپەلە داوای سزات لێدەکەن پێش چاکە و بەزەیی کە بێگومان لەپێش ئەمانەوە زۆر ھەبوون تۆڵەیان لێ سەنراوە و بەڕاستی پەروەردگارت خاوەنی لێبوردنە بۆ خەڵکی گەرچی ستەمکاریش بن ھەروەھا بەڕاستی پەروەردگارت تۆڵەسێنێکی توند وتیژە');
INSERT INTO "bamoki" VALUES(13,7,'13:7','وە ئەوانەی کە بێ باوەڕ بوون دەیانووت ئەوە بۆ نەنێردراوەتە خوارەوە بۆی موعجیزەیەک لەلایەن پەروەردگاریەوە ئەی موحەممەد صلى الله عليه وسلم تۆ ھەر ترسێنەری و بۆ ھەموو گەلێک ڕێنیشاندەرێک ھەیە');
INSERT INTO "bamoki" VALUES(13,8,'13:8','خوا دەزانێ ھەموو مێینەیەک (لە مناڵدانیدا) چی ھەڵگرتووە (کوڕ یان کچ) وە ھەروەھا دەزانێت منداڵدانەکان چی لێ کەمدەبێـتەوە و چی زیاد دەکات وە ھەموو شتێک لای ئەو (خوا) ئەندازەی (دیاریکراوی خۆی) ھەیە');
INSERT INTO "bamoki" VALUES(13,9,'13:9','(خوا) زانایە بەھەموو دیار و نادیارێک گەورە و بەرزە وبڵندە');
INSERT INTO "bamoki" VALUES(13,10,'13:10','یەکسانە (بۆ خوا) ئەوەتان بەنھێنی قسە بکات وە ئەوەتان بەئاشکرا (قسە) بکات و ئەوەی کە لە شەودا خۆی دەشارێتەوە ئەوەی کەلە ڕۆژدا دەردەکەوێت');
INSERT INTO "bamoki" VALUES(13,11,'13:11','بۆ ئەو (ئادەمی) ھەیە چەند فریشتەیەک کە بەدوای یەکدا دێن لەپێش و لەدوایەوە بەفەرمانی خوا دەیپارێزن لەزیان بەڕاستی خوا (بار و حاڵی) ھیچ گەلێک ناگۆڕێت ھەتا ئەوان خۆیان (بارو حاڵی) خۆیان نەگۆڕن وەکاتێک خوا بیەوێت گەل وھۆزێک تووشی بەڵا وسزا بکات ھیچ شتێک ناتوانێت ڕێگری لێ بکات وجگە لەو (خوا) ھیچ یارمەتیدەر و پشتیوانێکیان نیە');
INSERT INTO "bamoki" VALUES(13,12,'13:12','(خوا) ئەو زاتەیە بروسکەتان نیشان دەدات کەھۆی ترساندن و ھیوا وئومێدتانە ھەروەھا ھەوری قورس (بەئاوی باران) بەدی دەھێنێت');
INSERT INTO "bamoki" VALUES(13,13,'13:13','وە ھەورەگرمە سوپاس و ستایشی ئەو (خوای گەورەیە) دەکات ھەروەھا فریشتەکانیش لە ھەیبەت و ترسی ئەو (ستایشی دەکەن) وە (پەروەردگار) ھەورە بروسکەکان دەنێرێت و دەیدات لە ھەرکەسێک کەبیەوێت لە کاتێکدا ئەوانە دەمە دەمێ (وموجادەلە) دەکەن دەربارەی خوا و ئەو (خوای گەورە) ھێز و توانای زۆر بەتینە');
INSERT INTO "bamoki" VALUES(13,14,'13:14','نزاو پاڕانەوەی ھەقیش ھەر بۆ (خوایە) ئەوانەش دەلاڵێنەوە و نزا لای غەیری خوا دەکەن ھیچ وەڵامێکیان نادەنەوە (نایەن بەھاواریانەوە) مەگەر وەک کەسێک (کەتینووی بێت لەدوورەوە) ھەردوو مشتی خۆی بۆلای ئاوێک درێژ کردبێ بۆ ئەوەی (ئاو) بگاتە دەمی بەڵام نایگاتێ نزاو دوعای بێ بڕوایان (لەبتەکانیان) ھیچ نیە جگە لە گومڕایی و سەر لێشێوان');
INSERT INTO "bamoki" VALUES(13,15,'13:15','سوژدە بۆ خوا دەبات ھەرچی لەئاسمانەکان و زەویدایە بیانەوێ و نەیانەوێ ھەروەھا سێبەرەکانیان لەبەیانیان و ئێواراندا (سوژدە دەبەن)');
INSERT INTO "bamoki" VALUES(13,16,'13:16','(ئەی موحەممەد صلى الله عليه وسلم) بڵێ کێ پەروەردگاری ئاسمانەکان و زەویە بڵێ خوا جا بڵێ دەی ئێوە بێجگە لەو (خوای گەورە) پشتیوانانێکتان ھەڵبژاردووە (بۆ خۆتان) کەخاوەنی ھیچ سوود و زیانێک نین بۆ خۆشیان بڵێ ئایا کوێر و چاو ساغ یەکسانن؟ یان ئایا تاریکیەکان و ڕووناکی وەک یەک وان؟ نەخێر ئەوانە چەند ھاوبەشێکیان بۆ خوا داناوە؟ (گوایە) وەک دروستکراوانی خوایان دروست کردبێت؟ ئەوسا دروست کراوەکەیان لێ شێواوە!! بڵێ خوا دروستکەری ھەموو شتێکە و ھەر ئەویشە تاک و تەنھا وزاڵە بەسەر ھەموو شتێکدا');
INSERT INTO "bamoki" VALUES(13,17,'13:17','بارانی لە ئاسمانەوە باراندووە جا ھەر شیو و دۆڵێک بە ئەندازەی خۆی لافاوی ھەڵدەسێت ئەوسا لافاوەکە ھەڵی دەگرێت کەفێکی بەرزبووە (بە لافاوەکەوە) ھەروەھا لەو (کانزایانەش پەیدا دەبێت) کە ئاگری بۆ تاودەدەن (دەیتوێننەوە) بەمەبەستی دروست کردنی خشڵ (زێڕ وزیوو) یان شتوومەکی تر (وەک قاپ و قاچاغ) کەفێک وەکو ئەو (کەفی لافاوە پەیدا دەبێت) ئا بەو جۆرە خوا نموونەی حەق و بەتاڵ دەھێنێـتەوە جا کەفەکە دەڕوات و لە ناو دەچێت بەڵام ئەوەی کە سوود ئەگەیەنێت بەخەڵکی لەزەویدا دەمێنێتەوە ئابەو جۆرە خوا نموونەکان دەھێنێتەوە');
INSERT INTO "bamoki" VALUES(13,18,'13:18','بۆ ئەوانەی وەڵامی بانگەوازی پەروەردگاریان دایەوە (پاداشتی) ھەرە چاک (بەھەشت) ھەیە ئەوانەش وەڵامی بانگەوازی (پەروەردگار) یان نەدایەوە ئەگەر ھەرچی ھەیە لە زەویدا ھەمووی ھی ئەوان بێت ئەوەندەی تریشی لەگەڵدا بێت دەیدەن لە بریتی ئەوەی لە سزای ڕۆژی دوایی (رزگاریان ببێت) ئەوانە بۆیان ھەیە لێپرسینەوەیەکی سەخت وخراپ و جێگایان دۆزەخە ئای کەچەند شوێنێکی بەد و خراپە');
INSERT INTO "bamoki" VALUES(13,19,'13:19','جا ئایا کەسێک کە بزانێت ( ژیری بخاتە کار) بەڕاستی ئەوەی نێرراوەتە خوار بۆ تۆ لەلایەن پەروەردگارتەوە ھەق وڕاستە وەک ئەو کەسەیە کە کوێربێت (ژیری خۆی نەخاتە کار و بێ باوەر ِبێت) بێگومان ھەر ئەوانە بیر دەکەنەوە کە خاوەنی ژیرین');
INSERT INTO "bamoki" VALUES(13,20,'13:20','ئەوانەی کە پەیمانی خوا دەبەنە سەر وە بەڵێن ناشکێنن و ھەڵی ناوەشێننەوە');
INSERT INTO "bamoki" VALUES(13,21,'13:21','وە ئەوانەی کە پەیوەندی خزمایەتی بەجێ دەھێن بەوەی خوا فەرمانی داوە کەپەیوەست بکرێت لەگەڵیاندا (وەک بەسەرکردنەوەی خزمان) وە لە پەروەردگاریان دەترسن وە لەخراپە و سەختی لێپرسینەوە دەترسن');
INSERT INTO "bamoki" VALUES(13,22,'13:22','وە ئەوانەن کە دانیان بەخۆیاندا گرت بەمەبەستی ڕەزامەندی پەروەردگاریان وە نوێژیان بەجێھێناوە, وە لەو ڕۆزیەی پێمان داوون بەخشیوویانە بەنھێنی و بە ئاشکرا وە بەچاکە ڕێگری لەخراپە دەکەن (بەچاکە خراپەکانیان دەسڕنەوە و لای دەبەن) سەر ئەنجامی چاکی ئەو دونیا ھەر بۆ ئەوانە');
INSERT INTO "bamoki" VALUES(13,23,'13:23','بەھەشتانێک جێگای مانەوەی ھەمیشەییە کە ئەوان دەچنە ناویەوە ھەروەھا ئەوانەش کە چاک بوون لەباو و باپیرانیان وھاوسەرانیان و وەچە و نەوەکانیان فریشتەکانیش دەچنە ژورەوە بۆ لایان لە ھەموو دەرگایەکەوە');
INSERT INTO "bamoki" VALUES(13,24,'13:24','(پێیان دەڵێن) سەلامتان لێ بێت چونکە ئارامتان گرت (لە دونیادا) جا چەند سەر ئەنجامێکی چاکتان بوو');
INSERT INTO "bamoki" VALUES(13,25,'13:25','بەڵام ئەوانەی کە پەیمانی خوا ھەڵ دەوەشێننەوە لەپاش بەستن و گرێدانی ئەو پەیوەندی خزمایەتیەی کە خوا فەرمانی پێداوە بەجێی ناھێنن و ئەیبچڕێنن و خراپەکاری دەکەن لەزەویدا ھەر بۆ ئەوانە نەفرەت و دووریی لەبەزەیی خوا ھەروەھا بۆ ئەوانە سەرئەنجامی خراپ');
INSERT INTO "bamoki" VALUES(13,26,'13:26','خوا ڕزق و ڕۆزی فراوان دەکات بۆ ھەرکەسێک خۆی بیەوێت وەکەمی دەکات (بۆ ھەرکەسێک بیەوێت) وە ئەوانە دڵخۆش بوون بەژیانی دونیا لەکاتێکدا ژیانی دونیا ھیچ نیە لەچاو ڕۆژی دواییدا جگە لە ڕابواردنێکی چەند ساتێکی کەم نەبێت');
INSERT INTO "bamoki" VALUES(13,27,'13:27','ئەوانە کەبێ بڕوا بوون دەڵێن بۆچی نەنێررا بۆی موعجیزەیەک لەلایەن پەروەردگاریەوە (ئەی موحەممەد صلى الله عليه وسلم) بڵێ بەڕاستی خوا ھەر کەسێکی بوێت گومڕای دەکات (کەشیاوی گومڕاکردنە) وە ڕێنمونی ھەر کەسێک دەکات کە بۆلای خۆی گەڕابێتەوە');
INSERT INTO "bamoki" VALUES(13,28,'13:28','ئەوانەی کەبڕوایان ھێناوە ودڵیان ئارام دەبێت بەیادی خوا ئاگاداربن کەھەر بە یادی خوا دڵەکان ئارام دەگرن');
INSERT INTO "bamoki" VALUES(13,29,'13:29','ئەوانەی بڕوایان ھێناوە و کردەوە چاکەکانیان کردووە خۆش بەختی بۆ ئەوانە لەگەڵ سەرەنجامی چاک');
INSERT INTO "bamoki" VALUES(13,30,'13:30','ھەر بە شێوەی (پێغەمبەرانی تر) تۆمان ڕەوانە کردوە لەناو گەل و ئوممەتێکدا کە لەپێش ئەوانەوە گەل و ئوممەتی تر ھاتوون و چوون بۆ ئەوەی بیخوێنیتەوە بەسەریاندا ئەوەی نیگامان کردووە بۆت لەکاتێکدا ئەوانە بڕواناھێنن بەخوای میھرەبان بڵێ ئەو پەروەردگاری منە ھیچ پەرستراوێکی بەھەق نیە بێجگە لە ئەو پشتم ھەر بەو بەستووە گەڕانەوەشم ھەر بۆلای ئەوە');
INSERT INTO "bamoki" VALUES(13,31,'13:31','بەڕاستی ئەگەر قورئانێک (بەخوێندنەوەی) چیاکانی پێ ببرایە (بۆ شوێنێکی تر) یان زەوی پێ لەت لەت بکرایە یان لەگەڵ مردوواندا قسەی بکردایە (ئەمجا باوەڕ ناھێنن) بەڵام ھەموو کاروبارەکان بەدەست خوان دەی ئایا بێ ھیوا نەبوون و (نەیانزانیوە) ئەوانەی کەبڕوایان ھێناوە کەئەگەر خوا بیویستایە ھەموو خەڵکی دەخستە سەر ڕێگای ڕاست وە ئەوانەی کەبێ باوەڕ بوون ھەمیشە بەڵای سەختیان تووش دەبێت بەھۆی ئەوەی کردوویانە یان ئەو (بەڵایە) لە نزیک ماڵەکانیانەوە ڕوودەدات ھەتا بەڵێنی خوا دێتە دی بەڕاستی خوا بەڵێنی خۆی بەدی دەھێنێت');
INSERT INTO "bamoki" VALUES(13,32,'13:32','سوێند بەخوا بێگومان گاڵتە وسووکایەتی کرا بە پێغەمبەرانی زۆر لەپێش تۆوە منیش (ماوەیەک) مۆڵەتم دا بەوانەی بێ باوەر بوون پاشان ئەوانم گرت (تۆڵەم لێ سەندنەوە) جا چۆن بوو تۆڵەی من (دەربارەیان)');
INSERT INTO "bamoki" VALUES(13,33,'13:33','دەی ئەو (خوایە) ی چاودێرە بەسەر ھەموو کەسەوە ھەرچی کردووە (ئایا وەک بتانێکە ئاگایان لە ھیچ نیە) ئەوانە ھاوبەشانێکیان بۆ خوا بڕیار داوە (ئەی موحەممەد صلى الله عليه وسلم) بڵێ ناوی ئەو (بت و ھاوبەشانەم) بۆ بڵێن یان ئێوە ھەواڵ دەدەن بەخوا بەوەی کە (بەخەیاڵی ئێوە) نایزانێت لەزەویدا یان قسەی ڕوت و (بێ مانا) دەکەن (نەخێر خوا ھاوبەشی نیە) بەڵکو ئەوانەی بێ باوەڕ بوون فێڵ و پیلانی خۆیان بۆ ڕازێنرایەوە وە قەدەغە کران لەڕێگەی ڕاست ھەر کەسیش خوا گومڕای بکات ئەوە ھیچ ڕێ نیشاندەرێکی بۆ نیە');
INSERT INTO "bamoki" VALUES(13,34,'13:34','ئەوانە لە ژیانی دونیادا سزایان بۆ ھەیە وە بەڕاستی سزای ڕۆژی دوایی سەخترو گرانترە وە بێگومان ھیچ پارێزەرێک نیە بۆ ئەوان لە (سزای) خوا');
INSERT INTO "bamoki" VALUES(13,35,'13:35','وێنەی ئەو بەھەشتەی کەبەڵێن دراوە بە پارێزکاران و لەخواترسان ڕوبارەکان دەڕۆن بەژێر باخەکانیدا میوە وسێبەرەکانی (بەھەشت) ھەمیشەیین ئەو (بەھەشتە) ئاکام و (لانەی) ئەوانەیە کە خۆیان پاراست (لە دونیادا) و لە خواترسان و ئاکام و (لانەی) بێ باوەڕانیش ھەر ئاگرە');
INSERT INTO "bamoki" VALUES(13,36,'13:36','ئەوانەش کتێبمان پێدابوون شاد دەبن بەوەی نێرراوەتە خوارەوە بۆت و ھەندێک لە گرۆ و تاقمەکان ھەن ھەندێک لەوە ئینکار دەکەن بڵێ من تەنھا فەرمانم بەوە پێدراوە کە خوا بپەرستم و ھیچ ھاوبەشێکی بۆ دانەنێم ھەر بۆلای خوا (خەڵکی) بانگ ئەکەم وگەڕانەوەشم تەنھا بۆلای ئەو (خوایەیە)');
INSERT INTO "bamoki" VALUES(13,37,'13:37','بەو شێوە ئەو (قورئانە) مان ناردۆتە خوارەوە (بۆ تۆ) کە فەرمانێکی عەرەبیە سوێند بەخوا بەڕاستی ئەگەر تۆ شوێنی ئارەزوەکانی ئەوانە بکەویت لەدوای ئەو وەحی و شارەزاییەی کە ھاتووە بۆت ھیچ پارێزەرێکت نابێت لە (سزای) خوا');
INSERT INTO "bamoki" VALUES(13,38,'13:38','سوێند بەخوا بێگومان پێش تۆ پێغەمبەرانێکمان ناردووە و پێماندان ھاوسەر و نەوە و وەچە وە بۆ ھیچ پێغەمبەرێک نەبووە (پێش تۆ) نیشانەیەک (موعجیزەیەک) بھێنێت مەگەر بەئیزنی خوا نەبێت بۆ ھەموو کارێک وەختێکی تایبەتی ھەیە کە نووسراوە');
INSERT INTO "bamoki" VALUES(13,39,'13:39','ئەوەی خوا بیەوێت دەیسڕێتەوە و (ئەوەش بیەوێت) دەیھێڵێتەوە سەرچاوەی کتێبەکان لای ئەو (خوای گەورەیە) لە (لوح المحفوظ)');
INSERT INTO "bamoki" VALUES(13,40,'13:40','جا ئەگەر نیشانت دەین ھەندێک لەو ھەڕەشانەی لێمان کردون یان (پێش ئەوە) بتمرێنین بێگومان تۆ ھەر ڕاگەیاندنت لەسەرە و لێپرسینەوەش لەسەر ئێمەیە');
INSERT INTO "bamoki" VALUES(13,41,'13:41','ئایا (بێ باوەڕان) سەرنجیان نەداوە کەئێمە زەوی کەم دەکەینەوە لە ھەموو لایەکانیەوە و خوا (بەویستی خۆی) بڕیار دەدات کەس ناتوانێت بڕیاری ئەو (خوا) بگۆڕێَت (یان ھەڵیوەشێنێتەوە) و ئەو (خوا) لێپرسینەوەی خێرایە');
INSERT INTO "bamoki" VALUES(13,42,'13:42','وە بێگومان ئەوانەی پێش ئەمانیش فێڵ و پیلانیان گێڕا بەڵام ھەموو پیلان و (نەخشەیەکی سەرکەوتوو) بۆ خوایە (لەبەرامبەر نەخشە وپیلانی ئەواندا) (خوا) دەزانێت ھەموو کەس چی دەکات و لەمەو دواش بێ باوەڕان دەزانن سەرئەنجامی (ھەر دوو جیھان) بۆ کێ دەبێت');
INSERT INTO "bamoki" VALUES(13,43,'13:43','وە ئەوانەی بێ باوەڕ بوون پێت دەڵێن تۆ ڕەوانەکراو (وپێغەمبەری خوا) نیت تۆش (ئەی موحەممەد صلى الله عليه وسلم پێیان) بڵێ خوا بەسە شایەت بێت لەنێوان من و ئێوەدا ھەروەھا ئەو کەسەش کەشارەزایی کتێبی (ئاسمانی) لایە');
INSERT INTO "bamoki" VALUES(14,1,'14:1','بە (ئەلیف لام ڕا) دەخوێنرێتەوە خوا زاناترە بە ماناکەی، بۆزانیاری زیاتر سەیری سەرەتای سورەتی (البقرة) بکە ئەمە پەڕاوێکە ناردومانەتە خوارەوە بۆ تۆ (ئەی موحەممەد صلى الله عليه وسلم) بۆ ئەوەی خەڵکی ڕزگار بکەیت لەتاریکی یەکانەوە بۆ ڕووناکی (لەتاریکی بێ باوەڕیەوە بۆ ڕووناکی باوەڕ وئیمان) بەئیزنی پەروەردگاریان (رێنمونیان بکەیت) بۆ ڕێگای (خوای) بەدەسەڵات وزاڵ و سوپاس کراو');
INSERT INTO "bamoki" VALUES(14,2,'14:2','ئەو خوایەی کە ھەرچی لەئاسمانەکان و زەویدایە ھی ئەوە و ھاوار و تیاچوون بۆ بێ باوەڕان لە سزایەکی سەخت');
INSERT INTO "bamoki" VALUES(14,3,'14:3','ئەوانەی کە ژیانی دونیایان لا خۆشترە لە ژیانی ڕۆژی دوایی وقەدەغەی (خەڵکی) دەکەن لەڕێگای خوا دەیانەوێت ئەو (ڕێگای خوا) لارو چەوت نیشان دەن ئەوانە لە گومڕاییەکی دوور (لە ڕاستی) دان');
INSERT INTO "bamoki" VALUES(14,4,'14:4','وە ھیچ پێغەمبەرێکمان ڕەوانە نەکردووە بەزمانی گەلەکەی خۆی نەبێت بۆ ئەوەی (ئاینەکەیان) بۆ ڕوون بکاتەوە ئەوسا خوا ھەرکەسی ویستبێت گومڕای کردووە (بەھۆی سەرکەشی خۆیەوە) ھەرکەسێکیشی ویستبێت ڕێنمونی کردووە ھەر ئەویش زاڵ وبەدەسەڵات وکاردروستە');
INSERT INTO "bamoki" VALUES(14,5,'14:5','سوێند بەخوا بەڕاستی موسامان نارد بە موعجیزەکانمانەوە (فەرمانمان پێدا) کەگەلەکەت ڕزگار بکە لە تاریکیەکانی (بێ باوەڕیەوە) بۆ ڕووناکی (باوەڕ و ئیمان) ڕۆژە (پیرۆزە) کانی خودایان بەبیر بھێنەرەوە (یا فەزڵەکانی خوایان بیر بخەرەوە) بەڕاستی لەو (بیرخستنەوە) دا پەند و بەڵگەی زۆر ھەیە بۆ ھەموو خۆڕاگرێکی سوپاسگوزار');
INSERT INTO "bamoki" VALUES(14,6,'14:6','(بیریان بخەرەوە) کاتێک موسا بە گەلەکەی ووت بیری ئەو فەزڵ و بەھرە وچاکەی خودا بکەنەوە کە بەسەرتانەوەیە (ئەویش ئەوە بوو) کاتێک کە ڕزگاری کردن لە دارو دەستەکەی فیرعەون کەسزای خراپیان پێ دەچەشتن (بەھەمیشەیی) وکوڕانی ئێوەیان سەردەبڕی و ئافرەتانی ئێوەیان بەزیندوویی دەھێشتەوە (بۆ خزمەتکاری) لەوەتاندا تاقی کردنەوەیەکی گەورە بوو لەلایەن پەروەردگارتانەوە');
INSERT INTO "bamoki" VALUES(14,7,'14:7','(بیریان بخەرەوە) کاتێک کە پەروەردگارتان ڕایگەیاند سوێندبێت بێگومان ئەگەر ئێوە سوپاسی (چاکەی) من بکەن بۆتان زیاد دەکەم بەڵام بێگومان ئەگەر نا سوپاسی بکەن ئەوە بەڕاستی سزای من زۆربەتینە');
INSERT INTO "bamoki" VALUES(14,8,'14:8','موسا فەرمووی ئەگەر ئێوە ناسوپاسی (نیعمەتەکانی خوا) بکەن و ھەموو ئەو کەسانەش لە زەویدان ئەوە بەڕاستی خوا بێ نیازی سوپاسکراوە');
INSERT INTO "bamoki" VALUES(14,9,'14:9','ئایا بۆتان نەھاتووە ھەواڵی ئەو گەلانەی کە لەپێش ئێوەدا بوون وەک گەلی نوح و عاد و سەمود ھەروەھا ئەوانەی کە لەپاش ئەوان بوون وھاتن بێجگە لە خوا ھیچ کەس نایانزانێت پێغەمبەرەکانیان بۆھات بە موعجیزە وبەڵگەی ئاشکراوە ئەمجا ئەوان دەست و پەنجەی خۆیان گەست لە ڕق و قیندا و ووتیان بەڕاستی ئێمە بێ باوەڕین بەو (پەیامە) ی کە بە ئێوەدا نێردراوە, وە بەڕاستی ئێمە لە گومانداین لەوەی ئێوە بانگمان دەکەن بۆلای و دوو دڵین');
INSERT INTO "bamoki" VALUES(14,10,'14:10','پێغەمبەرەکانیان ووتیان (پێیان) ئایا لە (بوونی) خوادا گومان ھەیە کە بەدیھێنەری ئاسمانەکان و زەویە بانگتان دەکات بۆ ئەوەی لە گوناھەکانتان خۆش بێت و دواتان بخات بۆ کاتێکی دیاریکراو ووتیان ئێوەش ھەر ئادەمیەکی وەک ئێمەن دەتانەوێت ڕێگری ئێمە بکەن و لاماندەن لە (پەرستنی) ئەوەی کە باوو باپیرانمان دەیانپەرست دەی نیشانە و بەڵگەیەکی ڕوونمان بۆ بھێنن (ئەگەر ڕاست دەکەن پێغەمبەرن)');
INSERT INTO "bamoki" VALUES(14,11,'14:11','پێغەمبەرەکانیان پێـیان ووتن ئێمە ھەر ئادەمیەکین وەکو ئێوە بەڵام خوا منەت دەنێت بەسەر ھەرکەسێک لە بەندەکانیدا کە بیەوێت (دەیکاتە پێغەمبەر) وە ئێمە ھەرگیز ناتوانین موعجیزە و بەڵگەیەکتان بۆ بھێنین مەگەر بە ئیزنی خوا نەبێت وە بابڕواداران پشت ھەر بە خوا ببەستن');
INSERT INTO "bamoki" VALUES(14,12,'14:12','وە ئایا بۆچی پشت بە خوا نەبەستین کە بێگومان ئەو (زاتە) ڕێگای ڕاستی نیشان داوین؟ وە بەڕاستی دان بەخۆماندا دەگرین لەسەر ھەر ئازارێک کە بماندەن دەبا خۆسپێران پشت ھەر بە خوا ببەستن');
INSERT INTO "bamoki" VALUES(14,13,'14:13','بێ بڕواکان ووتیان بەپێغەمبەرەکانیان بەڕاستی لە خاک و وڵاتەکەمان دەرتان دەکەین یان بەڕاستی دەبێت بگەڕێنەوە ناو ئاینەکەی ئێمە جا پەروەردگاریان نیگای بۆ (پێغەمبەران) کرد کە بێگومان ستەمکاران لەناو دەبەین');
INSERT INTO "bamoki" VALUES(14,14,'14:14','و بێگومان ئێوە نیشتەجێ دەکەین لەو خاکەدا پاش (تیاچوونی) ئەوان ئەوەش بۆ کەسانێکە لەوەستانی بەر مەقامی من دەترسن (لە قیامەتدا) وە (پێشتر) ترسابێت لە ھەڕەشەی (من)');
INSERT INTO "bamoki" VALUES(14,15,'14:15','وە (پێغەمەران) داوای سەرکەوتن و زاڵ بونیان کرد (لە خوا) و ھەموو ملھوڕ و یاخیەک بێ ھیوا بوون');
INSERT INTO "bamoki" VALUES(14,16,'14:16','لەدوای (تیاچوونی جێگای) ئەو دۆزەخە و لە کێم و زووخاو ئاو دەدرێت (دەکرێت بە گەروویاندا)');
INSERT INTO "bamoki" VALUES(14,17,'14:17','کە قوم قوم (زۆر بە قیزەوە) دەیخواتەوە بەڵام ناتوانێ قوتی بدات و مردنی بۆ دێت لە ھەموو لایەکەوە و کەچی ناشمرێت و لە دوای ئەوەش سزای سەخت (دەدرێت)');
INSERT INTO "bamoki" VALUES(14,18,'14:18','ئەوانەی بێ بڕوابوون بە پەروەردگاریان نموونەی کردەوەکانیان وەک خۆڵەمێشێک وایە بەتوندی با لێی ھەڵکردبێت لە ڕۆژێکی ڕەشەبای سەختدا ئەوان ھیچیان دەست ناکەوێت لەوەی کەلە دونیادا کردویانە ئا ئەوەیە گومڕابوونی دوور لە ڕاستی');
INSERT INTO "bamoki" VALUES(14,19,'14:19','ئایا نەتان زانیوە (نەتانبینیوە) کە بێگومان خوا ئاسمانەکان و زەوی بەھەق و ڕاستی دروستکردوە ئەگەر بیەوێت ئێوە لادەبات و ناتان ھێڵێت وە (لە جێی ئێوە) دروستکراوێکی تر دەھێنێت');
INSERT INTO "bamoki" VALUES(14,20,'14:20','ئەوەش بۆ خوا گران و زەحمەت نیە');
INSERT INTO "bamoki" VALUES(14,21,'14:21','وھەموویان (لە گۆڕەکانیان دێنە دەرەوە) بۆ خوا دەردەکەون و ئاشکرا ئەبن ئەمجا (لە ڕۆژی دواییدا) بێ دەسەڵاتەکان دەڵێن بە زۆر دار و خۆ بەزل زانەکان بەڕاستی ئێمە لە دونیادا شوێن کەوتووی ئێوە بووین ئاخۆ ئێوە بەرگری دەکەن لە ئێمە ھەندێک لە سزای خوا (ئەوانیش) دەڵێن ئەگەر خوا ئێمەی ڕێنمونی بکردایە ئێمەش ئێوەمان ڕێنمونی دەکرد یەکسانە بۆمان ھاوار و ناڵە بکەین یان ئارام بگرین ڕزگار بوونمان نیە (لە سزای خوا)');
INSERT INTO "bamoki" VALUES(14,22,'14:22','جا شەیتان دەڵێت کاتێک کە کار تەواو دەبێت (و لێپرسینەوەیان کۆتایی دێت) بێگومان خوا بەڵێنی پێدان بە بەڵێنێکی ھەق و ڕاست و منیش بەڵێنم پێدان بەڵام درۆم کرد لەگەڵتاندا وەمن نەم بوو بەسەرتاندا ھیچ زۆر و دەسەڵاتێک تەنھا ئەوە نەبێت کە بانگم کردن (بۆ گومڕایی) ئیتر ئێوەش بەدەنگمەوە ھاتن (و بەگوێتان کردم) کەواتە لۆمەی من مەکەن بەڵکو لۆمەی خۆتان بکەن بەڕاستی من ناتوانم بێم بە ھاواری ئێوەوە و ئێوەش ناتوانن بێن بەھاواری منەوە بەڕاستی من بڕوام نەبوو بەوەی کە پێشتر (لە دونیادا) منتان کرد بوو بە ھاوبەش بۆ خوا بەڕاستی ستەمکاران سزای سەختیان بۆ ھەیە');
INSERT INTO "bamoki" VALUES(14,23,'14:23','وە ئەوانەی باوەڕیان ھێناوە و کردەوە چاکەکانیان کردووە دەخرێنە ناو بەھەشتانێک کە ڕوبارەکان دێن و دەچن بە ژێریاندا بە ھەمیشە تیایدا دەمێننەوە بە ئیزنی پەروەردگاریان بە خێرھاتنیان لەو (بەھەشتە) دا بەووتەی سەلامە');
INSERT INTO "bamoki" VALUES(14,24,'14:24','ئایا نەتزانیوە؟ خوا چۆن نموونەی ھێناوەتەوە کە ووتەی چاک و ڕاست (لا إلە إلا اللە) وەک درەختێکی چاک و بەسوود وایە ڕەگی قایم (لە زەوی) دا کۆتابێت و لق و پۆپەکانیشی (بەرز بووبێتەوە) بە ئاسماندا');
INSERT INTO "bamoki" VALUES(14,25,'14:25','ئەو (درەختە) میوە و بەربگرێت ھەموو کاتێک بەئیزنی پەروەردگاری وە خوا ئەو نموونانە دەھێنێتەوە بۆ خەڵکی بۆ ئەوەی بیربکەنەوە');
INSERT INTO "bamoki" VALUES(14,26,'14:26','وە نموونەی ووتەی خراپ و پیس و (بێ باوەڕی) وەکو درەختێکی خراپ و پیس وایە ھەڵکەنرابێت لەسەر زەوی (و ڕەگی نەبێت) ھەر خۆی نەگرێت');
INSERT INTO "bamoki" VALUES(14,27,'14:27','ئەوانەی کەبڕوایان ھێناوە خوا دامەزراویان دەکات بە ووتەی بەڕێ وجێ (کە شایەتومانە) لەژیانی دونیا و لەڕۆژی دوایشدا وە خوا ستەمکارانیش گومڕا دەکات وە خوا ھەرچی بیەوێت دەیکات');
INSERT INTO "bamoki" VALUES(14,28,'14:28','ئایا سەیری ئەوانەت نەکردووە ناز ونیعمەت و بەخششەکانی خوایان لە (ئیمان وباوەڕ) گۆڕیەوە بە بێ باوەڕی وسپڵەیی (لەبری ئەوەی شوکری خوا بکەن بێ باوەڕ ویاخی بوون) و گەلەکەیان بەرەو ھەڵدێر برد');
INSERT INTO "bamoki" VALUES(14,29,'14:29','ئەویش دۆزەخە کەدەچنە ناوی کە جێگایەکی زۆر خراپە');
INSERT INTO "bamoki" VALUES(14,30,'14:30','(بێ باوەڕان) چەند ھاوتایان بۆ خوا بڕیاردا بۆ ئەوەی (خەڵک) گومڕابکەن لەڕێگەی خوا ئەی (موحەممەد صلى الله عليه وسلم) بڵێ بەوانە ڕابوێرن (لە دونیادا بۆ کوێ دەچن؟) چونکە بێگومان چارەنوستان ھەر دۆزەخ دەبێت');
INSERT INTO "bamoki" VALUES(14,31,'14:31','(ئەی موحەممەد صلى الله عليه وسلم) بڵێ بەوبەندانەم کە بڕوایان ھێناوە بەڕێکی نوێژەکان بکەن و لەوەی پێمان داوون ببەخشن بەنھێنی و ئاشکرا لەپێش ئەوەی کە ڕۆژێک بێت نەکڕین و فرۆشتنی تێدایە و نە دۆستایەتی');
INSERT INTO "bamoki" VALUES(14,32,'14:32','خوا ئەو زاتەیە کە ئاسمانەکان و زەوی دروستکردووە و لە ئاسمانەوە بارانی باراندوە و ھەموو بەروبوم و (دانەوێڵە) یەکی پێڕواندووە کە ڕزق وڕۆزیە بۆ ئێوە ھەروەھا کەشتی بۆ ڕام ھێناون (و خستویەتیە خزمەتتانەوە) بۆ ئەوەی بڕوات بەناو دەریادا بەفەرمانی خوا و ڕووبارەکانی بۆ ڕام ھێناون و (لە خزمەتاندایە)');
INSERT INTO "bamoki" VALUES(14,33,'14:33','وە ھەروەھا ڕامی ھێناوە بۆتان و (لە خزمەتاندایە) ڕۆژ و مانگ بەھەمیشەیی وە شەو و ڕۆژیشی بۆ رام ھێناون');
INSERT INTO "bamoki" VALUES(14,34,'14:34','وە لە ھەموو ئەو شتانە کە داواتان کردووە پێی بەخشیوون خۆ ئەگەر (بتانەوێت) ناز و نیعمەتەکانی خوا بژمێرن بۆتان ناژمێردرێن بەڕاستی ئادەمی (بێ بڕوا) زۆر ستەمکار و سپڵەیە (بەرامبەر نیعمەتەکانی خوا)');
INSERT INTO "bamoki" VALUES(14,35,'14:35','(بیریان بخەرەوە) کاتێک ئیبراھیم ووتی ئەی پەروەردگارم ئەم شارە (مەککە) بکە بەشارێکی ئارام و ئاسایش وە خۆم و کوڕەکانم بپارێزە و دوورمان خەرەوە لەوەی بتەکان بپەرستین');
INSERT INTO "bamoki" VALUES(14,36,'14:36','ئەی پەروەردگارم بێگومان ئەو بتانە زۆر لە خەڵکیان گومڕا کردوە (و لایاندان لەڕێگەی ڕاست) جا ھەرکەسێک (لە ڕۆڵەکانم) شوێن من کەوت ئەوە بێگومان لە منە ھەرکەسیش سەرپێچی کردم ئەوە بێگومان تۆ لێبوردە و میھرەبانی');
INSERT INTO "bamoki" VALUES(14,37,'14:37','ئەی پەروەردگارمان من ھەندێک لە ڕۆڵەکانم نیشتەجێ کرد لە شیوێکی بێ کشتوکاڵـدا لای ماڵە پیرۆز و ڕێزگیراوەکەت ئەی پەروەردگارمان ھەتا بە چاکی نوێژ بەجێ بھێنن و ئەنجامی بدەن بەڵکو دڵی خەڵکانێک وا لێ بکەیت سۆزی و مەیلیان ببێت بۆلایان و لەھەموو بەر و میوەیەکیش ڕۆزیان بدە بەڵکو شوکرانە بژێربن');
INSERT INTO "bamoki" VALUES(14,38,'14:38','ئەی پەروەردگارمان بەڕاستی تۆ دەزانی بە ھەرچی دەیشارینەوە و ھەرچی دەری دەبڕین وە ھیچ شتێ لای خوا شاراوە نیە نە لە زەوی و نە لە ئاسماندا');
INSERT INTO "bamoki" VALUES(14,39,'14:39','ھەموو سوپاس و ستایشێک بۆ ئەو خوایەی کە ئیسماعیل و ئیسحاقی پێ بەخشیم بەپیری بەڕاستی پەروەردگارم بیسەری دوعا و پاڕانەوەیە');
INSERT INTO "bamoki" VALUES(14,40,'14:40','ئەی پەروەردگارم من و رۆڵەکانم وا لێ بکەیت بەچاکی نوێژەکان بەجێ بھێنین ئەی پەروەردگارمان پاڕانەوە و نزاکەم وەرگرە');
INSERT INTO "bamoki" VALUES(14,41,'14:41','ئەی پەروەردگارمان لە من و باوکم و دایکم و ھەموو بڕواداران ببورە و خۆش ببە لە ڕۆژێکدا کە لێپرسینەوە دەکرێت و بەرپا ئەبێت');
INSERT INTO "bamoki" VALUES(14,42,'14:42','وا مەزانە خوا بێ ئاگایە لەو (زوڵم و ستەمەی) کە ستەمکاران دەیکەن بێگومان ئەو دوایان دەخات بۆ ڕۆژێک کەچاوەکان تێیدا ئەبڵەق دەبن (و ناینوقێن لە ترسا)');
INSERT INTO "bamoki" VALUES(14,43,'14:43','بە خێرایی دەڕۆن (بەدەم بانگ کەریانەوە) سەریان بەرزکردۆتەوە (بۆ ئاسمان) پێڵوی چاویان ناتروکێنن و دڵیان خاڵی یە (لە تێگەیشتن)');
INSERT INTO "bamoki" VALUES(14,44,'14:44','وە (ئەی موحەممەد صلى الله عليه وسلم) خەڵکی ئاگاداربکە و بترسێنە لەو ڕۆژەی کە سزایان بۆ دێت ئەمجا ئەوانەی کەستەمیان کردووە دەڵێن ئەی پەروەردگارمان بۆ ماوەیەکی کەم دوامان بخە تا وەڵامی بانگەوازەکەت بدەینەوە و شوێنی پێغەمبەران بکەوین ئەی ئێوە نەبوون پێشتر سوێندتان خوارد کە ھەرگیز لە ناوناچن');
INSERT INTO "bamoki" VALUES(14,45,'14:45','ھەر لەجێ و شوێنی ئەوانە نیشتەجێ بوون کەستەمیان لە خۆیان کرد و بەڕوونی بۆتان دەرکەوت کە چیمان بەسەر ھێنان نموونەی زۆریشمان بۆتان ھێنایەوە');
INSERT INTO "bamoki" VALUES(14,46,'14:46','بێگومان ئەو (بێ باوەڕانە) پیلانی گەورەی خۆیانیان گێڕا (دژی پێغەمبەرەکانیان) خوای گەورە ئاگادارە بە پیلانەکانیان وە ھەر چەندە پیلانەکەیان شاخەکانی پێ بترازێت (و لەبن بێت)');
INSERT INTO "bamoki" VALUES(14,47,'14:47','جا وا گومان مەبە بە خوا ئەو بەڵێنەی داویەتی بە پێغەمبەرانی بەدی ناھێنێت و نایھێـنـێـتە جێ بەڕاستی خوا بەدەسەڵات و خاوەن تۆڵەیە');
INSERT INTO "bamoki" VALUES(14,48,'14:48','لە ڕۆژێکدا کە ئەم زەویە دەگۆڕێت بە زەوی یەکی تر و ئاسمانەکانیش (دەگۆڕێن بە ئاسمانی تر) ئەوسا ھەموو (لە گۆڕەکانیان دێنە دەرەوە) دەردەکەون بۆ خودای تەنھای زاڵ و بە دەسەڵات بەسەر ھەموو شتێکدا');
INSERT INTO "bamoki" VALUES(14,49,'14:49','و لەو ڕۆژەدا تاوان باران دەبینی لە کۆت و زنجیردا پێکەوە بەستراون');
INSERT INTO "bamoki" VALUES(14,50,'14:50','پۆشاکیان قەترانی ڕەشە و دەم وچاویشیان ئاگر دایپۆشیوە');
INSERT INTO "bamoki" VALUES(14,51,'14:51','بۆ ئەوەی خوا تۆڵە و پاداشتی ھەموو کەسێک بدات بە گوێرەی ئەوەی کە کردویەتی بەڕاستی خوا (موحاسەبەکردن) و لێپرسینەوەی خێرایە');
INSERT INTO "bamoki" VALUES(14,52,'14:52','ئەم (قورئانە) ڕاگەیاندن و جاڕێکە بۆ ھەموو خەڵک وە ھەتا پێی بێدار بکرێنەوە, وە بۆ ئەوەی بزانن بێگومان ئەو (خوا) پەرستراوێکی تاک و تەنھایە وە تا خاوەن ژیرییەکان بیربکەنەوە وپەند وەربگرن');
INSERT INTO "bamoki" VALUES(15,1,'15:1','بە (ئەلیف، لام، ڕا) دەخوێنرێتەوە خوا زاناترە بە ماناکەی، بۆ زانیاری زیاتر سەیری سەرەتای سورەتی (البقرة) بکە ئەمانە ئایەت و نیشانەکانی قورئانی ڕوون کەرەوە و ئاشکران');
INSERT INTO "bamoki" VALUES(15,2,'15:2','ئەوانەی کەبێ باوەڕ بوون (لە ئایندەیەکی نزیکدا) زۆر جار ئاوات دەخوازن کاشکای موسوڵمان بوونایە');
INSERT INTO "bamoki" VALUES(15,3,'15:3','لێیان گەڕێ بابخۆن و ڕابوێرن وئومێد وھیوا بێ ئاگایان بکات جا بێگومان لەمەو دوا دەزانن (چۆن سزایان دەدەین)');
INSERT INTO "bamoki" VALUES(15,4,'15:4','(خەڵکی) ھیچ شارێکمان لەناو نەبردووە مەگەر کاتی دیاریکراو بۆ لەناو چوونیان ھەبووە');
INSERT INTO "bamoki" VALUES(15,5,'15:5','وەھیچ ئوممەتێک پێش ناکەوێت لەکاتی لەناوبردنی خۆی ھەروەھا دواش ناکەوێت');
INSERT INTO "bamoki" VALUES(15,6,'15:6','(بێ باوەڕان) ووتیان ئەی ئەو کەسەی کە قورئان نێرراوەتە خوار بۆ سەرت بەڕاستی تۆ شێـتیت');
INSERT INTO "bamoki" VALUES(15,7,'15:7','دەی بۆ فریشتەمان بۆ ناھێنی (شایەتی بدات تۆ پێغەمبەری) ئەگەر تۆ لە ڕاست گۆیانیت');
INSERT INTO "bamoki" VALUES(15,8,'15:8','ئێمە فریشتە نانێرینە خوارەوە مەگەر بەڕاست و ڕەوا نەبێت وە ئەو کاتەش ئەوانە مۆڵەت نادرێن');
INSERT INTO "bamoki" VALUES(15,9,'15:9','بێگومان ھەر ئێمە قورئانمان ناردۆتە خوارەوە و بەڕاستی ھەر ئێمەش دەیپارێزین');
INSERT INTO "bamoki" VALUES(15,10,'15:10','سوێندبێت بێگومان ئێمە پێش تۆ پێغەمبەرانمان ناردووە لەناو ھۆز و کۆمەڵە پێشووەکاندا');
INSERT INTO "bamoki" VALUES(15,11,'15:11','ھیچ پێغەمبەرێکیان بۆ نەھاتووە ئیللا گاڵتە و لاقرتیـیان پێ کردووە');
INSERT INTO "bamoki" VALUES(15,12,'15:12','ئا بەو جۆرە ئێمە ئەو (قورئانە) دەخەینە ناو دڵی تاوانبارانەوە');
INSERT INTO "bamoki" VALUES(15,13,'15:13','کەچی ئەوان باوەڕی پێ ناھێنن کە بێگومان باو و ڕێبازی پێشووەکانیش ھەر وابووە');
INSERT INTO "bamoki" VALUES(15,14,'15:14','ئەگەر بۆمان بکردنایەتەوە دەرگایەک لە ئاسمانەوە و ئەوان پیایدا بڕۆشتنایەتە سەرەوە');
INSERT INTO "bamoki" VALUES(15,15,'15:15','ئەوسا دەیانووت بێگومان ئێمە چاوبەستـمان لێکراوە بەڵکو ئێمە کۆمەڵێکین جادومان لێکراوە');
INSERT INTO "bamoki" VALUES(15,16,'15:16','سوێند بە خوا بێگومان ئێمە دروستمان کردووە چەند بورجێک لە ئاسماندا و ئەو (ئاسمانە) مان ڕازاندۆتەوە بۆ سەرەنجدەران و تەماشاکەران');
INSERT INTO "bamoki" VALUES(15,17,'15:17','و ئەو (ئاسمانە) مان پاراستووە لە ھەموو شەیتانێکی دەرکراو لە بەزەیی خوا');
INSERT INTO "bamoki" VALUES(15,18,'15:18','مەگەر ئەوانەی کە بە دزیەوە گوێ دەگرن یەکسەر گڕو ئاگرێکی دیار دوای دەکەوێت (وڕاوی دەنێت)');
INSERT INTO "bamoki" VALUES(15,19,'15:19','زەویشمان ڕاخستووە و چەند کێو و شاخی دامەزراومان تێدا داناوە و ڕواندوومانە تیایدا لە ھەموو ڕووەک و درەختێک بەڕێک و پێکی');
INSERT INTO "bamoki" VALUES(15,20,'15:20','وە لەو (زەویە) دا ھۆکارەکانی ژیانمان بۆ ئێوە ڕەخساندوە ھەروەھا بۆ ئەوانەش کە ئێوە ڕزق و ڕۆزیان نادەن');
INSERT INTO "bamoki" VALUES(15,21,'15:21','وە ھیچ شتێک نیە مەگەر خەزێنەکانی لای ئێمەیە و نای نێرین و نایدەین بەئەندازەی دیاریکراو نەبێت');
INSERT INTO "bamoki" VALUES(15,22,'15:22','وە بـا ( ھەوا) مان ناردووە کە پیتێنەری دارو درەخت بێت ئەمجا لە ئاسمانەوە بارانمان باراندووە, وە ئێوەمان پێ تێراو کردووە لە کاتێکدا ئێوە نەتان دەتوانی ئەو (ئاوە) خەزن بکەن');
INSERT INTO "bamoki" VALUES(15,23,'15:23','بێگومان ھەر ئێمە دەژێنین و دەشمرێنین ھەر ئێمەش میراتگری ھەمووانین');
INSERT INTO "bamoki" VALUES(15,24,'15:24','سوێند بە خوا ئێمە ئاگادارین بەوانەی لەپێش ئێوەوە بوون و سوێند بە خوا بێگومان ئەوانەش دەناسین کە پاش ئێوە دێن');
INSERT INTO "bamoki" VALUES(15,25,'15:25','بێگومان ھەر پەروەردگارت ھەموویان کۆ دەکاتەوە بەڕاستی ھەر خۆی کاردروست و زانایە');
INSERT INTO "bamoki" VALUES(15,26,'15:26','سوێندبێت بێگومان ئێمە ئادەمیمان دروستکرد لە قوڕێکی ڕەشی ترشاوی ڕەق');
INSERT INTO "bamoki" VALUES(15,27,'15:27','جنۆکەشمان پێشتر دروستکرد لە ئاگرێکی بەتین');
INSERT INTO "bamoki" VALUES(15,28,'15:28','(بیریان بخەرەوە) کاتێک پەروەردگارت بە فریشتەکانی فەرموو بەڕاستی من بەشەرێک دروست دەکەم لە قوڕێکی ڕەشی ترشاوی ڕەق');
INSERT INTO "bamoki" VALUES(15,29,'15:29','ھەر کاتێک ڕێک وپێکم کرد و فوم بە بەریدا کرد لە رۆحێک کە خۆم دروستم کردووە (مەبەست لەدانە پاڵی رۆح بۆلای خوا ڕێز و تەشریفە) دەبێ یەکسەر سوژدەی بۆ بەرن');
INSERT INTO "bamoki" VALUES(15,30,'15:30','جا فریشتەکان ھەموو تێکڕا سوژدەیان برد (بۆی)');
INSERT INTO "bamoki" VALUES(15,31,'15:31','ئیبلیس (شەیتان) نەبێت کە سەرپێچی کرد لەوەی کە لەگەڵ سوژدەبەراندا بێت (بۆ ئادەم)');
INSERT INTO "bamoki" VALUES(15,32,'15:32','(خوا) فەرمووی ئەی ئیبلیس بۆ چی تۆ لەگەڵ سوژدە بەراندا نەبوویت؟!');
INSERT INTO "bamoki" VALUES(15,33,'15:33','(ئیبلیس) ووتی: من ھیچ کاتێ سوژدە نابەم بۆ بەشەرێک کە دروستت کردووە لە قوڕێکی ڕەشی ترشاوی ڕەق');
INSERT INTO "bamoki" VALUES(15,34,'15:34','(خوا پێی) فەرموو دەلەوێ (بەھەشت) بڕۆ دەرەوە چونکە بەڕاستی تۆ لە بەزەیی من دەرکراوی');
INSERT INTO "bamoki" VALUES(15,35,'15:35','وە بەڕاستی نەفرینت لەسەرە تاڕۆژی دوایی');
INSERT INTO "bamoki" VALUES(15,36,'15:36','ووتی ئەی پەروەردگارم دەی مۆڵەتم بدە ھەتا ئەو ڕۆژەی (مردووان) زیندوو دەکرێنەوە');
INSERT INTO "bamoki" VALUES(15,37,'15:37','(خوا) فەرمووی ئەوە بەڕاستی تۆ لە مۆڵەت دراوانی');
INSERT INTO "bamoki" VALUES(15,38,'15:38','تا ڕۆژێ کە کاتی دیاریکراوە');
INSERT INTO "bamoki" VALUES(15,39,'15:39','ووتی ئەی پەروەردگارم لەبەر ئەوەی گومڕات کردم بێگومان خۆشی (دونیا) یان لە زەویدا بۆ دەڕازێنمەوە وە بەڕاستی ھەموویان گومڕا دەکەم');
INSERT INTO "bamoki" VALUES(15,40,'15:40','بەندە دڵسۆزەکانت نەبێت');
INSERT INTO "bamoki" VALUES(15,41,'15:41','(خوا) فەرمووی ئەمە ڕێگای ھەق و ڕاستە بەرەو من');
INSERT INTO "bamoki" VALUES(15,42,'15:42','بەڕاستی بەندەکانی من تۆ ھیچ دەسەڵاتت نیە بە سەریاندا جگە لەو گومڕایانەی کەشوێنت کەوتوون');
INSERT INTO "bamoki" VALUES(15,43,'15:43','وە بێگومان دۆزەخ جێگای ھەموویانە');
INSERT INTO "bamoki" VALUES(15,44,'15:44','(دۆزەخ) حەوت دەرگای ھەیە ھەر دەرگایەکیشیان لەوانە دابەش کراوە بۆ چەند بەشێک');
INSERT INTO "bamoki" VALUES(15,45,'15:45','بەڕاستی خۆپارێزو لە خواترسان لەناو باخات و کانیاودان');
INSERT INTO "bamoki" VALUES(15,46,'15:46','(پێیان دەووترێت) بچنە ناویانەوە بەھێمنی و ئاسوودەیی');
INSERT INTO "bamoki" VALUES(15,47,'15:47','ھەر ڕق و کینەیەک لە دڵیاندا بووبێت دەرمان ھێناوە برای یەکترن بەرامبەر یەکترن لەسەر کورسی دانیشتوون');
INSERT INTO "bamoki" VALUES(15,48,'15:48','لەوێدا ماندوو بوونیان تووش نایەت و ھەرگیز لەو (بەھەشتە) دەرناکرێن');
INSERT INTO "bamoki" VALUES(15,49,'15:49','ھەواڵ بدە بە بەندەکانم کە بەڕاستی ھەر من لێبوردە و میھرەبانم');
INSERT INTO "bamoki" VALUES(15,50,'15:50','وبێگومان ھەر سزای منە سزای سەخت');
INSERT INTO "bamoki" VALUES(15,51,'15:51','وە ھەواڵیان پێ بدە دەربارەی میوانەکانی ئیبراھیم');
INSERT INTO "bamoki" VALUES(15,52,'15:52','کاتێک چوونە ژورەوە بۆلای ووتیان سەلامت لێ بێت (ئیبراھیم) ووتی بەڕاستی ئێمە لێتان دەترسین');
INSERT INTO "bamoki" VALUES(15,53,'15:53','ووتیان (پێی) مەترسە بەڕاستی ئێمە مژدەت دەدەینێ بە (لە دایک بوونی) کوڕێکی زانا');
INSERT INTO "bamoki" VALUES(15,54,'15:54','ووتی ئایا مزگێنیم پێ دەدەن لە کاتێکدا کەپیر بووم ئیتر چی مژدەیەکم دەدەنێ');
INSERT INTO "bamoki" VALUES(15,55,'15:55','ووتیان ئەو مژدەی داومانە پێت ڕاستە و (دێتە دی) جا لە نائومێدان مەبە');
INSERT INTO "bamoki" VALUES(15,56,'15:56','ووتی جا کێ لە میھرەبانی پەروەردگاری نائومێد دەبێت کەسانی گومڕا نەبێت');
INSERT INTO "bamoki" VALUES(15,57,'15:57','(ئیبراھیم) فەرمووی ئەی ڕەوانەکراوانی (خوا) چ کارێکی گرنگتان ھەیە');
INSERT INTO "bamoki" VALUES(15,58,'15:58','وتیان بەڕاستی ئێمە نێردراوین بۆ سەر گەلێکی تاوانبار (لەناویان بەرین)');
INSERT INTO "bamoki" VALUES(15,59,'15:59','جگە لە ماڵ و منداڵی لوط کە بێگومان ھەموویان ڕزگار دەکەین');
INSERT INTO "bamoki" VALUES(15,60,'15:60','ژنەکەی نەبێت کە بێگومان بڕیارمانداوە ئەو لە ڕیزی تیاچوەکان بێت');
INSERT INTO "bamoki" VALUES(15,61,'15:61','جا کاتێک نێردراوەکان ھاتن بۆ لای خاو خێزانی لوط');
INSERT INTO "bamoki" VALUES(15,62,'15:62','(لوط) ووتی بەڕاستی ئێوە دەستەیەکی نەناسراون');
INSERT INTO "bamoki" VALUES(15,63,'15:63','ووتیان بەڵکو ئێمە بۆمان ھێناوی ئەو (سزای) کە ئەوان تێیدا دوودڵ بوون');
INSERT INTO "bamoki" VALUES(15,64,'15:64','وە ئێمە ڕاستیمان بۆ تۆ ھێناوە بێگومان ئێمە ڕاستگۆین');
INSERT INTO "bamoki" VALUES(15,65,'15:65','جا کەواتە لە بەشێکی شەودا شەوڕەوی بە (شوێنکەوتووان و) خاوخێزانت بکە (لە شار دەرچۆ) و خۆت دوایان بکەوە ھیچ کامتان لانەکەنەوە و بڕۆن بۆ ئەو شوێنەی کەفەرمانتان پێدراوە');
INSERT INTO "bamoki" VALUES(15,66,'15:66','ئەم کارەمان بەنیگا بە لوط ڕاگەیاند کە بێگومان لە بەرەبەیاندا ئەوانە دوا بڕاو دەبن (ھەموویان لەناو دەچن)');
INSERT INTO "bamoki" VALUES(15,67,'15:67','جا خـەڵـکی (نێربازی) شارەکە ھاتن (بەرە و ماڵی لوط) و مژدەیان بە یەکتری دەدا');
INSERT INTO "bamoki" VALUES(15,68,'15:68','(لوط) ووتی دڵنیابن ئەمانە میوانی منن دەی ئابڕوم مەبەن');
INSERT INTO "bamoki" VALUES(15,69,'15:69','و لە خوا بترسن و شەرمەزار و ڕیسوام مەکەن');
INSERT INTO "bamoki" VALUES(15,70,'15:70','ووتیان ئێمە پێمان نەووتیت نابێت بەرگری لە کەس بکەیت (نابێت میوانداری کەس بکەیت وکەس توخنت بکەوێت)');
INSERT INTO "bamoki" VALUES(15,71,'15:71','(لوط) فەرمووی ئائەوانە کچانی منن (مارەیان کەن) ئەگەر ئێوە (ئەوکارە) ئەنجام دەدەن');
INSERT INTO "bamoki" VALUES(15,72,'15:72','سوێند بە تەمەن و ژیانی تۆ (ئەی موحەممەد صلى الله عليه وسلم) بەڕاستی ئەوانە لەسەر خۆشی خۆیاندا سەرگەردانن');
INSERT INTO "bamoki" VALUES(15,73,'15:73','جا لە کاتی خۆر ھەڵھاتندا شریخەیەکی بەھێز گرتنی');
INSERT INTO "bamoki" VALUES(15,74,'15:74','ئەمجا ئەو (شارە) مان سەرو ژێر کرد و گڵی بەردینمان بەسەردا باراندن');
INSERT INTO "bamoki" VALUES(15,75,'15:75','بەڕاستی لەمەدا بەڵگەی زۆر ھەیە بۆ ئەوانەی بیردەکەنەوە');
INSERT INTO "bamoki" VALUES(15,76,'15:76','وە بێگومان ئەو (شارە وێران کراوە) لەسەر ڕێگای سەرەکی (کاروانەکان) دایە');
INSERT INTO "bamoki" VALUES(15,77,'15:77','بەڕاستی لەوەدا بەڵگەی گرنگ ھەیە بۆ بڕواداران');
INSERT INTO "bamoki" VALUES(15,78,'15:78','بەڕاستی خاوەن باخاتە زۆر پڕەکانیش (قەومی شوعەیب) لە ستەم کاران بوون');
INSERT INTO "bamoki" VALUES(15,79,'15:79','جا تۆڵەمان لێ سەندن جا بەڕاستی ئەم دووانە لەسەر ڕێگایەکی ئاشکرادان');
INSERT INTO "bamoki" VALUES(15,80,'15:80','سوێند بە خوا بێگومان خەڵکی حیجریش (گەلەکەی صاڵح) بڕوایان بە پێغەمبەرەکانیان نەکرد');
INSERT INTO "bamoki" VALUES(15,81,'15:81','بەڵگەو نیشانەکانی خۆمانمان پێدان بەڵام ئەوان ڕوویان لێ وەرگێڕا');
INSERT INTO "bamoki" VALUES(15,82,'15:82','ئەوانە لە شاخەکاندا دەیانتاشی خانووی زۆر (قایمی) بێ ترس و بیم');
INSERT INTO "bamoki" VALUES(15,83,'15:83','ئەمجا دەنگ و پیاھەڵشاخانێکی بەھێز گرتنی لە بەرەبەیاندا');
INSERT INTO "bamoki" VALUES(15,84,'15:84','جا ھەرچیان کردبوو (خانوو لە چیاکاندا) ھیچ سوودی پێ نەگەیاندن');
INSERT INTO "bamoki" VALUES(15,85,'15:85','وە ئێمە ئاسمانەکان و زەویمان دروست نەکردوە ئەوەش لە نێوانیاندایە بەھەق و ڕاستی نەبێت وە بێگومان ڕۆژی دوایی دێت تۆش بە جوانی چاوپۆشی بکە لێیان');
INSERT INTO "bamoki" VALUES(15,86,'15:86','بەڕاستی ھەر پەروەردگارت بەدیھێنەر و زانایە');
INSERT INTO "bamoki" VALUES(15,87,'15:87','سوێند بە خوا بەڕاستی حەوت ئایەتی دووبارە کراوەمان پێ بەخشیت (مەبەست لە سورەتی الفاتحة یە کە چەند جارێک لە نویژدا دووبارە دەبێتەوە) ھەروەھا قورئانی گەورەش');
INSERT INTO "bamoki" VALUES(15,88,'15:88','(ئەی موحەممەد صلى الله عليه وسلم) چاوت مەبڕە ئەو نازو نیعمەتی (دونیا) کە داومانە بە کۆمەڵێک لەو بێ باوەڕانە و خەمیان بۆ مەخۆ وە باڵی بەزەیی ڕاخە بۆ بڕواداران');
INSERT INTO "bamoki" VALUES(15,89,'15:89','وە بڵێ بەڕاستی من ترسێنەرێکی ڕوون و ئاشکرام (دەتان ترسێنم لە سزایەک)');
INSERT INTO "bamoki" VALUES(15,90,'15:90','وێنەی ئەو (سزایە) ی ناردمان بۆ دابەشکەران (مەبەست پێ جولەکە وگاورەکانە کە باوەڕیان دەکرد بەھەندێکی قورئان وبێ باوەڕیش بوون بەھەندێکی تری)');
INSERT INTO "bamoki" VALUES(15,91,'15:91','ئەوانەی کە قورئانیان کردە چەند بەشەوە (باوەڕداربوون بە ھەندێکی و کافریش بوون بەوەی تری)');
INSERT INTO "bamoki" VALUES(15,92,'15:92','جا سوێند بە پەروەردگارت پرسیار لە ھەموویان دەکەین');
INSERT INTO "bamoki" VALUES(15,93,'15:93','لەوەی کە دەیانکرد (لە دونیادا)');
INSERT INTO "bamoki" VALUES(15,94,'15:94','جا ھەر فەرمانێکت پێ دەدرێت بە ئاشکرا ڕایگەیەنە و گوێ بە ھاوبەشدانەران مەدە');
INSERT INTO "bamoki" VALUES(15,95,'15:95','بەڕاستی ئێمە تۆ دەپارێزین لە شەڕ و پیلانی ئەو کەسانەی گاڵتە و لاقرتی دەکەن بە ئاینەکەت');
INSERT INTO "bamoki" VALUES(15,96,'15:96','ئەوانەی لەگەڵ خوادا پەرستراوێکی تر دادەنێن جا دواتر دەزانن (چۆن سزایان دەدەین)');
INSERT INTO "bamoki" VALUES(15,97,'15:97','سوێند بە خوا بێگومان ئێمە دەزانین کە بەڕاستی تۆ دڵت تەنگ دەبێت بەوەی ئەوانە دەیڵێن');
INSERT INTO "bamoki" VALUES(15,98,'15:98','کەواتە تەسبیحاتی پەروەردگارت بکە و لە سوژدەبەران بە');
INSERT INTO "bamoki" VALUES(15,99,'15:99','وە پەروەردگارت بپەرستە ھەتا مردن یەخەت پێ دەگرێت');
INSERT INTO "bamoki" VALUES(16,1,'16:1','ئەوا فەرمانی خوا (بە سزادانیان) ھات بۆیە پەلە مەکەن (لە ھاتنی) پاک و بێگەردی بۆ خوا لە ھەر شتێک ئەوانە دەیکەن بە ھاوبەشی');
INSERT INTO "bamoki" VALUES(16,2,'16:2','فریشتە دەنێرێتە خوارەوە بە وەحی بە فەرمانی خۆی بۆ ھەر کەسێک لە بەندەکانی کە خۆی بیەوێت بەوەی کە خەڵکی بترسێنن کە بەڕاستی بێجگە لە من ھیچ پەرستراوێکی بەھەقی تر نیە کەواتە ھەر لە من بترسن');
INSERT INTO "bamoki" VALUES(16,3,'16:3','(خوا) ئاسمانەکان و زەوی بەھەق دروستکرد بەرزو بڵندە لە ھەرچی ئەوانە دەیکەنە ھاوەڵ و ھاوبەشی');
INSERT INTO "bamoki" VALUES(16,4,'16:4','(خوای گەورە) ئادەمی دروستکرد لە دڵۆپە ئاوێک کەچی بۆتە دوژمنێکی ئاشکرای ( بەرنامەکەی خوا)');
INSERT INTO "bamoki" VALUES(16,5,'16:5','ئاژەڵیشی بۆ ئێوە دروستکردووە کە (ھۆی) گەرمایی و کەڵکیان تێدایە وە لە (گۆشتی) ئەوان دەخۆن');
INSERT INTO "bamoki" VALUES(16,6,'16:6','وە بۆ ئێوە ھەیە لەو (ئاژەڵانەدا دیمەنی) جوانی و ڕازاوە کاتێک (بە ئێواران) دەیان ھێننەوە لە لەوەڕگا و کاتێکیش (بەیانیان) دەیانبەن بۆ لەوەڕگا');
INSERT INTO "bamoki" VALUES(16,7,'16:7','وە (ھەندێ لەو ماڵاتانە) بارەکانتان ھەڵدەگرن (ودەیبەن) بۆ شار وجێگایە کە ئێوە پێی نەدەگەیشتن مەگەر زۆر بەناڕەحەت نەبێت بەڕاستی پەروەردگارتان زۆر بەسۆز و بە بەزەییە (بۆ ئادەمی)');
INSERT INTO "bamoki" VALUES(16,8,'16:8','وە ئەسپ و ماین و ئێسترو گوێدرێژی (بۆ دروستکردون) لەبەر ئەوەی سواریان بن و ھۆی ڕازانەوەش بن بۆ ئێوە, وە (خوا) شتی وا دروست دەکات کە (ئێستا) ئێوە نایزانن');
INSERT INTO "bamoki" VALUES(16,9,'16:9','ڕێگای ڕاست نیشاندان (بە بەندەکان) تەنھا بۆ خوایە وە ھەندێک لە ڕێگاکان لارو ناڕێکە وە ئەگەر (خوا) بیویستایە ھەمووتانی ڕێنمونی دەکرد');
INSERT INTO "bamoki" VALUES(16,10,'16:10','ئەو (خوا) زاتێکە کە لە ئاسمانەوە بارانی بۆ باراندون ئاو خواردنەوەتان لەو (بارانە)یە و ھۆی سەوز بوونی ڕوەکیشە کەمەڕ و ماڵاتی لێ دەلەوەڕێنن');
INSERT INTO "bamoki" VALUES(16,11,'16:11','بەو (ئاوە خوا) بۆتان دەڕوێنێت کشتوکاڵ و زەیتون و دار خورما مێو و ڕەز و ھەموو بەرو میوەیەک بەڕاستی لەوەدا (باس کرا) بەڵگەیەکی ڕوون ھەیە بۆ ئەو کەسانەی بیردەکەنەوە');
INSERT INTO "bamoki" VALUES(16,12,'16:12','وە ڕامی ھێناوە بۆتان شەوو ڕۆژ (لە خزمەتی ئێوەدایە) و خۆر و مانگ ھەروەھا ئەستێرەکانیش ڕام ھێنراوون بە فەرمانی خوا بەڕاستی لەوەدا (باس کرا) بەڵگەی زۆر ھەیە بۆ ئەوانە کەژیرن و تێدەگەن');
INSERT INTO "bamoki" VALUES(16,13,'16:13','وە (ڕامی ھێناوە) بۆتان ئەوەی دروستیکردووە لە زەویدا و بڵاوی کردوەتەوە کە ڕەنگ و جۆریان جیاوازە بەڕاستی لەوەدا باسکرا نیشانەیەکی ڕوون ھەیە بۆ ئەوانەی کە ئامۆژگاری وەردەگرن');
INSERT INTO "bamoki" VALUES(16,14,'16:14','وە ئەو (خوایە) زاتێکە کە دەریای ڕام ھێناوە بۆ ئەوەی گۆشتی تازەی لێ بخۆن (مەبەست ماسی و ھاوشێوەکانیەتی) وە بۆ ئەوەی خشڵیشی لێ دەربھێنن کەدەی پۆشن (بۆ خۆ ڕازاندنەوە وەک دوڕ و سەدەف و مرواری) وە کەشتیەکان دەبینیت (دێن و دەڕۆن بە سنگیان) ئاوی دەریا لەت دەکەن وە بۆ ئەوەی ھەندێک لە فەزڵ و ڕۆزی خوا بەدەس بھێنن و بۆ ئەوەی سوپاسی خواش بکەن');
INSERT INTO "bamoki" VALUES(16,15,'16:15','وە (خوا) لە زەویدا چەند شاخێکی دامەزراوی داناوە بۆ ئەوەی ئێوە نەلەرزێنێت وبتوانن لەسەری بژین وەچەم و ڕووباری (تیاداناوە) ھەروەھا رێگای زۆری (بەدی ھێناوە) بۆ ئەوەی ئێوە ڕێی پێ دەرکەن');
INSERT INTO "bamoki" VALUES(16,16,'16:16','ھەروەھا نیشانەی زۆری داناوە و بە ئەستێرەش خەڵکی ڕێ دەرئەکەن (لە شەودا)');
INSERT INTO "bamoki" VALUES(16,17,'16:17','جا ئایا زاتێک (ئەمانە) دروست دەکات وەک کەسێک وایە کە ھیچ دروست نەکات ئەوە بۆ بیرناکەنەوە؟');
INSERT INTO "bamoki" VALUES(16,18,'16:18','وە ئەگەر (بتانەوێت) نیعمەتە کانی خوا بژمێرن ناتوانن بیان ژمێرن بەڕاستی خوا لێبوردە و میھرەبانە');
INSERT INTO "bamoki" VALUES(16,19,'16:19','وە خوا دەزانێت چی دە شاردنەوە و چی ئاشکرا دەکەن');
INSERT INTO "bamoki" VALUES(16,20,'16:20','ئەوانەش ھاواریان لێ دەکەن بێجگە لە خوا ھیچ شتێک دروست ناکەن بەڵکو خۆیان دروستکراون');
INSERT INTO "bamoki" VALUES(16,21,'16:21','مردووی بێ گیانن (ئەو بتانە) ھەست ناکەن کەی (خۆیان و خەڵکی) زیندوو دەکرێنەوە');
INSERT INTO "bamoki" VALUES(16,22,'16:22','خوای ئێوە خوایەکی تاک و تەنھایە جا ئەوانەی کە بڕوا ناھێنن بە ڕۆژی دوایی دڵەکانیان بێ باوەڕە و ھەمیشە خۆیان بەزل دەزانن');
INSERT INTO "bamoki" VALUES(16,23,'16:23','ڕاستە بێگومان خوا دەزانێت بە ئەوەی دەیشارنەوەو ئەوەی ئاشکرای دەکەن بەڕاستی خوا خۆ بەزل زانانی خۆش ناوێت');
INSERT INTO "bamoki" VALUES(16,24,'16:24','وە کاتێک بەوانە بوترێت ئایا پەروەردگارتان چی ناردۆتە خوارەوە دەڵێن حیکایەت و ئەفسانەی پڕو پوچی پێشووەکان');
INSERT INTO "bamoki" VALUES(16,25,'16:25','بۆ ئەوەی باری گوناھی خۆیان بە تەواوی ھەڵبگرن لە ڕۆژی دوایدا لەگەڵ ھەندێک لەباری گوناھی ئەوانەی کە ئەمان گومڕایان دەکردن بەبێ زانیاری بێداربن زۆر بەدە ئەوەی کە ھەڵیدەگرن (و دەیگرنە ئەستۆ)');
INSERT INTO "bamoki" VALUES(16,26,'16:26','بەڕاستی ئەوانەش کە لە پێش ئەمانەوە بوون پیلانیان گێڕا جا خوا کـۆشک و خانووەکـانیانی لە بن و بنـاغەوە دەرھێنـا ئەوسا سەربانەکانیان لە سەریانەوە ڕوخا بە سەریاندا لەلایەکەوە سزایان بۆ ھات کە ھەستیان پێ نەدەکرد');
INSERT INTO "bamoki" VALUES(16,27,'16:27','پاشان لە ڕۆژی دواییدا ڕیسوا و سەرشۆڕیان دەکات و (خوا پێیان) دەفەرموێت ئەو ھاوبەشانەی داتان بۆم لەکوێن کە ئێوە لەبەر ئەوان ململانێی (پێغەمبەران و بڕواداران) تان دەکرد ئەوانەی زانیاریان پێدرا بوو دەڵێن بەڕاستی ڕیسوایی و بەدبەختی ئەمڕۆ بۆ بێ باوەڕانە');
INSERT INTO "bamoki" VALUES(16,28,'16:28','ئەوانەی کە فریشتەکان گیانیان دەکێشن لە کاتێکدا ستەمکار بوون لە خۆیان جالەو کاتەدا ملکەچ دەبن (و ئەڵێن) ئێمە ھیچ خراپەیەکمان نەکردووە بەڵێ (خراپەتان کردووە) بێگومان خوا ئاگادارە بەو کردەوانەی کە ئێوە دەتانکرد');
INSERT INTO "bamoki" VALUES(16,29,'16:29','کەواتە لە دەرگاکانی دۆزەخەوە بڕۆنە ژورەوە تێیدا بمێننەوە بە ھەمیشەیی و (ھەتا ھەتایی) ئای جێگەی خۆ بەگەورە زانان چەند ناخۆش و خراپە');
INSERT INTO "bamoki" VALUES(16,30,'16:30','وە ووترا بەوانەی کە خۆیان پاراست (لە بێ بڕوایی) ئەوە پەروەردگارتان چی ناردوەتە خوارەوە؟ دەڵێن خێرو چاکە بۆ ئەوانەی کە لەم دونیایەدا چاکەیان کردووە چاکە ھەیە و بێگومان جێگای ڕۆژی دوایی چاکترە و چ خۆشەماڵ و شوێنی پارێزکاران و لە خواترسان');
INSERT INTO "bamoki" VALUES(16,31,'16:31','بەھەشتانێک جێگای مانەوەی ھەمیشەیین کە ئەوان دەچنە ناوی ڕوبارەکانی دەڕۆن بەژێر (قەسرو درەختەکان) یاندا لەو بەھەشتانەدا ئەوەی بیانەوێ بۆیان ئامادەیە ئابەو جۆرە خوا پاداشتی خۆ پارێزەران و خواناسان دەداتەوە');
INSERT INTO "bamoki" VALUES(16,32,'16:32','ئەوانەی کە فریشتەکانی (گیان کێشان) گیانیان دەکێشن بە پاکی (فریشتەکان) دەڵێن سەلامتان لێ بێت بچنە بەھەشتەوە بەھۆی ئەوەی کە دەتانکرد (لە دونیادا)');
INSERT INTO "bamoki" VALUES(16,33,'16:33','ئایا (ئەو بێ باوەڕانە) چاوەڕێی چی ئەکەن جگە لەوەی فریشتەکان بۆتان بێن و (گیانیان بکێشن) یان فەرمانی پەروەردگارت بێت (بۆ سزادانیان) ئەوانەی پێش ئەمانیش ھەر وەکو ئەمانیان کرد و خواستەمی لێ نەکردن بەڵکو خۆیان ستەمیان لە خۆیان دەکرد');
INSERT INTO "bamoki" VALUES(16,34,'16:34','لەئاکامدا تۆڵەی ئەو خراپانانەیان تووش بوو کە کردبوویان وە ئەو (سزای) کە ئەوان گاڵتەیان پێدەکرد دەوری دان و ھاتەسەریان');
INSERT INTO "bamoki" VALUES(16,35,'16:35','وە ئەوانەی ھاوبەشیان داناوە بۆ خوا دەڵێن ئەگەر خوا بیویستایە بێجگە ئەو (جگە لە خوا) ھیچ شتێکمان نەدەپەرست نەئێمە و نەباو و باپیرانمان وە حەراممان نەدەکرد ھیچ شتێک بەبێ فەرمانی ئەو پێشینانی ئەمانیش ھەر بەو جۆرەیان دەکرد ئایا پێغەمبەران ھیچیان لەسەرە جگە لە ڕاگەیاندنێکی ئاشکرا و ڕوونی (ئاینی یەکخواپەرستی)');
INSERT INTO "bamoki" VALUES(16,36,'16:36','سوێند بەخوا لەناو ھەموو گەل و ئوممەتێکدا پێغەمبەرێکمان ناردووە (کە فەرمان دەکەن بەخەڵکی) بەوەی تەنھا خوا بپەرستن و دوورکەونەوە لە (پەرستنی) ھەرپەرستراوێکی تر جا خوا ھەندێکی لەو (ئوممەتانە) ڕێنمونی کرد و ھەندێکیشیان (لەبەر خراپیان) گومڕایی یەخەی گرتن دەی بە زەویدا بگەڕێن و تەماشاکەن سەر ئەنجامی بێ باوەڕان چۆن بوو');
INSERT INTO "bamoki" VALUES(16,37,'16:37','ھەر چەند زۆر سوور بیت لەسەر ڕێنمونی ئەوانە (بێ سوودە) چونکە بەڕاستی خوا ڕێنمونی کەسێک ناکات کە (بیەوێ) گومڕای بکات ھیچ پشتیوانێکیشیان نیە');
INSERT INTO "bamoki" VALUES(16,38,'16:38','وە ئەوانە زۆر توند سوێندیان دەخوارد بە خوا (دەیانووت) ھەرکەس بمرێت خوا زیندووی ناکاتەوە بەڵێ (زیندووی دەکاتەوە) بەڵێنێکی ھەق و ڕاستی خوایە بەڵام زۆربەی خەڵکی نازانن');
INSERT INTO "bamoki" VALUES(16,39,'16:39','بۆئەوەی ڕوونی بکاتەوە بۆخەڵکی ئەوەی تیایدا جیاوازن وەتا ئەوانەش کە بێ باوەڕ بوون بزانن کە بەڕاستی ئەوان درۆزن بوون');
INSERT INTO "bamoki" VALUES(16,40,'16:40','بەڕاستی کاتێک (زیندووکردنەوە و بەدیھێنانی ھەرشتێکمان ) بوێت (ئەمەیە) کەپێی دەڵێین ببە ئەویش یەکسەر دەبێت');
INSERT INTO "bamoki" VALUES(16,41,'16:41','وە ئەوانەی لەڕێی خوادا کۆچیان کرد دوای ئەوەی ستەمیان لێکرا بێـگومان لە دونیادا نیشتەجێیـان دەکەین زۆر بەچاکی وە بەڕاستی پاداشتی ڕۆژی دوایی گەورەترە ئەگەر بیان زانیایە');
INSERT INTO "bamoki" VALUES(16,42,'16:42','ئەوانە کەسانێک بوون دانیان بەخۆیاندا گرت و تەنھا پشتیان بە پەروەردگاریان دەبەست');
INSERT INTO "bamoki" VALUES(16,43,'16:43','وە ڕەوانەمان نەکردووە لە پێش تۆ بێجگە لە پیاوانێک کە نیگامان بۆ ناردوون دەی پرسیار بکەن لە زانایان ئەگەر ئێوە نازانن');
INSERT INTO "bamoki" VALUES(16,44,'16:44','لەگەڵ چەندین بەڵگەو پەڕاوی ئاسمانی و قورئانیشمان بۆ تۆ نارد بۆ ئەوەی ڕوونی بکەیتەوە بۆ خەڵکی ئەوەی بۆیان نێردراوەتە خوارەوە بەڵکو بیربکەنەوە وتێ بگەن');
INSERT INTO "bamoki" VALUES(16,45,'16:45','دەی ئایا ئەوانەی کەپ یلان و خراپەیان کردووە بێ باکن و ناترسن لەوەی کە خوا بیانبات بەناخی زەویدا یان سزایان بۆ بێت لە شوێنێکەوە کە ھەستی پێ ناکەن');
INSERT INTO "bamoki" VALUES(16,46,'16:46','یان (سزا) بیان گرێت لە کاتی گەڕان و ھاتوچۆدا ئیتر ناتوانن خۆیان قوتارکەن');
INSERT INTO "bamoki" VALUES(16,47,'16:47','یان لە کاتێکدا دەترسن (لە سزادان) لەناویان دەبات بێگومان پەروەردگارتان زۆر دڵسۆز و میھرەبانە');
INSERT INTO "bamoki" VALUES(16,48,'16:48','ئایا ئەوانە نابینن و سەرنج نادەن کە ھەرچی خوا دروستی کردووە سێبەرەکانیان دەگۆڕێت بەلای ڕاست و چەپیاندا بە ملکەچیەوە سوژدە بۆ خوا دەبن');
INSERT INTO "bamoki" VALUES(16,49,'16:49','ھەرچی لە ئاسمانەکان و زەویدایە ھەر بۆ خوا سوژدە دەبەن لە گیانلەبەران و فریشتەکان لەھەمان کاتدا خۆشیان بە گەورە دانانێن');
INSERT INTO "bamoki" VALUES(16,50,'16:50','لە پەروەردگاریان دەترسن کە لەلای سەروویانەوەیە و ھەر فەرمانێکیان پێ بکرێت دەیکەن');
INSERT INTO "bamoki" VALUES(16,51,'16:51','خوا فەرمووی دوو پەرستراو بۆ خۆتان دامەنێن بێگومان پەرستراوی (ئێوە) تەنھا یەکێکە (کە خوای گەورە و باڵادەستە) کەواتە ھەر لە من بترسن');
INSERT INTO "bamoki" VALUES(16,52,'16:52','وە تەنھا موڵکی خوایە ئەوەی لە ئاسمانەکان و لە زەویدایە وە گوێڕایەڵی بەردەوام ھەر بۆ ئەوە دەی ئایا لە غەیری خوا دەترسن؟');
INSERT INTO "bamoki" VALUES(16,53,'16:53','وە ھەر ناز و نیعمەتێکتان ھەبێت ئەوە ھەمووی لە خواوەیە پاشان کاتێک دووچاری زیان و ناڕەحەتیەک ببن ئەوە پەناو ھاوار ھەر بۆلای ئەو دەبەن');
INSERT INTO "bamoki" VALUES(16,54,'16:54','لەپاشان کاتێک ناڕەحەتیەکەی لەسەر لابردن کتوپڕ دەستەیەک لە ئێوە ھاوبەش بڕیار دەدەن بۆ پەروەردگاریان ( و دەگەڕێنەوە سەر بێ باوەڕی)');
INSERT INTO "bamoki" VALUES(16,55,'16:55','(لێیان گەڕێ) با بێباوەڕ و سپڵە بن بەرامبەر ئەوەی پێمان بەخشین وە (لە دونیادا) ڕابوێرن جا لەمەو دوا دەزانن (چییان لێدەکەین)');
INSERT INTO "bamoki" VALUES(16,56,'16:56','وە (بت پەرستان) بۆ ئەو (بتانەی) کە ھیچ نازانن دایدەنێن بەشێک لەوەی کە ئێمە پێمان بەخشیوون (لە خواردن و خواردنەوە)');
INSERT INTO "bamoki" VALUES(16,57,'16:57','ئەو (بت پەرستانە) کچان بۆ خوا دادەنێن (و دەڵێن فریشتەکان کچی خوان) پاک و بێگەردی بۆ خوا، وە بۆ خۆشیان چییان پێ خۆشە (دایدەنێن کە حەزیان لە کوڕە)');
INSERT INTO "bamoki" VALUES(16,58,'16:58','ھەرکاتێک یەکێک لەوانە مزگێنی کچی پێ بدرێت ڕوی ڕەش دائەگەڕێت و بۆ خۆی پیش دەخواتەوە');
INSERT INTO "bamoki" VALUES(16,59,'16:59','خۆی دەشارێتەوە لە خەڵکی لەبەر ئەو مژدە خراپەی پێی دراوە ( بە گومانی خۆی) ئایا (ئەو کچە) بیھێڵێتەوە بەسەر شۆڕی (بەخێوی بکات) یان زیندە بەچاڵی کات بێداربن ئای چەند خراپە ئەو بڕیارەی دەیدەن!');
INSERT INTO "bamoki" VALUES(16,60,'16:60','بۆ ئەوانە ھەیە کە بڕوایان بە ڕۆژی دوایی نیە نموونەی خراپ نموونەی ھەرە بەرزیش ھەر بۆ خوایە وە ھەر ئەو (خوا) زاڵ و بەدەسەڵات و کاردروستە');
INSERT INTO "bamoki" VALUES(16,61,'16:61','وە ئەگەر خوا خەڵکی سزا بدایە بەھۆی ستەم (و بێ بڕوایی) یانەوە ھیچ گیان لەبەرێ (مرۆڤ) ێکی لەسەر (زەویدا) نەدەھێشت بەڵام دوایان دەخات تا کاتی دیاریکراو (بۆیان) جا کاتێک کاتی دیاریکراویان ھات (ڕۆژیان تەواو بوو) ھیچ کات و ساتێک دواناکەون و پێشیش ناکەون');
INSERT INTO "bamoki" VALUES(16,62,'16:62','وە ئەوان شتێ بۆ خوا دادەنێن کە خۆیان حەزی لێ ناکەن وە زمانیان بەدرۆ دەڵێ کە بێگومان پاداشتی زۆر چاک ھەر بۆ ئەوانە ڕاستە وگومانی تێدا نیە (پاداشتی) ئەوانە ھەر ئاگری (دۆزەخ) ـە و زوو بە پەلە دەبرێن (بۆی)');
INSERT INTO "bamoki" VALUES(16,63,'16:63','سوێند بە خوا بەڕاستی پێغەمبەرانمان ناردووە بۆ سەر گەلانی پێش تۆ جاشەیتان کردەوە (خراپەکان) یانی بۆ ڕازاندنەوە بۆیە ئەمڕۆ (شەیتان) پشتیوانیانە وە بۆ ئەوانە سزای سەختی بە ئازار ھەیە');
INSERT INTO "bamoki" VALUES(16,64,'16:64','وە ئێمە قورئانمان نەناردۆتە خوارەوە بۆت مەگەر بۆ ئەوەی ڕوونی بکەیتەوە بۆ خەڵکی ئەوەی کە تیایدا جیاواز بوون وە تاڕێنمونی و میھرەبانیش بێت بۆ کەسانێک کە باوەڕ دەھێنن');
INSERT INTO "bamoki" VALUES(16,65,'16:65','وە خوا لە ئاسمانەوە بارانی باراندووە جا بەو (بارانە) زەوی زیندوو کردەوە دوای ئەوەی کە مرد بوو بەڕاستی لەمەدا نیشانە و بەڵگەیەکی ڕوون ھەیە بۆ کەسانێک کە (ڕاستی) دەبیستن');
INSERT INTO "bamoki" VALUES(16,66,'16:66','وە بەڕاستی لە ئاژەڵ و ماڵاتیشدا بۆ ئێوە پەند و ئامۆژگاری ھەیە لەوەی لە ناو سکیدایە بە ئێوەی دەنۆشین لە نێوان سڕوان (پیسی) و خوێندا شیرێکی پاکی بێ خەوشتان پێ دەنوشێت');
INSERT INTO "bamoki" VALUES(16,67,'16:67','وە لەبەرو بوومەکانی دارخورما و ڕەزەکانیش (خواردنەوەی) سەر خۆشکەری لێ دروست دەکەن وە ڕِۆزی و بژێوی پاک و چاکیشی (لێ دروست دەکەن وەک مێوژ و دۆشاو) بەڕاستی لەوەدا نیشانە ھەیە بۆ کەسانێک کە تێدەگەن');
INSERT INTO "bamoki" VALUES(16,68,'16:68','وە پەروەردگارت سرووش (ئیلھامی) کرد بۆ ھەنگ کە لە چیاکاندا خانوو (بۆ خۆت) ساز بدە و لە (کونی) دارو درەختەکان لەو (شوێنانەی) کە خەڵکی دروستی دەکەن (بۆی)');
INSERT INTO "bamoki" VALUES(16,69,'16:69','پاشان لە ھەموو بەرو بوومێک بمژە ئەوسا ئەو ڕێگایانەی پەروەردگارت بگرە بەر بە ملکەچی لە سکی ئەو (ھەنگانەوە) دێتە دەر خواردنەوەیەک (ھەنگوین) کە ڕەنگەکانی جیاوازە کە شیفای تێدایە بۆ خەڵکی بێگومان لەوەدا نیشانەی گەورە ھەیە بۆ کەسانێک کەبیر دەکەنەوە');
INSERT INTO "bamoki" VALUES(16,70,'16:70','بێگومان خوا ئێوەی دروست کردووە و پاشان دەتان مرێنێت وە ھەندێک لە ئێوە (زۆر پیر دەبێ تا) دەگەڕێنرێتەوە بۆ لاواز ترینی تەمەن بۆ ئەوەی لە پاش زانین ھیچ شتێک نەزانێت (ھەموو شتێکی لەبیر بچێتەوە) بەڕاستی خوا زانا و بەتوانایە');
INSERT INTO "bamoki" VALUES(16,71,'16:71','وە خوا فەزڵی ھەندێکتانی داوە بەسەر ھەندێکتاندا لە (پێدانی) ڕزق و ڕۆزیدا جا ئەوانەی کە فەزڵی خودایان بەسەرەوەیە و ڕزق و ڕۆزیان زۆرە بەشی بەندەکانی لێ نادەن تا بەھۆی ئەوەوە ئەوان (بەندەکان) و ئەمان یەکسان بن تێیدا دەی ئایا ئەوانە ئینکاری چاکە و نیعمەتەکانی خوا دەکەن؟');
INSERT INTO "bamoki" VALUES(16,72,'16:72','وە خوا لە ڕەگەزی خۆتان ھاوسەری بۆ بەدی ھێناون وە لە ژن و ھاوسەرەکانتان کوڕ و نەوەی بۆ فەراھەم ھێناون وە ڕزق و ڕۆزی پاک و خۆشی پێدا وون دەی ئایا ئەوان ھەر بە ناھەق و بەتاڵ باوەڕ دێنن وە بە چاکەو نیعمەتەکانی خوایش بێ باوەڕ و سپڵەن');
INSERT INTO "bamoki" VALUES(16,73,'16:73','ئەوان لە جیاتی خوا شتانێک دەپەرستن کە خاوەنی ھیچ ڕزق و ڕۆزیەکی ئەوان نین نە لە ئاسمانەکان و نە لە زەویدا و ناشتوانن (ڕۆزیان بدەن)');
INSERT INTO "bamoki" VALUES(16,74,'16:74','کەواتە وێنە بۆ خوا مەھێننەوە بەڕاستی خوا دەزانێت و ئێوە نازانن');
INSERT INTO "bamoki" VALUES(16,75,'16:75','خوا نموونەی ھێناوەتەوە بە بەندەیەکی خاوەندار کە توانای بەسەر ھیچ شتێکدا نیە لەگەڵ کەسێکی (باوەڕدار) کە ڕزق و ڕۆزی چاکمان پێ دابێت لە لایەن خۆمانەوە ئەویش لەو (ڕۆزیەی) ببەخشێت بەنھێنی و ئاشکرا ئایا ئەو دووانە یەکسانن سوپاس و ستایش بۆ خوا بەڵام زۆربەیان نازانن');
INSERT INTO "bamoki" VALUES(16,76,'16:76','وە ھەروەھا خوا نموونەی ھێناوەتەوە بەدوو پیاو یەکێکیان لاڵێکی زگماکە کە توانای بەسەر ھیچ شتێکدا نیە و ئەو (پیاوە) ئەرک و بارە بەسەر خاوەنەکەیەوە بەرەو ھەرشوێنێکی بنێرێت ھیچ خێرو چاکەیەک ناھێنێتەوە ئایا ئەو کەسە یەکسانە لەگەڵ کەسێک فەرمان بەدادگەری بدات و ئەو (کەسە) لەسەر ڕێگەیەکی ڕاست بێت');
INSERT INTO "bamoki" VALUES(16,77,'16:77','وە ھەر بۆ خوایە نھێنی و پەنھانیەکانی ئاسمانەکان و زەوی وە ھاتنی ڕۆژی دوایش (بەلای خواوە) ھەر وەکو چاو تروکانێک وایە، یان نزیک تر لەوەیش بەڕاستی خوا بەسەر ھەموو شتێکدا بەتوانایە');
INSERT INTO "bamoki" VALUES(16,78,'16:78','خوا ئەو خوایە یە کە ئێوەی لە سکی دایکتان دەرھێناوە کە ھیچتان نەدەزانی وە (خوا) گوێ و چاو و دڵی پێدان بۆ ئەوەی سوپاسی (خوا) بکەن');
INSERT INTO "bamoki" VALUES(16,79,'16:79','ئایا ئەوان سەرنجی باڵندەیان نەداوە؟ کە چۆن لە ھەوای ئاســماندا ڕام ھێنراون بێجگـە لە خوا کێ ڕایان دەگرێت (بەو ئاسمانەوە) بەڕاسـتی لەو (ڕاگیرکردنە) دا چەند نیشـانە و بەڵگە ھەیە بۆ ئەوانەی باوەڕ دەھێنن');
INSERT INTO "bamoki" VALUES(16,80,'16:80','وە خوا ماڵەکانتانی کردووە بەجێی دانیشتن و حەوانەوە, وە لە پێستی ئاژەڵیش دەوار و ڕەشماڵی بۆ فەراھەم ھێناون (ھەڵدەدرێت و دەگوێزرێتەوە) بە ئاسانی لە ڕۆژی کۆچ و بارکردنتاندا و لە ڕۆژی مانەوەو نیشتەجێ بونتاندا وە (سازاندویەتی بۆتان) لە خوری و کوڵکی (ئەو ئاژەڵانە) وە لە مـووەکانیان شــتومەکی ناوماڵ کەکەڵکی لێ وەردەگرن تا ماوەیەک');
INSERT INTO "bamoki" VALUES(16,81,'16:81','وە خوا لەوەی کە دروستی کردوە سێبەری زۆری ڕەخساندووە بۆتان وە لە چیاکاندا پەناگا و ئەشکەوتی بۆ بەدی ھێناون وە سازیداوە بۆتان جل و پۆشاکێک کە دەتان پارێزێ لە گەرما ھەروەھا جل و پۆشاکێک کە لەکاتی جەنگدا دەتان پارێزێت (لە دوژمنان) ئا بەو جۆرە چاکە و نیعمەتەکانی خۆی تەواو دەکات بەسەرتانەوە بۆ ئەوەی ملکەچ و موسوڵمان بن');
INSERT INTO "bamoki" VALUES(16,82,'16:82','ئەوسا ئەگەر ئەوان ڕوویان وەرگێڕا ئەوە بێگومان ئەوەی لەسەر تۆیە تەنھا ڕاگەیاندنی ئاشکرای (ئاینەکەتە)');
INSERT INTO "bamoki" VALUES(16,83,'16:83','ئەوانە ناز و نیعمەتەکانی خوا دەناسن و (دەزانن) پاشان ئینکاری دەکەن وە زۆربەی ئەوانە ھەر بێ باوەڕن');
INSERT INTO "bamoki" VALUES(16,84,'16:84','وە ڕۆژێک دادێت ئێمە لەھەر ئوممەتێک شایەتێک (پێغەمبەرێک) یان بۆ دەنێرین ئەوسا مۆڵەت نادرێت بەوانەی باوەڕیان نەھێناوە بیانوشیان لێ وەرناگیرێت');
INSERT INTO "bamoki" VALUES(16,85,'16:85','وە کاتیێک ئەوانەی ستەمیان کردوە و سزای دۆزەخ دەبینن نە سزاکەیان لەسەر سووک دەکرێت نە مۆڵەتیش دەدرێن');
INSERT INTO "bamoki" VALUES(16,86,'16:86','وە کاتێک ئەوانەی کە ھاوبەشیان داناوە پەرستراوەکانیان دەبینن دەڵێن ئەی پەروەردگارمان ئەوانە پەرستراوەکانمانن کە ئێمە ھاوارمان لێ ئەکردن لەجیاتی تۆ ئەمجا ئەو (پەرستراوانە) ووتەیان ئاڕاستە دەکەن (و دەڵێن) بەڕاستی ئێوە درۆزنن');
INSERT INTO "bamoki" VALUES(16,87,'16:87','وە لەو ڕۆژەدا ھەموو گوێڕایەڵی و ملکەچی ئەنوێنن بۆ خوا وە وون دەبێت لێیان ئەو درۆیانەی کە ھەڵیان دەبەست (لە دونیادا)');
INSERT INTO "bamoki" VALUES(16,88,'16:88','ئەوانەی کە بێ باوەڕ بوون و ڕێگری خەڵکیشیان کردووە لە ڕێگەی خوا جگە لە سزاکەی خۆیان سزایان بۆ زیاد دەکەین لەبەر ئەوەی کە خراپەکاریان دەکرد');
INSERT INTO "bamoki" VALUES(16,89,'16:89','وە ئەو ڕۆژەی کە ئێمە لە ھەر ئوممەتێک شایەتێک (پێغەمبەرێک) دەنێرین بۆسەریان لە خۆیان (کە شایەتیان لێ بدات) وە تۆش دەھێنین (ئەی موحەممەد صلى الله عليه وسلم) بەشایەت لەسەر ئەوانە وە ئێمە قورئانمان ناردوەتە خوارەوە بۆت کە ڕوون کەرەوەیە بۆ ھەموو شتێک وەڕێنمونی و میھرەبانی و مژدەیە بۆ موسڵمانان');
INSERT INTO "bamoki" VALUES(16,90,'16:90','بەڕاستی خوا فەرمان دەدات بە دادگەری و بە چاکە و بە بەخشین بە خزم و خوێش وە (خوا) ڕێگری دەکات لە داوێن پیسی و خراپە و دەستدرێژی ئامۆژگاریتان دەکات بۆ ئەوەی پەند وەرگرن و بیربکەنەوە');
INSERT INTO "bamoki" VALUES(16,91,'16:91','وە بەڵێنی خوا بەجێ بھێنن کاتێک کە بەڵێن دەدەن و بەڵێن و پەیمانەکانتان مەشکێنن لە پاش بەستن و دووپاتکردنەوەیان لە کاتێکدا خواتان کردووە بەشایەت و چاودێر بەسەر خۆتانەوە بەڕاستی ھەرچی دەکەن خوا دەیزانێت');
INSERT INTO "bamoki" VALUES(16,92,'16:92','وەک ئەو (ئافرەتە) مەبن کە ڕیسەکەی کردەوە بە خوری لە پاش بادان و ڕستن و شەتەک دانی سوێندو پەیمانەکانتان دەکەن بەھۆی ناپاکی و فڕو فێڵ لە نێوانتاندا لەبەر ئەوەی دەستە و کۆمەڵێک زۆرترو بەھێز ترە لەدەستەو کۆمەڵێکی تر خوا بەوە تاقیتان دەکاتەوە, وە سوێند بە خوا لە ڕۆژی دواییدا بۆتان ڕوون دەکاتەوە ئەوەی کە ئێوە تیایدا جیاواز بوون');
INSERT INTO "bamoki" VALUES(16,93,'16:93','وە ئەگەر خوا بیویستایە ئێوەی دەکرد بە یەک ئوممەت بەڵام (خوا) ھەر کەسێکی بوێت گومڕای دەکات (بەھۆی سەرکەشی خۆیەوە) ھەر کەسێکیشی بوێت ڕێنمونی دەکات وە سوێند بە خوا پرسیارتان لێ دەکرێت لەوەی کە دەتانکرد (لە دونیادا)');
INSERT INTO "bamoki" VALUES(16,94,'16:94','وە سوێند و پەیمانتان مەکەن بەھۆی غەدر و ناپاکی لە نێوان یەکتردا تا پێ لەپاش جێگیر بوونی (لەسەر باوەڕ) ھەڵنەخلیسکێ و سزاو ناڕەحەتی نەچەژن بەھۆی بەرھەڵستی کردنتان لە ڕێبازی خوا (ئەگەر وابن) سزای زۆر گەورەتان بۆ دەبێت');
INSERT INTO "bamoki" VALUES(16,95,'16:95','وە بەڵێن و پەیمانی خوا بەنرخێکی کەم مەگۆڕنەوە بەڕاستی ئەوەی کەلای خوا ھەیە چاکترە بۆتان ئەگەر ئێوە بزانن');
INSERT INTO "bamoki" VALUES(16,96,'16:96','(چونکە) ھەرچی لای ئێوەیە تەواو دەبێت ئەفەوتێت و نامێنێت بەڵام ئەوەی لای خوایە ھەر دەمێنێت سوێند بە خوا ئەوانەی کە بەئارام بوون پاداشتیان دەدەینەوە بە پاداشتی چاکتر لە کردەوەکانیان');
INSERT INTO "bamoki" VALUES(16,97,'16:97','ھەرکەس لە نێر یان لە مێ کردەوەی چاکی کردبێت و بیکات و بڕواداریش بوو بێت ئەوە سوێند بە خوا بە ژیانێکی خۆش دەیان ژیەنین و سوێند بێ پاداشتیان بدەینەوە بە چاکتر لەوەی کە ئەوان دەیانکرد');
INSERT INTO "bamoki" VALUES(16,98,'16:98','(ئەی موحەممەدصلى الله عليه وسلم) کاتێک ویستت قورئان بخوێنیت پەنا بگرە بە خوا لە شەیتانی نەفرەت لێکراو و دەرکراو لەبەزەیی خوا');
INSERT INTO "bamoki" VALUES(16,99,'16:99','بەڕاستی ئەو (شەیتان) دەسەڵاتی نیە بەسەر ئەوانەی کە باوەڕیان ھێناوە وتەنھا پشت بە پەروەردگاریان دەبەستن');
INSERT INTO "bamoki" VALUES(16,100,'16:100','تەنھا دەسەڵاتی بەسەر ئەوانەدا ھەیە کە ئەو (شەیتان) دەکەنە دۆست پشتیوانی خۆیان و ئەوانەی بەھۆی ئەوەوە بوونەتە ھاوەڵدانەر');
INSERT INTO "bamoki" VALUES(16,101,'16:101','ھەر کاتێک ئایەتێک لە جێی ئایەتێکی تردا دابەزێنین وە خوا چاکتر دەزانێت چی دەنێرێتە خوارەوە دەڵێن تۆ لە خۆتەوە درۆ ھەڵدەبەستی بە ناوی خواوە (وانیە) بەڵکو زۆر بەیان ھیچ نازانن');
INSERT INTO "bamoki" VALUES(16,102,'16:102','بڵێ جوبرەیل دایبەزاندوە لە لایەن پەروەردگارتەوە بەڕاستی تا ئەوانەی باوەڕیان ھێناوە دامەزراویان بکاتو ڕێنمونی و موژدەش بێت بۆ موسوڵمانان');
INSERT INTO "bamoki" VALUES(16,103,'16:103','سوێند بە خوا بێگومان ئێمە دەزانین کەئەو (بێ باوەڕانە) دەڵێن ھەر ئادەمیە (ئەم قورئانەی) فێری (موحەممەد) دەکات (مەبەستیان کابرایەکی ڕۆمی بوو) کەچی زمانی ئەو کەسەی ئەوەی دەدەنەپاڵ عەجەمیە و ئەم (قورئانە) ش به زمانی عەرەبی پاراو و ڕوون (دابەزیوە)');
INSERT INTO "bamoki" VALUES(16,104,'16:104','بەڕاستی ئەوانەی کە بڕوایان بە ئایەتەکانی خوا نیە خوای گەورە ڕێنمونیان ناکات، وسزای سەختی بۆ ئامادەکردوون');
INSERT INTO "bamoki" VALUES(16,105,'16:105','بێگومان تەنھا ئەوانە درۆھەڵدەبەستن بە ناوی خواوە کە باوەڕیان بە ئایەتەکانی خوا نیە و ھەر خۆیان درۆزنن');
INSERT INTO "bamoki" VALUES(16,106,'16:106','ئەو کەسەی بێ باوەڕ بێ بە خوا لە پاشان باوەڕھێنانی مەگەر کەسێک زۆری لێ کرا بێت و دڵیشی دامەزرا و ئارام بێت بە باوەڕ بەڵام کەسێ دڵی خۆش و شادمان بێت بەبێ باوەڕی ئەوە بەر خەشم و قینی خوا کەوتوون و سزای گەورەیان دەبێت');
INSERT INTO "bamoki" VALUES(16,107,'16:107','ئەم (ھەڕەشەیە) لەبەر ئەوەیە بەڕاستی ئەوانە ژیانی دونیا یان لا خۆشەویستر بوو لە ژیانی ڕۆژی دوایی وە بەڕاستی خوا ڕێنمونی کەسانی بێ باوەڕ ناکات');
INSERT INTO "bamoki" VALUES(16,108,'16:108','ئەوانە کەسانێکن خوا مۆری ناوە بەسەر دڵ و بەسەر گوێ و چاویاندا وە ھەر ئەوانەن بێ ئاگایان');
INSERT INTO "bamoki" VALUES(16,109,'16:109','ڕاستە و بێگومان ئەوانە لە ڕۆژی دواییدا ھەر ئەوان زەرەرمەندن');
INSERT INTO "bamoki" VALUES(16,110,'16:110','پاشان بەڕاستی پەروەردگارت بۆ ئەوانەی کۆچیان کردوە لە پاش ئەوەی سزا دران پاشان جیھاد و تێکۆشانیان کرد و بە ئارام بوون بێـگومان پەروەردگارت لە دوای ئەوە لێ بوردە و میھرەبانە (بۆیان)');
INSERT INTO "bamoki" VALUES(16,111,'16:111','لە ڕۆژێکدا کە ھەموو کەسێک دێت بەرگری لە خۆی دەکات وە ھەموو کەسێک ھەرچی کردبێت پاداشتی تەواوی وەردەگرێ و ئەوان ستەمیان لێ ناکرێت');
INSERT INTO "bamoki" VALUES(16,112,'16:112','خوا نموونەی ھێناوەتەوە بە (خەڵکی) گوند و شارێک کە ھێمن و دڵنیا بوون ڕزق و ڕۆزی زۆری بۆ دەھات لە ھەموو شوێنێکەوە کەچی بەرانبەر چاکە و نیعمەتەکانی خوا ناسوپاس و سپڵە بوون جا خوا بەو ھۆیەوە کردی بەبەر (خەڵکە کە) یدا پۆشاکی برسێـتی و ترس و بیم لەبەر ئەو کردەوانەی کە دەیانکرد');
INSERT INTO "bamoki" VALUES(16,113,'16:113','سوێند بە خوا بەڕاستی پێغەمبەرێکیان لە خۆیان بۆ ھات کەچی باوەڕیان پێ نەھێنا جا سزا گرتنی لە کاتێکدا ستەمکاربوون');
INSERT INTO "bamoki" VALUES(16,114,'16:114','جا لەو بژێویەی خوا پێی بەخشیوون بخۆن کە حەڵاڵ و پاکە و سوپاسی چاکە و نیعمەتی خوا بکەن ئەگەر ئێوە ھەر ئەو دەپەرستن');
INSERT INTO "bamoki" VALUES(16,115,'16:115','بێگومان (خوا) ئەوەی حەرامی کردووە لەسەرتان تەنھا مردارەوە بوو وخوێن و گۆشتی بەراز و ئەوەی (کەسەربڕاوە و) ناوی غەیری خوای لێ ھێنراوە بەڵام ھەر کەسێک ناچار بوو (لەو حەرامانە بخوات) بە بێ دەست درێژی و زیادەڕۆی ئەوە بێگومان خوا لێ بوردە و میھرەبانە');
INSERT INTO "bamoki" VALUES(16,116,'16:116','وە لە خۆتانەوە نەڵێن ئەوەی کە بەزمانتاندا دێت بە درۆ ئەوە حەڵاڵە و ئەوە حەرامە بۆ ئەوەی درۆ ھەڵبەستن بەدەم خواوە بەڕاستی ئەوانەی درۆ ھەڵدەبەستن بە ناوی خواوە ڕزگار نابن');
INSERT INTO "bamoki" VALUES(16,117,'16:117','(لە دونیادا) ڕابورادنێکی کەم (یان دەبێت) (لە دوا ڕۆژدا) سزای سەختیان بۆ ھەیە');
INSERT INTO "bamoki" VALUES(16,118,'16:118','وە لەسەر ئەوانەی کە جولەکە بوون حەراممان کرد (لێیان) ئەوەی کە باسمان کردوە بۆت لە پێش (دابەزینی ئەم ئایەتە) وە ئێمە ستەممان لێ نەکردن بەڵکو خۆیان ستەمیان لە خۆیان دەکرد');
INSERT INTO "bamoki" VALUES(16,119,'16:119','پاشان بەڕاستی پەروەردگارت بۆ ئەوانەی خراپەیان کردووە بەنەفامی پاشان دوای ئەوە گەڕاونەتەوە و تۆبەیان کردووە و کرداری خۆیان چاک کردووە بەڕاستی پەروەردگارت لە پاش ئەوانە لێبوردە و میھرەبانە');
INSERT INTO "bamoki" VALUES(16,120,'16:120','بەڕاستی ئیبراھیم (بۆ خۆی) ئوممەتێک بوو گوێڕایەڵ و فەرمانبەردار بۆ خوا لا دەربوو لە ھەموو ئاینێکی ناھەق و ھەرگیز لە ھاوبەشدانەران نەبووە');
INSERT INTO "bamoki" VALUES(16,121,'16:121','شوکرانە بژێری (خوا) بووە لە بەرامبەر نازو نیعمەتەکانیدا ئەویش ھەڵیبژارد و ڕێگەی ڕاستی نیشاندا (کە ئیسلامە)');
INSERT INTO "bamoki" VALUES(16,122,'16:122','وە ئێمە لە دونیادا ناز و نیعمەتی چاکمان پێ بەخشی وە بەڕاستی ئەو لە ڕۆژی دوایشدا لە ڕیزی چاکاندایە');
INSERT INTO "bamoki" VALUES(16,123,'16:123','پاشان نیگامان بۆ کردیت کە پەیڕەوی ئاینی ئیبراھیم بکە چونکە پاک و ڕاست بوو بەھیچ جۆرێک لە ھاوبەشدانەران نەبوو');
INSERT INTO "bamoki" VALUES(16,124,'16:124','بێگومان (ڕێزگرتنی) ڕۆژانی شەممە تەنھا لەسەر ئەوانە دانرا کە جیاواز بوون تیایدا وە بەڕاستی پەروەردگارت دادوەری دەکات لە نێوانیاندا لە ڕۆژی دواییدا لەوەی کە ئەوان تیایدا جیاواز بوون');
INSERT INTO "bamoki" VALUES(16,125,'16:125','(خەڵکی) بانگ بکە بۆ ڕێبازی پەروەردگارت بە دانایی و بە ئامۆژگاری جوان و چاک و شیرین وە لەگەڵیان بدوێ (موجادەلە بکە) بەچاکترین شێوە بێگومان پەروەردگارت خۆی چاک دەزانێت بەوانەی گومڕا بوون و لایاندا وە لە ڕێگاکەی وە ھەر خۆشی زانایە بەوانەی لەسەر ڕێگای ڕاستن');
INSERT INTO "bamoki" VALUES(16,126,'16:126','وە ئەگەر تۆڵەتان سەندەوە ئەوە بە ئەندازەی ئەو ستەمەی بەرامبەرتان کراوە تۆڵە بکەنەوە, وە ئەگەر ئارام بگرن ئەوە باشترە بۆ ئارام گران');
INSERT INTO "bamoki" VALUES(16,127,'16:127','وە (ئەی موحەممەد صلى الله عليه وسلم) دان بە خۆتدا بگرە ئارام گرتنەکەشت ھەر بە (یارمەتی) خوایە و خەفەتیان بۆ مەخۆ و بەو فێڵ و پیلانانەش دەیکەن دڵ تەنگ مەبە');
INSERT INTO "bamoki" VALUES(16,128,'16:128','بەڕاستی خوا لەگەڵ ئەوانەیە کە خۆیان پاراستووە (لە تاوان) ھەروەھا لەگەڵ ئەوانەشدایە کە خێرخواز و چاکەکارن');
INSERT INTO "bamoki" VALUES(17,1,'17:1','پاک و بێگەردی بۆ ئەوزاتەی کە شەوڕەوی کرد بە بەندەی خۆی لە شەوێکدا لە مزگەوتی حەرامەوە (لە شاری مەککە) بۆ مزگەوتی (الاقێی) لە (بیت المقدس) کە دەور و بەریمان پیرۆز و فەڕدار کردووە بۆ ئەوەی ھێندێک لە بەڵگە و نیشانە ڕوونەکانی خۆمانی نیشان بدەین بەڕاستی ھەر ئەو بیسەر و بینایە');
INSERT INTO "bamoki" VALUES(17,2,'17:2','وە تەوراتیشمان بەخشی بە موسا وە ئەو (تەوراتە) مان کردە ھۆی ڕێنمونی بۆ نەوەی ئیسرائیل (پێمان ووتن) کە بێجگە لە من کەس مەکەنە پشتیوانی خۆتان');
INSERT INTO "bamoki" VALUES(17,3,'17:3','(ئەی) نەوەی ئەو کەسانەی کە لەگەڵ نوح (لە کەشتیدا) ھەڵمان گرتن بەڕاستی نوح بەندە یەکی زۆر سوپاس گوزار بوو');
INSERT INTO "bamoki" VALUES(17,4,'17:4','نەوەکانی ئیسرائیلمان ئاگادار کردو ھەواڵمان پێدان لە نامەکەیاندا کە سوێند بە خوا ئێوە خراپەکاری دەکەن لە زەویدا دووجار و بێگومان بەرز دەبنەوە و زاڵ دەبن (بەسەر خەڵکیدا) بە بەرز بونەوەیەکی زۆر گەورە');
INSERT INTO "bamoki" VALUES(17,5,'17:5','جا کاتێک یەکەمین بەڵێن (لەو دووانە) ھات (کە بەڵێنتان پێدراوە) دەینێرینە سەرتان چەند بەندەیەکی خۆمان کە خاوەنی دەست وەشاندنێکی بەتینن لە جەنگدا ئەمجا ئەوانە دەگەڕێن بەناو ووڵاتەکەتاندا وە ئەوە بەڵێنێکی بەدی ھێنراو بوو');
INSERT INTO "bamoki" VALUES(17,6,'17:6','لە پاشان جارێکی تر ئێوە بەسەریاندا زاڵ دەکەینەوە, وە دارایی و کوڕی زۆرتان پێ دەبەخشین وە لە شکر و کەستان (لە جاری پێشوو) زۆرتر دەکەین');
INSERT INTO "bamoki" VALUES(17,7,'17:7','ئەگەر ئێوە چاکە بکەن ئەوا چاکە بۆ خۆتان دەکەن وە ئەگەر خراپەش بکەن ئەوا (سزاکەی) ھەر بۆ خۆتانە جا کاتێ بەڵێنی (سزا و تۆڵەی) دووھەم ھات بۆ ئەوەی پەژارە و خەفەت بە ڕوخسارتانەوە دەرکەوێت وە بۆ ئەوەی بچنەوە ناو مزگەوتی ئەقێاوە ھەرچۆنێک لە جاری یەکەمدا چوونە ناویەوە, وە بۆ ئەوەی وێرانی بکەن بە وێران کردنێکی گەورە');
INSERT INTO "bamoki" VALUES(17,8,'17:8','ئومێد وایە پەروەردگارتان ڕەحمتان پێ بکات (ئەگەر بگەڕێنەوە بۆلای خوا) ئەگەر بگەڕێنەوە (بۆ سەر تاوان) ئێمەش دەگەڕێینەوە (بۆ سزادانتان) وە دۆزەخمان کرد بە بەندیخانە بۆ بێ باوەڕان');
INSERT INTO "bamoki" VALUES(17,9,'17:9','بەڕاستی ئەم قورئانە ڕێنمونی دەکات بۆ ڕێباز و بەرنامەیەک کە ڕاسترینە و موژدەش دەدات بەو باوەڕدارانەی کە کردەوە چاکەکان دەکەن کە بەڕاستی پاداشتی گەورەیان بۆ ھەیە');
INSERT INTO "bamoki" VALUES(17,10,'17:10','وە بەڕاستی ئەوانەی کە باوەڕیان نیە بە ڕۆژی دوایی سزایەکی سەختمان بۆ ئامادەکردون');
INSERT INTO "bamoki" VALUES(17,11,'17:11','وە ئادەمی نزاو ھاوار بۆ شەڕ و خراپە دەکات (ھەروەکو) داوا و نزاکردنی بۆ خێرو چاکە وە ئادەمی ھەمیشە ھەر پەلەیەتی');
INSERT INTO "bamoki" VALUES(17,12,'17:12','شەو و ڕۆژمان کرد بە دوو نیشانەی (گەورەی دەسەڵاتی خوا) ئەمجا نیشانەی شەومان تاریک کرد (یا سڕیەوە) و بەڵگە و نیشانەی ڕۆژمان ڕووناک کردەوە بۆ ئەوەی بەشوێن ڕۆزی پەروەردگارتاندا بگەڕێن وە بۆ ئەوەی ژمارەی ساڵەکان و حیسابی (مانگ و شەو و ڕۆژەکان) بزانن وە ئێمە ھەموو شتێکمان بە ڕوون و ڕەوانی باسکردووە (بۆتان)');
INSERT INTO "bamoki" VALUES(17,13,'17:13','وە ھەموو مرۆڤێک (لە ڕۆژی دواییدا) کردەوەی خۆیمان کردۆتە گەردنیەوە (چاکە بێت یا خراپە) وە لە ڕۆژی دواییدا بۆی دەردەھێنن نامەیەک کە بە کراوەیی (و ھەموو کردەوەکانی تێدا نوسراوە) و دەیبینێت');
INSERT INTO "bamoki" VALUES(17,14,'17:14','(پێی دەووترێت) نامەکەت بخوێنەرەوە ئەمڕۆ خۆت بەسیت کە موحاسەبەی خۆت بکەیت');
INSERT INTO "bamoki" VALUES(17,15,'17:15','ھەرکەس ڕێنمونی و ھیدایەتی وەرگرتبێت ئەوە بێگومان ڕێنمونی و ھیدایەتەکەی (سوود و قازانجەکەی) ھەر بۆ خۆی دەبێت و ھەرکەسیش گومڕا بووبێت ئەوە بێگومان (زیان و سزاکەی) ھەر بۆ خۆی دەبێت وە ھیچ کەسێکی گوناھکار گوناھی ھیچ کەسێکی تر ھەڵناگرێت ئێمە ھەرگیز سزای (بەندەکان) نادەین ھەتا پێغەمبەر نەنێرین (بۆیان)');
INSERT INTO "bamoki" VALUES(17,16,'17:16','وە کاتێک بمانەوێت (دانیشتوانی) شار و گوندێک لەناو ببەین فەرمان دەدەین (بە گوێڕایەڵی و کردەوەی چاک) بە دەوڵەمەندە خراپەکارو زۆردارەکانی کەچی ئەوان یاخی دەبن و لە فەرمان دەردەچن جا بڕیاری (سزادان) پێویست دەبێت بۆ (دانیشتوانی) ئەو شار و گوندانە جا ئەوسا وێرانیان دەکەین بە وێران کردنێکی تەواو');
INSERT INTO "bamoki" VALUES(17,17,'17:17','وە زۆر (گەل و میللەت) مان لەناو برد لە سەردەمەکانی پاش نوح پەروەردگارت بەسە بە گوناھی بەندەکانی کە ئاگادار و بینایە');
INSERT INTO "bamoki" VALUES(17,18,'17:18','ھەر کەسێک تەنھا ژیانی دونیای بوێت لەم (دونیا) دا زوو پێی دەدەین ئەوەی بمانەوێت بۆ ھەرکەسێک لە پاشان دۆزەخمان بۆ سازداوە دەچێتە ناوی بە لۆمەکراوی و دەرکراوی لە میھرەبانی خوا');
INSERT INTO "bamoki" VALUES(17,19,'17:19','ھەر کەسێکیش مەبەستی ڕۆژی دوایی بووبێت وە ھەوڵی بۆ بدات بەو جۆرەی شایستەی ئەوە و باوەڕداریش بێت ئا ئەوانە کردەوە و تێکۆشانەکەیان وەرگیراوە (لای پەروەردگاریان)');
INSERT INTO "bamoki" VALUES(17,20,'17:20','دەبەخشین بە ھەمووان ئەوانەی (کەھەر دونیایان دەوێت) و ئەوانەش (کە ڕۆژی دواییان دەوێت) لە بەخششی پەروەردگارت (لەم دونیایەدا) بێگومان ڕۆزی و بەخشراوی پەروەردگارت ھەرگیز قەدەغە کراو نەبووە');
INSERT INTO "bamoki" VALUES(17,21,'17:21','(ئەی موحەممەد صلى الله عليه وسلم) سەرنج بدە چۆن (لە دونیادا) فەزڵی ھەندێکیانمان داوە بەسەر ھەندێکیاندا وە بێگومان پلەکان لە ڕۆژی دواییدا گەورەترە و فەزڵەکانیش گەورەترن');
INSERT INTO "bamoki" VALUES(17,22,'17:22','(ئەی ئادەمی) لەگەڵ خوادا پەرستراوێکی تر مەپەرستە ئەگینا دەبێتە لۆمەکراوێکی سووک و ڕسوا');
INSERT INTO "bamoki" VALUES(17,23,'17:23','وە پەروەردگارت بڕیاریداوە کە ھیچ شتێک نەپەرستن بێجگە لە ئەو وە لەگەڵ دایک و باوکتان چاکە بکەن ئەگەر یەکێکیان (باوکت و دایکت) یان ھەردووکیان لای تۆ پیربوون قسەی ناخۆشیان پێ مەڵێ تەنانەت ئۆفیش وە تێیان مەخوڕە و پێیاندا ھەڵمەشاخێ (بەڵکو) ووتەی نەرم و شیرینیان (لەگەڵدا) بەکاربێنە');
INSERT INTO "bamoki" VALUES(17,24,'17:24','باڵی خۆ بەکەمزانین و میھرەبانیتیان بۆ ڕابخە بڵێ ئەی پەروەردگارم ڕەحمیان پێ بکە ھەرچۆنێک ئەوان منیان پەروەردە کرد بە منداڵی');
INSERT INTO "bamoki" VALUES(17,25,'17:25','پەروەردگارتان چاک دەزانێت بە ھەرچی لە دڵ و دەرونتاندا ھەیە ئەگەر ئێوە چاک بن و نیازی چاکەتان ببێت ئەوا بێگومان ئەو (خوایە) لێ بوردەیە بۆ تۆبەکاران');
INSERT INTO "bamoki" VALUES(17,26,'17:26','وە مافی خزمانت بدە (ھەروەھا مافی) ھەژار و ڕێبواریش زیادە ڕەویش مەکەن');
INSERT INTO "bamoki" VALUES(17,27,'17:27','بەڕاستی زیادەڕەوان دۆست وبرای شەیتانەکانن شەیتانیش بەرانبەر (ناز و نیعمەتەکانی) پەروەردگاری ھەمیشە سپڵەیە');
INSERT INTO "bamoki" VALUES(17,28,'17:28','وە ئەگەر ڕووت وەرگێڕا لەوانە (لەبەر نەداری) چاوەڕوانی بەخششی و ڕۆزیەک بوویت لەلایەن پەروەردگارتەوە ئەوە قسەی جوان و نەرمیان لەگەڵدا بکە');
INSERT INTO "bamoki" VALUES(17,29,'17:29','وە دەستت مەکە کۆت بە گەردنتدا (واتە دەست قووچاو مەبە لە خێرکردندا) وکراوە و بەرەڵاشی مەکە بەتەواوی (ھەرچیت ھەیە بیدەی بە خەڵکی) تا نەبیتە کەسێکی لۆمەکراوی ھیچ بەدەستەوە نەماو');
INSERT INTO "bamoki" VALUES(17,30,'17:30','بەڕاستی پەروەردگارت ڕزق و ڕۆزی فراوان دەکات بۆ ھەر کەسێک کە بیەوێت و وەتەنگی دەکات بۆ ھەرکەسێک بیەوێت بەڕاستی ئەو ھەمیشە ئاگا و بینایە بە بەندەکانی');
INSERT INTO "bamoki" VALUES(17,31,'17:31','وە ڕۆڵەکانتان مەکوژن لە ترسی ھەژاری و نەداری ئێمە ڕۆزی ئەوانیش و ئێوەش دەدەین بەڕاستی کوشتنی ئەوانە (ئەو منداڵانەتان) ھەڵەو تاوانێکی گەورەیە');
INSERT INTO "bamoki" VALUES(17,32,'17:32','و نزیکی زیناش مەکەونەوە بەڕاستی ئەوە کردەوەیەکی زۆر ناشرین و ڕێگەیەکی پیس و خراپە');
INSERT INTO "bamoki" VALUES(17,33,'17:33','وە کەسێک مەکوژن کە خوا کوشتنی ئەوی حەرام کردوە مەگەر بە (ھۆیەکی) ڕاست و ڕەوا نەبێت (وەک ئەگەر ھاتوو کەسێکی کوشتبوو، یان لە ئاین پاشگەز ببوەوە، یان شووی کردبوو یان ژنی ھێنابوو زینایان کرد) جا ھەر کەسێک بەنا ھەق و ستەم لێکراوی کوژرا ئەوە دەسەڵاتمان داوە بەخاوەنەکەی (بەسەر بکوژەکەدا) جا با خاوەنەکەی زیادەڕۆیی نەکات لە تۆڵەسەندنەوەدا (ئەگەر عەفووی کرد پاش ئەوە نەچێت تۆڵەی لێ بسەنێتەوە، وە ئەگەر کوشتیشیەوە بە جوانترین شێوە بیکوژێتەوە و ڕازی بێت بە بڕیاری خوا ھەرچۆنێک بێت) چونکە بەڕاستی ئەو (خاوەنە) یارمەتیدراوە');
INSERT INTO "bamoki" VALUES(17,34,'17:34','وە نزیکی ماڵی ھەتیو مەکەونەوە مەگەر بە چاکترین شێوە نەبێت (کە بریتیە لە پاراستن و وەبەرھێنانی) ھەتا (ھەتیوەکە) دەگاتە کەماڵی ژیری و لێزانی وەفا بکەن بە بەڵێن و پەیمانەکانتان بەڕاستی (خاوەن) بەڵێن و پەیمان لێپرسراوە بەرامبەر بەڵێنەکانی');
INSERT INTO "bamoki" VALUES(17,35,'17:35','وە کاتێک کە پێوانە دەکەن (بۆ خەڵکی) بەڕێکوپێکی پێوانە بکەن و کێشانەش بکەن بە تەرازوی ڕاست و دروست ئەمە (بۆ ئێوە) چاکتر و ئاکامیشی باشترە ( لە دونیا و قیامەتیشدا)');
INSERT INTO "bamoki" VALUES(17,36,'17:36','وە شوێن شتێک مەکەوە کە زانیاریت دەربارەی نیە بەڕاستی گوێ و چاو و دڵ ھەموو ئەوانە (خاوەنەکانیان) بەرپرسیارن لێیان');
INSERT INTO "bamoki" VALUES(17,37,'17:37','وە مەڕۆ بە زەویدا بە فیز زلی و شانازیەوە (چونکە) بەڕاستی تۆ نە دەتوانی زەوی کون کەیت وە نە دەگەیتە شاخەکان لە درێژیدا');
INSERT INTO "bamoki" VALUES(17,38,'17:38','ھەموو ئەوەی باسکرا خراپەکانی لای پەروەردگارت نابەجێ و بێزراوە');
INSERT INTO "bamoki" VALUES(17,39,'17:39','ئەوەی (کـەباس کرا) ھەندێکە لەو دانـایی و حیکمـەتەی کە پەروەردگارت نیگای کردووە بۆت وە ھـیچ پـەرستراوێکی تر لەگەڵ خوادا مەپەرستە ئەگینا فڕێ دەدرێیتە ناو دۆزەخەوە بە لۆمەکراوی و دوورخراوەی لە بەزەیی خوا');
INSERT INTO "bamoki" VALUES(17,40,'17:40','ئایا پەروەردگارتان ڕەگەزی کوڕی تایبەتی کردووە بە ئێوەوە لە فریشتەکانیشیدا کچانی بۆ خۆی ھەڵبژاردووە بەڕاستی ئێوە وتەیەکی زۆرگەورە (و نابەجێ) دەڵێن');
INSERT INTO "bamoki" VALUES(17,41,'17:41','وە سوێند بە خوا بێگومان (مەبەستەکانمان) بە چەند جۆر ھێناوە لەم قورئانەدا بۆ ئەوەی بیربکەنەوە و ئامۆژگاری وەرگرن بەڵام (خوانەناسان) ھەر دوورکەوتنەوەیان زیاتر دەبێت (بەو باسکردنە)');
INSERT INTO "bamoki" VALUES(17,42,'17:42','(ئەی موحەممەد صلى الله عليه وسلم) بڵێ ئەگەر لەگەڵ خوادا پەرستراوانێکی تر ببوایە وەک ئەو (بت پەرستان) دەڵێن ئەو کاتە دەگەڕان بە دوای ڕێگایەکدا بۆلای خاوەنی عەرش (کە خوایە)');
INSERT INTO "bamoki" VALUES(17,43,'17:43','پاک و بێگەرد و بەرز و بڵندە ئەو (خوایە) لەوەی ئەو (بت پەرستانە) دەیڵێن (دەربارەی خوا) زۆر بەرز و بڵند ترە');
INSERT INTO "bamoki" VALUES(17,44,'17:44','ھەر حەوت ئاسمانەکان تەسبیحاتی خوا دەکەن و زەوی و ھەرچی لە ناویاندایه وە ھیچ شتێک نیە کە تەسبیحات و سوپاس و ستایشی ئەو نەکات بەڵام ئێوە تێناگەن لە تەسبیحاتەکەیان بەڕاستی خوا ھەمیشە بە ئارام و لەسەرخۆ و لێبوردەیە');
INSERT INTO "bamoki" VALUES(17,45,'17:45','وە (ئەی موحەممەد صلى الله عليه وسلم) کاتێک قورئان دەخوێنیت (بەسەریاندا) دادەنێین لە نێوان تۆ و ئەوانەدا کە باوەڕیان بە ڕۆژی دوایی نیه پەردەیەکی نادیار (داپۆشەر)');
INSERT INTO "bamoki" VALUES(17,46,'17:46','وە دەیدەین بەسەر دڵیاندا چەند پەردەیەکی زۆر ئەستوور بۆ ئەوەی لە قورئان تێ نەگەن و گوێشیان کەڕ دەکەین (تانەبیستن) وە ھەر کاتێک ناوی پەروەردگارت دەبەیت (لای ئەو بێ باوەڕانەدا) لە قورئاندا بە تاک و تەنھایی بە بێزاری پشتت تێدەکەن');
INSERT INTO "bamoki" VALUES(17,47,'17:47','ئێمە چاک دەزانین بۆچی ئەوان گوێ دەگرن کاتێک گوێ دەگرن لە تۆ (ئەی موحەممەد صلى الله عليه وسلم) ھەروەھا کاتێک پێکەوە چپە دەکەن کاتێ ستەمکارەکان دەڵێن ئێوە تەنھا شوێنی پیاوێکی جادولێکراو دەکەون');
INSERT INTO "bamoki" VALUES(17,48,'17:48','سەرنج بدە چۆن نموونەی جۆرا و جۆرت بۆ دەھێننەوە سەرئەنجام گومڕا بوون ئیتر ناتوانن ڕێگای ڕاست بدۆزنەوە');
INSERT INTO "bamoki" VALUES(17,49,'17:49','دەشڵێن ئایا کاتێ بووینە ئێسک پروسکێکی ڕزیو و پتورکاو ئایا (جارێکی تر) زیندوو دەکرێینەوە و دەبینەوە بە دروسکراوێکی نوێ');
INSERT INTO "bamoki" VALUES(17,50,'17:50','(ئەی موحەممەد صلى الله عليه وسلم) پێیان بڵێ ئێوە ببن بە بەرد یان بە ئاسن');
INSERT INTO "bamoki" VALUES(17,51,'17:51','یان ھەر دروستکراوێکی ترلەوەی لە مێشک و دڵتاندا گەورەیە جابێگومان ئەوانە دەڵێن کێ زیندوومان دەکاتەوە بڵێ ئەو زاتە (زیندووتان دەکاتەوە) کە یەکەم جار دروستی کردوون جا (بە لاقرتیەوە) سەرت بۆ دەلەقێنن و دەڵێن ئەوەکەی دەبێت بڵێ لەوانەیە ئەو (ڕۆژە ھاتنی) نزیک بێت');
INSERT INTO "bamoki" VALUES(17,52,'17:52','ئەو ڕۆژەی کە بانگتان دەکەن (لە گۆڕەکانتانەوە) جا ئێوەش دەست بەجێ دێن بەدەم بانگ کردنەکەوە بە سوپاس و سەناکردنەوە و وا دەزانن نەماونەتەوە (لە دونیادا) ماوەیەکی کەم نەبێت');
INSERT INTO "bamoki" VALUES(17,53,'17:53','وە (ئەی موحەممەد صلى الله عليه وسلم) بڵێ بە بەندەکانم قسەیەک بکەن کە جوانتر و باشترینی قسە بێت بەڕاستی شەیتان ناکۆکی و ئاژاوە دەخاتە نێوانیان بەڕاستی شەیتان ھەمیشە بۆ ئادەمی دووژمنێکی ڕون و ئاشکرایە');
INSERT INTO "bamoki" VALUES(17,54,'17:54','پەروەردگارتان زاناترە بە ئێوە ئەگەر بیەوێت ڕەحمتان پێ دەکات یان ئەگەر بیەوێت سزاتان دەدات وە ئێمە تۆمان نەناردووە بەچاودێر و پارێزەر بە سەریانەوە');
INSERT INTO "bamoki" VALUES(17,55,'17:55','وە پەروەردگارت زاناترە بەھەر چی لە ئاسمانەکان و لە زەویدایە وە سوێند بە خوا بێگومان فەزڵی ھەندێ لە پێغەمبەرانمان داوە بەسەر ھەندێکی تریاندا و زەبورمان بەخشی بە داود');
INSERT INTO "bamoki" VALUES(17,56,'17:56','(ئەی موحەممەد صلى الله عليه وسلم) بڵێ (بێ باوەڕان) ھاوار بکەن لە ئەوانەی کە ئێوە بە خواتان دەزانین بێجگە لە خوا جا ئەوانە دەسەڵاتیان نیە بەڵا و زیانتان لەسەر لابەن وە (دەسەڵاتی) گۆڕانیشیان نیە');
INSERT INTO "bamoki" VALUES(17,57,'17:57','ئەوانەی کە ئەو (نەفامانە) ھاواریان لێ دەکەن ئەوانە بەدوای ھۆکارێکدا دەگەڕێن کە نزیکیان کاتەوە لە پەروەدگاریان کامیان نزیک ترە (لە خواوە و پلەو پایەی بەرزترە) وە بە ھیوا و ئومێد و ڕەحمی ئەون و لە سزایشی دەترسن بەڕاستی سزای پەروەردگارت ھەمیشە مەترسی لێکراوە و خۆ لێ پارێزراوە');
INSERT INTO "bamoki" VALUES(17,58,'17:58','وە (خەڵکی) ھیچ شار و گوندێکمان کە ئێمە لە ناویان ببەین پێش (ھاتنی) ڕۆژی دوایی یان سزایان بدەین بە سزایەکی سەخت ئیللا ئەو (سزادانە یان لەناو بردنە) لە (لوح المحفوظ) دا نوسراوە');
INSERT INTO "bamoki" VALUES(17,59,'17:59','وە ھیچ شتێک ڕێگری ئێمەی نەکردووە کە ئەو موعجیزانە بنێرین جگە لەوەی کە پێشینەکان بە درۆیان دەزانی ناردمان بۆ (ھۆزی) سەمود ئەو حوشترەی کە (موعجیزەیەکی) ئاشکرا بوو کەچی (باوەڕیان نەھێنا) و ستەمیان لێکرد و (سەریان بڕی) وە ئێمە موعجیزەکان نانێرین مەگەر بۆ ترساندنی (ئەو گەلە نەبێت)');
INSERT INTO "bamoki" VALUES(17,60,'17:60','(بیربکەرەوە) کاتێ پێمان ووتی بەڕاستی پەروەردگارت دەوری ھەموو خەڵکی داوە (دەسەڵاتی ھەیە بەسەریاندا) وە ئەو خەو بینینەی کە پیشانی تۆماندا (لەو شەو ڕەویەدا) ھەرلەبەر تاقیکردنەوەی خەڵکی بوو ھەروەھا ئەو درەختە نەفرەت لێکراوەش کە لە قورئاندا باسمان کردووە, وە ئێمە (بەو جۆرە بەڵگانە) ئەوانە دەترسێنین کەچی ئەو (ترساندنە) ھیچیان بۆ زیاد ناکات جگە لە یاخی بوون و سەرکەشی نەبێت');
INSERT INTO "bamoki" VALUES(17,61,'17:61','وە باسی ئەو کاتەبکە کە ئێمە بەةفریشتەکانمان ووت سوژدەبەرن بۆ ئادەم ئەوانیش یەکسەر ھەموویان سوژدەیان برد بێجگە لە ئیبلیس (شەیتان نەبێت) ووتی ئایامن سوژدەبەرم بۆ کەسێک کە لە قوڕ دروستت کردووە');
INSERT INTO "bamoki" VALUES(17,62,'17:62','(ئیبلیس) ووتی پێم بڵێ و ھەواڵم بدەرێ لەبارەی ئەمەوە کە ڕێزی ئەوت داوە بەسەرمندا؟! سوێند بە خوا ئەگەر دوام بخەیت تاڕۆژی دوایی (نەمرم) بەڕاستی زاڵ ئەبم بەسەر نەوەکانیدا (بە گومڕاکردن) کەمێک لەوان نەبێت (کە بەندە تایبەتیەکانی خوان)');
INSERT INTO "bamoki" VALUES(17,63,'17:63','(خوا بە شەیتانی) فەرموو بڕۆ جا ھەر کەس لەوان شوێنی تۆ کەوت ئەوە بەڕاستی تۆڵەی ھەمووتان دۆزەخە کە تۆڵەیەکی پڕ بە پڕە');
INSERT INTO "bamoki" VALUES(17,64,'17:64','وە ھەرکەس لەوانە کە دەتوانی گومڕای بکە بە دەنگت و سەدای (ناھەقت) و بدە بە سەریاندا و تێـیان خوڕە بە سوار و پیادەی لەشکرەکەتەوە، وە ھاوبەشییان بکە لەماڵ و سامان و منداڵیاندا بەڵێنیشیان پێ بدە بەڵام شەیتان بەڵێنیان پێ نادات بە خەڵەتاندن نەبێت');
INSERT INTO "bamoki" VALUES(17,65,'17:65','بێگومان بەندەکانی من دەسەڵاتت بەسەریاندا نیە (و ناتوانیت گومڕایان بکەیت) وە پەروەردگارت بەسە پارێزەر و چاودێر بێت');
INSERT INTO "bamoki" VALUES(17,66,'17:66','پەروەردگارتان ئەو زاتەیە کە کەشتی لە دەریادا ھاتوچۆ پێدەکات بۆ ئێوە بۆ ئەوەی بگەڕێن بە دوای ھەندێک بەھرە و ڕۆزی ئەودا بەڕاستی ئەو (خوایە) ھەمیشەیی میھرەبان و بە بەزەییە بەرامبەر بە ئێوە');
INSERT INTO "bamoki" VALUES(17,67,'17:67','جا کاتێک لە دەریادا تووشی بەڵا و زیان بێن ھەموو ئەو (پەرستراوانەی) ھاواریان لێ دەکەن وون دەبن بێجگە لە خوا کە چی کاتێک کە (خوا) ڕزگارتان دەکات و دەگەنە ووشکانی ڕوو وەردەگێڕن (لە خوا) (چونکە) ئادەمی ھەمیشە سپڵە و ناسوپاسە');
INSERT INTO "bamoki" VALUES(17,68,'17:68','جا ئایا ئێوە ناترسن و بێباکن لەوەی کە لە ووشکانیدا (بوومەلەرزە) بتانبات بەناخی زەویدا؟ یان (ناترسن لەوەی) بای توندتان بنێرێتە سەر بەووردە زیخەوە پاشان پارێزەرێکیشتان دەست نەکەوێت');
INSERT INTO "bamoki" VALUES(17,69,'17:69','ئەی ئایا دڵنیا و بێباکن لەوەی بتان گێڕێتەوە ناو دەریا جارێکی تر ئەمجا بینێرێتە سەرتان بایەکی توند و تیژی وێرانکەر ئەوسا لە دەریاکەدا نقومتان بکات لەبەر ئەو بێ باوەڕ و سپڵە بوونـەتان پاشان دەستان ناکەوێت ھیچ کەسێک کە داوای تۆڵەی ئێوە لە ئێمە بکات');
INSERT INTO "bamoki" VALUES(17,70,'17:70','سوێند بە خوا بێگومان ڕێزمان لە نەوەی ئادەم گرت وە لە ووشکانی و لە دەریادا ھەڵمان گرتن (بەسواری و گەڕاندمانن) و ڕۆزی پاک و خاوێنمان پێداون وە فەزڵمان داون بەسەر زۆرێک لەو شتانەی کە دروستمان کردون بە فەزڵێکی گەورە');
INSERT INTO "bamoki" VALUES(17,71,'17:71','(بیریان بخەنەوە) ئەو ڕۆژەی کە ئێمە ھەموو ئادەمیەک بانگ دەکەین بە ناوی پێغەمبەرەکەیانەوە (بەناوی پەیامەکەیانەوە) جا ھەر کەسێک نامەی کردەوەکەی بدرێت بە دەستی ڕاستیەوە ئا ئەوانە نامەکەیان دەخوێننەوە, وە ستەمیان لێ ناکرێت بە ئەندازەی ئەو تاڵە باریکەی بە دەوری ناوکی خورمادایە');
INSERT INTO "bamoki" VALUES(17,72,'17:72','بەڵام ھەر کەسێک لەم (دونیایە) دا کوێر بووبێت و (ڕێگەی ڕاستی نەبینی بێت) ئەوە ئەو کەسە لە ڕۆژی دوایشدا کوێرە بگرە لە کوێریش گومڕاترە');
INSERT INTO "bamoki" VALUES(17,73,'17:73','وە بەڕاستی نزیک بوو ئەوانە لاتدەن و وەرتگێڕن لەوەی کە نیگامان کرد بۆت بۆ ئەوەی جگە لە قورئان شتێک بەدەم ئێمەوە ھەڵبەستیت (ئەگەر گوێڕایەڵیان بوویتایە) ئەوە لەو کاتەدا دەیان کردیت بە دۆست و خۆشەویستی خۆیان و (گومڕایان دەکردیت)');
INSERT INTO "bamoki" VALUES(17,74,'17:74','خۆ ئەگەر ئێمە تۆمان دامەزراو نەکردایە بەڕاستی نزیک بوو تۆ کەمێک بەلایاندا بچیت');
INSERT INTO "bamoki" VALUES(17,75,'17:75','جا بێگومان ئەوکاتە پێمان دەچەشتی دوو بەرانبەر (سزای) ژیان دوو بەرانبەر (سزای) مردن پاشان دەستت نەدەکەوت یارمەتیدەرێک بەرانبەر بە ئێمە (کە بەرگری ئەو سزایەت لێ بکات)');
INSERT INTO "bamoki" VALUES(17,76,'17:76','وە بەڕاستی نزیک بوو ئەو (بێ باوەڕانە) (بە پیلان و دوژمنایەتی) نارەحەتت بکەن لە خاکی مەککە بۆ ئەوەی دەرت بکەن لێی جا ئەوکاتە ئەوانە نەدەمانەوە لە پاش تۆ کاتێکی کەم نەبێت (خوا لەناوی دەبردن)');
INSERT INTO "bamoki" VALUES(17,77,'17:77','رێگا و نەریتێکە لە (ئێمەوە) بۆ ئەوانەی کە ناردومانن لە پێغەمبەرانمان پێش تۆ باو و یاسای ئێمەش گۆڕانی بەسەردا نایەت');
INSERT INTO "bamoki" VALUES(17,78,'17:78','(ئەی موحەممەد صلى الله عليه وسلم) نوێژ بەجێ بھێنە لەکاتی لادانی خۆر (لە ناوەڕاستی ئاسمان) ھەتا (ھاتنی) تاریکی شەو ھەروەھا نوێژی بەیانی (بەجێ بھێنە) بەڕاستی قورئان خوێندنی نوێژی بەیانی ھەمیشە دەبینرێت (لەلایەن فریشتەکانی شەو و ڕۆژەوە)');
INSERT INTO "bamoki" VALUES(17,79,'17:79','وە (ئەی موحەممەد صلى الله عليه وسلم) لە بەشێکی شەودا ھەستە شەو نوێژ بکە بەقورئان خوێندن تێیدا ئەوە زیادە نوێژی تایبەت بە تۆیە ئومێدەواربە پەروەردگارت بتگەیەنێـتە پلەو جێگایەکی پەسەندکراو (کە شەفاعەتی گەورەیە بۆ خەڵکی لە قیامەتدا)');
INSERT INTO "bamoki" VALUES(17,80,'17:80','وە بڵێ ئەی پەروەردگارم (لەھەر کارێکدا بووم) ڕاست و چاک پێم ئەنجام بدە ڕاست و چاکیش پێم کۆتایی بھێنە وە لەلایەن خۆتەوە بۆم بسازێنە دەسەڵاتێکی یارمەتیدەر (کە زاڵ بم بەسەر ھەموو کۆسپێکدا)');
INSERT INTO "bamoki" VALUES(17,81,'17:81','وە بڵێ ئاینی ڕاست ھات وە ئاینی ناحەق و بەتاڵ نەما و ڕۆیشت بەڕاستی بەتاڵ ھەمیشە لە ناوچووە');
INSERT INTO "bamoki" VALUES(17,82,'17:82','وە لەم قورئانەدا دەنێرینە خوارەوە ئەوەی کە ھۆی شیفا و میھرەبانی یە بۆ بڕواداران بەڵام (ھاتنی ئەم قورئانە) بۆ ستەمکاران ھیچ زیاد ناکات جگە لە خەسارۆمەندی نەبێت');
INSERT INTO "bamoki" VALUES(17,83,'17:83','وە کاتێک چاکە و نیعمەتی خۆمان بڕێژین بەسەر ئادەمی (بێ باوەڕدا) ڕوو وەردەگێڕێت (لە زیکری ویادی ئێمە) و دوور دەکەوێتەوە, وە کاتێک ناخۆشی و زیانی لێ بکەوێت زۆر نائومێد دەبێت');
INSERT INTO "bamoki" VALUES(17,84,'17:84','(ئەی موحەممەد صلى الله عليه وسلم) بڵێ ھەمووکەس کار دەکات لەسەر سرووشت و (ڕێبازی) خۆی جا پەروەردگارتان زاناترە بەو کەسەی کە ڕێبازەکەی ڕاسترە');
INSERT INTO "bamoki" VALUES(17,85,'17:85','وە (ئەی موحەممەد صلى الله عليه وسلم) پرسیارت لێ دەکەن لەبارەی ڕۆحەوە (کە چیە) بڵێ ڕۆح لە کارو فەرمانی تایبەت بە پەروەردگارمە وە ئێوە تەنھا کەمێک لە زانیاریتان پێ بەخشراوە');
INSERT INTO "bamoki" VALUES(17,86,'17:86','وە سوێند بە خوا ئەگەر بمانەوێت بێگومان ئەو (قورئانەی) نیگامان کردووە بۆت نایھێڵین (لە دڵ و پەڕەکاندا) لە پاشان دەستت ناکەوێت لە بەرانبەر ئێمەدا کەسێک کە بەرگریت لێ بکات');
INSERT INTO "bamoki" VALUES(17,87,'17:87','مەگەر میھرەبانیەک لەلایەن پەروەردگارتەوە نەبێت بەڕاستی فەزڵ و چاکەی ئەو بەسەرتەوە گەورەیە');
INSERT INTO "bamoki" VALUES(17,88,'17:88','(ئەی موحەممەد صلى الله عليه وسلم) بڵێ سوێند بە خوا ئەگەر ئادەمی و جنۆکە ھەموو کۆببنەوه بۆ ئەوەی وێنەی ئەم قورئانە بھێنن ناتوانن وێنەی ئەو بھێنن ھەرچەندە پشتیوانی یەکتریش بن');
INSERT INTO "bamoki" VALUES(17,89,'17:89','وە سوێند بە خوا ئێمە ھێناومانەتەوە بۆ خەڵکی لەم قورئانەدا لە ھەموو جۆرە نموونەیەک کەچی زۆربەی خەڵکی نایانەوێت جگە لە سپڵەیی و بێ باوەڕی نەبێت');
INSERT INTO "bamoki" VALUES(17,90,'17:90','ووتیان ھەرگیز باوەڕت پێ ناھێنین ھەتا لەم زەویەدا کانیاوێکمان بۆ ھەڵنەقوڵێنیت');
INSERT INTO "bamoki" VALUES(17,91,'17:91','یان باخێکت ببێت لە دارخورما و ترێ و چەند ڕوبارێک ھەڵقوڵێنیت لە نێوان درەختەکانیدا بە خوڕ بڕوات');
INSERT INTO "bamoki" VALUES(17,92,'17:92','یان ئاسمان بڕوخێنی بەسەرماندا ھەرچۆن لافت لێداوە کە بەپارچە پارچە دەکرێت یان خوا و فریشتەکان بھێنیت ڕوو بەڕوو (بیان بینین)');
INSERT INTO "bamoki" VALUES(17,93,'17:93','ڕازاوە بە نەخش و نیگار یان بەرزببیتەوە بۆ ئاسمان وە ھەرگیز باوەڕ ناھێنین بە بەرز بونەوەکەشت ھەتا پەیامێکمان بۆ نەھێنیتە خوارەوە خۆمان بیخوێنینەوە (ئەی موحەممەد صلى الله عليه وسلم) بڵێ پاکی و بێگەردی بۆ پەروەردگارم ئایا من (شتێکی ترم) جگە لە ئادەمیەکی نێرراو و پێغەمبەر (واتە: من دەسەڵاتی ئەو شتانەم نیە بەڵکو من تەنھا مرۆڤێکم و خوا کردوومی بە پێغەمبەر)');
INSERT INTO "bamoki" VALUES(17,94,'17:94','ھیچ شتێک ڕێگری خەڵکی نەکرد لەوەی کە باوەڕ بھێنن کاتێک کە ڕێنمونی و ئاینی حەقیان بۆ ھات بێجگە لەوەی دەیانووت چۆن خوا ئادەمیەک بە پێغەمبەر دەنێرێت');
INSERT INTO "bamoki" VALUES(17,95,'17:95','بلێ ئەگەر لە زەویدا فریشتە ھەبوایە و بە دڵنیاییەوە (تیایدا) بگەڕانایە (بە وێنەی ئادەمی) بێگومان لە ئاسمانەوە دەمان ناردەخوارەوە بۆیان فریشتەیەکی پێغەمبەر');
INSERT INTO "bamoki" VALUES(17,96,'17:96','بڵێ پێیان خوا بەسە کە شاھید بێت لە نێوان من و ئێوەدا بەڕاستی خوا ھەمیشە ئاگادار و بینایە بە بەندەکانی');
INSERT INTO "bamoki" VALUES(17,97,'17:97','ھەر کەس خوا ڕێنمونی بکات ھەر ئەو کەسە ڕێنمونی کراوە (بەڕاستی) و ھەر کەسیش خوا گومڕای بکات ئەوە ھەرگیز دەستت ناکەوێت بۆیان بێجگە لە خوا دۆست و یارمەتی دەرانێک وە لە ڕۆژی دواییدا حەشریان دەکەین بەسەر ڕوومەت و دەم و چاویاندا کە کوێر و لاڵ وکەڕن شوێنی نیشتەجێیان دۆزەخە ھەر جارێک (دۆزەخ) بڵێسەی بنیشێتەوە (دامرێتەوە) بڵێسەی ئاگرەکەیان بۆ زیاد دەکەین');
INSERT INTO "bamoki" VALUES(17,98,'17:98','ئەوە تۆڵەی ئەوانەیە چونکە بێگومان باوەڕیان نەھێنا بە ئایەتەکانی ئێمە و دەیانووت ئایا ئەگەر ئێمە بووینە ئێسک و پروسکی پارە پارەی ڕزیو ئایا ئێمە جارێکی تر زیندوو دەکرێینەوە بە دروست کردنێکی تازە');
INSERT INTO "bamoki" VALUES(17,99,'17:99','ئایا ئەوانە نەیانزانیوە بێگومان ئەو خوایەی کە ئاسمانەکان و زەوی دروستکردووە دەسەڵاتدار و بەتوانایە کەوێنەی ئەوانە دروست بکاتەوە، وە کات و ماوەیەکی دیاری کردووە بۆیان کە گومانی تێدا نیە بەڵام ستەمکاران ھیچیان ناوێت بێ باوەڕی نەبێت');
INSERT INTO "bamoki" VALUES(17,100,'17:100','بڵێ ئەگەر ئێوە خاوەنی خەزێنەکانی چاکە و نیعمەتەکانی پەروەردگارم بن ئەوکاتە دەتانگرێتەوە (لە بەخشین) لە ترسی خەرج بوون و تەواو بوون وە ئادەمی ھەمیشە ڕژدە و چروکە');
INSERT INTO "bamoki" VALUES(17,101,'17:101','وە سوێند بە خوا بەڕاستی بەخشیمان بەموسا نۆ موعجیزەی ڕوون وئاشکرا (ئەی موحەممەد صلى الله عليه وسلم) دەی پرسیار لە نەوەی ئیسرائیل بکە (سەبارەت بە ڕوداوی مووسا و فیرعەون) کاتێ (موسا) ھاتە لایان ئەمجا فیرعەون پێی ووت بەڕاستی ئەی موسا من واگومان دەبەم کە تۆ جادو لێکراو بیت');
INSERT INTO "bamoki" VALUES(17,102,'17:102','(موسا علیە السلام) فەرمووی سوێند بە خوا بێگومان تۆ دەزانی ھیچ کەس ئەم موعجیزانەی نەناردۆتە خوارەوە جگە لە پەروەردگاری ئاسمانەکان و زەوی نەبێت کە ڕوونکەرەوەن (بۆ خەڵکی) وە بەڕاستی من دڵنیام کە تۆ ئەی فیرعەون لە ناو دەچیت');
INSERT INTO "bamoki" VALUES(17,103,'17:103','جا فیرعەون ویستی کەوا (موسا و کۆمەڵەکەی) دەرکات لە زەوی (میسر) دا ئیتر ئێمەش فیرعەون و ئەوانەی لەگەڵی بوون ھەموویانمان خنکاند لە دەریادا');
INSERT INTO "bamoki" VALUES(17,104,'17:104','ئەوسا لە پاش خنکاندنیان ووتمان بە نەوەی ئیسرائیل نیشتەجێ بن لەو زەویەی (کە فیرعەون ویستی دەرتان کات لێی) جا کاتێک بەڵێنی ڕۆژی دوایی ھات ئەوسا ھەمووتان پێکەوە (بۆ دادگای خوا) دەھێنین');
INSERT INTO "bamoki" VALUES(17,105,'17:105','وە ئەم (قورئانە) مان بە ھەق ناردۆتە خوارەوە، وە بە ھەق و ڕاستیش ھاتووەتە خوارەوە، وە تۆمان ڕەوانە نەکردووە بە موژدەدەر و ترسێنەر نەبێت');
INSERT INTO "bamoki" VALUES(17,106,'17:106','وە قورئانمان بەش بەش بۆت ناردووە بۆ ئەوەی بیخوێنیتەوە بەسەر خەڵکیدا بە لەسەر خۆیی و ھێواشی وە ئەم (قورئانە) مان بەو شێوە دابەزاند');
INSERT INTO "bamoki" VALUES(17,107,'17:107','(ئەی موحەممەد صلى الله عليه وسلم) پێیان) بڵێ باوەڕبھێنن بەم (قورئانە) یان باوەڕی پێ نەھێنن بەڕاستی ئەوانەی کە زانیارییان پێ بەخشراوە لە پێش ئەم (قورئانە) دا کاتێک (قورئان) بخوێنرێتەوە بەسەریاندا بە خێرایی دەکەون بەڕودا بە سوژدە بردن');
INSERT INTO "bamoki" VALUES(17,108,'17:108','وە دەڵێن پاک و بێگەردی بۆ پەروەردگارمان بەڕاستی ھەمیشە بەڵێنی پەروەردگارمان بەدی دێت');
INSERT INTO "bamoki" VALUES(17,109,'17:109','وە دەکەون بەسەر ڕوودا و دەگرین و (قورئان) ھەمیشە ملکەچیان بۆ زیاد دەکات');
INSERT INTO "bamoki" VALUES(17,110,'17:110','بڵێ نزا و ھاوار لە خوا بکەن یان ھاوار لە ڕەحمان بکەن (یەکسانە) بەھەر کامیان نزاو ھاوار داوای لێ بکەن چونکە ھەر بۆ خوایە ھەموو ناوە زۆر جوانەکان لە نوێژکردنتدا دەنگ بەرزمەکەرەوە و بەنھێنیش مەیکە لەو نێوانەدا ڕێگایەک ھەڵبژێرە (بەشێوەیەک خۆت گوێت لە خۆت بێت)');
INSERT INTO "bamoki" VALUES(17,111,'17:111','وە (ئەی موحەممەد صلى الله عليه وسلم) بڵێ ھەموو سوپاس و ستایش ھەر بۆ خوایە زاتێک کە ھیچ مناڵێکی بۆ خۆی بڕیار نەداوە و ھیچ ھاوبەشێکی نەبووە لە موڵک و پادشایەتیدا ھیچ دۆست و یارمەتیدەرێکی نەبووە لەبەر بێ دەسەڵاتی (کەواتە) خوا زۆر بە گەورە بگرە');
INSERT INTO "bamoki" VALUES(18,1,'18:1','ھەموو سوپاس و ستایش بۆ ئەو خوایەی کە ناردوویەتیە خوارەوە قورئان بۆ سەر بەندکەی خۆی (موحەممەد صلى الله عليه وسلم) و ھیچ جورە لاریەکی تێدا دانەناوە');
INSERT INTO "bamoki" VALUES(18,2,'18:2','(کتێبێکی) ڕاست و پایەدارە بۆ ئەوەی بە سزایەکی زۆر بەتین بترسێنێت (ئەوانەی کە باوەڕی پێ ناھێنن) لە لایەن خۆیەوە و مژدەش بدات بەو باوەڕ دارانەی کە کردەوە چاکەکان ئەنجام دەدەن بەمەی کە پاداشتێکی چاکیان بۆ ھەیە');
INSERT INTO "bamoki" VALUES(18,3,'18:3','لەو (بەھەشتە) دا بێ بڕانەوە دەمێننەوە');
INSERT INTO "bamoki" VALUES(18,4,'18:4','وە (بۆ ئەوەی) کەسانێک بترسێنێت کە دەیانووت خوا ڕۆڵەی بۆ خۆی بڕیار داوە');
INSERT INTO "bamoki" VALUES(18,5,'18:5','نەخۆیان زانابوون پێی نەباو و باپیرانیان چ قسەیەکی (بوختانێکی) گەورەیە کە لە دەمیان دەردەچێت جگە لە درۆ ھیچ شتێکی تر ناڵێن');
INSERT INTO "bamoki" VALUES(18,6,'18:6','جا لەوانەیە خۆت لە ناوبەری لەبەر کردەوەی ئەوان ئەگەر ئیمان نەھێنن بەم قورئانە لە داخ و خەفەتدا (بمریت)');
INSERT INTO "bamoki" VALUES(18,7,'18:7','بەڕاستی ئێمە ھەرچی وا بەسەر زەویەوە کردوومانە بەزینەت و جوانی بۆ زەوی بۆ ئەوەی خەڵکی تاقی بکەینەوە کامیان کردەوەی جوانتر و چاکترە؟');
INSERT INTO "bamoki" VALUES(18,8,'18:8','وە بەڕاستی ئەوەی وابەسەر زەویەوە ئێمە ھەمووی دەکەین بە زەویەکی تەختی ووشک و برنگ');
INSERT INTO "bamoki" VALUES(18,9,'18:9','ئایا واتزانی کە ھاوەڵانی ئەشکەوت و تابلۆ (ئەو بەردەی ناویانی تیدا نوسرا بوو) ئەوان لەناو نیشانەکانی ئێمەدا شتێکی سەر سوڕھێنەر بوو؟');
INSERT INTO "bamoki" VALUES(18,10,'18:10','کاتێک کۆمەڵە گەنجێک پەنایان بردەبەر ئەشکەوتەکە ووتیان ئەی پەروەردگارمان لەلایەن خۆتەوە ڕەحمەتێکمان پێ ببەخشە وە فەراھەم بھێنە بۆمان لەم کارەی کەواین تیایدا ڕێی ڕاست و ڕزگاری');
INSERT INTO "bamoki" VALUES(18,11,'18:11','جا ئێمەش (دوعاکەیانمان گیرا کرد و) پەردەمان ھێنا بەسەر گوێیاندا لەو ئەشکەوتەدا چەند ساڵ (خەومان لێ خستن)');
INSERT INTO "bamoki" VALUES(18,12,'18:12','لە پاشان خەبەرمان کردنەوە بۆ ئەوەی دەریخەین لەو دوو تاقمە کامیان چاکتر ماوەی خەوی خۆیان دەژمێرن (لە ئەشکەوتەکەدا)');
INSERT INTO "bamoki" VALUES(18,13,'18:13','ئێمە بۆت دەگێڕینەوە ھەواڵی ئەوان بەڕێک و ڕاستی بەڕاستی ئەوانە کۆمەڵە لاوێک بوون باوەڕیان ھێنابوو بە پەروەردگاریان ئێمەش ڕێنمونیمان زیاد کردن');
INSERT INTO "bamoki" VALUES(18,14,'18:14','و دڵیانمان بەھێز کرد (لەسەر ئەو باوەڕە) لە کاتێکدا ھەستان و ڕاپەڕین ووتیان پەروەردگاری ئێمە پەروەردگاری ئاسمانەکان و زەویە ئێمە ھەرگیز بێجگە لەو پەرستراوێکی تر ناپەرستین (ئەگەر جگە لە خوا شتێ تر بپەرستین) سوێند بە خوا ئەوکاتە درۆمان ووتووە');
INSERT INTO "bamoki" VALUES(18,15,'18:15','ئا ئەوانە گەلەکەی ئێمەن بێجگە لە خوا پەرستراوی تریان بۆ خۆیان بڕیار داوە دەی با بۆیان بیھێننەوە بەڵگەو نیشانەی ڕوون و ئاشکرا دەی کێ ستەمکارترە لەو کەسەی کە درۆ ھەڵبەستێ بە دەم خوداوە');
INSERT INTO "bamoki" VALUES(18,16,'18:16','(ئەی ھاوەڵانی ئەشکەوت) کاتێک ئێوە دوورە پەرێزیتان گرت لەوان (لەو بێ باوەڕانە) و لەو (پەرستراوانەش) بێجگە لە خودا دەیان پەرستن دەی کەوایە پەنا بەرنە ئەشکەوتەکە پەروەردگارتان بڵاوی دەکاتەوە بۆتان بەزەیی وڕەحمەتی خۆی وە بۆتان فەراھەم دەھێنێت لەم کارەتاندا ھۆی گوزەران و ژیانتان');
INSERT INTO "bamoki" VALUES(18,17,'18:17','خۆر دەبینی کاتێک کە ھەڵدێت لەلای ڕاستی ئەشکەوتەکەیان دەدات کاتێکیش ئاوا دەبێت لای دەدات لێیان بەلای چەپدا لە کاتێکدا ئەوان لە ناو بۆشایی فراوانی ئەشکەوتەکەدان ئەوە ھەندێکە لە بەڵگە و نیشانەکانی خوا ھەر کەسێک خوا رێنمونی بکات تەنھا ئەو کەسە ڕێنمونی کراوە و ھەر کەسیش گومڕا بکات ئەوە دەستت ناکەوێت بۆی ھیچ پشتیوان و ڕێ نیشاندەرێک');
INSERT INTO "bamoki" VALUES(18,18,'18:18','(ئەگەر بیان بینیت) وا دەزانیت بەخەبەرن کە چی خەوتوون وە ئەو (خەوتووانە) ئەم دیو و دیویان پێ دەکەین (بۆ ئەوەی لاشەیان تێک نەچێت) بەلای ڕاست و بە لای چەپدا و سەگەکەیان ھەردوو دەستی درێژ کرد بوو لەبەر دەرگای ئەشکەوتەکەدا ئەگەر سەیرێکت بکردنایە بێگومان بە ڕاکردن بەرە و دوا دەگەڕایتەوە و پڕ بە دڵ لێیان دەترسایت');
INSERT INTO "bamoki" VALUES(18,19,'18:19','ئابەو شێوە (لە خەو) خەبەرمانکردنەوە بۆ ئەوەی پرسیار لە یەکتری بکەن یەکێکیان ووتی چەندە ماونەتەوە (بە خەوتوویی) (ھەندێکیان) ووتیان ڕۆژێک یا کەمتر لە رۆژێک ماوینەتەوە (ھەندێکی تریان) ووتیان پــەروەردگــارتان زاناترە بەماوەی مانەوەتان دە یەکێکتان بەم دراوە (زیوە) تا ن بنێرن بۆ ناو شار با سەیرکات کێ (لە خەڵکی شار) خۆراکی پاکترە ئەوە با لەوە خۆراکتان بۆ بھێنێت با نەرم و نیان و لەسەرخۆ بێت لە ھەڵسوکەوتیدا (تا نەناسرێتەوە) و کەس لە ئێوە ئاگادار نەکات');
INSERT INTO "bamoki" VALUES(18,20,'18:20','چونکە بێگومان ئەگەر ئەوان پێتان بزانن بەرد بارانتان دەکەن یان دەتان گێڕنەوە بۆ ناو ئاینەکەی خۆیان ئەو کاتە ئێوە ھەرگیز ڕزگارتان نابێت');
INSERT INTO "bamoki" VALUES(18,21,'18:21','(پاش سەدان ساڵ ) ئا بەو شێوە خەڵکمان ئاگادار کرد بەسەریاندا بۆ ئەوەی بزانن کە بێگومان بەڵێنی خوا ڕاستە وە دڵنیابن کەلاڕۆژی دوایی گومانی تێدا نیە (خەڵکی مان ئاگادارکرد بەسەریاندا) لە کاتێکدا ئەو خەڵکە کێشەیان بوو لە نێوان خۆیاندا دەربارەیان جا (ھەندێکیان) ووتیان چەند خانوویەکیان بەسەرەوە دروست بکەن پەروەردگاریان ئاگادارترە پێـیان ئەوانەی کەزاڵ بوون بەسەر کار و باری ئەوکۆمەڵەدا ووتیان بێگومان مزگەوتێکیان بەسەرەوە دروست دەکەین');
INSERT INTO "bamoki" VALUES(18,22,'18:22','ئەوسا ھەندێکیان دەڵێن (ئەو لاوانە) سێ کەسن و چوارەمیان سەگەکەیانە وەھەندێکی تریان دەڵێن پێنج کەسن و شەشەمیان سەگەکەیانە ئەمانە قسەی بێ بەڵگەن (تفەنگ بە تاریکە شەوە دەنێن) ھەندێکی تریان دەڵێن ئەوانە حەوت کەسن ھەشتەمەکەیان سەگەکەیانە (ئەی موحەممەد صلى الله عليه وسلم) بڵێ پەروەردگارم زاناترە بەژمارەیان کەمێک نەبێت لە خەڵکی کەس ژمارەیان نازانێت کەواتە (ئەی موحەممەد صلى الله عليه وسلم) لەبارەیانەوە دەمەقاڵێ مەکە جگە لە دەمەقاڵەیەکی ئاشکرا و ئاسان (تەنھا ئەوە بڵێ کە لە قورئاندا بۆت ھاتووە) وە لە بارەیانەوە پرسیار لە کەسیان (خاوەن نامەکان) مەکە');
INSERT INTO "bamoki" VALUES(18,23,'18:23','بۆ (کردنی) ھیچ شتێک مەڵێ بێگومان من سبەینێ ئەو شتە دەکەم');
INSERT INTO "bamoki" VALUES(18,24,'18:24','مەگەر (لەگەڵیدا بڵێیت) ئەگەر خوا بیەوێت ھەر کاتێک (إن شاء اللە) ت لە بیرچوو چ کاتێک بیرت ھاتەوە بیڵێ بڵێ ئومێدەوارم پەروەردگارم ڕێنمونیم بکات بۆ ڕێگایەکی نزیکتر (لە ڕاستی) لەمە');
INSERT INTO "bamoki" VALUES(18,25,'18:25','(ئەو کۆمەڵە لاوە) مانەوە لە ناو ئەشکەوتەکەیاندا (بە خەوتوویی) ماوەی سێ سەد ساڵ (بە ساڵی ھەتاوی) نۆ ساڵی تریشیان زیادکرد (ئەگەر بە ساڵی مانگی عەرەبی حیساب بکرێت)');
INSERT INTO "bamoki" VALUES(18,26,'18:26','بڵێ خوا زاناترە بە ماوەی مانەوەیان تەنھا بۆ خوایە زانینی نھێنی ئاسمانەکان و زەوی ئای (خوا) چ بینا و بیسەرە بێجگە لە (خوا) ھیچ پشتیوانێکیان بۆنیە کەسیش ناکات بە ھاوبەشی خۆی لە فەرمانی دا');
INSERT INTO "bamoki" VALUES(18,27,'18:27','بخوێنەرەوە ئەوەی کە وەحیت بۆکراوە لە قورئانی پەروەردگارت کەس نیە بتوانێت ووتەکانی ئەو بگۆڕێت ھەرگیز پەنایەکت دەست ناکەوێت بێجگە لە خوا پەنای بۆ بەریت');
INSERT INTO "bamoki" VALUES(18,28,'18:28','خۆت ڕاگرە لەگەڵ ئەوانەی لە پەروەردگاریان دەپاڕێنەوە و یادی ئەو دەکەن (و دەیپەرستن) بەیانیان و ئێواران کە مەبەستیان ڕەزامەندی خوایە چاوەکانت لەوان لامەدە و وەرمەگێڕە (بۆ کەسانی بێ باوەڕی دەوڵەمەند) کە جوانی و رازاوەیی ژیانی ئەم دونیای دەوێت و بەگوێی کەسێک مەکە کە دڵیمان لە یادی خۆمان بێ ئاگا کردوە و شوێنی ھەوا و ئارەزووی خۆی کەوتووە ھەموو کارەکانی خراپ و لە سنوور دەرچووە');
INSERT INTO "bamoki" VALUES(18,29,'18:29','بڵێ ھەق (کە ئەم قورئانە یە) لە لایەن پەروەردگارتانەوە ھاتووە جا ھەرکەسێک دەیەوێت با باوەڕبھێنی و ھەرکەسیش دەیەوێت با بێ باوەڕ بێت ( ئەمەش جۆرێکە لە ھەڕەشە و سەرزەنشت) بە ڕاستی ئێمە ئامادەمان کردووە بۆ ستەمکاران (بێ باوەڕان) ئاگرێک کە بڵێسەکەی (وەکو ڕەشماڵ) دەوریان دەدات وە ئەگەر (لە تینوانا) ھاواری ئاو بکەن ئاوێکیان بۆ دەھێنرێت وەک مسی تواوە (یا وەک چڵک و چەپەڵی ڕۆن و زەیت وایە) روو دەم و چاوەکان دەبرژێنێ ئای چ خواردنەوەیەکی پیس و خراپە و ئای چ شوێن و جێگایەکی خراپە');
INSERT INTO "bamoki" VALUES(18,30,'18:30','بەڕاستی ئەوانەی کە باوەڕیان ھێناوە و کردوەی چاکەیان کردووە بێگومان ئێمە پاداشتی ئەوانە وون ناکەین کە کردەوەی چاکیان کردوە');
INSERT INTO "bamoki" VALUES(18,31,'18:31','بۆ ئەوانە ھەیە بەھەشتانێکی ھەمیشەیی کە ڕوبارەکان بە ژێریاندا دێن و دەچن دەڕازێنرێنەوە لەو (بەھەشتانە) دا بە بازنی زۆری ئاڵتون وە پۆشاکی سەوز دەپۆشن لە ئاوریشمی تەنک و ئەستور پاڵ دەدەنەوە لەو (بەھەشتە) دا بە سەر کورسی و پاڵپشتەکانەوە ئای چەند پاداشتێکی چاک و جێگایەکی خۆشە');
INSERT INTO "bamoki" VALUES(18,32,'18:32','(ئەی موحەممەد صلى الله عليه وسلم) بۆیان بگێڕەرەوە نموونەی ئەو دوو پیاوە دابوومان بە یەکێکیان (کە بێ باوەڕ بوو) دوو باخ لە ھەموو جۆرە ترێیەک بە دار خورما دەوری ھەردوو باخەکەمان دابوو لە نێوان ئەو دوو باخەدا کشتوکاڵمان فەراھەم ھێنا بوو (بۆی)');
INSERT INTO "bamoki" VALUES(18,33,'18:33','ھەردوو باخەکە بەری چاکیان گرتبوو ھیچیان لێ خەسار نەبوو بوو و لە نێوان ئەو دوو باخەدا کانیاوێکمان ھەڵقوڵاندبوو (بۆ ئاودانیان)');
INSERT INTO "bamoki" VALUES(18,34,'18:34','ھەروەھا (خاوەنی ئەو دوو باخە) ماڵ و سامانی زۆری ھەبوو (لە ئاڵتون و زیوو شتی تر) جا بە ھاوڕێ (ئیماندارە ھەژارە) کەی ووت لە کاتێکدا کە قسەی لەگەڵدا دەکرد من ماڵ و سامانم لە تۆ زۆرترە کوڕ و کاڵ و خزمەتکاریشم زیاترە');
INSERT INTO "bamoki" VALUES(18,35,'18:35','(بێ باوەڕەکەیان) چووە ناو باخەکەیەوە لە کاتێکدا کە ستەمکاربوو لە خۆی ووتی باوەڕ ناکەم ھەرگیز ئەم باخەم لەناو بچێت');
INSERT INTO "bamoki" VALUES(18,36,'18:36','باوەڕیش ناکەم ڕۆژی دوایی بێت و ڕوو بدات سوێند بێ خۆ ئەگەر (پاش مردن) بمگێڕنەوە بۆ لای پەروەردگارم بێگومان باشتر لەم باخەم دەست دەکەوێ');
INSERT INTO "bamoki" VALUES(18,37,'18:37','ھاوڕێ (ئیماندارە) کەی پێی ووت لە کاتێکدا کە گفتوگۆی لەگەڵدا دەکرد ئایا تۆ بێ باوەڕیت بەو زاتەی کە تۆی لە خاک دروستکردووە پاشان لە دڵۆپە ئاوێک پاشان کردویتی بە پیاو');
INSERT INTO "bamoki" VALUES(18,38,'18:38','بەڵام من ھەر (دەڵێم) ئەو خوا پەروەردگارمە و ھیچ کەسیش ناکەم بە ھاوبەشی پەروەردگارم');
INSERT INTO "bamoki" VALUES(18,39,'18:39','وە با کاتێک چوویتە ناو باخەکەتەوە بتووتایە ھەرچی خوا بیەوێت (ھەر ئەوە دەبێت) ھەموو توانایەکیش ھەر بەدەس خوایە ئەگەر تۆ من دەبینی لە تۆ کەمتریش ماڵ و دارایی و مناڵم ھەیە');
INSERT INTO "bamoki" VALUES(18,40,'18:40','ئومێدم وایە کە پەروەردگارم چاکتر لە باخەکەی تۆم پێ بدات و بەڵا و بروسکەیەکیش لە ئاسمانەوە بنێرێت بۆ باخەکەی تۆ جا (باخەکەت) ببێت بەزەویەکی ووشکی ساف و خلیسک');
INSERT INTO "bamoki" VALUES(18,41,'18:41','یان ئاوەکەی ڕۆبچێت بە ناخی زەویدا ئیتر ھەرگیز نەتوانی بیدۆزیتەوە');
INSERT INTO "bamoki" VALUES(18,42,'18:42','سامان و بەر و بوومەکەی لە ناوبرا (بە ھەورە بروسکە سوتا و نەما) ئەمجا کابراش لە داخا دەسەکانی دەپرواند و دەیدان بە یەکدا لەسەر ئەو ماڵەی کە خەرجی کرد بوو (لە باخەکەیدا) لە کاتێکدا (کەپری) باخەکەی ھەموو کەوتوون بەسەر یەکدا (جا لە پاشە ڕۆژدا) دەڵێت: خۆزگە کەسم نەکردایە بەھاوەڵی پەروەردگارم');
INSERT INTO "bamoki" VALUES(18,43,'18:43','کەسانێک نەبوو یارمەتی بدەن و بێن بەھانایەوە بێجگە لە خوا خۆیشی سەرکەوتوو نەبوو');
INSERT INTO "bamoki" VALUES(18,44,'18:44','لەو شوێنانەدا پشتیوانی تایبەتی خوای ڕاست و ھەقە ھەر ئەویش چاکترین پاداشت ئەداتەوه و سەرئەنجامی باشیش (بە ئیمانداران) دەدات');
INSERT INTO "bamoki" VALUES(18,45,'18:45','(ئەی موحەممەد صلى الله عليه وسلم) بیھێنەرەوە بۆ ئەو (بێ باوەڕانە) نموونەی ژیانی دونیا وەک بارانێک وایە لە ئاسمانەوە دەیبارێنین ئەوسا جۆرەھا ڕواوی زەویی (پێ دەڕوێت) و دەڕۆن بەناو یەکدا ئەمجا دەبێتە پوش و پەڵاش و ھەوا پرش و بڵاوی دەکاتەوە، وە خوا ھەمیشە بە توانا و دەسەڵاتدارە بەسەر ھەموو شتێکدا');
INSERT INTO "bamoki" VALUES(18,46,'18:46','ماڵ و کوڕ و کاڵ جوانی و زینەتی ژیانی دونیان کردەوە چاکەکان کە (پاداشتیان) دەمێنێتەوە پاداشتیان چاکترە لای پەروەردگارت و زیاتر جێگەی ئومێدن (لە ماڵی دونیا)');
INSERT INTO "bamoki" VALUES(18,47,'18:47','وە (یادیان خەرەوە) ڕۆژێک کە شاخەکان دەخەینە ڕێ و ڕووی زەوی بە ڕوونی و تەختی دەبینی و ھەموو (خەڵکی) کۆ دەکەینەوە و ھیچ کەسیان بەجێ ناھێڵین');
INSERT INTO "bamoki" VALUES(18,48,'18:48','و ھەموو (ئادەمی) لە بەردەم پەروەردگارتدا ڕیز دەکرێن سوێند بە خوا دێنەوە بۆ لامان ھەر بەو شێوەیەی کە یەکەمجار دروستمان کردن (بە ڕووتی و بێ بەرگی) بەڵام ئێوە گومانتان وابوو کە ئێمە کاتێک دانانێین بۆ (لێپرسینەوە) تان');
INSERT INTO "bamoki" VALUES(18,49,'18:49','نامەی کردەوەکان دابەش دەکرێت (دەدرێتە دەستی ڕاست چاکان و دەستی چەپی تاوانباران) ئەمجا تاوانباران دەبینیت زۆر دەترسن لەو (ھەموو تاوان و خراپەی) کە تێیدایە و دەڵێن ئای ھاوار بۆ ئێمە ئەم نامە و دەفتەرە چییە؟ ھیچ کردەوەیەکی بچووک و گەورەی بەجێ نەھێشتووە و تۆماری کردووە ھەر کردەوەیەکیان کردبێت (تیایدا) ئامادەیە و پەروەردگارت ستەم لە کەس ناکات');
INSERT INTO "bamoki" VALUES(18,50,'18:50','(بیریان بھێنەرەوە) کاتێک بە فریشتەکانمان ووت سوژدە بەرن بۆ ئادەم ئەوانیش ھەموو سوژدەیان برد جگە لە ئیبلیس (شەیتان) نەبێت کە لە پێڕی جنۆکەکان بوو بۆیە لە فەرمانی پەروەردگاری دەرچوو دەی ئایا ئێوە (ئەی ئادەمیزادەکان) ئەو و نەوەی ئەو دەکەنە دۆست و پشتیوانی خۆتان لە جیاتی من لە کاتێکدا کە ئەوان دوژمنی ئێوەن ئای چ گۆڕینەوەیەکی خراپە بۆ ستەمکاران');
INSERT INTO "bamoki" VALUES(18,51,'18:51','من ئەوان (شەیتان و نەوەکانیم) ئامادەی دروستکردنی ئاسمانەکان و زەوی نەکردووە ھەروەھا لە (کاتی) دروستکردنی خۆشیاندا (ئامادەم نەکردوون) وە من ھەرگیز گومڕاکەرانم نەکردووە بە یارمەتیدەری خۆم');
INSERT INTO "bamoki" VALUES(18,52,'18:52','ڕۆژێک دەبێت کە خوا دەفەرموێت بانگی ئەو بتانە بکەن کە لاتان وابوو ھاوبەشی منن (با یارمەتیتان بدەن) ئەوانیش بانگیان دەکەن بەڵام وەڵامیان نادەنەوە (و نایەن بە ھاواریانەوە) وە ئێمە لە نێوانیاندا شیوێکی پڕ ئازارمان داناوە');
INSERT INTO "bamoki" VALUES(18,53,'18:53','وە تاوانباران ئاگری دۆزەخ دەبینن و ھەموو دەزانن و دڵنیان کە دەچنە ناوی وتێی دەکەون و ڕێگایەکیان دەست ناکەوێت بۆ گەڕانەوە لەو (ئاگرە)');
INSERT INTO "bamoki" VALUES(18,54,'18:54','سوێند بە خوا بەڕاستی ئێمە لەم قورئانەدا ھێناومانەتەوە ھەموو جۆرە نموونەیەک بۆ خەڵکی بەڵام ھەمیشە ئادەمی لە ھەموو شتێ زیاتر موجادەلە و دەماقاڵێ دەکات');
INSERT INTO "bamoki" VALUES(18,55,'18:55','ھیچ شتێک ڕێگری لە خەڵک نەکردووە کە باوەڕبھێنن کاتێک ھیدایەت و ڕێنمونیان بۆھات و داوای لێ بوردن بکەن لە پەروەردگاریان جگە لەوەی وەک ئوممەتانی پێشینانیان لێ بێت و (سـزای خودا لە ناویان ببات) یان سزا ڕوو بە ڕوویان بێتەوە (بە چاوی خۆیان بیبینن)');
INSERT INTO "bamoki" VALUES(18,56,'18:56','ئێمە پێغەمبەرانمان نە ناردووە تەنھا بۆ ئەوە نەبێت کە موژدە دەر و ترسێنەر بن ئەوانەی کە بێباوەڕن بەناھەق و بەتاڵ دەمەقاڵێ دەکەن بۆ ئەوەی (ئاینی) ڕاستی پێ لە ناو بەرن ئایەتەکانی من (کە قورئانە) و ئەو ھەرەشانەی پێی ترسێنراون بە گاڵتەیان دەزانین');
INSERT INTO "bamoki" VALUES(18,57,'18:57','کێ ستەمکارترە لەو کەسەی کە ئامۆژگاری کرا بێت بە ئایەتەکانی پەروەردگاری بەڵام ئەو گوێ ی پێ نەداو و ئەو تاوانانەی کە کردویەتی لە بیری خۆی بباتەوە بەڕاستی ئێمە چەند پەردەیەکی ئەستورمان داوە بەسەر دڵیاندا تا تێنەگەن (لە ئایەتەکانی ئێمە) وە قورسی و کەڕیمان خستۆتە گوێیانەوە (تاڕاستی نەبیستن) جا ئەگەر بانگیان بکەیت بۆ سەر رێگای ڕاست ئەوانە ھەرگیز ھیدایەت وەرناگرن');
INSERT INTO "bamoki" VALUES(18,58,'18:58','پەروەردگارت لێبووردەی خاوەن بەزەییە ئەگەر تۆڵەیان لێ بسەنێ بەھۆی ئەو (تاوانانەوە) ی کە کردوویانە بێگومان بە پەلە سزای سەختی بۆ دەناردن بەڵام بۆ (سزادانی) ئەوان کاتێکی دیاری کراو ھەیە بێجگە لەو (خوایە) ھیچ پەنایەکیان دەست ناکەوێت (لەو سزایە)');
INSERT INTO "bamoki" VALUES(18,59,'18:59','و ئەو ھەمووە شار و گوندانەمان وێرانکرد و لەناومان بردن کاتێک (دانیشتوانیان) ستەمیان کرد و بۆ لەناو برد نیشیان کاتێکی دیاری کراومان دانا بوو');
INSERT INTO "bamoki" VALUES(18,60,'18:60','(باسی ئەو کاتە بکە) کە موسا بەلاوە بەردەستەکەی خۆی ووت من ھەر دەڕۆم ھەتا دەگەمە شوێنی بە یەک گەیشتنی دوو دەریاکە یان ماوەیەکی دوور و درێژ دەڕۆم');
INSERT INTO "bamoki" VALUES(18,61,'18:61','جا کاتێک گەیشتنە ئەو شوێنەی دوو دەریاکە بەیەک دەگەن ماسیەکەیان لەبیر چوو (کە وەک توێشوو بۆ سەفەرەکەیان پێـیان بوو) جا (ماسیەکە خۆی فڕێداو) ڕێگەی خۆی گرتە بەر لەناو دەریاکەدا و (ئاوەکە) لەت دەبوو وەک خۆی لێ نەدەھاتەوە');
INSERT INTO "bamoki" VALUES(18,62,'18:62','جا کاتێک تێپەڕین (لەو شوێنە) (موسا) فەرمووی بەلاوە بەردەستەکەی خواردنەکەمان بۆ بھێنە سوێند بە خوا بەڕاستی لەم سەفەرەماندا دووچاری ماندوویی و زەحمەت بووین');
INSERT INTO "bamoki" VALUES(18,63,'18:63','(لاوەکە) ووتی لە بیرتە کاتێک چووینە پەنا بەردە گەورەکە (بۆ پشوودان) جا بەڕاستی من (لەوێ) ماسیەکەم لە بیر چوو ھیچ شتێک ئەو (ماسیەی) لەبیر نەبردمەوە کە باسی بکەم بۆت بێجگە لە شەیتان (ماسیەکە زیندوو بویەوە) و بەشێوەیەکی سەیر رێگای خۆی لە دەریاکە گرتەبەر');
INSERT INTO "bamoki" VALUES(18,64,'18:64','(موسا) ووتی ئێمەش ئەوەمان دەویست ئەمجا بە شوێنی خۆیاندا گەڕانەوە بە سەرنجدان');
INSERT INTO "bamoki" VALUES(18,65,'18:65','جا بەندەیەک لە بەندەکانی ئێمەیان بینی کە لەلایەن خۆمانەوە ڕەحمەتمان پێدابوو شارەزاشمان کردبوو لە زانیاری تایبەتی خۆمان');
INSERT INTO "bamoki" VALUES(18,66,'18:66','موسا بەو (بەندە صاڵحەی) ووت ئایا (ھاوڕێیەتیت بکەم) و شوێنت بکەوم بۆ ئەوەی فێرم بکەیت لەو زانیاریە ڕاستەی فێری بوویت');
INSERT INTO "bamoki" VALUES(18,67,'18:67','(بەندە صاڵحەکە) ووتی (بە موسا) بە ڕاستی تۆ ھەرگیز ناتوانیت لەگەڵ مندا خۆت بگری');
INSERT INTO "bamoki" VALUES(18,68,'18:68','تۆ چۆن خۆت دەگریت لەسەر شتێک کە لە ڕاز و نھێنی ئەو ئاگادار نیت');
INSERT INTO "bamoki" VALUES(18,69,'18:69','(موسا) ووتی ئەگەر خوا بیەوێت بە خۆڕاگر دە مبینی و سەرپێچی لە ھیچ فەرمانێکت ناکەم');
INSERT INTO "bamoki" VALUES(18,70,'18:70','ووتی (بە موسا) جا ئەگەر شوێنم کەوتیت ئەوە پرسیاری ھیچ شتێکم لێ مەکە ھەتا خۆم دەربارەی قسەت بۆ دەکەم (لە نھێنییەکەی ئاگادارت دەکەمەوە)');
INSERT INTO "bamoki" VALUES(18,71,'18:71','ئەمجا پێکەوە ھەردووکیان رۆیشتن ھەتا سواری کەشتیەک بوون (پیاو چاکەکە) کەشتێکەی کون کرد (موسا) ووتی کەشتییەکەت کون تێکرد بۆ ئەوەی سەرنشینەکانی (لە دەریادا) نوقم بکەیت سوێند بە خوا بەڕاستی کارێکی زۆر خراپ و ناشایستەت کرد');
INSERT INTO "bamoki" VALUES(18,72,'18:72','(پیاو چاکەکە) ووتی ئایا من نەم ووت تۆ ھەرگیز ناتوانی خۆت بگریت لەگەڵ مندا');
INSERT INTO "bamoki" VALUES(18,73,'18:73','(موسا) ووتی لێم ببورە لەبەر ئەوەی (ئەو مەرجەی دامان نابوو) لەبیرم چوو ئەم زانیاری فێربوونەم لەسەر قورس مەکە');
INSERT INTO "bamoki" VALUES(18,74,'18:74','ئەمجا پێکەوە ھەردووکیان رۆیشتن ھەتا گەشتن بە تازە لاوێک یەکسەر (پیاو چاکەکە) ئەو (تازە لاوەی) کوشت (موسا) ووتی کەسێکی پاکی بێ گوناھت ھەروا کوشت بێ ئەوەی کەسێکی کوشتبێ سوێند بە خوا بەڕاستی کارێکی زۆر خراپ و ناڕەوات کرد');
INSERT INTO "bamoki" VALUES(18,75,'18:75','(بەندە چاکەکە) ووتی(بە موسا) پێم نەووتیت بێگومان تۆ ھەرگیز ناتوانی خۆت بگری لەگەڵمندا');
INSERT INTO "bamoki" VALUES(18,76,'18:76','(موسا) ووتی ئەگەر دوای ئەم جارە پرسیاری ھەرشتێکم لێ کردیت (ڕەخنەم لێ گرتیت) ئیتر ھاوڕێیەتیم مەکە چونکە بەڕاستی لەلایەن منەوە بیانووت ھەیە (لەوەی وازم لێ بھێنیت)');
INSERT INTO "bamoki" VALUES(18,77,'18:77','ئەمجا پێکەوە ھەردووکیان ڕۆیشتن ھەتا گەشتنە لای خەڵکی شارێک (لەو کاتەدا برسیان بوو) داوای خۆراکیان کرد لەخەڵکی شارەکە ئەوانیش (خەڵکی شارەکە) میوانداریان لێ نەکردن ئەمجا بینیان دیوارێک لەودا (لەوشارەدا) نزیک بوو بکەوێت (بەندە چاکەکە) دیوارەکەی ڕاستکردەوە وھەڵیچنیەوە، (موسا) ووتی ئەگەر بتویستایە کرێیەکت لەسەر وەردەگرت');
INSERT INTO "bamoki" VALUES(18,78,'18:78','ووتی ئا لێرەدا کاتی جیابوونەوەی من و تۆیە ئاگادارت دەکەم لەحیکمەتی ئەو شتانەی کە تۆ نەتوانی خۆت بگریت لەسەر (ڕوودان)یان');
INSERT INTO "bamoki" VALUES(18,79,'18:79','ھۆی (کوون کردنی) کەشتیەکە (ئەوە بوو) (ئەو کەشتیە) ھی چەند کەسێکی ھەژار بوو ئیشیان دەکرد (بەو کەشتیە) لەو دەریایەدا بۆیە ویستم (بە کون کردنی) عەیبداری بکەم (تا پاشا دەستی بەسەردا نەگرێت) چونکە لەپێشیانەوە پاشایەک ھەبوو دەستی دەگرت بەسەر ھەموو کەشتیەکی (چاک)دا');
INSERT INTO "bamoki" VALUES(18,80,'18:80','وە تازە لاوەکەش (کە کوشتم) ئەوە باوک و دایکی ئیماندار بوون جا ئێمەش ترساین (خۆشەویستی ئەولاوە) پاڵیان پێوە بنێت بۆ سەرکەشی و بێ باوەڕی');
INSERT INTO "bamoki" VALUES(18,81,'18:81','جا (بەو کارە) ویستمان پەروەردگاریان (لەبری ئەوەدا) بۆیان بگۆڕێت (بەمنداڵێکی) چاکتر لەو پاکتر لە گوناهـ بەسۆزو بەزەیی تر بێت بۆیان');
INSERT INTO "bamoki" VALUES(18,82,'18:82','وەھۆی (چاککردنەوەی) دیوارەکەش چونکە ھی دوو منداڵی ھەتیو بوو لە شارەکەدا لەژێریدا گەنجینەیەک ھەبوو کەھی ئەو دوو منداڵە بوو وە باوکی ئەودوو منداڵە چاک و خواناس بوو لەبەرئەوە پەروەردگارت ویستی ھەردووکیان گەورەو بەھێز ببن پاشان گەنجینەکەیان دەربھێنن ئەمە ڕەحمەتێک بوو لەلایەن پەروەردگارتەوە (لەبەر باوکیان) ئەو (شتانەی)کردم لە خۆمەوە نەبوو ئەوە نھێنی و ھۆی ئەو کردەوانە بوو کە تۆ توانای خۆ گرتنت نەبوو لەسەر (کردن) یان');
INSERT INTO "bamoki" VALUES(18,83,'18:83','ھەروەھا پرسیارت لێ دەکەن دەربارەی (ذو القرنین) (لە وەڵامیاندا) بڵێ لە بارەیەوە ھەندێک باسدەکەم بۆتان');
INSERT INTO "bamoki" VALUES(18,84,'18:84','بەڕاستی ئێمە ھێز و دەسەڵاتمان پێدا لە زەویدا وە ھۆکاری ھەموو شتێکمان پێ دابوو (کە پاشایەتی پێویستی بوو)');
INSERT INTO "bamoki" VALUES(18,85,'18:85','ئەویش کەڵکی لە ھۆکارەکان وەردەگرت (بۆ گەشتن بە مەبەست)');
INSERT INTO "bamoki" VALUES(18,86,'18:86','(خۆی و لەشکرەکەی ڕۆیشت) تا ئەوکاتەی گەیشتە شوێنی ئاوابوونی خۆر (لەو شوێنەدا) وا دیار بوو خۆرئاوا دەبێت لە کانیاوێکدا کە قوڕە ڕەشەی بە دەوردایە جا لەو جێگادا ڕێی کەوت لەگەلێکی (بێ باوەڕ) پێمان ووت ئەی (ذو القرنین) یان سزایان بدە (بە کوشتن گەر باوەڕیان نەھێنا) یان چاکەیان لەگەڵدا بکە (گەر باوەڕیان ھێنا)');
INSERT INTO "bamoki" VALUES(18,87,'18:87','(ذو القرنین) ووتی ئەوەی ستەم بکات (باوەڕ نەھێنێت) ئەوە بێگومان ئێمە سزای ئەدەین (بە کوشتن) پاشان دەگەڕێنرێتەوە بۆ لای پەروەردگاری ئەویش سزای دەدات بە سزایەکی سەخت کە وێنەی نەبینراوە');
INSERT INTO "bamoki" VALUES(18,88,'18:88','بەڵام ئەوەی باوەڕ بھێنێت و کردەوەی چاکیش بکات ئەوە پاداشتی ھەرە چاک (لە دواڕۆژدا) بۆ ئەوە و کارو فەرمانی خۆمانی بۆ سووک و ئاسان دەکەین');
INSERT INTO "bamoki" VALUES(18,89,'18:89','پاشان گەڕایەوە بۆ ڕۆژھەڵات، وئەو ھۆکارانەی گرتەبەر کە خوای گەورە پێی دابوو');
INSERT INTO "bamoki" VALUES(18,90,'18:90','ھەتا کاتێک گەیشتە شوێنی ھەڵاتنی خۆر بینی خۆر ھەڵدێت لەسەر گەل و نەتەوەیەک کەھیچ پەردە و پەنایەکمان بۆ دانەنابوون (لەخۆر بیانشارێتەوە)');
INSERT INTO "bamoki" VALUES(18,91,'18:91','ھەر بەو شێوە بوو (بەسەرھاتی ذو القرنین) بێگومان ئێمە بەوەی ھەیبوو (لەبیریدا بوو) ئاگادار بووین');
INSERT INTO "bamoki" VALUES(18,92,'18:92','پاشان گەڕایەوە بۆ ڕۆژھەڵات، وئەو ھۆکارانەی گرتەبەر کە خوای گەورە پێی دابوو');
INSERT INTO "bamoki" VALUES(18,93,'18:93','ھەتا گەیشتە نێوانی دووبەربەست (نێوان دوو چیا) لەنزیک ئەو دوو بەربەستەدا گەلێکی دی لەھیچ ووتەیەک تێ نەدەگەیشتن (بەزمانی خۆیان نەبێت)');
INSERT INTO "bamoki" VALUES(18,94,'18:94','ووتیان ئەی (ذو القرنین) بەڕاستی (یـأجوج و مأجوج) لەم خاکەدا خراپەکارن ئاخۆ خەرجیەکت پێ بدەین بۆ ئەوەی بەربەستێک لەنێوان ئێمەو ئەواندا دروست بکەیت');
INSERT INTO "bamoki" VALUES(18,95,'18:95','ووتی ئەو سامان و دەسەڵاتەی پەروەردگارم پێی داوم چاکترە (لەوەی ئێوە باسی دەکەن) جا ئێوە بە ھێز و بازوو یارمەتیم بدەن لەنێوان ئێوەو ئەواندا بەربەستێک دروست ئەکەم');
INSERT INTO "bamoki" VALUES(18,96,'18:96','پارچە ئاسنم بۆ بھێنن (لەسەر یەک دایبنێن) ھەتا نێوانی ھەردوو کێوەکەی پڕ کرد و ڕێکی یەک بوون ووتی: (بەموشەدەمە) فوو بکەن (لەو ئاگرەی لە ژێریدا کرا بوویەوە) ھەتا کاتێک ئەو (پارچە ئاسنانە)ی کرد بەئاگر (ذو القرنین) ووتی مسی تواوەم بۆ بھێنن بیکەم بەسەریدا');
INSERT INTO "bamoki" VALUES(18,97,'18:97','ئەمجا (یأجوج و مأجوج) نەیاندەتوانی سەرکەون بەسەریدا (بەھۆی بەرزی و ساف و لوسی یەوە) وە نەیاندەتوانی کونی تێبکەن (لەبەر ڕەقی و ئەستووری)');
INSERT INTO "bamoki" VALUES(18,98,'18:98','(ذو القرنین) ووتی ئەم (دروستکردنە) ڕەحمەتێکە لە لایەن پەروەردگارمەوە جا ھەرکات بەڵێنی پەروەردگارم ھات (بەدەرچوونی یأجوج و مأجوج) دەیکاتە زەویەکی تەخت وە بێگومان بەڵێنی پەروەردگارم ھەق و ڕاستە');
INSERT INTO "bamoki" VALUES(18,99,'18:99','واز (لە خەڵکی) دههێنین لەو ڕۆژەدا دێن و دەچن بەناو یەکتردا (وەکو شەپۆلی دەریا) و فوو دەکرێت بەشەیپوردا ئەوسا ھەموویان کۆ دەکەینەوە سەراپا (کەسیان لێ جێ نامێنێت)');
INSERT INTO "bamoki" VALUES(18,100,'18:100','وە لەو ڕۆژەدا دۆزەخ نیشانی کافران دەدەین بەنیشان دانێکی تەواو');
INSERT INTO "bamoki" VALUES(18,101,'18:101','ئەوانەی کە(لەم دونیایەدا) چاویان پەردەی بەسەردا ھاتبوو لەیادی من وە ئەوانە نەیان دەتوانی (قورئان) ببیستن');
INSERT INTO "bamoki" VALUES(18,102,'18:102','ئایا ئەوانەی بێ باوەڕن وا گومان دەبەن (سوودی ھەبێت بۆیان) کە بەندەکانم (وەک فریشتە و عیسا) بکەن بە یار و یاوەر و پەرستراو جگە لە من بەڕاستی ئێمە دۆزەخمان ئامادەکردووە بەجێگا و شوێن (میوانداری) بۆ بێ باوەڕان');
INSERT INTO "bamoki" VALUES(18,103,'18:103','بڵێ ئایا ھەواڵتان بدەینێ بە خەسارۆمەندترین (کەس) لەکاروکردەوەکانیاندا');
INSERT INTO "bamoki" VALUES(18,104,'18:104','(ئەوانە) ئەو کەسانەن کە کردەوە و کۆششیان بەفیڕۆ چوو لەژیانی دونیادا واشیان گومان دەبرد بەڕاستی ئەوان کارێکی زۆر چاک دەکەن');
INSERT INTO "bamoki" VALUES(18,105,'18:105','ئەوانە کەسانێکن بێ باوەڕ بوون بەنیشانەکانی پەروردگاریان و گەیشتن بەخزمەتی (لەدوارۆژدا) لەبەر ئەوە کردەوەکانیان پوچەڵ و بێ پاداشت دەرچوو کەواتە لەڕۆژی دواییدا ھیچ نرخ و ڕێزێکیان بۆ دانانێین');
INSERT INTO "bamoki" VALUES(18,106,'18:106','ئەوە تۆڵە و سزایان دۆزەخە لەبەر ئەوە بێ باوەڕبوون و گاڵتەیان دەکرد بەنیشانەکان و پێغەمبەرانی ئێمە');
INSERT INTO "bamoki" VALUES(18,107,'18:107','بەڕاستی ئەوانەی کەئیمان و باوڕیان ھێناوە وەکردەوەی چاکیان کردووە بەھەشتەکانی فیردەوس جێگای (میوانداری) ئەوانە');
INSERT INTO "bamoki" VALUES(18,108,'18:108','بەبەردەوامی تێیدا دەمێننەوە داوای گواستنەوە ناکەن لەو(بەھەشتە) دا');
INSERT INTO "bamoki" VALUES(18,109,'18:109','(ئەی موحەممەد صلى الله عليه وسلم) بڵێ ئەگەر(ئاوی) دەریاکان ببوایە بەمرەکەب بۆ (نوسینی) زانیاری پەروەردگارم بێگومان (ئاوی) دەریاکان تەواو دەبوون پێش ئەوەی زانیاری پەروەردگارم تەواو ببێت ئەگەر چەند ئەوەندەی دەریاکانیش بھێنین و (بیکەین بە مرەکەب زانیاری خوا تەواو نابێت) بڵێ (بەو بێ باوەڕانە) بێگومان منیش تەنھا ئادەمیزادێکم وەک ئێوە');
INSERT INTO "bamoki" VALUES(18,110,'18:110','بەڵام وەحیم بۆ دێت بەڕاستی پەروەردگاری ئێوە پەروەردگارێکی تاک و تەنھایە جا ھەرکەس بەتەمایە بەخزمەت پەروەردگاری بگات (لەقیامەتدا) ئەوە با کردەوەی چاک بکات وە لەپەرستنی پەروەردگاریدا کەس نەکات بەھاوبەشی');
INSERT INTO "bamoki" VALUES(19,1,'19:1','بە (کاف، ھا، یا، عەین، صاد) دەخوێنرێتەوە خوا زاناترە بە ماناکەی، بۆزانیاری زیاتر سەیری سەرەتای سورەتی (البقرة) بکە');
INSERT INTO "bamoki" VALUES(19,2,'19:2','(ئەمەی دەخوێنرێتەوە بەسەرتدا) باس ویادی بەزەیی پەروەردگارتە بە بەندەی خۆی کە زەکەریایە');
INSERT INTO "bamoki" VALUES(19,3,'19:3','لەکاتێکدا پاڕایەوە لەپەروەردگاری بەپاڕانەوەیەکی نھێنی و پەنامەکی');
INSERT INTO "bamoki" VALUES(19,4,'19:4','ووتی ئەی پەروەردگارم بەڕاستی من ئێسکم لاواز بووە لەبەر پیری سەرم (بەمووی سپی) گڕی گرتووە ( ئەمە جۆرێکە لەڕەوانبیژی زمانی عەرەبی بەکاردێت بەمەبەستی ئەوەی کە مووی سپی لەکاتی پیریدا گڕی بەرداوە لە موو ڕەشەکانی سەردەمی گەنجێتی) پەروەردگارم ھەرگیز لەپاڕانەوەم لێت بێ ھیوا نەبووم');
INSERT INTO "bamoki" VALUES(19,5,'19:5','بەڕاستی من دەترسم کەس و کارەکەم (لەئاینی خوا لادەن) لەپاش (مردنی) خۆم (چونکە کەسێکیان تێدا نیە جێگرەوەی من بێت) وخێزانەکەشم نەزۆکە (منداڵی نابێت) سا تۆ لەلایەن خۆتەوە مناڵێکی چاکم پێ ببەخشە');
INSERT INTO "bamoki" VALUES(19,6,'19:6','میراتگری (پێغەمبەرێتی) من وبنەماڵەی یەعقوب بێت ئەی پەروەردگارم واشی لێ بکە کەخۆت لێی ڕازی بیت');
INSERT INTO "bamoki" VALUES(19,7,'19:7','(خوای گەورە فەرمووی) ئەی زەکەریا بەڕاستی موژدەت دەدەینێ بەمنداڵێک کەناوی یەحیایە پێشتر کەسمان بەو ناوە ناو نەناوە');
INSERT INTO "bamoki" VALUES(19,8,'19:8','ووتی ئەی پەروەردگارم چۆن منداڵم دەبێت لەکاتێکدا خێزانم نەزۆکە بێگومان خۆشم لە پیریدا گەیشتوومەتە ئەو پەڕی پیری');
INSERT INTO "bamoki" VALUES(19,9,'19:9','ووتی (بڕیار) بەو جۆرەیە پەروەردگاریشت فەرموویەتی ئەو (کارە) بەلای منەوە زۆر ئاسانە بێگومان پێشتریش تۆم دروستکردووە کەشتێک نەبوویت (پێش ئەو دروستکردنە)');
INSERT INTO "bamoki" VALUES(19,10,'19:10','(زەکەریا) ووتی ئەی پەروەردگارم نیشانەیەکم بۆ دابنێ (کاتێک ئەو مژدە ھاتە دی) (خوای گەورە) فەرمووی نیشانەی تۆ ئەوەیە (لەسەر بوونی ئەو کوڕە) ناتوانیت گفتوگۆ لەگەڵ خەڵکدا بکەیت سێ شەو لەسەر یەک (بە رۆژەکانیەوە ھەر چەندە) ساغیت و لاڵ نیت');
INSERT INTO "bamoki" VALUES(19,11,'19:11','پاشان زەکەریا لە پەرستگاکەی ھاتە دەر بۆ ناو گەلەکەی جا بەئیشارەت پێی ووتن کە بەیانیان و ئێواران تەسبیحاتی خوا بکەن');
INSERT INTO "bamoki" VALUES(19,12,'19:12','(خوا دەفەرموێت بە یەحیا پاش گەورە بوونی) ئەی یەحیا تەورات بەتووند و گرنگ بگرە تێگەیشتنی تەورات (یان حیکمەت و ژیری)مان پێبەخشی لەتەمەنی مناڵیدا');
INSERT INTO "bamoki" VALUES(19,13,'19:13','لەگەڵ دڵسۆزی و پاک و خاوێنی لەلایەن خۆمانەوە وە ئەو (یەحیا) لەخوا ترس و پارێزکار بوو');
INSERT INTO "bamoki" VALUES(19,14,'19:14','ھەڵسوکەوتی لەگەڵ دایک و باوکیدا جوان بوو خۆ بەزلزان و سەر پێچیکەر نەبوو (لەفەرمانی خوا)');
INSERT INTO "bamoki" VALUES(19,15,'19:15','سەلامی خوای لەسەر بێت لەو ڕۆژەی لەدایک بوو وە لەو رۆژەی کە وەفات دەکات وە لەو ڕۆژەی کەزیندوو دەکرێتەوە');
INSERT INTO "bamoki" VALUES(19,16,'19:16','(ئەی موحەممەد صلى الله عليه وسلم) باسی بەسەرھاتی مەریەم بکە لە قورئاندا ئەو کاتەی کە کەنارگیری کرد لە کەسوکاری لە شوێنێکی رۆژھەڵاتی (مزگەوتی قودسدا خواپەرستی دەکرد)');
INSERT INTO "bamoki" VALUES(19,17,'19:17','ئەمجا پەردەیەکی دانا لە نێوان خۆی و ئەوان (کەس و کاری)دا ئێمەیش جوبرەیلمان نارد بۆلای وەکو بەشەرێکی تەواو خۆی پیشانی (مەریەم) دا');
INSERT INTO "bamoki" VALUES(19,18,'19:18','(مەریەم) ووتی من پەنا دەگرم بەخوای میھرەبان لە تۆ ئەگەر تۆ لە خوا ترسیت (وازم لێ بھێنە و زیانم پێ مەگەیەنە)');
INSERT INTO "bamoki" VALUES(19,19,'19:19','جوبرەیل ووتی (ترست نەبێت) من نێردراوی پەروەردگارتم بۆ ئەوەی منداڵێکی پاک و خاوێنت پێ ببەخشم');
INSERT INTO "bamoki" VALUES(19,20,'19:20','(مەریەم) ووتی من چۆن منداڵم دەبێت!! کە ھیچ بەشەرێک لێم نەکەوتووە (ڕەفاقەتی نەکردوم بەحەڵاڵی) وە داوێن پیسیش نەبووم');
INSERT INTO "bamoki" VALUES(19,21,'19:21','(جوبرەیل) ووتی کارەکە بەو جۆرەیە (کە پێم ووتی) پەروەردگارت فەرموویەتی ئەو کارە لای من زۆر ئاسانە و بۆ ئەوەی بیکەین بەموعجیزە و نیشانەیەک بۆ خەڵکی و ڕەحمەتێکی تایبەتیش لەلایەن ئێمەوە ئەوە کارێکی بڕیاردراوە (لەلایەن خواوە)');
INSERT INTO "bamoki" VALUES(19,22,'19:22','جا (مەریەم) سکی پڕ بوو بەو (کۆرپەلەوە) ئەمجا بە (سکەکە)وە دوور کەوتەوە بۆ شوێنێکی دوور (لە کەسوکاری)');
INSERT INTO "bamoki" VALUES(19,23,'19:23','ئەوسا ئێشی پێش ژانی منداڵبوون ناچاری کردو بردی بۆ لای قەدی دار خورمایەک (تا لە کاتی منداڵ بوومەنەکەیدا خۆی پێوە بگرێت و پاڵی پێوە بدات) (مەریەم) ووتی ئای خۆزگە پێش ئەم (ڕوداوە) بمردمایە و من شتێکی لەبیرچووەی باس نەکراو بوومایە');
INSERT INTO "bamoki" VALUES(19,24,'19:24','ئەمجا (جوبرەیل) بانگی مەریەمی کرد لەژێری (دار خورماکە) وە (ووتی) خەفەت مەخۆ و غەمبار مەبە بێگومان پەروەردگارت لەخوارتەوە جۆگەیەک ئاوی بۆ فەراھەم ھێناوی');
INSERT INTO "bamoki" VALUES(19,25,'19:25','قەدی ئەو دار خورمایەش بۆ لای خۆت ڕاوەشێنە خورمای تازە پێ گەیووت بەسەردا دەبارێت');
INSERT INTO "bamoki" VALUES(19,26,'19:26','ئەمجا (لەو خورما) بخۆ و (لەو ئاوەش) بخۆرەوە چاوو دڵ ڕۆشنبە جا ئەگەر ھەر کەسێکت بینی (پرسیاری ئەم منداڵەی لێ کردیت) بە (ئیشارەت) پێی بڵێ بەڕاستی من قسە نەکردنم لەسە خۆم نەزر کردووە بۆ (خوای) میھرەبان لەبەرئەوە ئەمڕۆ قسە لەگەڵ ھیچ کەسێکدا ناکەم');
INSERT INTO "bamoki" VALUES(19,27,'19:27','جا بەو (منداڵەوە) ھاتەوە بۆ لای کەسوکارەکەی ئەو (منداڵەی) گرتبوو بە باوەشیەوە (خزمەکانی) ووتیان ئەی مەریەم بەڕاستی شتێکی زۆر خراپ و ئابڕو بەرت کردووە');
INSERT INTO "bamoki" VALUES(19,28,'19:28','ئەی ئەوەی وەک خوشکی ھاروون وایت (لە تەقوا وخواناسیدا) نەباوکت پیاوێکی خراپ و داوێن پیس بووە نە دایکت داوێن پیس و زیناکار بووە');
INSERT INTO "bamoki" VALUES(19,29,'19:29','جا(مەریەم) ئیشارەتی بۆ ئەو (منداڵە) کرد (خزمەکانی) ووتیان چۆن قسە بکەین لەگەڵ مناڵێکدا کە لە بێشکەدایە');
INSERT INTO "bamoki" VALUES(19,30,'19:30','(منداڵەکە ھاتە قسە و) ووتی بەڕاستی من بەندەی خوام پەڕاوی ئینجیلم پێ دەدات و دەمکات بە پێغەمبەر');
INSERT INTO "bamoki" VALUES(19,31,'19:31','وە پیرۆز و فەڕداری کردووم لە ھەر جێگایەکدا بم و فەرمانی پێداوم بە نوێژ کردن و بە زەکاتدان ھەتا لە ژیاندام و زیندووم');
INSERT INTO "bamoki" VALUES(19,32,'19:32','و (کردومی) بە ڕۆڵەیەکی چاکەکارو گوێرایەڵ بۆ دایکم نەی کردووم بە ملھوڕێکی بەد ڕەفتار');
INSERT INTO "bamoki" VALUES(19,33,'19:33','سڵاوی خوام لەسەر بێت ئەو ڕۆژەی کە لە دایک بووم وە ئەو ڕۆژەی کە دەمرم وە ئەو رۆژەشی کە زیندوو دەکرێمەوە');
INSERT INTO "bamoki" VALUES(19,34,'19:34','ئەوە (کە باس کرا) عیسای کوڕی مەریەمە کە ووتەیەکی ڕاستە (دەربارەی عیسا) کە ئەوان (گاور و جولەکەکان) تێیدا دوو دڵن');
INSERT INTO "bamoki" VALUES(19,35,'19:35','ناگونجێت و شایەن نیە بۆ خوا منداڵی ھەبێت پاک و بێگەردی بۆ خوا ھەرکاتێک بیەوێت کارێک ببێت ئەوە بێگومان تەنھا پێی دەڵێت ببە، یەکسەر (ئەویش) دەبێت');
INSERT INTO "bamoki" VALUES(19,36,'19:36','بێگومان خوا پەروەردگاری من و ئێوەشە کەواتە ھەر ئەو بپەرستن ئەمەیە ڕێگا و ڕێبازی ڕاست و دروست');
INSERT INTO "bamoki" VALUES(19,37,'19:37','بەڵام دەستەو تاقمی (گاور وجولەکە)کان لە نێو خۆیاندا ڕایان جیاواز بوو (لە بارەی عیساوە) جا سزاو لەناو چوون بۆ ئەوانەی کە بێ باوەڕ بوون لە بینینی ڕۆژە زۆر گەورەکە (کە ڕۆژی دواییە)');
INSERT INTO "bamoki" VALUES(19,38,'19:38','ئای کە چەندە بیسەر و بینەرن (ئەو بێ باوەڕانە) لەڕۆژێکدا کە دێنەوە بۆ لامان (لە قیامەتدا) بەڵام ستەمکاران ئەمڕۆ (لە دونیادا) لە گومڕاییەکی ئاشکرادان');
INSERT INTO "bamoki" VALUES(19,39,'19:39','(ئەی موحەممەد صلى الله عليه وسلم) ئەو(ستەمکارانە) لەڕۆژی داخ و خەفەت بترسێنە کاتێک کارو باری (خەڵکی) کۆتایی پێ دەھێنرێت لە کاتێکدا ئەوانە غافڵن و باوەڕیش ناھێنن (بەڕۆژی دوایی)');
INSERT INTO "bamoki" VALUES(19,40,'19:40','بەڕاستی ھەر ئێمە دەبینە میراتگری زەوی و ئەوەی لەسەریەتی وە ھەر بۆ لای ئێمە دەگەڕێنەوە');
INSERT INTO "bamoki" VALUES(19,41,'19:41','وە (ئەی موحەممەد صلى الله عليه وسلم) لە قورئاندا باسی ئیبراھیم بکە بەڕاستی ئەو (ئیبراھیم) زۆر ڕاستگۆ و پێغەمبەر بوو');
INSERT INTO "bamoki" VALUES(19,42,'19:42','کاتێک بە باوکی ووت ئەی باوکی خۆشەویستم بۆ دەپەرستی شتێک کە نابیستێ و نابینێت وە ھیچ سوودێکت پێ ناگەیەنێت');
INSERT INTO "bamoki" VALUES(19,43,'19:43','ئەی باوکی خۆشەویستم بەڕاستی دڵنیابە زانست و زانیارێکم بۆ ھاتووە کە بۆ تۆ نەھاتووە (وپێت نەدراوە) کەواتە شوێنم کەوە و گوێرایەڵیم بکە ڕێنمونیت دەکەم بۆ رێبازی ڕاست');
INSERT INTO "bamoki" VALUES(19,44,'19:44','ئەی باوکی خۆشەویستم شەیتان مەپەرستە بەڕاستی شەیتان ھەمیشە سەرپێچیکەر و یاخیە لە خوای میھرەبان');
INSERT INTO "bamoki" VALUES(19,45,'19:45','ئەی باوکی خۆشەویستم بەڕاستی دەترسم (گەر بەکافری بمریت) تووشی سزایەک ببیت لە لایەن خوای میھرەبانەوە ئیتر ببیتە ھاوڕێ و ھاودەمی شەیتان (لەدۆزەخدا)');
INSERT INTO "bamoki" VALUES(19,46,'19:46','(باوکی) ووتی تۆ واز لە پەرستراوەکانی من دەھێنیت (ونایانپەرستی)؟! ئەی ئیبراھیم سوێند بێت ئەگەر واز نەھێنیت (لەقسە ووتن بە پەرستراوەکانم) دڵنیا بە بەرد بارانت دەکەم (دەی) بڕۆ ماوەیەکی زۆر دوورکەوەرەوە لێم');
INSERT INTO "bamoki" VALUES(19,47,'19:47','(ئیبراھیم) فەرمووی سەلامت لێ بێت لە مەودوا داوای لێ خۆشبوونت بۆ دەکەم لە پەروەردگارم (چونکە) بەڕاستی ئەو (پەروەردگار) لەگەڵمدا میھرەبانە (و دوعام گیرا دەکات)');
INSERT INTO "bamoki" VALUES(19,48,'19:48','وە دوورە پەرێز دەبم لە ئێوە و لەو شتانەش کە دەیپەرستن جگە لە خوا تەنھا پەروردگارم دەپەرستم ئومێدەوارم لە پەرستن و نزای پەروەردگارم دا بێ ھیوا و نائومێد نەبم (وەکو ئێوە)');
INSERT INTO "bamoki" VALUES(19,49,'19:49','جا کاتێک (ئیبراھیم) دوورکەوتەوە لێیان (لە گەلە بت پەرستەکەی) ولەو شتانەش ئەوان دەیانپەرست جگە لە خوای گەورە ئێمە ئیسحاق و(لە ئیسحاقیش) یەعقوبمان پێ بەخشی و ھەریەک لەوانیشمان کرد بە پێغەمبەر');
INSERT INTO "bamoki" VALUES(19,50,'19:50','لە ڕەحمەتی خۆمان ئەوانمان بەھرەمەند کرد باسی چاکەی ئەوانمان خستە سەر زمانی (خەڵکی)');
INSERT INTO "bamoki" VALUES(19,51,'19:51','لە قورئاندا باسی موسا بکە بەڕاستی ئەو (کەسێکی) ھەڵبژاردە بوو (بێ گەردبوو لە ناتەواوی) و نێرراو و پێغەمبەر بوو (بۆ خەڵکی)');
INSERT INTO "bamoki" VALUES(19,52,'19:52','وە ئێمە ئەومان بانگ کرد لەلای ڕاستی کێوی طورەوە وە ئەو(موسا) مان نزیک کردەوە تا قسەی لەگەڵ بکەین');
INSERT INTO "bamoki" VALUES(19,53,'19:53','وە لەبەزەیی و بەخشندەیی خۆمان بەخشیمان بەو (موسا) بە ھارونی برای کە پێغەمبەر بوو');
INSERT INTO "bamoki" VALUES(19,54,'19:54','وە لە قورئاندا باسی ئیسماعیل بکە بێگومان ئەو لە پەیمان و بەڵێنەکانیدا ڕاستگۆ بوو و ئەو نێرراوو پێغەمبەری خوا بوو');
INSERT INTO "bamoki" VALUES(19,55,'19:55','وە ھەمیشە فەرمانی دەدا بە خێزان و کەسوکاری بە نوێژ کردن و زەکاتدان وە ھەمیشە لای پەروەردگاری جێگەی ڕەزامەندی بوو');
INSERT INTO "bamoki" VALUES(19,56,'19:56','وە باسی ئیدریس بکە لە قورئاندا بەڕاستی ئەو زۆر ڕاستگۆ و پێغەمبەر بوو');
INSERT INTO "bamoki" VALUES(19,57,'19:57','و پایە و پلەیمان بڵند و بەرز کردەوە');
INSERT INTO "bamoki" VALUES(19,58,'19:58','ئەوانەی (کە باسکران) کەسانێکن کە خوا نیعمەت و چاکەی خۆی ڕشت بەسەریاندا لەو پێغەمبەرانەی کە لە نەوەی ئادەمن (وەک ئیدریس) وە لە (نەوەی) ئەوانەیش (کە لە کەشتییەکەدا) لەگەڵ نوح دا ھەڵمان گرتن (وەک ئیبراھیم) وە لە نەوەی ئیبراھیم و یەعقوب (وەک ئیسماعیل و یوسف) وە ئەوانەی ھیدایەتمان داون ھەڵمان بژاردوون ئا ئەوانە ھەر کاتێک ئایەتەکانی خوای بە بەزەییان بۆ بخوێنرێتەوە دەست بەجێ دەکەون بە ڕوودا بەسوژدە و گریانەوە');
INSERT INTO "bamoki" VALUES(19,59,'19:59','ئەمجا لە پاش ئەوان کەسانێک (لە نەوەکانیان) بوونە جێنشینیان کە نوێژیان دەفەوتاند و شوێن ھەواو ئارەزوو کەوتن جا ئەوانە لە داھاتوودا تووشی (سزای) گومڕاییان دەبن');
INSERT INTO "bamoki" VALUES(19,60,'19:60','جگە لەوەی کە تۆبەی کردبێت و باوەڕی ھێنابێت و کارو کردەوەی چاکی کردبێت ئا ئەوانە دەچنە بەھەشتەوە و ھیچ ستەمێکیان لێ ناکرێت (لەپاداشتی کردەوە چاکەکانیان کەم ناکرێتەوە)');
INSERT INTO "bamoki" VALUES(19,61,'19:61','بەھەشتانی جێی مانەوەی ھەمیشەییە کەخوای میھرەبان بەڵێنی داوە بە بەندەکانی کە نادیارو پەنھانە وبەچاو نەیانبینیوە بەڕاستی خوا بەڵێنی بەدی دێت');
INSERT INTO "bamoki" VALUES(19,62,'19:62','(بەھەشتییەکان) لە بەھەشتدا ھیچ ووتە و قسەیەکی بێ سوود نابیستن تەنھا سەلام کردن دەبیستن لەوێدا ڕزق و ڕۆزی ئامادەیە بۆیان بە بەیانیان و ئێواراندا');
INSERT INTO "bamoki" VALUES(19,63,'19:63','ئەو بەھەشتەی باسی کرا ئەوەیە کەدەیبەخشین بەو کەسانەی کە (لە دونیادا) پارێزکار و لەخوا ترس بوون لە بەندەکانمان');
INSERT INTO "bamoki" VALUES(19,64,'19:64','ئێمە (فریشتەکان) لە ئاسمانەوە دانابەزین بە فەرمانی پەروەردگارت نەبێت تەنھا بۆ خوایە (و خوا دەزانێت) ھەموو کاروباری داھاتوو ڕابووردوو و ئێستامان وە پەروەردگارت (کردەوەی بەندەکانی) لەبیر نەکردووە');
INSERT INTO "bamoki" VALUES(19,65,'19:65','کە پەروەردگاری ئاسمانەکان و زەوی و ھەرچی لەنێوانیاندایە کەواتە بیپەرستە و زۆر خۆگر بە لە پەرستنیدا ئایا ئاگاداریت وێنەی ئەو (خوایەکی تر) ھەبێت');
INSERT INTO "bamoki" VALUES(19,66,'19:66','ئادەمی(بێ بڕوا) دەڵێ ئایا کە مردم لە داھاتوودا بە زیندوویی (لە گۆڕ) دەردەھێنرێم');
INSERT INTO "bamoki" VALUES(19,67,'19:67','ئەوە ئادەمیزاد بۆ بیرناکاتەوە کە بێگومان ئێمە لە سەرەتاوە ئەومان دروستکردووە کە ئەو شتێک نەبوو');
INSERT INTO "bamoki" VALUES(19,68,'19:68','ئەمجا سوێند بە پەروەردگارت ئەو (بێ بڕوایانە) کۆ دەکەینەوە (لە ڕۆژی دواییدا) (لەگەڵ) ئەو شەیتانانەی (کە گومڕایان کردن) پاشان بەڕاستی ھەموویان ئامادە دەکەین بەدەوری دۆزەخدا بەسەر ئەژنۆیاندا کەوتوون (لەبەر ترسناکی ئەو رۆژە)');
INSERT INTO "bamoki" VALUES(19,69,'19:69','پاشان بێگومان دەری دەھێنین (و دەیخەینە دۆزەخەوە) لە ھەر پێڕو دەستەیەک کامەیان تاوانبارتر (یاخیتر) بووە لە بەرانبەر خوای میھرەباندا');
INSERT INTO "bamoki" VALUES(19,70,'19:70','ئەمجا بێگومان ئێمە زاناترین بەوانەی کە شیاوترن بخرێنە دۆزەخ و بسوتێن پێی');
INSERT INTO "bamoki" VALUES(19,71,'19:71','کەس نیە لە ئێوە نەڕوات و تێنەپەڕێت بەسەر دۆزەخدا (ئەو تێپەڕینە) بڕیارێکی بڕاوەیە لە پەروەردگارتەوە');
INSERT INTO "bamoki" VALUES(19,72,'19:72','دواتر ئەوانە ڕزگار دەکەین کە پارێزکار ولەخواترس بوون و ستەمکاران لەسەرچۆک لەناو (دۆزەخ) دا دەھێڵینەوە');
INSERT INTO "bamoki" VALUES(19,73,'19:73','ھەر کاتێک ئایەتە ڕوون و ئاشکراکانی ئێمە بەسـەریاندا بخوێنرایەتەوە ئەوانەی بێ بڕوا بوون دەیانووت بەوانەی ئیمانیان ھێنا بوو کام لـەم دوو کۆمەڵە جێ و شـوێنی (خانوو بەرەی) خۆشـترە و کۆڕو مەجلیسی چاکترە');
INSERT INTO "bamoki" VALUES(19,74,'19:74','وەپێش ئەمان چەندینمان لەناو برد لەگەلە (بێ باوەڕەکان) کەل و پەلی ناوماڵ ولاشە وقیافەتیشیان لەمان جوانتر بوو');
INSERT INTO "bamoki" VALUES(19,75,'19:75','(ئەی موحەممەد صلى الله عليه وسلم) بڵێ ئەوەی لەگومڕاییدایە باخوای میھرەبان موڵـەتی پێ بدات و تەمەنی درێژ بکات (تازیاتر تاوان بکەن) ھەتا کاتێک ئەو ھەڕەشانەی کراوە لێیان دەبینن (دێتە دی) (ئەوەش) یان سزایە (لە دونیادا) یان (ھاتنی) ڕۆژی دواییە جا لـە داھاتوودا دەزانن کێ جێگاو شـوێنی (خانوو بەرەی) خراپ و ناخۆشـە وەکێ سەرباز و لەشکری بێ ھێزترە (کافران یان باوەڕداران)');
INSERT INTO "bamoki" VALUES(19,76,'19:76','ئەوانەی لەسەر ڕێبازی ھیدایەت بوون خوا ھیدایەتیان بۆ زیاد دەکات ئەو کردەوە چاکانەی لـە (ڕۆژی دواییدا) دەمێننەوە لای پەروەردگارت پاداشـتی چاکترە وئاکامیشی باشترە');
INSERT INTO "bamoki" VALUES(19,77,'19:77','ئایا ئەو کەسەت دیوە بێ باوەڕ بوو بە نیشانە و ئایەتەکانی ئێمە ووتی (لە ڕۆژی دواییشدا) ماڵ و کوڕم پێ دەدرێت');
INSERT INTO "bamoki" VALUES(19,78,'19:78','ئایا بەنھێنیەکان ئاگادارە یا بەڵێنی لە خوای میھرەبان وەرگرتووە');
INSERT INTO "bamoki" VALUES(19,79,'19:79','نەخێر (وانیە) لەمەوپاش ئەوەی دەیڵێت دەینوسین (لەسەری) ماوەی سزای بۆ درێژ دەکەینەوە');
INSERT INTO "bamoki" VALUES(19,80,'19:80','ئەوەی ئەو دەیڵێت (لەماڵ و منداڵ) بەمیرات لێی وەردەگرینەوە (لە ڕۆژی دواییدا) بەتەنھا دێتەوە لامان');
INSERT INTO "bamoki" VALUES(19,81,'19:81','ئەوانە بێجگە لە خوا بڕیاریانداوە بۆ خۆیان چەند پەرستراوێک بۆ ئەوەی ئەو پەرستراوانە ببنە یارمەتیدەر (و تکا کار) بۆیان');
INSERT INTO "bamoki" VALUES(19,82,'19:82','نەخێر (وا نیە) بەڵکو پەرستراوەکان (لە قیامەتدا) ئینکاری پەرستنەکەی ئەوان دەکەن وە دەبنە دژیان و بەرپەرچیان دەدەنەوە');
INSERT INTO "bamoki" VALUES(19,83,'19:83','ئایا نەتدیوە (سەرنجت نەداوە) کە بێگومان ئێمە شەیتانەکانمان زاڵ کردووە بەسەر بێ باوەڕاندا چاک ھانیان دەدەن (بۆ کوفر و تاوان)');
INSERT INTO "bamoki" VALUES(19,84,'19:84','جا (ئەی موحەممەد صلى الله عليه وسلم) پەلەیان لێ مەکە بێگومان ئێمە بەژماردنێکی ورد (ڕۆژانی تەمەنیان) دەژمێرین');
INSERT INTO "bamoki" VALUES(19,85,'19:85','باسی ئەو ڕۆژە بکە کە پارێزکاران ولەخواترسان کۆ دەکەینەوە دەستە دەستە وکۆمەڵ کۆمەڵ بەڕێز وحورمەتەوە دەیانبەن بۆ لای خوای میھرەبان');
INSERT INTO "bamoki" VALUES(19,86,'19:86','تاوانباریش دەدەینە بەر بۆ دۆزەخ بە تینویەتی (بەوێنەی گەلە ئاژەڵ)');
INSERT INTO "bamoki" VALUES(19,87,'19:87','دەسەڵاتی تکاو لاڵەیان نیە جگە لەو کەسەی بەڵێنی لە خوای میھرەبان وەرگرتووە (وەک پێغەمبەران و شەھیدان)');
INSERT INTO "bamoki" VALUES(19,88,'19:88','(گاور وجولەکەکان) دەیانووت خوا منداڵی بۆخۆی داناوە');
INSERT INTO "bamoki" VALUES(19,89,'19:89','سوێند بەخوا بەڕاستی شتێکی زۆر نابەجێ و سەیر دەڵێن');
INSERT INTO "bamoki" VALUES(19,90,'19:90','نزیکە ئاسمانەکان لەت لەت و پارە پارە ببن لەبەر ئەو (قسەیە) و (نزیکە) زەویش شەق بەرێت و بقڵیشێ و(نزیکە) چیاکان بە یەکجار بڕوخێن');
INSERT INTO "bamoki" VALUES(19,91,'19:91','لەبەر ئەوەی (بێ باوەڕان) منداڵ دەدەنە پاڵ خوای میھرەبان');
INSERT INTO "bamoki" VALUES(19,92,'19:92','بۆ خوای میھرەبان شیاو نیە کە منداڵی ھەبێت');
INSERT INTO "bamoki" VALUES(19,93,'19:93','ھەموو ئەوانەی لە ئاسمانەکان و زەویدان بەبەندەیی دێنە لای خوای میھرەبان (ھیچ کەسێک لەوانە نیە بەندەی خوای میھرەبان نەبێت)');
INSERT INTO "bamoki" VALUES(19,94,'19:94','سوێند بەخوا بەڕاستی ھەموویانی سەرژمێری کردووە بە ژماردن');
INSERT INTO "bamoki" VALUES(19,95,'19:95','ھەر ھەموویان بە تاک و تەنھا دێنەوە بۆ لای (خوا) لە ڕۆژی قیامەتدا');
INSERT INTO "bamoki" VALUES(19,96,'19:96','بەڕاستی ئەوانەی کەباوەڕیان ھێناوە و کردەوە چاکەکانیشیان کردووە بێگومان خوای میھرەبان خۆشەویستیان دەکات (لەدڵی باوەڕداراندا)');
INSERT INTO "bamoki" VALUES(19,97,'19:97','جا بەڕاستی ئێمە قورئانمان لەسەر زمانی تۆ ئاسان کردووە تا مژدەی پێ بدەیت بەپارێزکاران و لەخواترسان ئەو کەسانەشی پێ بترسێنێت کەبە سەختی دژایەتیت دەکەن');
INSERT INTO "bamoki" VALUES(19,98,'19:98','گەل و کۆمەڵی زۆرمان پێش ئەمان (لەبەر خراپیان) لـەنـاوبرد ئایا ھەست ئەکەیت بەھیچ کەسێک لەوانە مابێت؟ یان ھیچ وورتە و سرتەیەکیان دەبیستیت');
INSERT INTO "bamoki" VALUES(20,1,'20:1','بە (طا، ھا) دەخوێنرێتەوە، خوا زاناترە بە ماناکەی بۆزانیاری زیاتر سەیری سەرەتای سورەتی (البقرة) بکە');
INSERT INTO "bamoki" VALUES(20,2,'20:2','قورئانمان نەناردۆتە خوارەوە بۆ سەر تۆ بۆ ئەوەی دوو چاری ناڕەحەتی غەم و پەژارە ببیت');
INSERT INTO "bamoki" VALUES(20,3,'20:3','تەنھا ئامۆژگاری و بیر خستنەوەیە بۆ ئەو کەسەی (لەخوا) دەترسێت');
INSERT INTO "bamoki" VALUES(20,4,'20:4','(ئەم قورئانە) بەبەش بەش نێردراوەتە خوارەوە لەلایەن زاتێکەوە کەزەوی و ئاسمانە بەرزو بڵندەکانی دروستکردووە');
INSERT INTO "bamoki" VALUES(20,5,'20:5','کە (خوای) میھرەبانە کە بەرز بووە سەر عەرش و قەراری گرت (بەو شێوەی شیاوی بێت ئێمە چۆنیەتیەکەی نازانین)');
INSERT INTO "bamoki" VALUES(20,6,'20:6','تەنھا موڵکی خوایە ئەوەی لەئاسمانەکان وئەوەی لەزەویدایە وەئەوەی لەنێوانیاندایە وئەوەی لەژێر خاکدایە');
INSERT INTO "bamoki" VALUES(20,7,'20:7','ئەگەر تۆ بە ئاشکرا قسە بکەیت ئەوە بەڕاستی ئەو (خوا) نھێنی و لەنھێنی شاراوەتریش دەزانێت');
INSERT INTO "bamoki" VALUES(20,8,'20:8','خوایەکە ھیچ پەرستراوێکی ڕاست نیە جگە لەئەو پەروەردگاری عەرشی ھەرە گەورەیە');
INSERT INTO "bamoki" VALUES(20,9,'20:9','(ئەی موحەممەد صلى الله عليه وسلم) ئایا ھەواڵ و دەنگوباسی موسا پێغەمبەرت پێ گەیشتووە');
INSERT INTO "bamoki" VALUES(20,10,'20:10','کاتێ ئاگرێکی بینی (لەگەڕانەوەیدا بۆ میصر لەشەوێکی ساردا) جا (موسا) ووتی بەخێزانەکەی لێرە بمێننەوە بەڕاستی من ئاگرێکم بەدی کردووە (ئەڕۆم بۆ لای) بەڵکو لەو ئاگرە چڵۆسکێکتان بۆ بێنم (خۆتانی پێ گەرم بکەنەوە) یان لای ئەو ئاگرە ڕێگا نیشان دەرێکم دەست کەوێت');
INSERT INTO "bamoki" VALUES(20,11,'20:11','جا کەگەشتە لای (ئاگرەکە) بانگی لێ کرا: ئەی موسا');
INSERT INTO "bamoki" VALUES(20,12,'20:12','بەڕاستی من پەروەردگاری تۆم دەی پێڵاوەکانت داکەنە (لەبەر ڕێزی شوێنەکە) چونکە بێگومان تۆ لەشیوی پیرۆزو بەفەڕی (طووا) دایت');
INSERT INTO "bamoki" VALUES(20,13,'20:13','من تۆم ھەڵبژاردووە (بۆ پێغەمبەرایەتی) کەواتە گوێ بگرە بۆ ئەوەی نیگا دەکرێ (بۆت)');
INSERT INTO "bamoki" VALUES(20,14,'20:14','بەڕاستی ھەر من خوام ھیچ پەرستراوێکی ڕاست نیە جگە لە من کەواتە من بپەرستە بە چاکی نوێژ بەجێ بھێنە تامنت بێتەوە یاد');
INSERT INTO "bamoki" VALUES(20,15,'20:15','بێگومان ڕۆژی دوایی دێت نزیک بوو (کاتی ھاتنی لەخۆیشم) بشارمەوە، (چا جـای لەفریشتە وپێغەمبەران، کەواتە زانینی کاتی ھاتنی ڕۆژی دوایی تەنھا پەروەردگار دەیزانێت) بۆ ئەوەی پاداشت بدرێتەوە ھەموو کەسێک بە گوێرەی ئەو کردەوەی کە دەیکات');
INSERT INTO "bamoki" VALUES(20,16,'20:16','جا بـاڕێگری نەکات لێی ئەو کەسەی باوەڕی نیە بەو (قیامەتە) و شوێن ھەوا و ئارەزووی خۆی کەوتووە ئەوسا تیا دەچێت');
INSERT INTO "bamoki" VALUES(20,17,'20:17','(خوا فەرمووی) ئەی موسا ئەوە چیە بەدەستی ڕاستەوە');
INSERT INTO "bamoki" VALUES(20,18,'20:18','(موسا) فەرمووی ئەوە گۆچانەکەمە خۆم دەدەم بەسەریدا (کاتی رۆیشتن) گەڵای درەختی پێ دەوەرێنم بۆ مەڕەکانم وە چەند کارو پێویستی تری پێ دەکەم');
INSERT INTO "bamoki" VALUES(20,19,'20:19','(خوا) فەرمووی ئەی موسا گۆچانەکەت فڕێ بدە');
INSERT INTO "bamoki" VALUES(20,20,'20:20','جا ئەویش فڕێیدا دەست بەجێ گۆچانەکە بووە بە مارێک بەخێرایی دەڕۆیشت (موسا ترسا)');
INSERT INTO "bamoki" VALUES(20,21,'20:21','(خوای گەورە) فەرمووی بیگرە و مەترسە دڵنیابە دەی گێڕینەوە بۆ شێوەکەی یەکەمجاری (دەیکەینەوە بەگۆچان)');
INSERT INTO "bamoki" VALUES(20,22,'20:22','دەستت بخەرە بن باڵت (پاشان دەری بھێنە) کە دەردێت سپیە (وەک کافور دەدرەوشێتەوە) بێ ئەوەی بەڵەکی و نەخۆشی بێت (ئەمەش) موعجیزەیەکی ترە (جگە لە گۆچانەکەت)');
INSERT INTO "bamoki" VALUES(20,23,'20:23','بۆ ئەوەی ھەندێک لەموعجیزە و بەڵگە گەورەکانی خۆمانت پیشان بدەین');
INSERT INTO "bamoki" VALUES(20,24,'20:24','بڕۆ بۆ لای فیرعەون چونکە بەڕاستی لەسنوور دەرچووە ویاخی بووە');
INSERT INTO "bamoki" VALUES(20,25,'20:25','(موسا) ووتی پەروەردگارم سنگم (دڵ و دەرونم) فراوان بکە');
INSERT INTO "bamoki" VALUES(20,26,'20:26','وە ئیش و کارم بۆ ئاسان بکە');
INSERT INTO "bamoki" VALUES(20,27,'20:27','و گرێی زمانیشم بەرەڵا بکه');
INSERT INTO "bamoki" VALUES(20,28,'20:28','بۆ ئەوەی لە قسەکانم تێبگەن');
INSERT INTO "bamoki" VALUES(20,29,'20:29','وە لەخزم و کەسی خۆم کەسێک بکەرە پشتیوانم');
INSERT INTO "bamoki" VALUES(20,30,'20:30','كه هارونی برامه');
INSERT INTO "bamoki" VALUES(20,31,'20:31','پشتمی پێ قایم و بەھێز بکە');
INSERT INTO "bamoki" VALUES(20,32,'20:32','بێکەرە ھاوبەشم لەکارەکەمدا (گەیاندنی پەیامی خوا)');
INSERT INTO "bamoki" VALUES(20,33,'20:33','بۆ ئەوەی زۆرستایشت بکەین');
INSERT INTO "bamoki" VALUES(20,34,'20:34','وە یاد و زیکری زۆرت بکەین');
INSERT INTO "bamoki" VALUES(20,35,'20:35','بەڕاستی تۆ بینای بە (ھەموو شتێکی) ئێمە');
INSERT INTO "bamoki" VALUES(20,36,'20:36','(خوای گەورە) فەرمووی بەڕاستی ئەوەی داوات کرد پێت درا ئەی موسا');
INSERT INTO "bamoki" VALUES(20,37,'20:37','سوێند بەخوا بەڕاستی ئێمە جارێکی تر منەتمان ناوەتە سەرت');
INSERT INTO "bamoki" VALUES(20,38,'20:38','کاتێک ئێمە ئاگاداری دایکتمان کرد ئەوەی پێویست بوو ئاگادار بکرێت');
INSERT INTO "bamoki" VALUES(20,39,'20:39','کە ئەو (موسای شیرەخۆرە) بخەرە ناو سندوقێکەوە ئەمجا فڕێیدەرە دەریاوە جا بادەریاش بیباتە کەناری ئاوەکە (لە ئاکامدا) دوژمنی من و دوژمنی ئەو (کەفیرعەونە) بیگرێتەوە لەلایەن خۆمەوە جۆرە خۆشەویستییەکم پێدایت بۆ ئەوەی پەروەردە بکرێیت لە پێش چاوی خۆم (بە چاودێری و پارێزگاری خۆم)');
INSERT INTO "bamoki" VALUES(20,40,'20:40','(لەبیرت بێت) کاتێک کە خوشکەکەت دەڕۆیشت و دەیووت (بە فیرعەون و دەستەکەی کە موسا دەگریا و شیری ھیچ ئافرەتێکی نەدەخوارد) کەسێکتان نیشان بدەم کە بەخێوی بکات وبیگرێتە ئەستۆ بەو ھۆیەوە تۆمان گێڕایەوە بۆ لای دایکت تا چاو ودڵی گەش بێتەوەو خەفەت نەخوات وە کەسێکت کوشت (لە تاقمی فیرعەون) بەڵام ئێمە ڕزگارمان کردیت لەو غەم وپەژارەیە وە دووچاری چەندەھا تاقی کردنەوەمان کردیت ئەمجا چەند ساڵێک مایتەوە لەناو خەڵکی مەدیەندا پاشان ئەی موسا لە کاتی دیاریکراودا (بۆ پێغەمبەرایەتی) ھاتوویتە ئێرە');
INSERT INTO "bamoki" VALUES(20,41,'20:41','وە تۆم ھەڵبژارد بۆ خۆم (تاکو پێغەمبەر بیت و پەیامی من ڕاگەیەنیت)');
INSERT INTO "bamoki" VALUES(20,42,'20:42','بڕۆ خۆت و براکەت بە بەڵگە و موعجیزەکانمەوە سستی نەکەن لە زیکر و یادی مندا');
INSERT INTO "bamoki" VALUES(20,43,'20:43','(پێی ووت) بڕۆ بۆ لای فیرعەون(چونکە) بەڕاستی یاخیی بووە');
INSERT INTO "bamoki" VALUES(20,44,'20:44','جا قسەی لەگەڵ بکەن بە نەرم و نیانی بەڵکو بیربکاتەوە یا بترسێت (لە سزای خوا)');
INSERT INTO "bamoki" VALUES(20,45,'20:45','ھەردووکیان ووتیان ئەی پەروەردگارمان بەڕاستی ئێمە دەترسین دەس درێژی بکاتە سەرمان پێش ئەوەی ئێمە ھیچ بڵێین یان زیاتر یاخی و سەرکەش ببێت');
INSERT INTO "bamoki" VALUES(20,46,'20:46','(خوا) فەرمووی مەترسن بەڕاستی من لەگەڵتانام دەبیستم و دەبینم');
INSERT INTO "bamoki" VALUES(20,47,'20:47','کەوابوو بچنە لای و (پێی) بڵێن بێگومان ئێمە ھەردووک نێرراوی پەروەردگاری تۆین (بۆ سەر تۆ) جا واز لە بەنی ئیسرائیل بھێنە (با بێن) لەگەڵ ئێمەدا وە ئازار و سزایان مەدە بەڕاستی ئێمە لە لایەن پەروەردگارتەوە بەڵگەو موعجیزەمان بۆ ھێناویت سڵاویش لەسەر کەسێک بێت کە شوێن ڕێگای ڕاستی دەکەوێت');
INSERT INTO "bamoki" VALUES(20,48,'20:48','بەڕاستی ئێمە نیگامان بۆ کراوە کە بێگومان سزا بۆ کەسێکە (پێغەمبەران) بەدرۆ دەزانێ و ڕوو وەردەگێرێت (وباوەڕ ناھێنێت)');
INSERT INTO "bamoki" VALUES(20,49,'20:49','(فیرعەون) ووتی باشە موسا پەروەردگاری ھەردووکتان کێیە؟');
INSERT INTO "bamoki" VALUES(20,50,'20:50','(موسا) فەرمووی پەروەردگاری ئێمە ئەو زاتەیە کەھەموو شتێکی بەشێوەیەکی ڕێکوپێک وجوان دروستکردووە پاشان ڕێنمونیشی کردووە (بۆ کاری خۆی)');
INSERT INTO "bamoki" VALUES(20,51,'20:51','(فیرعەون) ووتی ئـەی خەڵکانی سەردەمە پێشووەکان حاڵیان چیەو چۆنە (لە پاشی مردنیان)');
INSERT INTO "bamoki" VALUES(20,52,'20:52','(موسا) فەرمووی زانیاری ئەوە لای پەروەردگارمە لە پەراوێکدایە پەروەردگارم ھیچی لێ وون نابێت و ھیچی لەبیر ناچێت');
INSERT INTO "bamoki" VALUES(20,53,'20:53','(پەروەردگارم) زاتێکە کە زەوی بۆ کردونەتە شوێنی حەسانەوە (وەکو بێشکە) وە لەناوییدا چەندەھا ڕێگای بۆ فەراھەم ھێناون وە لەئاسمانەوە باران دەبارێنێت (بۆتان) جا بەو بارانەی ڕواندومانە زۆر جوتی جۆراو جۆر لەڕووەک (کە جیاوازن لە تام و بۆن و ڕەنگ و شێوەدا)');
INSERT INTO "bamoki" VALUES(20,54,'20:54','(ئەی مرۆڤەکان) بخۆن و ئاژەڵ وماڵاتەکانیشتان بلەوەڕێنن بەڕاستی لەوەدا (کە باسکرا) بەڵگە و نیشانەی زۆر ھەیە بۆ ئەوانەی خاوەن ژیرین');
INSERT INTO "bamoki" VALUES(20,55,'20:55','لەم زەویە دروستمان کردون دووبارە دەتانگێڕینەوە ناو زەوی (بەمردوویی) جارێکی تر لەو زەویە دەرتان دەھێنینەوە (بەزیندوویی بۆ لێپرسینەوە)');
INSERT INTO "bamoki" VALUES(20,56,'20:56','سوێند بەخوا بەڕاستی ئێمە ھەموو بەڵگەو نیشانەکانی خۆمان نیشان فیرعەوندا کەچی باوەڕی نەھێناو سەرپێچی کرد');
INSERT INTO "bamoki" VALUES(20,57,'20:57','(فیرعەون) ووتی ھاتوویت بۆ ناومان بۆ ئەوی دەرمان کەیت لەخاکەکەمان بەجادوگەریەکەت ئەی موسا');
INSERT INTO "bamoki" VALUES(20,58,'20:58','سوێند بێت جادویەکت بۆ (بەکار) بھێنین وەک ئەو (جادوەی خۆت) جا شوێن و کاتێک لە نێوان ئێمە و خۆتدا دابنێ نە ئێمە و نەتۆ خۆمانی لێ نەبوێرین (با) جێگایەکی تەخت بێت');
INSERT INTO "bamoki" VALUES(20,59,'20:59','(موسا) فەرمووی کاتی دیاریکراوتان ڕۆژی خۆڕازاندنەوە (ڕۆژی چەژن) بێت بەمەرجێک لە کاتی چێشتەنگاودا خەڵکی کۆبکرێنەوە');
INSERT INTO "bamoki" VALUES(20,60,'20:60','ئەمجا فیرعەون ڕۆیشت لەو جێگایە وھەمووجادوگەرەکانی کۆکردەوە پاشان ھات (بۆ شوێنی دیاریکراو)');
INSERT INTO "bamoki" VALUES(20,61,'20:61','موسا بە جادوگەرانی ووت ھاوار بۆ ئێوە درۆ بەدەم خواوە نەکەن ئەگینا بەسزایەک لەناوتان دەبات بەڕاستی ھەر کەسێک درۆ ھەڵبەستێ بەدەم خواوە ڕەنجەڕۆ دەبێت');
INSERT INTO "bamoki" VALUES(20,62,'20:62','ئەمجا (جادوگەرەکان) سەبارەت بەکارەکەیان لە نێوانیان دا جیاوازی پەیدا بوو و بەنھێنی چرپەیان دەکرد');
INSERT INTO "bamoki" VALUES(20,63,'20:63','ووتیان بەڕاستی ئەمانە دوو جادوگەرن دەیانەوێت دەرتان پەڕێنن لە خاک و ووڵاتەکەتان بە جادوەکەیان وە ڕێباز و نەریتە ھەرە باشەکەتان لەناو بەرن');
INSERT INTO "bamoki" VALUES(20,64,'20:64','جا کەواتە ھەموو جادو و پیلانێکتان کۆبکەنەوە پاشان بە یەک ڕیز بێنە (گۆڕەپانەوە) بەڕاستی ھەرکەسێک ئەمڕۆ سەربکەوێت ڕزگاری دەبێت و سەرفرازە');
INSERT INTO "bamoki" VALUES(20,65,'20:65','ساحیرەکان) ووتیان ئەی موسا یاتۆ (چادوەکەت) فڕێ بدە یا ئێمە لەسەرەتاوە فڕێی دەدەین');
INSERT INTO "bamoki" VALUES(20,66,'20:66','(موسا) فەرمووی نەخێر ئێوە (لەپێشەوە) فڕێی بدەن (ئەوانیش فڕێیاندا) جا لەناکاودا گوریس و دار دەستەکانیان وەھا ھاتە پێش چاوی (موسا) بەھۆی جادوەکەیانەوە کەبەڕاستی دەجوڵێن و دەڕۆن');
INSERT INTO "bamoki" VALUES(20,67,'20:67','جا موسا لەدڵی خۆیدا کەمێک ترسا');
INSERT INTO "bamoki" VALUES(20,68,'20:68','ووتمان پێی مەترسەو بێـباک بە بێگومان ھەر تۆ سەرکەوتوو دەبیت');
INSERT INTO "bamoki" VALUES(20,69,'20:69','ئەوەی لەدەستی ڕاستتدایە (کەگۆچانەکەتە) فرێی بدە ھەرچی (جادو) ئەوان کردوویانە قوتی دەدات (ھەڵی دەلوشێت) بێگومان ئەوەی ئەوانە کردوویانە تەنھا فڕوفێڵی جادوگەرە جادوگەریش سەرکەوتوو نابێت لەھەر کوێوە ھاتبێت و (ھەرچی بکات)');
INSERT INTO "bamoki" VALUES(20,70,'20:70','ئەوسا جادوگەرەکان خران بەڕوودا بەسوژدە بردنەوە (بۆ خوا) ووتیان بڕوامان ھێنا بەپەروەردگاری ھارون و موسا');
INSERT INTO "bamoki" VALUES(20,71,'20:71','فیرعەون) ووتی پێیان ئایا ئێوە بڕواتان پێ ھێنا پێش ئەوەی مۆڵەتتان بدەم،بێگومان ئەو (موسا) گەورەتانە کەفێری جادوی کردوون جا سوێند بێت دەیبڕم (دەیقرتێنم) دەست و قاچتان بەپێچەوانەوە (ڕاست و چەپ) وەبێگومان بەقەدی دارخورمادا ھەڵتان دەواسم (دەتانکەم بە دارا) (ئەوسا) بەڕاستی دەزانن کاممان (من یان خواکەی موسا) سزامان سەخترو بەردەوامترە');
INSERT INTO "bamoki" VALUES(20,72,'20:72','(ساحیرەکان) ووتیان ئێمە ھەرگیز تۆ ھەڵنابژێرین بەسەر ئەو نیشانە و موعجیزانەدا کەبۆمان ھاتووە (وە تۆ ھەڵنابژێرین بەسەر) ئەو خوایەی کە لەنەبوون ئێمەی دروستکردووە جا ھەر چیت لەدەست دێت بیکە بێگومان تۆ تەنھا لەم ژیانی دونیایەدا دەستت دەڕوا و بڕیار دەدەیت');
INSERT INTO "bamoki" VALUES(20,73,'20:73','بەراستی ئێمە باوەڕمان ھێنا بەپەروەردگارمان بۆ ئەوەی لەتاوانەکانمان خۆش ببێت ھەروەھا لەو جادوەش کە بەزۆر پێت دەکردین وەخوای گەورە (پاداشتی) چاکترەو بەردەوامترە');
INSERT INTO "bamoki" VALUES(20,74,'20:74','بەڕاستی ھەر کەسێک بگەڕێتەوە لای پەروەردگاری بەتاوانباری ئەوە بێگومان دۆزەخ جێگایەتی نەدەمرێت تێیدا و نەدەژی (بەئاسوودەیی)');
INSERT INTO "bamoki" VALUES(20,75,'20:75','وە ئەو کەسەی بەباوەڕداری بگەڕێتەوە لای (خوای گەورە) بەڕاستی کردەوەی چاکیشی کرد بێت ئائەوانە پلەو پایەی بەرزو بڵندیان ھەیە');
INSERT INTO "bamoki" VALUES(20,76,'20:76','کە بەھەشتانێکە جێگای مانەوەی ھەمیشەییە ڕوبارەکان دەڕۆن بە ژێریاندا بێ بڕانەوە دەمێننەوە تێیدا ئەوە پاداشتی کەسێکە خۆی پاک و خاوێن ڕابگرێت (لە کوفر و تاوان)');
INSERT INTO "bamoki" VALUES(20,77,'20:77','بەڕاستی نیگامان نارد بۆ موسا کە بەندەکانم بە شەو ببە (و لە میصر دەرچن) ڕێگەیەکی ووشکیان لە دەریای (سوور)دا بۆ بکەرەوە مەترسە (فیرعەون) پێت بگات وە مەترسە (لەخنکان لەدەریادا)');
INSERT INTO "bamoki" VALUES(20,78,'20:78','ئەمجا فیرعەون بە سەربازەکانیەوە شوێنیان کەوتن جا لە ناکاو دەریا ئەوانی (لەناو شەپۆلی خۆیدا) داپۆشی چ داپۆشینێک');
INSERT INTO "bamoki" VALUES(20,79,'20:79','فیرعەون گەلەکەی خۆی گومڕا کرد و ڕێنمونی نەکردن');
INSERT INTO "bamoki" VALUES(20,80,'20:80','ئەی نەوەی ئیسرائیل بەڕاستی ڕزگارمان کردن لەدوژمنەکەتان وە بەڵێنمان پێدان (بەھاتنی موسا بۆ) لای ڕاستی کێوی طور و بۆمان ناردن گەزۆ و شێلاقە (باڵندەیەکە گۆشتی لە ھەموو باڵندەیەک خۆشترە)');
INSERT INTO "bamoki" VALUES(20,81,'20:81','بخۆن لەو ڕۆزیە پاکانەی پێمانداون و زیادە ڕۆی تێدا مەکەن ئەگینا خەشم و قینی من دەتانگرێتەوە جا ھەر کەسێک خەشم وقینی من دایگرێت ئەوە بێگومان تیاچووەو ھەڵدێراوە');
INSERT INTO "bamoki" VALUES(20,82,'20:82','وە بەڕاستی من زۆر لێبوردوم لەو کەسەی تەوبەی کردبێت و باوەڕی ھێنابێت و کردەوەی چاکی کردبێت پاشان بەردەوام بوو بێت لەسەر ئەو رێبازە');
INSERT INTO "bamoki" VALUES(20,83,'20:83','(خوا فەرمووی) ئەی موسا بۆ چی پێش گەلەکەت کەوتی (بۆ گەیشتن بە کێوی طور)');
INSERT INTO "bamoki" VALUES(20,84,'20:84','(موسا) فەرمووی گەلەکەم ئەوانەن بە دوامەوەن بۆیە بەپەلە ھاتم بۆ لات ئەی پەروەردگارم بۆ ئەوەی لێم ڕازی بیت');
INSERT INTO "bamoki" VALUES(20,85,'20:85','(خوای گەورە) فەرمووی جا بەڕاستی ئێمە گەلەکەتمان تاقیکردەوە لەپاش (ھاتنی) تۆ وە سامیری گومڕای کردن');
INSERT INTO "bamoki" VALUES(20,86,'20:86','ئەمجا (پاش وەرگرتنی تەورات) موسا گەڕایەوە بۆ لای گەلەکەی بە تووڕەیی و خەشم وقینەوە ووتی ئەی گەلەکەم ئایا پەروەردگارتان بەڵێنی پێ نەدان بە بەڵێنی چاک بۆچی من زۆرم پێ چوو یان ھەر ویستتان وڕەیی وخەشم لەلایەن پەروەردگارتانەوە یەخەتان بگرێت بۆیە بەڵێنی منتان شکاند');
INSERT INTO "bamoki" VALUES(20,87,'20:87','ووتیان ئێمە بەڵێنەکەی تۆمان نەشکاند بەویست و دەسەڵاتی خۆمان بەڵکو ئێمە چەند کۆڵ و بارێک درابوو بەکۆڵماندا لەخشڵی قیبتیەکان جا فڕێماندایە (ناو ئاگرەوە) ھەروەھا سامیریش فڕێیدا');
INSERT INTO "bamoki" VALUES(20,88,'20:88','ئیتر (سامیری لەو خشڵە تواوانە) پەیکەری گوێرەکەیەکی بۆ دروستکردنبۆڕەیەکی ھەبوو (وەک بۆڕەی گوێرەکە) ئەمجا ووتیان ئەمە خوای ئێوە و خوای موساشە ئەوە (موسا) لەبیری چووە (دەگەڕێت بەشوێن خوادا)');
INSERT INTO "bamoki" VALUES(20,89,'20:89','ئایا ئەوان نازانن (نابینن) (ئەو گوێرەکەیە) وەڵامی ھیچ ووتەیەکیان ناداتەوە وە خاوەنی ھیچ زیان و قازانجێک نیە بۆیان');
INSERT INTO "bamoki" VALUES(20,90,'20:90','سوێند بەخوا بەڕاستی ھارون پێی ووتن لەوەو پێش ئەی گەلەکەم بێگومان ئێوە بەم (گوێرەکەیە) تاقیکرانەوە (گومڕا بوون) بەڕاستی پەروەردگاری ئێوە خوای میھرەبانە کەواتە شوێنی من بکەون گوێرایەڵی فەرمانی من بن');
INSERT INTO "bamoki" VALUES(20,91,'20:91','ووتیان ھەرگیز واز ناھێنین و بەدەوریدا دێین و دەیپەرستین ھەتا موسا دەگەڕێتەوە بۆ لامان');
INSERT INTO "bamoki" VALUES(20,92,'20:92','(موسا) ووتی ئەی ھارون چ شتێک بەرگری کردیت کاتێک بینیت ئەمانە گومڕا بوون');
INSERT INTO "bamoki" VALUES(20,93,'20:93','(لەوەی) کەشوێن من بکەویت ئایا سەرپێچی فەرمانی منت کرد؟');
INSERT INTO "bamoki" VALUES(20,94,'20:94','(ھارون) فەرمووی ئەی کوڕی دایکم موی ڕیش و سەرم مەگرە و ڕایمەکێشە بەڕاستی ترسام کە (ھاتیتەوە پێم) بڵێیت تۆ جیاوازیت خستە نێوان بەنی ئیسرائیلیەکانەوە و چاوەڕوانی فەرمانی منت نەکرد');
INSERT INTO "bamoki" VALUES(20,95,'20:95','(موسا) ووتی ئەی سامیری تۆ ئەم کارە ترسناکە چی بوو کردت؟');
INSERT INTO "bamoki" VALUES(20,96,'20:96','(سامیری) ووتی من شتێکم زانی ئەوان نەیانزانی (یامن شتێکم بینی ئەوان نەیان بینی) ئەمجا من مشتێکم لەشوێن پێی(ووڵاخەکەی) جوبرەیل ھەڵگرت وە پرژاندم (بەخشڵە تواوەکەدا) بەو جۆرە نەفسم ئەو کارەی بۆ جوان وشیرین کردم');
INSERT INTO "bamoki" VALUES(20,97,'20:97','(موسا) ووتی دەبڕۆ ئەوسا بێگومان تاماوی لەم ژیانەدا (ھەر کەس لێت نزیک بویەوە) دەبێ بڵێیت: نزیکم مەکەوەرەوە، ودەستم لێ مەدە وەبێگومان بەڵێنێکیشت ھەیە (لەڕۆژی دواییدا) ھەرگیز ڕزگار نابیت لێی وەسەیری پەرستراوەکەشت کە کەبەردەوام دەتپەرست دڵنیابە چاک دەیسوتێنین پاشان بێگومان (خۆڵەمێشەکەش) پرژو بڵاو دەکەینەوە بەدەریادا بەپرژو بڵاوێکی وەھا (کە شوێنەواریشی نەمێنێت)');
INSERT INTO "bamoki" VALUES(20,98,'20:98','پەرستراوی (ڕاستەقینەی) ئێوە تەنھا خوایە کە ھیچ پەرستراوێکی ڕاست نیە ئەو نەبێت زانیاری ئەو ھەموو شتێکی گرتۆتەوە');
INSERT INTO "bamoki" VALUES(20,99,'20:99','(ئەی موحەممەد صلى الله عليه وسلم) ئا بەو جۆرە دەیگێڕینەوە بۆت ھەندێک لەبەسەرھات و ھەواڵی ئەو (گەلانەی) ڕابوردون بێگومان پێمان داویت لەلایەن خۆمانەوە قورئان');
INSERT INTO "bamoki" VALUES(20,100,'20:100','ھەر کەسێک پشتی تێ بکات ئەوە بەڕاستی ھەڵیدەگرێت لەڕۆژی دواییدا بارێکی قورس (لەتاوان)');
INSERT INTO "bamoki" VALUES(20,101,'20:101','بێ بڕانەوە تێیدا دەمێننەوە چ بارێکی خراپ و بێـفەڕیان بەکۆڵەوەیە لەرۆژی دواییدا');
INSERT INTO "bamoki" VALUES(20,102,'20:102','ئەو ڕۆژەی کە فوو دەکرێت بەشەیپوردا (بۆ جاری دووەم) ئەوسا تاوانباران کۆدەکەینەوە لەو ڕۆژەدا لەش و چاویان شین ھەڵگەڕاوە');
INSERT INTO "bamoki" VALUES(20,103,'20:103','بەچرپەوە لەنێوان خۆیاندا دەڵێن ئێوە تەنھا دە (شەوو ڕۆژ لەدونیادا) ماونەتەوە');
INSERT INTO "bamoki" VALUES(20,104,'20:104','ئێمە چاک دەزانین بەوەی ئەوان دەیڵێن کاتێک (ئەوەیان) کە ژیرتریانە و نزیک تریانە لەڕاستیەوە دەڵێ ئێوە تەنھا یەک ڕۆژ ماونەتەوە');
INSERT INTO "bamoki" VALUES(20,105,'20:105','(ئەی موحەممەد صلى الله عليه وسلم) پرسیارت لێ دەکەن سەبارەت بەشاخەکان تۆش بڵێ پەروەردگار لەبێخیان دەھێنیت و وردو خاشیان دەکات ئەمجا ھەوا پرژو بڵاویان دەکاتەوە');
INSERT INTO "bamoki" VALUES(20,106,'20:106','ئەمجا ڕێک و ساف بێ ئاوو گژوگیاو دەیھێڵێتەوە');
INSERT INTO "bamoki" VALUES(20,107,'20:107','نابینی تێیدا ھیچ جۆرە لاری و بەرزی و نزمیەک');
INSERT INTO "bamoki" VALUES(20,108,'20:108','لەو ڕۆژەدا ھەموو دەکەونە شوێن جاڕدەری (کۆکردنەوە) لارە جڵەوی ناکەن لەفەرمانی لەترسی خوای میھرەبان ھەموو دەنگەکان نزم دەبن چپەچپ نەبێت شتێک نابیستی');
INSERT INTO "bamoki" VALUES(20,109,'20:109','ئا لەو ڕۆژەدا تکا کردن سوودی نیە مەگەر کەسێک خوای میھرەبان ڕێگای بدات و لەووتەکەشی ڕازی بێت');
INSERT INTO "bamoki" VALUES(20,110,'20:110','(خوا) دەزانێت و ئاگادارە بەھەموو ئەو شتانەی لەپێشیانەوەیە (لەقیامەتدا چاوەڕێیانە) وە بەھەموو ئەو شتانە لەپاشیانەوەیە (لە دونیادا کردوویانە) بەڵام ئەوان دەربارەی خوا ھەموو شتێک نازانن');
INSERT INTO "bamoki" VALUES(20,111,'20:111','ھەموو دەم و چاوەکان (لەڕۆژی قیامەتدا) ملکەچ و فەرمانبەردارن بۆ (خوای) ھەمیشە زیندوو و ڕاگری بوونەوەرە بێگومان زەرەرمەند بووە ئەوەی کەباری ستەمی ھەڵگرتبێ');
INSERT INTO "bamoki" VALUES(20,112,'20:112','ئەوەش کردەوە چاکەکانی کردبێت باوەڕداریش بوو بێت ئەوە نەلە ستەم دەترسێت (لێی بکرێت) نەلە کەم کردنەوەی پاداشتی (دەترسێت)');
INSERT INTO "bamoki" VALUES(20,113,'20:113','ئابەو جۆرە قورئانێکی عەرەبیمان دابەزاندووە بە چەند جۆر و شێواز ھەڕەشەمان لەودا باسکردووە بۆ ئەوەی لە خوا بترسن و خۆیان بپارێزن (لە تاوان) یان ببێت بەھۆی بیدارکردنەوەیان لەغەفڵەت وبێ ئاگایی (بەھۆی قورئانەوە)');
INSERT INTO "bamoki" VALUES(20,114,'20:114','ئەمجا بەرزو بڵندە ئەو خوایە کەدەسەڵات دارێکی(پادشایەکی) حەق و ڕاستە پەلە مەکە لە(خوێندنی) قورئاندا لەپێش ئەوەی وەحی کردنەکەی بۆت تەواو بێت وەبڵێ ئەی پەروەردگارم زانست و زانیاریم زیاد بکە');
INSERT INTO "bamoki" VALUES(20,115,'20:115','سوێند بەخوا بەڕاستی ئێمە پێشتر پەیمان و بەڵێنمان لەئادەم وەرگرت (لەدرەختی قەدەغەکراو نەخوات) بەڵام کاری پێ نەکرد (لەبیری چوو) وە ئێمە لەودا لێبڕانمان نەدی (کە بیەوێت بێ فەرمانی خودا بکات)');
INSERT INTO "bamoki" VALUES(20,116,'20:116','(باسی) ئەو کاتە بکە بەفریشتەکانمان ووت: سوژدە بەرن بۆ ئادەم ئەوانیش ھەموو سوژدەیان برد جگە لەشەیتان کەسەرپێچی کرد(و سوژدەی نەبرد)');
INSERT INTO "bamoki" VALUES(20,117,'20:117','ئەمجا ووتمان ئەی ئادەم بەڕاستی ئەم (ئیبلیسە) دوژمنی تۆو ھاوسەرەکەتە کەواتە (ئاگاداربن) لە بەھەشت دەرتان نەکات چونکە دوو چاری ناخۆشی و زەحمەت دەبیت');
INSERT INTO "bamoki" VALUES(20,118,'20:118','بێگومان تۆ لەو (بەھەشتە) دا برسیت نابێت وڕووت و قوت نابیت');
INSERT INTO "bamoki" VALUES(20,119,'20:119','وبێگومان لەوێدا تینووت نابێت وە ھەتاوی (خۆر) لێت نادات (ئازارت پێ ناگەیەنێ)');
INSERT INTO "bamoki" VALUES(20,120,'20:120','ئەمجا شەیتان نیازی خراپی خستە دڵیەوە ووتی: ئەی ئادەم ئایا نیشانت بدەم دەرەختی نەمری (ھەر کەس لێی بخوات ھەرگیز نامرێت) و موڵک و دەسەڵاتێ ھەر دوایی نەیەت');
INSERT INTO "bamoki" VALUES(20,121,'20:121','ئەمجا ھەردووکیان لەو (بەروبوومی درەختە) یان خوارد بەو ھۆیەوە (جل وبەرگیان داماڵدراو) دەرکەوت عەیب و عەورەتیان وە دەستیان کردبە داپۆشینی لەشیان بەگەڵای (درەختەکانی) بەھەشت وەئادەم سەرپێچی فەرمانی پەروەردگاری کرد ئیتر لەڕێگای ڕاست لایدا');
INSERT INTO "bamoki" VALUES(20,122,'20:122','پاشان پەروەردگاری ھەڵیبژارد (بۆ پێغەمبەرێتی) وتەوبەی لێ قبووڵ کردو ڕێنمونی کرد');
INSERT INTO "bamoki" VALUES(20,123,'20:123','(خوا) فەرمووی ھەردووکتان برۆِنە خوارەوە لەوێ (لەبەھەشت)بەیەکەوە (نەوەکانتان) دەبنە دوژمنی یەکتری جا ئەگەر (بەپێغەمبەراندا) پەیامتان بۆ ھات لەلایەن منەوە ئەوسا ئەوەی شوێنی ڕێنمونی من بکەوێت ئەوە (لەدونیادا) گومڕا نابێت و (لەڕۆژی دوایشدا) تووشی ناخۆشی و زەحمەت نابێت');
INSERT INTO "bamoki" VALUES(20,124,'20:124','وە ھەرکەسێک ڕوو وەرگێرێت لەڕێبازو یادی من ئەوە بێگومان ئەو کەسە گوزەرانێکی سەخت و تەنگی دەبێت (لەگۆڕدا) و لەڕۆژی دواییشدا حەشری دەکەین بەکوێری');
INSERT INTO "bamoki" VALUES(20,125,'20:125','دەڵێ ئەی پەروەردگارم بۆچی بە کوێری حەشرت کردم خۆ من (لە دونیادا) چاو ساغ و بینا بووم');
INSERT INTO "bamoki" VALUES(20,126,'20:126','(خوا) دەفەرموێت ھەر بەو شێوەی ئایەتەکانی ئێمەت بۆ ھات و لەبیری خۆت بردەوە (و کوێر بوویت لەئاستیدا) بەھەمان شێوە تۆش ئەمڕۆ گوێت پێ نادرێت و لەبیر دەکرێیت');
INSERT INTO "bamoki" VALUES(20,127,'20:127','وەھەر بەو جۆرە تۆڵە دەستێنین لەوەی زیادەڕۆی کردبێت و باوەڕی نەھێنا بێت بەئایەتەکانی پەروردگاری وە سزای ڕۆژی دوایی بەتینترو بەردەوامترە (لەسزای دونیا)');
INSERT INTO "bamoki" VALUES(20,128,'20:128','دەی ئایا (خوا) بۆی ڕوون نەکردوونەتەوە چەندمان لە ناو برد لەگەل و نەوەی چەرخەکانی پێش ئەمان کە(ئەم بێ باوەڕانە) دەگەڕێن و ھات و چۆ دەکەن بەجێ و شوێنەکانیاندا بەڕاستی لەوەدا (کەباسکرا) چەند بەڵگە و نیشانەی ھەیە بۆ ئەو کەسانەی کەژیرن');
INSERT INTO "bamoki" VALUES(20,129,'20:129','ئەگەر لەبەر بەڵێن و بڕیارێک نەبوایە لەپەروەردگارتەوە پێشتر دراوە (بۆ لەناوبردنیان) بێگومان (لەناو بردنی بێ باوەڕان) پێویست دەبوو ئەگەر ماوەی دیاری کراویش نەبوایە (کەقیامەتە)');
INSERT INTO "bamoki" VALUES(20,130,'20:130','کەواتە (ئەی موحەممەد صلى الله عليه وسلم) ئارام بگرە لەسەر ئەوەی ئەوانە دەیڵێن بەحەمد و سوپاس وستایشی پەروەردگارت تەسبیحاتی خودا بکە لەپێش ھەڵاتنی خۆر و لەپێش ئاوا بوونیـدا و لەھەندێ کات و ساتەکانی شەودا خوا بپەرستە ونوێژ بکە وە لە سەرەتاو کۆتایی ڕۆژدا بۆ ئەوەی (پاداشتێ بدرێیتەوە) کەپێی ڕازی بیت');
INSERT INTO "bamoki" VALUES(20,131,'20:131','تۆ چاوەکانت مەبڕە ئەو نازو نیعمەتانەی داومانە بەتاقم و دەستەیەک لەوان (لەبێ باوەڕان) (چونکە ئەوە) زینەت و جوانی ئەم ژیانی دونیایە بۆ ئەوەیە تاقییان بکەینەوە تیایدا دڵنیابە ڕزق و ڕۆزی پەروەردگارت (لەدوا رۆژدا) چاکترو بەردەوامترە');
INSERT INTO "bamoki" VALUES(20,132,'20:132','(ئەی موحەممەد صلى الله عليه وسلم) فەرمان بدە بەئەندامانی خێزانەکەت بەنوێژ کردن، وە خۆیشت خۆڕاگرو بەردەوام بە لەسەر بەجێ ھێنانی ئێمە داوای ڕۆزیت لێ ناکەین بەڵکو ئێمە ڕزق و رۆزی تۆ دەدەین وسەرەنجامی چاک بۆ پارێزکاران و خواناسانە');
INSERT INTO "bamoki" VALUES(20,133,'20:133','(بێ باوەڕان) ووتیان (ئەگەر ڕاستە پێغەمبەرە) دەی بۆ بۆمان ناھێنێت بەڵگەو موعجیزەیەک لەلایەن پەروەردگاریەوە ئایا ئەوان بەڵگەی ئاشکرایان بۆ نەھاتووە کە لەپەڕاوە پێشووەکاندا ھەبوو (وەک تەورات و ئینجیل)');
INSERT INTO "bamoki" VALUES(20,134,'20:134','ئەگەر ئێمە ئەوانمان لەناو ببردایە بەسزایەک پێش (ناردنی پێغەمبەر صلى الله عليه وسلم) بێگومان (لەدوا ڕۆژدا) دەیانووت ئەی پەروەردگارمان بۆچی (لەدونیادا) پێغەمبەرێکت بۆ نەناردین تاشوێنی بەرنامە و ئایەتەکانی تۆ بکەوتینایە پێش ئەوەی سەرشۆڕ و ڕیسوا ببین');
INSERT INTO "bamoki" VALUES(20,135,'20:135','(ئەی موحەممەد صلى الله عليه وسلم) بڵێ: ھەموو چاوەڕوانین (چ لایەکمان سەردەکەوین)دە ئێوەش چاوەڕوان بن ئەوسا لە ئایندەدا دەزانن چ کەسێک خاوەنی ڕێگای ڕاستە وە چ کەسێک ھیدایەتی خوای وەرگرتووە');
INSERT INTO "bamoki" VALUES(21,1,'21:1','لێپرسینەوەی خەڵکی نزیک بۆتەوە بەڵام ئەوان لە بێ ئاگایی و غەفڵەتدا پشت تێکەر و ڕوو وەرگێڕن (لە ئاینی خوا)');
INSERT INTO "bamoki" VALUES(21,2,'21:2','ھەر پەیامێکی تازەیان لە پەروەردگاریانەوە بۆ بێت (کە قورئانە) ئەوان دەیبیستن و گاڵتـەی پێ دەکــەن');
INSERT INTO "bamoki" VALUES(21,3,'21:3','دڵەکانیان بێ ئاگایە زۆر بە نھێنی چپەیان دەکرد ئەوانەی کە ستەمیان کرد (دەڵێن) ئایا ئەم (پێغەمبەر)یش ھەربەشەرێک نیە وەک ئێوە دەی ئایا ئێوە دەڕۆن بە دوای جادودا لە کاتێکدا ئێوە دەبینن (دەزانن ئەوەی موحەممەد صلى الله عليه وسلم دەیڵێ سیحرە)');
INSERT INTO "bamoki" VALUES(21,4,'21:4','(پێغەمبەر صلى الله عليه وسلم) فەرمووی پەروەردگارم بە ھەموو قسەیەک (چرپە یەک) دەزانێت لە ئاسمان بێت یا لە زەوی ئەو زاتە بیسەر و زانایە');
INSERT INTO "bamoki" VALUES(21,5,'21:5','ئەوانە ووتیان: نەخێر (ئەوەی موحەممەد ھێناویەتی نیگا نیە) بەڵکو چەند خەو بینینێکی تێکەڵ و پێکەڵی (موحـەممەدە خۆیەتی) نا بەڵکو موحەممەد خۆی ئەم (قورئانە)ی ھەڵبەستووە نا بەڵکو ئەو (موحەممەدە) شاعیرە دەبا نیشانە وموعجیزەیەکمان بۆ بھێنێت وەک (ئەو موعجیزانەی) پێغەمبەرانی پێشووی پێ نێردرابوون');
INSERT INTO "bamoki" VALUES(21,6,'21:6','بڕوایان نەھێنا (خەڵکی) ھیچ شار و گوندێک پێش ئەمان کەلەناومان بردوون دەی ئایا ئەمان باوەڕ دەھێنن؟');
INSERT INTO "bamoki" VALUES(21,7,'21:7','نەمان ناردووە پێش تۆ (ئەی موحەممەد صلى الله عليه وسلم) جگە لە پیاوانێک کە نیگا و سرووشمان بۆ کردوون دەی پرسیار بکە لە خاوەنی کتێبەکانی (پێشوو لە زانایانی گاور و جولەکە) ئەگەر ئێوە نازانن');
INSERT INTO "bamoki" VALUES(21,8,'21:8','ئێمە ئەو پێغەمبەرانەمان نەکردوەتە پەیکەرێک کە خواردن نەخۆن وە ئەوان ھەمیشەیی و نەمر نەبوون');
INSERT INTO "bamoki" VALUES(21,9,'21:9','پاشان بەڵێنێک کە پێمان دابوون بەجێمان ھێنا جا ئەوانە و ھەرکەسێکمان دەویست (لە باوەڕداران) ڕزگارمان کردن وە زیادەڕۆیانمان لەناوبرد');
INSERT INTO "bamoki" VALUES(21,10,'21:10','سوێند بەخوا ناردومانەتە خوارەوە کتێبێک بۆ ئێوە کە سەربەرزی و ناوبانگی ئێوەی تێدایە دەی ئایا بۆ بیر ناکەنەوە');
INSERT INTO "bamoki" VALUES(21,11,'21:11','چەندەھا شارو گوندمان تێک شکاند (ولەناومان بردن) کە (خەڵکەکەی) ستەمکار بوون وەپاش ئەوان گەل و تاقمی ترمان (لەجێیان) بەدی ھێنا');
INSERT INTO "bamoki" VALUES(21,12,'21:12','جا کاتێک (بێ باوەڕان) ھەستیان کرد بەسزاو تۆڵەی ئێمە دەستبەجێ ئەوان لەو شارانە بەخێرایی ڕایان دەکرد');
INSERT INTO "bamoki" VALUES(21,13,'21:13','(فریشتەکان بەگاڵتەوە پێیان دەڵێن) ڕامەکەن وبگەڕێنەوە بۆلای ئەو نازو نیعمەتانەی کە ڕۆچوو بوون تێیدا وە(بگەڕێنەوە) بۆ سەر خانوو ماڵەکانتان بەڵکو پرسیارتان لێ بکرێت (لەوەی بەسەرتان ھاتووە)');
INSERT INTO "bamoki" VALUES(21,14,'21:14','(لەگەڵ ھەڵھاتندا) دەیانووت: ھاوار بۆ ئێمە بێگومان ئێمە ستەمکار بووین');
INSERT INTO "bamoki" VALUES(21,15,'21:15','ئیتر بەردەوام ئەمە ھاواریان بوو (ھاوار بۆ ئێمە) ھەتا ھەموویانمان دروێنە و خامۆش کرد (ولەناومان بردن)');
INSERT INTO "bamoki" VALUES(21,16,'21:16','ئێمە دروستمان نەکردووە ئاسمان و زەوی و ئەوەی لەنێوانیاندایە بەگاڵتەو بەبێ ھودە');
INSERT INTO "bamoki" VALUES(21,17,'21:17','ئەگەر بمان وویستایە سەرقاڵی بۆ خۆمان ھەڵبژێرین (وەک ھاوسەرو منداڵ) لەلایەن خۆمانەوە ھەڵمان دەبژارد');
INSERT INTO "bamoki" VALUES(21,18,'21:18','(واز لەوە بھێنە) بەڵکو حەق دەدەین بەسەر بەتاڵ و ناحەقدا بەھۆیەوە (بەتاڵ) مێشکی دەپژێت و تێکدەشکێت ئەمجا دەستبەجێ بەتاڵ دەفەوتێت و لەناو دەچێت ھاوار (وسزای بەتین) بۆ ئێوە لەبەر ئەوەی کە دەیڵێن و بڕیاری دەدەن بۆ خوا (لەھاوسەرو منداڵ)');
INSERT INTO "bamoki" VALUES(21,19,'21:19','تەنھا بۆ خوایە ئەوەی لەئاسمانەکان وزەویدایە وە ئەو (فریشتانە) کە لای خوان ھەرگیز لەپەرستنی ئەو خۆیان بەزل نازانن ھەرگیزیش ماندوو نابن');
INSERT INTO "bamoki" VALUES(21,20,'21:20','شەوو ڕۆژ تەسبیحات و ستایشی (خودا دەکەن) ماندوونابن و سارد نابنەوە');
INSERT INTO "bamoki" VALUES(21,21,'21:21','ئایا ئەوانە ھەڵیان بژاردووە (وبڕیاریانداوە) چەند پەرستراو و (خوایەک) لەزەویدا (بۆ خۆیان) (کە دەسەڵاتی ئەوەیان نیە مردوو زیندوو بکەنەوە) وخۆیان کۆدەکرێنەوە وزیندوو دەکرێنەوە');
INSERT INTO "bamoki" VALUES(21,22,'21:22','ئەگەر لەھەردووکیاندا (لە ئاسمان و زەویدا) چەند پەرستراوێک ھەبوایە جگە لەخوا بێگومان (ئاسمان و زەوی) ھەر دوو کاول وێران دەبوون دەی پاک و بێگەردی بۆ خوای پەروەردگاری عەرش لەوەی بێ باوەڕان (خوای گەورەی) پێ وەێف دەکەن');
INSERT INTO "bamoki" VALUES(21,23,'21:23','(خوا) پرسیاری لێ ناکرێت (ڕەخنەی لێ ناگیرێت) لەھەر کارێک کەدەیکات بەڵکو ئەوان (ئادەمیزاد) پرسیاریان لێ دەکرێت');
INSERT INTO "bamoki" VALUES(21,24,'21:24','ئایا ئەوانە ھەڵیان بژاردووە (وبڕیاریان داوە) چەند پەرستراوێک جگە لەخوا (پێیان) بڵێ بەڵگەتان بھێنن (لەسەر ئەوەی بڕیارتانداوە) ئەمە قسەی ئەم قورئانەیە (خوا ناردویەتی) بۆ من وەقسەی ئەو (پەیام وپەڕاوانەشە) لەپێش منەوە ھاتوون (وەک: تەورات و ئینجیل، لەھیچیاندا بوونی چەند پەرستراوێک بڕیار نەدراوە بۆ خوای گەورە) بەڵام زۆربەی زۆریان حەق و ڕاستی نازانن لەبەر ئەوە پشتی تێدەکەن و ڕووی لێ وەردەگێڕن');
INSERT INTO "bamoki" VALUES(21,25,'21:25','وەئێمە نەمان ناردووە پێش تۆ ھیچ پێغەمبەرێک مەگەر نیگامان بۆ ناردووە (بەوەی کە) بێگومان جگە لەمن ھیچ پەرستراوێکی ڕاست نیە کەواتە تەنھا من بپەرستن');
INSERT INTO "bamoki" VALUES(21,26,'21:26','(وەبێ باوەڕان) ووتیان خوای میھرەبان منداڵی بۆ خۆی بڕیارداوە (کە فریشتەکانن) پاک و بێگەردە خوا (لەم شتانە) بەڵکو ئەوانە ( ئەوفریشتانە) بەندەی ڕێز گیراوی (خوان)');
INSERT INTO "bamoki" VALUES(21,27,'21:27','لە خوا پێش ناکەون بەقسەو ووتەیان وەئەوان بەفەرمانی خوا کار دەکەن');
INSERT INTO "bamoki" VALUES(21,28,'21:28','(خوا) دەزانێت وئاگادارە بە ئەوەی لەدونیا لەبەر دەستیاندایە وە بە ئەوەی لەڕۆژی دواییدا بۆیان ئامادەکراوە وە (فریشتەکان) تکا ناکەن تەنھا بۆ ئەو کەسە نەبێت (خوا) ڕازی بێت (تکای بۆ بکرێت) وە ئەوان (فریشتەکان) لەترس وھەیبەتی خوا زۆر دەترسن');
INSERT INTO "bamoki" VALUES(21,29,'21:29','وە ھەرکام لەوانە (لەو فریشتانە) بڵێ بێگومان منیش پەرستراوم جگە لەخوا ئەو بە (ئاگری) دۆزەخ تۆڵەی لێ دەسێنین ھەربەوجۆرە پاداشتی ستەمکاران دەدەینەوە (تۆڵەیان لێ دەکەینەوە)');
INSERT INTO "bamoki" VALUES(21,30,'21:30','ئایا نەیان بینی (نەیانزانیوە) ئەوانە بێ بڕوا بوون کەئاسمانەکان و زەوی ھەردووکیان پێکەوە لکابوون (ویەک پارچە بوون) ئەمجا لەیەکترمان جیاکردنەوە وە فەراھەممان ھێنـا لەئاو ودروستمان کردووە ھەموو شتێکی زیندوو دەی ئایا ئەوانە ھەر باوەڕ ناھێنن؟');
INSERT INTO "bamoki" VALUES(21,31,'21:31','وەدروستمان کردووە (ودامان ناوە) لەزەویدا چەند شاخی دامەزراو بۆ ئەوە جووڵە جوڵ نەکات بەوانەی (بەسەر پشتیوەن) وە سازاندومانە لە زەوی (یان لە شاخەکان)دا دۆڵ و ڕێگاوبانی زۆر تا ئەوان شارەزا بن و پێویستی خۆیان دابین بکەن');
INSERT INTO "bamoki" VALUES(21,32,'21:32','وە ئاسمانمان کردووە بەسەقفێکی (سەرێکی) پارێزراو بەڵام ئەوانە سەبارەت بە ئایەتەکانی ئاسمان پشت تێکەرو ڕوو وەرگێر بوون');
INSERT INTO "bamoki" VALUES(21,33,'21:33','وە ھەر ئەو (خوا)یە کە شەو و ڕۆژی دروستکردووە وە خۆر و مانگیشی (دروست کردووە) ھەریەک لەوان لە خولگەیەکدا دەسوڕێنەوە');
INSERT INTO "bamoki" VALUES(21,34,'21:34','بڕیارمان نەداوە بۆ ھیچ کەسێک لەپێش تۆوە مانەوەی ھەمیشەیی (نەمری) ئەمجا ئەگەر تۆ بمری (دوای مردنی تۆ) ئایا ئەوان ژیانیان بەردەوام دەبێ و نامرن؟ (نەخێر)');
INSERT INTO "bamoki" VALUES(21,35,'21:35','ھەموو کەسێک مردن دەچێژێت (ودەمرێت) وە ئێمە تاقیتان دەکەینەوە بەخراپە وناخۆشی وە بە چاکە و خۆشی تادەرکەوێت (چاک و خراپ) وە ھەر بۆ لای ئێمە دەگەڕێنەوە');
INSERT INTO "bamoki" VALUES(21,36,'21:36','(ئەی موحەممەد صلى الله عليه وسلم) کاتێ تۆ دەبینن ئەوانەی کە بێ باوەڕن جگە لەگاڵتە و لاقرتی کردن بە تۆ ھیچی تر ناکەن (بەسەرسامیەکەوە دەڵێن) ئا ئەمە ئەو کەسەیە (بەخراپی) باسی بتەکانتان دەکات لەکاتێکدا خۆیان بەباسکردنی (قورئان) وزیکری خوای میھرەبان بێ بڕوان');
INSERT INTO "bamoki" VALUES(21,37,'21:37','(سرووشتی) ئینسان وایە لەسەر پەلەکردن بەدی ھێنراوە بەزوویی تۆڵەی خۆمتان نیشان دەدەم کەواتە پەلە مەکەن');
INSERT INTO "bamoki" VALUES(21,38,'21:38','وە (بت پەرستان بەگاڵتەوە) دەڵێن ئەم بەڵێنی (تۆڵەیە) کەی دێتئەگەر ئێوە ڕاستگۆن');
INSERT INTO "bamoki" VALUES(21,39,'21:39','ئەگەر ئەوانەی کە بێ باوەرنِ بیانزانیایە (چ سزایەکی سەخت لەبەر دەمیاندایە) کاتێ ناتوانن دووری بخەنەوە بڵێسەی ئاگر لەدەم وچاو و پشتیان وەئەوان یارمەتی نادرێن');
INSERT INTO "bamoki" VALUES(21,40,'21:40','بەڵکو لەناکاودا تووشیان دێت وسەرسامیان دەکات (بەشێوەیەک) نەدەتوانن بیگێڕنەوە (ڕێگری بکەن) وە نەمۆڵەتیشیان دەدرێت (تەوبە بکەن)');
INSERT INTO "bamoki" VALUES(21,41,'21:41','سوێند بەخوا بێگومان گاڵتە و لاقرتی کراوە بەپێغەمبەرانی پێش تۆش ئەمجا دەوری ئەوانەیدا لەوان کەگاڵتەیان دەکرد (بەپێغەمبەران) ئەو سزایەی ئەوان گاڵتەیان پێ دەکرد');
INSERT INTO "bamoki" VALUES(21,42,'21:42','بڵێ (بەگاڵتەکاران) کێ (دەتوانێت) ئێوە بپارێزێت بەشەوو بەرۆژ لە (سزای) خوای میھرەبان بەڵکو ئەوانە بەردەوام پشت ھەڵئەکەن لەیاد وزیکری پەروەردگاریان');
INSERT INTO "bamoki" VALUES(21,43,'21:43','ئاخۆ (ئەو بێ باوەڕانە) چەند پەرستراوێکیان ھەیە جگە لەئێمە ڕێگری (سزا) یان لێ بکات (ئەو پەرستراوانە) ناتوانن یارمەتی خۆیان بدەن وە لەلایەن ئێمەشەوە پەنا نادرێن');
INSERT INTO "bamoki" VALUES(21,44,'21:44','بەڵکو ئێمە ئەوان و باو و باپیرانیانمان ژیاندووە بە خۆشی (ناز و نیعمەتی دونیامان پێدابوون) تاتەمەنی زۆریان بەسەر برد دەی ئایا ئەوانە (قوڕەیشیەکان) سەرنج نادەن کە بێگومان ئێمە (وردە وردە) دێینە خاک و وڵاتی (ئەوان) لە ھەموو لایەکی یەوە کەمی دەکەینەوە (بەڕزگار کردن لەلایەن موسڵمانانەوە) دەی ئایا ئەوان زاڵ و سەرکەوتوون (یان ئێمە)؟');
INSERT INTO "bamoki" VALUES(21,45,'21:45','(ئەی موحەممەد صلى الله عليه وسلم) بڵێ: بێگومان من تەنھا ئێوە بێدار دەکەمەوە (دەتان ترسێنم) بەنیگا و وەحی وە کەڕەکان بانگەوازی(حەق) نابیستن کاتێک کە دەترسێنرێن');
INSERT INTO "bamoki" VALUES(21,46,'21:46','سوێند بەخوا ئەگەر دوو چاریان ببێت کەمێک لە سزای پەروەردگارت بێگومان ئەوان دەڵێن ھاوارو تیاچوون بۆ ئێمە بەڕاستی ئێمە (ھەموومان) ستەمکار بووین');
INSERT INTO "bamoki" VALUES(21,47,'21:47','وەئێمە دادەنێین تەرازووەکانی دادپەروەری (کەکردەوەی پێ دەکێشرێت) لەڕۆژی قیامەتدا ئەمجا ھیچ کەس کەمترین ستەمی لێ ناکرێت با قورسی (کردەوەکەی) بەقەدەر تۆوی خەرتەلەیەکیش بێت ئێمە دەیھێنین وە بەسە کەئێمە ژمێریاری (کردەوەی بەندەکان) بین');
INSERT INTO "bamoki" VALUES(21,48,'21:48','سوێند بەخوا بەڕاستی تەورات (کەجیا کەرەوەی حەق و بەتاڵە) بەخشیمانە موسا وھارون، وەڕوونکەرەوە و یاد خەرەوەشە بۆ پارێزکاران و لە خواترسان');
INSERT INTO "bamoki" VALUES(21,49,'21:49','ئەوانەی کەدەترسن لە پەروەردگاریان بەنادیاری وپەنھانی وەئەوان (کەسانێکن) لە ڕۆژی دوایی زۆر دەترسن');
INSERT INTO "bamoki" VALUES(21,50,'21:50','وەئەم (قورئانە) یاد خەرەوەیەکی بەفەڕ وپیرۆزە ناردومانەتە خوارەوە دەی ئایا ئێوە دانی پێدا نانێن(باوەڕی پێ ناکەن)');
INSERT INTO "bamoki" VALUES(21,51,'21:51','سوێند بەخوا بەڕاستی بەخشیمان بە ئیبراھیم ڕێنمونی وشارەزایی لەپێشدا وەئێمە لە (شیاوی) ئەو ئاگادار بووین');
INSERT INTO "bamoki" VALUES(21,52,'21:52','کاتێک ووتی بە باوکی وگەلەکەی ئا ئەم پەیکەر و بتانە چین؟ کە ئێوە دەیان پەرستن (بەلایانەوە ماونەتەوە)؟');
INSERT INTO "bamoki" VALUES(21,53,'21:53','(گەلەکەی) ووتیان: باوو باپیرانمان بینیوە ئەمانەیان پەرستووە');
INSERT INTO "bamoki" VALUES(21,54,'21:54','(ئیبراھیم) ووتی بەڕاستی ئێوە خۆتان و باوو باپیرانتان لە گومڕاییەکی ئاشکرادان');
INSERT INTO "bamoki" VALUES(21,55,'21:55','(لە وەڵامدا) ووتیان ئایا ڕاستی و ھەقت بۆ ھێناوین یان تۆ گاڵتەمان پێ دەکەیت');
INSERT INTO "bamoki" VALUES(21,56,'21:56','(ئیبراھیم) ووتی (ھەقم ھێناوە) بەڵکو پەروەردگاری ئێوە پەروەردگاری ئاسمانەکان و زەویە ھەر ئەویش بەدی ھێناون وە بێگومان من لەسەر ئەوانەی (کەباسم کرد) لە شاھید وبە ئاگایانم');
INSERT INTO "bamoki" VALUES(21,57,'21:57','سوێند بە خوا پیلان دادەنێم بۆ (لەناو بردنی) بتەکانتان دوای ئەوەی پشتان تێکردن (بەجێتان ھێشتن)');
INSERT INTO "bamoki" VALUES(21,58,'21:58','ئەمجا ئیبراھیم ھەموو بتەکانی وورد کرد جگە لە بتە زلەکەیان نەبێت بە ئومێدی ئەوەی بگەڕێنەوە بۆلای ئەو (بۆ لای ئیبراھیم ئەویش ڕاستیان پێ بڵێ، بەڵکو باوەڕبھێنن)');
INSERT INTO "bamoki" VALUES(21,59,'21:59','(بت پەرستان) ووتیان کێ ئەمەی کردووە بە خواکانمان (بەبتەکانمان) بە ڕاستی ئەو کەسە لەستەمکارانە (دەبێ سزا بدرێت)');
INSERT INTO "bamoki" VALUES(21,60,'21:60','(چەند کەسێک) ووتیان گوێمان لە لاوێک بوو باسی ئەوانی (بتەکانی بە خراپە) دەکرد پێی دەڵێن ئیبراھیم');
INSERT INTO "bamoki" VALUES(21,61,'21:61','(کۆمەڵێ لە گەلەکەی) ووتیان کەواتە بیھێنن لە پێش چاوی خەڵک (بیوەستێنن) بەڵکو (ئەوانەی دەیناسنەوە) شایەتی لەسەر بدەن');
INSERT INTO "bamoki" VALUES(21,62,'21:62','ووتیان ئایا تۆ ئەمەت کردووە بە خواکانمان (بەبتەکانمان) ئەی ئیبراھیم؟');
INSERT INTO "bamoki" VALUES(21,63,'21:63','(ئیبراھیم) ووتی نەخێر بەڵکو ئەم بتە گەورەیان ئەوەی کردووە دە پرسیار لەوانە (بتە شکاوەکان) بکەن ئەگەر ئەوانە قسە دەکەن');
INSERT INTO "bamoki" VALUES(21,64,'21:64','ئەمجا گەڕانەوە بۆ لای یەکتری (سەریان کرد بەیەکدا) جا (لە ناو خۆیاندا ھەندێکیان) ووتیان بەڕاستی ئێوە ھەر خۆتان ستەمکارن');
INSERT INTO "bamoki" VALUES(21,65,'21:65','پاشان سەر بەرەو خوار کرانەوە (گەڕانەوە بۆ سەر سەختی و نەفامیەکەیان) بەڕاستی تۆ دەتزانی ئەو بتانە قسە ناکەن (ئەی ئیبراھیم)');
INSERT INTO "bamoki" VALUES(21,66,'21:66','(ئیبراھیم) ووتی دەی ئێوە دەپەرستن؟ جگە لەخوا شتانێک کە ھیچ قازانج و سوودێکتان پێ ناگەیەنن وە ھیچ زیانێکیشتان لێ نادەن');
INSERT INTO "bamoki" VALUES(21,67,'21:67','ئۆف بۆ ئێوە بۆ ئەو بتانەش کە دەیپەرستن لە جیاتی خوا دەی ئایا ژیر نابن (بیر وھۆشتان ناخەنە کار)');
INSERT INTO "bamoki" VALUES(21,68,'21:68','(ھەندێک لە بت پەرستان) ووتیان (ئیبراھیم) چاک بسوتێنن و یارمەتی خواکانتان (بتەکانتان) بدەن وسەریان بخەن ئەگەر ئێوە ئەو کارە دەکەن');
INSERT INTO "bamoki" VALUES(21,69,'21:69','(کە ئیبراھیمیان خستە ئاگرەوە) ووتمان ئەی ئاگر سارد بەرەوەو بێ زیانبە لەسەر ئیبراھیم');
INSERT INTO "bamoki" VALUES(21,70,'21:70','ئەوان ویستیان پیلان بگێڕن لە ئیبراھیم (بە سووتان لەناوی بەرن) بەڵام ئێمە ئەوانمان کردە زەرەرۆمەندترین (دۆڕاوترین) کەس');
INSERT INTO "bamoki" VALUES(21,71,'21:71','وە ئیبراھیم ولوطمان ڕزگار کرد و (ناردمانن) بۆ ئەو زەویە ( بۆ ووڵاتی شام) کە پیت و فەڕمان تێ خستبوو');
INSERT INTO "bamoki" VALUES(21,72,'21:72','وە بەخشیمان بە ئیبراھیم ئیسحاق و (بە ئیسحاقیش) یەعقوب، بە زیاد (لە داواکەی ئیبراھیم کە داوای منداڵێکی کرد) وە ھەموویانمان کرد بە پیاوی چاک');
INSERT INTO "bamoki" VALUES(21,73,'21:73','وە ئەوانمان کرد بە پێشەوا بە فەرمانی ئێمە ڕێنمونی (خەڵکیان) دەکرد وە نیگامان کرد بۆیان بە ئەنجامدانی کاری چاک وە بە نوێژ کردن و زەکاتدان وە ئەوان ھەر بۆ ئێمە عیبادەتیان دەکرد');
INSERT INTO "bamoki" VALUES(21,74,'21:74','بەخشیمان بە لوط پێغەمبەرێتی و زانیاری وە ڕزگارمان کرد لە(دەست خەڵکی) ئەو شارەی (سەدوم) کە (خەڵکەکەی) کردەوەی پیس و دزێویان دەکرد (نێربازییان دەکرد) بەڕاستی ئەوانە گەلێکی خراپی لە فەرمان دەرچوو بوون');
INSERT INTO "bamoki" VALUES(21,75,'21:75','وە لوطمان خستە ناو ڕەحمەت و میھرەبانی خۆمانەوە بەڕاستی لوط لە چاکەکاران بوو');
INSERT INTO "bamoki" VALUES(21,76,'21:76','وەپێشتر نوح پێغەمبەر صلى الله عليه وسلم ھاواری (لە ئێمە) کرد ئەمجا ئێمەش وەڵامماندایەوە وبەدەنگیەوە ھاتین خۆی و (ئیماندارانی) خێزانەکەیمان ڕزگار کرد لە تەنگانە زۆر گەورەکە (لە لافاو و خنکان)');
INSERT INTO "bamoki" VALUES(21,77,'21:77','وە یارمەتیمان دا لە دژی ئەو گەلەی کە بەڵگەو ئایەتەکانی ئێمەیان بە درۆ دەزانی بەڕاستی ئەوانە گەلێک بوون ڕۆچوون لە خراپەدا لەبەر ئەوە ھەموویانمان خنکان (لە تۆفانەکەدا)');
INSERT INTO "bamoki" VALUES(21,78,'21:78','(باسی) داود و سولەیمان بکە کاتێ بڕیاریان دەرکرد دەربارەی ئەو کشتوکاڵەی کە بەشەو ئاژەڵی کەسانێک تێیدا لەوەڕابوون (زیانیان لێ دابوو) وە ئێمە بۆ دادوەری بڕیارەکەیان ئامادە (ئاگادار) بووین');
INSERT INTO "bamoki" VALUES(21,79,'21:79','ئەوسا ئێمە ئەوەمان فێری سولەیمان کرد (سولەیمان ووتی: ئاژەڵە زیان خۆرەکە بدرێتە دەست خاوەن کشتوکاڵەکە، کەڵک لەشیر وبەرھەمیان وەربگرێت، وە کشتوکاڵەکەش بدرێتە دەست خاوەن ئاژەڵەکە بەروبوومەکەی بخۆن و خزمەتی بکەن تـا وەک پێش زیان لێکەوتنەکەی لێ دێتەوە، پاشان ھەریەک لەئاژەڵ وکشتوکاڵەکە بگەڕێنرێنەوە بۆ خاوەنەکانیان) وە بە ھەر یەک لەو دووانە (داود و سولەیمان) پێغەمبەرێتی (ودادوەری) و زانستمان پێدان وە ڕاممان ھێنا لەگەڵ داودا چیاکان و باڵندەکە تەسبیحاتی خوایان دەکرد وە ھەر ئێمە ئەوەمان کرد (بۆ داود)');
INSERT INTO "bamoki" VALUES(21,80,'21:80','وە داودمان فێری دروستکردنی پۆشاکی زرێی (جەنگی) کرد بۆ ئێوە تابتان پارێزێت لەکاتی شەڕو کوشتارتاندا دەی ئایا ئێوە سوپاسگوزارن (بۆ خوا لەسەر ئەم نیعمەتانە)');
INSERT INTO "bamoki" VALUES(21,81,'21:81','وە (ڕاممان ھێنا) بۆ سولەیمان بای توند (ڕەشەبا) بەفەرمانی سولەیمان دەڕۆیشت بۆ ئەو ووڵات و زەویەی کە پڕ پیت و بەرەکەتمان کرد بوو وە ئێمە بەھەموو شتێک ئاگاداربووین');
INSERT INTO "bamoki" VALUES(21,82,'21:82','وە ھەندێک لە شەیتانەکان (مان ڕام ھێنابوو) دەچوونە ژێری دەریا بۆ سولەیمان (تا گەوھەرو مرواری بۆ دەربھێنن) وە جگە لەوە کاری تریشیان دەکرد (بۆی وەک کۆشک دروستکردن) بەڕاستی ئێمەش چاودێر (و پارێزەری) ئەوان بووین');
INSERT INTO "bamoki" VALUES(21,83,'21:83','وە (باسی) ئەیووب (بکە) کاتێک ھاواری لە پەروەردگاری کرد (لە دوای ناساغی ماوەیەکی زۆر) ووتی (ئەی پەرورەدگارم) بە ڕاستی من زیان و ئازار و نەخۆشی دووچارم بووە و تۆش میھرەبانترینی میھرەبانانی');
INSERT INTO "bamoki" VALUES(21,84,'21:84','جا ئێمەش ھاوار و نزاکەیمان گیرا کرد و لامان برد ئەو زیان و ئازار و نەخۆشییەی کە ھەیبوو وە ھەموو ماڵ و منداڵیمان پێ بەخشیەوە (کە پێشتر مردبوون) و ئەوەندەی تریش لەگەڵیاندا ئەوە ڕەحمەتێکی تایبەت بوو لە ئێمەوە وە بیرخەرەوە و ئامۆژگاری بوو بۆ خوا پەرستان');
INSERT INTO "bamoki" VALUES(21,85,'21:85','وە (باسی) ئیسماعیل و ئیدریس وذەلکیفل (بکە) ھەموویان لە ئارامگران بوون');
INSERT INTO "bamoki" VALUES(21,86,'21:86','وە ئێمە ئەوانمان خستە ناو ڕەحمەتی خۆمان (کە بەھەشتە) بێگومان ئەوانە لە چاکەکەکاران بوون');
INSERT INTO "bamoki" VALUES(21,87,'21:87','وە (باسی) ذەننون (یونس بکە کە پێغەمبەری خەڵکی نەینەوا بوو) کاتێک ڕۆیشت بە توڕەیی (گەلەکەی بەجێ ھێشت) وایدەزانی ئێمە تەنگانەی بەسەردا ناھێنین وھەروا وازی لێ دەھێنین (خوای گەورە تاقیکردوە وفڕێدرایە ناو دەریا ونەھەنگێک قووتیدا) جا لە تاریکیەکاندا (تاریکی شەو و دەریا و ناو سکی نەھەنگ) ھاواری کرد (ئەی خوایە) بەڕاستی ھیچ پەرستراوێکی بەھەق وڕاستی نیە جگە لە تۆ پاکی و بێگەردی بۆ تۆیە، بێگومان من لە ستەمکاران بووم (لەبەر ئەوەی ئارامم نەگرت و وازم لە گەلەکەم ھێنا)');
INSERT INTO "bamoki" VALUES(21,88,'21:88','ئێمەش نزا و ھاواری ئەومان گیراکرد و لەو غەم وخەفەتە ڕزگارمان کرد ھەر بەو شێوەیە ئیماندارانیش ڕزگار دەکەین');
INSERT INTO "bamoki" VALUES(21,89,'21:89','وە (باسی) زەکەریا (بکە) کاتێک ھاواری لە پەروەردگاری کرد ئەی پەروەردگارم بە تەنھا مەمھێڵەرەوە (منداڵێکم پێ ببەخشە) وە تۆ چاکترین میراتگرانی');
INSERT INTO "bamoki" VALUES(21,90,'21:90','ئێمەش دوعای ئەومان وەڵام دایەوە و یەحیامان پێ بەخشی و خیزانەکەشیمان بۆ چاک کردەوە (لە نەزۆکی) بێگومان ئەوانەی (باسکران لە پێغەمبەران) پێش بڕکێیان دەکرد لە (ئەنجامدانی) ھەموو خێر و چاکەیەکدا و ھاواریان لە ئێمە دەکرد بە ئومێدی (بەھەشت) ولە ترسی (دۆزەخ) وە ئەوان ملکەچ بوون بۆ ئێمە (ودەترسن لێمان)');
INSERT INTO "bamoki" VALUES(21,91,'21:91','وە(باسی) ئەو ئافرەتە (مەریەمی کچی عیمران بکە) کەداوێنی خۆی پاراست ئەمجا فوومان پێداکرد (بەھۆی جوبرەیلەوە) لەو ڕۆحەی کەھی خۆمان بوو وە ئەو (مەریەم) و کوڕەکەیمان کرد بە موعجیزەیەک بۆ جیھانیان');
INSERT INTO "bamoki" VALUES(21,92,'21:92','بەڕاستی ئا ئەمەیە ئاینی ئێوە کەیەک ئاینە (ئاینی ھەموو پێغەمبەرانە) ومنیش پەروەردگارتانم کەواتە تەنھا من بپەرستن');
INSERT INTO "bamoki" VALUES(21,93,'21:93','بەڵام خەڵکی جیاوازیان کەوتە نێوان و سەرپێچی پێغەمبەرەکانیان کرد، زۆربەیان بوون بەچەند کۆمەڵ گرۆھێکەوە (ھەستان دەستیانکرد بە پەرستنی بت وھەواو ئارزووی خۆیان) (بەڵام) ھەموویان تەنھا بۆ لای ئێمە دەگەڕێنەوە');
INSERT INTO "bamoki" VALUES(21,94,'21:94','ئەمجا ھەر کەس (ھەرچەند) لەکردەوە چاکەکان بکات (بەو مەرجەی)باوەڕداریش بێت ئەوە (پاداشتی) کۆششەکەی وون نابێت و بەفیڕۆ ناڕوات بەڕاستی ئێمە بۆ (کردەوەکانی) ئەو نووسەرین');
INSERT INTO "bamoki" VALUES(21,95,'21:95','وە حەرامە و قەدەغەیە و ناشێت بۆ خەڵکی ئەو گوند وشارانەی کە کاولمان کردن و لەناومان بردن کەجارێکی تر بگەڕێنەوە (بۆ دونیا)');
INSERT INTO "bamoki" VALUES(21,96,'21:96','(ژیان درێژەی دەبێت) تا ئەو کاتەی (بەربەستەکەی) یەئجوج و مەئجوج دەکرێتەوە ئەوانیش لەھەموو بەرزاییەکەوە بەپەلە (وبەخێرایی) دێنە خوارەوە');
INSERT INTO "bamoki" VALUES(21,97,'21:97','وەنزیک بۆتەوە بەڵێنی ڕاست (ھاتنی قیامەت) جا لەناکاودا ئەبڵەق و زەق دەبێت چاوی ئەوانەی بێ بڕوا بوون (دەڵێن) ھاوارو مەرگ بۆ ئێمە بێگومان ئێمە لەدونیادا لەبێ ئاگاییەکی (تەواو)دا بووین لە ئەمە، نەخێر بەڵکو ئێمە ستەمکار بووین');
INSERT INTO "bamoki" VALUES(21,98,'21:98','بەڕاستی ئێوە وئەوانەش دەتانپەرستن جگە لەخوا (دەبنە) سووتەمەنی (وھەڵگیرسێنەری) دۆزەخ، ئێوە (و ئەوان) دەچنە ناویەوە');
INSERT INTO "bamoki" VALUES(21,99,'21:99','ئەگەر ئا ئەوانە (بتەکان) خوا بوونایە نەدەچوونە ناو ئەو (ئاگری دۆزەخە)وە وە ھەموو (بت و بتپەرستان) بەھەمیشەیی تێیدا دەمێننەوە');
INSERT INTO "bamoki" VALUES(21,100,'21:100','بۆ ئەو (دۆزەخیانە) ھەیە لەدۆزەخدا ھەناسەی سارد ھەڵکێشان وەئەوان لەوێدا (لەدۆزەخدا) ھیچ نابیستن');
INSERT INTO "bamoki" VALUES(21,101,'21:101','بێگومان ئەوانەی لەلایەن ئێمەوە پێشتر بڕیار دراوە بۆیان کە بەختەوەربن (وچاکە بھێنرێتە ڕێیان) ئەوانە لەدۆزەخ دوور دەخرێنەوە');
INSERT INTO "bamoki" VALUES(21,102,'21:102','(ئەوان) نابیستن دەنگی (بڵێسەو قرچەی) ئاگری دۆزەخ وەئەوان لەناو ئەو (نازو نیعمەتەی) دڵیان حەزی لێیەتی بەھەمیشەیی وبێ بڕانەوە دەمێننەوە');
INSERT INTO "bamoki" VALUES(21,103,'21:103','غەمباریان ناکات ترسە ھەرە گەورەکە (لەقیامەتدا) و فریشتەکانیش پێشوازیان لێ دەکەن (لەبەر دەرگاکانی بەھەشتدا) (وپێیان دەڵێن) ئەمە ئەو ڕۆژیە کە بەڵێنتان پێ درابوو');
INSERT INTO "bamoki" VALUES(21,104,'21:104','(بیھێنەوە بیریان) ئەو ڕۆژەی کەئاسمانەکان دەپێچینەوە وەک پێچانەوە (لولکردنی) تۆماری کتێب وپەرتوکێک وەک یەکەمجار چۆن دروستمان کرد (بەڕوت و قووتی) (پاش مردنیش) ئەو (دروستکردنە) دووبارە دەکەینەوە ئەوە بەڵێنێکە (جێبەجێ کردنی) لەسەر ئێمەیە بێگومان ئێمە (ئەو بەڵێنە) بەدی دەھێنین');
INSERT INTO "bamoki" VALUES(21,105,'21:105','سوێند بەخوا بێگومان ئێمە نووسیوومانە لەزەبووردا لەدوای نووسینی لە (لوح المحفوظ) دا بەڕاستی سەر زەوی (بەھەشت) دەبنە خاوەنی وبەمیراتی دەیبەن بەندە چاک و پاکەکانم');
INSERT INTO "bamoki" VALUES(21,106,'21:106','بێگومان لەمەدا (لەوەی لەم سورەتەدا باسکرا ) بەسە کەپەند وئامۆژگاری بێت بۆ گەلی خوا پەرستان');
INSERT INTO "bamoki" VALUES(21,107,'21:107','وە (ئەی موحەممەد صلى الله عليه وسلم) تۆمان تەنھا بۆ ئەوە ناردووە کەڕەحمەت بیت بۆ جیھانیان');
INSERT INTO "bamoki" VALUES(21,108,'21:108','بڵێ بێگومان تەنھا شتێک کەنیگا کراوە بۆ من (ئەمەیە) کە بەڕاستی پەرستراوی ئێوە خوای تاک و تەنھایە، دەی ئایا ئێوە ملکەچ دەبن؟');
INSERT INTO "bamoki" VALUES(21,109,'21:109','جا ئەگەر ڕوویان وەرگێڕا ئەوە بڵێ ھەمووتانم ئاگادار کرد بە یەکسانی و ناشزانم نزیکە یادوورە (ھاتنی) ئەو ھەڕەشەی لێتان دەکرێت');
INSERT INTO "bamoki" VALUES(21,110,'21:110','بەڕاستی خوا دەزانێت بە ووتە و قسەی ئاشکرا وە دەشزانێت بەو قسەی دەیشارنەوە');
INSERT INTO "bamoki" VALUES(21,111,'21:111','وە من نازانم (مۆڵەت دانتان) لەوانەیە تاقیکردنەوە بێت بۆ ئێوە (بیەوێت) تاماوەیەک (وازتان لێ بھێنێت) کەڕابوێرن');
INSERT INTO "bamoki" VALUES(21,112,'21:112','(پێغەمبەر صلى الله عليه وسلم) فەرمووی ئەی پەروەردگارم بەدادگەرانە بڕیاربدە (لە نێوان من و ئەم بێ باوەڕانەدا) وە پەروەردگاری ئێمە میھربانی یارمەتی لێ داواکراوە لە بەرانبەر (ئەو قسە نابە جێیانەی) ئێوە دەیڵێن و ھەڵیدەبەستن');
INSERT INTO "bamoki" VALUES(22,1,'22:1','ئەی خەڵکینە بترسن لە (سزای) پەروەردگارتان چونکە بەڕاستی بوومەلەرزەی قیامەت ھەستان شتێکی زۆر گەورەیە، کارەساتێکی سامناکە');
INSERT INTO "bamoki" VALUES(22,2,'22:2','لەو ڕۆژەدا ئێوە ئەو (بوومەلەرزە) دەبینن بێ ئاگا دەبێت (لەترسی ئەو ڕۆژە)ھەموو ئافرەتێکی شیردەر (کاتێک مەمکی لەدەمی منداڵەکەیدایە) لە ئەو (منداڵە)ی شیری پێداوە وەفڕێی دەدات و لە باری دەچێت ھەموو دووگیانێک (مێینەیەکی سکپڕ) سکەکەی (کۆرپەکەی) خەڵکی دەبینیت (وەک) سەرخۆشن بەڵام سەرخۆشیش نین بەڵکو سزای سەخت و بەتینی خوا (وای لێ کردون)');
INSERT INTO "bamoki" VALUES(22,3,'22:3','لەمرۆڤەکاندا کەسی وا ھەیە (وەک نەضری کوڕی حارث) موجادەلە دەکات دەربارەی خوا (و سیفەتەکانی) بەبێ ھیچ زانیاری و زانستێک وە شوێنی ھەموو شەیتانێکی سەرکەش دەکەوێت');
INSERT INTO "bamoki" VALUES(22,4,'22:4','لەسەری نوسراوە (بڕیار دراوە) کە بێگومان ھەرکەسێک خۆشی بوێت (وشوێنی بکەوێت) ئەوە بەڕاستی گومڕای دەکات وە ڕێنمونی دەکاتبۆ سزای ئاگری ھەڵگیرساوی دۆزەخ');
INSERT INTO "bamoki" VALUES(22,5,'22:5','ئەی خەڵکینە ئەگەر ئێوە لەگوماندان دەربارەی زیندووبوونەوە ئەوسا (بزانن) بەڕاستی ئێمە ئێوەمان دروستکردووە لە خاک (خۆڵ) پاشان (نەوەکان) لە نوتفەیەک (دروست دەبن) دواتر (دەبێتە) پارچە خوێنێکی مەیو دوای ئەوە (دەبێتە) پارچە گوشتێک شکڵ و شێوە کێشراو و شکڵ و شێوە نەکێشراو بۆ ئەوەی ڕوونی بکەینەوە بۆ ئێوە وە لەمناڵداندا دەیھێڵینەوە (ڕایدەگرین) ئەو (بێچوەی) بمانەوێت (لەبار نەچێت) تاکاتێکی دیاریکراو پاشان (لەسکی دایکتان) دەرتان دەھێنین بەساوایی پاشان گەورە دەبن تادەگەنە تەواوی ھێزو تواناتان وە (لەم تەمەنەدا) ھەندێکتان دەمرێنرێت وە ھەندێکتان دەگەیەنرێنە ئەو پەڕی پیری، بێ کەڵکترینی تەمەن بۆ ئەوەی نەزانێت و لەبیری نەمێنێت ھەرچی کە دەیزانی وەزەوی دەبینی (لەزستاندا) خامۆشە ووشک و بێ گیایە جا کاتێک بارانمان بەسەردا باراند زەوی (ڕووەکی لێ دەڕوێت و) دەجوڵێت زەوی ھەڵ دەدات (بەرز دەبێت) و(زەوی) دەڕوێنێ لەھەموو جۆرە (گیا و گوڵێکی) جوان');
INSERT INTO "bamoki" VALUES(22,6,'22:6','ئەوەی (باس کرا) لەبەر ئەوەیە دڵنیابن کەخوا ھەق و ڕاستە وە بێگومان مردووەکان زیندوو دەکاتەوە و بێگومان ئەو(خوایە) توانای بەسەر ھەموو شتێکدا ھەیە');
INSERT INTO "bamoki" VALUES(22,7,'22:7','وەبەڕاستی قیامەت دێت (وبەرپا دەبێت) گومانی تێدا نیە ودڵنیاش بن خوا زیندووی دەکاتەوە ھەموو ئەوکەسانەی لەناو گۆڕەکاندا (مردوون)');
INSERT INTO "bamoki" VALUES(22,8,'22:8','وە لەناو خەڵکدا کەسی واھەیە موجادەلە دەکات دەربارەی خوا بەبێ ھیچ زانیاری و شارەزاییەک (بەڵگەیەک) وە بەبێ کتێبێکی ڕوونکەرەوە (لەلایەن خواوە)');
INSERT INTO "bamoki" VALUES(22,9,'22:9','(کەسێکە) ڕوو وەردەگێڕێت (وھەق وەرناگرێت) بۆ ئەوەی خەڵک لەڕێگەی خوا لادات ئەو کەسە لەدونیادا سەرشۆڕ دەبێت وە لە قیامەتیشدا پێی دەچێژین سزای سوتێنەر');
INSERT INTO "bamoki" VALUES(22,10,'22:10','(پێی دەووترێت) ئەو سزایە (کە باس کرا) بەھۆی ئەو (گوناھانەوەیە) کەبەدەستی خۆت پێشت خستووە وەبێگومان خوا ستەم کار نیە بەبەندەکانی');
INSERT INTO "bamoki" VALUES(22,11,'22:11','وە لەناو خەڵکدا کەسی واھەیە خوا دەپەرستێت لە قەراغدا، (لەنێوانێکدا) جا ئەگەر تووشی خێر و خۆشی بوو دڵخۆش دەبێت پێی (بەردەوام دەبێ لەخواپەرستیدا) وەئەگەر تووشی تاقیکردنەوەو بەڵا بوو بەرەو دوا ھەڵدەگەڕێتەوە پاش گەز دەبێتەوە لەئاین زەرەر مەندی دونیا و قیامەت بووە تەنھا ئەوەیە زیانباری و خەسارەت مەندی ڕوون و ئاشکرا');
INSERT INTO "bamoki" VALUES(22,12,'22:12','ئەو دەپاڕێتەوە (ودەپەرستێ) جگە لە خوا شتێک نەزیانی پێدەگەیەنێت وە نە سوود و قازانجیشی پێدەگەیەنێت، تەنھا ئەوەیە لە ڕێلادانی (گومڕایی) زۆر دوور لەڕاستی');
INSERT INTO "bamoki" VALUES(22,13,'22:13','ئەو ھاواردەکات، ودەپەرستێت (لە) کەسێک کەزیانی لە سوودی نزیکتر، ئای چ پشتیوانێکی خراپە وە چ ھاوڕێ و ھاودەمێکی خراپە');
INSERT INTO "bamoki" VALUES(22,14,'22:14','بەڕاستی خوا ئەوانەی کەباوەڕیان ھێناوە و کردەوە چاکەکانیان کردووە دەیانخاتە باخەکانی بەھەشتەوە ک ڕووبارەکان دەڕۆن بەژێریاندا بێگومان خوا ئەوەی بیەوێت دەیکات');
INSERT INTO "bamoki" VALUES(22,15,'22:15','ھەرکەس واگومان دەبات (لە بێ بڕوایان) کە ھەرگیز خوا ئەو (پێغەمبەرە صلى الله عليه وسلم) یارمەتی نادات و سەری ناخات لە دونیا و دواڕۆژدا، دەبا پەتێک بەسەرەوە (بە بەرزییەوە) ھەڵواسێت ( و ملی پێدا بکات) پاشان ھەناسەی ببڕێت (تا دەخنکێت) ئەوسا ببینێت (سەرنج بدا و بیرکاتەوە) ئایا بەم کارە ئەو ڕق وتوڕەییەی (لە دڵیدایە) لایدەبات (ونایھێڵێت)');
INSERT INTO "bamoki" VALUES(22,16,'22:16','ئابەم شێوە ئەو(قورئانە)مان ناردۆتە خوارەوە کە ئایەتەکانی ڕون وئاشکران وە بێگومان خوا ھەرکەسێک خۆی بیەوێت ھیدایەتی دەدات');
INSERT INTO "bamoki" VALUES(22,17,'22:17','بەڕاستی کەسانێک کەبڕوایان ھێناوە و کەسانێک کە بوون بەجولەکە و ئەستێرە (یا فریشتە) پەرستەکان و گاورەکان (نەسرانیەکان) و ئاگر پەرستەکان (زەردەشتی یەکان) و ئەوانەی ھاوبەشیان بۆ خوا دانا بێگومان خوا بڕیار دەدا لەنێوانیاندا (جیایان دەکاتەوە لەیەکتر کۆمەڵێک بۆ بەھەشت وکۆمەڵێکی تریان بۆ دۆزەخ) لەڕۆژی دواییدا بەڕاستی خوا بەسەر ھەموو شتێکدا ئاگادارە');
INSERT INTO "bamoki" VALUES(22,18,'22:18','ئایا نەتبینی کە بەڕاستی سوژدە بۆ خوا دەبات ئەوەی لە ئاسمانەکان و ئەوەی لە زەویدایە ھەروەھا ڕۆژ و مانگ و ئەستێرەکان و چیاکان و درەخت و گیان لەبەران زۆرێکیش لە خەڵکی (ھەموو ئەمانەی باسکران بۆ خوا سوژدە دەبەن) وە زۆرێکیش سزای بۆ بڕیاردرا (لە سەری چەسپا) وە ھەر کەسێک خوا بێ ڕێز و ڕیسوای بکات ئەوە کەس ڕێزی لێ ناگرێت بەڕاستی خوا ھەر شتێکی بوێت دەیکات');
INSERT INTO "bamoki" VALUES(22,19,'22:19','ئەمانە دوو کۆمەڵی دژ بە یەکن دەمەقاڵێ و کێشەیانە دەربارەی پەروەردگاریان جا ئەوانەی کە بێ باوەڕ بوون لە (ڕۆژی دواییدا) پۆشاکیان بۆ دەبڕدرێت (پڕ بە بەریان) لە ئاگر بەسەریاندا دەڕژێنرێت ئاوی زۆر گەرم');
INSERT INTO "bamoki" VALUES(22,20,'22:20','دەتوێنرێتەوە بەو (ئاوە گەرمە) ئەوەی لە ناو سک و پێستەکاندایە');
INSERT INTO "bamoki" VALUES(22,21,'22:21','بۆیان ئامادەیە گورزی ئاسنی');
INSERT INTO "bamoki" VALUES(22,22,'22:22','ھەرکاتێک بیانەوێت کە بێنە دەرەوە لەو(دۆزەخ) ە لەبەر زۆر ناڕەحەتی دەگێڕێنرێنەوە بۆ ناوی وە (پێیان دەووترێت) بچێژن سزای سوتێنەر');
INSERT INTO "bamoki" VALUES(22,23,'22:23','بەڕاستی خوا د ئەوانەی کە باوەڕیان ھێناوە و کردەوە چاکەکانیان کردووە دەیانخاتە بەھەشتانێک ڕووبارەکان بەژێریاندا دەڕۆن (ئەوان) دەڕازێنرێنەوە لەو (بەھەشتانە) دا بە بازنی ئاڵتون و مرواری وە پۆشاکیان لە بەھەشتدا ئاوریشمە');
INSERT INTO "bamoki" VALUES(22,24,'22:24','ئەو (بەھەشتیانە) ڕێنمونی کراون بۆ ئەو ووتەی کەڕاست وجوانە (ووتەی لا الە إلا اللە) ھەروەھا ڕێنمونی کراون بۆ ڕێگای (خوای) سوپاسکراو');
INSERT INTO "bamoki" VALUES(22,25,'22:25','بێگومان ئەوانەی بێ باوەڕ بوون وە ڕێگری (موسڵمانان) دەکەن لە رێگای خوا وە لە (ڕۆشتن بۆ) مزگەوتی حەرام (بۆ کەعبە) کە ئەوێمان داناوە (بۆ خوا پەرستی) بۆ ھەموو خەڵکی یەکسانە چ ئەوانەی لەوێدان یان لە دورەوە بۆی دێن وە ھەر کەس بیەوێت لەوێ (لە مزگەوتی حەرام) مەیلی لە ھەق لادان زوڵم وستەم بکات پێی دەچێژین سزای ئازاردەر');
INSERT INTO "bamoki" VALUES(22,26,'22:26','(بیریان بخەرەوە) کاتێک دیاریمان کرد بۆ ئیبراھیم (علیە السلام) جێگای کەعبە (پێمانووت) ھیچ شتێک مەکەرە ھاوبەشم و ماڵەکەم (مزگەوتی حەرام) پاک بکەرەوە بۆ تەواف کاران و بۆ نوێژگەران (بەپێوە) وکوڕنوش و سوژدە بەران');
INSERT INTO "bamoki" VALUES(22,27,'22:27','وە جاڕبدە بەناو خەڵکیدا بۆ حەج کردن دێن بۆ لات (بۆ حەج) بە پیادە و بەسوار ھەموو ووشترێکی لاواز (لەبەر ئەوەی) کە لە ھەموو رێگایەکی دوورەوە دێن');
INSERT INTO "bamoki" VALUES(22,28,'22:28','تا ئامادەی جۆرەھا سوود و قازانجی خۆیان ببن وە ناوی خوا ببەن (کاتێک قوربانی سەر دەبڕن) لە ڕۆژانێکی دیاریکراودا لەسەر ئەو ئاژەڵانەی خوا پێی داوون جا لەو (گۆشتی قوربانیانە) بخۆن وە بیدەن بە نەداران و ھەژاران');
INSERT INTO "bamoki" VALUES(22,29,'22:29','دوای (سەر بڕینی قوربانی) چڵک و پیسی (موو ونینۆک) لابەن بە خۆیانەوەوە نەزرەکانیان جێبەجێ بکەن و تەواف (ی ئیفاضە) بکەن بە دەوری ماڵی دێرین و بەڕێزدا (بەدەوری کەعبەدا)');
INSERT INTO "bamoki" VALUES(22,30,'22:30','ئەمەی (پێشوو باسی حەج بوو) جا ھەر کەس بپارێزێت و بە گەورەی بگرێت سنوورەکانی خوا ئەوە بۆ خۆی چاکترە لای پەروەردگاری وە گۆشتی ئاژەڵ (مەڕ، وبزن، ومانگا، وشتر) بۆ ئێوە حەڵاڵ کراوە جگە لەوانەی بۆتان باس دەکرێت (وەک مردارەوە بوو، یان بۆغەیری خودا سەربڕراو) کەواتە خۆتان دوور بخەنەوە لەو پیسیە کە بتەکانە ھەروەھا خۆتان بپارێزن لەقسەی درۆو ناڕەوا');
INSERT INTO "bamoki" VALUES(22,31,'22:31','گەردن کەچ بن بۆ خوا (خۆتان لە ھەموو بیرو باوەڕێکی لار دووربگرن) ھاوبەش بڕیاردەر مەبن بۆ خوا جا ھەرکەس ھاوبەش بۆ خوا دابنێت ئەوە وەک ئەوە وایە لە ئاسمان کەوتبێتە خوارەوە ئەمجا باڵندە (لە ھەوادا) بە پەلە بیفڕێنێت (وپارچە پارچەی بکات) یا بایەکی توند بیبات بۆ جێگایەکی زۆر دوور (چۆڵەوانی)');
INSERT INTO "bamoki" VALUES(22,32,'22:32','ئەمە (کە باسکرا نموونەی ھاوبەش دانان بوو بۆ خوا) وە ھەر کەس دروشمەکانی (ئاینی) خوا بەگەورە ڕاگرێت جا بەڕاستی ئەو کارە لە خوا ترسی دڵەکانیانەوەیە (وتەقواکارن)');
INSERT INTO "bamoki" VALUES(22,33,'22:33','بۆ ئێوە لەو (ئاژەڵانە)دا چەند سوود وەرگرتنێک ھەیە (وەک سوار بوون،وخواردنی شیریان) (جگە لەمانەیش دەتوانن سوودیان لێ ببینن بەشێوەیەک زیانیان پێ نەگات) تاکاتی دیاریکراو (بۆ سەربڕینیان) کە شوێنی (سەربڕینی) ئەوانە کاتێکە دەگەنە ماڵی دێرینی (کەعبە)');
INSERT INTO "bamoki" VALUES(22,34,'22:34','وە بۆ ھەموو ئوممەتێک قوربانی کردنمان داناوە بۆ ئەوەی ناوی خوا ببەن (کاتی سەربڕین) لەسەر ئەو ئاژەڵانەی خوا پێی داون جـا خوای ئێوە پەرستراوێکی تەنھایە کەواتە تەنھا ملکەچی ئەو بن وە مژدە بدە بەملکەچان و خۆ بەکەم زانان');
INSERT INTO "bamoki" VALUES(22,35,'22:35','ئەوانەی ھەرکاتێک ناوی خوا برا دڵەکانیان دەترسێت (دەلەرزێت) وە ئارامگرن لەسەر ئەوەی تووشیان دەبێت (لەناخۆشی) وە بەجێ ھێنەرانی نوێژەکانیانن وە لەو ڕزق رۆزیەی پێمان داون دەبەخشن');
INSERT INTO "bamoki" VALUES(22,36,'22:36','ئەو ووشترە گەورە و قەڵەوانە (دەبرێن لەبیت الحرام سەردەبڕێن) ئێمە کردومانەتە دروشمی خوا بۆ ئێوە بۆ ئێوە لەواندا خێر وچاکە ھەیە کەواتە (کاتی کردنیان بەقوربانی) ناوی خوایان لەسەر بھێنن لە کاتێکدا لەسەر سێ پەل وەستاون و دەستی چەپیان بەسراوەتەوە (لەبەر ئەوەی وشتر بە پێوە سەر دەبڕدرێت) جا کاتێک کەوتنە سەر زەوی بە تەنیشتدا (کاتێک گیانیان دەرچوو) ئەوە لەگۆشتەکەیان بخۆن وە بیدەن بە ھەژاران و سواڵکەران ئا بەو جۆرە ئەو ماڵاتانەمان بۆ ئێوە ڕامھێناوە تا سوپاسگوزار بن بۆ خوا');
INSERT INTO "bamoki" VALUES(22,37,'22:37','ھەگیز ناگات بە خوا نەگۆشتی ئەو (قوربانیانە) نەخوێنیان بەڵام ئەوەی بە خوا دەگات تەقوا و پارێزکاری ئێوەیە ئا بەو جۆرە ئەو ماڵاتانەی بۆ ئێوە ڕامھێناوە بۆ ئەوەی خوا بە گەورە بگرن (یادی بکەن) لەسەر ئەوەی ڕێنمونی کردوون (بۆ سەر ئاینی پیرۆزی ئیسلام) وەمژدە بدە بەچاکە کاران');
INSERT INTO "bamoki" VALUES(22,38,'22:38','بەڕاستی خوا بەرگری وپشتیوانی دەکات لەوانەی کە بڕوایان ھێناوە بێگومان خوا خۆشی ناوێت ھەموو خیانەتکارێکی بێ باوەڕ');
INSERT INTO "bamoki" VALUES(22,39,'22:39','رێگەدرا (بەجەنگ و جیھاد کردن) بە ئەو (موسڵمانانەی) شەڕیان لەگەڵدا دەکرێت لەبەر ئەوەی کە بەڕاستی ستەمیان لێکراوە وە بێگومان خوا بۆ سەرخستنیان بە توانایە');
INSERT INTO "bamoki" VALUES(22,40,'22:40','ئەوانەی کە دەرکران لە ماڵ و وڵاتیان بە ناھەق تەنھا (گوناھیان ئەوە بوو) کە دەیانووت پەروەردگارمان (اللە)یە وە ئەگەر خوا خەڵکی لە ناو نەبردایە ھەندێکیان بە ھەندێکی تریان بێگومان دەڕوخێنرا (وێران دەکرا) دێری جولەکەکان و کلیسا و کەنیسە مزگەوتەکان کە یادی خوا لەو (پەرستگایانە)دا زۆر دەکرا وە بێگومان خوا یارمەتی کەسانێک ئەدات (وسەریان دەخات) کە یارمەتی (دینی) خوا دەدەن بە ڕاستی خوا بەھێز وباڵادەست و بەتوانایە');
INSERT INTO "bamoki" VALUES(22,41,'22:41','ئەوانەی ئەگەر دەسەڵاتمان پێدان لە زەویدا بە چاکی نوێژ بەجێ دەھێنن وە زەکات دەدەن و فەرمان دەدەن بە چاکە وڕێگری دەکەن لە خراپە و سەرئەنجامی ھەموو کارەکان تەنھا بەدەست خوایە');
INSERT INTO "bamoki" VALUES(22,42,'22:42','وە ئەگەر (بێ باوەڕان) تۆ بە درۆزن دەزانن وباوەڕت پێ ناکەن ئەوە بێگومان پێش ئەوانیش (گەلانی تر) باوەڕیان نەکرد (بە پێغەمبەرەکانیان) (وەک) گەلی نوح و عاد و سەمود');
INSERT INTO "bamoki" VALUES(22,43,'22:43','وە گەلی ئیبراھیم و گەلی لوط');
INSERT INTO "bamoki" VALUES(22,44,'22:44','ھەروەھا یارانی مەدیەن (ھۆزی شوعەیب) وە موساش بە درۆزن دانرا ئەمجا (بۆ ماوەیەک) مۆڵەتی بێ باوەڕانمدا، دواتر تۆڵەم لێ سەندن وگرتمن جا چۆن بوو بەگژا چوونی ئێمە (لەوان)');
INSERT INTO "bamoki" VALUES(22,45,'22:45','ئەمجا چەندەھا شار و دێھاتمان وێرانکرد کە (خەڵکەکەی) ستەمکار بوون ئەو (شارانە دیواری خانوەکانی) کەوتبوون بەسەر سەربانەکانیاندا وە چەندەھا بیر بێ خاوەن بوون (لە کار کەوتبوون) وە چەندەھا کۆشکی بەرز و بڵند (بێ خاوەن بوون)');
INSERT INTO "bamoki" VALUES(22,46,'22:46','دەی ئایا ئەوانە بە زەویدا نەگەڕاون تا دڵیان ھەبێت بیری پێ بکەنەوە (لەو وێران بوونە) یان گوێیان ھەبێت گوێی پێ بگرن (بۆ ڕاستی) لەڕاستیدا چاوەکان کوێر نابن بەڵکو ئەو دڵانە کوێر دەبن کەلە سینەکاندان');
INSERT INTO "bamoki" VALUES(22,47,'22:47','وە ئەوان (قورەیش بە گاڵتەوە) بەپەلە داوای سزات لێ دەکەن لەکاتێکدا ھەرگیز خوا پێچەوانەی بەڵێنی خۆی ناکات و بێگومان ڕۆژێک لای پەروەردگارت وەک ھەزار ساڵ وایە لەو ساڵانەی ئێوە دەیژمێرن');
INSERT INTO "bamoki" VALUES(22,48,'22:48','وە (خەڵکی) چەندەھا شار و گوند (ھەبوون) مۆڵەتم دان کە ستەمکار بوون لەپاشان تۆڵەم لێ سەندن (ولەناوم بردن) وە ھەر بۆلای من دەگەڕێنەوە');
INSERT INTO "bamoki" VALUES(22,49,'22:49','بڵێ ئەی خەڵکینە بێگومان من بۆ ئێوە تەنھا ترسێنەرێکی ئاشکرام');
INSERT INTO "bamoki" VALUES(22,50,'22:50','ئەمجا ئەو کەسانەی بڕوایان ھێناو وە کردەوە چاکەکانیان کرد لێخۆشبوون و ڕۆزیەکی بەنرخ وفراوانیان بۆ ئامادەیە');
INSERT INTO "bamoki" VALUES(22,51,'22:51','وە ئەوانەی ھەوڵیان داوە بۆ (دژایەتی) ئایەتەکانی ئێمە ناتوانا و دەستەپاچەن ئەوانە ھاوەڵی دۆزەخن');
INSERT INTO "bamoki" VALUES(22,52,'22:52','وە ئێمە لەپێش تۆ نەمان ناردووە ھیچ ڕەوانەکراو یان پێغەمبەرێک مەگەر ھەر کاتێک (پەڕاوی خودای) خوێندبێتەوە بۆ گەلەکەی شەیتان گومان و وەسوەسەی دەخستە ناو خوێنراوەکەیەوە (تا خەڵک باوەڕی پێ نەھێنن) ئەمجا خوا لایدەبات (پوچەڵی دەکاتەوە) ئەوەی شەیتان داوێتی بە گوێی خەڵکدا (لە وەسوەسە ودڵەڕاوکێ و دوودڵی) پاشان خوا ئایەتەکانی خۆی دەچەسپێنێت وە خوا زانا و کاردروستە');
INSERT INTO "bamoki" VALUES(22,53,'22:53','لەبەر ئەوەی ئەو گومانانەی شەیتان دەیخاتە (دڵەکان خوا) دەیکاتە (ھۆی) تاقیکردنەوە بۆ ئەوانەی نەخۆشی لە دڵیاندایە و (ئەوانەی) دڵیان ڕەق وە بەڕاستی ستەمکاران لە دژایەتی یەکی زۆر دوور (لە ڕاستی)دان');
INSERT INTO "bamoki" VALUES(22,54,'22:54','وە بۆ ئەوە بزانن ئەو کەسانەی کە زانیاریان پێـدراوە بێگومان ئەم (قورئانه) ڕاستیەکە لە لایەن پەروەردگارتەوە تا باوەڕی پێ بھێنن (وبەردەوام بن لەسەری) وە دڵەکانیان ملکەچ و بەئارام بێت بۆ ئەو (خوا) وە بێگومان خوا ھیدایەتدەری ئەو کەسانەیە کە باوەڕیان ھێناوە بۆ سەر ڕێگای ڕاست');
INSERT INTO "bamoki" VALUES(22,55,'22:55','بەردەوام ئەو کەسانەی بێ باوەڕن لە گوماندان دەربارەی قورئان ھەتا لەناکاودا ڕۆژی دوایی یەخەیان دەگرێت یان یەخەیان دەگرێت (ودێت بۆیان) سزای ڕۆژێکی نەزۆک و بێ خێر (کە ڕۆژی قیامەتە)');
INSERT INTO "bamoki" VALUES(22,56,'22:56','دەسەڵات و پاشایەتی لەو ڕۆژەدا تەنھا بۆ خوایە لەنێوانیاندا دادوەری دەکات ئەمجا ئەو کەسانەی باوەڕیان ھێناوەو کردەوە چاکەکانیان کردووە لە بەھەشتانی پڕ نازو نیعمەت دان');
INSERT INTO "bamoki" VALUES(22,57,'22:57','وە ئەو کەسانەی بێ باوەڕ بوون وە ئایەتەکانی ئێمەیان بەدرۆ دەزانیبۆ ئەوانە ھەیە سزای ڕیسواکەر');
INSERT INTO "bamoki" VALUES(22,58,'22:58','وە ئەو کەسانەی کۆچیان کردووە لەڕێگای خوادا پاشان کوژران (شەھید بوون) یان مردن بێگومان خوا ڕزق وڕۆزیەکی چاکیان دەداتێ وە بێگومان خوا چاکترینی ڕۆزیدەرانە');
INSERT INTO "bamoki" VALUES(22,59,'22:59','سوێند بەخوا، خوا ئەوانە دەخاتە جێگایەک کەپێی ڕازی دەبنوە بێگومان خوا زانا و لەسەر خۆیە');
INSERT INTO "bamoki" VALUES(22,60,'22:60','ئەمەی (باسمان کرد بۆت) وە ھەر کەسێک تۆڵەی خۆی بسێنێت (لەدەست درێژیکەر) بەوێنەی ئەو ستەمەی لێی کراوە لەدوای ئەوە جارێکی تر دەست درێژی بکرێتە سەر ئەوە بێگومان خوا یارمەتی دەدات (سەریدەخات) بەڕاستی خوا چاوپۆشیکار و لێبوردەیە');
INSERT INTO "bamoki" VALUES(22,61,'22:61','ئەم (سەرخستنەی خوا) لەبەر ئەوەیە بەڕاستی خوا (ئەوەندە بەدەسەڵاتە) شەو دەباتە ناو ڕۆژەوە (کاتێک شەو درێژ تر بێت لەڕۆژ) وەڕۆژ دەباتە ناو شەوەوە وە بێگومان خوا بیسەر و بینایە');
INSERT INTO "bamoki" VALUES(22,62,'22:62','ئەمە لەبەر ئەوەیە بەڕاستی ھەر خوا ھەق و ڕاستە وەبێگومان ئەوەی جگە لەخوا ھاواری لێ دەکەن (ودەیپەرستن) ھەر ئەو بەتاڵ و ناڕاستە وبەڕاستی خوا ھەر خۆی بەرزو بڵندو گەورەیە');
INSERT INTO "bamoki" VALUES(22,63,'22:63','ئایا نابینیت کەبەڕاستی خوا باراندویەتی لە ئاسمانەوە باران (بەھۆی ئەو بارانەوە) زەوی سەوز دەبێت (بەڕوەک) بێگومان خوا ووردبین وبە ئاگایە (بە ھەموو شتێک)');
INSERT INTO "bamoki" VALUES(22,64,'22:64','ھەر بۆ خوایە ئەوەی لە ئاسمانەکانە و ئەوەیشی لە زەویدایە وە بەڕاستی خوا ھەر خۆی بێ نیاز و دەوڵەمەندە وشایستەی سوپاسە');
INSERT INTO "bamoki" VALUES(22,65,'22:65','ئایا نابینیت (نازانیت) بەڕاستی خوا ڕامی ھێناوە بۆتان وخستویەتیە خزمەتتانەوە ھەموو ئەو شتانەی لەزەویدایە وەکەشتی (ڕام ھێناوە بۆتان) ودەڕوات بەناو دەریادا بەفەرمانی ئەو (بەفەرمانی خوا) وەئاسمانی ڕاگیر کردووە تا نەکەوێت بەسەر زەویدا مەگەر بەفەرمانی ئەو نەبێت بەڕاستی خوا دڵسۆز و میھرەبانە بەرانبەر بەخەڵکی');
INSERT INTO "bamoki" VALUES(22,66,'22:66','وە ئەو (خوا) زاتێکە کەژیانی پێ بەخشیوون (لەسەرەتای دروسکردنتانەوە) دواتر دەتانمرێنێت پاشان زیندووتان دەکاتەوە بێگومان خەڵکی زۆر ناسوپاس و سپڵەن');
INSERT INTO "bamoki" VALUES(22,67,'22:67','بۆ ھەموو ئوممەتێک شەریعەت و بەرنامەیەکمان داناوەئـەوان کـاریـان پێ کـردووە کـەواتـە نـابێت (خـاوەن ئاینەکانی تر) لەکاری ئایندا کێشە و ئاژاوەت لەگەڵدا دروست کەن وەتۆ خەڵک بانگ بکە بۆ لای پەروەردگارت بێگومان تۆ لەسەر ئاینێکی ڕاستی');
INSERT INTO "bamoki" VALUES(22,68,'22:68','جا ئەگەر دەمەقاڵێ وموجادەلەیان لەگەڵدا کردیت تۆش بڵێ خوا خۆی چاک دەزانێت بەو کردەوانەی کە ئێوە دەیکەن');
INSERT INTO "bamoki" VALUES(22,69,'22:69','خوا دادوەری دەکات لەنێوانتاندا لەڕۆژی دواییدا لەو شتانەی کەئێوە تێیدا جیاوازن');
INSERT INTO "bamoki" VALUES(22,70,'22:70','ئایا نازانی بەڕاستی خوا دەزانێت بەھەموو ئەو شتانەی لە ئاسمان و زەویدایە، بێگومان ئەوەی باسکرا لەکتێب لە (لوح المحفوظ)دایە بەڕاستی ئەم (زانیاریەی باسکرا) بەلای خواوە ئاسانە');
INSERT INTO "bamoki" VALUES(22,71,'22:71','ئەوان جگە لەخوا شتێک دەپەرستن، (خوا) ھیچ بەڵگەیەکی نەناردۆتە خوارەوە لەسەری وە بێگومان خۆشیان ھیچ زانیاریان دەربارەی ئەو (پەرستراوانە) نیە وەبۆ ستەمکاران نیە ھیچ یارمەتیدەرێک');
INSERT INTO "bamoki" VALUES(22,72,'22:72','وەکاتێک بخوێنرێتەوە بۆیان نیشانە ڕوونەکانی ئێمە لەدەم و چاوی ئەو کەسانەی بێ باوەڕن دەبینی ڕووگرژی و توڕەیی نزیکە پەلامار (وھێرش) بەرن بۆ ئەو کەسانەی کە ئایەتەکانی ئێمەیان بەسەردا دەخوێنێتەوە (ئەی موحەممەد صلى الله عليه وسلم) بڵێ ئایا ھەواڵتان بدەمێ بەخراپتر لەو (ڕوگرژی و توڕەییەی) ئێوە چیە ئاگری دۆزەخە کەخوای گەورە بەڵێنی داوە بەو کەسانەی کە بێ باوەڕن چ جێگە و ئاکامێکی خراپە (کە پێی دەگەن)');
INSERT INTO "bamoki" VALUES(22,73,'22:73','ئەی خەڵکینە نموونەیەک ھێنراوەتەوە جا گوێی بۆ بگرن بێگومان ئەوانەی ھاواریان لێ دەکەن لە جیاتی خوا ھەرگیز ناتوانن مێشێک دروست بکەن ھەرچەند ھەمووشیان بۆی کۆببنەوە وە ئەگەر مێشیش شتێکیان لێ بفڕێنێ ناتوانن لێی بسێننەوە داواکار و داوالێکراو لاواز و بێ دەسەڵاتن');
INSERT INTO "bamoki" VALUES(22,74,'22:74','ئەوانە بەو شێوەی کە پێویستە خوا بناسن، نەیان ناسیوە بێگومان خوا بە توانا و بەدەسەڵاتە');
INSERT INTO "bamoki" VALUES(22,75,'22:75','خوا لە فریشتەکاندا نێردراو ھەڵدەبژێرێت ھەروەھا لە خەڵکیشدا بەڕاستی خوا بیسەر و بینایە');
INSERT INTO "bamoki" VALUES(22,76,'22:76','خوا ئاگادارە بەوەی لە بەردەمیاندایە (روویداوە) و بەوەی لە دوایانەوەیە (دواتر ڕوودەدات) و ھەموو کارەکان ھەر بۆ لای خوا دەگەڕێتەوە');
INSERT INTO "bamoki" VALUES(22,77,'22:77','ئەی ئەوانەی کە باوەڕتان ھێناوە کوڕنوش و سوژدە بەرن و پەروەردگارتان بپەرستن وە کاری چاک بکەن بۆ ئەوەی ڕزگاربن');
INSERT INTO "bamoki" VALUES(22,78,'22:78','جیھاد و تێکۆشان بکەن لە پێناوی خوادا بەو شێوەیەی شایستە وشیاویەتی ئەو (خوا) ئێوەی ھەڵبژاردوە (بۆ ھەڵگرتنی ئەم ئاینە) وە لە ئاینـدا ھیچ کارێکی قورس و گرانی لەسەر دانەناون ئەمە ئاینی ئیبراھیمی باوە گەورەتانەئەو (خوا) پێشتر ئێوەی بە موسڵمان ناوناوە، وە لەم قورئانەشدا تا پێغەمبەر ببێت بە شاھید لەسەر ئێوە وە ئێوەش ببنە شاھیـد لەسـەر خـەڵـکی کەواتە بە چاکی نوێژەکانتان بکەن وزەکات بدەن وە تەنھا پشت بە خوا ببەستن چونکە ھەر ئەو پشتیوانتانە ئای چەند پشتیوان و یارمەتیدەرێکی چاکە');
INSERT INTO "bamoki" VALUES(23,1,'23:1','بەڕاستی باوەڕداران ڕزگار دەبن (سەر دەکەون)');
INSERT INTO "bamoki" VALUES(23,2,'23:2','ئەوکەسانەی کەلە نوێژەکانیاندا لەخواترسن (گەردن کەچن بۆخوا)');
INSERT INTO "bamoki" VALUES(23,3,'23:3','وە ئەوانەی لەشتی بێ ھوودە خۆیان دوور دەگرن');
INSERT INTO "bamoki" VALUES(23,4,'23:4','وە ئەوانەی کەزەکاتی (سامانیان) دەردەکەن');
INSERT INTO "bamoki" VALUES(23,5,'23:5','وەئەوانەی کەپارێزەری داوێنیان (لەشەرواڵ پیسی)');
INSERT INTO "bamoki" VALUES(23,6,'23:6','مەگەر بۆ خێزانی خۆیان یان بۆ کەنیزەکێک کەبووبن بەخاوەنیان ئەوە بەڕاستی ئەوانە سەرزەنشت ناکرێن');
INSERT INTO "bamoki" VALUES(23,7,'23:7','ئەمجا ھەرکەسێک نیازی جگە لەو (دووجۆرە ئافرەتە) بکات (و ئارەزووی خۆی تێربکات) ئەوسا ئەوانە دەست درێژیکارن');
INSERT INTO "bamoki" VALUES(23,8,'23:8','وە ئەوانەی کەپارێزەری سپاردەو پەیمانەکانیانن');
INSERT INTO "bamoki" VALUES(23,9,'23:9','وە ئەوانەی کە بەردەوامن لەسەر نوێژەکانیان');
INSERT INTO "bamoki" VALUES(23,10,'23:10','ھەر ئەوانەن میراتگران');
INSERT INTO "bamoki" VALUES(23,11,'23:11','ئەوانەی کەدەبنە خاوەنی فیردەوس ومیراتگری (بەھەشت) ئەوان لەبەھەشتدا دەمێننەوە بەھەمیشەیی');
INSERT INTO "bamoki" VALUES(23,12,'23:12','سوێند بەخوا بەڕاستی مرۆڤمان دروستکرد لەخۆلاسە و پاڵفتەیە لەقوڕ');
INSERT INTO "bamoki" VALUES(23,13,'23:13','لەپاشان ئەومان کرد بەنوتفەیەک لەئارامگایەکی قایم (مناڵدانی دایک)دا');
INSERT INTO "bamoki" VALUES(23,14,'23:14','دواتر نوتفەکەمان کرد بەخوێنی مەیو ئەمجا خوێنە مەیوەکەشمان کرد بەپارچە گۆشتێکی جووا و ئەمجا پارچە گۆشتەکەشمان کرد بەئێسقان ئەمجا بەگۆشت (پەیکەرە) ئێسکەکەمان داپۆشی پاشان ئەومان کرد بە دروستکراوێکی تر جا گەورە وپیرۆزە ئەو خوایەی کەچاکترینی بەدیھێـنەرانە');
INSERT INTO "bamoki" VALUES(23,15,'23:15','پاشان بەڕاستی ئێوە لەدوای ئەوە ھەمووتان دەمرن');
INSERT INTO "bamoki" VALUES(23,16,'23:16','لە پاشان بێگومان ئێوە لەڕۆژی دواییدا زیندوو دەکرێنەوە');
INSERT INTO "bamoki" VALUES(23,17,'23:17','سوێند بەخوا بەڕاستی لەسەری ئێوەوە دروستمان کردوە حەوت نھۆم ئاسمان وە ئێمە لە دروست کراوەکان بێ ئاگانین');
INSERT INTO "bamoki" VALUES(23,18,'23:18','وە لە ئاسمانەوە بە ئەندازە (ی پێویست) بارانمان باراند ئەمجا ئەو (ئاوە)مان لە زەوی دا ھێڵایەوە (ڕاگرت) وە بێگومان ئێمە بەسەر لەناوبردنی ئەو (ئاوە)دا بەتواناین');
INSERT INTO "bamoki" VALUES(23,19,'23:19','ئەمجا بە ھۆی ئەو (بارانە)وە بەدیمان ھێناوە (ڕواندومانە) بۆتان چەند باخی خورما و ترێ بۆ ئێوە ھەیە لەو باخانەدا میوەی زۆر وە لە ھەندێکیان دەخۆن (بەشێوەی بژێوی وەک مێوژ و خورما)');
INSERT INTO "bamoki" VALUES(23,20,'23:20','ھەروەھا (ڕواندومانە)جۆرە دارێک (زەیتون) کەلە طوری سینا دەڕوێتکە ڕۆن و پێخۆرێک دەگرێت بۆ ئەوانەی دەیخۆن');
INSERT INTO "bamoki" VALUES(23,21,'23:21','وە بەڕاستی بۆ ئێوە لە ماڵات و ئاژەڵدا پەند ھەیە پێتان دەنۆشین ئەو (شیرەی) لە ناو سکیاندایە وە بۆ ئێوە لەو (ئاژەڵانەدا) سوود و کەڵکێکی زۆر ھەیە و لێشیان دەخۆن');
INSERT INTO "bamoki" VALUES(23,22,'23:22','وە لەسەر (پشتی ھەندێ لە) ئەوان و لەسەر (پشتی) کەشتییەکانیش سوار دەکرێن');
INSERT INTO "bamoki" VALUES(23,23,'23:23','سوێند بەخوا بەڕاستی ئێمە نوحمان نارد بۆ لای ھۆزەکەی جا پێی ووتن ئەی ھۆزەکەم خوا بپەرستن، ئێوە جگە لەئەو پەرستراوێکی ترتان نیە، دەی ئایا خۆتان ناپارێزن');
INSERT INTO "bamoki" VALUES(23,24,'23:24','جا ووتیان ئەو دەسەڵاتدار و ناودارانەی کە بێ باوەڕبوون لە ھۆزەکەی (نوح)، ئەم (نوحە) تەنھا کەسێکە وەک ئێوە، دەیەوێت خۆی بکاتە گەورە بەسەرتانەوە، ئەگەر خوا بیویستایە (پێغەمبەرێک بنێرێت) فریشتەی دەناردە خوارەوە ئێمە شتی وامان نەبیستووە لە باوو باپیرە پێشووەکانمانەوە');
INSERT INTO "bamoki" VALUES(23,25,'23:25','ئەم (نوحە) ھەر پیاوێکە شێتی لەگەڵدایە جا تاماوەیەک چاوەڕوانی بکەن (بەڵکو چاک بێتەوە)');
INSERT INTO "bamoki" VALUES(23,26,'23:26','(نوح) ووتی ئەی پەروەرد گارم یارمەتیم بدە (سەرم بخە) لەبەر ئەوەی باوەڕم پێ ناھێنن');
INSERT INTO "bamoki" VALUES(23,27,'23:27','ئەمجا ئێمە نیگامان بۆ کرد کە کەشتیەکە دروست بکە بە پێش چاو و چاودێری وفەرمان و نیگای ئێمە (لەم ئایەتەدا باسی سیفەتی -چاو، عەین - ھاتووە بۆ خوای گەورە، بڕوامان پێیەتی بەبێ ھیچ لێچواندن ولێکدانەوەیەک، شێوەکەی نازانین وە تەنھا خوا خۆی دەزانێت) ئەمجا کاتێک فەرمانی ئێمە (بۆ لەناوبردنی ئەوان) ھات و ئاو لە تەنورەوە ھەڵقوڵا (نیشانەی دەسپێکردنی توفانەکەیە) تۆش بیخەرە ناو کەشتیەکەوە (سواری کە) لە ھەموو جۆرە (ئاژەڵێک)جوتێک (نێر ومێ یەک) ھەروەھا (سواری بکە ئەندامانی) خێزانەکەت جگە لەوانەیان کە بڕیاری لە ناو چوونیان دراوە (کە کوڕەکەی و ھاوسەرەکەی بوون) وقسەشم لەگەڵدا مەکە لەبارەی ئەوانەی کەستەمیان کردوە (و باوەڕیان نەھێنا) چونکە بێگومان ئەوانە دەخنکێنرێن');
INSERT INTO "bamoki" VALUES(23,28,'23:28','ئەمجا کاتێک تۆو ئەو کەسانەی لەگەڵتدان سواری کەشتی یەکە بوون(ئەی نوح) بڵێ سوپاس بۆ ئەو خوایەی کە ئێمەی ڕزگار کرد لە دەست گەلی زاڵمان');
INSERT INTO "bamoki" VALUES(23,29,'23:29','وە بڵێ ئەی پەروەردگارم دامبەزێنە (لە کەشتیەکە) لە جێگایەکی پیرۆز و بەفەڕدا (کە کێوی جوودی یە لە کوردستانی تورکیا) وە تۆ (ئەی خوایە) چاکترینی دابەزێنەرانی');
INSERT INTO "bamoki" VALUES(23,30,'23:30','بەڕاستی لەم (بەسەرھاتەی نوحدا ) پەند ونیشانەی زۆر ھەیە (بۆ کەسانی ژیر و ھۆشمەند) وە بەڕاستی ئێمە تاقیکەرەوەین (بۆ ھەموو خەڵک)');
INSERT INTO "bamoki" VALUES(23,31,'23:31','پاشان لەدوای ئەوان بەدیمان ھێنا گەل و نەوەیەکی تر');
INSERT INTO "bamoki" VALUES(23,32,'23:32','ئەمجا لە ناو ئەواندا پێغەمبەرێکمان لە خۆیان نارد (پێی ووتن) تەنھا خوا بپەرستن جگە لەو (خوایە) پەرستراوێکی ترتان نیە دەی ئایا خۆتان ناپارێزن (لە خوا ناترسن)');
INSERT INTO "bamoki" VALUES(23,33,'23:33','دەسەڵاتداران وناودارانی (ھود علیە السلام) ووتیان ئەوانەی کە بڕوایان نەھێنا و بێ باوەڕ بوون بە دیداری ڕۆژی دوایی وە لە ژیانی دونیادا ناز و نیعمەتمان پێ دابوون (ووتیان) ئەمە ھەر مرۆڤێکە وەک ئێوە لەوەی ئێوە دەیخۆن (ئەویش) دەخوات وە لەوەی ئێوە دەیخۆنەوە (ئەویش) دەخواتەوە');
INSERT INTO "bamoki" VALUES(23,34,'23:34','سوێند بەخوا ئەگەر ئێوە گوێڕایەڵی بەشەرێکی وەک خۆتان بکەن بێگومان ئەو کاتە ئێوە لە زیان باران دەبن');
INSERT INTO "bamoki" VALUES(23,35,'23:35','ئایا ئەو (ھود علیە السلام) بەڵێن بە ئێوە دەدات بەڕاستی کاتێک مردن وە بوون بەخاک و ئێسکی (ڕزیوو) بێگومان ئێوە (لە گۆڕ) دەر دەھێنرێن (زیندوو دەکرێنەوە)');
INSERT INTO "bamoki" VALUES(23,36,'23:36','زۆر دوورە، ئەستەمە ئەو بەڵێنەی بە ئێوە دراوە (گوایە زیندوو دەکرێنەوە)');
INSERT INTO "bamoki" VALUES(23,37,'23:37','ژیان (ێکی تر) نیە تەنھا ژیانی ئەم دونیایەمان نەبێت دەمرین ودەژین (نەوە لە دوای نەوە) وە ئێمە زیندوو ناکرێینەوە');
INSERT INTO "bamoki" VALUES(23,38,'23:38','ئەوە (کە دەڵێ پێغەمبەرم) تەنھا پیاوێکە درۆ دەکات بە دەم خواوە وە ئێمە ھەرگیز باوەڕی پێ ناکەین');
INSERT INTO "bamoki" VALUES(23,39,'23:39','(نوح) ووتی ئەی پەروەرد گارم یارمەتیم بدە (سەرم بخە) لەبەر ئەوەی باوەڕم پێ ناھێنن');
INSERT INTO "bamoki" VALUES(23,40,'23:40','(خوا) فەرمووی لە ماوەیەکی زۆر کەمدا بێگومان پەشیمان دەبنەوە');
INSERT INTO "bamoki" VALUES(23,41,'23:41','ئەمجا شریخە و دەنگێکی زۆ بەھێز (دای بەسەریاندا) ھەموویانی گرت کە ھەقیان بوو وئەوانمان کرد بە (وێنەی) پوش و کەفی (سەر لافاو) جا دووری (لە ڕەحمەتی خوا) بۆگەلی ستەمکاران');
INSERT INTO "bamoki" VALUES(23,42,'23:42','دواتر لە پاش ئەوان بەدیمان ھێنا ﴿﴾ چەند گەل وئوممەتێکی تر');
INSERT INTO "bamoki" VALUES(23,43,'23:43','وەھیچ ئوممەتێک پێش ناکەوێت لەکاتی لەناوبردنی خۆی ھەروەھا دواش ناکەوێت');
INSERT INTO "bamoki" VALUES(23,44,'23:44','لەپاشان پێغەمبەرانی خۆمان یەک لەدوای یەک نارد ھەر کاتێک ئوممەتێک لەخۆیان پێغەمبەرێکیان بۆ بھاتایە باوەڕیان پێ نەدەھێنا جا ئێمەش ئەم ئوممەتە (بێ باوەڕانەمان) یەک بە دوای یەکدا (لەناوبرد) وە (باسی) ئەوانمان کردە ڕووداو وچەند بەسەرھاتێک (کەخەڵکی لەنێوان خۆیان باسی بکەن) ئەمجا دووری (لەڕەحمی خوا) بۆ گەلێک باوەڕ ناھێنن');
INSERT INTO "bamoki" VALUES(23,45,'23:45','لەپاشان موسا و ھارونی برایمان نارد بەنیشانەکانمان و بەڵگەی ئاشکراوە');
INSERT INTO "bamoki" VALUES(23,46,'23:46','بۆ لای فیرعەون و دارو دەستەکەی بەڵام خۆیان بە گەورە دانا (باوەڕیان نەھێنا) وە ئەوان گەلێکی دەسەڵاتدار بوون بەسەر خەڵکیدا');
INSERT INTO "bamoki" VALUES(23,47,'23:47','ئەمجا ووتیان ئایا ئێمە بە دوو کەسی وەک خۆمان باوەڕ بھێنین لەکاتێکدا گەلەکەیان (بەنی ئیسرائیل) ئێمە دەپەرستن (گەردن کەچن بۆ ئێمە)');
INSERT INTO "bamoki" VALUES(23,48,'23:48','لەبەر ئەوە باوەڕیان پێ نەھێنان ئەمانیش (وەک گەلانی تر) لەناو بران');
INSERT INTO "bamoki" VALUES(23,49,'23:49','سوێند بەخوا بێگومان ئێمە تەوراتمان بەخشی بەموسا تا (بەنی ئیسرائیل) ڕێنمون ببن');
INSERT INTO "bamoki" VALUES(23,50,'23:50','ھەروەھا عیسای کوڕی مەریەم و دایکیمان کرد بە بەڵگە و موعجیزەیەکو ھەر دووکیان (عیسا و دایکی)مان دانا لەشوێنێکی بڵند (بیت المقدس) کەخاوەنی (ھەموو ھۆیەکی) حەسانەوە و کانیاوە');
INSERT INTO "bamoki" VALUES(23,51,'23:51','ئەی نێرراوانی خوا لەخۆراکی پاکو (حەڵاڵ) بخۆن وە کردەوەی چاک بکەن (چونکە) بەڕاستی من بەوەی ئێوە دەیکەن ئاگادارم');
INSERT INTO "bamoki" VALUES(23,52,'23:52','وەبێگومان ھەر ئەمەیە ئاین و دینی ئێوە (ئەی پێغەمبەران دەستی پێوە بگرن) کەیەک ئاین و بەرنامەیە وەمنیش پەروردگارتانم کەواتە خۆتان بپارێزن (لەتۆڵەم)');
INSERT INTO "bamoki" VALUES(23,53,'23:53','(شوێنکەوتوانیان) ئاینەکەیان (کەیەک ئاین بوو) پارچە پارچە کرد لەنێوان خۆیاندا بەچەند پارچەیەک جا ھەر کۆمەڵ و دەستەیەکیان دڵخۆشن بەو (بەشە ئاینەی) کەھەیانە');
INSERT INTO "bamoki" VALUES(23,54,'23:54','کەوا بوو وازیان لێ بھێنە لەو بێ ئاگایی و گومڕاییەی دایپۆشیون (بابژین) تاماوەیەک');
INSERT INTO "bamoki" VALUES(23,55,'23:55','ئایا ئەوانە وادەزانن ئەوەی پێیان دەبەخشین لەسامان و ماڵ ومناڵ');
INSERT INTO "bamoki" VALUES(23,56,'23:56','(بەھۆی شیاویانەوە) پەلەمان کردووە بۆیان لەخێر وخۆشیەکاندا (نەخێر وانیە) بەڵکو ئەوانە ھەست ناکەن (کە بۆ تاقی کردنەوەیانە)');
INSERT INTO "bamoki" VALUES(23,57,'23:57','بەڕاستی ئەو کەسانەی لەترسی (سزای) پەروەردگاریان ھەمیشە دەترسن');
INSERT INTO "bamoki" VALUES(23,58,'23:58','وە ئەو کەسانەش کە بەئایەتەکانی پەروەردگاریان باوەڕ دەھێنن');
INSERT INTO "bamoki" VALUES(23,59,'23:59','وە ئەو کەسانەش کەھاوبەش بۆ پەروەردگاریان دانانێن');
INSERT INTO "bamoki" VALUES(23,60,'23:60','وە ئەو کەسانەی کە دەیبەخشن (لەزەکات و خێر) لەوەی پێمان بەخشیون لەکاتێکدا دڵیان دەترسێ و(دەلەرزێ) لەبەر ئەوەی دڵنیان بۆ لای پەروەردگاریان دەگەڕێنەوە');
INSERT INTO "bamoki" VALUES(23,61,'23:61','ئا ئەوانە (کە خاوەنی ئەو سیفەتانە بوون باسکران) پەلە دەکەن لە ئەنجامدانی چاکەکاندا وە ئەوان بۆ چاکە کردن لەپێشن');
INSERT INTO "bamoki" VALUES(23,62,'23:62','وە ئێمە ئەرک ناخەینە سەر ھیچ کەس بەقەدەر توانای خۆی نەبێت وە کتێبێکمان لەلایە (کردەوەی ھەموو بەندەکانی تێدایە) کەبەڕاست و دروستی دەدوێت وەھیچ ستەمێک لەوان ناکرێت');
INSERT INTO "bamoki" VALUES(23,63,'23:63','بەڵام (بێ بڕوایان) دڵەکانیان لەبێ ئاگایی و نەزانیدایە لەم (کتێب و نامەی کردەوانە) وە ئەوان چەند کردەوەی خراپی تریان ھەیە جیاوازە لە کردەوەی باوەڕداران کە ئەوان ھەمیشە ئەنجام دەریانن');
INSERT INTO "bamoki" VALUES(23,64,'23:64','ھەتا ئەو کاتەی کەسزا بەسەر گەورە و خۆشگوزەرانەکانیاندا دەھێنین (لەدونیادا) لەناکاودا ئەوان دەستدەکەن بە ھاوار و پاڕانەوە');
INSERT INTO "bamoki" VALUES(23,65,'23:65','(پێـیان دەوترێت) ئەمڕۆ ھاوار مەکەن بەڕاستی ئێوە لەلایەن ئێمەوە یارمەتی نادرێن');
INSERT INTO "bamoki" VALUES(23,66,'23:66','بێگومان (پێشتر) ئایەتەکانی من دەخوێنرایەوە بەسەرتاندا بەڵام ئێوە بەدوای خۆتاندا پاشەوپاش دەگەڕانەوە');
INSERT INTO "bamoki" VALUES(23,67,'23:67','لەبەرامبەر ئەو (قورئانە)دا خۆتان بەگەورە دەزانی شەوگارتان دەبردە سەر بەگاڵتە کردن و قسەی ناشرین ووتن (بەئایەتەکانی ئێمە)');
INSERT INTO "bamoki" VALUES(23,68,'23:68','دەی ئایا ئەوان (قورەیش) بیریان لەم قورئانە نەکردۆتەوە یان شتێکیان بۆھاتووە کە بۆ باو و باپیرە پێشینەکانیان نەھاتووە');
INSERT INTO "bamoki" VALUES(23,69,'23:69','یان پێغەمبەرەکەیان نەناسیووە بەو ھۆیەوە ئەوان دان بە پێغەمبەرایەتیەکەیدا نانێن');
INSERT INTO "bamoki" VALUES(23,70,'23:70','یان دەڵێن ئەو (پێغەمبەرە) شێـتە (نەخێر وا نیە) بەڵکو ھەق وڕاستی بۆ ھێناون بەڵام زۆربەیان بوغزێنەری ھەق و ڕاستین (ڕقیان لێیەتی)');
INSERT INTO "bamoki" VALUES(23,71,'23:71','وە ئەگەر ھەق و ڕاستی شوێن ھەوا و ئارەزوی ئەوان بکەوتایە ئاسمانەکان و زەوی و ھەموو ئەوانەی تیایاندایە دەشێوان و تێکدەچوون بەسەر یەکدا بەڵکو ئێمە قورئانێکمان بۆ ھێناون کەھۆی یادخستنەوە ( و سەربەرزی)یە بۆ ئەوان بەڵام ئەوان لەبیرخستنەوە (و سەربەرزی) خۆیان پشتێکەرن');
INSERT INTO "bamoki" VALUES(23,72,'23:72','یان داوای پارە و خەرج لەوان دەکەیت (لەبەرانبەر بانگەوازەکەتدا بۆیە باوەڕ ناھێنن) ئەو بەخشش و ڕۆزیەی پەروەردگارت چاکترە (بۆ تۆ ئەی موحەممەد صلى الله عليه وسلم) وە خوا چاکترینی ڕۆزیدەرانە');
INSERT INTO "bamoki" VALUES(23,73,'23:73','وە بەڕاستی تۆ ئەوان بانگ دەکەیت بۆ ڕێگای ڕاست');
INSERT INTO "bamoki" VALUES(23,74,'23:74','وە بێگومان کەسانێ کەباوەڕیان نیە بەڕۆژی دوایی لەم ڕێگا ڕاستە لادەرن');
INSERT INTO "bamoki" VALUES(23,75,'23:75','وە ئەگەر ڕەحم بەوان (بت پەرستانی قورەیش) بکەین وە ئەو ناخۆشی و بەڵایەی لەسەریان بوو لایبەین و ھەڵیگرین (کەبریتی بوو لەبرسێتی بۆماوەی حەوت ساڵ) (نەک بیر ناکەنەوە) بێگومان لەسەرکەشی خۆیاندا ڕۆدەچن وبەسەر گەردانی دەمێننەوە');
INSERT INTO "bamoki" VALUES(23,76,'23:76','سوێند بەخوا بەڕاستی ئێمە ئەوانمان تووشی سزا و ئازار کرد (تاھۆش بکەنەوە) بەڵام ئەوان (لەبەرانبەر) پەروەردگاریاندا نەخۆیان بەکەمزانی (ملکەچ بوون بۆی) وە نە ھانا و نزایان (بۆخوا) دەبەن لەکاتی ناڕەحەتیدا');
INSERT INTO "bamoki" VALUES(23,77,'23:77','(بەردەوام بوون لەسەر حاڵی خۆیان) تا کاتێک دەرگایەکمان بۆ کردنەوە کەسزایەکی زۆر سەختی ھەبوو ئیتر کتوپڕ لەوێدا بێ ئومێد بوون');
INSERT INTO "bamoki" VALUES(23,78,'23:78','وە ئەو (خوا) زاتێکە دروستی کردووە بۆ ئێوە گوێ و چاوو دڵ سوپاسی ئەو ناکەن زۆر کەم نەبێت');
INSERT INTO "bamoki" VALUES(23,79,'23:79','وە ئەو (خوا) زاتێکە ئێوەی (دروستکردوە و) بڵاوی کردونەتەوە بەزەویداوەھەر لای ئەویش کۆدەکرێنەوە (لەڕۆژی دواییدا)');
INSERT INTO "bamoki" VALUES(23,80,'23:80','وەئەو (خوا) زاتێکە دەژێنێت و دەمرێنێت وەجیاوازی شەوو ڕۆژیش ھەربەدەست ئەوە دەی ئایا ئێوە بیر ناکەنەوە؟');
INSERT INTO "bamoki" VALUES(23,81,'23:81','(نەخێر بیر ناکەنەوە) بەڵکو ئەوان ھەر ئەوەیان دەووت کە پێشووەکانیان دەیانووت');
INSERT INTO "bamoki" VALUES(23,82,'23:82','دەیانووت ئایا کاتێک کەمردین وبووین بەخۆڵ و ئێسکی (ڕزیوو) ئایا ئێمە (جارێکی تر) زیندوو دەکرێینەوە');
INSERT INTO "bamoki" VALUES(23,83,'23:83','سوێند بەخوا بێگومان ئەم بەڵینە بەئێمە و باوو باپیرانمان درابوو لەمەو پێش ئەم (بەڵێنانە) ھیچ نیە جگە لەئەفسانەی (قسەی پڕو پوچی) پێشووەکان');
INSERT INTO "bamoki" VALUES(23,84,'23:84','(ئەی موحەممەد صلى الله عليه وسلم) بڵێ زەوی و ئەوەی لەناویدایە ھی کێـیە؟ ئەگەر ئێوە شت دەزانن');
INSERT INTO "bamoki" VALUES(23,85,'23:85','بێگومان دەڵێن (ھەمووی) ھی خوایە بڵێ دەی ئایا بیر ناکەنەوە؟');
INSERT INTO "bamoki" VALUES(23,86,'23:86','بڵێ ئەی کێ پەروەردگاری حەوت ئاسمان و پەروەردگاری عەرشی گەورەیە؟');
INSERT INTO "bamoki" VALUES(23,87,'23:87','بێگومان دەڵێن (ھەمووی) ھی خوایە بڵێ دەی ئایا خۆتان ناپارێزن و (لەخوا ناترسن)');
INSERT INTO "bamoki" VALUES(23,88,'23:88','بڵێ کێ فەرمان ڕەوایی ھەموو شتێکی بەدەستە وە پەنای (ھەموو لایەک) دەدات و کەس لەو پەنا نادرێت ئەگەر ئێوە شت دەزانن ؟');
INSERT INTO "bamoki" VALUES(23,89,'23:89','بێگومان دەڵێن (ھەموو ئەوانە) ھی خوان بڵێ کەواتە ئێوە چۆن سیحر و جادووتان لێکراوە (کە ئاینی خـوا بەناھەق دەزانن لەگەڵ داننانتان بەھەموو ڕاستیەکانیدا)');
INSERT INTO "bamoki" VALUES(23,90,'23:90','(نەخێر وانیە) بەڵکو ئێمە ڕاستیمان بۆ ھێناون وە بێگومان ئەوان درۆزنن');
INSERT INTO "bamoki" VALUES(23,91,'23:91','خوا ھیچ ڕۆڵەیەکی بۆ خۆی بڕیار نەداوە وە ھیچ پەرستراوێکی تری لەگەڵدا نەبووە (ئەگەر پەرستراوی تری لەگەڵدا بوایە) ئەوکاتە ھەر پەرستراوێک چی دروستکردووە (بۆ خۆی) دەبرد وە ھەندێکیان زاڵ دەبوون بەسەر ھەندێکی تریاندا خوا پاک وبێگەردە لەوەی ئەوان دەیڵێن');
INSERT INTO "bamoki" VALUES(23,92,'23:92','(خوا) زانای ھەموو نھێنی وئاشکراکانە وە بەرزو بڵندە لەوەی کەئەوان (بت پەرستان) دەیکەنە ھاوبەشی');
INSERT INTO "bamoki" VALUES(23,93,'23:93','(ئەی موحەممەد صلى الله عليه وسلم) بڵێ ئەی پەروەردگارم ئەگەر ھەرنیشانم دەدەیت ئەو سزایەی کەبەوان بەڵێن دراوە');
INSERT INTO "bamoki" VALUES(23,94,'23:94','ئەی پەروەردگارم مەمخەرە ڕیزی گەلی ستەمکارانەوە');
INSERT INTO "bamoki" VALUES(23,95,'23:95','وە بەڕاستی ئێمە بەتواناین ئەو بەڵێنەی (بەسزادانیان) بەوانمان داوە نیشانت بدەین');
INSERT INTO "bamoki" VALUES(23,96,'23:96','بە جوانترین و چاکترین شێوە بەرگری بکە لەخراپە ئێمە ئاگادارترین بەوەی ئەوان دەیڵێن (بەخۆت و ئاینەکەت)');
INSERT INTO "bamoki" VALUES(23,97,'23:97','وە بڵێ ئەی پەروەردگارم پەنا دەگرم بە تۆ لەخەتەرە و(پاڵنەرو) وەسوەسەی شەیتانەکان');
INSERT INTO "bamoki" VALUES(23,98,'23:98','وە پەنات پێ دەگرم لەوەی (شەیتانەکان) ئامادەبن لام (سەرم لێ بشێوێنن)');
INSERT INTO "bamoki" VALUES(23,99,'23:99','(بێ بڕوایان وەک خۆیان دەمێننەوە) ھەتا کاتی مردن دێت بۆ ھەر کامیان ئەوسا دەڵێ ئەی پەروەردگارم بمگێرەرەوە (بۆ دونیا)');
INSERT INTO "bamoki" VALUES(23,100,'23:100','بەڵکو کردەوەی چاک بکەم (لە بری ئەوەی نەمکردوە) لەو تەمەنەی لە دەستم چوو نەخێر بێَگومان ئەمەی(کە دەیڵێت) تەنھا قسەیەکە ئەو دەیڵێ (ئەگەر بگەڕێتەوە بۆ دونیا ھەروەک جاران دەبێت) و لەپێشیانەوە بەرزەخێک (ژیانی ناو گۆڕ) ھەیە تا ئەو ڕۆژەی زیندوو دەکرێنەوە');
INSERT INTO "bamoki" VALUES(23,101,'23:101','ئەمجا کاتێک فوو دەکرێت بە شەیپوردا لەو ڕۆژەدا ھیچ خزمایەتیەک نامێنێت لە نێوانیاندا وە پرسیاری یەکتری ناکەن');
INSERT INTO "bamoki" VALUES(23,102,'23:102','ئەمجا ھەر کەسێک کێشراوە چاکەکانی (تای تەرازووی کردەوە چاکەکانەی) قورس بێت ئەوە ئەوانە ڕزگارو سەرکەوتوون');
INSERT INTO "bamoki" VALUES(23,103,'23:103','وە ھەر کەسێک کێشراوە چاکەکانی (تای تەرازووی کردەوە چاکەکانەی) سووک بێت ئەوانە کەسانێکن زیانیان لە خۆیانداوە، خۆیان دۆڕاندووە لە دۆزەخدان بێ بڕانەوە');
INSERT INTO "bamoki" VALUES(23,104,'23:104','(ئاگری دۆزەخ) دەم و چاویان دەسوتێنێ و ھەموویان لە ئاگری دۆزەخ دا لێویان ھەڵقرچاوە و دانەکانیان دەرکەوتووە');
INSERT INTO "bamoki" VALUES(23,105,'23:105','(پێیان دەوترێت) ئایا ئایەتەکانی من لەدونیادا بەسەرتاندا نەدەخوێنرایەوە؟ کەچی ئێوە بەدرۆتان دەزانی و ئیمانتان پێ نەدەھێنا');
INSERT INTO "bamoki" VALUES(23,106,'23:106','(لەوەڵامدا) دەڵێن ئەی پەروەردگاری ئێمە بەدی و خراپی خۆمان زاڵ بوو بوو بەسەرماندا وە ئێمە کەسانێکی گومڕابووین');
INSERT INTO "bamoki" VALUES(23,107,'23:107','ئەی پەروەدگارمان لەم (دۆزەخە) دەرمان بھێنە جا ئەگەر جارێکی تر گەڕاینەوە (بۆ بێ باوەڕی) ئەوە بەڕاستی ئێمە ستەمکارین');
INSERT INTO "bamoki" VALUES(23,108,'23:108','وە (خوا) دەفەرموێت: بە ڕیسوایی بمێننەوە لەدۆزەخدا و ھیچ قسەیەکم لەگەڵدا مەکەن (إخسأ: بۆ سەگ بەکاردێت کاتێ دەری دەکەیت و پێی دەڵێیت چغە)');
INSERT INTO "bamoki" VALUES(23,109,'23:109','بێگومان لەدونیادا کۆمەڵێک لەبەندەکانی من دەیانووت ئەی پەروەردگارمان ئێمە باوەڕمان ھێناوە دەی لێمان خۆشبە و ڕەحممان پێ بکە چونکە تۆ چاکترینی میھرەبانانی');
INSERT INTO "bamoki" VALUES(23,110,'23:110','کەچی ئێوە گاڵتەتان بەوان دەکرد تا (ئەو گاڵتە کردنەتان بە) ئەوان یادی منی لەبیربردنەوە وە ئێوە ھەمیشە بەوان پێدەکەنین');
INSERT INTO "bamoki" VALUES(23,111,'23:111','بێگومان ئەمڕۆ پاداشتم داونەتەوە بەھۆی خۆگرییانەوە بەڕاستی ھەر ئەوانن سەرکەوتوو');
INSERT INTO "bamoki" VALUES(23,112,'23:112','(خوا) دەفەرموێت: چەند ساڵ لەسەر زەویدا مانەوە؟');
INSERT INTO "bamoki" VALUES(23,113,'23:113','(لە وەڵامدا) دەڵێن ڕۆژێک یا کەمتر لەڕۆژێک ماینەوە لەو (فریشتانە) بپرسە کەژمێریارن');
INSERT INTO "bamoki" VALUES(23,114,'23:114','(خوا) فەرمووی نەماونەتەوە ماوەیەکی کەم نەبێت ئەگەر بەڕاستی ئێوە دەتانزانی؟');
INSERT INTO "bamoki" VALUES(23,115,'23:115','ئایا ئێوە گومانتان وابووە کە بێگومان ئێوەمان بەبێ ھودە دروستکردووە وە (وا گومانتان دەبرد) بێگومان ئێوە ناگەڕێنەوە بۆلای ئێمە؟');
INSERT INTO "bamoki" VALUES(23,116,'23:116','جا بەرزو بڵندی بۆ خوای پاشای ڕاست و ڕەوا ھیچ پەرستراوێک نیە جگە لە ئەو کە پەروەردگاری عەرشی بەڕێزو بەرزە');
INSERT INTO "bamoki" VALUES(23,117,'23:117','ھەر کەس (بپاڕێتەوە وداوا بکات لە غەیری خوا، یان) بپەرستێت پەرستراوێکی تر لەگەڵ خوادا کەھیچ بەڵگەیەکی نەبێت لەسەر ئەو (پەرستنە) ئەوە بەڕاستی لێپرسینەوەی تەنھا لای پەروەردگارێتی بێگومان کافران ڕزگار نابن');
INSERT INTO "bamoki" VALUES(23,118,'23:118','(ئەی موحەممەد صلى الله عليه وسلم) بڵێ ئەی پەروەردگارم لێم خۆشبە و ڕەحمم پێ بکە وە تۆ (ئەی خوایە) چاکترینی میھرەبانانی');
INSERT INTO "bamoki" VALUES(24,1,'24:1','ئەمە سورەتێکە ئێمە ناردومانەتە خوارەوە و فەرزمان کردووە (گوێڕایەڵی بڕیارەکانی) وە لەو (سورەتە)دا ھێناومانەتە خوارەوە ئایەتە ڕوونەکانبۆ ئەوەی ئێوە بیر بکەنەوە');
INSERT INTO "bamoki" VALUES(24,2,'24:2','ژن و پیاوی زیناکەر ھەریەکێکیان سەد دار(شەلاق)یان لێ بدەن وە بەزەیی ھاتنەوەتان پێیاندا (ژن و پیاوی زیناکار) نەتان گرێت (بەزەیتان پێیاندا نەیەتەوە) لە (جێ بەجێ کردنی) فەرمانی خوا ئەگەر ئێوە باوەڕتان ھەیە بەخواو بەڕۆژی دوایی وە با ئامادەی بینینی سزادانی ئەو دووانە بن کۆمەڵێک لەباوەڕداران');
INSERT INTO "bamoki" VALUES(24,3,'24:3','پیاوی زیناکار مارە ناکات تەنھا ئافرەتی زیناکار، یان ئافرەتی موشریک نەبێت وە ئافرەتی زیناکار مارەی ناکات پیاوی زیناکار یان پیاوی موشریک نەبێت ئەو (مارەکردن و شووکردن بەزیناکار)ە یاساغ کراوە لەسەر ئیمانداران');
INSERT INTO "bamoki" VALUES(24,4,'24:4','وە ئەوانەی کە بوھتانی (زینا) دەخەنە پاڵ ئافرەتانی داوێن پاک لەپاشان چوار شایەت ناھێنن (لەسەر ڕاستی بوھتانەکەیان) ئەوە ھەشتا دار (شەلاق)یان لێ بدەن ئیتر ھەرگیز شایەتیان لێ وەرمەگرن وە ئەوانە لەسنوور دەرچوون');
INSERT INTO "bamoki" VALUES(24,5,'24:5','جگە لەوانەی کە دوای ئەوە تەوبە دەکەن و کردەوەیان چاک دەکەن ئەوسا بەڕاستی خوا لێخۆش بوو و بەبەزەییە');
INSERT INTO "bamoki" VALUES(24,6,'24:6','ئەو (پیاوانەی) بوھتان بۆ خێزانەکانیان دەکەن وە بێجگە لەخۆیان ھیچ شایەتێکیان نیە ئەوە شایەتیدانی ھەریەک لەوانە (بەم جۆرەیە) کە چوار سوێند بەخوا بخوات کەبێگومان ئەو لەڕاستگۆیانە (کەدەڵێت خێزانەکەم بینیوە زینای کردووە)');
INSERT INTO "bamoki" VALUES(24,7,'24:7','وە لەپێنجەم جاردا بڵێ نەفرەتی خوا لەسەر ئەوبێ ئەگەر درۆزن بێت');
INSERT INTO "bamoki" VALUES(24,8,'24:8','ئەو ئافرەتەش (کەتاوانبار کراوە لەلایەن مێردەکەیەوە) سزای (زینای) لەسەر لادەبات بەمەی کەچوار جار سوێند بەخوا بخوات کەبێگومان مێردەکەی لەدرۆزنانە (درۆ دەکات)');
INSERT INTO "bamoki" VALUES(24,9,'24:9','وە لەپێنجەم جاردا (بڵێت) خەشمی خوای لەسەر بێت ئەگەر مێردەکەی لەڕاستگۆیان بێت');
INSERT INTO "bamoki" VALUES(24,10,'24:10','وە ئەگەر فەزڵ و بەزەیی خوا نەبوایە بەسەرتانەوە (ھەر کام لەو پیاو و ئافرەتەی باسکران درۆزن بن خوا دەریدەخست و سزای دەدا لەدونیادا)وە بێگومان خوا گیراکاری پەشیمانی و کار دروستە');
INSERT INTO "bamoki" VALUES(24,11,'24:11','بێگومان کەسانێک کە ئەو بوھتانەیان (بۆ عائیشەی خێزانی پێغەمبەر صلى الله عليه وسلم) ھەڵبەست دەستەیەک بوون لەخۆتان (ومونافیقیشیان تێدا بوو) وا مەزانن ئەو (بوھتانە) شەڕو خراپە بۆتان (وانیە) بەڵکو خێرو چاکەیە بۆتان بۆھەر یەک لەوانە ھەیە لەتاوان بەو ئەندازەی ھەوڵیداوە (لەو بوھتانەدا) وە ئەو کەسەیان کەبەشی گەورەی بوھتانەکەی گرتە ئەستۆ (کەعەبدوڵڵای کوڕی ئوبەی بوو) (لەڕۆژی دواییدا) بۆ ئەوە سزای ھەرە گەورە');
INSERT INTO "bamoki" VALUES(24,12,'24:12','دەبوا کاتێک ئەو بوھتانەتان بیست پیاوان وئافرەتانی ئیماندار گومانی چاکیان بەخۆیان ببردایە وە بیانووتایە ئەمە بوھتانێکی ئاشکراو ڕوونە');
INSERT INTO "bamoki" VALUES(24,13,'24:13','دە بۆچی ئەو بوھتانکارانە چوار شایەتیان لەسەری نەھێنا ئەمجا ئەگەر شایەتەکانیان نەھێنا کەواتە ھەر ئەوانەن لای خوا درۆزن');
INSERT INTO "bamoki" VALUES(24,14,'24:14','وە ئەگەر فەزڵ وڕەحمەتی خوا نەبوایە بەسەرتانەوە لەدونیا و ڕۆژی دواییدا دووچارتان دەبوو لەبەر ڕۆچوونتان لەو بوھتانەدا (بۆ عائیشە) سزایەکی گەورە');
INSERT INTO "bamoki" VALUES(24,15,'24:15','کاتێک ئەم بوھتانەتان بەدەماو دەم لە یەکترەوە وەردەگرت وە بەزمانتان شتێک دەڵێن کە زانیاریتان دەربارەی نەبوو واتان دەزانی شتێکی سووک و ئاسانە لەکاتێکدا لای خوا زۆر گەورەیە');
INSERT INTO "bamoki" VALUES(24,16,'24:16','بۆچی کاتێک ئەمەتان بیست نەتانووت بۆ ئێمە شیاو نیە قسە (و بوھتانی) وا بکەین پاک و بێگەردی بۆ تۆ خوایە، ئەمە بوھتانێکی زۆر گەورەیە');
INSERT INTO "bamoki" VALUES(24,17,'24:17','خوا ئامۆژگاریتان دەکات کە ھەرگیز نەگەڕێنەوە بۆ سەر وێنەی ئەوەی ڕوویدا ئەگەر ئێوە بڕوادارن');
INSERT INTO "bamoki" VALUES(24,18,'24:18','وە خوا ئایەتەکانی خۆی بۆ ئێوە ڕوون دەکاتەوە وە خوا زانا وکار دروستە');
INSERT INTO "bamoki" VALUES(24,19,'24:19','بەڕاستی ئەوانەی پێیان خۆشە زینا (کرداری ناشیرین) بڵاوبێتەوە لەناو خەڵکی ئیمانداردا سزای سەختیان بۆ ھەیە لەدونیاو ڕۆژی دواییدا وە خوا دەزانێت وئێوە نازانن');
INSERT INTO "bamoki" VALUES(24,20,'24:20','ئەگەر فەزڵ و بەزەیی خوا نەبوایە بەسەرتانەوە (دووچاری سزا دەبوون بەھۆی ئەو بوھتانەوە کە کرا بۆ عائیشە) وەبێگومان خوا بەسۆز و میھرەبانە');
INSERT INTO "bamoki" VALUES(24,21,'24:21','ئەی ئەوانەی باوەڕتان ھێناوە بەدوای ھەنگاوەکانی شەیتان دا مەڕۆن وە ھەر کەس شوێن ھەنگاوەکانی شەیتان بکەوێت چونکە بەڕاستی شەیتان فەرمان دەکات بەکاری زۆر ناشیرین (زینا) و خراپە، ئەگەر فەزڵ و میھرەبانی خوا نەبوایە بەسەرتانەوە ھەرگیز کەسێک لەئێوە پوخت و پاک نەدەبوەوە بەڵام خوا ھەر کەسی بوێت (شیاوبێ) پاکی دەکاتەوە وە خوا بیسەری زانایە');
INSERT INTO "bamoki" VALUES(24,22,'24:22','باسوێند نەخۆن ئەوانەتان کەخاوەنی چاکە وسەروەت و ساماننکەیارمەتی نەدەن بەخزمان و ھەژاران و کۆچەران لەپێناوی خوادا دەبا لێ بووردەبن و چاوپۆشی بکەن ئایا پێتان خۆش نیە خوا لێتان خۆش ببێت وە خوا لێخۆش بوو و میھرەبانە');
INSERT INTO "bamoki" VALUES(24,23,'24:23','بێگومان ئەوانەی بوھتانی زینا دەکەن بۆ ئافرەتانی داوێن پاکی بێ ئاگای ئیماندار نەفرینیان لێکراوە لەدونیا و ئاخیرەت دا وە بۆیان ھەیە سزای زۆر گەورە');
INSERT INTO "bamoki" VALUES(24,24,'24:24','ڕۆژێک (دێت) شایەتی ئەدات لەسەریان زمانیان و دەست و قاچەکانیان بەوکردەوانەی کە کردوویانە');
INSERT INTO "bamoki" VALUES(24,25,'24:25','لەو ڕۆژەدا خوا پاداشتی تەواوی کاروکردەوەیان بەڕاستی ودادگەرانەوە دەداتەوە وە ئەوکاتە دەزانن بەڕاستی ھەر خوا ھەقی ئاشکراو دیارە (بۆ خۆی وسیفات وکرداری)');
INSERT INTO "bamoki" VALUES(24,26,'24:26','(وا ڕاستە) ئافرەتانی ناپاک بۆ پیاوانی ناپاک بن وە پیاوە پیسەکان بۆ ئافرەتە پیسەکان وە ئافرەتە پاکەکان بۆ پیاوە پاکەکان وە پیاوە پاکەکان بۆ ئافرەتە پاکەکان بن، ئەو پاکانە بەری کراون لەو (تۆمەتانە)ی ناپاکان دەیڵێن بۆ ئەو پاک و چاکانەیە لێبووردن و ڕۆزی چاک و پیرۆز (کە بەھەشتە)');
INSERT INTO "bamoki" VALUES(24,27,'24:27','ئەی ئەو کەسانەی بڕواتان ھێناوە مەچنە ھیچ ماڵێک جگە لەماڵەکانی خۆتان ھەتا پرس دەکەن (بەخاوەنەکانیان) وە (دوای ئەوەی موڵەتدران) سەلام بکەن لە خاوەن ماڵەکە ئەوە چاکترە بۆتان بۆ ئەوەی پەندو ئامۆژگاری وەرگرن');
INSERT INTO "bamoki" VALUES(24,28,'24:28','ئەمجا ئەگەر کەستان نەدی تیایدا ئەوە مەچنە ژورەوە بۆ ئەوماڵە ھەتا مۆڵەت نەدرێن وە ئەگەر پێتان ووترا بگەڕێنەوە ئەبێ بگەڕێنەوە ئەوە چاکتر و پاکترە بۆتان وە خوا زانایە بەھەموو ئەو کردەوانەی دەیکەن');
INSERT INTO "bamoki" VALUES(24,29,'24:29','ھیچ گوناھێکتان لەسەر نیە (گوناھبار نابن) کەبچنە ئەو ماڵانەی کەسی تێدا نیشتەجێ نیە و کەل و پەلی خۆتانی تیدا بێت (وەک: میوانخانە) وە خوا دەزانێت بەوەی ئاشکرای دەکەن و بەوەیشی دەیشارنەوە');
INSERT INTO "bamoki" VALUES(24,30,'24:30','(ئەی موحەممەد صلى الله عليه وسلم) بڵێ بەپیاوانی ئیماندار چاوی خۆیان (لەسەیر کردنی نامەحرەم) داخەن و بپارێزن وە عەورەتیان (لەداوێن پیسی) بپارێزن ئەوە چاکتر و پاکترە بۆ ئەوان بێگومان خوا ئاگایە بەھەموو کردەوەیەک کەدەیکەن');
INSERT INTO "bamoki" VALUES(24,31,'24:31','وە بڵێ بە ئافرەتانی ئیمانداریش چاویان داخەن وبپارێزن (لەسەریر کردنی نامەحرەم) وە عەورەتیان بپارێزن (لەداوێن پیسی) وە جوانی خۆیان دەرنەخەن جگە لەوەی بەدەرەوەیە و دیارە لەجوانی (وەک دەم و چاو و دەستەکانیان) وە با سەرپۆشە کانیان بدەن بەسینگ و بەرۆکیاندا وە جوانی خۆیان دەرنەخەن (زیاد لەوەی دەردەکەوێت) بۆ مێردەکانیان نەبێت یان بۆ باوکیان یان باوکی مێردەکانیان یان کوڕەکانیان، یا کوڕی مێردەکانیان یا براکانیان یان کوڕی براکانیان یان کوڕی خوشکەکانیان یان بۆ ئافرەتانی (ئیماندار) یان کەنیزەکەکانیان یان ئەو شوێن کەوتەو پیاوانەی کەبێ نیازن لەئافرەت (وەک خزمەتکار و پیر و نەخۆش کەئارەزووی ژن و مێردایەتیان نیە) یان ئەو منداڵە کوڕانەی کەئاگایان لە لەش و(عەورەتی) ئافرەتان نەبێت (باڵغ نەبووبن) (ئافرەتان لەکاتی ڕۆشتندا) قاچیان نەدەن بەزەویدا (تا دەنگی خڕخاڵ و پاوانەکانیان بێت و) بزانرێت بەو جوانیەی پێیان کە شاردویانەتەوە ھەمووتان تەوبە بکەن و بگەڕێنەوە بۆ لای خوا ئەی ئیماندارەکان تا ڕزگار و سەرکەوتوو بن');
INSERT INTO "bamoki" VALUES(24,32,'24:32','ژن مارە ببڕن بۆ پیاوانی بێ ژن و ژنانی بێ مێردیش بدەن بەشوو (بە مێرد) ھەروەھا ئەوانەی ئیماندار و چاکن لەکۆیلەکان (ژنیان بۆ بھێنن) و کەنیزەکان (یش بدەن بەشوو) ئەگەر ئەوان ھەژارو کەمدەست بن خوا لەبەخشندەی و فەزڵی خۆی دەوڵەمەندو بێ نیازیان دەکات وە خوا (فەزڵ) فراوان و زانا و دانایە');
INSERT INTO "bamoki" VALUES(24,33,'24:33','وە دەبێت ئەوانەی کەتوانای (ئەرکی) ژن ھێنانیان نیە پاکژ خۆیان ڕاگرن ھەتا خوا بەفەزڵ (و ڕۆزی) خۆی بێ نیازیان دەکات وە ئەوانەی داوای نوسراو دەکەن (بۆ کڕینەوەی خۆیان) لەبەندە و کەنیزەکەکانتان ئەوە نوسراویان لەگەڵدا بنوسن (بۆ کڕینەوە و ئازاد بوونی خۆیان) ئەگەر خێرتان لەواندا بەدی دەکرد (کەبریتیە لەتوانای دانی ئەو پارەی ئەبێ بیدات بەخاوەنەکەی بۆ ئازاد بوونی) وە لەو ماڵەی کەخوا بەئێوەی داوە پێیان بدەن وە کەنیزەکەکانتان زۆر لێ مەکن (ناچاریان مەکەن) لەسەر زینا کردن ئەگەر ئەیانویست داوێن پاک بن تا ماڵ وسامانی ژیانی دونیاتان دەست کەوێت، جا ھەرکەس ناچاریان بکات (زۆریان لێ بکات) ئەمجا بەڕاستی خوا دوای ئەو زۆر لێ کردنەیان لێبووردە و میھرەبانە (بەو کەنیزەکانە کە بەزۆر زینایان پێکراوە)');
INSERT INTO "bamoki" VALUES(24,34,'24:34','سوێند بەخوا بەڕاستی ئێمە ناردوومانەتە خوارەوە بۆ ئێوە چەند ئایەت وبەڵگە ونیشانەیەکی ڕوونکەرەوە وە چەند نموونە و بەسەرھاتی ئەو گەلانەی لەپێش ئێوەوە بوون ھەروەھا پەندو ئامۆژگاری بۆ پارێزکاران ولەخوا ترسان');
INSERT INTO "bamoki" VALUES(24,35,'24:35','خوا نورو ڕوناکیەو ڕووناککەرەوەی ئاسمانەکان و زەویە وێنەی ڕووناککردنەوەی خوا وەک تاقێک وایە چرایەکی تێدا بێت ئەو چرایەش لەناو شوشەیەکدا بێت ئەو شوشەیەش وەک ئەستێرەیەکی گەش و پرشنگدار بێت (ئەو چرایە) دائەگیرسێت بە (ڕۆنی) درەختێکی زەیتونی بەفەڕو بەرەکەت کە نەڕۆژھەڵاتی بێت، وە نەڕۆژ ئاوایی (سێبەر نایگرێ نەکاتی ھەڵھاتنی خۆر نەکاتی ئاوابوونی) (ڕۆنەکەی ئەوەند پاک بێت) نزیکە ڕۆنەکەی داگیرسێ با ئاگریشی بەر نەکەوێ ڕووناکی لەسەر ڕوناکی (ڕووناکی زەیت و ڕووناکی ئاگر) خوا ھەرکەسی بوێت ڕێنمونی دەکات بۆلای ڕووناکی (ئاینی) خۆی وە خوا چەندین نموونە دەھێنێتەوە بۆ خەڵکی وە خوا زانا و ئاگادارە بەھەموو شتێک');
INSERT INTO "bamoki" VALUES(24,36,'24:36','لەمزگەوتانێکدا خوا مۆڵەتیداوە کە بەگەورەو پاک ڕاگیرێ (بەرزو بڵند دروست کرێت) زیکرو یادی خوای تێدا بکرێ لەو (مزگەوتانە)دا تەسبیحاتی خوا دەکەن بەبەیانیان و ئێواراندا');
INSERT INTO "bamoki" VALUES(24,37,'24:37','پیاوانێک کە بازرگانی و کڕین و فرۆشتن بێ ئاگایان ناکات لەیادی خواو نوێژ کردن و زەکاتدان لەڕۆژێک دەترسن (ڕۆژی قیامەت) دڵ و چاوەکان تیایدا (دەگۆڕێن و) ئاوەژوو دەبن');
INSERT INTO "bamoki" VALUES(24,38,'24:38','تا خوا پاداشتیان بداتەوە بەپاداشتی چاکترینی ئەوەی کردویانە وە (بێجگە لەپاداشتەکەیان) لە فەزڵی خۆی زیادەشیان دەداتێ وە خوا ڕزق و رۆزی (پاداشت) دەدا بەھەر کەس بیەوێت بەبێ ژمار، و بێ سنوور');
INSERT INTO "bamoki" VALUES(24,39,'24:39','وە ئەوانەی کەبێ بڕوان کردەوە (چاکە)کانیان وەک سەرابێک وایە (سەراب تیشکێکە کاتی زۆر گەرما لەبیاباندا وەک ئاو دێتە پێش چاو) لەبیابانێکدا (بریسکە بداتەوە) (کەسێکی) زۆر تینوو بەئاوی دەزانێ (لەدوورەوە) (بەپەلە دەڕوات بۆی) کاتێَک دەگاتە لای ھیچ بەدی ناکات (لەقیامەت کافر) دەبینێت خوا ئامادەیە لەلای ئەمجا خوا بە تەواوی پاداشتی (کردەوەکانی) دەداتێ وە خوا بە پەلە و خێرایە لەلێپرسینەوەدا');
INSERT INTO "bamoki" VALUES(24,40,'24:40','یا (کردەوەی بێ بڕوایان) وەک چەند تاریکییەک وایە لەناو دەریایەکی قوڵدا کەشەپۆل لەسەرشەپۆل دایپۆشی بێت لەسەر ئەو (شەپۆلە)یشەوە ھەورێکی ڕەشی (باراناوی) بێت تاریکیەکان نیشتبنە سەر یەکتری ئەگەر دەستی دەربھێنێت نزیکە دەستی خۆی نەبینێت (لەبەر تاریکی) جا ھەر کەس خوا نووری (ئیمانی) پێ نەبەخشیبێ ئەوەی ڕووناکی بێ نیەتی');
INSERT INTO "bamoki" VALUES(24,41,'24:41','ئایا نابینی (وە نازانی) بێگومان ھەمووئەوانەی لەئاسمانەکان و زەویدان تەسبیحاتی خوا دەکەن ھەروەھا باڵندەکان کاتێک باڵیان کردۆتەوە (بەئاسماندان تەسبیحاتی خوا دەکەن) ھەر یەک لەمانە نوێژ (دوعا) و تەسبیحاتی خۆی دەزانێت وە خوا بەوەی دەیکەن زانا وئاگادارە');
INSERT INTO "bamoki" VALUES(24,42,'24:42','تەنھا بۆ خوایە خاوەنیەتی ئاسمانەکان و زەوی وگەڕانەوەش ھەر بۆلای خوایە');
INSERT INTO "bamoki" VALUES(24,43,'24:43','ئایا نازانی (وە نابینی) کەخوا پەڵەھەورەکان دەبات (بەئاسماندا) بە ھێواشی پاشان ئەو (پەڵە ھەورانە) پەیوەست دەکات بەیەکەوە دواتر دەیانخاتە سەر یەکتری ئەمجا دەبینی دڵۆپە باران لەنێو ھەورەکەوە دەبارێت وە لە ئاسمانەوە لە ھەورەکان کەوەک کێون تەرزە دەبارێنێت ئەمجا تووشی زیان دەبێ بەم (تەرزەیە) ھەر کەسێک خوا بیەوێت وە لای دەدات لە سەر ھەر کەسێک کەخۆی بیەوێت نزیکە ڕووناکی و شەوقی چەخماخەی (ھەورە)کە (بینای) چاوەکان ببات');
INSERT INTO "bamoki" VALUES(24,44,'24:44','خوا شەوو ڕۆژ دەگۆڕێت (بەکورت و درێژ کردنیان یا بەرۆژ بوونەوەو شەو داھاتن) بەڕاستی لەمەدا پەندێک ھەیە بۆ خاوەن ژیرییەکان');
INSERT INTO "bamoki" VALUES(24,45,'24:45','وەخوا ھەموو زیندەوەرێکی لەئاو دروستکردووە ئەمجا ھەندێک لەوانە لەسەر سکی دەڕوات وە ھەندێکی تریان لەسەر دووقاچ دەڕوات وە ھەشیانە لەسەر چوار پێ دەڕوات خوا ھەرچی بوێت دروستی دەکات بێگومان خوا دەسەڵاتی بەسەر ھەموو شتێکدا ھەیە');
INSERT INTO "bamoki" VALUES(24,46,'24:46','سوێند بەخوا بەڕاستی ئێمە ناردومانەتە خوارەوە چەند ئایەتێکی ڕوونکەرەوە وخواش ھەر کەسی بوێ رێنمونی دەکات بۆ رێگای ڕاست');
INSERT INTO "bamoki" VALUES(24,47,'24:47','وە ئەوانە دەڵێن بڕوامان ھێناوە بەخواو بەپێغەمبەرو گوێڕایەڵیمان کردووە پاشان کۆمەڵێک لەوان پشتی تێ دەکەن لەپاش ئەوەی (ووتیان بڕوامان ھێناوە) وە ئەوانە (لەڕاستیدا) ئیماندار نین');
INSERT INTO "bamoki" VALUES(24,48,'24:48','وە کاتێک بانگ بکرێن بۆ لای (شەریعەتی) خواو پێغەمبەر صلى الله عليه وسلم بۆ ئەوەی حوکم بکات لەنێوانیاندا یەکسەر کۆمەڵێک لەوانە ڕوو وەردەگێڕن');
INSERT INTO "bamoki" VALUES(24,49,'24:49','بەڵام ئەگەر (لەحوکمدانەکەدا) حەقەکە بۆ ئەوان بێت دێن بۆ لای (حوکمەکە) بەو پەڕی ملکەچیەوە');
INSERT INTO "bamoki" VALUES(24,50,'24:50','ئایا دڵەکانیان نەخۆشی تێدایە یان لەگوماندا بوون یان دەترسن خواو پێغەمبەرەکەی صلى الله عليه وسلم ستەمیان لێ بکەن (نەخێر ھیچ لەوانە نیە) بەڵکو ئەوانە ھەر خۆیان ستەمکارن');
INSERT INTO "bamoki" VALUES(24,51,'24:51','قسەی ئیمانداران تەنھا ئەمەیە کاتێ بانگ بکرێن بۆ لای (بەرنامەی) خواو پێغەمبەرەکەی صلى الله عليه وسلم بۆ ئەوەی بڕیاربدات لەنێوانیاندا کەدەڵێن بیستمان و گوێڕایەڵین وە ھەر ئەوانەن ڕزگاربووان');
INSERT INTO "bamoki" VALUES(24,52,'24:52','جا ھەر کەس گوێرایەڵی خواو پێغەمبەرەکەی بکات وە لەخوا بترسێ و خۆی لە (سەرپێچی) خوا بپارێزێت ئەوە بەڕاستی ھەر ئەوانە سەرکەوتوون');
INSERT INTO "bamoki" VALUES(24,53,'24:53','(دوو رووەکان) سوێندیان دەخوارد بەخوا بە ئەو پەڕی سوێند خواردنیان کە ئەگەر فەرمانیان پێ بدەیت (بە دەرچوون بۆ غەزا) بێگومان دەردەچن بڵێ: سوێند مەخۆن ملکەچی و گوێڕایەڵی ئێوە زانراوە (قسەتان پێچەوانەی کردارتانە) بەڕاستی خوا ئاگادارە بەو کردەوانەی دەیکەن');
INSERT INTO "bamoki" VALUES(24,54,'24:54','بڵێ فەرمانبەرداری خواو فەرمانبەرداری پێغەمبەر صلى الله عليه وسلم بن ئەمجا ئەگەر پشتیان ھەڵکرد ئەوە ئەو (پێغەمبەر صلى الله عليه وسلم) تەنھا بەرپرسیارە لەوەی دراوە بەسەریدا (کە گەیاندنی پەیامی خوایە) وە ئێوەش بەرپرسن لەوەی دراوە بەسەرتاندا (کە باوەڕھێنانە) وە ئەگەر گوێڕایەڵی پێغەمبەر صلى الله عليه وسلم بکەن، ئەوە ھیدایەت وەردەگرن وە پێغەمبەر ھیچی لەسەر نیە بێجگە لەگەیاندنی ڕوون و ئاشکرای (پەیامی خوا) نەبێت');
INSERT INTO "bamoki" VALUES(24,55,'24:55','خوا بەڵێنی داوە بەو کەسانەتان کەباوەڕیان ھێناوە وە کردەوەی چاکەکانیان کردووە کە بێگومان دەیانکاتە جێنیشینی (خەڵکی تر) لەزەویدا ھەروەک ئەوانی (ئیماندارانی) پێش ئەمانی کردە جێنشین وە ئاینەکەیان بۆ دەچەسپێنێت ئەو (ئاینە)ی کەخوا پێی ڕازی بووە بۆیان وە ترس وبیمیان بۆ دەگۆڕێ بەئاسایش و ئارامی (لەوکاتەدا ئیتر) ھەر من دەپەرستن و ھیچ ھاوبەشیەکم بۆ بڕیار نادەن وە ھەر کەس بێ بڕوا بێ دوا ئەوە (بەدی ھاتنی بەڵێنەکان) ئەوە بەڕاستی ھەر ئەوانەن دەرچوو لەرێگەی خوا');
INSERT INTO "bamoki" VALUES(24,56,'24:56','وەنوێژ بەجێ بھێنن و زەکات بدەن و گویڕایەڵی پێغەمبەر صلى الله عليه وسلم بکەن تا خوا ڕەحمتان پێ بکات');
INSERT INTO "bamoki" VALUES(24,57,'24:57','ئەو کەسانەی بێ باوەڕن وانەزانن لەتۆڵەی خوا ڕزگاریان دەبێ لەزەویدا و ئەوان جێگایان ئاگرە ئای چ سەرئەنجامێکی بەد وخراپە');
INSERT INTO "bamoki" VALUES(24,58,'24:58','ئەی ئەو کەسانەی باوەڕتان ھێناوە دەبێ مۆڵەتتان لێ وەرگرن ئەوکەسانەی بەندەی ئێوەن (بەندەکانتان) ھەروەھا منداڵەکانتان کەھێشتا باڵغ نەبوون لەسێ کاتدا (بۆ چوونە ژوری نوستنی ژن و مێرد) لەپێش نوێژی بەیانی وە کاتێ پۆشاکتان دادەنێن (لادەبەن لەبەرتاندا) لەکاتی نیوەڕواندا و لەپاش نوێژی عیشا (خەوتنان) ئەم سێ کاتە کاتی خۆدەرخستنە بۆتان دروستە دوا ئەم سێ کاتە (بێ موڵەت وەرگرتن) نەبۆ ئێوەو نەبۆ ئەوان ھیچ گوناھێک نیە (موڵەت وەرنەگرن) (چونکە) ئەوان زۆر بەدەورو بەرتاندا دێن (بۆخزمەت کردنتان) ئێوە لەگەڵ یەکتردا تێکەڵن ئابەو جۆرە خوا ڕوونی دەکاتەوە ئایەت ونیشانەکانی بۆ ئێوە وەخوا زانا وکاردروستە');
INSERT INTO "bamoki" VALUES(24,59,'24:59','وەکاتێ کەمنداڵەکانتان باڵغ بوون ئەوسا دەبێت مۆڵەت وەربگرن وەکو چۆن کەسانی پێش ئەمان مۆڵەتیان وەردەگرت (لەھەموو کاتێکدا) ئابەم جۆرە خوا ڕوونی دەکاتەوە ئایەتەکانی بۆ ئێوە وەخوا زانا و دانایە');
INSERT INTO "bamoki" VALUES(24,60,'24:60','وەئافرەتانی پیرو لەکار کەوتوو ئەو ئافرەتانەی ئومێدی شوکردنیان نەماوە ھیچ گوناھێکیان لەسەر نیە کە(ھەندێک لە) پۆشاکەکانیان لابەرن بەڵام زینەت و جوانیان دەرنەخەن خۆ ئەگەر لاینەبەن و پۆشتە بن ئەوە چاکترە بۆیان وە خوا بیسەر و زانایە');
INSERT INTO "bamoki" VALUES(24,61,'24:61','کوێر گوناھی لەسەر نیە و شەل گوناھی لەسەر نیە ھەروەھا نەخۆش گوناھی لەسەر نیە (ئەم جۆرە کەسانە گوناە نیە بۆیان نان بخۆن لەگەڵ کەسانی ساغدا) وە بۆ ئێوەش گوناە نیە کە لەماڵەکانی خۆتان نان بخۆن (ماڵی منداڵ و خێزانەکانتان ھەر بەماڵی خۆتان دادەنرێت) یان (نان بخۆن لە) ماڵی باوکانتان یان لە ماڵی دایکانتان یان لە ماڵی براکانتان یان لە ماڵی خۆشکەکانتان یان لە ماڵی مامەکانتان یان لە ماڵە پورەکانتان (خوشکانی باوک) یان لە ماڵی خاڵەکانتان یان لە ماڵی پورەکانتان (خوشکانی دایک) یان لەو ماڵانەی کە کلیلەکانیان بەئێوەیە یان لە ماڵی برادەرو ھاوڕێتان ھیچ گوناھتان لەسەر نیە بەکۆمەڵ یان بەتەنھا نان بخۆن جا کاتێ چوونە (ھەر ماڵێک لە) ماڵان ئەوە سەلام لەیەکتر بکەن (لەوانەی کەوەک خۆتان وان)بەسەلام کردنێک لەلایەن خواوە (ھاتووە) سەلامێکی جوان وچاک و پاک ئابەو شێوە خوا ڕوونی دەکاتەوە ئایەتەکانی بۆ ئێوە بەڵکو ژیریتان بخەنە کارو بیربکەنەوە');
INSERT INTO "bamoki" VALUES(24,62,'24:62','بەڕاستی ئیمانداران تەنھا ئەوانەن کەبڕوایان بەخواو پێغەمبەرەکەی ھێناوە وە ھەر کاتێک لەگەڵ ئەو (پێغەبمبەر صلى الله عليه وسلم) دابن لەکارێکی گرنگدا کەکۆیان دەکاتەوە (وەک غەزاو جومعە و جەژن) ناڕۆن بە ھیچ لایەکدا ھەتا مۆڵەت لەپێغەمبەر صلى الله عليه وسلم وەرنەگرن بێگومان ئەوانەی مۆڵەتت لێ وەردەرگرن ئەوانە کەسانێکن کەبڕوایان ھەیە بەخواو پێغەمبەرەکەی جا ھەر کاتێک ئەوانە داوای مۆڵەتیان لێ کردیت بۆ ھەندێ ئیش و کاری تایبەتی خۆیان ئەوە ھەر کامیان خۆت ویستت مۆڵەتی بدە وە داوای لێخۆشبوونیان بۆ بکە لەخوا بەڕاستی خوا لێبووردە و میھرەبانە');
INSERT INTO "bamoki" VALUES(24,63,'24:63','بانگ کردنی پێغەمبەر صلى الله عليه وسلم لەناو خۆتاندا دامەنێن (نابێت) وەک بانگ کردنی خۆتان لەیەکتری بێت (دەبێت بەرێزەوە بانگ بکرێت وەک ئەی پێغەمبەر، یارسول اللە صلى الله عليه وسلم) بەڕاستی خوا دەزانێ و ئاگادارە بەوانەی لەئێوە (بەبێ مۆڵەت) لەپەنای خەڵکی ترەوە دەردەچن بەدزیەوە جا بابترسن ئەوانەی کەپێچەوانەی فەرمانی پێغەمبەر صلى الله عليه وسلم دەکەن کە (لەدونیادا) بەڵاو تاقیکردنەوەیەکیان تووش ببێت یا سزایەکی سەختیان تووش ببێت (لە دوا ڕۆژدا)');
INSERT INTO "bamoki" VALUES(24,64,'24:64','بێداربنەوە بەڕاستی تەنھا بۆ خوایە ئەوەی کە لە ئاسمانەکان و زەویدایە بێگومان خوا ئاگادارە بەتەواوی ئەو شتانەی ئێوەی لەسەرن، وە بەو(کارانەیشی کە دەیکەن) ئەو ڕۆژەش کە دەگەڕێنرێنەوە لای خوا ئەوسا ئاگاداریان دەکاتەوە بەو کردەوانەی کردوویانە وە خوا بەھەموو شتێک ئاگادارە');
INSERT INTO "bamoki" VALUES(25,1,'25:1','گەورە و پیرۆزە ئەو زاتەی کەفورقان (قورئان)ی بۆ سەر بەندەی خۆی ناردۆتە خوارەوە تا ترسێنەر بێت بۆ ھەموو جیھانیان');
INSERT INTO "bamoki" VALUES(25,2,'25:2','ئەو زاتەی کەدەسەڵات و موڵکی ئاسمانەکان و زەوی ھی ئەوە و ھیچ منداڵی بۆ خۆی بڕیارنەداوە و کەس ھاوبەشی نیە لە فەرمانڕەوایی و موڵک وداریدا وە ھەموو شتێکی دروستکردووە وە ئەندازەی بۆ دیاری کرد (لە ھەموو ڕوویەکەوە) بەچاکی');
INSERT INTO "bamoki" VALUES(25,3,'25:3','بێجگە لەخوا چەند پەرستراوێکیان بۆ خۆیان بڕیار داوە کەھیچ شتێک دروست ناکەن بەڵکو خۆشیان دروستکراون دەسەڵاتیان نیە بۆ خۆیان نەزیان نەسوود و قازاج (بگەیەنن) ھەروەھا مردن و ژیان و زیندوو بوونەوەی خۆیان بەدەس نیە');
INSERT INTO "bamoki" VALUES(25,4,'25:4','ئەوانەی کەبێ بڕوا بوون دەیانووت بێگومان ئەم (قورئانە) ھیچ نیە جگە لەدرۆیەکی گەورە کە موحەممەد صلى الله عليه وسلم ھەڵی بەستووە وە کەسانی تر بۆ ئەو (ھەڵبەستنە) یارمەتیان داوە ئەوە بەڕاستی ئەوانە ستەم و بوھتانێکی گەورەیان کردوە');
INSERT INTO "bamoki" VALUES(25,5,'25:5','وە(بێ بڕوایان) دەیانووت (ئەم قورئانە) بەسەرھات و داستانی پێشووەکانە (موحەممەد صلى الله عليه وسلم) داوای کردووە بۆی بنوسن ئەمجا دەخوێندرێتەوە بەسەریدا بەبەیانیان و ئێواراندا');
INSERT INTO "bamoki" VALUES(25,6,'25:6','(ئەی موحەممەد صلى الله عليه وسلم) بڵێ زاتێک ناردویەتیە خوارەوە کە ھەموو نھێنی ئاسمانەکان و زەوی دەزانێت بەڕاستی ئەو (خوا) لێبووردە ومیھرەبانە');
INSERT INTO "bamoki" VALUES(25,7,'25:7','(بێ بڕوایان) دەیانووت ئەمە چۆن پێغەمبەرێکە خواردن دەخوات وە بەبازاڕدا دەڕوات بۆچی فریشتەیەک نەنێرراوەتە خوارەوە بۆ لای بۆ ئەوەی لەگەڵیدا ترسێنەر بێت');
INSERT INTO "bamoki" VALUES(25,8,'25:8','یا گەنجینەیەکی (لەئاسمانەوە) بۆ فڕێ بدرایە یا باخێکی ببوایە لێی بخواردایە وە ستەمکاران (بێ بڕوایان) دەیانووت (بەئیمانداران) ئێوە تەنھا شوێن پیاوێکی جادو لێکراو کەوتوون');
INSERT INTO "bamoki" VALUES(25,9,'25:9','سەیرکە چۆن نموونەی (پووچ) بۆ تۆ دەھێننەوە بەو ھۆیەوە گومڕا بوون ئیتر ناتوانن رێگای ھەق و ڕاستی بدۆزنەوە');
INSERT INTO "bamoki" VALUES(25,10,'25:10','گەورە و پیرۆزە ئەو زاتەی ئەگەر بیەوێت (لەدونیادا) چاکتر لەوەی (بێ بڕوایان دەڵێن) بۆت ساز دەدات باخاتێک کە ڕوبارەکان بەژێریاندا دەڕوات وە چەند کۆشکت بۆ فەراھەم دەھێنێت');
INSERT INTO "bamoki" VALUES(25,11,'25:11','(ڕاست ناکەن وانیە) بەڵکو ئەوانە باوەڕیان بەڕۆژی دوایی نیە وە ئێمە ئامادەمان کردووە بۆ کەسێ باوەڕی بەڕۆژی دوایی نەبێت ئاگری ھەڵگیرساو');
INSERT INTO "bamoki" VALUES(25,12,'25:12','کاتێک (ئەم ئاگرە) لەدورەوە ئەوان دەبینێت نرکە وناڵەی گڕ وبڵێسەی دەبیسن (کە نیشانەی ڕق و قینی زۆریەتی لێیان)');
INSERT INTO "bamoki" VALUES(25,13,'25:13','وەکاتێ فڕێ دەدرێنە جێگایەکی تەنگ لەدۆزەخدا بەجوتکراوی دەست و قاچ وملیان لەکۆت و زنجیردا (یان لەشیان بەکۆت و زنجیر پێکەو بەسراوە) لەوێدا داوای تیاچوون و مردن دەکەن');
INSERT INTO "bamoki" VALUES(25,14,'25:14','ئەمڕۆ داوای تەنھا مردنێک مەکەن بەڵکو داوای مردنی زۆر بکەن');
INSERT INTO "bamoki" VALUES(25,15,'25:15','بڵێ ئایا ئەمە چاکترە یا ئەو بەھەشتە ھەمیشەییەی کەبەڵێن دراوە بەپارێزکاران ولەخواترسان کەپاداشت و سەرئەنجام و ئاکامیانە');
INSERT INTO "bamoki" VALUES(25,16,'25:16','لەو (بەھەشتە)دا ھەرچیان بوێ ھەیە بەھەمیشەیی و بێ بڕانەوە ئەوە بەڵێنێکە لەلایەن پەروەردگارتەوە ئەو لێی بەرپرسیارە');
INSERT INTO "bamoki" VALUES(25,17,'25:17','وە لەو ڕۆژەدا خوا ئەو (بت پەرستانە) کۆدەکاتەوە لەگەڵ ئەو پەرستراوانەی کەپەرستوونیان جگە لەخوا ئەمجا خوا (بە پەرستراوەکان) دەڵێ ئایا ئێوە ئەو بەندانەی منتان گومڕا کرد یان خۆیان ڕێگای (ڕاستیان) ونکرد و گومڕا بوون');
INSERT INTO "bamoki" VALUES(25,18,'25:18','(پەرستراوەکان) دەڵێن پاک و بێگەردی بۆ تۆ بۆ ئێمە شایستە نەبووە کەبێجگە لەتۆ پەرستراوێک بۆ خۆمان دابنێین بەڵام نازو نیعمەتت دا بەئەوان و باو و باپیرانیان ھەتا (وایان لێھات) یادی تۆیان لەبیر چویەوە وە ئەوانە کۆمەڵێکی تیاچووی بێ خێر بوون');
INSERT INTO "bamoki" VALUES(25,19,'25:19','(خوا بەبت پەرستەکان دەڵێ) جا بێگومان پەرستراوەکانتان ئێوەیان بەدرۆ خستەوە لەو قسانەی کەدەتان کرد ئەمجا نەدەتوانن (سزای خوا) لەخۆتان لادەن نەدەتوانن یارمەتی (خوا) بەدەس بھێنن وە ھەر کەس لەئێوە ستەم بکات (ھاوەڵ بۆ خوا دابنێت) سزایەکی گەورەی پێ دەچەژین');
INSERT INTO "bamoki" VALUES(25,20,'25:20','ئێمە پێش تۆ ھەرکام لەپێغەمبەرانمان ناردبێت بێگومان ئەوان خواردنیان خواردووە و بە بازاڕدا ڕۆشتوون وە ئێمە ھەندێک لەئێوەمان کردە ھۆی تاقیکردنەوەی ھەندێکی ترتان ئایا خۆ دەگرن وە پەروەردگارت بینایە (بەھەموو شتێک)');
INSERT INTO "bamoki" VALUES(25,21,'25:21','وە ئەوانەی کە بەھیوای دیداری ئێمەنین (لەڕۆژی دواییدا) دەڵێن ئەوە بۆ فریشتە نانێردرێتە خوارەوە بۆ سەرمان یاخود پەروەردگارمان نابینین سوێند بەخوا بەڕاستی ئەوانە لەدەرونی خۆیاندا خۆیان بەگەورە دەزانی وە سەرکەشییەکی گەورەیان کرد');
INSERT INTO "bamoki" VALUES(25,22,'25:22','ئەو رۆژەی فریشتەکان دەبینن (کاتی مردن یاقیامەت) لەو ڕۆژەدا ھیچ مژدەیەک بۆتاوانباران نییە وە دەڵێن (پەنامان بدەن) بەپەنادانی پارێزراو');
INSERT INTO "bamoki" VALUES(25,23,'25:23','ھاتین ھەر کردەوەیەکیان کرد بوو ھەموویمان وەکو تۆزی پرش وبڵاو لێکرد');
INSERT INTO "bamoki" VALUES(25,24,'25:24','بەھەشتیەکان لەو ڕۆژەدا لەچاکترین جێگادان وە لەباشترین شوێنی حەسانەوە (ی نیوەڕوان) دان');
INSERT INTO "bamoki" VALUES(25,25,'25:25','وە ڕۆژێک ئاسمان لەت دەبێت لەگەڵ ھەورێکی سپی (ھەموو ئاسمان دەگرێتەوە) وە فریشتەکان دەنێردرێنە خوارەوە کۆمەڵ کۆمەڵ');
INSERT INTO "bamoki" VALUES(25,26,'25:26','لەو ڕۆژەدا فەرمانڕەوایەتی تەنھا بۆ خوای میھرەبانە ئەو ڕۆژە رۆژێکی زۆر سەختە لەسەر بێ باوەڕان');
INSERT INTO "bamoki" VALUES(25,27,'25:27','وە ڕۆژێکە کەستەمکار قەپ دەکات بەھەر دوو دەستی خۆیدا و دەڵێ خۆزگە لەگەڵ پێغەمبەردا ڕێگای ڕاستم بگرتایەتەبەر');
INSERT INTO "bamoki" VALUES(25,28,'25:28','ئای تیاچوون بۆ من خۆزگە فڵان کەسم نەکردایە بەھاوەڵ و خۆشەویستی خۆم');
INSERT INTO "bamoki" VALUES(25,29,'25:29','بەڕاستی ئەو (کەسەی کردم بەھاوەڵ) منی لەقورئان وێڵ کرد دوا ئەوەی قورئانم پێ گەیشت (و باوەڕم پێ ھێنا) وە شەیتان ھەمیشە پشت تێکەرو پشت بەردەرە بۆ ئادەمیزاد');
INSERT INTO "bamoki" VALUES(25,30,'25:30','وە پێغەمبەر(صلى الله عليه وسلم) ووتی ئەی پەروەردگارم بەڕاستی قەومەکەم (قورەیش) ئەم قورئانەیان واز لێھێناوە و گوێی پێ نادەن');
INSERT INTO "bamoki" VALUES(25,31,'25:31','ئا بەو جۆرە (موحەممەد صلى الله عليه وسلم کەدژایەتی تۆ دەکرێت) دامان ناوە بۆ ھەموو پێغەمبەرێک دوژمنێک لەتاوانباران، بەسە پەروەردگارت ڕێنمونیکەر و یارمەتیدەرت بێت');
INSERT INTO "bamoki" VALUES(25,32,'25:32','وە ئەوانەی بێ بڕوا بوون دەیانووت بۆچی (موحەممەد صلى الله عليه وسلم) ھەموو قورئانی بەیەک جار بۆ دانەبەزێنراوە ئا بەوجۆرە (دامانبەزاندووە) بۆ ئەوەی دڵی تۆ (ئەی موحەممەد صلى الله عليه وسلم) بەو قورئانە دامەزراو و بەھێز بکەین وە (بەو بۆنەیەوە) ئایەتەکانی خۆمم بۆ ناردن بەرەبەرە');
INSERT INTO "bamoki" VALUES(25,33,'25:33','ھەر نموونە و مەسەلێکت بۆ بھێنن (لە ڕەخنە و پێشنیارە نابەجێکانیان)ئێمە وەڵامەکەیمان بۆ تۆ ھێناوە بەڕاستی و جوانترین ڕوونکردنەوە');
INSERT INTO "bamoki" VALUES(25,34,'25:34','ئەو کەسانەی کەبەسەر ڕوخساریاندا دەبرێن (ڕادەکێشرێن) بۆ دۆزەخ ئەوانە خراپترین جێگایان بۆ ھەیە وە ڕێگاشیان وونکردووە (گومڕاترن)');
INSERT INTO "bamoki" VALUES(25,35,'25:35','سوێند بەخوا بەڕاستی تەوراتمان دا بەموسا وە ھارونی برایشیمان کردە یاریدەدەری');
INSERT INTO "bamoki" VALUES(25,36,'25:36','ئەمجا ووتمان ھەردووکتان بڕۆن بۆ سەر ئەو گەلەی کەباوەڕیان بەئایەتەکانی ئێمە نیە و بە درۆی دەزانن ئەوسا لەناومان بردن بە لەناوبردنێکی زۆر خراپ');
INSERT INTO "bamoki" VALUES(25,37,'25:37','گەلی نوحیش کاتێک پێغەمبەرانیان بە درۆزانی (لەئاوی لافاودا) نوقممان کردن وە کردنمانن بەپەندو عیبرەت بۆ خەڵک وە ئامادەمان کردووە بۆ ستەمکاران سزای سەخت');
INSERT INTO "bamoki" VALUES(25,38,'25:38','و گەلی عاد و سەمود و خاوەنی چاڵ و بیرەکان وەزۆر گەلی تر کە لەنێوانیاندا بوون (لەناومان بردن)');
INSERT INTO "bamoki" VALUES(25,39,'25:39','وەبۆ ھەموویان چەندین نموونەمان ھێنایەوە (بەڵام بیریان نەکردەوە) وە ھەموویانمان لەناوبرد بە لەناوبردنێکی خراپ');
INSERT INTO "bamoki" VALUES(25,40,'25:40','سوێند بەخوا بەڕاستی ئەوان (قوڕەیش) تێپەڕبوون بە ئەو شارەدا (سەدوم) کەبارانی شەڕ (بەرد)ی بەسەردا بارێنرابوو ئایا ئەوان ئەو (شارە بەردباران کراوە)یان نەبینیوە (وانیە بینیوویانە) بەڵکو ئەوان بەتەمای زیندوو بوونەوە نین');
INSERT INTO "bamoki" VALUES(25,41,'25:41','وە (ئەی موحەممەد صلى الله عليه وسلم) کاتێک تۆ ببینن بەس گاڵتەت پێدەکەن و (دەڵێن) ئائەمەیە خوا ناردویەتی بەپێغەمبەر');
INSERT INTO "bamoki" VALUES(25,42,'25:42','(وەدەڵێن) نزیک بوو لە پەرستراوەکانمان لامان بدات ئەگەر لەسەر (پەرستنی) یان خۆگر نەبووینایە لە داھاتوودا دەزانن کاتێک سزا (ی خوا) دەبیننکێ (گومڕا ترە) و ڕێگای وونکردووە');
INSERT INTO "bamoki" VALUES(25,43,'25:43','ئایا دیت ئەو کەسەی ھەواو ئارەزووی خۆی کردۆتە پەرستراوی خۆی ئایا تۆ ئەبیتە پارێزەر بۆ ئەو (کەشوێنی ئارەزوی نەکەوێت)؟');
INSERT INTO "bamoki" VALUES(25,44,'25:44','ئایا وا دەزانیت زۆربەی ئەوانە دەبیستن یان تێدەگەن ئەوانە ھەر وەک ئاژەڵن بەڵکو ئەوان گومڕا تریشن');
INSERT INTO "bamoki" VALUES(25,45,'25:45','ئایا نابینی پەروەردگارت چۆن سێبەری درێژ کردوەتەوە (لەبانگی بەیانیە ھەتا خۆر ھەڵاتن) ئەگەر بیویستایە ڕایدەگرت بەوەستاوی (سێبەرەکە بەردەوام دەبوو) لەپاشان خۆرمان کردە بەڵگە لەسەر ئەو (سێبەرە چونکە ئەگەر خۆر نەبێت سێبەر نا ناسرێت)');
INSERT INTO "bamoki" VALUES(25,46,'25:46','پاشان ئەو سێبەرە بۆلای خۆمان دەبەینەوە بەشێنەیی و کەمە کەمە نایھێڵین');
INSERT INTO "bamoki" VALUES(25,47,'25:47','ئەو (خوا) زاتێکە کەشەوی بۆ کردون بەداپۆشەر (پۆشاک) وە خەوتنی بۆ کردوون بەھۆی مت بوون وە ڕۆژی بۆ ھەستان (لە خەو بۆ کارو ڕزق وڕۆزی پەیدا کردن) داناوە');
INSERT INTO "bamoki" VALUES(25,48,'25:48','وە ئەو (خوا) زاتێکە باکان دەنێرێت بەمژدەدەر پێش ڕەحمەتی خۆی (باران بارین) وە لەئاسمانەوە باراندومانە ئاوێکی پاکۆکەر (پاکوخاوێن)');
INSERT INTO "bamoki" VALUES(25,49,'25:49','تا زەوی مردووی پێ ببوژێنینەوە وە ئاوی دەدەن بەو (بارانە) لەوانەی دروستمان کردوون ئاژەڵ و خەڵکانێکی زۆر');
INSERT INTO "bamoki" VALUES(25,50,'25:50','سوێند بەخوا بەڕاستی ئێمە بەچەند جۆر ئەم (ئیشانە)مان گۆڕی لەنێوانیاندا تا بیربکەنەوە بەڵام زۆربەی خەڵک جگە لە سپڵەیی (بێ باوەڕی) ھیچیان ناوێت');
INSERT INTO "bamoki" VALUES(25,51,'25:51','ئەگەر بمان ویستایە بێگومان دەمان نارد لەھەموو شارو شارۆچکەیەکدا ترسێنەرێک');
INSERT INTO "bamoki" VALUES(25,52,'25:52','کەواتە تۆ بەگوێی بێباوەڕان مەکە بە(بەڵگەکانی) ئەم قورئانە بەربەرەکانی یەکی گەورەیان لەگەڵدا بکە');
INSERT INTO "bamoki" VALUES(25,53,'25:53','ئەو (خوا) زاتێکە دوو دەریا (ئاو) ی بەڕەڵا کردووە لەپاڵ یەکدا ئەمەیان شیرین و سازگارە (ئاوی ڕوبارو کانیاوە) ئەوی تریان زۆر سوێرو تاڵ و تفتە (ئاوی دەریاکانە) وە لەنێوان ئەو دوو (ئاوە) دا بەربەستێکی داناوە وڕێگریکەرێکی ئەستور (تا ئەو دوو ئاوە تێکەڵ بەیەک نەبن)');
INSERT INTO "bamoki" VALUES(25,54,'25:54','ئەو (خوا) زاتێکە لەئاوی (مرۆڤ) ئادەمیزادی دروستکردووە ئەمجا ئەو (ئاوە)ی کردۆتە ھۆی ڕەچەڵەک (پشت کە بەرێگەی نێرینە دەگەن بەمرۆڤ وەک کوڕ) و خزم (کە بەرێگەی ئافرەت دەگەنەوە بەمرۆڤ وەک کچە زاو خوشکەزا) وە پەروەردگارت ھەمیشە بە دەسەڵات و توانایە');
INSERT INTO "bamoki" VALUES(25,55,'25:55','ئەوانە جگە لەخوا شتانێک دەپەرستن کە نەسوود نەزیان بەوان ناگەیەنن وە بێ بڕوایان بەردەوام دژی پەروەردگاریان پشتیوانی یەکترن (بۆ تاوان و خراپە)');
INSERT INTO "bamoki" VALUES(25,56,'25:56','ئێمە تۆمان تەنھا بۆ ئەوە ناردووە کەمژدە دەرو ترسێنەر بیت');
INSERT INTO "bamoki" VALUES(25,57,'25:57','(ئەی موحەممەد صلى الله عليه وسلم) بڵێ من لەسەر گەیاندنی ئیسلام ھیچ کرێ و پاداشتێک لەئێوە داوا ناکەم بەڵام ھەر کەس ویستی رێگەی پەروردگاری بگرێت (با بیگرێ)');
INSERT INTO "bamoki" VALUES(25,58,'25:58','پشت ببەستە بە (خوای) زیندوو کەزاتێکە ھەرگیز نامرێت، تەسبیحات بکە بەستایشی ئەو بەسە (بۆ تۆ) کەخوا ئاگادارە بەگوناھی بەندەکانی');
INSERT INTO "bamoki" VALUES(25,59,'25:59','ئەو (خوایە)ی کەئاسمانەکان و زەوی دروستکردووە وەئەوەیشی لەنێوانیاندایە لەشەش ڕۆژدا لەپاشانیش بەرز بوویەوە بۆ سەر عەرش و قەراری گرت (باوەڕمان پێیەتی بەبێ ئەوەی شێوەکەی بزانین) ئەو خوای میھرەبانە کەواتە پرسیار لەزاتێک بکە کە بەھەموو شت زاناو ئاگادارە');
INSERT INTO "bamoki" VALUES(25,60,'25:60','وە کاتێک بەوان بوترێت سوژدە ببەن بۆخوای میھرەبان دەڵێن ڕەحمان کێیە (ئێمە نایناسین) ئایا سوژدە ببەین بۆ ئەو (زاتەی) کەتۆ فەرمانمان پێ دەدەیت وە (بەم داوای سوژدە بردنە) دورکەوتنەوەیان (لەڕێگای ڕاست) زیاتر دەبێت');
INSERT INTO "bamoki" VALUES(25,61,'25:61','پاک و بێگەرد وپیرۆزە ئەو (خوایەی) کە لەئاسماندا چەند خولگە (بورج)ی دروستکردووە ودایناوە لەئاسماندا خۆرێکی پڕشنگدار و مانگێکی ڕووناک کەرەوە (بۆ زەوی)');
INSERT INTO "bamoki" VALUES(25,62,'25:62','وە ئەو (خوایە) زاتێکە کەشەوو ڕۆژی لەجێی یەکتر داناوە (بەدوای یەکتردا دێن و شوێنی یەکتر دەگرنەوە) بۆ ئەو کەسەی بیەوێت بیربکاتەوە (لەدروسکراوەکانی خوا) یان بیەوێت شوکرانە بژێری خوا بکات');
INSERT INTO "bamoki" VALUES(25,63,'25:63','بەندە تایبەتیەکانی خوای میھرەبان کەسانێکن بە ویقارو نەرمی دەڕۆن بەسەر زەویدا (بێ لوت بەرزی و کەشخە و فیز کردن) وەکاتێک نەزان و نەفامەکان (بەووتەی ناشیرن) لەگەڵیان بدوێن ووتەیەک دەڵێن: جوان بێت (وەک: سڵاوتان لێ بێت)');
INSERT INTO "bamoki" VALUES(25,64,'25:64','وە کەسانێکن لەشەودا بۆ پەروەردگاریان سوژدە بەرن و شەو نوێژ دەکەن');
INSERT INTO "bamoki" VALUES(25,65,'25:65','وە کەسانێکن کەدەڵێن ئەی پەروەردگارمان سزای دۆزەخمان لێ لادە بەڕاستی سزای ئەو (دۆزەخە) لانەچووی بەردەوامە');
INSERT INTO "bamoki" VALUES(25,66,'25:66','بەڕاستی دۆزەخ جێگەو شوێن مانەوەیەکی زۆر خراپە');
INSERT INTO "bamoki" VALUES(25,67,'25:67','وە کەسانێکن کاتێک ماڵ و سامان خەرج دەکەن (بۆ خۆیان و خێزانیان)نەزیادە ڕۆی و نەڕەزیلی و دەست نوقاوی (تێدا) ناکەن (بەڵکو) مام ناوەندیە لەونێوانەدا');
INSERT INTO "bamoki" VALUES(25,68,'25:68','وەکەسانێکن کەلەگەڵ خوادا ناپەرستن ھیچ پەرستراوێکی تر و ناکوژن ھیچ کەسێک کەخوا کوشتنی ئەوی یاساغ و(حەرام) کردووە بەھەق و ڕەوا نەبێت وە زیناو داوێن پیسی ناکەن وە ھەر کەس ئەو تاوانانە بکات (کەباسکران) سزاکەی وەردەگرێت');
INSERT INTO "bamoki" VALUES(25,69,'25:69','سزای بۆ چەند بارە دەکرێتەوە لەڕۆژی قیامەتدا وە بێ بڕانەوە وبە بێ ڕێزو ڕیسوایی لەدۆزەخ دا دەمێنێتەوە');
INSERT INTO "bamoki" VALUES(25,70,'25:70','بێجگە لەکەسێ تەوبەی کردبێ و باوەڕی ھێنابێت کارو کردەوەی چاکیشی کردبێت ئا ئەوانە خوا خراپە و تاوانەکانیان دەگۆڕێت بەچاکە وەخوا ھەمیشە لێبووردەی بەبەزەییە');
INSERT INTO "bamoki" VALUES(25,71,'25:71','وە ھەر کەس تەوبەی کردو کردەوەی چاکی کرد ئەوە بەڕاستی ئەو کەسە دەگەڕێتەوە بۆ لای خوا بە گەڕانەوەیەکی تەواو');
INSERT INTO "bamoki" VALUES(25,72,'25:72','وە (بەندەکانی خوا) کەسانێکن کەشایەتی درۆ نادەن و کاتێ تێپەڕ بن بەلای شتی بێ سووددا (ئەنجام بدرێت لەلایەن نەفامانەوە) تێپەڕ دەکەن بەڕێزەوە (پێ گوێدانە نەفامان)');
INSERT INTO "bamoki" VALUES(25,73,'25:73','وە کەسانێکن کاتێک ئامۆژگاری بکرێن بەئایەتەکانی پەروەردگاریان ناکەون بەسەریاندا بە کەڕی و کوێری');
INSERT INTO "bamoki" VALUES(25,74,'25:74','وە کەسانێکن کەدەڵێن ئەی پەروەردگارمان ببەخشە پێمان لە ھاوسەران و نەوەکانمان (منداڵەکانمان) ئەوەی چاوانی پێ گەش بێ و ھۆی دڵخۆشی بێتوە بمانکە بە پێشەوای پارێزکاران و لەخوا ترسان');
INSERT INTO "bamoki" VALUES(25,75,'25:75','ئەوانە (کە ئەو سیفەتانەیان تیدایە) پاداشت دەدرێنەوە بەپلە و کۆشکی بەرز لە بەھەشتدا بەھۆی ئارام گرتنیان وە لەو شوێنەدا سڵاویان لێ دەکرێ (لەلایەن فریشتەکانەوە زۆر بەگەرمی)');
INSERT INTO "bamoki" VALUES(25,76,'25:76','بێ بڕانەوە تیایدا دەمێننەوە چەن جێگا وشوێن ومانەوەیەکی جوان و خۆشە');
INSERT INTO "bamoki" VALUES(25,77,'25:77','(ئەی موحەممەد صلى الله عليه وسلم) بڵێ خەڵکینە پەروەردگار گوێتان پێ نادات و ھیچ بایەخێکتان نیە لای ئەگەر دوعا و نزاتان نەبێت (لەکاتی تەنگی و ناڕەحەتیدا) کەچی (زۆربەتان) ئاینی خواتان بەدرۆ دەزانی (بڕواتان نەھێنا) جا دڵنیابن تۆڵەکەی یەخەتان دەگرێ و لێتان جیا نابێتەوە');
INSERT INTO "bamoki" VALUES(26,1,'26:1','بە(طا،سین، میم) دەخوێنرێتەوە خوازاناترە بە ماناکەی بۆ زانیاری زیاتر سەیری سەرتای سورەتی (البقرة) بکە');
INSERT INTO "bamoki" VALUES(26,2,'26:2','ئەم (سورەتە) لەئایەتەکانی قورئانی ڕوونکەرەوەیە');
INSERT INTO "bamoki" VALUES(26,3,'26:3','خەریکە خۆت لەناو دەبەی (دەکوژی) بەخەفەت لەبەر ئەوەی ئەوان (خەڵکی مەککە) باوەڕ ناھێنن');
INSERT INTO "bamoki" VALUES(26,4,'26:4','ئەگەر بمانەوێت دەھێنینە خوارەوە بۆیان نیشانەو بەڵگەیەک لەئاسمانەوە کەبەردەوام گەردنیانی بۆ دانەوێنن (ناچاریان بکات باوەڕ بھێنن)');
INSERT INTO "bamoki" VALUES(26,5,'26:5','ھەر بێت بۆیان پەیامێکی تازە دابەزێنراو (کەقورئانە) لەخوای میھرەبانەوە ئەوان بەردەوام پشت تێکەرو ڕوو وەڕگێڕن لێی');
INSERT INTO "bamoki" VALUES(26,6,'26:6','جا بێگومان ئەوانە بەدرۆیان زانی و باوەڕیان پێ نەکرد (ئەوەی ناردبومان) جا بەزوویی بۆیان دێت ھەواڵەکانی ئەوەی ئەوان گاڵتەو لاقرتیان پێ دەکرد');
INSERT INTO "bamoki" VALUES(26,7,'26:7','ئایا سەیری زەوی ناکەن چەندمان ڕواندووە تیایدا لەھەموو جۆرێکی چاکی بەکەڵک');
INSERT INTO "bamoki" VALUES(26,8,'26:8','بەڕاستی لەڕواندنی ئەو ھەموو ڕوواوەدا بەڵگەی گەورە ھەیە بەڵام زۆر بەیان بڕوادارنین و باوەڕ ناھێنن');
INSERT INTO "bamoki" VALUES(26,9,'26:9','بێگومان پەروەردگارت تەنھا ئەو خاوەن دەسەڵات و بە بەزەییە');
INSERT INTO "bamoki" VALUES(26,10,'26:10','(باس بکە) ئەو کاتەی پەروەردگارت بانگی موسای کرد کە (بەپێغەمبەری) بڕۆ بۆ لای ئەو گەلە ستەمکارە');
INSERT INTO "bamoki" VALUES(26,11,'26:11','کە گەلەکەی فیرعەونە ئایا ئەوان ناترسن (بێ فەرمانی خوادەکەن)');
INSERT INTO "bamoki" VALUES(26,12,'26:12','(موسا) ووتی ئەی پەروەردگارم بەڕاستی من دەترسم بڕوام پێ نەکەن');
INSERT INTO "bamoki" VALUES(26,13,'26:13','وەسینگم (دڵم) تەنگ ببێ وە(دەترسم) بەچاکی قسەم بۆ نەکرێت و زمانم بەر نەبێت (لەو گرێیەی ھەیەتی) کەواتە (خوایە جوبرەیل) بنێرە بۆ لای ھاروون (و بیکە بەپێغەمبەر بۆ ئەوەی یارمەتیم بدات)');
INSERT INTO "bamoki" VALUES(26,14,'26:14','وەئەوان (گەلی فیرعەون) تۆڵەی تاوانێکیان لەسەرمە بۆیە دەترسم کەبمکوژن (لەجیاتی ئەو کوژراوە)');
INSERT INTO "bamoki" VALUES(26,15,'26:15','(خوا) فەرمووی نەخێر (ئەوەی باست کرد ھیچی روونادات) کەواتە ھەردووکتان بەموعجیزەکانمانەوە بڕۆن (بۆ لای فیرعەون و گەلەکەی) ئێمە لەگەڵتان گوێ دەگرین');
INSERT INTO "bamoki" VALUES(26,16,'26:16','ئەوسا برۆنە لای فیرعەون و ھەردووکتان بڵێن بەڕاستی ئێمە پێغەمبەری پەروەردگاری جیھانیانین');
INSERT INTO "bamoki" VALUES(26,17,'26:17','(نێراوین) کەتۆ بەنو ئیسرائیل (لەژێر دەستەیی خۆت) بەڕەڵا کەیت لەگەڵ ئێمەدا (بڕۆین بۆ شام)');
INSERT INTO "bamoki" VALUES(26,18,'26:18','(فیرعەون) ووتی ئایا ئێمە پێمان نەگەیاندی لەناو (ماڵی) خۆماندا بەمنداڵی وە تۆ چەند ساڵێک لەتەمەنت لەناو ئێمەدا نەمایتەوە');
INSERT INTO "bamoki" VALUES(26,19,'26:19','وە ئەو کردەوە (خراپە)شت کرد کەکردت (کەکوشتنی قیبتیەک بوو) وە تۆ لەوانەی سپڵە و ناشوکرن');
INSERT INTO "bamoki" VALUES(26,20,'26:20','(موسا) ووتی ئەوکارەم کرد (ئەو پیاوەم کوشت) کاتێک کەمن لەڕیزی نەزاناندا بووم');
INSERT INTO "bamoki" VALUES(26,21,'26:21','ئەمجا رام کرد و ھەڵھاتم لەئێوە کاتێک ترسام لێتان (تۆڵەم لێ بسێنن) ئەوسا پەروەردگارم حیکمەتی (پێغەمبەریەتی) پێ بەخشیم منی خستە ریزی پێغەمبەرانەوە (کردمی بەپێغەمبەر)');
INSERT INTO "bamoki" VALUES(26,22,'26:22','وە ئەو (بەخێو کردنە) کە تۆ دەیکەیت بەمنەت بەسەرمەوە بەھۆی ئەوەوە بووە کەتۆ نەوەی ئیسرائیلت کردبووە کۆیلە و ژێردەستەی خۆت (لەترسا دایکی موسا موسای بەخێو نەکرد)');
INSERT INTO "bamoki" VALUES(26,23,'26:23','(ئەمجا) فیرعەون ووتی پەروەردگاری جیھانیان کێیە');
INSERT INTO "bamoki" VALUES(26,24,'26:24','(موسا) ووتی پەروەردگاری ئاسمانەکان وزەوی و ھەرچی لەنێوانیاندایە ئەگەر ئێوە دڵنیان');
INSERT INTO "bamoki" VALUES(26,25,'26:25','(فیرعەون) ووتی بەوانەی بەدەوری دابوون (لەشوێنکەوتوانی) ئایا گوێتان لێیە (چی دەڵێت)');
INSERT INTO "bamoki" VALUES(26,26,'26:26','(موسا) ووتی پەرەوەردگاری ئێوەو پەروەردگاری باوک وباپیرە پێشووەکانتانە');
INSERT INTO "bamoki" VALUES(26,27,'26:27','(فیرعەون) ووتی بەڕاستی ئەو پێغەمبەرە کەنێرراوە بۆ ئێوە شێـتە');
INSERT INTO "bamoki" VALUES(26,28,'26:28','(موسا) ووتی پەروەردگاری رۆژھەڵات و ڕۆژ ئاوایە و ئەوەی لەنێوانیادایە ئەگەر ئێوە بیربکەنەوە وژیربن');
INSERT INTO "bamoki" VALUES(26,29,'26:29','(فیرعەون) ووتی سوێند بێت ئەگەر پەرستراوێکی تر جگە لەمن دابنێیت بێگومان دەتخەمە ریزی بەندکراوەکانەوە و زیندانیت دەکەم');
INSERT INTO "bamoki" VALUES(26,30,'26:30','(موسا) ووتی ئایا ھەر زیندانیم دەکەیت ئەگەر شتێکی (موعجیزەیەکی) ڕوون و ئاشکرات بۆ بھێنم (کەڕاستە پێغەمبەرم)');
INSERT INTO "bamoki" VALUES(26,31,'26:31','(فیرعەون) ووتی دەی ئەو شتە بھێنە (بینوێنە) ئەگەر تۆ لەڕاستگۆیانی');
INSERT INTO "bamoki" VALUES(26,32,'26:32','ئەمجا موسا گۆچانەکەی فڕێدا دەست بەجێ بوو بەئەژدیھایەکی دیارو ئاشکرا');
INSERT INTO "bamoki" VALUES(26,33,'26:33','وە دەستی دەرھێنا (لەبن باڵیدا) یەکسەر دەستی سپی وپرشنگدار بوو بۆ بینەران');
INSERT INTO "bamoki" VALUES(26,34,'26:34','(فیرعەون) ووتی بەو پیاوە گەورانەی لەدەورەی بوون بێگومان ئەمە جادوگەرێکی زۆر زانایە');
INSERT INTO "bamoki" VALUES(26,35,'26:35','دەیەوێت دەرتان بکات لەووڵات و خاکی خۆتان بەجادوەکەی جا فەرمانی چی دەدەن (لەدژی بیکەم)');
INSERT INTO "bamoki" VALUES(26,36,'26:36','ووتیان جارێ موسا و براکەی واز لێ بھێنە (دوایانخە) سەربازەکانت کۆ بکەرەوە وبیانێرە بۆ ھەموو شارەکان');
INSERT INTO "bamoki" VALUES(26,37,'26:37','تاھەموو جادوگەرێکی زۆر زانات بۆ بھێنن');
INSERT INTO "bamoki" VALUES(26,38,'26:38','ئەمجا ئەو جادوگەرانە کۆکرانەوە لەکات و ڕۆژی دیاریکراودا');
INSERT INTO "bamoki" VALUES(26,39,'26:39','و بەخەڵکیش وترا ئێوەش کۆدەبنەوە؟');
INSERT INTO "bamoki" VALUES(26,40,'26:40','تا شوێن جادوگەران بکەوین ئەگەر ئەوان زاڵ و سەرکەوتوو بوون');
INSERT INTO "bamoki" VALUES(26,41,'26:41','ئەمجا کاتێک جادوگەرەکان ھاتن ووتیان بەفیرعەون ئایا بەڕاستی پاداشتمان ھەیە ئەگەر ئێمە زاڵ و سەرکەوتوو بین');
INSERT INTO "bamoki" VALUES(26,42,'26:42','(فیرعەون) ووتی بەڵێ وەبێگومان ئێوە ئەو کاتە لەنزیکانی خۆم دەبن');
INSERT INTO "bamoki" VALUES(26,43,'26:43','موسا بەساحیرەکانی ووت بیھاوێژن ئەوەی ئێوە ھاوێژەریانن (لەجادو)');
INSERT INTO "bamoki" VALUES(26,44,'26:44','ئەوانیش گوریس و حەبل و دار دەستەکانیان فڕێدا وە ووتیان سوێند بەگەورەیی فیرعەون بەڕاستی ھەر ئێمە زاڵ و سەرکەتووین');
INSERT INTO "bamoki" VALUES(26,45,'26:45','ئەمجا موسا داردەستەکەی فرێدا (و بوو بەمارێکی گەورە) جا کوت و پڕ ئەو (مارە گەورە) ھەرچی جادویان کردبوو ھەڵی لوشی');
INSERT INTO "bamoki" VALUES(26,46,'26:46','ئەمجا جادوگەرەکان خران (کەوتن) بەڕوودا بەسوژدە بردنەوە (بۆ خوا)');
INSERT INTO "bamoki" VALUES(26,47,'26:47','ووتیان ئەوا باوەڕمان ھێنا بەپەروەردگاری جیھانیان');
INSERT INTO "bamoki" VALUES(26,48,'26:48','کەپەروەردگاری موسا و ھارونە');
INSERT INTO "bamoki" VALUES(26,49,'26:49','(فیرعەون) ووتی ئایا باوەڕتان پێ ھێنا پێش ئەوەی مۆڵەتتان بدەم بەڕاستی ئەو (موسا) جادوگەرە گەورەکەتانە کە فێری جادوی کردون جا بێگومان لەمەودوا دەزانن (چیتان لێ دەکەم) بەڕاستی دەست و قاچتان ڕاست و چەپ دەبڕم وە بێگومان ھەمووتان ھەڵ دەواسم (بەدارا)');
INSERT INTO "bamoki" VALUES(26,50,'26:50','(جادوگەرەکان) ووتیان ھیچ زیانی نیە (بۆ ئێمە ئەوەی تۆ ئەتەوێ بیکەی) (چونکە) بەڕاستی ئێمە گەڕانەوەمان بۆ لای پەروەردگارمانە');
INSERT INTO "bamoki" VALUES(26,51,'26:51','بەڕاستی ئێمە بەتەماین و ئومێد دەکەین کەپەروەردگارمان لە گوناھو تاوانەکانمان ببورێت چونکە ئێمە یەکەمی باوەڕداران بووین (لەگەلەکەماندا)');
INSERT INTO "bamoki" VALUES(26,52,'26:52','وەحیمان نارد بۆ موسا کە بەشەو بەندە (باوەڕدارە)کانم ببە (و میصر بەجێ بھێڵن) بێگومان (فیرعەون و کۆمەڵەکەی) دواتان دەکەون');
INSERT INTO "bamoki" VALUES(26,53,'26:53','ئەوسا فیرعەون پیاوانی کۆ کەرەوەی نارد بەشارەکاندا (بۆکۆکردنەوەی لەشکرو ھێز)');
INSERT INTO "bamoki" VALUES(26,54,'26:54','(فیرعەون ووتی) بەڕاستی ئەوانە (موسا و شوێنکەوتوانی) کۆمەڵێکی کەمی بێ نرخن');
INSERT INTO "bamoki" VALUES(26,55,'26:55','وە بەڕاستی ئەوانە ڕقی ئێمە ھەڵدەسێنن (بەووتارو کرداریان)');
INSERT INTO "bamoki" VALUES(26,56,'26:56','وە بێگومان ئێمە کۆمەڵێکین بێدار و ئاگادارین (بەپیلانەکانیان)');
INSERT INTO "bamoki" VALUES(26,57,'26:57','ئەمجا ئەوان (فیرعەون و کۆمەڵەکەی)مان دەرکرد لە باخات و کانیاوەکان');
INSERT INTO "bamoki" VALUES(26,58,'26:58','وە (دەرمان کردن) لەو ھەموو گەنج و خەزێنە و کۆشکە زۆر جوان و چاکانە');
INSERT INTO "bamoki" VALUES(26,59,'26:59','ئا بەو جۆرە (لەناومان بردن) نەوەکانی یەعقوب (جولەکە)مان لەجێگایان دانا');
INSERT INTO "bamoki" VALUES(26,60,'26:60','ئەمجا (فیرعەون و لەشکرەکەی) کاتێک خۆر ھەڵھاتن شوێنیان کەوتن');
INSERT INTO "bamoki" VALUES(26,61,'26:61','ئەمجا کاتێک دوو کۆمەڵەکە چاویان بەیەکتر کەوت ھاوەڵەکانی موسا ووتیان بێگومان ئێمە گیراوین، (پێمان دەگەن)');
INSERT INTO "bamoki" VALUES(26,62,'26:62','(موسا) ووتی نەخێر پێتان ناگەن بێگومان من پەروردگارم لەگەڵە دڵنیابن ڕێگای (ڕزگار بوونم) نیشان دەدات');
INSERT INTO "bamoki" VALUES(26,63,'26:63','ئەمجا سرووشمان کرد بۆ موسا کە بە گۆچانەکەت بدە لەدەریاکە (ئەویش لێیداو) جا دەریاکە لەت بوو کە ھەر لەتێکی وەک شاخێکی زۆر گەورە وابوو');
INSERT INTO "bamoki" VALUES(26,64,'26:64','وە ئەوانی تر (فیرعەون و لەشکرەکەی)مان نزیک کردەوە لەوێ (لەدەریاکە)');
INSERT INTO "bamoki" VALUES(26,65,'26:65','جا موساو ھەموو ئەوانەی لەگەڵی بوون رزگارمان کردن');
INSERT INTO "bamoki" VALUES(26,66,'26:66','پاشان ئەوانی ترمان نوقم کرد (لەدەریادا)');
INSERT INTO "bamoki" VALUES(26,67,'26:67','بەڕاستی لەڕواندنی ئەو ھەموو ڕوواوەدا بەڵگەی گەورە ھەیە بەڵام زۆر بەیان بڕوادارنین و باوەڕ ناھێنن');
INSERT INTO "bamoki" VALUES(26,68,'26:68','بێگومان پەروەردگارت تەنھا ئەو خاوەن دەسەڵات و بە بەزەییە');
INSERT INTO "bamoki" VALUES(26,69,'26:69','(ئەی موحەممەد صلى الله عليه وسلم) بخوێنەرەوە بەسەریاندا ھەواڵی ئیبراھیم');
INSERT INTO "bamoki" VALUES(26,70,'26:70','کاتێک ووتی بەباوکی و گەلەکەی چی دەپەرستن');
