INSERT INTO "bamoki" VALUES(26,74,'26:74','ووتیان (نەخێر ھیچیان پێ ناکرێت) بەڵکو ئێمە باوو باپیرانمان دیوە ئابەو شێوەی (ئێمە)یان کردووە');
INSERT INTO "bamoki" VALUES(26,75,'26:75','ووتی ئایا ئێوە دەزانن چی دەپەرستن');
INSERT INTO "bamoki" VALUES(26,76,'26:76','ئێوە و باوو باپیرە پێشووەکانتان');
INSERT INTO "bamoki" VALUES(26,77,'26:77','جا بەڕاستی ھەموو ئەوانە دوژمنی منن جگە لەپەروەردگاری جیھانیان');
INSERT INTO "bamoki" VALUES(26,78,'26:78','کە ئەو منی دروستکردووە پاشان ھەر ئەویش ڕێنموونیم دەکات');
INSERT INTO "bamoki" VALUES(26,79,'26:79','وە ھەر ئەو زاتە خۆراکم پێدەدات و تێراوم دەکات');
INSERT INTO "bamoki" VALUES(26,80,'26:80','وە کاتێک نەخۆش بکەوم ھەر ئەو شیفام دەدات (چاکم دەکاتەوە)');
INSERT INTO "bamoki" VALUES(26,81,'26:81','وە زاتێکە کەمن دەمرێنێ و پاشان زیندووم دەکاتەوە');
INSERT INTO "bamoki" VALUES(26,82,'26:82','وە (پەروەردگارم) زاتێکە بەتەمام و ئومێد دەکەم لە ڕۆژی پاداشتدا (لە قیامەتدا) ببورێت لەھەڵەکانم و بیانسڕێتەوە');
