INSERT INTO "bamoki" VALUES(26,90,'26:90','وە (لەڕۆژی دواییدا) بەھەشت نزیک دەخرێتەوە بۆ پارێزکاران و لەخوا ترسان');
INSERT INTO "bamoki" VALUES(26,91,'26:91','دۆزەخیش دەردەخرێت (و ئاشکرا دەکرێت) بۆ گومڕایان');
