INSERT INTO "bamoki" VALUES(26,100,'26:100','وەھیچ تکاکارێ بۆ ئێمە نیە');
INSERT INTO "bamoki" VALUES(26,101,'26:101','وە ھیچ ھاورێیەکی دڵسۆزمان نیە');
INSERT INTO "bamoki" VALUES(26,102,'26:102','خۆزگە جارێکی تر بگەڕاینایەتەوە (بۆ دونیا) ئەوسا لەباوەڕداران دەبووین');
INSERT INTO "bamoki" VALUES(26,103,'26:103','بەڕاستی لەڕواندنی ئەو ھەموو ڕوواوەدا بەڵگەی گەورە ھەیە بەڵام زۆر بەیان بڕوادارنین و باوەڕ ناھێنن');
INSERT INTO "bamoki" VALUES(26,104,'26:104','بێگومان پەروەردگارت تەنھا ئەو خاوەن دەسەڵات و بە بەزەییە');
INSERT INTO "bamoki" VALUES(26,105,'26:105','گەلی نوح پێغەمبەرانیان بەدرۆزن دانا (بڕوایان پێ نەکردن)');
INSERT INTO "bamoki" VALUES(26,106,'26:106','کاتێک کەنوحی برایان پێی ووتن ئایا ناترسن لەخوا (خۆتان ناپارێزن)');
INSERT INTO "bamoki" VALUES(26,107,'26:107','بێگومان من پێغەمبەرێکی ئەمینم بۆ ئێوە');
INSERT INTO "bamoki" VALUES(26,108,'26:108','کەواتە لەخوا بترسن و گوێڕایەڵی من بکەن');
INSERT INTO "bamoki" VALUES(26,109,'26:109','وە من داوای ھیچ کرێیەکتان لێ ناکەم لەسەر ئەو (ڕێنمونی کردنەتان) (چونکە) پاداشتی من تەنھا لەسەر پەروەردگاری جیھانیانە');
INSERT INTO "bamoki" VALUES(26,110,'26:110','کەواتە لەخوا بترسن و گوێ رایەڵی من بکەن');
INSERT INTO "bamoki" VALUES(26,111,'26:111','ووتیان ئایا (چۆن) باوەڕ بەتۆ بھێنین لەکاتێکـدا ئـەوانـەی شـوێنت کـەوتون پەسترین کەسن (ھەژارو داماو ونەفامن)');
INSERT INTO "bamoki" VALUES(26,112,'26:112','(نوح علیە السلام) ووتی من چوزانم ئەوانە چی کردەوەیەک دەکەن');
