INSERT INTO "bamoki" VALUES(26,224,'26:224','(پێغەمبەر صلى الله عليه وسلم شاعیر نیە) (چونکە) شاعیرەکان تەنھا گومڕاکان شوێنیان دەکەون');
INSERT INTO "bamoki" VALUES(26,225,'26:225','ئایا نابینیت کەئەوانە بەھەموو دۆڵ و شیوێکدا بەسەر لێشێواوی دەڕۆن (شیعر دەڵێن)');
INSERT INTO "bamoki" VALUES(26,226,'26:226','وە بەڕاستی ئەوانە شتێك دەڵێن کەخۆیان نایکەن');
INSERT INTO "bamoki" VALUES(26,227,'26:227','جگە لەوانەیان بڕوایان ھێناوە و کردەوە چاکەکانیان کردووە وە یادو زیکری خوایان زۆر کردووە وە تۆڵەیان سەندووە لەپاش ئەوە ستەمیان لێکراوە وە ئەوانەی ستەمیان کردووە لەمەودوا دەزانن کەچۆن گەڕانەوەیەك دەگەڕێنەوە(بۆ لای خوا دوای مردن)');
INSERT INTO "bamoki" VALUES(27,1,'27:1','بە (طا، سین) دەخوێنریتەوە خوا زاناترە بە ماناکەی بۆزانیاری زیاتر سەیری سەرەتای سورەتی (البقرة) بکە ئەمە ئایەتەکانی قورئان و کتێبی ئاشکرایە');
INSERT INTO "bamoki" VALUES(27,2,'27:2','(ھۆی) ڕێنمونی و مژدەدانە بۆ بڕواداران');
INSERT INTO "bamoki" VALUES(27,3,'27:3','ئەوانەی کەنوێژ بەجێ دەھێنن و زەکات دەدەن وە ئەوانەی بڕوای بتەویان بەڕۆژی دوایی ھەیە');
INSERT INTO "bamoki" VALUES(27,4,'27:4','بەڕاستی ئەوانەی باوەڕ ناھێنن بەڕۆژی دوایی کردەوە (خراپە) کانیانمان ڕازاندوەتەوە بۆیان بۆیە ئەوان بەسەرگەردانی دەسوڕێنەوە');
INSERT INTO "bamoki" VALUES(27,5,'27:5','ئەوانە کەسانێکن سزای بەدیان بۆ ھەیە وە ئەوانە لەڕۆژی دواییدا زەرەرمەندترین کەسن');
INSERT INTO "bamoki" VALUES(27,6,'27:6','بەڕاستی تۆ (ئەی موحەممەد صلى الله عليه وسلم) قورئانت پێدەدرێ (فێر دەکرێیت) لەلایەن خوای کار دروست و زاناوە');
INSERT INTO "bamoki" VALUES(27,7,'27:7','(بیریان خەرەوە) کاتێ موسا بەخێزانەکەی ووت بێگومان من ئاگرێك بەدیدەکەم (ئێوە لێرەدا بن) من دەڕۆم بۆ لای ئەو ئاگرە بەزووی ھەواڵێکتان بۆ دەھێنم یا گڕو بزوسکێ (لەو ئاگرە) تان بۆ دەھێنم تا ئێوە خۆتانی پێ گەرم بکەنەوە');
INSERT INTO "bamoki" VALUES(27,8,'27:8','ئەمجا کاتێك (موسا علیە السلام) گەیشتە لای ئاگرەکە بانگی لێکرا کە پیرۆز بووە ئەو کەسەی لەناو ئاگرەکە (ڕووناکیەکە) دایە و ئەوەش بەدەوریدایە وە پاك و بێگەردە خوای پەوەردگاری جیھانیان');
INSERT INTO "bamoki" VALUES(27,9,'27:9','ئەی موسا بەڕاستی ئەوەی (بانگی کردی) منم خوای بەدەسەڵات وکار دروست');
INSERT INTO "bamoki" VALUES(27,10,'27:10','دە گۆچانەکەت فڕێدە (ئەویش فڕێیدا) ئەمجا کاتێك بینی وەك تیرەمار دەجوڵێت (موسا) ھەڵھات و ئاوڕی نەدایەوە. (خودا فەرمووی) ئەی موسا مەترسە بێگومان پێغەمبەران لای من ناترسن');
INSERT INTO "bamoki" VALUES(27,11,'27:11','بێجگە لەکەسێك کەستەمی کردبێت پاشان خراپەکەی گۆڕیبێت بەچاکە ئەوە بەڕاستی من لێبوردە وبەبەزەیم');
INSERT INTO "bamoki" VALUES(27,12,'27:12','وە دەستت بکە بەباخەڵتدا (کە دەرت ھێنا) بەسپێتی و پرشنگداری دێتە دەرەوە بەبێ بەڵەکی (ئەمە دوو موعجیزەیە) لەنۆ موعجیزە بۆ فیرعەون و گەلەکەی بەڕاستی ئەوانە گەلێکی لەسنوور دەرچوون');
INSERT INTO "bamoki" VALUES(27,13,'27:13','ئەمجا کاتێك موعجیزە ئاشکراو ڕوونەکانی ئێمەیان بۆ ھات ووتیان ئەمە جادویەکی ئاشکرایە');
INSERT INTO "bamoki" VALUES(27,14,'27:14','ئەو موعجیزانەیان دان پێدا نەدەنا وئینکاریان دەکرد کەچی لەدڵی خۆیان دا بەڕاستیان دەزانیلەبەر ستەمکاری و خۆ بە زل زانینیان (ئینکاریان دەکرد) ئەمجا سەرنج بدە سەر ئەنجامی خراپەکاران چۆن بوو');
INSERT INTO "bamoki" VALUES(27,15,'27:15','سوێند بە خوا زانستمان بە داود و سولەیمان بەخشی ھەردووکیان ووتیان: ھەموو سوپاس و سەنایە بۆ ئەو خوایەی کە ڕێزی داوین بەسەر زۆر لە بەندە باوەڕدارەکانیدا');
INSERT INTO "bamoki" VALUES(27,16,'27:16','وە سولەیمان بووە میراتگری داود (لە پێغەمبەرێتی و پاشایەتی و زانیاریدا) (سولەیمان) ووتی ئەی خەڵکینە فێری زمانی باڵندەکان کراوین وە لە ھەموو شتێکمان پێدراوە بەڕاستی ئەمە (بە من دراوە) فەزڵ و چاکەیەکی ئاشکرایە');
INSERT INTO "bamoki" VALUES(27,17,'27:17','وە کۆ کرایەوە بۆ سولەیمان ھەموو سەربازەکانی لە جنۆکەو ئادەمی و باڵندەکان ئەمجا ئەوان (لە پێش و دواکەوتن) بەرگیری دەکران (و بەڕیز دەڕۆشتن)');
INSERT INTO "bamoki" VALUES(27,18,'27:18','ھەتا کاتێك گەشتنە سەر شیوی مێروولە (شوێنێک بوو مێروولەی زۆر بوو) مێروولەیەك ووتی: ئەی مێروولەکان بڕۆنە ناو ماڵەکانی خۆتان ( ناوکونەکانتان) با سولەیمان و لەشکرەکەی نەتان پلیشێنن(لە ژێر پێیاندا) لە کاتێکدا ئەوان ھەست ناکەن');
INSERT INTO "bamoki" VALUES(27,19,'27:19','ئەمجا (سولەیمان بەسەر سوڕمانەوە) زەردەخەنەی کرد لەقسەی ئەو مێروولە یە و ووتی ئەی پەروەردگارم وام ڕابھێنە کە سوپاسی ئەو چاکە و نیعمەتەتانەت بکەم کە ڕشتووتە بەسەر من و دایک باوکمدا وە (ڕام بھێنە) کە کار و کردەوەی چاکە بکەم کە تۆ پێی ڕازی بێت وە بە بەزەیی خۆت بمخەرە ڕیزی بەندە چاکەکانت');
INSERT INTO "bamoki" VALUES(27,20,'27:20','جا (سولەیمان) چاوی بە باڵندەکان دا گێڕا ووتی ئەوە چی یە بۆ پەپوو سلێمانە نابینم یاخود لە ئامادەنەبوانە؟ (خۆی دزیوەتەوە)');
INSERT INTO "bamoki" VALUES(27,21,'27:21','سوێند بە خوا سزایەکی بەتینی دەدەم یا سەری دەبڕم یا بەڵگەیەکی ڕوونم بۆ دەھێنێت (لەسەر ئامادە نەبوونی)');
INSERT INTO "bamoki" VALUES(27,22,'27:22','ئەمجا (پەپوو) ماوەیەکی کورت مایەوە جا (ھاتەوە و بەسولەیمانی) ووت ئاگادار بووم لە شتێك کە تۆ ئاگادار نیت لێی لە(ووڵاتی) سەبەئەوە ھەواڵێکی ڕاستەقینەم بۆ ھێناوی');
INSERT INTO "bamoki" VALUES(27,23,'27:23','بەڕاستی من بینیم ئافرەتێك پادشای ئەوانە (بەناوی بەڵقیس) کەھەموو شتێکی (پادشایەتی) پێدراوە و تەختێکی (پادشایەتی) زۆر گەورەی ھەیە');
INSERT INTO "bamoki" VALUES(27,24,'27:24','خۆی و گەلەکەیم بینی لەجیاتی خوا سوژدە بۆ ڕۆژ دەبەن و شەیتان کردەوە (خراپە) کانیانی بۆ ڕازاندبوونەوە بەو ھۆیەوە ڕێگری کردون لەڕێگەی ڕاست بۆیە ئەوان ڕێگای ڕاست نادۆزنەوە');
INSERT INTO "bamoki" VALUES(27,25,'27:25','بۆ ئەوەی سوژدە نەبەن بۆ ئەو خوایەی کەئەوەی شاراوە بێت لەئاسمانەکان و زەویدا دەری دەخات (دەریدەھێنێت) ئەوەش دەی شارنەوەو ئەوەی دەری دەخەن دەیزانێت');
INSERT INTO "bamoki" VALUES(27,26,'27:26','خوایەکە ھیچ پەرستراوێکی ڕاست نیە جگە لەئەو پەروەردگاری عەرشی ھەرە گەورەیە');
INSERT INTO "bamoki" VALUES(27,27,'27:27','(سولەیمان بەپەپووەکەی) ووت ئێمە لێکۆڵینەوە دەکەین (سەیر دەکەین) (تا بزانین) ڕاست دەکەیت (لەو ھەواڵەدا) یا لە درۆزنانیت');
INSERT INTO "bamoki" VALUES(27,28,'27:28','ئەم نامەیەی منیان بۆ بەرە و فڕێیدە بۆیان پاشان دوور کەوەرەوە لێیان ئەمجا سەرنج بدە چ وەڵامێك دەگەڕێننەوە');
INSERT INTO "bamoki" VALUES(27,29,'27:29','(بەڵقیس) ووتی ئەی پیاو ماقوڵ و سەرۆکەکان بەڕاستی من نامەیەکی گرنگم بۆ ھاوێژراوە');
INSERT INTO "bamoki" VALUES(27,30,'27:30','کەبێگومان لە سولەیمانەوە (ھاتووە) بەڕاستی ئەو (نامە دەست پێدەکات): بەناوی خوای بەخشندەی میھرەبان');
INSERT INTO "bamoki" VALUES(27,31,'27:31','خۆتان بەگەورە مەزانن بەسەرمداو بەموسڵمانی (گەردن کەچی) وەرن بۆ لام');
INSERT INTO "bamoki" VALUES(27,32,'27:32','بەڵقیس وتی ئەی سەرۆك و ئەشرافەکان دەربارەی ئەم کارەم ڕای خۆتانم پێ بڵێن چونکە من ھیچ بڕیارێك نادەم ھەتا ئێوە ئامادە نەبن و ڕاوێژتان پێ نەکەم');
INSERT INTO "bamoki" VALUES(27,33,'27:33','ووتیان: ئێمە خاوەنی ھێزین (لە چەك و سەرباز) وە (لەجەنگدا) خاوەنی ئازایەتیەکی زۆرین وە فەرمان وبڕیاریش بەدەستی تۆیە کەواتە سەرنج بدە (بیربکەرەوە) چ فەرمانێک دەدەیت');
INSERT INTO "bamoki" VALUES(27,34,'27:34','ووتی بەڕاستی پادشایان ھەر کاتێك (بەزۆر) چوونە شارێکەوە خراپ و وێرانی دەکەن وە دەسەڵاتدارەکانی ئەو (شارە) ڕیسواو سەرشۆڕ دەکەن وە (کاتێ زاڵ بوون) ھەر بەو جۆرە دەکەن');
INSERT INTO "bamoki" VALUES(27,35,'27:35','جا من دیارییەکی بەنرخ و گەورەیان بۆ دەنێرم ئەمجا چاوەڕوان دەکەم کەنێردراوەکان بەچیەوە دەگەڕێنەوە');
INSERT INTO "bamoki" VALUES(27,36,'27:36','ئەمجا کاتێك (نێراوەکە) ھاتە لای سولەیمان (سولەیمان) ووتی: ئێوە ماڵ و سامان بەمن دەدەن؟! دەی ئەوەی خوا بەمنی داوە چاکترە لەوەی داویەتی بەئێوە بەڵکو ئێوە بەدیاریەکەتان دڵخۆش دەبن');
INSERT INTO "bamoki" VALUES(27,37,'27:37','(بە دیاریەکەوە) بگەڕێرەوە بۆلایان سوێند بەخوا بۆیان دەھێین بە لەشکرێکەوە کەتوانای ڕوو بەڕوو بوونەوەیان نیە لەگەڵیدا وە سوێند بەخوا بەزەلیلی و ژێردەستی لەووڵاتەکەیان دەریان دەکەین لەکاتێکدا ئەوان ڕیسواو سەرشۆڕن');
INSERT INTO "bamoki" VALUES(27,38,'27:38','(سولەیمان) ووتی ئەی دەستەی پیاوماقوڵ و ئەشرافەکان کامتان تەختە (پادشاییە)کەیم بۆ دەھێنێت پێش ئەوەی بە ملکەچی بێن بۆلام');
INSERT INTO "bamoki" VALUES(27,39,'27:39','جنۆکەیەکی گەورەی بەھێز لە جنۆکەکان ووتی من ئەو (تەختەیت) بۆ دەھێنم پێش ئەوەی لەجێگەی خۆت ھەستیت وە بێگومان من بەتوانام بەسەر ئەو (ھێنانەدا) و دەسپاکیشم');
INSERT INTO "bamoki" VALUES(27,40,'27:40','ئەوەی زانستی لەکتێبی (ئاسمانی) لا بوو ووتی من بۆت دەھێنم پێش ئەوەی چاوت بتروکێنیت (تەختەکەی ھێنا) جا کاتێک سولەیمان تەختەتەکەی (بەڵقیسی) لای خۆیدا بینی ووتی ئەمە لە فەزڵی پەروەردگارمەوەیە بۆ ئەوەی تاقیم بکاتەوە ئایا شوکری (ئەم نیعمەتە) دەکەم یان پێ نەزان و سپڵە دەبم وە ھەر کەسێك سوپاسی خوا بکات ئەوە بێگومان سوپاس بۆ خۆی دەکات (چونکە سوپاسکردنی خوای گەورە نیعمەتەکانی پێ زیاد دەبێت) وە ھەر کەسیش ناسوپاسی بکات (زیانی بۆ خۆیەتی) چونکە بێگومان پەروەردگارم دەوڵەمەند و بێ نیازە');
INSERT INTO "bamoki" VALUES(27,41,'27:41','(سولەیمان) ووتی تەختەکەی بگوڕن سەرنج دەین (بزانین) دەیناسێتەوە (کە تەختەکەی خۆیەتی) یان نایناسێتەوە');
INSERT INTO "bamoki" VALUES(27,42,'27:42','ئەمجا کاتێك (بەڵقیس) ھات پێی ووترا تەختەکەی تۆ وەک ئەمە وابوو (بەڵقیس) ووتی وەك ئەو وایە (ھەر ئەڵێی ئەوە) (سولەیمان) ووتی: پێش ئەم (بەڵقیسە) زانستمان پێدرابوو وە ئێمە ملکەچ و موسڵمان بووین');
INSERT INTO "bamoki" VALUES(27,43,'27:43','وە ئەوەی بێجگە لەخوا دەیپەرست ڕێگری کرد (لەباوەڕ ھێنان بەخوا) بێگومان ئەو (بەڵقیس) لەگەلی بێ باوەڕان بوو');
INSERT INTO "bamoki" VALUES(27,44,'27:44','ووترا بە (بەڵقیس) بچۆرە ناو کۆشکەکەوە ئەمجا کاتێك کەبینی بەدەریای زانی و ھەر دوو قاچی ھەڵکرد (بۆ ئەوەی تەڕ نەبێت) (سولەیمان) پێی ووت ئەوە (تۆ بە دەریای دەزانی) کۆشکێکە لەشوشە (بەڵقیس) ووتی ئەی پەروەردگارم بەڕاستی من ستەمم لە خۆم کردو لەگەڵ سولەیمان دا منیش ملکەچ بووم بۆ پەروەردگاری جیھانیان');
INSERT INTO "bamoki" VALUES(27,45,'27:45','سوێند بەخوا ناردمان بۆ گەلی سەمود صاڵح پێغەمبەری برایان (علیە السلام) (پێی ووتن) کەخوا ببپەرستن کەچی ئەوان بوونە دوو کۆمەڵی دژ بەیەك');
INSERT INTO "bamoki" VALUES(27,46,'27:46','ووتی ئەی گەلەکەم بۆچی پەلە دەکەن (بەھاتنی) سزا و تۆڵە پێش (ھاتنی) ڕەحمەت و چاکە بۆچی (ناگەڕێنەوە) داوای لێخۆشبوون ناکەن لەخوا تا ڕەحمتان پێ بکرێت');
INSERT INTO "bamoki" VALUES(27,47,'27:47','ووتیان ئێمە تووشی شومی و نەگبەتی بووین بەھۆی تۆو ئەوانەی لەگەڵ تۆدان (باوەڕداران) (صاڵح) ووتی ھۆی ئەو نەگبەتیەی ئێوە لەلای خوایە (کەبێ باوەڕیتانە) نەخێر بەڵکو ئێوە گەلێکن تاقی دەکرێنەوە');
INSERT INTO "bamoki" VALUES(27,48,'27:48','لەو شارەی (صاڵح)دا نۆ کەس ھەبوون لەزەویدا خراپەکاریان دەکرد بەھیچ جۆر چاکەیان نەدەکرد');
INSERT INTO "bamoki" VALUES(27,49,'27:49','ووتیان (بەیەکتری) سوێند بخۆن بەخوا کەبەشەودا ھێرش دەکەینە سەر ماڵ و خێزانی (صاڵح و ھەموویان لەناو دەبەین) لەپاشان دەڵێین بەکەسوکاری (صاڵح) ئێمە ئاگامان لەکوشتن و تیاچوونی خێزانی ئەو نیەوبێگومان ڕاستیش دەکەین');
INSERT INTO "bamoki" VALUES(27,50,'27:50','ئەوان پیلانی نەخشەیان داڕشت بەپیلاندانێکی خراپ (بۆ کوشتنی صاڵح) ئێمەش (لەبەرامبەر پیلانی ئەواندا) پیلانمان گێڕا (بۆ لەناو بردنیان) بە پیلانگێڕانێکی بەھێز لەکاتێکدا ئەوان ھەستیان نەدەکرد');
INSERT INTO "bamoki" VALUES(27,51,'27:51','ئەمجا سەرنج بدە سەر ئەنجامی پیلانی ئەوان چی لێھات چۆن ئێمە ئەوان و گەلەکەیانمان ھەموو پێکەوە لەناو برد');
INSERT INTO "bamoki" VALUES(27,52,'27:52','ئەمجا ئەوە (جێ) خانووەکانیانە چۆڵ و خاڵی یە لەبەر ئەوەی ستەمیان کرد بەڕاستی لەو بەسەرھاتەدا پەندو ئامۆژگاری وبەڵگە ھەیە بۆ کۆمەڵێک کە دەزانن (چیمان بەسەر ئەوانە ھێناوە)');
INSERT INTO "bamoki" VALUES(27,53,'27:53','وە ئەو کەسانەمان ڕزگار کرد کەبڕوایان ھێنابوو و لەخوا دەترسان');
INSERT INTO "bamoki" VALUES(27,54,'27:54','وە باسی لوط بکە کاتێك ووتی بەگەلەکەی ئایا ئێوە ئەو کارە زۆر خراپ و ناشیرینە دەکەن کەئێوە دەبینن و دەزانن (چەند پیس وناشیرینە)');
INSERT INTO "bamoki" VALUES(27,55,'27:55','چۆن!! ئایا بەڕاستی ئێوە دەچنە لای پیاوان لەجیاتی ئافرەتان بۆ بەجێھێنانی ئارەزووەکانتان؟ دیارە ئێوە گەل وکۆمەڵێکی نەفام و نەزانن');
INSERT INTO "bamoki" VALUES(27,56,'27:56','ئەمجا وەڵامی گەلەکەی ھیچ نەبوو جگە لەوەی کەووتیان لوط و کەسوکار و(شوێن کەوتوانی) لەشارەکەتان دەرکەن بەڕاستی ئەوان کەسانێکن خۆیان پاك ڕادەگرن (لەم کردەوەی ئێمە)');
INSERT INTO "bamoki" VALUES(27,57,'27:57','ئەمجا ئێمە لوط و خێزانەکەیمان ڕزگار کرد جگە لەھاوسەرەکەی کەبڕیارمان دا ئەو(ھاوسەرەکەی) لەجێماوان بێت لەسزادائەمجا ئێمە خۆی وشوێنکەوتوانیمان ڕزگار کرد جگە لەخێزانەکەی کە لەتیاچووان بوو');
INSERT INTO "bamoki" VALUES(27,58,'27:58','وەبارانێکمان (لەبەرد) بەسەریان دا باراند ئای چ خراپ بوو بارانی ترسێنراوەکان');
INSERT INTO "bamoki" VALUES(27,59,'27:59','(ئەی موحەممەد صلى الله عليه وسلم) بڵێ ھەموو سوپاس و ستایش بۆ خوا وە سەلام و درود لەسەر ئەو بەندانەی خوا ھەڵیبژاردون (بۆ پێغەمبەرایەتی) ئایا خوا باشترە یا ئەو بتانەی دەیکەنە ھاوبەشی خوا');
INSERT INTO "bamoki" VALUES(27,60,'27:60','ئایا زاتێك کەئاسمانەکان و زەوی دروستکردووە لەئاسمانەوە بارانی بۆ باراندون ئەمجا بەو(بارانە) چەند باخچەیەکی خاوەن جوانیمان ڕواند کە ئێوە نەتان دەتوانی درەخت و ڕواوی ئەو (باخچە) بڕوێنن ئایا ھیچ پەرستراوێکی تر لەگەڵ خوادا ھەیە (نەخێر نیە) بەڵام ئەوانە گەلێکن لادەدەن لەڕێگای ڕاست');
INSERT INTO "bamoki" VALUES(27,61,'27:61','ئایا زاتێك کەزەوی کردوەتە جێگای ئارامی و نیشتەجێ بوون وە لەناو (زەوی)دا چەند ڕوباری بەدی ھێناوە وە چەند چیای گەورەی بۆ زەوی داناوە وە لە نێوان دوو ئاودا (شیرین و سوێر) بەربەستی داناوە (بۆ ئەوەی تێکەڵ نەبن) ئایا ھیچ پەرستراوێکی تر ھەیە لەگەڵ خوادا نەخێر نیە بەڵام زۆربەیان نازانن');
INSERT INTO "bamoki" VALUES(27,62,'27:62','ئایا زاتێك کەدێت بەھاواری ناچار و داماوەوە کاتێك ھاواری لێ دەکات وە ناخۆشی و بەڵاکان لادەبات (لەسەری) وە دەتان کاتە جێنشینی گەلانی تری سەر زەوی ئایا پەرستراوێکی تر ھەیە لەگەڵ خوادا (نەخێر نیە) بەڵام زۆر کەم بیر دەکەنەوە');
INSERT INTO "bamoki" VALUES(27,63,'27:63','ئایا زاتێ کە ڕێنموونیی ئێوە دەکات (بە ئەستێرەو شتی تر) لە تاریکی یەکانی وشکانی و دەریادا (لە شەودا) و زاتێك کە با دەنێرێت بە موژدەدەر پێش بارانی ڕەحمەتی ئایا ھیچ پەرستراوێکی تر ھەیە لەگەڵ خوادا خوا بەرزو بڵندترە لەوەی بت پەرستان دەیکەنە ھاوبەشی');
INSERT INTO "bamoki" VALUES(27,64,'27:64','ئایا زاتێك کە لەسەرەتاوە بونەوەر بەدی دەھێنێت پاشان (دوای مردنی) جارێکی تر دروستی دەکاتەوە وە زاتێك کە لەئاسمان و زەویەوە ڕزق و ڕۆزیتان دەدات ئایا ھیچ پەرستراوێکی تر ھەیە لەگەڵ خوادا (نەخێر نیە) (ئەی موحەممەد صلى الله عليه وسلم) بڵێ بەڵگەتان بھێنن ئەگەر ئێوە ڕاست دەکەن');
INSERT INTO "bamoki" VALUES(27,65,'27:65','بڵێ کەسانێك کە لە ئاسمانەکان و زەویدان غەیب نازانن بێجگە لەخوا (تەنھا ئەو دەیزانێت) و ئەوان ھەست ناکەن چ کاتێک زیندوو دەکرێنەوە');
INSERT INTO "bamoki" VALUES(27,66,'27:66','بەڵکو زانیاری ئەوان دەربـارەی ڕۆژی دوایی کورتـە وبڕ ناکات (زانیاریەکی وایان نیە) بەڵکو لەبارەی (ڕۆژی دوای) یەوە ئەوان لەگومان و دوودڵیدان بەڵکو ئەوان لەو بارەیەوە کوێرن');
INSERT INTO "bamoki" VALUES(27,67,'27:67','ئەوانەی بێ باوەڕ بوون دەیانووت ئایا کاتێك ئێمەو باوو باپیرانمان بووین بەخاك ئایا ئێمە دەر دەرھێنرێین (لە گۆڕەکانمان و زیندوو دەبینەوە)');
INSERT INTO "bamoki" VALUES(27,68,'27:68','سوێند بەخوا بەڕاستی ئێمەو باوو باپیرانمان لەمەو پێش بەڵێنی ئەم (زیندوو کردنەوە)مان پێدرا بوو ئەم (ھەڕەشەی موحەممەد صلى الله عليه وسلم لێمان دەکات) تەنھا درۆ و دەلەسەی گەلە پێشووەکانە (کەنوسراوەتەوە)');
INSERT INTO "bamoki" VALUES(27,69,'27:69','(ئەی موحەممەد صلى الله عليه وسلم) پێیان) بڵێ بڕۆن بگەڕێن بەزەوی و وڵاتاندا ئەمجا سەرنج بدەن بزانن سەرئەنجامی تاوانباران چۆن بووە (چییان بەسەر ھاتووە)');
INSERT INTO "bamoki" VALUES(27,70,'27:70','خەفەت مەخۆ بۆ ئەوانە (کەبڕوا ناھێنن) و سینگ و دڵت تەنگ نەبێت لەو پیلان و خراپانەی دەیکەن (دژ بەتۆ)');
INSERT INTO "bamoki" VALUES(27,71,'27:71','دەڵێن کەی دەبێت ئەو بەڵێن و ھەڕەشەیە ئەگەر ئێوە ڕاست دەکەن');
INSERT INTO "bamoki" VALUES(27,72,'27:72','(ئەی موحەممەد صلى الله عليه وسلم) بڵێ لەوانەیە زۆر لێتانەوە نزیک بووبێتەوە ھەندێک لەو سزایەی بەپەلە داوای ھاتنی دەکەن');
INSERT INTO "bamoki" VALUES(27,73,'27:73','وەبەڕاستی پەروەردگارت خاوەن فەزڵ و چاکەیە بەسەرخەڵکەوە بەڵام زۆر بەیان سوپاسی(پەروەردگار) ناکەن');
INSERT INTO "bamoki" VALUES(27,74,'27:74','وە بەڕاستی پەوەردگارت ئاگادارەو دەزانێت بەو (دوژمنایەتی و کینەی) لە دەرونیاندا دەیشارنەوە وبەوەش دەری دەبڕن (نایشارنەوە)');
INSERT INTO "bamoki" VALUES(27,75,'27:75','وە ھیچ شتێکی نادیار نیە لەئاسمان و زەویدا مەگەر لەکتێبی ڕوون (لوح المحفوظ) دایە');
INSERT INTO "bamoki" VALUES(27,76,'27:76','بەڕاستی ئەم قورئانە دەیگێرێتەوە بۆ نەوەی ئیسرائیل زۆربەی ئەو مەسەلانەی کە ئەوان تێیدا جیاوازن');
INSERT INTO "bamoki" VALUES(27,77,'27:77','وە بەڕاستی ئەم (قورئانە) ڕێنمونی میھرەبانی یە بۆ ئیمانداران');
INSERT INTO "bamoki" VALUES(27,78,'27:78','بەڕاستی پەروەردگارت دادوەری دەکات لەنێوانیاندا بەبڕیاری خۆی وە خوا بەدەسەڵاتی زانایە');
INSERT INTO "bamoki" VALUES(27,79,'27:79','کەوا بوو پشت ھەر بەخوا ببەستە بەڕاستی تۆ لەسەر ئاینێکی ڕاست و ڕوونی');
INSERT INTO "bamoki" VALUES(27,80,'27:80','بەڕاستی تۆ ناتوانی(بانگەوازت) بەمردووەکان ببیستێنیت وە ناتوانی بانگەوازت بەوانەی کەڕن بژنەوێنیت کاتێ پشت ھەڵدەکەن و دەڕۆن');
INSERT INTO "bamoki" VALUES(27,81,'27:81','وە تۆ ناتوانی کوێران ڕزگار کەیت لەگومڕاییەکەیان (ڕێنمونیان بکەیت) تۆ تەنھا دەتوانیت (ڕێنمونی) کەسێك بکەیت و ئایەتەکانی ئێمەی پێ بژنەوێنیت کەبڕوا بە نیشانەکانی ئێمە دەکات چونکە ئەوانە ھەمیشە ملکەچن (بۆ ھەق)');
INSERT INTO "bamoki" VALUES(27,82,'27:82','وە کاتێك ئەو بەڵێنی (سزایەی پێیان دراوە) بەسەریاندا ھات (کەمێك پێش ڕودانی قیامەت) گیان لەبەرێک لەزەوی دەردە ھێنین بۆیان قسەیان لەگەڵدا دەکات (ئەم ئاژەڵە دەڵێت) بەڕاستی ئەم خەڵکە باوەڕی ڕاستەقینەیان نەھێنا بەئایەتەکانی ئێمە');
INSERT INTO "bamoki" VALUES(27,83,'27:83','(ئەی موحەممەد صلى الله عليه وسلم) باسی) ئەو ڕۆژە بکە لەھەموو میللەتێك پۆلێکی زۆر کۆدەکەینەوە لەوانەی ئایەتەکانی ئێمەیان بەدرۆ دەزانی ئەمجا ئەوان بەرو ودوا ھەموویان کۆدەکرێنەوە ودەدرێن بەر (بۆ لێپرسینەوە)');
INSERT INTO "bamoki" VALUES(27,84,'27:84','ھەتا کاتێك دێن (بۆ شوێنی کۆکردنەوەیان) (خوا) ئەفەرموێ ئایا ئێوە ئایەتەکانی منتان بەدرۆ دەزانی (باوەڕتان پێ نەھێنا) لەکاتێکدا ھیچ زانیاریەکتان دەربارەیان نەبوو یان ئێوە چیتان دەکرد (لەدونیا)');
INSERT INTO "bamoki" VALUES(27,85,'27:85','وە بڕیاری (سزادانیان) بەسەرا دەسەپێ بەھۆی ستەمکاریانەوە ئەمجا ئەوان ھیچ قسە ناکەن');
INSERT INTO "bamoki" VALUES(27,86,'27:86','ئایا (بێ بڕوایان) نەیان زانیوە و نابینن ئێمە شەومان سازاندووە بۆ ئەوەی ئەوان ئارامی تیا بگرن وە ڕۆژمان ڕووناك کردووە (ئیش و کاری تیا دەکەن) بەڕاستی لەمەدا چەند بەڵگە و نیشانە ھەیە بۆ کەسانێك باوەڕ دەھێنن (بەخوا)');
INSERT INTO "bamoki" VALUES(27,87,'27:87','(ئەی موحەممەد صلى الله عليه وسلم) باسی) ئەو ڕۆژە بکە فوو دەکرێ بەشەیپوردا جا ھەرچی کەس لەئاسمانەکان و زەویدایە دەترسێ وڕادەچڵەکێت (ھەموو دەمرن) جگە لەو کەسانەی خوا بیەوێت (بەئارام بن و نەمرن) وە ھەموو بەملکەچی و سەر شۆڕی دێنەوە لای خوا');
INSERT INTO "bamoki" VALUES(27,88,'27:88','وە ئەو کێوو چیانە دەبینیت وا دەزانیت دامەزراون (لەشوێنی خۆیاندا) (کاتێك فووکرا بەشەیپوردا) چیاکان وەکو ڕۆشتنی ھەور دەڕۆن ئەمە دروستکراوی ئەو خوایە کەھەموو شتێکی بەو پەڕی ڕێك و پێکی دروستکردووە بەڕاستی خوا ئاگادارە بەھەر کردەوەیەك ئێوە دەیکەن');
INSERT INTO "bamoki" VALUES(27,89,'27:89','ھەرکەس کردەوەی چاکی کردبێت (لەڕۆژی دواییدا) پاداشتی چاکتری لەو (کردەوەی) بۆ ھەیە وە ئەوانە دڵنیان لەترس و داچڵەکانی گەورەی ئەو ڕۆژە');
INSERT INTO "bamoki" VALUES(27,90,'27:90','وە ھەرکەس خراپەی کردبێت (لەو ڕۆژەدا) بەسەر ڕوومەت ودەموچاویاندا دەخرێنە ناو ئاگری(دۆزەخ) ئایا سزا دەدرێن جگە لەوەی دەتانکرد');
INSERT INTO "bamoki" VALUES(27,91,'27:91','(ئەی موحەممەد صلى الله عليه وسلم) بڵێ) بێگومان من فەرمانم پێدراوە کەتەنھا پەروەردگاری ئەم شارە (مەککە) بپەرستم ئەو (خوایە) ی (ڕێز شکاندی) ئەم شارەی حەرام کردووە و ھەموو شتێك تەنھا ھی ئەو (خوایە) وە فەرمانم پێدراوە لەموسڵمانان بم');
INSERT INTO "bamoki" VALUES(27,92,'27:92','وە (فەرمانم پێدراوە کە) قورئان بخوێنم (بەسەر خەڵکیدا) جا ھەر کەس ھیدایەتی وەرگرت ئەو بێگومان تەنھا بۆ خۆی ھیدایەت وەردەگرێت (قازانجەکەی بۆ خۆیەتی) ھەرکەسیش گومڕا بێت (زیانی بۆ خۆیەتی) وە بڵێ: بەڕاستی من تەنھا یەکێکم لەترسێنەران');
INSERT INTO "bamoki" VALUES(27,93,'27:93','وە بڵێ ھەموو سوپاسگوزاریەك بۆ خوایە ئەو (خوایە) بەزوویی ئایەت و نیشانەکانی نیشانی ئێوە دادەت ئێوەش دەیانناسن (کەئایەتی خوان) وە پەروەردگارت بێ ئاگا نیە لەوەی کەئێوە دەیکەن');
INSERT INTO "bamoki" VALUES(28,1,'28:1','بە(طا،سین، میم) دەخوێنرێتەوە خوازاناترە بە ماناکەی بۆ زانیاری زیاتر سەیری سەرتای سورەتی (البقرة) بکە');
INSERT INTO "bamoki" VALUES(28,2,'28:2','ئەم (ئایەتانە) ئایەتەکانی قورئانی ڕوون و ئاشکران');
INSERT INTO "bamoki" VALUES(28,3,'28:3','دەیخوێنینەوە بەسەرتا (ئەی موحەممەد صلى الله عليه وسلم) ھەندێک لەھەواڵی موسا و فیرعەون بەڕاستی و دروستی بۆ ئەوانەی بڕوا دەھێنن');
INSERT INTO "bamoki" VALUES(28,4,'28:4','بەڕاستی فیرعەون زاڵ و(ملھوڕ و یاخی) بوو لەزەوی (میصر) دا وە خەڵکەکەی (میصری) کرد بەچەند بەش و کۆمەڵێك کۆمەڵێکیانی لاوازو ژێر دەستە دەکرد کوڕانی ئەوانی سەر دەبڕی و ئافرەتانیانی بەزیندویی دەھێشتەوە (دەیکردنە خزمەتکارو کەنیزەك) بەڕاستی ئەو (فیرعەون) لەخراپەکاران بوو');
INSERT INTO "bamoki" VALUES(28,5,'28:5','وە ئێمە دەمانەوێت (بەچاکەی خۆمان) منەت بنێن بەسەر ئەوانەی کەلاواز و ژێر دەستکرا بوون لەووڵاتی (میصر) دا وە بیان کەینە پێشەواو بیان کەینە میراتگرانی(فیرعەون و و شوێنکەوتوانی)');
INSERT INTO "bamoki" VALUES(28,6,'28:6','وە دەسەڵاتداریان بکەین لەو زەویەدا وە نیشانی فیرعەون وھامان و لەشکری ھەردووکیان بدەین لە (بەنو ئیسرائیلی یەکانەوە) ئەوەی خۆیان لێ دەپاراست');
INSERT INTO "bamoki" VALUES(28,7,'28:7','بەدایکی موسامان ڕاگەیاند (ئیلھامان بۆ کرد) کە شیر بدە بە موسا ئەمجا ھەرکاتێك ترست لێی ھەبوو ئەوکاتە بیخەرە ڕووباری(نیلە) وە مەترسـە (لێی) وخەفەتیش مەخۆ (بۆی) بەڕاستی ئێمە دەیگێڕینەوە بۆلات وە دەیکەین بەیەکێکیش لەپێغەمبەران');
INSERT INTO "bamoki" VALUES(28,8,'28:8','(دایکی، موسای خستە ئاوی نیلەوە) ئەمجا دارو دەستەی فیرعەون (پێیان زانی و) ھەڵیان گرتەوە بۆ ئەوەی ببێت بەدوژمن و ھۆی خەفەت بۆیان بەڕاستی فیرعەون وھامان ولەشکر و سەربازەکانیان تاوانبارو ھەڵەبوون');
INSERT INTO "bamoki" VALUES(28,9,'28:9','(ویستیان موسا بکوژن) ئاسیەی ژنی فیرعەون ووتی مایەی چاو ودڵ ڕۆشنیە بۆ من و بۆ تۆش (ئەی فیرعەون) مەیکوژن بەڵکو سوود و کەڵکمان پێ بگەیەنێت یان بیکەین بەکوڕی خۆمان لەکاتێکدا ئەوان ھەستیان نەدەکرد (ئەم منداڵە ئەبێتە ھۆی تیاچوونیان)');
INSERT INTO "bamoki" VALUES(28,10,'28:10','جا دڵی دایکی موسا خاڵی بوو (لەھەموو شت جگە لەمنداڵەکەی) نزیك بوو (نھێنی) ئەو منداڵە ئاشکرابکات ئەگەر دڵیمان نەبەستایەتەوە (وە بەھێزمان نەکردایە) بۆ ئەوەی لەبڕواداران بێت');
INSERT INTO "bamoki" VALUES(28,11,'28:11','(دایکی موسا) ووتی بەخوشکەکەی (موسا) بەشوێنیدا بڕۆ ئەمجا لە دورەوە موسای بینی و پێی زانی لەکاتێکدا ئەوان (فیرعەون و کۆمەڵەکەی) ھەستیان نەدەکرد');
INSERT INTO "bamoki" VALUES(28,12,'28:12','وە ئێمە پێشتر (شیری) شیردەرەکانمان حەرام کرد لەموسا (مەمکی کەسیانی نەدەمژی) ئەمجا خوشکەکەی (موسا) ووتی پێیان ئایا ماڵێکتان نیشان دەم کەئەو منداڵە بۆ ئێوە بەخێو بکەن(و شیری بدەنێ) و دڵسۆز بن بۆی');
INSERT INTO "bamoki" VALUES(28,13,'28:13','ئەمجا موسا مان گێڕایەوە بۆلای دایکی بۆ ئەوەی چاوی پێ ڕوون بێـتەوەو خەفەت نەخوات وە بۆئەوەی بزانێت کەبەڵێنی خوا ڕاست و ھەقە بەڵام زۆر بەیان نازانن');
INSERT INTO "bamoki" VALUES(28,14,'28:14','وە کاتێك (یوسف) بوو بەپیاو و پێگەیی فەرمان ڕەوایی و زانیاریمان پێدا (وکردمان بە پێغەمبەر) وە ئابەم جۆرە پاداشتی چاکەکاران دەدەینەوە');
INSERT INTO "bamoki" VALUES(28,15,'28:15','وە (موسا) ڕۆشتە ناو شار بێ ئەوەی خەڵکی شار ئاگایان لێ بێت جا لەو شارەدا دوو پیاوی بینی لەگەڵ یەکتر شەڕیان دەکرد ئەمەیان لەتایفەی خۆی بوو ئەوی تریشیان لەدوژمنانی بوو ئەمجا ئەوەیان کە لەتایفەی خۆی بوو داوای یارمەتی لێکرد لەدژی ئەوەی کە لە(تاقمی) دوژمنەکەی بوو ئەمجا موساش بۆکزێکی لێدا و ئەو (قیبتیە) ی کوشت (موسا) ووتی ئەم (کوشتنە) لەکردەوەی شەیتانە بەڕاستی ئەو (شەیتان) دوژمنێکی گومڕاکەری ڕوون و ئاشکرایە');
INSERT INTO "bamoki" VALUES(28,16,'28:16','ووتی ئەی پەروەردگارم بەڕاستی من ستەمم لەخۆم کرد دەی لێم خۆشبە جا ئەویش (خودا) لێی خۆش بوو بێگومان تەنھا ئەو لێبوردەی بەبەزەییە');
INSERT INTO "bamoki" VALUES(28,17,'28:17','(موسا) ووتی ئەی پەروەردگارم بەھەقی چاکە ونیعمەتت لەسەرم (ئەو ھێزو توانایەیە کە پێتداوم) بڕیاربێ ھەرگیز نەبم بەپشتیوانی تاوانباران');
INSERT INTO "bamoki" VALUES(28,18,'28:18','بەھۆی ئەو (کوشتنە)وە لەشاردا بەترسەوە چاوەڕوانی دەکرد (فیرعەونیەکانی چ ھەڵوێستێکیان دەبێت) لەناکاودا ئەو (بەنی ئیسرائیلیەی) دوێنێ داوای یارمەتی کرد لەو (لە مووسا لەگەڵ یەکێکی تردا شەڕیەتی) ھانای بۆ موسا ھێنا تا فریای کەوێت موسا بەو (بەنو ئیسرائیلیە)ی ووت بەڕاستی تۆ کەسێکی گومڕای ئاشکرای');
INSERT INTO "bamoki" VALUES(28,19,'28:19','ئەمجا کاتێك کە (موسا) ویستی بەتوندی بدات لەو پیاوەی دووژمنی ھەردووکیانە ووتی ئەی موسا ئایا دەتەوێ بم کوژی ھەروەکو دوێنێ کەسێکت کوشت تۆ ھەر دەتەوێت زۆر دارو ملھوڕ بیت لەزەویدا وە ناتەوێ لەپیاو چاکان (ڕێکخەران) بیت');
INSERT INTO "bamoki" VALUES(28,20,'28:20','ئەمجا پیاوێك لەو پەڕی شارەوە بەپەلە ھات (بۆ لای موسا) ووتی ئەی موسا (ئاگاداربە) کاربەدەست وسەرگەورەکان کۆبوونەتەوە لەبارەی تۆوە (قسە دەکەن) بۆ ئەوەی بتکوژن کەواتە دەرچۆ لەشار (بانەت گرن) بێگومان من دڵسۆزتم');
INSERT INTO "bamoki" VALUES(28,21,'28:21','ئەویش لەشار دەرچوو بەترسەوە چاوەڕوانی دەکرد (پێی بزانن و بیگرن) (موسا) ووتی ئەی پەروەردگارم ڕزگارم بکە لەم کۆمەڵە ستەمکارە');
INSERT INTO "bamoki" VALUES(28,22,'28:22','لەکاتێکدا بەرەو شاری مەدیەن دەڕۆشت ووتی ئومێد دەکەم پەروەردگارم ڕێنمونیم بکات بۆ ڕێگای ڕاست و ھەق');
INSERT INTO "bamoki" VALUES(28,23,'28:23','وکاتێك گەیشتە سەر ئاوەکەی مەدیەن بینی لەسەر ئەو ئاوە کۆمەڵێك لەخەڵکی (ئاژەڵ و ماڵات) ئاو دەدەن وە لەنزیك ئەو (خەڵكانەوە) دوو ئافرەتی بینی ڕێگری دەکەن لەئاژەڵەکەیان (لەوەی بێتە سەر ئاوەکە) (موسا) ووتی ئێوە مەبەستان چیە؟ (ناھێڵن ئاژەڵەکەتان ئاو بخوات) ووتیان ئێمە (ئاژەڵەکەمان) ئاو نادەین ھەتا شوانەکان (وئاژەڵەکانیان لەسەر ئاوەکە) نەگەڕێنەوە وە باوکمان پیرێکی گەورەو بەتەمەنە');
INSERT INTO "bamoki" VALUES(28,24,'28:24','ئەمجا (موسا) مەڕەکانی بۆ ئاودان لەپاشان (موسا) چوو بۆ ژێر سێبەرێك جا ووتی ئەی پەروەردگارم بەڕاستی من ھەژارم (پێویستم ھەیە) بەھەر خێرو ڕۆزیەك بۆم بنێریت');
INSERT INTO "bamoki" VALUES(28,25,'28:25','ئەمجا یەکێك لەدوو کچەکە ھات بۆ لای موسا بەشەرمەوە دەڕۆشت لەڕێگادا ووتی بەڕاستی باوکم (شوعەیب) بانگت دەکات بۆ ئەوەی پاداشتی ئەوەت بداتەوە کەمەڕەکانت بۆ ئێمە ئاودا (موساش لەگەڵی ڕۆشت) ئەمجا کاتێك موسا ھات بۆ لای (باوکی کچەکە)و بەسەرھاتەکەی بۆ گێڕایەوە (شوعەیب علیە السلام) ووتی مەترسە ڕزگارت بووە لەو گەلە ستەمکارە');
INSERT INTO "bamoki" VALUES(28,26,'28:26','یەکێك لەو دوو (کچە بەباوکی) ووت ئەی باوە گیان بەکرێی بگرە (بۆ شوانی) چونکە بەڕاستی چاکترین کەسە کە بەکرێی بگری بەھێز و ئەمینە (دەس و داوێن پاکە)');
INSERT INTO "bamoki" VALUES(28,27,'28:27','(شوعەیب) ووتی بەڕاستی من دەمەوێ کە یەکێك لەم دوو کچەم لەتۆ مارەکەم بەو مەرجەی ھەشت ساڵ ڕەنجبەریم بۆ بکەیت ئەمجا ئەگەر دەساڵت تەواو کرد ئەوە لە(پیاوەتی) خۆتەوەیە و من نامەوێ قورسی بکەم لەسەرت ئەگەر خوا بیەوێت بێگومان دەمبینی لەپیاو چاکان دەبم');
INSERT INTO "bamoki" VALUES(28,28,'28:28','(موسا) ووتی ئەوەی ووتت پێم (بڕیار بێت) لەنێوان من و تۆدا ھەرکام لەو دوو ماوە (ھەشت یان دە ساڵە) م تەواو کرد نابێ ستەمم لێ بکرێت وە خوا لەسەر ئەوەی کەدەیڵێین شایەت و چاودێرە');
INSERT INTO "bamoki" VALUES(28,29,'28:29','ئەمجا کاتێك موسا ماوەی شوانی یەکەی تەواو کرد لەگەڵ خێزانەکەی بەشەو ڕۆشتن (لە مەدیەنەوە بۆ میێر) لە دورەوە لای ڕاستی کێوی گور ئاگرو ڕووناکی یەکی بینی (موسا) ووتی بەخێزانەکەی ئێوە (لێرەدا) بمێننەوە بەڕاستی من ئاگرێکم بەدی کرد (بینیم) بەڵکو ھەواڵێکی (ڕێگاتان) بۆ بھێنم لای ئەو (ئاگرە) یان چڵوسکێکی ئاگرتان بۆ بھێنم بەڵکو خۆتانی پێ گەرم کەنەوە');
INSERT INTO "bamoki" VALUES(28,30,'28:30','ئەمجا کاتێك ھاتە نزیکی ئەو (ئاگرە) بانگی لێکرا لەکەناری لای ڕاستی شیوەکەوە لەو پارچە زەویە بەفەڕو پیرۆزەی نزیك درەختەکەوە (بانگی لێکرا) کەئەی موسا بەڕاستی من خوایەکم پەروەردگاری جیھانیانم');
INSERT INTO "bamoki" VALUES(28,31,'28:31','(وە بانگی کرا) کە گۆچانەکەت فڕێدە ئەمجا کاتێك موسا ئەو (گۆچانە)ی بینی وەک تیرەمار دەجووڵێ بە پەلە پشتی تێکرد و ڕایکرد و نەگەڕایەوە سەری (خوا فەرمووی) ئەی موسا وەرە پێش و مەترسە بەڕاستی تۆ لەو کەسانەی بێ با و بێ ترسن');
INSERT INTO "bamoki" VALUES(28,32,'28:32','دەستت بخەرە ناو باخەڵ (و ژێر باڵت) (کاتێك دەری دێنیت) دێتە دەرەوە بەسپێتی و تیشك دەداتەوە بێ ئەوەی بەڵەکی و نەخۆشی بێت دەستت بلکێنە بەخۆتەوە (و بیخەرە سەردڵت) بۆ (نەھێشنی) ترس و بیم ئا ئەم دووانە (گۆچانەکەت و درەوشاوەیی دەستت) دوو موعجیزەن لەلایەن پەروەردگارتەوە(نێرراوە) بۆ فیرعەون و دارو دەستەکەی بەڕاستی ئەوانە کۆمەڵێکی لەسنوور دەرچوون');
INSERT INTO "bamoki" VALUES(28,33,'28:33','(موسا) ووتی ئەی پەروەردگارم بێگومان من یەکێکم لەوان کوشتووە لەبەر ئەوە دەترسم کەبم کوژن (لەجیاتی ئەو کوژراوە)');
INSERT INTO "bamoki" VALUES(28,34,'28:34','وە ھارونی برام لەمن زمانی پاراوترە جا ئەوم لەگەڵدا بنێرە پشتیوانم بێت ووتەکانم بسەلمێنێت بەڕاستی من دەترسم کە بە درۆزنم دابنێن (باوەڕم پێ نەھێنن)');
INSERT INTO "bamoki" VALUES(28,35,'28:35','(خوا) فەرمووی بێگومان ئێمە بە براکەت تۆ بەھێزت دەکەین وە دەسەڵاتێك بەھەردووکتان دەدەین ئەوسا بەھۆی ئایەتەکانی ئێمەوە دەستیان بەئێوە ناگات تەنھا ئێوە (موسا و ھارون) و ئەوەی شوێنتان دەکەوێت سەردەکەوون');
INSERT INTO "bamoki" VALUES(28,36,'28:36','ئەمجا کاتێك کەموسا موعجیزە ڕوونەکانی ئێمەی بۆ ھێنان ئەوان ووتیان ئەمە (تۆ ھێناوتە موسا) تەنھا جادویەکی دەست ھەڵبەستە ئێمە ئەمەمان نەبیستووە لەباوو باپیرە پێشوەکانمان');
INSERT INTO "bamoki" VALUES(28,37,'28:37','موسا ووتی پەروەردگارم زاناترە بەو کەسەی کەڕێنمونی لای ئەوەوە ھێناوە وە (زاناترە) بەو کەسەی سەرنجام ودوا ڕۆژی چاکی دەبێت بەڕاستی ئەو (خوا) ستەمکاران ڕزگار ناکات');
INSERT INTO "bamoki" VALUES(28,38,'28:38','فیرعەون ووتی: ئەی کاربەدەست ودەسەڵاتداران وا نازانم جگە لەخۆم ھیچ پەرستراوێکی ترتان ھەبێت جا (بۆ زیاتر دڵنیابوون) ئەی ھامان کوورەی ئاگر تاوبدە و خشتی سورەوە کراوی لێ دروست بکە بۆم ئەمجا (لەوخشتە) کۆشکێکی بەرزم بۆ دروست بکە (بەگاڵتەوە ووتی) بەڵکو سەیری خواکەی موسا بکەم و بیبینم وبەڕاستی وا دەزانم ئەو (موسا) لەدرۆزنانە');
INSERT INTO "bamoki" VALUES(28,39,'28:39','ئەو (فیرعەون) و لەشکرو سەربازەکانی خۆیان بەزل زانی لەووڵات و زەوی (میصر) دا بەناڕەوا وە گومانیان وابوو کەزیندوو ناکرێنەوەو ناگەڕێنرێنەوە بۆلای ئێمە');
INSERT INTO "bamoki" VALUES(28,40,'28:40','جا تۆڵەمان لەئەو (فیرعەون) و لەشکرو سەربازەکانی کردەوە و فڕێماندانە ناو دەریای سوورە وە ئەمجا سەرنج بدە (ئەی موحەممەد صلى الله عليه وسلم) سەرئەنجامی ستەمکاران چۆن بوو');
INSERT INTO "bamoki" VALUES(28,41,'28:41','ئەوانمان کردە پێشە وایانێک کە (خەڵك) بانگ دەکەن بۆ ئاگری دۆزەخ وە لەڕۆژی دواییشدا یارمەتی نادرێن');
INSERT INTO "bamoki" VALUES(28,42,'28:42','لەم دونیایەشدا نەفرەتمان نارد بەدوایاندا وە لەڕۆژی قیامەتیشدا ئەوانە لەبێزراو و دورخراوەکانن');
INSERT INTO "bamoki" VALUES(28,43,'28:43','سوێند بەخوا بێگومان تەوراتمان بەخشی بە موسا لەپاش ئەوەی خەڵکی سەردەمە پێشوەکانمان لەناوبرد تا بەرچاو ڕوونی و ڕێنمونی و ڕەحمەت بێت بۆ خەڵکی بۆ ئەوەی ئەو خەڵکە بیربکەنەوەو ئامۆژگاری وەرگرن');
INSERT INTO "bamoki" VALUES(28,44,'28:44','تۆ (ئەی موحەممەد صلى الله عليه وسلم) لەلای خۆر ئاوای (شاخی گور) نەبوویت کاتێک کە فەرمانی پێغەمبەرایەتمان دا بەموسا وە تۆ لەئامادە بووانی (ئەو ڕووداوە) نەبوویت');
INSERT INTO "bamoki" VALUES(28,45,'28:45','بەڵام ئێمە چەند چین و سەردەممان دروست کرد (لەپاش موسا) ئەمجا ماوەیەکی دوورو درێژیان بەسەردا تێپەڕی وە تۆ لەناو خەڵکی مەدیەندا نیشتەجێ نەبوویت کەئایەتەکانی ئێمەیان بەسەردا بخوێنیتەوە بەڵام ھەر ئێمە بووین پێغەمبەرمان نارد');
INSERT INTO "bamoki" VALUES(28,46,'28:46','وە تۆ لای کێوی گور نەبوویت کاتی بانگی(موسا)مان کرد بەڵام ئەوە بەزەییەکە لە پەروەردگارتەوە (بەتۆی داوە) بۆ ئەوەی گەلێك بترسێنیت کەپێش تۆ ھیچ ترسێنەرێکیان بۆ نەھاتووە بەڵکو بیربکەنەوەو پەند وەرگرن');
INSERT INTO "bamoki" VALUES(28,47,'28:47','وەئەگەر (پێش ناردنی پێغەمبەر) تووشیان ببوایە بەڵایەك بەھۆی ئەو (خراپانەوەی) کەکردوویانە جا دەیانووت ئەی پەروەردگاری ئێمە بۆچی پێغەمبەرێکت بۆ نەناردین بۆ ئەوەی شوێن ئایەتەکانت بکەوتینایە وە لەبڕواداران بووینایە');
INSERT INTO "bamoki" VALUES(28,48,'28:48','ئەمجا کاتێ ڕاستیان بۆھات لەلایەن ئێمەوە ووتیان بۆچی بەو (موحەممەد صلى الله عليه وسلم) نەدراوە وێنەی ئەوەی بەموسا دراوە ئایا ئەوان پێشتر بێ بڕوانەبوون بەوەی درابوو بەموسا دەیانووت (قورئان و تەورات) دوو سیحر و جادون پشتگیری یەکتر دەکەن وە دەیانووت بێگومان ئێمە بێ باوەڕین بەھەموویان (بە قورئان و بە تەورات)');
INSERT INTO "bamoki" VALUES(28,49,'28:49','(ئەی موحەممەد صلى الله عليه وسلم) بڵێ دەی ئێوە کتێبێك لەلایەن خواوە بھێنن کەئەو (کتێبە) ڕاستر بێت لەو دووانە (قورئان و تەورات) من شوێنی دەکەوم ئەگەر ئێوە ڕاستگۆن');
INSERT INTO "bamoki" VALUES(28,50,'28:50','ئەمجا ئەگەر بەگوێیان نەکردی (و وەڵامیان نەدایتەوە) ئەوە بزانە کەتەنھا شوێن ھەواو ئارەزوی خۆیان دەکەون وە کێ لەو کەسە گومڕاترە کەشوێن ئارەزووی خۆی بکەوێت بێ ڕێنمونی لەلایەن خواوە بێگومان خوا ڕێنمونی گەلی ستەمکار ناکات');
INSERT INTO "bamoki" VALUES(28,51,'28:51','سوێند بەخوا (ئایەتەکانی) قورئانمان بەشوێن یەکدا بۆ ناردن تا پەند وەرگرن و بیربکەنەوە');
INSERT INTO "bamoki" VALUES(28,52,'28:52','ئەو کەسانەی لەپێشدا کتێبی ئاسمانیمان پێ دابوون ئەوانە بڕوا دەھێنن بەقورئان');
INSERT INTO "bamoki" VALUES(28,53,'28:53','وە کاتێك (قورئان) بخوێنرێتەوە بەسەریاندا دەڵێن باوەڕمان پێ ھێناوە بێگومان ئەم (قورئانە) ڕاستەو لەلایەن پەروەردگارمانەوە ھاتووە بەڕاستی ئێمە لەپێش (ھاتنی) قورئان دا موسڵمان بووین');
INSERT INTO "bamoki" VALUES(28,54,'28:54','ئەوانە دووجار پاداشتیان پێ دەدرێت لەبەر ئەوەی خۆیان گرت و بەئارام بوون بەچاکە ڕێگری لە خراپەدەکەن وە لەو ڕزق و ڕۆزیەی پێمان داون دەبەخشن');
INSERT INTO "bamoki" VALUES(28,55,'28:55','وە کاتێك قسەی ناپەسەند و ھیچ و پوچ ببیستن پشتی تێدەکەن وە دەڵێن (بەنەفامان ئەنجامی) کردەوەکانمان بۆ خۆمانەو (ئەنجامی) کردەوەکانی ئێوەش بۆ خۆتانە سەلامتان لێ بێ ئێمە ھاوڕێیەتی نەفامانمان ناوێت');
INSERT INTO "bamoki" VALUES(28,56,'28:56','بەڕاستی (ئەی موحەممەد صلى الله عليه وسلم) تۆ ناتوانیت ئەو کەسەی خۆشت بوێ ڕێنمونی بکەیت بەڵام خوا ھەرکەسێکی بوێت ھیدایەتی دەدات و ئەو(خوایە) زاناترە بەوانەی ڕێنمونی وەرگرن');
INSERT INTO "bamoki" VALUES(28,57,'28:57','وە(بێ باوەڕان) دەڵێن ئەگەر ئێمە شوێنی ڕێنمونی (ئەو ئاینە) بکەوین کە بەتۆیە دەفڕێندرێین لەخاك و ووڵاتماندا (ودەردەکرێین وماڵ وسامانمان بەتاڵانی دەبرێت) (جا خوای گەورە وەڵامیان دەداتەوە ودەفەرموێت ) ئایا ئێمە ئەوانمان جێگیر نەکردووە لەشارێکی پڕ ئەمن وئاسایشی (وەك شاری مەککە کە ھەموو کوشت وبڕ وخراپەکاریەکی تێدا قەدەغە وحەرامکراوە) کۆدەکرێتەوەو دەبرێت بۆ ئەوێ ( بۆ شاری مەککە) ھەموو جۆرە بەروبوومێك بەڕۆزیدان لەلایەن ئێمەوە بەڵام زۆربەیان نازانن و تێ ناگەن');
INSERT INTO "bamoki" VALUES(28,58,'28:58','وە(خەڵکی) زۆر شار و دێھاتمان لەناو برد لەبەر خۆشگوزانیان یاخی بوون (و سوپاسی چاکەکانی خوایان نەکرد) جا ئەوە جێ و شوێنەکانیانە (وێران کراوە) لەپاش (تیا چوونی) ئەوان بۆ ماوەیەکی کەم نەبێت (لەلایەن ڕێبوارانەوە) کەسی تیا نیشتەجێ نەبووە وە ئێمە بووینە میراتگریان');
INSERT INTO "bamoki" VALUES(28,59,'28:59','وە پەروەردگارت ھیچ شار وگوندێکی وێران نەکردووە ھەتا لەشارە گەورەکەیاندا پێغەمبەرێکی نەناردبێت ئایەتەکانی ئێمەیان بەسەردا بخوێنێـتەوە وە ئێمە وێرانکاری ھیچ گوند و شارێك نەبووین مەگەر کاتێک خەڵکەکەی ستەمکار بووبن');
INSERT INTO "bamoki" VALUES(28,60,'28:60','وە ھەرچی بەئێوە دراوە ئەوە ھۆی خۆشی و ڕابواردنی ژیانی ئەم دونیا و جوانیەتی وە ئەو (پاداشتە) ی لای خوایە باشترو نەبڕاوەیە دەی ئایا ژیر نابن');
INSERT INTO "bamoki" VALUES(28,61,'28:61','ئایا کەسێك کەبەڵێنمان پێداوە بەبەڵێنی چاك ئەو کەسە دەگات بەو (بەڵێنە کە بەھەشتە) وەکو کەسێك وایە خۆشی و ڕابواردنمان پێداوە بەخۆشی و لەزەتی ژیانی دونیا لەپاشان لەڕۆژی دواییدا لەئامادەکراوان دەبێت (بۆ سزادان)');
INSERT INTO "bamoki" VALUES(28,62,'28:62','(ئەی موحەممەد صلى الله عليه وسلم) باسی ئەو ڕۆژە بکە کە(خوا) بانگی ئەو (بت پەرست)انە دەکات و (پێیان) دەفەرموێ لەکوێن ئەو ھاوبەشانەی کەئێوە بەخەیاڵی خۆتان بەھاوبەشی منتان دەزانین');
INSERT INTO "bamoki" VALUES(28,63,'28:63','ئەوانەی (سەرۆکەکانی گومڕایی) کە بڕیاری سزادان بەسەریان داچەسپا دەڵێن ئەی پەروەردگارمان ئا ئەوانەن کە ئێمە گومڕامان کردن گومڕامان کردن ھەروەکو خۆمان گومڕا بووین ئێمە بەری بووین (لەوانە بەری بوونەکەمان) لای تۆ دەکەین ئەوانە ئێمەیان نەدەپەرست');
INSERT INTO "bamoki" VALUES(28,64,'28:64','وە(پێیان) دەوترێت ھاوار بکەن لەوبتانەی کەداتان نابوو بەھاوبەشی خوا ئەوانیش ھاواریان لێ دەکەن بەڵام وەڵامیان نادەنەوە لەوکاتەدا سزای (دۆزەخ) دەبینن (ئاوات دەخوازن کە)خۆزگە تەنھا ڕێگای ڕاستیان بدایەتەبەر');
INSERT INTO "bamoki" VALUES(28,65,'28:65','وە ڕۆژێك کە (خوا) بانگیان دەکات و (پێیان) دەفەرموێت چیتان وەڵامی پێغەمبەران دایەوە؟');
INSERT INTO "bamoki" VALUES(28,66,'28:66','ئەمجا لەو ڕۆژەدا ھەواڵەکانیان (بەڵگەکانیان) لەبیر دەچێتەوە ئەوسا پرسیاریش لەیەکتری ناکەن');
INSERT INTO "bamoki" VALUES(28,67,'28:67','بەڵام ئەو کەسەی گەڕاوەتەوە (لەبێ باوەڕی) و باوەڕی ھێناوە و کردەوەی چاکی کردووە ئەوە بێگومان لەڕزگار بووان دەبێت');
INSERT INTO "bamoki" VALUES(28,68,'28:68','وە پەروەردگارت (ئەی موحەممەد صلى الله عليه وسلم) ئەوەی بیەوێت دروستی دەکات و ھەڵی دەبژێرێت (خەڵکی) ھەڵبژاردنیان بۆ نیە (لەکاری خوادا) خوا پاك و بێگەرد و بەرز و بڵندە لەوەی دەیکەنە ھاوبەشی');
INSERT INTO "bamoki" VALUES(28,69,'28:69','وەپەروەردگارت دەزانێت بەئەوەی لەسینگیان (دڵیان)دا دەیشارنەوە و بە ئەوەشی ئاشکرای دەکەن');
INSERT INTO "bamoki" VALUES(28,70,'28:70','وە ئەو خوایە کەھیچ پەرستراوێکی ڕاست نیە جگە لەو ھەموو سوپاس تەنھا بۆ خوا لەدونیاو لەڕۆژی دواییشدا وە بڕیارو حوکم ھەر بۆ ئەوە و ھەمووتان تەنھا بۆلای ئەو دەگەڕێنرێنەوە (لەڕۆژی دواییدا)');
INSERT INTO "bamoki" VALUES(28,71,'28:71','ئەی (موحەممەد صلى الله عليه وسلم پێیان) بڵێ ھەواڵم بدەنێ ئەگەر خوا شەوتان بۆ درێژ بکاتەوە (ھەر شەوبێت) تا ڕۆژی قیامەت چ پەرستراوێکی تر جگە لەخوا ڕووناکی (ڕۆژ)تان بۆ دێنێ دەی ئایا ئێوە نابیستن');
INSERT INTO "bamoki" VALUES(28,72,'28:72','(پێیان) بڵێ پێم بڵێن ئەگەر خوا ڕۆژ بکاتە ھەمیشەیی بۆتان تا ڕۆژی قیامەت کێیە پەرستراوی تر بێجگە لەخوا کەشەوتان بۆ دەھێنێت ئارامی تیا بگرن دەی ئایا ئێوە نابینن');
INSERT INTO "bamoki" VALUES(28,73,'28:73','لەبەزەیی ئەو (خوایە) کەشەو وڕۆژی بۆ دروست کردون بۆ ئەوە ئارام بگرن لە(شەو) داو بۆ ئەوە بگەڕێن بەدوای ڕۆزی و فەزڵی خوادا (لەڕۆژدا) وە بۆ ئەوەی سوپاسی خوا بکەن (لەسەر ئەو نیعمەتانەی)');
INSERT INTO "bamoki" VALUES(28,74,'28:74','(ئەی موحەممەد صلى الله عليه وسلم) باسی ئەو ڕۆژە بکە کە(خوا) بانگی ئەو (بت پەرست)انە دەکات و (پێیان) دەفەرموێ لەکوێن ئەو ھاوبەشانەی کەئێوە بەخەیاڵی خۆتان بەھاوبەشی منتان دەزانین');
INSERT INTO "bamoki" VALUES(28,75,'28:75','وە لەناو ھەموو ئوممەتێکدا شایەتێك دەھێنین ئەمجا (بەبت پەرستان) دەڵێن بەڵگەتان بھێنن ئەوسا دەزانن بێگومان ھەق و ڕاستی تایبەتی خوایە ئەوەی بەدرۆ ھەڵیان بەستبوو (کردبوویان بەپەرستراو جگە لە خوا) لێیان گوم دەبێت');
INSERT INTO "bamoki" VALUES(28,76,'28:76','بێگومان قارون لەگەلەکەی موسا بوو (جولەکە بوو) بەڵام دەستدرێژی و ستەمی لێ دەکردن ئەوەندەمان گەنج (ئاڵتون و زیوو) پێدا بوو کەبێگومان (ھەڵگرتنی) کلیلەکانی (ئەو گەنج و قاسانە) بەزەحمەت ھەڵدەگیرا بۆ کۆمەڵێکی بەھێز کاتێك (ئیماندارانی) گەلەکەی پێیانووت دڵخۆش مەبە (بەو سامانە) بێگومان خوا ئەو کەسانەی خۆش ناوێت دڵخۆشن (و لەخۆبایی بوون بەسەروەت و سامانەوە)');
INSERT INTO "bamoki" VALUES(28,77,'28:77','وە لەو (گەنج و سامانەی) خوا پێی داویت ژیانی دواڕۆژی پێ بەدەس بھێنە وە بەشی خۆیشت لەم دونیایە لەبیر نەچێت وە چاکە بکە (لەگەڵ خەڵکدا) ھەرچۆنێ خوا چاکەی لەگەڵ تۆدا کردووە وە لەسەر زەویدا بەشوێن خراپەدا مەگەرێ بەڕاستی خوا خراپەکارانی خۆش ناوێت');
INSERT INTO "bamoki" VALUES(28,78,'28:78','(قارون) ووتی بێگومان ئەم سامانەم پێدراوە بەھۆی زانیاریەکەوە کە ھەمە ئایا (قارون) نەیزانیوە کەبەڕاستی خوا لە سەردەمانی پێش ئەمدا لەناوی بردووە کەسانێکی وا کە لەو (لە قارون) بەھێزتر بوون کۆڕ و کۆمەڵیشیان لەو زۆر تربووە (کاتێك سزای خوا ھات) پرسیار ناکرێت لەتاوانەکانی ئەم تاوانبارانە');
INSERT INTO "bamoki" VALUES(28,79,'28:79','ئەمجا (قارون) بەخۆ ڕازاندنەوە ھاتەدەر بۆ ناو گەلەکەی ئەوانەی ژیانی دونیایان دەویست ووتیان ئای خۆزگە ئەوەندەی بەقارون دراوە ئێمەش بمان بوایە بەڕاستی ئەو (قارون) خاوەنی بەش و بەختێکی گەورەیە');
INSERT INTO "bamoki" VALUES(28,80,'28:80','بەڵام ئەو کەسانەی (باوەڕدار بوون) و زانستیان پێ درابوو ووتیان ھاوار بۆ ئێوە، پاداشتی خوا (لەڕۆژی دواییدا) لەوە چاکترە بۆ ئەو کەسانەی بڕوایان ھێناوە و کردەوەی چاکیان کردووە وە کەس ئەو (ووتەی زانایانە) وەرناگرێ جگە لەخۆڕاگران');
INSERT INTO "bamoki" VALUES(28,81,'28:81','ئەمجا ئەو (قارون) و کۆشکەکەیمان برد بەناخی زەویدا ھیچ کۆمەڵ ودەستە وتاقمێکیشی نەبوو لەبەرانبەری (سزای) خوادا یارمەتی بدات وە لەو کەسانەش نەبوو بتوانێت خۆی ڕزگار بکات');
INSERT INTO "bamoki" VALUES(28,82,'28:82','وە ئەوانەی دوێنێ ئاواتیان دەخواست لەشوێن و پلەی ئەو (قارون) دابن وایان لێھات دەیانووت وای وا دیارە خوا ڕۆزی زۆر دەکات بۆ ھەرکەس خۆی بیەوێ لە بەندەکانی کەمیشی دەکاتەوە (بۆ ھەرکەس خۆی بیەوێ) ئەگەر خوا منەتی نەکردایە بەسەرماندا ئێمەشی دەبرد بەناخی زەویدا ئای لەم بەسەرھاتە سەرسوڕ ھێنەرە، بێگومان ڕزگار نابن ئەوانەی شوکرانە بژێری چاکەی خوا نین (کافرن)');
INSERT INTO "bamoki" VALUES(28,83,'28:83','ئەو ماڵی دوا ڕۆژە (بەھەشت) دەیدەین بەو کەسانەی نەبەرزی و خۆ بەزلزانیان دەوێت لەزەویدا نەدەست درێژی و خراپەکاری وە سەرئەنجامی چاك بۆ خۆپارێزانە');
INSERT INTO "bamoki" VALUES(28,84,'28:84','ھەر کەس کارێکی چاك بکات ئەوە (پاداشتی) چاکتر لەوەی بۆ ھەیە ھەرکەسیش خراپە بکات ئەوە تۆڵە ناسەنرێت لەوانەی خراپەکانیان کردووە تەنھا بە ئەندازەی ئەو کردەوانە نەبێت کەکردویانە');
INSERT INTO "bamoki" VALUES(28,85,'28:85','بێگومان ئەو (خوایەی) کەقورئانی بۆ تۆ ناردووە (پێویست کردووە) دەتگێڕێتەوە بۆ مەککە (ئەی موحەممەد صلى الله عليه وسلم) بڵێ پەروەردگارم زاناترە بەو کەسەی ڕێنمونی (ئاینی ڕاستی) ھێناوە وە بەو کەسەی لەگومڕاییەکی ڕوون و ئاشکرادایە');
INSERT INTO "bamoki" VALUES(28,86,'28:86','وە تۆ (ئەی موحەممەد صلى الله عليه وسلم) بەھیوا نەبوویت کەئەم (قورئانە)ت بۆ بنێردرێ بەڵام ڕەحمەتێك بوو لەلایەن پەروەردگاتەوە (بۆت نێردرا) کەواتە ھەرگیز مەبە بەپشتیوان بۆ بێ بڕوایان');
INSERT INTO "bamoki" VALUES(28,87,'28:87','وە با (بێ بڕوایان) ڕێگریت نەکەن لە(گەیاندنی) ئایەتەکانی خوا دوای ئەوەی دابەزێنراوە بۆت بانگی (خەڵك) بکە بۆلای پەروەردگارت وە لەھاوبەشدانەران مەبە');
INSERT INTO "bamoki" VALUES(28,88,'28:88','وە لەگەڵ خوادا ھیچ پەرستراوێکی تر مەپەرستە ھیچ پەرستراوێکی ڕاست و بەھەق نیە جگە لەخوا ھەموو شتێك لەناو دەچێت جگە لە ڕووی پیرۆزی ئەو نەبێت (لەم ئایەتە پیرۆزەدا جێگیرکردنی سیفەتی (وجە - ڕوو) بۆ خوای گەورە ھاتووە، بەڵام بەشێوەیەك کە شایستەی گەورەی وتەواوی زاتی خوا بێت، بەبێ ھیچ لێچواندن ولێکدانەوەیەك، بڕوامان پێیەتی وچۆنیەتیەکەی نازانین) بڕیارو حوکم تایبەتی خوایە و تەنھا بۆ لای ئەویش دەگەڕێنرێنەوە');
INSERT INTO "bamoki" VALUES(29,1,'29:1','بە (ئەلیف ، لام ، میم) دەخوێنرێتەوە خوا زاناترە بە ماناکەی بۆ زانیاری زیاتر سەیری سەرەتای سورەتی (البقرة) بکە');
INSERT INTO "bamoki" VALUES(29,2,'29:2','ئایا خەڵك وا دەزانن کەوازیان لێ دەھێنرێت ئەگەر بڵێن بڕوامان ھێناوە ئیتر ئەوان تاقی ناکرێنەوە');
INSERT INTO "bamoki" VALUES(29,3,'29:3','سوێند بەخوا بێگومان کەسانی پێش ئەوانمان تاقی کردەوە (ئەمانیش تاقی دەکەینەوە) ئەمجا بێگومان خوا ئەو کەسانەی ڕاستیان کرد دیاری دەکات درۆزنەکانیش دیاری دەکات (دەناسرێن)');
INSERT INTO "bamoki" VALUES(29,4,'29:4','یا ئەوانەی خراپەکان دەکەن وایان زانیووە کە لە دەستمان دەردەچن زۆر خراپە ئەو بڕیارەی دەیدەن');
INSERT INTO "bamoki" VALUES(29,5,'29:5','ھەر کەس ئومێدی وایە بگات بەدیداری خوا (با بەندایەتی خوا بکات) چونکە بەڕاستی کاتی دیاریکراوی خوا ھەردێت وە تەنھا خوایە بیسەر و زانا و ئاگادار');
INSERT INTO "bamoki" VALUES(29,6,'29:6','وە ھەرکەس جیھاد بکات و تێبکۆشێت ئەوە تەنھا بۆ خۆی تێ دەکۆشێت بەڕاستی خوا لە ھەموو جیھانیان بێ نیاز ودەوڵەمەندە');
INSERT INTO "bamoki" VALUES(29,7,'29:7','وە ئەوانەی باوەڕیان ھێناوە و کردەوە چاکەکانیان کردووە بێگومان خراپەکانیان دەسڕینەوە لێیان بەباشتر لەوەی کەکردویانە پاداشتیان دەدەینەوە');
INSERT INTO "bamoki" VALUES(29,8,'29:8','وە فەرمانمان داوە بەئادەمی کەچاکە لەگەڵ دایك و باوکیدا بکات وە ئەگەر (دایک و باوکت) ھەوڵیان لەگەڵ دایت بۆ ئەوەی کەھاوبەشم بۆ بڕیار بدەیت کەھیچ زانیارییەکت نیە دەربارەی ئەوە بەگوێیان مەکە تەنھا بۆ لای منە گەڕانەوەتان (لەدوا ڕۆژدا) ئەوسا ھەواڵتان دەدەمێ بەو کردەوانەی دەتانکرد');
INSERT INTO "bamoki" VALUES(29,9,'29:9','وە ئەو کەسانەی باوەڕیان ھێناوە و کردەوەی چاکەیان کردووە بێگومان دەیانخەینە ڕیزی پیاو چاکانەوە');
INSERT INTO "bamoki" VALUES(29,10,'29:10','ھەندێك لەخەڵکی دەڵێن باوەڕمان بەخوا ھێناوە ئەمجا کاتێك سزا بدرێت لەپێناوی خوادا (لەلایەن کافرانەوە) ئازارو سزای خەڵکی وەکو ئازارو سزای خوا دادەنێت وە ئەگەر لەلایەن پەروەردگارتەوە سەرکەوتنێك بێت دڵنیابە ئەوانە دەڵێن بێگومان ئێمە لەگەڵ ئێوەدا بووین ئایا خوا زاناو ئاگادارتر نیە بەوەی لەدڵ و دەرونی ھەموو کەسدایە');
INSERT INTO "bamoki" VALUES(29,11,'29:11','سوێند بێت خوا چاك دەزانێت ئەوانەی باوەڕیان ھێناوە کێن وەچاکیش دەزانێت دووڕوانیش کێن');
INSERT INTO "bamoki" VALUES(29,12,'29:12','ئەوانەی بێ بڕوابوون ووتیان بەوانەی ئیماندار بوون شوێن ڕێگا و ڕێبازی ئێمە بکەون و تاوانەکانتان بە ئەستۆی ئێمە ئەوانە (بێ بڕوایان) ھیچێك لەتاوان و گوناھی ئەوان (باوەڕداران) ھەڵناگرن بەڕاستی ئەوانە درۆزنن');
INSERT INTO "bamoki" VALUES(29,13,'29:13','سوێند بەخوا (بێ باوەڕان) باری گرانی (کوفرو تاوانی) خۆیان ھەڵدەگرن وەباری گرانی (خەڵکانی) تریش لەگەڵ باری گرانی خۆیان دا (ھەڵدەگرن) وە بەڕاستی لەڕۆژی قیامەتدا پرسیاریان لێ دەکرێ لەو درۆ و دەلەسانەی لەدونیادا ھەڵیان دەبەست');
INSERT INTO "bamoki" VALUES(29,14,'29:14','سوێند بەخوا نوحمان نارد بۆلای گەلەکەی (بە پێغەمبەری) ئەمجا ئەویش مایەوە لەناویان دا نۆسەد و پەنجا ساڵ (ھەزار ساڵ پەنجا کەم) لەئاکامدا لافاو و زریان لەناوی بردن لەکاتێکدا ئەوان ستەمکار بوون');
INSERT INTO "bamoki" VALUES(29,15,'29:15','ئەمجا ئەو (نوح) و کەسانێك لەکەشتیەکەدا بوون ڕزگارمان کردن وە ئەو (کەشتیە)مان کرد بەنیشانەیەك بۆ جیھانیان');
INSERT INTO "bamoki" VALUES(29,16,'29:16','وە (ئەی موحەممەد صلى الله عليه وسلم) باسی) ئیبراھیم بکە کاتێك بەگەلەکەی ووت خوا بپەرستن ولێی بترسن ئەوە چاکترە بۆ ئێوە ئەگەر ئێوە بزانن');
INSERT INTO "bamoki" VALUES(29,17,'29:17','بەڕاستی ئێوە لەجیاتی خوا بتانێك دەپەرستن وە ئێوە درۆیەکی گەورە ھەڵدەبەستن بەڕاستی ئەوانەی کەئێوە دەیان پەرستن بێجگە لەخوا ناتوانن ڕزق و ڕۆزی ئێوە بدەن کەواتە داوای ڕزق و ڕۆزی لەخوا بکەن وە ئەو بپەرستن و ھەر سوپاسی ئەویش بکە تەنھا بۆلای ئەو دەگەڕێنرێنەوە (لەقیامەتدا)');
INSERT INTO "bamoki" VALUES(29,18,'29:18','وە ئەگەر ئێوە باوەڕ نەھێنن ئەوە ئوممەتانی پێش ئێوەش باوەڕیان (بە پێغەمبەرەکانیان) نەھێنا وە پێغەمبەر تەنھا ڕاگەیاندنی ئاشکراو ڕوونی لەسەرە');
INSERT INTO "bamoki" VALUES(29,19,'29:19','ئایا ئەوانە سەرنجیان نەداوە کە چۆن خوا دروستکراوان بەدی دەھێنێت لەپاش (نەمان) دەیھێنێتەوە بێگومان ئەو (زیندوو کردنەوە) بۆ خوا ئاسانە');
INSERT INTO "bamoki" VALUES(29,20,'29:20','(ئەی موحەممەد صلى الله عليه وسلم) بڵێ بڕۆن بگەڕێن بەزەویدا وسەرنج بدەن کەچۆن دروستکراوەکانی لەسەرەتاوە بەدی ھێناوە لەپاشان ھەر خوا بەدی ھێنانی دوا ڕۆژیش بەدی دەھێنێت بەڕاستی خوا بەتوانایە بەسەر ھەموو شتێکدا');
INSERT INTO "bamoki" VALUES(29,21,'29:21','(خوا) سزای ھەر کەسێك دەدات کەبیەوێت وە ڕەحمیش دەکات بەھەر کەسێك کەبیەوێت وەتەنھا بۆ لای خوا دەگەڕێنرێنەوە');
INSERT INTO "bamoki" VALUES(29,22,'29:22','وە ئێوە ناتوانن لەدەست خوا دەربازبن نە لەزەوی دا و نە لەئاسماندا وە بۆ ئێوە نیە جگە لەخوا ھیچ دۆست و یارمەتیدەرێك');
INSERT INTO "bamoki" VALUES(29,23,'29:23','وە ئەوانەی کە بەنیشانەکانی خوا و دیداری ئەو باوەڕیان نیە ئا ئەوانە لەبەزەیی من (لە بەزەیی خوا) نائومێد دەبن وە ئەوانە سزای بەسۆ وسەختیان بۆ ھەیە');
INSERT INTO "bamoki" VALUES(29,24,'29:24','ئەمجا وەڵامی گەلەکەی ئەو (ئیبراھیم) تەنھا ئەوە بوو کەووتیان (ئیبراھیم) بکوژن یان بیسوتێنن خواش ئەوی ڕزگار کرد لەو ئاگرە بێگومان لەو (ڕزگار کردنی ئیبراھیم) دا چەند نیشانەو بەڵگە ھەیە بۆ کەسانێک باوەڕ دەھێنن');
INSERT INTO "bamoki" VALUES(29,25,'29:25','وە (ئیبراھیم) ووتی بێگومان ئەوەی ئێوە ھەڵتان بژاردووە لەجیاتی خوا بتانێکن لەبەر خۆشەویستی نێوانتان لەژیانی دونیا (دەیانپەرستن) لەپاشان لەڕۆژی دواییدا ھەندێکتان بێزاری دەردەبڕن لەھەندێکی ترتان (دان نانێن بەیەکتریدا) وە نەفرەت لەیەکتری دەکەن جێگاشتان ئاگری دۆزخە وە ھیچ یارمەتی دەرێکیشتان نیە');
INSERT INTO "bamoki" VALUES(29,26,'29:26','ئەمجا لوط باوەڕی بەو (بە ئیبراھیم) ھێنا وە(ئیبراھیم) ووتی بێگومان من کۆچ دەکەم بۆ لای پەروەردگارم (لە بابلەوە بۆ شام) بەڕاستی تەنھا ئەو زاڵ وبەدەسەڵات وکار دروستە');
INSERT INTO "bamoki" VALUES(29,27,'29:27','وە بەخشیمان بە (بە ئیبراھیم) ئیسحاقی(کوڕی) و یەعقوبی (کوڕەزای) وە پێغەمبەرێتی و کتێبمان بڕیاردا لەنەوەکانی (ئیبراھیم)دا وە لەدونیادا پاداشتمان دایەوە وە بەڕاستی ئەو لەڕۆژی دوایشدا لەپیاو چاکانە');
INSERT INTO "bamoki" VALUES(29,28,'29:28','(باسی) لوطیش بکە کاتێك بەگەلەکەی ووت بێگومان ئێوە کارێکی ناشیرین و قێزەون دەکەن (کە نێربازیە) لەجیھاندا کەس پێش ئێوە ئەو کارەی نەکردووە');
INSERT INTO "bamoki" VALUES(29,29,'29:29','ئێوە نێربازی دەکەن وە ڕێگا دەگرن (لە ڕێبواران) وە لەنادی و شوێنی کۆبونەوەتاندا ھەموو خراپەیەك دەکەن کەچی وەڵامی گەلەکەی تەنھا ئەوە بوو کە ووتیان (بە لوط) سزای خوامان بۆ بھێنە ئەگەر تۆ لەڕاستگۆیانی');
INSERT INTO "bamoki" VALUES(29,30,'29:30','(لوط) ووتی ئەی پەروەردگارم یارمەتیم بدە (وزاڵم بکە) بەسەر ئەو گەلە خراپەکارەدا');
INSERT INTO "bamoki" VALUES(29,31,'29:31','وە کاتێك نێراوەکانی ئێمە مژدەی (بەخشینی ئیسماعیل)یان دا بەئیبراھیم ووتیان بێگومان ئێمە لەناوبەری دانیشتوانی ئەم شارەین (سەدوم) بەڕاستی دانیشتوانەکەی ستەمکار بوون');
INSERT INTO "bamoki" VALUES(29,32,'29:32','(ئیبراھیم) ووتی بێگومان لوط لەو (شارە) دایە ووتیان ئێمە چاک دەزانین کێی لێیە سوێند بەخوا ئێمە (لوط) و ئەندامانی خێزانەکەی رزگار دەکەین جگە ژنەکەی کە لەجێماوان (تیاچووان) دەبێت');
INSERT INTO "bamoki" VALUES(29,33,'29:33','کاتێ نێراوەکانمان ھاتن بۆ لای لوط پێیان تێکچوو وزۆر دڵ تەنگ وناڕەحەت بوو ووتیان مەترسە خەم مەخۆ (چونکە) بێگومان ئێمە تۆ وخێزانەکەت ڕزگار دەکەین جگەلە ژنەکەت نەبێت کەئەو لەجێماوان (تیاچووان) دەبێت');
INSERT INTO "bamoki" VALUES(29,34,'29:34','بەڕاستی ئێمە دەیبارێنین بەسەر دانیشتوانی ئەم شارەدا سزایەك لەئاسمانەوە لەبەر ئەوەی لەسنوورو فەرمانی (خوا) دەرچوون');
INSERT INTO "bamoki" VALUES(29,35,'29:35','سوێند بەخوا بێگومان لەو شارە (وێران کراوەدا) نیشانە وپەندێکی دیارمان بەجێھێشت بۆ گەل وکۆمەڵێك کەژیربن');
INSERT INTO "bamoki" VALUES(29,36,'29:36','وە بۆ خەڵکی مەدیەنیش شوعەیبی برایانمان (نارد) ووتی ئەی گەلەکەم خوا بپەرستن و بەھیواو ئومێدی ڕۆژی دوایی بن وەخراپەکاری ڕۆچوو مەبن لەزەویدا');
INSERT INTO "bamoki" VALUES(29,37,'29:37','بەڵام ئەوان باوەڕیان پێ نەھێنا ئەوسا بوومەلەرزە بەتینەکە گرتنی (لێی دان) بەھۆی ئەوەوە لە خانووەکانی خۆیاندا ئەژنۆیان دا دابوو وە مردن');
INSERT INTO "bamoki" VALUES(29,38,'29:38','وە گەلی عاد و سەمودیشمان (لەناو برد) بەڕاستی (ئەو لە ناوبردنە) ڕوون بووەتەوە بۆتان لەشوێنەوارە (وێرانە)کانیانەوە شەیتان کردەوە خراپەکانیانی بۆ ڕازاندبوونەوە لەڕێگا وڕێبازی ڕاست ودروست وێڵی کردن، لەکـاتێکدا ئـەوان خـاوەنی ژیری وبیروھۆش بوون');
INSERT INTO "bamoki" VALUES(29,39,'29:39','ھەروەھا قارون و فیرعەون و ھامانیشمان (لەناو برد) سوێند بەخوا بەڕاستی موسا ھات بۆ ناویان بەچەند موعجیزەیەکەوە بەڵام ئەوان خۆیان بەگەورە زانی لەزەویدا نەشیانتوانی لەدەستمان دەرچن');
INSERT INTO "bamoki" VALUES(29,40,'29:40','ئەوسا ئێمە ھەریەك لەوانمان بەتاوانی خۆی سزادا (لەناومان بردن) جا ھەندێکیان بایەکی بەھێزمان ناردە سەریان بەوردە بەردەوە وە ھەندێکی تریان پیاھەڵشاخانێک و دەنگێکی گەورە لەناوی بردن وە ھەندێکیشیانمان بەناخی زەویدا بردە خوارەوەوە ھەندێکی ترمان لەوان لەدەریادا نوقم کرد خوا ستەمی لێ نەکردن بەڵکو خۆیان ستەمیان لەخۆیان کرد');
INSERT INTO "bamoki" VALUES(29,41,'29:41','وێنەی کەسانێك کەبێجگە لەخوا پەرستراوانێك بۆ خۆیان دادەنێن وەکو جاڵجاڵۆکە وایە کەماڵێکی (بۆ خۆی) ڕەخساندووە کە بەڕاستی لاوازترین ماڵ ماڵی جاڵجاڵۆکەیە ئەگەر بزانن');
INSERT INTO "bamoki" VALUES(29,42,'29:42','بێگومان خوا دەزانێت ئەوان لەجیاتی خوا چی دەپەرستن و ھاوار ی لێدەکەن و ھەر ئەو (خوایە) زاڵ و بەدەسەڵات و کار دروستە');
INSERT INTO "bamoki" VALUES(29,43,'29:43','ئەو وێنە و نموونانە بۆ خەڵکی دەھێنینەوە تەنھا زانایان لەوانە تێدەگەن');
INSERT INTO "bamoki" VALUES(29,44,'29:44','خوا ئاسمانەکان و زەوی بەھەق و ڕاستی دروستکردووە بەڕاستی لەوەدا بەڵگە ھەیە بۆ بڕواداران');
INSERT INTO "bamoki" VALUES(29,45,'29:45','بیخوێنەرەوە ئەو قورئانەی کەوەحی کراوە بۆت وە نوێژ بکەبێگومان نوێژ ڕێگری (نوێژگەر) دەکات لەکاری ناشیرین و ناپەسەند (لەخراپە) وە زیکر و یادی خوا گەورەترە (لەھەموو شتێك) خواش دەزانێت بەھەر کارێك کە ئێوە دەیکەن');
INSERT INTO "bamoki" VALUES(29,46,'29:46','دەمەقاڵێ و موجادەلە مەکەن لەگەڵ خاوەنی کتێبە ئاسمانیەکاندا بە جوانترین شێوە نەبێت جگە لەوانەیان کە ستەمیان کرد (لە ئێوە) وە (بە خاوەن کتێبەکان) بڵێن باوەڕمان ھێناوە بەوەی نێردراوەتە خوارەوە بۆ ئێمە و (بەوەش) نێردراوەتە خوارەوە بۆ ئێوە وە خوای ئێمە و خوای ئێوە یەکێکە وە ئێمە بۆ ئەو (خوا) گەردن کەچین');
INSERT INTO "bamoki" VALUES(29,47,'29:47','ھەروەك (پێغەمبەرانی پێشوو) قورئانمان بۆ تۆ (ئەی موحەممەد صلى الله عليه وسلم) ناردەخوارەوە جا ئەو کەسانەی کە کتێبمان پێداون (ھەندێکیان) باوەڕ بەو (قورئانە) دەھێنن وە لەمانەش (بت پەرستەکانی عەرەب) کەسانێك بڕوای پێ دەھێنێت تەنھا بێ باوەڕانی (ڕۆچوو) دان نانێن بە ئایەتەکانی ئێمەدا');
INSERT INTO "bamoki" VALUES(29,48,'29:48','وە تۆ (ئەی موحەممەد صلى الله عليه وسلم) پێش ئەم (قورئانە) ھیچ کتێبێکت نەدەخوێندوەتەوە وە بەدەستی ڕاستت نەتنوسیووە (ئەگەر وابوایە) ئەو کاتە ناڕاستەکان (بێ بڕوایان) دەکەوتنە گومانەوە');
INSERT INTO "bamoki" VALUES(29,49,'29:49','(نەخێر وا نیە گومان دەبەن) بەڵکو قورئان نیشانەکانی ئاشکران لە دڵ و دەرونی کەسانێکدایە کە زانستیان پێدراوە (لە لایەن خواوە) تەنیا ستەمکاران دان نانێن بە نیشانەکانی ئێمەدا');
INSERT INTO "bamoki" VALUES(29,50,'29:50','وە ووتیان ئەوە بۆ چەند موعجیزە و بەڵگەیەك دانابەزێنرێت بۆ سەری (موحەممەد صلى الله عليه وسلم) لەلایەن پەروەردگاریەوە بڵێ (پێیان ھێنانە خوارەوەی) موعجیزەکان تەنھا لەلایەن خواوەیە وە بەڕاستی من تەنھا ترسێنەرێکی ئاشکرام');
INSERT INTO "bamoki" VALUES(29,51,'29:51','ئایا بۆ ئەوان بەس نیە کە ئێمە قورئانمان ناردۆتە خوارەوە بۆ سەر تۆ، دەخوێنرێتەوە بەسەریاندا بەڕاستی لەم قورئانەدا بەزەیی و پەند و ئامۆژگاری ھەیە بۆ باوەڕداران');
INSERT INTO "bamoki" VALUES(29,52,'29:52','بڵێ (پێیان) خوا بەسە بۆ شایەتیدان لەنێوان من و ئێوەدا ئەوەی لەئاسمانەکان و زەویدایە دەیزانێت وە کەسانێك کە باوەڕیان ھێناوە بە بەتاڵ و بێ باوەڕبوون بەخوا تەنھا ئەوانەن زەرەرۆمەندان');
INSERT INTO "bamoki" VALUES(29,53,'29:53','بە پەلە داوای (ھێنانی) سزات لێ دەکەن ئەگەر ماوەی دیاری کراو نەبوایە (بۆ سزادانی ئەوانە) بێگومان سزاکەیان بۆ دەھات وە بەڕاستی لەناکاودا ئەو سزایەیان بۆ دێت کە ئەوان ھەستی پێ ناکەن');
INSERT INTO "bamoki" VALUES(29,54,'29:54','بە پەلە لە تۆ داوای (ھێنانی) سزا دەکەن کە بەڕاستی دۆزەخ دەوری کافرانی داوە');
INSERT INTO "bamoki" VALUES(29,55,'29:55','لەڕۆژێکدا سزا دایان دەپۆشێت لەلای سەریانەوە و لە ژێر پێیانەوە وە (خوا) دەفەرموێ: بچێژن تۆڵەی ئەو کردەوانەی کە لە دونیادا دەتانکرد');
INSERT INTO "bamoki" VALUES(29,56,'29:56','ئەی بەندەکانم کە باوەڕتان ھێناوە بێگومان زەوی من پان و فراوانە کەواتە ھەر من بپەرستن');
INSERT INTO "bamoki" VALUES(29,57,'29:57','ھەمووکەس تامی مردن دەچێژێت وپاشان بۆ لای ئێمە دەگەڕێنرێنەوە');
INSERT INTO "bamoki" VALUES(29,58,'29:58','وە کەسانێك کە باوەڕیان ھێناوە و کردەوە چاکەکانیان کردووە سوێند بەخوا لەکۆشکە بەرزەکانی بەھەشتدا نیشتەجێیان دەکەین کە ڕووبارەکان بەژێریاندا دێن و دەچن بێ بڕانەوە تیایدا دەمێننەوە ئای چەند چاکە پاداشتی چاکەکاران');
INSERT INTO "bamoki" VALUES(29,59,'29:59','ئەوانەی دانیان بە خۆیاندا گرت و تەنھا پشتیان بە پەروەردگاریان بەست');
INSERT INTO "bamoki" VALUES(29,60,'29:60','زۆر گیان لەبەر (زیندەوەر) ھەیە ڕۆزی خۆی ھەڵناگرێت و کۆی ناکاتەوە (بۆ داھاتوو) خوا ڕۆزی ئەو (گیان لەبەرانەو) و ئێوەیش دەدات وە ھەر ئەوە بیسەر و زانا');
INSERT INTO "bamoki" VALUES(29,61,'29:61','سوێند بەخوا ئەگەر پرسیار لەو (بێ باوەڕانە) بکەیت کێ ئاسمانەکان و زەوی دروستکردووە و ڕۆژ و مانگی ڕامھێناوە وخستویەتیە خزمەتیانەوە بێگومان دەڵێن خوا دەی کەواتە چۆن (لە پەرستنی خوا) لادراون');
INSERT INTO "bamoki" VALUES(29,62,'29:62','خوا ڕۆزی فراوان دەکات بۆ ھەر کەس لە بەندەکان کە بیەوێت یان بۆی کەمدەکات بێگومان خوا بەھەموو شتێك زانایە');
INSERT INTO "bamoki" VALUES(29,63,'29:63','وە ئەگەر پرسیاریان لێ بکەیت کێ لەئاسمانەوە باران دەبارێنێت ئەمجا بەو بارانە زەوی مردووی پێ زیندوو دەکاتەوە بێگومان دەڵێن خوا (ئەی موحەممەد صلى الله عليه وسلم) بڵێ ھەموو سوپاسگوزاریەك ھەر بۆ خوایە بەڵام زۆربەیان ژیریان بەکار ناھێنن');
INSERT INTO "bamoki" VALUES(29,64,'29:64','وە ژیانی ئەم دونیایە تەنھا رابواردنێکی بێ ھودە و یاری (مناڵانە) وە بەڕاستی ماڵی قیامەت ھەر ئەوەیە ژیانی (ڕاستەقینە) ئەگەر ئەوان بیانزانیایە');
INSERT INTO "bamoki" VALUES(29,65,'29:65','ئەمجا کاتێك ئەو (بێ باوەڕانە) سوار کەشتی دەبن (و ترسان لە نوقم بوون) تەنھا ھاوار لە خوا دەکەن بە دڵسۆزی و گەردن کەچیەوە کەچی کاتێك خوا ڕزگاریان دەکات و دەگەنەوە ووشکانی کتوپڕ ئەوان ھاوبەش بۆ خوا دادەنێن');
INSERT INTO "bamoki" VALUES(29,66,'29:66','(وازیان لێ بھێنە) با دان نەنێن بەوەی ئێمە پێماندان، وە لەزەت ببەن لە ژیان جا لەمەولا دەزانن (ئەنجامی ئەو کردەو خراپانەیان چی دەبێت)');
INSERT INTO "bamoki" VALUES(29,67,'29:67','ئایا نەیان زانیوە و سەرنجیان نەداوە کە ئێمە (شاری مەککە)مان کردۆتە جێگایەکی ڕێز لێگیراوی بێ ترس وپڕ ئاسایش لە کاتێکدا لە دەوروبەری ئەواندا خەڵك دەفڕێنرێت دەی ئایا باوەڕ بە بەتاڵ دەھێنن وە سپڵە وپیِ نەزانن بەرامبەر نیعمەتەکانی خوا ی گەورە');
INSERT INTO "bamoki" VALUES(29,68,'29:68','کێ لەو کەسە ستەمکارترە کە درۆ بەدەم خواوە ھەڵدەبەستێت یان باوەڕ نەھێنێ بەئاینی ڕاست کاتێك بۆی ھات ئایا جێگای مانەوە بۆ بێ بڕوایان لە دۆزەخدا نیە');
INSERT INTO "bamoki" VALUES(29,69,'29:69','وە کەسانێك کە لە پێناو ڕەزامەندی ئێمەدا تێکۆشانیان کردووە بەڕاستی ڕێنموونیان دەکەین بۆ رێگا چاکەکانی خۆمان وە بێگومان خوا لەگەڵ چاکەکاراندایە');
INSERT INTO "bamoki" VALUES(30,1,'30:1','بـە ( ئـەلیف، لام، میم ) دەخوێنرێتەوە، خوای گەورە زاناترە بە واتاکەی.ئەم پیتانەی کە لەسەرەتای ھەندێک لەسورەتەکانی قورئاندا ھاتوون، تەنھا خوای گەورە دەزانێت مەبەستی پێیان چیە، ئەمە ڕاسترین تەفسیرە بۆ ئەم پیتانە کرابێت، بەڵام بەچەند جۆرێکی تریش تەفسیرکراون لەوانە: ئەم پیتانە ناون بۆ ئەوسورەتانەی لەسەرەتایانەوە ھاتوون، یان ناون بۆ خوای گەورە، یان بۆ دەستەوسانکردنی عەرەبە بێباوەڕەکانە کە دەیانووت ئەم قورئانە لەلایەن خوداوە نەھاتوەتە خوارەوە بۆ موحەممەد (صلى الله عليه وسلم) پێیان دەڵێت: دەی ئەگەر وایە وێنە وھاوشێوەی ئەم قورئانە بھێنن، کە ھەر لەو پیتانە پێـکھاتووە کە ئێوە قسەی پێ دەکەن و بەکاریان دەھێنن');
INSERT INTO "bamoki" VALUES(30,2,'30:2','ڕۆم شکێنران و ژێرکەوتن (فارس زاڵ بوو بە سەریاندا)');
INSERT INTO "bamoki" VALUES(30,3,'30:3','لە نزیك ترین خاکیان (لە مەککەوە، یان لە وڵاتی فارسەوە) وە ڕۆمەکان دوای تێشکانیان لە داھاتوودا سەر دەکەون (بەسەر فارسەکاندا)');
INSERT INTO "bamoki" VALUES(30,4,'30:4','لە ماوەی چەند ساڵێکدا (لە نێوان سێ ھەتا نۆ ساڵدا) ھەموو کاروبار تەنھا بە دەست خوایە لەپێش(سەرکەوتن) ولەدوا (سەرکەوتنیش) وە لەو ڕۆژەی (ڕۆمەکان سەردەکەون) ئیمانداران دڵخۆش دەبن (چونکە ڕۆمەکان خاوەن نامەی ئاسمانی بوون)');
INSERT INTO "bamoki" VALUES(30,5,'30:5','بەسەرخستنی خوا (بۆ ڕۆمەکان) خوا ھەر کەسی بوێت سەریدەخات وەھەرئەو زاڵ و باڵادەست ومیھرەبانە');
INSERT INTO "bamoki" VALUES(30,6,'30:6','ئەوە بەڵێنی خوایە (کە ڕۆمەکان سەربخات) خوایش پێچەوانەی بەڵێنی خۆی ناکات بەڵام زۆربەی خەڵك نازانن');
INSERT INTO "bamoki" VALUES(30,7,'30:7','تەنھا ڕواڵەتی ئەم ژیانی دونیا دەزانن بەڵام بێ ئاگان لە ڕۆژی دوایی');
INSERT INTO "bamoki" VALUES(30,8,'30:8','ئایا ئەوانە لە دەروونی خۆیاندا بیریان نەکردوەتەوە کە خوا ئاسمانەکان و زەوی و ئەوەی لە نێوانیاندایە دروستی نەکردووە بەھەق و ڕاستی نەبێت، وە بۆ ماوەیەکی دیاریکراو بێگومان زۆرێك لە خەڵك باوەڕیان نیە بە خزمەت پەروەردگاریان بگەن (لە دوا ڕۆژدا)');
INSERT INTO "bamoki" VALUES(30,9,'30:9','ئایا ئەوان بەسەر زەویدا نەگەڕاون بۆ ئەوەی ببینن سەرئەنجامی ئەو (گەلانەی بێ باوەڕ بوون) لە پێش ئەمان چۆن بووە کە زۆر بەھێزتر بوون لە ئەمان (لە خەڵکی مەککە) و زەویان کێڵا (بۆ کشتوکاڵ) و ئاوەدانیان کردەوە زیاتر لەمان وە پێغەمبەرانیان بۆ ھات بە موعجیزە ڕوونەکانەوە بۆیە خوا ستەمی لێ نەکردوون (کە لەناوی بردوون) بەڵام خۆیان ستەمیان لە خۆیان کرد');
INSERT INTO "bamoki" VALUES(30,10,'30:10','پاشان چارەنووسی ئەوانەی کەتاوان و خراپەیان کرد سەخترین سزایە (کەدۆزەخە) لەبەر ئەوەی باوڕیان نەھێنا بە ئایەتەکانی خوا و ھەمیشە گاڵتەیان پێ دەکرد');
INSERT INTO "bamoki" VALUES(30,11,'30:11','خوا لەسەرەتاوە (ئادەمی) دروستکرد وە لە پاش (مردنیش) دروستی دەکاتەوە پاشان بۆ لای ئەو (خوا) دەگەڕێنەوە');
INSERT INTO "bamoki" VALUES(30,12,'30:12','وە ئەو ڕۆژەی قیامەت ھەڵدەسێ تاوانباران غەمبار و نائومێد دەبن');
INSERT INTO "bamoki" VALUES(30,13,'30:13','بێگومان لە ناو پەرستراوەکانیاندا تکاکارانێك نیە بۆ ئەوان و ئەوان بێ باوەڕ دەبن بە پەرستراوەکانیان (کە کردبوونیان بە ھاوبەشی خوا)');
INSERT INTO "bamoki" VALUES(30,14,'30:14','ئەو ڕۆژەی کە قیامەت ھەڵدەسێ (خەڵك) لەو ڕۆژەدا جیادەبنەوە لەیەکتر');
INSERT INTO "bamoki" VALUES(30,15,'30:15','ئەمجا کەسانێك کەباوەڕیان ھێناوەو کردەوە چاکەکانیان کردووە ئەوان لە بەھەشتدا دڵخۆش دەکرێن');
INSERT INTO "bamoki" VALUES(30,16,'30:16','بەڵام ئەوانەی بێباوەڕ بوون و دانیان نەدەنا بەنیشانەکانی ئێمە و گەیشتن بەڕۆژی دوایی ئا ئەوانە لە سزای(دۆزەخدا)ئامادە دەکرێن');
INSERT INTO "bamoki" VALUES(30,17,'30:17','ئەمجا پاك و بێگەردی خوا بڵێن کاتێ ئێوارەتان لێدێت، وە کاتێ بەیانیتان لێ دەبێتەوە');
INSERT INTO "bamoki" VALUES(30,18,'30:18','وە سوپاس ھەر بۆ خوایە لە ئاسمانەکان و زەویدا وە(پاکی و بێگەردی خوا بڵێن) لەشەودا و کاتێك نیوەڕۆتان لێدێت');
INSERT INTO "bamoki" VALUES(30,19,'30:19','(خوا) زیندوو لەمردوو دەردەھێنێت و مردوو لەزیندوو دەردەھێنێت وە زەوی زیندوو دەکاتەوە لەپاش مردن و وشك بوونی وە ھەر بەوجۆرە ئێوەش دەردەھێنرێن (لە گۆڕەکانتان)');
INSERT INTO "bamoki" VALUES(30,20,'30:20','لە نیشانەکانی ئەو (خوایە) کە ئێوەی لە خاك دروستکردووە لە پاش (ماوەیەك) کتوپڕ ئێوە ئادەمین و بڵاوبوونەتەوە لە زەویدا');
INSERT INTO "bamoki" VALUES(30,21,'30:21','وە لەنیشانەکانی ئەوە (نیشانەکانی خوایە) کە بۆی دروست کردوون لە(ڕەگەزی) خۆتان ھاوسەرانێک بۆ ئەوەی ئارام بگرن لە لایاندا وە خۆشەویستی و بەزەیی خستۆتە نێوانتان بەڕاستی لەوەدا بەڵگە و نیشانەی زۆر ھەیە بۆ کەسانێك کەبیردەکەنەوە');
INSERT INTO "bamoki" VALUES(30,22,'30:22','وە لە نیشانەکانی (دەسەڵاتی) ئەوە دروستکردنی ئاسمانەکان و زەوی وجیاوازی زمان و ڕەگەز و ڕەنگتانە بەڕاستی لەوەدا (باسکرا) بەڵگەی زۆر ھەیە بۆ زانایان');
INSERT INTO "bamoki" VALUES(30,23,'30:23','وە لە نیشانەکانی ئەوە، خەوتنتان بە شەو و ڕۆژدا و ھەوڵدانتان (لە ڕۆژدا) بۆ بەھرە وەرگرتن لە بەخششی (ڕۆزی) خوا بەڕاستی لەوەدا بەڵگەو نیشانەی زۆر ھەیە بۆ کۆمەڵێك ببیستن');
INSERT INTO "bamoki" VALUES(30,24,'30:24','لە نیشانەکانی (خوایە) کەھەورە بروسکەتان نیشان دەدات بۆ ترسان و ئومێـد بوون (ترسان لە ھەورە بروسکە و ئومێد بوون بە بارانبارین) لەئاسمانەوە باران دەبارێنێت کە زەوی دوای مردنی بە ھۆی ئەو بارانەوە دەبوژێنێتەوە بەڕاستی لەوەدا (کەباسکرا) چەند بەڵگە و نیشانە ھەیە بۆئەوانەی ژیریان بەکار دەھێنن');
INSERT INTO "bamoki" VALUES(30,25,'30:25','وە لەنیشانەکانی ئەوە کەئاسمان و زەوی بەفەرمانی ئەو وەستاون پاشان کاتێک (لەڕۆژی دواییدا) لە زەویەوە بانگتان دەکەن یەکسەر ئێوە (لە گۆڕەکانتان) دێنە دەرەوە');
INSERT INTO "bamoki" VALUES(30,26,'30:26','ئەوەی لە ئاسمانەکان و زەویدایە تەنھا ھی خوایە ھەموویان ھەر بۆ ئەو (خوا) گەردن کەچن');
INSERT INTO "bamoki" VALUES(30,27,'30:27','ئەو (خوا) زاتێکە بەدی ھێنانی دەس پێکردووە لەپاشان (دوا مردنی) دووبارە دروستی دەکاتەوە ئەم (دروست کردنەوەی دووەم) لای خوا ئاسان ترە و ھەر بۆ ئەوە (ھەموو) سیفەتێکی بەرزو بڵند لە ئاسمانەکان و زەویدا و ھەر ئەوە زاڵ و کار دروست');
INSERT INTO "bamoki" VALUES(30,28,'30:28','(خوا) نموونەیەکتان لەخودی خۆتاندا بۆ دەھێنێـتەوە ئایا لە بەندەکانتاندا ھاوبەشانێك ھەیە بۆ ئێوەلەو ڕۆزی و سامانەی بە ئێوەمان دا بوو بە جۆرێك ئێوەو ئەو بەندانە تیایدا یەکسان بن و لەوان دەترسن (حسابیان بۆ بکەن) وەکو ترستان لە کەسانی وەك خۆتان ئا بەو جۆرە نیشانەکان ڕوون دەکەینەوە (بەدرێژی باسی دەکەین) بۆ کەسانێك ژیریان بەکار دەھێنن');
INSERT INTO "bamoki" VALUES(30,29,'30:29','(وازیان لێ بھێنە) بەڵکو ئەوانەی ستەمیان کرد شوێن ئارەزووی خۆیان کەوتن بێ شارەزایی و نەزانانە ئەمجا کێ (دەتوانێ) ڕێنمونی کەسێك بکات کە خوا گومڕای کردبێت لە کاتێکدا بۆ ئەوان ھیچ یارمەتی دەرێك نیە');
INSERT INTO "bamoki" VALUES(30,30,'30:30','کەواتە ڕووی خۆت رێك بکەرە ئاینی ئیسلام وپاك و ڕاست بۆ خوا (شوێن ئاینی) یەکخواپەرستی بکەوە، ئەو ئاینەی کەخوای گەورە (لەسەرەتاوە) فیترەتی پاکی مرۆڤی لەسەر دروستکردووە ئاین ودروستکراوەکانی خوا ھیچ جۆر گۆڕانی بۆ نیە ئەو (ئاینەی باسکرا) ھەر ئەوە ئاینی ڕێك و ڕاستە بەڵام زۆربەی ئادەمی نازانن');
INSERT INTO "bamoki" VALUES(30,31,'30:31','بەتۆبەکاریەوە شوێن ئاین بکەون، وە بۆلای خوا بگەڕێنەوە و ھەر لەویش بترسن، وە بەچاکی نوێژەکانتان ئەنجام بدەن و لە ھاوبەشدانەران مەبن');
INSERT INTO "bamoki" VALUES(30,32,'30:32','لەوانەی کە ئاینەکەیان بەش بەش کرد و بوون بە چەند کۆمەڵ ودەستەیەکەوە ھەر کۆمەڵێکیان بەو (ئاینەی) خۆیان دڵخۆشن');
INSERT INTO "bamoki" VALUES(30,33,'30:33','وە ھەر کاتێك زیان (وگرانی و بێ بارانی) بکەوێت لەخەڵکی (ھاوبەشدانەر بۆ خوا) ھاوار بۆ پەروەردگاریان دەبەن و بۆ لای ئەو دەگەڕێنەوە پاشان کاتێ لەلایەن خۆیەوە ڕەحمەت و ژیانی خۆشیان پێ بدات کتوپڕ دەستەیەك لەوان ھاوبەش بۆ پەروەردگاریان دادەنێن');
INSERT INTO "bamoki" VALUES(30,34,'30:34','(لێیان گەڕێ) با سپڵە وپێ نەزان بن ودان نەنێن بەو چاکانەی کە ئێمە پێمان داوون جا باڕابوێرن ولەزەت وەرگرن لەژیان ئەمجا لەمەودوا دەزانن (چیتان بەسەر دێت)');
INSERT INTO "bamoki" VALUES(30,35,'30:35','ئایا بەڵگەیەکمان بۆ ناردوون کە بدوێت دەربارەی ئەوەی ئەوان کردویانەتە ھاوبەشی خوا؟');
INSERT INTO "bamoki" VALUES(30,36,'30:36','وە کاتێ ڕەحمەت و چاکەیەك بەخەڵک بدەین پێی دڵخۆش دەبن وە ئەگەر زیانێکیان تووش ببێت بەھۆی کردەوەی خراپیانەوە کتوپڕ بێ ھیوا دەبن (لەبەزەیی خوا)');
INSERT INTO "bamoki" VALUES(30,37,'30:37','ئایا ئەوانە نەیان زانیوە کە بەڕاستی خوا ڕۆزی فراوان یان کەم دەکات بۆ ھەر کەس کەبیەوێت بێگومان لەوەدا (باسکرا) چەندین بەڵگە ھەیە بۆ ئەوانەی باوەڕ دەھێنن');
INSERT INTO "bamoki" VALUES(30,38,'30:38','کەواتە خزم مافی خۆی پێبدە ھەروەھا ھەژار و ڕێبوارانی کەمدەست ئەوە بۆ کەسانێك ڕەزامەندی خوایان دەوێت چاکترە و ھەر ئەوانەن ڕزگار بووان');
INSERT INTO "bamoki" VALUES(30,39,'30:39','ئەوەی بەناوی سوود(ڕیبا) داوتانە (بەخەڵکی) بۆ ئەوەی زیاد ببێ لە ماڵ و سامانی ئەو خەڵکانەدا (زیاتر لەوە داوتانە پێیان بتان دەنەوە) ئەوە لای خوا ھیچ زیاد ناکات (پاداشتی نیە) وە ھەر زەکات و خێرێک کەداوتانە و مەبەستان تەنھا ڕەزامەندی خوا بووە ئـائەوانە (پاداشتیان) چەند جارە دەکەینەوە');
INSERT INTO "bamoki" VALUES(30,40,'30:40','خوا زاتێکە ئێوەی بەدیھێناوە لەپاشان ڕۆزی پێ داون دواتر دەتانمرێنێت لەپاشان زیندووتان دەکاتەوە ئایا ھیچ یەك لەو ھاوبەشانەی بۆ خواتان دائەنا ھەیانە ھیچ شتێك لەوانە(ی خوا) بکات پاکی و بەرزی بۆ خوا لەوەی ئەوان دەیکەنە ھاوبەشی');
INSERT INTO "bamoki" VALUES(30,41,'30:41','خراپە (لە بەڵا وئاژاوە) لەوشکانی (زەوی) و دەریادا دەرکەوتووە بەھۆی ئەو خراپانەی خەڵکی کردویانە بۆ ئەوەی (تۆڵەی) ھەندێك لەو(خراپانەی) کردویانە پێیان بچەژێ بۆ ئەوەی بگەڕێنەوە (لەخراپەکاری)');
INSERT INTO "bamoki" VALUES(30,42,'30:42','(ئەی موحەممەد صلى الله عليه وسلم) بڵێ بەزەویدا بگەڕێن ئەمجا سەرنج بدە سەرئەنجامی ئەوانەی لەپێش (ئێوەوە) بوون چۆن بوو کە زۆربەیان ھاوبەشدانەر بوون بۆ خوا');
INSERT INTO "bamoki" VALUES(30,43,'30:43','کەواتە ڕووی خۆت بکەرە ئاینی ڕێك و ڕاست پێش ئەوەی ڕۆژێك بێت لەلایەن خواوە کە گێڕانەوەی بۆ نیە لەو ڕۆژەدا خەڵکی جیا ئەبنەوە لەیەکتر');
INSERT INTO "bamoki" VALUES(30,44,'30:44','ھەر کەس بێ باوەڕ بووبێت ئەوە (تۆڵەی) بێ باوەڕییەکەی لەسەرە ھەرکەسیش کردەوەی چاکی کردبێت ئەوە شوێن بۆ خۆیان (لە بەھەشتدا) ئامادە دەکەن');
INSERT INTO "bamoki" VALUES(30,45,'30:45','بۆ ئەوەی کەسانێك کە باوڕیان ھێناوەو کردەوەی چاکیان کردووە پاداشتیان بداتەوە لە میھرەبانی و چاکەی خۆی بەڕاستی ئەو (خوا) بێ باوەڕانی خۆش ناوێت');
INSERT INTO "bamoki" VALUES(30,46,'30:46','وە لە نیشانەکانی ئەو (خوایە) کە چەند بایەکی مژدەدەر (بەبارینی باران) دەنێرێت بۆ ئەوە ھەندێك لە (بارانی) بەزەیی خۆیتان پێ بچەژێت و بۆ ئەوەی کەشتیەکان بەفەرمانی خوا (بەو بایە) بڕۆن و بگەرێن و بۆ ئەوەی بەدوای ڕۆزی ئەو (خوا) دا بگەڕێن (لە دەریادا) تا شوکرانە بژێری خوا بن');
INSERT INTO "bamoki" VALUES(30,47,'30:47','سوێند بەخوا بەڕاستی پێش تۆ (ئەی موحەممەد صلى الله عليه وسلم) ناردومانە پێغەمبەرانێك بۆ لای گەلەکانیان ئەمجا موعجیزە و بەڵگە ڕوونەکانیان بۆ ھێنان (بەڵام باوەڕیان نەھێنا) ئەمجا تۆڵەمان سەند لەوانەیان تاوانیان کرد وە یارمەتیدانی باوەڕداران وسەرخستنیان ھەق ومافێکە لەسەرمان');
INSERT INTO "bamoki" VALUES(30,48,'30:48','خوا زاتێکە ئەو بایانە دەنێرێت بۆ ئەوەی ھەورەکان بجوڵێنن (بگەڕێنن) ئەمجا ئەو(ھەورە خوا) چۆن بیەوێ بڵاوی دەکاتەوە بەئاسماندا وە ئەو (ھەورە) دەکاتە پارچەیەك (وکۆیان دەکاتەوە لەسەر یەکتر) ئەمجا دەبینی قەترە قەترەی باران لە نێوانی ھەورەکەوە دەبارێت ئەمجا کاتێك (خوا) ئەو بارانە ببارێنێت بۆ (ووڵات وزەوی) ھەر کام لەبەندەکانی کەبیەوێت کتوپڕ دڵخۆش وشادمان دەبن (بەو بارانە)');
INSERT INTO "bamoki" VALUES(30,49,'30:49','ئەگەر چی پێش ئەوەی باران بەسەریاندا ببارێت بێ ھیوا و نائومێد بوون');
INSERT INTO "bamoki" VALUES(30,50,'30:50','ئەمجا سەیری ئاسەواری بارانی خوابکە (وەك کشتوکاڵ و گژو گیا و درەخت) چۆن زەوی زیندوو دەکاتەوە دوای مردن و ووشك بوونی بەڕاستی ھەر ئەو خوایە زیندوو کەرەوەی مردووانیشە و ھەرە ئەویش بەدەسەڵاتە بەسەر ھەموو شتێکدا');
INSERT INTO "bamoki" VALUES(30,51,'30:51','سوێند بەخوا ئەگەر بایەکی توندی (زەرەرۆمەند) بنێرین جا ئەو (ھەورە یان کشتوکاڵەکەیان) دەبینن بەزەرد بوویی لەپاش ئەو (زیانە) ھەر نا شوکری دەکەن');
INSERT INTO "bamoki" VALUES(30,52,'30:52','چونکە بەڕاستی تۆ (ئەی موحەممەد صلى الله عليه وسلم) ناتوانی بە مردووان (بەکافران) ببیستێنیت و ناتوانیت بانگەوازت بەکەڕان (بەکافران) ببیستێنیت کاتێك ڕوو وەردەگێڕن لێت بەپشت تێکردنەوە');
INSERT INTO "bamoki" VALUES(30,53,'30:53','وە تۆ ڕێنمونیکاری نابینایان نیت (ڕزگاریان بکەیت) لە گومڕاییان تۆ تەنھا بە کەسانێك دەشنەوێنی کە بەنیشانەکانی ئێمە باوەڕ دەھێنن چونکە ئەوانە گەردن کەچن (بۆ بانگ و فەرمانی تۆ)');
INSERT INTO "bamoki" VALUES(30,54,'30:54','خوا زاتێکە ئێوەی دروستکردووە لە لاوازی (لاوازی نوتفەو بێچوو منداڵی شیرە خۆر) پاشان دوای لاوزی بەھێزی کردون (بوون بە لاو) لەدوای بەھێز کردنتان لاواز وپیرتان دەکات خوا ھەرچی بوێت دروستی دەکات وە ھەر ئەوە زانا و بە دەسەڵات');
INSERT INTO "bamoki" VALUES(30,55,'30:55','وە ڕۆژی قیامەت ھەڵدەسێ تاوانباران سوێند دەخۆن (لە دونیادا یان لەبەرزەخدا) تەنھا ساتێك ماونەتەوە (لەدونیادا) ھەر بەو وێنە ئەوان لە دونیادا لادران لە ڕاستی');
INSERT INTO "bamoki" VALUES(30,56,'30:56','وە کەسانێك کەزانیاری و ئیمانیان پێـدراوە ووتیان سوێند بەخوا بەڕاستی ئێوە لە (بەرزەخدا بە پێی بڕیاری) نوسراوی خوا ھەتا ڕۆژی زیندووبوونەوە ماونەتەوە ئەمجا ئەمە ڕۆژی زیندوو بوونەوەیە بەڵام ئێوە نەتاندەزانی (ڕاستە و دێتە دی)');
INSERT INTO "bamoki" VALUES(30,57,'30:57','جا لەو ڕۆژەدا کەسانێك کە ستەمیان کردووە، بەھانە وبیانوو ھێنانەوە سوودیان پێ ناگەیەنێت وە ئەوان داوایان لێ ناکرێت خوا لە خۆیان ڕازی بکەن');
INSERT INTO "bamoki" VALUES(30,58,'30:58','سوێند بەخوا بەڕاستی ھێناومانەتەوە بۆ خەڵکی لەم قورئانەدا لە ھەموو جۆرە نموونە و باسێك ئەمجا بەڕاستی ئەگەر نیشانەیەکیان بۆ بھێنیت دڵنیابن ئەو بێ بڕوایانە دەڵێن ئێوە ھەر ناڕاست وبەتاڵن (ئەمە سیحرە)');
INSERT INTO "bamoki" VALUES(30,59,'30:59','ئا بەو جۆرە خوا مۆر دەنێت بەسەر دڵی ئەوانەی کەبێ ئاگا و نەزانن');
INSERT INTO "bamoki" VALUES(30,60,'30:60','جا (ئەی موحەممەد صلى الله عليه وسلم) دان بەخۆدا بگرە بێگومان بەڵێنی خوا ڕاستە تاوەکو ئەوانەی بێ باوەڕن لە (ئاینی) ڕاست لات نەدەن و بێزارت نەکەن');
INSERT INTO "bamoki" VALUES(31,1,'31:1','بـە ( ئـەلیف، لام، میم ) دەخوێنرێتەوە، خوای گەورە زاناترە بە واتاکەی.ئەم پیتانەی کە لەسەرەتای ھەندێک لەسورەتەکانی قورئاندا ھاتوون، تەنھا خوای گەورە دەزانێت مەبەستی پێیان چیە، ئەمە ڕاسترین تەفسیرە بۆ ئەم پیتانە کرابێت، بەڵام بەچەند جۆرێکی تریش تەفسیرکراون لەوانە: ئەم پیتانە ناون بۆ ئەوسورەتانەی لەسەرەتایانەوە ھاتوون، یان ناون بۆ خوای گەورە، یان بۆ دەستەوسانکردنی عەرەبە بێباوەڕەکانە کە دەیانووت ئەم قورئانە لەلایەن خوداوە نەھاتوەتە خوارەوە بۆ موحەممەد (صلى الله عليه وسلم) پێیان دەڵێت: دەی ئەگەر وایە وێنە وھاوشێوەی ئەم قورئانە بھێنن، کە ھەر لەو پیتانە پێـکھاتووە کە ئێوە قسەی پێ دەکەن و بەکاریان دەھێنن');
INSERT INTO "bamoki" VALUES(31,2,'31:2','ئەم ئایەتانە لەئایەتەکانی قورئانی پڕ لە حیکمەت و داناییە');
INSERT INTO "bamoki" VALUES(31,3,'31:3','(ھۆی) ڕێنمونی و بەزەیین بۆ چاکەکاران');
INSERT INTO "bamoki" VALUES(31,4,'31:4','ئەوانەی کەنوێژ بەجێ دەھێنن و زەکات دەدەن ھەروەھا باوەڕی پتەویان بەڕۆژی دوایی ھەیە');
INSERT INTO "bamoki" VALUES(31,5,'31:5','ئەوانە لەسەر ڕێنمونین لەلایەن پەروەردگاریانەوە وە ھەر ئەوان سەرکەوتوون');
INSERT INTO "bamoki" VALUES(31,6,'31:6','ھەندێك لەخەڵکی ھەیە قسە و باسی بێھودە دەکڕێت (وەك ئەفسانە وگۆرانی و مۆسیقا) بۆ ئەوەی بەنەزانی خەڵك لەڕێگای خوا گومڕا بکات وە ڕێگای خوا دەکاتە جێگای گاڵتەو لاقرتی ئەوانە سزای ڕیسواکەر یان بۆ ھەیە');
INSERT INTO "bamoki" VALUES(31,7,'31:7','وە ھەر کاتێك ئایەتەکانی ئێمەی بەسەردا بخوێنرێتەوە ڕوو وەردەگێڕێت بەخۆ بەگەورە زانینێکەوە ھەروەک نەیبیستبن، یان ھەروەك لە ھەردوو گوێچکەیدا کەڕی ھەبێت دەی (ئەی موحەممەد صلى الله عليه وسلم) مژدەی بدەرێ بەسزایەکی سەخت');
INSERT INTO "bamoki" VALUES(31,8,'31:8','بێگومان ئەوانەی باوەڕیان ھێناوە و کردەوە چاکەکانیان ئەنجامداوە بۆئەوانە بەھەشتانی پڕنازونیعمەت ھەیە');
INSERT INTO "bamoki" VALUES(31,9,'31:9','ھەتا ھەتایە دەمێننەوە تێیدا ئەوە بەڵێنێکی ڕاستە لەخواوە وە ھەر ئەو زاڵی باڵادەست و کاربەجێیە');
INSERT INTO "bamoki" VALUES(31,10,'31:10','(خوا) ئاسمانەکانی دروستکردووە بەبێ ھیچ کۆڵەکەو ئەستوونێك کە ئێوە بیبینن وە لە زەویدا چەندان شاخی لەنگەر داکوتاوی داناوە بۆ ئەوەی نەتانلەرزێنێ و نەجوڵێ لەژێرتاندا لەھەموو جۆرە زیندەوەرێکی تێدا بڵاوکردۆتەوە وە لەئاسمانەوە بارانمان باراندوە بەھۆی ئەو (بارانەوە) لەزەویدا ھەموو جۆرە ڕووەکێکی بەکەڵك و جوانمان ڕواندووە');
INSERT INTO "bamoki" VALUES(31,11,'31:11','ئەمە (باسکرا) دروستکراوی خوایە دەی نیشانم بدەن ئەو (بت و پەرستراوانە) ی بێجگە لەخودان چییان دروستکردووە (ھیچیان دروست نەکردووە) بەڵکو ستەمکاران لە گومڕایی یەکی ئاشکرادان');
INSERT INTO "bamoki" VALUES(31,12,'31:12','سوێند بەخوا بەڕاستی دانای و حیکمەتماندا بە لوقمان (وپێمانووت) سوپاسی خوا بکە جا ھەرکەس سوپاس وشوکرانە بژێری خوا بکات ئەوە بێگومان شوکرانە بژێری (بۆ قازانج و سوودی) خۆی دەکات ھەرکەسیش ناسوپاسی کردبێ (زیانی بەخوا نەگەیاندووە) ئەوە بێگومان خوا بێ نیازی سوپاسکراوە');
INSERT INTO "bamoki" VALUES(31,13,'31:13','(ئەی موحەممەد صلى الله عليه وسلم) باسی ئەوکاتە بکە کە لوقمان بەکوڕەکەی ووت کە ئامۆژگاری دەکرد (ووتی) ئەی کوڕە شیرینەکەم ھاوبەش بۆ خوا دامەنێ چونکە بەڕاستی ھاوبەش دانان بۆ خوا ستەمێکی زۆر گەورەیە');
INSERT INTO "bamoki" VALUES(31,14,'31:14','وە فەرمانمان داوە بەئادەمی (بەچاکی ھەڵس و کەوت کردن لەگەڵ) دایك و باوکیدا دایکی ھەڵی ئەگرێ بەدووگیانی (پێی لاواز دەبێ) لاوازی لەدوای لاوازی و بڕینەوەی لەشیر لە (دوا تەمەنی) دووساڵیەوە (وە فەرمانمان بەئادەمی دا) کە سوپاسگوزاری من (خوا) وباوک و دایکت بکە تەنھا بۆلای منە گەڕانەوەتان');
INSERT INTO "bamoki" VALUES(31,15,'31:15','وەئەگەر (باوك و دایکت) ئەوپەڕی ھەوڵیاندا لەگەڵت بۆئەوەی شتێك بکەیت بەھاوبەشی من کەھیچ زانیاری یەکت پێی نیە ئەوە گوێڕایەڵییان مەکە بەڵام لەدونیادا زۆر بەچاکی ھاوڕێیەتی وھەڵسوکەوتیان لەگەڵدا بکە وشوێن ڕێبازی کەسێك بکەوە کەبەدڵسۆزی گەڕاوەتەوە بۆلای من (لەدوا ڕۆژدا) گەڕانەوەتان تەنھا بۆلای منە ئەمجا ھەواڵتان دەدەمێ بەو کردەوانەی کەکردوتانە');
INSERT INTO "bamoki" VALUES(31,16,'31:16','(لقمان ووتی) ئەی کوڕە شیرینەکەم بەڕاستی ئەو (چاکە و تاوانە) ئەگەر کێشی تۆ وە خەرتەلەیەکیش بێت لەناو بەردێکدا یان لەناو ئاسمانەکان یان لەزەویدا بێت خوا (لەقیامەت دا) دەیھێنێت بەڕاستی خوا ووردبیین و ئاگادارە');
INSERT INTO "bamoki" VALUES(31,17,'31:17','ئەی کوڕە شیرینەکەم نوێژ بەجێ بھێنە وفەرمان بکە بەچاکە و ڕێگری بکە لەخراپە ھۆگر و بەئارامبە لەسەر ھەر بەڵاو ناخۆشی یەك تووشت دێت بێگومان ئەوە لەوکارە گەورانەیە کە خوا پێویستی کردووە');
INSERT INTO "bamoki" VALUES(31,18,'31:18','ڕوو لەخەڵك وەرمەگێڕە وبەکەشخە و فیزەوە مەڕۆ بەزەویدا چونکە بەڕاستی خوا ھیچ خۆبەزل زانێکی شانازیکەری خۆش ناوێت');
INSERT INTO "bamoki" VALUES(31,19,'31:19','وە لەڕۆشتندا مام ناوەندی بە (نەھێواشبە نەپەلەپەل) (لەقسەکردندا) دەنگت نزم بکەرەوە بەڕاستی ناخۆشترینی دەنگەکان دەنگی (سەڕەی) گوێدرێژە');
INSERT INTO "bamoki" VALUES(31,20,'31:20','ئایا نەتانزانیوە کە بەڕاستی خوا ڕامی ھێناوە بۆتان ئەوەی لەئاسمانەکان و ئەوەی لەزەویدایە وە نیعمەتەکانی ڕشتووە بەسەرتاندا چ دیار و ئاشکرا و چ پەنھان و نادیارە ھەندێك لەئادەمی موجادەلە و دەمەقاڵێ دەکات لەبارەی خواوە بەبێ ھیچ زانیاریەك یان ڕێنمونیەك یان کتێبێکی ڕوون وئاشکرا');
INSERT INTO "bamoki" VALUES(31,21,'31:21','کاتێك پێتان بووترێت شوێن ئەو (قورئانه) بکەون کە خوا ناردویەتە خوارەوە دەڵێن: نەخێر بەڵکو شوێن ئەوە دەکەوین کە باوو باپیرانمان لەسەری بوون ئەگەر چی باوو باپیرانیشیان ھیچ تێ نەگەیشتبن و گومڕا بووبن (ڕێنمایش وەرنەگرن)');
INSERT INTO "bamoki" VALUES(31,22,'31:22','ھەرکەسێك بەڕاستی ڕووبکاتە خوا (ملکەچی فەرمانەکانی ئەو ببێت) و چاکەکار بێت (لەگوفتار و کرداریدا) ئەوە بێگومان دەستی گرتووە بە بەھێزترین دەستە گییرەوە (کە یەکخواپەرستی وئاینی پیرۆزی ئیسلامە) وە سەرئەنجامی ھەموو کارێک تەنھا بۆلای خوایە');
INSERT INTO "bamoki" VALUES(31,23,'31:23','وە ھەرکەس بێ بڕوا بوو، بابێ بڕواییەکەی خەفەتبارت نەکات گەڕانەوەیان تەنھا بۆلای ئێمەیە ئەمجا ھەواڵیان دەدەینێ بەوەی کردوویانە بەڕاستی خوا بەوەی لەناو دڵەکاندایە ئاگادارە');
INSERT INTO "bamoki" VALUES(31,24,'31:24','کەمێك نازو نیعمەتیان پێ دەدەین (لە دونیادا ) لەپاشان ناچاریان دەکەین کە بڕۆنە ناو سزای سەختەوە');
INSERT INTO "bamoki" VALUES(31,25,'31:25','سوێند بەخوا ئەگەر پرسیاریان لێ بکەیت کێ ئاسمانەکان و زەوی دروستکردووە بێگومان دەڵێن: خوا (ئەی موحەممەد صلى الله عليه وسلم) بڵی: سوپاس بۆ خوا (کەدان بەو ڕاستیەدا دەنێن) بەڵام زۆربەیان نازانن');
INSERT INTO "bamoki" VALUES(31,26,'31:26','موڵکی خوایە ئەوەی لەئاسمانەکان و زەویدایە بەڕاستی ھەر خوایە بێ نیازی ستایشکراو');
INSERT INTO "bamoki" VALUES(31,27,'31:27','بێگومان ئەگەر ھەرچی درەخت لەزەویدایە قەلەم و پێنووس بێت ھەموو دەریای زەوی ببێتە مرەکەب حەوت دەریای تر دوای ئەوەی (باسکرا) ببێت بەمرەکەب ووتە و زانراوەکانی خوا دوایی نایەن و تەواو نابن بەڕاستی خوا زاڵی باڵادەست وکار دروستە');
INSERT INTO "bamoki" VALUES(31,28,'31:28','دروستکردنی ھەمووتان (لەسەرەتاوە) و زیندووکردنەوەتان وەك (دروستکردن و زیندوکردنەوەی) تەنھا کەسێك وایە بەڕاستی خوا بیسەر وبینایە');
INSERT INTO "bamoki" VALUES(31,29,'31:29','ئایا نەتزانیوە کەبەڕاستی خوا شەو دەکات بەناو ڕۆژدا وە ڕۆژ دەکات بەناو شەودا وە خۆرو مانگی ڕام ھێنـاوە وخستویەتیە خزمـەتیانەوە ھەر یەکـەیان تا ئـەوماوەیەی (بۆیان) دیاریکراوە، دەگەڕێن ودەسوڕێنەوە وە بەڕاستی خوا بەوەی دەیکەن خەبەردارە');
INSERT INTO "bamoki" VALUES(31,30,'31:30','ئەمە لەبەر ئەوەیە کە بێگومان تەنھا خوا ڕاست و ھەقە و بەڕاستی ئەوەی ھاواری لێدەکەن و دەیپەرستنن بێجگە لەو(خوا) بەتاڵ وناڕاستە وە بێگومان ھەر خوا بڵند و گەورەیە');
INSERT INTO "bamoki" VALUES(31,31,'31:31','ئایا نەتزانیوە (نەتدیوە) کە کەشتیەکان بە چاکە و فەزڵی خوا دەڕۆن لەدەریادا بۆ ئەوەی ھەندێك لەنیشانەکانی خۆیتان نیشان بدات چونکە بەڕاستی لەمەدا (کەباسکرا) چەند بەڵگەیە ھەیە بۆ ھەموو ئارام گرێکی شوکرانە بژێر');
INSERT INTO "bamoki" VALUES(31,32,'31:32','وە ھەرکاتێك (لەدەریادا) شەپۆلێکی وەك ھەورەکان دایان پۆشێ (ترسی خنکان و نوقم بوونیان ببێت) ھاوار لەخوا دەکەن، دڵسۆزانە ھەر بۆ ئەو ملکەچ دەبن ئەمجا کاتی (خوا) ڕزگاری کردن بۆ ووشکانی ئەوسا ھەندێکیان مام ناوەندیە (ھەندێکی تریان کافرە، وەدان نانێ بەچاکەی خوادا) کەس نیشانەکانی ئێمە ئینکار ناکات جگە لە پەیمان شکێنانی ناشوکر و (سپڵە) نەبێت');
INSERT INTO "bamoki" VALUES(31,33,'31:33','ئەی ئادەمی خۆبپارێزن لە (تۆڵەی) پەروەردگارتان لەو ڕۆژەش بترسن ھیچ باوکێك تاوانی رۆڵەکەی ھەڵناگرێت و ھیچ ڕۆڵەیەکیش شتێك لە تاوانی باوکی ھەڵناگرێت بێگومان بەڵێنی خوا ھەق و ڕاستە کەواتە با ژیانی دونیا ھەڵتان نەخەڵەتێنێت وە با شەیتانی فریودەر بە (بیانوی بەزەیی و لێ بوردنی)خوا نەتان خەڵەتێنێت');
INSERT INTO "bamoki" VALUES(31,34,'31:34','بەڕاستی تەنھا لای خوایە زانیاری (کاتی ھاتنی) ڕۆژی دوایی وە ھەر خوا باران دەبارێنێت تەنھا خوا دەزانێ بەوەی لەمناڵدانی (دایکان) دایە وە ھیچ کەس نازانێ سبەینێ (لە داھاتوودا) چی دەکات وە ھیچ کەس نازانێت لە چ شوێن و زەویەکدا دەمرێت بەڕاستی خوا زانا و ئاگادارە');
INSERT INTO "bamoki" VALUES(32,1,'32:1','بـە ( ئـەلیف، لام، میم ) دەخوێنرێتەوە، خوای گەورە زاناترە بە واتاکەی.ئەم پیتانەی کە لەسەرەتای ھەندێک لەسورەتەکانی قورئاندا ھاتوون، تەنھا خوای گەورە دەزانێت مەبەستی پێیان چیە، ئەمە ڕاسترین تەفسیرە بۆ ئەم پیتانە کرابێت، بەڵام بەچەند جۆرێکی تریش تەفسیرکراون لەوانە: ئەم پیتانە ناون بۆ ئەوسورەتانەی لەسەرەتایانەوە ھاتوون، یان ناون بۆ خوای گەورە، یان بۆ دەستەوسانکردنی عەرەبە بێباوەڕەکانە کە دەیانووت ئەم قورئانە لەلایەن خوداوە نەھاتوەتە خوارەوە بۆ موحەممەد (صلى الله عليه وسلم) پێیان دەڵێت: دەی ئەگەر وایە وێنە وھاوشێوەی ئەم قورئانە بھێنن، کە ھەر لەو پیتانە پێـکھاتووە کە ئێوە قسەی پێ دەکەن و بەکاریان دەھێنن');
INSERT INTO "bamoki" VALUES(32,2,'32:2','ھێنانە خوارەوەی ئەم قورئانە ھیچ گومانی تێدانیە لـەلایەن پەروەردگاری جیھانیانەوەیە');
INSERT INTO "bamoki" VALUES(32,3,'32:3','ئـایا (بێ باوەڕەکان) دەڵێن: موحەممەد صلى الله عليه وسلم خۆی ئەم (قورئانەی) ھەڵبەستووە (نەخێر ھەڵی نەبەستووە) بەڵکو ئەو (قورئانە) ڕاستە لەلایەن پەروەردگارتەوە (ھاتووە) بۆ ئەوەی بەو (قورئانە) خەڵکێک بترسێنیت کە لە پێش تۆ ھیچ ترسێنەرێکیان بۆ نەھاتووە بەڵکو ڕێنمونی وەرگرن');
INSERT INTO "bamoki" VALUES(32,4,'32:4','خوا زاتێکە ئاسمانەکان و زەوی و ئەوەی لە نێوانیاندایە بەدیھێناوە لە شەش ڕۆژدا پاشان بەرز بوویەوە بۆ سەر عەرش و قەراری گرت (ئیمانمان پێیەتی وچۆنیەتیەکەی نازانین) بێجگە لەو (خوا) ھیچ پشتیوان و تکا کارێکتان نیە دەی بۆ بیرناکەنەوە و پەند وەرناگرن');
INSERT INTO "bamoki" VALUES(32,5,'32:5','خوا ھەموو کاروبارەکان بەڕێوە دەبات لەئاسمانەوە تا زەوی (ھەتا ڕۆژی دوای) لە پاشان ھەموو ئەو کاروبارە بەرز دەبێتەوە بۆلای خۆی لە ڕۆژێکدا (لەقیامەتدا) کەئەندازەکەی ھەزار ساڵە لەو ساڵانەی ئێوە دەیژمێرن');
INSERT INTO "bamoki" VALUES(32,6,'32:6','ئەو (خوا) زانایە بە ھەموو نھێنی و ئاشکرایەك بە دەسەڵات و میھرەبانە');
INSERT INTO "bamoki" VALUES(32,7,'32:7','زاتێکە ھەموو شتێکی بەجوانترین شێوە دروستکردووە جا مرۆڤی لە سەرەتاوە لە قوڕ دروستکرد');
INSERT INTO "bamoki" VALUES(32,8,'32:8','لەپاشان نەوەی مرۆڤی لە پوختەیە لە ئاوی بێ نرخ و قێزەون دروستکردووە');
INSERT INTO "bamoki" VALUES(32,9,'32:9','لەپاشان ڕێك و پێکی کرد (بەشێوەی ئادەمی) و فوی کرد بە بەریدا لەڕۆحێك کە خۆی دروستی کرد (دانە پاڵی ڕۆح بۆ لای خوا بۆ ڕێزو تەشریفە) گوێ و چاو و دڵی پێبەخشیوون بەڵام زۆر کەم سوپاسی خوا دەکەن');
INSERT INTO "bamoki" VALUES(32,10,'32:10','(بێ بڕواکان) دەڵێن: ئایا کاتێك (مردین و لەشمان ڕزاو) لە ناو زەویدا وون بووین ئایا سەر لەنوێ جارێکی تر دروست دەکرێینەوە ( وزیندوو دەبینەوە) (وانیە) بەڵکو ئەوان باوەڕیان نیە بەخزمەت پەروەردگاریان بگەن (بۆ دادگایی کردنی قیامەت)');
INSERT INTO "bamoki" VALUES(32,11,'32:11','(ئەی موحەممەد صلى الله عليه وسلم) بڵێ فریشتەی گیان کێشان کە گیان کێشانی ئێوەی پێ سپێراوە دەتان مرێنێت لە پاشان بۆ لای پەروەردگارتان دەگەڕێنرێنەوە');
INSERT INTO "bamoki" VALUES(32,12,'32:12','ئەگەر دەبینی کاتێك (لەقیامەتدا) تاوانباران لەحوزوری پەروەردگاریاندا (لەخەجاڵەتیدا) سەریان داخستووە (دەڵێن) ئەی پەروەردگاری ئێمە (ئەوەی بەڵێنت پێ دابووین) دیمان و بیستمان دەی بمانگێڕەرەوە (بۆ دونیا ھەتا) کردەوەی چاك بکەین بەڕاستی ئێمە دڵنیاین (لەقیامەت)');
INSERT INTO "bamoki" VALUES(32,13,'32:13','ئەگەر بمانویستایە ڕێنمونیمان بەھەموو کەسێك دەدا بەڵام بەڵێنی من چەسپاو و جێگیر بووە کە بێگومان دۆزەخ پڕدەکەم لەجنۆکە و ئادەمی بەتێکڕا');
INSERT INTO "bamoki" VALUES(32,14,'32:14','کەواتە (سزای دۆزەخ) بچەژن، چونکە گەیشتن بەم ڕۆژەتان لەبیرکرد بوو بەڕاستی ئێمەش ئێوەمان لەبیرخۆمان بردەوە و سزای بەردەوام بچێژن بەھۆی ئەوەی دەتانکرد (لەدونیادا)');
INSERT INTO "bamoki" VALUES(32,15,'32:15','تەنھا کەسانێك باوەڕ بەئایەتەکانی ئێمە دەھێنن ھەر کاتێك بەو (ئایەتانە) ئامۆژگاری بکرێن ئەکەون بە ڕوودا بەسوژدە بردنەوە و بەپاکی ستایشی پەروەردگاریان دەکەن و خۆیان بەگەورە دانانێن');
INSERT INTO "bamoki" VALUES(32,16,'32:16','تەنیشتیان دوور ئەکەوێتەوە لە جێگای خەوتنیان (واتە: لەخەو ھەڵدەسن بۆ شەو نوێژ کردن) ھاوار و نزا لەپەروەردگاریان دەکەن بەترس و ھیواوە و لەو ڕۆزی و سامانەی پێمان داوون دەبەخشن');
INSERT INTO "bamoki" VALUES(32,17,'32:17','ئەمجا ھیچ کەس نازانێت چی شاراوەتەوە بۆیان لە (بەھەشت دا کە ھۆی) گەشبوونی چاوەکانن ئەم پاداشتە (دەدرێنەوە) بە ھۆی ئەو کردەوانەی کە کردویانە');
INSERT INTO "bamoki" VALUES(32,18,'32:18','دەی ئایا کەسێك ئیماندار بووبێت وەك کەسێکە دەرچووبێت لە ئیمان (وفاسق بووبێت) ھەرگیز چونیەك نین');
INSERT INTO "bamoki" VALUES(32,19,'32:19','جا ئەوانەی کە باوەڕیان ھێناوە و کردەوە چاکەکانیان کردووە ئەوە بەھەشتانێکی پڕ ڕێزو خۆشیان بۆ ھەیە بەھۆی ئەو کردەوانەوەی کەدەیانکرد');
INSERT INTO "bamoki" VALUES(32,20,'32:20','وە ئەوانەی کە فاسق بوون ئەوە جێگایان ئاگرە ھەر کاتێك بیانەوێت لە دۆزەخ دەرچن دووبارە دەیانگێڕنەوە ناو دۆزەخ و پێیان دەوترێت بچەژن سزای ئەو ئاگرەی کە ئێوە باوەڕتان پێ نەدەکرد');
INSERT INTO "bamoki" VALUES(32,21,'32:21','وە بەڕاستی ئێمە سزای دونیایان پێ دەچێژین جگە لە سزای ھەرە گەورە (ی دۆزەخ) تابگەڕێنەوە (بۆ لای خوا)');
INSERT INTO "bamoki" VALUES(32,22,'32:22','وە کێ ستەمکارترە لەو کەسەی بە ئایەتەکانی پەروەردگاری ئامۆژگاری بکرێت دوا ئەوە ڕوو وەرگێڕێت وپشتی تێ بکات بەڕاستی ئێمە تۆڵە سێنەرین لە تاوانباران');
INSERT INTO "bamoki" VALUES(32,23,'32:23','سوێند بەخوا بەڕاستی ئێمە تەوراتمان دا بەموسا ئەمجا گومانت نەبێت (ئەی موحەممەد صلى الله عليه وسلم) لەگەیشتنت بە موسا (یان گومانت نەبێت لەگەیشتنی تەورات بە موسا) وە ئێمە ئەو (تەوراتە) مان کرد بەرێنمونیکاری بەنو ئیسرائیل');
INSERT INTO "bamoki" VALUES(32,24,'32:24','وە لەناو ئەواندا پێشەوایانێکمان دانا ڕێنمونی (خەڵك)یان دەکرد بە فەرمانی ئێمە کاتێك خۆیان گرت و بەئایەتەکانی ئێمە دڵنیابوون (باوەڕی بتەویان ھێنا)');
INSERT INTO "bamoki" VALUES(32,25,'32:25','بێگومان پەروەردگارت بڕیار دەدات لەنێوان (بەندەکانیدا) لەڕۆژی قیامەتدا لەوەی ئەوان تێیدا جیاواز بوون');
INSERT INTO "bamoki" VALUES(32,26,'32:26','ئایا (ڕێگای ڕاست) ڕوون نەبۆتەوە بۆیان کە ئێمە پێش ئەمان زۆر گەل و نەتەوەمان لەناو بردووە ئەمان (بێ باوەڕانی مەککە) بەشوێن و خانووە (وێرانکراوە)کانی ئەواندا ھاتووچۆ دەکەن بەڕاستی لە (تیاچوونی) ئەواندا چەند بەڵگەی گەورە ھەیە دەی ئایا ئەوانە (ئەو ڕاستیانە) نابیستن');
INSERT INTO "bamoki" VALUES(32,27,'32:27','ئایا نابینن و نازانن کە ئێمە ئاو دەنێرین بۆ زەوی ووشکی بێ ڕووەك ئەمجا بە ھۆی ئەو (ئاوەوە) کشتوکاڵ و گژوگیا دەڕوێنین کە ئاژەڵیان لێێ دەلەوەرێت و خۆشیان لێی دەخۆن دەی ئایا ئەوانە نابینن');
INSERT INTO "bamoki" VALUES(32,28,'32:28','وە(بێ باوەڕانی مەککە بە موسڵمانان) دەڵێن ئەم حوکم و بڕیاردانی قیامەتە، کەی دەبێت ئەگەر ئێوە ڕاستگۆن');
INSERT INTO "bamoki" VALUES(32,29,'32:29','(ئەی موحەممەد صلى الله عليه وسلم) بڵێ ڕۆژی بڕیاردانی قیامەت باوەڕ ھینان سوود ناگەیەنێت بەوانەی بێ باوەڕبوون لە دونیادا و مۆڵەتیشیان پێ نادرێت');
INSERT INTO "bamoki" VALUES(32,30,'32:30','جا (ئەی موحەممەد صلى الله عليه وسلم) ڕوویان لێ وەرگێڕە و گوێیان مەدەرێ وە تۆ چاوەڕوانی بکە بێگومان ئەوانیش چاوەڕوانن');
INSERT INTO "bamoki" VALUES(33,1,'33:1','ئەی پێغەمبەر صلى الله عليه وسلم (بەردەوام) لە خوا بترسە بەگوێی کافران ودووڕوەکان مەکە بێگومان خوا زانا و کار بەجێیە');
INSERT INTO "bamoki" VALUES(33,2,'33:2','وە شوێن ئەوە بکەوە کە لە پەروەردگارتەوە وەحیت بۆکراوە بەڕاستی خوا بەوەی کەدەیکەن ئاگادارە');
INSERT INTO "bamoki" VALUES(33,3,'33:3','پشت ھەر بەخوا ببەستە وە خوا بەسە پارێزەر بێت');
INSERT INTO "bamoki" VALUES(33,4,'33:4','خوا داینەناوە بۆ ھیچ پیاوێك (ئادەمیەك) دوو دڵ لە ناوسنگیدا وە(خوا) ئەو ھاوسەرانەی نەکردۆتە دایکتان کە (ظھار) یان لەگەڵ دەکەن (پێیان دەڵێن ئێوە وەك پشتی دایکمان وان بۆ ئێمە) وەخوا بە کوڕ کراوەکانتانی نەکردۆتە کوڕتان ئەوە قسەی سەر زارەکیتانە خواش ووتەی ڕاست دەڵێ ھەر ئەویش ڕێنمونی(خەڵك) دەکات بۆ رێگای ڕاست');
INSERT INTO "bamoki" VALUES(33,5,'33:5','(ئەو بە کوڕکراوانە) بەناوی باوکیانەوە بانگیان بکەن ئەوە ڕاسترە بەلای خواوە ئەمجا ئەگەر باوکیان نەناسن (تا بەناویانەوە بانگیان بکەن) ئەوانە برای ئاینی و دۆست و خۆشەویستی ئێوەن ھیچ گوناھێکتان لەسەر نیە لەو (بانگ کردنەدا) کە بە نەزانین گوتبێـتان (پێش ھاتنی ئەم ئایەتە) بەڵام (گوناھبار دەبن) ئەگەر بەدڵ و بەدەس ئەنقەست بیکەن وە خوا لێبوردە و میھرەبانە');
INSERT INTO "bamoki" VALUES(33,6,'33:6','پێغەمبەر صلى الله عليه وسلم لە خودی بڕواداران بۆ خۆیان لە پێشترە خێزانەکانیشی دایکی باوەڕدارانن و ئەوانەی (بە نەسەب) خزمی یەکترن (ئەگەر موسڵمان بن) لەقورئانی خوا بۆیەکتری لە پێشترن (میرات لە یەکتر بەرن) لە ئیمانداران و کۆچەران (کە خزم نین) مەگەر خۆتان چاکەیەك (بەخشین یان وەسێتێك) بۆ دۆست و خۆشەویستانتان بکەن ئەو بڕیارە لە قورئان (یان لە لوح المحفوظ دا) نوسراوە');
INSERT INTO "bamoki" VALUES(33,7,'33:7','(ئەی موحەممەد صلى الله عليه وسلم بیریان بخەرەوە) کاتێك کە پەیمانمان وەرگرت لە پێغەمبەران ھەروەھا لە تۆ ولەنوح ولەئیبراھیم و لەموسا و لەعیسای کوڕی مەریەم وە لە ھەموویان پەیمانی بەھێزو بتەومان وەرگرت');
INSERT INTO "bamoki" VALUES(33,8,'33:8','بۆ ئەوەی (خوا) پرسیار لەڕاستگۆیان بکات دەربارەی ڕاستییان و سزای پڕ ئێش و ئازاریشی ئامادە کردووە بۆ بێ باوەڕان');
INSERT INTO "bamoki" VALUES(33,9,'33:9','ئەی ئەو کەسانەی کە باوەڕتان ھێناوە بیر لەچاکە و نیعمەتی خوا بکەنەوە بەسەرتانەوە کاتێك لە شکرێکی زۆرتان بۆ ھات (لە جەنگی خەندەقدا) ئەمجا ئێمە بایەکی بەھێزمان بۆ ناردن و لەشکری (فریشتەش) کە ئێوە نەتان بینین وەخوا بەوەی ئێوە دەیکەن بینایە');
INSERT INTO "bamoki" VALUES(33,10,'33:10','کاتێك (بێ باوەڕان) ھێرشیان بۆ ھێنان لە سەرووتانەوە و لە خوارتانەوە (چوار دەوری مەدینەیان گرت) وە چاوەکان ئەبڵەق بوون و دڵەکان (لە ترسا) گەیشتبوونە گەروو و گومانی جۆربەجۆرتان بە خوا دەبرد');
INSERT INTO "bamoki" VALUES(33,11,'33:11','لەوێدا ئیمانداران تاقیکرانەوە وە ڕاوەشێنران بەڕاوەشاندنێکی توند');
INSERT INTO "bamoki" VALUES(33,12,'33:12','(بیربکەنەوە) کاتێك دوو ڕووەکان وئەوانەی لە دڵیاندا نەخۆشی ھەبوو ووتیان خواو پێغەمبەرەکەی بێجگە لە تەفرەدان ھیچ بەڵێنێکیان پێنەداین');
INSERT INTO "bamoki" VALUES(33,13,'33:13','وە کاتێك دەستەیە لەو (دووڕووانە) ووتیان: ئەی دانیشتوانی مەدینە ئێرە جێی مانەوەتان نیە دەی بگەڕێنەوە وە کۆمەڵێکی تر لەوان مۆڵەتی گەڕانەوە لەپێغەمبەر صلى الله عليه وسلم دەخوازن دەیانووت: خانووەکانمان بێ پارێزەر و ڕووت و کەلێناویە لەکاتێکدا واش نەبوو (کەدەیانووت) ئەوان تەنھا مەبەستیان ھەڵھاتن بوو (لەجیھادکردن)');
INSERT INTO "bamoki" VALUES(33,14,'33:14','وە ئەگەر ھێرش بھێنرایەتە سەریان لە ھەموو لایەکی (مەدینە)وە ئەمجا داوای کافربوون و جەنگ کردنیان لێ بکرایە (دژ بەتۆ ئەی موحەممەد صلى الله عليه وسلم) داواکەیان بەجێ دەھێنا (و بەگوێیان دەکردن) وە ئەم داوایان دوانەدەخست ماوەیەکی کەم نەبێت');
INSERT INTO "bamoki" VALUES(33,15,'33:15','بێگومان ئەوانە لەپێش ئەم (ڕوداوە) پەیمانیان بە خوا دابوو کە (لە غەزادا) پشت ھەڵناکەن و ڕاناکەن وە پەیمانێك درا بە خوا (خاوەنەکەی) لێپرسراو دەبێت');
INSERT INTO "bamoki" VALUES(33,16,'33:16','(ئەی موحەممەد صلى الله عليه وسلم بەدووڕووەکان) بڵێ ھەڵھاتن سوودتان پێ ناگەیەنێت ئەگەر لەمردن یاکوشتن ھەڵبێن (خۆتان بدزنەوە) جا لەوکاتەدا بەھرەو خۆشی ژیانتان پێ نادرێت ماوەیەکی کەم نەبێت');
INSERT INTO "bamoki" VALUES(33,17,'33:17','بڵێ کێیە ئەو کەسەی ئێوە لەخوا دەپارێزێت؟ ئەگەر خوا زیان و خراپە، یان بەزەیی و ڕەحمەتی بۆ ئێوە بوێت بێجگە لەخوا بۆ ئەوان نیە ھیچ پشتیوان و یارمەتیدەرێك');
INSERT INTO "bamoki" VALUES(33,18,'33:18','بێگومان خوا دەزانێ بەو کەسانەتان کەئاستەنگ دروست دەکات لەڕێگەی (موسڵمانان بۆجیھاد کردن) کە بەبرادەرەکانیان دەڵێن: وەرن بۆلای ئێمە (وخۆیان دەدزیەوە لەجیھاد) ئەوانە نایەن بۆ جەنگ و غەزا زۆر بەکەمی نەبێت');
INSERT INTO "bamoki" VALUES(33,19,'33:19','لەئاستی ئێوەدا (ئەی موسڵمانان ئەوانە) چرووکن ئەمجا کاتێك (بەھۆی جەنگەوە) ترس و بیم پەیدا بێت دەیان بینی بەجۆرێ سەیرت دەکەن (لەترسی جەنگ) چاویان بەم لاولادا دەسوڕێت بـەوێنەی کەسێك لەسەرەمەرگدا بێھۆشی بەسەردا بێت بەڵام کاتێك ترسەکە ڕۆشت ونەما بەزمانی تیژ زمانتان لێ درێژ دەکەن (وە داوای غەنیمەتتان لێ دەکەن) کەچی چرووك و ڕژدن لەسەرماڵ وغەنیمەت ئائەوانە باوەڕیان نەھێنابوو بۆیە خوا کردەوەیانی پوچەڵ کردنەوە وەئەمە بەلای خواوە (کارێکی) ئاسانە');
INSERT INTO "bamoki" VALUES(33,20,'33:20','وایان دەزانی کۆمەڵەکان (لەشکریان ھێنابوو) نەڕۆشتوون وە ئەگەر ئەو کۆمەڵانە بگەڕێنەوە (بۆسەر مەدینە) ئاواتیان دەخواست کە لەناو (مەدینە) عەرەبە دەشتەکیەکان بەدوور بوونایە (لەترسی جەنگ) لەدوورەوە پرسیاریان لەھەواڵی ئێوە بکردایە وە ئەگەر لەناو ئێوەدا بوونایە جەنگیان نەدەکرد زۆر بەکەمی نەبێت');
INSERT INTO "bamoki" VALUES(33,21,'33:21','سوێند بەخوا بەڕاستی لەپێغەمبەری خوادا صلى الله عليه وسلم بۆ ئێوە ھەیە سەرمەشقی وپێشەوایەتی چا بۆ کەسێك بەھیوای خواو (پاداشتی) ڕۆژی دواییە وە زۆر یادی خوا دەکات');
INSERT INTO "bamoki" VALUES(33,22,'33:22','وەکاتێك ئیمانداران ئەو کۆمەڵانەیان بینی (لەشکریان ھێنا بوو بۆ سەر مەدینە) ووتیان ئەمە ئەو بەڵێنەیە خوا و پێغەمبەرەکەی بە ئێمەیان دابوو خوا و پێغەمبەرەکەی ڕاستیان فەرموو (ھاتنی ئەو ھەموو ھێزەی کوفر) باوەڕداران تەنھا ئیمان و ملکەچیان پێی زیادی کرد');
INSERT INTO "bamoki" VALUES(33,23,'33:23','لەناو باوەڕداران دا پیاوانێك ھەن ڕاستیان کرد لەگەڵ ئەو پەیمانەی کە دابویان بەخوا ئەمجا ھەندێکیان نەزر و بەڵێنی خۆی بەجێ ھێناو (شەھید بوو) ھەندێکیشیان چاوەڕوانن ( وھێشتا لەژیاندان) ھیچ گۆڕانێکیان (لەبەڵێنی خۆیاندا) نەکردووە');
INSERT INTO "bamoki" VALUES(33,24,'33:24','بۆ ئەوەی (خوا) پاداشتی ڕاستگۆیان بداتەوە بەھۆی ڕاستیەکەیانەوە وە سزای دووڕووەکان بدات ئەگەر بیەوێت یان تەوبەیان لێ گیرا کات بێگومان خوا لێبوردە و میھرەبانە');
INSERT INTO "bamoki" VALUES(33,25,'33:25','وە خوا کافرەکانی (قورەیش کە لەشکریان ھێنابوو بۆ سەر مەدینە) گەڕاندەوە بەڕق و مەراقەوە (بۆ وڵاتی خۆیان) ھیچ خێر و ئاواتێکیان (لەناوبردنی ئیمانداران) دەست نەکەوت خوا ئیمان دارانی لەجەنگ پاراست (بەھۆی بای توند و فریشتەوە کافرانی پرژو بڵاوکرد) وەخوا ھەمیشە بەھێز و بەدەسەڵاتە');
INSERT INTO "bamoki" VALUES(33,26,'33:26','(بەنی قورەیزە) کەپشتگیری ئەوان (قوڕەیش و غەطفان)یان کرد خوای گەورە ھێنانیە خوارەوە لەقەڵاکانیان وە ترسی خستە ناو دڵیانەوە کۆمەڵێکیان دەکوژن وە کۆمەڵێکی تریان بەدیل دەگرن');
INSERT INTO "bamoki" VALUES(33,27,'33:27','(دوای تیاچوونی ئەوان خوا) زەوی و خانوو سامانی ئەوانی دا بەئێوە ھەروەھا زەویەکیش کەپێتان پیانەنابوو وەخوا بەسەر ھەموو شێکدا بەتوانایە');
INSERT INTO "bamoki" VALUES(33,28,'33:28','ئەی پێغەمبەر صلى الله عليه وسلم بەخێزانەکانت بڵێ ئەگەر ئێوە ژیانی دونیا و ڕازاوەی ئەوتان دەوێت دەی ھەمووتان وەرن موتعەتان پێ بدەم (موتعە پارە و شتومەکێکە دەدرێتە ئافرەتی تەڵاقدراو) وە تەڵاقتان بدەم بە تەڵاق دانێکی چاك و بێ زیان');
INSERT INTO "bamoki" VALUES(33,29,'33:29','وە ئەگەر ئێوە خواو پێغەمبەرەکەی و پاداشتی ڕۆژی دواییتان دەوێت ئەوە بێگومان خوا ئامادەی کردووە بۆ چاکەکاران لەئێوە پاداشتێکی گەورە');
INSERT INTO "bamoki" VALUES(33,30,'33:30','ئەی خێزانەکانی پێغەمبەر صلى الله عليه وسلم ھەر کام لە ئێوە گوناھێکی گەورە بکات سزای بۆ دوو بەرابەر دەکرێتەوە بەدوو قاتی (خەڵکی تر) وەئەم کارەش بۆ خوا ئاسانە');
INSERT INTO "bamoki" VALUES(33,31,'33:31','(ئەی خێزانەکانی پێغەمبەر صلى الله عليه وسلم) ھەر کام لەئێوە لەسەر گوێرایەڵی خواو پێغەمبەرەکەی بەردەوام بێت وە کردەوەی چاکیش بکات دووجار پاداشتی پێ دەدەین وە ڕۆزییەکی چاك و خۆشمان بۆ ئامادە کردووە (لەبەھەشت دا)');
INSERT INTO "bamoki" VALUES(33,32,'33:32','ئەی ژنانی پێغەمبەر صلى الله عليه وسلم ئێوە وەك ھیچ یەك لەئافرەتانی دیکە نین ئەگەر خۆتان بپارێزن و لەخوا بترسن کەواتە بەخەمزەو نازەوە قسە مەکەن (وەك ئافەرەتانی ڕەوشت نزم) نەوەکو ئەوەی کەدڵی نەخۆشە (دەرون نزمە) نیازی خراپ بکات (کەواتە) قسەی چاك و بەجێ بکەن');
INSERT INTO "bamoki" VALUES(33,33,'33:33','وە لە ماڵەکانی خۆتاندا بمێننەوە وە زینەت و جوانی خۆتان دەرمەخەن (لەناو خەڵکی نامەحرەمدا) وەکو خۆ دەرخستنی ئافرەتانی سەردەمی نەفامی یەکەم (پێش ئیسلام) نوێژ بکەن و زەکات بدەن وە گوێڕایەڵی خواو پێغەمبەرەکەی بکەن بەڕاستی خوا دەیەوێت ئەی ئال و بەیتی (پێغەمبەر صلى الله عليه وسلم) ھەر جۆرە پیسی و گوناھێك لەئێوە دووربخاتەوە و پاك و خاوێنتان بکاتەوە بەتەواوی');
INSERT INTO "bamoki" VALUES(33,34,'33:34','وە بیربکەنەوە لەوەی دەخوێنرێت لەماڵەکانتاندا لەئایەتەکانی خواو دانایی (فەرمودەکانی پێغەمبەر صلى الله عليه وسلم) بەڕاستی خوا ووردبینی ئاگادارە');
INSERT INTO "bamoki" VALUES(33,35,'33:35','بەڕاستی پیاوە موسڵمانەکان وئافرەتە موسڵمانەکان وە پیاوە باوەڕدارەکان و ئافرەتە باوەردارەکان و پیاوانی ھەمیشە گوێڕایەڵ و ئافرەتانی ھەمیشە گوێرایەڵ و پیاوە ڕاستگۆکان و ئافرەتە ڕاستگۆکان و پیاوان و ئافرەتانی خۆگر و بەئارام وە پیاوانی لەخوا ترس و ژنانی لەخوا ترس و پیاوان و ئافرەتانی بەخشندە و پیاوان و ئافرەتانی ڕۆژوەوان، پیاوان و ئافرەتانی داوێن پاك، پیاوانێ کە زۆر یادی خوا دەکەن و ئافرەتانێ کە زۆر یادی خوا دەکەن خوا ئامادەی کردووە بۆ ھەموویان لێخۆشبوون و پاداشتی گەورە');
INSERT INTO "bamoki" VALUES(33,36,'33:36','بۆ ھیچ پیاوو ئافرەتێکی باوەڕدار نیە کاتێك خواو پێغەمبەرەکەی بڕیاری ھەر کارێکیان دا (بۆیان نیە) کەسەرپشك بن لەکارەکەیاندا وە ھەر کەس سەرپێچی خواو پێغەمبەرەکەی بکات ئەوە بەڕاستی گومڕا بووە بەگومڕا بونێکی ئاشکرا');
INSERT INTO "bamoki" VALUES(33,37,'33:37','(بیربکەرەوە) کاتێك دەتووت بەکەسێك (زەیدی کوڕی حاریثە) کەخوا نیعمەتی بەسەردا ڕشتبوو، تۆیش چاکەت لەگەڵدا کردبوو (بەئازاد کردنی و پەروەردەکردنی) خێزانت (زەینەب) لای خۆت ڕاگرە و بیھێڵەوە وە لەخوا بترسە لەدڵی خۆتدا شتێك دەشاریتەوە کەخوا ئاشکرای دەکات وە لە(قسەی) خەڵك دەترسی لەکاتێکدا خوا شیاوترە کەلێی بترسی ئەمجا کاتێ زەید پێویستی بە (زەینەب)نەما و تەڵاقیدا ئێمە (زەینەب)مان لێ مارەکردیت بۆ ئەوەی ھیچ ناڕەحەتی (وتاوانێ) نەبێت لەسەر ئیمانداران لە(مارەکردنی) ژنانی بەکوڕ کراوەکانیاندا کاتێك پێویستیان پێیان نەماو تەڵاقیان دان وە فەرمانی خوا (بەمارە کردنی زەینەب بۆت) دراوە و بڕاوەیە');
INSERT INTO "bamoki" VALUES(33,38,'33:38','ھیچ ناڕەحەتی (تاوان)ێك لەسەر پێغەمبەر صلى الله عليه وسلم نیە لە (مارە کردنی زەینەب) کەخوا بڕیاری داوە بۆی باوو سوننەت ورێگای (دانراوی) خوایە لە ناو پێغەمبەرانی لەمەوپێشدا وە فەرمانی خواش بڕیارێکی بڕاوەیە (ودەبێت جێ بەجێ بکرێت)');
INSERT INTO "bamoki" VALUES(33,39,'33:39','کەسانێك کەپەیامەکانی خوا ڕادەگەیەنن وە لە(خوا) دەترسن وبێجگە لەخوا لەکەسی تر ناترسن خوا بەسە کە چاودێر ولێپرسرەوە بێت');
INSERT INTO "bamoki" VALUES(33,40,'33:40','موحەممەد صلى الله عليه وسلم باوکی ھیچ یەك لەپیاوەکانتان نیە (نەزەیدی کوڕی حاریثە و نەھیچ کەسێکی تر) بەڵکو ئەو نێرداروی خواو کۆتاھەمینی پێغەمبەرانە وە خوا بەھەموو شتێك زانایە');
INSERT INTO "bamoki" VALUES(33,41,'33:41','ئەی ئەوانەی باوەڕتان ھێناوە زۆر یادی خوا بکەن');
INSERT INTO "bamoki" VALUES(33,42,'33:42','بەپاکی یادی خوا بکەن بەبەیانیان و ئێواراندا');
INSERT INTO "bamoki" VALUES(33,43,'33:43','ئەو زاتێکە بەزەیی خۆی دەڕژێنێت بەسەرتاندا و فریشتەکانی داوای لێبوردنتان بۆ دەکەن بەو ھۆیەوە (خوا) لەتاریکیەکانەوە دەرتان بھێنێت بەرەو نوورو ڕووناکی (ئیمان و ئیسلام) وە خوا بە بەزەییە بەئیمانداران');
INSERT INTO "bamoki" VALUES(33,44,'33:44','ڕۆژێك باوەڕداران دەگەنە خزمەت ئەو (خوا) بەخێرھاتنیان بە(ووشەی) سەلامە وە خوا پاداشتی چاکی بۆ ئامادە کردوون');
INSERT INTO "bamoki" VALUES(33,45,'33:45','ئەی پێغەمبەر صلى الله عليه وسلم بێگومان ئێمە تۆمان ناردووە (بۆ خەڵکی) بەشایەت و مژدەدەرو ترسێنەر');
INSERT INTO "bamoki" VALUES(33,46,'33:46','و بەبانگخواز بۆ (ڕێگەی) خوا بەفەرمانی ئەو و چرایەکی ڕووناك کەرەوە');
INSERT INTO "bamoki" VALUES(33,47,'33:47','وە مژدە بدە بەئیمانداران کەبێگومان بۆ ئەوان ھەیە بەھرە و بەخششێکی گەورە لەلایەن خواوە');
INSERT INTO "bamoki" VALUES(33,48,'33:48','گوێرایەڵی کافران و دوو ڕووان مەکە گوێ مەدە بەئازاردانی ئەوان (بۆ تۆ) وپشت ھەر بەخوا ببەستە وە خوا بەسە کەپشتیوان بێت');
INSERT INTO "bamoki" VALUES(33,49,'33:49','ئەی ئەوانەی کەباوەڕتان ھێناوە کاتێك ئافرەتانی ئیماندارتان مارەکرد لەپاشان لەپێش ئەوەی بچنە لایان تەڵاقتاندان ئەوە لەسەریان نیە بۆ ئێوە ھیچ عیددەیەك ھەڵژمێرن کەواتە موتعەیان (پۆشاك و نوێن بۆ دڵخۆشیان) بدەنێ و تەڵاقیان بدەن بە تەڵاقدانێکی جوان و ڕێك وپێك');
INSERT INTO "bamoki" VALUES(33,50,'33:50','ئەی پێغەمبەر صلى الله عليه وسلم بێگومان ئێمە حەڵاڵمان کرد بۆ تۆ ئەو ھاوسەرانەت کەمارەییت پێ داوون ھەروەھا ئەو کەنیزەکانەی کەخوا دەستی تۆی خستن (بەھۆی زاڵ بوونت بەسەر کافراندا) و (بۆمان حەڵاڵ کردویت) کچانی مامت و کچانی پورت (خوشکی باوکت) و کچانی خاڵت و کچانی پورت (خوشکی دایکت) ئەوانەی کەکۆچیان کرد لەگەڵت ھەروەھا ئافرەتێکی ئیماندار ئەگەر خۆی ببەخشێ بەپێغەمبەر (بەبێ مارەیی) ئەگەر پێغەمبەر صلى الله عليه وسلم وویستی مارەی بکات (ئەمەیان) تەنھا بۆ تۆ دروستە بەجیا لەئیماندارانی تر بەڕاستی ئێمە دەزانین چیمان پێویست کردووە لەسەر ئیمانداران دەربارەی ھاوسەرکانیان و کەنیزەکەکانیان لەبەر ئەوەی ھیچ لەلات زەحمەت و گران نەبێ (کەشتانێکت بەجیا لە موسڵمانان بۆ دروستە) وە خوا لێبوردە ومیھرەبانە');
INSERT INTO "bamoki" VALUES(33,51,'33:51','(ئەی موحەممەد صلى الله عليه وسلم) سەرەی ھەریەکێك لەخێزانەکانت وویست دوای بخە و ھەریەکێ لەوانت وویست (پێشی بخەو) بیھێنە بۆ لای خۆت وە ھەرکامت بوێت (بێتە لات) لەوانەی بەرکەنارت کردوون لەوەوپێش ھیچ گوناھێکت لەسەر نیە ئەو (سەرپشك کردنە) نزیکترە کەچاوو دڵیانی پێگەش بێـتەوە و خەفەت نەخۆن و ھەموویان ڕازی بن بەوەی کەپێت داوون وە خوا دەزانێت بەوەی کە لەدڵتاندایە وە خوا زانا ولەسەر خۆیە');
INSERT INTO "bamoki" VALUES(33,52,'33:52','دوای ئەمانە (کە ھاوسەرتن) ھیچ ئافرەتێکت بۆ حەڵاڵ نیە و ناشتوانی بیان گۆڕی بەھاوسەرانی تر ھەرچەندە بە جوانیان سەرسام بیت (ڕێك و پێکیان سەرنجت رابکێشێ) مەگەر وەك کەنیزەك ببیتە خاوەنی وەخوا بەسەر ھەموو شتێکدا چاودێرە');
INSERT INTO "bamoki" VALUES(33,53,'33:53','ئەی ئەوانەی باوەڕتان ھێناوە مەچنە ژوورەوە بۆ ماڵەکانی پێغەمبەر صلى الله عليه وسلم مەگەر ڕێگەتان پێ بدرێت بۆ نان خواردنێك بێ ئەوەی چاوەڕوانی پێگەیشتنی بکەن بەڵام ئەگەر بانگ کران (بۆنان خواردن) ئەوسا بڕۆنە ژوورەوە ئەمجا ئەگەر خواردنەکەتان خوارد بڵاوەی لێ بکەن وە مەمێننەوە بۆ قسە کردن چونکە بەڕاستی ئەو (مانەوەتان) ئازاری پێغەمبەر (صلى الله عليه وسلم ) دەدات ئەویش شەرم دەکات لێتان (کە بێ دەنگە) بەڵام خوا لە(ووتنی) ھەق شەرم ناکات وە ھەر کاتێك داوای شتێکی ناوماڵتان کرد لە (ھاوسەرەکانی پێغەمبەر صلى الله عليه وسلم) ئەوە لەپشتی پەردەوە لێیان داواکەن ئەو (شێوە داوا کردنە)تان خاوێن ترە بۆ دڵی ئێوەو ئەوانیش بە ھیچ شێوەیە بۆ ئێوە دروست نیە ئازاری (دڵی) پێغەمبەری خوا بدەن وە ھەرگیز نابێت خێزانەکانی پاش خۆی مارە بکەن بەڕاستی ئەو (ئازاردانی دڵ و مارەکردنە)تان بەلای خواوە (گوناھێکی) زۆر گەورەیە');
INSERT INTO "bamoki" VALUES(33,54,'33:54','ئەگەر ھەر شتێك ئاشکرا بکەن (بیڵێن) یان بیشارنەوە (لە دڵتاندا) ئەوە بەڕاستی خوا زانایە بەھەموو شتێك');
INSERT INTO "bamoki" VALUES(33,55,'33:55','(ھاوسەرانی پێغەمبەر صلى الله عليه وسلم) ھیچ گوناھێکیان لەسەر نیە (ئەگەر بێ حیجاب بن) لای باوکیان وکوڕانیان وبراکانیان وبرازاکانیان وخوشکەزاکانیان ولای ئافرەتانی ھاودینیان ولای غولام وکەنیزەکەکانی خۆیان وە لەخوا بترسن بەڕاستی خوا چاودێر و ئاگادارە بەسەر ھەموو شتێکدا');
INSERT INTO "bamoki" VALUES(33,56,'33:56','بەڕاستی خوا وفریشتەکانی سەڵاوات دەدەن لەسەر پێغەمبەر صلى الله عليه وسلم ئەی ئەوانەی کەباوەڕتان ھێناوە ئێوەش سەڵاوات و سەلامی زۆری لەسەر بدەن');
INSERT INTO "bamoki" VALUES(33,57,'33:57','بەڕاستی کەسانێك کەئازاری خواو پێغەمبەرەکەی دەدەن خوا نەفرەتی لێکردوون لەدونیا و لەدواڕۆژدا وە سزای ڕیسواکەری بۆ ئامادەکردون');
INSERT INTO "bamoki" VALUES(33,58,'33:58','وە کەسانێك کەئازاری پیاوان و ئافرەتانی ئیماندار ئەدەن بێ ئەوەی ھیچ تاوانێکیان کردبێت ئەوە بەڕاستی ئەوانە بوھتان و تاوانێکی ئاشکرایان گرتۆتە ئەستۆ');
INSERT INTO "bamoki" VALUES(33,59,'33:59','ئەی پێغەمبەر (صلى الله عليه وسلم) بڵێ بە ھاوسەرانت وبەکچانت و بە ئافرەتانی بڕواداران با عابا وباڵاپۆشەکانیان بدەن بەسەر خۆیاندا (خۆیان و جوانیانی پێ داپۆشن) ئەوە نزیکترە بناسرێن (بەداوێن پاک) تا بەھۆیەوە ئازار نەدرێن وە خوا لێبووردە و میھرەبانە');
INSERT INTO "bamoki" VALUES(33,60,'33:60','بەڕاستی ئەگەر کۆتایی نەھێنن دووڕووان وە کەسانێك لەدڵیاندا نەخۆشی ھەیە و پڕوپاگەندەچیەکان کە لەمەدینەن ئێمە تۆ ھان دەدەین (بەجەنگ کردن) لەدژیان پاشان ماوەیەکی کەم نەبێت (لەمەدینەدا) دراوسێت نابن (دەردەکرێن)');
INSERT INTO "bamoki" VALUES(33,61,'33:61','(ئەوانە) نەفرەت لێکراون لەھەر کوێیەك بەر دەستت کەوتن بەدیل دەگیرێن و بەکوشتن دەکوژرێن');
INSERT INTO "bamoki" VALUES(33,62,'33:62','خوا (ئەوەی کردووە) بەسوننەت و باوی (خۆی) لەناو کەسانێك کەپێشتر بوون ھەرگیز باوو سوننەتی خوا گۆڕانی بۆ نیە');
INSERT INTO "bamoki" VALUES(33,63,'33:63','(ئەی موحەممەد صلى الله عليه وسلم) خەڵك پرسیارت لێ دەکەن دەربارەی (بەرپابوونی) ڕۆژی دوایی بڵێ زانینی (بەرپابوونی) ئەو ڕۆژە تەنھا لای خوایە تۆ چوزانیت لەوانەیە ڕۆژی دوایی نزیك بێت (بەڵام خەڵك نازانن)');
INSERT INTO "bamoki" VALUES(33,64,'33:64','بەڕاستی خوا نەفرەتی لە بێ باوەڕان کردووە و ئاگری ھەڵگیرساوی بۆ ئامادە کردوون');
INSERT INTO "bamoki" VALUES(33,65,'33:65','بەھەمیشەیی تێیدا دەمێنەوە ھیچ پشتیوان و یاریدەدەرێکیان نیە');
INSERT INTO "bamoki" VALUES(33,66,'33:66','ڕۆژێك کەدەم وچاویان لەئاگری دۆزەخدا ئەم دیوو ئەو دیو دەکرێت دەڵێن: خۆزگە وکاشکای گوێرایەڵی خواو گوێرایەڵی پێغەمبەرمان بکردایە');
INSERT INTO "bamoki" VALUES(33,67,'33:67','وە دەڵێن: ئەی پەروەردگارمان بەڕاستی ئێمە بەگوێی سەران و گەورەکانمان کرد ئیتر لەڕێگای ڕاست گومڕایان کردین');
INSERT INTO "bamoki" VALUES(33,68,'33:68','ئەی پەروەردگارمان دوو بەرابەر سزایان بدە و بەر نەفرەتی گەورەی خۆتیان بخەیت');
INSERT INTO "bamoki" VALUES(33,69,'33:69','ئەی ئەوانەی باوەڕتان ھێناوە وەك ئەو کەسانە مەبن کە موسایان ئازاردا (تۆمەتیان بۆ کرد) جا خوا موسای پاك کردەوە لەوەی دەیانووت (موسا) لای خوا ڕێزو پلە وپایەی ھەبوو');
INSERT INTO "bamoki" VALUES(33,70,'33:70','ئەی ئەوانەی باوەڕتان ھێناوە لەخوا بترسن و پارێزکار بن وقسەی ڕاست و دروست بکەن');
INSERT INTO "bamoki" VALUES(33,71,'33:71','کردەوەکانتان چاك دەکات ولەتاوانەکانتان خۆش دەبێت و ھەر کەس گوێرایەڵی خواو پێغەمبەرەکەی بکات ئەوە بەڕاستی سەرکەوتنێکی گەورەی بەدەست ھێناوە');
INSERT INTO "bamoki" VALUES(33,72,'33:72','بێگومان ئێمە سپاردەمان نیشانی ئاسمانەکان و زەوی و چیاکان دا (ھەڵگرتنی ئەمانەتی دینداریمان پێشنیار کرد بۆیان) بەڵام ھیچیان ڕازی نەبوون کەھەڵیگرن و ترسان لەو (ئەمانەتە) کەچی ئادەمی ئەو(سپاردەی) ھەڵگرت بەڕاستی ئەو (ئادەمی) زۆر ستەمکار و نەزانە');
INSERT INTO "bamoki" VALUES(33,73,'33:73','بۆ ئەوەی خوا سزای پیاوان و ئافرەتانی دوو ڕوو بدات وە (سزای) پیاوان و ئافرەتانی ھاوەڵـدانەریش( بدات) وە لە پیاوان و ئافرەتانی ئیماندار تەوبە وەرگرێت وەخوا لێبوردەی میھرەبانە');
INSERT INTO "bamoki" VALUES(34,1,'34:1','ھەموو سوپاس وستایش بۆ خوایە کە ھەرچی لە ئاسمانەکان و زەویدایە تەنیا ھی ئەوە وە لەو دواڕۆژیشدا ستایش ھەر بۆ ئەوە وە ئەو( خوا) دروست کاری ووردبینە');
INSERT INTO "bamoki" VALUES(34,2,'34:2','خوا دەزانێ چی دەچێ بە ناو زەویدا و چی لێ دەردەچێ و چی دێتە خوارەوە لە ئاسمان و چی بۆ سەر دەکەوێ وەخوا میھرەبانی لێبوەردەیە');
INSERT INTO "bamoki" VALUES(34,3,'34:3','بێ باوەڕان وتیان: ڕۆژی دواییمان بەسەردا نایەت (ئەی موحەممەد(ێلی اللە علیە وسلم)) بڵێ (وانیە) سوێند بە پەروەردگارم ئەو (قیامەتە) تان بۆ دێ (خوا) زانای ھەموو پەنھانەکانە لەخوا گوم نابێ قورسایی گەردیلەیەک لە ئاسمانەکان و زەویدا وە بچوکتر و گەورەتر لەوە نییە ئیللا لە کتێبێکی ئاشکراو ڕووندایە');
INSERT INTO "bamoki" VALUES(34,4,'34:4','بۆ ئەوەی پاداشتی ئەوانەی باوەڕیان ھێناوەو کردەوەی چاکیان کردووە بداتەوە بۆ ئەوانە هەیە لێبوردنی (پەروەدگار) و پاداشت و ڕۆزییەکی چاك');
INSERT INTO "bamoki" VALUES(34,5,'34:5','وە کەسانێك کەھەوڵ دەدەن باوەڕ بە نیشانەکانی ئێمە نەکرێ، دەستە پاچەن وهیچیان پێ ناكرێت، بۆ ئەوانەیە سزای زۆر خراپ و سەخت');
INSERT INTO "bamoki" VALUES(34,6,'34:6','ئەوانە زانیاریان پێ دراوە دەزانن ئەوەی لە پەروەردگارتەوە بۆت ھاتووە تەنھا ئەوە ڕاستە و ڕێنموونی (خەڵك) دەکات بۆ ڕێگای خوای زاڵی ستایش کراو');
INSERT INTO "bamoki" VALUES(34,7,'34:7','وە ئەوانەی بێ باوەڕ بوون دەیان وت ئایا پیاوێکتان نیشان بدەین کە ھەواڵتان پێ دەدات کاتێك (مردن و) بەتەواوی ڕزین و پارە پارە بوون، بەڕاستی ئێوە سەر لەنوێ دروست دەکرێنەوە');
INSERT INTO "bamoki" VALUES(34,8,'34:8','ئایا ئەو (پێغەمبەر صلى الله عليه وسلم) بەدەم خواوە درۆ دەکات یان شێتە (نەخێر وانیە) بەڵکو ئەوانەی بێ باوەڕن بەدواڕۆژ لەناو سزاو گومڕاییەکی دوور (لەڕاستی)دان');
INSERT INTO "bamoki" VALUES(34,9,'34:9','دەی ئایا سەرنج نادەن بۆ ئەوەی لەبەردەم و لەپشتیانەوەیە لەئاسمان و زەویدا ئەگەر بمانەوێت دەیان بەین بەناخی زەویدا یان دەیخەینە خوار بەسەریاندا چەند پارچەیەك لەئاسمان بەڕاستی لەوەدا بەڵگەو پەند ھەیە بۆ ھەر بەندەیەك ڕوو لەخوا بێت');
INSERT INTO "bamoki" VALUES(34,10,'34:10','سوێند بەخوا بەڕاستی لەلایەن خۆمانەوە فەزڵ و بەھرەیەکی گەورەمان بەداود بەخشیبوو ئەی چیا وباڵندەکان لەگەڵ داود زیکرو تەسبیحاتی خوا بکەن وە ئاسنمان بۆ نەرم کردبوو');
INSERT INTO "bamoki" VALUES(34,11,'34:11','(فەرمانمان پێ کرد) قەڵغان وزرێی جەنگیان لێ دروست بکە و لەچنینی زرێدا بائەندازەیی ئەڵقەکان وەك یەك بن وکردەوەی چاك بکەن بەڕاستی من بەوەی کەدەیکەن بینام');
INSERT INTO "bamoki" VALUES(34,12,'34:12','وە بۆ سولەیمان (با، ھەوا)مان ڕام ھێنا بەیانیان تا نێوەڕۆ ( با) بەئەندازەی مانگێك ڕێگای دەبڕی وە (نێوەڕوان تا) ئێوارانیش بەئەندازەی مانگێك ڕێگای دەبڕی وە مسمان بۆ تواندەوە وەك کانیا و (دەڕۆی) و ھەندێك لەجنۆکەکانمان ڕام ھێنابوو لەبەر دەستیدا کاریان دەکرد بەفەرمانی پەروەردگاری وە ئەگەر ھەرکام لەوان (خوای گەورە ڕامی ھێنا بوونن) لەفەرمانی ئێمە لابدەن سزای ئاگری ھەڵگیرساوی پێ دەچێژین');
INSERT INTO "bamoki" VALUES(34,13,'34:13','(سولەیمان) ھەرچی بوویستایە بۆیان دروست دەکرد لەپەرستگاو پەیکەرو چەندین قاپی (نان خواردن) وەك حەوزی گەورەو چەند قازان و مەنجەڵی دامەزراو (نەدەجوڵان لەبەر گەورەییان) ئەی ئالی داود شوکرانە بژێری خوا بکەن بەڵام کەمێك لەبەندەکانم (بۆ خوا) سوپاسگوزارن');
INSERT INTO "bamoki" VALUES(34,14,'34:14','ئەمجا کاتێك سولەیمانمان مراند ھیچ شتێك مردنی سولەیمانی نیشان (جنۆکەکان) نەدا بێجگە لەخۆرەو مۆریانەی زەوی کە داردەستەکەی (سولەیمان) یان دەخوارد (کەسولەیمان خۆی دابوو بەسەریدا) ئەمجا کاتێك (سولەیمان) کەوت بۆ جنۆکەکان ڕوون بویەوە ە ئەگەر غەیبیان بزانیایە لەو سزا ڕیسواکەرەدا نەدەمانەوە');
INSERT INTO "bamoki" VALUES(34,15,'34:15','سوێند بەخوا بەڕاستی بۆ خەڵکی سەبەء لەو شوێنی ژیانەیاندا نیشانەیەك (لەسەر دەسەڵاتی خوا) ھەبوو دوو باخات لای ڕاست و لای چەپی (شوێنەکەیانەوە) ھەبوو (پێیان وترا) لەڕۆزی پەروەردگارتان بخۆن و سوپاسی ئەو بکەن وڵاتێکی پاك و خۆش و پەروەردگارێکی لێبوردە(یان ھەیە)');
INSERT INTO "bamoki" VALUES(34,16,'34:16','ئەمجا ڕوویان وەرگێڕا بەو ھۆیەوە لافاوی وێرانکارمان بۆ ناردن دوو باخاتی (چاکی بەروو بوومدار) ئەوانمان گۆڕی بەدوو باخاتی (خراپی) خاوەن بەرێکی تفت و تاڵ و دارگەز و کەمێك لەداری نەبگ (تاوگ، سیدر)');
INSERT INTO "bamoki" VALUES(34,17,'34:17','ئا بەو جۆرە تۆڵەمان لێ سەندن بەھۆی نا سوپاسیانەوە و ئایا جگە لەوانەی زۆر سپڵەن تۆڵە لەکەس ئەسێنین؟!');
INSERT INTO "bamoki" VALUES(34,18,'34:18','وە لەنێوان (ڕێگای) ئەوان و ئەو شارانەی پیتو بەرەکەتمان تێخستبوون (لەشام) سازاندبوومان چەند شارۆچکە و دێھاتی نزیك ولەیەکەوە دیار و ڕێگەی نزیکمان بۆ ڕۆشتن لەنێوان (ئەو دێھاتانە)دا دانابوو (ووتمان پێیان)چەند شەوو ڕۆژ بەو (رێگایانە)دا بڕۆن بەئارامی و بێ ترس');
INSERT INTO "bamoki" VALUES(34,19,'34:19','کەچی (خەڵکی سەبەء) ووتیان ئەی پەروەردگارمان ماوەی (ڕێگەی) سەفەرەکانمان دور بخەرەوە وستەمیان لەخۆیان کرد ئێمەش کردمانن بەداستانێك لەناو خەڵکدا بیگێڕنەوە پرژ و بڵاومان کردن (بەوڵاتاندا) بەو پەڕی پرژو بڵاوکردن، بەڕاستی لەو بەسەرھاتەدا چەند نیشانە ھەیە بۆ ھەموو زۆر خۆگری شوکرانە بژێر');
INSERT INTO "bamoki" VALUES(34,20,'34:20','سوێند بەخوا بەڕاستی شەیتان گومانی خۆی لەواندا بەدی ھێنا بۆیە ئیمانداران نەبێت ھەموویان شوێنی شەیتان کەوتن');
INSERT INTO "bamoki" VALUES(34,21,'34:21','وە شەیتان ھیچ دەسەڵاتی بەسەر ئیمانداران دا نەبوو تەنھا بۆ ئەوەی بزانرێت کێ باوەڕی بەڕۆژی دوایی ھەیە وەکێ لەوان دەربارەی (دواڕۆژ) لەگوماندایە وە پەروەردگارت (ئەی موحەممەد صلى الله عليه وسلم) بەسەر ھەموو شتێکدا چاودێرە');
INSERT INTO "bamoki" VALUES(34,22,'34:22','(ئەی موحەممەد صلى الله عليه وسلم) بڵێ ھاوار بکەن لەوانەی کەبێجگە لەخوا بەپەرستراوتان دەزانین بەقەدەر ھەرە وردیلەیەکیان بەدەست نیە نە لە ئاسمانەکان و نە لەزەویدا وە ئەو پەرستراوانە ھاوبەشی (خوا) نەبوون (لە دروستکردنی) ئاسمانەکان و زەویدا و ھیچ یەك لەوان یارمەتیدەری خوا نەبوون');
INSERT INTO "bamoki" VALUES(34,23,'34:23','وە تکا کردن لای خوا سوودی نیە مەگەر بۆ کەسێك ئەو (خوا) رێگە بدات ھەتا کاتێك ترس و بیم لەدڵی (تکاکاران) لادەبرێ (بەھۆی رێگەپێدانی شەفاعەت) (بە یەکتر) دەڵێن: پەروەردگارتان چی بڕیارێکی فەرموو دەڵێن: ووتە ڕاستەکەی فەرموو (کەرێگە دانە بەتکا کردن) وە تەنھا (خوا) بەرزو بڵندو گەورەیە');
INSERT INTO "bamoki" VALUES(34,24,'34:24','بڵێ کێ ڕۆزیتان دەدات لەئاسمانەکان و زەویدا بڵێ خوا (ڕۆزیمان دەدات) بێگومان ئێمە یان ئێوە (یەکێکمان) لەسەر ڕێنمونین یان لەگومڕاییەکی ئاشکراداین');
INSERT INTO "bamoki" VALUES(34,25,'34:25','(بڵێ) پرسیارتان لێ ناکرێت لەتاوانێ کەئێمە کردوومانە ئێمەش پرسیارمان لێ ناکرێت لەوەی ئێوە دەیکەن');
INSERT INTO "bamoki" VALUES(34,26,'34:26','بڵێ پەروەردگارمان ئێمەو ئێوە کۆدەکاتەوە لەپاشان بڕیار دەدات لەنێواناماندا بەھەق و ڕاستی وە ھەر ئەوە دادوەری زانا');
INSERT INTO "bamoki" VALUES(34,27,'34:27','بڵێ ئادەی نیشانم دەن ئەو ھاوبەشانەی بردوبووتاننە ڕێزی خواوە وانیە بەڵکو زاتی خوایە زاڵ و دانا وکار دروست');
INSERT INTO "bamoki" VALUES(34,28,'34:28','وە ئێمە تۆمان نەناردوە بۆ ھەموو ئادەمی بەمژدەدەر و ترسێنەر نەبێت بەڵام زۆربەی ئادەمی نازانن');
INSERT INTO "bamoki" VALUES(34,29,'34:29','وە دەڵێن ئەم بەڵێنی (ڕۆژی دواییە)کەی دێت ئەگەر ئێوە ڕاستگۆن');
INSERT INTO "bamoki" VALUES(34,30,'34:30','بڵێ بەڵێنی ئێوە ڕۆژێکە نەساتێك دوا دەکەون لێی و نەپێشیش دەکەون');
INSERT INTO "bamoki" VALUES(34,31,'34:31','وە ئەوانەی بێ باوەڕ بوون ووتیان ئێمە ھەرگیز باوەڕ ناھێنین بەم قورئانە و بەو کتێبانەش پێش ئەم (قورئانە) ھاتوون خۆزگە دەتبینی کاتێك ئەو ستەمکارانە ڕادەگیرێن لەبەردەم (دادگای) پەروەردگاریاندا قسەو تاوان ئاڕاستەی یەکتر دەکەن لاوازو ژێردەستە کراوان دەڵێن بەوانەی خۆیان بەگەورە دەزانی (بە دەسەڵاتدارەکان) ئەگەر ئێوە نەبوونایە بێگومان ئێمە باوەڕدار دەبووین');
INSERT INTO "bamoki" VALUES(34,32,'34:32','ئەوانەی خۆیان بەگەورە دادەنا دەڵێن بەوانەی ژێردەستە و لاواز کرابوون ئایا ئێمە ڕێگریمان کردن لەڕێنمونی دوای ئەوە بۆتان ھات (نەخێر وانیە) بەڵکو خۆتان تاوانبار بوون');
INSERT INTO "bamoki" VALUES(34,33,'34:33','ژێر دەستەو چەوساوەکان دەڵێن بەوانەی خۆ بەزل زان بوون (نەخێر وانیە) بەڵکو پیلانی (ئێوە) بەشەو و بەڕۆژ (چەواشەی کردین) کاتێك فەرمانتان پێ دەداین کە بێ بڕوا بین بەخوا وە وێنە وھاوبەشانی بۆ دابنێین ھەموویان پەشیمانی دەشارنەوە کاتێك کەسزای دۆزەخ دەبینن وە زنجیرو تەوقمان کردە گەردنی بێ باوەڕان ئایا تۆڵەیان لێ ئەسەنرێت جگە لەسزای ئەوەی دەیانکرد');
INSERT INTO "bamoki" VALUES(34,34,'34:34','وە لەھیچ شارێکدا پێغەمبەری ترسێنەرمان نەناردووە مەگەر خاوەن نازو نیعمەتەکانی (دەوڵەمەندەکانی) ووتوویانە بەڕاستی ئێمە باوەڕمان نیە بەو (ئاینە)ی ئێوەی پێ نێردراون');
INSERT INTO "bamoki" VALUES(34,35,'34:35','وە ووتیان ئێمە سامان و منداڵمان زۆرترە (لەئێوە) وە ئێمە قەت سزا نادرێین');
INSERT INTO "bamoki" VALUES(34,36,'34:36','بڵێ بەڕاستی پەروەردگارم ڕۆزی فراوان دەکات ودەیگرێتەوە بۆ ھەر کەسێك بیەوێ بەڵام زۆر بەی خەڵك نازانن');
INSERT INTO "bamoki" VALUES(34,37,'34:37','وە سامان و منداڵتان شتێك نین لای ئێمە پلەو پایەتان نزیك بکەنەوە مەگەر ئەوەی باوەڕی ھێنابێت و کردەوەی چاکی کردبێت بۆ ئەوانە پاداشتی دوو بەرامبەر ھەیە بەھۆی ئەو چاکانەی کەکردویانە ئەوان لەکۆشکە بەرزەکانی (بەھەشتدا) لە ئارامی وئاسایشدان');
INSERT INTO "bamoki" VALUES(34,38,'34:38','وە ئەوانەی ھەوڵی پوچەڵ کردنەوەی نیشانەکانی ئێمە دەدەن دەستە پاچەن و ھیچیان پێ ناکرێت ئەوانە لەسزای دۆزەخدا ئامادە کراو دەبن');
INSERT INTO "bamoki" VALUES(34,39,'34:39','بڵێ بێگومان پەروەردگارم ڕۆزی ھەرکام لەبەندەکانی بیەوێت فراوان یان کەمی دەکات بۆی وە ئێوە ھەر شتێکك ببەخشن (لەپێناو خوادا) ئەوە خوا جێگەی پڕدەکاتەوە وە ئەو (خوا) چاکترینی ڕۆزیدەرانە');
INSERT INTO "bamoki" VALUES(34,40,'34:40','وە ڕۆژێك (خوا) ھەموویان کۆ دەکاتەوە پاشان بەفریشتەکان دەفەرموێ ئایا ئەوانە (لەدونیا) ئێوەیان دەپەرست');
INSERT INTO "bamoki" VALUES(34,41,'34:41','(فریشتەکان) دەڵێن پاك و بێگەردی بۆ تۆ تەنھا تۆ گەورە وپشتیوانیی ئێمەی نەك ئەوان بەڵکو ئەوان جنۆکەیان دەپەرست زۆربەیان باوەڕیان بەوان (جنۆکە) ھەبوو');
INSERT INTO "bamoki" VALUES(34,42,'34:42','ئەمجا ئەمڕۆ ناتوانن ھیچ سوودو زیانێك بەیەکتری بگەیەنن وە بەستەمکاران دەڵێین: سزای ئاگری دۆزەخ بچەژن ئەوەی ئێوە باوەڕتان پێی نەبوو');
INSERT INTO "bamoki" VALUES(34,43,'34:43','کاتێک بخوێنرێتەوە بەسەریاندا نیشانە ڕوونەکانی ئێمە دەڵێن: ئەمە (پێغەمبەر صلى الله عليه وسلم) ھیچ نیە جگە لەپیاوێك دەیەوێت ڕێگریتان بکات لەوەی کەباوو باپیرانتان دەیانپەرست وە دەڵێن: ئەمە (قورئان) شتێك نیە بێجگە لە درۆیەکی ھەڵبەستراو بەناوی خواوە وە بێباوەڕان بەڕاستی بە (قورئان) یان دەووت کەبۆیان ھاتبوو ئەمە تەنھا جادویەکی ئاشکرایە');
INSERT INTO "bamoki" VALUES(34,44,'34:44','(خۆ ئێمە) پێشتر چەند کتێبێکمان پێ نەدابوون کە بیانخوێننەوە و بۆمان نەناردبوون پێش تۆ ھیچ پێغەمبەرێکی ترسێنەر');
INSERT INTO "bamoki" VALUES(34,45,'34:45','وە کەسانێك پێش ئەمان (قوڕەیشیەکان) باوەڕیان بەپێغەمبەرانیان نەھێنا ئەمان (قوڕەیش) نەگەیشتبوونە دەیەکی ئەوەی بەوانمان دابوو ئەمجا ئەوان باوەڕیان بەپێغەمبەرانی من نەھێنا جا چۆن بوو تۆڵە سەندنی ئێمە (لەوان)');
INSERT INTO "bamoki" VALUES(34,46,'34:46','بڵێ من تەنھا بەیەک شت ئامۆژگاریتان دەکەم کەدوو دوو، یەك یەك، لەبەر خوا ھەڵسن ( وگوێڕایەڵی فەرمانەکانی بن) پاشان بیر بکەنەوە ھاوڕێکەتان(پێغەمبەر صلى الله عليه وسلم) شێت نیە ئەو (پێغەمبەر صلى الله عليه وسلم) تەنھا ترسێنەرە بۆ ئێوە لەپێش ھاتنی سزای سەختی (قیامەت)');
INSERT INTO "bamoki" VALUES(34,47,'34:47','بڵێ ھەرکرێ و پاداشتێکم لێ داوا کردوون ئەوە بۆ خۆتان پاداشتی من تەنھا لەسەر خوایە وە ئەو (خوا) بەسەر ھەموو شتێکدا ئاگادارە');
INSERT INTO "bamoki" VALUES(34,48,'34:48','بڵێ بەڕاستی پەروەردگارم بەتوندی ھەق دەدات بەسەر (بەتاڵدا) (خوا) زانایە بە نھێنی و شاراوەکان');
INSERT INTO "bamoki" VALUES(34,49,'34:49','(ئەی موحەممەد صلى الله عليه وسلم) بڵێ (ئاینی) ڕاست ھات و بەتاڵ (نەما) و دروست نابێتەو دووبارە ناگەڕێتەوە');
INSERT INTO "bamoki" VALUES(34,50,'34:50','بڵێ ئەگەر من گومڕابم ئەوە زیانەکەی تەنھا بۆ خۆمە وە ئەگەر ڕێنمونبم ئەوە بەھۆی ئەو(قورئانە) وەیە کەپەروەردگارم بۆی ناردووم بەڕاستی خوا بیسەرو نزیکە');
INSERT INTO "bamoki" VALUES(34,51,'34:51','(ئەی موحەممەد صلى الله عليه وسلم) ئەگەر دەبینیت کاتێك (بێ باوەڕان) زۆر دەترسن ئەمجا بەھیچ جۆرێک ڕزگار بوونیان نیە وە لەشوێنێکی نزیك (دۆزەخەوە) دەبرێن بۆ ناوی');
INSERT INTO "bamoki" VALUES(34,52,'34:52','لەو کاتەدا دەڵێن باوەڕمان ھێنا بەو (قورئانە) چۆن (باوەڕیان) دەست دەکەوێ لەشوێنێکی دوورەوە');
INSERT INTO "bamoki" VALUES(34,53,'34:53','کەبەڕاستی ئەوان لەدونیادا بێ باوەڕ بوون پێی وە بە نەزانی قسەیان لەپەنھانیەکان دەکرد لەشوێنێکی دورەوە (کەدونیایە)');
INSERT INTO "bamoki" VALUES(34,54,'34:54','بەر بەست دەخرێتە نێوان ئەوان و ئەوەی ئارەزووی دەکەن (کەبڕوا ھێنان و تەوبە کردنە) ھەر وەکو بەھاوشێوەکانیان کرا لەپێش ئەماندا بەڕاستی ئەوان لەگومانێکی دوو دڵ کەردان');
INSERT INTO "bamoki" VALUES(35,1,'35:1','ھەموو ستایش بۆ خوایە کەبەدیھێنەری ئاسمانەکان و زەویە فریشتەکانی کردووە بەنێراوانێک خاوەنی (چەند جۆرە) باڵن : دووان و سیان و چوار (خوا) ھەرچی بیەوێت لەدروستکردندا زیادی دەکات بەڕاستی خوا بەسەر ھەموو شتێکدا بەتوانایە');
INSERT INTO "bamoki" VALUES(35,2,'35:2','خوا ھەر ڕەحمەت وچاکەیەک بۆ خەڵك بەرەڵا بکات و بیکاتەوە ئیتر شتێک نیە ڕێگری لێ بکات وبیگرێتەوە وەخوا ھەر بەھرەیەک بگرێتەوە ئیتر لەپاش گرتنەوەی خوا بەرەڵاکەر و نێرەری بۆ نیە وە خوا زاڵ و باڵادەست و کار دروستە');
INSERT INTO "bamoki" VALUES(35,3,'35:3','ئەی خەڵکینە بیربکەوە لەفەزڵی خوا بەسەرتانەوە ئایا بێجگە لەخوا دروستکەرێکی تر ھەیە کەڕۆزیتان بدات لەئاسمان و زەویدا ھیچ پەرستراوێکی ڕاست نیە بێجگە لەخوا دەی چۆن وا ڕوو وەردەگێڕن (لەیەکخواپەرستی)');
INSERT INTO "bamoki" VALUES(35,4,'35:4','ئەگەر باوەڕیان پێ نەھێنایت (دڵگران مەبە) چونکە بەڕاستی پێغەمبەرانی پێش تۆش باوەڕیان پێ نەھێنرا ھەموو کارو بارەکان تەنھا بۆ لای خوا دەگەڕێتەوە');
INSERT INTO "bamoki" VALUES(35,5,'35:5','ئەی خەڵکینە بێگومان بەڵێنی خوا ڕاستە دەی کەواتە با ژیانی دونیا فریوتان نەدات وە باشەیتان بە (بەزەیی و لێبوردەی) خوا فریوتان نەدات');
INSERT INTO "bamoki" VALUES(35,6,'35:6','بەڕاستی شەیتان دوژمنی ئێوەیە دەی کەواتە ئێوەش بەدوژمنی خۆتانی دابنێن بیگومان ئەو (شەیتان) تاقم وکۆمەڵەکەی بانگ دەکات بۆ ئەوەی ببەنە ھاوەڵانی ئاگری ھەڵگیرساوی (دۆزەخ)');
INSERT INTO "bamoki" VALUES(35,7,'35:7','ئەوانەی بێ باوەڕ بوون سزای بەتینیان بۆ ھەیە وە ئەوانەی باوەڕیان ھێناوەو کردەوە چاکەکانیان کردوە لێبوردن و پاداشتی گەورەیان بۆ ھەیە');
INSERT INTO "bamoki" VALUES(35,8,'35:8','دەی ئایا کەسێک کەکردەوەی خراپی خۆی بۆ جوانکراوە و بەچاک و جوانی دەبینێت (وەک کەسێکە پێوەری چاکەو خراپەی ئاین بێت) وە بەڕاستی خوا ھەرکەسێکی بوێت گومڕای دەکات وە ھەرکەسێکی بوێت ڕێنمونی دەکات دەی کەواتە بەخەفەت خواردن لەبەر (ئیمان نەھێنانی) ئەوان خۆت مەفەوتێنە بەڕاستی خوا بەوەی کەئەوان دەیکەن ئاگادارە');
INSERT INTO "bamoki" VALUES(35,9,'35:9','خوا زاتێکە (با،ھەوا) کانی ناردووە بۆ ئەوەی ھەورەکان بگەڕێنێت (بڵاویان بکاتەوە) ئەمجا ئەو (ھەورانە) دەنێرین بۆ سەر وڵاتێکی مردوو جا (بارانی لێ دەبارێت و) زەوی پێی زیندوو دەکەینەوە لەپاش ووشک بوون و مردنی زیندوو کردنەوەی(مردووانیش لەقیامەتدا) ھەر بەو شێوەیە');
INSERT INTO "bamoki" VALUES(35,10,'35:10','ھەر کەسێك گەورەیی و سەربەرزی دەوێت (لەخوای بوێت) چونکە بەڕاستی ھەموو گەورەیی و دەسەڵات ھەر بۆ خوایە ووتەی پاک و چاک تەنھا بۆ لای ئەو (خوا) سەر دەکەوێت وە (ووتەی پاکو) کردەوەی چاک بەرز دەکاتەوە (بۆلای خۆی) وە ئەوانەی کەپیلانە خراپەکان دەگێڕن سزایەکی توندو بەتینیان بۆ ھەیە و فێڵ و پیلانی ئەوانە لەناو دەچێت');
INSERT INTO "bamoki" VALUES(35,11,'35:11','خوا (باوکی) ئێوەی لەخاک دروستکردووە لەپاشان (نەوەکانی دروستکرد) لەنوتفە (دڵۆپە ئاوێک) ئەمجا (خوا) کردوونی بەچەند جووت (نێر و مێ) وە ھیچ مێینەیەك (ئافرەتێك) دوو گیان نابێت و سکەکەی دانانێت (مناڵی نابێت) مەگەر بەئاگاداری خوا نەبێت وە ھیچ تەمەن درێژ کراوێك تەمەنی درێژ ناکرێت وە لەتەمەنی کەم ناکرێت مەگەر لەکتێبێک(لوح المحفوظ )دا نوسراوە بەڕاستی ئەم کارە بۆ خوا ئاسانە');
INSERT INTO "bamoki" VALUES(35,12,'35:12','ئەو دوو (جۆرە) دەریا وەک یەک نین ئەمەیان سازگار وشیرینە و خواردنەوەی ئاسانە و خۆشە ئەوی تریان سوێرو تاڵ و تفتەو لەھەردووکیان گۆشتی تەڕو تازە دەخۆن و خشڵی (مرواری و سەدەف و دوڕ لەدەریا) دەرئەھێنن و دەیپۆشن، وخۆتانی پێ دەڕازێننەوە و کەشتیەکان دەبینیت (بە سنگیان) ئاوەکە لەت دەکەن (کاتێك بەناویدا دەڕۆن) بۆ ئەوەی ھەندێ ڕزق و ڕۆزی خوا پەیدا بکەن وە تا ستایشی خوا بکەن (لەسەر بەھرەکانی)');
INSERT INTO "bamoki" VALUES(35,13,'35:13','(خوا) شەو دەکات بەناو ڕۆژدا و ڕۆژ د ەکات بەناو شەودا وە خۆرو مانگی ڕام ھێناوە ھەریەکەیان (لە خولگەی خۆیدا) تا ئەو ماوەیەی بۆی دیاریکراوە دەگەڕێت ودەسوڕێتەوە ئەوەیە خوای پەروەردگاری ئێوە پادشایەتی تەنھا بۆ ئەوە و ئەوانەی ئێوە ھاواریان لێ دەکەن بێجگە لەخوا خاوەنی توێکڵە سپێکەی ناوکی خورمایش نین');
INSERT INTO "bamoki" VALUES(35,14,'35:14','(بتەکان) ئەگەر نزاو ھاواریان لێ بکەن ھاوارەکەتان نابیستن و ئەگەر بیشی بیسن وەڵامتان نادەنەوە و لەڕۆژی قیامەتیشدا بێ باوەڕ دەبن بەھاوبەش دانانەکەتان (وبەدرۆتان دەخەنەوە) وە دڵنیابە کەس وەك (خوای) ئاگادار، ئاگادارت ناکات (لەسەر ئەنجامتان)');
INSERT INTO "bamoki" VALUES(35,15,'35:15','ئەی خەڵکینە ھەر ئێوە ھەژاری لای خوان وە تەنھا خوایە بێ نیازی ستایشکراو');
INSERT INTO "bamoki" VALUES(35,16,'35:16','ئەگەر (خوا) بیەوێت لەناوتان دەبات وە خەڵکێکی تری تازە دەھێنێت');
INSERT INTO "bamoki" VALUES(35,17,'35:17','وە ئەمەش بۆ خوا کارێکی گران نیە');
INSERT INTO "bamoki" VALUES(35,18,'35:18','ھیچ گوناھبارێك گوناھی کەسێکی تر ھەڵناگرێت وە ئەگەر بارگرانێ (گوناھبارێك) بانگی کەسێکی تر بکات بۆ ھەڵگرتنی (بارە گوناھەکەی) ھیچ لەو (بارە گوناھە) ھەڵناگرێت ھەرچەندە (داوا لێ کراوەکە) خزمیشی بێت بەڕاستی تۆ (ئەی موحەممەد صلى الله عليه وسلم) تەنھا ئەوانە بێدار دەکەیتەوە کە بەپەنھانی لەپەروەردگاریان دەترسن و نوێژەکانیان بەرێکی بەجێ دەھێنن وە ھەر کەس خۆی پاک ڕاگرێ (لە تاوان) ئەوە بەڕاستی (سوودی) خۆ پاك ڕاگرتنەکەی ھەر بۆ خۆیەتی و گەڕانەوەش تەنھا بۆ لای خوایە');
INSERT INTO "bamoki" VALUES(35,19,'35:19','نەکوێرو بینا یەکسانن');
INSERT INTO "bamoki" VALUES(35,20,'35:20','و نە تاریکستان و ڕووناکی (وەك یەکن)');
INSERT INTO "bamoki" VALUES(35,21,'35:21','و نەسێبەرو خۆرەتاو (وەك یەکن)');
INSERT INTO "bamoki" VALUES(35,22,'35:22','نەزیندووان و مردووان بەرانبەرن بەڕاستی خوا (بانگی خۆی) دەبیستێنێ بەھەر کەس کەبیەوێت بەڵام تۆ ناتوانی (ئەی موحەممەد صلى الله عليه وسلم پەیامەکەت) ببیستێنی بەوانەی لەگۆڕەکاندان (مردوون)');
INSERT INTO "bamoki" VALUES(35,23,'35:23','تۆ تەنھا ترسێنەری');
INSERT INTO "bamoki" VALUES(35,24,'35:24','بێگومان ئێمە تۆمان بە(ئاینی) ڕاستەوە ناردووە مژدەدەر و ترسێنەر بیت ھەر گەل و ئوممەتێك (پێغەمبەرێکی) ترسێنەری تێدا ھاتووە');
INSERT INTO "bamoki" VALUES(35,25,'35:25','وە ئەگەر باوەڕت پێ نەھێنن ئەوە بێگومان لەپێش ئەوانیشەوە کەسانێک باوەڕیان نەھێنا (بە پێغەمبەرەکانیان) پێغەمبەرەکانیان ھاتنە لایان بەموعجیزەکانەوە و پەڕاوو کتێبە ڕوون کەرەوەکانەوە');
INSERT INTO "bamoki" VALUES(35,26,'35:26','پاشان ئەوانەیان بێ باوەڕ بوون قارم لێ گرتن جا چۆن بوو تۆڵە سەندنی ئێمە (لەوان)');
INSERT INTO "bamoki" VALUES(35,27,'35:27','ئایا نەتبینی کە بەڕاستی خوا لەئاسمانەوە بارانی باراندووە، ئەمجا بەو بارانە ھەموو جۆرە میوەیەکمان ڕواند کەشێوە و ڕەنگەکانیان جیاوازە و لەچیاکاندا چەند ڕێگا ھەیە کەڕەنگیان سپی و سورە (سپی و سورەکانیش) جیاوازن و ھەشیانە ڕەشێکی تۆخە');
INSERT INTO "bamoki" VALUES(35,28,'35:28','وە لەئادەمی و زیندەوەرو ئاژەڵ و ماڵاتیشدا ڕەنگی جۆراو جۆر ھەیە بەوێنەی جیاوازی میوەھات بەڕاستی لەناو بەندەکانی خوا، تەنھا زانایان (وەك پێویست) لەخوا دەترسن بێگومان خوا زاڵی باڵادەست و لێبوردەیە');
INSERT INTO "bamoki" VALUES(35,29,'35:29','بەڕاستی ئەوانەی کە کتێبی خوا دەخوێننەوە و بەچاکی نوێژیان بەجێھێناوە وە لەوەی کەپێمان داون بەنھێنی و ئاشکرا دەبەخشن چاوەڕوانی بازرگانیەکن ھەرگیز زەرەر ناکات');
INSERT INTO "bamoki" VALUES(35,30,'35:30','بۆ ئەوەی (خوا) بەتێرو تەواوی پاداشتیان بداتەوە وە لە فەزڵ وبەھرەی خۆی زیاتریشیان پێ بدات بە ڕاستی خوا لێبوردە و سوپاس وەرگرە');
INSERT INTO "bamoki" VALUES(35,31,'35:31','وە ئەوەی لەقورئاندا نیگامان کردوە بۆ تۆ ھەر ئەوە ڕاستەو پشتیوانی کتێبەکانی پێش خۆیەتی بێگومان خوا بەبەندەکانی ئاگادارو بینایە');
INSERT INTO "bamoki" VALUES(35,32,'35:32','لەپاشان ئەم قورئانەمان بەخشی بەکەسانێك کە لەناو بەندەکانماندا ھەڵمان بژاردن ئەمجا ھەندێکیان ستەمکارن لەخودی خۆیان وھەندێکیان مام ناوەندین و ھەندێکی تریان بەفەرمانی خوا پێشکەوتوون بەھۆی چاکەکانیانەوە ئەم (قورئان پێدانە) لەخۆیدا فەزڵێکی گەورەیە');
INSERT INTO "bamoki" VALUES(35,33,'35:33','ئەوانە دەچنە ناو بەھەشتانێک کە جێگای مانەوەی ھەمیشەییە لەوێدا بەبازنی ئاڵتونی و مرواری دەڕازێنرێنەوە وپۆشاکیشیان تێیدا ئاوریشمە');
INSERT INTO "bamoki" VALUES(35,34,'35:34','وە ئەوان دەڵێن: سوپاس بۆ ئەو خوایەی کەخەفەتی لابردین و نەیھێشت بەڕاستی پەروەردگارمان لێبوردە و شایستەی سوپاسە');
INSERT INTO "bamoki" VALUES(35,35,'35:35','ئەو (خوایە) کە بەچاکەو فەزڵی خۆی نیشتەجێی کردین لەماڵی ھەمیشەیی (بەھەشتدا) لەوێدا نەھیچ ماندوو بوونێکمان تووش دەبێت ونەھیچ ئەرك و زەحمەتێکمان دووچار دەبێت');
INSERT INTO "bamoki" VALUES(35,36,'35:36','ئاگری دۆزەخ بۆ ئەوانەیە بێ باوەڕ بوون بڕیاری مردن نادرێت بەسەردایاندا تا بمرن ھەروەھا ھەندێ لەسزای (دۆزەخ) یان لێ سووك ناکرێت ئا بەو جۆرە تۆڵە ئەسێنین لەھەموو بێ باوەڕێك');
INSERT INTO "bamoki" VALUES(35,37,'35:37','وە ئەوانە لەدۆزەخدا ھاوار دەکەن (دەڵێن) ئەی پەروەردگارمان (لەدۆزەخ) دەرمان بھێنە تا کردەوەی چاك بکەین جیاواز لەوەی دەمان کرد (لەدونیادا) (وەڵام دەدرێنەوە) ئایا تەمەنمان درێژ نەکردن ئەوەندەی ئەوەی دەیەوێ بیر بکاتەوە تیایدا بیر بکاتەوە وە ترسێنەرتان بۆ نەھات دەی (ئەو سزا) بچەژن چونکە ھیچ یاریدەدەرێک بۆ (بێ باوەڕە) ستەمکارەکان نیە');
INSERT INTO "bamoki" VALUES(35,38,'35:38','بەڕاستی خوا بەنھێنی ئاسمانەکان وزەوی زانایە بێگومان خوا بەنھێنی دڵان ئاگادارە');
INSERT INTO "bamoki" VALUES(35,39,'35:39','ھەر ئەو زاتە ئێوەی کردە جێنشینی(پێشوەکان) لەزەویدا ئەوسا ھەر کەس بێ باوەڕ بێ ئەوە (تۆڵەی) بێ باوەڕییەکەی ھەر بۆ خۆیەتی و بێ باوەڕی کافران ھیچیان بۆ زیاد ناکات لای پەروەردگاریان جگە لەخەشم و ڕقی زۆر وە بێ باوەڕیی کافران ھیچیان بۆ زیاد ناکات بێجگە لەزەرەرۆمەندی');
INSERT INTO "bamoki" VALUES(35,40,'35:40','(ئەی موحەممەد صلى الله عليه وسلم) بڵێ ھەواڵم بدەنێ ئەو ھاوبەشانەی لەجیاتی خوا دەتانپەرستن (ھاوارتان لێ دەکردن) نیشانم بدەن لەزەویدا چیان دروستکردووە؟ یان لەبەدی ھێنانی ئاسمانەکاندا بەشداری (خوا)یان کردووە؟ یان کتێبێکمان پێداون جا بەڵگەیان لەو کتێبەدا ھەیە (کەبتەکان ھاوبەشی خوان) (نەخێر وانیە) بەڵکو ستەمکاران ھیچ بەڵێنێک بەیەکتری نادەن جگە لەفریودان و(ھەڵخەڵەتاندن) نەبێت');
INSERT INTO "bamoki" VALUES(35,41,'35:41','بەڕاستی خوا ئاسمانەکان و زەوی راگیر دەکات بۆ ئەوەی لەڕێڕەوی خۆیان لانەدەن بەڕاستی ئەگەر لەرێڕەوی (خۆیان) لابدەن کێ ھەیە جگە خوا ڕایان بگرێت بێگومان ئەو (خوا) لەسەر خۆ و لێبوردەیە');
INSERT INTO "bamoki" VALUES(35,42,'35:42','(بت پەرستان) بەسوێندی ھەرە گەورەیان سوێندیان دەخوارد بەخوا (دەیانووت) ئەگەر پێغەمبەرێکی ترسێنەریان بۆ بێت بەڕاستی ئەوان لەھەر یەک لە ئوممەتانی تر ڕێنمونتر دەبن ئەمجا کاتێك پێغەمبەری ترسێنەریان بۆ ھات زیاتر دوور کەوتنەوە (لەرێگەی ڕاست)');
INSERT INTO "bamoki" VALUES(35,43,'35:43','لەبەر خۆ بەزل زانی لەزەویدا و فێڵ وپیلانی خراپکردن وە فێڵ و پیلانی خراپ تەنھا زیان بەخاوەنەکەی دەگەیەنێت دەی کەواتە ئەوان جگە لەچارەنووسی پێشینان چاوەڕوانی شتێکی تر دەکەن؟ چونکە باوی خوا ھەرگیز گۆڕانی بۆ نیە وە باوی خوا لادانی بۆ نیە');
INSERT INTO "bamoki" VALUES(35,44,'35:44','ئایا ئەوان بەزەویدا نەگەڕاون تا سەیر بکەن سەر ئەنجام گەلانی پێش خۆیان چۆن بووە کە ئەوان لەمان بەھێزتر بوون ھیچ شتێك ناتوانێت لەدەستی خوا دەرچێت نە لەئاسمانەکان ونەلەزەویدا بەڕاستی خوا زانا و بەدەسەڵات وبەتوانایە');
INSERT INTO "bamoki" VALUES(35,45,'35:45','وە ئەگەر خوا قار بگرێت لەخەڵکی (تۆڵەیان لێ بسێنێت) لەبەر ئەو (خراپانەی) کردویانە ھیچ گیاندارێکی بەسەر زەویەوە نەدەھێشت بەڵام (خوا) مۆڵەتی ئەوان دەدات تا ماوەی دیاریکراو ئەمجا کاتێک ماوەکەیان (کۆتایی) ھات (دەیان مرێنێ) جا بێگومان خوا بە بەندەکانی ئاگادارو بینایە');
INSERT INTO "bamoki" VALUES(36,1,'36:1','بە(یا، سین) دەخوێنرێتەوە خوا زاناترە بەماناکەی بۆ زانیاری زیاتر سەیری سەرەتای سورەتی (البقرة) بکە');
INSERT INTO "bamoki" VALUES(36,2,'36:2','سوێند بە قورئانی پڕ لەدانایی');
INSERT INTO "bamoki" VALUES(36,3,'36:3','بەڕاستی تۆ (ئەی موحەممەد صلى الله عليه وسلم) لە پێغەمبەرانی خوای');
INSERT INTO "bamoki" VALUES(36,4,'36:4','لەسەر رێبازی ڕاستی');
INSERT INTO "bamoki" VALUES(36,5,'36:5','(قورئان) دابەزێنراوە لەلایەن خوای زاڵ و باڵادەست و میھرەبانەوە');
INSERT INTO "bamoki" VALUES(36,6,'36:6','بۆ ئەوەی گەلێ بترسێنی (لەسزا) کەباوو باپیرانیان نەترسێنرابوون بۆیە ئەوان بێ ئاگا بوون');
INSERT INTO "bamoki" VALUES(36,7,'36:7','سوێند بەخوا بەڕاستی بەڵێنی خوا چەسپاو ھاتە دی لەسەر زۆربەیان چونکە ئەوان باوەڕ ناھێنن');
INSERT INTO "bamoki" VALUES(36,8,'36:8','(وەك کەسانێك وان کە) تەوقمان کردبێتە گەردنیان جا ئەو (تەوقانە) تا چەناکەیان ھاتبێت (گەردنیانی بەتەواوی گرتبێـتەوە) ئەمجا سەریان بەرز کرابێتەوە (نەتوانن بۆ خوارو ئەملاولا بیجوڵێنن)');
INSERT INTO "bamoki" VALUES(36,9,'36:9','وە بەربەستمان لەبەردەم و پشتیانەوە داناوە پەردەشمان داوە بەسەر چاویاندا بۆیە ئەوان (ڕاستی) نابینن');
INSERT INTO "bamoki" VALUES(36,10,'36:10','وە بۆ ئەوان یەکسانە چی بیان ترسێنیت یان نەیان ترسێنیت باوەڕ ناھێنن');
INSERT INTO "bamoki" VALUES(36,11,'36:11','تۆ تەنھا کەسێك دەترسێنیت کە شوێن قورئان دەکەوێت لەپەنھانیشدا لە خوای میھرەبان دەترسن بەھۆی (ئەو شوێن کەوتن و ترسانەوە) مژدەی بدەرێ بەلێخۆشبوونی (گوناھەکانی) و پاداشتی چاکی (بەھەشت)');
INSERT INTO "bamoki" VALUES(36,12,'36:12','بەڕاستی ھەر ئێمە مردووان زیندوو دەکەینەوە و ھەرچی پێشتر کردویانە وشوێنەواری پاش خۆشیان دەنوسین وە ھەموو شتێکمان لە پەڕاوێکی ڕوون وئاشکرادا ژماردووە و نووسییوە کە (لوح المحفوظ)');
INSERT INTO "bamoki" VALUES(36,13,'36:13','(ئەی موحەممەد صلى الله عليه وسلم) نموونەیان بۆ بھێنەرەوە بەخەڵکی شاری (انطاکیە) کاتێك کەپێغەمبەرانیان بۆ ھات');
INSERT INTO "bamoki" VALUES(36,14,'36:14','کاتێک دوو (پێغەمبەر) مان نارد بۆ ناویان باوەڕیان پێنەکردن ئەمجا بە (ناردنی پێغەمبەری) سێھەم بەھێزمان کردن (وپشتمان گرتن) جا (ھەموویان) ووتیان بێگومان ئێمە نێردراوین بۆ سەر ئێوە');
INSERT INTO "bamoki" VALUES(36,15,'36:15','(خەڵکەکە) ووتیان ئێوە ھەر ئادەمیەکی وەک ئێمەن و خوای میھرەبان ھیچ شتێکی (لەنیگا) نەناردۆتە خوارەوە ئێوە تەنھا درۆزنن و درۆ دەکەن');
INSERT INTO "bamoki" VALUES(36,16,'36:16','(پێغەمبەرەکان) ووتیان پەروەردگارمان دەزانێت بەڕاستی ئێمە (بەپێغەمبەرێتی) نێردراوین بۆ سەر ئێوە');
INSERT INTO "bamoki" VALUES(36,17,'36:17','و ئێمە تەنھا گەیاندنی (ئاینەکە)مان لەسەرە بەڕوونی');
INSERT INTO "bamoki" VALUES(36,18,'36:18','(خەڵکەکە) ووتیان بێگومان ئێمە بەھۆی ئێوەوە دووچاری شوومی و بەدبەختی بووین سوێند بەخوا ئەگەر واز نەھێنن (لەو کارەتان) بەرد بارانتان دەکەین و بەڕاستی لەلایەن ئێمەوە دووچاری سزایەکی پڕ ئێش و ئازار دەبن');
INSERT INTO "bamoki" VALUES(36,19,'36:19','(پێغەمبەرەکان) ووتیان شوومی ئێوە بەخۆتانەوەیە چونکە ئێوە ئەگەر ئامۆژگاری بکرێن (ھەست بەشوومی دەکەن) (نەخێر وانیە) بەڵکو ئێوە گەلێکی لەسنوور دەرچوون');
INSERT INTO "bamoki" VALUES(36,20,'36:20','وە لەو پەڕی شارەکەوە پیاوێك بەپەلە ھات ووتی ئەی گەلەکەم شوێن ئەم پێغەمبەرانە بکەون');
INSERT INTO "bamoki" VALUES(36,21,'36:21','شوێن کەسانێ بکەون داوای ھیچ کرێ و پاداشتێکتان لێ ناکەن کەخۆشتان ڕێنمونی کراون');
INSERT INTO "bamoki" VALUES(36,22,'36:22','وە من بۆچی زاتێك نەپەرستم کەمنی بەدی ھێناوە وە ھەر بۆ لای ئەوە دەگەڕێنەوە');
INSERT INTO "bamoki" VALUES(36,23,'36:23','ئایا بێجگە لەو (خوا) پەرستراوانێك بۆ خۆم بڕیار بدەم ئەگەر خوای میھرەبان زیانی بۆ من بوێت تکای ئەوان ھیچ سوودێك بەمن ناگەیەنێت و (لەسزای خوا) ڕزگارم ناکەن');
INSERT INTO "bamoki" VALUES(36,24,'36:24','بێگومان من (ئەگەر ھاوبەش دابنێم) ئەو کاتە لەگومڕاییەکی زۆر ئاشکرادام');
INSERT INTO "bamoki" VALUES(36,25,'36:25','بەڕاستی من باوەڕم ھێنا بەپەروەردگارتان و دە ئێوەش گوێتان لێ بێت');
INSERT INTO "bamoki" VALUES(36,26,'36:26','(ئەم پیاوەیان شەھید کرد) پێی ووترا: بڕۆرە بەھەشتەوە (ئەو پیاوەش) ووتی خۆزگە گەلەکەم بیانزانیایە');
INSERT INTO "bamoki" VALUES(36,27,'36:27','بە ئەوەی کەپەروەردگارم لێم خۆشبووە وەبردمیە ڕیزی ڕێز گیراوانەوە');
INSERT INTO "bamoki" VALUES(36,28,'36:28','وە لە دوای ئەو نەمان ناردە خوارەوە بۆ سەر گەلەکەی ھیچ لەشکرێك لەئاسمانەوە و ئێمە دابەزێنەر نەبووین');
INSERT INTO "bamoki" VALUES(36,29,'36:29','(ھۆی تیاچوونیان) تەنیا شریخەیەکی بەھێز بوو جا کتوپڕ ئەوانە ھەموو جوڵەیان نەما و مردن');
INSERT INTO "bamoki" VALUES(36,30,'36:30','ئای پەشیمانی وداخی سەخت بۆ ئەو بەندانەی (کەبێ باوەڕن) ھەر پێغەمبەرێکیان بۆ بھاتایە تەنیا گاڵتەیان پێ دەکرد (و ھیچ تر)');
INSERT INTO "bamoki" VALUES(36,31,'36:31','ئایا نەیان زانیوە لە پێش ئەواندا چەند گەلی (تاوانبار) مان لە ناو بردووە کەبێگومان ناگەڕێنەوە بۆ لای ئەمان');
INSERT INTO "bamoki" VALUES(36,32,'36:32','ھەر ھەموو (لە ناوبراوان) کۆ دەکرێنەوە لای ئێمە (و لەقیامەتدا) ئامادە دەکرێن');
INSERT INTO "bamoki" VALUES(36,33,'36:33','وە نیشانە و بەڵگەیەك بۆ ئەوان، ئەم زەویە مردووەیە کە زیندوومان کردەوە و دانەوێڵەمان لێ دەرھێنا (لێ ڕواند) کە لێی دەخۆن');
INSERT INTO "bamoki" VALUES(36,34,'36:34','وە لەو (زەویە)دا چەند باخ لەخورما و ترێمان بەدی ھێنا و چەند کانیاومان تیادا ھەڵقوڵاندوە');
INSERT INTO "bamoki" VALUES(36,35,'36:35','بۆ ئەوەی لەبەروبوومەکانی بخۆن کەدەستی ئەوان بەرھەمی نەھێناوە دەی ئایا سوپاسی خوا ناکەن');
INSERT INTO "bamoki" VALUES(36,36,'36:36','پاك وبێگەردە زاتێك کە ھەموو جووتەکانی (نێر و مێی) دروستکردووە لەوەی کە زەوی دەیڕوێنێت و لە(ئادەمی) خۆیان و لەو شتانەش کە نایزانن');
INSERT INTO "bamoki" VALUES(36,37,'36:37','شەویش بەڵگەو نیشانەیە بۆ ئەوان کە ئێمە ڕۆژی لێ دەردەھێنین ئەمجا لەناکاودا تاریکی ھەموویان دەگرێتەوە');
INSERT INTO "bamoki" VALUES(36,38,'36:38','خۆریش بەرە و بنکەی خۆی (لەگەڕاندایە و) دەڕوا ئەوە بڕیاردراوی (خوای) زاڵ و باڵادەست و زانایە');
INSERT INTO "bamoki" VALUES(36,39,'36:39','وەبۆ مانگیش چەند قۆناغێکمان داناوە (دوای تەواو کردنی قۆناغەکانی) ھەتا وەکو پەلی دار خورمای کۆنی (وشك بووی) لێ دێت');
INSERT INTO "bamoki" VALUES(36,40,'36:40','خۆر بۆی نیە (ناتوانێت) بگات بەمانگ و شەویش ناتوانێت پێشکەوێت لەرۆژ و ھەر یەکەیان (خۆرو مانگ) لە خولگەیەکدا مەلە دەکەن و دەسوڕێنەوە');
INSERT INTO "bamoki" VALUES(36,41,'36:41','وەبەڵگەیەکی ئاشکرای تر بۆ ئەوان ئەمەیە کەبێگومان ئێمە نەوەکانی ئەوانمان لەکەشتی پڕکراو (لەخۆیان و کەل و پەلیان) دا ھەڵگرتووە');
INSERT INTO "bamoki" VALUES(36,42,'36:42','وە بە وێنەی کەشتی شتانێکی تریشمان بۆ دروستکردوون کە سواری بن');
INSERT INTO "bamoki" VALUES(36,43,'36:43','وە ئەگەر بمانەوێت نوقمیان دەکەین جا ھیچ کەس نایەت بە ھاواریانەوە و ڕزگاریش ناکرێن(لە نوقم بوون)');
INSERT INTO "bamoki" VALUES(36,44,'36:44','مەگەر بەزەیی ئێمە (ڕزگاریان بکات) وتا کاتی دیارکراو بیان ژێنین');
INSERT INTO "bamoki" VALUES(36,45,'36:45','وە کاتێك پێیان بوترێت خۆتان بپارێزن (لەو سزای کە) لە بەردەمتاندایە و لەوەش لەپشتانەوەیە (لە ڕۆژی دوایی) تاڕەحمتان پێ بکرێت');
INSERT INTO "bamoki" VALUES(36,46,'36:46','وە ھەر بەڵگە و ئایەتێکیان لە ئایەتەکانی پەروەردگاریان بۆ بێت ئەوان ھەر ڕوو وەردەگێڕن لەو(ئایەتانە)');
INSERT INTO "bamoki" VALUES(36,47,'36:47','وە ھەر کاتێك پێیان بوترێت لەوەی خوا پێی داون ببەخشن ئەوانەی بێ باوەڕ بوون دەڵێن بەوانەی ئیمانیان ھێنا بوو ئایا خواردن بدەین بە کەسێك ئەگەر خوا بیوویستایە خواردنی پێدەدا ئێوە تەنھا لە گومڕاییەکی ئاشکرادان');
INSERT INTO "bamoki" VALUES(36,48,'36:48','وەدەڵێن ئەم بەڵێنەکەی دێت ئەگەر ئێوە ڕاستگۆن');
INSERT INTO "bamoki" VALUES(36,49,'36:49','ئەمانە چاوەڕوانی ھیچ ناکەن مەگەر شریخە و دەنگێکی بەھێز (فوو کردن بە کەڕەنادا) بیانگرێت لە کاتێکدا لە نێو خۆیاندا دەمەقاڵێ دەکەن');
INSERT INTO "bamoki" VALUES(36,50,'36:50','ئەوسا ناتوانن ھیچ وەسێتێك بکەن یان بگەڕێنەوە لای کەسوکاریان');
INSERT INTO "bamoki" VALUES(36,51,'36:51','وە (جارێ تر) فوو دەکرێت بە کەڕەنادا ئەمجا ئەوان کتوپڕ لە گۆڕەکان دێنەدەرەوەو بە پەلە دەڕۆن بۆ لای پەروەردگاریان');
INSERT INTO "bamoki" VALUES(36,52,'36:52','دەڵێن ئەی ھاوار بۆ ئێمە کێ زیندووی کردینەوە لە گۆڕەکانمان ئەمە ئەو بەڵێنەیە خوای میھرەبان پێی داین و پێغەمبەرەکان ڕاستیان کرد');
INSERT INTO "bamoki" VALUES(36,53,'36:53','تەنھا شریخە ودەنگێکی گەورە دێت ئیتر کتوپڕ ھەموویان (بە کۆمەڵ) لای ئێمە ئامادە کراون');
INSERT INTO "bamoki" VALUES(36,54,'36:54','ئەمڕۆش ھیچ جۆرە ستەمێك لەکەس ناکرێت وە جگە لەوەی کەدەتان کرد پاداشت وسزا نادرێنەوە');
INSERT INTO "bamoki" VALUES(36,55,'36:55','بەڕاستی بەھەشتیەکان ئەمڕۆ لەخۆشی و شادیدا سەرقاڵن');
INSERT INTO "bamoki" VALUES(36,56,'36:56','خۆیان و ھاوسەرەکانیان لە سێبەرەکاندا لە سەر تەخت (ی ڕازاوە) پاڵیان داوەتەوە');
INSERT INTO "bamoki" VALUES(36,57,'36:57','لەو(بەھەشتە) دا بۆیان ئامادەیە میوەھات وھەرچی داوا بکەن');
INSERT INTO "bamoki" VALUES(36,58,'36:58','سەلام (یان لێ دەکرێت) بەووتەیەك لە لایەن پەروەردگاری میھرەبانەوە');
INSERT INTO "bamoki" VALUES(36,59,'36:59','ئێوەش ئەی تاوانباران ئەمڕۆ جیا ببنەوە (لە ئیمانداران)');
INSERT INTO "bamoki" VALUES(36,60,'36:60','ئەی نەوەکانی ئادەم ئایا وەسێتم بۆ نەکردن کە شەیتان نەپەرستن چونکە بەڕاستی شەیتان دوژمنی ئاشکرای ئێوەیە');
INSERT INTO "bamoki" VALUES(36,61,'36:61','وە (ئایا وەسێتم بۆ نەکردن) کەمن بپەرستن ئەمە ڕێگای ڕاستە');
INSERT INTO "bamoki" VALUES(36,62,'36:62','سوێند بەخوا بێگومان (شەیتان) خەڵکێکی زۆری لە ئێوە گومڕاکرد دەی ئایا بۆ ژیر نەبوون');
INSERT INTO "bamoki" VALUES(36,63,'36:63','ئەمە ئەو دۆزەخەیە کەبەڵێنتان پێدرابوو');
INSERT INTO "bamoki" VALUES(36,64,'36:64','ئەمڕۆ بڕۆنە ناویەوە بەھۆی بێ باوەڕ بوونتانەوە');
INSERT INTO "bamoki" VALUES(36,65,'36:65','ئەمڕۆ مۆر دەنێین بەسەر دەمیاندا و دەستیان قسەمان لەگەڵدا دەکەن و قاچیان شایەتی دەدەن بەوەی کە کردویانە');
INSERT INTO "bamoki" VALUES(36,66,'36:66','وە ئەگەر بمانەوێت ڕووناکی چاویان ناھێڵین (کوێریان دەکەین) ئەوسا دەیانەوێت بەرەو ڕێگای ڕاست پێشبرکێ بکەن جا چۆن دەبینن');
INSERT INTO "bamoki" VALUES(36,67,'36:67','خۆ ئەگەر بمانەوێت لەشوێنی خۆیاندا شێوەی ئادەمیان دەگۆڕین (دەیان کەین بە شتێکی خراپ) بەھۆی ئەو (گوڕینەوە) نەیان دەتوانی نەبڕۆن ونەبگەڕێنەوە');
INSERT INTO "bamoki" VALUES(36,68,'36:68','وە تەمەنی ھەرکەس درێژ بکەین لاشەی لاواز و ناتەواو دەکەین دەی ئایا ژیر نابن؟');
INSERT INTO "bamoki" VALUES(36,69,'36:69','ئێمە ئەو (موحەممەد صلى الله عليه وسلم) مان فێری شیعر نەکردووە، وشیعرووتن شیاوی ئەو نیە و ئەوەی (موحەممەد صلى الله عليه وسلم) دەیڵێت پەند و ئامۆژگاری و قورئانی ڕونکەرەوەی (ڕاستیە)');
INSERT INTO "bamoki" VALUES(36,70,'36:70','بۆ ئەوەی ئەوانەی (دڵ) زیندوون بیان ترسێنێت وە بەڵێنی (سزا) بسەپێنێت بەسەر بێ باوەڕاندا');
INSERT INTO "bamoki" VALUES(36,71,'36:71','ئایا نەیان بینی (نەیان زانیوە) کە بێگومان ئێمە ئاژەڵمان بۆ ئەوان دروستکردووە ئەمجا ئەوان خاوەنیانن');
INSERT INTO "bamoki" VALUES(36,72,'36:72','وە ڕاممان ھێناون بۆیان ئەمجا سواری ھەندێکیان دەبن و(گۆشتی) ھەندێکیشیان دەخۆن');
INSERT INTO "bamoki" VALUES(36,73,'36:73','وە زۆر سوودیان لێ دەبینن وشتی خواردنەوەشیان لێ وەردەگرن دەی ئایا سوپاسی خوا ناکەن');
INSERT INTO "bamoki" VALUES(36,74,'36:74','وە بێجگە لە خوا چەند پەرستراوێکیان بۆ خۆیان داناوە بەو ھیوایەی یارمەتی بدرێن');
INSERT INTO "bamoki" VALUES(36,75,'36:75','(ئەو پەرستراوانە) ناتوانن یارمەتیان بدەن بەڵام ئەمان سەربازی ئامادەن، لە خزمەتیاندان (وملکەچی ئەو بتانەن)');
INSERT INTO "bamoki" VALUES(36,76,'36:76','جا قسەکانیان خەمبارت نەکات چونکە بەڕاستی ئەوەی دەی شارنەوەو ئەوەی ئاشکرای دەکەن دەیزانین');
INSERT INTO "bamoki" VALUES(36,77,'36:77','ئایا ئادەمی نەیزانیوە کە ئەومان لە نوتفە (دڵۆپە ئاوێك) دروستکردووە کەچی یەکسەر دەبێتە دوژمنێکی سەرسەختی دیار (دەمقاڵێ کەرێکی ئاشکرایە)');
INSERT INTO "bamoki" VALUES(36,78,'36:78','وە نموونەمان بۆ دێنێتەوە و بەدیھێنانی خۆی لە بیر چۆتەوە (ئەو بێ بڕوایە) دەڵێت کێ ئێسکەکان زیندوو دەکاتەوە لە کاتێکدا کە ڕزیون');
INSERT INTO "bamoki" VALUES(36,79,'36:79','(ئەی موحەممەد صلى الله عليه وسلم) بڵێ ئەو زاتە زیندوویان دەکاتەوە کە لە یەکەمجارەوە بەدیھێناون وئەو بە ھەموو دروست کراوێك زانا و ئاگادارە');
INSERT INTO "bamoki" VALUES(36,80,'36:80','زاتێک کە لە درەختی سەوز ئاگری بۆ دروستکردون ئیتر ئێوە ئاگری پێ دەکەنەوە');
INSERT INTO "bamoki" VALUES(36,81,'36:81','ئایا زاتێك کە ئاسمانەکان و زەوی بەدیھێناوە ناتوانێ وێنەیان دروست بکات؟ بەڵێ دەتوانێت و ھەر ئەویش دروستکەری زانایە');
INSERT INTO "bamoki" VALUES(36,82,'36:82','بەڕاستی کاری ئەو (خوا) ئەمەیە ھەر کاتێك بیەوێت شتێك بە دیبھێنێت پێی دەڵێ: ببە، دەست بەجێ (ئەو شتە) دەبێت');
INSERT INTO "bamoki" VALUES(36,83,'36:83','جا پاك و بێگەردە ئەو خوایەی کە موڵکایەتی ھەموو شتێك بەدەست ئەوە و بۆ لای ئەویش دەگەرێنرێنەوە');
INSERT INTO "bamoki" VALUES(37,1,'37:1','سوێند بەو فریشتانە ڕیز بوون (لە کاتی عیبادەتدا)');
INSERT INTO "bamoki" VALUES(37,2,'37:2','وە سوێند بەوانە کە دەخوڕن بەسەر ھەورەکاندا');
INSERT INTO "bamoki" VALUES(37,3,'37:3','وە سوێند بەخوێنەرانی قورئان');
INSERT INTO "bamoki" VALUES(37,4,'37:4','بەڕاستی پەرستراوی ئێوە تاك و تەنھایە');
INSERT INTO "bamoki" VALUES(37,5,'37:5','پەروەردگاری ئاسمانەکان و زەوی وئەوەی لە نێوانیاندایە و پەروەردگاری خۆرھەڵاتەکان');
INSERT INTO "bamoki" VALUES(37,6,'37:6','بێگومان ئێمە ئاسمانی دونیامان بەجوانی ئەستێرەکان ڕازاندۆتەوە');
INSERT INTO "bamoki" VALUES(37,7,'37:7','وە لەھەموو شەیتانێکی سەر کەش پاڕاستوومانە');
INSERT INTO "bamoki" VALUES(37,8,'37:8','ناتوانن گوێ بگرن بۆ ئەو فریشتانەی لە بەرزترین پلەدان وە (ئەگەر بچن گوێ بگرن) لە ھەموو لایەکی (ئاسمانەوە) ئاگر باران دەکرێن');
INSERT INTO "bamoki" VALUES(37,9,'37:9','ڕاودەنرێن زۆر بە توندی و (لە قیامەتدا) سزایان بەردەوامە وسەختە');
INSERT INTO "bamoki" VALUES(37,10,'37:10','(شەیتانەکان ناتوانن گوێ بگرن) مەگەر یەکێکیان بە دزیەوە ھەواڵێك (لە فریشتەیەك ببیستێ و) بە پەلە بیفڕێنێت پزیسکی گڕدار وسوتێنەری شوێن دەکەوێت (و دەی سوتێنێت)');
INSERT INTO "bamoki" VALUES(37,11,'37:11','ئەمجا (ئەی موحەممەد صلى الله عليه وسلم) بپرسە لەوانەی(کە بێباوەڕن) ئایا ئەوان دروستکراوێکی گەورەتر وگرنگتر و بەھێزترن ؟ یان ئەو شتانەی تر کە دروستمان کردووە (لە ئاسمان و خۆر و زەوی و... ) بەڕاستی ئێمە ئەوان (ئادەمی) مان دروستکرد لە قوڕێکی لینجی جیڕ');
INSERT INTO "bamoki" VALUES(37,12,'37:12','بەڵام (سەیرە) تۆ سەرت سوڕماوە (چۆن باوەڕ ناھێنن) کەچی ئەوان گاڵتە دەکەن (بە تۆ کە دەڵێیت زیندوو بوونەوە ھەیە)');
INSERT INTO "bamoki" VALUES(37,13,'37:13','وە کاتێك ئامۆژگاری بکرێن ئامۆژگاری وەرناگرن');
INSERT INTO "bamoki" VALUES(37,14,'37:14','وە کاتێ موعجیزەیەك ببینن دەیکەنە گاڵتەجاری');
INSERT INTO "bamoki" VALUES(37,15,'37:15','وە دەڵێن ئەم (قورئانە) ھیچ نیە جگە لە جادویەکی ئاشکرا نەبێت');
INSERT INTO "bamoki" VALUES(37,16,'37:16','(دەڵێن) ئایا ئێمە کە مردین و بووین بەخۆڵ و ئێسکی ڕزیو ئایا ئێمە زیندوو دەکرێینەوە؟!');
INSERT INTO "bamoki" VALUES(37,17,'37:17','لەگەڵ باوو باپیرانی پێشینانمان');
INSERT INTO "bamoki" VALUES(37,18,'37:18','(ئەی موحەممەد صلى الله عليه وسلم) بڵێ: بەڵێ (ھەمووتان زیندوو دەکرێنەوە) و لەو کاتەدا ئێوە زەلیل و سەرشۆڕن');
INSERT INTO "bamoki" VALUES(37,19,'37:19','چونکە ئەو (زیندوو بوونەوەیە) ھەر یەك دەنگە ئیتر دەست بەجێ زیندوون و دەڕوانن');
INSERT INTO "bamoki" VALUES(37,20,'37:20','وە دەڵێن: وای بۆ خۆمان ئەمە ڕۆژی تۆڵەسەندن وپاداشتە');
INSERT INTO "bamoki" VALUES(37,21,'37:21','(پێیان دەوترێت) ئەمە ئەو ڕۆژی بڕیاردانەیە کە باوەڕتان پێی نەبوو');
INSERT INTO "bamoki" VALUES(37,22,'37:22','(خوا بەفریشتەکان دەفەرموێت) ئەوانەی ستەمیان کرد کۆیان بکەنەوە لەگەڵ ھاوڕێکانیان (ھاو وێنەکانیان) لەگەڵ ئەوانەش دەیان پەرستن');
INSERT INTO "bamoki" VALUES(37,23,'37:23','جگە لە خوا ئەمجا ڕێگای دۆزەخیان نیشان دەن');
INSERT INTO "bamoki" VALUES(37,24,'37:24','وە ئەوانە ڕاوەستێنن بێگومان پرسیاریان لێدەکرێت');
INSERT INTO "bamoki" VALUES(37,25,'37:25','(پێیان دەوترێت) ئەوە چیتانە یارمەتی یەکتر نادەن');
INSERT INTO "bamoki" VALUES(37,26,'37:26','(نەخێر یارمەتی یەکتر نادەن) بەڵکو ئەوان ھەموو (بەندەو پەرستراوەکانیان) لەو ڕۆژەدا گەردن کەچن بۆ خوا');
INSERT INTO "bamoki" VALUES(37,27,'37:27','و (لەو ڕۆژەدا) ڕوو دەکەنە یەکترو پرسیار لەیەکتر دەکەن');
INSERT INTO "bamoki" VALUES(37,28,'37:28','(گومڕاکراوەکان بەگومڕاکەرەکان) دەڵێن بەڕاستی ئێوە بوون لەلای ڕاستەوە ھاتنە لامان (نەتان ھێشت شوێنی کەوین)');
INSERT INTO "bamoki" VALUES(37,29,'37:29','(گومڕاکەرەکان) دەڵێن (نەخێر ڕاست ناکەن) بەڵکو ئێوە خۆتان ئیماندار نەبوون');
