INSERT INTO "bamoki" VALUES(37,137,'37:137','بەڕاستی ئێوە (ئەی قوڕەیشیەکان) بە بەیانیاندا (بە ڕۆژ) دەڕۆن بەسەر (شوێنەوارەکانیاندا)');
INSERT INTO "bamoki" VALUES(37,138,'37:138','و بەشەودا ئایا ئیتر ژیر نابن');
INSERT INTO "bamoki" VALUES(37,139,'37:139','وە بە ڕاستی یونس (پێغەمبەرێك بوو) لەپێغەمبەران');
