INSERT INTO "bamoki" VALUES(37,141,'37:141','ئەمجا (ئەو کەشتیە لەبەر ئەوەی باری قوورس بوو، لەترسی نوقم نەبوونی، خەڵکەکەی کە لەناویدا بوون، قورعەکێشیان کرد بۆ ئەوەی ئەو کەسەی دەرچوو بیخەنە ناو دەریاکەوە وباری کەشتەیەکە سووکتر ببێت) قورعەیان کرد (یونس) ئەو کەسە بوو لەقورعەکە سەرنەکەوت');
INSERT INTO "bamoki" VALUES(37,142,'37:142','(خرایە دەریاکە و) ئەمجا نەھەنگێك قووتی دا ئەویش شایانی سەرزەنشت بوو');
INSERT INTO "bamoki" VALUES(37,143,'37:143','خۆ ئەگەر بەڕاستی ئەو لە یادکارانی (خوا) نەبوایە');
INSERT INTO "bamoki" VALUES(37,144,'37:144','ھەتا ڕۆژی زیندوو بوونەوە لەناو سکی ئەو (نەھەنگەدا) دەمایەوە');
INSERT INTO "bamoki" VALUES(37,145,'37:145','بە ھۆی ئەوەوە فڕێماندایە زەویەکی ڕووت (بێ دار و درەخت) و نەخۆشیش بوو');
