INSERT INTO "bamoki" VALUES(37,147,'37:147','ئەوسا بە پێغەمبەرێتی ناردمان بۆ لای سەد ھەزار کەس یان زیاتر');
INSERT INTO "bamoki" VALUES(37,148,'37:148','گەلەکەش باوەڕیان ھێنا و بەو ھۆیەوە بەھرەمەندمان کردن لەژیان تا ماوەیەك');
INSERT INTO "bamoki" VALUES(37,149,'37:149','ئەمجا (ئەی موحەممەد صلى الله عليه وسلم) پرسیاریان لێ بکە ئایا کچان بۆ پەروەردگاری تۆیە و کوڕانیش ھی ئەوانە؟');
INSERT INTO "bamoki" VALUES(37,150,'37:150','یان ئەوان بە دیارەوە بوون کە ئێمە فریشتەکانمان بە مێینە دروستکرد');
INSERT INTO "bamoki" VALUES(37,151,'37:151','بێدار بن بەڕاستی ئەوان لە دەس ھەڵبەسی خۆیانەوە دەڵێن');
