INSERT INTO "bamoki" VALUES(37,155,'37:155','دەی ئایا بیر ناکەنەوە؟');
INSERT INTO "bamoki" VALUES(37,156,'37:156','یان بەڵگەیەکی ڕوونتان (لەم بارەیەوە) ھەیە');
INSERT INTO "bamoki" VALUES(37,157,'37:157','دەی کتێبەکەتان (بەڵگەکەتان) بھێنن ئەگەر ڕاست دەکەن');
INSERT INTO "bamoki" VALUES(37,158,'37:158','وە ئەو (بێ باوەڕانە) لە نێوان خواو جنۆکەدا خزمایەتیان بڕیارداوە سوێند بەخوا بێگومان جنۆکەکان دەیانزانی کە بەڕاستی ئەوان ئامادە دەکرێن (لەڕۆژی دواییدا)');
INSERT INTO "bamoki" VALUES(37,159,'37:159','پاك و بێگەردە خوا لەوەی ئەو (بێ باوەڕانە) دەیڵێن');
INSERT INTO "bamoki" VALUES(37,160,'37:160','(ھەموویان سزا دەدرێن) بێجگە بەندە خاوێن کراوەکانی خوا');
INSERT INTO "bamoki" VALUES(37,161,'37:161','ئەمجا بەڕاستی ئێوە و ئەو (بتانە)یش کە دەیپەرستن');
INSERT INTO "bamoki" VALUES(37,162,'37:162','ئێوە ناتوانن لەبەرانبەری خوادا گومڕاکەری (کەس) بن');
INSERT INTO "bamoki" VALUES(37,163,'37:163','بێجگە ئەو کەسەی (خوا دەزانێت) دەچێتە دۆزەخەوە');
INSERT INTO "bamoki" VALUES(37,164,'37:164','(فریشتەکان دەڵێن) ھیچ کام لەئێمە نیە مەگەر پلەو شوێنێکی دیاریکراوی خۆی ھەیە');
INSERT INTO "bamoki" VALUES(37,165,'37:165','وە بەڕاستی ئێمە ھەموومان بەڕیز وەستاوین (بۆ عیبادەت و بەجێھێنانی فەرمانی خوا)');
INSERT INTO "bamoki" VALUES(37,166,'37:166','وە بێگومان ئێمەین ھەمیشە تەسبیحات وزیکر ویادی ئەو دەکەین');
INSERT INTO "bamoki" VALUES(37,167,'37:167','بێگومان ھەر چەندە بت پەرستان دەیانووت');
INSERT INTO "bamoki" VALUES(37,168,'37:168','بەڕاستی ئەگەر کتێبێ (ی ئاسمانی وەك کتێبی) پێشینانمان ھەبوایە');
INSERT INTO "bamoki" VALUES(37,169,'37:169','بەڕاستی ئێمە لە بەندە پاك و خاوێنەکانی خوا دەبووین');
INSERT INTO "bamoki" VALUES(37,170,'37:170','ئەمجا (کە کتێبیان بۆ ھات) باوەڕیان پێ نەھێنا، جابەزوویی دەزانن (سەر ئەنجامی بێ بڕواییەکەیان چۆن دەبێت)');
INSERT INTO "bamoki" VALUES(37,171,'37:171','سوێند بەخوا بێگومان پێشتر بڕیارمان داوە بۆ بەندە نێردراوەکانمان');
INSERT INTO "bamoki" VALUES(37,172,'37:172','کە بێگومان ھەر ئەوان سەر دەخرێن');
