INSERT INTO "bamoki" VALUES(37,174,'37:174','تۆش وازیان لێ بھێنە تا ئەو ماوەی(دیاریکراوە بۆ لەناوبردنیان)');
INSERT INTO "bamoki" VALUES(37,175,'37:175','وە بیان بینە (سەرنجامیان چۆن دەبێت) لەمەولا ئەوانیش دەبینن (چییان بەسەر دێت)');
INSERT INTO "bamoki" VALUES(37,176,'37:176','جا ئایا بەپەلە داوای سزای ئێمە دەکەن');
INSERT INTO "bamoki" VALUES(37,177,'37:177','ھەر کاتێك سزای ئێمە ھاتە خوارەوە بۆ ناو ماڵەکانیان ئەوسا ترسێنراوان چ ڕۆژێکی خراپ و تەنگیان دەبێت');
INSERT INTO "bamoki" VALUES(37,178,'37:178','وە وازیان لێ بھێنە ھەتائەو ماوەی (دیاریکراوە بۆیان)');
INSERT INTO "bamoki" VALUES(37,179,'37:179','وە ببینە ئەمجا لەمەولا ئەوانیش دەبینن');
INSERT INTO "bamoki" VALUES(37,180,'37:180','پەروەردگارت پاك و بێگەردە، پەروەردگاری باڵا دەست لەوەی بێ باوەڕان دەیڵێن (دەربارەی)');
INSERT INTO "bamoki" VALUES(37,181,'37:181','سڵاو و درودیش بۆ گشت پێغەمبەران');
INSERT INTO "bamoki" VALUES(37,182,'37:182','سوپاس و ستایشیش بۆ خوای پەروەردگاری ھەموو جیھانیان');
INSERT INTO "bamoki" VALUES(38,1,'38:1','بە (صاد) دەخوێنرێتەوە خوازاناترە بە ماناکەی بۆ زانیاری زیاتر سەیر سەرەتای سورەتی (البقرة) بکە سوێند بە قورئانی خاوەن ڕێز و پەند و ئامۆژگاری');
INSERT INTO "bamoki" VALUES(38,2,'38:2','بەڵام ئەوانەی بێ باوەڕن ڕۆ چوون، لە خۆبەگەورە زانین و دژایەتیدا (بەرانبەر ئیسلام و شوێن کەوتووانی)');
INSERT INTO "bamoki" VALUES(38,3,'38:3','چەندەھا گەلمان پێش ئەوان ( پێش کافرانی مەککە) لەناو برد ئەمجا ھاواریان کرد لە حاڵێکدا کاتی ڕزگار بوون نەمابوو');
INSERT INTO "bamoki" VALUES(38,4,'38:4','سەریان سوڕدەما کە ترسێنەرێکیان لە خۆیان بۆ ھاتووە وە بێ باوەڕان دەیانووت ئەم (پێغەمبەرە) جادوگەرێکی زۆر درۆزنە');
INSERT INTO "bamoki" VALUES(38,5,'38:5','ئایا ئەو (پێغەمبەرە) ھەموو خواکانی کردوەتە یەك خوا بەڕِاستی ئەمە (کەیەك خوا ببێت) شتێکی زۆر سەرسوڕھێنەرە');
INSERT INTO "bamoki" VALUES(38,6,'38:6','وە گەورەکانی (قوڕەیش لەگەڵ پێغەمبەر قسەیان کرد و ھەڵسان) بە پەلە دەرچوون (بەخەڵکیان ووت) بڕۆن و خۆگربن لەسەر (پەرستنی) خواکانتان بەڕاستی ئەمەی (موحەممەد صلى الله عليه وسلم) دەیڵێت) شتێکە لە ئێوە داوا کراوە (ھەتا ببێتە گەورەی ھەموو عەرەب)');
INSERT INTO "bamoki" VALUES(38,7,'38:7','ئەمەمان نەبیستووە لە کۆتا ئایندا (کە نەسرانیە) ئەمە تەنھا شتێکی ھەڵبەستراوە');
INSERT INTO "bamoki" VALUES(38,8,'38:8','ئایا چۆن وەحی لە ناو ھەموومان دا بۆ ئەو (موحەممەد صلى الله عليه وسلم) دابەزێنراوە (نەخێر ڕاست ناکەن) بەڵکو ئەوانە لە گوماندان لە قورئانەکەی من (نەخێر وانیە) بەڵکو ئەوانە ھێشتا سزای منیان نەچەشتووە (بۆیە باوەڕناھێنن)');
INSERT INTO "bamoki" VALUES(38,9,'38:9','یان خەزێنەکانی بەزەیی پەروەردگاری زاڵ و باڵادەست و بەخشندەت لای ئەوانە؟');
INSERT INTO "bamoki" VALUES(38,10,'38:10','یان دەسەڵات و پاشایی ئاسمانەکان و زەوی و ئەوەی لە نێوانیاندایە ھی ئەوانە (ئەگەر وایە) دەبا بەرز ببنەوە لە ناو ھۆکارەکانی بەرز بوونەوەدا (تا دەگەنە عەرش ئەمجا وەحی دابەزێنن بۆ ھەر کەسێك خۆیان دەیانەوێت)');
INSERT INTO "bamoki" VALUES(38,11,'38:11','(بێ بڕوایانی قوڕەیش) لەشکر و کۆمەڵێکی بێ نرخن لەو کۆمەڵانەی (دژی پێغەمبەرانن بەزوویی) دەشکێنرێن و سەر شۆڕ دەبن');
INSERT INTO "bamoki" VALUES(38,12,'38:12','پێش ئەوان گەلی نوح و عاد و فیرعەونی خاوەن مێخەکان باوەڕیان (بە پێغەمبەرەکانیان) نەھێنا');
INSERT INTO "bamoki" VALUES(38,13,'38:13','وە (گەلی) سەمود و گەلی لوط و خاوەن باخە چڕو پڕەکان ئەوانە تاقم و کۆمەڵانێك بوون');
INSERT INTO "bamoki" VALUES(38,14,'38:14','ھەر ھەموویان باوەڕیان بە پێغەمبەران نەھێنا بۆیە سزای منیان بەسەرا چەسپا (و لە ناو چوون)');
INSERT INTO "bamoki" VALUES(38,15,'38:15','ئەمانە تەنھا چاوەڕوانی دەنگێکی زۆر گەورە دەکەن ئەو دەنگە (فوو کردن بە کەڕەنادا) کەمترین ماوە دواخستنی نیە');
INSERT INTO "bamoki" VALUES(38,16,'38:16','(بەگاڵتە پێکردنێکەوە) وە ووتیان: ئەی پەروەردگارمان پێش ڕۆژی لێپرسینەوە بەشەکەمان (لە سزا، یان پاداشت) پێبدە');
INSERT INTO "bamoki" VALUES(38,17,'38:17','(ئەی موحەممەد صلى الله عليه وسلم) دان بە خۆتدا بگرە لەسەر ئەو قسانەی کە دەیکەن بیری بەندەمان (داود) ی خاوەن ھێز و توانا بکەرەوە بێگومان ئەو ھەمیشە گەڕاوە بوو بۆ لای خوا');
INSERT INTO "bamoki" VALUES(38,18,'38:18','بەڕاستی ئێمە چیاکانمان رام ھێنا بوو تەسبیحاتی خوایان دەکرد لەگەڵ ئەو (لەگەڵ داوددا) بەئێواران و بەیانیاندا');
INSERT INTO "bamoki" VALUES(38,19,'38:19','وە باڵندەکان کە لەدەوری ئەو (داود) کۆدەکرانەوە ھەموو(بۆ تەسبیحات) بۆلای ئەو دەگەڕانەو(تا لەگەڵ داود یادی خوا بکەن)');
INSERT INTO "bamoki" VALUES(38,20,'38:20','وە پاشایەتیەکەی ئەومان بەھێز کرد دانایی و گوفتاری جیاکەرەوەمان پێ بەخشی');
INSERT INTO "bamoki" VALUES(38,21,'38:21','وە ئایا ھەواڵی داوا چیەکانت پێ گەیشتووە کاتێك بەدیواری خەڵوەتگاکەدا سەرکەوتن');
INSERT INTO "bamoki" VALUES(38,22,'38:22','لە ناکاودا چوونە ژوورەوە بۆ لای داود ئەویش داچڵەکا و ترسا لێیان ووتیان: مەترسە (ئێمە) دوو داواچین یەکێکمان ستەمی لەوی تر کردووە جا لە نێوانماندا بەھەق وڕاستی بڕیاربدە ولە ڕاستی لامەدە (لە بڕیارەکەتدا) وە ڕێنمونیمان بکە بۆ رێگای ڕاست');
INSERT INTO "bamoki" VALUES(38,23,'38:23','(یەکیان ووتی) بێگومان ئەمە برامە، نەوەدو نۆ سەر مەڕی ھەیە و منیش یەك مەڕم ھەیە کەچی دەڵێت ئەو مەڕەش بدە بەمن (بیسپێرە بەمن) و لەکاتی قسە کردندا زاڵە بەسەرمدا');
INSERT INTO "bamoki" VALUES(38,24,'38:24','(ئەمجا داود علیە السلام بەبێ ئەوەی گوێ لەبراکەی تریان بگرێت وبەپەلە ئەم حوکمەی دا) ووتی: سوێند بەخوا بەڕاستی ستەمی لێکردوی کە داوات لێدەکات تاقە مەڕەکەت بخاتە ناو مەڕەکانی خۆیەوە و بێگومان زۆر لەشەریك و ھاوبەشەکان ستەم لەیەکتری دەکەن بێجگە لەوانەی باوەڕیان ھێناوە و کردەوەی چاکیان کردووە، ئەوانەش زۆر کەمن، داود زانی کە ئێمە تەنھا تاقیمان کردوەتەوە بۆیە داوای لێبوردنی کرد لە پەروەردگاری وکەوت بە ڕوودا بە سوژدە بردنەوە و تۆبەی کرد (لەوەی نەدەبوو بەبێ گوێگرتن لەکەسی بەرامبەر حوکم بدات وپەلەش بکات لەبڕیارداندا)');
INSERT INTO "bamoki" VALUES(38,25,'38:25','ئێمەش لەو ھەڵەی ئەو خۆش بووین بەڕاستی ئەو (داود) پلە و پایەی بەرزە و نزیکە لە ئێمەوە ودواڕۆژێکی چاکیشی ھەیە');
INSERT INTO "bamoki" VALUES(38,26,'38:26','ئەی داود بێگومان ئێمە تۆمان کرد بەجێنشینی(پێغەمبەرانی پێش خۆت) لە زەویدا کەوابوو تۆش لە نێوان خەڵکدا بەڕاستی فەرمانڕەوایی بکە شوێن ھەوا و ئارەزوو مەکەوە با لەڕێگای خوا لات نەدات بێگومان ئەوانەی لەڕێگای خوا لادەدەن و گومڕادەبن، سزای سـەخـتـیـان بۆ ھـەیە چـونکە ڕۆژی لێپرسینـەوەیـان لەبیرکردبوو');
INSERT INTO "bamoki" VALUES(38,27,'38:27','ئێمە ئاسمان و زەوی و ئەوەی لە نێوانیاندایە بەبێھودە دروستمان نەکردووە ئەوە گومانی ئەو کەسانەیە کە بێ باوەڕبوون سا ھاوار بۆ ئەوانەی کە بێ باوەڕبوون بەئاگری دۆزەخ');
INSERT INTO "bamoki" VALUES(38,28,'38:28','ئایا ئەوانەی باوەڕیان ھێناوەو کردەوە چاکەکانیان کردووە لەگەڵ خراپەکاران لە زەویدا وەك یەکیان دانێین (لە دوا رۆژدا، نەخێر وانیە) یان پارێزکاران ولەخواترسان وەك بەدکرداران لێ بکەین؟!');
INSERT INTO "bamoki" VALUES(38,29,'38:29','(ئەم قورئانە) کتێبێکی پیرۆزە ناردوومانەتە خوارەوە بۆ تۆ بۆ ئەوەی بیر بکەنەوە لەئایەتەکانی، وە خاوەنانی بیر و ژیری پەندی لێ وەربگرن');
INSERT INTO "bamoki" VALUES(38,30,'38:30','ئێمە سولەیمانمان بەخشی بە داود (علیھما السلام) ئەو (سولەیمان) بەندەیەکی چاك بوو، بێگومان ئەو زۆر ڕوو لەخوا بوو');
INSERT INTO "bamoki" VALUES(38,31,'38:31','سەر لە ئێوارە کاتێك ئەسپانی ڕەسەن و خێرا نیشانی سولەیمان درا');
INSERT INTO "bamoki" VALUES(38,32,'38:32','جا (سولەیمان) ووتی خۆشەویستی ئەم ئەسپانەم باودا بەسەر یاد و عیبادەتی پەروەردگارمدا (کە نوێژی عەسر بوو) (سەیری ئەسپەکانی دەکرد) ھەتا لەچاوو گوم بوون');
INSERT INTO "bamoki" VALUES(38,33,'38:33','(ووتی) جارێکی تر ئەو ئەسپانەم بۆ بگێڕنەوە ئەمجا دەستی ھێنا بە دەست و قاچ و گەردن و یاڵی (ئەو ئەسپانەدا)');
INSERT INTO "bamoki" VALUES(38,34,'38:34','سوێند بەخوا بەڕاستی سولەیمانمان تاقیکردەوە و لاشەی (لەتە کوڕێك) مان فڕێدا بەسەر تەختەکەیدا (چونکە پێشتر سوێندی خواردبوو، کە بەسەر تەواوی خێزانەکانیدا ئەسوڕێتەوە وکۆ ئەبێتەوە لەگەڵیاندا، وھەریەکەشیان کوڕێکی ببێت ولەپێناو خوادا جیھاد بکەن بەبێ ئەوەی (ان شاء اللە) بکات لەدواییدا لەھەر ھەموو خێزانەکانی تەنھا کوڕێکی نوقسانی ناتەواوی بوو، خوای گەورەش بەمە تاقیکردەوە) پاشان تەوبەی کرد و گەڕایەوە بۆ لای خوا');
INSERT INTO "bamoki" VALUES(38,35,'38:35','(سولەیمان) ووتی ئەی پەروەردگارم لێم خۆشبە و دەسەڵات و پاشایەتێکم پێ ببەخشە کەدوای من دەس نەدا بۆ کەسی تر بێگومان تەنھا تۆی بەخشندەی (ھەموو شتێکی)');
INSERT INTO "bamoki" VALUES(38,36,'38:36','(دوای نزاکەی) (با) مان بۆ ڕام ھێنا وخستمانە خزمەتیەوە بەفەرمانی ئەو بەنەرمی دەڕۆیی بۆ ھەر جێگایەك بیویستایە');
INSERT INTO "bamoki" VALUES(38,37,'38:37','وە ھەموو (جنۆکەو) شەیتانەکانمان بۆ ڕام ھێنا بوو کە بینا سازو مەلەوانی ژێر دەریا بوون');
INSERT INTO "bamoki" VALUES(38,38,'38:38','وە ھەندێکی تریان (کە سەرپێچی سولەیمانیان کردبوو ڕاممان ھێنان بۆی) بە پێکەوە بەستراوی لە کۆت زنجیردا');
INSERT INTO "bamoki" VALUES(38,39,'38:39','ئەمەی (کە باسکرا) بەخششی ئێمەیە (بۆ تۆ)، ئەمجا بەویستی خۆت بیبەخشە، یان بیگرەوە و مەیبەخشە بەبێ پرسینەوە (لێت)');
INSERT INTO "bamoki" VALUES(38,40,'38:40','وە بەڕاستی ئەو (سولەیمان) پلەی بەرزی ھەیە لای ئێمە وە سەرئەنجامی چاکی ھەیە');
INSERT INTO "bamoki" VALUES(38,41,'38:41','وە (ئەی موحەممەد صلى الله عليه وسلم) باسی بەندەی ئێمە ئەیوب بکە کاتێك ھاواری لە پەروەردگاری کرد (ووتی) بەڕاستی شەیتان تووشی زەحمەت و ناڕەحەتی و ئازاری کردووم');
INSERT INTO "bamoki" VALUES(38,42,'38:42','(پێی وترا) پێت بدە بە زەویدا ئەوە کانیاوێکی ساردە بۆ خۆشتن و خواردنەوە (پێی دا بەزەویدا و ئاو ھەڵقوڵی ولەو ئاوە خۆی شت و ئاوی خواردەوە ولە نەخۆشیەکەیشی چاك بوویەوە)');
INSERT INTO "bamoki" VALUES(38,43,'38:43','وە ژن و مناڵەکانی و ئەوەندەی تریشمان لەگەڵیاندا بە (ئەیوب) بەخشی بەڕەحم ومیھرەبانی لە لایەن خۆمانەوە (بۆ ئەیوب) وە بۆ پەند و ئامۆژگاری بۆ خاوەنانی ژیری و بیرو ھۆش');
INSERT INTO "bamoki" VALUES(38,44,'38:44','(پێمان ووت) چەپکێ شووڵ (داری زۆر باریك) بە دەستەوە بگرە و پێی بدە (لەخێزانت یەکجار) و سوێندەکەت مەشکێنە (کە لە خێزانت خوارد بوو سەد داری لێ بدەی) بەڕاستی ئێمە ئەو (ئەیوب)مان بەخۆگری بینی چ بەندەیەکی چاك بوو بەڕاستی ئەو زۆر گوێڕایەڵ و ڕوو لە خوا بوو');
INSERT INTO "bamoki" VALUES(38,45,'38:45','وە باسی بەندەکانی ئێمە بکە ئیبراھیم و ئیسحاق و یەعقوب کە خاوەنی ھێزو توانا و بینایی بوون');
INSERT INTO "bamoki" VALUES(38,46,'38:46','بەڕاستی لەبەر ئەوەی ھەمیشە ڕۆژی دواییان لەبیردابوو ئێمە (بە بەندەی تایبەتی خۆمان) ھەڵمان بژاردن');
INSERT INTO "bamoki" VALUES(38,47,'38:47','وە بەڕاستی ئەوانە (ھەر سیانیان) لای ئێمە لەبەند ھەڵبژێردراوە چاکەکانن');
INSERT INTO "bamoki" VALUES(38,48,'38:48','وە باسی (بەندەکانی ئێمە) ئیسماعیل و ئەلیەسەع و ذولکیفل بکە کەھەموویان لە چاکەکارانن');
INSERT INTO "bamoki" VALUES(38,49,'38:49','ئەم (باسی پێغەمبەرانە) باس و ناوبانگی چاکیانە لە دونیادا و بێگومان پارێزکاران سەرئەنجامێکی چاکیان ھەیە');
INSERT INTO "bamoki" VALUES(38,50,'38:50','(کە بریتیە لە) بەھەشتانی جێگای مانەوەی ھەمیشەیی، کەدەرگاکانی کراوەتەوە بۆیان');
INSERT INTO "bamoki" VALUES(38,51,'38:51','لەوێدا (لەسەر کورسی ڕازاوە) پاڵیان داوەتەوە تێیدا داوای میوەھاتی زۆر و جۆرەھا خواردنەوە دەکەن');
INSERT INTO "bamoki" VALUES(38,52,'38:52','و حۆریانی ھاوتەمەنیان لایە (خێزانیانن) کەچاویان بڕیوەتە مێردەکانیان');
INSERT INTO "bamoki" VALUES(38,53,'38:53','ئەمە ئەو (نازو نیعمەتانە)یە کە بەڵێنتان پێـدرا بوو بۆ ڕۆژی لێپرسینەوە (ڕۆژی قیامەت)');
INSERT INTO "bamoki" VALUES(38,54,'38:54','بەڕاستی ئەمە ڕزق و ڕۆزی ئێمەیە ھەرگیز تەواو بوونی بۆ نیە');
INSERT INTO "bamoki" VALUES(38,55,'38:55','ئا ئەمە (پاداشتی پارێزکارانە) و بێگومان سەرکەشانیش خراپترین پاشە ڕۆژیان ھەیە');
INSERT INTO "bamoki" VALUES(38,56,'38:56','دۆزەخە کەدەچنە ناویەوە، ئای چ جێگایەکی بەدو خراپە');
INSERT INTO "bamoki" VALUES(38,57,'38:57','ئەمە و ئاوی لەکوڵ و کێم وزووخاوە، دەبا بیچەژن');
INSERT INTO "bamoki" VALUES(38,58,'38:58','وە چەند جۆری تر سزایان ھەیە لەو شێوە');
INSERT INTO "bamoki" VALUES(38,59,'38:59','(بە گومڕاکەرەکان دەوترێت) ئەمە کۆمەڵێکە لەگەڵ ئێوەدا دێنە دۆزەخ (گومڕاکەرەکانیش دەڵێن) بەخێر نەیەن بێگومان ئەوانە دەچنە ناو ئاگری دۆزەخ');
INSERT INTO "bamoki" VALUES(38,60,'38:60','ئەوان (بە گومڕاکەرەکان) دەڵێن بەڵکو ئێوە بەخێر نەیەن ئێوە (لەدونیا گومـڕاتان کردین)و ئەم سزاتان پێش ھێنا بۆمان ئای چ جێگاو مەنزڵێکی خراپە');
INSERT INTO "bamoki" VALUES(38,61,'38:61','دەڵێن: ئەی پەروەردگارمان ھەر کەس (ھۆی) پێش ھێنانی ئەم سزایە بوو بۆ ئێمە ئەوە سزای ئەو کەسە لەدۆزەخدا چەند قات بکەرەوە');
INSERT INTO "bamoki" VALUES(38,62,'38:62','وە (گومڕاکەرەکان) دەڵێن ئەوە چیمانە بۆچ پیاوانێك نابینین کە ئێمە لە خراپەرکارانمان دەژماردن');
INSERT INTO "bamoki" VALUES(38,63,'38:63','ئایا ئەوانەن کە گاڵتەمان پێدەکردن؟ یان (لێرەن بەڵام) چاوەکان لێیان کەلا بووە (و نایان بینن)');
INSERT INTO "bamoki" VALUES(38,64,'38:64','بێگومان ئەوە (کە باسکرا) ئاژاوە و شەڕە قسەی دۆزەخیانە، وڕاستە (و دێتەدی)');
INSERT INTO "bamoki" VALUES(38,65,'38:65','(ئەی موحەممەد صلى الله عليه وسلم) بڵێ من تەنھا ترسێنەرێکم و ھیچ پەرستراوێکی ڕاست نیە بێجگە لە خوای تەنھای بەدەسەڵات');
INSERT INTO "bamoki" VALUES(38,66,'38:66','پەروەردگاری ئاسمانەکان و زەوی و ئەوەی لە نێوانیاندایە زاڵ و باڵادەست و لێبوردەیە');
INSERT INTO "bamoki" VALUES(38,67,'38:67','(ئەی موحەممەد صلى الله عليه وسلم) بڵێ ئەوە ھەواڵێکی زۆر گەورەیە');
INSERT INTO "bamoki" VALUES(38,68,'38:68','کە ئێوە گوێی پێ نادەن');
INSERT INTO "bamoki" VALUES(38,69,'38:69','(بڵێ ئەگەر وەحیم بۆ نەھاتایە) من ھیچ ئاگام لەکۆمەڵی ھەرە بڵند (فریشتەکان) نەبووە کاتێك دەمەقاڵێیان دەکرد (دەربارەی ئادەم)');
INSERT INTO "bamoki" VALUES(38,70,'38:70','نیگا بۆ من ناکرێت جگە لەوە نەبێت کە من تەنھا ترسێنەرێکی ڕوونکەرەوەم');
INSERT INTO "bamoki" VALUES(38,71,'38:71','ئەو کاتەی (دەمەقاڵێیان بوو) پەروەردگارت بەفریشتەکانی فەرموو بەڕاستی من مرۆڤێك لە قوڕ دروست دەکەم');
INSERT INTO "bamoki" VALUES(38,72,'38:72','ئەمجا کاتێك شێوەکەیم ڕێکخست و فوم پێدا کرد وگیانم کرد بە بەریدا لە گیانێك کە خۆم دروستم کردووە ئەوسا ھەموو بە سوژدە بردن بۆی بکەون بە ڕوودا');
INSERT INTO "bamoki" VALUES(38,73,'38:73','جا فریشتەکان ھەر ھەموویان سوژدەیان برد');
INSERT INTO "bamoki" VALUES(38,74,'38:74','بێجگە لە شەیتان کە خۆی بە گەورە دانا و لە بێ باوەڕان بوو');
INSERT INTO "bamoki" VALUES(38,75,'38:75','(خوا) فەرمووی ئەی ئیبلیس چی شتێك ڕێگری کردیت (لەوەی) کە سوژدە ببەیت بۆ کەسێ کە بە ھەردوو دەستی خۆم دروستم کردووە ئایا خۆت بەگەورە زانی، یاخو لەبەرزو بڵندەکانی؟');
INSERT INTO "bamoki" VALUES(38,76,'38:76','(شەیتان) ووتی من لە (ئادەم) چاکترم چونکە منت لەئاگر دروستکردووە، بەڵام ئەوت لەقوڕ درەستکردووە');
INSERT INTO "bamoki" VALUES(38,77,'38:77','(خوا) فەرمووی کەواتە لەو (بەھەشتە) دەرچۆ و بڕۆ، چونکە بێگومان تۆ دەرکراوی لەڕەحمەتی من');
INSERT INTO "bamoki" VALUES(38,78,'38:78','وە بەڕاستی نەفرینی منت لەسەر بێ تا ڕۆژی (پاداشت و تۆڵە)');
INSERT INTO "bamoki" VALUES(38,79,'38:79','(شەیتان) ووتی ئەی پەروەردگارم دەی مۆڵەتم بدە (نەمرم) تا رۆژێ (مردوان) زیندوو دەکرێنەوە');
INSERT INTO "bamoki" VALUES(38,80,'38:80','(خوا) فەرمووی: دەی بەڕاستی تۆ لە مۆڵەت دراوانی');
INSERT INTO "bamoki" VALUES(38,81,'38:81','تاڕۆژی کاتی دیاریکراو');
INSERT INTO "bamoki" VALUES(38,82,'38:82','(شەیتان) ووتی ئەمجا سوێند بەعیزەتت ھەر ھەموویان گومڕا دەکەم');
INSERT INTO "bamoki" VALUES(38,83,'38:83','ئەوانەیان نەبێت کەبەندەی پاك و ھەڵبژاردەی تۆن');
INSERT INTO "bamoki" VALUES(38,84,'38:84','(خوا) فەرمووی: من ھەقم ھەر ھەقیش دەڵێم');
INSERT INTO "bamoki" VALUES(38,85,'38:85','بەڕاستی دۆزەخ پڕ دەکەم لە تۆ و ئەوانەی (لە ئادەمییان) شوێنت دەکەون بەتێکڕا');
INSERT INTO "bamoki" VALUES(38,86,'38:86','(ئەی موحەممەد صلى الله عليه وسلم) بڵێ من ھیچ کرێ و پاداشتێك لەسەر گەیاندنی ئەم ئاینە لە ئێوە داواناکەم و من لەو کەسانەش نیم کە درۆ ھەڵبەستم و زۆر لە خۆم بکەم');
INSERT INTO "bamoki" VALUES(38,87,'38:87','ئەم (قورئانە) تەنھا پەند و ئامۆژگاریە بۆ ھەموو جیھانیان');
INSERT INTO "bamoki" VALUES(38,88,'38:88','بەڕاستی پاش ماوەیەك لە ھەواڵی ئەو (قورئانە) دەزانن');
INSERT INTO "bamoki" VALUES(39,1,'39:1','ئەم قورئانە ھێنراوەتەخوارەوە لە لایەن خوای باڵادەست و کار بەجێوە');
INSERT INTO "bamoki" VALUES(39,2,'39:2','بێگومان ئێمە بەھەق و ڕاستی قورئانمان بۆ تۆ ھێناوەتە خوارەوە کەواتە خوا بپەرستە و بە تەنھا ھەر بۆ ئەو ملکەچ بە');
INSERT INTO "bamoki" VALUES(39,3,'39:3','بێداربە ئاینی (گەردنکەچی) پاك و خاوێن ھەر بۆ خوایە وە کەسانێك جگە لە (خوا) چەند پەرستراوێکیان بۆ خۆیان داناوە (دەڵێن) تەنھا لەبەر ئەوە دەیان پەرستین نزیکمان بکەنەوە لە خوا بێگومان خوا بڕیار دەدات لە نێوانیاندا لەو شتانەدا کەئەوان تیایدا جیاواز بوون بەڕاستی خوا ڕێنمونی کەسێك ناکات کە درۆزنی زۆر بێ باوەڕە');
INSERT INTO "bamoki" VALUES(39,4,'39:4','ئەگەر خوا بیوویستایە منداڵێك بۆ خۆی بڕیار بدات ئەوە لەناو دروستکراوەکانیدا کامەی بوویستایە ھەڵی دەبژارد، پاك و بێگەردە ھەر ئەوە خوای تەنھا و زاڵ و بە دەسەڵات');
INSERT INTO "bamoki" VALUES(39,5,'39:5','(خوا) ئاسمانەکان و زەوی بەھەق دروستکردووە شەو دەھێنێت بەسەر ڕۆژدا و ڕۆژ دەھێنێت بەسەر شەودا و خۆر و مانگی ڕام ھێناوە ھەریەکەیان (لەخولگەی خۆیدا) تا ئەو ماوەیەی بۆی دیاریکراوە دەگەڕێت ودەسوڕێتەوە بێداربە ھەر ئەو (خوا) زاڵ و لێبوردەیە');
INSERT INTO "bamoki" VALUES(39,6,'39:6','(خوا) ئێوەی لە تاکە کەسێك (ئادەم) دروستکردووە پاشان لەو (کەسە) ھاوسەرەکەی (حەوای) بەدیھێناوە وە بۆ ئێوە ھەشت جووتی (نێر و مێ) لە ئاژەڵ بە دیھێناوە ئێوە دروست دەکات لەناو سکی دایکتاندا بە بەدی ھێنانی یەك لە دوای یەك لـەناو سێ تاریکی (ناو سك، مناڵدان، پەردەی کۆرپەلە) دا ئا ئەوە پەروەردگاری ئێوەیەکە دەسەڵات و پاشایی ھەر موڵکی ئەوە ھیچ پەرستراوێکی ڕاست جگە لە زاتی ئەو نیە، کەواتە ئێوە چۆن لە ئاینی ڕاست لادەدرێن');
INSERT INTO "bamoki" VALUES(39,7,'39:7','ئەگەر ئێوە بێ باوەڕبن ئەوە بێگومان خوا بێ نیازە لەئێوە و ڕازی نیە بە بێ باوەڕی بۆ بەندەکانی وە ئەگەر سوپاسی خوا بکەن (وگەردن کەچ بن) بەوە لێتان ڕازی دەبێت ھیچ گوناھبارێك گوناھی کەسێکی تر ھەڵناگرێت پاشان گەڕانەوەتان ھەر بۆ لای پەروەردگارتانە ئەوسا ھەواڵتان دەداتێ بەوەی کە دەتان کرد بەڕاستی خوا ئاگادارە بە نھێنی دڵان');
INSERT INTO "bamoki" VALUES(39,8,'39:8','وە کاتێك ئادەمی زیانێکی لێ بکەوێت دەگەڕێتەوە بۆ لای پەروەردگاری وھاواری لێ دەکات پاشان ئەگەر لەلایەن خۆیەوە نازو نیعمەتێکی دایە ئەوەی لەبیر دەچێت کە لەپێشترا ھاواری لە پەروەردگاری دەکرد وھاوتایانێك بۆ خوا دادەنێت بۆ ئەوەی (خەڵك) لە ڕێگای ئەو گومڕاکات (ئەی موحەممەد صلى الله عليه وسلم بە ھەڕەشەوە) بڵێ: بە بێ باوەڕێکەت کەمێك لەزەت وەرگرە لە ژیان چونکە بەڕاستی تۆ لەھاوەڵانی ئاگری دۆزەخی');
INSERT INTO "bamoki" VALUES(39,9,'39:9','(ھاوبەشدانەران چاکترن) یان کەسێك ملکەچ وخواپەرست بێت؟ لە کاتەکانی شەودا بە سوژدە بردن و ڕاوەستان (بە شەو نوێژوە خەریك بێت) کە لە (سزای) دواڕۆژ دەترسێت و بە ھیوای بەزەیی پەروەردگاریەتی (ئەی موحەممەد صلى الله عليه وسلم) بڵێ ئایا ئەوانەی دەزانن یەکسانن لەگەڵ ئەوانەی کە نازانن بەڕاستی تەنھا خاوەنانی ژیری ئامۆژگاری وەردەگرن');
INSERT INTO "bamoki" VALUES(39,10,'39:10','(ئەی موحەممەد صلى الله عليه وسلم) بڵێ بەو بەندانەم کە باوەڕیان ھێناوە لە (سزای) پەروەردگارتان خۆ بپارێزن کەسانێك لەم دونیایەدا چاکەیان کردووە، پاداشتی چاکیان بۆ ھەیە وە زەوی خوا فراوانە بەڕاستی تەنھا ئارامگران بەبێ حیساب وژماردن پاداشتیان دەدرێتەوە');
INSERT INTO "bamoki" VALUES(39,11,'39:11','(ئەی موحەممەد صلى الله عليه وسلم) بڵێ بەڕاستی من فەرمانم پێـدراوە کە خوا بپەرستم بە دڵسۆزی بۆی ملکەچ بم');
INSERT INTO "bamoki" VALUES(39,12,'39:12','وە فەرمانم پێـدراوە کە یەکەم کەسی موسڵمانان (ی ئوممەتم) بم');
INSERT INTO "bamoki" VALUES(39,13,'39:13','(ئەی موحەممەد صلى الله عليه وسلم) بڵێ بەڕاستی من دەترسم ئەگەر سەرپێچی پەروەردگارم بکەم لە سزای ڕۆژێکی گەورە (سزای قیامەت)');
INSERT INTO "bamoki" VALUES(39,14,'39:14','بڵێ من ھەر خوا دەپەرستم بە پاکی و تەنھا ھەر بۆ ئەو ملکەچم');
INSERT INTO "bamoki" VALUES(39,15,'39:15','(بە بێ باوەڕان بڵێ) ئێوەش جگە لە خوا چیتان وویست بی پەرستن بڵێ بێگومان زەرەرۆمەندان کەسانێکن کەخۆیان و کەسوکاریان لەدەستداوە لە ڕۆژی قیامەتدا بێدار بن، ھەر ئەوە زەرەرۆمەندی ئاشکراو دیار');
INSERT INTO "bamoki" VALUES(39,16,'39:16','بۆ ئەوان ھەیە لەلای سەریانەوە چەند تەبەقێك (وەك ھەوری ڕەش) لەئاگر و لەژێریشیانەوە چەند تەبەقێك ھەیە ئەو (سزایە) خوا بەندەکانی خۆی پێ دەترسێنێت ئەی بەندەکانم خۆتان بپارێزن (لە سزای من)');
INSERT INTO "bamoki" VALUES(39,17,'39:17','وە کەسانێك کە لەپەرستنی بت (و ھەموو پەرستراوێکی تر) خۆیان پاراستووە وە گەڕاونەتەوە بۆ لای خوا مژدەی گەورە تەنھا بۆ ئەوانەیە، کەواتە مژدە بدە بەبەندەکانم');
INSERT INTO "bamoki" VALUES(39,18,'39:18','ئەوانەی گوێ دەگرن بۆ قسە وگوفتار ئەمجا شوێن چاکترینی ئەو (قسانە) دەکەون ئەوانە کەسانێکن خودا ڕێنمونی کردوون ھەر ئەوانەن خاوەنانی ژیری و ھۆش');
INSERT INTO "bamoki" VALUES(39,19,'39:19','جا ئایا کەسێك بەڵێنی سزای بەسەردا چەسپابێت (بۆی بڕیاردرا بێت) دەی ئایا تۆ (دەتوانی) کەسێك ڕزگار بکەیت کە لەناو ئاگری دۆزەخدایە؟');
INSERT INTO "bamoki" VALUES(39,20,'39:20','بەڵام کەسانێك کە لە پەروەردگاریان ترساون و خۆیان (لە سزای) پاراستووە (لە بەھەشتدا) بۆ ئەوانە کۆشك و تەلاری چەند نھۆم ھەیە ڕووباری ئاو بە ژێریاندا دەڕوات (ئەمە) بەڵێنی خوایە و خواش بەڵێنی خۆی دەباتە سەر');
INSERT INTO "bamoki" VALUES(39,21,'39:21','ئایا نەتبینی کە خوا لە ئاسمانەوە بارانێك دەبارێنێت (بۆ سەر زەوی) ئەمجا ئەو بارانە دەبات بە ناو زەویدا کانیاوەکانی لێ دەردەھێنێت لە پاشان کشتوکاڵ و گژوگیا و گوڵی پێ دەڕوێنێت کە ڕەنگەکانیان جیاوازە پاشان دەیبینیت کە ووشك دەبێ و زەرد ھەڵدەگەڕێت دواتر دەیکات بە پووشی ورد و خاش بەڕاستی لەمەدا (کە باسکرا) ئامۆژگاری و پەند ھەیە بۆ ئەوانەی خاوەن ژیرین');
INSERT INTO "bamoki" VALUES(39,22,'39:22','دەی ئایا کەسێك خوا سینەی گوشاد کردووە بۆ ئیسلام و ئەو لەسەر رێبازی رووناکی پەروەردگاریەتی (وەك کەسێکە دڵی ڕەق بووبێت لەئاست ئیسلام) جا سزا و لە ناوچوون بۆ ئەوانەی دڵیان ڕەقە لەئاست (قورئان و) یادی خوادا ئائەوانە لە گومڕاییەکی ئاشکرادان');
INSERT INTO "bamoki" VALUES(39,23,'39:23','خوا جوانترین فەرمایشتی ناردۆتە خوارەوە قورئانێکە (کەئایەتەکانی) لەیەکچووی دووپات کراوەن، ئەو کەسانەی لەپەروەردگاریان دەترسن پێستیان تەزووی پێدا دێت (بە بیستنی قورئان) پاشان پێستیان و دڵیان نەرم دەبێت بە یادی (قورئانی) خوا ئەو (قورئانە) ڕێنمونی خوایە بەو (قورئانە) ڕێنمونی ھەر کەسێك دەکات کە بیەوێت ھەر کەسیش خوا گومڕای بکات ئەوە ڕێنمونیکەرێك بۆ ئەو نیە');
INSERT INTO "bamoki" VALUES(39,24,'39:24','دەی ئایا کەسێك بەدەم وچاوی خۆی بپارێزێت لەسزای سەختی ڕۆژی دوایی(وەك کەسێك وایە کە لە بەھەشت دابێت) بەستەمکاران دەگوترێت بچەژن سزای ئەو (کردەوانەی) لە دونیا دەتان کرد');
INSERT INTO "bamoki" VALUES(39,25,'39:25','ئەوانەی پێش ئەمان (پێش قوڕەیشیەکان) بوون باوەڕیان نەھێنا بە پێغەمبەرەکانیان بەو ھۆیەوە سزا (ی خوا) یان بۆ ھات لە لایەکەوە کە ھەستیان پێ نەکرد');
INSERT INTO "bamoki" VALUES(39,26,'39:26','ئەمجا خوا پێی چەشتن ڕیسوایی لە ژیانی دونیادا وە بێگومان سزای ڕۆژی دوایی گەورەتر ئەگەر ئەوانە بزانن');
INSERT INTO "bamoki" VALUES(39,27,'39:27','سوێند بەخوا بەڕاستی لەم قورئانەدا ھێناومانەتەوە بۆ خەڵکی ھەموو جۆرە نموونەیەك تا پەندو ئامۆژگاری وەرگرن');
INSERT INTO "bamoki" VALUES(39,28,'39:28','(ئەو قورئانە) قورئانێکی عەرەبیە ھیچ خواری و لاریەکی تێدا نیە بۆ ئەوەی (لەسزا) خۆ بپارێزن وببنە لە خواترس');
INSERT INTO "bamoki" VALUES(39,29,'39:29','خوا نموونەی ھێناوەتەوە بە پیاوێکی (بەندە) ھی چەند کەسێکە ئاژاوەیانە لەسەر ئەو (بەندە) وە پیاوێکیش کە تەنھابەندەی پیاوێکە ئایا ئەم دوو کەسە وەک یەکنھەموو سوپاس تەنھا بۆ خوایە بەڵام زۆربەیان نازانن');
INSERT INTO "bamoki" VALUES(39,30,'39:30','(ئەی موحەممەد صلى الله عليه وسلم) بەڕاستی تۆ دەمری و ئەوانیش دەمرن');
INSERT INTO "bamoki" VALUES(39,31,'39:31','لەپاشان بێگومان ئێوە لە ڕۆژی دواییدا لای پەروەردگارتان داوا چی دەبن دژی یەکتری');
INSERT INTO "bamoki" VALUES(39,32,'39:32','ئەمجا کێ ستەمکارترە لەوکەسەی کەدرۆ بەناوی خواوە دەکات وە باوەڕی بەڕاستی قورئان نیە کەبۆی ھاتووە ئایا لە دۆزەخدا جێگەیەك نیە بۆ بێ بڕوایان');
INSERT INTO "bamoki" VALUES(39,33,'39:33','وە ئەو کەسەی قورئانی ھێناوە (واتە پێغەمبەر صلى الله عليه وسلم) باوەڕیشی پێیەتی ھەر ئەوانن لەخواترسان و پارێزکاران');
INSERT INTO "bamoki" VALUES(39,34,'39:34','بۆیان ھەیە (لەبەھەشتدا) ئەوەی بیانەوێت لای پەروەردگاریان ئەوەیە پاداشتی چاکەکاران');
INSERT INTO "bamoki" VALUES(39,35,'39:35','تاخوا لێیان بسڕێتەوە خراپترین (کردەوەیەك) کەکردوویانە وە پاداشتیان بداتەوە بە (پاداشتی) چاکترین ئەو کردەوانەی کردوویانە');
INSERT INTO "bamoki" VALUES(39,36,'39:36','ئایا خوا بۆ بەندەی خۆی (پێغەمبەر صلى الله عليه وسلم) بەس نیە (کە پشتیوانی بێت)؟ (ئەی موحەممەد صلى الله عليه وسلم) ئەو بێ باوەڕانە دەتترسێنن بەوانەی جگە لەخوان (واتە دەترسێنن بەخواکانیان کە زیانت پێ دەگەیەنن) وە ھەر کەس کەخوا گومڕای بکات ئیتر ھیچ کەس ناتوانێت ببێت بە ڕێنمونیکاری و ھیدایەتی بدات');
INSERT INTO "bamoki" VALUES(39,37,'39:37','وە ھەر کەس کەخوا ڕێنمونی بکات ئەوە ھیچ گومڕاکارێکی بۆ نیە ئایا خوا زاڵ و باڵادەست و تۆڵەسێن نیە؟');
INSERT INTO "bamoki" VALUES(39,38,'39:38','سوێند بەخوا ئەگەر پرسیاریان لێ بکەیت کێ ئاسمانەکان و زەوی دروستکردووە بێگومان دەڵێن: خوا بڵێ: دەی پێم بڵێن ئەوانەی دەیان پەرستن جگە لەخوا ئەگەر خوا وویستی زیانێکم پێ بگەیەنێت ئایا ئەو (پەرستراوانە) دەتوانن ئازارو زیانی ئەوم لێ لادەن؟ یان ئەگەر وویستی نیعمەت وڕەحمەتێکی (خۆیم) پێ بدات ئایا ئەوانە دەتوانن نیعمەت و ڕەحمەتی ئەوم لێ بگرنەوە بڵێ: من ھەر خوام بەسە خۆسپێران تەنھا پشت بەو دەبەستن');
INSERT INTO "bamoki" VALUES(39,39,'39:39','بڵێ ئەی گەلەکەم ئێوە لەسەر ڕێبازو شێوازی خۆتان کاربکەن منیش (لەسەر ڕێبازی خۆم) کاردەکەم ئەمجا بەم زووانە دەزانن');
INSERT INTO "bamoki" VALUES(39,40,'39:40','کێ (لەم دونیادا) سـزایەکی بۆ دێت کەڕیسوای دەکات وە سزایەکی بێ بڕانەوەی بەسەردا دێت');
INSERT INTO "bamoki" VALUES(39,41,'39:41','بەڕاستی ئەم قورئانەمان بۆ سەر تۆ ناردۆتە خوارەوە بۆ ھەموو خەڵکی بەھەق و ڕاستی جا ھەرکەسێك ھیدایەت وەرگرێت ئەوە قازانجی ھەر بۆ خۆیەتی ھەر کەسیش لاڕێ وگومڕا بێت ئەوە بەڕاستی زیانی گومڕا بوونەکەی ھەر بۆخۆیەتی وە ئەی پێغەمبەر صلى الله عليه وسلم تۆ کارسپێرراو و چاودێر نیت بەسەریانەوە (تابڕوا بھێنن)');
INSERT INTO "bamoki" VALUES(39,42,'39:42','خوا گیانەکان دەکێشێت لەکاتی مردنیاندا وە ئەوەی نەمردبێت لەکاتی خەوتنیدا، ئەمجا ئەو (گیانانە) ڕادەگرێت بڕیاری مردنی داوە وە ئەوانی تر بەرەڵا دەکات (کە دەبێ زیندوو بمێنن) تاماوەیەکی دیاریکراو، بەڕاستی لەمەدا چەند بەڵگە ھەیە بۆ ئەو کەسانەی بیردەکەنەوە');
INSERT INTO "bamoki" VALUES(39,43,'39:43','ئاخۆ جگە لەخوا تکا کارانێکیان بۆ خۆیان داناوە بڵێ گەرچی ئەوانە نەھیچ شتێکیان بەدەستە وە نەتێیش دەگەن');
INSERT INTO "bamoki" VALUES(39,44,'39:44','بڵێ: تکا ھەمووی ھەر بۆ خوایە موڵکی ئاسمانەکان و زەوی ھەر بۆ ئەوە پاشان ھەر بۆلای ئەو دەگەڕێنرێنەوە');
INSERT INTO "bamoki" VALUES(39,45,'39:45','وەکاتێك یادی خوا بکرێت بەتاك و تەنھا تێکدەچێت وە بێزار دەبێ دڵی کەسانێك کەباوەڕیان نیە بەڕۆژی دوایی کەچی کاتێك ناوی پەرستراوانی بێجگە لەخوا ببرێت یەکسەر شادو ڕووخۆش دەبن');
INSERT INTO "bamoki" VALUES(39,46,'39:46','بڵێ: ئەی خوایە بەدیھێنەری ئاسمانەکان و زەوی زانای نھێنی و ئاشکراکان ھەر تۆ دادوەری دەکەیت لەنێوان بەندەکانتدا لەوەی ئەوان تیایدا جیاوازبوون');
INSERT INTO "bamoki" VALUES(39,47,'39:47','بەڕاستی ئەگەر ھی ئەو کەسانە بوایە ستەمیان کرد ھەموو ئەوەی لەسەر زەویدایە ئەوەندەی تریشی لەگەڵدا بێت لەبریتی خۆیان دەیاندا بۆ ڕزگاربوونیان لەسزای خراپی ڕۆژی قیامەت وە لەلایەن خواوە بۆیان دەرکەوت شتی وا کەئەوان بیریان لێ نەکردوەتەوەو گومانیان بۆی نەچووە');
INSERT INTO "bamoki" VALUES(39,48,'39:48','وە بۆشیان دەرکەوت چی خراپەیان کردووە وە دەوری گرتن ئەو سزای ئەوان گاڵتەیان پێ دەکرد');
INSERT INTO "bamoki" VALUES(39,49,'39:49','ئەمجا کاتێك ئادەمی زیانێکی لێکەوت وتووشی ناڕەحەتی بوو ھاوارمان بۆ دەھێنێت لەپاشان ھەر کاتێک نیعمەتێکمان پێـدا دەڵێ: بەڕاستی ئەم نیعمەتەم پێدراوە بەھۆی لێزانی وزانستی خۆمەوە (وانیە) بەڵکو ئەوە تاقیکردنەوەیە بەڵام زۆربەیان نازانن');
INSERT INTO "bamoki" VALUES(39,50,'39:50','بێگومان ئەم قسەیە کەسانی پێش ئەمانیش کردیان بەڵام ھیچ سوودی پێ نەگەیاندن ئەوەی ئەوان کردیان');
INSERT INTO "bamoki" VALUES(39,51,'39:51','ئەمجا دوو چاریان بوو (تۆڵەی) ئەو خراپانەی کردبوویان ئەو کەسانەیان ستەمیان کرد (بێباوەڕانی مەککە) لەمەو پاش دووچاری (تۆڵەی) کردەوە خراپەکانیان دەبن وە ئەمانە (لەدەستی خوا) دەرباز نابن');
INSERT INTO "bamoki" VALUES(39,52,'39:52','ئایا نەیانزانیوە کەبێگومان خوا ڕۆزی فراوان دەکات ودەیشیگرێتەوە لەھەر کەسێك کە بیەوێت بێگومان لەوەدا بەڵگە ونیشانەی زۆر ھەن بۆ ئەو کەسانەی باوەڕ دەھێنن');
INSERT INTO "bamoki" VALUES(39,53,'39:53','(ئەی موحەممەد صلى الله عليه وسلم) بڵێ: ئەی بەندەکانم ئەی ئەوانەی کەخۆتان زۆر گوناھبار کردووە نائومێد مەبن لەمیھرەبانی و بەزەیی خوا چونکە بێگومان خوا لەھەموو گوناھەکان خۆش دەبێت بەڕاستی ھەر ئەوە لێ بوردە و بەبەزەیی');
INSERT INTO "bamoki" VALUES(39,54,'39:54','(بەو مەرجەی کە) بگەڕێنەوە بۆلای پەروەردگارتان وە گەردنکەچی ئەو بن بەر لەوەی سزاتان بۆ بێت پاشان یارمەتی نادرێن (لەلایەن ھیچ کەسێکەوە)');
INSERT INTO "bamoki" VALUES(39,55,'39:55','وە شوێن چاکترین شتێك بکەون کە نێردراوەتە خوارەوە بۆتان لە پەروەردگارتانەوە بەر لەوەی لەناکاودا سزاتان بۆ بێت کە ئێوە ھەر ھەستی پێ نەکەن');
INSERT INTO "bamoki" VALUES(39,56,'39:56','نەکا ئەوسا کەسێك بڵێت ئەی مەخابن لەسەر ئەوەی کەمتەرخەمیم کرد لەکارکردن بەوەی خوا فەرمانی پێکردبوو وەبەڕاستی من لەو گاڵتە چیانە بووم (کە گاڵتەیان بە ئاینەکەی دەکرد)');
INSERT INTO "bamoki" VALUES(39,57,'39:57','یان بڵێت: ئەگەر خوا ڕێنمونی بکردمایە ئەوە بێگومان لەپارێزکاران ولەخواترسان دەبووم');
INSERT INTO "bamoki" VALUES(39,58,'39:58','یان کاتێك سزاکەی دەبینێت بڵێت خۆزگە جارێکی تر بگەڕامایەتەوە (بۆ دونیا) ئەوسا لەچاکەکاران دەبووم');
INSERT INTO "bamoki" VALUES(39,59,'39:59','(ڕاست ناکەیت) خۆ بێگومان بەڵگەو ئایەتەکانی منت بۆھات بەڵام باوەڕت پێ نەھێناو خۆت بەگەورەزانی وە تۆ لەبێ باوەڕان بوویت');
INSERT INTO "bamoki" VALUES(39,60,'39:60','لەڕۆژی دواییدا دەبینیت ئەوانەی درۆیان بۆ خوا ھەڵدەبەست ڕوویان ڕەشە ئایا لەدۆزەخدا شوێنێك نیە بۆ خۆ بەگەورە زانان؟');
INSERT INTO "bamoki" VALUES(39,61,'39:61','وە ئەوانەی خۆیان پاراست (لەکوفرو تاوان) خوا ڕزگاریان دەکات چونکە ڕێگەی ڕزگاریان گرتبوەبەر نەتووشی ناخۆشی دەبن وە نەخەم دەخۆن');
INSERT INTO "bamoki" VALUES(39,62,'39:62','خوا بەدیھێنەری ھەموو شتێکە وە ھەر ئەویش پارێزەری ھەموو شتێکە');
INSERT INTO "bamoki" VALUES(39,63,'39:63','کلیلەکانی ئاسمانەکان و زەوی بەدەست خوایە وە ئەوانەی بەبەڵگەو نیشانەکانی خوا بێ باوەڕن ھەر ئەوانن زەرەرۆمەند');
INSERT INTO "bamoki" VALUES(39,64,'39:64','(ئەی موحەممەد صلى الله عليه وسلم) بڵێ: دەی ئایا ئێوە داوام لێ دەکەن من بێجگە لەخوا شتێکی تر بپەرستم ئەی نەفام و نەزانان');
INSERT INTO "bamoki" VALUES(39,65,'39:65','سوێند بەخوا نیگا کراوە بۆ تۆ وەبۆ پێغەمبەرانی پێش تۆش ئەگەر ھاوبەش بۆ خوا دابنێیت بەڕاستی کردەوەت ھەڵدەوەشێتەوە وە بێگومان لەزەرەرمەندان دەبیت');
INSERT INTO "bamoki" VALUES(39,66,'39:66','(وا مەکە) بەڵکو تەنھا خوا بپەرستە و لە سوپاسگوزارانبە');
INSERT INTO "bamoki" VALUES(39,67,'39:67','(کافران) خوایان نەناسیوە (بە گەورەیان نەزانی) بەو جۆرەی شیاوێتی لە ڕۆژی دواییدا ھەموو زەوی لە چنگی ئەودایە (بەدەست خوایە) وە ئاسمانەکانیش بەدەستی ڕاستی ئەو پێچراونەتەوە (لەم ئایەتەدا جێگیرکردن سیفەتی دەستی ڕاست بۆ خودا ھاتووە ئێمە باوەڕمان پێیەتی، بەبێ ھیچ لێچواندن ولێکدانەوەیەك وشێوەکەی نازانین، تەنھا خودا دەیزانێت) خوا پاك و بڵند ترە لەوەی کە دەیکەنە ھاوبەشی');
INSERT INTO "bamoki" VALUES(39,68,'39:68','کاتێك فووکرا بەکەڕەنادا (بەشەیپوردا) یەکسەر ھەموو ئەوانەی لەئاسمانەکان و زەویدان دەمرن مەگەر کەسانێك خوا بیەوێت (نەمرن) پاشان جارێکی تر فوو دەکرێ بەکەڕەنادا جا لە پڕ ھەموو ڕاست دەبنەوە چاوەڕِوان دەکەن');
INSERT INTO "bamoki" VALUES(39,69,'39:69','وەزەوی لەو ڕۆژەدا بەنوری پەروەردگاری ڕووناك دەبێتەوە وەنامەی کردەوە لەبەردەمیان دادەنرێت و پێغەمبەران و شاھیدی (سەر گەلەکان) دەھێنرێن وە لەنێوانیاندا بەھەق دادوەری دەکرێت وە ئەوان ستەمیان لێ ناکرێت');
INSERT INTO "bamoki" VALUES(39,70,'39:70','وە (لەو ڕۆژەدا) ھەموو کەسێك چی کردووە بەتێرو تەواوی پاداشت دەدرێتەوە وە خوا خۆشی ئاگادارە (بە بێ شایەت و نامەی کردەوەکان) بەو کردەوانەی کەدەیکەن');
INSERT INTO "bamoki" VALUES(39,71,'39:71','ئەوسا ئەوانەی بێ باوەڕ بوون ڕاپێچ دەکرێن بۆ دۆزەخ کۆمەڵ کۆمەڵ تاکاتێك دێنە (نزیکی) دۆزەخ دەرگاکانی دەکرێنەوە وە دەرگاوانەکانی دۆزەخ (بەلۆمەکردنێکەوە) بەوان دەڵێن ئایا پێغەمبەرانتان لەخۆتان بۆ نەھات کەئایەتەکانی پەروەردگارتان بەسەردا بخوێننەوە وە بتانترسێنن لەگەشتنتان بەم ڕۆژە؟ دەڵێن: بەڵێ (ھاتن ئاگاداریان کردین) بەڵام بڕیاری سزا دراوە بەسەر (ئێمەی) بێ باوەڕاندا');
INSERT INTO "bamoki" VALUES(39,72,'39:72','پێیان دەوترێت: لەدەرگاکانی دۆزەخەوە بچنە ژورەوە بەھەمیشەیی لەوێدا بمێننەوە ئای چ خراپە جێگەو مەنزڵگای خۆ بەزل زانان');
INSERT INTO "bamoki" VALUES(39,73,'39:73','وە کەسانێك (کە خواناس بوون) و لە پەروەردگاریان دەترسان پۆل پۆل دەبرێن بۆ بەھەشت تاکاتێك دێنە (نزیکی) بەھەشت و دەرگاکانی دەکرێنەوە (بەڕویاندا) وە دەرگاوانەکانی بەوان دەڵێن سەلامتان لێ بێت ئێوە پاك و خاوێن بوون (لە تاوان لە دونیادا) کەواتە بڕۆنە ناو بەھەشت بەھەمیشەیی');
INSERT INTO "bamoki" VALUES(39,74,'39:74','(ئەوانیش) دەڵێن: سوپاس بۆ ئەو خوایەی ھەر بەڵێنێکی بەئێمەدا ڕاست بوو وە زەوی (بەھەشتی) بەمیرات پێ داین لەھەر شوێنێکی بەھەشتدا بمانەوێت نیشتەجێ دەبین ئای چەند چاکە پاداشتی چاکەکاران');
INSERT INTO "bamoki" VALUES(39,75,'39:75','وە فریشتەکان دەبینیت چوار دەوری عەرشیان داوە تەسبیحات دەکەن بەستایشی پەروەردگاریان وە لەنێوانیاندا بەھەق دادوەری کرا وە (لەکۆتایدا) دەووترێت ھەموو سوپاس ھەر بۆ خوایە کەپەروەردگاری جیھانیانە');
INSERT INTO "bamoki" VALUES(40,1,'40:1','بە (حا، میم) دەخوێنرێتەوەو خوا بەواتای ئەم پیتانە زاناترە، سەرنجی سەرەتای سورەتی(البقرة) بدە دەربارەی واتاکەیان');
INSERT INTO "bamoki" VALUES(40,2,'40:2','ئەم قورئانە ھێنراوەتە خوارەوە لەلایەن خوای باڵادەست و داناوە');
INSERT INTO "bamoki" VALUES(40,3,'40:3','لێبوردەی تاوان و وەرگری تۆبەی (گوناھباران وتۆبەکارانە) (ئەو خودایە) تۆڵەی سەخت وبەتینە وە خاوەن فەزڵ و بەھرە و چاکەیە بێجگە لەو ھیچ پەرستراوێکی بەھەق و ڕاست نیە گەڕانەوەی ھەمووان ھەر بۆلای ئەوە');
INSERT INTO "bamoki" VALUES(40,4,'40:4','دەمە قاڵێ وکێشمەکێش ناکات لەنیشانەکانی خوادا بێباوەڕان نەبێت کەوا بوو فریوت نەدات ھاتوچۆ و گەڕانیان بەووڵاتدا (بۆ بازرگانی وسامان دەس خستن)');
INSERT INTO "bamoki" VALUES(40,5,'40:5','پێش ئەوان گەلی نوح باوەڕیان نەھێنا (بەنوح) وە دەستەو تاقمەکانی دوای ئەوانیش (باوەڕیان نەھێنا) و ھەموو گەلێك وویستوویەتی (نیازی کردووە) کە پێغەمبەرەکەیان بکوژن (یان بیگرن وئازاری بدەن) وە دەمەقاڵێیان (لەگەڵ) دەکرد بەناڕەوا بۆ ئەوەی ڕاستی پێ لابەرن و نەیھێڵن ئیتر (قار وتووڕەیی) من گرتنی جا چۆن بوو تۆڵەسەندنی من لەوان');
INSERT INTO "bamoki" VALUES(40,6,'40:6','وە ئابەو جۆرە بڕیاری پەروەردگارت بەدیھات لەسەر ئەوانەی بێ باوەڕ بوون کەبێگومان ئەوان دۆزەخین');
INSERT INTO "bamoki" VALUES(40,7,'40:7','ئەو فریشتانەی ھەڵگری عەرشن و ئەوانەش بەدەوریدان بەستایشی پەروەردگاریان تەسبیحات دەکەن وە باوەڕیان بەو ھەیە وە داوای لێبوردن دەکەن بۆ ئەوانەی ئیماندارن (دەڵێن): ئەی پەروەردگارمان میھرەبانی وزانیاریت ھەموو شتێکی گرتۆتەوە دەی خۆش ببە لەوانەی تۆبەیان کردووە و شوێنی ڕێگای تۆ کەوتوون وە بیانپارێزە لەسزای دۆزەخ');
INSERT INTO "bamoki" VALUES(40,8,'40:8','ئەی پەروەردگارمان بیشیان خەرە بەھەشتانێکی ھەمیشەیی کە بەڵێنت پێ دابوون ئەو کەسانەش چاکەکار بوون لەباوو باپیران و ھاوسەران و ڕۆڵەکانیان (نەوەکانیان) بەڕاستی ھەر تۆ زاڵ و کار دروستی');
INSERT INTO "bamoki" VALUES(40,9,'40:9','وە بیانپارێزە لە (تۆڵەی) تاوانەکان چونکە ھەر کەسێك لە (تۆڵەی) خراپە و تاوان بپارێزیت لەو ڕۆژەدا ئەوە بێگومان ڕەحمت پێ کردووە وە ھەر ئەوەیە سەرکەوتنی زۆر گەورە');
INSERT INTO "bamoki" VALUES(40,10,'40:10','بەڕاستی کەسانێك بێ باوەڕ بوون (لەپاشەڕۆژدا) بانگ دەکرێن کەبەڕاستی توڕەیی و خەشمی خوا (لەدونیادا) گەورەتر بوو لەڕق و توڕەیتان لەخۆتان (لەقیامەتدا) کاتێك بانگ دەکران بۆ باوەڕ ھێنان بەڵام باوەڕتان نەدەھێنا');
INSERT INTO "bamoki" VALUES(40,11,'40:11','دەڵێن ئەی پەروەردگارمان تۆ دوو جارت مراندووتین وە دوو جاریش ژیانت بەبەردا کردووین جا دانمان نا بەتاوانەکانماندا دەی ئایا ڕێگایەك ھەیە بۆ دەرباز بوون');
INSERT INTO "bamoki" VALUES(40,12,'40:12','ئەم سزایەتان لەبەر ئەوەیە کە بەڕاستی ھەرکاتێك خوا بەتاك و تەنھایی بپەرسترایە ئێوە بێ باوەڕ دەبوون وە ئەگەر ھاوبەشی بۆ دابنرایە باوەڕتان دەھێنا (بەو ھاوبەش دانانە) بڕیاردان ھەر بۆ خوای بڵند و بەرزو گەورەیە');
INSERT INTO "bamoki" VALUES(40,13,'40:13','ئەو زاتێکە نیشانەکانی خۆیتان نیشان دەدات وە لەئاسمانەوە ڕزق و ڕۆزیتان بۆ دەنێرێتە خوارەوە کەس بیرناکاتەوە (پەند وەرناگرێت) مەگەر کەسێك ڕوو لەخوا بێت');
INSERT INTO "bamoki" VALUES(40,14,'40:14','کەواتە لەخوا بپارێنەوەو بەپاکیی ودڵسۆزی و بۆی گەردن کەچ بن ھەر چەندە بێ باوەڕانیش پێیان ناخۆش بێت');
INSERT INTO "bamoki" VALUES(40,15,'40:15','(خوا) پلەو پایە زۆر بەرزی خاوەن عەرشی مەزنە بەفەرمانی خۆی وەحی دەنێرێت بۆ ھەر کەس لەبەندەکان کەبیەوێت تا(خەڵك) بترسێنێت لە ڕۆژی بەیەکتر گەیشتن');
INSERT INTO "bamoki" VALUES(40,16,'40:16','ڕۆژێك کە ھەموو (لە گۆڕەکانیان دێنە دەرو) ئاشکرا دەبن ھیچ شتێکیان لە خوا پەنھان نابێت (خوا دەفەرمووێت) ئەمڕۆ پادشایەتی و دەسەڵات بۆ کێیە (وەڵام نادرێتەوە) (خۆی وەڵام دەداتەوە و دەفەرمووێت) ھەر بۆ خوای تاك و تەنھایە کە بەسەر ھەمووشتدا زاڵە');
INSERT INTO "bamoki" VALUES(40,17,'40:17','ئەمڕۆ ھەموو کەس پاداشت یان سزا دەدرێتەوە بەگوێرەی ئەوەی کردوویەتی ئەمڕۆ ستەم کردن نیە (لە کەس) بەڕاستی لێپرسینەوەی خوا بەپەلەو خێرایە');
INSERT INTO "bamoki" VALUES(40,18,'40:18','(ئەی موحەممەد ) ئەوان لەڕۆژە نزیکەکە (لەقیامەت) بترسێنە لەوکاتەی دڵەکان (لەترسا) گەشتوونەتە گەروو ھەموو پیش دەخۆنەوە ستەمکاران نەھیچ دۆستێکیان ھەیە وە نە تکاکارێک بەگوێی بکرێت');
INSERT INTO "bamoki" VALUES(40,19,'40:19','(خوا) دەزانێ بەخیانەتی چاوان و ھەرچی دڵان دەیشارنەوە');
INSERT INTO "bamoki" VALUES(40,20,'40:20','وە خوا بەھەق و ڕاستی دادوەری دەکات بەڵام ئەوانەی جگە لەخوا ھاواریان لێ دەکەن (دەیانپەرستن) ناتوانن بڕیاری ھیچ شتێك بدەن بەڕاستی ھەرخوا بیسەر و بینایە');
INSERT INTO "bamoki" VALUES(40,21,'40:21','ئایا بەزەویدا نەگەڕاون تا ببینن (بزانن) سەرەنجامی ئەوانەی کەپێش ئەمان بوون چۆن بوو ئەوانە لەمان بەھێزتر بوون وە شوێنەواریان لەزەویدا زیاتر بوو بەڵام خوا بەھۆی تاوانەکانیانەوە گرتنی وتۆڵەی لێ سەندن وە کەس پارێزەریان نەبوو لە (سزای) خوا');
INSERT INTO "bamoki" VALUES(40,22,'40:22','ئەمە لەبەر ئەوە بوو کەبێگومان ئەوان پێغەمبەرەکانیان بۆ دەھات بەنیشانە ئاشکراکانەوە کەچی باوەڕیان نەھێنا ئەمجا خوا قاری لێ گرتن و سزای دان بەڕاستی خوا بەھێزی تۆڵەسێنی بەتینە');
INSERT INTO "bamoki" VALUES(40,23,'40:23','سوێند بەخوا بێگومان موسامان نارد بەچەند موعجیزە و بەڵگەی ڕوونەوە (بەتەوراتەوە)');
INSERT INTO "bamoki" VALUES(40,24,'40:24','بۆ لای فیرعەون و ھامان و قارون ھەموو ووتیان: جادوگەرێکی درۆزنە');
INSERT INTO "bamoki" VALUES(40,25,'40:25','ئەمجا کاتێك موسا (ئاینی) ڕاسـتی لەلایەن ئێمەوە بۆ ھێنان ووتیان کوڕانی ئەوانەی لەگەڵ ئەو(موسا) باوەڕیان ھێناوە بکوژن وە ژنەکانیان بەزیندوویی بھێڵنەوە (بۆ کارەکەری) بەڵام فێڵی بێ باوەڕان ھەر مایەی سەر لێ شێوانە (ھەرگیز سەرناگرێت)');
INSERT INTO "bamoki" VALUES(40,26,'40:26','وە فیرعەون ووتی: لێم گەڕێن با موسا بکوژم ئەوسا بائەو ھانا بۆ پەروەردگاری بەرێت (ڕزگاری بکات) بەڕاستی من دەترسم کەئاینەکەتان بگۆڕێت یان لەزەویدا ئاژاوە وخراپەکاری بنێـتەوە');
INSERT INTO "bamoki" VALUES(40,27,'40:27','وە موسا ووتی: بەڕاستی من پەنا دەگرم بەپەروەردگاری خۆم و پەروەردگاری ئێوەش لەھەموو خۆبەزل زانێك کە باوەڕی بەڕۆژی لێ پرسینەوە نیە');
INSERT INTO "bamoki" VALUES(40,28,'40:28','وە پیاوێکی باوەڕدار ووتی: لەکەس و کاری فیرعەون بوو کە باوەڕِەکەی شاردبوەوە ئایا (چۆن) پیاوێك دەکوژن؟ لەبەر ئەوەی دەڵێ: خوا پەروەردگارمە کە بەڕاستی چەند بەڵگە و موعجیزەی ڕوونی لە پەروەردگارتانەوە بۆ ھێناون خۆ ئەگەر درۆزن بێت (کەدەڵێ پێغەمبەرم) ئەوە (تاوانی) درۆکەی بەئەستۆی خۆیەتی خۆ ئەگەر ڕاستگۆ بێت ھەندێ لەو (سزایەی) بەڵێنی پێ داوون تووشتان دەبێت بێگومان خوا ڕێنمونی کەسێک ناکات کە زیادەڕۆ و درۆزن بێت');
INSERT INTO "bamoki" VALUES(40,29,'40:29','(موسا ووتی): ئەی گەلەکەم فەرمان ڕەوایی ئەمڕۆ ھی ئێوەیە و بەدەسەڵات و زاڵن لەسەر زەویدا دەی کێ یارمەتیمان دەدات ئەگەر سزای خوامان بۆ بێت فیرعەون ووتی: چی خۆم بەچاکی دەبینم ھەر ئەوەتان نیشانە دەدەم وە تەنھا بۆ ڕێگای ڕاست ڕێنمونیتان دەکەم');
INSERT INTO "bamoki" VALUES(40,30,'40:30','وە ئەو (پیاوەی) باوەڕی ھێنا بوو ووتی: ئەی گەلەکەم بەڕاستی من لەوە دەترسم بەسەرتان بێ وێنەی (سزای) ئەو ڕۆژەی بەسەر کۆمەڵان و گەلانی پێشوو ھات');
INSERT INTO "bamoki" VALUES(40,31,'40:31','وەك ئەو (سزایەی) ھات بەسەر گەلی نوح و عاد و سەمود و ئەوانەش لەدوای ئەوان بوون وە خوا زوڵم و ستەمی ناوێت بۆ بەندەکانی');
INSERT INTO "bamoki" VALUES(40,32,'40:32','وە ئەی گەلەکەم بەڕاستی من ترسی ڕۆژی ھاوارو بانگ لەیەکتری کردنم ھەیە لێـتان (ئەو ڕۆژەی کە ھەموو کەس ھاوار وداوای یارمەتی لەیەکتری دەکەن)');
INSERT INTO "bamoki" VALUES(40,33,'40:33','ئەو ڕۆژەی پشت ھەڵدەکەن و ھەڵدێن (لەیەکتری) ھیچ پەناگەیەك بۆ ئێوە نیە لە(سزای) خوا وە ھەر کەسێك خوا گومڕای بکات ئەوە ھیچ ڕێنمونی کارێکی بۆ نیە');
INSERT INTO "bamoki" VALUES(40,34,'40:34','سوێند بەخوا بێگومان پێشتر یوسف پێغەمبەرتان بۆھات بەبەڵگە ڕوونەکانەوە کەچی ئێوە ھەر لەگوماندا بوون لەو بەڵگانەی بۆی ھێنابوون تا کاتێك (یوسف) وەفاتی کرد ووتتان ئیتر دوای ئەو خوا ھیچ پێغەمبەرێکی تر نانێرێت ئا بەو جۆرە خوا گومڕای دەکات ئەو کەسەی زیادەڕۆی دوو دڵە');
INSERT INTO "bamoki" VALUES(40,35,'40:35','ئەوانەی دەربارەی نیشانەکانی خوا دەمەقاڵێ دەکەن بەبێ ئەوەی بەڵگەیەکیان بۆ ھاتبێت (ئەو دەمەقاڵەیان) ھۆی توڕەییە لای خوای گەورە وەلای ئەوانەش باوەڕیان ھێناوە ئا بەو جۆرە خوا مۆر دەنێ بەسەر دڵی ھەموو خۆبەزل زانێکی زۆرداردا');
INSERT INTO "bamoki" VALUES(40,36,'40:36','فیرعەون ووتی ئەی ھامان کۆشکێکی بەرزم بۆ دروست بکە بەڵکو بگەمە ئەو ڕێگایانە');
INSERT INTO "bamoki" VALUES(40,37,'40:37','کەڕێگاکانی (بەرز بوونەوەن بۆ) ئاسمانەکان بۆ ئەوەی (لەوێوە) سەیری خواکەی موسا بکەم وە بەڕاستی من وا گومان دەبەم ئەو (موسا) درۆزنە ئابەو جۆرە ڕازێنرایەوە بۆ فیرعەون کردەوەی خراپی وە (بە ھۆیەوە) ڕێگری کرا لەڕێگای ڕاست وە ھەر فێڵ و پلانێکی فیرعەون ھەبوو بەفیڕۆ چوو');
INSERT INTO "bamoki" VALUES(40,38,'40:38','وە ئەو (پیاوەی) باوەڕی (بە موسا) ھێنا بوو ووتی ئەی گەلەکەم شوێن من بکەون ڕێگای ڕاستتان نیشان دەدەم');
INSERT INTO "bamoki" VALUES(40,39,'40:39','ئەی گەلەکەم بەڕاستی ژیانی ئەم دونیایە ڕابواردنێکی کەمە وە بەڕاستی ھەر پاشەڕۆژ جێی حەسانەوەی ھەمیشەییە');
INSERT INTO "bamoki" VALUES(40,40,'40:40','ھەر کەسێك کاری خراپەی کرد بێت ھەر بەئەندازەی خراپەکەی سزا دەدرێت ھەر کەسیش چاکەی کرد بێت ئافرەت بێت یان پیاو (بەو مەرجەی) باوەڕدار بێت ئا ئەوانە دەچنە بەھەشتەوە لەوێدا بەبێ ژمارە وحیساب ڕزق و ڕۆزییان پێ دەدرێت');
INSERT INTO "bamoki" VALUES(40,41,'40:41','وە ئەی گەلەکەم ئەوە چیە من بانگتان دەکەم بۆ ڕزگاربوون کەچی ئێوە من بانگ دەکەن بۆ ئاگری دۆزەخ');
INSERT INTO "bamoki" VALUES(40,42,'40:42','بانگم دەکەن بۆ ئەوەی بێ باوەڕبم بەخوا وە شتێ بکەمە ھاوبەشی خوا کەھیچ زانیاری یەکم پێی نیە لەکاتێکدا من بانگتان دەکەم بۆلای خوای بەدەسەڵات و لێبوردە');
INSERT INTO "bamoki" VALUES(40,43,'40:43','ڕاستە و گومانی تێدا نیە ئەوەی ئێوە منی بۆ بانگ دەکەن شیاوی بانگ کردن نیە نە لە دونیا و نە لە دواڕۆژدا وە بەڕاستی گەڕانەوەمان ھەر بۆلای خوایە و بێگومان زیادەڕۆیان ھاوەڵانی ئاگری دۆزەخن');
INSERT INTO "bamoki" VALUES(40,44,'40:44','جا لەمەودوا ئەوەی من پێتان دەڵێم بیرتان دەکەوێتەوە وە من کاری خۆم بەخوا دەسپێرم بەڕاستی خوا بینایە بەبەندەکانی');
INSERT INTO "bamoki" VALUES(40,45,'40:45','جا خوا ئەو (پیاوە ئیماندارە) ی پاراست لەزیانی ئەو فڕو فێڵانەی دەیان کرد وە سزای سەخت ھات و دارودەستەی فیرعەونی گرتەوە');
INSERT INTO "bamoki" VALUES(40,46,'40:46','بەیانیان و ئێواران (فیرعەون ودەست وپێوەندەکەی) دەبرێنە دیاری ئاگری دۆزەخ ڕۆژێکیش کە قیامەت ھەڵدەسێت (فەرمان دەدرێت) دارودەستەی فیرعەون بخەنە سەخترین سزاوە');
INSERT INTO "bamoki" VALUES(40,47,'40:47','وەکاتێك (دۆزەخیەکان) لەناو ئاگرەکەدا دەمە قاڵێ دەکەن، لاوازەکان بەوانەی خۆ بەزل زان بوون دەڵێن بەڕاستی ئێمە شوێن کەوتووی ئێوە بووین دەی ئایا ئێوە لەجیاتی ئێمە لەئەستۆ دەگرن بەشێك لەم ئاگرە');
INSERT INTO "bamoki" VALUES(40,48,'40:48','ئەوانەی خۆ بەزل زان و زۆردار بوون دەڵێن: بەڕاستی ھەموومان لەئاگرداین بێگومان خوا لەنێوان بەندەکانیدا دادوەری کردوو ( وحوکمی تەواو بوو)');
INSERT INTO "bamoki" VALUES(40,49,'40:49','وە ئەوانەی لەئاگری دۆزەخدان بەدەرگاوانەکانی دۆزەخ دەڵێن داوا لەپەروەردگارتان بکەن تەنھا یەك ڕۆژ سزامان لەسەر سووك بکات');
INSERT INTO "bamoki" VALUES(40,50,'40:50','دەڵێن: ئایا پێغەمبەرەکان نەھاتن بۆ لاتان؟ بەچەندین بەڵگەی ڕوون وئاشکراوە؟ دەڵێن :بەڵێ (ھێنا بوویان) پێیان دەڵێن دەی خۆتان بپاڕێنەوە بێگومان پاڕانەوەی بێ باوەڕان وون دەبێ و بێ سوودە');
INSERT INTO "bamoki" VALUES(40,51,'40:51','بەڕاستی ئێمە پێغەمبەرانمان و ئەوانەی باوەڕیان ھێناوە سەرکەوتوو دەکەین لەژیانی دونیادا وە لەو ڕۆژەی (قیامەت) کە شایەتەکان ھەڵدەسنەوە');
INSERT INTO "bamoki" VALUES(40,52,'40:52','ڕۆژێکە بیانوو و پاکانە کردنی بێ باوەڕان ھیچ سوودیان پێ ناگەیەنێت ھەموویان نەفرەتیان لێکراوە و خراپترین جێگایان ھەیە');
INSERT INTO "bamoki" VALUES(40,53,'40:53','سوێند بەخوا بەڕاستی ئێمە تەوراتمان دا بەموسا وە نەوەکانی ئیسرائیلمان کردە میراتگری تەورات');
INSERT INTO "bamoki" VALUES(40,54,'40:54','(کەھۆی) ڕێنمونی و پەندو بیرخەرەوەیە بۆ خاوەن ژیریەکان');
INSERT INTO "bamoki" VALUES(40,55,'40:55','ئەمجا (ئەی موحەممەد صلى الله عليه وسلم) خۆگربە بێگومان بەڵێنی خوا ھەقەو دێتە دی وە داوای لێبوردنی گوناھت بکە وە تەسبیحات بکە بەستایشی پەروەردگارت بەئێواران وبەیانیاندا');
INSERT INTO "bamoki" VALUES(40,56,'40:56','بەڕاستی ئەو کەسانەی دەمە قاڵێ و کێشە دەکەن دەربارەی نیشانەکانی خودا بەبێ ئەوەی بەڵگەیەکیان بۆ ھاتبێت ھیچ لە دڵیاندا نیە جگە لەخۆبەزل زانی نەبێت ھەرگیز ئەوان بە (مەبەستیان) ناگەن کەواتە پەنا بەخوا بگرە بەڕاستی ھەر ئەو بیسەر و بینایە');
INSERT INTO "bamoki" VALUES(40,57,'40:57','بەڕاستی بەدیھێنانی ئاسمانەکان و زەوی گەورەترە لەبەدی ھێنانی خەڵکی بەڵام زۆربەی مەردووم ئەمە نازانن');
INSERT INTO "bamoki" VALUES(40,58,'40:58','وە نابینا و بینا یەکسان نین وە کەسانێك باوەڕیان ھێناوە و کردەوە چاکەکانیان کردووە لەگەڵ بەدکاران وەك یەك نین زۆر کەم (لەخەڵکی) بیر دەکەنەوە');
INSERT INTO "bamoki" VALUES(40,59,'40:59','بەڕاستی ڕۆژی دوایی دێت ھیچ گومانی تێدا نیە بەڵام زۆربەی خەڵکی باوەڕ ناکەن');
INSERT INTO "bamoki" VALUES(40,60,'40:60','وە پەروەردگارتان فەرموویەتی: لێم بپاڕێنەوە وەڵامتان دەدەمەوە بەڕاستی ئەوانەی خۆ بەگەورە دەزانن کەبمپەرستن بێگومان بەسەر شۆڕی دەچنە دۆزەخەوە');
INSERT INTO "bamoki" VALUES(40,61,'40:61','خوا ئەو زاتەیە شەوی داناوە بۆ ئێوە تائارامی تێدا بگرن وە ڕۆژیشی ڕوناك کردۆتەوە (بۆ ژیان پەیدا کردن) بەڕاستی خوا خاوەن چاکەو میھرەبانیە بەسەر خەڵکەوە بەڵام زۆربەی ئادەمی سوپاس گوزار نین');
INSERT INTO "bamoki" VALUES(40,62,'40:62','ئەمەیە خوای پەروەردگاری ئێوە دروستکەر وداھێنەری ھەموو شتێکە ھیچ پەرستراوێکی بەھەق وڕاست نیە جگە لە ئەو دەی کەواتە چۆن لائەدەن لەڕاستی');
INSERT INTO "bamoki" VALUES(40,63,'40:63','ئا بەم جۆرە لائەدرێن ئەوانەی کەدان نانێن بەبەڵگەو نیشانەکانی خوادا');
INSERT INTO "bamoki" VALUES(40,64,'40:64','خوا زاتێکە کەزەوی بۆ کردونەتە نیشتەجێ (وئارامگا) وە ئاسمانیشی بۆ کردوون بەسەربان و بەجوانترین شێوە ئێوەی دروستکردووە لەوشتانەیش کەحەڵاڵ و پاکن ڕۆزی داوون ئەمەیە خوای پەروەردگاری ئێوە بەرزی و پیرۆزی بۆ خوای پەروەردگاری جیھانیان');
INSERT INTO "bamoki" VALUES(40,65,'40:65','ھەر زیندووە و ھیچ پەرستراوێکی بەھەق وڕاست نیە جگە لە ئەو کەواتە خوا بپەرستن بەدڵسۆزی وتەنھا بۆ ئەو ملکەچ بنسوپاس و ستایش بۆ خوای پەروەردگاری جیھانیان');
INSERT INTO "bamoki" VALUES(40,66,'40:66','(ئەی موحەممەد صلى الله عليه وسلم) بڵێ: بەڕاستی من ڕێگری کراوم لەپەرستنی ئەوانەی ئێوە دەیان پەرستن جگە لەخوا دوای ئەوەی بەڵگەی ڕوونم بۆ ھاتووە لەپەروەردگارمەوە وە فەرمانم پێکراوە کە گەردن کەچ بم بۆ پەروەردگاری جیھانیان');
INSERT INTO "bamoki" VALUES(40,67,'40:67','ئەو خوایەیە کەئێوەی لەخاك دروستکردووە لەپاشان لەدڵۆپێك ئاو پاشان لەپارچە خوێنێك لەپاشان بەمنداڵی (ساوایی) دەتان ھێنێـتە دەر (لەسکی دایك) لەپاشان تا دەگەنە تینی ھێزو لاوییتان لەپاشان تاپیر دەبن وە ھەندێکتان پێش پیربوون دەمرێنرێن ھەتا دەگەنە ئەو کاتەی کەبۆتان دیاریکراوە بۆ ئەوەی بیربکەنەوە');
INSERT INTO "bamoki" VALUES(40,68,'40:68','ئەو خوایەیە کەدەژێنێت و دەمرێنێت جا ھەرکـاتێك (بیەوێت) بڕیـارێك بـدات تەنھـا پێی دەفەرمووێت ببە ،دەست بەجێ دەبێت');
INSERT INTO "bamoki" VALUES(40,69,'40:69','ئایا سەرنجی ئەوانەت نەداوە سەبارەت بە بەڵگە ونیشانەکانی خوا دەمەقاڵێ وموجادەلە دەکەن (بەبێ ھیچ زانیاریەك) چۆن لەڕێی (ڕاست) لادەدرێن');
INSERT INTO "bamoki" VALUES(40,70,'40:70','ئەوانەی باوەڕیان بەقورئان نەھێنا وە بەو (پەڕاوانەی) بەپێغەمبەرەکانماندا ناردومانە ئەوە لەمەو پاش (سەرئەنجامی خۆیان) دەزانن');
INSERT INTO "bamoki" VALUES(40,71,'40:71','کاتێك زنجیرو تەوقیان لەگەردندایە پێی ڕادەکێشرێن');
INSERT INTO "bamoki" VALUES(40,72,'40:72','لەناو ئاوی زۆر گەرمدا پاشان لەدۆزەخدا دەسوتێنرێن');
INSERT INTO "bamoki" VALUES(40,73,'40:73','لەپاشان پێیان دەوترێت: لەکوێن ئەو ھاوبەشانەی بۆ خواتان دادەنان');
INSERT INTO "bamoki" VALUES(40,74,'40:74','بێجگە لەخوا دەڵێن: وون بوون لێمان (وا نیە) بەڵکو ئێمە لەدونیا (بێجگە لەخوا) شتێکمان نەدەپەرست ئابەم جۆرە خوا بێ باوەڕان لەڕێگە لادەدات');
INSERT INTO "bamoki" VALUES(40,75,'40:75','ئەمەتان لەبەر ئەوەیە کەئێوە بەناڕەوا شادی و کەیفتان دەکرد لەزەویدا وە لەبەر ئەوەی کەئێوە فیزو کەشخەتان دەکرد لەزەویدا');
INSERT INTO "bamoki" VALUES(40,76,'40:76','دەی لەدەرگاکانی دۆزەخەوە بڕۆنە ژوورەوە تیایدا دەمێننەوە بە ھەمیشەیی و ئێجگاری ئای چەند خراپە شوێنی خۆ بەزل زانان');
INSERT INTO "bamoki" VALUES(40,77,'40:77','دەی کەواتە (ئەی موحەممەد صلى الله عليه وسلم) خۆڕاگربە بێگومان بەڵێنی خوا ڕاستە جا یان ھەندێك لەو بەڵێنانەی پێیان دەدەین نیشانت دەدەین یان تۆ دەمرێنین (بەڵێنەکان نابینی) ئەوسا ھەمووتان ھەر بۆلای ئێمە دەگەڕێنەوە');
INSERT INTO "bamoki" VALUES(40,78,'40:78','سوێند بەخوا بەڕاستی بەر لەتۆ پێغەمبەرانێکمان ناردووە بەسەرھاتی ھەندێکیانمان بۆ باس کردوی وە ھەندێکیانمان بۆ باس نەکردویت وە ھیچ پێغەمبەرێك ناتوانێت موعجیزەیەك بھێنێت مەگەر بەئیزنی خوا نەبێت جاکاتێك فەرمانی خوا ھات (بەسزادانیان) بەھەق دادوەری دەکرێت (لەنێوانیاندا) وە لەوێدا ناڕاستان زەرەرۆمەند دەبن');
INSERT INTO "bamoki" VALUES(40,79,'40:79','خوا ئەو زاتەیە ئاژەڵ و ماڵاتی بۆ ئێوە بەدیھێناوە بۆ ئەوەی سواری ھەندێکیان بن و (گۆشتی) ھەندێکیشیان بخۆن');
INSERT INTO "bamoki" VALUES(40,80,'40:80','وە لەو (ئاژەڵانە) دا چەند سوودو کەڵك بۆ ئێوە ھەیە وە تابەھۆی ئەوانەوە بگەنە پێویستیەك کە لەدڵ و دەرونتاندایە وە لەسەر پشتی ئەو (وڵاخانە) و لەسەر کەشتی ئێوە سوار دەکرێن');
INSERT INTO "bamoki" VALUES(40,81,'40:81','خوا زۆر نیشانەی خۆیتان نیشان دەدات جا ئێوە ئینکاری کام لە نیشانە وبەڵگەکانی خوا دەکەن؟!');
INSERT INTO "bamoki" VALUES(40,82,'40:82','دەی ئایا بەزەویدا نەگەڕاون تا ببینن سەرەنجامی کەسانێك کە لە پێش ئەوان بوون چۆن بووە کەلەمان زۆر ترو بەھێزتریش بوون وە شوێنەواریشیان لەزەویدا (زیاتر بوو) ئەمجا ئەوەی کرد بوویان (لەدونیادا) سوودی پێ نەگەیاندن');
INSERT INTO "bamoki" VALUES(40,83,'40:83','جا کاتێك پێغەمبەرەکانیان بەبەڵگە ڕوونەکانەوە ھاتنە لایان دڵخۆش بوون بەو زانینەی ھەیان بوو ئەو (سزایەی ئەوان) گاڵتەیان پێ دەکرد چوار دەوریدان (وھاتە دی)');
INSERT INTO "bamoki" VALUES(40,84,'40:84','ئەمجا کاتێك سزای بەتینی ئێمەیان بینی ووتیان: باوەڕمان بەخوای تاك و تەنھا ھێنا وە بێ باوەڕین بەو شتانەی کردبوومانە ھاوبەشی خوا');
INSERT INTO "bamoki" VALUES(40,85,'40:85','جا باوەڕ ھێنانەکەیان ھیچ سوودی نەبوو بۆیان لەکاتێکدا سزای ئێمەیان بینی باوو سوننەتی خوا ھەر وا بووە پێشتر لەگەڵ بەندەکانیدا وە لەوێدا بێ باوەڕان زەرەرۆمەند بوون');
INSERT INTO "bamoki" VALUES(41,1,'41:1','بە(حا، میم) دەخوێنرێتەوە خوا زاناترە بەواتای، سەیری سەرەتای سورەتی (البقرة)ش بکە');
INSERT INTO "bamoki" VALUES(41,2,'41:2','قورئان لەلایەن خوای بەخشندەی میھرەبانەوە نێردراوەتە خوارەوە');
INSERT INTO "bamoki" VALUES(41,3,'41:3','پەڕاوێکە بەڵگەکانی ڕوونکراوەتەوە (لێك جیاکراوەتەوە) قورئانێکە بەزمانی عەرەبی بۆ گەلێك کەشارەزان و دەزانن');
INSERT INTO "bamoki" VALUES(41,4,'41:4','مژدەدەر وترسێنەرە کەچی زۆربەیان گوێی پێ نادەن و بۆیە (قورئان) نابیستن');
INSERT INTO "bamoki" VALUES(41,5,'41:5','دەڵێن دڵەکانمان لەناو چەند پەردەیەکدایە (تێ ناگەین) لەئەوەی تۆ ئێمەی بۆ بانگ دەکەیت وە لەگوێیەکانماندا گرانی و کەڕی ھەیە وە لەنێوان ئێمەو تۆدا پەردەیەك ھەیە کەواتە تۆ کاری خۆت بکە و بێگومان ئێمەیش کاری خۆمان دەکەین');
INSERT INTO "bamoki" VALUES(41,6,'41:6','بڵێ: بەڕاستی من مرۆڤێکم وەك ئێوە بەڵام (ھەندە ھەیە من) نیگام بۆ دەکرێت کە بەڕاستی پەرستراوی ئێوە خوایەکی تاك و تەنھایە کەواتە باڕووتان ھەر بەرە و خوا بێت (لەپەرستنەکانتاندا) و داوای لێبوردنی لێ بکەن وە سزاو لەناوچوون بۆ ھاوبەشدانەرانە');
INSERT INTO "bamoki" VALUES(41,7,'41:7','ئەوانەی کەزەکات نادەن و باوەڕیشیان بەڕۆژی دوایی نیە');
INSERT INTO "bamoki" VALUES(41,8,'41:8','بەڕاستی ئەوانەی باوەڕیان ھێناوەو کردەوە چاکەکانیان کردووە پاداشتی بێ بڕانەوەیان بۆ ھەیە');
INSERT INTO "bamoki" VALUES(41,9,'41:9','بڵێ: ئاخۆ بەڕاستی ئێوە باوەڕتان بەو زاتە نیە؟ کە زەوی لەدوو ڕۆژدا دروستکردووە وە ھاوبەشانی بۆ دادەنێن ھەر ئەوە پەروەردگاری جیھانیان');
INSERT INTO "bamoki" VALUES(41,10,'41:10','وە لەسەر زەویدا شاخی زۆری داناوە وە فەڕ و بەرەکەتی تێ خستووە وە بژێوی (ھەموو گیاندارێکی) لەزەویدا دیاریکرد لەماوەی چوار ڕۆژدا بەیەکسانی بۆ گشت داواکەران');
INSERT INTO "bamoki" VALUES(41,11,'41:11','پاش دروستکردنی زەوی ئیرادەی دروستکردنی ئاسمانی کرد کە پێشتر دوکەڵێک بوو ئەمجا بەئاسمان و بەزەوی فەرموو: وەرن بەوویستی خۆتان یان بە ناچاری (بێنە ژێر فەرمانم) ھەر دووکیان ووتیان بەوویستی خۆمان ھاتین');
INSERT INTO "bamoki" VALUES(41,12,'41:12','ئەوسا حەوت ئاسمانی لەدوو ڕۆژدا دروستکرد (کەواتە خوای گەورە زەوی و ئاسمانەکانی بەشەش ڕۆژ دروستکرد) وە ھەر ئاسمانەی فەرمان (کار) ی خۆی بۆ نارد وەئاسمانی دونیامان بەئەستێرە درەوشاوەکان ڕازاندەوە، وپارێزراوە (لەگوێ گرتنی شەیتانەکان) ئەوە بڕیاری خوای زاڵ و بەدەسەڵاتە');
INSERT INTO "bamoki" VALUES(41,13,'41:13','جا ئەگەر ڕوویان وەرگێڕا ئەوە بڵێ بەھەورە تریشقەیەك (بروسکەیەك) دەتانترسێنم وەك ھەورە تریشقەی (گەلی) عادو سەمود');
INSERT INTO "bamoki" VALUES(41,14,'41:14','کاتێك پێغەمبەرەکانیان ھاتنە لایان لەبەردەمیان و لە پشتیانەوە (لەھەموو لایەکەوە) (پێیان ووتن) کەجگە لەخوا نەپەرستن ووتیان: ئەگەر پەروەردگارمان بیویستایە فریشتەیەکی دەناردە خوارەوە (بۆمان) بۆیە بەڕاستی ئێمە بەوەی ئێوەی پێ نێرراون باوەڕ ناکەین');
INSERT INTO "bamoki" VALUES(41,15,'41:15','ئەمجا (گەلی) عاد بەناڕەوا خۆیان بەگەورە زانی لەزەویدا وە ووتیان: کێ لەئێمە بەھێز ترە ئایا نەیان زانی بوو بێگومان ئەو خوایەی کەئەوانی دروستکردووە لەئەوان بەھێز ترە وە ئەوان دانیان نەدەنا بەبەڵگەکانی ئێمەدا وئینکاریان دەکرد');
INSERT INTO "bamoki" VALUES(41,16,'41:16','ئەوسا ئێمە بایەکی زۆر بەھێزی ساردمان بۆ ناردن لەڕۆژانێکی بەردەوام (وشوم)دا بۆ ئەوەی سزای ئابڕو بەریان پێ بچەژین لەژیانی دونیادا بێگومان سزای پاشە ڕۆژیان ئابڕو بەر (وڕیسواکەر) ترە وە ئەوان یارمەتی نادرێن');
INSERT INTO "bamoki" VALUES(41,17,'41:17','وە (ھۆزی) سەمودمان ڕێنمونی کرد بەڵام ئەوان زیاتر حەزیان لەکوێری وگومڕایی کرد تا شارەزایی و ڕێنمونی جا بۆیە ھەورە بروسکەی سزای ڕیسواکەر لێی دان بەھۆی ئەو (خراپانەوە) کەدەیانکرد');
INSERT INTO "bamoki" VALUES(41,18,'41:18','وە ئەو کەسانەشمان ڕزگارکرد کەباوەڕدارو پارێزکار ولەخواترس بوون');
INSERT INTO "bamoki" VALUES(41,19,'41:19','وە ڕۆژێك ھەموو دوژمنانی خوا بەرەو ئاگری دۆزەخ کۆدەکرێنەوە جا ئەوان (لە پێش کەوتن و دواکەوتن) ڕێگری دەکرێن (تا ھەموو پێکەوە بەڕیز بڕۆن)');
INSERT INTO "bamoki" VALUES(41,20,'41:20','تا کاتێك دەگەنە لای ئاگری دۆزەخ شایەتی دەدات لە دژیان گوێ و چاو و پێستی لەشیان بەو کردەوانەی کە کردویانە ( لەدونیادا)');
INSERT INTO "bamoki" VALUES(41,21,'41:21','وە بەپێستەکانیان دەڵێن: بۆچی شایەتیتان دا لەدژمان دەڵێن: ئەو خوایە ئێمەی ھێناوەتە زمان کە ھەموو شتێکی ھێناوەتە زمان (وقسەکردن) ھەر ئەویش یەکەمجار ئێوەی دروستکردوە وە ھەر بۆلای ئەویش دەگەڕێنرێنەوە');
INSERT INTO "bamoki" VALUES(41,22,'41:22','وە ئێوە خۆتان نەدەشاردەوە (نەدەپاراست لەگوناە) لەشایەتیدانی گوێ و چاوو پێستی لەشتان لەدژتان بەڵکو وا گومانتان دەبرد کەخوا نازانێ بەزۆرێك لەو کارانەی کە دەیکەن');
INSERT INTO "bamoki" VALUES(41,23,'41:23','ئا ئەوە گومانتان بوو کە دەتان برد بەپەروەردگارتان ئێوەی ھەڵدێرا (لەناوی بردن) وچوونە ڕیزی زیانباران و خەسارۆمەندانەوە');
INSERT INTO "bamoki" VALUES(41,24,'41:24','ئەمجا ئەگەر ئارام بگرن ئەوانە ئاگر شوێنی مانەوەیانە وە ئەگەر داوای لێبوردن و ڕازی بوونی خوا بکەن ئەوە خوا ھەرگیز ڕازی نابێت (خۆش نابێ لێیان)');
INSERT INTO "bamoki" VALUES(41,25,'41:25','وە چەند دۆست (و ھاودەم)مان لەدونیا بۆ دانان کە خراپەکردنی دونیاو (بێ بڕوایی) ڕۆژی دوایی بۆ ڕازاندنەوە وە بەڵێنی (سزادانیان) بەسەردا درا لە(ڕیزی) ئەو گەلانەی ڕابوردن پێش ئەمان لەجنۆکە و ئادەمی بەڕاستی زەرەرۆمەند بوون');
INSERT INTO "bamoki" VALUES(41,26,'41:26','وە بێ باوەڕان (بەیەکتریان) دەووت گوێ مەگرن بۆ ئەم قورئانە وە قسەی پڕوپوچی تێکەڵ بکەن بەڵکو سەرکەون (بەسەریدا)');
INSERT INTO "bamoki" VALUES(41,27,'41:27','دەی سوێند بەخوا بێگومان ئەوانەی بێ باوەڕ بوون پێیان دەچێژین سزایەکی سەخت وە تۆڵەیان لێ دەسەنین (بەتۆڵەی) خراپترینی تاوانێك کەدەیان کرد');
INSERT INTO "bamoki" VALUES(41,28,'41:28','ئەوەی (باسکرا) سزای دوژمنانی خوایە کەئاگری دۆزەخە ماڵی ھەمیشەی ئەوان لەو ئاگرەدایە ئەو سزایە (دەدرێن) چونکە دانیان نەدەنا بەنیشانەکانی ئێمەدا');
INSERT INTO "bamoki" VALUES(41,29,'41:29','وە ئەوانەی بێ باوەڕن دەڵێن: ئەی پەروەردگارمان ئەو دوانەمان نیشان بدەن کە گومڕایان کردین لە جنۆکە وئادەمی تا بیانخەینە ژێر پێمان تا لە ژێری ژێرەوەی دۆزەخ بن');
INSERT INTO "bamoki" VALUES(41,30,'41:30','بەڕاستی ئەوانەی ووتیان خوا پەروەردگارمانە و لەپاشان ڕێك و ڕاست (لەسەر ئاین) بەردەوام بوون فریشتەکان پۆل پۆل دێنە لایان (کاتی مردن یان زیندوو بوونەوە) (پێیان دەڵێن) کەمەترسن و خەفەت مەخۆن وە مژدە بێ لێتان ئەو بەھەشتەی کەبەڵێنتان پێ درابوو');
INSERT INTO "bamoki" VALUES(41,31,'41:31','ئێمە لەژیانی دونیا و لەڕۆژی دواییدا پشتیوانی ئێوەین وە لەو (بەھەشتەدا) ھەرچی حەزی لێ بکەن بۆتان ئامادەیەوە ھەرچی داوا بکەن لەوێدا لەخزمەتتاندایە');
INSERT INTO "bamoki" VALUES(41,32,'41:32','ڕێزلێنان و میوانداریە لەلایەن خوای لێبوردەی میھرەبانەوە');
INSERT INTO "bamoki" VALUES(41,33,'41:33','کێ ووتەی چاکترو بەجێترە لەوکەسەی (خەڵك) بۆلای خوا بانگ دەکات وەکاری چاکە دەکات و دەڵێ بێگومان من لەکۆمەڵی گەردنکەچ و موسڵمانانم');
INSERT INTO "bamoki" VALUES(41,34,'41:34','چاکە و خراپە چوون یەك نین بەچاکترین شێوە وەڵامی (خراپە) بدەرەوە ئەوسا کەسێك کە لەنێوان تۆ و ئەودا دوژمنایەتی ھەیە وەك دۆستێکی دڵسۆز و گیانی بەگیانی لێ دێت (ئەمەش دەرەنجامی ڕەوشتی بەرز وکار وکردەوەی جوانە)');
INSERT INTO "bamoki" VALUES(41,35,'41:35','بەم ئاستە ناگات کەسانێك نەبێت کە خۆڕادەگرن و بەئارامن وە پێی ناگات مەگەر کەسێك خاوەنی بەشی ھەرە گەورە بێت (لەپاداشت)');
INSERT INTO "bamoki" VALUES(41,36,'41:36','وە ئەگەر لەشەیتانەوە تووشی خەتەرەو خەیاڵی نادروست بوویت ئەوە پەنا بگرە بەخوا چونکە بەڕاستی ھەر ئەو بیسەر و زانایە');
INSERT INTO "bamoki" VALUES(41,37,'41:37','لەنیشانەکانی (دەسەڵاتی) خوایە شەوو ڕۆژو خۆرو مانگ، سوژدە مەبەن نەبۆ خۆرو نەبۆ مانگ بەڵکو سوژدە بەرن بۆ ئەو خوایەی ئەوانی بەدیھێناوە ئەگەر ئێوە تەنھا ئەو دەپەرستن');
INSERT INTO "bamoki" VALUES(41,38,'41:38','جا ئەگەر خۆیان بەگەورە زانی (من بپەرستن) دەی ئەوانەی لای پەروەردگارتن (فریشتەکان) بەشەو و بەڕۆژ عیبادەتی ئەو دەکەن و ماندو وەڕەس نابن');
INSERT INTO "bamoki" VALUES(41,39,'41:39','لەنیشانەکانی(دەسەڵاتی) خوایە کەزەوی دەبینیت ووشك و ماتە کەچی کاتێك بارانمان باراند بەسەریدا (بەڕواندنی ڕوەك) دەکەوێتە جوڵەو دەبوژێتەوە (بەرز دەبێتەوە) بێگومان ئەوەی (زەوی ووشك) دەبوژێنێتەوە زیندووکەرەوەی مردووەکانیشە بەڕاستی ئەو (خوا) بەسەر ھەموو شتێکدا بەتوانایە');
INSERT INTO "bamoki" VALUES(41,40,'41:40','بێگومان ئەوانەی لەئایەتەکانی ئێمە لادەدەن لەئێمە شاراوە نین دەی ئایا کەسێك دەخرێتە ناو ئاگرەوە چاکترە یان ئەو کەسەی لەڕۆژی قیامەتدا بێ ترس دێت ھەرچیتان وویست (حەزتان لێ کرد) بیکەن بێگومان خوا بەوەی دەیکەن بینایە');
INSERT INTO "bamoki" VALUES(41,41,'41:41','بەڕاستی ئەوانەی کەقورئانیان بۆ ھات وباوەڕیان پێ نەھێنا (سزایان دەدەین) کەبەڕاستی کتێبێکی زۆر بەرزو جێگەی ڕێزە');
INSERT INTO "bamoki" VALUES(41,42,'41:42','بەتاڵ وناڕاستی ڕوی تێ ناکات نە لەئێستا و نەلە دوواڕۆژدا (نە بەلێ کەمکردن نە بەلێ زیادکردن) چونکە نێردراوەتە خوارەوە لەلایەن (خوای) کاردروست و ستایشکراوەوە');
INSERT INTO "bamoki" VALUES(41,43,'41:43','ھیچ شتێك بەتۆ ناوترێت مەگەر ئەوەی بەپێغەمبەرانی بەرلەتۆ ووتراوە بەڕاستی پەروەردگارت خاوەن لێبوردنە (بۆ ئیمانداران) وە خاوەن تۆڵەی سەختە (بۆ بێ باوەڕان)');
INSERT INTO "bamoki" VALUES(41,44,'41:44','ئەگەر ئەم قورئانەمان بەزمانێکی تر جگـە لـەعەرەبی بناردایە بێ باوەڕان دەیانووت بۆچی ئایەتەکانی بەڕوونی شینەکراونەتەوە (چۆن دەبێت) قورئانی عەجەمی و پێغەمبەری عەرەبی (ئەی موحەممەد صلى الله عليه وسلم) بڵێ: قورئان بۆ کەسانێك باوەڕیان پێی ھێناوە ڕێنیشاندەرو شیفایە وە ئەوانەی باوەڕی پێ نایەنن لەگوێیاندا گرانی و کەڕی ھەیە (لەبیستنی ھەق) ھەروەھا لەئاست ئەو قورئانەدا کوێرن، ئەوانە لەشوێنێکی زۆر دورەوە بانگ دەکرێن');
INSERT INTO "bamoki" VALUES(41,45,'41:45','سوێند بەخوا بەڕاستی تەوراتمان دابە موسا بەڵام جیاوازی تێدا کرا (لەلایەن جولەکەکانەوە) وە ئەگەر پێشتر بڕیار نەدرایە لەلایەن پەروەردگارتەوە (کە سزایان دوا بخرێت) لەنێوانیاندا دادوەری دەکرا (بەسزا دانیان لەدونیا) وە بەڕاستی ئەوان لەدوو دڵی و بەدگومانیدان دەربارەی قورئان');
INSERT INTO "bamoki" VALUES(41,46,'41:46','ھەر کەسێك کاری چاکە بکات (قازانجەکەی) بۆ خۆیەتی ھەر کەسیش خراپە بکات (زیانەکەی) بۆ خۆیەتی وە پەروەردگارت ستەم لەبەندەکانی ناکات');
INSERT INTO "bamoki" VALUES(41,47,'41:47','زانینی ڕۆژی دوایی تەنیا بۆ لای خوا دەگەڕێنرێتەوە وە ھەر بەروبوومێك کە لە پێست و قەباغەکەی دێتە دەرەوە و ھەر مێینەیەك ئاوس دەبێ و (سکەکەی) دادەنێت ئەوە (خوا) ئاگای لێیە وە ڕۆژێك خوا (ھاوبەش پەیدا کەران) بانگ دەکات (پێیان دەڵێت) ھاوبەشانێك (کەداتان نابوو بۆ من) لەکوێن دەڵێن پێمان ڕاگەیاندی کەس لەئێمە شایەت نین (تۆھاوبەشت ھەبێت)');
INSERT INTO "bamoki" VALUES(41,48,'41:48','وە وون بوولێیان ئەوانەی پێشتر دەیانپەرستن وە زانیان کە ھیچ پەنایەکیان نیە');
INSERT INTO "bamoki" VALUES(41,49,'41:49','ئادەمی ماندو نابێت لە داواکردنی خێرو خۆشی وە ئەگەر ناخۆشیەك دووچاری ببێت ئەوە بێ ھیوا و نائومێد دەبێت');
INSERT INTO "bamoki" VALUES(41,50,'41:50','وە ئەگەر پێمان بەخشی نیعمەت وڕەحمەتێك لەلایەن خۆمانەوە لەپاش زیانێك لێی کەوتووە دەڵێت: ئەمە ھی خۆمە وەگومان نابەم ڕۆژی دوایی بەرپا بێت وە ئەگەر گەڕێندرامەوە بۆلای پەروەردگارم (زیندووبونەوە ھەبوو) بێگومان لای خوا (پاداشتی) زۆر چاکم ھەیە جا سوێند بەخوا ھەواڵ دەدەین بەوانەی بێ باوەڕ بوون بەوەی کردوویانە (لەدونیا) وە سوێند بەخودا پێیان دەچەژین سزایەکی بەھێز و بەتین');
INSERT INTO "bamoki" VALUES(41,51,'41:51','وە کاتێك چاکە و خۆشیمان ڕشت بەسەر ئادەمیدا ڕوو وەردەگێڕێ و بە خۆبەزل زانینە وە دوور دەکەوێتەوە بەڵام کاتێك ناخۆشی تووش بێت ئەوە نزاو ھاواری زۆر دەکات');
INSERT INTO "bamoki" VALUES(41,52,'41:52','بڵێ پێم بڵێن ئەگەر (قورئان) لەلایەن خواوە بوو پاشان ئێوەش باوەڕتان پێ نەکرد کێ گومڕاترە لەو کەسەی کەلە دژایەتیەکی دوور لەڕاستیدا بێت');
INSERT INTO "bamoki" VALUES(41,53,'41:53','لەمەودوا بەڵگە کانی خۆمانیان نیشان دەدەین لە ئاسۆی دونیا (لەبوونەوەردا) وە لە خودی خۆیاندا ھەتا بۆیان ڕوون بێتەوە بێگومان ئەم (قورئانە) ڕاستە ئایا پەروەردگارت بەس نیە کە بەڕاستی ئاگادارە بەسەر ھەموو شتێکدا');
INSERT INTO "bamoki" VALUES(41,54,'41:54','ئاگاداربە بەڕاستی ئەوانە لە دوو دڵیدان لەدیدار وگەشتن بە پەروەردگاریان ئاگاداربە بەڕاستی (زانست وزانیاری) خوا دەوری ھەموو شتێکی داوە');
INSERT INTO "bamoki" VALUES(42,1,'42:1','بە (حا میم) دەخوێنرێتەوە');
INSERT INTO "bamoki" VALUES(42,2,'42:2','بە (عەین، سین، قاف) دەخوێنرێتەوە خوا زاناترە بە ماناکەی بۆ زانیاری زیاترو واتاکەشیان سەرنجی سەرەتای سورەتی (البقرة) بدە');
INSERT INTO "bamoki" VALUES(42,3,'42:3','ئا بەو جۆرە خودا نیگا دەکات بۆ تۆ وە بۆ ئەو (پێغەمبەرانەی) پێش تۆش خوای زاڵی باڵادەستی کاردروستە');
INSERT INTO "bamoki" VALUES(42,4,'42:4','ھەر بۆ خوایە ھەرچی لە ئاسمانەکان وە ھەرچی لە زەویدایە وە ئەو (خوا) بڵند و گەورەیە');
INSERT INTO "bamoki" VALUES(42,5,'42:5','نزیکە ئاسمانەکان پارچە پارچەببن لە سەرویانەوە وە فریشتەکان بەپاکی یاد وسوپاسی پەروەردگاریان دەکەن وە داوای لێبوردن دەکەن بۆ ئەوانەی لەزەویدان ئاگاداربن کە بێگومان تەنھا خوا لێبوردە و میھرەبانە');
INSERT INTO "bamoki" VALUES(42,6,'42:6','وە ئەوانەی جگە لەخوا دۆستانێکیان بۆ خۆیان ھەڵبژاردووە خوا ئاگادار و چاودێرە بەسەریانەوە وە تۆ (ئەی موحەممەد صلى الله عليه وسلم) پارێزەر وچاودێر نیت بەسەریانەوە');
INSERT INTO "bamoki" VALUES(42,7,'42:7','ھەروەھا نیگامان کردووە بۆت قورئانێك (بەزمانی) عەرەبی بۆ ئەوەی خەڵکی مەککە و کەسانی دەوروبەری بترسێنیت وە (خەڵك) بترسێنیت لەڕۆژیی کۆکردنەوە کە ھیچ گومانی تێدا نیە کۆمەڵێك لە بەھەشتدان وە کۆمەڵێك لە ئاگریی بڵێسەداردان');
INSERT INTO "bamoki" VALUES(42,8,'42:8','وە ئەگەر خوا بیویستایە دەیکردن بەیەك نەتەوە (باوەڕداریان بێ باوەڕ) بەڵام ھەرکەسێکی بوێت دەیخاتە ناو میھرەبانی خۆیەوە، ستەمکارەکانیش ھیچ دۆست و یارمەتی دەرێکیان نیە');
INSERT INTO "bamoki" VALUES(42,9,'42:9','ئایا ئەوان جگە لەخوا پشتیوان و دۆستانێکیان بۆ خۆیان ھەڵبژاردوە؟ ھەر خوا پشتیوان و دۆستە وە تەنھا ئەو مردووان زیندوو دەکاتەوە وە ئەو بەدەسەڵات وبە توانایە بەسەر ھەموو شتێکدا');
INSERT INTO "bamoki" VALUES(42,10,'42:10','وە ھەرشتێك کە تێیدا جیاواز بوون ئەوە بڕیارەکەی بۆ لای خوا دەگەڕێتەوە ئەو خوایە پەروەردگاری منە تەنھا پشتم بەو بەستووە وە ھەر بۆلای ئەویش دەگەڕێمەوە');
INSERT INTO "bamoki" VALUES(42,11,'42:11','(خوا) بەدیھێنەری ئاسمانەکان و زەویە، بۆی بەدیھێناون ھاوسەران لەخودی خۆتان ھەروەھا لە ئاژەڵیشدا جووتی (دروستکردووە) لەجووت بووندا ژمارەتان زۆر دەکات ھیچ شتێك بەوێنەی خوا نیە (نە لەزات و نە لەسیفات و نە لە کرداریدا) وە ئەوە بیسەرو بینایە');
INSERT INTO "bamoki" VALUES(42,12,'42:12','ھەر بۆ ئەوە (کلیلەکانی) دەسەڵاتداریی ئاسمانەکان وزەوی، ڕزق و ڕۆزی فراوان دەکات و دەیگرێتەوە لە ھەرکەسێك کە بیەوێت بەڕاستی خوا ئاگادارە بەھەموو شتێك');
INSERT INTO "bamoki" VALUES(42,13,'42:13','ئەو ئاینەی بۆ داناوون کە فەرمانی داوە بە نوح و ئەوەش نیگامان کردووە بۆ تۆ و ئەوەش کە فەرمانمان داوە بە ئیبراھیم وموسا وعیسا کە لەسەر ئەو ئاینەبن بەڕێك و ڕاستی و جیاوازی تێدا مەکەن زۆر قورس وگرانە بەلای ھاوبەشدانەرانەوە ئەوەی ئەوانی بۆ بانگ دەکەیت خوا ھەرکەسی بوێت ھەڵی دەبژێرێ بۆ(موسڵمان بوون) لای خۆی وە ڕێنمونی کەسێك دەکات بۆ لای خۆی بگەڕێتەوە');
INSERT INTO "bamoki" VALUES(42,14,'42:14','وە جیاواز نەبوون مەگەر لەپاش ئەوەی کەزانست و زانیاریان بۆ ھات بەھۆی دژایەتی و حەسوودی نێوانیانەوە وە ئەگەر فەرمانێك پێشتر لە پەروەردگارتەوە دەرنەچوایە تا کاتێکی دیاریکراو (دەبێ بژین) ئەوە بڕیار لەنێوانیاندا دەکرا (بەسزادانیان) وە بەڕاستی ئەوانەی لەدوای ئەوان نامەیان پێـدرا لە گومان و دوو دڵیدا بوون لێی');
INSERT INTO "bamoki" VALUES(42,15,'42:15','کەواتە تۆ بۆ ئەوە بانگەوازبکە و ڕاست و ڕاوەستاوبە لەسەر ئاینەکەت بەو جۆرەی فەرمانت پێکراوە وە شوێن ئارەزووەکانی ئەوان مەکەوە وەبڵێ باوەڕم ھێناوە بەھەرنامەیەك کە خوا ناردویەتیە خوارەوە وە فەرمانم پێـدراوە کە داد پەروەربم لە نێوانتاندا خوا پەروەردگاری ئێمە و ئێوەشە (پاداشتی) کردەوەی خۆمان بۆ خۆمانە و (پاداشتی) کردەوەی ئێوەش بۆ خۆتانە ھیچ بەڵگەیەك نەماوە لە نێوان ئێمە و ئێوەدا (باس نەکرابێت)، خوا (لەڕۆژی دواییدا) کۆمان دەکاتەوە سەرەنجام و گەڕانەوەش ھەر بۆ لای ئەوە');
INSERT INTO "bamoki" VALUES(42,16,'42:16','ئەوانەی دەمەدەمێ دەکەن لەبارەی خواوە لەدوای ئەوەی (خەڵکی بانگەوازی) ئەویان وەڵام دایەوە و(موسڵمان بوون) بەڵگەکانیان پوچ و بەتاڵە لای پەروەردگاریان وە خەشم و ڕقی خوایان لەسەرە وە سزای زۆر سەختیان بۆ ھەیە');
INSERT INTO "bamoki" VALUES(42,17,'42:17','خوا ئەو زاتەیە کە ئەم قورئانەی بەڕاستی ناردووەتە خوارەوە وە تەرازووی ناردووە (بۆ جیاکردنەوەی ھەق و بەتاڵ) وە تۆ چوزانیت لەوانەیە ھاتنی ڕۆژی دوایی زۆر نزیك بێت');
INSERT INTO "bamoki" VALUES(42,18,'42:18','ئەوانەی باوەڕیان پێی نیە پەلەی ھاتنی دەکەن بەڵام ئەوانەی باوەڕیان پێی ھەیە دەترسن لێی وە دەزانن ڕۆژی دوایی ڕاستە بێداربن بەڕاستی ئەوانەی کە گومانیان ھەیە لەڕۆژی دوایی ئەوە بەڕاستی لە گومڕاییەکی زۆر دوور لەڕاستی دان');
INSERT INTO "bamoki" VALUES(42,19,'42:19','خوا دڵسۆزە بەبەندەکانی ھەرکەسی بوێت ڕزق و ڕۆزی دەدات وە ھەر ئەوە بەھێز و باڵادەسەت');
INSERT INTO "bamoki" VALUES(42,20,'42:20','ھەرکەس بەرھەمی دوا ڕۆژی بوێت ئێمە بەرھەمەکەی بۆ زیاد دەکەین وە ھەرکەسێك بەرھەمی دونیای بوێت ھەندێك لەدونیای پێ دەدەین بەڵام لەڕۆژی دواییدا ھیچ بەشێکی نیە');
INSERT INTO "bamoki" VALUES(42,21,'42:21','ئاخۆ ئەو (بێباوەڕانە) ھاوبەشانێکیان ھەیە ئاینێکیان بۆ داناوون کە خوا ڕێگەی پێ نەداوە وە ئەگەر بڕیاری یەکجاری دەرنەچوایە لەنێوانیان دا بڕیار دەدرا (بە لە ناوبردنیان) وە بەڕاستی بۆ ستەمکاران سزای سەختی ئازاردەر ھەیە');
INSERT INTO "bamoki" VALUES(42,22,'42:22','تۆ ستەمکاران دەبینیت زۆر دەترسن لەتاو ئەوەی کردویانە بێگومان ئەو سزایە ھەر دووچاریان دەبێت وە ئەوانەی باوەڕیان ھێناوە و چاکەکانیان کردووە لە ناو باخ وباخاتی بەھەشتدان لە لایەن پەروەردگاریانەوە ھەرچیەکیان بوێت بۆیان ئامادەیە ئە وەیە چاکەو بەخششی زۆر گەورە');
INSERT INTO "bamoki" VALUES(42,23,'42:23','ئەو (بەخششە) ئەوەیە کە خوا موژدە دەدات بە بەندەکانی ئەوانەی کە باوەڕیان ھێناوە و چاکەکانیان کردووە (ئەی موحەممەد صلى الله عليه وسلم) بڵێ داوای ھیچ پاداشتێکتان لێ ناکەم لەسەری مەگەر خۆشویستنی خزمایەتی ھەر کەس ھەر چاکەیەک بکات چاکەی بۆ زیاد دەکەین (بە چەند بەرانبەر) بەڕاستی خوا لێبوردە و سوپاسگوزارە');
INSERT INTO "bamoki" VALUES(42,24,'42:24','یان ئەو (بێ باوەڕانە) دەڵێن پێغەمبەر درۆی بەدەم خواوە ھەڵبەستووە خۆ ئەگەر خوا بیەوێت مۆر دەنێ بەسەر دڵتدا وە خوا بەتاڵ لە ناو دەبات وە بە ووتەکانی (قورئان) ڕاستی جێگیر دەکات بەڕاستی خوا زانایە بەوەی لە ناو دڵاندایە');
INSERT INTO "bamoki" VALUES(42,25,'42:25','وە ئە و خوا زاتێکە تەوبە لە بەندەکانی وەردەگرێت وە لە کردەوە خراپەکان چاوپۆشی دەکات وە دەزانێت بە ھەرچی کەئێوە دەیکەن');
INSERT INTO "bamoki" VALUES(42,26,'42:26','ئەوانەی باوەڕیان ھێناوە و چاکەکانیان کردووە (داوایان) وەردەگرێت وە لە فەزڵ و بەخششی خۆیی زیاتریان دەداتێ بەڵام بێ باوەڕان سزای سەختیان بۆ ھەیە');
INSERT INTO "bamoki" VALUES(42,27,'42:27','خۆ ئەگەر خوا ڕزق و زۆری فراوان بکردایە بۆ بەندەکانی لە زەویدا ستەمیان دەکرد وسەرکەش دەبوون بەڵام (خوا) ڕزق و ڕۆزی بەو ئەندازە دەنێرێتەخوارەوە کە خۆی بیەوێت بەڕاستی خوا بەئاگا و بینایە بە بەندەکانی');
INSERT INTO "bamoki" VALUES(42,28,'42:28','وە خوا ئەو زاتەیە کە باران دەبارێنێت لە پاش ئەوەی کە خەڵکی بێ ئومێد بوون وە (بارانی) ڕەحمەتی خۆی بڵاو دەکاتەوە وەھەر ئە و پشتیوان و سوپاسکرا وە');
INSERT INTO "bamoki" VALUES(42,29,'42:29','وە لە بەڵگەکانی (دەسەڵاتی) خوایە دروستکردنی ئاسمانەکان و زەوی و ئەوەی بڵاوی کردوەتەوە لە گیانلەبەران لە ھەر دووکیاندا (لە زەوی و لە ئاسمانەکان) وە خوا ھەر کاتێک بیەوێت بەتوانایە بەسەر کۆکردنەوەیاندا');
INSERT INTO "bamoki" VALUES(42,30,'42:30','وە ھەر ناخۆشیەکتان تووش بووبێت ئەوە بەھۆیی کردەوەیی (خراپی) خۆتانەوەیە وە لە زۆرێکیش چاوپۆشی کردووە');
INSERT INTO "bamoki" VALUES(42,31,'42:31','وە ئێوە ناتوانن لە زەویدا قوتاربن وە نیتانە بێجگە لە خوا ھیچ پشتیوان و یارمەتی دەرێك');
INSERT INTO "bamoki" VALUES(42,32,'42:32','وە لە بەڵگەکانی (دەسەڵاتی) خوایە کەشتیانێک وەک شاخەکان لە دەریادا دەڕۆن');
INSERT INTO "bamoki" VALUES(42,33,'42:33','ئەگەر خوابیەوێت (ھەوا) ڕادەگرێت ئیتر کەشتیەکان بەسەر پشتی دەریاکەوە دەوەستن بەڕاستی لەوەی (کەباس کرا) چەندین بەڵگە و نیشانە ھەیە بۆ ھەموو ئارام گرێکی سوپاسگوزار');
INSERT INTO "bamoki" VALUES(42,34,'42:34','یان بەھۆی کردەوەکانیانەوە نوقمیان دەکات لەزۆرێکیش (لە گوناھیان) چاوپۆشی دەکات');
INSERT INTO "bamoki" VALUES(42,35,'42:35','ئەوانەی کەدەمە قاڵی دەکەن دەربارەی بەڵگەکانی ئێمە دەزانن ھیچ پەنایەکیان نیە');
INSERT INTO "bamoki" VALUES(42,36,'42:36','جا ھەرشتێکتان پێ بەخشراوە ئەوە تەنھا خۆشی ژیانی ئەم دونیایە وە ئەوەی لای خوایە چاکتر و بەردەوامترە بۆ ئەوانەی باوەڕیان ھێناوە وە تەنھا پشت بەپەروەردگاریان دەبەستن');
INSERT INTO "bamoki" VALUES(42,37,'42:37','وە ئەوانەی لە گوناھە گەورەکان و خراپە زۆر ناشیرینەکان دور دەکەونەوە وە کاتێک ڕق و قینیان ھەڵسێت ئەوان لێ دەبوورن');
INSERT INTO "bamoki" VALUES(42,38,'42:38','وە ئەوانەی کە گوێڕایەڵی پەروەردگاریان کردووە نوێژەکانیان بەچاکی ئەنجامداوەوە کارو باری نێوانیان بەپرس و ڕاوێژە وە لەو ڕۆزیەی پێمان داوون دەیبەخشن');
INSERT INTO "bamoki" VALUES(42,39,'42:39','وە ئەوانەی کاتێك دەست درێژیان لێ دەکرێت تۆڵەی خۆیان دەستێنن (و سەرداناخەن)');
INSERT INTO "bamoki" VALUES(42,40,'42:40','وە تۆڵەی ھەر خراپەیەك خراپەیەکە بەوێنەی خۆی جا ھەرکەسێک ببورێت و ئاشتی بکات ئەوە پاداشتی ئەو لە لای خوایە بەڕاستی خوا ستەمکارانی خۆش ناوێت');
INSERT INTO "bamoki" VALUES(42,41,'42:41','وە ھەرکەس تۆڵە بسەنێ لە پاش ئەوەی ستەمی لێکراوە ئا ئەوانە ھیچ تاوان وگوناھێکیان لەسەر نیە');
INSERT INTO "bamoki" VALUES(42,42,'42:42','تاوان وگوناە ھەر لەسەر ئەو کەسانەیە کەستەم لەخەڵکی دەکەن وە بە ناھەق خراپەکاری دەکەن لە زەویدا ئەوانە سزای زۆر سەختیان بۆ ھەیە');
INSERT INTO "bamoki" VALUES(42,43,'42:43','وە ھەر کەس خۆ گرو لێ بوردە بێت بەڕاستی ئەمە لەکارە پڕ بایەخ وپەسەندەکانە');
INSERT INTO "bamoki" VALUES(42,44,'42:44','وە ھەر کەس خوا گومڕایی بکات ئەوە پاش ئەوە ھیچ دۆست و یارمەتی دەرێکی نیە وە ستەمکاران دەبینیت کاتێك سزای (خوا) دەبینن دەڵێن ئایا ڕێگـایەك ھەیە بگەرێینەوە بۆ دونیا');
INSERT INTO "bamoki" VALUES(42,45,'42:45','وە دەیان بینیت پیشانی دۆزەخ دەدرێن بەملکەچی وزەلیلی و داماوی بەگۆشەی چاو و بەدزیەوە دەڕوانن وە ئەوانەی کە باوەڕیان ھێناوە دەڵێن بەڕاستی زیانبارەکان ئەوانەن کە خۆیان و کەس و کاریان لەڕۆژی ھەستانەوەدا لە دەستداوە بێداربن بەڕاستی ستەمکاران لە سزایەکی ھەمیشەییدان');
INSERT INTO "bamoki" VALUES(42,46,'42:46','وە ئەوانە ھیچ پشتیوانێکیان نیە کەیارمەتیان بدەن بێجگە لەخوا وە ھەرکەسێک خوا گومڕایی بکات ئەوە ھیچ ڕێگەیەکی ڕزگار بوونی بۆ نیە');
INSERT INTO "bamoki" VALUES(42,47,'42:47','گوێڕایەڵ بن بۆ پەروەردگارتان پێش ئەوەی ڕۆژێك بێت لەلایەن خواوە کە گەڕانەوەی بۆ نیە ئەو ڕۆژە ھیچ پەناگایەکتان نابێت وە ھیچ ئینکاریەکتان پێ ناکرێت');
INSERT INTO "bamoki" VALUES(42,48,'42:48','جا ئەگەر ڕوویان وەرگێڕا ئەوە ئێمە تۆمان بەپارێزەر وچاودێر نەناردووە بۆ سەریان تۆ تەنھا ڕاگەیاندت لەسەرە وە بەڕاستی کاتێك ئێمە لەلایەن خۆمانەوە ڕەحمەت و نیعمەتێک بە کەسێك بچەژین پێی دڵخۆش دەبێت وە ئەگەر ناخۆشیەکیان تووش ببێت بەھۆی کردەوەی خۆیانەوە ئەوە بەڕاستی ئادەمی پێ نەزان و سپڵەیە');
INSERT INTO "bamoki" VALUES(42,49,'42:49','ھەر بۆ خوایە موڵك وپاشایەتی ئاسمانەکان و زەوی ھەرچیەکی بیەوێت بەدی دەھێنێت بۆ ھەرکەسێك بیەوێت کچی پێ دەبەخشێت وە بۆ ھەرکەسیش بیەوێت کوڕی پێ دەبەخشێت');
INSERT INTO "bamoki" VALUES(42,50,'42:50','یان کوڕ و کچی بەیەکەوە پێ دەبەخشێت وە ھەرکەسێکیشی بوێت نەزۆکی دەکات بەڕاستی خوا زانا و دەسەڵاتدارە');
INSERT INTO "bamoki" VALUES(42,51,'42:51','بۆ ھیچ کەس نەبووە کەخوا قسەی لەگەڵدا بکات بە وەحی نەبێت یان لەپشت پەردەوە یان نێرراوێك بنێرێت و بەفەرمانی خۆی ئەوەی کە بیەوێت وەحی بۆ دەکات بەڕاستی خوا بڵند وکاردروستە');
INSERT INTO "bamoki" VALUES(42,52,'42:52','ھەر بەو شێوە (کە وەحی پێ دێت) قورئانمان بۆ تۆ نارد (ئەی موحەممەد صلى الله عليه وسلم) بە فەرمانی خۆمان کەتۆ نەتدەزانی کتێب وئیمان چیە بەڵام ئێمە ئەوەمان کردە ڕووناکیەك ڕێنمونی ھەر کەسێکی پێ دەکەین کەبمانەوێت لەبەندەکانمان وە بەڕاستی تۆ (خەڵك) ڕێنمونی دەکەیت بۆ ڕێگای ڕاست');
INSERT INTO "bamoki" VALUES(42,53,'42:53','ڕێگەی ئەو خوایەی کە ھەرچی لە ئاسمانەکان و ھەرچی لە زەویدایە ھی ئەوە بێداربن ھەموو کارەکان ھەر بۆلای خوا دەگەڕێتەوە');
INSERT INTO "bamoki" VALUES(43,1,'43:1','بە (حا، میم) دەخوێنرێتەوە خوا زاناترە بە ماناکەی بۆ زانیاری زیاتر سەیری سەرەتای سورەتی (البقرة) بکە');
INSERT INTO "bamoki" VALUES(43,2,'43:2','سوێند بەم پەڕاوە ڕوون کەرەوەیە');
INSERT INTO "bamoki" VALUES(43,3,'43:3','بەڕاستی ئێمە قورئانمان بە عەرەبی داناوە بۆ ئەوەی تێ بگەن');
INSERT INTO "bamoki" VALUES(43,4,'43:4','بەڕاستی ئەم قورئانە لەتابلۆی پارێزراودایە لای ئێمە (لە لوح المحفوظ دا) کەزۆر بڵندە و پڕە لەدانایی');
INSERT INTO "bamoki" VALUES(43,5,'43:5','دەی ئایا ئێمە ئەم قورئانەتان لێ دوور بخەینەوە، لەبەر ئەوەی ئێوە گەلێكی زیادەڕۆن');
INSERT INTO "bamoki" VALUES(43,6,'43:6','وە زۆر پێغەمبەرمان لە گەلە پێشوەکان ناردووە');
INSERT INTO "bamoki" VALUES(43,7,'43:7','وە ھەر پێغەمبەرێکیان بۆ ھاتبێت ئەوان ھەر گاڵتەیان پێکردووە');
INSERT INTO "bamoki" VALUES(43,8,'43:8','جا کەسانێك کە لەمان بەھێزتر بوون لەناومان بردن وە بەسەرچوو سەرگوزشتەی پێشینان');
INSERT INTO "bamoki" VALUES(43,9,'43:9','وە ئەگەر پرسیاریان لێ بکەیت کێ ئاسمانەکان و زەوی بەدیھێناوە؟ بێگومان دەڵێن خوای زاڵ و باڵادەست و زانا دروستی کردوون');
INSERT INTO "bamoki" VALUES(43,10,'43:10','ئەو خوایەی زەوی کردووە بەلانکە بۆتان وە چەند ڕێگایەکی تیادا دروستکردووە بۆتان بۆ ئەوەی بگەن بە مەبەستەکانتان');
INSERT INTO "bamoki" VALUES(43,11,'43:11','وە ئەو خوایەی کە لەئاسمانەوە بەئەندازە بارانی بۆ باراندوون جا ووڵاتێکی ووشك و مردووی پێ زیندوو دەکەینەوە ئابەو جۆرە ئێوەش زیندوو دەکرێنەوە');
INSERT INTO "bamoki" VALUES(43,12,'43:12','وە ئەو خوایەی کە ھەموو جووتەکانی بەدیھێناوە وە کەشتی و وڵاخی بۆ فەراھەم ھێناون کە سواریان دەبن');
INSERT INTO "bamoki" VALUES(43,13,'43:13','بۆ ئەوەی سواری پشتیان بن لەپاشان بیر لەنیعمەتی پەروەردگارتان بکەنەوە کاتێك سواریان بوون وەبڵێن پاك و بێگەردی بۆ ئەو (خوایە)ی کە ئەمەی بۆ ڕام ھێناوین کە پێشتر ئێمە توانامان بەسەریدا نەبوو');
INSERT INTO "bamoki" VALUES(43,14,'43:14','وە بەڕاستی ئێمە بۆ لای پەروەردگارمان دەگەڕێینەوە');
INSERT INTO "bamoki" VALUES(43,15,'43:15','وە بێگومان لەناو بەندەکانی خوادا ھەندێك (منداڵیان) بۆ پەروەردگار دانا وە بەڕاستی مرۆڤی(کافر) پێ نەزان و سپڵەی ئاشکرایە');
INSERT INTO "bamoki" VALUES(43,16,'43:16','ئایا لەناو دروستکراوەکانیدا کچانی بۆ خۆی ھەڵبژاردوە وە کوڕانی تایبەت کردووە بەئێوەوە');
INSERT INTO "bamoki" VALUES(43,17,'43:17','وە کاتێك مژدە بەیەکێك لەوان بدرێت بەوەی بەنموونە بۆ خوای دادەنێت (واتە: ئەگەر پێی بووترێت کچتان بووە) ڕووی ڕەش ھەڵدەگەڕێت و پیش دەخواتەوە');
INSERT INTO "bamoki" VALUES(43,18,'43:18','ئایا ئەو (کچە)ی لەناو ئاڵتون و خشڵا بەخێوکرابێت و لەکاتی دەمەقاڵێدا ناتوانێ مەبەستی خۆی دەرببڕێت (بۆ خوای بڕیار دەدەن)');
INSERT INTO "bamoki" VALUES(43,19,'43:19','وەئەو فریشتانەی کە بەندەی خوای بەخشندەن بەمێینەیان دادەنان ئایا ئەوان بەدیاری دروستکردنیانەوە بوون بێگومان ئەو شایەتیانەیان دەنوسرێت و پرسیاریان لێ دەکرێت');
INSERT INTO "bamoki" VALUES(43,20,'43:20','وە دەڵێن ئەگەر خوا بیوویستایە ئێمە ئەو (فریشتانەمان) نەدەپەرست ھیچ زانیاریەکیان نیە لەو بارەیەوە ئەوانە ھەردرۆ دەکەن');
INSERT INTO "bamoki" VALUES(43,21,'43:21','وە ئایا لە پێش قورئان پەرتوکێکمان پێـداون کە ئەوان دەستیان پێوە گرتبێت');
INSERT INTO "bamoki" VALUES(43,22,'43:22','(نەخێر وانیە) بەڵکو دەیانووت بەڕاستی ئێمە باو وباپیرانمان لەسەر ئاینێك بینیوە (لەسەر بت پەرستی) وە بێگومان ئێمەش پەیڕەوی ڕێبازی ئەوانین');
INSERT INTO "bamoki" VALUES(43,23,'43:23','ھەروەھا پێش تۆ ھیچ پێغەمبەرێکمان نەناردووە لەھیچ شارێکدا مەگەر خۆشگوزەرانەکان (دەوڵەمەندەکان)ی ووتویانە بەڕاستی ئێمە باوو باپیرانی خۆمان لەسەر ئاینێك بینیوە وە بەڕاستی ئێمە لەسەر ئاینی ئەوان دەڕۆین');
INSERT INTO "bamoki" VALUES(43,24,'43:24','ووتی ئەگەر چی من (ئاینی) ڕاستریشم بۆ ھێنابن لەوەی کە باووباپیرتان بینیوە لەسەری، ووتیان بێگومان ئێمە بێ باوەڕین بەو(ئاینەی) بەئێوەدا نێرراوە');
INSERT INTO "bamoki" VALUES(43,25,'43:25','بۆیە تۆڵەمان لێسەندن جا سەیرکە سەرئەنجامی درۆزنان چۆن بوو');
INSERT INTO "bamoki" VALUES(43,26,'43:26','وە کاتێك ئیبراھیم بەباوکی وگەلەکەی ووت بێگومان من بەریم لەوەی ئێوە دەیپەرستن');
INSERT INTO "bamoki" VALUES(43,27,'43:27','جگە لەو زاتەی کەدروستی کردووم چونکە ھەر ئەویش ڕێنمونیم دەکات');
INSERT INTO "bamoki" VALUES(43,28,'43:28','ئەو (یەکخواپەرستی) لەناو نەوەکانیدا بەجێھێشت تابگەڕێنەوە (سەری)');
INSERT INTO "bamoki" VALUES(43,29,'43:29','بەڵکو من ئەوانە وباو وباپیرانیانم لەخۆشگوزەرانیدا ھێشتەوە ھەتا ئەو کاتەی قورئانیان بۆ ھات لەگەڵ پێغەمبەری ئاشکرا');
INSERT INTO "bamoki" VALUES(43,30,'43:30','جا کاتێك قورئانیان بۆ ھات ووتیان ئەمە جادوە وە بەڕاستی ئێمە باوەڕی پێ ناکەین');
INSERT INTO "bamoki" VALUES(43,31,'43:31','وە ووتیان بۆچی ئەم قورئانە نەنێرراوەتە خوارەوە بۆ پیاوێکی گەورەی (ناودارو دارا) لەم دوو شارە (لە مەککە و طائف)');
INSERT INTO "bamoki" VALUES(43,32,'43:32','ئایا ئەوان ڕەحمەتی پەروەردگارت دابەش دەکەن لەکاتێکدا ئێمە لەنێوانیاندا دابەشمان کردووە ھۆی گوزەرانیان لەژیانی ئەم دونیایەدا وە ھەندێکیانمان چەند پلە بەرزکردۆتەوە بەسەر ھەندێکی تریاندا تاھەندێکیان ئیش بەھەندێکی تریان بکەن ولەخزمەتی یەکتردا بن وە بەزەیی پەروەردگارت (بۆ تۆ) لەوە چاکترە کە ئەوان کۆی دەکەنەوە');
INSERT INTO "bamoki" VALUES(43,33,'43:33','خۆ ئەگەر لەبەر ئەوە نەبوایە کە خەڵکی یەك ئوممەتی بێ باوەڕ بوونایە وامان دەکرد ھەر کەس بێ باوەڕ بوایە بەخوای میھرەبان، سەقفی خانووەکانیان لەزیوو بوایە ھەروەھا پلیکانەکان کەپێیـداسەر دەکەون (بۆمان دەکردن بەزیوو)');
INSERT INTO "bamoki" VALUES(43,34,'43:34','وەدەرگای زۆرمان بۆ خانووەکانیان دادەنا وە کورسی وقەنەفەی زۆر تا لەسەری دانیشن وپاڵ بدەنەوە');
INSERT INTO "bamoki" VALUES(43,35,'43:35','زرق وبرق وڕازاندنەوەی ماڵی دونیا (لەئاڵتون و زیوو) (خوای گەورە واز دەھێنێت لەو بێباوەڕانە کە لەزەت لەماڵ وسامان وخۆشی دونیا وەربگرن، بەڵام پاشەڕۆژ ھەر دەگەڕێنەوە بەردەستی) جا ھەموو ئەوانە چەند ساتێکە لە لەزەت وخۆشی ژیانی دونیا بەڵام (پاداشتی) ڕۆژی دوایی لای پەروەردگارت بۆ پارێزکاران ولەخواترسانە');
INSERT INTO "bamoki" VALUES(43,36,'43:36','وە ھەرکەس پشت لە یادی خوا ھەڵکات شەیتانێکی بۆ ئامادە دەکەن کە ھەمیشە لەگەڵی بێت ولێی جیا نەبێتەوە');
INSERT INTO "bamoki" VALUES(43,37,'43:37','وە بێگومان ئەوانە (شەیتانەکان) لەڕێگایی ڕاست ڕێگریان دەکەن واش دەزانن کەبێگومان ھەر ئەوانن کە ھیدایەتدراوون');
INSERT INTO "bamoki" VALUES(43,38,'43:38','ھەتا کاتێك کە(دەمرێ و) دێتەوە لامان دەڵێ خۆزگە نێوانی من وتۆ بەقەدەر نێوان خۆرھەڵات وخۆرئاوا بوایە ئای چ ھاوڕێیەکی خراپ بوویت');
INSERT INTO "bamoki" VALUES(43,39,'43:39','(ئەم خۆزگە) ھیچ سوودێکتان پێناگەیەنێت چونکە ستەمتان کرد بێگومان ئێوە لە سزای سەختدا ھاوبەشن');
INSERT INTO "bamoki" VALUES(43,40,'43:40','دەی ئایا تۆ(ئەی موحەممەد صلى الله عليه وسلم) دەتوانی بەکەڕ بشنەوێنیت یان کوێر ڕێنمونی بکەیت وە (ڕێنمونی) ئەو کەسە (بکەیت) کەلەگومڕاییەکی ئاشکرادایە');
INSERT INTO "bamoki" VALUES(43,41,'43:41','جا ئەگەر تۆ بمرێنین ئەوە بێگومان ئێمە ھەر تۆڵە لەوان دەسێنین');
INSERT INTO "bamoki" VALUES(43,42,'43:42','یان ئەو ھەڕەشەی لێمان کردوون پیشانت دەدەین چونکە بەڕاستی ئێمە بەدەسەڵاتین بەسەریاندا');
INSERT INTO "bamoki" VALUES(43,43,'43:43','کەواتە دەست بگرە بەو قورئانەوەی بۆت نێرراوە بەڕاستی تۆ لەسەر ڕێبازێکی ڕاستیت');
INSERT INTO "bamoki" VALUES(43,44,'43:44','وەبێگومان ئەم قورئانە ھۆی ناوداریە بۆ خۆت و بۆ گەلەکەت لە داھاتووشدا پرسیارتان لێ دەکرێت');
INSERT INTO "bamoki" VALUES(43,45,'43:45','تۆ پرسیار بکە لەو پێغەمبەرانەی پێش تۆ ناردوومانن ئایا بێجگە لە خوای میھرەبان بڕیارمانداوە پەرستراوانێك کە بپەرسترێن');
INSERT INTO "bamoki" VALUES(43,46,'43:46','سوێند بەخوا بەڕاستی موسامان بەبەڵگەکانمانەوە نارد بۆلای فیرعەون ودەست وپێوەندەکەی، ووتی بەڕاستی من پێغەمبەری پەروەردگاری جیھانیانم');
INSERT INTO "bamoki" VALUES(43,47,'43:47','جا کاتێك بەڵگەکانی ئێمەی بۆ ھێنان کەچی ئەوان بەبەڵگەکان پێـدەکەنین');
INSERT INTO "bamoki" VALUES(43,48,'43:48','وە ھیچ بەڵگەیەکمان نیشان نەداون مەگەر ئەو بەڵگەیە گەورەتربوو لەھاوتاکەی وەتووشی بەڵاو سزامان کردن (چونکە باوەڕیان نەھێنا) بۆ ئەوەی بگەڕێنەوە (لەبێ باوەڕیی)');
INSERT INTO "bamoki" VALUES(43,49,'43:49','ووتیان (بەموسا) ئەی جادوگەر داوامان بۆ بکە لە پەروەردگارت ئەوەی بەڵێنی پێ داویت (بەدی بھێنێت) ئەوسا بەڕاستی ئێمە باوەڕدەھێنین');
INSERT INTO "bamoki" VALUES(43,50,'43:50','جا کاتی بەڵاو سزاکەمان لەسەریان لابرد کتوپڕ پەیمانەکەیان ھەڵدەوەشاندەوە');
INSERT INTO "bamoki" VALUES(43,51,'43:51','وە فیرعەون جاڕیدا بەناو گەلەکەیدا ووتی ئەی گەلەکەم ئایا پادشایی میصر ھی من نیە؟ وە (ئایا) ئەم ڕووبارانە بەژێر (کۆشکەکانی) مندا ناڕۆن؟ دەی ئایا ئێوە نایبینن؟!');
INSERT INTO "bamoki" VALUES(43,52,'43:52','بەڵکو من باشترم لەو (موسایە) کەلاواز و بێ ڕێزە وە ناتوانێت بەچاکی قسە بکات (زمانی تەواو نیە)');
INSERT INTO "bamoki" VALUES(43,53,'43:53','دە بۆچی بازنی ئاڵتونی پێ نەدراوە؟ (ئەگەر ڕاست دەکات) یان فریشتەی (شایەتیدەری) لەگەڵدا نەھاتووە');
INSERT INTO "bamoki" VALUES(43,54,'43:54','جا بەم قسانە گەلەکەی ھەڵخەڵەتان وئەوانیش گوێرایەڵیان کرد، بێگومان ئەوان ھەمیشە گەلێکی لە سنوور دەرچووبوون');
INSERT INTO "bamoki" VALUES(43,55,'43:55','جا کاتێك ڕقیان ھەڵساندین تۆڵەمان لێسەندن، ئەمجا ھەموویانمان لەدەریادا خنکاند');
INSERT INTO "bamoki" VALUES(43,56,'43:56','و کردمانن بەپەند ونموونە وئامۆژگاری بۆ کەسانی دوای خۆیان');
INSERT INTO "bamoki" VALUES(43,57,'43:57','وە کاتێك عیسای کوڕی مەریەم بەنموونە ھێنرایەوە دەستبەجێ گەلەکەت دەیکەنە ھاوار وقیڕەی خۆشی (وادەزانن قسەی ئەوان بەسەر قسەی پێغەمبەردا صلى الله عليه وسلم سەرکەوتووە)');
INSERT INTO "bamoki" VALUES(43,58,'43:58','وە دەڵێن ئایا پەرستراوەکانی ئێمە باشترە یان عیسا ئەوانە نموونەی عیسایان تەنھا بۆ دەمبازی ودەمەدەمێ بۆ دەھێنایتەوە (وانیە کە دەیڵێن) بەڵکو ئەوانە گەلێکی زۆر دەمەقاڵێ کەرن');
INSERT INTO "bamoki" VALUES(43,59,'43:59','عیسا ھیچ نیە جگە لە بەندەیەك کە نیعمەتی خۆمان ڕشتووە بەسەریدا وە کردومانە بەنموونە بۆ نەوەی ئیسرائیل');
INSERT INTO "bamoki" VALUES(43,60,'43:60','ئەگەر بمانویستایە لەجیاتی ئێوە فریشتەمان دادەنا لەزەویدا دەبوونە جێنشینی ئێوە');
INSERT INTO "bamoki" VALUES(43,61,'43:61','وە بێگومان (دابەزینی عیسا بۆ سەر زەوی) یەکێکە لەنیشانەکانی قیامەت، کەواتە دەربارەی قیامەت ھیچ گومانتان نەبێ ڕاستە وە شوێنی من بکەون ئەمە ڕێگای ڕاستە');
INSERT INTO "bamoki" VALUES(43,62,'43:62','وەباشەیتان ڕێگریتان نەکات لەڕێنمونی خوا، بەڕاستی ئەو (شەیتان) دوژمنێکی ئاشکرای ئێوەیە');
INSERT INTO "bamoki" VALUES(43,63,'43:63','وە کاتێك عیسا بەڵگە ڕوونەکانی ھێنا ووتی (بەنەوەی ئیسرائیل) بێگومان بەپێغەمبەرایەتی ھاتووم بۆ ناوتان بۆ ئەوەی ڕوونی بکەمەوە بۆتان ھەندێ لەوشتانەی جیاوازن تێیدا، دەی لەخوا بترسن وگوێرایەڵیم بکەن');
INSERT INTO "bamoki" VALUES(43,64,'43:64','بەڕاستی خوا پەروەردگاری من وپەروەردگاری ئێوەشە کەواتە ھەر ئەو بپەرستن ھەر ئەمە ڕێگای ڕاستە');
INSERT INTO "bamoki" VALUES(43,65,'43:65','جا کۆمەڵەکان جیاواز بوون لەنێوان خۆیاندا، سزاو لەناوچوون بۆ ئەوانەی ستەمیان کرد، لەسزای سەختی ڕۆژی بەئازار');
INSERT INTO "bamoki" VALUES(43,66,'43:66','ئایا جگە لە ڕۆژی دوایی چاوەڕوانی چی دەکەن کە کتوپڕ بێت بۆیان لەکاتێکدا ئەوان ھەست ناکەن');
INSERT INTO "bamoki" VALUES(43,67,'43:67','دۆست وخۆشەویستەکان لەو ڕۆژەدا دوژمنی یەکترن جگە لە پارێزکاران ولەخواترسان نەبێت');
INSERT INTO "bamoki" VALUES(43,68,'43:68','(پێیان دەوترێت) ئەی بەندەکانم ئەمڕۆ ھیچ مەترسیەکتان لەسەر نیە خەفەتیش ناخۆن');
INSERT INTO "bamoki" VALUES(43,69,'43:69','ئەوانەی باوەڕیان بەئایەتەکانی ئێمە ھێناوە وھەمیشە گەردنکەچ بوون');
INSERT INTO "bamoki" VALUES(43,70,'43:70','(لەو ڕۆژەدا پێیان دەڵێن) بچنە بەھەشتەوە ئێوە وھاوسەرەکانتان (ڕێز دەگیرێن و) دڵ خۆش دەکرێن');
INSERT INTO "bamoki" VALUES(43,71,'43:71','بەسینی وپەرداخی ئاڵتونی (خواردن وخواردنەوە) دەگێڕن بەسەریاندا وە لەو بەھەشتەدا ھەیە ئەوەی دڵ حەزی لێ بکات وچاو لەززەت لەبینینی ببات وە ئێوە بەھەتا ھەتایی دەمێننەوە تیایدا');
INSERT INTO "bamoki" VALUES(43,72,'43:72','ئەوە ئەو بەھەشتەیە کە بۆ ئێوە ماوەتەوە (پێتان دراوە) بەھۆی ئەو کردەوانەوەی کە دەتانکرد');
INSERT INTO "bamoki" VALUES(43,73,'43:73','بۆ ئێوە ھەیە تێیدا میوەھاتی زۆر کەلێیان دەخۆن');
INSERT INTO "bamoki" VALUES(43,74,'43:74','بەڕاستی تاوانباران لەسزای دۆزەخدا بەھەمیشەیی دەمێننەوە');
INSERT INTO "bamoki" VALUES(43,75,'43:75','سزایان لەسەر سووك ناکرێت وە ئەوان لەناو ئەو سزایەدا بێ ئومێدن');
INSERT INTO "bamoki" VALUES(43,76,'43:76','وە ئێمە ھیچ ستەمێکمان لێ نەکردوون بەڵکو ھەر خۆیان ستەمکاربوون');
INSERT INTO "bamoki" VALUES(43,77,'43:77','وە ھاوار دەکەن ئەی مالیك (سەرپەرشتیاری دۆزەخ) با پەروەردگارت بمان مرێنێت (تاڕزگاربین) (ئەویش پاش ماوەیەکی زۆر وەڵامیان دەداتەوە و) دەڵێت: بێگومان ئێوە دەمێننەوە تیایدا');
INSERT INTO "bamoki" VALUES(43,78,'43:78','سوێند بەخوا بەڕاستی (ئاینی) ھەقمان بۆ ھێناون بەڵام زۆربەتان ڕاستیتان پێ ناخۆشە');
INSERT INTO "bamoki" VALUES(43,79,'43:79','بەڵکو ئەوانە بۆ پیلان وفێڵیان (لەپێغەمبەر صلى الله عليه وسلم) لێبڕان، بۆیە بەڕاستی ئێمەش (بۆ تۆڵە لێ سەندنیان) لێبڕاین');
INSERT INTO "bamoki" VALUES(43,80,'43:80','ئاخۆ وا دەزانن ئێمە نھێنی وچپەکانیان نابیستین، بەڵێ دەیبیستین فریشتەکانیشمان کەلەلایانن وئامادەی (ئەو پیلان وفێڵانەیان) دەنوسن');
INSERT INTO "bamoki" VALUES(43,81,'43:81','بڵێ ئەگەر پەروەردگار کوڕی ببوایە ئەوە من یەکەمین کەس بووم دەمپەرست');
INSERT INTO "bamoki" VALUES(43,82,'43:82','پاك وبێگەردی بۆ پەروەردگاری ئاسمانەکان وزەوی وپەروەردگاری عەرش لەو (قسە ناڕێکانە)ی کە ئەوان بۆ خوای ھەڵدەبەستن');
INSERT INTO "bamoki" VALUES(43,83,'43:83','جا لێیان گەرێ با سەرگەرمی پڕوپوچی بن وگاڵتە بکەن ھەتا دەگەن بەڕۆژی خۆیان کە بەڵێـنیان پێـدراوە');
INSERT INTO "bamoki" VALUES(43,84,'43:84','وە ئەو (خوایە) زاتێکە پەرستراوە لە ئاسماندا وە پەرستراوە لە زەویدا وە تەنھا ئەویش کار دروست وزانایە');
INSERT INTO "bamoki" VALUES(43,85,'43:85','وە پاك وبەفەڕو گەورەیە ئەو خوایەی کە خاوەنی ئاسمانەکان وزەویە وە ھەرچی لەنێوانیاندایە وە زانینی (ھاتنی) ڕۆژی دوایی تەنھا لای ئەوە وە ھەر بۆ لای ئەو دەگەڕێنەوە');
INSERT INTO "bamoki" VALUES(43,86,'43:86','وە ئەوانەی بێجگە لە خوا دەپەرسترێن (وھاواریان لێ دەکرێت) ناتوانن تکا بکەن مەگەر کەسانێك شایەتییان بەھەق دابێت وە ھەقیان زانیوە وئاگاداری بوون');
INSERT INTO "bamoki" VALUES(43,87,'43:87','وە ئەگەر پرسیاریان لێ بکەیت کێ دروستی کردوون؟ بێگومان دەڵێن خوا دەی چۆن ئەوان لەھەق وڕاستی لادەدن');
INSERT INTO "bamoki" VALUES(43,88,'43:88','وە ( پێغەمبەر صلى الله عليه وسلم شکاتی کردو) ووتی ئەی پەروەردگار (خوای گەورە فەرمووی: ئەی موحەممەد صلى الله عليه وسلم) تۆش وازیان لێ بھێنە وگوێیان پێ مەدە وپێان بڵێ (سەلام) لەمەو پاش دەزانن (چی یان بەسەردێت)بەڕاستی ئەوانە گەلێکن باوەڕ ناھێنن');
INSERT INTO "bamoki" VALUES(43,89,'43:89','(خوای گەورە فەرمووی: ئەی موحەممەد صلى الله عليه وسلم) تۆش وازیان لێ بھێنە و گوێیان پێ مەدە وپێان بڵێ (سەلام) لەمەو پاش دەزانن (چی یان بەسەردێت)');
INSERT INTO "bamoki" VALUES(44,1,'44:1','بە (حا، میم) دەخوێنرێتەوە خوا زاناترە بە ماناکەی بۆ زانیاری زیاتر سەیری سەرەتای سورەتی (البقرة) بکە');
INSERT INTO "bamoki" VALUES(44,2,'44:2','سوێند بەم قورئانە ڕوونکەرەوەیە');
INSERT INTO "bamoki" VALUES(44,3,'44:3','بەڕاستی ئێمە ئەم قورئانەمان لە شەوێکی پیرۆزدا ناردوەتە خوارەوە، بێگومان ئێمە ترسێنەرین');
INSERT INTO "bamoki" VALUES(44,4,'44:4','لەو (شەوە پیرۆزە)دا ھەموو کار وبارێکی شیاو لە یەك جیادەکرێتەوە');
INSERT INTO "bamoki" VALUES(44,5,'44:5','فەرمانێکە لەلایەن ئێمەوە بەڕاستی ئێمە پێغەمبەران دەنێرین');
INSERT INTO "bamoki" VALUES(44,6,'44:6','کەڕەحمەتێکە لەلایەن پەروەردگارتەوە بێگومان ھەر خوا بیسەر وزانایە');
INSERT INTO "bamoki" VALUES(44,7,'44:7','پەروەردگاری ئاسمانەکان وزەوی وە ھەرچی لە نێوانیاندایە، ئەگەر ئێوە ئیمانداری ڕاستەقینەن');
INSERT INTO "bamoki" VALUES(44,8,'44:8','ھیچ پەرستراوێکی بەھەق وڕاست نیە جگە لە خوا، (خوا) دەژێنێت ودەمرێنێت پەروەردگاری ئێوە وپەروەردگاری باو وباپیرە پێشوەکانتانە');
INSERT INTO "bamoki" VALUES(44,9,'44:9','بەڵام ئەوان لە دوو دڵی وگوماندا گەمە وگاڵتە دەکەن');
INSERT INTO "bamoki" VALUES(44,10,'44:10','جا چاوەڕوانبە ڕۆژێك کە ئاسمان دوکەڵێکی ئاشکرای لێ پەیدا دەبێت');
INSERT INTO "bamoki" VALUES(44,11,'44:11','ھەموو ئادەمی دادەگرێت و (دەڵێن) ئەمە بەڵایەکی سەختە');
INSERT INTO "bamoki" VALUES(44,12,'44:12','ئەی پەروەردگارمان ئەم سزایەمان لەسەر لابەرە بەڕاستی ئێمە باوەڕدارین');
INSERT INTO "bamoki" VALUES(44,13,'44:13','چۆن تەمێ خواردەبن وئامۆژگاری وەردەگرن کە بەڕاستی پێغەمبەرێکی ڕوون وئاشکرایان بۆ ھاتووە');
INSERT INTO "bamoki" VALUES(44,14,'44:14','لە پاشان گوێیان پێنەداو وە ووتیان: فێرکراوێکی شێـتە');
INSERT INTO "bamoki" VALUES(44,15,'44:15','بێگومان بۆ ماوەیەکی کەم ئەم بەڵایە لا دەبەین، بێگومان ئێوە دەگەڕێنەوە (بۆ بێ باوەڕی)');
INSERT INTO "bamoki" VALUES(44,16,'44:16','ڕۆژێک دێت پەلامارێکی بەھێز و ھەرە گەورەیان بۆ دەبەین، بەڕاستی ئێمە تۆڵە دەسێنین (لە کافران لە جەنگی بەدردا)');
INSERT INTO "bamoki" VALUES(44,17,'44:17','سوێند بەخوا بەڕاستی لە پێش ئەمان گەلی فیرعەونمان تاقیکردەوە وپێغەمبەرێکی بەڕێزیان بۆ ھات');
INSERT INTO "bamoki" VALUES(44,18,'44:18','(پێی ووتن) کە بەندەکانی خوا (بەنو ئیسرائیل) بە من بسپێرن چونکە بەڕاستی من پێغەمبەرێکی ئەمین وباوەڕ پێکراوم بۆ ناو ئێوە');
INSERT INTO "bamoki" VALUES(44,19,'44:19','وە خۆتان بەبەرز وگەورە مەزانن لەبەرامبەر خوادا بێگومان من بەڵگە ونیشانەیەکی ئاشکرام بۆ ھێناون');
INSERT INTO "bamoki" VALUES(44,20,'44:20','وە بێگومان من پەنادەگرم بەپەروەردگاری خۆم و پەروەردگاری ئێوە لەوەی کە ئێوە بەردبارانم بکەن');
INSERT INTO "bamoki" VALUES(44,21,'44:21','وە ئەگەر باوەڕم پێناھێنن ئەوە وازم لێ بھێنن (و خراپەم دەربارە مەکەن)');
INSERT INTO "bamoki" VALUES(44,22,'44:22','جا ھاواری لە پەروەردگاری کرد (ووتی) بەڕاستی ئەمانە گەلێکی تاوانبارن');
INSERT INTO "bamoki" VALUES(44,23,'44:23','ئەوسا (پێی ووترا) بەندەکانم بەشەو بەرە چونکە بەڕاستی (فیرعەون وکۆمەڵەکەی) دواتان دەکەوون');
INSERT INTO "bamoki" VALUES(44,24,'44:24','وە دەریاکە (دەریای سوور) بەئارامی و ئاوەڵا بەجێبھێڵە بێگومان ئەوانە سەربازانێکی خنکێنراو دەبن (تێیدا)');
INSERT INTO "bamoki" VALUES(44,25,'44:25','چەندەھایان بەجێھێشت لە باخات وکانیاو');
INSERT INTO "bamoki" VALUES(44,26,'44:26','و کشتوکاڵ و جێ ومەئوای چاك و خۆش');
INSERT INTO "bamoki" VALUES(44,27,'44:27','وە ناز ونیعمەتی زۆر کەئەوان تیایدا دەژیان وڕایان دەبوارد');
INSERT INTO "bamoki" VALUES(44,28,'44:28','ھەربەم جۆرە ئەو (خۆشیە)مان دا بەگەلێکی تر');
INSERT INTO "bamoki" VALUES(44,29,'44:29','جا نەئاسمان ونەزەوی نەگریان بۆیان ومۆڵەتیشیان پێنەدرا');
INSERT INTO "bamoki" VALUES(44,30,'44:30','سوێند بەخوا بەڕاستی نەوەی ئیسرائیلمان ڕزگار کرد لە سزای ڕیسواکەر');
INSERT INTO "bamoki" VALUES(44,31,'44:31','لە فیرعەون، بێگومان فیرعەون خۆبەزل زانێکی لەسنوور دەرچوو بوو');
INSERT INTO "bamoki" VALUES(44,32,'44:32','سوێند بەخوا بەڕاستی نەوەی ئیسرائیلمان ھەڵبژارد بەسەر خەڵکی (سەردەمی خۆیان)دا بەپێی زانیاری (خۆمان)');
INSERT INTO "bamoki" VALUES(44,33,'44:33','وە موعجیزە وبەڵگەی زۆرمان پێدان کە بۆ تاقیکردنەوەی ڕوون وئاشکرا بەس بێت');
INSERT INTO "bamoki" VALUES(44,34,'44:34','بەڕاستی ئا ئەوانە دەڵێن');
INSERT INTO "bamoki" VALUES(44,35,'44:35','ھەر بۆیە یەکەمجار دەمرین ئیتر زیندوو ناکرێینەوە');
INSERT INTO "bamoki" VALUES(44,36,'44:36','دەی باوو باپیرانمان (زیندوو بکەنەوەو) بیان ھێننەوە (بۆ دونیا) ئەگەر ئێوە ڕاست دەکەن');
INSERT INTO "bamoki" VALUES(44,37,'44:37','ئایا ئەوانە چاکترن یان گەلی توببەع (ی پادشایی یەمەن) و ئەوانەی لە پێشیانەوە بوون ھەموویانمان لەناو برد چونکە بێگومان گشتیان تاوانباربوون');
INSERT INTO "bamoki" VALUES(44,38,'44:38','وە دروستمان نەکردووە ئاسمانەکان وزەوی ئەوەی لە نێوانیاندایە بەبێ ھودە وگاڵتە');
INSERT INTO "bamoki" VALUES(44,39,'44:39','ھەر دووکیانمان بەدی نەھێناوە بەھەق وڕاستی نەبێت بەڵام زۆربەیان نازانن');
INSERT INTO "bamoki" VALUES(44,40,'44:40','بێگومان ڕۆژی جیاکردنەوەی(ھەق و بەتاڵ) کاتی دیاریکراوی ھەموویانە');
INSERT INTO "bamoki" VALUES(44,41,'44:41','ڕۆژێکە ھیچ دۆستێک ناتوانێت کەمترین سوود بگەیەنێت بە دۆستی خۆی وە یارمەتیش نادرێن');
INSERT INTO "bamoki" VALUES(44,42,'44:42','مەگەر کەسێك خوا ڕەحمی پێ بکات بەڕاستی تەنھا خوا زاڵ وباڵادەست ومیھرەبانە');
INSERT INTO "bamoki" VALUES(44,43,'44:43','بێگومان درەختی ژەقنەموت');
INSERT INTO "bamoki" VALUES(44,44,'44:44','خۆراکی کەسانی تاوانبارە (کافرە)');
INSERT INTO "bamoki" VALUES(44,45,'44:45','وەکو خڵتەی ڕۆن زەیتوون لەناو سکەکاندا دەکوڵێت');
INSERT INTO "bamoki" VALUES(44,46,'44:46','ھەر وەکو کوڵانی ئاوی زۆر گەرم');
INSERT INTO "bamoki" VALUES(44,47,'44:47','(ئەو کافرە) بیگرن وبەتوندی رایکێشن تا (دەیبەنە) ناوەڕاستی دۆزەخ');
INSERT INTO "bamoki" VALUES(44,48,'44:48','پاشان بیڕژن بەسەر سەریدا ھەندێك لە ئاگر و ئاوی زۆر گەرم');
INSERT INTO "bamoki" VALUES(44,49,'44:49','(پێی دەوترێت) بیچێژە (بەگاڵتە پێکردنێکەوە پێی دەوترێت)چونکە بەڕاستی تۆ بە دەسەڵات وبەڕێز بوویت ( لەدونیادا)');
INSERT INTO "bamoki" VALUES(44,50,'44:50','بێگومان ئەمە ئەو سزایەیە کەئێوە گومانتان لێی ھەبوو');
INSERT INTO "bamoki" VALUES(44,51,'44:51','بەڕاستی پارێزکاران ولەخواترسان لە شوێنێکی بێوەیی وئارامدان');
INSERT INTO "bamoki" VALUES(44,52,'44:52','لەناو باخات ونزیك کانیاودان');
INSERT INTO "bamoki" VALUES(44,53,'44:53','لە ئاوریشمی ناسك وئەستور دەپۆشن ڕوبەڕووی یەکتری دادەنیشن');
INSERT INTO "bamoki" VALUES(44,54,'44:54','ھەروەھا کیژانی چاوگەورە وجوان دەکەینە ھاوسەریان');
INSERT INTO "bamoki" VALUES(44,55,'44:55','لەو بەھەشتەدا ھەموو جۆرە میوەیەك داوا دەکەن بەئاسوودەیی وبێوەیی');
INSERT INTO "bamoki" VALUES(44,56,'44:56','لە بەھەشتدا ھەرگیز مردن ناچەژن جگە لەمردنی یەکەمجار (کەلە دونیادا بوو) وە (خوا) لەسزای دۆزەخ پاراستوونی');
INSERT INTO "bamoki" VALUES(44,57,'44:57','ئەمە بەخششە لەلایەن پەروەردگارتەوە ئا ئەوەیە سەرکەوتنی زۆر گەورە');
INSERT INTO "bamoki" VALUES(44,58,'44:58','جا بەڕاستی ئەم قورئانەمان لەسەر زمانی تۆ ئاسان کردووە بۆ ئەوەی بیربکەنەوە');
INSERT INTO "bamoki" VALUES(44,59,'44:59','ئیتر تۆ چاوەڕوان بە، بێگومان ئەوانیش چاواڕوانن');
INSERT INTO "bamoki" VALUES(45,1,'45:1','بە (حا، میم) دەخوێنرێتەوە خوا زاناترە بە ماناکەی بۆ زانیاری زیاتر سەیری سەرەتای سورەتی (البقرة) بکە');
INSERT INTO "bamoki" VALUES(45,2,'45:2','ھێنانە خوارەوەی ئەم قورئانە لەلایەن خوای باڵادەست وکاردروستەوەیە');
INSERT INTO "bamoki" VALUES(45,3,'45:3','بێگومان لە ئاسمانەکان وزەویدا چەندەھا بەڵگە ھەیە بۆ باوەڕداران');
INSERT INTO "bamoki" VALUES(45,4,'45:4','وە لە دروستکردنی خۆتاندا وە لەو گیان لەبەرانەی بڵاودەبنەوە چەند بەڵگە ھەیە بۆ کەسانێك باوەڕی بتەویان ھەبێت');
INSERT INTO "bamoki" VALUES(45,5,'45:5','وە لە جیاوازی شەو وڕۆژدا وە لەو بارانەی کەخوا لەئاسمانەوە دەیبارێنێت (کە ھۆی ڕزق وڕۆزیە) کە بەھۆیەوە زەوی زیندوودەکاتەوە (ودەبووژێنێتەوە) وە لەگۆڕانی بایەکاندا چەند بەڵگە ھەیە بۆ کەسانێك کەتێـدەگەن');
INSERT INTO "bamoki" VALUES(45,6,'45:6','ئائەمانە بەڵگەکانی خوان دەیخوێنینەوە بۆت بەڕاستی ودروستی، جا بە چی قسە وگوفتارێك دوای خوا وبەڵگەکانی باوەڕ دەھێنن');
INSERT INTO "bamoki" VALUES(45,7,'45:7','سزاو لەناوچوون بۆ ھەموو درۆزنێکی گوناھبار');
INSERT INTO "bamoki" VALUES(45,8,'45:8','کە بەڵگەکانی خوا دەبیستێت وبۆی دەخوێنرێتەوە لەپاشان (لەسەر کافری) بەردەوامە چونکە خۆبەزل زانە ئەڵێی ھەر نەیبیستووە، دەی مژدەی پێبدە بەسزایەکی سەخت');
INSERT INTO "bamoki" VALUES(45,9,'45:9','وە ھەرکاتێك شتێك لەبەڵگەکانمان ئاگاداربێت بەگاڵتەی دەگرێت، ئەوانە سزای ڕیسواکەریان بۆ ھەیە');
INSERT INTO "bamoki" VALUES(45,10,'45:10','لە پێشیانەوە ئاگری دۆزەخە، ھیچ سوودێکیان پێ ناگەیەنێت ئەوەی لە(دونیا)دا کردوویانە وە ئەو پشتیوانانەی جگە لەخوا ھەڵیان بژاردون (سوودی نیە) وە سزایەکی زۆر گەورەیان بۆ ھەیە');
INSERT INTO "bamoki" VALUES(45,11,'45:11','ئەم قورئانە ھۆی ڕێنمونیە وە ئەوانەی بێ باوەڕن بە ئایەت وبەڵگەکانی پەروەردگاریان، بۆ ئەوانە ھەیە سزایەکی پڕ ئازاری سەخت');
INSERT INTO "bamoki" VALUES(45,12,'45:12','خوا ئەو زاتەیە دەریای بۆ ڕام ھێناون تا بەفەرمانی خوا کەشتی تیایدا بگەڕێت وە تا لەھەندێك بەخششی خوا بەھرەمەند ببن وە بۆ ئەوەی سوپاسی خوا بکەن');
INSERT INTO "bamoki" VALUES(45,13,'45:13','وە ئەوەی لە ئاسمانەکان وزەویدا ھەیە بۆ ئێوەی ژێربار کردووە (وخستویەتیە خزمەتتانەوە) ھەمووی لەلایەن خواوەیە، بێگومان لـەو شـتانـەدا چـەنـد بـەڵگـە ھـەیـە بـۆ کەسـانێك بیر بکەنەوە');
INSERT INTO "bamoki" VALUES(45,14,'45:14','بڵێ بەو کەسانەی بڕوایان ھێناوە چاو پۆشی بکەن لەو کەسانەی ھیوایان بە پاداشت وسزای خوا نیە، بۆ ئەوەی پاداشتی خەڵکانێك بداتەوە بەگوێرەی ئەوەی کردوویانە');
INSERT INTO "bamoki" VALUES(45,15,'45:15','ھەرکەسێك چاکەی کرد ئەوە (قازانجەکەی) بۆ خۆیەتی وە ھەرکەسێکیش خراپەی کرد ئەوە (زیانەکەی) ھەر بۆ خۆیەتی لەپاشان بۆلای پەروەردگارتان دەگەرێنرێنەوە');
INSERT INTO "bamoki" VALUES(45,16,'45:16','سوێند بەخوا بێگومان بە نەوەی ئیسرائیلمان بەخشی پەیام وحوکمڕانی وپێغەمبەرایەتی وە ڕۆزیمان پێـدان لە شتە حەڵاڵ وپاکەکان وە ڕێزماندان بەسەر جیھانیاندا');
INSERT INTO "bamoki" VALUES(45,17,'45:17','وە بەڵگە ڕوونەكانمان پێدان لە بارەی شەریعەتەوە، جا جیاواز نەبوون لە نێو خۆیاندا دوای ئەوە نەبێت زانستیان بۆهات، لەبەر دوژمنایەتی وحەسادەتی نێوان خۆیان، بێگومان پەروەردگارت لە ڕۆژی هەستانەوەدا لە نیاونیاندا دادوەری دەكات لەو شتانەی جیاوازیان تێدا دەكرد');
INSERT INTO "bamoki" VALUES(45,18,'45:18','لە پاشان تۆمان خستەسەر شەریعەتێك لە بارەی ئاینەوە تۆش شوێنی بكەوە وە شوێن هەواو ئارەزووی ئەوانە مەكەوە كە نازانن');
INSERT INTO "bamoki" VALUES(45,19,'45:19','بێگومان ئەوانە لەلایەن خواوە ھیچ سوودێکت پێ ناگەیەنن وە بەڕاستی ستەمکاران ھەندێکیان دۆست وپیشتیوانی ھەندێکیانن وەخوا دۆست وپشتیوانی پارێزکاران ولەخواترسانە');
INSERT INTO "bamoki" VALUES(45,20,'45:20','ئەم قورئانە بیناییە وھۆی چاوڕشنیە بۆ مەردومان وە ڕێنمونی ومیھرەبانیە بۆ گەلێك کەباوەڕی پتەویان ھەیە');
INSERT INTO "bamoki" VALUES(45,21,'45:21','ئایا ئەوانەی کە خراپەکانیان کردووە وا گومان دەبەن ئێمە ئەوانە وەکو کەسانێك دادەنێین کەبڕوایان ھێناوە وچاکەکانیان کردووە وەکو یەك بێت ژیانیان ومردنیان، ئای چەند خراپ دادوەری دەکەن');
INSERT INTO "bamoki" VALUES(45,22,'45:22','وەخوا ئاسمانەکان وزەوی بەھەق وڕاستی دروستکردووە بۆ ئەوەی ھەموو کەسێك پاداشت بدرێتەوە بەگوێرەی ئەو کار وکردەوانەی کە کردوویەتی وە ئەوان ھیچ ستەمیان لێناکرێت');
INSERT INTO "bamoki" VALUES(45,23,'45:23','ئاخۆ بینیوتە ئەو کەسەی ھەواو ئارەزووی خۆی کردۆتە پەرستراوی خۆی، خواش بەزانایی گومڕای کرد وە مۆری ناوە بەسەر گوێ ودڵیدا وە پەردەی داوە بەسەرچاویدا دەی جگە لە خوا کێ ڕێنمونی دەکات دەی بۆچی بیر ناکەنەوە');
INSERT INTO "bamoki" VALUES(45,24,'45:24','وە ووتیان ھیچ نیە جگە لە ژیانی دونیامان وە دەمرین ودەژین وە جگە لە ڕۆژگار ھیچ شتێك ئێمە لەناو نابات وە ئەوانە بەو قسانە ھیچ زانیاریان نیە تەنھا گومان دەبەن');
INSERT INTO "bamoki" VALUES(45,25,'45:25','وە کاتێك بخوێنرێتەوە بەسەریاندا بەڵگە ڕوونەکانمان ھیچ بەڵگەیەکیان نیە جگە ئەوە کە دەڵێن باووباپیرانمان زیندووبکەنەوە ئەگەر ئێوە ڕاست دەکەن');
INSERT INTO "bamoki" VALUES(45,26,'45:26','بڵێ: خوا زیندووتان دەکاتەوە لەپاشان دەتان مرێنێت پاشان کۆتان دەکاتەوە لە ڕۆژی دواییدا، ھیچ گومانی تێدا نیە، بەڵام زۆربەی مەردوومان نازانن');
INSERT INTO "bamoki" VALUES(45,27,'45:27','وە بۆ خوایە موڵکدارێتی ئاسمانەکان وزەوی، ئەو ڕۆژەی قیامەت ھەڵدەسێ، لەو ڕۆژەدا بەتاڵ خوازەکان خەسارۆمەندن');
INSERT INTO "bamoki" VALUES(45,28,'45:28','ھەموو گەلێك دەبینی کۆمەڵ کۆمەڵ کەوتوون بەسەر ئەژنۆدا، ھەموو گەلێك بانگ دەکرێت بۆلای پەڕاوەکەی، ئەمڕۆ سزاو پاداشت دەدرێنەوە بە گوێرەی ئەو کردەوانەی کە دەتانکرد');
INSERT INTO "bamoki" VALUES(45,29,'45:29','ئەمە پەڕاوەکەمانە بەڕاستی لەسەرتان دەدوێت بەڕاستی ئێمە وەك خۆی نوسیومانەتەوە (وێنەمان گرتووە) ئەوەی کە ئێوە دەتانکرد');
INSERT INTO "bamoki" VALUES(45,30,'45:30','جا ئەوانەی بڕوایان ھێناوە وکردەوە چاکەکانیان ئەنجامداوە ئەوە پەروەردگاریان دەیانخاتە ناو میھرەبانی خۆیەوە، ئا ئەوەیە سەرکەوتنی ئاشکرا');
INSERT INTO "bamoki" VALUES(45,31,'45:31','وە ئەوانەی بێ بڕوابوون (پێیان دەڵێن) ئاخۆ بەڵگەکانی ئێمەتان بەسەردا نەدەخوێنرایەوە کەچی خۆتان بە گەورە دەزانی وە ئێوە گەلێکی تاوانبار بوون');
INSERT INTO "bamoki" VALUES(45,32,'45:32','وە ھەر کاتێك بووترایە بێگومان بەڵێنی خوا ڕاستە وە ڕۆژی دوای ھیچ گومانی تێدا نیە، دەتانووت نازانین ڕۆژی دوایی چیە، ئێمە تەنیا گومان دەبەین وە ئێمە دڵنیانین');
INSERT INTO "bamoki" VALUES(45,33,'45:33','وە دەردەكەوێت بۆیان ئەو خراپانەی كە كردویانە وە دەوریان دەدا تۆڵەی ئەوەی گاڵتەیان پێدەكرد');
INSERT INTO "bamoki" VALUES(45,34,'45:34','پێیان دەووترێت ئەمڕۆ لەبیرتان دەکەین ھەر چۆنێك ئێوە ئەم ڕۆژەتان لەبیر چوو بوو وە جێگای مانەوەی ئێوە دۆزەخە وھیچ یارمەتیدەرێکتان نیە');
INSERT INTO "bamoki" VALUES(45,35,'45:35','ئەمە لەبەر ئەوەیە بێگومان ئێوە بەڵگەکانی خواتان بەگاڵتە دانا بوو وە ژیانی دونیا فریوی دان، جا ئەمڕۆ لەو دۆزەخەدا دەرناھێنرێن وەڕێگە نادرێن داوای لێبوردنیش بکەن');
INSERT INTO "bamoki" VALUES(45,36,'45:36','جا ھەموو سوپاس وستایش بۆ خوای پەروەردگاری ئاسمانەکان وزەوی وپەروەردگاری جیھانیانە');
INSERT INTO "bamoki" VALUES(45,37,'45:37','وە دەسەڵات وگەورەی تەنھا بۆ ئەوە لەھەموو ئاسمانەکان وزەویدا وە ھەر ئەو زاڵ وباڵادەست ودانا وکار سازە');
INSERT INTO "bamoki" VALUES(46,1,'46:1','بە (حا، میم) دەخوێنرێتەوە خوا زاناترە بە ماناکەی بۆ زانیاری زیاتر سەیری سەرەتای سورەتی (البقرة) بکە');
INSERT INTO "bamoki" VALUES(46,2,'46:2','ئەم قورئانە ھێنراوەتە خوارەوە لەلایەن خوای زاڵ وباڵادەست وکاربەجێوە');
INSERT INTO "bamoki" VALUES(46,3,'46:3','ئاسمانەکان وزەوی وئەوەی لە نێوانیاندایە دروستمان نەکردووە مەگەر بە ھەق وڕاستی و بۆ کاتێکی دیاری کراو نەبێت، کەچی ئەوانەی بێ باوەڕ بوون لەوەی کە پێی ترسێنرابوون ڕوو وەردەگێڕن');
INSERT INTO "bamoki" VALUES(46,4,'46:4','(ئەی موحەممەد صلى الله عليه وسلم) بڵێ: پێم بڵێن ئەو شتانەی ئێوە دەیانپەرستن جگە لە خوا پیشانم بدەن لە زەویدا چیان دروست کردووە یان لە(دروست کردنی) ئاسمانەکاندا بەشداریان کردووە، دەی پەڕاوێکم بۆ بھێنن لە پێش ئەم (قورئانە ھاتبێت) یان زانیاریەك کە لە پێشووان بەجێ مابێت ئەگەر ئێوە ڕاستگۆن');
INSERT INTO "bamoki" VALUES(46,5,'46:5','کێ گومڕاترە لەو کەسەی کە ھاوار لەغەیری خوا بکات کە وەڵامی ناداتەوە تا ڕۆژی دوایی وە ئەو (ھاوار لێکراو)انە لە ھاواری ئەوان بێئاگان');
INSERT INTO "bamoki" VALUES(46,6,'46:6','کاتێکیش خەڵك کۆدەکرێنەوە (لەقیامەت)دا پەرستراوەکان دەبنە دوژمنیان وە(پەرستراوەکان) بەپەرستنەکەیان باوەڕ ناکەن');
INSERT INTO "bamoki" VALUES(46,7,'46:7','کاتێکیش ئایەتە ڕوونەکانی ئێمەیان بەسەردا بخوێنرێتەوە ئەوانەی بێ باوەڕبوون دەڵێن بەو قورئانەی بۆیان دێت ئەمە جادوویەکی ئاشکرا ودیارە');
INSERT INTO "bamoki" VALUES(46,8,'46:8','یان دەڵێن (موحەممەد صلى الله عليه وسلم) خۆی ئەم قورئانەی ھەڵبەستووە (ئەی موحەممەد صلى الله عليه وسلم) بڵێ: ئەگەر خۆم ھەڵم بەستبێت (خوا سزای بەتینم دەدات) ئەوسا ئێوەش لەبەرانبەر خوادا بۆ من ھیچتان لە دەست نایەت، خوا چاك دەزانێت بەو توانجانەی دەیدەن لە قورئان، ئەو خوایە بەسە کەشایەت بێت لە نێوان من وئێوەدا، ھەر ئەوە لێبوردە ومیھرەبان');
INSERT INTO "bamoki" VALUES(46,9,'46:9','بڵێ من یەکەم پێغەمبەر نیم (نێررابم) ناشزانم چی بەسەر من و ئێوەش دەھێنرێت تەنھا پەیڕەوی ئەوە دەکەم کە وەحی دەکرێت بۆم وەمن تەنھا ترسێنەرێکی ئاشکرام وبەس');
INSERT INTO "bamoki" VALUES(46,10,'46:10','(ئەی موحەممەد صلى الله عليه وسلم) بڵێ: پێم بڵێن ئەگەر (ئەم قورئانە) لەلایەن خواوە بێت ئێوەش باوەڕتان پێ نەھێنابێت وە کەسێك لە نەوەی ئیسرائیل شایەتی دابێت لەسەر وێنەی (ئەوەی وا لە) قورئاندا ھەیە جا باوەڕی ھێنابێت، کەچی ئێوە خۆتان بە گەورەزانی، بێگومان خوا ڕێنموونیی گەلی ستەمکاران ناکات');
INSERT INTO "bamoki" VALUES(46,11,'46:11','ئەوانەی بێ باوەڕن دەڵێن دەربارەی ئەوانەی باوەڕیان ھێناوە ئەگەر (ئیسلام و باوەڕ ھێنان) چاك بوایە ئەوانە پێش ئێمە نەدەکەوتن بۆ ئەو (باوەڕ ھێنانە) چونکە خۆیان بەو (قورئانە) ڕێنموون نەبوون، جا دواتر دەڵێن ئەمە دەس ھەڵبەسێکی کۆنە');
INSERT INTO "bamoki" VALUES(46,12,'46:12','وە لەپێش ئەم (قورئانە)دا تەوراتی موسا پێشەوا وڕەحمەت بوو، ئەم قورئانەش (کتێبەکانی پێش خۆی) بەڕاست دادەنێت کە بەزمانی عەرەبیە تاستەمکاران بترسینێت وە بۆ چاکەکاران مژدە بێت');
INSERT INTO "bamoki" VALUES(46,13,'46:13','بەڕاستی ئەوانەی کە دەڵێن پەروەردگارمان (اللە) یە پاشان بەردەوام بوون (لەسەر باوەڕەکەیان) ئەوە نەترسیان لەسەرە ونەخەفەتیش دەخۆن');
INSERT INTO "bamoki" VALUES(46,14,'46:14','ئەوانە ھاوەڵانی بەھەشتن بەھەمیشەیی تێیدا دەمێننەوە لەپاداشتی ئەو کردەوانەی کە دەیانکرد (لەدونیادا)');
INSERT INTO "bamoki" VALUES(46,15,'46:15','وە ئێمە ئامۆژگاری ئادەمیمان کرد بە چاکە کردن لەگەڵ دایك وباوکیدا، دایکی بە ناڕەحەتی وئازارەوە ھەڵی گرتووە وە بەئازار وناڕەحەتیش بوویەتی، ماوەی دووگیان بوون (ی دایك) پێیەوە و لە شیر بڕینەوەی سی مانگە تا دەگاتە تافی لاوی (پێگەیشتوی لاشەیی و ژیری) و دەگاتە چل ساڵی، دەڵێ ئەی پەروەردگار ڕام بھێنە تا شوکرانە بژێری ئەو بەھرە ونیعمەتانەت بکەم کە ڕشتوتە بەسەر من و دایك وباوکمدا وە (ڕام بھێنە) کردەوەی چاك بکەم کە تۆ پێی ڕازی بیت، نەوەکانیشم چاك و صاڵح بکە، بێگومان بۆلای تۆ گەڕاومەتەوە وە بەڕاستی من لەموسوڵمانی گەردن کەچم');
INSERT INTO "bamoki" VALUES(46,16,'46:16','ئەوانە کەسانێکن کە ئێمە کردەوە چاکەکانیان لێ وەردەگرین وە چاوپۆشی دەکەین لە خراپەکانیان لە ڕیزی بەھەشتیاندان، ئەمە ئەو بەڵێنە ڕاستەیە کە بەڵێنیان پێدرابوو لەدونیادا');
INSERT INTO "bamoki" VALUES(46,17,'46:17','ئەو کەسەش کە بەباوك ودایکی بڵێت ئۆف لە دەس ئێوە، ئایا ھەڕەشەم لێ دەکەن کە (زیندوو دەکرێمەوە و لە گۆڕ) دێمە دەرەوە لەکاتێکدا چەند چین خەڵك پێش من ڕۆیشتوون (و زیندوو نەبوونەتەوە) وە (دایکی و باوکی) ھەردووکیان ھانا بۆ خوا دەبەن، (دەڵێن) ھاوار بۆ تۆ باوەڕبھێنە بێگومان بەڵێنی خوا ڕاستە کەچی ئەو دەڵێت ئەمە تەنھا ئەفسانەی پێشینانە وھیچی تر');
INSERT INTO "bamoki" VALUES(46,18,'46:18','ئەوانە ئەو کەسانەن بڕیار(ی خوا)یان بەسەردا چەسپا لە ڕیزی ئەو ئوممەتانەدا کە پێش ئەمان ڕابوردوون لە جنۆکە وئادەمیی بێگومان ئەوانە خۆیان زەرەرمەندن');
INSERT INTO "bamoki" VALUES(46,19,'46:19','وە بۆ ھەریەك لەوانە پلە وپایە ھەیە بە گوێرەی کردەوەکانیان تا خوا پاداشت وسزای کردەوەکانیان بە تەواوی بداتەوە وە ئەوان ھیچ ستەمیان لێ ناکرێت');
INSERT INTO "bamoki" VALUES(46,20,'46:20','وە (بیریان بخەرەوە) ئەو ڕۆژەی ئەوانەی کە بێ باوەڕن بەرانبەر دۆزەخ ڕادەگیرێن (پێیان دەووترێت) ھەرچی خۆشیتان بوو بەسەرتان برد لە ژیانی دونیاتاندا وەلەزەتتان لێ برد و(ڕاتان بوارد) بۆیە ئەمڕۆ تۆڵەتان لێ دەسەنرێت بەسزایەکی ڕیسواکەر، لەبەر ئەوەی کە ئێوە بە ناھەق خۆتان بە زل دەزانی لە زەویدا وە لەبەر ئەوەی لە فەرمانی خوا دەردەچوو بوون');
INSERT INTO "bamoki" VALUES(46,21,'46:21','(ئەی موحەممەد صلى الله عليه وسلم) باسی (ھودی ) برای گەلی عاد بکە کاتێك گەلەکەی ترساند لە (ووڵاتی) ئەحقاف بێگومان پێغەمبەرانی زۆر ڕابوردوون لە پێش ودوای ئەویشەوە (ووتوویانە) جگە لەخوا ھیچی تر نەپەرستن، بێگومان من دەترسم لەسزای ڕۆژێکی گەورە بۆ ئێوە (پێش بێت)');
INSERT INTO "bamoki" VALUES(46,22,'46:22','(گەلەکەی) ووتیان ئایا تۆ (ئەی ھود) ھاتوویت ھەتا لامان بدەیت لە(پەرستنی) پەرستراوەکانمان دەی بۆمان بھێنە ئەو (سزایەی) بەڵێنت پێ داووین ئەگەر لە ڕاست گۆیانیت');
INSERT INTO "bamoki" VALUES(46,23,'46:23','(ھود) ووتی بێگومان زانیاری (ئەو سزا) لای خوایەوە وبەس وەمن تەنھا ئەوەی پێی نێردراوم پێتان ڕادەگەیەنم بەڵام من دەبینم ئێوە گەلێکی نەزان ونەفامن');
INSERT INTO "bamoki" VALUES(46,24,'46:24','جا کاتێك ئەو (سزایە)یان بینی پەڵە ھەورێك بوو بەرەو شیوەکانیان دێت (دڵخۆش بوون) ووتیان ئەمە ھەورێکە بارانی پێیە بۆمان (ھود ووتی نەخێر) بەڵکو ئەوە ئەو (سزایە)یە بەپەلە داواتان دەکرد، بایەکی بەھێزە سزای سەختی پێیە');
INSERT INTO "bamoki" VALUES(46,25,'46:25','بەفەرمانی پەروەردگاری ھەموو شتێك دەڕوخێنێت، ئیتر وایان بەسەرھات تەنھا شوێنی خانووەکانیان دەبینرا ھەر بەو جۆرە سزای ھۆزی تاونباران دەدەین');
INSERT INTO "bamoki" VALUES(46,26,'46:26','سوێند بەخوا ئەوەی بەوان (گەلی عاد) مان دابوو (لەسامان ودەسەڵات) بە ئێوەمان نەدابوو وە گوێ وچاو ودڵمان پێدابوون، بەڵام سوود وکەڵکی پێ نەگەیاندن نە گوێ و نە چاو و نە دڵیان، ھیچ شتێك، چونکە ئەوان باوەڕیان بەنیشانەکانی خوا نەبوو وە دەوری دان ئەو سزای گاڵتەیان پێ دەکرد');
INSERT INTO "bamoki" VALUES(46,27,'46:27','وە بێگومان لەناومان برد (دانیشتووانی) ئەو شار ودێھاتانەی کە بەدەور وبەرتان دابوون وە بەڵگەو نیشانەی جۆراو جۆرمان نیشان دان تا (لەبێ باوەڕی) بگەڕێنەوە');
INSERT INTO "bamoki" VALUES(46,28,'46:28','دەی بۆچی یارمەتی نەدان ئەوانەی جگە لەخوا بۆ نزیك بوونەوە لەخوا کردبوونیانە پەرستراوی (خۆیان) (نەك یارمەتیان نەدان) بـەڵـکو گومیش بوون لێیان، ئەوە بوو (بەرھەمی پەرستراوە) درۆکەیان و ئەو درۆیانەی ھەڵیان دەبەستن');
INSERT INTO "bamoki" VALUES(46,29,'46:29','وە (بیریان خەرەوە) کاتێك چەند جنۆکەیەکمان نارد بۆ لات تا گوێ لەقورئان بگرن جا کاتێك ئامادەی گوێ گرتن لە قورئان بوون ووتیان (بەیەکتری) بێ دەنگ بن (و گوێ بگرن) جا کە (قورئان خوێندنەکە) تەواو بوو ھەموویان گەڕانەوە بۆلای قەوم وھۆزەکەیان بەترسێنەر');
INSERT INTO "bamoki" VALUES(46,30,'46:30','ووتیان ئەی ھۆز وقەومەکەمان بەڕاستی ئیمە گوێ بیستی کتێبێك بووین کە لەپاش موسا نێردراوەتە خوارەوە پەڕاوەکانی پێش خۆی بەڕاست دادەنێت (خەڵك) ڕێنموونی دەکات بۆ ھەق وڕێبازی ڕاستی');
INSERT INTO "bamoki" VALUES(46,31,'46:31','ئەی قەومەکەمان وەڵامی بانگخوازی خوا (موحەممەدصلى الله عليه وسلم)بدەنەوە وباوەڕی پێ بهێنن (خوا) لە تاوانەکانتان خۆش دەبێت ودەیسڕێتەوە وە لەسزای سەخت دەتان پارێزێت');
INSERT INTO "bamoki" VALUES(46,32,'46:32','ھەرکەسیش وەڵامی بانگخوازی خوا (کە موحەممەدە صلى الله عليه وسلم) نەداتەوە، ئەو بەھیچ جۆرێك ناتوانێ لە زەویدا لە دەست خوا دەرچێ وخۆی قوتار کات وە جگە لەخوا یارمەتیدەر وپشتیوانێکی نیە، ئا ئەوانە لە گومڕاییەکی ئاشکرادان');
INSERT INTO "bamoki" VALUES(46,33,'46:33','ئایا ئەوانە نەیان زانیوە بەڕاستی ئەو خوایەی ئاسمانەکان وزەوی دروستکردووە وە لەدروستکردنیاندا ناتوانا ودەستە پاچە نەبووە، وە بەتوانایە مردووانیش زیندوو بکاتەوە، بەڵێ بێگومان خوا بەسەر ھەموو شتێکدا بەتوانایە');
INSERT INTO "bamoki" VALUES(46,34,'46:34','وە ڕۆژێك بێ باوەڕان بەرانبەر دۆزەخ ڕادەگیرێن (پێیان دەڵێن) ئایا ئەمە ڕاست نیە؟ دەڵێن: بەڵێ سوێند بەپەروەردگارمان ڕاستە، (خوا) دەفەرموێت: دەی ئەو سزایە بچەژن چونکە ئێوە باوەڕتان پێ نەدەکرد');
INSERT INTO "bamoki" VALUES(46,35,'46:35','کەواتە خۆگربە (ئەی موحەممەد صلى الله عليه وسلم) ھەروەکو پێغەمبەرانی خۆڕاگری بەھێز دانیان بەخۆداگرت وە پەلە مەکە بۆ (سزادان)یان ئەو ڕۆژەی کە (سزای) بەڵێن پێدراویان دەبینن وا دەزانن تەنھا ساتێك لەرۆژ لە دونیادا ماونەتەوە، (ئەم قورئانە) ئاگادارییە، جا ئایا کەس لەناو دەچێت جگە لە گەلی لەسنوور دەرچوو نەبێت');
INSERT INTO "bamoki" VALUES(47,1,'47:1','ئەو کەسانەی بێ باوەڕ بوون وە ڕێگری (خەڵک)یان کرد لەڕێبازی خوا، (خوا) کردەوەکانیان پوچەڵ دەکاتەوە');
INSERT INTO "bamoki" VALUES(47,2,'47:2','ئەوانەش باوەڕیان ھێناوە وکردەوە چاکەکانیان کردو باوەڕیشیان ھێنا بەو (قورئانە)ی کە نێردراوەتە خوارەوە بۆ موحەممەد (صلى الله عليه وسلم) تەنھا ئەویش ڕاستەو لەلایەن پەروەردگاریانەوە ھاتووە، خوا لە گوناھەکانیان خۆش بووە، دڵ وھۆشیانی ڕێك وچاك کردوە');
INSERT INTO "bamoki" VALUES(47,3,'47:3','ئەوەش لەبەر ئەوەیە بەڕاستی ئەوانەی بێ باوەڕ بوون شوێن بەتاڵ و پڕو پووچ کەوتن، وەبێگومان ئەوانەش ئیمانیان ھێنا بوو شوێن ئەو ھەقە کەوتبوون کە لەلایەن پەروەردگاریانەوە ھاتووە، ئابەو شێوە خوا نموونەی (کردەوەی) خۆیانیان بۆ دەھێنێتەوە');
INSERT INTO "bamoki" VALUES(47,4,'47:4','ئەمجا ئەگەر (لە جەنگدا) گەشتن بەوانەی بێ باوەڕن ئەوە بدەن لە گەردنیان وبیانکوژن ھەتا کاتێك کوشتاری زۆریان لێ دەکەن ئەمجا (دیلەکانیان) قایم ببەستەنەوە جا دواتر یان بەمنەت وچاکە (بەرەڵایان کەن) یان بە فیدیە وبریتی وەرگرتن، ھەتا جەنگ چەکەکان (بە بێ باوەڕان) دادەنێت (یان تا جەنگ کۆتایی دێت بە نەمانی بت پەرستان)، فەرمان ئەوەیە ئەگەر خوا بیوویستایە خۆی تۆڵەی لێ دەسەندن بەڵام (فەرمانی پێدان بە جەنگ) تا بەیەکتری تاقیتان بکاتەوە، ئەو کەسانەش لە پێناو ڕێبازی خوادا کوژران ئەوە خوا ھەرگیز (پاداشتی) کردەوەکانیان وون ناکات');
INSERT INTO "bamoki" VALUES(47,5,'47:5','بێگومان ڕێنموونیان دەکات ودڵ وبیریان پاك ساف دەکات');
INSERT INTO "bamoki" VALUES(47,6,'47:6','وە دەیانخاتە ئەوبەھەشتەی بۆی باسکردون');
INSERT INTO "bamoki" VALUES(47,7,'47:7','ئەی ئەو کەسانەی باوەڕتان ھێناوە ئەگەر ئێوە یارمەتی (ئاینی) خوا بدەن خواش ئێوە زاڵ وسەرکەوتوو دەکات (بەسەر کافراندا) وە پێیەکانتان جێگیر دەکات (لە جەنگدا خۆڕاگرتان دەکات)');
INSERT INTO "bamoki" VALUES(47,8,'47:8','ئەوانەش کە بێ باوەڕبوون ئەوە نەگبەتی وسەرشۆڕی بەشیانە وە (خوا) کردەوەکانیان پووچ و بێ مایە دەکات');
INSERT INTO "bamoki" VALUES(47,9,'47:9','ئەو تۆڵەیان لەبەر ئەوەیە کە بێگومان ئەوانە ڕقیان لەو قورئانە بوو کە خوا ناردوویەتیە خوارەوە، بۆیە خواش کردەوەکانیانی پووچ کردنەوە');
INSERT INTO "bamoki" VALUES(47,10,'47:10','ئاخۆ ئەوانە بە زەویدا نەگەڕاون تا ببینن ئەوانەی پێش خۆیان سەرەنجامیان چۆن بووە, خوا لە ناوی بردن (دەی بێگومان بن) بۆ بێ باوەڕانیش سەرئەنجامی ئاوا ھەیە');
INSERT INTO "bamoki" VALUES(47,11,'47:11','چونکە بێگومان خوا پشتیوانی ئەوانەیە کـە بـاوەڕیان ھێناوە وە بێگومان بێ باوەڕانیش ھیچ پشتیوانێکیان نیة');
INSERT INTO "bamoki" VALUES(47,12,'47:12','بێگومان خوا ئەوانەی باوەڕیان ھێناوە وکردەوەی باشیان کردوە دەیانخاتە باخاتی بەھەشتەوە کە ڕووبارەکان بەژێریاندا دەڕوات, ئەوانەش بێ باوەڕ بوون (لە دونیادا) رادەبوێرن و وەك ئاژەڵ دەخۆن ودەلەوەڕێن وە دۆزەخ شوێنی مانەوەیانە');
INSERT INTO "bamoki" VALUES(47,13,'47:13','وە(خەڵکی) زۆر شار ھەبوون کە بەھێزتر بوون لەو(خەڵکی) مەککەیە کە تۆیان دەرکرد لە ناومان بردن وھیچ پشتیوانێکیان نەبوو');
INSERT INTO "bamoki" VALUES(47,14,'47:14','ئا خۆ ئەو کەسەی بەڵگەی ڕوونی لە پەروەردگاریەوە بۆ ھاتبێت (کە قورئانە) وەك ئەو کەسة وایە کە کرداری خراپی بۆ جوان کرابێ و ڕازێنرابێتەوە, شوێن ھەواو ئارەزووی خۆیان کەوتبن');
INSERT INTO "bamoki" VALUES(47,15,'47:15','شێوەی نایابی ئەو بەھەشتە وایە کە بەڵێن دراوە بە پارێزکاران وخواناسان چەندەھا ڕووباری تێدایە لە ئاوی سازگاری تام وبۆن نەگۆڕاو وە چەندەھا ڕووبار لەشیر کە تامی نەگۆڕاوە وە چەندەھا ڕووبار لەشەرابی نایابی بەلەزەت وخۆش بۆ بنۆشانی وە چەند ڕووباریشی تێدایە لەھەنگوینی پاڵفتە وە لەوێ ھەموو جۆرە میوەھاتێکیان بۆ ئامادەیە، لەگەڵ لێبوردن لە پەروەردگاریانەوە (ئایا ئەو بەھەشتیانە) وەکو کەسانێك وان کە لە ئاگری دۆزەخ دابن بەھەمیشەیی ئاوی کوڵا وکوڵیان دەرخوارد بدرێت کە ڕیخۆڵەکانیان پارچە پارچە بکات');
INSERT INTO "bamoki" VALUES(47,16,'47:16','(ئەی موحەممەد صلى الله عليه وسلم) ھەندێ لەوانە گوێت لێدەگرن بەڵام کاتێك لای تۆ دەردەچن دەڵێن بەوانەی زانیاریان پێدراوە (لە ھاوەڵان) ئەوە ئێستا موحەممەد (صلى الله عليه وسلم) ووتی چی؟ ئەوانە ئەو کەسانەن کە خوا مۆری ناوە بەسەر دڵیاندا وە شوێن ھەوا وئارەزووی خۆیان کەوتوون');
INSERT INTO "bamoki" VALUES(47,17,'47:17','ئەو کەسانەی ھیدایەتیان وەرگرتووە (خوا) شارەزاییان زیاد دەکات وە تەقوا وپارێزکاری وخواناسیان پێ دەبەخشێت');
INSERT INTO "bamoki" VALUES(47,18,'47:18','دەی ئایا ئەو (ناپاکانە) چاوەڕوانی چی دەکەی جگە لە قیامەت کە کتوپڕ یەخەیان بگرێت دەی بێگومان ھەندێك لەنیشانەکانی ھاتۆتە دی، کاتێك (ڕۆژی دوایی) یەخەیان بگرێت ئیتر کەی بیردەکەنەوە');
INSERT INTO "bamoki" VALUES(47,19,'47:19','کەواتە دڵنیابە وبزانە بێجگە لە (اللە) ھیچ پەرستراوێکی بەھەق و ڕاست نیە وە داوای لێبوردن بکە (لەخوا) بۆ گوناھی خۆت وە بۆ (گوناھی) پیاوان وئافرەتانی ئیماندار وە بێگومان خوا دەزانێت بە جم وجوڵتان (لە ڕۆژدا) وئارام گرتنتان (لە شەودا)');
INSERT INTO "bamoki" VALUES(47,20,'47:20','ئەوانەی باوەڕیان ھێناوە دەڵێن بۆچی سورەتێك (لەقورئان) نانێردرێتە خوارەوە؟ (دەربارەی جەنگ وجیھاد) کەچی کاتێك سورەتێکی ڕوون ودامەزراو نێرابێتە خوارەوە وتیایدا باسی جەنگ کرا بێت، دەبینی ئەو کەسانەی کە لەدڵیاندا نەخۆشی (دوو ڕوویی) ھەیە، تەماشای تۆ دەکەن وەك تەماشا کردنی کەسێك کە لە ترسی مردن لە ھۆشی خۆی چووبێت بۆ ئەوان واچاکتر بوو');
INSERT INTO "bamoki" VALUES(47,21,'47:21','فەرمان بەردار بوونایە وقسەی جوان وچاکیان بکردایە ھەر کاتێکیش فەرمانی (جەنگ) درا (جێ بەجێی ناکەن) خۆ ئەگەر لەگەڵ خوا ڕاستیان بکردایە ئەوە باشتر بوو بۆیان');
INSERT INTO "bamoki" VALUES(47,22,'47:22','جا ئایا ئێوە بەتەمان (ئەی ناپاکان) ئەگەر پشت ھەڵکەن (لە ئیسلام وجیھاد) لە زەویدا ئاژاوە وخراپە بەرپابکەن وە ھەرچی پەیوەندی خزمایەتیە بیبچڕێنن');
INSERT INTO "bamoki" VALUES(47,23,'47:23','ئا ئەوانە ئەو کەسانەن کە خوا نەفرەتی لێكردوون وە کەڕی کردوون وچاویانی کوێر کردووە');
INSERT INTO "bamoki" VALUES(47,24,'47:24','ئاخۆ ئەوانە وورد نابنەوە لە قورئان (و تێی ناگەن)؟! (وانیە) بەڵکو دڵیان قوفلـَی لێدراوە');
INSERT INTO "bamoki" VALUES(47,25,'47:25','بێگومان ئەو کەسانەی پاشە و پاش گەڕانەوە لە ئایینەکەیان له پاش ئەوەی ھەقیان بۆ ڕوون بوویەوە شەیتان خراپەکانیانی بۆ جوان وڕازاوە کردبوون وە بەھیوا وئومێد فریوی دابوون');
INSERT INTO "bamoki" VALUES(47,26,'47:26','چونکە ئەوان دەیانووت بەو کەسانەی کەڕقیان بوو لەو قورئانەی خوا ناردبووی، ئێمە لە مەولا لەھەندێ کاردا گوێڕایەڵیتان دەکەین وە خوا بە نھێنیەکانیان دەزانێت');
INSERT INTO "bamoki" VALUES(47,27,'47:27','ئاخۆ چۆن بێ (حاڵی ئەو ناپاکانە) کاتێك فریشتەکان گیانیان دەکێشن و لە ڕوخسار وپاشیان دەدەن');
INSERT INTO "bamoki" VALUES(47,28,'47:28','ئەو (جۆرە گیان کێشانە) لەبەر ئەوەیە کە بێگومان ئەوانە شوێن شتانێك کەوتبوون کە خوای توڕە کردبوو، ڕەزامەندی خواشیان پێناخۆش بوو، بۆیە خواش کردەوەکانیانی پووچەڵ کردەوە');
INSERT INTO "bamoki" VALUES(47,29,'47:29','ئایا وا دەزانن ئەوانەی کە نەخۆشی (ناپاکی) لە دڵیان دایە کە خوا ھەرگیز ڕق وکینەیان دەرناخات وئاشکرای ناکات');
INSERT INTO "bamoki" VALUES(47,30,'47:30','ئەگەر بمانویستایە ئەوانەمان نیشان دەدایت (کە دووڕوون) ئەوسا ھەر بەشێوە ودیمەنیاندا دەتناسین وە بێگومان بە شێوازی قسەشیاندا دەیانناسیت وە دڵنیابن خوا بە ھەموو کردەوەکانتان دەزانێت');
INSERT INTO "bamoki" VALUES(47,31,'47:31','سوێند بەخوا تاقیتان دەکەینەوە تا دەری بخەین ودیاربێت موجاھیدان وخۆڕاگرانتان وە تا ھەواڵتان چیە ئاشکرای کەین');
INSERT INTO "bamoki" VALUES(47,32,'47:32','بێگومان کەسانێ کە بێ باوەڕبوون وەڕێگری (خەڵك)یان دەکرد لە ڕێبازی خوا دژایەتی پێغەمبەریشیان دەکرد پاش ئەوەی ھەق وڕاستیان بۆ ڕوون بۆتەوە، ھیچ زیانێك بەخوا ناگەیەنن وە بە زوویی (خوا پیلان) وکردەوەکـانیـان پـووچەڵ دەکـاتەوە');
INSERT INTO "bamoki" VALUES(47,33,'47:33','ئەی ئەوکەسانەی کەباوەڕتان ھێناوە فەرمانبەردار وگوێڕایەڵی خوا وپێغەمبەربن وە کردەوە (چاکە)کانتان ھەڵمەوەشێننەوە');
INSERT INTO "bamoki" VALUES(47,34,'47:34','بێگومان ئەوانەی کە بێ باوەڕبوون وەڕێگری (خەڵک)یان دەکرد لە ڕێبازی خوا، پاشان بەبێ باوەڕی مردن ئەوە ھەرگیز خوا لێیان خۆش نابێت');
INSERT INTO "bamoki" VALUES(47,35,'47:35','جا (باوەڕداران) لاواز وسست مەبن وداوای ئاشتی (و شەڕ وەستان) مەکەن چونکە ھەر ئێوە زاڵ وبەھێزترن، (یارمەتی) خواش لەگەڵ ئێوەدایە وە (پاداشتی) کردەوەکانتان کەم ناکاتەوە');
INSERT INTO "bamoki" VALUES(47,36,'47:36','بێگومان ژیانی دونیا تەنھا گاڵتە وگەپێکە (ودەبڕێتەوە) خۆ ئەگەر باوەڕ بھێنن ولەخواترس بن (خوا) پاداشتی خۆتان پێ دەدات وە داوای بەخشینی ھەموو دارایی وماڵتان لێ ناکات');
INSERT INTO "bamoki" VALUES(47,37,'47:37','ئەگەر (خوا) داوای بەخشینی ھەموو سامانتان لێ بکات، زۆریش داکۆکی بکات لێتان، ئێوە چرووکی وڕەزیلی دەکەن و ڕق وکینەی دەروونتان دەردەخات');
INSERT INTO "bamoki" VALUES(47,38,'47:38','بێداربن ئێوە ئەو کەسانەن کە بانگ دەکرێن تا لە پێناوی خوا ماڵ ببەخشن کەچی ھەندێکتان ڕژدی دەکەن، ھەر کەسیش ڕژدیی و ڕەزیلی بکات ئەوە بێگومان تەنھا ڕژدیی لە (دژی) خۆی دەکات، چونکە خوا (دەوڵەمەندە) و بێ نیازە، ھەر ئێوەن ھەژار و بێ نەوا، جا ئەگەر ئێوە پشت ھەڵکەن (لەئاینەکەتان) (خوای گەورە) ئێوە دەگۆڕێت بەکەسانێکی تری(زۆر چاك) پاشان (ئەوان) وەکو ئێوە نابن (ڕاستگۆن لەگەڵ پەروەردگاریاندا)');
INSERT INTO "bamoki" VALUES(48,1,'48:1','(ئەی موحەممەد صلى الله عليه وسلم) بێگومان ئێمە سەرکەوتنێکی ئاشکرامان پێ دایت (کەڕێکەوتنی حودەیبیەیە)');
INSERT INTO "bamoki" VALUES(48,2,'48:2','تا خوا خۆش ببێت لە گوناھەکانی ڕابوردو وداھاتووت وەنیعمەتی خۆی بەتەواوی بڕژێت بەسەرتدا وەرێگای ڕاستت نیشان بدات (بەردەوامت کات)');
INSERT INTO "bamoki" VALUES(48,3,'48:3','وەخوا سەرت بخات بەسەرکەوتنێکی باڵادەستانە');
INSERT INTO "bamoki" VALUES(48,4,'48:4','ئەو خوا زاتێکە ئارامی دابەزاندووەتە ناو دڵی ئیمانداران تا ئیمانیان زیاد بکات لەگەڵ ئەو ئیمانەی کە ھەیانە، تەنھا ھی خوایە سەربازانی ئاسمانەکان وزەوی وە ھەمیشە خوا زانا وکاردروستە');
INSERT INTO "bamoki" VALUES(48,5,'48:5','تا پیاوان وئافرەتانی باوەڕدار بباتە بەھەشتانێك کە ڕوبارەکان بەژێریاندا دەڕوات بەھەمیشەیی تیایدا دەمێننەوە وەتا لەگوناھەکانیان خۆش ببێت وە ئەم (بەڵێنە) لای خوا سەرکەوتنێکی گەورەیە');
INSERT INTO "bamoki" VALUES(48,6,'48:6','وە تا سزای بدات پیاوان وئافرەتانی (دوو ڕوو) وناپاك وپیاوان وئافرەتانی ھاوبەش پەیداکەری گومان بەرانی خراپ دەربارەی خوا، ئەوخراپە(لەناو چوونەی چاوەڕوانی دەکەن بۆ ئیمانداران) ھەر بەسەر خۆیاندا دێت وەخوا خەشمی لێ گرتوون ونەفرەتی لێ کردوون و دۆزەخی بۆ ئامادە کردوون، ئای چ سەرئەنجامێکی خراپە');
INSERT INTO "bamoki" VALUES(48,7,'48:7','تەنھا ھی خوایە سەربازانی ئاسمانەکان وزەوی وەخوا ھەمیشە باڵادەستی کاربەجێیە');
INSERT INTO "bamoki" VALUES(48,8,'48:8','بەڕاستی (ئەی موحەممەد صلى الله عليه وسلم) تۆمان ناردووە بەشایەت ومژدەدەر وترسێنەر');
INSERT INTO "bamoki" VALUES(48,9,'48:9','بۆ ئەوەی باوەڕ بە خوا وپێغەمبەرەکەی بھێنن وە پشتیوانی لێ بکەن وڕێزی لێبگرن وە تەسبیحاتی خوا بکەن بە بەیانیان وئێواراندا');
INSERT INTO "bamoki" VALUES(48,10,'48:10','بەڕاستی ئەوکەسانەی پەیمان دەدەن بەتۆ (ئەی موحەممەد صلى الله عليه وسلم) بێگومان ئەوانە پەیمان بەخوا دەدەن، دەستی خوا لەسەر دەستی ئەوانەوەیە (لەم ئایەتە پیرۆزەدا جێگیرکردن وباسکردنی سیفەتی دەست بۆ خوا ھاتووە ئێمەش بڕوامان پێیەتی بەبێ ھیچ لێچواندن ولەیەکدانەوەیەك، وەشێوەکەی نازانین تەنھا خوا خۆی دەیزانێت) جا ھەرکەس پەیمانەکەی شکاند ئەوە بێگومان ئەو پەیمان شکاندنە تەنھا (زیانی) بۆ خۆیەتی ھەر کەسیش وەفا بکات بەو پەیمانەی کەداوێتی بەخوا ئەوە بەڕاستی خوا پاداشتی گەورەیی دەداتێ');
INSERT INTO "bamoki" VALUES(48,11,'48:11','بێگومان پێت دەڵێن ئەو عەرەبە دەشتەکیانەی کەلە غەزا جێ مابوون، سامان وخاوخێزانمان مەشغوڵیان کردین (نەیان ھێشت بێین بۆ غەزا) لەبەر ئەوە داوای لێ خۆشبوونمان بۆ بکە، ئەوەی بەزمانیان دەیڵێن لەدڵیاندا نیە (وباوەڕیان پێی نیە) بڵێ جا کێ لەبەرانبەر خوادا ھیچی بەدەستە بۆتان ئەگەر خوا زیانێکی بۆتان بوێت یان سوودو قازانجێکی بۆتان بوێت (وانیە کە دەیڵێن) بەڵکو خوا ئاگادارە بەھەرچییەك کە دەیکەن');
INSERT INTO "bamoki" VALUES(48,12,'48:12','بەڵکو وا گومانتان دەبرد کە پێغەمبەر وئیمانداران ھەرگیز ناگەڕێنەوە بۆ ناو کەس وکاری خۆیان (و شەھید دەبن) ئەو گومانە لەدڵتاندا ڕازێنرابوویەوە وە گومانی خراپتان دەبرد وبەڕاستی ئێوە گەلێکن لەناو دەچن');
INSERT INTO "bamoki" VALUES(48,13,'48:13','وە ھەر کەس باوەڕی نەھێنابێت بەخوا وپێغەمبەرەکەی ئەوە بەڕاستی ئێمە ئامادەمان کردووە بۆ ئەو بێ باوەرانە ئاگرێکی ھەڵگیرساو');
INSERT INTO "bamoki" VALUES(48,14,'48:14','ئاسمانەکان وزەوی تەنھا موڵکی خوایە، لەھەر کەس کە بیەوێت خۆش دەبێت، سزای ھەرکەسیش دەدات کە بیەوێت (لەتاوانباران) وەخوا لێبوردەی میھرەبانە');
INSERT INTO "bamoki" VALUES(48,15,'48:15','بێگومان بەجێماوان لەغەزا دەڵێن ھەرکاتێك (لەمەدینە) دەرچوون بۆ گرتنی دەسکەوتەکان (غەنیمەت) لێمان گەڕێن باشوێنتان بکەوین، دەیانەوێت ووتەی خوا (کە دەسکەوتەکانی خەیبەر بۆ بەشدارانی حودەیبیەیە) بگۆرن، (ئەی موحەممەد صلى الله عليه وسلم) بڵێ ھەرگیز نابێت شوێنمان کەون چونکە پێشتر خوا دەربارەتان ھەروای فەرمووە، ئەمجا دواتر دەڵێن (وا نیە) بەڵکو حەسوودی بە ئێمە دەبەن (وا نیە) بەڵکو ئەوان زۆر کەم تێدەگەن');
INSERT INTO "bamoki" VALUES(48,16,'48:16','بڵێ بەجێماوان لەغەزا لە عەرەبە دەشتەکیەكان لەمەولا بانگ دەکرێن بۆ (جەنگ لەگەڵ) گەلێکی زۆر بەھێزی جەنگاوەر (دەبێ) جەنگیان لەگەڵ بکەن یان خۆیان موسڵمان ببن، جا ئەگەر گوێرایەڵ بن خوا پێیان دەدات پاداشتێکی زۆرباش وە ئەگەر سەرپێچی بکەن ھەروەك لەوەو پێش سەرپێچیتان کرد (لە حودەیبیەدا)، (خوا) سزایەکی سەختان دەدات');
INSERT INTO "bamoki" VALUES(48,17,'48:17','بۆ کوێر گوناە نیە (بەشداری جیھاد نەکات) بۆ شەلیش گوناە نیە ھەروەھا بۆ نەخۆشیش گوناە نیە وە ھەر کەس گوێرایەڵی خوا و پێغەمبەرەکەی بێت دەیباتە بەھەشتانێك کەڕوبارەکان بەژێریاندا دێن ودەچن ھەر کەسیش سەرپێچی بکات، (خوا) سزای سەختی دەدات');
INSERT INTO "bamoki" VALUES(48,18,'48:18','سوێند بەخوا بێگومان خوا ڕازی بوو لەو ئیماندارانەی کە پەیمانیان پێدایت لەژێر درەختەکەدا (لە حودەیبیە)، جا خوا ئاگادار بوو بەوەی لە دڵیاندایە، بۆیە دڵنیایی وئارامی بۆ ناردن وە پاداشتی دانەوە بەسەرکەوتنێکی نزیك (کەئازاد کردنی خەیبەر بوو)');
INSERT INTO "bamoki" VALUES(48,19,'48:19','وە غەنیمەتی زۆر کەبەدەستی دەھێنن وە خوا ھەمیشە زاڵ وباڵادەست وکاربەجێیە');
INSERT INTO "bamoki" VALUES(48,20,'48:20','خوا بەڵێنی پێ داوون کەغەنیمەتی زۆر (لە بێ باوەران) دەگرن بۆیە ئەم (غەنیمەتەی خەیبەر)ی بۆ پێش خستن وە دەستی خەڵکی (مەککەی) لێتان کۆتا کرد تا (ئەم ڕووداوە) ببێتە بەڵگەیەك بۆ باوەڕداران وەتا ڕێنموونیتان بکات بۆ ڕێگای ڕاست');
INSERT INTO "bamoki" VALUES(48,21,'48:21','وە چەندەھا (غەنیمەتی)تر ھەیە کەھێشتا دەسەڵاتتان نیە بەسەریدا، بێگومان خوا ئاگای لەھەموویانە وەخوا ھەمیشە بەسەر ھەموو شتێکدا بەدەسەڵات وبـەتوانایە');
INSERT INTO "bamoki" VALUES(48,22,'48:22','ئەگەر ئەوانەی کافرن جەنگیان لەدژتان بکردایە بێگومان (تێك دەشکان و) ھەڵدەھاتن پاشان دەستیان نەدەکەوت ھیچ دۆست وپشتیوانێك');
INSERT INTO "bamoki" VALUES(48,23,'48:23','ئەوە یاسا وباوی خوایە کە بێگومان پێشتریش ھەروا بووە قەت نابینیت باو ویاسای خوا ھیچ گۆڕانێکی بەسەردا بێت');
INSERT INTO "bamoki" VALUES(48,24,'48:24','خوا ئەو زاتەیە کەدەستی ئەوانی (کافران) لەئێوە کۆتا کرد، دەستی ئێوەشی لەوان (کۆتاکرد بەڕێکەوتنی حودەیبیە) لەناوجەرگەی مەککەدا لەپاش ئەوەی ئێوەی بەسەر ئەواندا زاڵ کرد وەخوا بەھەرچی ئێوە دەیکەن بینایە');
INSERT INTO "bamoki" VALUES(48,25,'48:25','ھەر ئەوان (مەککەیەکان) بوون کافر بوون وە ڕێگری ئێوەیان کرد (لەزیارەتی) مزگەوتی حەرام ( کەعبەی پیرۆز) وە(نەیانھێشت) قوربانیە وەستێنراوەکان (لەحودەیبیەدا) بگاتە شوێنی سەربڕینی خۆی (خوا زاڵی دەکردن بەسەر مەککەدا لەو ساڵەدا) ئەگەر پیاو وئافرتانی ئیماندار نەبوایە (لەناو کافرانی مەککەدا) کەئێوە نەتان دەناسین، پێتان پێدادەنان (دەتان کوشتن) جا بەھۆیانەوە تووشی تاوان دەبوون بێ ئەوەی بزانن، تاخوا بیخاتە بەر بەزەیی خۆی ئەو کەسەی کە خۆی دەیەوێت، بەڵام ئەگەر (ئیمانداران وکافران لەمەککەدا) لەیەکتر جیاببوونایەتەوە، بێگومان بێ باوەڕەکانیانمان سزادەدا بەسزادانێکی سەخت');
INSERT INTO "bamoki" VALUES(48,26,'48:26','کاتێك ئەوانەی بێ باوەڕن دەمار گیرییان خستە دڵیانەوە کەدەمارگیری نەفامیە، جا خواش دڵنیایی وئارامی خۆیی ناردە خوارەوە بۆ پێغەمبەر صلى الله عليه وسلم وئیمانداران وەباوەڕدانی پابەند کرد بەووشەی (لا الە الا اللە محمد رسول اللە)وە وەئەوان بەووشـەیە شیاو تر بوون وخاوەنی بوون وە خوا بەھەموو شتێ ئاگادار وزانایە');
INSERT INTO "bamoki" VALUES(48,27,'48:27','سوێند بە خوا بێگومان خوا ئەو خەوەی کە پێغەمبەرەکەی دیبوی بەڕاستی گێڕاو ھێنایەدی کە(ووتبوی) دڵنیابن دەچنە ناو مزگەوتی حەرام ( کەعبەی پیرۆز) بەوویستی خوا بە ئارامی و بێ ترس، سەرتان تاشییوە یان کورتان کردۆتەوە، (لەدوژمن) ناترسن، جا ئەوەی (خوا) دەیزانی ئێوە نەتان دەزانی (کەخێر لەڕێکەوتن دایە) وە جگە لە (چوونە مەککە) بڕیاری داوە سەرکەوتنێکی نزیك (بۆ ئێوەیە کەڕێکەوتنی حودەیبیەیە)');
INSERT INTO "bamoki" VALUES(48,28,'48:28','خوا ئەو زاتەیە کەپێغەمبەرەکەی ناردووە بەڕێنموونی وئایینی ڕاستەوە، تا بەسەر ھەموو ڕێبازێکدا زاڵی بکات تەنھا خوا بەسە کە شاھید بێت لەسەر ئەمە');
INSERT INTO "bamoki" VALUES(48,29,'48:29','موحەممەد صلى الله عليه وسلم پێغەمبەری خوایە ئەو (باوەڕدار)انەش کەلەگەڵیدان بەرامبەر بێ باوەڕان توندو تیژن و لەنێوان خۆیاندا بە بەزەیین، دەیان بینی (ھەمیشە) لە کوڕنوش وسوژدە بردنـدان، داوای فەزڵ وڕەزامەندی خودا دەکەن، نیشانەیان لە ناو چاوانیاندا دیارە بەھۆی زۆر سوژدە بردن (یانەوە)، جا ئەو (ھاوەڵە بەڕێزانە) ئەوە نموونە وصیفەتیان بوو لە تەوراتدا وە نموونە وصیفەتیان لە ئینجیلیشدا وەك ڕووەك وکشتوکاڵێك وایە چەکەرەی دەرکردبێت (سەوز بووبێت) ئەمجا چەکەرەکەی بەھێز کرد بێ و قاییم بوو بێت، جا لەسەر لاسکی خۆی ڕێك وەستابێ (ئەوەندە جوان بێ) جوتیارەکانی سەرسام کرد بێ، تا بە (زۆر بوونی) ھاوەڵانی داخ ومەراق بدا بەکافران, خوا بەڵێنی داوە بەوانەیان کە باوەڕیان ھێناوە وکردەوەی چاکەیان کردووە بە لێبوردن و پاداشتی گەورە (کە بەھەشتە)');
INSERT INTO "bamoki" VALUES(49,1,'49:1','ئەی ئەو کەسانەی کەباوەڕتان ھێناوە پێش مەکەون (لە ھیچ کاروبڕیارێکدا) لەخواو پێغەمبەرەکەی و لەخوا بترسن، بێگومان خوا بیسەر وزانایە');
INSERT INTO "bamoki" VALUES(49,2,'49:2','ئەی ئەو کەسانەی باوەڕتان ھێناوە دەنگتان بەرز مەکەنەوە بەسەر دەنگی پێغەمبەردا صلى الله عليه وسلم وە بەدەنگی بەرز قسەی لەگەڵ مەکەن وەك دەنگ بەرز کردنەوەتان لەگەڵ یەکتریدا، نەوەکو کردەوەکانتان بەتاڵ ببێتەوە وخۆشتان ھەستی پێنەكەن');
INSERT INTO "bamoki" VALUES(49,3,'49:3','بێگومان ئەوانەی دەنگیان نزم دەکەنەوە لەخزمەتی پێغەمبەردا صلى الله عليه وسلم ئەوانە ئەو کەسانەن خوا دڵیانی زاخاو داوە وپاکی کردوەتەوە بۆ تەقوا وخواناسین، لێخۆشبوون وپاداشتی گەورەیان بۆ ھەیە');
INSERT INTO "bamoki" VALUES(49,4,'49:4','بێگومان ئەو کەسانەی لە دەرەوەی ژوورەکان بانگت دەکەن (ئەی موحەممەد صلى الله عليه وسلم) زۆربەیان نەفامن وتێناگەن');
INSERT INTO "bamoki" VALUES(49,5,'49:5','خۆ ئەگەر چاوەڕوانیان بکردایە تادەڕۆشتیتە دەرەوە بۆ لایان، ئەوە چاکتر بوو بۆیان وەخوا لێبوردە ومیھرەبانە');
INSERT INTO "bamoki" VALUES(49,6,'49:6','ئەی ئەوانەی باوەڕتان ھێناوە ئەگەر گوناھکارێ ھەواڵێکی گرنگی ھێنا بۆتان ئەوە لێی بکۆڵنەوە (تاڕوون ببێتەوە) نەوەك زیان بگەیەنن بەخەڵکێك بەنەزانین ئەوسا پەشیمان بن لەوکارەی کەکردووتانە');
INSERT INTO "bamoki" VALUES(49,7,'49:7','زۆر چاك بزانن کە بێگومان پێغەمبەری خوا لە ناوتاندایە ئەگەر بەگوێی ئێوەی بکردایە لەزۆرێك لەکارەکاندا تووشی ناڕەحەتی دەبوون، بەڵام خوا ئیمانی لاتان خۆشەویست کردووە وە لەدڵتاندا (شیرین و) ڕازاوەی کردووە وە بێزراوو ناشرینی کردووە لەلاتان بێ باوەڕیی وتاوان وسەرپێچی فەرمانی خوا، ھەر ئەوانەن شارەزاو لەسەر ڕێبازی ڕاستن');
INSERT INTO "bamoki" VALUES(49,8,'49:8','(ئەوە) بەخشش ونیعمەتێکە لەلایەن خواوە خواش زانا ودانایە');
INSERT INTO "bamoki" VALUES(49,9,'49:9','ئەگەر دوو کۆمەڵ لەئیمانداران دژ بەیەکتر جەنگیان کرد ئەوە لەنێوانیاندا ئاشتەوایی بکەن، ئەمجا ئەگەر کۆمەڵێکیان دەست درێژی وسەرکەشی کرد بۆ سەر کۆمەڵێکی تریان، ئەوە ئێوە (ئەی موسوڵمانان) دژی ئەوەیان بجەنگن دەست درێژی دەکات تا دەگەڕێتەوە بۆ فەرمانی خوا (ملکەچی فەرمانی خوا دەبێت) ئەمجا ئەگەر گەڕایەوە ئەوسا لەنێوانیاندا ئاشتەوایی بکەن، بەدادگەرانە وڕاست ودادپەروەر بن (لەنێوانیاندا) بێگومان خوا دادپەروەرانی خۆش دەوێت');
INSERT INTO "bamoki" VALUES(49,10,'49:10','بێگومان تەنھا باوەڕداران برای یەکترن کەواتە لەنێوان دوو براتاندا (ئاژاوە ھەبوو) ئاشتەوایی بکەن و لەخوا بترسن تاڕەحمتان پێبكات');
INSERT INTO "bamoki" VALUES(49,11,'49:11','ئەی ئەوانەی کەباوەڕتان ھێناوە باھیچ کۆمەڵە پیاوێك گاڵتە نەکەن بەکۆمەڵێکی تریان، ڕەنگە گاڵتە پێ کراوەکان لە گاڵتەکەرەکان باشتربن (لای خوا) وە ژنانێك گاڵتە نەکەن بەژنانێکی تر، ڕەنگە ئافرەتە گاڵتە پێکراوەکان لە ئافرەتە گاڵتەکەرەکان باشتربن (لای خوای گەورە) تانە وتەشەر لەیەکتری مەدەن (لەنێو خۆتاندا) ناوو ناتۆرە لەیەکتری مەنێن، ئای چەند خراپە ناو بردنێ کە گوناھە (بە موسڵمان بڵێیت کافر، یان داوێن پیس) پاش باوەڕ ھێنان جـا ئەوەی پەشیمان نەبێتەوە ئەوە ئەوانە ھەر خۆیان ستەمکارن');
INSERT INTO "bamoki" VALUES(49,12,'49:12','ئەی ئەوکەسانەی باوەڕتان ھێناوە خۆتان لەزۆرێك لە گومانەکان بپارێزن، چونکە بەڕاستی ھەندێك لەگومانەکان تاوانن، سیخوڕی مەکەن (وبەدوای ناتەواوی یەکتردا مەگەڕێن) لەپاشملە بەخراپە باسی یەکتری مەکەن، ئایا کام لەئێوە پێی خۆشە کەگۆشتی برا (باوەڕدارەكەی) بخوات بەمردووی؟ بێگومان قێزتان لێی دێتەوە وپێتان ناخۆشە وە لەخوا بترسن بێگومان خوا تەوبە گیراکەر وبەبەزەییە');
INSERT INTO "bamoki" VALUES(49,13,'49:13','ئەی خەڵکینە بێگومان ئێمە دروستمان کردوون لە نێر و مێیەك وە کردومانن بەچەند گەل وھۆزێکەوە تایەکتری بناسن، بەڕاستی بەڕێزترینتان لای خوا ئەو کەسەتانە زیاتر لەخودا دەترسێت، بێگومان خوا زانا وئاگادارە');
INSERT INTO "bamoki" VALUES(49,14,'49:14','عەرەبە دەشتەکیەکان ووتیان باوەڕمان ھێناوە، (ئەی موحەممەد صلى الله عليه وسلم) بڵێ باوەڕتان نەھێناوە، بەڵکو بڵێن موسڵمان بووین (بەسەر زارەکی) چونکە ھێشتا باوەڕ نەچووەتە ناو دڵەکانتانەوە، خۆ ئەگەر گوێڕایەڵی خوا وپێغەمبەرەکەی بن ھیچ لە (پاداشتی) کردەوەکانتان کەم ناکاتەوە، بەڕاستی خوا لێبوردە ومیھرەبانە');
INSERT INTO "bamoki" VALUES(49,15,'49:15','بێگومان ئیماندارانی ڕاستەقینە ئەوانەن کە باوەڕیان ھێناوە بە خوا وپێغەمبەرەکەی پاشان دوو دڵ نەبوون (لە ئیمانەکەیاندا) وە بەماڵ وگیانیان تێکۆشانیان کردووە لە پێناو ڕێبازی خوادا ھەر ئەوانەن ڕاسگۆیان');
INSERT INTO "bamoki" VALUES(49,16,'49:16','(ئەی موحەممەد صلى الله عليه وسلم) بڵێ: ئایا ئێوە خوا ئاگادار دەکەن لەئایینەکەتان لەکاتێکدا خوا ئاگادارە ودەزانێت بەھەرچی لەئاسمانەکان وزەویدایە وە خوا بەھەموو شتێك زانایە');
INSERT INTO "bamoki" VALUES(49,17,'49:17','ئەوکەسانە منەت دەکەن بەسەر تۆدا (ئەی موحەممەد صلى الله عليه وسلم) کە موسڵمان بوون، تۆش بڵێ منەتی موسڵمان بوونی خۆتان بەسەر مندا مەکەن بەڵکو خوا منەت دەکات بەسەر ئێوەدا کە ڕێنموونیی کردوون بۆ باوەڕھێنان ئەگەر ئێوە ڕاستگۆن (کە ئیماندارن)');
INSERT INTO "bamoki" VALUES(49,18,'49:18','بەڕاستی خوا دەزانێت نھێنی ئاسمانەکان وزەوی وەخوا بینایە بەھەر کردەوەیەك کە ئێوە دەیکەن');
INSERT INTO "bamoki" VALUES(50,1,'50:1','بە (قاف) دەخوێندرێتەوە، ماناکەی تەنھا خوا دەیزانێت بۆ زانیاری زیاتر سەیری سەرەتای سورەتی (البقرة) بکە، سوێند بەقورئانی بەرز وبەڕێز (تۆ نێردراوی خوای ئەی موحەممەد صلى الله عليه وسلم)');
INSERT INTO "bamoki" VALUES(50,2,'50:2','بەڵام (بێ باوەڕان) سەریان سوڕماوە لەوەی کە پێغەمبەرێکی ترسێنەریان بۆ ھاتووە لە خۆیان، جا بێ باوەڕان دەیان ووت ئەمە شتێکی سەیرە');
INSERT INTO "bamoki" VALUES(50,3,'50:3','ئایا کاتێ مردین وبووین بەخۆڵ (چۆن زیندوو دەبینەوە) ئەوە گەڕانەوەیەکی دوورە ونابێ');
INSERT INTO "bamoki" VALUES(50,4,'50:4','بێگومان ئێمە دەزانین زەوی چەندێ لە لاشەیان دەخوات (ھیچیمان لێ وون نابێت) پەڕاوێکیشمان لایە کە پارێزەری ھەموو شتێکە');
INSERT INTO "bamoki" VALUES(50,5,'50:5','(زیندوو بوونەوە دوور نیە) بەڵکو ئەوان باوەڕیان بەقورئان نەکرد کاتێك بۆیان ھات، بۆیە ئەوان لەناو کارێکی تێکەڵ وپێکەڵدان');
INSERT INTO "bamoki" VALUES(50,6,'50:6','دەی ئایا سەرنجی ئەو ئاسمانەیان نەداوە کە لەسەریانەوەیە چۆن دروستمان کردووە وڕازاندومانەتەوە وەھیچ درز وکەلێنێکی تێدا نیە');
INSERT INTO "bamoki" VALUES(50,7,'50:7','زەویشمان ڕاخستووە، چەندەھا شاخی دامەزراومان تێدا داناوە ڕواندومانە تێیـشیدا ھەموو جۆرە ڕووەکێکی جوان');
INSERT INTO "bamoki" VALUES(50,8,'50:8','تابەر چاو ڕوونی وپەند وبیرخەرەوە بێت بۆ ھەموو بەندەیەکی تەوبەکار وگەڕاوە بۆ لای خوا');
INSERT INTO "bamoki" VALUES(50,9,'50:9','وە لەئاسمانەوە باراندوومانە بارانێکی بەپیت وفەڕ جا بەو بارانە ڕوواندومانە باخات ودانەوێڵەی(ئەو کشتوکاڵانەی) کەدروێنە دەکرێن');
INSERT INTO "bamoki" VALUES(50,10,'50:10','ھەروەھا دارخورمای بەرز وبڵند بە ھێشووی تێك پەستراوەوە');
INSERT INTO "bamoki" VALUES(50,11,'50:11','(تابێتە) ڕزق وڕۆزی بۆ بەندەکان ھەر بەو بارانە زەوی مردو (ووشك) دەبوژێنینەوە، زیندوو کردنەوە(ی مردوانیش) ھەر بەو شێوەیە');
INSERT INTO "bamoki" VALUES(50,12,'50:12','پێش ئەمانیش باوەڕیان نەھێنا (بە پێغەمبەرەکانیان) گەلی نووح وخاوەن چاڵ وبیرەکان و(گەلی) سەموود');
INSERT INTO "bamoki" VALUES(50,13,'50:13','وەگەلی عاد وفیرعەون وگەلی لوطیش');
INSERT INTO "bamoki" VALUES(50,14,'50:14','ھەروەھا خاوەن باخاتە چڕو پڕەکان وگەلی توببەعیش ھەر ھەموویان باوەڕیان نەھێنا بەپێغەمبەرەکانیان بۆیە ھەڕەشەی (من لەدژیان) ھاتەدی');
INSERT INTO "bamoki" VALUES(50,15,'50:15','جا ئایا ئێمە لەدروستکردنی یەکەم جاردا دەستەوسان بووین (تانەتوانین زیندووتان بکەینەوە)؟! (نەخێر وانیە) بەڵکو ئەوان لە گوماندان لە دروست بوونەوەیەکی سەر لە نوێ');
INSERT INTO "bamoki" VALUES(50,16,'50:16','سوێند بەخوا ئێمە ئادەمیمان بەدی ھێناوە وچاك ئاگادارین بەو خەیاڵ وخەتەرانەی دێن بەدڵ ودەروونیدا وئێمە لەوەوە نزیکترین (واتە خوای گەورە عیلم وزانینی تەواوی ھەیە بە ھەموو جموجولێکی ئادەمیزاد) لە شادەماری دڵی');
INSERT INTO "bamoki" VALUES(50,17,'50:17','کاتێك ئەو دووفریشتە (کرداری) تۆمار دەکەن کە لەلای ڕاست و لای چەپیەوە دانیشتوون');
INSERT INTO "bamoki" VALUES(50,18,'50:18','ھەر ووتەیەك لەدەمی دەرچێ چاودێرێکی بەسەرەوەیە (و دەینوسێت)');
INSERT INTO "bamoki" VALUES(50,19,'50:19','ناڕەحەتی وبێھۆشی مردن دێت لەگەڵ ڕاستیەکاندا (پێی دەووترێت) ئەمە ئەو (مردنە)یە کە خۆت لێ دەپاراست ولادەدا');
INSERT INTO "bamoki" VALUES(50,20,'50:20','ئەمجا فوو دەکرێ بە (شەیپوڕ) کەڕەنادا ئەوە ڕۆژی (بەدی ھاتنی) ھەڕەشەکانە');
INSERT INTO "bamoki" VALUES(50,21,'50:21','لەگەڵ ھەموو کەسێکدا دێت (بۆ مەحشەر) دوو فریشتە، یەکێکیان پێش خۆیداوە، ئەوی دیکەشیان شاھیدی لەسەر ئەدات، (لەتەواوی ئەو کاروکردەوانەی کە کردوونی لە دونیادا)');
INSERT INTO "bamoki" VALUES(50,22,'50:22','(پێی دەووترێت) سوێند بەخوا بەڕاستی تۆ لەمە بێئاگا بوویت لەدونیادا، جا ئێمە پەردەی (قیامەت) مان لەسەر لادایی، جـا ئەمڕۆ (کە قیامەتە) چاوت تیژە و چاك دەبینێت');
INSERT INTO "bamoki" VALUES(50,23,'50:23','وە ئەو (فریشتە شاھیدەی) لەگەڵیدایە دەڵێت ئەمە (نامەی کردەوەکانە) ئامادەیە و لای منە');
INSERT INTO "bamoki" VALUES(50,24,'50:24','(خوا دەفەرموێت بەدوو فریشتەکە) فڕێی بدەنە ناو دۆزەخەوە ھەموو کافرێکی سەرکەشی');
INSERT INTO "bamoki" VALUES(50,25,'50:25','ڕێگریكەر لەچاکە دەست درێژ کەری گوماناوی');
INSERT INTO "bamoki" VALUES(50,26,'50:26','ئەو کەسەی کەپەرستراوی تری لەگەڵ خوادا داناوە جا ھەر دووکتان (ئەی فریشتەکان) فڕێی بدەنە سزای سەختەوە');
INSERT INTO "bamoki" VALUES(50,27,'50:27','ئەو (شەیتانەی لەدونیادا) لەگەڵیدا بوو دەڵێت ئەی پەروەردگارم من ئەوم سەرکەش نەکرد بەڵکو خۆی لەگومڕاییەكی دوور لەڕاستیدا بوو');
INSERT INTO "bamoki" VALUES(50,28,'50:28','(خوا دەفەرموێت) لەلای مندا دەمەقاڵێ ومشتومڕ مەکەن چونکە بێگومان پێشتر ھەرەشەم (لەم بارەیەوە) ناردووە بۆتان');
INSERT INTO "bamoki" VALUES(50,29,'50:29','بەڵێنی من ھەرگیز گۆڕانی بەسەردا نایەت وە من ھیچ جۆرە ستەمێك لە بەندەکانم ناکەم');
INSERT INTO "bamoki" VALUES(50,30,'50:30','ڕۆژێك دەبێت بەدۆزەخ دەڵێین ئایا پڕ بوویت؟ دۆزەخیش دەڵێ ئایا کەسی تر ھەیە (بیھێنن)؟');
INSERT INTO "bamoki" VALUES(50,31,'50:31','بەھەشتیش (لەو ڕۆژەدا) نزیك کراوەتەوە بۆ پارێزکاران ولەخواترسان دوور نیە (لێیان)');
INSERT INTO "bamoki" VALUES(50,32,'50:32','(پێیان دەوترێت) ئەمە ئەو (بەھەشتە)یە کە بەڵێنتان پێدرابوو بەھەموو ڕوو لەخوایەکی خۆپارێز');
INSERT INTO "bamoki" VALUES(50,33,'50:33','ئەوەی لە خوای میھرەبان ترسابێت لەپەنھاندا ( وخۆی لـەگـوناە پاراست بێت) وە بەدڵێکی ڕوو لە خواوە گەڕابێتەوە (بۆ لامان)');
INSERT INTO "bamoki" VALUES(50,34,'50:34','(پێیان دەووترێت) ھەمووتان بڕۆنە ناو بەھەشتەوە، دڵنیا و بێترس بن (لەسزاو ناخۆشی) ئەم ڕۆژە ڕۆژی ھەمیشەیی ونەمرییە');
INSERT INTO "bamoki" VALUES(50,35,'50:35','لەو بەھەشتەدا ھەرچیان بوێت بۆیان ئامادەیە وە لەوە زیاتریشمان ھەیە (کە بینینی خوای گەورەیە)');
INSERT INTO "bamoki" VALUES(50,36,'50:36','وەپێش ئەمان گەل وچینی زۆرمان لەناوبرد کەئەوان لەمانیش بەھێزتر بوون، خۆ شاران و ووڵاتانیان پشکنیوە وگەڕاوون، ئایا ھیچ پەنا گایەك ھەبوو(بۆیان لەمردن)');
INSERT INTO "bamoki" VALUES(50,37,'50:37','بێگومان لەم (باسەدا) پەندو ئامۆژگاری ھەیە بۆ کەسێك خاوەن دڵێکی (زیندوو بێت یان ژیربێ) یاگوێ بگرێ (بۆ قورئان) وبیریشی لەلای بێت');
INSERT INTO "bamoki" VALUES(50,38,'50:38','سوێند بەخوا بێگومان دروستمان کردووە ئاسمانەکان وزەوی و ھەرچی لە نێوانیاندایە، لە شەش ڕۆژدا وە ھیچ ماندوو بوونێکمان تووش نەبوو');
INSERT INTO "bamoki" VALUES(50,39,'50:39','کەواتە (ئەی موحـەممەد صلى الله عليه وسلم) خۆگـربـە بـەرامبـەر ئـەو (قسە نابەجێیانەی) کەدەیڵێن، بەستایشی پەروەردگارت تەسبیحات بکە پێش ھەڵھاتنی خۆر وپێش ئاوا بوونیشی');
INSERT INTO "bamoki" VALUES(50,40,'50:40','وە لەبەشێك لەشەودا ستایشی پەروەردگارت بکە وە لەپاش نوێژەکانیش');
INSERT INTO "bamoki" VALUES(50,41,'50:41','وە گوێ بگرە لەڕۆژێکدا جاڕ دەرێك لەشوێنێکی نزیکەوە بانگ دەکات');
INSERT INTO "bamoki" VALUES(50,42,'50:42','ڕۆژێك کە نەعرەتەیەك (دەنگێکی گەورەی ترسناك) بەڕاستیی دەبیستن ئەو ڕۆژە ڕۆژی ھاتنەدەرەوەیە (لەگۆرەکان)');
INSERT INTO "bamoki" VALUES(50,43,'50:43','بێگومان ھەر ئێمەین دەژێنین ودەشمرێنین، گەڕانەوەش ھەر بۆلای ئێمەیە');
INSERT INTO "bamoki" VALUES(50,44,'50:44','ڕۆژێك دێت زەوی لەئاست ئەوانەی (مردوون) قڵیش دەبات بەپەلە (دێنە دەرەوە) ئەو کۆکردنەوە بەلای ئێمەوە ئاسانە');
INSERT INTO "bamoki" VALUES(50,45,'50:45','بێگومان ئێمە ئاگادارین بەوەی کەدەیڵێن تۆ دەسەڵاتت بەسەریاندا نیە کەوایە تۆ بەم قورئانە ئامۆژگاری ئەو کەسە بکە کە لەھەڕەشەی من دەترسێ');
INSERT INTO "bamoki" VALUES(51,1,'51:1','سوێند بەو بایانەی کە زۆر شت پرژو بڵاودەکەنەوە بە بڵاوکردنەوە');
INSERT INTO "bamoki" VALUES(51,2,'51:2','ئەمجا سوێند بەو ھەورانەی کە ھەڵگری بارانن');
INSERT INTO "bamoki" VALUES(51,3,'51:3','ئەمجا سوێند بەو کەشتیانەی کە بەئاسانی دەگەڕێن (لە ناو دەریادا)');
INSERT INTO "bamoki" VALUES(51,4,'51:4','ئەمجا سوێند بەو فریشتانەی کار دابەشکەرن');
INSERT INTO "bamoki" VALUES(51,5,'51:5','بێگومان ھەر بەڵێنێك کە پێتان دەدرێ ڕاستە');
INSERT INTO "bamoki" VALUES(51,6,'51:6','وە بەڕاستی (ڕۆژی) پاداشت وتۆڵە ڕوودەدات');
INSERT INTO "bamoki" VALUES(51,7,'51:7','وەسوێند بەئاسمانی خاوەن ڕێگای زۆر (خاوەن جوانی وڕێك وپێکی)');
INSERT INTO "bamoki" VALUES(51,8,'51:8','بێگومان ئێوە لەقسە وگوفتاری جیاوازدان');
INSERT INTO "bamoki" VALUES(51,9,'51:9','ئەو کەسە لادەدرێت لەئیمان کەلادراوە (چونکە خۆی گومڕایە)');
INSERT INTO "bamoki" VALUES(51,10,'51:10','دەک بەکوشت چن ئەو درۆزنانە(و نەفرەتیان لێ بێت)');
INSERT INTO "bamoki" VALUES(51,11,'51:11','ئەوانە کەسانێکن لەگێژاوی نەزانیدا بێ ئاگان (لەھەموو شت)');
INSERT INTO "bamoki" VALUES(51,12,'51:12','(بەگاڵتەوە) دەپرسن کەی ڕۆژی دوایی دێت');
INSERT INTO "bamoki" VALUES(51,13,'51:13','ڕۆژێك (سزا دەدرێن) کەئەوان بەئاگری دۆزەخ دەسوتێنرێن');
INSERT INTO "bamoki" VALUES(51,14,'51:14','(پێیان دەووترێت) سزاکەتان بچەژن، ئەمە ئەو (سزایە) (بەگاڵتەوە) بە پەلە داواتان دەکرد');
INSERT INTO "bamoki" VALUES(51,15,'51:15','بێگومان پارێزکاران و لەخواترسان وان لە باخات وکەناری سەرچەشمەی ڕەواندا');
INSERT INTO "bamoki" VALUES(51,16,'51:16','ئەوەی خوا پێی داوون وەریدەگرن (چونکە) بێگومان ئەوان لەوەو پێش چاکەکار بوون');
INSERT INTO "bamoki" VALUES(51,17,'51:17','ئەوانە کەمێك لەشەودا دەخەوتن (ئەوی تری شەو نوێژیان دەکرد)');
INSERT INTO "bamoki" VALUES(51,18,'51:18','وە لەبەرەبەیاناندا داوای لێخۆشبوونیان دەکرد');
INSERT INTO "bamoki" VALUES(51,19,'51:19','وە لە ماڵ و داراییاندا بۆ (ھەژاری) داواکەر وداوانەکەر مافی خۆیان ھەبوو');
INSERT INTO "bamoki" VALUES(51,20,'51:20','وەلەزەویدا بەڵگە ونیشانە ھەیە بۆ باوەڕداران');
INSERT INTO "bamoki" VALUES(51,21,'51:21','وە لەخودی خۆشتاندا (بەڵگەی زۆر ھەیە) دەی ئایا بۆ وورد نابنەوە وسەرنج نادەن');
INSERT INTO "bamoki" VALUES(51,22,'51:22','وە ڕزق وڕۆزی ئێوە و ئەو بەڵێنانەی پێتان دەدرێت لەئاسمانە');
INSERT INTO "bamoki" VALUES(51,23,'51:23','جا سوێند بەپەوەردگاری ئاسمان وزەوی بێگومان ئەوە(ی باسکرا) ھەق وڕاستە بەوێنەی ئەو قسانەی خۆتان دەیکەن');
INSERT INTO "bamoki" VALUES(51,24,'51:24','ئایا پێت گەشتووە بەسەر ھاتی میوانە ڕێزدارەکانی ئیبراھیم');
INSERT INTO "bamoki" VALUES(51,25,'51:25','کاتێ چوونە لای ئیبراھیم و ووتیان سڵاو (ئەویش) ووتی سڵاو لەخۆتان (ئێوە) کەسانێکی نەناسراوون');
INSERT INTO "bamoki" VALUES(51,26,'51:26','جا خێرا چوو بۆلای خێزانەکەی ئەمجا گوێرەکەیەکی قەڵەوی (بەبرژاوی بۆ) ھێنان');
INSERT INTO "bamoki" VALUES(51,27,'51:27','وەنزیکی کردەوە لێیان (بەڵام نەیان خوارد) ووتی ئەوە ناخۆن؟');
INSERT INTO "bamoki" VALUES(51,28,'51:28','ئەوسا لێیان ترسا ووتیان مەترسە (ئێمە نێردراوی خواین) وە مزگێنیان پێ دا بەکوڕێکی دانا (کەئیسحاقە)');
INSERT INTO "bamoki" VALUES(51,29,'51:29','جاژنەکەی قریوەیەکی کرد وھات وە دای بەڕوومەتی خۆیدا (وەك باووی ژنان) وە ووتی پیرەژنێکی نەزۆك (چۆن منداڵی دەبێت)');
INSERT INTO "bamoki" VALUES(51,30,'51:30','(فریشتەکان) ووتیان پەروەردگارت وای فەرمووە بێگومان ھەر خوا کار بەجێ وزانایە');
INSERT INTO "bamoki" VALUES(51,31,'51:31','(ئیبراھیم) ووتی: چیتان پێ سپێردراوە ئەی نێرراوانی خوا');
INSERT INTO "bamoki" VALUES(51,32,'51:32','ووتیان بێگومان ئێمە نێرراوین بۆ سەر گەلی تاوانباران');
INSERT INTO "bamoki" VALUES(51,33,'51:33','بۆ ئەوەی ببارێنین بە سەریاندا بەرد لە گڵی سورەوە کراوی');
INSERT INTO "bamoki" VALUES(51,34,'51:34','نیشانە کراو لەلایەن پەروەردگارتەوە بۆ لە سنوور دەرچوان');
INSERT INTO "bamoki" VALUES(51,35,'51:35','ئەمجا ھەرچی بوو لەو (شارەی لوط) دا لە بڕواداران دەرمان کردن');
INSERT INTO "bamoki" VALUES(51,36,'51:36','وە نەماندی تێیدا بێجگە لەیەك ماڵی موسڵمان نەبێت (ئەویش ماڵی لوط بوو)');
INSERT INTO "bamoki" VALUES(51,37,'51:37','وەپەند ونیشانەیەکمان تێدا بەجێ ھێشت بۆ ئەوانەی کە لە سزای بە ئازار دەترسن');
INSERT INTO "bamoki" VALUES(51,38,'51:38','ھەروەھا لەبەسەر ھاتی موسادا (چەند بەڵگە وپەند ھەیە) کاتێك کەناردمان بۆ لای فیرعەون بە بەڵگەی ڕوون وئاشکراوە');
INSERT INTO "bamoki" VALUES(51,39,'51:39','فیرعەونیش ڕوی وەرگێڕا وسەرپێچی کرد بەھۆی دەسەڵاتەکەیەوە وە ووتی: (موسا) جادووگـەرە یان شێتە');
INSERT INTO "bamoki" VALUES(51,40,'51:40','ئێمەش خۆی ولەشکرەکەیمان گرت وفڕێمان دانە ناو دەریاوە کە ئەو شیاوی لۆمە کردن بوو');
INSERT INTO "bamoki" VALUES(51,41,'51:41','وە لەگەلی عاددا بەڵگەو پەند ھەیە، کاتێ کە ناردمانە سەریان بای (گەرمی توندی) بێ خێر');
INSERT INTO "bamoki" VALUES(51,42,'51:42','بەسەر ھەر شتێکدا بڕۆیشتایە وازی لێ نەدەھێنا ھەتا وەك ڕزیوی لێ ئەکرد');
INSERT INTO "bamoki" VALUES(51,43,'51:43','وە لە گەلی سەموددا (پەند وئامۆژگاری ھەیە) کاتێك کە پێیان ووترا ڕابوێرن تا کاتی خۆی دێت');
INSERT INTO "bamoki" VALUES(51,44,'51:44','جا ئەوان سەرکەشیان کرد لەفەرمانی پەروەردگاریان، ئەمجا ھەورە بروسکە لێیدان لەکاتێکدا ئەوان (تیاچوونی خۆیانیان) بەچاو دەبینی');
INSERT INTO "bamoki" VALUES(51,45,'51:45','ئیتر نەیانتوانی ھەستنەوە وە سەرکەوتوویش نەبوون');
INSERT INTO "bamoki" VALUES(51,46,'51:46','وە گەلی نووحیش لەپێش ئەماندا بوو کە بەڕاستی گەلێکی بەدکارو لەسنوور دەرچوو بوون');
INSERT INTO "bamoki" VALUES(51,47,'51:47','وە ئاسمانمان بەھێز وتوانایەکی گەورە دروست کردووە وە ئێمە بەڕاستی فراوانکەری (ئاسمانین) بە بەردەوامی');
INSERT INTO "bamoki" VALUES(51,48,'51:48','وە زەویمان ڕاخستووە ئای چ ڕاخەرێکی چاکین ئێمە');
INSERT INTO "bamoki" VALUES(51,49,'51:49','وە لە ھەموو شتێك جووتێك (نێر و مێ) مان دروست کردووە، بۆ ئەوەی بیربکەنەوە وپەندی لێ وەربگرن');
INSERT INTO "bamoki" VALUES(51,50,'51:50','ئەمجا بەپەلە بەرەو لای خوا بڕۆن بەڕاستی من لەلایەن خواوە ترسێنەرێکی ئاشکراو ڕوونم بۆ ئێوە');
INSERT INTO "bamoki" VALUES(51,51,'51:51','وە لەگەڵ خوادا پەرستراوی تر دامەنێن، بەڕاستی من لەلایەن خواوە ترسێنەرێکی ئاشکراو ڕوونم بۆ ئێوە');
INSERT INTO "bamoki" VALUES(51,52,'51:52','ھەر بەم شێوەیە ھیچ پێغەمبەرێك نەھاتووە بۆ لای ئوممەتانی پێش ئەمان مەگەر ووتوویانە(بەو پێغەمبەرە) جادوگەرە یان شێتە');
INSERT INTO "bamoki" VALUES(51,53,'51:53','ئایا(ئەمان وئەوان) ڕاسپاردەیان بۆیەکتری کردووە؟ (نەخێر) بەڵکو ئەمان گەلێکی سەرکەش ویاخین');
INSERT INTO "bamoki" VALUES(51,54,'51:54','تۆ (ئەی موحەممەد صلى الله عليه وسلم) ڕوویان لێ وەرگێڕە چونکە تۆ لۆمەکراو نیت');
INSERT INTO "bamoki" VALUES(51,55,'51:55','وە ئامۆژگاریان بکەو بیریان بخەرەوە چونکە بەڕاستی ئامۆژگاری (ئەگەر سوودی بۆ بێ بڕوایان نەبێت) سوود ئەگەیەنێت بەبڕواداران');
INSERT INTO "bamoki" VALUES(51,56,'51:56','وە من جنۆکە وئادەمیزادم تەنھا بۆ ئەوە دروست کردووە کەبەندایەتی من بکەن');
INSERT INTO "bamoki" VALUES(51,57,'51:57','من ھیچ ڕۆزییەکم ناوێ لێیان، ناشمەوێت خواردنم بدەنێ');
INSERT INTO "bamoki" VALUES(51,58,'51:58','بەڕاستی تەنھا خوا خۆی ڕۆزی دەرە کە خاوەن دەسەڵاتی زۆر بەھێزە');
INSERT INTO "bamoki" VALUES(51,59,'51:59','ئەمجا بێگومان بۆ ئەوانەی ستەمیان کردووە، سزای زۆرمان بۆ ئامادەکردوون ھەروەک چۆن بۆ ھاوەڵ وھاوبیرەکانیان ئامادەمانکردووە دەی با ئیتر پەلە نەکەن (لە داواکردنی سزا)');
INSERT INTO "bamoki" VALUES(51,60,'51:60','سا ھاوار بۆ ئەوانەی بێ باوەڕ بوون لەو ڕۆژەی کە ھەڕەشەیان پێ لێکراوە');
INSERT INTO "bamoki" VALUES(52,1,'52:1','سوێند بە کێوی طور (بەو کێوەی موسا لەسەری قسەی لەگەڵ خوای گەورە کرد)');
INSERT INTO "bamoki" VALUES(52,2,'52:2','وە بەکتێبی نووسراو (مەبەست پێی قورئانە)');
INSERT INTO "bamoki" VALUES(52,3,'52:3','لەچەند پەڕەیەکی بڵاوکراوە (قورئان نووسراوەتەوە لەچەند پەڕەیەکدا)');
INSERT INTO "bamoki" VALUES(52,4,'52:4','وە سوێند بەو خانەی ئاوەدانە (بیت المعمور: جێگایەکە لە ئاسمان، فریشتەکان ھەمیشە بەدەوریدا دێن ودەچن، بەوانەوە ئاوەدانە)');
INSERT INTO "bamoki" VALUES(52,5,'52:5','وە سوێند بەبانی بڵندکراو (ئاسمانی دونیا)');
INSERT INTO "bamoki" VALUES(52,6,'52:6','وە سوێند بەدەریای پڕ کراو (لە ئاو یان ئاگری داگیرساو)');
INSERT INTO "bamoki" VALUES(52,7,'52:7','بێگومان سزای پەروەردگارت(بۆ بێ بڕوایان) دەبێت');
INSERT INTO "bamoki" VALUES(52,8,'52:8','ھیچ بەرگری کەری نیە');
INSERT INTO "bamoki" VALUES(52,9,'52:9','ڕۆژێکك کەئاسمان زۆر توند دەکەوێتە جوڵە وھاتوچۆ');
INSERT INTO "bamoki" VALUES(52,10,'52:10','وە چیاکان (لەجێی خۆیان) دەجووڵێن وتوند دەڕۆن');
INSERT INTO "bamoki" VALUES(52,11,'52:11','کەواتە لەو ڕۆژەدا ھاوار بۆ بێ باوەڕان');
INSERT INTO "bamoki" VALUES(52,12,'52:12','ئەوانەی کە لەکاری ناڕەوادا ڕۆدەچن وھەمیشە گاڵتە وگەپ دەکەن');
INSERT INTO "bamoki" VALUES(52,13,'52:13','ڕۆژێ پاڵیان پێوە دەنرێت بەرەو ئاگری دۆزەخ چۆن پاڵ پێوەنانێك');
INSERT INTO "bamoki" VALUES(52,14,'52:14','(پێیان دەووترێت) ئەمە ئەو ئاگرەیە کەئێوە بە درۆتان دەزانی');
INSERT INTO "bamoki" VALUES(52,15,'52:15','ئایا ئەم ئاگرە جادوە یان ئێوە نابینن');
INSERT INTO "bamoki" VALUES(52,16,'52:16','بچنە ناویەوە ئەمجا ئارام بگرن یا نەیگرن یەکسانە بۆتان، بێگومان ھەر تۆڵەی ئەو کردەوانەی دەتانکرد لێتان دەسەنرێتەوە');
INSERT INTO "bamoki" VALUES(52,17,'52:17','بە ڕاستی پارێزکاران ولەخواترسان لە ناو باخات ونیعمەتەکانی بەھەشتدان');
INSERT INTO "bamoki" VALUES(52,18,'52:18','چێژ ئەبەن لەو شتانەی پەروەردگاریان پێی بەخشیوون وە پەروەردگاریان پاراستوونی لەسزای دۆزەخ');
INSERT INTO "bamoki" VALUES(52,19,'52:19','(پێیان دەووترێت) بخۆن وبخۆنەوە نۆشی گیانتان بێت بەھۆی ئەوچاکانەی لەدونیادا دەتانکرد');
INSERT INTO "bamoki" VALUES(52,20,'52:20','پاڵیان داوەتەوە لەسەر کورسی وقەنەفەی ڕیزکراو وە حۆریانی چاو گەش وچاو گەورەمان کردە ھاوسەریان');
INSERT INTO "bamoki" VALUES(52,21,'52:21','وە ئەوانەی بڕوایان ھێناوە وە نەوەکانیشیان دوایان کەوتوون بەئیمانەوە، نەوەکانیان دەگەیەنین پێیان (لەپلەی بەھەشتدا) وە ھیچ لە کردەوەی(باوکە)کانیان کەم ناکەینەوە، ھەرکەسە بارمتەی کردەوەکانی خۆیەتی');
INSERT INTO "bamoki" VALUES(52,22,'52:22','وە بەردەوام ھەموو جۆرە میوە وگۆشتێکیان پێ دەدەن لەوەی ئارەزووی دەکەن');
INSERT INTO "bamoki" VALUES(52,23,'52:23','لەو (بەھەشتە)دا پەرداخی شەراب لە یەکتری وەردەگرن نە قسەی پڕو پووچی تێدایە ونەگوناە کردن');
INSERT INTO "bamoki" VALUES(52,24,'52:24','دەگەڕێن بەسەریاندا کوڕانی نەوجەوانی ئێسك سووکی خۆیان وخزمەتیان دەکەن، وەك مرواری ناو سەدەفن (لە جوانی وپاکیدا)');
INSERT INTO "bamoki" VALUES(52,25,'52:25','بەھەشتیەکان ڕوو دەکەنە یەکتری وپرسیار لە یەکتری دەکەن');
INSERT INTO "bamoki" VALUES(52,26,'52:26','ئەڵێن: بەڕاستی ئێمە پێشتر لەناو کەس وکارماندا دەترساین (لەسزای دۆزەخ)');
INSERT INTO "bamoki" VALUES(52,27,'52:27','ئەوەتە خوا منەتی نا بەسەرماندا وە لەسزای دۆزەخ پاراستینی');
INSERT INTO "bamoki" VALUES(52,28,'52:28','بەڕاستی ئێمە لەمەو پێش خوامان دەپەرست وھاوارمان لێدەكرد، بەڕاستی ئەو چاکەکارێکی میھرەبانە');
INSERT INTO "bamoki" VALUES(52,29,'52:29','جا (ئەی موحەممەد صلى الله عليه وسلم) یادیان خەرەوە چونکە تۆ بەھۆی نیعمەتەکانی پەروەردگارتەوە نەفاڵچیت ونەشێتیت');
INSERT INTO "bamoki" VALUES(52,30,'52:30','نا بەڵکو دەڵێن (موحەممەد صلى الله عليه وسلم) شاعیرە چاوەڕوانی مردنی دەکەین');
INSERT INTO "bamoki" VALUES(52,31,'52:31','(ئەی موحەممەد صلى الله عليه وسلم) بڵێ: چاوەڕوان بن بێگومان منیش لەگەڵتاندا لەچاوەڕوانانم');
INSERT INTO "bamoki" VALUES(52,32,'52:32','ئاخۆ فام وژیرییان فەرمانیان پێ ئەدا بەم قسانە (نەخێر) بەڵکو ئەمانە گەلێکی سەرکەش ویاخین');
INSERT INTO "bamoki" VALUES(52,33,'52:33','یا دەڵێن قورئان (موحەممەد صلى الله عليه وسلم) خۆی ھەڵیبەستووە؟ (نەخێر وانیە) بەڵکو بڕوا ناھێنن');
INSERT INTO "bamoki" VALUES(52,34,'52:34','دەی با ئەوان(بێ باوەڕەکان) نموونەیەکی وەك قورئان بھێنن ئەگەر ڕاست ئەکەن، (کە ھەڵبەستراوی مرۆڤە)');
INSERT INTO "bamoki" VALUES(52,35,'52:35','ئایا ئەوان (بێ خالقێك) لە خۆیانەوە دروست بوون یان خۆیان (خالق) ودروستکەری خۆیانن؟');
INSERT INTO "bamoki" VALUES(52,36,'52:36','ئەی ئایا ئەوان ئاسمانەکان وزەویان دروست کردووە (نەخێر وا نیە) بەڵکو دڵنیانین وگومانمان ھەیە');
INSERT INTO "bamoki" VALUES(52,37,'52:37','ئەی ئایا گەنجینەکانی پەروەردگاری تۆ لای ئەوانە؟ یان ئایا ھەر ئەوان دەسەڵاتدار وزاڵن');
INSERT INTO "bamoki" VALUES(52,38,'52:38','یان پەیژەیەکیان ھەیە (پێـیدا سەربکەون بۆ ئاسمان و) گوێ بگرن تێیدا (لە ھەواڵی ئاسمان)؟ جا (ئەگەر ڕاست ئەکەن) با گوێ گرەکەیان بەڵگەیەکی ڕوون بهێنێت');
INSERT INTO "bamoki" VALUES(52,39,'52:39','یان ئایا کچان بۆ خوایە وکوڕان بۆ ئێوەیە؟');
INSERT INTO "bamoki" VALUES(52,40,'52:40','یان ئایا تۆ (ئەی موحەممەد صلى الله عليه وسلم) داوای کرێ وپاداشتیانیان لێ دەکەیت؟ ئەوانیش لەبەر بژاردنی باریان گرانە؟');
INSERT INTO "bamoki" VALUES(52,41,'52:41','یا ئایا زانیاری غەیبیان لایە؟ کە (لەبەری) بنووسنەوە');
INSERT INTO "bamoki" VALUES(52,42,'52:42','ئەی ئایا ئەیانەوێت فێڵ بکەن؟ دەی ھەربێ باوەڕان فێڵ لێ کراوون');
INSERT INTO "bamoki" VALUES(52,43,'52:43','ئەی ئایا پەرستراوێکیان ھەیە بێجگە لە خوا؟ خوا پاکە لە ھەموو ھاوبەشێك کە بۆی بڕیار دەدەن');
INSERT INTO "bamoki" VALUES(52,44,'52:44','وە ئەگەر پارچەیەك لە ئاسمان ببینن بکەوێتە خوارەوە ئەڵێن: (ئەمە) ھەورێکی بەسەریەکدا کەوتووە (بارانمان بۆ دەبارێنێ)');
INSERT INTO "bamoki" VALUES(52,45,'52:45','دەی لێیان گەڕێ تا تووشی ڕۆژی خۆیان دەبن کە تیایدا لە ناو دەچن');
INSERT INTO "bamoki" VALUES(52,46,'52:46','ڕۆژێك کە فێڵەکانیان ھیچ سوودێکیان پێ ناگەیەنێت وە ھیچ سەرکەوتوو وسەرفراز نابن');
INSERT INTO "bamoki" VALUES(52,47,'52:47','بە ڕاستی ئەوانەی ستەمیان کردووە (لە خۆیان و بێ بڕوا بوون) بۆیان ھەیە سزایەك پێش سزای ڕۆژی دوایی، بەڵام زۆربەیان نایزانن');
INSERT INTO "bamoki" VALUES(52,48,'52:48','چاوەڕوانی بڕیاری پەروەردگارت بە چونکە بێگومان تۆ لەپێش چاو وچاودێری ئێمەدایت (لەم ئایەتەدا جێگیرکردن وباسکردنی سیفەتی چاو بۆ خوا ھاتووە ئێمە بڕوامان پێیەتی بەبێ ھیچ لێچواندن ولێکدانەوەیەك، وەشێوەکەی نازانین تەنھا خوا دەیزانێت) ھەرکاتێ (لەخەو) ھەستایت تەسبیحات بکە بە ستایشی پەروەردگارت');
INSERT INTO "bamoki" VALUES(52,49,'52:49','وە بیپەرستە وستایشی بکە لە بەشێك لەشەودا وە کاتێك ئەستێرەکان وون دەبن (وپشت ھەڵدەکەن)');
INSERT INTO "bamoki" VALUES(53,1,'53:1','سوێند بە ئەستێرە کاتێك ئاوا دەبێت');
INSERT INTO "bamoki" VALUES(53,2,'53:2','ھاوڕێکەتان (موحەممەد صلى الله عليه وسلم) گومڕا نەبووە وە لە ڕاستی لای نەداوە');
INSERT INTO "bamoki" VALUES(53,3,'53:3','وە لە ھەوا وھەوسەوە قسە ناکات (کە قورئان دەخوێنێت)');
INSERT INTO "bamoki" VALUES(53,4,'53:4','ھەرچی دەیڵێت (قورئانەو) نیگا دەکرێت بۆی');
INSERT INTO "bamoki" VALUES(53,5,'53:5','(جوبرەیلی) زۆر بەتوانا فێری کردووە');
INSERT INTO "bamoki" VALUES(53,6,'53:6','خاوەنی بیرو ھۆشی بەھێزە، جا لەسەر شێوەی ڕاستەقینەی خۆی ڕاوەستاوە');
INSERT INTO "bamoki" VALUES(53,7,'53:7','کە بە ئاسۆی ھەرە بەرزی ئاسمانەوە بوو');
INSERT INTO "bamoki" VALUES(53,8,'53:8','پاشان نزیك بوویەوە (لە موحەممەد صلى الله عليه وسلم) و ئەمجا ھاتە خوارەوە');
INSERT INTO "bamoki" VALUES(53,9,'53:9','تا بە ئەندازەی دوو کەوان یان کەمتری(ماوە بوو لەگەڵ موحەممەددا صلى الله عليه وسلم)');
INSERT INTO "bamoki" VALUES(53,10,'53:10','ئەمجا (خوا) نیگای کرد بۆ بەندەی خۆی، ئای چ نیگایەك بوو');
INSERT INTO "bamoki" VALUES(53,11,'53:11','ئەوەی (موحەممەد صلى الله عليه وسلم) بەچاوی سەر بینی دڵی (بڕوای پێ کرد و) بە درۆی نەزانی');
INSERT INTO "bamoki" VALUES(53,12,'53:12','جا ئایا (بێ باوەڕان) ئێوە دەمەقاڵێی لەگەڵ دەکەن دەربارەی ئەوەی کە بە چاوی خۆی دەی بینێت');
INSERT INTO "bamoki" VALUES(53,13,'53:13','سوێند بەخوا بێگومان جارێکی تر جوبرەئیلی بینیوە');
INSERT INTO "bamoki" VALUES(53,14,'53:14','لای درەختی(سیدرەتولمونتـەھا)');
INSERT INTO "bamoki" VALUES(53,15,'53:15','کە لای ئەوەوەیە بەھەشتی نیشتەجێ بوون');
INSERT INTO "bamoki" VALUES(53,16,'53:16','ئەو کاتەی (پێغەمَبەرصلى الله عليه وسلم) ئەو شتە گرنگ وسەر سوڕھێنەرەی بینی کە سیدرەی داپۆشی چۆن داپۆشینێك');
INSERT INTO "bamoki" VALUES(53,17,'53:17','چاوی ئەو (موحەممەد صلى الله عليه وسلم) بەملاولادا نەیـدەڕوانی (لە مەبەست تێ نەدەپەڕی)');
INSERT INTO "bamoki" VALUES(53,18,'53:18','سوێند بەخوا بێگومان بینی گەورەترین بەڵگە ونیشانەی پەروەردگاری');
INSERT INTO "bamoki" VALUES(53,19,'53:19','دەی پێم بڵێن ئایا لات و عوززا (کە دووبتن)');
INSERT INTO "bamoki" VALUES(53,20,'53:20','وە بتی سێهەم کە مەناتە (ئایا ھیچیان پێ دەکرێت)');
INSERT INTO "bamoki" VALUES(53,21,'53:21','ئایا ڕاستە ئێوە کوڕان بۆ خۆتان وکچان بۆ خودا بەڕەوا وچاك دەزانن؟');
INSERT INTO "bamoki" VALUES(53,22,'53:22','ئەگەر وابێت دابەشکردنێکی نادروست دەبێ');
INSERT INTO "bamoki" VALUES(53,23,'53:23','ئەم بتانە ھەر ناوانێکی ڕوتن کە ئێوە و باو وباپیرانتان لێتان ناون (بەبێ ناوەڕۆك) خوا ھیچ بەڵگەیەکی لەسەر پەرستراوی ئەوان نەناردووەتە خوارەوە، تەنھا شوێنی گومان وئەو شتە دەکەون کە نەفسی خۆیان حەزی لێیەتی، لە کاتێکدا کە بەڕاستی لەلایەن پەروەردگاریانەوە ھیدایەتیان بۆ ھاتووە');
INSERT INTO "bamoki" VALUES(53,24,'53:24','ئایا مرۆڤ ئارەزووی ھەرچی کرد بۆی دێتە دی (نەخێر)');
INSERT INTO "bamoki" VALUES(53,25,'53:25','چونکە ھەر خوایە خاوەنی دونیا ودواڕۆژە');
INSERT INTO "bamoki" VALUES(53,26,'53:26','وە چەندەھا فریشتە کە لە ئاسمانەکاندا ھەن تکا وشەفاعەتیان ھیچ سوودی نیە، مەگەر دوای ئەوەی کە خوا مۆڵەت بدات بۆ ھەرکەسێك کە خۆی بیەوێت وڕازی بێت پێی');
INSERT INTO "bamoki" VALUES(53,27,'53:27','بەڕاستی ئەوانەی کە باوەڕ بـە ڕۆژی دوایی ناھێنن، فریشتەکان بە کچی (خوا) ناو دەبەن');
INSERT INTO "bamoki" VALUES(53,28,'53:28','لە کاتێکدا ھیچ زانیاریەکیان دەربارەی نیە، تەنھا دوای گومان دەکەون وە بەڕاستی گومان ھیچ سوودێکی نیە لەزانینی ھەق وڕاستیدا');
INSERT INTO "bamoki" VALUES(53,29,'53:29','دەی کەواتە تۆش وازبھێنە لەو کەسە پشت دەکاتە قورئان ویادی ئێمە، وە بێجگە ژیانی دونیا ھیچی تری ناوێت');
INSERT INTO "bamoki" VALUES(53,30,'53:30','ئەمە ئەوپەڕی زانیاری وتێگەیشتنیانە لەزانیاریدا، بەڕاستی پەروەردگارت چاك دەزانێت بەوەی لە ڕێبازەکەی گومڕا بووە وە چاك دەزانێت بەوەی کە ڕێنووین بووە');
INSERT INTO "bamoki" VALUES(53,31,'53:31','وە خوا خاوەنێتی ھەرچی لەئاسمانـەکان وزەویدایە، تا سزای ئەوانە بدات کە خراپەیان کردووە بە پێی کردەوەکانیان وە پاداشتی ئەوانە بداتەوە کەچاکەیان کردووە بەچاکەتر (لەوەی کردویانە)');
INSERT INTO "bamoki" VALUES(53,32,'53:32','ئەوانەی کە لەگوناھی گەورە وکردەوەی دزێو خۆیان دەپارێزن جگە تاوانی بچوك نەبێت، بەڕاستی پەروەردگارت لێبوردنی فراوانە، ئەو لەھەموو کەس بەحاڵی ئێوە زاناترە لەکاتێکدا کە لەزەوی دروستی کردون وە کاتێك کە ئێوە کۆرپەلەن ولەسکی دایکتاندان، کەواتە خۆتان بەچاك وپاك دامەنێن، خوا زاناترە بەو کەسەی زیاتر لە خواترسە');
INSERT INTO "bamoki" VALUES(53,33,'53:33','جا ئایا بینیت ئەو کەسەی کە پشتی ھەڵکرد (لەباوەڕ)');
INSERT INTO "bamoki" VALUES(53,34,'53:34','(لەوەی بڕیاری دابوو) کەمێکی بەخشی وئەوەی تری گرتەوە');
INSERT INTO "bamoki" VALUES(53,35,'53:35','ئایا (ئەو کەسە) زانیاری پەنھانی لایەو بەچاو دەیبینێت');
INSERT INTO "bamoki" VALUES(53,36,'53:36','یان ئایا ئاگادار نەکرابوو بەوەی کە لەپەڕاوەکانی موسادا بوو');
INSERT INTO "bamoki" VALUES(53,37,'53:37','(وە بەوەی کە لەپەڕاوی ئیبراھیمدا بوو) ئەو ئیبراھیمەی کە پەیمانی خۆی بەچاکی بەجێ ھێنا');
INSERT INTO "bamoki" VALUES(53,38,'53:38','(لەو پەڕاوانەدا ھاتووە) کە ھیچ تاوانبارێك گوناھی تاوانبارێکی تر ھەڵناگرێت');
INSERT INTO "bamoki" VALUES(53,39,'53:39','وەبێگومان ئادەمی جگە لەکۆششی خۆی خاوەنی ھیچی تر نیە (لە دواڕۆژدا)');
INSERT INTO "bamoki" VALUES(53,40,'53:40','و بێگومان ھەوڵ کۆششەکەی لە داھاتوو (لەقیامەت)دا دەبینرێتەوە');
INSERT INTO "bamoki" VALUES(53,41,'53:41','پاشان پاداشت دەدرێتەوە بەتێر وتەواوی');
INSERT INTO "bamoki" VALUES(53,42,'53:42','وە بەڕاستی ھەموو کارێک سەرەنجامەکەی ھەر بۆ لای پەروەردگارت دەگەڕێتەوە');
INSERT INTO "bamoki" VALUES(53,43,'53:43','وە بێگومان ھەر ئەویش بەندەکان دەخاتە گریان وپێکەنین');
INSERT INTO "bamoki" VALUES(53,44,'53:44','وە بێگومان ھەر ئەوە دەیانمرێنێت وزیندووشیان دەکاتەوە');
INSERT INTO "bamoki" VALUES(53,45,'53:45','وەبەڕاستی ھەر ئەوە کە جوتێ نێر ومێی دروستكردووە');
INSERT INTO "bamoki" VALUES(53,46,'53:46','لە نوتفەیەك (دڵۆپە ئاوێك)کاتێك دەڕژێنرێتە منداڵـدانەوە');
INSERT INTO "bamoki" VALUES(53,47,'53:47','وە بەڕاستی ھەر لەسەر خوایە دروست کردنەوەی لەڕۆژی دواییدا');
INSERT INTO "bamoki" VALUES(53,48,'53:48','وە بێگومان ھەر ئەویشە کەخەڵکی دەوڵەمەند وھەژار دەکات');
INSERT INTO "bamoki" VALUES(53,49,'53:49','وە بەڕاستی ھەر ئەوە پەروەردگاری ئەستێرەی شیعرا ( شیعرا : ئەستێرەیەك بوو خەڵکانێك لەسەردەمی نەفامیدا دەیانپەرست)');
INSERT INTO "bamoki" VALUES(53,50,'53:50','وەبێگومان ھەر ئەو خوایە گەلی عادی یەکەمی لەناو برد');
INSERT INTO "bamoki" VALUES(53,51,'53:51','گەلی سەموودیشی لەناوبرد وکەسی نەھێشتن');
INSERT INTO "bamoki" VALUES(53,52,'53:52','وەگەلی نووحیشی (لەناو برد) لەپێش (عاد وسەمود)دا کەبەڕاستی ئەوان ستەم کارتر وسەر کەش تربوون');
INSERT INTO "bamoki" VALUES(53,53,'53:53','وەگوندە سەروژێرکراوەکانی (بەزەویدا) بردە خوارەوە (کەگوندەکانی گەلی لوط بوون)');
INSERT INTO "bamoki" VALUES(53,54,'53:54','ئەمجا ئەو گوندانەی داپۆشی (بەجۆرەھا سزای سەخت) چۆن داپۆشینێك');
INSERT INTO "bamoki" VALUES(53,55,'53:55','لەکام ناز ونیعمەتەکانی پەروەردگارت دوو دڵیت');
INSERT INTO "bamoki" VALUES(53,56,'53:56','ئەم (پێغەمبەرە) ترسێنەرێکە لە (پێغەمبەرە) ترسێنەرەکانی پێشوو');
INSERT INTO "bamoki" VALUES(53,57,'53:57','ڕۆژی دوایی زۆر نزیك بووەتەوە');
INSERT INTO "bamoki" VALUES(53,58,'53:58','جگە لەخوای گەورە کەس نیە ئاشکرای بکات (وبزانێت کەی دێت)');
INSERT INTO "bamoki" VALUES(53,59,'53:59','جـا ئایا لەم قورئانە سەرتان سوڕ دەمێنێت؟!');
INSERT INTO "bamoki" VALUES(53,60,'53:60','وە پێدەکەنن وناگرین!');
INSERT INTO "bamoki" VALUES(53,61,'53:61','وە ئێوە سەر گەرم و بێ ئاگان');
INSERT INTO "bamoki" VALUES(53,62,'53:62','دەی سوژدە بەرن بۆ خوا وپەرستشی بکەن');
INSERT INTO "bamoki" VALUES(54,1,'54:1','ڕۆژی دوایی نزیك بوویەوە وە مانگ لەت بوو');
INSERT INTO "bamoki" VALUES(54,2,'54:2','وە ئەگەر بێ بڕوایان موعجیزەیەك ببینن پشت ھەڵدەکەن وە دەڵێن ئەمە جادوویەکی بەردەوامە (و موحەممەد لێیان دەکات)');
INSERT INTO "bamoki" VALUES(54,3,'54:3','وەئەو (بت پەرستانە) باوەڕیان نەھێناو شوێن ھەواو ئارەزووی خۆیان کەوتن لەکاتێکدا ھەموو کارێك سەرەنجامێکی ھەیە');
INSERT INTO "bamoki" VALUES(54,4,'54:4','بێگومان ھەواڵی زۆر (لەناوبردنی یاخیانیان) پێ گەیشتووە (لەقورئاندا) کە بیانگێڕێتەوە (لە بت پەرستی)');
INSERT INTO "bamoki" VALUES(54,5,'54:5','(قورئان) ھەمووی حیکمەتێکی تەواوە بەڵام ترساندنی ترسێنەران (بۆ گەلی بێ بڕوا) سوودی نیە');
INSERT INTO "bamoki" VALUES(54,6,'54:6','ئەمجا تۆ (ئەی موحەممەد صلى الله عليه وسلم) ڕوویان لێ وەرچەرخێنە (چاوەڕوانی) ئەو ڕۆژە بکە کە جاڕ دەرێك بانگی خەڵکی دەکات بۆ شتێکی زۆر ناخۆش(کەڕۆژی حەشرە)');
INSERT INTO "bamoki" VALUES(54,7,'54:7','(بێ باوەڕِان) بەکزوچاو شۆڕیەوە لەگۆڕەکانیان دێنەدەرەوە وەك کوللەی پەرش وبڵاون');
INSERT INTO "bamoki" VALUES(54,8,'54:8','خێرا دوای جاڕ دەرەکە دەکەون، بێ باوەڕان دەڵێن: ئەمڕۆ ڕۆژێکی زۆر دژوار وسەختە');
INSERT INTO "bamoki" VALUES(54,9,'54:9','پێش ئەمان گەلی نووح بێ باوەڕ بوون، جابەندەی ئێمە (نوح)یان بەدرۆزن دانا وە ووتیان (نووح) شێتە وە ھـەڕەشەیان لێکرد وڕێگری کـرا لـەبانگەوازەکەی');
INSERT INTO "bamoki" VALUES(54,10,'54:10','جا (نوح) ھاواری کردە پەروەردگاری و (ووتی): بەڕاستی من شکستم خواردووە (بێ دەسەڵاتم) دەی تۆ یارمەتیم بدە،');
INSERT INTO "bamoki" VALUES(54,11,'54:11','ئێمەش ھەموو دەرگای ئاسمانمان کردەوە بەئاوی بارانی بەلێزمە');
INSERT INTO "bamoki" VALUES(54,12,'54:12','وە کانیاوی زۆرمان لەزەویدا ھەڵقوڵاند، جائاوی ئاسمان وزەوی بەیەك گەشتن بەو ئەندازەی کە بڕیاری لەسەر درا بوو');
INSERT INTO "bamoki" VALUES(54,13,'54:13','وە نوحمان (لەگەڵ شوێن کەوتووانی) ھەڵگرت لەسەر کەشتییە کە بەچەند پارچە تەختە وبزمار (دروست کرابوو)');
INSERT INTO "bamoki" VALUES(54,14,'54:14','لە پێش چاو وچاودێری ئێمە دەڕۆیشت (نەمان ھێشت نوقم ببێت) (لەم ئایەتەدا جێگیرکردنی صیفەتی [عین = چاو] بۆ خوای گەورە ھاتووە، بەڵام بەشێوەیە کە شایستەی گەورەیی وتەواوی زاتی خوا بێت، بەبێ ھیچ لێچواندن ولێکدانەوەیەك، وە بڕوامان پێیەتی وچۆنیەتیەکەی نازانین، تەنھا خوا دەیزانێت) ئەمە پاداشتە بۆ ئەو کەسەی باوەڕی پێ نەکرا (کە نووحە)');
INSERT INTO "bamoki" VALUES(54,15,'54:15','بێگومان ئەو ڕووداوەمان ھێشتەوە تاببێتە پەندێك، جا ئایا کەسێك ھەیە پەند وەرگرێت؟');
INSERT INTO "bamoki" VALUES(54,16,'54:16','دەی ئایا سزا وترساندنم چۆن بوو؟');
