INSERT INTO "bamoki" VALUES(57,4,'57:4','(خـوا) ئـەو زاتـەیە کـەئـاسمـانـەکـان وزەوی دروسـت کـردووە لەشەش ڕۆژدا پاشان بەرزبویەوە سەر عەرش وقەراری گرت (باوەڕمان پێیەتی بەو شێوەی شیاوی خوا بێت، وەچۆنیەتیەکەی نازانین، تەنھا خوا خۆی دەزانێت) دەزانێت چی دەچێتە ناوی زەوی وەچی لێ دێتە دەرەوە وەچی لەئاسمانەوە دێتە خوار وەچی بۆ سەردەکەوێت وە لەھەر کوێ بن خوا لەگەڵتانە (بەزانیاری و ئاگاداری) وەخوا بینایە بەھەرچی کە دەیکەن');
INSERT INTO "bamoki" VALUES(57,5,'57:5','خاوەندارێتی ئاسمانەکان وزەوی ھەر بۆ ئەوە وە ھەموو کارێك ھەر بۆ لای خوا دەگەڕێنرێتەوە');
INSERT INTO "bamoki" VALUES(57,6,'57:6','شەو دەبات بەناو ڕۆژدا (لەھاوین وپایزدا) وە ڕۆژ دەبات بەناو شەودا (لە زستان وبەھاردا) وە ئەو (خوا) ئاگادارە بەنیازی ناو دڵان');
INSERT INTO "bamoki" VALUES(57,7,'57:7','باوەڕ بھێنن بەخوا وپێغەمبەرەکەی وببەخشن لەو ماڵ وسامانەی کە (خوای گەورە) خستویەتیە ژێر دەستان وجێنشین تیایدا، ئەمجا ئەوانەتان کە باوەڕی ھێناوە وداراییان بەخشیووە ،پاداشتی گەورەیان بۆ ھەیە');
INSERT INTO "bamoki" VALUES(57,8,'57:8','ئەوە چیتانە بۆ باوەڕ بەخوا ناھێنن؟ کە پێغەمبەر بانگتان دەکات کە باوەڕ بە پەروەردگارتان بھێنن لەکاتێکدا کە بێگومان خوا پەیمانی (باوەڕ ھێنانی) لێ وەرگرتوون ئەگەر ئێوە باوەڕدارن');
INSERT INTO "bamoki" VALUES(57,9,'57:9','خوا ئەو زاتەیە دەینێرێت بۆ بەندەی خۆی (کە موحەممەد صلى الله عليه وسلم)ە بەڵگە وئایەتانێکی ڕوون وئاشکرا تا لەتاریکستانی (گومڕایی وبێباوەڕی) دەرتان بھێنێت بۆ ناو ڕووناکی ( ئیمان وئیسلام) وە بەڕاستی خوا بەرامبەر ئێوە بەسۆز ومیھرەبانە');
INSERT INTO "bamoki" VALUES(57,10,'57:10','ئەوە چیتانە کەوا ھیچ نابەخشن لە ڕێی خوادا لە کاتێکدا میراتی ئاسمانەکان وزەوی بۆ خوا دەمێنێتەوە، یەکسان نین ئەوانەتان کە لەپێش گرتنی (مەککە)دا ماڵیان بەخشیوو جەنگاون (لەگەڵ ئەوانەی لە دوای فەتحی مەککە ئەو کارانە بکەن) ئەوانە پلەو پایەیان زۆر گەورەترە لەوانەی کە لەدوای (گرتنی مەککە) ماڵیان بەخشیووە وجەنگاون وە بەھەر دوولا خوا بەڵێنی بەھەشتی داوە وە خوا ئاگادارە بەوەی کە دەیکەن');
INSERT INTO "bamoki" VALUES(57,11,'57:11','کێیە ئەو کەسەی قەرز دەدات بەخوا بەقەرزێکی چاك (مەبەست ئەوەیە ماڵ ببەخشێت لەپێناو خوا) تا خواش چەند بەرامبەر بیداتەوە وە پاداشتی زۆر چاکی بۆ ھەیە');
INSERT INTO "bamoki" VALUES(57,12,'57:12','لەرۆِژێکدا کە تۆ پیاوان وئافرەتانی ئیماندار دەبینیت کەڕووناکی (باوەڕیان) لەپێشیانەوە ولەلای ڕاستیانەوە دەڕوات (پێیان دەڵێن) مژدەی ئێوە ئەمڕۆ بەھەشتانێکە کەڕووباری زۆر بەژێریاندا دەڕوات تێیدا دەمێننەوە بەھەمیشەیی، ھەر ئەوەیە سەرکەوتنی گەورە');
INSERT INTO "bamoki" VALUES(57,13,'57:13','لەو ڕۆژەدا پیاوان وئافرەتانی دووڕوو دەڵێن بەوانەی کەباوەڕدار بوون سەیرێکمان بکەن تا لەڕووناکیتان تروسکێك وەرگرین، پێیان دەوترێت بگەڕێنەوە بۆ دواوەی خۆتان وە داوای ڕووناکی بکەن بۆخۆتان، یەکسەر شوورایەك دەکێشرێت لەنێوانیاندا کە دەرگایەکی تێدایە ڕِووی ناوەوەی ڕەحمەتی تێدایە دەرەوەیشی ڕووی لەسزایە');
INSERT INTO "bamoki" VALUES(57,14,'57:14','(دوو ڕووان ئیمانداران) بانگ دەکەن ودەڵێن: ئایا ئێمە لەگەڵ ئێوە نەبوین (لە نوێژ و ڕۆژوودا) ئیمانداران دەڵێن: بەڵێ لەگەڵماندا بوون بەڵام ئێوە خۆتان خەڵەتاند (دووڕوو وپیلان گێڕبوون) وە چاوەڕوانی (نەمانی باوەڕدارانتان) دەکرد ودوودڵ بوون وە ھیواو ئاوات فریوویدان تا فەرمانی خوا ھات( و مردن) وە شەیتان فریویدان بە لێ بوردەیی خوای گەورە');
INSERT INTO "bamoki" VALUES(57,15,'57:15','ئەمڕۆ ھیچ بریتیەك وەرناگیرێت نە لەئێوە وە نە لە بێ باوەڕان بۆ ڕزگار بوونتان، جێگاتان ئاگری دۆزەخە، ئەو ئاگرە شیاوتانە، ئای چەند سەرئەنجامێکی خراپە');
INSERT INTO "bamoki" VALUES(57,16,'57:16','ئایا کاتی ئەوە نەھاتووە بۆ بڕواداران کە دڵیان ڕاچڵەکێت وملکەچ بێ بۆ یاد وزیکری خوا وە بۆ ئەو قورئانەی کەناردویەتیە خوارەوە؟ وەك ئەوکەسانە (جولەکە وگاور) نەبن کە لەمەو پێش کتێبی ئاسمانیان پێدرا وە ماوەیەکی دوورو ودرێژیان بەسەردا تێپەڕی ئەمجا دڵیان ڕەق بوو وە زۆربەیان یاخی ولەسنوور دەرچووبوون');
INSERT INTO "bamoki" VALUES(57,17,'57:17','بێگومان بزانن کەخوا زەوی زیندوو دەکاتەوە دوای مردنی، بێگومان نیشانەو بەڵگەکانمان بۆ ڕوون کردونەتەوە تابیربکەنەوە');
INSERT INTO "bamoki" VALUES(57,18,'57:18','بێگومان پیاوان وژنانی خێرخواز ئەوانەش قەرز دەدەن بەخوا بەقەرزێکی چاك (یارمەتی ھەژاران دەدەن) چەند بەرامبەر دەیاندرێتەوە وە بۆ ئەوان ھەیە پاداشتی زۆر چاك');
INSERT INTO "bamoki" VALUES(57,19,'57:19','وە ئەوانەی باوەڕیان ھێناوە بەخوا وبەپێغەمبەرانی ئەوانە خۆیانن ڕاست گۆیان وەشەھیدان لەلای پەروەردگاریان، خاوەن پاداشت ونور وڕووناکی خۆیانن وە ئەوانەی کەبێ باوەڕبوون وبەڵگە ونیشانەکانی ئێمەیان بەدرۆ زانی ئەوانە ھاوەڵی ئاگری دۆزەخن');
INSERT INTO "bamoki" VALUES(57,20,'57:20','وە بزانن کەبێگومان ژیانی دونیا تەنھا گاڵتە وگەمە وڕازاندنەوە وخۆھەڵکێشانە لەنێوانتاندا وەلاف لێدانە بەدارایی ومنداڵی زۆرەوە، وەك بارانێك وایە کە حاسڵاتەکەی جووتیاران دڵخۆش بکات، پاشان ووشك ببێت وبەزەردی بیبینیت لە پاشان ببێت بە پووش وپەڵاش، لە ڕۆژی دوایشدا سزای توند وسەخت (ھەیە بۆ دونیا پەرستان) وە لێبوردن وڕەزامەندی ھەیە لەلایەن خواوە (بۆ خواناسان) وە ژیانی دونیا ھیچ نیە جگە لە ڕابواردنێکی خەڵەتێنەر نەبێت');
INSERT INTO "bamoki" VALUES(57,21,'57:21','(کەواتە) پێشبرکێ بکەن بۆ (دەست خستنی) لێخۆشبوون لەلایەن پەروەردگارتانەوە وە بۆ بەھەشتێك کە پانتاییەكەی بەوێنەی پانتایی ئاسمان وزەویە، ئامادەکراوە بۆ ئەوانەی باوەڕیان ھێناوە بەخوا وپێغەمبەرانی، ئەو (پاداشتە) بەخششێکی خواییە دەیدات بە ھەر کەس کە خۆی بیەوێت وە خوا خاوەنی بەخششی گەورەیە');
INSERT INTO "bamoki" VALUES(57,22,'57:22','ھیچ زیان و بەڵایەك ڕووی نەداوە نە لەزەوی و نە لە خـودی خۆتاندا ئیللا لە (لوح المحفوظ) دا نوسراوە پێش ئەوەی بەدی بھێنین بێگومان ئەوە بۆ خوا زۆر ئاسانە');
INSERT INTO "bamoki" VALUES(57,23,'57:23','تا خەفەت نەخۆن بۆ ئەوەی لەدەستتان چووە وە دڵخۆش ومەغرور نەبن بەوەی کە پێی داوون وەخوا ھیچ خۆ بەزل زانێکی شانازی کەری خۆش ناوێت');
INSERT INTO "bamoki" VALUES(57,24,'57:24','ئەوانەی چروکی وڕەزیلی دەکەن (لەماڵ بەخشین لەپێناو خوادا) وە فەرمان بەخەڵکیش دەکەن ڕەزیل وچروك بن وە ھەر کەس پشت ھەڵکات ئەوە بێگومان خوا ھەر خۆی بێ نیاز وسوپاسکراوە');
INSERT INTO "bamoki" VALUES(57,25,'57:25','سوێند بەخوا بەڕاستی ئێمە پێغەمبەرانی خۆمانمان ناردووە بە بەڵگە وموعجیزەی ڕوونەوە وە کتێبی (ئاسمانی) وتەرازوی دادمان لەگەڵدا ناردن بۆ ئەوەی خەڵکی بەدادگەری ڕەفتار بکەن (لەناو خۆیاندا) وە ئاسنمان دروست کرد کە ھێزێکی بەتینی توندی تێدایە بۆجەنگ کردن وە سوودێکی زۆریشی ھەیە بۆ خەڵکی (لەبەرکاھێنانی لە ھەموو جۆرە پیشەسازیە خزمەتگوزاریەکاندا) وە تا خوا دەری بخات چ کەسێك یارمەتی (ئاینی) خوا وپێغەمبەرانی دەدات کە پەنھانیشە، بێگومان خوا بەھێز ودەسەڵاتە');
INSERT INTO "bamoki" VALUES(57,26,'57:26','سوێند بەخوا بێگومان نوح وئیبراھیم مان ڕەوانەکرد وە لە ناو وەچەکانیشیاندا پێغەمبەرێتی وکتێبی ئاسمانیمان دانا ئەمجا ھەندێکیان ڕێنموونیان وەرگرت وە زۆریشیان یاخی وکافربوون');
INSERT INTO "bamoki" VALUES(57,27,'57:27','لە پاشان بەشوێن ئەواندا دیسان پێغەمبەرانی خۆمانمان نارد وە دواتر عیسای کوڕی مەریەممان نارد وەئینجیلمان پێ بەخشی وە لە دڵی ئەوانەدا کەشوێنی کەوتن سۆز وبەزەییمان دروست کرد وە گۆشە گیرییەك کە خۆیان دایان ھێنا بوو کە ئێمە ئەوەمان لەسەر پێویست نەکردبوون بەڵام خۆیان دایان ھێنا بۆ ڕەزامەندی خوا، کەچی وەك پێویست پارێزگاری ئەو گۆشەگیرییەیان نەکرد، ئێمەش بەخشیمان بەوانەیان کەباوەڕیان ھێنا بوو پاداشتی خۆیان. وە زۆریشیان لە سنوور دەرچوون');
INSERT INTO "bamoki" VALUES(57,28,'57:28','ئەی ئەو کەسانەی باوەڕتان ھێناوە لەخوا بترسن وباوەڕبھێنن بەپێغەمبەرەکەی، دوو بەشتان لەڕەحمەت وبەخششی خۆی پێ دەبەخشێت وە ڕووناکیەکتان دەداتێ کە ڕێنموونی وڕێی ڕاستی پێ دەرکەن وە لێتان خۆش دەبێت وەخوا لێ خۆشبووی میھرەبانە');
INSERT INTO "bamoki" VALUES(57,29,'57:29','تا خاوەن پەڕاوە ئاسمانیەکان بزانن کە دەسەڵاتیان نیە بەسەر ھیچێك لە بەخشیشی خوا وە ھەرچی بەخشش ھەیە بە دەست خوایە، دەیدات بە ھەر کەسێك کە خۆی بیەوێت وە خوا خاوەنی بەخششی زۆر گەورەیە');
INSERT INTO "bamoki" VALUES(58,1,'58:1','بەڕاستی خوا بیستی ووتەی ئەو ژنەی کە لەبارەی مێردەکەیەوە گفتوگۆی لەگەڵ ئەکردی وە سکاڵای خۆی بۆ لای خوا ئەبرد وە خوا گوێی لە ووتووێژی ھەردووکتان بوو بەڕاستی خوا بیسەر وبینایە');
INSERT INTO "bamoki" VALUES(58,2,'58:2','ئەوانەتان کە (ظهار) دەکەن لەگەڵ ژنەکانیاندا (پێیان دەڵێن وەك دایکمان وان) ئەوانە دایکی ئەوان نین، دایکی ئەوان ھەر ئەو ژنانەن کەئەوانیان لێ بووە وە بێگومان ئەوانە گوفتارێکی خراپی ناپەسەند دەڵێن ودرۆ دەکەن وە بەڕاستی خوا لێخۆشبو ولێبووردەیە');
INSERT INTO "bamoki" VALUES(58,3,'58:3','ئەوانەی (ظهار) دەکەن لەگەڵ ھاوسەرەکانیان لەپاشان پەشیمان دەبنەوەو (دەیانەوێت بچنە لایان) لەوەی کەووتویانە (واتە خێرا تەڵاقیان نادەن) ئەوە پێویستە بەندەیەك ئازاد بکەن لەپێش ئەوەی بەیەك بگەن (و بچنە لایان) ئەم کەفارەتەی (کە لەسەرتان پێویست کراوە) ئامۆژگاری دەکرێن پێی وە خوا شارەزا وئاگادارە بەوەی کە دەیکەن');
INSERT INTO "bamoki" VALUES(58,4,'58:4','ئەمجا ئەگەر یەکێك (بەندەی) دەست نەکەوت ئەوە با دوو مانگ لەسەریەک بەڕۆژوو بێت پێش ئەوەی بچنە لای یەك، ئەگەر یەکێك نەیتوانی (دوومانگ بەڕۆژوو بێت) ئەوە با نانی شەست ھەژار بدات، ئەو (کەفارەتانە کە لەسەرتان پێویست کراوە) بۆ ئەوەیە باوەڕ بکەن بەخوا وپێغەمبەری خوا (ملكەچی فەرمانی خوا وپێغەمبەری خوا بن) وە ئەوانە سنووری خوان وە بۆ بێ باوەڕان ھەیە سزای سەخت');
INSERT INTO "bamoki" VALUES(58,5,'58:5','بێگومان ئەوانەی دژایەتی وسەرپێچی خوا وپێغەمبەری خوا دەکەن ڕیسوا وسەرشۆر دەکرێن ھەر وەك ئەوانەی پێش ئەمان ڕیسوا وسەرشۆر کران (چۆن نافەرمانی دەکەن) لەکاتێکدا دڵنیان کە ئێمە بەڵگەی ڕوونمان ناردۆتە خوارەوە وە بۆ بێ باوەڕان سزای ڕیسواکەر ھەیە');
INSERT INTO "bamoki" VALUES(58,6,'58:6','لەڕۆژێکدا کەخوا ھەموویان تێکڕا زیندوو دەکاتەوە پاشان ھەواڵیان پێ دەدات بەوەی کە کردویانە کەخوا (ھەمووی) ژماردووە وە (بەڵام) خۆیان لەبیریان چووە وە خوا بەسەر ھەموو شتێکدا ئاگادارە');
INSERT INTO "bamoki" VALUES(58,7,'58:7','ئایا نەت زانیوە کە خوا دەزانێت چی لەئاسمانەکان وە چی لەزەویدایە، چپەی ھیچ سێ کەسێك نیە کە خوا چوارەمیان نەبێت وە چپەی ھیچ پێنج کەسێك نیە کە ئەو شەشەمیان نەبێت وە نەکەمتر لەو ژمارە نە زۆرتر نیە مەگەر خوایان لەگەڵدایە(بە زانیاری وئاگاداری) لەھەر جێگایەکدا بن پاشان لەڕۆژی دواییدا ئاگاداریان دەکاتەوە بەوەی کە کردوویانە، بەڕاستی خوا بەھەموو شتێك ئاگادار وزانایە');
INSERT INTO "bamoki" VALUES(58,8,'58:8','ئایا سەرنجت نەداوە لە ئەوانەی کە قەدەغە کران لە چپە، پاشان دەگەڕێنەوە بۆ ئەوەی لێیان قەدەغە کرا بوو وە لەگەڵ یەکتریدا بەچپە ئەدوێن دەربارەی گوناھو تاوان ودەست درێژی وبێ فەرمانی پێغەمبەریش صلى الله عليه وسلم وە کاتێ بێن بۆلات سەلامت لێ دەکەن بە شێوەیەك کە خوا بەو جۆرە سەلامی لێ نەکردوویت وە لەدڵی خۆیاندا دەڵێن: (ئەگەر پێغەمبەرە) بۆچی خوا سزامان نادات بە ھۆی ئەو قسانەی کە پێی دەڵێن، ئەوانە دۆزەخیان بەسە کە (بێگومان) دەچنە ناویەوە کە جێگایەکی زۆر خراپە');
INSERT INTO "bamoki" VALUES(58,9,'58:9','ئەی ئەوانەی کە بڕواتان ھێناوە کە چپە تان کرد بە چپە باسی گوناھو دەست درێژی وسەرپێچی پێغەمبەر مەکەن وە باسی چاکە وپارێزکاری ولەخواترسان بکەن، لەخوا بترسن (وخۆتان بپارێزن لە سزای ئەو خوایەی) کە ھەر لای ئەو کۆدەکرێنەوە');
INSERT INTO "bamoki" VALUES(58,10,'58:10','بێگومان چپە تەنھا لە شەیتانەوەیە تا ئەوانەی کە بڕوایان ھێناوە دڵتەنگ بکات، لەکاتێکدا ھیچ زیانیان پێ ناگەیەنێت مەگەر بە فەرمانی خوا نەبێت (دەبا)ئیماندارانیش تەنھا پشت بەخوا ببەستن');
INSERT INTO "bamoki" VALUES(58,11,'58:11','ئەی ئەو کەسانەی کە باوەڕتان ھێناوە کاتێك پێتان ووترا لە کۆڕ ودانیشتنەکاندا جێگە بکەنەوە (وفراوانی بکەن) ئێوەش فراوانی بکەن خواش گوشادی بە ئێوە دەدات وە کاتێك پێتان ووترا ھەستن ، ئێوەش ھەستن، خواش پلە وپایەی ئەو کەسانە بەرز دەکاتەوە لەئێوە کە بڕوایان ھێناوەوە ئەوانەی کە زانستیان پێ بەخشراوە بە چەند پلەیەک (پلەیان بەرز دەکاتەوە) وە خوا شارەزایە بەوەی دەیکەن');
INSERT INTO "bamoki" VALUES(58,12,'58:12','ئەی ئەو کەسانەی کە باوەڕتان ھێناوە ھەر کاتێ (ویستان) قسەی پەنھانی لەگەڵ پێغەمبەردا صلى الله عليه وسلم بکەن ئەوە پێش چپە کردنەکەتان خێرێکك بکەن ئەمە بۆ خۆتان چاکتر وپاکترە، ئەمجا ئەگەر ھیچتان نەبوو ئەوە بێگومان خوا لێخۆشبوو ومیھرەبانە');
INSERT INTO "bamoki" VALUES(58,13,'58:13','ئایا دەترسان (لە ھەژاری) پێش چپە کردنەکەتان خێرێك بکەن ئەمجا ئەگەر خێرەکەتان نەکرد وخواش تەوبەی لێ گیرا کردن ئەوسا نوێژ بکەن وزەکات بدەن وە گوێڕایەڵی خوا وپێغەمبەرەکەی بکەن وە خوا شارەزایە بەوەی کە دەیکەن');
INSERT INTO "bamoki" VALUES(58,14,'58:14','ئایا بۆ سەرنجێك نادەی لە ئەوانەی کە دۆستایەتی ئەکەن لەگەڵ ھۆزێك (ی جولەکە) کەخوا غەزەبی لێگرتوون ئەوان نە لەئێوەن ونە لەئەوانیشن وە سوێند دەخۆن بەدرۆ لە کاتێکدا خۆیان دەزانن (درۆ دەکەن)');
INSERT INTO "bamoki" VALUES(58,15,'58:15','خوا سزای دژواری بۆ ئامادە کردوون بەڕاستی ئەوەی ئەوان دەیانکرد کارێکی ناپەسەند بوو');
INSERT INTO "bamoki" VALUES(58,16,'58:16','(دوو ڕووەکان) سوێندەکانیان کردبووە قەڵغانی (درۆ وخراپەکانیان) وە ڕێگری (خەڵك)یان کرد لەڕێبازی خوا ئەمجا بۆ ئەوان ھەیە سزای ڕیسواکەر');
INSERT INTO "bamoki" VALUES(58,17,'58:17','ماڵ و منداڵیان ھیچ سوودێکی نیە بۆیان ئەوانە یار وھاودەمی ئاگرن تێیدا دەمێننەوە بەھەمیشەیی');
INSERT INTO "bamoki" VALUES(58,18,'58:18','لەڕۆژێکدا کە خوا ھەموویان زیندوو دەکاتەوە ئەوسا سوێندی بۆ دەخۆن ھەر چۆنێ سوێندیان دەخوارد بۆ ئێوە (لە دونیاندا) وە وادەزانن شتێکیان بە دەستەوەیە با بزانن بەڕاستی ھەر ئەوانن درۆزن');
INSERT INTO "bamoki" VALUES(58,19,'58:19','شەیتان بەسەریاندا زاڵ بووە (بەجۆرێك) کە یادی خوای لەبیر بردوونەتەوە، ئەوانە کۆمەڵ ودەستەی شەیتانن، با بزانن کە بەڕاستی ھەر کۆمەڵ ودەستەی شەیتان زەرەرۆمەندن');
INSERT INTO "bamoki" VALUES(58,20,'58:20','بەڕاستی ئەوانەی کە دوژمنایەتی خوا وپێغەمبەری خوا دەکەن ئەوانە لە ڕیسوا ترینی خەڵکن');
INSERT INTO "bamoki" VALUES(58,21,'58:21','خوا نووسیویەتی کە بێگومان من وپێغەمبەرانم سەردەکەوین، بەڕاستی خوا بەھێز وبە توانا وباڵادەستە');
INSERT INTO "bamoki" VALUES(58,22,'58:22','کەسانێك نابینیت کە باوەڕیان ھەبێت بە خوا وبەڕۆژی دوایی کە دۆستایەتی بکەن لەگەڵ ئەوانەی دژایەتی خوا وپێغەمبەری خوا دەکەن (واتە: نابێت باوەڕداران دۆستایەتی وپشتیوانی بێ باوەڕان بن) ھەر چەندە ئەو دوژمنانەش باوکیان بن یان کوڕ وبرا وخزمیان بن، ئەوانە خوا باوەڕی لە دڵیاندا چەسپاندووە بەھێزی کردوون وسەری خستون بە قورئان وئیمان لەلایەن خۆیەوە وە دەیانخاتە بەھەشتانێکەوە کە جۆگە ئاوی زۆر دەڕوات بەژێر درەختەکانیاندا بە ھەمیشەیی تێیدا دەمێننەوە، خوا لێیان ڕازی بووە، ئەوانیش لە خوا ڕازی بوون، ئەوانە کۆمەڵ ودەستەی خوان، ئاگاداربن کە تەنھا کۆمەڵ ودەستەی خودا ڕزگار وسەرفرازن');
INSERT INTO "bamoki" VALUES(59,1,'59:1','ھەرچی لە ئاسمانەکان وزەویدایە تەسبیحاتی خوا دەکات وە تەنھا ئەو زاڵ وباڵادەست وکاردروستە');
INSERT INTO "bamoki" VALUES(59,2,'59:2','خوا زاتێکە بێ باوەڕانی خاوەن کتێبی دەرکرد لە ماڵ وشوێنی خۆیان بۆ یەکەم کۆبوونەوە بە تەمانەبوون (ئەی موسوڵمانینە) کە (لە قەڵاکانیان) دەربچن خۆشیان وایان دەزانی کە قەڵاکانیان دەیانپارێزن لە (سزای) خوا بۆیە (سزای) خوایان بۆ ھات لە شوێنێکەوە کە گومانیان بۆی نەدەچوو وە خوا ترسێکی خستە ناو دڵیانەوە کە خانوەکانی خۆیانیان وێران دەکرد بە دەستی خۆیان وبەدەستی ئیمانداران ئەمجا پەند وەرگرن ئەی خاوەن بیرو ھۆشان');
INSERT INTO "bamoki" VALUES(59,3,'59:3','ئەگەر خوا دەربەدەری لەسەر بڕیار نەدانایە (لەسەر ئەو جولەکانە) بێگومان لە دونیادا سزای دەدان، لە ڕۆژی دواییشدا بۆ ئەوان ھەیە سزای ئاگری دۆزەخ');
INSERT INTO "bamoki" VALUES(59,4,'59:4','ئەم سزایە بە ھۆی ئەوەوە بوو دژایەتی خوا وپێغەمبەریان کرد وە ھەر کەسێك دژایەتی خوا بکات ئەوە بەڕاستی خوا تۆڵە سێنی بەتینە');
INSERT INTO "bamoki" VALUES(59,5,'59:5','ئەوەی بڕیتان لە دارخورما یا لێی گەڕان بە وەستاوی لەسەر بنەکانیان بمێننەوە ئەوە بە فەرمانی خوا بووە وە بۆ سەر شۆرکردنی لەسنوور دەرچووان بوو (مەبەست پێ جولەکەکانە)');
INSERT INTO "bamoki" VALUES(59,6,'59:6','ئەو (دەستکەوتانە) کەخوا سەندیەوە (بێ جەنگ کردن) بۆ پێغەمبەرەکەی لە ماڵی (بەنی نەضیر) ئێوە بۆ بەدەست ھێنانی (ئەو ماڵ وسامانەی جولەکە) ھیچ ئەسپ وحوشترێکیان تاونەداوە و(نەجەنگاون) بەڵکو خوا پێغەمبەرانی زاڵ دەکات بەسەر ھەرکەسدا کەبیەوێت وە خوا بەسەر ھەموو شتێکدا بە دەسەڵاتە');
INSERT INTO "bamoki" VALUES(59,7,'59:7','ئەوەی خوا دەست پێغەمبەری خست (بەبێ جەنگ) لە (سەروەت وسامانی) گوندنشینەکانی جولەکە ئەوە بۆ خواو بۆ پێغەمبەرە صلى الله عليه وسلم (واتە لە بەرژەوەندی گشتی موسڵماناندا خەرج دەکرێت) وە بۆ خزمانی (پێغەمبەر صلى الله عليه وسلم) وھەتیوان وە بۆ ھەژاران وڕێبوارانی کەم دەستە تا (ئەو ماڵ وسامانە) تەنھا لەناو دەوڵەمەندەکانتاندا دەست بە دەستی پێ نەکرێت وبەس وە ئەوەی پێغەمبەر صلى الله عليه وسلم پێی دان وەریبگرن وە ئەوەی لێتانی قەدەغەکرد وازی لێ بھێنن وە لە خوا بترسن بێگومان خوای گەورە سزای سەختە');
INSERT INTO "bamoki" VALUES(59,8,'59:8','(ئەو دەسکەوتەی لە بەنی نەضیر گیرا) بۆ نەدارانی کۆچکەرانە ئەوانەی کە دەرکران لەماڵ وسامانیان، فەزڵ وبەھرەی خوا وڕزامەندی ئەویان دەوێت وە یارمەتی (ئاینی) خوا وپێغەمبەری خوا دەدەن ھەر ئەوانن ڕاستگۆیان');
INSERT INTO "bamoki" VALUES(59,9,'59:9','ئەوانە (یاریدەدەرەکان) کە لەماڵ وشوێنی خۆیان بوون (لەشاری مەدینە) وباوەڕیان ھێنا پێش (کۆچکردنی برا موھاجرەکانیان) ئەوەی کۆچ بکات بۆ لایان خۆشیان دەوێ وە شك نابەن لە دڵ ودەروونی خۆیاندا ھیچ جۆرە حەسوودی وبەخیلیەك لەبەر ئەوەی بەشی کۆچکەران درا و(بەشی ئەوان نەدرا لەو ماڵ وسامانەی کە گیرا لەجولەکەکان) وە فەزڵی ئەوان ئەدەن بەسەر فەزڵی خۆیاندا ئەگەر چی خۆشیان زۆر پێویستیان پێی بێت وە ئەو کەسەی خۆی لە ڕژدی وبەخیلی دەروونی بپارێزێت (بەڕاستی) ھەر ئەوانن سەرفەراز وسەرکەوتوو');
INSERT INTO "bamoki" VALUES(59,10,'59:10','ئەو (باوەڕدارانەش) لەپاش ئەمانەوە ھاتوون ئەڵێن: ئەی پەروەردگارمان لێمان خۆشبە وە لەو برایانەشمان (خۆش ببە) کە پێشمان کەوتوون لەئیمان ھێناندا وە ھیچ ڕق وکینەیەك مەخەرە دڵمانەوە دەربارەی باوەڕادران ئەی پەروەردگارمان بەڕاستی ھەرتۆی دڵسۆز ومیھرەبان');
INSERT INTO "bamoki" VALUES(59,11,'59:11','ئایا سەرنجێکت نەداوە لەوانەی کە دووڕوو بوون ئەڵێن بەو برادەرانەیان کە بێ باوەڕ بوون لە (جوولەکە) خاوەن کتێبەکان ئەگەر دەربکرێن (لەماڵ وخاك وئاوتان) ئێمەش لەگەڵتاندا دەردەچین وە ھەرگیز گوێڕایەڵی کەس ناکەین لەدژی ئێوە وە ئەگەر جەنگتان لەگەڵ دا بکرێت بێگومان ئێمە یارمەتی ئێوە ئەدەین بێگومان خوا شایەتی ئەدات کە ئەوان درۆزنن');
INSERT INTO "bamoki" VALUES(59,12,'59:12','ئەگەر دەر بکرێن ئەوان لەگەڵیاندا دەرناچن وە ئەگەر جەنگیان لە دژ کرا یارمەتیان نادەن وە ئەگەر یارمەتیان بدەن بێگومان (ئەشکێن و) پشت ھەڵئەکەن پاشان سەرناخرێن');
INSERT INTO "bamoki" VALUES(59,13,'59:13','بەڕاستی (ئەی باوەڕداران) ئێوە ترستان زیاترە لەدڵی ئەواندا تاخوا، ئەوەش لەبەرئەوەیە کە بێگومان ئەوانە کەسانێکن تێناگەن');
INSERT INTO "bamoki" VALUES(59,14,'59:14','(جوولەکە) جەنگتان لەگەڵ ناکەن ھەموو پێکەوە، مەگەر لەچەند گوندێکی قایمی قەڵاییندا نەبێت یان لەپشتی دیوارەوە، دژایەتی نێوانیان زۆر سەختە، تۆ وادەزانی کە ئەوان کۆ وپێکەوەن لەکاتێکدا دڵیان جیا ولەیەك دوورە، ئەوە بەھۆی ئەوەوەیە کە ئەوان گەلێکی نەفامن');
INSERT INTO "bamoki" VALUES(59,15,'59:15','نموونەی (ئەم جوولەکانە) وەك نموونەی ئەوانەن کە لە پێش ئەماندا بوون بە ماوەیەکی کەم (مەبەست پێی موشریکەکانی قوڕەیش وجولەکەکانی بەنو قەینوقاعە) سەرئەنجامی کاری خۆیان چەشت وە بۆ ئەوان ھەیە سزای سەخت');
INSERT INTO "bamoki" VALUES(59,16,'59:16','(نموونەی ئەم ناپاکانە) وەك شەیتان وایە کە بەمرۆڤی ووت: بێ بڕوابە جا کە بێ بڕوا بوو (شەیتان) ووتی: بەڕاستی من بەریم لە تۆ بێگومان من لە خوای پەروەردگاری جیھانیان دەترسم');
INSERT INTO "bamoki" VALUES(59,17,'59:17','جا سەرئەنجامی ھەردووکیان ئەوەیە کە بێگومان ھەردوولا لە دۆزەخدا ئەبن بەھەمیشەیی وە ئەمەیە سزای ستەمکاران');
INSERT INTO "bamoki" VALUES(59,18,'59:18','ئەی ئەوانەی کە باوەڕتان ھێناوە لەخوا بترسن وە ھەموو کەسێك با سەرنج بدات (بزانێ) چی پێش خۆی ناردوە بۆ پاشەڕۆژی (لە کاروکردەوەی چاکە) وە لەخوا بترسن بەڕاستی خوا شارەزایە بەوەی کە دەیکەن');
INSERT INTO "bamoki" VALUES(59,19,'59:19','وەك ئەوانە مەبن کە خوایان لەبیرخۆیان بردەوە جا خواش ئەوانی لەبیر خۆیان بردوەتەوە ھەر ئەوانن دەرچوان لە فەرمانی خوا');
INSERT INTO "bamoki" VALUES(59,20,'59:20','دۆزەخیان و بەھەشتیان وەك یەك ویەکسان نین بەھەشتیان ڕزگار وسەرکەتوون');
INSERT INTO "bamoki" VALUES(59,21,'59:21','ئەگەر ئەم قورئانەمان دابەزاندایە بۆ سەر کێوێك (وە تێبگەشتایە لەوەی کە تێیدا ھاتووە) دەتبینی بەملکەچی ولەت لەت بووی لەترسی خوا ئەم جۆرە نموونانە دەیان ھێنینەوە بۆ خەڵکی بۆ ئەوەی بیربکەنەوە');
INSERT INTO "bamoki" VALUES(59,22,'59:22','خوا ھەر ئەو زاتەیە ھیچ پەرستراوێکی ڕاست نیە بێجگە ئەو، زانای ھەموو نھێنی وئاشکراکانە ھەر ئەوە بەخشندەی میھرەبان');
INSERT INTO "bamoki" VALUES(59,23,'59:23','ئەوە ئەو خوایەیە کە بێجگە لەو پەرستراوێکی ڕاست نیە پاشا وپاك وبێگەرد وئاشتیدەر و، دڵنیاکەرە وچاودێر و پارێزگار وزاڵ وباڵادەسەت وخاوەن شکۆ وشایستەی ھەموو گەورەییەكە، پاکی وبێگەردی بۆ خوایە لەو ھاوبەشانەی کە بۆی دائەنرێ');
INSERT INTO "bamoki" VALUES(59,24,'59:24','ھەر ئەوە خوا وبەدیھێنەر وداھێنەرو ووێنە کێشە، ھەر بۆ ئەوە ھەموو ناوێکی ھەرە جوان وپاك، ھەرچی لە ئاسمانەکان وزەوی دایە ستایشی ئەو دەکەن وە ھەر ئەوە زاڵ وباڵادەست وکار بەجێ');
INSERT INTO "bamoki" VALUES(60,1,'60:1','ئەی ئەو کەسانەی کەباوەڕتان ھێناوە، دوژمنانی من وخۆتان مەکەن بەدۆستی خۆتان دۆستایەتی خۆتانیان بۆ بنێرن لەکاتێکدا دڵنیاشن ئەوان بێ باوەڕن بەو ئاینە ڕاستەی کە بۆتان ھاتووە، پێغەمبەر صلى الله عليه وسلم وئێوەش دەردەکەن (لەبەر ئەوەی) کە باوەڕتان ھەیە بەخوای پەروەردگارتان جا ئەگەر دەرچوون لە ووڵاتی خۆتان بۆ تێکۆشان وجەنگ کردن لە ڕێی مندا وە بۆ بەدەستھێنانی خۆشنوودی من (دۆستایەتی ئەوان مەکەن) ڕازو نھێنی (باوەڕدارانیان) پێ دەڵێن بە دۆستایەتی، لە کاتێکدا من ئەزانم بەوەی شاردتانەوە وبەوەیشی ئاشکراتان کرد وە ھەرکەسێك لە ئێوە دۆستایەتیان بکات ئەوە بێگومان ڕێی ڕاستی وون کردووە');
INSERT INTO "bamoki" VALUES(60,2,'60:2','ئەگەر تووشتان ببن ئەبن بەدوژمنی ئێوە وە درێژ ئەکەن بۆتان دەستیان وزمانیان بەخراپی، ئاواتە خوازن کە ئێوە بێ باوەڕ بوونایە');
INSERT INTO "bamoki" VALUES(60,3,'60:3','نـەخزمانتان ونەمناڵانتان سوودتان پێ ناگەیەنن لەڕۆژی دواییدا (خوا ئێوە وخزمەکانتان) لە یەك جیادەکاتەوە وە خوا بینایە بەوەی کەدەیکەن');
INSERT INTO "bamoki" VALUES(60,4,'60:4','بەڕاستی بۆ ئێوە ھەیە سەرمەشقێکی جوان وچاك لە ئیبراھیم وئەوانەی لەگەڵیدا بوون کە بەگەلەکەی خۆیانیان ووت بەڕاستی ئێمە بێزار وبەرین لەئێوە وە لەو شتانەش کەدەیان پەرستن لەجیاتی خوا، ئێمە باوەڕمان نیە بە(ئاینی) ئێوە وە پەیدابوو لەنێوان ئێمەوئێوەدا دوژمنایەتی وکینە بۆ ھەمیشەیی تا کاتێك کەبڕوا بەخوای تاك وتەنیا دەھێنن جگە لەو قسەیەی ئیبراھیم کە بەباوکی ووت بێگومان داوای لێخۆشبوونت بۆ ئەکەم وە من ھیچم بەدەست نیە لەبەرانبەر خوادا بۆ تۆ، ئەی پەروەردگارمان تەنھا پشتمان بەتۆ بەستووە وە ھەر بۆ لای تۆ گەڕاوینەتەوە سەر ئەنجامیش ھەر بۆلای تۆیە');
INSERT INTO "bamoki" VALUES(60,5,'60:5','ئەی پەروەردگار نەمان کەیت بە مایەی گومڕایی ئەوانەی بێ باوەڕ بوون وە لێمان خۆشبە ئەی پەروەردگارمان بەڕاستی ھەر تۆیت زاڵ وباڵادەست وکاربەجێ');
INSERT INTO "bamoki" VALUES(60,6,'60:6','سوێند بەخوا بێگومان بۆ ئێوە ھەیە لەئیبراھیم وبڕوادارەکاندا سەر مەشقێکی جوان بۆ کەسێك کە ئومێد وھیوای ببێ بە خوا وبەڕۆژی دوای وەھەر کەسێك پشت ھەڵکات ئەوە بەڕاستی ھەر خوایە بێ نیاز ودەوڵەمەند وشایستەی ستایش');
INSERT INTO "bamoki" VALUES(60,7,'60:7','دڵنیا بن خوای گەورە دۆستایەتی وخۆشەویستی دروستی دەکات لەنێوان ئێوە وئەوانەی دوژمنایەتیتان لەگەڵ کردوون (بەئیمان ھێنانی زۆربەیان) وە خوا بەتوانایە وەخوا لێ خۆشبو ومیھرەبانە');
INSERT INTO "bamoki" VALUES(60,8,'60:8','خوا ڕێتان لێ ناگرێت لەگەڵ ئەوانەی کە جەنگیان لەگەڵ نەکردوون لەسەر ئەم دینە وە دەریان نەکردوون لەجێگا وشوێن وخاك ووڵاتی خۆتان کەچاکەیان لەگەڵ بکەن وەدادگەربن لەگەڵیاندا بەڕاستی خوا ئەوانەی خۆش ئەوێت کەدادگەرن');
INSERT INTO "bamoki" VALUES(60,9,'60:9','بێگومان خوا ڕێگریتان لێ دەکات تەنھا لەوانەی کە جەنگیان لەگەڵ کردوون لەسەر دین(وباوەڕەکەتان) وەدەریان کردوون لە جێگا وشوێن وخاك ووڵاتی خۆتان وەیارمەتیان داوە لەسەر دەرکردنتان (ڕێگریتان دەکات) لەوەی کەدۆستایەتیان لەگەڵ بکەن وبەدۆستیان دابنێن وە ھەر کەسێك دۆستایەتیان بکات ئا ئەوانە خۆیان ستەمکارن');
INSERT INTO "bamoki" VALUES(60,10,'60:10','ئەی ئەوانەی باوەڕتان ھێناوە، ئەگەر ئافرەتانی ئیماندار ھاتن بۆلاتان بە کۆچ کەری تاقیان بکەنەوە خوا بەباوەڕی ئەوان زاناترە، ئەمجا ئەگەر زانیتان ئەو (ئافرەتانە) باوەڕدارن ئەوە مەیانگێڕنەوە بۆلای (مێردە) بێ بڕواکانیان، نەئەو ئافرەتانە حەڵاڵن بۆ ئەو مێردە بێ باوەڕانە وە نە ئەو مێردانەش بۆ ئەو ئافرەتانە حەڵاڵن وە ئەوەی (مێردەکانیان) خەرجیان کردووە، بیاندەنەوە ھیچ گوناھێکتان لەسەر نیە کە ئەم ئافرەتانە مارە بکەن ئەگەر مارەییەکانیان بدەنێ، ژنە کافرە (بت پەرستەکان) لای خۆتان ڕامەگرن (ئەوانەی کە لە مەککە بەبێ باوەڕی ماونەتەوە) ئێوە داوای ئەوە بکەنەوە کەخەرجتان کردووە (بۆ ژنە کافرەکانتان کە ئێستە چوونەتە لای کافران) ئەوانیش با داوای ئەوە بکەن کە خەرجیان کردووە (بۆ ژنە موسوڵمان بوەکانیان کە ھاتونەتە لای ئێوە) ئەمە فەرمانی خوایە بڕیار ئەدا لەنێوانیاندا وە خوا زانا وکاربەجێیە');
INSERT INTO "bamoki" VALUES(60,11,'60:11','ئەگەر لە دەستتان چوو یەکێك لە خێزانەکانتان (بەچوونیان) بۆ لای بێ باوەڕان ئێوەش تۆڵەتان لێ سەندن(و غەنیمەتتان گرت) ئەمجا (لەو غەنیمەتە)بدەن بەوانەی کە ژنەکانیان ڕۆشتووە بە ئەندازەی ئەوەی کە خەرجیان کردووە وە بترسن لەو خوایەی کە ئێوە بڕواتان پێهێناوە');
INSERT INTO "bamoki" VALUES(60,12,'60:12','ئەی پێغەمبەر صلى الله عليه وسلم ھەر کاتێك ئافرەتانی ئیماندار ھاتن بۆلات کە پەیمانت لەگەڵ ببەستن لەسەر ئەوەی ھیچ شتێك نەکەن بەھاوبەشی خوا وە دزیی وداوێن پیسی نەکەن و وە منداڵانی خۆیان نەکوژن وە ھیچ جۆرە بوھتانێك ھەڵنەبەستن لەنێوان دەست وپێیاندا وە بێ فەرمانیت نەکەن لەھیچ کارێکی چاکەدا، تۆش پەیمانیان لەگەڵ ببەستە وە لەخوا داوای لێ خۆشبوونیان بۆبکە بەڕاستی خوا لێ خۆشبوو ومیھرەبانە');
INSERT INTO "bamoki" VALUES(60,13,'60:13','ئەی ئەو کەسانەی کە باوەڕتان ھێناوە کەسانێك کەخوا غەزەبی لێ گرتوون مەیکەن بە دۆست وپشتیوانی خۆتان بەڕاستی ئەوان بێ ھیوا بوون لە (پاداشتی) ڕۆژی دوایی ھەر چۆن بێ بڕوایان بێ ھیوان لە مردووی ناو گۆڕستان (کە زیندوو ببێتەوە)');
INSERT INTO "bamoki" VALUES(61,1,'61:1','تەسبیحاتی خوا دەکات ھەرچی لە ئاسمانەکان ولە زەویدایە وە ئەو زاڵ وباڵادەست وکار بەجێیە');
INSERT INTO "bamoki" VALUES(61,2,'61:2','ئەی ئەوانەی کە بڕواتان ھێناوە بۆچی ئەوەی ئەیڵێن ئەنجامی نادەن');
INSERT INTO "bamoki" VALUES(61,3,'61:3','زۆر مایەی ڕق و توڕەییە لای خوا کە قسەیەك بڵێن وکردەوەی پێنەكەن');
INSERT INTO "bamoki" VALUES(61,4,'61:4','بەڕاستی خوا کەسانێکی خۆش دەوێت کە بەیەك ڕیز دەجەنگن لەڕێی خوادا وەك کۆشکێکی قایمی چەسپیووی دامەزراو');
INSERT INTO "bamoki" VALUES(61,5,'61:5','(بیربکەرەوە) کاتێك کە مووسا بەگەلەکەی ووت: ئەی گەلەکەم بۆچی ئازارم دەدەن لە کاتێکدا بەدڵنیاییەوە دەزانن کە من نێردراوی خوام بۆلای ئێوە جا کاتێك لایاندا لەھەق خواش دڵی وەرگێڕان وە خوا ڕێنموونی گەلی لەڕێ دەرچوو ناکات');
INSERT INTO "bamoki" VALUES(61,6,'61:6','(بیربکەرەوە) کاتێك کە عیسای کوڕی مەریەم ووتی ئەی بەنو ئیسرائیل ( نەوەکانی یەعقوب) بێگومان من نێردراوی خوام بۆلای ئێوە، ئەو تەوراتەی کە لە پێش مندا ھاتووە بەڕاستی دائەنێم وە مژدەتان پێ دەدەم بە پێغەمبەرێك کە لەدوای من دێ وناوی ئەحمەدە ئەمجا کاتێك کە ئەو (پێغەمبەرە) یان بۆ ھات بە چەند نیشانەی ڕوون وئاشکرا وە ووتیان: ئەمە جادوویەکی ئاشکرایە');
INSERT INTO "bamoki" VALUES(61,7,'61:7','جا کێ لەو کەسە ستەمکارترە کە درۆ ھەڵببەستێت بەدەم خواوە لە کاتێکدا کە بۆ ئیسلام بانگ دەکرێت وە خوا ڕێنموونی گەلی ستەمكار ناکات');
INSERT INTO "bamoki" VALUES(61,8,'61:8','دەیانەوێت ڕووناکی دینی خوا بکوژێننەوە بە(فوی) دەمیان وە خوا ڕووناکی خۆی تەواو دەکات با بێ باوەڕانیش پێیان ناخۆش بێت');
INSERT INTO "bamoki" VALUES(61,9,'61:9','خوا ئەو زاتەیە کە پێغەمبەری خۆی ناردووە بە ڕێنموونیی وئاینێکی ڕاست ودروستەوە، ھەتا بەسەر ھەموو ئاینێکدا سەری بخاتھەرچەندە ھاوبەش پەیداکەرانیش پێیان ناخۆش بێت');
INSERT INTO "bamoki" VALUES(61,10,'61:10','ئەی ئەو کەسانەی کە باوەڕتان ھێناوە ئایا بازرگانیەکتان نیشان بدەم کە ڕزگارتان بکات لە سزایەکی پڕ لە ئازار');
INSERT INTO "bamoki" VALUES(61,11,'61:11','(بازرگانیەکەش ئەمەیە) باوەڕ بھێنن بەخوا وپێغەمبەرەکەی وە تێبکۆشن لە ڕێی خوادا بە سامان وگیانی خۆتان ئەمەتان باشترە بۆ خۆتان ئەگەر ئێوە بزانن');
INSERT INTO "bamoki" VALUES(61,12,'61:12','(ئەوسا خوا) لە گوناھەکانتان خۆش دەبێت وە (لە ڕۆژی دواییدا) دەتان خاتە باخاتێکەوە کە جۆگەی ئاوی زۆر بەژێریاندا دەڕوات (وە دەتانخاتە) خانوو کۆشکی زۆر پاك وخۆشەوە لە بەھەشتانی جێگای مانەوەی ھەمیشەیی دان ئەمەیە سەرکەوتنی گەورە');
INSERT INTO "bamoki" VALUES(61,13,'61:13','وە شتێکی تر کە حەزی لێ دەکەن کەیارمەتی دانە لە خواوە وە ڕزگارکردنێکی نزیك (وئازادکردنی شاری مەککە یە) وە (بەمە) مژدە بدە بەئیمانداران');
INSERT INTO "bamoki" VALUES(61,14,'61:14','ئەی ئەوانەی کە باوەڕتان ھێناوە ببن بە یارمەتی دەرانی (ئاینی) خوا ھەروەك عیسای کوڕی مەریەم ووتی بە ھاوڕێ تایبەتیەکانی خۆی: کێ یارمەتی دەری منە بۆ لای خوا، ھاوڕێکانی ووتیان ئێمە یارمەتی دەرانی (دینی) خواین، ئەمجا دەستەیەك لە بەنو ئیسرائیل باوەڕیان ھێنا وە دەستەیەکیشیان بێ باوەڕ بوون، ئێمەش پشتیوانی ئەوانەمان کرد کە باوەڕیان ھێنا لە بەرانبەر دوژمنەکانیاندا، بۆیە سەرکەوتن وزاڵ بوون بەسەریاندا');
INSERT INTO "bamoki" VALUES(62,1,'62:1','تەسبیحاتی خوا دەکات ئەوەی لە ئاسمانەکان وە ئەوەی لە زەوی دایە، (ئەو خوایەی) کە پاشایەکی پاك وبێگەردی زاڵ وباڵادەسەت وکار بەجێیە');
INSERT INTO "bamoki" VALUES(62,2,'62:2','خوا ئەو زاتەیە کە ناردوویەتی لە ناو نەخوێندەواراندا (عەرەبەکاندا) پێغەمبەرێك لە خۆیان دەیخوێنێتەوە بەسەریاندا نیشانەکانی خوا، پاکیان دەکات وە قورئان وفەرموودەی (پڕ سوود)یان فێردەکات، ھەر چەند ئەوان لەپێشدا لە گومڕاییەکی ئاشکرادا بوون');
INSERT INTO "bamoki" VALUES(62,3,'62:3','وە (ڕەوانەشی کردووە بۆ) گەلانی تری جگە لەوان کە ھێشتا پێیان نەگەیشتوون (و موسوڵمان نەبوون) وە خوا زاڵ وباڵادەسەت وکار دروستە');
INSERT INTO "bamoki" VALUES(62,4,'62:4','ئەوە بەھرەیەکی خوایە ئەیدا بە ھەر کەسێك کە(خۆی) بیەوێت وە خوا خاوەنی فەزڵ وبەخششی گەورەیە');
INSERT INTO "bamoki" VALUES(62,5,'62:5','نموونەی بەسەرھاتی ئەوانەی کە تەوراتیان درا بەسەردا لە پاشان ھەڵیان نەگرت (ڕەفتاریان پێ نەکرد) وەك گوێ درێژێکە کە کتێبێکی زۆری ھەڵگرتبێت (سوودی لێ نابینێ) ئای چەند خراپە نموونەی ئەو گەلەی کە نیشانەکانی خوایان بە درۆزانی وە خوا ڕێنمونی گەلی ستەمکاران ناکات');
INSERT INTO "bamoki" VALUES(62,6,'62:6','(ئەی موحەممەد صلى الله عليه وسلم) بڵێ: ئەی ئەوانەی کە لەسەر دینی جولەکەن ئەگەر پێتان وایە کە بەڕاستی ئێوە دۆستی خوان بێجگە لە خەڵکی تر کەوا بوو داوای مردن بکەن ئەگەر ئێوە ڕاست دەکەن');
INSERT INTO "bamoki" VALUES(62,7,'62:7','وە ئەوانە ھەرگیز داوای مردن ناکەن لەبەر ئەو (گوناھانەی) کە بەدەستی خۆیان پێشیان خستووە وە خوا ئاگادارە بەستەمكاران');
INSERT INTO "bamoki" VALUES(62,8,'62:8','بڵێ بەڕاستی ئەو مردنەی کە لێی ڕادەکەن بێگومان پێتان دەگات وتووشتان دەبێت، لەپاشان دەگەڕێنرێنەوە بۆ لای خوای زانا بەئاشکرا ونھێنیەکان، ئەمجا ھەواڵتان پێ دەدات بەوەی کە دەتانکرد');
INSERT INTO "bamoki" VALUES(62,9,'62:9','ئەی ئەوانەی کەباوەڕتان ھێناوە کاتێك کە بانگدرا بۆ نوێژ لە ڕۆژی ھەینیدا بەپەلە بڕۆن بۆ یادی خوا (و نوێژی ھەینی) و واز لەکڕین وفرۆشتن بھێنن، ئائەوە بۆ ئێوە چاکترە ئەگەر بزانن وتێبگەن');
INSERT INTO "bamoki" VALUES(62,10,'62:10','جا کاتێك کە نوێژی ھەینی تەواوبوو، ئیتر بڵاو ببنەوە بەسەر زەویدا وە داوای ڕزق وڕۆزیی خوا بکەن وە زۆر یادی خوا بکەن، بۆ ئەوەی ڕزگار وسەر فەرازببن');
INSERT INTO "bamoki" VALUES(62,11,'62:11','ھەر کارێکی بازرگانی، یان ھەر کارێکی بێھودە ببینن بڵاوەی لێ ئەکەن بۆلای ئەو بازرگانیە وە تۆ بەجێ دەهێڵن بە ڕاوەستاوی (لەسەر مینبەرەکەت)، (ئەی موحەممەد صلى الله عليه وسلم) بڵێ: ئەوەی لای خوایە چاکترە لەشتی بێهودە وبازرگانی وەخوا باشترینی ڕۆزیدەرانە');
INSERT INTO "bamoki" VALUES(63,1,'63:1','کاتێك کە دووڕوان بێنە لات دەڵێن شایەتی دەدەین کە بەڕاستی تۆ پێغەمبەری خوایت وە دیارە خوایش دەزانێت کە تۆ ڕەوانەکراوی ئەوی وەخوا شایەتی دەدات کە بێگومان دووڕوان درۆزنن');
INSERT INTO "bamoki" VALUES(63,2,'63:2','سوێندە (درۆ)کانی خۆیان کرد بەقەڵغان (بۆ خۆیان) ئەمجا ڕێگری (خەڵك)یان دەکرد لەڕێی خوا، بەڕاستی ئەوان کارێکی خراپیان دەکرد');
INSERT INTO "bamoki" VALUES(63,3,'63:3','ئەوەی (کەووترا) لەبەر ئەوە بوو کە بەڕاستی ئەوان باوەڕیان ھێنا ولەپاشان بێ باوەڕبوون، ئەمجا مۆر نرا بەسەر دڵیاندا، ئیتر ئەوان ھیچ تێ ناگەن');
INSERT INTO "bamoki" VALUES(63,4,'63:4','ھەر کاتێك دەیان بینیت لەش وقەڵافەتیان سەرسامت دەکات وە ئەگەر قسە بکەن گوێ لە قسەکەیان دەگرییت، (دوو ڕووان) ھەروەك داری ھەڵسپێردراو وان، وا گومان دەبەن ھەموو ھاوار وقیڕەیەك بۆ سەر ئەوانە، ھەر ئەوانن دوژمنی (ئێوە)، کەواتە خۆتیان لێ بپارێزە، خوا بیانکوژێت (یان خوا لەناویان بەرێت) چۆن وەردەگێڕرێن (لەحەق)');
INSERT INTO "bamoki" VALUES(63,5,'63:5','کاتێك پێیان بووترێت وەرن بۆ لای پێغەمبەری خوا تا داوای لێخۆشبوونتان بۆ بکات (سەرپێچی دەکەن) سەریان با دەدەن وە دەیان بینیت ڕوو وەرئەگێڕن لەکاتێکدا کە ئەوان خۆیان بەگەورە دەزانن');
INSERT INTO "bamoki" VALUES(63,6,'63:6','بۆ ئەوان یەکسانە داوای لێخۆشبوونیان بۆ بکەیت یان داوای لێخۆشبوونیان بۆ نەکەیت ھەرگیز خوا لێیان خۆش نابێت، بەڕاستی خوا ڕێنموونی گەلی لەڕێ لادەران ناکات');
INSERT INTO "bamoki" VALUES(63,7,'63:7','ئەوان ئەو کەسانەن کە دەڵێن: (ھیچ) مەبەخشن بە ئەوانەی کە لەگەڵ پێغەمبەردان تا بڵاوەی لێ بکەن ( و ئەم دینە بفەوتێت) لە کاتێکدا ھەرچی گەنجینەکانی ئاسمانەکان وزەویی ھەیە ھەمووی ھی خوایە، بەڵام دوو ڕووەکان نازانن');
INSERT INTO "bamoki" VALUES(63,8,'63:8','(دووڕوەکان) دەڵێن سوێندبێت ئەگەر گەڕاینەوە بۆ مەدینە، بێگومان دەسەڵاتدار وبەڕێزەکان (مەبەست پێی خۆیان بوو) لات وبێ ڕێزەکان لە مەدینە دەردەکەن (مەبەست پێی بڕواداران بوو) (خوای گەورەش بەرپەرچیان دەداتەوە ودەفەرموێت): ڕێز وگەورەیی تەنھا بۆ خوا وبۆ پێغەمبەری خوا وباوەڕدارانە بەڵام دووڕووان نازانن');
INSERT INTO "bamoki" VALUES(63,9,'63:9','ئەی ئەوانەی کە باوەڕتان ھێناوە ماڵ ومناڵانتان بێ ئاگاتان نەکەن لەیادی خوا، ھەر کەسێك وابکات ئا ئەوانە خۆیان خەسارۆمەندن');
INSERT INTO "bamoki" VALUES(63,10,'63:10','ببەخشن لەوەی ڕزق و ڕۆزیمان داوون، پێش ئەوەی مردن یەخە بە یەکێکتان بگرێت ئەوسا بڵێت: خوایە خۆزگە مردنی منت دوابخستایە بۆ ماوەیەکی نزیك جا ماڵم ببەخشیایە وە لە چاکەکاران بوومایە');
INSERT INTO "bamoki" VALUES(63,11,'63:11','ھەرگیز خوا ھیچ کەسێك دوا ناخات (لەمردن) کاتێك کەوەختی (مردنی) ھات وەخوا شارەزایە بەوەی کە دەیکەن');
INSERT INTO "bamoki" VALUES(64,1,'64:1','تەسبیحاتی خوا دەکات ئەوەی لە ئاسمانەکان وزەویدایە، دەسەڵاتی تەواو بۆ ئەوە وە ھەرچی ستایش ھەیە ھەمووی بۆ ئەوە وە ئەوە بەسەر ھەموو شتێکدا بەدەسەڵاتە');
INSERT INTO "bamoki" VALUES(64,2,'64:2','خوا ئەو زاتەیە کە ئێوەی دروستكردووە ئەمجا ھەندێکتان بێ باوەڕ وھەندێکی تریشتان باوەڕدارن وە خوا بینایە بەھەر شتێك کە ئەنجامی دەدەن');
INSERT INTO "bamoki" VALUES(64,3,'64:3','خوا ئاسمانەکان وزەوی دروست کردووە بەحەق وڕاستی لەجوانترین شێوەشدا وێنەی کێشاون وە سەرئەنجامیش ھەر بۆلای ئەوە (گەڕانەوە)');
INSERT INTO "bamoki" VALUES(64,4,'64:4','ھەرچی لە ئاسمانەکان وزەویدایە خوا دەیزانێت وە شارەزایە بەوەی دەیشارنەوە، وە بەوەیشی ئاشکرای دەکەن وە خوا زانایە بەوەی لەناو دڵ ودەروندایە');
INSERT INTO "bamoki" VALUES(64,5,'64:5','ئایا پێتان نەگەیشتووە ھەواڵی ئەوانەی کە بێ باوەڕ بوون لەمەو پێش، بۆیە چەشتیان تۆڵەی کاری خۆیان وە بۆ ئەوان ھەیە سزای سەخت');
INSERT INTO "bamoki" VALUES(64,6,'64:6','ئەو (سزایە) بێگومان بەھۆی ئەوەوەبوو کە پێغەمبەرەکانیان بۆھات بەبەڵگەی ئاشکراوە، کە چی دەیانووت چۆن ئەبێت ئادەمییەك لە خۆمان ڕێنمونیمان بکات، ئەمجا بێ بڕوا بوون وڕوویان وەرگێڕا، وە خواش بێ نیازیی نواند (لە باوەڕھێنانیان) وەخوا دەوڵەمەندە وشایستەی ستایشە');
INSERT INTO "bamoki" VALUES(64,7,'64:7','بێ باوەڕان وایان دەزانی کە (لە ڕۆژی دواییدا) زیندوو ناکرێنەوە (ئەی موحەممەد صلى الله عليه وسلم) پێیان بڵێ: بەڵێ سوێند بە پەروەردگارم بێگومان زیندوو دەکرێنەوە دڵنیابن پاشان ھەواڵتان پێ دەدرێت بەوەی کە کردووتانە وە ئەمە بۆ خوا (زۆر) ئاسانە');
INSERT INTO "bamoki" VALUES(64,8,'64:8','کەواتە باوەڕ بھێنن بەخوا وپێغەمبەرەکەی وبەو ڕووناکیەی دامان بەزاندووە (کە قورئانە) وە خوا ئاگادارە بەوەی کە دەیکەن');
INSERT INTO "bamoki" VALUES(64,9,'64:9','(پاداشتان دەداتەوە) لەڕۆژێکدا کە کۆتان دەکاتەوە بۆ ڕۆژی کۆکردنەوە، ئەوڕۆژە ڕۆژی زیانباریە وە ھەرکەسێك بڕوا بھێنێت بەخواوە کردەوەی چاك بکات، خوا چاوپۆشی دەکات لەگوناھەکانی ودەیانسڕێتەوە وەدەیخاتە ناو بەھەشتانێکەوە کە جۆگە ئاوی زۆر بەژێریاندا دەڕوات، تێیدا دەمێننەوە بەھەمیشەیی وھەتا ھەتایی، ئەوەیە بردنەوە وسەرکەوتنی گەورە');
INSERT INTO "bamoki" VALUES(64,10,'64:10','ئەوانەی کە بێ باوەڕبوون ونیشانە وبەڵگەکانی ئێمەیان بە درۆزانی ئەوانە ھاوەڵی ئاگرن، تێیدا دەمێننەوە بەھەمیشەیی وە سەر ئەنجامێکی زۆر خراپە');
INSERT INTO "bamoki" VALUES(64,11,'64:11','ھیچ ناڕەحەتیەك تووشی (خەڵك) نابێت مەگەر بەفەرمانی خوا نەبێت، ھەر کەسێك باوەڕ بھێنێت بەخوا، خوا ڕێنمونی دڵی دەکات وە خوا بەھەموو شت ئاگادارە');
INSERT INTO "bamoki" VALUES(64,12,'64:12','گوێڕایەڵی خوا وگوێڕایەڵی پێغەمبەر بکەن ئەوسا ئەگەر پشتان تێ کرد، جا ئەوەی لەسەر پێغەمبەری ئێمەیە تەنھا گەیاندنی ڕوون وئاشکرای (ئاینەکەیەتی)');
INSERT INTO "bamoki" VALUES(64,13,'64:13','خوا زاتێکە کە جگە لەو ھیچ پەرستراوێکی ڕاست نیە، دەی با باوەڕداران تەنھا پشت بەخوا ببەستن');
INSERT INTO "bamoki" VALUES(64,14,'64:14','ئەی ئەوانەی بڕواتان ھێناوە بەڕاستی ھەندێك لەژنان ومنداڵەکانتان دوژمنی ئێوەن (بەوەی دەیانەوێت ڕێگریتان بکەن لەڕێگەی خودا وپەشیمانتان بکەنەوە لەئاینەکەتان) جا خۆتانیان لێبپارێزن ( وئاگاداریان بن، بەگوێیان نەکەن) وە ئەگەر (لێیان) ببورن وە واز بھێنن (لێیان) وە (لێیان) خۆش ببن ئەوە بێگومان خواش لێخۆشبوو ومیھرەبانە');
INSERT INTO "bamoki" VALUES(64,15,'64:15','بێگومان سەروەت وسامان وماڵ ومنداڵتان ھۆی تاقیكردەوەتانن لەکاتێکدا خوا پاداشتی زۆر گەورەی لەلایە');
INSERT INTO "bamoki" VALUES(64,16,'64:16','کەوا بوو لەخوا بترسن وبەندایەتی ئەو بکەن ئەوەندەی کە دەتوانن وە گوێڕایەڵ وملکەچی بن وە ماڵ ببەخشن کە بۆ خۆتان چاکترە وە ئەوەی پارێزراو بێت لەچرووکی نەفسی خۆی ھەر ئەوانن سەرکەوتو وڕزگار');
INSERT INTO "bamoki" VALUES(64,17,'64:17','ئەگەر قەرز بدەن بەخوا بە قەرزێکی چاك (واتە بیبەخشن لەپێناو خوا) چەند بەرانبەری دەکاتەوە بۆتان وە لێتان خۆش دەبێ وە خوا سوپاسگوزار ولەسەرخۆ وبەئارامە');
INSERT INTO "bamoki" VALUES(64,18,'64:18','(ئەو زاتە پەروەردگارێکی) زانا وئاگادارە بە شتە پەنھان وئاشکراکان، زاڵ وباڵادەست وکاربەجێیە');
INSERT INTO "bamoki" VALUES(65,1,'65:1','ئەی پێغەمبەر صلى الله عليه وسلم (تۆ وئوممەتەکەت) ھەر کاتێك ویستان ژنەکانتان تەڵاق بدەن با تەڵاقیان بدەن لەکاتی عیدەیاندا (ئەو کاتەی لەحەیز پاك بوونەتەوە ولەگەڵیان دروست نەبوون) وە عیدەی (ژنەکانتان) بەووردی بژمێرن وە لەخوا بترسن کەپەروەردگارتانە، ئەو ژنانە دەرمەکەن لەماڵەکانی خۆیان (تا عیدەیان تەواو دەبێت) ئافرەتانیش با دەرنەچن (لە ماڵەکانیان) مەگەر کارێکی خراپی ئاشکرا بکەن وە ئەوانە سنوورەکانی خودان، ھەر کەسێك لەسنوورەکانی خوا دەربچێت ئەوە بێگومان ستەمی لەخۆی کردووە، تۆ چووزانی لەوانەیە خوا پێشی بھێنێت دوای ئەوە کار وبارودۆخێکی تر (و ژن وپیاوەکە بکەونەوە یەکتر وئاشتیان ببێتەوە)');
INSERT INTO "bamoki" VALUES(65,2,'65:2','ئەمجا کە گەیشتنە کاتی خۆیان (پاش تەڵاقێك، یان دوو تەڵاق) ئەوسا یان بیانھێڵنەوە بەباشی (بیانبەنەوە بۆ لای خۆتان بەشێوەیەکی چاك) یان لێیان جیا ببنەوە بەباشی وە دوو شاھیدی دادپەروەر بگرن لەخۆتان کە لەبەر خوا ئەو شاھیدیە بدەن، ئەوانەی (باسکران پەندێکن) کە ئامۆژگاری ئەوانەی پێ دەکرێ کە باوەڕیان ھەبێت بە خوا وڕۆژی دوایی وە ھەرکەسێك خواناس بێت، خوا دەرووی لێ دەکاتەو');
INSERT INTO "bamoki" VALUES(65,3,'65:3','وە رزق وڕۆزی دەدات لەلایەکەوەکە بەخەیاڵیدا نەھاتووە وبیری لێ نەکردوەتەوە وە ھەکەسێك پشت بەخوا ببەستێت ئەوە خوای بەسە، بەڕاستی خوا کار وفەرمانی خۆی بەئەنجام دەگەیەنێت، بێگومان خوا بۆ ھەموو شتێك ئەندازەیەکی دیاریكراوی داناوە');
INSERT INTO "bamoki" VALUES(65,4,'65:4','ئەوانەی کە بێ ھیوا دەبن لەکەوتنە حەیزەوە لە ژنانتان ئەگەر (دەربارەی زانینی عیددەیان) دوو دڵ بوون ئەوا عیدەیان سێ مانگە وە (ھەروا) ئەوانەش کە (لەبەر منداڵی) ھێشتا حەیزیان پێیدا نەھاتووە (عیددەیان ھەر سێ مانگە) وە ژنانی سك پڕ (دوو گیان) عیددەیان ئەو کاتەیە کە سکەکەیان دابنێن، ھەر کەسێك لە خوا بترسێت خوا کاری بۆ ئاسان دەکات');
INSERT INTO "bamoki" VALUES(65,5,'65:5','ئەوەی (کە باسکرا) فەرمانی خوایە کە بۆی ناردونەتە خوارەوە وە ھەر کەسێك خواناس ولەخواترس بێت گوناھەکانی بۆ دائەپۆشێ وئەیانسڕێتەوە وپاداشتی گەورەی دەداتەوە');
INSERT INTO "bamoki" VALUES(65,6,'65:6','(ژنانی تەڵاقدراو) دابنێن ونیشتەجێیان بکەن لەو شوێنەی کە (خۆتان) دادەنیشن ونیشتەجێ دەبن بە پێی توانای خۆتان وە زیانیان پێ مەگەیەنن تا تەنگیان پێ ھەڵچنن وە ئەگەر دووگیان بوون ئەوە خەرجیان بکێشن تا سکەکەیان دائەنێن، ئەوسا ئەگەر شییریان دا بە منداڵەکەتان ئەوا ئێوەش کرێیان بدەنێ وە لەناو خۆتاندا ڕاوێژ بکەن بەچاکی (دەربارەی منداڵەکە) وە ئەگەر تەنگیتان کرد(نەسازان لە کرێکەدا) ئەوە با ئافرەتێکی تر شیری بداتێ');
INSERT INTO "bamoki" VALUES(65,7,'65:7','بادەوڵەمەند بەپێی دەوڵەمەندیی نەفەقە بدات وە ھەر کەسێك کەم دەست وھەژار بوو با بەپێی ئەوەی کەخوا داویەتیە نەفەقە بدات، خوا داوا لەکەس ناکات مەگەر بە پێی ئەوە نەبێت کەپێی داوە (لە توانا) بێگومان خوا دوای تەنگانە ئاسانی دەهێنێت');
INSERT INTO "bamoki" VALUES(65,8,'65:8','زۆر لە گوندنشین وشارنشینەکان سەرپێچیان کرد لە فەرمانی پەروەردگار وپێغەمبەرەکانی، ئێمەش پرسینەوەمان لەگەڵ کردن بە پرسینەوەیەکی سەخت وە سزامان دان بەسزایەکی دژوار');
INSERT INTO "bamoki" VALUES(65,9,'65:9','ئیتر ئاکامی کردەوەی خۆیانیان چەشت وە سەرئەنجامی کاریان بوو بەزیانباریی وخەسارۆمەندی');
INSERT INTO "bamoki" VALUES(65,10,'65:10','خوا ئامادەی کردووە بۆیان سزایەکی سەخت، کەواتە لەخوا بترسن ئەی ئەو کەسانەی خاوەن ژیری وبیر وھۆشن، ئەوانەی باوەڕیان ھێناوە بێگومان خوا قورئانی بۆ دابەزاندون');
INSERT INTO "bamoki" VALUES(65,11,'65:11','(ھەروەك بۆی ناردون) پێغەمبەرێك کەدەخوێنێتەوە بۆتان ئایەتەکانی خوا کە ڕوون کەرەوەن تا دەربھێنێت ئەوانەی کە باوەڕیان ھێناوە وە کردەوەی چاکیان کردووە لەتاریکستانی (بێ دینی) بۆ ڕووناکی (دینداری) وە ھەر کەسێك باوەڕ بھێنێت بەخوای گەورە وە کار وکردەوەی چاك ئەنجام بدات ئەوە خوای گەورە دەیخاتە ناو بەھەشتانێکەوە کە بە ژێر درەختەکانیدا جۆگە ئاوی زۆر دەڕوات، تیادەمێننەوە بەھەمیشەیی، بێگومان خوا (لەوێ) ڕۆزی چاکی بۆ ئامادەکردووە');
INSERT INTO "bamoki" VALUES(65,12,'65:12','خوا زاتێکە کە حەوت ئاسمانی دروستكردووە وە بەوێنەی ئەوان زەویشی دروستكردووە، فەرمانی خوا لە نێوانیاندا دادەبەزێت تا چاك بزانن کە خوا بەسەر ھەموو شتێکدا بە دەسەڵات وبە توانایە وە بێگومان خوا (بەزانست وزانیاری) دەوری ھەموو شتێکی داوە (واتە: ئاگاداری ھەموو بوونەوەرە)');
INSERT INTO "bamoki" VALUES(66,1,'66:1','ئەی پێغەمبەر صلى الله عليه وسلم بۆچی شتێك لەخۆت حەرام دەکەیت کە خوا بۆی حەڵاڵ کردویت (مەبەست پێی خاتوو ماریە بوو) تۆ ڕەزامەندی ژنەکانت دەوێ لەکاتێکدا خوا لێخۆشبوو ومیھرەبانە');
INSERT INTO "bamoki" VALUES(66,2,'66:2','بێگومان خوا دایناوە بۆتان ھەڵوەشاندنەوەی سوێندەکانتان، خوا پشتیوان ودۆستی ئێوەیە وە ئەو زانا وکاردروستە');
INSERT INTO "bamoki" VALUES(66,3,'66:3','کاتێك کە (پێغەمبەر صلى الله عليه وسلم) بەنھێنی لای ھەندێ لەژنەکانی قسەیەکی درکاند ئەمجا کەئەو (ژنە) قسەکەی کرد (بۆ ھەوێکەی) خواش پێغەمبەری صلى الله عليه وسلم ئاگادار کرد، (پێغەمبەر صلى الله عليه وسلم) ھەندێك (لەقسەکان) ی باسکرد وبێ دەنگ بوو لە ھەندێکی تری، ئەمجا کە پێی ووت (ژنەکەی) ووتی: کێ ئەمەی پێ گەیاندی، فەرمووی خوای زانای ئاگادار پێی گەیاندم');
INSERT INTO "bamoki" VALUES(66,4,'66:4','(ئەی حەفصە وعائیشە) ئەگەر ھەردووکتان تەوبە بکەن وبگەڕێنەوە بۆلای خوا (جێی خۆیەتی) چونکە بەڕاستی دڵتان لایدا (لە حەق وڕاستی) وە ئەگەر پشتی یەکتر بگرن لەدژی پێغەمبەر صلى الله عليه وسلم (سەرکەوتوو نابن) چونکە بێگومان پشگیری ئەوە خوا وجوبرەیل وئیماندارە چاکەکانیش، دوای ئەوان فریشتەکانیش پشتیوانی ئەون');
INSERT INTO "bamoki" VALUES(66,5,'66:5','دڵنیابن ئەگەر ئێوە تەڵاق بدات، پەروەردگاری لەبری ئێوە ژنانێکی باشتر لەئێوەی دەداتێ، موسڵمانانی، بڕوادارانی، نزاکارانی، تەوبەکارانی، خواپەرستانی، ڕۆژووگران لە بێوەژنان وکیژان');
INSERT INTO "bamoki" VALUES(66,6,'66:6','ئەی ئەوانەی کەباوەڕتان ھێناوە خۆتان وکەس وکارتان بپارێزن لەئاگرێك کە سووتەمەنیەكەی ئادەمی وبەردە، سەرپەرشتی ئەو (ئاگری دۆزەخەن) چەند فریشتەیەکی زبر وتوند وتیژی وان کە نافەرمانی خوا ناکەن لەوەی کەفەرمانیان پێ ئەدات وە چ فەرمانێکیان پێ بدرێت دەیکەن');
INSERT INTO "bamoki" VALUES(66,7,'66:7','ئەی ئەوانەی کە بێ باوەڕ بوون ئەمڕۆ (کە ڕۆژی دواییە) عوزر وبیانوو مەھێننەوە، چونکە بێگومان پاداشت دەدرێنەوە لەسەر ئەوەی کە دەتانکرد');
INSERT INTO "bamoki" VALUES(66,8,'66:8','ئەی ئەوانەی کە باوەڕتان ھێناوە بگەڕێنەوە بۆ لای خوای گەورە بەگەڕانەوەیەکی ئێجگاریی (وڕاست ودروست، کەجارێکی تر نەگەڕێنەوە سەر ئەو گوناھە) تا پەروەردگارتان لە گوناھەکانتان خۆش ببێ وە بتان خاتە ناو باخاتێکەوە کە دەڕوا بەژێریاندا جۆگە ئاوی زۆر، لە ڕۆژێکدا کە خوای گەورە، پێغەمبەر صلى الله عليه وسلم وکەسانێ کە باوەڕیان ھێناوە لەگەڵیدا سەرشۆڕیان ناکات، ڕووناکی (ئیمان)یان دەڕوات لەپێشیانەوە ولەلای ڕاستیانەوە، دەڵێن: ئەی پەروەردگارمان ڕووناکییەكەمان بۆ زیاد وتەواوبکە وەلێمان خۆشبە، بەڕاستی ھەر تۆیت بەسەر ھەموو شتێکدا دەسەڵاتداری');
INSERT INTO "bamoki" VALUES(66,9,'66:9','ئەی پێغەمبەر صلى الله عليه وسلم بجەنگە لەگەڵ بێ باوەڕان ودوو ڕوواندا وەتوند وتییژبە لەگەڵیاندا وەجێیان دۆزەخە کە سەرئەنجامێکی زۆر خراپە');
INSERT INTO "bamoki" VALUES(66,10,'66:10','خوا بۆ بێ باوەڕان ژنی نووح وژنی لوطی بەنموونە ھێنایەوە کە لەژێر (نیکاحی) دوو بەندەدا بوون لەبەندە چاکەکانی ئێمە، ئەمجا خیانەتیان لەگەڵکردن (مەبەست پێی بێ باوەڕ بوون پێیان، نەك داوێن پیس بوو بن) کەچی (مێردەکانیان) نەیانتوانی شتێك لەسزای خوایان لێ دووربخەنەوە وەپێیان ووترا کەبڕۆنە ناو ئاگرەوە لەگەڵ ئەوانەی کە دەڕۆنە ناویەوە');
INSERT INTO "bamoki" VALUES(66,11,'66:11','(ھەروا) خوا نموونەیەکی تری ھێنایەوە بۆ ئەوانەی کە بڕوایان ھێناوە (بە) ژنی فیرعەون کاتێك کە ووتی پەروەردگارم بۆم دروست بکە لای خۆت ماڵێك لە بەھەشتدا وە ڕزگارم بکە لە فیرعون وکردەوەکانی وەڕزگارم بکە لە گەلی ستەمکاران');
INSERT INTO "bamoki" VALUES(66,12,'66:12','وە(بە) مەریەمی کچی عیمران (نموونە دەھێنێتەوە) ئەوەی کە داوێنێ خۆی پاك ڕاگرت، ئەوسا ئێمە فوومان پیاکرد لەڕۆحێك کە خۆمان دروستمان کردووە وە باوەڕی ھێنا بوو بە ووتەکانی پەروەردگاری خۆی وکتێبەکانی وە لەڕیزی گوێڕایەڵ وفەرمانبەرداراندا بوو');
INSERT INTO "bamoki" VALUES(67,1,'67:1','گەورە وپیرۆزە ئەو زاتەی کەھەرچی دەسەڵات وپاشایی ھەیە بەدەست ئەوە، وە ئەو دەسەڵاتدارە بەسەر ھەموو شتێکدا');
INSERT INTO "bamoki" VALUES(67,2,'67:2','ئەو زاتەی کە مردن وژیانی داھێناوە ودروستکردووە تا تاقیتان بکاتەوە کە کردەوەی کامتان چاکترە وە ھەر ئەوە باڵادەست ولێخۆشبوو');
INSERT INTO "bamoki" VALUES(67,3,'67:3','ئەو زاتەی کە حەوت ئاسمانی دروست کردووە یەك لەسەر یەك تۆ نابینیت (ئەی تەماشاکەر) لە دروست کراوەکانی خوای میھرەباندا ھیچ ناڕێکییەك، چاو بگێڕە وورد بەرەوە (بزانە) ئایا ھیچ ناتەواویەك دەبینیت');
INSERT INTO "bamoki" VALUES(67,4,'67:4','دووبارە چاو بگێڕە یەك لە دوای یەك چاووت بەڕیسوایی ونائومێدیی دەگەڕێتەوە بۆ لای خۆت وە ئەو (چاوەت) ماندووە وتاقەتی چووە');
INSERT INTO "bamoki" VALUES(67,5,'67:5','سوێندبێت بێگومان ئاسمانی دونیامان ڕازاندوەتەوە بەچرای زۆری(ئەستێرە) وە گێڕاومانن بەڕەجمی شەیتانەکان وە ئامادەمان کردووە بۆ ئەوان (لە قیامەتدا) سزای ئاگری ھەڵگیرساو');
INSERT INTO "bamoki" VALUES(67,6,'67:6','وە بۆ ئەوانەی کە بێ بڕوان بە پەروەردگاریان سزای دۆزەخ ئامادەیە، کە مەنزڵگایەکی زۆر خراپە');
INSERT INTO "bamoki" VALUES(67,7,'67:7','کاتێك کە فڕێدرانە ناویەوە لەو دۆزەخەوە لوورە ونرکەیەك دەبیستن وە ئەو ئەکوڵێ وجۆش دەخوات');
INSERT INTO "bamoki" VALUES(67,8,'67:8','نزیکە (دۆزەخ) لەڕقانا پارە پارە ببێت، ھەر کاتێك دەستەیەك فڕێ بدرێنە ناویەوە دەرگاوانەکانی دۆزەخ پرسیاریان لێ دەکەن، ئایا پێغەمبەرێکی ترسێنەرتان بۆ نەھاتووە (لە دونیادا)');
INSERT INTO "bamoki" VALUES(67,9,'67:9','دەڵێن بەڵێ بۆمان ھاتووە بەڵام بەدرۆمان دەزانی ودەمان ووت (پێی) خوا ھیچی نە ناردۆتە خوارەوە ئێوە بەس لە گومڕاییەکی گەورەدان');
INSERT INTO "bamoki" VALUES(67,10,'67:10','وە دەڵێن ئەگەر قسەی (پێغەمبەران) مان ببیستایە یان تێبگەشتینایە ئەمڕۆ لە ھاوەڵانی دۆزەخ نەدەبووین');
INSERT INTO "bamoki" VALUES(67,11,'67:11','ئەوسا ئیتر بەناچاری دان بەگوناھەکانیاندا دەنێن، کەوابوو دووریی لە میھرەبانی خوا بۆ ھاوەڵانی دۆزەخ');
INSERT INTO "bamoki" VALUES(67,12,'67:12','بێگومان ئەوانەی لە پەنھانیدا لە خوای خۆیان دەترسن، لێخۆشبوون وپاداشتی گەورەیان بۆ ھەیە');
INSERT INTO "bamoki" VALUES(67,13,'67:13','ئێوە قسە بە پەنھانی بکەن یان بە ئاشکرا (بۆ خوا یەکسانە چونکە) ئەو (خوای گەورە) ئاگادارە بەخودی (نھێنی) دڵان');
INSERT INTO "bamoki" VALUES(67,14,'67:14','ئایا ئەو خوایەی کە مەردوومی دروست کردووە ئاگای لە نھێنییان نیە، لە کاتێکدا کە ئەو ووردبین وئاگادارە');
INSERT INTO "bamoki" VALUES(67,15,'67:15','خوا ئەو زاتەیە کە زەوی بۆ ڕام کـردوون، دەی بگەڕێن بە ھەموو گۆشە وکەنارێکیدا وە لە ڕۆزی خوا بخۆن وە ھەر بۆلای ئەوە گەڕانەوە');
INSERT INTO "bamoki" VALUES(67,16,'67:16','ئایا دڵنیان لەو خوایەی کە لە ئاسمانە کە بتانباتە خوارەوە بەناخی زەویدا لە کاتێکدا کە زەوی (وەك دەریا) گێژ بخوات و (بێتە لەرزین)؟');
INSERT INTO "bamoki" VALUES(67,17,'67:17','ئایا دڵنیان لەو خوایەی کە لە ئاسمانە کە بای توندتان بنێرێتە سەر بە ووردە زیخەوە، ئەو دەمە تێدەگەن کە ھەڕەشەی ئێمە چۆنە؟');
INSERT INTO "bamoki" VALUES(67,18,'67:18','سوێند بەخوا بەڕاستی ئەو (گەلانەی) کە لەپێش ئەماندا بوون (پێغەمبەرانی ئێمەیان) بە درۆزن دانا، جا چۆن بوو بەگژدا چوونەوەی ئێمە (لەوان)');
INSERT INTO "bamoki" VALUES(67,19,'67:19','ئایا سەیری باڵندەکان ناکەن بە ژوور سەریانەوە بە باڵ کراوەیی وھێنانەوەیەك، ڕای نەگرتوون (بەو ئاسمانەوە) خوا نەبێت، کە ئەو بە ھەموو شتێك بیینایە');
INSERT INTO "bamoki" VALUES(67,20,'67:20','ئایا ئەوە کێیە ئەو کەسەی کە لەشکری ئێوەیە یارمەتتیان دەدات لە جیاتی خوای میھرەبان ؟! بێ بڕوایان ھەر لە فریو خواردنان');
INSERT INTO "bamoki" VALUES(67,21,'67:21','ئایا ئەوە کێیە ئەو کەسەی کە ڕۆزیتان دەدات ئەگەر خوا ڕۆزی خۆیتان لێبگرێتەوە؟ بەڵکو (بە ئارەزوو) درێژەیان دا بەسەرکەشی ودوور کەوتنەوە (لە ڕێی ڕاست)');
INSERT INTO "bamoki" VALUES(67,22,'67:22','ئایا ئەو کەسەی بڕوا وبەدەما بکەوێت باشتر ڕێ دەردەکات؟ یان یەکێك کە قنج وقیت بڕوا بەسەر ڕێگایەکی ڕاست وڕەواندا؟');
INSERT INTO "bamoki" VALUES(67,23,'67:23','(ئەی موحەممەد صلى الله عليه وسلم) بڵێ: ھەر خوایە کە درووستی کردوون و بەدیھێناون وە گوێ وچـاو ودڵی پێ داوون، زۆر بە دەگمەن سوپاسی (ئەم بەھرانە) دەکەن');
INSERT INTO "bamoki" VALUES(67,24,'67:24','بڵێ: ھەر خوایە کە ئێوەی بڵاوکردوەتەوە لە زەویدا وەھەر لای خواش کۆدەکرێنەوە (لەڕۆژی دواییدا)');
INSERT INTO "bamoki" VALUES(67,25,'67:25','(بێ بڕواکان بە گاڵتەوە) دەڵێن: ئەم بەڵێنە (بە ھاتنی ڕۆژی دوایی) کەی دەبێت ئەگەر ڕاست دەکەن');
INSERT INTO "bamoki" VALUES(67,26,'67:26','(ئەی موحەممەد صلى الله عليه وسلم) بڵێ : زانینی ئەو ڕۆژە ھەر لای خوایە وە من تەنھا ترسێنەرێکی ئاشکرام');
INSERT INTO "bamoki" VALUES(67,27,'67:27','کاتێك بێ بڕوایان ئەو سزایەیان بەدی کرد لە نزیك خۆیانەوە، تاڵ دەبێ ڕووخساری ئەوانەی کەبێ بڕوا بوون وە (پێیان) دەووترێت: ئەمەیە ئەو (ڕۆژە) ی کە داواتان دەکرد');
INSERT INTO "bamoki" VALUES(67,28,'67:28','بڵێ: پێم بڵێن ئەگەر خوا من وئەو (موسوڵمانانە) ی کە لەگەڵمان لەناومان ببات یا میھرەبانیمان لەگەڵ بکات دەی کێ پەنای بێ بڕوایان دەدات لە سزای سەخت');
INSERT INTO "bamoki" VALUES(67,29,'67:29','بڵێ: ھەر ئەوە خوای بەخشندە کە بڕوامان پێهێناوە وە ھەر بەو پشتمان بەستووە، جا لەمەولا دەزانن کە کێ لە گومڕاییەكی ئاشکرا دایە');
INSERT INTO "bamoki" VALUES(67,30,'67:30','بڵێ: پێم بڵێن ئەگەر ئاوی(خواردنەوە) تان ڕۆبچێ بە ناخی زەویدا، دەی کێ (بێجگە لە خوا) ئاوی زوڵاڵتان بۆ دەھێنێ');
INSERT INTO "bamoki" VALUES(68,1,'68:1','بە (نوون) دەخوێنرێتەوە، خوا بەماناکەی زاناترە بۆ زیاتر زانیاری سەیری سەرەتای سورەتی (البقرة) بکە، سوێند بە پێنووس وبەو دێڕانەی دەینوسن');
INSERT INTO "bamoki" VALUES(68,2,'68:2','تۆ لە سایەیی بەخششی خواوە شێت نیت');
INSERT INTO "bamoki" VALUES(68,3,'68:3','وە بێگومان پاداشتی نەبڕانەوە ھەیە بۆ تۆ');
INSERT INTO "bamoki" VALUES(68,4,'68:4','وە بەڕاستی تۆ لەسەر ڕەوشت وخویەکی ئێجگار بەرز وگەورەیت');
INSERT INTO "bamoki" VALUES(68,5,'68:5','جا بەم زووانە تۆش وئەوانیش دەبینن');
INSERT INTO "bamoki" VALUES(68,6,'68:6','کە کامتان شێتن');
INSERT INTO "bamoki" VALUES(68,7,'68:7','بێگومان پەروەردگاری تۆ لە ھەموو کەس زاناترە بەو کەسەی کە ڕێی ڕاستی خوای وون کردبێت وە ئەو زاناترە بەو کەسانەی کە ڕێی ڕاستیان گرتبێت');
INSERT INTO "bamoki" VALUES(68,8,'68:8','گوێڕایەڵی ئەوانە مەبە کە قسەکانت بە درۆ دەزانن');
INSERT INTO "bamoki" VALUES(68,9,'68:9','حەزیـان دەکرد کە تۆ نەرمی نیشان بدەیت، ئەوسا ئەوانیش نەرمیی (بەرانبەرت) دەنوێنن');
INSERT INTO "bamoki" VALUES(68,10,'68:10','گوێڕایەڵی مەکە بۆ ھیچ سوێند خۆرێکی پەست وڕیسوا (ی درۆزن)');
INSERT INTO "bamoki" VALUES(68,11,'68:11','پیتك گری ھاتووچۆکەر بۆ قسە ھێنان وبردن');
INSERT INTO "bamoki" VALUES(68,12,'68:12','زۆر ڕێگری کەر لە چاکە، ستەمکاری گوناھکار');
INSERT INTO "bamoki" VALUES(68,13,'68:13','دڵ ڕەقی کەللە شەق دوای ئەوەش زۆڵ');
INSERT INTO "bamoki" VALUES(68,14,'68:14','لەبەر ئەوەی کە خاوەنی دارایی وکوڕی زۆرە (ھەموو ئەم سیفەتانە وەك زانایان دەڵێن مەبەست پێی وەلیدی کوڕی موغەیرەیە)');
INSERT INTO "bamoki" VALUES(68,15,'68:15','کە ئایەتەکانی ئێمەی بە سەردا بخوێنرێتەوە دەڵێ: (ئەمانە) ئەفسانەی پێشینانن');
INSERT INTO "bamoki" VALUES(68,16,'68:16','بەم زووانە سەری لووتی داخ دەکەین');
INSERT INTO "bamoki" VALUES(68,17,'68:17','ئێمە تاقیمان کردنەوە ھەروەك لەمەوپێش خاوەنانی ئەو باخەمان تاقی کردەوە کە (لە شەودا) سوێندیان خوارد کە لەدەمی بەرەبەیاندا (بەروبوومەکەی) بڕنن (تا ھەژاران نەزانن)');
INSERT INTO "bamoki" VALUES(68,18,'68:18','ھیچی (بۆ ھەژاران) لێ دەرنەکەن');
INSERT INTO "bamoki" VALUES(68,19,'68:19','ئەمجا بەڵایەك لە لایەن پەروەردگارتەوە سوڕایەوە بە سەر باخەکەدا لە کاتێکدا کە ئەوان خەوتبوون');
INSERT INTO "bamoki" VALUES(68,20,'68:20','ئەوسا وەك باخێکی ڕنراوی لێهات');
INSERT INTO "bamoki" VALUES(68,21,'68:21','لە شەبەقدا (براکان) بانگی یەکترییان کرد');
INSERT INTO "bamoki" VALUES(68,22,'68:22','(ووتیان) : کە ھەستن بچن بۆ سەر باخەکەتان ئەگەر ئەتانەوێ بیڕنن');
INSERT INTO "bamoki" VALUES(68,23,'68:23','ئەمجا کەوتنە ڕێ وە ئەوان بەچرپە بە یەکتریان ئەووت');
INSERT INTO "bamoki" VALUES(68,24,'68:24','ئەمڕۆ ھیچ ھەژارێك نەیەت بەسەرتاندا لە ناو باخەکەدا');
INSERT INTO "bamoki" VALUES(68,25,'68:25','دەمەو بەیان ڕۆشتن لایان وابوو کە دەتوانن بێ بەشیان بکەن');
INSERT INTO "bamoki" VALUES(68,26,'68:26','کە باخەکەیان بینی (بە سوتاوی) ووتیان: ئێمە ڕێگامان وون کردووە (ئەمە باخەکەی ئێمە نیە)');
INSERT INTO "bamoki" VALUES(68,27,'68:27','نا، بەڵکو بەش بڕاو کراوین');
INSERT INTO "bamoki" VALUES(68,28,'68:28','برا ناوەنجی یەکەیان (لە ژیریدا) ووتی: مەگەر پێم نەووتن؟ بۆ چی یاد وسوپاسی خوا ناکەن');
INSERT INTO "bamoki" VALUES(68,29,'68:29','ھەموو ووتیان: پاك وبێگەردی بۆ پەروەردگارمانە (ستەم لەکەس ناکات) بێگومان ئێمە خۆمان ستەم کارین');
INSERT INTO "bamoki" VALUES(68,30,'68:30','ئەمجا ڕوویان کردە یەکتریی بەسەرزەنشت کردنەوە');
INSERT INTO "bamoki" VALUES(68,31,'68:31','ووتیان: وای بۆ خۆمان بەڕاستی لە خوا یاخی بووین');
INSERT INTO "bamoki" VALUES(68,32,'68:32','ئومێدەوارین کە خوا لە جیاتی ئەو باخە باشترمان بداتێ، بەڕاستی ئێمە ھیوامان ھەر بە پەروەردگاری خۆمانە');
INSERT INTO "bamoki" VALUES(68,33,'68:33','ئاوایە سزای (خوا لە جیھاندا) وە سزای ڕۆژی دوایی گەلێ گەورەترە خۆزگە بیانزانیایە');
INSERT INTO "bamoki" VALUES(68,34,'68:34','بێگومان بۆ لەخواترسان ھەیە لای پەروەردگاریان باخاتی پڕ لە ناز ونیعمەت');
INSERT INTO "bamoki" VALUES(68,35,'68:35','چۆن ئێمە موسوڵمان وبەدکاران وەك یەك لێ دەکەین؟');
INSERT INTO "bamoki" VALUES(68,36,'68:36','ئەوە چیتانە چۆن بڕیارێك دەدەن');
INSERT INTO "bamoki" VALUES(68,37,'68:37','نەخێر ئایا کتێبێکی (ئاسمانی) تان ھەیە ئەمەی تێدا دەخوێننەوە');
INSERT INTO "bamoki" VALUES(68,38,'68:38','وە لەو کتێبەدا ڕێتان پێدراوە چیتان بوێت ئەوە ھەڵبژێرن بۆ خۆتان؟');
INSERT INTO "bamoki" VALUES(68,39,'68:39','یان پەیمانی بەھێزتان ھەیە لە گەڵمان تا ڕۆژی دوایی کە ھەرچی ئێوە بڕیار بدەن بتاندرێتێ');
INSERT INTO "bamoki" VALUES(68,40,'68:40','بپرسە لێیان (بزانە) کامیان زامنی ئەم کارەن');
INSERT INTO "bamoki" VALUES(68,41,'68:41','ئاخۆ ھاوبیریان ھەیە (لەم قسەیەدا) دەبا ھاوبیرەکانیان بھێنن ئەگەر ڕاست دەکەن');
INSERT INTO "bamoki" VALUES(68,42,'68:42','لە ڕۆژێکدا ( کە قیامەت وناڕەحەتیەکانیەتی) خوای گەورە لاقی پیرۆزی دەردەخات (وەك لە فەرموودەی ئیمامی بوخاری وموسلیمدا ھاتووە، باوەڕمان پێیەتی بەبێ ھیچ لێچواندن ولێکدانەوەیەك، وە ئێمەش چۆنێتیەکەی نازانین) وە (بە تەوسەوە) بانگ دەکرێن بۆ سوژدە بردن (بۆ خوای گەورە) (کەچی) ناتوانن (سوژدە ببەن)');
INSERT INTO "bamoki" VALUES(68,43,'68:43','چاویان شۆڕ وڕیسوایی دایان دەگرێت، بەڕاستی جاران کە بانگ دەکران بۆ سوژدە و(نەیان دەبرد) کە ساغیش بوون');
INSERT INTO "bamoki" VALUES(68,44,'68:44','وازم لێ بھێنە بۆ ئەوانەی کە ئەم قورئانە بەدرۆ دەزانن بەرە بەرە کێشیان دەکەم بە ڕەنگێك کە پێ نەزانن');
INSERT INTO "bamoki" VALUES(68,45,'68:45','وە مۆڵەتیان دەدەم بەڕاستی پیلانی من تۆکمە وبەھێزە (لە بەرانبەر پیلانی ئەواندا)');
INSERT INTO "bamoki" VALUES(68,46,'68:46','ئاخۆ تۆ لە بێ بڕوایان داوای کرێی (کۆششی پێغەمبەرێتی) یان لێ دەکەیت، ئەوانیش لە بژاردنی قەرزداری گران بارن');
INSERT INTO "bamoki" VALUES(68,47,'68:47','یان ڕووپەڕەی غەیبیان لەلایە وە ئەوان لەبەری دەنووسنەوە');
INSERT INTO "bamoki" VALUES(68,48,'68:48','(ئەی موحەممەد صلى الله عليه وسلم) دان بە خۆتا بگرە وچاوەڕوانی فەرمانی پەروەردگارت بکە، وەك ھاوڕێی نەھەنگەکە مەبە (کە یونس پێغەمبەرە علیە السلام) کاتێك کە بانگی (خودای) کرد، ئەو تووڕە بوو (لە بێ بڕوایی گەلەکەی)');
INSERT INTO "bamoki" VALUES(68,49,'68:49','ئەگەر میھرەبانیەك لەلایەن پەروەردگاریەوە فریای نەکەوتایە فڕێ دەدرایە دەشتێکی ڕووتەن وە ئەو بەدناو دەبوو');
INSERT INTO "bamoki" VALUES(68,50,'68:50','ئەمجا پەروەردگاری ھەڵی بژاردەوە وە کردی (بە یەکێك) لە چاکەکاران');
INSERT INTO "bamoki" VALUES(68,51,'68:51','بەڕاستی نزیك بوو بێ بڕواکان ھەڵت خلیسکێنن بەچاوی (پیس) یان، لە کاتێکدا کە قورئان دەبیستن وە دەیانووت: بەڕاستی ئەم (پێغەمبەرە) شێتە');
INSERT INTO "bamoki" VALUES(68,52,'68:52','لە کاتێکدا کە ئەم قورئانە ھەر ئامۆژگاریە بۆ ھەموو جیھانیان');
INSERT INTO "bamoki" VALUES(69,1,'69:1','ھاتنی ڕۆژی دوایی (ھەق وڕاستە)');
INSERT INTO "bamoki" VALUES(69,2,'69:2','جا چی بێت ئەو ڕۆژە (ھەق وڕاستە)');
INSERT INTO "bamoki" VALUES(69,3,'69:3','تۆ چووزانیت ئەو ڕۆژە چیە');
INSERT INTO "bamoki" VALUES(69,4,'69:4','گەلی سەمود وعاد ئەو ڕۆژەیان بەدرۆ زانی (کە دڵەکان دادەخورپێنێ)');
INSERT INTO "bamoki" VALUES(69,5,'69:5','ئەمجا (گەلی) سەمود لە ناوبران بە نەعرەتەیەکی زۆر گەورە');
INSERT INTO "bamoki" VALUES(69,6,'69:6','وە (گەلی) عادیش لەناوبران بە بایەکی سەختی ساردی بەتینی بەگرمە');
INSERT INTO "bamoki" VALUES(69,7,'69:7','(خوا) زاڵی کرد بە سەریاندا حەوت شەو وھەشت ڕۆژ بەبێ ووچان (لەناوی بردن) ئەو گەلەت دەبینی لەو ماوەیەدا بێ گیان کەوتبوون وەك قەدی داری خورمای گەندەڵ (ناوڕزاویان لێهاتبوو)');
INSERT INTO "bamoki" VALUES(69,8,'69:8','ئایا کەس دەبینیت لەوان کە مابێتەوە؟');
INSERT INTO "bamoki" VALUES(69,9,'69:9','(ھەر بەو ڕەنگە) فیرعەون وگەلانی پێش ئەو لەگەڵ (دانیشتووانی دێھاتە) سەرە وژێربووەکان ھاتن بە گوناھو خراپەوە');
INSERT INTO "bamoki" VALUES(69,10,'69:10','وە بێ فەرمانیی پێغەمبەری پەروەردگاریان کرد ئەوسا گرتنی بە گرتنێکی زۆر سەخت');
INSERT INTO "bamoki" VALUES(69,11,'69:11','بەڕاستی ئێمە کاتێك کە ئاو(ی تۆفان) سەرکەوت (باپیرەکانی) ئێوەمان ھەڵگرت لە کەشتیدا ئەی بێ بڕوایانی مەککە');
INSERT INTO "bamoki" VALUES(69,12,'69:12','تا ئەم کردەوەیە بکەینە بیرھێنەرەوە بۆ ئێوە وە تا لە گوێی بگرێت گوێگرێکی تێگەیشتوو');
INSERT INTO "bamoki" VALUES(69,13,'69:13','ئەمجا کاتێك فوو کرا بە کەڕەنادا بە تاقە فوو کردنێك');
INSERT INTO "bamoki" VALUES(69,14,'69:14','وە زەوی وشاخەکان ھەڵگیران بە جارێك دران بەیەکدا (وتەخت کران)');
