INSERT INTO "bamoki" VALUES(69,16,'69:16','وە ئاسمان لەت دەبێت وە ئەو لەو ڕۆژەدا سست و (بێ کەڵکە)');
INSERT INTO "bamoki" VALUES(69,17,'69:17','وە فریشتەکان لە کەناری ئاسماندان وە لەو ڕۆژەدا ھەشت (فریشتەی بەھێز) عەرشی پەروەردگارت بەبان سەریانەوە ھەڵ دەگرن');
INSERT INTO "bamoki" VALUES(69,18,'69:18','لەو ڕۆژەدا (لە بەردەمی خوادا) ھەمووتان نیشان دەدرێن ھیچ (کردەوەیەکی) پەنھانیتان بە نھێنی نامێنێتەوە');
INSERT INTO "bamoki" VALUES(69,19,'69:19','ئەوسا ئەو کەسەی کە نامەی (کردەوەی) درایە دەستی ڕاستی دەڵێ: ھا نامەکەم بگرن وبیخوێننەوە');
INSERT INTO "bamoki" VALUES(69,20,'69:20','بێگومان من خۆم دەمزانی کە تووشی ئەم لێپرسینەوەیە دەبم');
INSERT INTO "bamoki" VALUES(69,21,'69:21','ئەو کەسە لە ژیانێکی پەسەندا دەبێت');
INSERT INTO "bamoki" VALUES(69,22,'69:22','لە بەھەشتێکی بەرزدا');
INSERT INTO "bamoki" VALUES(69,23,'69:23','کە میوەی نزیکە دەستە');
INSERT INTO "bamoki" VALUES(69,24,'69:24','(پێیان دەووترێت) بخۆن وبخۆنەوە، نۆشتان بێت بەھۆی ئەو چاکانەوە کە لە ڕۆژانی ڕابردوودا کردووتانە');
INSERT INTO "bamoki" VALUES(69,25,'69:25','بەڵام ئەو کەسەی کە نامە درایە دەستی چەپی دەڵێت: خۆزگە ھەر نامەم نەدرایەتێ');
INSERT INTO "bamoki" VALUES(69,26,'69:26','وە نەم زانیایە چیە (سەرئەنجامی) لێپرسینەوەم');
INSERT INTO "bamoki" VALUES(69,27,'69:27','خۆزگە مردنەکەم یەکجاری بووایە');
INSERT INTO "bamoki" VALUES(69,28,'69:28','سامانەکەم ھیچ قازانجێکی پێ نەگەیاندم');
INSERT INTO "bamoki" VALUES(69,29,'69:29','لەدەستم چوو دەسەڵاتەکەم');
INSERT INTO "bamoki" VALUES(69,30,'69:30','(ئەمجا بە چاودێرانی دۆزەخ دەووترێت) بیگرن زنجیر بکەنە دەست وگەردنی');
INSERT INTO "bamoki" VALUES(69,31,'69:31','لە پاشان بیبەن بۆ دۆزەخ (بیسوتێنن لە دۆزەخدا)');
INSERT INTO "bamoki" VALUES(69,32,'69:32','ئەمجا بە زنجیرێکی حەفتا گەزیی بیبەستنەوە');
INSERT INTO "bamoki" VALUES(69,33,'69:33','(چونکە ) بێگومان ئەو بڕوای بە خوای گەورە نەدەکرد');
INSERT INTO "bamoki" VALUES(69,34,'69:34','وە ھانی کەسی نەدەدا بۆ خۆراك دان بە ھەژاران');
INSERT INTO "bamoki" VALUES(69,35,'69:35','ئەمڕۆ ئەویش لێرە نە دڵسۆزێکی ھەیە (یارمەتی بدات)');
INSERT INTO "bamoki" VALUES(69,36,'69:36','وە نەخواردنێ (بیخوات) جگە لە کێم وزووخاوی جەستەی دۆزەخیەکان نەبێ');
INSERT INTO "bamoki" VALUES(69,37,'69:37','کە (کەس) نایخوات گوناھباران نەبێت');
INSERT INTO "bamoki" VALUES(69,38,'69:38','جا سوێند بەو شتانەی کە دەیان بینن');
INSERT INTO "bamoki" VALUES(69,39,'69:39','وە بەوەش کە نایان بینن');
INSERT INTO "bamoki" VALUES(69,40,'69:40','بێگومان ئەم قورئانە فەرموودەی ڕەوانە کراوێکی بەڕێزو گەورەیە (لە خواوە بۆتان دەهێنێـت)');
INSERT INTO "bamoki" VALUES(69,41,'69:41','وە ئەم (قورئانە) قسەی شاعیر نیە، زۆر کەم بڕوا دەھێنن (بە قورئان)');
INSERT INTO "bamoki" VALUES(69,42,'69:42','وە قسەی فاڵچی وزێوانیش نیە، زۆر کەم بیر دەکەنەوە');
INSERT INTO "bamoki" VALUES(69,43,'69:43','(ئەم قورئانە) نێرراوەتە خوارەوە لە پەروەردگاری جیھانیانەوە');
INSERT INTO "bamoki" VALUES(69,44,'69:44','ئەگەر (موحەممەد صلى الله عليه وسلم) بەدەم ئێمەوە ھەندێك (ووتەی) ھەڵببەستایە');
INSERT INTO "bamoki" VALUES(69,45,'69:45','بێگومان ئێمە بەدەستی خۆمان تۆڵەمان لێ دەسەند');
INSERT INTO "bamoki" VALUES(69,46,'69:46','لە پاشان ڕەگی دڵیمان دەبڕی');
INSERT INTO "bamoki" VALUES(69,47,'69:47','وە کەس لە ئێوە نەیدەتوانی بەرگری لێ بکات');
INSERT INTO "bamoki" VALUES(69,48,'69:48','وە بێگومان ئەم قورئانە ئامۆژگاری وپەندە بۆ پارێزکاران ولەخواترسان');
INSERT INTO "bamoki" VALUES(69,49,'69:49','وە بێگومان ئێمە دەزانین کە ھەندێکتان قورئان بە درۆ دەزانن');
INSERT INTO "bamoki" VALUES(69,50,'69:50','وە بێگومان ئەم قورئانە ھۆی پەشیمانی بێ بڕوایانە');
INSERT INTO "bamoki" VALUES(69,51,'69:51','وە بەڕاستی قورئان ڕاست ودرووست ودڵنیا بەخشە');
INSERT INTO "bamoki" VALUES(69,52,'69:52','کەواتە: (ئەی موحەممەد صلى الله عليه وسلم) تۆش ھەمیشە یادی پەروەردگاری گەورەی خۆت بکە');
INSERT INTO "bamoki" VALUES(70,1,'70:1','داواکارێك داوای سزایەکی (خوایی) کرد کە (ئەو سزایە) ھەر ڕوو دەدات');
INSERT INTO "bamoki" VALUES(70,2,'70:2','بۆ بێ باوەڕان نیە هیچ بەرگریكارێك (کە بیان پارێزێت)');
INSERT INTO "bamoki" VALUES(70,3,'70:3','لە (سزای) خوای خاوەن پلەو پایەی زۆر بەرز');
INSERT INTO "bamoki" VALUES(70,4,'70:4','فریشتە وجوبرەیل سەر دەکەون بۆ لای خوا لە ڕۆژێکدا کە ئەندازەکەی پەنجا ھەزار ساڵە');
INSERT INTO "bamoki" VALUES(70,5,'70:5','کەوا بوو خۆت ڕابگرە (ئەی موحەممەد صلى الله عليه وسلم) بە خۆڕاگرتنێکی چاك وجوان');
INSERT INTO "bamoki" VALUES(70,6,'70:6','بەڕاستی ئەو بێ بڕوایانە ئەو ڕۆژە بە دوور دەبینن');
INSERT INTO "bamoki" VALUES(70,7,'70:7','ئێمەیش زۆر بە نزیكی دەبینین');
INSERT INTO "bamoki" VALUES(70,8,'70:8','ئەو ڕۆژەی کە ئاسمان وەك مسی تواوە (یان خڵتەی زەیتی) لێ دێت');
INSERT INTO "bamoki" VALUES(70,9,'70:9','کێوەکانیش وەك خوری شیکراوەیان لێ دێت');
INSERT INTO "bamoki" VALUES(70,10,'70:10','وە ھیچ کەسێك ناپرسێتەوە لە ھیچ کەسێکی خۆشەویستی خۆی');
INSERT INTO "bamoki" VALUES(70,11,'70:11','کە نیشانی یەکتری دەدرێن، تاوانبار ئاوات دەخوازێت کە (قوربانی) بدایە لەبری ڕزگار بوونی خۆی لە سزای ئەو ڕۆژە بە کوڕەکانی');
INSERT INTO "bamoki" VALUES(70,12,'70:12','بەژن وبە براکانی');
INSERT INTO "bamoki" VALUES(70,13,'70:13','بە ھـۆزەکەشیەوە کە پەنایان دەدا');
INSERT INTO "bamoki" VALUES(70,14,'70:14','وە بە ھەرچی کەس لەسەر زەویە (ھەمووی بدایە و) لەپاشان ھەر خۆی ڕزگاری ببێت');
INSERT INTO "bamoki" VALUES(70,15,'70:15','نەخێر (وا نیە) بەڕاستی ئاگری دۆزەخ بە بڵێسەیە');
INSERT INTO "bamoki" VALUES(70,16,'70:16','داماڵێنەری پێستی سەرە');
INSERT INTO "bamoki" VALUES(70,17,'70:17','(ئەو ئاگرە) بانگی کەسێك دەکات کە پشتی ھەڵکردبێ لە خواو ڕووی وەرگێڕابێت');
INSERT INTO "bamoki" VALUES(70,18,'70:18','وە سامانی کۆ کردبێتەوە وھەڵی گرتبێت');
INSERT INTO "bamoki" VALUES(70,19,'70:19','بێگومان ئادەمی دروست کراوە بە چاو چنۆکێکی تەنگەتیلکە');
INSERT INTO "bamoki" VALUES(70,20,'70:20','کە ناخۆشیەكی تووش ببێت ھاوار وناڵە دەست پێ دەکات');
INSERT INTO "bamoki" VALUES(70,21,'70:21','کە خێر وسامانیش ڕووی تێ بکات ئەی گرێتەوە (لە خەڵکی)');
INSERT INTO "bamoki" VALUES(70,22,'70:22','جگە نوێژگەران نەبێت');
INSERT INTO "bamoki" VALUES(70,23,'70:23','ئەوانە کە بەردەوامن لەسەر نوێژەکانیان');
INSERT INTO "bamoki" VALUES(70,24,'70:24','ئەوانەی کە بەشێکی دیاریكراو لە ماڵ وسامانیاندا ھەیە (تەرخانكراوە)');
INSERT INTO "bamoki" VALUES(70,25,'70:25','بۆ ھەژاری داواکەر وبێ نەوای داوانەکەر');
INSERT INTO "bamoki" VALUES(70,26,'70:26','وە ئەوانەی کە باوەڕیان بە ڕۆژی پاداشت ھەیە');
INSERT INTO "bamoki" VALUES(70,27,'70:27','وە ئەوانەی کە لە سزای پەروەردگاری خۆیان دەترسن');
INSERT INTO "bamoki" VALUES(70,28,'70:28','چونکە بەڕاستی سزای پەروەردگاری ئەوان کەس لێی دڵنیا نیە');
INSERT INTO "bamoki" VALUES(70,29,'70:29','وە ئەوانەی کە ئاگاداری داوێنی خۆیان دەکەن');
INSERT INTO "bamoki" VALUES(70,30,'70:30','لەگەڵ ژنانی خۆیان نەبێت یا کەنیزەکێك کە بووبن بە خاوەنیان، بێگومان ئەوانە لۆمە کراو نین');
INSERT INTO "bamoki" VALUES(70,31,'70:31','ئەمجا ھەر کەسێك دوای شتی تر بکەوێت، ئەو کەسانە ئەوانەن کە دەست درێژکەرن');
INSERT INTO "bamoki" VALUES(70,32,'70:32','وە ئەوانەی کە چاودێری سپاردە وپەیمانی خۆیان دەکەن');
INSERT INTO "bamoki" VALUES(70,33,'70:33','وە ئەوانەی کە شایەتی خۆیان بە تەواوی دەدەن');
INSERT INTO "bamoki" VALUES(70,34,'70:34','وە ئەوانەی کە پارێزگاری نوێژیان دەکەن');
INSERT INTO "bamoki" VALUES(70,35,'70:35','ئەوانە (کە باس کران) ڕێز دەگیرێن لە باخاتی بەھەشتدا');
INSERT INTO "bamoki" VALUES(70,36,'70:36','دەی چیانە ئەو بێ بڕوایانە (کە تۆ دەرئەکەویت ئەی موحەممەد صلى الله عليه وسلم) بەپەلە دێن بۆ لات');
INSERT INTO "bamoki" VALUES(70,37,'70:37','لەلای ڕاست وچەپتەوە دەستە دەستە دەبن');
INSERT INTO "bamoki" VALUES(70,38,'70:38','ئایا ھەموو یەکێك لەوان بەتەمایە کە بخرێتە بەھەشتی پڕ لە ناز ونیعمەتەوە');
INSERT INTO "bamoki" VALUES(70,39,'70:39','وا نیە: (وەك بەتەمان) بەڕاستی ئێمە درووستمان کردوون لەوەی کە دەیزانن');
INSERT INTO "bamoki" VALUES(70,40,'70:40','جا سوێند بە پەروەردگاری ڕۆژھەڵاتەکان وڕۆژئاواکان، بێگومان ئێمە بە دەسەڵاتین');
INSERT INTO "bamoki" VALUES(70,41,'70:41','لە سەر ئەوەی کە بیانگۆڕین وباشتر لەوان بھێنین وە کەس لە دەستمان دەرناچێت');
INSERT INTO "bamoki" VALUES(70,42,'70:42','ئەمجا وازیان لێ بھێنە، با لە بێباوەڕی وقسەی ھیچ وپووچ و گاڵتەی خۆیاندا ڕۆبچن، تا دووچاری ئەو ڕۆژەی خۆیان دەبن کە بەڵێنیان پێ دراوە');
INSERT INTO "bamoki" VALUES(70,43,'70:43','ئەو ڕۆژەی کە بەپەلە لە گۆڕەکانیان دێنـە دەرەوە وەك ئەوەی کە بۆ لای بتەکانیان ڕابـکەن');
INSERT INTO "bamoki" VALUES(70,44,'70:44','چاویان شۆڕو (سەر بەرە وژێرە) ڕیسوایی وشەرمەزاری دایان دەگرێت، ئەمە ئەو ڕۆژەیە کە لەوەوپێش بەڵێنیان پێ درابوو');
INSERT INTO "bamoki" VALUES(71,1,'71:1','بێگومان ئێمە نوحمان نارد بۆ لای ھۆزەکەی (وە پێمان ووت) کە ھۆزەکەت بێدارکەرەوە وبیانترسێنە پێش ئەوەی کە سزای سەختیان تووش ببێت');
INSERT INTO "bamoki" VALUES(71,2,'71:2','(نوح) ووتی: ئەی گەلی خۆم بەڕاستی من (پێغەمبەرو) ترسێنەرێکی ئاشکرام بۆ ئێوە');
INSERT INTO "bamoki" VALUES(71,3,'71:3','کە ھەر خوا بپەرستن ولێی بترسن وە فەرمانبەرداری من بکەن');
INSERT INTO "bamoki" VALUES(71,4,'71:4','تا خوا لە گوناھەکانتان خۆش ببێت وە (بە زیندوویی) دەتان ھێڵێتەوە تا کاتی دیاری کراوی مردنتان، بێگومان کاتێ سزای خوا کە ھات دوا ناکەوێت ئەگەر بتانزانیایە');
INSERT INTO "bamoki" VALUES(71,5,'71:5','ووتی: ئەی پەروەردگارم من گەلەکەی خۆمم (بە پەنھانی) بانگ کرد بە شەو وبە ڕۆژ');
INSERT INTO "bamoki" VALUES(71,6,'71:6','وە هەتا بانگم دەکردن ئەوان زۆرتر دوور ئەکەوتنەوە لێم وھەڵئەھاتن');
INSERT INTO "bamoki" VALUES(71,7,'71:7','وە بێگومان من ھەر کات بانگم دەکردن (بۆ بڕوا ھێنان) تا لێیان خۆش ببیت ئەوان پەنجەکانیان دەکرد بە گوێچکەكانیاندا وە پۆشاکەکانیان دەدا بە سەرو چاویاندا وە بەردەوام دەبوون لە بێ باوەڕیدا، وە سەرکەشییان دەکرد چۆن سەرکەشییەك');
INSERT INTO "bamoki" VALUES(71,8,'71:8','لەپاشان بە ئاشکرا بانگم کردن');
INSERT INTO "bamoki" VALUES(71,9,'71:9','ئەمجا (دەمێك) بۆم ئاشکرا دەکردن، (دەمێك) بۆم پەنھان دەکردن بە پەنھان کردنێکی تەواو');
INSERT INTO "bamoki" VALUES(71,10,'71:10','ئەوسا پێم ووتن: داوای لێخۆشبوون بکەن لە پەروەردگارتان بەڕاستی پەروەردگارتان زۆر لێبووردەیە');
INSERT INTO "bamoki" VALUES(71,11,'71:11','(ئەمجا گەر وا بکەن) بارانی خوڕەم ولێزمەتان بۆ دەبارێنێ');
INSERT INTO "bamoki" VALUES(71,12,'71:12','وە سامان وکوڕی زۆرتان پێ دەبەخشێت وە باخ وباخاتی زۆرتان پێ دەدات وە جۆگە ئاوی زۆرتان دەداتێ');
INSERT INTO "bamoki" VALUES(71,13,'71:13','ئەوە چیتانە ناترسن لە شکۆ وگەورەیی خوا؟');
INSERT INTO "bamoki" VALUES(71,14,'71:14','لە کاتێکدا کە دروستی کردوون بە چەند قۆناغێك');
INSERT INTO "bamoki" VALUES(71,15,'71:15','ئایا نابینن کە خوا چۆن حەوت ئاسمانی دروستكردووە نھۆم لەسەر نھۆم');
INSERT INTO "bamoki" VALUES(71,16,'71:16','وە مانگی گێڕاوە لە ناویاندا بە ڕووناك وە ڕۆژیشی گێڕاوە بە چرا');
INSERT INTO "bamoki" VALUES(71,17,'71:17','وە خوا ئێوەی ڕوواندووە لە زەوی بە ڕوواندن');
INSERT INTO "bamoki" VALUES(71,18,'71:18','لەپاشان دەتان باتەوە ناوی وە دەرتان دەھێنێتەوە (لێی) بە دەرھێنان');
INSERT INTO "bamoki" VALUES(71,19,'71:19','خوا زەوی بۆ ڕاخستوون');
INSERT INTO "bamoki" VALUES(71,20,'71:20','تا ھاتوچۆ بکەن بە ڕێگە کراوەکانیدا');
INSERT INTO "bamoki" VALUES(71,21,'71:21','نوح ووتی: ئەی پەروەردگارم بێگومان گەلەکەم بێ فەرمانیان کردم وە شوێن کەسانێك کەوتن کە ماڵ و منداڵی قازانجی پێ نەگەیاندوە زیان نەبێ');
INSERT INTO "bamoki" VALUES(71,22,'71:22','وە پیلان وفڕو فێڵی زۆر گەورەیان کرد');
INSERT INTO "bamoki" VALUES(71,23,'71:23','وە ووتیان دەست ھەڵنەگرن لە بت وخواکانتان وە دەست ھەڵنەگرن لە (پەرستنی) وەد وسواع ویەغووث ویەعوق ونەسر (ئەمانە کۆمەڵە پیاوچاکێك بوون، جا کاتێك مردن، شەیتان ھات ووتی بەشوێنکەوتوانیان کە چەند پەیکەرێکیان بۆ بتاشن تاوەکو ھەمیشە لەبەرچاوتان بن، جا پاش ماوەیەك کەبەسەر چوو شەیتان ھات بەخەڵکی ووت ئەو پەیکەرانە بپەرستن، چونکە باوباپیرانتان ئەو پەیکەرانەیان پەرستووە، ئێوەش لەسەر ڕێبازی ئەوان بن)');
INSERT INTO "bamoki" VALUES(71,24,'71:24','وە بەڕاستی ئەوانە زۆر (خەڵك) یان گومڕا کرد، (خوایە) ئەم ستەمكارانە لە گومڕایی زیاتر ھیچی تریان بۆ زۆر مەکە');
INSERT INTO "bamoki" VALUES(71,25,'71:25','بەھۆی گوناھەکانیانەوە بە زریان خنکێنران، ئەمجا خرانە ئاگرەوە، ئیتر بێجگە لە خوا ھیچ یارمەتی دەرێکیان دەست نەکەوت');
INSERT INTO "bamoki" VALUES(71,26,'71:26','نوح فەرمووی: ئەی پەروەردگار مەیھێڵە لەسەر زەوی ھیچ کەسێك لە بێ بڕوایان');
INSERT INTO "bamoki" VALUES(71,27,'71:27','(چونکە) بێگومان ئەگەر تۆ وازیان لێ بھێنیت، بەندەکانی تۆ گومڕا دەکەن وە ھەر منداڵی تاوانباری سوپاس نەکەر دەخەنەوە');
INSERT INTO "bamoki" VALUES(71,28,'71:28','ئەی پەروەردگارم خۆش ببە لە خۆم وباوکم ودایکم وە لەو کەسە کە دێتە ماڵم بە بڕواداریەوە وە لە ھەموو پیاوان وژنانی ئیمانداروە بەشی ستەمکاران زیاد مەکە، تیاچوون ولە ناوچوون نەبێت');
INSERT INTO "bamoki" VALUES(72,1,'72:1','(ئەی موحەممەد صلى الله عليه وسلم) بڵێ: نیگام بۆ کرا کە بێگومان چەند کەسێك لە جنۆکە گوێیان گرت (لە قورئان خوێندنەوەم) ئەمجا ووتیان: بەڕاستی ئێمە قورئانێکی سەر سوڕھێنەرمان بیست');
INSERT INTO "bamoki" VALUES(72,2,'72:2','ڕێنمونی دەکات بۆ ڕێگای ڕاست ئێمەش باوەڕمان پێ کرد وە ھەرگیز کەس ناکەین بەھاوبەشی پەروەردگارمان');
INSERT INTO "bamoki" VALUES(72,3,'72:3','وە بەڕاستی بڵندە گەورەیی پەروەردگارمان - لە شتی ناشایستە -، ھیچ ھاوسەر ومنداڵێکی بۆ خۆی دانەناوە');
INSERT INTO "bamoki" VALUES(72,4,'72:4','بێگومان گێل ونەفامەکەمان درۆی نالەباری ھەڵدەبەست دەربارەی خوا');
INSERT INTO "bamoki" VALUES(72,5,'72:5','وە ئێمە پێمان وابوو کە ئادەمیی وجنۆکە ھەرگیز درۆ ناکەن دەربارەی خوا');
INSERT INTO "bamoki" VALUES(72,6,'72:6','بەڕاستی پیاوانێك لە ئادەمیی پەنایان دەبردە بەر پیاوانێك لە جنۆکە (کە بیان پارێزن) کەچی زۆرتر ماندوویان دەکردن');
INSERT INTO "bamoki" VALUES(72,7,'72:7','وە ئادەمیی گومانیان بردووە ھەروەك ئێوە - ئەی جنۆکە - گومانتان بردووە کە ئیتر خوا ھیچ پێغەمبەرێك نانێرێت');
INSERT INTO "bamoki" VALUES(72,8,'72:8','جنۆکەکان ووتیان : ئێمە دەگەڕاین بە شوێن دەنگ وباسی ئاسماندا کەچی بینیمان پڕکراوە لە پاسەوانی بەھێز وتیری ئەستێرەکان');
INSERT INTO "bamoki" VALUES(72,9,'72:9','وە بێگومان ئێمە جاران دادەنیشتین لە چەند شوێنێك لە ئاسمان بۆ گوێ گرتن، بەڵام ھەر کەسێك لەئێستا بە دواوە گوێ بگرێت تیرێکی ئەستێرە دەبینێت لە بۆسەیدایە');
INSERT INTO "bamoki" VALUES(72,10,'72:10','وە بەڕاستی نازانین ئایا خراپی ویستراوە بۆ ئەوانەی کە لە سەر زەوین؟ یان پەروەردگاریان ڕێنمونی ویستووە بۆیان؟');
INSERT INTO "bamoki" VALUES(72,11,'72:11','وە بێگومان ئێمە ھەندێکمان چاکین وە ھەندێکمان لەوە کەمتر، ئێمە جاران خاوەنی ڕێبازی زۆر بووین');
INSERT INTO "bamoki" VALUES(72,12,'72:12','وە ئێمە دڵنیا بووین کە ھەرگیز ناتوانین لە دەستی خوا دەرچین لە زەوی دا وە بە ھەڵھاتنیش لە دەستی خوا ڕزگار نابین');
INSERT INTO "bamoki" VALUES(72,13,'72:13','بەڕاستی ئێمە کاتێ ڕێنمونی (قورئان) مان بیست باوەڕمان پێ ھێنا، ئەمجا ھەر کەسێك بڕوای ھێنا بە پەروەردگاری خۆی ئیتر ناترسێت نە لە کەمیی (پاداشت) ونە لە زۆریی (تۆڵە)');
INSERT INTO "bamoki" VALUES(72,14,'72:14','وە بێگومان ئێمە ھەندێکمان موسوڵمانین وھەندێکمان ستەمکار، ئەمجا ئەوانەی موسوڵمان بوون ئەوە ئەوانە ڕێگای ڕاستیان دۆزیوەتەوە');
INSERT INTO "bamoki" VALUES(72,15,'72:15','بەڵام ئـەوانەی لەرێگای ڕاست لایانداوە (ستەمکارەکان) دەبنە سووتەمەنی دۆزەخ');
INSERT INTO "bamoki" VALUES(72,16,'72:16','(ئەمجا خوای گەورە دەفەرموێت) : ئەگەر ئەوان (مرۆڤ و جنۆکە) بەردەوام بوونایە لەسەر ڕێگای ڕاست ئاوی زۆرمان پێ ئەنۆشین (نیعمەتی زۆرمان دەدانێ)');
INSERT INTO "bamoki" VALUES(72,17,'72:17','ھەتا تاقیان کەینەوە تیایدا وە ھەر کەسێك ڕوو وەرگێڕێت لە یادی پەروەردگاری خۆی دەیخاتە ناو سزای دژوارەوە');
INSERT INTO "bamoki" VALUES(72,18,'72:18','بێگومان مزگەوتەکان بۆ پەرستنی خوان بەتەنھا، جگە لەئەو کەسی تری تێدا نەپەرسترێت، کەواتە لەگەڵ خوادا ھاوار لە کەسی تر مەکەن');
INSERT INTO "bamoki" VALUES(72,19,'72:19','وە بەندەی خوا (موحەممەد صلى الله عليه وسلم) کاتێك کە ھەستا لە خوا بپاڕێتەوە (و بیپەرستێت)، (بێ بڕواکانی مەککە) نزیك بوو دابارن بە سەریدا');
INSERT INTO "bamoki" VALUES(72,20,'72:20','بڵێ: دڵنیا بن من ھەر خوای خۆم دەپەرستم وە کەس ناکەم بە ھاوبەشی');
INSERT INTO "bamoki" VALUES(72,21,'72:21','بڵێ: بەڕاستی من نەزیان ونەڕێنمونی ئێوەم بەدەست نیە');
INSERT INTO "bamoki" VALUES(72,22,'72:22','بڵێ: بێگومان کەسێك نیە کە من بپارێزێت لە (سزای) خوا، وە بێجگە لەو پەناگەیەك شك نابەم');
INSERT INTO "bamoki" VALUES(72,23,'72:23','(وە ھیچم بەدەست نیە) ڕاگەیاندن نەبێت لە خواوە وگەیاندنی پەیامەكانی ئەو نەبێت وە ھەر کەسێك بێ فەرمانی خوا وپێغەمبەری بکات ئەوە بێگومان ئاگری دۆزەخ بۆ ئەوە، تێیدا دەمێنێتەوە بە ھەمیشەیی');
INSERT INTO "bamoki" VALUES(72,24,'72:24','تا بەچاوی خۆیان دەبینن ئەو سزایانەی کە بەڵێنیان پێدراوە، ئەمجا ئەو کاتە دەزانن، کە کێ یارمەتیدەری کەمترە وژمارەی کەمە');
INSERT INTO "bamoki" VALUES(72,25,'72:25','بڵێ من نازانم ئاخۆ نزیکە ئەو ڕۆژی دواییەی کە بەڵێنتان پێدراوە یان پەروەردگارم ماوەیەکی دووری بۆ داناوە');
INSERT INTO "bamoki" VALUES(72,26,'72:26','ھەر خوایە زانا بە نھێنیەکان وە کەس ئاگادار ناکات بەسەر نھێنی خۆیدا');
INSERT INTO "bamoki" VALUES(72,27,'72:27','مەگەر بۆ پێغەمبەرێك کە خۆی ڕازی بێت، جا بێگومان خوا لەبەردەمی وپشتیەوە پاسەوان (لە فریشتە) دەنێرێت');
INSERT INTO "bamoki" VALUES(72,28,'72:28','تا بزانێت کە پەیامی خوای خۆیان گەیاندووە وە خوا ئاگادارە بەوەی کە لایانە وە ھەموو شتێکی (لەسەریان) ژماردووە بە ھەڵژماردن');
INSERT INTO "bamoki" VALUES(73,1,'73:1','ئەی ئەو کەسەی کە خۆت داپۆشیووە');
INSERT INTO "bamoki" VALUES(73,2,'73:2','شەو ھەستە (شەو نوێژ بکە) مەگەر کەمێکی نەبێت');
INSERT INTO "bamoki" VALUES(73,3,'73:3','نیوەی یا کەمێك لە نیوەی کەم بکەرەوە');
INSERT INTO "bamoki" VALUES(73,4,'73:4','یا کەمێ لێی زیاد بکە، لەسەرخۆ قورئان بخوێنەرەوە و(بەڕێك وپێکی) دەوری بکەرەوە');
INSERT INTO "bamoki" VALUES(73,5,'73:5','بێگومان ئێمە لەمەولا بۆت دەنێرینە خوارەوە گوفتاری (قورئان)ی قورس وگران');
INSERT INTO "bamoki" VALUES(73,6,'73:6','بەڕاستی ھەستانی شەو دوای خەو (بۆ خواپەرستی) قورسترە وگرانترە لە ڕووی کردارەوە (لە ھی ڕۆژ وبەھێزترە بۆ دڵ)، دروسترە لە ڕووی ووتنەوە');
INSERT INTO "bamoki" VALUES(73,7,'73:7','بەڕاستی تۆ لە ڕۆژدا لە ھاتووچۆکردنێکی زۆردایت');
INSERT INTO "bamoki" VALUES(73,8,'73:8','ھەمیشە یادی پەروەردگارت بکە وە خۆت یەکلاکەرەوە بە یەکلایی کردنەوەیەکی تەواو');
INSERT INTO "bamoki" VALUES(73,9,'73:9','ئەو خوای ڕۆژھەڵات وڕۆژئاوایە ھیچ پەرستراوێکی بەڕاستی نیە تەنھا ئەو نەبێت، کەوابوو بیکە بە پارێزەر وپشتیوانی خۆت');
INSERT INTO "bamoki" VALUES(73,10,'73:10','دان بە خۆتدا بگرە لەسەر ئەو قسانەی کە دەیڵێن وە وازیان لێبهێنە ولێیان گەڕێ بە واز لێھێنانێکی جوان');
INSERT INTO "bamoki" VALUES(73,11,'73:11','واز بھێنە لە من وئەو خاوەن ناز ونیعمەتانەی کە ئایینی من بە درۆ دەزانن وە کەمێك مۆڵەتیان بدە');
INSERT INTO "bamoki" VALUES(73,12,'73:12','بێگومان لای ئێمە ھەیە کۆت وزنجیری قورس وگران وە ئاگری ھەڵگیرساو');
INSERT INTO "bamoki" VALUES(73,13,'73:13','وە خواردنی گەرو گیر ھەروەھا سزای پڕ لە داخ و دەرد');
INSERT INTO "bamoki" VALUES(73,14,'73:14','لەڕۆژێکدا کە زەوی وچیاکان دێنە لەرزە وە کێوەکان دەبن بە گردۆڵکە لە لمی ھەڵڕژاو');
INSERT INTO "bamoki" VALUES(73,15,'73:15','بێگومان ئێمە پێغەمبەرێکمان بۆ ناردن کە شایەتە لە سەرتان، ھەروەك پێغەمبەرێکمان نارد بۆلای فیرعەون');
INSERT INTO "bamoki" VALUES(73,16,'73:16','ئەمجا فیرعەون سەرپێچی ئەو پێغەمبەرەی کرد (سەرپێچی موسا) ئێمەش تۆڵەمان لێ سەند بە تۆڵە سەندنێکی سەخت');
INSERT INTO "bamoki" VALUES(73,17,'73:17','جا ئەگەر بێ بڕوایی بکەن چۆن خۆتان دەپارێزن، لە ڕۆژێکدا کە (بە ترس وسامی خۆی) مناڵان پیر دەکات');
INSERT INTO "bamoki" VALUES(73,18,'73:18','ئاسمان لەت دەبێت بەھۆی (سەختی) ئەو ڕۆژەوە، بەڵێنی خوا دێتە دی');
INSERT INTO "bamoki" VALUES(73,19,'73:19','بەڕاستی ئەم ئایەتانەی پێشوو پەند وئامۆژگارین، ئەمجا ھەرکەسێك کە بیەوێت ڕێگا دەگرێت بۆلای پەروەردگاری');
INSERT INTO "bamoki" VALUES(73,20,'73:20','بێگومان پەروەردگاری تۆ دەزانێت (ئەی موحەممەد صلى الله عليه وسلم) کە تۆ ھەڵدەسیت (بۆشەو نوێژکردن) شتێك کەمتر لە دووبەش لە سێ بەشی شەو، وە نیوەی" یان سێیەكی وە دەستەیەك لەوانەی کە لە گەڵتدان وە خوا ئەندازەی شەو وڕۆژ دیاری دەکات، (خوا) زانی کە ئێوە ناتوانن ئەندازەی شەو نوێژ ڕابگرن لە بەرئەوە لێتان خۆش بوو، دەی ئەوەندەی بۆتان دەلوێت وئاسانە لاتان قورئان بخوێنن ودەوری بکەنەوە، (خوا) دەزانێت کە لەمەولا کەسانێکتان نەخۆش دەبێت وە کەسانێکی ترتان دەرۆن بەسەر زەویدا بە شوێن ڕۆزیی ونیعمەتی خوادا دەگەڕێن وەکەسانێکی ترتان دەجەنگن لە ڕێگای خوادا، ئەمجا بخوێننەوە لە قورئان ئەوەندەی کە بۆتان دەلوێ ولاتان ئاسانە وە نوێژ بکەن وزەکات بدەن وە قەرز بدەن بەخوا بە قەرزێکی چاك (مەبەست ئەوەیە خێر وخێرات وسەدەقە بکەن لە ڕێی خوادا)، ھەر چاکەیەك پێش بخەن بۆ خۆتان، لای خوا دەستان دەکەوێتەوە، ئەوەیشی لای خوایە چاکتر وگەورەترە لە ڕووی پاداشتەوە وە داوای لێخۆش بوون بکەن لە خوا، بەڕاستی خوا لێبووردە ومیھرەبانە');
