INSERT INTO "bayan" VALUES(26,164,'26:164','اور میں تم سے اس پر کسی اجرت کا طالب نہیں ہوں نہیں ہے میری اجرت مگر تمام جہانوں کے رب کے ّ ذمے');
INSERT INTO "bayan" VALUES(26,165,'26:165','کیا تمام جہانوں میں تم ہی (شہوت کے لیے) َ مردوں کے پاس جاتے ہو');
INSERT INTO "bayan" VALUES(26,166,'26:166','اور تم چھوڑ دیتے ہو جن کو تمہارے لیے پیدا کیا ہے تمہارے رب نے تمہاری بیویوں میں سے بلکہ تم تو حد سے بڑھنے والے لوگ ہو');
INSERT INTO "bayan" VALUES(26,167,'26:167','انہوں نے کہا کہ اے لوط ! اگر تم باز نہ آئے تو یہاں سے نکال باہر کیے جاؤ گے');
INSERT INTO "bayan" VALUES(26,168,'26:168','اس ؑ نے کہا کہ میں تو تمہارے ان طور طریقوں سے سخت بیزار ہوں');
INSERT INTO "bayan" VALUES(26,169,'26:169','پروردگار ! تو نجات دے مجھے اور میرے گھر والوں کو ان کے عمل سے');
INSERT INTO "bayan" VALUES(26,170,'26:170','تو ہم نے نجات دی اس ؑ کو بھی اور اس ؑ کے سب گھر والوں کو بھی');
INSERT INTO "bayan" VALUES(26,171,'26:171','سوائے ایک بڑھیا کے جو پیچھے رہنے والوں میں تھی');
INSERT INTO "bayan" VALUES(26,172,'26:172','پھر ہم نے اٹھا کر پٹخ دیا باقیوں کو');
INSERT INTO "bayan" VALUES(26,173,'26:173','اور ہم نے برسائی ان پر ایک بارش تو بہت ہی بری تھی وہ بارش جو ان لوگوں پر برسی جنہیں خبردار کردیا گیا تھا');
INSERT INTO "bayan" VALUES(26,174,'26:174','یقیناً اس میں ایک بڑی نشانی ہے لیکن ان کی اکثریت ماننے والی نہیں ہے');
INSERT INTO "bayan" VALUES(26,175,'26:175','اور یقیناً آپ صلى الله عليه وسلم کا رب بہت زبردست ہے نہایت رحم کرنے والا');
INSERT INTO "bayan" VALUES(26,176,'26:176','(اور اسی طرح) اصحاب الایکہ نے بھی جھٹلایا رسولوں ؑ کو');
INSERT INTO "bayan" VALUES(26,177,'26:177','جب کہا ان سے شعیب ؑ نے کہ کیا تم ڈرتے نہیں ہو');
INSERT INTO "bayan" VALUES(26,178,'26:178','میں یقیناً تمہارے لیے ایک امانت دار رسول ؑ ہوں');
INSERT INTO "bayan" VALUES(26,179,'26:179','پس تم اللہ کا تقویٰ اختیار کرو اور میری اطاعت کرو');
INSERT INTO "bayan" VALUES(26,180,'26:180','اور میں تم سے اس پر کسی اجرت کا طالب نہیں ہوں نہیں ہے میری اجرت مگر تمام جہانوں کے رب کے ذمے');
INSERT INTO "bayan" VALUES(26,181,'26:181','پیمانوں کو پورا بھرا کرو اور خسارہ پہنچانے والوں میں سے مت ہوجاؤ');
INSERT INTO "bayan" VALUES(26,182,'26:182','اور وزن کیا کرو سیدھی ترازو کے ساتھ');
INSERT INTO "bayan" VALUES(26,183,'26:183','اور مت گھٹا کردیا کرو لوگوں کو ان کی چیزیں اور مت پھرو زمین میں فساد مچاتے ہوئے');
INSERT INTO "bayan" VALUES(26,184,'26:184','اور ڈرو اس سے جس نے تمہیں پیدا کیا اور پہلی مخلوق کو بھی');
INSERT INTO "bayan" VALUES(26,185,'26:185','انہوں نے کہا کہ تم تو بس سحر زدہ لوگوں میں سے ہو');
INSERT INTO "bayan" VALUES(26,186,'26:186','اور تم نہیں ہو مگر ہمارے ہی جیسے ایک انسان اور تمہارے بارے میں ہمارا گمان غالب ہے کہ تم جھوٹوں میں سے ہو');
INSERT INTO "bayan" VALUES(26,187,'26:187','تو گرا دو ہم پر آسمان کا کوئی ٹکڑا اگر تم سچے ہو');
INSERT INTO "bayan" VALUES(26,188,'26:188','اس ؑ نے جواب دیا کہ میرا رب خوب جانتا ہے جو کچھ تم کر رہے ہو');
INSERT INTO "bayan" VALUES(26,189,'26:189','تو انہوں نے اس کو جھٹلا دیا تو انہیں آپکڑا سائبان والے دن کے عذاب نے یقیناً وہ ایک بڑے دن کا عذاب تھا');
INSERT INTO "bayan" VALUES(26,190,'26:190','یقیناً اس میں ایک بڑی نشانی ہے لیکن ان کی اکثریت ایمان لانے والی نہیں ہے');
INSERT INTO "bayan" VALUES(26,191,'26:191','اور (اے نبی صلى الله عليه وسلم !) آپ کا رب بہت زبردست نہایت رحم کرنے والا ہے');
INSERT INTO "bayan" VALUES(26,192,'26:192','اور یقیناً یہ (قرآن) تمام جہانوں کے پروردگار کی طرف سے نازل کردہ ہے');
INSERT INTO "bayan" VALUES(26,193,'26:193','اُترے ہیں اسے لے کر روح الامین ؑ');
INSERT INTO "bayan" VALUES(26,194,'26:194','آپ صلى الله عليه وسلم کے دل پر تاکہ آپ صلى الله عليه وسلم ہوجائیں خبردار کرنے والوں میں سے');
INSERT INTO "bayan" VALUES(26,195,'26:195','(یہ نازل ہوا ہے) واضح عربی زبان میں');
INSERT INTO "bayan" VALUES(26,196,'26:196','اور یقیناً یہ پہلوں کے صحیفوں میں بھی موجود ہے');
INSERT INTO "bayan" VALUES(26,197,'26:197','کیا ان کے لیے یہ نشانی کافی نہیں ہے کہ اس کو جانتے ہیں علمائے بنی اسرائیل');
INSERT INTO "bayan" VALUES(26,198,'26:198','اور اگر ہم نے اس (قرآن) کو نازل کردیا ہوتا کسی عجمی پر');
INSERT INTO "bayan" VALUES(26,199,'26:199','اور وہ اسے ان کو پڑھ کر سناتا تب بھی وہ اس پر ایمان لانے والے نہیں تھے');
INSERT INTO "bayan" VALUES(26,200,'26:200','اسی طرح ہم نے داخل کردیا ہے اس (انکار) کو مجرموں کے دلوں میں');
INSERT INTO "bayan" VALUES(26,201,'26:201','یہ ایمان نہیں لائیں گے اس پر جب تک کہ دیکھ نہ لیں درد ناک عذاب کو');
INSERT INTO "bayan" VALUES(26,202,'26:202','تو وہ ان پر اچانک آجائے گا اور انہیں گمان بھی نہیں ہوگا');
INSERT INTO "bayan" VALUES(26,203,'26:203','(اس وقت) یہ کہیں گے کہ کیا ہمیں مہلت مل سکتی ہے');
INSERT INTO "bayan" VALUES(26,204,'26:204','تو کیا یہ لوگ ہمارے عذاب کے لیے جلدی مچا رہے ہیں');
INSERT INTO "bayan" VALUES(26,205,'26:205','تو کیا آپ صلى الله عليه وسلم نے دیکھا اگر ہم انہیں چند سال اور بھی فائدہ اٹھانے دیں');
INSERT INTO "bayan" VALUES(26,206,'26:206','پھر ان پر وہ عذاب آجائے جس کا ان سے وعدہ کیا جا رہا ہے');
INSERT INTO "bayan" VALUES(26,207,'26:207','تو کچھ کام نہیں آئے گا ان کے وہ سب کچھ جس سے وہ فائدہ پہنچائے جاتے تھے');
INSERT INTO "bayan" VALUES(26,208,'26:208','اور ہم نے کبھی کسی بستی کو ہلاک نہیں کیا مگر اس کے لیے خبردار کرنے والے تھے');
INSERT INTO "bayan" VALUES(26,209,'26:209','یاد دہانی کے لیے اور ہم ظالم نہیں ہیں');
INSERT INTO "bayan" VALUES(26,210,'26:210','اور اس (قرآن) کو لے کر شیاطین نازل نہیں ہوئے');
INSERT INTO "bayan" VALUES(26,211,'26:211','اور نہ تو ایسا کرنا ان کے لائق ہے اور نہ ہی وہ اس کی استطاعت رکھتے ہیں');
INSERT INTO "bayan" VALUES(26,212,'26:212','وہ تو (وحی الٰہی کے) سننے سے بھی معزول کیے جا چکے ہیں');
INSERT INTO "bayan" VALUES(26,213,'26:213','تو مت پکارنا اللہ کے ساتھ کسی اور معبود کو ورنہ تم عذاب پانے والوں میں سے ہو کر رہو گے');
INSERT INTO "bayan" VALUES(26,214,'26:214','اور خبردار کیجیے اپنے قریبی رشتہ داروں کو');
INSERT INTO "bayan" VALUES(26,215,'26:215','اور اپنے بازو جھکا کر رکھیں ان کے لیے جو آپ صلى الله عليه وسلم کے پیروکار ہیں مؤمنین میں سے');
INSERT INTO "bayan" VALUES(26,216,'26:216','پھر اگر یہ لوگ آپ ؑ کی نافرمانی کریں تو ان سے کہہ دیجیے کہ میں بری ہوں اس سے جو کچھ تم کر رہے ہو');
INSERT INTO "bayan" VALUES(26,217,'26:217','اور (اے نبی صلى الله عليه وسلم !) آپ بھروسہ کیجیے اس اللہ پر جو بہت زبردست نہایت رحم کرنے والا ہے');
INSERT INTO "bayan" VALUES(26,218,'26:218','جو دیکھتا ہے آپ صلى الله عليه وسلم کو جب آپ صلى الله عليه وسلم کھڑے ہوتے ہیں');
INSERT INTO "bayan" VALUES(26,219,'26:219','اور (وہ دیکھتا ہے) آپ صلى الله عليه وسلم کے آنے جانے کو سجدہ کرنے والوں میں');
INSERT INTO "bayan" VALUES(26,220,'26:220','یقیناً وہ سب کچھ سننے والا اور سب کچھ جاننے والا ہے');
INSERT INTO "bayan" VALUES(26,221,'26:221','کیا میں تمہیں بتاؤں کہ شیاطین کن پر اترا کرتے ہیں');
INSERT INTO "bayan" VALUES(26,222,'26:222','وہ تو اترتے ہیں ہر جھوٹ گھڑنے والے گناہگار پر');
INSERT INTO "bayan" VALUES(26,223,'26:223','وہ القا کرتے ہیں سنی سنائی باتیں اور ان میں سے اکثر جھوٹے ہوتے ہیں');
INSERT INTO "bayan" VALUES(26,224,'26:224','اور شعراء کی پیروی تو گمراہ لوگ کرتے ہیں');
INSERT INTO "bayan" VALUES(26,225,'26:225','کیا تم دیکھتے نہیں ہو کہ وہ ہر وادی میں سرگرداں رہتے ہیں');
INSERT INTO "bayan" VALUES(26,226,'26:226','اور یہ کہ وہ جو کہتے ہیں وہ کرتے نہیں');
INSERT INTO "bayan" VALUES(26,227,'26:227','سوائے ان لوگوں کے جو ایمان لائیں اور نیک اعمال کریں اور کثرت سے اللہ کا ذکر کریں اور وہ بدلہ لیں اس کے بعد کہ ان پر ظلم کیا گیا ہو اور عنقریب یہ ظالم جان لیں گے کہ کس جگہ لوٹ کر جائیں گے');
INSERT INTO "bayan" VALUES(27,1,'27:1','طٰسۗ یہ قرآن اور کتاب مبین کی آیات ہیں');
INSERT INTO "bayan" VALUES(27,2,'27:2','یہ ہدایت اور بشارت ہے اہل ایمان کے لیے');
INSERT INTO "bayan" VALUES(27,3,'27:3','جو نماز قائم کرتے ہیں اور زکوٰۃ ادا کرتے ہیں اور وہی ہیں جو آخرت پر یقین رکھتے ہیں');
INSERT INTO "bayan" VALUES(27,4,'27:4','یقیناً وہ لوگ جو آخرت پر ایمان نہیں رکھتے ہم نے ان کے لیے ان کے اعمال کو مزین کردیا ہے پس وہ بھٹکتے پھر رہے ہیں');
INSERT INTO "bayan" VALUES(27,5,'27:5','یہی وہ لوگ ہیں کہ جن کے لیے بہت برا عذاب ہے اور یہی وہ لوگ ہیں کہ جو آخرت میں سب سے زیادہ خسارے والے ہوں گے');
INSERT INTO "bayan" VALUES(27,6,'27:6','اور (اے نبی صلى الله عليه وسلم !) یقینا آپ کو یہ قرآن دیا جا رہا ہے ایک حکیم اور علیم ہستی کی طرف سے');
INSERT INTO "bayan" VALUES(27,7,'27:7','یاد کرو جب موسیٰ ؑ نے کہا تھا اپنے گھر والوں سے کہ میں نے ایک آگ دیکھی ہے میں وہاں سے تمہارے پاس کوئی خبر لے کر آؤں گا یا کوئی دہکتا ہوا انگارہ لے آؤں گا تاکہ تم (آگ) تاپ سکو');
INSERT INTO "bayan" VALUES(27,8,'27:8','پھر جب وہ وہاں پہنچے تو انہیں پکارا گیا کہ بہت مبارک ہے وہ جو اس آگ میں ہے اور وہ جو اس کے آس پاس ہے اور پاک ہے اللہ جو تمام جہانوں کا پروردگار ہے');
INSERT INTO "bayan" VALUES(27,9,'27:9','اے موسیٰ ! یہ تو میں ہوں اللہ بہت زبردست کمال حکمت والا');
INSERT INTO "bayan" VALUES(27,10,'27:10','اور اپنا عصا (زمین پر) ڈال دو تو جب اس نے اسے حرکت کرتے ہوئے دیکھا گویا وہ سانپ ہو تو وہ پیٹھ پھیر کر بھاگا اور پیچھے ُ مڑکر بھی نہ دیکھا (اللہ نے فرمایا :) اے موسیٰ ! ڈرو نہیں میرے حضور رسولوں کے لیے کوئی خوف نہیں ہوتا');
INSERT INTO "bayan" VALUES(27,11,'27:11','سوائے اس کے جس نے کوئی ظلم کیا ہو پھر اس نے بدل دیا برائی کو نیکی سے تو یقیناً میں بہت بخشنے والا بیحد مہربان ہوں');
INSERT INTO "bayan" VALUES(27,12,'27:12','اور ذرا اپنا ہاتھ داخل کرو اپنے گریبان میں وہ نکلے گا سفید چمکتا ہوا بغیر کسی مرض کے یہ (دو نشانیاں) فرعون اور اس کی قوم کے لیے نو نشانیوں میں سے ہیں یقیناً وہ بڑے نافرمان لوگ ہیں');
INSERT INTO "bayan" VALUES(27,13,'27:13','تو جب ان کے پاس ہماری آنکھیں کھول دینے والی نشانیاں آئیں انہوں نے کہا کہ یہ تو کھلا جادو ہے');
INSERT INTO "bayan" VALUES(27,14,'27:14','اور انہوں نے ان کا انکار کیا ظلم اور سر کشی کے ساتھ جبکہ ان کے دلوں نے ان کا یقین کیا تو دیکھ لو ! کیسا ہوا انجام مفسدوں کا');
INSERT INTO "bayan" VALUES(27,15,'27:15','اور ہم نے داؤد ؑ اور سلیمان ؑ کو علم عطا کیا تھا اور ان دونوں نے کہا کہ کل شکر اور کل تعریف اس اللہ کے لیے ہے جس نے ہمیں اپنے بہت سے مؤمن بندوں پر فضیلت عطا فرمائی');
INSERT INTO "bayan" VALUES(27,16,'27:16','اور وارث ہوا سلیمان ؑ داؤد ؑ کا اور اس نے کہا : اے لوگو ! ہمیں سکھا دی گئی ہیں پرندوں کی بولیاں اور ہمیں (اللہ کی طرف سے) ہرچیز عطا کی گئی ہے۔ یقیناً یہ (اللہ کا) بہت واضح فضل ہے');
INSERT INTO "bayan" VALUES(27,17,'27:17','اور جمع کیے گئے سلیمان کے (معائنہ کے) لیے اس کے تمام لشکر جنوں ّ انسانوں اور پرندوں میں سے اس طرح کہ انہیں جماعتوں میں منظم کیا جاتا تھا');
INSERT INTO "bayan" VALUES(27,18,'27:18','یہاں تک کہ جب وہ چیونٹیوں کی وادی میں پہنچے تو ایک چیونٹی نے کہا : اے چیونٹیو ! تم سب اپنے بلوں میں گھس جاؤ کہیں ایسا نہ ہو کہ سلیمان ؑ اور اس کے لشکر تمہیں کچل کر رکھ دیں اور انہیں اس کا احساس بھی نہ ہو');
INSERT INTO "bayan" VALUES(27,19,'27:19','تو وہ خوش ہو کر مسکرایا اس کی اس بات پر اور اس نے کہا : اے میرے پروردگار ! مجھے توفیق دے کہ میں شکر ادا کروں تیری اس نعمت کا جو تو نے مجھے اور میرے والدین کو عطا کی ہے اور یہ کہ میں اچھے اعمال کروں جن سے تو راضی ہو اور مجھے داخل کرنا اپنی رحمت سے اپنے نیک بندوں میں');
INSERT INTO "bayan" VALUES(27,20,'27:20','اور اس نے پرندوں کے لشکر کا معائنہ کیا تو کہا کہ کیا بات ہے مجھے ُ ہد ہد نظر نہیں آ رہا ؟ کیا وہ غیر حاضر ہے');
INSERT INTO "bayan" VALUES(27,21,'27:21','میں اسے بہت سخت سزا دوں گا یا اسے ذبح کر ڈالوں گا یا وہ میرے پاس کوئی واضح دلیل لے کر آئے');
INSERT INTO "bayan" VALUES(27,22,'27:22','تو کچھ زیادہ دیر نہیں گزری (کہ ہدہد پہنچ گیا) تو اس نے کہا کہ میں نے وہ کچھ معلوم کیا ہے جو آپ کو معلوم نہیں اور میں آپ ؑ کے پاس قوم سبا سے متعلق یقینی معلومات لے کر آیا ہوں');
INSERT INTO "bayan" VALUES(27,23,'27:23','میں نے ایک عورت کو ان پر حکومت کرتے ہوئے دیکھا ہے اور اسے ہر شے دی گئی ہے اور اس کا تخت بہت عظیم الشان ہے');
INSERT INTO "bayan" VALUES(27,24,'27:24','اور میں نے دیکھا اس کو اور اس کی قوم کو کہ وہ سجدہ کرتے ہیں سورج کو اللہ کو چھوڑ کر اور شیطان نے ان کے لیے ان کے اعمال کوّ مزین کردیا ہے اور انہیں روک دیا ہے سیدھے راستے سے تو اب وہ راستہ نہیں پا رہے');
INSERT INTO "bayan" VALUES(27,25,'27:25','کہ وہ سجدہ نہیں کرتے اللہ کو جو نکالتا ہے ہر چھپی چیز کو آسمانوں اور زمین میں سے اور وہ خوب جانتا ہے جو کچھ تم چھپاتے ہو اور جو کچھ تم ظاہر کرتے ہو');
INSERT INTO "bayan" VALUES(27,26,'27:26','وہ اللہ کہ جس کے علاوہ کوئی معبود نہیں اور جو بہت بڑے عرش کا مالک ہے');
INSERT INTO "bayan" VALUES(27,27,'27:27','سلیمان ؑ نے کہا : ہم عنقریب دیکھیں گے کہ تم نے سچ کہا ہے یا تم جھوٹے ہو');
INSERT INTO "bayan" VALUES(27,28,'27:28','میرا یہ خط لے جاؤ اسے ان کے پاس جا کر ڈال آؤ پھر ان سے الگ ہو کر دیکھتے رہو کہ وہ کیا جواب دیتے ہیں');
INSERT INTO "bayan" VALUES(27,29,'27:29','اس نے کہا کہ اے (میری قوم کے) سردارو ! میری طرف ایک بہتّ عزت والا خط ڈالا گیا ہے');
INSERT INTO "bayan" VALUES(27,30,'27:30','یہ (خ ط) سلیمان کی طرف سے ہے اور اس کا آغاز بسم اللہ الرحمن الرحیم سے ہوا ہے');
INSERT INTO "bayan" VALUES(27,31,'27:31','یہ کہ میرے مقابلے میں تم لوگ سرکشی نہ کرو اور مطیع ہو کر میرے پاس حاضر ہوجاؤ');
INSERT INTO "bayan" VALUES(27,32,'27:32','اس نے کہا : اے سردارو ! میرے اس معاملے میں آپ لوگ مجھے مشورہ دیں۔ میں کسی معاملے میں بھی حتمی فیصلہ نہیں کرتی جب تک آپ لوگ موجود نہ ہوں');
INSERT INTO "bayan" VALUES(27,33,'27:33','انہوں نے کہا : ہم طاقتور بھی ہیں اور زبردست جنگی صلاحیت والے بھی اور فیصلے کا اختیار تو آپ ہی کے پاس ہے چناچہ آپ خود دیکھ لیں کہ کیا حکم دیتی ہیں');
INSERT INTO "bayan" VALUES(27,34,'27:34','اس نے کہا کہ بادشاہ جب کسی بستی میں داخل ہوتے ہیں تو اس میں فساد برپا کردیتے ہیں اور اس کے معزز لوگوں کو ذلیل کردیتے ہیں اور وہ ایسے ہی کرتے ہیں');
INSERT INTO "bayan" VALUES(27,35,'27:35','تو میں ان کی طرف اپنے ایلچی کچھ تحائف کے ساتھ بھیجتی ہوں پھر دیکھتی ہوں کہ وہ کیا جواب لے کر واپس آتے ہیں');
INSERT INTO "bayan" VALUES(27,36,'27:36','تو جب وہ (وفد) آیا سلیمان کے پاس اس نے کہا کہ کیا تم میری اعانت کرنا چاہتے ہو مال و دولت سے ؟ تو جو کچھ مجھے اللہ نے دے رکھا ہے وہ کہیں بہتر ہے اس سے جو اس نے تمہیں دیا ہے اپنے ان تحائف سے تم خود ہی خوش رہو');
INSERT INTO "bayan" VALUES(27,37,'27:37','تم لوٹ جاؤ ان کی طرف تو ہم ان پر ایسے لشکروں سے حملہ آور ہوں گے جن کا مقابلہ ان کے لیے ممکن نہیں ہوگا اور ہم نکال باہر کریں گے انہیں اس ملک سے ذلیل کر کے اور وہ خوار ہوجائیں گے');
INSERT INTO "bayan" VALUES(27,38,'27:38','پھر اپنے درباریوں سے مخاطب ہو کر) سلیمان نے کہا : اے درباریو ! تم میں سے کون اس (ملکہ) کا تخت میرے پاس لائے گا اس سے پہلے کہ وہ لوگ فرمانبردار ہو کر میرے پاس پہنچیں');
INSERT INTO "bayan" VALUES(27,39,'27:39','جنوں میں سے ایک دیو نے کہا کہ میں اسے آپ کے پاس لے آتا ہوں اس سے پہلے کہ آپ ؑ اپنی اس مجلس سے اٹھیں اور میں یقیناً اس کام کے لیے طاقت بھی رکھتا ہوں اور امانت دار بھی ہوں');
INSERT INTO "bayan" VALUES(27,40,'27:40','کہنے لگا وہ شخص جس کے پاس کتاب کا علم تھا کہ میں اسے آپ کے پاس لے آتا ہوں اس سے قبل کہ آپ ؑ کی نگاہ پلٹ کر آپ ؑ کی طرف آئے پھر جب اس نے دیکھا اسے اپنے سامنے رکھا ہوا اس ؑ نے کہا کہ یہ میرے رب ہی کے فضل سے ہے تاکہ وہ مجھے آزمائے کہ کیا میں شکر ادا کرتا ہوں یا نا شکری کرتا ہوں اور جو کوئی شکر کرتا ہے وہ اپنے ہی (بھلے کے) لیے کرتا ہے اور جو کوئی نا شکری کرتا ہے تو میرا رب یقیناً بےنیاز ہے بہت کرم کرنے والا');
INSERT INTO "bayan" VALUES(27,41,'27:41','سلیمان ؑ نے کہا کہ اس کے لیے اس کے تخت کی ہیئت ذرا بدل دو ہم دیکھیں کہ وہ پہچان پاتی ہے یا ان لوگوں میں سے ہوتی ہے جو نہیں پہچان پاتے');
INSERT INTO "bayan" VALUES(27,42,'27:42','پھر جب وہ آئی تو (اس سے) کہا گیا کہ کیا اسی طرح کا ہے آپ کا تخت ؟ اس نے کہا یہ تو گویا وہی ہے اور ہمیں اس سے پہلے ہی علم حاصل ہوچکا ہے اور ہم اسلام لا چکے ہیں');
INSERT INTO "bayan" VALUES(27,43,'27:43','اور سلیمان ؑ نے اسے روک دیا (اس سے) جس کو وہ پوجتی تھی اللہ کے سوا وہ ایک کافر قوم میں سے تھی');
INSERT INTO "bayan" VALUES(27,44,'27:44','اس سے کہا گیا کہ اب محل میں داخل ہو جاؤتو جب اس نے اس (کے فرش) کو دیکھا تو اسے گہرا پانی سمجھا اور اپنی دونوں پنڈلیاں کھول دیں سلیمان ؑ نے کہا : یہ تو ایسا محل ہے جو مرصعّ ہے شیشوں سے اس نے کہا : اے میرے پروردگار ! یقیناً میں نے اپنی جان پر ظلم کیا اور اب میں نے سلیمان ؑ کے ساتھ اللہ کی اطاعت اختیار کی ہی جو تمام جہانوں کا پروردگار ہے');
INSERT INTO "bayan" VALUES(27,45,'27:45','اور ہم نے بھیجا تھا قوم ثمود کی طرف ان کے بھائی صالح ؑ کو کہ تم اللہ کی بندگی کرو تو اس پر وہ لوگ دو گروہ بن کر آپس میں جھگڑنے لگے');
INSERT INTO "bayan" VALUES(27,46,'27:46','اس ؑ نے کہا : اے میری قوم کے لوگو ! تم کیوں جلدی مچاتے ہو برائی کے لیے بھلائی سے پہلے تم لوگ اللہ سے مغفرت کیوں نہیں مانگتے تاکہ تم پر رحم کیا جائے');
INSERT INTO "bayan" VALUES(27,47,'27:47','انہوں نے کہا کہ ہم منحوس سمجھتے ہیں تم کو اور تمہارے ساتھیوں کو اس نے کہا کہ تمہاری نحوست کا معاملہ اللہ کے اختیار میں ہے بلکہ تم ایسے لوگ ہو جو آزمائے جا رہے ہو');
INSERT INTO "bayan" VALUES(27,48,'27:48','اور اس شہر میں نو (9) بڑے بڑے سردار تھے وہ زمین میں فساد مچاتے تھے اور اصلاح نہیں کرتے تھے');
INSERT INTO "bayan" VALUES(27,49,'27:49','انہوں نے کہا کہ تم سب آپس میں اللہ کی قسم کھا کر عہد کرو کہ ہم لازماً رات کو حملہ کریں گے اس پر اور اس کے گھر والوں پر پھر ہم اس کے وارث سے کہہ دیں گے کہ ہم تو اس کے گھر والوں کے قتل کے وقت موجود ہی نہیں تھے اور ہم بالکل سچے ہیں');
INSERT INTO "bayan" VALUES(27,50,'27:50','اور انہوں نے ایک چال چلی اور ہم نے بھی ایک تدبیر کی اور انہیں پتا بھی نہ چلا');
INSERT INTO "bayan" VALUES(27,51,'27:51','تو دیکھ لو کیا انجام ہوا ان کی چال کا ہم نے انہیں اور ان کی پوری قوم کو ہلاک کر ڈالا');
INSERT INTO "bayan" VALUES(27,52,'27:52','تو یہ ان کے گھر ہیں جو ویران پڑے ہیں اس ظلم کے سبب جو انہوں نے کیا یقیناً اس میں نشانی ہے ان لوگوں کے لیے جو علم رکھتے ہیں');
INSERT INTO "bayan" VALUES(27,53,'27:53','اور ہم نے نجات دی ان لوگوں کو جو ایمان لائے تھے اور جنہوں نے تقویٰ کی روش اختیار کی تھی');
INSERT INTO "bayan" VALUES(27,54,'27:54','اور لوط ؑ کو بھی (ہم نے بھیجا) جب اس نے اپنی قوم سے کہا کہ کیا تم فحش کام کرتے ہو اور تم دیکھتے بھی ہو');
INSERT INTO "bayan" VALUES(27,55,'27:55','کیا تم مردوں کا رخ کرتے ہو شہوت رانی کے لیے عورتوں کو چھوڑ کر ! بلکہ تم بڑے ہی جاہل لوگ ہو');
INSERT INTO "bayan" VALUES(27,56,'27:56','تو اس ؑ کی قوم کا کوئی جواب نہیں تھا مگر یہ کہ انہوں نے کہا : نکال باہر کرو لوط کے گھر والوں کو اپنے شہر سے۔ یہ لوگ بڑے پاکباز بنتے ہیں');
INSERT INTO "bayan" VALUES(27,57,'27:57','تو ہم نے نجات دی اس ؑ کو اور اس ؑ کے گھر والوں کو سوائے اس کی بیوی کے جس کے بارے میں ہم نے طے کردیا تھا کہ وہ پیچھے رہ جانے والوں میں ہوگی');
INSERT INTO "bayan" VALUES(27,58,'27:58','اور ان کے اوپر ہم نے ایک بارش برسائی تو بہت ہی بری بارش تھی جو ان لوگوں پر برسائی گئی جن کو خبردار کردیا گیا تھا');
INSERT INTO "bayan" VALUES(27,59,'27:59','آپ ؑ کہہ دیجیے کہ کل حمد اور کل شکر اللہ کے لیے ہے اور سلام ہے اس کے ان بندوں پر جن کو اس نے ُ چن لیا ہے کیا اللہ بہتر ہے یا وہ جن کو یہ شریک بناتے ہیں');
INSERT INTO "bayan" VALUES(27,60,'27:60','بھلا کون ہے جس نے پیدا کیا آسمانوں اور زمین کو اور آسمان سے تمہارے لیے پانی اتارا پھر اس کے ذریعے سے ہم نے ُ پررونق باغات اگائے تمہارے لیے ممکن نہیں تھا کہ ان کے درختوں کو خود اگا سکتے کیا کوئی اور معبود بھی ہے اللہ کے ساتھ بلکہ یہ ایسے لوگ ہیں جو (حق سے) انحراف کر رہے ہیں');
INSERT INTO "bayan" VALUES(27,61,'27:61','بھلا کس نے بنایا زمین کو ٹھہرنے کی جگہ اور رواں کردیے اس کے اندر دریا (اور ندیاں) اور بنائے اس کے لیے لنگر (پہاڑ) اور بنایا دو دریاؤں کے درمیان پردہ کیا کوئی اور معبود بھی ہے اللہ کے ساتھ بلکہ ان کی اکثریت علم نہیں رکھتی');
INSERT INTO "bayan" VALUES(27,62,'27:62','بھلا کون ہے جو سنتا ہے ایک مجبور و لاچار کو جب وہ اس کو پکارتا ہے اور (اس کی) تکلیف کو دور کرتا ہے اور جو تمہیں جانشین بناتا ہے زمین میں کیا کوئی اور معبود بھی ہے اللہ کے ساتھ (ان کاموں میں شریک) ؟ بہت ہی کم نصیحت ہے جو تم لوگ حاصل کرتے ہو');
INSERT INTO "bayan" VALUES(27,63,'27:63','بھلا کون ہے جو تمہیں راستہ دکھاتا ہے خشکی اور سمندر کے اندھیروں میں ؟ اور کون بھیجتا ہے ہواؤں کو بشارت دیتی ہوئی اپنے باران رحمت کے آگے آگے کیا کوئی اور معبود بھی ہے اللہ کے ساتھ ؟ بہت بلند وبالا ہے اللہ اس شرک سے جو یہ لوگ کرتے ہیں');
INSERT INTO "bayan" VALUES(27,64,'27:64','بھلا کون ہے جو ابتدا میں پیدا کرتا ہے مخلوق کو پھر اس کا اعادہ کرتا ہے ؟ اور کون ہے جو تم لوگوں کو رزق دتیا ہے آسمانوں اور زمین سے کیا کوئی اور معبود بھی ہے اللہ کے ساتھ ؟ آپ صلى الله عليه وسلم کہیے کہ لاؤ اپنی دلیل اگر تم سچے ہو');
INSERT INTO "bayan" VALUES(27,65,'27:65','آپ صلى الله عليه وسلم کہہ دیجیے کہ جو کوئی بھی آسمانوں اور زمین میں ہیں کسی کو بھی غیب کا علم نہیں سوائے اللہ کے اور انہیں تو یہ بھی نہیں معلوم کہ انہیں کب اٹھایا جائے گا');
INSERT INTO "bayan" VALUES(27,66,'27:66','بلکہ تھک ہار کر رہ گیا ہے ان کا علم آخرت کے بارے میں بلکہ اس کے بارے میں وہ شک میں مبتلا ہیں بلکہ اس کی طرف سے وہ اندھے ہوچکے ہیں');
INSERT INTO "bayan" VALUES(27,67,'27:67','اور یہ کافر کہتے ہیں کہ کیا جب ہم اور ہمارے آباء واجداد مٹی ہوجائیں گے تو کیا ہمیں پھر سے نکال لیا جائے گا');
INSERT INTO "bayan" VALUES(27,68,'27:68','یہی وعدہ ہم سے بھی کیا گیا ہے اور اس سے پہلے ہمارے آباء و اَجداد سے بھی کیا گیا تھا یہ کچھ نہیں مگر پہلے لوگوں کی کہانیاں ہیں');
INSERT INTO "bayan" VALUES(27,69,'27:69','آپ صلى الله عليه وسلم کہیے کہ ذرا گھومو پھرو زمین میں اور دیکھ لو کہ کیسا انجام ہوا مجرم قوموں کا');
INSERT INTO "bayan" VALUES(27,70,'27:70','(اے نبی صلى الله عليه وسلم !) آپ ان پر رنجیدہ نہ ہوں اور جو چالیں یہ چل رہے ہیں ان پر دل تنگ نہ کریں');
INSERT INTO "bayan" VALUES(27,71,'27:71','اور وہ کہتے ہیں کہ یہ وعدہ کب پورا ہوگا اگر آپ سچے ہیں');
INSERT INTO "bayan" VALUES(27,72,'27:72','آپ صلى الله عليه وسلم کہہ دیجیے کہ ہوسکتا ہے جس چیز کی تم لوگ جلدی مچا رہے ہو اس کا کچھ حصہ تمہارے قریب ہی آ لگا ہو');
INSERT INTO "bayan" VALUES(27,73,'27:73','اور یقیناً آپ صلى الله عليه وسلم کا رب بڑے فضل والا ہے لوگوں کے حق میں لیکن ان کی اکثریت شکر نہیں کرتی');
INSERT INTO "bayan" VALUES(27,74,'27:74','اور یقیناً آپ صلى الله عليه وسلم کا رب خوب جانتا ہے جو کچھ چھپاتے ہیں ان کے سینے اور جو کچھ وہ ظاہر کرتے ہیں');
INSERT INTO "bayan" VALUES(27,75,'27:75','اور نہیں ہے کوئی پوشیدہ چیز آسمان اور زمین میں مگر وہ ایک روشن کتاب میں موجود ہے');
INSERT INTO "bayan" VALUES(27,76,'27:76','یقیناً یہ قرآن کھول کر بیان کر رہا ہے بنی اسرائیل پر اکثر وہ باتیں جن میں وہ اختلاف کرتے ہیں');
INSERT INTO "bayan" VALUES(27,77,'27:77','اور یقیناً یہ (قرآن) ہدایت اور رحمت ہے اہل ایمان کے حق میں');
INSERT INTO "bayan" VALUES(27,78,'27:78','یقیناً آپ صلى الله عليه وسلم کا رب فیصلہ کردے گا ان کے درمیان اپنے حکم سے۔ اور وہ زبردست ہے سب کچھ جاننے والا');
INSERT INTO "bayan" VALUES(27,79,'27:79','تو (اے نبی صلى الله عليه وسلم !) آپ توکلّ کیجیے اللہ پر۔ یقیناً آپ صلى الله عليه وسلم ہی واضح حق پر ہیں');
INSERT INTO "bayan" VALUES(27,80,'27:80','البتہ آپ صلى الله عليه وسلم ُ مردوں کو نہیں سنا سکتے اور نہ آپ صلى الله عليه وسلم بہروں کو اپنی پکار سنا سکتے ہیں جبکہ وہ پیٹھ پھیر کر چل پڑیں');
INSERT INTO "bayan" VALUES(27,81,'27:81','اور نہ ہی آپ صلى الله عليه وسلم اندھوں کو ان کی گمراہی سے پھیر کر راہ پر لانے والے بن سکتے ہیں آپ صلى الله عليه وسلم نہیں سنا سکتے مگر صرف انہی کو جو ہماری آیات پر ایمان رکھتے ہیں اور وہ فرمانبرداری کی روش اختیار کرتے ہیں');
INSERT INTO "bayan" VALUES(27,82,'27:82','اور جب ان پر ہماری بات واقع ہوجائے گی تو ہم نکالیں گے ان کے لیے زمین سے ایک جانور جو ان سے کلام کرے گا کہ لوگ ہماری نشانیوں پر یقین نہیں رکھتے تھے');
INSERT INTO "bayan" VALUES(27,83,'27:83','اور ذرا تصور کرو اس دن کا جس دن ہم جمع کریں گے ہر امتّ میں سے ایک فوج ان لوگوں میں سے جو ہماری آیات کو جھٹلایا کرتے تھے پھر ان کی درجہ بندی کی جائے گی');
INSERT INTO "bayan" VALUES(27,84,'27:84','یہاں تک کہ جب وہ سب آجائیں گے تو اللہ فرمائے گا : کیا تم نے میری آیات کو جھٹلادیا تھا حالانکہ تم نے ان کا علمی احاطہ نہیں کیا تھا ؟ یا تم لوگ کیا کرتے رہے تھے');
INSERT INTO "bayan" VALUES(27,85,'27:85','اور واقع ہوجائے گی ان پر بات اس لیے کہ وہ ظلم کے مرتکب ہوئے تھے چناچہ وہ بول نہیں سکیں گے');
INSERT INTO "bayan" VALUES(27,86,'27:86','کیا وہ دیکھتے نہیں کہ ہم نے بنایا ہے رات کو تاکہ وہ اس میں آرام کریں اور دن کو روشن بنایا ہے یقیناً اس میں نشانیاں ہیں ان لوگوں کے لیے جو ایمان رکھتے ہیں');
INSERT INTO "bayan" VALUES(27,87,'27:87','اور جس دن صور میں پھونکا جائے گا تو گھبرا اٹھیں گے وہ سب جو آسمانوں اور زمین میں ہیں سوائے ان کے جنہیں اللہ (محفوظ رکھنا) چاہے اور سب حاضر ہوجائیں گے اس کے آگے عاجزی کے ساتھ');
INSERT INTO "bayan" VALUES(27,88,'27:88','اور تم پہاڑوں کو دیکھتے ہو اور سمجھتے ہو کہ وہ خوب جمے ہوئے ہیں اور (اُس دن) وہ چلیں گے جیسے بادل چلتے ہیں یہ اللہ کی کاری گری ہے جس نے ہرچیز کو محکم بنایا ہے یقیناً وہ ہر اس چیز سے باخبر ہے جو تم کر رہے ہو');
INSERT INTO "bayan" VALUES(27,89,'27:89','جو کوئی بھی (اس دن) نیکی لے کر آئے گا تو اس کے لیے اس سے بہتر صلہ ہوگا اور وہ اس دن کی گھبراہٹ سے امن میں ہوں گے');
INSERT INTO "bayan" VALUES(27,90,'27:90','اور جو کوئیُ برائی لے کر آئے گا تو ایسے لوگوں کے منہ آگ میں اوندھے کردیے جائیں گے) اور کہا جائے گا کہ) تمہیں بدلے میں وہی تو دیا جارہا ہے جو تم عمل کرتے رہے ہو');
INSERT INTO "bayan" VALUES(27,91,'27:91','(دیکھو !) مجھے تو یہی حکم ہوا ہے کہ میں بندگی کروں اس شہر کے رب کی جس نے اسے محترم قرار دیا ہے اور اسی کے اختیار میں ہے ہرچیز اور مجھے حکم ہوا ہے کہ میں شامل ہوجاؤں اس کے فرمانبردار بندوں میں');
INSERT INTO "bayan" VALUES(27,92,'27:92','اور یہ کہ میں تلاوت کروں قرآن کی تو جو کوئی ہدایت پائے گا وہ اپنے ہی بھلے کے لیے ہدایت پائے گا اور جو کوئی گمراہی کی روش اختیار کرے تو آپ صلى الله عليه وسلم کہہ دیجیے کہ میں تو صرف خبردار کرنے والا ہوں');
INSERT INTO "bayan" VALUES(27,93,'27:93','اور آپ صلى الله عليه وسلم کہہ دیجیے کہ کل حمد اور کل ثنا اللہ کے لیے ہے عنقریب وہ تمہیں اپنی آیات دکھائے گا تو تم انہیں پہچان لو گے اور آپ کا رب غافل نہیں ہے اس سے جو اعمال تم کر رہے ہو');
INSERT INTO "bayan" VALUES(28,1,'28:1','طٰسۗمّۗ');
INSERT INTO "bayan" VALUES(28,2,'28:2','یہ کتاب روشن کی آیات ہیں');
INSERT INTO "bayan" VALUES(28,3,'28:3','ہم سناتے ہیں آپ ؑ کو موسیٰ ؑ اور فرعون کے کچھ احوال حق کے ساتھ ان لوگوں کے لیے جو ایمان لاتے ہیں');
INSERT INTO "bayan" VALUES(28,4,'28:4','یقیناً فرعون نے بہت سرکشی کی تھی زمین (مصر) میں اور اس نے تقسیم کردیا تھا اس کے باسیوں کو گروہوں میں اس نے دبا رکھا تھا ان میں سے ایک گروہ کو وہ ان کے بیٹوں کو ذبح کردیتا تھا اور ان کی عورتوں کو زندہ رہنے دیتا تھا یقیناً وہ فساد مچانے والوں میں سے تھا');
INSERT INTO "bayan" VALUES(28,5,'28:5','اور ہم نے ارادہ کیا کہ ہم احسان کریں ان لوگوں پر جو زمین میں دبا دیے گئے تھے اور (ہم نے چاہا کہ) ہم انہیں امام بنا دیں اور انہی کو ہم وارث بھی بنائیں');
INSERT INTO "bayan" VALUES(28,6,'28:6','اور ہم تمکن عطا کریں ان کو زمین میں اور ہم دکھا دیں فرعون ہامان اور ان کے لشکروں کو وہ کچھ جس سے وہ ڈرتے تھے');
INSERT INTO "bayan" VALUES(28,7,'28:7','اور ہم نے وحی کردی موسیٰ ؑ کی والدہ کو کہ تم اسے دودھ پلاتی رہو اور جب تمہیں اس کے بارے میں اندیشہ ہو تو اسے دریا میں ڈال دینا اور نہ خوف کھانا اور نہ رنجیدہ ہونا ہم اسے لوٹانے والے ہیں تمہاری طرف اور اسے بنانے والے ہیں رسولوں میں سے');
INSERT INTO "bayan" VALUES(28,8,'28:8','تو اسے اٹھا لیا فرعون کے گھر والوں نے تاکہ وہ بن جائے ان کے لیے دشمن اور پریشانی (کا باعث) یقیناً فرعون ہامان اور ان کے سب لشکر (اپنی تدبیر میں) خطاکار تھے');
INSERT INTO "bayan" VALUES(28,9,'28:9','اور فرعون کی بیوی نے کہا کہ یہ آنکھوں کی ٹھنڈک ہوگا میرے لیے بھی اور تمہارے لیے بھی تم اسے قتل مت کرو کیا عجب کہ یہ ہمیں کوئی فائدہ پہنچائے یا ہم اسے بیٹا ہی بنا لیں اور وہ (انجام سے) بالکل بیخبر تھے');
INSERT INTO "bayan" VALUES(28,10,'28:10','اور (ادھر) موسیٰ ؑ کی ماں کا دل حوصلہ چھوڑ بیٹھا قریب تھا کہ وہ اس (راز) کو ظاہر ہی کردیتی اگر ہم نے اس کے دل کو مضبوط نہ کردیا ہوتا تاکہ وہ ہوجائے ایمان والوں میں سے');
INSERT INTO "bayan" VALUES(28,11,'28:11','اور اس نے موسیٰ ؑ کی بہن سے کہا کہ تو اس کے پیچھے پیچھے جا تو وہ اس کو دور سے دیکھتی رہی اور انہیں احساس بھی نہ ہوا');
INSERT INTO "bayan" VALUES(28,12,'28:12','اور ہم نے اس پر پہلے ہی حرام کردی تھیں تمام دودھ پلانے والی عورتیں تو اس لڑکی نے ان سے کہا : کیا میں تمہیں ایک ایسے گھر والوں کے بارے میں بتاؤں جو تمہارے لیے اس کی پرورش کردیں اور وہ اس کے خیر خواہ بھی ہوں');
INSERT INTO "bayan" VALUES(28,13,'28:13','تو یوں ہم نے اسے لوٹادیا اس کی والدہ کے پاس تاکہ اس کی آنکھیں ٹھنڈی رہیں اور وہ رنجیدہ نہ ہو اور اسے معلوم ہوجائے کہ اللہ کا وعدہ سچا ہے لیکن اکثر لوگ جانتے نہیں ہیں');
INSERT INTO "bayan" VALUES(28,14,'28:14','اور جب موسیٰ اپنی جوانی کو پہنچ گیا اور ہر لحاظ سے تواناہو گیا تو ہم نے اسے حکم اور علم عطا فرمایا اور اسی طرح ہم بدلہ دیتے ہیں نیکو کاروں کو');
INSERT INTO "bayan" VALUES(28,15,'28:15','اور (ایک روز) وہ شہر میں داخل ہوا ایسے وقت جبکہ اس کے باسی غفلت میں (پڑے سو رہے) تھے تو اس نے وہاں پایا دو اشخاص کو آپس میں لڑتے ہوئے ایک اس کی اپنی قوم میں سے تھا اور دوسرا اس کے دشمنوں میں سے تو مدد مانگی موسیٰ ؑ سے اس نے جو اس کی قوم میں سے تھا اس کے خلاف جو اس کی دشمن قوم سے تھا تو موسیٰ ؑ نے اس کو ایک مکاّ رسید کیا اور اس کا کام تمام کردیا (یہ دیکھتے ہی) وہ پکار اٹھا کہ یہ تو شیطان کا عمل ہے۔ یقیناً وہ دشمن ہے کھلا گمراہ کرنے والا');
INSERT INTO "bayan" VALUES(28,16,'28:16','عرض کیا : اے میرے پروردگار ! میں تو اپنی جان پر ظلم کر بیٹھا ہوں پسُ تو مجھے بخش دے تو اللہ نے اسے بخش دیا یقیناً وہی ہے بڑا بخشنے والا رحم فرمانے والا');
INSERT INTO "bayan" VALUES(28,17,'28:17','اس نے کہا : پروردگار ! بسبب اس احسان کے جو تو نے مجھ پر کیا ہے (میں عہد کرتا ہوں کہ) اب میں کبھی مددگار نہیں بنوں گا مجرموں کا');
INSERT INTO "bayan" VALUES(28,18,'28:18','تو اس نے صبح کی شہر میں (اس کیفیت میں) کہ وہ خوف زدہ بھی تھا اور چوکنا بھی تو اچانک (اس نے دیکھا کہ) جس شخص نے کل اس سے مدد مانگی تھی وہی آج پھر اس سے فریاد کر رہا ہے موسیٰ ؑ نے اس سے کہا کہ (معلوم ہوتا ہے) تم ہی ایک کھلے ہوئے شریر آدمی ہو');
INSERT INTO "bayan" VALUES(28,19,'28:19','پھر جب اس نے پکڑنا چاہا اس کو جو ان دونوں کا دشمن تھا وہ چیخ اٹھا : اے موسیٰ ؑ ! کیا تم مجھے بھی قتل کرنا چاہتے ہو جس طرح تم نے کل ایک شخص کو مارڈالا تھا تم صرف یہ چاہتے ہو کہ تم اس ملک میں بڑے جابر بن جاؤ اور تم نہیں چاہتے کہ تم اصلاح کرنے والوں میں سے بنو');
INSERT INTO "bayan" VALUES(28,20,'28:20','اور ایک شخص آیا شہر کے آخری کنارے سے دوڑتا ہوا اس نے کہا : اے موسیٰ ؑ ٰ ! اعیان حکومت تمہارے بارے میں مشورہ کر رہے ہیں کہ تمہیں قتل کردیں تو تم یہاں سے نکل جاؤ میں تمہارے خیر خواہوں میں سے ہوں');
INSERT INTO "bayan" VALUES(28,21,'28:21','تو وہ نکلا وہاں سے ڈرتے ڈرتے بڑا چوکنا ہو کر اور اس نے دعا کی کہ اے میرے پروردگار ! مجھے ان ظالموں کی قوم سے نجات دے دے');
INSERT INTO "bayan" VALUES(28,22,'28:22','اور جب اس نے مدین کی طرف رخ کیا اس نے کہا : امید ہے میرا رب سیدھے راستے کی طرف میری راہنمائی کرے گا');
INSERT INTO "bayan" VALUES(28,23,'28:23','اور جب وہ مدین کے کنویں پر پہنچا اس نے اس پر لوگوں کے ایک گروہ کو (اپنے جانوروں کو) پانی پلاتے ہوئے پایا اور اس نے ان سے الگ دو عورتوں کو دیکھا جو (اپنے جانوروں کو) روکے کھڑی تھیں اس نے کہا : آپ دونوں کا کیا معاملہ ہے انہوں نے کہا کہ ہم (اپنے جانوروں کو) پانی نہیں پلا سکتے جب تک تمام چرواہے (اپنے جانور) نکال نہ لے جائیں اور ہمارے والد بہت زیادہ بوڑھے ہیں');
INSERT INTO "bayan" VALUES(28,24,'28:24','تو موسیٰ ؑ نے ان دونوں کے لیے پانی پلا دیا پھر سائے کی طرف پھر آیا تو اس نے دعا کی : پروردگار ! جو خیر بھی تو میری جھولی میں ڈال دے میں اس کا محتاج ہوں');
INSERT INTO "bayan" VALUES(28,25,'28:25','اتنے میں اس کے پاس ان دو میں سے ایک لڑکی شرم و حیا کے ساتھ چلتی ہوئی آئی اس نے کہا : میرے والد آپ کو بلا رہے ہیں تاکہ وہ بدلہ دیں آپ کو اس کا جو آپ نے ہمارے لیے پانی پلایا ہے تو جب موسیٰ ؑ اس کے پاس آیا اور اس نے اسے اپنا سارا قصہ سنایا اس نے کہا : اب ڈرو نہیں تم نجات پاچکے ہو ظالموں کی قوم سے');
INSERT INTO "bayan" VALUES(28,26,'28:26','ان دونوں میں سے ایک نے کہا : ابا جان ! آپ ان کو ملازم رکھ لیں یقیناً بہترین آدمی جسے آپ ملازم رکھیں وہی ہوسکتا ہے جو طاقتور اور امین ہو');
INSERT INTO "bayan" VALUES(28,27,'28:27','اس نے (موسیٰ ؑ سے) کہا کہ میں چاہتا ہوں کہ اپنی ان دونوں بیٹیوں میں سے ایک کا نکاح تمہارے ساتھ کر دوں اس شرط پر کہ تم آٹھ سال تک میری ملازمت کرو پھر اگر تم دس سال پورے کر دو تو یہ تمہاری طرف سے ہے اور میں تمہیں مشقت میں ڈالنے کا کوئی ارادہ نہیں رکھتا ان شاء اللہ تم مجھے نیک آدمی پاؤ گے');
INSERT INTO "bayan" VALUES(28,28,'28:28','موسیٰ ؑ ٰ نے کہا : (ٹھیک ہے) یہ بات میرے اور آپ کے درمیان طے ہوگئی ان دونوں میں سے جو مدت بھی میں پوری کردوں تو مجھ پر کوئی زیادتی نہ ہو اور جو کچھ (اس وقت) ہم کہہ رہے ہیں اللہ اس پر وکیل ہے');
INSERT INTO "bayan" VALUES(28,29,'28:29','تو جب موسیٰ ؑ نے وہ مدت پوری کردی اور وہ اپنے گھر والوں کو لے کر چلاتو اس نے طور پہاڑ کے ایک جانب ایک آگ دیکھی اس نے اپنے گھر والوں سے کہا کہ تم لوگ (یہیں) ٹھہرو میں نے ایک آگ دیکھی ہے شاید میں وہاں سے تمہارے لیے کوئی خبر لے آؤں یا اس آگ سے کوئی انگارا (ہی لے آؤں) تاکہ تم تاپ سکو');
INSERT INTO "bayan" VALUES(28,30,'28:30','تو جب وہ وہاں پہنچا تو اسے ندا دی گئی وادی کے دائیں کنارے سے بابرکت جگہ میں ایک درخت سے کہ اے موسیٰ ؑ ٰ ! میں ہی اللہ ہوں تمام جہانوں کا پروردگار');
INSERT INTO "bayan" VALUES(28,31,'28:31','اور یہ کہ تم اپنی لاٹھی ڈال دو تو جب اس نے دیکھا کہ وہ حرکت کر رہی ہے جیسے کہ سانپ ہو تو وہ پیٹھ پھیر کر بھاگا اور اس نے پیچھے مڑ کر بھی نہ دیکھا (اللہ نے فرمایا :) اے موسیٰ ! آگے آؤ اور ڈرو نہیں تم بالکل مأمون ہو');
INSERT INTO "bayan" VALUES(28,32,'28:32','اپنا ہاتھ اپنے گریبان میں ڈالو وہ نکلے گا سفید ہو کر بغیر کسی بیماری کے اور اپنا بازو اپنے ساتھ چپکا لو خوف سے (بچنے کے لیے) تو یہ دو نشانیاں ہیں تمہارے رب کی طرف سے فرعون اور اس کے سرداروں کی طرف یقیناً وہ بڑے نافرمان لوگ ہیں');
INSERT INTO "bayan" VALUES(28,33,'28:33','اس نے کہا : پروردگار ! میں نے ان میں سے ایک شخص کو قتل کردیا تھا چناچہ مجھے اندیشہ ہے کہ وہ مجھے قتل کردیں گے');
INSERT INTO "bayan" VALUES(28,34,'28:34','اور میرا بھائی ہارون ؑ مجھ سے زیادہ فصیح زبان والا ہے تو اسے میرے ساتھ مددگار کے طور پر بھیج تاکہ وہ میری تصدیق کرے مجھے اندیشہ ہے کہ وہ مجھے جھٹلا دیں گے');
INSERT INTO "bayan" VALUES(28,35,'28:35','اللہ نے فرمایا : ہم تمہارے بازو کو مضبوط کریں گے تمہارے بھائی کے ذریعے سے اور ہم تم دونوں کے لیے ایسا رعب پیدا کردیں گے کہ وہ تمہیں ہاتھ نہیں لگا سکیں گے ہماری نشانیوں کی بدولت تم دونوں اور تمہارے پیروکار سب غالب رہیں گے');
INSERT INTO "bayan" VALUES(28,36,'28:36','تو جب موسیٰ ؑ پہنچا ان کے پاس ہماری روشن نشانیاں لے کر انہوں نے کہا کہ یہ کچھ بھی نہیں سوائے گھڑے ہوئے جادو کے اور ہم نے ایسی کوئی بات اپنے پہلے آباء و اَجداد میں نہیں سنی');
INSERT INTO "bayan" VALUES(28,37,'28:37','اور موسیٰ ؑ نے کہا : میرا پروردگار خوب جانتا ہے اس کو جو ہدایت لے کر آیا ہے اس کی طرف سے اور (وہی خوب جانتا ہے کہ) کس کے لیے دار آخرت کا اچھا انجام ہے یقیناظالم لوگ فلاح نہیں پائیں گے');
INSERT INTO "bayan" VALUES(28,38,'28:38','اور فرعون نے کہا : اے درباریو ! میں تو اپنے سوا تمہارے لیے کسی معبود کو نہیں جانتا تو اے ہامان ! ذرا تم میرے لیے مٹی کی اینٹوں کو آگ سے پختہ کرو پھر میرے لیے ایک اونچا محل بنواؤ تاکہ میں جھانک سکوں موسیٰ کے الٰہ کو اور میں تو سمجھتا ہوں کہ وہ جھوٹا ہے');
INSERT INTO "bayan" VALUES(28,39,'28:39','اور زمین میں ناحق تکبر کیا اس نے بھی اور اس کے لشکروں نے بھی اور انہوں نے سمجھا کہ وہ ہماری طرف لوٹائے نہیں جائیں گے');
INSERT INTO "bayan" VALUES(28,40,'28:40','اور ہم نے پکڑ لیا اس کو بھی اور اس کے لشکروں کو بھی پھر ہم نے پھینک دیا انہیں سمندر میں تو دیکھ لو کیا انجام ہوا ظالموں کا');
INSERT INTO "bayan" VALUES(28,41,'28:41','اور ہم نے بنا دیا انہیں امام آگ کی طرف بلانے والے اور قیامت کے دن ان کی کوئی مدد نہیں کی جائے گی');
INSERT INTO "bayan" VALUES(28,42,'28:42','اور ہم نے ان کے پیچھے لعنت لگا دی اس دنیا میں بھی اور قیامت کے دن وہ بہت ہی برے حال والوں میں سے ہوں گے');
INSERT INTO "bayan" VALUES(28,43,'28:43','اور ہم نے موسیٰ ؑ کو کتاب عطا فرمائی اس کے بعد کہ ہم نے پہلی جماعتوں کو ہلاک کردیا تھا (یہ کتاب) لوگوں کی آنکھیں کھولنے کے لیے تھی اور ہدایت اور رحمت تھی تاکہ وہ نصیحت حاصل کریں');
INSERT INTO "bayan" VALUES(28,44,'28:44','اور (اے نبی صلى الله عليه وسلم !) آپ موجود نہیں تھے (اس پہاڑ کے) غربی جانب جب ہم نے موسیٰ ؑ کی طرف حکم بھیجا تھا اور نہ آپ صلى الله عليه وسلم ان لوگوں میں شامل تھے جو وہاں حاضر تھے');
INSERT INTO "bayan" VALUES(28,45,'28:45','لیکن ہم نے پیدا کیں بہت سی نسلیں تو ان کے اوپر ایک لمبی عمر بیت گئی اور نہ ہی آپ صلى الله عليه وسلم اہل مدین کے درمیان مقیم تھے کہ ان کو ہماری آیات پڑھ کر سناتے بلکہ یہ تو ہم ہیں (رسولوں کو) بھیجنے والے');
INSERT INTO "bayan" VALUES(28,46,'28:46','اور نہ آپ صلى الله عليه وسلم (اس وقت) طور کے پاس موجود تھے جب ہم نے (موسیٰ ؑ کو) پکارا تھا بلکہ یہ سب رحمت ہے آپ صلى الله عليه وسلم کے رب کی طرف سے تاکہ آپ صلى الله عليه وسلم اس قوم کو خبردار کریں جس کی طرف آپ صلى الله عليه وسلم سے پہلے کوئی خبردار کرنے والا نہیں آیا شاید کہ وہ نصیحت حاصل کریں');
INSERT INTO "bayan" VALUES(28,47,'28:47','اور (یہ ہم نے اس لیے کیا کہ) کہیں ایسا نہ ہو کہ جب انہیں کوئی مصیبت پہنچے ان کے کرتوتوں کے سبب تو یہ کہیں کہ اے ہمارے پروردگار ! ُ تو نے کیوں نہ بھیجا ہماری طرف کوئی رسول کہ ہم تیری آیات کی پیروی کرتے اور ایمان لانے والوں میں سے ہوجاتے');
INSERT INTO "bayan" VALUES(28,48,'28:48','لیکن جب ان کے پاس ہماری طرف سے حق آگیا تو کہنے لگے کہ ان (رسول صلى الله عليه وسلم کو وہی کچھ کیوں نہیں دیا گیا جو موسیٰ ؑ کو دیا گیا تھا تو جو کچھ پہلے موسیٰ ؑ کو دیا گیا تھا کیا لوگوں نے اس کے ساتھ کفر نہیں کیا تھا انہوں نے کہا کہ یہ تو دو جادو ہیں ایک دوسرے کے مددگار اور انہوں نے کہا کہ ہم تو دونوں کا انکار کرتے ہیں');
INSERT INTO "bayan" VALUES(28,49,'28:49','آپ صلى الله عليه وسلم کہیے کہ پھر لاؤ کوئی ایسی کتاب اللہ کے پاس سے جو ان دونوں سے زیادہ ہدایت والی ہو میں اس کی پیروی کروں گا اگر تم سچے ہو');
INSERT INTO "bayan" VALUES(28,50,'28:50','پھر اگر یہ لوگ آپ صلى الله عليه وسلم کی بات کو قبول نہ کریں تو جان لیں کہ یہ لوگ صرف اپنی خواہشات کی پیروی کر رہے ہیں اور اس شخص سے بڑھ کر کون گمراہ ہوگا جو اپنی خواہشات کی پیروی کر رہا ہو اللہ کی طرف سے کسی ہدایت کے بغیر یقیناً اللہ ایسی ظالم قوم کو ہدایت نہیں دیتا');
INSERT INTO "bayan" VALUES(28,51,'28:51','اور ہم نے لگاتار بھیجا ان کے لیے اپنا کلام تاکہ یہ نصیحت اخذ کریں');
INSERT INTO "bayan" VALUES(28,52,'28:52','جن لوگوں کو ہم نے اس سے پہلے کتاب دی تھی وہ اس (قرآن) پر ایمان رکھتے ہیں');
INSERT INTO "bayan" VALUES(28,53,'28:53','اور جب یہ (قرآن) انہیں پڑھ کر سنایا جاتا ہے تو وہ کہتے ہیں کہ ہم اس پر ایمان لائے یقیناً یہ ہمارے رب کی طرف سے حق ہے ہم تو اس سے پہلے ہی مسلم تھے');
INSERT INTO "bayan" VALUES(28,54,'28:54','یہ وہ لوگ ہیں جنہیں ان کا اجر دیا جائے گا دو مرتبہ بسبب اس کے کہ وہ ثابت قدم رہے اور وہ بھلائی کے ذریعے سے برائی کو دفع کرتے ہیں اور جو کچھ ہم نے انہیں دیا ہے اس میں سے وہ خرچ کرتے ہیں');
INSERT INTO "bayan" VALUES(28,55,'28:55','اور جب انہوں نے لغو باتیں سنیں تو انہوں نے ان سے اعراض کیا اور کہا کہ ہمارے لیے ہمارے اعمال ہیں اور تمہارے لیے تمہارے اعمال تم لوگوں پر سلام ! ہم جاہلوں سے الجھنا نہیں چاہتے');
INSERT INTO "bayan" VALUES(28,56,'28:56','(اے نبی صلى الله عليه وسلم !) آپ ہدایت نہیں دے سکتے جس کو آپ چاہیں بلکہ اللہ ہدایت دیتا ہے جس کو چاہتا ہے اور وہ خوب جانتا ہے ہدایت پانے والوں کو');
INSERT INTO "bayan" VALUES(28,57,'28:57','اور وہ کہتے ہیں کہ اگر ہم آپ صلى الله عليه وسلم کے ساتھ اس ہدایت کی پیروی کریں تو ہم اپنی زمین سے اچک لیے جائیں گے کیا ہم نے انہیں امن والے حرم میں متمکن نہیں کیا کھنچے چلے آتے ہیں اس (حرم) کی طرف ہر قسم کے پھل یہ خاص رزق ہے ہماری طرف سے لیکن ان کی اکثریت علم نہیں رکھتی');
INSERT INTO "bayan" VALUES(28,58,'28:58','اور کتنی ہی بستیوں کو ہم نے ہلاک کردیا جو کہ اتراتی تھیں اپنی معیشت پر تو (دیکھ لو !) یہ ہیں ان کے (کھنڈر بنے ہوئے) گھر نہیں ہوئے ان میں رہنے والے ان کے بعد مگر بہت تھوڑے۔ اور ہم ہی (ان کے) وارث ہو کر رہے');
INSERT INTO "bayan" VALUES(28,59,'28:59','اور نہیں تھا آپ کا رب بستیوں کو ہلاک کرنے والا جب تک کہ وہ ان کی مرکزی بستی میں کوئی رسول نہ بھیج دیتا جو ان کو پڑھ کر سناتا تھا ہماری آیات اور ہم ہرگز ان بستیوں کو ہلاک کرنے والے نہیں تھے مگر اس بنا پر کہ ان کے باسی ظالم تھے');
INSERT INTO "bayan" VALUES(28,60,'28:60','اور جو کچھ بھی تم لوگوں کو دیا گیا ہے وہ بس دنیوی زندگی کا سامان اور اس کی زیب وزینت ہے اور جو کچھ اللہ کے پاس ہے وہ بہتر ہے اور باقی رہنے والا ہے۔ تو کیا تم لوگ عقل سے کام نہیں لیتے');
INSERT INTO "bayan" VALUES(28,61,'28:61','بھلا وہ شخص جس سے ہم نے اچھا وعدہ کیا ہے اور وہ اس (وعدے کو) پالینے والا بھی ہے اس شخص جیسا ہوجائے گا جسے ہم نے دنیوی زندگی کا سازو سامان دے دیا ہو پھر وہ قیامت کے دن گرفتار کر کے حاضر کیے جانے والوں میں سے ہوگا');
INSERT INTO "bayan" VALUES(28,62,'28:62','جس دن اللہ انہیں پکارے گا اور کہے گا کہ کہاں ہیں میرے وہ شریک جن کا تم زعم رکھتے تھے');
INSERT INTO "bayan" VALUES(28,63,'28:63','تو کہیں گے وہ لوگ جو (عذاب کے) فیصلے کے مستحق ہوچکے ہوں گے : اے ہمارے پروردگار ! یہی وہ لوگ ہیں جنہوں نے ہم کو گمراہ کیا تھا (وہ جواب میں کہیں گے) ہم نے انہیں گمراہ کیا جیسے ہم خود گمراہ ہوئے تھے ہم تیرے سامنے (ان سے) اظہار براءت کرتے ہیں یہ ہماری پرستش تو نہیں کرتے تھے');
INSERT INTO "bayan" VALUES(28,64,'28:64','اور ان سے کہا جائے گا کہ اب تم پکارو اپنے شریکوں کو تو وہ انہیں پکاریں گے لیکن وہ انہیں کوئی جواب نہیں دیں گے اور وہ دیکھ لیں گے عذاب کو کاش وہ ہدایت پائے ہوئے ہوتے');
INSERT INTO "bayan" VALUES(28,65,'28:65','اور جس دن اللہ انہیں پکارے گا اور پوچھے گا کہ تم لوگوں نے کیا جواب دیا تھا رسولوں کو');
INSERT INTO "bayan" VALUES(28,66,'28:66','تو اندھی ہوجائیں گی ان پر تمام خبریں اس دن پھر وہ ایک دوسرے سے پوچھ بھی نہیں سکیں گے');
INSERT INTO "bayan" VALUES(28,67,'28:67','تو جس نے توبہ کی اور ایمان لایا اور نیک اعمال کیے تو امید ہے کہ وہ فلاح پانے والے والوں میں سے ہوگا');
INSERT INTO "bayan" VALUES(28,68,'28:68','اور آپ صلى الله عليه وسلم کا رب پیدا کرتا ہے جو چاہتا ہے اور منتخب کرلیتا ہے (جسے چاہتا ہے) (جبکہ) ان کو کچھ بھی اختیار نہیں۔ اللہ پاک ہے اور بہت بلند ہے اس سے جو یہ لوگ شرک کرتے ہیں');
INSERT INTO "bayan" VALUES(28,69,'28:69','اور آپ صلى الله عليه وسلم کا رب خوب جانتا ہے جو کچھ چھپاتے ہیں ان کے سینے اور جو کچھ وہ ظاہر کرتے ہیں');
INSERT INTO "bayan" VALUES(28,70,'28:70','اور وہی ہے اللہ اس کے سوا کوئی معبود نہیں اسی کے لیے حمد ہے دنیا میں بھی اور آخرت میں بھی اور اسی کا حکم ہے اور اسی کی طرف تم سب لوٹائے جاؤ گے');
INSERT INTO "bayan" VALUES(28,71,'28:71','آپ صلى الله عليه وسلم کہیے : کیا تم لوگوں نے کبھی غور کیا کہ اگر اللہ تم پر رات مسلط کر دے ہمیشہ کے لیے قیامت کے دن تک تو کون معبود ہوگا اللہ کے سوا جو تمہارے لیے روشنی لائے گا ؟ تو کیا تم لوگ سنتے نہیں ہو');
INSERT INTO "bayan" VALUES(28,72,'28:72','آپ صلى الله عليه وسلم کہیے : کیا تم لوگوں نے کبھی یہ سوچا کہ اگر اللہ تم پر ہمیشہ کے لیے دن ہی کو قائم کر دے قیامت کے دن تک تو کون معبود ہے اللہ کے سوا جو تمہارے لیے رات لائے گا جس میں تم آرام کرسکو ؟ تو کیا تم لوگ دیکھتے نہیں ہو');
INSERT INTO "bayan" VALUES(28,73,'28:73','اور اس کی رحمت (کے مظاہر) میں سے ہے کہ اس نے تمہارے لیے رات اور دن بنائے تاکہ اس (رات) میں تم لوگ آرام کرو اور (دن میں) اس کا فضل تلاش کرو اور تاکہ تم (اس کی ان نعمتوں کا) شکر ادا کرو');
INSERT INTO "bayan" VALUES(28,74,'28:74','اور جس دن وہ انہیں پکارے گا اور پوچھے گا کہ کہاں ہیں میرے وہ شریک جن کا تم لوگوں کو زعم تھا');
INSERT INTO "bayan" VALUES(28,75,'28:75','اور ہم نکالیں گے ہر امت میں سے ایک گواہ پھر ہم کہیں گے کہ تم اپنی دلیل پیش کرو تو وہ جان لیں گے کہ حق تو اللہ ہی کے طرف ہے اور گم ہوجائے گا ان سے وہ سب کچھ جو افتراوہ کرتے رہے تھے');
INSERT INTO "bayan" VALUES(28,76,'28:76','یقیناً قارون موسیٰ ؑ کی قوم ہی سے تھا لیکن اس نے ان کے خلاف سر کشی کی اور اس کو ہم نے اتنے خزانے دے رکھے تھے کہ ان کی چابیاں ایک طاقتور جماعت مشکل سے اٹھا سکتی تھی جب اس سے کہا اس کی قوم کے لوگوں نے کہ اتراؤ مت یقیناً اللہ اترانے والوں کو پسند نہیں کرتا');
INSERT INTO "bayan" VALUES(28,77,'28:77','اور جو کچھ اللہ نے تمہیں دیا ہے اس سے دار آخرت حاصل کرنے کی کوشش کرو اور مت بھولو تم دنیا سے اپنا حصہ اور لوگوں کے ساتھ احسان کرو جیسے اللہ نے تمہارے ساتھ احسان کیا ہے اور زمین میں فساد مت مچاؤ یقیناً اللہ فساد مچانے والوں کو پسند نہیں کرتا');
INSERT INTO "bayan" VALUES(28,78,'28:78','اس نے کہا کہ مجھے تو یہ سب کچھ ملا ہے اس علم کی بنیاد پر جو میرے پس ہے کیا اسے معلوم نہیں کہ اللہ اس سے پہلے کتنی ہی قوموں کو ہلاک کرچکا ہے جو طاقت اور مال و اسباب کی فراوانی میں اس سے کہیں بڑھ کر تھیں اور (اللہ کے ہاں) مجرم لوگوں سے ان کی خطاؤں کے بارے میں پوچھا بھی نہیں جاتا');
INSERT INTO "bayan" VALUES(28,79,'28:79','پھر (ایک دن) وہ نکلا اپنی قوم کے سامنے اپنے پورے ٹھاٹھ باٹھ میں کہا ان لوگوں نے جو خواہش مند تھے دنیا ہی کی زندگی کے کہ کاش یہ سب کچھ ہمارے لیے بھی ہوتا جو قارون کو ملا ہے یقیناً وہ بہت بڑے نصیب والا ہے');
INSERT INTO "bayan" VALUES(28,80,'28:80','اور کہا ان لوگوں نے جنہیں علم عطا ہو اتھا افسوس ہے تم پر ! اللہ کا (عطا کردہ) ثواب (اس سے) کہیں بہتر ہے اس شخص کے لیے جو ایمان لایا اور اس نے نیک عمل کیے اور وہ نہیں ملے گا مگر ان لوگوں کو جو صبر کرنے والے ہیں');
INSERT INTO "bayan" VALUES(28,81,'28:81','تو ہم نے اسے اور اس کے محل کو زمین میں دھنسا دیا تو اس کے لیے نہ تو کوئی ایسا لشکر تھا جو اس کی مدد کرتا اللہ کے مقابلے میں اور نہ وہ خود اس قابل تھا کہ بدلہ لینے والوں میں ہوتا');
INSERT INTO "bayan" VALUES(28,82,'28:82','اور جن لوگوں نے کل اس کے مرتبے کی تمنا کی تھی اب وہ یوں کہہ رہے تھے ہائے افسوس ! اللہ ہی کشادہ کرتا ہے رزق جس کا چاہتا ہے اپنے بندوں میں سے اور وہی تنگ کرتا ہے اگر اللہ نے ہم پر احسان نہ کیا ہوتا تو ہمیں بھی زمین میں دھنسا دیتا افسوس ! کہ کافر بالکل فلاح نہیں پائیں گے');
INSERT INTO "bayan" VALUES(28,83,'28:83','یہ آخرت کا گھر تو ہم ان لوگوں کے لیے مخصوص کرتے ہیں جو نہ تو زمین میں اقتدار و اختیار کے خواہاں ہیں اور نہ ہی فساد مچانا چاہتے ہیں اور آخرت کی زندگی تو ہے ہی اہل تقویٰ کے لیے');
INSERT INTO "bayan" VALUES(28,84,'28:84','جو کوئی نیکی لے کر آئے گا تو اس کے لیے بدلہ ہوگا اس سے بہتر اور جو کوئیُ برائی لے کر آئے گا تو برائیوں کا ارتکاب کرنے والوں کو بدلہ نہیں دیا جائے گا مگر وہی کہ جو کچھ وہ کرکے لائیں گے');
INSERT INTO "bayan" VALUES(28,85,'28:85','(اے نبی صلى الله عليه وسلم !) یقیناً جس نے آپ پر قرآن کی ذمہ داری ڈالی ہے وہ آپ کو پہنچا کے رہے گا ایک بہت اچھی لوٹنے کی جگہ آپ صلى الله عليه وسلم کہہ دیجیے کہ میرا رب خوب جانتا ہے کون ہدایت لے کر آیا ہے اور کون ہے جو کھلی گمراہی میں پڑا ہوا ہے');
INSERT INTO "bayan" VALUES(28,86,'28:86','اور (اے نبی صلى الله عليه وسلم !) آپ کو تو کوئی توقع نہیں تھی کہ آپ پر کتاب القا کی جائے گی یہ تو محض آپ صلى الله عليه وسلم کے رب کی رحمت سے ہے پس آپ صلى الله عليه وسلم کافروں کے پشت پناہ نہ بنیں');
INSERT INTO "bayan" VALUES(28,87,'28:87','اور وہ آپ صلى الله عليه وسلم کو روک نہ سکیں اللہ کی آیات سے اس کے بعد کہ وہ آپ پر نازل کردی گئی ہیں اور اپنے رب کی طرف دعوت دیتے رہیے اور شرک (جیسے گھناؤنے جرم) کا شائبہ بھی اپنے قریب مت آنے دیجیے');
INSERT INTO "bayan" VALUES(28,88,'28:88','اور مت پکارئیے اللہ کے ساتھ کسی دوسرے معبود کو۔ اس کے سوا کوئی معبود نہیں ہے ہرچیز فنا ہونے والی ہے سوائے اس کے چہرے کے فرماں روائی اسی کی ہے اور اسی کی طرف تم سب لوٹا دیے جاؤ گے');
INSERT INTO "bayan" VALUES(29,1,'29:1','الۗمّۗ');
INSERT INTO "bayan" VALUES(29,2,'29:2','کیا لوگوں نے یہ سمجھا تھا کہ وہ چھوڑ دیے جائیں گے صرف یہ کہنے سے کہ ہم ایمان لے آئے اور انہیں آزمایا نہ جائے گا');
INSERT INTO "bayan" VALUES(29,3,'29:3','ہم نے تو ان کو بھی آزمایا تھا جو ان سے پہلے تھے پس اللہ ظاہر کر کے رہے گا ان کو جو سچے ہیں اور ان کو بھی جو جھوٹے ہیں');
INSERT INTO "bayan" VALUES(29,4,'29:4','کیا سمجھ رکھا ہے ان لوگوں نے جو برائیوں کا ارتکاب کر رہے ہیں کہ وہ ہماری پکڑ سے بچ کر نکل جائیں گے ؟ بہت ہی برا ہے جو وہ فیصلہ کرتے ہیں');
INSERT INTO "bayan" VALUES(29,5,'29:5','جو کوئی بھی اللہ کی ملاقات کا امیدوار ہے تو (اسے یقین رکھنا چاہیے کہ) یقیناً اللہ کا معینّ کردہ وقت آکر رہے گا اور وہ سب کچھ سننے والا ہرچیز کا علم رکھنے والا ہے');
INSERT INTO "bayan" VALUES(29,6,'29:6','اور جو کوئی بھی جہاد کرتا ہے تو وہ اپنے (ہی فائدے کے) لیے جہاد کرتا ہے۔ یقیناً اللہ تمام جہانوں سے بےنیاز ہے');
INSERT INTO "bayan" VALUES(29,7,'29:7','اور جو لوگ ایمان لائے اور انہوں نے نیک اعمال کیے ہم لازماً دور کردیں گے ان سے ان کی برائیاں اور ہم لازماً ان کو بہترین بدلہ دیں گے ان کے اعمال کا');
INSERT INTO "bayan" VALUES(29,8,'29:8','اور ہم نے ہدایت کی ہے انسان کو اپنے والدین کے ساتھ حسن سلوک کی اور اگر وہ تم پر دباؤ ڈالیں کہ تم میرے ساتھ شریک ٹھہراؤ ایسی چیزوں کو جن کے بارے میں تمہیں کوئی علم نہیں تو ان کا کہنا مت مانو میری ہی طرف تمہیں لوٹ کر آنا ہے پھر میں تمہیں بتادوں گا جو کچھ تم کرتے رہے تھے');
INSERT INTO "bayan" VALUES(29,9,'29:9','اور وہ لوگ جو ایمان لائے اور انہوں نے نیک اعمال کیے ہم انہیں لازماً داخل کریں گے صالحین میں');
INSERT INTO "bayan" VALUES(29,10,'29:10','اور لوگوں میں سے کچھ ایسے بھی ہیں جو کہتے ہیں ہم ایمان لائے اللہ پر مگر جب انہیں اللہ کی راہ میں ایذا پہنچائی جاتی ہے تو وہ لوگوں کی ایذا رسانی کو اللہ کے عذاب کی مانند سمجھ لیتے ہیں اور (اے نبی صلى الله عليه وسلم !) اگر آپ کے رب کی طرف سے مدد آجائے یہ ضرور کہیں گے کہ ہم آپ لوگوں کے ساتھ ہی تو تھے تو کیا اللہ بخوبی واقف نہیں ہے اس سے جو جہان والوں کے سینوں میں مضمر ہے');
INSERT INTO "bayan" VALUES(29,11,'29:11','اور یقیناً اللہ ظاہر کر کے رہے گا سچے اہل ایمان کو بھی اور ظاہر کر کے رہے گا منافقین کو بھی');
INSERT INTO "bayan" VALUES(29,12,'29:12','اور یہ کافر کہتے ہیں اہل ایمان سے کہ تم ہمارے راستے کی پیروی کرو ہم (آخرت میں) تمہاری خطاؤں کا بوجھ اٹھا لیں گے اور وہ نہیں اٹھانے والے ان کی خطاؤں میں سے کچھ بھی۔ یقیناً وہ جھوٹ بول رہے ہیں');
INSERT INTO "bayan" VALUES(29,13,'29:13','البتہ وہ لازماً اٹھائیں گے اپنے بوجھ بھی اور ان کے ساتھ کچھ دوسرے بوجھ بھی اور ان سے لازماً باز پرس ہوگی قیامت کے دن اس کے بارے میں جو جھوٹ یہ گھڑ رہے ہیں');
INSERT INTO "bayan" VALUES(29,14,'29:14','اور ہم نے بھیجا تھا نوح ؑ کو اس کی قوم کی طرف تو وہ رہا ان کے مابین پچاس برس کم ایک ہزار برس تو ان کو آپکڑا طوفان نے اور وہ ظالم تھے');
INSERT INTO "bayan" VALUES(29,15,'29:15','تو ہم نے بچالیا اس (نوح (ؑ) کو اور کشتی والوں کو اور اسے بنا دیا ہم نے تمام جہان والوں کے لیے ایک نشانی');
INSERT INTO "bayan" VALUES(29,16,'29:16','اور ابراہیم ؑ کو (بھی رسول بنا کر بھیجا) جب اس نے کہا اپنی قوم سے کہ اللہ کی بندگی کرو اور اس کا تقویٰ اختیار کرو یہی تمہارے لیے بہتر ہے اگر تم علم رکھتے ہو');
INSERT INTO "bayan" VALUES(29,17,'29:17','جن کو تم پوج رہے ہو اللہ کو چھوڑ کر یہ تو محض بت ہیں اور تم ایک جھوٹ گھڑ رہے ہو جن کو تم پوجتے ہو اللہ کے سوا وہ تمہیں رزق دینے کا کچھ اختیار نہیں رکھتے پس تم اللہ ہی کے پاس رزق کے طالب بنو اور اسی کی بندگی کرو اور اسی کا شکر ادا کرو۔ اسی کی طرف تم لوٹا دیے جاؤ گے');
INSERT INTO "bayan" VALUES(29,18,'29:18','اور اگر تم جھٹلا رہے ہو تو (یاد رکھو کہ) تم سے پہلے بہت سی قومیں جھٹلا چکی ہیں اور رسول صلى الله عليه وسلم پر کوئی ذمہ داری نہیں ہے سوائے صاف صاف پہنچا دینے کے');
INSERT INTO "bayan" VALUES(29,19,'29:19','کیا انہوں نے دیکھا نہیں کہ کس طرح اللہ پہلی بار پید ا کرتا ہے پھر اسے دوبارہ پیدا کرے گا یقیناً یہ اللہ پر بہت آسان ہے');
INSERT INTO "bayan" VALUES(29,20,'29:20','ان سے کہیے کہ ذرا گھومو پھرو زمین میں اور دیکھو کس طرح اللہ نے پہلی بار پیدا کیا پھر اللہ ہی اٹھاتا ہے (یا اٹھائے گا) دوسری بار یقیناً اللہ ہر شے پر قادر ہے');
INSERT INTO "bayan" VALUES(29,21,'29:21','وہ جسے چاہے گا عذاب دے گا اور جس پر چاہے گا رحم فرمائے گا اور اسی کی طرف تم لوٹا دیے جاؤ گے');
INSERT INTO "bayan" VALUES(29,22,'29:22','اور تم اسے عاجز نہیں کرسکتے زمین میں اور نہ آسمان میں اور نہیں ہے اللہ کے مقابلے میں کوئی تمہارا حمایتی اور نہ ہی کوئی مدد گار');
INSERT INTO "bayan" VALUES(29,23,'29:23','اور جنہوں نے انکار کیا اللہ کی آیات کا اور اس کی ملاقات کا یہی لوگ ہیں جو مایوس ہوچکے ہیں میری رحمت سے اور یہی لوگ ہیں جن کے لیے درد ناک عذاب ہے');
INSERT INTO "bayan" VALUES(29,24,'29:24','تو کوئی جواب نہیں تھا اس (ابراہیم (ؑ) کی قوم کا سوائے اس کے کہ انہوں نے کہا : اسے قتل کر دو یا اس کو جلا دو تو اللہ نے اسے نجات دی آگ سے۔ یقیناً اس میں نشانیاں ہیں ان لوگوں کے لیے جو ایمان رکھتے ہیں');
INSERT INTO "bayan" VALUES(29,25,'29:25','اور ابراہیم ؑ نے کہا کہ تم لوگوں نے اللہ کے سوا جوُ بت بنا رکھے ہیں یہ تو بس دنیا کی زندگی میں تمہاری آپس کی محبت کی وجہ سے ہے پھر قیامت کے دن تم ایک دوسرے کا انکار کرو گے اور ایک دوسرے پر لعنت بھیجو گے اور تم سب کا ٹھکانا آگ ہوگی اور (اس دن وہاں) تمہارا کوئی مدد گار نہیں ہوگا');
INSERT INTO "bayan" VALUES(29,26,'29:26','تو لوط ؑ اس (ابراہیم (ؑ) پر ایمان لایا اور ابراہیم ؑ نے کہا کہ میں اپنے رب کی طرف ہجرت کر رہا ہوں۔ یقیناً وہ زبردست ہے کمال حکمت والا');
INSERT INTO "bayan" VALUES(29,27,'29:27','اور ہم نے اسے اسحاق ؑ (جیسا بیٹا) اور یعقوب ؑ (جیسا پوتا) عطا کیا اور اس کی نسل میں ہم نے رکھ دی نبوت اور کتاب اور ہم نے اسے دنیا میں بھی اس کا اجر عطا کیا اور آخرت میں بھی وہ یقیناً ہمارے نیک بندوں میں سے ہوگا');
INSERT INTO "bayan" VALUES(29,28,'29:28','اور (ہم نے بھیجا) لوط ؑ کو جب اس نے اپنی قوم سے کہا کہ تم لوگ ایسی بےحیائی کا ارتکاب کر رہے ہو جو تم سے پہلے تمام جہان والوں میں سے کسی نے نہیں کی');
INSERT INTO "bayan" VALUES(29,29,'29:29','کیا تم آتے ہو مردوں کے پاس (شہوت رانی کے لیے) اور (فطری) راستہ منقطع کرتے ہو اور یہ مذموم فعل تم اپنی مجلسوں کے اندر کرتے ہو تو اس کی قوم کا کوئی جواب اس کے سوا نہیں تھا کہ انہوں نے کہا : تم لے آؤ ہم پر اللہ کا عذاب اگر تم سچے ہو');
INSERT INTO "bayan" VALUES(29,30,'29:30','اس نے دعا کی : اے میرے پروردگار ! میری مدد فرما اس مفسد قوم کے خلاف');
INSERT INTO "bayan" VALUES(29,31,'29:31','اور جب ہمارے فرستادے ابراہیم ؑ کے پاس بشارت لے کر آئے انہوں نے کہا کہ ہم اس بستی والوں کو ہلاک کرنے والے ہیں۔ یقیناً اس کے باسی بڑے ظالم ہیں');
INSERT INTO "bayan" VALUES(29,32,'29:32','ابراہیم ؑ نے کہا کہ اس میں لوط ؑ بھی تو ہے انہوں نے کہا کہ ہمیں خوب معلوم ہے کون کون اس میں ہے ہم ضرور بچا لیں گے اسے بھی اور اس کے گھر والوں کو بھی سوائے اس کی بیوی کے جو پیچھے رہ جانے والوں میں ہوگی');
INSERT INTO "bayan" VALUES(29,33,'29:33','اور جب ہمارے فرستادے لوط ؑ کے پاس پہنچے تو وہ ان کی وجہ سے بہت پریشان اور دل تنگ ہوا انہوں نے کہا کہ آپ ؑ خوف نہ کھائیں اور نہ آپ ؑ رنجیدہ ہوں ہم تو نجات دینے والے ہیں آپ ؑ کو بھی اور آپ ؑ کے اہل خانہ کو بھی سوائے آپ ؑ کی بیوی کے وہ ہوگی پیچھے رہ جانے والوں میں سے');
INSERT INTO "bayan" VALUES(29,34,'29:34','ہم نازل کرنے والے ہیں اس بستی والوں پر ایک عذاب آسمان سے بسبب اس فسق و فجور کے جو وہ کر رہے تھے');
INSERT INTO "bayan" VALUES(29,35,'29:35','اور ہم نے چھوڑ دی اس (بستی) میں سے ایک واضح نشانی ان لوگوں کے لیے جو عقل رکھتے ہیں');
INSERT INTO "bayan" VALUES(29,36,'29:36','اور مدین کی طرف بھیجا ہم نے ان کے بھائی شعیب ؑ کو تو اس نے کہا کہ اے میری قوم کے لوگو ! اللہ کی بندگی کرو اور آخرت کے دن کی امید رکھو اور زمین میں فساد مت مچاتے پھرو');
INSERT INTO "bayan" VALUES(29,37,'29:37','تو انہوں نے اسے جھٹلا دیا چناچہ انہیں آپکڑا زلزلے نے تو وہ رہ گئے اپنے گھروں کے اندر اوندھے گرے ہوئے');
INSERT INTO "bayan" VALUES(29,38,'29:38','اور عاد اور ثمود (کو بھی ہم نے ہلاک کیا) اور تم پر واضح ہوچکے ہیں ان کے مساکن اور شیطان نے مزین کردیے تھے ان کے لیے ان کے ُ (برے) اعمال اور اس (شیطان) نے روک دیا تھا انہیں سیدھے راستے سے حالانکہ وہ بہت سمجھ بوجھ والے تھے');
INSERT INTO "bayan" VALUES(29,39,'29:39','اور (اسی طرح ہلاک کیا ہم نے) قارون فرعون اور ہامان کو بھی اور ان کے پاس آئے تھے موسیٰ ؑ واضح نشانیاں لے کر تو انہوں نے زمین میں تکبر کیا لیکن وہ ہماری پکڑ سے بچ کر نکل جانے والے نہیں تھے');
INSERT INTO "bayan" VALUES(29,40,'29:40','چناچہ ہم نے ان میں سے ہر ایک کو اس کے گناہ کی پاداش میں پکڑا تو ان میں وہ بھی تھے جن پر ہم نے زور دار آندھی بھیجی اور ان میں وہ بھی تھے جنہیں چنگھاڑ نے آپکڑا اور ان میں وہ بھی تھے جنہیں ہم نے زمین میں دھنسا دیا اور وہ بھی تھے جن کو ہم نے غرق کردیا اور اللہ ایسا نہیں تھا کہ ان پر ظلم کرتا بلکہ وہ لوگ خود ہی اپنی جانوں پر ظلم کرتے تھے');
INSERT INTO "bayan" VALUES(29,41,'29:41','ان لوگوں کی مثال جنہوں نے اللہ کے سوا کچھ حمایتی بنا رکھے ہیں ایسی ہے جیسے مکڑی کی مثال جس نے ایک گھر بنایا اور بیشک تمام گھروں میں سب سے زیادہ کمزور گھر مکڑی ہی کا گھر ہے۔ کاش کہ انہیں معلوم ہوتا');
INSERT INTO "bayan" VALUES(29,42,'29:42','یقیناً اللہ خوب جانتا ہے جس چیز کو بھی یہ لوگ اس کے سوا پکارتے ہیں۔ اور وہ زبردست ہے حکمت والا');
INSERT INTO "bayan" VALUES(29,43,'29:43','اور یہ مثالیں ہم بیان کرتے ہیں لوگوں کے لیے لیکن انہیں نہیں سمجھتے مگر وہی لوگ جو علم والے ہیں');
INSERT INTO "bayan" VALUES(29,44,'29:44','اللہ نے تخلیق کیا آسمانوں اور زمین کو حق کے ساتھ۔ یقیناً اس میں عظیم نشانی ہے ایمان والوں کے لیے');
INSERT INTO "bayan" VALUES(29,45,'29:45','تلاوت کرتے رہا کریں اس کی جو وحی کی گئی ہے آپ کی طرف کتاب میں سے اور نماز قائم کریں یقیناً نماز روکتی ہے بےحیائی سے اور برے کاموں سے اور اللہ کا ذکر سب سے بڑی چیز ہے اور اللہ خوب جانتا ہے جو کچھ تم کر رہے ہو');
INSERT INTO "bayan" VALUES(29,46,'29:46','اور اہل کتاب سے جھگڑا مت کرو مگر بہترین طریقے سے سوائے ان کے جو ان میں سے ناانصافی پرُ تل جائیں اور (ان سے) کہیے کہ ہم ایمان رکھتے ہیں اس پر بھی جو ہم پر نازل کیا گیا اور اس پر بھی جو آپ لوگوں پر نازل کیا گیا اور ہمارا معبود اور تمہارا معبود ایک ہی ہے اور ہم تو اسی کے سامنے سر جھکا چکے ہیں');
INSERT INTO "bayan" VALUES(29,47,'29:47','اور (اے نبی صلى الله عليه وسلم !) اسی طرح ہم نے آپ کی طرف یہ کتاب نازل کی ہے تو جن لوگوں کو ہم نے (اس سے پہلے) کتاب دی تھی وہ بھی اس پر ایمان لائیں گے اور ان (مشرکین مکہ) میں سے بھی بہت سے لوگ اس پر ایمان لا رہے ہیں اور ہماری آیات کا انکار نہیں کرتے سوائے ان لوگوں کے جو کفر پر اڑ گئے ہیں');
INSERT INTO "bayan" VALUES(29,48,'29:48','اور (اے نبی صلى الله عليه وسلم !) آپ تو اس سے پہلے کوئی کتاب نہیں پڑھتے تھے اور نہ ہی آپ اپنے داہنے ہاتھ سے اسے لکھتے تھے (اگر ایسا ہوتا) تب تو یہ جھٹلانے والے ضرور شک کرتے');
INSERT INTO "bayan" VALUES(29,49,'29:49','بلکہ یہ تو روشن آیات ہیں ان لوگوں کے سینوں میں جنہیں علم دیا گیا ہے اور ہماری آیات کا انکار نہیں کرتے مگر وہی لوگ جو ظالم ہیں');
INSERT INTO "bayan" VALUES(29,50,'29:50','اور وہ کہتے ہیں کہ کیوں نہیں نازل کی گئیں ان پر نشانیاں ان کے رب کی طرف سے آپ صلى الله عليه وسلم کہیے کہ نشانیاں (نازل کرنے کے اختیارات) تو اللہ ہی کے پاس ہیں اور میں تو صرف واضح طور پر خبردار کرنے والا ہوں');
INSERT INTO "bayan" VALUES(29,51,'29:51','کیا ان کے لیے یہ (نشانی) کافی نہیں کہ ہم نے آپ صلى الله عليه وسلم پر یہ کتاب نازل کی ہے جو ان کو پڑھ کر سنائی جاتی ہے یقیناً اس میں رحمت اور یاد دہانی ہے ان لوگوں کے لیے جو ایمان لاتے ہیں');
INSERT INTO "bayan" VALUES(29,52,'29:52','(اے نبی صلى الله عليه وسلم !) آپ کہہ دیجیے کہ میرے اور تمہارے درمیان اللہ کافی ہے بطور گواہ وہ جانتا ہے جو کچھ آسمانوں اور زمین میں ہے اور وہ لوگ جو باطل پر ایمان رکھتے ہیں اور اللہ کا کفر کرتے ہیں یقیناً وہی خسارہ پانے والے ہیں');
INSERT INTO "bayan" VALUES(29,53,'29:53','یہ لوگ جلدی مچا رہے ہیں عذاب کی۔ اور اگر (پہلے سے) ایک وقت معینّ طے نہ ہوچکا ہوتا تو ضرور ان پر عذاب آچکا ہوتا اور وہ ان پر اچانک آجائے گا اور انہیں پتا بھی نہیں چلے گا');
INSERT INTO "bayan" VALUES(29,54,'29:54','یہ لوگ عذاب کے لیے جلدی مچا رہے ہیں حالانکہ جہنم ان کافروں کا گھیراؤ کرچکی ہے');
INSERT INTO "bayan" VALUES(29,55,'29:55','جس دن عذاب ڈھانپ لے گا انہیں اوپر سے بھی اور ان کے قدموں کے نیچے سے بھی اور کہا جائے گا کہ اب چکھو مزہ اپنے کرتوتوں کا');
INSERT INTO "bayan" VALUES(29,56,'29:56','اے میرے وہ بندو جو ایمان لائے ہو ! میری زمین بہت وسیع ہے پس عبادت تم میری ہی کرو');
INSERT INTO "bayan" VALUES(29,57,'29:57','ہر جان موت کا مزا چکھنے والی ہے پھر تم ہماری ہی طرف لوٹا دیے جاؤ گے');
INSERT INTO "bayan" VALUES(29,58,'29:58','اور جو لوگ ایمان لائے اور انہوں نے اچھے اعمال کیے ہم انہیں ضرور جگہ دیں گے جنت کے بالا خانوں میں جس کے نیچے ندیاں بہتی ہوں گی اس میں وہ ہمیشہ رہیں گے۔ کیا ہی اچھا اجر ہوگا عمل کرنے والوں کا');
INSERT INTO "bayan" VALUES(29,59,'29:59','وہ لوگ جنہوں نے صبر کیا اور وہ اپنے رب پر توکلّ کرتے ہیں');
INSERT INTO "bayan" VALUES(29,60,'29:60','اور کتنے ہی جاندار ہیں جو اپنا رزق اٹھائے نہیں پھرتے اللہ انہیں بھی رزق دیتا ہے اور وہ تم لوگوں کو بھی دے گا اور یقیناً وہ سب کچھ سننے والا سب کچھ جاننے والا ہے');
INSERT INTO "bayan" VALUES(29,61,'29:61','اور (اے نبی صلى الله عليه وسلم !) اگر آپ ان سے پوچھیں کہ کس نے پیدا کیا ہے آسمانوں اور زمین کو اور کس نے مسخر کیا ہے سورج اور چاند کو ؟ تو وہ ضرور کہیں گے کہ اللہ نے تو پھر یہ لوگ کہاں سے لوٹا دیے جاتے ہیں');
INSERT INTO "bayan" VALUES(29,62,'29:62','اللہ ہی رزق کشادہ کرتا ہے جس کے لیے چاہتا ہے اپنے بندوں میں سے اور (جس کے لیے چاہتا ہے) اسے نپاتلا دیتا ہے یقیناً اللہ ہرچیز کا علم رکھتا ہے');
INSERT INTO "bayan" VALUES(29,63,'29:63','اور اگر آپ صلى الله عليه وسلم ان سے پوچھیں کہ کس نے برسایا آسمان سے پانی پس اس سے زندہ کردیا زمین کو اس کے ُ مردہ ہوجانے کے بعد ؟ تو وہ ضرور کہیں گے کہ اللہ نے آپ صلى الله عليه وسلم کہہ دیں کہ کل شکر اور کل حمد اللہ ہی کے لیے ہے۔ لیکن ان میں سے اکثر لوگ عقل سے کام نہیں لیتے');
INSERT INTO "bayan" VALUES(29,64,'29:64','اور یہ دنیا کی زندگی تو کھیل اور تماشے کے سوا کچھ نہیں اور آخرت کا گھر ہی یقیناً اصل زندگی ہے۔ کاش کہ انہیں معلوم ہوتا');
INSERT INTO "bayan" VALUES(29,65,'29:65','سو جب یہ لوگ سوار ہوتے ہیں کشتی میں تو پکارتے ہیں اللہ کو اس کے لیے اطاعت کو خالص کرتے ہوئے پھر جب وہ انہیں نجات دے دیتا ہے خشکی کی طرف تو جبھی وہ شرک کرنے لگتے ہیں');
INSERT INTO "bayan" VALUES(29,66,'29:66','تا کہ نا شکری کریں ان (نعمتوں) کی جو ہم نے انہیں عطا کر رکھی ہیں اور تاکہ مزلے اڑا لیں تو عنقریب انہیں معلوم ہوجائے گا');
INSERT INTO "bayan" VALUES(29,67,'29:67','کیا یہ لوگ دیکھتے نہیں کہ ہم نے حرم کو امن کی جگہ بنا دیا ہے اور لوگ اچک لیے جاتے ہیں ان کے ارد گرد سے تو کیا یہ لوگ باطل پر ایمان رکھتے ہیں اور اللہ کی نعمتوں کی ناشکری کر رہے ہیں');
INSERT INTO "bayan" VALUES(29,68,'29:68','اور اس شخص سے بڑا ظالم کون ہوگا جس نے اللہ پر جھوٹ گھڑا یا جس نے حق کو جھٹلادیا جب وہ اس کے پاس آیا تو کیا جہنم میں ٹھکانہ نہیں ہے ان کافروں کا');
INSERT INTO "bayan" VALUES(29,69,'29:69','اور جو لوگ ہماری راہ میں جدوجہد کریں گے ہم لازماً ان کی راہنمائی کریں گے اپنے راستوں کی طرف اور یقیناً اللہ احسان کرنے والوں کے ساتھ ہے');
INSERT INTO "bayan" VALUES(30,1,'30:1','الۗمّۗ');
INSERT INTO "bayan" VALUES(30,2,'30:2','رومی مغلوب ہوگئے');
INSERT INTO "bayan" VALUES(30,3,'30:3','قریب کی سر زمین میں۔ اور وہ اپنی اس مغلوبیت کے بعد عنقریب غالب آجائیں گے');
INSERT INTO "bayan" VALUES(30,4,'30:4','چند سالوں میں اللہ ہی کا اختیار ہے پہلے بھی اور بعد میں بھی اور اس دن اہل ایمان خوشیاں منا رہے ہوں گے');
INSERT INTO "bayan" VALUES(30,5,'30:5','(وہ خوش ہوں گے) اللہ کی مدد سے۔ وہ مدد کرتا ہے جس کی چاہتا ہے۔ اور وہ زبردست بہت رحم فرمانے والا ہے');
INSERT INTO "bayan" VALUES(30,6,'30:6','یہ اللہ کا وعدہ ہے۔ اللہ اپنے وعدے کے خلاف نہیں کرتا لیکن اکثر لوگ علم نہیں رکھتے');
INSERT INTO "bayan" VALUES(30,7,'30:7','یہ لوگ دنیا کی زندگی کے بھی صرف ظاہر کو جانتے ہیں اور وہ آخرت سے بالکل ہی غافل ہیں');
INSERT INTO "bayan" VALUES(30,8,'30:8','کیا یہ لوگ اپنی ذات میں غور نہیں کرتے نہیں پیدا کیا اللہ نے آسمانوں اور زمین کو اور جو کچھ ان دونوں کے مابین ہے مگر حق کے ساتھ اور ایک وقت معینّ تک کے لیے اور یقیناً بہت سے لوگ اپنے رب سے ملاقات کے منکر ہیں');
INSERT INTO "bayan" VALUES(30,9,'30:9','کیا یہ لوگ زمین میں گھومے پھرے نہیں کہ وہ دیکھتے کہ کیسا انجام ہوا ان لوگوں کا جو ان سے پہلے تھے وہ قوت میں ان سے کہیں زیادہ تھے اور انہوں نے زمین کو کاشت کیا اور اسے آباد کیا اس سے کہیں بڑھ کر جو (آ ج) انہوں نے آباد کیا اور ان کے پاس بھی ان کے رسول ؑ آئے تھے کھلی نشانیاں لے کر سو اللہ ان پر ظلم کرنے والا نہیں تھا بلکہ انہوں نے خود ہی اپنی جانوں پر ظلم کیا تھا');
INSERT INTO "bayan" VALUES(30,10,'30:10','پھر ان لوگوں کا انجام جنہوں نے ُ بری روش اختیار کی بہت برا ہوا اس لیے کہ انہوں نے اللہ کی آیات کو جھٹلایا اور وہ ان کا مذاق اڑاتے رہے');
INSERT INTO "bayan" VALUES(30,11,'30:11','اللہ ہی پہلی مرتبہ پیدا کرتا ہے پھر وہی اسے دوبارہ پیدا کرتا ہے پھر اسی کی طرف تمہیں لوٹ کر جانا ہے');
INSERT INTO "bayan" VALUES(30,12,'30:12','اور جس دن قیامت قائم ہوجائے گی تو مجرم اس دن مایوس ہو کر رہ جائیں گے');
INSERT INTO "bayan" VALUES(30,13,'30:13','اور نہیں ہوں گے ان کے شریکوں میں سے کوئی بھی ان کے لیے سفارش کرنے والے اور وہ خود بھی اپنے شریکوں کا انکار کرنے والے ہوں گے');
INSERT INTO "bayan" VALUES(30,14,'30:14','اور جس دن قیامت قائم ہوگی اس دن لوگ الگ الگ ہوجائیں گے');
INSERT INTO "bayan" VALUES(30,15,'30:15','تو جو لوگ ایمان لائے تھے اور جنہوں نے نیک عمل کیے تھے وہ ایک باغ میں مسرور کیے جائیں گے');
INSERT INTO "bayan" VALUES(30,16,'30:16','اور وہ لوگ جنہوں نے کفر کیا تھا اور ہماری آیات اور آخرت کی ملاقات کو جھٹلایا تھا تو وہ عذاب میں پکڑے ہوئے ہوں گے');
INSERT INTO "bayan" VALUES(30,17,'30:17','تو تم تسبیح کرو اللہ کی جب تم شام کرتے ہو اور جب تم صبح کرتے ہو');
INSERT INTO "bayan" VALUES(30,18,'30:18','اور اسی کے لیے حمد ہے آسمانوں اور زمین میں اور رات کو اور جب تم ظہر کرتے ہو');
INSERT INTO "bayan" VALUES(30,19,'30:19','وہ نکالتا ہے زندہ کو مردہ سے اور (اسی طرح) وہ نکالتا ہے ُ مردہ کو زندہ سے اور وہ زندہ کرتا ہے زمین کو اس کے مردہ ہوجانے کے بعد۔ اور اسی طرح تمہیں بھی نکال لیا جائے گا');
INSERT INTO "bayan" VALUES(30,20,'30:20','اور اس کی نشانیوں میں سے ہے کہ اس نے تمہیں مٹی سے پیدا کیا پھر تم انسان بن گئے (زمین میں) پھیلے ہوئے');
INSERT INTO "bayan" VALUES(30,21,'30:21','اور اس کی نشانیوں میں سے ہے کہ اس نے پیدا کیے تمہارے لیے تمہاری نوع میں سے جوڑے تاکہ تم ان سے سکون حاصل کرو اور اس نے تمہارے مابین محبت اور رحمت پیدا کردی یقیناً اس میں نشانیاں ہیں ان لوگوں کے لیے جو غور و فکر کریں');
INSERT INTO "bayan" VALUES(30,22,'30:22','اور اس کی نشانیوں میں سے ہے آسمانوں اور زمین کی تخلیق اور تمہاری زبانوں اور تمہارے رنگوں کا فرق یقیناً اس میں نشانیاں ہیں ان لوگوں کے لیے جو علم رکھتے ہیں');
INSERT INTO "bayan" VALUES(30,23,'30:23','اور اس کی نشانیوں میں سے ہے تمہارا رات اور دن کو سونا اور تمہارا اس کے فضل کو تلاش کرنا یقیناً اس میں نشانیاں ہیں ان لوگوں کے لیے جو سنتے ہیں');
INSERT INTO "bayan" VALUES(30,24,'30:24','اور اس کی نشانیوں میں سے ہے کہ وہ تمہیں بجلی (کی چمک) دکھاتا ہے خوف اور امید کے ساتھ اور وہ برساتا ہے آسمان سے پانی پھر زندہ کرتا ہے اس کے ذریعے سے زمین کو اس کے مردہ ہوجانے کے بعد یقیناً اس میں نشانیاں ہیں ان لوگوں کے لیے جو عقل سے کام لیں');
INSERT INTO "bayan" VALUES(30,25,'30:25','اور اس کی نشانیوں میں سے ہے کہ قائم ہیں آسمان و زمین اس کے حکم سے پھر جب وہ تمہیں پکارے گا ایک ہی بار زمین سے (نکلنے کے لیے) تو تم دفعۃً نکل پڑو گے');
INSERT INTO "bayan" VALUES(30,26,'30:26','اور اسی کا ہے جو کچھ ہے آسمانوں اور زمین میں۔ سب اسی کے حکم کے تابع فرمان ہیں');
INSERT INTO "bayan" VALUES(30,27,'30:27','اور وہی ہے جو پہلی بار پیدا کرتا ہے مخلوق کو پھر اسے دوبارہ بھی پیدا کرے گا اور وہ اس کے لیے زیادہ آسان ہے اور اس کی شان بہت بلند ہے آسمانوں اور زمین میں اور وہ بہت زبردست ہے کمال حکمت والا');
INSERT INTO "bayan" VALUES(30,28,'30:28','وہ تمہارے لیے خود تمہارے اندر سے ایک مثال بیان کرتا ہے بھلا وہ (غلام اور لونڈیاں) جو تمہاری ملکیت ہیں کیا ان میں سے کچھ شریک بن جاتے ہیں اس مال و اسباب میں جو ہم نے تمہیں دیا ہے اس طرح کہ (وہ اور) تم برابر ہوجاؤ اور کیا تم ان کے بارے میں ایسے خدشات رکھتے ہو جیسے خدشات خود اپنے بارے میں رکھتے ہو اسی طرح ہم اپنی آیات کی تفصیل کرتے ہیں ان لوگوں کے لیے جو عقل رکھتے ہیں');
INSERT INTO "bayan" VALUES(30,29,'30:29','بلکہ (حقیقت یہ ہے کہ) ان ظالموں نے اپنی خواہشات کی پیروی کی ہے بغیر کسی علم کے تو اس شخص کو کون ہدایت دے گا جس کو اللہ نے بھٹکا دیاہو اور اب ان کے لیے کوئی مددگار نہیں ہے');
INSERT INTO "bayan" VALUES(30,30,'30:30','پس تم قائم رکھو اپنے چہرے کو (اللہ کے) دین کے لیے یکسو ہو کر اللہ کی بنائی ہوئی فطرت پر (قائم رہو) جس پر اس نے انسانوں کو پیدا کیا ہے اللہ کی تخلیق میں کوئی تبدیلی نہیں ہوسکتی یہی ہے سیدھا دین لیکن اکثر لوگ علم نہیں رکھتے');
INSERT INTO "bayan" VALUES(30,31,'30:31','رجوع کرتے ہوئے اسی کی طرف اور اسی سے ڈرو اور نماز قائم کرو اور مشرکین میں سے مت ہونا');
INSERT INTO "bayan" VALUES(30,32,'30:32','(یعنی) ان لوگوں میں سے جنہوں نے اپنا دین ٹکڑے ٹکڑے کردیا اور وہ مختلف گروہ بن گئے ہر گروہ اسی پر خوش ہے جو اس کے پاس ہے');
INSERT INTO "bayan" VALUES(30,33,'30:33','اور جب انسانوں کو کوئی تکلیف پہنچتی ہے تو وہ پکارتے ہیں اپنے رب کو اسی کی طرف رجوع کرتے ہوئے پھر جب وہ انہیں اپنی طرف سے رحمت کا مزہ چکھاتا ہے تو جبھی ان میں سے ایک گروہ اپنے رب کے ساتھ شرک کرنے لگتا ہے');
INSERT INTO "bayan" VALUES(30,34,'30:34','تا کہ ناشکری کریں اس کی جو کچھ ہم نے انہیں عطا کیا ہے تو ٹھیک ہے (چند روزہ زندگی کا) فائدہ اٹھا لو پھر جلدی ہی تمہیں معلوم ہوجائے گا');
INSERT INTO "bayan" VALUES(30,35,'30:35','کیا ہم نے ان پر کوئی ایسی سند اتاری ہے جو انہیں ان چیزوں کے متعلق بتارہی ہے جن کو یہ شریک ٹھہرا رہے ہیں');
INSERT INTO "bayan" VALUES(30,36,'30:36','اور جب ہم لوگوں کو اپنی رحمت کا مزہ چکھاتے ہیں تو وہ اس پر اترانے لگتے ہیں اور اگر ان پر کوئی مصیبت آجاتی ہے ان کے اپنے ہاتھوں کی کمائی کے سبب تو جبھی وہ آس توڑ دیتے ہیں');
INSERT INTO "bayan" VALUES(30,37,'30:37','کیا وہ دیکھتے نہیں کہ اللہ ہی کشادہ کرتا ہے رزق جس کے لیے چاہتا ہے اور نپا تلا دیتا ہے (جسے چاہتا ہے) یقیناً اس میں نشانیاں ہیں ان لوگوں کے لیے جو ایمان لاتے ہیں');
INSERT INTO "bayan" VALUES(30,38,'30:38','تو قرابت داروں مسکینوں اور مسافروں کو ان کا حق دو یہی بہتر روش ہے ان لوگوں کے لیے جو اللہ کی رضا چاہتے ہیں اور یہی لوگ فلاح پانے والے ہیں');
INSERT INTO "bayan" VALUES(30,39,'30:39','اور جو کچھ تم دیتے ہو سود پر تاکہ بڑھتا رہے لوگوں کے مال میں تو اللہ کے ہاں اس میں کوئی بڑھوتری نہیں ہوتی اور جو تم زکوٰۃ دیتے ہو (اور اس سے) اللہ کی رضا چاہتے ہو تو یہی لوگ ہیں جو (اللہ کے ہاں اپنے مال کو) بڑھانے والے ہیں');
INSERT INTO "bayan" VALUES(30,40,'30:40','اللہ ہی ہے جس نے تمہیں پیدا کیا پھر تمہیں رزق دیا پھر وہ تمہیں موت دے گا پھر تمہیں زندہ کرے گا کیا تمہارے شریکوں میں سے بھی کوئی ایسا ہے جو ان میں سے کوئی بھی کام کرتا ہو وہ پاک ہے اور بہت بلندو برتر ہے اس شرک سے جو یہ لوگ کر رہے ہیں');
INSERT INTO "bayan" VALUES(30,41,'30:41','بحر و بر میں فساد رونما چکا ہے لوگوں کے اعمال کے سبب تاکہ وہ انہیں مزہ چکھائے ان کے بعض اعمال کا تاکہ وہ لوٹ آئیں');
INSERT INTO "bayan" VALUES(30,42,'30:42','(اے نبی صلى الله عليه وسلم ! ان سے) کہیے کہ تم زمین میں گھومو پھرو اور دیکھو کہ ان لوگوں کا کیا انجام ہوا جو (تم سے) پہلے تھے ان میں سے اکثر مشرک ہی تھے');
INSERT INTO "bayan" VALUES(30,43,'30:43','پس اپنے چہرے کو قائم رکھو دین قیم کی طرف اس سے پہلے کہ وہ دن آجائے جسے اللہ کی طرف سے کوئی لوٹانے والا نہیں ہوگا جس دن یہ لوگ علیحدہ علیحدہ ہوجائیں گے');
INSERT INTO "bayan" VALUES(30,44,'30:44','جس نے کفر کیا تو اس کے کفر کا وبال اسی پر ہوگا اور جن لوگوں نے نیک اعمال کیے تو وہ اپنی ہی جانوں کے لیے سامان کرتے ہیں');
INSERT INTO "bayan" VALUES(30,45,'30:45','تا کہ اللہ بدلہ دے اپنے فضل سے ان لوگوں کو جو ایمان لائے اور انہوں نے نیک اعمال کیے یقیناً وہ کافروں کو پسند نہیں کرتا');
INSERT INTO "bayan" VALUES(30,46,'30:46','اور اس کی نشانیوں میں سے یہ بھی ہے کہ وہ بھیجتا ہے ہواؤں کو بشارت دینے والی بنا کر اور تاکہ وہ تمہیں مزہ چکھائے اپنی رحمت کا اور اس لیے بھی کہ اسی کے حکم سے کشتیاں چلیں اور تاکہ تم (ان کشتیوں پر سوار ہو کر) اس کا فضل تلاش کرو اور تاکہ تم اس کا شکرادا کرو');
INSERT INTO "bayan" VALUES(30,47,'30:47','اور (اے نبی صلى الله عليه وسلم !) آپ سے پہلے بھی ہم نے رسول ؑ بھیجے ان کی اپنی اپنی قوم کی طرف تو وہ ان کی طرف لے کر آئے واضح نشانیاں پھر ہم نے انتقام لیا (ان میں سے) ان لوگوں سے جو مجرم تھے اور اہل ایمان کی مدد کرنا ہمارے ذمہ تھا');
INSERT INTO "bayan" VALUES(30,48,'30:48','اللہ ہی ہے جو ہواؤں کو بھیجتا ہے پھر وہ اٹھاتی ہیں بادلوں کو پھر وہ پھیلا دیتا ہے ان (بادلوں) کو فضا میں جیسے چاہتا ہے اور انہیں کردیتا ہے تہ بر تہ پھر تم دیکھتے ہو بارش کو کہ برستی ہے ان کے درمیان سے پھر جب وہ برساتا ہے اسے اپنے بندوں میں سے جن پر چاہتا ہے تو یکایک وہ خوش ہوجاتے ہیں');
INSERT INTO "bayan" VALUES(30,49,'30:49','اگرچہ اس سے پہلے کہ وہ ان پر برستی وہ لوگ بالکل ناامید تھے');
INSERT INTO "bayan" VALUES(30,50,'30:50','تو دیکھو اللہ کی رحمت کے آثار کی طرف وہ کس طرح زندہ کردیتا ہے زمین کو اس کے ُ مردہ ہوجانے کے بعد یقیناً وہی زندہ کرنے والا ہے مردوں کو بھی اور یقیناً وہ ہرچیز پر قادر ہے');
INSERT INTO "bayan" VALUES(30,51,'30:51','اور اگر ہم ایک ایسی ہوا بھیج دیں کہ (جس کے اثر ات سے) وہ اسے زرد دیکھیں تو اس کے بعد وہ ناشکری کرنے لگتے ہیں');
INSERT INTO "bayan" VALUES(30,52,'30:52','تو (اے نبی صلى الله عليه وسلم !) آپ مردوں کو نہیں سنا سکتے اور نہ ہی آپ اپنی پکار بہروں کو سنا سکتے ہیں (خاص طور پر) جب وہ پیٹھ پھیر کر جا رہے ہوں');
INSERT INTO "bayan" VALUES(30,53,'30:53','اور آپ صلى الله عليه وسلم نہیں ہدایت دے سکتے اندھوں کو ان کی گمراہی سے (پھیر کر) آپ نہیں سنا سکتے مگر انہی کو جو ہماری آیات پر ایمان رکھتے ہیں اور وہ فرمانبردار ہیں');
INSERT INTO "bayan" VALUES(30,54,'30:54','اللہ ہی ہے جس نے تمہیں پیدا کیا کمزوری سے پھر اس نے طاقت عطاکی کمزوری کے بعد پھر طاقت کے بعد کمزوری اور بڑھاپا طاری کردیا وہ پیدا کرتا ہے جو چاہتا ہے اور وہ سب کچھ جاننے والا بہت قدرت والا ہے');
INSERT INTO "bayan" VALUES(30,55,'30:55','اور جس دن قیامت قائم ہوگی مجرمین قسمیں کھاکھا کر کہیں گے کہ وہ ایک گھڑی سے زیادہ نہیں ٹھہرے اسی طرح وہ (دُنیا میں بھی) الٹے پھرائے جاتے رہے ہیں');
INSERT INTO "bayan" VALUES(30,56,'30:56','اور (اس وقت) کہیں گے وہ لوگ جنہیں علم اور ایمان عطا کیا گیا تھا کہ تم ٹھہرے ہو اللہ کے فیصلے کے مطابق دوبارہ اٹھنے کے دن تک تو یہ ہے یوم بعث لیکن تم لوگ تو علم ہی نہیں رکھتے تھے');
INSERT INTO "bayan" VALUES(30,57,'30:57','تو آج کے دن کچھ فائدہ نہیں پہنچائے گی ظالم لوگوں کو ان کی معذرت اور نہ ہی انہیں توبہ کا موقع دیا جائے گا');
INSERT INTO "bayan" VALUES(30,58,'30:58','اور ہم نے تو لوگوں کے لیے قرآن میں ہر طرح کی مثالیں بیان کردی ہیں اور اگر آپ لے آئیں ان کے پاس کوئی بھی نشانی تو کہیں گے وہ لوگ جنہوں نے کفر کیا ہے کہ نہیں ہو تم لوگ مگر جھوٹ گھڑنے والے');
INSERT INTO "bayan" VALUES(30,59,'30:59','اسی طرح اللہ مہر لگا دیا کرتا ہے ان لوگوں کے دلوں پر جو علم نہیں رکھتے');
INSERT INTO "bayan" VALUES(30,60,'30:60','تو (اے نبی صلى الله عليه وسلم !) آپ صبر کیجیے بیشک اللہ کا وعدہ سچا ہے اور آپ صلى الله عليه وسلم کو ہرگز ہلکا نہ پائیں وہ لوگ جو یقین نہیں رکھتے');
INSERT INTO "bayan" VALUES(31,1,'31:1','الۗمّۗ');
INSERT INTO "bayan" VALUES(31,2,'31:2','یہ حکمت بھری کتاب کی آیات ہیں');
INSERT INTO "bayan" VALUES(31,3,'31:3','ہدایت اور رحمت محسنین کے حق میں');
INSERT INTO "bayan" VALUES(31,4,'31:4','جو نماز قائم کرتے ہیں اور زکوٰۃ دیتے ہیں اور آخرت پر یہی لوگ پختہ یقین رکھتے ہیں');
INSERT INTO "bayan" VALUES(31,5,'31:5','یہی لوگ اپنے رب کی طرف سے ہدایت پر ہیں اور یہی لوگ فلاح پانے والے ہیں');
INSERT INTO "bayan" VALUES(31,6,'31:6','اور لوگوں میں سے کچھ ایسے بھی ہیں جو کھیل تماشے کی چیزیں خریدتے ہیں تاکہ گمراہ کریں (لوگوں کو) اللہ کے راستے سے بغیر علم کے اور اس کو ہنسی بنا لیں یہ وہ لوگ ہیں جن کے لیے اہانت آمیز عذاب ہے');
INSERT INTO "bayan" VALUES(31,7,'31:7','جب اسے سنائی جاتی ہیں ہماری آیات تو وہ پیٹھ موڑ کر چل دیتا ہے استکبار کرتے ہوئے جیسے کہ اس نے انہیں سنا ہی نہیں گویا اس کے کانوں میں بوجھ ہے تو (اے نبی صلى الله عليه وسلم !) آپ اس شخص کو دردناک عذاب کی بشارت دے دیجیے');
INSERT INTO "bayan" VALUES(31,8,'31:8','یقیناً وہ لوگ جو ایمان لائے اور جنہوں نے نیک اعمال کیے ان کے لیے نعمتوں بھرے باغات ہیں');
INSERT INTO "bayan" VALUES(31,9,'31:9','وہ ان میں ہمیشہ رہیں گے۔ یہ اللہ کا وعدہ ہے جو سچا ہے۔ اور وہ زبردست ہے حکمت والا');
INSERT INTO "bayan" VALUES(31,10,'31:10','اس نے آسمانوں کو تخلیق کیا بغیر ایسے ستونوں کے کہ جنہیں تم دیکھ سکو اور اس نے زمین میں لنگر ڈال دیے تاکہ وہ تمہیں لے کر ایک طرف لڑھک نہ جائے اور اس میں ہر طرح کے جانور پھیلا دیے اور ہم نے آسمان سے پانی اتارا پھر اگائے اس میں ہر طرح کے (نباتات کے) خوبصورت جوڑے');
INSERT INTO "bayan" VALUES(31,11,'31:11','یہ تو ہے اللہ کی تخلیق اب تم مجھے دکھاؤ کہ انہوں نے کیا پیدا کیا ہے جو اس کے سوا (تمہارے معبود) ہیں بلکہ یہ ظالم کھلی گمراہی میں پڑے ہوئے ہیں');
INSERT INTO "bayan" VALUES(31,12,'31:12','اور ہم نے لقمان کو دانائی عطا کی تھی کہ اللہ کا شکر ادا کرو اور جو کوئی بھی شکر کرتا ہے تو وہ شکر کرتا ہے اپنے ہی بھلے کے لیے اور جو کوئی نا شکری کرتا ہے تو اللہ بےنیاز ہے اور وہ اپنی ذات میں خود محمود ہے');
INSERT INTO "bayan" VALUES(31,13,'31:13','اور جب کہا لقمان نے اپنے بیٹے سے جبکہ وہ اسے نصیحت کر رہا تھا اے میرے بیٹے ! اللہ کے ساتھ شرک مت کرنا۔ یقیناً شرک بہت بڑا ظلم ہے');
INSERT INTO "bayan" VALUES(31,14,'31:14','اور ہم نے وصیت کی انسان کو اس کے والدین کے بارے میں اس کو اٹھائے رکھا اس کی ماں نے (اپنے پیٹ میں) کمزوری پر کمزوری جھیل کر اور اس کا دودھ چھڑانا ہوا دو سال میں کہ تم شکر کرو میرا اور اپنے والدین کا اور میری ہی طرف تمہارا لوٹنا ہوگا');
INSERT INTO "bayan" VALUES(31,15,'31:15','اور اگر وہ تم پر دباؤڈالیں کہ تم میرے ساتھ شریک کرو اس چیز کو جس کا تمہارے پاس کوئی علم نہیں تو ان کا کہنا مت مانو اور دنیا میں ان کے ساتھ رہو بہتر انداز میں اور پیروی کرو اس شخص کی جو میری طرف رخ کرچکا ہے پھر میری ہی طرف تمہارا لوٹنا ہے پھر میں تمہیں جتلا دوں گا جو کچھ تم کرتے رہے تھے');
INSERT INTO "bayan" VALUES(31,16,'31:16','اے میرے بچے ! اگر وہ (کوئی اچھا یا برا عمل) رائی کے دانے کے برابر بھی ہو پھر وہ ہو کسی چٹان میں یا آسمانوں میں یا زمین کے اندر اسے اللہ لے آئے گا یقیناً اللہ بہت باریک بین ہرچیز کی خبر رکھنے والا ہے');
INSERT INTO "bayan" VALUES(31,17,'31:17','اے میرے بچے ! نماز قائم کرو اور نیکی کا حکم دو اور برائی سے روکو اور جو بھی تکلیف تمہیں پہنچے اس پر صبر کرو یقیناً یہ ہمت کے کاموں میں سے ہے');
INSERT INTO "bayan" VALUES(31,18,'31:18','اور اپنے گالوں کو پھلا کر مت رکھو لوگوں کے سامنے اور زمین میں اکڑ کر مت چلو یقیناً اللہ ہر شیخی خورے تکبر کرنے والے کو پسند نہیں کرتا');
INSERT INTO "bayan" VALUES(31,19,'31:19','اور اپنی چال میں میانہ روی اختیار کرو اور اپنی آواز کو پست رکھو یقیناً سب سے ناپسندیدہ آواز گدھے کی آواز ہے');
INSERT INTO "bayan" VALUES(31,20,'31:20','کیا تم نے دیکھا نہیں کہ اللہ نے تمہارے لیے مسخر کردیا ہے جو کچھ آسمانوں میں ہے اور جو کچھ زمین میں ہے اور اس نے اپنی ظاہری و باطنی نعمتیں تم پر مکمل کردی ہیں اور لوگوں میں سے کچھ ایسے بھی ہیں جو اللہ کے بارے میں جھگڑتے ہیں بغیر اس کے کہ ان کے پاس کوئی علم ہو یا کوئی ہدایت ہو یا کوئی روشن کتاب ہو');
INSERT INTO "bayan" VALUES(31,21,'31:21','اور جب ان سے کہا جاتا ہے کہ پیروی کرو اس (ہدایت) کی جو اللہ نے نازل فرمائی ہے تو کہتے ہیں : بلکہ ہم تو اس (طریقے) کی پیروی کریں گے جس پر ہم نے اپنے آباء و اَجداد کو پایا ہے خواہ انہیں شیطان بلا رہا ہو جہنم کے عذاب کی طرف');
INSERT INTO "bayan" VALUES(31,22,'31:22','اور جس شخص نے اپنا چہرہ اللہ کے سامنے جھکا دیا اور وہ احسان کی روش اختیار کرنے والا بھی ہو تو اس نے تھام لیا مضبوط حلقے کو اور تمام معاملات کا انجام اللہ ہی کے ہاتھ میں ہے');
INSERT INTO "bayan" VALUES(31,23,'31:23','اور (اے نبی صلى الله عليه وسلم !) جو کوئی کفر کرے تو اس کا کفر آپ کو رنجیدہ نہ کرنے پائے ہماری ہی طرف ان سب کو لوٹنا ہے پھر ہم انہیں آگاہ کردیں گے ان کاموں کے بارے میں جو وہ کیا کرتے تھے یقیناً اللہ خوب جاننے والا ہے اسے بھی جو سینوں میں چھپا ہوا ہے');
INSERT INTO "bayan" VALUES(31,24,'31:24','ہم انہیں تھوڑی دیر کے لیے مال و متاع دے رہے ہیں پھر ہم انہیں کھینچ کر لائیں گے ایک بہت گاڑھے عذاب کی طرف');
INSERT INTO "bayan" VALUES(31,25,'31:25','اور (اے نبی صلى الله عليه وسلم !) اگر آپ ان سے پوچھیں کہ کس نے پیدا کیا ہے آسمانوں اور زمین کو تو ضرور کہیں گے کہ اللہ نے آپ صلى الله عليه وسلم کہیے کہ سب تعریفیں اللہ ہی کے لیے ہیں لیکن ان کی اکثریت علم نہیں رکھتی');
INSERT INTO "bayan" VALUES(31,26,'31:26','اللہ ہی کے لیے ہے جو کچھ آسمانوں اور زمین میں ہے یقیناً اللہ ہی غنی ہے بہت خوبیوں والا');
INSERT INTO "bayan" VALUES(31,27,'31:27','اور اگر زمین میں جتنے بھی درخت ہیں وہ سب قلم بن جائیں اور سمندر (سیاہی بن جائے) اس کے بعداس کو مزید سات سمندر سیاہی مہیا کریں اللہ کے کلمات ختم نہ ہوں گے۔ یقیناً اللہ زبردست ہے کمال حکمت والا');
INSERT INTO "bayan" VALUES(31,28,'31:28','تم سب کا پیدا کرنا اور دوبارہ اٹھانا (اللہ کے لیے) ایسا ہی ہے جیسے ایک جان کا (پیدا کردینا اور دوبارہ زندہ کردینا) یقیناً اللہ سب کچھ سننے والا ہرچیز پر نظر رکھنے والا ہے');
INSERT INTO "bayan" VALUES(31,29,'31:29','کیا تم دیکھتے نہیں کہ اللہ رات کو پرو لاتا ہے دن میں اور دن کو پرو لاتا ہے رات میں اور اس نے مسخر کردیا ہے سورج اور چاند کو۔ یہ سب کے سب چل رہے ہیں ایک وقت معین تک کے لیے اور یہ کہ اللہ باخبر ہے اس سے جو تم کر رہے ہو');
INSERT INTO "bayan" VALUES(31,30,'31:30','یہ اس لیے کہ اللہ ہی حق ہے اور یہ کہ یہ لوگ جس کسی کو بھی اس کے سوا پکارتے ہیں وہ باطل ہے اور یقیناً اللہ ہی سب سے بلند وبالا اور عظمت والا ہے');
INSERT INTO "bayan" VALUES(31,31,'31:31','کیا تم دیکھتے نہیں کہ کشتیاں چلتی ہیں سمندر میں اللہ کی نعمتوں کو لے کرتا کہ وہ دکھائے تمہیں اپنی نشانیوں میں سے یقیناً اس میں نشانیاں ہیں ہر اس شخص کے لیے جو صبر اور شکر کرنے والا ہے');
INSERT INTO "bayan" VALUES(31,32,'31:32','اور جب کبھی (سمندر میں) موج انہیں ڈھانپ لیتی ہے سائبانوں کی طرح تو وہ پکارنے لگتے ہیں اللہ کو اس کے لیے اطاعت کو خالص کرتے ہوئے پھر جب وہ انہیں نجات دے کر خشکی پر لے آتا ہے تو ان میں کچھ ہی ہیں جو میانہ روی اختیار کرتے ہیں اور ہماری آیات کا انکار نہیں کرتے مگر وہی لوگ جو قول کے جھوٹے اور نا شکرے ہیں');
INSERT INTO "bayan" VALUES(31,33,'31:33','اے لوگو ! اپنے رب کا تقویٰ اختیار کرو اور ڈرو اس دن سے جس دن کوئی باپ اپنے بیٹے کے کچھ کام نہیں آئے گا اور نہ ہی بیٹا اپنے باپ کے کچھ کام آسکے گا یقیناً اللہ کا وعدہ سچا ہے تو (دیکھو !) تمہیں دھوکے میں نہ ڈال دے دنیا کی زندگی اور (دیکھنا !) تمہیں دھوکے میں نہ ڈال دے اللہ کے حوالے سے وہ بڑا دھوکے باز');
INSERT INTO "bayan" VALUES(31,34,'31:34','یقیناً اللہ ہی ہے جس کے پاس ہے قیامت کا علم اور وہی بارش برساتا ہے اور وہ جانتا ہے جو کچھ رحموں میں ہے اور کوئی جان نہیں جانتی کہ کل وہ کیا کمائی کرے گی اور کوئی جان نہیں جانتی کہ کس زمین میں اس کی موت واقع ہوگی یقیناً اللہ سب کچھ جاننے والا ہرچیز کی خبر رکھنے والا ہے');
INSERT INTO "bayan" VALUES(32,1,'32:1','الف لام میم۔');
INSERT INTO "bayan" VALUES(32,2,'32:2','اس کتاب کا اتاراجانا اس میں کوئی شک نہیں تمام جہانوں کے پروردگار کی طرف سے ہے۔');
INSERT INTO "bayan" VALUES(32,3,'32:3','کیا یہ کہتے ہیں کہ اس (محمد صلى الله عليه وسلم نے اسے خود گھڑ لیا ہے ؟ (نہیں !) بلکہ یہ حق ہے آپ صلى الله عليه وسلم کے رب کی طرف سے تاکہ آپ صلى الله عليه وسلم خبردار کریں اس قوم کو جس کے پاس آپ صلى الله عليه وسلم سے پہلے کوئی خبردار کرنے والا نہیں آیا شاید کہ وہ ہدایت پائیں');
INSERT INTO "bayan" VALUES(32,4,'32:4','اللہ وہ ہے جس نے پیدا کیا آسمانوں کو اور زمین کو اور جو کچھ ان دونوں کے مابین ہے چھ دنوں میں پھر وہ متمکنّ ہوا عرش (تخت حکومت) پر نہیں ہے تمہارے لیے اس کے مقابلے میں کوئی حمایتی اور نہ کوئی سفارش کرنے والا۔ تو کیا تم نصیحت نہیں پکڑتے !');
INSERT INTO "bayan" VALUES(32,5,'32:5','وہ تدبیر کرتا ہے اپنے امر کی آسمان سے زمین کی طرف پھر وہ (امر) چڑھتا ہے اس کی طرف (یہ سارا معاملہ طے پاتا ہے) ایک دن میں جس کی مقدار تمہاری گنتی کے حساب سے ایک ہزار برس ہے');
INSERT INTO "bayan" VALUES(32,6,'32:6','وہ جاننے والا ہے ہر چھپی اور ظاہری چیز کا وہ بہت زبردست ہے نہایت رحم فرمانے والا');
INSERT INTO "bayan" VALUES(32,7,'32:7','جس نے جو شے بھی بنائی بہت خوبصورت انداز میں بنائی اور اس نے آغاز کیا انسان کی تخلیق کا گارے سے');
INSERT INTO "bayan" VALUES(32,8,'32:8','پھر اس کی نسل چلائی ایک بےقدر پانی کے خلاصے سے');
INSERT INTO "bayan" VALUES(32,9,'32:9','پھر اس کی نوک پلک درست کی اور پھونکا اس میں اپنی روح میں سے اور اس نے بنائے تمہارے لیے کان آنکھیں اور دل بہت ہی کم ہے جو شکر تم لوگ کرتے ہو');
INSERT INTO "bayan" VALUES(32,10,'32:10','اور یہ کہتے ہیں : کیا جب ہم زمین میں تحلیل ہوجائیں گے تو کیا پھر ہم ایک نئی خلقت میں ڈھالے جائیں گے ؟ بلکہ (حقیقت میں) یہ لوگ اپنے رب کی ملاقات کے منکر ہیں');
INSERT INTO "bayan" VALUES(32,11,'32:11','(اے نبی صلى الله عليه وسلم ! آپ کہہ دیجیے کہ تمہیں قبض کرلے گا موت کا وہ فرشتہ جو تم پر مقرر کردیا گیا ہے پھر تم اپنے رب کی طرف لوٹا دیے جاؤ گے');
INSERT INTO "bayan" VALUES(32,12,'32:12','اور کاش تم وہ منظر دیکھ سکو جب یہ مجرم کھڑے ہوں گے سر جھکائے ہوئے اپنے پروردگار کے سامنے (اور کہہ رہے ہوں گے :) اے ہمارے پروردگار ! اب ہم نے خوب دیکھ لیا اور سن لیا تو (ایک بار) ہمیں واپس لوٹا دے ہم نیک عمل کریں گے اب ہمیں یقین آگیا ہے');
INSERT INTO "bayan" VALUES(32,13,'32:13','اور اگر ہم چاہتے تو ہر جان کو اس کی ہدایت دے دیتے لیکن میری طرف سے یہ فیصلہ صادر ہوچکا ہے کہ میں جہنم کو جنوں اور آدمیوں سب سے بھر کر رہوں گا۔');
INSERT INTO "bayan" VALUES(32,14,'32:14','تو اب چکھو مزہ (آگ کا) اس لیے کہ تم نے آج کے دن کی ملاقات کو بھلائے رکھا اب ہم نے بھی تمہیں نظر انداز کردیا ہے پس تم چکھو ہمیشگی کا عذاب اپنے ان کرتوتوں کے سبب جو تم کرتے رہے ہو۔');
INSERT INTO "bayan" VALUES(32,15,'32:15','ہماری آیات پر ایمان رکھنے والے تو وہی لوگ ہیں کہ جب ان کے ذریعے انہیں نصیحت کی جاتی ہے تو وہ سجدے میں گرپڑتے ہیں اور اپنے پروردگار کی حمد کے ساتھ تسبیح کرتے ہیں اور وہ تکبر نہیں کرتے');
INSERT INTO "bayan" VALUES(32,16,'32:16','(راتوں کو) ان کے پہلو بستروں سے الگ رہتے ہیں وہ اپنے رب کو پکارتے رہتے ہیں خوف اور امید کی کیفیت میں اور جو کچھ ہم نے انہیں دیا ہے اس میں سے (ہماری رضا جوئی کے لیے) خرچ کرتے ہیں');
INSERT INTO "bayan" VALUES(32,17,'32:17','تو کوئی انسان نہیں جانتا کہ ان کی آنکھوں کی ٹھنڈک کے لیے کیا کچھ چھپا کر رکھا گیا ہے ان کے اعمال کے بدلے کے طور پر');
INSERT INTO "bayan" VALUES(32,18,'32:18','تو بھلا جو کوئی مؤمن ہے کیا وہ اس کی مانند ہوجائے گا جو فاسق ہے ! وہ (دونوں) ہرگز برابر نہیں ہوسکتے۔');
INSERT INTO "bayan" VALUES(32,19,'32:19','وہ لوگ جو ایمان لائے اور جنہوں نے نیک عمل کیے تو ان کے لیے باغات ہیں ٹھکانے کے طور پر ابتدائی مہمان نوازی کے طور پر ان کے اعمال کے بدلے میں');
INSERT INTO "bayan" VALUES(32,20,'32:20','اور جن لوگوں نے نافرمانی کی ان کا ٹھکانہ آگ ہے اور ان سے کہا جائے گا کہ چکھو اب مزہ اس آگ کے عذاب کا جسے تم جھٹلایا کرتے تھے');
INSERT INTO "bayan" VALUES(32,21,'32:21','اور ہم لازماً چکھائیں گے انہیں مزہ چھوٹے عذاب کا بڑے عذاب سے پہلے شاید کہ یہ لوگ پلٹ آئیں۔');
INSERT INTO "bayan" VALUES(32,22,'32:22','اور اس سے بڑھ کر ظالم کون ہوگا جسے اس کے رب کی آیات کے ذریعے نصیحت کی گئی مگر پھر بھی اس نے اعراض کیا یقیناً ہم ایسے مجرموں سے انتقام لے کر رہیں گے');
INSERT INTO "bayan" VALUES(32,23,'32:23','اور ہم نے موسیٰ ؑ کو بھی کتاب عطا کی تھی تو آپ کسی شک میں نہ رہیے اس کی ملاقات سے اور اس (تورات) کو ہم نے بنا دیا تھا راہنمائی بنی اسرائیل کے لیے');
INSERT INTO "bayan" VALUES(32,24,'32:24','اور ہم نے ان میں سے ایسے امام اٹھائے جو ہمارے حکم سے لوگوں کو ہدایت کرتے تھے جب کہ انہوں نے صبر کی روش اختیار کی اور وہ ہماری آیات پر یقین رکھتے تھے');
INSERT INTO "bayan" VALUES(32,25,'32:25','یقیناً آپ صلى الله عليه وسلم کا رب فیصلہ کرے گا ان کے مابین قیامت کے دن ان چیزوں میں جن میں یہ اختلاف کرتے تھے');
INSERT INTO "bayan" VALUES(32,26,'32:26','کیا اس بات نے انہیں ہدایت نہ دی کہ ان سے پہلے ہم کتنی ہی قوموں کو ہلاک کرچکے ہیں جن کے مساکن میں یہ گھومتے پھرتے ہیں یقیناً اس میں بڑی نشانیاں ہیں۔ تو کیا یہ لوگ سنتے نہیں ہیں !');
INSERT INTO "bayan" VALUES(32,27,'32:27','کیا انہوں نے دیکھا نہیں کہ ہم پانی کو ہانک کرلے جاتے ہیں بےآب وگیاہ زمین کی طرف پھر ہم اس سے کھیتی اگاتے ہیں جس میں سے کھاتے ہیں ان کے مویشی بھی اور یہ خود بھی۔ تو کیا یہ لوگ دیکھتے نہیں ہیں !');
INSERT INTO "bayan" VALUES(32,28,'32:28','اور یہ لوگ کہتے ہیں کہ کب ہوگا یہ فیصلہ اگر تم سچے ہو ؟');
INSERT INTO "bayan" VALUES(32,29,'32:29','(اے نبی صلى الله عليه وسلم !) آپ کہیے کہ فیصلے کے دن ان لوگوں کا ایمان لانا انہیں کچھ فائدہ نہیں دے گا جنہوں نے کفر کیا ہوگا اور نہ ہی (اس وقت) انہیں کوئی مہلت دی جائے گی۔');
INSERT INTO "bayan" VALUES(32,30,'32:30','تو (اے نبی صلى الله عليه وسلم !) آپ ان سے اعراض فرمائیں اور انتظار کریں یہ بھی انتظار ہی کر رہے ہیں');
INSERT INTO "bayan" VALUES(33,1,'33:1','(اے نبی صلى الله عليه وسلم !) اللہ کا تقویٰ اختیار کیجیے اور کافرین و منافقین کی باتیں نہ مانیے۔ یقینا اللہ سب کچھ جاننے والا کمال حکمت والا ہے۔');
INSERT INTO "bayan" VALUES(33,2,'33:2','اور آپ صلى الله عليه وسلم پیروی کیجیے اس (ہدایت) کی جو آپ صلى الله عليه وسلم کی طرف وحی کی جا رہی ہے آپ صلى الله عليه وسلم کے رب کی جانب سے۔ یقینا اللہ ان سب باتوں سے باخبر ہے جو کچھ تم کرتے ہو۔');
INSERT INTO "bayan" VALUES(33,3,'33:3','اور آپ صلى الله عليه وسلم اللہ پر توکل ّکیجیے۔ اور اللہ کافی ہے توکل کے لیے۔');
INSERT INTO "bayan" VALUES(33,4,'33:4','اللہ نے کسی شخص کے سینے میں دو دل نہیں رکھے۔ اور نہ اس نے تمہاری ان بیویوں کو تمہاری مائیں بنایا ہے جن سے تم ظہار کر بیٹھتے ہو۔ ور نہ ہی اس نے تمہارے ُ منہ بولے بیٹوں کو تمہارے بیٹے بنایا ہے۔ یہ سب تمہارے اپنے منہ کی باتیں ہیں۔ اور اللہ حق کہتا ہے اور وہی سیدھے راستے کی طرف ہدایت دیتا ہے۔');
INSERT INTO "bayan" VALUES(33,5,'33:5','(لہٰذا) انہیں پکارا کرو ان کے باپوں کی نسبت سے یہ زیادہ مبنی بر انصاف ہے اللہ کے نزدیک۔ اور اگر تمہیں ان کے باپوں کے بارے میں پتا نہ ہو تو وہ تمہارے دینی بھائی اور رفیق ہیں۔ اور اس معاملے میں تم سے جو خطا ہوئی ہے اس پر تم سے کوئی مواخذہ نہیں لیکن تم لوگ جو کچھ اپنے دلوں کے ارادے سے کرو گے (اس کا ضرور احتساب ہوگا) اور اللہ بہت بخشنے والا نہایت رحم کرنے والا ہے۔');
INSERT INTO "bayan" VALUES(33,6,'33:6','یقینا نبی صلى الله عليه وسلم کا حق مومنوں پر خود ان کی جانوں سے بھی زیادہ ہے اور نبی صلى الله عليه وسلم کی بیویاں ان کی مائیں ہیں۔ اور رحمی رشتے رکھنے والے اللہ کی کتاب کے مطابق مومنین و مہاجرین کی نسبت ایک دوسرے کے زیادہ حق دار ہیں سوائے اس کے کہ تم لوگ اپنے دوستوں کے ساتھ کوئی حسن سلوک کرنا چاہو۔ } یہ سب باتیں (پہلے سے) کتاب میں لکھی ہوئی ہیں۔');
INSERT INTO "bayan" VALUES(33,7,'33:7','اور (یاد کرو) جب ہم نے ابنیاء سے ان کا عہد لیا تھا اور آپ صلى الله عليه وسلم سے بھی اور نوح ؑ ابراہیم موسیٰ ؑ ٰ اور عیسیٰ ؑ ٰ ابن مریم سے بھی۔ اور ان سب سے ہم نے بڑا گاڑھا قول وقرار لیا تھا۔');
INSERT INTO "bayan" VALUES(33,8,'33:8','تا کہ اللہ پوچھ لے سچے لوگوں سے ان کے سچ کے بارے میں۔ اور اس نے تیار کر رکھا ہے کافروں کے لیے ایک دردناک عذاب۔');
INSERT INTO "bayan" VALUES(33,9,'33:9','اے اہل ِایمان ! یاد کرو اللہ کے اس انعام کو جو تم لوگوں پر ہو جب تم پر حملہ آور ہوئے بہت سے لشکرتو ہم نے ان پر ایک سخت آندھی بھیجی اور ایسے لشکر بھی جو تم نے نہیں دیکھے۔ } اور جو کچھ تم لوگ کر رہے تھے اللہ اسے دیکھ رہا تھا۔');
INSERT INTO "bayan" VALUES(33,10,'33:10','جب وہ (لشکر) آئے تم پر تمہارے اوپر سے بھی اور تمہارے نیچے سے بھی اور جب (خوف کے مارے) نگاہیں پتھرا گئی تھیں اور دل حلق میں آگئے تھے اور تم اللہ کے بارے میں طرح طرح کے گمان کرنے لگے۔');
INSERT INTO "bayan" VALUES(33,11,'33:11','اس وقت اہل ایمان کو خوب آزما لیا گیا اور وہ شدت کے ساتھ جھنجھوڑ ڈالے گئے');
INSERT INTO "bayan" VALUES(33,12,'33:12','اور جب کہہ رہے تھے منافقین اور وہ لوگ جن کے دلوں میں روگ تھا کہ نہیں وعدہ کیا تھا ہم سے اللہ اور اس کے رسول صلى الله عليه وسلم نے مگر دھوکے کا۔');
INSERT INTO "bayan" VALUES(33,13,'33:13','اور جب ان میں سے ایک گروہ نے کہا کہ اے اہل ِیثرب ! اب تمہارا کوئی ٹھکانہ نہیں چناچہ تم لوٹ جائو اور ان میں سے ایک گروہ نبی صلى الله عليه وسلم سے اجازت طلب کر رہا تھا وہ کہہ رہے تھے کہ ہمارے گھر غیر محفوظ ہیں۔ حالانکہ وہ غیر محفوظ نہیں تھے۔ } حقیقت میں وہ کچھ نہیں چاہتے تھے سوائے فرار کے۔');
INSERT INTO "bayan" VALUES(33,14,'33:14','اور اگر کہیں ان پر دشمن گھس آئے ہوتے مدینہ کے اطراف سے پھر ان سے مطالبہ کیا جاتا فتنے (ارتداد) کا تو وہ اسے قبول کرلیتے اور اس میں بالکل توقف نہ کرتے مگر تھوڑا سا۔');
INSERT INTO "bayan" VALUES(33,15,'33:15','حالانکہ اس سے قبل وہ اللہ سے وعدہ کرچکے تھے کہ وہ کبھی پیٹھ نہیں دکھائیں گے۔ } اور اللہ سے کیے گئے عہد کی باز پرس تو ہونی ہے۔');
INSERT INTO "bayan" VALUES(33,16,'33:16','(اے نبی صلى الله عليه وسلم ! ان سے) کہہ دیجیے کہ تمہارا یہ بھاگنا تمہیں کوئی فائدہ نہیں پہنچائے گا اگر تم لوگ موت یا قتل سے بھاگ رہے ہو } اور (اگر بھاگو گے) تب بھی تم (زندگی کے سازوسامان سے) فائدہ نہیں اٹھا سکو گے مگر تھوڑا سا۔');
INSERT INTO "bayan" VALUES(33,17,'33:17','آپ صلى الله عليه وسلم کہیے کہ کون ہے وہ جو تمہیں اللہ سے بچا سکتا ہے اگر وہ ارادہ کرے تمہارے ساتھ نقصان کا ؟ یا (اُس کی رحمت کو روک سکتا ہے) اگر وہ ارادہ کرے تمہارے ساتھ رحمت کا ؟ } اور یہ لوگ نہیں پائیں گے اپنے لیے اللہ کے مقابلے میں کوئی حمایتی اور نہ کوئی مدد گار۔');
INSERT INTO "bayan" VALUES(33,18,'33:18','اللہ خوب جانتا ہے تم میں سے ان لوگوں کو جو روکنے والے ہیں اور اپنے بھائیوں سے یہ کہنے والے ہیں کہ ہماری طرف آ جائو } اور وہ نہیں آتے جنگ کی طرف مگر بہت تھوڑی دیر کے لیے۔');
INSERT INTO "bayan" VALUES(33,19,'33:19','(اے مسلمانو !) تمہارا ساتھ دینے میں یہ سخت بخیل ہیں تو جب خطرہ پیش آجاتا ہے تو (اے نبی صلى الله عليه وسلم !) آپ ان کو دیکھتے ہیں کہ وہ آپ کی طرف اس طرح تاک رہے ہوتے ہیں کہ ان کی آنکھیں گردش کرتی ہیں اس شخص کی (آنکھوں کی) طرح جس پر موت کی غشی طاری ہو۔ پھر جب خطرہ جاتا رہتا ہے تو وہ تم لوگوں پر چڑھ دوڑتے ہیں اپنی تیز زبانوں سے لالچ کرتے ہوئے مال پر۔ یہ وہ لوگ ہیں جو حقیقت میں ایمان نہیں لائے تو اللہ نے ان کے اعمال ضائع کردیے۔ } اور یہ اللہ پر بہت آسان ہے۔');
INSERT INTO "bayan" VALUES(33,20,'33:20','وہ لشکروں کے بارے میں سمجھتے ہیں کہ ابھی وہ گئے نہیں ہیں۔ اور اگر لشکر (دوبارہ) حملہ آور ہوجائیں تو ان کی خواہش ہوگی کہ وہ بدوئوں کے ساتھ صحرا میں رہ رہے ہوتے (اور وہیں سے) تمہاری خبریں معلوم کرتے رہتے۔ } اور اگر وہ تمہارے درمیان رہتے تو قتال نہ کرتے مگر بہت ہی تھوڑا۔');
INSERT INTO "bayan" VALUES(33,21,'33:21','(اے مسلمانو !) تمہارے لیے اللہ کے رسول میں ایک بہترین نمونہ ہے (یہ اسوہ ہے) ہر اس شخص کے لیے جو اللہ سے ملاقات اور آخرت کی امید رکھتا ہو } اور کثرت کے ساتھ اللہ کا ذکر کرتا ہو۔');
INSERT INTO "bayan" VALUES(33,22,'33:22','اور جب اہل ِایمان نے دیکھا ان لشکروں کو تو انہوں نے کہا کہ یہی تو ہے جس کا ہم سے وعدہ کیا تھا اللہ اور اس کے رسول صلى الله عليه وسلم نے اور بالکل سچ فرمایا تھا اللہ اور اس کے رسول صلى الله عليه وسلم نے۔ } اور اس (واقعہ) نے ان میں کسی بھی شے کا اضافہ نہیں کیا مگر ایمان اور فرمانبرداری کا۔');
INSERT INTO "bayan" VALUES(33,23,'33:23','اہل ِایمان میں وہ جواں مرد لوگ بھی ہیں جنہوں نے سچا کر دکھایا وہ عہد جو انہوں نے اللہ سے کیا تھا پس ان میں سے کچھ تو اپنی نذر پوری کرچکے اور ان میں سے کچھ انتظار کر رہے ہیں } اور انہوں نے ہرگز کوئی تبدیلی نہیں کی');
INSERT INTO "bayan" VALUES(33,24,'33:24','تا کہ اللہ سچوں کو ان کی سچائی کا پورا پورا بدلہ دے اور منافقین کو چاہے تو عذاب دے اور چاہے تو ان کی توبہ قبول کرلے } یقینا اللہ بہت بخشنے والا نہایت رحم کرنے والا ہے۔');
INSERT INTO "bayan" VALUES(33,25,'33:25','اور لوٹا دیا اللہ نے کافروں کو ان کے غصے کے ساتھ ہی وہ کوئی کامیابی حاصل نہ کرسکے۔ اور اللہ کافی رہا اہل ِایمان کی طرف سے قتال کے لیے۔ } اور یقینا اللہ بڑی طاقت والا سب پر غالب ہے۔');
INSERT INTO "bayan" VALUES(33,26,'33:26','اور اللہ نے اتار لیا اہل ِکتاب میں سے ان لوگوں کو جنہوں نے ان (مشرکین) کی مدد کی تھی ان کے قلعوں سے اور ان کے دلوں میں اس نے رعب ڈال دیا } تو اب ان میں سے کچھ کو تم قتل کر رہے ہو اور کچھ کو تم قیدی بنا رہے ہو');
INSERT INTO "bayan" VALUES(33,27,'33:27','اور اس نے تمہیں وارث بنا دیا ان کی زمینوں ان کے گھروں اور ان کے اموال کا اور اس زمین کا بھی جس پر ابھی تم نے قدم نہیں رکھے۔ } اور اللہ ہرچیز پر قادر ہے۔');
INSERT INTO "bayan" VALUES(33,28,'33:28','اے نبی صلى الله عليه وسلم آپ اپنی بیویوں سے کہہ دیجیے کہ اگر تم دنیا کی زندگی اور اس کی زینت چاہتی ہو } تو آئو میں تمہیں کچھ مال و متاع دے کر اچھے طریقے سے رخصت کر دوں');
INSERT INTO "bayan" VALUES(33,29,'33:29','اور اگر تم اللہ اور اس کے رسول اور آخرت کے گھر کی طالب ہو تو (اطمینان رکھو کہ) اللہ نے تم جیسی نیک خواتین کے لیے اجر عظیم تیار کر رکھا ہے۔');
INSERT INTO "bayan" VALUES(33,30,'33:30','اے نبی صلى الله عليه وسلم کی بیویو ! تم میں سے جو کوئی (بالفرض) کسی کھلی بےحیائی کا ارتکاب کرے گی اسے دوگنا عذاب دیاجائے گا۔ } اور یہ اللہ پر بہت آسان ہے۔');
INSERT INTO "bayan" VALUES(33,31,'33:31','اور جو کوئی تم میں سے اللہ اور اس کے رسول صلى الله عليه وسلم کی اطاعت پر کاربند رہے گی اور نیک عمل کرے گی تو اسے ہم دوگنا اجردیں گے } اور اس کے لیے ہم نے عزت ّوالا رزق تیار کر رکھا ہے۔');
INSERT INTO "bayan" VALUES(33,32,'33:32','اے نبی صلى الله عليه وسلم کی بیویو ! تم عام عورتوں کی مانند نہیں ہو اگر تم تقویٰ اختیار کرو تو گفتگو میں نرمی پیدا نہ کرو کہ وہ شخص جس کے دل میں روگ ہے وہ کسی لالچ میں پڑجائے } اور بات کرو معروف انداز میں۔');
INSERT INTO "bayan" VALUES(33,33,'33:33','اور تم اپنے گھروں میں قرار پکڑو اور مت نکلو بن سنور کر پہلے دور جاہلیت کی طرح اور نماز قائم کرو زکوٰۃ ادا کرو اور اللہ اور اس کے رسول صلى الله عليه وسلم کی اطاعت پر کاربند رہو۔ } اللہ تو بس یہی چاہتا ہے اے نبی صلى الله عليه وسلم کے گھر والو ! کہ وہ دور کر دے تم سے ناپاکی اور تمہیں خوب اچھی طرح پاک کر دے۔');
INSERT INTO "bayan" VALUES(33,34,'33:34','اور یاد کیا کرو جو تمہارے گھروں میں اللہ کی آیات تلاوت کی جاتی ہیں اور حکمت کی باتیں (سنائی جاتی ہیں) } یقینا اللہ بہت باریک بین ہرچیز سے باخبر ہے۔');
INSERT INTO "bayan" VALUES(33,35,'33:35','یقینا مسلمان مرد اور مسلمان عورتیں اور مومن مرد اور مومن عورتیں ور فرماں بردار مرد اور فرماں بردار عورتیں اور راست باز مرد اور راست باز عورتیں اور صبر کرنے والے مرد اور صبر کرنے والی عورتیں اور خشوع کرنے والے مرد اور خشوع کرنے والی عورتیں اور صدقہ دینے والے مرد اور صدقہ دینے والی عورتیں اور روزہ رکھنے والے مرد اور روزہ رکھنے والی عورتیں اور اپنی شرم گاہوں کی حفاظت کرنے والے مرد اور اپنی شرم گاہوں کی حفاظت کرنے والی عورتیں اور کثرت سے اللہ کا ذکر کرنے والے مرد اور ذکر کرنے والی عورتیں } اللہ نے ان سب کے لیے مغفرت اور بہت بڑا اجر تیار کر رکھا ہے۔');
INSERT INTO "bayan" VALUES(33,36,'33:36','اور کسی مومن مرد اور مومن عورت کے لیے روا نہیں کہ جب اللہ اور اس کا رسول صلى الله عليه وسلم کسی معاملے کا فیصلہ کردیں تو (پھر بھی انہیں یہ خیال ہو کہ) ان کے لیے اپنے اس معاملے میں کوئی اختیار باقی ہے۔ } اور جو کوئی اللہ اور اس کے رسول صلى الله عليه وسلم کی نافرمانی کرے تو وہ صریح گمراہی میں پڑگیا۔');
INSERT INTO "bayan" VALUES(33,37,'33:37','اور (اے نبی صلى الله عليه وسلم !) جب آپ کہتے تھے اس شخص سے جس پر اللہ نے بھی انعام کیا تھا اور آپ نے بھی انعام کیا تھا کہ اپنی بیوی کو اپنے پاس روکے رکھو اور اللہ سے ڈرو اور آپ صلى الله عليه وسلم اپنے دل میں چھپائے ہوئے تھے وہ بات جسے اللہ ظاہر کرنے والا تھا اور آپ صلى الله عليه وسلم لوگوں سے ڈر رہے تھے حالانکہ اللہ اس کا زیادہ حقدار ہے کہ آپ صلى الله عليه وسلم اس سے ڈریں۔ پس جب زید ؓ نے اس سے اپنا تعلق منقطع کرلیا تو اسے ہم نے آپ صلى الله عليه وسلم کی زوجیت میں دے دیا تاکہ مومنوں کے لیے ان کے منہ بولے بیٹوں کی بیویوں کے بارے میں کوئی تنگی نہ رہے جبکہ وہ ان سے اپنا تعلق بالکل کاٹ لیں۔ } اور اللہ کا فیصلہ تو پورا ہو کر ہی رہنا تھا۔');
INSERT INTO "bayan" VALUES(33,38,'33:38','نبی صلى الله عليه وسلم پر کوئی مضائقہ نہیں اس بات میں جو اللہ نے اس کے لیے فرض کردی ہے اللہ کا یہی طریقہ رہا ہے ان لوگوں کے بارے میں بھی جو پہلے گزر چکے ہیں۔ } اور اللہ کا فیصلہ قطعی طے شدہ تھا۔');
INSERT INTO "bayan" VALUES(33,39,'33:39','وہ (اللہ کے رسول صلى الله عليه وسلم جو اللہ کے پیغامات پہنچاتے ہیں اور صرف اسی سے ڈرتے ہیں اور وہ نہیں ڈرتے کسی سے سوائے اللہ کے۔ اور اللہ کافی ہے حساب لینے والا۔');
INSERT INTO "bayan" VALUES(33,40,'33:40','(دیکھو !) محمد صلى الله عليه وسلم تمہارے مردوں میں سے کسی کے باپ نہیں ہیں بلکہ آپ صلى الله عليه وسلم اللہ کے رسول صلى الله عليه وسلم ہیں اور سب نبیوں پر مہر ہیں۔ } اور یقینا اللہ ہرچیز کا علم رکھنے والا ہے۔');
INSERT INTO "bayan" VALUES(33,41,'33:41','اے اہل ایمان ! اللہ کا ذکر کیا کرو کثرت کے ساتھ۔');
INSERT INTO "bayan" VALUES(33,42,'33:42','اور صبح وشام اس کی تسبیح کرتے رہو۔');
INSERT INTO "bayan" VALUES(33,43,'33:43','وہی ہے جو رحمتیں بھیجتا رہتا ہے تم پر اور اس کے فرشتے بھی تاکہ نکالے تمہیں اندھیروں سے روشنی کی طرف۔ اور اہل ایمان کے حق میں وہ بہت مہربان ہے۔');
INSERT INTO "bayan" VALUES(33,44,'33:44','} جس دن وہ اس سے ملیں گے تو ان کی دعا سلام ہوگی اور اس نے تیار کر رکھا ہے ان کے لیے بہت با عزت اجر');
INSERT INTO "bayan" VALUES(33,45,'33:45','اے نبی صلى الله عليه وسلم ! یقینا ہم نے بھیجا ہے آپ کو گواہ بنا کر اور بشارت دینے والا اور خبردار کرنے والا');
INSERT INTO "bayan" VALUES(33,46,'33:46','اور اللہ کی طرف بلانے والا اس کے حکم سے اور ایک روشن چراغ بنا کر');
INSERT INTO "bayan" VALUES(33,47,'33:47','اور آپ صلى الله عليه وسلم اہل ایمان کو بشارت دے دیجیے کہ ان کے لیے اللہ کی طرف سے بہت بڑا فضل ہے۔');
INSERT INTO "bayan" VALUES(33,48,'33:48','اور آپ صلى الله عليه وسلم ان کافروں اور منافقوں کی باتوں کا دھیان نہ کریں اور ان کی ایذا رسانی کو نظر انداز کردیں اور اللہ پر توکل ّکریں۔ اور اللہ کافی ہے (آپ صلى الله عليه وسلم کے لیے) مددگار کے طور پر۔');
INSERT INTO "bayan" VALUES(33,49,'33:49','اے اہل ایمان ! جب تم مومن خواتین سے نکاح کرو پھر انہیں طلاق دے دو قبل اس کے کہ انہیں چھوئو تو تمہاری طرف سے ان پر کوئی عدت لازم نہیں ہے جس کی تم انہیں گنتی پوری کرائو۔ } پس انہیں کچھ دے دلا کر بڑی خوبصورتی کے ساتھ رخصت کر دو۔');
INSERT INTO "bayan" VALUES(33,50,'33:50','اے نبی صلى الله عليه وسلم ! ہم نے آپ کے لیے حلال ٹھہرایا ہے آپ کی ان تمام ازواج کو جن کے مہر آپ نے ادا کیے ہیں ور (ان کو بھی) جو آپ صلى الله عليه وسلم کی ملک یمین (باندیاں) ہیں ان سے جو اللہ نے آپ صلى الله عليه وسلم کو بطور فے عطا کیں (اسی طرح آپ صلى الله عليه وسلم کو نکاح کرنا جائز ہے) اپنے چچا کی بیٹیوں سے اور اپنی پھوپھیوں کی بیٹیوں سے اور اپنی ماموئوں کی بیٹیوں اور اپنی خالائوں کی بیٹیوں سے جنہوں نے آپ صلى الله عليه وسلم کے ساتھ ہجرت کی۔ اور وہ مومن عورت بھی جو ہبہ کرے اپنا آپ نبی صلى الله عليه وسلم کے لیے اگر نبی صلى الله عليه وسلم اسے اپنے نکاح میں لانا چاہیں۔ یہ (رعایت) خالص آپ صلى الله عليه وسلم کے لیے ہے مومنین سے علیحدہ۔ ہمیں خوب معلوم ہے جو ہم نے ان (عام مسلمانوں) پر ان کی بیویوں اور ان کی باندیوں کے ضمن میں فرض کیا ہے } تاکہ آپ صلى الله عليه وسلم پر کوئی تنگی نہ رہے۔ اور اللہ بہت بخشنے والا نہایت رحم کرنے والا ہے۔');
INSERT INTO "bayan" VALUES(33,51,'33:51','آپ صلى الله عليه وسلم ان میں سے جس کو چاہیں پیچھے ہٹائیں اور جس کو چاہیں اپنے قریب جگہ دیں۔ اور جن کو آپ صلى الله عليه وسلم نے دور کردیا تھا ان میں سے کسی کو (دوبارہ قریب کرنا) چاہیں تو بھی آپ صلى الله عليه وسلم پر کوئی حرج نہیں یہ زیادہ قریب ہے اس سے کہ ان کی آنکھیں ٹھنڈی رہیں اور وہ رنجیدہ نہ ہوں اور وہ سب کی سب راضی رہیں اس پر جو بھی آپ انہیں دیں۔ } اور اللہ خوب جانتا ہے جو کچھ تمہارے دلوں میں ہے۔ اور اللہ ہرچیز کا علم رکھنے والا بہت بردبار ہے۔');
INSERT INTO "bayan" VALUES(33,52,'33:52','اب اس کے بعد اور عورتیں آپ صلى الله عليه وسلم کے لیے حلال نہیں اور نہ ہی (اس کی اجازت ہے کہ) آپ صلى الله عليه وسلم ان میں سے کسی کی جگہ کوئی اور بیوی لے آئیں اگرچہ ان کا حسن آپ صلى الله عليه وسلم کو اچھا لگے سوائے اس کے جو آپ صلى الله عليه وسلم کی مملوکہ ہو } اور اللہ ہرچیز پر نگران ہے۔');
INSERT INTO "bayan" VALUES(33,53,'33:53','اے اہل ِ ایمان ! مت داخل ہوجایا کرو نبی صلى الله عليه وسلم کے گھروں میں مگر یہ کہ تمہیں کسی کھانے پر آنے کی اجازت دی جائے نہ انتظار کرتے ہوئے کھانے کی تیاری کا ہاں جب تمہیں بلایا جائے تب داخل ہوا کرو پھر جب کھانا کھا چکو تو منتشر ہوجایا کرو اور باتوں میں لگے ہوئے بیٹھے نہ رہا کرو۔ تمہاری یہ باتیں نبی صلى الله عليه وسلم کے لیے تکلیف کا باعث تھیں مگر وہ (نبی صلى الله عليه وسلم آپ لوگوں سے جھجک محسوس کرتے ہیں اور اللہ حق (بیان کرنے) سے نہیں جھجکتا اور جب تمہیں نبی صلى الله عليه وسلم کی بیویوں سے کوئی چیز مانگنی ہو تو پردے کی اوٹ سے مانگا کرو۔ یہ طرز عمل زیادہ پاکیزہ ہے تمہارے دلوں کے لیے بھی اور ان ؓ کے دلوں کے لیے بھی۔ اور تمہارے لیے جائز نہیں ہے کہ تم اللہ کے رسول صلى الله عليه وسلم کو ایذا پہنچائو اور نہ یہ (جائز ہے) کہ تم نکاح کرو آپ صلى الله عليه وسلم کی بیویوں سے آپ صلى الله عليه وسلم کے بعد کبھی بھی۔ } یقینا اللہ کے نزدیک یہ بہت بڑی بات ہے۔');
INSERT INTO "bayan" VALUES(33,54,'33:54','خواہ تم کسی چیز کو ظاہر کرو خواہ چھپائو اللہ یقینا ہرچیز کا علم رکھنے والا ہے۔');
INSERT INTO "bayan" VALUES(33,55,'33:55','کوئی حرج نہیں اُن (ازواجِ نبی صلى الله عليه وسلم پر ان کے باپوں کے معاملے میں اور نہ (کوئی حرج ہے) ان کے بیٹوں کے بارے میں اور نہ ان کے بھائیوں کے بارے میں اور نہ ان کے بھائیوں کے بیٹوں کے بارے میں اور نہ ان کی بہنوں کے بیٹوں کے بارے میں اور نہ ان کی جان پہچان کی عورتوں کے بارے میں اور نہ ہی ان کی ِملک یمین کے بارے میں۔ } اور (اے نبی صلى الله عليه وسلم کی بیویو !) تم اللہ سے ڈرتی رہو۔ یقینا اللہ ہرچیز پر گواہ ہے۔');
INSERT INTO "bayan" VALUES(33,56,'33:56','یقینا اللہ اور اس کے فرشتے رحمتیں نازل کرتے ہیں نبی صلى الله عليه وسلم پر } اے ایمان والو ! تم بھی آپ صلى الله عليه وسلم پر رحمتیں اور سلام بھیجا کرو');
INSERT INTO "bayan" VALUES(33,57,'33:57','یقینا وہ لوگ جو ایذا پہنچاتے ہیں اللہ اور اس کے رسول صلى الله عليه وسلم کو } اللہ نے لعنت کی ہے ان پر دنیا اور آخرت دونوں میں اور ان کے لیے اس نے اہانت آمیز عذاب تیار کر رکھا ہے۔');
INSERT INTO "bayan" VALUES(33,58,'33:58','اور وہ لوگ جو ایذا پہنچاتے ہیں مومن مردوں اور مومن عورتوں کو بغیر اس کے کہ انہوں نے کسی گناہ کا ارتکاب کیا ہو } تو انہوں نے اپنے سر بہتان اور صریح گناہ کا بوجھ لے لیا ہے۔');
INSERT INTO "bayan" VALUES(33,59,'33:59','اے نبی صلى الله عليه وسلم ! اپنی بیویوں اپنی بیٹیوں اور اہل ایمان خواتین سے کہہ دیں کہ وہ اپنے اوپر اپنی چادروں کا ایک حصہ لٹکا لیا کریں۔ یہ اس سے نزدیک تر ہے کہ وہ پہچان لی جائیں تو انہیں کوئی ایذا نہ پہنچائی جائے۔ } اور اللہ بہت بخشنے والا نہایت رحم کرنے والا ہے۔');
INSERT INTO "bayan" VALUES(33,60,'33:60','اگر باز نہ آئے یہ منافق اور وہ لوگ جن کے دلوں میں روگ ہے اور جو مدینہ میں سنسنی پھیلانے والے ہیں تو (اے نبی صلى الله عليه وسلم !) ہم آپ کو ان پر اکسا دیں گے } پھر وہ آپ صلى الله عليه وسلم کے پڑوسی بن کر اس (شہر) میں نہیں رہ سکیں گے مگر بہت تھوڑا عرصہ۔');
INSERT INTO "bayan" VALUES(33,61,'33:61','یہ پھٹکارے ہوئے لوگ ہیں جہاں بھی پائے جائیں گے پکڑ لیے جائیں گے اور ُ بری طرح قتل کردیے جائیں گے۔');
INSERT INTO "bayan" VALUES(33,62,'33:62','اللہ کی یہی ُ سنت ّرہی ہے ان لوگوں کے بارے میں بھی جو پہلے گزر چکے ہیں۔ اور تم ہرگز نہیں پائو گے اللہ کے طریقے میں کوئی تبدیلی۔');
INSERT INTO "bayan" VALUES(33,63,'33:63','(اے نبی صلى الله عليه وسلم !) لوگ آپ سے قیامت کے بارے میں پوچھتے ہیں۔ کہہ دیجیے کہ اس کا علم تو اللہ ہی کے پاس ہے اور تمہیں کیا معلوم شاید کہ قیامت قریب ہی آچکی ہو');
INSERT INTO "bayan" VALUES(33,64,'33:64','بیشک اللہ نے کافروں پر لعنت فرمائی ہے اور ان کے لیے دہکتی ہوئی آگ تیار کر رکھی ہے');
INSERT INTO "bayan" VALUES(33,65,'33:65','وہ اس میں رہیں گے ہمیشہ ہمیش۔ نہیں پائیں گے وہ کوئی حمایتی اور نہ کوئی مددگار۔');
INSERT INTO "bayan" VALUES(33,66,'33:66','جس دن ان کے چہرے آگ میں الٹ پلٹ کیے جائیں گے } (اس وقت) وہ کہیں گے : ہائے کاش ! ہم نے اطاعت کی ہوتی اللہ کی اور اطاعت کی ہوتی رسول صلى الله عليه وسلم کی');
INSERT INTO "bayan" VALUES(33,67,'33:67','اور کہیں گے : اے ہمارے پروردگار ! ہم نے اپنے سرداروں اور اپنے بڑوں کی اطاعت کی تو انہوں نے ہمیں سیدھے راستے سے بھٹکا دیا۔');
INSERT INTO "bayan" VALUES(33,68,'33:68','اے ہمارے پروردگار ! ُ تو ان کو دوگنا عذاب دے اور ان پر لعنت فرما بہت ہی بڑی لعنت۔');
INSERT INTO "bayan" VALUES(33,69,'33:69','اے اہل ِایمان ! تم ان لوگوں کی مانند نہ ہوجانا جنہوں نے موسیٰ ؑ کو اذیت دی تھی تو اللہ نے اس کو بری ثابت کردیا اس بات سے جو انہوں نے کہی تھی اور وہ اللہ کے نزدیک بڑے مرتبے والا تھا۔');
INSERT INTO "bayan" VALUES(33,70,'33:70','اے اہل ِایمان ! اللہ کا تقویٰ اختیار کرو اور بات کیا کرو سیدھی۔');
INSERT INTO "bayan" VALUES(33,71,'33:71','اللہ تمہارے سارے اعمال درست کردے گا اور تمہاری خطائیں بخش دے گا۔ اور جو کوئی اللہ اور اس کے رسول صلى الله عليه وسلم کی اطاعت پر کاربند ہوگیا تو اس نے بہت بڑی کامیابی حاصل کرلی۔');
INSERT INTO "bayan" VALUES(33,72,'33:72','ہم نے اس امانت کو پیش کیا آسمانوں پر اور زمین پر اور پہاڑوں پر تو ان سب نے انکار کردیا اس کو اٹھانے سے اور وہ اس سے ڈر گئے اور انسان نے اسے اٹھا لیا۔ یقینا وہ بڑا ظالم اور بڑا نادان تھا۔');
INSERT INTO "bayan" VALUES(33,73,'33:73','تا کہ اللہ عذاب دے منافق مردوں اور منافق عورتوں کو اور مشرک َمردوں اور مشرک عورتوں کو اور اللہ رحمت کے ساتھ توجہ فرمائے مومن مردوں اور مومن عورتوں پر اور یقینا اللہ بہت بخشنے والا نہایت رحم کرنے والا ہے۔');
INSERT INTO "bayan" VALUES(34,1,'34:1','کل حمد اور کل شکر اس اللہ کے لیے ہے جس کی ملکیت ہے ہر وہ شے جو آسمانوں اور زمین میں ہے اور آخرت میں بھی اسی کے لیے حمد ہوگی اور وہ کمال حکمت والا ہرچیز سے باخبر ہے۔');
INSERT INTO "bayan" VALUES(34,2,'34:2','وہ جانتا ہے جو کچھ زمین میں داخل ہوتا ہے اور جو کچھ اس سے نکلتا ہے اور جو کچھ آسمان سے نازل ہوتا ہے اور جو کچھ اس میں چڑھتا ہے اور وہ نہایت رحم کرنے والا بہت بخشنے والا ہے۔');
INSERT INTO "bayan" VALUES(34,3,'34:3','اور یہ کافر کہتے ہیں کہ ہم پر قیامت کبھی نہیں آئے گی (اے نبی صلى الله عليه وسلم !) آپ کہیے کیوں نہیں ! میرے رب کی قسم جو تمام پوشیدہ چیزوں کو جاننے والا ہے وہ تم پر ضرور آکر رہے گی اس سے پوشیدہ نہیں رہ سکتی ذرّہ برابر بھی کوئی چیز نہ آسمانوں میں اور نہ زمین میں } اور نہ کوئی اس سے چھوٹی چیز اور نہ بڑی مگر وہ ایک روشن کتاب میں (لکھی ہوئی) موجود ہے۔');
INSERT INTO "bayan" VALUES(34,4,'34:4','تا کہ وہ جزا دے ان لوگوں کو جو ایمان لائے اور جنہوں نے نیک اعمال کیے یہی وہ لوگ ہیں جن کے لیے (آخرت میں) مغفرت بھی ہوگی اور بہت با عزت روزی بھی۔');
INSERT INTO "bayan" VALUES(34,5,'34:5','اور جو لوگ ہماری آیات کو شکست دینے کے لیے کوشاں رہے ان کے لیے بدترین قسم کا دردناک عذاب ہے۔');
INSERT INTO "bayan" VALUES(34,6,'34:6','اور (تا کہ) دیکھ لیں وہ لوگ جنہیں علم دیا گیا ہے کہ آپ صلى الله عليه وسلم پر جو چیز نازل کی گئی ہے آپ صلى الله عليه وسلم کے رب کی طرف سے وہ حق ہے اور وہ راہنمائی کرتی ہے نہایت غالب لائق ِحمد و ثنا ہستی کے راستے کی طرف۔');
INSERT INTO "bayan" VALUES(34,7,'34:7','اور یہ کافر کہتے ہیں : کیا ہم تمہیں ایک ایسے شخص کے بارے میں بتائیں جو تمہیں یہ خبر دیتا ہے کہ جب تم (مٹی میں مل کر) بالکل ریزہ ریزہ ہو جائو گے تو پھر تمہیں از سر ِنو پیدا کردیا جائے گا۔');
INSERT INTO "bayan" VALUES(34,8,'34:8','کیا اس نے جھوٹ باندھا ہے اللہ پر یا اسے جنون ہوگیا ہے بلکہ وہ لوگ جو آخرت پر ایمان نہیں رکھتے وہ عذاب میں اور دور کی گمراہی میں مبتلا ہیں۔');
INSERT INTO "bayan" VALUES(34,9,'34:9','تو کیا انہوں نے دیکھا نہیں جو ان کے سامنے ہے اور جو ان کے پیچھے ہے آسمان اور زمین میں سے اگر ہم چاہیں تو انہیں زمین میں دھنسا دیں یا ان پر آسمان کا کوئی ٹکڑا گرا دیں یقینا اس میں نشانی ہے ہر اس بندے کے لیے جو (اللہ کی طرف) رجوع کرنے والا ہو۔');
INSERT INTO "bayan" VALUES(34,10,'34:10','اور ہم نے دائود ؑ کو اپنی طرف سے خاص فضل عطا کیا تھا (ہم نے حکم دیا کہ) اے پہاڑو ! تم تسبیح کو دہرائو اس کے ساتھ اور پرندوں کو بھی (یہی حکم دیا تھا) اور ہم نے اس کے لیے لوہے کو نرم کردیا تھا۔');
INSERT INTO "bayan" VALUES(34,11,'34:11','کہ کشادہ زرہیں بنائو اور کڑیاں ملانے میں ٹھیک ٹھیک اندازہ رکھو اور تم سب نیک اعمال کرو۔ تم جو کچھ کر رہے ہو میں یقینا اسے دیکھ رہا ہوں۔');
INSERT INTO "bayan" VALUES(34,12,'34:12','اور سلیمان ؑ کے لیے ہوا کو (مسخر کردیا تھا) اس کا صبح کو چلنا بھی ایک ماہ (کا سفر) تھا اور اس کا شام کو چلنا بھی ایک ماہ (کا سفر) تھا اور ہم نے اس کے لیے پگھلے ہوئے تانبے کا چشمہ جاری کردیا تھا اور کچھ ِجن بھی (اس ؑ کے تابع کردیے تھے) جو اس کے سامنے کام کرتے تھے اس کے رب کے حکم سے اور ان میں سے جو کوئی بھی ہمارے حکم سے سرتابی کرتا اسے ہم جلا دینے والا عذاب چکھاتے تھے۔');
INSERT INTO "bayan" VALUES(34,13,'34:13','وہ بناتے تھے اس کے لیے جو وہ چاہتا تھا بڑی بڑی عمارتیں اور مجسمے اور تالابوں کی مانند بڑے بڑے لگن اور بڑی بڑی دیگیں جو ایک جگہ مستقل پڑی رہتی تھیں اے دائود ؑ کے گھر والو ! عمل کرو شکر ادا کرتے ہوئے } اور (واقعہ یہ ہے کہ) میرے بندوں میں شکر کرنے والے کم ہی ہیں۔');
INSERT INTO "bayan" VALUES(34,14,'34:14','پھر جب ہم نے اس ؑ پر موت طاری کردی تو کسی نے ان (جنات) کو اس کی موت سے آگاہ نہ کیا مگر زمین کے کیڑے نے جو اس ؑ کے عصا کو کھاتا تھا پھر جب وہ گرپڑا توجنوں پر واضح ہوگیا کہ اگر وہ غیب جانتے ہوتے تو اس ذلت کے عذاب میں نہ پڑے رہتے۔');
INSERT INTO "bayan" VALUES(34,15,'34:15','(اسی طرح) قوم سبا کے لیے بھی ان کے مسکن میں ایک نشانی موجود تھی دو باغات (کے سلسلے) تھے دائیں اور بائیں طرف کھائو اپنے رب کے رزق میں سے اور اس کا شکر اداکرو } (تمہارا) شہر بہت پاکیزہ ہے اور (تمہارا) رب بہت بخشنے والا ہے۔');
INSERT INTO "bayan" VALUES(34,16,'34:16','تو انہوں نے اعراض کیا چناچہ ہم نے بھیج دیا ان پر سیلاب بہت زور کا اور ہم نے بدل دیے ان کے دو باغوں کی جگہ دو اور باغ جن میں کڑوے کسیلے پھل جھائو کے درخت اور کچھ تھوڑی سی بیریاں تھیں۔');
INSERT INTO "bayan" VALUES(34,17,'34:17','یہ بدلہ دیا ہم نے ان کو ان کے کفر انِ نعمت کی وجہ سے } اور ہم ایسا برا بدلہ نہیں دیتے مگر نا شکرے لوگوں کو');
INSERT INTO "bayan" VALUES(34,18,'34:18','ہم نے ان کے (علاقے) اور ان بستیوں کے درمیان جنہیں ہم نے برکت عطا کر رکھی تھی واضح نظر آنے والی بستیاں قائم کردی تھیں اور ہم نے اندازہ مقرر کردیا تھا ان میں سفر کرنے کا } (ہم نے انہیں کہہ دیا تھا کہ) تم سفر کرو ان میں راتوں کو بھی اور دن کو بھی امن کے ساتھ۔');
INSERT INTO "bayan" VALUES(34,19,'34:19','تو انہوں نے کہا : اے ہمارے پروردگار ! ہمارے سفروں کے درمیان دوری پیدا کر دے اور انہوں نے اپنی جانوں پر ظلم کیا تو ہم نے انہیں کہانیاں بنا دیا اور انہیں ٹکڑے ٹکڑے کر کے رکھ دیا یقینا اس میں نشانیاں ہیں ہر اس شخص کے لیے جو بڑا صبر کرنے والا شکر کرنے والا ہو۔');
INSERT INTO "bayan" VALUES(34,20,'34:20','اور یقینا ابلیس نے ان کے بارے میں اپنا گمان سچ کر دکھایا } سو ان سب نے اس (ابلیس) کی پیروی کی سوائے مومنین کی ایک جماعت کے');
INSERT INTO "bayan" VALUES(34,21,'34:21','اور اس کو ان پر کوئی اختیار حاصل نہیں تھا مگر یہ کہ ہم دیکھ لیں کہ کون ہے جو آخرت پر یقین رکھتا ہے } ان لوگوں سے جو اس سے متعلق شک میں ہیں۔ اور آپ کا پروردگار ہرچیز پر نگران ہے۔');
INSERT INTO "bayan" VALUES(34,22,'34:22','(اے نبی صلى الله عليه وسلم ! ان مشرکین سے) کہیے کہ تم بلائو ان کو جنہیں تم نے (معبود) گمان کیا ہے اللہ کے سوا وہ ذرّہ برابر بھی اختیار نہیں رکھتے نہ آسمانوں میں اور نہ ہی زمین میں اور نہ ان دونوں (زمین اور آسمان) میں ان کا کوئی حصہ ہے اور نہ ہی ان میں سے کوئی اس کا مدد گار ہے۔');
INSERT INTO "bayan" VALUES(34,23,'34:23','اور نہ نفع دے گی اس کے ہاں کوئی سفارش مگر اسی کے حق میں جس کے لیے اس نے اجازت دی ہو۔ یہاں تک کہ جب گھبراہٹ دور کردی جاتی ہے ان کے دلوں سے وہ پوچھتے ہیں تمہارے ربّ نے کیا فرمایا تھا ؟ وہ کہتے ہیں کہ (اُس نے جو کچھ فرمایا ہے وہ) حق ہے اور وہ بہت بلند وبالا بہت بڑا ہے۔');
INSERT INTO "bayan" VALUES(34,24,'34:24','آپ صلى الله عليه وسلم (ان سے) پوچھئے کہ کون ہے جو تمہیں رزق بہم پہنچاتا ہے آسمانوں اور زمین سے ؟ آپ صلى الله عليه وسلم کہیے کہ اللہ ! اور یقینا ہم یا تم لوگ یا تو ہدایت پر ہیں یا کھلی گمراہی میں !');
INSERT INTO "bayan" VALUES(34,25,'34:25','آپ صلى الله عليه وسلم کہیے کہ تم سے نہیں پوچھا جائے گا ہمارے جرائم کے بارے میں } اور نہ ہی ہم سے پوچھا جائے گا تمہارے اعمال کے بارے میں۔');
INSERT INTO "bayan" VALUES(34,26,'34:26','آپ صلى الله عليه وسلم کہہ دیجیے کہ ہمارا رب ہم سب کو (ایک دن) جمع کرے گا پھر وہ فیصلہ کر دے گا ہمارے مابین حق کے ساتھ اور وہ خوب فیصلہ کرنے والا خوب جاننے والا ہے۔');
INSERT INTO "bayan" VALUES(34,27,'34:27','آپ صلى الله عليه وسلم کہیے کہ ذرا مجھے دکھائو تو وہ (معبود) جنہیں تم نے شریک بنا کر اس کے ساتھ ملا رکھا ہے ! کوئی نہیں ! بلکہ وہی اللہ ہے بہت زبردست نہایت حکمت والا۔');
INSERT INTO "bayan" VALUES(34,28,'34:28','اور (اے نبی صلى الله عليه وسلم !) ہم نے نہیں بھیجا ہے آپ کو مگر تمام بنی نوع انسانی کے لیے بشیر اور نذیر بنا کر } لیکن اکثر لوگ جانتے نہیں ہیں۔');
INSERT INTO "bayan" VALUES(34,29,'34:29','اور وہ پوچھتے ہیں کہ کب پورا ہوگا یہ وعدہ اگر تم لوگ سچے ہو');
INSERT INTO "bayan" VALUES(34,30,'34:30','آپ صلى الله عليه وسلم کہہ دیجیے کہ تمہارے لیے ایک خاص دن کی میعاد مقرر ہے جس سے تم نہ تو ایک گھڑی پیچھے ہٹ سکو گے اور نہ آگے بڑھ سکو گے۔');
INSERT INTO "bayan" VALUES(34,31,'34:31','اور کہا ان کافروں نے کہ ہم ہرگز ایمان نہیں لائیں گے اس قرآن پر اور نہ ہی اس (قرآن) پر جو اس سے پہلے تھا۔ اور کاش آپ دیکھیں جب یہ ظالم کھڑے کیے جائیں گے اپنے رب کے سامنے وہ ایک دوسرے کی طرف بات لوٹائیں گے } جو لوگ کمزور تھے وہ ان سے کہیں گے جو بڑے بنے ہوئے تھے کہ اگر تم لوگ نہ ہوتے تو ہم ضرور مومن ہوتے۔');
INSERT INTO "bayan" VALUES(34,32,'34:32','(جواباً) وہ کہیں گے جو بڑے بنے ہوئے تھے ان سے جو کمزور تھے : کیا ہم نے تمہیں ہدایت سے روکا تھا اس کے بعد کہ وہ تمہارے پاس آگئی تھی ؟ بلکہ تم خود ہی مجرم تھے۔');
INSERT INTO "bayan" VALUES(34,33,'34:33','اس پر کہیں گے وہ جو کمزور تھے ان سے جو بڑے بنے ہوئے تھے بلکہ یہ (تمہاری) رات دن کی سازشیں تھیں جب تم ہمیں حکم دیتے تھے کہ ہم اللہ کا کفر کریں اور اس کے لیے مد ِمقابل ٹھہرائیں۔ اور وہ چھپائیں گے ندامت کو جب وہ عذاب کو دیکھ لیں گے۔ اور ہم نے طوق ڈال دیے ہیں ان کافروں کی گردنوں میں } ان کو بدلہ نہیں ملے گا مگر اسی کا جو عمل وہ کرتے تھے۔');
INSERT INTO "bayan" VALUES(34,34,'34:34','اور ہم نے نہیں بھیجا کسی بھی بستی میں کوئی خبردار کرنے والا مگر (ہمیشہ ایسا ہوا کہ) اس کے آسودہ حال لوگوں نے کہا کہ جو چیز آپ دے کر بھیجے گئے ہیں ہم اس کے منکر ہیں۔');
INSERT INTO "bayan" VALUES(34,35,'34:35','اور انہوں نے کہا کہ ہم اموال اور اولاد میں بڑھ کر ہیں اور ہمیں عذاب نہیں دیا جائے گا');
INSERT INTO "bayan" VALUES(34,36,'34:36','آپ صلى الله عليه وسلم کہیے کہ یقینا میرا رب جس کے لیے چاہتا ہے (دنیا کا) رزق وسیع کردیتا ہے اور جس کے لیے چاہتا ہے تنگ کردیتا ہے } لیکن اکثر لوگ علم نہیں رکھتے۔');
INSERT INTO "bayan" VALUES(34,37,'34:37','اور (دیکھو !) تمہارے اموال اور تمہاری اولاد ایسی چیزیں نہیں کہ وہ تمہیں مرتبے میں ہمارا مقرب بنا دیں سوائے اس شخص کے جو ایمان لایا اور اس نے نیک اعمال کیے } تو ایسے لوگوں کے لیے ان کے اعمال کا دو گنا اجر ہے اور وہ بالا خانوں میں امن سے رہیں گے۔');
INSERT INTO "bayan" VALUES(34,38,'34:38','اور وہ لوگ جو کوشش کر رہے ہیں ہماری آیات کو ناکام کرنے کی وہی لوگ عذاب میں حاضر کیے جائیں گے۔');
INSERT INTO "bayan" VALUES(34,39,'34:39','(اے نبی صلى الله عليه وسلم !) آپ کہیے کہ یقینا میرا ربّ کشادہ کرتا ہے رزق جس کے لیے چاہتا ہے اپنے بندوں میں سے اور (جسے چاہتا ہے) اس کے لیے تنگ کردیتا ہے۔ اور جو کچھ بھی تم لوگ خرچ کرتے ہو تو وہ اسے لوٹا دیتا ہے۔ } اور وہ بہترین رزق دینے والا ہے۔');
INSERT INTO "bayan" VALUES(34,40,'34:40','اور جس دن وہ جمع کرے گا ان سب کو پھر فرشتوں سے فرمائے گا : کیا یہ لوگ تمہیں پوجا کرتے تھے ؟');
INSERT INTO "bayan" VALUES(34,41,'34:41','وہ کہیں گے : ُ تو پاک ہے تو ہمارا ولی ہے ان کے سوا۔ } بلکہ یہ لوگ ِجنات ّکی عبادت کیا کرتے تھے ان کی اکثریت ان ہی پر ایمان رکھتی تھی۔');
INSERT INTO "bayan" VALUES(34,42,'34:42','تو آج کے دن تم میں سے کوئی کسی دوسرے کے لیے کچھ اختیار نہیں رکھتا نہ نفع کا اور نہ نقصان کا } اور ہم کہیں گے ان ظالموں سے کہ اب چکھو اس آگ کے عذاب کا مزا جسے تم جھٹلایا کرتے تھے۔');
INSERT INTO "bayan" VALUES(34,43,'34:43','اور جب انہیں پڑھ کر سنائی جاتی ہیں ہماری روشن آیات تو وہ (آپس میں ایک دوسرے سے) کہتے ہیں کہ یہ شخص تو بس یہی چاہتا ہے کہ تم کو ان سے روک دے جن کی عبادت تمہارے آباء و اَجداد کرتے تھے اور وہ (یہ بھی) کہتے ہیں کہ یہ کچھ بھی نہیں ہے مگر ایک گھڑا ہوا جھوٹ۔ } اور ان کافروں نے حق کے بارے میں جبکہ یہ ان کے پاس آگیا کہا کہ یہ کچھ بھی نہیں ہے مگر ایک کھلا ہواجادو۔');
INSERT INTO "bayan" VALUES(34,44,'34:44','اور ہم نے انہیں ایسی کوئی کتابیں نہیں دیں جنہیں یہ پڑھتے ہوں } اور نہ ہی ہم نے آپ صلى الله عليه وسلم سے پہلے ان کی طرف کوئی خبردار کرنے والا بھیجا تھا۔');
INSERT INTO "bayan" VALUES(34,45,'34:45','اور ان لوگوں نے بھی جھٹلایا تھا جو ان سے پہلے تھے اور یہ تو اس کے دسویں حصے کو بھی نہیں پہنچے جو کچھ ہم نے انہیں دے رکھا تھا } تو انہوں نے میرے رسولوں ؑ کو جھٹلایا پس کیسی رہی (ان کے لیے) میری پکڑ');
INSERT INTO "bayan" VALUES(34,46,'34:46','(اے نبی صلى الله عليه وسلم !) آپ کہیے کہ میں تمہیں بس ایک بات کی نصیحت کرتا ہوں یہ کہ تم کھڑے ہو جائو اللہ کے لیے دو دو ہو کر یا اکیلے اکیلے پھر غور کرو تمہارے ساتھی کو جنون کا کوئی عارضہ نہیں ہے } وہ نہیں ہیں مگر تمہارے لیے ایک خبردار کرنے والے ایک سخت عذاب کے آنے سے پہلے');
INSERT INTO "bayan" VALUES(34,47,'34:47','آپ صلى الله عليه وسلم کہیے کہ اگر میں نے تم سے کچھ اجرت مانگی ہو تو وہ تمہارے ہی لیے ہے } میرا ا جر تو اللہ ہی کے ذمہ ہے اور وہ ہرچیز پر گواہ ہے۔');
INSERT INTO "bayan" VALUES(34,48,'34:48','} آپ صلى الله عليه وسلم کہہ دیجیے کہ یقینا میرا رب حق کے ساتھ ضرب لگاتا ہے (باطل کو) وہ خوب جاننے والا ہے تمام غیبوں کا');
INSERT INTO "bayan" VALUES(34,49,'34:49','آپ صلى الله عليه وسلم کہہ دیجیے کہ حق آگیا ہے } اور باطل نہ تو (کسی چیز کی) ابتدا کرسکتا ہے اور نہ ہی اعادہ۔');
INSERT INTO "bayan" VALUES(34,50,'34:50','آپ صلى الله عليه وسلم (ان سے یہ بھی) کہیے کہ اگر میں بہک گیا ہوں تو اس کا وبال میرے اوپر ہی آئے گا } اور اگر میں ہدایت پر ہوں تو یہ اس وحی کے طفیل ہے جو میرا رب میری طرف کرتا ہے۔ یقینا وہ خوب سننے والا بہت قریب ہے۔');
INSERT INTO "bayan" VALUES(34,51,'34:51','اور کاش آپ دیکھیں جب وہ گھبرائے ہوئے ہوں گے تب بچ نکلنا ممکن نہیں ہوگا } اور وہ پکڑ لیے جائیں گے قریبی جگہ سے۔');
INSERT INTO "bayan" VALUES(34,52,'34:52','اور (اُس وقت) وہ کہیں گے کہ اب ہم اس پر ایمان لے آئے ہیں۔ اور (اُس وقت) کہاں ہوگی ان کی رسائی (ایمان تک) اتنی دور کی جگہ سے');
INSERT INTO "bayan" VALUES(34,53,'34:53','اور پہلے تو انہوں نے اس کا کفر کیا تھا اور وہ (اٹکل کے) تیر ُ تکے چلاتے رہے بن دیکھے دور کی جگہ سے');
INSERT INTO "bayan" VALUES(34,54,'34:54','اور آڑ کردی جائے گی ان کے اور ان کی من پسند چیزوں کے مابین جیسا کہ اس سے پہلے ان جیساطرز عمل اختیار کرنے والے لوگوں کے ساتھ کیا گیا تھا۔ } وہ لوگ بھی (ایسے ہی) اضطراب والے شک میں پڑے رہے تھے۔');
INSERT INTO "bayan" VALUES(35,1,'35:1','کل حمد اور کل شکر اللہ کے لیے ہے جو پید ا کرنے والا ہے آسمانوں اور زمین کا فرشتوں کو پیغام رساں بنانے والا جو دو دو تین تین اور چار چار پروں والے ہیں } اللہ اضافہ کرتا رہتا ہے تخلیق میں جو چاہتا ہے۔ یقینا اللہ ہرچیز پر قادر ہے۔');
INSERT INTO "bayan" VALUES(35,2,'35:2','جو رحمت (کا دروازہ) بھی اللہ لوگوں کے لیے کھول دے تو اسے بند کرنے والا کوئی نہیں اور جسے وہ روک لے تو پھر اسے کوئی جاری کرنے والا نہیں ہے اس کے بعد } اور یقینا وہ زبردست ہے کمال حکمت والا ہے۔');
INSERT INTO "bayan" VALUES(35,3,'35:3','اے لوگو ! اللہ کی اس نعمت کو یاد کرو جو تم پر ہوئی ہے کیا اللہ کے سوا کوئی اور خالق ہے جو تمہیں رزق بہم پہنچاتا ہو آسمان سے اور زمین سے ؟ } نہیں ہے کوئی معبود سوائے اس کے تو تم کہاں سے الٹے پھرائے جا رہے ہو');
INSERT INTO "bayan" VALUES(35,4,'35:4','اور (اے نبی صلى الله عليه وسلم !) اگر یہ لوگ آپ کو جھٹلا رہے ہیں تو آپ سے پہلے بھی بہت سے رسولوں ؑ کو جھٹلایا گیا ہے } اور (بالآخر) تمام معاملات اللہ ہی کی طرف لوٹا دیے جائیں گے۔');
INSERT INTO "bayan" VALUES(35,5,'35:5','اے لوگو ! اللہ کا وعدہ سچا ہے تو دیکھو دنیا کی زندگی تمہیں دھوکے میں نہ ڈال دے اور نہ ہی تمہیں دھوکے میں ڈالے اللہ کے بارے میں وہ بڑا دھوکے باز۔');
INSERT INTO "bayan" VALUES(35,6,'35:6','یقینا شیطان تمہارا دشمن ہے چناچہ تم بھی اس کو دشمن ہی سمجھو } یہ تو بلاتا ہے اپنے ہی گروہ کے لوگوں کو تاکہ وہ جہنم والوں میں سے ہوجائیں۔');
INSERT INTO "bayan" VALUES(35,7,'35:7','وہ لوگ جو کفر کی روش اختیار کریں گے ان کے لیے بہت سخت عذاب ہے } اور جو لوگ ایمان لائیں اور نیک عمل کریں ان کے لیے مغفرت اور بہت بڑا اجر ہے۔');
INSERT INTO "bayan" VALUES(35,8,'35:8','تو کیا وہ شخص جس کے لیے مزین ّکر دی گئی ہو اس کے عمل کی برائی اور وہ اسے اچھاسمجھ رہا ہو (ہدایت پاسکتا ہے تو اللہ گمراہ کردیتا ہے جس کو چاہتا ہے اور ہدایت دیتا ہے جس کو چاہتا ہے تو (اے نبی صلى الله عليه وسلم !) آپ کی جان نہ گھلے ان لوگوں پر رنج کی وجہ سے } یقینا اللہ خوب جاننے والا ہے جو کچھ کہ یہ لوگ کر رہے ہیں۔');
INSERT INTO "bayan" VALUES(35,9,'35:9','اور اللہ ہی ہے جو ہوائوں کو بھیجتا ہے پھر وہ اٹھا لیتی ہیں بادلوں کو پھر ہم ہانک دیتے ہیں اس (بادل) کو ایک ُ مردہ زمین کی طرف پھر ہم اس سے اس زمین کو زندہ کردیتے ہیں اس کے مردہ ہوجانے کے بعد } اسی طرح سے ہوگا (تمہارا) اٹھایا جانا بھی۔');
INSERT INTO "bayan" VALUES(35,10,'35:10','جو کوئی عزت کا طالب ہے تو (وہ جان لے کہ) عزت سب کی سب اللہ کے پاس ہے اسی کی طرف اٹھتی ہیں اچھی باتیں اور عمل ِصالح اسے اوپر اٹھاتا ہے اور جو لوگ بری سازشیں کر رہے ہیں ان کے لیے سخت سزا ہوگی۔ } اور ان کی سازشیں ناکام ہو کر رہ جائیں گی۔');
INSERT INTO "bayan" VALUES(35,11,'35:11','اور وہ اللہ ہی ہے جس نے تمہیں مٹی سے پیدا کیا پھر نطفے سے پھر تمہیں جوڑے جوڑے بنا دیا اور نہ ہی کسی مادہ کو کوئی حمل ہوتا ہے اور نہ ہی وہ جنتی ہے مگر یہ اس کے علم میں ہوتا ہے اور کسی عمر والے کو عمر نہیں دی جاتی اور نہ ہی کسی کی عمر میں کمی کی جاتی ہے مگر یہ سب ایک کتاب میں (لکھا ہوا) ہے } یقینا یہ اللہ پر بہت آسان ہے۔');
INSERT INTO "bayan" VALUES(35,12,'35:12','اور دو سمندر ایک جیسے نہیں ہیں یہ میٹھا ہے فرحت بخش اس کا پینا خوشگوار ہے اور یہ کھاری ہے سخت کڑوا اور ان سب سے تم کھاتے ہو تازہ گوشت اور (دونوں سے ہی) تم نکالتے ہو زیورات جنہیں تم پہنتے ہو اور تم دیکھتے ہوکشتیوں کو کہ چلتی ہیں (اس کے سینے کو) چیرتے ہوئے تاکہ تم اس کا فضل تلاش کرو اور تاکہ تم شکر اداکرو');
INSERT INTO "bayan" VALUES(35,13,'35:13','وہ داخل کرتا ہے رات کو دن میں اور داخل کرتا ہے دن کو رات میں اور اس نے مسخر کیا ہے سورج اور چاند کو ہرچیز چل رہی ہے ایک معین ّوقت تکیہ اللہ ہے تمہارا ربّ ! ُ کل بادشاہی اسی کی ہے } اور جنہیں تم پکارتے ہو اس کے سوا وہ ایک ذرّہ بھر اختیار نہیں رکھتے');
INSERT INTO "bayan" VALUES(35,14,'35:14','اگر تم انہیں پکارو تو وہ تمہاری پکار کو نہیں سنتے اور اگر سن بھی لیں تو تمہاری دعا قبول نہیں کرسکتے اور قیامت کے دن وہ تمہارے شرک کا انکار کردیں گے اور نہیں آگاہ کرے گا کوئی بھی آپ کو اس (اللہ) کی طرح جو ہرچیز سے باخبر ہے');
INSERT INTO "bayan" VALUES(35,15,'35:15','اے لوگو ! تم سب اللہ کے محتاج ہو اور اللہ تو الغنی اور الحمید ہے');
INSERT INTO "bayan" VALUES(35,16,'35:16','اگر وہ چاہے تو تم سب کو لے جائے (ختم کردے) اور ایک نئی مخلوق لے آئے');
INSERT INTO "bayan" VALUES(35,17,'35:17','اور یہ اللہ پر ہرگز بھاری نہیں ہے');
INSERT INTO "bayan" VALUES(35,18,'35:18','اور کوئی جان نہیں اٹھائے گی کسی دوسری جان کا بوجھ اور اگر بوجھ تلے دبی کوئی جان کسی کو پکارے گی اپنے بوجھ (کے ہٹانے) کے لیے تو نہیں اٹھائے گا کوئی اس میں سے کچھ بھی اگرچہ وہ قرابت دار ہی ہو (اے نبی صلى الله عليه وسلم !) آپ تو انہی لوگوں کو خبردار کرسکتے ہیں جو ڈرتے ہیں اپنے ربّ سے غیب میں رہتے ہوئے اور نماز قائم کرتے ہیں اور جو کوئی بھی پاکیزگی اختیار کرتا ہے تو وہ اپنے ہی بھلے کے لیے کرتا ہے اور اللہ ہی کی طرف (سب کو) پلٹنا ہے');
INSERT INTO "bayan" VALUES(35,19,'35:19','اور برابر نہیں ہوسکتے اندھا اور دیکھنے والا');
INSERT INTO "bayan" VALUES(35,20,'35:20','اور نہ اندھیرے اور روشنی (برابر ہوسکتے ہیں)');
INSERT INTO "bayan" VALUES(35,21,'35:21','اور نہ سایہ اور دھوپ (برابر ہوسکتے ہیں');
INSERT INTO "bayan" VALUES(35,22,'35:22','اور نہ برابر ہوسکتے ہیں زندہ اور مردہ لوگ } یقینا اللہ سناتا ہے جس کو چاہتا ہے اور (اے نبی صلى الله عليه وسلم !) آپ نہیں سنا سکتے انہیں جو قبروں کے اندر ہیں');
INSERT INTO "bayan" VALUES(35,23,'35:23','آپ صلى الله عليه وسلم نہیں ہیں مگر صرف خبردار کرنے والے');
INSERT INTO "bayan" VALUES(35,24,'35:24','یقینا ہم نے بھیجا ہے آپ صلى الله عليه وسلم کو حق کے ساتھ بشیر اور نذیر بنا کر } اور کوئی امت ایسی نہیں جس میں کوئی خبردار کرنے والا نہ گزرا ہو');
INSERT INTO "bayan" VALUES(35,25,'35:25','اور اگر یہ لوگ آپ صلى الله عليه وسلم کو جھٹلائیں تو جھٹلا چکے ہیں وہ لوگ بھی جو ان سے پہلے تھے ان کے پاس آئے تھے ان کے رسول ؑ واضح نشانیاں صحیفے اور روشن کتابیں لے کر');
INSERT INTO "bayan" VALUES(35,26,'35:26','پھر میں نے پکڑ لیا ان کو جنہوں نے کفر کیا تو (دیکھ لو) کیسی رہی میری سزا ؟');
INSERT INTO "bayan" VALUES(35,27,'35:27','کیا تم نے نہیں دیکھا کہ اللہ نے اتارا آسمان سے پانی ؟ پھر ہم نے نکالے اس سے پھل جن کے رنگ ایک دوسرے سے مختلف ہیں اور پہاڑوں میں بھی دھاریاں ہیں سفید اور سرخ ان کے مختلف رنگ ہیں اور کو وں جیسے سیاہ بھی');
INSERT INTO "bayan" VALUES(35,28,'35:28','اور اسی طرح انسانوں جانوروں اور چوپایوں کے بھی مختلف رنگ ہیں یقینا اللہ سے ڈرتے تو اس کے بندوں میں سے وہی ہیں جو اہل علم ہیں۔ یقینا اللہ بہت زبردست ہے نہایت بخشنے والا۔');
INSERT INTO "bayan" VALUES(35,29,'35:29','یقینا وہ لوگ جو تلاوت کرتے ہیں اللہ کی کتاب کی اور نماز قائم کرتے ہیں اور خرچ کرتے ہیں اس میں سے جو ہم نے انہیں دیا ہے خفیہ اور اعلانیہ وہ ایسی تجارت کے امیدوار ہیں جس میں کبھی خسارہ نہیں ہوگا');
INSERT INTO "bayan" VALUES(35,30,'35:30','تا کہ اللہ ان کو ان (کے اعمال) کے بھرپور اجر دے اور اپنے فضل سے انہیں مزید عطا کرے۔ یقینا وہ بہت بخشنے والا بہت قدر دان ہے');
INSERT INTO "bayan" VALUES(35,31,'35:31','اور (اے نبی صلى الله عليه وسلم !) ہم نے جو وحی بھیجی ہے آپ کی طرف کتاب میں سے وہی حق ہے تصدیق کرتے ہوئے آئی ہے اس (کتاب) کی جو اس سے پہلے موجود ہے یقینا اللہ اپنے بندوں سے خوب باخبر انہیں دیکھنے والا ہے');
INSERT INTO "bayan" VALUES(35,32,'35:32','پھر ہم نے کتاب کا وارث بنایا ان لوگوں کو جنہیں ہم نے اپنے بندوں میں سے ُ چن لیا تو ان میں سے کچھ اپنی جانوں پر ظلم کرنے والے ہیں اور ان میں کچھ درمیانی راہ پر چلنے والے ہیں اور ان میں سے کچھ نیکیوں میں سبقت لے جانے والے ہیں اللہ کی توفیق سے } یہی بہت بڑی فضیلت ہے');
INSERT INTO "bayan" VALUES(35,33,'35:33','(ان کے لیے) باغات ہوں گے رہنے کے جن میں وہ داخل ہوں گے ان میں انہیں پہنائے جائیں گے سونے کے کنگن اور موتی اور ان میں ان کا لباس ریشم ہوگا');
INSERT INTO "bayan" VALUES(35,34,'35:34','اور وہ (جب جنت میں داخل ہوں گے تو) کہیں گے کہ ُ کل حمد اور کل شکر اس اللہ کے لیے ہے جس نے ہم سے غم کو دور کردیا یقینا ہمارا رب بہت بخشنے والا اور بہت قدر افزائی فرمانے والا ہے');
INSERT INTO "bayan" VALUES(35,35,'35:35','جس نے ہمیں ہمیشہ آباد رہنے والے گھر میں لااتارا ہے اپنے خاص فضل سے اب ہمیں اس میں نہ تو کوئی مشقت جھیلنی پڑے گی اور نہ ہی اس میں ہمیں کوئی تکان لاحق ہوگی');
INSERT INTO "bayan" VALUES(35,36,'35:36','(اس کے برعکس) جن لوگوں نے کفر کیا ہے ان کے لیے جہنم کی آگ ہوگی نہ تو ان کا قصہ چکایا جائے گا کہ وہ مرجائیں اور نہ ہی ان کے لیے اس کے عذاب میں تخفیف کی جائے گی اسی طرح ہم بدلہ دیتے ہیں ہر ناشکرے انسان کو');
INSERT INTO "bayan" VALUES(35,37,'35:37','اور وہ اس میں چیخ و پکار کریں گے : اے ہمارے پروردگار ! ہمیں (یہاں سے) نکال لے ! اب ہم نیک اعمال کریں گے ان اعمال سے مختلف جو ہم (پہلے) کیا کرتے تھے کیا ہم نے تمہیں اتنی عمر نہیں دی تھی کہ اس میں سبق حاصل کرلیا جس نے سبق حاصل کرنا چاہا اور تمہارے پاس خبردار کرنے والا بھی تو آیا تھا تو اب چکھو (مزہ اس عذاب کا اور یاد رکھو کہ) ظالموں کے لیے کوئی مدد گار نہیں ہے');
INSERT INTO "bayan" VALUES(35,38,'35:38','یقینا اللہ خوب جاننے والا ہے آسمانوں اور زمین کی چھپی ہوئی چیزوں کا یقینا وہ واقف ہے سینوں میں چھپے رازوں سے');
INSERT INTO "bayan" VALUES(35,39,'35:39','وہی تو ہے جس نے تمہیں جانشین بنایا زمین میں تو جس کسی نے کفر کیا تو اس کے کفر کا وبال اسی پر ہوگا اور کافروں کے لیے ان کا کفر ان کے رب کے نزدیک سوائے غضب کے کسی چیز میں اضافہ نہیں کرے گا اور کافروں کے لیے ان کا کفر سوائے خسارے کے اور کچھ نہیں بڑھائے گا');
INSERT INTO "bayan" VALUES(35,40,'35:40','آپ صلى الله عليه وسلم کہیے کہ کیا تم نے اپنے ان شریکوں کے بارے میں کبھی غور کیا جن کو تم اللہ کے سوا پکارتے ہو ؟ ذرا مجھے دکھائو کہ انہوں نے زمین میں کونسی چیز پیدا کی ہے یا ان کی کوئی شراکت ہے آسمانوں میں یا ہم نے انہیں کوئی کتاب عطا کی تھی اور وہ اس کی کسی واضح دلیل پر ہیں بلکہ یہ ظالم آپس میں ایک دوسرے کو وعدے نہیں دے رہے مگر فریب کے');
INSERT INTO "bayan" VALUES(35,41,'35:41','یقینا اللہ ہی تھامے ہوئے ہے آسمانوں اور زمین کو کہ وہ (اپنے راستے سے) ہٹ نہ جائیں اور اگر وہ ہٹ جائیں تو کوئی نہیں جو ان کو تھام سکے اس کے بعد یقینا وہ بہت بردبار بہت بخشنے والا ہے');
INSERT INTO "bayan" VALUES(35,42,'35:42','اور انہوں نے اللہ کی پختہ قسمیں کھائی تھیں کہ اگر ان کے پاس کوئی خبردار کرنے والاآیا تو وہ لازماً ہدایت یافتہ ہوجائیں گے کسی بھی امت سے بڑھ کر پھر جب آگئے ان کے پاس خبردار کرنے والے (محمد صلى الله عليه وسلم تو اس چیز نے ان کے اندر (حق سے) فرار کے سوا کوئی اضافہ نہیں کیا');
INSERT INTO "bayan" VALUES(35,43,'35:43','زمین میں تکبر کرتے ہوئے اور ُ بری چالیں چلتے ہوئے اور ُ بری چال کا وبال نہیں پڑتا مگر اس کے چلنے والے پر ہی تو وہ کس چیز کے انتظار میں ہیں سوائے پہلے والوں کے انجام کے تو تم ہرگز نہیں پائو گے اللہ کے قانون میں کوئی تبدیلی اور تم ہرگز نہیں پائو گے اللہ کے قانون کو رخ پھیرتے ہوئے');
INSERT INTO "bayan" VALUES(35,44,'35:44','کیا یہ لوگ زمین میں گھومے پھرے نہیں ہیں پس یہ دیکھتے کہ کیسا ہوا انجام ان کا جو ان سے پہلے تھے حالانکہ وہ طاقت میں ان سے کہیں بڑھ کر تھے ! اور اللہ کی شان ایسی نہیں کہ اسے کوئی بھی چیز آسمانوں میں یا زمین میں عاجز کرسکے یقینا وہ ہرچیز کا علم رکھنے والا ہرچیز پر قادر ہے');
INSERT INTO "bayan" VALUES(35,45,'35:45','اور اگر اللہ (فوری) گرفت کرتا لوگوں کی ان کے اعمال کے سبب تو اس (زمین) کی پشت پر کوئی جاندار بھی باقی نہ چھوڑتا لیکن وہ موخر کرتا رہتا ہے ان کو ایک وقت مقرر تک پھر جب ان کا وقت ِمعین ّآ جائے گا تو اللہ یقینا اپنے بندوں کے حالات کو خود دیکھنے والا ہے');
INSERT INTO "bayan" VALUES(36,1,'36:1','یٰسٓ');
INSERT INTO "bayan" VALUES(36,2,'36:2','قسم ہے قرآن حکیم کی');
INSERT INTO "bayan" VALUES(36,3,'36:3','کہ (اے محمد صلى الله عليه وسلم !) یقینا آپ رسولوں میں سے ہیں');
INSERT INTO "bayan" VALUES(36,4,'36:4','سیدھے راستے پر ہیں۔');
INSERT INTO "bayan" VALUES(36,5,'36:5','(اور اس قرآن کا) نازل کیا جانا ہے اس ہستی کی جانب سے جو بہت زبردست نہایت رحم فرمانے والا ہے');
INSERT INTO "bayan" VALUES(36,6,'36:6','تا کہ آپ صلى الله عليه وسلم خبردار کردیں اس قوم کو جن کے آباء و اَجداد کو خبردار نہیں کیا گیا پس وہ غفلت میں پڑے ہوئے ہیں');
INSERT INTO "bayan" VALUES(36,7,'36:7','ان کی اکثریت پر ہمارا قول (قانونِ عذاب) سچ ثابت ہوچکا ہے تو اب وہ ایمان نہیں لائیں گے');
INSERT INTO "bayan" VALUES(36,8,'36:8','ہم نے ان کی گردنوں میں طوق ڈال دیے ہیں اور وہ ان کی ٹھوڑیوں تک ہیں پس ان کے سر اونچے اٹھے رہ گئے ہیں');
INSERT INTO "bayan" VALUES(36,9,'36:9','اور ہم نے ایک دیوار کھڑی کردی ہے ان کے آگے اور ایک ان کے پیچھے اس طرح ہم نے انہیں ڈھانپ دیا ہے پس اب وہ دیکھ نہیں سکتے');
INSERT INTO "bayan" VALUES(36,10,'36:10','اور (اے نبی صلى الله عليه وسلم !) ان کے حق میں برابر ہے خواہ آپ انہیں خبردار کریں یا نہ کریں یہ ایمان نہیں لائیں گے');
INSERT INTO "bayan" VALUES(36,11,'36:11','آپ صلى الله عليه وسلم تو بس اسی شخص کو خبردار کرسکتے ہیں جو الذکر ّ (قرآن) کی پیروی کرے اور غیب میں ہوتے ہوئے رحمٰن سے ڈرے تو ایسے لوگوں کو آپ صلى الله عليه وسلم خوشخبری دے دیجیے مغفرت اور باعزت اجر کی۔');
INSERT INTO "bayan" VALUES(36,12,'36:12','یقینا ہم ہی زندہ کریں گے ُ مردوں کو اور ہم لکھ رہے ہیں جو کچھ انہوں نے آگے بھیجا اور جو اثرات وہ اپنے پیچھے چھوڑ گئے ہیں ور ہرچیز کا احاطہ ہم نے ایک واضح کتاب میں کررکھا ہے');
INSERT INTO "bayan" VALUES(36,13,'36:13','ان کے لیے آپ صلى الله عليه وسلم اس بستی والوں کی مثال بیان کیجیے جب کہ ان کے پاس رسول آئے');
INSERT INTO "bayan" VALUES(36,14,'36:14','جب ہم نے بھیجے ان کی طرف دو رسول پس انہوں نے ان دونوں کو جھٹلادیا تو ہم نے (ان کو) تقویت دی ایک تیسرے کے ساتھ تو انہوں نے کہا کہ ہم تمہاری طرف بھیجے گئے ہیں');
INSERT INTO "bayan" VALUES(36,15,'36:15','انہوں نے کہا کہ تم نہیں ہو مگر ہم جیسے انسان اور رحمٰن نے کوئی چیز نازل نہیں کی تم محض جھوٹ بول رہے ہو ');
INSERT INTO "bayan" VALUES(36,16,'36:16','انہوں نے کہا کہ ہمارا رب جانتا ہے کہ ہم یقینا تمہاری طرف بھیجے گئے ہیں');
INSERT INTO "bayan" VALUES(36,17,'36:17','اور نہیں ہے ہم پر کوئی اور ذمہ داری سوائے (اللہ کا پیغام) صاف صاف پہنچا دینے کے');
INSERT INTO "bayan" VALUES(36,18,'36:18','نہوں نے کہا کہ ہمیں تم سے نحوست کا اندیشہ ہے گر تم باز نہ آئے تو ہم ضرور تمہیں سنگسار کردیں گے اور تمہیں ضرور پہنچے گی ہماری طرف سے دردناک سزا');
INSERT INTO "bayan" VALUES(36,19,'36:19','نہوں نے کہا کہ تمہاری نحوست تو تمہارے اپنے ساتھ ہے یا اس لیے کہ تمہیں نصیحت کی گئی ہے ؟ اور تمہارے اپنے اندر بھی (اللہ کی نشانیاں) ہیں کیا تم دیکھتے نہیں ہو ؟ بلکہ تم حد سے بڑھ جانے والے لوگ ہو۔');
INSERT INTO "bayan" VALUES(36,20,'36:20','اور شہر کے َپرلے سرے سے آیا ایک شخص بھاگتا ہوا اس نے کہا : اے میری قوم کے لوگو ! تم ان رسولوں ؑ کی پیروی کرو');
INSERT INTO "bayan" VALUES(36,21,'36:21','پیروی کرو ان کی جو تم سے کسی اجرت کے طالب نہیں ہیں اور جو ہدایت یافتہ ہیں');
INSERT INTO "bayan" VALUES(36,22,'36:22','اور مجھے کیا ہے کہ میں عبادت نہ کروں اس ہستی کی جس نے مجھے پیدا کیا ہے اور اسی کی طرف تم سب لوٹا دیے جائو گے');
INSERT INTO "bayan" VALUES(36,23,'36:23','یا میں اس کے سوا دوسروں کو معبود بنالوں ؟ اگر رحمن مجھے کوئی نقصان پہنچانا چاہے تو ان (جھوٹے معبودوں) کی سفارش میرے کسی کام نہیں آئے گی اور نہ وہ مجھے چھڑاسکیں گے');
INSERT INTO "bayan" VALUES(36,24,'36:24','تب تو میں یقینا بہت کھلی گمراہی میں پڑ جائوں گا');
INSERT INTO "bayan" VALUES(36,25,'36:25','یں تو تمہارے رب پر ایمان لے آیا ہوں سو میری بات سنو !');
INSERT INTO "bayan" VALUES(36,26,'36:26','ہہ دیا گیا کہ تم داخل ہو جائو جنت میں اس نے کہا : کاش میری قوم کو معلوم ہوجاتا');
INSERT INTO "bayan" VALUES(36,27,'36:27','وہ جو میرے رب نے میری مغفرت فرمائی ہے اور مجھے باعزت لوگوں میں شامل کرلیا ہے');
INSERT INTO "bayan" VALUES(36,28,'36:28','اور اس کے بعد ہم نے اس کی قوم پر آسمان سے کوئی لشکر نازل نہیں کیا تھا اور نہ ہی ہم (لشکر) نازل کرنے والے تھے');
INSERT INTO "bayan" VALUES(36,29,'36:29','وہ تو بس ایک زور دار چنگھاڑ تھی جبھی وہ سب بجھ کر رہ گئے');
INSERT INTO "bayan" VALUES(36,30,'36:30','افسوس ہے بندوں کے حال پر ! نہیں آتا ان کے پاس کوئی پیغمبر مگر وہ اس کا مذاق ہی اڑاتے ہیں');
INSERT INTO "bayan" VALUES(36,31,'36:31','کیا انہوں نے دیکھا نہیں کہ ان سے پہلے ہم نے کتنی نسلوں کو ہلاک کردیا کہ وہ ان کی طرف لوٹ کر نہیں آتے !');
INSERT INTO "bayan" VALUES(36,32,'36:32','اور وہ سب کے سب ہمارے ہی سامنے حاضر کیے جائیں گے');
INSERT INTO "bayan" VALUES(36,33,'36:33','ور ان کے لیے ایک نشانی ُ مردہ زمین ہے ہم نے اسے زندہ کیا اور اس سے اناج نکالا تو اس میں سے وہ کھاتے ہیں');
INSERT INTO "bayan" VALUES(36,34,'36:34','اور ہم نے بنا دیے ہیں اس میں باغات کھجور اور انگور کے اور جاری کردیے ہیں ہم نے اس میں چشمے');
INSERT INTO "bayan" VALUES(36,35,'36:35','تا کہ یہ ان کے پھلوں میں سے کھائیں اور یہ سب ان کے ہاتھوں نے تو نہیں بنایا تو کیا تم لوگ شکر نہیں کرتے ؟');
INSERT INTO "bayan" VALUES(36,36,'36:36','اک ہے وہ ذات جس نے پیدا کیا ہے تمام جوڑوں کو اس میں سے بھی جو زمین اگاتی ہے اور خود ان کی اپنی جانوں سے بھی اور اس میں سے بھی جن کے بارے میں یہ نہیں جانتے');
INSERT INTO "bayan" VALUES(36,37,'36:37','اور ان کے لیے ایک نشانی رات بھی ہے ہم کھینچ لیتے ہیں اس سے دن کو تو اس وقت یہ اندھیرے میں رہ جاتے ہیں');
INSERT INTO "bayan" VALUES(36,38,'36:38','ور سورج چلتا رہتا ہے اپنے مقررہ راستے پر یہ اندازہ مقرر کیا ہوا ہے اس کا جو بہت زبردست بہت علم والا ہے');
INSERT INTO "bayan" VALUES(36,39,'36:39','اور چاند کی ہم نے منزلیں مقرر کردی ہیں یہاں تک کہ وہ پھر کھجور کی پرانی سوکھی ٹہنی کی طرح ہوجاتا ہے');
INSERT INTO "bayan" VALUES(36,40,'36:40','نہ تو سورج کے لیے ممکن ہے کہ وہ چاند کو پکڑ لے اور نہ ہی رات دن سے آگے نکل سکتی ہے ور یہ سب اپنے اپنے دائرے میں تیر رہے ہیں');
INSERT INTO "bayan" VALUES(36,41,'36:41','اور ان کے لیے ایک نشانی یہ بھی ہے کہ ہم نے ان کی نسل کو بھری ہوئی کشتی میں سوار کردیا');
INSERT INTO "bayan" VALUES(36,42,'36:42','اور ہم نے ان کے لیے اسی طرح کی اور چیزیں بھی پیدا کردیں جن پر یہ لوگ سواری کرتے ہیں');
INSERT INTO "bayan" VALUES(36,43,'36:43','اور اگر ہم چاہیں تو انہیں غرق کردیں پھر نہ تو ان کا کوئی فریاد رس ہوگا اور نہ ہی وہ چھڑائے جائیں گے');
INSERT INTO "bayan" VALUES(36,44,'36:44','مگر یہ ہماری طرف سے رحمت ہے اور فائدہ اٹھانا ہے ایک وقت ِمعین ّتک کے لیے');
INSERT INTO "bayan" VALUES(36,45,'36:45','اور جب ان سے کہا جاتا ہے کہ سبق حاصل کرو اس سے جو تمہارے سامنے ہے اور جو تمہارے پیچھے ہے تاکہ تم پر رحم کیا جائے');
INSERT INTO "bayan" VALUES(36,46,'36:46','اور نہیں آتی ان کے پاس کوئی نشانی ان کے رب کی نشانیوں میں سے مگر یہ اس سے اعراض ہی کرتے ہیں');
INSERT INTO "bayan" VALUES(36,47,'36:47','اور جب ان سے کہا جاتا ہے کہ خرچ کرو اس میں سے جو اللہ نے تمہیں دیا ہے تو یہ کافر کہتے ہیں اہل ِ ایمان سے کہ کیا ہم انہیں کھلائیں جنہیں اللہ چاہتا تو خود کھلا دیتا ! تم تو خود ہی صریح گمراہی میں پڑے ہوئے ہو !');
INSERT INTO "bayan" VALUES(36,48,'36:48','اور وہ یہ بھی کہتے ہیں کہ یہ وعدہ کب پورا ہوگا اگر تم لوگ سچے ہو !');
INSERT INTO "bayan" VALUES(36,49,'36:49','یہ لوگ نہیں انتظار کر رہے مگر ایک چنگھاڑ کا وہ انہیں آپکڑے گی اور یہ (اسی طرح) جھگڑ رہے ہوں گے');
INSERT INTO "bayan" VALUES(36,50,'36:50','پھر نہ تو وہ کوئی وصیت ہی کرسکیں گے اور نہ ہی اپنے گھر والوں کی طرف لوٹ کر جاسکیں گے۔ وہ تو بس ایک زور دار چنگھاڑ تھی تو جبھی وہ سب بجھ کر رہ گئے');
INSERT INTO "bayan" VALUES(36,51,'36:51','اور صور پھونکا جائے گا تو وہ یکایک قبروں سے (نکل کر) اپنے رب کی طرف دوڑ پڑیں گے');
INSERT INTO "bayan" VALUES(36,52,'36:52','وہ کہیں گے : ہائے ہماری شامت ! ہمیں کس نے اٹھا دیا ہماری قبروں سے ؟ پھر خود ہی کہیں گے :) ارے یہ تو وہی (دن) ہے جس کا رحمن نے وعدہ کیا تھا اور پیغمبروں نے سچ کہا تھا');
INSERT INTO "bayan" VALUES(36,53,'36:53','پھر) ایک ہی چنگھاڑ ہوگی تو (اس کے نتیجے میں) وہ سب کے سب ہمارے حضور حاضر کردیے جائیں گے');
INSERT INTO "bayan" VALUES(36,54,'36:54','پس آج کے دن کسی جان پر کوئی ظلم نہیں کیا جائے گا اور تمہیں بدلے میں نہیں دیا جائے گا مگر وہی کچھ جو تم عمل کرتے رہے تھے');
INSERT INTO "bayan" VALUES(36,55,'36:55','یقینا اہل ِجنت اس دن مزے کرنے میں مشغول ہوں گے');
INSERT INTO "bayan" VALUES(36,56,'36:56','وہ اور ان کی بیویاں سائے میں تختوں کے اوپر تکیے لگائے بیٹھے ہوں گے۔');
INSERT INTO "bayan" VALUES(36,57,'36:57','اس میں ان کے لیے تمام میوے ہوں گے اور ان کے لیے ہر وہ شے ہوگی جو وہ طلب کریں گے');
INSERT INTO "bayan" VALUES(36,58,'36:58','سلام کہا جائے گا ربّ ِرحیم کی طرف سے');
INSERT INTO "bayan" VALUES(36,59,'36:59','ور (اعلان کیا جائے گا :) اے مجرمو ! آج تم الگ ہو جائو۔');
INSERT INTO "bayan" VALUES(36,60,'36:60','اے آدم کی اولاد ! کیا میں نے تم سے یہ وعدہ نہیں لے لیا تھا کہ تم شیطان کی بندگی مت کرنا بیشک وہ تمہارا کھلا دشمن ہے');
INSERT INTO "bayan" VALUES(36,61,'36:61','اور میری ہی بندگی کرنا۔ یہی سیدھا راستہ ہے');
INSERT INTO "bayan" VALUES(36,62,'36:62','اور وہ تو تم میں سے بہت بڑی تعداد کو گمراہ کر کے لے گیا تو کیا تم لوگ عقل سے کام نہیں لیتے تھے');
INSERT INTO "bayan" VALUES(36,63,'36:63','اب یہ جہنم حاضر ہے جس کا تم کو وعدہ دیا جاتا تھا');
INSERT INTO "bayan" VALUES(36,64,'36:64','آج اس میں داخل ہو جائو اپنے کفر کی وجہ سے جو تم کرتے رہے تھے');
INSERT INTO "bayan" VALUES(36,65,'36:65','آج ہم ان کے مونہوں پر مہر لگا دیں گے اور ان کے ہاتھ ہم سے باتیں کریں گے اور ان کے پائوں گواہی دیں گے اس کمائی کے بارے میں جو وہ کرتے رہے تھے');
INSERT INTO "bayan" VALUES(36,66,'36:66','ور اگر ہم چاہیں تو ان کی آنکھیں مٹا دیں پھر یہ دوڑیں راستہ پانے کے لیے لیکن کہاں دیکھ سکیں گے ؟');
INSERT INTO "bayan" VALUES(36,67,'36:67','اور اگر ہم چاہیں تو ان کی صورتیں مسنح کردیں ان کے اپنے مقام پر تو نہ وہ آگے چل سکیں اور نہ پیچھے لوٹ سکیں');
INSERT INTO "bayan" VALUES(36,68,'36:68','اور جس کو ہم زیادہ عمر دیتے ہیں اس کی ِخلقت میں ضعف پیدا کردیتے ہیں۔ تو کیا یہ لوگ عقل سے کام نہیں لیتے ؟');
INSERT INTO "bayan" VALUES(36,69,'36:69','اور ہم نے ان کو شعر نہیں سکھایا اور نہ ہی یہ ان کے شایانِ شان ہے۔ یہ تو بس ایک یاد دہانی اور نہایت واضح قرآن ہے');
INSERT INTO "bayan" VALUES(36,70,'36:70','ا کہ وہ خبردار کر دے اس کو جو زندہ ہو اور کافروں پر قول واقع ہوجائے۔');
INSERT INTO "bayan" VALUES(36,71,'36:71','کیا انہوں نے غور نہیں کیا کہ ہم نے اپنے ہاتھوں کی بنائی ہوئی چیزوں میں سے ان کے لیے چوپائے پیدا کردیے جن کے اب یہ مالک بنے پھرتے ہیں');
INSERT INTO "bayan" VALUES(36,72,'36:72','اور ہم نے ان (مویشیوں) کو ان کا مطیع کردیا ہے تو ان میں سے بعض پر وہ سوار ہوتے ہیں اور بعض کا گوشت کھاتے ہیں');
INSERT INTO "bayan" VALUES(36,73,'36:73','اور ان کے لیے ان میں بہت سی منفعتیں اور پینے کی جگہیں ہیں۔ تو کیا یہ لوگ شکر نہیں کرتے !');
INSERT INTO "bayan" VALUES(36,74,'36:74','اور انہوں نے اللہ کے سوا دوسرے معبود گھڑ لیے ہیں شاید کہ ان کی مدد کی جائے');
INSERT INTO "bayan" VALUES(36,75,'36:75','وہ ہرگز کوئی استطاعت نہیں رکھتے ان کی مدد کی ل کہ یہ تو خود ہوں گے ان کے پکڑے ہوئے قیدی');
INSERT INTO "bayan" VALUES(36,76,'36:76','تو (اے نبی صلى الله عليه وسلم !) ان کی بات آپ کو رنجیدہ نہ کرے۔ ہم خوب جانتے ہیں جو کچھ یہ لوگ چھپا رہے ہیں اور جو کچھ ظاہر کر رہے ہیں');
INSERT INTO "bayan" VALUES(36,77,'36:77','تو کیا انسان نے دیکھا نہیں کہ ہم نے اسے بنایا ہے ایک قطرہ سے تو اب یہ بن گیا ہے کھلا جھگڑنے والا !');
INSERT INTO "bayan" VALUES(36,78,'36:78','اور یہ ہمارے بارے میں تو مثالیں بیان کرتا ہے اور اپنی پیدائش کو بھول گیا ہے ! کہتا ہے کہ کون زندہ کرے گا ہڈیوں کو جبکہ وہ بالکل بوسیدہ ہوچکی ہوں گی ؟');
INSERT INTO "bayan" VALUES(36,79,'36:79','(اے نبی صلى الله عليه وسلم !) آپ کہیے کہ ان کو وہی زندہ کرے گا جس نے ان کو پہلی مرتبہ پیدا کیا تھا اور وہ ہر مخلوق کا مکمل علم رکھنے والا ہے');
INSERT INTO "bayan" VALUES(36,80,'36:80','جس نے تمہارے لیے سبز درخت سے آگ پیدا کردی تو تم اس سے آگ سلگاتے ہو');
INSERT INTO "bayan" VALUES(36,81,'36:81','تو کیا جس نے پیدا کیا آسمانوں کو اور زمین کو وہ اس پر قادر نہیں ہے کہ ان جیسی مخلوق دوبارہ پیدا کر دے ! کیوں نہیں ! جبکہ وہ تو بہت تخلیق فرمانے والا سب کچھ جاننے والا ہے');
INSERT INTO "bayan" VALUES(36,82,'36:82','اس کے امر کی شان تو یہ ہے کہ جب وہ کسی شے کا ارادہ کرلیتا ہے تو وہ اسے کہتا ہے ہوجا اور وہ ہوجاتی ہے');
INSERT INTO "bayan" VALUES(36,83,'36:83','تو بڑی بابرکت ہے وہ ذات جس کے قبضہ قدرت میں ہر شے کا اختیار ہے اور اسی کی طرف تم سب لوٹا دیے جائو گے');
INSERT INTO "bayan" VALUES(37,1,'37:1','قسم ہے ان (فرشتوں) کی جو قطار در قطار صفیں باندھے حاضر رہتے ہیں');
INSERT INTO "bayan" VALUES(37,2,'37:2','ھرقسم ہے ان کی جو ڈانٹنے والے ہیں۔');
INSERT INTO "bayan" VALUES(37,3,'37:3','پھر قسم ہے ان کی جو تلاوت کرنے والے ہیں ذکر کی');
INSERT INTO "bayan" VALUES(37,4,'37:4','یقینا تمہارا اِلٰہ ایک ہی ہے۔');
INSERT INTO "bayan" VALUES(37,5,'37:5','جو مالک ہے آسمانوں اور زمین کا اور جو کچھ ان دونوں کے مابین ہے اور تمام مشرقوں کا');
INSERT INTO "bayan" VALUES(37,6,'37:6','ہم نے مزین کردیا ہے آسمانِ دنیا کو ستاروں کی زیبائش سے');
INSERT INTO "bayan" VALUES(37,7,'37:7','اور حفاظت کی خاطر ہر سرکش شیطان سے');
INSERT INTO "bayan" VALUES(37,8,'37:8','وہ سن نہیں پاتے ملأ اعلیٰ کی باتیں ور ان کو مار پڑتی ہے ہر طرف سے');
INSERT INTO "bayan" VALUES(37,9,'37:9','بھگانے کے لیے اور ان کے لیے (پھر) ایک دائمی عذاب ہے۔');
INSERT INTO "bayan" VALUES(37,10,'37:10','سوائے اس کے کہ جو کوئی اُچک لے کوئی چیز تو اس کا پیچھا کرتا ہے ایک چمکتا ہوا انگارہ');
INSERT INTO "bayan" VALUES(37,11,'37:11','تو (اے نبی صلى الله عليه وسلم !) ان سے پوچھئے کہ کیا ان کی تخلیق زیادہ مشکل ہے یا وہ کچھ جو ہم نے پیدا کیا ہے ان کو تو ہم نے پیدا کیا ہے ایک لیس دار گارے سے');
INSERT INTO "bayan" VALUES(37,12,'37:12','بلکہ (اے نبی صلى الله عليه وسلم !) آپ تعجب کرتے ہیں اور یہ لوگ تمسخر کرتے ہیں !');
INSERT INTO "bayan" VALUES(37,13,'37:13','اور جب انہیں نصیحت کی جاتی ہے تو وہ نصیحت حاصل نہیں کرتے۔');
INSERT INTO "bayan" VALUES(37,14,'37:14','اور جب کوئی نشانی دیکھتے ہیں تو مذاق اڑاتے ہیں');
INSERT INTO "bayan" VALUES(37,15,'37:15','اور کہتے ہیں کہ یہ تو کچھ نہیں مگر ایک کھلا جادو');
INSERT INTO "bayan" VALUES(37,16,'37:16','(وہ کہتے ہیں) کیا جب ہم مرجائیں گے اور مٹی اور ہڈیاں ہوجائیں گے تو کیا ہمیں دوبارہ اٹھایا جائے گا ؟');
INSERT INTO "bayan" VALUES(37,17,'37:17','اور کیا پہلے زمانے کے ہمارے آباء و اَجداد کو بھی');
INSERT INTO "bayan" VALUES(37,18,'37:18','ٓپ صلى الله عليه وسلم کہیے : ہاں ! (تم اٹھائے بھی جائو گے) اور تم ذلیل بھی ہوگے');
INSERT INTO "bayan" VALUES(37,19,'37:19','بس وہ ایک زور کی ڈانٹ ہوگی تو جبھی وہ (زندہ ہو کر) دیکھنے لگیں گے۔');
INSERT INTO "bayan" VALUES(37,20,'37:20','اور وہ کہیں گے : ہائے ہماری شامت یہ تو بدلے کا دن ہے');
INSERT INTO "bayan" VALUES(37,21,'37:21','(اُس وقت انہیں کہا جائے گا : ہاں) یہ وہی فیصلے کا دن ہے جس کو تم لوگ جھٹلایا کرتے تھے');
INSERT INTO "bayan" VALUES(37,22,'37:22','(پھر حکم دیا جائے گا) جمع کرو ان سب ظالموں کو اور ان کے ساتھیوں کو اور ان کو بھی جن کی یہ لوگ پوجا کیا کرتے تھے');
INSERT INTO "bayan" VALUES(37,23,'37:23','اللہ کے سوا پھر ان سب کو جہنم کا راستہ دکھائو !');
INSERT INTO "bayan" VALUES(37,24,'37:24','اور انہیں ذرا روکو ! ابھی ان سے کچھ پوچھا جائے گا۔');
INSERT INTO "bayan" VALUES(37,25,'37:25','یہ تمہیں کیا ہوگیا ہے تم ایک دوسرے کی مدد کیوں نہیں کر رہے ہو ؟');
INSERT INTO "bayan" VALUES(37,26,'37:26','بلکہ آج تو یہ بہت فرمانبردار بنے ہوئے ہیں !');
INSERT INTO "bayan" VALUES(37,27,'37:27','اور پھر وہ ایک دوسرے کی طرف رخ کرکے پوچھنے لگیں گے');
INSERT INTO "bayan" VALUES(37,28,'37:28','کہیں گے کہ تم ہی تو آیا کرتے تھے ہمارے پاس بڑے دبدبے کے ساتھ !');
INSERT INTO "bayan" VALUES(37,29,'37:29','وہ کہیں گے (کہ نہیں !) بلکہ تم لوگ خود ہی ایمان لانے والے نہیں تھے');
INSERT INTO "bayan" VALUES(37,30,'37:30','اور ہمیں تم پر کوئی اختیار تو تھا نہیں بلکہ تم خود ہی حد سے بڑھ جانے والے لوگ تھے');
INSERT INTO "bayan" VALUES(37,31,'37:31','تو اب ثابت ہوگیا ہے ہم پر ہمارے رب کا قول اب تو ہمیں (عذاب کا) مزہ چکھنا ہی ہوگا');
INSERT INTO "bayan" VALUES(37,32,'37:32','تو ہم نے تم لوگوں کو گمراہ کیا ہم خود بھی تو گمراہ تھے');
INSERT INTO "bayan" VALUES(37,33,'37:33','تو اس دن وہ سب کے سب عذاب میں شریک ہوں گے');
INSERT INTO "bayan" VALUES(37,34,'37:34','یقینا ہم مجرموں کے ساتھ ایسے ہی کیا کرتے ہیں');
INSERT INTO "bayan" VALUES(37,35,'37:35','ان کا معاملہ یہ تھا کہ جب ان سے کہا جاتا تھا کہ اللہ کے سوا کوئی معبود نہیں تو وہ استکبار کرتے تھے');
INSERT INTO "bayan" VALUES(37,36,'37:36','اور کہا کرتے تھے کہ کیا ہم اپنے معبودوں کو ایک مجنون شاعر کی خاطر چھوڑ دیں');
INSERT INTO "bayan" VALUES(37,37,'37:37','بلکہ وہ تو حق لے کر آئے اور انہوں صلى الله عليه وسلم نے تصدیق کی تمام رسولوں ؑ کی !');
INSERT INTO "bayan" VALUES(37,38,'37:38','اب تمہیں یقینا دردناک عذاب کا مزہ چکھنا ہوگا');
INSERT INTO "bayan" VALUES(37,39,'37:39','اور تمہیں بدلہ نہیں مل رہا مگر اسی کا جو کچھ تم کرتے رہے تھے۔');
INSERT INTO "bayan" VALUES(37,40,'37:40','سوائے اللہ کے مخلص بندوں کے');
INSERT INTO "bayan" VALUES(37,41,'37:41','یہی وہ لوگ ہیں جن کے لیے طے شدہ رزق ہے۔');
INSERT INTO "bayan" VALUES(37,42,'37:42','(یعنی) میوے اور ان کا اکرام کیا جائے گا۔');
INSERT INTO "bayan" VALUES(37,43,'37:43','نعمتوں والے باغات میں');
INSERT INTO "bayan" VALUES(37,44,'37:44','وہ تختوں کے اوپر بیٹھے ہوں گے آمنے سامنے');
INSERT INTO "bayan" VALUES(37,45,'37:45','گردش کر رہے ہوں گے ان پر ُ (خدام) ّنفیس شراب کے پیالوں کے ساتھ');
INSERT INTO "bayan" VALUES(37,46,'37:46','بالکل سفید انتہائی لذیذ پینے والوں کے لیے');
INSERT INTO "bayan" VALUES(37,47,'37:47','اس میں نہ تو سرگرانی (کی کیفیت) ہوگی اور نہ ہی وہ اس سے بہکیں گے۔');
INSERT INTO "bayan" VALUES(37,48,'37:48','اور ان کے پاس ہوں گی نیچی نگاہوں والی بڑی بڑی آنکھوں والی (بیویاں)');
INSERT INTO "bayan" VALUES(37,49,'37:49','گویا وہ انڈے ہوں چھپا کر رکھے گئے');
INSERT INTO "bayan" VALUES(37,50,'37:50','پھر وہ ایک دوسرے کی طرف رخ کر کے سوال کریں گے');
INSERT INTO "bayan" VALUES(37,51,'37:51','ان میں سے ایک کہنے والا کہے گا کہ میرا ایک ساتھی ہوا کرتا تھا');
INSERT INTO "bayan" VALUES(37,52,'37:52','وہ کہا کرتا تھا کہ کیا تم بھی تصدیق کرنے والوں میں سے ہو ؟');
INSERT INTO "bayan" VALUES(37,53,'37:53','کیا جب ہم مرجائیں گے اور مٹی اور ہڈیاں بن کر رہ جائیں گے تو کیا ہمیں (زندہ کر کے) بدلہ دیا جائے گا ؟');
INSERT INTO "bayan" VALUES(37,54,'37:54','(کوئی کہنے والا) کہے گا کہ کیا اب تم اسے جھانک کر دیکھنا چاہو گے ؟');
INSERT INTO "bayan" VALUES(37,55,'37:55','تو وہ جھانکے گا اور اسے دیکھے گا دوزخ کے عین درمیان میں۔');
INSERT INTO "bayan" VALUES(37,56,'37:56','وہ پکار اٹھے گا : اللہ کی قسم تم تو قریب تھے کہ مجھے بھی برباد کردیتے !');
INSERT INTO "bayan" VALUES(37,57,'37:57','اور اگر میرے رب کی نعمت مجھ پر نہ ہوتی تو میں بھی لازماً پکڑے گئے لوگوں میں سے ہوتا');
INSERT INTO "bayan" VALUES(37,58,'37:58','تو کیا اب ہمیں مرنا تو نہیں !');
INSERT INTO "bayan" VALUES(37,59,'37:59','سوائے ہماری اس پہلی موت کے اور اب ہمیں کوئی عذاب بھی نہیں دیا جائے گا');
INSERT INTO "bayan" VALUES(37,60,'37:60','یقینا یہی بہت بڑی کامیابی ہے');
INSERT INTO "bayan" VALUES(37,61,'37:61','یسی ہی چیز کے لیے عمل کرنا چاہیے عمل کرنے والوں کو !');
INSERT INTO "bayan" VALUES(37,62,'37:62','بھلا مہمانی کے طور پر یہ بہتر ہے یا زقوم کا درخت ؟');
INSERT INTO "bayan" VALUES(37,63,'37:63','ہم نے اس (درخت) کو فتنہ بنا دیا ہے ظالموں کے لیے۔');
INSERT INTO "bayan" VALUES(37,64,'37:64','وہ ایک درخت ہے جو جہنم کی تہہ میں سے نکلے گا۔');
INSERT INTO "bayan" VALUES(37,65,'37:65','اس کے خوشے ایسے ہوں گے جیسے شیاطین کے سر۔');
INSERT INTO "bayan" VALUES(37,66,'37:66','پھر وہ اس میں سے کھائیں گے اور اسی سے اپنے پیٹوں کو بھریں گے');
INSERT INTO "bayan" VALUES(37,67,'37:67','پھر اس کے اوپر ان کے لیے گرم پانی کا آمیزہ ہوگا');
INSERT INTO "bayan" VALUES(37,68,'37:68','پھر ان کا لوٹ کر آنا ہے جہنم کی طرف۔');
INSERT INTO "bayan" VALUES(37,69,'37:69','یہ وہ لوگ ہیں جنہوں نے اپنے آباء و اَجداد کو گمراہ پایا');
INSERT INTO "bayan" VALUES(37,70,'37:70','تو وہ ان ہی کے نقوش قدم پر دوڑے چلے جا رہے ہیں');
INSERT INTO "bayan" VALUES(37,71,'37:71','اور ان سے پہلے لوگوں کی بھی اکثریت گمراہ تھی');
INSERT INTO "bayan" VALUES(37,72,'37:72','اور ہم نے ان میں اپنے خبردار کرنے والے بھیجے تھے');
INSERT INTO "bayan" VALUES(37,73,'37:73','تو دیکھ لو ! کیسا انجام ہوا ان لوگوں کا جنہیں خبردار کردیا گیا تھا');
INSERT INTO "bayan" VALUES(37,74,'37:74','سوائے اللہ کے خاص بندوں کے');
INSERT INTO "bayan" VALUES(37,75,'37:75','اور ہمیں پکارا تھا نوح ؑ نے تو ہم کیا ہی اچھے دعا قبول کرنے والے ہیں !');
INSERT INTO "bayan" VALUES(37,76,'37:76','اور ہم نے نجات دی اس کو اور اس کے گھر والوں کو کرب عظیم سے');
INSERT INTO "bayan" VALUES(37,77,'37:77','اور ہم نے اس کی اولاد کو ہی باقی رہنے والابنایا۔');
INSERT INTO "bayan" VALUES(37,78,'37:78','اور ہم نے اسی (کے طریقے) پر بعد میں آنے والوں میں سے بھی (کچھ لوگوں کو) چھوڑا۔');
INSERT INTO "bayan" VALUES(37,79,'37:79','سلام ہو نوح ؑ پر تمام جہانوں میں !');
INSERT INTO "bayan" VALUES(37,80,'37:80','یقینا ہم اسی طرح بدلہ دیا کرتے ہیں محسنین کو۔');
INSERT INTO "bayan" VALUES(37,81,'37:81','یقینا وہ ہمارے مومن بندوں میں سے تھا۔');
INSERT INTO "bayan" VALUES(37,82,'37:82','پھر ہم نے غرق کردیا باقی سب کو۔');
INSERT INTO "bayan" VALUES(37,83,'37:83','ور اسی کی جماعت میں سے ابراہیم ؑ بھی تھا');
INSERT INTO "bayan" VALUES(37,84,'37:84','جب وہ اپنے رب کی طرف متوجہ ہوا قلب ِسلیم کے ساتھ');
INSERT INTO "bayan" VALUES(37,85,'37:85','جب کہ اس نے اپنے والد اور اپنی قوم سے کہا کہ تم لوگ کن چیزوں کو پوجتے ہو !');
INSERT INTO "bayan" VALUES(37,86,'37:86','کیا تم لوگ اللہ کے سوا خود ساختہ معبودوں کو چاہتے ہو !');
INSERT INTO "bayan" VALUES(37,87,'37:87','تو تمہارا کیا گمان ہے ربّ العالمین کے بارے میں ؟');
INSERT INTO "bayan" VALUES(37,88,'37:88','پس اس نے ایک نظر ستاروں پر ڈالی۔');
INSERT INTO "bayan" VALUES(37,89,'37:89','پھر کہا کہ میں تو بیمار ہوں۔');
INSERT INTO "bayan" VALUES(37,90,'37:90','تو وہ اس سے ِپھرگئے پیٹھ پھیرتے ہوئے۔');
INSERT INTO "bayan" VALUES(37,91,'37:91','پھر وہ چپکے سے ان کے معبودوں میں جا گھسا اور کہنے لگا کہ تم کھاتے کیوں نہیں ؟');
INSERT INTO "bayan" VALUES(37,92,'37:92','تمہیں کیا ہوگیا ہے تم بولتے بھی نہیں ہو !');
INSERT INTO "bayan" VALUES(37,93,'37:93','پھر وہ پل پڑا ان پر ضرب لگاتا ہوا داہنے ہاتھ سے۔');
INSERT INTO "bayan" VALUES(37,94,'37:94','تو وہ اس کی طرف آئے دوڑتے ہوئے۔');
INSERT INTO "bayan" VALUES(37,95,'37:95','اس نے کہا : کیا تم انہیں پوجتے ہو جنہیں تم خود تراشتے ہو !');
INSERT INTO "bayan" VALUES(37,96,'37:96','جبکہ اللہ نے پیدا کیا ہے تمہیں بھی اور جو کچھ تم بناتے ہو (اس کو بھی)');
INSERT INTO "bayan" VALUES(37,97,'37:97','انہوں نے کہا کہ اس کے لیے ایک عمارت بنائو پھر اس کو شعلے مارتی ہوئی آگ میں جھونک دو۔');
INSERT INTO "bayan" VALUES(37,98,'37:98','تو اس طرح انہوں نے اس کے ساتھ ایک دائو آزمایا لیکن ہم نے ان کو ہی نیچا دکھادیا۔');
INSERT INTO "bayan" VALUES(37,99,'37:99','اور اب اس نے کہا کہ میں اپنے رب کی طرف جانے والا ہوں وہ ضرور میری راہنمائی کرے گا۔');
INSERT INTO "bayan" VALUES(37,100,'37:100','(آپ ؑ نے دعا کی :) پروردگار ! مجھے ایک صالح بیٹا عطا فرما۔');
INSERT INTO "bayan" VALUES(37,101,'37:101','تو ہم نے اسے بشارت دی ایک حلیم الطبع لڑکے کی۔');
INSERT INTO "bayan" VALUES(37,102,'37:102','پھر جب وہ پہنچا اس کے ساتھ بھاگ دوڑ کرنے کی عمر کو اس نے کہا : اے میرے بیٹے ! میں دیکھ رہا ہوں خواب میں کہ میں تمہیں ذبح کر رہا ہوں تو دیکھو ! تمہاری کیا رائے ہے ؟اُس نے کہا : ابا جان ! آپ کر گزرئیے جس کا آپ کو حکم ہو رہا ہے۔ اللہ نے چاہا تو آپ مجھے صبر کرنے والوں میں پائیں گے۔');
INSERT INTO "bayan" VALUES(37,103,'37:103','پھر جب دونوں نے فرمانبرداری کی روش اختیار کرلی اور ابراہیم نے اس کو پیشانی کے َبل لٹا دیا۔');
INSERT INTO "bayan" VALUES(37,104,'37:104','اور ہم نے اسے پکارا کہ اے ابراہیم ؑ !');
INSERT INTO "bayan" VALUES(37,105,'37:105','تم نے خواب سچ کر دکھایا یقینا ہم اسی طرح بدلہ دیتے ہیں محسنین کو');
INSERT INTO "bayan" VALUES(37,106,'37:106','یقینا یہ بہت بڑی آزمائش تھی۔');
INSERT INTO "bayan" VALUES(37,107,'37:107','اور ہم نے ایک ذبح عظیم اس کا فدیہ دیا');
INSERT INTO "bayan" VALUES(37,108,'37:108','اور اسی (کے طریقے) پر ہم نے باقی رکھا بعد میں آنے والوں میں سے بھی (کچھ لوگوں کو)');
INSERT INTO "bayan" VALUES(37,109,'37:109','سلام ہو ابراہیم ؑ پر');
INSERT INTO "bayan" VALUES(37,110,'37:110','اسی طرح ہم بدلہ دیتے ہیں محسنین کو');
INSERT INTO "bayan" VALUES(37,111,'37:111','یقینا وہ ہمارے مومن بندوں میں سے تھا');
INSERT INTO "bayan" VALUES(37,112,'37:112','اور ہم نے اسے بشارت دی اسحاق ؑ کی ایک نبی صالحین میں سے');
INSERT INTO "bayan" VALUES(37,113,'37:113','ور ہم نے برکت نازل کی اس پر بھی اور اسحاق ؑ پر بھی۔ اور ان دونوں کی اولاد میں سے نیکوکار بھی تھے اور اپنی جانوں پر صریح ظلم کرنے والے بھی');
INSERT INTO "bayan" VALUES(37,114,'37:114','اور ہم نے موسیٰ ؑ ٰ اور ہارون ؑ پر بھی احسان فرمایا');
INSERT INTO "bayan" VALUES(37,115,'37:115','اور ہم نے نجات دی ان دونوں کو اور ان کی قوم کو ایک کرب عظیم سے');
INSERT INTO "bayan" VALUES(37,116,'37:116','اور ہم نے ان کی مدد کی پھر وہی غالب ہوئے۔');
INSERT INTO "bayan" VALUES(37,117,'37:117','اور ہم نے ان دونوں کو ایک روشن کتاب دی۔ یعنی تورات جس کی تعلیمات بہت واضح تھیں');
INSERT INTO "bayan" VALUES(37,118,'37:118','اور ہم نے ان دونوں کو صراط مستقیم کی ہدایت دی۔');
INSERT INTO "bayan" VALUES(37,119,'37:119','اور ان دونوں (کے طریقے) پر ہم نے باقی رکھا بعد میں آنے والوں میں سے بھی (کچھ لوگوں کو)');
INSERT INTO "bayan" VALUES(37,120,'37:120','سلام ہو موسیٰ ؑ ٰ پر اور ہارون ؑ پر۔');
INSERT INTO "bayan" VALUES(37,121,'37:121','یقینا ہم ایسا ہی بدلہ دیا کرتے ہیں محسنین کو۔');
INSERT INTO "bayan" VALUES(37,122,'37:122','یقیناوہ دونوں ہمارے مومن بندوں میں سے تھے');
INSERT INTO "bayan" VALUES(37,123,'37:123','اور یقینا الیاس ؑ بھی رسولوں میں سے تھا۔');
INSERT INTO "bayan" VALUES(37,124,'37:124','جب اس نے اپنی قوم سے کہا : کیا تم تقویٰ اختیار نہیں کرتے ؟');
INSERT INTO "bayan" VALUES(37,125,'37:125','کیا تم بعل کو پکارتے ہو اور بہترین خالق کو چھوڑتے ہو !');
INSERT INTO "bayan" VALUES(37,126,'37:126','یعنی اللہ جو تمہارا رب ہے اور تمہارے پہلے آباء و اَجداد کا بھی رب ہے۔');
INSERT INTO "bayan" VALUES(37,127,'37:127','تو انہوں نے اس کو جھٹلا دیا چناچہ وہ سب (قیامت کے دن) حاضرکر لیے جائیں گے۔');
INSERT INTO "bayan" VALUES(37,128,'37:128','سوائے اللہ کے ان بندوں کے جنہیں خالص کرلیا گیا۔');
INSERT INTO "bayan" VALUES(37,129,'37:129','اور اسی (کے طریقے) پر ہم نے باقی رکھا بعد میں آنے والوں میں سے بھی (کچھ لوگوں کو)۔');
INSERT INTO "bayan" VALUES(37,130,'37:130','سلام ہو الیاس ؑ پر۔');
INSERT INTO "bayan" VALUES(37,131,'37:131','یقینا ہم اسی طرح بدلہ دیا کرتے ہیں محسنین کو');
INSERT INTO "bayan" VALUES(37,132,'37:132','یقینا وہ ہمارے مومن بندوں میں سے تھا');
INSERT INTO "bayan" VALUES(37,133,'37:133','اور یقینا لوط ؑ بھی رسولوں میں سے تھا');
INSERT INTO "bayan" VALUES(37,134,'37:134','جب ہم نے نجات دی اس کو اور اس کے گھر والوں کو سب کو۔');
INSERT INTO "bayan" VALUES(37,135,'37:135','سوائے ایک بڑھیا کے جو پیچھے رہ جانے والوں میں تھی');
INSERT INTO "bayan" VALUES(37,136,'37:136','پھر ہم نے جڑ سے اکھاڑ کر پٹخ دیا باقیوں کو');
INSERT INTO "bayan" VALUES(37,137,'37:137','اور تم لوگ گزرتے ہو ان (کے کھنڈرات) پر صبح کے وقت بھی');
INSERT INTO "bayan" VALUES(37,138,'37:138','اور رات کو بھی تو کیا تم عقل سے کام نہیں لیتے ؟');
INSERT INTO "bayan" VALUES(37,139,'37:139','اور یقینا یونس ؑ بھی رسولوں میں سے تھا۔');
INSERT INTO "bayan" VALUES(37,140,'37:140','جب وہ بھاگ کر پہنچا اس کشتی کی طرف جو (پہلے سے ہی) بھری ہوئی تھی');
INSERT INTO "bayan" VALUES(37,141,'37:141','تو انہوں نے قرعہ ڈالا تو وہ ہوگیا دھکیل دیے جانے والوں میں سے');
INSERT INTO "bayan" VALUES(37,142,'37:142','تو اس کو نگل لیا مچھلی نے اور وہ ملامت زدہ تھا');
INSERT INTO "bayan" VALUES(37,143,'37:143','تو اگر وہ تسبیح کرنے والوں میں سے نہ ہوتا۔');
INSERT INTO "bayan" VALUES(37,144,'37:144','تو اس کے پیٹ ہی میں رہتا اس دن تک کہ جس میں لوگ اٹھائے جائیں گے');
INSERT INTO "bayan" VALUES(37,145,'37:145','تو ہم نے ڈال دیا اسے ایک چٹیل میدان میں اور وہ خستہ حالت میں تھا');
INSERT INTO "bayan" VALUES(37,146,'37:146','تو ہم نے اس کے اوپر یقطین کا ایک پودا اگا دیا۔');
INSERT INTO "bayan" VALUES(37,147,'37:147','پھر ہم نے اس کو بھیج دیا ایک لاکھ یا اس سے زیادہ لوگوں کی طرف۔');
INSERT INTO "bayan" VALUES(37,148,'37:148','تو وہ ایمان لے آئے اور ہم نے انہیں متاع دنیوی عطا کردی ایک خاص وقت تک۔');
INSERT INTO "bayan" VALUES(37,149,'37:149','تو (اے نبی صلى الله عليه وسلم !) آپ ان سے پوچھیں کیا تمہارے ربّ کے لیے تو بیٹیاں ہیں اور ان کے لیے بیٹے !');
INSERT INTO "bayan" VALUES(37,150,'37:150','کیا واقعی ہم نے فرشتوں کو مونث بنایا تھا اور وہ اس کے گواہ ہیں ؟');
INSERT INTO "bayan" VALUES(37,151,'37:151','خبردار ! یقینا یہ لوگ جھوٹ گھڑ کر کہتے ہیں');
INSERT INTO "bayan" VALUES(37,152,'37:152','کہ اللہ کے اولاد ہوئی اور یقینا یہ جھوٹے ہیں');
INSERT INTO "bayan" VALUES(37,153,'37:153','کیا اس نے بیٹیاں پسند کرلیں بیٹوں کو چھوڑ کر !');
INSERT INTO "bayan" VALUES(37,154,'37:154','تمہیں کیا ہوگیا ہے تم کیسا فیصلہ کرتے ہو ؟');
INSERT INTO "bayan" VALUES(37,155,'37:155','کیا تم ہوش سے کام نہیں لیتے ؟');
INSERT INTO "bayan" VALUES(37,156,'37:156','کیا تمہارے پاس کوئی واضح سند موجود ہے ؟');
INSERT INTO "bayan" VALUES(37,157,'37:157','تو لائو تم اپنی کتاب اگر تم (اپنے دعوے میں) سچے ہو !');
INSERT INTO "bayan" VALUES(37,158,'37:158','اور انہوں نے تو اللہ کے اور ِجنوں کے درمیان بھی نسبی رشتہ قائم کردیا ہے۔ حالانکہ جنوں کو خوب معلوم ہے کہ وہ تو گرفتارکرکے حاضر کیے جائیں گے۔');
INSERT INTO "bayan" VALUES(37,159,'37:159','اللہ پاک ہے ان باتوں سے جو یہ لوگ بنا رہے ہیں');
INSERT INTO "bayan" VALUES(37,160,'37:160','سوائے اللہ کے ان بندوں کے جو خالص کرلیے گئے ہیں۔');
INSERT INTO "bayan" VALUES(37,161,'37:161','پس تم اور جن کو تم پوجتے ہو۔');
INSERT INTO "bayan" VALUES(37,162,'37:162','تم اللہ کے خلاف کسی کو بہکا نہیں سکتے ہو۔');
INSERT INTO "bayan" VALUES(37,163,'37:163','سوائے اس کے جو خود جہنم میں گرنے والا ہو۔');
INSERT INTO "bayan" VALUES(37,164,'37:164','اور ہم میں سے کوئی نہیں مگر اس کے لیے ایک مقام مقرر ہے۔');
INSERT INTO "bayan" VALUES(37,165,'37:165','اور ہم تو سب کے سب صفیں باندھے کھڑے رہتے ہیں۔');
INSERT INTO "bayan" VALUES(37,166,'37:166','اور ہم تو سب کے سب (اپنے ربّ کی) تسبیح کرنے والے ہیں');
INSERT INTO "bayan" VALUES(37,167,'37:167','اور یہ لوگ تو کہا کرتے تھے');
INSERT INTO "bayan" VALUES(37,168,'37:168','کہ اگر ہمارے پاس بھی پہلوں کی کوئی نصیحت ہوتی');
INSERT INTO "bayan" VALUES(37,169,'37:169','تو ضرور ہم اللہ کے برگزیدہ بندے ہوتے۔');
INSERT INTO "bayan" VALUES(37,170,'37:170','مگر (اب وہ ذکر آگیا ہے تو) انہوں نے اس کا انکار کردیا تو عنقریب یہ جان جائیں گے');
INSERT INTO "bayan" VALUES(37,171,'37:171','اور ہماری یہ بات پہلے سے طے شدہ ہے اپنے ان بندوں کے لیے جن کو ہم (رسول بنا کر) بھیجتے رہے ہیں۔');
INSERT INTO "bayan" VALUES(37,172,'37:172','کہ ان کی لازماً مدد کی جائے گی۔');
INSERT INTO "bayan" VALUES(37,173,'37:173','اور یقینا ہمارا لشکر ہی غالب رہے گا');
INSERT INTO "bayan" VALUES(37,174,'37:174','تو (اے نبی صلى الله عليه وسلم !) آپ ان سے ذرا رخ پھیر لیجیے ایک وقت تک کے لیے۔');
INSERT INTO "bayan" VALUES(37,175,'37:175','اور آپ صلى الله عليه وسلم انہیں دیکھتے رہیے پس عنقریب وہ بھی دیکھ لیں گے۔');
INSERT INTO "bayan" VALUES(37,176,'37:176','تو کیا یہ لوگ ہمارے عذاب کے بارے میں جلدی مچا رہے ہیں ؟');
INSERT INTO "bayan" VALUES(37,177,'37:177','تو جب وہ نازل ہوگا ان کے میدان میں تو وہ بہت بری صبح ہوگی ان لوگوں کی جنہیں خبردار کردیا گیا تھا۔');
INSERT INTO "bayan" VALUES(37,178,'37:178','اور آپ صلى الله عليه وسلم رخ پھیر لیجیے ان کی طرف سے ایک وقت تک کے لیے۔');
INSERT INTO "bayan" VALUES(37,179,'37:179','اور آپ دیکھتے رہیے عنقریب وہ بھی دیکھ لیں گے');
INSERT INTO "bayan" VALUES(37,180,'37:180','پاک ہے آپ صلى الله عليه وسلم کا ربّ عزت اور اقتدار کا مالک ان تمام باتوں سے جو یہ لوگ بنا رہے ہیں۔');
INSERT INTO "bayan" VALUES(37,181,'37:181','اور سلام ہو تمام رسولوں پر۔');
INSERT INTO "bayan" VALUES(37,182,'37:182','اور ُ کل حمد اور تمام شکر اللہ کے لیے ہے جو تمام جہانوں کا ربّ ہے۔');
INSERT INTO "bayan" VALUES(38,1,'38:1','ص قسم ہے اس قرآن کی جو ذکر والا ہے۔');
INSERT INTO "bayan" VALUES(38,2,'38:2','لیکن جن لوگوں نے کفر کی روش اختیار کی ہے وہ غرور اور ضد میں مبتلا ہیں');
INSERT INTO "bayan" VALUES(38,3,'38:3','کتنی ہی قوموں کو ہم نے ان سے پہلے ہلاک کیا ہے تو (عذابِ الٰہی کے وقت) وہ لگے چیخنے ِچلانے حالانکہ تب خلاصی کا وقت نہیں رہا تھا');
INSERT INTO "bayan" VALUES(38,4,'38:4','اور انہیں بڑا تعجب ہوا ہے کہ ان کے پاس آیا ہے ایک خبردار کرنے والا ان ہی میں سے اور کافر کہتے ہیں کہ یہ ساحر ہے کذاب ّہے۔');
INSERT INTO "bayan" VALUES(38,5,'38:5','کیا اس نے تمام معبودوں کو بس ایک معبود بنا دیا ؟ یہ تو بڑی عجیب بات ہے !');
INSERT INTO "bayan" VALUES(38,6,'38:6','اور چل پڑے ان کے سردار (یہ کہتے ہوئے) کہ چلو جائو اور جمے رہو اپنے معبودوں پر یقینا اس بات میں تو کوئی غرض پوشیدہ ہے۔');
INSERT INTO "bayan" VALUES(38,7,'38:7','ہم نے ایسی کوئی بات پچھلے دین میں تو نہیں سنی ہے۔ یہ تو نہیں ہے مگر ایک گھڑی ہوئی چیز۔');
INSERT INTO "bayan" VALUES(38,8,'38:8','کیا اسی پر نازل ہوا ہے یہ ذکر ہمارے درمیان ؟ بلکہ حقیقت یہ ہے کہ یہ لوگ میرے ذکر کے معاملے میں شک میں پڑگئے ہیں بلکہ ابھی تک انہوں نے میرے عذاب کا مزہ نہیں چکھا۔');
INSERT INTO "bayan" VALUES(38,9,'38:9','کیا ان کے اختیار میں ہیں آپ کے رب کی رحمت کے خزانے ؟ جو بہت زبردست بہت عطا کرنے والا ہے۔');
INSERT INTO "bayan" VALUES(38,10,'38:10','کیا ان کے پاس ہے بادشاہی آسمانوں اور زمین کی اور جو کچھ ان دونوں کے مابین ہے اس سب کی ؟ تو چاہیے کہ یہ چڑھ جائیں رسیاں تان کر (آسمان پر)۔');
INSERT INTO "bayan" VALUES(38,11,'38:11','یہ بھی ایک لشکر ہے (پہلے ہلاک کیے گئے) لشکروں میں سے جواَب یہاں ہلاک ہوگا۔');
INSERT INTO "bayan" VALUES(38,12,'38:12','ان سے پہلے جھٹلایا تھا قوم نوح ؑ قوم عاد اور میخوں والے فرعون نے۔');
INSERT INTO "bayan" VALUES(38,13,'38:13','اور قوم ثمود قوم لوط ؑ اور َبن والوں (قومِ شعیب ؑ نے یہ ہیں وہ (ہلاک ہونے والے) لشکر !');
INSERT INTO "bayan" VALUES(38,14,'38:14','ان سب نے ہی رسولوں ؑ کی تکذیب کی تو وہ میری سزا کے مستحق ہوگئے۔');
INSERT INTO "bayan" VALUES(38,15,'38:15','اور یہ لوگ بھی اب منتظر نہیں ہیں مگر صرف ایک چنگھاڑ کے جس میں کوئی وقفہ نہیں ہوگا۔');
INSERT INTO "bayan" VALUES(38,16,'38:16','اور وہ کہتے ہیں : اے ہمارے رب ! ہمارا حصہ ُ تو ہمیں جلدی دے دے یومِ حساب سے پہلے۔');
INSERT INTO "bayan" VALUES(38,17,'38:17','(اے محمد صلى الله عليه وسلم !) آپ صبرکیجیے اس پر جو کچھ یہ لوگ کہہ رہے ہیں اور آپ صلى الله عليه وسلم تذکرہ کیجیے ہمارے بندے دائود کا جو بہت قوت والا تھا بیشک وہ (اللہ کی طرف) بہت رجوع کرنے والا تھا');
INSERT INTO "bayan" VALUES(38,18,'38:18','ہم نے تو اس کے ساتھ پہاڑوں کو مسخر کردیا تھا جو اس کے ساتھ تسبیح کرتے تھے شام کو بھی اور صبح کے وقت بھی');
INSERT INTO "bayan" VALUES(38,19,'38:19','اور پرندے بھی جو کہ جمع کردیے جاتے تھے۔ یہ سب کے سب اس کی طرف رجوع کرنے والے تھے۔');
INSERT INTO "bayan" VALUES(38,20,'38:20','اور ہم نے اس کی حکومت کو خوب مضبوط کیا تھا اور ہم نے اسے حکمت عطا کی تھی اور فیصلہ کن بات کہنے کی صلاحیت بھی۔');
INSERT INTO "bayan" VALUES(38,21,'38:21','اور کیا آپ کے پاس خبر آئی ہے جھگڑنے والوں کی ؟ جب وہ دیوار پھلانگ کر محراب میں داخل ہوگئے۔');
INSERT INTO "bayan" VALUES(38,22,'38:22','جب وہ دائود ؑ کے پاس آئے تو وہ ان سے ڈرا۔ انہوں نے کہا کہ آپ ڈریں نہیں ہم دو مخالف فریق ہیں ہم میں سے ایک نے دوسرے پر زیادتی کی ہے تو آپ ہمارے درمیان حق کے ساتھ فیصلہ کردیجیے اور آپ اسے ٹالیے نہیں اور سیدھی راہ کی طرف ہماری راہنمائی کیجیے۔');
INSERT INTO "bayan" VALUES(38,23,'38:23','یہ میرا بھائی ہے اس کے پاس ننانوے دنبیاں ہیں اور میری ایک ہی دنبی ہے۔ اب یہ کہتا ہے کہ وہ بھی میرے حوالے کر دو اور گفتگو میں اس نے مجھے دبا لیا ہے');
INSERT INTO "bayan" VALUES(38,24,'38:24','دائود ؑ نے کہا کہ یہ تو واقعتا اس نے بہت ظلم کیا ہے تمہاری دنبی کو اپنی دنبیوں کے ساتھ ملانے کا مطالبہ کر کے۔ اور یقینا مشترک معاملہ رکھنے والوں میں سے اکثر ایک دوسرے پر زیادتی کرتے ہیں سوائے ان لوگوں کے جو صاحب ِ ایمان اور نیک عمل والے ہوں اور ایسے لوگ بہت کم ہوتے ہیں۔ اور دائود ؑ کو اچانک خیال آیا کہ ہم نے اسے آزمایا ہے تو اس نے (فوراً) اپنے رب سے استغفار کیا اور اس کے حضور جھک گیا پوری طرح متوجہ ہو کر۔');
INSERT INTO "bayan" VALUES(38,25,'38:25','تو ہم نے اسے معاف کردیا۔ اور یقینا ہمارے پاس اس کے لیے مقام قرب اور اچھا انجام ہے۔');
INSERT INTO "bayan" VALUES(38,26,'38:26','(ہم نے کہا :) اے دائود ؑ ! ہم نے تمہیں زمین میں خلیفہ بنایا ہے لہٰذا تم لوگوں کے درمیان حق کے ساتھ فیصلے کرو اور دیکھو ! اپنی خواہش کی پیروی نہ کرنا کہ وہ تمہیں اللہ کی راہ سے بھٹکا دے گی۔ یقینا وہ لوگ جو بھٹک جاتے ہیں اللہ کے راستے سے ان کے لیے بڑا سخت عذاب ہے بسبب اس کے کہ وہ بھول گئے حساب کے دن کو۔');
INSERT INTO "bayan" VALUES(38,27,'38:27','اور ہم نے آسمان اور زمین کو اور جو کچھ ان دونوں کے مابین ہے اسے بےکار پیدا نہیں کیا۔ یہ گمان تو ان لوگوں کا ہے جنہوں نے کفر کیا پس ہلاکت اور بربادی ہے ان کافروں کے لیے آگ کی');
INSERT INTO "bayan" VALUES(38,28,'38:28','کیا ہم ان لوگوں کو جو ایمان لائے اور انہوں نے نیک اعمال کیے زمین میں فساد مچانے والوں کی طرح کردیں گے ؟ کیا ہم متقیوں کو فاجروں کی طرح کردیں گے ؟');
INSERT INTO "bayan" VALUES(38,29,'38:29','(اے نبی صلى الله عليه وسلم !) یہ کتاب جو ہم نے آپ پر نازل کی ہے بہت بابرکت ہے تاکہ وہ اس کی آیات پر تدبر کریں اور ہوش مند لوگ اس سے سبق حاصل کریں');
INSERT INTO "bayan" VALUES(38,30,'38:30','اور ہم نے دائود ؑ کو سلیمان ؑ (جیسا بیٹا) عطا کیا۔ وہ بہت ہی اچھا بندہ تھا۔ بیشک وہ (ہماری طرف) بڑا ہی رجوع کرنے والا تھا');
INSERT INTO "bayan" VALUES(38,31,'38:31','جب اس کے سامنے پیش کیے گئے شام کے وقت عمدہ نسل کے اصیل گھوڑے۔');
INSERT INTO "bayan" VALUES(38,32,'38:32','تو اس نے کہا کہ میں نے پسند کرلیا مال کی محبت کو اپنے رب کے ذکر کے مقابلے میں ! یہاں تک کہ (سور ج) چھپ گیا اوٹ میں');
INSERT INTO "bayan" VALUES(38,33,'38:33','(تو اس نے کہا :) واپس لائو ان کو میرے پاس ! تو اب وہ لگا تلوار مارنے ان کی ٹانگوں اور گردنوں پر۔');
INSERT INTO "bayan" VALUES(38,34,'38:34','اور (اسی طرح) ہم نے آزمائش میں ڈالا سلیمان ؑ کو اور اس کے تخت پر ہم نے ایک جسد ڈال دیا پھر اس نے رجوع کیا');
INSERT INTO "bayan" VALUES(38,35,'38:35','اس نے دعا کی : پروردگار ! مجھے معاف فرما دے اور مجھے ایسی حکومت عطا فرما جو میرے بعد کسی اور کے لیے سزاوار نہ ہو یقینا تو ہی عطا فرمانے والا ہے۔');
INSERT INTO "bayan" VALUES(38,36,'38:36','تو ہم نے اس کے لیے ہوا کو بھی مسخر کردیا وہ اس کے حکم سے چلتی تھی بہت نرمی سے جہاں کہیں کا وہ قصد کرتا');
INSERT INTO "bayan" VALUES(38,37,'38:37','اور سرکش ِجنات ّکو بھی (ہم نے مسخر کردیا تھا) سب کے سب عمارتیں بنانے والوں اور غوطہ خوروں کو۔');
INSERT INTO "bayan" VALUES(38,38,'38:38','اور دوسرے ِجنات ّکو بھی جو جکڑے ہوئے تھے زنجیروں میں');
INSERT INTO "bayan" VALUES(38,39,'38:39','یہ ہماری عطا ہے پس تم چاہو تو لوگوں پر احسان کرو یا اپنے پاس روکے رکھو اس پر کوئی حساب نہیں۔');
INSERT INTO "bayan" VALUES(38,40,'38:40','اور اس کے لیے ہمارے پاس مقام قرب بھی ہے اور بہت عمدہ ٹھکانہ بھی۔');
INSERT INTO "bayan" VALUES(38,41,'38:41','اور ذرا یاد کیجیے ہمارے بندے ایوب ؑ کو جبکہ اس نے پکارا اپنے رب کو کہ مجھے شیطان نے شدید بیماری اور تکلیف میں مبتلا کردیا ہے۔');
INSERT INTO "bayan" VALUES(38,42,'38:42','ذرا اپنا پائوں زمین پر مارو یہ (چشمہ جو جاری ہوا ہے) ٹھنڈا پانی ہے غسل کے لیے ہے اور پینے کے لیے۔');
INSERT INTO "bayan" VALUES(38,43,'38:43','اور ہم نے اسے اس کے اہل و عیال بھی عطا کردیے اور ان کے ساتھ ویسے ہی (اہل و عیال) مزید بھی دیے یہ سب ہماری طرف سے رحمت کا مظہر تھا اور یاددہانی تھی ہوش مند لوگوں کے لیے');
INSERT INTO "bayan" VALUES(38,44,'38:44','اور تم لے لو اپنے ہاتھ میں تنکوں کا ایک ُ مٹھا تو اس سے ایک ضرب لگا دو اور قسم نہ توڑو ! یقینا ہم نے اسے صابر پایا بہت ہی خوب بندہ۔ یقینا وہ (ہماری طرف) بڑا ہی رجوع کرنے والا تھا');
INSERT INTO "bayan" VALUES(38,45,'38:45','اور تذکرہ کیجیے ہمارے بندوں ابراہیم ؑ اسحاق ؑ اور یعقوب ؑ کا جو قوت والے اور بصیرت والے تھے۔');
INSERT INTO "bayan" VALUES(38,46,'38:46','ہم نے ان کو خالص کرلیا تھا ایک خالص شے یعنی آخرت کے گھر کی یاد دہانی کے لیے');
INSERT INTO "bayan" VALUES(38,47,'38:47','اور یقینا وہ ہمارے نزدیک ُ چنے ہوئے اور نیک لوگوں میں سے ہیں۔');
INSERT INTO "bayan" VALUES(38,48,'38:48','اور ذکرکیجیے اسماعیل ؑ الیسع ؑ اور ذوالکفل ؑ کا یہ سب بھی بہت عمدہ لوگوں میں سے تھے۔');
INSERT INTO "bayan" VALUES(38,49,'38:49','یہ ایک ذکر ہے اور یقینا متقین کے لیے بہت ہی اچھی لوٹنے کی جگہ ہے');
INSERT INTO "bayan" VALUES(38,50,'38:50','(یعنی) رہنے والے باغات جن کے دروازے ان کے لیے کھلے رکھے جائیں گے۔');
INSERT INTO "bayan" VALUES(38,51,'38:51','اس میں وہ تکیے لگا کر بیٹھے ہوں گے طلب کریں گے اس میں میوے بہت سے اور مشروبات بھی');
INSERT INTO "bayan" VALUES(38,52,'38:52','اور ان کے پاس ہوں گی (ان کی بیویاں) نیچی نگاہوں والی ان کی ہم عمر۔');
INSERT INTO "bayan" VALUES(38,53,'38:53','یہ کچھ ہے جس کا تم سے وعدہ کیا جا رہا ہے حساب کے دن کے لیے !');
INSERT INTO "bayan" VALUES(38,54,'38:54','یہ ہیں ہماری عطا کی ہوئی چیزیں جو کبھی ختم نہیں ہوں گی۔');
INSERT INTO "bayan" VALUES(38,55,'38:55','(متقین کے لیے تو) یہ ہے ! اور یقینا سرکشوں کے لیے لوٹنے کی بہت بری جگہ ہے۔');
INSERT INTO "bayan" VALUES(38,56,'38:56','(یعنی) جہنم جس میں وہ داخل ہوں گے پس وہ بہت ہی برا بچھونا ہوگا');
INSERT INTO "bayan" VALUES(38,57,'38:57','یہ ہے (ان کے لیے) پس اس کو چکھیں کھولتا ہوا پانی اور پیپ !');
INSERT INTO "bayan" VALUES(38,58,'38:58','اور اسی طرح کی کئی دوسری چیزیں بھی۔');
INSERT INTO "bayan" VALUES(38,59,'38:59','یہ ایک فوج ہے جو تمہارے ساتھ دھنسی چلی آرہی ہے ان کے لیے کوئی کشادگی نہ ہو ! یہ ہیں آگ میں گھسے آنے والے');
INSERT INTO "bayan" VALUES(38,60,'38:60','وہ (نئے آنے والے) کہیں گے کہ نہیں بلکہ تم ایسے ہو ! تمہارے لیے نہ ہو کوئی کشادگی ! تم نے ہی اسے ہمارے لیے پہلے بھیجا ہے تو یہ بہت ہی بری جگہ ہے ٹھہرنے کی۔');
INSERT INTO "bayan" VALUES(38,61,'38:61','وہ کہیں گے : پروردگار ! جس نے یہ سب کچھ ہمارے لیے آگے بھیجا ہے تو اس کے لیے عذاب کو آگ میں دوگنا بڑھا دے');
INSERT INTO "bayan" VALUES(38,62,'38:62','اور وہ کہیں گے : کیا بات ہے ہم یہاں ان کو نہیں دیکھ رہے جنہیں ہم (دنیا میں) برے لوگوں میں شمار کرتے تھے ؟');
INSERT INTO "bayan" VALUES(38,63,'38:63','کیا ہم نے انہیں خواہ مخواہ ہنسی مذاق بنا لیا تھا یا ہماری نگاہیں ان سے چوک رہی ہیں ؟');
INSERT INTO "bayan" VALUES(38,64,'38:64','یقینا یہ حق ہے اہل جہنم کے مابین جھگڑا !');
INSERT INTO "bayan" VALUES(38,65,'38:65','(اے نبی صلى الله عليه وسلم !) کہہ دیجیے کہ میں تو بس ایک خبردار کرنے والا ہوں اور نہیں ہے کوئی معبود سوائے اللہ کے وہ اکیلا ہے اور ہرچیز پر پوری طرح چھایا ہوا ہے');
INSERT INTO "bayan" VALUES(38,66,'38:66','وہ رب ہے آسمانوں اور زمین کا اور جو کچھ ان کے مابین ہے اور وہ بہت زبردست بہت بخشنے والا ہے۔');
INSERT INTO "bayan" VALUES(38,67,'38:67','آپ کہہ دیجیے کہ یہ ایک بہت بڑی خبر ہے۔');
INSERT INTO "bayan" VALUES(38,68,'38:68','تم اس سے اعراض کر رہے ہو۔');
INSERT INTO "bayan" VALUES(38,69,'38:69','مجھے کچھ علم نہیں ملااعلیٰ کے بارے میں جب وہ باہم بحث و تکرار کر رہے ہوتے ہیں۔');
INSERT INTO "bayan" VALUES(38,70,'38:70','میری طرف تو بس یہی وحی کی جاتی ہے کہ میں صرف واضح طور پر خبردار کردینے والا ہوں');
INSERT INTO "bayan" VALUES(38,71,'38:71','جب کہا تھا آپ صلى الله عليه وسلم کے رب نے فرشتوں سے کہ میں بنانے والا ہوں ایک بشر کو مٹی سے۔');
INSERT INTO "bayan" VALUES(38,72,'38:72','تو جب میں اس کو پوری طرح درست کر دوں اور میں اس میں اپنی روح میں سے پھونک دوں تو تم گرپڑنا اس کے سامنے سجدے میں۔');
INSERT INTO "bayan" VALUES(38,73,'38:73','تو سجدہ کیا سب کے سب فرشتوں نے اکٹھے ہو کر۔');
INSERT INTO "bayan" VALUES(38,74,'38:74','سوائے ابلیس کے اس نے تکبر کیا اور وہ کافروں میں سے ہوگیا۔');
INSERT INTO "bayan" VALUES(38,75,'38:75','اللہ نے فرمایا : اے ابلیس ! تجھے کس چیز نے روکا کہ تو اس کو سجدہ کرتا جسے میں نے اپنے دونوں ہاتھوں سے بنایا ہے ؟ کیا تو نے تکبر کیا یا تو عالی مرتبہ لوگوں میں سے ہے ؟');
INSERT INTO "bayan" VALUES(38,76,'38:76','اس نے کہا : میں اس سے بہتر ہوں ! مجھے بنایا تو نے آگ سے اور اس کو بنایا مٹی سے !');
INSERT INTO "bayan" VALUES(38,77,'38:77','اللہ نے فرمایا : نکل جائو یہاں سے ! اب تم مردود ہوگئے ہو۔');
INSERT INTO "bayan" VALUES(38,78,'38:78','اور اب تم پر میری لعنت رہے گی جزا اور سزا کے دن تک');
INSERT INTO "bayan" VALUES(38,79,'38:79','اس نے کہا : پروردگار ! پھر مجھے مہلت دے اس دن تک جب یہ لوگ دوبارہ اٹھائے جائیں گے');
INSERT INTO "bayan" VALUES(38,80,'38:80','اللہ نے فرمایا : ٹھیک ہے تمہیں یہ مہلت دے دی گئی۔');
INSERT INTO "bayan" VALUES(38,81,'38:81','اس دن تک جس کا وقت طے شدہ ہے۔');
INSERT INTO "bayan" VALUES(38,82,'38:82','اس نے کہا : پس تیری عزت کی قسم ! میں ان سب کو گمراہ کر کے رہوں گا۔');
INSERT INTO "bayan" VALUES(38,83,'38:83','سوائے ان میں سے تیرے ان بندوں کے جنہیں تو نے خالص کرلیا ہو۔');
INSERT INTO "bayan" VALUES(38,84,'38:84','اللہ نے فرمایا : تو حق یہ ہے اور میں تو حق ہی کہتا ہوں۔');
INSERT INTO "bayan" VALUES(38,85,'38:85','کہ پھر میں بھی بھر کر رہوں گا جہنم کو تجھ سے اور ان سب سے جو تیری پیروی کریں گے');
INSERT INTO "bayan" VALUES(38,86,'38:86','(اے نبی صلى الله عليه وسلم !) آپ کہیے کہ میں تم سے اس پر کوئی اجر نہیں مانگتا اور میں کوئی بناوٹ کرنے والوں میں سے بھی نہیں ہوں۔');
INSERT INTO "bayan" VALUES(38,87,'38:87','نہیں ہے یہ مگر ایک یاد دہانی اور نصیحت تمام جہان والوں کے لیے۔');
INSERT INTO "bayan" VALUES(38,88,'38:88','اور تم ضرور جان لوگے اس کی اصل خبر ایک وقت کے بعد۔');
INSERT INTO "bayan" VALUES(39,1,'39:1','اس کتاب کا نازل کیا جانا ہے اس اللہ کی طرف سے جو زبردست کمال حکمت والا ہے');
INSERT INTO "bayan" VALUES(39,2,'39:2','(اے نبی صلى الله عليه وسلم !) ہم نے آپ پر یہ کتاب اتاری ہے حق کے ساتھ پس بندگی کرو اللہ کی اپنی اطاعت کو اس کے لیے خالص کرتے ہوئے');
INSERT INTO "bayan" VALUES(39,3,'39:3','آگاہ ہو جائو کہ اطاعت ِخالص اللہ ہی کا حق ہے۔ اور جن لوگوں نے اللہ کے سوا کچھ اور کو اولیاء بنا یا ہوا ہے (وہ کہتے ہیں کہ) ہم تو ان کو صرف اس لیے پوجتے ہیں کہ یہ ہمیں اللہ سے قریب کردیں یقینا اللہ فیصلہ کر دے گا ان کے مابین ان تمام چیزوں میں جن میں یہ اختلاف کر رہے ہیں اللہ ہرگز ہدایت نہیں دیتا جھوٹے اور نا شکرے لوگوں کو۔');
INSERT INTO "bayan" VALUES(39,4,'39:4','اگر اللہ کا واقعی ارادہ ہوتا کہ کسی کو اولاد بنائے تو وہ چن لیتا اپنی مخلوق میں سے جسے چاہتا وہ پاک ہے۔ وہ اللہ ہے اکیلا اور (کل کائنات پر) چھایا ہوا');
INSERT INTO "bayan" VALUES(39,5,'39:5','اس نے پیدا کیا ہے آسمانوں اور زمین کو حق کے ساتھ وہ رات کو لپیٹ دیتا ہے دن پر اور دن کو لپیٹ دیتا ہے رات پر اور اس نے مسخر کردیا سورج اور چاند کو۔ یہ سب کے سب چل رہے ہیں ایک وقت معین تک کے لیے۔ آگاہ ہو جائو ! وہ زبردست ہے بہت بخشنے والا');
INSERT INTO "bayan" VALUES(39,6,'39:6','اس نے پیدا کیا تمہیں ایک جان سے پھر اسی جان سے اس کا جوڑا بنایا اور تمہارے لیے مویشیوں میں سے آٹھ جوڑے اتار دیے اور تمہارے لیے مویشیوں میں سے آٹھ جوڑے اتار دیے وہ پیدا کرتا ہے تمہیں تمہارے مائوں کے پیٹوں میں ایک خلق کے بعد دوسری خلق (یہ تخلیق ہوتی ہے) تین اندھیروں کے اندر وہ ہے اللہ تمہارا رب اسی کی بادشاہی ہے اس کے سوا کوئی معبود نہیں تو تم لوگ کہاں سے پھرائے جا رہے ہو !');
INSERT INTO "bayan" VALUES(39,7,'39:7','اگر تم کفر کرتے ہو تو یقینا اللہ تم سے بےنیاز ہے لیکن وہ اپنے بندوں کے لیے کفر پر راضی نہیں ہے لیکن وہ اپنے بندوں کے لیے کفر پر راضی نہیں ہے اور اگر تم شکر کرو تو وہ تم سے راضی ہوگا اور نہیں اٹھائے گی بوجھ اٹھانے والی کوئی جان کسی دوسرے کے بوجھ کو پھر تمہارا لوٹنا ہے تمہارے رب ہی کی طرف پھر وہ تمہیں بتادے گا جو کچھ کہ تم کرتے رہے تھے یقینا وہ جاننے والا ہے سینوں کے اندر چھپی ہوئی باتوں کا۔');
INSERT INTO "bayan" VALUES(39,8,'39:8','اور جب انسان کو کوئی تکلیف پہنچتی ہے تو وہ اپنے رب کو پکارتا ہے اس کی طرف رجوع کرتے ہوئے پھر جب وہ عطا کردیتا ہے اسے کوئی نعمت اپنی طرف سے تو وہ جس چیز کے لیے پہلے اس کے حضور دعائیں کر رہا تھا سب بھول جاتا ہے اور وہ اللہ کے ّمد ِمقابل ٹھہرا لیتا ہے تاکہ بہکائے لوگوں کو اس کے راستے سے اے نبی صلى الله عليه وسلم !) آپ کہہ دیجیے کہ مزے اڑا لو اپنے کفر کے ساتھ تھوڑی دیر یقینا تم آگ والوں میں سے ہو');
INSERT INTO "bayan" VALUES(39,9,'39:9','بھلا وہ شخص جو بندگی کرنے والا ہے رات کی گھڑیوں میں سجود و قیام کرتے ہوئے وہ آخرت سے ڈرتا رہتا ہے اور اپنے رب کی رحمت کا امیدوار بھی ہے (اے نبی صلى الله عليه وسلم !) آپ کہہ دیجیے کہ کیا برابر ہوسکتے ہیں وہ لوگ جو علم رکھتے ہیں اور وہ جو علم نہیں رکھتے ؟ حقیقی نصیحت اور سبق تو وہی لوگ حاصل کرتے ہیں جو عقل مند ہیں۔');
INSERT INTO "bayan" VALUES(39,10,'39:10','(اے نبی صلى الله عليه وسلم !) آپ کہیے کہ اے میرے وہ بندو جو ایمان لائے ہو ! اپنے رب کا تقویٰ اختیار کرو جو لوگ احسان کی روش اختیار کریں گے ان کے لیے اس دنیا میں بھی بھلائی ہے اور اللہ کی زمین بہت وسیع ہے یقینا صبر کرنے والوں کو ان کا اجر پورا پورا دیا جائے گا بغیر حساب کے');
INSERT INTO "bayan" VALUES(39,11,'39:11','(اے نبی صلى الله عليه وسلم !) آپ کہہ دیجیے مجھے تو حکم ہوا ہے کہ میں بندگی کروں اللہ کی اس کے لیے اپنی اطاعت کو خالص کرتے ہوئے');
INSERT INTO "bayan" VALUES(39,12,'39:12','اور مجھے حکم ہوا ہے کہ سب سے پہلا فرماں بردار میں خود بنوں۔');
INSERT INTO "bayan" VALUES(39,13,'39:13','کہہ دیجیے کہ مجھے خود اندیشہ ہے ایک بڑے دن کے عذاب کا اگر میں اپنے رب کی نافرمانی کروں');
INSERT INTO "bayan" VALUES(39,14,'39:14','کہہ دیجیے کہ میں تو اللہ ہی کی بندگی کرتا ہوں اس کے لیے اپنی اطاعت کو خالص کرتے ہوئے');
INSERT INTO "bayan" VALUES(39,15,'39:15','تو تم جس کو چاہو پوجو اس کے سوا ! آپ صلى الله عليه وسلم کہہ دیجئے کہ اصل میں خسارے میں رہنے والے وہی لوگ ہوں گے جنہوں نے اپنے آپ کو اور اپنے اہل و عیال کو خسارے میں ڈالا قیامت کے دن آگاہ ہو جائو یہی تو کھلا خسارا ہے');
INSERT INTO "bayan" VALUES(39,16,'39:16','ان کے لیے ان کے اوپر سے بھی آگ کے بادل ہوں گے تہہ بر تہہ اور نیچے سے بھی آگ کے بھب کے ہوں گے تہہ بر تہہ۔ اے میرے بندو ! پس تم مجھ سے ڈرو۔');
INSERT INTO "bayan" VALUES(39,17,'39:17','اور وہ لوگ جنہوں نے طاغوت سے کنارہ کشی کرلی (اس طرح) کہ اس کی بندگی نہ کی اور انہوں نے رجوع کرلیا اللہ کی طرف ان کے لیے بشارت ہے تو (اے نبی صلى الله عليه وسلم !) میرے ان بندوں کو بشارت دے دیجیے');
INSERT INTO "bayan" VALUES(39,18,'39:18','جو بات کو توجہ سے سنتے ہیں پھر اس کے بہترین پہلو کی پیروی کرتے ہیں یہی وہ لوگ ہیں جنہیں اللہ نے ہدایت دی ہے اور یہی وہ لوگ ہیں جو عقل مند ہیں۔');
INSERT INTO "bayan" VALUES(39,19,'39:19','تو کیا وہ شخص جس پر ثابت ہوچکا ہے عذاب کا فیصلہ ! تو (اے نبی صلى الله عليه وسلم !) کیا آپ اس کو بچا سکیں گے جو آگ میں ہے ؟');
INSERT INTO "bayan" VALUES(39,20,'39:20','لیکن وہ لوگ جو اپنے رب کا تقویٰ اختیار کرتے ہیں ان کے لیے ہوں گے بالا خانوں پر بالا خانے (ایک دوسرے کے اوپر) ُ چنے ہوئے۔ ان کے دامن میں ندیاں بہتی ہوں گی یہ اللہ کا وعدہ ہے اللہ اپنے وعدے کی خلاف ورزی نہیں کرتا۔');
INSERT INTO "bayan" VALUES(39,21,'39:21','کیا تم نے نہیں دیکھا کہ اللہ نے اتارا آسمان سے پانی پھر اس کو چلا دیا چشموں کی شکل میں زمین میں پھر اس کے ذریعے سے وہ نکالتا ہے کھیتی جس کے مختلف رنگ ہیں پھر وہ پک کر تیار ہوجاتی ہے پھر تم اسے دیکھتے ہو کہ وہ زرد ہوگئی ہے پھر وہ اسے چورا چورا کردیتا ہے۔ یقینا اس میں یاد دہانی (اور سبق) ہے ہوشمند لوگوں کے لیے۔');
INSERT INTO "bayan" VALUES(39,22,'39:22','بھلا وہ شخص کہ جس کے سینے کو اللہ نے کھول دیا ہے اسلام کے لیے اور وہ اپنے رب کی طرف سے نور پر ہے تو ہلاکت اور بربادی ہے ان لوگوں کے لیے جن کے دل سخت ہوگئے ہیں اللہ کے ذکر سے۔ ! یہ لوگ کھلی گمراہی میں ہیں۔');
INSERT INTO "bayan" VALUES(39,23,'39:23','اللہ نے بہترین کلام نازل کیا ہے کتاب کی صورت میں جس کے مضامین باہم مشابہ ہیں اور بار بار دہرائے گئے ہیں اس (کی تلاوت) سے ان لوگوں کے رونگٹے کھڑے ہوجاتے ہیں جو اپنے رب سے ڈرتے ہیں۔ اس (کی تلاوت) سے ان لوگوں کے رونگٹے کھڑے ہوجاتے ہیں جو اپنے رب سے ڈرتے ہیں۔ پھر ان کی کھالیں اور ان کے دل اللہ کی یاد کے لیے نرم پڑجاتے ہیں یہ اللہ کی ہدایت ہے جس سے وہ ہدایت بخشتا ہے جس کو چاہتا ہے۔ اور جس کو اللہ گمراہ کر دے پھر اس کے لیے کوئی ہدایت دینے والا نہیں۔');
INSERT INTO "bayan" VALUES(39,24,'39:24','بھلا وہ شخص جو اپنے چہرے پر روکے گا بدترین عذاب قیامت کے دن ! اور کہہ دیا جائے گا ان ظالموں سے کہ اب چکھو مزہ اس کا جو کچھ کمائی تم کرتے رہے تھے');
INSERT INTO "bayan" VALUES(39,25,'39:25','(اے نبی صلى الله عليه وسلم !) جھٹلایا تھا انہوں نے بھی جو ان سے پہلے تھے تو ان پر آن دھمکا عذاب وہاں سے جہاں سے انہیں گمان تک نہیں تھا۔');
INSERT INTO "bayan" VALUES(39,26,'39:26','تو اللہ نے انہیں دنیا کی زندگی میں رسوائی کا مزہ چکھا دیا اور آخرت کا عذاب تو بہت بڑا ہے۔ کاش کہ انہیں معلوم ہوتا !');
INSERT INTO "bayan" VALUES(39,27,'39:27','اور ہم نے انسانوں کے لیے اس قرآن میں ہر قسم کی مثالیں بیان کردی ہیں شاید کہ وہ سبق حاصل کریں۔');
INSERT INTO "bayan" VALUES(39,28,'39:28','(ہم نے اسے بنا دیا ہے) قرآن عربی اس میں کوئی کجی نہیں ہے شاید کہ وہ تقویٰ کی روش اختیار کریں۔');
INSERT INTO "bayan" VALUES(39,29,'39:29','اللہ نے مثال بیان فرمائی ہے ایک شخص کی جس میں بہت سے آپس میں ضد رکھنے والے آقا شریک ہیں اور ایک وہ شخص ہے جو پورے طور پر ایک ہی آقا کا غلام ہے کیا یہ دونوں برابر ہوسکتے ہیں مثال میں ؟ کل ُ تعریف اللہ کے لیے ہے لیکن ان کی اکثریت علم نہیں رکھتی');
INSERT INTO "bayan" VALUES(39,30,'39:30','(اے نبی صلى الله عليه وسلم !) یقینا موت آپ پر بھی وارد ہوگی اور یہ سب بھی مرنے والے ہیں');
INSERT INTO "bayan" VALUES(39,31,'39:31','پھر تم قیامت کے دن اپنے رب کے پاس آپس میں جھگڑو گے');
INSERT INTO "bayan" VALUES(39,32,'39:32','اس شخص سے بڑھ کر ظالم اور کون ہوگا جو اللہ پر جھوٹ باندھے اور جھٹلائے سچی بات کو جبکہ اس کے پاس آگئی ہو ! تو کیا جہنم ہی میں ٹھکانہ نہیں ہے ایسے کافروں کا !');
INSERT INTO "bayan" VALUES(39,33,'39:33','اور وہ شخص جو سچائی لے کر آیا اور وہ جس نے اس کی تصدیق کی یہی لوگ ہیں کہ جو متقی ہیں۔');
INSERT INTO "bayan" VALUES(39,34,'39:34','ان کے لیے اپنے رب کے پاس وہ سب کچھ ہے جو یہ چاہیں گے۔ یہی بدلہ ہے نیکوکاروں کا !');
INSERT INTO "bayan" VALUES(39,35,'39:35','ا کہ اللہ ان سے دور کر دے ان کے ُ برے اعمال کو اور ان کو اجر عطا فرمائے ان کے بہترین اعمال کے حساب سے');
INSERT INTO "bayan" VALUES(39,36,'39:36','کیا اللہ کافی نہیں ہے اپنے بندے کے لیے ؟ اور (اے نبی صلى الله عليه وسلم !) یہ لوگ آپ کو ڈراتے ہیں ان سے جو اس کے علاوہ (ان کے معبود) ہیں۔ اور جس کو اللہ گمراہ کر دے تو اس کے لیے کوئی ہدایت دینے والا نہیں ہے۔');
INSERT INTO "bayan" VALUES(39,37,'39:37','اور جس کو اللہ ہدایت دے دے توا سے گمراہ کرنے والا کوئی نہیں تو کیا اللہ زبردست انتقام لینے والا نہیں ہے ؟');
INSERT INTO "bayan" VALUES(39,38,'39:38','اور (اے نبی صلى الله عليه وسلم !) اگر آپ ان سے پوچھیں کہ کس نے پیدا کیا آسمانوں اور زمین کو تو وہ یقینا یہی کہیں گے کہ اللہ نے ! تو ان سے کہیے کہ ذرا غور کرو ! جن کو تم پکارتے ہو اللہ کے سوا اگر اللہ نے میرے لیے کسی تکلیف کا فیصلہ کرلیا ہے تو کیا وہ (تمہارے معبود) اس تکلیف کو دور کرسکیں گے ؟ یا اگر اس نے میرے لیے رحمت کا کوئی فیصلہ کرلیا ہے تو کیا وہ اس کی رحمت کو روک سکتے ہیں ؟ آپ کہہ دیجیے کہ میرے لیے تو اللہ ہی کافی ہے ! اور اسی پر توکل ّکرتے ہیں توکل کرنے والے');
INSERT INTO "bayan" VALUES(39,39,'39:39','آپ کہیے کہ اے میری قوم کے لوگو ! تم اپنی جگہ پر جو کرسکتے ہو کرلو میں بھی اپنی سی محنت کر رہا ہوں پس عنقریب تمہیں معلوم ہوجائے گا');
INSERT INTO "bayan" VALUES(39,40,'39:40','کہ کس پر آتا ہے وہ عذاب جو اسے رسوا کر دے اور اس پر وہ عذاب نازل ہوجائے جو ہمیشہ رہنے والا ہو !');
INSERT INTO "bayan" VALUES(39,41,'39:41','(اے نبی صلى الله عليه وسلم !) ہم نے آپ پر یہ کتاب نازل کردی ہے لوگوں کے لیے حق کے ساتھ تو جو کوئی ہدایت کا راستہ اختیار کرتا ہے وہ اپنے ہی بھلے کے لیے کرتا ہے اور جو کوئی گمراہی اختیار کرتا ہے تو اس کا وبال اسی پر آئے گا اور آپ ان کے ذمہ دار نہیں ہیں');
INSERT INTO "bayan" VALUES(39,42,'39:42','اللہ قبض کرلیتا ہے جانوں کو ان کی موت کے وقت اور جن کی موت نہیں آئی ہوتی ان کو بھی ان کی نیند کی حالت میں تو جس کی موت کا وہ فیصلہ کرچکا ہو اس کو اپنے پاس روکے رکھتا ہے اور دوسروں کو واپس بھیج دیتا ہے ایک وقت ِمعین ّ تک کے لیے یقینا اس میں نشانیاں ہیں ان لوگوں کے لیے جو غور و فکر سے کام لیتے ہیں');
INSERT INTO "bayan" VALUES(39,43,'39:43','کیا انہوں نے اللہ کے سوا کوئی سفارشی بنا رکھے ہیں ؟ (اے نبی صلى الله عليه وسلم !) آپ ان سے کہیے اگرچہ انہیں سرے سے کوئی اختیار ہی نہ ہو اور نہ ہی انہیں کچھ سمجھ ہو (پھر بھی وہ تمہاری شفاعت کریں گے) ؟');
INSERT INTO "bayan" VALUES(39,44,'39:44','آپ صلى الله عليه وسلم کہیے کہ شفاعت ُ کل کی کل اللہ کے اختیار میں ہے آسمانوں اور زمین کی بادشاہی اسی کے لیے ہے۔ پھر تم اسی کی طرف لوٹا دیے جائو گے');
INSERT INTO "bayan" VALUES(39,45,'39:45','اور جب ذکر کیا جاتا ہے اکیلے اللہ کا تو جو لوگ آخرت پر ایمان نہیں رکھتے ان کے دل ُ کڑھنے لگتے ہیں اور جب اس کے سوا دوسروں کا ذکر کیا جاتا ہے تب وہ خوش ہوجاتے ہیں');
INSERT INTO "bayan" VALUES(39,46,'39:46','آپ صلى الله عليه وسلم کہیے : اے اللہ ! آسمانوں اور زمین کے پیدا کرنے والے ! غائب اور حاضر کے جاننے والے ! یقینا تو ُ فیصلہ کرے گا اپنے بندوں کے مابین ان تمام چیزوں میں جن میں وہ اختلاف کرتے رہے ہیں');
INSERT INTO "bayan" VALUES(39,47,'39:47','اور اگر ان ظالموں کے پاس وہ سب کچھ ہوتا جو بھی زمین میں ہے اور اس کے ساتھ اتنا اور بھی ہوتا تو وہ قیامت کے دن ُ برے عذاب سے بچنے کے لیے وہ سب فدیہ میں دے دیتے۔ اور (اُس روز) ان کے سامنے اللہ کی طرف سے وہ کچھ آجائے گا جس کا انہیں گمان بھی نہیں تھا');
INSERT INTO "bayan" VALUES(39,48,'39:48','اور ان کے سامنے آجائیں گے ان کی کمائی کے سارے ُ برے نتائج اور ان کو گھیرے میں لے لے گی وہ چیز جس کا وہ مذاق اڑایا کرتے تھے۔');
INSERT INTO "bayan" VALUES(39,49,'39:49','تو جب انسان کو کوئی تکلیف پہنچتی ہے تو وہ ہمیں پکارتا ہے پھر جب ہم اسے لپیٹ دیتے ہیں اپنی نعمت میں تو وہ کہتا ہے مجھے تو یہ ملا ہے اپنے علم کی بنیاد پر بلکہ یہ تو ایک آزمائش ہے لیکن ان کی اکثریت علم نہیں رکھتی');
INSERT INTO "bayan" VALUES(39,50,'39:50','ان سے پہلوں نے بھی یہی بات کہی تھی تو جو کچھ بھی انہوں نے کمائی کی تھی وہ ان کے کسی کام نہ آسکی');
INSERT INTO "bayan" VALUES(39,51,'39:51','پس انہیں اس کے بدنتائج پہنچ گئے جو وہ کماتے تھے۔ ور ان میں سے بھی جو لوگ ظلم (شرک) کی روش اختیار کریں گے ان کو پہنچ کر رہیں گے ان کے کرتوتوں کے برے نتائج اور وہ (اللہ کو) عاجز کردینے والے نہیں ہیں');
INSERT INTO "bayan" VALUES(39,52,'39:52','کیا یہ جانتے نہیں کہ اللہ ہی ہے جو کشادہ کردیتا ہے رزق کو جس کے لیے چاہتا ہے اور تنگ کردیتا ہے (جس کے لیے چاہتا ہے)۔ یقینا اس میں نشانیاں ہیں ان لوگوں کے لیے جو ماننے والے ہیں');
INSERT INTO "bayan" VALUES(39,53,'39:53','(اے نبی صلى الله عليه وسلم !) آپ کہیے : اے میرے وہ بندو جنہوں نے اپنی جانوں پر زیادتی کی ہے اللہ کی رحمت سے مایوس نہ ہونا یقینا اللہ سارے گناہ معاف فرما دے گا یقینا وہ بہت بخشنے والا نہایت رحم کرنے والا ہے');
INSERT INTO "bayan" VALUES(39,54,'39:54','اور اپنے رب کی طرف رجوع کرو اور اس کے فرمانبردار بن جائو اس سے پہلے کہ تم پر عذاب ّمسلط ہوجائے پھر تمہاری کہیں سے مدد نہیں کی جائے گی');
INSERT INTO "bayan" VALUES(39,55,'39:55','اور پیروی کرو اس کے بہترین پہلو کی جو نازل کیا گیا ہے تمہاری طرف تمہارے رب کی طرف سے اس سے پہلے کہ تم پر عذاب اچانک آدھمکے اور تمہیں اس کا گمان تک نہ ہو');
INSERT INTO "bayan" VALUES(39,56,'39:56','مبادا کہ اس وقت کوئی جان یہ کہے کہ ہائے افسوس اس کوتاہی پر جو مجھ سے اللہ کی جناب میں ہوئی اور میں تو مذاق اڑانے والوں ہی میں شامل رہا۔');
INSERT INTO "bayan" VALUES(39,57,'39:57','یا وہ یہ کہے کہ اگر اللہ نے مجھے ہدایت دی ہوتی تو میں بھی متقین میں سے ہوجاتا !');
INSERT INTO "bayan" VALUES(39,58,'39:58','یا جب وہ عذاب کو دیکھے تو یوں کہے کہ اگر مجھے ایک بار لوٹنا نصیب ہوجائے تو میں محسنین میں سے ہو جائوں');
INSERT INTO "bayan" VALUES(39,59,'39:59','کیوں نہیں ! تیرے پاس میری آیات آئی تھیں تو تو ُ نے ان کو جھٹلا دیا تھا اور تکبر کیا تھا اور تو کافروں میں سے تھا');
INSERT INTO "bayan" VALUES(39,60,'39:60','اور قیامت کے دن تم دیکھو گے ان لوگوں کو جنہوں نے اللہ پر جھوٹ باندھا تھا کہ ان کے چہرے سیاہ ہوں گے تو کیا جہنم ہی میں ٹھکانہ نہیں ہے ایسے متکبرین کا ؟');
INSERT INTO "bayan" VALUES(39,61,'39:61','اور اللہ نجات دے گا ان لوگوں کو جنہوں نے تقویٰ کی روش اختیار کی تھی (اور انہیں پہنچا دے گا) ان کی کامیابی کی جگہوں پر نہ ان کو کوئی تکلیف چھو سکے گی اور نہ ہی وہ غمگین ہوں گے');
INSERT INTO "bayan" VALUES(39,62,'39:62','اللہ ہی ہر شے کا خالق ہے اور وہی ہرچیز پر نگہبان ہے');
INSERT INTO "bayan" VALUES(39,63,'39:63','اسی کے لیے ہیں تمام کنجیاں آسمانوں کی اور زمین کی۔ اور جن لوگوں نے اللہ کی آیات کا انکار کیا وہی ہیں گھاٹے میں رہنے والے');
INSERT INTO "bayan" VALUES(39,64,'39:64','(اے نبی صلى الله عليه وسلم !) آپ کہہ دیجئے کہ اے جاہلو ! کیا تم مجھے بھی یہ مشورہ دے رہے ہو کہ میں اللہ کے سوا کسی اور کی بندگی کروں ؟');
INSERT INTO "bayan" VALUES(39,65,'39:65','اور (اے نبی صلى الله عليه وسلم !) آپ کی طرف تو وحی کی جا چکی ہے اور جو (رسول) آپ سے پہلے تھے ان کی طرف بھی (وحی کردی گئی تھی) اگر آپ بھی (بالفرض) شرک کریں گے تو آپ کے سارے اعمال بھی ضائع ہوجائیں گے اور آپ بھی نہایت خسارہ پانے والوں میں سے ہوجائیں گے۔');
INSERT INTO "bayan" VALUES(39,66,'39:66','بلکہ آپ صلى الله عليه وسلم صرف اللہ ہی کی بندگی کیجیے اور بن جائیے شکر گزار بندوں میں سے !');
INSERT INTO "bayan" VALUES(39,67,'39:67','اور انہوں نے اللہ کی قدر نہیں کی جیسے کہ اس کی قدر کا حق تھا اور زمین پوری کی پوری اس کی مٹھی میں ہوگی قیامت کے دن اور آسمان اس کے داہنے ہاتھ میں لپٹے ہوئے ہوں گے وہ پاک ہے اور بہت بلند وبالا اس تمام شرک سے جو یہ لوگ کر رہے ہیں');
INSERT INTO "bayan" VALUES(39,68,'39:68','اور صور میں پھونکا جائے گا تو بےہوش ہوجائیں گے جو کوئی بھی ہیں آسمانوں میں اور زمین میں سوائے ان کے جنہیں اللہ چاہے گا پھر اس (صور) میں دوبارہ پھونکا جائے گا تو اچانک وہ سب کے سب کھڑے ہوجائیں گے دیکھتے ہوئے');
INSERT INTO "bayan" VALUES(39,69,'39:69','اور زمین جگمگا اٹھے گی اپنے رب کے نور سے اور اعمال نامہ لا کر رکھ دیا جائے گا اور لائے جائیں گے انبیاء اور شہید اور ان کے مابین فیصلہ کردیا جائے گا حق کے ساتھ اور ان پر کوئی ظلم نہیں کیا جائے گا');
INSERT INTO "bayan" VALUES(39,70,'39:70','اور پورا پورا دے دیا جائے گا ہر جان کو جو کچھ کہ اس نے عمل کیا ہوگا اور اللہ خوب جانتا ہے جو عمل یہ لوگ کر رہے ہیں');
INSERT INTO "bayan" VALUES(39,71,'39:71','اور ہانک کرلے جائے جائیں گے کافر جہنم کی طرف گروہ در گروہ یہاں تک کہ جب وہ پہنچ جائیں گے جہنم پر تو اس کے دروازے کھولے جائیں گے اور اس کے داروغہ ان سے کہیں گے کیا تمہارے پاس تم ہی میں سے رسول ؑ نہیں آئے تھے جو تمہیں سناتے تھے تمہارے رب کی آیات اور تمہیں خبردار کرتے تھے تمہاری آج کے دن کی اس ملاقات سے وہ کہیں گے کیوں نہیں ! لیکن کافروں پر عذاب کا حکم ثابت ہو کررہا کہہ دیا جائے گا');
INSERT INTO "bayan" VALUES(39,72,'39:72','ان سے کہ داخل ہو جائو جہنم کے دروازوں میں اس میں ہمیشہ رہنے کے لیے۔ تو بہت برا ہے یہ ٹھکانہ متکبرین کا');
INSERT INTO "bayan" VALUES(39,73,'39:73','اور لے جایا جائے گا ان لوگوں کو جنت کی طرف جو اپنے رب کا تقویٰ اختیار کیے رہے تھے گروہ در گروہ یہاں تک کہ جب وہ وہاں پہنچیں گے اور اس کے دروازے (پہلے ہی) کھولے جا چکے ہوں گے اور اس کے داروغہ ان سے کہیں گے : آپ پر سلام ہو آپ لوگ کتنے پاکباز ہیں اب داخل ہوجائیے اس میں ہمیشہ ہمیش رہنے کے لیے');
INSERT INTO "bayan" VALUES(39,74,'39:74','اور وہ کہیں گے کہ ُ کل حمد اور کل شکر اس اللہ کے لیے ہے جس نے ہم سے اپنا وعدہ پورا کردیا اور ہمیں اس زمین کا وارث بنا دیا کہ اب ہم گھر بنا لیں جنت میں جہاں چاہیں و بہت ہی اچھا ہوگا اجر عمل کرنے والوں کا !');
INSERT INTO "bayan" VALUES(39,75,'39:75','اور تم دیکھو گے فرشتوں کو کہ وہ عرش الٰہی کو گھیرے ہوئے ہوں گے اپنے ربّ کی تسبیح بیان کر رہے ہوں گے اس کی حمد کے ساتھ ور ان کے مابین حق کے ساتھ فیصلہ کردیا جائے گا اور پکارا جائے گا کہ ُ کل کی کل حمد اللہ کے لیے ہے جو تمام جہانوں کا رب ہے');
INSERT INTO "bayan" VALUES(40,1,'40:1','ح م۔');
INSERT INTO "bayan" VALUES(40,2,'40:2','نازل کیا جانا ہے اس کتاب کا اس اللہ کی جانب سے جو زبردست ہے صاحب علم ہے');
INSERT INTO "bayan" VALUES(40,3,'40:3','(وہ اللہ کہ جو) بخشنے والا ہے گناہ کا قبول کرنے والا ہے توبہ کا سزا دینے میں بہت سخت ہے وہ بہت طاقت اور قدرت والا ہے۔ اس کے علاوہ کوئی معبود نہیں اور اسی کی طرف لوٹ کر جانا ہے');
INSERT INTO "bayan" VALUES(40,4,'40:4','نہیں جھگڑتے اللہ کی آیات میں مگر وہی لوگ جو کفر کرتے ہیں تو (اے نبی صلى الله عليه وسلم !) زمین میں ان کی چلت پھرت آپ کو دھوکے میں نہ ڈالے۔');
INSERT INTO "bayan" VALUES(40,5,'40:5','ان سے پہلے نوح ؑ کی قوم نے جھٹلایا تھا اور ان کے بعد بہت سی دوسری قوموں نے بھی اور ہر قوم نے اپنے رسول کے متعلق ارادہ کیا کہ اسے پکڑ لے (اور قتل کر دے اور وہ باطل (دلیلوں) کے ساتھ جھگڑتے رہے تاکہ اس کے ذریعے حق کو پسپا کردیں بالآخر میں نے انہیں پکڑ لیا تو کیسی رہی میری سزا ؟)');
INSERT INTO "bayan" VALUES(40,6,'40:6','اور اسی طرح ان کافروں پر بھی آپ کے رب کی بات پوری ہوچکی ہے کہ یہ سب کے سب جہنمی ہیں');
INSERT INTO "bayan" VALUES(40,7,'40:7','وہ (فرشتے) جو عرش کو اٹھائے ہوئے ہیں اور وہ جو ان کے ارد گرد ہیں وہ سب تسبیح کر رہے ہوتے ہیں اپنے رب کی حمد کے ساتھ اور اس پر پورا یقین رکھتے ہیں اور اہل ایمان کے لیے استغفار کرتے ہیں۔ اے ہمارے پروردگار ! تیری رحمت اور تیرا علم ہرچیز کا احاطہ کیے ہوئے ہے پس بخش دے ُ تو ان لوگوں کو جنہوں نے توبہ کی اور تیرے راستے کی پیروی کی اور ان کو جہنم کے عذاب سے بچا لے۔');
INSERT INTO "bayan" VALUES(40,8,'40:8','پروردگار ! اور انہیں داخل فرما ناان رہنے والے باغات میں جن کا ُ تو نے ان سے وعدہ کیا ہے اور (ان کو بھی) جو نیک ہوں ان کے آباء و اَجداد ان کی بیویوں اور ان کی اولاد میں سے۔ تو ُ یقینا زبردست ہے کمالِ حکمت والا ہے');
INSERT INTO "bayan" VALUES(40,9,'40:9','اور انہیں بچا لے برائیوں سے۔ اور جسے تو ُ نے اس دن برائیوں سے بچالیا اس پر تو نے بڑا رحم کیا اور یقینا یہی بہت بڑی کامیابی ہے');
INSERT INTO "bayan" VALUES(40,10,'40:10','جن لوگوں نے کفر کیا تھا انہیں پکارا جائے گا کہ آج تم جس قدر اپنی جانوں سے بےزار ہوگئے ہو اللہ کی بیزاری تم سے اس سے کہیں بڑھ کر تھی جب تمہیں ایمان کی دعوت دی جاتی تھی اور تم کفر کرتے تھے');
INSERT INTO "bayan" VALUES(40,11,'40:11','وہ فریاد کریں گے : اے ہمارے رب ! تو نے ہمیں دو دفعہ مارا اور دو دفعہ زندہ کیا تو اب ہم نے اپنے گناہوں کا اعتراف کرلیا ہے تو کیا اب یہاں سے نکلنے کی بھی کوئی راہ ہے ؟');
INSERT INTO "bayan" VALUES(40,12,'40:12','یہ اس لیے ہے کہ جب اکیلے اللہ کو پکارا جاتا تھا تو تم انکار کرتے تھے۔ اور اگر اس کے ساتھ شرک کیا جاتا تو تم مان لیتے تو اب ُ کل اختیار اللہ ہی کے ہاتھ میں ہے جو بہت بلند بہت عظمت والا ہے');
INSERT INTO "bayan" VALUES(40,13,'40:13','وہی ہے جو تمہیں اپنی نشانیاں دکھاتا ہے اور اتارتا ہے تمہارے لیے آسمان سے روزی۔ اور نہیں نصیحت حاصل کرتا مگر وہی جو رجوع کرتا ہے (اللہ کی طرف');
INSERT INTO "bayan" VALUES(40,14,'40:14','پس تم پکارو اللہ کو اسی کے لیے اطاعت کو خالص کرتے ہوئے خواہ یہ کافروں کو کتنا ہی ناگوار ہو');
INSERT INTO "bayan" VALUES(40,15,'40:15','(وہی اللہ ہے) درجات کو بلند کرنے والا عرش کا مالک۔ یہاں پھر اللہ کی شان کا بیان مرکب اضافی کی شکل میں ہوا ہے وہ القا کرتا ہے روح کو اپنے امر میں سے جس پر چاہتا ہے اپنے بندوں میں سے تاکہ وہ خبردار کر دے (لوگوں کو) ملاقات کے دن سے۔');
INSERT INTO "bayan" VALUES(40,16,'40:16','جس روز یہ سامنے نکل کھڑے ہوں گے ان کی کوئی چیز بھی اللہ سے مخفی نہیں ہوگی کس کے لیے ہے بادشاہی آج کے دن ؟ (جواب ملے گا :) اکیلے اللہ کے لیے ہے جو تمام کائنات پر چھایا ہوا ہے');
INSERT INTO "bayan" VALUES(40,17,'40:17','آج ہر جان کو وہی بدلہ دیا جائے گا جو اس نے کمایا آج کوئی ظلم نہیں ہوگا۔ یقینا اللہ جلد حساب چکا دینے والا ہے');
INSERT INTO "bayan" VALUES(40,18,'40:18','اور (اے نبی صلى الله عليه وسلم !) انہیں خبردار کر دیجئے اس نزدیک آجانے والے دن سے جبکہ دل حلق میں آ پھنسیں گے اور وہ غم کو دبا رہے ہوں گے۔ ان ظالموں کا نہ کوئی دوست ہوگا اور نہ کوئی ایسا سفارشی ہوگا جس کی بات مانی جائے');
INSERT INTO "bayan" VALUES(40,19,'40:19','وہ جانتا ہے نگاہوں کی چوری کو بھی اور جو کچھ سینوں میں چھپا ہے اس کو بھی');
INSERT INTO "bayan" VALUES(40,20,'40:20','اور اللہ فیصلہ کرے گا حق کے ساتھ۔ اور جن کو یہ لوگ اللہ کے سوا پکارتے ہیں وہ کسی چیز کا بھی فیصلہ نہیں کریں گے یقینا اللہ سب کچھ سننے والا سب کچھ دیکھنے والا ہے');
INSERT INTO "bayan" VALUES(40,21,'40:21','کیا یہ زمین میں گھومے پھرے نہیں ہیں کہ دیکھتے کیسا انجام ہوا ان لوگوں کا جو ان سے پہلے تھے ! وہ کہیں زیادہ بڑھ کر تھے ان سے قوت میں اور زمین میں آثار کے حوالے سے بھی تو اللہ نے ان کو پکڑا ان کے گناہوں کی پاداش میں۔ اور پھر کوئی نہ ہوا انہیں اللہ سے بچانے والا۔');
INSERT INTO "bayan" VALUES(40,22,'40:22','یہ اس لیے ہوا کہ ان کے پاس آتے رہے ان کے رسول بہت واضح نشانیوں کے ساتھ لیکن انہوں نے کفر کیا تو اللہ نے انہیں پکڑ لیا یقینا وہ بہت طاقتور سزا دینے میں بہت سخت ہے');
INSERT INTO "bayan" VALUES(40,23,'40:23','اور ہم نے بھیجا تھا موسیٰ ؑ ٰ کو اپنی نشانیوں اور کھلی سند کے ساتھ');
INSERT INTO "bayan" VALUES(40,24,'40:24','فرعون ہامان اور قارون کی طرف تو انہوں نے کہا کہ یہ تو ایک جادوگر ہے انتہائی جھوٹا۔');
INSERT INTO "bayan" VALUES(40,25,'40:25','تو جب وہ آیا ان کے پاس ہماری طرف سے حق لے کر تو انہوں نے کہا کہ جو لوگ ان کے ساتھ ایمان لائیں ان کے بیٹوں کو قتل کر دو اور ان کی بیٹیوں کو زندہ رکھو لیکن کافروں کا دائو بھٹک کر ہی رہ جاتا ہے۔');
INSERT INTO "bayan" VALUES(40,26,'40:26','اور فرعون نے کہا : مجھے چھوڑو کہ میں موسیٰ ؑ ٰ کو قتل کر دوں اور وہ (بچائو کے لیے) پکارلے اپنے رب کو۔ مجھے اندیشہ ہے کہ وہ تمہارا دین بدل دے گا یا زمین میں فساد برپا کر دے گا');
INSERT INTO "bayan" VALUES(40,27,'40:27','اور موسیٰ ؑ ٰ نے کہا کہ میں پناہ پکڑتا ہوں اپنے اور تمہارے رب کی ہر اس متکبر شخص کے مقابلے میں جو یوم حساب پر ایمان نہیں رکھتا۔');
INSERT INTO "bayan" VALUES(40,28,'40:28','اور آل فرعون میں سے ایک مومن مرد نے جو ابھی تک اپنے ایمان کو چھپائے ہوئے تھا کہا کیا تم قتل کرنا چاہتے ہو ایک شخص کو محض اس لیے کہ وہ کہتا ہے میرا رب اللہ ہے ! حالانکہ وہ تمہارے رب کی طرف سے واضح نشانیاں لے کر آیا ہے اور اگر وہ جھوٹا ہے تو اس کے جھوٹ کا وبال اسی پر آئے گا اور اگر وہ سچا ہوا تو تمہیں وہ بعض باتیں پہنچ کر رہیں گی جن کے بارے میں وہ تمہیں وعید سنا رہا ہے۔ یقینا اللہ تعالیٰ راہ یاب نہیں کرتا اس کو جو حد سے بڑھنے والا بہت جھوٹا ہو');
INSERT INTO "bayan" VALUES(40,29,'40:29','اے میری قوم کے لوگو ! آج تو تمہارے ہاتھ میں حکومت ہے اور تم ہر طرح سے زمین میں غالب ہو۔ لیکن اگر کہیں ہم پر اللہ کا عذاب آگیا تو اس سے بچانے کے لیے ہماری مدد کون کرے گا ؟ فرعون نے کہا : میں تو تمہیں وہی کچھ دکھا رہا ہوں جو مجھے نظر آ رہا ہے اور میں نہیں راہنمائی کر رہا تمہاری مگر کامیابی کے راستے کی طرف۔');
INSERT INTO "bayan" VALUES(40,30,'40:30','اور مرد مومن نے (اپنی تقریر جاری رکھتے ہوئے) کہا : اے میری قوم کے لوگو ! مجھے اندیشہ ہے تم پر ایسے دن کا جیسے دن پہلی قوموں پر آئے تھے');
INSERT INTO "bayan" VALUES(40,31,'40:31','جیسا کہ معاملہ ہوا تھا قوم نوح ؑ کا اور قوم عاد اور قوم ثمود اور ان کے بعد کی قوموں کا اور اللہ تو اپنے بندوں پر کسی ظلم کا ارادہ نہیں رکھتا');
INSERT INTO "bayan" VALUES(40,32,'40:32','اور اے میری قوم کے لوگو ! مجھے اندیشہ ہے تم پر چیخ پکار کے دن کا');
INSERT INTO "bayan" VALUES(40,33,'40:33','جس دن تم پیٹھ موڑ کر بھاگو گے لیکن اللہ کی پکڑ سے تمہیں بچا نے والا کوئی نہیں ہوگا اور جسے اللہ ہی گمراہ کر دے پھر اسے ہدایت دینے والا کوئی نہیں ہوتا');
INSERT INTO "bayan" VALUES(40,34,'40:34','اور (دیکھو !) اس سے پہلے تمہارے پاس یوسف ؑ آئے تھے واضح نشانیاں لے کر لیکن تم شکوک و شبہات میں ہی پڑے رہے ان تعلیمات کے بارے میں جو وہ ؑ تمہارے پاس لے کر آئے تھے یہاں تک کہ جب وہ فوت ہوگئے تو تم نے کہا کہ اب ان کے بعد اللہ کسی اور کو رسول بنا کر نہیں بھیجے گا اسی طرح اللہ گمراہ کردیتا ہے ان لوگوں کو جو حد سے بڑھنے والے اور شکوک و شبہات میں مبتلا رہنے والے ہوں');
INSERT INTO "bayan" VALUES(40,35,'40:35','جو اللہ کی آیات کے بارے میں جھگڑتے ہیں بغیر کسی سند کے جو ان کے پاس آئی ہو بڑی بیزاری کی بات ہے یہ اللہ کے نزدیک بھی اور اہل ِایمان کے نزدیک بھی اسی طرح اللہ مہر لگا دیا کرتا ہے ہر اس شخص کے دل پر جو متکبر ّاور سرکش ہو');
INSERT INTO "bayan" VALUES(40,36,'40:36','اور فرعون نے کہا : اے ہامان ! میرے لیی ایک بلند عمارت بنوائو تاکہ میں پہنچ جائوں راستوں تک');
INSERT INTO "bayan" VALUES(40,37,'40:37','یعنی آسمان کے راستوں تک پھر میں جھانک کر دیکھوں موسیٰ کے الٰہ کو اور میرا گمان تو یہ ہے کہ وہ جھوٹا ہے اور اسے روک دیا گیا سیدھی راہ سے۔ اور فرعون کی یہ چال نہیں تھی مگر تباہ ہونے کے لیے');
INSERT INTO "bayan" VALUES(40,38,'40:38','اور کہا اس مومن نے کہ اے میری قوم کے لوگو ! تم میری پیروی کرو میں تمہاری راہنمائی کروں گا نیکی کے راستے پر۔');
INSERT INTO "bayan" VALUES(40,39,'40:39','اے میری قوم کے لوگو ! یہ دنیا کی زندگی تو بس (چند روزہ) برتنے کا سامان ہے اور مستقل رہنے کی جگہ تو آخرت ہے');
INSERT INTO "bayan" VALUES(40,40,'40:40','جس نے کوئی بدی کمائی ہوگی تو اسے بدلہ ملے گا اسی کے مانند اور جو کوئی نیک عمل کرے گا چاہے مرد ہو یا عورت لیکن ہو وہ مومن تو وہی لوگ جنت ّمیں داخل ہوں گے وہاں انہیں رزق ملے گا بغیر حساب کے');
INSERT INTO "bayan" VALUES(40,41,'40:41','اور اے میری قوم کے لوگو ! مجھے کیا ہے کہ میں تمہیں پکار رہا ہوں نجات کی طرف اور تم مجھے دعوت دے رہے ہو آگ کی !');
INSERT INTO "bayan" VALUES(40,42,'40:42','تم مجھے دعوت دے رہے ہو اس بات کی کہ میں اللہ کا کفر کروں اور شریک ٹھہرائوں اس کے ساتھ جس کا مجھے کوئی علم نہیں اور میں تمہیں بلا رہا ہوں اس ذات کی طرف جو زبردست ہے بخشش کرنے والا ہے');
INSERT INTO "bayan" VALUES(40,43,'40:43','بلاشبہ جن (معبودوں) کی طرف تم مجھے بلا رہے ہو ان کے لیے کوئی دعوت نہ دنیا میں ہے اور نہ آخرت میں اور یہ کہ ہم سب کو لوٹنا تو اللہ ہی کی طرف ہے اور جو حد سے گزرنے والے ہیں وہی جہنمی ہیں۔');
INSERT INTO "bayan" VALUES(40,44,'40:44','تو عنقریب تم یاد کرو گے (یہ باتیں) جو میں تم سے کہہ رہا ہوں اور میں تو اپنا معاملہ اللہ کے حوالے کرتا ہوں اللہ یقینا اپنے بندوں کو دیکھ رہا ہے');
INSERT INTO "bayan" VALUES(40,45,'40:45','و بچالیا اللہ نے اس کو ان کی چالوں کی برائیوں سے اور گھیر لیا آلِ فرعون کو بدترین عذاب نے');
INSERT INTO "bayan" VALUES(40,46,'40:46','آگ ہے جس پر وہ پیش کیے جاتے ہیں صبح وشام۔ اور جس دن قیامت قائم ہوگی (اُس دن کہہ دیا جائے گا کہ) داخل کر دو فرعون کی قوم کو شدید ترین عذاب میں');
INSERT INTO "bayan" VALUES(40,47,'40:47','اور جب وہ آگ میں ایک دوسرے سے جھگڑیں گے تو کمزور لوگ بڑے بننے والوں سے کہیں گے ہم تو تمہاری پیروی کرتے تھے تو کیا تم لوگ ہم سے آگ کے عذاب کا کوئی حصہ کم کروا سکتے ہو ؟');
INSERT INTO "bayan" VALUES(40,48,'40:48','وہ بڑے بننے والے کہیں گے کہ ہم سبھی اس کے اندر پڑے ہوئے ہیں اللہ نے تو اپنے بندوں کے مابین فیصلہ کردیا ہے');
INSERT INTO "bayan" VALUES(40,49,'40:49','اور کہیں گے وہ لوگ جو آگ میں ہوں گے جہنم کے داروغوں (فرشتوں) سے آپ اپنے رب سے دعا کریں کہ وہ ہم سے بس ایک دن ہی عذاب میں تخفیف کر دے۔');
INSERT INTO "bayan" VALUES(40,50,'40:50','وہ جواب میں کہیں گے کہ کیا تمہارے پاس رسول ؑ نہیں آتے رہے تھے واضح نشانیوں (اور واضح تعلیمات) کے ساتھ ؟ وہ کہیں گے : ہاں (آئے تو تھے) ! وہ کہیں گے : تو اب تم خود ہی پکارو ! اور کافروں کی دعا نہیں ہے مگر بھٹک کر رہ جانے والی۔');
INSERT INTO "bayan" VALUES(40,51,'40:51','ہم لازماً مدد کرتے ہیں اپنے رسولوں کی اور ان لوگوں کی جو ایمان لائے دنیا کی زندگی میں بھی اور اس دن بھی (مدد کریں گے) جس دن گواہ کھڑے ہوں گے');
INSERT INTO "bayan" VALUES(40,52,'40:52','جس دن کہ ظالموں کو ان کی معذرت کچھ فائدہ نہ دے گی اور ان کے لیے لعنت ہوگی اور ان کے لیے بہت ُ برا گھرہو گا');
INSERT INTO "bayan" VALUES(40,53,'40:53','اور ہم نے موسیٰ ؑ ٰ کو ہدایت دی تھی اور ہم نے وارث بنایا تھا بنی اسرائیل کو کتاب کا');
INSERT INTO "bayan" VALUES(40,54,'40:54','جو ہدایت اور یاد دہانی تھی ہوشمندوں کے لیے۔');
INSERT INTO "bayan" VALUES(40,55,'40:55','تو (اے نبی صلى الله عليه وسلم !) آپ صبر کیجیے ! یقینا اللہ کا وعدہ سچا ہے اور اپنے قصور کی معافی چاہیں اور اپنے رب کی حمد کے ساتھ اس کی تسبیح کیجیے شام کو بھی اور صبح کو بھی');
INSERT INTO "bayan" VALUES(40,56,'40:56','یقینا وہ لوگ جو اللہ کی آیات میں جھگڑے ڈالتے ہیں بغیر کسی سند کے جو ان کے پاس آئی ہو نہیں ہے ان کے دلوں میں کچھ مگر تکبر جس تک وہ پہنچنے والے نہیں ہیں پس آپ اللہ کی پناہ طلب کیجیے یقینا وہ سب کچھ سننے والا ہرچیز کو دیکھنے والا ہے');
INSERT INTO "bayan" VALUES(40,57,'40:57','آسمانوں اور زمین کی تخلیق یقینا زیادہ بڑا کام ہے انسانوں کی تخلیق سے لیکن اکثر لوگ جانتے نہیں ہیں۔');
INSERT INTO "bayan" VALUES(40,58,'40:58','اور برابر نہیں ہوسکتے اندھے اور آنکھوں والے اور (نہ برابر ہوسکتے ہیں) وہ جو ایمان لائے اور انہوں نے نیک اعمال کیے اور وہ جو بدکار ہیں بہت ہی کم ہے جو کہ تم سبق حاصل کرتے ہو۔');
INSERT INTO "bayan" VALUES(40,59,'40:59','(اور دیکھو !) یقینا قیامت آکر رہے گی اس میں کوئی شک نہیں ہے لیکن اکثر لوگ ماننے والے نہیں ہیں۔');
INSERT INTO "bayan" VALUES(40,60,'40:60','اور تمہارا رب کہتا ہے کہ مجھے پکارو میں تمہاری دعا قبول کروں گا یقینا وہ لوگ جو میری عبادت سے تکبر کی بنا پر اعراض کرتے ہیں وہ داخل ہوں گے جہنم میں ذلیل و خوار ہو کر۔');
INSERT INTO "bayan" VALUES(40,61,'40:61','اللہ ہی ہے جس نے تمہارے لیے رات بنائی ہے تاکہ تم اس میں سکون حاصل کرو اور دن بنا دیا ہے دیکھنے کے لیے۔ یقینا اللہ تو اپنے بندوں پر بہت فضل والا ہے لیکن اکثر لوگ شکر نہیں کرتے۔');
INSERT INTO "bayan" VALUES(40,62,'40:62','وہ ہے اللہ تمہارا رب جو ہرچیز کا خالق ہے۔ اس کے سوا کوئی معبود نہیں تو کہاں سے تم پھرائے جا رہے ہو !');
INSERT INTO "bayan" VALUES(40,63,'40:63','اسی طرح وہ لوگ بھی الٹے پھرائے گئے تھے جو اللہ کی آیات کا انکار کرتے تھے');
INSERT INTO "bayan" VALUES(40,64,'40:64','اللہ ہی ہے جس نے تمہارے لیے بنا دیا زمین کو ٹھہرنے کی جگہ اور آسمان کو چھت اور تمہاری صورت گری کی تو کیا ہی عمدہ تمہاری صورتیں بنائیں اور تمہیں رزق بہم پہنچایا پاکیزہ چیزوں سے۔ تو بہت بابرکت ہے اللہ جو تمام جہانوں کا پروردگار ہے');
INSERT INTO "bayan" VALUES(40,65,'40:65','وہ زندہ وجاوید ہستی ہے اس کے سوا کوئی معبود نہیں پس اسی کو پکارو ! اس کے لیے اطاعت کو خالص کرتے ہوئے کل ُ حمد اللہ ہی کے لیے ہے جو تمام جہانوں کا پروردگار ہے۔');
INSERT INTO "bayan" VALUES(40,66,'40:66','(اے نبی صلى الله عليه وسلم !) آپ کہہ دیجیے : مجھے تو روک دیا گیا ہے کہ میں بندگی کروں ان کی جن کو تم پکار رہے ہو اللہ کے سوا جبکہ میرے پاس آچکی ہیں واضح تعلیمات میرے رب کی طرف سے اور مجھے تو حکم ہوا ہے کہ میں سرتسلیم خم کر دوں تمام جہانوں کے پروردگار کے سامنے');
INSERT INTO "bayan" VALUES(40,67,'40:67','وہی ہے جس نے تمہیں پیدا کیا پہلے مٹی سے پھر نطفے سے پھر علقہ سے پھر وہ تمہیں نکال لیتا ہے ایک بچے کی حیثیت سے پھر (تمہیں پروان چڑھاتا ہے) تاکہ تم پہنچ جائو اپنی جوانی کو پھر (تمہیں مزید عمر دیتا ہے) تاکہ تم ہو جائو بوڑھے اور تم میں سے بعض وہ بھی ہیں جنہیں اس سے پہلے ہی وفات دے دی جاتی ہے اور (بعض کو مہلت دیتا ہے) تاکہ تم ایک وقت معین ّکو پہنچ جائو اور (یہ اس لیے ہے) تاکہ تم نصیحت حاصل کرو');
INSERT INTO "bayan" VALUES(40,68,'40:68','وہی ہے جو زندہ رکھتا ہے اور موت وارد کرتا ہے۔ چناچہ (اُس کے امر کی شان تو یہ ہے کہ) جب وہ کسی کام کا فیصلہ کرلیتا ہے تو کہتا ہے ہوجا تو وہ ہوجاتا ہے');
INSERT INTO "bayan" VALUES(40,69,'40:69','کیا تم نے غور نہیں کیا ان لوگوں کے حال پر جو اللہ کی آیات کے بارے میں جھگڑتے ہیں ! وہ کہاں سے پھرائے جا رہے ہیں ؟');
INSERT INTO "bayan" VALUES(40,70,'40:70','وہ لوگ کہ جنہوں نے جھٹلایا کتاب کو اور ان چیزوں کو جن کے ساتھ ہم نے بھیجا اپنے رسولوں کو تو عنقریب انہیں معلوم ہوجائے گا');
INSERT INTO "bayan" VALUES(40,71,'40:71','جب ان کی گردنوں میں طوق پڑے ہوں گے اور وہ زنجیروں میں جکڑے ہوں گے (اس حالت میں) انہیں گھسیٹا جائے گا');
INSERT INTO "bayan" VALUES(40,72,'40:72','کھولتے پانی میں پھر انہیں آگ میں جھونک دیا جائے گا۔');
INSERT INTO "bayan" VALUES(40,73,'40:73','پھر ان سے کہا جائے گا : کہاں ہیں وہ جنہیں تم شریک بنایا کرتے تھے');
INSERT INTO "bayan" VALUES(40,74,'40:74','اللہ کے سوا ؟ وہ کہیں گے : سب گم ہوگئے ہم سے بلکہ ہم نہیں پکار رہے تھے اس سے پہلے کسی بھی شے کو۔ اسی طرح اللہ بھٹکا دیتا ہے کافروں کو۔');
INSERT INTO "bayan" VALUES(40,75,'40:75','اور یہ سب اس لیے ہوا کہ تم زمین میں ناحق اتراتے تھے اور اس لیے بھی کہ تم اکڑا بھی کرتے تھے۔');
INSERT INTO "bayan" VALUES(40,76,'40:76','اب داخل ہو جائو جہنم کے دروازوں میں اس میں ہمیشہ رہنے کے لیے تو یہ بہت ہی ُ برا ٹھکانہ ہے متکبرین ّکا۔');
INSERT INTO "bayan" VALUES(40,77,'40:77','تو (اے نبی صلى الله عليه وسلم !) آپ صبرکیجیے یقینا اللہ کا وعدہ حق ہے تو اگر ہم آپ صلى الله عليه وسلم کو دکھا دیں اس میں سے کچھ جس کا ہم ان سے وعدہ کر رہے ہیں یا ہم آپ کو وفات دے دیں پھر ان کو ہماری ہی طرف لوٹ کر آنا ہے');
INSERT INTO "bayan" VALUES(40,78,'40:78','اور ہم نے بہت سے رسول بھیجے آپ صلى الله عليه وسلم سے پہلے ان میں سے کچھ وہ بھی ہیں جن کا ذکر ہم نے آپ سے کیا ہے اور کچھ وہ بھی ہیں جن کا ذکر ہم نے آپ سے نہیں کیا اور کسی بھی رسول کے لیے یہ ممکن نہ تھا کہ وہ وہ کوئی نشانی لے آتا مگر اللہ کے اذن سے پھر جب اللہ کا حکم آگیا تو فیصلہ چکا دیا گیا حق کے ساتھ اور اس وقت خسارے میں پڑگئے جھٹلانے والے');
INSERT INTO "bayan" VALUES(40,79,'40:79','اللہ ہی ہے جس نے تمہارے لیے چوپائے بنا دیے تاکہ تم سواری کرو ان میں سے بعض پر اور ان میں سے بعض کا تم گوشت بھی کھاتے ہو');
INSERT INTO "bayan" VALUES(40,80,'40:80','اور ان میں تمہارے لیے اور بھی بہت سی منفعتیں ہیں اور تاکہ ان پر سوار ہو کر تم اپنی وہ ضرورتیں پوری کرو جو تمہارے دل میں ہوتی ہیں اور ان پر بھی اور کشتیوں پر بھی تمہیں سوار کرایا جاتا ہے');
INSERT INTO "bayan" VALUES(40,81,'40:81','اور وہ تمہیں اپنی آیات دکھاتا رہتا ہے تو تم اللہ کی کون کون سی آیات کا انکار کرو گے ؟');
INSERT INTO "bayan" VALUES(40,82,'40:82','تو کیا یہ لوگ زمین میں گھومے پھرے نہیں کہ غور کرتے کہ کیسا انجام ہوا ان کا جو ان سے پہلے تھے ! وہ ان سے کہیں زیادہ تھے تعداد میں اور زیادہ شدید تھے طاقت میں بھی اور زمین میں نشانات چھوڑنے کے اعتبار سے بھی تو ان کے کچھ کام نہ آیا وہ سب کچھ جو وہ کماتے رہے تھے۔');
INSERT INTO "bayan" VALUES(40,83,'40:83','تو جب آگئے ان کے پاس ان کے رسول واضح نشانیاں لے کر تو وہ اتراتے رہے اسی پر جو بھی علم ان کے پاس تھا اور گھیرے میں لے لیا انہیں اسی چیز نے جس کا وہ مذاق اڑاتے تھے');
INSERT INTO "bayan" VALUES(40,84,'40:84','پھر جب انہوں نے دیکھ لیا ہمارا عذاب تب انہوں نے کہا کہ ہم ایمان لائے اللہ واحد پر اور ہم نے انکار کیا ان (معبودوں) کا جنہیں ہم اللہ کے ساتھ شریک ٹھہراتے رہے تھے');
INSERT INTO "bayan" VALUES(40,85,'40:85','تو پھر ان کا ایمان لانا ان کے لیے ہرگز مفید نہیں ہوا جبکہ انہوں نے ہمارا عذاب دیکھ لیا۔ یہ اللہ کا وہ دستور ہے جو اس کے بندوں میں جاری رہا ہے اور اس وقت کافرلوگ خسارے میں رہے');
INSERT INTO "bayan" VALUES(41,1,'41:1','حٰ مٓ');
INSERT INTO "bayan" VALUES(41,2,'41:2','(اس کتاب کا) اتارا جانا ہے اس ہستی کی طرف سے جو بیحد مہربان نہایت رحم کرنے والا ہے');
INSERT INTO "bayan" VALUES(41,3,'41:3','یہ ایک ایسی کتاب ہے جس کی آیات خوب کھول کھول کر بیان کردی گئی ہیں قرآن عربی کی صورت میں ان لوگوں کے لیے جو علم رکھتے ہوں (یا علم رکھنا چاہیں)');
INSERT INTO "bayan" VALUES(41,4,'41:4','بشارت دینے والی اور آگاہ کردینے والی تو ان کی اکثریت نے اعراض کیا اور وہ سنتے ہی نہیں ہیں');
INSERT INTO "bayan" VALUES(41,5,'41:5','اور وہ کہتے ہیں کہ ہمارے دل پر دوں میں ہیں اس چیز سے جس کی طرف آپ ہمیں بلا رہے ہیں اور ہمارے کانوں میں بوجھ ہے اور ہمارے اور آپ صلى الله عليه وسلم کے درمیان تو ایک پردہ حائل ہے تو آپ اپنا کام کریں ہم اپنا کام کر رہے ہیں');
INSERT INTO "bayan" VALUES(41,6,'41:6','آپ صلى الله عليه وسلم کہہ دیجیے کہ میں تو بس ایک انسان ہوں تمہاری طرح کا میری طرف وحی کی جا رہی ہے کہ تمہارا معبود ایک ہی معبود ہے تو اسی کی طرف سیدھا رکھو اپنے رخ کو اور اس سے استغفار کرو اور بڑی تباہی اور ہلاکت ہوگی مشرکین کے لیے');
INSERT INTO "bayan" VALUES(41,7,'41:7','وہ لوگ جو خود کو پاک کرنے کی کوشش نہیں کرتے اور وہ آخرت کے منکر ہیں');
INSERT INTO "bayan" VALUES(41,8,'41:8','بیشک وہ لوگ جو ایمان لائے اور جنہوں نے نیک عمل کیے ان کے لیے ایسا اجر ہے جس کا سلسلہ کبھی منقطع نہیں ہوگا');
INSERT INTO "bayan" VALUES(41,9,'41:9','(اے نبی صلى الله عليه وسلم !) آپ ان سے کہیے کہ کیا تم لوگ کفر کر رہے ہو اس ہستی کا جس نے زمین کو بنایا دو دنوں میں ؟ اور تم اس کے لیے مدمقابل ٹھہرا رہے ہو ! وہ ہے تمام جہانوں کا رب');
INSERT INTO "bayan" VALUES(41,10,'41:10','اور اس میں اس نے بنائے پہاڑوں کے لنگر اس کے اوپر سے اور اس میں برکات پیدا کیں اور اس میں اندازے مقررکیے اس کی غذائوں کے چار دنوں میں تمام سائلین کے لیے برابر');
INSERT INTO "bayan" VALUES(41,11,'41:11','پھر وہ آسمان کی طرف متوجہ ہوا اور ابھی وہ ایک دھواں سا تھا تو اس نے آسمان اور زمین سے کہا کہ تم دونوں چلے آئو خوشی سے یا جبراً ! ان دونوں نے کہا کہ ہم حاضر ہیں پوری آمادگی کے ساتھ۔');
INSERT INTO "bayan" VALUES(41,12,'41:12','پھر اس نے ان کو سات آسمانوں کی شکل دے دی دو دنوں میں۔ اور اس نے وحی کردیا ہر آسمان پر اس کا حکم ور اس نے وحی کردیا ہر آسمان پر اس کا حکم اور ہم نے آسمانِ دنیا کو چراغوں سے ّمزین کردیا اور اس کی خوب حفاظت کی یہ اس ہستی کا بنایا ہوا اندازہ ہے جو زبردست ہے اور ُ کل علم رکھنے والا ہے۔');
INSERT INTO "bayan" VALUES(41,13,'41:13','تو (اے نبی صلى الله عليه وسلم !) اگر یہ لوگ اعراض کریں تو آپ کہہ دیجیے کہ میں نے تو تمہیں خبردار کردیا ہے ایک ایسی خوفناک کڑک سے جیسی کہ قوم عاد اور قوم ثمود کی کڑک تھی۔');
INSERT INTO "bayan" VALUES(41,14,'41:14','جبکہ ان کے پاس رسول ؑ آئے ان کے سامنے سے بھی اور ان کے پیچھے سے بھی (اس دعوت کے ساتھ) کہ اللہ کے سوا کسی اور کی بندگی نہ کرو (جواب میں ہر قوم کے) لوگوں نے یہی کہا کہ اگر ہمارا رب چاہتا تو فرشتوں کو نازل کردیتا پس جو چیز دے کر آپ بھیجے گئے ہیں ہم تو اس کا انکار کرتے ہیں۔');
INSERT INTO "bayan" VALUES(41,15,'41:15','عاد کا معاملہ یہ تھا کہ انہوں نے زمین میں بہت تکبر کیا بالکل ناحق اور انہوں نے کہا کہ کون ہے ہم سے بڑھ کر قوت میں ؟ کیا انہوں نے غور نہیں کیا تھا کہ وہ اللہ جس نے انہیں پیدا کیا وہ ان سے بہت بڑھ کر ہے قوت میں اور وہ ہماری آیات کا انکار کرتے رہے');
INSERT INTO "bayan" VALUES(41,16,'41:16','تو ہم نے ان پر چھوڑ دی بہت زور کی ہوا نحوست کے دنوں میں تاکہ ہم انہیں مزہ چکھائیں رسوا کن عذاب کا اس دنیا کی زندگی میں۔ اور آخرت کا عذاب تو کہیں زیادہ رسوا کن ہوگا اور وہاں کوئی ان کی مدد کرنے والا نہیں ہوگا');
INSERT INTO "bayan" VALUES(41,17,'41:17','اور جو قوم ثمود تھی ان کو ہم نے ہدایت کی راہ دکھائی لیکن انہوں نے ہدایت کے مقابلے میں اندھے پن کو ہی پسند کیا توا نہیں بھی آپکڑا ذلت کے عذاب کی ہولناک کڑک نے ان کے کرتوتوں کی پاداش میں');
INSERT INTO "bayan" VALUES(41,18,'41:18','اور ہم نے نجات دے دی ان کو جو (ان میں سے) ایمان لائے تھے اور جنہوں نے تقویٰ کی روش اختیار کی تھی');
INSERT INTO "bayan" VALUES(41,19,'41:19','اور جس دن اللہ کے یہ دشمن جمع کیے جائیں گے آگ کی طرف تو ان کی درجہ بندی کی جائے گی۔');
INSERT INTO "bayan" VALUES(41,20,'41:20','یہاں تک کہ جب یہ وہاں پہنچیں گے تو ان کے خلاف گواہی دیں گے ان کے کان ان کی آنکھیں اور ان کی کھالیں اس بارے میں جو کچھ کہ وہ کرتے رہے تھے۔');
INSERT INTO "bayan" VALUES(41,21,'41:21','اور وہ کہیں گے اپنی کھالوں سے کہ تم نے کیوں ہمارے خلاف گواہی دی ؟ وہ کہیں گی کہ ہمیں بھی اس اللہ نے بولنے کی صلاحیت عطا کردی ہے جس نے ہر شے کو قوت گویائی عطا کی ہے اور اسی نے تمہیں پیدا کیا پہلی مرتبہ اور اسی کی طرف تم لوٹا دیے جائوگے');
INSERT INTO "bayan" VALUES(41,22,'41:22','اور تم (اس خیال سے) پردہ داری نہیں کرتے تھے کہ تمہارے خلاف گواہی دیں گے تمہارے اپنے کان تمہاری اپنی آنکھیں اور تمہاری اپنی کھالیں بلکہ تمہیں تو یہ گمان ہوگیا تھا کہ بہت سی باتیں تو اللہ کے علم میں ہی نہیں ہیں جو تم کر رہے ہو');
INSERT INTO "bayan" VALUES(41,23,'41:23','اور تمہارا یہی وہ گمان ہے جو تم نے اپنے رب کے بارے میں کیا تھا جس نے تمہیں غارت کیا ہے تو آج تم ہوگئے خسارہ پانے والوں میں');
INSERT INTO "bayan" VALUES(41,24,'41:24','تو اب اگر یہ لوگ صبر کریں (یا نہ کریں) پس ان کا ٹھکانہ آگ ہی ہے۔ اور اگر وہ معافی مانگیں تو اب انہیں معاف نہیں کیا جائے گا۔');
INSERT INTO "bayan" VALUES(41,25,'41:25','اور ہم نے ان کے لیے ایسے ساتھی مقرر کردیے جنہوں نے ان کو مزین ّکر کے دکھلائی ہر وہ چیز جو ان کے سامنے تھی اور جوان کے پیچھے تھی یقینا وہ خسارہ پانے والے لوگ تھے');
INSERT INTO "bayan" VALUES(41,26,'41:26','اور کہا ان لوگوں نے جنہوں نے کفر کیا کہ مت سنو اس قرآن کو اور اس (کی تلاوت کے دوران) میں شور مچایا کرو تاکہ تم غالب رہو۔');
INSERT INTO "bayan" VALUES(41,27,'41:27','تو ہم لازماً مزہ چکھائیں گے ان کافروں کو بہت سخت عذاب کا اور ہم انہیں سزادیں گے ان کے بدترین اعمال کے حساب سے');
INSERT INTO "bayan" VALUES(41,28,'41:28','یہ ہے بدلہ اللہ کے دشمنوں کا (یعنی) آگ ! ان کے لیے اسی میں ہمیشگی کا ٹھکانہ ہوگا ان کے لیے اسی میں ہمیشگی کا ٹھکانہ ہوگا یہ بدلہ ہوگا اس کا کہ وہ ہماری آیات کا انکار کرتے رہے تھے');
INSERT INTO "bayan" VALUES(41,29,'41:29','اور جو کافر ہیں وہ کہیں گے کہ اے ہمارے پروردگار ! ذرا ہمیں دکھا دے ِجنوں اور انسانوں میں سے وہ لوگ جنہوں نے ہمیں گمراہ کیا تھا ہم انہیں اپنے قدموں کے نیچے روندیں تاکہ وہ ہوجائیں سب سے نیچے والے');
INSERT INTO "bayan" VALUES(41,30,'41:30','بیشک وہ لوگ جنہوں نے کہا کہ ہمارا رب اللہ ہے پھر وہ اس پر ثابت قدم رہے ان پر فرشتوں کا نزول ہوتا ہے کہ آپ لوگ ڈرو نہیں اور غمگین نہ ہو اور خوشیاں منائو اس جنت کی جس کا تم سے وعدہ کیا جاتا رہا ہے');
INSERT INTO "bayan" VALUES(41,31,'41:31','ہم ہیں تمہارے رفیق دنیوی زندگی میں بھی اور آخرت میں بھی۔ اور تمہارے لیے اس (جنت) میں وہ سب کچھ ہوگا جو تمہارے جی چاہیں گے اور تمہارے لیے اس میں وہ سب کچھ بھی ہوگا جو تم مانگو گے');
INSERT INTO "bayan" VALUES(41,32,'41:32','یہ ابتدائی مہمان نوازی ہوگی اس ہستی کی طرف سے جو غفور اور رحیم ہے۔');
INSERT INTO "bayan" VALUES(41,33,'41:33','اور اس شخص سے بہتر بات اور کس کی ہوگی جو بلائے اللہ کی طرف اور وہ نیک عمل کرے اور کہے کہ میں مسلمانوں میں سے ہوں');
INSERT INTO "bayan" VALUES(41,34,'41:34','اور (دیکھو !) اچھائی اور برائی برابر نہیں ہوتے تم مدافعت کرو بہترین طریقے سے تو (تم دیکھو گے کہ) وہی شخص جس کے اور تمہارے درمیان دشمنی ہے وہ گویا گرم جوش دوست بن جائے گا');
INSERT INTO "bayan" VALUES(41,35,'41:35','اور یہ مقام نہیں حاصل ہوسکتا مگر ان ہی لوگوں کو جو بہت صبر کرتے ہوں اور یہ نہیں دیا جاتا مگر ان ہی کو جو بڑے نصیب والے ہوں۔');
INSERT INTO "bayan" VALUES(41,36,'41:36','اور اگر کبھی تمہیں شیطان کی طرف سے کوئی چوک لگنے لگے تو اللہ کی پناہ طلب کرلیا کرویقینا وہی ہے سب کچھ سننے والا سب کچھ جاننے والا۔');
INSERT INTO "bayan" VALUES(41,37,'41:37','اور اسی کی نشانیوں میں سے ہیں رات دن سورج اور چاندتم سورج کو سجدہ مت کرو اور نہ چاند کو بلکہ اس اللہ کو سجدہ کرو جس نے ان سب کو پیدا کیا اگر تم واقعتا اسی کی بندگی کرتے ہو۔');
INSERT INTO "bayan" VALUES(41,38,'41:38','پس (اے نبی صلى الله عليه وسلم !) اگر وہ استکبار کریں تو (پروا نہیں) وہ جو آپ کے رب کے پاس ہیں وہ دن رات اس کی تسبیح میں لگے ہوئے ہیں اور وہ (اس سے) تھکتے نہیں۔');
INSERT INTO "bayan" VALUES(41,39,'41:39','اور اس کی نشانیوں میں سے یہ بھی ہے کہ تم زمین کو دیکھتے ہو کہ وہ دبی پڑی ہے پھر جب ہم اس پر پانی برسادیتے ہیں تو اس میں حرکت پیدا ہوتی ہے اور وہ ابھر آتی ہے۔ یقینا وہ (اللہ) جس نے اس کو زندہ کیا ہے وہی ُ مردوں کو بھی زندہ کرے گا۔ یقینا وہ ہرچیز پر قادر ہے');
INSERT INTO "bayan" VALUES(41,40,'41:40','جو لوگ ہماری آیات کے بارے میں کج روی اختیار کرتے ہیں وہ ہم سے چھپے ہوئے نہیں ہیں تو کیا وہ شخص جو آگ میں جھونک دیا جائے گا وہ بہتر ہوگا یا وہ کہ جو قیامت کے دن امن کی حالت میں آئے گا تم کیے جائو جو بھی کرنا چاہتے ہو وہ خوب دیکھ رہا ہے جو کچھ تم کر رہے ہو۔');
INSERT INTO "bayan" VALUES(41,41,'41:41','یقینا وہ لوگ جنہوں نے اس یاد دہانی کا انکار کیا جبکہ یہ ان کے پاس آگئی۔ (انہیں معلوم ہونا چاہیے کہ) بلاشبہ یہ ایک زبردست کتاب ہے');
INSERT INTO "bayan" VALUES(41,42,'41:42','باطل اس پر حملہ آور ہو ہی نہیں سکتا نہ اس کے سامنے سے اور نہ ہی اس کے پیچھے سے۔ اس کا اتارا جانا ہے ایک حکیم اور حمید ہستی کی طرف سے');
INSERT INTO "bayan" VALUES(41,43,'41:43','(اے نبی صلى الله عليه وسلم !) نہیں کہا جاتا آپ سے مگر وہی کچھ جو آپ سے پہلے رسولوں سے کہا گیا تھا یقینا آپ کا رب مغفرت والا بھی ہے اور دردناک سزا دینے والا بھی');
INSERT INTO "bayan" VALUES(41,44,'41:44','اور اگر ہم نے اسے عجمی قرآن بنایا ہوتا تو یہ کہتے کہ اس کی آیات واضح کیوں نہیں کی گئیں کیا کتاب عجمی اور مخاطب عربی ؟ (اے نبی صلى الله عليه وسلم !) آپ کہیے کہ یہ ہدایت اور شفا ہے ان لوگوں کے لیے جو ایمان لائیں اور جو لوگ ایمان نہیں لاتے ان کے کانوں میں بوجھ ہے اور یہ (قرآن) ان کے حق میں اندھا پن ہے۔ یہ وہ لوگ ہیں جو پکارے جاتے ہیں بہت دور کی جگہ سے۔');
INSERT INTO "bayan" VALUES(41,45,'41:45','اور ہم نے موسیٰ ؑ ٰ کو بھی کتاب دی تھی تو اس میں بھی اختلاف کیا گیا اور اگر آپ کے رب کی طرف سے ایک بات پہلے سے طے نہ ہوچکی ہوتی تو ان کے مابین فیصلہ کردیا جاتا اور یقینا وہ اس کے بارے میں خلجان انگیز شک میں مبتلا ہیں');
INSERT INTO "bayan" VALUES(41,46,'41:46','جو کوئی نیک عمل کرتا ہے تو اپنے ہی لیے کرتا ہے اور جو کوئی برا کام کرتا ہے تو اس کا وبال اسی پر آئے گا اور آپ کا رب کچھ بھی ظلم کرنے والا نہیں ہے اپنے بندوں کے حق میں');
INSERT INTO "bayan" VALUES(41,47,'41:47','اسی کی طرف لوٹتا ہے قیامت کا علم۔ اور نہیں نکلتے کوئی پھل اپنے غلافوں سے اور نہ کسی مادہ کو حمل ہوتا ہے اور نہ ہی وہ اس کو جنتی ہے مگر اسی کے علم سے۔ اور جس دن وہ ان کو پکارے گا کہ کہاں ہیں میرے وہ شریک ؟ وہ کہیں گے : ہم نے تو آپ سے عرض کردیا ہے کہ ہم میں سے کوئی بھی اس کی گواہی دینے والا نہیں');
INSERT INTO "bayan" VALUES(41,48,'41:48','اور وہ سب گم ہوجائیں گے ان سے جنہیں وہ پکارا کرتے تھے اس سے پہلے اور وہ یقین کرلیں گے کہ اب ان کے لیے کوئی جائے فرار نہیں');
INSERT INTO "bayan" VALUES(41,49,'41:49','انسان بھلائی مانگنے سے نہیں تھکتا اور اگر کہیں اسے کوئی تکلیف پہنچ جائے تو بالکل مایوس و دل شکستہ ہوجاتا ہے');
INSERT INTO "bayan" VALUES(41,50,'41:50','اور اگر اسے ہم اپنی رحمت کا مزہ چکھا دیں اس کے بعد کہ اسے کوئی تکلیف پہنچی تھی تو وہ ضرور کہے گا کہ ہاں یہ تو میرا حق ہے اور میں نہیں سمجھتا کہ یہ قیامت وغیرہ کوئی قائم ہونے والی شے ہے اور اگر مجھے لوٹا ہی دیا گیا میرے رب کی طرف یقینا میرے لیے اس کے پاس بہتری ہی ہوگی تب ہم پوری طرح سے جتلادیں گے ان کافروں کو جو کچھ بھی اعمال انہوں نے کیے ہوں گے اور انہیں ہم لازماً مزا چکھائیں گے بہت ہی گاڑھے عذاب کا');
INSERT INTO "bayan" VALUES(41,51,'41:51','اور جب ہم انسان پر نعمتوں کی بارش کردیتے ہیں تو وہ رخ پھیر لیتا ہے اور اپنا پہلو بدل لیتا ہے اور جب اسے کوئی تکلیف پہنچتی ہے تو بڑی لمبی لمبی دعائیں کرنے لگتا ہے');
INSERT INTO "bayan" VALUES(41,52,'41:52','(اے نبی صلى الله عليه وسلم !) ان سے کہیے : کیا تم نے غور کیا کہ اگر یہ (قرآن) واقعی اللہ ہی کی طرف سے ہو پھر تم نے اس کا کفر کیا تو کون ہوگا اس سے بڑھ کر بھٹکا ہوا جو دور کی مخالفت میں پڑا ہو !');
INSERT INTO "bayan" VALUES(41,53,'41:53','عنقریب ہم انہیں دکھائیں گے اپنی نشانیاں آفاق میں بھی اور ان کی اپنی جانوں کے اندر بھی یہاں تک کہ ان پر واضح ہوجائے گا کہ یہ (قرآن) حق ہے کیا یہ بات کافی نہیں ہے کہ آپ کا ربّ ہرچیز پر گواہ ہے !');
INSERT INTO "bayan" VALUES(41,54,'41:54','آگاہ ہو جائو ! یہ لوگ اپنے رب کے ساتھ ملاقات سے متعلق شک میں پڑے ہوئے ہیں آگاہ ہو جائو ! بیشک وہ ہرچیز کا احاطہ کیے ہوئے ہے');
INSERT INTO "bayan" VALUES(42,1,'42:1','ح م۔');
INSERT INTO "bayan" VALUES(42,2,'42:2','ع س ق۔');
INSERT INTO "bayan" VALUES(42,3,'42:3','(اے نبی صلى الله عليه وسلم !) اسی طرح وحی کرتا ہے آپ کی طرف اور ان کی طرف بھی کرتا رہا ہے جو آپ سے پہلے تھے وہ اللہ جو بہت زبردست بہت حکمت والا ہے۔');
INSERT INTO "bayan" VALUES(42,4,'42:4','اسی کا ہے جو کچھ آسمانوں میں ہے اور جو کچھ زمین میں ہے۔ اور وہ بہت بلند وبالا بہت عظمت والا ہے۔');
INSERT INTO "bayan" VALUES(42,5,'42:5','قریب ہے کہ آسمان اپنے اوپر سے پھٹ پڑیں اور فرشتے تسبیح کرتے ہیں اپنے رب کی حمد کے ساتھ اور زمین میں جو (اہل ایمان) ہیں ان کے لیے استغفار کرتے ہیں۔ آگاہ ہو جائو ! یقینا اللہ ہی بخشنے والا رحم کرنے والا ہے۔');
INSERT INTO "bayan" VALUES(42,6,'42:6','اور جن لوگوں نے اس کے سوا دوسرے مددگار بنا رکھے ہیں اللہ ان پر نگران ہے اور (اے نبی صلى الله عليه وسلم !) آپ ان کے ذمہ دار نہیں ہیں۔');
INSERT INTO "bayan" VALUES(42,7,'42:7','اور اسی طرح ہم نے وحی کیا ہے آپ کی طرف یہ قرآن عربی تاکہ آپ خبردار کردیں بستیوں کے مرکز اور اس کے ارد گرد رہنے والوں کو اور آپ صلى الله عليه وسلم خبردار کردیں اس جمع ہونے والے دن سے جس میں کوئی شک نہیں۔ } (اُس دن) ایک گروہ جنت میں جائے گا اور ایک گروہ جہنم میں۔');
INSERT INTO "bayan" VALUES(42,8,'42:8','اور اگر اللہ چاہتا تو انہیں ایک ہی امت بنا دیتا لیکن اللہ داخل کرتا ہے اپنی رحمت میں جس کو چاہتا ہے۔ اور جو ظالم (کافر و مشرک) ہوں گے ان کے لیے نہ کوئی حمایتی ہوگا اور نہ کوئی مددگار۔');
INSERT INTO "bayan" VALUES(42,9,'42:9','کیا انہوں نے اللہ کے سوا کوئی اور حمایتی بنا لیے ہیں ؟ سو حمایتی تو صرف اللہ ہی ہے اور وہی ہے جو زندہ کرے گا ُ مردوں کو اور وہ ہرچیز پر قادر ہے۔');
INSERT INTO "bayan" VALUES(42,10,'42:10','اور جس بات میں بھی تم اختلاف کرو تو اس کا فیصلہ اللہ ہی کی طرف ہے۔ } وہ ہے اللہ میرا رب اسی پر میں نے ّتوکل کیا ہے اور اسی کی طرف میں رجوع کرتا ہوں۔');
INSERT INTO "bayan" VALUES(42,11,'42:11','وہ آسمانوں اور زمین کا پیدا فرمانے والا ہے اس نے تمہاری ہی نوع سے تمہارے جوڑے بنادیے اور چوپایوں سے بھی جوڑے (بنائے) اسی میں وہ تمہاری افزائش کرتا ہے۔ اس کی مثال کی سی بھی کوئی شے نہیں۔ اور وہی ہے سب کچھ سننے والا سب کچھ دیکھنے والا ہے۔');
INSERT INTO "bayan" VALUES(42,12,'42:12','اسی کے لیے ہیں آسمانوں اور زمین کی تمام کنجیاں۔ وہ کشادہ کردیتا ہے رزق جس کے لیے چاہتا ہے اور (جس کو چاہتا ہے) ناپ تول کردیتا ہے۔ یقینا وہ ہرچیز کا جاننے والا ہے۔');
INSERT INTO "bayan" VALUES(42,13,'42:13','(اے مسلمانو !) اللہ نے تمہارے لیے دین میں وہی کچھ مقرر کیا ہے جس کی وصیت اس نے نوح ؑ کو کی تھی اور جس کی وحی ہم نے (اے محمد صلى الله عليه وسلم آپ کی طرف کی ہے اور جس کی وصیت ہم نے کی تھی ابراہیم کو اور موسیٰ کو اور عیسیٰ کو کہ قائم کرو دین کو۔ اور اس میں تفرقہ نہ ڈالو۔ (اے نبی صلى الله عليه وسلم بہت بھاری ہے مشرکین پر یہ بات جس کی طرف آپ ان کو بلا رہے ہیں اللہ جس کو چاہتا ہے اپنی طرف (آنے کے لیے) ُ چن لیتا ہے اور وہ اپنی طرف ہدایت اسے دیتا ہے جو خود رجوع کرتا ہے۔');
INSERT INTO "bayan" VALUES(42,14,'42:14','اور انہوں نے تفرقہ نہیں کیا مگر اس کے بعد کہ ان کے پاس علم آچکا تھا آپس میں ضدم ّضدا کے باعث۔ اور اگر ایک بات آپ کے رب کی طرف سے پہلے سے ایک وقت معین کے لیے طے نہ پا چکی ہوتی تو ان کے مابین (اختلافات کا) فیصلہ چکا دیا جاتا۔ } اور جو لوگ کتاب کے وارث بنائے گئے ان کے بعد وہ اس کے متعلق ایک خلجان آمیز شک میں مبتلا ہیں۔');
INSERT INTO "bayan" VALUES(42,15,'42:15','تو (اے نبی صلى الله عليه وسلم !) آپ اسی کی دعوت دیتے رہیے اور جمے رہیے جیسا کہ آپ کو حکم دیا گیا ہے اور ان کی خواہشات کی پیروی نہ کیجیے۔ اور آپ کہہ دیجیے کہ میں تو اس کتاب پر ایمان لایا ہوں جو اللہ نے نازل کی ہے۔ اور (آپ صلى الله عليه وسلم کہہ دیجیے کہ) مجھے حکم ہوا کہ میں تمہارے درمیان عدل قائم کروں۔ اللہ ہمارا بھی رب ہے اور تمہارا بھی رب ہے ہمارے لیے ہمارے اعمال ہیں اور تمہارے لیے تمہارے اعمال ہیں۔ ہمارے درمیان کسی حجت بازی کی ضرورت نہیں اللہ ہمیں جمع کر دے گا۔ اور اسی کی طرف سب کو لوٹنا ہے۔');
INSERT INTO "bayan" VALUES(42,16,'42:16','اور وہ لوگ جو اللہ کے بارے میں حجت بازی میں لگے رہے اس کے بعد کہ اس کی دعوت پر لبیک کہہ دی گئی ہے ان کی حجت بازی ان کے رب کے نزدیک بالکل پسپا ہے اور ان پر اللہ کا غضب ہے اور ان کے لیے بہت سخت عذاب بھی ہے۔');
INSERT INTO "bayan" VALUES(42,17,'42:17','اللہ ہی ہے جس نے اتاری ہے کتاب بھی اور میزان بھی حق کے ساتھ۔ اور تمہیں کیا معلوم کہ قیامت قریب ہی ہو !');
INSERT INTO "bayan" VALUES(42,18,'42:18','اس (قیامت) کے لیے جلدی تو وہی مچا رہے ہیں جو اس پر ایمان نہیں رکھتے۔ اور جو لوگ ایمان رکھتے ہیں وہ تو اس سے لرزاں و ترساں رہتے ہیں اور وہ جانتے ہیں کہ وہ حق ہے۔ آگاہ ہو جائو جو لوگ قیامت کے بارے میں جھگڑتے ہیں وہ بڑی دور کی گمراہی میں پڑچکے ہیں۔');
INSERT INTO "bayan" VALUES(42,19,'42:19','اللہ اپنے بندوں کے حق میں بہت مہربان ہے وہ رزق دیتا ہے جس کو چاہتا ہے۔ } اور وہ تو بہت قوی ہے بہت زبردست ہے۔');
INSERT INTO "bayan" VALUES(42,20,'42:20','جو شخص آخرت کی کھیتی کا طالب ہوتا ہے تو ہم اس کے لیے اس کی کھیتی میں اضافہ کردیتے ہیں۔ } اور جو کوئی دنیا کی کھیتی کا طالب بن جاتا ہے تو ہم اس کو اس میں سے کچھ دے دیتے ہیں اور آخرت میں اس کا کوئی حصہ نہیں ہے');
INSERT INTO "bayan" VALUES(42,21,'42:21','کیا ان کے کچھ ایسے شریک ہیں جنہوں نے ان کے لیے دین کا کوئی ایسا راستہ طے کردیا ہو جس کا اذن اللہ نے نہیں دیا ؟ } اور اگر ایک قطعی حکم پہلے سے طے نہ ہوچکا ہوتا تو ان کے درمیان فیصلہ کردیا جاتا۔ اور ظالموں کے لیے تو بہت دردناک عذاب ہے۔');
INSERT INTO "bayan" VALUES(42,22,'42:22','تم دیکھو گے ان ظالموں کو کہ وہ اپنے کرتوتوں کے سبب ڈر رہے ہوں گے اور وہ ان کے اوپر پڑنے والا ہوگا اور وہ لوگ جو ایمان لائے اور اعمالِ صالحہ پر کاربند رہے وہ جنتوں کے باغات میں ہوں گے۔ } ان کے لیے ان کے رب کے پاس وہ سب کچھ ہوگا جو وہ چاہیں گے۔ یہی (ان کے لیے) بہت بڑی فضیلت ہوگی۔');
INSERT INTO "bayan" VALUES(42,23,'42:23','یہ ہے وہ (انجامِ نیک) جس کی بشارت دے رہا ہے اللہ اپنے ان بندوں کو جو ایمان لائے اور انہوں نے نیک عمل کیے۔ (اے نبی صلى الله عليه وسلم !) آپ کہہ دیجیے کہ میں تم سے کوئی اجر نہیں مانگتا سوائے قرابت داری کے لحاظ کے } اور جو کوئی بھلائی کمائے گا تو ہم اس کے لیے اس میں بھلائی کا اضافہ کرتے رہیں گے۔ یقینا اللہ بہت بخشنے والا بہت قدردان ہے۔');
INSERT INTO "bayan" VALUES(42,24,'42:24','کیا وہ کہتے ہیں کہ اس نے اللہ کے اوپر جھوٹ گھڑلیا ہے ؟ تو اگر اللہ چاہے تو آپ کے قلب پر مہر لگا دے۔ اور اللہ باطل کو مٹا دیتا ہے اور حق کا حق ہونا ثابت کردیتا ہے اپنے کلمات سے۔ } یقینا وہ واقف ہے اس سے بھی جو کچھ سینوں کے اندر پوشیدہ ہے۔');
INSERT INTO "bayan" VALUES(42,25,'42:25','اور وہی ہے کہ جو اپنے بندوں کی توبہ قبول فرماتا ہے اور ان کی بہت سی برائیوں سے درگزر کرتا ہے اور وہ جانتا ہے جو کچھ تم کرتے ہو۔');
INSERT INTO "bayan" VALUES(42,26,'42:26','اور وہ دعائیں قبول کرتا ہے ان لوگوں کی جو ایمان لائیں اور نیک اعمال کریں اور اپنے فضل سے انہیں (ایمان واعمالِ صالحہ میں) ترقی بھی دیتا ہے۔ اور کافروں کے لیے بہت سخت عذاب ہے۔');
INSERT INTO "bayan" VALUES(42,27,'42:27','اور اگر اللہ کشادہ کردیتا رزق اپنے سب بندوں کے لیے تو وہ زمین میں سرکشی کرتے } لیکن اللہ نازل کرتا ہے ایک اندازے کے مطابق جو چاہتا ہے۔ یقینا وہ اپنے بندوں (کے حالات) سے باخبر ان کو دیکھنے والا ہے۔');
INSERT INTO "bayan" VALUES(42,28,'42:28','اور وہی ہے جو بارش برساتا ہے اس کے بعد کہ لوگ مایوس ہوچکے ہوتے ہیں اور اپنی رحمت پھیلا دیتا ہے۔ اور وہی ہے جو (اپنے بندوں کا) مددگار ہے اور وہ اپنی ذات میں آپ ستودہ صفات ہے۔');
INSERT INTO "bayan" VALUES(42,29,'42:29','اور اس کی نشانیوں میں سے ہے آسمانوں اور زمین کی تخلیق اور ان دونوں میں اس نے جو جان دار پھیلادیے ہیں۔ اور وہ جب چاہے ان سب کو جمع کرنے پر قادر ہے۔');
INSERT INTO "bayan" VALUES(42,30,'42:30','اور تم پر جو بھی مصیبت آتی ہے وہ درحقیقت تمہارے اپنے ہاتھوں کی کمائی (اعمال) کے سبب آتی ہے اور (تمہاری خطائوں میں سے) اکثر کو تو وہ معاف بھی کرتا رہتا ہے۔');
INSERT INTO "bayan" VALUES(42,31,'42:31','اور تم زمین میں (اللہ کو) عاجز کرنے والے نہیں ہو۔ اور تمہارے لیے اللہ کے سوا نہ کوئی کارساز ہے اور نہ مددگار۔');
INSERT INTO "bayan" VALUES(42,32,'42:32','اور اس کی نشانیوں میں سے سمندروں میں (چلنے والے) پہاڑوں کے مانند جہا زبھی ہیں۔');
INSERT INTO "bayan" VALUES(42,33,'42:33','اگر وہ چاہے تو ہوا کو ساکن کردے سو وہ کھڑے کے کھڑے رہ جائیں سمندر کی سطح پر۔ یقینا اس میں نشانیاں ہیں ہر ایک صبر کرنے والے اور شکر کرنے والے کے لیے۔');
INSERT INTO "bayan" VALUES(42,34,'42:34','یا وہ (چاہے تو) تباہ کر دے ان (جہازوں) کو لوگوں کے گناہوں کی پاداش میں لیکن وہ بہت سی خطائوں سے درگزر فرماتا رہتا ہے۔');
INSERT INTO "bayan" VALUES(42,35,'42:35','اور تاکہ جان لیں وہ لوگ جو ہماری آیات میں کٹ حجتی کر رہے ہیں کہ ان کے لیے بھاگنے کی کوئی جگہ نہیں ہے۔');
INSERT INTO "bayan" VALUES(42,36,'42:36','پس جو کچھ بھی تمہیں دیا گیا ہے وہ دنیا ہی کی زندگی کا سازو سامان ہے۔ } اور جو کچھ اللہ کے پاس ہے وہ بہتر اور باقی رہنے والا ہے ان لوگوں کے لیے جو ایمان رکھتے ہیں اور اپنے رب پر توکل کرتے ہیں۔');
INSERT INTO "bayan" VALUES(42,37,'42:37','اور وہ لوگ کہ جو اجتناب کرتے ہیں بڑے بڑے گناہوں سے اور بےحیائی کے کاموں سے اور جب انہیں غصہ آتا ہے تو معاف کردیتے ہیں۔');
INSERT INTO "bayan" VALUES(42,38,'42:38','اور وہ لوگ جنہوں نے اپنے رب کی پکار پر لبیک کہا اور نماز قائم کی۔ اور ان کا کام آپس میں مشورے سے ہوتا ہے۔ اور جو کچھ ہم نے انہیں دیا اس میں سے وہ خرچ کرتے ہیں۔');
INSERT INTO "bayan" VALUES(42,39,'42:39','اور وہ لوگ کہ جب ان پر زیادتی ہو تو وہ بدلہ لیتے ہیں۔');
INSERT INTO "bayan" VALUES(42,40,'42:40','اور کسی برائی کا بدلہ ویسی ہی برائی ہے پس جو کوئی معاف کر دے اور اصلاح کرے تو اس کا اجر اللہ کے ذمے ہے۔ یقینا اللہ ظالموں کو پسند نہیں کرتا۔');
INSERT INTO "bayan" VALUES(42,41,'42:41','اور جو کوئی بدلہ لے اس کے بعد کہ اس پر ظلم کیا گیا ہو تو ان لوگوں پر کوئی الزام نہیں ہے۔');
INSERT INTO "bayan" VALUES(42,42,'42:42','اصل میں الزام تو ان پر ہے جو لوگوں پر ظلم کرتے ہیں اور زمین کے اندر سرکشی کرتے ہیں بغیر کسی حق کے۔ یہی لوگ ہیں جن کے لیے دردناک عذاب ہے۔');
INSERT INTO "bayan" VALUES(42,43,'42:43','اور ہاں جو کوئی صبر کرے اور معاف کر دے تو یہ واقعتابڑے ہمت کے کاموں میں سے ہے۔');
INSERT INTO "bayan" VALUES(42,44,'42:44','اور جس کو اللہ نے ہی گمراہ کردیا ہو تو اس کے بعد اس کے لیے کوئی مدد گار نہیں۔ اور تم دیکھو گے ظالموں کو کہ جب وہ عذاب کو دیکھیں گے تو کہیں گے : کیا واپس لوٹ جانے کا بھی کوئی راستہ ہے ؟');
INSERT INTO "bayan" VALUES(42,45,'42:45','اور تم دیکھو گے انہیں کہ وہ پیش کیے جائیں گے اس (جہنم) پر نگاہیں زمین میں گاڑے ہوں گے ذلت کی وجہ سے دیکھ رہے ہوں گے کن انکھیوں سے۔ اور (اُس وقت) اہل ایمان کہیں گے کہ واقعتا تباہ و برباد ہونے والے تو وہ لوگ ہیں جنہوں نے اپنے آپ کو اور اپنے اہل و عیال کو قیامت کے دن خسارے میں مبتلا کیا۔ } آگاہ ہو جائو ! یہ ظالم تو ہمیشہ قائم رہنے والے عذاب میں رہیں گے۔');
INSERT INTO "bayan" VALUES(42,46,'42:46','اور ان کے لیے کوئی مدد گار نہیں ہوں گے جو ان کی مدد کرسکیں اللہ کے مقابلے میں اور جسے اللہ نے ہی گمراہ کردیا ہو پھر اس کے لیے کوئی راستہ نہیں ہے');
INSERT INTO "bayan" VALUES(42,47,'42:47','(اے اہل ِایمان !) اپنے رب کی پکار پر لبیک کہو اس سے پہلے کہ اللہ کے حکم سے وہ دن آجائے جسے لوٹایا نہ جاسکے گا نہیں ہوگی تمہارے لیے اس دن کوئی جائے پناہ اور نہیں ہوگا تمہارے لیے کوئی موقع انکار');
INSERT INTO "bayan" VALUES(42,48,'42:48','پھر اگر یہ لوگ اعراض کریں تو (اے نبی صلى الله عليه وسلم !) ہم نے آپ کو ان پر کوئی داروغہ بنا کر نہیں بھیجا نہیں ہے آپ پر کوئی ذمہ داری مگر صاف صاف پہنچا دینے کی اور جب ہم انسان کو اپنی رحمت کا کوئی مزہ چکھاتے ہیں تو وہ اس پر اتراتا ہے اور اگر ان پر آپڑے کوئی برائی ان کے اپنے ہاتھوں کے کرتوتوں کی بدولت تو انسان بالکل نا شکرا بن جاتا ہے');
INSERT INTO "bayan" VALUES(42,49,'42:49','آسمانوں اور زمین کی بادشاہی اللہ ہی کے لیے ہے وہ پیدا کرتا ہے جو چاہتا ہے۔ جس کو چاہتا ہے بیٹیاں دیتا ہے اور جس کو چاہتا ہے بیٹے دیتا ہے');
INSERT INTO "bayan" VALUES(42,50,'42:50','یا وہ انہیں ملا کردیتا ہے بیٹے اور بیٹیاں اور جس کو چاہتا ہے بانجھ کردیتا ہے۔ یقینا وہ سب کچھ جاننے والا ہرچیز پر قدرت رکھنے والا ہے');
INSERT INTO "bayan" VALUES(42,51,'42:51','اور کسی بشر کا یہ مقام نہیں کہ اللہ اس سے کلام کرے سوائے وحی کے یا (پھر وہ بات کرتا ہے) پردے کی اوٹ سے یا وہ بھیجتا ہے کسی پیغام بر َ (فرشتے) کو پھر وہ وحی کرتا ہے اللہ کے حکم سے جو وہ چاہتا ہے وہ بہت بلند وبالا ہے کمال حکمت والا ہے');
INSERT INTO "bayan" VALUES(42,52,'42:52','اور (اے نبی صلى الله عليه وسلم !) اسی طرح ہم نے آپ کی طرف وحی کی ہے ایک روح اپنے امر میں سے (اے نبی صلى الله عليه وسلم !) آپ نہیں جانتے تھے کہ کتاب کیا ہوتی ہے اور ایمان کیا ہوتا ہے لیکن اس (قرآن) کو ہم نے ایسا نور بنایا ہے جس کے ذریعے سے ہم ہدایت دیتے ہیں اپنے بندوں میں سے جس کو چاہتے ہیں اور آپ صلى الله عليه وسلم یقینا سیدھے راستے کی طرف ہدایت دیتے ہیں');
INSERT INTO "bayan" VALUES(42,53,'42:53','اس اللہ کے راستے کی طرف جس کی ملکیت ہے ہر وہ شے جو آسمانوں میں ہے اور ہر وہ شے جو زمین میں ہے آگاہ ہو جائو ! تمام معاملات اللہ ہی کی طرف لوٹ جائیں گے');
INSERT INTO "bayan" VALUES(43,1,'43:1','ح م۔');
INSERT INTO "bayan" VALUES(43,2,'43:2','قسم ہے اس کتاب کی جو بالکل واضح ہے');
INSERT INTO "bayan" VALUES(43,3,'43:3','ہم نے اس کو بنا یا ہے قرآن عربی تاکہ تم سمجھ سکو');
INSERT INTO "bayan" VALUES(43,4,'43:4','اور یہ اُمّ الکتاب میں ہے ہمارے پاس بہت بلند وبالا بہت حکمت والی');
INSERT INTO "bayan" VALUES(43,5,'43:5','کیا ہم اس ذکر کا رخ تمہاری طرف سے اس لیے پھیر دیں کہ تم حد سے بڑھنے والے لوگ ہو');
INSERT INTO "bayan" VALUES(43,6,'43:6','اور ہم نے کتنے ہی نبی بھیجے پہلوں میں بھی');
INSERT INTO "bayan" VALUES(43,7,'43:7','اور نہیں آیا کوئی بھی نبی ان کے پاس مگر وہ اس کا مذاق ہی اڑاتے رہے');
INSERT INTO "bayan" VALUES(43,8,'43:8','پھر ہم نے انہیں ہلاک کردیا جو ان سے بہت زیادہ بڑھ کر تھے قوت میں اور پہلے لوگوں کی مثالیں گزر چکی ہیں');
INSERT INTO "bayan" VALUES(43,9,'43:9','اور اگر آپ صلى الله عليه وسلم ان سے پوچھیں گے کہ کس نے پیدا کیا آسمانوں اور زمین کو تو وہ لازماً یہی کہیں گے کہ انہیں بنایا ہے اس ہستی نے جو العزیز اور العلیم ہے');
INSERT INTO "bayan" VALUES(43,10,'43:10','جس نے تمہارے لیے زمین کو بچھونا بنا دیا اور پھر اس میں تمہارے لیے راستے بنا دیے تاکہ تم منزل تک پہنچ سکو');
INSERT INTO "bayan" VALUES(43,11,'43:11','اور وہ جس نے اتارا آسمان سے پانی ایک اندازے کے مطابق تو اس سے ہم نے ُ مردہ زمین کو اٹھا کھڑا کیا اسی طرح (ایک روز) تمہیں بھی نکال لیا جائے گا');
INSERT INTO "bayan" VALUES(43,12,'43:12','اور وہ کہ جس نے بنائے ہیں تمام مخلوقات کے جوڑے اور تمہارے لیے بنائی ہیں کشتیاں بھی اور چوپائے بھی جن پر تم سواری کرتے ہو');
INSERT INTO "bayan" VALUES(43,13,'43:13','تا کہ تم جم کر بیٹھو ان کی پیٹھوں پر پھر اپنے رب کے انعام کا ذکر کرو جب کہ تم ان کے اوپر جم کر بیٹھ جائو اور تم کہو کہ پاک ہے وہ ذات جس نے ان (سواریوں) کو ہمارے بس میں کردیا ہے اور ہم تو انہیں قابو میں لانے والے نہیں تھے');
INSERT INTO "bayan" VALUES(43,14,'43:14','اور یقینا ہم اپنے ربّ ہی کی طرف لوٹ کر جانے والے ہیں');
INSERT INTO "bayan" VALUES(43,15,'43:15','اور انہوں نے اس کے بندوں میں سے اس کا ایک ُ جزوٹھہرایا یقینا انسان بہت کھلا نا شکرا ہے');
INSERT INTO "bayan" VALUES(43,16,'43:16','کیا اس نے بنا لی ہیں اپنی مخلوق میں سے بیٹیاں اور تمہیں پسند کرلیا ہے بیٹوں کے ساتھ');
INSERT INTO "bayan" VALUES(43,17,'43:17','اور جب ان میں سے کسی کو اس کی بشارت دی جاتی ہے جس کی مثال وہ رحمان کے لیے بیان کرتا ہے تو اس کے چہرے پر سیاہی چھا جاتی ہے اور وہ غم کے گھونٹ پی رہا ہوتا ہے');
INSERT INTO "bayan" VALUES(43,18,'43:18','کیا وہ جو پرورش پاتی ہے زیور میں اور بحث میں اپنا موقف واضح نہیں کرسکتی');
INSERT INTO "bayan" VALUES(43,19,'43:19','اور انہوں نے فرشتوں کو جو کہ رحمان کے مقرب ّبندے ہیں (اس کی) بیٹیاں قرار دے دیا ہے کیا یہ لوگ موجود تھے ان کی تخلیق کے وقت ؟ ان کی یہ گواہی (کہ فرشتے مونث ہیں) لکھ لی جائے گی اور پھر ان سے باز پرس ہوگی');
INSERT INTO "bayan" VALUES(43,20,'43:20','اور وہ کہتے ہیں کہ اگر رحمان چاہتا تو ہم ان کی بندگی نہ کرتے ان کے پاس اس کے لیے کوئی علمی سند نہیں ہے وہ تو محض اٹکل کے تیر چلا رہے ہیں');
INSERT INTO "bayan" VALUES(43,21,'43:21','کیا ہم نے انہیں اس سے پہلے کوئی کتاب دی ہے جسے وہ مضبوطی سے تھامے ہوئے ہیں ؟');
INSERT INTO "bayan" VALUES(43,22,'43:22','بلکہ وہ کہتے ہیں کہ ہم نے تو اپنے آباء و اَجداد کو پایا ایک راستے پر اور اب ہم ان ہی کے نقش ِقدم پر ہدایت یافتہ ہیں');
INSERT INTO "bayan" VALUES(43,23,'43:23','اور (اے نبی صلى الله عليه وسلم !) اسی طرح ہم نے نہیں بھیجا آپ سے پہلے کسی بستی میں کسی خبردار کرنے والے کو مگر اس کے خوشحال لوگوں نے یہی کہا کہ ہم نے پایا ہے اپنے آباء و اَجداد کو ایک راستے پر اور اب ہم ان ہی کے نقش ِقدم کی اقتدا کر رہے ہیں');
INSERT INTO "bayan" VALUES(43,24,'43:24','اس (خبردار کرنے والے) نے کہا کہ خواہ میں لایا ہوں تمہارے پاس زیادہ ہدایت والی چیز اس کے مقابلے میں جس پر تم نے اپنے آباء و اَجداد کو پایا انہوں نے کہا کہ ہم اس کے جس کے ساتھ آپ بھیجے گئے ہیں منکر ہیں');
INSERT INTO "bayan" VALUES(43,25,'43:25','تو ہم نے ان سے انتقام لیا تو دیکھ لو پھر کیسا انجام ہوا جھٹلانے والوں کا');
INSERT INTO "bayan" VALUES(43,26,'43:26','اور یاد کرو جب ابراہیم ؑ نے کہا تھا اپنے والد اور اپنی قوم سے کہ یقینا میں بیزار ہوں ان سے جنہیں تم پوجتے ہو');
INSERT INTO "bayan" VALUES(43,27,'43:27','سوائے اس ہستی کے جس نے مجھے پیدا کیا ہے تو یقینا وہی مجھے راستہ دکھائے گا');
INSERT INTO "bayan" VALUES(43,28,'43:28','اور اس نے اسی بات کو باقی رکھا اپنے پیچھے (اپنی اولاد میں) بھی تاکہ وہ (اللہ ہی کی جناب میں) رجوع کیے رہیں');
INSERT INTO "bayan" VALUES(43,29,'43:29','لیکن میں نے ان لوگوں کو اور ان کے آباء و اَجداد کو کچھ سازو سامان دے دیا یہاں تک کہ ان کے پاس آگیا حق اور ایک واضح کردینے والا رسول');
INSERT INTO "bayan" VALUES(43,30,'43:30','اور جب حق ان کے پاس آیا تو انہوں نے کہا کہ یہ تو جادو ہے اور ہم اس کا انکار کرتے ہیں');
INSERT INTO "bayan" VALUES(43,31,'43:31','اور کہنے لگے کہ کیوں نہیں اتارا گیا یہ قرآن ان دو بستیوں میں سے کسی عظیم شخص پر ؟');
INSERT INTO "bayan" VALUES(43,32,'43:32','کیا آپ کے رب کی رحمت کو یہ لوگ تقسیم کریں گے ؟ ہم نے ان کے درمیان ان کی معیشت کا سامان دنیا کی زندگی میں تقسیم کردیا ہے اور ان میں سے بعض کو بعض پر ہم نے درجات میں فوقیت دے دی ہے تاکہ بعض لوگ دوسروں کو خدمت گار بنا سکیں اور آپ کے رب کی رحمت کہیں بہتر ہے ان چیزوں سے جو یہ لوگ جمع کر رہے ہیں');
INSERT INTO "bayan" VALUES(43,33,'43:33','اور اگر یہ (اندیشہ) نہ ہوتا کہ تمام لوگ ایک ہی امت بن جائیں گے تو جو لوگ رحمن کا کفر کرتے ہم ان کے لیے بنا دیتے ان کے گھروں کی چھتیں چاندی کی اور سیڑھیاں بھی جن پر وہ چڑھتے');
INSERT INTO "bayan" VALUES(43,34,'43:34','اور ان کے گھروں کے دروازے اور وہ تخت بھی (چاندی کے ہوتے) جن پر وہ تکیے لگا کر بیٹھتے');
INSERT INTO "bayan" VALUES(43,35,'43:35','اور سونے کی بھی (بنا دیتے) اور یہ سب کچھ تو بس دنیا کی زندگی کا سازو سامان ہے اور آخرت (کی کامیابی) آپ کے رب کے نزدیک صرف اہل ِتقویٰ کے لیے ہے');
INSERT INTO "bayan" VALUES(43,36,'43:36','اور جو کوئی ُ منہ پھیر لے رحمان کے ذکر سے اس پر ہم ایک شیطان مسلط کردیتے ہیں تو وہ اس کا ساتھی بنا رہتا ہے');
INSERT INTO "bayan" VALUES(43,37,'43:37','اور وہ (شیاطین) ان کو روکتے ہیں سیدھے راستے سے اور وہ سمجھتے ہیں کہ وہ ہدایت یافتہ ہیں');
INSERT INTO "bayan" VALUES(43,38,'43:38','یہاں تک کہ جب وہ ہمارے پاس آئے گا تو (اپنے ساتھی شیطان سے) کہے گا کہ کاش میرے اور تمہارے درمیان مشرق و مغرب کا فاصلہ ہوتا ُ تو تو بہت ہی برا ساتھی ہے');
INSERT INTO "bayan" VALUES(43,39,'43:39','اور جب کہ تم ظلم کرتے رہے ہو تو آج کے دن یہ بات تمہیں کچھ فائدہ نہیں دے گی کہ تم سب عذاب میں شریک ہو');
INSERT INTO "bayan" VALUES(43,40,'43:40','تو (اے نبی صلى الله عليه وسلم !) کیا آپ بہروں کو سنائیں گے یا آپ اندھوں کو راہ دکھائیں گے اور ان کو جو کھلی گمراہی میں مبتلا ہیں');
INSERT INTO "bayan" VALUES(43,41,'43:41','تو اگر ہم آپ کو لے بھی جائیں تب بھی ان سے تو ہم انتقام لے کر ہی رہیں گے');
INSERT INTO "bayan" VALUES(43,42,'43:42','یا (یہ بھی ہوسکتا ہے کہ) ہم آپ صلى الله عليه وسلم کو دکھا دیں وہ کچھ جس کا ہم نے ان سے وعدہ کیا ہے ہمیں یقینا ان پر پوری قدرت حاصل ہے');
INSERT INTO "bayan" VALUES(43,43,'43:43','تو (اے نبی صلى الله عليه وسلم !) آپ مضبوطی سے تھام لیجیے اس کو جو آپ کی طرف وحی کیا گیا ہے یقینا آپ سیدھے راستے پر ہیں');
INSERT INTO "bayan" VALUES(43,44,'43:44','اور یہ (قرآن) آپ کے لیے بھی یاد دہانی ہے اور آپ کی قوم کے لیے بھی اور عنقریب آپ سب سے پوچھ گچھ ہوگی');
INSERT INTO "bayan" VALUES(43,45,'43:45','اور آپ پوچھ لیجیے ان سے جن کو ہم نے آپ سے پہلے بھیجا تھا اپنے رسولوں میں سے کیا ہم نے رحمان کے سوا کوئی اور بھی ایسے معبود بنائے ہیں کہ جن کی پوجا کی جائے ؟');
INSERT INTO "bayan" VALUES(43,46,'43:46','اور ہم نے بھیجا تھا موسیٰ ؑ کو اپنی نشانیوں کے ساتھ فرعون اور اس کے سرداروں کی طرف تو اس نے کہا کہ (دیکھو) میں تمام جہانوں کے پروردگار کا بھیجا ہوا ہوں');
INSERT INTO "bayan" VALUES(43,47,'43:47','تو جب وہ آیا ان کے پاس ہماری نشانیاں لے کر تو وہ ان (نشانیوں) پر ہنسنے لگے');
INSERT INTO "bayan" VALUES(43,48,'43:48','اور نہیں دکھاتے تھے ہم انہیں کوئی نشانی مگر وہ اپنی بہن (اپنے جیسی پہلی نشانی) سے زیادہ بڑی ہوتی تھی اور ہم انہیں پکڑتے رہے اس عذاب کے ذریعے شاید کہ وہ باز آجائیں');
INSERT INTO "bayan" VALUES(43,49,'43:49','اور (ہر عذاب کے موقع پر) وہ کہتے : اے جادوگر ! تم اپنے رب سے ہمارے لیے دعا کرو اس عہد کی بنا پر جو اس نے تم سے کر رکھا ہے ہم ضرور ہدایت پر آجائیں گے');
INSERT INTO "bayan" VALUES(43,50,'43:50','پھر جب ہم دور کردیتے تھے ان سے وہ عذاب تو اسی وقت وہ وعدے سے پھرجاتے تھے');
INSERT INTO "bayan" VALUES(43,51,'43:51','اور فرعون نے اپنی قوم میں ڈھنڈورا پٹوا دیا اس نے کہا کہ اے میری قوم کے لوگو ! کیا مصر کی حکومت میری نہیں ہے ؟ اور یہ نہریں میرے نیچے نہیں بہہ رہی ہیں ؟ تو کیا تم دیکھتے نہیں ؟');
INSERT INTO "bayan" VALUES(43,52,'43:52','کیا میں بہتر نہیں ہوں اس سے جو ایک بےعزت آدمی ہے ؟ اور صاف بات بھی نہیں کرسکتا');
INSERT INTO "bayan" VALUES(43,53,'43:53','تو کیوں نہیں اتارے گئے اس پر سونے کے کنگن ؟ یا اس کے ساتھ فرشتے چلے آ رہے ہوتے صفیں باندھے ہوئے');
INSERT INTO "bayan" VALUES(43,54,'43:54','تو (اس طرح) اس نے اپنی قوم کی مت مار دی اور انہوں نے اسی کا کہنا مانا یقینا وہ لوگ خود بھی نافرمان ہی تھے');
INSERT INTO "bayan" VALUES(43,55,'43:55','توجب انہوں نے ہمیں غصہ دلایا تو ہم نے ان سے انتقام لیا پس ہم نے ان سب کو غرق کردیا');
INSERT INTO "bayan" VALUES(43,56,'43:56','چناچہ ہم نے انہیں بنا دیا ماضی کی داستان اور بعد والوں کے لیے ایک نشان عبرت');
INSERT INTO "bayan" VALUES(43,57,'43:57','اور جب (قرآن میں) ابن ِمریم کی مثال بیان کی جاتی ہے تو اس پر آپ کی قوم چلانے ّلگتی ہے');
INSERT INTO "bayan" VALUES(43,58,'43:58','اور وہ کہتے ہیں کہ ہمارے معبود بہتر ہیں یا وہ ؟ یہ باتیں وہ آپ سے نہیں کرتے مگر صرف جھگڑنے کو۔ حقیقت یہ ہے کہ یہ ہیں ہی جھگڑالو لوگ');
INSERT INTO "bayan" VALUES(43,59,'43:59','وہ تو بس ہمارا ایک بندہ تھا جس پر ہم نے خاص انعام فرمایا اور ہم نے اس کو ایک نشانی بنا دیا بنی اسرائیل کے لیے');
INSERT INTO "bayan" VALUES(43,60,'43:60','اور اگر ہم چاہیں تو تم میں سے فرشتے بنا دیں جو زمین میں تمہارے جانشین ہوں');
INSERT INTO "bayan" VALUES(43,61,'43:61','اور یقینا وہ قیامت کی ایک علامت ہے تو اس (قیامت) کے بارے میں تم ہرگز شک نہ کرو اور میری پیروی کرو۔ یہی سیدھا راستہ ہے');
INSERT INTO "bayan" VALUES(43,62,'43:62','اور (دیکھو) شیطان تمہیں روک نہ دے بیشک وہ تمہارا کھلا دشمن ہے');
INSERT INTO "bayan" VALUES(43,63,'43:63','اور جب آئے عیسیٰ ؑ ٰ واضح نشانیاں لے کر تو انہوں نے کہا کہ میں تمہارے پاس حکمت لے کر آیا ہوں اور تاکہ میں واضح کردوں تمہارے لیے بعض وہ چیزیں جن میں تم اختلاف کر رہے ہو پس تم اللہ کا تقویٰ اختیار کرو اور میری اطاعت کرو');
INSERT INTO "bayan" VALUES(43,64,'43:64','یقینا اللہ ہی ہے جو میرا بھی رب ہے اور تمہارا بھی رب ہے پس تم اسی کی بندگی کرو۔ یہی سیدھا راستہ ہے');
INSERT INTO "bayan" VALUES(43,65,'43:65','پھر ان میں سے کئی گروہ باہم اختلافات میں مبتلا ہوگئے پس تباہی اور بربادی ہے ان ظالموں کے لیے ایک دردناک دن کے عذاب سے');
INSERT INTO "bayan" VALUES(43,66,'43:66','یہ لوگ کس چیز کا انتظار کر رہے ہیں سوائے قیامت کے کہ وہ آجائے ان پر اچانک اور انہیں خبر بھی نہ ہو');
INSERT INTO "bayan" VALUES(43,67,'43:67','بہت گہرے دوست اس دن ایک دوسرے کے دشمن بن جائیں گے سوائے متقین کے');
INSERT INTO "bayan" VALUES(43,68,'43:68','ان سے کہا جائے گا :) اے میرے بندو ! آج تم پر نہ تو کوئی خوف ہے اور نہ ہی تم رنجیدہ ہوگے');
INSERT INTO "bayan" VALUES(43,69,'43:69','یعنی وہ لوگ جو ہماری آیات پر ایمان لائے اور فرماں بردار تھے');
INSERT INTO "bayan" VALUES(43,70,'43:70','داخل ہو جائو جنت میں تم بھی اور تمہاری بیویاں بھی تمہارا اعزازو اکرام کیا جائے گا');
INSERT INTO "bayan" VALUES(43,71,'43:71','گردش کرائے جائیں گے ان پر سونے کی طشتریاں اور پیالے اور وہاں ہوگا وہ سب کچھ جس کی طلب ہوگی انسان کے نفس کو اور جس سے نگاہیں لذت حاصل کریں گی اور تم اب اس میں ہمیشہ ہمیش رہو گے');
INSERT INTO "bayan" VALUES(43,72,'43:72','اور یہ وہ جنت ہے جس کے تم وارث بنائے گئے ہو اُن اَعمال کی بنا پر جو تم کرتے تھے');
INSERT INTO "bayan" VALUES(43,73,'43:73','اس میں تمہارے لیے بکثرت پھل موجود ہیں جن میں سے تم کھاتے ہو');
INSERT INTO "bayan" VALUES(43,74,'43:74','(اس کے برعکس) جو مجرم ہوں گے وہ جہنم کے عذاب میں ہمیشہ رہیں گے');
INSERT INTO "bayan" VALUES(43,75,'43:75','وہ ان سے ہلکا نہ کیا جائے گا اور وہ اسی میں پڑے رہیں گے بالکل ناامیدہو کر');
INSERT INTO "bayan" VALUES(43,76,'43:76','اور ہم نے ان پر ظلم نہیں کیا بلکہ وہ خود ہی اپنی جانوں پر ظلم کرتے تھے');
INSERT INTO "bayan" VALUES(43,77,'43:77','اور وہ ندا کریں گے کہ اے مالک ! اب تو آپ کا رب ہمارا کام ہی تمام کر دے وہ جواب دے گا کہ تمہیں تو اب ہمیشہ (یہیں) رہنا ہے');
INSERT INTO "bayan" VALUES(43,78,'43:78','ہم تو تمہارے پاس حق لے کر آئے تھے لیکن تمہاری اکثریت حق کو ناپسند کرتی ہے۔');
INSERT INTO "bayan" VALUES(43,79,'43:79','کیا انہوں نے کوئی قطعی فیصلہ کرلیا ہے ؟ تو ہم بھی ایک قطعی فیصلہ کیے لیتے ہیں');
INSERT INTO "bayan" VALUES(43,80,'43:80','کیا وہ سمجھتے ہیں کہ ہم ان کی خفیہ باتوں اور راز دارانہ مشوروں کو سنتے نہیں ہیں کیوں نہیں (ہم سب کچھ سن رہے ہیں) اور ہمارے ایلچی ان کے پاس ہی لکھ رہے ہوتے ہیں');
INSERT INTO "bayan" VALUES(43,81,'43:81','(اے نبی صلى الله عليه وسلم !) آپ ان سے کہیے کہ اگر رحمن کا کوئی بیٹا ہوتا تو سب سے پہلا اس کی عبادت کرنے والا میں ہوتا');
INSERT INTO "bayan" VALUES(43,82,'43:82','پاک ہے آسمانوں اور زمین کا رب عرش کا مالک ان تمام چیزوں سے جو یہ بیان کرتے ہیں');
INSERT INTO "bayan" VALUES(43,83,'43:83','تو (اے نبی صلى الله عليه وسلم !) آپ ان کو چھوڑ دیجئے یہ میں میخ نکالتے رہیں اور لہو و لعب میں پڑے رہیں یہاں تک کہ یہ ملاقات کرلیں اپنے اس دن سے جس کا ان سے وعدہ کیا جا رہا ہے');
INSERT INTO "bayan" VALUES(43,84,'43:84','اور وہی ہے جو آسمان میں بھی الٰہ ہے اور زمین میں بھی اِلٰہ ہے۔ اور وہ بہت حکمت والا ہرچیز کا علم رکھنے والا ہے');
INSERT INTO "bayan" VALUES(43,85,'43:85','اور بہت بابرکت ہے وہ ذات جس کے اختیار میں ہے بادشاہی آسمانوں کی اور زمین کی اور ان دونوں کے درمیان کی ساری چیزوں کی اور اسی کے پاس ہے قیامت کا علم اور اسی کی طرف تم لوٹا دیے جائو گے');
INSERT INTO "bayan" VALUES(43,86,'43:86','اور کوئی اختیار نہیں رکھتے کسی بھی شفاعت کا جن کو یہ پکار رہے ہیں اللہ کے سوا سوائے اس کے کہ جو حق کی گواہی دیں اور وہ جانتے ہوں');
INSERT INTO "bayan" VALUES(43,87,'43:87','اور اگر آپ ان سے پوچھیں کہ ان کو کس نے پیدا کیا تو کہیں گے اللہ نے تو پھر یہ کہاں سے پھیر دیے جاتے ہیں');
INSERT INTO "bayan" VALUES(43,88,'43:88','اور قسم ہے نبی صلى الله عليه وسلم کے اس قول کی کہ اے میرے رب ! یہ وہ لوگ ہیں جو ایمان نہیں لا رہے');
INSERT INTO "bayan" VALUES(43,89,'43:89','تو (اے نبی صلى الله عليه وسلم !) آپ ان سے در گزر کیجیے اور کہیے سلام ہے تو بہت جلد یہ لوگ جان جائیں گے');
INSERT INTO "bayan" VALUES(44,1,'44:1','ح م۔');
INSERT INTO "bayan" VALUES(44,2,'44:2','قسم ہے اس روشن کتاب کی');
INSERT INTO "bayan" VALUES(44,3,'44:3','یقینا ہم نے اس کو نازل کیا ہے ایک مبارک رات میں یقینا ہم خبردار کردینے والے ہیں');
INSERT INTO "bayan" VALUES(44,4,'44:4','اس رات میں تمام ُ پر حکمت امور کے فیصلے صادر کیے جاتے ہیں');
INSERT INTO "bayan" VALUES(44,5,'44:5','طے شدہ احکام ہماری طرف سے۔ یقینا ہم ہی ہیں (رسولوں کو) بھیجنے والے');
INSERT INTO "bayan" VALUES(44,6,'44:6','رحمت کے طور پر آپ کے رب کی طرف سے۔ یقینا وہی ہے سب کچھ سننے والا سب کچھ جاننے والا');
INSERT INTO "bayan" VALUES(44,7,'44:7','آسمانوں اور زمین کا رب اور جو کچھ ان کے مابین ہے۔ اگر تم یقین کرنے والے ہو');
INSERT INTO "bayan" VALUES(44,8,'44:8','اس کے سوا کوئی معبود نہیں وہی زندہ رکھتا ہے اور وہی مارتا ہے وہ تمہارا بھی رب ہے اور تمہارے پہلے آباء واجداد کا بھی رب ہے');
INSERT INTO "bayan" VALUES(44,9,'44:9','لیکن یہ لوگ شک میں پڑے ہوئے کھیل رہے ہیں');
INSERT INTO "bayan" VALUES(44,10,'44:10','تو آپ انتظار کیجیے اس دن کا جس دن آسمان لائے گا واضح دھواں');
INSERT INTO "bayan" VALUES(44,11,'44:11','وہ ڈھانپ لے گا لوگوں کو۔ یہ ایک درد ناک عذاب ہوگا');
INSERT INTO "bayan" VALUES(44,12,'44:12','(اُس وقت لوگ کہیں گے :) اے ہمارے رب ! اس عذاب کو ہم سے دور کر دے ہم ایمان لانے والے ہیں');
INSERT INTO "bayan" VALUES(44,13,'44:13','کہاں ہے ان کے لیے یاد دہانی کی استعداد جبکہ آچکا ہے ان کے پاس ایک واضح کردینے والا رسول');
INSERT INTO "bayan" VALUES(44,14,'44:14','پھر انہوں نے منہ پھیرلیا اس کی طرف سے اور کہا کہ یہ تو سکھایا پڑھایا ہوا مجنون ہے');
INSERT INTO "bayan" VALUES(44,15,'44:15','ہم دور کردیں گے اس عذاب کو تھوڑی دیر کے لیے تو تم لوٹ کر وہی کچھ کرو گے جو پہلے کر رہے تھے');
INSERT INTO "bayan" VALUES(44,16,'44:16','جس دن ہم پکڑیں گے بڑی پکڑ (اُس دن) یقینا ہم پورا پورا انتقام لیں گے');
INSERT INTO "bayan" VALUES(44,17,'44:17','اور ہم نے آزمایا تھا ان سے پہلے قوم فرعون کو بھی اور ان کے پاس بھی ایک بہت معزز رسول آیا تھا');
INSERT INTO "bayan" VALUES(44,18,'44:18','(اس پیغام کے ساتھ) کہ تم اللہ کے بندوں کو میرے حوالے کر دو۔ یقینا میں تمہارے لیے ایک امانت دار رسول ہوں');
INSERT INTO "bayan" VALUES(44,19,'44:19','اور یہ کہ تم اللہ کے مقابلے میں سرکشی نہ کرو۔ میں تمہارے پاس لے کر آیا ہوں ایک واضح سند');
INSERT INTO "bayan" VALUES(44,20,'44:20','اور میں نے پناہ پکڑ لی ہے اپنے اور تمہارے رب کی اس بات سے کہ تم مجھے سنگسار کرو');
INSERT INTO "bayan" VALUES(44,21,'44:21','اور اگر تم لوگ میری بات نہیں مانتے ہو تو مجھ سے دوررہو');
INSERT INTO "bayan" VALUES(44,22,'44:22','پھر اس نے اپنے رب سے دعا کی کہ یہ مجرم قوم ہے');
INSERT INTO "bayan" VALUES(44,23,'44:23','پس میرے بندوں کو لے کر راتوں رات نکل جائو آگاہ رہو کہ تمہارا پیچھا کیا جائے گا');
INSERT INTO "bayan" VALUES(44,24,'44:24','اور چھوڑ دینا سمندر کو ساکن یہ ایک لشکر ہیں جو غرق کیے جائیں گے');
INSERT INTO "bayan" VALUES(44,25,'44:25','انہوں نے کتنے ہی باغوں اور چشموں کو چھوڑا');
INSERT INTO "bayan" VALUES(44,26,'44:26','اور کھیتیوں اور شاندار رہائش گاہوں کو');
INSERT INTO "bayan" VALUES(44,27,'44:27','اور کیسی کیسی نعمتوں کو جن میں وہ عیش کیا کرتے تھے');
INSERT INTO "bayan" VALUES(44,28,'44:28','یوں ہی ہوا اور ہم نے وارث بنا دیا ان چیزوں کا دوسرے لوگوں کو');
INSERT INTO "bayan" VALUES(44,29,'44:29','تو نہ ان پر آسمان رویا اور نہ زمین اور نہ ہی انہیں کوئی مہلت دی گئی');
INSERT INTO "bayan" VALUES(44,30,'44:30','اور ہم نے نجات دے دی بنی اسرائیل کو ذلت آمیز عذاب سے');
INSERT INTO "bayan" VALUES(44,31,'44:31','یعنی فرعون سے یقینا وہ بڑا ہی سرکش حد سے نکل جانے والوں میں سے تھا');
INSERT INTO "bayan" VALUES(44,32,'44:32','اور ہم نے علم رکھنے کے باوجود اُن (بنی اسرائیل) کو اقوامِ عالم پر ترجیح دی تھی');
INSERT INTO "bayan" VALUES(44,33,'44:33','اور ہم نے انہیں بہت سی ایسی نشانیاں دیں جن میں بہت واضح آزمائش تھی');
INSERT INTO "bayan" VALUES(44,34,'44:34','یہ لوگ تو یہی کہہ رہے ہیں');
INSERT INTO "bayan" VALUES(44,35,'44:35','کچھ نہیں مگر ہماری یہ پہلی موت ہی ہوگی اور ہم (دوبارہ) اٹھائے جانے والے نہیں ہیں');
INSERT INTO "bayan" VALUES(44,36,'44:36','تو لائو ذرا ہمارے آباء و اَجداد کو اگر تم سچے ہو');
INSERT INTO "bayan" VALUES(44,37,'44:37','کیا یہ بہتر ہیں یا ُ تبع ّکی قوم اور وہ لوگ جو ان سے پہلے تھے ؟ ہم نے ان سب کو ہلاک کردیا بیشک وہ سب کے سب مجرم تھے');
INSERT INTO "bayan" VALUES(44,38,'44:38','اور ہم نے آسمانوں اور زمین کو اور جو کچھ ان کے مابین ہے محض کھیل کے طور پر تو تخلیق نہیں فرمایا');
INSERT INTO "bayan" VALUES(44,39,'44:39','اور ہم نے نہیں بنایا ان دونوں کو مگر حق کے ساتھ مگر ان لوگوں کی اکثریت علم نہیں رکھتی');
INSERT INTO "bayan" VALUES(44,40,'44:40','یقینا فیصلے کا دن ان سب کا وقت ِمعین ّہے');
INSERT INTO "bayan" VALUES(44,41,'44:41','جس دن کوئی دوست کسی دوست کے کچھ کام نہیں آسکے گا اور نہ ہی ان کی کوئی مدد کی جائے گی');
INSERT INTO "bayan" VALUES(44,42,'44:42','سوائے اس کے جس پر اللہ رحم فرما دے۔ یقینا وہی ہے زبردست بہت رحم فرمانے والا');
INSERT INTO "bayan" VALUES(44,43,'44:43','یقینا زقوم کا درخت');
INSERT INTO "bayan" VALUES(44,44,'44:44','گنہگاروں کا کھانا ہے');
INSERT INTO "bayan" VALUES(44,45,'44:45','پگھلے ہوئے تانبے کی طرح کھولے گا پیٹوں کے اندر');
INSERT INTO "bayan" VALUES(44,46,'44:46','جیسے گرم پانی کھولتا ہے');
INSERT INTO "bayan" VALUES(44,47,'44:47','(پھر کہا جائے گا :) پکڑو اس کو اور دھکیلتے ہوئے لے جائو اسے عین جہنم کے بیچوں بیچ');
INSERT INTO "bayan" VALUES(44,48,'44:48','پھر انڈیل دو اس کے سر کے اوپر کھولتے ہوئے پانی کا عذاب');
INSERT INTO "bayan" VALUES(44,49,'44:49','اب چکھو ! تم تو بڑے طاقتور بہت معزز تھے نا');
INSERT INTO "bayan" VALUES(44,50,'44:50','یقینایہی ہے وہ چیز جس کے بارے میں تم شک کرتے تھے');
INSERT INTO "bayan" VALUES(44,51,'44:51','(اس کے برعکس) متقین بڑی امن والی جگہ میں ہوں گے');
INSERT INTO "bayan" VALUES(44,52,'44:52','باغات اور چشموں میں');
INSERT INTO "bayan" VALUES(44,53,'44:53','باریک اور موٹے ریشم کا لباس پہنیں گے ایک دوسرے کی طرف رخ کیے ہوئے (بیٹھے ہوں گے)');
INSERT INTO "bayan" VALUES(44,54,'44:54','اسی طرح ہوگا اور ہم ان سے بیاہ دیں گے بڑی بڑی آنکھوں والی حوریں');
INSERT INTO "bayan" VALUES(44,55,'44:55','وہ طلب کریں گے اس میں ہر طرح کے پھل اطمینان کے ساتھ');
INSERT INTO "bayan" VALUES(44,56,'44:56','وہ نہیں چکھیں گے اس میں موت سوائے اس پہلی موت کے اور اللہ نے انہیں بچالیا ہے جہنم کے عذاب سے');
INSERT INTO "bayan" VALUES(44,57,'44:57','یہ فضل ہوگا آپ کے رب کی طرف سے۔ یقینا یہ بہت بڑی کامیابی ہے');
INSERT INTO "bayan" VALUES(44,58,'44:58','تو (اے نبی صلى الله عليه وسلم !) ہم نے اس قرآن کو آسان کردیا ہے آپ کی زبان پر تاکہ یہ لوگ نصیحت حاصل کریں');
INSERT INTO "bayan" VALUES(44,59,'44:59','تو آپ صلى الله عليه وسلم انتظار کیجیے یہ بھی انتظار کر رہے ہیں');
INSERT INTO "bayan" VALUES(45,1,'45:1','ح م۔');
INSERT INTO "bayan" VALUES(45,2,'45:2','اس کتاب کا اتارا جانا ہے اس اللہ کی طرف سے جو زبردست کمال حکمت والا ہے');
INSERT INTO "bayan" VALUES(45,3,'45:3','یقینا آسمانوں اور زمین میں (بہت سی) نشانیاں ہیں ماننے والوں کے لیے');
INSERT INTO "bayan" VALUES(45,4,'45:4','اور تمہاری تخلیق میں اور جو اس نے پھیلادیے ہیں (زمین میں) جاندار ان میں بھی نشانیاں ہیں ان لوگوں کے لیے جو یقین کریں');
INSERT INTO "bayan" VALUES(45,5,'45:5','اور رات اور دن کے اختلاف میں اور اس میں جو اللہ آسمان سے رزق اتارتا ہے پھر زندہ کردیتا ہے اس سے زمین کو اس کی موت کے بعد اور ہوائوں کے چلنے میں نشانیاں ہیں ان لوگوں کے لیے جو عقل سے کام لیں');
INSERT INTO "bayan" VALUES(45,6,'45:6','یہ اللہ کی آیات ہیں جو ہم پڑھ کر سنا رہے ہیں آپ کو حق کے ساتھ تو اب اللہ اور اس کی آیات کے بعد اور کون سی بات ہے جس پر یہ لوگ ایمان لائیں گے ؟');
INSERT INTO "bayan" VALUES(45,7,'45:7','بربادی ہے ہر جھوٹ گھڑنے والے گنہگار کے لیے');
INSERT INTO "bayan" VALUES(45,8,'45:8','وہ سنتا ہے اللہ کی وہ آیات جو اسے پڑھ کر سنائی جاتی ہیں پھر وہ اڑ جاتا ہے ضد پر تکبر ّکرتے ہوئے جیسے کہ اس نے انہیں سنا ہی نہ ہو تو آپ صلى الله عليه وسلم اسے ایک دردناک عذاب کی بشارت دے دیجئے');
INSERT INTO "bayan" VALUES(45,9,'45:9','اور جب ہماری آیات میں سے کوئی بات اس کے علم میں آتی ہے تو وہ اس کا مذاق اڑاتا ہے۔ یہی لوگ ہیں جن کے لیے اہانت آمیز عذاب ہوگا');
INSERT INTO "bayan" VALUES(45,10,'45:10','ان کے پیچھے جہنم ہے اور جو کچھ انہوں نے کمایا ہے وہ ان کے ذرا بھی کام نہیں آسکے گا اور نہ وہ جن کو انہوں نے اللہ کے سوا مدد گار بنا رکھا ہے اور ان کے لیے بہت بڑا عذاب ہوگا');
INSERT INTO "bayan" VALUES(45,11,'45:11','یہ (قرآن) ہے ہدایت۔ اور جن لوگوں نے اپنے رب کی آیات کا کفر کیا ان کے لیے بہت ہی دردناک عذاب ہوگا');
INSERT INTO "bayan" VALUES(45,12,'45:12','اللہ ہی ہے جس نے مسخر کردیا تمہارے لیے سمندر کو تاکہ اس میں کشتیاں چلیں اس کے حکم سے تاکہ تم تلاش کرو اس کا فضل اور تاکہ تم شکر ادا کرو');
INSERT INTO "bayan" VALUES(45,13,'45:13','اور اس نے مسخر کردیا تمہارے لیے اپنی طرف سے جو کچھ آسمانوں میں اور جو کچھ زمین میں ہے سب کچھ یقینا اس میں نشانیاں ہیں ان لوگوں کے لیے جو غور و فکر سے کام لیں');
INSERT INTO "bayan" VALUES(45,14,'45:14','(اے نبی صلى الله عليه وسلم !) آپ اہل ِایمان سے کہہ دیجیے کہ وہ ذرا درگزر کریں ان لوگوں سے جو اللہ کے دنوں کی توقع نہیں رکھتے تاکہ اللہ بدلہ دے ایک قوم کو ان کی اپنی کمائی کے مطابق');
INSERT INTO "bayan" VALUES(45,15,'45:15','جس کسی نے اچھا کام کیا تو اس نے اپنے ہی (بھلے کے) لیے کیا اور جس کسی نے برا کام کیا تو اس کا وبال بھی اسی پر ہوگا پھر تم اپنے رب کی طرف لوٹا دیے جائو گے');
INSERT INTO "bayan" VALUES(45,16,'45:16','اور ہم نے بنی اسرائیل کو عطا کی کتاب حکومت اور نبوت اور ہم نے انہیں عطا کیں بہت سی پاکیزہ چیزیں اور انہیں ہم نے تمام جہان کی قوموں پر فضیلت دی');
INSERT INTO "bayan" VALUES(45,17,'45:17','اور ہم نے انہیں عطا کیں دین کے معاملہ میں واضح ہدایات اور انہوں نے نہیں اختلاف کیا مگر اس کے بعد کہ ان کے پاس علم آچکا تھا محض باہمی ِضدم ّضدا کے سبب سے یقینا آپ صلى الله عليه وسلم کا رب فیصلہ کر دے گا ان کے مابین قیامت کے دن ان تمام باتوں کا جن میں وہ اختلاف کرتے رہے ہیں');
INSERT INTO "bayan" VALUES(45,18,'45:18','پھر (اے نبی صلى الله عليه وسلم !) ہم نے آپ کو قائم کردیا دین کے معاملہ میں ایک صاف شاہراہ (شریعت) پر تو آپ اسی کی پیروی کریں اور ان لوگوں کی خواہشات کی پیروی نہ کیجیے جن کے پاس کوئی علم ہی نہیں ہے');
INSERT INTO "bayan" VALUES(45,19,'45:19','یہ لوگ اللہ کے مقابلے میں آپ کے کچھ بھی کام نہیں آسکیں گے اور یقینا یہ ظالم (مشرک) لوگ ایک دوسرے کے مدد گار ہیں جبکہ اللہ مددگار ہے متقین کا');
INSERT INTO "bayan" VALUES(45,20,'45:20','یہ ہیں آنکھیں کھولنے والی باتیں لوگوں کے لیے اور ہدایت اور رحمت ان لوگوں کے لیے جو ان پر یقین کریں');
INSERT INTO "bayan" VALUES(45,21,'45:21','جو لوگ ُ بری باتوں کا ارتکاب کر رہے ہیں کیا انہوں نے یہ سمجھ رکھا ہے کہ ہم انہیں ان لوگوں کی طرح کردیں گے جو ایمان لائے اور جنہوں نے نیک عمل کیے ؟ کہ ان کی زندگی اور موت ایک جیسی ہوجائے ؟ بہت برا فیصلہ ہے جو یہ لوگ کر رہے ہیں');
INSERT INTO "bayan" VALUES(45,22,'45:22','اور اللہ نے تو آسمان اور زمین کو حق (عدل) کے ساتھ پیدا کیا ہے تاکہ پورا پورا بدلہ دیا جائے ہر جان کو جو کچھ اس نے کمایا ہو اور ان پر کوئی ظلم نہ ہو');
INSERT INTO "bayan" VALUES(45,23,'45:23','کیا آپ نے دیکھا اس شخص کو جس نے اپنی خواہش ِ نفس کو اپنا معبود بنا رکھا ہے ؟ اور اللہ نے اسے گمراہ کر رکھا ہے اس کے علم کے باوجود اور اللہ نے اس کی سماعت اور اس کے دل پر مہر لگا دی ہے اور اس کی آنکھوں پر پردہ ڈال دیا ہے تو اللہ کے (اس فیصلے کے) بعداب کون اسے ہدایت دے سکتا ہے تو کیا تم لوگ نصیحت حاصل نہیں کرتے ؟');
INSERT INTO "bayan" VALUES(45,24,'45:24','وہ کہتے ہیں کہ نہیں ہے (کوئی اور زندگی) سوائے ہماری دنیا کی زندگی کے ہم خود ہی مرتے ہیں اور خود ہی جیتے ہیں اور ہمیں نہیں ہلاک کرتا مگر زمانہ حالانکہ ان کے پاس (اس بارے میں) کوئی علم نہیں ہے وہ تو صرف ظن سے کام لے رہے ہیں');
INSERT INTO "bayan" VALUES(45,25,'45:25','اور جب انہیں پڑھ کر سنائی جاتی ہیں ہماری روشن آیات تو نہیں ہوتی ان کی کوئی دلیل سوائے اس کے کہ وہ کہتے ہیں کہ (زندہ کر کے) لے آئو ہمارے آباء و اَجداد کو اگر تم سچے ہو');
INSERT INTO "bayan" VALUES(45,26,'45:26','(اے نبی صلى الله عليه وسلم !) آپ کہیے کہ اللہ ہی تمہیں زندہ کرتا ہے پھر وہی تمہیں موت دے گا پھر تمہیں جمع کرے گا قیامت کے دن جس میں کوئی شک نہیں لیکن اکثر لوگ جانتے نہیں ہیں');
INSERT INTO "bayan" VALUES(45,27,'45:27','اور اللہ ہی کے لیے ہے آسمانوں اور زمین کی بادشاہی اور جس دن قیامت قائم ہوگی اس دن جھٹلانے والے بڑے خسارے میں ہوں گے');
INSERT INTO "bayan" VALUES(45,28,'45:28','اور تم دیکھو گے کہ ہر امت گھٹنوں کے بل پڑی ہوگی ہر امت کو بلایا جائے گا اس کے اعمال نامے کی طرف آج تمہیں بدلہ دیا جائے گا ان اعمال کا جو تم کرتے رہے ہو');
INSERT INTO "bayan" VALUES(45,29,'45:29','یہ ہمارا (تیار کردہ) اعمال نامہ ہے جو تمہارے اوپر حق کے ساتھ گواہی دے گا جو کچھ تم کیا کرتے تھے ہم اسے لکھواتے جا رہے تھے۔');
INSERT INTO "bayan" VALUES(45,30,'45:30','پس جو لوگ ایمان لائے ہوں گے اور جنہوں نے نیک اعمال کیے ہوں گے تو ان کو داخل کر دے گا ان کا پروردگار اپنی رحمت میں۔ یہ بہت واضح کامیابی ہوگی');
INSERT INTO "bayan" VALUES(45,31,'45:31','اور وہ لوگ جنہوں نے کفر کیا تھا (ان سے اللہ پوچھے گا کہ) کیا تمہیں میری آیات پڑھ کر نہیں سنائی جاتی تھیں ؟ تو تم نے استکبار کیا اور تم مجرم لوگ تھے');
INSERT INTO "bayan" VALUES(45,32,'45:32','اور جب کہا جاتا کہ اللہ کا وعدہ سچ ہے اور قیامت آنے میں کوئی شک نہیں تو تم کہتے کہ ہم نہیں جانتے کہ قیامت کیا ہوتی ہے ہاں ہمیں ایک گمان سا تو ہوتا ہے اور ہم اس کا یقین کرنے والے نہیں ہیں');
INSERT INTO "bayan" VALUES(45,33,'45:33','اور ان کے سامنے آجائیں گے ان کے وہ تمام ُ برے اعمال جو انہوں نے کیے تھے اور انہیں گھیرے میں لے لے گی وہی چیز جس کا وہ استہزا کیا کرتے تھے');
INSERT INTO "bayan" VALUES(45,34,'45:34','اور ان سے کہہ دیا جائے گا کہ آج ہم تمہیں اسی طرح نظر انداز کردیں گے جیسے تم نے اس دن کی ملاقات کو نظر انداز کررکھا تھا اور تمہارا ٹھکانہ اب آگ ہے اور تمہارے لیے کوئی مدد گار نہیں ہے');
INSERT INTO "bayan" VALUES(45,35,'45:35','یہ اس لیے کہ تم نے اللہ کی آیات کو مذاق بنا لیا تھا اور دنیا کی زندگی نے تمہیں دھوکے میں ڈالے رکھا تو آج انہیں نکالا نہیں جائے گا اس سے اور نہ ہی انہیں موقع دیا جائے گا کہ وہ توبہ کرلیں');
INSERT INTO "bayan" VALUES(45,36,'45:36','پس ُ کل تعریف اور کل شکر اللہ ہی کے لیے ہے جو آسمانوں کا اور زمین کا رب ہے اور تمام جہانوں کا رب ہے');
INSERT INTO "bayan" VALUES(45,37,'45:37','اور اسی کے لیے ہے بڑائی آسمانوں اور زمین میں۔ اور وہی ہے زبردست حکمت والا');
INSERT INTO "bayan" VALUES(46,1,'46:1','ح م۔');
INSERT INTO "bayan" VALUES(46,2,'46:2','اس کتاب کا اتارا جانا ہے اس اللہ کی طرف سے جو زبردست کمال حکمت والا ہے۔');
INSERT INTO "bayan" VALUES(46,3,'46:3','اور ہم نے نہیں پید ا کیا آسمانوں اور زمین کو اور جو کچھ ان دونوں کے مابین ہے مگر حق کے ساتھ اور ایک اجل معین کے لیے اور جن لوگوں نے کفر کیا ہے وہ اعراض کر رہے ہیں اس سے جس سے انہیں خبردار کیا جا رہا ہے۔');
INSERT INTO "bayan" VALUES(46,4,'46:4','(اے نبی صلى الله عليه وسلم !) ان سیکہیے کہ کبھی تم نے غور بھی کیا کہ جنہیں تم اللہ کے سوا پکارتے ہو مجھے دکھائو تو سہی کہ انہوں نے کیا پیدا کیا ہے زمین میں ؟ یا ان کی کوئی شراکت ہے آسمانوں میں ؟ } لائو میرے پاس کوئی کتاب اس سے پہلے کی یا کوئی ایسی روایت جس کی بنیاد علم پر ہو اگر تم سچے ہو !');
INSERT INTO "bayan" VALUES(46,5,'46:5','اور اس سے بڑھ کر ظالم اور کون ہوگا جو اللہ کے سوا اسے پکارتا ہے جو اس کو جواب ہی نہیں دے سکتا قیامت کے دن تک اور وہ ان کی دعا سے غافل ہیں۔');
INSERT INTO "bayan" VALUES(46,6,'46:6','اور جب لوگوں کو جمع کیا جائے گا تو وہ ان کے دشمن بن جائیں گے اور وہ ان کی عبادت کے منکر ہوں گے۔');
INSERT INTO "bayan" VALUES(46,7,'46:7','اور جب انہیں پڑھ کر سنائی جاتی ہیں ہماری آیت ِبینات ّتو یہ کافر کہتے ہیں حق کے بارے میں جبکہ وہ ان کے پاس آگیا کہ یہ تو کھلا جادو ہے۔');
INSERT INTO "bayan" VALUES(46,8,'46:8','کیا وہ کہتے ہیں کہ اس نے اس کو گھڑ لیا ہے ؟ (اے نبی صلى الله عليه وسلم !) ان سے کہیے کہ اگر میں نے اس کو گھڑ لیا ہے تو تم مجھے اللہ (کی پکڑ) سے ذرا بھی نہیں بچا سکتے۔ وہ خوب جانتا ہے جن باتوں میں تم لگے ہوئے ہو۔ } وہ کافی ہے بطور گواہ میرے اور تمہارے مابین اور وہ خوب بخشنے والا بہت رحم فرمانے والا ہے۔');
INSERT INTO "bayan" VALUES(46,9,'46:9','(اے نبی صلى الله عليه وسلم ! ان سے یہ بھی) کہیے کہ میں کوئی نیا رسول تو نہیں ہوں اور مجھے یہ معلوم نہیں کہ میرے ساتھ کیا معاملہ کیا جائے گا اور نہ یہ کہ تمہارے ساتھ کیا معاملہ کیا جائے گا } میں تو بس اسی کی پیروی کر رہا ہوں جو میری طرف وحی کی جا رہی ہے اور میں نہیں ہوں مگر ایک کھلا خبردار کردینے والا۔');
INSERT INTO "bayan" VALUES(46,10,'46:10','(اے نبی صلى الله عليه وسلم !) ان سے کہیے کہ کیا تم نے سوچا بھی ہے کہ اگر یہ (قرآن) واقعتا اللہ کی طرف سے ہوا اور تم نے اس کا انکار کردیا (تو اس کا نتیجہ کیا نکلے گا) ؟ اور گواہی دے چکا ہے ایک گواہ بنی اسرائیل میں سے ایک ایسی ہی کتاب کی۔ پس وہ تو ایمان لے آیا اور تم استکبار کر رہے ہو بیشک اللہ ظالموں کو ہدایت نہیں دیتا۔');
INSERT INTO "bayan" VALUES(46,11,'46:11','اور کہا ان لوگوں نے جنہوں نے کفر کی روش اختیار کی ان لوگوں کے بارے میں جو ایمان لائے کہ اگر یہ (قرآن) کوئی خیر ہوتا تو یہ لوگ اس کی طرف ہم سے سبقت نہ لے جاتے۔ } اور چونکہ انہوں نے اس (قرآن) کے ذریعے سے ہدایت نہیں پائی تو اب کہیں گے کہ یہ تو گھڑی ہوئی پرانی چیز ہے۔');
INSERT INTO "bayan" VALUES(46,12,'46:12','حالانکہ اس سے پہلے موسیٰ ؑ ٰ کی کتاب موجود ہے راہنمائی کرنے والی اور رحمت اور یہ کتاب اس کی تصدیق کرنے والی ہے عربی زبان میں تاکہ خبردار کر دے ان لوگوں کو جنہوں نے ظلم کی روش اختیار کی اور بشارت بن جائے احسان کی روش اختیار کرنے والوں کے لیے۔');
INSERT INTO "bayan" VALUES(46,13,'46:13','بیشک جن لوگوں نے اقرار کیا کہ ہمارا رب اللہ ہے پھر اس پر جم گئے تو ان پر نہ کوئی خوف ہوگا اور نہ وہ حزن سے دوچار ہوں گے۔');
INSERT INTO "bayan" VALUES(46,14,'46:14','یہی لوگ جنت والے ہیں اس میں ہمیشہ رہنے والے۔ یہ جزا ہوگی ان اعمال کی جو وہ کرتے رہے۔');
INSERT INTO "bayan" VALUES(46,15,'46:15','اور ہم نے تاکید کی انسان کو اس کے والدین کے بارے میں حسن سلوک کی۔ اور فیصلہ کردیا ہے آپ صلى الله عليه وسلم کے رب نے کہ تم نہیں عبادت کرو گے کسی کی سوائے اس کے اور والدین کے ساتھ احسان (کروگے)۔ اگر پہنچ جائیں تمہارے پاس بڑھاپے کو ان میں سے کوئی ایک یا دونوں تو مت کہو ان کو اف بھی اور مت جھڑکو ان کو اور بات کرو ان سے نرمی کے ساتھ۔ اس کی ماں نے اسے اٹھائے رکھا (پیٹ میں) تکلیف جھیل کر اور اسے جنا تکلیف کے ساتھ۔ اور اس کا یہ حمل اور دودھ چھڑانا ہے (لگ بھگ) تیس مہینے میں یہاں تک کہ جب وہ اپنی پوری قوت کو پہنچتا ہے اور چالیس برس کا ہوجاتا ہے وہ کہتا ہے : اے میرے پروردگار ! مجھے توفیق دے کہ میں شکر کرسکوں تیرے انعامات کا جو تو نے مجھ پر اور میرے والدین پر فرمائے۔ اور یہ کہ میں ایسے اعمال کروں جنہیں تو پسند کرے اور میرے لیے میری اولاد میں بھی اصلاح فرما دے۔ } میں تیری جناب میں توبہ کرتا ہوں اور یقینا میں (تیرے) فرمانبرداروں میں سے ہوں۔');
INSERT INTO "bayan" VALUES(46,16,'46:16','یہ وہ لوگ ہیں جن سے ہم ان کے بہترین اعمال قبول فرمائیں گے اور ہم ان کی خطائوں سے بھی درگزر فرمائیں گے (اور وہ ہوں گے) جنت والوں میں سے یہ وہ سچا وعدہ ہے جو ان سے کیا جاتا تھا۔');
INSERT INTO "bayan" VALUES(46,17,'46:17','اور ایک وہ شخص ہے جو اپنے والدین سے کہتا ہے کہ میں بیزار ہوں آپ دونوں سے کیا آپ مجھے اس سے ڈراتے ہیں کہ میں نکال کھڑا کیا جائوں گا (زندہ کر کے قبر سے) ؟ حالانکہ مجھ سے پہلے کتنی ہی نسلیں گزر چکی ہیں اور وہ دونوں اللہ کی دہائی دے کر کہتے ہیں : بربادی ہو تیری ایمان لے آ ! یقینا اللہ کا وعدہ سچا ہے لیکن وہ کہتا ہے : یہ کچھ نہیں مگر پہلے لوگوں کی کہانیاں ہیں');
INSERT INTO "bayan" VALUES(46,18,'46:18','یہ وہ لوگ ہیں جن پر اللہ کی بات صادق آئی (یہ بھی شامل کردیے جائیں گے) ان قوموں میں جو ان سے پہلے گزر چکی ہیں جنوں اور انسانوں میں سے یقینا یہی لوگ ہیں خسارے والے');
INSERT INTO "bayan" VALUES(46,19,'46:19','اور ہر ایک کے لیے درجے (اور مقامات) ہوں گے ان کے اعمال کے اعتبار سے اور تاکہ وہ پورا پورا دے انہیں ان کے اعمال کا بدلہ اور ان پر کوئی ظلم نہیں ہوگا');
INSERT INTO "bayan" VALUES(46,20,'46:20','اور جس دن یہ کافر پیش کیے جائیں گے آگ پر (اُس وقت ان سے کہا جائے گا :) تم اپنے حصے کی اچھی چیزیں اپنی دنیا کی زندگی میں لے چکے اور ان سے تم نے خوب فائدہ اٹھا لیا تو آج تمہیں ذلت کا عذاب دیا جائے گا بسبب اس تکبر کے جو تم زمین میں ناحق کرتے رہے ہو اور بسبب ان نافرمانیوں کے جو تم کرتے رہے ہو');
INSERT INTO "bayan" VALUES(46,21,'46:21','اور ذرا تذکرہ کیجیے قوم عاد کے بھائی (ہود (ؑ) کا جب اس نے خبردار کیا اپنی قوم کو احقاف میں اور اس سے پہلے بھی اور اس کے بعد بھی بہت سے خبردار کرنے والے گزر چکے تھے کہ مت عبادت کرو کسی کی سوائے اللہ کے مجھے اندیشہ ہے تم پر ایک بڑے دن کے عذاب کا');
INSERT INTO "bayan" VALUES(46,22,'46:22','انہوں نے کہا : کیا تم ہمارے پاس اس لیے آئے ہو کہ ہمیں بہکا کر ہمارے معبودوں سے برگشتہ کر دو تو جس عذاب کی دھمکی تم ہمیں دے رہے ہو وہ ہم پر لے آئو اگر تم سچوں میں سے ہو');
INSERT INTO "bayan" VALUES(46,23,'46:23','اس نے کہا کہ اس کا علم تو اللہ کے پاس ہے اور میں تو تمہیں پہنچا رہا ہوں وہ پیغام جو اس نے مجھے دے کر بھیجا ہے لیکن میں دیکھ رہا ہوں کہ تم لوگ بالکل جہالت کی روش اختیار کر رہے ہو');
INSERT INTO "bayan" VALUES(46,24,'46:24','پھر جب انہوں نے اس (عذاب) کو دیکھا بادل کی صورت میں اپنی وادیوں کی طرف بڑھتے ہوئے تو انہوں نے کہا : یہ تو بادل ہے جو ہم کو سیراب کرنے والا ہے نہیں ! بلکہ یہ تو وہ شے ہے جس کے بارے میں تم نے جلدی مچارکھی تھی یہ وہ جھکڑ ّہے جس میں بہت دردناک عذاب ہے');
INSERT INTO "bayan" VALUES(46,25,'46:25','تباہ و برباد کرتا ہے ہر شے کو اپنے رب کے حکم سے پھر وہ ایسے ہوگئے کہ کچھ نظر نہیں آ رہا تھا سوائے ان کے مسکنوں کے اسی طرح ہم بدلہ دیا کرتے ہیں مجرموں کی قوم کو');
INSERT INTO "bayan" VALUES(46,26,'46:26','اور ہم نے جو تمکن انہیں دیا تھا ایسا تمکن ّہم نے تمہیں نہیں دیا ہے اور ہم نے انہیں کان آنکھیں اور دل (یاعقلیں) عطا کیے تھے تو ان کے کچھ بھی کام نہ آئے ان کے کان نہ ان کی آنکھیں اور نہ ہی ان کے دل (عقلیں) جبکہ وہ انکار ہی کرتے رہے اللہ کی آیات کا اور ان کو گھیر لیا اسی چیز نے جس کا وہ مذاق اڑایا کرتے تھے');
INSERT INTO "bayan" VALUES(46,27,'46:27','اور ہم نے ہلاک کیا ہے بہت سی ایسی بستیوں کو جو تمہارے اردگرد آباد تھیں اور ہم نے پھیر پھیر کر ان کو اپنی آیات سنائیں تاکہ وہ رجوع کریں');
INSERT INTO "bayan" VALUES(46,28,'46:28','تو کیوں نہ مدد کی ان کی ان ہستیوں نے جن کو انہوں نے اللہ کے سوا معبود بنا رکھا تھا تقرب ّ (الی اللہ) کا ذریعہ سمجھتے ہوئے بلکہ وہ سب تو ان سے گم ہوگئے اور یہ تھا ان کا جھوٹ اور جو کچھ وہ افترا کر رہے تھے');
INSERT INTO "bayan" VALUES(46,29,'46:29','اور (اے نبی صلى الله عليه وسلم !) جب ہم نے آپ کی طرف متوجہ کردیا جنات کی ایک جماعت کو کہ وہ قرآن سننے لگے تو جب وہ وہاں پہنچے تو انہوں نے کہا : چپ ہو جائو پھر جب (قراءت) ختم ہوئی تو وہ پلٹے اپنی قوم کی طرف خبردار کرنے والے بن کر');
INSERT INTO "bayan" VALUES(46,30,'46:30','انہوں نے کہا : اے ہماری قوم کے لوگو ! ہم نے ایک کتاب سنی ہے جو موسیٰ ؑ ٰ کے بعد نازل ہوئی ہے تصدیق کرتی ہوئی اس کی جو اس کے سامنے موجود ہے (یعنی تورات) راہنمائی کرتی ہے حق کی طرف اور ایک سیدھے راستے کی طرف');
INSERT INTO "bayan" VALUES(46,31,'46:31','اے ہماری قوم کے لوگو ! اللہ کی طرف پکارنے والے کی پکار پر لبیک کہو اور اس پر ایمان لے آئو تاکہ وہ (اللہ) تمہارے گناہوں کو بخش دے اور تمہیں ایک درد ناک عذاب سے بچا لے');
INSERT INTO "bayan" VALUES(46,32,'46:32','اور جو کوئی لبیک نہیں کہتا اللہ کی طرف پکارنے والے کی پکار پر تو وہ اللہ کو عاجز کرنے والا نہیں ہے زمین میں اور نہیں ہوں گے اس کے لیے اللہ کے مقابلے میں دوسرے مددگار۔ یہی لوگ کھلی گمراہی میں مبتلا ہیں');
INSERT INTO "bayan" VALUES(46,33,'46:33','کیا انہوں نے غور نہیں کیا کہ وہ اللہ جس نے آسمانوں اور زمین کی تخلیق فرمائی اور ان کی تخلیق سے تھکا نہیں وہ اس پر قادر ہے کہ ُ مردوں کو زندہ کر دے کیوں نہیں یقینا وہ ہرچیز پر قادر ہے');
INSERT INTO "bayan" VALUES(46,34,'46:34','اور جس روز پیش کیے جائیں گے یہ کافر آگ پر (اور ان سے پوچھا جائے گا :) کیا یہ حقیقت نہیں ہے ؟ وہ کہیں گے : کیوں نہیں ! ہمارے پروردگار کی قسم یہ (حق ہے) اللہ فرمائے گا : تو اب چکھو عذاب کا مزہ اپنے اس کفر کی پاداش میں جو تم کرتے رہے ہو');
INSERT INTO "bayan" VALUES(46,35,'46:35','تو (اے محمد صلى الله عليه وسلم !) آپ بھی صبر کیجیے جیسے اولوالعزم رسول ؑ صبر کرتے رہے ہیں اور ان کے لیے جلدی نہ کیجیے جس دن یہ لوگ دیکھیں گے اس (عذاب) کو جس کی انہیں وعید سنائی جا رہی ہے (تو ایسے محسوس کریں گے) گویا نہیں رہے تھے (دنیا میں) مگر دن کی ایک گھڑی یہ پہنچا دینا ہے ! کیا سوائے نافرمان لوگوں کے کوئی اور بھی ہلاک کیا جائے گا ؟');
INSERT INTO "bayan" VALUES(47,1,'47:1','جن لوگوں نے کفر کیا اور اللہ کے راستے سے روکا (اور خود بھی رکے) اللہ نے ان کی ساری جدوجہد کو رائیگاں کردیا۔');
INSERT INTO "bayan" VALUES(47,2,'47:2','اور جو لوگ ایمان لائے اور جنہوں نے نیک اعمال کیے اور جو ایمان لائے اس شے پر جو نازل کی گئی محمد صلى الله عليه وسلم پر اور وہ حق ہے ان کے رب کی طرف سے } اللہ نے دور کردیں ان سے ان کی خطائیں اور ان کے حال کو سنوار دیا۔');
INSERT INTO "bayan" VALUES(47,3,'47:3','یہ اس لیے کہ جن لوگوں نے کفر کیا انہوں نے باطل کی پیروی کی اور جو لوگ ایمان لائے انہوں نے حق کی پیروی کی جو ان کے رب کی طرف سے آیا ہے اسی طرح اللہ لوگوں کے لیے ان کی مثالیں بیان کرتا ہے۔');
INSERT INTO "bayan" VALUES(47,4,'47:4','تو (اے مسلمانو !) جب تمہاری مڈ بھیڑ ہو کافروں سے تو ان کی گردنیں مارنا ہے یہاں تک کہ جب تم انہیں اچھی طرح سے ُ کچل دو تب انہیں مضبوطی کے ساتھ باندھو پھر اس کے بعد (تم انہیں چھوڑ سکتے ہو) احسان کر کے یا فدیہ لے کر یہاں تک کہ جنگ اپنے ہتھیار ڈال دے یہ ہے (اس بارے میں حکم) اور اگر اللہ چاہتا تو خود ہی ان سے بدلہ لے لیتا لیکن اللہ آزماتا ہے تمہارے بعض لوگوں کو بعض دوسروں کے ذریعے سے } اور جو لوگ اللہ کی راہ میں قتل ہوگئے تو اللہ ان کے اعمال کو ہرگز ضائع نہیں کرے گا۔');
INSERT INTO "bayan" VALUES(47,5,'47:5','وہ ان کی راہنمائی کرے گا اور ان کے احوال کو سنوار دے گا۔');
INSERT INTO "bayan" VALUES(47,6,'47:6','اور داخل کرے گا انہیں جنت میں جس کی ان کو پہچان کرا دی ہے۔');
INSERT INTO "bayan" VALUES(47,7,'47:7','} اے اہل ِایمان ! اگر تم اللہ کی مدد کرو گے تو اللہ تمہاری مدد کرے گا اور وہ تمہارے قدموں کو جما دے گا۔');
INSERT INTO "bayan" VALUES(47,8,'47:8','اور وہ لوگ جنہوں نے کفر کیا تو ان کے لیے نا کامی ہے اور وہ ان کے تمام اعمال کو برباد کر دے گا۔');
INSERT INTO "bayan" VALUES(47,9,'47:9','یہ اس لیے کہ انہوں نے اس چیز کو ناپسند کیا جو اللہ نے نازل کی تو اس نے ان کے تمام اعمال کو ضائع کردیا۔');
INSERT INTO "bayan" VALUES(47,10,'47:10','تو کیا یہ زمین میں گھومے پھرے نہیں کہ وہ دیکھتے کہ کیا انجام ہوا ان لوگوں کا جو ان سے پہلے تھے اللہ نے ان پر ہلاکت کو مسلط کردیا اور ان کافروں کے لیے بھی ان ہی کی مثالیں ہوں گی۔');
INSERT INTO "bayan" VALUES(47,11,'47:11','یہ اس لیے کہ اللہ مولیٰ (پشت پناہ) ہے اہل ایمان کا اور یہ کہ کافروں کا کوئی مولیٰ ہے ہی نہیں۔');
INSERT INTO "bayan" VALUES(47,12,'47:12','یقینا اللہ داخل کرے گا ان لوگوں کو جو ایمان لائے اور جنہوں نے نیک اعمال کیے ان باغات میں جن کے دامن میں نہریں بہتی ہوں گی۔ اور جو کافر ہیں وہ (دنیا میں) عیش کر رہے ہیں اور ایسے کھا رہے ہیں جیسے حیوانات کھاتے ہیں اور ان کا ٹھکانہ آگ ہے۔');
INSERT INTO "bayan" VALUES(47,13,'47:13','اور (اے نبی صلى الله عليه وسلم !) کتنی ہی ایسی بستیاں تھیں جو کہیں زیادہ قوت کی حامل تھیں آپ کی اس بستی سے جس نے آپ کو نکالا ہے۔ انہیں ہم نے ہلاک کردیا پس ان کا کوئی مد دگارنہ ہوا۔');
INSERT INTO "bayan" VALUES(47,14,'47:14','تو بھلا وہ شخص جو اپنے رب کی طرف سے ایک روشن دلیل پر ہے وہ اس شخص جیسا ہوجائے گا جس کے لیے اس کے ُ برے اعمال مزین ّکر دیے گئے ہیں اور انہوں نے اپنی خواہشات کی پیروی کی ہے۔');
INSERT INTO "bayan" VALUES(47,15,'47:15','مثال اس جنت کی جس کا وعدہ کیا گیا ہے متقین سے۔ اس میں نہریں ہیں پانی کی جس میں کوئی ُ بو نہیں۔ اور دودھ کی نہریں ہیں جس کا ذائقہ تبدیل نہیں ہوتا۔ اور ایسی شراب کی نہریں ہیں جو سراپالذت ہے پینے والوں کے لیے۔ اور نہریں ہیں صاف شفاف شہد کی۔ اور (مزید برآں) ان کے لیے ہوں گے اس میں ہر قسم کے پھل اور مغفرت ہوگی ان کے رب کی طرف سے } اُن جیسے ہوسکتے ہیں جو ہمیشہ آگ میں رہنے والے ہیں اور انہیں پلایا جائے گا کھولتا ہوا پانی جو ان کی انتڑیوں کو کاٹ کر رکھ دے گا۔');
INSERT INTO "bayan" VALUES(47,16,'47:16','اور (اے نبی صلى الله عليه وسلم !) ان میں کچھ لوگ ایسے بھی ہیں جو آپ کی بات کو بڑی توجہ سے سنتے ہیں۔ یہاں تک کہ جب وہ آپ کے پاس سے نکل کر باہر جاتے ہیں تو ان لوگوں سے پوچھتے ہیں جنہیں علم دیا گیا ہے کہ ابھی انہوں (محمد صلى الله عليه وسلم نے کیا کہا تھا ؟ } یہ وہ لوگ ہیں جن کے دلوں پر اللہ نے مہر لگا دی ہے اور وہ اپنی خواہشات کی پیروی کر رہے ہیں۔');
INSERT INTO "bayan" VALUES(47,17,'47:17','اور وہ لوگ جو ہدایت پر ہیں اللہ نے ان کی ہدایت میں اور اضافہ کردیا ہے اور انہیں ان کے حصے کا تقویٰ عطا فرمایا ہے۔');
INSERT INTO "bayan" VALUES(47,18,'47:18','تو یہ لوگ اب کس چیز کے منتظر ہیں سوائے قیامت کے کہ وہ آدھمکے ان پر اچانک ؟ پس اس کی علامات تو ظاہر ہو ہی چکی ہیں تو جب وہ ان پر آدھمکے گی تو اس وقت ان کا نصیحت حاصل کرنا کس کام کا ہوگا ؟');
INSERT INTO "bayan" VALUES(47,19,'47:19','بس جان لو کہ اللہ کے سوا کوئی معبود نہیں اور اپنی خطائوں کے لیے (اللہ سے) استغفار کرو اور اہل ِایمان مردوں اور عورتوں کے لیے بھی (استغفار کریں) اور اللہ خوب جانتا ہے تمہارے لوٹنے کی جگہ کو اور تمہارے مستقل ٹھکانے کو۔');
INSERT INTO "bayan" VALUES(47,20,'47:20','اور یہ لوگ جو ایمان کے دعویدار ہیں کہتے ہیں کہ (قتال کے بارے میں) کوئی سورت کیوں نازل نہیں ہوئی ؟ پھر جب ایک محکم سورت نازل کردی گئی اور اس میں قتال کا حکم آگیا تو اب آپ دیکھیں گے ان لوگوں کو جن کے دلوں میں روگ ہے کہ یہ لوگ آپ صلى الله عليه وسلم کی طرف ایسے دیکھیں گے جیسے وہ شخص دیکھتا ہے جس پر موت کی غشی طاری ہو رہی ہوتی ہے تو بربادی ہے ان کے لیے۔');
INSERT INTO "bayan" VALUES(47,21,'47:21','اطاعت لازم ہے اور کوئی بھلی بات کہی جاسکتی ہے تو جب کوئی فیصلہ طے پا جائے تو پھر اگر وہ اللہ سے (اپنے ایمان کے وعدے میں) سچے ثابت ہوں تبھی ان کے لیے بہتری ہے۔');
INSERT INTO "bayan" VALUES(47,22,'47:22','پس تم سے اس کے سوا کچھ متوقع نہیں ہے کہ اگر تم لوگوں کو حکومت مل جائے تو تم زمین میں فساد مچائو گے اور اپنے رحمی رشتے کاٹو گے۔');
INSERT INTO "bayan" VALUES(47,23,'47:23','یہ وہ لوگ ہیں جن پر اللہ نے لعنت کی ہے پس ان کے کانوں کو بہرا اور ان کی نگاہوں کو اندھا کردیا ہے۔');
INSERT INTO "bayan" VALUES(47,24,'47:24','کیا یہ لوگ قرآن پر تدبر ّنہیں کرتے یا ان کے دلوں پر قفل پڑچکے ہیں۔');
INSERT INTO "bayan" VALUES(47,25,'47:25','یقینا وہ لوگ جو پھرگئے اپنی پیٹھوں کے َبل اس کے بعد کہ ان پر ہدایت واضح ہوچکی شیطان نے ان کے لیے (ارتداد کا یہ مرحلہ) آسان کردیا ہے اور انہیں لمبی لمبی امیدیں دلائی ہیں۔');
INSERT INTO "bayan" VALUES(47,26,'47:26','یہ اس لیے ہوا کہ انہوں نے ان لوگوں سے جنہیں اللہ کا نازل کیا ہوا (قرآن) ناپسند ہے کہا کہ ہم بعض امور میں تمہاری اطاعت جاری رکھیں گے۔ اور اللہ کو ان کی خفیہ باتوں کا خوب علم ہے۔');
INSERT INTO "bayan" VALUES(47,27,'47:27','تو کیا حال ہوگا (ان کا اس وقت) جب فرشتے ان کی جانیں قبض کریں گے ضربیں لگاتے ہوئے ان کے چہروں پر اور ان کی پیٹھوں پر');
INSERT INTO "bayan" VALUES(47,28,'47:28','یہ اس لیے کہ انہوں نے پیروی کی اس چیز کی جو اللہ کو ناراض کرنے والی ہے اور انہوں نے ناپسند کیا اس کی رضا کو تو اس نے ان کے تمام اعمال اکارت کردیے۔');
INSERT INTO "bayan" VALUES(47,29,'47:29','کیا سمجھ رکھا تھا ان لوگوں نے جن کے دلوں میں روگ ہے کہ اللہ ان کے کھوٹ کو ظاہر نہیں کرے گا ؟ اور یقینا اللہ ظاہر کر کے رہے گا سچے اہل ایمان کو اور ظاہر کر کے رہے گا منافقین کو بھی۔');
INSERT INTO "bayan" VALUES(47,30,'47:30','اور اگر ہم چاہیں تو آپ کو یہ لوگ دکھا دیں اس طرح کہ آپ ان کے چہروں سے انہیں پہچان لیں گے۔ اور آپ انہیں لازماً پہچان لیں گے (ان کی) گفتگو کے انداز سے۔ اور اللہ تمہارے اعمال کو خوب جانتا ہے۔');
INSERT INTO "bayan" VALUES(47,31,'47:31','اور ہم تمہیں لازماً آزمائیں گے یہاں تک کہ ہم ظاہر کردیں انہیں جو تم میں سے جہاد کرنے والے اور صبر کرنے والے ہیں اور ہم پوری تحقیق کرلیں تمہارے حالات کی۔');
INSERT INTO "bayan" VALUES(47,32,'47:32','یقینا وہ لوگ جنہوں نے کفر کیا اور جو روکتے رہے دوسروں کو اللہ کے راستے سے اور وہ رسول صلى الله عليه وسلم کی مخالفت میں سرگرم رہے اس کے بعد کہ ان کے لیے ہدایت واضح ہوچکی تھی وہ اللہ کا کچھ نہیں بگاڑ سکیں گے۔ اور وہ ان کے اعمال کو اکارت کر دے گا۔');
INSERT INTO "bayan" VALUES(47,33,'47:33','اے اہل ِایمان ! تم لوگ اللہ کی اطاعت کرو اور رسول کی اطاعت کرو اور کہیں اپنے اعمال ضائع نہ کر بیٹھنا۔');
INSERT INTO "bayan" VALUES(47,34,'47:34','یقینا وہ لوگ جنہوں نے کفر کیا اور وہ اللہ کے راستے سے رکے رہے (اور دوسروں کو بھی روکتے رہے) پھر وہ مرگئے اس حالت میں کہ کافر ہی رہے تو ایسے لوگوں کو اللہ ہرگز نہیں بخشے گا۔');
INSERT INTO "bayan" VALUES(47,35,'47:35','تو (اے مسلمانو !) تم لوگ ڈھیلے نہ پڑو اور صلح و سلامتی کی دعوت مت دو } اور تم لوگ ہی غالب ہو گے اللہ تمہارے ساتھ ہے اور وہ تمہارے اعمال کو ہرگز ضائع نہیں کرے گا۔');
INSERT INTO "bayan" VALUES(47,36,'47:36','یہ دنیا کی زندگی تو محض کھیل ہے اور کچھ جی کا بہلانا ہے۔ اور اگر تم لوگ ایمان لائو اور تقویٰ اختیار کیے رہو تو وہ تمہیں تمہارے اجر عطا کرے گا اور تم سے تمہارے اموال نہیں مانگے گا۔');
INSERT INTO "bayan" VALUES(47,37,'47:37','اگر وہ تم سے ان (اموال) کے بارے میں مطالبہ کرے اور اس معاملے میں تم پر تنگی کرے تو تم بخل سے کام لو گے اور وہ ظاہر کر دے گا تمہارے (دلوں کے) کھوٹ کو۔');
INSERT INTO "bayan" VALUES(47,38,'47:38','تم وہ لوگ ہو کہ تمہیں بلایا جا رہا ہے کہ تم اللہ کی راہ میں خرچ کرو۔ پس تم میں سے کوئی وہ بھی ہے جو بخل سے کام لیتا ہے اور جو کوئی بھی بخل کرتا ہے وہ اپنے آپ ہی سے بخل کرتا ہے۔ اور اللہ غنی ہے اور محتاج تو تم ہی ہو۔ } اور اگر تم پیٹھ پھیر لو گے تو وہ تمہیں ہٹا کر کسی اور قوم کو لے آئے گا پھر وہ تمہاری طرح نہیں ہوں گے۔');
INSERT INTO "bayan" VALUES(48,1,'48:1','یقینا ہم نے آپ صلى الله عليه وسلم کو ایک بڑی روشن فتح عطا فرمائی ہے۔');
INSERT INTO "bayan" VALUES(48,2,'48:2','تا کہ اللہ بخش دے آپ کی کوئی کوتاہیاں جو پیچھے ہوئیں اور جو بعد میں ہوئیں اور تاکہ اللہ اپنی نعمت کا اتمام فرما دے آپ صلى الله عليه وسلم پر اور آپ صلى الله عليه وسلم کی راہنمائی کرے سیدھے راستے کی طرف۔');
INSERT INTO "bayan" VALUES(48,3,'48:3','اور اللہ آپ کی مدد کرے گا بہت زبردست مدد۔');
INSERT INTO "bayan" VALUES(48,4,'48:4','وہی ہے جس نے اہل ایمان کے دلوں میں سکینت نازل کردی تاکہ وہ اضافہ کرلیں اپنے ایمان میں مزید ایمان کا } اور آسمانوں اور زمین کے تمام لشکر تو اللہ ہی کے ہیں۔ اور اللہ سب کچھ جاننے والا کمال حکمت والا ہے۔');
INSERT INTO "bayan" VALUES(48,5,'48:5','تا کہ وہ داخل کرے اہل ِایمان مردوں اور عورتوں کو ان باغات میں جن کے دامن میں ندیاں بہتی ہوں گی جن میں وہ ہمیشہ ہمیش رہیں گے اور دور کر دے ان سے ان کی برائیوں کو اور یہی ہے اللہ کے نزدیک بڑی کامیابی۔');
INSERT INTO "bayan" VALUES(48,6,'48:6','اور (تاکہ) اللہ سزا دے منافق مردوں اور منافق عورتوں کو اور مشرک مردوں اور مشرک عورتوں کو جو اللہ کے بارے میں بہت ُ برے گمان رکھنے والے ہیں انہی پر مسلط ہے برائی کا دائرہ۔ اور اللہ ان پر غضبناک ہوا ہے اور اس نے ان پر لعنت فرمائی ہے اور ان کے لیے جہنم تیار کر رکھی ہے۔ اور وہ بہت بری جگہ ہے لوٹنے کی۔');
INSERT INTO "bayan" VALUES(48,7,'48:7','اور آسمانوں اور زمین کے لشکر ُ کل کے کل اللہ ہی کے اختیار میں ہیں۔ اور اللہ زبردست ہے کمال حکمت والا');
INSERT INTO "bayan" VALUES(48,8,'48:8','} (اے نبی صلى الله عليه وسلم !) ہم نے آپ کو بھیجا ہے گواہی دینے والا بشارت دینے والا اور خبردار کرنے والا بنا کر۔');
INSERT INTO "bayan" VALUES(48,9,'48:9','تا کہ (اے مسلمانو !) تم ایمان رکھو اللہ اور اس کے رسول پر اور رسول کی مدد کرو اور اس کی تعظیم کرو } اور تاکہ تم اللہ کی تسبیح کرو صبح وشام۔');
INSERT INTO "bayan" VALUES(48,10,'48:10','(اے نبی صلى الله عليه وسلم !) یقینا وہ لوگ جو آپ سے بیعت کر رہے ہیں وہ حقیقت میں اللہ سے بیعت کر رہے ہیں۔ ان کے ہاتھوں کے اوپر اللہ کا ہاتھ ہے تو اب جو کوئی اس کو توڑے گا تو اس کے توڑنے کا وبال اپنے اوپر ہی لے گا } اور جو کوئی پورا کرے گا اس معاہدے کو جو وہ اللہ سے کر رہا ہے تو وہ اسے اجر عظیم عطا فرمائے گا۔');
INSERT INTO "bayan" VALUES(48,11,'48:11','عنقریب آپ صلى الله عليه وسلم سے کہیں گے وہ لوگ جو پیچھے رہ جانے والے تھے دیہاتیوں میں سے ہمیں مشغول رکھا ہمارے اموال اور اہل و عیال (کی دیکھ بھال) نے چناچہ آپ ہمارے لیے استغفار کیجیے۔ یہ لوگ اپنی زبانوں سے وہ کچھ کہہ رہے ہیں جو ان کے دلوں میں نہیں ہے۔ آپ صلى الله عليه وسلم (ان سے) کہیے کہ کون ہے جو کچھ اختیار رکھتا ہو تمہارے بارے میں اللہ کی طرف سے کچھ بھی اگر وہ تمہیں نقصان پہنچانے کا ارادہ کرے یا تمہیں نفع پہنچانے کا ارادہ کرے ؟ } بلکہ جو کچھ تم کرتے رہے ہو اللہ اس سے باخبر ہے۔');
INSERT INTO "bayan" VALUES(48,12,'48:12','بلکہ تم لوگوں نے یہ گمان کیا کہ اب رسول اور اہل ِایمان اپنے گھر والوں کی طرف کبھی بھی لوٹ کر نہیں آئیں گے اور یہ بات تمہارے دلوں میں خوشنما بنا دی گئی اور تم نے بہت برا گمان کیا اور تم ہو ہی تباہ ہونے والے لوگ۔');
INSERT INTO "bayan" VALUES(48,13,'48:13','اور جو کوئی اللہ اور اس کے رسول صلى الله عليه وسلم پر ایمان نہ رکھے تو ہم نے ان کافروں کے لیے جہنم تیار کر رکھی ہے');
INSERT INTO "bayan" VALUES(48,14,'48:14','اور آسمانوں اور زمین کی بادشاہی اللہ ہی کے لیے ہے وہ بخشے گا جس کو چاہے گا اور عذاب دے گا جس کو چاہے گا۔ اور اللہ بہت بخشنے والا نہایت رحم کرنے والا ہے۔');
INSERT INTO "bayan" VALUES(48,15,'48:15','(اے مسلمانو !) عنقریب یہی پیچھے رہنے والے کہیں گے جب تم لوگ غنیمتیں حاصل کرنے کے لیے جائو گے کہ ہمیں بھی اجازت دیجیے کہ ہم آپ کے ساتھ چلیں یہ چاہتے ہیں کہ اللہ کے کلام کو تبدیل کردیں آپ صلى الله عليه وسلم کہیے کہ تم ہرگز ہمارے ساتھ نہیں جائو گے یہ بات اللہ پہلے ہی فرما چکا ہے۔ اس پر وہ کہیں گے : بلکہ آپ لوگ ہم سے حسد کر رہے ہیں۔ بلکہ حقیقت یہ ہے کہ یہ لوگ فہم ہی نہیں رکھتے مگر بہت کم۔');
INSERT INTO "bayan" VALUES(48,16,'48:16','ان بدوئوں میں سے جو لوگ پیچھے رہ گئے تھے آپ ان سے کہہ دیجیے کہ عنقریب تمہیں بلایا جائے گا ایک ایسی قوم کے ساتھ مقابلے کے لیے جو بہت طاقتور ہوگی یا تو تم ان سے قتال کرو گے یا وہ اطاعت قبول کرلیں گے تو اس وقت اگر تم اطاعت کرو گے تو اللہ تمہیں بہت اچھا اجر دے گا۔ } لیکن اگر تم نے (اس موقع پر بھی) پیٹھ دکھا دی جیسے کہ تم نے پہلے پیٹھ دکھائی تھی تو وہ تمہیں بہت درد ناک عذاب دے گا۔');
INSERT INTO "bayan" VALUES(48,17,'48:17','ہاں کسی اندھے پر کوئی تنگی نہیں اور نہ ہی کسی لنگڑے پر اور نہ ہی کسی مریض پر کوئی تنگی ہے اور جو اللہ اور اس کے رسول کی اطاعت کرے گا تو وہ داخل کرے گا اس کو ان باغات مین جن کے دامن میں ندیاں بہتی ہوں گی۔ } اور جو کوئی پیٹھ پھیر لے تو اسے وہ ایک دردناک عذاب سے دوچار کرے گا۔');
INSERT INTO "bayan" VALUES(48,18,'48:18','اللہ راضی ہوگیا اہل ایمان سے جب کہ وہ آپ صلى الله عليه وسلم سے بیعت کر رہے تھے درخت کے نیچے تو اللہ کے علم میں تھا جو کچھ ان کے دلوں میں تھا } تو اللہ نے ان پر سکینت نازل کردی اور انہیں (بدلے میں) ایک قریبی فتح بخشی');
INSERT INTO "bayan" VALUES(48,19,'48:19','اور بہت سے اموالِ غنیمت جو وہ حاصل کریں گے۔ اور اللہ زبردست حکمت والا ہے۔');
INSERT INTO "bayan" VALUES(48,20,'48:20','(اے مسلمانو !) اللہ تم سے وعدہ کرتا ہے بہت سے اموالِ غنیمت کا جنہیں تم حاصل کرو گے پس یہ (فتح) تو اس نے تمہیں فوری طور پر عطاکر دی ہے اور اس نے روک لیا لوگوں کے ہاتھوں کو تم سے } تاکہ یہ اہل ِایمان کے لیے ایک نشانی بن جائے اور وہ تمہاری راہنمائی فرمائے سیدھے راستے کی طرف۔');
INSERT INTO "bayan" VALUES(48,21,'48:21','اور کچھ (فتوحات) اور بھی ہیں جن پر ابھی تمہیں قدرت حاصل نہیں ہوئی اللہ ان کا بھی احاطہ کیے ہوئے ہے } اور اللہ ہرچیز پر قادر ہے۔');
INSERT INTO "bayan" VALUES(48,22,'48:22','اور اگر یہ کافر (قریش ِمکہ) ّتم سے جنگ کرتے تو لازماً پیٹھ پھیر جاتے اور پھر نہ پاتے وہ اپنے لیے کوئی حمایتی اور نہ کوئی مددگار۔');
INSERT INTO "bayan" VALUES(48,23,'48:23','یہ اللہ کا وہ دستور ہے جو پہلے سے چلا آ رہا ہے۔ } اور تم ہرگز نہیں پائو گے اللہ کے قانون میں کوئی تبدیلی۔');
INSERT INTO "bayan" VALUES(48,24,'48:24','اور وہی ہے جس نے روک دیے ان کے ہاتھ تمہاری طرف بڑھنے سے اور تمہارے ہاتھ ان کی طرف بڑھنے سے مکہ کی وادی میں اس کے بعد کہ اس نے تمہیں ان پر فتح دے دی تھی۔ } اور جو کچھ تم لوگ کر رہے ہو اللہ اسے دیکھ رہا ہے۔');
INSERT INTO "bayan" VALUES(48,25,'48:25','وہی لوگ ہیں جنہوں نے کفر کیا اور تمہیں روکے رکھا مسجد ِحرام تک جانے سے اور قربانی کے جانور بھی روکے گئے اپنی جگہ پہنچنے سے اور اگر نہ ہوتے (مکہ میں موجود) ایسے مومن مرد اور مومن عورتیں جنہیں تم نہیں جانتے تھے اندیشہ تھا کہ تم لوگ انہیں بھی کچل دیتے تو ان کے بارے میں تم پر الزام آتا بیخبر ی میں (تو جنگ نہ روکی جاتی) (جنگ اس لیے روکی گئی) تاکہ اللہ داخل کرے اپنی رحمت میں جس کو چاہے۔ } اگر وہ (اہل ِایمان) علیحدہ ہوچکے ہوتے تو ان (اہل ّمکہ) میں سے جو کافر تھے ان کو ہم ایک دردناک عذاب کا مزہ چکھا دیتے۔');
INSERT INTO "bayan" VALUES(48,26,'48:26','جب ان کافروں نے اپنے دلوں میں حمیت بٹھالی جاہلیت کی حمیت تو اللہ نے سکینت نازل کردی اپنے رسول صلى الله عليه وسلم پر اور اہل ایمان پر اور اس نے لازم کردیا ان پر تقویٰ کی بات کو اور وہ اس کے زیادہ حق دار بھی ہیں اور اس کے اہل بھی ہیں۔ اور اللہ ہرچیز کا علم رکھتا ہے۔');
INSERT INTO "bayan" VALUES(48,27,'48:27','اللہ نے اپنے رسول کو بالکل سچا خواب دکھایا حق کے ساتھ (اے مسلمانو !) تم لازماً داخل ہو گے مسجد الحرام میں ان شاء اللہ پورے امن کے ساتھ اپنے سروں کو منڈواتے ہوئے اور بالوں کو ترشواتے ہوئے (اُس وقت) تمہیں کوئی اندیشہ نہیں ہوگا۔ } تو اللہ وہ کچھ جانتا ہے جو تم نہیں جانتے تو اس کے علاوہ اس نے (تمہارے لیے) ایک قریب کی فتح بھی رکھی ہے۔');
INSERT INTO "bayan" VALUES(48,28,'48:28','وہی ہے جس نے بھیجا ہے اپنے رسول کو الہدیٰ اور دین حق دے کرتا کہ وہ غالب کر دے اسے ُ کل کے کل نظام زندگی پر اور کافی ہے اللہ مددگار کے طور پر۔');
INSERT INTO "bayan" VALUES(48,29,'48:29','محمد صلى الله عليه وسلم اللہ کے رسول ہیں۔ اور جو ان کے ساتھ ہیں وہ کافروں پر بہت بھاری اور آپس میں بہت رحم دل ہیں تم دیکھو گے انہیں رکوع کرتے ہوئے سجدہ کرتے ہوئے وہ (ہر آن) اللہ کے فضل اور اس کی رضا کے متلاشی رہتے ہیں ان کی پہچان ان کے چہروں پر (ظاہر) ہے سجدوں کے اثرات کی وجہ سے یہ ہے ان کی مثال تورات میں اور انجیل میں ان کی مثال یوں ہے کہ جیسے ایک کھیتی ہو جس نے نکالی اپنی کو نپل پھر اس کو تقویت دی پھر وہ سخت ہوئی پھر وہ اپنے تنے پر کھڑی ہوگئی یہ کا شتکار کو بڑی بھلی لگتی ہے تاکہ ان سے کافروں کے دل جلائے } اللہ نے وعدہ کیا ہے ان لوگوں سے جو ان میں سے ایمان لائے اور انہوں نے نیک اعمال کیے مغفرت اور اجر عظیم کا');
INSERT INTO "bayan" VALUES(49,1,'49:1','اے اہل ِایمان مت آگے بڑھو اللہ اور اس کے رسول سے اور اللہ کا تقویٰ اختیار کرو۔ یقینا اللہ سب کچھ سننے والا سب کچھ جاننے والا ہے۔');
INSERT INTO "bayan" VALUES(49,2,'49:2','اے اہل ِایمان ! اپنی آواز کبھی بلند نہ کرنا نبی صلى الله عليه وسلم کی آواز پر اور نہ انہیں اس طرح آواز دے کر پکارنا جس طرح تم آپس میں ایک دوسرے کو بلند آواز سے پکارتے ہو } مبادا تمہارے سارے اعمال ضائع ہوجائیں اور تمہیں خبر بھی نہ ہو۔');
INSERT INTO "bayan" VALUES(49,3,'49:3','بیشک وہ لوگ جو اپنی آوازوں کو پست رکھتے ہیں اللہ کے رسول صلى الله عليه وسلم کے سامنے یہی لوگ ہیں جن کے دلوں کو اللہ نے جانچ پرکھ کر ُ چن لیا ہے تقویٰ کے لیے۔ } ان کے لیے مغفرت بھی ہے اور بہت بڑا اجر بھی۔');
INSERT INTO "bayan" VALUES(49,4,'49:4','بیشک وہ لوگ جو آپ صلى الله عليه وسلم کو پکارتے ہیں حجروں کے پیچھے سے ان میں سے اکثر وہ ہیں جو عقل نہیں رکھتے۔');
INSERT INTO "bayan" VALUES(49,5,'49:5','اور اگر وہ صبر کرتے یہاں تک کہ آپ خود ان کے پاس نکل کر آجاتے تو یہ ان کے حق میں بہتر ہوتا۔ بہرحال اللہ بہت بخشنے والا بہت رحم کرنے والا ہے۔');
INSERT INTO "bayan" VALUES(49,6,'49:6','اے اہل ِایمان ! اگر تمہارے پاس کوئی فاسق شخص کوئی بڑی خبر لے کر آئے تو تم تحقیق کرلیا کرو } مبادا کہ تم جا پڑو کسی قوم پر نادانی میں اور پھر تمہیں اپنے کیے پر نادم ہونا پڑے۔');
INSERT INTO "bayan" VALUES(49,7,'49:7','اور جان لو کہ تمہارے مابین اللہ کا رسول موجود ہے۔ اگر وہ تمہارا کہنا مانا کریں اکثر معاملات میں تو تم لوگ مشکل میں پڑ جائو لیکن (اے نبی صلى الله عليه وسلم کے ساتھیو !) اللہ نے تمہارے نزدیک ایمان کو بہت محبوب بنا دیا ہے اور اسے تمہارے دلوں کے اندر کھبا دیا ہے اور اس نے تمہارے نزدیک بہت ناپسندیدہ بنا دیا ہے کفر فسق اور نافرمانی کو۔ } یہی لوگ ہیں جو صحیح راستے پر ہیں۔');
INSERT INTO "bayan" VALUES(49,8,'49:8','اللہ کی طرف سے بہت بڑے فضل اور انعام کی بنا پر۔ اور اللہ ہرچیز کا علم رکھنے والا کمال حکمت والا ہے۔');
INSERT INTO "bayan" VALUES(49,9,'49:9','اور اگر اہل ِایمان میں سے دو گروہ آپس میں لڑ پڑیں تو ان کے درمیان صلح کرا دیا کرو۔ پھر اگر ان میں سے ایک فریق دوسرے پر زیادتی کر رہا ہو تو تم جنگ کرو اس کے ساتھ جو زیادتی کر رہا ہے یہاں تک کہ وہ لوٹ آئے اللہ کے حکم کی طرف } پھر اگر وہ لوٹ آئے تو ان دونوں کے درمیان صلح کرا دو عدل کے ساتھ اور دیکھو ! انصاف کرنا۔ بیشک اللہ انصاف کرنے والوں کو محبوب رکھتا ہے۔');
INSERT INTO "bayan" VALUES(49,10,'49:10','یقینا تمام اہل ِایمان آپس میں بھائی بھائی ہیں پس اپنے دو بھائیوں کے درمیان صلح کر ادیا کرو } اور اللہ کا تقویٰ اختیار کرو تاکہ تم پر رحم کیا جائے۔');
INSERT INTO "bayan" VALUES(49,11,'49:11','اے اہل ایمان ! تم میں سے کوئی گروہ دوسرے گروہ کا مذاق نہ اڑائے ہوسکتا ہے کہ وہ ان سے بہتر ہوں (اسی طرح) عورتیں بھی دوسری عورتوں کا مذاق نہ اڑائیں ہوسکتا ہے وہ ان سے بہتر ہوں اور اپنے آپ کو عیب مت لگائو اور نہ آپس میں ایک دوسرے کے چڑانے والے نام رکھا کرو۔ ایمان کے بعد تو برائی کا نام بھی برا ہے۔ اور جو باز نہیں آئیں گے وہی تو ظالم ہیں۔');
INSERT INTO "bayan" VALUES(49,12,'49:12','اے اہل ِایمان ! زیادہ گمان کرنے سے بچو بیشک بعض گمان گناہ ہوتے ہیں اور ایک دوسرے کے حالات کی ٹوہ میں نہ رہا کرو اور تم میں سے کوئی دوسرے کی غیبت نہ کرے۔ کیا تم میں سے کوئی شخص پسند کرے گا کہ اپنے ُ مردہ بھائی کا گوشت کھائے ؟ یہ تو تمہیں بہت ناگوار لگا اور اللہ کا تقویٰ اختیار کرو اللہ تو بہ کا بہت قبول فرمانے والا اور بہت رحم فرمانے والا ہے۔');
INSERT INTO "bayan" VALUES(49,13,'49:13','اے لوگو ! ہم نے تمہیں پیدا کیا ہے ایک مرد اور ایک عورت سے اور ہم نے تمہیں مختلف قوموں اور قبیلوں میں تقسیم کردیا ہے تاکہ تم ایک دوسرے کو پہچان سکو۔ یقینا تم میں سب سے زیادہ با عزت اللہ کے ہاں وہ ہے جو تم میں سب سے بڑھ کر متقی ہے۔ } یقینا اللہ سب کچھ جاننے والا ہرچیز سے باخبر ہے۔');
INSERT INTO "bayan" VALUES(49,14,'49:14','یہ بدو کہہ رہے ہیں کہ ہم ایمان لے آئے ہیں۔ (اے نبی صلى الله عليه وسلم ان سے) کہہ دیجیے : تم ہرگز ایمان نہیں لائے ہو بلکہ تم یوں کہو کہ ہم مسلمان (اطاعت گزار) ہوگئے ہیں اور ایمان ابھی تمہارے دلوں میں داخل نہیں ہوا۔ لیکن اگر تم اللہ اور اس کے رسول کی اطاعت کرتے رہو گے تو وہ تمہارے اعمال میں سے کوئی کمی نہیں کرے گا۔ } یقینا اللہ بہت بخشنے والا بہت مہربان ہے۔');
INSERT INTO "bayan" VALUES(49,15,'49:15','مومن تو بس وہی ہیں جو ایمان لائے اللہ اور اس کے رسول صلى الله عليه وسلم پر پھر شک میں ہرگز نہیں پڑے اور انہوں نے جہاد کیا اپنے مالوں اور اپنی جانوں کے ساتھ اللہ کی راہ میں۔ یہی لوگ ہیں جو (اپنے دعوائے ایمان میں) سچے ہیں۔');
INSERT INTO "bayan" VALUES(49,16,'49:16','(اے نبی صلى الله عليه وسلم !) ان سے کہیے : کیا تم اللہ کو جتلانا چاہتے ہو اپنادین ؟ اور اللہ جانتا ہے جو کچھ آسمانوں میں ہے اور جو کچھ زمین میں ہے۔ } اللہ تو ہرچیز کا علم رکھنے والا ہے۔');
INSERT INTO "bayan" VALUES(49,17,'49:17','(اے نبی صلى الله عليه وسلم !) یہ لوگ آپ پر احسان دھر رہے ہیں کہ وہ اسلام لے آئے ہیں ان سے کہیے کہ مجھ پر اپنے اسلام کا احسان نہ دھرو } بلکہ اللہ تم پر احسان دھرتا ہے کہ اس نے تمہیں ایمان کے راستے پر ڈال دیا ہے اگر تم سچے ہو۔');
INSERT INTO "bayan" VALUES(49,18,'49:18','یقینا اللہ جانتا ہے آسمانوں اور زمین کی چھپی ہوئی چیزیں۔ اور جو کچھ تم کر رہے ہو اللہ اسے بھی دیکھ رہا ہے۔');
INSERT INTO "bayan" VALUES(50,1,'50:1','قسم ہے عظیم الشان قرآن کی۔');
INSERT INTO "bayan" VALUES(50,2,'50:2','بلکہ انہیں بہت عجیب محسوس ہوا ہے کہ ان کے پاس آیا ہے ایک خبردار کرنے والا ان ہی میں سے تو کافروں نے کہا یہ تو بڑی عجیب سی بات ہے');
INSERT INTO "bayan" VALUES(50,3,'50:3','کیا جب ہم مرجائیں گے اور مٹی ہوجائیں گے (تو ہم پھر سے زندہ کیے جائیں گے یہ لوٹایا جانا تو بہت دور کی بات ہے۔');
INSERT INTO "bayan" VALUES(50,4,'50:4','ہم خوب جانتے ہیں کہ زمین ان میں سے کیا چیز کم کرتی ہے۔ اور ہمارے پاس تو ایک محفوظ رکھنے والی کتاب بھی موجود ہے۔');
INSERT INTO "bayan" VALUES(50,5,'50:5','بلکہ انہوں نے جھٹلا دیا حق کو جب کہ وہ ان کے پاس آیا سو اب وہ ایک بڑی الجھن میں مبتلاہو گئے ہیں۔');
INSERT INTO "bayan" VALUES(50,6,'50:6','تو کیا انہوں نے دیکھا نہیں آسمان کی طرف جو ان کے اوپر ہے کہ ہم نے اسے کیسے بنایا ہے اور کیسے اسے مزین کیا ہے اور اس میں کہیں کوئی رخنہ نہیں ہے۔');
INSERT INTO "bayan" VALUES(50,7,'50:7','اور زمین کو ہم نے پھیلا دیا ہے اور ہم نے اس میں لنگر ڈال دیے ہیں اور ہم نے اس میں ہر طرح کی بڑی ہی رونق والی چیزیں (جوڑوں کی شکل میں) پیدا کردیں۔');
INSERT INTO "bayan" VALUES(50,8,'50:8','یہ اللہ کی نشانیاں ہیں) سجھانے اور یاد دہانی کرانے کو ہر اس بندے کے لیے جو رجوع رکھے ہوئے ہو۔');
INSERT INTO "bayan" VALUES(50,9,'50:9','اور ہم نے آسمان سے اتارا برکت والا پانی تو ہم نے اگایا اس کے ذریعے سے باغات کو اور ان فصلوں کو جو کٹ جاتی ہیں۔');
INSERT INTO "bayan" VALUES(50,10,'50:10','اور کھجوروں کے بلند وبالا درخت جن کے خوشے ہیں تہ بہ تہ');
INSERT INTO "bayan" VALUES(50,11,'50:11','یہ سب رزق ہے بندوں کے لیے اور اسی (پانی) سے ہم زندہ کردیتے ہیں مردہ زمین کو اسی طریقے سے نکلنا ہوگا (تمہارا بھی)۔');
INSERT INTO "bayan" VALUES(50,12,'50:12','جھٹلایا تھا ان سے پہلے بھی نوح ؑ کی قوم نے کنویں والوں نے اور قوم ثمود نے۔');
INSERT INTO "bayan" VALUES(50,13,'50:13','اور قوم عاد فرعون اور لوط ؑ کے بھائیوں نے۔');
INSERT INTO "bayan" VALUES(50,14,'50:14','اور بن والوں اور قومِ ُ تبع نے ان سب نے رسولوں کو جھٹلایا تو وہ مستحق ہوگئے میری وعید کے۔');
INSERT INTO "bayan" VALUES(50,15,'50:15','تو کیا ہم پہلی مرتبہ پیدا کر کے عاجز آگئے ہیں بلکہ یہ لوگ شک میں پڑے ہوئے ہیں نئی تخلیق سے متعلق۔');
INSERT INTO "bayan" VALUES(50,16,'50:16','اور ہم نے ہی انسان کو پیدا کیا ہے اور ہم خوب جانتے ہیں جو اس کا نفس وسوسے ڈالتا ہے اور ہم تو اس سے اس کی رَگِ جاں سے بھی زیادہ قریب ہیں۔');
INSERT INTO "bayan" VALUES(50,17,'50:17','جبکہ لیتے جاتے ہیں دو لینے والے جو دائیں طرف اور بائیں طرف بیٹھے ہوتے ہیں۔');
INSERT INTO "bayan" VALUES(50,18,'50:18','وہ کوئی لفظ بھی نہیں بولتا ہے مگر اس کے پاس ایک مستعد نگران موجود ہوتا ہے۔');
INSERT INTO "bayan" VALUES(50,19,'50:19','اور بالآخر موت کی بےہوشی کا وقت آن پہنچا ہے حق کے ساتھ۔ یہی ہے نا وہ چیز جس سے تو بھاگا کرتا تھا');
INSERT INTO "bayan" VALUES(50,20,'50:20','اور صور میں پھونکا جائے گا یہ ہے وعدے کا دن۔');
INSERT INTO "bayan" VALUES(50,21,'50:21','اور ہر جان آئے گی (اس حالت میں کہ) اس کے ساتھ ہوگا ایک دھکیلنے والا اور ایک گواہ۔');
INSERT INTO "bayan" VALUES(50,22,'50:22','(اللہ فرمائے گا : اے انسان !) تو اس دن سے غفلت میں رہا تھا نا۔ تو آج ہم نے تجھ سے تیرا پردہ ہٹا دیا ہے تو آج تمہاری نگاہ کتنی تیز ہوگئی ہے۔');
INSERT INTO "bayan" VALUES(50,23,'50:23','اور اس کا ساتھی کہے گا : (پروردگار !) یہ جو میری تحویل میں تھا حاضر ہے');
INSERT INTO "bayan" VALUES(50,24,'50:24','اللہ تعالیٰ فرمائے گا) جھونک دو جہنم میں ہر ناشکرے سرکش کو۔');
INSERT INTO "bayan" VALUES(50,25,'50:25','جو خیر سے روکنے والا حد سے بڑھنے والا اور شکوک و شبہات میں مبتلا رہنے والا ہے۔');
INSERT INTO "bayan" VALUES(50,26,'50:26','جس نے اللہ کے ساتھ کوئی دوسرا الٰہ بھی بنا لیا تھا۔ تو جھونک دو اس کو بڑے سخت عذاب میں۔');
INSERT INTO "bayan" VALUES(50,27,'50:27','اس کا ساتھی (شیطان) کہے گا : پروردگار ! میں نے اس کو سرکش نہیں بنایا بلکہ یہ خود ہی بہت دور کی گمراہی میں پڑا ہوا تھا');
INSERT INTO "bayan" VALUES(50,28,'50:28','اللہ فرمائے گا :) اب میرے سامنے جھگڑ 1 مت کرو جبکہ میں پہلے ہی تمہاری طرف وعید بھیج چکا ہوں۔');
INSERT INTO "bayan" VALUES(50,29,'50:29','میرے حضور میں بات تبدیل نہیں کی جاسکتی اور میں اپنے بندوں پر ظلم کرنے والا نہیں ہوں۔');
INSERT INTO "bayan" VALUES(50,30,'50:30','جس دن ہم پوچھیں گے جہنم سے کہ کیا تو بھر گئی ؟ اور وہ کہے گی کیا کچھ اور بھی ہے ؟');
INSERT INTO "bayan" VALUES(50,31,'50:31','اور جنت قریب لائی جائے گی اہل تقویٰ کے لیے کچھ بھی دور نہیں ہوگی۔');
INSERT INTO "bayan" VALUES(50,32,'50:32','(ان سے کہا جائے گا) یہ ہے جس کا وعدہ تم لوگوں سے کیا جاتا تھا ہر اس شخص کے لیے جو (اللہ کی طرف) رجوع کرنے والا اور حفاظت کرنے والا ہو۔');
INSERT INTO "bayan" VALUES(50,33,'50:33','جو ڈرتا رہا رحمن سے غیب میں ہونے کے باوجود۔ اور لے کر آیا رجوع کرنے والا دل۔');
INSERT INTO "bayan" VALUES(50,34,'50:34','ان سے کہا جائے گا :) داخل ہو جائو اس (جنت) میں سلامتی کے ساتھ اب یہ ہمیشہ رہنے کا دن ہے۔');
INSERT INTO "bayan" VALUES(50,35,'50:35','ان کے لیے اس میں وہ سب کچھ ہوگاجو وہ چاہیں گے اور ہمارے پاس مزید بھی بہت کچھ ہے');
INSERT INTO "bayan" VALUES(50,36,'50:36','اور کتنی ہی قوموں کو ہم نے ان سے پہلے ہلاک کیا ہے جو قوت میں ان سے بڑھ کر تھیں سو انہوں نے ملکوں کے ملک فتح کیے ! پھر کیا وہ کوئی جائے فرار پا سکے ؟');
INSERT INTO "bayan" VALUES(50,37,'50:37','۔ } یقینا اس میں یاد دہانی ہے اس کے لیے جس کا دل ہو یا جو توجہ سے سنے حاضر ہو کر۔');
INSERT INTO "bayan" VALUES(50,38,'50:38','اور ہم نے آسمانوں اور زمین کو اور جو کچھ ان کے مابین ہے پیدا کیا چھ دنوں میں۔ اور ہم پر کوئی تکان طاری نہیں ہوئی۔');
INSERT INTO "bayan" VALUES(50,39,'50:39','تو (اے نبی صلى الله عليه وسلم آپ صبر کیجیے اس پر جو یہ لوگ کہہ رہے ہیں اور اپنے رب کی حمد کے ساتھ تسبیح کیجیے سورج کے طلوع ہونے سے قبل اور غروب ہونے سے قبل۔');
INSERT INTO "bayan" VALUES(50,40,'50:40','اور رات کے حصوں میں بھی اس کی تسبیح بیان کرو۔ اور سجدوں (نمازوں) کے بعد بھی۔');
INSERT INTO "bayan" VALUES(50,41,'50:41','اور کان لگائے رکھو جس دن پکارنے والا پکارے گا بہت قریب کی جگہ سے۔');
INSERT INTO "bayan" VALUES(50,42,'50:42','جس دن کہ وہ سنیں گے ایک چنگھاڑ حق کے ساتھ۔ وہ ہوگا نکلنے کا دن۔');
INSERT INTO "bayan" VALUES(50,43,'50:43','یقینا ہم ہی زندہ رکھتے ہیں اور ہم ہی موت وارد کرتے ہیں۔ اور ہماری ہی طرف (سب کو) پلٹ کر آنا ہے۔');
INSERT INTO "bayan" VALUES(50,44,'50:44','جس دن زمین ان پر سے پھٹ جائے گی اور وہ تیزی سے نکل پڑیں گے۔ یہ (سب کا) جمع کردینا ہمارے لیے بہت آسان ہے۔');
INSERT INTO "bayan" VALUES(50,45,'50:45','(اے نبی صلى الله عليه وسلم !) ہم خوب جانتے ہیں جو کچھ یہ لوگ کہہ رہے ہیں اور آپ ان پر کوئی زبردستی کرنے والے نہیں ہیں۔ پس آپ تذکیر کرتے رہیے اس قرآن کے ذریعے سے ہر اس شخص کو جو میری وعید سے ڈرتا ہے۔');
INSERT INTO "bayan" VALUES(51,1,'51:1','قسم ہے ان ہوائوں کی جو بکھیرنے والی ہیں جیسے کہ بکھیرا جاتا ہے۔');
INSERT INTO "bayan" VALUES(51,2,'51:2','پھر بوجھ اٹھا کر لانے والی ہیں۔');
INSERT INTO "bayan" VALUES(51,3,'51:3','پھر نرمی سے چلنے والی ہیں۔');
INSERT INTO "bayan" VALUES(51,4,'51:4','پھر اللہ کے امر کو تقسیم کرنے والی ہیں۔');
INSERT INTO "bayan" VALUES(51,5,'51:5','جو وعدہ تمہیں دیا جا رہا ہے وہ یقینا سچ ہے۔');
INSERT INTO "bayan" VALUES(51,6,'51:6','اور جزا و سزا ضرور واقع ہو کر رہے گی۔');
INSERT INTO "bayan" VALUES(51,7,'51:7','اور قسم ہے جالی دار آسمان کی۔');
INSERT INTO "bayan" VALUES(51,8,'51:8','یقینا تم لوگ ایک جھگڑے کی بات میں پڑگئے ہو۔');
INSERT INTO "bayan" VALUES(51,9,'51:9','اس سے وہی پھیرا جاتا ہے جو پھیر دیا گیا ہے۔');
INSERT INTO "bayan" VALUES(51,10,'51:10','ہلاک ہوجائیں یہ اٹکلیں دوڑانے والے۔');
INSERT INTO "bayan" VALUES(51,11,'51:11','جو اپنی غفلت میں پڑے ہوئے ہیں۔');
INSERT INTO "bayan" VALUES(51,12,'51:12','وہ پوچھتے ہیں کب آئے گا وہ جزا و سزا کا دن');
INSERT INTO "bayan" VALUES(51,13,'51:13','جس دن یہ لوگ آگ پر سینکے جائیں گے۔');
INSERT INTO "bayan" VALUES(51,14,'51:14','ُس دن ان سے کہا جائے گا) اب چکھو مزا اپنی شرارت کا۔ یہ ہے وہ (عذاب) جس کی تم جلدی مچایا کرتے تھے۔');
INSERT INTO "bayan" VALUES(51,15,'51:15','یقینا اہل تقویٰ باغات اور چشموں کے اندر ہوں گے۔');
INSERT INTO "bayan" VALUES(51,16,'51:16','وہ (شکریے کے ساتھ) لے رہے ہوں گے جو کچھ ان کا رب انہیں دے گا۔ یہ وہ لوگ ہیں جو اس سے پہلے نیکوکار تھے۔');
INSERT INTO "bayan" VALUES(51,17,'51:17','رات کا تھوڑا ہی حصہ ہوتا تھا جس میں وہ سوتے تھے۔');
INSERT INTO "bayan" VALUES(51,18,'51:18','اور سحری کے اوقات میں وہ استغفار کرتے تھے۔');
INSERT INTO "bayan" VALUES(51,19,'51:19','اور ان کے اموال میں سائل اور محتاج کا حق ہوا کرتا تھا۔');
INSERT INTO "bayan" VALUES(51,20,'51:20','اور زمین میں (ہر چہارطرف) نشانیاں ہیں یقین کرنے والوں کے لیے۔');
INSERT INTO "bayan" VALUES(51,21,'51:21','اور تمہاری اپنی جانوں میں بھی (نشانیاں ہیں)۔ تو کیا تم دیکھتے نہیں ہو ؟');
INSERT INTO "bayan" VALUES(51,22,'51:22','اور آسمان میں تمہارا رزق ہے اور وہ بھی جس کا تم سے وعدہ کیا جا رہا ہے۔');
INSERT INTO "bayan" VALUES(51,23,'51:23','تو آسمانوں اور زمین کے رب کی قسم یقینایہ حق ہے بالکل ایسے جیسے (اِس وقت) تم بات چیت کر رہے ہو۔');
INSERT INTO "bayan" VALUES(51,24,'51:24','کیا آپ صلى الله عليه وسلم کے پاس ابراہیم ؑ کے معزز مہمانوں کی خبرپہنچی ہے');
INSERT INTO "bayan" VALUES(51,25,'51:25','جب وہ اس کے ہاں داخل ہوئے تو انہوں نے سلام کہا۔ اس نے بھی (جواب میں) سلام کہا (اور دل میں کہا کہ) یہ تو کوئی اجنبی لوگ ہیں۔');
INSERT INTO "bayan" VALUES(51,26,'51:26','پھر وہ چپکے سے اپنے گھر والوں کی طرف گیا اور ایک (بھنا ہوا) موٹا تازہ بچھڑا لے آیا۔');
INSERT INTO "bayan" VALUES(51,27,'51:27','پھر اسے ان کی طرف بڑھایا اور کہا کہ آپ لوگ کھاتے نہیں ؟');
INSERT INTO "bayan" VALUES(51,28,'51:28','تو اس نے ان کی طرف سے دل میں اندیشہ محسوس کیا۔ انہوں نے کہا : آپ ڈریں نہیں۔ اور انہوں نے اسے بشارت دی ایک صاحب علم بیٹے کی۔');
INSERT INTO "bayan" VALUES(51,29,'51:29','اس پر اس کی بیوی سامنے آئی بڑبڑاتی ہوئی اور اس نے اپنا ماتھا پیٹ لیا اور کہنے لگی : بڑھیا بانجھ (بچہ جنے گی کیا) ؟');
INSERT INTO "bayan" VALUES(51,30,'51:30','انہوں نے کہا : ایسا ہی فرمایا ہے آپ کے رب نے۔ یقینا وہ سب کچھ جاننے والا بھی ہے اور بہت حکمت والا بھی۔');
INSERT INTO "bayan" VALUES(51,31,'51:31','ابراہیم نے پوچھا کہ اے ایلچیو ! پھر آپ لوگوں کا مقصد کیا ہے ؟');
INSERT INTO "bayan" VALUES(51,32,'51:32','انہوں نے کہا : ہم بھیجے گئے ہیں ایک مجرم قوم کی طرف۔');
INSERT INTO "bayan" VALUES(51,33,'51:33','تاکہ ہم ان پر بارش برسائیں کنکریوں کی۔');
INSERT INTO "bayan" VALUES(51,34,'51:34','جو نشان زدہ ہیں آپ کے رب کے پاس حد سے بڑھ جانے والوں کے لیے۔');
INSERT INTO "bayan" VALUES(51,35,'51:35','پھر ہم نے نکال لیا جو بھی اس بستی میں تھے اہل ایمان۔');
INSERT INTO "bayan" VALUES(51,36,'51:36','تو نہیں پایا ہم نے اس میں سوائے ایک گھر کے کسی کو مسلمانوں میں سے۔');
INSERT INTO "bayan" VALUES(51,37,'51:37','اور ہم نے اس میں ایک نشانی چھوڑ دی ان لوگوں کے لیے جو ڈرتے ہوں دردناک عذاب سے۔');
INSERT INTO "bayan" VALUES(51,38,'51:38','اور موسیٰ کے معاملے میں بھی (نشانی ہے) جب ہم نے اسے بھیجا فرعون کی طرف کھلی سند دے کر۔');
INSERT INTO "bayan" VALUES(51,39,'51:39','تو اس نے منہ موڑ لیا اپنی شان و شوکت کے گھمنڈ میں اور کہا کہ یہ ساحر ہے یا مجنون ہے۔');
INSERT INTO "bayan" VALUES(51,40,'51:40','سو ہم نے پکڑا اس کو اور اس کے لشکروں کو اور انہیں پھینک دیا سمندر میں اور وہ ملامت زدہ تھا۔');
INSERT INTO "bayan" VALUES(51,41,'51:41','اور قوم عاد میں بھی (نشانی ہے) جب ہم نے ان پر ایک ایسی ہوا چھوڑ دی تھی جوہر خیر سے خالی تھی۔');
INSERT INTO "bayan" VALUES(51,42,'51:42','وہ نہیں چھوڑتی تھی کسی شے کو جس پر وہ آجاتی تھی مگر اس کو چوراچورا کر کے رکھ دیتی تھی۔');
INSERT INTO "bayan" VALUES(51,43,'51:43','اور اسی طرح ثمود کے معاملے میں بھی (نشانی ہے) جب ان سے کہا گیا کہ ایک خاص وقت تک کے لیے تم فائدہ اٹھا لو (دنیا کی نعمتوں سے)۔');
INSERT INTO "bayan" VALUES(51,44,'51:44','تو انہوں نے سرکشی کی اپنے رب کے حکم سے تو ان کے دیکھتے ہی دیکھتے ایک چنگھاڑ نے انہیں آپکڑا۔');
INSERT INTO "bayan" VALUES(51,45,'51:45','تو نہ ان میں کھڑے ہونے کی سکت رہی اور نہ ہی اس قابل رہے کہ بدلہ لیتے۔');
INSERT INTO "bayan" VALUES(51,46,'51:46','اور قوم نوح کو بھی اس سے پہلے (ہم نے پکڑا)۔ یقینا وہ بھی بڑے ہی نافرمان لوگ تھے۔');
INSERT INTO "bayan" VALUES(51,47,'51:47','اور آسمان کو ہم نے بنایا اپنے ہاتھوں سے اور ہم (اس کو) توسیع دینے والے ہیں۔');
INSERT INTO "bayan" VALUES(51,48,'51:48','اور زمین کو ہم نے (فرش کی مانند) بچھا دیا پس ہم کیا ہی خوب بچھانے والے ہیں');
INSERT INTO "bayan" VALUES(51,49,'51:49','اور ہم نے ہر شے کے جوڑے بنائے ہیں شاید کہ تم نصیحت اخذ کرو۔');
INSERT INTO "bayan" VALUES(51,50,'51:50','۔ } تو دوڑو اللہ کی طرف یقینا میں تم لوگوں کے لیے اس کی طرف سے صاف صاف خبردار کرنے والا ہوں۔');
INSERT INTO "bayan" VALUES(51,51,'51:51','اور اللہ کے ساتھ کوئی دوسرا معبود نہ ٹھہرائو۔ یقینا میں اس کی جانب سے تمہارے لیے واضح طور پر خبردار کرنے والا ہوں۔');
INSERT INTO "bayan" VALUES(51,52,'51:52','۔ } اسی طرح (ہوتا آیا ہے کہ) نہیں آیا تھا ان سے پہلے لوگوں کے پاس کوئی رسول مگر انہوں نے یہی کہا تھا کہ یہ ساحر ہے یا مجنون ہے۔');
INSERT INTO "bayan" VALUES(51,53,'51:53','کیا وہ ایک دوسرے کو وصیت کر گئے تھے اس کی ؟ بلکہ یہ ہیں ہی سرکش لوگ');
INSERT INTO "bayan" VALUES(51,54,'51:54','پس (اے نبی صلى الله عليه وسلم !) آپ ان سے رُخ پھیر لیں آپ پر کوئی ملامت نہیں ہے۔');
INSERT INTO "bayan" VALUES(51,55,'51:55','اور آپ تذکیر کرتے رہیے کیونکہ یہ تذکیر اہل ایمان کے لیے بہت فائدہ مند ہے۔');
INSERT INTO "bayan" VALUES(51,56,'51:56','۔ } اور میں نے نہیں پیدا کیا جنوں اور انسانوں کو مگر صرف اس لیے کہ وہ میری بندگی کریں۔');
INSERT INTO "bayan" VALUES(51,57,'51:57','میں ان سے کوئی رزق نہیں چاہتا اور نہ میں یہ چاہتا ہوں کہ یہ مجھے کھلائیں پلائیں۔');
INSERT INTO "bayan" VALUES(51,58,'51:58','یقینا اللہ ہی سب کو رزق دینے والا قوت والا زبردست ہے۔');
INSERT INTO "bayan" VALUES(51,59,'51:59','پس ان ظالموں کا پیمانہ بھی لبریز ہوچکا ہے جس طرح ان کے ساتھیوں کا پیمانہ لبریز ہوا تھا سو یہ مجھ سے جلدی نہ مچائیں۔');
INSERT INTO "bayan" VALUES(51,60,'51:60','تو ہلاکت اور بربادی ہے ان کافروں کے لیے اس دن سے جس کا ان سے وعدہ کیا جا رہا ہے۔');
INSERT INTO "bayan" VALUES(52,1,'52:1','قسم ہے طور کی۔');
INSERT INTO "bayan" VALUES(52,2,'52:2','اور اس کتاب کی قسم جو لکھی ہوئی ہے۔');
INSERT INTO "bayan" VALUES(52,3,'52:3','کشادہ ورق میں۔');
INSERT INTO "bayan" VALUES(52,4,'52:4','اور قسم ہے آباد گھر کی۔');
INSERT INTO "bayan" VALUES(52,5,'52:5','اور قسم ہے اونچی چھت کی۔');
INSERT INTO "bayan" VALUES(52,6,'52:6','اور قسم ہے ابلتے ہوئے سمندر کی۔');
INSERT INTO "bayan" VALUES(52,7,'52:7','تیرے رب کا عذاب یقینا واقع ہو کر رہے گا۔');
INSERT INTO "bayan" VALUES(52,8,'52:8','اسے کوئی روکنے والا نہ ہوگا۔');
INSERT INTO "bayan" VALUES(52,9,'52:9','جس روز کہ آسمان بری طرح لرزے گا۔');
INSERT INTO "bayan" VALUES(52,10,'52:10','اور پہاڑ چل رہے ہوں گے جیسے چلا جاتا ہے۔');
INSERT INTO "bayan" VALUES(52,11,'52:11','پس ہلاکت و بربادی ہے اس دن جھٹلانے والوں کے لیے۔');
INSERT INTO "bayan" VALUES(52,12,'52:12','وہ لوگ جو خوش گپیوں میں مصروف کھیل رہے ہیں۔');
INSERT INTO "bayan" VALUES(52,13,'52:13','جس دن ان کو دھکے دے دے کر جہنم کی طرف دھکیلاجائے گا۔');
INSERT INTO "bayan" VALUES(52,14,'52:14','اور کہا جائے گا) یہ ہے وہ آگ جس کو تم جھٹلایا کرتے تھے۔');
INSERT INTO "bayan" VALUES(52,15,'52:15','کیا یہ جادو ہے یا تم دیکھ نہیں رہے ہو ؟');
INSERT INTO "bayan" VALUES(52,16,'52:16','اب داخل ہو جائو اس میں تم خواہ صبر کرو یا نہ کرو اب تمہارے حق میں برابر ہے۔ تمہیں بدلے میں وہی کچھ تو مل رہا ہے جو کچھ تم عمل کیا کرتے تھے۔');
INSERT INTO "bayan" VALUES(52,17,'52:17','یقینا متقی لوگ باغات میں اور نعمتوں میں ہوں گے۔');
INSERT INTO "bayan" VALUES(52,18,'52:18','وہ مزے کر رہے ہوں گے ان نعمتوں کے ساتھ جو ان کے رب نے انہیں عطا کی ہوں گی۔ اور بچا لے گا انہیں ان کا ربّ جہنم کے عذاب سے۔');
INSERT INTO "bayan" VALUES(52,19,'52:19','اور کھائو پیو رچتا پچتا اُن اعمال کا صلہ جو تم کرتے رہے ہو۔');
INSERT INTO "bayan" VALUES(52,20,'52:20','وہ تکیے لگائے بیٹھے ہوں گے ان تختوں پر جو برابر بچھے ہوں گے صف درصف۔ اورا نہیں ہم بیاہ دیں گے بڑی بڑی آنکھوں والی حوروں کے ساتھ۔');
INSERT INTO "bayan" VALUES(52,21,'52:21','اور وہ لوگ جو ایمان لائے اور ان کی اولاد نے بھی ان کی پیروی کی ایمان کے ساتھ ہم ملا دیں گے ان کے ساتھ ان کی اس اولاد کو اور ہم ان کے عمل میں سے کوئی کمی نہیں کریں گے۔ ہر انسان اپنی کمائی کے عوض رہن ہوگا۔');
INSERT INTO "bayan" VALUES(52,22,'52:22','اور ہم انہیں دیے چلے جائیں گے پھل اور گوشت جس سے وہ چاہیں گے۔');
INSERT INTO "bayan" VALUES(52,23,'52:23','اس میں وہ ایک دوسرے سے چھین رہے ہوں گے وہ جام جن (کے مشروب) میں نہ تو کوئی بےہودگی ہوگی اور نہ ہی گناہ میں ڈالنے والی کوئی بات۔');
INSERT INTO "bayan" VALUES(52,24,'52:24','اور ان پر گردش میں رہیں گے نوجوان لڑکے ان کی خدمت کے لیے وہ ایسے ہوں گے جیسے موتی ہوں غلافوں میں رکھے ہوئے۔');
INSERT INTO "bayan" VALUES(52,25,'52:25','اور وہ (اہل جنت) ایک دوسرے کی طرف رخ کرکے باہم سوال کریں گے۔');
INSERT INTO "bayan" VALUES(52,26,'52:26','وہ کہیں گے کہ ہم پہلے اپنے اہل و عیال میں ڈرتے ہوئے رہتے تھے۔');
INSERT INTO "bayan" VALUES(52,27,'52:27','تو اللہ نے بڑا احسان کیا ہم پر اور ہمیں بچالیا لو کے عذاب سے۔');
INSERT INTO "bayan" VALUES(52,28,'52:28','یقینا ہم پہلے سے اس کو پکارا کرتے تھے۔');
INSERT INTO "bayan" VALUES(52,29,'52:29','تو (اے نبی صلى الله عليه وسلم !) آپ تذکیر کرتے رہیے پس آپ اپنے رب کے فضل سے نہ کاہن ہیں اور نہ مجنون۔');
INSERT INTO "bayan" VALUES(52,30,'52:30','کیا ان کا کہنا ہے کہ یہ ایک شاعر ہے جس کے لیے ہم منتظر ہیں گردش زمانہ کے ؟');
INSERT INTO "bayan" VALUES(52,31,'52:31','آپ صلى الله عليه وسلم کہیے کہ اچھا تم انتظار کرو میں بھی تمہارے ساتھ انتظار کرنے والوں میں ہوں۔');
INSERT INTO "bayan" VALUES(52,32,'52:32','کیا ان کی عقلیں انہیں یہی کچھ سکھا رہی ہیں یا یہ ہیں ہی سرکش لوگ ؟');
INSERT INTO "bayan" VALUES(52,33,'52:33','کیا ان کا کہنا ہے کہ یہ اس (محمد صلى الله عليه وسلم نے خود گھڑ لیا ہے ؟ بلکہ (اصل بات یہ ہے کہ) یہ ماننے والے نہیں ہیں۔');
INSERT INTO "bayan" VALUES(52,34,'52:34','تو وہ لے آئیں اس جیسی کوئی ایک بات اگر وہ سچے ہیں۔');
INSERT INTO "bayan" VALUES(52,35,'52:35','کیا یہ بغیر کسی کے بنائے ہوئے خود بن گئے ہیں یا یہ خود ہی خالق ہیں ؟');
INSERT INTO "bayan" VALUES(52,36,'52:36','کیا آسمانوں اور زمین کو انہوں نے بنایا ہے ؟ بلکہ (اصل بات یہ ہے کہ) یہ یقین نہیں رکھتے۔');
INSERT INTO "bayan" VALUES(52,37,'52:37','کیا ان کے قبضہ قدرت میں آپ کے رب کے خزانے ہیں یا یہ داروغہ ہیں ؟');
INSERT INTO "bayan" VALUES(52,38,'52:38','کیا ان کے پاس کوئی ایسی سیڑھی ہے جس کے ذریعے سے یہ آسمان کی خبریں سن لیتے ہیں ؟ اگر ایسا ہے) تو ان کا سننے والا کوئی واضح دلیل لائے۔');
INSERT INTO "bayan" VALUES(52,39,'52:39','کیا اس کے لیے تو بیٹیاں ہیں اور تمہارے لیے بیٹے ؟');
INSERT INTO "bayan" VALUES(52,40,'52:40','کیا آپ ان لوگوں سے کوئی اجرت طلب کر رہے ہیں کہ یہ تاوان کے بوجھ تلے دبے جا رہے ہیں ؟');
INSERT INTO "bayan" VALUES(52,41,'52:41','کیا ان کے پاس غیب کا علم ہے جسے یہ لکھ رہے ہیں ؟');
INSERT INTO "bayan" VALUES(52,42,'52:42','کیا یہ لوگ کوئی چال چلنا چاہتے ہیں ؟ اصل میں تو یہ کافر خود ہی چال کا شکار ہوگئے ہیں۔');
INSERT INTO "bayan" VALUES(52,43,'52:43','کیا ان کے لیے اللہ کے سوا کوئی اور الٰہ بھی ہے ؟ پاک ہے اللہ اس سے جو شرک یہ کر رہے ہیں۔');
INSERT INTO "bayan" VALUES(52,44,'52:44','اور اگر کبھی یہ دیکھیں آسمان کا کوئی ٹکڑا گرتا ہوا تو کہیں گے کہ یہ تو بادل ہیں تہ بر تہ۔');
INSERT INTO "bayan" VALUES(52,45,'52:45','تو (اے نبی صلى الله عليه وسلم !) چھوڑے رکھیے ان کو یہاں تک کہ وہ اپنے اس دن سے دوچار ہوں جس میں ان پر بجلی کی کڑک گرے گی۔');
INSERT INTO "bayan" VALUES(52,46,'52:46','جس دن ان کی چالیں ان کے کسی کام نہ آسکیں گی اور نہ ہی ان کی کوئی مدد ہی ہوگی۔');
INSERT INTO "bayan" VALUES(52,47,'52:47','اور ان ظالموں کے لیے اس (آخرت کے عذاب) کے علاوہ بھی ایک عذاب ہے لیکن ان کی اکثریت علم نہیں رکھتی۔');
INSERT INTO "bayan" VALUES(52,48,'52:48','(اور اے نبی صلى الله عليه وسلم آپ اپنے ربّ کے فیصلے کا انتظار کیجیے۔ بیشک آپ ہماری نگاہوں میں ہیں۔ اور آپ تسبیح کرتے رہیں اپنے رب کی حمد کے ساتھ جب آپ کھڑے ہوں۔');
INSERT INTO "bayan" VALUES(52,49,'52:49','اور رات کے ایک حصے میں بھی آپ اس کی تسبیح کریں اور ستاروں کے پیٹھ موڑتے وقت بھی (آپ تسبیح کیجیے)');
INSERT INTO "bayan" VALUES(53,1,'53:1','قسم ہے ستارے کی جبکہ وہ گرتا ہے۔');
INSERT INTO "bayan" VALUES(53,2,'53:2','اے لوگو !) تمہارے یہ ساتھی نہ تو بھٹکے ہیں اور نہ ہی بہکے ہیں۔');
INSERT INTO "bayan" VALUES(53,3,'53:3','اور یہ (جو کچھ کہہ رہے ہیں) اپنی خواہش نفس سے نہیں کہہ رہے ہیں۔');
INSERT INTO "bayan" VALUES(53,4,'53:4','یہ تو صرف وحی ہے جو ان کی طرف کی جاتی ہے۔');
INSERT INTO "bayan" VALUES(53,5,'53:5','انہیں سکھایا ہے اس نے جو زبردست قوت والا ہے۔');
INSERT INTO "bayan" VALUES(53,6,'53:6','جو بڑا زور آور ہے۔ وہ سیدھا ہوا۔');
INSERT INTO "bayan" VALUES(53,7,'53:7','اور وہ افق اعلیٰ پر تھا۔');
INSERT INTO "bayan" VALUES(53,8,'53:8','پھر وہ قریب آیا اور جھک پڑا۔');
INSERT INTO "bayan" VALUES(53,9,'53:9','بس دو کمانوں کے برابر (فاصلہ رہ گیا) یا اس سے بھی قریب۔');
INSERT INTO "bayan" VALUES(53,10,'53:10','پھر اس نے وحی کی اللہ کے بندے کی طرف جو وحی کی۔');
INSERT INTO "bayan" VALUES(53,11,'53:11','جو کچھ انہوں نے دیکھا ان کے دل و دماغ نے اسے جھٹلایا نہیں۔');
INSERT INTO "bayan" VALUES(53,12,'53:12','تو کیا تم اس سے جھگڑتے ہو اس چیز پر جسے وہ دیکھتا ہے');
INSERT INTO "bayan" VALUES(53,13,'53:13','اور انہوں نے تو اس کو ایک مرتبہ اور بھی اترتے دیکھا ہے۔');
INSERT INTO "bayan" VALUES(53,14,'53:14','سدرۃ المنتہیٰ کے پاس۔');
INSERT INTO "bayan" VALUES(53,15,'53:15','جنت الماویٰ بھی اس کے پاس ہی ہے۔');
INSERT INTO "bayan" VALUES(53,16,'53:16','جبکہ چھائے ہوئے تھا سدرہ پر جو چھائے ہوئے تھا۔');
INSERT INTO "bayan" VALUES(53,17,'53:17','اس وقت محمد صلى الله عليه وسلم کی) آنکھ نہ تو کج ہوئی اور نہ ہی حد سے بڑھی۔');
INSERT INTO "bayan" VALUES(53,18,'53:18','انہوں نے اپنے رب کی عظیم ترین آیات کو دیکھا۔');
INSERT INTO "bayan" VALUES(53,19,'53:19','تو کیا تم لوگوں نے لات اور عزیٰ کے بارے میں غور کیا ہے ؟');
INSERT INTO "bayan" VALUES(53,20,'53:20','اور جو تیسری ایک اور (دیوی) منات ہے ؟');
INSERT INTO "bayan" VALUES(53,21,'53:21','کیا تمہارے لیے بیٹے ہیں اور اس کے لیے بیٹیاں ؟');
INSERT INTO "bayan" VALUES(53,22,'53:22','یہ تو بہت بھونڈی تقسیم ہے');
INSERT INTO "bayan" VALUES(53,23,'53:23','یہ کچھ نہیں ہے سوائے اس کے کہ کچھ نام ہیں جو رکھ لیے ہیں تم نے اور تمہارے آباء و اَجداد نے اللہ نے ان کے لیے کوئی سند نہیں اتاری یہ لوگ نہیں پیروی کر رہے مگر ظن وتخمین کی اور اپنی خواہشاتِ نفس کی جب کہ ان کے پاس ان کے رب کی طرف سے الہدیٰ آچکا ہے۔');
INSERT INTO "bayan" VALUES(53,24,'53:24','کیا انسان کو وہی کچھ مل جائے گا جس کی وہ آرزو کرتا ہے ؟');
INSERT INTO "bayan" VALUES(53,25,'53:25','پس آخرت اور دنیا سب اللہ ہی کے اختیار میں ہے۔');
INSERT INTO "bayan" VALUES(53,26,'53:26','اور کتنے ہی فرشتے ہیں آسمانوں میں جن کی شفاعت کسی کام نہیں آئے گی مگر اس کے بعد کہ اللہ جس کے لیے چاہے اجازت بخشے اور (سفارش) پسند کرے۔');
INSERT INTO "bayan" VALUES(53,27,'53:27','یہ لوگ جو آخرت پر ایمان نہیں رکھتے انہوں نے فرشتوں کے مونث نام رکھ دیے ہیں۔');
INSERT INTO "bayan" VALUES(53,28,'53:28','اور ان کے پاس اس بارے میں کوئی علم نہیں ہے۔ وہ نہیں پیروی کر رہے مگر صرف گمان کی۔ اور ظن تو حق سے کچھ بھی مستغنی نہیں کرسکتا۔');
INSERT INTO "bayan" VALUES(53,29,'53:29','تو (اے نبی صلى الله عليه وسلم !) آپ اعراض کر لیجیے ہر اس شخص سے جس نے روگردانی کی ہے ہمارے ذکر سے اور دنیا کی زندگی کے سوا جسے کچھ مطلوب نہیں ہے۔');
INSERT INTO "bayan" VALUES(53,30,'53:30','یہ ہے ان کے علم کی رسائی کی حد یقینا آپ صلى الله عليه وسلم کا ربّ خوب جانتا ہے ان کو بھی جو اس کی راہ سے بھٹک گئے ہیں اور وہ خوب جانتا ہے ان کو بھی جو ہدایت پر ہیں۔');
INSERT INTO "bayan" VALUES(53,31,'53:31','اور اللہ ہی کا ہے جو کچھ آسمانوں میں ہے اور جو کچھ زمین میں ہے تاکہ وہ بدلہ دے ان کو جنہوں نے برائیاں کمائی ہیں ان کے اعمال کا اور بدلہ دے ان کو جنہوں نے نیک کام کیے ہیں اچھا۔');
INSERT INTO "bayan" VALUES(53,32,'53:32','وہ لوگ جو بڑے بڑے گناہوں اور بےحیائی سے بچتے ہیں سوائے کچھ آلودگی کے۔ یقینا آپ کا ربّ بہت ہی وسیع مغفرت والا ہے۔ وہ تمہیں خوب جانتا ہے جبکہ اس نے تمہیں زمین سے اٹھایا اور جب کہ تم اپنی مائوں کے پیٹوں میں جنین کی شکل میں تھے۔ تو تم خود کو بہت پاکباز نہ ٹھہرائو۔ وہ اسے خوب جانتا ہے جو تقویٰ اختیار کرتا ہے۔');
INSERT INTO "bayan" VALUES(53,33,'53:33','پھر (اے نبی صلى الله عليه وسلم !) کیا آپ نے اس شخص کو بھی دیکھا جس نے پیٹھ موڑ لی ؟');
INSERT INTO "bayan" VALUES(53,34,'53:34','تھوڑا سا دیا اور پھر سخت ہوگیا۔');
INSERT INTO "bayan" VALUES(53,35,'53:35','کیا اس کے پاس غیب کا علم ہے کہ وہ دیکھ رہا ہے');
INSERT INTO "bayan" VALUES(53,36,'53:36','کیا اسے خبر نہیں پہنچی اس بارے میں جو کچھ موسیٰ ؑ کے صحیفوں میں تھا ؟');
INSERT INTO "bayan" VALUES(53,37,'53:37','اور ابراہیم کے (صحیفوں میں تھا) جس نے وفا کی انتہا کردی');
INSERT INTO "bayan" VALUES(53,38,'53:38','کہ نہیں اٹھائے گی کوئی جان کسی دوسری جان کے بوجھ کو۔');
INSERT INTO "bayan" VALUES(53,39,'53:39','اور یہ کہ انسان کے لیے نہیں ہے مگر وہی کچھ جس کی اس نے سعی کی ہوگی۔');
INSERT INTO "bayan" VALUES(53,40,'53:40','اور یہ کہ اس کی سعی عنقریب اسے دکھا دی جائے گی۔');
INSERT INTO "bayan" VALUES(53,41,'53:41','پھر اس کو بدلہ دیا جائے گا پورا پورا بدلہ۔');
INSERT INTO "bayan" VALUES(53,42,'53:42','اور یہ کہ بالآخر پہنچنا تمہارے رب ہی کی طرف ہے۔');
INSERT INTO "bayan" VALUES(53,43,'53:43','اور یہ کہ وہی ہے جو ہنساتا بھی ہے اور رلاتا بھی ہے۔');
INSERT INTO "bayan" VALUES(53,44,'53:44','اور یہ کہ وہی ہے جو مارتا بھی ہے اور زندہ بھی رکھتا ہے۔');
INSERT INTO "bayan" VALUES(53,45,'53:45','اور یہ کہ وہی ہے جس نے پیدا کیے ہیں جوڑے نر اور مادہ کے');
INSERT INTO "bayan" VALUES(53,46,'53:46','ایک ہی بوندھ سے جبکہ وہ ٹپکائی جاتی ہے۔');
INSERT INTO "bayan" VALUES(53,47,'53:47','اور یہ کہ اسی کے ذمے ہے دوبارہ اٹھانا۔');
INSERT INTO "bayan" VALUES(53,48,'53:48','اور یہ کہ اسی نے دولت دی اور اسی نے خزانہ دیا۔');
INSERT INTO "bayan" VALUES(53,49,'53:49','اور یہ کہ وہی شعریٰ کا بھی ربّ ہے۔');
INSERT INTO "bayan" VALUES(53,50,'53:50','اور یہ کہ اسی نے ہلاک کیا تھا عاد اولیٰ کو۔');
INSERT INTO "bayan" VALUES(53,51,'53:51','اور ثمود کو بھی پس کسی کو باقی نہ چھوڑا۔');
INSERT INTO "bayan" VALUES(53,52,'53:52','اور قوم نوح ؑ کو بھی (ہلاک کیا) ان سے پہلے۔');
INSERT INTO "bayan" VALUES(53,53,'53:53','اور (اُسی نے) الٹی ہوئی بستیوں کو پٹخ دیا۔');
INSERT INTO "bayan" VALUES(53,54,'53:54','اور پھر اس کو ڈھانپ لیاجس چیز نے ڈھانپ لیا۔');
INSERT INTO "bayan" VALUES(53,55,'53:55','تو تم اپنے رب کی کون کون سی قدرتوں کے بارے میں شک کرو گے ؟');
INSERT INTO "bayan" VALUES(53,56,'53:56','یہ (محمد صلى الله عليه وسلم ایک خبردار کرنے والے ہیں پہلے خبردار کرنے والوں (کے زمرے) میں سے۔');
INSERT INTO "bayan" VALUES(53,57,'53:57','قریب آچکی ہے وہ آنے والی۔');
INSERT INTO "bayan" VALUES(53,58,'53:58','نہیں ہے اس کو اللہ کے سوا کوئی کھولنے والا۔');
INSERT INTO "bayan" VALUES(53,59,'53:59','تو کیا تم لوگوں کو اس کلام کے بارے میں تعجب ہورہا ہے ؟');
INSERT INTO "bayan" VALUES(53,60,'53:60','اور تم ہنستے ہو اور روتے نہیں ہو');
INSERT INTO "bayan" VALUES(53,61,'53:61','اور تم خوش فعلیاں کر رہے ہو');
INSERT INTO "bayan" VALUES(53,62,'53:62','پس سجدہ کرو اللہ کے لیے اور اسی کی بندگی کرو');
INSERT INTO "bayan" VALUES(54,1,'54:1','قیامت کی گھڑی قریب آچکی اور چاند شق ہوگیا۔');
INSERT INTO "bayan" VALUES(54,2,'54:2','اور اگر وہ دیکھیں گے کوئی نشانی تب بھی وہ اعراض ہی کریں گے اور کہیں گے یہ تو جادو ہے جو پہلے سے چلا آ رہا ہے۔');
INSERT INTO "bayan" VALUES(54,3,'54:3','اور انہوں نے تکذیب کی اور اپنی خواہشات کی پیروی کی');
INSERT INTO "bayan" VALUES(54,4,'54:4','اور ان کے پاس وہ خبریں آچکی ہیں جن میں تنبیہہ ہے۔');
INSERT INTO "bayan" VALUES(54,5,'54:5','کامل دانائی (کی باتیں) لیکن ان خبردار کرنے والوں سے انہیں کوئی فائدہ نہ پہنچا۔');
INSERT INTO "bayan" VALUES(54,6,'54:6','تو (اے نبی صلى الله عليه وسلم !) آپ ان سے صرفِ نظر کر لیجیے۔');
INSERT INTO "bayan" VALUES(54,7,'54:7','(اس وقت) ان کی نگاہیں زمین میں گڑی ہوں گی۔ یہ اپنی قبروں سے ایسے نکل کر آئیں گے جیسے ٹڈی ّدَل پھیلا ہوا ہو۔');
INSERT INTO "bayan" VALUES(54,8,'54:8','دوڑتے ہوئے آئیں گے پکارنے والے کی طرف۔ کافر کہیں گے کہ یہ تو بڑا سخت دن ہے۔');
INSERT INTO "bayan" VALUES(54,9,'54:9','جھٹلایا تھا ان سے پہلے نوح کی قوم نے تو انہوں نے ہمارے بندے کو جھٹلایا اور کہا کہ یہ تو مجنون ہے اور اسے جھڑک دیا گیا۔');
INSERT INTO "bayan" VALUES(54,10,'54:10','تو اس نے پکارا اپنے رب کو کہ میں مغلوب ہوچکا ہوں اب تو ُ ان سے انتقام لے۔');
INSERT INTO "bayan" VALUES(54,11,'54:11','تو ہم نے کھول دیے آسمان کے دروازے اس پانی کے ساتھ جو مسلسل چھاجوں برستا رہا۔');
INSERT INTO "bayan" VALUES(54,12,'54:12','اور ہم نے زمین کو پھاڑ کر چشمے ہی چشمے کردیا تو وہ سارا پانی مل گیا ایک ایسے کام کے لیے جس کا فیصلہ ہوچکا تھا۔');
INSERT INTO "bayan" VALUES(54,13,'54:13','اور ہم نے اسے سوار کردیا اس (کشتی) پر جو تختوں اور کیلوں سے بنی تھی۔');
INSERT INTO "bayan" VALUES(54,14,'54:14','جو چل رہی تھی ہماری نگاہوں کے سامنے یہ بدلہ تھا اس شخص کے لیے جس کی ناقدری کی گئی تھی۔');
INSERT INTO "bayan" VALUES(54,15,'54:15','اور ہم نے اسے چھوڑ دیا ایک نشانی کے طور پر تو ہے کوئی نصیحت حاصل کرنے والا');
INSERT INTO "bayan" VALUES(54,16,'54:16','تو کیسا رہا میرا عذاب اور میرا خبردار کرنا');
INSERT INTO "bayan" VALUES(54,17,'54:17','اور ہم نے قرآن کو آسان کردیا ہے نصیحت اخذ کرنے کے لیے تو ہے کوئی نصیحت حاصل کرنے والا');
INSERT INTO "bayan" VALUES(54,18,'54:18','جھٹلایا تھا قوم عاد نے بھی تو کیسا رہا میرا عذاب اور میرا خبردار کرنا ؟');
INSERT INTO "bayan" VALUES(54,19,'54:19','ہم نے ان پر مسلط کردی ایک ُ تند و تیز ہوا ایک مسلسل نحوست کے دن میں۔');
INSERT INTO "bayan" VALUES(54,20,'54:20','وہ لوگوں کو یوں اکھاڑ پھینکتی تھی جیسے وہ کھجور کے تنے ہوں اکھڑی ہوئی جڑوں والے');
INSERT INTO "bayan" VALUES(54,21,'54:21','تو کیسا رہا میرا عذاب اور میرا خبردار کرنا ؟');
INSERT INTO "bayan" VALUES(54,22,'54:22','اور ہم نے اس قرآن کو آسان کردیا ہے نصیحت حاصل کرنے کے لیے تو ہے کوئی اس سے نصیحت حاصل کرنے والا');
INSERT INTO "bayan" VALUES(54,23,'54:23','قومِ ثمود نے بھی جھٹلایا خبردار کرنے والوں کو');
INSERT INTO "bayan" VALUES(54,24,'54:24','انہوں نے کہا : کیا ہم اپنے میں سے ہی ایک بشر کی پیروی کریں ؟ پھر تو یقیناہم پڑجائیں گے گمراہی میں اور آگ میں۔');
INSERT INTO "bayan" VALUES(54,25,'54:25','کیا یہ ذکر ہمارے مابین اسی پر القا کیا گیا ہے ؟ بلکہ یہ انتہائی جھوٹا اور شیخی خورا ہے۔');
INSERT INTO "bayan" VALUES(54,26,'54:26','جلد ہی انہیں معلوم ہوجائے گا کہ کون انتہائی جھوٹا اور شیخی خورا ہے');
INSERT INTO "bayan" VALUES(54,27,'54:27','ہم بھیجے دیتے ہیں اونٹنی کو ان کی آزمائش کے لیے تو آپ ؑ انتظار کیجیے ان کے بارے میں اور صبر کیجیے۔');
INSERT INTO "bayan" VALUES(54,28,'54:28','اور انہیں بتا دیجیے کہ اب پانی ان کے مابین تقسیم ہوگا ہر پینے کی باری پر حاضری ہوگی۔');
INSERT INTO "bayan" VALUES(54,29,'54:29','تو انہوں نے پکارا اپنے ایک ساتھی کو پس اس نے ہاتھ بڑھایا اور اس کی کونچیں کاٹ دیں۔');
INSERT INTO "bayan" VALUES(54,30,'54:30','پھر کیسا رہا میرا عذاب اور میرا خبردار کرنا ؟');
INSERT INTO "bayan" VALUES(54,31,'54:31','ہم نے ان پر بس ایک ہی چنگھاڑ بھیجی تو وہ باڑھ لگانے والے کی روندی ہوئی باڑھ کی طرح چورا ہو کر رہ گئے');
INSERT INTO "bayan" VALUES(54,32,'54:32','اور ہم نے تو اس قرآن کو آسان کردیا ہے سمجھنے کے لیے تو ہے کوئی سوچنے سمجھنے والا ؟');
INSERT INTO "bayan" VALUES(54,33,'54:33','لوط ؑ کی قوم نے بھی خبردار کرنے والوں کو جھٹلایا۔');
INSERT INTO "bayan" VALUES(54,34,'54:34','ہم نے ان پر بھیج دی ایک ایسی تیز آندھی جس میں پتھر تھے سوائے لوط کے گھر والوں کے۔ ان کو ہم نے نجات دے دی صبح کے وقت۔');
INSERT INTO "bayan" VALUES(54,35,'54:35','وہ نعمت تھی ہمارے پاس سے۔ اسی طرح ہم بدلہ دیتے ہیں اس کو جو شکر کرتا ہے۔');
INSERT INTO "bayan" VALUES(54,36,'54:36','اور لوط ؑ نے ان کو خبردار کردیا تھا ہماری پکڑ سے لیکن انہوں نے شک کیا ان چیزوں پر جن کے بارے میں انہیں خبردار کیا گیا تھا۔');
INSERT INTO "bayan" VALUES(54,37,'54:37','اور انہوں نے اس سے اس کے مہمانوں کو لے جانا چاہا تو ہم نے ان کی آنکھوں کو مٹا دیا تو مزا چکھو اب میرے عذاب کا اور میرے خبردار کرنے کا۔');
INSERT INTO "bayan" VALUES(54,38,'54:38','اور ان پر صبح ہی صبح آ دھمکا ایک عذاب جو کہ ٹھہر چکا تھا۔');
INSERT INTO "bayan" VALUES(54,39,'54:39','تو چکھو مزا اب میرے عذاب کا اور میرے خبردار کرنے کا۔');
INSERT INTO "bayan" VALUES(54,40,'54:40','اور ہم نے آسان کردیا ہے قرآن سمجھنے کو تو ہے کوئی سوچنے سمجھنے والا ؟');
INSERT INTO "bayan" VALUES(54,41,'54:41','اسی طرح قوم فرعون کے پاس بھی آئے تھے خبردار کرنے والے۔');
INSERT INTO "bayan" VALUES(54,42,'54:42','انہوں نے ہماری تمام نشانیوں کو جھٹلا دیا پھر ہم نے انہیں پکڑا ایک زبردست صاحب قدرت کا پکڑنا۔');
INSERT INTO "bayan" VALUES(54,43,'54:43','(تو اے قریش !) کیا تمہارے کفار اُن (کفار) سے کچھ بہتر ہیں ؟ یا تمہارے لیے سابقہ الہامی کتب میں کوئی فارغ خطی آچکی ہے ؟');
INSERT INTO "bayan" VALUES(54,44,'54:44','یا یہ کہتے ہیں کہ ہم ایک طاقتور جمعیت ہیں اور بدلہ لینے پر قادر ہیں ؟');
INSERT INTO "bayan" VALUES(54,45,'54:45','عنقریب ان کی جمعیت شکست کھاجائے گی اور وہ پیٹھ پھیر کر بھاگیں گے۔');
INSERT INTO "bayan" VALUES(54,46,'54:46','بلکہ ان کے اصل وعدے کا وقت تو قیامت ہے اور قیامت بہت بڑی آفت ہے اور بہت زیادہ کڑوی ہے۔');
INSERT INTO "bayan" VALUES(54,47,'54:47','یقینا یہ مجرمین گمراہی میں ہیں اور یہ آگ (کے مختلف طبقات) میں ہوں گے۔');
INSERT INTO "bayan" VALUES(54,48,'54:48','جس دن یہ گھسیٹے جائیں گے آگ میں اپنے چہروں کے بل۔ ان سے کہا جائے گا :) اب چکھوآگ کی لپٹ کا مزا');
INSERT INTO "bayan" VALUES(54,49,'54:49','یقینا ہم نے ہرچیز ایک اندازے کے مطابق پیدا کی ہے۔');
INSERT INTO "bayan" VALUES(54,50,'54:50','اور ہمارا امر تویکبارگی ہوتا ہے جیسے نگاہ کا لپک جانا۔');
INSERT INTO "bayan" VALUES(54,51,'54:51','تو (اے قریش ِمکہ) ہم نے تم جیسے بہت سے لوگوں کو ہلاک کیا ہے تو کوئی ہے اس سے سبق حاصل کرنے والا ؟');
INSERT INTO "bayan" VALUES(54,52,'54:52','اور وہ تمام اعمال جو ان لوگوں نے کیے ہیں وہ صحیفوں میں محفوظ ہیں۔');
INSERT INTO "bayan" VALUES(54,53,'54:53','اور ہر ایک چھوٹی اور بڑی چیز لکھی ہوئی ہے۔');
INSERT INTO "bayan" VALUES(54,54,'54:54','یقینا متقین باغات اور نہروں (کے ماحول) میں ہوں گے۔');
INSERT INTO "bayan" VALUES(54,55,'54:55','بہت اعلیٰ راستی کے مقام میں اس بادشاہ کے پاس جو اقتدارِ مطلق کا مالک ہے۔');
INSERT INTO "bayan" VALUES(55,1,'55:1','رحمن نے');
INSERT INTO "bayan" VALUES(55,2,'55:2','قرآن سکھایا۔');
INSERT INTO "bayan" VALUES(55,3,'55:3','اسی نے انسان کو بنایا۔');
INSERT INTO "bayan" VALUES(55,4,'55:4','اس کو بیان سکھایا۔');
INSERT INTO "bayan" VALUES(55,5,'55:5','سورج اور چاند ایک حساب کے ساتھ گردش کرتے ہیں');
INSERT INTO "bayan" VALUES(55,6,'55:6','اور ستارے اور درخت (اللہ کو) سجدہ کرتے ہیں۔');
INSERT INTO "bayan" VALUES(55,7,'55:7','اور آسمان کو اس نے بلند کیا اور میزان قائم کی۔');
INSERT INTO "bayan" VALUES(55,8,'55:8','تاکہ تم میزان میں زیادتی مت کرو۔');
INSERT INTO "bayan" VALUES(55,9,'55:9','اور قائم رکھو وزن کو انصاف کے ساتھ اور میزان میں کوئی کمی نہ کرو۔');
INSERT INTO "bayan" VALUES(55,10,'55:10','اور زمین کو اس نے بچھا دیا مخلوق کے لیے۔');
INSERT INTO "bayan" VALUES(55,11,'55:11','اس میں میوے ہیں اور کھجوریں ہیں جن پر غلاف چڑھے ہوئے ہیں۔');
INSERT INTO "bayan" VALUES(55,12,'55:12','اور ُ بھس والا اناج بھی ہے اور خوشبودار پھول بھی۔');
INSERT INTO "bayan" VALUES(55,13,'55:13','تو تم دونوں (گروہ) اپنے رب کی کون کونسی نعمتوں اور قدرتوں کا انکار کرو گے ؟');
INSERT INTO "bayan" VALUES(55,14,'55:14','اس نے پیدا کیا انسان کو ٹھیکرے کی طرح کھنکھناتی ہوئی مٹی سے۔');
