INSERT INTO "bayan" VALUES(56,49,'56:49','اے نبی صلى الله عليه وسلم !) آپ کہیے کہ یقینا پہلے بھی اور پچھلے بھی۔');
INSERT INTO "bayan" VALUES(56,50,'56:50','لازماً جمع کیے جائیں گے ایک مقرر دن کے طے شدہ وقت پر۔');
INSERT INTO "bayan" VALUES(56,51,'56:51','پھر تم اے گمراہو اور جھٹلانے والو');
INSERT INTO "bayan" VALUES(56,52,'56:52','ضرور کھائو گے زقوم کے درخت سے۔');
INSERT INTO "bayan" VALUES(56,53,'56:53','پس اسی سے تم اپنے پیٹ بھرو گے۔');
INSERT INTO "bayan" VALUES(56,54,'56:54','پھر پیو گے اس پر کھولتا ہوا پانی۔');
INSERT INTO "bayan" VALUES(56,55,'56:55','اور ایسے پیو گے جیسے پیاس کا مارا اونٹ پیتا ہے۔');
INSERT INTO "bayan" VALUES(56,56,'56:56','یہ ہوگی ان کی ابتدائی ضیافت جزا کے دن۔');
INSERT INTO "bayan" VALUES(56,57,'56:57','ہم نے تمہیں پیدا کیا ہے تو تم لوگ تصدیق کیوں نہیں کرتے ؟');
INSERT INTO "bayan" VALUES(56,58,'56:58','کیا تم نے کبھی غور کیا اس پر جو (منی) تم ٹپکادیتے ہو ؟');
INSERT INTO "bayan" VALUES(56,59,'56:59','کیا اس کی تخلیق تم کرتے ہو یا ہم تخلیق کرنے والے ہیں ؟');
INSERT INTO "bayan" VALUES(56,60,'56:60','ہم نے تمہارے مابین موت کو ٹھہرا دیا ہے اور ہم اس سے عاجز نہیں ہیں');
INSERT INTO "bayan" VALUES(56,61,'56:61','کہ تمہاری مثل بدل کر لائیں اور تمہیں ایسی تخلیق میں اٹھائیں جسے تم نہیں جانتے !');
INSERT INTO "bayan" VALUES(56,62,'56:62','اور تم (اپنی) پہلی زندگی کے بارے میں تو جانتے ہی ہو');
INSERT INTO "bayan" VALUES(56,63,'56:63','کیا تم نے کبھی غور کیا کہ یہ بیج جو تم بوتے ہو ؟');
INSERT INTO "bayan" VALUES(56,64,'56:64','کیا تم اسے اگاتے ہو یا ہم اگانے والے ہیں ؟');
INSERT INTO "bayan" VALUES(56,65,'56:65','اگر ہم چاہیں تو اسے چورا چورا کردیں پھر تم بیٹھے رہو باتیں بناتے ہوئے');
INSERT INTO "bayan" VALUES(56,66,'56:66','کہ لو جی !) ہم پر تو بڑا تاوان پڑگیا۔');
INSERT INTO "bayan" VALUES(56,67,'56:67','بلکہ ہم تو بالکل ہی محروم ہو کر رہ گئے۔');
INSERT INTO "bayan" VALUES(56,68,'56:68','کبھی تم نے غور کیا کہ وہ پانی جو تم پیتے ہو ؟');
INSERT INTO "bayan" VALUES(56,69,'56:69','کیا بادلوں سے تم نے اسے برسایا ہے یا ہم ہیں برسانے والے ؟');
INSERT INTO "bayan" VALUES(56,70,'56:70','اگر ہم چاہیں تو اسے کڑوا کردیں تو تم کیوں شکر ادا نہیں کرتے ؟');
INSERT INTO "bayan" VALUES(56,71,'56:71','کبھی تم نے سوچا کہ وہ آگ جو تم جلاتے ہو ؟');
INSERT INTO "bayan" VALUES(56,72,'56:72','کیا اس کے درخت کو تم نے پیدا کیا ہے یا اس کے پیدا کرنے والے ہم ہیں ؟');
INSERT INTO "bayan" VALUES(56,73,'56:73','ہم نے بنا دیا اس کو ایک نشانی یاد دلانے کو اور ایک بہت فائدہ مند چیز صحرا کے مسافروں کے لیے۔');
INSERT INTO "bayan" VALUES(56,74,'56:74','پس تم تسبیح بیان کرو اپنے ربّ کے نام کی جو بہت عظمت والا ہے۔');
INSERT INTO "bayan" VALUES(56,75,'56:75','پس نہیں ! قسم ہے مجھے ان مقامات کی جہاں ستارے ڈوبتے ہیں۔');
INSERT INTO "bayan" VALUES(56,76,'56:76','اور یقینا یہ بہت بڑی قسم ہے اگر تم جانو');
INSERT INTO "bayan" VALUES(56,77,'56:77','یقینا یہ بہت عزت والا قرآن ہے۔');
INSERT INTO "bayan" VALUES(56,78,'56:78','ایک چھپی ہوئی کتاب میں۔');
INSERT INTO "bayan" VALUES(56,79,'56:79','اسے چھو نہیں سکتے مگر وہی جو بالکل پاک ہیں۔');
INSERT INTO "bayan" VALUES(56,80,'56:80','اس کا اتارا جانا ہے ربّ العالمین کی جانب سے');
INSERT INTO "bayan" VALUES(56,81,'56:81','۔ } تو کیا تم لوگ اس کتاب کے بارے میں مداہنت کر رہے ہو ؟');
INSERT INTO "bayan" VALUES(56,82,'56:82','اور تم نے اپنا نصیب یہ ٹھہرا لیا ہے کہ تم اس کو جھٹلا رہے ہو۔');
INSERT INTO "bayan" VALUES(56,83,'56:83','تو کیوں نہیں جب جان حلق میں آ (کر پھنس) جاتی ہے۔');
INSERT INTO "bayan" VALUES(56,84,'56:84','اور تم اس وقت دیکھ رہے ہوتے ہو۔');
INSERT INTO "bayan" VALUES(56,85,'56:85','اور ہم تمہارے مقابلے میں اس سے قریب تر ہوتے ہیں لیکن تم دیکھ نہیں پاتے۔');
INSERT INTO "bayan" VALUES(56,86,'56:86','تو اگر تم کسی کے اختیار میں نہیں ہو');
INSERT INTO "bayan" VALUES(56,87,'56:87','تو اس (جان) کو لوٹا کیوں نہیں لیتے اگر تم سچے ہو ؟');
INSERT INTO "bayan" VALUES(56,88,'56:88','۔ } پھر اگر وہ مقربین میں سے تھا۔');
INSERT INTO "bayan" VALUES(56,89,'56:89','تو اس کے لیے راحت اور سرور اور نعمتوں والی جنت ہے۔');
INSERT INTO "bayan" VALUES(56,90,'56:90','اور اگر وہ اصحاب الیمین میں سے تھا۔');
INSERT INTO "bayan" VALUES(56,91,'56:91','تو سلامتی پہنچے آپ کو اصحاب الیمین کی طرف سے۔');
INSERT INTO "bayan" VALUES(56,92,'56:92','اور اگر وہ تھا جھٹلانے والوں اور گمراہوں میں سے۔');
INSERT INTO "bayan" VALUES(56,93,'56:93','تو اس کے لیے مہمانی ہے کھولتے پانی سے۔');
INSERT INTO "bayan" VALUES(56,94,'56:94','اور جہنم میں جلنا۔');
INSERT INTO "bayan" VALUES(56,95,'56:95','یقینا یہ سب کچھ قطعی حق ہے۔');
INSERT INTO "bayan" VALUES(56,96,'56:96','پس آپ تسبیح کیجیے اپنے رب کے نام کی جو کہ بہت عظمت والا ہے۔');
INSERT INTO "bayan" VALUES(57,1,'57:1','تسبیح کرتی ہے اللہ کی ہر وہ شے جو آسمانوں میں ہے اور زمین میں ہے۔ اور وہ بہت زبردست ہے کمال حکمت والا۔');
INSERT INTO "bayan" VALUES(57,2,'57:2','اسی کے لیے ہے آسمانوں اور زمین کی بادشاہی۔ وہی زندہ رکھتا ہے وہی مارتا ہے۔ اور وہ ہرچیز پر قادر ہے۔');
INSERT INTO "bayan" VALUES(57,3,'57:3','وہی اول ہے وہی آخر ہے وہی ظاہر ہے وہی باطن ہے۔ اور وہ ہرچیز کا علم رکھتا ہے۔');
INSERT INTO "bayan" VALUES(57,4,'57:4','وہی ہے جس نے پیدا کیا آسمان اور زمین کو چھ دنوں میں پھر وہ متمکن ہوا عرش پر۔ وہ جانتا ہے جو کچھ داخل ہوتا ہے زمین میں اور جو کچھ نکلتا ہے اس سے اور جو کچھ اترتا ہے آسمان سے اور جو کچھ چڑھتا ہے اس میں۔ اور تم جہاں کہیں بھی ہوتے ہو وہ تمہارے ساتھ ہوتا ہے۔ اور جو کچھ تم کر رہے ہو اللہ اسے دیکھ رہا ہے۔');
INSERT INTO "bayan" VALUES(57,5,'57:5','آسمانوں اور زمین کی بادشاہی اسی کی ہے۔ اور تمام معاملات اسی کی طرف لوٹا دیے جائیں گے۔');
INSERT INTO "bayan" VALUES(57,6,'57:6','وہ پرولاتا ہے رات کو دن میں اور پرولاتا ہے دن کو رات میں۔ اور وہ جانتا ہے اس کو بھی جو سینوں کے اندر ہے۔');
INSERT INTO "bayan" VALUES(57,7,'57:7','ایمان لائو اللہ پر اور اس کے رسول صلى الله عليه وسلم پر اور خرچ کرو ان سب میں سے جن میں اس نے تمہیں خلافت عطا کی ہے۔ پس جو لوگ تم میں سے ایمان لائے اور انہوں نے (اپنے مال و جان کو) خرچ کیا ان کے لیے بہت بڑا اجر ہے۔');
INSERT INTO "bayan" VALUES(57,8,'57:8','اور تمہیں کیا ہوگیا ہے کہ تم نہیں ایمان رکھتے اللہ پر جبکہ رسول (محمد صلى الله عليه وسلم تمہیں دعوت دے رہے ہیں کہ تم ایمان رکھو اپنے رب پر اور دیکھو ! وہ تم سے عہد بھی لے چکا ہے اگر تم حقیقتاً مومن ہو');
INSERT INTO "bayan" VALUES(57,9,'57:9','وہی تو ہے جو اپنے بندے صلى الله عليه وسلم پر یہ آیات بینات نازل کر رہا ہے تاکہ نکالے تمہیں اندھیروں سے روشنی کی طرف۔ اور یقینا اللہ تمہارے حق میں بہت رئوف اور رحیم ہے۔');
INSERT INTO "bayan" VALUES(57,10,'57:10','اور تمہیں کیا ہوگیا ہے کہ تم خرچ نہیں کرتے اللہ کی راہ میں جبکہ اللہ ہی کے لیے ہے آسمانوں اور زمین کی وراثت تم میں سے وہ لوگ جنہوں نے انفاق کیا اور قتال کیا فتح سے پہلے وہ (فتح کے بعد انفاق اور قتال کرنے والوں کے) برابر نہیں ہیں۔ ان لوگوں کا درجہ بہت بلند ہے ان کے مقابلے میں جنہوں نے انفاق اور قتال کیا فتح کے بعد۔ اگرچہ ان سب سے اللہ نے بہت اچھا وعدہ فرمایا ہے۔ اور جو کچھ تم کر رہے ہو اللہ اس سے باخبر ہے۔');
INSERT INTO "bayan" VALUES(57,11,'57:11','کون ہے جو اللہ کو قرض دے قرض حسنہ ؟ کہ وہ اس کے لیے اسے بڑھاتا رہے اور اس کے لیے بڑا باعزت اجر ہے۔');
INSERT INTO "bayan" VALUES(57,12,'57:12','جس دن تم دیکھو گے مومن مردوں اور مومن عورتوں کو کہ ان کا نور دوڑتا ہوگا ان کے سامنے اور ان کے داہنی طرف (ان سے کہا جائے گا :) آج تمہارے لیے بشارت ہے ان جنتوں کی جن کے نیچے نہریں بہہ رہی ہیں تم رہو گے اس میں ہمیشہ ہمیش۔ یقینا یہی بہت بڑی کامیابی ہے۔');
INSERT INTO "bayan" VALUES(57,13,'57:13','جس دن کہیں گے منافق مرد اور منافق عورتیں ایمان والوں سے کہ ذرا ہمارا انتظار کرو کہ ہم بھی تمہاری روشنی سے فائدہ اٹھالیں۔ کہا جائے گا : لوٹ جائو پیچھے کی طرف اور تلاش کرو نور پھر ان کے درمیان ایک فصیل کھڑی کردی جائے گی جس میں ایک دروازہ ہوگا۔ اس (فصیل) کی اندرونی جانب رحمت ہوگی اور اس کے باہر کی طرف عذاب ہوگا۔');
INSERT INTO "bayan" VALUES(57,14,'57:14','وہ (منافق) انہیں پکار کر کہیں گے کیا ہم تمہارے ساتھ نہ تھے ؟ وہ کہیں گے : ہاں کیوں نہیں ! (تم تھے تو ہمارے ساتھ ہی)۔ لیکن تم نے اپنے آپ کو (اپنے ہاتھوں) فتنے میں ڈالا اور تم گومگو کی کیفیت میں مبتلا ہوگئے اور تم لوگ شکوک و شبہات میں پڑگئے اور تمہیں دھوکے میں ڈال دیا تمہاری خواہشات نے یہاں تک کہ اللہ کا فیصلہ آگیا اور تمہیں خوب دھوکہ دیا اللہ کے معاملے میں اس بڑے دھوکے باز نے۔');
INSERT INTO "bayan" VALUES(57,15,'57:15','تو آج نہ تم سے کوئی فدیہ قبول کیا جائے گا اور نہ ہی کافروں سے۔ تمہارا ٹھکانہ آگ ہے وہی تمہاری غم خوار ہے اور وہ بہت ہی برا ٹھکانہ ہے۔');
INSERT INTO "bayan" VALUES(57,16,'57:16','کیا ابھی وقت نہیں آیا ہے اہل ایمان کے لیے کہ ان کے دل جھک جائیں اللہ کی یاد کے لیے اور اس (قرآن) کے آگے کہ جو حق میں سے نازل ہوچکا ہے ؟ اور ان لوگوں کی طرح نہ ہوجائیں جنہیں پہلے کتاب دی گئی تھی تو جب ان پر ایک طویل مدت گزر گئی تو ان کے دل سخت ہوگئے۔ اور ان کی اکثریت اب فاسقوں پر مشتمل ہے۔');
INSERT INTO "bayan" VALUES(57,17,'57:17','جان لو کہ اللہ زندہ کردیتا ہے زمین کو اس کے مرنے کے بعد۔ ہم نے تو تمہارے لیے اپنی آیات خوب واضح کردی ہیں تاکہ تم عقل سے کام لو');
INSERT INTO "bayan" VALUES(57,18,'57:18','یقینا صدقہ دینے والے مرد اور صدقہ دینے والی عورتیں اور جو قرض دیں اللہ کو قرض حسنہ ان کو کئی گنا بڑھا کردیا جائے گا اور ان کے لیے بڑا باعزت اجر ہے۔');
INSERT INTO "bayan" VALUES(57,19,'57:19','اور جو لوگ ایمان لائے اللہ پر اور اس کے رسولوں پر وہی ہیں صدیق اور شہداء اپنے رب کے پاس ان کے لیے ہوگا ان کا اجر اور ان کا نور۔ اور جن لوگوں نے کفر کیا اور ہماری آیات کو جھٹلایا وہی دوزخ والے ہیں۔');
INSERT INTO "bayan" VALUES(57,20,'57:20','خوب جان لو کہ یہ دنیا کی زندگی اس کے سوا کچھ نہیں کہ ایک کھیل دل لگی کا سامان اور ظاہری ٹیپ ٹاپ ہے اور تمہارا آپس میں ایک دوسرے پر فخر جتانا اور مال و اولاد میں ایک دوسرے سے بڑھ جانے کی کوشش ہے۔ (انسانی زندگی کی مثال ایسے ہے) جیسے بارش برستی ہے تو اس سے پیدا ہونے والی روئیدگی کسانوں کو بہت اچھی لگتی ہے پھر وہ کھیتی اپنی پوری قوت پر آتی ہے پھر تم دیکھتے ہو کہ وہ زرد ہوجاتی ہے پھر وہ کٹ کر چورا چورا ہوجاتی ہے۔ اور آخرت میں بہت سخت عذاب ہے اور (یا پھر) اللہ کی طرف سے مغفرت اور (اس کی) رضا ہے۔ اور دنیا کی زندگی تو سوائے دھوکے کے ساز و سامان کے اور کچھ نہیں ہے۔');
INSERT INTO "bayan" VALUES(57,21,'57:21','ایک دوسرے سے آگے بڑھنے کی کوشش کرو اپنے رب کی مغفرت اور اس جنت کی طرف جس کی وسعت آسمان اور زمین جیسی ہے۔ وہ تیار کی گئی ہے ان لوگوں کے لیے جو ایمان لائے اللہ پر اور اس کے رسولوں پر۔ یہ اللہ کا فضل ہے جس کو بھی وہ چاہے گا دے گا۔ اور اللہ بہت بڑے فضل والا ہے۔');
INSERT INTO "bayan" VALUES(57,22,'57:22','نہیں پڑتی کوئی پڑنے والی مصیبت زمین میں اور نہ تمہاری اپنی جانوں میں مگر یہ کہ وہ ایک کتاب میں درج ہے اس سے پہلے کہ ہم اسے ظاہر کریں۔ یقینا یہ اللہ پر بہت آسان ہے۔');
INSERT INTO "bayan" VALUES(57,23,'57:23','تاکہ تم افسوس نہ کیا کرو اس پر جو تمہارے ہاتھ سے جاتا رہے اور اس پر اترایا نہ کرو جو وہ تمہیں دے دے اور اللہ کو بالکل پسند نہیں ہیں اترانے والے اور فخر کرنے والے۔');
INSERT INTO "bayan" VALUES(57,24,'57:24','جو لوگ بخل سے کام لیتے ہیں اور لوگوں کو بھی بخل کا مشورہ دیتے ہیں۔ اور جو کوئی رخ پھیرے گا تو (وہ سن رکھے کہ) اللہ بےنیاز اور اپنی ذات میں خود ستودہ صفات ہے۔');
INSERT INTO "bayan" VALUES(57,25,'57:25','ہم نے بھیجا اپنے رسولوں کو واضح نشانیوں کے ساتھ اور ہم نے ان کے ساتھ کتاب اور میزان اتاری تاکہ لوگ انصاف پر قائم ہوں اور ہم نے لوہا بھی اتارا ہے اس میں شدید جنگی صلاحیت ہے اور لوگوں کے لیے دوسری منفعتیں بھی ہیں۔ اور تاکہ اللہ جان لے کہ کون مدد کرتا ہے اس کی اور اس کے رسولوں کی غیب میں ہونے کے باوجود۔ یقینا اللہ بہت قوت والا بہت زبردست ہے۔');
INSERT INTO "bayan" VALUES(57,26,'57:26','ہم نے ہی بھیجا تھا نوح ؑ کو بھی اور ابراہیم ؑ کو بھی اور ہم نے انہی دونوں کی نسل میں رکھ دی نبوت اور کتاب } تو ان (کی نسل) میں کچھ تو ہدایت یافتہ بھی ہیں لیکن ان کی اکثریت فاسقوں پر مشتمل ہے');
INSERT INTO "bayan" VALUES(57,27,'57:27','پھر ہم نے بھیجے ان کے نقش قدم پر اپنے بہت سے رسول ؑ اور پھر ان کے پیچھے بھیجا ہم نے عیسیٰ ابن مریم کو اور اسے ہم نے انجیل عطا فرمائی اور جن لوگوں نے اس کی پیروی کی ہم نے ان کے دلوں میں بڑی نرمی اور رحمت پیدا کردی۔ اور رہبانیت کی بدعت انہوں نے خود ایجاد کی تھی ہم نے اسے ان پر لازم نہیں کیا تھا مگر اللہ کی خوشنودی کی تلاش میں پھر وہ اس کی رعایت بھی نہ کرسکے جیسا کہ اس کی رعایت کرنے کا حق تھا۔ تو ہم نے ان میں سے ان لوگوں کو ان کا اجر دیا جو ایمان لے آئے۔ لیکن ان کی اکثریت فاسقوں پر مشتمل ہے۔');
INSERT INTO "bayan" VALUES(57,28,'57:28','اے لوگو جو ایمان لائے ہو ! اللہ کا تقویٰ اختیار کرو اور اس کے رسول صلى الله عليه وسلم (محمد صلى الله عليه وسلم پر ایمان لائو وہ تمہیں دہرا حصہ عطا کرے گا اپنی رحمت سے اور وہ تمہیں ایسا نور عطا فرمائے گا جس کو لے کر تم چل سکوگے او وہ تمہیں بخش دے گا اور اللہ بہت بخشنے والا بہت رحم کرنے والا ہے۔');
INSERT INTO "bayan" VALUES(57,29,'57:29','(یہ اس لیے ہے) تاکہ اہل کتاب یہ نہ سمجھ لیں کہ اللہ کے فضل پر اب ان کا کوئی حق نہیں ہے اور فضل یقینا اللہ کے ہاتھ میں ہے وہ جس کو چاہتا ہے دیتا ہے۔ اور اللہ بہت بڑے فضل والا ہے۔');
INSERT INTO "bayan" VALUES(58,1,'58:1','اللہ نے سن لی اس عورت کی بات (جو اے نبی صلى الله عليه وسلم !) آپ سے جھگڑ رہی ہے اپنے شوہر کے بارے میں اور وہ اللہ سے بھی فریاد کر رہی ہے۔ اور اللہ سن رہا ہے آپ دونوں کے مابین ہونے والی گفتگو۔ یقینا اللہ سب کچھ سننے والا سب کچھ دیکھنے والا ہے۔');
INSERT INTO "bayan" VALUES(58,2,'58:2','(اے مسلمانو !) تم میں سے جو لوگ اپنی بیویوں کو مائیں کہہ بیٹھتے ہیں وہ ان کی مائیں نہیں بن جاتی ہیں۔ ان کی مائیں تو وہی ہیں جنہوں نے انہیں جنا ہے البتہ یہ لوگ ایک نہایت ناپسندیدہ اور جھوٹی بات کہتے ہیں۔ اور یقینا اللہ بہت معاف فرمانے والا بہت بخشنے والا ہے۔');
INSERT INTO "bayan" VALUES(58,3,'58:3','اور جو لوگ اپنی بیویوں سے ظہار کربیٹھیں پھر وہ اپنی کہی ہوئی بات سے واپس لوٹنا چاہیں تو ایک غلام کا آزاد کرنا ہوگا اس سے پہلے کہ وہ ایک دوسرے کو مس کریں۔ یہ بات ہے جس کی تمہیں نصیحت کی جا رہی ہے۔ اور جو کچھ تم کر رہے ہو اللہ اس سے باخبر ہے۔');
INSERT INTO "bayan" VALUES(58,4,'58:4','تو جو کوئی (غلام) نہ پائے وہ دو مہینوں کے روزے رکھے لگاتار اس سے پہلے کہ وہ دونوں ایک دوسرے کو چھوئیں۔ تو جو کوئی یہ بھی نہ کرسکتا ہو تو وہ ساٹھ مسکینوں کو کھانا کھلائے۔ یہ اس لیے تاکہ تم ایمان رکھو اللہ پر اور اس کے رسول صلى الله عليه وسلم پر۔ اور یہ اللہ کی (مقرر کردہ) حدود ہیں اور کافروں کے لیے بہت دردناک عذاب ہے۔');
INSERT INTO "bayan" VALUES(58,5,'58:5','یقینا وہ لوگ جو تل گئے ہیں مخالفت کرنے پر اللہ اور اس کے رسول صلى الله عليه وسلم کی اور ہم اتار چکے ہیں روشن آیات۔ اور کافروں کے لیے بہت ذلت والاعذاب ہے۔');
INSERT INTO "bayan" VALUES(58,6,'58:6','جس دن اللہ ان سب کو اٹھائے گا پھر انہیں جتلا دے گا ان کے اعمال کے بارے میں جو انہوں نے کیے تھے۔ اللہ نے ان (اعمال) کو محفوظ کر رکھا ہے جبکہ وہ انہیں بھول چکے ہیں۔ اور اللہ تو ہرچیز پر خودگواہ ہے۔');
INSERT INTO "bayan" VALUES(58,7,'58:7','کیا تم دیکھتے نہیں کہ اللہ جانتا ہے اس سب کچھ کے بارے میں جو آسمانوں میں ہے اور جو زمین میں ہے ؟ نہیں ہوتے کبھی بھی تین آدمی سرگوشیاں کرتے ہوئے مگر ان کا چوتھا وہ (اللہ) ہوتا ہے اور نہیں (سرگوشی کر رہے) ہوتے کوئی پانچ افراد مگر ان کا چھٹا وہ (اللہ) ہوتا ہے اور نہیں ہوتے وہ اس سے کم (یعنی دو افراد سرگوشی میں مصروف) اور نہ اس سے زیادہ مگر یہ کہ وہ ان کے ساتھ ہوتا ہے جہاں کہیں بھی وہ ہوں۔۔ } پھر وہ ان کو جتلا دے گا قیامت کے دن جو کچھ بھی انہوں نے عمل کیا تھا یقینا اللہ ہرچیز کا علم رکھنے والا ہے۔');
INSERT INTO "bayan" VALUES(58,8,'58:8','کیا تم نے دیکھا نہیں ان لوگوں کو جنہیں منع کیا گیا تھا نجویٰ سے پھر وہ اعادہ کر رہے ہیں اسی شے کا جس سے انہیں منع کیا گیا تھا اور وہ سرگوشیاں کرتے ہیں گناہ زیادتی اور رسول صلى الله عليه وسلم کی نافرمانی سے متعلق۔ اور جب وہ آپ صلى الله عليه وسلم کے پاس آتے ہیں تو آپ صلى الله عليه وسلم کو اس (کلمہ) سے دعا دیتے ہیں جس سے اللہ نے آپ صلى الله عليه وسلم کو دعا نہیں دی اور اپنے دل میں کہتے ہیں کہ اللہ ہمیں عذاب کیوں نہیں دیتا ہمارے اس طرح کہنے پر ؟ ان کے لیے تو اب جہنم ہی کافی ہے یہ اس میں داخل ہوں گے پس وہ بہت برا ٹھکانہ ہے۔');
INSERT INTO "bayan" VALUES(58,9,'58:9','اے اہل ایمان ! اگر تمہیں کوئی سرگوشی کرنی ہو تو گناہ زیادتی اور رسول صلى الله عليه وسلم کی نافرمانی کی باتوں سے متعلق ہرگز سرگوشی نہ کرو ہاں تم نیکی اور تقویٰ کے بارے میں سرگوشی کرسکتے ہو۔ اور اللہ سے ڈرتے رہو جس کی طرف تمہیں جمع کیا جائے گا۔');
INSERT INTO "bayan" VALUES(58,10,'58:10','یہ نجویٰ تو شیطان کی طرف سے ہے تاکہ وہ اہل ایمان کو رنجیدہ کرے حالانکہ یہ (شیطان) انہیں کچھ بھی ضرر پہنچانے پر قادر نہیں مگر اللہ کے اذن سے۔ اور اہل ایمان کو صرف اللہ پر توکل ّکرنا چاہیے۔');
INSERT INTO "bayan" VALUES(58,11,'58:11','اے مسلمانو ! جب تمہیں کہا جائے کہ مجالس میں کھل کر بیٹھو تو کھل جایا کرو اللہ تمہارے لیے کشادگی پیدا کر دے گا۔ اور جب کہا جائے کہ اٹھ جائو تو اٹھ جایا کرو اللہ بلند فرما دے گا ان لوگوں کے درجات جو تم میں سے واقعی ایمان والے ہیں اور جن کو حقیقی علم عطا ہوا ہے۔ اور تم جو کچھ کر رہے ہو اللہ اس سے باخبر ہے۔');
INSERT INTO "bayan" VALUES(58,12,'58:12','اے اہل ایمان ! جب تم رسول صلى الله عليه وسلم سے تخلیہ میں کوئی بات کرنا چاہو تو اپنی اس بات چیت سے پہلے کچھ صدقہ دے دیا کرو۔ یہ تمہارے لیے بہتر بھی ہے اور زیادہ پاکیزہ بھی۔ البتہ اگر تم (صدقہ دینے کے لیے) کچھ نہ پائو تو اللہ بہت بخشنے والا بہت رحم کرنے والا ہے۔');
INSERT INTO "bayan" VALUES(58,13,'58:13','کیا تم ڈر گئے اس سے کہ (رسول صلى الله عليه وسلم کے ساتھ) اپنی تنہائی کی باتوں سے پہلے صدقات پیش کرو ؟ پھر جب تم نے یہ نہیں کیا اور اللہ نے بھی تم پر نظر ِ عنایت فرما دی تو بس نماز قائم رکھو زکوٰۃ ادا کرتے رہو اور اللہ اور اس کے رسول صلى الله عليه وسلم کی اطاعت کرتے رہو۔ اور اللہ باخبر ہے اس سے جو تم کر رہے ہو۔');
INSERT INTO "bayan" VALUES(58,14,'58:14','کیا تم نے غور نہیں کیا ان لوگوں (کے طرزِعمل) پر جنہوں نے دوستی گانٹھی ہے ان لوگوں سے جن پر اللہ کا غضب ہوا ہے۔ نہ وہ تم میں سے ہیں اور نہ ان میں سے ہیں اور وہ جانتے بوجھتے جھوٹ پر قسمیں اٹھاتے ہیں۔');
INSERT INTO "bayan" VALUES(58,15,'58:15','اللہ نے تیار کر رکھا ہے ان کے لیے سخت عذاب یقینا بہت ہی برا ہے وہ طرزعمل جو وہ اختیار کیے ہوئے ہیں۔');
INSERT INTO "bayan" VALUES(58,16,'58:16','انہوں نے اپنی قسموں کو ڈھال بنا لیا ہے پس وہ اللہ کے راستے سے رک گئے ہیں (اور دوسروں کو بھی روکتے ہیں) پس ان کے لیے اہانت آمیز عذاب ہے۔');
INSERT INTO "bayan" VALUES(58,17,'58:17','ان کے کچھ بھی کام نہیں آئیں گے ان کے اموال اور نہ ہی ان کی اولادیں اللہ سے بچانے کے لیے۔ یہ آگ والے ہیں اسی میں یہ ہمیشہ ہمیش رہیں گے۔');
INSERT INTO "bayan" VALUES(58,18,'58:18','جس دن اللہ ان سب کو اٹھائے گا تو وہ اس کے سامنے بھی قسمیں کھائیں گے جیسے (آج) تمہارے سامنے قسمیں کھاتے ہیں اور وہ سمجھیں گے کہ وہ کسی بات پر ہیں۔ آگاہ ہو جائو کہ وہ بالکل جھوٹے ہیں۔');
INSERT INTO "bayan" VALUES(58,19,'58:19','شیطان نے ان کے اوپر قابو پا لیا ہے پس انہیں اللہ کی یاد بھلا دی ہے۔ یہ لوگ ہیں شیطان کی جماعت۔ آگاہ ہو جائو ! شیطان کی جماعت کے لوگ ہی حقیقت میں خسارہ اٹھانے والے ہیں۔');
INSERT INTO "bayan" VALUES(58,20,'58:20','یقینا جو لوگ اللہ اور اس کے رسول صلى الله عليه وسلم کی مخالفت پر ُ تلے بیٹھے ہیں وہی ذلیل ترین لوگوں میں سے ہوں گے۔');
INSERT INTO "bayan" VALUES(58,21,'58:21','اللہ نے لکھ دیا ہے کہ یقینا میں غالب رہوں گا اور میرے رسول۔ بیشک اللہ زبردست ہے زور آور ہے۔');
INSERT INTO "bayan" VALUES(58,22,'58:22','تم نہیں پائو گے ان لوگوں کو جو حقیقتاً ایمان رکھتے ہیں اللہ پر اور یوم آخرت پر کہ وہ محبت کرتے ہوں ان سے جو مخالفت کر رہے ہیں اللہ اور اس کے رسول صلى الله عليه وسلم کی خواہ وہ ان کے باپ ہوں یا ان کے بیٹے ہوں یا ان کے بھائی ہوں یا ان کے رشتے دار ہوں۔ یہ وہ لوگ ہیں جن کے دلوں میں اللہ نے ایمان لکھ دیا ہے اور وہ داخل کرے گا انہیں ان باغات میں جن کے دامن میں ندیاں بہتی ہوں گی جس میں وہ ہمیشہ ہمیش رہیں گے۔ اللہ ان سے راضی ہوگیا اور وہ اس سے راضی ہوگئے۔ یہ لوگ ہیں اللہ کی جماعت ! آگاہ ہو جائو ! یقینا اللہ کی جماعت کے لوگ ہی فلاح پانے والے ہیں۔');
INSERT INTO "bayan" VALUES(59,1,'59:1','تسبیح کرتی ہے اللہ کی ہر وہ شے جو آسمان میں ہے اور ہر وہ شے جو زمین میں ہے۔ اور وہ زبردست ہے کمال حکمت والا');
INSERT INTO "bayan" VALUES(59,2,'59:2','وہی ہے جس نے نکال باہر کیا ان کافروں کو جو اہل کتاب میں سے تھے ان کے گھروں سے پہلے جمع ہونے کے وقت (اے مسلمانو !) تمہیں یہ گمان نہیں تھا کہ وہ (اتنی آسانی سے) نکل جائیں گے اور وہ بھی سمجھتے تھے کہ ان کے قلعے انہیں اللہ (کی پکڑ) سے بچا لیں گے تو اللہ نے ان پر حملہ کیا وہاں سے جہاں سے انہیں گمان بھی نہ تھا اور ان کے دلوں میں اس نے رعب ڈال دیا وہ برباد کر رہے تھے اپنے گھروں کو خود اپنے ہاتھوں سے بھی اور اہل ایمان کے ہاتھوں سے بھی پس عبرت حاصل کرو اے آنکھیں رکھنے والو !');
INSERT INTO "bayan" VALUES(59,3,'59:3','اور اگر اللہ نے پہلے سے نہ لکھ دیا ہوتا ان پر جلاوطن ہونا تو انہیں سخت عذاب دیتا دنیوی زندگی میں اور آخرت میں ان کے لیے آگ کا عذاب ہے۔');
INSERT INTO "bayan" VALUES(59,4,'59:4','یہ اس لیے کہ انہوں نے اللہ اور اس کے رسول صلى الله عليه وسلم کی مخالفت کی اور جو اللہ کی مخالفت پر کمرکس لے تو (سمجھ لو کہ) اللہ سزا دینے میں بہت سخت ہے۔');
INSERT INTO "bayan" VALUES(59,5,'59:5','تم نے کھجور کے جو درخت کاٹے یا جن کو چھوڑ دیا ان کی جڑوں پر کھڑے تو یہ اللہ کے اذن سے ہوا اور تاکہ وہ فاسقوں کو ذلیل و رسوا کرے۔');
INSERT INTO "bayan" VALUES(59,6,'59:6','اور جو مال کہ ہاتھ لگا دیا ہے اللہ نے اپنے رسول صلى الله عليه وسلم کے ان (بنونضیر) سے تو (اے مسلمانو !) تم نے اس پر نہیں دوڑائے گھوڑے اور نہ اونٹ لیکن مسلط کردیتا ہے اللہ اپنے رسولوں ؑ کو جس پر چاہتا ہے۔ اور اللہ ہرچیز پر قادر ہے۔');
INSERT INTO "bayan" VALUES(59,7,'59:7','جو مال بھی ہاتھ لگا دے اللہ اپنے رسول صلى الله عليه وسلم کے بستیوں والوں سے تو وہ ہے اللہ کے لیے رسول صلى الله عليه وسلم کے لیے قرابت داروں یتیموں مسکینوں اور مسافروں کے لیے تاکہ وہ تم میں سے مال داروں ہی کے درمیان گردش میں نہ رہے۔ اور جو کچھ رسول صلى الله عليه وسلم تم لوگوں کو دے دیں وہ لے لو اور جس چیز سے روک دیں اس سے رک جائو۔ اور اللہ کا تقویٰ اختیار کرو۔ بیشک اللہ تعالیٰ سزا دینے میں بہت سخت ہے۔');
INSERT INTO "bayan" VALUES(59,8,'59:8','یہ (خاص طور پر) اُن تنگ دست مہاجرین کے لیے ہے جو نکال دیے گئے اپنے گھروں اور اپنے اموال سے وہ اللہ کے فضل اور اس کی رضا کے متلاشی ہیں اور اللہ اور اس کے رسول صلى الله عليه وسلم کی مدد کر رہے ہیں۔ یہی لوگ ہیں جو سچے ہیں۔');
INSERT INTO "bayan" VALUES(59,9,'59:9','اور (اس مال میں ان کا بھی حق ہے) جو آباد تھے اپنے گھروں میں اور جن کے پاس ایمان بھی تھا ان (مہاجرین کی آمد) سے پہلے وہ محبت کرتے ہیں ان سے جنہوں نے ہجرت کی ان کی طرف اور وہ نہیں پاتے اپنے سینوں میں کوئی حاجت اس بارے میں کہ جو کچھ ان (مہاجرین) کو دیا جاتا ہے اور وہ تو خود پر ترجیح دیتے ہیں دوسروں کو خواہ ان کے اپنے اوپر تنگی ہو۔ اور جو کوئی بھی بچالیا گیا اپنے جی کے لالچ سے تو وہی لوگ ہیں فلاح پانے والے۔');
INSERT INTO "bayan" VALUES(59,10,'59:10','اور وہ لوگ جو ان کے بعد آئے (مالِ فے پر ان کا بھی حق ہے) وہ کہتے ہیں : اے ہمارے ربّ ! تو بخش دے ہمیں بھی اور ہمارے ان بھائیوں کو بھی جو ایمان میں ہم سے سبقت لے گئے اور ہمارے دلوں میں اہل ایمان کے لیے کوئی کدورت نہ پیدا ہونے دے اے ہمارے رب بیشک تو نہایت شفیق اور رحم فرمانے والا ہے۔');
INSERT INTO "bayan" VALUES(59,11,'59:11','کیا تم نے دیکھا نہیں ان لوگوں کو جو نفاق میں مبتلا ہیں وہ کہتے ہیں اپنے ان بھائیوں سے جنہوں نے اہل کتاب میں سے کفر کیا ہے کہ اگر تم لوگوں کو (کبھی مدینہ سے) نکالا گیا تو ہم تمہارے ساتھ نکلیں گے اور تمہارے معاملے میں ہم کسی کی بھی اطاعت نہیں کریں گے اور اگر تمہارے ساتھ جنگ کی گئی تو ہم لازماً تمہاری مدد کریں گے۔ اور اللہ گواہی دیتا ہے کہ یہ بالکل جھوٹے ہیں۔');
INSERT INTO "bayan" VALUES(59,12,'59:12','اگر وہ نکالے گئے تو یہ ان کے ساتھ نہیں نکلیں گے۔ اور اگر ان سے جنگ کی گئی تو یہ ان کی مدد نہیں کریں گے۔ اور اگر انہوں نے کبھی ان کی مدد کی بھی تو پیٹھ دکھا دیں گے پھر ان کی کہیں سے مدد نہیں ہو سکے گی');
INSERT INTO "bayan" VALUES(59,13,'59:13','(اے مسلمانو !) یقینا تمہارا ڈر ان کے دلوں میں اللہ کی نسبت شدید تر ہے۔ یہ اس لیے ہے کہ یہ ناسمجھ لوگ ہیں۔');
INSERT INTO "bayan" VALUES(59,14,'59:14','یہ کبھی اکٹھے ہو کر تمہارے خلاف جنگ نہیں کریں گے سوائے اس کے کہ قلعہ بند بستیوں میں (رہ کر لڑیں) یا دیواروں کے پیچھے سے ان کے آپس کے جھگڑے بہت سخت ہیں۔ تم انہیں متحد گمان کرتے ہو حالانکہ ان کے دل پھٹے ہوئے ہیں۔ یہ اس لیے کہ یہ ایک ایسا گروہ ہیں جو عقل سے کام نہیں لیتے۔');
INSERT INTO "bayan" VALUES(59,15,'59:15','(ان کا وہی حال ہوگا) جیسے ان لوگوں کا معاملہ ہوا جو ان سے پہلے قریب ہی اپنے کیے کی سزا چکھ چکے ہیں اور ان کے لیے بہت ہی دردناک عذاب ہے۔');
INSERT INTO "bayan" VALUES(59,16,'59:16','جیسے شیطان کی مثال جب وہ انسان کو کہتا ہے کہ کفر کر ! پھر جب وہ کفر کا ارتکاب کرلیتا ہی تو وہ (شیطان) کہتا ہے میں تم سے لاتعلق ہوں میں تو ڈرتا ہوں اللہ سے جو تمام جہانوں کا رب ہے۔');
INSERT INTO "bayan" VALUES(59,17,'59:17','تو ان دونوں کا انجام یہ ہے کہ وہ آگ میں رہیں گے ہمیشہ ہمیش۔ اور یہی ہے بدلہ ظالموں کا۔');
INSERT INTO "bayan" VALUES(59,18,'59:18','اے اہل ِایمان ! اللہ کا تقویٰ اختیار کرو اور ہر جان کو دیکھتے رہنا چاہیے کہ اس نے کل کے لیے کیا آگے بھیجا ہے اور اللہ کا تقویٰ اختیار کرو یقینا تم جو کچھ کر رہے ہو اللہ اس سے باخبر ہے');
INSERT INTO "bayan" VALUES(59,19,'59:19','اور (اے مسلمانو دیکھنا !) تم ان لوگوں کی طرح نہ ہوجانا جنہوں نے اللہ کو بھلا دیا تو اللہ نے انہیں اپنے آپ سے غافل کردیا۔ یہی لوگ ہیں جو فاسق ہیں۔');
INSERT INTO "bayan" VALUES(59,20,'59:20','(دیکھو !) برابر نہیں ہوسکتے آگ والے اور جنت والے۔ یقینا جنت والے ہی کامیاب ہوں گے۔');
INSERT INTO "bayan" VALUES(59,21,'59:21','اگر ہم اس قرآن کو اتار دیتے کسی پہاڑ پر تو تم دیکھتے کہ وہ دب جاتا اور پھٹ جاتا اللہ کے خوف سے۔ اور یہ مثالیں ہیں جو ہم لوگوں کے لیے بیان کرتے ہیں تاکہ وہ غور کریں۔');
INSERT INTO "bayan" VALUES(59,22,'59:22','وہی ہے اللہ جس کے سوا کوئی معبود برحق نہیں۔ وہ جاننے والا ہے چھپے کا اور کھلے کا۔');
INSERT INTO "bayan" VALUES(59,23,'59:23','وہی ہے اللہ جس کے سوا کوئی الٰہ نہیں۔ حقیقی بادشاہ یکسرپاک سراپا سلامتی (اور ہر اعتبار سے سالم) امن دینے والا پناہ میں لینے والا زبردست (مطلق العنان) اپنا حکم بزور نافذ کرنے والا سب بڑائیوں کا مالک۔ اللہ پاک ہے ان تمام چیزوں سے جو یہ شرک کرتے ہیں۔');
INSERT INTO "bayan" VALUES(59,24,'59:24','وہی ہے اللہ تخلیق کا منصوبہ بنانے والا وجود بخشنے والا صورت گری کرنے والا۔ تمام اچھے نام اسی کے ہیں۔ اسی کی تسبیح کرتی ہے ہر وہ چیز جو آسمانوں میں ہے اور زمین میں ہے اور وہ بہت زبردست ہے کمال حکمت والا۔');
INSERT INTO "bayan" VALUES(60,1,'60:1','اے اہل ِایمان ! تم میرے دشمنوں اور اپنے دشمنوں کو اپنا دوست نہ بنائو تم ان کی طرف دوستی اور محبت کے پیغامات بھیجتے ہو حالانکہ انہوں نے انکار کیا ہے اس حق کا جو تمہارے پاس آیا ہے۔ وہ رسول صلى الله عليه وسلم کو اور تم لوگوں کو صرف اس بنا پر جلاوطن کرتے ہیں کہ تم ایمان رکھتے ہو اللہ اپنے رب پر۔ اگر تم نکلے تھے میرے راستے میں جہاد کرنے اور میری رضاجوئی کے لیے (تو تمہارا یہ طرزعمل اس کے منافی ہے) تم انہیں خفیہ پیغامات بھیجتے ہو محبت اور دوستی کے اور میں خوب جانتا ہوں جسے تم چھپاتے ہو اور جسے تم ظاہر کرتے ہو۔ اور جو کوئی بھی تم میں سے یہ کام کرے تو وہ یقینا سیدھے راستے سے بھٹک گیا');
INSERT INTO "bayan" VALUES(60,2,'60:2','اگر وہ تمہیں کہیں پالیں تو وہ تمہارے ساتھ دشمنی کریں گے اور تمہاری طرف بڑھائیں گے وہ اپنے ہاتھ اور اپنی زبانیں برے ارادے کے ساتھ اور ان کی شدید خواہش ہوگی کہ تم بھی کافر ہو جائو۔');
INSERT INTO "bayan" VALUES(60,3,'60:3','تمہیں ہرگز نفع نہیں پہنچائیں گے تمہارے رحمی رشتے اور نہ تمہاری اولادیں قیامت کے دن۔ اللہ فیصلہ کر دے گا تمہارے مابین۔ اور جو کچھ تم کر رہے ہو اللہ اسے دیکھ رہا ہے۔');
INSERT INTO "bayan" VALUES(60,4,'60:4','تمہارے لیے بہت اچھا نمونہ ہے ابراہیم ؑ اور ان کے ساتھیوں (کے طرزِعمل) میں جب انہوں نے اپنی قوم سے برملا کہہ دیا کہ ہم بالکل بری ہیں تم سے اور ان سے جنہیں تم پوجتے ہو اللہ کے سوا۔ ہم تم سے منکر ہوئے اور اب ہمارے اور تمہارے درمیان عداوت اور بغض کا سلسلہ شروع ہوگیا ہے ہمیشہ کے لیے یہاں تک کہ تم بھی ایمان لے آئو اللہ پر توحید کے ساتھ سوائے ابراہیم ؑ کے اپنے باپ سے یہ کہنے کے کہ میں آپ کے لیے ضرور استغفار کروں گا اور میں آپ کے بارے میں اللہ کے ہاں کچھ بھی اختیار نہیں رکھتا۔ پروردگار ! ہم نے تجھ پر ہی تو کل ّکیا اور تیری ہی طرف رجوع کیا اور ہمیں تیری طرف ہی لوٹ کر جانا ہے۔');
INSERT INTO "bayan" VALUES(60,5,'60:5','پروردگار ! تو ہمیں کافروں کے لیے تختہ امتحان نہ بنا دینا اور تو ہمیں بخش دے اے ہمارے پروردگار ! یقینا تو ہی زبردست اور حکمت والا ہے۔');
INSERT INTO "bayan" VALUES(60,6,'60:6','تمہارے لیے یقینا ان (کے طرزِعمل) میں ایک بہت اچھا نمونہ ہے اس کے لیے جو اللہ تعالیٰ (سے ملاقات) اور یوم آخرت کی امید رکھتا ہو');
INSERT INTO "bayan" VALUES(60,7,'60:7','بعید نہیں کہ اللہ تمہارے اور ان لوگوں کے درمیان جن سے تمہاری دشمنی ہے دوستی پیدا کر دے۔ اللہ ہر شے پر قادر ہے اور اللہ بہت بخشنے والا بہت رحم کرنے والا ہے');
INSERT INTO "bayan" VALUES(60,8,'60:8','اللہ تمہیں نہیں روکتا ان لوگوں سے جنہوں نے دین کے معاملے میں تم سے کبھی جنگ نہیں کی اور تمہیں تمہارے گھروں سے نہیں نکالا کہ تم ان کے ساتھ کوئی بھلائی کرو یا انصاف کا معاملہ کرو۔ بیشک اللہ تعالیٰ انصاف کرنے والوں کو پسند کرتا ہے۔');
INSERT INTO "bayan" VALUES(60,9,'60:9','وہ تو تمہیں منع کرتا ہے ان لوگوں سے جنہوں نے تم سے دین کے معاملے میں جنگ کی اور تمہیں تمہارے گھروں سے نکالا اور تمہارے نکالنے میں دوسروں کی مدد کی کہ تم ان کے ساتھ ولایت اور دوستی کا رشتہ قائم کرو۔ اور جو کوئی ان کے ساتھ دوستی کرے گا تو وہی لوگ ہیں جو ظالم ہیں');
INSERT INTO "bayan" VALUES(60,10,'60:10','اے اہل ِایمان ! جب تمہارے پاس آئیں مومن خواتین ہجرت کر کے تو ان کا امتحان لے لیا کرو اللہ تو ان کے ایمان کے بارے میں بہت اچھی طرح جانتا ہے۔ پھر اگر تم جان لو کہ وہ واقعی مومنات ہیں تو انہیں کفار کی طرف مت لوٹائو۔ نہ اب یہ ان (کافروں) کے لیے حلال ہیں اور نہ وہ ان کے لیے حلال ہیں اور ان (کافروں) کو ادا کر دو جو کچھ انہوں نے خرچ کیا تھا۔ اور (اے مسلمانو !) تم پر کوئی گناہ نہیں اگر تم ان خواتین سے نکاح کرلو جبکہ تم انہیں ان کے مہر ادا کر دو۔ اور تم مانگ لو وہ مال جو تم نے (بطورِ مہر) خرچ کیا ہے اور وہ (کافر) بھی مانگ لیں جو کچھ انہوں نے خرچ کیا ہے۔ یہ اللہ کا حکم ہے وہ تمہارے مابین فیصلہ کر رہا ہے۔ اور اللہ سب کچھ جاننے والا کمال حکمت والا ہے۔');
INSERT INTO "bayan" VALUES(60,11,'60:11','اور اگر تمہاری بیویوں (کے مہر میں) سے کچھ کفار کی طرف رہ جائے تو جب تمہیں موقع ہاتھ آجائے تو جن لوگوں کی بیویاں جاتی رہی ہیں انہیں اتنی رقم ادا کر دو جتنی انہوں نے خرچ کی ہے۔ اور اس اللہ کا تقویٰ اختیار کرو جس پر تمہارا ایمان ہے۔');
INSERT INTO "bayan" VALUES(60,12,'60:12','اے نبی صلى الله عليه وسلم جب آپ کے پاس آئیں مومن خواتین آپ سے بیعت کرنے کے لیے اس بات پر کہ وہ اللہ کے ساتھ کسی کو شریک نہیں کریں گی اور نہ چوری کریں گی نہ بدکاری کریں گی اور نہ اپنی اولاد کو قتل کریں گی اور نہ وہ کوئی بہتان باندھیں گی جو ان کے ہاتھوں اور پائوں کے مابین سے ہو تو (اے نبی صلى الله عليه وسلم آپ ان سے بیعت لے لیں اور ان کے لیے استغفار کریں۔ یقینا اللہ بہت بخشنے والا بہت رحم کرنے و الا ہے۔');
INSERT INTO "bayan" VALUES(60,13,'60:13','اے اہل ِایمان ! ان لوگوں کے ساتھ دوستی مت کرو جن پر اللہ کا غضب نازل ہوا ہے یہ لوگ آخرت سے مایوس ہوچکے ہیں جیسے کہ کفار مایوس ہوچکے ہیں اصحابِ قبور میں سے');
INSERT INTO "bayan" VALUES(61,1,'61:1','تسبیح کرتی ہے اللہ کی ہر وہ شے جو آسمانوں میں ہے اور زمین میں ہے اور وہ بہت زبردست ہے کمال حکمت والا۔');
INSERT INTO "bayan" VALUES(61,2,'61:2','اے مسلمانو ! تم کیوں کہتے ہو وہ جو کرتے نہیں ہو ؟');
INSERT INTO "bayan" VALUES(61,3,'61:3','بڑی شدید بیزاری کی بات ہے اللہ کے نزدیک کہ تم وہ کہو جو تم کرتے نہیں');
INSERT INTO "bayan" VALUES(61,4,'61:4','اللہ کو تو محبوب ہیں وہ بندے جو اس کی راہ میں صفیں باندھ کر قتال کرتے ہیں جیسے کہ وہ سیسہ پلائی دیوار ہوں۔');
INSERT INTO "bayan" VALUES(61,5,'61:5','اور یاد کرو جب کہ موسیٰ ؑ نے اپنی قوم سے کہا : اے میری قوم کے لوگو ! تم کیوں مجھے ایذا دے رہے ہو ؟ جبکہ تم جانتے ہو کہ میں تمہاری طرف اللہ کا رسول ہوں۔ پھر جب وہ ٹیڑھے ہوگئے تو اللہ نے ان کے دلوں کو ٹیڑھا کردیا۔ اور اللہ فاسقوں کو (زبردستی) ہدایت نہیں دیتا۔');
INSERT INTO "bayan" VALUES(61,6,'61:6','اور یاد کرو جب عیسیٰ ؑ ابن مریم نے کہا کہ اے بنی اسرائیل ! میں اللہ کا رسول ہوں تمہاری طرف میں تصدیق کرتے ہوئے آیا ہوں اس کی جو میرے سامنے موجود ہے تورات میں سے اور بشارت دیتا ہوا ایک رسول کی جو میرے بعد آئیں گے ان کا نام احمد صلى الله عليه وسلم ہوگا۔ پھر جب وہ (عیسیٰ ؑ آئے ان کے پاس واضح نشانیوں کے ساتھ تو انہوں نے کہہ دیا کہ یہ تو کھلا جادو ہے۔');
INSERT INTO "bayan" VALUES(61,7,'61:7','اور اس سے بڑھ کر ظالم کون ہوگا جو اللہ پر جھوٹ تراشے درآں حالیکہ اسے بلایا جا رہاہو اسلام کی طرف ! اور اللہ ایسے ظالموں کو (زبردستی) ہدایت نہیں دیتا۔');
INSERT INTO "bayan" VALUES(61,8,'61:8','وہ ُ تلے ہوئے ہیں کہ اللہ کے نور کو اپنے منہ (کی پھونکوں) سے بجھا کر رہیں گے اور اللہ اپنے نور کا اتمام فرما کر رہے گا خواہ یہ کافروں کو کتنا ہی ناگوار ہو۔');
INSERT INTO "bayan" VALUES(61,9,'61:9','وہی ہے (اللہ) جس نے بھیجا اپنے رسول صلى الله عليه وسلم کو الہدیٰ اور دین حق کے ساتھ تاکہ غالب کر دے اس کو پورے نظام زندگی پر اور خواہ مشرکوں کو یہ کتنا ہی ناگوار ہو !');
INSERT INTO "bayan" VALUES(61,10,'61:10','اے ایمان کے دعوے دارو ! کیا میں تمہیں ایسی تجارت کے بارے میں بتائوں جو تمہیں دردناک عذاب سے چھٹکارا دلا دے ؟');
INSERT INTO "bayan" VALUES(61,11,'61:11','(وہ یہ کہ) تم ایمان لائو اللہ اور اس کے رسول صلى الله عليه وسلم پر اور جہاد کرو اللہ کے راستے میں اپنے مالوں اور اپنی جانوں کے ساتھ۔ یہ تمہارے لیے بہتر ہے اگر تم سمجھ رکھتے ہو۔');
INSERT INTO "bayan" VALUES(61,12,'61:12','وہ تمہارے لیے تمہارے گناہ بخش دے گا اور تمہیں داخل کرے گا ان باغات میں جن کے دامن میں نہریں بہتی ہوں گی اور بہت پاکیزہ مساکن عطا کرے گا رہنے کے باغات میں۔ یہی سب سے بڑی کامیابی ہے۔');
INSERT INTO "bayan" VALUES(61,13,'61:13','اور ایک اور چیز جو تمہیں بہت پسند ہے۔ اللہ کی طرف سے مدد اور قریبی فتح۔ اور (اے نبی صلى الله عليه وسلم آپ مومنین کو خوشخبری سنا دیجیے۔');
INSERT INTO "bayan" VALUES(61,14,'61:14','اے اہل ایمان ! تم اللہ کے مددگار بن جائو جیسے کہا تھا عیسیٰ ؑ ابن مریم نے اپنے حواریوں سے کون ہے میرا مددگار اللہ کی طرف ؟ حواریوں نے کہا کہ ہم ہیں اللہ کے مددگار ! تو بنی اسرائیل کا ایک گروہ (حضرت مسیح ؑ پر) ایمان لے آیا اور دوسرا گروہ کفر پر اڑا رہا۔ تو ہم نے مدد کی ان کی جو ایمان لائے تھے ان کے دشمنوں کے خلاف تو (بالآخر) وہی غالب ہوئے۔');
INSERT INTO "bayan" VALUES(62,1,'62:1','تسبیح کرتی ہے اللہ کی ہر وہ چیز جو آسمانوں میں ہے اور جو زمین میں ہے جو بادشاہ ہے (ہر عیب سے) پاک ہے بہت زبردست ہے بہت حکمت والا ہے۔');
INSERT INTO "bayan" VALUES(62,2,'62:2','وہی تو ہے جس نے اٹھایا امیین میں ایک رسول ان ہی میں سے جو ان کو پڑھ کر سناتا ہے اس کی آیات اور ان کا تزکیہ کرتا ہے اور انہیں تعلیم دیتا ہے کتاب و حکمت کی۔ اور یقینا اس سے پہلے تو وہ کھلی گمراہی میں تھے۔');
INSERT INTO "bayan" VALUES(62,3,'62:3','اور ان ہی میں سے ان دوسرے لوگوں میں بھی جو ابھی ان میں شامل نہیں ہوئے۔ اور وہ بہت زبردست ہے کمال حکمت والا ہے۔');
INSERT INTO "bayan" VALUES(62,4,'62:4','یہ اللہ کا فضل ہے وہ دیتا ہے جسے چاہتا ہے۔ اور اللہ بڑے فضل والا ہے۔');
INSERT INTO "bayan" VALUES(62,5,'62:5','مثال ان لوگوں کی جو حامل تورات بنائے گئے پھر وہ اس کے حامل ثابت نہ ہوئے اس گدھے کی سی (مثال) ہے جو اٹھائے ہوئے ہو کتابوں کا بوجھ۔ بہت بری مثال ہے اس قوم کی جنہوں نے اللہ کی آیات کو جھٹلایا۔ اور اللہ ایسے ظالموں کو (زبردستی) ہدایت نہیں دیتا۔');
INSERT INTO "bayan" VALUES(62,6,'62:6','(اے نبی صلى الله عليه وسلم !) آپ کہہ دیجیے کہ اے وہ لوگو جو یہودی ہوگئے ہو اگر تمہیں واقعی یہ گمان ہے کہ بس تم ہی اللہ کے دوست ہو باقی سب لوگوں کو چھوڑ کر تو تم موت کی تمنا کرو اگر تم واقعی سچے ہو۔');
INSERT INTO "bayan" VALUES(62,7,'62:7','اور (حقیقت یہ ہے کہ) یہ لوگ ہرگز کبھی موت کی تمنا نہیں کریں گے اپنے ان اعمال کے سبب جو ان کے ہاتھ آگے بھیج چکے ہیں۔ اور اللہ ان ظالموں سے خوب واقف ہے۔');
INSERT INTO "bayan" VALUES(62,8,'62:8','(اے نبی صلى الله عليه وسلم !) آپ کہہ دیجیے کہ وہ موت جس سے تم بھاگتے ہو وہ تم سے ملاقات کر کے رہے گی پھر تمہیں لوٹا دیا جائے گا اس ہستی کی طرف جو پوشیدہ اور ظاہر سب کا جاننے والا ہے پھر وہ تمہیں جتلا دے گا جو کچھ تم کرتے رہے تھے۔');
INSERT INTO "bayan" VALUES(62,9,'62:9','اے ایمان والو ! جب تمہیں پکارا جائے نماز کے لیے جمعہ کے دن تو دوڑو اللہ کے ذکر کی طرف اور کاروبار چھوڑ دو۔ یہی تمہارے لیے بہتر ہے اگر تم علم رکھتے ہو۔');
INSERT INTO "bayan" VALUES(62,10,'62:10','پھر جب نماز پوری ہوچکے تو زمین میں منتشر ہو جائو اور اللہ کا فضل تلاش کرو اور اللہ کو یاد کرو کثرت سے تاکہ تم فلاح پائو۔');
INSERT INTO "bayan" VALUES(62,11,'62:11','اور جب انہوں نے دیکھا تجارت کا معاملہ یا کوئی کھیل تماشا تو اس کی طرف چل دیے اور آپ صلى الله عليه وسلم کو کھڑا چھوڑ دیا۔ (اے نبی صلى الله عليه وسلم !) آپ کہہ دیجیے کہ جو کچھ اللہ کے پاس ہے وہ کہیں بہتر ہے کھیل کود اور تجارت سے۔ اور اللہ بہترین رزق عطا کرنے والا ہے۔');
INSERT INTO "bayan" VALUES(63,1,'63:1','(اے نبی صلى الله عليه وسلم !) جب منافق آپ کے پاس آتے ہیں تو کہتے ہیں : ہم گواہی دیتے ہیں کہ بیشک آپ اللہ کے رسول ہیں۔ اور اللہ جانتا ہے کہ یقینا آپ اس کے رسول ہیں۔ اور اللہ گواہی دیتا ہے کہ یہ منافقین یقینا جھوٹے ہیں۔');
INSERT INTO "bayan" VALUES(63,2,'63:2','انہوں نے اپنی قسموں کو ڈھال بنا رکھا ہے اور یہ اللہ کے راستے سے رُک گئے ہیں۔ بہت ہی برا کام ہے جو یہ لوگ کرر ہے ہیں۔');
INSERT INTO "bayan" VALUES(63,3,'63:3','یہ اس وجہ سے ہے کہ یہ لوگ ایمان لائے پھر کافر ہوگئے تو ان کے دلوں پر مہر لگا دی گئی پس یہ سمجھنے سے عاری ہوگئے۔');
INSERT INTO "bayan" VALUES(63,4,'63:4','(اے نبی صلى الله عليه وسلم !) جب آپ انہیں دیکھتے ہیں تو ان کے جسم آپ کو بڑے اچھے لگتے ہیں۔ اور اگر وہ بات کرتے ہیں تو آپ صلى الله عليه وسلم ان کی بات سنتے ہیں۔ (لیکن اصل میں) یہ دیوار سے لگائی ہوئی خشک لکڑیوں کی مانند ہیں۔ یہ ہر زور کی آواز کو اپنے ہی اوپر گمان کرتے ہیں۔ اللہ ان کو ہلاک کرے یہ کہاں سے پھرائے جا رہے ہیں !');
INSERT INTO "bayan" VALUES(63,5,'63:5','اور جب ان سے کہا جاتا ہے کہ آئو (اپنی غلطی مان لو) تاکہ اللہ کے رسول صلى الله عليه وسلم تمہارے لیے استغفار کریں اور آپ انہیں دیکھتے ہیں کہ وہ رک جاتے ہیں تکبر کرتے ہوئے۔');
INSERT INTO "bayan" VALUES(63,6,'63:6','(اے نبی صلى الله عليه وسلم !) ان کے لیے برابر ہے کہ آپ ان کے لیے استغفار کریں یا ان کے لیے استغفار نہ کریں۔ اللہ ان کو ہرگز معاف نہیں کرے گا۔ یقینا اللہ ایسے فاسقوں کو ہدایت نہیں دیتا۔');
INSERT INTO "bayan" VALUES(63,7,'63:7','یہی ہیں وہ لوگ جو کہتے ہیں کہ مت خرچ کرو ان پر جو اللہ کے رسول صلى الله عليه وسلم کے گرد جمع ہوگئے ہیں یہاں تک کہ یہ منتشر ہوجائیں۔ حالانکہ آسمانوں اور زمین کے خزانے تو اللہ ہی کے ہیں لیکن منافقین اس حقیقت کا فہم نہیں رکھتے۔');
INSERT INTO "bayan" VALUES(63,8,'63:8','وہ کہتے ہیں کہ اگر ہم مدینہ لوٹ گئے تو جو طاقتور ہیں وہ لازماً نکال باہر کریں گے وہاں سے ان کمزور لوگوں کو۔ حالانکہ اصل عزت تو اللہ اس کے رسول صلى الله عليه وسلم اور مومنین کے لیے ہے لیکن یہ منافق جانتے نہیں۔');
INSERT INTO "bayan" VALUES(63,9,'63:9','اے اہل ایمان ! تمہیں غافل نہ کرنے پائیں تمہارے اموال اور تمہاری اولاد اللہ کی یاد سے۔ اور جو کوئی ایسا کریں گے تو وہی خسارے میں رہیں گے۔');
INSERT INTO "bayan" VALUES(63,10,'63:10','اور خرچ کر دو اس میں سے جو کچھ ہم نے تمہیں دیا ہے اس سے پہلے پہلے کہ تم میں سے کسی کی موت کا وقت آجائے پھر وہ اس وقت کہے کہ اے میرے رب ! تو نے مجھے ایک قریب وقت تک کیوں مہلت نہ دی کہ میں صدقہ کرتا اور نیک لوگوں میں سے ہوجاتا !');
INSERT INTO "bayan" VALUES(63,11,'63:11','اور اللہ ہرگز مہلت نہیں دے گا کسی جان کو جب اس کا وقت معین آپہنچے گا اور جو کچھ تم کر رہے ہو اللہ اس سے باخبر ہے۔');
INSERT INTO "bayan" VALUES(64,1,'64:1','تسبیح کرتی ہے اللہ کی ہر وہ شے جو آسمانوں میں ہے اور ہر وہ شے جو زمین میں ہے۔ اسی کی بادشاہی ہے اور اسی کے لیے حمد ہے۔ اور وہ ہرچیز پر قادر ہے۔');
INSERT INTO "bayan" VALUES(64,2,'64:2','وہی ہے جس نے تمہیں پیدا کیا ہے پھر تم میں سے کوئی کافر ہے اور کوئی مومن۔ اور جو کچھ تم کر رہے ہو اللہ اسے دیکھ رہا ہے۔');
INSERT INTO "bayan" VALUES(64,3,'64:3','اس نے آسمانوں اور زمین کو پیدا کیا حق کے ساتھ اور اس نے تمہاری صورت گری کی تو بہت ہی عمدہ صورت گری کی۔ اور اسی کی طرف (سب کو) لوٹنا ہے۔');
INSERT INTO "bayan" VALUES(64,4,'64:4','وہ جانتا ہے جو کچھ آسمانوں اور زمین میں ہے اور وہ جانتا ہے جو کچھ تم چھپاتے ہو اور جو کچھ تم ظاہر کرتے ہو۔ اور اللہ اس سے بھی باخبر ہے جو تمہارے سینوں کے اندر ہے۔');
INSERT INTO "bayan" VALUES(64,5,'64:5','کیا تمہارے پاس خبریں آ نہیں چکی ہیں ان لوگوں کی جنہوں نے کفر کیا تھا پہلے تو انہوں نے اپنے کیے کی سزا چکھ لی اور ان کے لیے دردناک عذاب بھی ہے۔');
INSERT INTO "bayan" VALUES(64,6,'64:6','یہ اس لیے ہوا کہ ان کے پاس ان کے رسول آتے رہے واضح نشانیاں لے کر تو انہوں نے کہا کہ کیا انسان ہمیں ہدایت دیں گے ؟ پس انہوں نے کفر کیا اور رُخ پھیرلیا اور اللہ نے بھی (ان سے) بےنیازی اختیار کی۔ اور اللہ تو ہے ہی بےنیاز ستودہ صفات۔');
INSERT INTO "bayan" VALUES(64,7,'64:7','کافروں کو یہ زعم ہے کہ وہ (مرنے کے بعد) ہرگز اٹھائے نہیں جائیں گے۔ (اے نبی صلى الله عليه وسلم !) آپ کہہ دیجیے : کیوں نہیں ! مجھے میرے رب کی قسم ہے تم لازماً اٹھائے جائو گے پھر تمہیں لازماً جتلایا جائے گا ان اعمال کے بارے میں جو تم نے کیے ہیں۔ اور یہ اللہ پر بہت آسان ہے۔');
INSERT INTO "bayan" VALUES(64,8,'64:8','پس ایمان لائو اللہ پر اور اس کے رسول صلى الله عليه وسلم پر اور اس نور پر جو ہم نے نازل کیا ہے۔ اور جو کچھ تم کر رہے ہو اللہ اس سے باخبر ہے۔');
INSERT INTO "bayan" VALUES(64,9,'64:9','جس دن کہ وہ تمہیں جمع کرے گا جمع ہونے کے دن کے لیے وہی ہے ہار اور جیت کے فیصلے کا دن۔ اور جو کوئی ایمان لائے اللہ پر اور نیک اعمال کرے وہ اس کی برائیوں کو اس سے دور کر دے گا اور اسے ان باغات میں داخل کرے گا جن کے دامن میں نہریں بہتی ہوں گی وہ اس میں رہیں گے ہمیشہ ہمیش یہ ہے بہت بڑی کامیابی۔');
INSERT INTO "bayan" VALUES(64,10,'64:10','اور جنہوں نے انکار کیا اور تکذیب کی ہماری آیات کی وہی ہوں گے جہنمی وہ ہمیشہ رہیں گے اس میں۔ اور وہ بہت برا ٹھکانہ ہے۔');
INSERT INTO "bayan" VALUES(64,11,'64:11','نہیں آتی کوئی مصیبت مگر اللہ کے اذن سے۔ اور جو کوئی اللہ پر ایمان رکھتا ہے وہ اس کے دل کو ہدایت دے دیتا ہے۔ اور اللہ ہرچیز کا علم رکھنے والا ہے۔');
INSERT INTO "bayan" VALUES(64,12,'64:12','اور اطاعت کرو اللہ کی اور اطاعت کرو رسول صلى الله عليه وسلم کی۔ پھر اگر تم نے پیٹھ موڑ لی تو جان لو کہ ہمارے رسول صلى الله عليه وسلم کے ذمے تو صرف صاف صاف پہنچا دینے کی ذمہ داری ہے۔');
INSERT INTO "bayan" VALUES(64,13,'64:13','اللہ وہ ہے کہ اس کے سوا کوئی الٰہ نہیں۔ پس اہل ایمان کو اللہ ہی پر توکل کرنا چاہیے۔');
INSERT INTO "bayan" VALUES(64,14,'64:14','اے ایمان کے دعوے دارو ! تمہاری بیویوں اور تمہاری اولاد میں سے بعض تمہارے دشمن ہیں سو ان سے بچ کر رہو۔ اور اگر تم معاف کردیا کرو اور چشم پوشی سے کام لو اور بخش دیا کرو تو اللہ بہت بخشنے والا نہایت مہربان ہے۔');
INSERT INTO "bayan" VALUES(64,15,'64:15','تمہارے مال اور تمہاری اولاد تمہارے لیے امتحان ہیں۔ اور اللہ ہی کے پاس اجر عظیم ہے۔');
INSERT INTO "bayan" VALUES(64,16,'64:16','پس اللہ کا تقویٰ اختیار کرو اپنی حد ِامکان تک اور سنو اور اطاعت کرو اور خرچ کرو (اللہ کی راہ میں) یہی تمہارے لیے بہتر ہے۔ اور جو کوئی اپنے جی کے لالچ سے بچالیا گیا تو ایسے ہی لوگ ہوں گے فلاح پانے والے۔');
INSERT INTO "bayan" VALUES(64,17,'64:17','اگر تم اللہ کو قرض حسنہ دو گے تو وہ اسے تمہارے لیے کئی گنا بڑھا دے گا اور تمہیں بخش دے گا۔ اور اللہ شکور (یعنی قدر دان) بھی ہے اور حلیم (یعنی بردبار) بھی۔');
INSERT INTO "bayan" VALUES(64,18,'64:18','جاننے والا ہے چھپے اور کھلے سب کا وہ بہت زبردست ہے کمال حکمت والا ہے۔');
INSERT INTO "bayan" VALUES(65,1,'65:1','اے نبی صلى الله عليه وسلم !) جب آپ لوگ اپنی عورتوں کو طلاق دو تو ان کی عدت کے حساب سے طلاق دو اور عدت کا پورا لحاظ رکھو۔ اور اللہ کا تقویٰ اختیار کرو جو تمہارا ربّ ہے۔ انہیں مت نکال باہر کرو ان کے گھروں سے اور وہ خود بھی نہ نکلیں سوائے اس کے کہ وہ ارتکاب کریں کسی کھلی بےحیائی کا۔ اور یہ اللہ کی حدود ہیں۔ اور جو کوئی اللہ کی حدود سے تجاوز کرے گا تو اس نے اپنی ہی جان پر ظلم کیا۔ تمہیں نہیں معلوم کہ شاید اس کے بعد اللہ کوئی نئی صورت پیدا کردے۔');
INSERT INTO "bayan" VALUES(65,2,'65:2','پھر جب وہ اپنی (عدت کی) میعاد کو پہنچنے لگیں تو اب ان کو یا تو (اپنے نکاح میں) روک رکھو معروف طریقے سے یا جدا کردو معروف طریقے سے اور اپنے میں سے دو معتبر اشخاص کو گواہ بنا لو اور گواہی قائم کرو اللہ کے لیے۔ یہ ہے جس کی نصیحت کی جا رہی ہے ہراس شخص کو کہ جو ایمان رکھتا ہو اللہ پر اور یوم آخر پر۔ اور جو شخص اللہ کا تقویٰ اختیار کرے گا اللہ اس کے لیے (مشکلات سے) نکلنے کا راستہ پیدا کر دے گا۔');
INSERT INTO "bayan" VALUES(65,3,'65:3','اور اسے وہاں سے رزق دے گا جہاں سے اسے گمان بھی نہیں ہوگا۔ اور جو کوئی اللہ پر توکل کرتا ہے تو اس کے لیے وہ کافی ہے۔ اللہ تو یقینا اپنا کام پورا کر کے ہی رہتا ہے۔ اللہ نے ہرچیز کے لیے ایک اندازہ مقرر کر رکھا ہے۔');
INSERT INTO "bayan" VALUES(65,4,'65:4','اور تمہارے ہاں کی خواتین میں سے جو حیض سے مایوس ہوچکی ہوں اگر تمہیں شک ہو تو ان کی عدت تین ماہ ہوگی اور حاملہ خواتین کی عدت یہ ہے کہ ان کا وضع حمل ہوجائے۔ اور جو کوئی اللہ کا تقویٰ اختیار کرتا ہے وہ اس کے کاموں میں آسانی پیدا کردیتا ہے۔');
INSERT INTO "bayan" VALUES(65,5,'65:5','یہ اللہ کا حکم ہے جو اس نے نازل کردیا ہے تمہاری طرف۔ اور جو اللہ کا تقویٰ اختیار کرے گا وہ اس کی برائیوں کو اس سے دور فرما دے گا اور اسے بہت بڑا اجرو ثواب عطا کرے گا۔');
INSERT INTO "bayan" VALUES(65,6,'65:6','اور ان عورتوں کو وہیں رکھو جہاں تم خود رہتے ہو اپنی حیثیت کے مطابق اور انہیں کوئی تکلیف نہ پہنچائو انہیں تنگ کرنے کے لیے۔ پھر اگر وہ تمہارے لیے (تمہارے بچے کو) دودھ پلائیں تو انہیں ان کا معاوضہ ادا کرو۔ اور آپس میں مشورہ کرلیا کرو بھلے طریقے سے۔ اور اگر تم ایک دوسرے سے تنگی محسوس کرو تو پھر کوئی اور عورت اس کے لیے دودھ پلائے گی۔');
INSERT INTO "bayan" VALUES(65,7,'65:7','چاہیے کہ خرچ کرے وسعت والا اپنی وسعت کے مطابق۔ اور جس پر اس کا رزق تنگ کردیا گیا ہے وہ خرچ کرے اس میں سے جو اللہ نے اس کو دیا ہے۔ اللہ کسی جان کو ذمہ دار نہیں ٹھہراتا مگر اسی قدر جو اس نے اسے دے رکھا ہے۔ عنقریب اللہ تنگی کے بعد آسانی بھی پیدا کر دے گا۔');
INSERT INTO "bayan" VALUES(65,8,'65:8','اور کتنی ہی بستیاں ایسی ہیں جنہوں نے اپنے رب کے حکم اور اس کے رسولوں ؑ سے سرکشی کی تو ہم نے ان کا محاسبہ کیا بہت شدید محاسبہ اور ہم نے ان کو عذاب دیا بہت ہولناک عذاب۔');
INSERT INTO "bayan" VALUES(65,9,'65:9','تو انہوں نے اپنے معاملے کی پوری سزا بھگت لی اور ان کے کام کا انجام خسارہ ہی تھا۔');
INSERT INTO "bayan" VALUES(65,10,'65:10','اللہ نے ان کے لیے بہت شدید عذاب تیار کر رکھا ہے تو اللہ کا تقویٰ اختیار کرو اے ہوش مندو جو ایمان بھی لائے ہو ! اللہ نے تمہاری طرف ذکر نازل کردیا ہے۔');
INSERT INTO "bayan" VALUES(65,11,'65:11','(یعنی) ایک رسول صلى الله عليه وسلم جو اللہ کی آیات بینات تم لوگوں کو پڑھ کر سنا رہا ہے تاکہ وہ نکالے ان لوگوں کو جو ایمان لائے اور جنہوں نے نیک اعمال کیے اندھیروں سے نور کی طرف۔ اور جو کوئی اللہ پر ایمان رکھتا ہے اور نیک عمل کرتا ہے وہ اسے داخل کرے گا ان باغات میں جن کے نیچے ندیاں بہتی ہوں گی جن میں وہ لوگ رہیں گے ہمیشہ ہمیش۔ اللہ نے اس کے لیے بہت عمدہ رزق فراہم کیا ہے۔');
INSERT INTO "bayan" VALUES(65,12,'65:12','اللہ وہ ہے جس نے سات آسمان بنائے ہیں اور زمین میں سے بھی انہی کی مانند۔ ان کے درمیان (اللہ کا) امر نازل ہوتا ہے تاکہ تم یقین رکھو کہ اللہ ہرچیز پر قادر ہے اور یہ کہ اللہ نے اپنے علم سے ہر شے کا احاطہ کیا ہوا ہے۔');
INSERT INTO "bayan" VALUES(66,1,'66:1','اے نبی صلى الله عليه وسلم !) آپ کیوں حرام ٹھہرا رہے ہیں (اپنے اوپر) وہ شے جو اللہ نے آپ کے لیے حلال کی ہے ؟ آپ صلى الله عليه وسلم چاہتے ہیں اپنی بیویوں کی رضا جوئی ! اور اللہ بہت معاف کرنے والا بہت رحم کرنے والا ہے۔');
INSERT INTO "bayan" VALUES(66,2,'66:2','اللہ نے تمہارے لیے اپنی قسموں کو کھولنے کا طریقہ مقرر کردیا ہے۔ اور اللہ تمہارا مددگار ہے اور وہ سب کچھ جاننے والا کمال حکمت والا ہے۔');
INSERT INTO "bayan" VALUES(66,3,'66:3','اور جب نبی صلى الله عليه وسلم نے رازداری سے اپنی کسی زوجہ کو ایک بات بتائی۔ تو جب اس نے اس کو ظاہر کردیا اور اللہ نے اس بارے میں ان صلى الله عليه وسلم کو مطلع کردیا تو نبی صلى الله عليه وسلم نے اس پر کسی حد تک (اس بیوی کو) خبردار کیا اور کسی حد تک اس سے چشم پوشی کی۔ تو جب آپ صلى الله عليه وسلم نے اسے یہ خبر دی اس نے کہا کہ آپ صلى الله عليه وسلم کو یہ کس نے بتایا ؟ آپ صلى الله عليه وسلم نے کہا : مجھے اس نے بتایا ہے جو العلیم ہے اور الخبیر ہے۔');
INSERT INTO "bayan" VALUES(66,4,'66:4','اگر تم دونوں اللہ کی جناب میں توبہ کرو تو (یہی تمہارے لیے بہتر ہے کیونکہ) تمہارے دل تو مائل ہو ہی چکے ہیں۔ اور اگر تم دونوں نے ان کے خلاف گٹھ جوڑ کرلیا ہے ان صلى الله عليه وسلم کا پشت پناہ تو خود اللہ ہے اور جبریل اور تمام صالح مومنین اور مزید برآں تمام فرشتے بھی ان صلى الله عليه وسلم کے مددگار ہیں۔');
INSERT INTO "bayan" VALUES(66,5,'66:5','بعید نہیں کہ اگر وہ تم سب کو طلاق دے دیں تو ان کا رب انہیں تم سے کہیں بہتر بیویاں عطا کردے اطاعت شعار ایمان والیاں فرمانبردار توبہ کرنے والیاں عبادت گزار لذاتِ دنیوی سے بیگانہ شوہر دیدہ بھی اور کنواری بھی۔');
INSERT INTO "bayan" VALUES(66,6,'66:6','اے اہل ایمان ! بچائو اپنے آپ کو اور اپنے اہل و عیال کو اس آگ سے جس کا ایندھن بنیں گے انسان اور پتھر اس پر بڑے تند خو بہت سخت دل فرشتے مامور ہیں اللہ ان کو جو حکم دے گا وہ فرشتے اس کی نافرمانی نہیں کریں گے اور وہ وہی کریں گے جس کا انہیں حکم دیا جائے گا۔');
INSERT INTO "bayan" VALUES(66,7,'66:7','(اُس دن کہہ دیا جائے گا :) اے کافرو ! آج تم عذر مت پیش کرو۔ آج تمہیں بدلے میں وہی کچھ دیا جا رہا ہے جو تم عمل کرکے لائے ہو۔');
INSERT INTO "bayan" VALUES(66,8,'66:8','اے اہل ایمان ! توبہ کرو اللہ کی جناب میں خالص توبہ۔ امید ہے تمہارا رب تم سے تمہاری برائیوں کو دور کردے گا اور تمہیں داخل کرے گا ایسے باغات میں جن کے دامن میں ندیاں بہتی ہوں گی جس دن اللہ اپنے نبی صلى الله عليه وسلم کو اور ان کے ساتھ ایمان لانے والوں کو رسوا نہیں کرے گا۔ (اُس دن) ان کا نور دوڑتا ہوگا ان کے سامنے اور ان کے داہنی طرفوہ کہتے ہوں گے : اے ہمارے رب ! ہمارے لیے ہمارے نور کو کامل کر دے اور تو ہمیں بخش دے یقینا تو ہر شے پر قادر ہے۔');
INSERT INTO "bayan" VALUES(66,9,'66:9','اے نبی صلى الله عليه وسلم !) جہاد کیجیے کافروں سے بھی اور منافقوں سے بھی اور ان پر سختی کیجیے۔ ان کا ٹھکانہ جہنم ہے اور وہ بہت برا ٹھکانہ ہے۔');
INSERT INTO "bayan" VALUES(66,10,'66:10','اللہ نے مثال بیان کی ہے کافروں کے لیے نوح ؑ کی بیوی اور لوط ؑ کی بیوی کی۔ وہ دونوں ہمارے دو بہت صالح بندوں کے عقد میں تھیں تو انہوں نے ان سے خیانت کی تو وہ دونوں اللہ کے مقابل میں ان کے کچھ بھی کام نہ آسکے اور (آخرت میں) کہہ دیاجائے گا کہ تم دونوں داخل ہوجائو آگ میں دوسرے سب داخل ہونے والوں کے ساتھ۔');
INSERT INTO "bayan" VALUES(66,11,'66:11','اور اہل ِا یمان (خواتین) کے لیے اللہ نے مثال بیان کی ہے فرعون کی بیوی کی۔ جب اس نے کہا : اے میرے پروردگار ! ُ تو میرے لیے بنا دے اپنے پاس ایک گھر جنت میں اور مجھے نجات دے دے فرعون سے بھی اور اس کے عمل سے بھی اور مجھے اس ظالم قوم سے (جلد از جلد) چھٹکارا دلا دے۔');
INSERT INTO "bayan" VALUES(66,12,'66:12','اور عمران کی بیٹی مریم ؑ جس نے اپنی عصمت کی حفاظت کی تو ہم نے اس میں اپنی روح میں سے پھونکا اور اس نے تصدیق کی اپنے رب کی تمام باتوں کی اور اس کی کتابوں کی اور وہ بہت ہی فرمانبرداروں میں سے تھیں۔');
INSERT INTO "bayan" VALUES(67,1,'67:1','بہت ہی بابرکت ہے وہ ہستی جس کے ہاتھ میں بادشاہی ہے اور وہ ہرچیز پر قادر ہے۔');
INSERT INTO "bayan" VALUES(67,2,'67:2','جس نے موت اور زندگی کو پیدا کیا تاکہ تمہیں آزمائے کہ تم میں سے کون اچھے اعمال کرنے والا ہے۔ اور وہ بہت زبردست بھی ہے اور بہت بخشنے والا بھی۔');
INSERT INTO "bayan" VALUES(67,3,'67:3','(وہ اللہ کہ) جس نے بنائے سات آسمان ایک دوسرے کے اوپر۔ تم نہیں دیکھ پائو گے رحمن کی تخلیق میں کہیں کوئی فرق۔ پھر لوٹائو نگاہ کو کیا تمہیں کہیں کوئی رخنہ نظر آتا ہے ؟');
INSERT INTO "bayan" VALUES(67,4,'67:4','پھر لوٹائو نگاہ کو بار بار (کوئی رخنہ ڈھونڈنے کے لیے) پلٹ آئے گی نگاہ تمہاری طرف ناکام تھک ہار کر۔');
INSERT INTO "bayan" VALUES(67,5,'67:5','اور ہم نے سب سے قریبی آسمان کو سجا دیا ہے چراغوں سے اور ان کو بنا دیا ہے ہم نے شیاطین کو نشانہ بنانے کا ذریعہ اور ان کے لیے ہم نے تیار کر رکھا ہے جلا دینے والا عذاب۔');
INSERT INTO "bayan" VALUES(67,6,'67:6','اور جو لوگ اپنے رب کا کفر کریں (چاہے وہ انسان ہوں یا جنات) ان کے لیے جہنم کا عذاب ہے اور وہ بہت ہی ُ برا ٹھکانہ ہے۔');
INSERT INTO "bayan" VALUES(67,7,'67:7','جب وہ اس میں جھونکے جائیں گے تو اسے سنیں گے دھاڑتے ہوئے اور وہ بہت جوش کھا رہی ہوگی۔');
INSERT INTO "bayan" VALUES(67,8,'67:8','قریب ہوگا کہ وہ غصے سے پھٹ جائے۔ جب بھی ڈالا جائے گا اس میں کسی ایک گروہ کو تو اس کے داروغے ان سے پوچھیں گے : کیا تمہارے پاس کوئی خبردار کرنے والا نہیں آیا تھا ؟');
INSERT INTO "bayan" VALUES(67,9,'67:9','وہ کہیں گے کیوں نہیں ! ہمارے پاس خبردار کرنے والا آیا تھا لیکن ہم نے اسے جھٹلا دیا اور ہم نے کہا کہ اللہ نے کوئی شے نہیں اتاری۔ تم تو کھلی گمراہی میں مبتلا ہوگئے ہو۔');
INSERT INTO "bayan" VALUES(67,10,'67:10','اور وہ کہیں گے کہ اگر ہم سنتے اور عقل سے کام لیتے تو ہم نہ ہوتے ان جہنم والوں میں سے۔');
INSERT INTO "bayan" VALUES(67,11,'67:11','پس وہ اپنے اصل گناہ کا اعتراف کرلیں گے۔ پس پھٹکار ہے جہنمی لوگوں کے لیے۔');
INSERT INTO "bayan" VALUES(67,12,'67:12','بیشک وہ لوگ جو اپنے رب سے ڈرتے ہیں غیب میں رہتے ہوئے ان کے لیے مغفرت بھی ہے اور بہت بڑا اجر بھی۔');
INSERT INTO "bayan" VALUES(67,13,'67:13','اور (دیکھو !) چاہے تم اپنی بات کو چھپا کر بیان کرو یا بلند آواز سے بیان کرو (وہ اس کو جانتا ہے)۔ وہ تو اس سے بھی واقف ہے جو تمہارے سینوں کے اندر پنہاں ہے۔');
INSERT INTO "bayan" VALUES(67,14,'67:14','کیا وہی نہ جانے گا جس نے پیدا کیا ہے ؟ اور وہ بہت باریک بین ہے ہر شے کی خبر رکھنے والا ہے۔');
INSERT INTO "bayan" VALUES(67,15,'67:15','وہی ہے جس نے تمہارے لیے بنا دیا ہے زمین کو پست تو تم چلو پھرو اس کے کندھوں کے مابین اور اس کے (دیے ہوئے) رزق سے کھائو پیو اور (یاد رکھو کہ تم نے) اسی کی طرف زندہ ہو کر جانا ہے۔');
INSERT INTO "bayan" VALUES(67,16,'67:16','کیا تم بےخوف ہوگئے ہو اس سے جو آسمان میں ہے کہ وہ تمہیں زمین میں دھنسا دے اور وہ یکایک لرزنے لگے۔');
INSERT INTO "bayan" VALUES(67,17,'67:17','کیا تم بےخوف ہوگئے ہو اس سے جو آسمان میں ہے کہ وہ تم پر پتھر برسانے والی آندھی بھیج دے ؟ پھر تمہیں معلوم ہوجائے گا کہ میرا خبردار کرنا کیسا تھا !');
INSERT INTO "bayan" VALUES(67,18,'67:18','اور یقینا ان سے پہلے والوں نے بھی جھٹلایا تھا تو کیسا ہوا (ان پر) میرا عذاب ؟');
INSERT INTO "bayan" VALUES(67,19,'67:19','کیا یہ لوگ دیکھتے نہیں پرندوں کو اپنے اوپر (اڑتے ہوئے) کبھی پروں کو پھیلائے ہوئے اور کبھی سمیٹے ہوئے ؟ نہیں روکے ہوئے انہیں کوئی (فضا میں) مگر رحمن ! یقینا وہ ہرچیز کو خوب دیکھنے والا ہے !');
INSERT INTO "bayan" VALUES(67,20,'67:20','بھلا وہ کون ہے جو تمہارا لشکر بن کر تمہاری مدد کرے رحمن کے مقابلہ میں ؟ نہیں ہیں یہ کافر مگر دھوکے میں مبتلا ہیں۔');
INSERT INTO "bayan" VALUES(67,21,'67:21','پھر بھلا کون ہے وہ جو تمہیں رزق دے سکے اگر اللہ اپنے رزق کو روک لے ؟ بلکہ یہ لوگ اپنی سرکشی اور حق سے گریز میں بہت آگے بڑھ گئے ہیں۔');
INSERT INTO "bayan" VALUES(67,22,'67:22','تو کیا وہ شخص جو اپنے منہ کے بل گھسٹ رہا ہے زیادہ ہدایت پر ہے یا وہ جو سیدھا ہو کر چل رہا ہے ایک سیدھے راستے پر ؟');
INSERT INTO "bayan" VALUES(67,23,'67:23','کہہ دیجیے کہ وہی ہے جس نے تمہیں پیدا کیا اور تمہارے لیے کان آنکھیں اور دل بنائے۔ بہت ہی کم شکر ہے جو تم لوگ کرتے ہو۔');
INSERT INTO "bayan" VALUES(67,24,'67:24','کہہ دیجیے اسی نے تمہیں پھیلا دیا ہے زمین میں اور اسی کی طرف تم اکٹھے کردیے جائو گے۔');
INSERT INTO "bayan" VALUES(67,25,'67:25','وہ کہتے ہیں کب پورا ہوگا یہ وعدہ ؟ اگر تم سچے ہو (تو بتائو) !');
INSERT INTO "bayan" VALUES(67,26,'67:26','کہہ دیجیے کہ یہ علم تو اللہ ہی کے پاس ہے اور میں تو بس ایک واضح طور پر خبردار کردینے والا ہوں۔');
INSERT INTO "bayan" VALUES(67,27,'67:27','پھر جب وہ دیکھیں گے اس کو اپنے قریب آتے تو ان کافروں کے چہرے بگڑ جائیں گے اور کہا جائے گا یہ ہے وہ چیز جس کا تم مطالبہ کرتے تھے۔');
INSERT INTO "bayan" VALUES(67,28,'67:28','(اے نبی صلى الله عليه وسلم !) ان سے کہیے کہ اگر اللہ مجھے اور جو لوگ میرے ساتھ ہیں ان کو ہلاک کردے یا وہ ہم پر رحم کرے تو کافروں کو دردناک عذاب سے کون بچائے گا ؟');
INSERT INTO "bayan" VALUES(67,29,'67:29','(اے نبی صلى الله عليه وسلم !) آپ کہیے کہ وہ تو رحمن ہے ہم اس پر ایمان لاچکے ہیں اور اسی پر ہمارا توکل ّہے۔ تو عنقریب تمہیں معلوم ہوجائے گا کہ کھلی گمراہی میں کون تھا !');
INSERT INTO "bayan" VALUES(67,30,'67:30','آپ صلى الله عليه وسلم کہیے کہ ذرا سوچو ! اگر تمہارا پانی گہرائی میں اتر جائے تو کون ہے جو لائے گا تمہارے پاس صاف نتھرا ہواپانی ؟');
INSERT INTO "bayan" VALUES(68,1,'68:1','ن قسم ہے قلم کی اور جو کچھ یہ لکھتے ہیں۔');
INSERT INTO "bayan" VALUES(68,2,'68:2','آپ اپنے رب کے فضل و کرم سے مجنون نہیں ہیں۔');
INSERT INTO "bayan" VALUES(68,3,'68:3','اور یقینا آپ کے لیے تو وہ اجر ہے جس کا سلسلہ کبھی منقطع نہیں ہوگا۔');
INSERT INTO "bayan" VALUES(68,4,'68:4','اور آپ صلى الله عليه وسلم یقینا اخلاق کے بلند ترین مرتبے پر فائز ہیں۔');
INSERT INTO "bayan" VALUES(68,5,'68:5','تو عنقریب آپ صلى الله عليه وسلم بھی دیکھ لیں گے اور وہ بھی دیکھ لیں گے۔');
INSERT INTO "bayan" VALUES(68,6,'68:6','کہ تم میں سے کون فتنے میں مبتلا تھا !');
INSERT INTO "bayan" VALUES(68,7,'68:7','یقینا آپ صلى الله عليه وسلم کا رب خوب جانتا ہے کہ کون اس کی راہ سے بھٹک گیا ہے اور وہ ان کو بھی خوب جانتا ہے جو ہدایت یافتہ ہیں۔');
INSERT INTO "bayan" VALUES(68,8,'68:8','تو (اے نبی صلى الله عليه وسلم !) آپ ان جھٹلانے والوں کی باتوں پر دھیان نہ دیں۔');
INSERT INTO "bayan" VALUES(68,9,'68:9','وہ تو چاہتے ہیں کہ آپ صلى الله عليه وسلم ذرا ڈھیلے پڑیں تو وہ بھی ڈھیلے پڑجائیں۔');
INSERT INTO "bayan" VALUES(68,10,'68:10','اور آپ صلى الله عليه وسلم مت مانیے کسی ایسے شخص کی بات جو بہت قسمیں کھانے والا انتہائی گھٹیا ہے۔');
INSERT INTO "bayan" VALUES(68,11,'68:11','رُو در رُو طعنے دیتا ہے چغلیاں کھاتا پھرتا ہے۔');
INSERT INTO "bayan" VALUES(68,12,'68:12','خیر سے روکنے والا حد سے بڑھنے والا گنہگار۔');
INSERT INTO "bayan" VALUES(68,13,'68:13','بالکل گنوار ہے اس کے بعد یہ کہ بداصل بھی ہے۔');
INSERT INTO "bayan" VALUES(68,14,'68:14','صرف اس گھمنڈ پر کہ وہ مال و دولت اور بیٹوں والا ہے۔');
INSERT INTO "bayan" VALUES(68,15,'68:15','جب اسے ہماری آیات پڑھ کر سنائی جاتی ہیں تو وہ کہتا ہے کہ یہ تو پہلے لوگوں کی کہانیاں ہیں۔');
INSERT INTO "bayan" VALUES(68,16,'68:16','ہم عنقریب اس کی سونڈ پر داغ لگائیں گے۔');
INSERT INTO "bayan" VALUES(68,17,'68:17','یقینا ہم نے ان (اہل مکہ) کو اسی طرح آزمایا ہے جیسے ہم نے باغ والوں کو آزمایا تھا۔ جبکہ انہوں نے قسم کھائی کہ وہ ضرور اس کا پھل اتار لیں گے صبح سویرے۔');
INSERT INTO "bayan" VALUES(68,18,'68:18','اور انہوں نے (اس پر) ان شاء اللہ بھی نہ کہا۔');
INSERT INTO "bayan" VALUES(68,19,'68:19','پس ایک پھرنے والا پھر گیا اس (باغ) پر آپ کے رب کی طرف سے جبکہ وہ ابھی سوئے ہوئے ہی تھے۔');
INSERT INTO "bayan" VALUES(68,20,'68:20','تو وہ ایسے ہوگیا جیسے کٹی ہوئی فصل ہو۔');
INSERT INTO "bayan" VALUES(68,21,'68:21','اب صبح ہی صبح انہوں نے ایک دوسرے کو پکارا۔');
INSERT INTO "bayan" VALUES(68,22,'68:22','کہ صبح سویرے چلو اپنے کھیت کی طرف اگر تم پھل توڑنا چاہتے ہو۔');
INSERT INTO "bayan" VALUES(68,23,'68:23','چناچہ وہ چلے اور آپس میں چپکے چپکے یہ باتیں کرتے جا رہے تھے۔');
INSERT INTO "bayan" VALUES(68,24,'68:24','کہ دیکھو آج کوئی مسکین تمہارے پاس باغ میں ہرگز داخل نہ ہونے پائے۔');
INSERT INTO "bayan" VALUES(68,25,'68:25','اور وہ صبح سویرے چلے جلدی جلدی (یہ سمجھتے ہوئے) کہ وہ اس ارادہ پر پوری طرح قادر ہیں۔');
INSERT INTO "bayan" VALUES(68,26,'68:26','پھر جب انہوں نے اس (باغ) کو دیکھا تو کہنے لگے کہ ہم تو کہیں بھٹک گئے ہیں۔');
INSERT INTO "bayan" VALUES(68,27,'68:27','نہیں نہیں (باغ تویہی ہے) ہم تو محروم ہوگئے ہیں۔');
INSERT INTO "bayan" VALUES(68,28,'68:28','ان کے درمیان والے نے کہا : میں تمہیں کہتا نہ تھا کہ تم (اپنے رب کی) تسبیح کیوں نہیں کرتے ؟');
INSERT INTO "bayan" VALUES(68,29,'68:29','انہوں نے کہا (واقعی تم ٹھیک کہتے ہو) ہمارا رب پاک ہے بیشک ہم ہی ظالم تھے۔');
INSERT INTO "bayan" VALUES(68,30,'68:30','پھر وہ آپس میں ایک دوسرے کو ملامت کرنے لگے۔');
INSERT INTO "bayan" VALUES(68,31,'68:31','(بالآخر اعتراف کرتے ہوئے) وہ کہنے لگے : ہائے ہماری بدبختی ! اصل میں ہم سب ہی اپنی حدود سے تجاوز کرنے والے تھے۔');
INSERT INTO "bayan" VALUES(68,32,'68:32','اُمید ہے ہمارا رب ہمیں اس سے بہتر عطا کر دے گا اب ہم اپنے رب کی طرف رجوع کرتے ہیں۔');
INSERT INTO "bayan" VALUES(68,33,'68:33','اسی طرح آتا ہے عذاب ! اور آخرت کا عذاب تو یقینا بہت ہی بڑا ہے۔');
INSERT INTO "bayan" VALUES(68,34,'68:34','متقین کے لیے یقینا ان کے رب کے پاس نعمت والے باغات ہیں۔');
INSERT INTO "bayan" VALUES(68,35,'68:35','کیا ہم اپنے فرمانبرداروں کو مجرموں کے برابر کردیں گے ؟');
INSERT INTO "bayan" VALUES(68,36,'68:36','تمہیں کیا ہوگیا ہے تم کیسے حکم لگاتے ہو ؟');
INSERT INTO "bayan" VALUES(68,37,'68:37','کیا تمہارے پاس کوئی کتاب ہے جس میں تم یہ پڑھتے ہو ؟');
INSERT INTO "bayan" VALUES(68,38,'68:38','کہ اس (آخرت) میں تمہیں وہ سب کچھ مل جائے گا جو تم پسند کرو گے !');
INSERT INTO "bayan" VALUES(68,39,'68:39','کیا تم نے ہم سے کوئی قسم لے رکھی ہے جو باقی رہنے والی ہو قیامت کے دن تک کہ تمہارے لیے وہی کچھ ہوگا جو تم فیصلہ کرو گے ؟');
INSERT INTO "bayan" VALUES(68,40,'68:40','(اے نبی صلى الله عليه وسلم !) ذرا ان سے پوچھئے کہ ان میں سے کون ہے جو اس کا ضامن ہو ؟');
INSERT INTO "bayan" VALUES(68,41,'68:41','کیا ان کے کوئی شریک ہیں ؟ تو لائیں یہ اپنے شریکوں کو اگر یہ سچے ہیں !');
INSERT INTO "bayan" VALUES(68,42,'68:42','جس دن پنڈلی کھولی جائے گی اور انہیں پکارا جائے گا (اللہ کے حضور) سجدے کے لیے تو وہ کر نہیں سکیں گے۔');
INSERT INTO "bayan" VALUES(68,43,'68:43','ان کی نگاہیں زمین پر گڑی رہ جائیں گی ان (کے چہروں) پر ذلت چھا رہی ہوگی۔ اور ان کو (دنیا میں) پکارا جاتا تھا سجدے کے لیے جبکہ یہ صحیح سالم تھے۔');
INSERT INTO "bayan" VALUES(68,44,'68:44','تو (اے نبی صلى الله عليه وسلم !) آپ چھوڑ دیجیے مجھے اور ان لوگوں کو جو اس کلام کی تکذیب کر رہے ہیں۔ ہم انہیں رفتہ رفتہ وہاں سے لے آئیں گے جہاں سے انہیں علم تک نہیں ہوگا۔');
INSERT INTO "bayan" VALUES(68,45,'68:45','اور میں ان کو ڈھیل دے رہا ہوں۔ بیشک میری تدبیر بہت مضبوط ہے۔');
INSERT INTO "bayan" VALUES(68,46,'68:46','(اے نبی صلى الله عليه وسلم !) کیا آپ ان سے کوئی اجرت مانگتے ہیں جس کے تاوان کے بوجھ تلے یہ دبے جا رہے ہیں ؟');
INSERT INTO "bayan" VALUES(68,47,'68:47','یا ان کے پاس غیب کا علم ہے جسے یہ لکھ رہے ہیں ؟');
INSERT INTO "bayan" VALUES(68,48,'68:48','تو (اے نبی صلى الله عليه وسلم !) آپ انتظار کیجیے اپنے رب کے حکم کا اور دیکھئے آپ صلى الله عليه وسلم اس مچھلی والے کی طرح نہ ہوجایئے گا !');
INSERT INTO "bayan" VALUES(68,49,'68:49','اگر اس کی دست گیری نہ کرتا اس کے رب کا ایک انعام (اور احسان) تو وہ ملامت زدہ ہو کر پھینک دیاجاتا کسی چٹیل زمین پر۔');
INSERT INTO "bayan" VALUES(68,50,'68:50','تو اس کے ربّ نے اس کو ُ چن لیا اور اسے پھر صالحین میں سے کردیا۔');
INSERT INTO "bayan" VALUES(68,51,'68:51','اور یہ کافر تو ُ تلے ہوئے ہیں اس پر کہ اپنی نگاہوں کے زور سے آپ صلى الله عليه وسلم کو پھسلا دیں گے جب وہ قرآن سنتے ہیں اور کہتے ہیں کہ یہ تو دیوانہ ہے۔');
INSERT INTO "bayan" VALUES(68,52,'68:52','اور نہیں ہے وہ مگر ایک یاد دہانی تمام جہان والوں کے لیے۔');
INSERT INTO "bayan" VALUES(69,1,'69:1','وہ حق ہوجانے والی !');
INSERT INTO "bayan" VALUES(69,2,'69:2','کیا ہے وہ حق ہوجانے والی ؟');
INSERT INTO "bayan" VALUES(69,3,'69:3','اور تم نے کیا سمجھا کہ وہ حق ہونے والی کیا ہے ؟');
INSERT INTO "bayan" VALUES(69,4,'69:4','جھٹلایا تھا ثمود نے بھی اور عاد نے بھی اس کھڑکھڑا دینے والی کو۔');
INSERT INTO "bayan" VALUES(69,5,'69:5','پس ثمود تو ہلاک کردیے گئے ایک حد سے بڑھ جانے والی آفت سے۔');
INSERT INTO "bayan" VALUES(69,6,'69:6','اور عاد ہلاک کردیے گئے ایک جھکڑ ّوالی تیز آندھی سے۔');
INSERT INTO "bayan" VALUES(69,7,'69:7','اللہ نے مسلط کردیا اسے ان پر مسلسل سات راتوں اور آٹھ دنوں تک ان کی بیخ کنی کے لیے تو تم دیکھتے ان لوگوں کو کہ وہاں ایسے پچھڑے پڑے ہیں جیسے کھجور کے کھوکھلے تنے ہوں۔');
INSERT INTO "bayan" VALUES(69,8,'69:8','تو کیا تم ان میں سے کسی کو دیکھتے ہو باقی بچا ہوا ؟');
INSERT INTO "bayan" VALUES(69,9,'69:9','اور (اسی طرح) فرعون اور اس سے پہلے والوں نے اور الٹ دی جانے والی بستیوں (کے باسیوں) نے بھی خطاکاری کی روش اختیار کی تھی۔');
INSERT INTO "bayan" VALUES(69,10,'69:10','تو انہوں نے نافرمانی کی اپنے رب کے رسولوں کی تو اللہ نے ان کو اپنی سخت گرفت میں دبوچ لیا۔');
INSERT INTO "bayan" VALUES(69,11,'69:11','جب (سیلابِ نوح ؑ کا) پانی طغیانی پر آیا تھا تو ہم نے تمہیں سوار کرا لیا تھا کشتی پر۔');
INSERT INTO "bayan" VALUES(69,12,'69:12','تاکہ ہم اس کو تمہارے لیے ایک یاددہانی بنا دیں اور وہ کان جو حفاظت کرنے والے ہیں اس کو پوری حفاظت سے یاد رکھیں۔');
INSERT INTO "bayan" VALUES(69,13,'69:13','تو جب صور میں پھونکا جائے گا یکبارگی۔');
INSERT INTO "bayan" VALUES(69,14,'69:14','اور جب زمین اور پہاڑوں کو اٹھا کر ایک ہی بار میں پاش پاش کر دیاجائے گا۔');
INSERT INTO "bayan" VALUES(69,15,'69:15','تو اس روز وہ واقعہ رونما ہوجائے گا۔');
INSERT INTO "bayan" VALUES(69,16,'69:16','اور آسمان پھٹ جائے گا تو وہ اس دن بہت بودا سا ہوجائے گا۔');
INSERT INTO "bayan" VALUES(69,17,'69:17','اور فرشتے ہوں گے اس کے کناروں پر۔ اور اس دن تیرے رب کے عرش کو اپنے اوپر اٹھائے ہوئے ہوں گے آٹھ فرشتے۔');
INSERT INTO "bayan" VALUES(69,18,'69:18','اس دن تمہاری پیشی ہوگی تمہاری کوئی مخفی سے مخفی بات بھی چھپی نہیں رہے گی۔');
INSERT INTO "bayan" VALUES(69,19,'69:19','تو وہ شخص جس کو اس کا اعمال نامہ دیا جائے گا اس کے داہنے ہاتھ میں تو وہ کہے گا : آئو آئو ! دیکھو میرا اعمال نامہ !');
INSERT INTO "bayan" VALUES(69,20,'69:20','(وہ کہے گا :) مجھے یقین تھا کہ مجھے اپنے حساب سے دوچار ہونا ہے۔');
INSERT INTO "bayan" VALUES(69,21,'69:21','پس وہ پسندیدہ زندگی بسر کرے گا۔');
INSERT INTO "bayan" VALUES(69,22,'69:22','بڑے ہی بلند باغ میں۔');
INSERT INTO "bayan" VALUES(69,23,'69:23','اس کے خوشے جھکے ہوئے ہوں گے۔');
INSERT INTO "bayan" VALUES(69,24,'69:24','(کہہ دیا جائے گا) کھائو اور پیو رچتا پچتا ان اعمال کے صلے میں جو تم نے گزرے دنوں میں کیے۔');
INSERT INTO "bayan" VALUES(69,25,'69:25','اور جس شخص کا اعمال نامہ اس کے بائیں ہاتھ میں دیاجائے گا وہ کہے گا : اے کاش ! مجھے میرا اعمال نامہ دیا ہی نہ گیا ہوتا۔');
INSERT INTO "bayan" VALUES(69,26,'69:26','اور مجھے معلوم ہی نہ ہوتا کہ میرا حساب کیا ہے !');
INSERT INTO "bayan" VALUES(69,27,'69:27','اے کاش کہ وہی موت قصہ پاک کردینے والی ہوتی !');
INSERT INTO "bayan" VALUES(69,28,'69:28','میرا مال و اسباب کچھ بھی میرے کام نہ آیا۔');
INSERT INTO "bayan" VALUES(69,29,'69:29','میرا اقتدار بھی مجھ سے چھن گیا۔');
INSERT INTO "bayan" VALUES(69,30,'69:30','پکڑو اسے اور اس کی گردن میں طوق ڈال دو !');
INSERT INTO "bayan" VALUES(69,31,'69:31','پھر اسے جہنم کے اندر جھونک دو !');
INSERT INTO "bayan" VALUES(69,32,'69:32','پھر ایک زنجیر میں اس کو باندھ دو جس کا طول ستر ّگز ہے۔');
INSERT INTO "bayan" VALUES(69,33,'69:33','یہ اللہ پر ایمان نہیں رکھتا تھا جو عظمت والا ہے۔');
INSERT INTO "bayan" VALUES(69,34,'69:34','اور نہ مسکین کو کھانا کھلانے کی ترغیب دیتا تھا۔');
INSERT INTO "bayan" VALUES(69,35,'69:35','تو اس کے لیے یہاں کوئی گرم جوش دوست نہیں ہے۔');
INSERT INTO "bayan" VALUES(69,36,'69:36','اور نہ ہی کچھ کھانے کو ہے سوائے زخموں کے دھو ون کے۔');
INSERT INTO "bayan" VALUES(69,37,'69:37','نہیں کھائیں گے اس کو مگر وہی جو خطاکار تھے۔');
INSERT INTO "bayan" VALUES(69,38,'69:38','تو میں قسم کھاتا ہوں اس کی جو تم دیکھتے ہو۔');
INSERT INTO "bayan" VALUES(69,39,'69:39','اور اس کی بھی جو تم نہیں دیکھتے ہو۔');
INSERT INTO "bayan" VALUES(69,40,'69:40','یہ قول ہے ایک رسول کریم کا۔');
INSERT INTO "bayan" VALUES(69,41,'69:41','اور یہ کسی شاعر کا قول نہیں ہے۔ کم ہی ہے جو تم یقین کرتے ہو۔');
INSERT INTO "bayan" VALUES(69,42,'69:42','اور نہ ہی یہ کسی کاہن کا کلام ہے۔ کم ہی ہے جو تم غور کرتے ہو۔');
INSERT INTO "bayan" VALUES(69,43,'69:43','اس کا اتارا جانا ہے تمام جہانوں کے رب کی طرف سے۔');
INSERT INTO "bayan" VALUES(69,44,'69:44','اور اگر یہ (نبی صلى الله عليه وسلم خود گھڑ کر بعض باتیں ہماری طرف منسوب کرتا۔');
INSERT INTO "bayan" VALUES(69,45,'69:45','تو ہم پکڑتے اس کو داہنے ہاتھ سے۔');
INSERT INTO "bayan" VALUES(69,46,'69:46','پھر اس کی گردن کاٹ ڈالتے۔');
INSERT INTO "bayan" VALUES(69,47,'69:47','پھر تم میں سے کوئی بھی (ہمیں) اس سے روکنے والا نہ ہوتا۔');
INSERT INTO "bayan" VALUES(69,48,'69:48','اور یقینا یہ تو ایک یاد دہانی ہے متقین کے لیے۔');
INSERT INTO "bayan" VALUES(69,49,'69:49','اور ہمیں خوب معلوم ہے کہ تم میں کچھ لوگ جھٹلانے والے بھی ہیں۔');
INSERT INTO "bayan" VALUES(69,50,'69:50','اور یقینا یہ کافروں کے لیے حسرت کا باعث بن جائے گا۔');
INSERT INTO "bayan" VALUES(69,51,'69:51','اور یقینا یہ (قرآن) بالکل یقینی حق ہے۔');
INSERT INTO "bayan" VALUES(69,52,'69:52','پس آپ صلى الله عليه وسلم تسبیح کیجیے اپنے رب کے نام کی جو کہ بہت عظمت والا ہے۔');
INSERT INTO "bayan" VALUES(70,1,'70:1','مانگا ایک مانگنے والے نے ایک ایسا عذاب جو واقع ہونے والا ہو');
INSERT INTO "bayan" VALUES(70,2,'70:2','کافروں کے لیے جس کو کوئی ٹال نہ سکے گا۔');
INSERT INTO "bayan" VALUES(70,3,'70:3','(وہ عذاب اپنے وقت پر آئے گا) اس اللہ کی طرف سے جو بہت بلند درجات والا ہے۔');
INSERT INTO "bayan" VALUES(70,4,'70:4','چڑھتے ہیں فرشتے اور روح اس کی جانب ایک دن میں جس کی مقدار پچاس ہزار برس ہے۔');
INSERT INTO "bayan" VALUES(70,5,'70:5','تو آپ صلى الله عليه وسلم بڑی خوبصورتی سے صبر کیجیے۔');
INSERT INTO "bayan" VALUES(70,6,'70:6','یہ لوگ تو اس (عذاب) کو دور سمجھ رہے ہیں');
INSERT INTO "bayan" VALUES(70,7,'70:7','اور ہم اسے نہایت قریب دیکھ رہے ہیں۔');
INSERT INTO "bayan" VALUES(70,8,'70:8','جس دن آسمان ہوجائے گا جیسے پگھلا ہوا تانبا۔');
INSERT INTO "bayan" VALUES(70,9,'70:9','اور پہاڑ ایسے ہوجائیں گے جیسے دھنکی ہوئی روئی (یا رنگی ہوئی اُون)۔');
INSERT INTO "bayan" VALUES(70,10,'70:10','اور کوئی دوست کسی دوسرے دوست کا حال نہیں پوچھے گا۔');
INSERT INTO "bayan" VALUES(70,11,'70:11','وہ سب انہیں دکھائے جائیں گے۔ اس روز مجرم چاہے گا کہ کاش وہ اس دن کے عذاب سے بچنے کے لیے فدیے میں دے دے اپنے بیٹوں کو');
INSERT INTO "bayan" VALUES(70,12,'70:12','اور اپنی بیوی کو اور اپنے بھائی کو');
INSERT INTO "bayan" VALUES(70,13,'70:13','اور اپنے کنبے کو جو اسے پناہ دیتا تھا');
INSERT INTO "bayan" VALUES(70,14,'70:14','اور روئے زمین پر بسنے والے تمام انسانوں کو پھر یہ (فدیہ) اس کو بچا لے !');
INSERT INTO "bayan" VALUES(70,15,'70:15','ہرگز نہیں ! اب تو یہ بھڑکتی ہوئی آگ ہی ہے۔');
INSERT INTO "bayan" VALUES(70,16,'70:16','جو کلیجوں کو کھینچ لے گی۔');
INSERT INTO "bayan" VALUES(70,17,'70:17','وہ پکارے گی ہر اس شخص کو جس نے پیٹھ موڑ لی تھی اور رُخ پھیرلیا تھا');
INSERT INTO "bayan" VALUES(70,18,'70:18','اور جو مال جمع کرتا رہا پھر اسے سینت سینت کر رکھتا رہا۔');
INSERT INTO "bayan" VALUES(70,19,'70:19','یقینا انسان پیدا کیا گیا ہے تھڑدلا۔');
INSERT INTO "bayan" VALUES(70,20,'70:20','جب اسے کوئی تکلیف پہنچتی ہے تو بہت گھبرا جانے والا ہے۔');
INSERT INTO "bayan" VALUES(70,21,'70:21','اور جب اسے بھلائی ملتی ہے تو بہت بخیل بن جاتا ہے۔');
INSERT INTO "bayan" VALUES(70,22,'70:22','سوائے نمازیوں کے۔');
INSERT INTO "bayan" VALUES(70,23,'70:23','جو اپنی نمازوں پر مداومت کرتے ہیں۔');
INSERT INTO "bayan" VALUES(70,24,'70:24','اور وہ جن کے اموال میں معین حق ہے');
INSERT INTO "bayan" VALUES(70,25,'70:25','مانگنے والے کا اور محروم کا۔');
INSERT INTO "bayan" VALUES(70,26,'70:26','اور جو فیصلے کے دن کی تصدیق کرتے ہیں۔');
INSERT INTO "bayan" VALUES(70,27,'70:27','اور جو اپنے رب کے عذاب سے لرزاں و ترساں رہتے ہیں۔');
INSERT INTO "bayan" VALUES(70,28,'70:28','یقینا ان کے رب کا عذاب ایسا نہیں ہے کہ کوئی اس سے نڈرہو جائے۔');
INSERT INTO "bayan" VALUES(70,29,'70:29','اور جو اپنی شرم گاہوں کی حفاظت کرنے والے ہیں۔');
INSERT INTO "bayan" VALUES(70,30,'70:30','سوائے اپنی بیویوں یا اپنی لونڈیوں کے تو ان لوگوں پر کوئی ملامت نہیں۔');
INSERT INTO "bayan" VALUES(70,31,'70:31','تو جو کوئی بھی اس کے علاوہ کچھ چاہے گا تو وہی لوگ حد سے بڑھنے والے ہیں۔');
INSERT INTO "bayan" VALUES(70,32,'70:32','اور وہ جو اپنی امانتوں اور اپنے عہد کا پاس کرنے والے ہیں۔');
INSERT INTO "bayan" VALUES(70,33,'70:33','اور وہ جو اپنی گواہیوں پر قائم رہنے والے ہیں۔');
INSERT INTO "bayan" VALUES(70,34,'70:34','اور وہ لوگ کہ جو اپنی نماز کی محافظت کرتے ہیں۔');
INSERT INTO "bayan" VALUES(70,35,'70:35','یہی لوگ ہیں جو جنتوں میں ہوں گے اور وہاں ان کا اعزازو اکرام ہوگا۔');
INSERT INTO "bayan" VALUES(70,36,'70:36','تو (اے نبی صلى الله عليه وسلم !) ان کافروں کو کیا ہوگیا ہے کہ وہ آپ کی طرف دوڑے چلے آتے ہیں۔');
INSERT INTO "bayan" VALUES(70,37,'70:37','دائیں اور بائیں سے غول در غول۔');
INSERT INTO "bayan" VALUES(70,38,'70:38','کیا ان میں سے ہر ایک واقعتا اس کا خواہش مند ہے کہ وہ نعمت بھری جنت میں داخل کیا جائے گا ؟');
INSERT INTO "bayan" VALUES(70,39,'70:39','ہرگز نہیں ! ہم نے ان کو پیدا کیا ہے اس چیز سے جس کو وہ جانتے ہیں۔');
INSERT INTO "bayan" VALUES(70,40,'70:40','تو نہیں ! قسم ہے مجھے مشرقوں اور مغربوں کے ربّ کی');
INSERT INTO "bayan" VALUES(70,41,'70:41','اس پر کہ ہم ان کو ہٹاکر ان سے بہتر لوگ لے آئیں اور (اس معاملے میں) ہم ہارے ہوئے نہیں ہیں۔');
INSERT INTO "bayan" VALUES(70,42,'70:42','تو (اے نبی صلى الله عليه وسلم !) آپ چھوڑ دیجیے انہیں یہ لگے رہیں بےہودہ باتوں اور کھیل کود میں یہاں تک کہ یہ ملاقات کریں اپنے اس دن سے جس کا ان سے وعدہ کیا جا رہا ہے۔');
INSERT INTO "bayan" VALUES(70,43,'70:43','جس دن وہ نکلیں گے اپنی قبروں سے دوڑتے ہوئے جیسے کہ وہ مقرر نشانوں کی طرف بھاگے جا رہے ہوں۔');
INSERT INTO "bayan" VALUES(70,44,'70:44','ان کی نگاہیں زمین میں گڑی ہوئی ہوں گی ذلت ان پر چھائی ہوئی ہوگی۔ یہ ہے وہ دن جس کا ان سے وعدہ کیا جاتا تھا۔');
INSERT INTO "bayan" VALUES(71,1,'71:1','ہم نے بھیجا تھا نوح کو ان کی قوم کی طرف کہ خبردار کر دو اپنی قوم کو اس سے پہلے کہ آدھمکے ان پر ایک دردناک عذاب۔');
INSERT INTO "bayan" VALUES(71,2,'71:2','آپ ؑ نے کہا : اے میری قوم کے لوگو ! میں تمہارے لیے واضح طور پر ایک خبردار کرنے والا ہوں۔');
INSERT INTO "bayan" VALUES(71,3,'71:3','کہ تم لوگ اللہ کی بندگی کرو اس کا تقویٰ اختیار کرو اور میری اطاعت کرو۔');
INSERT INTO "bayan" VALUES(71,4,'71:4','اللہ تمہارے کچھ گناہ معاف کر دے گا اور تمہیں مہلت دے دے گا ایک وقت معین تک۔ اللہ کا مقرر کردہ وقت جب آجائے گا تو اسے موخر نہیں کیا جاسکے گا۔ کاش کہ تمہیں معلوم ہوتا !');
INSERT INTO "bayan" VALUES(71,5,'71:5','نوح نے عرض کیا : پروردگار ! میں نے تو اپنی اس قوم کو رات دن دعوت دی ہے۔');
INSERT INTO "bayan" VALUES(71,6,'71:6','لیکن میری اس دعوت نے ان میں کچھ اضافہ نہیں کیا سوائے فرار کے۔');
INSERT INTO "bayan" VALUES(71,7,'71:7','اور میں نے جب بھی انہیں پکارا تاکہ تو ان کی مغفرت فرما دے تو انہوں نے اپنی انگلیاں اپنے کانوں میں ٹھونس لیں اور اپنے کپڑے بھی اپنے اوپر لپیٹ لیے اور وہ ضد پر اَڑ گئے اور انہوں نے استکبار کیا بہت بڑا استکبار۔');
INSERT INTO "bayan" VALUES(71,8,'71:8','پھر (اے پروردگار) میں نے انہیں بلند آواز سے بھی پکارا۔');
INSERT INTO "bayan" VALUES(71,9,'71:9','پھر میں نے انہیں علانیہ دعوت بھی دی اور خفیہ طور پر بھی سمجھایا۔');
INSERT INTO "bayan" VALUES(71,10,'71:10','پس میں نے ان سے کہا کہ تم اپنے رب سے استغفار کرو۔ یقینا وہ بہت بخشنے والا ہے۔');
INSERT INTO "bayan" VALUES(71,11,'71:11','وہ تم پر آسمان سے خوب بارشیں برسائے گا۔');
INSERT INTO "bayan" VALUES(71,12,'71:12','اور وہ بڑھا دے گا تمہیں مال اور بیٹوں سے اور تمہیں باغات عطا کرے گا اور تمہارے لیے (چشمے اور) نہریں رواں کر دے گا۔');
INSERT INTO "bayan" VALUES(71,13,'71:13','تمہیں کیا ہوگیا ہے کہ تم اللہ کی عظمت کے امیدوار نہیں ہو ؟');
INSERT INTO "bayan" VALUES(71,14,'71:14','اور اس نے تمہیں پیدا کیا ہے درجہ بدرجہ۔');
INSERT INTO "bayan" VALUES(71,15,'71:15','کیا تم نے دیکھا نہیں کہ اللہ نے کیسے پیدا کیا ہے سات آسمانوں کو تہ بر تہ !');
INSERT INTO "bayan" VALUES(71,16,'71:16','اور ان کے اندر اس نے چاند کو روشنی اور سورج کو (درخشاں) چراغ بنا یا !');
INSERT INTO "bayan" VALUES(71,17,'71:17','اور اللہ نے تمہیں اگایا ہے زمین سے جیسے (سبزہ) اُگایا جاتا ہے۔');
INSERT INTO "bayan" VALUES(71,18,'71:18','پھر وہ تمہیں لوٹا دے گا اسی میں اور پھر نکالے گا تمہیں جیسے نکالا جاتا ہے۔');
INSERT INTO "bayan" VALUES(71,19,'71:19','اور اللہ نے تمہارے لیے زمین کو بچھا دیا ہے بچھونے کی طرح۔');
INSERT INTO "bayan" VALUES(71,20,'71:20','تاکہ تم چلو پھرو اس میں کشادہ راستوں پر۔');
INSERT INTO "bayan" VALUES(71,21,'71:21','نوح ؑ نے کہا : پروردگار ! انہوں نے میری نافرمانی کی اور انہوں نے ان لوگوں کی پیروی کی جن کے مال اور اولاد نے ان کے لیے سوائے خسارے کے اور کچھ نہیں بڑھایا۔');
INSERT INTO "bayan" VALUES(71,22,'71:22','اور ان لوگوں نے ایک چال بھی چلی بہت بڑی چال۔');
INSERT INTO "bayan" VALUES(71,23,'71:23','اور انہوں نے کہا کہ تم اپنے ان معبودوں کو ہرگز چھوڑ نہ بیٹھنا۔ ہرگز مت چھوڑنا وَد کو سواع کو یغوث کو یعوق کو اور نسر کو۔');
INSERT INTO "bayan" VALUES(71,24,'71:24','اور انہوں نے تو بہتوں کو بہکا دیا ہے۔ اور (اے اللہ !) اب ُ تو ان ظالموں کے لیے سوائے گمراہی کے اور کسی چیز میں اضافہ نہ فرما !');
INSERT INTO "bayan" VALUES(71,25,'71:25','اپنی خطائوں کی وجہ سے ہی وہ غرق کیے گئے اور داخل کردیے گئے آگ میں تو نہ پایا انہوں نے اپنے لیے اللہ کے مقابل کوئی مددگار۔');
INSERT INTO "bayan" VALUES(71,26,'71:26','اور نوح ؑ نے کہا : اے میرے پروردگار ! اب ُ تو اس زمین پر کافروں کا بستا ہوا ایک گھر بھی مت چھوڑ۔');
INSERT INTO "bayan" VALUES(71,27,'71:27','اگر تو نے ان کو چھوڑ دیاتو یہ تیرے بندوں کو گمراہ کریں گے اور ان کی نسلوں میں بھی اب فاجر اور کافر لوگوں کے سوا اور کوئی پیدا نہیں ہوگا۔');
INSERT INTO "bayan" VALUES(71,28,'71:28','پروردگار ! مغفرت فرما دے میری اور میرے والدین کی اور جو کوئی بھی میرے گھر میں داخل ہوجائے ایمان کے ساتھ اس کی بھی اور تمام مومن مردوں اور مومن عورتوں کی بھی (مغفرت فرمادے) اور ان ظالموں کے لیے ُ تو اب کسی چیز میں اضافہ مت کر سوائے تباہی اور بربادی کے۔');
INSERT INTO "bayan" VALUES(72,1,'72:1','(اے نبی صلى الله عليه وسلم !) آپ کہہ دیجیے : میری طرف وحی کی گئی ہے کہ جنات کی ایک جماعت نے بڑے غور سے سنا تو انہوں نے (جا کر دوسرے جنات سے) کہا کہ ہم نے سنا ہے ایک بہت ہی دل کو لبھانے والا قرآن۔');
INSERT INTO "bayan" VALUES(72,2,'72:2','جو راہ راست کی طرف راہنمائی کرتا ہے تو ہم اس پر ایمان لے آئے۔ اور اب ہم کبھی بھی اپنے رب کے ساتھ کسی کو شریک نہیں ٹھہرائیں گے۔');
INSERT INTO "bayan" VALUES(72,3,'72:3','اور یہ کہ ہمارے ربّ کی شان بہت بلند ہے اس نے اپنے لیے نہ کوئی بیوی بنائی ہے اور نہ کوئی اولاد۔');
INSERT INTO "bayan" VALUES(72,4,'72:4','اور یقینا ہمارا بیوقوف (سردار) اللہ کے بارے میں خلافِ حقیقت باتیں کہتا رہا ہے۔');
INSERT INTO "bayan" VALUES(72,5,'72:5','اور یہ کہ ہم تو اس گمان میں رہے کہ جن اور انسان اللہ پر ہرگز کوئی جھوٹ نہیں باندھیں گے۔');
INSERT INTO "bayan" VALUES(72,6,'72:6','اور یہ کہ انسانوں میں سے کچھ مرد جنات میں سے کچھ مردوں کی پناہ پکڑتے تھے تو انہوں نے ان (جنات) کی سرکشی میں مزید اضافہ کیا۔');
INSERT INTO "bayan" VALUES(72,7,'72:7','اور یہ کہ انہوں نے بھی ایسا ہی سمجھا جیسا کہ تم نے سمجھا ہوا ہے کہ اللہ کسی کو ہرگز نہیں اٹھائے گا۔');
INSERT INTO "bayan" VALUES(72,8,'72:8','اور یہ کہ ہم نے ٹٹولا آسمان کو تو ہم نے دیکھا کہ وہ سخت پہروں اور انگاروں سے بھرا ہوا ہے۔');
INSERT INTO "bayan" VALUES(72,9,'72:9','اور یہ کہ (اس سے پہلے) ہم اس کے بعض ٹھکانوں میں بیٹھا کرتے تھے کچھ سن گن لینے کے لیے۔ لیکن اب اگر کسی نے کچھ سننے کی کوشش کی تو وہ پائے گا اپنے واسطے ایک انگارہ گھات میں لگا ہوا۔');
INSERT INTO "bayan" VALUES(72,10,'72:10','اور یہ کہ ہم نہیں جانتے کہ زمین والوں کے لیے کسی شر کا ارادہ کیا جا رہا ہے یا ان کے لیے ان کے رب نے کسی بھلائی کا ارادہ کیا ہے۔');
INSERT INTO "bayan" VALUES(72,11,'72:11','اور یہ کہ ہم میں نیک لوگ بھی ہیں اور کچھ اس سے مختلف قسم کے بھی ہیں۔ ہم مختلف راستوں پر پھٹے ہوئے تھے۔');
INSERT INTO "bayan" VALUES(72,12,'72:12','اور یہ کہ ہمیں یقین ہوگیا تھا کہ ہم زمین میں اللہ کو عاجز نہیں کرسکتے اور نہ بھاگ کر اسے ہرا سکتے ہیں۔');
INSERT INTO "bayan" VALUES(72,13,'72:13','اور یہ کہ ہم نے جونہی اس ہدایت (الہدیٰ) کو سنا ہم اس پر ایمان لے آئے۔ تو جو کوئی بھی ایمان لائے گا اپنے رب پر اسے نہ تو کسی نقصان کا خوف ہوگا اور نہ زیادتی کا۔');
INSERT INTO "bayan" VALUES(72,14,'72:14','اور یہ کہ ہم میں فرمانبردار بھی ہیں اور بےانصافی کرنے والے بھی۔ تو جن لوگوں نے اطاعت قبول کرلی تو انہوں نے ڈھونڈ نکالی نیکی کی راہ۔');
INSERT INTO "bayan" VALUES(72,15,'72:15','اور جو بےانصاف ہیں تو وہ جہنم کا ایندھن بن کر رہیں گے۔');
INSERT INTO "bayan" VALUES(72,16,'72:16','اور (اے نبی صلى الله عليه وسلم ! آپ کہیے کہ مجھ پر یہ وحی بھی کی گئی ہے) کہ اگر یہ لوگ درست طریقے پر چلتے رہتے تو ہم انہیں خوب سیراب کرتے۔');
INSERT INTO "bayan" VALUES(72,17,'72:17','تاکہ ہم اس (فراوانی) میں ان کی آزمائش کریں۔ اور جو کوئی بھی اعراض کرے گا اپنے رب کے ذکر سے تو وہ ڈال دے گا اس کو چڑھتے عذاب میں۔');
INSERT INTO "bayan" VALUES(72,18,'72:18','اور یہ کہ مسجدیں اللہ ہی کے لیے ہیں تو تم اللہ کے ساتھ کسی اور کو مت پکارو !');
INSERT INTO "bayan" VALUES(72,19,'72:19','اور یہ کہ جب اللہ کا بندہ اس کو پکارنے کے لیے کھڑا ہوتا ہے تو معلوم ہوتا ہے کہ لوگ اس پر ہجوم کر کے آجائیں گے۔');
INSERT INTO "bayan" VALUES(72,20,'72:20','(اے نبی صلى الله عليه وسلم !) آپ کہہ دیجیے کہ میں تو اپنے رب ہی کو پکارتا ہوں اور اس کے ساتھ کسی کو شریک نہیں کرتا۔');
INSERT INTO "bayan" VALUES(72,21,'72:21','آپ صلى الله عليه وسلم کہہ دیجیے کہ مجھے کوئی اختیار نہیں تمہارے لیے کسی نقصان کا اور نہ راہ پر لانے کا۔');
INSERT INTO "bayan" VALUES(72,22,'72:22','آپ صلى الله عليه وسلم کہہ دیجیے کہ مجھے اللہ (کی پکڑ) سے کوئی پناہ نہیں دے سکتا اور نہ ہی میں اس کے علاوہ سر چھپانے کی کوئی جگہ پائوں گا۔');
INSERT INTO "bayan" VALUES(72,23,'72:23','بس (میرا فرض) اللہ کی طرف سے تبلیغ اور اس کے پیغامات کا پہنچا دینا ہے۔ اور جو کوئی بھی اللہ اور اس کے رسول صلى الله عليه وسلم کی نافرمانی کرے گا تو اس کے لیے جہنم کی آگ ہے جس میں وہ ہمیشہ ہمیش رہے گا۔');
INSERT INTO "bayan" VALUES(72,24,'72:24','یہاں تک کہ جب وہ دیکھیں گے وہ چیز جس کی انہیں دھمکی دی جا رہی ہے اس وقت انہیں معلوم ہوجائے گا کہ کون کمزور ہے مددگاروں کے اعتبار سے اور کون اقلیت میں ہے تعداد کے لحاظ سے۔');
INSERT INTO "bayan" VALUES(72,25,'72:25','آپ صلى الله عليه وسلم یہ بھی کہہ دیجیے کہ مجھے معلوم نہیں کہ جس چیز کا تم سے وعدہ کیا جا رہا ہے وہ قریب آچکی ہے یا میرا رب اس کی مدت اور لمبی کر دے گا۔');
INSERT INTO "bayan" VALUES(72,26,'72:26','وہی ہے غیب کا جاننے والا پس وہ اپنے غیب پر کسی کو مطلع نہیں کرتا۔');
INSERT INTO "bayan" VALUES(72,27,'72:27','سوائے اس کے جس کو اس نے پسند فرما لیا ہو اپنے رسولوں میں سے تو اس کے آگے اور پیچھے وہ محافظ لگا دیتا ہے۔');
INSERT INTO "bayan" VALUES(72,28,'72:28','تاکہ وہ دیکھ لے کہ انہوں نے واقعتا اپنے رب کے پیغامات پہنچادیے ہیں اور وہ احاطہ کیے ہوئے ہے اس سب کچھ کا جو ان کے پاس ہے اور اس نے ہرچیز کا حساب کتاب رکھا ہوا ہے گنتی کے ساتھ۔');
INSERT INTO "bayan" VALUES(73,1,'73:1','اے کمبل میں لپٹ کر لیٹنے والے صلى الله عليه وسلم !');
INSERT INTO "bayan" VALUES(73,2,'73:2','آپ کھڑے رہا کریں رات کو (نماز میں) سوائے اس کے تھوڑے سے حصے کے۔');
INSERT INTO "bayan" VALUES(73,3,'73:3','(یعنی) اس کا آدھا یا اس سے تھوڑا کم کر لیجیے۔');
INSERT INTO "bayan" VALUES(73,4,'73:4','یا اس پر تھوڑا بڑھا لیں اور ٹھہر ٹھہر کر قرآن پڑھتے جایئے۔');
INSERT INTO "bayan" VALUES(73,5,'73:5','ہم عنقریب آپ صلى الله عليه وسلم پر ایک بھاری بات ڈالنے والے ہیں۔');
INSERT INTO "bayan" VALUES(73,6,'73:6','یقینا رات کا جاگنا بہت موثر ہے نفس کو زیر کرنے کے لیے اور بات کو زیادہ درست رکھنے والا ہے۔');
INSERT INTO "bayan" VALUES(73,7,'73:7','یقینا دن کے اوقات میں تو آپ صلى الله عليه وسلم کے لیے بڑی مصروفیات ہیں۔');
INSERT INTO "bayan" VALUES(73,8,'73:8','اور آپ صلى الله عليه وسلم اپنے رب کے نام کا ذکر کیا کریں اور ہر طرف سے کٹ کر بس اسی کے ہو رہیں۔');
INSERT INTO "bayan" VALUES(73,9,'73:9','وہ رب ہے مشرق کا بھی اور مغرب کا بھی اس کے سوا کوئی معبود نہیں بس آپ اسی کو بنا لیجیے اپناکارساز۔');
INSERT INTO "bayan" VALUES(73,10,'73:10','اور جو کچھ یہ لوگ کہہ رہے ہیں اس پر صبر کیجیے اور ان کو چھوڑ دیجیے بڑی خوبصورتی سے کنارہ کشی کرتے ہوئے۔');
INSERT INTO "bayan" VALUES(73,11,'73:11','آپ مجھے اور ان جھٹلانے والوں کو چھوڑ دیں جو بڑی نعمتوں سے نوازے گئے ہیں اور ابھی آپ انہیں تھوڑی سی مہلت دیں۔');
INSERT INTO "bayan" VALUES(73,12,'73:12','ہمارے پاس ان کے لیے بھاری بیڑیاں اور بھڑکتی آگ ہے۔');
INSERT INTO "bayan" VALUES(73,13,'73:13','اور وہ کھانا جو حلق میں اٹک جائے گا اور دردناک عذاب ہے۔');
INSERT INTO "bayan" VALUES(73,14,'73:14','جس دن کہ زمین اور پہاڑ لرزنے لگیں گے اور پہاڑ ریت کے بکھرتے تودے بن جائیں گے۔');
INSERT INTO "bayan" VALUES(73,15,'73:15','(اے لوگو !) ہم نے تمہاری طرف ایک رسول صلى الله عليه وسلم بھیج دیا ہے تم پر گواہ بنا کر۔ جیسے کہ ہم نے بھیجا تھا فرعون کی طرف بھی ایک رسول۔');
INSERT INTO "bayan" VALUES(73,16,'73:16','پس فرعون نے نافرمانی کی ہمارے رسول ؑ کی تو ہم نے پکڑ لیا اس کو بڑے وبال والی پکڑ۔');
INSERT INTO "bayan" VALUES(73,17,'73:17','اب اگر تم کفر کرو گے تو تم کیسے بچ جائو گے اس دن جو بچوں کو بوڑھا کر دے گا');
INSERT INTO "bayan" VALUES(73,18,'73:18','آسمان اس سے پھٹ پڑنے والا ہے اس کا وعدہ پورا ہو کر رہے گا۔');
INSERT INTO "bayan" VALUES(73,19,'73:19','یقینا یہ ایک یاددہانی ہے۔ تو جو کوئی بھی چاہے وہ اپنے رب کی طرف راستہ اختیار کرلے۔');
INSERT INTO "bayan" VALUES(73,20,'73:20','(اے نبی صلى الله عليه وسلم !) یقینا آپ کا رب جانتا ہے کہ آپ قیام کرتے ہیں کبھی دو تہائی رات کے قریب کبھی نصف رات اور کبھی ایک تہائی رات اور جو لوگ آپ کے ساتھ ہیں ان میں سے بھی ایک جماعت آپ کے ساتھ (کھڑی) ہوتی ہے۔ اور اللہ ہی رات اور دن کا اندازہ کرتا ہے۔ اللہ جانتا ہے کہ تم اس کی پابندی نہیں کرسکو گے تو اس نے تم پر مہربانی فرمائی ہے تو اب قرآن سے جتنا بآسانی پڑھ سکتے ہو پڑھ لیا کرو۔ اللہ کے علم میں ہے کہ تم میں کچھ لوگ مریض ہوں گے اور بعض دوسرے زمین میں سفر کریں گے اللہ کے فضل کو تلاش کرتے ہوں گے اور کچھ اللہ کی راہ میں قتال کر رہے ہوں گے چناچہ جس قدر تمہارے لیے آسان ہو اس میں سے پڑھ لیا کرو اور نماز قائم کرو اور زکوٰۃ ادا کرو اور اللہ کو قرض حسنہ دو۔ اور جو بھلائی بھی تم آگے بھیجو گے اپنی جانوں کے لیے اسے موجود پائو گے اللہ کے پاس بہتر اور اجر میں بڑھ کر۔ اور اللہ سے مغفرت طلب کرتے رہو۔ اور اللہ سے مغفرت طلب کرتے رہو۔ یقینا اللہ تعالیٰ بہت بخشنے والا رحمت فرمانے والا ہے۔');
INSERT INTO "bayan" VALUES(74,1,'74:1','اے کمبل میں لپٹ کر لیٹنے والے صلى الله عليه وسلم !');
INSERT INTO "bayan" VALUES(74,2,'74:2','آپ صلى الله عليه وسلم اٹھئے اور (لوگوں کو) خبردار کیجیے۔');
INSERT INTO "bayan" VALUES(74,3,'74:3','اور اپنے رب کو بڑا کرو !');
INSERT INTO "bayan" VALUES(74,4,'74:4','اور اپنے کپڑوں کو صاف رکھنے کا اہتمام کیجیے۔');
INSERT INTO "bayan" VALUES(74,5,'74:5','اور ہر قسم کی گندگی سے دور رہیے۔');
INSERT INTO "bayan" VALUES(74,6,'74:6','اور زیادہ لینے کے لیے کسی پر احسان نہ کیجیے۔');
INSERT INTO "bayan" VALUES(74,7,'74:7','اور اپنے رب کے لیے صبر کرو۔');
INSERT INTO "bayan" VALUES(74,8,'74:8','جب صور میں پھونکا جائے گا۔');
INSERT INTO "bayan" VALUES(74,9,'74:9','تو وہ دن بہت سخت دن ہوگا۔');
INSERT INTO "bayan" VALUES(74,10,'74:10','کافروں پر وہ ہلکا نہیں ہوگا۔');
INSERT INTO "bayan" VALUES(74,11,'74:11','آپ چھوڑ دیجیے مجھے اور جس کو میں نے اکیلا پیدا کیا۔');
INSERT INTO "bayan" VALUES(74,12,'74:12','اور اسے میں نے بہت سا مال دیا پھیلا ہوا۔');
INSERT INTO "bayan" VALUES(74,13,'74:13','اور نگاہوں کے سامنے رہنے والے بیٹے دیے۔');
INSERT INTO "bayan" VALUES(74,14,'74:14','اور میں نے اس کے لیے ہر قسم کا سامان خوب اچھی طرح سے تیار کردیا۔');
INSERT INTO "bayan" VALUES(74,15,'74:15','پھر وہ چاہتا ہے کہ میں اسے اور بھی دوں !');
INSERT INTO "bayan" VALUES(74,16,'74:16','ہرگز نہیں ! وہ تو ہماری آیات کا مخالف (اور دشمن بن گیا) ہے۔');
INSERT INTO "bayan" VALUES(74,17,'74:17','میں اسے عنقریب ایک سخت چڑھائی چڑھوائوں گا۔');
INSERT INTO "bayan" VALUES(74,18,'74:18','اس نے غور کیا اور کچھ اندازہ کیا۔');
INSERT INTO "bayan" VALUES(74,19,'74:19','پس ہلاک ہوجائے اس نے کیسا غلط اندازہ ٹھہرایا۔');
INSERT INTO "bayan" VALUES(74,20,'74:20','پھر ہلاک ہوجائے اس نے کیسا غلط اندازہ ٹھہرایا۔');
INSERT INTO "bayan" VALUES(74,21,'74:21','پھر اس نے دیکھا۔');
INSERT INTO "bayan" VALUES(74,22,'74:22','پھر تیوری چڑھائی اور منہ بسورا۔');
INSERT INTO "bayan" VALUES(74,23,'74:23','پھر پیٹھ پھیری اور تکبر کیا۔');
INSERT INTO "bayan" VALUES(74,24,'74:24','پھر اس نے کہا کہ یہ تو بس جادو ہے جو پہلے سے چلا آ رہا ہے۔');
INSERT INTO "bayan" VALUES(74,25,'74:25','یہ نہیں ہے مگر انسان کا کلام۔');
INSERT INTO "bayan" VALUES(74,26,'74:26','میں عنقریب اسے ڈالوں گا سقر (دوزخ) میں !');
INSERT INTO "bayan" VALUES(74,27,'74:27','اور تمہیں کیا معلوم کہ وہ سقر کیا ہے ؟');
INSERT INTO "bayan" VALUES(74,28,'74:28','وہ نہ تو باقی رہنے دے گی اور نہ چھوڑے گی !');
INSERT INTO "bayan" VALUES(74,29,'74:29','انسان کی کھال کو جھلسا ڈالنے والی۔');
INSERT INTO "bayan" VALUES(74,30,'74:30','اس پر انیس (19) داروغے مقرر ہیں۔');
INSERT INTO "bayan" VALUES(74,31,'74:31','اور ہم نے نہیں مقرر کیے جہنم کے داروغے مگر فرشتے اور ہم نے نہیں ٹھہرائی ان کی یہ تعداد مگر کافروں کی آزمائش کے لیے تاکہ جنہیں کتاب دی گئی تھی انہیں یقین آجائے اور جو اہل ایمان ہیں وہ ایمان میں بڑھیں اور نہ شک میں پڑیں اہل کتاب اور اہل ایمان اور تاکہ کہیں وہ لوگ جن کے دلوں میں روگ ہے اور کفار بھی کہ بھلا اس سے اللہ کی کیا مراد ہے ؟ اسی طرح اللہ گمراہ کردیتا ہے جس کو چاہتا ہے اور ہدایت دیتا ہے جس کو چاہتا ہے۔ اور کوئی نہیں جانتا آپ کے رب کے لشکروں کو سوائے اس کے۔ اور یہ آیات صرف انسانوں کی یاد دہانی کے لیے ہیں۔');
INSERT INTO "bayan" VALUES(74,32,'74:32','کیوں نہیں قسم ہے چاند کی۔');
INSERT INTO "bayan" VALUES(74,33,'74:33','اور قسم ہے رات کی جب کہ وہ پیٹھ موڑے۔');
INSERT INTO "bayan" VALUES(74,34,'74:34','اور قسم ہے صبح کی جبکہ وہ روشن ہوجائے۔');
INSERT INTO "bayan" VALUES(74,35,'74:35','یقینا یہ بہت بڑی باتوں میں سے ایک بات ہے۔');
INSERT INTO "bayan" VALUES(74,36,'74:36','خبردار کرنے کے لیے انسانوں کو۔');
INSERT INTO "bayan" VALUES(74,37,'74:37','جو بھی تم میں سے چاہے کہ وہ آگے بڑھے یا پیچھے رہ جائے۔');
INSERT INTO "bayan" VALUES(74,38,'74:38','ہر جان رہن ہے اس کے عوض جو کچھ کہ اس نے کمایا ہے۔');
INSERT INTO "bayan" VALUES(74,39,'74:39','سوائے ان لوگوں کے جو داہنے والے ہوں گے۔');
INSERT INTO "bayan" VALUES(74,40,'74:40','وہ جنتوں میں ہوں گے پوچھتے ہوں گے۔');
INSERT INTO "bayan" VALUES(74,41,'74:41','گنہگاروں کے بارے میں۔');
INSERT INTO "bayan" VALUES(74,42,'74:42','تم لوگوں کو کس چیز نے جہنم میں ڈالا ؟');
INSERT INTO "bayan" VALUES(74,43,'74:43','وہ کہیں گے کہ ہم نماز پڑھنے والوں میں سے نہیں تھے۔');
INSERT INTO "bayan" VALUES(74,44,'74:44','اور نہ ہم مسکین کو کھانا کھلاتے تھے۔');
INSERT INTO "bayan" VALUES(74,45,'74:45','اور ہم کٹ حجتیاں کرنے والوں کے ساتھ مل کر کٹ حجتیاں کیا کرتے تھے۔');
INSERT INTO "bayan" VALUES(74,46,'74:46','اور (سب سے بڑھ کر یہ کہ) ہم جزاء وسزا کے دن کا انکار کرتے رہے۔');
INSERT INTO "bayan" VALUES(74,47,'74:47','یہاں تک کہ ہمیں موت نے آلیا۔');
INSERT INTO "bayan" VALUES(74,48,'74:48','تو اب ان کے لیے نفع بخش نہیں ہوگی شفاعت کرنے والوں کی کوئی شفاعت۔');
INSERT INTO "bayan" VALUES(74,49,'74:49','تو انہیں کیا ہوگیا ہے کہ یہ اس یاد دہانی (قرآن) سے اعراض کرنے والے بنے ہوئے ہیں ؟');
INSERT INTO "bayan" VALUES(74,50,'74:50','گویا وہ بدکے ہوئے جنگلی گدھے ہیں۔');
INSERT INTO "bayan" VALUES(74,51,'74:51','جو شیر سے ڈر کر بھاگ پڑے ہیں۔');
INSERT INTO "bayan" VALUES(74,52,'74:52','بلکہ ان میں سے ہر شخص چاہتا ہے کہ اس کو پکڑا دیے جائیں کھلے صحیفے۔');
INSERT INTO "bayan" VALUES(74,53,'74:53','ہرگز نہیں ! اصل بات یہ ہے کہ وہ آخرت کا خوف نہیں رکھتے۔');
INSERT INTO "bayan" VALUES(74,54,'74:54','ہرگز نہیں ! یہ تو بس ایک یاد دہانی ہے۔');
INSERT INTO "bayan" VALUES(74,55,'74:55','اب جو چاہے اس سے نصیحت اخذ کرلے۔');
INSERT INTO "bayan" VALUES(74,56,'74:56','اور یہ لوگ نصیحت اخذ نہیں کریں گے مگر یہ کہ اللہ ہی ایسا چاہے۔ وہی ڈرنے کے لائق ہے اور وہی مغفرت کا مجاز۔');
INSERT INTO "bayan" VALUES(75,1,'75:1','نہیں ! میں قسم کھاتا ہوں قیامت کے دن کی۔');
INSERT INTO "bayan" VALUES(75,2,'75:2','اور نہیں ! میں قسم کھاتا ہوں نفس لوامہ کی۔');
INSERT INTO "bayan" VALUES(75,3,'75:3','کیا انسان یہ گمان کرتا ہے کہ ہم اس کی ہڈیاں جمع نہیں کرسکیں گے ؟');
INSERT INTO "bayan" VALUES(75,4,'75:4','کیوں نہیں ! ہم تو پوری طرح قادر ہیں اس پر بھی کہ ہم اس کی ایک ایک پور درست کردیں۔');
INSERT INTO "bayan" VALUES(75,5,'75:5','بلکہ انسان تو یہ چاہتا ہے کہ فسق و فجور آگے بھی جاری رکھے۔');
INSERT INTO "bayan" VALUES(75,6,'75:6','وہ پوچھتا ہے : کب آئے گا قیامت کا دن ؟');
INSERT INTO "bayan" VALUES(75,7,'75:7','پس جب نگاہیں چندھیا جائیں گی۔');
INSERT INTO "bayan" VALUES(75,8,'75:8','اور چاند بےنور ہوجائے گا۔');
INSERT INTO "bayan" VALUES(75,9,'75:9','اور سورج اور چاند یکجا کردیے جائیں گے۔');
INSERT INTO "bayan" VALUES(75,10,'75:10','اس دن انسان کہے گا : کہاں ہے کوئی بھاگ جانے کی جگہ ؟');
INSERT INTO "bayan" VALUES(75,11,'75:11','(کہا جائے گا :) ہرگز نہیں کوئی جائے پناہ نہیں ہے۔');
INSERT INTO "bayan" VALUES(75,12,'75:12','اس روز تمہارے ربّ ہی کے حضور میں جا کر کھڑے ہونا ہے۔');
INSERT INTO "bayan" VALUES(75,13,'75:13','جتلا دیا جائے گا انسان کو اس دن جو کچھ اس نے آگے بھیجا ہوگا اور جو کچھ پیچھے چھوڑا ہوگا۔');
INSERT INTO "bayan" VALUES(75,14,'75:14','بلکہ انسان تو اپنے نفس کے احوال پر خود ہی خوب بصیرت رکھتا ہے۔');
INSERT INTO "bayan" VALUES(75,15,'75:15','اور چاہے وہ کتنے ہی بہانے پیش کرے۔');
INSERT INTO "bayan" VALUES(75,16,'75:16','آپ اس (قرآن) کے ساتھ اپنی زبان کو تیزی سے حرکت نہ دیں۔');
INSERT INTO "bayan" VALUES(75,17,'75:17','اسے جمع کرنا اور پڑھوا دینا ہمارے ذمہ ہے۔');
INSERT INTO "bayan" VALUES(75,18,'75:18','پھر جب ہم اسے پڑھوا دیں تو آپ اس کی قراءت کی پیروی کیجیے۔');
INSERT INTO "bayan" VALUES(75,19,'75:19','پھر ہمارے ہی ذمے ہے اس کو واضح کردینا بھی۔');
INSERT INTO "bayan" VALUES(75,20,'75:20','ہرگز نہیں ! اصل بات یہ ہے کہ تم لوگ جلدی ملنے والی چیز (یعنی دنیا) سے محبت کرتے ہو۔');
INSERT INTO "bayan" VALUES(75,21,'75:21','اور تم آخرت کو چھوڑ دیتے ہو۔');
INSERT INTO "bayan" VALUES(75,22,'75:22','بہت سے چہرے اس دن تروتازہ ہوں گے۔');
INSERT INTO "bayan" VALUES(75,23,'75:23','اپنے رب کی طرف دیکھ رہے ہوں گے۔');
INSERT INTO "bayan" VALUES(75,24,'75:24','اور بہت سے چہرے اس روز اترے ہوئے ہوں گے۔');
INSERT INTO "bayan" VALUES(75,25,'75:25','ان کو یقین ہوگا کہ اب ان کے ساتھ کمرتوڑ سلوک ہونے و الا ہے۔');
INSERT INTO "bayan" VALUES(75,26,'75:26','ہرگز نہیں ! جب کہ جان آکر پھنس جاتی ہے ہنسلیوں میں۔');
INSERT INTO "bayan" VALUES(75,27,'75:27','اور کہا جاتا ہے کہ ہے کوئی جھاڑ پھونک کرنے والا ؟');
INSERT INTO "bayan" VALUES(75,28,'75:28','اور وہ سمجھ جاتا ہے کہ اب جدائی کی گھڑی آن پہنچی ہے۔');
INSERT INTO "bayan" VALUES(75,29,'75:29','اور پنڈلی پنڈلی سے لپٹ جاتی ہے۔');
INSERT INTO "bayan" VALUES(75,30,'75:30','اس دن تو تیرے رب ہی کی طرف دھکیلے جانا ہے۔');
INSERT INTO "bayan" VALUES(75,31,'75:31','پس اس نے نہ تو تصدیق کی اور نہ نماز پڑھی۔');
INSERT INTO "bayan" VALUES(75,32,'75:32','بلکہ اس نے جھٹلادیا اور پیٹھ موڑ لی۔');
INSERT INTO "bayan" VALUES(75,33,'75:33','پھر چل دیا اپنے گھر والوں کے پاس اکڑتا ہوا۔');
INSERT INTO "bayan" VALUES(75,34,'75:34','افسوس ہے تجھ پر افسوس ہے !');
INSERT INTO "bayan" VALUES(75,35,'75:35','پھر افسوس ہے تجھ پر افسوس ہے !');
INSERT INTO "bayan" VALUES(75,36,'75:36','کیا انسان یہ سمجھتا ہے کہ اسے یوں ہی چھوڑ دیا جائے گا ؟');
INSERT INTO "bayan" VALUES(75,37,'75:37','کیا وہ حقیر پانی کی ایک بوند نہیں تھا جو (رحم مادر میں) ٹپکائی جاتی ہے ؟');
INSERT INTO "bayan" VALUES(75,38,'75:38','پھر وہ ایک علقہ بنا پھر اللہ نے اس کو بنایا اور اس کے اعضاء درست کیے۔');
INSERT INTO "bayan" VALUES(75,39,'75:39','پھر اسی سے اس نے دو زوج بنائے نر اور مادہ۔');
INSERT INTO "bayan" VALUES(75,40,'75:40','تو کیا وہ اس پر قادر نہیں کہ ُ مردوں کو زندہ کردے ؟');
INSERT INTO "bayan" VALUES(76,1,'76:1','کیا انسان پر اس زمانے میں ایک ایسا وقت بھی گزرا ہے جبکہ وہ کوئی قابل ذکر شے نہ تھا ؟');
INSERT INTO "bayan" VALUES(76,2,'76:2','ہم نے انسان کو پیدا کیا ہے ملے جلے نطفے سے ہم اس کو الٹتے پلٹتے رہے پھر ہم نے اس کو بنا دیا سننے والا دیکھنے والا۔');
INSERT INTO "bayan" VALUES(76,3,'76:3','ہم نے اس کو راہ سجھا دی اب چاہے تو وہ شکر گزار بن کر رہے چاہے ناشکرا ہوکر۔');
INSERT INTO "bayan" VALUES(76,4,'76:4','یقینا کافروں کے لیے ہم نے تیار کر رکھی ہیں زنجیریں اور طوق اور دہکتی ہوئی آگ۔');
INSERT INTO "bayan" VALUES(76,5,'76:5','یقینا نیکوکار بندے ایسی شراب کے جام نوش کریں گے جس میں کافور کی ملونی ہوگی۔');
INSERT INTO "bayan" VALUES(76,6,'76:6','یہ ایک چشمہ ہے جس میں سے اللہ کے خاص بندے پئیں گے اور جدھر چاہیں گے اس کی شاخیں نکال لے جائیں گے۔');
INSERT INTO "bayan" VALUES(76,7,'76:7','وہ نذر کو پورا کرتے ہیں اور ڈرتے رہتے ہیں اس دن سے جس کا شر ہر ُ سو پھیل جائے گا۔');
INSERT INTO "bayan" VALUES(76,8,'76:8','اور وہ کھانا کھلاتے ہیں اللہ کی محبت میں مسکین کو یتیم کو اور قیدی کو۔');
INSERT INTO "bayan" VALUES(76,9,'76:9','(اور کہتے ہیں کہ) ہم تو آپ کو یہ کھانا کھلا رہے ہیں صرف اللہ (کی رضا) کے لیے ہم آپ سے نہ تو کوئی بدلہ چاہتے ہیں اور نہ ہی شکریہ۔');
INSERT INTO "bayan" VALUES(76,10,'76:10','ہم تو ڈرتے ہیں اپنے رب کی طرف سے ایک ایسے دن سے جس کی اداسی بڑی ہولناک ہوگی۔');
INSERT INTO "bayan" VALUES(76,11,'76:11','تو اللہ انہیں بچا لے گا اس دن کے شر سے اور بخش دے گا انہیں تروتازگی اور مسرت۔');
INSERT INTO "bayan" VALUES(76,12,'76:12','اور بدلے میں دے گا انہیں ان کے صبر کے سبب جنت اور ریشم کا لباس۔');
INSERT INTO "bayan" VALUES(76,13,'76:13','وہ تکیے لگائے بیٹھے ہوئے ہوں گے اس میں تختوں کے اوپر۔ نہیں دیکھیں گے وہ اس میں دھوپ کی حدت اور نہ سخت سردی۔');
INSERT INTO "bayan" VALUES(76,14,'76:14','اور جنت کے سائے ان کے اوپر جھکے ہوئے ہوں گے اور اس کے پھلوں کے خوشے نیچے جھکے ہوئے لٹک رہے ہوں گے۔');
INSERT INTO "bayan" VALUES(76,15,'76:15','اور گردش میں ہوں گے ان پر (خدامِ جنت) چاندی کے برتنوں اور شیشے کے پیالوں کے ساتھ۔');
INSERT INTO "bayan" VALUES(76,16,'76:16','ایسا شیشہ جو چاندی کا ہوگا ان کو (ساقیانِ جنت نے) بھرا ہوگا بھرپور اندازے کے مطابق۔');
INSERT INTO "bayan" VALUES(76,17,'76:17','اور ان کو پلایا جائے گا اس میں ایساجام کہ جس میں ملونی ہوگی سونٹھ کی۔');
INSERT INTO "bayan" VALUES(76,18,'76:18','یہ ایک چشمہ ہے اس (جنت) میں جس کا نام سلسبیل ہے۔');
INSERT INTO "bayan" VALUES(76,19,'76:19','اور ان پر گردش میں رہیں گے ایسے لڑکے (خدامِ جنت) جو ہمیشہ اسی عمر (اور شکل) میں رہیں گے۔ جب تم انہیں دیکھو گے تو محسوس کرو گے کہ جیسے وہ موتی ہیں بکھرے ہوئے۔');
INSERT INTO "bayan" VALUES(76,20,'76:20','اور تم جہاں بھی دیکھو گے وہیں نعمتیں ہی نعمتیں اور بہت بڑی بادشاہی دیکھو گے۔');
INSERT INTO "bayan" VALUES(76,21,'76:21','ان کے اوپر لباس ہوں گے باریک سبز ریشم کے اور گاڑھے ریشم کے اور انہیں کنگن پہنائے جائیں گے چاندی کے۔ اور پلائے گا انہیں ان کا پروردگار نہایت پاکیزہ مشروب۔');
INSERT INTO "bayan" VALUES(76,22,'76:22','(اور کہا جائے گا :) یہ تم لوگوں کے لیے بدلہ ہے اور تمہاری جدوجہد مقبول ہوچکی ہے۔');
INSERT INTO "bayan" VALUES(76,23,'76:23','(اے نبی صلى الله عليه وسلم ہم نے ہی نازل کیا ہے آپ پر قرآن تھوڑا تھوڑا کر کے۔');
INSERT INTO "bayan" VALUES(76,24,'76:24','تو آپ انتظار کیجیے اپنے رب کے حکم کا اور آپ صلى الله عليه وسلم ان میں سے کسی گناہگار یا ناشکرے کی باتوں پر دھیان نہ دیجیے۔');
INSERT INTO "bayan" VALUES(76,25,'76:25','اور اپنے رب کے نام کا ذکر کیجیے صبح و شام۔');
INSERT INTO "bayan" VALUES(76,26,'76:26','اور رات کے ایک حصے میں اس کے لیے سجدہ کیا کیجیے اور رات کے بڑے حصے میں اس کی تسبیح کیا کیجیے۔');
INSERT INTO "bayan" VALUES(76,27,'76:27','یقینا یہ لوگ فوری ملنے والی چیز (دنیا) سے محبت کرتے ہیں اور ایک بھاری دن جو ان کے پیچھے آنے والا ہے اس کا دھیان چھوڑے بیٹھے ہیں۔');
INSERT INTO "bayan" VALUES(76,28,'76:28','ہم نے ہی ان کو تخلیق فرمایا ہے اور ان کے جوڑ بند مضبوط کیے ہیں۔ اور ہم جب چاہیں گے ان جیسے بدل کر اور لے آئیں گے۔');
INSERT INTO "bayan" VALUES(76,29,'76:29','یقینا یہ تو ایک یاد دہانی ہے۔ تو جو چاہے اپنے ربّ کی طرف راستہ اختیار کرلے۔');
INSERT INTO "bayan" VALUES(76,30,'76:30','اور تمہارے چاہے کچھ نہیں ہوسکتا جب تک کہ اللہ نہ چاہے۔ یقینا اللہ سب کچھ جاننے والا کمال حکمت والا ہے۔');
INSERT INTO "bayan" VALUES(76,31,'76:31','وہ داخل کرے گا اپنی رحمت میں جس کو چاہے گا۔ اور رہے ظالم تو ان کے لیے اس نے بہت دردناک عذاب تیار کر رکھا ہے۔');
INSERT INTO "bayan" VALUES(77,1,'77:1','قسم ہے ان ہوائوں کی جو چلائی جاتی ہیں بڑی آہستگی سے۔');
INSERT INTO "bayan" VALUES(77,2,'77:2','پھر تیز و تند جھکڑ ّکی صورت میں چلتی ہیں۔');
INSERT INTO "bayan" VALUES(77,3,'77:3','اور ان ہوائوں کی قسم جو (بادلوں کو) پھیلا دیتی ہیں۔');
INSERT INTO "bayan" VALUES(77,4,'77:4','پھر تقسیم کرتی ہیں جدا جدا۔');
INSERT INTO "bayan" VALUES(77,5,'77:5','پھر قسم ہے ان (فرشتوں) کی جو ذکر کا القاء کرتے ہیں۔');
INSERT INTO "bayan" VALUES(77,6,'77:6','عذر کے طور پر یا خبردار کرنے کے لیے۔');
INSERT INTO "bayan" VALUES(77,7,'77:7','جس چیز کا تم سے وعدہ کیا جا رہا ہے وہ واقع ہو کر رہے گی۔');
INSERT INTO "bayan" VALUES(77,8,'77:8','پس جب ستارے مٹا دیے جائیں گے۔');
INSERT INTO "bayan" VALUES(77,9,'77:9','اور جب آسمان میں شگاف پڑجائیں گے۔');
INSERT INTO "bayan" VALUES(77,10,'77:10','اور جب پہاڑ (ریت بنا کر) اُڑا دیے جائیں گے۔');
INSERT INTO "bayan" VALUES(77,11,'77:11','اور جب رسولوں ؑ (کے کھڑے ہونے) کا وقت آپہنچے گا۔');
INSERT INTO "bayan" VALUES(77,12,'77:12','(یہ معاملہ) کس دن کے لیے موخر کیا گیا ہے ؟');
INSERT INTO "bayan" VALUES(77,13,'77:13','فیصلے کے دن کے لیے۔');
INSERT INTO "bayan" VALUES(77,14,'77:14','اور تم لوگ کیا سمجھتے ہو کہ فیصلے کا دن کیا ہے ؟');
INSERT INTO "bayan" VALUES(77,15,'77:15','(ہلاکت اور) بربادی ہے اس دن جھٹلانے والوں کے لیے۔');
INSERT INTO "bayan" VALUES(77,16,'77:16','کیا ہم نے پہلوں کو ہلاک نہیں کیا ؟');
INSERT INTO "bayan" VALUES(77,17,'77:17','پھر ہم ان کے پیچھے لگاتے رہے بعد میں آنے والوں کو۔');
INSERT INTO "bayan" VALUES(77,18,'77:18','اسی طرح ہم کرتے رہے ہیں مجرموں کے ساتھ۔');
INSERT INTO "bayan" VALUES(77,19,'77:19','(ہلاکت اور) بربادی ہے اس دن جھٹلانے والوں کے لیے۔');
INSERT INTO "bayan" VALUES(77,20,'77:20','کیا ہم نے تمہیں حقیر پانی سے پیدا نہیں کیا ؟');
INSERT INTO "bayan" VALUES(77,21,'77:21','پھر ہم نے اس (نطفے) کو رکھ دیا ایک محفوظ مقام میں۔');
INSERT INTO "bayan" VALUES(77,22,'77:22','ایک طے شدہ مدت تک۔');
INSERT INTO "bayan" VALUES(77,23,'77:23','تو ہم نے اندازہ مقرر کیا اور ہم کیا ہی اچھے ہیں اندازہ مقرر کرنے والے !');
INSERT INTO "bayan" VALUES(77,24,'77:24','(ہلاکت اور) بربادی ہے اس دن جھٹلانے والوں کے لیے۔');
INSERT INTO "bayan" VALUES(77,25,'77:25','کیا ہم نے زمین کو نہیں بنا دیا سمیٹ لینے والی ؟');
INSERT INTO "bayan" VALUES(77,26,'77:26','زندوں کو بھی اور ُ مردوں کو بھی !');
INSERT INTO "bayan" VALUES(77,27,'77:27','اور ہم نے اس کے اندر بنا دیے خوب جمے ہوئے اونچے اونچے پہاڑ اور ہم نے تمہیں پلایا (اس میں سے) تسکین بخش پانی۔');
INSERT INTO "bayan" VALUES(77,28,'77:28','(ہلاکت اور) بربادی ہے اس دن جھٹلانے والوں کے لیے۔');
INSERT INTO "bayan" VALUES(77,29,'77:29','چلو اب اسی چیز کی طرف جس کو تم جھٹلایا کرتے تھے۔');
INSERT INTO "bayan" VALUES(77,30,'77:30','چلو اس تین شاخوں والے سائے کی طرف !');
INSERT INTO "bayan" VALUES(77,31,'77:31','نہ تو وہ سایہ دار ہے اور نہ ہی آگ کی تپش سے بچانے والا۔');
INSERT INTO "bayan" VALUES(77,32,'77:32','وہ آگ اتنے بڑے بڑے انگارے پھینک رہی ہوگی جیسے کہ وہ محل ہوں۔');
INSERT INTO "bayan" VALUES(77,33,'77:33','(یا) جیسے وہ زرد رنگ کے اونٹ ہوں۔');
INSERT INTO "bayan" VALUES(77,34,'77:34','(ہلاکت اور) بربادی ہے اس دن جھٹلانے والوں کے لیے۔');
INSERT INTO "bayan" VALUES(77,35,'77:35','یہ ہے وہ دن جس میں یہ بول نہیں سکیں گے۔');
INSERT INTO "bayan" VALUES(77,36,'77:36','اور نہ ہی انہیں اجازت دی جائے گی کہ وہ کوئی عذر تراشیں۔');
INSERT INTO "bayan" VALUES(77,37,'77:37','(ہلاکت اور) بربادی ہے اس دن جھٹلانے والوں کے لیے۔');
INSERT INTO "bayan" VALUES(77,38,'77:38','یہ ہے فیصلے کا دن ہم نے جمع کرلیا تمہیں بھی اور پہلوں کو بھی۔');
INSERT INTO "bayan" VALUES(77,39,'77:39','اب اگر تمہارے پاس کوئی چال ہے تو میرے خلاف وہ چال چل دیکھو۔');
INSERT INTO "bayan" VALUES(77,40,'77:40','(ہلاکت اور) بربادی ہے اس دن جھٹلانے والوں کے لیے۔');
INSERT INTO "bayan" VALUES(77,41,'77:41','(اس دن) متقین یقینا سائے اور چشموں کے ماحول میں ہوں گے۔');
INSERT INTO "bayan" VALUES(77,42,'77:42','اور ایسے پھلوں کے اندر جو انہیں پسند ہوں گے۔');
INSERT INTO "bayan" VALUES(77,43,'77:43','(اور ہم ان سے کہہ دیں گے) کھائو پیو مزے سے رچتا پچتا ان اعمال کے انعام کے طور پر جو تم کرتے رہے تھے۔');
INSERT INTO "bayan" VALUES(77,44,'77:44','یقینا ہم اسی طرح بدلہ دیا کرتے ہیں اپنے محسن بندوں کو۔');
INSERT INTO "bayan" VALUES(77,45,'77:45','(ہلاکت اور) بربادی ہے اس دن جھٹلانے والوں کے لیے۔');
INSERT INTO "bayan" VALUES(77,46,'77:46','(اے کفار و مشرکین !) تم کھا پی لو تھوڑی دیر کے لیے یقینا تم لوگ مجرم ہو۔');
INSERT INTO "bayan" VALUES(77,47,'77:47','جس دن ہلاکت اور بربادی ہوگی (تمہارے جیسے تمام) جھٹلانے والوں کے لیے۔');
INSERT INTO "bayan" VALUES(77,48,'77:48','اور جب ان سے کہا جاتا ہے کہ (اپنے رب کے سامنے) جھکو تو یہ جھکتے نہیں ہیں۔');
INSERT INTO "bayan" VALUES(77,49,'77:49','(ہلاکت اور) بربادی ہے اس دن جھٹلانے والوں کے لیے۔');
INSERT INTO "bayan" VALUES(77,50,'77:50','تو اب اس (قرآن) کے بعد یہ اور کس کلام پر ایمان لائیں گے ؟');
INSERT INTO "bayan" VALUES(78,1,'78:1','کس چیز کے بارے میں یہ لوگ آپس میں پوچھ گچھ کر رہے ہیں ؟');
INSERT INTO "bayan" VALUES(78,2,'78:2','اس بڑی خبر کے بارے میں۔');
INSERT INTO "bayan" VALUES(78,3,'78:3','جس کے بارے میں یہ اختلافِ رائے میں مبتلا ہوگئے ہیں۔');
INSERT INTO "bayan" VALUES(78,4,'78:4','نہیں ! عنقریب یہ جان لیں گے۔');
INSERT INTO "bayan" VALUES(78,5,'78:5','ہاں کوئی بات نہیں ! عنقریب یہ جان لیں گے۔');
INSERT INTO "bayan" VALUES(78,6,'78:6','کیا ہم نے نہیں بنا دیا زمین کو بچھونا ؟');
INSERT INTO "bayan" VALUES(78,7,'78:7','اور پہاڑوں کو میخیں ؟');
INSERT INTO "bayan" VALUES(78,8,'78:8','اور تمہیں ہم نے جوڑوں کی شکل میں پیدا کیا۔');
INSERT INTO "bayan" VALUES(78,9,'78:9','اور تمہاری نیند کو بنا دیا ہم نے تھکان دور کرنے والی۔');
INSERT INTO "bayan" VALUES(78,10,'78:10','اور رات کو ہم نے بنا دیا ڈھانپ لینے والی۔');
INSERT INTO "bayan" VALUES(78,11,'78:11','اور دن کو ہم نے بنا دیا معاش (کی جدوجہد) کے لیے۔');
INSERT INTO "bayan" VALUES(78,12,'78:12','اور تمہارے اوپر بنا دیے ہم نے سات مضبوط آسمان۔');
INSERT INTO "bayan" VALUES(78,13,'78:13','اور ہم نے (سورج کو) بنا دیا ایک روشن چراغ۔');
INSERT INTO "bayan" VALUES(78,14,'78:14','اور ہم نے اتار دیا نچڑنے والی بدلیوں سے چھاجوں پانی۔');
INSERT INTO "bayan" VALUES(78,15,'78:15','تاکہ اس کے ذریعے سے ہم نکالیں اناج اور دوسرے نباتات۔');
INSERT INTO "bayan" VALUES(78,16,'78:16','اور بڑے گھنے باغات بھی۔');
INSERT INTO "bayan" VALUES(78,17,'78:17','یقینا فیصلے کا دن ایک معین وقت ہے۔');
INSERT INTO "bayan" VALUES(78,18,'78:18','جس دن پھونکا جائے گا صور میں تو تم سب چلے آئو گے فوج در فوج۔');
INSERT INTO "bayan" VALUES(78,19,'78:19','اور آسمان کھول دیا جائے گا تو اس میں دروازے ہی دروازے بن جائیں گے۔');
INSERT INTO "bayan" VALUES(78,20,'78:20','اور پہاڑ چلا دیے جائیں گے تو وہ ہوجائیں گے چمکتی ہوئی ریت۔');
INSERT INTO "bayan" VALUES(78,21,'78:21','یقینا جہنم گھات میں ہے۔');
INSERT INTO "bayan" VALUES(78,22,'78:22','وہ ٹھکانہ ہے سرکش لوگوں کا۔');
INSERT INTO "bayan" VALUES(78,23,'78:23','وہ رہیں گے اس میں قرن ہا قرن۔');
INSERT INTO "bayan" VALUES(78,24,'78:24','وہ نہیں چکھیں گے اس میں کوئی ٹھنڈی شے اور نہ کوئی مشروب۔');
INSERT INTO "bayan" VALUES(78,25,'78:25','سوائے کھولتے ہوئے پانی اور بہتی ہوئی پیپ کے۔');
INSERT INTO "bayan" VALUES(78,26,'78:26','بدلہ (ان کے اعمال کا) پورا پورا۔');
INSERT INTO "bayan" VALUES(78,27,'78:27','یہ لوگ کسی حساب کتاب کی کوئی توقع نہیں رکھتے تھے۔');
INSERT INTO "bayan" VALUES(78,28,'78:28','اور انہوں نے جھٹلا دیا تھا ہماری آیات کو دھڑلے ّکے ساتھ۔');
INSERT INTO "bayan" VALUES(78,29,'78:29','اور ہم نے تو ہرچیز کو گن گن کر لکھ رکھا ہے۔');
INSERT INTO "bayan" VALUES(78,30,'78:30','تو اب چکھو ! ہم ہرگز اضافہ نہیں کریں گے تمہارے لیے مگر عذاب ہی میں۔');
INSERT INTO "bayan" VALUES(78,31,'78:31','یقینا اہل ِتقویٰ کے لیے کامیابی ہوگی۔');
INSERT INTO "bayan" VALUES(78,32,'78:32','(ان کے لیے) باغ اور انگور ہوں گے۔');
INSERT INTO "bayan" VALUES(78,33,'78:33','اور نوجوان ہم عمر بیویاں۔');
INSERT INTO "bayan" VALUES(78,34,'78:34','اور جام چھلکتے ہوئے۔');
INSERT INTO "bayan" VALUES(78,35,'78:35','وہ نہیں سنیں گے اس میں کوئی لغو بات اور نہ جھوٹ۔');
INSERT INTO "bayan" VALUES(78,36,'78:36','یہ بدلہ ہوگا آپ کے رب کی طرف سے دیا ہوا حساب سے۔');
INSERT INTO "bayan" VALUES(78,37,'78:37','جو رب ہے آسمانوں کا اور زمین کا اور جو کچھ ان کے مابین ہے جو الرحمن ہے۔ ان (میں سے کسی) کو ہمت نہیں ہوگی اس سے بات کرنے کی۔');
INSERT INTO "bayan" VALUES(78,38,'78:38','جس دن کھڑے ہوں گے جبرائیل اور تمام فرشتے صفیں باندھے۔ وہ کوئی بات نہ کرسکیں گے سوائے اس کے جسے رحمن کی طرف سے اجازت مل جائے اور وہ بات بھی درست کرے۔');
INSERT INTO "bayan" VALUES(78,39,'78:39','یہ دن حق ہے ! تو جو چاہے اپنے رب کی طرف اپنا ٹھکانہ بنا لے۔');
INSERT INTO "bayan" VALUES(78,40,'78:40','دیکھو ! ہم نے تو تمہیں خبردار کردیا ہے اس عذاب سے جو بالکل قریب ہے۔ جس دن انسان دیکھ لے گا جو اس کے دونوں ہاتھوں نے آگے بھیجا تھا اور کافر کہے گا : کاش کہ میں مٹی ہوتا !');
INSERT INTO "bayan" VALUES(79,1,'79:1','قسم ہے ان (فرشتوں) کی جو غوطہ لگا کر کھینچتے ہیں۔');
INSERT INTO "bayan" VALUES(79,2,'79:2','اور ان (فرشتوں) کی قسم جو گرہیں کھولتے ہیں آسانی سے۔');
INSERT INTO "bayan" VALUES(79,3,'79:3','اور ان (فرشتوں) کی قسم جو تیزی سے تیرتے ہوئے جاتے ہیں۔');
INSERT INTO "bayan" VALUES(79,4,'79:4','پھر وہ (تعمیل ِارشاد میں) ایک دوسرے سے آگے بڑھنے کی کوشش کرتے ہیں۔');
INSERT INTO "bayan" VALUES(79,5,'79:5','پھر (حسب حکم) معاملات کی تدبیر کرتے ہیں۔');
INSERT INTO "bayan" VALUES(79,6,'79:6','جس دن کانپے گی کانپنے والی۔');
INSERT INTO "bayan" VALUES(79,7,'79:7','اس کے پیچھے ایک اور جھٹکا آئے گا۔');
INSERT INTO "bayan" VALUES(79,8,'79:8','کتنے ہی دل اس دن (خوف کے مارے) دھڑک رہے ہوں گے۔');
INSERT INTO "bayan" VALUES(79,9,'79:9','ان کی نگاہیں جھکی ہوئی ہوں گی۔');
INSERT INTO "bayan" VALUES(79,10,'79:10','یہ لوگ کہتے ہیں : کیا ہمیں لوٹا دیا جائے گا الٹے پائوں ؟');
INSERT INTO "bayan" VALUES(79,11,'79:11','کیا جب ہم کھوکھلی ہڈیاں ہوچکے ہوں گے ؟');
INSERT INTO "bayan" VALUES(79,12,'79:12','کہتے ہیں : تب تو یہ لوٹنا بہت گھاٹے کا سودا ہوگا۔');
INSERT INTO "bayan" VALUES(79,13,'79:13','حالانکہ یہ تو بس ایک ہی ڈانٹ ہوگی۔');
INSERT INTO "bayan" VALUES(79,14,'79:14','تو وہ سب کے سب ایک چٹیل میدان میں ہوں گے۔');
INSERT INTO "bayan" VALUES(79,15,'79:15','کیا آپ تک موسیٰ کی خبر پہنچی ہے ؟');
INSERT INTO "bayan" VALUES(79,16,'79:16','جب اس کو پکارا تھا اس کے پروردگار نے طویٰ کی مقدس وادی میں۔');
INSERT INTO "bayan" VALUES(79,17,'79:17','کہ جائو فرعون کے پاس وہ بہت سرکش ہوگیا ہے۔');
INSERT INTO "bayan" VALUES(79,18,'79:18','اور اسے کہو کہ کیا تو چاہتا ہے کہ پاک ہوجائے ؟');
INSERT INTO "bayan" VALUES(79,19,'79:19','اور میں تمہاری راہنمائی کروں تمہارے رب کی طرف تاکہ تمہارے اندر خشیت پیدا ہو ؟');
INSERT INTO "bayan" VALUES(79,20,'79:20','تو موسیٰ ؑ نے اس کو دکھائی بہت بڑی نشانی۔');
INSERT INTO "bayan" VALUES(79,21,'79:21','لیکن اس نے جھٹلا دیا اور نافرمانی کی۔');
INSERT INTO "bayan" VALUES(79,22,'79:22','پھر وہ پلٹا بھاگ دوڑ کرنے کے لیے۔');
INSERT INTO "bayan" VALUES(79,23,'79:23','پھر اس نے (اپنی رعیت کو) جمع کیا اور اعلان کیا۔');
INSERT INTO "bayan" VALUES(79,24,'79:24','پس کہا کہ میں ہوں تمہارا سب سے بڑا ربّ !');
INSERT INTO "bayan" VALUES(79,25,'79:25','تو پکڑ لیا اس کو اللہ نے آخرت اور دنیا کی سزا میں۔');
INSERT INTO "bayan" VALUES(79,26,'79:26','یقینا اس میں عبرت ہے اس کے لیے جو ڈرتا ہے۔');
INSERT INTO "bayan" VALUES(79,27,'79:27','(اے لوگو ! ذرا سوچو !) کیا تمہاری تخلیق زیادہ مشکل ہے یا آسمان کی ؟ اس نے اسے تخلیق کیا۔');
INSERT INTO "bayan" VALUES(79,28,'79:28','اس کے گنبد کو بلند کیا پھر اسے ہر طرح سے درست کیا۔');
INSERT INTO "bayan" VALUES(79,29,'79:29','اور اس کی رات کو اندھیرا کردیا اور اس کے دن کو ظاہر کردیا۔');
INSERT INTO "bayan" VALUES(79,30,'79:30','اور زمین کو اس کے بعد بچھا دیا۔');
INSERT INTO "bayan" VALUES(79,31,'79:31','اس میں سے نکالا اس کا پانی اور اس کا چارہ۔');
INSERT INTO "bayan" VALUES(79,32,'79:32','اور پہاڑوں کو اس میں گاڑ دیا۔');
INSERT INTO "bayan" VALUES(79,33,'79:33','سامانِ زیست کے طور پر تمہارے لیے اور تمہارے چوپایوں کے لیے۔');
INSERT INTO "bayan" VALUES(79,34,'79:34','پھر جب برپاہو گا وہ بڑا ہنگامہ۔');
INSERT INTO "bayan" VALUES(79,35,'79:35','اس دن انسان یاد کرے گا جو کچھ اس نے بھاگ دوڑ کی تھی۔');
INSERT INTO "bayan" VALUES(79,36,'79:36','اور کھول کر رکھ دی جائے گی جہنم ہر دیکھنے والے کے لیے۔');
INSERT INTO "bayan" VALUES(79,37,'79:37','پس جس نے سرکشی کی تھی۔');
INSERT INTO "bayan" VALUES(79,38,'79:38','اور دنیا کی زندگی کو ترجیح دی تھی۔');
INSERT INTO "bayan" VALUES(79,39,'79:39','تویقینا اس کا ٹھکانہ اب جہنم ہی ہے۔');
INSERT INTO "bayan" VALUES(79,40,'79:40','اور جو کوئی ڈرتا رہا اپنے ربّ کے حضور کھڑا ہونے (کے خیال) سے');
INSERT INTO "bayan" VALUES(79,41,'79:41','تو یقینااُس کا ٹھکانہ جنت ہی ہے۔');
INSERT INTO "bayan" VALUES(79,42,'79:42','(اے نبی صلى الله عليه وسلم !) یہ لوگ آپ سے پوچھتے ہیں قیامت کے بارے میں کہ وہ کب آکرٹھہرے گی ؟');
INSERT INTO "bayan" VALUES(79,43,'79:43','آپ صلى الله عليه وسلم کا کیا تعلق ہے اس کے بیان کرنے سے ؟');
INSERT INTO "bayan" VALUES(79,44,'79:44','اس کا انجام تو آپ کے رب ہی کی طرف ہے۔');
INSERT INTO "bayan" VALUES(79,45,'79:45','آپ صلى الله عليه وسلم تو بس خبردار کرنے والے ہیں ہر اس شخص کو جو اس سے ڈرتا ہو۔');
INSERT INTO "bayan" VALUES(79,46,'79:46','جس دن وہ اسے دیکھیں گے (انہیں یوں لگے گا) گویا وہ نہیں رہے (دنیا میں) مگر ایک شام یا اس کی ایک صبح۔');
INSERT INTO "bayan" VALUES(80,1,'80:1','تیوری چڑھائی اور منہ پھیرلیا۔');
INSERT INTO "bayan" VALUES(80,2,'80:2','اس بات پر کہ آیا اس کے پاس نابینا۔');
INSERT INTO "bayan" VALUES(80,3,'80:3','اور (اے نبی صلى الله عليه وسلم آپ کو کیا معلوم شاید کہ وہ تزکیہ حاصل کرتا۔');
INSERT INTO "bayan" VALUES(80,4,'80:4','یا وہ نصیحت حاصل کرتا اور وہ نصیحت اس کے لیے مفید ہوتی۔');
INSERT INTO "bayan" VALUES(80,5,'80:5','لیکن وہ جو بےنیازی دکھاتا ہے۔');
INSERT INTO "bayan" VALUES(80,6,'80:6','آپ اس کی تو فکر میں رہتے ہیں۔');
INSERT INTO "bayan" VALUES(80,7,'80:7','اور اگر وہ پاکی اختیار نہیں کرتا تو آپ پر کوئی الزام نہیں۔');
INSERT INTO "bayan" VALUES(80,8,'80:8','اور وہ جو آپ صلى الله عليه وسلم کے پاس دوڑتا ہوا آیا ہے۔');
INSERT INTO "bayan" VALUES(80,9,'80:9','اور اس کے دل میں خشیت بھی ہے۔');
INSERT INTO "bayan" VALUES(80,10,'80:10','تو اس سے آپ صلى الله عليه وسلم استغناء برت رہے ہیں۔');
INSERT INTO "bayan" VALUES(80,11,'80:11','ایسا ہرگز نہ کیجیے یہ تو ایک یاد دہانی ہے۔');
INSERT INTO "bayan" VALUES(80,12,'80:12','تو جو چاہے اس سے نصیحت اخذ کرلے۔');
INSERT INTO "bayan" VALUES(80,13,'80:13','(یہ قرآن) ایسے صحیفوں میں (درج ہے) جو باعزت ہیں۔');
INSERT INTO "bayan" VALUES(80,14,'80:14','بلند وبالا نہایت پاک۔');
INSERT INTO "bayan" VALUES(80,15,'80:15','ان لکھنے والوں کے ہاتھوں میں۔');
INSERT INTO "bayan" VALUES(80,16,'80:16','جو بڑے معزز اور نیک ہیں۔');
INSERT INTO "bayan" VALUES(80,17,'80:17','ہلاک ہوجائے انسان یہ کس قدر ناشکرا ہے !');
INSERT INTO "bayan" VALUES(80,18,'80:18','(ذرا سوچیے کہ) کس چیز سے اللہ نے اسے پیدا کیا ہے ؟');
INSERT INTO "bayan" VALUES(80,19,'80:19','ایک بوند سے۔ اس نے اسے پیدا کیا اور اس کا ایک اندازہ مقرر کردیا۔');
INSERT INTO "bayan" VALUES(80,20,'80:20','پھر آسان کردیا اس پر راستہ۔');
INSERT INTO "bayan" VALUES(80,21,'80:21','پھر اس پر موت وارد کی اور اسے قبر میں داخل کردیا۔');
INSERT INTO "bayan" VALUES(80,22,'80:22','پھر جب وہ چاہے گا اسے اٹھا کھڑا کرے گا۔');
INSERT INTO "bayan" VALUES(80,23,'80:23','نہیں ! اس نے تاحال وہ بات پوری نہیں کی جس کا اللہ نے اسے حکم دیا تھا۔');
INSERT INTO "bayan" VALUES(80,24,'80:24','تو انسان ذرا اپنے کھانے ہی کو دیکھ لے۔');
INSERT INTO "bayan" VALUES(80,25,'80:25','کہ ہم نے آسمان سے پانی برسایا جیسے کہ برسایا جاتا ہے۔');
INSERT INTO "bayan" VALUES(80,26,'80:26','پھر ہم نے زمین کو پھاڑا جیسے کہ وہ پھٹتی ہے۔');
INSERT INTO "bayan" VALUES(80,27,'80:27','پھر ہم نے اگا دیے اس میں اناج۔');
INSERT INTO "bayan" VALUES(80,28,'80:28','اور انگور اور مختلف ترکاریاں۔');
INSERT INTO "bayan" VALUES(80,29,'80:29','اور زیتون اور کھجوریں۔');
INSERT INTO "bayan" VALUES(80,30,'80:30','اور بڑے گھنے باغات۔');
INSERT INTO "bayan" VALUES(80,31,'80:31','اور میوے اور چارہ۔');
INSERT INTO "bayan" VALUES(80,32,'80:32','ضرورت کا سامان تمہارے لیے بھی اور تمہارے مویشیوں کے لیے بھی۔');
INSERT INTO "bayan" VALUES(80,33,'80:33','تو جب وہ آجائے گی کان پھوڑنے والی (آواز)۔');
INSERT INTO "bayan" VALUES(80,34,'80:34','اس دن بھاگے گا انسان اپنے بھائی سے۔');
INSERT INTO "bayan" VALUES(80,35,'80:35','اور اپنی ماں سے اور اپنے باپ سے۔');
INSERT INTO "bayan" VALUES(80,36,'80:36','اور اپنی بیوی سے اور اپنے بیٹوں سے۔');
INSERT INTO "bayan" VALUES(80,37,'80:37','اس دن ان میں سے ہر شخص کو ایسی فکر لاحق ہوگی جو اسے (ہر ایک سے) بےپروا کر دے گی۔');
INSERT INTO "bayan" VALUES(80,38,'80:38','کچھ چہرے اس دن روشن ہوں گے۔');
INSERT INTO "bayan" VALUES(80,39,'80:39','مسکراتے ہوئے خوش و خرم۔');
INSERT INTO "bayan" VALUES(80,40,'80:40','اور کچھ چہرے اس دن غبار آلود ہوں گے۔');
INSERT INTO "bayan" VALUES(80,41,'80:41','ان پر سیاہی چھائی ہوئی ہوگی۔');
INSERT INTO "bayan" VALUES(80,42,'80:42','یہی ہوں گے وہ کافر اور فاجر لوگ۔');
INSERT INTO "bayan" VALUES(81,1,'81:1','جب سورج لپیٹ لیاجائے گا۔');
INSERT INTO "bayan" VALUES(81,2,'81:2','اور جب ستارے ماند پڑجائیں گے۔');
INSERT INTO "bayan" VALUES(81,3,'81:3','اور جب پہاڑ چلادیے جائیں گے۔');
INSERT INTO "bayan" VALUES(81,4,'81:4','اور جب گابھن اونٹنیاں چھٹی پھریں گی۔');
INSERT INTO "bayan" VALUES(81,5,'81:5','اور جب وحشی جانور جمع کردیے جائیں گے۔');
INSERT INTO "bayan" VALUES(81,6,'81:6','اور جب سمندر دہکا دیے جائیں گے۔');
INSERT INTO "bayan" VALUES(81,7,'81:7','اور جب جانوں کے جوڑ ملائے جائیں گے۔');
INSERT INTO "bayan" VALUES(81,8,'81:8','اور جب زندہ دفن کی گئی لڑکی سے پوچھا جائے گا۔');
INSERT INTO "bayan" VALUES(81,9,'81:9','کہ وہ کس گناہ کی پاداش میں قتل کی گئی تھی ؟');
INSERT INTO "bayan" VALUES(81,10,'81:10','اور جب اعمال نامے کھول دیے جائیں گے۔');
INSERT INTO "bayan" VALUES(81,11,'81:11','اور جب آسمان کی کھال اتار لی جائے گی۔');
INSERT INTO "bayan" VALUES(81,12,'81:12','اور جب جہنم دہکائی جائے گی۔');
INSERT INTO "bayan" VALUES(81,13,'81:13','اور جب جنت قریب لے آئی جائے گی۔');
INSERT INTO "bayan" VALUES(81,14,'81:14','(اُس دن) ہر جان جان لے گی کہ اس نے کیا کچھ حاضر کیا ہے۔');
INSERT INTO "bayan" VALUES(81,15,'81:15','تو نہیں ! میں قسم کھاتا ہوں پیچھے ہٹنے والے چلنے والے');
INSERT INTO "bayan" VALUES(81,16,'81:16','اور چھپ جانے والے ستاروں کی۔');
INSERT INTO "bayan" VALUES(81,17,'81:17','قسم ہے رات کی جب وہ روانہ ہونے لگے۔');
INSERT INTO "bayan" VALUES(81,18,'81:18','اور صبح کی جب وہ سانس لے۔');
INSERT INTO "bayan" VALUES(81,19,'81:19','یقینا یہ (قرآن) ایک بہت باعزت پیغامبر کا قول ہے۔');
INSERT INTO "bayan" VALUES(81,20,'81:20','جو (جبرائیل) بہت قوت والا ہے صاحب ِعرش کے نزدیک بلند مرتبہ ہے۔');
INSERT INTO "bayan" VALUES(81,21,'81:21','جس کی اطاعت کی جاتی ہے اور وہ امانت دار بھی ہے۔');
INSERT INTO "bayan" VALUES(81,22,'81:22','اور تمہارے یہ ساتھی (محمد صلى الله عليه وسلم کوئی مجنون نہیں ہیں۔');
INSERT INTO "bayan" VALUES(81,23,'81:23','اور انہوں نے دیکھا ہے اس کو کھلے افق پر۔');
INSERT INTO "bayan" VALUES(81,24,'81:24','اور وہ غیب کے معاملے میں حریص یا بخیل نہیں ہیں۔');
INSERT INTO "bayan" VALUES(81,25,'81:25','اور یہ کسی شیطانِ مردود کا قول نہیں ہے۔');
INSERT INTO "bayan" VALUES(81,26,'81:26','تو تم کدھر چلے جا رہے ہو ؟');
INSERT INTO "bayan" VALUES(81,27,'81:27','نہیں ہے یہ مگر تمام جہان والوں کے لیے ایک یاد دہانی۔');
INSERT INTO "bayan" VALUES(81,28,'81:28','ہر اس شخص کے لیے جو تم میں سے سیدھے راستے پر چلنا چاہے۔');
INSERT INTO "bayan" VALUES(81,29,'81:29','اور تمہارے چاہے بھی کچھ نہیں ہوسکتا جب تک کہ اللہ نہ چاہے جو تمام جہانوں کا ربّ ہے۔');
INSERT INTO "bayan" VALUES(82,1,'82:1','جب آسمان پھٹ جائے گا۔');
INSERT INTO "bayan" VALUES(82,2,'82:2','اور جب تارے بکھر جائیں گے۔');
INSERT INTO "bayan" VALUES(82,3,'82:3','اور جب سمندرپھاڑ دیے جائیں گے۔');
INSERT INTO "bayan" VALUES(82,4,'82:4','اور جب قبریں تلپٹ کردی جائیں گی۔');
INSERT INTO "bayan" VALUES(82,5,'82:5','(اُس وقت) ہر جان جان لے گی کہ اس نے کیا آگے بھیجا اور کیا پیچھے چھوڑا۔');
INSERT INTO "bayan" VALUES(82,6,'82:6','اے انسان ! تجھے کس چیز نے دھوکے میں ڈال دیا ہے اپنے ربّ کریم کے بارے میں۔');
INSERT INTO "bayan" VALUES(82,7,'82:7','جس نے تمہیں تخلیق کیا پھر تمہارے نوک پلک سنوارے پھر تمہارے اندر اعتدال پیدا کیا۔');
INSERT INTO "bayan" VALUES(82,8,'82:8','پھر جس شکل میں اس نے چاہا تجھے ترکیب دے دیا۔');
INSERT INTO "bayan" VALUES(82,9,'82:9','ہرگز نہیں ! بلکہ اصل میں تم جزا و سزا کا انکار کر رہے ہو۔');
INSERT INTO "bayan" VALUES(82,10,'82:10','حالانکہ تم پر نگران (فرشتے) مقرر ہیں۔');
INSERT INTO "bayan" VALUES(82,11,'82:11','جو بڑے باعزت لکھنے والے ہیں۔');
INSERT INTO "bayan" VALUES(82,12,'82:12','وہ جانتے ہیں جو کچھ تم کر رہے ہو۔');
INSERT INTO "bayan" VALUES(82,13,'82:13','یقینا نیکوکار بندے نعمتوں میں ہوں گے۔');
INSERT INTO "bayan" VALUES(82,14,'82:14','اور یقینا فاسق و فاجر جہنم میں ہوں گے۔');
INSERT INTO "bayan" VALUES(82,15,'82:15','داخل ہوں گے اس میں جزا و سزا کے فیصلے کے دن۔');
INSERT INTO "bayan" VALUES(82,16,'82:16','اور وہ اس سے کہیں غائب نہیں ہو سکیں گے۔');
INSERT INTO "bayan" VALUES(82,17,'82:17','اور کیا تمہیں کچھ معلوم ہے کہ روز جزا کیا ہے ؟');
INSERT INTO "bayan" VALUES(82,18,'82:18','پھر کیا تمہیں کچھ اندازہ ہوا ہے کہ روزجزا کیا ہے ؟');
INSERT INTO "bayan" VALUES(82,19,'82:19','جس روز کسی جان کو کسی دوسری جان کے لیے کوئی اختیار حاصل نہیں ہوگا اور امر ُ کل کا کل اس دن اللہ ہی کے ہاتھ میں ہوگا۔');
INSERT INTO "bayan" VALUES(83,1,'83:1','ہلاکت ہے کمی کرنے والوں کے لیے۔');
INSERT INTO "bayan" VALUES(83,2,'83:2','وہ لوگ کہ جب دوسروں سے ناپ کرلیتے ہیں تو پورا لیتے ہیں۔');
INSERT INTO "bayan" VALUES(83,3,'83:3','اور جب خود انہیں ناپ کر یا تول کردیتے ہیں تو کمی کردیتے ہیں۔');
INSERT INTO "bayan" VALUES(83,4,'83:4','کیا ان کو یہ گمان نہیں کہ وہ دوبارہ اٹھائے جانے والے ہیں۔');
INSERT INTO "bayan" VALUES(83,5,'83:5','ایک بڑے دن کے لیے۔');
INSERT INTO "bayan" VALUES(83,6,'83:6','جس دن کہ لوگ کھڑے ہوں گے تمام جہانوں کے رب کے سامنے۔');
INSERT INTO "bayan" VALUES(83,7,'83:7','ہرگز نہیں ! یقینا گناہگاروں کے اعمال نامے سجین میں ہوں گے۔');
INSERT INTO "bayan" VALUES(83,8,'83:8','اور تم نے کیا سمجھا کہ سجین کیا ہے ؟');
INSERT INTO "bayan" VALUES(83,9,'83:9','لکھا ہوا دفتر۔');
INSERT INTO "bayan" VALUES(83,10,'83:10','تباہی اور ہلاکت ہے اس دن جھٹلانے والوں کے لیے۔');
INSERT INTO "bayan" VALUES(83,11,'83:11','جو جھٹلا رہے ہیں جزا و سزا کے دن کو۔');
INSERT INTO "bayan" VALUES(83,12,'83:12','اور نہیں جھٹلاتا اس دن کو مگر وہی کہ جو حد سے بڑھنے والا گناہگار ہے۔');
INSERT INTO "bayan" VALUES(83,13,'83:13','جب اسے پڑھ کر سنائی جاتی ہیں ہماری آیات تو کہتا ہے کہ یہ تو پہلے لوگوں کی کہانیاں ہیں۔');
INSERT INTO "bayan" VALUES(83,14,'83:14','نہیں ! بلکہ (اصل صورت حال یہ ہے کہ) ان کے دلوں پر زنگ آگیا ہے ان کے اعمال کی وجہ سے۔');
INSERT INTO "bayan" VALUES(83,15,'83:15','نہیں ! یقینایہ لوگ اس دن اپنے رب سے اوٹ میں رکھے جائیں گے۔');
INSERT INTO "bayan" VALUES(83,16,'83:16','پھر انہیں جھونک دیا جائے گا جہنم میں۔');
INSERT INTO "bayan" VALUES(83,17,'83:17','پھر ان سے کہا جائے گا : یہ ہے وہ چیز جس کی تم تکذیب کیا کرتے تھے !');
INSERT INTO "bayan" VALUES(83,18,'83:18','نہیں ! یقینا نیکوکاروں کے کتاب یا اعمال نامے علیین میں ہوں گے۔');
INSERT INTO "bayan" VALUES(83,19,'83:19','اور تمہیں کچھ اندازہ ہے وہ علیون کیا ہے ؟');
INSERT INTO "bayan" VALUES(83,20,'83:20','لکھا ہوا دفتر۔');
INSERT INTO "bayan" VALUES(83,21,'83:21','وہاں موجود ہوں گے ملائکہ مقربین۔');
INSERT INTO "bayan" VALUES(83,22,'83:22','یقینا نیکوکار نعمتوں میں ہوں گے۔');
INSERT INTO "bayan" VALUES(83,23,'83:23','وہ تختوں پر بیٹھے (مناظر ِجنت) دیکھ رہے ہوں گے۔');
INSERT INTO "bayan" VALUES(83,24,'83:24','تم دیکھو گے ان کے چہروں پر تروتازگی کی علامات۔');
INSERT INTO "bayan" VALUES(83,25,'83:25','انہیں پلائی جائے گی خالص شراب جس پر مہر لگی ہوگی۔');
INSERT INTO "bayan" VALUES(83,26,'83:26','اس کی مہر ہوگی مشک کی۔ اس چیز کے لیے سبقت لے جانے کی کوشش کریں سبقت لے جانے والے۔');
INSERT INTO "bayan" VALUES(83,27,'83:27','اور اس کی ملونی ہوگی تسنیم سے۔');
INSERT INTO "bayan" VALUES(83,28,'83:28','یہ ایک چشمہ ہے جس پر جام نوشی کریں گے مقربین بارگاہ۔');
INSERT INTO "bayan" VALUES(83,29,'83:29','یقینا جو مجرم تھے وہ اہل ِ ایمان پر ہنسا کرتے تھے۔');
INSERT INTO "bayan" VALUES(83,30,'83:30','اور جب یہ ان کے قریب سے گزرتے تھے تو آپس میں آنکھیں مارتے تھے۔');
INSERT INTO "bayan" VALUES(83,31,'83:31','اور جب وہ اپنے گھر والوں کی طرف لوٹتے تھے تو باتیں بناتے ہوئے لوٹتے تھے۔');
INSERT INTO "bayan" VALUES(83,32,'83:32','اور جب وہ ان (اہل ایمان) کو دیکھتے تھے تو کہتے تھے کہ یقینا یہ بہکے ہوئے لوگ ہیں۔');
INSERT INTO "bayan" VALUES(83,33,'83:33','جب کہ انہیں نہیں بھیجا گیا تھا ان پر نگران بنا کر۔');
INSERT INTO "bayan" VALUES(83,34,'83:34','تو آج (قیامت) کے دن اہل ایمان ان کفار پر ہنس رہے ہیں۔');
INSERT INTO "bayan" VALUES(83,35,'83:35','وہ تختوں پر بیٹھے (ان کا حشر) دیکھ رہے ہیں۔');
INSERT INTO "bayan" VALUES(83,36,'83:36','بدلہ مل گیا نا کافروں کو اس کا جو کچھ وہ کیا کرتے تھے !');
INSERT INTO "bayan" VALUES(84,1,'84:1','جب آسمان پھٹ جائے گا۔');
INSERT INTO "bayan" VALUES(84,2,'84:2','اور اپنے رب کے حکم کی تعمیل کرے گا اور اسے یہی زیب دیتا ہے۔');
INSERT INTO "bayan" VALUES(84,3,'84:3','اور جب زمین کو پھیلا دیا جائے گا۔');
INSERT INTO "bayan" VALUES(84,4,'84:4','اور وہ نکال باہر کرے گی جو کچھ اس کے اندر تھا اور خالی ہوجائے گی۔');
INSERT INTO "bayan" VALUES(84,5,'84:5','اور وہ بھی اپنے رب کے حکم کی تعمیل کرے گی اور اسے یہی زیب دیتا ہے۔');
INSERT INTO "bayan" VALUES(84,6,'84:6','اے انسان ! تو مشقت پر مشقت برداشت کرتے جا رہا ہے اپنے رب کی طرف پھر تو اس سے ملنے والا ہے۔');
INSERT INTO "bayan" VALUES(84,7,'84:7','تو جس کو دیا جائے گا اس کا اعمال نامہ داہنے ہاتھ میں۔');
INSERT INTO "bayan" VALUES(84,8,'84:8','تو اس سے لیا جائے گا بہت ہی آسان حساب۔');
INSERT INTO "bayan" VALUES(84,9,'84:9','اور وہ لوٹے گا اپنے گھر والوں کی طرف شاداں وفرحاں۔');
INSERT INTO "bayan" VALUES(84,10,'84:10','اور جس کو دیا جائے گا اس کا اعمالنامہ اس کی پیٹھ کے پیچھے سے۔');
INSERT INTO "bayan" VALUES(84,11,'84:11','تو اب وہ موت کی طلب کرے گا۔');
INSERT INTO "bayan" VALUES(84,12,'84:12','لیکن وہ داخل ہوگا جہنم میں۔');
INSERT INTO "bayan" VALUES(84,13,'84:13','یقینا (دنیا میں) وہ اپنے اہل و عیال میں بہت خوش و خرم تھا۔');
INSERT INTO "bayan" VALUES(84,14,'84:14','اسے گمان ہوگیا تھا کہ کبھی لوٹ کر نہیں آنا ہے۔');
INSERT INTO "bayan" VALUES(84,15,'84:15','کیوں نہیں ! یقینا اس کا رب تو اسے خوب دیکھ رہا تھا۔');
INSERT INTO "bayan" VALUES(84,16,'84:16','تو نہیں مجھے قسم ہے شام کی سرخی کی۔');
INSERT INTO "bayan" VALUES(84,17,'84:17','اور رات کی اور ان چیزوں کی جن کو وہ سمیٹے ہوئے ہے۔');
INSERT INTO "bayan" VALUES(84,18,'84:18','اور چاند کی جب وہ پورا ہوجاتا ہے۔');
INSERT INTO "bayan" VALUES(84,19,'84:19','(اسی طرح) تم لازماً چڑھو گے درجہ بدرجہ۔');
INSERT INTO "bayan" VALUES(84,20,'84:20','تو انہیں کیا ہوگیا ہے کہ یہ ایمان نہیں لاتے ؟');
INSERT INTO "bayan" VALUES(84,21,'84:21','اور جب انہیں قرآن پڑھ کر سنایا جاتا ہے تو یہ سجدہ نہیں کرتے ؟');
INSERT INTO "bayan" VALUES(84,22,'84:22','بلکہ یہ کافر تو جھٹلا رہے ہیں۔');
INSERT INTO "bayan" VALUES(84,23,'84:23','اور اللہ کو خوب معلوم ہے کہ یہ لوگ (اپنے اندر) کیا بھرے ہوئے ہیں۔');
INSERT INTO "bayan" VALUES(84,24,'84:24','تو (اے نبی صلى الله عليه وسلم !) ان کو بشارت دے دیجیے دردناک عذاب کی۔');
INSERT INTO "bayan" VALUES(84,25,'84:25','البتہ جو لوگ ایمان لائے اور جنہوں نے نیک عمل کیے ان کے لیے کبھی نہ ختم ہونے والا اجر ہے۔');
INSERT INTO "bayan" VALUES(85,1,'85:1','قسم ہے آسمان کی جو برجوں والا ہے۔');
INSERT INTO "bayan" VALUES(85,2,'85:2','اور قسم ہے اس دن کی جس کا وعدہ کیا گیا ہے۔');
INSERT INTO "bayan" VALUES(85,3,'85:3','اور قسم ہے حاضر ہونے والے کی اور اس کی جس کے پاس حاضر ہوا جائے۔');
INSERT INTO "bayan" VALUES(85,4,'85:4','ہلاک ہوگئے وہ کھائیوں والے۔');
INSERT INTO "bayan" VALUES(85,5,'85:5','وہ آگ جو بڑی ایندھن والی تھی۔');
INSERT INTO "bayan" VALUES(85,6,'85:6','جبکہ وہ اس (کے کناروں) پر بیٹھے ہوئے تھے۔');
INSERT INTO "bayan" VALUES(85,7,'85:7','اور مومنین کے ساتھ وہ جو کچھ کر رہے تھے خود اس کا نظارہ بھی کر رہے تھے۔');
INSERT INTO "bayan" VALUES(85,8,'85:8','اور وہ نہیں انتقام لے رہے تھے ان سے مگر اس لیے کہ وہ ایمان لے آئے تھے اللہ پر جو زبردست ہے اور اپنی ذات میں خود ستودہ صفات ہے۔');
INSERT INTO "bayan" VALUES(85,9,'85:9','جس کے لیے بادشاہی ہے آسمانوں کی اور زمین کی۔ اور اللہ تو ہرچیز پر خود گواہ ہے۔');
INSERT INTO "bayan" VALUES(85,10,'85:10','یقینا جن لوگوں نے ظلم و ستم توڑا مومن مردوں اور مومن عورتوں پر پھر انہوں نے توبہ بھی نہیں کی تو ان کے لیے ہوگا جہنم کا عذاب اور جلا ڈالنے والا عذاب۔');
INSERT INTO "bayan" VALUES(85,11,'85:11','یقینا وہ لوگ جو ایمان لائے اور جنہوں نے نیک عمل کیے ان کے لیے وہ باغات ہوں گے جن کے نیچے نہریں بہتی ہوں گی۔ یہ ہے اصل بڑی کامیابی۔');
INSERT INTO "bayan" VALUES(85,12,'85:12','یقینا تیرے رب کی پکڑ بڑی سخت ہے۔');
INSERT INTO "bayan" VALUES(85,13,'85:13','وہی ہے جو پہلی مرتبہ پیدا کرتا ہے اور وہی اعادہ بھی کرے گا۔');
INSERT INTO "bayan" VALUES(85,14,'85:14','اور وہ بخشنے والا بھی ہے محبت کرنے والا بھی ہے۔');
INSERT INTO "bayan" VALUES(85,15,'85:15','عرش کا مالک ہے بڑی شان والا ہے۔');
INSERT INTO "bayan" VALUES(85,16,'85:16','وہ جو ارادہ کرلے کر گزرنے والا ہے۔');
INSERT INTO "bayan" VALUES(85,17,'85:17','کیا آپ کے پاس لشکروں کی خبر پہنچی ہے ؟');
INSERT INTO "bayan" VALUES(85,18,'85:18','فرعون اور ثمود (کے لشکروں) کی ؟');
INSERT INTO "bayan" VALUES(85,19,'85:19','لیکن یہ کافر جو ہیں یہ جھٹلانے ہی میں لگے رہیں گے۔');
INSERT INTO "bayan" VALUES(85,20,'85:20','جبکہ اللہ ان کو گھیرے میں لیے ہوئے ہے۔');
INSERT INTO "bayan" VALUES(85,21,'85:21','بلکہ یہ تو قرآن ہے بہت عزت والا۔');
INSERT INTO "bayan" VALUES(85,22,'85:22','لوحِ محفوظ میں (نقش ہے)۔');
INSERT INTO "bayan" VALUES(86,1,'86:1','قسم ہے آسمان کی اور رات کو نمودار ہونے والے کی۔');
INSERT INTO "bayan" VALUES(86,2,'86:2','اور تم کیا جانتے ہو کہ وہ رات کو نمودار ہونے والا کیا ہے ؟');
INSERT INTO "bayan" VALUES(86,3,'86:3','وہ ستارہ ہے چمکدار۔');
INSERT INTO "bayan" VALUES(86,4,'86:4','کوئی جان ایسی نہیں جس پر کوئی نگہبان نہ ہو۔');
INSERT INTO "bayan" VALUES(86,5,'86:5','تو انسان کو غور کرنا چاہیے کہ وہ کس چیز سے پیدا کیا گیا ہے۔');
INSERT INTO "bayan" VALUES(86,6,'86:6','وہ پیدا کیا گیا ہے اچھلتے ہوئے پانی سے۔');
INSERT INTO "bayan" VALUES(86,7,'86:7','جو نکلتا ہے پیٹھ اور پسلیوں کے درمیان سے۔');
INSERT INTO "bayan" VALUES(86,8,'86:8','یقینا وہ اسے لوٹانے پر بھی قادر ہے۔');
INSERT INTO "bayan" VALUES(86,9,'86:9','جس دن تمام چھپے ہوئے رازوں کی جانچ پڑتال ہوگی۔');
INSERT INTO "bayan" VALUES(86,10,'86:10','تو نہیں ہوگی کسی انسان کے لیے کوئی طاقت اور نہ ہوگا اس کا کوئی مددگار۔');
INSERT INTO "bayan" VALUES(86,11,'86:11','قسم ہے اس آسمان کی جو بارش برساتا ہے وقفے وقفے سے۔');
INSERT INTO "bayan" VALUES(86,12,'86:12','اور قسم ہے اس زمین کی جو پھوٹ پڑتی ہے۔');
INSERT INTO "bayan" VALUES(86,13,'86:13','یہ (قرآن) قولِ فیصل ہے۔');
INSERT INTO "bayan" VALUES(86,14,'86:14','اور یہ کوئی ہنسی مذاق نہیں ہے۔');
INSERT INTO "bayan" VALUES(86,15,'86:15','(اے نبی صلى الله عليه وسلم !) یہ لوگ اپنی سی چالیں چل رہے ہیں');
INSERT INTO "bayan" VALUES(86,16,'86:16','اور میں بھی اپنی چال چل رہا ہوں۔');
INSERT INTO "bayan" VALUES(86,17,'86:17','تو آپ ان کافروں کو ذرا مہلت دے دیجیے تھوڑی دیر انہیں (ان کے حال پر) چھوڑ دیجیے !');
INSERT INTO "bayan" VALUES(87,1,'87:1','پاکی بیان کرو اپنے رب کے نام کی جو بہت بلند وبالا ہے۔');
INSERT INTO "bayan" VALUES(87,2,'87:2','جس نے (ہر چیز کو) پیدا کیا پھر تناسب قائم کیا۔');
INSERT INTO "bayan" VALUES(87,3,'87:3','اور جس نے (ہر شے کا) اندازہ مقرر کیا پھر اسے (فطری) ہدایت عطا فرمائی۔');
INSERT INTO "bayan" VALUES(87,4,'87:4','اور جس نے (زمین سے) چارہ نکالا۔');
INSERT INTO "bayan" VALUES(87,5,'87:5','پھر اس کو کردیا سیاہ چورا۔');
INSERT INTO "bayan" VALUES(87,6,'87:6','(اے نبی صلى الله عليه وسلم !) ہم آپ کو پڑھا دیں گے پھر آپ بھولیں گے نہیں۔');
INSERT INTO "bayan" VALUES(87,7,'87:7','سوائے اس کے کہ جو اللہ چاہے۔ یقینا وہ جانتا ہے اونچی آواز میں کہی گئی بات کو بھی اور جو مخفی رکھی جائے اسے بھی۔');
INSERT INTO "bayan" VALUES(87,8,'87:8','اور ہم رفتہ رفتہ پہنچائیں گے آپ کو آسانی تک۔');
INSERT INTO "bayan" VALUES(87,9,'87:9','تو آپ صلى الله عليه وسلم یاد دہانی کراتے رہیے اگر یاد دہانی فائدہ دے۔');
INSERT INTO "bayan" VALUES(87,10,'87:10','وہ نصیحت حاصل کرلے گا جو ڈرتا ہے۔');
INSERT INTO "bayan" VALUES(87,11,'87:11','البتہ جو شقی (بدبخت) ہے وہ اس سے پہلوتہی کرے گا۔');
INSERT INTO "bayan" VALUES(87,12,'87:12','جو (آخر کار) داخل ہوگا بڑی آگ میں۔');
INSERT INTO "bayan" VALUES(87,13,'87:13','پھر نہ اس میں وہ مرے گا نہ زندہ رہے گا۔');
INSERT INTO "bayan" VALUES(87,14,'87:14','یقینا وہ کامیاب ہوگیا جس نے خود کو پاک کرلیا۔');
INSERT INTO "bayan" VALUES(87,15,'87:15','اور اس نے اپنے رب کا نام لیا اور نماز پڑھی۔');
INSERT INTO "bayan" VALUES(87,16,'87:16','بلکہ تم دنیا کی زندگی کو ترجیح دیتے ہو۔');
INSERT INTO "bayan" VALUES(87,17,'87:17','جبکہ آخرت بہتر بھی ہے اور باقی رہنے والی بھی۔');
INSERT INTO "bayan" VALUES(87,18,'87:18','یقینا یہی بات اگلے صحیفوں میں بھی۔');
INSERT INTO "bayan" VALUES(87,19,'87:19','(یعنی) ابراہیم اور موسیٰ (e) کے صحیفوں میں۔');
INSERT INTO "bayan" VALUES(88,1,'88:1','کیا پہنچ چکی ہے آپ صلى الله عليه وسلم کے پاس اس ڈھانپ لینے والی (آفت) کی خبر ؟');
INSERT INTO "bayan" VALUES(88,2,'88:2','بہت سے چہرے اس دن ذلیل ہوں گے۔');
INSERT INTO "bayan" VALUES(88,3,'88:3','مشقت زدہ تھکے ماندے۔');
INSERT INTO "bayan" VALUES(88,4,'88:4','وہ داخل ہوں گے دہکتی ہوئی آگ میں۔');
INSERT INTO "bayan" VALUES(88,5,'88:5','انہیں پلایا جائے گا پانی ایک کھولتے ہوئے چشمے سے۔');
INSERT INTO "bayan" VALUES(88,6,'88:6','نہیں ہوگا ان کے لیے کھانے کو کچھ بھی سوائے خاردار جھاڑیوں کے۔');
INSERT INTO "bayan" VALUES(88,7,'88:7','جو نہ تو موٹا کرے اور نہ ہی بھوک مٹائے۔');
INSERT INTO "bayan" VALUES(88,8,'88:8','بہت سے چہرے اس روز تروتازہ ہوں گے۔');
INSERT INTO "bayan" VALUES(88,9,'88:9','وہ اپنی کوشش (کے نتائج) پر راضی ہوں گے۔');
INSERT INTO "bayan" VALUES(88,10,'88:10','(وہ ہوں گے) عالی مقام جنت میں۔');
INSERT INTO "bayan" VALUES(88,11,'88:11','وہ اس میں کوئی لغو بات نہیں سنیں گے۔');
INSERT INTO "bayan" VALUES(88,12,'88:12','اس میں ایک چشمہ ہے بہتا ہوا۔');
INSERT INTO "bayan" VALUES(88,13,'88:13','اس میں اونچے اونچے تخت ہیں۔');
INSERT INTO "bayan" VALUES(88,14,'88:14','اور جام قرینے سے رکھے ہوئے۔');
INSERT INTO "bayan" VALUES(88,15,'88:15','اور قالین بچھے ہوئے صف در صف۔');
INSERT INTO "bayan" VALUES(88,16,'88:16','اور مخمل کے نہالچے جگہ جگہ پھیلے ہوئے۔');
INSERT INTO "bayan" VALUES(88,17,'88:17','تو کیا یہ لوگ دیکھتے نہیں اونٹوں کو کہ انہیں کیسے بنایا گیا ہے !');
INSERT INTO "bayan" VALUES(88,18,'88:18','اور (کیا یہ دیکھتے نہیں) آسمان کو کہ کیسے بلند کیا گیا ہے !');
INSERT INTO "bayan" VALUES(88,19,'88:19','اور (کیا یہ دیکھتے نہیں) پہاڑوں کو کہ کیسے گاڑ دیے گئے ہیں !');
INSERT INTO "bayan" VALUES(88,20,'88:20','اور (کیا یہ دیکھتے نہیں) زمین کی طرف کہ کیسے بچھا دی گئی ہے !');
INSERT INTO "bayan" VALUES(88,21,'88:21','تو (اے نبی صلى الله عليه وسلم !) آپ یاد دہانی کراتے رہیے آپ تو بس یاد دہانی کرانے والے ہیں۔');
INSERT INTO "bayan" VALUES(88,22,'88:22','آپ ان پر کوئی داروغہ نہیں ہیں۔');
INSERT INTO "bayan" VALUES(88,23,'88:23','مگر جس نے منہ موڑا اور کفر کیا۔');
INSERT INTO "bayan" VALUES(88,24,'88:24','تو اس کو اللہ عذاب دے گا سب سے بڑا عذاب۔');
INSERT INTO "bayan" VALUES(88,25,'88:25','(اے نبی صلى الله عليه وسلم !) ان سب کو ہماری ہی طرف لوٹ کر آنا ہے۔');
INSERT INTO "bayan" VALUES(88,26,'88:26','پھر ان کا حساب ہمارے ذمہ ہے۔');
INSERT INTO "bayan" VALUES(89,1,'89:1','قسم ہے فجر کی۔');
INSERT INTO "bayan" VALUES(89,2,'89:2','اور قسم ہے دس راتوں کی۔');
INSERT INTO "bayan" VALUES(89,3,'89:3','اور قسم ہے ُ جفت کی اور طاق کی۔');
INSERT INTO "bayan" VALUES(89,4,'89:4','اور قسم ہے رات کی جب وہ گزرنے لگے۔');
INSERT INTO "bayan" VALUES(89,5,'89:5','کیا اس میں کوئی قسم (دلیل) ہے ان لوگوں کے لیے جو عقل مند ہیں ؟');
INSERT INTO "bayan" VALUES(89,6,'89:6','کیا تم نے دیکھا نہیں کیا کیا تھا آپ کے رب نے عاد کے ساتھ ؟');
INSERT INTO "bayan" VALUES(89,7,'89:7','وہ ارم جو ستونوں والے تھے۔');
INSERT INTO "bayan" VALUES(89,8,'89:8','جن کے مانند نہیں پیدا کیے گئے (دنیا کے) ملکوں میں۔');
INSERT INTO "bayan" VALUES(89,9,'89:9','اور ثمود کے ساتھ (کیا کیا آپ کے رب نے) جنہوں نے وادی میں چٹانوں کو تراشا تھا۔');
INSERT INTO "bayan" VALUES(89,10,'89:10','اور فرعون کے ساتھ (کیا کیا) جو میخوں والا تھا۔');
INSERT INTO "bayan" VALUES(89,11,'89:11','جنہوں نے (اپنے اپنے) ملکوں میں سرکشی اختیار کی تھی۔');
INSERT INTO "bayan" VALUES(89,12,'89:12','سو انہوں نے ان میں بکثرت فساد پھیلا دیا تھا۔');
INSERT INTO "bayan" VALUES(89,13,'89:13','تو دے مارا ان کے اوپر آپ کے رب نے عذاب کا کوڑا۔');
INSERT INTO "bayan" VALUES(89,14,'89:14','بیشک آپ کا رب تو (سرکشوں اور مفسدوں کی) تاک میں ہے۔');
INSERT INTO "bayan" VALUES(89,15,'89:15','انسان کا معاملہ یہ ہے کہ جب اس کا ربّ اسے آزماتا ہے پھر اسے عزت دیتا ہے اور نعمتیں عطا کرتا ہے تو وہ کہتا ہے میرے رب نے مجھے عزت دی !');
INSERT INTO "bayan" VALUES(89,16,'89:16','اور جب وہ اسے آزماتا ہے پھر اس کا رزق اس پر تنگ کردیتا ہے تو وہ کہتا ہے میرے رب نے مجھے ذلیل کردیا !');
INSERT INTO "bayan" VALUES(89,17,'89:17','ایسا ہرگز نہیں ہے ! ایسا ہرگز نہیں بلکہ تم لوگ یتیم کی عزت نہیں کرتے۔');
INSERT INTO "bayan" VALUES(89,18,'89:18','اور نہ ہی تم لوگ آپس میں مسکینوں کو کھلانے کی ترغیب دیتے ہو۔');
INSERT INTO "bayan" VALUES(89,19,'89:19','اور تم ساری کی ساری میراث سمیٹ کر کھا جاتے ہو۔');
INSERT INTO "bayan" VALUES(89,20,'89:20','اور تم مال سے ٹوٹ کر محبت کرتے ہو۔');
INSERT INTO "bayan" VALUES(89,21,'89:21','ہرگز نہیں ! جب زمین کو کوٹ کوٹ کر ہموار کردیا جائے گا۔');
INSERT INTO "bayan" VALUES(89,22,'89:22','اور آپ کا رب جلوہ فرما ہوگا جب کہ فرشتے قطار در قطار حاضر ہوں گے۔');
INSERT INTO "bayan" VALUES(89,23,'89:23','اور لے آئی جائے گی اس روز جہنم بھی اس دن انسان کو سمجھ آئے گی لیکن اب سمجھنے کا کیا فائدہ !');
INSERT INTO "bayan" VALUES(89,24,'89:24','وہ کہے گا : اے کاش میں نے اپنی زندگی کے لیے کچھ آگے بھیجا ہوتا !');
INSERT INTO "bayan" VALUES(89,25,'89:25','تو اس دن اللہ جو عذاب دے گا ویسا عذاب کوئی نہیں دے سکتا۔');
INSERT INTO "bayan" VALUES(89,26,'89:26','اور اس کا سا باندھنا کوئی اور نہیں باندھ سکتا۔');
INSERT INTO "bayan" VALUES(89,27,'89:27','اے نفس ِمطمئنہ ! ّ');
INSERT INTO "bayan" VALUES(89,28,'89:28','اب لوٹ جائو اپنے رب کی طرف اس حال میں کہ تم اس سے راضی وہ تم سے راضی۔');
INSERT INTO "bayan" VALUES(89,29,'89:29','تو داخل ہو جائو میرے (نیک) بندوں میں۔');
INSERT INTO "bayan" VALUES(89,30,'89:30','اور داخل ہو جائو میری جنت میں !');
INSERT INTO "bayan" VALUES(90,1,'90:1','نہیں ! میں قسم کھاتا ہوں اس شہر کی۔');
INSERT INTO "bayan" VALUES(90,2,'90:2','اور (اے نبی صلى الله عليه وسلم !) آپ حلال کرلیے گئے ہیں اس شہر میں۔');
INSERT INTO "bayan" VALUES(90,3,'90:3','اور قسم ہے والد کی اور اولاد کی۔');
INSERT INTO "bayan" VALUES(90,4,'90:4','بیشک ہم نے انسان کو پیدا ہی محنت اور مشقت میں کیا ہے۔');
INSERT INTO "bayan" VALUES(90,5,'90:5','کیا یہ سمجھتا ہے کہ اس کے اوپر کوئی قابو نہیں پاسکے گا ؟');
INSERT INTO "bayan" VALUES(90,6,'90:6','کہتا ہے میں نے تو ڈھیروں مال خرچ کر ڈالا۔');
INSERT INTO "bayan" VALUES(90,7,'90:7','کیا اس کا گمان ہے کہ اسے کسی نے دیکھا نہیں ؟');
INSERT INTO "bayan" VALUES(90,8,'90:8','کیا ہم نے اس کو دو آنکھیں نہیں دیں ؟');
INSERT INTO "bayan" VALUES(90,9,'90:9','اور ایک زبان اور دو ہونٹ (نہیں دیے) ؟');
INSERT INTO "bayan" VALUES(90,10,'90:10','اور ہم نے اس کو راہ دکھلا دی دو گھاٹیوں کی۔');
INSERT INTO "bayan" VALUES(90,11,'90:11','لیکن وہ گھاٹی کو عبور نہ کرسکا۔');
INSERT INTO "bayan" VALUES(90,12,'90:12','اور تمہیں کیا معلوم کہ وہ گھاٹی کون سی ہے ؟');
INSERT INTO "bayan" VALUES(90,13,'90:13','کسی گردن کا چھڑا دینا۔');
INSERT INTO "bayan" VALUES(90,14,'90:14','یا کھانا کھلا دینا بھوک کے دن میں۔');
INSERT INTO "bayan" VALUES(90,15,'90:15','اس یتیم کو جو قرابت دار بھی ہے۔');
INSERT INTO "bayan" VALUES(90,16,'90:16','یا اس محتاج کو جو مٹی میں رُل رہا ہے۔');
INSERT INTO "bayan" VALUES(90,17,'90:17','پھر وہ شامل ہو ان لوگوں میں جو ایمان لائے اور جنہوں نے باہم ایک دوسرے کو صبر کی تلقین کی اور باہم ایک دوسرے کو ہمدردی کی نصیحت کی۔');
INSERT INTO "bayan" VALUES(90,18,'90:18','یہ ہوں گے داہنے والے۔');
INSERT INTO "bayan" VALUES(90,19,'90:19','اور جنہوں نے انکار کیا ہماری آیات کا وہ ہوں گے بائیں والے۔');
INSERT INTO "bayan" VALUES(90,20,'90:20','ان پر آگ بند کردی جائے گی۔');
INSERT INTO "bayan" VALUES(91,1,'91:1','قسم ہے سورج کی اور اس کی دھوپ کی۔');
INSERT INTO "bayan" VALUES(91,2,'91:2','اور قسم ہے چاند کی جبکہ وہ اس کے پیچھے آتا ہے۔');
INSERT INTO "bayan" VALUES(91,3,'91:3','اور قسم ہے دن کی جب وہ اس (سورج) کو روشن کردیتا ہے۔');
INSERT INTO "bayan" VALUES(91,4,'91:4','اور قسم ہے رات کی جب وہ اس (سورج) کو ڈھانپ لیتی ہے۔');
INSERT INTO "bayan" VALUES(91,5,'91:5','اور قسم ہے آسمان کی اور جیسا کہ اسے بنایا۔');
INSERT INTO "bayan" VALUES(91,6,'91:6','اور قسم ہے زمین کی اور جیسا کہ اسے بچھا دیا۔');
INSERT INTO "bayan" VALUES(91,7,'91:7','اور قسم ہے نفس انسانی کی اور جیسا کہ اس کو سنوارا۔');
INSERT INTO "bayan" VALUES(91,8,'91:8','پس اس کے اندر نیکی اور بدی کا علم الہام کردیا۔');
INSERT INTO "bayan" VALUES(91,9,'91:9','یقینا کامیاب ہوگیا جس نے اس (نفس) کو پاک کرلیا۔');
INSERT INTO "bayan" VALUES(91,10,'91:10','اور ناکام ہوگیا جس نے اسے مٹی میں دفن کردیا۔');
INSERT INTO "bayan" VALUES(91,11,'91:11','قومِ ثمود نے بھی جھٹلایا تھا اپنی سرکشی کے باعث۔');
INSERT INTO "bayan" VALUES(91,12,'91:12','جب اٹھ کھڑا ہوا ان کا سب سے شقی انسان۔ م');
INSERT INTO "bayan" VALUES(91,13,'91:13','تو اللہ کے رسول نے ان سے کہا کہ (خبردار !) یہ اللہ کی اونٹنی ہے اور یہ اس کے پانی پینے کا دن ہے۔');
INSERT INTO "bayan" VALUES(91,14,'91:14','تو انہوں نے اس کو جھٹلا دیا اور اونٹنی کی کونچیں کاٹ دیں۔ تو الٹ دیا ان پر عذاب ان کے رب نے ان کے گناہ کی پاداش میں اور سب کو برابر کردیا۔');
INSERT INTO "bayan" VALUES(91,15,'91:15','اور وہ اس کے انجام سے نہیں ڈرتا۔');
INSERT INTO "bayan" VALUES(92,1,'92:1','قسم ہے رات کی جب وہ ڈھانپ لیتی ہے۔');
INSERT INTO "bayan" VALUES(92,2,'92:2','q اور قسم ہے دن کی جب وہ روشن ہوجاتا ہے۔');
INSERT INTO "bayan" VALUES(92,3,'92:3','اور (قسم ہے) اس کی جو اس نے پیدا کیے نر اور مادہ۔');
INSERT INTO "bayan" VALUES(92,4,'92:4','بیشک تمہاری کوشش الگ الگ ہے۔');
INSERT INTO "bayan" VALUES(92,5,'92:5','تو جس نے عطا کیا اور تقویٰ اختیار کیا۔');
INSERT INTO "bayan" VALUES(92,6,'92:6','اور اس نے تصدیق کی اچھی بات کی۔');
INSERT INTO "bayan" VALUES(92,7,'92:7','تو اس کو ہم رفتہ رفتہ آسان منزل (جنت) تک پہنچادیں گے۔');
INSERT INTO "bayan" VALUES(92,8,'92:8','اور جس نے بخل کیا اور بےپروائی اختیار کی۔');
INSERT INTO "bayan" VALUES(92,9,'92:9','اور جھٹلا دیا اچھی بات کو۔');
INSERT INTO "bayan" VALUES(92,10,'92:10','تو اس کو ہم رفتہ رفتہ مشکل منزل (جہنم) تک پہنچا دیں گے۔');
INSERT INTO "bayan" VALUES(92,11,'92:11','اور اس کا مال اس کے کچھ کام نہیں آئے گا جب وہ (جہنم کے) گڑھے میں گرے گا۔');
INSERT INTO "bayan" VALUES(92,12,'92:12','(دیکھو انسانو !) یقینا ہمارے ذمے ہے ہدایت پہنچا دینا۔');
INSERT INTO "bayan" VALUES(92,13,'92:13','اور ہمارے ہی لیے ہے اختیار آخرت کا بھی اور دنیا کا بھی۔');
INSERT INTO "bayan" VALUES(92,14,'92:14','دیکھو ! میں نے تمہیں خبردار کردیا ہے بھڑکتی ہوئی آگ سے۔');
INSERT INTO "bayan" VALUES(92,15,'92:15','نہیں پڑے گا اس میں مگر وہ جو انتہائی بدبخت ہے۔');
INSERT INTO "bayan" VALUES(92,16,'92:16','جس نے جھٹلایا اور منہ پھیرلیا۔');
INSERT INTO "bayan" VALUES(92,17,'92:17','اور بچالیا جائے گا اس سے جو انتہائی متقی ہے۔');
INSERT INTO "bayan" VALUES(92,18,'92:18','جو اپنا مال دیتا ہے اپنے (نفس) کو پاک کرنے کے لیے۔');
INSERT INTO "bayan" VALUES(92,19,'92:19','اور اس کے ذمے کسی کا احسان نہیں جس کا وہ بدلہ چکا رہا ہو۔');
INSERT INTO "bayan" VALUES(92,20,'92:20','بلکہ (وہ تو خرچ کر رہا ہے) صرف اپنے بلندو برتر پروردگار کی رضاجوئی کے لیے۔');
INSERT INTO "bayan" VALUES(92,21,'92:21','اور وہ عنقریب راضی ہوجائے گا۔');
INSERT INTO "bayan" VALUES(93,1,'93:1','قسم ہے دھوپ چڑھنے کے وقت کی۔');
INSERT INTO "bayan" VALUES(93,2,'93:2','قسم ہے رات کی جبکہ وہ سکون کے ساتھ چھا جائے۔');
INSERT INTO "bayan" VALUES(93,3,'93:3','آپ صلى الله عليه وسلم کے رب نے آپ کو رخصت نہیں کیا اور نہ ہی وہ آپ صلى الله عليه وسلم سے ناراض ہوا ہے۔');
INSERT INTO "bayan" VALUES(93,4,'93:4','اور یقینا بعد کا وقت آپ کے لیے بہتر ہوگا پہلے سے۔');
INSERT INTO "bayan" VALUES(93,5,'93:5','اور عنقریب آپ صلى الله عليه وسلم کا رب آپ صلى الله عليه وسلم کو اتنا کچھ عطا فرمائے گا کہ آپ صلى الله عليه وسلم راضی ہوجائیں گے۔');
INSERT INTO "bayan" VALUES(93,6,'93:6','کیا اس نے نہیں پایا آپ کو یتیم پھر پناہ دی !');
INSERT INTO "bayan" VALUES(93,7,'93:7','اور آپ صلى الله عليه وسلم کو تلاش حقیقت میں سرگرداں پایا تو ہدایت دی !');
INSERT INTO "bayan" VALUES(93,8,'93:8','اور اس نے آپ صلى الله عليه وسلم کو تنگ دست پایا تو غنی کردیا !');
INSERT INTO "bayan" VALUES(93,9,'93:9','تو آپ صلى الله عليه وسلم کسی یتیم پر سختی نہ کریں۔');
INSERT INTO "bayan" VALUES(93,10,'93:10','اور آپ صلى الله عليه وسلم کسی سائل کو نہ جھڑکیں۔');
INSERT INTO "bayan" VALUES(93,11,'93:11','اور اپنے رب کی نعمت کا بیان کریں۔');
INSERT INTO "bayan" VALUES(94,1,'94:1','کیا ہم نے آپ صلى الله عليه وسلم کے لیے آپ صلى الله عليه وسلم کے سینے کو کھول نہیں دیا ؟');
INSERT INTO "bayan" VALUES(94,2,'94:2','اور ہم نے اتار نہیں دیا آپ صلى الله عليه وسلم سے آپ صلى الله عليه وسلم کا وہ بوجھ ؟');
INSERT INTO "bayan" VALUES(94,3,'94:3','جو آپ کی کمر کو توڑے دے رہا تھا !');
INSERT INTO "bayan" VALUES(94,4,'94:4','اور ہم نے آپ صلى الله عليه وسلم کے ذکر کو بلند کردیا ہے۔ (1)');
INSERT INTO "bayan" VALUES(94,5,'94:5','تو یقینا مشکل ہی کے ساتھ آسانی ہے۔');
INSERT INTO "bayan" VALUES(94,6,'94:6','یقینا مشکل ہی کے ساتھ آسانی ہے۔');
INSERT INTO "bayan" VALUES(94,7,'94:7','پھر جب آپ صلى الله عليه وسلم (فرائض نبوت سے) فارغ ہوجائیں تو اسی کام میں لگ جایئے۔');
INSERT INTO "bayan" VALUES(94,8,'94:8','اور اپنے رب کی طرف راغب ہوجایئے۔');
INSERT INTO "bayan" VALUES(95,1,'95:1','گواہ ہے انجیر اور گواہ ہے زیتون۔');
INSERT INTO "bayan" VALUES(95,2,'95:2','اور گواہ ہے طور سینا۔');
INSERT INTO "bayan" VALUES(95,3,'95:3','اور گواہ ہے یہ امن والا شہر۔');
INSERT INTO "bayan" VALUES(95,4,'95:4','ہم نے انسان کو بہترین ساخت پر پیدا کیا۔');
INSERT INTO "bayan" VALUES(95,5,'95:5','پھر ہم نے لوٹا دیا اس کو پست ترین حالت کی طرف۔');
INSERT INTO "bayan" VALUES(95,6,'95:6','سوائے ان لوگوں کے جو ایمان لائے اور انہوں نے نیک اعمال کیے تو ان کے لیے ایسا اجر ہوگا جس کا سلسلہ کبھی منقطع نہیں ہوگا۔');
INSERT INTO "bayan" VALUES(95,7,'95:7','تو اس کے بعد کیا چیز تجھے آمادہ کرتی ہے جزا و سزا کے انکار پر ؟');
INSERT INTO "bayan" VALUES(95,8,'95:8','کیا اللہ تمام حاکموں سے بڑا حاکم نہیں ہے ؟');
INSERT INTO "bayan" VALUES(96,1,'96:1','پڑھیے اپنے اس رب کے نام سے جس نے پیدا کیا۔');
INSERT INTO "bayan" VALUES(96,2,'96:2','انسان کو پیدا کیا ہے اس جونک کی طرح کی چیز سے جو رحم مادر میں چمٹ گئی تھی۔');
INSERT INTO "bayan" VALUES(96,3,'96:3','پڑھیے اور آپ کا ربّ بہت کریم ہے۔');
INSERT INTO "bayan" VALUES(96,4,'96:4','جس نے تعلیم دی ہے قلم کے ساتھ۔');
INSERT INTO "bayan" VALUES(96,5,'96:5','اور انسان کو وہ کچھ سکھایا ہے جو وہ نہیں جانتا تھا۔');
INSERT INTO "bayan" VALUES(96,6,'96:6','کوئی نہیں ! انسان سرکشی پر آمادہ ہو ہی جاتا ہے۔');
INSERT INTO "bayan" VALUES(96,7,'96:7','اس بنا پر کہ وہ اپنے آپ کو مستغنی دیکھتا ہے۔');
INSERT INTO "bayan" VALUES(96,8,'96:8','یقینا تجھے اپنے رب کی طرف لوٹ کر جانا ہے۔');
INSERT INTO "bayan" VALUES(96,9,'96:9','کیا تم نے دیکھا اس شخص کو جو روکتا ہے۔');
INSERT INTO "bayan" VALUES(96,10,'96:10','(ہمارے) ایک بندے کو جب وہ نماز پڑھتا ہے۔');
INSERT INTO "bayan" VALUES(96,11,'96:11','کیا تم نے غور کیا اگر وہ شخص ہدایت پر ہوتا ؟');
INSERT INTO "bayan" VALUES(96,12,'96:12','یا وہ تقویٰ کی تعلیم دیتا !');
INSERT INTO "bayan" VALUES(96,13,'96:13','کیا تم نے سوچا کہ اس نے جو جھٹلایا ہے اور منہ موڑ لیا ہے۔');
INSERT INTO "bayan" VALUES(96,14,'96:14','کیا یہ جانتا نہیں کہ اللہ دیکھ رہا ہے !');
INSERT INTO "bayan" VALUES(96,15,'96:15','ہرگز نہیں ! اگر یہ باز نہ آیا تو ہم گھسیٹیں گے اس کی پیشانی کے بال پکڑ کر۔');
INSERT INTO "bayan" VALUES(96,16,'96:16','وہ پیشانی کہ جو خطاکار ہے جھوٹی ہے۔');
INSERT INTO "bayan" VALUES(96,17,'96:17','تو وہ بلا لے اپنی مجلس کے لوگوں کو۔');
INSERT INTO "bayan" VALUES(96,18,'96:18','ہم بھی بلا لیں گے جہنم کے فرشتوں کو۔');
INSERT INTO "bayan" VALUES(96,19,'96:19','کوئی بات نہیں ! (اے نبی صلى الله عليه وسلم آپ اس کی بات نہ مانیے آپ سجدہ کیجیے اور (اللہ سے اور) قریب ہوجایئے !');
INSERT INTO "bayan" VALUES(97,1,'97:1','یقینا ہم نے اتارا ہے اس (قرآن) کو لیلۃ القدر میں۔');
INSERT INTO "bayan" VALUES(97,2,'97:2','اور تم کیا جانتے ہو کہ لیلۃ القدر کیا ہے !');
INSERT INTO "bayan" VALUES(97,3,'97:3','لیلۃ القدر ہزار مہینوں سے بہتر ہے۔');
INSERT INTO "bayan" VALUES(97,4,'97:4','(اس رات میں) اُترتے ہیں فرشتے اور روح اپنے رب کے حکم سے ہر کام کے لیے۔');
INSERT INTO "bayan" VALUES(97,5,'97:5','سراسر سلامتی ہے۔ یہ (رات) رہتی ہے طلوع فجر تک۔');
INSERT INTO "bayan" VALUES(98,1,'98:1','نہیں تھے وہ لوگ جنہوں نے کفر کیا تھا اہل کتاب میں سے اور مشرکین میں سے الگ ہونے والے (یا باز آنے والے) جب تک کہ ان کے پاس البیّنہنہ آجاتی۔');
INSERT INTO "bayan" VALUES(98,2,'98:2','ایک رسول صلى الله عليه وسلم اللہ کی جانب سے جو تلاوت کرتے ہیں پاکیزہ اوراق کی۔');
INSERT INTO "bayan" VALUES(98,3,'98:3','ان میں بڑے مضبوط احکام (تحریر) ہیں۔');
INSERT INTO "bayan" VALUES(98,4,'98:4','اور جن لوگوں کو کتاب دی گئی تھی انہوں نے تفرقہ نہیں کیا تھا مگر اس کے بعد جبکہ ان کے پاس البیِّنہ آچکی تھی۔');
INSERT INTO "bayan" VALUES(98,5,'98:5','اور انہیں حکم نہیں ہوا تھا مگر یہ کہ وہ بندگی کریں اللہ کی اپنی اطاعت کو اس کے لیے خالص کرتے ہوئے بالکل یکسو ہو کر اور (یہ کہ) نماز قائم کریں اور زکوٰۃ ادا کریں اور یہی ہے سیدھا (اور سچا) دین۔');
INSERT INTO "bayan" VALUES(98,6,'98:6','بیشک وہ لوگ جنہوں نے کفر کی روش اختیار کی خواہ وہ اہل کتاب میں سے تھے خواہ مشرکین میں سے وہ ہوں گے جہنم کی آگ میں اس میں ہمیشہ رہیں گے۔ یہی لوگ بدترین خلائق ہیں۔');
INSERT INTO "bayan" VALUES(98,7,'98:7','(اس کے برعکس) وہ لوگ جو ایمان لائے اور جنہوں نے نیک عمل کیے یہی بہترین خلائق ہیں۔');
INSERT INTO "bayan" VALUES(98,8,'98:8','ان کا بدلہ ہوگا ان کے رب کے پاس دائمی قیام کے باغات کی صورت میں جن کے دامن میں ندیاں بہتی ہوں گی ان میں وہ رہیں گے ہمیشہ ہمیش۔');
INSERT INTO "bayan" VALUES(99,1,'99:1','جب زمین ہلائی جائے گی جیسے کہ ہلائی جائے گی۔');
INSERT INTO "bayan" VALUES(99,2,'99:2','اور زمین اپنے سارے بوجھ نکال کر باہر پھینک دے گی۔');
INSERT INTO "bayan" VALUES(99,3,'99:3','اور انسان کہے گا کہ اسے کیا ہوگیا ہے ؟');
INSERT INTO "bayan" VALUES(99,4,'99:4','اس دن یہ اپنی خبریں کہہ سنائے گی۔');
INSERT INTO "bayan" VALUES(99,5,'99:5','اس لیے کہ اسے اس کے رب نے حکم دیا ہوگا۔');
INSERT INTO "bayan" VALUES(99,6,'99:6','اس دن لوگ علیحدہ علیحدہ ہو کر نکل پڑیں گے تاکہ انہیں ان کے اعمال دکھا دیے جائیں۔');
INSERT INTO "bayan" VALUES(99,7,'99:7','توجس کسی نے ذرّہ کے ہم وزن بھی کوئی نیکی کی ہوگی وہ اسے دیکھ لے گا۔');
INSERT INTO "bayan" VALUES(99,8,'99:8','اور جس کسی نے ذرّہ کے ہم وزن کوئی بدی کی ہوگی وہ بھی اسے دیکھ لے گا۔');
INSERT INTO "bayan" VALUES(100,1,'100:1','قسم ہے ان گھوڑوں کی جو دوڑتے ہیں ہانپتے ہوئے۔');
INSERT INTO "bayan" VALUES(100,2,'100:2','پھر وہ ُ سم مار کر چنگاریاں نکالتے ہیں۔');
INSERT INTO "bayan" VALUES(100,3,'100:3','پھر وہ علی الصبح غارت گری کرتے ہیں۔');
INSERT INTO "bayan" VALUES(100,4,'100:4','پھر وہ اس سے گرد اڑاتے ہوئے جاتے ہیں۔');
INSERT INTO "bayan" VALUES(100,5,'100:5','پھر اس کے ساتھ وہ (دشمن کی) جمعیت کے اندر گھس جاتے ہیں۔');
INSERT INTO "bayan" VALUES(100,6,'100:6','یقیناانسان اپنے رب کا بہت ہی ناشکرا ہے۔');
INSERT INTO "bayan" VALUES(100,7,'100:7','اور وہ خود اس پر گواہ ہے۔');
INSERT INTO "bayan" VALUES(100,8,'100:8','اور وہ مال و دولت کی محبت میں بہت شدید ہے۔');
INSERT INTO "bayan" VALUES(100,9,'100:9','تو کیا وہ اس وقت کو نہیں جانتا جب نکال لیا جائے گا وہ سب کچھ جو قبروں میں ہے۔');
INSERT INTO "bayan" VALUES(100,10,'100:10','اور ظاہر کردیا جائے گا جو کچھ سینوں میں ہے۔');
INSERT INTO "bayan" VALUES(100,11,'100:11','یقینا ان کا رب اس دن ان سے پوری طرح باخبر ہوگا۔');
INSERT INTO "bayan" VALUES(101,1,'101:1','وہ کھٹکھٹانے والی۔');
INSERT INTO "bayan" VALUES(101,2,'101:2','کیا ہے وہ کھٹکھٹانے والی !');
INSERT INTO "bayan" VALUES(101,3,'101:3','اور تم کیا سمجھے کہ کیا ہے وہ کھٹکھٹانے والی !');
INSERT INTO "bayan" VALUES(101,4,'101:4','جس دن لوگ بکھرے ہوئے پتنگوں کی مانند ہوں گے۔');
INSERT INTO "bayan" VALUES(101,5,'101:5','اور پہاڑ دھنی ہوئی اون کی مانند ہوجائیں گے۔');
INSERT INTO "bayan" VALUES(101,6,'101:6','تو جن لوگوں کے (نیکیوں کے) پلڑے بھاری ہوں گے۔');
INSERT INTO "bayan" VALUES(101,7,'101:7','تو وہ ہوگا عیش و عشرت کی زندگی میں۔');
INSERT INTO "bayan" VALUES(101,8,'101:8','اور جس (کی نیکیوں) کے پلڑے ہلکے ہوئے۔');
INSERT INTO "bayan" VALUES(101,9,'101:9','تو اس کا ٹھکانہ ایک گڑھا ہوگا۔');
INSERT INTO "bayan" VALUES(101,10,'101:10','اور تم کیا جانتے ہو کہ وہ کیا ہے ؟');
INSERT INTO "bayan" VALUES(101,11,'101:11','آگ ہے دہکتی ہوئی !');
INSERT INTO "bayan" VALUES(102,1,'102:1','تمہیں غافل کیے رکھا ہے بہتات کی طلب نے !');
INSERT INTO "bayan" VALUES(102,2,'102:2','یہاں تک کہ تم قبروں کو پہنچ جاتے ہو۔');
INSERT INTO "bayan" VALUES(102,3,'102:3','کوئی بات نہیں ! بہت جلد تمہیں معلوم ہوجائے گا۔');
INSERT INTO "bayan" VALUES(102,4,'102:4','پھر کوئی بات نہیں ! بہت جلد تمہیں معلوم ہوجائے گا۔');
INSERT INTO "bayan" VALUES(102,5,'102:5','کوئی بات نہیں ! کاش کہ تم علم یقین کے ساتھ جان جاتے !');
INSERT INTO "bayan" VALUES(102,6,'102:6','تم جہنم کو دیکھ کر رہو گے۔');
INSERT INTO "bayan" VALUES(102,7,'102:7','پھر تم اس کو عین الیقین کے ساتھ دیکھو گے۔');
INSERT INTO "bayan" VALUES(102,8,'102:8','پھر اس دن تم سے ضرور پوچھا جائے گا نعمتوں کے بارے میں۔');
INSERT INTO "bayan" VALUES(103,1,'103:1','زمانے کی قسم ہے۔');
INSERT INTO "bayan" VALUES(103,2,'103:2','یقینا انسان خسارے میں ہے۔');
INSERT INTO "bayan" VALUES(103,3,'103:3','سوائے ان کے جو ایمان لائے اور انہوں نے نیک عمل کیے اور انہوں نے ایک دوسرے کو حق کی نصیحت کی اور انہوں نے باہم ایک دوسرے کو صبر کی تلقین کی۔');
INSERT INTO "bayan" VALUES(104,1,'104:1','بڑی خرابی ہے ہر اس شخص کے لیے جو لوگوں کے عیب ُ چنتا رہتا ہے اور طعنے دیتا رہتا ہے۔');
INSERT INTO "bayan" VALUES(104,2,'104:2','جو مال جمع کرتا رہا اور اس کو گنتا رہا۔');
INSERT INTO "bayan" VALUES(104,3,'104:3','وہ سمجھتا ہے کہ اس کا مال اسے ہمیشہ باقی رکھے گا۔');
INSERT INTO "bayan" VALUES(104,4,'104:4','ہرگز نہیں وہ تو یقینا جھونک دیا جائے گا حطمہ میں۔');
INSERT INTO "bayan" VALUES(104,5,'104:5','اور کیا تم جانتے ہو وہ حطمہ کیا ہے ؟');
INSERT INTO "bayan" VALUES(104,6,'104:6','وہ آگ ہے اللہ کی بھڑکائی ہوئی۔');
INSERT INTO "bayan" VALUES(104,7,'104:7','جو دلوں کے اوپر جا چڑھے گی۔');
INSERT INTO "bayan" VALUES(104,8,'104:8','بیشک وہ (آگ) ان پر بند کردی جائے گی۔');
INSERT INTO "bayan" VALUES(104,9,'104:9','بڑے اونچے اونچے لمبے ستونوں میں۔');
INSERT INTO "bayan" VALUES(105,1,'105:1','کیا تم نے دیکھا نہیں کیا حشر کیا تمہارے رب نے ان ہاتھی والوں کا ؟');
INSERT INTO "bayan" VALUES(105,2,'105:2','کیا اس نے ان کی تمام تدبیروں کو بےکار اور غیر موثر نہیں کردیا ؟');
INSERT INTO "bayan" VALUES(105,3,'105:3','اور ان پر بھیج دیے ُ جھنڈ کے جھنڈ اڑتے ہوئے پرندوں کے۔');
INSERT INTO "bayan" VALUES(105,4,'105:4','جو ان پر مارتے تھے کنکر کی پتھریاں۔');
INSERT INTO "bayan" VALUES(105,5,'105:5','پھر اس نے کردیا ان کو کھائے ہوئے بھس کی طرح۔');
INSERT INTO "bayan" VALUES(106,1,'106:1','قریش کے مانوس رکھنے کی وجہ سے۔');
INSERT INTO "bayan" VALUES(106,2,'106:2','(یعنی) سردیوں اور گرمیوں کے سفر سے ان کو مانوس رکھنے کی وجہ سے۔');
INSERT INTO "bayan" VALUES(106,3,'106:3','پس انہیں بندگی کرنی چاہیے اس گھر کے رب کی۔');
INSERT INTO "bayan" VALUES(106,4,'106:4','جس نے انہیں بھوک میں کھانے کو دیا ور انہیں خوف سے امن عطا کیا۔');
INSERT INTO "bayan" VALUES(107,1,'107:1','کیا تم نے دیکھا اس شخص کو جو جزا و سزا کو جھٹلاتا ہے ؟');
INSERT INTO "bayan" VALUES(107,2,'107:2','یہ وہی ہے جو یتیم کو دھکے دیتا ہے۔');
INSERT INTO "bayan" VALUES(107,3,'107:3','اور نہ وہ مسکین کو کھانا کھلانے کی تلقین کرتا ہے۔');
INSERT INTO "bayan" VALUES(107,4,'107:4','تو بربادی ہے ان نماز پڑھنے والوں کے لیے');
INSERT INTO "bayan" VALUES(107,5,'107:5','جو اپنی نمازوں سے غافل ہیں۔');
INSERT INTO "bayan" VALUES(107,6,'107:6','یہ وہ لوگ ہیں جو دکھاوا کرتے ہیں۔');
INSERT INTO "bayan" VALUES(107,7,'107:7','اور عام استعمال کی چیز بھی (مانگنے پر) نہیں دیتے۔');
INSERT INTO "bayan" VALUES(108,1,'108:1','(اے نبی صلى الله عليه وسلم !) ہم نے آپ کو خیر کثیر عطا کیا۔');
INSERT INTO "bayan" VALUES(108,2,'108:2','پس آپ صلى الله عليه وسلم اپنے رب کے لیے نماز پڑھا کیجیے اور قربانی کیا کیجیے۔');
INSERT INTO "bayan" VALUES(108,3,'108:3','یقینا آپ صلى الله عليه وسلم کا دشمن ہی جڑ کٹا ہوگا۔');
INSERT INTO "bayan" VALUES(109,1,'109:1','(اے نبی صلى الله عليه وسلم آپ کہہ دیجیے کہ اے کافرو !');
INSERT INTO "bayan" VALUES(109,2,'109:2','میں ان کو ہرگز نہیں پوجتا جن کو تم پوجتے ہو۔');
INSERT INTO "bayan" VALUES(109,3,'109:3','اور نہ تم پوجنے والے ہو اسے جسے میں پوجتا ہوں۔');
INSERT INTO "bayan" VALUES(109,4,'109:4','اور نہ ہی میں آئندہ کبھی پوجنے والا ہوں ان کو جن کی تم پرستش کر رہے ہو۔');
INSERT INTO "bayan" VALUES(109,5,'109:5','اور نہ تم پوجنے والے ہو اس کو جس کو میں پوج رہا ہوں۔');
INSERT INTO "bayan" VALUES(109,6,'109:6','اب تمہارے لیے تمہارا دین اور میرے لیے میرا دین۔');
INSERT INTO "bayan" VALUES(110,1,'110:1','جب آجائے مدد اللہ کی اور فتح نصیب ہو۔');
INSERT INTO "bayan" VALUES(110,2,'110:2','اور آپ صلى الله عليه وسلم دیکھ لیں لوگوں کو داخل ہوتے ہوئے اللہ کے دین میں فوج در فوج۔');
INSERT INTO "bayan" VALUES(110,3,'110:3','تو پھر آپ صلى الله عليه وسلم اپنے رب کی حمد کے ساتھ اس کی تسبیح کیجیے اور اس سے مغفرت طلب کیجیے۔ یقیناوہ بہت توبہ قبول فرمانے والا ہے۔');
INSERT INTO "bayan" VALUES(111,1,'111:1','ٹوٹ گئے ابولہب کے دونوں ہاتھ اور وہ تباہ و برباد ہوگیا۔');
INSERT INTO "bayan" VALUES(111,2,'111:2','کچھ کام نہ آیا اس کے اس کا یہ مال اور وہ کمائی جو اس نے کی ہے۔');
INSERT INTO "bayan" VALUES(111,3,'111:3','عنقریب وہ جھونکا جائے گا بھڑکتی ہوئی آگ میں۔');
INSERT INTO "bayan" VALUES(111,4,'111:4','اور اس کی بیوی کو بھی۔ جو ایندھن اٹھانے والی ہوگی۔');
INSERT INTO "bayan" VALUES(111,5,'111:5','اس کے گلے میں بٹی ہوئی رسّی ہوگی۔');
INSERT INTO "bayan" VALUES(112,1,'112:1','کہہ دیجیے وہ اللہ یکتا ہے۔');
INSERT INTO "bayan" VALUES(112,2,'112:2','اللہ سب کا مرجع ہے۔');
INSERT INTO "bayan" VALUES(112,3,'112:3','اس نے کسی کو جنا اور نہ وہ جنا گیا۔');
INSERT INTO "bayan" VALUES(112,4,'112:4','اور کوئی بھی اس کا کفو نہیں ہے۔');
INSERT INTO "bayan" VALUES(113,1,'113:1','کہو کہ میں پناہ میں آتا ہوں صبح کے رب کی۔');
INSERT INTO "bayan" VALUES(113,2,'113:2','(میں پناہ میں آتا ہوں) اس کی تمام مخلوقات کے شر سے۔');
INSERT INTO "bayan" VALUES(113,3,'113:3','اور (خاص طور پر) اندھیرے کے شر سے جب وہ چھا جائے۔');
INSERT INTO "bayan" VALUES(113,4,'113:4','اور ان عورتوں کے شر سے جو گرہیں باندھ کر پھونکیں مارتی ہیں۔');
INSERT INTO "bayan" VALUES(113,5,'113:5','اور حسد کرنے والے کے شر سے بھی (میں اللہ کی پناہ مانگتا ہوں) جب وہ حسد کرے۔');
INSERT INTO "bayan" VALUES(114,1,'114:1','کہیے کہ میں پناہ میں آتا ہوں تمام انسانوں کے رب کی۔');
INSERT INTO "bayan" VALUES(114,2,'114:2','تمام انسانوں کے بادشاہ کی۔');
INSERT INTO "bayan" VALUES(114,3,'114:3','تمام انسانوں کے معبود کی۔');
INSERT INTO "bayan" VALUES(114,4,'114:4','اس بار بار وسوسہ ڈالنے والے پیچھے ہٹ جانے والے کے شر سے۔');
INSERT INTO "bayan" VALUES(114,5,'114:5','جو لوگوں کے سینوں میں وسوسہ اندازی کرتا ہے۔');
INSERT INTO "bayan" VALUES(114,6,'114:6','خواہ وہ جنوں میں سے ہو یا انسانوں میں سے۔');
