INSERT INTO "bs_darassalam" VALUES(1,1,'1:1','U ime Allaha, Milostivog, Samilosnog!');
INSERT INTO "bs_darassalam" VALUES(1,2,'1:2','Sva hvala pripada Allahu, Gospodaru svih svjetova!');
INSERT INTO "bs_darassalam" VALUES(1,3,'1:3','Milostivom, Samilosnom!');
INSERT INTO "bs_darassalam" VALUES(1,4,'1:4','Vladaru Sudnjega dana!');
INSERT INTO "bs_darassalam" VALUES(1,5,'1:5','Samo Tebe obožavamo i samo od Tebe pomoć tražimo!');
INSERT INTO "bs_darassalam" VALUES(1,6,'1:6','Uputi nas na Pravi put!');
INSERT INTO "bs_darassalam" VALUES(1,7,'1:7','Na put onih kojima si blagodati Svoje darovao, a ne na put onih na koje si se rasrdio, niti onih koji su zalutali.');
INSERT INTO "bs_darassalam" VALUES(2,1,'2:1','Elif-Lām-Mīm');
INSERT INTO "bs_darassalam" VALUES(2,2,'2:2','Ova Knjiga – u koju nema sumnje – uputa je bogobojaznima');
INSERT INTO "bs_darassalam" VALUES(2,3,'2:3','onima koji u svijet nevidljivi vjeruju i koji namaz obavljaju i dijele od onoga čime smo ih Mi opskrbili,');
INSERT INTO "bs_darassalam" VALUES(2,4,'2:4','i onima koji vjeruju u ono što je objavljeno tebi i što je objavljeno prije tebe, i koji u ahiret čvrsto vjeruju.');
INSERT INTO "bs_darassalam" VALUES(2,5,'2:5','Njima je Gospodar njihov na Pravi put ukazao i to su oni koji su uspjeli.');
INSERT INTO "bs_darassalam" VALUES(2,6,'2:6','Onima koji ne vjeruju doista je svejedno upozoravao ti njih ili ne upozoravao, oni ne vjeruju.');
INSERT INTO "bs_darassalam" VALUES(2,7,'2:7','Allah je zapečatio srca njihova i uši njihove, a pred očima njihovim je koprena; njih čeka patnja golema.');
INSERT INTO "bs_darassalam" VALUES(2,8,'2:8','Ima ljudi koji govore: "Vjerujemo u Allaha i Posljednji dan", a oni nisu vjernici!');
INSERT INTO "bs_darassalam" VALUES(2,9,'2:9','Oni misle da varaju Allaha i one koji vjeruju, a nikog osim sebe oni ne varaju, ali ne osjećaju.');
INSERT INTO "bs_darassalam" VALUES(2,10,'2:10','U srcima njihovim je bolest i Allah im povećava bolest. Njih čeka patnja bolna zbog toga što su lagali.');
INSERT INTO "bs_darassalam" VALUES(2,11,'2:11','Kada im se kaže: "Ne pravite nered na Zemlji!", odgovaraju: "Mi samo red uspostavljamo!"');
INSERT INTO "bs_darassalam" VALUES(2,12,'2:12','Naprotiv! Oni su uistinu pravi smutljivci, ali to ne opažaju.');
INSERT INTO "bs_darassalam" VALUES(2,13,'2:13','Kada im se kaže: "Vjerujte kao što ovi ljudi vjeruju!", oni odgovaraju: "Zar da vjerujemo kao što maloumnici vjeruju!?" A doista su oni maloumnici, ali to oni ne znaju!');
INSERT INTO "bs_darassalam" VALUES(2,14,'2:14','Kada susretnu one koji vjeruju, oni govore: "Vjerujemo!", a čim ostanu nasamo, sa šejtanima svojim, govore: "Mi smo s vama. Mi se samo izrugujemo."');
INSERT INTO "bs_darassalam" VALUES(2,15,'2:15','Allah se njima izruguje i ostavlja ih da u svojoj osionosti lutaju.');
INSERT INTO "bs_darassalam" VALUES(2,16,'2:16','To su oni koji su zabludu uzeli umjesto Upute, ali im njihova trgovina neće donijeti dobiti, jer oni Uputu nisu slijedili.');
INSERT INTO "bs_darassalam" VALUES(2,17,'2:17','Oni su slični onome koji potpali vatru, a kada ona osvijetli ono što oko njega je, Allah im oduzme svjetlo i ostavi ih u tminama, pa ništa ne vide.');
INSERT INTO "bs_darassalam" VALUES(2,18,'2:18','Gluhi, nijemi i slijepi su oni, pa ne mogu da se povrate!');
INSERT INTO "bs_darassalam" VALUES(2,19,'2:19','Ili su oni slični onima koji za vrijeme silnog pljuska s neba, uz tmine, grmljavinu i munje, zbog gromova stavljaju prste u uši bojeći se smrti. A Allah obuhvata nevjernike.');
INSERT INTO "bs_darassalam" VALUES(2,20,'2:20','Munja samo što ih ne zaslijepi: kad god im osvijetli – oni krenu, a čim ih tami prepusti – zastanu! A da Allah hoće, On bi im oduzeo i sluh i vid. Allah, uistinu, sve može.');
INSERT INTO "bs_darassalam" VALUES(2,21,'2:21','O ljudi, ibadet činite Gospodaru svome, Koji je stvorio vas i one prije vas, da biste se uščuvali.');
INSERT INTO "bs_darassalam" VALUES(2,22,'2:22','Koji vam je Zemlju posteljom, a nebo zdanjem učinio; Koji s neba spušta vodu i s njom izvodi plodove, opskrbu vašu. Zato ne pripisujte Allahu ortake, a vi znate.');
INSERT INTO "bs_darassalam" VALUES(2,23,'2:23','A ako sumnjate u ono što smo Mi objavili robu Svome, sačinite vi jednu suru sličnu tome i pozovite i svjedoke vaše, mimo Allaha, ako istinu govorite.');
INSERT INTO "bs_darassalam" VALUES(2,24,'2:24','Pa ako to ne učinite, a sigurno nećete učiniti, onda se čuvajte vatre čije će gorivo biti ljudi i kamenje; pripremljena je ona za nevjernike.');
INSERT INTO "bs_darassalam" VALUES(2,25,'2:25','A obraduj one koji vjeruju i dobra djela čine da njima pripadaju džennetske bašče kroz koje rijeke teku! Kad god budu opskrbljeni kakvim plodom, reći će: "Ovim smo i prije bili opskrbljeni!" A bit će im davani samo njima slični! U njima će oni supruge očišćene imati i oni će u njima vječno boraviti.');
INSERT INTO "bs_darassalam" VALUES(2,26,'2:26','Doista se Allah ne ustručava za primjer navesti komarca ili nešto iznad njega; oni koji vjeruju – pa, oni znaju da je to Istina od Gospodara njihova; a oni koji ne vjeruju govore: "Šta to Allah hoće ovim primjerom?" Time On da da mnogi u zabludu skrenu, a mnoge na Pravi put upućuje, a time na stranputicu skreće samo razvratnike;');
INSERT INTO "bs_darassalam" VALUES(2,27,'2:27','One koji krše obavezu prema Allahu nakon što je prihvaćena i prekidaju ono što je Allah naredio da se održava i nered na Zemlji prave; to su pravi gubitnici.');
INSERT INTO "bs_darassalam" VALUES(2,28,'2:28','Kako ne vjerujete u Allaha, vi koji ste bili mrtvi pa vas je On oživio, zatim će vas usmrtiti, zatim ponovo oživiti, potom ćete Njemu biti vraćeni?!');
INSERT INTO "bs_darassalam" VALUES(2,29,'2:29','On je za vas sve na Zemlji stvorio, zatim se na nebo usmjerio, i kao sedam nebesa ga uredio; On sve dobro zna.');
INSERT INTO "bs_darassalam" VALUES(2,30,'2:30','A kada Gospodar tvoj reče melekima: "Ja ću na Zemlji namjesnika –halifu postaviti!", oni upitaše: "Zar ćeš na Zemlji postaviti onoga koji će na njoj nered širiti i krv prolijevati?! A mi Tebe veličamo hvalom i kako Tebi dolikuje štujemo." On reče: "Ja, doista, znam ono što vi ne znate."');
INSERT INTO "bs_darassalam" VALUES(2,31,'2:31','I pouči On Adema svim imenima, a onda ih predoči melekima i reče: "Kažite Mi imena njihova, ako istinu govorite!"');
INSERT INTO "bs_darassalam" VALUES(2,32,'2:32','"Uzvišen si!", rekoše oni, "mi znamo samo ono čemu si nas Ti poučio! Zasigurno si Ti Sveznajući i Mudri."');
INSERT INTO "bs_darassalam" VALUES(2,33,'2:33','"O Ademe", reče On, "saopći im njihova imena!" I kada im on saopći imena njihova, Allah reče: "Zar vam nisam rekao da samo Ja znam tajne nebesa i Zemlje i da samo Ja znam ono što pokazujete i što krijete?"');
INSERT INTO "bs_darassalam" VALUES(2,34,'2:34','A kad rekosmo melecima: "Sedždu učinite Ademu!", oni sedždu učiniše svi izuzev Iblisa." On ne htjede i uzoholi se, a nevjernik bio je.');
INSERT INTO "bs_darassalam" VALUES(2,35,'2:35','I Mi rekosmo: "O Ademe, nastanite se ti i supruga tvoja u džennetu i jedite u njemu gdje god želite, ali se ovome drvetu ne približujte da ne budete među zulumćarima."');
INSERT INTO "bs_darassalam" VALUES(2,36,'2:36','Ali, šejtan ih navede na grijeh zbog tog drveta i izvede ih iz onoga u čemu su bili. "Siđite!"," rekosmo Mi. "Jedni drugima bit ćete neprijatelji. Do određenog vremena na Zemlji je vaše boravište i neko vrijeme uživanje."');
INSERT INTO "bs_darassalam" VALUES(2,37,'2:37','I Adem primi neke riječi od Gospodara svoga, pa mu On oprosti; On je, doista, mnogo pokajanje prima i milostiv je.');
INSERT INTO "bs_darassalam" VALUES(2,38,'2:38','Mi rekosmo: "Silazite iz njega svi! Od Mene će vam Uputa dolaziti, a oni koj Uputu Moju budu slijedili, ničega se neće bojati, niti će za bilo čim tugovati."');
INSERT INTO "bs_darassalam" VALUES(2,39,'2:39','A oni koji ne budu vjerovali i dokaze i ajete Naše budu poricali bit će stanovnici Vatre; u njoj će vječno ostati.');
INSERT INTO "bs_darassalam" VALUES(2,40,'2:40','O sinovi Israilovi, sjetite se blagodati Mojih koje sam vam podario, i ispunite zavjet koji ste Mi dali, pa ću i Ja ispuniti Svoj zavjet koji sam vam dao! I samo od Mene strahujte!');
INSERT INTO "bs_darassalam" VALUES(2,41,'2:41','Vjerujte u ono što sam objavio potvrđujući da je istinito ono što vi imate i ne budite prvi koji u to ne vjeruju! Ne prodajite riječi Moje za nešto što sasvim malo vrijedi i samo se Mene bojte!"');
INSERT INTO "bs_darassalam" VALUES(2,42,'2:42','I ne miješajte Istinu s neistinom, a i ne tajite Istinu, a vi je znate!');
INSERT INTO "bs_darassalam" VALUES(2,43,'2:43','Namaz obavljajte, zekat dajite i idite na ruku zajedno s onima koji na ruku idu!');
INSERT INTO "bs_darassalam" VALUES(2,44,'2:44','Zar od drugih tražite da dobro čine dok pri tome sebe zaboravljate, a vi Knjigu čitate? Zar se opametiti nećete!?');
INSERT INTO "bs_darassalam" VALUES(2,45,'2:45','Tražite sebi pomoć u strpljenju i namazu. To je doista teško, osim poniznima,');
INSERT INTO "bs_darassalam" VALUES(2,46,'2:46','onima koji su uvjereni da će se sresti sa Gospodarom svojim i da će se Njemu vratiti.');
INSERT INTO "bs_darassalam" VALUES(2,47,'2:47','O sinovi Israilovi! Sjetite se blagodati Mojih koje sam vam podario i što sam vas nad drugim svjetovima bio odlikovao.');
INSERT INTO "bs_darassalam" VALUES(2,48,'2:48','I bojte se Dana kada niko ni za koga ništa neće moći učiniti, kada se ničiji zagovor neće prihvaćati, kada se ni od koga otkup neće primati i kada nikome niko neće u pomoć priteći.');
INSERT INTO "bs_darassalam" VALUES(2,49,'2:49','I kada smo vas izbavili od faraonovih ljudi koji su vas najgorim mukama mučili: mušku vam djecu klali, a žensku u životu ostavljali – to vam je bilo veliko iskušenje od Gospodara vašeg.');
INSERT INTO "bs_darassalam" VALUES(2,50,'2:50','I kada smo vam more rastavili i vas izbavili, a faraonove ljude – vi ste to gledali – potopili.');
INSERT INTO "bs_darassalam" VALUES(2,51,'2:51','I kada s Musaom, četrdeset noći, susret odredismo, vi ste, nakon što on ode, zulum čineći, tele za obožavanje prihvatili.');
INSERT INTO "bs_darassalam" VALUES(2,52,'2:52','Potom smo vam, i poslije toga, oprostili, da biste zahvalni bili.');
INSERT INTO "bs_darassalam" VALUES(2,53,'2:53','I kada smo dali Musau Knjigu i ono što razdvaja Istinu od neistine, da biste se Pravim putem uputili.');
INSERT INTO "bs_darassalam" VALUES(2,54,'2:54','I kada je Musa rekao narodu svome: "Narode moj, vi ste doista sebi zulum učinili prihvativši tele; zato se Stvoritelju svome pokajte i poubijajte se!" To vam je kod vašeg Stvoritelja bolje, pa će vam pokajanje prihvatiti. Zaista je On stalni Primatelj pokajanja, Milostivi."');
INSERT INTO "bs_darassalam" VALUES(2,55,'2:55','I kada ste vi rekli: "Musa, mi ti nećemo vjerovati dok Allaha jasno ne vidimo!", pa vas je strašna kazna obuzela, a vi ste gledali.');
INSERT INTO "bs_darassalam" VALUES(2,56,'2:56','Zatim smo vas poslije smrti vaše oživili, da biste zahvalni bili.');
INSERT INTO "bs_darassalam" VALUES(2,57,'2:57','I od oblaka smo vam hladovinu načinili i manu i prepelice vam slali. "Jedite lijepa jela, kojima vas opskrbljujemo!" Oni Nama nisu zulum nanijeli, nego su sami sebi zulum nanosili.');
INSERT INTO "bs_darassalam" VALUES(2,58,'2:58','I kada smo rekli: "Uđite u ovaj grad i jedite što god hoćete! Na kapiju uđite klanjajući se i kažite: ''Oprost!'', pa ćemo vam oprostiti grijehe vaše, a onima koji čine dobra djela dat ćemo i više."');
INSERT INTO "bs_darassalam" VALUES(2,59,'2:59','Onda su oni koji su zulum učinili zamijenili riječi drugim koje im nisu rečene, pa smo Mi na one koji su zulum učinili s neba kaznu spustili, zato što su grešnici bili.');
INSERT INTO "bs_darassalam" VALUES(2,60,'2:60','I kada je Musa za narod svoj vodu molio, Mi smo rekli: "Udari štapom svojim po kamenu!" Iz njega je dvanaest vrela provrelo, i svi ljudi su znali iz kojeg će vrela piti. "Jedite i pijte Allahovu opskrbu i ne činite zlo po Zemlji nered praveći."');
INSERT INTO "bs_darassalam" VALUES(2,61,'2:61','I kada ste rekli: “Musa, mi ne možemo više jednu te istu hranu jesti. Zato, moli za nas Gospodara svoga da nam podari od onoga što zemlja rađa: povrća i krastavaca, pšenice, leće i luka crvenog!” On je rekao: “Zar da ono što je bolje zamijenite onim što je lošije?! Idite u bilo koji grad, imat ćete ono što tražite!” I poniženje i bijeda na njih padoše i Allahovu srdžbu oni na sebe navukoše, zato što u Allahove dokaze vjerovali nisu i što su vjerovjesnike bespravno ubijali, zato što su neposlušni bili i sve granice zla prelazili.');
INSERT INTO "bs_darassalam" VALUES(2,62,'2:62','One koji vjeruju, te one koji su bili židovi, kršćani i sabijci, a koji su vjerovali u Allaha i Posljednji dan i dobra djela činili – doista čeka nagrada od Gospodara njihova; za njih nema straha i oni neće tugovati.');
INSERT INTO "bs_darassalam" VALUES(2,63,'2:63','I kada smo od vas zavjet uzeli i brdo Tur iznad vas podigli: "Snažno prihvatite ono što smo vam dali i neka vam je na umu ono što je u Knjizi, da biste se sačuvali."');
INSERT INTO "bs_darassalam" VALUES(2,64,'2:64','Zatim ste odustali, i da nije bilo Allahove darežljivosti prema vama i milosti Njegove, vi biste, uistinu, među gubitnicima bili.');
INSERT INTO "bs_darassalam" VALUES(2,65,'2:65','Vama je poznato šta se dogodilo onima od vas koji su subotu oskrnavili, pa smo im Mi rekli: "Budite majmuni prezreni!"');
INSERT INTO "bs_darassalam" VALUES(2,66,'2:66','To naselje smo učinili opomenom obližnjim naseljima, a poukom za bogobojazne.');
INSERT INTO "bs_darassalam" VALUES(2,67,'2:67','I kada Musa reče narodu svome: "Allah vam, doista, naređuje da kravu zakoljete", oni upitaše: "Zbijaš li to ti s nama šalu?", a on reče: "Allahu se utječem od toga da neznalica budem!"');
INSERT INTO "bs_darassalam" VALUES(2,68,'2:68','Oni rekoše: "Moli za nas Gospodara svoga da nam objasni koje dobi ona treba biti!" "On kaže", odgovori on, "da krava ne smije biti ni stara, ni mlada, nego između toga, srednje dobi, pa učinite to što se od vas traži!"');
INSERT INTO "bs_darassalam" VALUES(2,69,'2:69','"Zamoli za nas Gospodara svoga", rekoše oni, "da nam objasni kakve boje treba biti!" "On kaže", reče on, "da treba biti žute boje, da se svidi onima koji je vide."');
INSERT INTO "bs_darassalam" VALUES(2,70,'2:70','"Moli za nas Gospodara svoga", rekoše oni, "da nam objasni kakva još treba biti, jer nama krave izgledaju slične, a mi ćemo nju, ako Allah htjedne, sigurno pronaći."');
INSERT INTO "bs_darassalam" VALUES(2,71,'2:71','"On kaže", reče on, "da krava ne smije biti iscrpljena oranjem zemlje i natapanjem usjeva; treba biti bez ikakve mahane i biljega." "E, sad si kazao istinu!", rekoše oni, pa je zaklaše, i jedva to učiniše.');
INSERT INTO "bs_darassalam" VALUES(2,72,'2:72','I kada ste jednog čovjeka ubili, pa se o njemu prepirati počeli, Allah je dao da izađe na vidjelo ono što ste bili sakrili.');
INSERT INTO "bs_darassalam" VALUES(2,73,'2:73','Pa smo Mi rekli: "Udarite ga jednim njezinim dijelom!" I eto, tako Allah mrtve vraća u život i pruža vam dokaze Svoje, da biste shvatili.');
INSERT INTO "bs_darassalam" VALUES(2,74,'2:74','Ali su srca vaša, poslije toga, postala tvrda kao kamen ili još tvrđa. A ima kamenja iz kojega rijeke izbijaju; ima ga koje puca i iz kojeg izlazi voda; a ima, doista, i onoga koje se iz straha od Allaha ruši. A Allah ne zanemaruje ono što vi radite.');
INSERT INTO "bs_darassalam" VALUES(2,75,'2:75','Zar vi žudite da vam oni vjeruju, a neki od njih su Allahov govor slušali, a zatim bi ga, nakon što su ga shvatili, svjesno iskrivljavali?');
INSERT INTO "bs_darassalam" VALUES(2,76,'2:76','Kada sretnu one koji vjeruju, oni govore: "Mi vjerujemo!", a čim se osame jedni s drugima, kažu: "Zar ćete im kazivati ono što je Allah vama objavio, pa da im to bude dokaz protiv vas pred Gospodarom vašim? Zar se nećete opametiti!');
INSERT INTO "bs_darassalam" VALUES(2,77,'2:77','A zar oni ne znaju da Allah zna i ono što kriju i ono što pokazuju?!');
INSERT INTO "bs_darassalam" VALUES(2,78,'2:78','Neki od njih su nepismeni i ne poznaju Knjigu, nego samo gatke. Oni samo izmišljaju.');
INSERT INTO "bs_darassalam" VALUES(2,79,'2:79','A teško onima koji svojim rukama pišu knjigu, a zatim govore: "Ovo je od Allaha", da bi to za malu vrijednost prodali. I teško njima zbog onoga što ruke njihove pišu, i teško njima što na taj način zarađuju!');
INSERT INTO "bs_darassalam" VALUES(2,80,'2:80','Oni govore: "Vatra nas neće doticati više od nekoliko dana."Reci: "Da li ste za to od Allaha dobili obećanje, jer Allah neće prekršiti Svoje obećanje, ili na Allaha iznosite ono što ne znate?!"');
INSERT INTO "bs_darassalam" VALUES(2,81,'2:81','A nije tako! Jer oni koji budu zlo činili i grijesi ih njihovi budu sa svih strana stizali, stanovnici Vatre oni bit će i u njoj će vječno ostati.');
INSERT INTO "bs_darassalam" VALUES(2,82,'2:82','A oni koji budu vjerovali i dobra djela činili, oni će stanovnici dženneta biti; u njemu će vječno boraviti.');
INSERT INTO "bs_darassalam" VALUES(2,83,'2:83','I kada smo od potomaka Israilovih zavjet uzeli da ćete jedino Allahu u ibadetu biti, i roditeljima, i bližnjima, i siročadi, i siromasima dobročinstvo činiti, a ljudima lijepe riječi govoriti; i namaz obavljati i zekat davati, vi ste se poslije, izuzev nekolicine, okrećući se od toga udaljili.');
INSERT INTO "bs_darassalam" VALUES(2,84,'2:84','I kada smo od vas zavjet uzeli da krv jedni drugima nećete prolijevati i da jedni druge iz domova vaših nećete izgoniti, vi ste to onda, prihvatili i tome svjedoci bili.');
INSERT INTO "bs_darassalam" VALUES(2,85,'2:85','Nakon toga, vi ipak jedni druge ubijate, a pojedine od vas i iz zavičaja njihovog istjerujete, pomažući se protiv njih, uz grijeh i nasilje, pa ako vam kao sužnji dođu, vi ih otkupljujete. A zabranjeno vam je da ih izgonite! Zar vi u jedan dio Knjige vjerujete, a drugi negirate?! Nikakve kazne za one od vas koji tako čine, sem poniženja na ovome svijetu, neće biti, a na Kijametskom danu bit će stavljeni na muke najteže! Allah, doista, nije nemaran prema onome što vi radite.');
INSERT INTO "bs_darassalam" VALUES(2,86,'2:86','To su oni koji su život na ovome svijetu uzeli u zamjenu za ahiret; zato im patnja olakšana neće biti, niti će im se u pomoć priteći.');
INSERT INTO "bs_darassalam" VALUES(2,87,'2:87','I Mi smo Musau Knjigu dali i, poslije njega, jednog za drugim, poslanike smo slali. I Isau, sinu Merjeminom, jasne smo dokaze dali i Duhom Blagoslovljenim ga pomogli. I kad god vam je koji poslanik donio ono što nije godilo dušama vašim, vi ste se uzoholili, pa ste neke u laž ugonili, a neke ubijali.');
INSERT INTO "bs_darassalam" VALUES(2,88,'2:88','Oni govore: "Naša srca okorjela!" A nije tako, nego je Allah njih prokleo zbog nevjerovanja njihova, a malo je njih koji vjeruju.');
INSERT INTO "bs_darassalam" VALUES(2,89,'2:89','A kada im od Allaha dođe Knjiga koja potvrđuje istinitost onoga što oni imaju – a još otprije su pomoć protiv nevjernika molili – kada im dođe ono što im je poznato, oni to negiraše. Pa neka Allahovo prokletstvo stigne nevjernike!');
INSERT INTO "bs_darassalam" VALUES(2,90,'2:90','Jadno je to za što su se prodali: da negiraju ono što Allah objavljuje, zbog zlobe što Allah spušta Svoju darežljivost kome On hoće od Svojih robova; pa su na sebe gnjev za gnjevom navlačili! A nevjernicima pripada patnja ponižavajuća!');
INSERT INTO "bs_darassalam" VALUES(2,91,'2:91','A kada im se kaže: "Vjerujte u ono što Allah objavljuje!", oni odgovaraju: "Mi vjerujemo samo u ono što je nama objavljeno", i neće da vjeruju u ono što se objavljuje poslije, a to je Istina, koja potvrđuje da je istinito i ono što oni imaju. Reci: "Pa zašto ste još ranije Allahove vjerovjesnike ubijali, ako ste vjernici bili?!"');
INSERT INTO "bs_darassalam" VALUES(2,92,'2:92','I Musa vam je jasne dokaze bio donio, pa ste, ipak, nakon što on ode, zulum čineći, tele za obožavanje prihvatili.');
INSERT INTO "bs_darassalam" VALUES(2,93,'2:93','I kada smo od vas vaš zavjet primili i brdo Tur iznad vas podigli: "Čvrsto prihvatite ono što vam dajemo i slušajte!", oni su odgovorili: "Čujemo, ali poslušati nećemo!", jer srca su njihova, zbog nevjerovanja, nadojena teletom bila. Reci: "Ružno je to na što vas vaše vjerovanje navodi, ako ste vi uopće vjernici."');
INSERT INTO "bs_darassalam" VALUES(2,94,'2:94','Reci: "Ako je kod Allaha džennet osiguran samo za vas, mimo ostalog svijeta, onda vi smrt poželite, ako istinu govorite?!"');
INSERT INTO "bs_darassalam" VALUES(2,95,'2:95','A oni je neće nikada poželjeti zbog onoga što čine, a Allah dobro zna zulumćare.');
INSERT INTO "bs_darassalam" VALUES(2,96,'2:96','I vidjet ćeš sigurno da više žude za životom od drugih ljudi, pa čak i od onih koji Allahu druge u obožavanju pridružuju; svaki od njih volio bi poživjeti hiljadu godina, mada ga to, i kada bi toliko dugo živio, ne bi od kazne udaljilo. A Allah dobro vidi ono što oni rade.');
INSERT INTO "bs_darassalam" VALUES(2,97,'2:97','Reci: "Ko je neprijatelj Džibrilu, neka zna da on Allahovom dozvolom tebi na srce stavlja Kur''an potvrdujući istinitost onoga otprije, kao putokaz i radosnu vijest vjernicima."');
INSERT INTO "bs_darassalam" VALUES(2,98,'2:98','Ko je neprijatelj Allahu, melekima Njegovim i poslanicima Njegovim i Džibrilu i Mikalu" – pa, Allah je, doista, neprijatelj onima koji ne vjeruju!');
INSERT INTO "bs_darassalam" VALUES(2,99,'2:99','A Mi tebi ajete – jasne dokaze objavljujemo, u koje jedino grešnici neće vjerovati.');
INSERT INTO "bs_darassalam" VALUES(2,100,'2:100','Zar svaki put kada neku obavezu preuzmu, neka njihova skupina je odbaci?! Štaviše, većina ih ne vjeruje.');
INSERT INTO "bs_darassalam" VALUES(2,101,'2:101','A kada im je od Allaha Poslanik došao, potvrđujući da je istinito ono što već imaju, dio onih kojima je Knjiga data za leđa svoja Allahovu knjigu su odbacili, kao da oni ne znaju.');
INSERT INTO "bs_darassalam" VALUES(2,102,'2:102','I povedoše se za onim što su šejtani o Sulejmanovoj vladavini lažno kazivali. A Sulejman nije bio nevjernik, nego šejtani, Harut i Marut, nisu vjerovali, podučavajući vradžbini ljude u Babilonu. A to nije objavljeno dvojici meleka. Njih dvojica nikoga nisu učili dok mu ne bi rekli: "Mi samo iskušavamo, a ti nemoj biti nevjernik!" I ljudi su od njih dvojice učili kako će muža od žene rastaviti, ali oni nisu mogli nikome bez Allahove dozvole nauditi. Učili su ono što će im nauditi i od čega koristi neće imati, iako su znali da onaj ko to sebi u zamjenu za vjerovanjekupi, na ahiretu nikakvog udjela neće imati. A doista je jadnoono za što su se prodali, kada bi samo oni to znali!');
INSERT INTO "bs_darassalam" VALUES(2,103,'2:103','A da su oni vjerovali i čuvali se, pa sigurno nagrada od Allaha bolja je – samo da su to oni znali!');
INSERT INTO "bs_darassalam" VALUES(2,104,'2:104','O vi koji vjerujete, ne govorite: "Ra''ina!", nego recite "Unzurna (Pogledaj nas, obrati pažnju na nas)!" i slušajte. A nevjernicima bolna patnja pripada.');
INSERT INTO "bs_darassalam" VALUES(2,105,'2:105','Oni kojima je data Knjiga koji ne vjeruju, a ni oni koji Allahu druge u obožavanju pridružuju, ne vole da se vama od Gospodara vašeg bilo kakvo dobro objavi. Allah Svojom milošću odlikuje koga hoće, i Allah je posjednik obilja velikog.');
INSERT INTO "bs_darassalam" VALUES(2,106,'2:106','Koji god ajet promijenimo, ili ga zaboravu prepustimo, Mi bolji od njega ili sličan njemu donesemo. Zar ti ne znaš da Allah nad svime ima moć?!');
INSERT INTO "bs_darassalam" VALUES(2,107,'2:107','Zar ti ne znaš da Allahu pripada vlast i na nebesima i na Zemlji i da, osim Allaha, ni zaštitnika ni pomagača nemate?!');
INSERT INTO "bs_darassalam" VALUES(2,108,'2:108','Zar da tražite od svog poslanika, kao što su prije od Musaa tražili?! A ko vjerovanje zamijeni nevjerovanjem, taj je s Pravog puta zalutao.');
INSERT INTO "bs_darassalam" VALUES(2,109,'2:109','Mnogi sljedbenici Knjige voljeli bi, iz lične zlobe svoje, da vas, nakon vašeg vjerovanja, vrate u nevjernike, nakon što im je Istina jasnom postala. Ali, vi oprostite i preko toga pređite, dok Allah Svoju odluku ne donese. Allah, doista, nad svime ima moć.');
INSERT INTO "bs_darassalam" VALUES(2,110,'2:110','I namaz obavljajte i zekat dajite, a dobro koje za sebe date, naći ćete kod Allaha. Allah, doista, dobro vidi šta vi radite.');
INSERT INTO "bs_darassalam" VALUES(2,111,'2:111','Oni govore: "U džennet neće ući niko osim onoga ko bude židov ili kršćanin!" To su puste želje njihove. Reci: "Dajte svoj dokaz, ako istinu govorite!"');
INSERT INTO "bs_darassalam" VALUES(2,112,'2:112','A nije tako! Onome ko se bude Allahu predao i usto dobra djela činio, pripada nagrada kod Gospodara njegova; takvi se neće ničega bojati, niti će za bilo čim tugovati.');
INSERT INTO "bs_darassalam" VALUES(2,113,'2:113','Židovi govore: "Kršćani nisu ni na čemu!", a kršćani kažu: "Židovi nisu ni na čemu!", a oni Knjigu čitaju. Tako gotovo istim riječima govore oni koji ne znaju. Allah će im na Kijametskom danu presuditi u onome o čemu su se razilazili.');
INSERT INTO "bs_darassalam" VALUES(2,114,'2:114','Ima li većeg zulumćara od onoga koji brani da se u Allahovim mesdžidima ime Njegovo spominje i koji radi na tome da se oni opustoše?! Takvi bi trebalo da u njih samo sa strahom ulaze. Oni će na ovome svijetu poniženje doživjeti, a na drugom ih čeka kazna velika.');
INSERT INTO "bs_darassalam" VALUES(2,115,'2:115','Allahu pripadaju i istok i zapad; kuda god se okrenete, tamo je Allahovo lice. Allah je, doista, neizmjerno darežljiv i Sveznajući.');
INSERT INTO "bs_darassalam" VALUES(2,116,'2:116','Nevjernici govore: "Allah je sebi dijete uzeo." Uzvišen neka je On! Naprotiv, Njemu pripada sve što je na nebesima i na Zemlji i Njemu se sve pokorava.');
INSERT INTO "bs_darassalam" VALUES(2,117,'2:117','On je Stvoritelj nebesa i Zemlje, a kada nešto odredi, On samo kaže: "Budi!" – i to biva.');
INSERT INTO "bs_darassalam" VALUES(2,118,'2:118','A oni koji ne znaju, govore: "Trebalo bi da Allah s nama razgovara, ili da nam kakav dokaz dođe!" Tako su gotovo istim riječima govorili i oni prije njih; srca su im slična! A Mi dokaze objašnjavamo ljudima koji čvrsto vjeruju.');
INSERT INTO "bs_darassalam" VALUES(2,119,'2:119','Mi smo te, zaista, poslali s Istinom, kao onoga ko radosne vijesti donosi i upozorava, a ti nećeš biti pitan za one koji će u džehennemu biti.');
INSERT INTO "bs_darassalam" VALUES(2,120,'2:120','Ni židovi, ni kršćani neće biti tobom zadovoljni sve dok ne budeš slijedio vjeru njihovu. Reci: "Allahova Uputa jest Prava uputa!" A ako bi se ti poveo za željama njihovim, nakon znanja koje si dobio, od Allaha te niko ne bi mogao zaštititi, niti pomoći ti.');
INSERT INTO "bs_darassalam" VALUES(2,121,'2:121','Oni kojima smo objavili Knjigu, pa je čitaju onako kako treba, to su oni koji u nju vjeruju. A oni koji u nju ne budu vjerovali, to su sigurni gubitnici.');
INSERT INTO "bs_darassalam" VALUES(2,122,'2:122','O sinovi Israilovi! Sjetite se blagodati Mojih koje sam vam podario i što sam vas nad drugim svjetovima bio odlikovao.');
INSERT INTO "bs_darassalam" VALUES(2,123,'2:123','I bojte se Dana kada niko ni za koga ništa neće moći učiniti, kada se ni od koga otkup neće primati, kada se ničiji zagovor neće koristiti, i kada nikome niko neće u pomoć priteći.');
INSERT INTO "bs_darassalam" VALUES(2,124,'2:124','I kada je Ibrahima Gospodar njegov s nekoliko riječi na iskušenje stavio, pa ih je on potpuno izvršio, Allah je rekao: "Učinit ću da ti budeš ljudima vođa." On je upitao: "A i neki iz mog potomstva?" Odgovorio mu je: "Obećanje Moje ne obuhvata zulumćare!"');
INSERT INTO "bs_darassalam" VALUES(2,125,'2:125','I kada Mi Kabu učinismo utočištem i sigurnim mjestom za ljude: "Neka vam mjesto na kojem je Ibrahim stajao bude prostor gdje ćete namaz obavljati!" I Ibrahimu i Ismailu stavili smo u obavezu: "Kabu Moju vas dvojica očistite, za one koji je budu obilazili, za one koji u njoj budu boravili, i za one koji ruku i sedždu budu činili."');
INSERT INTO "bs_darassalam" VALUES(2,126,'2:126','A kada je Ibrahim zamolio: "Gospodaru moj, učini ovo mjesto sigurnim boravištem i snabdij plodovima stanovnike njegove, one između njih koji budu u Allaha i Posljednji dan vjerovali!", On je rekao: "Onome koji ne bude vjerovao, dat ću da neko vrijeme uživa, a onda ću ga prisiliti da u patnju vatrenu uđe. A užasno je to konačno odredište!"');
INSERT INTO "bs_darassalam" VALUES(2,127,'2:127','I dok su Ibrahim i Ismail temelje Kabe podizali, molili su: "Gospodaru naš, primi to od nas, Ti si, uistinu, Svečujući i Sveznajući.');
INSERT INTO "bs_darassalam" VALUES(2,128,'2:128','Gospodaru naš, učini nas dvojicu muslimanima – Tebi predanim, a i u potomstvu našem neka bude zajednica Tebi predana, pokaži nam obrede naše i oprosti nam! Ti si, doista, stalni Primatelj pokajanja, Milostivi!');
INSERT INTO "bs_darassalam" VALUES(2,129,'2:129','Gospodaru naš, pošalji među njih poslanika, jednoga od njih, koji će im ajete Tvoje kazivati, Knjizi i mudrosti ih poučiti i očistiti ih! Ti si, doista, Silni i Mudri!"');
INSERT INTO "bs_darassalam" VALUES(2,130,'2:130','Ko drugi vjeru Ibrahimovu izbjegava do onaj koji ne drži do sebe!? Mi smo njega na ovome svijetu odabrali, pa će on i na drugom svijetu biti među dobrima.');
INSERT INTO "bs_darassalam" VALUES(2,131,'2:131','Kada je njemu Gospodar njegov rekao: "Predan budi!", on je odgovorio: "Ja sam predan Gospodaru svjetova."');
INSERT INTO "bs_darassalam" VALUES(2,132,'2:132','I Ibrahim je, kao i Jakub, to zapovjedio sinovima svojim: "Sinovi moji, Allah je vama vjeru odabrao, i nipošto nemojte umrijeti nego kao muslimani."');
INSERT INTO "bs_darassalam" VALUES(2,133,'2:133','Da niste možda prisutni bili kada je Jakubu smrtni čas došao?! Kad je on sinove svoje upitao: "Kome ćete poslije mene ibadet činiti?" Odgovorili su: "Ibadet ćemo činiti Bogu tvome, Bogu tvojih predaka Ibrahima, Ismaila i Ishaka, Bogu Jedinome! I mi se samo Njemu predajemo!"');
INSERT INTO "bs_darassalam" VALUES(2,134,'2:134','Taj je narod nestao; njemu pripada ono što je zaslužio, a vama što ste zaslužili, i vi nećete biti pitani za ono što su oni radili.');
INSERT INTO "bs_darassalam" VALUES(2,135,'2:135','Oni govore: "Budite židovi, odnosno kršćani, bit ćete na Pravom putu!" Ti reci: "Ne, mi smo sljedbenici Ibrahima pravovjernika. On nije u višebošce spadao."');
INSERT INTO "bs_darassalam" VALUES(2,136,'2:136','Recite: "Mi vjerujemo u Allaha i u ono što se objavljuje nama, i u ono što je objavljeno Ibrahimu, i Ismailu, i Ishaku, i Jakubu, i potomcima," i u ono što je dato Musau i Isau, i u ono što je dato vjerovjesnicima od Gospodara njihova; mi ne pravimo nikakve razlike među njima i mi smo Njemu predani."');
INSERT INTO "bs_darassalam" VALUES(2,137,'2:137','Pa, ako oni budu vjerovali u ono u što vi vjerujete, na Pravom su putu; a ako se okrenu, oni su onda raskol počinili. Allah će te sigurno od njih zaštititi. On je Svečujući i Sveznajući.');
INSERT INTO "bs_darassalam" VALUES(2,138,'2:138','"Allahove vjere se držite! Zar ima ljepše vjere od Allahove?! Mi sesamo Njemu pokoravamo."');
INSERT INTO "bs_darassalam" VALUES(2,139,'2:139','Reci: "Zar s nama o Allahu raspravljate, a On je i naš i vaš Gospodar! Nama pripadaju naša djela, a vama vaša djela! A mi smo prema Njemu iskreni."');
INSERT INTO "bs_darassalam" VALUES(2,140,'2:140','Kako možete govoriti da su Ibrahim, Ismail, Ishak, Jakub i potomci njegovi bili židovi ili kršćani? Reci: "Znate li bolje vi ili Allah?" Zar je iko veći zulumćar od onoga koji skriva svjedočanstvo Istine dobivene od Allaha! A Allah nije nemaran prema onome što vi radite.');
INSERT INTO "bs_darassalam" VALUES(2,141,'2:141','Taj je narod nestao; njemu pripada ono što je zaslužio, a vama što ste zaslužili, i vi nećete biti pitani za ono što su oni radili.');
INSERT INTO "bs_darassalam" VALUES(2,142,'2:142','Reći će neki ljudi kratke pameti: "Šta ih je odvratilo od kible njihove, prema kojoj su se okretali?" Reci: "Allahu pripadaju i istok i zapad; On upućuje na Pravi put onoga koga On hoće."');
INSERT INTO "bs_darassalam" VALUES(2,143,'2:143','Tako smo vas učinili najboljom – zajednicom sredine, kako biste svjedočili o drugim ljudima, i kako bi Poslanik o vama svjedočio. I Mi smo promijenili kiblu prema kojoj si se okretao samo da bismo znali one koji slijede Poslanika naspram onih koji ne vjeruju, jer to je doista teško bilo svima, izuzev onim koje je Allah uputio na Pravi put. Allah neće dozvoliti da propadne vjerovanje vaše – a doista je Allah prema ljudima milosrdan i milostiv.');
INSERT INTO "bs_darassalam" VALUES(2,144,'2:144','Mi vidimo kako okrećeš lice svoje prema nebu i Mi ćemo te sigurno okrenuti prema kibli koju ti želiš; pa, okreni lice svoje prema Časnoj džamiji! I gdje god bili, lice svoje okrenite prema njoj. Doista, oni kojima je data Knjiga znaju da je to istina od Gospodara njihova. A Allah nije nemaran prema onome šta oni rade.');
INSERT INTO "bs_darassalam" VALUES(2,145,'2:145','I kada bi ti onima kojima je data Knjiga sve dokaze donio, oni opet neće prihvatiti tvoju kiblu, kao što ni ti nećeš prihvatiti njihovu kiblu, niti će iko od njih prihvatiti kiblu drugoga. A ako bi se ti za njihovim željama poveo nakon što ti je došlo znanje, tada bi sigurno među zulumćarima bio.');
INSERT INTO "bs_darassalam" VALUES(2,146,'2:146','Oni kojima smo dali Knjigu poznaju njega – Poslanika, kao što sinove svoje znaju, ali neki od njih doista svjesno istinu prikrivaju.');
INSERT INTO "bs_darassalam" VALUES(2,147,'2:147','Istina je od Gospodara tvoga, pa zato nikako ne budi od onih koji sumnjaju.');
INSERT INTO "bs_darassalam" VALUES(2,148,'2:148','Svako ima svoj pravac prema kojem se okreće, pa se natječite u dobru! Ma gdje bili, Allah će vas sve sabrati. Allah, doista, nad svime ima moć!');
INSERT INTO "bs_darassalam" VALUES(2,149,'2:149','I iz svakoga mjesta u koje dođeš, ti lice svoje u pravcu Časne džamije okreni; to je, doista, Istina od Gospodara tvoga; Allah nije nemaran prema onome što vi radite.');
INSERT INTO "bs_darassalam" VALUES(2,150,'2:150','I iz svakog mjesta u koje dođeš, ti lice svoje prema Časnoj džamiji okreni; i gdje god se nalazili, vi lica svoja prema njoj okrećite, da vam ljudi ne bi imali šta prigovoriti, izuzev onih između njih koji zulum čine. Njih se ne bojte, nego se Mene bojte, da bih blagodat Svoju prema vama upotpunio i da biste na Pravom putu bili.');
INSERT INTO "bs_darassalam" VALUES(2,151,'2:151','Kao što vam Mi jednoga od vas kao poslanika poslasmo da vam ajete Naše kazuje, da vas očisti, da vas Knjizi i mudrosti pouči, i da vas ono što niste znali nauči;');
INSERT INTO "bs_darassalam" VALUES(2,152,'2:152','pa, sjećajte se vi Mene, i Ja ću se vas sjećati! I zahvaljujte Mi, a nemojte Mi nezahvalni biti!');
INSERT INTO "bs_darassalam" VALUES(2,153,'2:153','O vi koji vjerujete, tražite sebi pomoći u strpljivosti i namazu! Allah je, doista, uz strpljive.');
INSERT INTO "bs_darassalam" VALUES(2,154,'2:154','I ne kažite za one koji su na Allahovom putu poginuli: "Mrtvi su!" Ne, oni su živi, ali vi to ne opažate.');
INSERT INTO "bs_darassalam" VALUES(2,155,'2:155','Mi ćemo vas u iskušenje dovoditi, malo strahom i gladovanjem, te gubljenjem imetka, života i ljetine. A ti obraduj strpljive,');
INSERT INTO "bs_darassalam" VALUES(2,156,'2:156','one koji, kada ih kakva nevolja zadesi, samo kažu: "Mi smo, doista, Allahovi i Njemu ćemo se, sigurno, vratiti."');
INSERT INTO "bs_darassalam" VALUES(2,157,'2:157','To su oni kojima pripadaju blagoslovi od Gospodara njihova i milost; oni su na Pravom putu.');
INSERT INTO "bs_darassalam" VALUES(2,158,'2:158','Safa i Merva su, doista, Allahova časna mjesta. Zato onaj koji Kuću Allahovu hodočasti ili umru obavi, ne čini nikakav prijestup ako obiđe oko njih. A onaj koji drage volje učini kakvo dobro djelo, pa Allah je doista blagodaran i sve zna.');
INSERT INTO "bs_darassalam" VALUES(2,159,'2:159','One koji budu tajili jasne dokaze koje smo Mi objavili, i Pravi put nakon što smo ga u Knjizi ljudima označili, Allah će prokleti, a proklet će ih i oni koji proklinju.');
INSERT INTO "bs_darassalam" VALUES(2,160,'2:160','Izuzeti će biti samo oni koji se pokaju i poprave, i to javno ispolje; takvima ću Ja oprostiti, a Ja sam stalni Primatelj pokajanja, Milostivi.');
INSERT INTO "bs_darassalam" VALUES(2,161,'2:161','Doista, onima koji ne vjeruju i koji kao nevjernici umru, slijedi prokletstvo Allahovo, prokletstvo meleka i svih ljudi.');
INSERT INTO "bs_darassalam" VALUES(2,162,'2:162','U njemu će vječno ostati, a patnja njihova neće se smanjivati, niti će im se šta odlagati.');
INSERT INTO "bs_darassalam" VALUES(2,163,'2:163','A vaš Bog – jedan je Bog; nema boga osim Njega, Svemilosnog, Milostivog.');
INSERT INTO "bs_darassalam" VALUES(2,164,'2:164','U stvaranju nebesa i Zemlje, u smjeni noći i dana, u lađi koja morem plovi s korisnim tovarom za ljude i u vodi koju Allah pušta s neba, pa tako u život vraća zemlju nakon zamrlosti njene, po kojoj je rasijao svakojaka živa bića, u usmjeravanju vjetrova i oblacima koji između neba i Zemlje lebde – ima, doista, znakova za one koji razumiju.');
INSERT INTO "bs_darassalam" VALUES(2,165,'2:165','Ima ljudi koji pored Allaha druge, kao takmace Njemu, prihvaćaju; vole ih kao što se Allah voli! Ali, oni koji vjeruju, još više Allaha vole! A da znaju oni koji su zulum činili – kada kaznu dožive, da će vidjeti da moć sva Allahu pripada i da Allah strahovito kažnjava.');
INSERT INTO "bs_darassalam" VALUES(2,166,'2:166','Kada se oni za kojim su se drugi povodili odreknu onih koji su ih slijedili i kaznu budu iskusili, i kada im se svi putevi spasa pokidaju,');
INSERT INTO "bs_darassalam" VALUES(2,167,'2:167','tada će sljedbenici njihovi reći: "Da nam je samo da se za trenutak vratimo, pa da se i mi njih odreknemo kao što su se oni nas odrekli!" Eto, tako će Allah njima pokazati pogubnim djela njihova i oni iz Vatre neće izlaziti.');
INSERT INTO "bs_darassalam" VALUES(2,168,'2:168','O ljudi, jedite od onoga što ima na Zemlji, što je dozvoljeno i lijepo, i ne slijedite šejtanove stope! On vam je, zaista, neprijatelj očevidni.');
INSERT INTO "bs_darassalam" VALUES(2,169,'2:169','On vas navraća na grijeh i razvrat, i da o Allahu govorite ono što ne znate.');
INSERT INTO "bs_darassalam" VALUES(2,170,'2:170','A kada im se kaže: "Slijedite ono što je Allah objavio!" – oni odgovaraju: "Ne, nego ćemo slijediti ono na čemu smo naše pretke zatekli." Zar i onda kada im preci nisu ništa shvaćali, niti su na Pravom putu bili?!');
INSERT INTO "bs_darassalam" VALUES(2,171,'2:171','Oni koji ne vjeruju slični su onome koji viče, ali sam ne čuje ništa osim zov i zapomaganje; oni su gluhi, nijemi i slijepi i oni ništa ne shvaćaju.');
INSERT INTO "bs_darassalam" VALUES(2,172,'2:172','O vi koji vjerujete, jedite dobra kojim smo vas opskrbili i Allahu zahvaljujte, ako, doista, samo Njemu ibadet činite.');
INSERT INTO "bs_darassalam" VALUES(2,173,'2:173','On vam, doista, zabranjuje: strv, krv, svinjsko meso i ono što je prineseno kao žrtva u nečije drugo ime, a ne u Allahovo. A onome koji bude primoran, ne svojom voljom i ne pretjerujući, nije mu grijeh. Allah je, doista, Onaj Koji oprašta grijehe i milostiv je.');
INSERT INTO "bs_darassalam" VALUES(2,174,'2:174','Doista, oni koji taje ono što je Allah u Knjizi objavio i to prodaju za nešto što malo vrijedi – oni u trbuhe svoje samo vatru trpaju; na Kijametskom danu Allah ih neće ni osloviti, niti ih očistiti; njima pripada patnja bolna.');
INSERT INTO "bs_darassalam" VALUES(2,175,'2:175','To su oni koji umjesto Upute uzimaju zabludu, a umjesto oprosta patnju; kako li samo oni mogu da trpe Vatru?!');
INSERT INTO "bs_darassalam" VALUES(2,176,'2:176','To je tako, jer Allah je objavio Knjigu s istinom, a oni koji se o Knjizi razilaze doista su u raskolu velikom.');
INSERT INTO "bs_darassalam" VALUES(2,177,'2:177','Nije dobročinstvo u tome da okrećete lica svoja prema istoku i zapadu, nego je pravo dobročinstvo dobročinstvo onih koji vjeruju u Allaha, u ahiret, i u meleke, i u knjige, i u vjerovjesnike, te onih koji od imetka, iako im je drag, daju rođacima i siročadi, siromasima i putnicima namjernicima, prosjacima, i za otkup iz ropstva, i onih koji namaz obavljaju i zekat daju, i koji obavezu svoju, kada je preuzmu, ispunjavaju, te onih koji su strpljivi u neimaštini i u bolesti, i za vrijeme borbe. To su oni koji su iskreni i oni su, uistinu, bogobojazni.');
INSERT INTO "bs_darassalam" VALUES(2,178,'2:178','O vi koji vjerujete! Propisuje vam se odmazda za ubijene: slobodan za slobodnog, rob za roba, i žensko za žensko. A prema onome kome od njegovog brata bude nešto oprošteno, neka onaj ko prihvati otkup postupi pravedno, a i on neka njemu isplati lijepo i po propisu. To je olakšanje od Gospodara vašeg i milost. A ko nasilje i poslije toga izvrši, njemu slijedi bolna patnja.');
INSERT INTO "bs_darassalam" VALUES(2,179,'2:179','U odmazdi vam je život, o razumom obdareni, da biste se sačuvali.');
INSERT INTO "bs_darassalam" VALUES(2,180,'2:180','Kada neko od vas na samrti bude, pa ostavlja kakvo dobro, propisano vam je da, na lijep i pravedan način, roditeljima i bližnjima oporuku učinite, što je obaveza bogobojaznih.');
INSERT INTO "bs_darassalam" VALUES(2,181,'2:181','Ko god to zamijeni nakon što je čuo kako glasi, grijeh za to pada na one koji to mijenjaju. A Allah je, doista, Onaj Koji sve čuje i Onaj Koji sve zna.');
INSERT INTO "bs_darassalam" VALUES(2,182,'2:182','Pa ko se boji pogreške ili griješnog postupka oporučioca, pa to valjano između onih kojih se oporuka tiče popravi, nije zgriješio! Allah je, doista, Onaj Koji oprašta grijehe i milostiv je.');
INSERT INTO "bs_darassalam" VALUES(2,183,'2:183','O vi koji vjerujete! Propisuje vam se post, kao što je propisan onima prije vas, da biste se grijeha sačuvali,');
INSERT INTO "bs_darassalam" VALUES(2,184,'2:184','određen broj dana; a onome od vas koji je bolestan ili na putu bude, toliko drugih dana. Onima koji ga teško podnose, otkup je da siromaha nahrane. A ko drage volje da više, za njega je bolje. A bolje vam je da postite, neka znate.');
INSERT INTO "bs_darassalam" VALUES(2,185,'2:185','U ramazanu je počelo objavljivanje Kur''ana, koji je putokaz ljudima, jasan dokaz Pravog puta i razlikovanja dobra od zla. Ko od vas u tom mjesecu bude kod kuće, neka posti, a ko bolestan bude ili se na putu zadesi, neka isti broj dana naposti. Allah hoće da vam olakša, a ne da teškoće imate, da određeni broj dana ispunite i da Allaha veličate jer vas je uputio, te da zahvalni budete.');
INSERT INTO "bs_darassalam" VALUES(2,186,'2:186','A kada te robovi Moji za Mene upitaju, Ja sam, sigurno, blizu: odazivam se molbi molitelja kada Me zamoli. Zato, neka se i oni Meni odazovu i neka vjeruju u Mene, kako bi ispravnim putem krenuli.');
INSERT INTO "bs_darassalam" VALUES(2,187,'2:187','Dozvoljava vam se da u noćima posta imate odnos sa ženama vašim; one su odjeća vaša, a vi ste odjeća njihova; Allah zna da ste sami sebe varali, pa je prihvatio pokajanje vaše i oprostio vam; zato se sada sastajte s njima i žudite za onim što vam je Allah odredio; jedite i pijte sve dok budete mogli razlikovati bijelu nit od crne niti zore, a potom upotpunite post, do noći; sa ženama ne općite dok ste u itikafu u džamijama; to su Allahove granice i ne približujte im se; tako Allah u ajetima Svojim obznanjuje propise ljudima, kako bi se bogobojazni bili.');
INSERT INTO "bs_darassalam" VALUES(2,188,'2:188','Ne jedite imovinu jedni drugih na nepošten način i ne sporite se zbog nje pred sudijama kako biste s grijehom, i znajući to, dio imovine drugih ljudi pojeli.');
INSERT INTO "bs_darassalam" VALUES(2,189,'2:189','Pitaju te o mlađacima. Reci: "Oni su ljudima oznake za vrijeme i hadž." I nije dobročinstvo u tome da sa stražnje strane u kuće ulazite, nego je pravo dobročinstvo ono što čini onaj koji se Allaha boji. U kuće ulazite na vrata njihova i Allaha se bojte da biste uspjeli.');
INSERT INTO "bs_darassalam" VALUES(2,190,'2:190','I borite se na Allahovu putu protiv onih koji se bore protiv vas, ali ne prelazite granice. Zaista Allah ne voli one koji prekoračaju granice.');
INSERT INTO "bs_darassalam" VALUES(2,191,'2:191','I ubijajte ih gdje god ih stignete, i progonite ih odakle su oni vas prognali! A smutnja je teža od ubijanja! I ne borite se protiv njih kod Časne džamije dok vas oni tu ne napadnu. Ako vas tu napadnu, onda ih ubijajte! Takva je kazna za nevjernike.');
INSERT INTO "bs_darassalam" VALUES(2,192,'2:192','Ako se prođu, pa Allah je, doista, Onaj Koji oprašta grijehe i milostiv je.');
INSERT INTO "bs_darassalam" VALUES(2,193,'2:193','I borite se protiv njih dok smutnje ne nestane i dok vjera ne bude samo Allahu iskazivana. Pa, ako se okane, onda neprijateljstvo prestaje, a jedino protiv zulumćara ostaje.');
INSERT INTO "bs_darassalam" VALUES(2,194,'2:194','Sveti mjesec je za sveti mjesec, a i u svetinjama vrijedi odmazda: onima koji vas napadnu, uzvratite istom mjerom i Allaha se bojte; znajte da je Allah uz bogobojazne.');
INSERT INTO "bs_darassalam" VALUES(2,195,'2:195','I trošite imetak na Allahovom putu, i svojim rukama se u propast ne bacajte, i dobro činite; Allah, doista, voli dobročinitelje.');
INSERT INTO "bs_darassalam" VALUES(2,196,'2:196','Hadž i umru radi Allaha upotpunite! A ako budete spriječeni, onda kurbane što vam se nađu pri ruci zakoljite, a glave svoje, dok kurbani ne stignu do mjesta svoga, ne brijte! A onaj od vas koji se razboli ili ima nešto na glavi što ga muči, neka se postom ili sadakom ili kurbanom iskupi! Kada budete sigurni, ko bude obavljao umru s hadžom, kurban do kojeg može doći, neka zakolje! A onaj ko ga ne nađe, neka tri dana posti u danima hadža i sedam dana po povratku, to je punih deset dana. To važi za onoga čija porodica ne živi u granicama Časne džamije. Bojte se Allaha i znajte da On žestoko kažnjava!');
INSERT INTO "bs_darassalam" VALUES(2,197,'2:197','Hadž je u poznatim mjesecima, pa onaj ko se obaveže u toku njih obaviti hadž ne može imati intimni odnos, nikakve grijehe činiti, niti se prepirati u toku hadža. A za dobro koje učinite Allah zna. Za put se opskrbite, a najbolja je opskrba bogobojaznost! I Mene se bojte – vi, razumom obdareni!');
INSERT INTO "bs_darassalam" VALUES(2,198,'2:198','Nije vam grijeh da od Gospodara svoga tražite neko dobro. A kada pođete s Arefata, Allaha spominjite kod Meš''ari-harama; spominjite Ga budući da vas je On uputio, a zaista ste prije toga u zabludi bili.');
INSERT INTO "bs_darassalam" VALUES(2,199,'2:199','Zatim pohrlite odakle hrle ostali ljudi i od Allaha oprost molite. Allah je, doista, Onaj Koji oprašta grijehe i milostiv je.');
INSERT INTO "bs_darassalam" VALUES(2,200,'2:200','A kada završite obrede vaše, Allaha spominjite kao što spominjete pretke vaše, pa i više od toga! Ima ljudi koji govore: "Gospodaru naš, daj Ti nama na ovome svijetu!", a na drugom svijetu neće imati nikakvog udjela!');
INSERT INTO "bs_darassalam" VALUES(2,201,'2:201','A ima i onih koji govore: "Gospodaru naš, daj nam dobro i na dunjaluku i na ahiretu i sačuvaj nas patnje paklene!"');
INSERT INTO "bs_darassalam" VALUES(2,202,'2:202','Njima pripada udio koji su zaslužili! Allah doista brzo sviđa račune.');
INSERT INTO "bs_darassalam" VALUES(2,203,'2:203','I Allaha spominjite u danima određenim! A onome ko požuri, pa to učini u dva dana, nije grijeh, kao što nije grijeh ni onome koji još ostane, ako je bogobojazan. Bojte se Allaha, i znajte da ćete svi pred Njim biti sakupljeni.');
INSERT INTO "bs_darassalam" VALUES(2,204,'2:204','Ima ljudi čije te riječi o životu na ovome svijetu oduševljavaju i koji se pozivaju na Allaha kao svjedoka za ono što je u srcima njihovim, a najljući su protivnici.');
INSERT INTO "bs_darassalam" VALUES(2,205,'2:205','Čim se neki od njih okrene od tebe, krene nered po Zemlji praviti, ništiti usjeve i stoku. A Allah ne voli nered!');
INSERT INTO "bs_darassalam" VALUES(2,206,'2:206','A kada mu se kaže: "Allaha se boj!", on se onda ponosi grijesima. Pa dosta mu je džehennem, a to je, doista, grozno boravište!');
INSERT INTO "bs_darassalam" VALUES(2,207,'2:207','Ima ljudi koji se žrtvuju u težnji za zadovoljstvom Allahovim. A Allah je milosrdan prema robovima Svojim.');
INSERT INTO "bs_darassalam" VALUES(2,208,'2:208','O vi koji vjerujete, uđite u islam potpuno i ne idite stopama šejtanovim; on vam je, doista, neprijatelj otvoreni!');
INSERT INTO "bs_darassalam" VALUES(2,209,'2:209','A ako posrnete, nakon što su vam došli jasni dokazi, onda znajte da je Allah, zaista, silan i mudar.');
INSERT INTO "bs_darassalam" VALUES(2,210,'2:210','Čekaju li oni da im Allah dođe u tminama oblaka, i meleki, i da bude svemu kraj?! A Allahu se sve vraća!');
INSERT INTO "bs_darassalam" VALUES(2,211,'2:211','Upitaj sinove Israilove koliko smo im jasnih dokaza dali! A oni koji zamjenjuju blagodat Allahovu koja im je došla, pa Allah, doista, strahovito kažnjava!');
INSERT INTO "bs_darassalam" VALUES(2,212,'2:212','Nevjernicima se život na ovome svijetu čini lijepim i oni se rugaju onima koji vjeruju. A na Sudnjem danu biće iznad njih oni koji su se Allaha bojali i grijeha klonili. – A Allah daje u obilju onome kome On hoće, bez računa.');
INSERT INTO "bs_darassalam" VALUES(2,213,'2:213','Svi ljudi su sačinjavali jednu zajednicu, pa je Allah slao vjerovjesnike da donose radosne vijesti i opomenu, i po njima je slao Knjigu sa Istinom, da po njoj sudi ljudima u onome u čemu se oni razilaziše. A o njoj su se, iz međusobne zavisti, razišli samo oni kojima je ona data, i to nakon što su im došli jasni dokazi. Zatim je Allah, Svojom dozvolom uputio ka Istini, o kojoj su se oni razišli, one koji vjeruju. A Allah upućuje na Pravi put onoga koga On hoće.');
INSERT INTO "bs_darassalam" VALUES(2,214,'2:214','Zar mislite da ćete ući u džennet, a još niste iskusili ono što su iskusili oni koji prije vas minuše?! Njih je pogađala neimaština i bolest, i toliko su bivali potresani da bi i poslanik i oni koji su s njim vjerovali, govorili: "Kada će doći Allahova pomoć!?" Eto, Allahova pomoć je, doista, blizu!');
INSERT INTO "bs_darassalam" VALUES(2,215,'2:215','Pitaju te, šta će dijeliti? Reci: "Imetak koji ćete udjeljivati neka pripadne roditeljima i rođacima, i siročadi, i siromasima, i putnicima namjernicima; a za dobro koje činite Allah sigurno dobro zna."');
INSERT INTO "bs_darassalam" VALUES(2,216,'2:216','Propisana vam je borba, a to vam je mrsko! Možda nešto ne volite, a to je dobro po vas; možda nešto volite, a to je zlo po vas! Allah zna, a vi ne znate.');
INSERT INTO "bs_darassalam" VALUES(2,217,'2:217','Pitaju te o svetom mjesecu, o ratovanju u njemu! Reci: "Ratovanje u njemu veliki je grijeh; ali, odvraćanje od Allahova puta i nevjerovanje u Njega, te odvraćanje od Časne džamije i izgonjenje stanovnika njenih, još je veći grijeh kod Allaha. A smutnja je gora od ubijanja! Oni će se neprestano boriti protiv vas dok vas ne odvrate od vjere vaše, ako budu mogli! A oni među vama koji od vjere svoje otpadnu i kao nevjernici umru – njihova djela bit će poništena na dunjaluku, a i na ahiretu. Oni će stanovnici Vatre biti i tamo će vječno ostati.');
INSERT INTO "bs_darassalam" VALUES(2,218,'2:218','Zaista, oni koji vjeruju, i koji se isele i na Allahovom putu bore, oni žele milost Allahovu. A Allah je Onaj Koji oprašta grijehe i milostiv je.');
INSERT INTO "bs_darassalam" VALUES(2,219,'2:219','Pitaju te o opojnim pićima i kocki! Reci: "U njima je veliki grijeh, a i ima i nekih koristi ljudima; samo je grijeh od njih veći od koristi njihove!" Pitaju te, šta da udjeljuju! Reci: "Višak!" Eto, tako vama Allah propise objašnjava da biste razmislili,');
INSERT INTO "bs_darassalam" VALUES(2,220,'2:220','i o dunjaluku i o ahiretu. I pitaju te o siročadi! Reci: "Unaprijediti njihova imanja je bolje!" A ako budete s njima zajedno živjeli, pa to su braća vaša! Allah zna razliku između pokvarenjaka i dobročinitelja! Da je Allah htio, mogao je propisati ono što vam je teško! On je, doista, silan i mudar.');
INSERT INTO "bs_darassalam" VALUES(2,221,'2:221','Ne ženite se višeboškinjama dok vjernice ne postanu; uistinu je robinja-vjernica bolja od višeboškinje, makar vam se ona i sviđala! Ne udajite vjernice za višebošce dok ne postanu vjernici; jer uistinu je rob-vjernik bolji od višebošca, makar vam se i dopadao. Oni zovu u Vatru, a Allah poziva džennetu i oprostu, dozvolom Svojom, te objašnjava ljudima ajete i dokaze Svoje, da bi se prisjećali i pouku primili!');
INSERT INTO "bs_darassalam" VALUES(2,222,'2:222','I pitaju te o mjesečnom pranju! Reci: "To je neprijatnost!" Zato, ne općite sa ženama za vrijeme mjesečnog pranja, i ne prilazite im dok se ne očiste! A kada se očiste, onda im prilazite onako kako vam je Allah naredio! Allah, doista, voli one koji se kaju i voli one koji se čiste.');
INSERT INTO "bs_darassalam" VALUES(2,223,'2:223','Žene vaše su njive vaše, i vi njivama svojim prilazite kako hoćete! A pripremite nešto i za duše svoje! I bojte se Allaha i znajte da ćete se s Njim susresti! A ti obraduj vjernike!');
INSERT INTO "bs_darassalam" VALUES(2,224,'2:224','I neka vam zakletva Allahom ne bude prepreka da dobro činite, da se Allaha bojite i ljude mirite. A Allah je Onaj Koji sve čuje i Onaj Koji sve zna!');
INSERT INTO "bs_darassalam" VALUES(2,225,'2:225','Allah vas neće kazniti ako se neoprezno zakunete, ali će vas kazniti za ono što s predumišljajem srca vaših uradite. Allah je Onaj Koji oprašta grijehe i Onaj Koji ne žuri s kaznom.');
INSERT INTO "bs_darassalam" VALUES(2,226,'2:226','Oni koji se zakunu da se neće približavati ženama svojim, sačekat će četiri mjeseca! Pa ako im se vrate, Allah je, doista, Onaj Koji oprašta grijehe i milostiv je.');
INSERT INTO "bs_darassalam" VALUES(2,227,'2:227','A ako odluče da se rastave, pa Allah je, doista, Onaj Koji sve čuje i Onaj Koji sve zna!');
INSERT INTO "bs_darassalam" VALUES(2,228,'2:228','Puštene žene neka čekaju tri mjesečna pranja; i nije im dopušteno da kriju ono što je Allah stvorio u matericama njihovim, ako u Allaha i u ahiret vjeruju! Muževi njihovi su preči da ih vrate dok su u tome, ako žele pomirenje. One imaju onoliko prava koliko i obaveza; treba da ih izvrše lijepo i po propisu. A muškarci su nad njima za jedan stepen! Allah je, doista, silan i mudar.');
INSERT INTO "bs_darassalam" VALUES(2,229,'2:229','Puštanje supruge može biti dva puta, nakon čega se ona, ili lijepo i pravedno zadržava, ili joj se uz dobročinstvo razvod daje! A vama nije dopušteno uzimati bilo šta od onoga što ste im darovali, osim ako se njih dvoje plaše da Allahove propise neće izvršavati. A ako se bojite da njih dvoje Allahove propise neće izvršavati, onda im nije grijeh da se ona otkupi. To su Allahove granice, pa ih ne prekoračujte! A oni koji Allahove granice prekoračuju, to su pravi zulumćari!');
INSERT INTO "bs_darassalam" VALUES(2,230,'2:230','A ako je opet pusti, ona mu nije nakon toga dopuštena sve dok se ne uda za drugog muža. Pa ako je i on pusti, onda njima dvoma nije grijeh da se jedno drugom vrate, ako misle da će Allahove propise izvršavati. To su Allahove granice koje On objašnjava ljudima koji znaju.');
INSERT INTO "bs_darassalam" VALUES(2,231,'2:231','Kada pustite supruge, pa one ispune svoj razvodni rok, tada ih, ili lijepo i pravedno zadržite, ili uz dobročinstvo otpremite! I ne zadržavajte ih nanoseći im štetu, a onaj ko tako postupi, učinio je sebi zulum! Ne poigravajte se Allahovim propisima i sjetite se blagodati Allahove prema vama i što vam je objavio Knjigu i mudrost kojom vas savjetuje! Allaha se bojte i znajte da Allah sve zna!');
INSERT INTO "bs_darassalam" VALUES(2,232,'2:232','A kada pustite supruge, pa ispune svoj razvodni rok, ne ometajte ih da se udaju za muževe svoje, kada se slože da lijepo žive! Ovim se savjetuju oni među vama koji vjeruju u Allaha i Posljednji dan. Tako vam je čednije i čestitije. A Allah zna, a vi ne znate.');
INSERT INTO "bs_darassalam" VALUES(2,233,'2:233','Majke neka doje djecu svoju pune dvije godine, ako žele da dojenje bude potpuno! Očevi su dužni hraniti ih i odijevati na lijep i uobičajen način. Niko se ne zadužuje iznad mogućnosti svojih: majka ne smije naštetiti ocu djeteta zbog djeteta svoga, a ni otac majci djeteta zbog svoga djeteta! Nasljednik ima iste obaveze. Zato, ako njih dvoje na lijep način i po dogovoru odluče dijete odbiti, nisu grešni! A ako zaželite svojoj djeci dojilje naći, nije vam grijeh ako im, kako je uobičajeno, date ono što hoćete dati. Bojte se Allaha i znajte da Allah dobro vidi šta vi radite!');
INSERT INTO "bs_darassalam" VALUES(2,234,'2:234','Oni od vas koji umru ostavljajući supruge svoje, njihove supruge dužnesu čekati četiri mjeseca i deset dana. I kada one ispune propisani rok, vine snosite grijeh za ono što one na lijep i uobičajen način urade. A Allahu potpunosti zna ono što vi radite.');
INSERT INTO "bs_darassalam" VALUES(2,235,'2:235','I nije vam grijeh ako tim ženama na znanje date da ćete ih vi zaprositi iliako to u dušama svojim krijete. Allah zna da ćete o njima misliti, ali impotajno ništa ne obećavajte, nego im samo što je dozvoljeno govorite. I ne odlučujte se na brak prije nego što propisano vrijeme za čekanje ne istekne; i imajte na umu da Allah zna šta je u dušama vašim, pa Ga se pričuvajte, i znajte da je Allah, zaista, Onaj Koji oprašta grijehe i Onaj Koji ne žuri s kaznom.');
INSERT INTO "bs_darassalam" VALUES(2,236,'2:236','Nije vam grijeh ako žene pustite prije nego u intimni odnos s njima stupite ili prije nego što im vjenčani dar odredite. I na lijep i uobičajen način ih darujte, imućan prema svom stanju, a siromah prema svom; to je dužnost onima koji žele dobro činiti.');
INSERT INTO "bs_darassalam" VALUES(2,237,'2:237','A ako ih pustite prije nego što ste u intimni odnos s njima stupili, a već ste im vjenčani dar odredili, one će zadržati polovinu onoga što ste odredili, osim ako se odreknu ili se odrekne onaj koji odlučuje o sklapanju braka; a ako se vi odreknete – to je bliže bogobojaznosti! I ne zaboravite da jedni prema drugima dobri budete! Allah, doista, dobro vidi šta vi radite.');
INSERT INTO "bs_darassalam" VALUES(2,238,'2:238','Čuvajte namaz, naročito srednji namaz, i pred Allahom ponizno stojte!');
INSERT INTO "bs_darassalam" VALUES(2,239,'2:239','Ako se budete bojali, onda klanjajte hodeći ili jašući! A kada budete sigurni, spominjite Allaha onako kako vas je On naučio onome šta niste znali.');
INSERT INTO "bs_darassalam" VALUES(2,240,'2:240','Oni među vama kojima se primiče smrt, a koji iza sebe ostavljaju supruge, treba da im oporukom, unaprijed za godinu dana, odrede izdržavanje i da se one ne udaljuju iz kuće. A ako je same napuste, vi ne snosite grijeh za ono što one lijepo i po propisu sa sobom učine. A Allah je silan i mudar!');
INSERT INTO "bs_darassalam" VALUES(2,241,'2:241','Razvedenim ženama pripada otpremnina u skladu s propisom i na lijep način, i to je obaveza bogobojaznih.');
INSERT INTO "bs_darassalam" VALUES(2,242,'2:242','Allah vam tako objašnjava propise Svoje, da biste razmislili!');
INSERT INTO "bs_darassalam" VALUES(2,243,'2:243','Zar ne znaš za one koji su iz straha od smrti iz zemlje svoje pobjegli, a bijaše ih na hiljade, pa im je Allah rekao: "Pomrite!", a poslije ih je oživio. Allah je, doista, darežljiv prema ljudima, ali većina ljudi ne zahvaljuje.');
INSERT INTO "bs_darassalam" VALUES(2,244,'2:244','I borite se na Allahovu putu i znajte da je Allah Onaj Koji sve čuje i Onaj Koji sve zna.');
INSERT INTO "bs_darassalam" VALUES(2,245,'2:245','Ko je taj koji će Allahu lijep zajam dati, pa da mu ga On mnogostruko vrati, Allah uskraćuje i obilno daje, i Njemu ćete se vratiti.');
INSERT INTO "bs_darassalam" VALUES(2,246,'2:246','Zar ne znaš kada su prvaci sinova Israilovih poslije Musaa svome vjerovjesniku rekli: "Pošalji nam kralja da se na Allahovom putu borimo!" "Možda se vi nećete boriti, ako vam borba bude propisana?'''', reče on. "Zašto da se ne borimo na Allahovom putu?'''', rekoše. "A mi smo iz zemlje naše prognani i od sinova naših odvojeni!" A kada im borba bi propisana, oni, osim nekolicine, okrenuše se. A Allah dobro zna zulumćare.');
INSERT INTO "bs_darassalam" VALUES(2,247,'2:247','"Allah vam je Taluta za vladara poslao", reče im vjerovjesnik njihov. "Odakle da nam on bude vladar kada smo mi priličniji od njega da vladamo? Njemu ni veliko bogatstvo nije dato!", rekoše oni. "Doista je Allah njega da vam vlada izabrao", reče on, "i velikim znanjem i snagom tjelesnom ga obdario. Allah daje vlast kome On hoće. Allah je neizmjerno darežljiv i Onaj Koji sve zna!');
INSERT INTO "bs_darassalam" VALUES(2,248,'2:248','"Zaista, znak njegove vlasti", reče im vjerovjesnik njihov, "bit će kovčeg koji će vam stići. U njemu će biti smirenje za vas od Gospodara vašega i ostatak onoga što su Musa i Harun ostavili, nosit će ga meleci! U tome je, doista, znak za vas, ako ste vjernici!"');
INSERT INTO "bs_darassalam" VALUES(2,249,'2:249','I kad Talut izađe s vojskom, reče: "Allah će vas sigurno staviti na kušnju jednom rijekom, pa ko se napije iz nje, nije moj, a ko se ne napije, on je moj, izuzev ako šakom zahvati gutljaj!" I oni se, osim nekolicine, napiše, a kad je oni koji su s njim vjerovali prijeđoše, neki rekoše: "Mi danas ne možemo izaći nakraj s Džalutom i vojskom njegovom." Oni koji su čvrsto vjerovali da će susresti Allaha, rekoše: "Koliko su puta malobrojne grupe savladale grupe mnogobrojne Allahovom dozvolom." A Allah je sa strpljivima.');
INSERT INTO "bs_darassalam" VALUES(2,250,'2:250','I kada iziđoše pred Džaluta i vojsku njegovu, oni rekoše: "Gospodaru naš, nadahni nas izdržljivošću i učvrsti noge naše i pomozi nam protiv naroda koji ne vjeruje!"');
INSERT INTO "bs_darassalam" VALUES(2,251,'2:251','I oni ih, Allahovom dozvolom, poraziše, i Davud ubi Džaluta, i Allah mu dade vlast i mudrost, i nauči ga onome čemu je On htio. A da Allah ljude ne suzbija jedne drugima, na Zemlji bi doista nered nastao! Ali, Allah je darežljiv svim svjetovima.');
INSERT INTO "bs_darassalam" VALUES(2,252,'2:252','To su Allahovi ajeti, koje Mi tebi istinito kazujemo. A ti si, doista, jedan od poslanika.');
INSERT INTO "bs_darassalam" VALUES(2,253,'2:253','Te poslanike Mi smo odlikovali jedne nad drugim. S nekima od njih je i Allah govorio, a neke je za više stepeni uzdigao. A Isau, sinu Merjeminu, jasne smo dokaze dali i Duhom Blagoslovljenim ga podržali. Da je Allah htio, oni se ne bi međusobno poslije njih ubijali, kada su im jasni dokazi već došli, ali oni su se razišli: neki su od njih vjerovali, a neki su poricali. A da je Allah htio, oni se ne bi međusobno ubijali! Ali, Allah radi ono što On hoće.');
INSERT INTO "bs_darassalam" VALUES(2,254,'2:254','O vi koji vjerujete, podijelite dio onoga čime vas Mi opskrbljujemo, prije nego što dođe Dan kada neće biti ni otkupa, ni prijateljstva, ni posredništva! A nevjernici su pravi zulumćari!');
INSERT INTO "bs_darassalam" VALUES(2,255,'2:255','Allah je, nema boga osim Njega, Živi i Samoopstojeći Održavatelj! Ne obuzima Ga ni drijemež ni san! Njegovo je ono što je na nebesima i na Zemlji! Ko se može pred Njim zauzimati bez Njegovog dopuštenja?! On zna šta je bilo prije i šta će biti poslije njih i oni ne mogu obuhvatiti od Njegovoga znanja osim koliko On želi. Njegov Kursi obuhvata i nebesa i Zemlju i Njemu nije teško održavati ih! On je Svevišnji i Veličanstveni!');
INSERT INTO "bs_darassalam" VALUES(2,256,'2:256','Nema prisile u vjeru, Pravi put se jasno razlikuje od zablude! Onaj ko ne vjeruje u tagute, a vjeruje u Allaha, drži se za najčvršću vezu, koja se neće prekinuti. A Allah je Onaj Koji sve čuje i Onaj Koji sve zna.');
INSERT INTO "bs_darassalam" VALUES(2,257,'2:257','Allah je zaštitnik onih koji vjeruju, izvodi ih iz tmina na svjetlo, a onima koji ne vjeruju zaštitnici su taguti, odvode ih sa svjetla u tmine! To su stanovnici Vatre i u njoj će vječno ostati.');
INSERT INTO "bs_darassalam" VALUES(2,258,'2:258','Zar ne znaš za onoga koji se s Ibrahimom o Gospodaru njegovom prepirao, kada mu je Allah carstvo dao?! Kad Ibrahim reče: "Gospodar moj je Onaj Koji život i smrt daje!", on odgovori: "Ja život i smrt dajem!" Ibrahim reče: "Allah čini da Sunce izlazi s istoka, pa učini ti da izađe sa zapada!" I nevjernik se zbuni. Allah, doista, zulumćare ne upućuje na Pravi put.');
INSERT INTO "bs_darassalam" VALUES(2,259,'2:259','Ili za onoga koji je, prolazeći pored jednoga naselja koje je bilo do temelja porušeno i pusto, povikao: "Kako će Allah oživiti ove što su pomrli!" I Allah ga mrtvim učini stotinu godina, a zatim ga oživi i upita: "Koliko si ostao?" On reče: "Ostao sam dan ili dio dana!" Tada mu reče: "Ne, ostao si stotinu godina! Pogledaj jelo svoje i piće svoje, nije se pokvarilo! A pogledaj i magarca svoga, to smo uradili da te učinimo znakom za ljude, a pogledaj i kosti, kako ih sastavljamo, a onda mesom oblažemo!" I kada njemu bi jasno, povika: "Ja znam da Allah, doista, nad svime ima moć!"');
INSERT INTO "bs_darassalam" VALUES(2,260,'2:260','A kada Ibrahim reče: "Gospodaru moj, pokaži mi kako oživljuješ umrle?", On reče: "Zar ne vjeruješ?!" "Dakako, vjerujem", odgovori on, "ali da mi se srce smiri!" Zatim mu reče: "Pa, onda, uzmi četiri ptice, isijeci ih, pa po jedan dio njihov stavi na svaki brežuljak. Zatim ih pozovi, doći će ti žureći! Znaj da je Allah, zaista, silan i mudar."');
INSERT INTO "bs_darassalam" VALUES(2,261,'2:261','Primjer onih koji imovinu svoju troše na Allahovom putu sličan je zrnu iz kojega nikne sedam klasova, a u svakom klasu po stotinu zrna. A Allah povećava kome On hoće. Allah je neizmjerno darežljiv i Onaj Koji sve zna.');
INSERT INTO "bs_darassalam" VALUES(2,262,'2:262','Onima koji troše imetke svoje na Allahovom putu, a onda ono što potroše ne poprate prigovorima i uvredama, pripada nagrada Gospodara njihova. Njima nema straha niti će oni tugovati.');
INSERT INTO "bs_darassalam" VALUES(2,263,'2:263','Lijepa riječ i oprost vredniji su od sadake popraćene vrijeđanjem. A Allah je neovisan i Onaj Koji ne žuri s kaznom.');
INSERT INTO "bs_darassalam" VALUES(2,264,'2:264','O vi koji vjerujete, ne kvarite sadake svoje prigovorima i uvredama, kao što to čini onaj koji imetak svoj troši da bi se ljudima pokazao, a ne vjeruje u Allaha i ahiret. Primjer njegov je kao primjer glatke stijene – na kojoj ima zemlje, pa je pogodi veliki pljusak i ogoli je. Oni nemaju nikakve koristi od onoga što su stjecali. A Allah ne upućuje na Pravi put ljude koji ne vjeruju.');
INSERT INTO "bs_darassalam" VALUES(2,265,'2:265','Oni koji troše svoj imetak u želji za zadovoljstvom Allahovim i sigurni su u nagradu za to, slični su bašči na uzvišici na koju se izlila obilna kiša, pa daje dvostruki urod! A ako se na nju ne izlije obilna kiša, opet bude rose! Allah dobro vidi šta vi radite!');
INSERT INTO "bs_darassalam" VALUES(2,266,'2:266','Da li bi iko od vas volio da posjeduje bašču punu palmi i grožđa, kroz koju rijeke teku i u kojoj ima svakojakih plodova, a da je ostario i da ima nejaku djecu, a da je onda vatrena oluja zadesi i ona izgori?! Tako vam Allah objašnjava ajete i znakove Svoje, da biste razmislili.');
INSERT INTO "bs_darassalam" VALUES(2,267,'2:267','O vi koji vjerujete, dijelite od lijepih imetaka koje ste stekli, i od onoga što smo vam Mi iz Zemlje izveli! Ne izdvajajte ono što ne vrijedi da biste to udijelili, kada ni sami to ne biste primili, osim zatvorenih očiju! I znajte da je Allah, zaista, Neovisni i Hvaljeni.');
INSERT INTO "bs_darassalam" VALUES(2,268,'2:268','Šejtan vam obećava siromaštvo i naređuje vam zlo, a Allah vam obećava oprost i dobro. Allah je neizmjerno darežljiv i Onaj Koji sve zna.');
INSERT INTO "bs_darassalam" VALUES(2,269,'2:269','On daje mudrost kome hoće, a onome kome je mudrost data, dato je veliko dobro! A samo razumom obdareni prisjećaju se i pouku prihvataju.');
INSERT INTO "bs_darassalam" VALUES(2,270,'2:270','Za sve što potrošite ili se zavjetujete Allah sigurno zna! A zulumćarima nema pomagača!');
INSERT INTO "bs_darassalam" VALUES(2,271,'2:271','Ako javno dajete sadake to je lijepo, ali je za vas bolje ako to prikrijete i date siromasima, i On će pokriti neka vaša loša djela. A Allah dobro zna ono što vi radite.');
INSERT INTO "bs_darassalam" VALUES(2,272,'2:272','Što se upute njihove tiče, ne ovisi to o tebi, jer Allah izvodi na Pravi put koga On hoće. Dobro koje podijelite vaše je, a ono što dijelite, dijelite samo u želji za licem Allahovim. A ono što od dobara udijelite, to će vam biti nadoknađeno i neće vam zulum biti učinjen.');
INSERT INTO "bs_darassalam" VALUES(2,273,'2:273','To pripada siromasima koji su iznureni na Allahovom putu, pa ne mogu hoditi po Zemlji; onaj ko ne zna misli da su imućni zbog sustezljivosti njihove. Poznat ćeš ih po izgledu njihovu, jer oni ne dodijavaju ljudima proseći. A za ono što od dobara podijelite, Allah, doista, dobro zna.');
INSERT INTO "bs_darassalam" VALUES(2,274,'2:274','Onima koji dijele imovinu svoju noću i danju, tajno i javno, pripada nagrada kod Gospodara njihova. I za njih nema straha, niti će se oni žalostiti.');
INSERT INTO "bs_darassalam" VALUES(2,275,'2:275','Oni koji se kamatom hrane dići će se iz grobova svojih baš kao oni koje je šejtan dodirom izbezumio. To je zato što su govorili: "Trgovina je, doista, isto što i kamata!" A Allah je dozvolio trgovinu, a zabranio kamatu. Onome do koga dopre pouka Gospodara njegova, pa se toga okani, pripada ono što je prije stekao a njegov je slučaj Allahu prepušten. Oni, pak, koji opet to učine, bit će stanovnici Vatre u kojoj će vječno ostati.');
INSERT INTO "bs_darassalam" VALUES(2,276,'2:276','Allah uništava kamatu, a umnožava sadake. Allah ne voli ni jednog nevjernika, grešnika.');
INSERT INTO "bs_darassalam" VALUES(2,277,'2:277','Doista će oni koji vjeruju, i dobra djela čine, i namaz obavljaju, i zekat daju, imati nagradu u Gospodara svoga. Za njih nema straha i oni ne treba da se žaloste!');
INSERT INTO "bs_darassalam" VALUES(2,278,'2:278','O vi koji vjerujete, bojte se Allaha i ostavite što je preostalo od kamate, ako ste vjernici.');
INSERT INTO "bs_darassalam" VALUES(2,279,'2:279','Ako to ne učinite, navješćuje vam se rat od Allaha i Poslanika Njegova! A ako se pokajete, pripadaju vam glavnice imetaka vaših; nećete nepravdu učiniti, niti će vama zulum biti učinjen.');
INSERT INTO "bs_darassalam" VALUES(2,280,'2:280','A ako je dužnik u nevolji, onda pričekajte dok mu bude lakše; a da to date kao sadaku bolje vam je, ako znate.');
INSERT INTO "bs_darassalam" VALUES(2,281,'2:281','I bojte se Dana u kome ćete Allahu vraćeni biti, zatim će svakome što je zaslužio isplaćeno biti. I nikome zulum neće biti učinjen.');
INSERT INTO "bs_darassalam" VALUES(2,282,'2:282','O vi koji vjerujete, kada jedan od drugoga pozajmljujete do određenoga roka, zapišite to. Neka jedan pisar između vas to pravedno napiše i neka se pisar ne susteže napisati onako kako ga je Allah poučio. Neka on piše, a neka mu kazuje dužnik! Neka se boji Allaha, Gospodara svoga, i ništa od toga neka ne izostavi. A ako je dužnik umno ograničen ili slab, ili ako nije u stanju kazivati, onda neka kazuje njegov staratelj i to pravedno. I uzmite dva svjedoka između vas, pa ako ne nađete dva muškarca, onda jednog muškarca i dvije žene sa kojima ste kao svjedocima zadovoljni. Ako jedna od njih dvije pogrešno šta kaže, neka je druga podsjeti. I neka se svjedoci ne ustežu, ako budu pozvani, da dođu. Neka vam ne bude mrsko da to zapišete s rokom vraćanja, bio dug mali ili veliki! To vam je kod Allaha ispravnije i za svjedočenje jače, i da ne sumnjate bolje, izuzev ako je riječ o svakodnevnoj trgovini koju obavljate među vama, jer onda vam nije grijeh da to ne zapišete. Uzmite svjedoke i kada kupoprodaju zaključujete, i neka ne bude oštećen ni pisar ni svjedok. A ako to učinite - oštetite ih, onda je to vaš grijeh. I bojte se Allaha i Allah će vas poučiti! Allah sve dobro zna.');
INSERT INTO "bs_darassalam" VALUES(2,283,'2:283','Ako ste na putu i ne nađete pisara, onda neka se uzme zalog. A ako jedan drugome nešto povjerite, neka onaj kome je povjereno, to povjerenje ispuni i neka se boji Allaha, Gospodara svoga! I ne skrivajte svjedočenje, jer ko ga uskrati, srce njegovo je, doista, grešno. A Allah dobro zna ono što vi radite.');
INSERT INTO "bs_darassalam" VALUES(2,284,'2:284','Allahu pripada sve što je na nebesima i što je na Zemlji. Pokazivali vi šta je u dušama vašim ili skrivali, Allah će vas za to pitati, pa će oprostiti kome hoće, a kazniti koga hoće. A Allah nad svime ima moć.');
INSERT INTO "bs_darassalam" VALUES(2,285,'2:285','Poslanik vjeruje u ono što mu se objavljuje od Gospodara njegova, a i vjernici, svaki od njih vjeruje u Allaha, i meleke Njegove, i Knjige Njegove, i poslanike Njegove: "Mi među poslanicima Njegovim ne pravimo razliku!" I govore: "Čujemo i pokoravamo se! Molimo Te za oprost, Gospodaru naš, a kod Tebe je konačno odredište!"');
INSERT INTO "bs_darassalam" VALUES(2,286,'2:286','Allah svaku osobu obavezuje samo onoliko kolike su njene mogućnosti; njoj u korist je ono dobro koje zaradi, a na njenu štetu je ono zlo koje sebi privrijedi. Gospodaru naš, nemoj nas kazniti ako zaboravimo ili nehotice pogriješimo! Gospodaru naš, ne tovari na nas breme kao što si ga natovario na one prije nas! Gospodaru naš, ne opterećuj nas onim za što nemamo snage! Preko grijeha naših prijeđi, oprosti nam i smiluj nam se! Ti si Gospodar naš, pa nas pomozi protiv naroda koji ne vjeruje!');
INSERT INTO "bs_darassalam" VALUES(3,1,'3:1','Elif Lām Mīm');
INSERT INTO "bs_darassalam" VALUES(3,2,'3:2','Allah, osim Koga drugog Boga nema, Živi je i Samoopstojeći Održavatelj.');
INSERT INTO "bs_darassalam" VALUES(3,3,'3:3','On tebi objavljuje Knjigu s Istinom, koja potvrđuje prethodne, a i Tevrat i Indžil je objavio,');
INSERT INTO "bs_darassalam" VALUES(3,4,'3:4','još prije kao putokaz ljudima, a spustio je i Furkan! – ono što rastavlja istinu od zablude. Onima koji u Allahove ajete i znakove ne vjeruju, pripada teška patnja, a Allah je silan i žestoko kažnjava.');
INSERT INTO "bs_darassalam" VALUES(3,5,'3:5','Allahu, doista, ništa nije skriveno, ni na Zemlji, ni na nebu.');
INSERT INTO "bs_darassalam" VALUES(3,6,'3:6','On je Onaj Koji vas oblikuje u matericama kako On hoće; nema drugog boga osim Njega, Silnog i Mudrog.');
INSERT INTO "bs_darassalam" VALUES(3,7,'3:7','On je Onaj Koji ti objavljuje Knjigu u kojoj su ajeti jasni; oni su matica Knjige; drugi nisu sasvim jasni. Oni u čijim je srcima zastranjenost slijede one što nisu sasvim jasni u težnji za smutnjom i svojevoljnim tumačenjem. A tumačenje njihovo zna samo Allah. Oni, pak, koji su u znanje duboko pronikli, kažu: "Mi u to vjerujemo; sve je to od našega Gospodara." A prisjećaju se samo oni koji razum imaju.');
INSERT INTO "bs_darassalam" VALUES(3,8,'3:8','"Gospodaru naš, ne dopusti srcima našim da skrenu, kada si nam već na Pravi put ukazao, i daruj nam Svoju milost; Ti si, uistinu, Onaj Koji mnogo daruje!');
INSERT INTO "bs_darassalam" VALUES(3,9,'3:9','Gospodaru naš, Ti ćeš, doista, sakupiti ljude na Dan u koji nema sumnje! Allah, doista, ne krši obećanje Svoje!"');
INSERT INTO "bs_darassalam" VALUES(3,10,'3:10','Onima koji ne vjeruju, kod Allaha sigurno neće koristiti imeci njihovi, a ni djeca njihova, i oni su gorivo za Vatru.');
INSERT INTO "bs_darassalam" VALUES(3,11,'3:11','Kao što su to i faraonovi ljudi, te oni prije njih koji su znakove naše lažnim smatrali, pa ih je Allah zbog grijeha njihovih kaznio. A Allah žestoko kažnjava.');
INSERT INTO "bs_darassalam" VALUES(3,12,'3:12','Reci onima koji ne vjeruju: "Bit ćete pobijeđeni i u džehennemu okupljeni, a užasno je to boravište!"');
INSERT INTO "bs_darassalam" VALUES(3,13,'3:13','Vi ste imali znak u dvjema grupama koje su se sukobile: jednoj koja se borila na Allahovom putu, i drugoj, nevjerničkoj. Oni su ih vidjeli dvostruko brojnijim od sebe. A Allah Svojom pomoći podržava koga On hoće! To je, doista, pouka za one koji su razboriti.');
INSERT INTO "bs_darassalam" VALUES(3,14,'3:14','Ljudima je uljepšana ljubav prema strastima; ženama, sinovima, gomilama zlata i srebra, rasnim konjima, stoci i zemlji za obrađivanje. To su užici ovosvjetskoga života, a najljepše je mjesto povratka kod Allaha.');
INSERT INTO "bs_darassalam" VALUES(3,15,'3:15','Reci: "Hoćete li da vas obavijestim šta je bolje od toga?" Oni koji se budu uščuvali, kod Gospodara svoga imat će džennetske bašče kroz koje rijeke teku, u kojima će vječno boraviti, i supruge očišćene i zadovoljstvo od Allaha. Allah dobro vidi robove Svoje.');
INSERT INTO "bs_darassalam" VALUES(3,16,'3:16','To su oni koji govore: "Gospodaru naš, mi doista vjerujemo, pa oprosti nam grijehe naše i sačuvaj nas od patnje u Vatri!"');
INSERT INTO "bs_darassalam" VALUES(3,17,'3:17','Strpljivi, i iskreni, i Allahu poslušni, i koji od imetka dijele, i koji u osvit zore za oprost mole.');
INSERT INTO "bs_darassalam" VALUES(3,18,'3:18','Allah svjedoči da nema boga osim Njega, a i meleki i učeni, postupajući pravedno. Nema boga osim Njega, Silnog i Mudrog.');
INSERT INTO "bs_darassalam" VALUES(3,19,'3:19','Vjera je kod Allaha, doista, islam! A oni kojima je data Knjiga podvojili su se baš onda kada im je došlo znanje, i to iz međusobne zlobe. A onaj ko ne vjeruje u ajete i znakove Allahove – pa, Allah, zaista, brzo sviđa račune.');
INSERT INTO "bs_darassalam" VALUES(3,20,'3:20','Ako se oni budu prepirali s tobom, reci: "Ja se samo Allahu predajem, kao i oni koji me slijede!" I reci onima kojima je data Knjiga i nepismenima: "Da li ste islam primili?" Pa ako prime islam, onda su oni na Pravom putu, a ako odbiju, tvoje je jedino da obznaniš. A Allah dobro vidi robove Svoje.');
INSERT INTO "bs_darassalam" VALUES(3,21,'3:21','Onima koji ne vjeruju u znakove Allahove i koji vjerovjesnike ubijaju neopravdano, i ubijaju one koji traže da se po pravdi postupa prema ljudima, navijesti bolnu patnju.');
INSERT INTO "bs_darassalam" VALUES(3,22,'3:22','To su oni čija djela su poništena i na dunjaluku i na ahiretu, i kojima niko neće u pomoć priteći.');
INSERT INTO "bs_darassalam" VALUES(3,23,'3:23','Zar ne vidiš one kojima je dat udio u Knjizi?! Pozivaju se Allahovoj Knjizi da im se po njoj sudi, a zatim se neki od njih, okrećući se od toga, udaljavaju.');
INSERT INTO "bs_darassalam" VALUES(3,24,'3:24','To je zato što oni govore: "Vatra nas neće ni dodirnuti više od nekoliko dana!", i u njihovoj vjeri ih zavarava to što su izmišljali.');
INSERT INTO "bs_darassalam" VALUES(3,25,'3:25','A šta će biti kada ih sakupimo onoga Dana u koji nema sumnje, i kada svako dobije ono što je zaslužio? A njima zulum neće biti učinjen.');
INSERT INTO "bs_darassalam" VALUES(3,26,'3:26','Reci: "Allahu moj! Koji svu vlast imaš! Ti vlast daješ kome hoćeš, a oduzimaš od koga Ti hoćeš! Ti uzvisuješ koga hoćeš, a unizuješ koga hoćeš! U Tvojoj ruci je dobro! Ti, doista, nad svime imaš moć."');
INSERT INTO "bs_darassalam" VALUES(3,27,'3:27','Ti uvodiš noć u dan i uvodiš dan u noć! Živo izvodiš iz mrtvog i mrtvo izvodiš iz živog! Ti opskrbljuješ koga hoćeš, bez ikakva računa!');
INSERT INTO "bs_darassalam" VALUES(3,28,'3:28','Neka vjernici za prisne prijatelje i zaštitnike ne uzimaju nevjernike mimo vjernika, a ko to čini, s Allahom nema ništa! Učinite to samo ako se time od njih štitite! Allah vas na Sebe upozorava, a kod Allaha je konačno odredište.');
INSERT INTO "bs_darassalam" VALUES(3,29,'3:29','Reci: "Skrivali vi ono što je u prsima vašim ili pokazivali, Allah to zna. On zna sve što je na nebesima i što je na Zemlji. Allah nad svime ima moć!"');
INSERT INTO "bs_darassalam" VALUES(3,30,'3:30','Onoga dana kada svako ugleda pred sobom dobro koje je uradio, a i zlo isto tako, poželjet će da je između njih i njega udaljenost velika. A Allah vas na Sebe upozorava i Allah je milosrdan prema robovima Svojim.');
INSERT INTO "bs_darassalam" VALUES(3,31,'3:31','Reci: "Ako vi Allaha volite, onda mene slijedite, i vas će Allah voljeti i grijehe vam oprostiti! A Allah je Onaj Koji oprašta grijehe i milostiv je."');
INSERT INTO "bs_darassalam" VALUES(3,32,'3:32','Reci: "Pokoravajte se Allahu i Poslaniku!" A ako se oni okrenu, Allah, doista, ne voli nevjernike.');
INSERT INTO "bs_darassalam" VALUES(3,33,'3:33','Doista je Allah odabrao Adema, i Nuha, i porodicu Ibrahimovu, i porodicu Imranovu nad drugim svjetovima,');
INSERT INTO "bs_darassalam" VALUES(3,34,'3:34','kao potomstvo, jedne od drugih. Allah je Onaj Koji sve čuje i Onaj Koji sve zna.');
INSERT INTO "bs_darassalam" VALUES(3,35,'3:35','Kada Imranova žena reče: "Gospodaru moj, ovo što je u trbuhu mome ja zaista zavjetujem Tebi na službu, pa primi to od mene, zbilja si Ti Svečujući i Sveznajući."');
INSERT INTO "bs_darassalam" VALUES(3,36,'3:36','A nakon što je rodila, ona reče: “Gospodaru moj, rodila sam žensko!” – a Allah najbolje zna šta je ona rodila, a muško nije kao žensko. “Dalasam joj ime Merjema. Ja nju i porod njezin stavljam pod Tvoju zaštitu od prokletog šejtana.”');
INSERT INTO "bs_darassalam" VALUES(3,37,'3:37','Pa je Gospodar njezin lijepo primi, i učini da lijepo izraste i da se o njoj Zekerijja brine. Kad god bi joj Zekerijja u mihrab ušao, kod nje bi hrane našao. "Odakle ti ovo, Merjema?", pitao bi on, a ona bi odgovorila: "To je od Allaha! Allah, doista, opskrbljuje koga hoće, bez ikakva računa."');
INSERT INTO "bs_darassalam" VALUES(3,38,'3:38','Tu Zekerijja zamoli Gospodara svoga: "Gospodaru moj, podari mi od Sebe čestita potomka, Ti doista molbu čuješ!"');
INSERT INTO "bs_darassalam" VALUES(3,39,'3:39','I dok se on, stojeći u odaji, molio, meleci ga pozvaše i rekoše: “Allah teraduje Jahjaom, koji će vjerovati u onog koji je Riječ Allahova, prvakom, čednim čovjekom i vjerovjesnikom, potomkom čestitih!”');
INSERT INTO "bs_darassalam" VALUES(3,40,'3:40','"Gospodaru moj", reče on, "odakle ću ja imati sina kada me starost ophrvala, a i žena mi je nerotkinja?" "Eto tako", reče On, "Allah čini što On hoće."');
INSERT INTO "bs_darassalam" VALUES(3,41,'3:41','"Gospodaru moj", zamoli Zekerijja, "daj mi neki znak!" "Znak će biti", reče On, "što tri dana s ljudima nećeš govoriti, osim išaretorn! I mnogo spominji Gospodara svoga i hvali Ga krajem dana i rano ujutro."');
INSERT INTO "bs_darassalam" VALUES(3,42,'3:42','I kada meleci kazaše: "O Merjem, tebe je, doista, Allah odabrao i čistom učinio! On te je nad ženama svih svjetova odabrao!"');
INSERT INTO "bs_darassalam" VALUES(3,43,'3:43','"Merjema, budi skrušena i pokorna Gospodaru svome, i sedždu i ruku čini s onima koji u namazu ruku čine."');
INSERT INTO "bs_darassalam" VALUES(3,44,'3:44','To su neke od vijesti o onome što čulima nije dokučivo, a koje ti objavljujemo. Ti nisi bio među njima kada su pera svoja pobacali da bi vidjeli koji će se o Merjemi brinuti, a nisi bio među njima ni kada su se prepirali.');
INSERT INTO "bs_darassalam" VALUES(3,45,'3:45','I kada meleci rekoše: "Merjema, Allah te obveseljava onim koji je Riječ Njegova, čije je ime Mesih, Isa, sin Merjemin, ugledan i na dunjaluku i na ahiretu i jedan od Allahu bliskih!');
INSERT INTO "bs_darassalam" VALUES(3,46,'3:46','On će govoriti ljudima i u kolijevci, i kao odrastao, i on je jedan od dobrih!"');
INSERT INTO "bs_darassalam" VALUES(3,47,'3:47','Ona reče: "Gospodaru moj, kako ću ja imati dijete kada me muškarac nije dodirnuo?!" "Eto, tako", reče, "Allah stvara šta On hoće. Kada nešto odredi, On samo kaže: ''Budi!'' i to biva."');
INSERT INTO "bs_darassalam" VALUES(3,48,'3:48','On će ga poučiti Knjizi i mudrosti, i Tevratu i Indžilu.');
INSERT INTO "bs_darassalam" VALUES(3,49,'3:49','I poslat će ga kao poslanika sinovima Israilovim: "Donio sam vam znak od Gospodara vašega: oblikovat ću vam od ilovače nešto poput ptice, i u nju puhnuti, pa će, Allahovom dozvolom, ptica biti; iscijelit ću slijepa od rođenja i gubava, i oživljavat ću mrtve dozvolom Allahovom; i kazivat ću vam šta jedete i šta u domovima vašim pohranjujete. U svemu tome ima dokaza za vas, ako vjernici budete.');
INSERT INTO "bs_darassalam" VALUES(3,50,'3:50','Potvrđujući tako istinitost Tevrata, objavljenog prije mene, i dopuštajući nešto što vam je bilo zabranjeno, ja vam donosim dokaz od Gospodara vašega. Pa Allaha se bojte, a meni pokorni budite!');
INSERT INTO "bs_darassalam" VALUES(3,51,'3:51','Allah je doista i moj i vaš Gospodar i Njemu ibadet činite. To je Pravi put!');
INSERT INTO "bs_darassalam" VALUES(3,52,'3:52','I kada je Isa osjetio da oni ne vjeruju, rekao je: "Ko su pomagači moji na putu prema Allahu?" "Mi smo pomagači Allahovi", rekoše učenici, "mi u Allaha vjerujemo, a ti budi svjedok da smo mi muslimani – predani."');
INSERT INTO "bs_darassalam" VALUES(3,53,'3:53','Gospodaru naš, mi vjerujemo u ono što si Ti objavio, mi Poslanika slijedimo, pa upiši nas među svjedoke!');
INSERT INTO "bs_darassalam" VALUES(3,54,'3:54','A oni su lukavstvo smišljali, pa je i Allah njima lukavstvo priredio. A Allah najbolje lukavstvo priređuje.');
INSERT INTO "bs_darassalam" VALUES(3,55,'3:55','I kada Allah reče: "Isa, Ja ću te uzeti i k Sebi te uzdignuti! Očistiti te od nevjernika, i učinit ću da oni koji te slijede budu iznad onih koji ne vjeruju do Kijametskog dana. Zatim ćete se Meni vratiti, pa ću Ja presuditi o onome u čemu ste se razilazili.');
INSERT INTO "bs_darassalam" VALUES(3,56,'3:56','Što se tiče onih koji ne vjeruju, njih ću na strašne muke staviti na dunjaluku i na ahiretu, i oni neće imati pomagača.');
INSERT INTO "bs_darassalam" VALUES(3,57,'3:57','A onima koji budu vjerovali i dobra djela činili, On će punu nagradu dati. A, Allah ne voli zulumćare.');
INSERT INTO "bs_darassalam" VALUES(3,58,'3:58','Ovo što ti kazujemo jesu ajeti i Opomena usavršena.');
INSERT INTO "bs_darassalam" VALUES(3,59,'3:59','Primjer Isaov doista je kod Allaha isti kao i primjer Ademov: od prašine ga je stvorio, a zatim mu rekao: "Budi!" i on bi!');
INSERT INTO "bs_darassalam" VALUES(3,60,'3:60','Istina je od Gospodara tvoga, pa zato nikako ne budi od onih koji sumnjaju.');
INSERT INTO "bs_darassalam" VALUES(3,61,'3:61','A onima koji s tobom o njemu budu raspravljali, nakon što ti je došlo znanje, reci: "Dođite, pozvat ćemo sinove naše i sinove vaše, i žene naše i žene vaše, a doći ćemo i mi i vi, pa ćemo se proklinjati i Allahovo prokletstvo na one koji lažu prizvati!"');
INSERT INTO "bs_darassalam" VALUES(3,62,'3:62','Ovo je, doista, istinito kazivanje, i nema boga osim Allaha! A Allah je doista Silni i Mudri!');
INSERT INTO "bs_darassalam" VALUES(3,63,'3:63','A ako se oni okrenu, pa Allah, doista, dobro zna smutljivce.');
INSERT INTO "bs_darassalam" VALUES(3,64,'3:64','Reci: "O vi kojima je Knjiga data, dođite da se okupimo uz riječi koje mi i vi podjednako dijelimo: da nikoga osim Allaha ne obožavamo, da Mu ništa ne pridružujemo, i da jedni druge, pored Allaha, bogovima ne smatramo!" Pa, ako se oni okrenu, vi recite: "Budite svjedoci da smo mi muslimani!"');
INSERT INTO "bs_darassalam" VALUES(3,65,'3:65','O vi kojima je Knjiga data, zašto se o Ibrahimu prepirete kada su Tevrat i Indžil poslije njega objavljeni!? Zar vi ne razumijete?!');
INSERT INTO "bs_darassalam" VALUES(3,66,'3:66','Eto, vi raspravljate o onome o čemu imate neko znanje, ali zašto raspravljate o onome o čemu nemate nikakvoga znanja? A Allah zna, a vi ne znate.');
INSERT INTO "bs_darassalam" VALUES(3,67,'3:67','Ibrahim nije bio ni židov ni kršćanin, već pravi vjernik, musliman. On višebožac nije bio!');
INSERT INTO "bs_darassalam" VALUES(3,68,'3:68','Doista su najbliži ljudi Ibrahimu oni koji su ga slijedili, i ovaj Vjerovjesnik i oni koji vjeruju. A Allah je zaštitnik vjernika.');
INSERT INTO "bs_darassalam" VALUES(3,69,'3:69','Neki kojima je Knjiga data voljeli bi da vas na stranputicu odvedu. Ali oni ne odvode u zabludu nikoga drugoga do sebe, a da to i ne osjećaju.');
INSERT INTO "bs_darassalam" VALUES(3,70,'3:70','O vi kojima je data Knjiga, zašto u Allahove ajete ne vjerujete, a svjedoci ste da su Istina!?');
INSERT INTO "bs_darassalam" VALUES(3,71,'3:71','O vi kojima je data Knjiga, zašto Istinu neistinom zaogrćete, i krijete Istinu, a vi je znate!?');
INSERT INTO "bs_darassalam" VALUES(3,72,'3:72','Neki kojima je Knjiga data govore: "Vjerujte u ono što je objavljeno onima koji vjeruju, u početku dana, a porecite to na kraju dana, ne bi li i oni svoju vjeru napustili!?');
INSERT INTO "bs_darassalam" VALUES(3,73,'3:73','I vjerujte i povjeravajte se samo onome ko slijedi vašu vjeru!" Ti reci: "Doista je prava uputa Allahova uputa!" I govore: "Ne vjerujte i ne povjeravajte se ikome, pa da nekome bude dato kao što je dato vama ili da ima dokaz pred Gospodarom vašim!" Reci: "Zaista je sve dobro u ruci Allahovoj i On ga daje kome On hoće!" A Allah je neizmjerno darežljiv i Onaj Koji sve zna.');
INSERT INTO "bs_darassalam" VALUES(3,74,'3:74','On Svoju milost daje kome hoće. Allah je posjednik velikog dobra.');
INSERT INTO "bs_darassalam" VALUES(3,75,'3:75','Ima onih kojima je data Knjiga koji će ti vratiti ako im povjeriš tovar blaga, a ima ih koji ti neće vratiti ako im povjeriš i samo jedan dinar, osim ako ga od njih ne budeš stalno potraživao. To je zato jer oni govore: "Nama nije grijeh što učinimo nepismenima", i o Allahu govore laži, a znaju istinu.');
INSERT INTO "bs_darassalam" VALUES(3,76,'3:76','Međutim, ko obavezu svoju ispunjava i grijeha se kloni, pa Allah, doista, voli bogobojazne.');
INSERT INTO "bs_darassalam" VALUES(3,77,'3:77','Doista, oni koji za obavezu svoju prema Allahu i zakletve svoje kupuju nešto što malo vrijedi, na ahiretu nikakva udjela neće imati. Allah s njima neće govoriti, niti će na njih pogledati na Kijametskom danu, niti će ih očistiti. Njima pripada patnja bolna.');
INSERT INTO "bs_darassalam" VALUES(3,78,'3:78','Jedna skupina njihova uvija jezike svoje!" Knjigu čitajući, da biste vi pomislili da je to iz Knjige, a to nije iz Knjige. I govore: ''To je od Allaha!", a to nije od Allaha. I govore na Allaha laži, a to znaju.');
INSERT INTO "bs_darassalam" VALUES(3,79,'3:79','Nije primjereno čovjeku da mu Allah da Knjigu, i mudrost, i vjerovjesništvo, a zatim da govori ljudima: "Budite robovi moji, a ne Allahovi!", nego: "Budite učeni i pobožni, time što Knjizi poučavate i što je i sami proučavate!"');
INSERT INTO "bs_darassalam" VALUES(3,80,'3:80','On vam ne naređuje da meleke i vjerovjesnike gospodarima smatrate. Zar da vam naređuje nevjerovanje, nakon što ste postali muslimani?!');
INSERT INTO "bs_darassalam" VALUES(3,81,'3:81','I kada je Allah od vjerovjesnika čvrsto obećanje uzeo: "Kad god da vam podarim Knjigu i mudrost, zatim vam dođe poslanik koji potvrđuje da je istina ono što vi imate, da li biste sigurno u njega vjerovali i njega pomogli?!" Da li potvrđujete i prihvaćate obavezu prema Meni?", reče. "Potvrđujemo!", odgovoriše. "Budite onda svjedoci!", reče On, "a i Ja s vama svjedočim!"');
INSERT INTO "bs_darassalam" VALUES(3,82,'3:82','A oni koji se i poslije toga okrenu, doista su to nevjernici.');
INSERT INTO "bs_darassalam" VALUES(3,83,'3:83','Zar pored Allahove vjere žele neku drugu, kada se Njemu predaju, milom ili silom, i oni na nebesima i na Zemlji, i Njemu će vraćeni biti?!');
INSERT INTO "bs_darassalam" VALUES(3,84,'3:84','Reci: "Mi vjerujemo u Allaha i u ono što je objavljeno nama, i što je objavljeno Ibrahimu, i Ismailu, i Ishaku, i Jakubu i potomcima, i u ono što je dato Musau i Isau i vjerovjesnicima od Gospodara njihovoga; mi nikakvu razliku između njih ne pravimo, i mi smo Njemu predani."');
INSERT INTO "bs_darassalam" VALUES(3,85,'3:85','A onaj ko želi neku drugu vjeru osim islama, neće mu biti primljena, i on će na drugom svijetu biti među gubitnicima.');
INSERT INTO "bs_darassalam" VALUES(3,86,'3:86','Kako Allah da uputi na Pravi put narod koji ne vjeruje nakon što je vjerovao i svjedočio da je Poslanik istina, i Jasni dokazi su mu došli!? Allah neće uputiti na Pravi put narod koji zulum čini.');
INSERT INTO "bs_darassalam" VALUES(3,87,'3:87','A to su oni čija je kazna prokletstvo od Allaha i meleka i svih ljudi!');
INSERT INTO "bs_darassalam" VALUES(3,88,'3:88','U njemu će vječno ostati i patnja im olakšana biti neće, niti će im se vremena dati.');
INSERT INTO "bs_darassalam" VALUES(3,89,'3:89','Izuzetak su oni koji se pokaju poslije toga i poprave, a Allah je, doista, Onaj Koji oprašta grijehe i milostiv je.');
INSERT INTO "bs_darassalam" VALUES(3,90,'3:90','Doista, onima koji postanu nevjernici, nakon što su vjerovali, zatim postanu još veći nevjernici, pokajanje neće biti primljeno i oni su sigurno zalutali.');
INSERT INTO "bs_darassalam" VALUES(3,91,'3:91','Zaista, nijednom od onih koji nisu vjerovali, a koji i umru kao nevjernici, neće biti prihvaćeno da se otkupe pa makar to bilo sve blago na Zemlji. Njima pripada patnja bolna i oni neće imati nikakvog pomagača.');
INSERT INTO "bs_darassalam" VALUES(3,92,'3:92','Nećete postići dobro sve dok ne budete dijelili od onoga što vam je drago. A šta god da dijelite, Allah to dobro zna.');
INSERT INTO "bs_darassalam" VALUES(3,93,'3:93','Svaka hrana bila je dozvoljena sinovima Israilovim, osim one koju je Israil sam sebi zabranio prije nego je Tevrat objavljen bio. Reci: "Donesite Tevrat i čitajte ga, ako istinu govorite!"');
INSERT INTO "bs_darassalam" VALUES(3,94,'3:94','A oni koji o Allahu iznose laži i poslije toga, oni su, doista, zulumćari.');
INSERT INTO "bs_darassalam" VALUES(3,95,'3:95','Reci: "Allah istinu govori!" – da slijedite vjeru Ibrahimovu, koji je pravi vjernik bio. On u višebošce nije spadao!');
INSERT INTO "bs_darassalam" VALUES(3,96,'3:96','Prva bogomolja podignuta za ljude jest ona u Bekki, blagoslovljena i putokaz svjetovima.');
INSERT INTO "bs_darassalam" VALUES(3,97,'3:97','U njoj su znamenja očevidna, mjesto na kojem je stajao Ibrahim. I onaj ko u nju uđe, bit će siguran. Hodočastiti Kabu, radi Allaha, dužan je svako ko bude u mogućnosti do nje doći. A ko ne vjeruje – pa, Allah je, doista, neovisan o svjetovima.');
INSERT INTO "bs_darassalam" VALUES(3,98,'3:98','Reci: "O sljedbenici Knjige, zašto u Allahova ajete i znakove ne vjerujete. Allah je svjedok svega što radite!"');
INSERT INTO "bs_darassalam" VALUES(3,99,'3:99','Reci: "O vi kojima je Knjiga data, zašto onoga koji vjeruje od Allahove vjere odvraćate, želeći da je iskrivite, a svjedoci ste da je istina!? A Allah ne zanemaruje ono što vi radite."');
INSERT INTO "bs_darassalam" VALUES(3,100,'3:100','O vi koji vjerujete, ako se budete pokoravali jednoj skupini onih kojima je data Knjiga, oni će vas, nakon što ste prihvatili pravu vjeru, ponovo vratiti u nevjernike!');
INSERT INTO "bs_darassalam" VALUES(3,101,'3:101','A kako da ne vjerujete kada vam se kazuju Allahovi ajeti i kad je među vama Poslanik Njegov?! A ko se čvrsto drži Allaha, on je na Pravi put upućen.');
INSERT INTO "bs_darassalam" VALUES(3,102,'3:102','O vi koji vjerujete, bojte se Allaha istinskom bogobojaznošću i nipošto ne umirite osim kao muslimani!');
INSERT INTO "bs_darassalam" VALUES(3,103,'3:103','Svi se čvrsto držite za Allahovo uže i ne razjedinjujte se! I sjetite se blagodati Allahove prema vama, kada ste bili neprijatelji, pa je On sjedinio srca vaša i postali ste, Njegovom milošću, braća! I bili ste na ivici vatrene jame, pa vas je On od nje spasio. Tako vam Allah objašnjava ajete i znakove Svoje da biste se ispravnim putem uputili.');
INSERT INTO "bs_darassalam" VALUES(3,104,'3:104','I neka među vama bude grupa ljudi koja na dobro poziva i naređuje dobro i odvraća od zla. To su oni koji su uspjeli.');
INSERT INTO "bs_darassalam" VALUES(3,105,'3:105','I ne budite kao oni koji su se razjedinili i razišli, nakon što su im jasni dokazi već došli! Njima pripada patnja velika.');
INSERT INTO "bs_darassalam" VALUES(3,106,'3:106','Na Dan kada će neka lica pobijeljeti, a neka pocrnjeti. Onima čija lica budu pocrnjela bit će rečeno: "Zar ste postali nevjernici nakon vašeg vjerovanja?! Pa iskusite patnju zato što niste vjerovali!"');
INSERT INTO "bs_darassalam" VALUES(3,107,'3:107','A oni čija lica pobijele, pa u milosti Allahovoj su, oni će u njoj vječno ostati.');
INSERT INTO "bs_darassalam" VALUES(3,108,'3:108','To su Allahovi ajeti koje tebi istinito kazujemo. A Allah ne želi da bilo kome zulum učini.');
INSERT INTO "bs_darassalam" VALUES(3,109,'3:109','Allahu pripada sve što je na nebesima i što je na Zemlji, i Allahu se sve vraća.');
INSERT INTO "bs_darassalam" VALUES(3,110,'3:110','Vi ste najbolji narod koji se ikada pojavio: tražite da se čini dobro, a odvraćate od zla, i vjerujete u Allaha! A kada bi sljedbenici Knjige vjerovali, bilo bi bolje za njih; među njima ima vjernika, ali – većina njih su buntovnici.');
INSERT INTO "bs_darassalam" VALUES(3,111,'3:111','Oni vam ne mogu nauditi, nego vas mogu samo uznemiriti. Ako vas napadnu, dat će se u bijeg, i poslije im pomoći biti neće.');
INSERT INTO "bs_darassalam" VALUES(3,112,'3:112','Ma gdje se našli, oni su poniženi, osim ako su pod zaštitom Allahovom i zaštitom drugih ljudi. Oni su zaslužili srdžbu Allahovu i bijeda je pala na njih, zato što u ajete i dokaze Allahove nisu vjerovali i što su vjerovjesnike bespravno ubijali, zato što su Allahu bili nepokorni, i što su prešli svaku granicu u griješenju.');
INSERT INTO "bs_darassalam" VALUES(3,113,'3:113','Nisu svi oni isti! Među onima kojima je Knjiga data ima ispravnih, koji po cijelu noć ajete Allahove uče i sedždu Mu čine.');
INSERT INTO "bs_darassalam" VALUES(3,114,'3:114','Oni u Allaha i ahiret vjeruju, naređuju da se čine dobra djela, a odvraćaju od zlih, i žure činiti dobro. Oni su među dobrima.');
INSERT INTO "bs_darassalam" VALUES(3,115,'3:115','Kakvo god dobro da urade, neće im biti negirano, a Allah dobro zna one koji se Njega boje.');
INSERT INTO "bs_darassalam" VALUES(3,116,'3:116','Doista, one koji ne vjeruju neće odbraniti imeci njihovi, niti djeca njihova; oni će stanovnici Vatre biti, u kojoj će vječno ostati.');
INSERT INTO "bs_darassalam" VALUES(3,117,'3:117','Primjer onoga što oni u ovosvjetskom životu udijele sličan je ledenom vjetru koji pogodi usjeve ljudi koji su sami sebi zulum učinili, pa ih uništi. Njima Allah nije zulum učinio, nego ga oni sami sebi čine.');
INSERT INTO "bs_darassalam" VALUES(3,118,'3:118','O vi koji vjerujete, ne uzimajte za bliske prijatelje i zaštitnike druge mimo vas, jer drugi vam propast žele, jedva čekaju da muka dopadnete! Mržnja izbija iz usta njihovih, dok je ono što kriju grudi njihove još gore. Mi vam dokaze iznosimo, ako razumijete.');
INSERT INTO "bs_darassalam" VALUES(3,119,'3:119','Eto, vi njih volite, a oni vas ne vole, a vi vjerujete u sve Knjige. Kada vas sretnu, oni govore: "Vjerujemo!", a čim se nasamo nađu, od zlobe prema vama vrhove prstiju grizu. Reci: "Umrite od muke!" Allah, doista, dobro zna šta je u grudima.');
INSERT INTO "bs_darassalam" VALUES(3,120,'3:120','Ako vas zadesi kakvo dobro, to ih ozlojedi; a zadesi li vas kakva nevolja, obraduju joj se. Ako se budete strpjeli i bogobojazni bili, njihovo lukavstvo vam nimalo neće nauditi. A Allah, doista, sve što oni rade obuhvata.');
INSERT INTO "bs_darassalam" VALUES(3,121,'3:121','I sjeti se kada si poranio, porodicu svoju ostavio, da vjernicima odrediš mjesta za borbu. A Allah je Onaj Koji sve čuje i Onaj Koji sve zna.');
INSERT INTO "bs_darassalam" VALUES(3,122,'3:122','Kada dvije skupine vaše gotovo ne uzmakoše, a Allah je njihov zaštitnik. I samo na Allaha neka se vjernici oslone!');
INSERT INTO "bs_darassalam" VALUES(3,123,'3:123','Allah vas je i na Bedru pomogao, kada ste bili malobrojni! Zato se Allaha bojte, da biste bili zahvalni.');
INSERT INTO "bs_darassalam" VALUES(3,124,'3:124','Kada ti reče vjernicima: "Zar vam nije dovoljno da vas Gospodar vaš pomogne sa tri hiljade poslanih meleka?"');
INSERT INTO "bs_darassalam" VALUES(3,125,'3:125','Dakako! Ako strpljivi i bogobojazni budete; i ako vas oni napadnu naglo, Gospodar vaš će vam poslati pomoć od pet hiljada meleka obilježenih.');
INSERT INTO "bs_darassalam" VALUES(3,126,'3:126','To je Allah učinio samo da vas obraduje i da se time srca vaša smire. A pomoći nema osim od Allaha, Silnog i Mudrog.');
INSERT INTO "bs_darassalam" VALUES(3,127,'3:127','I da jedan dio nevjernika uništi, ili da ih osramoti, te da se vrate razočarani.');
INSERT INTO "bs_darassalam" VALUES(3,128,'3:128','Ništa ne zavisi od tebe: bilo da im On pokajanje primi, ili da ih kazni, jer oni su, doista, zulumćari.');
INSERT INTO "bs_darassalam" VALUES(3,129,'3:129','Allahu pripada sve što je na nebesima i što je na Zemlji! On prašta kome On hoće, a na muke stavlja koga On hoće. Jer, Allah je Onaj Koji oprašta grijehe i milostiv je.');
INSERT INTO "bs_darassalam" VALUES(3,130,'3:130','O vi koji vjerujete, ne jedite kamatu više puta udvostručenu, i Allaha se bojte da biste spašeni bili.');
INSERT INTO "bs_darassalam" VALUES(3,131,'3:131','I čuvajte se Vatre koja je za nevjernike pripremljena.');
INSERT INTO "bs_darassalam" VALUES(3,132,'3:132','I pokoravajte se Allahu i Poslaniku kako bi vam milost ukazana bila.');
INSERT INTO "bs_darassalam" VALUES(3,133,'3:133','I požurite oprostu Gospodara vašega, i džennetu širokom kao nebesa i Zemlja – pripremljenom za bogobojazne –');
INSERT INTO "bs_darassalam" VALUES(3,134,'3:134','za one koji udjeljuju, i kada su u obilju i kada su u oskudici, koji srdžbu savlađuju i ljudima opraštaju. A Allah voli dobročinitelje.');
INSERT INTO "bs_darassalam" VALUES(3,135,'3:135','I za one koji, kada učine nešto loše, ili sebi zulum učine, Allaha se sjete i oprost za grijehe svoje mole. A ko oprašta grijehe, ako ne Allah?! I za one koji u grijehu svjesno ne ustraju.');
INSERT INTO "bs_darassalam" VALUES(3,136,'3:136','To su oni čija je nagrada oprost Gospodara njihova, i bašče kroz koje rijeke teku, gdje će vječno ostati. Divne li nagrade za one koji tako postupaju!');
INSERT INTO "bs_darassalam" VALUES(3,137,'3:137','Prije vas su mnogi narodi bili i nestali. Zato, putujte po Zemlji i gledajte kako su završili oni koji su istinu poricali.');
INSERT INTO "bs_darassalam" VALUES(3,138,'3:138','To je objašnjenje ljudima, i uputa i pouka za one koji se Allaha boje.');
INSERT INTO "bs_darassalam" VALUES(3,139,'3:139','Ne klonite duhom, i ne žalostite se! Vi ćete pobijediti, ako vjernici budete.');
INSERT INTO "bs_darassalam" VALUES(3,140,'3:140','Ako vas rane spopadnu – pa sigurno su takve rane i druge spopale – i te dane pobjede Mi naizmjenice dajemo ljudima, da bi Allah znao one koji vjeruju, i odabrao neke od vas za šehide. A Allah ne voli zulumćare.');
INSERT INTO "bs_darassalam" VALUES(3,141,'3:141','I da bi vjernike očistio, a nevjernike uništio.');
INSERT INTO "bs_darassalam" VALUES(3,142,'3:142','Zar mislite da ćete ući u džennet, a da Allah ne zna one od vas koji se bore, i da ne zna one koji su strpljivi?!');
INSERT INTO "bs_darassalam" VALUES(3,143,'3:143','Vi ste priželjkivali smrt prije nego što ste se s njom suočili, pa ste je, eto, vidjeli i u nju ste gledali.');
INSERT INTO "bs_darassalam" VALUES(3,144,'3:144','Muhammed je samo poslanik, a i prije njega je bilo poslanika. Ako bi on umro, ili ubijen bio, zar biste se vi nazad vratili?! Onaj ko se nazad vrati, Allahu neće ništa nauditi, a Allah će zahvalne nagraditi.');
INSERT INTO "bs_darassalam" VALUES(3,145,'3:145','Nijedna osoba neće umrijeti bez određenja Allahovog, časa suđenog. Ko želi nagradu na ovome svijetu, dat ćemo mu dio nagrade koji zaslužuje, a ko želi nagradu na ahiretu, dat ćemo mu dio nagrade koji zaslužuje. I zahvalne ćemo nagraditi!');
INSERT INTO "bs_darassalam" VALUES(3,146,'3:146','A koliko je bilo vjerovjesnika uz koje su se brojni pobožni ljudi borili, pa nisu klonuli zbog onoga što bi ih zadesilo na putu Allahovom, niti su posustajali, niti se predavali?! A Allah voli strpljive.');
INSERT INTO "bs_darassalam" VALUES(3,147,'3:147','Oni nisu govorili ništa drugo nego: "Gospodaru naš, oprosti nam grijehe naše i pretjerivanje u postupcima našim, učvrsti korake naše i pomozi nam protiv naroda nevjerničkog."');
INSERT INTO "bs_darassalam" VALUES(3,148,'3:148','Pa im je Allah dao nagradu na dunjaluku i ljepotu nagrade na ahiretu, a Allah voli dobročinitelje.');
INSERT INTO "bs_darassalam" VALUES(3,149,'3:149','O vi koji vjerujete, ako se budete pokoravali onima koji ne vjeruju, vratit će vas unazad pa ćete postati gubitnici.');
INSERT INTO "bs_darassalam" VALUES(3,150,'3:150','Međutim, samo je Allah zaštitnik vaš i On je najbolji pomagač.');
INSERT INTO "bs_darassalam" VALUES(3,151,'3:151','Mi ćemo uliti strah u srca onih koji ne vjeruju, zato što Allahu pridružuju ono o čemu Allah nije spustio nikakav dokaz; Vatra je njihovo boravište, a grozno je stjecište zulumćara.');
INSERT INTO "bs_darassalam" VALUES(3,152,'3:152','Allah je već potvrdio obećanje Svoje, kada ste ih vi uništavali, dozvolom Njegovom, dok niste neuspjeh doživjeli i o naredbi se razišli, i nepokorni bili, nakon što vam je pokazao šta volite: neki od vas žele dunjaluk, a drugi ahiret! Onda vas je On odvojio od njih, kako bi vas iskušao! I oprostio vam je; a Allah je prema vjernicima neizmjerno dobar.');
INSERT INTO "bs_darassalam" VALUES(3,153,'3:153','Kada ste uzmicali penjući se i ne obazirući se ni na koga, dok vas je Poslanik zvao iza leđa vaših, Allah vas je kaznio nedaćom na nedaću, kako ne biste tugovali za onim što vam je izmaklo niti zbog onoga što vas je zadesilo. A Allah u potpunosti zna ono što radite.');
INSERT INTO "bs_darassalam" VALUES(3,154,'3:154','Zatim vam je, poslije nevolje, spokojstvo spustio, san koji je neke od vas obuzeo, dok su se drugi brinuli samo o sebi, misleći o Allahu ono što nije istina, mislima paganskim. Govorili su: "Imamo li mi išta u svemu ovom?"!" Reci: "Odredba u cjelosti pripada samo Allahu!" Oni u sebi kriju ono što tebi ne pokazuju. I govorili su: "Da smo išta u ovome imali, ne bismo ovdje izginuli!" Reci: "I da ste u kućama svojim bili, opet bi oni kojima je propisano da poginu izišli na mjesta pogibije svoje, da Allah iskuša šta je u grudima vašim i da ispita šta je u srcima vašim! A Allah dobro zna šta je u grudima.');
INSERT INTO "bs_darassalam" VALUES(3,155,'3:155','One od vas koji su uzmakli na dan kada su se dvije skupine sukobile, doista je šejtan naveo da posrnu zbog nečeg od onoga što su prije zaradili. A Allah im je već oprostio, Allah je, zaista, Onaj Koji oprašta grijehe i Onaj Koji ne žuri s kaznom.');
INSERT INTO "bs_darassalam" VALUES(3,156,'3:156','O vi koji vjerujete, ne budite kao oni koji ne vjeruju i koji govore za braću svoju, kada se na put po svijetu otisnu ili se budu borili: "Da su bili s nama, ne bi umrli, niti bi poginuli!" Jer, Allah time čini tugu u srcima njihovim, a Allah daje i život, i smrt. On dobro vidi ono što vi radite.');
INSERT INTO "bs_darassalam" VALUES(3,157,'3:157','A ako vi na Allahovom putu poginete ili umrete, pa Allahov oprost i milost bolji su od onoga što oni gomilaju.');
INSERT INTO "bs_darassalam" VALUES(3,158,'3:158','I bilo da umrete ili poginete, vi ćete pred Allaha sigurno sakupljeni biti.');
INSERT INTO "bs_darassalam" VALUES(3,159,'3:159','S Allahovom milošću ti si blag prema njima; a da si grub i tvrda srca bio, sigurno bi pobjegli na sve strane od tebe. Zato im praštaj i moli da im bude oprošteno, i dogovaraj se s njima. A kada se odlučiš, na Allaha se osloni; Allah, doista voli one koji se na Njega oslanjaju.');
INSERT INTO "bs_darassalam" VALUES(3,160,'3:160','Ako vas Allah pomogne, niko vas pobijediti neće; a ako vas napusti, pa ko je taj koji vam, osim Njega, može pomoći?! Pa, na Allaha neka se zato oslanjaju vjernici!');
INSERT INTO "bs_darassalam" VALUES(3,161,'3:161','Nije moguće da Vjerovjesnik šta utaji! A onaj ko šta utaji, doći će na Kijametski dan s tim što je utajio. Zatim će svakome u potpunosti biti dato ono što je zaslužio, i nikome neće zulum biti učinjen.');
INSERT INTO "bs_darassalam" VALUES(3,162,'3:162','Zar je onaj koji je Allahovo zadovoljstvo slijedio isti kao onaj koji je Allahovu srdžbu navukao i čije prebivalište je džehennem?! A užasno je to konačno odredište!');
INSERT INTO "bs_darassalam" VALUES(3,163,'3:163','Oni su kod Allaha na različitim stepenima i Allah dobro vidi šta oni rade!');
INSERT INTO "bs_darassalam" VALUES(3,164,'3:164','Allah je vjernike milošću Svojom darovao kada im je poslanika između njih poslao, da im ajete Njegove kazuje, da ih očisti, i da ih Knjizi i mudrosti pouči, iako su prije u očitoj zabludi bili.');
INSERT INTO "bs_darassalam" VALUES(3,165,'3:165','Zar niste rekli, kada vas je snašla nevolja koju ste vi njima dvostruko nanijeli: "Odakle sada ovo?" Reci: "To je od vas samih!" Allah, doista, nad svime ima moć.');
INSERT INTO "bs_darassalam" VALUES(3,166,'3:166','Ono što vas je zadesilo kada su se dvije skupine sukobile, bilo je Allahovom dozvolom i da bi znao ko su vjernici.');
INSERT INTO "bs_darassalam" VALUES(3,167,'3:167','I da bi ukazao na licemjere. Njima je rečeno: "Dođite, borite se na Allahovom putu, ili se branite!", a oni su kazali: "Da znamo da će biti borbe, mi bismo vas slijedili." Toga dana oni su bili bliži nevjerovanju nego vjerovanju. Svojim su ustima govorili ono što nije bilo u srcima njihovim! A Allah najbolje zna što oni kriju.');
INSERT INTO "bs_darassalam" VALUES(3,168,'3:168','Onima koji se nisu borili, a o braći svojoj su govorili: "Da su nas poslušali, ne bi izginuli!", reci: "Od sebe smrt odagnajte, ako istinu govorite!"');
INSERT INTO "bs_darassalam" VALUES(3,169,'3:169','Nikako ne smatraj mrtvima one koji su na Allahovom putu poginuli. Ne, oni su živi i opskrbljeni su kod Gospodara svoga.');
INSERT INTO "bs_darassalam" VALUES(3,170,'3:170','Radosni su zbog onoga što im je Allah iz obilja Svoga dao, i veseli zbog onih koji im se još nisu pridružili, za koje nikakva straha neće biti i koji ni za čim tugovati neće.');
INSERT INTO "bs_darassalam" VALUES(3,171,'3:171','Oni se raduju Allahovoj blagodati i dobroti, te tome što Allah neće dopustiti da propadne nagrada koja pripada vjernicima.');
INSERT INTO "bs_darassalam" VALUES(3,172,'3:172','Onima koji su se Allahu i Poslaniku odazvali i nakon zadobijenih rana, onima između njih koji su dobro činili i bogobojazni bili – pripada nagrada velika.');
INSERT INTO "bs_darassalam" VALUES(3,173,'3:173','Onima kojima se – kada su im ljudi rekli: "Neprijatelji se okupljaju zbog vas, pa pazite ih se!" – vjerovanje povećalo, pa su kazali: "Nama je Allah dovoljan, a divan je On Zaštitnik."');
INSERT INTO "bs_darassalam" VALUES(3,174,'3:174','Pa su se vratili s Allahovim blagodatima i obiljem; nikakvo ih zlo nije zadesilo. Slijedili su zadovoljstvo Allahovo, a Allah je posjednik velikog dobra.');
INSERT INTO "bs_darassalam" VALUES(3,175,'3:175','To vas samo šejtan plaši pristalicama svojim, pa ih se ne bojte, nego se Mene bojte, ako ste vjernici!');
INSERT INTO "bs_darassalam" VALUES(3,176,'3:176','Neka te ne žaloste oni koji srljaju u nevjerovanje. Oni nimalo neće Allahu nauditi. Allah neće da im učini bilo kakvo dobro na ahiretu i njima pripada patnja velika.');
INSERT INTO "bs_darassalam" VALUES(3,177,'3:177','Oni koji su vjerovanje za nevjerovanje prodali, Allahu neće ništa nauditi. Njima pripada patnja bolna.');
INSERT INTO "bs_darassalam" VALUES(3,178,'3:178','Neka nevjernici nikako ne misle da je dobro za njih to što im dajemo dug život. Mi im dug život dajemo samo zbog toga kako bi ogrezli u grijehu i njima pripada patnja ponižavajuća.');
INSERT INTO "bs_darassalam" VALUES(3,179,'3:179','I ne može biti da Allah vjernike ostavi u stanju u kojem ste vi, dok loše od dobrih ne odvoji. I ne može biti da vam Allah otkrije ono što je čulima nedokučivo, nego On odabire koga hoće od poslanika Svojih. Zato, vjerujte u Allaha i poslanike Njegove, a ako vjerovali budete i Allaha se bojali i grijeha se čuvali, vama pripada velika nagrada.');
INSERT INTO "bs_darassalam" VALUES(3,180,'3:180','Neka oni koji škrtare u onome što im Allah iz obilja Svoga daje, nikako ne misle da je to dobro za njih. Naprotiv, to je zlo za njih. Na Kijametskom danu bit će im oko vrata omotano ono čime su škrtarili. A Allahu pripada nasljedstvo nebesa i Zemlje i Allah dobro zna ono što vi radite.');
INSERT INTO "bs_darassalam" VALUES(3,181,'3:181','Allah je čuo riječi onih koji su govorili: "Allah je siromašan, a mi smo bogati!" Mi ćemo zabilježiti ono što su oni govorili kao i to što su bez opravdanja vjerovjesnike ubijali. I reći ćemo: "Iskusite kaznu u Ognju!"');
INSERT INTO "bs_darassalam" VALUES(3,182,'3:182','To je zbog djela vaših ruku, a Allah, zaista, ne čini zulum robovima Svojim.');
INSERT INTO "bs_darassalam" VALUES(3,183,'3:183','Onima koji govore: "Allah je od nas obavezu uzeo da ne vjerujemo nijednom poslaniku dok ne prinese žrtvu koju će vatra progutati!", reci: "I prije mene su vam poslanici s jasnim dokazima dolazili, a i s tim o čemu govorite. Pa zašto ste ih, onda, ubijali – ako istinu govorite?"');
INSERT INTO "bs_darassalam" VALUES(3,184,'3:184','A ako te budu lašcem smatrali, pa i prije tebe su lašcima smatrani poslanici koji su Jasne dokaze i spise i Knjigu svjetilju donosili.');
INSERT INTO "bs_darassalam" VALUES(3,185,'3:185','Svaka osoba smrt će okusiti, a samo ćete na Kijametskom danu u potpunosti dobiti plaće vaše. Pa ko bude od Vatre udaljen i u džennet uveden, uspio je. A život na dunjaluku samo je varljivo naslađivanje.');
INSERT INTO "bs_darassalam" VALUES(3,186,'3:186','Vi ćete sigurno biti iskušavani u imecima vašim, i životima vašim, a slušat ćete od onih kojima je prije vas data Knjiga i od višebožaca mnoge neugodnosti. Ako budete strpljivi i Allaha se bojali, to, doista, u istinske postupke spada.');
INSERT INTO "bs_darassalam" VALUES(3,187,'3:187','A kada Allah uze obavezu od onih kojima je data Knjiga da će to sigurno ljudima objašnjavati i ništa iz nje neće kriti, oni su je za leđa svoja bacili, prodajući je za bagatelnu cijenu. A ružno li je ono što za to kupuju!');
INSERT INTO "bs_darassalam" VALUES(3,188,'3:188','Nikako ne misli za one koje raduje to što su uradili i koji vole biti pohvaljeni i za ono što nisu učinili – nikako ne misli da će se patnje sačuvati. Njima pripada patnja bolna.');
INSERT INTO "bs_darassalam" VALUES(3,189,'3:189','Allahu pripada vlast na nebesima i na Zemlji i Allah nad svime ima moć.');
INSERT INTO "bs_darassalam" VALUES(3,190,'3:190','U stvaranju nebesa i Zemlje, i u izmjeni noći i dana, doista su znakovi za razumom obdarene,');
INSERT INTO "bs_darassalam" VALUES(3,191,'3:191','koji Allaha spominju stojeći, sjedeći i ležeći na svojim bokovima, i o stvaranju nebesa i Zemlje razmišljaju. "Gospodaru naš, Ti ovo nisi uzalud stvorio! Uzvišen si Ti i sačuvaj nas od patnje u Vatri!');
INSERT INTO "bs_darassalam" VALUES(3,192,'3:192','Gospodaru naš, onoga koga Ti budeš u Vatru bacio, već si ga ponizio, a zulumćarima neće biti pomagača!');
INSERT INTO "bs_darassalam" VALUES(3,193,'3:193','Gospodaru naš, mi smo čuli glasnika koji poziva vjeri: ''Da vjerujete u Gospodara vašega!'', pa smo povjerovali! Gospodaru naš, oprosti nam grijehe, i pokrij loše postupke naše, i usmrti nas s čestitima!');
INSERT INTO "bs_darassalam" VALUES(3,194,'3:194','Gospodaru naš, daj nam ono što si nam obećao preko Svojih poslanika i ne ponizi nas na Kijametskom danu! Ti, doista, ne kršiš obećanje Svoje!"');
INSERT INTO "bs_darassalam" VALUES(3,195,'3:195','I Gospodar im se njihov odazva: "Nijednom između vas koji dobro čini, bio muškarac ili žena, djela neću poništiti; vi ste jedni od drugih. Onima koji se isele i koji budu iz domova svojih prognani, i koji budu na putu Mome mučeni, i koji se budu borili i poginuli, sigurno ću loša djela pokriti i sigurno ću ih u džennetske bašče, kroz koje rijeke teku, uvesti. Nagrada je to Allahova, a u Allaha nagrada je najljepša.');
INSERT INTO "bs_darassalam" VALUES(3,196,'3:196','Neka te nikako ne obmanjuje to što oni koji ne vjeruju svuda po svijetu obijesno bogatstvom raspolažu.');
INSERT INTO "bs_darassalam" VALUES(3,197,'3:197','Kratko uživanje, a potom – džehennem boravište je njihovo; a užasno je to prebivalište!');
INSERT INTO "bs_darassalam" VALUES(3,198,'3:198','Ali, onima koji se Gospodara svoga boje pripadaju džennetske bašče kroz koje rijeke teku; u njima će vječno ostati, Allah će ih ugostiti. A ono što je u Allaha, bolje je za čestite.');
INSERT INTO "bs_darassalam" VALUES(3,199,'3:199','Ima i onih kojima je data Knjiga koji vjeruju u Allaha i u ono što je objavljeno vama, i u ono što je objavljeno njima, koji su ponizni Allahu i ne prodaju ajete Allahove za bagatelnu cijenu. Njima pripada nagrada Gospodara njihova, a Allah će, doista, brzo svidjeti račun.');
INSERT INTO "bs_darassalam" VALUES(3,200,'3:200','O vi koji vjerujete, budite strpljivi i izdržljivi, postojano bdijte i Allaha se bojte, da biste uspjeli!');
INSERT INTO "bs_darassalam" VALUES(4,1,'4:1','O ljudi, bojte se Gospodara svoga, Koji vas je stvorio od jedne osobe, od koje je njen par stvorio, a od njih dvoje mnoge muškarce i žene rasijao. Bojte se Allaha, Čijim imenom jedni druge molite, i čuvajte rodbinske veze. Allah vas, doista, nadzire.');
INSERT INTO "bs_darassalam" VALUES(4,2,'4:2','I siročadima imetke njihove dajte, ono što je loše i zabranjeno ne mijenjajte za ono što je lijepo i dopušteno, i imetke njihove s imecima vašim ne trošite. To je, doista, prestup veliki.');
INSERT INTO "bs_darassalam" VALUES(4,3,'4:3','Ako se bojite da prema sirotama nećete biti pravedni, onda se ženite onim ženama koje su vam drage i dopuštene, sa po dvije, sa po tri ili sa po četiri. A ako strahujete da nećete pravedni biti, onda samo jednom, ili onim koje posjedujete. Tako ćete se najlakše nepravde sačuvati.');
INSERT INTO "bs_darassalam" VALUES(4,4,'4:4','I draga srca ženama darove njihove dajte; pa ako vam one draga srca štogod od toga poklone, to s prijatnošću i ugodnošću trošite.');
INSERT INTO "bs_darassalam" VALUES(4,5,'4:5','I maloumnicima ne dajite imetke koje vam je Allah za izdržavanje učinio, nego ih njima opskrbite i obucite. I lijepe riječi im govorite.');
INSERT INTO "bs_darassalam" VALUES(4,6,'4:6','I iskušavajte siročad dok ne stasaju za brak, pa ako se uvjerite da su zreli, uručite im imetke njihove. I ne trošite ih rasipnički i u žurbi, prije nego odrastu. Ko je imućan neka se suzdrži, a ko je siromašan, neka troši onoliko koliko je neophodno. A kada im imetke uručujete, to učinite u prisustvu svjedoka; a dosta je što će se pred Allahom račun polagati.');
INSERT INTO "bs_darassalam" VALUES(4,7,'4:7','Muškarcima pripada dio onoga što ostave roditelji i rodbina, a i ženama pripada dio od onoga što ostave roditelji i rodbina. Bilo toga malo ili mnogo, dio je obavezan.');
INSERT INTO "bs_darassalam" VALUES(4,8,'4:8','A kada diobi prisustvuju rođaci, siročad i siromasi, opskrbite ih dijelom toga, i lijepe riječi im recite.');
INSERT INTO "bs_darassalam" VALUES(4,9,'4:9','I neka se plaše oni, koji bi strahovali za svoju nejaku djecu ako ih ostave iza sebe. Neka se Allaha boje i neka govore samo pravo.');
INSERT INTO "bs_darassalam" VALUES(4,10,'4:10','Doista, oni koji nepravedno troše imetke siročadi, u svoje stomake stavljaju vatru i oni će u Ognju gorjeti.');
INSERT INTO "bs_darassalam" VALUES(4,11,'4:11','Allah vam nalaže, u vezi s djecom vašom, da muškom pripadne dio jednak dijelu dvoje ženske djece zajedno. A ako bude više od dvoje ženske djece, njima pripadaju dvije trećine onoga što je umrli ostavio, a ako je samo jedno žensko, pripada joj polovina. A roditeljima, svakome posebno, pripada šestina od onoga što je ostavio ako ima djece. A ako ne bude imao djece, i naslijede ga roditelji, onda njegovoj majci pripada trećina. A ako ima braće, onda njegovoj majci pripada šestina, pošto se izvrši oporuka koju je ostavio ili podmiri dug. Vi ne znate ko vam je bliži po koristi, roditelji vaši ili sinovi vaši. To je Allahova zapovijed, a Allah je, doista, Onaj Koji sve zna i mudar je.');
INSERT INTO "bs_darassalam" VALUES(4,12,'4:12','Vama pripada polovina onoga što ostave supruge vaše, ako ne budu imale djece; a ako budu imale djece, vama pripada četvrtina onoga što one ostave, pošto se izvrši oporuka koju ostave, ili podmiri dug; njima pripada četvrtina onoga što vi ostavite, ukoliko ne budete imali djece, a ako budete imali djece, njima pripada osmina onoga što vi ostavite nakon što se izvrši oporuka koju ostavite, ili podmiri dug. A ako se muškarac ili žena, koji nemaju ni djece ni roditelja, budu nasljeđivali i budu imali brata ili sestru, onda će svako od njih dvoje dobiti šestinu; ako ih bude više, oni onda zajednički učestvuju u trećini, pošto se izvrši oporuka koja je ostavljena, ili podmiri dug, ne oštećujući time bilo koga. To je zapovijed Allahova, a Allah je Onaj Koji sve zna i Onaj Koji ne žuri s kaznom.');
INSERT INTO "bs_darassalam" VALUES(4,13,'4:13','To su Allahovi propisi, a onoga ko se pokorava Allahu i Poslaniku Njegovu On će uvesti u džennetske bašče kroz koje teku rijeke i u kojima će vječno ostati. To je uspjeh veliki.');
INSERT INTO "bs_darassalam" VALUES(4,14,'4:14','A onoga ko bude nepokoran Allahu i Poslaniku Njegovom, te preko granica Njegovih bude prelazio, On će uvesti u Vatru u kojoj će vječno ostati; njemu pripada patnja ponižavajuća.');
INSERT INTO "bs_darassalam" VALUES(4,15,'4:15','Za one žene iz vaše porodice koje preljubu počine, tražite da posvjedoče četverica između vas, pa ako posvjedoče protiv njih, držite ih u kućama sve dok ih smrt ne umori ili dok im Allah neki put ne nađe.');
INSERT INTO "bs_darassalam" VALUES(4,16,'4:16','A ono dvoje koji to učine, izgrdite, pa ako se pokaju i poprave, onda ih na miru ostavite. Allah je, doista, Onaj Koji stalno prima pokajanja i milostiv je.');
INSERT INTO "bs_darassalam" VALUES(4,17,'4:17','Allah prima samo pokajanje onih koji učine kakvo loše djelo iz neznanja, a potom se ubrzo pokaju. Njima će Allah oprostiti, a Allah je Onaj Koji sve zna i mudar je.');
INSERT INTO "bs_darassalam" VALUES(4,18,'4:18','Nema pokajanja onima koji čine loša djela sve dok im se ne približi smrt, pa onda govore: "Sad se, doista, kajem!", niti za one koji umru kao nevjernici. Njima smo bolnu patnju pripremili.');
INSERT INTO "bs_darassalam" VALUES(4,19,'4:19','O vi koji vjerujete, ne dozvoljava vam se da žene preko volje njihove u naslijeđe uzimate i da im poteškoće pričinjavate s namjerom da nešto od onoga što ste im dali prisvojite, osim ako budu učinile očiti razvrat. Prema njima se lijepo ophodite. A ako prema njima ne osjetite želju, pa moguće je da ne volite nešto u čemu je Allah veliko dobro dao.');
INSERT INTO "bs_darassalam" VALUES(4,20,'4:20','Ako hoćete jednu suprugu zamijeniti drugom, i jednoj od njih dali ste blaga, ne uzimajte od toga ništa. Zar da to uzimate čineći potvoru i grijeh očiti?!');
INSERT INTO "bs_darassalam" VALUES(4,21,'4:21','Kako biste mogli to i uzeti, kada ste jedno s drugim intimni bili i kad su one od vas čvrstu obavezu uzele!?');
INSERT INTO "bs_darassalam" VALUES(4,22,'4:22','I ne ženite se onima koje su oženili očevi vaši nakon onoga što je bilo – to su bili razvrat, mržnja i ružan put.');
INSERT INTO "bs_darassalam" VALUES(4,23,'4:23','Zabranjuju vam se majke vaše, i kćeri vaše, i sestre vaše, i sestre očeva vaših, i sestre majki vaših, i bratične vaše i sestrične vaše, i pomajke vaše, koje su vas dojile, i sestre vaše po mlijeku, i majke supruga vaših, i pastorke vaše koje se nalaze pod vašim skrbništvom od supruga vaših s kojim ste imali intimne odnose; ako s njima niste imali intimne odnose, onda vam nije grijeh; i supruge vaših rođenih sinova, i da dvije sestre sastavite, osim što je ranije bilo! Allah je, doista, Onaj Koji oprašta grijehe i milostiv je.');
INSERT INTO "bs_darassalam" VALUES(4,24,'4:24','I zabranjuju vam se udate žene, osim onih koje su u vlasništvu vašem. To vam je propis Allahov! A ostalo vam je dozvoljeno da želite, svojom imovinom da se njima oženite, a ne da blud činite! A suprugama vašim s kojima imate bračne odnose dajte vjenčane darove kao što je propisano. I nije vam grijeh ako se, poslije određenog vjenčanog dara, s njima nagodite. Allah je, doista, Onaj Koji sve zna i mudar je.');
INSERT INTO "bs_darassalam" VALUES(4,25,'4:25','A onaj među vama koji nije dovoljno imućan da se oženi slobodnom vjernicom – eto mu one vjernice koje su u vašem vlasništvu – a Allah najbolje zna vjerovanje vaše – jedni ste od drugih. I ženite se njima uz odobrenje vlasnika njihovih, i dajte im vjenčane darove kako je lijepo i uobičajeno, ako su čedne, a nisu preljubnice, niti tajne ljubavnice. A ako one budu udate, pa počine razvrat, neka budu kažnjene polovinom kazne propisane za slobodne žene. To je za onoga od vas koji se boji razvrata, a bolje vam je da se uzdržite, a Allah je Onaj Koji oprašta grijehe i milostiv je.');
INSERT INTO "bs_darassalam" VALUES(4,26,'4:26','Allah vam želi objasniti i uputiti vas putevima kojima su išli oni prije vas i oprostiti vam. Allah je Onaj Koji sve zna i mudar je.');
INSERT INTO "bs_darassalam" VALUES(4,27,'4:27','Allah vam želi oprostiti, a oni koji se za strastima svojim povode žele da daleko skrenete.');
INSERT INTO "bs_darassalam" VALUES(4,28,'4:28','Allah vam želi olakšati, a čovjek je stvoren slab.');
INSERT INTO "bs_darassalam" VALUES(4,29,'4:29','O vi koji vjerujete, jedni od drugih na nedozvoljen način imanja ne prisvajajte, osim trgovinom uz obostrani pristanak, i sami sebe ne ubijajte. Allah je, doista, prema vama milostiv.');
INSERT INTO "bs_darassalam" VALUES(4,30,'4:30','Onoga koji to nepravično i nasilno učini, Mi ćemo u Vatri pržiti, a to je Allahu lahko.');
INSERT INTO "bs_darassalam" VALUES(4,31,'4:31','Ako se budete klonili velikih grijeha koji su vam zabranjeni, Mi ćemo pokriti vaša loša djela i u divno mjesto vas uvesti.');
INSERT INTO "bs_darassalam" VALUES(4,32,'4:32','I ne poželite ono čime je Allah neke od vas odlikovao nad drugima: muškarcima pripada udio za ono što zarade, a ženama udio za ono što one zarade. I molite Allaha za obilje Njegovo. Allah, doista, sve dobro zna.');
INSERT INTO "bs_darassalam" VALUES(4,33,'4:33','Za sve smo odredili nasljednike u onome što ostave roditelji i rođaci, a onima s kojima ste vezani ugovorom dajte njihov dio! Allah je, doista, svemu svjedok.');
INSERT INTO "bs_darassalam" VALUES(4,34,'4:34','Muškarci su nadređeni i staraju se o ženama zato što je Allah odlikovao jedne nad drugima i zato što oni troše imetke svoje. Zbog toga su čestite žene poslušne i za vrijeme muževljeva odsustva čuvaju ono što je Allah odredio da se čuva. One čije neposlušnosti se pribojavate vi posavjetujte, a onda napustite postelje njihove, pa ih i udarite. Ako vam postanu poslušne, protiv njih ništa ne činite. Allah je, doista, uzvišen i velik.');
INSERT INTO "bs_darassalam" VALUES(4,35,'4:35','A ako se bojite razdora između njih dvoje, onda pošaljite jednog suca iz njegove, a jednog iz njene porodice. Ako oni žele izmirenje, Allah će ih pomiriti; Allah je, zaista, sveznajući i Onaj Koji sve, i u potpunosti, zna.');
INSERT INTO "bs_darassalam" VALUES(4,36,'4:36','I Allahu ibadet činite i ništa Mu ne pridružujte, a roditeljima činite dobro, i rođacima, i siročadima, i siromasima, i komšiji bližnjem, i komšiji daljnjem, i drugu pored sebe, i putniku, i onome koji je u vašem posjedu. Allah, doista, ne voli one koji se ohole i hvališu.');
INSERT INTO "bs_darassalam" VALUES(4,37,'4:37','To su oni koji škrtare i zapovijedaju drugima da budu škrti, i koji kriju ono što im je Allah iz obilja Svoga darovao, a Mi smo nevjernicima pripremili patnju ponižavajuću.');
INSERT INTO "bs_darassalam" VALUES(4,38,'4:38','I oni koji troše imetke svoje pretvarajući se pred ljudima, a u Allaha i ahiret ne vjeruju. A kome je šejtan drug, to mu je zao drug.');
INSERT INTO "bs_darassalam" VALUES(4,39,'4:39','A šta bi im bilo da u Allaha i Posljednji dan vjeruju i da od onoga što im je Allah dao udjeljuju?! A Allah o njima sve zna.');
INSERT INTO "bs_darassalam" VALUES(4,40,'4:40','Zaista Allah ni trunku nepravde ne čini, pa ako to bude dobro djelo, On će ga umnogostručiti i od Sebe nagradu veliku dati.');
INSERT INTO "bs_darassalam" VALUES(4,41,'4:41','A šta će tek biti kada iz svakoga naroda dovedemo svjedoka, a i tebe dovedemo da o ovima svjedočiš?!');
INSERT INTO "bs_darassalam" VALUES(4,42,'4:42','Taj dan će poželjeti oni koji nisu vjerovali i koji su se protiv Poslanika dizali da zemlja nad njima bude poravnana, a od Allaha nijednu riječ neće moći sakriti.');
INSERT INTO "bs_darassalam" VALUES(4,43,'4:43','O vi koji vjerujete, ne pristupajte namazu pijani, dok ne budete znali šta govorite, niti kada ste džunubi, osim kao prolaznici, sve dok se ne okupate. A ako ste bolesni, ili na putu, ili ako je neko od vas došao iz nužnika, ili ako ste imali odnos sa suprugama, a ne nađete vode, onda dlanovima čistu zemlju dotaknite i lica svoja i ruke svoje potarite. Allah je, doista, Onaj Koji stalno grijehe poništava i oprašta.');
INSERT INTO "bs_darassalam" VALUES(4,44,'4:44','Zar ne vidiš kako oni kojima je dat udio u Knjizi kupuju zabludu i žele da vi s Pravog puta skrenete?!');
INSERT INTO "bs_darassalam" VALUES(4,45,'4:45','Allah najbolje poznaje neprijatelje vaše; Allah je dovoljan zaštitnik i Allah je dovoljan pomagač.');
INSERT INTO "bs_darassalam" VALUES(4,46,'4:46','Onima koji su judejstvo prihvatili, koji izvrću smisao riječi govoreći: "Čujemo, ali se ne pokoravamo!" i: "Slušaj, dabogda ne čuo!" i: "Čuvaj nas!", prevrćući jezicima svojim i huleći vjeru, bolje bi bilo i ispravnije da kažu: "Slušamo i pokoravamo se!" i: "Slušaj!" i: "Pogledaj na nas!" Ali, Allah je njih zbog nevjerovanja njihova prokleo i malo ko od njih vjeruje.');
INSERT INTO "bs_darassalam" VALUES(4,47,'4:47','O vi kojima je Knjiga data, povjerujte u ovo što objavljujemo potvrđujući ono što već imate, prije nego što crte lica izbrišemo i vratimo ih nazad ili ih prokunemo kao što smo prokleli one koji nisu subotu poštivali, a zapovijest Allahova biva izvršena.');
INSERT INTO "bs_darassalam" VALUES(4,48,'4:48','Allah, doista, neće oprostiti da se Njemu išta u obožavanju pridružuje, a oprostit će sve mimo toga, kome On hoće! A onaj ko drugog Allahu u obožavanju pridružuje, taj čini potvoru i grijeh veliki.');
INSERT INTO "bs_darassalam" VALUES(4,49,'4:49','Zar ne vidiš one koji sebe smatraju od grijeha čistim?! Međutim, Allah čisti od grijeha koga hoće i nikome se neće, ni koliko nit na hurminoj košpici, zulum učiniti.');
INSERT INTO "bs_darassalam" VALUES(4,50,'4:50','Pogledaj kako izmišljaju laž o Allahu! To je Allahu dovoljno kao jasan grijeh.');
INSERT INTO "bs_darassalam" VALUES(4,51,'4:51','Zar ne vidiš one kojima je dat udio u Knjizi kako u sihr i taguta – šejtana vjeruju, a onima koji ne vjeruju govore: "Ovi su na ispravnijem putu od onih koji vjeruju?"');
INSERT INTO "bs_darassalam" VALUES(4,52,'4:52','To su oni koje je Allah prokleo, a ti nećeš naći da će pomagača imati onaj koga je Allah prokleo.');
INSERT INTO "bs_darassalam" VALUES(4,53,'4:53','Da možda oni nemaju kakav udio u vlasti?! A da imaju, oni ljudima ništa – ni koliko udubljenje na hurminoj košpici – ne bi dali.');
INSERT INTO "bs_darassalam" VALUES(4,54,'4:54','I zar ljudima zavide na onome što im je Allah dao iz obilja svoga?! A Mi smo potomcima Ibrahimovim Knjigu i mudrost dali, i dali smo im veliku vlast.');
INSERT INTO "bs_darassalam" VALUES(4,55,'4:55','Pa, bilo ih je koji su vjerovali, a bilo je i onih koji su od toga odvraćali. A džehennem je dovoljan kao oganj užareni.');
INSERT INTO "bs_darassalam" VALUES(4,56,'4:56','Doista, one koji ne vjeruju u ajete i znakove Naše Mi ćemo u Vatru baciti, pa kada god im se kože ispeku, zamijenit ćemo ih drugim kožama, kako bi patnju osjetili. Allah je, doista, silan i mudar.');
INSERT INTO "bs_darassalam" VALUES(4,57,'4:57','A one koji vjeruju i dobra djela čine Mi ćemo uvesti u džennetske bašče kroz koje rijeke teku. Tamo će zauvijek ostati i supruge očišćene u njima imati; i u debelu hladovinu ćemo ih uvesti.');
INSERT INTO "bs_darassalam" VALUES(4,58,'4:58','Allah vam, doista, naređuje da emanete date onima kojima pripadaju, a kada ljudima sudite – da pravedno sudite. Savjet Allahov je, uistinu, divan i Allah je, doista, Onaj Koji sve čuje i Onaj Koji sve vidi.');
INSERT INTO "bs_darassalam" VALUES(4,59,'4:59','O vi koji vjerujete, pokoravajte se Allahu i pokoravajte se Poslaniku i nadležnim između vas. A ako se u nečemu ne slažete, obratite se Allahu i Poslaniku, ako vjerujete u Allaha i Posljednji dan. To je bolje i posljedice su ljepše.');
INSERT INTO "bs_darassalam" VALUES(4,60,'4:60','Zar ne vidiš one koji tvrde da vjeruju u ono što je objavljeno tebi i u ono što je objavljeno prije tebe kako žele da im se pred tagutom – lažnim sudijom sudi, iako im je naređeno da u njega ne vjeruju?! A šejtan samo želi da ih u veliku zabludu navede.');
INSERT INTO "bs_darassalam" VALUES(4,61,'4:61','I kada im se kaže: "Pristupite ovome što je Allah objavio i pristupite Poslaniku", vidiš licemjere kako se od tebe sasvim okreću.');
INSERT INTO "bs_darassalam" VALUES(4,62,'4:62','A šta će tek biti kada ih pogodi nevolja zbog djela ruku njihovih, pa ti dođu zaklinjući se Allahom: "Mi smo samo htjeli da dobro i pravo učinimo."');
INSERT INTO "bs_darassalam" VALUES(4,63,'4:63','To su oni za koje Allah zna šta je u srcima njihovim, pa okreni se od njih i savjetuj ih, i reci im ono što će ih dirnuti.');
INSERT INTO "bs_darassalam" VALUES(4,64,'4:64','A mi nismo nijednog poslanika poslali, osim da bi mu se pokoravalo uz Allahovo odobrenje. A da ti oni dođu kada sami sebi zulum učine i zamole Allaha da im oprosti, pa da i Poslanik zamoli da im se oprosti, oni bi vidjeli da Allah, doista, prima pokajanje i da je milostiv.');
INSERT INTO "bs_darassalam" VALUES(4,65,'4:65','I, tako Mi Gospodara tvoga, oni neće vjerovati dok za sudiju u sporovima međusobnim tebe ne prihvate, a potom u dušama svojim tegobe ne osjete za ono što si odredio i sasvim se ne predaju!');
INSERT INTO "bs_darassalam" VALUES(4,66,'4:66','I da smo Mi njima propisali "da sami sebe poubijate!" ili "da se iselite iz svoga zavičaja!", malo ko od njih bi to učinio! A kada bi onako kako im se savjetuje postupili, bilo bi im bolje i u vjeri čvršće.');
INSERT INTO "bs_darassalam" VALUES(4,67,'4:67','I tada bismo im Mi, od Nas, veliku nagradu dali.');
INSERT INTO "bs_darassalam" VALUES(4,68,'4:68','I na Pravi put bismo ih uputili.');
INSERT INTO "bs_darassalam" VALUES(4,69,'4:69','Oni koji su pokorni Allahu i Poslaniku bit će u društvu onih koje je Allah blagodario: vjerovjesnika, onih koji srcem iskreno i u potpunosti vjeruju i šehida i čestitih ljudi, a divno li je to društvo!');
INSERT INTO "bs_darassalam" VALUES(4,70,'4:70','To obilje je od Allaha i dovoljno je da Allah sve to dobro zna.');
INSERT INTO "bs_darassalam" VALUES(4,71,'4:71','O vi koji vjerujete, budite oprezni i idite u borbu u grupama, ili odjednom svi.');
INSERT INTO "bs_darassalam" VALUES(4,72,'4:72','Među vama ima, doista, onih koji će oklijevati, pa će, ako vas pogodi nevolja, takav reći: "Sam Allah me blagodario što s njima nisam bio."');
INSERT INTO "bs_darassalam" VALUES(4,73,'4:73','A ako vas od Allaha dobro zadesi, on će, kao da između vas i njega nije bilo prijateljstva, sigurno reći: "Kamo sreće da sam s njima bio, veliki plijen bih dobio!?"');
INSERT INTO "bs_darassalam" VALUES(4,74,'4:74','I neka se zato na Allahovu putu vjernici bore protiv onih koji život na dunjaluku kupuju za ahiret, a onoga ko se na Allahovu putu bori, pa pogine ili pobijedi, Mi ćemo, sigurno, velikom nagradom nagraditi.');
INSERT INTO "bs_darassalam" VALUES(4,75,'4:75','Šta je s vama pa se ne borite na Allahovom putu za nemoćne; za muškarce, žene i djecu koji govore: "Gospodaru naš, izbavi nas iz ovoga grada čiji su stanovnici nasilnici, odredi nam zaštitnika i daj nam pomagača!"');
INSERT INTO "bs_darassalam" VALUES(4,76,'4:76','Oni koji vjeruju bore se na Allahovom putu, a oni koji ne vjeruju bore se na putu taguta – šejtana. Zato se borite protiv štićenika šejtanovih, a šejtanovo lukavstvo, doista, je slabo.');
INSERT INTO "bs_darassalam" VALUES(4,77,'4:77','Zar nisi vidio one kojima je rečeno: "K sebi ruke svoje, već namaz obavljajte i zekat dajite." A kada im bi propisana borba, odjednom se grupa njih poboja ljudi, kao što se Allaha boji, ili još više, i rekoše: "Gospodaru naš, zašto si nam borbu propisao? Da si nam to do određenog vremena odgodio?" Reci: "Uživanje na dunjaluku je kratko, a ahiret je bolji za bogobojazne." A vama, ni koliko nit na hurminoj košpici, zulum neće biti učinjen.');
INSERT INTO "bs_darassalam" VALUES(4,78,'4:78','Gdje god bili, stići će vas smrt, pa makar bili i u kulama visokim! Ako ih zadesi kakvo dobro, oni kažu: "To je od Allaha!", a ako ih zadesi kakvo zlo, oni govore: "To je od tebe!" Reci: "Sve je od Allaha!" Pa šta je s ovim ljudima?! Oni kao da ne razumiju šta im se govori.');
INSERT INTO "bs_darassalam" VALUES(4,79,'4:79','Dobro koje te zadesi, to je od Allaha, a zlo koje te zadesi, to je od tebe. Mi smo te kao poslanika ljudima poslali, a Allah je dovoljan svjedok.');
INSERT INTO "bs_darassalam" VALUES(4,80,'4:80','Ko se pokori Poslaniku, pokorio se i Allahu, a ko se okrene, pa Mi te nismo poslali da im čuvar budeš.');
INSERT INTO "bs_darassalam" VALUES(4,81,'4:81','Oni govore: "Pokoravamo se!", a kada od tebe odu, neki od njih noću smišljaju drugačije nego što ti govoriš. Allah zapisuje ono što oni noću smišljaju. Zato se okreni od njih i u Allaha se pouzdaj, a dovoljan je zaštitnik Allah!');
INSERT INTO "bs_darassalam" VALUES(4,82,'4:82','A zašto oni ne promišljaju o Kur''anu? Jer, da je on od nekoga drugog, a ne od Allaha, oni bi u njemu našli mnoge protivrječnosti.');
INSERT INTO "bs_darassalam" VALUES(4,83,'4:83','A kada im dođe nešto što se odnosi na sigurnost ili strah, oni to razglase. A da to prepuste Poslaniku ili pretpostavljenim svojima, to bi sigurno od njih saznali oni koji to ispituju. A da nije dobrote Allahove prema vama i milosti Njegove, vi biste, osim nekolicine, šejtana slijedili.');
INSERT INTO "bs_darassalam" VALUES(4,84,'4:84','Zato se bori na Allahovom putu, ne obavezujući osim sebe, a vjernike podstiči. Allah će zaustaviti silu onih koji ne vjeruju, a Allah je od njih silniji i kazne Njegove su strožije.');
INSERT INTO "bs_darassalam" VALUES(4,85,'4:85','Ko se bude za dobro zalagao, i on će imati udio u tome, a ko se bude za zlo zalagao, i on će imati udio u tome. A Allah nad svim bdije.');
INSERT INTO "bs_darassalam" VALUES(4,86,'4:86','Kada pozdravljeni budete, na ljepši način uzvratite, ili isto otpozdravite, a Allah, doista, sve obračunava.');
INSERT INTO "bs_darassalam" VALUES(4,87,'4:87','Allah, osim Koga drugoga boga nema, sigurno će vas sakupiti na Sudnjem danu, u što nema nimalo sumnje, a čiji je govor istinitiji od Allahovog?!');
INSERT INTO "bs_darassalam" VALUES(4,88,'4:88','Zašto se podvajate kada su u pitanju licemjeri koje je Allah vratio u nevjerstvo zbog onoga što su sami zaslužili? Zar želite na Pravi put uputiti one kojima je Allah dao da su u zabludi? A kome Allah da da je u zabludi, ti mu nećeš Put naći!');
INSERT INTO "bs_darassalam" VALUES(4,89,'4:89','Oni bi voljeli da i vi ne vjerujete kao što oni ne vjeruju, pa da budete jednaki. Zato, između njih ne uzimajte prijatelje dok se na Allahovom Putu ne isele. A ako se okrenu, onda ih zarobljavajte i ubijajte gdje god ih nađete i od njih prijatelje ni pomagače ne uzimajte...');
INSERT INTO "bs_darassalam" VALUES(4,90,'4:90','...osim onih koji se sklone kod naroda s kojim vi imate ugovor, ili dođu kod vas, jer im je teško boriti se protiv vas ili svoga roda. A da Allah hoće, on bi ih okrenuo protiv vas, pa bi se oni borili protiv vas! Ako vas oni ostave na miru i ne bore se protiv vas, nego vam ponude mir, onda vam Allah ne daje pravo da ih napadate.');
INSERT INTO "bs_darassalam" VALUES(4,91,'4:91','Vi ćete nalaziti druge koji žele biti sigurni od vas i naroda svog, pa kada god budu vraćeni na smutnju – širk, oni mu se još više predaju. Ako se oni ne okane vas, i ne ponude vam mir, i ako vas se ne klone, onda ih hvatajte i ubijajte gdje god stignete, a Mi smo vam za borbu protiv njih jasan dokaz dali.');
INSERT INTO "bs_darassalam" VALUES(4,92,'4:92','Vjernik ne može ubiti vjernika osim greškom, a ko ubije vjernika greškom, on mora osloboditi roba vjernika, i predati krvarinu porodici njegovoj, izuzev ako oni to oproste. Ako on pripada narodu koji vam je neprijatelj, a vjernik je, on mora osloboditi jednog roba vjernika! Ako, pak, pripada narodu s kojim imate sporazum, tada mora dati krvarinu porodici njegovoj i osloboditi roba vjernika! Ko to ne nađe, obavezan je postiti dva mjeseca uzastopno, kajući se Allahu. A Allah je Onaj Koji sve zna i mudar je.');
INSERT INTO "bs_darassalam" VALUES(4,93,'4:93','A ko hotimično ubije vjernika, kazna mu je džehennem u kojem će vječno ostati. Na njega se Allah rasrdi i proklete ga, a još mu je i patnju veliku pripremio.');
INSERT INTO "bs_darassalam" VALUES(4,94,'4:94','O vi koji vjerujete, kada u boj na Allahovom putu krenete, sve dobro provjerite i onome ko vam nazove selam nemojte reći: "Ti nisi vjernik!", u želji za prolaznim dobrima dunjalučkog života, jer kod Allaha su mnoga dobra. Takvi ste ranije i vi bili, pa vam je Allah darovao milost Svoju. Zato, sve dobro provjerite, a Allah, doista, dobro zna ono što vi radite.');
INSERT INTO "bs_darassalam" VALUES(4,95,'4:95','Vjernici koji se ne bore, osim onih koji su nesposobni, ne mogu se izjednačiti s onima koji se na Allahovu putu bore imecima svojim i životima svojim. One koji se bore imecima svojim i životima svojim Allah je odlikovao za stepen nad onima koji se ne bore, i svima Allah obećava lijepu nagradu. A Allah je borce, u odnosu na one koji se ne bore, odlikovao velikom nagradom;');
INSERT INTO "bs_darassalam" VALUES(4,96,'4:96','položajima kod Njega, oprostom i milošću, a Allah je Onaj Koji oprašta grijehe i milostiv je.');
INSERT INTO "bs_darassalam" VALUES(4,97,'4:97','Doista će meleki, kada budu uzimali duše onima koji su prema sebi zulum učinili, pitati: "Šta je bilo s vama?'''', a oni će odgovoriti: "Bili smo nemoćni na Zemlji." "Zar Allahova Zemlja nije prostrana i zar se niste mogli nekuda iseliti?'''', reći će im oni. Zato će njihovo prebivalište biti džehennem, a loše je to konačno odredište!');
INSERT INTO "bs_darassalam" VALUES(4,98,'4:98','Izuzetak su nemoćni muškarci, i žene, i djeca, koji se snaći ne mogu i koji ne nađu puta.');
INSERT INTO "bs_darassalam" VALUES(4,99,'4:99','Allah će njima oprostiti; Allah je Onaj Koji stalno poništava grijehe i oprašta.');
INSERT INTO "bs_darassalam" VALUES(4,100,'4:100','Ko se iseli na putu Allahovom, na Zemlji će naći mnogo mjesta i obilja! A onaj ko napusti svoj dom, iseljavajući se radi Allaha i Njegovoga Poslanika, pa ga stigne smrt, od Allaha će dobiti nagradu. Allah je Onaj Koji oprašta grijehe i milostiv je.');
INSERT INTO "bs_darassalam" VALUES(4,101,'4:101','Kada idete po Zemlji, nije vam grijeh namaz skratiti ako se bojite da će vam smutnju nevjernici učiniti, jer nevjernici su vam, doista, otvoreni neprijatelji.');
INSERT INTO "bs_darassalam" VALUES(4,102,'4:102','Kada ti budeš među njima i budeš ih u namazu predvodio, neka jedna grupa s tobom klanja i neka svoje oružje uzmu; i dok budu na sedždi, neka drugi budu iza vas, a onda neka dođe druga grupa koja nije klanjala i neka s tobom klanjaju, ali neka drže oružje svoje i oprezni budu. Nevjernici jedva čekaju da vi zanemarite oružje svoje i opremu svoju, pa da na vas svi odjednom navale. A ako vam bude smetala kiša ili budete bolesni, nije vam grijeh oružje svoje odložiti, ali, oprezni budite.Allah je, doista, nevjernicima ponižavajuću patnju pripremio.');
INSERT INTO "bs_darassalam" VALUES(4,103,'4:103','A kada namaz završite, Allaha spominjite, i stojeći, i sjedeći, i ležeći na bokovima. A kada budete sigurni, namaz obavljajte u potpunosti; namaz je, uistinu, propisan vjernicima u određenim vremenskim razmacima.');
INSERT INTO "bs_darassalam" VALUES(4,104,'4:104','I nemojte malaksati tragajući za neprijateljem; ako vi trpite bol, trpe i oni bol kao i vi, a vi se još od Allaha nadate onome čemu se oni ne nadaju. Allah je Onaj Koji sve zna i mudar je.');
INSERT INTO "bs_darassalam" VALUES(4,105,'4:105','Mi tebi objavljujemo Knjigu s istinom, da ljudima sudiš onako kako ti Allah pokazuje i ne budi branilac izdajicama.');
INSERT INTO "bs_darassalam" VALUES(4,106,'4:106','I Allaha moli za oprost; Allah je, uistinu, Onaj Koji oprašta grijehe i milostiv je.');
INSERT INTO "bs_darassalam" VALUES(4,107,'4:107','I ne zauzimaj se za one koji sami sebe iznevjeravaju, Allah, doista, ne voli onoga koji je pronevjeritelj i grešnik.');
INSERT INTO "bs_darassalam" VALUES(4,108,'4:108','Oni se kriju od ljudi, a ne kriju se od Allaha, a On je s njima i kada noću smišljaju riječi kojima On nije zadovoljan. A Allah sve što oni rade, obuhvata.');
INSERT INTO "bs_darassalam" VALUES(4,109,'4:109','Eto, vi se zauzimate za njih u životu na dunjaluku, a ko će se pred Allahom za njih zauzimati na Kijametskom danu, ili ko će im zastupnik biti?!');
INSERT INTO "bs_darassalam" VALUES(4,110,'4:110','Ko učini kakvo zlo, ili prema sebi zulum učini, a zatim Allaha moli da mu oprosti, naći će da Allah je Onaj Koji oprašta grijehe i milostiv je.');
INSERT INTO "bs_darassalam" VALUES(4,111,'4:111','Ko grijeh uradi, na svoju štetu ga je uradio, a Allah je Onaj Koji sve zna i mudar je.');
INSERT INTO "bs_darassalam" VALUES(4,112,'4:112','Ko počini prestup ili grijeh, a potom zato optuži nedužna čovjeka, natovario je na sebe i kletvu i grijeh očiti.');
INSERT INTO "bs_darassalam" VALUES(4,113,'4:113','Da nije Allahove dobrote i milosti Njegove prema tebi, jedna skupina njih bi nastojala da te zavede, ali oni samo sebe zavode, a tebi ne mogu ništa nauditi. Allah tebi objavljuje Knjigu i mudrost i poučava te onome što nisi znao a Allahovo dobro prema tebi je veliko.');
INSERT INTO "bs_darassalam" VALUES(4,114,'4:114','Nema dobra u mnogim njihovim potajnim govorima, osim u govoru onog ko traži da se sadaka udjeljuje, ili dobro čini, ili uspostavlja mir među ljudima. Onome ko to čini iz želje za Allahovim zadovoljstvom, Mi ćemo veliku nagradu dati.');
INSERT INTO "bs_darassalam" VALUES(4,115,'4:115','A onoga ko se suprotstavi Poslaniku, nakon što mu jasno bude pokazan Pravi put, i slijedi put koji nije put vjernika, pustit ćemo da čini što je naumio, i bacit ćemo ga u džehennem, a loše je to konačno odredište!');
INSERT INTO "bs_darassalam" VALUES(4,116,'4:116','Allah, doista, neće oprostiti da se Njemu išta u obožavanju pridružuje, a kome hoće oprostit će sve osim toga; onaj ko Allahu išta u obožavanju pridružuje – doista je daleko zalutao.');
INSERT INTO "bs_darassalam" VALUES(4,117,'4:117','Oni mimo Allaha samo ženske kumire prizivaju i ne prizivaju drugog do šejtana prkosnika.');
INSERT INTO "bs_darassalam" VALUES(4,118,'4:118','Allah ga je prokleo, a on je rekao: "Ja ću sigurno preuzeti za sebe određeni dio Tvojih robova!');
INSERT INTO "bs_darassalam" VALUES(4,119,'4:119','I sigurno ću ih navoditi u zabludu, i sigurno ih lažnim nadama mamiti, i zapovjedit ću im sigurno da uši stoci režu, i sigurno ću im narediti da Allahovo stvaranje preinačavaju!" A ko šejtana, umjesto Allaha, za zaštitnika prihvati, on je na gubitku očitom.');
INSERT INTO "bs_darassalam" VALUES(4,120,'4:120','On im obećanja daje i lažnim nadama ih mami. A ono što im šejtan obećava samo je obmana.');
INSERT INTO "bs_darassalam" VALUES(4,121,'4:121','To su oni čije je prebivalište džehennem, i oni iz njega neće spasa naći.');
INSERT INTO "bs_darassalam" VALUES(4,122,'4:122','A one koji vjeruju i dobra djela čine Mi ćemo uvesti u bašče džennetske kroz koje rijeke teku, i u kojima će vječno ostati. Obećanje Allahovo je istinito, a čije su riječi od Allahovih istinitije?!');
INSERT INTO "bs_darassalam" VALUES(4,123,'4:123','To neće biti ni po vašim željama, ni po željama sljedbenika Knjige; onaj koji radi zlo, bit će kažnjen za to i neće naći, mimo Allaha, ni zaštitnika ni pomagača,');
INSERT INTO "bs_darassalam" VALUES(4,124,'4:124','a onaj koji čini dobro, bio muškarac ili žena, a vjernik je, ući će u džennet i nikome, ni koliko udubljenje na hurminoj košpici, zulum neće biti nanesen.');
INSERT INTO "bs_darassalam" VALUES(4,125,'4:125','Ko je bolje vjere od onoga ko je predao svoje lice Allahu, a dobročinitelj je i slijedi vjeru Ibrahimovu, koji je bio vjere čiste?! A Allah je uzeo Ibrahima za prijatelja.');
INSERT INTO "bs_darassalam" VALUES(4,126,'4:126','Allahovo je sve što je na nebesima i što je na Zemlji. Allah sve obuhvata.');
INSERT INTO "bs_darassalam" VALUES(4,127,'4:127','Oni traže od tebe propise o ženama. Reci: "Allah će vam objasniti propise o njima i o onome što vam se čita u Knjizi o ženama –siroticama, kojima ne dajete ono što im je propisano, a ne želite da se njima oženite, i o nejakoj djeci, i o tome da prema siročadi pravedno postupate. A Allah sigurno zna za dobro koje učinite."');
INSERT INTO "bs_darassalam" VALUES(4,128,'4:128','Ako se neka žena plaši muževe mržnje ili izbjegavanja, onda se oni neće ogriješiti ako se međusobno nagode, a nagodba je bolja. Ljudske duše sklone su pohlepi. I ako vi budete lijepo postupali i Allaha se bojali – pa Allah u potpunosti zna ono što radite.');
INSERT INTO "bs_darassalam" VALUES(4,129,'4:129','Vi nećete moći potpuno jednako postupati prema suprugama svojim ma koliko to željeli. I nemojte prema nekoj sasvim naginjati pa da neku učinite kao da je bez muža, a nerazvedena. A ako se popravite i Allaha bojite, pa Allah je, uistinu, Onaj Koji oprašta grijehe i milostiv je.');
INSERT INTO "bs_darassalam" VALUES(4,130,'4:130','A ako se njih dvoje ipak rastave, Allah će ih, iz obilja Svoga, neovisnim učiniti. Allah je neizmjerno darežljiv i mudar.');
INSERT INTO "bs_darassalam" VALUES(4,131,'4:131','Allahovo je ono što je na nebesima i ono što je na Zemlji. Mi smo onima kojima je data Knjiga prije vas, a i vama, već zapovjedili da se bojite Allaha. A ako ne budete vjerovali, pa, uistinu, Allahovo je ono što je na nebesima i ono što je na Zemlji, Allah je neovisan i hvaljen.');
INSERT INTO "bs_darassalam" VALUES(4,132,'4:132','Allahovo je ono što je na nebesima i što je na Zemlji, i Allah je kao zaštitnik dovoljan.');
INSERT INTO "bs_darassalam" VALUES(4,133,'4:133','Ako On hoće, vas će ukloniti, o ljudi, a druge će dovesti! On je kadar to učiniti.');
INSERT INTO "bs_darassalam" VALUES(4,134,'4:134','Onaj koji želi nagradu na dunjaluku, pa u Allaha je nagrada i ovoga i onoga svijeta; Allah je Onaj Koji sve čuje i Onaj Koji sve vidi.');
INSERT INTO "bs_darassalam" VALUES(4,135,'4:135','O vi koji vjerujete, budite uvijek pravedni, svjedočite radi Allaha, pa makar to bilo protiv vas samih, ili roditelja i rodbine. Bude li onaj o kome svjedočite bogat ili siromah, pa Allah je preči njima! I ne slijedite strasti, kako ne biste bili nepravedni. A ako budete krivo svjedočili ili svjedočenje izbjegavati, pa Allah, zaista, u potpunosti zna ono što radite.');
INSERT INTO "bs_darassalam" VALUES(4,136,'4:136','O vi koji vjerujete, vjerujte u Allaha i Poslanika Njegova, i u Knjigu koju On Svome Poslaniku objavljuje i u knjige koje je objavio prije. A onaj koji ne bude vjerovao u Allaha, i u meleke Njegove, i u knjige Njegove, i u poslanike Njegove, i u Posljednji dan – daleko je zalutao.');
INSERT INTO "bs_darassalam" VALUES(4,137,'4:137','Onima koji su bili vjernici, a zatim postali nevjernici, pa opet postali vjernici i ponovo postali nevjernici, i pojačali nevjerovanje, Allah, doista, neće oprostiti i neće ih na Pravi put izvesti.');
INSERT INTO "bs_darassalam" VALUES(4,138,'4:138','Bolnu patnju navijesti licemjerima,');
INSERT INTO "bs_darassalam" VALUES(4,139,'4:139','koji prisno prijateljuju s nevjernicima, mimo vjernika! Zar od njih traže moć, a sva moć pripada samo Allahu?!');
INSERT INTO "bs_darassalam" VALUES(4,140,'4:140','On vam je već u Knjizi objavio: "Kada čujete da su Allahove riječi predmet poricanja i izrugivanja, ne sjedite s onima koji to čine dok ne stupe u drugi razgovor, inače, bit ćete kao oni." Allah će, sigurno, sastaviti u džehennemu sve licemjere i nevjernike,');
INSERT INTO "bs_darassalam" VALUES(4,141,'4:141','one koji iščekuju šta će biti s vama. Pa ako vam Allah daruje pobjedu, oni reknu: "Zar nismo bili uz vas", a ako sreća posluži nevjernike, onda govore njima: "Zar vas nismo mogli pobijediti, i zar vas nismo odbranili od vjernika?!" Na Kijametskom danu Allah će vam svima presuditi! A Allah neće dati priliku nevjernicima da unište vjernike.');
INSERT INTO "bs_darassalam" VALUES(4,142,'4:142','Licemjeri, zaista, nastoje prevariti Allaha, a On će njih nadmudriti. Kada ustaju da namaz obavljaju, lijeno se dižu i prikazuju se svijetu, a Allaha samo malo spominju.');
INSERT INTO "bs_darassalam" VALUES(4,143,'4:143','Kolebaju se između ovoga i toga – vjerovanja i nevjerovanja, niti su sa ovima niti sa onima. A onome kome Allah da da je u zabludi nećeš naći puta ka Uputi.');
INSERT INTO "bs_darassalam" VALUES(4,144,'4:144','O vi koji vjerujete, ne uzimajte za zaštitnike i prijatelje nevjernike umjesto vjernika! Zar hoćete da pružite Allahu očigledan dokaz protiv sebe?!');
INSERT INTO "bs_darassalam" VALUES(4,145,'4:145','Licemjeri će, doista, na samom dnu džehennema biti i ti im nećeš pomagača naći.');
INSERT INTO "bs_darassalam" VALUES(4,146,'4:146','Osim onih koji se pokaju i poprave i koji čvrsto Allaha prihvate i vjeru svoju u Allaha iskreno ispolje, oni će biti s vjernicima, a Allah će, sigurno, vjernicima veliku nagradu dati.');
INSERT INTO "bs_darassalam" VALUES(4,147,'4:147','Zašto bi vas Allah kažnjavao ako budete zahvaljivali i vjerovali!? Allah je zahvalan i Onaj Koji sve zna.');
INSERT INTO "bs_darassalam" VALUES(4,148,'4:148','Allah ne voli da se o zlu glasno govori, to može samo onaj kome je zulum učinjen. A Allah je Onaj Koji sve čuje i Onaj Koji sve zna.');
INSERT INTO "bs_darassalam" VALUES(4,149,'4:149','Bilo da vi dobro djelo javno učinite, ili ga sakrijete, ili nepravdu oprostite – pa Allah je Onaj Koji stalno poništava grijehe i svemoćan je.');
INSERT INTO "bs_darassalam" VALUES(4,150,'4:150','Oni koji u Allaha i poslanike Njegove ne vjeruju i žele između Allaha i poslanika Njegovih u vjerovanju napraviti razliku, te govore: "U neke vjerujemo, a u neke ne vjerujemo", i žele između to dvoje iznaći put neki...');
INSERT INTO "bs_darassalam" VALUES(4,151,'4:151','...oni su zbilja pravi nevjernici, a Mi smo nevjernicima pripremili ponižavajuću patnju.');
INSERT INTO "bs_darassalam" VALUES(4,152,'4:152','A one koji u Allaha i poslanike Njegove vjeruju i ni jednoga od njih ne izdvajaju – On će, sigurno, nagraditi. A Allah je Onaj Koji oprašta grijehe i milostiv je.');
INSERT INTO "bs_darassalam" VALUES(4,153,'4:153','Oni kojima je data Knjiga traže od tebe da im s neba spustiš Knjigu. Pa od Musaa su tražili i više od toga, kad su rekli: "Pokaži nam Allaha!" Pa ih je, zbog zuluma njihova, munja ošinula. Poslije su, kada su im očigledni dokazi pokazani, tele za božanstvo prihvatili, ali smo i to oprostili, a Musau smo protiv njih očiti dokaz dali.');
INSERT INTO "bs_darassalam" VALUES(4,154,'4:154','I iznad njih smo brdo digli, zbog zavjeta koji su dali, i rekli smo im: "Na kapiju pognutih glava uđite!" I još smo rekli: "Subotu ne oskrnavite!" I od njih smo čvrsto obećanje uzeli.');
INSERT INTO "bs_darassalam" VALUES(4,155,'4:155','Zato što su zavjet prekršili i što u Allahove ajete i znakove nisu povjerovali, što su bez razloga vjerovjesnike ubijali i što su govorili: "Naša srca su u omotu", Allah je, zbog nevjerovanja, njihova srca zapečatio, pa ih samo malo vjeruje.');
INSERT INTO "bs_darassalam" VALUES(4,156,'4:156','I zbog nevjerovanja njihovoga i zbog iznošenja teške klevete protiv Merjeme.');
INSERT INTO "bs_darassalam" VALUES(4,157,'4:157','I zbog riječi njihovih: "Mi smo ubili Mesiha, Isaa, sina Merjemina, Allahova poslanika." A nisu ga ubili, ni razapeli, već im se pričinilo. Oni koji su se o njemu u mišljenju razišli, zaista su u sumnji u pogledu njega; o tome ništa pouzdano ne znaju, samo se za nagađanjem povode; a sigurno je da ga nisu ubili...');
INSERT INTO "bs_darassalam" VALUES(4,158,'4:158','...već ga je Allah uzdigao Sebi. A Allah je silan i mudar.');
INSERT INTO "bs_darassalam" VALUES(4,159,'4:159','I nema nijednog sljedbenika Knjige koji, prije njegove smrti, neće u njega kako treba povjerovati, a na Kijametskom danu on će protiv njih svjedočiti.');
INSERT INTO "bs_darassalam" VALUES(4,160,'4:160','I zbog zuluma jevreja Mi smo im neka lijepa jela zabranili, koja su im bila dozvoljena, i zbog toga što su mnoge od Allahova puta odvraćali.');
INSERT INTO "bs_darassalam" VALUES(4,161,'4:161','I zato što su kamatu uzimali, a bilo im je zabranjeno, i zato što su tuđe imetke na nedozvoljen način jeli. A za nevjernike među njima Mi smo patnju bolnu pripremili.');
INSERT INTO "bs_darassalam" VALUES(4,162,'4:162','Ali oni među njima koji su u znanje duboko pronikli, i vjernici koji vjeruju u ono što se objavljuje tebi i u ono što je objavljeno prije tebe, naročito oni koji namaz obavljaju i oni koji zekat daju i u Allaha i Posljednji dan vjeruju – njima ćemo sigurno veliku nagradu dati.');
INSERT INTO "bs_darassalam" VALUES(4,163,'4:163','Mi objavljujemo tebi kao što smo objavljivali Nuhu i vjerovjesnicima poslije njega, objavljivali smo Ibrahimu, i Ismailu i Ishaku i Jakubu i potomcima, i Isau, i Ejjubu, i Junusu, i Harunu, i Sulejmanu, a Davudu smo dali Zebur...');
INSERT INTO "bs_darassalam" VALUES(4,164,'4:164','...i poslali smo poslanike o kojima smo ti prije kazivali i poslanike o kojima ti nismo kazivali. A Allah je sigurno s Musaom razgovarao.');
INSERT INTO "bs_darassalam" VALUES(4,165,'4:165','Poslali smo poslanike koji su radosne vijesti i opomene donosili, da ljudi poslije poslanika ne bi nikakvog opravdanja pred Allahom imali. A Allah je silan i mudar.');
INSERT INTO "bs_darassalam" VALUES(4,166,'4:166','Allah svjedoči da je istina ono što ti objavljuje, objavljuje ga Svojim znanjem, a i meleki svjedoče. A dovoljan je Allah kao svjedok.');
INSERT INTO "bs_darassalam" VALUES(4,167,'4:167','Doista, oni koji ne vjeruju i koji druge od Allahova puta odvraćaju, daleko su zalutali.');
INSERT INTO "bs_darassalam" VALUES(4,168,'4:168','Onima koji ne vjeruju i koji zulum čine, Allah, doista, neće oprostiti i neće ih na put uputiti...');
INSERT INTO "bs_darassalam" VALUES(4,169,'4:169','...osim puta u džehennem, u kojem će vječno i zauvijek ostati; to je Allahu lahko.');
INSERT INTO "bs_darassalam" VALUES(4,170,'4:170','O ljudi, Poslanik vam je već donio istinu od Gospodara vašeg; zato, vjerujte – bolje vam je! A ako ne budete vjerovali – pa zaista je Allahovo sve što je na nebesima i na Zemlji. Allah je Onaj Koji sve zna i mudar je.');
INSERT INTO "bs_darassalam" VALUES(4,171,'4:171','O vi kojima je data Knjiga, ne zastranjujte u svom vjerovanju i o Allahu govorite samo istinu! Mesih, Isa, sin Merjemin, samo je Allahov poslanik, i Njegova Riječ, koju je Merjemi dostavio, i duh Njegov; zato vjerujte u Allaha i Njegove poslanike i ne govorite: "Trojica su!" Prestanite, bolje vam je! Allah je samo jedan bog, uzvišen neka je On, zar da ima dijete?! Njegovo je ono što je na nebesima i ono što je na Zemlji, i Allah je dovoljan kao zaštitnik.');
INSERT INTO "bs_darassalam" VALUES(4,172,'4:172','Mesih se neće sustezati da bude Allahov rob, pa ni meleki, Njemu bliski. A one koji se budu sustezali da Njemu ibadet čine, i one koji se budu oholili, Allah će pred Sobom sakupiti.');
INSERT INTO "bs_darassalam" VALUES(4,173,'4:173','Vjernike koji su dobra djela činili On će prema zasluzi nagraditi i još će im, iz obilja Svoga, više dati; a one koji su zazorni bili i oholili se – na bolne će muke staviti i oni neće naći sebi, mimo Allaha, ni zaštitnika ni pomagača.');
INSERT INTO "bs_darassalam" VALUES(4,174,'4:174','O ljudi, dokaz vam je već stigao od Gospodara vašeg i Mi smo vam objavili jasnu Svjetlost.');
INSERT INTO "bs_darassalam" VALUES(4,175,'4:175','One koji budu u Allaha vjerovali i čvrsto Ga se držali On će sigurno u milost Svoju i blagodat uvesti i Pravim putem Sebi uputiti.');
INSERT INTO "bs_darassalam" VALUES(4,176,'4:176','Oni traže od tebe tumačenje. Reci: "Allah će vam kazati propis o onome ko nema ni roditelja ni djece: ako neko umre i ne bude imao djeteta, a ima sestru, njoj polovina njegove ostavštine pripada, a on će naslijediti nju ako ona ne bude imala dijete; a ako su dvije sestre, njima pripadaju dvije trećine njegove ostavštine. A ako su oni braća i sestre, onda će muški dio nasljedstva biti jednak iznosu dva dijela ženskog. To vam Allah objašnjava, da ne zalutate. A Allah sve dobro zna."');
INSERT INTO "bs_darassalam" VALUES(5,1,'5:1','O vi koji vjerujete, ispunjavajte preuzete obaveze! Dozvoljava vam se stoka, ali ne ona koja će vam biti spomenuta; dok obrede hadža obavljate nije vam dozvoljeno da lovite. Uistinu, Allah propisuje što On hoće.');
INSERT INTO "bs_darassalam" VALUES(5,2,'5:2','O vi koji vjerujete, ne skrnavite Allahove obrede hadža, ni sveti mjesec, ni kurbane, naročito one ogrlicama obilježene, ni one ljude koji su krenuli ka časnoj Kabi želeći od Gospodara svoga obilje i zadovoljstvo. A kada obrede hadža obavite, onda loviti možete. I neka vas mržnja koju prema nekim ljudima nosite, zato što su vam spriječili pristup Časnoj džamiji, nikako ne navede da pravdu skrnavite! Potpomažite se u dobročinstvu i bogobojaznosti, a ne potpomažite se u grijehu i skrnavljenju pravde i bojte se Allaha; Allah, doista, strašno kažnjava.');
INSERT INTO "bs_darassalam" VALUES(5,3,'5:3','Zabranjena vam je strv, i krv, i svinjsko meso, i ona životinja koja je zaklana u nečije drugo, a ne u Allahovo ime, i koja je udavljena i ubijena i koja je strmoglavljena, i rogom ubodena, ili od zvijeri načeta – osim ako ste je preklali – i koja je na žtrvenicima žrtvovana i zabranjuje vam se gatanje strelicama. To je neposluh! Danas su nevjernici izgubili svaku nadu da ćete vi otpasti od svoje vjere, zato se ne bojte njih, već se bojte Mene. Danas sam vam vjeru vašu usavršio i blagodat Svoju prema vama upotpunio i zadovoljan sam da vam islam bude vjera. A ko god bude primoran, kada hara glad, bez namjere da učini grijeh, pa Allah je, zaista, Oprostitelj grijeha, Milostivi.');
INSERT INTO "bs_darassalam" VALUES(5,4,'5:4','Pitaju te šta im je dozvoljeno. Reci: "Dozvoljena su vam lijepa jela i ono što ulove životinje koje ste lovu podučili, od onoga čime je Allah vas naučio. Jedite ono što vam one uhvate i spomenite Allahovo ime pri tome, i bojte se Allaha; Allah, zaista, brzo sviđa račune.');
INSERT INTO "bs_darassalam" VALUES(5,5,'5:5','Danas vam se dozvoljavaju sva lijepa jela; i jela onih kojima je data Knjiga dozvoljena su vam, i vaša su jela njima dozvoljena, i čestite žene vjernice, i čestite kćeri onih kojima je data Knjiga prije vas, kada im vjenčane darove njihove dadnete s namjerom da se njima oženite, a ne da s njima blud činite i da ih za prilježnice uzimate. A onaj ko niječe propise vjere – uzalud će mu biti djela njegova, i on će, na ahiretu, među gubitnicima biti.');
INSERT INTO "bs_darassalam" VALUES(5,6,'5:6','O vi koji vjerujete, kada hoćete namaz obaviti, lica svoja i ruke svoje do iza laktova operite – a glave svoje potarite – i noge svoje do iza članaka operite. Ako ste džunupi, onda se očistite; a ako ste bolesni, ili na putu, i izvršili ste prirodnu potrebu, ili ste imali odnos sa suprugama, a ne nađete vode, onda rukama svojim čistu zemlju dotaknite i njima preko lica svojih i ruku svojih pređite. Allah vam ne želi pričiniti poteškoće, već vas želi učiniti čistim i blagodat Svoju vam upotpuniti, da biste bili zahvalni.');
INSERT INTO "bs_darassalam" VALUES(5,7,'5:7','I sjetite se Allahove blagodati kojom vas je obasuo i zavjeta kojim vas je obavezao, kad ste rekli: "Slušamo i pokoravamo se!" I bojte se Allaha! Zaista Allah dobro zna sve što je u grudima.');
INSERT INTO "bs_darassalam" VALUES(5,8,'5:8','O vi koji vjerujete, dužnosti prema Allahu izvršavajte, i pravedno svjedočite. Neka vas mržnja koju prema nekim ljudima nosite nikako ne navede da nepravedni budete! Pravedni budite, to je najbliže bogobojaznosti, i bojte se Allaha. Allah, doista, upotpunosti zna ono što činite.');
INSERT INTO "bs_darassalam" VALUES(5,9,'5:9','Allah je onima koji vjeruju i dobra djela čine obećao oprost i nagradu veliku;');
INSERT INTO "bs_darassalam" VALUES(5,10,'5:10','a oni koji ne budu vjerovali i ajete i znakove Naše budu poricali – bit će stanovnici džehennema.');
INSERT INTO "bs_darassalam" VALUES(5,11,'5:11','O vi koji vjerujete, sjetite se Allahove blagodati prema vama kada su vas se neki ljudi htjeli dočepati, a On je zadržao ruke njihove. I bojte se Allaha, i neka se vjernici samo u Allaha pouzdaju!');
INSERT INTO "bs_darassalam" VALUES(5,12,'5:12','Allah je prihvatio zavjet sinova lsrailovih – a između njih bili smo postavili dvanaest starješina – i Allah je rekao: "Ja sam, doista, s vama! Ako budete namaz obavljali i zekat davali, i ako budete u poslanike Moje vjerovali, pomagali im i drage volje zajam Allahu davali, sigurno ću prekriti vaša loša djela i uvest ću vas u džennetske bašče kroz koje rijeke teku. A onaj među vama koji ni poslije toga ne bude vjerovao – s Puta pravoga je skrenuo."');
INSERT INTO "bs_darassalam" VALUES(5,13,'5:13','Ali, zato što su zavjet svoj prekršili, Mi smo ih prokleli i srca njihova okrutnim učinili. Oni su riječi s mjesta na kojima su bile uklanjali, a dobar dio onoga čime su bili opominjani izostavili. I ti ćeš, osim u slučaju malo njih, neprestano njihovo vjerolomstvo otkrivati, ali ti pređi preko toga i oprosti im! Allah, uistinu, voli one koji čine dobro.');
INSERT INTO "bs_darassalam" VALUES(5,14,'5:14','Od onih koji govore: "Mi smo kršćani", Mi smo zavjet prihvatili, ali su i oni dio onoga čime su bili opominjani izostavili, zato smo medu njih neprijateljstvo i mržnju do Kijametskog dana ubacili; a Allah će ih sigurno obavijestiti o onome što su radili.');
INSERT INTO "bs_darassalam" VALUES(5,15,'5:15','O vi kojima je data Knjiga, došao vam je Poslanik Naš da vam ukaže na mnogo šta što vi iz Knjige krijete, a preko mnogo čega će i preći. A od Allaha vam dolazi svjetlost i Knjiga jasna.');
INSERT INTO "bs_darassalam" VALUES(5,16,'5:16','Njome Allah upućuje na puteve spasa one koji nastoje steći zadovoljstvo Njegovo i izvodi ih, dozvolom Svojom, iz tmina na svjetlo i na Pravi put ih upućuje.');
INSERT INTO "bs_darassalam" VALUES(5,17,'5:17','Nevjernici su oni koji govore: "Doista je Bog Mesih sin Merjemin!" Reci: "Ko može spriječiti Allaha da, ako hoće, uništi Mesiha, sina Merjemina, i majku njegovu, i sve one koji su na Zemlji?" Allahova je vlast na nebesima i na Zemlji i na onome što je između njih; On stvara šta hoće, i Allah nad svime ima moć.');
INSERT INTO "bs_darassalam" VALUES(5,18,'5:18','jevreji i kršćani kažu: "Mi smo djeca Božija i miljenici Njegovi." Reci: "Pa zašto vas On kažnjava zbog grijeha vaših?" A nije tako! Vi ste kao i ostali ljudi koje On stvara: kome hoće On će oprostiti, a koga hoće On će patnji podvrgnuti. Allahova je vlast na nebesima i na Zemlji i na onome što je između njih, i kod Njega je konačno odredište.');
INSERT INTO "bs_darassalam" VALUES(5,19,'5:19','O vi kojima je data Knjiga, došao vam je Poslanik Naš – nakon što je neko vrijeme prekinuto slanje poslanika – da vam objasni, da ne biste rekli: "Nije nam dolazio ni onaj koji donosi radosne vijesti ni onaj koji upozorava!" Pa, došao vam je, eto, onaj koji donosi radosne vijesti i koji upozorava. A Allah nad svime ima moć.');
INSERT INTO "bs_darassalam" VALUES(5,20,'5:20','A kad Musa reče narodu svome: "O narode moj, sjetite se Allahove blagodati prema vama kada je neke od vas vjerovjesnicima učinio, i kada vas je vladarima učinio, i dao vam ono što nijednom narodu nije dao.');
INSERT INTO "bs_darassalam" VALUES(5,21,'5:21','O narode moj, uđite u Blagoslovljenu zemlju koju vam je Allah dodijelio, i ne uzmičite nazad pa da se vratite kao gubitnici,');
INSERT INTO "bs_darassalam" VALUES(5,22,'5:22','oni rekoše: "O Musa! U njoj je nemilosrdan narod i mi u nju nećemo ući dok god oni iz nje ne izađu; pa, ako oni iz nje izađu, mi ćemo onda sigurno ući."');
INSERT INTO "bs_darassalam" VALUES(5,23,'5:23','Dva čovjeka, koja su se Allaha bojala i kojima je On darovao blagodat Svoju, rekoše: "Navalite na njihovu kapiju, pa kad kroz nju prođete, bit ćete sigurno pobjednici; a u Allaha se pouzdajte, ako ste vjernici."');
INSERT INTO "bs_darassalam" VALUES(5,24,'5:24','"O Musa", rekoše oni, "dok god su oni u njoj, mi nećemo u nju ulaziti! Idite ti i Gospodar tvoj pa se borite, a mi se odavde nećemo micati!"');
INSERT INTO "bs_darassalam" VALUES(5,25,'5:25','"Gospodaru moj", reče Musa, "ja osim sebe imam moć samo nad bratom svojim; zato razluči između nas i buntovnih ljudi!"');
INSERT INTO "bs_darassalam" VALUES(5,26,'5:26','"Četrdeset godina oni će zemljom lutati", reče On, "jer će im Sveta zemlja zabranjena biti, a ti ne tuguj za narodom buntovnim."');
INSERT INTO "bs_darassalam" VALUES(5,27,'5:27','I ispričaj im vijest o dvojici Ademovih sinova, onako kako je bilo, kad su njih dvojica kurban žrtvovali, pa je od jednog bio primljen a od drugog nije, pa je on rekao: "Sigurno ću te ubiti!" A prvi reče: "Allah prima samo od onih koji su bogobojazni.');
INSERT INTO "bs_darassalam" VALUES(5,28,'5:28','I kad bi ti pružio ruku svoju prema meni da me ubiješ, ja ne bih pružio svoju prema tebi da te ubijem. Zaista se ja bojim Allaha, Gospodara svjetova.');
INSERT INTO "bs_darassalam" VALUES(5,29,'5:29','Ja želim da ti poneseš i moj i svoj grijeh i da budeš stanovnik u Vatri. A ona je kazna za sve zulumćare."');
INSERT INTO "bs_darassalam" VALUES(5,30,'5:30','I duša ga navede da ubije brata svoga, pa ga on ubi i postade jedan od gubitnika.');
INSERT INTO "bs_darassalam" VALUES(5,31,'5:31','Allah onda posla jednog gavrana da kopa po zemlji da bi mu pokazao kako da zakopa mrtvo tijelo brata svoga. "Teško meni", povika on, "zar i ja ne mogu, kao ovaj gavran, zakopati mrtvo tijelo brata svoga!" I pokajnik postade.');
INSERT INTO "bs_darassalam" VALUES(5,32,'5:32','Zbog toga smo Mi propisali sinovima Israilovim: ako neko ubije nekoga koji nije ubio nikoga, ili onoga koji na Zemlji nered ne čini – kao da je sve ljude poubijao; a ako neko bude uzrok da se nečiji život sačuva – kao da je svim ljudima život sačuvao. Naši su im poslanici jasne dokaze donosili, ali su mnogi od njih, i poslije toga, zaista, na Zemlji sve granice zla prelazili.');
INSERT INTO "bs_darassalam" VALUES(5,33,'5:33','Kazna za one koji protiv Allaha i Poslanika Njegova vojuju i koji nered na Zemlji čine jeste: da budu ubijeni, ili razapeti, ili da im se unakrst ruke i noge odsijeku, ili da iz zemlje budu prognani. To im je poniženje na dunjaluku, a na ahiretu čeka ih patnja velika,');
INSERT INTO "bs_darassalam" VALUES(5,34,'5:34','ali ne i za one koji se pokaju prije nego što ih se domognete. I znajte da je Allah Onaj Koji opršta grijehe i milostiv je.');
INSERT INTO "bs_darassalam" VALUES(5,35,'5:35','O vi koji vjerujete, Allaha se bojte i tražite ono što će vas Njemu približiti, i na putu Njegovu se borite da biste uspjeli.');
INSERT INTO "bs_darassalam" VALUES(5,36,'5:36','Zaista, kad bi sve ono što je na Zemlji bilo u posjedu nevjernika, i još toliko, i kad bi htjeli time da se otkupe od patnje na Kijametskom danu, ne bi im primljeno bilo. Njih čeka patnja bolna.');
INSERT INTO "bs_darassalam" VALUES(5,37,'5:37','Zaželjet će oni da iz Vatre iziđu, ali im iz nje izlaska neće biti, za njih će biti patnja neprestana.');
INSERT INTO "bs_darassalam" VALUES(5,38,'5:38','Kradljivcu i kradljivici odsijecite ruke, neka im to bude kazna za ono što su učinili i opomena od Allaha! Allah je silan i mudar.');
INSERT INTO "bs_darassalam" VALUES(5,39,'5:39','A onome ko se poslije zuluma svoga pokaje i popravi se – Allah će sigurno oprostiti, Allah je, doista, Onaj Koji oprašta grijehe i milostiv je.');
INSERT INTO "bs_darassalam" VALUES(5,40,'5:40','Ti sigurno znaš da samo Allah ima vlast na nebesima i na Zemlji. On patnji podvrgava onoga koga hoće, a prašta onome kome hoće. Allah nad svime ima moć.');
INSERT INTO "bs_darassalam" VALUES(5,41,'5:41','O Poslaniče, neka te ne zabrinjavaju oni što brzo nevjerovanje ispoljavaju, oni koji ustima svojim govore: "Vjerujemo", a srcem ne vjeruju, i jevreji, koji izmišljotine mnogo slušaju i koji tuđe riječi rado prihvataju, a tebi ne dolaze; koji riječi izvrću i govore: "Ako vam se ovako presudi, onda pristanite na to, a ako vam se ne presudi, onda nemojte pristati! A onome kome Allah želi zabludu, ti mu, u odnosu na Allaha, ništa pomoći ne možeš. To su oni čija srca Allah ne želi očistiti; njih na dunjaluku čeka poniženje, a na ahiretu patnja golema.');
INSERT INTO "bs_darassalam" VALUES(5,42,'5:42','Oni mnogo laži slušaju i mnogo ono što je zabranjeno jedu; pa ako ti dođu, ti im ili presudi ili se okreni od njih; ako se okreneš od njih, oni ti ne mogu nimalo nauditi. A ako im budeš sudio, sudi im pravo. Doista, Allah voli pravedne.');
INSERT INTO "bs_darassalam" VALUES(5,43,'5:43','A otkud da oni traže od tebe da im sudiš kad imaju Tevrat, u kome su Allahovi propisi? Oni ni poslije presude tvoje ne bi bili zadovoljni, i oni vjernici nisu.');
INSERT INTO "bs_darassalam" VALUES(5,44,'5:44','Mi smo objavili Tevrat, u kome je uputa i svjetlo. Po njemu su jevrejima sudili vjerovjesnici koji su bili Allahu poslušni, i pobožni ljudi, i učeni, od kojih je traženo da čuvaju Allahovu knjigu, i oni su nad njom bdjeli. Zato se, kad budete sudili, ne bojte ljudi, već se bojte Mene, i ne zamjenjujte riječi Moje za nešto što malo vrijedi! A oni što ne sude prema onom što je Allah objavio, oni su baš nevjernici.');
INSERT INTO "bs_darassalam" VALUES(5,45,'5:45','Mi smo im u njemu propisali: glava za glavu, i oko za oko, i nos za nos, i uho za uho, i zub za zub, da za rane treba uzvratiti, te da onome ko od odmazde odustane, bit će mu to od grijeha iskupljenje. Oni koji ne sude prema onome što je Allah objavio baš su zulumćari.');
INSERT INTO "bs_darassalam" VALUES(5,46,'5:46','Poslije njih smo Isaa, sina Merjemina, poslali, koji je priznavao Tevrat, prije njega objavljen, a njemu smo dali Indžil, u kojem je bila uputa i svjetlo, te da potvrdi Tevrat, prije njega objavljen, u kome je također bila uputa i pouka bogobojaznima,');
INSERT INTO "bs_darassalam" VALUES(5,47,'5:47','i sljedbenicima Indžila smo bili naredili da sude prema onome što je Allah objavio u njemu. Oni koji nisu sudili prema onome što je Allah objavio – baš su prema Allahu buntovni.');
INSERT INTO "bs_darassalam" VALUES(5,48,'5:48','A tebi objavljujemo Knjigu, s istinom, da potvrdi knjige prije objavljene i da nad njima bdi. I ti im sudi prema onome što Allah objavljuje i ne povodi se za prohtjevima njihovim, i ne odstupaj od Istine koja ti dolazi; svima vama smo zakon i pravac propisali. A da je Allah htio, On bi vas sljedbenicima jedne vjere učinio, ali, On hoće da vas iskuša u onome što vam propisuje, zato se natječite ko će više dobra učiniti; Allahu ćete se svi vratiti, pa će vas On o onome u čemu ste se razilazili obavijestiti.');
INSERT INTO "bs_darassalam" VALUES(5,49,'5:49','I sudi im prema onome što Allah objavljuje i ne povodi se za prohtjevima njihovim, i čuvaj ih se da te ne odvrate od nečega što ti Allah objavljuje. A ako se okrenu, ti onda znaj da ih Allah želi zbog nekih grijeha njihovih kazniti. A mnogi ljudi su, zaista, buntovnici.');
INSERT INTO "bs_darassalam" VALUES(5,50,'5:50','Zar oni da traže da im se kao u pagansko doba sudi!? A ko je od Allaha bolji sudija narodu koji čvrsto vjeruje?!');
INSERT INTO "bs_darassalam" VALUES(5,51,'5:51','O vi koji vjerujete, ne uzimajte za zaštitnike i prijatelje jevreje i kršćane! Oni su sami sebi zaštitnici i prijatelji! A njihov je i onaj među vama koji ih za zaštitnike i prijatelje prihvati; Allah, uistinu, neće uputiti na Pravi put ljude zulumćare.');
INSERT INTO "bs_darassalam" VALUES(5,52,'5:52','Zato ti vidiš one čija su srca bolesna kako se žure da s njima prijateljstvo sklope, govoreći: "Bojimo se da nas kakva nevolja ne zadesi." A Allah će sigurno pobjedu ili nešto drugo od Sebe dati, pa će se oni, zbog onoga što su u dušama svojim krili, kajati,');
INSERT INTO "bs_darassalam" VALUES(5,53,'5:53','a oni koji vjeruju reći će: "Jesu li to oni koji su se zaklinjali Allahom, svojom najjačom zakletvom, da su, zaista, s vama?" Djela njihova bit će poništena, i oni će gubitnici biti.');
INSERT INTO "bs_darassalam" VALUES(5,54,'5:54','O vi koji vjerujete, ako neko od vas od svoje vjere otpadne – pa, Allah će sigurno umjesto njih dovesti ljude koje On voli i koji Njega vole, prema vjernicima ponizne, a prema nevjernicima ponosite; oni će se na Allahovu putu boriti i neće se ničijeg prijekora bojati. To je Allahov dar koji On daje kome hoće – a Allah je neizmjerno darežljiv i Onaj Koji sve zna.');
INSERT INTO "bs_darassalam" VALUES(5,55,'5:55','Vaši su zaštitnici samo Allah i Poslanik Njegov i vjernici koji ponizno namaz obavljaju i zekat daju.');
INSERT INTO "bs_darassalam" VALUES(5,56,'5:56','Onaj ko za zaštitnika uzme Allaha i Poslanika Njegova i vjernike – pa, Allahova strana će, svakako, pobijediti.');
INSERT INTO "bs_darassalam" VALUES(5,57,'5:57','O vi koji vjerujete, ne uzimajte za zaštitnike i prijatelje one koji vašu vjeru za podsmijeh i zabavu uzimaju, bili to oni kojima je data Knjiga prije vas, ili bili nevjernici – i Allaha se bojte ako ste vjernici.');
INSERT INTO "bs_darassalam" VALUES(5,58,'5:58','I kad pozivate na namaz, i to za podsmijeh i zabavu uzimaju, zato što su oni ljudi koji ne razumiju.');
INSERT INTO "bs_darassalam" VALUES(5,59,'5:59','Reci: "O vi kojima je data Knjiga, zar da nas osuđujete zato što vjerujemo u Allaha i u ono što nam se objavljuje, i u ono što je objavljeno prije – a većina vas ste prema Allahu buntovni?"');
INSERT INTO "bs_darassalam" VALUES(5,60,'5:60','Reci: "Hoćete li da vam kažem koji su gori od njih i koje će Allah još teže kazniti? Oni koje je Allah prokleo i na koje se rasrdio i učinio od njih majmune, i svinje, i one koji su tagutu – šejtanu robovali – njih čeka najgore mjesto i oni su najdalje s Pravog puta odlutali."');
INSERT INTO "bs_darassalam" VALUES(5,61,'5:61','A kada vam dolaze, oni govore: "Vjerujemo!", ali oni dolaze kao nevjernici, a takvi i odlaze, a Allah dobro zna ono što oni kriju.');
INSERT INTO "bs_darassalam" VALUES(5,62,'5:62','Vidiš mnoge od njih kako u grijehe i nasilje srljaju i kako ono što je zabranjeno jedu; ružno li je to kako postupaju.');
INSERT INTO "bs_darassalam" VALUES(5,63,'5:63','Trebalo bi da ih čestiti i učeni ljudi od lažna govora i zabranjena jela odvraćaju; ružno li je to kako postupaju.');
INSERT INTO "bs_darassalam" VALUES(5,64,'5:64','jevreji govore: "Allahova ruka je stisnuta!" Stisnute bile ruke njihove i prokleti bili zbog toga što govore! Naprotiv, obje ruke Njegove su pružene, On udjeljuje koliko hoće. A to što ti objavljuje Gospodar tvoj pojačat će kod mnogih od njih osionost i nevjerovanje. Mi smo ubacili među njih neprijateljstvo i mržnju sve do Kijametskog dana. Kad god pokušaju potpaliti ratnu vatru, Allah je ugasi. Oni nastoje na Zemlji smutnju praviti, a Allah ne voli smutljivce.');
INSERT INTO "bs_darassalam" VALUES(5,65,'5:65','A da oni kojima je data Knjiga vjeruju i čuvaju se, Mi bismo prešli preko ružnih postupaka njihovih i sigurno bismo ih uveli u džennetske bašče uživanja.');
INSERT INTO "bs_darassalam" VALUES(5,66,'5:66','Da se oni pridržavaju Tevrata i lndžila i onoga što im objavljuje Gospodar njihov, imali bi šta jesti, i od onoga što je iznad njih i od onoga što je ispod nogu njihovih. Ima ih i umjerenih, ali ružno je ono što radi većina njih.');
INSERT INTO "bs_darassalam" VALUES(5,67,'5:67','O Poslaniče, kazuj ono što ti se objavljuje od Gospodara tvoga – ako ne učiniš, onda nisi dostavio poslanicu Njegovu – a Allah će te od ljudi štititi. Allah, zaista, neće uputiti na Pravi put narod koji ne vjeruje.');
INSERT INTO "bs_darassalam" VALUES(5,68,'5:68','Reci: "O vi kojima je data Knjiga, vi niste ni na čemu ako se ne budete pridržavali Tevrata i Indžila i onoga što vam objavljuje Gospodar vaš. A to što ti objavljuje Gospodar tvoj pojačat će, uistinu, kod mnogih od njih osionost i nevjerovanje, ali ti ne očajavaj zbog naroda koji ne vjeruje."');
INSERT INTO "bs_darassalam" VALUES(5,69,'5:69','Oni koji su vjerovali, i oni koji su bili jevreji, te sabijci, i kršćani – oni koji su u Allaha i u ahiret vjerovali i dobra djela činili – ničega se oni uistinu neće bojati i ni za čim oni neće tugovati.');
INSERT INTO "bs_darassalam" VALUES(5,70,'5:70','Mi smo od sinova Israilovih zavjet uzeli i poslanike im slali. Kad god bi im koji poslanik donio ono što nije godilo dušama njihovim, jedne su u laž utjerivali a druge ubijali.');
INSERT INTO "bs_darassalam" VALUES(5,71,'5:71','Mislili su da neće na muke biti stavljeni, pa su bili i slijepi i gluhi, i onda kad je Allah primio pokajanje njihovo, mnogi od njih su opet bili i slijepi i gluhi; a Allah dobro vidi ono šta oni rade.');
INSERT INTO "bs_darassalam" VALUES(5,72,'5:72','nevjernici su oni koji govore: "Bog je, doista, Mesih, sin Merjemin!" A Mesih je govorio: "O sinovi lsrailovi, ibadet činite Allahu, i mome i vašem Gospodaru!" Ko drugog Allahu u obožavanju pridružuje, Allah će mu ulazak u džennet zabraniti i boravište njegovo će džehennem biti; a zulumćarima neće niko moći pomoći.');
INSERT INTO "bs_darassalam" VALUES(5,73,'5:73','nevjernici su oni koji govore: "Allah je, zaista, jedan od trojice!" A samo je jedan Bog! I ako se ne okane onoga što govore, nesnosna patnja će, zaista, stići svakog od njih koji nevjernik ostane.');
INSERT INTO "bs_darassalam" VALUES(5,74,'5:74','Zašto se oni ne pokaju Allahu i ne zamole Njegov oprost, a Allah je Onaj Koji oprašta grijehe i milostiv je.');
INSERT INTO "bs_darassalam" VALUES(5,75,'5:75','Mesih, sin Merjemin, samo je poslanik – i prije njega su dolazili i odlazili poslanici – a majka njegova je iskreno srcem vjerovala; i oboje su hranu jeli. Pogledaj kako im Mi iznosimo jasne dokaze, i pogledaj, zatim, njih kako se odmeću!');
INSERT INTO "bs_darassalam" VALUES(5,76,'5:76','Reci: "Zar pored Allaha obožavate nešto što vam ne može nanijeti nikakvu štetu, niti vam korist neku pribaviti? A Allah je, baš, Svečujući i Sveznajući."');
INSERT INTO "bs_darassalam" VALUES(5,77,'5:77','Reci: "O vi kojima je data Knjiga, ne zastranjujte u vjerovanju svome, suprotno istini, i ne povodite se za prohtjevima ljudi koji su još davno zalutali, i mnoge u zabludu odveli, i sami s Pravoga puta skrenuli."');
INSERT INTO "bs_darassalam" VALUES(5,78,'5:78','Jezikom Davuda i Isaa, sina Merjemina, prokleti su oni sinovi Israilovi koji nisu vjerovali – zato što su neposlušni bili i uvijek granice prelazili;');
INSERT INTO "bs_darassalam" VALUES(5,79,'5:79','jedni druge od zla što činiše odvraćali nisu. Ružno li je, zaista, to kako su postupali!');
INSERT INTO "bs_darassalam" VALUES(5,80,'5:80','Ti vidiš mnoge od njih kako s višebošcima prijateljuju. Ružno je, zaista, ono što sami sebi pripremaju: da se Allah na njih rasrdi i da u patnji vječno ostanu.');
INSERT INTO "bs_darassalam" VALUES(5,81,'5:81','A da vjeruju u Allaha i Vjerovjesnika i u ono što se njemu objavljuje, oni s njima ne bi prijateljevali, ali mnogi su od njih prema Allahu buntovni.');
INSERT INTO "bs_darassalam" VALUES(5,82,'5:82','Ti ćeš sigurno naći da su onima koji vjeruju najljući neprijatelji jevreji i višebošci; i svakako ćeš naći da su vjernicima najbliži prijatelji oni koji govore: "Mi smo kršćani", zato što među njima ima svećenika i monaha i što se oni ne ohole.');
INSERT INTO "bs_darassalam" VALUES(5,83,'5:83','Kada slušaju ono što se objavljuje Poslaniku, vidiš kako im liju suze iz očiju jer znaju da je to istina, pa govore: "Gospodaru naš, mi vjerujemo, pa upiši i nas među one koji su posvjedočili..."');
INSERT INTO "bs_darassalam" VALUES(5,84,'5:84','Zašto da ne vjerujemo u Allaha i u istinu koja nam dolazi, kada žudimo da i nas Gospodar naš uvede s ljudima dobrim."');
INSERT INTO "bs_darassalam" VALUES(5,85,'5:85','I Allah će im, zbog onoga što govore, kao nagradu džennetske bašče dati, kroz koje rijeke teku, u kojima će vječno boraviti; a to će biti nagrada za sve one koji dobra djela čine.');
INSERT INTO "bs_darassalam" VALUES(5,86,'5:86','a oni koji ne budu vjerovali i ajete i znakove Naše budu poricali – bit će stanovnici džehennema.');
INSERT INTO "bs_darassalam" VALUES(5,87,'5:87','O vi koji vjerujete, ne zabranjujte sebi ono što je lijepo, što je Allah dozvolio, samo ne prelazite granicu! Zaista Allah ne voli one koji granicu prelaze.');
INSERT INTO "bs_darassalam" VALUES(5,88,'5:88','I jedite ono što vam Allah daje, što je dozvoljeno i lijepo; i bojte se Allaha, u Kojeg vjerujete.');
INSERT INTO "bs_darassalam" VALUES(5,89,'5:89','Allah vas neće kazniti ako se zakunete nenamjerno, ali će vas kazniti ako se zakunete namjerno. Otkup za prekršenu zakletvu je: da deset siromaha običnom hranom kojom hranite čeljad svoju nahranite, ili da ih odjenete, ili da roba ropstva oslobodite. A onaj ko ne bude mogao – neka tri dana posti. Tako se za zakletve svoje otkupljujte kada se zakunete; a o zakletvama svojim brinite se! Eto, tako vam Allah objašnjava propise Svoje, da biste bili zahvalni.');
INSERT INTO "bs_darassalam" VALUES(5,90,'5:90','O vi koji vjerujete, opojna pića i kocka i kumiri i strelice za gatanje odvratno su, šejtanovo djelo; zato se toga klonite, da biste uspjeli.');
INSERT INTO "bs_darassalam" VALUES(5,91,'5:91','Šejtan želi pomoću opojnih pića i kocke unijeti među vas neprijateljstvo i mržnju i od sjećanja na Allaha i obavljanja namaza odvratiti vas. Pa, hoćete li se okaniti?');
INSERT INTO "bs_darassalam" VALUES(5,92,'5:92','I budite poslušni Allahu i budite poslušni Poslaniku i oprezni budite! A ako se okrenete, onda znajte da je Poslanik Naš dužan samo da jasno obznani.');
INSERT INTO "bs_darassalam" VALUES(5,93,'5:93','Onima koji vjeruju i dobra djela čine nema nikakva grijeha u onome što su pojeli i popili prije zabrane, ako se klone onoga što im je zabranjeno i ako vjeruju i dobra djela čine, zatim se Allaha boje i vjeruju i onda se grijeha klone i dobro čine. A Allah voli one koji drugima dobro čine.');
INSERT INTO "bs_darassalam" VALUES(5,94,'5:94','O vi koji vjerujete, Allah će vas dovoditi u iskušenje sa divljači koja će biti nadohvat ruku vaših i kopalja vaših – da bi Allah znao onoga koji Ga se boji kad ga niko ne vidi. A onoga ko i poslije toga nasilje učini, bolna patnja čeka.');
INSERT INTO "bs_darassalam" VALUES(5,95,'5:95','O vi koji vjerujete, ne ubijajte divljač dok ste u ihramima! Onome od vas ko to hotimično učini kazna je da jednu domaću životinju, čiju će vrijednost procijeniti dvojica vaših pravednih ljudi, kao kurban zakolje kod Kabe, ili da se iskupi time što će, ravno tome, nahraniti siromahe ili postiti, da bi osjetio pogubnost postupka svoga. A Allah je već oprostio ono što je bilo. Onoga ko to opet uradi – Allah će kazniti. Allah je silan i strog.');
INSERT INTO "bs_darassalam" VALUES(5,96,'5:96','Vama se dopušta da u moru lovite i ulov jedete, da se njime vi i putnici koristite, a zabranjuje vam se da na kopnu lovite dok obrede hadža obavljate. I bojte se Allaha, pred Kojim ćete biti sabrani.');
INSERT INTO "bs_darassalam" VALUES(5,97,'5:97','Allah je učinio da Kaba, Sveta kuća, bude ljudima temelj sigurnosti i unapređenja života, a tako i sveti mjeseci i kurbani, naročito oni ogrlicama označeni. To je zato da znate da Allah zna ono što je na nebesima i ono što je na Zemlji, i da Allah sve dobro zna.');
INSERT INTO "bs_darassalam" VALUES(5,98,'5:98','Znajte da Allah strogo kažnjava, ali i da je Onaj Koji oprašta grijehe i milostiv je.');
INSERT INTO "bs_darassalam" VALUES(5,99,'5:99','Poslaniku je jedino dužnost objaviti, a Allah zna i ono što javno činite i ono što sakrijete.');
INSERT INTO "bs_darassalam" VALUES(5,100,'5:100','Reci: "Nije isto ono što je zabranjeno i ono što je dozvoljeno", iako te iznenađuje mnoštvo onoga što je zabranjeno. Zato se Allaha bojte, o vi koji razum imate, da biste uspjeli.');
INSERT INTO "bs_darassalam" VALUES(5,101,'5:101','O vi koji vjerujete, ne zapitkujte o onome što će vam pričiniti neprijatnosti ako vam bude objašnjeno; a ako budete pitali za to dok Kur''an bude objavljivan, bit će vam objašnjeno; ono ranije Allah vam je već oprostio. A Allah je Onaj Koji oprašta grijehe i Onaj Koji ne žuri s kaznom.');
INSERT INTO "bs_darassalam" VALUES(5,102,'5:102','Neki su ljudi prije vas pitali za to, pa poslije u to nisu povjerovali.');
INSERT INTO "bs_darassalam" VALUES(5,103,'5:103','Allah nije propisao ni behiru ni saibu ni vasilu a ni hama; to oni koji ne vjeruju govore o Allahu laži, i većina njih ne razumiju.');
INSERT INTO "bs_darassalam" VALUES(5,104,'5:104','A kada im se kaže: "Slijedite ono što je Allah objavio!" – oni odgovaraju: "Ne, nego ćemo slijediti ono na čemu smo naše pretke zatekli." Zar i onda kada im preci nisu ništa shvaćali, niti su na Pravom putu bili?!');
INSERT INTO "bs_darassalam" VALUES(5,105,'5:105','O vi koji vjerujete, brinite se o sebi; ako ste na Pravom putu, neće vam nauditi onaj koji je zalutao! Allahu ćete se svi vratiti i On će vas obavijestiti o onome što ste radili.');
INSERT INTO "bs_darassalam" VALUES(5,106,'5:106','O vi koji vjerujete, kada vam se približi smrt, prilikom davanja oporuke neka vam posvjedoče dvojica pravednih od vas, ili neka druga dvojica, koji nisu vaši – ako ste na putu, a pojave se znaci smrti. A ako posumnjate, zadržite ih poslije obavljenog namaza i neka se Allahom zakunu: "Mi zakletvu ni za kakvu cijenu nećemo prodati, makar se radilo i o kakvu rođaku i svjedočenje koje je Allah propisao nećemo uskratiti, jer bismo tada doista bili grešnici."');
INSERT INTO "bs_darassalam" VALUES(5,107,'5:107','A ako se dozna da su njih dvojica zgriješila, onda će njih zamijeniti druga dvojica od onih kojima je šteta nanesena, i neka se Allahom zakunu: "Naše zakletve su sigurno vjerodostojnije od zakletvi njihovih, mi se nismo krivo zakleli, jer bismo tada, zaista, u redovima zulumćara bili."');
INSERT INTO "bs_darassalam" VALUES(5,108,'5:108','Time će biti najbliže ispravnom svjedočenju, i da se ne plaše da će njihove zakletve drugim zakletvama biti pobijene. I bojte se Allaha i slušajte! A Allah neće uputiti na Pravi put ljude buntovnike.');
INSERT INTO "bs_darassalam" VALUES(5,109,'5:109','Na dan kada Allah sakupi poslanike i upita: "Kako su vam se odazivali?", oni će reći: "Mi znanja nemamo; zaista Ti dobro znaš sve što je čulima nedokučivo."');
INSERT INTO "bs_darassalam" VALUES(5,110,'5:110','Kad Allah rekne: "O Isa, sine Merjemin, sjeti se blagodati Moje prema tebi i majci tvojoj kada sam te Duhom Blagoslovljenim pomogao pa si s ljudima, u bešici i kao zreo čovjek, razgovarao; i kada sam te pismenosti i mudrosti, i Tevratu i Indžilu naučio; i kada si, dozvolom Mojom, od blata nešto poput ptice oblikovao i u nju udahnuo, i kada je ona, dozvolom Mojom, postala ptica; i kada si, dozvolom Mojom, od rođenja slijepa i gubava iscijelio; i kada si, dozvolom Mojom, mrtve dizao; i kada sam od tebe sinove Israilove odbio, kad si im ti jasne dokaze donio, pa su oni među njima koji nisu vjerovali – povikali: "Ovo nije ništa drugo do jasna vradžbina",');
INSERT INTO "bs_darassalam" VALUES(5,111,'5:111','i kada sam učenicima naredio: "Vjerujte u Mene i Poslanika Moga", oni su odgovorili: "Vjerujemo, a Ti budi svjedok da smo muslimani."');
INSERT INTO "bs_darassalam" VALUES(5,112,'5:112','A kada učenici rekoše: "O Isa, sine Merjemin, može li nam Gospodar tvoj trpezu s neba spustiti?", on reče: "Bojte se Allaha, ako ste vjernici."');
INSERT INTO "bs_darassalam" VALUES(5,113,'5:113','Mi želimo, rekoše oni, s nje jesti i da srca naša budu smirena i uvjeriti se da si nam istinu govorio, i da budemo oni koji će o njoj svjedočiti.');
INSERT INTO "bs_darassalam" VALUES(5,114,'5:114','Isa, sin Merjemin, reče: "O Allahu, Gospodaru naš, spusti nam s neba trpezu da nam bude praznik, i prvima od nas i onima kasnijim, i znak Tvoj, i opskrbi nas, a Ti si Opskrbitelj najbolji!"');
INSERT INTO "bs_darassalam" VALUES(5,115,'5:115','Allah reče: "Ja ću vam je, zaista, spustiti, ali ću one među vama koji i poslije budu poricali podvrgnuti patnji kakvom nikoga među svjetovima neću mučiti."');
INSERT INTO "bs_darassalam" VALUES(5,116,'5:116','A kada Allah rekne: "O Isa, sine Merjemin, jesi li ti govorio ljudima: ''Prihvatite mene i majku moju kao dva boga uz Allaha!''" on će reći: "Uzvišen si Ti od toga! Meni nije prilično da govorim ono što nemam pravo. Ako sam ja to govorio, Ti to već znaš; Ti znaš šta je u meni, a ja ne znam šta je u Tebi; baš Ti, zaista, dobro znaš ono što je čulima nedokučivo."');
INSERT INTO "bs_darassalam" VALUES(5,117,'5:117','Ja sam im samo govorio ono što si im Ti naredio: "Ibadet činite Allahu, i mome i vašem Gospodaru!" I ja sam njima svjedok bio dok sam među njima bio, a kad si me Ti k Sebi uzeo, Ti si ih jedini nadzirao; Ti nad svime bdiješ.');
INSERT INTO "bs_darassalam" VALUES(5,118,'5:118','Ako ih patnji podvrgneš, robovi su Tvoji, a ako im oprostiš, pa, uistinu si Ti Silni i Mudri.');
INSERT INTO "bs_darassalam" VALUES(5,119,'5:119','Allah će reći: "Ovo je Dan u kome će iskrenima od koristi iskrenost njihova biti; njima će džennetske bašče, kroz koje teku rijeke, pripasti; vječno i dovijeka u njima će ostati. Allah će zadovoljan njima biti, a i oni Njim. To će veliki uspjeh biti."');
INSERT INTO "bs_darassalam" VALUES(5,120,'5:120','Allahova je vlast na nebesima i na Zemlji i nad onim što je na njima; On nad svime ima moć.');
INSERT INTO "bs_darassalam" VALUES(6,1,'6:1','Sva hvala neka je Allahu, Koji je nebesa i Zemlju stvorio i tmine i svjetlo dao, ali i pored toga oni koji ne vjeruju – druge sa Gospodarom svojim izjednačuju.');
INSERT INTO "bs_darassalam" VALUES(6,2,'6:2','On vas je od zemlje stvorio i rok odredio, a u Njega je i rok utvrđeni, a vi i pored toga sumnjate.');
INSERT INTO "bs_darassalam" VALUES(6,3,'6:3','On je Allah na nebesima i na Zemlji, On zna i što krijete i što pokazujete, i On zna ono što radite.');
INSERT INTO "bs_darassalam" VALUES(6,4,'6:4','A njima – nevjernicima ne dođe nijedan dokaz od Gospodara njihova a da se od njega ne okrenu.');
INSERT INTO "bs_darassalam" VALUES(6,5,'6:5','Oni su zanijekali istinu kada im je došla, ali njima će sigurno stići vijesti o onome čemu su se rugali.');
INSERT INTO "bs_darassalam" VALUES(6,6,'6:6','Zar oni ne znaju koliko smo Mi naraštaja prije njih uništili, kojima smo na Zemlji mogućnosti davali kakve vama nismo dali i kojima smo kišu obilatu slali i učinili da rijeke pored njih teku, pa smo ih, zbog grijeha njihovih, uništavali, i druge naraštaje, poslije njih, stvarali?');
INSERT INTO "bs_darassalam" VALUES(6,7,'6:7','A i da ti Knjigu na papiru spustimo i da je oni rukama svojim opipaju, opet bi oni koji ne vjeruju rekli: "Ovo zaista nije ništa drugo do jasna vradžbina."');
INSERT INTO "bs_darassalam" VALUES(6,8,'6:8','I još govore: "Zašto mu ne bude poslan melek?!" A da meleke pošaljemo, slučaj bi određen bio, i više se na njih ne bi čekalo.');
INSERT INTO "bs_darassalam" VALUES(6,9,'6:9','A da ga melekom učinimo, opet bismo ga kao čovjeka stvorili i opet bismo im učinili nejasnim ono što im jasno nije.');
INSERT INTO "bs_darassalam" VALUES(6,10,'6:10','Poslanicima su se i prije tebe rugali, pa je one koji su im se rugali stiglo baš ono čemu su se rugali.');
INSERT INTO "bs_darassalam" VALUES(6,11,'6:11','Reci: "Putujte po Zemlji, zatim pogledajte kako su završili oni koji su poslanike lažnim smatrali!"');
INSERT INTO "bs_darassalam" VALUES(6,12,'6:12','Upitaj: "Čije je sve ono što je na nebesima i na Zemlji?", i odgovori: "Allahovo!" On je sebi propisao da bude milostiv. On će vas na Kijametskom danu sakupiti, u to nema nikakve sumnje; oni koji su sebe upropastili – pa, oni ne vjeruju.');
INSERT INTO "bs_darassalam" VALUES(6,13,'6:13','Njemu pripada sve što postoji u noći i danu; On je Svečujući i Sveznajući.');
INSERT INTO "bs_darassalam" VALUES(6,14,'6:14','Reci: "Zar da za zaštitnika uzmem nekog drugog osim Allaha, Koji je nebesa i Zemlju, bez prethodnog primjera stvorio?! On hrani, a hranjen ne biva!" Reci: "Meni je naređeno da budem prvi ko se pokorava", i nikako ne budi jedan od višebožaca.');
INSERT INTO "bs_darassalam" VALUES(6,15,'6:15','Reci: "Ako budem neposlušan svome Gospodaru, ja se plašim patnje na Velikom danu."');
INSERT INTO "bs_darassalam" VALUES(6,16,'6:16','A onaj ko toga dana bude pošteđen, On mu se smilovao, i to je očiti uspjeh.');
INSERT INTO "bs_darassalam" VALUES(6,17,'6:17','Ako te Allah nekom nevoljom pogodi – pa, niko je osim Njega ne može otkloniti; a ako ti kakvo dobro podari – pa, On nad svime ima moć.');
INSERT INTO "bs_darassalam" VALUES(6,18,'6:18','A On je iznad robova Svojih Svepotčinitelj – i On je Mudri, Onaj Koji sve u potpunosti zna.');
INSERT INTO "bs_darassalam" VALUES(6,19,'6:19','Reci: "Šta je po svjedočenju najveće?", i odgovori: "Allah, On će između mene i vas svjedok biti. A meni se ovaj Kur''an objavljuje da njime vas i one do kojih on dopre upozoravam. Zar vi, zaista, tvrdite da pored Allaha ima i drugih bogova?" Reci: "Ja ne tvrdim." Reci: "On je Bog Jedini, i ja nemam ništa s time što vi Njemu pridružujete."');
INSERT INTO "bs_darassalam" VALUES(6,20,'6:20','Oni kojima smo Knjigu dali poznaju Poslanika kao što poznaju sinove svoje. Oni koji su sebe upropastili – pa, oni ne vjeruju.');
INSERT INTO "bs_darassalam" VALUES(6,21,'6:21','Ima li većeg zulumćara od onoga koji o Allahu govori laži ili ne priznaje ajete i znakove Njegove?! Zulumćari zaista neće uspjeti.');
INSERT INTO "bs_darassalam" VALUES(6,22,'6:22','A na Dan kada ih sve sakupimo, pa upitamo one koji su druge Njemu u obožavanju pridruživali: "Gdje su vam božanstva vaša koja ste za bogove smatrali?",');
INSERT INTO "bs_darassalam" VALUES(6,23,'6:23','neće im nikakvo opravdanje preostati nego da reknu: "Allaha nam, Gospodara našeg, mi nismo višebošci bili."');
INSERT INTO "bs_darassalam" VALUES(6,24,'6:24','Gledaj kako će oni sami sebi lagati, a zagubit će im se oni koje su bili izmislili.');
INSERT INTO "bs_darassalam" VALUES(6,25,'6:25','Ima onih koji dolaze da te slušaju, ali Mi smo na srca njihova zastore stavili, da Kur''an ne bi razumjeli, i gluhim ih učinili, pa i ako bi sve dokaze vidjeli, opet u njih ne bi povjerovali. A kada ti dolaze da s tobom raspravljaju, govore oni koji ne vjeruju: "To su samo izmišljotine naroda drevnih."');
INSERT INTO "bs_darassalam" VALUES(6,26,'6:26','Oni zabranjuju da se u Kur''an vjeruje, i sami se od njega udaljavaju, i sami sebe upropaštavaju, a da i ne primjećuju.');
INSERT INTO "bs_darassalam" VALUES(6,27,'6:27','A da ti je vidjeti kako će, kad pred Vatrom budu zadržani, reći: "Da nam je da vraćeni budemo, pa da ajete i znakove Gospodara našeg ne poričemo i da vjernici postanemo."');
INSERT INTO "bs_darassalam" VALUES(6,28,'6:28','Ne, naprotiv, njima će očevidno postati ono što su prije krili; a kada bi ponovo bili vraćeni, opet bi nastavili raditi ono što im je bilo zabranjeno, jer oni su, doista, lažljivci,');
INSERT INTO "bs_darassalam" VALUES(6,29,'6:29','i rekli bi: "Nema života osim na dunjaluku i mi oživljeni biti nećemo."');
INSERT INTO "bs_darassalam" VALUES(6,30,'6:30','A da ti je vidjeti kako će, kada pred Gospodarem svojim budu zaustavljeni i kad ih On upita: "Zar ovo nije istina?", odgovoriti: "Jest, tako nam Gospodara našeg!", a i kako će On reći: "E, pa iskusite onda patnju zbog toga što niste vjerovali!"');
INSERT INTO "bs_darassalam" VALUES(6,31,'6:31','Oni koji ne vjeruju da će pred Allaha stati nastradat će kad im iznenada Čas oživljenja dođe, i reći će: "O žalosti naše, šta smo sve na Zemlji propustili!", i grijehe će svoje na leđima nositi, a užasno je ono što će uprtiti.');
INSERT INTO "bs_darassalam" VALUES(6,32,'6:32','Život na dunjaluku samo je igra i zabava, a ahiret je, zaista, bolji za one koji se nastoje uščuvati. Zar ne razumijete?!');
INSERT INTO "bs_darassalam" VALUES(6,33,'6:33','Mi znamo da tebe zaista žalosti što oni govore. Oni, uistinu, ne smatraju da si lažac, nego zulumćari poriču Allahove riječi.');
INSERT INTO "bs_darassalam" VALUES(6,34,'6:34','A poslanici su prije tebe lažnim smatrani, pa su trpjeli što su ih u laž ugonili i mučili sve dok im ne bi došla pomoć Naša – a niko ne može Allahove riječi zamijeniti – i do tebe su doprle o poslanicima neke vijesti.');
INSERT INTO "bs_darassalam" VALUES(6,35,'6:35','Ako je tebi teško to što se oni okreću, onda, ako možeš, potraži kakav otvor u Zemlji ili kakve ljestve za nebo, pa im donesi drugi znak?! Da Allah hoće, On bi ih sve na pravom putu sakupio; zato, nikako ne budi od onih koji ne znaju.');
INSERT INTO "bs_darassalam" VALUES(6,36,'6:36','Odazvat će se jedino oni koji slušaju. A Allah će umrle oživiti; zatim će se Njemu svi vratiti.');
INSERT INTO "bs_darassalam" VALUES(6,37,'6:37','Oni govore: "Zašto mu ne bude spušten od njegova Gospodara kakav znak." Reci: "Allah je, zaista, kadar spustiti znak, ali većina njih ne zna."');
INSERT INTO "bs_darassalam" VALUES(6,38,'6:38','Nema ni jednog živog bića koje po zemlji hodi, niti ptice koja na krilima svojim leti, a da nisu zajednice poput vas. U Knjizi Mi ništa izostavili nismo – a naposljetku, svi će pred Gospodarom svojim biti sakupljeni.');
INSERT INTO "bs_darassalam" VALUES(6,39,'6:39','A oni koji ajete i znakove Naše poriču gluhi su i nijemi, u tminama su. Onoga koga hoće – Allah na stranputicu skrene, a onome kome hoće – da na Pravom putu bude.');
INSERT INTO "bs_darassalam" VALUES(6,40,'6:40','Reci: "Kažite vi meni, ako istinu govorite, kad bi vam došla Allahova kazna, ili vam dođe Čas sudnji, da li biste ikog drugog osim Allaha prizivali?"');
INSERT INTO "bs_darassalam" VALUES(6,41,'6:41','Naprotiv, Njega samo molite pa, ako hoće, otklonit će od vas ono za što ste Ga molili, a zaboravljate one koje ste Mu u obožavanju pridruživali.');
INSERT INTO "bs_darassalam" VALUES(6,42,'6:42','A poslanike smo i narodima prije tebe slali i neimaštinom i bolešću ih kažnjavali, ne bi li se ponizno molili.');
INSERT INTO "bs_darassalam" VALUES(6,43,'6:43','Pa zašto se nisu ponizno molili kada bi im kazna Naša došla?! Ali, srca njihova ostajala su tvrda, a šejtan im je lijepim prikazao ono što su radili.');
INSERT INTO "bs_darassalam" VALUES(6,44,'6:44','I kada bi zaboravili ono čime su opominjani, Mi bismo im kapije svega otvorili; a kada bi se onome što im je dato obradovali, iznenada bismo ih kaznili i oni bi, odjednom, svaku nadu izgubili,');
INSERT INTO "bs_darassalam" VALUES(6,45,'6:45','i do zadnjeg bi bio iskorijenjen narod koji je zulum činio; pa neka je hvaljen Allah, Gospodar svjetova.');
INSERT INTO "bs_darassalam" VALUES(6,46,'6:46','Reci: "Kažite vi meni, ako bi vam Allah sluh vaš i vid vaš oduzeo i srca vaša zapečatio, koji bi vam bog, osim Allaha, to vratio?!" Pogledaj kako ajete i znakove izlažemo, a oni se opet okreću."');
INSERT INTO "bs_darassalam" VALUES(6,47,'6:47','Reci: "Kažite vi meni, ako bi vas Allahova kazna stigla iznenada ili očigledno, zar bi iko drugi osim ljudi zulumćara nastradao?!"');
INSERT INTO "bs_darassalam" VALUES(6,48,'6:48','Mi šaljemo poslanike samo zato da donose radosne vijesti i da opominju; neka se zato oni koji vjeruju i dobra djela čine ničega ne boje i ni za čime neka ne tuguju.');
INSERT INTO "bs_darassalam" VALUES(6,49,'6:49','A one koji su ajete i znakove Naše poricali stići će kazna zato što su buntovni bili.');
INSERT INTO "bs_darassalam" VALUES(6,50,'6:50','Reci: "Ja vam ne kažem: ''U mene su Allahove riznice'', niti: ''Ja ono što je čulima nedokučivo znam'', niti vam kažem: ''Ja sam uistinu melek'', ja slijedim samo ono što mi se objavljuje." Reci: "Zar su isto slijepac i onaj koji vidi?! Zašto ne razmislite?"');
INSERT INTO "bs_darassalam" VALUES(6,51,'6:51','I upozoravaj Kur''anom one koji strahuju što će pred Gospodarom svojim sakupljeni biti, kad osim Njega ni zaštitnika ni zagovornika neće imati – da bi se uščuvali.');
INSERT INTO "bs_darassalam" VALUES(6,52,'6:52','I ne tjeraj od sebe one koji se ujutro i navečer Gospodaru svome mole u želji za licem Njegovim; ti nećeš za njih odgovarati, a ni oni neće odgovarati za tebe, jer bi ti, ako bi ih otjerao, u zulumćare spadao.');
INSERT INTO "bs_darassalam" VALUES(6,53,'6:53','I tako Mi jedne drugima iskušavamo da bi nevjernici rekli: "Zar su to oni, među nama, kojima je Allah, milost ukazao?!" A zar Allah najbolje ne poznaje zahvalne?!');
INSERT INTO "bs_darassalam" VALUES(6,54,'6:54','A kada ti dođu oni koji u riječi Naše vjeruju, ti reci: "Selamun alejkum! Gospodar vaš je sam Sebi propisao da bude milostiv: ako neko od vas kakvo ružno djelo iz lahkomislenosti učini, pa se poslije pokaje i popravi –pa, Allah je, doista, Onaj Koji oprašta grijehe i milostiv je."');
INSERT INTO "bs_darassalam" VALUES(6,55,'6:55','Tako Mi potanko izlažemo ajete i znakove, da bi očevidan bio put kojim idu prestupnici.');
INSERT INTO "bs_darassalam" VALUES(6,56,'6:56','Reci: "Meni je zabranjeno obožavati one koje vi, pored Allaha, molite." Reci: "Ja se ne povodim za željama vašim, jer bih tada zalutao i ne bih među upućenima bio."');
INSERT INTO "bs_darassalam" VALUES(6,57,'6:57','Reci: "Ja sam zaista na jasnoj uputi moga Gospodara, a vi je poričete. Nije u mojoj vlasti ono što vi požurujete; pita se samo Allah, On sudi po pravdi i On je Sudija najbolji."');
INSERT INTO "bs_darassalam" VALUES(6,58,'6:58','Reci: "Da je u mojoj vlasti ono što požurujete, između mene i vas svršeno bi bilo, a Allah najbolje zna zulumćare."');
INSERT INTO "bs_darassalam" VALUES(6,59,'6:59','U Njega su ključevi onoga što je čulima nedokučivo, samo ih On zna, i On jedini zna šta je na kopnu i šta je u moru, i nijedan list ne opadne a da On ne zna za njega; i nema zrna u tminama Zemlje niti ičeg svježeg niti ičeg suhog, a da nije u jasnoj Knjizi.');
INSERT INTO "bs_darassalam" VALUES(6,60,'6:60','On vas noću umrtvljuje – a zna i šta ste preko dana uradili – zatim vas danju budi, kako bi rok koji je svakome određen, prošao. Na kraju, Njemu ćete se vratiti i On će vas o onome što ste radili obavijestiti');
INSERT INTO "bs_darassalam" VALUES(6,61,'6:61','On je iznad robova Svojih Svepotčinitelj i šalje vam čuvare; a kad nekom od vas smrt dođe, izaslanici Naši mu dušu uzmu; oni nikad propust ne prave.');
INSERT INTO "bs_darassalam" VALUES(6,62,'6:62','Oni će, poslije, Allahu biti vraćeni, svome istinskom Zaštitniku. Samo će se On pitati i On će najbrže obračun svidjeti.');
INSERT INTO "bs_darassalam" VALUES(6,63,'6:63','Reci: "Ko vas iz strahota na kopnu i na moru izbavlja kada mu se i javno i tajno ponizno molite: ''Ako nas On iz ovoga izbavi, sigurno ćemo biti zahvalni!''"');
INSERT INTO "bs_darassalam" VALUES(6,64,'6:64','Reci: "Allah vas iz tih i iz svake nevolje izbavlja, pa vi opet Njemu druge u obožavanju pridružujete."');
INSERT INTO "bs_darassalam" VALUES(6,65,'6:65','Reci: "On je kadar poslati protiv vas kaznu iznad vaših glava ili ispod vaših nogu, ili vas u stranke podijeliti i učiniti da silu jedni drugih iskusite. Pogledaj samo kako Mi potanko izlažemo ajete i znakove, ne bi li ljudi shvatili."');
INSERT INTO "bs_darassalam" VALUES(6,66,'6:66','Tvoj narod Kur''an poriče, a on je istina. Reci: "Ja nisam zadužen da vam Uputu dam.');
INSERT INTO "bs_darassalam" VALUES(6,67,'6:67','svaki nagovještaj ima svoje vrijeme, i vi ćete znati."');
INSERT INTO "bs_darassalam" VALUES(6,68,'6:68','Kada vidiš one koji lažima zadiru u Naše ajete i rugaju se, ti se od njih okreni sve dok drugu priču ne započnu. A ako te šejtan navede da zaboraviš, onda, nakon što se sjetiš, ne sjedi s ljudima zulumćarima.');
INSERT INTO "bs_darassalam" VALUES(6,69,'6:69','Oni koji se boje Allaha neće za njih račun polagati, ali su dužni opominjati ne bi li se okanili.');
INSERT INTO "bs_darassalam" VALUES(6,70,'6:70','Ostavi one koji vjeru svoju kao igru i zabavu uzimaju, i koje je život na ovom svijetu obmanuo, a opominji Kur''anom da niko, zbog onoga što radi, ne bi stradao, jer, osim Allaha, ni zaštitnika ni posrednika neće imati, i od takvog se neće nikakva otkupnina primiti. Oni će, zbog onoga što su radili, biti u muci držani; njih čeka piće od ključale vode i patnja bolna zato što nisu vjerovali.');
INSERT INTO "bs_darassalam" VALUES(6,71,'6:71','Reci: "Zar da, pored Allaha, molimo one koji nam ne mogu nikakvu korist pribaviti ni neku štetu otkloniti, pa da budemo vraćeni svojoj prošlosti – a Allah nas je već uputio – i da budemo kao onaj koga su na Zemlji šejtani zaveli pa ništa ne zna, a koga drugovi njegovi zovu na Pravi put: ''Hodi nama!''" Reci: "Zaista je Allahova uputa – istinska uputa, i nama je naređeno da se Gospodaru svjetova predamo,');
INSERT INTO "bs_darassalam" VALUES(6,72,'6:72','i da namaz obavljamo i da se Njega bojimo"; On je Taj pred Kojim ćete biti sakupljeni,');
INSERT INTO "bs_darassalam" VALUES(6,73,'6:73','i On je Taj Koji je nebesa i Zemlju s istinom stvorio. Čim On za nešto rekne: "Budi!" – ono biva; riječ Njegova je istina; samo će On imati vlast na Dan kada se u rog puhne; On zna ono što je čulima nedokučivo i ono što je pojavno; On je Mudri i Onaj Koji sve u potpunosti zna.');
INSERT INTO "bs_darassalam" VALUES(6,74,'6:74','A kad Ibrahim reče ocu svome Azeru: "Zar kumire smatraš bogovima?! Uistinu, vidim da ste i ti i narod tvoj u očitaj zabludi."');
INSERT INTO "bs_darassalam" VALUES(6,75,'6:75','I Mi pokazasmo Ibrahimu carstvo nebesa i Zemlje da bi postao od onih što čvrsto vjeruju.');
INSERT INTO "bs_darassalam" VALUES(6,76,'6:76','I kad nastupi noć, on ugleda zvijezdu i reče: "Ovo je Gospodar moj!" A pošto zađe, reče: "Ne volim one koji zalaze!"');
INSERT INTO "bs_darassalam" VALUES(6,77,'6:77','A kad ugleda Mjesec kako izlazi, reče: "Ovo je Gospodar moj!" A pošto zađe, on reče: "Ako me Gospodar moj ne uputi, bit ću sigurno jedan od ljudi koji su zalutali."');
INSERT INTO "bs_darassalam" VALUES(6,78,'6:78','A kad ugleda Sunce kako se rađa, on uzviknu: "Ovo je Gospodar moj, ovo je veće!" A pošto zađe, on reče: "Narode moj, ja zaista nemam ništa s time što vi Njemu pridružujete!');
INSERT INTO "bs_darassalam" VALUES(6,79,'6:79','Ja okrećem lice svoje, kao pravi vjernik, prema Onome Koji je nebesa i Zemlju, bez prethodnog primjera, stvorio; ja u višebošce ne spadam!"');
INSERT INTO "bs_darassalam" VALUES(6,80,'6:80','I narod je njegov s njim raspravljao. "Zar da sa mnom o Allahu raspravljate, a On je mene uputio?'''', reče on. "Ja se ne bojim onih koje vi Njemu pridružujete, osim što se bojim da moj Gospodar htjedne nešto. Gospodar moj znanjem svojim obuhvata sve. Zašto ne promislite i pouku ne prihvatite?');
INSERT INTO "bs_darassalam" VALUES(6,81,'6:81','A kako bih se bojao onih koje Njemu pridružujete, kada se vi ne bojite što Allahu u obožavanju pridružujete one za koje vam On nije nikakav dokaz spustio? Pa koji od dva tabora više sigurnost zaslužuje, ako vi znate?');
INSERT INTO "bs_darassalam" VALUES(6,82,'6:82','Samo oni koji vjeruju i vjerovanje svoje sa zulumom ne miješaju bit će uistinu sigurni: oni su upućeni."');
INSERT INTO "bs_darassalam" VALUES(6,83,'6:83','To je čvrst dokaz Naš koji dadosmo Ibrahimu protiv naroda njegova. Mi na više stepene uzdižemo one koje hoćemo. Gospodar tvoj je, uistinu, mudar i Onaj Koji sve zna.');
INSERT INTO "bs_darassalam" VALUES(6,84,'6:84','I Mi mu darovasmo Ishaka i Jakuba; i svakog uputismo – a Nuha smo još prije uputili – i od potomaka njegovih Davuda, i Sulejmana, i Ejjuba, i Jusufa, i Musaa, i Haruna – eto, tako Mi nagrađujemo one koji dobra djela čine,');
INSERT INTO "bs_darassalam" VALUES(6,85,'6:85','i Zekerijjaa, i Jahjaa, i Isaa, i Il''jasa – svi su oni među čestitima bili...');
INSERT INTO "bs_darassalam" VALUES(6,86,'6:86','i Ismaila, i el-Jese''a i Junusa i Luta – i svima smo prednost nad svjetovima ostalim dali...');
INSERT INTO "bs_darassalam" VALUES(6,87,'6:87','i neke pretke njihove i potomke njihove i braću njihovu – njih smo odabrali i na Pravi put ih uputili.');
INSERT INTO "bs_darassalam" VALUES(6,88,'6:88','To je Allahova uputa kojom On upućuje one koje hoće od robova Svojih. A da su oni Allahu ikoga u obožavanju pridruživali, sigurno bi propalo ono što su radili.');
INSERT INTO "bs_darassalam" VALUES(6,89,'6:89','To su oni kojima smo Mi Knjigu i mudrost i vjerovjesništvo dali. Pa ako oni u to ne vjeruju, Mi smo to povjerili ljudima koji to ne niječu.');
INSERT INTO "bs_darassalam" VALUES(6,90,'6:90','Njih je Allah uputio, i zato slijedi njihov put. Reci: "Ja od vas ne tražim nagradu za Kur''an, on je samo pouka svjetovima."');
INSERT INTO "bs_darassalam" VALUES(6,91,'6:91','Oni ne poznaju Allaha kako treba, kad govore: "Nijednom čovjeku Allah nije ništa objavio!" Reci: "A ko je objavio Knjigu koju je donio Musa kao svjetlo i uputu ljudima, koju na listove stavljate i pokazujete, a mnogo i krijete, i poučeni ste onome što ni vi ni preci vaši niste znali?" Reci: "Allah!" Zatim ih ostavi, neka se lažima svojim zabavljaju.');
INSERT INTO "bs_darassalam" VALUES(6,92,'6:92','A ova Knjiga, koju objavljujemo, blagoslovljena je, ona potvrđuje one prije nje i da upozoriš Meku – Majku naselja i sve oko nje. A oni koji u ahiret vjeruju – vjeruju i u nju i o namazima svojim brinu.');
INSERT INTO "bs_darassalam" VALUES(6,93,'6:93','Ko je veći zulumćar od onoga koji laži o Allahu iznosi ili koji govori: "Objavljuje mi se'''', a ništa mu se ne objavljuje, ili koji kaže: "I ja ću reći isto onako kao što Allah objavljuje." A da ti je vidjeti zulumćare u smrtnim mukama, kada meleki budu ispružili ruke svoje prema njima: "Izvadite svoje duše! Od sada ćete neizdržljivom kaznom biti kažnjeni zato što ste na Allaha ono što nije istina iznosili i što ste se prema ajetima i znakovima Njegovim oholo ponašali."');
INSERT INTO "bs_darassalam" VALUES(6,94,'6:94','A doći ćete nam pojedinačno, onakvi kakve smo vas prvi put stvorili, napustivši dobra koja smo vam bili darovali. Mi ne vidimo s vama zagovarače vaše za koje ste smatrali da ih Allahu treba u obožavanju pridružiti, pokidane su veze među vama i ostaviše vas oni za koje ste tako tvrdili.');
INSERT INTO "bs_darassalam" VALUES(6,95,'6:95','Zaista je Allah Onaj Koji čini da se raspukne zrno i košpica. On izvodi živo iz mrtvoga, i On je Taj Koji izvodi mrtvo iz živoga; to vam je, eto, Allah, pa kuda se onda odmećete!?');
INSERT INTO "bs_darassalam" VALUES(6,96,'6:96','On je Taj Koji čini da zora sviće, On je noć odredio za počinak, a Sunce i Mjesec za računanje vremena; to je odredba Silnoga i Sveznajućeg.');
INSERT INTO "bs_darassalam" VALUES(6,97,'6:97','On vam je stvorio zvijezde da se po njima u mraku upravljate, na kopnu i moru. Mi smo obrazložili ajete i znakove ljudima koji znaju.');
INSERT INTO "bs_darassalam" VALUES(6,98,'6:98','On vas je stvorio od jednog čovjeka, a zatim vam slijedi boravište i spremište. Mi smo obrazložili ajete i znakove ljudima koji razumiju.');
INSERT INTO "bs_darassalam" VALUES(6,99,'6:99','On vodu s neba spušta, pa Mi onda činimo da pomoću nje niču sve vrste bilja i da iz njega izrasta zelenilo, a iz njega klasje gusto, i iz palmi, iz zametka njihova, grozdovi koje je lahko ubrati, i vrtovi lozom zasađeni, i masline i šipci – slično i različito. Posmatrajte, zato, plodove njihove kad se tek pojave i kad zru. U tome su, zaista, dokazi za ljude koji vjeruju.');
INSERT INTO "bs_darassalam" VALUES(6,100,'6:100','I oni drže da su džini Allahu takmaci, a On je njih stvorio, i izmislili su, ne znajući šta govore, da On ima sinove i kćeri. Uzvišen je On i vrlo visoko iznad onoga kako Ga oni opisuju!');
INSERT INTO "bs_darassalam" VALUES(6,101,'6:101','On je nebesa i Zemlje, bez prethodnog primjera, Stvoritelj. Otkud Njemu dijete kad nema žene?! On sve stvara, i samo On sve zna.');
INSERT INTO "bs_darassalam" VALUES(6,102,'6:102','To vam je Allah, Gospodar vaš, nema drugog boga osim Njega, Stvoritelja svega; zato Njemu ibadet činite; On nad svime bdi.');
INSERT INTO "bs_darassalam" VALUES(6,103,'6:103','Pogledi do Njega ne mogu doprijeti, a On do pogleda dopire; On je Dobrostiv i Onaj Koji sve u potpunosti zna.');
INSERT INTO "bs_darassalam" VALUES(6,104,'6:104','Od Gospodara vašeg došli su vam jasni dokazi, pa onaj ko ih vidi – u svoju korist to čini, a onaj ko ostane na to slijep – na svoju je štetu slijep; a ja nisam taj koji vas nadzire.');
INSERT INTO "bs_darassalam" VALUES(6,105,'6:105','I eto, tako Mi izlažemo ajete da bi oni rekli: "Ti si to učio", i da bismo to objasnili ljudima koji znaju.');
INSERT INTO "bs_darassalam" VALUES(6,106,'6:106','Ti slijedi ono što Gospodar tvoj objavljuje, drugoga boga osim Njega nema, i višebošce izbjegavaj.');
INSERT INTO "bs_darassalam" VALUES(6,107,'6:107','Da Allah hoće, oni ne bi širk činili, a Mi tebe nismo dali da ih nadzireš niti si ti za njih zadužen.');
INSERT INTO "bs_darassalam" VALUES(6,108,'6:108','Ne grdite one koje oni, pored Allaha, prizivaju, da ne bi i oni, nepravedno i ne misleći šta govore, Allaha grdili. Kao i njima, tako smo svakom narodu lijepim postupke njihove predstavljali. Oni će se, na kraju, Gospodaru svome vratiti, pa će ih On o onom što su radili obavijestiti.');
INSERT INTO "bs_darassalam" VALUES(6,109,'6:109','Oni se zaklinju Allahom, najjačom zakletvom, da će, ako im dođe kakvo znamenje, sigurno zbog njega vjernici postati. Reci: "Sva su znamenja samo u Allaha!" A šta vi znate, možda bi oni, kada bi im ona došla, vjernici postali?');
INSERT INTO "bs_darassalam" VALUES(6,110,'6:110','A mi srca njihova i oči njihove okrećemo, pa neće vjerovati kao što nisu vjerovali ni prvi put, i ostavljamo ih da u osionosti svojoj lutaju smeteni.');
INSERT INTO "bs_darassalam" VALUES(6,111,'6:111','Kad bismo im meleke poslali, i kad bi im mrtvi progovorili, i kad bismo pred njih sve dokaze sabrali - oni, opet, ne bi vjerovali, osim ako bi Allah htio, ali većina njih ne zna.');
INSERT INTO "bs_darassalam" VALUES(6,112,'6:112','Tako smo svakom vjerovjesniku neprijatelje određivali, šejtane u vidu ljudi i džina, koji su jedni drugima kićene besjede govorili, da bi ih obmanuli; a da je Gospodar tvoj htio, oni to ne bi učinili; zato ti ostavi njih, i ono što izmišljaju;');
INSERT INTO "bs_darassalam" VALUES(6,113,'6:113','i da bi srca onih koji u Ahiret ne vjeruju bila sklona tome i zadovoljna time, i da bi počinili grijehe koje su počinili.');
INSERT INTO "bs_darassalam" VALUES(6,114,'6:114','Zar da pored Allaha tražim drugog sudiju, kad vam On objavljuje Knjigu potanko?! A oni kojima smo Mi dali knjigu dobro znaju da Kur''an objavljuje Gospodar tvoj s Istinom, zato ti ne sumnjaj nikako!');
INSERT INTO "bs_darassalam" VALUES(6,115,'6:115','Riječi Gospodara tvoga vrhunac su istine i pravde; Njegove riječi niko ne može promijeniti i On je Svečujući i Sveznajući.');
INSERT INTO "bs_darassalam" VALUES(6,116,'6:116','Ako bi se ti pokoravao većini onih koji žive na Zemlji, oni bi te od Allahova puta odvratili; oni se samo za pretpostavkama povode, i oni samo laži izmišljaju.');
INSERT INTO "bs_darassalam" VALUES(6,117,'6:117','Zaista Gospodar tvoj najbolje zna one koji su skrenuli s Njegova puta i On najbolje zna one koji su na Pravom putu.');
INSERT INTO "bs_darassalam" VALUES(6,118,'6:118','Zato, jedite ono pri čijem je klanju spomenuto Allahovo ime, ako u Njegove ajete i znakove vjerujete.');
INSERT INTO "bs_darassalam" VALUES(6,119,'6:119','A zašto da ne jedete ono pri čijem je klanju spomenuto Allahovo ime kad vam je On objasnio šta vam je zabranio - osim kad ste prisiljeni; mnogi, prema prohtjevima svojim, nemajući za to nikakva dokaza, zavode druge u zabludu. U istinu, Gospodar tvoj najbolje zna one koji granice prelaze.');
INSERT INTO "bs_darassalam" VALUES(6,120,'6:120','Okanite se i javnog i tajnog griješenja! Oni koji griješe sigurno će biti kažnjeni za ono što su zgriješili.');
INSERT INTO "bs_darassalam" VALUES(6,121,'6:121','Ne jedite ono pri čijem klanju nije spomenuto Allahovo ime; to je, uistinu, neposluh! A šejtani, doista, navode štićenike svoje da se s vama raspravljaju, pa ako biste im se pokorili - i vi biste, sigurno, mušrici postali.');
INSERT INTO "bs_darassalam" VALUES(6,122,'6:122','Zar je onaj koji je bio u zabludi, a koga smo Mi oživili i dali mu svjetlo pomoću kojeg se među ljudima kreće, kao onaj koji je u tminama iz kojih ne izlazi? A nevjernicima je uljepšano to što rade.');
INSERT INTO "bs_darassalam" VALUES(6,123,'6:123','Isto tako, Mi učinimo da u svakom naselju velikaši postanu prestupnici i da u njemu lukavstva smišljaju, ali oni samo protiv sebe lukavstva smišljaju, a da i ne primjećuju.');
INSERT INTO "bs_darassalam" VALUES(6,124,'6:124','A kada njima kakav znak dođe, oni govore: "Mi nećemo vjerovati sve dok se i nama ne da nešto slično onome što je Allahovim poslanicima dato." A Allah najbolje zna kome će povjeriti poslanstvo Svoje. Prestupnike će sigurno kod Allaha zadesiti poniženje i stići će ih patnja velika zato što su spletkarili.');
INSERT INTO "bs_darassalam" VALUES(6,125,'6:125','Onome koga Allah želi uputiti - On srce njegovo prema islamu raspoloži, a onome kome hoće dati da je u zabludi - On srce njegovo tijesnim i stegnutim učini, kao kad čini napor da na nebo uzleti. Eto, tako Allah učini da gnusoba - šejtan ovlada nad onima koji ne vjeruju.');
INSERT INTO "bs_darassalam" VALUES(6,126,'6:126','Ovo je pravi put Gospodara tvoga, a Mi smo obrazložili ajete i znakove ljudima koji promišljaju i pouku primaju.');
INSERT INTO "bs_darassalam" VALUES(6,127,'6:127','Njih čeka Kuća spasa u Gospodara njihova; On će biti zaštitnik njihov zbog onoga što su činili.');
INSERT INTO "bs_darassalam" VALUES(6,128,'6:128','A na Dan kada On sve sakupi: "O skupe džinski, vi ste mnoge ljude zaveli!" "Gospodaru naš", reći će ljudi, štićenici njihovi, "mi smo jedni drugima bili od koristi i stigli smo do roka našeg koji si nam odredio Ti!" "Vatra će biti prebivalište vaše", "reći će On", "u njoj ćete vječno ostati, osim ako Allah drukčije ne odredi." Gospodar tvoj je, zaista, mudar i Onaj Koji sve zna.');
INSERT INTO "bs_darassalam" VALUES(6,129,'6:129','Tako isto Mi prepuštamo vlast jednim zulumćarima nad drugima zbog onoga što su zaradili.');
INSERT INTO "bs_darassalam" VALUES(6,130,'6:130','O skupe džinski i ljudski, zar vam iz redova vaših nisu dolazili poslanici koji su vam ajete Moje kazivali i upozoravali vas da ćete ovaj vaš dan dočekati? Oni će reći: "Mi svjedočimo protiv sebe." Njih je život na Zemlji bio obmanuo i oni će sami protiv sebe posvjedočiti da su bili nevjernici.');
INSERT INTO "bs_darassalam" VALUES(6,131,'6:131','Tako je, jer Gospodar tvoj nije uništavao naselja zbog zuluma njihova - bez prethodne opomene njihovim stanovnicima."');
INSERT INTO "bs_darassalam" VALUES(6,132,'6:132','Svima će pripasti nagrada ili kazna, prema tome kako su postupali, a Gospodar tvoj nije nemaran prema onom šta su radili.');
INSERT INTO "bs_darassalam" VALUES(6,133,'6:133','Gospodar tvoj je neovisan i milostiv. Ako hoće, uklonit će vas, i poslije vas one koje On hoće dovesti, kao što je od potomstva drugih naroda vas stvorio.');
INSERT INTO "bs_darassalam" VALUES(6,134,'6:134','Ono što vam se prijeteći obećava doista će doći i vi nećete moći umaći.');
INSERT INTO "bs_darassalam" VALUES(6,135,'6:135','Reci: "O narode moj, čini sve što možeš, činit ću, uistinu, i ja; a vi ćete već saznati koga čeka sretan kraj!" Zulumćari sigurno neće uspjeti.');
INSERT INTO "bs_darassalam" VALUES(6,136,'6:136','Oni određuju za Allaha dio ljetine i dio stoke, koju je On stvorio, pa govore: "Ovo je za Allaha", tvrde oni, "a ovo za božanstva naša!" Međutim, ono što je namijenjeno božanstvima njihovim ne stiže Allahu, dok ono što je određeno za Allaha stiže božanstvima njihovim!? Kako ružno oni sude!');
INSERT INTO "bs_darassalam" VALUES(6,137,'6:137','Mnogim su mušricima isto tako šejtani njihovi ubijanje vlastite djece lijepim prikazali, da bi ih upropastili i da bi ih u vjeri njihovoj zbunili. A da je Allah htio, oni to ne bi činili. Zato, i njih i njihove izmišljotine ostavi!');
INSERT INTO "bs_darassalam" VALUES(6,138,'6:138','Oni govore: "Ova stoka i plodovi zabranjeni su, smiju ih jesti samo oni kojima mi dozvoljavamo", tvrde oni, "a ovo je stoka čija su leđa zabranjena." Ima stoke prilikom čijeg klanja ne spominju Allahovo ime, izmišljajući o Njemu laži. A On će ih zbog onoga što izmišljaju, kazniti.');
INSERT INTO "bs_darassalam" VALUES(6,139,'6:139','Oni govore: "Ono što je u utrobama ove i ove stoke dozvoljeno je samo muškarcima našim, a zabranjeno našim ženama. A ako se plod izjalovi, onda su u tome sudionici." Allah će ih za neistine koje pričaju kazniti, zaista je On Mudri i Sveznajući.');
INSERT INTO "bs_darassalam" VALUES(6,140,'6:140','Gubitnici su oni koji iz lahkoumnosti i ne znajući šta rade djecu svoju ubijaju i koji ono čime ih je Allah opskrbio zabranjenim smatraju, izmišljajući o Allahu laži. Oni su zalutali i neupućeni.');
INSERT INTO "bs_darassalam" VALUES(6,141,'6:141','On je Taj Koji stvara vinograde, poduprte i nepoduprte, i palme i usjeve različita okusa, i masline i šipke - oboje, i slično i različito; jedite plodove njihove kad plod dadnu, i podajte na dan žetve i berbe ono na što drugi pravo imaju, i ne rasipajte, a On, zaista, ne voli rasipnike.');
INSERT INTO "bs_darassalam" VALUES(6,142,'6:142','Stvara i stoku koja teret nosi i onu od koje je prostirka; jedite dio onoga čime vas Allah opskrbljuje, a ne slijedite šejtanove korake, zaista vam je on otvoreni neprijatelj,');
INSERT INTO "bs_darassalam" VALUES(6,143,'6:143','i to osam životinja u paru: par ovaca i par koza. Reci: "Da li je On zabranio mužjake ili ženke, ili ono što se nalazi u utrobama ženki? Obavijestite me pouzdano, ako je istina to što govorite",');
INSERT INTO "bs_darassalam" VALUES(6,144,'6:144','i par kamila i par goveda. Reci: "Da li je On zabranio mužjake i ženke i ono što se nalazi u utrobama ženki? Da li ste vi bili prisutni kad vam je Allah to propisao?" Ima li onda većeg zulumćara od onoga koji, ne znajući istinu, izmišlja laži o Allahu da bi ljude u zabludu doveo? Allah sigurno neće uputiti na Pravi put ljude koji su zulumćari.');
INSERT INTO "bs_darassalam" VALUES(6,145,'6:145','Reci: "Ja ne vidim u ovome što mi se objavljuje da je ikome zabranjeno jesti ma šta drugo osim strvi, ili krvi koja istječe, ili svinjskog mesa, to je, doista, pogano, ili što je kao vid neposluha zaklano u nečije drugo, a ne u Allahovo ime." A bude li ko primoran, a ne želi to i ne pretjeruje u tome - pa Gospodar tvoj je, doista, Oprostitelj grijeha, Milostivi.');
INSERT INTO "bs_darassalam" VALUES(6,146,'6:146','Jevrejima smo sve životinje koje imaju kopita ili kandže zabranili, a od goveda i brava njihov loj, osim onog s leđa ili s crijeva, ili onog pomiješanog s kostima. Time smo ih zbog zuluma njihova kaznili; i Mi, zaista, govorimo istinu.');
INSERT INTO "bs_darassalam" VALUES(6,147,'6:147','A ako ti oni ne budu vjerovali, ti reci: "Gospodar vaš je posjednik milosti neizmjerne, ali kazne Njegove ne bivaju pošteđeni ljudi prestupnici."');
INSERT INTO "bs_darassalam" VALUES(6,148,'6:148','Govorit će oni koji su Allahu druge u obožavanju pridruživali: "Da je Allah htio, mi ne bismo širk činili, a ni preci naši, niti bismo išta zabranjenim učinili." Tako su i oni prije njih poricali, sve dok Našu kaznu nisu iskusili. Reci: "Imate li vi kakav dokaz, da nam ga iznesete? Vi se samo za pretpostavkama povodite i vi samo neistinu govorite."');
INSERT INTO "bs_darassalam" VALUES(6,149,'6:149','Reci: "Allah ima potpun dokaz, i da On hoće, sve bi vas na Pravi put uputio!"');
INSERT INTO "bs_darassalam" VALUES(6,150,'6:150','Reci: "Dovedite svoje svjedoke koji će posvjedočiti da je Allah to zabranio!" Pa ako oni posvjedoče, ti im nemoj povjerovati i ne povodi se za strastima onih koji Naše ajete i znakove smatraju lažnim i koji u Ahiret ne vjeruju i koji druge Gospodaru svome ravnim smatraju.');
INSERT INTO "bs_darassalam" VALUES(6,151,'6:151','Reci: "Dođite da vam kažem šta vam Gospodar vaš propisuje: da Mu ništa u obožavanju ne pridružujete, da roditeljima dobro činite, da djecu svoju zbog neimaštine ne ubijate, Mi i vas i njih hranimo, ne približujte se nevaljalštinama, bile javne ili tajne; ne ubijajte onog koga je Allah zabranio ubiti, osim kada to pravda zahtijeva; to vam On zapovijeda, da biste razumjeli."');
INSERT INTO "bs_darassalam" VALUES(6,152,'6:152','I ne približavajte se imetku siročeta, osim na najljepši način, sve dok punoljetno ne postane, i mjericu i vagu pravično napunite. Mi nikoga preko njegove mogućnosti ne zadužujemo; i kad govorite, po pravdi govorite, pa makar se ticalo i srodnika, i obaveze prema Allahu ispunjavajte, eto, to vam On zapovijeda da biste se prisjetili i pouku primili.');
INSERT INTO "bs_darassalam" VALUES(6,153,'6:153','I zato što je ovo pravi put Moj, njega se držite i druge puteve ne slijedite, pa da vas odvoje od puta Njegova; eto, to vam On naređuje, da biste se uščuvali.');
INSERT INTO "bs_darassalam" VALUES(6,154,'6:154','Mi smo Musau Knjigu dali da učinimo potpunom blagodat onome koji će prema njoj postupati i kao objašnjenje svemu i uputu i milost, da bi oni povjerovali da će pred Gospodara svoga stati.');
INSERT INTO "bs_darassalam" VALUES(6,155,'6:155','A ova je Knjiga koju objavljujemo blagoslovljena, zato je slijedite i Allaha se bojte, da bi vam milost ukazana bila.');
INSERT INTO "bs_darassalam" VALUES(6,156,'6:156','I zato, da ne kažete: "Knjiga je objavljena dvjema zajednicama prije nas, ali mi je ne znamo čitati kao oni",');
INSERT INTO "bs_darassalam" VALUES(6,157,'6:157','i da ne kažete: "Da je Knjiga objavljena nama, bolje bismo se od njih držali Pravog puta." Pa objavljuje vam se, eto, od Gospodara vašeg jasan dokaz i uputa i milost; i ima li, onda, većeg zulumćara od onoga koji Allahove ajete ne priznaje i od njih odvraća?! A Mi ćemo teškom kaznom kazniti one koji od ajeta Naših odvraćaju, zato što to stalno čine.');
INSERT INTO "bs_darassalam" VALUES(6,158,'6:158','Zar oni čekaju da im meleki dođu, ili da im Gospodar tvoj dođe, ili neki znakovi od Gospodara tvoga?! Onoga dana kada neki znakovi od Gospodara tvoga dođu nijednom čovjeku neće koristiti to što će tada vjerovati, ako prije nije vjerovao, ili ako nije kao vjernik kakvo dobro uradio. Reci: "Samo vi čekajte, i mi ćemo, doista, čekati!"');
INSERT INTO "bs_darassalam" VALUES(6,159,'6:159','Zaista, s onima koji su vjeru svoju razdijelili i u stranke se podijelili ti nemaš ništa! Njihov slučaj je Allahu predat. On će ih, potom, o onome što su radili obavijestiti.');
INSERT INTO "bs_darassalam" VALUES(6,160,'6:160','Ko god uradi dobro djelo, bit će deseterostruko nagrađen; a ko god uradi loše djelo, bit će samo prema zasluzi kažnjen, i neće im zulum biti učinjen.');
INSERT INTO "bs_darassalam" VALUES(6,161,'6:161','Reci: "Mene je Gospodar moj na Pravi put zaista uputio, u pravu vjeru, vjeru Ibrahima pravovjemika, a on višebožac nije bio."');
INSERT INTO "bs_darassalam" VALUES(6,162,'6:162','Reci: "Klanjanje moje, i obredi moji, i život moj, i smrt moja doista su zarad Allaha, Gospodara svjetova,');
INSERT INTO "bs_darassalam" VALUES(6,163,'6:163','Koji nema saučesnika; to mi je naređeno i ja sam prvi musliman."');
INSERT INTO "bs_darassalam" VALUES(6,164,'6:164','Reci: "Zar da za Gospodara tražim nekog drugog osim Allaha, kad je On Gospodar svega?!" Što god ko uradi, sebi uradi, i svaki grešnik samo će svoje breme nositi. Na kraju, Gospodaru svome ćete se vratiti i On će vas o onome u čemu ste se razilazili obavijestiti.');
INSERT INTO "bs_darassalam" VALUES(6,165,'6:165','On čini da jedni druge na Zemlji smjenjujete i On vas po položajima jedne iznad drugih uzdiže, da bi vas iskušao u onome što vam daje. Gospodar tvoj, zaista, brzo kažnjava, ali On je, uistinu, i Onaj Koji oprašta grijehe i milostiv je.');
INSERT INTO "bs_darassalam" VALUES(7,1,'7:1','Elif Lām Mīm Sād.');
INSERT INTO "bs_darassalam" VALUES(7,2,'7:2','Ovo je knjiga koja ti se spušta - pa neka ti u grudima ne bude nikakve tegobe zbog nje - da njome upozoravaš, i da vjernicima bude opomena.');
INSERT INTO "bs_darassalam" VALUES(7,3,'7:3','Slijedite ono što vam se od Gospodara vašeg objavljuje i pored Njega ne slijedite druge kao zaštitnike! A kako se vi malo prisjećate i pouku primate?!');
INSERT INTO "bs_darassalam" VALUES(7,4,'7:4','Koliko smo Mi samo naselja razorili; a kazna im je Naša dolazila noću ili danju kad bi prilegli?');
INSERT INTO "bs_darassalam" VALUES(7,5,'7:5','A priznanje njihovo, kada bi im došla kazna Naša, bile su samo riječi: Zulumćari smo, zaista, bili!');
INSERT INTO "bs_darassalam" VALUES(7,6,'7:6','I sigurno ćemo pitati one kojima smo poslanike slali, a pitat ćemo, doista, i poslanike,');
INSERT INTO "bs_darassalam" VALUES(7,7,'7:7','pa ćemo im, sigurno, kazivati po znanju Našemu, sve što o njima znamo, a Mi nismo odsutni bili.');
INSERT INTO "bs_darassalam" VALUES(7,8,'7:8','Vaganje toga dana bit će pravedno: oni čiji tas dobra bude teži, oni će uspjeti;');
INSERT INTO "bs_darassalam" VALUES(7,9,'7:9','a oni čiji tas dobra bude lakši, oni će, zato što ajete i znakove Naše nisu priznavali, stradati.');
INSERT INTO "bs_darassalam" VALUES(7,10,'7:10','Mi smo vas na Zemlji smjestili i na njoj vam sve što je potrebno za život dali, kako vi malo zahvaljujete?!');
INSERT INTO "bs_darassalam" VALUES(7,11,'7:11','Mi smo vas stvorili i onda vam oblik dali, a poslije melekima rekli: "Sedždu Ademu učinite!" Pa svi sedždu učiniše, osim Iblisa, on nije bio među onim koji na sedždu padoše!');
INSERT INTO "bs_darassalam" VALUES(7,12,'7:12','"Šta te je navelo pa da ničice ne padneš kad sam ti naredio?", upita On. "Ja sam bolji od njega; mene si od vatre stvorio, a njega od ilovače", odgovori.');
INSERT INTO "bs_darassalam" VALUES(7,13,'7:13','"E, onda silazi iz Dženneta", reče On, "nije tvoje da se u njemu oholiš; silazi, ti si, zaista, među prezrenim!"');
INSERT INTO "bs_darassalam" VALUES(7,14,'7:14','"Daj mi vremena do dana njihova oživljenja!", zamoli.');
INSERT INTO "bs_darassalam" VALUES(7,15,'7:15','"Daje ti se vremena!", reče On.');
INSERT INTO "bs_darassalam" VALUES(7,16,'7:16','"E, kao što si me u zabludu zaveo", reče," tako ću i ja ljude na Tvom Pravom putu sigurno presretati,');
INSERT INTO "bs_darassalam" VALUES(7,17,'7:17','pa ću im, doista, i sprijeda, i straga, i zdesna, i slijeva prilaziti, i Ti ćeš ustanoviti da većina njih neće zahvalna biti!"');
INSERT INTO "bs_darassalam" VALUES(7,18,'7:18','"Izlazi iz njega, pokuđen i ponižen!", reče On. "Tobom i svima koji se za tobom budu povodili doista ću Džehennem napuniti!"');
INSERT INTO "bs_darassalam" VALUES(7,19,'7:19','a ti, o Ademe, i supruga tvoja u Džennetu stanujte i odakle god hoćete jedite, samo se ovom drvetu ne približujte, pa da tako u redovima zulumćara budete!"');
INSERT INTO "bs_darassalam" VALUES(7,20,'7:20','I šejtan ih poče navraćati da bi im otkrio avrete njihove, koja su im skrivena bila, i reče: "Gospodar vaš zabranjuje vam ovo drvo samo zato da ne biste meleki postali ili da ne biste u redovima besmrtnika bili",');
INSERT INTO "bs_darassalam" VALUES(7,21,'7:21','i zaklinjaše im se: "Ja sam vama dvoma, zaista, savjetnik iskreni!"');
INSERT INTO "bs_darassalam" VALUES(7,22,'7:22','I na prevaru ih zavede. Kad oni ono drvo okusiše, avreti njihovi ukazaše im se i oni po sebi džennetsko lišće stavljati počeše. "Zar vam to drvo nisam zabranio?", zovnu ih Gospodar njihov, "i rekao vam: ''šejtan vam je, zbilja, otvoreni neprijatelj.''"');
INSERT INTO "bs_darassalam" VALUES(7,23,'7:23','"Gospodaru naš", rekoše oni, "sami smo sebi zulum učinili, i ako nam Ti ne oprostiš i ne smiluješ nam se, sigurno ćemo u redovima gubitnika biti."');
INSERT INTO "bs_darassalam" VALUES(7,24,'7:24','"Silazite!", reče On, "jedni drugima bit ćete neprijatelji! Na Zemlji ćete boraviti i do određenog vremena uživati."');
INSERT INTO "bs_darassalam" VALUES(7,25,'7:25','"Na njoj ćete živjeti, na njoj umirati i iz nje oživljeni biti", reče On.');
INSERT INTO "bs_darassalam" VALUES(7,26,'7:26','O sinovi Ademovi, dali smo vam odjeću koja će pokrivati avrete vaše, a i raskošna odijela, ali, odjeća bogobojaznosti, to je ono najbolje. To su neki Allahovi znakovi da bi se oni prisjetili i pouku prihvatili.');
INSERT INTO "bs_darassalam" VALUES(7,27,'7:27','O potomci Ademovi, neka vas nikako ne zavede šejtan kao što je roditelje vaše iz Dženneta izveo, skinuvši s njih odjeću da bi im avrete njihove pokazao! On vas, doista, vidi; on i vojske njegove, odakle vi njih ne vidite. Uistinu, Mi smo učinili šejtane zaštitnicima onih koji ne vjeruju.');
INSERT INTO "bs_darassalam" VALUES(7,28,'7:28','A kada urade neko gnusno djelo, govore: "Zatekli smo pretke naše da to rade, a i Allah nam je to zapovijedio." Reci: "Allah ne zapovijeda da se rade gnusna djela! Zašto o Allahu govorite ono što ne znate?"');
INSERT INTO "bs_darassalam" VALUES(7,29,'7:29','"Gospodar moj naređuje pravednost. I obraćajte se samo Njemu kad god obavljate namaz, i molite se iskreno Mu ispovijedajući vjeru!, kao što vas je prvi put stvorio, tako će vas ponovo oživiti."');
INSERT INTO "bs_darassalam" VALUES(7,30,'7:30','On jedne na Pravi put upućuje, a drugi, s pravom, zabludu zaslužuju, jer oni, umjesto Allaha, doista šejtane za zaštitnike uzimaju i misle da su upućeni.');
INSERT INTO "bs_darassalam" VALUES(7,31,'7:31','O sinovi Ademovi, kad god u mesdžid pođete odjeću pristojnu - ukras vaš - nosite! I jedite i pijte, samo ne pretjerujte; On, zaista, ne voli one koji pretjeruju.');
INSERT INTO "bs_darassalam" VALUES(7,32,'7:32','Reci: "Ko je zabranio Allahove ukrase koje je On za robove Svoje stvorio, i ukusna jela?" Reci: "To je za vjernike na dunjaluku, a i na Ahiretu jesu samo za njih." Eto, tako Mi podrobno izlažemo ajete ljudima koji znaju.');
INSERT INTO "bs_darassalam" VALUES(7,33,'7:33','Reci: "Gospodar moj zabranjuje gnusna djela, i javna i tajna, i grijehe, i bespravnu upotrebu sile, i da Allahu u obožavanju pridružujete one za koje On nikakav dokaz objavio nije, i da o Allahu govorite ono što ne znate."');
INSERT INTO "bs_darassalam" VALUES(7,34,'7:34','Svaki narod ima svoj rok, i kada dođe njihov rok, neće ga moći ni za tren jedan ni odložiti ni ubrzati.');
INSERT INTO "bs_darassalam" VALUES(7,35,'7:35','O sinovi Ademovi, kad vam između vas budu dolazili poslanici koji vam kazuju ajete Moje - onda za one koji se budu uščuvali i dobra djela činili nema straha niti će za bilo čim tugovati.');
INSERT INTO "bs_darassalam" VALUES(7,36,'7:36','A oni koji ne budu vjerovali i dokaze i ajete Naše budu iz oholosti poricali bit će stanovnici Vatre; u njoj će vječno ostati.');
INSERT INTO "bs_darassalam" VALUES(7,37,'7:37','Ima li, onda, većeg zulumćara od onoga koji o Allahu govori laži ili poriče Njegove ajete i znakove?! Takvi će dobiti sve ono što im je zapisano. Ali, kada im izaslanici Naši dođu da im duše uzmu, upitat će: "A gdje su oni koje ste, umjesto Allaha, prizivali?" "Izgubili su nam se", odgovorit će, i sami protiv sebe posvjedočiti da su nevjernici bili.');
INSERT INTO "bs_darassalam" VALUES(7,38,'7:38','Reći će On: "Ulazite u Vatru sa zajednicama džina i ljudi koje su prije vas bile i nestale!" I kad god neka zajednica uđe, proklinjat će onu sebi sličnu - za kojom se u nevjerovanju povela. A kad se svi u njemu iskupe, tada će oni iz druge reći za one iz prve: "Gospodaru naš, ovi su nas u zabludu odveli, zato im daj dvostruku patnju u Vatri!" "Za sve će biti dvostruka!", reći će On, "ali vi ne znate."');
INSERT INTO "bs_darassalam" VALUES(7,39,'7:39','A oni iz prve reći će onima iz druge: "Vi nemate nikakve prednosti nad nama! Zato iskusite patnju za ono što ste radili."');
INSERT INTO "bs_darassalam" VALUES(7,40,'7:40','Doista, onima koji ajete i znakove Naše budu poricali i prema njima se budu oholo odnosili - kapije nebeske neće se otvoriti, niti će oni u Džennet ući sve dok se deva kroz iglene uši ne provuče!? Eto tako ćemo Mi prestupnike kazniti.');
INSERT INTO "bs_darassalam" VALUES(7,41,'7:41','Od džehennemske vatre će im ležaji, a i pokrivači, iznad njih biti. Eto, tako ćemo Mi zulumćare kazniti.');
INSERT INTO "bs_darassalam" VALUES(7,42,'7:42','Oni koji budu vjerovali i dobra djela činili - a Mi nikog ne zadužujemo mimo njegovih mogućnosti, bit će stanovnici Dženneta, u njemu će vječno ostati.');
INSERT INTO "bs_darassalam" VALUES(7,43,'7:43','Iz njihovih ćemo grudi zlobu odstraniti; ispred njih će rijeke teći, i oni će govoriti: "Hvaljen neka je Allah, Koji nas je na Pravi put uputio; mi ne bismo na Pravom putu bili da nas Allah nije uputio, poslanici Gospodara našeg Istinu su donosili", i doviknut će im se: "Taj ste Džennet u nasljedstvo dobili za ono što ste činili!"');
INSERT INTO "bs_darassalam" VALUES(7,44,'7:44','I stanovnici Dženneta stanovnike Vatre dozivat će: "Mi nađosmo da je istinito ono što nam je Gospodar naš obećao, da li ste i vi našli da je istinito ono što je vama Gospodar vaš prijeteći obećao?" "Jesmo!", odgovorit će. A onda će jedan glasnik među njima viknuti: "Neka se Allahovo prokletstvo na zulumćarima ostvari,');
INSERT INTO "bs_darassalam" VALUES(7,45,'7:45','koji su od Allahova puta odvraćali i nastojali da ga iskrive, i koji u Ahiret nisu vjerovali!"');
INSERT INTO "bs_darassalam" VALUES(7,46,'7:46','Između njih bit će zid, a na vrhovima ljudi koji će svakog po obilježju njegovom poznati. I oni će stanovnicima Dženneta, prije nego uđu u njega, viknuti: "Selamun alejkum!" a to će silno željeti.');
INSERT INTO "bs_darassalam" VALUES(7,47,'7:47','Kada im pogledi skrenu prema stanovnicima Vatre, uzviknut će: "Gospodaru naš, ne daj nam da budemo s ljudima zulumćarima!"');
INSERT INTO "bs_darassalam" VALUES(7,48,'7:48','Oni koji će po vrhovima bedema biti zovnut će ljude koje će po obilježju njihovom poznati, i reći će: "Nije vam koristilo vaše mnoštvo i to što ste se oholo držali."');
INSERT INTO "bs_darassalam" VALUES(7,49,'7:49','Zar ovo nisu oni za koje ste se zaklinjali da ih Allahova milost neće stići? Uđite u Džennet, nikakva straha za vas neće biti i ni za čim vi nećete tugovati!"');
INSERT INTO "bs_darassalam" VALUES(7,50,'7:50','I stanovnici Vatre dozivat će stanovnike Dženneta: "Prolijte na nas vode ili nešto od onoga čime vas je Allah obdario!", a oni će reći: "Allah je, doista, to dvoje nevjernicima zabranio",');
INSERT INTO "bs_darassalam" VALUES(7,51,'7:51','onima kojima su vjeru svoju za zabavu i igru držali i koje je život na Zemlji bio obmanuo. Zato ćemo danas Mi njih zaboraviti, kao što su zaboravljali da će na ovaj Dan pred Nama stajati i zato što su ajete i znakove Naše poricali.');
INSERT INTO "bs_darassalam" VALUES(7,52,'7:52','A Mi smo im donijeli Knjigu koju smo po znanju Našem objasnili, da bude uputa i milost ljudima koji vjeruju.');
INSERT INTO "bs_darassalam" VALUES(7,53,'7:53','Čekaju li oni da se to obistini? Onoga dana kada se obistini, reći će oni koji su je prije bili zaboravili: "Istinu su poslanici Gospodara našeg donosili! Da nam je zagovornik kakav, pa da se za nas zauzme ili da nam je da budemo vraćeni pa da postupamo drukčije nego što smo prije postupali!" Ali, oni su sami sebe upropastili, i zagubit će im se oni koje su izmišljali."');
INSERT INTO "bs_darassalam" VALUES(7,54,'7:54','Zaista je Gospodar vaš Allah Koji je nebesa i Zemlju u šest dana stvorio, a zatim se nad Aršom uzvisio. On tamom noći dan prekriva i ona ga ustopu prati, a Sunce i Mjesec i zvijezde pokoravaju se Njegovoj naredbi. Samo On raspolaže stvaranjem i odlučivanjem! Uzvišen neka je Allah, Gospodar svjetova!');
INSERT INTO "bs_darassalam" VALUES(7,55,'7:55','Molite se ponizno i u sebi Gospodaru svome, On, doista, ne voli one koji granicu prelaze.');
INSERT INTO "bs_darassalam" VALUES(7,56,'7:56','I ne pravite nered na Zemlji, kada je na njoj red uspostavljen, a Njemu se molite sa strahom i nadom; milost je Allahova, doista, blizu onih koji dobra djela čine.');
INSERT INTO "bs_darassalam" VALUES(7,57,'7:57','On je Taj Koji šalje vjetrove kao radosnu vijest milosti Svoje; a kad oni pokrenu teške oblake, Mi ih prema mrtvom predjelu potjeramo, pa na njeg kišu spustimo i učinimo da uz njenu pomoć rastu plodovi svakovrsni; tako ćemo i mrtve oživiti da bi ste se prisjetili i pouku prihvatili!');
INSERT INTO "bs_darassalam" VALUES(7,58,'7:58','U plodnom predjelu raste bilje uz dopuštenje Gospodara njegova, a u neplodnom tek s mukom. Eto, tako Mi, na razne načine, izlažemo znakove ljudima koji zahvaljuju.');
INSERT INTO "bs_darassalam" VALUES(7,59,'7:59','Mi smo Nuha poslali narodu njegovu, pa im je on govorio: "O narode moj, Allahu ibadet činite, vi drugog boga osim Njega nemate! Ja se, doista, bojim za vas patnje na Velikom danu!"');
INSERT INTO "bs_darassalam" VALUES(7,60,'7:60','A glavešine naroda njegova odgovarale su: "Mi smatramo da si ti, doista, u potpunoj zabludi."');
INSERT INTO "bs_darassalam" VALUES(7,61,'7:61','A on je govorio: "O narode moj, nisam ja ni u kakvoj zabludi, nego sam poslanik Gospodara svjetova;');
INSERT INTO "bs_darassalam" VALUES(7,62,'7:62','dostavljam vam poslanice Gospodara svoga i savjetujem vas; ja od Allaha znam ono što ne znate vi.');
INSERT INTO "bs_darassalam" VALUES(7,63,'7:63','Zar vam je čudno što vam pouka od Gospodara vašeg dolazi po čovjeku, jednom od vas, da vas upozorava, da biste se Allaha bojali i da biste pomilovani bili?"');
INSERT INTO "bs_darassalam" VALUES(7,64,'7:64','Ali, oni su ga lažnim smatrali, pa smo spasili njega i one koji su bili uz njega u lađi, a one koji u ajete i znakove Naše nisu vjerovali - potopili. Uistinu, oni su pravi slijepci bili.');
INSERT INTO "bs_darassalam" VALUES(7,65,'7:65','A Adu njihova brata Huda poslasmo. On je govorio: "O narode moj, Allahu ibadet činite, vi drugog boga osim Njega nemate, zar se sačuvati nećete!"');
INSERT INTO "bs_darassalam" VALUES(7,66,'7:66','Glavešine naroda njegova, koji nisu vjerovali, odgovarali su: "Mi smatramo da si ti, doista, u ludilu i mi mislimo da si, zaista, lažac."');
INSERT INTO "bs_darassalam" VALUES(7,67,'7:67','A on je govorio: "O narode moj, nije pri meni nikakvo ludilo, nego sam Gospodara svjetova poslanik;');
INSERT INTO "bs_darassalam" VALUES(7,68,'7:68','Dostavljam vam poslanice Gospodara svoga, i ja sam vam iskreni savjetnik."');
INSERT INTO "bs_darassalam" VALUES(7,69,'7:69','Zar vam je čudno što vam pouka od Gospodara vašeg dolazi po čovjeku, jednom između vas, da vas opominje? Sjetite se da vas je On nasljednicima Nuhova naroda učinio i stvorio vas krupnog rasta. I neka su vam zato uvijek na umu Allahove blagodati, da biste uspjeli.');
INSERT INTO "bs_darassalam" VALUES(7,70,'7:70','Oni su govorili: "Zar si nam došao zato da samo Allahu ibadet činimo, a da napustimo one koje su preci naši obožavali? Učini da nas snađe to što nam prijeteći obećavaš, ako je istina to što govoriš!"');
INSERT INTO "bs_darassalam" VALUES(7,71,'7:71','"Već vas je snašla kazna i gnjev Gospodara vašeg!", govorio je on. "Zar sa mnom da se prepirete o imenima nekakvim kojima ste ih vi i preci vaši nazvali, a o kojima Allah nikakav dokaz nije objavio? Zato čekajte, i ja ću s vama sigurno čekati!"');
INSERT INTO "bs_darassalam" VALUES(7,72,'7:72','I Mi smo iz milosti Naše njega i one koji su bili uz njega spasili, a do posljednjeg istrijebili one koji dokaze Naše nisu priznavali i koji nisu vjerovali.');
INSERT INTO "bs_darassalam" VALUES(7,73,'7:73','A Semudu smo njegova brata Saliha poslali. "O narode moj", govorio je on, "Allahu ibadet činite, vi drugog boga osim Njega nemate! Evo vam znaka Gospodara vašeg: ova Allahova kamila za vas je znak. Pustite je neka pase po Allahovoj zemlji i ne zlostavljajte je da vas bolna kazna ne bi snašla!"');
INSERT INTO "bs_darassalam" VALUES(7,74,'7:74','"I sjetite se da vas je On nasljednicima Ada učinio, i da vas je On na Zemlji nastanio: u ravnicama njezinim palače gradite, a u brdima kuće klešete. I neka su vam uvijek na umu Allahove blagodati, i ne činite zlo po Zemlji nered praveći!"');
INSERT INTO "bs_darassalam" VALUES(7,75,'7:75','A glavešine naroda njegova, oni koji su se oholili, upitaše potlačene, one među njima koji su vjerovali: "Znate li vi da je Saliha poslao Gospodar njegov?" "Mi, uistinu, vjerujemo u sve ono što je po njemu poslano", odgovoriše oni.');
INSERT INTO "bs_darassalam" VALUES(7,76,'7:76','"A mi, doista, ne vjerujemo u to u što vi vjerujete", rekoše oni koji su bili oholi.');
INSERT INTO "bs_darassalam" VALUES(7,77,'7:77','I zaklaše oni kamilu, i zapovijed Gospodara svoga ne poslušaše i rekoše: "O Salih, učini da nas snađe to što prijeteći obećavaš, ako si iz reda poslanih!?"');
INSERT INTO "bs_darassalam" VALUES(7,78,'7:78','I zadesi ih strašan potres i oni u zemlji svojoj osvanuše mrtvi, nepomični.');
INSERT INTO "bs_darassalam" VALUES(7,79,'7:79','A on ih napusti i reče: "O narode moj, prenio sam vam poslanicu Gospodara svoga i savjetovao sam vas, ali vi ne volite one koji savjetuju."');
INSERT INTO "bs_darassalam" VALUES(7,80,'7:80','I sjeti se Luta - kad reče narodu svome: "Zašto činite razvrat koji niko među svim svjetovima prije vas nije činio?');
INSERT INTO "bs_darassalam" VALUES(7,81,'7:81','Vi zaista strasno prilazite muškarcima, umjesto ženama. Štaviše, vi ste narod koji sve granice prelazi!"');
INSERT INTO "bs_darassalam" VALUES(7,82,'7:82','A odgovor naroda njegova bio je samo to što su rekli: "Istjerajte ih iz naselja vašeg, oni su, zaista, ljudi koji se od toga ustežu!"');
INSERT INTO "bs_darassalam" VALUES(7,83,'7:83','I Mi smo njega i porodicu njegovu spasili, osim žene njegove; ona je ostala sa onima koji su kaznu iskusili.');
INSERT INTO "bs_darassalam" VALUES(7,84,'7:84','I na njih smo kišu grumenja spustili, pa pogledaj kako su prijestupnici skončali.');
INSERT INTO "bs_darassalam" VALUES(7,85,'7:85','A i u Medjen - njihova brata Šuajba poslasmo. "O narode moj", govorio je on, "Allahu ibadet činite, vi drugog boga osim Njega nemate! Jasan dokaz od Gospodara vašeg došao vam je, zato punite mjericu i vagu, i ljude ni u čemu ne zakidajte, i red na Zemlji ne remetite kad je već na njoj red uspostavljen. To je bolje za vas, ako ste vjernici.');
INSERT INTO "bs_darassalam" VALUES(7,86,'7:86','I ne postavljajte zasjede na putu, prijeteći i od Allahova puta odvraćajući one koji u Njega vjeruju, želeći da on kriv bude. I sjetite se da vas je bilo malo i da vas je On umnožio, a pogledajte kako su skončali oni koji su nered pravili.');
INSERT INTO "bs_darassalam" VALUES(7,87,'7:87','I ako jedni od vas vjeruju u ono što je po meni poslano, a drugi ne vjeruju, pa pričekajte dok nam Allah ne presudi, a On je najbolji sudija!"');
INSERT INTO "bs_darassalam" VALUES(7,88,'7:88','I Glavešine naroda njegova, oni koji su bili oholi, rekoše: "Ili ćemo mi, o Šuajbe, i tebe i one koji s tobom vjeruju iz naselja našeg istjerati, ili ćete se vratiti u našu vjeru!" "Zar i protiv naše volje?", reče on.');
INSERT INTO "bs_darassalam" VALUES(7,89,'7:89','"Ako bismo se u vjeru vašu vratili nakon što nas je Allah iz nje izbavio, na Allaha bismo laž iznijeli. Mi ne treba da se u nju vraćamo, osim ako to htjedne Allah, Gospodar naš. Gospodar naš znanjem Svojim sve obuhvata; u Allaha se uzdamo! Gospodaru naš, Ti presudi nama i narodu našem, po pravdi, Ti si Presuditelj najbolji!"');
INSERT INTO "bs_darassalam" VALUES(7,90,'7:90','A glavešine naroda njegova, oni koji nisu vjerovali, rekoše: "Ako pođete za Šuajbom, bit ćete sigurno izgubljeni."');
INSERT INTO "bs_darassalam" VALUES(7,91,'7:91','I zadesi ih strašan potres i oni u zemlji svojoj osvanuše mrtvi, nepomični.');
INSERT INTO "bs_darassalam" VALUES(7,92,'7:92','Oni koji su smatrali Šuajba lašcem - kao da nikad nisu u njoj ni bili; oni koji su smatrali Šuajba lašcem, oni su baš gubitnici bili.');
INSERT INTO "bs_darassalam" VALUES(7,93,'7:93','A on ih napusti i reče: "O narode moj, prenio sam vam poslanice Gospodara svoga i savjetovao vas, pa zašto da tugujem za narodom nevjerničkim?!"');
INSERT INTO "bs_darassalam" VALUES(7,94,'7:94','I Mi nijednog vjerovjesnika u neko naselje nismo poslali, a da stanovnike njegove bolešću i neimaštinom nismo kaznili da bi se pokajnički molili.');
INSERT INTO "bs_darassalam" VALUES(7,95,'7:95','Poslije bismo zlo zamijenili dobrim dok se ne bi umnožili i rekli: "Zadesili su i naše pretke i neimaština i blagostanje!", i tada bismo ih iznenada kaznili, a oni ni predosjetili ne bi.');
INSERT INTO "bs_darassalam" VALUES(7,96,'7:96','A da su stanovnici naselja vjerovali i bogobojazni bili, Mi bismo im blagoslove i s neba i iz zemlje slali, ali, oni su poricali, pa smo ih kažnjavali zbog onog što su činili.');
INSERT INTO "bs_darassalam" VALUES(7,97,'7:97','A zar su stanovnici naselja sigurni da ih Naša kazna neće snaći noću dok budu spavali?');
INSERT INTO "bs_darassalam" VALUES(7,98,'7:98','A zar su stanovnici naselja sigurni da ih Naša kazna neće snaći danju dok se budu igrali?');
INSERT INTO "bs_darassalam" VALUES(7,99,'7:99','Zar oni misle da su sigurni od Allahovog lukavstva? Da su sigurni od Allahovog lukavstva misli samo narod gubitnički.');
INSERT INTO "bs_darassalam" VALUES(7,100,'7:100','Zar nije jasno onima koji nasljeđuju Zemlju nakon prijašnjih stanovnika njezinih da ćemo i njih, ako budemo htjeli, zbog grijeha njihovih kazniti ili im srca zapečatiti, pa neće moći čuti?!');
INSERT INTO "bs_darassalam" VALUES(7,101,'7:101','O tim naseljima Mi ti neke događaje kazujemo. Poslanici njihovi jasne su im dokaze donosili, ali oni nisu povjerovali zato što ni prije nisu vjerovali. Eto, tako Allah srca nevjernika zapečati.');
INSERT INTO "bs_darassalam" VALUES(7,102,'7:102','A Mi nismo ustanovili da se većina njih zavjeta drži, a ustanovili smo da su većina njih, doista, buntovnici.');
INSERT INTO "bs_darassalam" VALUES(7,103,'7:103','Zatim smo, poslije njih, poslali Musaa sa dokazima Našim faraonu i glavešinama njegovim, ali oni u njih nisu povjerovali, pa pogledaj kako su skončali oni koji su nered činili.');
INSERT INTO "bs_darassalam" VALUES(7,104,'7:104','I Musa reče: "O faraone, ja sam, uistinu, poslanik Gospodara svjetova!"');
INSERT INTO "bs_darassalam" VALUES(7,105,'7:105','"Dužnost mi je da o Allahu samo istinu kažem. Donio sam vam jasan dokaz od Gospodara vašeg, zato pusti da idu sa mnom sinovi Israilovi!"');
INSERT INTO "bs_darassalam" VALUES(7,106,'7:106','"Ako si donio kakav dokaz", reče, "pokaži ga, ako si od onih koji istinu govore."');
INSERT INTO "bs_darassalam" VALUES(7,107,'7:107','I on baci svoj štap - kad on prava zmijurina;');
INSERT INTO "bs_darassalam" VALUES(7,108,'7:108','i izvadi ruku svoju - ona za prisutne postade bijela.');
INSERT INTO "bs_darassalam" VALUES(7,109,'7:109','Glavešine naroda faraonova povikaše: "Ovaj je, doista, čarobnjak koji zna,');
INSERT INTO "bs_darassalam" VALUES(7,110,'7:110','on hoće da vas izvede iz zemlje vaše, pa šta predlažete?"');
INSERT INTO "bs_darassalam" VALUES(7,111,'7:111','"Zadrži njega i brata njegova", rekoše, "a pošalji u gradove sakupljače,');
INSERT INTO "bs_darassalam" VALUES(7,112,'7:112','preda te će sve vješte čarobnjake dovesti."');
INSERT INTO "bs_darassalam" VALUES(7,113,'7:113','I faraonu čarobnjaci dođoše. "Da li ćemo, doista, nagradu dobiti ako budemo pobjednici?", upitaše.');
INSERT INTO "bs_darassalam" VALUES(7,114,'7:114','"Da", reče, "i bit ćete, zaista, među meni bliskima."');
INSERT INTO "bs_darassalam" VALUES(7,115,'7:115','"O Musa", rekoše onda, "hoćeš li ti, ili ćemo mi baciti?"');
INSERT INTO "bs_darassalam" VALUES(7,116,'7:116','"Bacite vi", reče on. I kad oni baciše, oči ljudima začaraše i jako ih prestrašiše, i vradžbinu veliku prirediše.');
INSERT INTO "bs_darassalam" VALUES(7,117,'7:117','I Mi naredismo Musau: "Baci štap svoj!", i on odjednom proguta sve ono čime su oni obmanu bili izveli.');
INSERT INTO "bs_darassalam" VALUES(7,118,'7:118','I tako istina na vidjelo izbi i pokaza se da je bilo lažno ono što su oni priredili,');
INSERT INTO "bs_darassalam" VALUES(7,119,'7:119','i tu oni bijahu pobijeđeni i postadoše poniženi.');
INSERT INTO "bs_darassalam" VALUES(7,120,'7:120','I čarobnjaci se licem na tlo baciše.');
INSERT INTO "bs_darassalam" VALUES(7,121,'7:121','"Mi vjerujemo u Gospodara svjetova", povikaše,');
INSERT INTO "bs_darassalam" VALUES(7,122,'7:122','"Gospodara Musaova i Harunova!"');
INSERT INTO "bs_darassalam" VALUES(7,123,'7:123','"Zar da mu povjerujete prije nego što vam ja dozvolim!", viknu faraon. "Ovo je, uistinu, smicalica koju ste u gradu smislili da biste iz njega stanovnike njegove izveli. Znat ćete vi!"');
INSERT INTO "bs_darassalam" VALUES(7,124,'7:124','"Izodsijecat ću vam, sigurno, ruke vaše i noge vaše unakrst, a onda ću vas sve zbilja razapeti!"');
INSERT INTO "bs_darassalam" VALUES(7,125,'7:125','A oni rekoše: "Mi ćemo se, doista, Gospodaru našem vratiti!"');
INSERT INTO "bs_darassalam" VALUES(7,126,'7:126','"Ti nam zamjeraš samo to što smo u dokaze Gospodara našeg povjerovali kad su nam došli. Gospodaru naš, obaspi nas strpljivošću i učini da kao predani vjernici umremo!"');
INSERT INTO "bs_darassalam" VALUES(7,127,'7:127','A glavešine naroda faraonovog rekoše: "Zar ćeš ostaviti Musaa i narod njegov da nered po Zemlji prave i da tebe i božanstva tvoja napusti?" On reče: "Ubijat ćemo mušku djecu njihovu, a žensku ćemo im ostavljati u životu; mi, uistinu, vladamo nad njima."');
INSERT INTO "bs_darassalam" VALUES(7,128,'7:128','Musa reče narodu svome: "Molite Allaha da vam pomogne i budite strpljivi, Zemlja je, doista, Allahova. On je daje u naslijeđe kome On hoće od robova Svojih; a lijep ishod pripada bogobojaznima."');
INSERT INTO "bs_darassalam" VALUES(7,129,'7:129','"Zlostavljani smo", rekoše oni, "prije nego što si nam došao, a i nakon što si došao!" A Musa reče: "Možda će Gospodar vaš neprijatelja vašeg uništiti, a vas nasljednicima na Zemlji učiniti, da bi vidio kako ćete postupiti."');
INSERT INTO "bs_darassalam" VALUES(7,130,'7:130','I Mi smo kaznili faraonov narod gladnim godinama i oskudicom u plodovima, da bi se prisjetili i pouku uzeli.');
INSERT INTO "bs_darassalam" VALUES(7,131,'7:131','I kad bi im bilo dobro, oni bi govorili: "Ovo smo zaslužili", a kad bi ih snašla kakva nevolja, zloslutnju su nalazili u Musau i onima koji su s njim vjerovali. Ali ne! Njihova je zloslutnja od Allaha bila, samo što većina njih nije znala!"');
INSERT INTO "bs_darassalam" VALUES(7,132,'7:132','I govorili su: "Kakav god da nam dokaz doneseš da nas njime opčaraš, mi ti nećemo vjerovati!"');
INSERT INTO "bs_darassalam" VALUES(7,133,'7:133','Pa Mi na njih poslasmo i poplavu, i skakavce, i krpelje, i žabe, i krv - sve jasne znakove, ali su se oni oholili, a bijahu narod prestupnički.');
INSERT INTO "bs_darassalam" VALUES(7,134,'7:134','I kad bi ih zadesila patnja, govorili bi: "O Musa, moli se za nas Gospodaru svome - onako kako ti je On naredio. Ako nas oslobodiš patnje, mi ćemo zaista vjerovati i s tobom ćemo sinove Israilove zbilja poslati."');
INSERT INTO "bs_darassalam" VALUES(7,135,'7:135','I pošto bismo ih patnje oslobodili - do vremena do kog im je bilo određeno da je podnose - oni bi, odjednom, obećanje prekršili.');
INSERT INTO "bs_darassalam" VALUES(7,136,'7:136','I onda ih Mi kaznismo i u moru ih potopismo, jer su znakove Naše poricali i prema njima ravnodušni bili,');
INSERT INTO "bs_darassalam" VALUES(7,137,'7:137','a potlačenom narodu dodadosmo u naslijeđe istočne i zapadne krajeve zemlje koju smo blagoslovili, i lijepe riječi Gospodara tvoga sinovima Israilovim bile su ispunjene - zato što su trpjeli, a sa zemljom sravnismo ono što su faraon i narod njegov bili sagradili i ono što su uvis podizali.');
INSERT INTO "bs_darassalam" VALUES(7,138,'7:138','I Mi sinove Israilove preko mora prevedosmo, pa oni naiđoše na narod koji je privržen obožavanju kumira svojih bio. "O Musa", rekoše, "napravi i ti nama boga kao što i oni imaju bogove!" "Vi ste, uistinu, narod koji ne zna!", reče on.');
INSERT INTO "bs_darassalam" VALUES(7,139,'7:139','Zaista će biti poništeno ono u šta su ogrezli bili i beskorisno će im biti ono što su radili.');
INSERT INTO "bs_darassalam" VALUES(7,140,'7:140','On reče: "Zar da vam, pored Allaha, tražim drugog boga, a On vas nad svim drugim odlikovao?"');
INSERT INTO "bs_darassalam" VALUES(7,141,'7:141','I pošto smo vas Mi izbavili od faraonovih ljudi, koji su vas najgorom mukom mučili: mušku su djecu vašu ubijali, a žensku vam u životu ostavljali- to je bilo teško iskušenje od Gospodara vašeg.');
INSERT INTO "bs_darassalam" VALUES(7,142,'7:142','Mi odredismo da čas susreta s Musaom bude kad se napuni trideset noći, i dopunismo ih još sa deset, pa se vrijeme koje je odredio Gospodar njegov ispuni za četrdeset noći. A Musa je bio rekao bratu svome Harunu: "Zamijeni me u narodu mome, i red pravi i ne slijedi puteve smutljivaca!"');
INSERT INTO "bs_darassalam" VALUES(7,143,'7:143','I kad Nam Musa dođe u određeno vrijeme, i kada mu Gospodar njegov progovori, on reče: "Gospodaru moj, ukaži mi se da Te vidim!" "Nećeš Me vidjeti", reče, "ali pogledaj u ono brdo, pa ako ono ostane na svome mjestu, vidjet ćeš Me!" I kad se Gospodar njegov onom brdu otkri, On ga sa zemljom sravni, a Musa izgubi svijest i strovali se. Čim se osvijesti, reče: "Uzvišen si Ti! Kajem Ti se, ja sam vjernik prvi!"');
INSERT INTO "bs_darassalam" VALUES(7,144,'7:144','"O Musa", reče On, "Ja sam tebe, doista, nad drugima poslanicama Svojim i govorom Svojim odabranim učinio. Ono što ti dajem uzmi i zahvalan budi!"');
INSERT INTO "bs_darassalam" VALUES(7,145,'7:145','I Mi mu na pločama napisasmo pouku za sve, i objašnjenje za svašta. Prihvati ih snažno, a narodu svom zapovjedi da se pridržava onoga što je u njima ljepše. A pokazat ću vam i stanište buntovnika.');
INSERT INTO "bs_darassalam" VALUES(7,146,'7:146','Odvratit ću od znamenja Svojih one koji se, bez ikakva osnova, na Zemlji ohole. I kakav god dokaz oni vide, neće u njega vjerovati; ako vide Pravi put - neće ga kao put prihvatiti, a ako vide stranputicu - kao put će je prihvatiti. To je zato što znakove Naše poriču i što su prema njima ravnodušni.');
INSERT INTO "bs_darassalam" VALUES(7,147,'7:147','Djela će biti poništena onima koji poriču znakove Naše i susret na onom svijetu. Zar će biti drukčije kažnjeni nego kako su radili?');
INSERT INTO "bs_darassalam" VALUES(7,148,'7:148','I narod Musaov, poslije odlaska njegova, izradi od nakita svoga kip teleta koje je rikalo i koje za božanstvo prihvati. Zar nisu vidjeli da im ono ne govori i da ih putem pravim ne vodi? Oni ga prihvatiše i zulumćari postaše.');
INSERT INTO "bs_darassalam" VALUES(7,149,'7:149','Pošto se poslije gorko pokajaše i uvidješe da su zalutali, oni rekoše: "Ako nam se Gospodar naš ne smiluje i ako nam ne oprosti, sigurno ćemo među gubitnicima biti."');
INSERT INTO "bs_darassalam" VALUES(7,150,'7:150','A kad se Musa, srdit i bijesan, narodu svome vrati, povika: "Kako ste tako ružno poslije odlaska moga postupili! Zar ste ubrzali naredbu Gospodara svoga?!", i ploče baci, i brata svoga za kosu dohvati i poče ga vući sebi. "O sine majke moje", reče Harun, "narod moj me je slabim smatrao i umalo me nije ubio; nemoj da mi se svete dušmani i ne ubrajaj mene u zulumćare."');
INSERT INTO "bs_darassalam" VALUES(7,151,'7:151','"Gospodaru moj", zamoli Musa, "oprosti meni i bratu mome i učini da budemo pod okriljem Tvoje milosti, Ti si među milostivima najmilostiviji!"');
INSERT INTO "bs_darassalam" VALUES(7,152,'7:152','One koji su tele prihvatili stići će doista srdžba Gospodara njihova i poniženje još na ovom svijetu. Tako Mi kažnjavamo one koji kuju laži.');
INSERT INTO "bs_darassalam" VALUES(7,153,'7:153','A oni koji hrđava djela rade, pa se poslije pokaju i vjernici postanu - pa, Gospodar tvoj je, poslije toga, sigurno, Onaj Koji oprašta grijehe i milostiv je.');
INSERT INTO "bs_darassalam" VALUES(7,154,'7:154','I kada Musaova srdžba minu, on uze ploče na kojima je bila ispisana uputa i milost za one koji od Gospodara svoga strahuju.');
INSERT INTO "bs_darassalam" VALUES(7,155,'7:155','I Musa odabra iz naroda svoga sedamdeset ljudi da u određeno vrijeme stanu pred Nas. A kad ih zadesi potres, on reče: "Gospodaru moj, da si htio, mogao si i njih i mene uništiti još prije. Zar da nas uništiš zbog onoga što su uradili bezumnici naši? To je samo iskušenje Tvoje kojim Ti, koga hoćeš, na stranputicu skrećeš, a koga hoćeš, na Pravi put upućuješ; Ti si Gospodar naš, pa nam oprosti i smiluj nam se! A Ti si Oprostitelj najbolji!"');
INSERT INTO "bs_darassalam" VALUES(7,156,'7:156','"I dosudi nam dobro na dunjaluku, i na ahiretu - mi se, uistinu, vraćamo Tebi!" "Kaznom Svojom Ja kažnjavam koga hoću", reče On, "a milost Moja obuhvata sve; dat ću je onima koji se budu grijeha klonili i zekat davali, i onima koji u ajete i dokaze Naše budu vjerovali",');
INSERT INTO "bs_darassalam" VALUES(7,157,'7:157','"onima koji će slijediti Poslanika, Vjerovjesnika, koji neće znati ni čitati ni pisati, kojeg oni kod sebe, u Tevratu i Indžilu, zapisana nalaze, koji će od njih tražiti da čine dobra djela, a od odvratnih odvraćati, koji će im lijepa jela dozvoliti, a ružna zabraniti, koji će ih tereta i teškoća koje su oni imali osloboditi. A oni koji u njega budu vjerovali, koji ga budu podržavali i pomagali i svjetlo po njemu poslano slijedili - oni će baš uspjeti."');
INSERT INTO "bs_darassalam" VALUES(7,158,'7:158','Reci: "O ljudi, ja sam zaista svima vama Allahov Poslanik, Njegova vlast je i na nebesima i na Zemlji; nema drugog boga osim Njega, On život i smrt daje, i zato vjerujte u Allaha i Poslanika Njegova, Vjerovjesnika, koji ne zna čitati i pisati, koji vjeruje u Allaha i riječi Njegove; njega slijedite - da biste bili upućeni!"');
INSERT INTO "bs_darassalam" VALUES(7,159,'7:159','U narodu Musaovu ima zajednica ljudi koji se napućuju Istinom i koji prema njoj pravedno sude.');
INSERT INTO "bs_darassalam" VALUES(7,160,'7:160','I Mi smo ih u dvanaest rodova podijelili, i Musau smo objavili, kad mu je narod njegov vode zatražio: "Udari štapom svojim po stijeni!", i iz nje je dvanaest vrela provrelo, svaki rod je znao vrelo iz kog će piti. I Mi smo im od oblaka hlad pravili i manu i prepelice im davali: "Jedite ono lijepo čime vas opskrbljujemo!" Oni nisu Nama, nego su sami sebi zulum učinili."');
INSERT INTO "bs_darassalam" VALUES(7,161,'7:161','A kada im je bilo rečeno: "Nastanite se u ovom naselju i jedite odakle hoćete i recite: ''Oprosti!'', a na kapiju uđite glava pognutih", oprostit ćemo vam grijehe vaše, a onima koji čine dobra djela dat ćemo i više.');
INSERT INTO "bs_darassalam" VALUES(7,162,'7:162','Onda su oni koji su zulum učinili zamijenili riječi drugim koje im nisu rečene, pa smo Mi na one koji su zulum učinili s neba kaznu spustili, zato što su griješnici bili.');
INSERT INTO "bs_darassalam" VALUES(7,163,'7:163','I upitaj ih o naselju koje se nalazilo pored mora kad su propise o suboti kršili: kada su im ribe, na oči njihove, dolazile dok su subotu svetkovali, a kad nisu svetkovali one im nisu dolazile. Eto, tako smo ih u iskušenje dovodili zato što su buntovni bili.');
INSERT INTO "bs_darassalam" VALUES(7,164,'7:164','A kad neki od njih rekoše: "Zašto opominjete narod koji će Allah uništiti ili ga teškim mukama namučiti?", oni odgovoriše: "Da bismo se pred Gospodarom vašim opravdali i da bi se oni toga klonili."');
INSERT INTO "bs_darassalam" VALUES(7,165,'7:165','I kada zaboraviše ono čime su bili upozoravani, Mi izbavismo one koji su od nevaljalih djela odvraćali, a zulumćare kaznismo teškom kaznom, zato što su stalno buntovni bili.');
INSERT INTO "bs_darassalam" VALUES(7,166,'7:166','I pošto su oni bahato odbili da se okane onoga što im je zabranjivano, Mi smo im rekli: "Postanite majmuni prezreni!"');
INSERT INTO "bs_darassalam" VALUES(7,167,'7:167','I Gospodar tvoj obznani da će prepuštati nad njima vlast do Kijametskog dana nekom ko će ih na najgori način tlačiti. Gospodar tvoj, uistinu, brzo kažnjava, a On je, doista, i Onaj Koji oprašta grijehe i milostiv je.');
INSERT INTO "bs_darassalam" VALUES(7,168,'7:168','I Mi smo ih po Zemlji kao narode raspodijelili: bilo je među njima dobrih, a i onih koji to nisu; iskušavali smo ih i u dobru i u zlu, ne bi li se ispravnom putu vratili.');
INSERT INTO "bs_darassalam" VALUES(7,169,'7:169','I poslije njih ostala su pokoljenja koja su Knjigu naslijedila i koja su uzimala prolazna dobra ovoga nižeg svijeta, i govorila: "Bit će nam oprošteno!" A ako bi im opet dopalo šaka tako nešto, opet bi to prihvatili. Zar od njih nije uzet zavjet u Knjizi da će o Allahu samo istinu govoriti - a oni čitaju ono što je u njoj. Ahiret je bolji za one koji se nastoje uščuvati; pa zar ne razumijete?');
INSERT INTO "bs_darassalam" VALUES(7,170,'7:170','A oni koji se čvrsto drže Knjige i koji obavljaju namaz - pa, Mi, doista, nećemo dopustiti da propadne nagrada onima koji čine dobra djela.');
INSERT INTO "bs_darassalam" VALUES(7,171,'7:171','A kada smo iznad njih brdo podigli - činilo se kao oblak - oni su bili uvjereni da će na njih pasti. Prihvatite snažno ono što smo vam dali, i neka vam je na umu ono što je u njemu - da biste se sačuvali!');
INSERT INTO "bs_darassalam" VALUES(7,172,'7:172','I kad je Gospodar tvoj od Ademovih sinova, iz njihovih kičmi, izveo potomstvo njihovo i zatražio od njih da posvjedoče protiv sebe: "Zar Ja nisam Gospodar vaš?", oni su odgovarali: "Jesi, mi svjedočimo ", i zato da na Kijametskom danu ne reknete: "Mi o ovome nismo ništa znali."');
INSERT INTO "bs_darassalam" VALUES(7,173,'7:173','Ili da ne reknete: "Naši su preci prije nas druge Allahu ravnim smatrali, a mi smo pokoljenje poslije njih. Zar ćeš nas kazniti za ono što su lažljivci činili?"');
INSERT INTO "bs_darassalam" VALUES(7,174,'7:174','I tako, eto, Mi opširno iznosimo dokaze, da bi oni došli sebi.');
INSERT INTO "bs_darassalam" VALUES(7,175,'7:175','I kaži im vijest o onome kome smo dokaze Naše dali, ali koji se od njih udaljio pa ga šejtan dostigao, i on je zalutao.');
INSERT INTO "bs_darassalam" VALUES(7,176,'7:176','A da smo htjeli, mogli smo ga time uzvisiti, ali se on ovom svijetu priklonio i za svojom strašću krenuo. Njegov je slučaj kao slučaj psa: ako ga potjeraš, on isplažena jezika dahće, a ako ga se okaniš - on opet dahće. Takvi su ljudi koji Naše dokaze smatraju lažnim; zato, kazuj događaje da bi oni razmislili.');
INSERT INTO "bs_darassalam" VALUES(7,177,'7:177','Loš su primjer ljudi koji ne priznaju Naše dokaze, oni zulum čine sami sebi.');
INSERT INTO "bs_darassalam" VALUES(7,178,'7:178','Koga Allah uputi na Pravi put - bit će na Pravom putu, a koga na stranputicu skrene - pa takvi su pravi gubitnici.');
INSERT INTO "bs_darassalam" VALUES(7,179,'7:179','Mi smo za Džehennem mnoge džine i ljude stvorili; oni srca imaju - a njima ne shvataju, oni oči imaju - a njima ne vide, oni uši imaju - a njima ne čuju; oni su kao stoka, čak i gori - oni su baš nemarni.');
INSERT INTO "bs_darassalam" VALUES(7,180,'7:180','Allah ima najljepša imena i vi Ga zovite tim imenima, a klonite se onih koji iskreću Njegova imena; kako budu radili, onako će biti kažnjeni!');
INSERT INTO "bs_darassalam" VALUES(7,181,'7:181','A među onima koje stvaramo ima ljudi koji druge upućuju istini i koji prema njoj pravedno sude.');
INSERT INTO "bs_darassalam" VALUES(7,182,'7:182','A one koji Naše ajete i znakove poriču Mi ćemo malo-pomalo, a da oni ni znati neće, u propast voditi.');
INSERT INTO "bs_darassalam" VALUES(7,183,'7:183','I davat ću im vremena; zamka je Moja, doista čvrsta.');
INSERT INTO "bs_darassalam" VALUES(7,184,'7:184','Pa zašto oni ne razmisle da Poslanik koji im je poslan nije lud; on je samo upozoritelj jasni.');
INSERT INTO "bs_darassalam" VALUES(7,185,'7:185','I zašto oni ne promisle o carstvu nebesa i Zemlje i o svemu onome što je On stvorio, i da im se, možda, kraj primakao? Pa u koji će govor, ako ne u Kur''an, vjerovati?');
INSERT INTO "bs_darassalam" VALUES(7,186,'7:186','Koga Allah na stranputicu skrene, niko ga ne može na Pravi put uputiti! On će ih ostaviti da u osionosti svojoj lutaju.');
INSERT INTO "bs_darassalam" VALUES(7,187,'7:187','Pitaju te o Smaku svijeta, kada će se zbiti. Reci: "To zna jedino Gospodar moj, On će ga u određeno vrijeme otkriti, a težak će biti nebesima i Zemlji, sasvim neočekivano će vam doći." Pitaju te kao da ti o njemu nešto znaš. Reci: "To samo Allah zna, ali većina ljudi ne zna."');
INSERT INTO "bs_darassalam" VALUES(7,188,'7:188','Reci: "Ja ne mogu ni samom sebi neku korist pribaviti, ni od sebe kakvu štetu otkloniti; biva onako kako Allah hoće. A da znadoh nedokučivo, stekao bih mnoga dobra, a zlo bi bilo daleko od mene; ja samo donosim opomene i radosne vijesti ljudima koji vjeruju."');
INSERT INTO "bs_darassalam" VALUES(7,189,'7:189','On je Taj Koji vas je od jednog čovjeka stvorio - a od njega je drugu njegovu stvorio da se on uz nju smiri. Kada je čovjek imao odnos sa svojom suprugom, ona je zanijela lahko breme i nosila ga; a kad joj je ono otežalo, njih dvoje su zamolili Allaha, Gospodara svoga: "Ako nam daruješ zdrava potomka, bit ćemo, doista, u redovima zahvalnih!"');
INSERT INTO "bs_darassalam" VALUES(7,190,'7:190','I kad im je On darovao zdrava potomka, njih dvoje su izjednačili druge s Njim u onome što im On daje, a Allah je vrlo visoko iznad onih koje Njemu u obožavanju pridružuju!');
INSERT INTO "bs_darassalam" VALUES(7,191,'7:191','Zar da Njemu ravnim smatraju one koji ne mogu ništa stvoriti, i sami su stvoreni,');
INSERT INTO "bs_darassalam" VALUES(7,192,'7:192','i koji im ne mogu pomoći niti mogu sebi pomoći?');
INSERT INTO "bs_darassalam" VALUES(7,193,'7:193','A ako ih zamolite da vas na Pravi put upute, neće vam se odazvati; isto vam je pozivali ih ili šutjeli.');
INSERT INTO "bs_darassalam" VALUES(7,194,'7:194','Oni kojima se vi, pored Allaha, molite, zaista su robovi, kao i vi. Pa, vi ih molite, i neka vam se odazovu ako istinu govorite!');
INSERT INTO "bs_darassalam" VALUES(7,195,'7:195','Imaju li oni noge da na njima hodaju, ili ruke da njima prihvataju, imaju li oči da njima gledaju, ili uši da njima čuju? Reci: "Zovite božanstva svoja, pa protiv mene kakvo hoćete lukavstvo smislite, i ne odugovlačite."');
INSERT INTO "bs_darassalam" VALUES(7,196,'7:196','Moj je, zaista, zaštitnik Allah Koji Knjigu objavljuje i On se o dobrima brine.');
INSERT INTO "bs_darassalam" VALUES(7,197,'7:197','A oni kojima se vi, pored Njega, molite, ne mogu ni vama, a ni sebi pomoći.');
INSERT INTO "bs_darassalam" VALUES(7,198,'7:198','A kad ih zamolite da vas upute na pravi put, oni ne čuju; vidiš ih kao da te gledaju, ali oni ne vide.');
INSERT INTO "bs_darassalam" VALUES(7,199,'7:199','Ti ono lijepo što čine prihvati!, i traži da se čine dobra djela, a neznalica se kloni!');
INSERT INTO "bs_darassalam" VALUES(7,200,'7:200','A ako te šejtan pokuša na zlo navesti, ti potraži utočište u Allaha, On je, uistinu, Onaj Koji sve čuje i Onaj Koji sve zna.');
INSERT INTO "bs_darassalam" VALUES(7,201,'7:201','Doista, oni koji se Allaha boje, čim ih dodirne sablazan šejtanska, sjete se, i postanu razboriti,');
INSERT INTO "bs_darassalam" VALUES(7,202,'7:202','dok prijatelje šejtanove šejtani podržavaju u zabludi i nikako ne prestaju.');
INSERT INTO "bs_darassalam" VALUES(7,203,'7:203','Kad im nikakav znak ne doneseš, oni govore: "Zašto ga sam ne izmisliš!" Reci: "Ja slijedim samo ono što mi Gospodar moj objavljuje." Ovo su jasni dokazi od Gospodara vašeg i uputa i milost za ljude koji vjeruju.');
INSERT INTO "bs_darassalam" VALUES(7,204,'7:204','A kad se uči Kur''an, vi ga slušajte i šutite - da bi vam milost ukazana bila.');
INSERT INTO "bs_darassalam" VALUES(7,205,'7:205','I spominji Gospodara svoga u sebi, ponizno i sa strahopoštovanjem i ne podižući jako glas - ujutro i navečer, i ne budi među nemarnima.');
INSERT INTO "bs_darassalam" VALUES(7,206,'7:206','Oni koji su kod Gospodara tvoga doista ne zaziru da Mu ibadet čine; samo Njega hvale i samo Njemu na sedždu padaju.');
INSERT INTO "bs_darassalam" VALUES(8,1,'8:1','Pitaju te o ratnom plijenu. Reci: "Ratni plijen pripada Allahu i Poslaniku." Zato se bojte Allaha i izgladite međusobne razmirice, i pokoravajte se Allahu i Njegovu Poslaniku, ako ste pravi vjernici.');
INSERT INTO "bs_darassalam" VALUES(8,2,'8:2','Pravi su vjernici samo oni čija se srca strahom ispune kad se Allah spomene, a kad im se ajeti Njegovi uče, vjerovanje im povećaju i samo se na Gospodara svoga oslanjaju,');
INSERT INTO "bs_darassalam" VALUES(8,3,'8:3','oni koji namaz obavljaju i dijele od onoga čime smo ih Mi opskrbili,');
INSERT INTO "bs_darassalam" VALUES(8,4,'8:4','Oni su, zbilja, pravi vjernici - njih kod Gospodara njihova čekaju počasti, i oprost, i opskrba divna.');
INSERT INTO "bs_darassalam" VALUES(8,5,'8:5','Tako je bilo i onda kada te je Gospodar tvoj s pravom iz doma tvoga izveo, a to jednoj grupi vjernika nije nikako po volji bilo.');
INSERT INTO "bs_darassalam" VALUES(8,6,'8:6','Raspravljali su s tobom o borbi, nakon što je bilo očito da će pobijediti, kao da se naočigled svoj u smrt gone.');
INSERT INTO "bs_darassalam" VALUES(8,7,'8:7','Kad vam je Allah obećao da će vaša biti jedna od dvije skupine - a vi ste više voljeli da vam padne šaka ona koja nije bila naoružana - Allah je htio riječima Svojim Istinu utvrditi i nevjernike u korijenu istrijebiti,');
INSERT INTO "bs_darassalam" VALUES(8,8,'8:8','da Istinu utvrdi i neistinu uništi, makar to prestupnicima ne bilo po volji.');
INSERT INTO "bs_darassalam" VALUES(8,9,'8:9','I kada ste od Gospodara svoga spas zatražili, On vam se odazvao: "Poslat ću vam u pomoć hiljadu meleka koji će jedni za drugima dolaziti."');
INSERT INTO "bs_darassalam" VALUES(8,10,'8:10','Allah je to učinio da bi vas obradovao i da bi se time srca vaša umirila; a pobjeda je samo od Allaha -Allah je zaista silan i mudar.');
INSERT INTO "bs_darassalam" VALUES(8,11,'8:11','Kad je On učinio da vas san, kao spokojstvo od Njega, obuzme i s neba vam vodu spustio da bi vas njome očistio, i da bi od vas šejtanovo uznemiravanje odstranio, i da bi srca vaša jakim učinio i da bi njome - kišom stopala učvrstio.');
INSERT INTO "bs_darassalam" VALUES(8,12,'8:12','Kad je Gospodar tvoj nadahnuo meleke: "Ja sam s vama, pa učvrstite one koji vjeruju! U srca nevjernika Ja ću strah uliti, pa ih vi po šijama udarite, i udarite ih po svim udovima."');
INSERT INTO "bs_darassalam" VALUES(8,13,'8:13','To je zato što su se suprotstavljali Allahu i Poslaniku Njegovu; a onoga ko se suprotstavlja Allahu i Poslaniku Njegovu, Allah će, zaista, strašno kazniti.');
INSERT INTO "bs_darassalam" VALUES(8,14,'8:14','Kazna vam je to, pa je iskusite, a nevjernike čeka patnja u Vatri.');
INSERT INTO "bs_darassalam" VALUES(8,15,'8:15','O vi koji vjerujete, kada se s nevjernicima sukobite, a njih nastupa mnogo, leđa im ne okrećite;');
INSERT INTO "bs_darassalam" VALUES(8,16,'8:16','onaj ko im tada leđa okrene - osim onog koji se povuče s namjerom da se ponovno bori ili drugoj četi pristupi - vratit će se natovaren Allahovom srdžbom; prebivalište njegovo bit će Džehennem, a užasno je to krajnje odredište.');
INSERT INTO "bs_darassalam" VALUES(8,17,'8:17','Njih niste ubijali vi, nego Allah; i nisi ti bacio, kad si bacio, nego je Allah bacio, da bi vjernike lijepom kušnjom iskušao -Allah je, zaista, Onaj Koji sve čuje i Onaj Koji sve zna.');
INSERT INTO "bs_darassalam" VALUES(8,18,'8:18','Tako je to bilo, a doista je Allah Onaj Koji oslabljuje spletke nevjernika.');
INSERT INTO "bs_darassalam" VALUES(8,19,'8:19','Ako ste se molili da pobijedite - pa došla vam je, eto, pobjeda! A da se okanite, bolje bi vam bilo! I ako se ponovo vratite, Mi ćemo se ponovo vratiti, i nimalo vam neće koristiti tabor vaš, ma koliko brojan bio, a Allah je, zaista, uz vjernike.');
INSERT INTO "bs_darassalam" VALUES(8,20,'8:20','O vi koji vjerujete, pokoravajte se Allahu i Njegovom Poslaniku, i ne okrećite se od njega, a čujete ga,');
INSERT INTO "bs_darassalam" VALUES(8,21,'8:21','i ne budite kao oni koji su rekli: "Čuli smo!" - a ne slušaju.');
INSERT INTO "bs_darassalam" VALUES(8,22,'8:22','Doista, najgora su bića kod Allaha oni koji su gluhi i nijemi, koji ne razumiju.');
INSERT INTO "bs_darassalam" VALUES(8,23,'8:23','Da Allah zna da od njih može biti ikakva dobra, učinio bi da čuju, a da je učinio i da čuju, oni bi se, opet, okrećući se od toga udaljili.');
INSERT INTO "bs_darassalam" VALUES(8,24,'8:24','O vi koji vjerujete, odazovite se Allahu i Poslaniku kad od vas zatraži da činite ono što vas oživljava; i neka znate da se Allah upliće između čovjeka i srca njegova, i da ćete svi pred Njim biti sakupljeni.');
INSERT INTO "bs_darassalam" VALUES(8,25,'8:25','I čuvajte se iskušenja koja neće pogoditi samo one među vama koji su zulum činili, i znajte da Allah strašno kažnjava.');
INSERT INTO "bs_darassalam" VALUES(8,26,'8:26','I sjetite se kad vas je bilo malo, kad ste na zemlji bili potlačeni - bojali ste se da vas ljudi ne pohvataju, pa vam je On sklonište dao i Svojom pomoći vas ojačao i ljepotama vas opskrbio da biste bili zahvalni.');
INSERT INTO "bs_darassalam" VALUES(8,27,'8:27','O vi koji vjerujete, ne iznevjeravajte Allaha i Poslanika i ne pronevjeravajte emanete svoje, a znate - svjesni ste da to radite.');
INSERT INTO "bs_darassalam" VALUES(8,28,'8:28','I neka znate da su imeci vaši i djeca vaša samo iskušenje, i da je samo u Allaha nagrada velika.');
INSERT INTO "bs_darassalam" VALUES(8,29,'8:29','O vi koji vjerujete, ako se budete Allaha bojali, On će vam ono što istinu od neistine razdvaja dati i preko ružnih postupaka vaših će preći i oprostiti vam. A Allahova je dobrota neizmjerna.');
INSERT INTO "bs_darassalam" VALUES(8,30,'8:30','I kad su ti nevjernici zamke razapinjali da bi te u tamnicu bacili ili da bi te ubili, ili da bi te prognali; oni su zamke pleli pa je i Allah njima zamke pleo, a Allah najbolje zamke plete.');
INSERT INTO "bs_darassalam" VALUES(8,31,'8:31','Kada im se ajeti Naši uče, govore: "Već smo čuli! Da hoćemo, i mi bismo tako nešto rekli; to su samo izmišljotine naroda drevnih."');
INSERT INTO "bs_darassalam" VALUES(8,32,'8:32','A kad su oni rekli: "Bože, ako je ovo zbilja istina Tvoja, Ti pusti na nas kamenje s neba kao kišu ili nam pošalji patnju bolnu!"');
INSERT INTO "bs_darassalam" VALUES(8,33,'8:33','Allah ih i nije kaznio dok si ti među njima bio; i Allah ih neće kazniti ako oni za oprost mole.');
INSERT INTO "bs_darassalam" VALUES(8,34,'8:34','A kako da ih Allah ne kazni kad brane drugima pristup Časnoj džamiji, a oni nisu njeni čuvari. Čuvari njeni treba da budu samo oni koji se Allaha boje, ali većina njih ne zna.');
INSERT INTO "bs_darassalam" VALUES(8,35,'8:35','Molitva njihova pored Kabe svodi se samo na zviždanje i pljeskanje rukama; zato patnju iskusite jer ne vjerujete.');
INSERT INTO "bs_darassalam" VALUES(8,36,'8:36','Zaista oni koji ne vjeruju troše imovinu svoju da bi od Allahova puta odvraćali. Oni će je tako trošiti, zatim će zbog toga žaliti i na kraju će pobijeđeni biti. A oni koji ne budu vjerovali - u Džehennem će biti potjerani,');
INSERT INTO "bs_darassalam" VALUES(8,37,'8:37','da bi Allah dobre od nevaljalih odvojio i da bi nevaljale jedne na druge naslagao, a onda ih sve u gomilu zbio i u Džehennem bacio. Oni su baš gubitnici.');
INSERT INTO "bs_darassalam" VALUES(8,38,'8:38','Reci onima koji ne vjeruju: ako se okane, bit će im oprošteno ono što je prije bilo; a ako se ne okane - pa zna se šta je s drevnim narodima bilo.');
INSERT INTO "bs_darassalam" VALUES(8,39,'8:39','I borite se protiv njih dok smutnja ne iščezne i dok samo Allahova vjera ne ostane. Ako se oni okane - pa zaista Allah dobro vidi šta oni rade.');
INSERT INTO "bs_darassalam" VALUES(8,40,'8:40','A ako se okrenu, znajte da je Allah vaš zaštitnik, a divan je On Zaštitnik i divan Pomagač!');
INSERT INTO "bs_darassalam" VALUES(8,41,'8:41','I znajte da od svega što u borbi zaplijenite jedna petina pripada Allahu i Poslaniku, i rodbini njegovoj, i siročadi, i siromasima, i putnicima – ako vjerujete u Allaha i u ono što smo objavili robu Našem na Dan pobjede, na dan kada su se sukobile dvije vojske, a Allah sve može...');
INSERT INTO "bs_darassalam" VALUES(8,42,'8:42','kada ste vi bili u bližoj strani doline, oni u daljoj strani doline, a karavana niže vas. I da ste se dogovarali o vremenu borbe, ne biste se dogovorili, ali se ona dogodila da bi Allah dao da se ispuni ono što se moralo dogoditi, da nevjernik ostane nevjernik poslije očigledna dokaza, i da vjernik ostane vjernik poslije očigledna dokaza - a Allah je, doista, Onaj Koji sve čuje i Onaj Koji sve zna.');
INSERT INTO "bs_darassalam" VALUES(8,43,'8:43','Kad ti je Allah u snu pokazao da je njih malo; a da ti je pokazao da ih je mnogo, vi biste duhom klonuli i o boju se raspravljali, ali je Allah spas ukazao. On, zaista, dobro zna šta svačije grudi kriju.');
INSERT INTO "bs_darassalam" VALUES(8,44,'8:44','A kad ste se sukobili, u očima vašim On ih je prikazao u malom broju, a vas u očima njihovim također u malom broju, da bi Allah dao da se ispuni ono što se moralo dogoditi. – A Allahu se sve vraća.');
INSERT INTO "bs_darassalam" VALUES(8,45,'8:45','O vjernici, kada se s kakvom četom sukobite, smjeli budite i neprestano Allaha spominjite da biste postigli što želite...');
INSERT INTO "bs_darassalam" VALUES(8,46,'8:46','i pokoravajte se Allahu i Poslaniku Njegovu, i ne prepirite se da ne biste klonuli i bez borbenog duha ostali – i budite izdržljivi, jer Allah je, zaista, na strani izdržljivih.');
INSERT INTO "bs_darassalam" VALUES(8,47,'8:47','I ne budite kao oni koji su, da se pokažu svijetu, nadmeno iz grada svog izašli da bi od Allahova puta odvraćali. – A Allah dobro zna ono što oni rade.');
INSERT INTO "bs_darassalam" VALUES(8,48,'8:48','I kada im je šejtan kao lijepe njihove postupke predstavio i rekao: “Niko vas danas ne može pobijediti, i ja sam vaš zaštitnik!” – onda je on, kada su se dva protivnička tabora sukobila, natrag uzmaknuo, i rekao: “Ja nemam ništa s vama, ja vidim ono što ne vidite vi, i ja se bojim Allaha, jer Allah strašno kažnjava!”');
INSERT INTO "bs_darassalam" VALUES(8,49,'8:49','Kad su dvoličnjaci i oni čija su srca bolesna govorili: “Ove je obmanula vjera njihova!” A onaj ko se u Allaha pouzda – pa Allah je zaista silan i mudar.');
INSERT INTO "bs_darassalam" VALUES(8,50,'8:50','A da si samo vidio kad su meleki nevjernicima duše uzimali i po licima ih njihovim i straga udarali: “Iskusite patnju u ognju!');
INSERT INTO "bs_darassalam" VALUES(8,51,'8:51','To je za ono što ste rukama svojim pripremili, jer Allah nije nepravedan robovima Svojim!”');
INSERT INTO "bs_darassalam" VALUES(8,52,'8:52','Tako je bilo i s faraonovim ljudima i onima prije njih: u Allahove dokaze nisu vjerovali, pa ih je Allah zbog grijehova njihovih kaznio. – Allah je, uistinu, moćan i strašno kažnjava!');
INSERT INTO "bs_darassalam" VALUES(8,53,'8:53','To je zato što Allah neće lišiti blagostanja narod kome ga je podario, sve dok se on sam ne promijeni. – A Allah sve čuje i sve zna.');
INSERT INTO "bs_darassalam" VALUES(8,54,'8:54','Tako je bilo i s faraonovim ljudima i onima prije njih: dokaze Gospodara svog nisu priznavali, pa smo ih Mi, zbog grijeha njihovih, uništili, a faraonove smo ljude potopili – svi su oni nevjernici bili.');
INSERT INTO "bs_darassalam" VALUES(8,55,'8:55','Najgora su bića kod Allaha oni koji poriču, oni koji neće da vjeruju...');
INSERT INTO "bs_darassalam" VALUES(8,56,'8:56','oni s kojima ti ugovore sklapaš, pa oni svaki put, ne bojeći se posljedica, krše ugovor svoj.');
INSERT INTO "bs_darassalam" VALUES(8,57,'8:57','Ako se u borbi s njima sukobiš, tako ih razjuri da se opamete oni koji su iza njih.');
INSERT INTO "bs_darassalam" VALUES(8,58,'8:58','Čim primijetiš vjerolomstvo nekog plemena, i ti njemu isto tako otkaži ugovor. – Allah uistinu ne voli vjerolomnike.');
INSERT INTO "bs_darassalam" VALUES(8,59,'8:59','I neka nikako ne misle oni koji ne vjeruju da će se spasiti, oni doista neće moći umaći.');
INSERT INTO "bs_darassalam" VALUES(8,60,'8:60','I protiv njih pripremite koliko god možete snage i konja za boj, da biste time zaplašili Allahove i vaše neprijatelje, i druge osim njih – vi ih ne poznajete, Allah ih zna. Sve što na Allahovu putu potrošite nadoknađeno će vam biti, neće vam se nepravda učiniti.');
INSERT INTO "bs_darassalam" VALUES(8,61,'8:61','Ako oni budu skloni miru, budi i ti sklon i pouzdaj se u Allaha – jer On, uistinu, sve čuje i sve zna.');
INSERT INTO "bs_darassalam" VALUES(8,62,'8:62','A ako te htjednu prevariti – pa tebi je doista dovoljan Allah, On te podržava Svojom pomoći i vjernicima...');
INSERT INTO "bs_darassalam" VALUES(8,63,'8:63','i On je sjedinio srca njihova. Da si ti potrošio sve ono što na Zemlji postoji, ti ne bi sjedinio srca njihova, ali ih je Allah sjedinio. – On je zaista silan i mudar.');
INSERT INTO "bs_darassalam" VALUES(8,64,'8:64','O Vjerovjesniče, Allah je dovoljan tebi i vjernicima koji te slijede.');
INSERT INTO "bs_darassalam" VALUES(8,65,'8:65','O Vjerovjesniče, podstiči vjernike na borbu! Ako vas bude dvadesetak izdržljivih, pobijedit će dvije stotine; a ako vas bude stotina, pobijedit će hiljadu onih koji ne vjeruju – zato što su oni ljudi koji ne shvaćaju.');
INSERT INTO "bs_darassalam" VALUES(8,66,'8:66','Sad vam Allah daje olakšicu, On zna da ste izmoreni: ako vas bude stotina izdržljivih, pobijedit će dvije stotine; ako vas bude hiljada, pobijedit će, Allahovom voljom, dvije hiljade. – A Allah je uz one koji su izdržljivi.');
INSERT INTO "bs_darassalam" VALUES(8,67,'8:67','Nijednom vjerovjesniku nije dopušteno držati zarobljenike dok ne izvojuje pobjedu na Zemlji; vi želite prolazna dobra ovoga svijeta, a Allah želi onaj svijet. – Allah je silan i mudar.');
INSERT INTO "bs_darassalam" VALUES(8,68,'8:68','Da nije ranije Allahove odredbe, snašla bi vas patnja velika zbog onoga što ste uzeli.');
INSERT INTO "bs_darassalam" VALUES(8,69,'8:69','Sad jedite ono što ste zaplijenili, kao dopušteno i lijepo, i bojte se Allaha. – Allah zaista prašta i milostiv je.');
INSERT INTO "bs_darassalam" VALUES(8,70,'8:70','O Vjerovjesniče, reci zarobljenicima koji se nalaze u rukama vašim: “Ako Allah zna da u srcima vašim ima bilo šta dobro, dat će vam bolje od onoga što vam je uzeto i oprostit će vam.” – A Allah prašta i milostiv je.');
INSERT INTO "bs_darassalam" VALUES(8,71,'8:71','A ako htjednu da te prevare, pa - oni su i prije Allaha nastojali varati i zato ti je On omogućio da ih pobijediš; a Allah je Onaj Koji sve zna i mudar je.');
INSERT INTO "bs_darassalam" VALUES(8,72,'8:72','Zaista oni koji vjeruju i iseljavaju se, i u borbi na Allahovu putu zalažu imetke svoje i živote svoje, i oni koji daju utočište i pomažu, oni su jedni drugima zaštitnici. A onima koji vjeruju, a koji se nisu iselili, vi ne možete, sve dok se ne isele, zaštitnici biti. A ako vas zamole da im u vjeri pomognete, dužni ste im u pomoć priteći, osim protiv naroda s kojim o nenapadanju zaključen ugovor imate. A Allah dobro vidi ono što radite.');
INSERT INTO "bs_darassalam" VALUES(8,73,'8:73','Nevjernici su jedni drugima zaštitnici. Ne postupite li tako, nastat će smutnja na Zemlji i nered veliki.');
INSERT INTO "bs_darassalam" VALUES(8,74,'8:74','Oni koji vjeruju i isele se, i bore se na Allahovu putu, i oni koji daju sklonište i pomažu – oni su, zbilja, pravi vjernici, njih čeka oprost i obilje plemenito.');
INSERT INTO "bs_darassalam" VALUES(8,75,'8:75','A oni koji kasnije vjernici postanu pa se isele i u borbi zajedno s vama sudjeluju – i oni su vaši. A rođaci su, prema Allahovoj Knjizi, jedni drugima preči. – Allah, zaista, sve zna.');
INSERT INTO "bs_darassalam" VALUES(9,1,'9:1','Obznana od Allaha i Njegova Poslanika onim višebošcima s kojima ste zaključili ugovore...');
INSERT INTO "bs_darassalam" VALUES(9,2,'9:2','“Putujte po svijetu još četiri mjeseca, ali znajte da Allahu nećete umaći i da će Allah nevjernike osramotiti”...');
INSERT INTO "bs_darassalam" VALUES(9,3,'9:3','Ovo je i proglas od Allaha i Njegova Poslanika ljudima na najveći dan hadža, da Allah i Njegov Poslanik nemaju ništa s višebošcima, pa ako se pokajete - to je za vas bolje a ako se okrenete znajte da Allahu nećete umaći. A nevjernicima navijesti patnju bolnu.');
INSERT INTO "bs_darassalam" VALUES(9,4,'9:4','Izuzetak su višebošci s kojima imate zaključene ugovore koje oni nisu ni u čemu povrijedili, niti su ikoga protiv vas pomagali, ispoštujte ugovore s njima do dogovorenog roka; Allah zaista voli bogobojazne.');
INSERT INTO "bs_darassalam" VALUES(9,5,'9:5','Kada prođu sveti mjeseci, onda ubijajte višebošce gdje god ih nađete, zarobljavajte ih, opsjedajte i na svakom prolazu dočekujte. Pa ako se pokaju i budu namaz obavljali i zekat davali, ostavite ih na miru, a Allah je, zaista, Onaj Koji oprašta grijehe i milostiv je.');
INSERT INTO "bs_darassalam" VALUES(9,6,'9:6','Ako te neki od višebožaca zamoli za zaštitu, ti ga zaštiti da bi saslušao Allahove riječi, a potom ga otpremi na mjesto pouzdano za njega. To je zato što oni pripadaju narodu koji ne zna.');
INSERT INTO "bs_darassalam" VALUES(9,7,'9:7','Kako će višebošci imati ugovor s Allahom i Poslanikom Njegovim!? – Ali, s onima s kojima ste ugovor kod Časnog hrama zaključili, sve dok se oni ugovora budu pridržavali, pridržavajte se i vi, jer Allah zaista voli pobožne.');
INSERT INTO "bs_darassalam" VALUES(9,8,'9:8','Kako, kad oni, ako bi vas pobijedili, ne bi, kad se o vama radi, ni srodstvo ni sporazum poštivali!? Oni vam se ustima svojim umiljavaju, ali im se srca protive, većina njih su nevjernici.');
INSERT INTO "bs_darassalam" VALUES(9,9,'9:9','Oni Allahove ajete za ono što malo vrijedi zamjenjuju, pa od puta Njegova odvraćaju – zaista je ružno kako postupaju.');
INSERT INTO "bs_darassalam" VALUES(9,10,'9:10','Ni rodbinstvo ni sporazum, kad se radi o vjerniku, ne poštuju, i sve granice zla prekoračuju.');
INSERT INTO "bs_darassalam" VALUES(9,11,'9:11','Ali ako se oni budu pokajali i molitvu obavljali i zekat davali, braća su vam po vjeri. A Mi dokaze objašnjavamo ljudima koji razumiju.');
INSERT INTO "bs_darassalam" VALUES(9,12,'9:12','A ako prekrše zakletve svoje, poslije zaključenja ugovora s njima, i ako vjeru vašu budu vrijeđali, onda se borite protiv kolovođā bezvjerstva – za njih, doista, ne postoje zakletve – da bi se okanili.');
INSERT INTO "bs_darassalam" VALUES(9,13,'9:13','Zar se nećete boriti protiv ljudi koji su zakletve svoje prekršili i nastojali protjerati Poslanika, i prvi vas napali? Zar ih se bojite? Preče je da se Allaha bojite, ako ste vjernici.');
INSERT INTO "bs_darassalam" VALUES(9,14,'9:14','Borite se protiv njih! Allah će ih rukama vašim kazniti i poniziti, a vas će protiv njih pomoći, i grudi vjernika zaliječiti...');
INSERT INTO "bs_darassalam" VALUES(9,15,'9:15','i iz srca njihovih srdžbu odstraniti. A Allah će onome kome On hoće pokajanje primiti; Allah je Onaj Koji sve čuje i mudar je.');
INSERT INTO "bs_darassalam" VALUES(9,16,'9:16','Zar mislite da ćete biti ostavljeni, a da Allah ne ukaže na one među vama koji se bore i koji, umjesto Allaha i Poslanika Njegova i vjernikā, nisu uzeli nikoga za prisna prijatelja? – A Allah dobro zna za ono što radite.');
INSERT INTO "bs_darassalam" VALUES(9,17,'9:17','višebošci nisu dostojni da Allahove džamije održavaju kad sami priznaju da su nevjernici. Djela će se njihova poništiti i u Vatri će vječno ostati.');
INSERT INTO "bs_darassalam" VALUES(9,18,'9:18','Allahove džamije održavaju oni koji u Allaha i u ahiret vjeruju i koji namaz obavljaju i zekat daju i koji se nikoga osim Allaha ne boje, oni su na Pravom putu.');
INSERT INTO "bs_darassalam" VALUES(9,19,'9:19','Zar smatrate da je onaj koji hodočasnike vodom napaja i koji vodi brigu o Časnom hramu ravan onom koji u Allaha i u onaj svijet vjeruje i koji se na Allahovu putu bori? – Nisu oni jednaki pred Allahom. A Allah neće ukazati na Pravi put onima koji sami sebi nepravdu čine.');
INSERT INTO "bs_darassalam" VALUES(9,20,'9:20','U većoj su časti kod Allaha oni koji vjeruju i koji se iseljavaju i koji se bore na Allahovu putu zalažući imetke svoje i živote svoje – oni će postići što žele.');
INSERT INTO "bs_darassalam" VALUES(9,21,'9:21','Gospodar im njihov šalje radosne vijesti da će im milostiv i blagonaklon biti i da će ih u džennetske bašče uvesti, u kojima će neprekidno uživati...');
INSERT INTO "bs_darassalam" VALUES(9,22,'9:22','vječno će i zauvijek u njima boraviti. – Uistinu, u Allaha je nagrada velika.');
INSERT INTO "bs_darassalam" VALUES(9,23,'9:23','O vi koji vjerujete, ne uzimajte za prisne prijatelje ni očeve svoje ni braću svoju, ako više vole nevjerovanje od vjerovanja. A ko god od vas bude s njima prisno prijateljevao, pa takvi su pravi zulumćari.');
INSERT INTO "bs_darassalam" VALUES(9,24,'9:24','Reci: “Ako su vam očevi vaši, i sinovi vaši, i braća vaša, i žene vaše, i rodvaš, i imanja vaša koja ste stekli, i trgovačka roba za koju strahujete da nećeprođe imati, i kuće vaše u kojima se prijatno osjećate – miliji od Allaha iPoslanika, i od borbe na Njegovom putu, onda pričekajte dok Allah Svojuodluku ne donese. A Allah neće uputiti ljude buntovnike.”');
INSERT INTO "bs_darassalam" VALUES(9,25,'9:25','Allah vas je na mnogim bojištima pomogao, a i onog dana na Hunejnu kad vas je mnoštvo vaše zanijelo, ali vam ono nije ni od kakve koristi bilo, nego vam je zemlja, koliko god da je bila prostrana, tijesna postala, pa ste se u bijeg dali.');
INSERT INTO "bs_darassalam" VALUES(9,26,'9:26','Zatim je Allah na Poslanika Svog i na vjernike milost Svoju spustio, i vojske koje vi niste vidjeli poslao i one koji nisu vjerovali na muke stavio; i to je bila kazna za nevjernike.');
INSERT INTO "bs_darassalam" VALUES(9,27,'9:27','Allah je poslije toga onom kome je htio oprostio. A Allah prašta i samilostan je.');
INSERT INTO "bs_darassalam" VALUES(9,28,'9:28','O vjernici, višebošci su sama pogan, i neka se, nakon ove godine,više ne približuju Časnoj džamiji. A ako se bojite oskudice, pa Allah ćevas, ako hoće, iz obilja Svoga imućnim učiniti. Allah je, zaista, Onaj Kojisve zna i mudar je.');
INSERT INTO "bs_darassalam" VALUES(9,29,'9:29','Borite se protiv onih kojima je data Knjiga a koji ne vjeruju ni u Allaha ni u onaj svijet, ne smatraju zabranjenim ono što Allah i Njegov Poslanik zabranjuju i ne ispovijedaju istinsku vjeru – sve dok ne daju glavarinu poslušno i pri tom budu ponizni.');
INSERT INTO "bs_darassalam" VALUES(9,30,'9:30','Jevreji govore: “Uzejr je – Allahov sin.” – A kršćani kažu: “Mesih je – Allahov sin.” To su riječi njihove, iz usta njihovih, oponašaju riječi nevjernika prijašnjih – ubio ih Allah! Kuda se odmeću?');
INSERT INTO "bs_darassalam" VALUES(9,31,'9:31','Oni, pored Allaha, bogovima smatraju svećenike svoje i monahe svoje i Mesiha, sina Merjemina, a naređeno im je da se samo jednom Bogu klanjaju – nema boga osim Njega. On je vrlo visoko iznad onih koje oni Njemu ravnim smatraju.');
INSERT INTO "bs_darassalam" VALUES(9,32,'9:32','Oni žele ustima svojim utrnuti Allahovo svjetlo, a Allah želi vidljivim učiniti svjetlo Svoje, makar ne bilo po volji nevjernicima.');
INSERT INTO "bs_darassalam" VALUES(9,33,'9:33','On je poslao Poslanika Svog s uputstvom i pravom vjerom da bi je izdigao iznad ostalih vjera, makar ne bilo po volji višebošcima.');
INSERT INTO "bs_darassalam" VALUES(9,34,'9:34','O vjernici, mnogi svećenici i monasi doista na nedozvoljen način tuđa imanja jedu i od Allahova puta odvraćaju. One koji zlato i srebro gomilaju i ne troše ga na Allahovom putu – obraduj bolnom patnjom...');
INSERT INTO "bs_darassalam" VALUES(9,35,'9:35','na Dan kad se ono u vatri džehennemskoj bude usijalo, pa se njime čela njihova i slabine njihove i leđa njihova budu žigosala. “Ovo je ono što ste za sebe zgrtali, iskusite zato kaznu za ono što ste gomilali!”');
INSERT INTO "bs_darassalam" VALUES(9,36,'9:36','Broj mjeseci u Allaha je dvanaest, prema Allahovoj Knjizi, od dana kad je nebesa i Zemlju stvorio, a četiri su sveta – to je prava vjera. U (pogotovo) nepravdu sebi ne činite! A borite se protiv svih višebožaca, kao što se oni svi bore protiv vas, i znajte da je Allah na strani onih koji se Allaha boje i grijeha klone.');
INSERT INTO "bs_darassalam" VALUES(9,37,'9:37','Premještanjem svetih mjeseci samo se povećava nevjerovanje, čime se nevjernici dovode u zabludu. Jedne ga godine proglašavaju običnim, a druge godine proglašavaju ga svetim – da bi ispunili broj onih mjeseci koje je Allah učinio svetim. Ružni postupci njihovi predstavljeni su im kao lijepi. – A Allah neće ukazati na Pravi put onima koji neće da vjeruju.');
INSERT INTO "bs_darassalam" VALUES(9,38,'9:38','O vi koji vjerujete, zašto su neki od vas oklijevali kad vam je bilo rečeno: "Krenite u borbu na Allahovu putu!", kao da ste za zemlju prikovani? Zar vam je draži život na dunjaluku od Ahireta? A uživanje na dunjaluku prema ahiretu neznatno je.');
INSERT INTO "bs_darassalam" VALUES(9,39,'9:39','Ako ne budete u boj išli, On će vas na nesnosne muke staviti i drugim će vas narodom zamijeniti, a vi Mu nećete nimalo nauditi. A Allah sve može.');
INSERT INTO "bs_darassalam" VALUES(9,40,'9:40','Ako ga vi ne pomognete, pa pomogao ga je Allah onda kad su ga oni koji ne vjeruju prisilili da ode, kad je s njim bio samo drug njegov, kad su njih dvojica bili u pećini, i kad je on rekao drugu svome: "Ne brini, Allah je s nama!" Pa je Allah spustio smiraj Svoj na njega i pomagao ga vojskom koju vi niste vidjeli i učinio da riječ nevjernika bude donja, a Allahova riječ - ona je gornja. Allah je silan i mudar.');
INSERT INTO "bs_darassalam" VALUES(9,41,'9:41','Krećite u boj, bili slabi ili snažni, i borite se na Allahovu putu ulažući imetke svoje i živote svoje! To vam je, da znate, bolje!');
INSERT INTO "bs_darassalam" VALUES(9,42,'9:42','Da se radilo o plijenu nadohvat ruke i ne toliko dalekom pohodu, dvoličnjaci bi te slijedili, ali im je put izgledao dalek. I oni će se zaklinjati Allahom: “Da smo mogli, doista bismo s vama pošli” – i tako upropastiti sami sebe, a Allah zna da su oni lažljivci.');
INSERT INTO "bs_darassalam" VALUES(9,43,'9:43','Neka ti Allah oprosti što si dozvolio da izostanu, dok se nisi uvjerio koji od njih govore istinu, a koji lažu.');
INSERT INTO "bs_darassalam" VALUES(9,44,'9:44','Oni koji vjeruju u Allaha i u Ahiret neće od tebe tražiti dozvolu da se ne bore zalažući imetke svoje i živote svoje. A Allah dobro zna one koji su bogobojazni.');
INSERT INTO "bs_darassalam" VALUES(9,45,'9:45','Od tebe će tražiti dozvolu samo oni koji ne vjeruju u Allaha i u Ahiret i čija se srca kolebaju, pa sumnjaju i neodlučni su.');
INSERT INTO "bs_darassalam" VALUES(9,46,'9:46','Da su imali namjeru poći, sigurno bi za to pripremili ono što je potrebno, ali Allahu nije bilo po volji da idu, pa ih je zadržao. I bi im rečeno: “Sjedite s onima koji sjede!”');
INSERT INTO "bs_darassalam" VALUES(9,47,'9:47','Da su pošli s vama, bili bi vam samo na smetnji i brzo bi među vas smutnju ubacili, a među vama ima i onih koji ih rado slušaju. A Allah zna nasilnike.');
INSERT INTO "bs_darassalam" VALUES(9,48,'9:48','Oni su i prije smutnju priželjkivali i smicalice ti smišljali sve dok nije, uprkos njima, istina pobijedila i Allahova vjera zavladala.');
INSERT INTO "bs_darassalam" VALUES(9,49,'9:49','Ima ih koji govore: “Oslobodi me i ne dovedi me u iskušenje!” Eto, baš u iskušenje su pali! – A nevjernici sigurno neće umaći Džehennemu.');
INSERT INTO "bs_darassalam" VALUES(9,50,'9:50','Ako postigneš uspjeh, to ih ogorči; a kad te pogodi nesreća, oni govore: “Mi smo i ranije bili oprezni” – i odlaze veseli.');
INSERT INTO "bs_darassalam" VALUES(9,51,'9:51','Reci: “Dogodit će nam se samo ono što nam Allah odredi, On je Gospodar naš.” I neka se vjernici samo u Allaha pouzdaju!');
INSERT INTO "bs_darassalam" VALUES(9,52,'9:52','Reci: “Očekujete li za nas šta drugo već jedno od dva dobra? A mi očekujemo da vas Allah sām ili rukama našim kazni. Pa iščekujte, i mi ćemo s vama čekati.”');
INSERT INTO "bs_darassalam" VALUES(9,53,'9:53','Reci: “Trošili milom ili silom, od vas se neće primiti, jer vi ste opak narod.”');
INSERT INTO "bs_darassalam" VALUES(9,54,'9:54','A prilozi njihovi neće biti primljeni zato što u Allaha i u Njegova Poslanika ne vjeruju, što s predanošću molitve ne obavljaju i što samo preko volje udjeljuju.');
INSERT INTO "bs_darassalam" VALUES(9,55,'9:55','Neka te ne oduševljavaju bogatstva njihova, a ni djeca njihova! Allah hoće da ih njima kazni na ovom svijetu i da skončaju kao nevjernici.');
INSERT INTO "bs_darassalam" VALUES(9,56,'9:56','Oni se zaklinju Allahom da su doista vaši, a oni nisu vaši, nego su narod kukavički.');
INSERT INTO "bs_darassalam" VALUES(9,57,'9:57','Kad bi našli kakvo skrovište ili kakve pećine ili kakvu jamu, oni bi tamo trkom pohrlili.');
INSERT INTO "bs_darassalam" VALUES(9,58,'9:58','Ima ih koji ti prigovaraju zbog raspodjele zekata. Ako im se iz njega da, zadovolje se; a ako im se ne da, odjednom se razljute.');
INSERT INTO "bs_darassalam" VALUES(9,59,'9:59','A trebali bi se zadovoljiti onim što im daju Allah i Poslanik Njegov i kazati: “Dovoljan nam je Allah, Allah će nam dati iz obilja Svog, a i Poslanik Njegov, mi samo, za Allahom žudimo.”');
INSERT INTO "bs_darassalam" VALUES(9,60,'9:60','Zekat pripada samo siromasima i nevoljnicima, i onima koji ga skupljaju, i onima čija srca treba pridobiti, i za otkup iz ropstva, i prezaduženima, i u svrhe na Allahovu putu, i putniku. Allah je odredio tako! – A Allah sve zna i mudar je.');
INSERT INTO "bs_darassalam" VALUES(9,61,'9:61','Ima ih koji vrijeđaju Vjerovjesnika govoreći: “On vjeruje što god čuje!” Reci: “On čuje ono što je dobro, vjeruje u Allaha i ima vjere u vjernike, i milost je onima između vas koji vjeruju.” – A one koji Allahova Poslanika vrijeđaju čeka patnja nesnosna.');
INSERT INTO "bs_darassalam" VALUES(9,62,'9:62','Zaklinju vam se Allahom da biste bili zadovoljni, a preče bi im bilo da Allah i Njegov Poslanik budu zadovoljni, ako su vjernici.');
INSERT INTO "bs_darassalam" VALUES(9,63,'9:63','Zar oni ne znaju da onoga koji se suprotstavlja Allahu i Poslaniku Njegovu čeka vatra džehennemska, u kojoj će vječno ostati? To je poniženje veliko!');
INSERT INTO "bs_darassalam" VALUES(9,64,'9:64','Dvoličnjaci se plaše da se vjernicima ne objavi sura koja bi im otkrila ono što je u njihovim srcima. Reci: “Samo se vi rugajte, Allah će doista na vidjelo iznijeti ono čega se vi plašite.”');
INSERT INTO "bs_darassalam" VALUES(9,65,'9:65','A ako ih zapitaš, oni će sigurno reći: “Mi smo samo razgovarali i zabavljali se.” Reci: “Zar se niste Allahu i riječima Njegovim i Poslanikom Njegovim ismijavali?');
INSERT INTO "bs_darassalam" VALUES(9,66,'9:66','Ne ispričavajte se! Postali ste nevjernici, nakon što ste vjernici bili.” Ako nekima od vas i oprostimo, druge ćemo kazniti zato što su zločinci.');
INSERT INTO "bs_darassalam" VALUES(9,67,'9:67','Licemjeri i licemjerke slični su jedni drugima: traže da se čine nevaljala djela, a odvraćaju od dobrih, i ruke su im stisnute; zaboravljaju Allaha, pa je i On njih zaboravio. Munafici su, zaista, izašli van pokornosti Allahu.');
INSERT INTO "bs_darassalam" VALUES(9,68,'9:68','Licemjerima i licemjerkama i nevjernicima Allah prijeti džehennemskom vatrom, vječno će u njoj boraviti, dosta će im ona biti! Allah ih je prokleo, i njih čeka patnja neprekidna.');
INSERT INTO "bs_darassalam" VALUES(9,69,'9:69','Vi ste kao i oni prije vas! Oni su jači od vas bili i više su blaga i djece imali i slatkim su životom živjeli, a i vi slatkim životom živite isto onako kao što su oni prije vas živjeli; i vi se upuštate u nevaljalštine, kao što su se i oni upuštali. To su oni čija će djela biti poništena i na ovom i na onom svijetu – njima propast predstoji.');
INSERT INTO "bs_darassalam" VALUES(9,70,'9:70','Zar do njih nije doprla vijest o onima prije njih: o narodu Nuhovu, i o Adu, i o Semudu, i o narodu Ibrahimovu, i o stanovnicima Medjena, i o onima čija su naselja izvrnuta? Poslanici su im njihovi jasne dokaze donosili i Allah im nije učinio nikakvu nepravdu, nego su je oni sami sebi nanijeli.');
INSERT INTO "bs_darassalam" VALUES(9,71,'9:71','A vjernici i vjernice prijatelji su jedni drugima: traže da se čine dobra djela, a od nevaljalih odvraćaju, i molitvu obavljaju i zekat daju, i Allahu i Poslaniku Njegovu se pokoravaju. To su oni kojima će se Allah, sigurno, smilovati. – Allah je doista silan i mudar.');
INSERT INTO "bs_darassalam" VALUES(9,72,'9:72','Allah obećava vjernicima i vjernicama džennetske bašče, kroz rijeke teku, u kojima će vječno boraviti, i divne dvorove u vrtovima edenskim. A i malo naklonosti Allahove veće je od svega toga – to će, doista, uspjeh veliki biti!');
INSERT INTO "bs_darassalam" VALUES(9,73,'9:73','O Vjerovjesniče, bori se protiv nevjernikā i dvoličnjakā i budi prema njima strog! Prebivalište njihovo bit će Džehennem, a grozno je on boravište.');
INSERT INTO "bs_darassalam" VALUES(9,74,'9:74','Licemjeri se zaklinju Allahom da nisu govorili, a sigurno su govorili nevjerničke riječi i pokazivali da su nevjernici, nakon što su javno islam bili primili, i htjeli su učiniti ono što nisu uspjeli. A prigovaraju samo zato što su ih Allah, iz obilja Svoga, i Poslanik Njegov, imućnim učinili. Pa ako se pokaju, bit će im dobro, a ako se okrenu, Allah će ih na ovom i na drugom svijetu na bolne muke staviti, a na Zemlji ni zaštitnika ni pomagača neće imati.');
INSERT INTO "bs_darassalam" VALUES(9,75,'9:75','Ima ih koji su se obavezali Allahu: “Ako nam iz obilja Svog da, udjeljivat ćemo, zaista, milostinju i bit ćemo, doista, dobri!”');
INSERT INTO "bs_darassalam" VALUES(9,76,'9:76','A kad im je On dao iz obilja Svog, oni su u tome postali škrti i okrenuli se – a oni ionako glave okreću.');
INSERT INTO "bs_darassalam" VALUES(9,77,'9:77','I nadovezao im je On na to pritvornost u srcima njihovim sve do dana kada će pred Njega stati, zato što se onoga što su Allahu obećali ne pridržavaju i zato što stalno lažu.');
INSERT INTO "bs_darassalam" VALUES(9,78,'9:78','Zar oni ne znaju da Allah zna ono što oni u sebi kriju i ono o čemu se sašaptavaju i da je Allah Znalac svega skrivenog?');
INSERT INTO "bs_darassalam" VALUES(9,79,'9:79','One koji ogovaraju vjernike, koji dobrovoljno zekat i sadaku daju, a rugaju se i onima koji ga s mukom daju -Allah će poruzi izvrgnuti, i njih čeka patnja bolna.');
INSERT INTO "bs_darassalam" VALUES(9,80,'9:80','Molio ti oprosta za njih ili ne molio, molio čak i sedamdeset puta, Allah im neće oprostiti – zato što u Allaha i Njegova Poslanika ne vjeruju. – A Allah neće ukazati na Pravi put one koji neće da budu pokorni Allahu.');
INSERT INTO "bs_darassalam" VALUES(9,81,'9:81','Oni koji su izostali iza Allahova Poslanika veselili su se kod kuća svojih - mrsko im je bilo da se bore na Allahovu putu zalažući imetke svoje i živote svoje, i jedni drugima su govorili: "Ne krećite u boj po vrućini", reci : "Džehennemska vatra još je vrelija!" Samo da su oni shvatali!?');
INSERT INTO "bs_darassalam" VALUES(9,82,'9:82','Malo će se oni smijati, a dugo će plakati – bit će to kazna za ono što su radili.');
INSERT INTO "bs_darassalam" VALUES(9,83,'9:83','I ako te Allah ponovo vrati nekima od njih, pa te zamole da im dopustiš da pođu s tobom u boj, ti im reci: "Nikad sa mnom u boj nećete ići i nikada se sa mnom protiv neprijatelja nećete boriti! Bili ste zadovoljni što ste prvi put izostali, zato ostanite s onima koji ionako ne idu u boj."');
INSERT INTO "bs_darassalam" VALUES(9,84,'9:84','I nijednom od njih, kad umre, nemoj dženazu obaviti, i nemoj nad kaburom njegovim stajati. Oni u Allaha i Njegova Poslanika, zaista, nisu vjerovali i kao buntovnici oni umiru.');
INSERT INTO "bs_darassalam" VALUES(9,85,'9:85','Neka te ne ushićuju imeci njihovi i djeca njihova! Allah želi da ih njima na dunjaluku namuči i da im duše - dok su nevjernici - iziđu.');
INSERT INTO "bs_darassalam" VALUES(9,86,'9:86','A kad je objavljena sura da u Allaha vjerujete i da se zajedno s Poslanikom Njegovim borite, najimućniji od njih zatražili su odobrenje od tebe i rekli: “Ostavi nas da budemo s onima koji ne idu u boj!"');
INSERT INTO "bs_darassalam" VALUES(9,87,'9:87','Zadovoljavaju se da budu s onima koji ne idu u boj, srca njihova su zapečaćena, pa oni ne shvaćaju!');
INSERT INTO "bs_darassalam" VALUES(9,88,'9:88','Ali, Poslanik i oni koji s njime vjeruju bore se zalažući imetke svoje i živote svoje. – Njima će svako dobro pripasti i oni će ono što žele ostvariti.');
INSERT INTO "bs_darassalam" VALUES(9,89,'9:89','Allah im je pripremio džennetske bašče, kroz koje rijeke teku, u kojima će vječno boraviti. To je veliki uspjeh!');
INSERT INTO "bs_darassalam" VALUES(9,90,'9:90','Dolazili su i neki beduini koji su se izvinjavali i tražili dopuštenje da ne idu, i tako su izostali oni koji su Allahu i Njegovu Poslaniku lagali; a teška patnja pogodit će one među njima koji nisu vjerovali.');
INSERT INTO "bs_darassalam" VALUES(9,91,'9:91','Neće se ogriješiti nemoćni i bolesni, i oni koji ne mogu naći sredstva za borbu, samo ako su prema Allahu i Njegovu Poslaniku iskreni. Nema razloga da se išta prigovara onima koji čine dobra djela – a Allah prašta i samilostan je...');
INSERT INTO "bs_darassalam" VALUES(9,92,'9:92','ni onima kojima si rekao, kad su ti došli da im daš životinje za jahanje: “Ne mogu naći za vas životinje za jahanje” – pa su se vratili suznih očiju, tužni što ih ne mogu kupiti...');
INSERT INTO "bs_darassalam" VALUES(9,93,'9:93','a ima razloga da se prigovara onima koji od tebe traže dozvolu da izostanu, a imućni su. Zadovoljavaju se da ostanu sa onima koji ne idu u boj, Allah je njihova srca zapečatio, pa oni ne znaju.');
INSERT INTO "bs_darassalam" VALUES(9,94,'9:94','Oni će vam se pravdati kada se među njih vratite. Reci: "Nemojte se pravdati, mi vam nećemo vjerovati, jer nas je Allah o vama obavijestio. Allah i Njegov Poslanik vidjeće vaša djela, a zatim ćete vraćeni biti Onome koji nevidljivi i vidljivi svijet zna, pa će vas On obavijestiti o onome što ste radili."');
INSERT INTO "bs_darassalam" VALUES(9,95,'9:95','Zaklinjat će vam se Allahom, kada se njima vratite, samo da ih se okanite, pa ih se okanite. Oni su nečist i njihovo prebivalište bit će džehennem, kao kazna za ono što su radili.');
INSERT INTO "bs_darassalam" VALUES(9,96,'9:96','Oni vam se zaklinju zato da biste bili zadovoljni njima. Ako vi budete zadovoljni njima, Allah sigurno nije zadovoljan ljudima buntovnicima.');
INSERT INTO "bs_darassalam" VALUES(9,97,'9:97','Beduini su u nevjerovanju i licemjerju žešći, i najpriličnije je da ne poznaju propise koje Allah Svome Poslaniku objavljuje. A Allah je Onaj Koji sve zna i mudar je.');
INSERT INTO "bs_darassalam" VALUES(9,98,'9:98','Ima beduina koji ono što daju smatraju nametom i jedva čekaju da vas nesreće stignu – neka njih pogodi nesreća, a Allah je Onaj Koji sve zna i mudar je.');
INSERT INTO "bs_darassalam" VALUES(9,99,'9:99','A ima beduina koji vjeruju u Allaha i u ahiret i koji smatraju da je ono što daju put da se Allahu približe i da Poslanikove dove zasluže. To im je, zaista, dobro djelo. Allah će ih milošću Svojom obasuti, Allah je, zaista, Onaj Koji oprašta grijehe i milostiv je.');
INSERT INTO "bs_darassalam" VALUES(9,100,'9:100','Allah je zadovoljan prvim muslimanima, muhadžirima i ensarijama, i svima onima koji ih slijede dobra djela čineći. A i oni su zadovoljni Njime. Za njih je On pripremio džennetske bašče, kroz koje rijeke teku, i oni će vječno i zauvijek u njima boraviti. To je veliki uspjeh.');
INSERT INTO "bs_darassalam" VALUES(9,101,'9:101','Među beduinima oko vas ima licemjera, a ima ih i među stanovnicima Medine, koji su u licermjerstvu uporni – ti ih ne poznaješ, ali ih Mi poznajemo. Njih ćemo na dvostruke muke staviti, a zatim će biti u veliku patnju vraćeni.');
INSERT INTO "bs_darassalam" VALUES(9,102,'9:102','A ima i drugih koji su grijehe svoje priznali, i koji su djela dobra s drugim koja su loša izmiješali; njima će Allah oprostiti – Allah je, zaista, Onaj Koji oprašta grijehe i milostiv je.');
INSERT INTO "bs_darassalam" VALUES(9,103,'9:103','Uzmi od dobara njihovih zekat i sadaku, da ih njime očistiš i blagoslovljenim ih učiniš, i pomoli se za njih, molitva tvoja će ih, zaista, smiriti – a Allah je Onaj Koji sve čuje i sve zna.');
INSERT INTO "bs_darassalam" VALUES(9,104,'9:104','Zar ne znaju oni da jedino Allah prima pokajanje od robova Svojih i da samo On prihvata zekat i sadaku, i da je samo Allah stalni Primatelj pokajanja, Milostivi?');
INSERT INTO "bs_darassalam" VALUES(9,105,'9:105','l reci: "Trudite se! Allah će trud vaš vidjeti, a i Poslanik Njegov i vjernici, i vi ćete biti vraćeni Onome Koji zna ono što je čulima nedokučivo i ono što je pojavno, pa će vas o onome što ste radili obavijestiti."');
INSERT INTO "bs_darassalam" VALUES(9,106,'9:106','Ima i drugih koji su u neizvjesnosti da li će ih Allah kazniti ili će im oprostiti. A Allah je Onaj Koji sve zna i mudar je.');
INSERT INTO "bs_darassalam" VALUES(9,107,'9:107','A oni koji su džamiju sagradili da bi štetu nanijeli, i nevjerovanje osnažili, i razdor među vjernike unijeli, pripremajući je za onoga koji se protiv Allaha i Njegova Poslanika još prije borio, sigurno će se zaklinjati: "Mi smo samo najbolje željeli, a Allah je svjedok da su oni sigurno lažljivci."');
INSERT INTO "bs_darassalam" VALUES(9,108,'9:108','Ti u njoj nemoj nikada na namaz stati! Džamija čiji su temelji, već od prvog dana, postavljeni u strahu od Allaha, zaista više zaslužuje da u njoj na namaz staješ. U njoj su ljudi koji se vole često prati, a Allah voli one koji se mnogo čiste.');
INSERT INTO "bs_darassalam" VALUES(9,109,'9:109','Da li je bolji onaj koji je temelj zgrade svoje postavio u strahu od Allaha i u Njegovom zadovoljstvu, ili onaj koji je temelj zgrade svoje postavio na rub podlokane obale koja se nagnula, da se zajedno s njim u vatru džehennemsku sruši? A Allah neće uputiti na Pravi put zulumćare.');
INSERT INTO "bs_darassalam" VALUES(9,110,'9:110','Zgrada koju su oni sagradili stalno će unositi nemir u srca njihova, sve dok im srca ne popucaju – a Allah je Onaj Koji sve zna i mudar je.');
INSERT INTO "bs_darassalam" VALUES(9,111,'9:111','Allah je od vjernika kupio živote njihove i imetke njihove u zamjenu za džennet koji će im dati. Oni će se na Allahovu putu boriti, pa ubijati i ginuti. On je to, zbilja, obećao u Tevratu i Indžilu i Kur''anu. A ko odAllaha dosljednije ispunjava obećanje svoje? Zato se radujte pogodbi svojoj koju ste s Njim ugovorili, i to je zasigurno uspjeh veliki.');
INSERT INTO "bs_darassalam" VALUES(9,112,'9:112','Oni se kaju, i Njemu ibadet čine, i Njega hvale, i poste, i ruku i sedždu obavljaju, i traže da se čine dobra djela, a od nevaljalih odvraćaju i Allahovih se propisa pridržavaju. A vjernike obraduj!');
INSERT INTO "bs_darassalam" VALUES(9,113,'9:113','Vjerovjesniku i vjernicima nije dopušteno da mole oprost za višebošce, makar im bili i rod najbliži, kad im je jasno da će oni stanovnici u džehennemu biti.');
INSERT INTO "bs_darassalam" VALUES(9,114,'9:114','A što je Ibrahim tražio oprost za svoga oca, bilo je to samo zbog obećanja koje mu je dao. A čim mu je bilo jasno da je on Allahov neprijatelj, on ga se odrekao. Ibrahim je, doista, mnogo Allaha molio i obazriv bio.');
INSERT INTO "bs_darassalam" VALUES(9,115,'9:115','Allah neće učiniti da zaluta narod koji je na Pravi put uputio prije nego što im objasni ono čega treba da se čuvaju; Allah, zaista, sve dobro zna.');
INSERT INTO "bs_darassalam" VALUES(9,116,'9:116','Zaista je Allahova vlast na nebesima i na Zemlji, On život i smrt daje, i vi osim Allaha nemate ni zaštitnika ni pomagača.');
INSERT INTO "bs_darassalam" VALUES(9,117,'9:117','Allah je oprostio Vjerovjesniku, i muhadžirima i ensarijama, koji su ga u teškom času slijedili, u vrijeme kada se srca nekih od njih zamalo nisu pokolebala: On je poslije i njima oprostio. On je prema njima, zaista, milosrdan i milostiv.');
INSERT INTO "bs_darassalam" VALUES(9,118,'9:118','A i onoj trojici čiji je slučaj bio odgođen tako da im je zemlja, koliko god da je bila prostrana, postala tijesna, i bilo im se stisnulo u dušama njihovim, i vidjeli su da nema utočišta od Allaha nego samo u Njega. On je poslije i njima oprostio da bi se i ubuduće kajali, a Allah je, uistinu, stalni Primatelj pokajanja, Milostivi.');
INSERT INTO "bs_darassalam" VALUES(9,119,'9:119','O vi koji vjerujete, bojte se Allaha i budite s onima koji su iskreni!');
INSERT INTO "bs_darassalam" VALUES(9,120,'9:120','Nije trebalo da stanovnici Medine ni beduini u njihovoj blizini iza Allahova Poslanika izostanu i da svoj život njegovu životu pretpostave jer njih neće zadesiti ni žeđ, ni umor, ni glad na Allahovu putu, niti će stupiti na neko mjesto koje će nevjernike naljutiti, niti će ikakvu dobit nad neprijateljem ostvariti, a da im to sve neće kao dobro djelo upisano biti; Allah, zaista, neće dopustiti da propadne nagrada onima koji čine dobro.');
INSERT INTO "bs_darassalam" VALUES(9,121,'9:121','I neće dati nikakav prilog, ni mali ni veliki, niti će ikakvu dolinu prevaliti, a da im to neće zapisano biti, da bi ih Allah nagradio za ono dobro što su radili.');
INSERT INTO "bs_darassalam" VALUES(9,122,'9:122','Nije trebalo da svi vjernici odjednom idu u boj. Neka se po nekoliko njih iz svake zajednice potrudi da se upute u vjerske nauke i neka upozoravaju svoj narod kada im se vrate, da bi na oprezu bili.');
INSERT INTO "bs_darassalam" VALUES(9,123,'9:123','O vi koji vjerujete, borite se protiv nevjernika koji su u blizini vašoj i neka oni osjete vašu strogost, i znajte da je Allah s bogobojaznima.');
INSERT INTO "bs_darassalam" VALUES(9,124,'9:124','A kad bude objavljena neka sura, ima ih koji govore: "Kome je od vas ova sura povećala vjerovanje?" Što se tiče vjernika, njima je povećala vjerovanje i oni se raduju.');
INSERT INTO "bs_darassalam" VALUES(9,125,'9:125','A što se tiče onih čija su srca bolesna, ona im je sumnju na sumnju koju već imaju dodala i oni kao nevjernici umiru.');
INSERT INTO "bs_darassalam" VALUES(9,126,'9:126','Zar oni ne vide da svake godine jedanput ili dvaput u iskušenje padaju, pa opet, oni se ne kaju, niti se prisjećaju i pouku ne uzimaju!');
INSERT INTO "bs_darassalam" VALUES(9,127,'9:127','A kad bude objavljena koja sura, samo se zagledaju: "Da li vas ko vidi?", i onda se udaljuju. Allah je srca njihova skrenuo, zato što su oni ljudi koji ne shvataju.');
INSERT INTO "bs_darassalam" VALUES(9,128,'9:128','Došao vam je Poslanik, jedan od vas, teško mu je što ćete na muke udariti, jedva čeka da Pravim putem pođete, a prema vjernicima je milosrdan i milostiv.');
INSERT INTO "bs_darassalam" VALUES(9,129,'9:129','A ako oni glavu okrenu, ti reci: "Meni je dovoljan Allah, nema boga osim Njega; samo se u Njega uzdam, On je Gospodar Arša veličanstvenoga!"');
INSERT INTO "bs_darassalam" VALUES(10,1,'10:1','Elif-Lam-Ra. Ovo su ajeti Mudre Knjige.');
INSERT INTO "bs_darassalam" VALUES(10,2,'10:2','Zašto je čudno ljudima što Mi objavljujemo jednom između njih: "Opominji ljude! A one koji vjeruju obraduj da ih iskrena djela koja su uradili kod Gospodara njihova čekaju." Ali, nevjernici govore:"Ovaj je, zaista, očiti čarobnjak!"');
INSERT INTO "bs_darassalam" VALUES(10,3,'10:3','Gospodar vaš je Allah, Koji je nebesa i Zemlju za šest dana stvorio a onda se nad Aršom uzvisio i Koji sve određuje. Niko se neće moći zauzimati ni za koga bez dopuštenja Njegova. Eto, to vam je Allah, Gospodar vaš, pa Njemu ibadet činite! Zašto se ne prisjetite i pouku ne uzmete?');
INSERT INTO "bs_darassalam" VALUES(10,4,'10:4','Njemu ćete se svi vratiti – Allahovo je obećanje istinito; On, doista, započinje stvaranje – iz ničega stvara, On će poslije to ponoviti, da bi pravedno nagradio one koji budu vjerovali i dobra djela činili. A one koji ne budu vjerovali čeka piće od ključale vode i patnja bolna, zato što su nevjernici bili.');
INSERT INTO "bs_darassalam" VALUES(10,5,'10:5','On je Taj Koji je Sunce izvorom svjetlosti učinio a Mjesec sjajnim, i položaje mu odredio da biste znali broj godina i računanje. Allah je to s Istinom stvorio. On potanko izlaže ajete i znakove ljudima koji znaju.');
INSERT INTO "bs_darassalam" VALUES(10,6,'10:6','U smjeni noći i dana i u onom što je Allah na nebesima i na Zemlji stvorio zaista postoje znakovi za ljude koji se čuvaju.');
INSERT INTO "bs_darassalam" VALUES(10,7,'10:7','Zaista onima koji ne očekuju da će pred Nas stati i koji su zadovoljni životom na ovom svijetu, koji su u njemu smireni, i onima koji su prema ajetima i znakovima našim ravnodušni,');
INSERT INTO "bs_darassalam" VALUES(10,8,'10:8','njima prebivalište bit će Vatra, zbog onoga što su radili.');
INSERT INTO "bs_darassalam" VALUES(10,9,'10:9','One koji vjeruju i čine dobra djela Gospodar njihov će, zaista, zbog njihovog vjerovanja uputiti. Rijeke će ispod njih u džennetima uživanja teći.');
INSERT INTO "bs_darassalam" VALUES(10,10,'10:10','Molitva njihova bit će u njima: "Uzvišen neka si, Allahu", pozdrav njihov: "Selam", a zadnje što će u molitvi biti: "Hvala Allahu, Gospodaru svjetova."');
INSERT INTO "bs_darassalam" VALUES(10,11,'10:11','Da Allah ljudima daje zlo onako brzo kao što im se odaziva kad traže dobro, oni bi, uistinu, stradali. A Mi, ipak, ostavljamo da u osionosti svojoj lutaju oni koji ne vjeruju da će pred Nas stati.');
INSERT INTO "bs_darassalam" VALUES(10,12,'10:12','Kada čovjeka snađe nevolja, on Nam se moli ležeći, ili sjedeći, ili stojeći. A čim mu nevolju otklonimo, on nastavlja kao da Nam se nije ni obraćao molbom zbog nevolje koja ga je zadesila. Tako je onima koji pretjeruju uljepšano ono što radiše.');
INSERT INTO "bs_darassalam" VALUES(10,13,'10:13','Mi smo narode prije vas uništavali pošto bi zulum činili i poslanici njihovi im jasne dokaze donosili. Ali, oni ionako ne bi vjerovali. Tako Mi kažnjavamo ljude prestupnike.');
INSERT INTO "bs_darassalam" VALUES(10,14,'10:14','Zatim smo vas, poslije njih, nasljednicima na Zemlji učinili, da bismo vidjeli kako ćete postupati.');
INSERT INTO "bs_darassalam" VALUES(10,15,'10:15','A kad im se uče ajeti Naši jasni, onda govore oni koji ne vjeruju da će pred Nas stati: "Donesi ti kakav drugi Kur''an, ili ga izmijeni!" Reci: "Nije moje da ga ja sam od sebe mijenjam, ja slijedim samo ono što mi se objavljuje, ja se bojim, ako svome Gospodaru nepokoran budem, patnje na Velikom danu."');
INSERT INTO "bs_darassalam" VALUES(10,16,'10:16','Reci: "Da Allah nije htio, ja vam ga ne bih učio niti bi vas On njime podučio. Ja sam prije poslanstva dugo među vama boravio – zar ne razumijete?"');
INSERT INTO "bs_darassalam" VALUES(10,17,'10:17','Pa ima li onda većeg zulumćara od onoga koji o Allahu laži iznosi ili koji Njegove ajete i znakove smatra neistinitim? Prestupnici, doista, neće uspjeti!');
INSERT INTO "bs_darassalam" VALUES(10,18,'10:18','Oni, pored Allaha, obožavaju nešto što im ne može ni nauditi niti im može kakvu korist pribaviti, i govore: "Ovo su naši zagovornici kod Allaha." Reci: "Zar da Allaha obavještavate da na nebesima i na Zemlji postoji nešto što On ne zna da postoji?!" Uzvišen je On i vrlo visoko u odnosu na ono što Njemu u obožavanju pridružuju.');
INSERT INTO "bs_darassalam" VALUES(10,19,'10:19','Ljudi su bili samo jedna zajednica, a onda su se razdvojili. A da nije riječi ranije izrečene od Gospodara tvoga, ovima bi već bilo presuđeno o onome u čemu se razilaze.');
INSERT INTO "bs_darassalam" VALUES(10,20,'10:20','Oni govore: "Zašto mu Gospodar njegov ne pošalje kakav znak?" Ti reci: "Samo Allah zna ono što je čulima nedokučivo, pa pričekajte, i ja ću, zaista, s vama se u one što čekaju svrstati."');
INSERT INTO "bs_darassalam" VALUES(10,21,'10:21','A kada Mi dopustimo da osjete milost poslije nevolje koja ih snađe, oni opet u vezi s ajetima i znakovima Našim lukavstvo smišljaju. Reci: "Allah je u lukavstvu najbrži; izaslanici Naši, ono što vi spletkarite, doista zapisuju."');
INSERT INTO "bs_darassalam" VALUES(10,22,'10:22','On je Taj Koji vam omogućava da kopnom i morem putujete, pa kad ste u lađama, i kad one, uz blag povjetarac zaplove s putnicima, te se oni obraduju tome, naiđe silan vjetar i valovi navale na njih sa svih strana i oni se uvjere da će nastradati, iskreno se mole Allahu: "Ako nas iz ovoga izbaviš, sigurno ćemo u zahvalne spadati."');
INSERT INTO "bs_darassalam" VALUES(10,23,'10:23','A kad ih On izbavi, odmah potpuno neosnovano na Zemlji nasilje čine. O ljudi, vaše nasilje koje činite radi užitka u životu dunjalučkom samo vama šteti; Nama ćete se poslije vratiti i Mi ćemo vas o onom što ste radili obavijestiti.');
INSERT INTO "bs_darassalam" VALUES(10,24,'10:24','Život na dunjaluku sličan je bilju zemaljskom na koje Mi spustimo s neba vodu s kojim se ona izmiješa, kojim se onda hrane ljudi i stoka. Pa kad se Zemlja ukrasi svojim ruhom i okiti, i kad stanovnici njezini pomisle da oni time raspolažu, dođe odredba Naša, noću ili danju, i Mi to pokosimo kao da prije ničega nije ni bilo. Eto, tako Mi potanko izlažemo ajete i znakove ljudima koji razmišljaju.');
INSERT INTO "bs_darassalam" VALUES(10,25,'10:25','Allah poziva u Kuću spasa i upućuje na Pravi put onoga koga On hoće.');
INSERT INTO "bs_darassalam" VALUES(10,26,'10:26','Onima koji čine dobra djela pripada lijepa nagrada, a i više od toga. Lica njihova neće tama i potištenost prekrivati, oni će stanovnici dženneta biti, u njemu će vječno boraviti.');
INSERT INTO "bs_darassalam" VALUES(10,27,'10:27','A one koji počiniše loša djela čeka kazna srazmjerna onom što su počinili i njih će potištenost prekrivati. Nikoga neće imati da ih od Allaha zaštiti, lica će im biti tamna kao da su prekrivena dijelovima mrkle noći, stanovnici Vatre će oni biti, i u njoj će vječno boraviti.');
INSERT INTO "bs_darassalam" VALUES(10,28,'10:28','A na Dan kad ih sve sakupimo, reći ćemo onima koji su Allahu druge u obožavanju pridruživali: "Na mjestu ostanite i vi i božanstva vaša! " Pa ćemo ih razdvojiti, a božanstva njihova će reći: "Niste vi nas obožavali."');
INSERT INTO "bs_darassalam" VALUES(10,29,'10:29','Allah je dovoljan svjedok i nama i vama, mi, doista, nismo znali da ste nas obožavali.');
INSERT INTO "bs_darassalam" VALUES(10,30,'10:30','Tu će svako znati ono što je prije uradio – bit će vraćeni Allahu, svom istinskom Gospodaru, a neće im biti onih koje su izmišljali.');
INSERT INTO "bs_darassalam" VALUES(10,31,'10:31','Upitaj: "Ko vas opskrbljuje s neba i iz zemlje?", ili: "Ko vlada sluhom i vidom, ko izvodi živo iz neživog i iz živog neživo, i ko upravlja svim?" "Allah", reći će oni. A ti reci: "Pa zar se sačuvati nećete?!"');
INSERT INTO "bs_darassalam" VALUES(10,32,'10:32','To vam je Allah, Gospodar vaš istinski! Zar poslije istine ima išta osim zablude?! Pa kuda se onda odmećete?');
INSERT INTO "bs_darassalam" VALUES(10,33,'10:33','Tako se obistinila riječ Gospodara tvoga nad onima koji su buntovni, da vjerovati neće.');
INSERT INTO "bs_darassalam" VALUES(10,34,'10:34','Reci: "Ima li među vašim božanstvima ijedno koje prvobitno stvara, a zatim to ponovo učini?'''' Reci: "Allah prvobitno stvara, a zatim to ponavlja! Pa kuda se onda odmećete?"');
INSERT INTO "bs_darassalam" VALUES(10,35,'10:35','Reci: "Može li ijedno vaše božanstvo uputiti ka istini?" l odgovori: "Samo Allah upućuje ka istini! Pa, da li je onda dostojniji slijeđenja Onaj Koji ka istini upućuje ili onaj koji ni sam nije na Pravom putu, osim ako on upućen bude? Šta vam je, kako rasuđujete?"');
INSERT INTO "bs_darassalam" VALUES(10,36,'10:36','Većina njih slijedi samo pretpostavke, ali pretpostavke nimalo nisu od koristi istini. Allah uistinu dobro zna ono što oni rade.');
INSERT INTO "bs_darassalam" VALUES(10,37,'10:37','I nije bilo (moguće) za ovaj Kur''an da bude izmišljen mimo Allaha; međutim, potvrda je onog prije njega, i detaljno izlaganje Knjige u koju nema sumnje, od Gospodara svjetova.');
INSERT INTO "bs_darassalam" VALUES(10,38,'10:38','I zar oni govore: "On ga izmišlja!" Reci: "Pa dajte vi jednu suru kao što je njemu objavljena, i koga god hoćete, od onih u koje mimo Allaha vjerujete, u pomoć pozovite, ako istinu govorite."');
INSERT INTO "bs_darassalam" VALUES(10,39,'10:39','Štaviše, oni poriču prije nego temeljito saznaju šta ima u njemu, a još im nije došlo ni tumačenje njegovo. Tako su i oni prije njih poricali, pa pogledaj kako su zulumćari završili.');
INSERT INTO "bs_darassalam" VALUES(10,40,'10:40','Ima ih koji u njega vjeruju, a ima ih koji ne vjeruju u njega. A Gospodar tvoj najbolje poznaje smutljivce.');
INSERT INTO "bs_darassalam" VALUES(10,41,'10:41','I ako te oni budu u laž utjerivali, ti reci: "Meni moja, a vama vaša djela; vi nećete odgovarati za ono što ja radim, a ja neću odgovarati za ono što vi radite."');
INSERT INTO "bs_darassalam" VALUES(10,42,'10:42','Ima među njima onih koji te slušaju. A možeš li ti učiniti da te gluhi čuju, ako još i ne razumiju?!');
INSERT INTO "bs_darassalam" VALUES(10,43,'10:43','A ima među njima i onih koji te posmatraju, a možeš li ti uputiti na Pravi put slijepe, ako još i ne uviđaju?');
INSERT INTO "bs_darassalam" VALUES(10,44,'10:44','Allah, zaista, nikakav zulum ne čini ljudima, ljudi ga sami sebi čine.');
INSERT INTO "bs_darassalam" VALUES(10,45,'10:45','A na Dan kada ih On sakupi, učinit će im se da su boravili samo jedan kratki dio dana, i jedni druge će prepoznati. Izgubili su, zasigurno, oni koji su poricali da će pred Allaha stati i koji nisu Uputu slijedili!');
INSERT INTO "bs_darassalam" VALUES(10,46,'10:46','Bilo da ti pokažemo dio onoga što im prijeteći obećavamo, bilo da te usmrtimo, Nama će se sve vratiti, a povrh toga Allah je svjedok onoga što oni rade.');
INSERT INTO "bs_darassalam" VALUES(10,47,'10:47','Svaki je narod imao poslanika, i kad poslanik njihov dođe među njih, njima će biti pravedno presuđeno, zulum im neće biti učinjen.');
INSERT INTO "bs_darassalam" VALUES(10,48,'10:48','Oni govore: "Kada će već jednom to prijeteće obećanje, ako istinu govorite?"');
INSERT INTO "bs_darassalam" VALUES(10,49,'10:49','Reci: "Sam od sebe ne mogu nikakvu štetu otkloniti, a ni neku korist sebi pribaviti; biva onako kako Allah hoće!" Svaki narod ima rok određeni, a kad rok njegov dođe, ni za tren ga neće moći ni odložiti ni ubrzati.');
INSERT INTO "bs_darassalam" VALUES(10,50,'10:50','Reci: "Kažite vi meni: Ako će vas kazna Njegova noću ili danju zadesiti, zašto je onda požuruju zločinitelji?"');
INSERT INTO "bs_darassalam" VALUES(10,51,'10:51','Zar ćete tek onda, kad se dogodi, u nju povjerovati?! Zar tek tada, a ranije ste je požurivati?!');
INSERT INTO "bs_darassalam" VALUES(10,52,'10:52','Zatim će onima koji su zulum učinili rečeno biti: "Iskusite patnju vječnu; da li ste izloženi kazni osim prema onome što ste činili?!"');
INSERT INTO "bs_darassalam" VALUES(10,53,'10:53','Oni te zapitkuju: "Da li je istina da će to biti?" Reci: "Jest, Gospodara mi moga, zaista je istina, i vi je nećete moći spriječiti."');
INSERT INTO "bs_darassalam" VALUES(10,54,'10:54','Kad bi onaj ko je zulum činio imao sve ono što na Zemlji postoji, sve bi on to dao samo da se iskupi. A kada oni dožive patnju, sakrit će tugu, i bit će im po pravdi presuđeno i neće im zulum učinjen biti.');
INSERT INTO "bs_darassalam" VALUES(10,55,'10:55','Zasigurno, Allahovo je sve što je na nebesima i na Zemlji! Zasigurno, Allahovo obećanje je istinito, ali većina njih ne zna.');
INSERT INTO "bs_darassalam" VALUES(10,56,'10:56','On život i smrt daje i Njemu ćete se vratiti.');
INSERT INTO "bs_darassalam" VALUES(10,57,'10:57','O ljudi, već vam je stigla Opomena od Gospodara vašega, i lijek za vaša srca, i uputa i milost vjernicima.');
INSERT INTO "bs_darassalam" VALUES(10,58,'10:58','Reci: "Zbog Allahove blagodarnosti i milosti - eto, zbog toga neka se raduju; to je bolje od onoga što gomilaju."');
INSERT INTO "bs_darassalam" VALUES(10,59,'10:59','Reci: "Kažite vi meni zašto neku hranu koju vam Allah daje smatrate zabranjenom, a drugu dopuštenom?" Reci: "Da li vam je to Allah dozvolio ili o Allahu laži iznosite?!"');
INSERT INTO "bs_darassalam" VALUES(10,60,'10:60','l, šta misle oni koji o Allahu iznose laži, šta će na Sudnjem danu biti? Allah je, zaista, blagodaran prema ljudima, ali većina njih ne zahvaljuje.');
INSERT INTO "bs_darassalam" VALUES(10,61,'10:61','Što god ti činio, i što god iz Kur''ana učio i kakav god vi posao radili, Mi nad vama bdijemo dok god se time zanimate. Gospodaru tvome nije ništa skriveno ni na Zemlji ni na nebu, ni koliko trun jedan, i ne postoji ništa, ni manje ni veće od toga, što nije u Knjizi jasnoj.');
INSERT INTO "bs_darassalam" VALUES(10,62,'10:62','Zaista, za Allahove štićenike nema straha i oni ni za čim ne treba da tuguju,');
INSERT INTO "bs_darassalam" VALUES(10,63,'10:63','oni koji budu vjerovali i koji se budu Allaha bojali,');
INSERT INTO "bs_darassalam" VALUES(10,64,'10:64','za njih su dobre vijesti i na ovom i na drugom svijetu – Allahove riječi niko ne može izmijeniti – to je, zaista, uspjeh veliki.');
INSERT INTO "bs_darassalam" VALUES(10,65,'10:65','Neka te ne žaloste riječi njihove! Uistinu, sva moć pripada Allahu; On je Svečujući i Sveznajući.');
INSERT INTO "bs_darassalam" VALUES(10,66,'10:66','Zaista, u Allahovoj je vlasti sve što je na nebesima i na Zemlji. A oni koji se, pored Allaha, božanstvima mole, povode se samo za pretpostavkama i samo laži izmišljaju.');
INSERT INTO "bs_darassalam" VALUES(10,67,'10:67','On je Taj Koji vam je dao noć da u njoj počinak imate, a dan da vidite. U tome su, zaista, znakovi za ljude koji čuju.');
INSERT INTO "bs_darassalam" VALUES(10,68,'10:68','Neki govore: "Allah je Sebi uzeo dijete!" Uzvišen je On! On ni o kom ovisan nije! Sve što je na nebesima i na Zemlji Njegovo je! Vi za to nikakva dokaza nemate. Zašto o Allahu govorite ono što ne znate?');
INSERT INTO "bs_darassalam" VALUES(10,69,'10:69','Reci: "Oni koji o Allahu laži iznose neće uspjeti."');
INSERT INTO "bs_darassalam" VALUES(10,70,'10:70','Uživat će kratko na ovom svijetu, a zatim će se Nama vratiti i Mi ćemo dati da iskuse žestoku patnju zato što nisu vjerovali.');
INSERT INTO "bs_darassalam" VALUES(10,71,'10:71','Kaži im pripovijest o Nuhu! Kada on reče narodu svome: "O narode moj, ako vam je težak moj boravak među vama i moje podsjećanje na Allahova znamenja, pa ja se uzdam u Allaha, a vi se, zajedno sa božanstvima svojim, odlučite, i neka vam odluka vaša ne bude nejasna; zatim je nada mnom izvršite i ne odgađajte!');
INSERT INTO "bs_darassalam" VALUES(10,72,'10:72','A ako se okrenete, pa ja od vas nikakvu nagradu ne tražim, mene će Allah nagraditi, meni je naređeno da budem među muslimanima – Njemu predanima."');
INSERT INTO "bs_darassalam" VALUES(10,73,'10:73','Ali, nazvaše ga lašcem, pa Mi u lađi njega i one koji bijahu uz njega spasismo i nasljednicima ih učinismo a one koji dokaze naše poricaše potopismo, pa pogledaj kakav je bio završetak onih koji su upozoravani!');
INSERT INTO "bs_darassalam" VALUES(10,74,'10:74','Zatim smo, poslije njega, poslanike narodima njihovim slali i oni su im jasne dokaze donosili, ali oni nisu htjeli vjerovati u ono što prije nisu priznavali. Tako Mi pečatimo srca onih koji granice prelaze.');
INSERT INTO "bs_darassalam" VALUES(10,75,'10:75','Zatim smo, poslije njih, Musaa i Haruna poslali sa znacima Našim faraonu i glavešinama njegovim, ali su se oni uzoholili; bijahu ljudi prestupnici.');
INSERT INTO "bs_darassalam" VALUES(10,76,'10:76','A kada im je od Nas došla istina, rekli su: "Ovo je, zaista, prava čarolija!"');
INSERT INTO "bs_darassalam" VALUES(10,77,'10:77','"Zar za istinu koja vam je došla kažete da je čarolija?", reče Musa, "a čarobnjaci neće nikad uspjeti!"');
INSERT INTO "bs_darassalam" VALUES(10,78,'10:78','A oni rekoše: "Zar si došao da nas odvratiš od onoga na čemu smo zatekli pretke naše, da bi vas dvojica imali vlast na zemlji? E, nećemo mi vama dvojici vjerovati."');
INSERT INTO "bs_darassalam" VALUES(10,79,'10:79','I faraon reče: "Dovedite mi svakog čarobnjaka znana!"');
INSERT INTO "bs_darassalam" VALUES(10,80,'10:80','I kad čarobnjaci dođoše, Musa im reče: "Bacite što imate da bacite!"');
INSERT INTO "bs_darassalam" VALUES(10,81,'10:81','l kad oni baciše, Musa uzviknu: "Ono što ste priredili čarolija je! Allah će je zaista uništiti, Allah ne čini da djelo smutljivaca valjano bude."');
INSERT INTO "bs_darassalam" VALUES(10,82,'10:82','Allah će Svojim riječima istinu utvrditi, makar što će to zločincima krivo biti!');
INSERT INTO "bs_darassalam" VALUES(10,83,'10:83','I ne povjerova Musau niko, osim malo njih iz naroda faraonova, iz straha da ih faraon i glavešine njegove ne počnu zlostavljati – a faraon je, zaista, na Zemlji silnik bio i zaista je u one što svaku mjeru prevršavaju spadao.');
INSERT INTO "bs_darassalam" VALUES(10,84,'10:84','I Musa reče: "O narode moj, ako u Allaha vjerujete, u Njega se pouzdajte ako ste muslimani!"');
INSERT INTO "bs_darassalam" VALUES(10,85,'10:85','"U Allaha se uzdamo", rekoše oni."Gospodaru naš, ne učini nas iskušenjem za ljude koji zulum čine.');
INSERT INTO "bs_darassalam" VALUES(10,86,'10:86','l spasi nas milošću Svojom od naroda koji ne vjeruje!"');
INSERT INTO "bs_darassalam" VALUES(10,87,'10:87','I mi objavismo Musau i bratu njegovu: "U Misiru narodu svome kuće izgradite i bogomoljama ih učinite i u njima namaz klanjajte! A ti, obraduj vjernike!"');
INSERT INTO "bs_darassalam" VALUES(10,88,'10:88','I Musa reče: "Gospodaru naš! Ti si dao faraonu i glavešinama njegovim ukrase i imetke na ovom svijetu, da oni, Gospodaru moj, zavode s puta Tvoga! Gospodaru naš, uništi imetke njihove i zapečati srca njihova, pa neka ne vjeruju dok ne dožive patnju bolnu!"');
INSERT INTO "bs_darassalam" VALUES(10,89,'10:89','"Uslišena je molba vaša!", reče On, "a vas dvojica na Pravom putu ustrajte i nikako put neznalica ne slijedite!"');
INSERT INTO "bs_darassalam" VALUES(10,90,'10:90','I mi prevedosmo preko mora sinove Israilove, a za petama su im bili faraon i vojnici njegovi, progoneći ih iz zlobe i neprijateljstva. A on, kad se poče daviti, uzviknu: "Ja vjerujem da nema boga osim Onoga u Koga vjeruju sinovi lsrailovi i ja u muslimane, pokorne, spadam!"');
INSERT INTO "bs_darassalam" VALUES(10,91,'10:91','"Zar sada, a prije si buntovan bio i u one si što nered šire spadao?!"');
INSERT INTO "bs_darassalam" VALUES(10,92,'10:92','"Danas ćemo izbaviti samo tijelo tvoje, da bi bio poučan primjer onima poslije tebe, ali mnogi ljudi su prema Našim ajetima i znakovima ravnodušni."');
INSERT INTO "bs_darassalam" VALUES(10,93,'10:93','I Mi smo sinove Israilove u časni predjel naselili i lijepom hranom ih opskrbili, i tek kad im je došlo pravo znanje, oni su se u mišljenju razišli. A Gospodar tvoj će im, sigurno, na Kijametskom danu presuditi o onome u čemu su se razilazili.');
INSERT INTO "bs_darassalam" VALUES(10,94,'10:94','A ako sumnjaš u ono što ti objavljujemo, upitaj one koji čitaju Knjigu, prije tebe objavljenu. Tebi istina od Gospodara tvoga dolazi, i nikako ne budi od onih koji sumnjaju.');
INSERT INTO "bs_darassalam" VALUES(10,95,'10:95','I ne budi nikako od onih koji Allahove ajete i znakove poriču, pa da u gubitnike spadao ne bi.');
INSERT INTO "bs_darassalam" VALUES(10,96,'10:96','A oni za koje se ispuni Riječ Gospodara tvoga zaista neće vjerovati,');
INSERT INTO "bs_darassalam" VALUES(10,97,'10:97','makar im došli svi dokazi, sve dok ne dožive patnju bolnu.');
INSERT INTO "bs_darassalam" VALUES(10,98,'10:98','Zašto nije bilo nijednog naselja koje je povjerovalo i kome je vjerovanje njegovo koristilo, osim naroda Junusova; kada su povjerovali, od njih smo patnju poniženja u životu na ovom svijetu otklonili, i dali da još neko vrijeme uživaju?');
INSERT INTO "bs_darassalam" VALUES(10,99,'10:99','Da Gospodar tvoj hoće na Zemlji bi, baš svi, bili vjernici. Pa zašto onda ti da nagoniš ljude da budu vjernici?');
INSERT INTO "bs_darassalam" VALUES(10,100,'10:100','Nijedna osoba ne može vjerovati osim s Allahovom dozvolom; a On spusti propast na one koji ne razumiju.');
INSERT INTO "bs_darassalam" VALUES(10,101,'10:101','Reci: "Posmatrajte ono što je na nebesima i na Zemlji!", a ni od kakve koristi neće biti znakovi i upozorenja narodu koji ne vjeruje.');
INSERT INTO "bs_darassalam" VALUES(10,102,'10:102','Zar oni čekaju da ih snađe nešto slično onome što je snašlo one koji su prije njih bili i nestali?! Reci: "Pa čekajte, i ja ću s vama čekati!"');
INSERT INTO "bs_darassalam" VALUES(10,103,'10:103','Poslije smo spasavali poslanike Naše i one koji su vjerovali. Eto, tako je dužnost Naša da spasimo vjernike.');
INSERT INTO "bs_darassalam" VALUES(10,104,'10:104','Reci: "O ljudi, ako imate sumnje spram vjere moje – pa, ja neću obožavati one koje vi, mimo Allaha, obožavate, već ću ibadet činiti Allahu, Koji će vam duše uzeti. Meni je naređeno da u vjernike spadam.');
INSERT INTO "bs_darassalam" VALUES(10,105,'10:105','I naređeno mi je: "Predaj se pravoj vjeri iskreno, i nikako ne budi iz reda onih koji Allahu u obožavanju druge pridružuju,');
INSERT INTO "bs_darassalam" VALUES(10,106,'10:106','i, pored Allaha, ne moli se onome ko ti ne može ni koristiti ni nauditi, jer ako bi to uradio, uistinu, bi u zulumćare spadao."');
INSERT INTO "bs_darassalam" VALUES(10,107,'10:107','Ako ti Allah kakvu nevolju da, niko je osim Njega ne može otkloniti, a ako ti htjedne dobro – pa, niko ne može blagodat Njegovu spriječiti; On njome nagrađuje onoga koga hoće od robova Svojih; On je Oprostitelj grijeha, Milostivi.');
INSERT INTO "bs_darassalam" VALUES(10,108,'10:108','Reci: "O ljudi, istina vam je došla od Gospodara vašeg, i onaj ko se uputi Pravim putem – voju štetu, a ja nisam zadužen da vam Uputu dam."');
INSERT INTO "bs_darassalam" VALUES(10,109,'10:109','Ti slijedi ono što ti se objavljuje i budi strpljiv dok Allah ne presudi, On je sudija najbolji!');
INSERT INTO "bs_darassalam" VALUES(11,1,'11:1','Elif-Lam-Ra. Ovo je Knjiga čiji su ajeti usavršeni a potom razjašnjeni, od mudrog i Onog Koji sve u potpunosti zna,');
INSERT INTO "bs_darassalam" VALUES(11,2,'11:2','"da samo Allahu ibadet činite – ja sam vam od Njega upozoritelj i donositelj radosnih vijesti,');
INSERT INTO "bs_darassalam" VALUES(11,3,'11:3','da od Gospodara svoga oprosta tražite i da se pokajete, a On će vam dati da do roka koji je određen lijepo proživite i svakom darežljivom dat će iz Svoje darežljivosti. A ako se okrenete – pa, ja se, zaista, bojim za vas patnje na Velikom danu."');
INSERT INTO "bs_darassalam" VALUES(11,4,'11:4','Allahu ćete se vratiti, a On Svojom moći sve nadilazi!');
INSERT INTO "bs_darassalam" VALUES(11,5,'11:5','Eto, oni grudi svoje uvijaju da bi se od Njega sakrili. A i kad se u odjeću svoju zaogrću, On zna ono što skrivaju i ono što pokazuju – On, uistinu, zna šta se u grudima krije.');
INSERT INTO "bs_darassalam" VALUES(11,6,'11:6','Na Zemlji nema nijednog živog bića, a da ga Allah ne hrani i da ne zna gdje koje skončava i gdje se sklanja. Sve to ima u jasnoj Knjizi.');
INSERT INTO "bs_darassalam" VALUES(11,7,'11:7','On je Taj Koji je za šest dana nebesa i Zemlju stvorio - a Njegov je Arš iznad vode bio - da bi vas iskušao - koji će od vas bolje postupati. Ako ti rekneš: "Poslije smrti bit ćete doista oživljeni", nevjernici će sigurno reći: "Ovo nije ništa drugo do očita čarolija!"');
INSERT INTO "bs_darassalam" VALUES(11,8,'11:8','Ako im Mi patnju do određenog roka odgodimo, oni će sigurno reći: "Šta je zadržava?" A onoga dana kad im dođe, neće biti od njih otklonjena i sa svih strana bit će okruženi onim čemu su se rugali.');
INSERT INTO "bs_darassalam" VALUES(11,9,'11:9','Ako čovjeku damo da osjeti milost Našu, pa mu je poslije uskratimo, tada on, zaista, pravi očajnik i nezahvalnik postaje.');
INSERT INTO "bs_darassalam" VALUES(11,10,'11:10','A ako mu damo da nakon nevolje koja ga je zadesila blagostanje osjeti, on će sigurno reći: "Nevolje su me napustile!" On je, doista, odviše radostan i hvalisav.');
INSERT INTO "bs_darassalam" VALUES(11,11,'11:11','Osim onih koji su se strpili i dobra djela činili - njima pripada oprost i nagrada velika.');
INSERT INTO "bs_darassalam" VALUES(11,12,'11:12','Nemoj ti ništa od onoga što ti se objavljuje izostaviti, što tišti grudi tvoje, zato da oni ne bi rekli: "Zašto mu nije poslano kakvo blago, ili, zašto s njim nije došao melek?" Tvoje je da opominješ, a o svemu se samo Allah brine.');
INSERT INTO "bs_darassalam" VALUES(11,13,'11:13','Zar oni da govore: "On ga izmišlja!" Reci: "Pa sačinite vi deset Kur''anu sličnih, izmišljenih sura, i koga god hoćete, od onih u koje pored Allaha vjerujete, u pomoć pozovite, ako istinu govorite!"');
INSERT INTO "bs_darassalam" VALUES(11,14,'11:14','A ako vam se ne odazovu, onda znajte da se on objavljuje samo s Allahovim znanjem i da nema boga osim Njega - pa jeste li muslimani?!');
INSERT INTO "bs_darassalam" VALUES(11,15,'11:15','Onima koji streme životu na ovome svijetu i dunjalučkim ukrasima, Mi ćemo im to zbog njihovih djela - u potpunosti - i dati, i u tome zakinuti neće biti.');
INSERT INTO "bs_darassalam" VALUES(11,16,'11:16','To su oni koji će na Ahiretu samo vatru imati; tamo će propasti sve ono što su na Zemlji činili i bit će uzaludno sve što su radili.');
INSERT INTO "bs_darassalam" VALUES(11,17,'11:17','Jesu li oni kao oni kojima je jasno ko je Gospodar njihov - kojima je došao Kur''an - svjedok Njegov, a još prije njega i Knjiga Musaova, putovođa i milost?! To su oni koji vjeruju u njega. A ko god ga negira, među raznim grupacijama, Vatra će mu mjesto gdje će skončati biti. Zato, ti nikako ne sumnjaj u njega, on je zaista istina od Gospodara tvoga, ali većina ljudi ne vjeruje.');
INSERT INTO "bs_darassalam" VALUES(11,18,'11:18','A ima li većih zulumćara od onih koji o Allahu laž iznose?! Oni će pred Gospodara svoga biti dovedeni, a svjedoci će reći: "Ovi su izmišljali laži o Gospodaru svome!" I, Allahovo prokletstvo stiglo je zulumćare,');
INSERT INTO "bs_darassalam" VALUES(11,19,'11:19','koji su od Allahova puta odvraćali i nastojali da ga iskrive, i koji u Ahiret nisu vjerovali!"');
INSERT INTO "bs_darassalam" VALUES(11,20,'11:20','Oni na Zemlji ne mogu Allahu umaći, niti, osim Allaha, drugog zaštitnika imaju. Njihova patnja bit će umnogostručena. Oni nisu mogli čuti, i nisu vidjeli.');
INSERT INTO "bs_darassalam" VALUES(11,21,'11:21','To su oni koji su sami sebe upropastili - a neće biti ni traga onog što su izmišljali.');
INSERT INTO "bs_darassalam" VALUES(11,22,'11:22','oni će, nema sumnje, na onom svijetu biti izgubljeni.');
INSERT INTO "bs_darassalam" VALUES(11,23,'11:23','A oni koji budu vjerovali, Allahu pokorni i dobra djela činili, oni će stanovnici Dženneta biti; u njemu će vječno boraviti.');
INSERT INTO "bs_darassalam" VALUES(11,24,'11:24','Te dvije vrste su kao slijep i gluh i kao onaj koji vidi i čuje. A jesu li ta dvojica jednaki?! Pa zašto se ne prisjetite i pouku ne uzmete!"');
INSERT INTO "bs_darassalam" VALUES(11,25,'11:25','I Nuha poslasmo narodu njegovu: "Ja sam vam, uistinu, jasni upozoritelj,');
INSERT INTO "bs_darassalam" VALUES(11,26,'11:26','da samo Allahu u ibadetu budete; ja se, zaista, plašim za vas patnje na danu bolnome."');
INSERT INTO "bs_darassalam" VALUES(11,27,'11:27','Glavešine naroda njegova, oni koji nisu vjerovali, rekoše: "Vidimo da si i ti čovjek kao i mi i da te, bez ikakva razmišljanja, slijede samo oni koji su naš najniži sloj; vidimo da vi nikakve prednosti nad nama nemate. Štaviše, mislimo da ste lažljivci."');
INSERT INTO "bs_darassalam" VALUES(11,28,'11:28','"O narode moj", reče on, "recite mi, ako mi je Gospodar moj jasan dokaz dao i ako mi je On od Sebe milost - vjerovjesništvo dao, a vi to ne vidite, da li vas možemo prisiliti na to kad vam to po volji nije?!');
INSERT INTO "bs_darassalam" VALUES(11,29,'11:29','O narode moj! Za ovo od vas imetak ne tražim; samo od Allaha ja nagradu želim. I ja neću otjerati one koji vjeruju, oni će sigurno svoga Gospodara sresti; ali, ja vidim da ste vi neuk narod.');
INSERT INTO "bs_darassalam" VALUES(11,30,'11:30','O narode moj! Ko bi me od Allaha odbranio kad bih ih otjerao? Zašto se ne prisjetite?!');
INSERT INTO "bs_darassalam" VALUES(11,31,'11:31','Ja vam ne kažem: ''U mene su Allahove riznice'' - niti: ''Meni je poznato ono što je čulima nedokučivo'' - niti kažem: ''Ja sam melek'' - a ne govorim ni o onima koje vaše oči s prezirom gledaju: ''Allah im nikakvo dobro neće dati'', Allah najbolje zna šta je u dušama njihovim - a ja bih se tada, sigurno, među zulumćare svrstao."');
INSERT INTO "bs_darassalam" VALUES(11,32,'11:32','"O Nuh", rekoše oni, "ti si želio da se s nama raspravljaš i dugo si se raspravljao. Daj, neka se ostvari ono što nam prijeteći obećavaš, ako si jedan od iskrenih!"');
INSERT INTO "bs_darassalam" VALUES(11,33,'11:33','"To će vam učiniti samo Allah, ako bude htio", reče on, "i vi nećete moći umaći."');
INSERT INTO "bs_darassalam" VALUES(11,34,'11:34','"Neće vam savjet moj koristiti, ma koliko ja želio da vas savjetujem, ako Allah bude htio da vas u zabludu okrene. On je Gospodar vaš i Njemu ćete se vratiti."');
INSERT INTO "bs_darassalam" VALUES(11,35,'11:35','Zar da govore: "On ga izmišlja!" Reci: "Ako ga izmišljam, grijeh svoj ja ću nositi, a ja nemam ništa s vašim grijesima."');
INSERT INTO "bs_darassalam" VALUES(11,36,'11:36','I Nuhu bi objavljeno: "Iz naroda tvoga vjerovat će samo oni koji otprije vjeruju; zato se ne žalosti zbog onoga što oni stalno čine,');
INSERT INTO "bs_darassalam" VALUES(11,37,'11:37','I gradi lađu pred Našim očima, i po Našem uputstvu, i ne obraćaj Mi se više zbog onih koji su zulum činili – oni će sigurno biti potopljeni!"');
INSERT INTO "bs_darassalam" VALUES(11,38,'11:38','I on je gradio lađu. I kad god bi pored njega prolazile glavešine iz naroda njegova, rugali bi mu se. "Ako se vi rugate nama", govorio je on, "rugat ćemo se i mi vama, onako kako se vi rugate,');
INSERT INTO "bs_darassalam" VALUES(11,39,'11:39','i saznat ćete, zaista, kome će doći kazna koja će ga poniziti i koga će vječna patnja snaći."');
INSERT INTO "bs_darassalam" VALUES(11,40,'11:40','I kad je stigla odredba Naša i voda s površine zemlje pokuljala, Mi smo rekli: "Ukrcaj u lađu od svake životinjske vrste po jedan par, i porodicu svoju - osim onih o čijoj je kazni prethodila Riječ odredbe - i vjernike", a malo je bilo onih koji su s njim vjerovali.');
INSERT INTO "bs_darassalam" VALUES(11,41,'11:41','I on reče: "Ukrcajte se u nju, u ime Allaha, neka plovi i neka pristane! Gospodar moj uistinu je Oprostitelj grijeha, Milostivi."');
INSERT INTO "bs_darassalam" VALUES(11,42,'11:42','I ona ih je ponijela na valovima velikim kao brda; i Nuh zovnu sina svoga koji je bio izdvojen: "O sinko moj, ukrcaj se s nama, ne budi s nevjernicima!"');
INSERT INTO "bs_darassalam" VALUES(11,43,'11:43','A on reče: "Sklonit ću se na kakvo brdo koje će me od vode zaštititi. Od Allahove odredbe danas nema spasa, osim onome kome se On smilovao!", reče Nuh, i val ih razdvoji, i on potopljen bi.');
INSERT INTO "bs_darassalam" VALUES(11,44,'11:44','I bi rečeno: "O Zemljo, gutaj vodu svoju, a ti, o nebo, prestani!" I voda se povuče i ispuni se odredba, a lađa pristade na planini El-Džudijj i bi rečeno: "Daleko neka je narod koji zulum čini!"');
INSERT INTO "bs_darassalam" VALUES(11,45,'11:45','A Nuh se prije toga obrati Gospodaru svome i reče: "Gospodaru moj, sin moj je, uistinu, iz porodice moje, a obećanje Tvoje je, zaista, istinito i Ti si Sudija najpravedniji!"');
INSERT INTO "bs_darassalam" VALUES(11,46,'11:46','"O Nuh", odgovori mu, "on nije od onih iz porodice tvoje koje sam obećao spasiti; on, doista, radi ono što ne valja, zato Me ne moli za ono što ne znaš! Upozoravam te da ne budeš među neznalicama."');
INSERT INTO "bs_darassalam" VALUES(11,47,'11:47','"Gospodaru moj", reče, "Tebi se ja utječem da Te više nikada ne zamolim za ono što ne znam! Ako mi ne oprostiš i ne smiluješ mi se, sigurno ću biti među gubitnicima."');
INSERT INTO "bs_darassalam" VALUES(11,48,'11:48','"O Nuh", bi rečeno "iskrcaj se, sa selamom Našim i blagoslovima tebi i narodima koji će se izroditi od ovih koji su s tobom! Bit će naroda kojima ćemo davati da uživaju, a kojima ćemo Mi dati da osjete bolnu patnju!"');
INSERT INTO "bs_darassalam" VALUES(11,49,'11:49','Taj slučaj spada u vijesti koje su čulima nedokučive, koje ti Mi objavljujemo; ni ti ni narod tvoj niste prije ovoga o tome ništa znali. Zato budi strpljiv, ishod će, zaista, u korist bogobojaznih biti.');
INSERT INTO "bs_darassalam" VALUES(11,50,'11:50','A ''Adu njihova brata Huda poslasmo. On je govorio: "O narode moj, Allahu ibadet činite, vi drugog boga osim Njega nemate, a vi samo neistine tvrdite!"');
INSERT INTO "bs_darassalam" VALUES(11,51,'11:51','O narode moj, ja ne tražim od vas nagradu za ovo; nagradu želim samo od Onoga Koji me je stvorio! Zar ne razumijete?!');
INSERT INTO "bs_darassalam" VALUES(11,52,'11:52','O narode moj, molite Gospodara svoga da vam oprosti, i pokajte Mu se, a On će vam slati kišu obilnu i dat će vam još veću snagu, uz onu koju imate, i ne odlazite kao oni koji zlo čine!"');
INSERT INTO "bs_darassalam" VALUES(11,53,'11:53','"O Hude", govorili su oni, "nisi nam nikakav dokaz donio i mi na samu tvoju riječ nećemo napustiti božanstva svoja, mi tebi ne vjerujemo.');
INSERT INTO "bs_darassalam" VALUES(11,54,'11:54','Mi kažemo samo to da te je neko božanstvo naše zlom pogodilo." "Ja pozivam Allaha za svjedoka", reče on, "a i vi posvjedočite da ja nemam ništa s tim što vi u obožavanju pridružujete –');
INSERT INTO "bs_darassalam" VALUES(11,55,'11:55','umjesto Njega; i zato svi zajedno protiv mene lukavstvo smislite i nimalo mi vremena ne dajte,');
INSERT INTO "bs_darassalam" VALUES(11,56,'11:56','ja se uzdam u Allaha, u moga i vašeg Gospodara! Sve živo samo je Njemu potčinjeno; Gospodar moj je, zaista, na putu pravome."');
INSERT INTO "bs_darassalam" VALUES(11,57,'11:57','Pa ako se okrenete - a ja sam vam saopćio ono što vam je po meni poslano - Gospodar moj će umjesto vas drugi narod dati, i vi Mu nećete ničim nauditi; Gospodar moj zaista bdije nad svim."');
INSERT INTO "bs_darassalam" VALUES(11,58,'11:58','I kad je došla odredba Naša, Mi smo, milošću Našom, Huda i vjernike s njim spasili i patnje surove ih poštedjeli.');
INSERT INTO "bs_darassalam" VALUES(11,59,'11:59','Eto, to je bio narod ''Ad, oni su dokaze Gospodara svoga poricali, bili neposlušni poslanicima svojim, i slijedili su zapovijed svakog silnika, inadžije.');
INSERT INTO "bs_darassalam" VALUES(11,60,'11:60','I prokletstvo je na ovom svijetu stalno bilo s njim, a bit će i na Sudnjem danu. ''Ad, doista, nije vjerovao u Gospodara svoga; daleko neka je ''Ad, narod Hudov!');
INSERT INTO "bs_darassalam" VALUES(11,61,'11:61','I Semudu - brata njihova Saliha poslasmo. "O narode moj", govorio je on, "ibadet samo Allahu činite, vi drugog boga osim Njega nemate! On vas je od zemlje stvorio i dao vam da živite na njoj! Zato Ga molite da vam oprosti i pokajte Mu se! Gospodar moj je, zaista, blizu i odaziva se."');
INSERT INTO "bs_darassalam" VALUES(11,62,'11:62','"O Salih", govorili su oni, "ti si među nama prije ovoga cijenjen bio. Zašto braniš da obožavamo ono što su preci naši obožavali? Mi uveliko sumnjamo u ono čemu nas ti pozivaš."');
INSERT INTO "bs_darassalam" VALUES(11,63,'11:63','"O narode moj", govorio je on, "šta mislite: ako je meni jasno ko je Gospodar moj i ako mi je On sam milost - vjerovjesništvo dao, pa ko će me od Allaha odbraniti ako Mu ne budem poslušan? Vi možete samo uvećati propast moju.');
INSERT INTO "bs_darassalam" VALUES(11,64,'11:64','O narode moj, evo, ova Allahova kamila je znak za vas, pa je pustite neka pase po Allahovoj zemlji i ne činite joj nikakvo zlo, da vas ne bi zadesila kazna bliska."');
INSERT INTO "bs_darassalam" VALUES(11,65,'11:65','Ali, oni je zaklaše, pa on reče: "Uživajte u zemlji svojoj još samo tri dana, to nije lažna prijetnja."');
INSERT INTO "bs_darassalam" VALUES(11,66,'11:66','I kad je došla odredba Naša, Mi smo, milošću Našom, Saliha i vjernike s njim spasili od sramote toga dana - Gospodar tvoj je, uistinu, Moćni i Silni,');
INSERT INTO "bs_darassalam" VALUES(11,67,'11:67','a one koji su činili zulum pogodio je strašan glas i oni su u svojim domovima osvanuli mrtvi, nepomični,');
INSERT INTO "bs_darassalam" VALUES(11,68,'11:68','kao da na njoj nikad nisu ni živjeli. Narod Semud, doista, u Gospodara svoga nije vjerovao; daleko neka je Semud!');
INSERT INTO "bs_darassalam" VALUES(11,69,'11:69','I Ibrahimu su izaslanici Naši došli da mu donesu radosnu vijest. "Selam!", rekoše. "Selam!", odgovori on, i ubrzo im donese pečeno tele.');
INSERT INTO "bs_darassalam" VALUES(11,70,'11:70','A kad vidje da ga se ruke njihove ne dotiču, on shvati da su neobični i osjeti strah od njih. Ne boj se!, rekoše oni, mi smo i Lutovu narodu poslani.');
INSERT INTO "bs_darassalam" VALUES(11,71,'11:71','A žena njegova stajaše tu, nasmija se, i Mi je obradovasmo Ishakom, a poslije Ishaka Jakubom.');
INSERT INTO "bs_darassalam" VALUES(11,72,'11:72','"Jadna ja!", reče, "zar da rodim ovako stara, a i ovaj moj muž je star?! Ovo je zaista nešto neobično!"');
INSERT INTO "bs_darassalam" VALUES(11,73,'11:73','"Zar se čudiš Allahovoj odredbi?", rekoše oni, "Allahova milost i blagoslovi Njegovi neka su na vama, obitelji vjerovjesnička. Zaista je On hvaljen i slavljen!"');
INSERT INTO "bs_darassalam" VALUES(11,74,'11:74','I pošto Ibrahima minu strah i dođe mu radosna vijest, on poče raspravljati s Nama o narodu Lutovu;');
INSERT INTO "bs_darassalam" VALUES(11,75,'11:75','Ibrahim je, zaista, obazriv bio, Allahu je mnogo dovu činio i Njemu se vraćao.');
INSERT INTO "bs_darassalam" VALUES(11,76,'11:76','"O Ibrahime, prođi se toga, naređenje od Gospodara tvoga je stiglo; njih će stići patnja neizbježna!"');
INSERT INTO "bs_darassalam" VALUES(11,77,'11:77','I kada izaslanici Naši dođoše Lutu, on se zbog njih nađe u neprilici i to mu teško pade, pa reče: "Ovo je mučan dan!"');
INSERT INTO "bs_darassalam" VALUES(11,78,'11:78','I narod njegov pohrli njemu - a i prije su nevaljaštine činili. "O narode moj", reče on, "eto vam za brak mojih kćeri, one su vam čistije! Bojte se Allaha i pred gostima mojim ne sramotite me! Zar među vama nema razumna čovjeka?"');
INSERT INTO "bs_darassalam" VALUES(11,79,'11:79','"Tebi je svakako poznato da nam kćeri tvoje nisu potrebne", rekoše oni, "ti, doista, znaš šta mi hoćemo."');
INSERT INTO "bs_darassalam" VALUES(11,80,'11:80','"Kad bih samo imao kakvu moć nad vama", reče on, "ili da mogu kod zaštitnika snažnog utočište naći!"');
INSERT INTO "bs_darassalam" VALUES(11,81,'11:81','Odgovoriše: "O Lut, mi smo izaslanici Gospodara tvoga, oni tebi ne mogu nauditi. Ti kreni sa porodicom svojom u gluho doba noći bez žene svoje, nju će zadesiti isto što i njih, i neka se niko od vas ne obazire! Rok im je praskozorje, a zar praskozorje nije blizu?"');
INSERT INTO "bs_darassalam" VALUES(11,82,'11:82','I kada dođe naredba Naša, učinismo da, ono što je bilo gore - bi dolje, i na njih kišu grumenja od pečena blata spustismo, koje je neprekidno sipalo,');
INSERT INTO "bs_darassalam" VALUES(11,83,'11:83','bilježeno od Gospodara tvoga, a ono od zulumćara nije daleko.');
INSERT INTO "bs_darassalam" VALUES(11,84,'11:84','I Medjenu - brata njihova Šu''ajba poslasmo. "O narode moj", govorio je on, "Allahu ibadet činite, vi drugog boga osim Njega nemate, i nemojte iz posude za mjerenje i sa vage ništa umanjivati. Vidim da zaista u obilju živite i bojim se za vas kazne na Dan koji će sve skoliti');
INSERT INTO "bs_darassalam" VALUES(11,85,'11:85','O narode moj! Posudu za mjerenje i vagu pravedno ispunite, i ne zakidajte ljude ni u čemu što je njihovo, i ne činite zlo po zemlji nered praveći.');
INSERT INTO "bs_darassalam" VALUES(11,86,'11:86','Ono što vam pretekne, a što vam je Allah dozvolio, bolje vam je, ako hoćete biti vjernici; a ja nisam vaš čuvar."');
INSERT INTO "bs_darassalam" VALUES(11,87,'11:87','"O Šuajbe", govorili su oni, "da li molitva tvoja traži od tebe da napustimo ono što su preci naši obožavali ili da ne postupamo sa imanjima našim onako kako nam je volja? Ti si, uistinu, obazriv i razuman..."');
INSERT INTO "bs_darassalam" VALUES(11,88,'11:88','"O narode moj", govorio je on, "šta mislite, ako sam pri jasnom dokazu od Gospodara moga i On mi dao lijepu opskrbu?! Ja ne želim činiti ono što vama zabranjujem; jedino želim učiniti dobro koliko mogu, a uspjeh moj zavisi samo od Allaha; u Njega se uzdam i Njemu se obraćam.');
INSERT INTO "bs_darassalam" VALUES(11,89,'11:89','O narode moj! Neka vas neslaganje sa mnom nikako ne dovede do toga da vas zadesi ono što je zadesilo Nuhov narod, ili Hudov narod, ili Salihov narod. A i Lutov narod nije od vas daleko.');
INSERT INTO "bs_darassalam" VALUES(11,90,'11:90','I tražite oprost od Gospodara svoga, i onda Mu se pokajte! Gospodar je moj, uistinu, milostiv i Onaj Koji voli."');
INSERT INTO "bs_darassalam" VALUES(11,91,'11:91','"O Šuajbe", rekoše oni, "Mi ne shvatamo mnogo toga što ti govoriš, a vidimo da si ti među nama jadan; da nije rodbine tvoje, mi bismo te kamenovali, ti nad nama nemaš moć i ne uživaš nikakav ugled."');
INSERT INTO "bs_darassalam" VALUES(11,92,'11:92','"O narode moj", reče on, "zar rodbina moja kod vas ima veći ugled od Allaha, a Njega ste iza sebe odbacili?! Uistinu Gospodar moj ono što radite znanjem obuhvata.');
INSERT INTO "bs_darassalam" VALUES(11,93,'11:93','O narode moj, činite sve što možete, a činit ću i ja. Vi ćete sigurno saznati koga će kazna stići koja će ga osramotiti i ko je lažac. Pa, čekajte, i ja ću s vama čekati!"');
INSERT INTO "bs_darassalam" VALUES(11,94,'11:94','I kada je nastupila naredba Naša, Mi smo, iz milosti Naše, Šuajba i one koji su uz njega vjerovali spasili, a one koji su zulum činili pogodio je užasan glas i oni su u zemlji svojoj mrtvi, nepomični osvanuli,');
INSERT INTO "bs_darassalam" VALUES(11,95,'11:95','kao da na njoj nikad nisu ni postojali. Daleko bio Medjen, kao i Semud!');
INSERT INTO "bs_darassalam" VALUES(11,96,'11:96','I Musaa smo poslali sa znakovima Našim i dokazom jasnim,');
INSERT INTO "bs_darassalam" VALUES(11,97,'11:97','faraonu i glavešinama njegovim, ali su oni slijedili faraonovu praksu, a faraonova praksa bila je zabluda.');
INSERT INTO "bs_darassalam" VALUES(11,98,'11:98','Na Kijametskom danu on će svoj narod predvoditi i u Vatru ga uvesti, a užasno je mjesto u koje će doveden biti!');
INSERT INTO "bs_darassalam" VALUES(11,99,'11:99','Na ovom svijetu ih je pratilo prokletstvo a pratit će ih i na Kijametskom danu; strašan će biti dar kojim će darivani biti.');
INSERT INTO "bs_darassalam" VALUES(11,100,'11:100','To su neke vijesti koje ti o naseljima kazujemo; neka od njih još postoje, a neka su sa zemljom sravnjena.');
INSERT INTO "bs_darassalam" VALUES(11,101,'11:101','Mi im nismo zulum učinili. Oni su sami sebi zulum učinili. Ništa im nisu pomogla božanstva njihova koja su mimo Allaha molili, kad bi naredba Gospodara tvoga nastupila, samo bi im propast njihovu povećala.');
INSERT INTO "bs_darassalam" VALUES(11,102,'11:102','Eto, tako Gospodar tvoj kažnjava kad kažnjava naselja koja zulum čine. Njegova kazna zaista je bolna i žestoka.');
INSERT INTO "bs_darassalam" VALUES(11,103,'11:103','U tome je, zaista, pouka za one koji se plaše patnje na ahiretu; to je Dan kada će svi ljudi biti sabrani i to je Dan kad će svi biti prisutni,');
INSERT INTO "bs_darassalam" VALUES(11,104,'11:104','a Mi ga odgađamo samo za neko vrijeme.');
INSERT INTO "bs_darassalam" VALUES(11,105,'11:105','A onda kad dođe, bez dopuštenja Njegova niko ni riječ neće izustiti, a među njima bit će nesretnih i sretnih.');
INSERT INTO "bs_darassalam" VALUES(11,106,'11:106','I nesretni će u Vatru, u njoj će teško izdisati i udisati;');
INSERT INTO "bs_darassalam" VALUES(11,107,'11:107','U njoj će ostati dok je nebesa i Zemlje, osim ako drukčije Gospodar tvoj ne odredi. Gospodar tvoj, zaista, radi ono što želi.');
INSERT INTO "bs_darassalam" VALUES(11,108,'11:108','A sretni će u Džennet; u njemu će boraviti, dok je nebesa i Zemlje, osim ako drukčije Gospodar tvoj ne odredi; bit će to dar koji će neprekidno trajati.');
INSERT INTO "bs_darassalam" VALUES(11,109,'11:109','Zato ne sumnjaj da je lažno ono što oni obožavaju; oni obožavaju upravo ono što su, još prije, obožavali preci njihovi. Mi ćemo im, doista, ono što su zaslužili - bez odbitka dati!');
INSERT INTO "bs_darassalam" VALUES(11,110,'11:110','I Musau smo Knjigu dali, pa su se o njoj u mišljenju podvojili. I da nije riječi Gospodara tvoga ranije izrečene, bilo bi s njima svršeno, jer oni u nju sumnjaju mnogo.');
INSERT INTO "bs_darassalam" VALUES(11,111,'11:111','I svima njima će Gospodar tvoj prema djelima platiti, On u potpunosti zna ono što su radili.');
INSERT INTO "bs_darassalam" VALUES(11,112,'11:112','Budi ustrajan na Pravom putu, kao što ti je naređeno, i neka tako postupe i vjernici koji su uz tebe, i obijesni ne budite, On dobro vidi ono što radite.');
INSERT INTO "bs_darassalam" VALUES(11,113,'11:113','I ne naginjite onima koji zulum čine, pa da vas Vatra prži, da nemate drugih zaštitnika osim Allaha, i da zatim nemate pomoći!');
INSERT INTO "bs_darassalam" VALUES(11,114,'11:114','I uspostavljaj namaz početkom i krajem dana, i u prvim časovima noći! Dobra djela zaista poništavaju hrđava. To je opomena za one koji se opomene prisjećaju.');
INSERT INTO "bs_darassalam" VALUES(11,115,'11:115','I strpljiv budi! Allah, doista, neće uskratiti nagradu onima koji dobra djela čine.');
INSERT INTO "bs_darassalam" VALUES(11,116,'11:116','A da je barem među narodima prije vas bilo čestitih koji su branili da se na Zemlji nered čini, osim malo njih koje smo Mi spasili! A oni koji su zulum činili odavali su se onome u čemu su uživali, a prestupnici su bili.');
INSERT INTO "bs_darassalam" VALUES(11,117,'11:117','Gospodar tvoj ne bi nikada uništavao naselja zbog zuluma ako su stanovnici oni koji su dobri i popravljaju stanje.');
INSERT INTO "bs_darassalam" VALUES(11,118,'11:118','A da je Gospodar tvoj htio, sve bi ljude sljedbenicima jedne vjere učinio. Međutim, oni će se uvijek u vjerovanju razilaziti,');
INSERT INTO "bs_darassalam" VALUES(11,119,'11:119','osim onih kojima se Gospodar tvoj smiluje. A zato ih je i stvorio. I ispunit će se riječ Gospodara tvoga: "Napunit ću, zaista, Džehennem džinima i ljudima - zajedno!"');
INSERT INTO "bs_darassalam" VALUES(11,120,'11:120','A sve ove vijesti koje ti o pojedinim događajima o poslanicima kazujemo zato su da njima srce tvoje učvrstimo. I u ovima došla ti je prava istina, i pouka, i vjernicima opomena.');
INSERT INTO "bs_darassalam" VALUES(11,121,'11:121','I reci onima koji neće da vjeruju: "Radite što god možete, i mi ćemo raditi;');
INSERT INTO "bs_darassalam" VALUES(11,122,'11:122','i čekajte, i mi ćemo, sigurno, čekati!"');
INSERT INTO "bs_darassalam" VALUES(11,123,'11:123','Allah zna tajne nebesa i Zemlje i Njemu se sve vraća, zato samo Njemu ibadet čini i samo se u Njega uzdaj! A Gospodar tvoj nije nemaran spram onoga što radite.');
INSERT INTO "bs_darassalam" VALUES(12,1,'12:1','Elif-lam-ra. Ovo su ajeti Mudre Knjige.');
INSERT INTO "bs_darassalam" VALUES(12,2,'12:2','Objavljujemo je kao Kur''an na arapskom jeziku, da biste razumjeli.');
INSERT INTO "bs_darassalam" VALUES(12,3,'12:3','Mi tebi o najljepšim događajima kazujemo tako što ti ovaj Kur''an objavljujemo, iako si prije njega, jedan od neukih bio.');
INSERT INTO "bs_darassalam" VALUES(12,4,'12:4','Kad Jusuf reče svome ocu: "O oče moj, sanjao sam jedanaest zvijezda, i Sunce i Mjesec, i u snu sam ih vidio kako mi se pokloniše."');
INSERT INTO "bs_darassalam" VALUES(12,5,'12:5','On reče: "O sinko moj, ne kazuj svoj san braći svojoj, da ti ne učine kakvu spletku; šejtan je, doista, čovjeku neprijatelj otvoreni."');
INSERT INTO "bs_darassalam" VALUES(12,6,'12:6','I, eto tako, Gospodar tvoj će tebe odabrati, i tumačenju snova te naučiti, i blagodat Svoju tebi i Jakubovoj porodici upotpuniti, kao što je prije upotpunio precima tvojim, Ibrahimu i Ishaku, Gospodar tvoj je, zaista, Onaj Koji sve zna i mudar je.');
INSERT INTO "bs_darassalam" VALUES(12,7,'12:7','U Jusufu i braći njegovoj nalaze se pouke za sve koji se raspituju.');
INSERT INTO "bs_darassalam" VALUES(12,8,'12:8','Kada oni rekoše: "Jusuf i brat njegov draži su našem ocu od nas, a mi smo grupa. Naš otac je, zaista, u zabludi očitoj.');
INSERT INTO "bs_darassalam" VALUES(12,9,'12:9','Ubijte Jusufa ili ga u nekoj zabiti ostavite, otac vaš će se vama okrenuti, i poslije toga ćete dobri ljudi biti."');
INSERT INTO "bs_darassalam" VALUES(12,10,'12:10','Jedan od njih reče: "Ako baš hoćete nešto učiniti, onda Jusufa ne ubijte, već ga na dno nekog bunara bacite, uzet će ga kakav karavan."');
INSERT INTO "bs_darassalam" VALUES(12,11,'12:11','"O oče naš", rekoše oni, "zašto nam ne povjeriš Jusufa? Mi mu, zaista, želimo dobro.');
INSERT INTO "bs_darassalam" VALUES(12,12,'12:12','Pošalji ga sutra s nama da se zabavi i razonodi, mi ćemo ga, sigurno, čuvati."');
INSERT INTO "bs_darassalam" VALUES(12,13,'12:13','On odgovori: "Bit će mi, doista, žao ako ga odvedete, a plašim se da ga vuk ne pojede kad vi na njega ne budete pazili."');
INSERT INTO "bs_darassalam" VALUES(12,14,'12:14','"Ako bi ga vuk pojeo, a mi smo grupa!", rekoše oni, "bili bismo zaista gubitnici."');
INSERT INTO "bs_darassalam" VALUES(12,15,'12:15','I kada ga odvedoše i odlučiše da ga bace na dno bunara, Mi mu objavismo: "Ti ćeš ih o ovom njihovom postupku sigurno obavijestiti, a oni tebe neće ni prepoznati."');
INSERT INTO "bs_darassalam" VALUES(12,16,'12:16','I uvečer dođoše svom ocu plačući.');
INSERT INTO "bs_darassalam" VALUES(12,17,'12:17','"O oče naš", rekoše, "bili smo otišli da se nadmećemo, a Jusufa smo ostavili kod naših stvari, pa ga je vuk pojeo. A ti nam nećeš vjerovati iako istinu govorimo."');
INSERT INTO "bs_darassalam" VALUES(12,18,'12:18','I košulju njegovu lažnom krvlju okrvaviše. "U vašim dušama je ponikla zla misao", reče on, "a strpljivost je lijepa; od Allaha tražim pomoć u onome što vi govorite."');
INSERT INTO "bs_darassalam" VALUES(12,19,'12:19','I dođe jedan karavan, te poslaše vodonošu svoga i on spusti vedro svoje. "Gle sreće!", viknu on, "evo jednog dječaka!" I oni su ga kao trgovačku robu sakrili, a Allah dobro zna šta su činili.');
INSERT INTO "bs_darassalam" VALUES(12,20,'12:20','I prodadoše ga za malu nadoknadu, za nekoliko srebrenjaka; u vezi s njim bili su ravnodušni.');
INSERT INTO "bs_darassalam" VALUES(12,21,'12:21','I onda onaj iz Misira, koji ga je kupio, reče ženi svojoj: "Učini mu boravak prijatnim! Može nam koristan biti, a možemo ga i posiniti!" I eto tako Mi Jusufu dadosmo lijepo mjesto na Zemlji i naučismo ga tumačenju snova - a Allah čini šta hoće, ali većina ljudi ne zna.');
INSERT INTO "bs_darassalam" VALUES(12,22,'12:22','I kad on stasa, Mi mu podarismo mudrost i znanje; tako Mi nagrađujemo dobročinitelje.');
INSERT INTO "bs_darassalam" VALUES(12,23,'12:23','I poče ga na grijeh navoditi ona u čijoj je kući bio, pa zaključa sva vrata i reče: "Hodi!" "Allahovu zaštitu tražim!", uzviknu on, "gospodar me moj lijepo pazi; a zulumćari neće nikad uspjeti."');
INSERT INTO "bs_darassalam" VALUES(12,24,'12:24','I ona je bila poželjela njega, a i on bi nju poželio da od Gospodara svoga nije jasan znak ugledao - tako bi, da odvratimo od njega zlo i blud. Uistinu je on među Našim odabranim robovima.');
INSERT INTO "bs_darassalam" VALUES(12,25,'12:25','I njih dvoje prema vratima potrčaše - a ona razdera straga košulju njegovu - i muža njezina kraj vrata zatekoše. "Kakvu kaznu zaslužuje onaj koji je htio ženi tvojoj zlo učiniti", reče ona, "ako ne tamnicu ili kaznu bolnu?"');
INSERT INTO "bs_darassalam" VALUES(12,26,'12:26','"Ona je pokušala da me zavede", reče Jusuf. I posvjedoči jedan svjedok iz porodice njene: "Ako je košulja njegova sprijeda razderana, onda ona istinu govori, a on je među lažljivcima,');
INSERT INTO "bs_darassalam" VALUES(12,27,'12:27','a ako je košulja njegova straga razderana, onda ona laže, a on govori istinu."');
INSERT INTO "bs_darassalam" VALUES(12,28,'12:28','I kad on vidje da je košulja njegova straga razderana, reče: "To je jedno od vaših - ženskih lukavstava; vaša lukavstva su, zaista, velika!');
INSERT INTO "bs_darassalam" VALUES(12,29,'12:29','Ti, Jusufe, zaboravi i ne spominji ovo, a ti traži oproštenje za grijeh svoj; ti si, uistinu, jedna od grješnica bila!"');
INSERT INTO "bs_darassalam" VALUES(12,30,'12:30','I žene u gradu počeše govorkati: "Velikaševa supruga zavodi mladića, slugu svoga, u njega se ludo zaljubila! Mi mislimo da je ona u zabludi očitoj."');
INSERT INTO "bs_darassalam" VALUES(12,31,'12:31','I kad ona ču za ogovaranja njihova, posla po njih, te im naslone pripremi, dade svakoj od njih po nož i reče: "Izađi pred njih!" A kad ga one ugledaše, njegova ljepota ih zanese i po rukama svojim se porezaše: "Sačuvaj Allahu!", uskliknuše, "ovo nije čovjek, ovo je melek plemeniti!"');
INSERT INTO "bs_darassalam" VALUES(12,32,'12:32','"E, to vam je onaj zbog koga ste me korile", reče ona. "Istina je da sam ga zavodila, ali se on uščuvao. Ako ne učini ono što od njega tražim, sigurno će u zatvoru završiti i među poniženim biti."');
INSERT INTO "bs_darassalam" VALUES(12,33,'12:33','"Gospodaru moj", rekao je on, "draža mi je tamnica od ovoga na što me one navraćaju! I ako Ti ne odvratiš od mene lukavstva njihova, ja mogu prema njima naklonost osjetiti i jedan od lahkomislenih postati."');
INSERT INTO "bs_darassalam" VALUES(12,34,'12:34','I Gospodar mu njegov molbu usliša i spasi ga lukavstva njihova; On je, uistinu, Svečujući i Sveznajući.');
INSERT INTO "bs_darassalam" VALUES(12,35,'12:35','A potom, kada su vidjeli dokaze da je nedužan, doniješe odluku da ga za neko vrijeme smjeste u tamnicu.');
INSERT INTO "bs_darassalam" VALUES(12,36,'12:36','S njim su u tamnicu ušla još dva momka. "Ja sam sanjao da cijedim vino", reče jedan od njih. "A ja, opet", reče drugi, "kako na glavi nosim hljeb koji ptice kljuju. Protumači nam to, jer vidimo da si, zaista, jedan od dobročinitelja."');
INSERT INTO "bs_darassalam" VALUES(12,37,'12:37','"Ništa od onoga što jedete neće vam doneseno biti, a da vam ja prije ne kažem što ćete dobiti", reče Jusuf. "To je samo dio onoga čemu me naučio Gospodar moj, ja se klonim vjere naroda koji u Allaha i u Ahiret ne vjeruje,');
INSERT INTO "bs_darassalam" VALUES(12,38,'12:38','i slijedim vjeru predaka svojih, Ibrahima i Ishaka i Jakuba; nama ne priliči da ikoga Allahu u obožavanju pridružujemo. To je iz Allahove darežljivosti prema nama i ostalim ljudima, ali većina ljudi ne zahvaljuje."');
INSERT INTO "bs_darassalam" VALUES(12,39,'12:39','"O drugovi moji u tamnici, da li su raznorazna božanstva bolja ili Allah, Jedini i Potčinitelj svega?');
INSERT INTO "bs_darassalam" VALUES(12,40,'12:40','Oni koje, mimo Njega, obožavate - samo su imena koja ste im nadjenuli vi i preci vaši - Allah o njima nikakva dokaza nije objavio. Sud pripada jedino Allahu, a On je naredio da samo Njemu u ibadetu budete. To je jedino prava vjera, ali većina ljudi ne zna.');
INSERT INTO "bs_darassalam" VALUES(12,41,'12:41','O drugovi moji u tamnici, jedan će od vas gospodara svoga vinom pojiti, a drugi će raspet biti, pa će mu ptice glavu kljuvati. Ono što ste pitali već je određeno!"');
INSERT INTO "bs_darassalam" VALUES(12,42,'12:42','A onome od njih dvojice za kojeg je znao da će spasen biti reče: "Spomeni me gospodaru svome!", ali šejtan učini te on zaboravi da ga spomene gospodaru svome, i Jusuf ostade u tamnici nekoliko godina.');
INSERT INTO "bs_darassalam" VALUES(12,43,'12:43','I vladar reče: "Sanjao sam kako sedam mršavih krava pojede sedam debelih, i sanjao sam sedam klasova zelenih i sedam drugih, sasušenih. O velikaši, protumačite mi san moj, ako snove znate tumačiti?"');
INSERT INTO "bs_darassalam" VALUES(12,44,'12:44','"Zbrkanih li snova!", rekoše oni, "mi snove ne znamo tumačiti."');
INSERT INTO "bs_darassalam" VALUES(12,45,'12:45','I tada jedan od one dvojice koji se spasio, koji se poslije toliko vremena sjetio, reče: "Ja ću vam protumačiti san, samo me pošaljite!"');
INSERT INTO "bs_darassalam" VALUES(12,46,'12:46','"Jusufe, o ti koji si uvijek iskren, protumači nam šta znači: sedam mršavih krava pojede sedam debelih; i sedam klasova zelenih i sedam drugih sasušenih - pa da se vratim ljudima, da bi oni saznali."');
INSERT INTO "bs_darassalam" VALUES(12,47,'12:47','"Sijat ćete sedam godina uzastopno", reče, "pa ono što požanjete u klasu ostavite, osim ono malo što ćete jesti.');
INSERT INTO "bs_darassalam" VALUES(12,48,'12:48','Zatim će poslije doći sedam teških koje će pojesti ono što ste za njih pripremili, ostat će jedino ono malo što ćete za sjetvu sačuvati.');
INSERT INTO "bs_darassalam" VALUES(12,49,'12:49','"Zatim će, poslije toga, doći godina u kojoj će ljudima kiše u obilju biti i u kojoj će cijediti."');
INSERT INTO "bs_darassalam" VALUES(12,50,'12:50','I vladar reče: "Dovedite mi ga!" I kad Jusufu izaslanik dođe, on reče: "Vrati se gospodaru svome i upitaj ga: ''Šta je s onim ženama koje su svoje ruke porezale? Gospodar moj dobro zna spletke njihove!''"');
INSERT INTO "bs_darassalam" VALUES(12,51,'12:51','"Šta je s vama bilo kad ste Jusufa zavodile?", upita velikaš. "Sačuvaj Allahu!", rekoše one, "mi o njemu ništa ružno ne znamo!" "Sad će istina izaći na vidjelo", reče velikaševa žena, "ja sam njega zavodila, a on je, doista, jedan od iskrenih.');
INSERT INTO "bs_darassalam" VALUES(12,52,'12:52','To je zato da on zna da ga ja nisam tajno iznevjerila i da Allah ne da da se ostvare spletke izdajnika.');
INSERT INTO "bs_darassalam" VALUES(12,53,'12:53','Ja ne pravdam sebe, duša je zaista sklona zlu, osim one kojoj se Gospodar moj smiluje. Gospodar moj je, zaista, Onaj Koji oprašta grijehe i Koji je milostiv."');
INSERT INTO "bs_darassalam" VALUES(12,54,'12:54','I velikaš reče: "Dovedite mi ga, uzet ću ga u svoju svitu", i pošto porazgovara s njim, reče mu: "Ti ćeš od danas kod nas utjecajan i od povjerenja biti."');
INSERT INTO "bs_darassalam" VALUES(12,55,'12:55','"Postavi me", reče, "da vodim brigu o zalihama u zemlji, ja sam zaista čuvaran i znan."');
INSERT INTO "bs_darassalam" VALUES(12,56,'12:56','I tako Mi Jusufu dadosmo vlast u zemlji; boravio je ondje gdje je htio; milost svoju Mi dajemo onome kome hoćemo i ne uništavamo nagradu onima koji dobra djela čine.');
INSERT INTO "bs_darassalam" VALUES(12,57,'12:57','A nagrada na Ahiretu bolja je za one koji vjeruju i koji se Allaha boje i grijeha klone.');
INSERT INTO "bs_darassalam" VALUES(12,58,'12:58','I dođoše braća Jusufova i uđoše k njemu, pa ih on poznade, a oni njega ne poznadoše.');
INSERT INTO "bs_darassalam" VALUES(12,59,'12:59','I kad ih podmiri namirnicama i tovare im pripremi, reče: "Dovedite mi svoga brata po ocu, zar ne vidite da punu mjeru dajem i da najbolje ugošćujem."');
INSERT INTO "bs_darassalam" VALUES(12,60,'12:60','Ako mi ga ne dovedete, nećete više od mene mjeru s namirnicama dobiti i blizu mi ne dolazite!"');
INSERT INTO "bs_darassalam" VALUES(12,61,'12:61','"Pobrinut ćemo se da ga nekako od oca njegova izmamimo, zaista ćemo tako postupiti", rekoše oni.');
INSERT INTO "bs_darassalam" VALUES(12,62,'12:62','A Jusuf reče momcima svojim: "Stavite njihove stvari i namirnice u tovare njihove, oni će ih, kad se vrate svojima, prepoznati i opet će se vratiti."');
INSERT INTO "bs_darassalam" VALUES(12,63,'12:63','I pošto se vratiše ocu svome, rekoše: “O oče naš, uskraćeni ćemo za mjeru s namirnicama biti. Zato pošalji s nama brata našeg da bismo dobili mjeru s namirnicama, a mi ćemo ga, zaista, čuvati.”');
INSERT INTO "bs_darassalam" VALUES(12,64,'12:64','"Zar da vam ga povjerim kao što sam prije povjerio brata njegova?", reče on. "Ali, Allah je Čuvar najbolji i On je Najmilostiviji!"');
INSERT INTO "bs_darassalam" VALUES(12,65,'12:65','A kada otvoriše tovare svoje, nađoše da su im vraćene stvari njihove, i rekoše: "O oče naš, šta možemo više poželjeti? Evo, vraćene su nam stvari naše, i hranom ćemo porodicu našu namiriti, i brata našeg ćemo čuvati, a dobit ćemo i dodatni tovar koji kamila može ponijeti; to je za njega neznatan iznos."');
INSERT INTO "bs_darassalam" VALUES(12,66,'12:66','"Ja ga s vama neću poslati", reče, "dok se čvrsto Allahom ne zakunete da ćete mi ga doista vratiti, osim ako onemogućeni budete." I pošto mu se oni zakleše, on reče: "Allah je za ono što govorimo jamac!"');
INSERT INTO "bs_darassalam" VALUES(12,67,'12:67','I reče: "O sinovi moji, ne ulazite na jednu kapiju, već na razne kapije, a ja vas ne mogu spasiti od onoga što vam Allah odredi; vlast pripada jedino Njemu, ja se u Njega uzdam, i neka se samo u Njega uzdaju oni koji se uzdaju!"');
INSERT INTO "bs_darassalam" VALUES(12,68,'12:68','I kad uđoše onako kako im je otac njihov naredio, to im nimalo nije pomoglo da budu pošteđeni onoga što im je Allah bioodredio, jedino se ostvarila želja Jakubova, koju je izvršio, a on je, uistinu, znanje imao, zato što smo ga Mi naučili, ali većina ljudi ne zna.');
INSERT INTO "bs_darassalam" VALUES(12,69,'12:69','I kad dođoše kod Jusufa, on privi na grudi brata svoga i reče: "Ja sam, doista, brat tvoj i ne žalosti se zbog onog što su oni činili."');
INSERT INTO "bs_darassalam" VALUES(12,70,'12:70','I pošto ih namiri potrebnom hranom, stavi pehar u tovar brata svoga, a poslije jedan glasnik stade vikati: "Karavano, vi ste, doista, kradljivci!"');
INSERT INTO "bs_darassalam" VALUES(12,71,'12:71','Oni im pristupiše i upitaše: "Šta ste to izgubili?"');
INSERT INTO "bs_darassalam" VALUES(12,72,'12:72','"Izgubili smo vladarevu posudu", odgovoriše. "Ko je donese, dobit će tovar hrane koji kamila može ponijeti. Ja za to garantujem."');
INSERT INTO "bs_darassalam" VALUES(12,73,'12:73','"Allaha nam", rekoše oni, "vi znate da mi nismo došli da činimo nered u zemlji, i mi nismo kradljivci."');
INSERT INTO "bs_darassalam" VALUES(12,74,'12:74','"A kakva je kazna lopovu ako budete lagali?", upitaše.');
INSERT INTO "bs_darassalam" VALUES(12,75,'12:75','“Kazna je onome u čijem se tovaru nađe – sam on.”, odgovoriše. “Eto, tako mi kažnjavamo zulumćare.”');
INSERT INTO "bs_darassalam" VALUES(12,76,'12:76','I on poče s vrećama njihovim, prije vreće brata svoga, a onda izvadi pehar iz vreće brata svoga. Tako smo, eto, u Jusufovu korist varku napravili. On, po vladarevu zakonu, nije mogao uzeti kao roba brata svoga, izuzev da to Allah hoće. Mi uzdižemo na veće položaje onoga koga hoćemo. Sve znaniji od znanijeg ima, a Sveznajući je iznad svakog.');
INSERT INTO "bs_darassalam" VALUES(12,77,'12:77','"Ako je on ukrao", rekoše oni, "pa i prije je brat njegov krao!" I Jusuf to u sebi zataji i to im ne otkri. "Vi ste u gorem položaju", pomisli u sebi, "Allah najbolje zna ono što vi spominjete."');
INSERT INTO "bs_darassalam" VALUES(12,78,'12:78','"O velikašu", rekoše oni, "on ima vrlo stara oca, pa uzmi jednog od nas umjesto njega! Mi uistinu vidimo da si jedan od dobročinitelja."');
INSERT INTO "bs_darassalam" VALUES(12,79,'12:79','"Sačuvaj Allahu", reče, "da uzmemo nekog drugog do onoga kod koga smo našli ono što je naše! Tada bismo zaista zulumćari bili!"');
INSERT INTO "bs_darassalam" VALUES(12,80,'12:80','I pošto u njemu izgubiše svaku nadu da će brata spasiti, odvojiše se ustranu da se posavjetuju. "Zar ne znate", reče najstariji među njima, "da ste se svome ocu Allahom zakleli, a i da ste prije toga u vezi s Jusufom propust učinili?! Neću napustiti ovu zemlju dok mi to moj otac ne dozvoli ili dok Allah u moju korist ne presudi, a On je Sudija najbolji."');
INSERT INTO "bs_darassalam" VALUES(12,81,'12:81','"Vratite se svom ocu pa recite: ''O oče naš, sin tvoj je ukrao; mi tvrdimo samo ono što smo vidjeli, a mi nismo mogli neviđeno znati.''"');
INSERT INTO "bs_darassalam" VALUES(12,82,'12:82','"Pitaj naselje u kome smo boravili i karavanu s kojom smo došli. Mi, zaista, govorimo istinu!"');
INSERT INTO "bs_darassalam" VALUES(12,83,'12:83','"Nije tako", reče Jakub, "u dušama vašim ponikla je zla misao, a lijepa je strpljivost. Nadam se da će mi ih Allah sve vratiti; uistinu je On Sveznajući i Mudri."');
INSERT INTO "bs_darassalam" VALUES(12,84,'12:84','I okrenu se od njih i reče: "O Jusufe, tugo moja!", i oči mu pobijelješe od jada, bio je vrlo potišten.');
INSERT INTO "bs_darassalam" VALUES(12,85,'12:85','"Allaha nam", rekoše oni, "ti toliko spominješ Jusufa da ćeš teško oboljeti ili umrijeti!"');
INSERT INTO "bs_darassalam" VALUES(12,86,'12:86','"Ja tugu svoju i jad svoj pred Allaha iznosim, a Allah mi je dao da znam ono što vi ne znate", reče on.');
INSERT INTO "bs_darassalam" VALUES(12,87,'12:87','"O sinovi moji, idite i raspitajte se za Jusufa i brata njegova, i ne gubite nadu u milost Allahovu; doista, nadu u Allahovu milost gube samo ljudi koji su nevjernici."');
INSERT INTO "bs_darassalam" VALUES(12,88,'12:88','I kad oni uđoše kod njega, rekoše: "O velikašu, i nas i porodicu našu pritisla je nevolja; donijeli smo malo vrijedne stvari, ali ti nam podaj punu mjeru i udijeli nam milostinju, jer Allah, doista, nagrađuje one koji milostinju udjeljuju."');
INSERT INTO "bs_darassalam" VALUES(12,89,'12:89','"A znate li", upita on, "šta ste uradili s Jusufom i bratom njegovim kada ste lahkomisleni bili?"');
INSERT INTO "bs_darassalam" VALUES(12,90,'12:90','"A da ti nisi, uistinu, Jusuf?", povikaše oni. "Da, ja sam Jusuf, a ovo je brat moj, Allah nam je milost darovao; a ko god se uščuva i strpljiv bude bio - pa, Allah, uistinu, neće dopustiti da propadne nagrada onima koji dobra djela čine."');
INSERT INTO "bs_darassalam" VALUES(12,91,'12:91','"Allaha nam", rekoše oni, "Allah ti je nad nama prednost dao, mi smo, zaista, griješili!"');
INSERT INTO "bs_darassalam" VALUES(12,92,'12:92','"Danas vam neće biti ukora",reče, "Allah će vam oprostiti, On je najmilostiviji!"');
INSERT INTO "bs_darassalam" VALUES(12,93,'12:93','"Ponesite ovu košulju moju i bacite je na lice moga oca; on će progledati; i svu porodicu svoju mi dovedite!"');
INSERT INTO "bs_darassalam" VALUES(12,94,'12:94','I kad karavan napusti Misir, otac njihov reče: "Ja zbilja osjećam miris Jusufov, samo ne recite da sam omatuhio."');
INSERT INTO "bs_darassalam" VALUES(12,95,'12:95','"Allaha nam", rekoše oni, "doista si ti još uvijek u prijašnjoj zabludi."');
INSERT INTO "bs_darassalam" VALUES(12,96,'12:96','A kad glasonoša s radosnom vijesti dođe, on stavi košulju na lice njegovo i on progleda. "Zar vam ne rekoh", reče, "da ja znam od Allaha ono što vi ne znate."');
INSERT INTO "bs_darassalam" VALUES(12,97,'12:97','"O oče naš", rekoše oni, "zamoli za oprost grijeha naših, mi smo, zaista, griješili."');
INSERT INTO "bs_darassalam" VALUES(12,98,'12:98','"Zamolit ću Gospodara svoga da vam oprosti", odgovori on, "jer On je Oprostitelj grijeha, Milostivi."');
INSERT INTO "bs_darassalam" VALUES(12,99,'12:99','I kad dođoše kod Jusufa, on privi roditelje svoje na grudi i reče: "Nastanite se u Misiru, od svega, ako Allah da, sigurni!"');
INSERT INTO "bs_darassalam" VALUES(12,100,'12:100','I on roditelje svoje postavi na prijesto i svi mu se nakloniše, pa on reče: "O oče moj, ovo je ostvarenje moga sna nekadašnjeg. Gospodar moj ga je istinitim učinio. On je bio dobar prema meni kad me je iz tamnice izbavio i vas iz pustinje doveo, nakon što je šejtan između mene i braće moje bio razdor posijao. Gospodar moj je, zaista, dobrostiv onome kome On hoće, i On je, zaista, Sveznajući i Mudri!');
INSERT INTO "bs_darassalam" VALUES(12,101,'12:101','Gospodaru moj, Ti si mi dao dio vlasti i naučio me tumačenju snova! O Ti Koji si nebesa i Zemlju bez prethodnog primjera stvorio, Ti si Zaštitnik moj i na dunjaluku i na ahiretu; daj da umrem kao musliman i pridruži me čestitima!"');
INSERT INTO "bs_darassalam" VALUES(12,102,'12:102','Eto, to su neke vijesti iz onoga što je čulima nedokučivo i koje tebi objavljujemo, a ti nisi bio s njima kada su se oni odlučili, i kada su spletke smislili.');
INSERT INTO "bs_darassalam" VALUES(12,103,'12:103','A većina ljudi, ma koliko ti nastojao, nisu vjernici.');
INSERT INTO "bs_darassalam" VALUES(12,104,'12:104','Ti od njih ne tražiš nagradu za to, to je samo opomena svim svjetovima.');
INSERT INTO "bs_darassalam" VALUES(12,105,'12:105','A koliko ima znakova na nebesima i na Zemlji pored kojih prolaze, i tom prilikom se od njih okreću!');
INSERT INTO "bs_darassalam" VALUES(12,106,'12:106','Većina njih ne vjeruje u Allaha, osim tako što Mu druge pridružuju.');
INSERT INTO "bs_darassalam" VALUES(12,107,'12:107','Zar mogu biti sigurni da ih nevolja, kao Allahova kazna, neće stići ili da ih čas suđeni neće iznenaditi, a da oni to neće ni primijetiti?');
INSERT INTO "bs_darassalam" VALUES(12,108,'12:108','Reci: "Ovo je put moj, ja pozivam k Allahu, imajući jasne dokaze, ja, i svaki onaj koji me slijedi, i neka je Uzvišen Allah, ja nisam od onih koji Mu druge pridružuju."');
INSERT INTO "bs_darassalam" VALUES(12,109,'12:109','A Mi smo i prije tebe samo muškarce slali, koji su iz naseljenih mjesta bili i kojima smo objave dostavljali. Zašto ovi ne putuju po svijetu, pa da vide kako su skončali oni prije njih - a ahiret je zaista bolji za one koji se uščuvaju - zar ne razumijete?''');
INSERT INTO "bs_darassalam" VALUES(12,110,'12:110','I kad bi poslanici gotovo nadu gubili i pomišljali da će ih za lašce proglasiti, pomoć Naša bi im došla. Spasili bismo one koje smo Mi htjeli, a kazna Naša ne bi mimoišla ljude prestupnike.');
INSERT INTO "bs_darassalam" VALUES(12,111,'12:111','U kazivanju o njima pouka je za one koji su razumom obdareni. Kur''an nije izmišljena besjeda, on priznaje knjige prije njega objavljene, i objašnjava sve, i uputa je i milost narodu koji vjeruje.');
INSERT INTO "bs_darassalam" VALUES(13,1,'13:1','Elif-lam-mim-ra; Ovo su ajeti Knjige! A ono što ti se objavljuje od Gospodara tvoga istina je, ali većina ljudi ne vjeruje.');
INSERT INTO "bs_darassalam" VALUES(13,2,'13:2','Allah je nebesa, bez stupova koje vidite podigao, i onda se nad Aršom uzvisio, i Sunce i Mjesec potčinio; svako se kreće do roka određenog. On upravlja svim i potanko izlaže dokaze kako biste u susret s Gospodarom svojim čvrsto bili uvjereni.');
INSERT INTO "bs_darassalam" VALUES(13,3,'13:3','I On je Taj Koji je Zemlju rasprostro i na njoj nepomične planine i rijeke stvorio i od svakog ploda po par, muško i žensko dao; On dan zastire noću. U tome, doista, ima dokaza za ljude koji razmišljaju.');
INSERT INTO "bs_darassalam" VALUES(13,4,'13:4','Na Zemlji ima predjela koji jedni s drugima graniče, i bašča s grožđem, i njiva, i palmi; sa više izdanaka i samo s jednim, i jednom te istom vodom se napajaju; ipak plod nekih činimo ukusnijim od drugih. U tome, doista, ima znakova za ljude koji razumiju.');
INSERT INTO "bs_darassalam" VALUES(13,5,'13:5','A ako se čudiš, pa - čudi se riječima njihovim: "Zar ćemo kad prah budemo, zaista ponovo biti stvoreni?" To su oni što ne vjeruju Gospodara svoga. To su oni na čijim vratovima će biti sindžiri. To su stanovnici Vatre; u njoj će vječno ostati.');
INSERT INTO "bs_darassalam" VALUES(13,6,'13:6','Oni te požuruju sa zlom, prije nego s dobrim, a bilo je kazni i prije njih. Gospodar tvoj ljudima prašta i uprkos zulumu njihovom, ali Gospodar tvoj, doista, i kažnjava žestoko.');
INSERT INTO "bs_darassalam" VALUES(13,7,'13:7','A oni koji ne vjeruju govore: "Zašto mu Gospodar njegov ne pošalje kakav znak?" Ti si, međutim, samo opominjač, a svaki je narod imao onoga koji ga je na Pravi put usmjeravao.');
INSERT INTO "bs_darassalam" VALUES(13,8,'13:8','Allah zna šta svako žensko nosi i šta materice kraće nose, a šta duže; u Njega sve ima mjeru.');
INSERT INTO "bs_darassalam" VALUES(13,9,'13:9','Poznavatelj onoga što je čulima nedokučivo i onoga što je pojavno, Silni i Mudri.');
INSERT INTO "bs_darassalam" VALUES(13,10,'13:10','Za Njega je jednak onaj od vas koji tiho govori i onaj koji to glasno čini, onaj koji se noću skriva i onaj koji po danu hodi.');
INSERT INTO "bs_darassalam" VALUES(13,11,'13:11','Uz svakog od vas su meleki, koji se smjenjuju ispred njega i iza njega - po Allahovu naređenju nad njim bdiju. Sigurno je da Allah neće izmijeniti stanje u kojem se nalazi određeni narod sve dok taj narod ne promijeni ono što je u njemu. A kad Allah htjedne nekom narodu zlo, niko to ne može spriječiti; bez Njega im nema zaštitnika.');
INSERT INTO "bs_darassalam" VALUES(13,12,'13:12','On je Taj Koji vam pokazuje munju, da se uplašite i ponadate, i On daje da iznova nastaju teški oblaci.');
INSERT INTO "bs_darassalam" VALUES(13,13,'13:13','I grmljavina veliča i hvali Njega, a i meleki, iz strahopoštovanja prema Njemu; On šalje gromove i njima pogađa koga hoće, a oni, i pored toga, raspravljaju o Allahu, a On žestoko kažnjava.');
INSERT INTO "bs_darassalam" VALUES(13,14,'13:14','Samo se Njemu može istinski moliti. A oni kojima se pored Njega mole - neće im se odazvati, kao što ni voda neće stići u usta onome koji prema njoj samo dlanove svoje pruži; dova nevjernika zasigurno je beskorisna.');
INSERT INTO "bs_darassalam" VALUES(13,15,'13:15','Allahu sedždu čini sve što je na nebesima i na Zemlji, svojom voljom ili pod moranje, a i sjene njihove - ujutro i na kraju dana.');
INSERT INTO "bs_darassalam" VALUES(13,16,'13:16','Reci: "Ko je Gospodar nebesa i Zemlje?" - i odgovori: "Allah!" "Reci: "Pa zašto ste onda umjesto Njega kao zaštitnike prihvatili one koji sami sebi ne mogu neku korist pribaviti niti od sebe kakvu štetu otkloniti?" Reci: "Zar su jednaki slijepac i onaj koji vidi, ili, zar su isto tmine i svjetlo, ili, možda misle da oni koje su Allahu u obožavanju pridruživali stvaraju kao što On stvara, pa im se stvaranje čini sličnim"!" Reci: "Allah je Stvoritelj svega i On je, Jedini, Potčinitelj svega."');
INSERT INTO "bs_darassalam" VALUES(13,17,'13:17','On vodu s neba spusti, pa svakom udolinom poteče onoliko koliko može da prihvati i bujica nosi pjenu koja narasta. I ono što ljudi tope na vatri u želji da dobiju nakit ili oruđe ima, također, otpad, sličan pjeni. Tako Allah navodi primjer za istinu i neistinu: pjena se raspršuje, dok ono što koristi ljudima ostaje na Zemlji. Tako, eto, Allah navodi primjere.');
INSERT INTO "bs_darassalam" VALUES(13,18,'13:18','Onima koji se Gospodaru svome odazovu - nagrada najljepša pripada; a oni koji Mu se ne odazovu - kad bi sve što je na Zemlji njihovo bilo, i još toliko, rado bi se time otkupili. Njih čeka užas polaganja računa, prebivalište njihovo bit će Džehennem, a grozno li je to boravište!');
INSERT INTO "bs_darassalam" VALUES(13,19,'13:19','Zar je onaj koji zna da je istina ono što ti se objavljuje od Gospodara tvoga kao onaj koji je slijep?! A pouku primaju samo oni koji razum imaju,');
INSERT INTO "bs_darassalam" VALUES(13,20,'13:20','oni koji obavezu prema Allahu ispunjavaju i ne krše obećanje;');
INSERT INTO "bs_darassalam" VALUES(13,21,'13:21','i oni koji održavaju ono što je Allah naredio da se održava i Gospodara svoga se boje i užasa obračuna se plaše.');
INSERT INTO "bs_darassalam" VALUES(13,22,'13:22','I oni koji trpe u želji za licem Gospodara svoga, i oni koji namaz obavljaju, i koji od onoga što im dajemo i tajno i javno udjeljuju, i koji dobrim na zlo uzvraćaju - njima će lijep ishod dunjaluka pripasti, njih čeka najljepše prebivalište.');
INSERT INTO "bs_darassalam" VALUES(13,23,'13:23','Vrtovi Adna u koje će ući i oni i roditelji njihovi i žene njihove i porod njihov - oni koji su bili čestiti - meleci će im ulaziti na vrata svaka.');
INSERT INTO "bs_darassalam" VALUES(13,24,'13:24','Selam neka je vama, zato što ste trpjeli, a divan li je ishod nakon dunjaluka!');
INSERT INTO "bs_darassalam" VALUES(13,25,'13:25','A oni koji krše obećanje prema Allahu, nakon što su se na to čvrsto obavezali, i kidaju ono što je Allah naredio da se održava, i čine nered na Zemlji - njih čekaju prokletstvo i užas boravišta.');
INSERT INTO "bs_darassalam" VALUES(13,26,'13:26','Kome hoće, Allah daje opskrbu u obilju, i uskraćuje. Oni se raduju životu na ovom svijetu, a život na ovom svijetu prema ahiretu samo je prolazno uživanje.');
INSERT INTO "bs_darassalam" VALUES(13,27,'13:27','Oni koji ne vjeruju govore: "Zašto mu od Gospodara njegova nije spušten kakav znak?" Reci: "Allah odvodi na stranputicu onoga koga On hoće, a k Sebi upućuje onoga ko Mu se pokajnički obraća."');
INSERT INTO "bs_darassalam" VALUES(13,28,'13:28','One koji vjeruju i čija se srca, kad se Allah spomene, smiruju - a srca se, doista, kad se Allah spomene, smiruju.');
INSERT INTO "bs_darassalam" VALUES(13,29,'13:29','Onima koji vjeruju i čine dobra djela - blago njima, njih čeka divno prebivalište.');
INSERT INTO "bs_darassalam" VALUES(13,30,'13:30','I tako smo te poslali narodu prije kojeg su bili i nestali drugi narodi, da im kazuješ ono što ti objavljujemo, a oni u Svemilosnog ne vjeruju. Reci: "On je Gospodar moj, nema boga osim Njega, u Njega se uzdam i Njemu se obraćam."');
INSERT INTO "bs_darassalam" VALUES(13,31,'13:31','Kad bi se kakvom knjigom brda pokrenula ili zemlja iskomadala ili mrtvi dozvali, Kur''an bi to bio! Štaviše, Allahu pripada sve! A zar nemaju nade i ne znaju oni koji vjeruju da bi Allah, kad bi samo htio, sve ljude uputio. A nevjernike će nesreća neprestano pogađati ili će u blizini mjesta njihova doći, zbog onoga što rade - sve dok se Allahovo obećanje ne ostvari. Allah, zaista, ne krši obećanje.');
INSERT INTO "bs_darassalam" VALUES(13,32,'13:32','I prije tebe su poslanici ismijavani bili, pa sam Ja onima koji nisu vjerovali vremena ostavljao, i poslije sam ih kažnjavao, a kakva je samo kazna Moja bila!');
INSERT INTO "bs_darassalam" VALUES(13,33,'13:33','Pa zar je Onaj Koji nad svakim bdi, što god da uradi, kao onaj ko to nije u stanju uraditi?! A Allahu su pridružili ortake. Reci: ''Imenujte ih!'' Zar da Ga vi obavještavate o nečemu što se na Zemlji nalazi - što on zna da postoji, ili ih vi po svome nahođenju tako nazivate?! Ustvari, predstavljeno je lijepim onima koji ne vjeruju spletkarenje njihovo, i oni su od Pravog puta odvraćeni. A onaj koga Allah na stranputicu okrene neće imati nikoga da ga na Pravi put uputi.');
INSERT INTO "bs_darassalam" VALUES(13,34,'13:34','Oni će u životu na dunjaluku patiti, ali će patnja na ahiretu sigurno teža biti; od Allaha ih niko neće odbraniti.');
INSERT INTO "bs_darassalam" VALUES(13,35,'13:35','Ovakav je Džennet obećan onima koji se budu Allaha bojali: vrtovi s rijekama, kroz njega rijeke teku, njegovi plodovi i hladovina trajni su; to će, na kraju, slijediti onima koji se Allaha boje i grijeha klone, a ono što nevjernicima na kraju slijedi Vatra će biti.');
INSERT INTO "bs_darassalam" VALUES(13,36,'13:36','Oni kojima smo dali Knjigu raduju se svemu što objavljujemo tebi, ali u urotničkim grupama ima onih koji poriču nešto od toga. Reci: "Meni je naređeno da samo Allahu ibadet činim i da Njemu nikoga u obožavanju ne pridružujem; Njemu ja pozivam i Njemu se vraćam."');
INSERT INTO "bs_darassalam" VALUES(13,37,'13:37','I Mi ga tako objavljujemo usavršenog na arapskom jeziku. A ako bi se ti za ćudima njihovim poveo, nakon što ti je znanje došlo, ti ne bi imao nikoga ko bi te od Allaha zaštitio ni odbranio.');
INSERT INTO "bs_darassalam" VALUES(13,38,'13:38','I prije tebe smo poslanike slali, i supruge i potomke im davali. I nijedan poslanik nije mogao s nekim znakom istupiti, osim s Allahovom dozvolom. Svako doba imalo je Knjigu.');
INSERT INTO "bs_darassalam" VALUES(13,39,'13:39','Allah uklanja šta hoće, a ostavlja šta hoće; u Njega je glavna Knjiga.');
INSERT INTO "bs_darassalam" VALUES(13,40,'13:40','Bilo da ti pokažemo dio onoga što im prijeteći obećavamo, bilo da ti život oduzmemo, tvoje je da obznanjuješ, a Naše da račune svodimo.');
INSERT INTO "bs_darassalam" VALUES(13,41,'13:41','Zar oni ne vide da Mi Zemlju sužavamo umanjujući krajeve njene?! A Allah sudi i niko ne može presudu Njegovu pobiti, i On brzo račun svodi.');
INSERT INTO "bs_darassalam" VALUES(13,42,'13:42','Lukavstva su smišljali i oni prije njih, ali svako lukavstvo pripada Allahu, i On zna šta svako zaslužuje. A nevjernici će sigurno saznati kome će lijep ishod na kraju pripasti.');
INSERT INTO "bs_darassalam" VALUES(13,43,'13:43','Oni koji ne vjeruju govore: "Ti nisi poslanik." Reci: "Meni i vama dovoljan će kao svjedok biti Allah, i oni koji znanje iz Knjige imaju."');
INSERT INTO "bs_darassalam" VALUES(14,1,'14:1','Elif-lam-ra, Ovo je knjiga koju ti objavljujemo da ljude dozvolom njihova Gospodara izvedeš iz tmina na svjetlo, na put Silnoga i Hvaljenoga');
INSERT INTO "bs_darassalam" VALUES(14,2,'14:2','Allaha, čije je ono što je na nebesima i ono što je na Zemlji. A teško nevjernicima od patnje žestoke,');
INSERT INTO "bs_darassalam" VALUES(14,3,'14:3','onima koji život na dunjaluku više vole od ahireta i koji od Allahova puta odvraćaju i nastoje ga krivim prikazati! Oni su daleko zalutali.');
INSERT INTO "bs_darassalam" VALUES(14,4,'14:4','Mi nismo poslali nijednog poslanika koji nije govorio jezikom naroda svoga, da bi im objasnio. A Allah u zabludu okreće onoga koga hoće i upućuje na Pravi put onoga koga hoće; On je Silni i Mudri.');
INSERT INTO "bs_darassalam" VALUES(14,5,'14:5','I Musaa smo poslali s dokazima Našim: "Izvedi narod svoj iz tmina na svjetlo i podsjeti ga na Allahove blagodati." To su, uistinu, znakovi za svakog ko je strpljiv i zahvalan.');
INSERT INTO "bs_darassalam" VALUES(14,6,'14:6','I kad Musa reče narodu svom: "Sjetite se Allahove blagodati kad vas je izbavio od faraonovih ljudi koji su vas najgorim mukama mučili, koji su vam mušku djecu klali, a žensku u životu ostavljali - to je bilo veliko iskušenje od Gospodara vašega."');
INSERT INTO "bs_darassalam" VALUES(14,7,'14:7','I kad je Gospodar vaš objavio: "Ako budete zahvalni, Ja ću vam sigurno još više dati; budete li nezahvalni, nevjernici, žestoka će, doista, biti kazna Moja."');
INSERT INTO "bs_darassalam" VALUES(14,8,'14:8','I Musa još reče: "Ako budete nezahvalni nevjernici i vi i svi drugi na Zemlji, pa, Allah je, doista, neovisan i hvaljen."');
INSERT INTO "bs_darassalam" VALUES(14,9,'14:9','Zar do vas nije doprla vijest o onima prije vas, o narodu Nuhovu, i o Adu, i o Semudu, i o onima poslije njih? Samo ih Allah zna! Poslanici su im njihovi jasne dokaze donosili, ali oni su ruke svoje na usta stavljali i govorili: "Mi ne vjerujemo u ono što se po vama šalje i mi itekako sumnjamo u ono u što nas pozivate."');
INSERT INTO "bs_darassalam" VALUES(14,10,'14:10','Poslanici su njihovi govorili: "Zar se može sumnjati u Allaha, Koji je nebesa i Zemlju bez prethodnog primjera-iz ničega stvorio? On vas poziva da bi vam neke grijehe oprostio i da bi vas do roka određenog ostavio." Oni su odgovorili: "Vi ste ljudi kao i mi; hoćete da nas odvratite od onih koje su naši preci obožavali - pa, donesite nam dokaz očevidni."');
INSERT INTO "bs_darassalam" VALUES(14,11,'14:11','"Mi jesmo ljudi kao i vi", govorili su im poslanici njihovi, "ali, Allah dariva samo one robove Svoje koje On hoće; mi vam ne možemo donijeti dokaz osim s dozvolom Allahovom - a vjernici neka se samo u Allaha uzdaju."');
INSERT INTO "bs_darassalam" VALUES(14,12,'14:12','"Zašto da se ne uzdamo u Allaha kad nas je On putevima kojim idemo uputio? Mi ćemo, doista, strpljivo podnositi ono čime nas uznemiravate - a oni koji se uzdaju, neka se samo u Allaha uzdaju!"');
INSERT INTO "bs_darassalam" VALUES(14,13,'14:13','Oni koji nisu vjerovali govorili su poslanicima svojim: "Ili ćete se u vjeru našu vratiti ili ćemo vas, doista, iz zemlje naše protjerati!" A poslanicima je Gospodar njihov objavljivao: "Mi ćemo zulumćare sigurno uništiti.');
INSERT INTO "bs_darassalam" VALUES(14,14,'14:14','I poslije njih vas na Zemlji nastaniti. Bit će to za one koji će se polaganja računa preda Mnom bojati i koji će od prijetnje Moje strahovati."');
INSERT INTO "bs_darassalam" VALUES(14,15,'14:15','I poslanici su pomoć tražili, pa je svaki oholi inadžija nastradao.');
INSERT INTO "bs_darassalam" VALUES(14,16,'14:16','Pred njim će Džehennem biti - i on će biti napajan tekućinom od sukrvice.');
INSERT INTO "bs_darassalam" VALUES(14,17,'14:17','Mučit će se da je proguta, ali je nikako neće moći proždrijeti i smrt će mu sa svih strana prilaziti, ali on neće umrijeti; njega poslije toga tek čeka patnja teška.');
INSERT INTO "bs_darassalam" VALUES(14,18,'14:18','Djela onih koji ne vjeruju u Gospodara svoga nalik su na pepeo koji vihor u olujnom danu raznese; neće moći očekivati nikakvu nagradu za djela koja su učinili, to je stranputica daleka.');
INSERT INTO "bs_darassalam" VALUES(14,19,'14:19','Zar ne vidiš da je Allah nebesa i Zemlju s Istinom stvorio? Ako htjedne vas će ukloniti i nova stvorenja dovesti.');
INSERT INTO "bs_darassalam" VALUES(14,20,'14:20','To Allahu nije teško.');
INSERT INTO "bs_darassalam" VALUES(14,21,'14:21','I izići će svi pred Allaha, pa će oni koji su bili nemoćni reći svojim glavešinama, onima koji su osioni bili: "Mi smo bili vaše pristalice, pa da li ste u stanju da nas bar malo od Allahove kazne zaštitite?" "Da je nas Allah uputio", odgovorit će oni, "i mi bismo bili vas uputili. Žalili se mi ili trpjeli, svejedno nam je, spasa nama više neće biti."');
INSERT INTO "bs_darassalam" VALUES(14,22,'14:22','I kada bude sve riješeno, šejtan će reći: "Allah vam je istinsko obećanje dao, a ja sam vam obećavao i svoja obećanja iznevjerio; ali, ja nisam nikakvu vlast nad vama imao, samo sam vas pozivao i vi ste mi se odazivali; zato ne korite mene, već sami sebe; niti ja mogu vama pomoći, niti vi možete pomoći meni. Ja nemam ništa s tim što ste me prije Njemu u obožavanju pridruživali. Zulumćare sigurno čeka bolna patnja."');
INSERT INTO "bs_darassalam" VALUES(14,23,'14:23','A oni koji su vjerovali i dobra djela činili bit će uvedeni, s dozvolom Gospodara njihova, u džennetske bašče kroz koje rijeke teku i u njima će vječno boraviti, njihov pozdrav će u njima selam biti.');
INSERT INTO "bs_darassalam" VALUES(14,24,'14:24','Zar ne vidiš kako Allah navodi primjer - lijepa je riječ kao lijepo drvo: korijen mu je čvrsto u zemlji a grane prema nebu.');
INSERT INTO "bs_darassalam" VALUES(14,25,'14:25','Ono plod svoj u svakom momentu daje s dozvolom Gospodara svoga- a Allah ljudima navodi primjere da bi se prisjetili i pouku uzeli.');
INSERT INTO "bs_darassalam" VALUES(14,26,'14:26','a ružna je riječ kao ružno drvo koje je s površine zemlje iščupano. Njemu nema opstanka.');
INSERT INTO "bs_darassalam" VALUES(14,27,'14:27','Allah vjernike postojanom riječju učvršćuje u životu na dunjaluku, a i na ahiretu, a zulumćare u zabludu odvodi; Allah radi što hoće.');
INSERT INTO "bs_darassalam" VALUES(14,28,'14:28','Zar ne vidiš one koji su umjesto zahvalnosti Allahu na blagodatima - nezahvalnošću uzvratili i narod svoj u Kuću propasti doveli,');
INSERT INTO "bs_darassalam" VALUES(14,29,'14:29','u Džehennem, u kome će gorjeti - a užasno je on prebivalište!');
INSERT INTO "bs_darassalam" VALUES(14,30,'14:30','I koji su Allahu druge kao ravne dodavali da bi zavodili s puta Njegova? Reci: "Naslađujte se, završit ćete, sigurno, u Vatri!"');
INSERT INTO "bs_darassalam" VALUES(14,31,'14:31','Reci Mojim robovima koji vjeruju da namaz obavljaju i da udjeljuju i tajno i javno iz onoga što im Mi darujemo, prije nego što nastupi Dan u kome neće biti ni trgovanja ni prijateljstva.');
INSERT INTO "bs_darassalam" VALUES(14,32,'14:32','Allah je Taj Koji je stvorio nebesa i Zemlju; On spušta s neba vodu i čini da pomoću nje rađaju plodovi kojima se hranite; i potčinio vam je lađe da morem plove po odredbi Njegovoj, i potčinio vam je rijeke,');
INSERT INTO "bs_darassalam" VALUES(14,33,'14:33','i potčinio vam je Sunce i Mjesec, koji se stalno kreću, i potčinio vam je noć i dan.');
INSERT INTO "bs_darassalam" VALUES(14,34,'14:34','I daje vam od svega onoga što od Njega tražite, i ako biste Allahove blagodati brojali, ne biste ih nabrojali. Čovjek je, uistinu, itekako nepravedan i nezahvalan.');
INSERT INTO "bs_darassalam" VALUES(14,35,'14:35','A kada Ibrahim reče: "Gospodaru moj, učini ovaj grad sigurnim i sačuvaj i mene i sinove moje od toga da obožavamo kumire.');
INSERT INTO "bs_darassalam" VALUES(14,36,'14:36','Oni su, Gospodaru moj, mnoge ljude na stranputicu naveli. Onaj ko bude mene slijedio - moje je vjere, a onaj ko bude protiv mene ustajao, pa - Ti si, uistinu, Onaj Koji oprašta grijehe i milostiv si.');
INSERT INTO "bs_darassalam" VALUES(14,37,'14:37','Gospodaru naš, ja sam neke potomke svoje naselio u kotlini u kojoj se ništa ne sije, kod Tvoje časne Kabe, da bi, Gospodaru naš, namaz obavljali; zato učini da srca nekih ljudi čeznu za njima i opskrbi ih raznim plodovima da bi zahvalni bili.');
INSERT INTO "bs_darassalam" VALUES(14,38,'14:38','Gospodaru naš, Ti sigurno znaš šta mi tajimo, a šta na javu iznosimo. A Allahu ništa nije skriveno; ni na Zemlji, ni na nebu.');
INSERT INTO "bs_darassalam" VALUES(14,39,'14:39','Hvala Allahu, Koji mi je u starosti podario Ismaila i Ishaka; Gospodar moj dove, uistinu, uslišava.');
INSERT INTO "bs_darassalam" VALUES(14,40,'14:40','Gospodaru moj, daj da ja i potomci moji u obavljanju namaza budemo ustrajni; Gospodaru naš, Ti usliši dovu moju!');
INSERT INTO "bs_darassalam" VALUES(14,41,'14:41','Gospodaru naš, oprosti meni, i roditeljima mojim i svim vjernicima- na Dan kad se bude polagao račun!"');
INSERT INTO "bs_darassalam" VALUES(14,42,'14:42','A ti nikako ne misli da Allah ne mari za onim što rade zulumćari! On ih samo pušta do Dana kada će im oči ostati otvorene!');
INSERT INTO "bs_darassalam" VALUES(14,43,'14:43','Žurit će, glave im uzdignute, pogled ukočen; a srca će im prazna biti.');
INSERT INTO "bs_darassalam" VALUES(14,44,'14:44','Ti opominji ljude Danom kada će im kazna doći, kad će oni koji su zulum činili - govoriti: "Gospodaru naš, ostavi nas još samo kratko vrijeme, odazvat ćemo se pozivu Tvome i slijedit ćemo poslanike!", a zar se prije niste zaklinjali da s ovog svijeta nećete otići?');
INSERT INTO "bs_darassalam" VALUES(14,45,'14:45','I nastanili ste se bili u mjestima gdje su živjeli oni koji su prema sebi zulum učinili, a bilo vam je poznato kako smo s njima postupili i primjere smo vam navodili.');
INSERT INTO "bs_darassalam" VALUES(14,46,'14:46','I oni lukavstva svoja pletu, a Allah zna za lukavstva njihova, samo što lukavstva njihova ne mogu brda pokrenuti.');
INSERT INTO "bs_darassalam" VALUES(14,47,'14:47','Nemoj ni pomisliti da Allah neće održati obećanje Svoje poslanicima Svojim-Allah je, uistinu, silan i strog.');
INSERT INTO "bs_darassalam" VALUES(14,48,'14:48','Na Dan kad Zemlja bude zamijenjena drugom zemljom, a i nebesa, i kad svi iziđu pred Allaha, Jedinoga, Potčinitelja svega.');
INSERT INTO "bs_darassalam" VALUES(14,49,'14:49','Taj dan ćeš vidjeti prijestupnike u okovima zajedno povezane.');
INSERT INTO "bs_darassalam" VALUES(14,50,'14:50','Košulje će im od katrana biti, a vatra će lica njihova obaviti.');
INSERT INTO "bs_darassalam" VALUES(14,51,'14:51','To je zato da Allah svakome da što je zaslužio - Allah zaista brzo račun svodi.');
INSERT INTO "bs_darassalam" VALUES(14,52,'14:52','Ovo je obznana ljudima, i da njome budu opomenuti, i da znaju da je samo On Jedan Bog, i da prime pouku oni koji razum imaju!');
INSERT INTO "bs_darassalam" VALUES(15,1,'15:1','Elif Lām Rā. Ovo su ajeti knjige, Kur’ana jasnog!');
INSERT INTO "bs_darassalam" VALUES(15,2,'15:2','Zažalit će nevjernici često što nisu postali muslimani.');
INSERT INTO "bs_darassalam" VALUES(15,3,'15:3','Pusti ih neka jedu i naslađuju se, i neka ih zavara nada – znat će oni!');
INSERT INTO "bs_darassalam" VALUES(15,4,'15:4','A Mi smo uništavali gradove samo u određeno vrijeme.');
INSERT INTO "bs_darassalam" VALUES(15,5,'15:5','nijedan narod ne može ni ubrzati ni usporiti kraj svoj.');
INSERT INTO "bs_darassalam" VALUES(15,6,'15:6','Oni govore: “Ej ti kome se Kur’an objavljuje, ti si uistinu lud!');
INSERT INTO "bs_darassalam" VALUES(15,7,'15:7','Zašto nam meleke ne dovedeš, ako je istina što govoriš!”');
INSERT INTO "bs_darassalam" VALUES(15,8,'15:8','Mi meleke šaljemo samo s istinom, i tad im se ne bi dalo vremena da čekaju.');
INSERT INTO "bs_darassalam" VALUES(15,9,'15:9','Mi, uistinu, Kur’an objavljujemo i zaista ćemo Mi nad njim bdjeti!');
INSERT INTO "bs_darassalam" VALUES(15,10,'15:10','I prije tebe smo poslanike prijašnjim narodima slali');
INSERT INTO "bs_darassalam" VALUES(15,11,'15:11','i nijedan im poslanik nije došao, a da se nisu njime ismijavali.');
INSERT INTO "bs_darassalam" VALUES(15,12,'15:12','Eto, tako dajemo da to uđe u srca prestupnika.');
INSERT INTO "bs_darassalam" VALUES(15,13,'15:13','Oni u njega neće vjerovati, a zna se šta je bilo s narodima davnašnjim.');
INSERT INTO "bs_darassalam" VALUES(15,14,'15:14','Kad bismo radi njih kapiju na nebu otvorili i oni se kroz nju uspinjali');
INSERT INTO "bs_darassalam" VALUES(15,15,'15:15','opet bi oni, zacijelo, rekli: “Samo nam se pričinjava, mi smo ljudi opčinjeni!”');
INSERT INTO "bs_darassalam" VALUES(15,16,'15:16','Mi smo na nebu sazviježđa stvorili i za one koji ih posmatraju ukrasili.');
INSERT INTO "bs_darassalam" VALUES(15,17,'15:17','i čuvamo ih od svakog šejtana prokletog.');
INSERT INTO "bs_darassalam" VALUES(15,18,'15:18','a onog koji kradom prisluškuje stiže svjetlica vidljiva.');
INSERT INTO "bs_darassalam" VALUES(15,19,'15:19','A Zemlju smo prostrli i po njoj nepomične planine razbacali i učinili da na njoj sve s mjerom raste.');
INSERT INTO "bs_darassalam" VALUES(15,20,'15:20','i dajemo vam iz nje hranu, a i onima koje vi ne hranite.');
INSERT INTO "bs_darassalam" VALUES(15,21,'15:21','I ne postoji ništa čije riznice ne posjedujemo, a od toga Mi dajemo samo onoliko koliko je potrebno.');
INSERT INTO "bs_darassalam" VALUES(15,22,'15:22','Mi šaljemo vjetrove da oplođuju, a iz neba spuštamo kišu da imate šta piti – vi time ne možete raspolagati.');
INSERT INTO "bs_darassalam" VALUES(15,23,'15:23','I samo Mi dajemo život i smrt, i samo Mi sve nasljeđujemo.');
INSERT INTO "bs_darassalam" VALUES(15,24,'15:24','i samo Mi znamo one koji su vam prethodili, i samo Mi znamo one koji će poslije doći.');
INSERT INTO "bs_darassalam" VALUES(15,25,'15:25','A On, Gospodar tvoj, zaista će ih sabrati. On je mudar i sve zna.');
INSERT INTO "bs_darassalam" VALUES(15,26,'15:26','Mi smo stvorili Adema od ilovače, od blata ustajalog');
INSERT INTO "bs_darassalam" VALUES(15,27,'15:27','a još prije smo stvorili džine od vatre užarene.');
INSERT INTO "bs_darassalam" VALUES(15,28,'15:28','I kad Gospodar tvoj reče melekima: "Ja ću stvoriti čovjeka od ilovače, od blata ustajalog,');
INSERT INTO "bs_darassalam" VALUES(15,29,'15:29','i kad mu dam lik i u njega udahnem dušu, vi mu se poklonite!”');
INSERT INTO "bs_darassalam" VALUES(15,30,'15:30','Svi su se meleki, zajedno, poklonili.');
INSERT INTO "bs_darassalam" VALUES(15,31,'15:31','osim Iblisa; on se nije htio s njima pokloniti.');
INSERT INTO "bs_darassalam" VALUES(15,32,'15:32','“O Iblise”, reče On, “zašto se ti ne htjede pokloniti?”');
INSERT INTO "bs_darassalam" VALUES(15,33,'15:33','“Nije moje”, reče, “da se poklonim čovjeku koga si stvorio od ilovače, od blata ustajalog.”');
INSERT INTO "bs_darassalam" VALUES(15,34,'15:34','“Onda izlazi iz Dženneta”, reče On, “neka si proklet."');
INSERT INTO "bs_darassalam" VALUES(15,35,'15:35','i neka se prokletstvo zadrži na tebi do Dana sudnjeg!”');
INSERT INTO "bs_darassalam" VALUES(15,36,'15:36','“Gospodaru moj”, reče on, “daj mi vremena do dana kad će oni biti oživljeni!”');
INSERT INTO "bs_darassalam" VALUES(15,37,'15:37','“Daje ti se rok”, reče On.');
INSERT INTO "bs_darassalam" VALUES(15,38,'15:38','“do Dana već određenog.”');
INSERT INTO "bs_darassalam" VALUES(15,39,'15:39','“Gospodaru moj”, reče, “zato što si me u zabludu odveo, ja ću njima na Zemlji poroke lijepim predstaviti i potrudit ću se da ih sve zavedem.');
INSERT INTO "bs_darassalam" VALUES(15,40,'15:40','osim među njima Tvojih robova iskrenih.”');
INSERT INTO "bs_darassalam" VALUES(15,41,'15:41','"Ovo je Pravi put k Meni", reče On.');
INSERT INTO "bs_darassalam" VALUES(15,42,'15:42','“ti nećeš imati nikakve vlasti nad robovima Mojim, osim nad onima koji te budu slijedili, od onih zalutalih.”');
INSERT INTO "bs_darassalam" VALUES(15,43,'15:43','Za sve njih mjesto sastanka Džehennem će biti.');
INSERT INTO "bs_darassalam" VALUES(15,44,'15:44','on će sedam kapija imati i kroz svaku će određen broj njih proći.');
INSERT INTO "bs_darassalam" VALUES(15,45,'15:45','Oni koji su se Allaha bojali i onog što im je zabranjeno klonili, oni će u džennetskim baščama pored izvorā biti.');
INSERT INTO "bs_darassalam" VALUES(15,46,'15:46','“Uđite u njih sigurni, straha oslobođeni!”');
INSERT INTO "bs_darassalam" VALUES(15,47,'15:47','I Mi ćemo zlobu iz grudi njihovih istisnuti, oni će kao braća na divanima jedni prema drugima sjediti.');
INSERT INTO "bs_darassalam" VALUES(15,48,'15:48','tu ih umor neće doticati, oni odatle nikad neće izvedeni biti.');
INSERT INTO "bs_darassalam" VALUES(15,49,'15:49','Kaži robovima Mojim da sam Ja, zaista, Onaj Koji prašta i da sam milostiv.');
INSERT INTO "bs_darassalam" VALUES(15,50,'15:50','ali da je i kazna Moja, doista, bolna kazna!');
INSERT INTO "bs_darassalam" VALUES(15,51,'15:51','I obavijesti ih o gostima Ibrahimovim.');
INSERT INTO "bs_darassalam" VALUES(15,52,'15:52','kad su mu ušli i rekli: “Mir!”, on je rekao: “Mi smo se vas uplašili.”');
INSERT INTO "bs_darassalam" VALUES(15,53,'15:53','“Ne plaši se!”, rekoše, “donosimo ti radosnu vijest, učena ćeš sina imati.”');
INSERT INTO "bs_darassalam" VALUES(15,54,'15:54','“Zar mi donosite radosnu vijest sad kad me je starost ophrvala?”, reče on, “čime ćete me obradovati?”');
INSERT INTO "bs_darassalam" VALUES(15,55,'15:55','“Donosimo ti radosnu vijest koja će se doista obistiniti”, rekoše oni, “zato nadu ne gubi!”');
INSERT INTO "bs_darassalam" VALUES(15,56,'15:56','“Nadu u milost Gospodara svog mogu gubiti samo oni koji su zabludjeli”, reče on.');
INSERT INTO "bs_darassalam" VALUES(15,57,'15:57','I upita: "A koji je tačno razlog vašeg dolaska, o izaslanici?"');
INSERT INTO "bs_darassalam" VALUES(15,58,'15:58','“Mi smo poslati narodu nevjerničkom”, rekoše.');
INSERT INTO "bs_darassalam" VALUES(15,59,'15:59','“izuzev Lutove porodice, njih ćemo, zaista, sve spasiti,"');
INSERT INTO "bs_darassalam" VALUES(15,60,'15:60','osim žene njegove, ona će, odlučili smo, s ostalima kaznu iskusiti.”');
INSERT INTO "bs_darassalam" VALUES(15,61,'15:61','I kad izaslanici dođoše do Lutove porodice,');
INSERT INTO "bs_darassalam" VALUES(15,62,'15:62','on reče: “Vi ste, doista, ljudi neznani!”');
INSERT INTO "bs_darassalam" VALUES(15,63,'15:63','“Ne!”, rekoše oni, “donosimo ti ono u šta ovi stalno sumnjaju.');
INSERT INTO "bs_darassalam" VALUES(15,64,'15:64','donosimo ti ono što će se, sigurno, dogoditi, a mi zaista istinu govorimo.');
INSERT INTO "bs_darassalam" VALUES(15,65,'15:65','Izvedi čeljad svoju u gluho doba noći, a ti budi na začelju njihovu, i neka se niko od vas ne osvrće, već produžite u pravcu kuda vam se naređuje!”');
INSERT INTO "bs_darassalam" VALUES(15,66,'15:66','I Mi smo mu objavili ono što će se zbiti: da će oni, svi do posljednjeg, u svitanje uništeni biti.');
INSERT INTO "bs_darassalam" VALUES(15,67,'15:67','I dođoše stanovnici grada, veseli.');
INSERT INTO "bs_darassalam" VALUES(15,68,'15:68','“Ovo su gosti moji”, reče on, “pa me ne sramotite.');
INSERT INTO "bs_darassalam" VALUES(15,69,'15:69','i bojte se Allaha, i mene ne ponizujte!”');
INSERT INTO "bs_darassalam" VALUES(15,70,'15:70','“A zar ti nismo zabranili da ikoga primaš?”, povikaše oni.');
INSERT INTO "bs_darassalam" VALUES(15,71,'15:71','“Ako već hoćete nešto činiti, eto kćeri mojih!”, reče on.');
INSERT INTO "bs_darassalam" VALUES(15,72,'15:72','Tako Mi života tvog, oni su u pijanstvu svom lutali.');
INSERT INTO "bs_darassalam" VALUES(15,73,'15:73','I njih je zadesio strašan glas kad je Sunce izlazilo.');
INSERT INTO "bs_darassalam" VALUES(15,74,'15:74','i Mi smo učinili da ono što je gore bude dolje, i na njih smo kao kišu grumenje od skamenjene gline sručili.');
INSERT INTO "bs_darassalam" VALUES(15,75,'15:75','U tome su, zaista, znaci za one koji o tome pronicljivo razmišljaju.');
INSERT INTO "bs_darassalam" VALUES(15,76,'15:76','a naselje je baš uz postojeći put.');
INSERT INTO "bs_darassalam" VALUES(15,77,'15:77','U tome su, zaista, znaci za one koji vjeruju.');
INSERT INTO "bs_darassalam" VALUES(15,78,'15:78','A i stanovnici Ejke bili su nevjernici.');
INSERT INTO "bs_darassalam" VALUES(15,79,'15:79','pa smo ih kaznili, i oba naselja su uz put koji se vidi.');
INSERT INTO "bs_darassalam" VALUES(15,80,'15:80','I stanovnici su Hidžra poslanike lažnim smatrali.');
INSERT INTO "bs_darassalam" VALUES(15,81,'15:81','a Mi smo im dokaze Naše bili dali, ali su oni od njih glave okrenuli.');
INSERT INTO "bs_darassalam" VALUES(15,82,'15:82','Oni su kuće u brdima klesali, vjerujući da su sigurni.');
INSERT INTO "bs_darassalam" VALUES(15,83,'15:83','pa i njih u svitanje strašan glas zadesi.');
INSERT INTO "bs_darassalam" VALUES(15,84,'15:84','i ne bijaše im ni od kakve koristi ono što su bili stekli.');
INSERT INTO "bs_darassalam" VALUES(15,85,'15:85','Mi smo nebesa i Zemlju i ono između njih s Istinom stvorili. Čas oživljenja će, zacijelo, doći, zato ti velikodušno oprosti,');
INSERT INTO "bs_darassalam" VALUES(15,86,'15:86','Gospodar tvoj sve stvara i On je Sveznajući.');
INSERT INTO "bs_darassalam" VALUES(15,87,'15:87','Mi smo ti objavili sedam ajeta, koji se ponavljaju, i Kur’an ti veličanstveni objavljujemo.');
INSERT INTO "bs_darassalam" VALUES(15,88,'15:88','Ne pružaj poglede svoje ka onome što Mi dajemo na uživanje nekim od njih i ne budi tužan zbog njih, a prema vjernicima blag budi.');
INSERT INTO "bs_darassalam" VALUES(15,89,'15:89','i reci: "Ja sam samo upozoritelj jasni."');
INSERT INTO "bs_darassalam" VALUES(15,90,'15:90','kao što smo sljedbenike Knjige opomenuli.');
INSERT INTO "bs_darassalam" VALUES(15,91,'15:91','one koji Kur’an na dijelove dijele.');
INSERT INTO "bs_darassalam" VALUES(15,92,'15:92','I tako Mi Gospodara tvog, njih ćemo sve na odgovornost pozvati');
INSERT INTO "bs_darassalam" VALUES(15,93,'15:93','za ono što su radili!');
INSERT INTO "bs_darassalam" VALUES(15,94,'15:94','Ti javno ispovijedaj ono što ti se naređuje i mnogobožaca se okani.');
INSERT INTO "bs_darassalam" VALUES(15,95,'15:95','Mi ćemo te sačuvati onih koji se rugaju.');
INSERT INTO "bs_darassalam" VALUES(15,96,'15:96','koji pored Allaha drugog boga uzimaju; i znat će oni!');
INSERT INTO "bs_darassalam" VALUES(15,97,'15:97','Mi dobro znamo da ti je teško u duši zbog onog što oni govore.');
INSERT INTO "bs_darassalam" VALUES(15,98,'15:98','zato veličaj Gospodara svog i hvali Ga, i molitvu obavljaj.');
INSERT INTO "bs_darassalam" VALUES(15,99,'15:99','i sve dok si živ, Gospodaru se svom klanjaj!');
INSERT INTO "bs_darassalam" VALUES(16,1,'16:1','Ono što je Allah odredio – dogodit će se; zato to ne požurujte! Hvaljen neka je On i neka je vrlo visoko iznad onih koje Njemu ravnim smatraju!');
INSERT INTO "bs_darassalam" VALUES(16,2,'16:2','On šalje meleke s Objavom, po volji Svojoj, onim robovima Svojim kojim hoće: “Opominjite da nema Boga osim Mene i bojte Me se!”');
INSERT INTO "bs_darassalam" VALUES(16,3,'16:3','On je mudro nebesa i Zemlju stvorio; neka je On vrlo visoko iznad onih koje Njemu ravnim smatraju!');
INSERT INTO "bs_darassalam" VALUES(16,4,'16:4','On stvara čovjeka od kapi sjemena, a on odjednom – otvoreni protivnik!');
INSERT INTO "bs_darassalam" VALUES(16,5,'16:5','I stoku On za vas stvara; njome se od hladnoće štitite, a i drugih koristi imate, njome se najviše i hranite.');
INSERT INTO "bs_darassalam" VALUES(16,6,'16:6','ona vam je ukras kad je sa ispaše vraćate i kad je na pašu izgonite.');
INSERT INTO "bs_darassalam" VALUES(16,7,'16:7','a nosi vam i terete u mjesta u koja bez velike muke ne biste stigli – Gospodar vaš je, uistinu, blag i milostiv.');
INSERT INTO "bs_darassalam" VALUES(16,8,'16:8','i konje, i mazge, i magarce – da ih jašete, i kao ukras – a stvara i ono što ne znate.');
INSERT INTO "bs_darassalam" VALUES(16,9,'16:9','Allahovo je da ukaže na Pravi put, a ima ih i krivih; a da On hoće, sve bi vas uputio.');
INSERT INTO "bs_darassalam" VALUES(16,10,'16:10','On spušta s neba vodu koju pijete i kojom se natapa rastinje kojim stoku napasate.');
INSERT INTO "bs_darassalam" VALUES(16,11,'16:11','On čini da vam pomoću nje raste žito, i masline, i palme, i grožđe, i svakovrsni plodovi – to je, zaista, dokaz za ljude koji razmišljaju.');
INSERT INTO "bs_darassalam" VALUES(16,12,'16:12','On čini da se noći i danom koristite, i Suncem i Mjesecom, a zvijezde su volji Njegovoj potčinjene – to su, uistinu, dokazi za ljude koji pameti imaju.');
INSERT INTO "bs_darassalam" VALUES(16,13,'16:13','i svim onim raznobojnim što vam na Zemlji stvara – to je, doista, dokaz ljudima koji pouku primaju.');
INSERT INTO "bs_darassalam" VALUES(16,14,'16:14','On čini da se morem koristite, da iz njega svježe meso jedete i da vadite nakit kojim se kitite – ti vidiš lađe kako ga sijeku da biste nešto iz obilja Njegova stekli i da biste zahvalni bili.');
INSERT INTO "bs_darassalam" VALUES(16,15,'16:15','On je po Zemlji nepomična brda pobacao da vas ona ne potresa, a i rijeke i puteve da se ispravno usmjeravate.');
INSERT INTO "bs_darassalam" VALUES(16,16,'16:16','i putokaze, a i po zvijezdama se oni upravljaju.');
INSERT INTO "bs_darassalam" VALUES(16,17,'16:17','Pa je li onda Onaj Koji stvara kao onaj koji ne stvara!? Urazumite se!');
INSERT INTO "bs_darassalam" VALUES(16,18,'16:18','Ako vi budete brojali Allahove blagodati, nećete ih nabrojati – Allah, uistinu, prašta i samilostan je.');
INSERT INTO "bs_darassalam" VALUES(16,19,'16:19','Allah zna šta tajite, a šta javno iznosite.');
INSERT INTO "bs_darassalam" VALUES(16,20,'16:20','A oni kojima se oni, umjesto Allahu, klanjaju – ništa ne stvaraju; oni su sami stvoreni.');
INSERT INTO "bs_darassalam" VALUES(16,21,'16:21','mrtvi su, nisu živi i ne znaju kada će biti oživljeni.');
INSERT INTO "bs_darassalam" VALUES(16,22,'16:22','Vaš Bog je – jedan Bog! Srca onih koji u onaj svijet ne vjeruju, poriču, oni se ohološću razmeću.');
INSERT INTO "bs_darassalam" VALUES(16,23,'16:23','nema sumnje da Allah zna i ono što oni taje, a i ono što javno iznose; On doista ne voli one koji se ohole.');
INSERT INTO "bs_darassalam" VALUES(16,24,'16:24','A kad ih neko upita: “Šta to Gospodar vaš objavljuje?”, oni odgovaraju: “Naroda drevnih izmišljotine!”');
INSERT INTO "bs_darassalam" VALUES(16,25,'16:25','da bi na Sudnjem danu nosili čitavo breme svoje i dio bremena onih koje su, a da oni nisu bili svjesni, u zabludu odveli. A grozno je to što će oni nositi!');
INSERT INTO "bs_darassalam" VALUES(16,26,'16:26','I oni prije njih su spletke pleli, pa je Allah iz temelja zgrade njihove porušio, i krov se na njih srušio – stigla ih je kazna odakle nisu očekivali.');
INSERT INTO "bs_darassalam" VALUES(16,27,'16:27','A na Sudnjem danu On će ih osramotiti i upitati: “Gdje su oni koje ste Meni ravnim smatrali, oni zbog kojih ste se prepirali?” Oni koji su razumni reći će: “Danas će bruka i muka nevjernike stići.');
INSERT INTO "bs_darassalam" VALUES(16,28,'16:28','kojima su meleki dušu uzeli u času kad su nevjernici bili.” I oni će se pokoriti i reći: “Mi nismo nikakvo zlo činili!” “Jest, Allah, doista, dobro zna ono što ste radili.');
INSERT INTO "bs_darassalam" VALUES(16,29,'16:29','zato ulazite na kapije Džehennema, u njemu ćete vječno ostati!” O kako će prebivalište onih koji su se oholili grozno biti!');
INSERT INTO "bs_darassalam" VALUES(16,30,'16:30','A onima koji su se Allaha bojali reći će se: “Šta je objavljivao Gospodar vaš?” “Dobro!”, odgovorit će. Oni koji čine dobra djela imat će još na ovom svijetu lijepu nagradu, a onaj svijet je, sigurno, još bolji. O kako će boravište onih koji su se Allaha bojali divno biti.');
INSERT INTO "bs_darassalam" VALUES(16,31,'16:31','edenski perivoji u koje će ući, kroz koje rijeke teku, i u kojima će sve što zažele imati. Tako će Allah one koji Ga se budu bojali nagraditi.');
INSERT INTO "bs_darassalam" VALUES(16,32,'16:32','one kojima će meleki duše uzeti – a oni čisti, i kojima će govoriti: “Selam vama! Uđite u Džennet zbog onog što ste činili!”');
INSERT INTO "bs_darassalam" VALUES(16,33,'16:33','Šta li višebošci čekaju: da im dođu meleci, ili da dođe odredba Gospodara tvoga?! Tako su postupili i oni prije njih; njima nije zulum učinio Allah, oni su sami sebi zulum učinili.');
INSERT INTO "bs_darassalam" VALUES(16,34,'16:34','I stigla ih je kazna za ružna djela koja su činili, i sa svih strana ih je snašlo ono čemu su se rugali.');
INSERT INTO "bs_darassalam" VALUES(16,35,'16:35','Oni koji Njemu druge smatraju ravnim govore: “Da je Allah htio, ne bismo se ni mi ni preci naši, pored Allaha, nikom klanjali i ne bismo, bez Njegove volje, ništa zabranjenim smatrali.” Tako su isto i oni prije njih postupali. A zar su poslanici bili dužni nešto drugo već jasno istinu dostaviti?');
INSERT INTO "bs_darassalam" VALUES(16,36,'16:36','Mi smo svakom narodu poslanika poslali: “Allahu ibadet činite, a kumira se klonite!” I bilo je među njima onih kojima je Allah na Pravi put ukazao, a i onih koji su zaslužili da ostanu u zabludi; zato putujte po svijetu da vidite kako su završili oni koji su poslanike u laž utjerivali.');
INSERT INTO "bs_darassalam" VALUES(16,37,'16:37','Ma koliko ti želio da oni budu na Pravom putu, Allah neće ukazati na Pravi put onom koga je u zabludu odveo i njima niko neće pomoći.');
INSERT INTO "bs_darassalam" VALUES(16,38,'16:38','Oni se zaklinju Allahom, najtežom zakletvom: “Allah neće oživjeti onog koji umre!” Naprotiv, hoće! To je Njegovo obećanje koje će se, doista, ispuniti! Ali većina ljudi ne zna.');
INSERT INTO "bs_darassalam" VALUES(16,39,'16:39','da bi im objasnio ono oko čega su se razilazili i da bi saznali oni koji nisu vjerovali da su lašci bili.');
INSERT INTO "bs_darassalam" VALUES(16,40,'16:40','Ako nešto hoćemo, Mi samo za to reknemo: “Budi!” – i ono bude.');
INSERT INTO "bs_darassalam" VALUES(16,41,'16:41','One koji se isele Allaha radi, nakon što su bili progonjeni, Mi ćemo još na ovom svijetu na lijepo mjesto smjestiti; a nagrada na onom svijetu bit će još veća – kad bi oni samo znali.');
INSERT INTO "bs_darassalam" VALUES(16,42,'16:42','onima koji budu trpjeli i u Gospodara svog se uzdali.');
INSERT INTO "bs_darassalam" VALUES(16,43,'16:43','Mi smo i prije tebe samo ljude kao poslanike slali i objavljivali im – pitajte sljedbenike Knjige ako ne znate.');
INSERT INTO "bs_darassalam" VALUES(16,44,'16:44','jasne dokaze i knjige. A tebi objavljujemo Kur’an da bi objasnio ljudima ono što im se objavljuje, i da bi oni razmislili.');
INSERT INTO "bs_darassalam" VALUES(16,45,'16:45','A zar su sigurni oni koji podmuklosti snuju da ih Allah neće u zemlju utjerati ili da im neće, odakle ne mogu ni pomisliti, kazna doći.');
INSERT INTO "bs_darassalam" VALUES(16,46,'16:46','ili da ih neće na putovanjima njihovim kazniti – oni neće umaći.');
INSERT INTO "bs_darassalam" VALUES(16,47,'16:47','ili da ih neće kazniti dok u strahu strepe? Ali, Gospodar vaš je doista blag i milostiv.');
INSERT INTO "bs_darassalam" VALUES(16,48,'16:48','Zar oni ne vide da sve ono što je Allah stvorio sad desno, sad lijevo, pruža sjene svoje Allahu sedždu čineći i da je sve to pokorno?');
INSERT INTO "bs_darassalam" VALUES(16,49,'16:49','Allahu sedždu čini sve živo na nebesima i na Zemlji, i meleci, a oni se ne ohole,');
INSERT INTO "bs_darassalam" VALUES(16,50,'16:50','boje se Gospodara svog, Koji je iznad njih, i čine ono što im se naredi.');
INSERT INTO "bs_darassalam" VALUES(16,51,'16:51','Allah kaže: “Dvojici bogova se ne klanjajte – samo je jedan Bog – i samo se Mene bojte!”');
INSERT INTO "bs_darassalam" VALUES(16,52,'16:52','Sve što je na nebesima i na Zemlji Njegovo je i Njemu treba uvijek poslušan biti. Zar nekog drugog, osim Allaha, da se bojite?');
INSERT INTO "bs_darassalam" VALUES(16,53,'16:53','Od Allaha je svaka blagodat koju uživate, a čim vas kakva nevolja zadesi, opet od Njega glasno pomoć tražite.');
INSERT INTO "bs_darassalam" VALUES(16,54,'16:54','I kad vam On poslije nevolju otkloni, neki od vas isti čas Gospodara svog s drugim izjednači.');
INSERT INTO "bs_darassalam" VALUES(16,55,'16:55','da bi nezahvalnost pokazali prema onom što smo im Mi dali. Pa uživajte, ali, zbilja, znat ćete!');
INSERT INTO "bs_darassalam" VALUES(16,56,'16:56','višebošci onima koji ništa ne znaju ostavljaju dio hrane koju im Mi dajemo. Allaha mi, bit ćete, sigurno, pitani zato što stalno laži izmišljate.');
INSERT INTO "bs_darassalam" VALUES(16,57,'16:57','Oni Allahu kćeri pripisuju – hvaljen neka je On! – a sebi ono što priželjkuju.');
INSERT INTO "bs_darassalam" VALUES(16,58,'16:58','I kad se nekom od njih javi da mu se rodila kći, lice mu potamni i postaje potišten.');
INSERT INTO "bs_darassalam" VALUES(16,59,'16:59','krije se od ljudi zbog loše vijesti koja mu je dojavljena: da li da je uz poniženje zadrži, ili u prašinu da je zarovi? Kako ružno oni prosuđuju!');
INSERT INTO "bs_darassalam" VALUES(16,60,'16:60','Oni koji u onaj svijet ne vjeruju hrđavih su osobina, a Allah ima svojstva najuzvišenija; On je silan i mudar.');
INSERT INTO "bs_darassalam" VALUES(16,61,'16:61','Kad bi Allah ljude zbog grijehova njihovih kažnjavao, ništa živo na Zemlji ne bi ostavio, ali On ih do roka određenog ostavlja, i kad rok njihov dođe, ni za tren ga jedan ne mogu ni odgoditi ni ubrzati.');
INSERT INTO "bs_darassalam" VALUES(16,62,'16:62','Oni Allahu pripisuju ono prema čemu sami odvratnost osjećaju i njihovi jezici govore laž da njih čeka najljepša nagrada; a njih, nema sumnje, vatra čeka – oni će se u nju prvi potjerati.');
INSERT INTO "bs_darassalam" VALUES(16,63,'16:63','Allaha Nam, Mi smo i prije tebe narodima poslanike slali, ali im je šejtan lijepim predstavljao postupke njihove i on je sad drug njihov, njih čeka patnja nesnosna.');
INSERT INTO "bs_darassalam" VALUES(16,64,'16:64','Mi tebi objavljujemo Knjigu da bi im objasnio ono oko čega se razilaze i da bude vjernicima uputa i milost.');
INSERT INTO "bs_darassalam" VALUES(16,65,'16:65','Allah vodu s neba spušta i njome život mrtvoj zemlji vraća! To je, zaista, dokaz za ljude koji hoće da čuju.');
INSERT INTO "bs_darassalam" VALUES(16,66,'16:66','Vi imate pouku i u stoci: "Mi vam dajemo da pijete ono što iz njene utrobe dolazi, što nastaje između grizina i krvi; mlijeko je to, čisto i ukusno onima koji ga piju.');
INSERT INTO "bs_darassalam" VALUES(16,67,'16:67','A od plodova palmi i loze pripremate piće i dobru opskrbu. To je, doista, dokaz onima koji pameti imaju.');
INSERT INTO "bs_darassalam" VALUES(16,68,'16:68','Gospodar tvoj je pčelu nadahnuo: “Pravi sebi kuće u brdima i u dubovima i u onom što naprave ljudi.');
INSERT INTO "bs_darassalam" VALUES(16,69,'16:69','Zatim, hrani se svakovrsnim plodovima, pa onda idi stazama Gospodara svog, poslušno!” Iz utroba njihovih izlazi piće različitih boja koje je lijek ljudima. To je, uistinu, dokaz za ljude koji razmišljaju.');
INSERT INTO "bs_darassalam" VALUES(16,70,'16:70','Allah vas stvara, a poslije vam duše uzima; među vama ima onih koji senilnost - duboku starost dožive, pa opet ne znaju nakon što su znanje imali. Allah je, zaista, Onaj Koji sve zna i svemoćan je.');
INSERT INTO "bs_darassalam" VALUES(16,71,'16:71','Allah opskrbljujući vas daje jednima više nego drugima. Ali oni kojima je dato više ne daju onima koji su u njihovoj vlasti, a potrebe su im jednake. Zašto nisu na Allahovim blagodatima zahvalni!');
INSERT INTO "bs_darassalam" VALUES(16,72,'16:72','Allah za vas stvara žene od vaše vrste, a od žena vaših daje vam sinove i unuke, a i ukusnim jelima vas opskrbljuje. Pa zašto u laž oni vjeruju, a Allahove blagodati poriču.');
INSERT INTO "bs_darassalam" VALUES(16,73,'16:73','i klanjaju se, pored Allaha, onima koji nisu u stanju da im bilo kakvu hranu daju, ni iz nebesa ni iz zemlje, i koji ništa ne mogu!');
INSERT INTO "bs_darassalam" VALUES(16,74,'16:74','Zato ne navodite Allahu slične! Allah doista zna, a vi ne znate.');
INSERT INTO "bs_darassalam" VALUES(16,75,'16:75','Allah navodi kao primjer roba u tuđem vlasništvu koji ništa nema i onog koga smo Mi bogato obdarili i koji udjeljuje iz toga, i tajno i javno – zar su oni jednaki? Neka je hvaljen Allah! Ali većina njih ne zna.');
INSERT INTO "bs_darassalam" VALUES(16,76,'16:76','Allah vam navodi kao primjer dvojicu ljudi od kojih je jedan gluhonijem, ništa nema i na potpuno je ovisan od svog vlasnika – kud god ga pošalje, nikakva dobra ne donese. Je li on ravan onom koji traži da se pravedno postupa, a i sam je na Pravom putu?');
INSERT INTO "bs_darassalam" VALUES(16,77,'16:77','Allah zna tajne nebesa i Zemlje! A Smak svijeta će u tren oka doći, ili još brže, jer je Allah, uistinu, Svemoćni!');
INSERT INTO "bs_darassalam" VALUES(16,78,'16:78','Allah vas iz trbuha majki vaših izvodi, vi ništa ne znate, i daje vam sluh i vid i razum da biste bili zahvalni.');
INSERT INTO "bs_darassalam" VALUES(16,79,'16:79','Zar oni ne vide kako ptice u prostranstvu nebeskom bez muke lete, njih samo Allah drži. To su, zaista, dokazi ljudima koji budu vjerovali.');
INSERT INTO "bs_darassalam" VALUES(16,80,'16:80','Allah vam daje da u kućama svojim stanujete i daje vam od koža stoke šatore koje lahko nosite kad na put idete i kad konačite, a od vune, dlake i kostrijeti njihove prostirku i korisne stvari – sve dok se ne istroše.');
INSERT INTO "bs_darassalam" VALUES(16,81,'16:81','Od onog što je stvorio – Allah vam daje hlad i skloništa u brdima, i odjeću koja vas čuva od vrućine; daje vam i oklope koji vas u borbi štite; i tako vam upotpunjava blagodat Svoju da biste bili poslušni.');
INSERT INTO "bs_darassalam" VALUES(16,82,'16:82','A ako oni okrenu glave, pa ti si dužan samo jasno objavljivati.');
INSERT INTO "bs_darassalam" VALUES(16,83,'16:83','Oni znaju da je blagodat od Allaha, pa je poslije poriču – većina njih su nevjernici.');
INSERT INTO "bs_darassalam" VALUES(16,84,'16:84','A na Dan kada iz svakog naroda svjedoka podignemo, nevjernicima neće biti dopušteno da se pravdaju, niti će im biti udovoljeno da blagonaklonost za povratak traže.');
INSERT INTO "bs_darassalam" VALUES(16,85,'16:85','Kad oni koji nisu vjerovali ugledaju patnju – ona im se neće ni ublažiti ni odložiti.');
INSERT INTO "bs_darassalam" VALUES(16,86,'16:86','A kad oni koji su Njemu druge ravnim smatrali božanstva svoja ugledaju i reknu: “Gospodaru naš, ovo su božanstva naša; njima smo se klanjali, a ne Tebi”, božanstva će im dobaciti: “Vi ste, uistinu, lažljivci!”');
INSERT INTO "bs_darassalam" VALUES(16,87,'16:87','I oni će se tog Dana Allahu pokoriti, i propast će ono što su potvarali.');
INSERT INTO "bs_darassalam" VALUES(16,88,'16:88','One koji nisu vjerovali i koji su od Allahova puta odvraćali Mi ćemo dvostrukom kaznom kazniti zato što su pravili smutnju.');
INSERT INTO "bs_darassalam" VALUES(16,89,'16:89','A šta će biti onog Dana kad protiv svakog naroda dovedemo po jednog svjedoka, od njega samog, i tebe dovedemo kao svjedoka protiv ovih! Mi tebi objavljujemo Knjigu kao objašnjenje za sve i kao uputu i milost i radosnu vijest za one koji jedino u Njega vjeruju.');
INSERT INTO "bs_darassalam" VALUES(16,90,'16:90','Allah zahtijeva da se svačije pravo poštuje, dobro čini, i da se bližnjima udjeljuje, a razvrat i sve što je odvratno i nasilje zabranjuje – da pouku primite, On vas savjetuje.');
INSERT INTO "bs_darassalam" VALUES(16,91,'16:91','I ispunjavajte obaveze na koje ste se Allahovim imenom obavezali i ne kršite zakletve kad ste ih tvrdo dali, a Allaha kao jamca sebi uzeli, jer Allah zna ono što radite.');
INSERT INTO "bs_darassalam" VALUES(16,92,'16:92','I ne budite kao ona koja bi svoju pređu rasprela kad bi je već bila čvrsto oprela, i ne služite se zakletvama svojim da biste jedni druge prevarili samo zato što je jedno pleme mnogobrojnije od drugog. Allah vas time samo iskušava, a na Sudnjem danu će vam, doista, objasniti ono oko čega ste se razilazili.');
INSERT INTO "bs_darassalam" VALUES(16,93,'16:93','Da Allah hoće, učinio bi vas sljedbenicima jedne vjere, ali On u zabludu odvodi onog koga hoće, a na Pravi put ukazuje onom kome On hoće; i vi ćete doista odgovarati za ono što ste radili.');
INSERT INTO "bs_darassalam" VALUES(16,94,'16:94','I ne služite se zakletvama svojim zato da biste jedni druge varali, da se ne bi pokliznula noga koja čvrsto stoji i da ne biste nesreću iskusili zato što ste od Allahova puta odvraćali; a patnja velika vas još čeka.');
INSERT INTO "bs_darassalam" VALUES(16,95,'16:95','I ne zamjenjujte obavezu datu Allahu za nešto što malo vrijedi – ono što je u Allaha za vas je, da znate, bolje!');
INSERT INTO "bs_darassalam" VALUES(16,96,'16:96','Ono što je u vas – prolazno je, a ono što je u Allaha – vječno je. One koji su trpjeli Mi ćemo, sigurno, nagraditi mnogostrukom nagradom za ono što su činili.');
INSERT INTO "bs_darassalam" VALUES(16,97,'16:97','Onom ko čini dobro, bio muškarac ili žena, a vjernik je, Mi ćemo dati da proživi lijep život i doista ćemo ih u skladu s najboljim što su činili nagraditi.');
INSERT INTO "bs_darassalam" VALUES(16,98,'16:98','Kad hoćeš učiti Kur’an, zatraži od Allaha zaštitu od šejtana prokletog.');
INSERT INTO "bs_darassalam" VALUES(16,99,'16:99','on doista nema nikakve vlasti nad onima koji vjeruju i koji se u Gospodara svog pouzdaju.');
INSERT INTO "bs_darassalam" VALUES(16,100,'16:100','Njegova je vlast jedino nad onima koji njega za zaštitnika uzimaju i koji druge Allahu ravnim smatraju.');
INSERT INTO "bs_darassalam" VALUES(16,101,'16:101','Kad Mi ajet dokinemo drugim – a Allah najbolje zna što objavljuje – oni govore: “Ti samo izmišljaš!” A nije tako, nego većina njih ne zna.');
INSERT INTO "bs_darassalam" VALUES(16,102,'16:102','Reci: “Od Gospodara tvog objavljuje ga melek Džibril kao istinu, da još više učvrsti vjernike u vjerovanju, i da bude putokaz i radosna vijest svim muslimanima.”');
INSERT INTO "bs_darassalam" VALUES(16,103,'16:103','Mi dobro znamo da oni govore: “Poučava ga jedan čovjek!” Jezik onog zbog koga oni krivo govore jest jezik tuđina, a ovaj Kur’an je na jasnom arapskom jeziku.');
INSERT INTO "bs_darassalam" VALUES(16,104,'16:104','Onima koji neće da vjeruju u Allahove dokaze Allah, sigurno, neće ukazati na Pravi put, i njih čeka patnja nesnosna.');
INSERT INTO "bs_darassalam" VALUES(16,105,'16:105','Usuđuju se da laži izmišljaju samo oni koji u Allahove riječi ne vjeruju, i oni su pravi lažljivci.');
INSERT INTO "bs_darassalam" VALUES(16,106,'16:106','Onog ko učini kufr u Allaha nakon što je u Njega vjerovao – osim ako bude na to primoran, a srce mu ostane čvrsto u vjeri – čeka Allahova kazna. One kojima se nevjerstvo bude mililo stići će srdžba Allahova i njih čeka patnja velika.');
INSERT INTO "bs_darassalam" VALUES(16,107,'16:107','zato što više vole život na ovom nego na onom svijetu, a Allah neće ukazati na Pravi put onima koji neće da vjeruju.');
INSERT INTO "bs_darassalam" VALUES(16,108,'16:108','To su oni čija je srca i sluh i vid Allah zapečatio, i oni su zaista nemarni.');
INSERT INTO "bs_darassalam" VALUES(16,109,'16:109','oni će, nema sumnje, na Ahiretu pravi gubitnici.');
INSERT INTO "bs_darassalam" VALUES(16,110,'16:110','Gospodar tvoj će onima koji se isele, nakon što su zlostavljani bili, pa se onda budu borili i sve strpljivo podnosili – Gospodar tvoj će im, poslije toga, doista oprostiti i samilostan biti.');
INSERT INTO "bs_darassalam" VALUES(16,111,'16:111','na Dan u kojem će svaki čovjek samo o sebi brinuti i u kome će se svakom čovjeku za djela njegova puna nagrada ili kazna dati, nepravda im se neće učiniti!');
INSERT INTO "bs_darassalam" VALUES(16,112,'16:112','Allah navodi kao primjer grad, siguran i spokojan, kome je u obilju dolazila opskrba sa svih strana, pa je nezahvalan na Allahovim blagodatima postao, pa mu je Allah zbog onog što je radio dao da iskusi i glad i strah.');
INSERT INTO "bs_darassalam" VALUES(16,113,'16:113','I njima je došao poslanik, jedan od njih, ali su ga oni lažljivcem nazvali, i njih je stigla kazna zato što su nepravedni bili.');
INSERT INTO "bs_darassalam" VALUES(16,114,'16:114','Hranite se dopuštenim i lijepim jelima koja vam Allah daruje i budite zahvalni Allahu na blagodatima Njegovim, ako se samo Njemu klanjate.');
INSERT INTO "bs_darassalam" VALUES(16,115,'16:115','On vam zabranjuje jedino strv, i krv, i svinjsko meso, i onu stoku koja je zaklana u nečije drugo, a ne u Allahovo ime. A onom ko bude primoran, ali ne iz želje, samo toliko da utoli glad – pa Gospodar tvoj će, zaista, oprostiti i samilostan biti.');
INSERT INTO "bs_darassalam" VALUES(16,116,'16:116','I ne govorite neistine jezicima svojim: “Ovo je dopušteno, a ovo je zabranjeno” – da biste tako o Allahu neistine iznosili. Oni koji o Allahu govore neistine neće uspjeti.');
INSERT INTO "bs_darassalam" VALUES(16,117,'16:117','kratko će uživati, i njih će strašna patnja čekati.');
INSERT INTO "bs_darassalam" VALUES(16,118,'16:118','Jevrejima smo zabranili ono o čemu smo ti prije kazivali; Mi njima nismo nepravedni bili, oni su sami sebi nepravdu nanijeli.');
INSERT INTO "bs_darassalam" VALUES(16,119,'16:119','Onima koji urade zlo iz neznanja, pa se kasnije pokaju i poprave, Gospodar tvoj će poslije toga, sigurno, oprostiti i samilostan biti.');
INSERT INTO "bs_darassalam" VALUES(16,120,'16:120','Ibrahim je bio primjer čestitosti, pokoran Allahu, pravi vjernik, nije druge smatrao Allahu ravnim.');
INSERT INTO "bs_darassalam" VALUES(16,121,'16:121','i bio je zahvalan na blagodatima Njegovim; On je njega izabrao i na Pravi put izveo.');
INSERT INTO "bs_darassalam" VALUES(16,122,'16:122','i Mi smo mu sačuvali lijep spomen na ovom svijetu, a na Ahiretu će doista biti među onima.');
INSERT INTO "bs_darassalam" VALUES(16,123,'16:123','Poslije smo tebi objavili: “Slijedi vjeru Ibrahimovu, vjeru pravu, on nije Allahu druge smatrao ravnim!”');
INSERT INTO "bs_darassalam" VALUES(16,124,'16:124','Svetkovanje subote je propisano onima koji su imali različita mišljenja o njoj, i Gospodar tvoj će na Sudnjem danu njima presuditi u onom oko čega su se razišli.');
INSERT INTO "bs_darassalam" VALUES(16,125,'16:125','Na put Gospodara svog mudro i lijepim savjetom pozivaj i s njima na najljepši način raspravljaj. Gospodar tvoj zna one koji su zalutali s puta Njegova, i On zna one koji su na Pravom putu.');
INSERT INTO "bs_darassalam" VALUES(16,126,'16:126','Ako hoćete da na nepravdu uzvratite, onda učinite to samo u onolikoj mjeri koliko vam je učinjeno; a ako otrpite, to je, doista, bolje za strpljive.');
INSERT INTO "bs_darassalam" VALUES(16,127,'16:127','Strpljiv budi! Ali, strpljiv ćeš biti samo uz Allahovu pomoć. I ne tuguj za njima, i neka ti ne bude teško zbog spletkarenja njihova.');
INSERT INTO "bs_darassalam" VALUES(16,128,'16:128','Allah je zaista na strani onih koji se Njega boje i grijeha klone i koji dobra djela čine.');
INSERT INTO "bs_darassalam" VALUES(17,1,'17:1','Hvaljen neka je Onaj Koji je u jednom času noći preveo Svog roba iz Hrama časnog u Hram daleki, čiju smo okolinu blagoslovili kako bismo mu neka znamenja Naša pokazali. On, uistinu, sve čuje i sve vidi.');
INSERT INTO "bs_darassalam" VALUES(17,2,'17:2','A Musau smo Knjigu dali i uputstvom je sinovima Israilovim učinili: “Mjesto Mene – Gospodara drugog ne uzimajte...');
INSERT INTO "bs_darassalam" VALUES(17,3,'17:3','o potomci onih koje smo sa Nuhom nosili!” On je, doista, bio rob zahvalni.');
INSERT INTO "bs_darassalam" VALUES(17,4,'17:4','I Mi smo u Knjizi objavili sinovima Israilovim: “Vi ćete doista dva puta nered na Zemlji učiniti i preko mjere oholi postati.');
INSERT INTO "bs_darassalam" VALUES(17,5,'17:5','I kad dođe vrijeme prve od dvije prijetnje, poslat ćemo protiv vas robove Naše, silno moćne, oni će uzduž i poprijeko zemlju vašu pregaziti, i prijetnja će se ispuniti.');
INSERT INTO "bs_darassalam" VALUES(17,6,'17:6','Zatim ćemo vam dati pobjedu protiv njih i pomoći ćemo vas imecima i sinovima i učinit ćemo vas brojnijim.');
INSERT INTO "bs_darassalam" VALUES(17,7,'17:7','Sve što činite – činite sebi, dobro i zlo. A kad dođe vrijeme druge prijetnje, poslat ćemo ih da na licima vašim tugu i jad ostave i da u Hram kao i prvi put, ponovo provale i da sve što osvoje do temelja poruše.');
INSERT INTO "bs_darassalam" VALUES(17,8,'17:8','I Gospodar će vam se vaš opet smilovati; ako vi ponovo započnete, započet ćemo i Mi.” A džehennem smo za nevjernike tamnicom učinili.');
INSERT INTO "bs_darassalam" VALUES(17,9,'17:9','Ovaj Kur’an vodi jedinom ispravnom putu, i vjernicima koji čine dobra djela donosi radosnu vijest da ih čeka nagrada velika...');
INSERT INTO "bs_darassalam" VALUES(17,10,'17:10','a da smo za one koji u onaj svijet ne vjeruju – bolnu patnju pripremili.');
INSERT INTO "bs_darassalam" VALUES(17,11,'17:11','Baš kao što moli za ono što je dobro, čovjek nekada moli i za ono što ne valja. Čovjek je zaista sklon da ishitren bude.');
INSERT INTO "bs_darassalam" VALUES(17,12,'17:12','I Mi smo noć i dan kao dva znamenja učinili: znamenje za noć smo uklonili, a znamenje za dan smo vidnim učinili kako biste mogli tražiti od Gospodara svog blagodati i da biste broj godina znali i da biste vrijeme računali – i sve smo potanko objasnili.');
INSERT INTO "bs_darassalam" VALUES(17,13,'17:13','I svakom ćemo čovjeku ono što uradi o vrat privezati, a na Sudnjem danu knjigu ćemo mu otvorenu pokazati...');
INSERT INTO "bs_darassalam" VALUES(17,14,'17:14','“Čitaj knjigu svoju, dosta ti je danas što ćeš svoj račun polagati!”');
INSERT INTO "bs_darassalam" VALUES(17,15,'17:15','Onaj koji ide Pravim putem, od toga će samo on koristi imati; a onaj ko luta – na svoju štetu luta, i nijedan grešnik neće tuđe grijehe nositi. A Mi nijedan narod nismo kaznili dok poslanika nismo poslali!');
INSERT INTO "bs_darassalam" VALUES(17,16,'17:16','Kad hoćemo jedan grad uništiti, onima koji su u njemu na raskoš navikli prepustimo da se razvratu odaju i da tako zasluže kaznu, pa ga onda do temelja razrušimo.');
INSERT INTO "bs_darassalam" VALUES(17,17,'17:17','I koliko smo samo naroda poslije Nuha uništili! A dovoljno je to što Gospodar tvoj zna i vidi grijehe robova Svojih.');
INSERT INTO "bs_darassalam" VALUES(17,18,'17:18','Onome koji želi ovaj - kratkotrajni svijet, Mi mu na njemu brzo dajemo - što hoćemo i kome hoćemo, ali ćemo mu poslije Džehennem pripremiti, u kome će se prezren i odbačen peći.');
INSERT INTO "bs_darassalam" VALUES(17,19,'17:19','A onaj ko želi onaj svijet i trudi se da ga zasluži, a vjernik je, trud će mu hvale vrijedan biti.');
INSERT INTO "bs_darassalam" VALUES(17,20,'17:20','Svima njima, i jednima i drugima, dajemo darove Gospodara tvog; a darovi Gospodara tvog nisu nikom zabranjeni.');
INSERT INTO "bs_darassalam" VALUES(17,21,'17:21','Gledaj kako jednima dajemo prednost nad drugima; a na onom svijetu razlika u stupnjevima i prednostima bit će, doista, veća.');
INSERT INTO "bs_darassalam" VALUES(17,22,'17:22','Ne stavljaj uz Allaha nekog drugog boga da ne bi osudu zaslužio i bez podrške ostao.');
INSERT INTO "bs_darassalam" VALUES(17,23,'17:23','Gospodar tvoj zapovijeda da se samo Njemu klanjate i da roditeljima dobročinstvo činite. Kad jedno od njih dvoje, ili oboje, kod tebe starost dožive, ne reci im ni: “Uh!” – i ne podvikni na njih, i obraćaj im se riječima poštovanja punim.');
INSERT INTO "bs_darassalam" VALUES(17,24,'17:24','Budi prema njima pažljiv i ponizan i reci: “Gospodaru moj, smiluj im se, oni su mene, kad sam bio dijete, njegovali!”');
INSERT INTO "bs_darassalam" VALUES(17,25,'17:25','Gospodar vaš dobro zna šta je u dušama vašim: ako budete poslušni – pa Allah će doista oprostiti onima koji se kaju.');
INSERT INTO "bs_darassalam" VALUES(17,26,'17:26','Daj bližnjem svom pravo njegovo, i siromahu, i putniku, ali ne rasipaj mnogo...');
INSERT INTO "bs_darassalam" VALUES(17,27,'17:27','jer su rasipnici braća šejtanova, a šejtan je Gospodaru svom nezahvalan.');
INSERT INTO "bs_darassalam" VALUES(17,28,'17:28','A ako moraš od njih glavu okrenuti, jer i sam od Gospodara svog milost tražiš i njoj se nadaš, onda im barem koju lijepu riječ reci.');
INSERT INTO "bs_darassalam" VALUES(17,29,'17:29','Ne drži ruku svoju stisnutu, a ni posve otvorenu da ne bi prijekor zaslužio i bez ičega ostao...');
INSERT INTO "bs_darassalam" VALUES(17,30,'17:30','Gospodar tvoj pruža obilnu opskrbu onom kome hoće, a i ograničava je, jer zna i vidi robove Svoje.');
INSERT INTO "bs_darassalam" VALUES(17,31,'17:31','Ne ubijajte djecu svoju iz straha od neimaštine, i njih i vas Mi hranimo, jer je ubijati njih doista veliki grijeh.');
INSERT INTO "bs_darassalam" VALUES(17,32,'17:32','I što dalje od bluda, jer to je razvrat, kako je to ružan put!');
INSERT INTO "bs_darassalam" VALUES(17,33,'17:33','I ne ubijajte nikog koga je Allah zabranio ubiti, osim kad pravda zahtijeva! A ako je neko, ni kriv ni dužan, ubijen, onda njegovu nasljedniku dajemo vlast, ali neka ni on ne prekoračuje granicu u ubijanju, ta njemu je data vlast.');
INSERT INTO "bs_darassalam" VALUES(17,34,'17:34','A od imetka siročeta – što dalje! Osim ako ga želite unaprijediti, sve dok ne postane punoljetno. I ispunjavajte obavezu, jer će se za obavezu zaista odgovarati!');
INSERT INTO "bs_darassalam" VALUES(17,35,'17:35','Napunite mjeru kad mjerite na litru i pravo mjerite na kantaru! To je bolje i posljedice su ljepše.');
INSERT INTO "bs_darassalam" VALUES(17,36,'17:36','Ne povodi se za onim što ne znaš! I sluh, i vid, i razum – za sve to će se, zaista, odgovarati.');
INSERT INTO "bs_darassalam" VALUES(17,37,'17:37','Ne hodi po zemlji nadmeno, jer zemlju ne možeš probiti ni brda u visinu dostići...');
INSERT INTO "bs_darassalam" VALUES(17,38,'17:38','sve je to ružno, Gospodaru tvome mrsko.');
INSERT INTO "bs_darassalam" VALUES(17,39,'17:39','To je mudrost koju ti Gospodar tvoj objavljuje. I ne dodaji Allahu drugog boga da ne bi u džehennem bio bačen, prekoren i od milosti Njegove udaljen.');
INSERT INTO "bs_darassalam" VALUES(17,40,'17:40','Zar je vaš Gospodar vas sinovima obdario, a Sebi, kao kćeri, meleke uzeo? Vi, zaista, izgovarate krupne riječi.');
INSERT INTO "bs_darassalam" VALUES(17,41,'17:41','Mi u ovom Kur’anu objašnjavamo da bi oni pouku izvukli, ali ih on sve više otuđuje.');
INSERT INTO "bs_darassalam" VALUES(17,42,'17:42','Reci: “Da pored Njega postoje drugi bogovi, kao što oni govore, oni bi onda potražili put do Vlasnika Arša.”');
INSERT INTO "bs_darassalam" VALUES(17,43,'17:43','Hvaljen neka je On i vrlo visoko iznad onog što oni govore!');
INSERT INTO "bs_darassalam" VALUES(17,44,'17:44','Njega veličaju sedmera nebesa, i Zemlja, i oni na njima – i ne postoji ništa što Ga ne veliča, hvaleći Ga – ali vi ne razumijete veličanje njihovo. On je doista blag i mnogo prašta.');
INSERT INTO "bs_darassalam" VALUES(17,45,'17:45','Kad čitaš Kur’an između tebe i onih koji u onaj svijet ne vjeruju, Mi zastor nevidljivi stavimo...');
INSERT INTO "bs_darassalam" VALUES(17,46,'17:46','a na srca njihova pokrivače da ga ne bi razumjeli, i gluhim ih učinimo. I kad ti spomeneš Gospodara svog u Kur’anu, Njega jedinog, oni se preplašeni daju u bijeg.');
INSERT INTO "bs_darassalam" VALUES(17,47,'17:47','Mi dobro znamo šta oni žele čuti kad te dolaze prisluškivati, i o čemu se sašaptavaju kad nevjernici govore: “Vi slijedite samo opčinjena čovjeka!”');
INSERT INTO "bs_darassalam" VALUES(17,48,'17:48','Vidi šta o tebi oni govore, pa onda lutaju i ne mogu naći Pravi put.');
INSERT INTO "bs_darassalam" VALUES(17,49,'17:49','Oni govore: “Zar kad se u kosti i prašinu pretvorimo, zar ćemo, kao nova bića, doista biti oživljeni?”');
INSERT INTO "bs_darassalam" VALUES(17,50,'17:50','Reci: “Hoćete, i da ste kamenje ili gvožđe...');
INSERT INTO "bs_darassalam" VALUES(17,51,'17:51','ili bilo kakvo stvorenje za koje mislite da ne može biti oživljeno.” “A ko će nas u život vratiti?”, upitat će oni, a ti reci: “Onaj Koji vas je i prvi put stvorio”, a oni će prema tebi odmahnuti glavama svojim i upitati: “Kad to?” Ti reci: “Možda ubrzo!');
INSERT INTO "bs_darassalam" VALUES(17,52,'17:52','Bit će to onog Dana kad vas On pozove, i odazvat ćete se, hvaleći Ga, i pomislit ćete da ste ostali samo malo vremena.”');
INSERT INTO "bs_darassalam" VALUES(17,53,'17:53','Reci robovima Mojim da govore samo lijepe riječi jer bi šejtan mogao posijati neprijateljstvo među njima, šejtan je, doista, čovjekov otvoreni neprijatelj.');
INSERT INTO "bs_darassalam" VALUES(17,54,'17:54','Gospodar vaš dobro vas poznaje: ako hoće, On će vam se smilovati ili će vas, ako hoće, na muke staviti. – A Mi tebi nismo dali vlast nad njima.');
INSERT INTO "bs_darassalam" VALUES(17,55,'17:55','Gospodar tvoj dobro zna one na nebesima i one na Zemlji. Mi smo jedne vjerovjesnike nad drugima odlikovali, a Davudu smo Zebur dali.');
INSERT INTO "bs_darassalam" VALUES(17,56,'17:56','Reci: “Molite se onima koje, pored Njega, smatrate bogovima – ali vas oni neće moći nevolje osloboditi niti je izmijeniti.”');
INSERT INTO "bs_darassalam" VALUES(17,57,'17:57','Oni kojima se oni mole sami traže načina kako će se što više Gospodaru svom približiti, i nadaju se milosti Njegovoj i plaše se kazne Njegove. A kazne Gospodara tvog svako se treba čuvati.');
INSERT INTO "bs_darassalam" VALUES(17,58,'17:58','I ne postoji nijedno naselje koje Mi prije Sudnjeg dana nećemo uništiti ili ga teškoj muci izložiti – to je u Knjizi zapisano.');
INSERT INTO "bs_darassalam" VALUES(17,59,'17:59','A da ti pošaljemo velike znakove spriječilo Nas je samo to što su ih prijašnji narodi lažnim smatrali; Semudu smo kao očiti znak kamilu dali, pa su u vezi s njom zulum učinili. A velike znakove šaljemo samo za zastrašivanje.');
INSERT INTO "bs_darassalam" VALUES(17,60,'17:60','I kada smo ti rekli: “Sve ljude Gospodar tvoj, zaista, obuhvata! A prizor koji smo ti pokazali i drvo prokleto, u Kur’anu spomenuto, iskušenje su za ljude. Mi ih zastrašujemo, ali to samo njihovu osionost pojačava.”');
INSERT INTO "bs_darassalam" VALUES(17,61,'17:61','I kad rekosmo melekima: “Poklonite se Ademu!”, oni se svi, osim Iblisa, pokloniše. “Zar da se poklonim onom koga si Ti od ilovače stvorio?”, reče.');
INSERT INTO "bs_darassalam" VALUES(17,62,'17:62','“Reci mi”, reče onda, “evo ovog koga si iznad mene uzdigao: ako me ostaviš do Smaka svijeta sigurno ću, osim malobrojnih, nad potomstvom njegovim zagospodariti.”');
INSERT INTO "bs_darassalam" VALUES(17,63,'17:63','“Odlazi!”, reče On. “Onima koji se za tobom budu poveli i tebi – kazna džehennemska bit će vam puna kazna.');
INSERT INTO "bs_darassalam" VALUES(17,64,'17:64','I zavódi glasom svojim koga možeš i potjeraj na njih svoju konjicu i svoju pješadiju, i budi im ortak u imecima, i u djeci, i daji im obećanja – a šejtan ih samo obmanjuje...');
INSERT INTO "bs_darassalam" VALUES(17,65,'17:65','ali ti, doista, nećeš imati nikakve vlasti nad robovima Mojim!” A Gospodar je tvoj dovoljan kao zaštitnik!');
INSERT INTO "bs_darassalam" VALUES(17,66,'17:66','Gospodar vaš, vas radi, pokreće lađe po moru da biste tražili Njegove blagodati, jer je On prema vama milostiv.');
INSERT INTO "bs_darassalam" VALUES(17,67,'17:67','Kad vas na moru nevolja zadesi, tada nestanu oni koje inače prizivate, postoji samo On. A kad vas On na kopno spasi, vi se okrećete, a čovjek je itekakav nezahvalnik!');
INSERT INTO "bs_darassalam" VALUES(17,68,'17:68','Zar ste sigurni da vas On neće u zemlju utjerati ili da protiv vas neće pješčanu oluju poslati, pa da onda sebi zaštitnika nećete naći?');
INSERT INTO "bs_darassalam" VALUES(17,69,'17:69','Ili, zar ste sigurni da vas On po drugi put neće na more izvesti, i buru na vas poslati i potopiti vas zbog toga što ste bili nezahvalni? Tad ne biste nikog našli ko bi Nas zbog vas na odgovornost pozvao.');
INSERT INTO "bs_darassalam" VALUES(17,70,'17:70','Mi smo sinove Ademove, doista, odlikovali; dali smo im da kopnom i morem putuju, i opskrbili ih ukusnim jelima, i dali im velike prednosti nad mnogima koje smo stvorili.');
INSERT INTO "bs_darassalam" VALUES(17,71,'17:71','A na Dan kad pozovemo sve ljude s vođom njihovim, oni kojima se knjiga njihova da u desnu ruku čitat će knjige svoje i neće im biti ni koliko trun jedan učinjena nepravda...');
INSERT INTO "bs_darassalam" VALUES(17,72,'17:72','onaj ko je na ovom svijetu bio slijep bit će slijep i na Ahiretu i daleko od svakog dobra.');
INSERT INTO "bs_darassalam" VALUES(17,73,'17:73','I zamalo da te oni odvrate od onog što ti Mi objavljujemo, da bi protiv nas nešto drugo iznio, i tad bi te oni smatrali prijateljem.');
INSERT INTO "bs_darassalam" VALUES(17,74,'17:74','A da te nismo učinili čvrstim, gotovo da bi im se malo priklonio...');
INSERT INTO "bs_darassalam" VALUES(17,75,'17:75','i tad bismo ti doista dali da iskusiš dvostruku muku u životu i dvostruku patnju poslije smrti; tad ne bi nikog našao ko bi ti protiv Nas pomogao.');
INSERT INTO "bs_darassalam" VALUES(17,76,'17:76','A oni su te toliko na zemlji uznemiravali da bi te iz nje istjerali, ali tad ni oni u njoj ne bi dugo, poslije tebe, ostali...');
INSERT INTO "bs_darassalam" VALUES(17,77,'17:77','jer tako je bilo sa svima onima koji su poslanike protjerali, koje smo prije tebe poslali, i ni ti nećeš naići na odstupanje od zakona Našeg.');
INSERT INTO "bs_darassalam" VALUES(17,78,'17:78','Obavljaj namaz kad Sunce s polovine neba krene, pa do noćne tmine, i Kur’an u namazu u zoru uči, jer učenju Kur’ana u namazu u zoru mnogi prisustvuju.');
INSERT INTO "bs_darassalam" VALUES(17,79,'17:79','I bdij dio noći u namazu - tebi dodatnom; na Ahiretu će te tvoj Gospodar na hvaljeni položaj postaviti.');
INSERT INTO "bs_darassalam" VALUES(17,80,'17:80','reci: "Gospodaru moj, uvedi me uvodom Istine i izvedi me ishodom Istine! I daruj mi od Sebe moć koja će mi pomoći!"');
INSERT INTO "bs_darassalam" VALUES(17,81,'17:81','I reci: “Došla je istina, a nestalo je laži – laž, zaista, nestaje!”');
INSERT INTO "bs_darassalam" VALUES(17,82,'17:82','Mi objavljujemo u Kur’anu ono što je lijek i milost vjernicima, a nevjernicima on samo povećava propast.');
INSERT INTO "bs_darassalam" VALUES(17,83,'17:83','Kad čovjeku kakvu blagodat darujemo, on se okreće i oholo udaljava, a kad ga zadesi zlo, onda očajava.');
INSERT INTO "bs_darassalam" VALUES(17,84,'17:84','Reci: “Svako po svom radi, a samo Gospodar vaš zna ko je na Pravom putu.”');
INSERT INTO "bs_darassalam" VALUES(17,85,'17:85','Pitaju te o duši. Reci: “Šta je duša samo Gospodar moj zna, a vama je dato samo malo znanja.”');
INSERT INTO "bs_darassalam" VALUES(17,86,'17:86','A da hoćemo, Mi bismo učinili da iščezne ono što smo ti objavili, i ti, poslije, ne bi nikog našao ko bi ti protiv Nas pomogao...');
INSERT INTO "bs_darassalam" VALUES(17,87,'17:87','ali Gospodar tvoj je prema tebi milostiv i Njegova dobrota prema tebi zaista je velika.');
INSERT INTO "bs_darassalam" VALUES(17,88,'17:88','Reci: “Kad bi se svi ljudi i džini udružili da sačine jedan ovakav Kur’an, oni, takav kao što je on, ne bi sačinili, pa makar jedni drugima pomagali.”');
INSERT INTO "bs_darassalam" VALUES(17,89,'17:89','Mi u ovom Kur’anu objašnjavamo ljudima svakojake primjere, ali većina ljudi nikako neće da vjeruje...');
INSERT INTO "bs_darassalam" VALUES(17,90,'17:90','i govore: “Nećemo ti vjerovati sve dok nam iz zemlje izvorsku vodu ne izvedeš...');
INSERT INTO "bs_darassalam" VALUES(17,91,'17:91','ili dok ne budeš imao vrt od palmi i loze, pa da kroz njeg svukuda rijeke provedeš...');
INSERT INTO "bs_darassalam" VALUES(17,92,'17:92','ili dok na nas nebo u parčadima ne oboriš, kao što tvrdiš; ili dok Allaha i meleke kao jamce ne dovedeš...');
INSERT INTO "bs_darassalam" VALUES(17,93,'17:93','ili dok ne budeš imao kuću od zlata ili dok se na nebo ne uspneš; a nećemo vjerovati ni da si se uspeo sve dok nam ne doneseš Knjigu da je čitamo.” Reci: “Hvaljen neka je Gospodar moj! – Zar ja nisam samo čovjek, poslanik?”');
INSERT INTO "bs_darassalam" VALUES(17,94,'17:94','A ljude je, kad im je dolazila uputa, odvraćalo od vjerovanja samo to što su govorili: “Zar je Allah kao poslanika čovjeka poslao?”');
INSERT INTO "bs_darassalam" VALUES(17,95,'17:95','Reci: “Kad bi na Zemlji meleki smireno hodili, Mi bismo im s neba meleka za poslanika poslali.”');
INSERT INTO "bs_darassalam" VALUES(17,96,'17:96','Reci: “Allah je dovoljan svjedok meni i vama, jer On zna i vidi robove Svoje.”');
INSERT INTO "bs_darassalam" VALUES(17,97,'17:97','Onaj kome Allah ukaže na Pravi put – na Pravom je putu, a onom koga u zabludu odvede – tome, mimo Njega, nećeš naći zaštitnika. Mi ćemo ih, na Sudnjem danu, sakupiti, licem zemlji okrenute, slijepe, nijeme i gluhe. Boravište njihovo bit će džehennem – kad god mu plamen jenja, pojačat ćemo im oganj.');
INSERT INTO "bs_darassalam" VALUES(17,98,'17:98','To će im biti kazna zato što u dokaze Naše nisu vjerovali i što su govorili: “Zar kad postanemo kosti i prah, zar ćemo kao nova stvorenja, doista, biti oživljeni?”');
INSERT INTO "bs_darassalam" VALUES(17,99,'17:99','Zar oni ne znaju da je Allah, Stvoritelj nebesa i Zemlje, kadar stvoriti slične njima i da im je već odredio čas oživljenja u koji nema sumnje? A nevjernici samo poriču.');
INSERT INTO "bs_darassalam" VALUES(17,100,'17:100','Reci: “Da vi posjedujete riznice milosti Gospodara moga, i tad biste škrtarili iz straha da ne potrošite – čovjek je uistinu tvrdica.');
INSERT INTO "bs_darassalam" VALUES(17,101,'17:101','Mi smo Musau devet očevidnih znamenja dali, pa upitaj sinove Israilove kad je precima njihovim došao i kad mu je faraon rekao: “Ja mislim, o Musa, da si ti doista opčinjen”...');
INSERT INTO "bs_darassalam" VALUES(17,102,'17:102','da je odgovorio: “Ti znaš da ovo nije dao niko drugi nego Gospodar nebesa i Zemlje, kao očigledna znamenja, i ja mislim da ćeš ti, o faraone, sigurno nastradati.”');
INSERT INTO "bs_darassalam" VALUES(17,103,'17:103','I faraon odluči da ih iz zemlje istjera, pa Mi potopismo i njega i one koji su bili s njime – sve.');
INSERT INTO "bs_darassalam" VALUES(17,104,'17:104','I poslije toga rekosmo sinovima Israilovim: "Naselite se u zemlji, pa kad vrijeme ahireta nastupi, sve ćemo vas zajedno dovesti."');
INSERT INTO "bs_darassalam" VALUES(17,105,'17:105','Mi Kur’an pun mudrosti objavljujemo, i na istinit način se on objavljuje. A tebe smo poslali samo zato da radosne vijesti donosiš i da opominješ.');
INSERT INTO "bs_darassalam" VALUES(17,106,'17:106','I kao Kur’an, sve dio po dio ga objavljujemo da bi ga ti ljudima malo-pomalo kazivao, i prema potrebi ga objavljujemo.');
INSERT INTO "bs_darassalam" VALUES(17,107,'17:107','Reci: “Vjerovali u njega ili ne vjerovali, oni kojima je još prije objavljivanja njegova dato znanje padaju licem na tlo kad im se on čita...');
INSERT INTO "bs_darassalam" VALUES(17,108,'17:108','i govore: ‘Hvaljen neka je Gospodar naš, obećanje Gospodara našeg se ispunilo!’');
INSERT INTO "bs_darassalam" VALUES(17,109,'17:109','I padaju licem na tlo plačući, i on im uvećava strahopoštovanje.”');
INSERT INTO "bs_darassalam" VALUES(17,110,'17:110','Reci: “Zovite: ‘Allah!’ ili zovite: ‘Milostivi!’ – a kako god Ga budete zvali, Njegova su imena najljepša.” Ne izgovaraj na sav glas Kur’an kad molitvu obavljaš, a i ne prigušuj ga; traži sredinu između toga...');
INSERT INTO "bs_darassalam" VALUES(17,111,'17:111','i reci: “Hvaljen neka je Allah, Koji Sebi nije uzeo djeteta i Koji u vlasti nema ortaka, i Kome ne treba zaštitnik zbog nemoći – i hvaleći Ga veličaj!”');
INSERT INTO "bs_darassalam" VALUES(18,1,'18:1','Sva hvala neka je Allahu Koji Svome robu objavljuje Knjigu, i nije je dao iskrivljenu,');
INSERT INTO "bs_darassalam" VALUES(18,2,'18:2','ispravnu, da teškom kaznom, koju će On dati, opomene, a da vjernike koji čine dobra djela divnom nagradom obraduje...');
INSERT INTO "bs_darassalam" VALUES(18,3,'18:3','u kojoj će vječno boraviti...');
INSERT INTO "bs_darassalam" VALUES(18,4,'18:4','i da opomene one koji govore: “Allah je Sebi uzeo sina.”');
INSERT INTO "bs_darassalam" VALUES(18,5,'18:5','O tome oni ništa ne znaju, a ni preci njihovi. Kako krupna riječ izlazi iz usta njihovih! Oni ne govore drugo do neistinu!');
INSERT INTO "bs_darassalam" VALUES(18,6,'18:6','Pa zar ćeš ti za njima od tuge svisnuti, ako oni u govor ovaj neće povjerovati?');
INSERT INTO "bs_darassalam" VALUES(18,7,'18:7','Sve što je na Zemlji Mi smo kao ukras njoj stvorili da iskušamo ljude ko će od njih bolje postupati');
INSERT INTO "bs_darassalam" VALUES(18,8,'18:8','a Mi ćemo nju i golom ledinom učiniti.');
INSERT INTO "bs_darassalam" VALUES(18,9,'18:9','Misliš li ti da su samo stanovnici Pećine, čija su imena na ploči napisana, bili čudo među čudima Našim?');
INSERT INTO "bs_darassalam" VALUES(18,10,'18:10','Kad se nekoliko momaka u pećini sklonilo pa reklo: “Gospodaru naš, daj nam Svoju milost i pruži nam u ovom našem postupku prisebnost”...');
INSERT INTO "bs_darassalam" VALUES(18,11,'18:11','Mi smo ih u pećini tvrdo uspavali za dugo godina.');
INSERT INTO "bs_darassalam" VALUES(18,12,'18:12','Poslije smo ih probudili da bismo pokazali koja će od dvije skupine bolje ocijeniti koliko su vremena proboravili.');
INSERT INTO "bs_darassalam" VALUES(18,13,'18:13','A Mi tebi kazujemo vijest njihovu po Istini. Tu su bili mladići, vjerovali su u Gospodara svoga, a Mi smo im Uputu još više povećali.');
INSERT INTO "bs_darassalam" VALUES(18,14,'18:14','Osnažili smo bili njihova srca kad su se digli i rekli: “Gospodar naš – Gospodar je nebesa i Zemlje, mi se nećemo pored Njega drugom bogu moliti, jer bismo tad ono što je daleko od istine govorili.');
INSERT INTO "bs_darassalam" VALUES(18,15,'18:15','Narod ovaj naš je mimo Njega druge bogove prihvatio, zašto jasan dokaz nije donio o tome da se treba njima klanjati? A ima li nepravednijeg od onog koji o Allahu iznosi neistinu?');
INSERT INTO "bs_darassalam" VALUES(18,16,'18:16','Kad napustite njih i one kojima umjesto Allahu robuju, sklonite se u pećinu, Gospodar vaš će vas milošću Svojom obasuti i za vas će ono što će vam korisno biti pripremiti.”');
INSERT INTO "bs_darassalam" VALUES(18,17,'18:17','I ti si mogao vidjeti kako Sunce, kad se rađa, obilazi pećinu s desne strane, a kad zalazi – zaobilazi je s lijeve strane, a oni su bili u sredini njezinoj. To je dokaz Allahove moći! Kome Allah ukaže na Pravi put, on će Pravim putem ići, a koga u zabludu odvede, ti mu nećeš naći zaštitnika koji će ga na Pravi put uputiti.');
INSERT INTO "bs_darassalam" VALUES(18,18,'18:18','I pomislio bi da su budni, ali oni su spavali. I Mi smo ih prevrtali sad na desnu, sad na lijevu stranu, a pas njihov, opruženih prednjih šapa, na ulazu je ležao – da si ih vidio, od njih bi pobjegao i strah bi te uhvatio.');
INSERT INTO "bs_darassalam" VALUES(18,19,'18:19','I Mi smo ih, isto tako, probudili da bi jedni druge pitali. "Koliko ste ovdje ostali?", upita jedan od njih. "Ostali smo dan ili dio dana", odgovoriše. "Gospodar vaš najbolje zna koliko ste ostali", rekoše. "Pošaljite jednog od vas s ovim srebrenjacima vašim u grad, pa nek vidi u koga je najčistije jelo i neka vam od njega donese hrane i neka bude pažljiv, neka nikome ne govori ništa o vama,');
INSERT INTO "bs_darassalam" VALUES(18,20,'18:20','jer ako oni doznaju za vas, kamenovat će vas ili će vas u svoju vjeru obratiti, i tad nikad nećete ono što želite postići!”');
INSERT INTO "bs_darassalam" VALUES(18,21,'18:21','I Mi smo, isto tako, učinili da oni za njih saznaju, da bi se uvjerili da je istinito Allahovo obećanje i da u čas oživljenja nema nikakve sumnje, kad su se između sebe o njima raspravljali, i rekli: “Sagradite na ulazu u nju ogradu, Gospodar njihov najbolje zna ko su oni.” A onda oni do čijih se riječi najviše držalo rekoše: “Napravit ćemo na ulazu u nju bogomolju!”');
INSERT INTO "bs_darassalam" VALUES(18,22,'18:22','Neki će reći: “Bila su trojica, pas njihov je bio četvrti”, a neki će govoriti: “Bila su peterica, pas njihov je bio šesti”, nagađajući ono što ne znaju – dok će neki reći: “Bila su sedmerica, a pas njihov bio je osmi.” Reci: “Gospodaru mome je dobro poznat njihov broj, samo malo njih to zna. Zato ne raspravljaj o njima, osim površno, i ne pitaj o njima od njih nikog!”');
INSERT INTO "bs_darassalam" VALUES(18,23,'18:23','I nikako za bilo šta ne reci: “Uradit ću to sigurno sutra!” – ne dodavši...');
INSERT INTO "bs_darassalam" VALUES(18,24,'18:24','...ne dodavši: “Ako Bog da!” A kad zaboraviš, sjeti se Gospodara svog i reci: “Gospodar moj će me uputiti na ono što je bolje i korisnije od ovog.”');
INSERT INTO "bs_darassalam" VALUES(18,25,'18:25','A oni su ostali u pećini svojoj tri stotine i još devet godina.');
INSERT INTO "bs_darassalam" VALUES(18,26,'18:26','Reci: “Allah najbolje zna koliko su ostali; tajne nebesa i Zemlje jedino On zna. Kako On sve vidi, kako On sve čuje! Oni nemaju drugog zaštitnika osim Njega, a On ne uzima nikog u odlukama Svojim kao ortaka.”');
INSERT INTO "bs_darassalam" VALUES(18,27,'18:27','Kazuj iz Knjige Gospodara svog ono što ti se objavljuje, niko ne može izmijeniti riječi Njegove, pa ni ti; osim kod Njega, nećeš naći utočišta nikakva.');
INSERT INTO "bs_darassalam" VALUES(18,28,'18:28','Budi strpljiv uz one koji se Gospodaru svome mole ujutro i navečer u želji za Licem Njegovim, i ne skidaj očiju svojih s njih iz želje za sjajem u životu na ovome svijetu, i ne slušaj onoga čije smo srce da Nas spominje nehajnim učinili, koji strast svoju slijedi i čiji su postupci propali.');
INSERT INTO "bs_darassalam" VALUES(18,29,'18:29','i reci: “Istina dolazi od Gospodara vašeg, pa ko hoće – neka vjeruje, a ko hoće – neka ne vjeruje!” Mi smo nevjernicima pripremili vatru čiji će ih dim sa svih strana obuhvatiti; ako zamole pomoć, pomoći će im se tekućinom poput rastopljene kovine koja će lica ispeći. Užasna li pića i grozna li boravišta!');
INSERT INTO "bs_darassalam" VALUES(18,30,'18:30','One koji budu vjerovali i dobra djela činili – Mi doista nećemo dopustiti da propadne nagrada onom koji je dobra djela činio...');
INSERT INTO "bs_darassalam" VALUES(18,31,'18:31','čekaju sigurno edenski vrtovi, kroz koje rijeke teku, u njima će se narukvicama od zlata kititi i u zelena odijela od dibe i kadife oblačiti, na divanima će u njima naslonjeni biti. Divne li nagrade i krasna li boravišta!');
INSERT INTO "bs_darassalam" VALUES(18,32,'18:32','I navedi im kao primjer dva čovjeka; jednom od njih smo dva vrta lozom zasađena dali i palmama ih opasali, a između njih njive postavili.');
INSERT INTO "bs_darassalam" VALUES(18,33,'18:33','Oba vrta su davala svoj plod, ničeg nije manjkalo, a kroz sredinu njihovu smo rijeku proveli.');
INSERT INTO "bs_darassalam" VALUES(18,34,'18:34','On je i drugog imetka imao. I reče drugu svom, dok je s njime razgovarao: “Od tebe sam bogatiji i jačeg sam roda!”');
INSERT INTO "bs_darassalam" VALUES(18,35,'18:35','I uđe u vrt svoj nezahvalan Gospodaru svom na blagodatima, govoreći: “Ne mislim da će ovaj ikad propasti...');
INSERT INTO "bs_darassalam" VALUES(18,36,'18:36','i ne mislim da će ikad Smak svijeta doći; a ako budem vraćen Gospodaru svom, sigurno ću nešto bolje od ovog naći.”');
INSERT INTO "bs_darassalam" VALUES(18,37,'18:37','I reče mu drug njegov, dok je s njime razgovarao: “Zar ne vjeruješ u Onog Koji te je od zemlje stvorio, zatim od kapi sjemena, i najzad te potpunim čovjekom učinio?');
INSERT INTO "bs_darassalam" VALUES(18,38,'18:38','Što se mene tiče, On, Allah, moj je Gospodar i ja Gospodaru svom ne smatram ravnim nikog.');
INSERT INTO "bs_darassalam" VALUES(18,39,'18:39','A zašto nisi, kad si u vrt svoj ušao, rekao: ‘Mašallah! Moć je samo u Allaha!’ Ako vidiš da je u mene manje blaga i manje roda nego u tebe...');
INSERT INTO "bs_darassalam" VALUES(18,40,'18:40','pa Gospodar moj može mi bolji vrt od tvog dati, a na tvoj nepogodu s neba poslati, pa da osvane samo klizava ledina, bez ičega...');
INSERT INTO "bs_darassalam" VALUES(18,41,'18:41','ili da mu voda u ponor ode, pa da je ne mogneš pronaći nikad.”');
INSERT INTO "bs_darassalam" VALUES(18,42,'18:42','I propadoše plodovi njegovi i on poče pljeskati rukama svojim žaleći za onim što je na njega utrošio, a loza se bijaše povaljala po podupiračima svojim – i govoraše: “Kamo sreće da Gospodaru svome nisam nikoga u obožavanju pridruživao!”');
INSERT INTO "bs_darassalam" VALUES(18,43,'18:43','I nije imao ko bi mu mogao pomoći, osim Allaha, a sam sebi nije mogao pomoći.');
INSERT INTO "bs_darassalam" VALUES(18,44,'18:44','Tad može pomoći samo Allah, Istiniti, On daje najbolju nagradu i čini da se sve na najbolji način okonča.');
INSERT INTO "bs_darassalam" VALUES(18,45,'18:45','Navedi im kao primjer da je život na ovom svijetu kao bilje, koje i poslije natapanja vodom, koju Mi s neba spuštamo, ipak postane suho, i vjetrovi ga raznesu. A Allah sve može!');
INSERT INTO "bs_darassalam" VALUES(18,46,'18:46','Bogatstvo i sinovi su ukras u životu na ovom svijetu, a dobra djela, koja vječno ostaju, bit će od Gospodara tvog bolje nagrađena i ono u što se čovjek može pouzdati.');
INSERT INTO "bs_darassalam" VALUES(18,47,'18:47','A na Dan kad planine uklonimo i kad vidiš Zemlju ogoljenu – a njih smo već sakupili, nijednog nismo izostavili...');
INSERT INTO "bs_darassalam" VALUES(18,48,'18:48','pred Gospodarom tvojim bit će oni u redove poredani: “Došli ste Nam onako kako smo vas prvi put stvorili, a tvrdili ste da vam nećemo vrijeme za oživljenje odrediti.”');
INSERT INTO "bs_darassalam" VALUES(18,49,'18:49','I Knjiga će biti postavljena i vidjet ćeš grešnike prestravljene zbog onog što je u njoj. “Teško nama!”, govorit će, “kakva je ovo Knjiga: ni mali ni veliki grijeh nije propustila, sve je nabrojala!” – i naći će upisano ono što su radili. Gospodar tvoj neće nikom nepravdu učiniti.');
INSERT INTO "bs_darassalam" VALUES(18,50,'18:50','A kad smo rekli melekima: “Poklonite se Ademu!”, svi su se poklonili osim Iblisa, on je bio jedan od džina i zato se ogriješio o zapovijest Gospodara svog. Pa zar ćete njega i porod njegov, pored Mene, kao prijatelje prihvatiti, kad su vam oni neprijatelji? Kako je šejtan loša zamjena nevjernicima!');
INSERT INTO "bs_darassalam" VALUES(18,51,'18:51','Ja nisam uzimao njih za svjedoke prilikom stvaranja nebesa i Zemlje ni neke od njih prilikom stvaranja drugih i za pomagače nisam uzimao one koji na krivi put upućuju.');
INSERT INTO "bs_darassalam" VALUES(18,52,'18:52','I na Dan kada On rekne: "Pozovite one za koje ste tvrdili da su ortaci Moji", i kad ih pozovu, oni im se neće odazvati, a između njih ćemo mjesto propasti postaviti.');
INSERT INTO "bs_darassalam" VALUES(18,53,'18:53','i grešnici će ugledati vatru i uvjerit će se da će u nju pasti, i da im iz nje neće povratka biti.');
INSERT INTO "bs_darassalam" VALUES(18,54,'18:54','U ovom Kur’anu Mi na razne načine objašnjavamo ljudima svakovrsne primjere, ali je čovjek, više nego iko, spreman raspravljati.');
INSERT INTO "bs_darassalam" VALUES(18,55,'18:55','A ljude, kada im dolazi uputa, odvraća od vjerovanja i od toga da od Gospodara svoga mole oprosta samo zato što očekuju sudbinu drevnih naroda ili što čekaju da ih snađe kazna naočigled svega svijeta.');
INSERT INTO "bs_darassalam" VALUES(18,56,'18:56','Mi šaljemo poslanike samo zato da donose radosne vijesti i da opominju. A nevjernici se raspravljaju, služeći se neistinama, da bi time opovrgli istinu, i rugaju se dokazima i opomenama Mojim.');
INSERT INTO "bs_darassalam" VALUES(18,57,'18:57','I ima li nepravednijeg od onog koji, kad se dokazima Gospodara svog opominje, za njih ne mari, i zaboravlja na posljedice onog što je učinio? Mi na srca njihova pokrivače stavljamo da Kur’an ne shvate, i gluhim ih činimo; i ako ih ti na Pravi put pozoveš oni, kad su takvi, nikad neće Pravim putem poći.');
INSERT INTO "bs_darassalam" VALUES(18,58,'18:58','Gospodar tvoj mnogo prašta i neizmjerno je milostiv; da ih On za ono što zaslužuju kažnjava, odmah bi ih na muke stavio. Ali, njih čeka određeni čas, od koga neće naći utočišta.');
INSERT INTO "bs_darassalam" VALUES(18,59,'18:59','A ona sela i gradove smo razorili zato što stanovnici njihovi nisu vjerovali i za propast njihovu tačno vrijeme bismo odredili.');
INSERT INTO "bs_darassalam" VALUES(18,60,'18:60','A kad Musa reče pomoćniku svom: “Sve ću ići dok ne stignem do mjesta gdje se sastaju dva mora, ili ću dugo, dugo ići.”');
INSERT INTO "bs_darassalam" VALUES(18,61,'18:61','I kad njih dvojica stigoše do mjesta na kome se ona sastaju, zaboraviše na ribu svoju, pa ona u more kliznu.');
INSERT INTO "bs_darassalam" VALUES(18,62,'18:62','A kad se udaljiše, Musa reče pomoćniku svom: “Daj nam užinu našu, jer smo se od ovog našeg putovanja umorili.”');
INSERT INTO "bs_darassalam" VALUES(18,63,'18:63','"Vidi!", reče on, "kad smo kod one stijene svratili, ja sam zaboravio onu ribu - sam šejtan je učinio da je zaboravim, da ti je ne spomenem - pa se ona prihvati svoga puta u moru, čudnovato!"');
INSERT INTO "bs_darassalam" VALUES(18,64,'18:64','“E, to je ono što tražimo!”, reče Musa, i njih dvojica se vratiše putem kojim su bili došli...');
INSERT INTO "bs_darassalam" VALUES(18,65,'18:65','i nađoše jednog Našeg roba kome smo milost Našu darovali i onom što samo Mi znamo naučili.');
INSERT INTO "bs_darassalam" VALUES(18,66,'18:66','“Mogu li te pratiti”, upita ga Musa, “ali da me poučiš onome čemu si ti ispravno poučen?”');
INSERT INTO "bs_darassalam" VALUES(18,67,'18:67','“Ti sigurno nećeš moći sa mnom izdržati”, reče onaj...');
INSERT INTO "bs_darassalam" VALUES(18,68,'18:68','“a i kako bi izdržao ono o čemu ništa ne znaš?”');
INSERT INTO "bs_darassalam" VALUES(18,69,'18:69','“Vidjet ćeš da ću strpljiv biti, ako Bog da”, reče Musa, “i da ti se neću ni u čemu protiviti.”');
INSERT INTO "bs_darassalam" VALUES(18,70,'18:70','“Ako ćeš me već pratiti”, reče onaj, “onda me ni o čemu ne pitaj dok ti ja o tome prvi ne kažem!”');
INSERT INTO "bs_darassalam" VALUES(18,71,'18:71','I njih dvojica krenuše. I kad se u lađu ukrcaše, onaj je probuši. “Zar je probuši da potopiš one koji na njoj plove? Učinio si, doista, nešto vrlo krupno!”');
INSERT INTO "bs_darassalam" VALUES(18,72,'18:72','“Ne rekoh li ja”, reče onaj, “da ti, doista, nećeš moći izdržati sa mnom?”');
INSERT INTO "bs_darassalam" VALUES(18,73,'18:73','“Ne kori me što sam zaboravio”, reče, “i ne čini mi poteškoće u ovom poslu mom!”');
INSERT INTO "bs_darassalam" VALUES(18,74,'18:74','I njih dvojica krenuše. I kad sretoše jednog dječaka pa ga onaj ubi, Musa reče: “Što ubi dijete bezgrešno, koje nije nikog ubilo! Učinio si, zaista, nešto vrlo ružno!”');
INSERT INTO "bs_darassalam" VALUES(18,75,'18:75','“Ne rekoh li ja tebi”, reče onaj, “da ti, doista, nećeš moći izdržati sa mnom?”');
INSERT INTO "bs_darassalam" VALUES(18,76,'18:76','“Ako te i poslije ovog za bilo šta upitam”, reče, “onda se nemoj sa mnom družiti. Eto sam ti se opravdao!”');
INSERT INTO "bs_darassalam" VALUES(18,77,'18:77','I njih dvojica krenuše. Kad dođoše do jednog grada, zamoliše stanovnike njegove da ih nahrane, ali oni odbiše da ih ugoste. U gradu njih dvojica naiđoše na jedan zid koji tek što se nije srušio, pa ga onaj prezida i ispravi. “Mogao si”, reče Musa, “uzeti za to nagradu.”');
INSERT INTO "bs_darassalam" VALUES(18,78,'18:78','“Sad se rastajemo ja i ti”, reče onaj, “pa da ti objasnim zbog čega se nisi mogao strpiti.”');
INSERT INTO "bs_darassalam" VALUES(18,79,'18:79','“Što se one lađe tiče, ona je vlasništvo siromaha koji rade na moru, i ja sam je oštetio jer je pred njima bio jedan vladar koji je svaku ispravnu lađu otimao...');
INSERT INTO "bs_darassalam" VALUES(18,80,'18:80','što se onog dječaka tiče, roditelji njegovi su vjernici, pa smo se pobojali da ih on neće na nasilje i nevjerovanje navratiti...');
INSERT INTO "bs_darassalam" VALUES(18,81,'18:81','a mi želimo da im Gospodar njihov, umjesto njega, da boljeg i čestitijeg od njega, i milostivijeg...');
INSERT INTO "bs_darassalam" VALUES(18,82,'18:82','a što se onog zida tiče, on je dvojice dječaka, siročadi iz grada, a pod njim je zakopano njihovo blago. Otac njihov je bio dobar čovjek i Gospodar tvoj želi, iz milosti Svoje, da oni odrastu i izvade blago svoje. Sve to ja nisam uradio po svom rasuđivanju. Eto to je objašnjenje za tvoje nestrpljenje!”');
INSERT INTO "bs_darassalam" VALUES(18,83,'18:83','I pitaju te o Zulkarnejnu. Reci: “Kazat ću vam o njemu neke vijesti.”');
INSERT INTO "bs_darassalam" VALUES(18,84,'18:84','Mi smo mu dali vlast na Zemlji i omogućili mu da izvrši ono što želi.');
INSERT INTO "bs_darassalam" VALUES(18,85,'18:85','I on je slijedio put.');
INSERT INTO "bs_darassalam" VALUES(18,86,'18:86','Kad stiže do mjesta gdje sunce zalazi, učini mu se kao da zalazi u jedan mutan izvor i nađe u blizini njegovoj jedan narod. “O Zulkarnejne”, rekosmo Mi, “ili ćeš ih kazniti ili ćeš s njima lijepo postupiti?”');
INSERT INTO "bs_darassalam" VALUES(18,87,'18:87','“Onog ko ostane mnogobožac”, reče, “kaznit ćemo, a poslije će se svom Gospodaru vratiti, pa će ga i On teškom mukom mučiti.');
INSERT INTO "bs_darassalam" VALUES(18,88,'18:88','A onom ko bude vjerovao i dobra djela činio – nagrada najljepša, i s njime ćemo blago postupiti.”');
INSERT INTO "bs_darassalam" VALUES(18,89,'18:89','I on opet pođe.');
INSERT INTO "bs_darassalam" VALUES(18,90,'18:90','I kad stiže do mjesta gdje Sunce izlazi, on nađe da ono izlazi iznad jednog naroda kome Mi nismo dali da se od njega bilo čime zakloni.');
INSERT INTO "bs_darassalam" VALUES(18,91,'18:91','Tako to bi. A Mi obuhvatamo znanjem sve što je imao on.');
INSERT INTO "bs_darassalam" VALUES(18,92,'18:92','I on pođe.');
INSERT INTO "bs_darassalam" VALUES(18,93,'18:93','Kad stiže između dvije planine, nađe ispred njih narod koji je jedva govor razumijevao.');
INSERT INTO "bs_darassalam" VALUES(18,94,'18:94','“O Zulkarnejne”, rekoše oni, “Jedžudž i Medžudž čine nered po Zemlji, pa hoćeš li da između nas i njih zid podigneš, mi ćemo te nagraditi.”');
INSERT INTO "bs_darassalam" VALUES(18,95,'18:95','“Bolje je ono što mi je Gospodar moj dao”, reče on, “nego, samo vi pomozite meni što više možete, i ja ću između vas i njih zid podići.');
INSERT INTO "bs_darassalam" VALUES(18,96,'18:96','Donesite mi velike komade gvožđa!” I kad on izravna dvije strane brda, reče: “Pūšite!” A kad ga usija, reče: “Donesite mi rastopljen bakar da ga zalijem.”');
INSERT INTO "bs_darassalam" VALUES(18,97,'18:97','I tako oni nisu mogli ni preći niti su ga mogli prokopati.');
INSERT INTO "bs_darassalam" VALUES(18,98,'18:98','“Ovo je blagodat Gospodara mog!”, reče on, “a kad se prijetnja Gospodara mog ispuni, On će ga sa zemljom sravniti, a prijetnja Gospodara moga će se, sigurno, ispuniti.”');
INSERT INTO "bs_darassalam" VALUES(18,99,'18:99','I Mi ćemo tad učiniti da se jedni od njih kao talasi sudaraju s drugima. I puhnut će se u rog, pa ćemo ih sve sakupiti...');
INSERT INTO "bs_darassalam" VALUES(18,100,'18:100','i tog ćemo Dana nevjernicima džehennem jasno pokazati...');
INSERT INTO "bs_darassalam" VALUES(18,101,'18:101','onima čije su oči bile koprenom zastrte, da o dokazima Mojim razmisle, onima koji nisu htjeli ništa čuti.');
INSERT INTO "bs_darassalam" VALUES(18,102,'18:102','Zar nevjernici misle da pored Mene mogu za bogove uzimati robove Moje? Mi smo, doista, za prebivalište nevjernicima pripremili džehennem.');
INSERT INTO "bs_darassalam" VALUES(18,103,'18:103','Reci: “Hoćete li da vam kažem čija djela neće nikako priznata biti...');
INSERT INTO "bs_darassalam" VALUES(18,104,'18:104','čiji će trud u životu na ovom svijetu uzaludan biti, a koji će misliti da je dobro ono što rade?');
INSERT INTO "bs_darassalam" VALUES(18,105,'18:105','To su oni koji u dokaze Gospodara svog ne budu vjerovali i koji budu poricali da će pred Njega izići; zbog toga će trud njihov uzaludan biti i na Sudnjem danu im nikakva značaja nećemo dati.');
INSERT INTO "bs_darassalam" VALUES(18,106,'18:106','Njima će kazna džehennem biti, zato što su nevjernici bili i što su se dokazima Mojim i poslanicima Mojim rugali.”');
INSERT INTO "bs_darassalam" VALUES(18,107,'18:107','Onima koji budu vjerovali i dobra djela činili – džennetske će bašče prebivalište biti...');
INSERT INTO "bs_darassalam" VALUES(18,108,'18:108','vječno će u njima boraviti i neće poželjeti da ih nečim drugim zamijene.');
INSERT INTO "bs_darassalam" VALUES(18,109,'18:109','Reci: “Kad bi more bilo mastilo da se ispišu riječi Gospodara moga, more bi presahlo, ali ne i riječi Gospodara moga, pa i kad bismo se pomogli još jednim sličnim.”');
INSERT INTO "bs_darassalam" VALUES(18,110,'18:110','Reci: “Ja sam čovjek kao i vi, meni se objavljuje da je vaš Bog – jedan Bog. Ko žudi da od Gospodara svog bude lijepo primljen, neka čini dobra djela i neka, klanjajući se Gospodaru svom, Njemu nikog ne pridružuje!”');
INSERT INTO "bs_darassalam" VALUES(19,1,'19:1','Kāf Hā Jā Ajīn Sād.');
INSERT INTO "bs_darassalam" VALUES(19,2,'19:2','Kazivanje o milosti Gospodara tvog prema robu Njegovu Zekerijjau...');
INSERT INTO "bs_darassalam" VALUES(19,3,'19:3','kad je on Gospodara svog tiho zovnuo...');
INSERT INTO "bs_darassalam" VALUES(19,4,'19:4','i rekao: “Gospodaru moj, kosti su mi oronule i glava osijedjela, a nikad nisam, kad sam Ti, Gospodaru moj, molbu uputio, nesretan ostao.');
INSERT INTO "bs_darassalam" VALUES(19,5,'19:5','Bojim se rođaka svojih po krvi poslije mene, a žena mi je nerotkinja, zato mi pokloni od Sebe nasljednika...');
INSERT INTO "bs_darassalam" VALUES(19,6,'19:6','da naslijedi mene i porodicu Jakubovu, i učini, Gospodaru moj, da budeš njime zadovoljan.”');
INSERT INTO "bs_darassalam" VALUES(19,7,'19:7','“O Zekerijja, javljamo ti radosnu vijest da će ti se dječak roditi, ime će mu Jahja biti, nikom prije njega to ime nismo htjeli dati.”');
INSERT INTO "bs_darassalam" VALUES(19,8,'19:8','“Gospodaru moj”, reče on, “kako ću imati sina kad mi je žena nerotkinja, a već sam duboku starost doživio?”');
INSERT INTO "bs_darassalam" VALUES(19,9,'19:9','“Eto tako!”, reče. “Gospodar je tvoj rekao: ‘To je Meni lahko, i tebe sam ranije stvorio, a nisi ništa bio.’”');
INSERT INTO "bs_darassalam" VALUES(19,10,'19:10','“Gospodaru moj”, reče, “daj mi neki znak!” “Znak će ti biti to što tri noći nećeš s ljudima razgovarati, a zdrav ćeš biti."');
INSERT INTO "bs_darassalam" VALUES(19,11,'19:11','I on izađe iz hrama u narod svoj i znakom im dade na znanje: “Hvalite Ga ujutro i navečer!”');
INSERT INTO "bs_darassalam" VALUES(19,12,'19:12','“O Jahja, prihvati Knjigu odlučno!”, a dadosmo mu mudrost još dok je dječak bio...');
INSERT INTO "bs_darassalam" VALUES(19,13,'19:13','i nježnost i čednost, i čestit je bio...');
INSERT INTO "bs_darassalam" VALUES(19,14,'19:14','i roditeljima svojima bio je dobar, i nije bio drzak i neposlušan.');
INSERT INTO "bs_darassalam" VALUES(19,15,'19:15','I neka je mir njemu na dan kad se rodio i na dan kad je umro i na dan kad bude iz mrtvih ustao!');
INSERT INTO "bs_darassalam" VALUES(19,16,'19:16','I spomeni u Knjizi Merjemu: kad se od ukućana svojih na istočnu stranu povukla...');
INSERT INTO "bs_darassalam" VALUES(19,17,'19:17','i jedan zastor da se od njih zakloni uzela, Mi smo k njoj meleka Džibrila poslali i on joj se prikazao u liku savršeno stvorenog muškarca.');
INSERT INTO "bs_darassalam" VALUES(19,18,'19:18','“Utječem se Milostivom od tebe, ako se Njega bojiš!”, uzviknu ona.');
INSERT INTO "bs_darassalam" VALUES(19,19,'19:19','“A ja sam upravo izaslanik Gospodara tvog”, reče on, “da ti poklonim dječaka čista!”');
INSERT INTO "bs_darassalam" VALUES(19,20,'19:20','“Kako ću imati dječaka”, reče ona, “kad me nijedan muškarac dodirnuo nije, a ja nisam nevaljalica!”');
INSERT INTO "bs_darassalam" VALUES(19,21,'19:21','“To je tako!”, reče on. “Gospodar je tvoj rekao: ‘To je Meni lahko’ – i zato da ga učinimo znamenjem ljudima i znakom milosti Naše. Tako je unaprijed određeno!”');
INSERT INTO "bs_darassalam" VALUES(19,22,'19:22','I ona zanese i bremenita se skloni daleko negdje.');
INSERT INTO "bs_darassalam" VALUES(19,23,'19:23','I porođajni bolovi prisiliše je da dođe do stabla jedne palme. “Kamo sreće da sam ranije umrla i da sam potpuno u zaborav pala!”, uzviknu ona.');
INSERT INTO "bs_darassalam" VALUES(19,24,'19:24','Tad je on ispod nje dozva: “Ne žalosti se, Gospodar tvoj dao je da niže tebe potok poteče.');
INSERT INTO "bs_darassalam" VALUES(19,25,'19:25','Zatresi palmino stablo, posut će po tebi datule svježe...');
INSERT INTO "bs_darassalam" VALUES(19,26,'19:26','pa jedi i pij i budi vesela! A ako vidiš čovjeka kakva, ti reci: ‘Ja sam se zavjetovala Milostivom da ću šutjeti, i danas ni s kim neću govoriti.’”');
INSERT INTO "bs_darassalam" VALUES(19,27,'19:27','I dođe ona s njime porodici svojoj, noseći ga. “O Merjemo”, rekoše oni, “učinila si nešto nečuveno...');
INSERT INTO "bs_darassalam" VALUES(19,28,'19:28','Ej ti, sestro Harunova, otac ti nije bio nevaljao, a ni mati tvoja nije bila nevaljalica!”');
INSERT INTO "bs_darassalam" VALUES(19,29,'19:29','A ona im na njega pokaza. “Kako da govorimo djetetu u bešici?”, rekoše.');
INSERT INTO "bs_darassalam" VALUES(19,30,'19:30','“Ja sam Allahov rob”, ono reče, “meni će On Knjigu dati i vjerovjesnikom me učiniti...');
INSERT INTO "bs_darassalam" VALUES(19,31,'19:31','i učinit će me, gdje god budem, blagoslovljenim, i naredit će mi da dok sam živ molitvu obavljam i milostinju udjeljujem...');
INSERT INTO "bs_darassalam" VALUES(19,32,'19:32','i da majci svojoj budem dobar, a neće mi dopustiti da budem drzak i nepristojan.');
INSERT INTO "bs_darassalam" VALUES(19,33,'19:33','I neka je mir meni na dan kad sam se rodio i na dan kad budem umro i na dan kad budem iz mrtvih ustajao!”');
INSERT INTO "bs_darassalam" VALUES(19,34,'19:34','To je Isa, sin Merjemin – to je prava istina o njemu – onaj u koga oni sumnjaju.');
INSERT INTO "bs_darassalam" VALUES(19,35,'19:35','Nezamislivo je da Allah ima dijete, hvaljen neka je On! Kad nešto odluči, On za to samo rekne: “Budi!”, i ono bude.');
INSERT INTO "bs_darassalam" VALUES(19,36,'19:36','“Allah je, uistinu, i moj i vaš Gospodar, zato se klanjajte samo Njemu! To je Pravi put.”');
INSERT INTO "bs_darassalam" VALUES(19,37,'19:37','I sljedbenici Knjige su se o njemu u mišljenju podvojili, pa teško onima koji ne vjeruju kad budu na Danu velikom prisutni!');
INSERT INTO "bs_darassalam" VALUES(19,38,'19:38','Kako će dobro čuti i kako će dobro vidjeti onog Dana kad pred Nas stanu! A nevjernici su sad u očitoj zabludi!');
INSERT INTO "bs_darassalam" VALUES(19,39,'19:39','I opomeni ih na Dan tuge kad će biti s polaganjem računa završeno, a oni su ravnodušni bili i nisu vjerovali.');
INSERT INTO "bs_darassalam" VALUES(19,40,'19:40','Mi ćemo Zemlju i one koji žive na njoj naslijediti i Nama će se oni vratiti.');
INSERT INTO "bs_darassalam" VALUES(19,41,'19:41','Spomeni u Knjizi Ibrahima! On je bio istinoljubiv, vjerovjesnik.');
INSERT INTO "bs_darassalam" VALUES(19,42,'19:42','Kad je rekao ocu svom: “O oče moj, zašto se klanjaš onom koji niti čuje niti vidi, niti ti može od ikakve koristi biti?');
INSERT INTO "bs_darassalam" VALUES(19,43,'19:43','O oče moj, meni dolazi znanje, a ne tebi; zato mene slijedi, i ja ću te na Pravi put uputiti.');
INSERT INTO "bs_darassalam" VALUES(19,44,'19:44','O oče moj, ne klanjaj se šejtanu, šejtan je Milostivom uvijek neposlušan.');
INSERT INTO "bs_darassalam" VALUES(19,45,'19:45','O oče moj, bojim se da te od Milostivog ne stigne kazna, pa da budeš šejtanu drug.”');
INSERT INTO "bs_darassalam" VALUES(19,46,'19:46','Otac njegov je rekao: “Zar ti mrziš božanstva moja, o Ibrahime? Ako se ne okaniš, zbilja ću te kamenjem potjerati, zato me za dugo vremena napusti!”');
INSERT INTO "bs_darassalam" VALUES(19,47,'19:47','“Mir tebi!”, reče Ibrahim. “Molit ću Gospodara svog da ti oprosti, jer On je vrlo dobar prema meni.');
INSERT INTO "bs_darassalam" VALUES(19,48,'19:48','I napustit ću i vas i sve one kojima se mimo Allaha molite, i Gospodara svoga ću moliti; nadam se da dovom koju uputim Gospodaru mome neću razočaran biti.”');
INSERT INTO "bs_darassalam" VALUES(19,49,'19:49','Pošto ih je napustio i one koje su, mimo Allaha, obožavali, Mi mu Ishaka i Jakuba darovasmo, i obojicu vjerovjesnicima učinismo,');
INSERT INTO "bs_darassalam" VALUES(19,50,'19:50','i darovasmo im svako dobro i učinismo da budu hvaljeni i po dobru spominjani.');
INSERT INTO "bs_darassalam" VALUES(19,51,'19:51','I spomeni u Knjizi Musaa! On je bio iskren i bio je poslanik, vjerovjesnik.');
INSERT INTO "bs_darassalam" VALUES(19,52,'19:52','Mi smo ga s desne strane Tura zovnuli i Sebi ga približili da čuje riječi Naše...');
INSERT INTO "bs_darassalam" VALUES(19,53,'19:53','i darovali smo mu milošću Našom kao vjerovjesnika brata njegova Haruna.');
INSERT INTO "bs_darassalam" VALUES(19,54,'19:54','I spomeni u Knjizi Ismaila! On je ispunjavao dato obećanje i bio poslanik, vjerovjesnik...');
INSERT INTO "bs_darassalam" VALUES(19,55,'19:55','i tražio je od čeljadi svoje da molitvu obavljaju i da milostinju udjeljuju, i Gospodar njegov je bio njime zadovoljan.');
INSERT INTO "bs_darassalam" VALUES(19,56,'19:56','I spomeni u Knjizi Idrisa! On je bio istinoljubiv i vjerovjesnik...');
INSERT INTO "bs_darassalam" VALUES(19,57,'19:57','i Mi smo ga na visoko mjesto digli.');
INSERT INTO "bs_darassalam" VALUES(19,58,'19:58','To su ti vjerovjesnici koje je Allah milošću Svojom obasuo, potomci Ademovi i onih koje smo sa Nuhom nosili, i potomci Ibrahimovi i Israilovi, i onih koje smo uputili i odabrali. Kad bi im se ajeti Milostivog čitali, oni bi licem na tlo padali i plakali.');
INSERT INTO "bs_darassalam" VALUES(19,59,'19:59','A njih smijeniše zli potomci, koji molitvu napustiše i za požudama pođoše – oni će sigurno zlo proći...');
INSERT INTO "bs_darassalam" VALUES(19,60,'19:60','ali oni koji su se pokajali, i vjerovali, i dobro činili njima se neće nikakva nepravda učiniti, oni će u Džennet ući...');
INSERT INTO "bs_darassalam" VALUES(19,61,'19:61','u edenske vrtove koje je Milostivi robovima Svojim obećao zato što su u njih vjerovali, a nisu ih vidjeli – a obećanje Njegovo će se doista ispuniti...');
INSERT INTO "bs_darassalam" VALUES(19,62,'19:62','u njima prazne besjede neće slušati, već samo: “Mir!”, i u njima će i ujutro i navečer opskrbljeni biti.');
INSERT INTO "bs_darassalam" VALUES(19,63,'19:63','Dat ćemo da takav džennet naslijedi onaj od robova Naših koji se bude grijeha klonio.');
INSERT INTO "bs_darassalam" VALUES(19,64,'19:64','“I ne spuštamo se, izuzev po naredbi Gospodara tvog, Njegovo je sve što je ispred nas i šta je iza nas i ono što je između toga. Gospodar tvoj ne zaboravlja.”');
INSERT INTO "bs_darassalam" VALUES(19,65,'19:65','On je Gospodar nebesa i Zemlje i onog što je između njih, zato se samo Njemu klanjaj i u tome budi ustrajan! Znaš li da ime Njegovo ima iko!?');
INSERT INTO "bs_darassalam" VALUES(19,66,'19:66','Čovjek kaže: “Zar ću, kad umrem, zbilja biti oživljen?”');
INSERT INTO "bs_darassalam" VALUES(19,67,'19:67','A zar se čovjek ne sjeća da smo ga još prije stvorili, a da nije bio ništa?');
INSERT INTO "bs_darassalam" VALUES(19,68,'19:68','I tako mi Gospodara tvog, Mi ćemo i njih i šejtane sakupiti, zatim ćemo ih dovesti da oko džehennema na koljenima kleče...');
INSERT INTO "bs_darassalam" VALUES(19,69,'19:69','a onda ćemo iz svake skupine izdvojiti one koji su prema Milostivom najdrskiji bili...');
INSERT INTO "bs_darassalam" VALUES(19,70,'19:70','jer Mi dobro znamo one koji su najviše zaslužili da u njemu gore.');
INSERT INTO "bs_darassalam" VALUES(19,71,'19:71','I svaki od vas će do njega stići! Gospodar tvoj se, sigurno, tako obavezao!');
INSERT INTO "bs_darassalam" VALUES(19,72,'19:72','Zatim ćemo one koji su se grijeha klonili spasiti, a nevjernike ćemo da u njemu na koljenima kleče ostaviti.');
INSERT INTO "bs_darassalam" VALUES(19,73,'19:73','Kad su im se Naši jasni ajeti kazivali, onda su oni koji nisu vjerovali govorili onima koji su vjerovali: “Ili smo mi ili vi u boljem položaju i ko ima više pobornika?”');
INSERT INTO "bs_darassalam" VALUES(19,74,'19:74','A koliko smo Mi prije njih naroda uništili koji su blagom i izgledom divljenje izazivali!');
INSERT INTO "bs_darassalam" VALUES(19,75,'19:75','Reci: “Onom ko je u zabludi, neka Milostivi dug život da!” Ali kad takvi dožive da se opomene ostvare – bilo kazna, bilo Smak svijeta – zbilja će saznati ko je u gorem položaju i ko ima pobornika manje...');
INSERT INTO "bs_darassalam" VALUES(19,76,'19:76','i Allah će pomoći onima koji su na Pravom putu! A dobra djela koja vječno ostaju – od Gospodara tvog bit će bolje nagrađena i ljepše uzvraćena.');
INSERT INTO "bs_darassalam" VALUES(19,77,'19:77','Zar nisi vidio onog koji u dokaze Naše ne vjeruje i govori: “Zacijelo će mi biti dato bogatstvo i djeca!”');
INSERT INTO "bs_darassalam" VALUES(19,78,'19:78','Ili je on budućnost prozreo ili je od Milostivog obećanje primio?');
INSERT INTO "bs_darassalam" VALUES(19,79,'19:79','Nijedno! Mi ćemo ono što on govori zapisati i patnju mu veoma produžiti...');
INSERT INTO "bs_darassalam" VALUES(19,80,'19:80','a Mi ga naslijeđujemo u onom što govori, i sam samcat će Nam se vratiti.');
INSERT INTO "bs_darassalam" VALUES(19,81,'19:81','Oni kao zagovornike nekakva božanstva, a ne Allaha, uzimaju.');
INSERT INTO "bs_darassalam" VALUES(19,82,'19:82','A ne valja tako! Božanstva ta će poreći da su im se klanjali, i bit će im protivnici.');
INSERT INTO "bs_darassalam" VALUES(19,83,'19:83','Zar ne vidiš da Mi nevjernike šejtanima prepuštamo da ih što više na zlo navraćaju?');
INSERT INTO "bs_darassalam" VALUES(19,84,'19:84','Zato ne traži da što prije stradaju, Mi im polahko dane odbrojavamo!');
INSERT INTO "bs_darassalam" VALUES(19,85,'19:85','Onog Dana kad čestite kao uzvanike pred Milostivim sakupimo...');
INSERT INTO "bs_darassalam" VALUES(19,86,'19:86','a kad u džehennem žedne grešnike potjeramo...');
INSERT INTO "bs_darassalam" VALUES(19,87,'19:87','neće se moći ni za koga zauzimati, osim onog kome Milostivi dopusti.');
INSERT INTO "bs_darassalam" VALUES(19,88,'19:88','Oni govore: “Milostivi je uzeo dijete!”');
INSERT INTO "bs_darassalam" VALUES(19,89,'19:89','Vi, doista, nešto odvratno govorite!');
INSERT INTO "bs_darassalam" VALUES(19,90,'19:90','Gotovo da se nebesa raspadnu, a Zemlja provali i planine zdrobe...');
INSERT INTO "bs_darassalam" VALUES(19,91,'19:91','što Milostivom pripisuju dijete.');
INSERT INTO "bs_darassalam" VALUES(19,92,'19:92','Nezamislivo je da Milostivi ima dijete...');
INSERT INTO "bs_darassalam" VALUES(19,93,'19:93','ta svi će oni, i oni na nebesima i oni na Zemlji, kao robovi u Milostivog tražiti utočište!');
INSERT INTO "bs_darassalam" VALUES(19,94,'19:94','On ih je sve zapamtio i tačno izbrojio...');
INSERT INTO "bs_darassalam" VALUES(19,95,'19:95','i svi će Mu na Sudnjem danu doći pojedinačno.');
INSERT INTO "bs_darassalam" VALUES(19,96,'19:96','One koji su vjerovali i dobra djela činili Milostivi će, sigurno, voljenim učiniti.');
INSERT INTO "bs_darassalam" VALUES(19,97,'19:97','Mi smo Kur’an učinili lahkim, na tvom jeziku, da bi njime one koji se Allaha boje i grijeha klone obradovao, a inadžije nepopustljive opomenuo.');
INSERT INTO "bs_darassalam" VALUES(19,98,'19:98','A koliko smo samo naroda prije njih uništili! Vidiš li ijednog od njih i čuješ li i najslabiji glas njihov?');
INSERT INTO "bs_darassalam" VALUES(20,1,'20:1','Tā Hā.');
INSERT INTO "bs_darassalam" VALUES(20,2,'20:2','Ne objavljujemo ti Kur’an da se mučiš.');
INSERT INTO "bs_darassalam" VALUES(20,3,'20:3','već da bude pouka onom koji se boji.');
INSERT INTO "bs_darassalam" VALUES(20,4,'20:4','objavljuje ga Stvoritelj Zemlje i nebesa visokih.');
INSERT INTO "bs_darassalam" VALUES(20,5,'20:5','Milostivi, Koji se iznad Arša uzvisio.');
INSERT INTO "bs_darassalam" VALUES(20,6,'20:6','Njegovo je što je na nebesima i što je na Zemlji i što je između njih i što je pod zemljom!');
INSERT INTO "bs_darassalam" VALUES(20,7,'20:7','Ako si glasan u govoru – pa On zna i šta drugom tajno rekneš i što samo pomisliš!');
INSERT INTO "bs_darassalam" VALUES(20,8,'20:8','Allah, drugog boga osim Njega nema, najljepša imena ima!');
INSERT INTO "bs_darassalam" VALUES(20,9,'20:9','A je li do tebe doprla vijest o Musau...');
INSERT INTO "bs_darassalam" VALUES(20,10,'20:10','kad je vatru ugledao, pa čeljadi svojoj rekao: “Ostanite vi tu, ja sam vatru vidio, možda ću vam nekakvu glavnju donijeti ili ću pored vatre naći nekog ko će mi put pokazati.”');
INSERT INTO "bs_darassalam" VALUES(20,11,'20:11','A kad do nje dođe, neko ga zovnu: “O Musa...');
INSERT INTO "bs_darassalam" VALUES(20,12,'20:12','Ja sam, uistinu, Gospodar tvoj! Izuj, zato, obuću svoju, ti si, doista, u blagoslovljenoj dolini Tuva.');
INSERT INTO "bs_darassalam" VALUES(20,13,'20:13','Tebe sam izabrao, zato ono što ti se objavljuje slušaj!');
INSERT INTO "bs_darassalam" VALUES(20,14,'20:14','Ja sam, uistinu, Allah, drugog boga osim Mene nema; pa Meni ibadet čini i kad god Me se sjetiš, namaz obavi!');
INSERT INTO "bs_darassalam" VALUES(20,15,'20:15','Čas oživljenja će sigurno doći – od svakog ga tajim – kad će svaki čovjek prema trudu svom nagrađen ili kažnjen biti.');
INSERT INTO "bs_darassalam" VALUES(20,16,'20:16','I neka te zato nikako ne odvrati od vjerovanja u njega onaj koji u njega ne vjeruje i koji slijedi strast svoju, pa da budeš izgubljen.');
INSERT INTO "bs_darassalam" VALUES(20,17,'20:17','A šta ti je to u desnoj ruci, o Musa?”');
INSERT INTO "bs_darassalam" VALUES(20,18,'20:18','“Ovo je moj štap”, odgovori on, “kojim se poštapam i kojim lišće ovcama svojim skidam, a služi mi i za druge potrebe.”');
INSERT INTO "bs_darassalam" VALUES(20,19,'20:19','“Baci ga, o Musa!”, reče On.');
INSERT INTO "bs_darassalam" VALUES(20,20,'20:20','I on ga baci, kad on – zmija koja zmija koja puže.');
INSERT INTO "bs_darassalam" VALUES(20,21,'20:21','“Uzmi je i ne boj se!”, reče On, “Mi ćemo je vratiti u ono što je bila prije.');
INSERT INTO "bs_darassalam" VALUES(20,22,'20:22','I uvuci ruku pod pazuho svoje, ruka će se pojaviti bijela, ali ne bolesna; i eto – znamenje drugo...');
INSERT INTO "bs_darassalam" VALUES(20,23,'20:23','da ti pokažemo neka od Naših velikih čuda.');
INSERT INTO "bs_darassalam" VALUES(20,24,'20:24','Idi faraonu jer je u zlu svaku mjeru prevršio!”');
INSERT INTO "bs_darassalam" VALUES(20,25,'20:25','“Gospodaru moj”, reče Musa, “učini prostranim prsa moja...');
INSERT INTO "bs_darassalam" VALUES(20,26,'20:26','i olakšaj zadatak moj...');
INSERT INTO "bs_darassalam" VALUES(20,27,'20:27','odriješi uzao sa jezika moga...');
INSERT INTO "bs_darassalam" VALUES(20,28,'20:28','da bi razumjeli govor moj...');
INSERT INTO "bs_darassalam" VALUES(20,29,'20:29','i podaj mi za pomoćnika iz porodice moje...');
INSERT INTO "bs_darassalam" VALUES(20,30,'20:30','Haruna, brata mog...');
INSERT INTO "bs_darassalam" VALUES(20,31,'20:31','osnaži me njime...');
INSERT INTO "bs_darassalam" VALUES(20,32,'20:32','i učini drugom u zadatku mom...');
INSERT INTO "bs_darassalam" VALUES(20,33,'20:33','da bismo Te mnogo slavili...');
INSERT INTO "bs_darassalam" VALUES(20,34,'20:34','i da bismo Te mnogo spominjali.');
INSERT INTO "bs_darassalam" VALUES(20,35,'20:35','Ti, uistinu, vidiš nas.');
INSERT INTO "bs_darassalam" VALUES(20,36,'20:36','“Udovoljeno je molbi tvojoj, o Musa!”, reče On.');
INSERT INTO "bs_darassalam" VALUES(20,37,'20:37','“A ukazali smo ti milost Svoju još jednom...');
INSERT INTO "bs_darassalam" VALUES(20,38,'20:38','kad smo majku tvoju nadahnuli onim što se samo nadahnućem stječe...');
INSERT INTO "bs_darassalam" VALUES(20,39,'20:39','‘Metni ga u sanduk i u rijeku baci, rijeka će ga na obalu izbaciti, pa će ga i Moj i njegov neprijatelj prihvatiti.’ I Ja sam učinio da te svako voli i da rasteš pod okom Mojim.');
INSERT INTO "bs_darassalam" VALUES(20,40,'20:40','Kad je sestra tvoja otišla i rekla: ‘Hoćete li da vam pokažem onu koja će se o njemu brinuti?’ – Mi smo te majci tvojoj povratili da se raduje i da više ne tuguje. A ti si ubio jednog čovjeka, pa smo te Mi brige oslobodili i iz raznih nevolja te spasili. I ti si ostao godinama među stanovnicima Medjena, zatim si, o Musa, u pravo vrijeme došao...');
INSERT INTO "bs_darassalam" VALUES(20,41,'20:41','i Ja sam te za Sebe izabrao.');
INSERT INTO "bs_darassalam" VALUES(20,42,'20:42','Idite ti i brat tvoj, s dokazima Mojim, i neka sam vam Ja uvijek na pameti.');
INSERT INTO "bs_darassalam" VALUES(20,43,'20:43','Idite faraonu, on se, doista, osilio...');
INSERT INTO "bs_darassalam" VALUES(20,44,'20:44','pa mu blagim riječima govorite, ne bi li razmislio ili se pobojao!”');
INSERT INTO "bs_darassalam" VALUES(20,45,'20:45','“Gospodaru naš”, rekoše oni, “bojimo se da nas odmah na muke ne stavi ili da svaku mjeru zla ne prekorači.”');
INSERT INTO "bs_darassalam" VALUES(20,46,'20:46','“Ne bojte se!”, reče On, “Ja sam s vama, Ja sve čujem i vidim."');
INSERT INTO "bs_darassalam" VALUES(20,47,'20:47','Idite k njemu i recite: ‘Mi smo poslanici Gospodara tvog, pusti sinove Israilove da idu s nama i nemoj ih mučiti! Donijeli smo ti dokaz od Gospodara tvog, a nek živi u miru onaj koji Pravi put slijedi!');
INSERT INTO "bs_darassalam" VALUES(20,48,'20:48','Nama se objavljuje da će, sigurno, stići kazna onog koji ne povjeruje i glavu okrene.');
INSERT INTO "bs_darassalam" VALUES(20,49,'20:49','“Pa ko je Gospodar vaš, o Musa?”, upita faraon.');
INSERT INTO "bs_darassalam" VALUES(20,50,'20:50','Reče: “Gospodar naš je Onaj Koji je svemu onom što je stvorio dao ono što mu je potrebno, zatim ga kako da se time koristi nadahnuo.”');
INSERT INTO "bs_darassalam" VALUES(20,51,'20:51','“A šta je s narodima davnašnjim?”, upita on.');
INSERT INTO "bs_darassalam" VALUES(20,52,'20:52','Musa reče: “O njima zna sve Gospodar moj, u Knjizi je, Gospodar moj ne griješi i On ništa ne zaboravlja.');
INSERT INTO "bs_darassalam" VALUES(20,53,'20:53','On je za vas Zemlju posteljom učinio i po njoj vam prolaze utro, i On spušta s neba kišu da uz njenu pomoć u parovima niče bilje raznovrsno.');
INSERT INTO "bs_darassalam" VALUES(20,54,'20:54','Jedite i napasajte stoku svoju! To su dokazi za one koji pameti imaju.');
INSERT INTO "bs_darassalam" VALUES(20,55,'20:55','Od zemlje vas stvaramo i u nju vas vraćamo i iz nje ćemo vas po drugi put izvesti.');
INSERT INTO "bs_darassalam" VALUES(20,56,'20:56','I Mi smo faraonu sve dokaze Naše pokazali, ali je on ipak porekao i da povjeruje odbio.');
INSERT INTO "bs_darassalam" VALUES(20,57,'20:57','“Zar si došao da nas pomoću vradžbine svoje iz zemlje naše izvedeš, o Musa?”, upitao je.');
INSERT INTO "bs_darassalam" VALUES(20,58,'20:58','“I mi ćemo tebi vradžbinu sličnu ovoj doista pripremiti! Zakaži nam ročište kojeg ćemo se i mi i ti pridržavati, onako kako odgovara i nama i tebi!”');
INSERT INTO "bs_darassalam" VALUES(20,59,'20:59','Reče: "Neka ročište bude za praznik i nek se narod izjutra sakupi.”');
INSERT INTO "bs_darassalam" VALUES(20,60,'20:60','I faraon se okrenu, sakupi čarobnjake svoje i poslije dođe.');
INSERT INTO "bs_darassalam" VALUES(20,61,'20:61','“Teško vama!”, reče im Musa. “Ne iznosite laži o Allahu, pa da vas On kaznom uništi; a, sigurno, neće uspjeti onaj koji laži iznosi!”');
INSERT INTO "bs_darassalam" VALUES(20,62,'20:62','I oni se, tiho šapćući, stadoše o svom poslu između sebe raspravljati.');
INSERT INTO "bs_darassalam" VALUES(20,63,'20:63','“Ova dvojica su čarobnjaci”, rekoše jedni drugima, “hoće da vasvradžbinama svojim iz zemlje vaše izvedu i da unište vaš pravacnajčasniji."');
INSERT INTO "bs_darassalam" VALUES(20,64,'20:64','zato lukavstvo svoje pametno pripremite, a onda u red stanite. Ko danas pobijedi, sigurno će postići šta želi!”');
INSERT INTO "bs_darassalam" VALUES(20,65,'20:65','“O Musa”, rekoše oni, “hoćeš li ti ili ćemo najprije mi baciti?”');
INSERT INTO "bs_darassalam" VALUES(20,66,'20:66','“Bacite vi!”, reče on. I odjednom mu se pričini da se konopi njihovi i štapovi njihovi, zbog vradžbine njihove, kreću...');
INSERT INTO "bs_darassalam" VALUES(20,67,'20:67','i Musa u sebi osjeti zebnju.');
INSERT INTO "bs_darassalam" VALUES(20,68,'20:68','“Ne boj se!”, rekosmo Mi, “ti ćeš, doista, pobijediti!');
INSERT INTO "bs_darassalam" VALUES(20,69,'20:69','Samo baci to što ti je u desnoj ruci, progutat će ono što su oni napravili, jer je ono što su oni napravili samo varka čarobnjaka, a čarobnjak neće, ma gdje došao, uspjeti.”');
INSERT INTO "bs_darassalam" VALUES(20,70,'20:70','I čarobnjaci se baciše licem na tlo govoreći: “Mi vjerujemo u Musaova i Harunova Gospodara!”');
INSERT INTO "bs_darassalam" VALUES(20,71,'20:71','“Vi ste mu povjerovali”, faraon viknu, “prije nego što sam vam ja dopustio! On je učitelj vaš, on vas je vradžbini naučio i ja ću vam, zacijelo, unakrst ruke i noge vaše odsjeći i po stablima palmi vas razapeti i sigurno ćete saznati ko je od nas u mučenju strašniji i istrajniji.”');
INSERT INTO "bs_darassalam" VALUES(20,72,'20:72','“Mi nećemo tebe staviti iznad jasnih dokaza koji su nam došli i iznad Onog Koji nas je stvorio”, odgovoriše oni, “pa čini šta hoćeš! To možeš učiniti samo u životu na ovom svijetu!');
INSERT INTO "bs_darassalam" VALUES(20,73,'20:73','Mi vjerujemo u Gospodara našeg da bi nam grijehe naše oprostio i vradžbine na koje si nas ti primorao. A Allah bolje nagrađuje i kažnjava trajnije.”');
INSERT INTO "bs_darassalam" VALUES(20,74,'20:74','Onog koji pred Gospodara svog kao nevjernik izađe čeka Džehennem, u njemu neće ni umrijeti ni živjeti.');
INSERT INTO "bs_darassalam" VALUES(20,75,'20:75','A one koji pred Njega izađu kao vjernici, a koji su dobra djela činili – njih sve čekaju visoki stepeni...');
INSERT INTO "bs_darassalam" VALUES(20,76,'20:76','edenski vrtovi kroz koje rijeke teku, u njima će oni vječno ostati, i to će biti nagrada za one koji se budu od grijeha očistili.');
INSERT INTO "bs_darassalam" VALUES(20,77,'20:77','I Mi objavismo Musau: “Noću izvedi robove Moje i s njima suhim putem kroz more prođi, ne strahujući da će te oni stići i da ćeš se utopiti.”');
INSERT INTO "bs_darassalam" VALUES(20,78,'20:78','A faraon je za njima s vojskama svojim krenuo, ali su ih talasi mora prekrili...');
INSERT INTO "bs_darassalam" VALUES(20,79,'20:79','faraon je narod svoj u zabludu odveo, a nije na Pravi put izveo.');
INSERT INTO "bs_darassalam" VALUES(20,80,'20:80','O sinovi Israilovi, Mi smo vas od neprijatelja vašeg izbavili, i na desnu stranu Tura vas doveli, i mànu i prepelice vam slali.');
INSERT INTO "bs_darassalam" VALUES(20,81,'20:81','“Jedite ukusna jela kojima vas opskrbljujemo i ne budite u tome obijesni da vas ne bi snašla srdžba Moja; a koga snađe srdžba Moja – nastradao je!');
INSERT INTO "bs_darassalam" VALUES(20,82,'20:82','Ja ću sigurno oprostiti onom koji se pokaje i uzvjeruje i dobra djela čini i koji zatim na Pravom putu istraje.”');
INSERT INTO "bs_darassalam" VALUES(20,83,'20:83','“A zašto si prije naroda svog požurio, o Musa?”');
INSERT INTO "bs_darassalam" VALUES(20,84,'20:84','“Evo ide za mnom”, odgovori on, “a požurio sam Tebi, Gospodaru moj, da budeš zadovoljan.”');
INSERT INTO "bs_darassalam" VALUES(20,85,'20:85','“Mi smo narod tvoj poslije tvog odlaska u iskušenje doveli”, reče On, “njega je zaveo Samirija.”');
INSERT INTO "bs_darassalam" VALUES(20,86,'20:86','I Musa se narodu svom vrati srdit i žalostan. “O narode moj”, reče, “zar vam Gospodar vaš nije dao lijepo obećanje? Zar vam se vrijeme oduljilo ili hoćete da vas stigne srdžba Gospodara vašeg, pa se zato niste držali obećanja koje ste mi dali!”');
INSERT INTO "bs_darassalam" VALUES(20,87,'20:87','“Nismo od svoje volje prekršili obećanje koje smo ti dali.”, odgovoriše. “Bili smo natovareni teretima, nakitom faraonova naroda, pa smo to pobacali.” A to je uradio i Samirija.');
INSERT INTO "bs_darassalam" VALUES(20,88,'20:88','pa im izlio tele koje je davalo glas kao da mūče, i oni su onda rekli: “Ovo je vaš bog i Musaov bog, on ga je zaboravio!”');
INSERT INTO "bs_darassalam" VALUES(20,89,'20:89','Zar oni nisu vidjeli da im ono ni riječi ne odgovara i da od njih ne može nikakvu nevolju otkloniti niti im ikakvu korist pribaviti?');
INSERT INTO "bs_darassalam" VALUES(20,90,'20:90','A njima je Harun još prije govorio: “O narode moj, vi ste njime samo u iskušenje dovedeni. Gospodar vaš je Milostivi, zato slijedite mene i slušajte naređenje moje!”');
INSERT INTO "bs_darassalam" VALUES(20,91,'20:91','“Mi ćemo mu se klanjati sve dok nam se ne vrati Musa”, odgovorili su oni.');
INSERT INTO "bs_darassalam" VALUES(20,92,'20:92','“O Harune”, povika Musa, “šta te je spriječilo, kad si ih vidio da su zalutali...');
INSERT INTO "bs_darassalam" VALUES(20,93,'20:93','da za mnom nisi pošao? Zašto nisi naređenje moje poslušao?”');
INSERT INTO "bs_darassalam" VALUES(20,94,'20:94','“O sine majke moje”, reče Harun, “ne hvataj me za bradu i za kosu moju! Ja sam se plašio da ti ne rekneš: ‘Razdor si među sinovima Israilovim posijao i nisi postupio onako kako sam ti rekao.’”');
INSERT INTO "bs_darassalam" VALUES(20,95,'20:95','“A šta si ti to htio, o Samirija?”, upita Musa.');
INSERT INTO "bs_darassalam" VALUES(20,96,'20:96','“Ja sam vidio ono što oni nisu vidjeli”, odgovori on, “pa sam šaku zemlje ispod izaslanikove stope uzeo i to bacio – i eto tako je u mojoj duši ponikla zla misao.”');
INSERT INTO "bs_darassalam" VALUES(20,97,'20:97','“E onda se gubi!”, reče Musa. “Čitavog svog života govorit ćeš: ‘Neka me niko ne dotiče!’ – a čeka te još i određeni čas koji te neće mimoići. Pogledaj samo ovog tvog ‘boga’ kojem si se klanjao; mi ćemo ga, sigurno, spaliti i po moru mu prah rasuti.”');
INSERT INTO "bs_darassalam" VALUES(20,98,'20:98','Vaš bog je – Allah, drugog boga, osim Njega, nema! On sve zna!');
INSERT INTO "bs_darassalam" VALUES(20,99,'20:99','I tako, eto, kazujemo ti neke vijesti o onima koji su bili i nestali, i objavljujemo ti od Sebe Kur’an.');
INSERT INTO "bs_darassalam" VALUES(20,100,'20:100','Ko za njega ne bude mario, na Sudnjem danu će doista teško breme ponijeti...');
INSERT INTO "bs_darassalam" VALUES(20,101,'20:101','vječno će u muci ostati, a jeziv tovar će im na Sudnjem danu biti...');
INSERT INTO "bs_darassalam" VALUES(20,102,'20:102','na Dan kad će se u rog puhnuti. Tog Dana ćemo nevjernike modre sakupiti...');
INSERT INTO "bs_darassalam" VALUES(20,103,'20:103','i jedan drugom će tiho govoriti: “Niste ostali više od deset dana.”');
INSERT INTO "bs_darassalam" VALUES(20,104,'20:104','Mi dobro znamo o čemu će oni govoriti kad najrazboritiji između njih rekne: “Ostali ste samo dan jedan!”');
INSERT INTO "bs_darassalam" VALUES(20,105,'20:105','A pitaju te o planinama, pa ti reci: “Gospodar moj će ih u prah pretvoriti i razasuti...');
INSERT INTO "bs_darassalam" VALUES(20,106,'20:106','a mjesta na kojima su bile ravnom ledinom ostaviti...');
INSERT INTO "bs_darassalam" VALUES(20,107,'20:107','ni udubinā ni uzvisinā na Zemlji nećeš vidjeti.”');
INSERT INTO "bs_darassalam" VALUES(20,108,'20:108','Tog Dana će se oni glasniku odazvati, morat će ga slijediti i pred Milostivim glasovi će se stišati i ti ćeš samo šapat čuti.');
INSERT INTO "bs_darassalam" VALUES(20,109,'20:109','Tog Dana će biti od koristi posredovanje samo onog kome Milostivi dopusti i dozvoli da za nekog govori.');
INSERT INTO "bs_darassalam" VALUES(20,110,'20:110','On zna šta su radili i šta ih čeka, a oni znanjem ne mogu Njega obuhvatiti.');
INSERT INTO "bs_darassalam" VALUES(20,111,'20:111','Ljudi će se Živom i Vječnom pokoriti, onaj koji Mu je druge ravnim smatrao svaku nadu će izgubiti...');
INSERT INTO "bs_darassalam" VALUES(20,112,'20:112','a onaj ko je dobra djela činio, a vjernik bio, neće se nepravde ni zakidanja nagrade plašiti.');
INSERT INTO "bs_darassalam" VALUES(20,113,'20:113','I eto tako, Mi Kur’an na arapskom jeziku objavljujemo i u njemu opomene ponavljamo da bi se oni grijeha klonili ili da bi ih na poslušnost pobudio.');
INSERT INTO "bs_darassalam" VALUES(20,114,'20:114','Neka je uzvišen Allah, Vladar Istiniti! I ne žuri s učenjem Kur’ana prije nego što ti se objavljivanje njegovo završi, i reci: “Gospodaru moj, Ti znanje moje proširi!”');
INSERT INTO "bs_darassalam" VALUES(20,115,'20:115','A Ademu smo odmah u početku naredili, ali on je zaboravio i nije odlučan bio.');
INSERT INTO "bs_darassalam" VALUES(20,116,'20:116','A kad smo melekima rekli: “Sedždu učinite Ademu!”, svi su sedždu učinili, samo Iblis nije htio.');
INSERT INTO "bs_darassalam" VALUES(20,117,'20:117','“O Ademe”, rekli smo, “ovaj je doista neprijatelj tebi i tvojoj ženi, zato nikako ne dozvoli da on bude uzrok vašem izlasku iz dženneta, pa da se onda mučiš.');
INSERT INTO "bs_darassalam" VALUES(20,118,'20:118','U njemu nećeš ni ogladnjeti, ni go biti...');
INSERT INTO "bs_darassalam" VALUES(20,119,'20:119','u njemu nećeš ni ožednjeti, ni žegu osjetiti.”');
INSERT INTO "bs_darassalam" VALUES(20,120,'20:120','Ali šejtan mu poče bajati i govoriti: “O Ademe, hoćeš li da ti pokažem drvo besmrtnosti i carstvo koje neće nestati?”');
INSERT INTO "bs_darassalam" VALUES(20,121,'20:121','I njih dvoje pojedoše s njega i ukazaše im se stidna mjesta njihova pa počeše po sebi lišće džennetsko stavljati – tako Adem nije Gospodara svog poslušao i s Puta je skrenuo.');
INSERT INTO "bs_darassalam" VALUES(20,122,'20:122','Poslije ga je Gospodar njegov izabranikom učinio, pa mu oprostio i na Pravi put ga uputio.');
INSERT INTO "bs_darassalam" VALUES(20,123,'20:123','“Izlazite iz njega oboje”, reče On, “jedni drugima ćete neprijatelji biti!” Od Mene će vam uputa dolaziti, i onaj ko bude slijedio uputu Moju neće zalutati i neće nesretan biti.');
INSERT INTO "bs_darassalam" VALUES(20,124,'20:124','A onaj ko okrene glavu od Knjige Moje, taj će teškim životom živjeti i na Sudnjem danu ćemo ga slijepim oživiti.');
INSERT INTO "bs_darassalam" VALUES(20,125,'20:125','“Gospodaru moj”, reći će, “zašto si me slijepa oživio kad sam vid imao?”');
INSERT INTO "bs_darassalam" VALUES(20,126,'20:126','“Evo zašto: dokazi Naši su ti dolazili, ali si ih zaboravljao, pa ćeš danas ti isto tako biti zaboravljen”, reći će On.');
INSERT INTO "bs_darassalam" VALUES(20,127,'20:127','I tako ćemo Mi kazniti sve one koji se pohotama previše odaju i u dokaze Gospodara svog ne vjeruju. A patnja na onom svijetu bit će, uistinu, bolnija i vječna.');
INSERT INTO "bs_darassalam" VALUES(20,128,'20:128','Zar njima nije poznato koliko smo naroda prije njih uništili, onih po čijim nastambama oni hode! To su, zaista, dokazi za ljude pametne.');
INSERT INTO "bs_darassalam" VALUES(20,129,'20:129','I da nije ranije izrečene riječi Gospodara tvog, kazna bi već bila neminovna.');
INSERT INTO "bs_darassalam" VALUES(20,130,'20:130','Zato otrpi njihove riječi i obavljaj molitvu, slaveći Gospodara svog, prije izlaska Sunca i prije zalaska njegova, i obavljaj je u noćnim satima, i na krajevima dana – da bi bio zadovoljan.');
INSERT INTO "bs_darassalam" VALUES(20,131,'20:131','I nikako ne gledaj dugo ljepote ovog svijeta koje Mi kao užitak raznim sortama nevjernika pružamo, da ih time na kušnju stavimo, ta nagrada Gospodara tvog je bolja i vječna.');
INSERT INTO "bs_darassalam" VALUES(20,132,'20:132','Naredi čeljadi svojoj da namaz obavljaju i istraj u tome! Mi ne tražimo od tebe da se sam hraniš, Mi ćemo te hraniti! A samo one koji se budu Allaha bojali i grijeha klonili čeka lijep svršetak.');
INSERT INTO "bs_darassalam" VALUES(20,133,'20:133','Mnogobošci govore: “Zašto nam ne donese kakvo čudo od Gospodara svog?” A zar im nije došao dokaz u onom što je objavljeno u davnašnjim listovima?');
INSERT INTO "bs_darassalam" VALUES(20,134,'20:134','A da smo ih kakvom kaznom prije njega uništili, sigurno bi rekli: “Gospodaru naš, zašto nam nisi poslanika poslao, pa bismo riječi Tvoje slijedili prije nego što smo poniženi i osramoćeni postali?”');
INSERT INTO "bs_darassalam" VALUES(20,135,'20:135','Reci: “Svi mi čekamo, pa čekajte i vi, a sigurno ćete saznati ko su bili sljedbenici prave vjere i ko je bio na Pravom putu.”');
INSERT INTO "bs_darassalam" VALUES(21,1,'21:1','Ljudima se bliži čas polaganja računa njihova, a oni, bezbrižni, ne mare za to.');
INSERT INTO "bs_darassalam" VALUES(21,2,'21:2','I ne dođe im nijedna nova opomena od Gospodara njihova kojoj se, slušajući je, ne podsmjehuju...');
INSERT INTO "bs_darassalam" VALUES(21,3,'21:3','srca rasijanih. A mnogobošci govore šapatom: “Je li ovaj nešto drugo do čovjek kao i vi? Zar ćete slijediti vradžbinu, a vidite da jest?”');
INSERT INTO "bs_darassalam" VALUES(21,4,'21:4','“Gospodar moj zna”, reče on, “šta se govori i na nebu i na Zemlji. – On sve čuje i sve zna!”');
INSERT INTO "bs_darassalam" VALUES(21,5,'21:5','Oni, čak, govore: “To su samo smušeni snovi, on ga izmišlja, on je pjesnik, neka nam donese kakvo čudo kao i prijašnji poslanici!”');
INSERT INTO "bs_darassalam" VALUES(21,6,'21:6','Nijedan grad koji smo Mi prije njih uništili nije u čudo povjerovao, pa zar će ovi vjerovati?');
INSERT INTO "bs_darassalam" VALUES(21,7,'21:7','I prije tebe smo samo ljude slali kojima smo objavljivali, zato pitajte sljedbenike Knjige ako ne znate vi!');
INSERT INTO "bs_darassalam" VALUES(21,8,'21:8','Mi ih nismo stvarali kao bića koja žive bez hrane, niti su besmrtni bili.');
INSERT INTO "bs_darassalam" VALUES(21,9,'21:9','Poslije smo im obećanje ispunjavali i njih, i one koje smo htjeli, spašavali, a one koji su nevaljali bili uništavali.');
INSERT INTO "bs_darassalam" VALUES(21,10,'21:10','Mi vam Knjigu objavljujemo u kojoj je slava vaša, pa zašto ne razumijete?');
INSERT INTO "bs_darassalam" VALUES(21,11,'21:11','A koliko je bilo nevjerničkih sela i gradova koje smo uništili i poslije kojih smo druge narode podigli!');
INSERT INTO "bs_darassalam" VALUES(21,12,'21:12','I čim bi silu Našu osjetili, kud koji bi se iz njih razbježali.');
INSERT INTO "bs_darassalam" VALUES(21,13,'21:13','“Ne bježite, vratite se uživanjima vašim i domovima vašim, možda će vas neko što upitati.”');
INSERT INTO "bs_darassalam" VALUES(21,14,'21:14','“Teško nama”, oni bi govorili, “mi smo, zaista, nevjernici bili!”');
INSERT INTO "bs_darassalam" VALUES(21,15,'21:15','I kukali bi tako sve dok ih ne bismo učinili, kao žito požnjeveno, nepomičnim.');
INSERT INTO "bs_darassalam" VALUES(21,16,'21:16','Mi nismo stvorili nebo i Zemlju i ono što je između njih da se zabavljamo.');
INSERT INTO "bs_darassalam" VALUES(21,17,'21:17','Da smo htjeli uzeti neku zabavu, uzeli bismo je od Nas, ali Mi to nečinimo.');
INSERT INTO "bs_darassalam" VALUES(21,18,'21:18','nego istinom suzbijamo laž, istina je uguši i laži nestane – a teško vama zbog onog što o Njemu iznosite!');
INSERT INTO "bs_darassalam" VALUES(21,19,'21:19','Njegovo je ono što je na nebesima i na Zemlji! A oni koji su kod Njega ne zaziru da Mu se klanjaju, i ne zamaraju se...');
INSERT INTO "bs_darassalam" VALUES(21,20,'21:20','hvale Ga noću i danju, ne malaksavaju.');
INSERT INTO "bs_darassalam" VALUES(21,21,'21:21','Zar će kumiri, koje oni od zemlje prave, mrtve oživiti?');
INSERT INTO "bs_darassalam" VALUES(21,22,'21:22','Da Zemljom i nebesima upravljaju drugi bogovi, a ne Allah, poremetili bi se. Pa neka je uzvišen Allah, Gospodar Arša, od onog što Mu pripisuju!');
INSERT INTO "bs_darassalam" VALUES(21,23,'21:23','On neće biti pitan za ono što radi, a oni će biti pitani.');
INSERT INTO "bs_darassalam" VALUES(21,24,'21:24','Zar da pored Njega oni uzimaju bogove?! Reci: "Dokažite!" Ova Knjiga je pouka za moje sljedbenike, a bilo je knjiga i za one koji su prije mene bili i nestali. Međutim, većina njih ne zna istinu, pa zato glave okreću.');
INSERT INTO "bs_darassalam" VALUES(21,25,'21:25','Prije tebe nijednog poslanika nismo poslali, a da mu nismo objavili: “Nema istinskog Boga osim Mene, zato Meni ibadet činite!”');
INSERT INTO "bs_darassalam" VALUES(21,26,'21:26','Oni govore: “Milostivi ima dijete!” Hvaljen neka je On! A meleki su samo robovi poštovani.');
INSERT INTO "bs_darassalam" VALUES(21,27,'21:27','Oni ne govore dok On ne odobri i postupaju onako kako On naredi.');
INSERT INTO "bs_darassalam" VALUES(21,28,'21:28','On zna šta su radili i šta će uraditi, i oni će se samo za onog kojim On bude zadovoljan zauzimati, a oni su i sami, iz strahopoštovanja prema Njemu, brižni.');
INSERT INTO "bs_darassalam" VALUES(21,29,'21:29','A onog od njih koji bi rekao: “Ja sam, doista, pored Njega, bog!”, kaznili bismo džehennemom, jer Mi tako kažnjavamo mnogobošce.');
INSERT INTO "bs_darassalam" VALUES(21,30,'21:30','Zar ne znaju nevjernici da su nebesa i Zemlja bili jedna cjelina, pa smo ih Mi raskomadali, i da Mi od vode sve živo stvaramo? I zar neće vjerovati?');
INSERT INTO "bs_darassalam" VALUES(21,31,'21:31','Mi smo po Zemlji nepomične planine razmjestili da ih ona ne potrese, i po njima smo prolaze i staze učinili da bi oni kuda žele stizali.');
INSERT INTO "bs_darassalam" VALUES(21,32,'21:32','I to što je nebeski svod osiguran Naše je djelo, a oni se ipak okreću od znamenja koja su na njemu.');
INSERT INTO "bs_darassalam" VALUES(21,33,'21:33','I On je Taj Koji je stvorio noć i dan, i Sunce i Mjesec, i svi oni u svemiru plove.');
INSERT INTO "bs_darassalam" VALUES(21,34,'21:34','Nijedan čovjek prije tebe nije bio besmrtan; ako ti umreš, zar će oni dovijeka živjeti?');
INSERT INTO "bs_darassalam" VALUES(21,35,'21:35','Svako živo biće smrt će okusiti! Mi vas stavljamo na kušnju i u zlu i u dobru i Nama ćete se vratiti.');
INSERT INTO "bs_darassalam" VALUES(21,36,'21:36','Kad te vide nevjernici, samo ti se rugaju: “Je li to onaj koji vaše bogove huli?” A oni sami ne vjeruju kad se spomene Milostivi.');
INSERT INTO "bs_darassalam" VALUES(21,37,'21:37','Čovjek je stvoren od žurbe. Pokazat ću Ja vama, doista, dokaze Svoje, zato Me ne požurujte!');
INSERT INTO "bs_darassalam" VALUES(21,38,'21:38','Oni govore: “Kad će već jednom ta prijetnja, ako istinu govorite?”');
INSERT INTO "bs_darassalam" VALUES(21,39,'21:39','A da nevjernici znaju da tad neće moći otkloniti vatru od lica svojih i leđa svojih, i da im niko neće moći pružiti pomoć...');
INSERT INTO "bs_darassalam" VALUES(21,40,'21:40','nego će im nenadano doći i zaprepastiti ih i neće je moći nazad vratiti i neće im se vremena dati!');
INSERT INTO "bs_darassalam" VALUES(21,41,'21:41','I prije tebe su poslanike ruglu izvrgavali, pa je one koji su im se rugali stiglo baš ono čemu su se rugali.');
INSERT INTO "bs_darassalam" VALUES(21,42,'21:42','Reci: “Ko će vas od Milostivog noću i danju štititi?” Niko! Pa ipak oni od Kur’ana glave okreću.');
INSERT INTO "bs_darassalam" VALUES(21,43,'21:43','Zar oni imaju božanstva, osim Nas, koja će ih odbraniti?! Ona sama sebi ne mogu pomoći, niti se od Nas mogu zaštititi.');
INSERT INTO "bs_darassalam" VALUES(21,44,'21:44','Mi smo ovima, a i precima njihovim, dali da uživaju tako da im se život u tome oduljio. A zar oni ne vide da Mi Zemlju s krajeva njezinihumanjujemo, pa kako da oni budu pobjednici?!');
INSERT INTO "bs_darassalam" VALUES(21,45,'21:45','Reci: “Ja vas opominjem Objavom!”, ali gluhi ne čuju poziv kad se opominju.');
INSERT INTO "bs_darassalam" VALUES(21,46,'21:46','A da ih samo dašak kazne Gospodara tvog dotakne, sigurno bi povikali: “Teško nama, doista smo sami sebi nepravdu učinili!”');
INSERT INTO "bs_darassalam" VALUES(21,47,'21:47','Mi ćemo na Sudnjem danu ispravne terazije postaviti, pa se nikom krivo neće učiniti. Ako nešto bude teško koliko zrno gorušice, Mi ćemo za to kazniti ili nagraditi. A dosta je to što ćemo Mi račune ispitivati.');
INSERT INTO "bs_darassalam" VALUES(21,48,'21:48','Mi smo Musau i Harunu dali Tevrat, svjetlo i pouku za one koji se budu grijeha klonili...');
INSERT INTO "bs_darassalam" VALUES(21,49,'21:49','za one koji se Gospodara svog budu bojali i kad ih niko ne vidi, i koji od časa oživljenja budu strepjeli.');
INSERT INTO "bs_darassalam" VALUES(21,50,'21:50','A i ovaj Kur’an je blagoslovljena pouka koju objavljujemo, pa zar da ga vi poričete?');
INSERT INTO "bs_darassalam" VALUES(21,51,'21:51','Mi smo još prije Ibrahimu razboritost dali i dobro smo ga poznavali.');
INSERT INTO "bs_darassalam" VALUES(21,52,'21:52','Kad on ocu svom i narodu svom reče: “Kakvi su ovo kumiri kojima se i dan i noć klanjate?”');
INSERT INTO "bs_darassalam" VALUES(21,53,'21:53','Rekoše: “I naši preci su im se klanjali.”');
INSERT INTO "bs_darassalam" VALUES(21,54,'21:54','“I vi ste, a i preci vaši su bili u očitoj zabludi”, reče.');
INSERT INTO "bs_darassalam" VALUES(21,55,'21:55','“Govoriš li ti to ozbiljno ili se samo šališ?”, upitaše oni.');
INSERT INTO "bs_darassalam" VALUES(21,56,'21:56','“Ne.”, reče. “Gospodar vaš je Gospodar nebesa i Zemlje. On je njih stvorio i ja sam vam za to jedan od svjedoka.');
INSERT INTO "bs_darassalam" VALUES(21,57,'21:57','Tako mi Allaha, ja ću, čim se udaljite, vaše kumire udesiti!”');
INSERT INTO "bs_darassalam" VALUES(21,58,'21:58','I porazbija ih on u komade, osim onog najvećeg, da bi se njemu obratili.');
INSERT INTO "bs_darassalam" VALUES(21,59,'21:59','“Ko uradi ovo sa bogovima našim”, povikaše oni, “zaista je nasilnik!”');
INSERT INTO "bs_darassalam" VALUES(21,60,'21:60','“Čuli smo jednog momka kako ih huli”, rekoše, “ime mu je Ibrahim.”');
INSERT INTO "bs_darassalam" VALUES(21,61,'21:61','“Dovedite ga da ga ljudi vide”, rekoše, “da posvjedoče.”');
INSERT INTO "bs_darassalam" VALUES(21,62,'21:62','“Jesi li ti uradio ovo s bogovima našim, o Ibrahime?”, upitaše.');
INSERT INTO "bs_darassalam" VALUES(21,63,'21:63','“To je učinio ovaj najveći od njih, pitajte ih ako umiju govoriti”, reče on.');
INSERT INTO "bs_darassalam" VALUES(21,64,'21:64','I oni se zamisliše, pa sami sebi rekoše: “Vi ste, zaista, nepravedni!”');
INSERT INTO "bs_darassalam" VALUES(21,65,'21:65','Zatim glave oboriše i rekoše: “Ta ti znaš da ovi ne govore!”');
INSERT INTO "bs_darassalam" VALUES(21,66,'21:66','“Pa zašto se onda, umjesto Allahu, klanjate onima koji vam ne mogu ni koristiti niti od vas kakvu štetu otkloniti?”, upita on.');
INSERT INTO "bs_darassalam" VALUES(21,67,'21:67','“Teško vama i onima kojima se, umjesto Allahu, klanjate! Zašto se ne opametite?”');
INSERT INTO "bs_darassalam" VALUES(21,68,'21:68','“Spalite ga i bogove vaše osvetite, ako hoćete išta učiniti!”, povikaše.');
INSERT INTO "bs_darassalam" VALUES(21,69,'21:69','“O vatro”, rekosmo Mi, “postani hladna, i spas Ibrahimu!”');
INSERT INTO "bs_darassalam" VALUES(21,70,'21:70','I oni mu htjedoše postaviti zamku, ali ih Mi onemogućismo...');
INSERT INTO "bs_darassalam" VALUES(21,71,'21:71','i spasismo i njega i Luta u zemlju koju smo za ljude blagoslovili...');
INSERT INTO "bs_darassalam" VALUES(21,72,'21:72','i poklonismo mu Ishaka, i Jakuba, kao unuka, i sve ih učinismo dobrim...');
INSERT INTO "bs_darassalam" VALUES(21,73,'21:73','I učinismo ih vođama da upućuju prema zapovijedi Našoj, i objavismo im da čine dobra djela, i da namaz obavljaju, i da zekat udjeljuju, i samo su Nama ibadet činili.');
INSERT INTO "bs_darassalam" VALUES(21,74,'21:74','I Lutu mudrost i znanje dadosmo i iz grada ga, u kome su stanovnici njegovi odvratne stvari činili, izbavismo – to, uistinu, bijaše narod razvratan i zao...');
INSERT INTO "bs_darassalam" VALUES(21,75,'21:75','i u milost Našu ga uvedosmo; on je doista od onih dobrih.');
INSERT INTO "bs_darassalam" VALUES(21,76,'21:76','I Nuhu se, kad u davno vrijeme zavapi, odazvasmo i njega i čeljad njegovu od jada velikog spasismo...');
INSERT INTO "bs_darassalam" VALUES(21,77,'21:77','i od naroda ga, koji je smatrao neistinitim dokaze Naše, zaštitismo. To bijahu opaki ljudi, pa ih sve potopismo.');
INSERT INTO "bs_darassalam" VALUES(21,78,'21:78','I Davudu i Sulejmanu, kad su sudili o usjevu što su ga noću ovce nečije opasle – i Mi smo bili svjedoci suđenju njihovu...');
INSERT INTO "bs_darassalam" VALUES(21,79,'21:79','i učinismo da Sulejman pronikne u to, a obojici smo mudrost i znanje dali. I potčinismo planine i ptice da s Davudom Allaha hvale – to smo Mi bili kadri učiniti.');
INSERT INTO "bs_darassalam" VALUES(21,80,'21:80','I naučismo ga da izrađuje pancire za vas da vas štite u borbi s neprijateljem – pa zašto niste zahvalni?');
INSERT INTO "bs_darassalam" VALUES(21,81,'21:81','A Sulejmanu vjetar jaki poslušnim učinismo, po zapovijedi njegovoj je puhao prema zemlji koju smo blagoslovili, a Mi sve dobro znamo.');
INSERT INTO "bs_darassalam" VALUES(21,82,'21:82','I šejtane neke da zbog njega rone, a radili su i poslove druge, i nad njima smo Mi bdjeli.');
INSERT INTO "bs_darassalam" VALUES(21,83,'21:83','I Ejjubu se – kad je Gospodaru svom zavapio: “Mene je nevolja snašla, a Ti si od milostivih najmilostiviji!”...');
INSERT INTO "bs_darassalam" VALUES(21,84,'21:84','I Mi mu se odazvasmo i nevolju mu koja ga je morila otklonismo ivratismo mu, milošću Našom, porodicu njegovu, i uz njih još toliko, i dabude opomena onima koji su Nama pokorni.');
INSERT INTO "bs_darassalam" VALUES(21,85,'21:85','I Ismailu, i Idrisu, i Zulkiflu – a svi su oni bili strpljivi.');
INSERT INTO "bs_darassalam" VALUES(21,86,'21:86','I obasusmo ih milošću Našom, oni doista bijahu dobri.');
INSERT INTO "bs_darassalam" VALUES(21,87,'21:87','I Zunnunu se, kad srdit ode i pomisli da ga nećemo kazniti – pa poslije u tminama zavapi: “Nema boga, osim Tebe, hvaljen neka si, a ja sam se zaista ogriješio prema sebi!”...');
INSERT INTO "bs_darassalam" VALUES(21,88,'21:88','Pa mu se odazvasmo i tegobe ga spasismo; eto, tako Mi spašavamo vjernike.');
INSERT INTO "bs_darassalam" VALUES(21,89,'21:89','I spomeni Zekerijjaa, kada pozva Gospodara svoga: “Gospodaru moj, ne ostavljaj me samog, Ti si najbolji Nasljednik!’’');
INSERT INTO "bs_darassalam" VALUES(21,90,'21:90','Pa smo mu se odazvali i poklonili mu Jahjaa i suprugu mu izliječili su se trudili da što više dobra učine i molili su Nam se u nadi i strahu, i bili su prema Nama ponizni.');
INSERT INTO "bs_darassalam" VALUES(21,91,'21:91','I spomeni onu koja je sačuvala čednost – djevičanstvo svoje, pa smo u nju Naš duh udahnuli, i nju i sina njezina učinili da budu svim svjetovima znamenje.');
INSERT INTO "bs_darassalam" VALUES(21,92,'21:92','Doista, ova vaša vjera – jedna je vjera, a Ja sam – vaš Gospodar, pa Meni u ibadetu budite!');
INSERT INTO "bs_darassalam" VALUES(21,93,'21:93','I oni su se između sebe u vjeri podvojili, a svi će se Nama vratiti.');
INSERT INTO "bs_darassalam" VALUES(21,94,'21:94','Ko bude dobra djela činio i uz to vjernik bio, trud mu neće lišen nagrade ostati, jer smo ga, sigurno, Mi pribilježili.');
INSERT INTO "bs_darassalam" VALUES(21,95,'21:95','A dopušteno nije da se stanovnici bilo kojeg naselja koje smo Mi uništili vrate!');
INSERT INTO "bs_darassalam" VALUES(21,96,'21:96','I kad se oslobode Je’džudž i Me’džudž i kad se budu niz sve strmine žurno spuštali,');
INSERT INTO "bs_darassalam" VALUES(21,97,'21:97','i približi se istinita prijetnja, tad će se pogledi nevjernika ukočiti. “Teško nama, mi smo prema ovom ravnodušni bili, mi smo sami sebi nepravdu učinili!”');
INSERT INTO "bs_darassalam" VALUES(21,98,'21:98','I vi, i oni kojima se, pored Allaha, klanjate bit ćete gorivo u džehennemu, a u njega ćete doista ući.');
INSERT INTO "bs_darassalam" VALUES(21,99,'21:99','Da su oni bogovi, ne bi u njega ušli, i svi će u njemu vječno boraviti...');
INSERT INTO "bs_darassalam" VALUES(21,100,'21:100','u njemu će prigušeno uzdisati, u njemu ništa neće čuti.');
INSERT INTO "bs_darassalam" VALUES(21,101,'21:101','A oni kojima smo još prije lijepu nagradu obećali, oni će od njega daleko biti...');
INSERT INTO "bs_darassalam" VALUES(21,102,'21:102','huku njegovu neće čuti, i vječno će u onom što im budu duše željele uživati...');
INSERT INTO "bs_darassalam" VALUES(21,103,'21:103','neće ih brinuti najveći užas, nego će ih meleki dočekivati: “Evo ovo je vaš dan, vama obećan!”...');
INSERT INTO "bs_darassalam" VALUES(21,104,'21:104','Na Dan kad smotamo nebesa kao što se smota list papira za pisanje, onako kako smo prvi put iz ništa stvorili, tako ćemo ponovo iz ništa stvoriti, to je obećanje Naše, Mi ćemo, doista, to učiniti.');
INSERT INTO "bs_darassalam" VALUES(21,105,'21:105','Mi smo u objavama, poslije Ploče pomno čuvane, napisali da će Zemlju Moji čestiti robovi naslijediti.');
INSERT INTO "bs_darassalam" VALUES(21,106,'21:106','U ovom je doista pouka za ljude koji se budu Allahu klanjali...');
INSERT INTO "bs_darassalam" VALUES(21,107,'21:107','a tebe smo samo kao milost svjetovima poslali.');
INSERT INTO "bs_darassalam" VALUES(21,108,'21:108','Reci: “Meni se objavljuje da je vaš Bog – jedan Bog, pa samo Njemu budite predani!”');
INSERT INTO "bs_darassalam" VALUES(21,109,'21:109','I ako oni leđa okrenu, ti reci: “Ja sam vas sve, bez razlike, opomenuo, a ne znam je li blizu ili daleko ono čime vam se prijeti.');
INSERT INTO "bs_darassalam" VALUES(21,110,'21:110','On zna glasno izgovorene riječi, zna i ono što krijete');
INSERT INTO "bs_darassalam" VALUES(21,111,'21:111','a ja ne znam da nije to vama iskušenje i pružanje uživanja još za neko vrijeme.');
INSERT INTO "bs_darassalam" VALUES(21,112,'21:112','“Gospodaru moj, presudi onako kako su zaslužili”, reče on, “a Gospodar naš Svemilosni jeste Onaj od Kojega treba tražiti pomoć protiv onoga čime Ga opisujete.”');
INSERT INTO "bs_darassalam" VALUES(22,1,'22:1','O ljudi, Gospodara svog se bojte! Zaista će potres, kad Smak svijeta nastupi, veliki događaj biti!');
INSERT INTO "bs_darassalam" VALUES(22,2,'22:2','Na Dan kad ga doživite svaka dojilja će ono što doji zaboraviti, a svaka trudnica će svoj plod pobaciti, i ti ćeš vidjeti ljude pijane, a oni neće pijani biti, već će tako izgledati zato što će Allahova kazna strašna biti.');
INSERT INTO "bs_darassalam" VALUES(22,3,'22:3','Ima ljudi koji se bez ikakva znanja prepiru o Allahu i koji slijede svakog šejtana prkosnoga.');
INSERT INTO "bs_darassalam" VALUES(22,4,'22:4','Za njega je već propisano da će svakog onog ko ga uzme za zaštitnika na stranputicu zavesti i u patnju ga ognjenu naputiti.');
INSERT INTO "bs_darassalam" VALUES(22,5,'22:5','O ljudi! Ako ste u sumnji spram proživljenja – pa Mi smo vas, zaista,stvorili od prašine, zatim od kapi sjemena, potom od zakvačka, zatim od grude mesa, oblikovanog i neoblikovanog, da bismo vama objasnili! A u matericama zadržavamo šta hoćemo do roka određenog, zatim činimo da se kao dojenčad rađate i da poslije do muževnog doba uzrastete; neko od vas umire, a neko duboku starost doživljava, tako da, nakon što je znao, ne zna ništa. I ti vidiš zemlju kako je zamrla, ali kad na nju vodu spustimo, ona ustrepće i uzbuja, i iz nje iznikne svakovrsno bilje prekrasno.');
INSERT INTO "bs_darassalam" VALUES(22,6,'22:6','To je zato što je Allah, baš On, zaista, Istina i što On oživljava mrtve, i što On nad svime ima moć,');
INSERT INTO "bs_darassalam" VALUES(22,7,'22:7','i što će Čas oživljenja, u to nema sumnje, doći i što će Allah one ugrobovima oživjeti.');
INSERT INTO "bs_darassalam" VALUES(22,8,'22:8','Ima ljudi koji se o Allahu prepiru bez ikakva znanja i bez ikakva nadahnuća i bez Knjige svjetilje,');
INSERT INTO "bs_darassalam" VALUES(22,9,'22:9','hodeći nadmeno da bi s Allahova puta odvraćali; njih na ovom svijetu čeka poniženje, a na onom svijetu dat ćemo im da iskuse patnju u ognju.');
INSERT INTO "bs_darassalam" VALUES(22,10,'22:10','“Eto, to je zbog djela ruku tvojih, jer Allah nije nepravedan prema robovima Svojim.”');
INSERT INTO "bs_darassalam" VALUES(22,11,'22:11','Ima ljudi kao onaj koji Allaha obožava kolebljivo, na ivici: ako ga zadesi dobro, smiri se time, a ako ga zadesi iskušenje, on se potpuno okrene, pa tako izgubi i dunjaluk i ahiret. To je, uistinu, očiti gubitak.');
INSERT INTO "bs_darassalam" VALUES(22,12,'22:12','On se, pored Allaha, klanja onom koji od njega ne može nikakvu štetu otkloniti niti mu može bilo kakvu korist pribaviti – to je, zaista, velika zabluda...');
INSERT INTO "bs_darassalam" VALUES(22,13,'22:13','moli onoga što će mu prije nauditi nego od koristi biti; takav je, doista, loš zaštitnik i zao drug!');
INSERT INTO "bs_darassalam" VALUES(22,14,'22:14','Allah će one koji vjeruju i čine dobra djela, zaista, uvesti u džennetske bašče kroz koje rijeke teku. – Allah radi ono što hoće!');
INSERT INTO "bs_darassalam" VALUES(22,15,'22:15','Onaj ko misli da Poslaniku Allah neće pomoći ni na ovom ni na onom svijetu, neka rastegne uže na tavanicu da se objesi pa ga prekine i neka razmisli hoće li njegov postupak, ako se objesi, otkloniti ono zbog čega se on ljuti.');
INSERT INTO "bs_darassalam" VALUES(22,16,'22:16','I tako, eto, Mi ga u vidu jasnih ajeta objavljujemo, a Allah će onom kome On hoće na Pravi put ukazati.');
INSERT INTO "bs_darassalam" VALUES(22,17,'22:17','Zaista će onima koji vjeruju, jevrejima, sabijcima, kršćanima, poklonicima vatre i višebošcima, Allah presuditi na Kijametskom danu. Allah je, zaista, svemu svjedok.');
INSERT INTO "bs_darassalam" VALUES(22,18,'22:18','Zar ne znaš da se i oni na nebesima i oni na Zemlji Allahu klanjaju, a i Sunce, i Mjesec, i zvijezde, i planine, i drveće, i životinje, i mnogi ljudi, a mnogi kaznu i zaslužuju. A koga Allah ponizi, niko ga ne može poštovanim učiniti. – Allah ono što hoće radi.');
INSERT INTO "bs_darassalam" VALUES(22,19,'22:19','Ova dva protivnička tabora spore se oko Gospodara svog. Onima koji ne budu vjerovali bit će odijela od vatre skrojena, a ključala voda bit će na glave njihove lijevana...');
INSERT INTO "bs_darassalam" VALUES(22,20,'22:20','od nje će se istopiti ono što je u trbusima njihovim, i koža...');
INSERT INTO "bs_darassalam" VALUES(22,21,'22:21','a gvozdenim maljevima bit će mlaćeni...');
INSERT INTO "bs_darassalam" VALUES(22,22,'22:22','kad god pokušaju zbog teškog jada iz nje izići, bit će u nju vraćeni: “Iskusite patnju u užasnoj vatri!”');
INSERT INTO "bs_darassalam" VALUES(22,23,'22:23','A one koji budu vjerovali i dobra djela činili Allah će sigurno uvesti u džennetske bašče, kroz koje rijeke teku, u njima će se narukvicama od zlata i biserom kititi, a odjeća će im svilena biti.');
INSERT INTO "bs_darassalam" VALUES(22,24,'22:24','Oni su bili nadahnuti da govore lijepe riječi i bili su nadahnuti na put Onog Koji je hvale dostojan.');
INSERT INTO "bs_darassalam" VALUES(22,25,'22:25','Nevjernicima i onima koji odvraćaju od Allahova puta i Časnog hrama – a Mi smo ga namijenili svim ljudima, kako za mještanina tako i za došljaka – i onom ko u njemu bilo kakvo nasilje učini dat ćemo da patnju nesnosnu iskusi.');
INSERT INTO "bs_darassalam" VALUES(22,26,'22:26','I kada smo Ibrahimu pokazali mjesto Kuće rekavši: “Meni ništa ne pridružuj, i očisti Kuću Moju za one koji će je obilaziti, koji će stajati i koji će na ruku‘u i sedždi biti.”');
INSERT INTO "bs_darassalam" VALUES(22,27,'22:27','I oglasi ljudima hadž – i dolazit će ti pješke i na kamilama iznurenim koje dolaze sa dalekog puta');
INSERT INTO "bs_darassalam" VALUES(22,28,'22:28','da bi koristi imali i da bi u određene dane, prilikom klanja kurbana, kojim ih je Allah opskrbio, Njegovo ime spominjali. Jedite meso njihovo, a nahranite i siromaha ubogog!');
INSERT INTO "bs_darassalam" VALUES(22,29,'22:29','Zatim, neka sa sebe prljavštinu uklone, neka svoje zavjete ispune i neka oko Hrama drevnoga obilaze.');
INSERT INTO "bs_darassalam" VALUES(22,30,'22:30','To je tako! A ko Allahove zabrane smatra velikim, pa to će mu biti dobro kod Gospodara njegova – a dozvoljena vam je stoka, osim one koja vam se spominje – pa klonite se prljavštine kumira i klonite se lažna govora,');
INSERT INTO "bs_darassalam" VALUES(22,31,'22:31','budite iskreno Allahu odani, ne smatrajte nikog Njemu ravnim! A onaj ko bude smatrao da Allahu ima iko ravan – bit će kao onaj koji je s neba pao i koga su ptice razgrabile, ili kao onaj kojeg je vjetar u daleki predio odnio.');
INSERT INTO "bs_darassalam" VALUES(22,32,'22:32','Eto toliko! Pa ko poštuje Allahove propise – znak je čestita srca.');
INSERT INTO "bs_darassalam" VALUES(22,33,'22:33','Vama one služe do određenog roka, a poslije, njihovo mjesto je kraj Drevnog hrama.');
INSERT INTO "bs_darassalam" VALUES(22,34,'22:34','Svakoj vjerskoj zajednici propisali smo klanje kurbana da bi spominjali Allahovo ime prilikom klanja stoke koju im On daje. Vaš Bog je jedan Bog, zato se samo Njemu iskreno predajte! A radosnom viješću obraduj poslušne...');
INSERT INTO "bs_darassalam" VALUES(22,35,'22:35','čija srca, kad se Allah spomene, strah obuzme, i one koji strpljivo podnose nevolje koje ih zadese, i one koji molitvu obavljaju i koji, od onog što im Mi dajemo, udjeljuju.');
INSERT INTO "bs_darassalam" VALUES(22,36,'22:36','A deve smo vam učinili jednim od Allahovih obreda hadža, i vi od njih imate koristi, zato spominjite Allahovo ime kad budu u redove poredane. A kad padnu na zemlju, preklane, jedite ih, a nahranite i onog koji ne prosi, a i onog koji prosi – tako smo vam ih potčinili da biste zahvalni bili.');
INSERT INTO "bs_darassalam" VALUES(22,37,'22:37','Do Allaha neće doprijeti meso njihovo i krv njihova, ali će Mu stići iskreno učinjena dobra djela vaša; tako vam ih je potčinio da biste Allaha veličali zato što vas je uputio – i obraduj one koji dobra djela čine!');
INSERT INTO "bs_darassalam" VALUES(22,38,'22:38','Allah doista štiti vjernike, Allah sigurno ne voli nijednog izdajnika, nezahvalnika.');
INSERT INTO "bs_darassalam" VALUES(22,39,'22:39','Dopušta se odbrana onima koje drugi napadnu, zato što im se nasilje čini – a Allah ih je, doista, kadar pomoći...');
INSERT INTO "bs_darassalam" VALUES(22,40,'22:40','onima koji su ni krivi ni dužni iz zavičaja svog prognani samo zato što su govorili: “Gospodar naš je Allah!” A da Allah ne suzbija neke ljude drugima, do temelja bi bili porušeni manastiri, i crkve, i havre, a i džamije u kojima se mnogo spominje Allahovo ime. A Allah će sigurno pomoći one koji vjeru Njegovu pomažu – ta Allah je zaista moćan i silan...');
INSERT INTO "bs_darassalam" VALUES(22,41,'22:41','one koji će, ako im damo vlast na Zemlji, molitvu obavljati i milostinju udjeljivati i koji će tražiti da se čine dobra djela, a odvraćati od nevaljalih. – A Allahu se na kraju sve vraća.');
INSERT INTO "bs_darassalam" VALUES(22,42,'22:42','A to što te oni smatraju lažnim – pa i prije njih su narod Nuhov, i Ad, i Semud poslanike smatrali lažnim...');
INSERT INTO "bs_darassalam" VALUES(22,43,'22:43','i narod Ibrahimov, i narod Lutov...');
INSERT INTO "bs_darassalam" VALUES(22,44,'22:44','i stanovnici Medjena; a lažnim je smatran i Musa. I Ja sam nevjernicima vremena davao, zatim sam ih kažnjavao, pa kakva je bila osuda Moja?!');
INSERT INTO "bs_darassalam" VALUES(22,45,'22:45','I koliko smo naselja uništili, čiji su žitelji grješni bili, i ona su opustjela, samo su ruševine ostale! I koliko bunareva ima zapuštenih i koliko visokih palata ima praznih!');
INSERT INTO "bs_darassalam" VALUES(22,46,'22:46','Zašto oni po svijetu ne putuju pa da srca njihova shvate ono što trebaju shvatiti i da uši njihove čuju ono što trebaju čuti, ali oči nisu slijepe, već srca u grudima.');
INSERT INTO "bs_darassalam" VALUES(22,47,'22:47','Oni od tebe traže da ih kazna što prije stigne, a Allah će ispuniti prijetnju Svoju; samo jedan dan u Gospodara tvog traje koliko hiljadu godina, po vašem računanju.');
INSERT INTO "bs_darassalam" VALUES(22,48,'22:48','I koliko je bilo naselje čiji su žitelji bili nepravedni, kojima sam Ja kaznu odlagao, da bih ih najzad kaznio! A Meni se sve vraća.');
INSERT INTO "bs_darassalam" VALUES(22,49,'22:49','Reci: “O ljudi, ja sam tu da vas javno opomenem!”');
INSERT INTO "bs_darassalam" VALUES(22,50,'22:50','One koji budu vjerovali i dobra djela činili čeka oproštaj i opskrba plemenita...');
INSERT INTO "bs_darassalam" VALUES(22,51,'22:51','a oni koji se budu trudili da dokaze Naše poreknu, uvjereni da će Nam umaći, bit će stanovnici džehennema.');
INSERT INTO "bs_darassalam" VALUES(22,52,'22:52','Prije tebe Mi nijednog poslanika i vjerovjesnika nismo poslali, a da šejtan nije, kad bi on što kazivao, u kazivanje njegovo nešto ubacio. Allah bi ono što bi šejtan ubacio uklonio, a zatim bi riječi Svoje učvrstio – Allah sve zna i mudar je...');
INSERT INTO "bs_darassalam" VALUES(22,53,'22:53','To je zato da ono što šejtan ubaci učini iskušenjem za one u čijim srcima je bolest i za one čija su srca okrutna – a zulumćari su, zaista, u rascjepu i na stranputici dalekoj,');
INSERT INTO "bs_darassalam" VALUES(22,54,'22:54','i da bi oni koji su znanjem obdareni spoznali da je Kur’an istina od Gospodara tvog, pa u njega povjerovali i da mu srca njihova budu sklona. A Allah će vjernike, doista, na Pravi put izvesti.');
INSERT INTO "bs_darassalam" VALUES(22,55,'22:55','A nevjernici neće prestati u njega sumnjati, sve dok im iznenada Čas ne dođe ili dok ne osjete patnju u jednom strahotnom danu.');
INSERT INTO "bs_darassalam" VALUES(22,56,'22:56','Tog Dana vlast će samo Allah imati, On će im suditi: vjernici i oni koji su dobra djela činili bit će u džennetskim baščama uživanja...');
INSERT INTO "bs_darassalam" VALUES(22,57,'22:57','a nevjernici i oni koji su ajete Naše poricali, oni će u patnji sramnoj boraviti.');
INSERT INTO "bs_darassalam" VALUES(22,58,'22:58','Allah će, zaista, lijepo opskrbiti one koji su svoj rodni kraj napustili da bi se na Allahovu putu borili, pa zatim poginuli ili umrli, a zaista je Allah, baš On, najbolji Opskrbitelj.');
INSERT INTO "bs_darassalam" VALUES(22,59,'22:59','On će ih u mjesto kojim će oni biti zadovoljni doista uvesti! – A Allah sve zna i blag je.');
INSERT INTO "bs_darassalam" VALUES(22,60,'22:60','Eto tako! A onom ko istom mjerom uzvrati za učinjeno zlo i kome opet nepravda bude učinjena, Allah će, sigurno, pomoći. – Allah je Onaj Koji grijehe poništava i Koji ih prašta.');
INSERT INTO "bs_darassalam" VALUES(22,61,'22:61','Eto tako! A Allah smjenjuje noć danom, a dan noću, i Allah je Onaj Koji sve čuje i sve vidi.');
INSERT INTO "bs_darassalam" VALUES(22,62,'22:62','To je zato što je Allah – Istina, a oni koje oni, pored Allaha, mole – laž, i zato što je Allah Svevišnji i Veliki.');
INSERT INTO "bs_darassalam" VALUES(22,63,'22:63','Zar ne vidiš da Allah s neba spušta kišu od koje zemlja zazeleni? – Allah je zaista dobar i sve zna...');
INSERT INTO "bs_darassalam" VALUES(22,64,'22:64','Njegovo je ono što je na nebesima i ono što je na Zemlji! Allah, doista, nije ni o kome ovisan, i On je jedini hvale dostojan!');
INSERT INTO "bs_darassalam" VALUES(22,65,'22:65','Zar ne vidiš da je Allah sve što je na Zemlji vama podredio, pa i lađe koje, voljom Njegovom, morem plove? On drži ono što je na nebu da ne bi palo na Zemlju, osim ako On to dopusti. – Allah je, uistinu, prema ljudima blag i milostiv.');
INSERT INTO "bs_darassalam" VALUES(22,66,'22:66','On vam daje život i zatim će vam ga oduzeti i poslije vas opet oživiti – a čovjek je, zaista, nezahvalan!');
INSERT INTO "bs_darassalam" VALUES(22,67,'22:67','Svakoj vjerskoj zajednici propisali smo vjerozakon prema kojem se trebala vladati, i ne dozvoli, nikako, da se s tobom o tome raspravljaju. Ti pozivaj svom Gospodaru jer ti si, uistinu, na Pravom putu.');
INSERT INTO "bs_darassalam" VALUES(22,68,'22:68','A ako se oni budu s tobom prepirali, reci: “Allah dobro zna ono što vi radite!”');
INSERT INTO "bs_darassalam" VALUES(22,69,'22:69','Allah će vam na Sudnjem danu presuditi o onom u čemu se razilazite.');
INSERT INTO "bs_darassalam" VALUES(22,70,'22:70','Zar ne znaš da je Allahu poznato sve što je na nebesima i na Zemlji? To je sve u Knjizi. To je, uistinu, Allahu lahko!');
INSERT INTO "bs_darassalam" VALUES(22,71,'22:71','Višebošci, pored Allaha, obožavaju one za koje im On nije poslao nikakav dokaz, i o njima sami ništa ne znaju. A zulumćarima nema pomagača.');
INSERT INTO "bs_darassalam" VALUES(22,72,'22:72','A kad im se Naši jasni ajeti kazuju, ti primjećuješ veliko negodovanje na licima onih što ne vjeruju, koji umalo da ne nasrnu na one koji im riječi Naše kazuju. Reci: “Hoćete li da vam kažem što će vam biti mrže od toga? – Vatra kojom Allah nevjernicima prijeti, a grozno će ona prebivalište biti.”');
INSERT INTO "bs_darassalam" VALUES(22,73,'22:73','O ljudi, evo jednog primjera, pa ga poslušajte: oni kojima se vi, pored Allaha, klanjate ne mogu nikako ni mušicu stvoriti, makar se radi nje sakupili. A ako bi mušica nešto ugrabila, oni to ne bi mogli od nje izbaviti; nejak je i onaj koji se klanja, a i onaj kome se klanja!');
INSERT INTO "bs_darassalam" VALUES(22,74,'22:74','Oni ne veličaju i ne poznaju Allaha kako treba. – A Allah je, uistinu, moćan i silan.');
INSERT INTO "bs_darassalam" VALUES(22,75,'22:75','Allah odabire poslanike među melekima i ljudima. – Allah sve čuje i sve vidi.');
INSERT INTO "bs_darassalam" VALUES(22,76,'22:76','On zna šta su uradili i šta će uraditi. – A Allahu se sve vraća.');
INSERT INTO "bs_darassalam" VALUES(22,77,'22:77','O vjernici, pred Allahom se sagibajte i čelima po tlu padajte, Gospodara svog obožavajte i dobra djela činite da biste postigli ono što želite...');
INSERT INTO "bs_darassalam" VALUES(22,78,'22:78','i borite se, Allaha radi, onako kako se treba boriti! On vas je izabrao i u vjeri vam nije ništa teško propisao, u vjeri pretka vašeg Ibrahima. Allah vas je odavno muslimanima nazvao, i u ovom Kur’anu, da bi Poslanik bio svjedok protiv vas i da biste vi bili svjedoci protiv ostalih ljudi. Zato, molitvu obavljajte i zekat dajte i u Allaha se pouzdajte. On je Gospodar vaš, i to kakav Gospodar i kakav zaštitnik!');
INSERT INTO "bs_darassalam" VALUES(23,1,'23:1','Ono što žele vjernici će postići...');
INSERT INTO "bs_darassalam" VALUES(23,2,'23:2','oni koji molitvu svoju ponizno obavljaju...');
INSERT INTO "bs_darassalam" VALUES(23,3,'23:3','i koji ono što ih se ne tiče izbjegavaju...');
INSERT INTO "bs_darassalam" VALUES(23,4,'23:4','i koji zekat udjeljuju...');
INSERT INTO "bs_darassalam" VALUES(23,5,'23:5','i koji stidna mjesta svoja čuvaju...');
INSERT INTO "bs_darassalam" VALUES(23,6,'23:6','osim od žena svojih ili onih koje su u posjedu njihovu, oni, doista, prijekor ne zaslužuju...');
INSERT INTO "bs_darassalam" VALUES(23,7,'23:7','a oni koji i pored toga traže, oni u zlu sasvim pretjeruju...');
INSERT INTO "bs_darassalam" VALUES(23,8,'23:8','i koji o povjerenim im emanetima i obavezama svojim brigu brinu...');
INSERT INTO "bs_darassalam" VALUES(23,9,'23:9','i koji molitve svoje na vrijeme obavljaju...');
INSERT INTO "bs_darassalam" VALUES(23,10,'23:10','oni su dostojni nasljednici...');
INSERT INTO "bs_darassalam" VALUES(23,11,'23:11','koji će džennet naslijediti, oni će u njemu vječno boraviti.');
INSERT INTO "bs_darassalam" VALUES(23,12,'23:12','Mi čovjeka od bīti zemlje stvaramo...');
INSERT INTO "bs_darassalam" VALUES(23,13,'23:13','zatim ga kao kap sjemena na sigurno mjesto stavljamo...');
INSERT INTO "bs_darassalam" VALUES(23,14,'23:14','pa onda kap sjemena ugruškom učinimo, zatim od ugruška grudu mesa stvorimo, pa od grude mesa kosti napravimo, a onda kosti mesom zaodjenemo, i poslije ga, kao drugo stvorenje, oživimo – pa neka je uzvišen Allah, najljepši Stvoritelj!');
INSERT INTO "bs_darassalam" VALUES(23,15,'23:15','Vi ćete, poslije toga, pomrijeti...');
INSERT INTO "bs_darassalam" VALUES(23,16,'23:16','zatim ćete, na onom svijetu, oživljeni biti.');
INSERT INTO "bs_darassalam" VALUES(23,17,'23:17','Mi smo sedam nebesa iznad vas stvorili, i Mi bdijemo nad onim što smo stvorili.');
INSERT INTO "bs_darassalam" VALUES(23,18,'23:18','Mi s neba s mjerom kišu spuštamo, i u zemlji je zadržavamo – a kadri smo je i odvesti...');
INSERT INTO "bs_darassalam" VALUES(23,19,'23:19','i pomoću nje bašče za vas podižemo od palmi i loze vinove – u njima mnogo voća imate, i vi ga jedete...');
INSERT INTO "bs_darassalam" VALUES(23,20,'23:20','i drvo koje na Sinajskoj gori raste, koje zejtin daje i začin je onima koji jedu.');
INSERT INTO "bs_darassalam" VALUES(23,21,'23:21','I stoka vam je pouka: Mi vam dajemo da pijete ono što se nalazi u utrobama njezinim, i vi od nje mnogo koristi imate i vi se njome hranite...');
INSERT INTO "bs_darassalam" VALUES(23,22,'23:22','i na njima, i na lađama se vozite.');
INSERT INTO "bs_darassalam" VALUES(23,23,'23:23','Mi smo poslali Nuha narodu njegovu, i on je govorio: “O narode moj, Allahu se samo klanjajte, vi drugog boga osim Njega nemate; zar se ne bojite?”');
INSERT INTO "bs_darassalam" VALUES(23,24,'23:24','Ali su glavešine naroda njegova, koje nisu vjerovale, govorile: “Ovo je samo čovjek kao i vi, samo hoće da je od vas ugledniji. Da je Allah htio, meleke bi poslao; ovakvo nešto nismo čuli od naših predaka davnih...');
INSERT INTO "bs_darassalam" VALUES(23,25,'23:25','on je čovjek džinom opsjednuti, pa pustite ga neko vrijeme!”');
INSERT INTO "bs_darassalam" VALUES(23,26,'23:26','“Gospodaru moj”, reče on, “pomozi mi, oni me u laž utjeruju!”');
INSERT INTO "bs_darassalam" VALUES(23,27,'23:27','I Mi mu objavismo: "Gradi lađu pod Našom pažnjom i prema Našem nadahnuću, pa kad zapovijed Naša dođe i voda na površinu zemlje izbije, ti u nju ukrcaj od svake vrste po par, mužjaka i ženku, i čeljad svoju, ali ne one o kojima je već sud donesen , i ne obraćaj Mi se za nevjernike, jer će, doista, biti potopljeni."');
INSERT INTO "bs_darassalam" VALUES(23,28,'23:28','Pa kad se smjestiš u lađu, ti i oni koji su uz tebe, reci: “Hvala Allahu Koji nas je spasio naroda koji ne vjeruje!”');
INSERT INTO "bs_darassalam" VALUES(23,29,'23:29','I reci: “Gospodaru moj, iskrcaj me na blagoslovljeno mjesto, Ti to najbolje umiješ!”');
INSERT INTO "bs_darassalam" VALUES(23,30,'23:30','U tome su, zaista, pouke, a Mi smo doista stavljali na kušnju.');
INSERT INTO "bs_darassalam" VALUES(23,31,'23:31','Poslije njih smo druga pokoljenja stvarali...');
INSERT INTO "bs_darassalam" VALUES(23,32,'23:32','i jednog između njih bismo im kao poslanika poslali: “Allahu se samo klanjajte, vi drugog boga osim Njega nemate, zar se ne bojite?”');
INSERT INTO "bs_darassalam" VALUES(23,33,'23:33','Ali bi glavešine naroda njegova, koji nisu vjerovali, koji su poricali da će na onom svijetu biti oživljeni i kojima smo dali da u životu na ovom svijetu raskošno žive, govorili: “On je čovjek kao i vi – jede ono što i vi jedete, i pije ono što i vi pijete...');
INSERT INTO "bs_darassalam" VALUES(23,34,'23:34','i ako se budete pokoravali čovjeku kao što ste vi, sigurno ćete biti izgubljeni.');
INSERT INTO "bs_darassalam" VALUES(23,35,'23:35','Zar vama on da prijeti da ćete, pošto pomrete i zemlja i kosti postanete, doista oživljeni biti?');
INSERT INTO "bs_darassalam" VALUES(23,36,'23:36','Daleko je, daleko ono čime vam se prijeti!');
INSERT INTO "bs_darassalam" VALUES(23,37,'23:37','Postoji samo život na ovom svijetu, mi živimo i umiremo, a oživljeni nećemo biti!"');
INSERT INTO "bs_darassalam" VALUES(23,38,'23:38','On je čovjek koji o Allahu iznosi laži, i mi mu ne vjerujemo.”');
INSERT INTO "bs_darassalam" VALUES(23,39,'23:39','“Gospodaru moj”, reče on, “pomozi mi, oni me u laž utjeruju!”');
INSERT INTO "bs_darassalam" VALUES(23,40,'23:40','“Uskoro će se oni pokajati!”, odgovorio bi On.');
INSERT INTO "bs_darassalam" VALUES(23,41,'23:41','I zasluženo bi ih pogodio strašan glas, i Mi bismo ih kao što je nanos riječni učinili – stradao bi narod nasilnički!');
INSERT INTO "bs_darassalam" VALUES(23,42,'23:42','A zatim bismo, poslije njih, druga pokoljenja stvarali...');
INSERT INTO "bs_darassalam" VALUES(23,43,'23:43','nijedan narod ne može ubrzati ni usporiti vrijeme propasti svoje...');
INSERT INTO "bs_darassalam" VALUES(23,44,'23:44','i poslanike, jedne za drugim slali. Kad bi jednom narodu došao njegov poslanik, u laž bi ga utjerivali, i Mi smo ih zato jedne drugima smjenjivali, i samo u pričama o njima spomen sačuvali – daleko bili ljudi koji nisu vjerovali!');
INSERT INTO "bs_darassalam" VALUES(23,45,'23:45','Poslije smo poslali Musaa i brata mu Haruna sa znamenjima Našim i dokazom očiglednim...');
INSERT INTO "bs_darassalam" VALUES(23,46,'23:46','faraonu i glavešinama njegovim, ali su se oni uzoholili, bili su to ljudi nadmeni.');
INSERT INTO "bs_darassalam" VALUES(23,47,'23:47','“Zar da povjerujemo dvojici ljudi koji su isti kao i mi, a narod njihov je roblje naše?”, govorili su.');
INSERT INTO "bs_darassalam" VALUES(23,48,'23:48','I njih dvojicu su lažljivcima proglasili, pa su zato uništeni bili.');
INSERT INTO "bs_darassalam" VALUES(23,49,'23:49','Musau smo onda Knjigu dali da bi sinovi Israilovi Pravim putem išli.');
INSERT INTO "bs_darassalam" VALUES(23,50,'23:50','I sina Merjemina i majku njegovu znakom smo učinili. Mi smo njih na jednoj visoravni s tekućom vodom nastanili.');
INSERT INTO "bs_darassalam" VALUES(23,51,'23:51','“O poslanici, dozvoljenim i lijepim jelima se hranite i dobra djela činite, jer Ja dobro znam šta vi radite!');
INSERT INTO "bs_darassalam" VALUES(23,52,'23:52','Ova vaša vjera – jedina je prava vjera, a Ja sam Gospodar vaš, pa Me se bojte!”');
INSERT INTO "bs_darassalam" VALUES(23,53,'23:53','A oni su se u pitanjima vjere svoje podijelili na skupine, svaka stranka radosna onim što ispovijeda...');
INSERT INTO "bs_darassalam" VALUES(23,54,'23:54','zato ostavi ove u zabludi njihovoj još neko vrijeme!');
INSERT INTO "bs_darassalam" VALUES(23,55,'23:55','Misle li oni – kad ih imetkom i sinovima pomažemo...');
INSERT INTO "bs_darassalam" VALUES(23,56,'23:56','da žurimo da im neko dobro učinimo? Nikako, ali oni ne opažaju.');
INSERT INTO "bs_darassalam" VALUES(23,57,'23:57','Oni koji iz bojazni prema Gospodaru svom strahuju...');
INSERT INTO "bs_darassalam" VALUES(23,58,'23:58','i oni koji u dokaze Gospodara svog vjeruju...');
INSERT INTO "bs_darassalam" VALUES(23,59,'23:59','i oni koji druge Gospodaru svom ravnim ne smatraju...');
INSERT INTO "bs_darassalam" VALUES(23,60,'23:60','i oni koji od onog što im se daje udjeljuju, i čija su srca puna straha zato što će se vratiti svom Gospodaru...');
INSERT INTO "bs_darassalam" VALUES(23,61,'23:61','oni hitaju da čine dobra djela, i radi njih druge pretiču.');
INSERT INTO "bs_darassalam" VALUES(23,62,'23:62','Mi nikog ne opterećujemo preko njegovih mogućnosti. U Nas je Knjiga koja istinu govori, i nikom se nepravda neće učiniti.');
INSERT INTO "bs_darassalam" VALUES(23,63,'23:63','Ali, srca nevjernika su prema ovom sasvim ravnodušna, a pored toga i ružna djela stalno čine.');
INSERT INTO "bs_darassalam" VALUES(23,64,'23:64','A kad smo na muke stavili one među njima koji su raskošnim životom živjeli, oni su odmah zapomagali.');
INSERT INTO "bs_darassalam" VALUES(23,65,'23:65','“Sad ne zapomažite, Mi vam nećemo pomoć ukazati!');
INSERT INTO "bs_darassalam" VALUES(23,66,'23:66','Vama su ajeti Moji kazivani, ali vi ste uzmicali...');
INSERT INTO "bs_darassalam" VALUES(23,67,'23:67','dičeći se Hramom i sijeleći – vi ste ružne riječi govorili!”');
INSERT INTO "bs_darassalam" VALUES(23,68,'23:68','Zašto oni o Kur’anu ne razmisle? Ili im dolazi nešto što nije dolazilo njihovim precima davnim?');
INSERT INTO "bs_darassalam" VALUES(23,69,'23:69','Ili oni ne poznaju Poslanika svog, pa ga zato poriču?');
INSERT INTO "bs_darassalam" VALUES(23,70,'23:70','Kako govore: “On je lud!” Međutim, on im istinu donosi, ali većina njih prezire istinu.');
INSERT INTO "bs_darassalam" VALUES(23,71,'23:71','Da se Allah za prohtjevima njihovim povodi, sigurno bi nestalo poretka na nebesima i na Zemlji i u onom što je na njima. Mi smo im dali Kur’an, slavu njihovu, ali oni za slavu svoju ne haju.');
INSERT INTO "bs_darassalam" VALUES(23,72,'23:72','Ili od njih tražiš nagradu? Ta nagrada Gospodara tvog bolja je, On najbolje nagrađuje.');
INSERT INTO "bs_darassalam" VALUES(23,73,'23:73','Ti njih pozivaš na Pravi put...');
INSERT INTO "bs_darassalam" VALUES(23,74,'23:74','ali oni koji u onaj svijet neće da vjeruju s Pravog puta, doista, skreću.');
INSERT INTO "bs_darassalam" VALUES(23,75,'23:75','I kad bismo im se smilovali i nevolje ih oslobodili, opet bi oni u zabludi svojoj jednako lutali.');
INSERT INTO "bs_darassalam" VALUES(23,76,'23:76','Mi smo ih na muke stavljali, ali se oni Gospodaru nisu pokorili, niti su molitve upućivali...');
INSERT INTO "bs_darassalam" VALUES(23,77,'23:77','tek kad im kapiju teške patnje otvorimo, oni će nadu izgubiti i u očajanje zapasti.');
INSERT INTO "bs_darassalam" VALUES(23,78,'23:78','On vam daje sluh, i vid, i pamet – a kako malo vi zahvaljujete!');
INSERT INTO "bs_darassalam" VALUES(23,79,'23:79','On vas na Zemlji stvara, i pred Njim ćete se sakupiti...');
INSERT INTO "bs_darassalam" VALUES(23,80,'23:80','On život i smrt daje i samo od Njega zavisi izmjena noći i dana, pa zašto ne shvatite?');
INSERT INTO "bs_darassalam" VALUES(23,81,'23:81','Ali, oni govore kao što su govorili oni prije njih.');
INSERT INTO "bs_darassalam" VALUES(23,82,'23:82','Govorili su: “Zar kad pomremo i kad prah i kosti postanemo, zar ćemo, zaista, biti oživljeni?');
INSERT INTO "bs_darassalam" VALUES(23,83,'23:83','I nama i još davno precima našim time se prijetilo, ali to su samo izmišljotine naroda drevnih.”');
INSERT INTO "bs_darassalam" VALUES(23,84,'23:84','Upitaj: “Čija je Zemlja i sve ono što je na njoj, znate li?”');
INSERT INTO "bs_darassalam" VALUES(23,85,'23:85','“Allahova!”, odgovorit će, a ti reci: “Pa zašto onda ne dođete sebi?”');
INSERT INTO "bs_darassalam" VALUES(23,86,'23:86','Upitaj: “Ko je Gospodar sedam nebesa i ko je Gospodar Arša veličanstvenog?”');
INSERT INTO "bs_darassalam" VALUES(23,87,'23:87','“Allah!”, odgovorit će, a ti reci: “Pa zašto se onda ne bojite?”');
INSERT INTO "bs_darassalam" VALUES(23,88,'23:88','Upitaj: “U čijoj je ruci vlast nad svim, i ko uzima u zaštitu, i od koga niko ne može zaštićen biti, znate li?”');
INSERT INTO "bs_darassalam" VALUES(23,89,'23:89','“Allahovo je to!”, odgovorit će, a ti reci: “Pa zašto onda dopuštate da budete zavedeni?”');
INSERT INTO "bs_darassalam" VALUES(23,90,'23:90','Da, Mi im istinu donosimo, a oni su zaista lažljivci...');
INSERT INTO "bs_darassalam" VALUES(23,91,'23:91','Allah nije uzeo Sebi sina, i s Njime nema drugog boga! Inače, svaki bi bog, s onim što je stvorio, radio što bi htio, i jedan drugog bi pobjeđivao. – Hvaljen neka je Allah Koji je daleko od onog što oni iznose...');
INSERT INTO "bs_darassalam" VALUES(23,92,'23:92','Koji zna i nevidljivi i vidljivi svijet i On je vrlo visoko iznad onih koje Njemu ravnim smatraju!');
INSERT INTO "bs_darassalam" VALUES(23,93,'23:93','Reci: “Gospodaru moj, ako mi hoćeš pokazati ono čime se njima prijeti...');
INSERT INTO "bs_darassalam" VALUES(23,94,'23:94','onda me, Gospodaru moj, ne ostavi s narodom nevjerničkim!”');
INSERT INTO "bs_darassalam" VALUES(23,95,'23:95','A Mi ti zaista možemo pokazati ono čime im prijetimo.');
INSERT INTO "bs_darassalam" VALUES(23,96,'23:96','Ti lijepim zlo uzvrati, Mi dobro znamo šta oni iznose...');
INSERT INTO "bs_darassalam" VALUES(23,97,'23:97','i reci: “Tebi se ja, Gospodaru moj, obraćam za zaštitu od priviđenja šejtanskih...');
INSERT INTO "bs_darassalam" VALUES(23,98,'23:98','i Tebi se, Gospodaru moj, obraćam da me od njihova prisustva zaštitiš!”');
INSERT INTO "bs_darassalam" VALUES(23,99,'23:99','Kad nekom od njih smrt dođe, on uzvikne: “Gospodaru moj, povrati me...');
INSERT INTO "bs_darassalam" VALUES(23,100,'23:100','da uradim kakvo dobro u onom što sam ostavio!” Nikad! To su riječi koje će on uzalud govoriti – pred njima će prepreka biti sve do Dana kad će oživljeni biti.');
INSERT INTO "bs_darassalam" VALUES(23,101,'23:101','Pa kad se u rog puhne, tad rodbinskih veza među njima neće biti i jedni druge neće ništa pitati.');
INSERT INTO "bs_darassalam" VALUES(23,102,'23:102','Oni čija dobra djela budu teška, oni će želje svoje ostvariti...');
INSERT INTO "bs_darassalam" VALUES(23,103,'23:103','a oni čija dobra djela budu lahka, oni će posve izgubljeni biti, u džehennemu će vječno boraviti...');
INSERT INTO "bs_darassalam" VALUES(23,104,'23:104','vatra će im lica pržiti i iskeženih zuba će u njemu ostati.');
INSERT INTO "bs_darassalam" VALUES(23,105,'23:105','“Zar vam ajeti Moji nisu kazivani, a vi ste ih poricali?”');
INSERT INTO "bs_darassalam" VALUES(23,106,'23:106','“Gospodaru naš”, reći će, “naši prohtjevi su bili od nas jači, te smo postali narod zalutali.');
INSERT INTO "bs_darassalam" VALUES(23,107,'23:107','Gospodaru naš, izbavi nas iz nje. Ako bismo ponovo zlo radili, sami bismo sebi nepravdu učinili.”');
INSERT INTO "bs_darassalam" VALUES(23,108,'23:108','“Ostanite u njoj prezreni i ništa Mi ne govorite!”, reći će On.');
INSERT INTO "bs_darassalam" VALUES(23,109,'23:109','“Kad su neki robovi Moji govorili: ‘Gospodaru naš, mi vjerujemo, zato nam oprosti i smiluj nam se, jer Ti si najmilostiviji!’...');
INSERT INTO "bs_darassalam" VALUES(23,110,'23:110','vi ste im se toliko rugali da ste zbog toga na Mene zaboravljali i uvijek ste ih ismijavali.');
INSERT INTO "bs_darassalam" VALUES(23,111,'23:111','Njih sam Ja danas nagradio za ono što su trpjeli, oni su, doista, postigli ono što su željeli.”');
INSERT INTO "bs_darassalam" VALUES(23,112,'23:112','“A koliko ste godina na Zemlji proveli?”, bit će rečeno.');
INSERT INTO "bs_darassalam" VALUES(23,113,'23:113','“Proveli smo dan ili samo dio dana”, odgovorit će, “pitaj one koji su brojali.”');
INSERT INTO "bs_darassalam" VALUES(23,114,'23:114','“Pa da, kratko ste proveli”, reći će im se, “da ste samo znali!”');
INSERT INTO "bs_darassalam" VALUES(23,115,'23:115','“Zar ste mislili da smo vas uzalud stvorili i da Nam se nećete povratiti?”');
INSERT INTO "bs_darassalam" VALUES(23,116,'23:116','I neka je uzvišen Allah, Vladar istiniti, nema drugog boga osim Njega, Gospodara Arša veličanstvenog!');
INSERT INTO "bs_darassalam" VALUES(23,117,'23:117','A onaj koji se, pored Allaha, moli drugom bogu, bez ikakva dokaza o njemu, pred Gospodarom svojim će račun polagati, i nevjernici ono što žele neće postići.');
INSERT INTO "bs_darassalam" VALUES(23,118,'23:118','I reci: “Gospodaru moj, oprosti i smiluj se, Ti si najmilostiviji!');
INSERT INTO "bs_darassalam" VALUES(24,1,'24:1','Objavljujemo suru i njezine propise činimo obaveznim! U njoj objavljujemo jasne dokaze da biste pouku primili.');
INSERT INTO "bs_darassalam" VALUES(24,2,'24:2','Bludnicu i bludnika izbičujte sa stotinu udaraca biča, svakog od njih, i neka vas pri vršenju Allahovih propisa ne obuzima prema njima nikakvo sažaljenje, ako u Allaha i u onaj svijet vjerujete, i neka kažnjavanju njihovu jedna skupina vjernika prisustvuje!');
INSERT INTO "bs_darassalam" VALUES(24,3,'24:3','Bludnik se ne treba ženiti osim bludnicom ili mnogoboškinjom, a bludnica ne treba biti poželjna osim bludniku ili mnogobošcu – to je zabranjeno vjernicima.');
INSERT INTO "bs_darassalam" VALUES(24,4,'24:4','One koji okrive poštene žene, a ne dokažu to s četiri svjedoka, sa osamdeset udaraca biča izbičujte i nikad više svjedočenje njihovo ne primajte – to su nečasni ljudi...');
INSERT INTO "bs_darassalam" VALUES(24,5,'24:5','osim onih koji se poslije toga pokaju i poprave, jer i Allah prašta i samilostan je!');
INSERT INTO "bs_darassalam" VALUES(24,6,'24:6','A oni koji okrive svoje žene, a ne budu imali drugih svjedoka, nego su samo oni svjedoci, potvrdit će svoje svjedočenje zakletvom Allahom, i to četiri puta da zaista govore istinu...');
INSERT INTO "bs_darassalam" VALUES(24,7,'24:7','a peti put da ga pogodi Allahovo prokletstvo ako laže!');
INSERT INTO "bs_darassalam" VALUES(24,8,'24:8','A ona će kazne biti pošteđena, ako se četiri puta Allahom zakune da on, doista, laže...');
INSERT INTO "bs_darassalam" VALUES(24,9,'24:9','a peti put da je stigne Allahova srdžba, ako on govori istinu!');
INSERT INTO "bs_darassalam" VALUES(24,10,'24:10','A da nije Allahove dobrote prema vama i milosti Njegove i da Allah ne prima pokajanje i da mudar nije...');
INSERT INTO "bs_darassalam" VALUES(24,11,'24:11','Među vama je bilo onih koji su iznosili potvoru. Vi ne smatrajte to nekim zlom po vas. Ne, to je dobro po vas. Svaki od njih bit će kažnjen prema grijehu koji je zaslužio, a onog od njih koji je to najviše činio čeka patnja velika.');
INSERT INTO "bs_darassalam" VALUES(24,12,'24:12','Zašto, čim ste to čuli, nisu vjernici i vjernice jedni o drugima dobro pomislili i rekli: “Ovo je očita potvora!”');
INSERT INTO "bs_darassalam" VALUES(24,13,'24:13','Zašto nisu četvericu svjedoka doveli? A pošto svjedoke nisu doveli, oni su onda kod Allaha lažljivci.');
INSERT INTO "bs_darassalam" VALUES(24,14,'24:14','A da nije Allahove dobrote prema vama i milosti Njegove i na ovom i na onom svijetu, već bi vas stigla teška kazna zbog onog u što ste se upustili...');
INSERT INTO "bs_darassalam" VALUES(24,15,'24:15','kad ste to jezicima svojim prepričavati stali i kad ste na sva usta govorili ono o čemu niste ništa znali, a vi ste to sitnicom smatrali, ali je ono Allahu krupno.');
INSERT INTO "bs_darassalam" VALUES(24,16,'24:16','Zašto niste, čim ste to čuli, rekli: “Ne dolikuje nam da o tome govorimo, hvaljen neka si Ti! To je velika potvora!”');
INSERT INTO "bs_darassalam" VALUES(24,17,'24:17','Allah vam naređuje da više nikad takvo nešto ne ponovite, ako ste vjernici...');
INSERT INTO "bs_darassalam" VALUES(24,18,'24:18','i Allah vam propise objašnjava. – A Allah sve zna i mudar je.');
INSERT INTO "bs_darassalam" VALUES(24,19,'24:19','One koji vole da se o vjernicama šire bestidne glasine čeka teška kazna i na ovom i na onom svijetu. – Allah sve zna, a vi ne znate.');
INSERT INTO "bs_darassalam" VALUES(24,20,'24:20','A da nije Allahove dobrote prema vama i milosti Njegove i da Allah nije blag i milostiv...');
INSERT INTO "bs_darassalam" VALUES(24,21,'24:21','O vjernici, ne idite šejtanovim stopama! Onog ko bude išao šejtanovim stopama on će na razvrat i odvratna djela navoditi. A da nije Allahove dobrote prema vama i milosti Njegove, nijedan se od vas ne bi nikad od grijeha očistio; ali Allah čisti onog koga On hoće. – Allah sve čuje i sve zna.');
INSERT INTO "bs_darassalam" VALUES(24,22,'24:22','Neka se čestiti i imućni među vama ne zaklinju da više neće pomagati rođake i siromahe, i one koji su na Allahovom putu rodni kraj svoj napustili; neka im oproste i ne zamjere! Zar vam ne bi bilo drago da i vama Allah oprosti? – A Allah prašta i samilostan je.');
INSERT INTO "bs_darassalam" VALUES(24,23,'24:23','Oni koji obijede čestite, bezazlene vjernice, neka budu prokleti i na ovom i na onom svijetu; njih čeka patnja nesnosna...');
INSERT INTO "bs_darassalam" VALUES(24,24,'24:24','na Dan kad protiv njih budu svjedočili jezici njihovi, i ruke njihove, i noge njihove za ono što su radili.');
INSERT INTO "bs_darassalam" VALUES(24,25,'24:25','Tog Dana Allah će ih kazniti kaznom koju su zaslužili i oni će saznati da je Allah, doista, Istina očigledna.');
INSERT INTO "bs_darassalam" VALUES(24,26,'24:26','Nevaljale žene su za nevaljale muškarce, a nevaljali muškarci su za nevaljale žene; čestite žene su za čestite muškarce, a čestiti muškarci su za čestite žene. Oni nisu krivi za ono što o njima govore, njih čeka oprost i veliko obilje.');
INSERT INTO "bs_darassalam" VALUES(24,27,'24:27','O vjernici, u tuđe kuće ne ulazite dok dopuštenje ne dobijete i dok ukućane ne pozdravite – to vam je bolje, poučite se!');
INSERT INTO "bs_darassalam" VALUES(24,28,'24:28','A ako u njima nikog ne nađete, ne ulazite u njih dok vam se ne dopusti; a ako vam se rekne: “Vratite se!”, vi se vratite, bolje vam je, a Allah zna ono što radite.');
INSERT INTO "bs_darassalam" VALUES(24,29,'24:29','Nije vam grijeh ulaziti u nenastanjene zgrade, u kojima se nalaze stvari vaše, a Allah zna ono što javno pokazujete i ono što krijete.');
INSERT INTO "bs_darassalam" VALUES(24,30,'24:30','Reci vjernicima neka obore poglede svoje i neka vode brigu o stidnim mjestima svojim; to im je bolje, jer Allah, uistinu, zna ono što oni rade.');
INSERT INTO "bs_darassalam" VALUES(24,31,'24:31','A reci vjernicama neka obore poglede svoje i neka vode brigu o stidnim mjestima svojim; i neka ne dozvole da se od ukrasa njihovih vidi išta osim onog što je ionako spoljašnje, i neka vela svoja spuste na grudi svoje; neka ukrase svoje ne pokazuju drugima, to mogu samo muževima svojim, ili očevima svojim, ili očevima muževa svojih, ili sinovima svojim, ili sinovima muževa svojih, ili braći svojoj ili sinovima braće svoje, ili sinovima sestara svojih, ili prijateljicama svojim, ili robinjama svojim, ili muškarcima kojima nisu potrebne žene, ili djeci koja još ne znaju koja su stidna mjesta žena; i neka ne udaraju nogama svojim da bi se čuo zveket nakita njihova koji pokrivaju. I svi se Allahu pokajte, o vjernici, da biste postigli ono što želite.');
INSERT INTO "bs_darassalam" VALUES(24,32,'24:32','Udavajte neudate i ženite neoženjene, i čestite robove i robinje svoje; ako su siromašni, Allah će im iz obilja Svog dati. – Allah je neizmjerno dobar i sve zna.');
INSERT INTO "bs_darassalam" VALUES(24,33,'24:33','I neka se suzdrže oni koji se nemaju mogućnosti oženiti, dok im Allah iz obilja svog ne pomogne! A s onima u posjedu vašem koji se žele otkupiti, ako su u stanju to učiniti, o otkupu se dogovorite. I dajte im nešto od imetka koji je Allah vama dao. I ne nagonite robinje svoje da se bludom bave – a one žele biti čestite – da biste stekli prolazna dobra ovog svijeta. A ako ih neko na to prisili, Allah će im, zato što su bile primorane, oprostiti i prema njima samilostan biti.');
INSERT INTO "bs_darassalam" VALUES(24,34,'24:34','Mi vam objavljujemo jasne ajete i primjere iz života onih koji su prije vas bili i nestali i pouku onima koji se budu Allaha bojali.');
INSERT INTO "bs_darassalam" VALUES(24,35,'24:35','Allah je svjetlost nebesa i Zemlje! Primjer svjetlosti Njegove je udubina u zidu u kojoj je svjetiljka. Svjetiljka je u kandilju, a kandilj je kao zvijezda blistava koja se užiže blagoslovljenim drvetom maslinovim, i istočnim i zapadnim, čije ulje gotovo da sija kad ga vatra ne dotakne – sama svjetlost nad svjetlošću! Allah vodi ka svjetlosti Svojoj onog koga On hoće. Allah navodi primjere ljudima, Allah sve dobro zna.');
INSERT INTO "bs_darassalam" VALUES(24,36,'24:36','U džamijama koje se Allahovom voljom podižu i u kojima se spominje Njegovo ime – hvale Njega, ujutro i navečer...');
INSERT INTO "bs_darassalam" VALUES(24,37,'24:37','ljudi koje kupovina i prodaja ne ometaju da Allaha spominju i koji molitvu obavljaju i milostinju udjeljuju, i koji strepe od Dana u kom će srca i pogledi biti uznemireni...');
INSERT INTO "bs_darassalam" VALUES(24,38,'24:38','da bi ih Allah lijepom nagradom za djela njihova nagradio i da bi im od dobrote Svoje i više dao. A Allah daje kome hoće, bez računa.');
INSERT INTO "bs_darassalam" VALUES(24,39,'24:39','A djela nevjernika su kao varka, fatamorgana u ravnici u kojoj žedan vidi vodu, ali kad do tog mjesta dođe, ništa ne nađe – a zateći će da ga čeka kraj njega Allahova kazna i On će mu potpuno isplatiti račun njegov jer Allah veoma brzo obračunava...');
INSERT INTO "bs_darassalam" VALUES(24,40,'24:40','ili su kao tmine nad dubokim morem koje prekrivaju talasi sve jedan za drugim, iznad kojih su oblaci, sve tmine jedna iznad drugih, prst se pred okom ne vidi – a onaj kome Allah ne da svjetlo neće svjetla ni imati.');
INSERT INTO "bs_darassalam" VALUES(24,41,'24:41','Zar ne znaš da Allaha hvale svi koji su na nebesima i na Zemlji, a i ptice širenjem krila svojih; svi znaju kako će Mu se moliti i kako će Ga hvaliti. A Allah dobro zna ono što oni rade.');
INSERT INTO "bs_darassalam" VALUES(24,42,'24:42','Samo je Allahova vlast na nebesima i na Zemlji, i Allahu se sve vraća!');
INSERT INTO "bs_darassalam" VALUES(24,43,'24:43','Zar ne vidiš da Allah razgoni oblake, i onda ih spaja i jedne nad drugima gomila, pa ti vidiš kišu kako iz njih pada. On s neba, iz oblaka veličine brda, spušta grad, pa njime koga hoće pogodi, a koga hoće poštedi – blijesak munje Njegove gotovo da oduzme vid.');
INSERT INTO "bs_darassalam" VALUES(24,44,'24:44','On čini da noć i dan naizmjenice nastupaju i u tome je, doista, pouka za one koji pameti imaju.');
INSERT INTO "bs_darassalam" VALUES(24,45,'24:45','Allah sve životinje stvara od vode, neke od njih na trbuhu puze, neke idu na dvije noge, a neke, opet, hode na četiri. – Allah stvara što hoće, jer Allah sve može.');
INSERT INTO "bs_darassalam" VALUES(24,46,'24:46','Mi objavljujemo ajete jasne. A Allah ukazuje na Pravi put onom kome On hoće.');
INSERT INTO "bs_darassalam" VALUES(24,47,'24:47','A dvoličnjaci govore: “Mi vjerujemo u Allaha i Poslanika i pokoravamo se”, zatim neki od njih glave okreću, nisu oni vjernici.');
INSERT INTO "bs_darassalam" VALUES(24,48,'24:48','Kad budu pozvani Allahu i Poslaniku Njegovu da im presudi, neki od njih odjednom leđa okrenu...');
INSERT INTO "bs_darassalam" VALUES(24,49,'24:49','samo ako znaju da je pravda na njihovoj strani, dolaze mu poslušno.');
INSERT INTO "bs_darassalam" VALUES(24,50,'24:50','Jesu li im srca bolesna, ili sumnjaju, ili strahuju da će Allah i Poslanik Njegov prema njima nepravedno postupiti? Nijedno, nego žele drugima nepravdu učiniti.');
INSERT INTO "bs_darassalam" VALUES(24,51,'24:51','Kad se vjernici Allahu i Poslaniku Njegovu pozovu da im presudi, samo reknu: “Slušamo i pokoravamo se!” Oni će uspjeti.');
INSERT INTO "bs_darassalam" VALUES(24,52,'24:52','Oni koji se Allahu i Poslaniku Njegovu budu pokoravali, koji se Allaha budu bojali i koji od Njega budu strahovali – oni će postići ono što budu željeli.');
INSERT INTO "bs_darassalam" VALUES(24,53,'24:53','Dvoličnjaci se zaklinju Allahom, najtežom zakletvom, da će sigurno ići u boj ako im ti narediš. Reci: “Ne zaklinjite se! Pokornost – zna se šta je! Allah dobro zna ono što vi radite.”');
INSERT INTO "bs_darassalam" VALUES(24,54,'24:54','Reci: “Pokoravajte se Allahu i pokoravajte se Poslaniku!” A ako ne htjednete, on je dužan raditi ono što se njemu naređuje, a vi ste dužni raditi ono što se vama naređuje, pa ako mu budete poslušni, bit ćete na Pravom putu – a Poslanik je jedino dužan jasno obznaniti.');
INSERT INTO "bs_darassalam" VALUES(24,55,'24:55','Allah obećava da će one među vama koji budu vjerovali i dobra djela činili sigurno nasljednicima na Zemlji postaviti, kao što je postavio one prije njih, i da će im zacijelo vjeru njihovu učvrstiti, onu koju im On želi, i da će im sigurno strah sigurnošću zamijeniti; oni će se samo Meni klanjati, i neće druge Meni ravnim smatrati. A oni koji i poslije toga budu nevjernici – oni su pravi griješnici.');
INSERT INTO "bs_darassalam" VALUES(24,56,'24:56','A vi molitvu obavljajte i zekat dajte i Poslaniku poslušni budite da bi vam se ukazala milost.');
INSERT INTO "bs_darassalam" VALUES(24,57,'24:57','Nikako ne misli da će nevjernici umaći Njemu na Zemlji; njihovo boravište bit će vatra, a ona je, zaista, grozno prebivalište!');
INSERT INTO "bs_darassalam" VALUES(24,58,'24:58','O vjernici, neka od vas u tri slučaja zatraže dopuštenje da vam uđu oni koji su u posjedu vašem i oni koji još nisu spolno zreli: prije jutarnje molitve i kad u podne odložite odjeću svoju, i poslije obavljanja noćne molitve. To su tri doba kad niste obučeni, a u drugo doba nije ni vama ni njima grijeh, ta vi jedni drugima morate ulaziti. Tako vam Allah objašnjava propise! A Allah sve zna i mudar je.');
INSERT INTO "bs_darassalam" VALUES(24,59,'24:59','A kad djeca vaša dostignu spolnu zrelost, neka onda uvijek traže dopuštenje za ulazak, kao što su tražili dopuštenje oni stariji od njih – tako vam Allah objašnjava propise Svoje! A Allah sve zna i mudar je.');
INSERT INTO "bs_darassalam" VALUES(24,60,'24:60','A starim ženama koje više ne žude za udajom nije grijeh da odlože ogrtače svoje, ali ne pokazujući ona mjesta na kojima se ukrasi nose; a bolje im je da budu kreposne. – Allah sve čuje i zna.');
INSERT INTO "bs_darassalam" VALUES(24,61,'24:61','Nije grijeh slijepcu, niti je grijeh hromu, niti je grijeh bolesnu! Vama nije grijeh jesti u kućama vašim, ili kućama očeva vaših, ili u kućama matera vaših, ili u kućama braće vaše, ili u kućama sestara vaših, ili u kućama amidža vaših, ili u kućama tetaka vaših po ocu, ili u kućama daidža vaših, ili u kućama tetaka vaših po materi, ili u onih čiji su ključevi u vas ili u prijatelja vašeg – nije vam grijeh jesti zajednički ili pojedinačno. A kad ulazite u kuće, vi ukućane njene pozdravite pozdravom od Allaha propisanim, blagoslovljenim i uljudnim. Tako vam Allah objašnjava propise, da biste se opametili!');
INSERT INTO "bs_darassalam" VALUES(24,62,'24:62','Pravi vjernici su samo oni koji u Allaha i Njegova Poslanika vjeruju, a koji se, kad su s njime na kakvom odgovornom sastanku, ne udaljuju dok od njega dopuštenje ne dobiju. Oni koji od tebe traže dopuštenje, u Allaha i Poslanika Njegova, doista, vjeruju. I kad oni zatraže dopuštenje od tebe zbog kakva posla svog, dopusti kome hoćeš od njih, i zamoli Allaha da im oprosti jer Allah prašta i On je milostiv.');
INSERT INTO "bs_darassalam" VALUES(24,63,'24:63','Ne smatrajte Poslanikov poziv upućen vama kao poziv koji vi jedni drugima upućujete; Allah sigurno zna one među vama koji se kradom izvlače. Neka se pripaze oni koji postupaju suprotno naređenju njegovu da ih iskušenje kakvo ne stigne ili da ih patnja bolna ne snađe.');
INSERT INTO "bs_darassalam" VALUES(24,64,'24:64','Allahovo je sve što je na nebesima i na Zemlji, On sigurno zna kakvi ste vi, i On će ih obavijestiti o svemu što su radili na Dan kad se budu Njemu vratili. – A Allah sve dobro zna!');
INSERT INTO "bs_darassalam" VALUES(25,1,'25:1','Neka je uzvišen Onaj Koji robu Svom objavljuje Kur’an da bi svjetovima bio opomena...');
INSERT INTO "bs_darassalam" VALUES(25,2,'25:2','Onaj Kome pripada vlast na nebesima i na Zemlji, Koji nema djeteta, Koji u vlasti nema ortaka i Koji je sve stvorio i kako treba uredio!');
INSERT INTO "bs_darassalam" VALUES(25,3,'25:3','Neki pored Njega prihvataju božanstva koja ništa ne stvaraju, a koja su sama stvorena, koja nisu u stanju od sebe neku štetu otkloniti ni sebi kakvu korist pribaviti i koja nemaju moći život oduzeti, život dati niti oživiti.');
INSERT INTO "bs_darassalam" VALUES(25,4,'25:4','Oni koji ne vjeruju govore: “Ovo nije ništa drugo do velika laž koju on izmišlja, a u tome mu i drugi ljudi pomažu”, i čine nepravdu i potvoru...');
INSERT INTO "bs_darassalam" VALUES(25,5,'25:5','i govore: “To su izmišljotine naroda drevnih, on traži da mu se prepisuju i ujutro i navečer da mu ih čitaju.”');
INSERT INTO "bs_darassalam" VALUES(25,6,'25:6','Reci: “Objavljuje ih Onaj Kome su poznate tajne nebesa i Zemlje, On mnogo prašta i samilostan je.”');
INSERT INTO "bs_darassalam" VALUES(25,7,'25:7','I oni govore: “Šta je ovom ‘poslaniku’, on hranu uzima i po trgovima hoda; trebao mu se jedan melek poslati da zajedno sa njim opominje...');
INSERT INTO "bs_darassalam" VALUES(25,8,'25:8','ili da mu se spusti kakvo blago ili da ima vrt iz kojeg bi se hranio?” I nevjernici još govore: “Vi samo začarana čovjeka slijedite!”');
INSERT INTO "bs_darassalam" VALUES(25,9,'25:9','Vidi kakve primjere o tebi oni navode, pa onda lutaju i Pravi put ne mogu naći.');
INSERT INTO "bs_darassalam" VALUES(25,10,'25:10','Neka je uzvišen Onaj Koji ti, ako hoće, može dati bolje od toga: vrtove kroz koje rijeke teku i dvorove.');
INSERT INTO "bs_darassalam" VALUES(25,11,'25:11','Oni, čak, i Čas oživljenja poriču, a Mi smo za one koji Čas oživljenja poriču pripremili vatru razbuktalu.');
INSERT INTO "bs_darassalam" VALUES(25,12,'25:12','Kad ih Oganj opazi iz daljine, oni će čuti kako gnjevan ključa i od bijesa huči...');
INSERT INTO "bs_darassalam" VALUES(25,13,'25:13','a kad budu bačeni u nju, u tjesnac, vezanih ruku, propast će tamo prizivati.');
INSERT INTO "bs_darassalam" VALUES(25,14,'25:14','“Ne prizivajte danas jednu propast, nego prizivajte mnoge propasti!”');
INSERT INTO "bs_darassalam" VALUES(25,15,'25:15','Reci: “Je li bolje to ili vječni džennet koji je obećan onima koji se budu Allaha bojali? On će im nagrada i prebivalište biti...');
INSERT INTO "bs_darassalam" VALUES(25,16,'25:16','u njemu će dovijeka sve što zažele imati – to je obećanje Gospodara tvog – oni će imati pravo to iskati od Njega.”');
INSERT INTO "bs_darassalam" VALUES(25,17,'25:17','A na Dan kad ih On sakupi, a i one kojima su pored Allaha robovali, te upita: “Jeste li vi ove robove Moje u zabludu zaveli, ili su oni sami s Pravog puta zalutali?”...');
INSERT INTO "bs_darassalam" VALUES(25,18,'25:18','oni će reći: “Hvaljen neka si Ti, nezamislivo je da smo mi pored Tebe ikakve zaštitnike uzimali, nego Ti si ovima i precima njihovim dao da uživaju, pa su zaboravili da Te se sjećaju; oni su propast zaslužili!”');
INSERT INTO "bs_darassalam" VALUES(25,19,'25:19','Oni će poreći to što vi govorite, a vi nećete biti u mogućnosti da mučenje otklonite niti da pomoć nađete. A onom od vas koji je Njemu druge ravnim smatrao dat ćemo da patnju veliku iskusi.');
INSERT INTO "bs_darassalam" VALUES(25,20,'25:20','Mi prije tebe nismo poslali nijednog poslanika koji nije jeo i po trgovima hodao. Mi činimo da jedni druge u iskušenje dovodite, pa izdržite! A Gospodar tvoj vidi sve.');
INSERT INTO "bs_darassalam" VALUES(25,21,'25:21','Oni koji ne vjeruju da će pred Nas stati govore: “Zašto nam se ne pošalju meleki ili zašto Gospodara svog ne vidimo?” Oni su, zaista, u dušama svojim oholi, a u nepravičnosti su sve granice prešli.');
INSERT INTO "bs_darassalam" VALUES(25,22,'25:22','Onog Dana kad ugledaju meleke, grešnici se neće radovati, jer meleki će govoriti: “U potpunosti prikraćeni ste!”');
INSERT INTO "bs_darassalam" VALUES(25,23,'25:23','I Mi ćemo pristupiti djelima njihovim koja su učinili i u prah i pepeo ih pretvoriti.');
INSERT INTO "bs_darassalam" VALUES(25,24,'25:24','Stanovnici dženneta će tog Dana bolje prebivalište i ljepše odmaralište imati.');
INSERT INTO "bs_darassalam" VALUES(25,25,'25:25','A na Dan kad se nebo rastvori i samo tanak oblak pojavi i kad se meleki sigurno spuste...');
INSERT INTO "bs_darassalam" VALUES(25,26,'25:26','tog Dana će istinska vlast biti samo u Milostivog, a bit će to mučan Dan za nevjernike.');
INSERT INTO "bs_darassalam" VALUES(25,27,'25:27','Na Dan kad nevjernik svoje dvije ruke bude grizao govoreći: “Kamo sreće da sam se uz Poslanika Pravog puta držao...');
INSERT INTO "bs_darassalam" VALUES(25,28,'25:28','kamo sreće, teško meni, da toga i toga za prijatelja nisam uzeo...');
INSERT INTO "bs_darassalam" VALUES(25,29,'25:29','on me je od Kur’ana odvratio nakon što mi je priopćen bio!”, a šejtan čovjeka uvijek ostavlja na cjedilu.');
INSERT INTO "bs_darassalam" VALUES(25,30,'25:30','Poslanik je rekao: “Gospodaru moj, narod moj ovaj Kur’an izbjegava!”');
INSERT INTO "bs_darassalam" VALUES(25,31,'25:31','Isto tako smo Mi dali da svakom vjerovjesniku nevaljalci neprijatelji budu. A tebi je Gospodar tvoj dovoljan kao vodič i kao pomagač!');
INSERT INTO "bs_darassalam" VALUES(25,32,'25:32','Oni koji ne vjeruju govore: “Trebalo je da mu Kur’an bude objavljen čitav, i to odjednom!” A tako se objavljuje da bismo njime srce tvoje učvrstili, i Mi ga sve ajet po ajet objavljujemo.');
INSERT INTO "bs_darassalam" VALUES(25,33,'25:33','Oni ti neće nijedan prigovor postaviti, a da ti Mi nećemo odgovor i najljepše objašnjenje navesti.');
INSERT INTO "bs_darassalam" VALUES(25,34,'25:34','Oni koji budu lica okrenutih zemlji u džehennem vučeni bit će u najgorem položaju i u najvećoj nevolji.');
INSERT INTO "bs_darassalam" VALUES(25,35,'25:35','Mi smo Musau Knjigu dali i brata mu Haruna pomoćnikom učinili...');
INSERT INTO "bs_darassalam" VALUES(25,36,'25:36','pa rekli: “Otiđite vas dvojica narodu koji dokaze Naše poriče”, i poslije smo taj narod potpuno uništili.');
INSERT INTO "bs_darassalam" VALUES(25,37,'25:37','A i narod Nuhov smo, kad su poslanike u laž utjerivali, potopili i ljudima ih poučnim primjerom učinili, a nevjernicima smo patnju bolnu pripremili...');
INSERT INTO "bs_darassalam" VALUES(25,38,'25:38','i Ad i Semud i stanovnike er-Ressa i mnoge narode između njih...');
INSERT INTO "bs_darassalam" VALUES(25,39,'25:39','a svima smo primjere za pouku navodili, i sve smo poslije sasvim uništili.');
INSERT INTO "bs_darassalam" VALUES(25,40,'25:40','A ovi prolaze pored grada na koji se sručila kobna kiša – zar ga ne vide? – pa ipak ne očekuju oživljenje.');
INSERT INTO "bs_darassalam" VALUES(25,41,'25:41','Kad te vide, rugaju ti se: “Je li ovo onaj kojeg je Allah kao poslanika poslao?');
INSERT INTO "bs_darassalam" VALUES(25,42,'25:42','Umalo da nas od božanstava naših nije odvratio, ali mi im vjerni ostadosmo.” A kad dožive patnju, saznat će ko je dalje s Pravog puta bio skrenuo.');
INSERT INTO "bs_darassalam" VALUES(25,43,'25:43','Kaži ti Meni, hoćeš li ti biti čuvar onom koji je strast svoju za boga svog uzeo?');
INSERT INTO "bs_darassalam" VALUES(25,44,'25:44','Misliš li ti da većina njih hoće da čuje ili da nastoji shvatiti? Kao stoka su oni, čak su još dalje s Pravog puta skrenuli.');
INSERT INTO "bs_darassalam" VALUES(25,45,'25:45','Zar ne vidiš kako Gospodar tvoj sjenu rasprostire – a da hoće, ostavio bi je da miruje – i kako smo uredili da na nju Sunce utječe...');
INSERT INTO "bs_darassalam" VALUES(25,46,'25:46','a poslije je malo-pomalo Sebi privlačimo.');
INSERT INTO "bs_darassalam" VALUES(25,47,'25:47','On vam je noć učinio pokrivkom, san vam je učinio počinkom, a dan da se krećete.');
INSERT INTO "bs_darassalam" VALUES(25,48,'25:48','I On šalje vjetrove kao radosnu vijest, kao prethodnicu milosti Svoje; i Mi s neba čistu vodu spuštamo...');
INSERT INTO "bs_darassalam" VALUES(25,49,'25:49','da njome već mrtav predio oživimo i da mnogu stoku i mnoge ljude koje smo stvorili napojimo.');
INSERT INTO "bs_darassalam" VALUES(25,50,'25:50','I njima objasnili smo da bi razmislili, ali većina ljudi osim nevjerništva odbija sve.');
INSERT INTO "bs_darassalam" VALUES(25,51,'25:51','Da hoćemo, u svaki grad bismo poslali nekog da opominje...');
INSERT INTO "bs_darassalam" VALUES(25,52,'25:52','zato ne čini nevjernicima ustupke i Kur’anom se svim silama protiv njih bori.');
INSERT INTO "bs_darassalam" VALUES(25,53,'25:53','On je dvije vodene površine jednu pored druge ostavio – jedna je pitka i slatka, druga slana i gorka, a između njih je pregradu i nevidljivu branu postavio.');
INSERT INTO "bs_darassalam" VALUES(25,54,'25:54','On od vode stvara ljude i čini da su rod po krvi i po tazbini. – Gospodar tvoj je kadar sve.');
INSERT INTO "bs_darassalam" VALUES(25,55,'25:55','Umjesto Allahu oni se klanjaju onima koji im nisu u stanju neku korist pribaviti, ni od njih kakvu štetu otkloniti. Nevjernik je šejtanov saučesnik protiv njegova Gospodara...');
INSERT INTO "bs_darassalam" VALUES(25,56,'25:56','a Mi smo tebe poslali samo zato da radosne vijesti donosiš i da opominješ.');
INSERT INTO "bs_darassalam" VALUES(25,57,'25:57','Reci: “Za ovo od vas ne tražim druge nagrade, već da onaj koji hoće pođe Putem koji vodi njegovu Gospodaru.”');
INSERT INTO "bs_darassalam" VALUES(25,58,'25:58','Ti se pouzdaj u Živog, Koji ne može umrijeti, i veličaj Ga, i hvali! A dovoljno je to što grijehe robova Svojih zna...');
INSERT INTO "bs_darassalam" VALUES(25,59,'25:59','Onaj Koji je za šest vremenskih razdoblja nebesa i Zemlju i ono što je među njima stvorio, a onda se iznad Arša uzvisio. On je Milostivi i upitaj o Njemu Onog Koji zna.');
INSERT INTO "bs_darassalam" VALUES(25,60,'25:60','A kad im se rekne: “Padajte ničice pred Milostivim!”, oni pitaju: “A ko je Milostivi? Zar da padamo ničice samo zato što nam ti naređuješ?” I još se više otuđuju.');
INSERT INTO "bs_darassalam" VALUES(25,61,'25:61','Neka je uzvišen Onaj Koji je na nebu sazviježđa stvorio i na njemu dao svjetiljku i Mjesec koji sija.');
INSERT INTO "bs_darassalam" VALUES(25,62,'25:62','On čini da se noć i dan smjenjuju, to je pouka za onog koji hoće razmisliti ili ko želi biti blagodaran.');
INSERT INTO "bs_darassalam" VALUES(25,63,'25:63','A robovi su Milostivog oni koji po Zemlji mirno hodaju, a kad ih bestidnici oslove, odgovaraju: “Mir vama!”');
INSERT INTO "bs_darassalam" VALUES(25,64,'25:64','i oni koji provode noći pred Gospodarom svojim na tlo padajući i stojeći...');
INSERT INTO "bs_darassalam" VALUES(25,65,'25:65','i oni koji govore: “Gospodaru naš, poštedi nas patnje u džehennemu, jer je patnja u njemu, doista, propast neminovna...');
INSERT INTO "bs_darassalam" VALUES(25,66,'25:66','on je ružno prebivalište i boravište”...');
INSERT INTO "bs_darassalam" VALUES(25,67,'25:67','i oni koji, kad udjeljuju, ne rasipaju i ne škrtare, već se u tome drže sredine...');
INSERT INTO "bs_darassalam" VALUES(25,68,'25:68','i oni koji se mimo Allaha drugom bogu ne klanjaju i koji, one koje je Allah zabranio, ne ubijaju, osim kad pravda zahtijeva i koji ne bludniče; a ko to radi, iskusit će kaznu...');
INSERT INTO "bs_darassalam" VALUES(25,69,'25:69','patnja će mu na onom svijetu udvostručena biti i vječno će u njoj ponižen ostati.');
INSERT INTO "bs_darassalam" VALUES(25,70,'25:70','Ali onima koji se pokaju i uzvjeruju i dobra djela čine, Allah će njihova hrđava djela u dobra promijeniti, a Allah prašta i samilostan je.');
INSERT INTO "bs_darassalam" VALUES(25,71,'25:71','A onaj ko se bude pokajao i dobra djela činio, on se, uistinu, Allahu iskreno vratio.');
INSERT INTO "bs_darassalam" VALUES(25,72,'25:72','I oni koji ne svjedoče lažno i koji, prolazeći pored onog što ih se ne tiče, prolaze dostojanstveno...');
INSERT INTO "bs_darassalam" VALUES(25,73,'25:73','i oni koji, kad budu opomenuti dokazima Gospodara svog, ni gluhi ni slijepi ne ostanu...');
INSERT INTO "bs_darassalam" VALUES(25,74,'25:74','i oni koji govore: “Gospodaru naš, podari nam u ženama našim i djeci našoj radost i učini da se čestiti na nas ugledaju!”...');
INSERT INTO "bs_darassalam" VALUES(25,75,'25:75','oni će biti, za ono što su trpjeli, odajama džennetskim nagrađeni i u njima će pozdravom i blagoslovom biti susretani...');
INSERT INTO "bs_darassalam" VALUES(25,76,'25:76','u njima će vječno ostati, a kako su one divno prebivalište i boravište!');
INSERT INTO "bs_darassalam" VALUES(25,77,'25:77','Reci: “Gospodar moj vam poklanja pažnju samo zbog vaše molitve, a pošto ste vi poricali, neminovno vas čeka patnja."');
INSERT INTO "bs_darassalam" VALUES(26,1,'26:1','Tā Sīn Mīm.');
INSERT INTO "bs_darassalam" VALUES(26,2,'26:2','Ovo su ajeti Knjige jasne!');
INSERT INTO "bs_darassalam" VALUES(26,3,'26:3','Zar ćeš ti sebe uništiti zato što ovi neće da postanu vjernici?');
INSERT INTO "bs_darassalam" VALUES(26,4,'26:4','Kad bismo htjeli, Mi bismo im s neba jedan znak poslali pred kojim bi oni šije svoje sagnuli.');
INSERT INTO "bs_darassalam" VALUES(26,5,'26:5','I njima ne dođe nijedna nova opomena od Milostivoga, a da se od nje ne okrenu.');
INSERT INTO "bs_darassalam" VALUES(26,6,'26:6','Oni poriču – pa stići će ih sigurno posljedice onog čemu se izruguju.');
INSERT INTO "bs_darassalam" VALUES(26,7,'26:7','Zar oni ne vide kako činimo da iz zemlje niče svakovrsno bilje plemenito?');
INSERT INTO "bs_darassalam" VALUES(26,8,'26:8','To je doista dokaz, ali većina njih ne vjeruje.');
INSERT INTO "bs_darassalam" VALUES(26,9,'26:9','A Gospodar je tvoj zaista silan i milostiv.');
INSERT INTO "bs_darassalam" VALUES(26,10,'26:10','A kad je Gospodar tvoj Musaa zovnuo: “Idi narodu koji se prema sebi ogriješio...');
INSERT INTO "bs_darassalam" VALUES(26,11,'26:11','narodu faraonovu, ne bi li se Allaha pobojao”...');
INSERT INTO "bs_darassalam" VALUES(26,12,'26:12','on je rekao: “Gospodaru moj, bojim se da me oni u laž ne utjeraju...');
INSERT INTO "bs_darassalam" VALUES(26,13,'26:13','da mi ne postane teško u duši i da mi se jezik ne saplete – zato podaj poslanstvo i Harunu.');
INSERT INTO "bs_darassalam" VALUES(26,14,'26:14','A ja sam njima i odgovoran, pa se plašim da me ne ubiju.”');
INSERT INTO "bs_darassalam" VALUES(26,15,'26:15','“Neće!”, reče On. “Idite obojica s dokazima Našim, Mi ćemo s vama biti i slušati.');
INSERT INTO "bs_darassalam" VALUES(26,16,'26:16','Otiđite faraonu i recite: ‘Mi smo poslanici Gospodara svjetova...');
INSERT INTO "bs_darassalam" VALUES(26,17,'26:17','dopusti da sinovi Israilovi pođu s nama!’”');
INSERT INTO "bs_darassalam" VALUES(26,18,'26:18','“Zar te među nama nismo odgojili dok si dijete bio i zar među nama tolike godine života svog nisi proveo?”, reče faraon...');
INSERT INTO "bs_darassalam" VALUES(26,19,'26:19','“I uradio si nedjelo koje si uradio i još si nezahvalnik!”');
INSERT INTO "bs_darassalam" VALUES(26,20,'26:20','“Tad sam to učinio”, Musa reče, “jer sam spadao u zabludjele...');
INSERT INTO "bs_darassalam" VALUES(26,21,'26:21','a od vas sam pobjegao zato što sam vas se bojao, pa mi je Gospodar moj mudrost darovao i poslanikom me učinio.');
INSERT INTO "bs_darassalam" VALUES(26,22,'26:22','A dobročinstvo koje mi predbacuješ – da nije to što si sinove Israilove robljem učinio?”');
INSERT INTO "bs_darassalam" VALUES(26,23,'26:23','“A ko je Gospodar svjetova?”, upita faraon.');
INSERT INTO "bs_darassalam" VALUES(26,24,'26:24','“Gospodar nebesa i Zemlje i onog što je između njih, ako vjerujete”, odgovori on.');
INSERT INTO "bs_darassalam" VALUES(26,25,'26:25','“Čujete li?”, reče onima oko sebe faraon.');
INSERT INTO "bs_darassalam" VALUES(26,26,'26:26','“Gospodar vaš i Gospodar vaših davnih predaka”, reče Musa.');
INSERT INTO "bs_darassalam" VALUES(26,27,'26:27','“Poslanik koji vam je poslat, uistinu, lud je”, reče faraon.');
INSERT INTO "bs_darassalam" VALUES(26,28,'26:28','“Gospodar istoka i zapada i onog što je između njih, ako pameti imate”, reče Musa.');
INSERT INTO "bs_darassalam" VALUES(26,29,'26:29','A faraon reče: “Ako budeš kao boga nekog drugog osim mene priznavao, sigurno ću te u tamnicu baciti!”');
INSERT INTO "bs_darassalam" VALUES(26,30,'26:30','“Zar i onda kad ti budem očigledan dokaz donio?”, upita on.');
INSERT INTO "bs_darassalam" VALUES(26,31,'26:31','“Pa daj ga, ako istinu govoriš!”, reče faraon.');
INSERT INTO "bs_darassalam" VALUES(26,32,'26:32','I Musa baci štap svoj, kad on – zmija prava.');
INSERT INTO "bs_darassalam" VALUES(26,33,'26:33','A onda izvadi ruku iz njedara, kad ona, onima koji su gledali – bijela.');
INSERT INTO "bs_darassalam" VALUES(26,34,'26:34','“Ovaj je zaista vješt čarobnjak”, reče glavešinama oko sebe faraon...');
INSERT INTO "bs_darassalam" VALUES(26,35,'26:35','“hoće da vas čarolijom svojom iz zemlje vaše izvede. Pa šta savjetujete?”');
INSERT INTO "bs_darassalam" VALUES(26,36,'26:36','“Ostavi njega i brata njegova!”, rekoše, “a u gradove pošalji da sakupljaju...');
INSERT INTO "bs_darassalam" VALUES(26,37,'26:37','sve čarobnjake vješte će ti dovesti.”');
INSERT INTO "bs_darassalam" VALUES(26,38,'26:38','I čarobnjaci se u određeno vrijeme i označenog dana sabraše...');
INSERT INTO "bs_darassalam" VALUES(26,39,'26:39','a narodu bī rečeno: “Hoćete li se iskupiti...');
INSERT INTO "bs_darassalam" VALUES(26,40,'26:40','da slijedimo čarobnjake, ako oni budu pobjednici!”');
INSERT INTO "bs_darassalam" VALUES(26,41,'26:41','A kad dođoše, čarobnjaci faraona upitaše: “Hoće li nama, doista, pripasti nagrada ako mi budemo pobjednici?”');
INSERT INTO "bs_darassalam" VALUES(26,42,'26:42','“Hoće!”, odgovori on, “bit ćete tad sigurno meni najbliži.”');
INSERT INTO "bs_darassalam" VALUES(26,43,'26:43','Musa im reče: “Bacite ono što želite baciti!”');
INSERT INTO "bs_darassalam" VALUES(26,44,'26:44','I oni pobacaše konope svoje i štapove svoje i rekoše: “Tako nam dostojanstva faraonova, mi ćemo svakako pobijediti!”');
INSERT INTO "bs_darassalam" VALUES(26,45,'26:45','Zatim Musa baci svoj štap, koji, odjednom, proguta ono što su oni lažno izveli.');
INSERT INTO "bs_darassalam" VALUES(26,46,'26:46','Čarobnjaci se onda na tlo baciše...');
INSERT INTO "bs_darassalam" VALUES(26,47,'26:47','i rekoše: “Mi vjerujemo u Gospodara svjetova...');
INSERT INTO "bs_darassalam" VALUES(26,48,'26:48','Gospodara Musaova i Harunova!”');
INSERT INTO "bs_darassalam" VALUES(26,49,'26:49','“Da povjerujete njemu prije dopuštenja moga!?”, viknu faraon, “on je vaš učitelj, on vas je čarobnjaštvu naučio – a vi ćete zapamtiti! Poodsijecat ću vam ruke i noge vaše unakrst i sve ću vas porazapinjati!”');
INSERT INTO "bs_darassalam" VALUES(26,50,'26:50','“Ništa strašno!”, rekoše oni. “Mi ćemo se Gospodaru svom vratiti.');
INSERT INTO "bs_darassalam" VALUES(26,51,'26:51','Mi se nadamo da će nam Gospodar naš grijehe naše oprostiti zato što smo prvi vjernici.”');
INSERT INTO "bs_darassalam" VALUES(26,52,'26:52','I Mi objavismo Musau: “Kreni noću s robovima Mojim, ali bit ćete gonjeni.”');
INSERT INTO "bs_darassalam" VALUES(26,53,'26:53','I faraon posla po gradovima sakupljače...');
INSERT INTO "bs_darassalam" VALUES(26,54,'26:54','“Ovih je zaista malo...');
INSERT INTO "bs_darassalam" VALUES(26,55,'26:55','i rasrdili su nas...');
INSERT INTO "bs_darassalam" VALUES(26,56,'26:56','a mi smo svi budni!”');
INSERT INTO "bs_darassalam" VALUES(26,57,'26:57','I Mi ih izvedosmo iz vrtova i rijeka...');
INSERT INTO "bs_darassalam" VALUES(26,58,'26:58','iz riznica i dvoraca divnih.');
INSERT INTO "bs_darassalam" VALUES(26,59,'26:59','Eto tako je bilo, i Mi dadosmo da to naslijede sinovi Israilovi.');
INSERT INTO "bs_darassalam" VALUES(26,60,'26:60','I oni ih, kad se sunce rađalo, sustigoše.');
INSERT INTO "bs_darassalam" VALUES(26,61,'26:61','Pa kad jedni druge ugledaše, drugovi Musaovi povikaše: “Samo što nas nisu stigli!”');
INSERT INTO "bs_darassalam" VALUES(26,62,'26:62','“Neće!”, reče on, “Gospodar moj je sa mnom, On će mi put pokazati.”');
INSERT INTO "bs_darassalam" VALUES(26,63,'26:63','I Mi objavismo Musau: “Udari štapom svojim po moru!”, i ono se rastavi i svaki bok njegov bijaše kao veliko brdo...');
INSERT INTO "bs_darassalam" VALUES(26,64,'26:64','i Mi onda tamo one druge približismo...');
INSERT INTO "bs_darassalam" VALUES(26,65,'26:65','a Musaa i sve one koji bijahu s njime spasismo...');
INSERT INTO "bs_darassalam" VALUES(26,66,'26:66','i one druge potopismo.');
INSERT INTO "bs_darassalam" VALUES(26,67,'26:67','To je doista dokaz, ali većina njih ne vjeruje.');
INSERT INTO "bs_darassalam" VALUES(26,68,'26:68','A Gospodar je tvoj zaista silan i milostiv.');
INSERT INTO "bs_darassalam" VALUES(26,69,'26:69','I kaži im vijest o Ibrahimu...');
INSERT INTO "bs_darassalam" VALUES(26,70,'26:70','kad je oca svog i narod svoj upitao: “Čemu se vi klanjate?”...');
INSERT INTO "bs_darassalam" VALUES(26,71,'26:71','a oni odgovorili: “Klanjamo se kumirima i povazdan im se molimo.”');
INSERT INTO "bs_darassalam" VALUES(26,72,'26:72','On je rekao: “Čuju li vas oni kad se molite...');
INSERT INTO "bs_darassalam" VALUES(26,73,'26:73','ili mogu li vam koristiti ili naškoditi?”');
INSERT INTO "bs_darassalam" VALUES(26,74,'26:74','“Ne”, odgovoriše, “ali mi smo upamtili pretke naše kako tako postupaju.”');
INSERT INTO "bs_darassalam" VALUES(26,75,'26:75','“A jeste li razmišljali”, upita on, “da su oni kojima se klanjate...');
INSERT INTO "bs_darassalam" VALUES(26,76,'26:76','vi i kojima su se klanjali davni preci vaši...');
INSERT INTO "bs_darassalam" VALUES(26,77,'26:77','doista, neprijatelji moji? Ali, to nije Gospodar svjetova...');
INSERT INTO "bs_darassalam" VALUES(26,78,'26:78','Koji me je stvorio i na Pravi put uputio...');
INSERT INTO "bs_darassalam" VALUES(26,79,'26:79','i Koji me hrani i poji...');
INSERT INTO "bs_darassalam" VALUES(26,80,'26:80','i Koji me, kad se razbolim, liječi...');
INSERT INTO "bs_darassalam" VALUES(26,81,'26:81','i Koji će mi život oduzeti, i Koji će me poslije oživiti...');
INSERT INTO "bs_darassalam" VALUES(26,82,'26:82','i Koji će mi, nadam se, pogreške moje na Sudnjem danu oprostiti!');
INSERT INTO "bs_darassalam" VALUES(26,83,'26:83','Gospodaru moj, podari mi znanje i uvrsti me među one koji su dobri...');
INSERT INTO "bs_darassalam" VALUES(26,84,'26:84','i učini da me po lijepom spominju oni što će poslije mene doći...');
INSERT INTO "bs_darassalam" VALUES(26,85,'26:85','i učini me jednim od onih kojima ćeš džennetske blagodati darovati...');
INSERT INTO "bs_darassalam" VALUES(26,86,'26:86','i ocu mom oprosti, on je jedan od zalutalih...');
INSERT INTO "bs_darassalam" VALUES(26,87,'26:87','i ne osramoti me na Dan kad će ljudi oživljeni biti...');
INSERT INTO "bs_darassalam" VALUES(26,88,'26:88','na Dan kad neće nikakvo blago, a ni sinovi od koristi biti...');
INSERT INTO "bs_darassalam" VALUES(26,89,'26:89','samo će onaj koji Allahu srca čista dođe spašen biti.”');
INSERT INTO "bs_darassalam" VALUES(26,90,'26:90','I džennet će se čestitima približiti...');
INSERT INTO "bs_darassalam" VALUES(26,91,'26:91','a džehennem zalutalima ukazati...');
INSERT INTO "bs_darassalam" VALUES(26,92,'26:92','i reći će im se: “Gdje su oni kojima ste se klanjali...');
INSERT INTO "bs_darassalam" VALUES(26,93,'26:93','a niste Allahu, mogu li vam oni pomoći, a mogu li i sebi pomoći?”...');
INSERT INTO "bs_darassalam" VALUES(26,94,'26:94','pa će i oni i oni koji su ih u zabludu odveli u njega biti bačeni...');
INSERT INTO "bs_darassalam" VALUES(26,95,'26:95','i vojske Iblisove – svi zajedno.');
INSERT INTO "bs_darassalam" VALUES(26,96,'26:96','I oni će, svađajući se u njemu, govoriti...');
INSERT INTO "bs_darassalam" VALUES(26,97,'26:97','“Allaha nam, bili smo, doista, u očitoj zabludi...');
INSERT INTO "bs_darassalam" VALUES(26,98,'26:98','kad smo vas s Gospodarom svjetova izjednačavali...');
INSERT INTO "bs_darassalam" VALUES(26,99,'26:99','a na stranputicu su nas naveli zlikovci...');
INSERT INTO "bs_darassalam" VALUES(26,100,'26:100','pa nemamo ni zagovornika...');
INSERT INTO "bs_darassalam" VALUES(26,101,'26:101','ni prisna prijatelja...');
INSERT INTO "bs_darassalam" VALUES(26,102,'26:102','da nam je samo da se povratimo, pa da postanemo vjernici!”');
INSERT INTO "bs_darassalam" VALUES(26,103,'26:103','To je doista dokaz, ali većina njih ne vjeruje.');
INSERT INTO "bs_darassalam" VALUES(26,104,'26:104','A Gospodar je tvoj zaista silan i milostiv.');
INSERT INTO "bs_darassalam" VALUES(26,105,'26:105','I Nuhov je narod smatrao lažnim poslanike.');
INSERT INTO "bs_darassalam" VALUES(26,106,'26:106','Kad im brat njihov Nuh reče: “Kako to da se Allaha ne bojite?');
INSERT INTO "bs_darassalam" VALUES(26,107,'26:107','Ja sam vam, sigurno, poslanik pouzdani...');
INSERT INTO "bs_darassalam" VALUES(26,108,'26:108','zato se bojte Allaha i budite poslušni meni!');
INSERT INTO "bs_darassalam" VALUES(26,109,'26:109','Za ovo od vas ne tražim nikakve nagrade, mene će Gospodar svjetova nagraditi...');
INSERT INTO "bs_darassalam" VALUES(26,110,'26:110','zato se bojte Allaha i budite poslušni meni!');
INSERT INTO "bs_darassalam" VALUES(26,111,'26:111','oni rekoše: “Kako da te poslušamo, kad te slijede oni koji su najmizerniji?”');
INSERT INTO "bs_darassalam" VALUES(26,112,'26:112','“Ne znam ja šta su oni radili”, reče on...');
INSERT INTO "bs_darassalam" VALUES(26,113,'26:113','“svi će pred Gospodarom mojim račun polagati, kad biste osjetili...');
INSERT INTO "bs_darassalam" VALUES(26,114,'26:114','a ja vjernike neću otjerati...');
INSERT INTO "bs_darassalam" VALUES(26,115,'26:115','ja samo javno opominjem!”');
INSERT INTO "bs_darassalam" VALUES(26,116,'26:116','“Ako se ne okaniš, o Nuhu”, rekoše oni, “bit ćeš sigurno kamenovan!”');
INSERT INTO "bs_darassalam" VALUES(26,117,'26:117','“Gospodaru moj”, reče on, “narod me moj u laž utjeruje...');
INSERT INTO "bs_darassalam" VALUES(26,118,'26:118','pa Ti meni i njima presudi i mene i vjernike koji su sa mnom spasi!”');
INSERT INTO "bs_darassalam" VALUES(26,119,'26:119','I Mi smo njega i one koji su bili uz njega u lađi krcatoj spasili...');
INSERT INTO "bs_darassalam" VALUES(26,120,'26:120','a ostale potopili.');
INSERT INTO "bs_darassalam" VALUES(26,121,'26:121','To je doista dokaz, ali većina njih ne vjeruje.');
INSERT INTO "bs_darassalam" VALUES(26,122,'26:122','A Gospodar je tvoj zaista silan i milostiv.');
INSERT INTO "bs_darassalam" VALUES(26,123,'26:123','I Ad je smatrao lažnim poslanike.');
INSERT INTO "bs_darassalam" VALUES(26,124,'26:124','Kad im brat njihov Hud reče: “Kako to da se Allaha ne bojite?');
INSERT INTO "bs_darassalam" VALUES(26,125,'26:125','Ja sam vam, sigurno, poslanik pouzdani...');
INSERT INTO "bs_darassalam" VALUES(26,126,'26:126','zato se bojte Allaha i budite poslušni meni!');
INSERT INTO "bs_darassalam" VALUES(26,127,'26:127','Za ovo od vas ne tražim nikakve nagrade, mene će Gospodar svjetova nagraditi...');
INSERT INTO "bs_darassalam" VALUES(26,128,'26:128','Zašto na svakoj uzvišici palate zidate, druge ismijavajući...');
INSERT INTO "bs_darassalam" VALUES(26,129,'26:129','i podižete utvrde kao da ćete vječno živjeti...');
INSERT INTO "bs_darassalam" VALUES(26,130,'26:130','a kad kažnjavate – kažnjavate kao silnici?');
INSERT INTO "bs_darassalam" VALUES(26,131,'26:131','zato se bojte Allaha i budite poslušni meni!');
INSERT INTO "bs_darassalam" VALUES(26,132,'26:132','Bojte se Onog Koji vam daruje ono što znate...');
INSERT INTO "bs_darassalam" VALUES(26,133,'26:133','daruje vam stoku i sinove...');
INSERT INTO "bs_darassalam" VALUES(26,134,'26:134','i bašče i izvore.');
INSERT INTO "bs_darassalam" VALUES(26,135,'26:135','Ja se, doista, bojim, za vas na Velikom danu patnje”...');
INSERT INTO "bs_darassalam" VALUES(26,136,'26:136','rekoše oni: “Nama je svejedno savjetovao ti ili ne bio savjetnik...');
INSERT INTO "bs_darassalam" VALUES(26,137,'26:137','ovako su i narodi davnašnji vjerovali...');
INSERT INTO "bs_darassalam" VALUES(26,138,'26:138','i mi nećemo biti mučeni.”');
INSERT INTO "bs_darassalam" VALUES(26,139,'26:139','I oni su ga nastavili u laž utjerivati, pa smo ih Mi uništili. To je pouka, ali većina ovih neće da vjeruje...');
INSERT INTO "bs_darassalam" VALUES(26,140,'26:140','A Gospodar je tvoj zaista silan i milostiv.');
INSERT INTO "bs_darassalam" VALUES(26,141,'26:141','I Semud je smatrao lažnim poslanike.');
INSERT INTO "bs_darassalam" VALUES(26,142,'26:142','Kad im brat njihov Salih reče: “Kako to da se Allaha ne bojite?');
INSERT INTO "bs_darassalam" VALUES(26,143,'26:143','Ja sam vam, sigurno, poslanik pouzdani...');
INSERT INTO "bs_darassalam" VALUES(26,144,'26:144','zato se bojte Allaha i budite poslušni meni!');
INSERT INTO "bs_darassalam" VALUES(26,145,'26:145','Za ovo od vas ne tražim nikakve nagrade, mene će Gospodar svjetova nagraditi...');
INSERT INTO "bs_darassalam" VALUES(26,146,'26:146','Zar mislite da ćete ovdje biti ostavljeni sigurni...');
INSERT INTO "bs_darassalam" VALUES(26,147,'26:147','u vrtovima i među izvorima...');
INSERT INTO "bs_darassalam" VALUES(26,148,'26:148','u usjevima i među palmama sa plodovima zrelim?');
INSERT INTO "bs_darassalam" VALUES(26,149,'26:149','Vi u brdima vrlo spretno kuće klešete...');
INSERT INTO "bs_darassalam" VALUES(26,150,'26:150','zato se bojte Allaha i budite poslušni meni!');
INSERT INTO "bs_darassalam" VALUES(26,151,'26:151','i ne slušajte naredbe onih koji u zlu pretjeruju...');
INSERT INTO "bs_darassalam" VALUES(26,152,'26:152','koji na Zemlji ne zavode red već nered uspostavljaju”...');
INSERT INTO "bs_darassalam" VALUES(26,153,'26:153','rekoše oni: “Ti si samo opčinjen...');
INSERT INTO "bs_darassalam" VALUES(26,154,'26:154','ti si čovjek, kao i mi – zato nam donesi jedno čudo ako je istina to što govoriš!”');
INSERT INTO "bs_darassalam" VALUES(26,155,'26:155','“Evo, to je deva”, reče on, “u određeni dan ona će piti, a u poznati dan vi.');
INSERT INTO "bs_darassalam" VALUES(26,156,'26:156','I ne učinite joj nikakvo zlo da vas ne bi stigla patnja na velikom danu!”');
INSERT INTO "bs_darassalam" VALUES(26,157,'26:157','Ali, oni je zaklaše i potom se pokajaše.');
INSERT INTO "bs_darassalam" VALUES(26,158,'26:158','I stiže ih kazna. To je pouka, ali većina ovih neće da vjeruje...');
INSERT INTO "bs_darassalam" VALUES(26,159,'26:159','A Gospodar je tvoj zaista silan i milostiv.');
INSERT INTO "bs_darassalam" VALUES(26,160,'26:160','I Lutov narod je smatrao lažnim poslanike.');
INSERT INTO "bs_darassalam" VALUES(26,161,'26:161','Kad im njihov brat Lut reče: “Kako to da se ne bojite?');
INSERT INTO "bs_darassalam" VALUES(26,162,'26:162','Ja sam vam, sigurno, poslanik pouzdani...');
INSERT INTO "bs_darassalam" VALUES(26,163,'26:163','zato se bojte Allaha i budite poslušni meni!');
INSERT INTO "bs_darassalam" VALUES(26,164,'26:164','Za ovo od vas ne tražim nikakve nagrade, mene će Gospodar svjetova nagraditi...');
INSERT INTO "bs_darassalam" VALUES(26,165,'26:165','Zašto vi, mimo svog svijeta, sa muškarcima općite...');
INSERT INTO "bs_darassalam" VALUES(26,166,'26:166','a žene svoje, koje je za vas Gospodar vaš stvorio, ostavljate? Vi ste ljudi koji svaku granicu zla prelazite”...');
INSERT INTO "bs_darassalam" VALUES(26,167,'26:167','rekoše oni: “Ako se ne okaniš, o Lute, bit ćeš sigurno prognan.”');
INSERT INTO "bs_darassalam" VALUES(26,168,'26:168','“Ja se gnušam toga što vi radite!”, reče on...');
INSERT INTO "bs_darassalam" VALUES(26,169,'26:169','“Gospodaru moj, sačuvaj mene i porodicu moju kazne za ono što oni rade!”');
INSERT INTO "bs_darassalam" VALUES(26,170,'26:170','I Mi smo sačuvali njega i porodicu njegovu – sve...');
INSERT INTO "bs_darassalam" VALUES(26,171,'26:171','osim starice koja je ostala sa onima koji su kaznu iskusili...');
INSERT INTO "bs_darassalam" VALUES(26,172,'26:172','a zatim smo ostale uništili...');
INSERT INTO "bs_darassalam" VALUES(26,173,'26:173','spustivši na njih kišu – a strašne li kiše za one koji su opomenuti bili!');
INSERT INTO "bs_darassalam" VALUES(26,174,'26:174','To je doista dokaz, ali većina njih ne vjeruje.');
INSERT INTO "bs_darassalam" VALUES(26,175,'26:175','A Gospodar je tvoj zaista silan i milostiv.');
INSERT INTO "bs_darassalam" VALUES(26,176,'26:176','I stanovnici Ejke su u laž ugonili poslanike.');
INSERT INTO "bs_darassalam" VALUES(26,177,'26:177','Kad im Šuajb reče: “Kako to da se ne bojite?');
INSERT INTO "bs_darassalam" VALUES(26,178,'26:178','Ja sam vam, sigurno, poslanik pouzdani...');
INSERT INTO "bs_darassalam" VALUES(26,179,'26:179','zato se bojte Allaha i budite poslušni meni!');
INSERT INTO "bs_darassalam" VALUES(26,180,'26:180','Za ovo od vas ne tražim nikakve nagrade, mene će Gospodar svjetova nagraditi...');
INSERT INTO "bs_darassalam" VALUES(26,181,'26:181','Pravo mjerite na litru i ne zakidajte...');
INSERT INTO "bs_darassalam" VALUES(26,182,'26:182','a na kantaru ispravnom mjerom mjerite.');
INSERT INTO "bs_darassalam" VALUES(26,183,'26:183','I ljudima prava njihova ne umanjujte i zlo po Zemlji, nered praveći, ne činite...');
INSERT INTO "bs_darassalam" VALUES(26,184,'26:184','i Onog Koji je stvorio vas i narode davnašnje bojte se”...');
INSERT INTO "bs_darassalam" VALUES(26,185,'26:185','rekoše oni: “Ti si samo opčinjen...');
INSERT INTO "bs_darassalam" VALUES(26,186,'26:186','i ti si samo čovjek kao i mi – za nas si ti, doista, lažac pravi...');
INSERT INTO "bs_darassalam" VALUES(26,187,'26:187','zato spusti na nas kaznu s neba, ako istinu govoriš!”');
INSERT INTO "bs_darassalam" VALUES(26,188,'26:188','“Gospodar moj dobro zna šta vi radite”, reče on.');
INSERT INTO "bs_darassalam" VALUES(26,189,'26:189','I oni su nastavili utjerivati ga u laž, pa ih je stigla kazna iz oblaka – a to je bila kazna jednog strašnog dana.');
INSERT INTO "bs_darassalam" VALUES(26,190,'26:190','To je doista dokaz, ali većina njih ne vjeruje.');
INSERT INTO "bs_darassalam" VALUES(26,191,'26:191','A Gospodar je tvoj zaista silan i milostiv.');
INSERT INTO "bs_darassalam" VALUES(26,192,'26:192','I Kur’an je, sigurno, objava Gospodara svjetova...');
INSERT INTO "bs_darassalam" VALUES(26,193,'26:193','donosi ga povjerljivi Džibril...');
INSERT INTO "bs_darassalam" VALUES(26,194,'26:194','na srce tvoje, da opominješ...');
INSERT INTO "bs_darassalam" VALUES(26,195,'26:195','na jasnom arapskom jeziku...');
INSERT INTO "bs_darassalam" VALUES(26,196,'26:196','on je spomenut u knjigama poslanika prijašnjih...');
INSERT INTO "bs_darassalam" VALUES(26,197,'26:197','zar ovima nije dokaz to što za njega znaju učeni ljudi sinova Israilovih?');
INSERT INTO "bs_darassalam" VALUES(26,198,'26:198','A da ga objavljujemo i nekom nearapu...');
INSERT INTO "bs_darassalam" VALUES(26,199,'26:199','pa da im ga on čita, opet u njega ne bi povjerovali.');
INSERT INTO "bs_darassalam" VALUES(26,200,'26:200','Eto, tako ga Mi u srca grešnikā uvodimo...');
INSERT INTO "bs_darassalam" VALUES(26,201,'26:201','oni u njega neće vjerovati dok ne dožive patnju nesnosnu...');
INSERT INTO "bs_darassalam" VALUES(26,202,'26:202','koja će im iznenada doći, kad je najmanje budu očekivali...');
INSERT INTO "bs_darassalam" VALUES(26,203,'26:203','pa će reći: “Hoće li nam se imalo vremena dati?”');
INSERT INTO "bs_darassalam" VALUES(26,204,'26:204','Zašto oni kaznu Našu požuruju!?');
INSERT INTO "bs_darassalam" VALUES(26,205,'26:205','Šta ti misliš, ako im Mi dopuštamo da godinama uživaju...');
INSERT INTO "bs_darassalam" VALUES(26,206,'26:206','i naposlijetku ih snađe ono čime im se prijeti...');
INSERT INTO "bs_darassalam" VALUES(26,207,'26:207','zar će imati šta od slatkog života koji su provodili?');
INSERT INTO "bs_darassalam" VALUES(26,208,'26:208','Mi nijedan grad nismo razorili prije nego su im došli oni koji su ih opominjali...');
INSERT INTO "bs_darassalam" VALUES(26,209,'26:209','da bi pouku primili, Mi nismo nepravedni bili.');
INSERT INTO "bs_darassalam" VALUES(26,210,'26:210','Kur’an ne donose šejtani...');
INSERT INTO "bs_darassalam" VALUES(26,211,'26:211','nezamislivo je da to oni čine, oni to nisu kadri...');
INSERT INTO "bs_darassalam" VALUES(26,212,'26:212','oni ga nikako ne mogu prisluškivati.');
INSERT INTO "bs_darassalam" VALUES(26,213,'26:213','Zato se, mimo Allaha, ne moli drugom bogu da ne bi bio jedan od onih koji će biti mučeni!');
INSERT INTO "bs_darassalam" VALUES(26,214,'26:214','I opominji rodbinu svoju najbližu...');
INSERT INTO "bs_darassalam" VALUES(26,215,'26:215','i spusti krilo svoje pred vjernicima što te slijede!');
INSERT INTO "bs_darassalam" VALUES(26,216,'26:216','A ako te ne budu poslušali, ti reci: “Ja nemam ništa s tim što vi radite.”');
INSERT INTO "bs_darassalam" VALUES(26,217,'26:217','I pouzdaj se u Silnoga i Milostivoga...');
INSERT INTO "bs_darassalam" VALUES(26,218,'26:218','Koji te vidi kad ustaneš...');
INSERT INTO "bs_darassalam" VALUES(26,219,'26:219','da sa ostalima molitvu obaviš...');
INSERT INTO "bs_darassalam" VALUES(26,220,'26:220','jer On, doista, sve čuje i sve zna.');
INSERT INTO "bs_darassalam" VALUES(26,221,'26:221','Hoću li vam kazati kome dolaze šejtani?');
INSERT INTO "bs_darassalam" VALUES(26,222,'26:222','Oni dolaze svakom lašcu, grešniku...');
INSERT INTO "bs_darassalam" VALUES(26,223,'26:223','oni prisluškuju i većinom oni lažu.');
INSERT INTO "bs_darassalam" VALUES(26,224,'26:224','A zavedeni slijede pjesnike.');
INSERT INTO "bs_darassalam" VALUES(26,225,'26:225','Zar ne znaš da oni svakom dolinom blude...');
INSERT INTO "bs_darassalam" VALUES(26,226,'26:226','i da govore ono što ne rade...');
INSERT INTO "bs_darassalam" VALUES(26,227,'26:227','tako ne govore samo oni koji vjeruju i dobra djela čine, i koji često Allaha spominju, i koji uzvraćaju kad ih ismijavaju. A mnogobošci će, sigurno, saznati u kakvu će se muku uvaliti.');
INSERT INTO "bs_darassalam" VALUES(27,1,'27:1','Tā Sīn. Ovo su ajeti Kur’ana i Knjige jasne...');
INSERT INTO "bs_darassalam" VALUES(27,2,'27:2','Upute i radosne vijesti onima koji vjeruju...');
INSERT INTO "bs_darassalam" VALUES(27,3,'27:3','koji molitvu obavljaju i milostinju udjeljuju i koji u onaj svijet čvrsto vjeruju.');
INSERT INTO "bs_darassalam" VALUES(27,4,'27:4','Onima koji u onaj svijet ne vjeruju Mi prikazujemo kao lijepe postupke njihove – zato oni lutaju...');
INSERT INTO "bs_darassalam" VALUES(27,5,'27:5','njih čeka zla kob, a na onom svijetu će biti posve izgubljeni...');
INSERT INTO "bs_darassalam" VALUES(27,6,'27:6','a ti, zaista, primaš Kur’an od Mudrog i Sveznajućeg!');
INSERT INTO "bs_darassalam" VALUES(27,7,'27:7','Kad Musa reče čeljadi svojoj: “Vidio sam vatru, donijet ću vam otuda vijest kakvu ili ću vam donijeti razgorjelu glavnju da biste se ogrijali”...');
INSERT INTO "bs_darassalam" VALUES(27,8,'27:8','neko ga, kad joj se približi, zovnu: “Neka su blagoslovljeni oni koji se nalaze na mjestu na kojem je vatra i oni oko nje, i neka je hvaljen Allah, Gospodar svjetova!');
INSERT INTO "bs_darassalam" VALUES(27,9,'27:9','O Musa, to sam Ja – Allah, Silni i Mudri!');
INSERT INTO "bs_darassalam" VALUES(27,10,'27:10','Baci svoj štap!” Pa kad ga vidje da se, kao da je hitra zmija, kreće, on uzmače i ne vrati se. “O Musa, ne boj se! Poslanici se kod Mene ne trebaju ničeg bojati.');
INSERT INTO "bs_darassalam" VALUES(27,11,'27:11','A onom koji grijeh počini, a onda zlo dobrim djelom zamijeni – Ja ću, uistinu, oprostiti i samilostan biti.');
INSERT INTO "bs_darassalam" VALUES(27,12,'27:12','Uvuci ruku svoju u njedra svoja, pojavit će se bijela, ali neće biti bolesna – bit će to jedno od devet čuda faraonu i narodu njegovu; oni su, doista, narod nevjernički.”');
INSERT INTO "bs_darassalam" VALUES(27,13,'27:13','I kad im očito dođoše znamenja Naša, oni rekoše: “Ovo je prava čarolija!”');
INSERT INTO "bs_darassalam" VALUES(27,14,'27:14','I oni ih, nepravedni i oholi, porekoše, ali su u sebi vjerovali da su istinita, pa pogledaj kako su skončali smutljivci.');
INSERT INTO "bs_darassalam" VALUES(27,15,'27:15','Davudu i Sulejmanu smo znanje dali i oni su govorili: “Hvala Allahu Koji nas je odlikovao iznad mnogih vjernika, robova Svojih!”');
INSERT INTO "bs_darassalam" VALUES(27,16,'27:16','I Sulejman naslijedi Davuda i reče: “O ljudi, dato nam je da razumijemo ptičije glasove i svašta nam je dato – ovo je, zaista, prava blagodat!”');
INSERT INTO "bs_darassalam" VALUES(27,17,'27:17','I sakupiše se Sulejmanu vojske njegove – džini, ljudi i ptice, sve četa do čete, postrojeni...');
INSERT INTO "bs_darassalam" VALUES(27,18,'27:18','i kad stigoše do mravlje doline, jedan mrav reče: “O mravi, ulazite u stanove svoje da vas ne izgazi Sulejman i vojske njegove, a da to i ne primijete!”');
INSERT INTO "bs_darassalam" VALUES(27,19,'27:19','I on se nasmiješi zasmijan riječima njegovim i reče: “Gospodaru moj, omogući mi da budem zahvalan na blagodati Tvojoj, koju si ukazao meni i roditeljima mojim, i da činim dobra djela na zadovoljstvo Tvoje, i uvedi me, milošću Svojom, među dobre robove Svoje!”');
INSERT INTO "bs_darassalam" VALUES(27,20,'27:20','I on izvrši smotru ptica, pa reče: “Zašto ne vidim pupavca, da nije odsutan?');
INSERT INTO "bs_darassalam" VALUES(27,21,'27:21','Ako mi ne donese valjano opravdanje, teškom ću ga kaznom kazniti ili ću ga zaklati!”');
INSERT INTO "bs_darassalam" VALUES(27,22,'27:22','I ne potraja dugo, a on dođe, pa reče: “Doznao sam ono što ti ne znaš, iz Sabe ti donosim pouzdanu vijest.');
INSERT INTO "bs_darassalam" VALUES(27,23,'27:23','Vidio sam da jedna žena njima vlada i da joj je svega i svačega dato, a ima i prijesto veličanstveni...');
INSERT INTO "bs_darassalam" VALUES(27,24,'27:24','vidio sam da se i ona i narod njezin Suncu klanjaju, a ne Allahu – šejtan im je prikazao lijepim postupke njihove i od Pravog ih puta odvratio, te oni ne umiju naći Pravi put...');
INSERT INTO "bs_darassalam" VALUES(27,25,'27:25','pa da se klanjaju Allahu, Koji izvodi ono što je skriveno na nebesima i u Zemlji i Koji zna ono što krijete i ono što na javu iznosite.');
INSERT INTO "bs_darassalam" VALUES(27,26,'27:26','Allah je, nema boga osim Njega, Gospodar Arša veličanstvenog!”');
INSERT INTO "bs_darassalam" VALUES(27,27,'27:27','“Vidjet ćemo”, reče Sulejman, “govoriš li istinu ili ne.');
INSERT INTO "bs_darassalam" VALUES(27,28,'27:28','Odnesi ovo moje pismo pa im ga baci, a onda se od njih malo izmakni i pogledaj šta će jedni drugima reći!”');
INSERT INTO "bs_darassalam" VALUES(27,29,'27:29','“O velikaši”, reče ona, “meni je dostavljeno jedno poštovanja vrijedno pismo...');
INSERT INTO "bs_darassalam" VALUES(27,30,'27:30','od Sulejmana i glasi: ‘U ime Allaha, Milostivog, Samilosnog!...');
INSERT INTO "bs_darassalam" VALUES(27,31,'27:31','Ne pravite se većim od mene i dođite da mi se pokorite!’');
INSERT INTO "bs_darassalam" VALUES(27,32,'27:32','O velikaši”, reče ona, “savjetujte mi šta trebam u ovom slučaju uraditi, ja bez vas neću ništa odlučiti!”');
INSERT INTO "bs_darassalam" VALUES(27,33,'27:33','“Mi smo vrlo jaki i hrabri”, rekoše oni, “a ti se pitaš! Pa, gledaj šta ćeš narediti!”');
INSERT INTO "bs_darassalam" VALUES(27,34,'27:34','“Kad carevi osvoje neki grad”, reče ona, “oni ga razore, a ugledne stanovnike njegove učine poniženim – eto, tako oni rade.');
INSERT INTO "bs_darassalam" VALUES(27,35,'27:35','Poslat ću im jedan dar i vidjet ću sa čim će se izaslanici vratiti.”');
INSERT INTO "bs_darassalam" VALUES(27,36,'27:36','I kad on pred Sulejmana izađe, ovaj mu reče: “Zar da blagom mene pridobijete? Ono što je Allah meni dao bolje je od onog što je dao vama. Vi se onom što vam se daruje radujete!');
INSERT INTO "bs_darassalam" VALUES(27,37,'27:37','Vrati se njima! Mi ćemo im dovesti vojske kojima se neće moći oduprijeti i istjerat ćemo ih iz Sabe ponižene i pokorene.”');
INSERT INTO "bs_darassalam" VALUES(27,38,'27:38','“O dostojanstvenici, ko će mi od vas donijeti njezin prijesto prije nego što oni dođu da mi se pokore?”');
INSERT INTO "bs_darassalam" VALUES(27,39,'27:39','"Ja ću ti ga donijeti" – reče Ifrit, jedan od džina – "prije nego iz ove sjednice svoje ustaneš, ja sam za to snažan i pouzdan."');
INSERT INTO "bs_darassalam" VALUES(27,40,'27:40','“A ja ću ti ga donijeti”, reče onaj koji je učio iz Knjige, “prije nego što okom trepneš.” I kad Sulejman vidje da je prijesto već pored njega postavljen, uzviknu: “Ovo je blagodat Gospodara mog, Koji me iskušava hoću li zahvalan ili nezahvalan biti! A ko je zahvalan – u svoju je korist zahvalan, a ko je nezahvalan – pa Gospodar je moj neovisan i plemenit.');
INSERT INTO "bs_darassalam" VALUES(27,41,'27:41','Promijenite izgled njezina prijestola da vidimo hoće li ga ili neće prepoznati!”');
INSERT INTO "bs_darassalam" VALUES(27,42,'27:42','I kad ona dođe, bī joj rečeno: “Je li ovo tvoj prijesto?” “Kao da je on!”, uzviknu ona. “A nama je prije nego njoj dato znanje, i mi smo muslimani.”');
INSERT INTO "bs_darassalam" VALUES(27,43,'27:43','A da nije ispravno vjerovala, nju su omeli oni kojima se ona, mimo Allaha, klanjala, jer je ona narodu nevjerničkom pripadala.');
INSERT INTO "bs_darassalam" VALUES(27,44,'27:44','“Uđi u dvoranu!”, bī joj rečeno. I kad ona pogleda, pomisli da je duboka voda, pa zadiže haljinu uz noge svoje. “Ova je dvorana uglačanim staklom popločana!”, reče on. “Gospodaru moj”, uzviknu ona, “ja sam se prema sebi ogriješila i u društvu sa Sulejmanom predajem se Allahu, Gospodaru svjetova!”');
INSERT INTO "bs_darassalam" VALUES(27,45,'27:45','A Semudu smo poslali brata njihova Saliha da se klanjaju jedino Allahu – a oni se podijeliše u dvije skupine koje su se međusobno prepirale.');
INSERT INTO "bs_darassalam" VALUES(27,46,'27:46','“O narode moj”, govorio im je on, “zašto tražite da vas stigne kazna prije nego što se pokajete? Zašto od Allaha ne tražite oprosta da bi vam se ukazala milost?”');
INSERT INTO "bs_darassalam" VALUES(27,47,'27:47','“Mi smatramo hrđavim predznakom tebe i one koji su s tobom!”, rekoše oni. “Od Allaha vam je i dobro i zlo”, reče on, “vi ste narod koji je stavljen u iskušenje.”');
INSERT INTO "bs_darassalam" VALUES(27,48,'27:48','U gradu je bilo devet osoba koje su ne red nego nered činile.');
INSERT INTO "bs_darassalam" VALUES(27,49,'27:49','“Zakunite se najtežom zakletvom”, rekoše, “da ćemo noću njega i porodicu njegovu ubiti, a onda njegovu najbližem krvnom srodniku reći: ‘Mi nismo prisustvovali pogibiji porodice njegove, mi zaista istinu govorimo.’”');
INSERT INTO "bs_darassalam" VALUES(27,50,'27:50','I kovali su spletke, ali smo i Mi kovali spletke a da to oni i ne naslutiše.');
INSERT INTO "bs_darassalam" VALUES(27,51,'27:51','Pa pogledaj kakva je bila posljedica spletkarenja njihova: uništili smo sve, i njih i narod njihov...');
INSERT INTO "bs_darassalam" VALUES(27,52,'27:52','eno kuća njihovih, puste su zbog nepravde koju su činili – to je zaista pouka narodu koji zna...');
INSERT INTO "bs_darassalam" VALUES(27,53,'27:53','a spasili smo one koji su vjerovali i koji su se grijeha klonili.');
INSERT INTO "bs_darassalam" VALUES(27,54,'27:54','I Luta, kad reče narodu svom: “Zašto činite razvrat naočigled jedni drugih?');
INSERT INTO "bs_darassalam" VALUES(27,55,'27:55','Zar zbilja sa strašću općite sa muškarcima umjesto sa ženama? Vi ste, uistinu, bezumnici.”');
INSERT INTO "bs_darassalam" VALUES(27,56,'27:56','A odgovor naroda njegova glasio je: “Istjerajte Lutovu porodicu iz grada vašeg, oni su ljudi-čistunci!”');
INSERT INTO "bs_darassalam" VALUES(27,57,'27:57','I Mi smo spasili njega i porodicu njegovu, sve osim žene njegove. Mi smo odredili da ona ostane s onima koji će kaznu iskusiti...');
INSERT INTO "bs_darassalam" VALUES(27,58,'27:58','i pustili smo na njih kišu – a strašne li kiše za one koji su bili opomenuti!');
INSERT INTO "bs_darassalam" VALUES(27,59,'27:59','Reci: “Hvala Allahu i mir robovima Njegovim koje je On odabrao! – Šta je bolje: Allah ili oni koje Njemu ravnim smatraju?...');
INSERT INTO "bs_darassalam" VALUES(27,60,'27:60','Onaj Koji je nebesa i Zemlju stvorio i Koji vam spušta s neba kišu pomoću koje Mi dajemo da ozelene bašče prekrasne – nemoguće je da vi učinite da izraste drveće njihovo. Zar pored Allaha postoji drugi bog? Ne postoji, ali su oni narod koji druge s Njime izjednačuje.');
INSERT INTO "bs_darassalam" VALUES(27,61,'27:61','Onaj Koji je Zemlju prebivalištem učinio i kroz nju rijeke proveo i na njoj brda nepomična postavio i dva mora pregradio. Zar pored Allaha postoji drugi bog? Ne postoji, nego većina njih u neznanju živi.');
INSERT INTO "bs_darassalam" VALUES(27,62,'27:62','Onaj Koji se nevoljniku, kad Mu se obrati, odaziva, i Koji zlo otklanja i Koji vas na Zemlji namjesnicima postavlja. Zar pored Allaha postoji drugi bog? Kako nikako pouku vi da primite!');
INSERT INTO "bs_darassalam" VALUES(27,63,'27:63','Onaj Koji vam u tminama, na kopnu i na moru, put pokazuje i Koji vjetrove kao radosnu vijest ispred milosti Svoje šalje. Zar pored Allaha postoji drugi bog? Kako je Allah visoko iznad onih koji druge Njemu ravnim smatraju!');
INSERT INTO "bs_darassalam" VALUES(27,64,'27:64','Onaj Koji sve iz ničeg stvara, Koji će zatim to ponovo učiniti i Koji vam opskrbu s neba i iz zemlje daje. Zar pored Allaha postoji drugi bog? Reci: ‘Dokažite, ako istinu govorite!’”');
INSERT INTO "bs_darassalam" VALUES(27,65,'27:65','Reci: “Niko, osim Allaha, ni na nebu ni na Zemlji, ne zna šta će se dogoditi; i oni ne znaju kad će oživljeni biti.”');
INSERT INTO "bs_darassalam" VALUES(27,66,'27:66','Oni će o onom svijetu sve znati! Oni u njega sumnjaju, oni su slijepi prema njemu.');
INSERT INTO "bs_darassalam" VALUES(27,67,'27:67','Nevjernici govore: “Zar ćemo, kad postanemo zemlja, i mi i preci naši, zaista, biti oživljeni?');
INSERT INTO "bs_darassalam" VALUES(27,68,'27:68','Ovim nam se već odavno prijeti, i nama i precima našim, a ovo su samo izmišljotine naroda drevnih.”');
INSERT INTO "bs_darassalam" VALUES(27,69,'27:69','Reci: “Putujte po svijetu i vidite kako su završili grešnici!”');
INSERT INTO "bs_darassalam" VALUES(27,70,'27:70','I ne žalosti se zbog njih i neka ti nije u duši teško zbog spletki njihovih.');
INSERT INTO "bs_darassalam" VALUES(27,71,'27:71','“Kad će se već jednom obistiniti ta prijetnja, ako istinu govorite?”, pitaju oni.');
INSERT INTO "bs_darassalam" VALUES(27,72,'27:72','Reci: “Stići će vas sigurno nešto od onog što požurujete!”');
INSERT INTO "bs_darassalam" VALUES(27,73,'27:73','A Gospodar tvoj je neizmjerno dobar ljudima, ali većina njih nije zahvalna.');
INSERT INTO "bs_darassalam" VALUES(27,74,'27:74','Gospodar tvoj dobro zna ono što grudi njihove taje i ono što oni na javu iznose.');
INSERT INTO "bs_darassalam" VALUES(27,75,'27:75','Nema ničeg skrivenog ni na nebu ni na Zemlji, a da nije u Knjizi jasnoj.');
INSERT INTO "bs_darassalam" VALUES(27,76,'27:76','Ovaj Kur’an sinovima Israilovim kazuje najviše o onom u čemu se oni razilaze...');
INSERT INTO "bs_darassalam" VALUES(27,77,'27:77','i on je, uistinu, putokaz i milost svakom onom koji vjeruje.');
INSERT INTO "bs_darassalam" VALUES(27,78,'27:78','Gospodar tvoj će im po pravdi Svojoj presuditi – On je Silni i Sveznajući...');
INSERT INTO "bs_darassalam" VALUES(27,79,'27:79','zato se pouzdaj u Allaha, jer ti, doista, slijediš pravu istinu!');
INSERT INTO "bs_darassalam" VALUES(27,80,'27:80','Ti ne možeš mrtve dozvati ni gluhe dovikati kad se leđima okrenu...');
INSERT INTO "bs_darassalam" VALUES(27,81,'27:81','niti možeš slijepe od zablude njihove odvratiti. Možeš jedino dozvati one koji u riječi Naše vjeruju, oni će se odazvati.');
INSERT INTO "bs_darassalam" VALUES(27,82,'27:82','I kad dođe vrijeme da oni budu kažnjeni, Mi ćemo učiniti da iz zemlje izađe jedna životinja koja će im reći da ljudi u dokaze Naše nisu uvjereni.');
INSERT INTO "bs_darassalam" VALUES(27,83,'27:83','A na Dan kad od svakog naroda sakupimo gomilu onih koji su dokaze Naše poricali – oni će biti zadržani...');
INSERT INTO "bs_darassalam" VALUES(27,84,'27:84','i kad dođu, On će upitati: “Jeste li vi dokaze Moje poricali ne razmišljajući o njima ili šta ste to radili?”');
INSERT INTO "bs_darassalam" VALUES(27,85,'27:85','I njih će stići kazna zato što su mnogobošci bili, pa neće moći ni riječ izustiti.');
INSERT INTO "bs_darassalam" VALUES(27,86,'27:86','Zar nisu vidjeli da smo učinili noć da u njoj otpočinu, a dan vidnim? To su, zaista, dokazi za narod koji vjeruje.');
INSERT INTO "bs_darassalam" VALUES(27,87,'27:87','A na Dan kad se u rog puhne, pa se smrtno istrave i oni na nebesima i oni na Zemlji, izuzev onih koje Allah poštedi, svi će Mu ponizno doći.');
INSERT INTO "bs_darassalam" VALUES(27,88,'27:88','Ti vidiš planine i misliš da su nepomične, a one promiču kao što promiču oblaci – to je Allahovo djelo Koji je sve savršeno stvorio. – On, doista, zna ono što radite.');
INSERT INTO "bs_darassalam" VALUES(27,89,'27:89','Ko učini dobro djelo, dobit će veliku nagradu za njega i bit će straha na Sudnjem danu pošteđen.');
INSERT INTO "bs_darassalam" VALUES(27,90,'27:90','A oni koji budu zlo činili, u vatru će naglavačke biti gurnuti. “Zar se već kažnjavate za ono što ste radili?”');
INSERT INTO "bs_darassalam" VALUES(27,91,'27:91','Ja sam primio zapovijest da se klanjam jedino Gospodaru ovog grada kojeg je On učinio svetim – a Njemu sve pripada – i naređeno mi je da budem poslušan...');
INSERT INTO "bs_darassalam" VALUES(27,92,'27:92','i da Kur’an kazujem. Onaj ko bude išao Pravim putem, na Pravom je putu za svoje dobro; a onom ko je u zabludi ti reci: “Ja samo opominjem.”');
INSERT INTO "bs_darassalam" VALUES(27,93,'27:93','I reci: “Hvala Allahu, On će vam znamenja Svoja pokazati, pa ćete ih vi poznati!” A Gospodar tvoj motri na ono što radite.');
INSERT INTO "bs_darassalam" VALUES(28,1,'28:1','Tā Sīn Mīm.');
INSERT INTO "bs_darassalam" VALUES(28,2,'28:2','Ovo su ajeti Knjige jasne!');
INSERT INTO "bs_darassalam" VALUES(28,3,'28:3','Mi ćemo ti po Istini kazati neke vijesti o Musau i faraonu, i to za one ljude koji vjeruju.');
INSERT INTO "bs_darassalam" VALUES(28,4,'28:4','Zaista se faraon u zemlji oholio, i od stanovnika njenih načinio poklonike raznovrsne; jednu zajednicu je tlačio, mušku im djecu klao, a žensku u životu ostavljao. doista je jedan od remetitelja poretka bio!');
INSERT INTO "bs_darassalam" VALUES(28,5,'28:5','A Mi smo htjeli da one koji su na Zemlji tlačeni milošću obaspemo i da ih vođama i nasljednicima učinimo...');
INSERT INTO "bs_darassalam" VALUES(28,6,'28:6','i da im na Zemlji vlast darujemo, a da faraonu, Hamanu i vojskama njihovim damo da dožive baš ono zbog čega su od njih strahovali.');
INSERT INTO "bs_darassalam" VALUES(28,7,'28:7','I Mi nadahnusmo Musaovu majku: “Doji ga, a kad se uplašiš za njegov život, baci ga u rijeku, i ne strahuj i ne tuguj, Mi ćemo ti ga, doista, vratiti i poslanikom ga učiniti.”');
INSERT INTO "bs_darassalam" VALUES(28,8,'28:8','I nađoše ga faraonovi ljudi, da im postane dušmanin i jad; zaista su faraon i Haman i vojske njihove uvijek griješili.');
INSERT INTO "bs_darassalam" VALUES(28,9,'28:9','I žena faraonova reče: “On će biti radost i meni i tebi! Ne ubijte ga, možda će nam od koristi biti, a možemo ga i posiniti.” A oni ništa ne predosjetiše.');
INSERT INTO "bs_darassalam" VALUES(28,10,'28:10','I srce Musaove majke ostade prazno, umalo ga razotkri da Mi srce njeno nismo učvrstili i vjernicom je učinili.');
INSERT INTO "bs_darassalam" VALUES(28,11,'28:11','I ona reče sestri njegovoj: “Idi za njim!” I ona ga ugleda izdaleka, a oni nisu bili ništa primijetili.');
INSERT INTO "bs_darassalam" VALUES(28,12,'28:12','A Mi smo mu već otprije zabranili dojilje, pa ona reče: "Hoćete li da vam ja pokažem porodicu koja će se o njemu brinuti i koja će mu dobro željeti?"');
INSERT INTO "bs_darassalam" VALUES(28,13,'28:13','I vratismo ga majci njegovoj da se raduje i da ne tuguje, i da se uvjeri da je Allahovo obećanje istinito – ali većina njih ne zna.');
INSERT INTO "bs_darassalam" VALUES(28,14,'28:14','I kad se on opasa snagom i stasa, dadosmo mu mudrost i znanje; tako Mi nagrađujemo one koji dobra djela čine.');
INSERT INTO "bs_darassalam" VALUES(28,15,'28:15','I on uđe u grad neopažen od stanovnika njegovih i u njemu zateče dvojicu ljudi kako se tuku – jedan je pripadao njegovu, a drugi neprijateljskom narodu – pa ga zovnu u pomoć onaj iz njegova naroda protiv onog iz neprijateljskog naroda, i Musa ga udari šakom i usmrti. “Ovo je šejtanov posao”, uzviknu, “on je, zaista, otvoreni neprijatelj koji u zabludu odvodi!”');
INSERT INTO "bs_darassalam" VALUES(28,16,'28:16','“Gospodaru moj”, reče onda, “ja sam sam sebi zlo nanio, oprosti mi!” I On mu oprosti, On, uistinu, prašta i On je milostiv.');
INSERT INTO "bs_darassalam" VALUES(28,17,'28:17','"O Gospodaru moj", reče, "zato što si mi blagodati dao, nikada prestupnicima neću biti od pomoći."');
INSERT INTO "bs_darassalam" VALUES(28,18,'28:18','I Musa u gradu osvanu prestrašen, očekujući šta će biti, kad ga onaj isti od jučer pozva ponovo u pomoć. “Ti si, zbilja, u pravoj zabludi!”, reče.');
INSERT INTO "bs_darassalam" VALUES(28,19,'28:19','I kad htjede ščepati zajedničkog im neprijatelja, reče mu njegov sunarodnik: “O Musa, zar ćeš ubiti i mene kao što si jučer ubio čovjeka? Ti hoćeš na Zemlji silu provoditi, a ne želiš miriti.”');
INSERT INTO "bs_darassalam" VALUES(28,20,'28:20','I jedan čovjek s kraja grada dotrča: “O Musa”, reče, “glavešine se dogovaraju da te ubiju; zato bježi, ja sam ti zbilja iskren savjetnik.”');
INSERT INTO "bs_darassalam" VALUES(28,21,'28:21','I Musa izađe iz grada, ustrašen, iščekujući šta će se desiti. “Gospodaru moj”, reče, “spasi me naroda koji ne vjeruje!”');
INSERT INTO "bs_darassalam" VALUES(28,22,'28:22','I kad se uputi prema Medjenu, on reče: “Gospodar moj će mi pokazati pravi put!”');
INSERT INTO "bs_darassalam" VALUES(28,23,'28:23','A kad stiže do vode medjenske, zateče oko nje mnoge ljude kako napajaju stoku, a malo podalje od njih ugleda dvije žene koje su je od vode odbijale. “Šta vi radite?”, upita on. “Mi ne napajamo dok čobani ne odu”, odgovoriše one, “a otac nam je veoma star.”');
INSERT INTO "bs_darassalam" VALUES(28,24,'28:24','I on im je napoji, a onda ode u hladovinu i reče: “Gospodaru moj, ma kakvu mi hranu dao, zaista mi je potrebna!”');
INSERT INTO "bs_darassalam" VALUES(28,25,'28:25','I jedna od njih dvije dođe mu, poslije, idući stidljivo i reče: “Otac moj te zove da te nagradi zato što si nam stoku napojio!” I kad mu on dođe i kaza mu šta je doživio, on reče: “Ne strahuj, spasio si se naroda koji ne vjeruje!”');
INSERT INTO "bs_darassalam" VALUES(28,26,'28:26','“O oče moj”, reče jedna od njih, “uzmi ga u najam, najbolje da unajmiš snažna i pouzdana.”');
INSERT INTO "bs_darassalam" VALUES(28,27,'28:27','“Ja te želim oženiti jednom od ove dvije kćeri moje”, reče on, “ali trebaš me osam godina služiti. A ako deset napuniš, bit će dobra volja tvoja, a ja te ne želim na to siliti. Ti ćeš vidjeti, ako Bog da, da sam dobar.”');
INSERT INTO "bs_darassalam" VALUES(28,28,'28:28','“Neka bude tako između mene i tebe”, reče Musa, “koji god od ta dva roka ispunim, nema mi se šta prigovoriti, a Allah je jamac za ono što smo utanačili.”');
INSERT INTO "bs_darassalam" VALUES(28,29,'28:29','I kad Musa ispuni ugovoreni rok i krenu sa čeljadi svojom, on ugleda vatru na jednoj strani brda. “Pričekajte”, reče čeljadi svojoj, “vidio sam vatru, možda ću vam od nje kakvu vijest donijeti ili zapaljenu glavnju, da se ogrijete.”');
INSERT INTO "bs_darassalam" VALUES(28,30,'28:30','A kad dođe do vatre, neko ga zovnu s desne strane doline, pored stabla, u blagoslovljenom kraju: “O Musa, Ja sam Allah, Gospodar svjetova!');
INSERT INTO "bs_darassalam" VALUES(28,31,'28:31','I baci svoj štap!" I kad je vidje da se tresući kreće, kao da je to džann-zmija, on okrenut leđima uzmače i ne osvrnu se. "O Musa, priđi i ne boj se! Ti si od onih sigurnih!');
INSERT INTO "bs_darassalam" VALUES(28,32,'28:32','Uvuci svoju ruku u njedra svoja, pojavit će se bijela, a bez mahane, i priberi se tako od straha! To su dva dokaza od Gospodara tvog faraonu i glavešinama njegovim; oni su, zaista, narod raskalašeni.”');
INSERT INTO "bs_darassalam" VALUES(28,33,'28:33','“Gospodaru moj”, reče, “ja sam ubio jednog njihovog čovjeka, pa se bojim da i oni mene ne ubiju.');
INSERT INTO "bs_darassalam" VALUES(28,34,'28:34','A moj brat Harun rječitiji je od mene, pa pošalji sa mnom i njega kao pomoćnika da potvrđuje riječi moje, jer se bojim da me ne nazovu lašcem.”');
INSERT INTO "bs_darassalam" VALUES(28,35,'28:35','“Pomoći ćemo te bratom tvojim”, reče On, “i obojici ćemo vlast dati, pa vam se oni neće usuditi prići. S Našim znamenjima vas dvojica i oni koji vas budu slijedili postat ćete pobjednici.”');
INSERT INTO "bs_darassalam" VALUES(28,36,'28:36','I kad im Musa donese Naše jasne dokaze, oni povikaše: “Ovo je samo smišljena čarolija, nismo čuli da se ovakvo nešto dešavalo u doba predaka naših!”');
INSERT INTO "bs_darassalam" VALUES(28,37,'28:37','“Gospodar moj dobro zna onog koji donosi uputstvo od Njega”, reče Musa, “onog koji će na kraju pobijediti – a nevjernici doista neće uspjeti!”');
INSERT INTO "bs_darassalam" VALUES(28,38,'28:38','“O velikaši”, reče faraon, “ja ne znam da vi imate drugog boga osim mene. A ti, o Hamane, peci mi opeke i sagradi mi toranj da se popnem k Musaovu Bogu, jer ja mislim da je on, zaista, lažac!”');
INSERT INTO "bs_darassalam" VALUES(28,39,'28:39','A on i vojske njegove bijahu se bez ikakva osnova ponijeli na Zemlji i mislili su da Nam neće biti vraćeni...');
INSERT INTO "bs_darassalam" VALUES(28,40,'28:40','pa Mi dohvatismo i njega i vojske njegove i u more ih bacismo; pogledaj kako su skončali nevjernici!');
INSERT INTO "bs_darassalam" VALUES(28,41,'28:41','A bili smo ih učinili vođama koji su pozivali u ono zbog čega se ide u vatru – a na Sudnjem danu niko im neće pomoći...');
INSERT INTO "bs_darassalam" VALUES(28,42,'28:42','i popratismo ih prokletstvom na ovom svijetu, a na onom svijetu bit će od svakog dobra udaljeni.');
INSERT INTO "bs_darassalam" VALUES(28,43,'28:43','I Mi smo Musau Knjigu dali, nakon što smo drevne narode uništili, da bude svjetlo ljudima i uputstvo i milost da bi sebi došli.');
INSERT INTO "bs_darassalam" VALUES(28,44,'28:44','Ti nisi bio na strani zapadnoj kad smo Musau poslanstvo povjerili, a nisi bio ni njegov savremenik.');
INSERT INTO "bs_darassalam" VALUES(28,45,'28:45','Mi smo mnoge narode podigli i oni su dugo živjeli. A ti nisi boravio među stanovnicima Medjena da im kazuješ riječi Naše, nego ti Mi o njima kazujemo vijesti.');
INSERT INTO "bs_darassalam" VALUES(28,46,'28:46','Ti nisi bio pored brda kad smo Musaa pozvali, ali Mi smo te poslali kao milost Gospodara tvog da opominješ ljude kojima prije tebe nije došao niko ko bi ih opomenuo, da bi se opametili...');
INSERT INTO "bs_darassalam" VALUES(28,47,'28:47','i da ne reknu kad ih kazna stigne zbog onog što su počinili: “Gospodaru naš, zašto nam nisi poslao poslanika, pa da dokaze Tvoje slijedimo i vjernici budemo?”');
INSERT INTO "bs_darassalam" VALUES(28,48,'28:48','A kad im dolazi istina od Nas, oni govore: “Zašto mu nije dato onako isto kao što je dato Musau?” A zar oni još davno nisu porekli ono što je Musau dato? Oni govore: “Dvije čarolije, jedna drugu podržava.” I govore: “Mi ni u jednu ne vjerujemo.”');
INSERT INTO "bs_darassalam" VALUES(28,49,'28:49','Reci: “Pa donesite vi od Allaha knjigu koja bolje nego ove dvije na Pravi put upućuje, nju bih slijedio, ako istinu govorite!”');
INSERT INTO "bs_darassalam" VALUES(28,50,'28:50','Pa ako ti se ne odazovu, onda znaj da se oni povode jedino za strastima svojim. A zar je iko gore zalutao od onog koji slijedi strast svoju, a ne Allahovu uputu? Allah, doista, neće ukazati na Pravi put narodu koji sam sebi nepravdu čini.');
INSERT INTO "bs_darassalam" VALUES(28,51,'28:51','Riječ smo im temeljito dostavili ne bi li se opametili.');
INSERT INTO "bs_darassalam" VALUES(28,52,'28:52','Oni kojima smo dali Knjigu prije Kur’ana, vjeruju u njega.');
INSERT INTO "bs_darassalam" VALUES(28,53,'28:53','A kad im se kazuje, govore: “Mi vjerujemo u njega, on je istina od Gospodara našeg, mi smo i prije bili muslimani.”');
INSERT INTO "bs_darassalam" VALUES(28,54,'28:54','Oni će dobiti dvostruku nagradu zato što bili strpljivi i što dobrim djelom odbijaju zlo i što od onog što im dajemo udjeljuju.');
INSERT INTO "bs_darassalam" VALUES(28,55,'28:55','A kad čuju besmislicu kakvu, od nje se okrenu i reknu: “Nama naša djela, a vama vaša djela – mir vama! Mi ne želimo društvo neukih.”');
INSERT INTO "bs_darassalam" VALUES(28,56,'28:56','Ti, doista, ne možeš uputiti na Pravi put onog koga ti želiš uputiti. Allah ukazuje na Pravi put onom kome On hoće, i On dobro zna one koji će Pravim putem poći.');
INSERT INTO "bs_darassalam" VALUES(28,57,'28:57','Oni govore: “Ako s tobom budemo Pravi put slijedili, bit ćemo brzo iz rodnog kraja protjerani.” Zar im Mi ne pružamo priliku da borave u svetom i sigurnom mjestu gdje se, kao Naš dar, slivaju plodovi svakovrsni; međutim, većina njih ne zna.');
INSERT INTO "bs_darassalam" VALUES(28,58,'28:58','A koliko smo Mi sela i gradova uništili čiji su stanovnici u životu obijesni bili! Eno domova njihovih, malo ko, poslije njih, navrati u njih, Nama su ostali.');
INSERT INTO "bs_darassalam" VALUES(28,59,'28:59','A Gospodar tvoj nikada nije naselja uništavao dok u njihov glavni grad poslanika ne bi poslao, da im dokaze Naše kazuje. I Mi smo samo onda naselja uništavali kad su stanovnici njihovi nasilnici bili.');
INSERT INTO "bs_darassalam" VALUES(28,60,'28:60','Sve što vam je darovano samo su naslade i ukrasi u životu na ovom svijetu, a ono što je u Allaha bolje je i trajno je. Zar ne razumijete?');
INSERT INTO "bs_darassalam" VALUES(28,61,'28:61','Kako može biti jednak onaj kome smo lijepu nagradu obećali, i koju će dobiti, i onaj kome smo dali da se naslađuje u životu na ovom svijetu, a koji će, poslije, na onom svijetu u vatru ubačen biti?');
INSERT INTO "bs_darassalam" VALUES(28,62,'28:62','A na Dan kad ih On pozove i upita: “Gdje su oni koje ste Meni ravnim smatrali?”...');
INSERT INTO "bs_darassalam" VALUES(28,63,'28:63','reći će oni na kojima će se riječ obistiniti: “Gospodaru naš, ove što smo na stranputicu naveli – naveli smo zato što smo i sami na stranputici bili; mi ih se pred Tobom odričemo, oni se nama nisu klanjali.”');
INSERT INTO "bs_darassalam" VALUES(28,64,'28:64','“Pozovite božanstva vaša!”, reći će im se, pa će ih oni pozivati, ali im se ona neće odazvati, i patnju će doživjeti. Ah, da su na Pravom putu bili!');
INSERT INTO "bs_darassalam" VALUES(28,65,'28:65','A na Dan kad ih On pozove i upita: “Šta ste poslanicima odgovorili?”...');
INSERT INTO "bs_darassalam" VALUES(28,66,'28:66','tog Dana oni neće znati šta će odgovoriti, pa ni jedan drugog neće ništa pitati.');
INSERT INTO "bs_darassalam" VALUES(28,67,'28:67','A onaj koji se bude pokajao, i koji bude vjerovao, i koji bude dobra djela činio – on će postići šta je želio.');
INSERT INTO "bs_darassalam" VALUES(28,68,'28:68','Gospodar tvoj stvara šta hoće, i On odabire. Oni nemaju pravo birati. Hvaljen neka je Allah i vrlo visoko iznad onih koje s Njime izjednačuju!');
INSERT INTO "bs_darassalam" VALUES(28,69,'28:69','Gospodar tvoj zna ono što srca njihova kriju i ono što oni iskazuju.');
INSERT INTO "bs_darassalam" VALUES(28,70,'28:70','On je Allah, drugog boga osim Njega nema; Njemu neka je hvala i na ovom i na onom svijetu! Samo On sudi i Njemu ćete se vratiti.');
INSERT INTO "bs_darassalam" VALUES(28,71,'28:71','Reci: “Kažite vi meni – ako bi Allah dao da vam noć potraje vječno, do Sudnjega dana – koji bog bi vam, osim Allaha, svjetlo dao? Zar ne čujete?”');
INSERT INTO "bs_darassalam" VALUES(28,72,'28:72','Reci: “Kažite vi meni – ako bi Allah dao da vam dan potraje vječno, do Sudnjega dana – koji bog bi vam, osim Allaha, noć dao da u njoj smiraj nađete? Zar ne vidite?!"');
INSERT INTO "bs_darassalam" VALUES(28,73,'28:73','Iz milosti Svoje On vam je dao noć i dan; da se u njoj odmarate, a da iz dobara Njegovih privređujete i da zahvalni budete.”');
INSERT INTO "bs_darassalam" VALUES(28,74,'28:74','A na Dan kad ih On pozove i upita: “Gdje su oni koje ste Meni sudruzima smatrali?"');
INSERT INTO "bs_darassalam" VALUES(28,75,'28:75','iz svakog naroda dovest ćemo svjedoka i reći: “Dajte svoj dokaz!” i oni će saznati da je Bog jedino Allah, a neće im biti onih koje su izmišljali.');
INSERT INTO "bs_darassalam" VALUES(28,76,'28:76','Karun je iz Musaova naroda bio, pa ih je tlačio, a bili smo mu dali toliko blaga da mu je ključeve od njega teško mogla nositi gomila snažnih ljudi. “Ne budi obijestan, jer Allah ne voli one koji su obijesni!”, govorili su mu ljudi iz naroda njegova...');
INSERT INTO "bs_darassalam" VALUES(28,77,'28:77','“i nastoj time što ti je Allah dao steći onaj svijet, a ne zaboravi ni svoj udio na ovom svijetu i čini drugima dobro, kao što je Allah tebi dobro učinio, i ne čini nered po Zemlji, jer Allah ne voli one koji nered čine.”');
INSERT INTO "bs_darassalam" VALUES(28,78,'28:78','“Ovo mi je dato samo po znanju mom!”, govorio je on. A zar nije znao da je Allah prije njega uništio neke narode koji su bili od njega jači i koji su bili više nakupili – a zločinci neće o grijesima svojim ni ispitivani biti.');
INSERT INTO "bs_darassalam" VALUES(28,79,'28:79','I izađe on pred narod svoj u svom sjaju. “Ah, da je i nama ono što je dato Karunu”, govorili su oni koji su čeznuli za životom na ovom svijetu, “on je, uistinu, presretan.”');
INSERT INTO "bs_darassalam" VALUES(28,80,'28:80','“Teško vama”, govorili su učeni, “onom koji vjeruje i čini dobra djela bolja je Allahova nagrada, a bit će samo strpljivima pružena!”');
INSERT INTO "bs_darassalam" VALUES(28,81,'28:81','I Mi smo i njega i dvorac njegov u zemlju utjerali, i niko ga od Allahove kazne nije mogao odbraniti, a ni sam sebi nije mogao pomoći.');
INSERT INTO "bs_darassalam" VALUES(28,82,'28:82','A oni koji su ranije priželjkivali da su na njegovu mjestu, stadoše govoriti: “Zar ne vidite da Allah daje obilje onom od robova Svojih kome On hoće, a i da uskraćuje! Da nam Allah nije milost Svoju ukazao, i nas bi u zemlju utjerao. Zar ne vidiš da nezahvalnici nikad neće uspjeti?”');
INSERT INTO "bs_darassalam" VALUES(28,83,'28:83','Taj drugi svijet dat ćemo onima koji se ne žele na Zemlji oholiti i nered činiti, a one koji se Allaha boje čeka sretan kraj.');
INSERT INTO "bs_darassalam" VALUES(28,84,'28:84','Onaj ko učini dobro djelo dobit će deseterostruku nagradu za njega, a onaj ko uradi zlo – pa oni koji budu zlo radili bit će tačno prema zasluzi kažnjeni.');
INSERT INTO "bs_darassalam" VALUES(28,85,'28:85','Onaj Koji ti objavljuje Kur’an sigurno će te vratiti povratištu. Reci: “Gospodar moj dobro zna ko je na Pravom putu i ko je u očitoj zabludi.”');
INSERT INTO "bs_darassalam" VALUES(28,86,'28:86','Ti nisi očekivao da će ti Knjiga biti objavljena, ali ona ti je objavljena kao milost Gospodara tvog – zato nikako ne budi nevjernicima saučesnik!');
INSERT INTO "bs_darassalam" VALUES(28,87,'28:87','I neka te oni nikako ne odvrate od Allahovih riječi, kad ti se objave, i pozivaj Gospodaru svom i nikako ne budi od onih koji druge Njemu smatraju ravnim!');
INSERT INTO "bs_darassalam" VALUES(28,88,'28:88','I ne klanjaj se, pored Allaha, drugom bogu! Nema boga osim Njega! Sve će, osim Njegova lica, propasti! On će suditi, i Njemu ćete se povratiti!');
INSERT INTO "bs_darassalam" VALUES(29,1,'29:1','Elif Lām Mīm.');
INSERT INTO "bs_darassalam" VALUES(29,2,'29:2','Misle li ljudi da će biti ostavljeni na miru ako kažu: “Mi vjerujemo!”, i da u iskušenje neće biti dovedeni?');
INSERT INTO "bs_darassalam" VALUES(29,3,'29:3','A Mi smo u iskušenje dovodili i one prije njih, da bi Allah sigurno ukazao na one koji govore istinu i na one koji lažu.');
INSERT INTO "bs_darassalam" VALUES(29,4,'29:4','Zar misle oni koji zla djela rade da će Nama umaći? – Loše prosuđuju!');
INSERT INTO "bs_darassalam" VALUES(29,5,'29:5','Onaj ko se boji susreta s Allahom – pa doći će, sigurno, Dan obećani. – A On sve čuje i sve zna!');
INSERT INTO "bs_darassalam" VALUES(29,6,'29:6','A onaj ko se bori – bori se samo za sebe, jer Allah sigurno može bez svih svjetova biti.');
INSERT INTO "bs_darassalam" VALUES(29,7,'29:7','Onima koji vjeruju i dobra djela čine preći ćemo sigurno preko hrđavih postupaka njihovih i za ono što su radili doista ćemo ih najljepšom nagradom nagraditi.');
INSERT INTO "bs_darassalam" VALUES(29,8,'29:8','Mi smo svakog čovjeka zadužili da bude dobar prema roditeljima svojim. Ali, ako te oni budu nagovarali da Meni nekog ravnim smatraš, o kome ti ništa ne znaš, onda ih ne slušaj. Meni ćete se vratiti, pa ću vas Ja o onom što ste radili obavijestiti.');
INSERT INTO "bs_darassalam" VALUES(29,9,'29:9','One koji vjeruju i dobra djela čine sigurno ćemo među one koji su dobri uvrstiti.');
INSERT INTO "bs_darassalam" VALUES(29,10,'29:10','Ima ljudi koji govore: “Vjerujemo u Allaha”, a kad neki Allaha radi bude na muke stavljen, on drži da je ljudsko mučenje isto što i Allahova kazna. A ako pobjeda dođe od Gospodara tvog, sigurno će oni reći: “Bili smo uz vas!” A zar Allah ne zna dobro ono što je u grudima čijim?');
INSERT INTO "bs_darassalam" VALUES(29,11,'29:11','Allah dobro zna one koji vjeruju i dobro zna one koji su dvolični.');
INSERT INTO "bs_darassalam" VALUES(29,12,'29:12','Nevjernici govore vjernicima: “Slijedite naš put, a mi ćemo nositi grijehe vaše!”, a ne bi ponijeli nijedan grijeh njihov, oni samo lažu...');
INSERT INTO "bs_darassalam" VALUES(29,13,'29:13','ali će, sigurno, vlastito breme i breme onih koje su u zabludu odveli nositi, i za laži koje su iznosili doista će na Sudnjem danu odgovarati.');
INSERT INTO "bs_darassalam" VALUES(29,14,'29:14','Mi smo Nuha narodu njegovu poslali i on je među njima ostao hiljadu manje pedeset godina, pa ih je potom zadesio potop, zato što su Allahu druge ravnim smatrali.');
INSERT INTO "bs_darassalam" VALUES(29,15,'29:15','I Mi smo njega i one što su bili u lađi spasili, i poučnim primjerom svjetovima je učinili.');
INSERT INTO "bs_darassalam" VALUES(29,16,'29:16','A i Ibrahima. “Allahu se jedino klanjajte”, govorio je on narodu svom, “i Njega se bojte, to vam je bolje, da znate.');
INSERT INTO "bs_darassalam" VALUES(29,17,'29:17','Vi se, mimo Allaha, kumirima klanjate i laži smišljate. Oni kojima se vi, mimo Allaha, klanjate ne mogu vas nikakvom hranom nahraniti. Vi hranu od Allaha tražite i Njemu se klanjajte i Njemu zahvalni budite! Njemu ćete se vratiti.”');
INSERT INTO "bs_darassalam" VALUES(29,18,'29:18','Ako poričete vi, pa poricali su i narodi koji su prije vas bili; a poslanik je jedino dužan jasno obznaniti.');
INSERT INTO "bs_darassalam" VALUES(29,19,'29:19','Zar ovi ne vide kako Allah sve iz ničeg stvara? On će to opet učiniti; Allahu je to, zaista, lahko.');
INSERT INTO "bs_darassalam" VALUES(29,20,'29:20','Reci: “Putujte po svijetu da vidite šta je On iz ničeg stvorio. I, Allah će to, poslije, po drugi put stvoriti. Allah, zaista, sve može...');
INSERT INTO "bs_darassalam" VALUES(29,21,'29:21','On kažnjava onog koga On hoće i milost ukazuje onom kome On hoće, i Njemu ćete se vratiti.”');
INSERT INTO "bs_darassalam" VALUES(29,22,'29:22','Njegovoj kazni nećete umaći ni na Zemlji ni na nebu, a nemate, mimo Allaha, ni zaštitnika ni pomagača.');
INSERT INTO "bs_darassalam" VALUES(29,23,'29:23','A onima koji neće da vjeruju u Allahove dokaze i u susret s Njime, njima nije stalo do Moje milosti – njih čeka patnja nesnosna.');
INSERT INTO "bs_darassalam" VALUES(29,24,'29:24','Odgovor naroda njegova bijaše: “Ubijte ga ili spalite!”, ali Allah ga je iz vatre izbavio. To su, uistinu, dokazi za narod koji vjeruje.');
INSERT INTO "bs_darassalam" VALUES(29,25,'29:25','“Vi ste”, reče on, “mimo Allaha kumire prihvatili da biste u životu na ovom svijetu međusobne prijateljske odnose održavali, a poslije, na Sudnjem danu, jedni drugih ćete se odricati i jedni druge ćete proklinjati, vatra će vaše boravište biti i niko vam u pomoć neće moći priteći.”');
INSERT INTO "bs_darassalam" VALUES(29,26,'29:26','I Lut mu jedini povjerova! A Ibrahim reče: “Ja se selim onamo kuda mi je Gospodar moj naredio, jer je On, uistinu, silan i mudar.”');
INSERT INTO "bs_darassalam" VALUES(29,27,'29:27','I Mi smo mu Ishaka i Jakuba poklonili i potomcima njegovim vjerovjesništvo i Knjigu dali, a njemu na ovom svijetu lijep spomen sačuvali, a na Onom će, doista, jedan od onih dobrih biti.');
INSERT INTO "bs_darassalam" VALUES(29,28,'29:28','I Luta. Kad narodu svom reče: “Vi činite takav razvrat kakav prije vas niko na svijetu nije činio...');
INSERT INTO "bs_darassalam" VALUES(29,29,'29:29','s muškarcima općite, po drumovima presrećete i na skupovima svojim najodvratnije stvari činite”, odgovor naroda njegova bijaše: “Učini da nas Allahova kazna stigne, ako istinu govoriš!”');
INSERT INTO "bs_darassalam" VALUES(29,30,'29:30','“Gospodaru moj”, reče on, “pomozi mi protiv iskvarenih ljudi!”');
INSERT INTO "bs_darassalam" VALUES(29,31,'29:31','I kad izaslanici Naši Ibrahimu radosnu vijest donesoše i kad rekoše: “Mi ćemo uništiti stanovnike onog grada, jer su njegovi stanovnici nevjernici”...');
INSERT INTO "bs_darassalam" VALUES(29,32,'29:32','Ibrahim reče: “U njemu je Lut!” “Mi dobro znamo ko je u njemu”, rekoše oni, “mi ćemo njega i porodicu njegovu sigurno spasiti, osim žene njegove, ona će ostati s onima koji će kaznu iskusiti.”');
INSERT INTO "bs_darassalam" VALUES(29,33,'29:33','I kad izaslanici Naši dođoše Lutu, on se zbog njih sneveseli i uznemiri. “Ne boj se i nemoj se žalostiti”, rekoše oni. “Mi ćemo tebe i porodicu tvoju spasiti, osim žene tvoje – ona će ostati s onima koji će kaznu iskusiti.');
INSERT INTO "bs_darassalam" VALUES(29,34,'29:34','A na stanovnike ovog grada spustit ćemo strašnu kaznu s neba zbog toga što su razvratnici.”');
INSERT INTO "bs_darassalam" VALUES(29,35,'29:35','I od njega smo ostavili vidljive ostatke ljudima koji budu pameti imali.');
INSERT INTO "bs_darassalam" VALUES(29,36,'29:36','A u Medjen brata njihova Šuajba: “O narode moj”, govorio je on, “Allahu se jedino klanjajte i činite ono za što ćete dobiti nagradu na onom svijetu, a po Zemlji, nered praveći, zlo ne radite!”');
INSERT INTO "bs_darassalam" VALUES(29,37,'29:37','Ali mu oni ne povjerovaše, pa ih zadesi strašan potres i oni osvanuše u zemlji svojoj mrtvi, nepomični.');
INSERT INTO "bs_darassalam" VALUES(29,38,'29:38','A i Ada i Semuda – ostaci domova njihovih su vam vidljivi – šejtan im je lijepim njihove postupke predočio, pa ih, iako su razumni bili, od Pravog puta odvratio...');
INSERT INTO "bs_darassalam" VALUES(29,39,'29:39','I Karuna i faraona i Hamana; Musa im je jasne dokaze donio, ali su se oni na Zemlji oholo ponijeli i kaznu nisu izbjegli.');
INSERT INTO "bs_darassalam" VALUES(29,40,'29:40','I sve smo zbog grijeha njihovih kaznili: na neke vjetar, pun pijeska poslali, a neke strašnim glasom uništili; neke u zemlju utjerali, a neke potopili. – Allah im nije učinio nepravdu, sami su sebi nepravdu nanijeli.');
INSERT INTO "bs_darassalam" VALUES(29,41,'29:41','Oni koji, mimo Allaha, zaštitnike uzimaju slični su pauku koji sebi isplete kuću – a najslabija je kuća, uistinu, paukova kuća, neka znaju!');
INSERT INTO "bs_darassalam" VALUES(29,42,'29:42','Allah dobro zna da ovi kojima se oni, pored Njega, klanjaju ništa ne predstavljaju; On je Silni i Mudri!');
INSERT INTO "bs_darassalam" VALUES(29,43,'29:43','To su primjeri koje Mi ljudima navodimo, ali ih samo učeni shvaćaju.');
INSERT INTO "bs_darassalam" VALUES(29,44,'29:44','Allah je nebesa i Zemlju s razlogom stvorio; to je, doista, pouka onima koji vjeruju.');
INSERT INTO "bs_darassalam" VALUES(29,45,'29:45','Kazuj Knjigu koja ti se objavljuje i obavljaj molitvu. Molitva, zaista, odvraća od razvrata i od svega što je ružno, a sigurno je spominjanje Allaha veće! – A Allah zna šta radite.');
INSERT INTO "bs_darassalam" VALUES(29,46,'29:46','I sa sljedbenicima Knjige raspravljajte na najljepši način, ali ne i s onima među njima koji su nepravedni, i recite: “Mi vjerujemo u ono što se objavljuje nama, a naš Bog i vaš Bog jest – jedan, i mi se Njemu pokoravamo.”');
INSERT INTO "bs_darassalam" VALUES(29,47,'29:47','Tako isto i tebi objavljujemo Knjigu, a neki od onih kojima smo već dali Knjigu - i u nju vjeruju, a i među ovima ima onih koji u nju vjeruju, a ajete i dokaze Naše samo nevjernici negiraju.');
INSERT INTO "bs_darassalam" VALUES(29,48,'29:48','Ti prije nje nijednu knjigu nisi kazivao, a nisi je ni desnom rukom svojom pisao, inače posumnjali bi oni što laži govore.');
INSERT INTO "bs_darassalam" VALUES(29,49,'29:49','A to su ajeti jasni, u srcima su onih kojima je razum dat; a Naše ajete samo nepravedni osporavaju...');
INSERT INTO "bs_darassalam" VALUES(29,50,'29:50','I govore: "Zašto mu od Gospodara njegova nisu neki znaci poslani?" Reci: "Znaci su jedino u Allaha, a ja sam samo upozoritelj jasni."');
INSERT INTO "bs_darassalam" VALUES(29,51,'29:51','A zar im nije dosta to što Mi tebi objavljujemo Knjigu koja im se kazuje; u njoj je, doista, blagodat i pouka narodu koji vjeruje.');
INSERT INTO "bs_darassalam" VALUES(29,52,'29:52','Reci: “Allah je dovoljan svjedok meni i vama, On zna sve što je na nebesima i na Zemlji. A oni koji u laž vjeruju, a u Allaha ne vjeruju, oni su izgubljeni.”');
INSERT INTO "bs_darassalam" VALUES(29,53,'29:53','Oni traže od tebe da ih što prije stigne kazna. A da nije određenog roka za to, kazna bi im došla, a doći će im, sigurno, iznenada, oni neće predosjetiti.');
INSERT INTO "bs_darassalam" VALUES(29,54,'29:54','Požuruju te da ih kazna zadesi, a džehennem će sigurno sve nevjernike obuhvatiti...');
INSERT INTO "bs_darassalam" VALUES(29,55,'29:55','na Dan kad ih patnja i odozgo i odozdo obuzme, a Allah rekne: “Ispaštajte za ono što ste radili!”');
INSERT INTO "bs_darassalam" VALUES(29,56,'29:56','O robovi Moji koji vjerujete, Moja je Zemlja prostrana, zato se samo Meni ibadet činite!');
INSERT INTO "bs_darassalam" VALUES(29,57,'29:57','Svako će živo biće smrt okusiti, i Nama ćete se poslije vratiti.');
INSERT INTO "bs_darassalam" VALUES(29,58,'29:58','One koji budu vjerovali i dobra djela činili smjestit ćemo u džennetske odaje, ispod kojih rijeke teku, u njima će vječno boraviti. Kako će divna biti nagrada onima koji su se trudili...');
INSERT INTO "bs_darassalam" VALUES(29,59,'29:59','onima koji su trpjeli i u Gospodara svog se uzdali!');
INSERT INTO "bs_darassalam" VALUES(29,60,'29:60','A koliko ima životinja koje ne sakupljaju opskrbu svoju, Allah njih, a i vas, opskrbljuje! On sve čuje i sve zna.');
INSERT INTO "bs_darassalam" VALUES(29,61,'29:61','A da ih upitaš: “Ko je nebesa i Zemlju stvorio i ko je Sunce i Mjesec potčinio?”, sigurno bi rekli: “Allah!” Pa kuda se onda odmeću?');
INSERT INTO "bs_darassalam" VALUES(29,62,'29:62','Allah u izobilju daje hranu onom kome hoće od robova Svojih, a nekom i uskraćuje. – Allah, zaista, zna sve.');
INSERT INTO "bs_darassalam" VALUES(29,63,'29:63','A ako ih upitaš: “Ko s neba kišu spušta i njome mrtvu zemlju oživljava?”, sigurno će reći: “Allah!” A ti reci: “Hvala Allahu!”, ali većina njih ne shvaća.');
INSERT INTO "bs_darassalam" VALUES(29,64,'29:64','Život na ovom svijetu nije ništa drugo do zabava i igra, a samo onaj svijet je život, kad bi samo oni znali!');
INSERT INTO "bs_darassalam" VALUES(29,65,'29:65','Kad se u lađe ukrcaju, iskreno se mole Allahu. A kad ih On do kopna dovede, odjednom druge Njemu ravnim smatraju...');
INSERT INTO "bs_darassalam" VALUES(29,66,'29:66','da bi poricali ono što im Mi dajemo, i da bi uživali. A znat će oni!');
INSERT INTO "bs_darassalam" VALUES(29,67,'29:67','Zar ne vide da smo Harem svetim i sigurnim učinili, dok se svuda okolo njih otima i pljačka? I zar u laž vjeruju, a na Allahovim blagodatima su nezahvalni?');
INSERT INTO "bs_darassalam" VALUES(29,68,'29:68','I ima li onda nepravednijeg od onog koji o Allahu izmišlja laži ili poriče istinu koja mu dolazi? I zar nevjernicima nije mjesto u Džehennemu?');
INSERT INTO "bs_darassalam" VALUES(29,69,'29:69','One koji se budu zbog Nas borili Mi ćemo, sigurno, putevima koji Nama vode uputiti. – A Allah je, zaista, na strani onih koji dobra djela čine!');
INSERT INTO "bs_darassalam" VALUES(30,1,'30:1','Elif Lām Mīm.');
INSERT INTO "bs_darassalam" VALUES(30,2,'30:2','Bizantinci su pobijeđeni...');
INSERT INTO "bs_darassalam" VALUES(30,3,'30:3','u najnižoj zemlji, ali oni će, poslije poraza svog, sigurno pobijediti...');
INSERT INTO "bs_darassalam" VALUES(30,4,'30:4','za nekoliko godina – samo Allahu pripada odluka i prije i poslije – i tad će se vjernici radovati...');
INSERT INTO "bs_darassalam" VALUES(30,5,'30:5','Allahovoj pomoći – On pomaže kome hoće, On je Silni i Samilosni...');
INSERT INTO "bs_darassalam" VALUES(30,6,'30:6','obećanje je Allahovo, a Allah će obećanje Svoje ispuniti, ali većina ljudi ne zna.');
INSERT INTO "bs_darassalam" VALUES(30,7,'30:7','Oni znaju samo spoljašnju stranu života na ovom svijetu, a prema Ahiretu su nemarni.');
INSERT INTO "bs_darassalam" VALUES(30,8,'30:8','A zašto ne razmisle sami o sebi? Allah je stvorio nebesa i Zemlju i ono što je između njih sa ciljem i do roka određenog. A mnogi ljudi ne vjeruju da će pred Gospodara svog doista izaći.');
INSERT INTO "bs_darassalam" VALUES(30,9,'30:9','Zašto ne putuju po svijetu pa da vide kako su završili oni prije njih? Oni su bili od njih jači, i zemlju su orali i obrađivali je više nego što je obrađuju ovi, i poslanici su im donosili jasne dokaze. Allah im nije učinio nepravdu, sami su sebi nepravdu nanijeli.');
INSERT INTO "bs_darassalam" VALUES(30,10,'30:10','A onda, završetak onih koji su zlo činili bio je - i bit će - najužasnija kazna, zato što su Allahove riječi poricali i što su ih ruglu izvrgavali.');
INSERT INTO "bs_darassalam" VALUES(30,11,'30:11','Allah je počeo stvaranje, zatim ga ponavlja i na kraju Njemu ćete se vratiti.');
INSERT INTO "bs_darassalam" VALUES(30,12,'30:12','Na Dan kad nastupi Čas, mnogobošci će svaku nadu izgubiti...');
INSERT INTO "bs_darassalam" VALUES(30,13,'30:13','I oni neće imati među svojim božanstvima zagovornika i oni će svoja božanstva nijekati.');
INSERT INTO "bs_darassalam" VALUES(30,14,'30:14','Na Dan kad nastupi Čas oživljenja, ljudi će se razdvojiti...');
INSERT INTO "bs_darassalam" VALUES(30,15,'30:15','oni koji su vjerovali i dobra djela činili u džennetskom perivoju će se radovati...');
INSERT INTO "bs_darassalam" VALUES(30,16,'30:16','a oni koji nisu vjerovali i koji su ajete Naše i susret na Sudnjem danu poricali u trajnoj će muci biti.');
INSERT INTO "bs_darassalam" VALUES(30,17,'30:17','Pa hvaljen neka je Allah kad god omrknete i kad god osvanete...');
INSERT INTO "bs_darassalam" VALUES(30,18,'30:18','Njemu neka je hvala i na nebesima i na Zemlji – i predvečer i u podne!');
INSERT INTO "bs_darassalam" VALUES(30,19,'30:19','On iz neživog stvara živo i živo pretvara u neživo. On oživljava zemlju nakon mrtvila njezina – isto tako ćete i vi biti oživljeni.');
INSERT INTO "bs_darassalam" VALUES(30,20,'30:20','Jedan od dokaza Njegovih je to što vas od zemlje stvara, i odjednom vas, ljudi, svuda ima razasutih.');
INSERT INTO "bs_darassalam" VALUES(30,21,'30:21','I jedan od dokaza Njegovih je to što za vas, od vrste vaše, stvara žene da se uz njih smirite, i što između vas uspostavlja ljubav i samilost – to su, zaista, pouke za ljude koji razmišljaju.');
INSERT INTO "bs_darassalam" VALUES(30,22,'30:22','I jedan od dokaza Njegovih je stvaranje nebesa i Zemlje, i raznovrsnost jezika vaših i boja vaših – to su, zaista, pouke za one koji znaju.');
INSERT INTO "bs_darassalam" VALUES(30,23,'30:23','I jedan od dokaza Njegovih je san vaš noću i po danu, i nastojanje vaše da steknete nešto iz obilja Njegova – to su, zaista, pouke za ljude koji čuju.');
INSERT INTO "bs_darassalam" VALUES(30,24,'30:24','I jedan od dokaza Njegovih je to što vam pokazuje munju, da se pobojite i ponadate, a spušta s neba kišu i oživljava njome zemlju poslije mrtvila njezina – to su, zaista, pouke za ljude koji razumiju.');
INSERT INTO "bs_darassalam" VALUES(30,25,'30:25','I jedan od dokaza Njegovih je i to što nebo i Zemlja postoje voljom Njegovom. Zatim, čim vas On samo jednom iz zemlje pozove, brzo ćete ustati.');
INSERT INTO "bs_darassalam" VALUES(30,26,'30:26','Njemu pripada sve što je na nebesima i na Zemlji, sve je Njemu poslušno.');
INSERT INTO "bs_darassalam" VALUES(30,27,'30:27','On je Taj Koji iz ničeg stvara i On će to ponovo učiniti, to je Njemu lahko. On je uzvišen i na nebesima i na Zemlji. – On je silan i mudar.');
INSERT INTO "bs_darassalam" VALUES(30,28,'30:28','On vam kao primjer navodi vas same: jesu li oni koji su u posjedu vašem izjednačeni s vama u onom što vam Mi dajemo, pa ste u tome isti, i bojite li ih se kao što se vi jedni drugih bojite? – Eto, tako Mi, podrobno, izlažemo dokaze Naše ljudima koji razmišljaju.');
INSERT INTO "bs_darassalam" VALUES(30,29,'30:29','Ali, nevjernici lahkomisleno slijede strasti svoje – a ko će na Pravi put uputiti onog koga je Allah u zabludi ostavio? – njima neće moći niko u pomoć priteći.');
INSERT INTO "bs_darassalam" VALUES(30,30,'30:30','Ti upravi lice svoje vjeri, kao pravi vjernik, djelu Allahovu, prema kojoj je On ljude načinio – ne može se mijenjati Allahova vjera, ali većina ljudi to ne zna...');
INSERT INTO "bs_darassalam" VALUES(30,31,'30:31','obraćajući Mu se predano! Bojte se Njega i obavljajte molitvu, i ne budite od onih koji Mu druge ravnim smatraju...');
INSERT INTO "bs_darassalam" VALUES(30,32,'30:32','od onih koji su vjeru svoju razbili i u stranke se podijelili; svaka stranka zadovoljna onim što ispovijeda.');
INSERT INTO "bs_darassalam" VALUES(30,33,'30:33','A kad ljude nevolja snađe, oni se Gospodaru svom pokajnički obraćaju, a poslije, kad im On dadne da okuse milost Njegovu, odjednom neki od njih Gospodaru svom druge ravnim smatraju...');
INSERT INTO "bs_darassalam" VALUES(30,34,'30:34','da bi bili nezahvalni na onom što im Mi dajemo. Pa uživajte, a saznat ćete!');
INSERT INTO "bs_darassalam" VALUES(30,35,'30:35','Zar smo im Mi poslali kakav dokaz koji govori u prilog onih koje Njemu ravnim smatraju?');
INSERT INTO "bs_darassalam" VALUES(30,36,'30:36','Kad dopustimo ljudima da se blagodatima naslađuju, oni im se obraduju, a kad ih pogodi nevolja, zbog onog što su ruke njihove činile, odjednom očajavaju.');
INSERT INTO "bs_darassalam" VALUES(30,37,'30:37','Zar oni ne znaju da Allah u obilju daje hranu onom kome On hoće i da uskraćuje? To su, uistinu, dokazi za ljude koji vjeruju.');
INSERT INTO "bs_darassalam" VALUES(30,38,'30:38','Zato podaj bližnjemu pravo njegovo, i siromahu i putniku! To je bolje za one koji se nastoje Allahu umiliti – ti će postići šta žele.');
INSERT INTO "bs_darassalam" VALUES(30,39,'30:39','A novac koji dajete da se uveća novcem drugih ljudi neće se kod Allaha uvećati, a za milostinju koju udijelite da biste se Allahu umilili – takvi će dobra djela svoja umnogostručiti.');
INSERT INTO "bs_darassalam" VALUES(30,40,'30:40','Allah vas stvara, i opskrbljuje. On će vam život oduzeti i na kraju vas oživiti. Postoji li ijedno božanstvo vaše koje bilo šta od toga čini? Hvaljen neka je On i vrlo visoko iznad onih koje Njemu smatraju ravnim!');
INSERT INTO "bs_darassalam" VALUES(30,41,'30:41','Zbog onog što ljudi rade, pojavio se metež i na kopnu i na moru, da im On dadne da iskuse kaznu zbog onog što rade, ne bi li se popravili.');
INSERT INTO "bs_darassalam" VALUES(30,42,'30:42','Reci: “Putujte po svijetu pa pogledajte kako su oni prije završili; većinom su oni mnogobošci bili.”');
INSERT INTO "bs_darassalam" VALUES(30,43,'30:43','Zato ti upravi lice svoje prema pravoj vjeri, prije nego što Allahovom voljom nastupi Dan koji niko neće moći odbiti. Tog Dana On će ih razdvojiti...');
INSERT INTO "bs_darassalam" VALUES(30,44,'30:44','oni koji nisu vjerovali – na svoju štetu nisu vjerovali, a oni koji su dobra djela činili – sebi su zaradili...');
INSERT INTO "bs_darassalam" VALUES(30,45,'30:45','da On iz obilja Svog nagradi one koji su vjerovali i dobra djela činili. – A On, zaista, ne voli nevjernike.');
INSERT INTO "bs_darassalam" VALUES(30,46,'30:46','Jedan od dokaza Njegovih je to što On šalje vjetrove kao nosioce radosnih vijesti da vam dadne da milost Njegovu osjetite i da lađe voljom Njegovom plove i da iz obilja Njegova stječete i da biste zahvalni bili.');
INSERT INTO "bs_darassalam" VALUES(30,47,'30:47','I prije tebe smo poslanike narodima njihovim slali i oni su im prave dokaze donosili, pa smo one koji su griješili kažnjavali – a dužnost Nam je bila vjernike pomoći.');
INSERT INTO "bs_darassalam" VALUES(30,48,'30:48','Allah je Taj Koji vjetrove šalje, pa oni oblake tjeraju i On ih po nebu, kako On hoće, rasprostire i na komade dijeli, pa ti vidiš kišu kako iz njih pada, i kad je On na robove Svoje na koje želi prolije, oni se odjednom radošću ispune...');
INSERT INTO "bs_darassalam" VALUES(30,49,'30:49','iako su bili očajni prije nego što se spustila na njih.');
INSERT INTO "bs_darassalam" VALUES(30,50,'30:50','Zato pogledaj tragove Allahove milosti – kako On oživi zemlju nakon mrtvila njezina! On će, uistinu, i mrtve oživiti, On sve može.');
INSERT INTO "bs_darassalam" VALUES(30,51,'30:51','A da pošaljemo vjetar i da oni vide da je sve požutjelo, oni bi, i poslije toga, blagodati poricali.');
INSERT INTO "bs_darassalam" VALUES(30,52,'30:52','Ti ne možeš mrtve dozvati, ni gluhe, kad se od tebe okrenu, dovikati...');
INSERT INTO "bs_darassalam" VALUES(30,53,'30:53','niti možeš slijepe od zablude njihove odvratiti. Možeš jedino dozvati one koji u ajete Naše vjeruju, jedino oni će se odazvati.');
INSERT INTO "bs_darassalam" VALUES(30,54,'30:54','Allah je Taj Koji vas nejakim stvara i onda vam, poslije nejakosti, snagu daje, a poslije snage iznemoglost i sijede vlasi. On stvara što hoće. On sve zna i sve može.');
INSERT INTO "bs_darassalam" VALUES(30,55,'30:55','A na Dan kad nastupi Čas oživljenja, zločinci će se zaklinjati da su u grobovima samo jedan čas ostali, a i prije su istinu izbjegavali.');
INSERT INTO "bs_darassalam" VALUES(30,56,'30:56','A reći će oni kojima je dato znanje i vjerovanje: “Vi ste ostali, prema Allahovoj odredbi, sve do Dana oživljenja – a ovo je Dan oživljenja, samo što vi niste znali.”');
INSERT INTO "bs_darassalam" VALUES(30,57,'30:57','Tog Dana neće ničem poslužiti pravdanja onima koji su se prema sebi ogriješili i neće se od njih tražiti da se Allahu umile.');
INSERT INTO "bs_darassalam" VALUES(30,58,'30:58','U ovom Kur’anu Mi navodimo ljudima svakovrsne primjere. A kad bi im ti ne znam kakvo čudo donio, opet bi rekli oni koji neće da vjeruju: “Vi samo iznosite laži!”');
INSERT INTO "bs_darassalam" VALUES(30,59,'30:59','Eto tako Allah pečati srca onih koji neće da znaju.');
INSERT INTO "bs_darassalam" VALUES(30,60,'30:60','A ti budi strpljiv! Allahovo obećanje je, zaista, istina i neka te nikako ne obmanu oni koji nemaju čvrsto ubjeđenje.');
INSERT INTO "bs_darassalam" VALUES(31,1,'31:1','Elif Lām Mīm.');
INSERT INTO "bs_darassalam" VALUES(31,2,'31:2','Ovo su ajeti mudre Knjige');
INSERT INTO "bs_darassalam" VALUES(31,3,'31:3','upute i milosti onima koji budu dobro činili');
INSERT INTO "bs_darassalam" VALUES(31,4,'31:4','onima koji molitvu budu obavljali i zekat davali i koji u onaj svijet budu čvrsto vjerovali...');
INSERT INTO "bs_darassalam" VALUES(31,5,'31:5','njima je Gospodar njihov na Pravi put ukazao i oni su želje svoje ostvarili.');
INSERT INTO "bs_darassalam" VALUES(31,6,'31:6','Ima ljudi koji kupuju sredstva za razonodu, da bi, ne znajući koliki je to grijeh, s Allahovog puta odvodili i da bi ga predmetom za ismijavanje uzimali. Njih čeka sramna kazna.');
INSERT INTO "bs_darassalam" VALUES(31,7,'31:7','Kad se nekom od njih ajeti Naši kazuju, on oholo glavu okreće, kao da ih nije ni čuo, kao da je gluh – zato mu navijesti patnju nesnosnu.');
INSERT INTO "bs_darassalam" VALUES(31,8,'31:8','One koji budu vjerovali i dobra djela činili doista čekaju bašče uživanja...');
INSERT INTO "bs_darassalam" VALUES(31,9,'31:9','u njima će vječno boraviti – obećanje je Allahovo istinito, a On je silan i mudar.');
INSERT INTO "bs_darassalam" VALUES(31,10,'31:10','Nebesa je, vidite ih, bez stupova stvorio, a po Zemlji planine nepomične razbacao da vas ne trese i po njoj životinje svih vrsta razasuo. Mi s neba kišu spuštamo i činimo da po njoj niču svakovrsne plemenite biljke.');
INSERT INTO "bs_darassalam" VALUES(31,11,'31:11','To je Allahovo djelo, a pokažite Mi šta su drugi, mimo Njega, stvorili? Ništa! Mnogobošci su u pravoj zabludi.');
INSERT INTO "bs_darassalam" VALUES(31,12,'31:12','A Mi smo Lukmanu mudrost darovali: “Budi zahvalan Allahu! Ko je zahvalan, čini to u svoju korist, a ko je nezahvalan – pa Allah je, zaista, neovisan i hvale dostojan.”');
INSERT INTO "bs_darassalam" VALUES(31,13,'31:13','Kad Lukman reče sinu svom, savjetujući ga: “O sinko moj, ne smatraj druge Allahu ravnim, mnogoboštvo je, zaista, velika nepravda.”');
INSERT INTO "bs_darassalam" VALUES(31,14,'31:14','Mi smo naredili čovjeku da bude poslušan roditeljima svojim. Majka ga nosi, a njeno zdravlje trpi, i odbija ga u toku dvije godine. Budi zahvalan Meni i roditeljima svojim, Meni će se svi vratiti.');
INSERT INTO "bs_darassalam" VALUES(31,15,'31:15','A ako te budu nagovarali da drugog Meni ravnim smatraš, onog o kome ništa ne znaš, ti ih ne slušaj i prema njima se, na ovom svijetu, velikodušno ponašaj, a slijedi put onog koji se iskreno Meni obraća. Meni ćete se poslije vratiti i Ja ću vas o onom što ste radili obavijestiti.');
INSERT INTO "bs_darassalam" VALUES(31,16,'31:16','“O sinko moj, dobro ili zlo, teško koliko zrno gorušice, bilo u stijeni ili na nebesima ili u zemlji, Allah će na vidjelo iznijeti, jer Allah Preblagi, On je Sveznajući.');
INSERT INTO "bs_darassalam" VALUES(31,17,'31:17','O sinko moj, obavljaj molitvu i traži da se čine dobra djela, a odvraćaj od hrđavih i strpljivo podnosi ono što te zadesi – dužnost je tako postupiti.');
INSERT INTO "bs_darassalam" VALUES(31,18,'31:18','I, iz oholosti, ne okreći od ljudi lice svoje i ne idi zemljom nadmeno, jer Allah ne voli ni gordog ni hvalisavog.');
INSERT INTO "bs_darassalam" VALUES(31,19,'31:19','U hodu budi odmjeren, a u govoru ne budi grlat – ta najneprijatniji glas je revanje magarca!”');
INSERT INTO "bs_darassalam" VALUES(31,20,'31:20','Kako ne vidite da vam je Allah omogućio da se koristite svim onim što postoji na nebesima i na Zemlji i da vas darežljivo obasipa milošću Svojom, i vidljivom i nevidljivom? A ima ljudi koji raspravljaju o Allahu bez ikakva znanja, bez ikakve upute i bez knjige svjetilje.');
INSERT INTO "bs_darassalam" VALUES(31,21,'31:21','A kad im se govori: “Slijedite ono što vam Allah objavljuje!”, odgovaraju: “Ne, mi slijedimo ono što smo zapamtili od predaka naših.” Zar i onda kad ih šejtan poziva na patnju u Ognju!?');
INSERT INTO "bs_darassalam" VALUES(31,22,'31:22','Onaj ko se sasvim preda Allahu, a, usto, čini dobra djela, uhvatio se za najčvršću vezu. Allahu se na koncu sve vraća.');
INSERT INTO "bs_darassalam" VALUES(31,23,'31:23','A onaj koji ne vjeruje, pa neka te ne zabrinjava nevjerovanje njegovo. Nama će se svi vratiti i Mi ćemo ih o onom šta su radili obavijestiti. – Allahu su, uistinu, poznate svačije misli.');
INSERT INTO "bs_darassalam" VALUES(31,24,'31:24','Mi im dajemo da kratko uživaju, a onda ćemo ih natjerati u patnju neizdržljivu.');
INSERT INTO "bs_darassalam" VALUES(31,25,'31:25','A da ih upitaš: “Ko je stvorio nebesa i Zemlju?”, sigurno bi rekli: “Allah!” A ti reci: “Hvaljen neka je Allah!”, samo što većina njih ne zna.');
INSERT INTO "bs_darassalam" VALUES(31,26,'31:26','Allahovo je sve na nebesima i na Zemlji! Allah je, uistinu, nezavisan i hvale dostojan.');
INSERT INTO "bs_darassalam" VALUES(31,27,'31:27','Da su sva stabla na Zemlji pisaljke, a da se u more, kad presahne, ulije još sedam mora, ne bi se ispisale Allahove riječi. – Allah je, uistinu, silan i mudar.');
INSERT INTO "bs_darassalam" VALUES(31,28,'31:28','Stvoriti sve vas i sve vas oživiti isto je kao stvoriti i oživiti jednog čovjeka. – Allah, zaista, sve čuje i sve vidi.');
INSERT INTO "bs_darassalam" VALUES(31,29,'31:29','Kako ne vidiš da Allah uvodi noć u dan i uvodi dan u noć, i da je potčinio Sunce i Mjesec – svako se kreće do roka određenog – i da Allah dobro zna ono što radite?');
INSERT INTO "bs_darassalam" VALUES(31,30,'31:30','To zato što je Allah – istina, a oni kojima se, pored Njega, oni mole – neistina, i što je Allah uzvišen i velik.');
INSERT INTO "bs_darassalam" VALUES(31,31,'31:31','Zar ne vidiš da lađe Allahovom milošću morem plove da bi vam pokazao neke dokaze Svoje? To su, zaista, pouke za sve strpljive i zahvalne.');
INSERT INTO "bs_darassalam" VALUES(31,32,'31:32','A kad ih talas, kao oblak, prekrije, mole se Allahu iskreno Mu vjeru ispovijedajući; a čim ih On do kopna dovede, samo Mu neki djelimično zahvalni ostaju. A dokaze Naše samo izdajnik, nezahvalnik poriče.');
INSERT INTO "bs_darassalam" VALUES(31,33,'31:33','O ljudi, bojte se Gospodara svog i strahujte od Dana kad roditelj djetetu svom neće moći nimalo pomoći, niti će dijete moći svom roditelju imalo pomoći! Allahova prijetnja je istinita, pa neka vas nikako život na ovom svijetu ne zavara i neka vas u Allaha šejtan ne pokoleba.');
INSERT INTO "bs_darassalam" VALUES(31,34,'31:34','Samo Allah zna kad će Smak svijeta nastupiti, samo On spušta kišu i samo On zna šta je u maternicama, a čovjek ne zna šta će sutra zaraditi i ne zna čovjek u kojoj će zemlji umrijeti. – Allah, uistinu, sve zna i o svemu je obaviješten.');
INSERT INTO "bs_darassalam" VALUES(32,1,'32:1','Elif Lām Mīm.');
INSERT INTO "bs_darassalam" VALUES(32,2,'32:2','Knjigu objavljuje, u to nema sumnje, Gospodar svjetova...');
INSERT INTO "bs_darassalam" VALUES(32,3,'32:3','a oni govore: “On je izmišlja!” Ne, ona je istina od Gospodara tvog da opominješ narod kojem prije tebe nije došao niko da ga opominje, da bi išao Pravim putem.');
INSERT INTO "bs_darassalam" VALUES(32,4,'32:4','Allah je nebesa i Zemlju i ono što je između njih u šest vremenskih razdoblja (dana) stvorio, a onda se iznad Arša uzvisio. Vi, osim Njega, ni zaštitnika ni posrednika nemate, pa zašto se ne urazumite?');
INSERT INTO "bs_darassalam" VALUES(32,5,'32:5','On upravlja svima, od neba do Zemlje, a onda se sve to Njemu vraća u danu koji, prema vašem računanju vremena, hiljadu godina traje.');
INSERT INTO "bs_darassalam" VALUES(32,6,'32:6','To je Onaj Koji zna i nevidljivi i vidljivi svijet, Silni i Milostivi...');
INSERT INTO "bs_darassalam" VALUES(32,7,'32:7','Koji sve savršeno stvara, Koji je prvog čovjeka stvorio od ilovače...');
INSERT INTO "bs_darassalam" VALUES(32,8,'32:8','a potomstvo njegovo stvara od kapi hude tekućine...');
INSERT INTO "bs_darassalam" VALUES(32,9,'32:9','zatim mu savršeno udove uobliči i život mu udahne – i On vam i sluh i vid i pamet daje – a kako vi malo zahvaljujete!');
INSERT INTO "bs_darassalam" VALUES(32,10,'32:10','Oni govore: “Zar ćemo, kad nestanemo pod zemljom, ponovo stvoreni biti?” Oni ne vjeruju da će pred Gospodara svog izići.');
INSERT INTO "bs_darassalam" VALUES(32,11,'32:11','Reci: “Melek smrti, koji vam je za to određen, duše će vam uzeti, a poslije ćete se Gospodaru svom vratiti.”');
INSERT INTO "bs_darassalam" VALUES(32,12,'32:12','A da ti je vidjeti griješnike kako će, oborenih glava pred Gospodarom svojim, reći: “Gospodaru naš, vidjeli smo i čuli smo, pa nas povrati da dobra djela činimo, mi doista čvrsto vjerujemo!”');
INSERT INTO "bs_darassalam" VALUES(32,13,'32:13','A kad bismo htjeli, svakog čovjeka bismo na Pravi put uputili, ali Ja sam već istinu rekao: “Napunit ću, zaista, džehennem džinima i ljudima zajedno!”');
INSERT INTO "bs_darassalam" VALUES(32,14,'32:14','Pa trpite zato što ste zaboravljali da ćete ovaj Dan doživjeti – i Mi ćemo vas zaboraviti – i vječnu patnju trpite zbog onog što ste radili.');
INSERT INTO "bs_darassalam" VALUES(32,15,'32:15','U Naše riječi vjeruju samo oni koji, kad se njima opomenu, licem na tlo padaju i koji Gospodara svog veličaju i hvale i koji se ne ohole.');
INSERT INTO "bs_darassalam" VALUES(32,16,'32:16','Bokovi se njihovi postelja lišavaju i oni se Gospodaru svom iz straha i želje klanjaju, a dio onog što im Mi dajemo udjeljuju.');
INSERT INTO "bs_darassalam" VALUES(32,17,'32:17','I niko ne zna kakve ih, kao nagrada za ono što su činili, skrivene radosti čekaju.');
INSERT INTO "bs_darassalam" VALUES(32,18,'32:18','Zar da vjerniku bude isto kao griješniku? Ne, njima neće biti isto...');
INSERT INTO "bs_darassalam" VALUES(32,19,'32:19','one koji su vjerovali i dobra djela činili čekaju džennetske bašče u kojima će boraviti, kao nagrada za ono što su radili...');
INSERT INTO "bs_darassalam" VALUES(32,20,'32:20','a one koji nisu vjerovali čeka vatra u kojoj će prebivati – kad god pokušaju iz nje izići, bit će u nju vraćeni i bit će im rečeno: “Trpite kaznu u vatri koju ste poricali.”');
INSERT INTO "bs_darassalam" VALUES(32,21,'32:21','I Mi ćemo učiniti da blažu kaznu iskuse, prije one najveće, ne bi li se pokajali.');
INSERT INTO "bs_darassalam" VALUES(32,22,'32:22','A ima li nepravednijeg od onog koji, opomenut riječima Gospodara svog, njima leđa okrene? Mi ćemo se, zaista, osvetiti zlikovcima!');
INSERT INTO "bs_darassalam" VALUES(32,23,'32:23','Musau smo Mi Knjigu dali – ne sumnjaj nimalo u susret s njime; i putokazom je sinovima Israilovim učinili.');
INSERT INTO "bs_darassalam" VALUES(32,24,'32:24','Između njih smo Mi vođe određivali i oni su, odazivajući se zapovijedi Našoj, na Pravi put upućivali, jer su strpljivi bili i u dokaze Naše čvrsto vjerovali.');
INSERT INTO "bs_darassalam" VALUES(32,25,'32:25','Gospodar tvoj će među njima na Sudnjem danu presuditi u onom oko čega su se razilazili.');
INSERT INTO "bs_darassalam" VALUES(32,26,'32:26','Zar ovima nije jasno koliko smo Mi prije njih naroda uništili, po čijim oni nastambama hodaju? To su zaista dokazi, pa zašto neće da čuju?');
INSERT INTO "bs_darassalam" VALUES(32,27,'32:27','Kako oni ne vide da Mi gonimo kišu u ogoljelu zemlju i činimo da, uz pomoć njenu, niče rastinje kojim se hrani stoka njihova, a i oni sami – pa zašto neće da vide!?');
INSERT INTO "bs_darassalam" VALUES(32,28,'32:28','I oni govore: “Kad će već jednom ta presuda, ako istinu govorite?”');
INSERT INTO "bs_darassalam" VALUES(32,29,'32:29','Reci: “Na Sudnjem danu, kad nevjernicima neće nikako koristiti to što će tad vjerovati i kad im se nimalo vremena neće dati.”');
INSERT INTO "bs_darassalam" VALUES(32,30,'32:30','Zato se ti okreni od njih i čekaj, i oni doista čekaju!');
INSERT INTO "bs_darassalam" VALUES(33,1,'33:1','O Vjerovjesniče, Allaha se boj, a nevjernike i dvoličnjake ne slušaj – Allah, uistinu, sve zna i mudar je...');
INSERT INTO "bs_darassalam" VALUES(33,2,'33:2','i slijedi ono što ti Gospodar tvoj objavljuje – Allah dobro zna ono što vi radite...');
INSERT INTO "bs_darassalam" VALUES(33,3,'33:3','i u Allaha se pouzdaj, Allah je zaštitnik dovoljan!');
INSERT INTO "bs_darassalam" VALUES(33,4,'33:4','Allah nijednom čovjeku dva srca u njedrima njegovim nije dao, a ni žene vaše, od kojih se ziharom rastavljate, materama vašim nije učinio, niti je posinke vaše sinovima vašim učinio. To su samo vaše riječi, iz usta vaših, a Allah istinu govori i na Pravi put izvodi.');
INSERT INTO "bs_darassalam" VALUES(33,5,'33:5','Zovite ih po očevima njihovim, to je kod Allaha ispravnije. A ako ne znate imena očeva njihovih, pa braća su vaša po vjeri i štićenici su vaši. Nije grijeh ako u tome pogriješite, grijeh je ako to namjerno učinite. – A Allah prašta i samilostan je.');
INSERT INTO "bs_darassalam" VALUES(33,6,'33:6','Vjerovjesnik treba biti preči vjernicima nego oni sami sebi, a žene njegove su kao majke njihove. A srodnici, po Allahovoj knjizi, preči su jedni drugima od ostalih vjernika i muhadžira. Prijateljima svojim možete oporukom nešto ostaviti. To u Knjizi piše.');
INSERT INTO "bs_darassalam" VALUES(33,7,'33:7','Mi smo od vjerovjesnikā zavjet njihov uzeli, i od tebe, i od Nuha, i od Ibrahima, i od Musaa, i od Isaa, sina Merjemina, čvrst smo zavjet uzeli...');
INSERT INTO "bs_darassalam" VALUES(33,8,'33:8','da bi On mogao pozvati na odgovornost vjerovjesnike za ono što su govorili. A nevjernicima je On pripremio bolnu patnju.');
INSERT INTO "bs_darassalam" VALUES(33,9,'33:9','O vjernici, sjetite se Allahove milosti prema vama kad su do vas vojske došle, pa smo Mi protiv njih vjetar poslali, a i vojske koje vi niste vidjeli – a Allah dobro vidi šta vi radite...');
INSERT INTO "bs_darassalam" VALUES(33,10,'33:10','kad su vam došle i odozgo i odozdo, a duša došla do grkljana, i kad ste o Allahu svašta pomišljali...');
INSERT INTO "bs_darassalam" VALUES(33,11,'33:11','tad su vjernici bili u iskušenje stavljeni i ne može biti gore uznemireni...');
INSERT INTO "bs_darassalam" VALUES(33,12,'33:12','kad su dvoličnjaci i oni bolesna srca govorili: “Allah i Poslanik Njegov su nas samo obmanjivali kad su nam obećavali!”...');
INSERT INTO "bs_darassalam" VALUES(33,13,'33:13','kad su neki među njima rekli: “O stanovnici Jesriba, ovdje vam nema stanka, zato se vratite!”, a drugi među njima su tražili dopuštenje od Vjerovjesnika i govorili: “Kuće su naše nezaštićene!”, a nisu bile nezaštićene, već su se oni htjeli izvući.');
INSERT INTO "bs_darassalam" VALUES(33,14,'33:14','A da su im s raznih strana prodrli i da su potom od njih zatražili da opet postanu mnogobošci, oni bi to, ne dvoumeći se oko toga, brzo učinili...');
INSERT INTO "bs_darassalam" VALUES(33,15,'33:15','a bili su se još prije Allahu obavezali da neće uzmicati – a za Allahu datu obavezu će se odgovarati!');
INSERT INTO "bs_darassalam" VALUES(33,16,'33:16','Reci: “Ako bježite od smrti ili pogibije, bježanje vam neće pomoći, opet ćete samo kratko uživati.”');
INSERT INTO "bs_darassalam" VALUES(33,17,'33:17','Reci: “Ko će vas od Allaha zaštititi ako On hoće da vas zlo snađe ili ko vam može nauditi ako vam On želi milost ukazati?” Osim Allaha, oni neće naći sebi ni zaštitnika ni pomagača.');
INSERT INTO "bs_darassalam" VALUES(33,18,'33:18','Allah dobro zna one među vama koji su druge zadržavali i prijateljima svojim govorili: “Priključite se nama!”, a samo su rijetko u boj išli...');
INSERT INTO "bs_darassalam" VALUES(33,19,'33:19','ne želeći da vam pomognu. A kad zavlada strah, vidiš ih kako gledaju u tebe kolutajući svojim očima kao pred smrt onesviješćeni; čim strah mine, oni vas psuju svojim oštrim jezicima, škrti da učine bilo kakvo dobro. Oni ne vjeruju, i zato će Allah djela njihova poništiti – a to je Allahu lahko.');
INSERT INTO "bs_darassalam" VALUES(33,20,'33:20','Oni misle da saveznici još nisu otišli. A kad bi saveznici opet došli, najdraže bi im bilo da su među beduinima u pustinji i da se raspituju za vas; a da s vama ostanu, malo bi se borili.');
INSERT INTO "bs_darassalam" VALUES(33,21,'33:21','Vi u Allahovom Poslaniku imate divan uzor za onog koji se nada Allahovoj milosti i nagradi na onom svijetu i koji često Allaha spominje.');
INSERT INTO "bs_darassalam" VALUES(33,22,'33:22','A kad su vjernici saveznike ugledali, rekli su: “Ovo je ono što su nam Allah i Poslanik Njegov obećali, i Allah i Poslanik Njegov su istinu govorili!”, i to im je samo učvrstilo vjerovanje i predanost.');
INSERT INTO "bs_darassalam" VALUES(33,23,'33:23','Ima vjernika koji ispunjavaju zavjet dat Allahu, ima ih koji su poginuli i ima ih koji to očekuju – nisu ništa izmijenili...');
INSERT INTO "bs_darassalam" VALUES(33,24,'33:24','da Allah nagradi iskrene za njihovu iskrenost, a da, ako hoće, stavi na muke dvoličnjake ili da im oprosti. – Allah zaista prašta i samilostan je.');
INSERT INTO "bs_darassalam" VALUES(33,25,'33:25','Allah je nevjernike pune srdžbe odbio, nisu nimalo uspjeli, i vjernike je Allah borbe poštedio – Allah je, uistinu, moćan i silan...');
INSERT INTO "bs_darassalam" VALUES(33,26,'33:26','a sljedbenike Knjige, koji su ih pomagali, iz utvrda njihovih je izveo i strah u srca njihova ulio, pa ste jedne pobili, a druge kao sužnje uzeli...');
INSERT INTO "bs_darassalam" VALUES(33,27,'33:27','i dao vam je da naslijedite zemlje njihove i domove njihove i bogatstva njihova, i zemlju kojom prije niste hodali. – Allah sve može.');
INSERT INTO "bs_darassalam" VALUES(33,28,'33:28','O Vjerovjesniče, reci ženama svojim: “Ako želite život na ovom svijetu i njegov sjaj, onda se odlučite, dat ću vam pristojnu otpremu i lijepo ću vas otpustiti.');
INSERT INTO "bs_darassalam" VALUES(33,29,'33:29','A ako želite Allaha, i Poslanika Njegovog, i onaj svijet – pa Allah je, doista, onima među vama koji čine dobra djela pripremio nagradu veliku.”');
INSERT INTO "bs_darassalam" VALUES(33,30,'33:30','O žene Vjerovjesnikove, ako bi koja od vas očit grijeh učinila, kazna bi joj udvostručena bila, a to je Allahu lahko.');
INSERT INTO "bs_darassalam" VALUES(33,31,'33:31','A onoj koja se bude Allahu i Poslaniku Njegovu pokoravala i dobra djela činila – dat ćemo nagradu dvostruku i pripremit ćemo joj opskrbu plemenitu.');
INSERT INTO "bs_darassalam" VALUES(33,32,'33:32','O žene Vjerovjesnikove, vi niste kao druge žene! Ako se Allaha bojite, na sebe pažnju govorom ne skrećite, pa da u napast dođe onaj čije je srce bolesno, i neusiljeno govorite!');
INSERT INTO "bs_darassalam" VALUES(33,33,'33:33','U kućama svojim boravite i ljepotu svoju, kao u davno pagansko doba, ne pokazujte, i molitvu obavljajte i zekat dajte, i Allaha i Poslanika Njegova slušajte! Allah želi da od vas, o porodico Poslanikova, grijehe odstrani i da vas potpuno očisti.');
INSERT INTO "bs_darassalam" VALUES(33,34,'33:34','I pamtite Allahove ajete i mudrost, koja se kazuje u domovima vašim. – Allah je, uistinu, dobar i sve zna.');
INSERT INTO "bs_darassalam" VALUES(33,35,'33:35','Muslimanima i muslimankama, i vjernicima i vjernicama, i poslušnim muškarcima i poslušnim ženama, i iskrenim muškarcima i iskrenim ženama, i strpljivim muškarcima i strpljivim ženama, i poniznim muškarcima i poniznim ženama, i muškarcima koji dijele zekat i ženama koje dijele zekat i muškarcima koji poste i ženama koje poste, i muškarcima koji o svojim stidnim mjestima vode brigu i ženama koje o svojim stidnim mjestima vode brigu, i muškarcima koji često spominju Allaha i ženama koje često spominju Allaha – Allah je, doista, za sve njih oprost i veliku nagradu pripremio.');
INSERT INTO "bs_darassalam" VALUES(33,36,'33:36','Kad Allah i Poslanik Njegov nešto odrede, onda ni vjernik ni vjernica nemaju pravo po svom nahođenju postupiti. A ko Allaha i Njegovog Poslanika ne posluša, taj je sigurno skrenuo s Pravog puta.');
INSERT INTO "bs_darassalam" VALUES(33,37,'33:37','A kad ti reče onom kome je Allah milost darovao, a kome si i ti dobro učinio: “Zadrži ženu svoju i boj se Allaha!”, u sebi si skrivao ono što će Allah objelodaniti i ljudi si se bojao, a preče je da se Allaha bojiš. I pošto je Zejd s njome živio i od nje se razveo, Mi smo je za tebe udali kako se vjernici više ne bi ustručavali ženiti se ženama posinaka svojih kad se oni od njih razvedu – kako Allah odredi, onako treba biti.');
INSERT INTO "bs_darassalam" VALUES(33,38,'33:38','Vjerovjesniku nije grijeh učiniti ono što mu Allah naloži, jer takav je bio Allahov propis i za one koji su prije bili i nestali – a Allahova zapovijed je odredba konačna...');
INSERT INTO "bs_darassalam" VALUES(33,39,'33:39','za one koji su Allahove poslanice dostavljali i od Njega strahovali, i koji se nikog, osim Allaha, nisu bojali. A dovoljno je to što će se pred Allahom račun polagati!');
INSERT INTO "bs_darassalam" VALUES(33,40,'33:40','Muhammed nije roditelj nijednom od vaših ljudi, nego je Allahov Poslanik i posljednji Vjerovjesnik. – A Allah sve dobro zna.');
INSERT INTO "bs_darassalam" VALUES(33,41,'33:41','O vjernici, često Allaha spominjite i hvalite...');
INSERT INTO "bs_darassalam" VALUES(33,42,'33:42','i ujutro i navečer Ga veličajte...');
INSERT INTO "bs_darassalam" VALUES(33,43,'33:43','On vas blagosilja, a i meleki Njegovi, da bi vas iz tmina na svjetlo izveo – On je prema vjernicima samilostan...');
INSERT INTO "bs_darassalam" VALUES(33,44,'33:44','a na Dan kad oni pred Njega stanu, On će ih pozdravljati sa: “Mir vama!”, i On im je pripremio nagradu plemenitu.');
INSERT INTO "bs_darassalam" VALUES(33,45,'33:45','O Vjerovjesniče, Mi smo te poslali kao svjedoka i kao donosioca radosnih vijesti i kao poslanika koji opominje...');
INSERT INTO "bs_darassalam" VALUES(33,46,'33:46','da – po Njegovu naređenju – pozivaš k Allahu, i kao svjetiljku koja sija.');
INSERT INTO "bs_darassalam" VALUES(33,47,'33:47','I obraduj vjernike time da će u Allaha veliku blagodat imati...');
INSERT INTO "bs_darassalam" VALUES(33,48,'33:48','a ne slušaj nevjernike i dvoličnjake, i na uvrede njihove pažnju ne obraćaj i u Allaha se pouzdaj, Allah je dovoljan kao zaštitnik.');
INSERT INTO "bs_darassalam" VALUES(33,49,'33:49','O vjernici, kad se vjernicama oženite, a onda ih, prije stupanja u bračni odnos, pustite, one nisu dužne čekati određeno vrijeme koje ćete vi brojati, već ih darujte i lijepo ih otpremite.');
INSERT INTO "bs_darassalam" VALUES(33,50,'33:50','O Vjerovjesniče, Mi smo ti dozvolili da budu žene tvoje one kojima si dao vjenčane darove njihove, i one u vlasti tvojoj koje ti je Allah dao iz plijena, i kćeri amidža tvojih, i kćeri tetaka tvojih po ocu, i kćeri daidža tvojih, i kćeri tetaka tvojih po materi koje su se s tobom iselile, i samo tebi, a ne ostalim vjernicima – ženu vjernicu koja sebe pokloni Vjerovjesniku, ako se Vjerovjesnik hoće njome oženiti – da ti ne bi bilo teško. Mi znamo šta smo propisali vjernicima kad je riječ o ženama njihovim i o onima koje su u vlasništvu njihovu. – A Allah prašta i samilostan je.');
INSERT INTO "bs_darassalam" VALUES(33,51,'33:51','Možeš zanemariti one među njima koje hoćeš i možeš pozvati sebi onu koju hoćeš, a možeš zatražiti onu koju si udaljio, nijedno od toga nije tvoj grijeh. Najlakše će tako one radosne biti i neće se žalostiti i tako će sve onim što im ti daješ zadovoljne biti – a Allah zna šta je u srcima vašim. Allah zna sve i blag je.');
INSERT INTO "bs_darassalam" VALUES(33,52,'33:52','Odsad ti nisu dopuštene druge žene, ni da umjesto njih neku drugu uzmeš, makar te i zadivila ljepota njihova, osim onih koje postanu robinje tvoje. – A Allah na sve motri.');
INSERT INTO "bs_darassalam" VALUES(33,53,'33:53','O vjernici, ne ulazite u sobe Vjerovjesnikove, osim ako vam se dopusti radi jela, ali ne da čekate da se ono zgotovi. Tek kad budete pozvani onda uđite i, pošto jedete, raziđite se ne slušajući razgovor. To smeta Vjerovjesniku, a on se stidi da vam to rekne, a Allah se ne stidi istine. A ako od njih nešto tražite, tražite to od njih iza zastora. To je čistije i za vaša i za njihova srca. Vama nije dopušteno da Allahovog Poslanika uznemirujete niti da se ženama njegovim poslije smrti njegove ikad oženite. To bi, uistinu, kod Allaha, bio velik grijeh!');
INSERT INTO "bs_darassalam" VALUES(33,54,'33:54','Iznosili vi o tome šta u javnost ili to u sebi krili – pa Allah sve zna.');
INSERT INTO "bs_darassalam" VALUES(33,55,'33:55','Njima nije grijeh da budu otkrivene pred očevima svojim, i sinovima svojim, i braćom svojom, i sinovima braće svoje, i sinovima sestara svojih, i ženama vjernicama, i pred onima koje su u vlasništvu njihovu. I Allaha se bojte, jer Allah je, doista, svjedok svakoj stvari.');
INSERT INTO "bs_darassalam" VALUES(33,56,'33:56','Allah i meleki Njegovi blagosiljaju Vjerovjesnika. O vjernici, blagosiljajte ga i vi i šaljite mu pozdrav srdačni!');
INSERT INTO "bs_darassalam" VALUES(33,57,'33:57','One koji Allaha i Poslanika Njegova budu vrijeđali Allah će i na ovom i na onom svijetu prokleti, i sramnu im patnju pripremiti.');
INSERT INTO "bs_darassalam" VALUES(33,58,'33:58','A oni koji vjernike i vjernice vrijeđaju, a oni to ne zaslužuju, tovare na sebe klevetu i pravi grijeh.');
INSERT INTO "bs_darassalam" VALUES(33,59,'33:59','O Vjerovjesniče, reci ženama svojim, i kćerima svojim, i ženama vjernika neka spuste haljine svoje niza se. Tako će se najlakše prepoznati pa neće napastvovane biti. A Allah prašta i samilostan je.');
INSERT INTO "bs_darassalam" VALUES(33,60,'33:60','Ako se dvoličnjaci i oni čija su srca bolesna i oni koji po Medini šire laži – ne okane toga, Mi ćemo ti vlast nad njima prepustiti i oni će samo kratko vrijeme kao susjedi tvoji u njoj ostati...');
INSERT INTO "bs_darassalam" VALUES(33,61,'33:61','prokleti su. Gdje god da se nađu, neka budu uhvaćeni i ubijeni...');
INSERT INTO "bs_darassalam" VALUES(33,62,'33:62','prema Allahovu zakonu koji je vrijedio za one koji su bili i nestali! A ti nećeš u Allahovom zakonu izmjene naći.');
INSERT INTO "bs_darassalam" VALUES(33,63,'33:63','Ljudi te pitaju o Smaku svijeta, reci: “To jedino Allah zna!” A ti ne znaš, možda je Smak svijeta blizu!');
INSERT INTO "bs_darassalam" VALUES(33,64,'33:64','Allah je nevjernike prokleo i za njih oganj razbuktali pripremio...');
INSERT INTO "bs_darassalam" VALUES(33,65,'33:65','u njemu će vječno i zauvijek boraviti, ni zaštitnika ni pomagača neće naći.');
INSERT INTO "bs_darassalam" VALUES(33,66,'33:66','Na Dan kad se njihova lica u vatri budu prevrtala, govorit će: “Kamo sreće da smo se Allahu pokoravali i da smo Poslanika slušali!”');
INSERT INTO "bs_darassalam" VALUES(33,67,'33:67','I govorit će: “Gospodaru naš, mi smo prvake naše i starješine naše slušali, pa su nas oni s Pravog puta odveli.');
INSERT INTO "bs_darassalam" VALUES(33,68,'33:68','Gospodaru naš, podaj im dvostruku patnju i prokuni ih prokletstvom velikim!”');
INSERT INTO "bs_darassalam" VALUES(33,69,'33:69','O vjernici, ne budite kao oni koji su Musaa uznemiravali, pa ga je Allah oslobodio onog što su govorili, i on kod Allaha ugled uživa.');
INSERT INTO "bs_darassalam" VALUES(33,70,'33:70','O vjernici, bojte se Allaha i govorite samo istinu...');
INSERT INTO "bs_darassalam" VALUES(33,71,'33:71','On će vas za vaša dobra djela nagraditi i grijehe vam vaše oprostiti. A onaj ko se Allahu i Poslaniku Njegovu bude pokoravao – postići će ono što bude želio.');
INSERT INTO "bs_darassalam" VALUES(33,72,'33:72','Mi smo nebesima, Zemlji i planinama ponudili emanet, pa su se sustegli i pobojali da ga ponesu, ali ga je preuzeo čovjek – a on je, zaista, prema sebi nepravedan i lahkomislen...');
INSERT INTO "bs_darassalam" VALUES(33,73,'33:73','da bi Allah licemjere i licemjerke, i mnogobošce i mnogoboškinje kaznio, a vjernicima i vjernicama oprostio. – A Allah prašta i samilostan je.');
INSERT INTO "bs_darassalam" VALUES(34,1,'34:1','Neka je hvaljen Allah, Čije je sve ono na nebesima i sve ono na Zemlji! Hvaljen neka bude i na onom svijetu! On je Mudri i Sveznajući.');
INSERT INTO "bs_darassalam" VALUES(34,2,'34:2','On zna šta u zemlju ulazi, a šta iz nje izlazi, i šta se s neba spušta, a šta se na njega uspinje; On je samilostan i On prašta grijehe.');
INSERT INTO "bs_darassalam" VALUES(34,3,'34:3','A nevjernici govore: “Čas oživljenja nam neće doći!” Reci: “Hoće, tako mi Gospodara moga, Koji zna i ono što je skriveno, zacijelo će vam doći. Njemu ne može ništa, ni trunčica jedna ni na nebesima ni na Zemlji, izmaći, i ne postoji ništa, ni manje ni veće od toga, što nije u jasnoj Knjizi.”');
INSERT INTO "bs_darassalam" VALUES(34,4,'34:4','Zato da bi dao nagradu onima koji vjeruju i dobra djela čine – njih čeka oprost i opskrba plemenita...');
INSERT INTO "bs_darassalam" VALUES(34,5,'34:5','a da kazni najbolnijom patnjom one koji se protiv dokaza Naših bore, nastojeći ih onemogućiti.');
INSERT INTO "bs_darassalam" VALUES(34,6,'34:6','Oni kojima je dato znanje dobro znaju da je ono što ti se objavljuje od Gospodara tvoga istina i da vodi na put Silnoga i Hvaljenoga...');
INSERT INTO "bs_darassalam" VALUES(34,7,'34:7','dok oni koji ne vjeruju govore: “Hoćete li da vam pokažemo čovjeka koji vam predskazuje da ćete, kad se sasvim raspadnete, zaista ponovo stvoreni biti?');
INSERT INTO "bs_darassalam" VALUES(34,8,'34:8','Iznosi li on o Allahu laži? Ne, on je lud!” Ne, nego će oni koji u onaj svijet neće da vjeruju na muci biti i u zabludi su velikoj.');
INSERT INTO "bs_darassalam" VALUES(34,9,'34:9','Kako ne vide nebo i Zemlju, ono što je iznad njih i ono što je ispod njih!? Kad bismo htjeli, u zemlju bismo ih utjerali, ili komade neba na njih sručili. To je, zaista, pouka svakom robu koji je odan.');
INSERT INTO "bs_darassalam" VALUES(34,10,'34:10','Mi smo Davudu Našu milost ukazali: “O brda, ponavljajte zajedno s njime hvalu, i vi ptice!”, i učinili da mu mekahno gvožđe bude.');
INSERT INTO "bs_darassalam" VALUES(34,11,'34:11','“Pravi široke pancire i čestito ih pleti!”, i činite dobro, jer Ja vidim šta radite vi.');
INSERT INTO "bs_darassalam" VALUES(34,12,'34:12','A Sulejmanu – vjetar, ujutro je prevaljivao rastojanje od mjesec dana, a i navečer rastojanje od mjesec dana; i učinili smo da mu iz izvora rastopljen bakar teče i da džini, voljom njegova Gospodara, pred njim rade; a kad bi neki od njih otkazao poslušnost naređenju Našem, učinili bismo da ognjenu patnju osjeti.');
INSERT INTO "bs_darassalam" VALUES(34,13,'34:13','Oni su mu izrađivali šta god je htio: hramove i spomenike, i zdjele kao čatrnje, i kotlove nepokretne. “Trudite se i budite zahvalni, o čeljadi Davudova!” A malo je zahvalnih među robovima Mojim.');
INSERT INTO "bs_darassalam" VALUES(34,14,'34:14','A kad smo odredili da umre, crv koji je bio rastočio štap njegov – upozorio ih je da je umro, i kad se on srušio, džini shvatiše da ne bi na muci sramnoj ostali da su budućnost prozreti mogli.');
INSERT INTO "bs_darassalam" VALUES(34,15,'34:15','Stanovnici Sabe imali su dokaz u mjestu u kome su živjeli: vrtove zdesna i slijeva. “Jedite hranu Gospodara svog i budite Mu zahvalni; kakav divan kraj i Gospodar Koji mnogo prašta!”');
INSERT INTO "bs_darassalam" VALUES(34,16,'34:16','Ali oni su nezahvalni postali, pa smo na njih poplavu pustili, popuštanjem brana nastalu, i zamijenili im njihove vrtove drugim vrtovima sa plodovima gorkim i tamariskom i neznatnim lotosom divljim.');
INSERT INTO "bs_darassalam" VALUES(34,17,'34:17','Kaznili smo ih tako zato što su bili nezahvalni, a kažnjavamo li Mi ikoga drugog do nevjernika, nezahvalnika!?');
INSERT INTO "bs_darassalam" VALUES(34,18,'34:18','A između njih i gradova koje smo blagoslovili izgradili smo bili naselja povezana i odredili im potrebnu udaljenost. “Putujte kroz njih i po noći i po danu, sigurni!”');
INSERT INTO "bs_darassalam" VALUES(34,19,'34:19','Ali oni rekoše: “Gospodaru naš, učini veće rastojanje prilikom putovanja naših!”, i ogriješiše se prema sebi, i Mi učinismo da se o njima samo priča, a njih potpuno satrli. To su, zaista, pouke za svakog strpljivog i zahvalnog.');
INSERT INTO "bs_darassalam" VALUES(34,20,'34:20','I Iblis se uvjerio da je o njima ispravno mislio, i oni su se poveli za njim, osim nekolicine vjernika...');
INSERT INTO "bs_darassalam" VALUES(34,21,'34:21','nad kojima nikakve vlasti nije imao. Mi smo htjeli ukazati na onog ko vjeruje u onaj svijet, a ko u njega sumnja. – A Gospodar tvoj bdi nad svim.');
INSERT INTO "bs_darassalam" VALUES(34,22,'34:22','Reci: “Zovite one koje, pored Allaha, bogovima smatrate. Oni ništa nemaju, ni koliko trun jedan, ni na nebesima ni na Zemlji; oni u njima nemaju nikakva udjela i On nema od njih nikakve pomoći.”');
INSERT INTO "bs_darassalam" VALUES(34,23,'34:23','Kod Njega će se moći zauzimati za nekog samo onaj kome On dopusti. I kad iz srca njihovih nestane straha, oni upitaju: “Šta je to rekao Gospodar vaš?” “Istinu. On je Uzvišeni i Veliki”, odgovore.');
INSERT INTO "bs_darassalam" VALUES(34,24,'34:24','Upitaj: “Ko vas opskrbljuje i iz neba i iz zemlje?”, i odgovori: “Allah! Jesmo li onda mi ili vi na Pravom putu ili u očitoj zabludi?”');
INSERT INTO "bs_darassalam" VALUES(34,25,'34:25','Reci: “Vi nećete odgovarati za grijehe koje mi počinimo niti ćemo mi odgovarati za ono što uradite vi.”');
INSERT INTO "bs_darassalam" VALUES(34,26,'34:26','Reci: “Gospodar naš će nas sabrati i onda nam pravedno presuditi. On je sudija Pravedni, Sveznajući.”');
INSERT INTO "bs_darassalam" VALUES(34,27,'34:27','Reci: “Pokažite mi one koje smatrate Njemu ravnim!” Nema ih, postoji samo Allah, Silni i Mudri!');
INSERT INTO "bs_darassalam" VALUES(34,28,'34:28','Mi smo te poslali svim ljudima da radosne vijesti donosiš i da opominješ, ali većina ljudi ne zna.');
INSERT INTO "bs_darassalam" VALUES(34,29,'34:29','I govore: “Kad će već jednom ta prijetnja, ako istinu govorite?”');
INSERT INTO "bs_darassalam" VALUES(34,30,'34:30','Reci: “Dan vam je već određen, ne možete ga ni za čas jedan zaustaviti niti ubrzati.”');
INSERT INTO "bs_darassalam" VALUES(34,31,'34:31','A oni koji ne vjeruju govore: “Mi nećemo vjerovati u ovaj Kur’an niti u one prije njega!” A kad bi samo vidio kad oni koji su učinili zlo pred Gospodarom svojim budu zadržani i kad stanu među sobom razgovarati: “Da vās nije bilo, sigurno bismo bili vjernici”, reći će oni koji su bili tlačeni onima koji su bili oholi.');
INSERT INTO "bs_darassalam" VALUES(34,32,'34:32','“A zar smo vas mi od Pravog puta odvratili nakon što vam je bilo na njega ukazano? Ne, sami ste vi griješnici bili”, reći će oni koji su bili oholi onima koji su bili tlačeni.');
INSERT INTO "bs_darassalam" VALUES(34,33,'34:33','“Nije bilo tako”, odgovorit će oni koji su bili tlačeni onima koji su bili oholi, “nego ste danju i noću spletkarili kad ste od nas tražili da u Allaha ne vjerujemo i da Mu druge jednakim smatramo.” I svi će prikriti tugu kad vide da će kažnjeni biti, a Mi ćemo na vratove nevjernika sindžire staviti. Zar će biti kažnjeni drukčije nego prema onom kako su radili?');
INSERT INTO "bs_darassalam" VALUES(34,34,'34:34','Mi ni u jedan grad nismo poslanika poslali, a da nisu rekli oni koji su na raskošan život bili navikli: “Ne vjerujemo mi u ono što je po vama poslato!”...');
INSERT INTO "bs_darassalam" VALUES(34,35,'34:35','i da nisu govorili: “Imamo više imetka i djece – mi nećemo biti mučeni!”');
INSERT INTO "bs_darassalam" VALUES(34,36,'34:36','Reci: “Gospodar moj daje obilnu opskrbu onom kome hoće, a i uskraćuje, ali većina ljudi ne zna.”');
INSERT INTO "bs_darassalam" VALUES(34,37,'34:37','Ni bogatstva vaša ni djeca vaša neće vas učiniti Nama bliskim. Samo one koji budu vjerovali i dobra djela činili čeka višestruka nagrada za ono što su radili, i oni će u visokim odajama biti sigurni.');
INSERT INTO "bs_darassalam" VALUES(34,38,'34:38','A oni koji se budu trudili da se dokazima Našim suprotstave, da ih onemoguće, oni će biti kažnjeni.');
INSERT INTO "bs_darassalam" VALUES(34,39,'34:39','Reci: “Gospodar moj daje obilnu opskrbu onom kome hoće od robova Svojih, a kome hoće uskraćuje.” Šta god vi udijelite, On će to nadoknaditi, On najbolje opskrbljuje.');
INSERT INTO "bs_darassalam" VALUES(34,40,'34:40','A na Dan kad ih sve sabere, pa meleke upita: “Zar su se ovi vama klanjali?”');
INSERT INTO "bs_darassalam" VALUES(34,41,'34:41','Oni će odgovoriti: “Hvaljen neka si, Ti si Gospodar naš, između nas i njih nije bilo prijateljstva; oni su se džinima pokoravali i većina njih je vjerovala u njih.”');
INSERT INTO "bs_darassalam" VALUES(34,42,'34:42','Tog Dana jedni drugima nećete moći ni koristiti ni nauditi, a Mi ćemo onima koji su se prema sebi ogriješili reći: “Trpite patnju u vatri koju ste poricali!”');
INSERT INTO "bs_darassalam" VALUES(34,43,'34:43','Kad im se Naši jasni ajeti kazuju, oni govore: “Ovaj čovjek vas samo hoće odvratiti od onog čemu su se vaši preci klanjali”, pa dodaju: “Ovo nije ništa drugo nego izmišljena laž!” A oni koji ne vjeruju govore o istini kad im dolazi: “Ovo nije ništa drugo nego očito vračanje!”');
INSERT INTO "bs_darassalam" VALUES(34,44,'34:44','A Mi im nismo dali nikakve knjige da ih izučavaju i Mi im, prije tebe, nismo bili poslali nekog ko bi ih opominjao.');
INSERT INTO "bs_darassalam" VALUES(34,45,'34:45','A i oni prije njih su poslanike u laž ugonili, a ovi nisu postigli ni deseti dio onog što smo onima bili dali, pa su ipak poslanike Moje lažnim smatrali, i kakva je samo bila kazna Moja!');
INSERT INTO "bs_darassalam" VALUES(34,46,'34:46','Reci: “Ja vam savjetujem samo jedno: ustanite iskreno prema Allahu, dvojica po dvojica, ili jedan po jedan, pa zatim razmislite da drug vaš nije lud. On vas samo prije teške patnje opominje.”');
INSERT INTO "bs_darassalam" VALUES(34,47,'34:47','Reci: “Kakvu god nagradu da zatražim od vas, neka ostane vama, mene će nagraditi Allah, On nad svim bdi.”');
INSERT INTO "bs_darassalam" VALUES(34,48,'34:48','Reci: “Gospodar moj pobjeđuje istinom, On dobro zna i sve ono što je veoma skriveno.”');
INSERT INTO "bs_darassalam" VALUES(34,49,'34:49','Reci: “Došla je istina, a laži je nepovratno nestalo!”');
INSERT INTO "bs_darassalam" VALUES(34,50,'34:50','Reci: “Ako zalutam, zalutao sam na svoju štetu. A ako sam na Pravom putu, to je zbog onog što mi Gospodar moj objavljuje. – On zaista sve čuje i blizu je.”');
INSERT INTO "bs_darassalam" VALUES(34,51,'34:51','A da ti je vidjeti kad ih strava uhvati – umaći neće moći, izbliza bit će ščepani.');
INSERT INTO "bs_darassalam" VALUES(34,52,'34:52','Pa kad reknu: “Vjerujemo u njega!” – to prigrljivanje na mjestu daleku od kakve im je koristi!');
INSERT INTO "bs_darassalam" VALUES(34,53,'34:53','U njega oni nisu prije vjerovali, govorili su ono što nije poznato, što je od istine daleko...');
INSERT INTO "bs_darassalam" VALUES(34,54,'34:54','i između njih i onog što budu željeli bit će prepreka postavljena kao što je prije bilo učinjeno sa njima sličnim, jer su svi oni, doista, mnogo sumnjali.');
INSERT INTO "bs_darassalam" VALUES(35,1,'35:1','Hvaljen neka je Allah, Stvoritelj nebesa i Zemlje, Koji meleke sa po dva, tri i četiri krila čini izaslanicima. On onom što stvara dodaje što hoće. – On, uistinu, sve može.');
INSERT INTO "bs_darassalam" VALUES(35,2,'35:2','Milost koju Allah podari ljudima niko ne može uskratiti, a ono što On uskrati niko ne može, poslije Njega, dati. – On je silan i mudar.');
INSERT INTO "bs_darassalam" VALUES(35,3,'35:3','O ljudi, sjetite se milosti kojom vas Allah obasipa. Postoji li, osim Allaha, ikakav drugi stvoritelj koji vas s neba i iz zemlje opskrbljuje? Osim Njega nema drugog boga, pa kuda se onda odmećete?');
INSERT INTO "bs_darassalam" VALUES(35,4,'35:4','Ako te oni u laž utjeruju, pa i prije tebe su poslanike u laž utjerivali. – A Allahu će se sve vratiti.');
INSERT INTO "bs_darassalam" VALUES(35,5,'35:5','O ljudi, Allahova prijetnja je zaista istina, pa neka vas nikako život na ovom svijetu ne zaslijepi i neka vas šejtan u Allaha ne pokoleba.');
INSERT INTO "bs_darassalam" VALUES(35,6,'35:6','Šejtan je, uistinu, vaš neprijatelj, pa ga takvim i smatrajte! On poziva pristaše svoje da budu stanovnici u vatri.');
INSERT INTO "bs_darassalam" VALUES(35,7,'35:7','One koji ne vjeruju čeka patnja teška, a one koji vjeruju i dobra djela čine oprost i nagrada velika.');
INSERT INTO "bs_darassalam" VALUES(35,8,'35:8','Kako mogu biti isti: onaj kome su njegova ružna djela prikazana lijepim, a i on ih smatra lijepim? Allah u zabludi ostavlja onog koga hoće, a na Pravi put ukazuje onom kome hoće, pa ne izgaraj od žalosti za njima. – Allah, doista, dobro zna sve što oni rade.');
INSERT INTO "bs_darassalam" VALUES(35,9,'35:9','Allah šalje vjetrove koji pokreću oblake, a Mi ih onda u mrtve predjele upravljamo i njima zemlju oživljavamo, koja je mrtva bila – takvo će biti i oživljenje.');
INSERT INTO "bs_darassalam" VALUES(35,10,'35:10','Ako neko želi veličinu, pa u Allaha je sva veličina! K Njemu se dižu lijepe riječi, i dobro djelo On prima. A one koji imaju hrđave namjere čeka patnja nesnosna, i njihovo spletkarenje je rabota bezuspješna.');
INSERT INTO "bs_darassalam" VALUES(35,11,'35:11','Allah vas stvara od zemlje, zatim od kapi sjemena, i najzad vas čini muškarcima i ženama. I nijedna žena ne zanese niti rodi, a da to On ne zna. I ničiji život se ne produži niti skrati, a da to nije zabilježeno u Knjizi – to je Allahu, uistinu lahko!');
INSERT INTO "bs_darassalam" VALUES(35,12,'35:12','Ni dva mora nisu jednaka: jedno je slatko i prijatno – voda mu se lahko pije, a drugo je slano i gorko. Vi iz svakog jedete svježe meso i vadite nakit kojim se kitite. Ti vidiš lađe kako po njemu sijeku vodu da stječete iz obilja njegova, da biste bili zahvalni.');
INSERT INTO "bs_darassalam" VALUES(35,13,'35:13','On uvodi noć u dan i uvodi dan u noć, On je potčinio Sunce i Mjesec – svako plovi do roka određenog. To vam je, eto, Allah. Gospodar vaš, carstvo je Njegovo! A oni kojima se, pored Njega, klanjate ne posjeduju ništa.');
INSERT INTO "bs_darassalam" VALUES(35,14,'35:14','Ako im se molite, ne čuju vašu molbu. A da i čuju, ne bi vam se odazvali. Na Sudnjem danu će poreći da ste ih Njemu ravnim smatrali. I niko te neće obavijestiti kao Onaj Koji zna.');
INSERT INTO "bs_darassalam" VALUES(35,15,'35:15','O ljudi, vi ste siromasi, vi trebate Allaha, a Allah je nezavisan i hvale dostojan.');
INSERT INTO "bs_darassalam" VALUES(35,16,'35:16','Ako hoće, uklonit će vas i nova stvorenja dovesti...');
INSERT INTO "bs_darassalam" VALUES(35,17,'35:17','to Allahu nije teško.');
INSERT INTO "bs_darassalam" VALUES(35,18,'35:18','I nijedan griješnik neće grijehove drugog nositi. Ako grijehovima pretovareni pozove da mu se ponesu, niko mu ih neće ponijeti, pa ni rođak. A ti ćeš opomenuti samo one koji se Gospodara svog boje, iako ih niko ne vidi, i koji obavljaju molitvu – onaj ko se očisti, očistio se za svoje dobro. – A Allahu se sve vraća.');
INSERT INTO "bs_darassalam" VALUES(35,19,'35:19','Nisu nikako isti slijepac i onaj koji vidi...');
INSERT INTO "bs_darassalam" VALUES(35,20,'35:20','ni tmine ni svjetlo...');
INSERT INTO "bs_darassalam" VALUES(35,21,'35:21','ni hladovina i vjetar vrući...');
INSERT INTO "bs_darassalam" VALUES(35,22,'35:22','i nisu nikako isti živi i mrtvi. Allah će učiniti da čuje onaj koga On odabere, a ti ne možeš one u grobovima dozvati...');
INSERT INTO "bs_darassalam" VALUES(35,23,'35:23','tvoje je samo da opominješ.');
INSERT INTO "bs_darassalam" VALUES(35,24,'35:24','Mi smo te poslali s istinom da radosne vijesti donosiš i da opominješ. A nije bilo naroda kome nije došao onaj koji ga je opominjao.');
INSERT INTO "bs_darassalam" VALUES(35,25,'35:25','Ako te oni u laž utjeruju – pa i oni prije njih su poslanike u laž utjerivali, a oni su im očigledna čuda donosili, i listove, i Knjigu svjetilju.');
INSERT INTO "bs_darassalam" VALUES(35,26,'35:26','I Ja sam onda kažnjavao one koji nisu vjerovali, a kakvo je samo bilo neodobravanje Moje!');
INSERT INTO "bs_darassalam" VALUES(35,27,'35:27','Zar ne znaš da Allah s neba spušta vodu i da Mi pomoću nje stvaramo plodove različitih vrsta? A postoje brda bijelih i crvenih staza, različitih boja, i sasvim crnih.');
INSERT INTO "bs_darassalam" VALUES(35,28,'35:28','I ljudī i životinjā i stokē ima, isto tako, različitih vrsta. A Allaha se boje od robova Njegovih učeni. Allah je doista silan i On prašta.');
INSERT INTO "bs_darassalam" VALUES(35,29,'35:29','Oni koji Allahovu knjigu čitaju i molitvu obavljaju i od onog čime ih Mi opskrbljujemo udjeljuju, i tajno i javno, mogu se nadati nagradi koja neće nestati...');
INSERT INTO "bs_darassalam" VALUES(35,30,'35:30','da ih On prema onom što su radili nagradi i još im iz obilja Svog da, jer On mnogo prašta i blagodaran je.');
INSERT INTO "bs_darassalam" VALUES(35,31,'35:31','A ono što iz Knjige objavljujemo sušta je istina, ona potvrđuje da su istinite i one prije nje. – Allah, zaista, o robovima Svojim sve zna i On sve vidi.');
INSERT INTO "bs_darassalam" VALUES(35,32,'35:32','Mi ćemo učiniti da Knjigu poslije naslijede oni Naši robovi koje Mi izaberemo. Bit će onih koji će se prema sebi ogriješiti, bit će onih čija će dobra i loša djela podjednako teška biti, i bit će i onih koji će, Allahovom voljom, svojim dobrim djelima druge nadmašiti. I to je blagodat velika.');
INSERT INTO "bs_darassalam" VALUES(35,33,'35:33','Edenski perivoji! Ući će u njih, u njima će se zlatnim narukvicama, biserom ukrašenim, kititi, a haljine će im, u njima, od svile biti.');
INSERT INTO "bs_darassalam" VALUES(35,34,'35:34','“Hvaljen neka je Allah”, govorit će, “Koji je od nas tugu odstranio – Gospodar naš, zaista, mnogo prašta i blagodaran je...');
INSERT INTO "bs_darassalam" VALUES(35,35,'35:35','Koji nam je, od dobrote Svoje, vječno boravište darovao, gdje nas umor neće doticati i u kome nas klonulost neće snalaziti.”');
INSERT INTO "bs_darassalam" VALUES(35,36,'35:36','A nevjernike čeka vatra džehennemska, oni neće biti na smrt osuđeni, i neće umrijeti, i neće im se patnja u njemu ublažiti – eto tako ćemo svakog nevjernika kazniti...');
INSERT INTO "bs_darassalam" VALUES(35,37,'35:37','oni će u njemu jaukati: “Gospodaru naš, izbavi nas, činit ćemo dobra djela, drugačija od onih koja smo činili.” “A zar vas nismo ostavili da živite dovoljno dugo da bi onaj koji je trebao razmisliti imao vremena razmisliti, a bio vam je došao i onaj koji opominje? Zato iskusite patnju, nevjernicima nema pomoći!”');
INSERT INTO "bs_darassalam" VALUES(35,38,'35:38','Allah sigurno zna tajne nebesa i Zemlje, On dobro zna svačije misli.');
INSERT INTO "bs_darassalam" VALUES(35,39,'35:39','On čini da se smjenjujete na Zemlji. Ko ne bude vjerovao, nevjerovanje njegovo je na njegovu štetu. Nevjernicima će njihovo nevjerovanje kod Gospodara njihovog samo povećati odvratnost i nevjernicima će njihovo nevjerovanje povećati propast.');
INSERT INTO "bs_darassalam" VALUES(35,40,'35:40','Reci: “Kažite vi meni koji su dio Zemlje stvorila božanstva vaša kojima se, umjesto Allahu, klanjate, i recite mi imaju li oni u stvaranju nebesa ikakva udjela, ili smo mnogobošcima Mi dali Knjigu, pa imaju u njoj dokaz za to? Nijedno, već nevjernici jedni druge obmanjuju.”');
INSERT INTO "bs_darassalam" VALUES(35,41,'35:41','Zaista, Allah drži nebesa i Zemlju da ne bi nestali; a da nestanu – niko to osim Njega ne bi mogao spriječiti; On je zaista blag i prašta grijehe.');
INSERT INTO "bs_darassalam" VALUES(35,42,'35:42','Oni su se zaklinjali Allahom, najtežom zakletvom, da će se, bolje nego bilo koji narod, držati Pravog puta – samo ako im dođe onaj koji će ih opominjati. I kad im je došao onaj koji opominje, njegov im je dolazak samo povećao otuđenje...');
INSERT INTO "bs_darassalam" VALUES(35,43,'35:43','oholost na Zemlji i ružno spletkarenje, a spletke će pogoditi upravo one koji se njima služe. Zar oni mogu očekivati nešto drugo već ono što je zadesilo narode drevne? U Allahovim zakonima ti nikad nećeš naći promjene, u Allahovim zakonima ti nećeš naći odstupanja.');
INSERT INTO "bs_darassalam" VALUES(35,44,'35:44','Zašto oni ne putuju po svijetu da vide kako su završili oni prije njih, koji su bili jači od njih? – Allahu ne može ništa umaći ni na nebesima ni na Zemlji. On, uistinu, sve zna i sve može.');
INSERT INTO "bs_darassalam" VALUES(35,45,'35:45','Da Allah kažnjava ljude prema onom što zasluže, ništa živo na Zemljinoj površini ne bi ostavio, ali On ih ostavlja do roka određenog. I kad im rok dođe – pa Allah dobro zna robove Svoje.');
INSERT INTO "bs_darassalam" VALUES(36,1,'36:1','Ja Sin.');
INSERT INTO "bs_darassalam" VALUES(36,2,'36:2','Tako Mi Kur’ana mudrog...');
INSERT INTO "bs_darassalam" VALUES(36,3,'36:3','uistinu, jedan od poslanikā si ti...');
INSERT INTO "bs_darassalam" VALUES(36,4,'36:4','na Pravom putu...');
INSERT INTO "bs_darassalam" VALUES(36,5,'36:5','po objavi Silnoga i Samilosnoga...');
INSERT INTO "bs_darassalam" VALUES(36,6,'36:6','da opominješ narod čiji preci nisu bili opominjani, pa je ravnodušan!');
INSERT INTO "bs_darassalam" VALUES(36,7,'36:7','O većini njih se već obistinila Riječ – zato oni neće vjerovati.');
INSERT INTO "bs_darassalam" VALUES(36,8,'36:8','Mi smo učinili da budu kao oni na čije smo vratove sindžire stavili sve do podbradaka – zato su oni glava uzdignutih...');
INSERT INTO "bs_darassalam" VALUES(36,9,'36:9','i kao oni ispred kojih i iza kojih smo pregradu metnuli i na oči im koprenu stavili. Zato oni ne vide...');
INSERT INTO "bs_darassalam" VALUES(36,10,'36:10','i njima je svejedno opominjao ih ti ili ne opominjao, oni neće vjerovati.');
INSERT INTO "bs_darassalam" VALUES(36,11,'36:11','Tvoja opomena će koristiti samo onom koji Kur’an slijedi i Milostivoga se boji, iako Ga ne vidi – njega obraduj oprostom i nagradom lijepom!');
INSERT INTO "bs_darassalam" VALUES(36,12,'36:12','Mi ćemo, zaista, mrtve oživiti i Mi smo zapisali ono što su uradili i djela koja su iza sebe ostavili – sve smo Mi to u Knjizi jasno pobrojali.');
INSERT INTO "bs_darassalam" VALUES(36,13,'36:13','Navedi im kao pouku stanovnike jednog grada kad su im došli poslanici.');
INSERT INTO "bs_darassalam" VALUES(36,14,'36:14','Kad im Mi poslasmo dvojicu, ali im oni ne povjerovaše, i pojačasmo trećim, pa rekoše: “Mi smo vama poslati!”');
INSERT INTO "bs_darassalam" VALUES(36,15,'36:15','“Vi ste ljudi kao i mi”, oni odgovoriše, “Milostivi nije objavio ništa, vi neistinu govorite!”');
INSERT INTO "bs_darassalam" VALUES(36,16,'36:16','“Gospodar naš zna da smo, doista, vama poslati”, rekoše oni...');
INSERT INTO "bs_darassalam" VALUES(36,17,'36:17','“i dužni smo samo jasno obznaniti.”');
INSERT INTO "bs_darassalam" VALUES(36,18,'36:18','Oni rekoše: “Mi slutimo da nam nesreću donosite. Ako se ne okanite, kamenovat ćemo vas i stići će vas, zaista, bolna patnja od nas.”');
INSERT INTO "bs_darassalam" VALUES(36,19,'36:19','“Uzrok vaše nesreće je s vama!”, rekoše oni. “Zar zato što ste opomenuti? Ta vi ste narod koji svaku granicu zla prelazi.”');
INSERT INTO "bs_darassalam" VALUES(36,20,'36:20','I s kraja grada žurno dođe jedan čovjek i reče: “O narode moj, slijedi one koji su poslati...');
INSERT INTO "bs_darassalam" VALUES(36,21,'36:21','slijedite one koji od vas ne traže nikakvu nagradu, a na Pravom su putu!');
INSERT INTO "bs_darassalam" VALUES(36,22,'36:22','Zašto da se ne klanjam Onom Koji me je stvorio, a Njemu ćete se vratiti?');
INSERT INTO "bs_darassalam" VALUES(36,23,'36:23','Zašto da prihvatam druge bogove mimo Njega? Ako Milostivi hoće da me snađe neko zlo, njihovo posredovanje neće mi biti ni od kakve koristi i oni me neće moći spasiti...');
INSERT INTO "bs_darassalam" VALUES(36,24,'36:24','a ja bih tad bio u pravoj zabludi...');
INSERT INTO "bs_darassalam" VALUES(36,25,'36:25','ja vjerujem u Gospodara vašeg, čujte mene!”');
INSERT INTO "bs_darassalam" VALUES(36,26,'36:26','I bī rečeno: “Uđi u džennet!”, a on reče: “Kamo sreće da narod moj zna...');
INSERT INTO "bs_darassalam" VALUES(36,27,'36:27','zašto mi je Gospodar moj oprostio i lijep mi prijem priredio!”');
INSERT INTO "bs_darassalam" VALUES(36,28,'36:28','I protiv naroda njegova, poslije njega, Mi nismo vojsku s neba poslali, niti smo to ikad činili...');
INSERT INTO "bs_darassalam" VALUES(36,29,'36:29','samo bi se čuo jedan užasan krik, i oni bi odjednom svi pomrli.');
INSERT INTO "bs_darassalam" VALUES(36,30,'36:30','O kako će ljudi biti jadni! Nijedan poslanik im nije došao, a da mu se nisu narugali.');
INSERT INTO "bs_darassalam" VALUES(36,31,'36:31','Kako oni ne znaju koliko smo prije njih naroda uništili od kojih im se niko vratio nije...');
INSERT INTO "bs_darassalam" VALUES(36,32,'36:32','a svi oni bit će zajedno pred Nas dovedeni.');
INSERT INTO "bs_darassalam" VALUES(36,33,'36:33','Dokaz im je mrtva zemlja: Mi joj život dajemo i iz nje niče žito koje oni jedu...');
INSERT INTO "bs_darassalam" VALUES(36,34,'36:34','Mi po njoj stvaramo bašče, palmike i vinograde, i činimo da iz nje izvori izviru...');
INSERT INTO "bs_darassalam" VALUES(36,35,'36:35','da oni jedu plodove njihove i od onog što ruke njegove privrijede, pa zašto neće da budu zahvalni?');
INSERT INTO "bs_darassalam" VALUES(36,36,'36:36','Neka je hvaljen Onaj Koji u svemu stvara spol: u onom što iz zemlje niče, u njima samima, i u onom što oni ne znaju!');
INSERT INTO "bs_darassalam" VALUES(36,37,'36:37','I noć im je dokaz: Mi uklanjamo dnevnu svjetlost i oni ostaju u mraku.');
INSERT INTO "bs_darassalam" VALUES(36,38,'36:38','I Sunce se kreće do svog odredišta, to je odredba Silnog i Sveznajućeg.');
INSERT INTO "bs_darassalam" VALUES(36,39,'36:39','I Mjesecu smo odredili položaj, i on se uvijek ponovo vraća kao stari savijeni palmin prut.');
INSERT INTO "bs_darassalam" VALUES(36,40,'36:40','Niti Sunce može dostići Mjesec niti noć može prestići dan, svi oni u svemiru plove.');
INSERT INTO "bs_darassalam" VALUES(36,41,'36:41','Dokaz im je i to što smo Mi nosili potomke njihove u lađi natovarenoj...');
INSERT INTO "bs_darassalam" VALUES(36,42,'36:42','i što za njih, slične njima, stvaramo one na kojima se voze.');
INSERT INTO "bs_darassalam" VALUES(36,43,'36:43','I ako želimo, Mi ih potopimo. I neće im spasa biti, neće se izbaviti...');
INSERT INTO "bs_darassalam" VALUES(36,44,'36:44','osim ako im se ne smilujemo, da bi do roka određenog uživali.');
INSERT INTO "bs_darassalam" VALUES(36,45,'36:45','A kad im se rekne: “Bojte se onog šta je prije vas i onog što vas čeka da biste pomilovani bili!”');
INSERT INTO "bs_darassalam" VALUES(36,46,'36:46','I ne dođe im nijedan dokaz od Gospodara njihova kojem oni leđa ne okrenu.');
INSERT INTO "bs_darassalam" VALUES(36,47,'36:47','A kad im se kaže: “Udjeljujte od onog što vam Allah daje”, onda nevjernici govore vjernicima: “Zar da hranimo onog koga je Allah, da je htio, mogao nahraniti? Vi ste, uistinu, u pravoj zabludi!”');
INSERT INTO "bs_darassalam" VALUES(36,48,'36:48','I govore: “Kad će već jednom ta prijetnja, ako istinu govorite?”');
INSERT INTO "bs_darassalam" VALUES(36,49,'36:49','A ne čekaju drugo do strašan glas koji će ih, dok se budu jedni s drugima prepirali, obuzeti...');
INSERT INTO "bs_darassalam" VALUES(36,50,'36:50','pa neće moći ništa oporučiti, niti se čeljadi svojoj vratiti.');
INSERT INTO "bs_darassalam" VALUES(36,51,'36:51','I puhnut će se u rog, pa će oni iz grobova prema Gospodaru svom pohrliti...');
INSERT INTO "bs_darassalam" VALUES(36,52,'36:52','govoreći: “Teško nama! Ko nas iz naših grobova oživi?” “Eto ostvaruje se prijetnja Milostivog, poslanici su istinu govorili!”');
INSERT INTO "bs_darassalam" VALUES(36,53,'36:53','Bit će to samo jedan glas i oni će se svi pred Nama obresti.');
INSERT INTO "bs_darassalam" VALUES(36,54,'36:54','Danas se neće nikom nepravda učiniti i vi ćete, prema onom kako ste radili, nagrađeni biti...');
INSERT INTO "bs_darassalam" VALUES(36,55,'36:55','stanovnici Dženneta uživat će tog Dana u blagodatima veseli i radosni...');
INSERT INTO "bs_darassalam" VALUES(36,56,'36:56','oni i žene njihove bit će u hladovini na ukrašenim divanima naslonjeni...');
INSERT INTO "bs_darassalam" VALUES(36,57,'36:57','u njemu će imati voća, i ono što budu željeli.');
INSERT INTO "bs_darassalam" VALUES(36,58,'36:58','“Mir vama!”, bit će riječi Gospodara Milostivog...');
INSERT INTO "bs_darassalam" VALUES(36,59,'36:59','“a vi, o grešnici, danas se odvojite!”');
INSERT INTO "bs_darassalam" VALUES(36,60,'36:60','O sinovi Ademovi, zar vam nisam naredio: “Ne obožavajte šejtana, on vam je neprijatelj otvoreni?...');
INSERT INTO "bs_darassalam" VALUES(36,61,'36:61','već se klanjajte Meni – to je Put pravi.');
INSERT INTO "bs_darassalam" VALUES(36,62,'36:62','On je mnoge od vas u zabludu odveo, kako niste pameti imali!?');
INSERT INTO "bs_darassalam" VALUES(36,63,'36:63','Ovo je Džehennem kojim vam se prijetilo...');
INSERT INTO "bs_darassalam" VALUES(36,64,'36:64','pržite se sad u njemu zato što niste vjerovali!”');
INSERT INTO "bs_darassalam" VALUES(36,65,'36:65','Danas ćemo im usta zapečatiti, njihove ruke će Nam govoriti, a noge njihove će o onom što su radili svjedočiti.');
INSERT INTO "bs_darassalam" VALUES(36,66,'36:66','Da smo htjeli, mogli smo ih vida njihova lišiti, pa kad bi na sirat-ćupriju pošli, kako bi vidjeli?');
INSERT INTO "bs_darassalam" VALUES(36,67,'36:67','A da smo htjeli, mogli smo ih na mjestu na kome su zgriješili u nešto pretvoriti, pa ne bi mogli nikuda otići niti se vratiti.');
INSERT INTO "bs_darassalam" VALUES(36,68,'36:68','Onom kome dug život damo, Mi mu izgled nagore izmijenimo. Zar oni ne razumiju?');
INSERT INTO "bs_darassalam" VALUES(36,69,'36:69','Mi Poslanika nismo pjesništvu učili, to mu ne priliči. Ovo je samo pouka – Kur’an jasni...');
INSERT INTO "bs_darassalam" VALUES(36,70,'36:70','da opominje onog ko ima pameti, i da zasluže kaznu nevjernici.');
INSERT INTO "bs_darassalam" VALUES(36,71,'36:71','Kako oni ne vide da Mi samo zbog njih stoku stvaramo i da oni njome raspolažu kao vlasnici...');
INSERT INTO "bs_darassalam" VALUES(36,72,'36:72','i da smo im dali da se njome služe – na nekim jašu, a nekima se hrane...');
INSERT INTO "bs_darassalam" VALUES(36,73,'36:73','i drugih koristi od nje imaju, i mlijeko, pa zašto nisu zahvalni...');
INSERT INTO "bs_darassalam" VALUES(36,74,'36:74','već pored Allaha druge bogove prihvataju u nadi da će im oni na pomoći biti...');
INSERT INTO "bs_darassalam" VALUES(36,75,'36:75','oni im, međutim, neće moći pomoći i oni su za njih vojska privedena?');
INSERT INTO "bs_darassalam" VALUES(36,76,'36:76','I neka te ne žaloste riječi njihove. Mi, doista, znamo i ono što kriju i ono što pokazuju.');
INSERT INTO "bs_darassalam" VALUES(36,77,'36:77','Kako čovjek ne vidi da ga Mi od kapi sjemena stvaramo, i opet je otvoreni protivnik...');
INSERT INTO "bs_darassalam" VALUES(36,78,'36:78','i Nama navodi primjer, a zaboravlja kako je stvoren, i govori: “Ko će oživiti kosti kad budu truhle?”');
INSERT INTO "bs_darassalam" VALUES(36,79,'36:79','Reci: “Oživit će ih Onaj Koji ih je prvi put stvorio, On dobro zna sve što je stvorio.');
INSERT INTO "bs_darassalam" VALUES(36,80,'36:80','Onaj Koji vam iz zelenog drveća vatru stvara i vi njome potpaljujete.”');
INSERT INTO "bs_darassalam" VALUES(36,81,'36:81','Zar Onaj Koji je stvorio nebesa i Zemlju nije kadar stvoriti njima slične? Jest, On sve stvara i On je Sveznajući.');
INSERT INTO "bs_darassalam" VALUES(36,82,'36:82','Kad nešto hoće, On samo za to rekne: “Budi!”, i ono bude.');
INSERT INTO "bs_darassalam" VALUES(36,83,'36:83','Pa neka je hvaljen Onaj u Čijoj je ruci vlast nad svim, Njemu ćete se vratiti!');
INSERT INTO "bs_darassalam" VALUES(37,1,'37:1','Tako Mi onih u redove poredanih...');
INSERT INTO "bs_darassalam" VALUES(37,2,'37:2','i onih koji odvraćaju...');
INSERT INTO "bs_darassalam" VALUES(37,3,'37:3','i onih koji opomenu čitaju...');
INSERT INTO "bs_darassalam" VALUES(37,4,'37:4','vaš Bog je, uistinu, Jedan...');
INSERT INTO "bs_darassalam" VALUES(37,5,'37:5','Gospodar nebesa i Zemlje i onog što je između njih i Gospodar istokā!');
INSERT INTO "bs_darassalam" VALUES(37,6,'37:6','Mi smo nebo najbliže vama sjajnim zvijezdama okitili...');
INSERT INTO "bs_darassalam" VALUES(37,7,'37:7','i čuvamo ga od svakog šejtana prkosnog...');
INSERT INTO "bs_darassalam" VALUES(37,8,'37:8','da ne prisluškuje meleke uzvišene. Njih sa svih strana gađaju...');
INSERT INTO "bs_darassalam" VALUES(37,9,'37:9','da ih otjeraju, njih čeka patnja neprekidna...');
INSERT INTO "bs_darassalam" VALUES(37,10,'37:10','a onog koji šta ugrabi – stigne svjetlica blistava.');
INSERT INTO "bs_darassalam" VALUES(37,11,'37:11','Upitaj ih je li teže njih stvoriti ili sve ostalo što smo stvorili? – Njih stvaramo od ljepljive ilovače.');
INSERT INTO "bs_darassalam" VALUES(37,12,'37:12','Ti se čudiš, a oni se rugaju...');
INSERT INTO "bs_darassalam" VALUES(37,13,'37:13','a kad im se savjeti upućuju, oni ih ne prihvataju...');
INSERT INTO "bs_darassalam" VALUES(37,14,'37:14','i kad dokaz vide, oni jedni druge na ismijavanje podstiču...');
INSERT INTO "bs_darassalam" VALUES(37,15,'37:15','i govore: “Ovo nije ništa drugo do prava čarolija!');
INSERT INTO "bs_darassalam" VALUES(37,16,'37:16','Zar kad poumiremo i kad kosti i zemlja postanemo, zar ćemo mi, zaista, biti oživljeni...');
INSERT INTO "bs_darassalam" VALUES(37,17,'37:17','i naši preci davni?”');
INSERT INTO "bs_darassalam" VALUES(37,18,'37:18','Reci: “Da, a bit ćete i poniženi!”');
INSERT INTO "bs_darassalam" VALUES(37,19,'37:19','To će biti samo glas jedan, i svi će odjednom čekati...');
INSERT INTO "bs_darassalam" VALUES(37,20,'37:20','i reći: “Teško nama, ovo je Sudnji dan!”');
INSERT INTO "bs_darassalam" VALUES(37,21,'37:21','Da, ovo je Dan strašnog suda u koji vi niste vjerovali!');
INSERT INTO "bs_darassalam" VALUES(37,22,'37:22','Sakupite nevjernike i drugare njihove i one kojima su se klanjali...');
INSERT INTO "bs_darassalam" VALUES(37,23,'37:23','mimo Allaha, i pokažite im put koji u Džehennem vodi...');
INSERT INTO "bs_darassalam" VALUES(37,24,'37:24','i zaustavite ih, oni će biti pitani.');
INSERT INTO "bs_darassalam" VALUES(37,25,'37:25','“Šta vam je, zašto jedni drugima ne pomognete?”');
INSERT INTO "bs_darassalam" VALUES(37,26,'37:26','Ali, tog Dana oni će se sasvim prepustiti...');
INSERT INTO "bs_darassalam" VALUES(37,27,'37:27','i jedni drugima predbacivati...');
INSERT INTO "bs_darassalam" VALUES(37,28,'37:28','“Vi ste nas varali.”');
INSERT INTO "bs_darassalam" VALUES(37,29,'37:29','“Nismo”, odgovorit će, “nego vi niste htjeli vjerovati.');
INSERT INTO "bs_darassalam" VALUES(37,30,'37:30','A nikakve vlasti nad vama nismo imali, obijestan ste narod bili.');
INSERT INTO "bs_darassalam" VALUES(37,31,'37:31','I riječ Gospodara našeg da ćemo, doista, kaznu iskusiti na nama se ispunila.');
INSERT INTO "bs_darassalam" VALUES(37,32,'37:32','A u zabludu smo vas pozivali jer smo i sami u zabludi bili.”');
INSERT INTO "bs_darassalam" VALUES(37,33,'37:33','I oni će tog Dana zajedno na muci biti...');
INSERT INTO "bs_darassalam" VALUES(37,34,'37:34','jer Mi ćemo tako s mnogobošcima postupiti.');
INSERT INTO "bs_darassalam" VALUES(37,35,'37:35','Kad im se govorilo: “Nema istinskog božanstva osim Allaha!”, oni su se oholili...');
INSERT INTO "bs_darassalam" VALUES(37,36,'37:36','i govorili: “Zar da napustimo božanstva naša zbog jednog ludog pjesnika?”');
INSERT INTO "bs_darassalam" VALUES(37,37,'37:37','A nije tako, on istinu donosi i tvrdi da su svi poslanici istinu donosili.');
INSERT INTO "bs_darassalam" VALUES(37,38,'37:38','A vi ćete sigurno bolnu patnju iskusiti...');
INSERT INTO "bs_darassalam" VALUES(37,39,'37:39','kako ste radili, onako ćete kažnjeni biti!');
INSERT INTO "bs_darassalam" VALUES(37,40,'37:40','A Allahovi iskreni robovi...');
INSERT INTO "bs_darassalam" VALUES(37,41,'37:41','posebnu će opskrbu imati...');
INSERT INTO "bs_darassalam" VALUES(37,42,'37:42','razno voće, i bit će poštovani...');
INSERT INTO "bs_darassalam" VALUES(37,43,'37:43','u džennetskim baščama nasladā...');
INSERT INTO "bs_darassalam" VALUES(37,44,'37:44','na divanima, jedni prema drugima...');
INSERT INTO "bs_darassalam" VALUES(37,45,'37:45','bit će posluženi pićem – iz izvora koji će stalno teći...');
INSERT INTO "bs_darassalam" VALUES(37,46,'37:46','bistrim i prijatnim onima koji budu pili...');
INSERT INTO "bs_darassalam" VALUES(37,47,'37:47','od njega neće glava boljeti i od njega se neće pamet gubiti.');
INSERT INTO "bs_darassalam" VALUES(37,48,'37:48','Pored njih će biti one koje će preda se gledati, očiju prekrasnih...');
INSERT INTO "bs_darassalam" VALUES(37,49,'37:49','kao da su one jaja pokrivena.');
INSERT INTO "bs_darassalam" VALUES(37,50,'37:50','I oni će jedan s drugim razgovarati...');
INSERT INTO "bs_darassalam" VALUES(37,51,'37:51','i jedan od njih će reći: “Imao sam druga jednog...');
INSERT INTO "bs_darassalam" VALUES(37,52,'37:52','koji je govorio: ‘Zar i ti vjeruješ...');
INSERT INTO "bs_darassalam" VALUES(37,53,'37:53','da ćemo, kad poumiremo i zemlja i kosti postanemo, doista račun polagati?’”');
INSERT INTO "bs_darassalam" VALUES(37,54,'37:54','“Hoćete li pogledati?”, reći će.');
INSERT INTO "bs_darassalam" VALUES(37,55,'37:55','I on će pogledati, i toga usred džehennema ugledati.');
INSERT INTO "bs_darassalam" VALUES(37,56,'37:56','“Allaha mi”, reći će, “zamalo me nisi upropastio!');
INSERT INTO "bs_darassalam" VALUES(37,57,'37:57','Da nije bilo milosti Gospodara mog, i ja bih se sad mučio.”');
INSERT INTO "bs_darassalam" VALUES(37,58,'37:58','“A mi više, je li, nećemo biti zatečeni smrću?');
INSERT INTO "bs_darassalam" VALUES(37,59,'37:59','Jednom smo umrli, više nećemo biti mučeni.');
INSERT INTO "bs_darassalam" VALUES(37,60,'37:60','To će, zaista, uspjeh veliki biti!”');
INSERT INTO "bs_darassalam" VALUES(37,61,'37:61','Za nešto nalik ovom neka se trudbenici trude!');
INSERT INTO "bs_darassalam" VALUES(37,62,'37:62','A je li bolja ta gozba ili drvo Zekkum...');
INSERT INTO "bs_darassalam" VALUES(37,63,'37:63','koje smo nevjernicima kao iskušenje odredili?');
INSERT INTO "bs_darassalam" VALUES(37,64,'37:64','To je drvo koje u dnu Džehennema raste...');
INSERT INTO "bs_darassalam" VALUES(37,65,'37:65','plod će mu poput glava šejtanskih biti.');
INSERT INTO "bs_darassalam" VALUES(37,66,'37:66','Oni će se njime hraniti i trbuhe svoje njime puniti...');
INSERT INTO "bs_darassalam" VALUES(37,67,'37:67','zatim će to s ključalom vodom izmiješati...');
INSERT INTO "bs_darassalam" VALUES(37,68,'37:68','a potom će se, sigurno, opet u pakao vratiti.');
INSERT INTO "bs_darassalam" VALUES(37,69,'37:69','Oni su očeve svoje u zabludi zatekli...');
INSERT INTO "bs_darassalam" VALUES(37,70,'37:70','pa i oni stopama njihovim nastavili.');
INSERT INTO "bs_darassalam" VALUES(37,71,'37:71','A i prije njih su većinom drevni narodi u zabludi bili...');
INSERT INTO "bs_darassalam" VALUES(37,72,'37:72','iako smo im Mi slali one koji su ih opominjali.');
INSERT INTO "bs_darassalam" VALUES(37,73,'37:73','Zato pogledaj kakav je bio kraj onih koji su bili opomenuti...');
INSERT INTO "bs_darassalam" VALUES(37,74,'37:74','A Allahovi iskreni robovi...');
INSERT INTO "bs_darassalam" VALUES(37,75,'37:75','A kad Nas je Nuh zovnuo, Mi smo se lijepo odazvali...');
INSERT INTO "bs_darassalam" VALUES(37,76,'37:76','njega i porodicu njegovu smo nevolje teške spasili...');
INSERT INTO "bs_darassalam" VALUES(37,77,'37:77','i samo potomke njegove u životu ostavili...');
INSERT INTO "bs_darassalam" VALUES(37,78,'37:78','i u naraštajima mu kasnijim spomen sačuvali...');
INSERT INTO "bs_darassalam" VALUES(37,79,'37:79','Mir Nuhu od svjetova svih!');
INSERT INTO "bs_darassalam" VALUES(37,80,'37:80','Eto tako Mi nagrađujemo one koji dobra djela čine.');
INSERT INTO "bs_darassalam" VALUES(37,81,'37:81','On je bio rob Naš, vjernik...');
INSERT INTO "bs_darassalam" VALUES(37,82,'37:82','a ostale smo poslije potopili.');
INSERT INTO "bs_darassalam" VALUES(37,83,'37:83','Iste vjere kao i on bio je i Ibrahim...');
INSERT INTO "bs_darassalam" VALUES(37,84,'37:84','kad je Gospodaru svom iskrena srca došao...');
INSERT INTO "bs_darassalam" VALUES(37,85,'37:85','kad je ocu svom i narodu svom rekao: “Čemu se to vi klanjate...');
INSERT INTO "bs_darassalam" VALUES(37,86,'37:86','Zar lažna božanstva umjesto Allaha hoćete?');
INSERT INTO "bs_darassalam" VALUES(37,87,'37:87','I šta o Gospodaru svjetova mislite?”');
INSERT INTO "bs_darassalam" VALUES(37,88,'37:88','I on baci pogled na zvijezde...');
INSERT INTO "bs_darassalam" VALUES(37,89,'37:89','pa reče: "Zbilja, ja sam bolestan."');
INSERT INTO "bs_darassalam" VALUES(37,90,'37:90','I oni ga napustiše, uzmaknuvši...');
INSERT INTO "bs_darassalam" VALUES(37,91,'37:91','a on se kumirima njihovim prikrade, pa reče: “Zašto ne jedete...');
INSERT INTO "bs_darassalam" VALUES(37,92,'37:92','Šta vam je, što ne govorite?”');
INSERT INTO "bs_darassalam" VALUES(37,93,'37:93','I krišom im priđe desnom ih rukom udarajući...');
INSERT INTO "bs_darassalam" VALUES(37,94,'37:94','pa mu narod trkom dođe.');
INSERT INTO "bs_darassalam" VALUES(37,95,'37:95','“Kako možete obožavati one koje sami klešete”, upita...');
INSERT INTO "bs_darassalam" VALUES(37,96,'37:96','“kad Allah stvara i vas i ono što napravite?”');
INSERT INTO "bs_darassalam" VALUES(37,97,'37:97','“Pripremite za njega lomaču”, povikaše, “pa ga u vatru bacite!”');
INSERT INTO "bs_darassalam" VALUES(37,98,'37:98','I htjedoše ga na muke staviti, ali Mi njih učinismo poniženim.');
INSERT INTO "bs_darassalam" VALUES(37,99,'37:99','“Idem onamo gdje mi je Gospodar moj naredio”, reče, “On će me kuda treba uputiti."');
INSERT INTO "bs_darassalam" VALUES(37,100,'37:100','"Gospodaru moj, daruj mi porod čestiti."');
INSERT INTO "bs_darassalam" VALUES(37,101,'37:101','i Mi smo ga obradovali dječakom blage naravi.');
INSERT INTO "bs_darassalam" VALUES(37,102,'37:102','I kad on odraste toliko da mu poče u poslu pomagati, Ibrahim reče: “O sinko moj, u snu sam vidio da te trebam zaklati, pa šta ti misliš?” “O oče moj”, reče, “postupi onako kako ti se naređuje! Vidjet ćeš, ako Bog da, da ću sve izdržati.”');
INSERT INTO "bs_darassalam" VALUES(37,103,'37:103','I njih dvojica poslušaše. I kad ga on čelom prema zemlji položi...');
INSERT INTO "bs_darassalam" VALUES(37,104,'37:104','Mi ga zovnusmo: “O Ibrahime...');
INSERT INTO "bs_darassalam" VALUES(37,105,'37:105','ti si se objavi u snu odazvao, a Mi ovako nagrađujemo one koji dobra djela čine.”');
INSERT INTO "bs_darassalam" VALUES(37,106,'37:106','To je, zaista, bilo pravo iskušenje!');
INSERT INTO "bs_darassalam" VALUES(37,107,'37:107','I kurbanom ga velikim iskupismo...');
INSERT INTO "bs_darassalam" VALUES(37,108,'37:108','i u naraštajima mu kasnijim spomen sačuvali...');
INSERT INTO "bs_darassalam" VALUES(37,109,'37:109','Neka je u miru Ibrahim!');
INSERT INTO "bs_darassalam" VALUES(37,110,'37:110','Eto tako Mi nagrađujemo one koji dobra djela čine...');
INSERT INTO "bs_darassalam" VALUES(37,111,'37:111','On je bio rob Naš, vjernik...');
INSERT INTO "bs_darassalam" VALUES(37,112,'37:112','I obradovali smo ga Ishakom, vjerovjesnikom i čovjekom dobrim.');
INSERT INTO "bs_darassalam" VALUES(37,113,'37:113','I blagoslovili smo i njega i Ishaka, a među potomcima njihovim ima vjernika i nevjernika očitih.');
INSERT INTO "bs_darassalam" VALUES(37,114,'37:114','I Musau i Harunu milost smo ukazali...');
INSERT INTO "bs_darassalam" VALUES(37,115,'37:115','pa i njih i narod njihov nevolje velike spasili...');
INSERT INTO "bs_darassalam" VALUES(37,116,'37:116','i pomogli im da pobijede...');
INSERT INTO "bs_darassalam" VALUES(37,117,'37:117','i Knjigu im jasnu dali...');
INSERT INTO "bs_darassalam" VALUES(37,118,'37:118','i obojicu na Pravi put uputili...');
INSERT INTO "bs_darassalam" VALUES(37,119,'37:119','i u naraštajima im kasnijim spomen sačuvali.');
INSERT INTO "bs_darassalam" VALUES(37,120,'37:120','Nek su u miru Musa i Harun!');
INSERT INTO "bs_darassalam" VALUES(37,121,'37:121','Eto tako Mi nagrađujemo one koji dobra djela čine.');
INSERT INTO "bs_darassalam" VALUES(37,122,'37:122','a njih dvojica su, uistinu, bili robovi Naši, vjernici.');
INSERT INTO "bs_darassalam" VALUES(37,123,'37:123','I Il’jas je bio poslanik.');
INSERT INTO "bs_darassalam" VALUES(37,124,'37:124','Kad on reče narodu svom: “Zar se ne bojite?...');
INSERT INTO "bs_darassalam" VALUES(37,125,'37:125','Što se Ba''lu molite, a najljepšeg Stvoritelja ostavljate...');
INSERT INTO "bs_darassalam" VALUES(37,126,'37:126','Allaha, Gospodara svog i Gospodara vaših predaka drevnih?”...');
INSERT INTO "bs_darassalam" VALUES(37,127,'37:127','oni ga lašcem nazvaše i zato će, sigurno, u vatru svi oni biti bačeni...');
INSERT INTO "bs_darassalam" VALUES(37,128,'37:128','A Allahovi iskreni robovi...');
INSERT INTO "bs_darassalam" VALUES(37,129,'37:129','i u naraštajima mu kasnijim spomen sačuvali...');
INSERT INTO "bs_darassalam" VALUES(37,130,'37:130','Neka je u miru Il’jas!');
INSERT INTO "bs_darassalam" VALUES(37,131,'37:131','Eto tako Mi nagrađujemo one koji dobra djela čine.');
INSERT INTO "bs_darassalam" VALUES(37,132,'37:132','On je bio rob Naš, vjernik...');
INSERT INTO "bs_darassalam" VALUES(37,133,'37:133','I Lut je bio poslanik.');
INSERT INTO "bs_darassalam" VALUES(37,134,'37:134','Mi smo njega i čitavu njegovu porodicu spasili...');
INSERT INTO "bs_darassalam" VALUES(37,135,'37:135','osim starice, ona je nastradala s onima koji su nastradali.');
INSERT INTO "bs_darassalam" VALUES(37,136,'37:136','A ostale smo uništili.');
INSERT INTO "bs_darassalam" VALUES(37,137,'37:137','I vi pored nastambi njihovih prolazite i ujutro...');
INSERT INTO "bs_darassalam" VALUES(37,138,'37:138','i noću, pa zašto se ne urazumite?');
INSERT INTO "bs_darassalam" VALUES(37,139,'37:139','I Junus je bio poslanik.');
INSERT INTO "bs_darassalam" VALUES(37,140,'37:140','I on pobježe na jednu lađu prepunu...');
INSERT INTO "bs_darassalam" VALUES(37,141,'37:141','i baci kocku i kocka na njega pade.');
INSERT INTO "bs_darassalam" VALUES(37,142,'37:142','I riba ga proguta, a bio je zaslužio prijekor.');
INSERT INTO "bs_darassalam" VALUES(37,143,'37:143','I da nije bio jedan od onih koji Allaha hvale...');
INSERT INTO "bs_darassalam" VALUES(37,144,'37:144','sigurno bi ostao u utrobi njenoj do Dana kad će svi biti oživljeni.');
INSERT INTO "bs_darassalam" VALUES(37,145,'37:145','I Mi ga izbacismo na jedno pusto mjesto, a on je bio bolan...');
INSERT INTO "bs_darassalam" VALUES(37,146,'37:146','i učinismo da iznad njega izraste vriježa jedne tikve.');
INSERT INTO "bs_darassalam" VALUES(37,147,'37:147','I poslasmo ga stotini hiljada ljudi, i više...');
INSERT INTO "bs_darassalam" VALUES(37,148,'37:148','i oni povjerovaše, i njima dadosmo da do roka određenog požive.');
INSERT INTO "bs_darassalam" VALUES(37,149,'37:149','A upitaj ih: “Zar su za Gospodara tvog kćeri, a za njih sinovi?');
INSERT INTO "bs_darassalam" VALUES(37,150,'37:150','Zar smo u njihovu prisustvu meleke kao žene stvorili?”');
INSERT INTO "bs_darassalam" VALUES(37,151,'37:151','Eto, oni, zbog toga što lažu, doista govore...');
INSERT INTO "bs_darassalam" VALUES(37,152,'37:152','“Allah je rodio”, oni su, zaista, lažljivci.');
INSERT INTO "bs_darassalam" VALUES(37,153,'37:153','Zar je On kćeri sinovima pretpostavio?');
INSERT INTO "bs_darassalam" VALUES(37,154,'37:154','Šta vam je, kako rasuđujete!?');
INSERT INTO "bs_darassalam" VALUES(37,155,'37:155','Zašto ne razmislite?');
INSERT INTO "bs_darassalam" VALUES(37,156,'37:156','Ili, gdje vam je dokaz očiti?');
INSERT INTO "bs_darassalam" VALUES(37,157,'37:157','Donesite Knjigu svoju, ako istinu govorite!');
INSERT INTO "bs_darassalam" VALUES(37,158,'37:158','Mnogobošci između Njega i melekā srodstvo uspostavljaju, a meleki odavno znaju da će oni koji tako govore u vatru biti bačeni...');
INSERT INTO "bs_darassalam" VALUES(37,159,'37:159','čist je Allah i daleko od onoga kako Ga oni opisuju!');
INSERT INTO "bs_darassalam" VALUES(37,160,'37:160','To ne čine Allahovi iskreni robovi.');
INSERT INTO "bs_darassalam" VALUES(37,161,'37:161','Ali, ni vi, s onima kojima se klanjate...');
INSERT INTO "bs_darassalam" VALUES(37,162,'37:162','ne možete o njima nikog u zabludu zavesti...');
INSERT INTO "bs_darassalam" VALUES(37,163,'37:163','možete samo onog koji će ionako u vatri gorjeti.');
INSERT INTO "bs_darassalam" VALUES(37,164,'37:164','“Svakom od nas mjesto je određeno...');
INSERT INTO "bs_darassalam" VALUES(37,165,'37:165','mi smo u redove poredani...');
INSERT INTO "bs_darassalam" VALUES(37,166,'37:166','i samo Njega hvalimo!”');
INSERT INTO "bs_darassalam" VALUES(37,167,'37:167','A oni su sigurno govorili...');
INSERT INTO "bs_darassalam" VALUES(37,168,'37:168','“Da smo mi Knjigu imali kao što su je imali narodi prijašnji...');
INSERT INTO "bs_darassalam" VALUES(37,169,'37:169','sigurno bismo bili Allahovi robovi iskreni!”');
INSERT INTO "bs_darassalam" VALUES(37,170,'37:170','Ali u Kur’an ne htjedoše povjerovati, i znat će oni!');
INSERT INTO "bs_darassalam" VALUES(37,171,'37:171','A riječ Naša davno je rečena o robovima Našim, o poslanicima...');
INSERT INTO "bs_darassalam" VALUES(37,172,'37:172','“Oni će biti, doista, potpomognuti...');
INSERT INTO "bs_darassalam" VALUES(37,173,'37:173','i vojska će Naša zacijelo pobijediti!”');
INSERT INTO "bs_darassalam" VALUES(37,174,'37:174','Zato se okreni od njih za neko vrijeme...');
INSERT INTO "bs_darassalam" VALUES(37,175,'37:175','i posmatraj ih, i oni će posmatrati!');
INSERT INTO "bs_darassalam" VALUES(37,176,'37:176','Zar oni kaznu Našu da požuruju?');
INSERT INTO "bs_darassalam" VALUES(37,177,'37:177','Kad ih ona stigne, zlo će jutro osvanuti onima koji su bili opomenuti!');
INSERT INTO "bs_darassalam" VALUES(37,178,'37:178','Zato se okreni od njih za neko vrijeme...');
INSERT INTO "bs_darassalam" VALUES(37,179,'37:179','i posmatraj, i oni će posmatrati!');
INSERT INTO "bs_darassalam" VALUES(37,180,'37:180','Uzvišen je Gospodar tvoj, Gospodar dostojanstva, u odnosu na ono kako ga opisuju!');
INSERT INTO "bs_darassalam" VALUES(37,181,'37:181','I mir poslanicima...');
INSERT INTO "bs_darassalam" VALUES(37,182,'37:182','i hvaljen neka je Allah, Gospodar svjetova!');
INSERT INTO "bs_darassalam" VALUES(38,1,'38:1','Sād. Sad. Tako Mi Kur''ana koji opomenu sadrži,');
INSERT INTO "bs_darassalam" VALUES(38,2,'38:2','doista su oni koji ne vjeruju u osionosti i raskolu!');
INSERT INTO "bs_darassalam" VALUES(38,3,'38:3','Koliko smo Mi naroda prije njih uništili. I oni su za pomoć vapili, ali nije bilo vremena za uzmicanje!');
INSERT INTO "bs_darassalam" VALUES(38,4,'38:4','Oni se čude što im je jedan od njih došao da ih opominje. I govore nevjernici: “Ovo je čarobnjak, lažov!');
INSERT INTO "bs_darassalam" VALUES(38,5,'38:5','Zar on da svede bogove na nekakvog boga jednog? To je, zaista, nešto veoma čudno!”');
INSERT INTO "bs_darassalam" VALUES(38,6,'38:6','I oni ugledni među njima krenuše: "Idite, nastavite i ustrajte uz božanstva svoja! Uistinu, to je nešto što se hoće!');
INSERT INTO "bs_darassalam" VALUES(38,7,'38:7','Za ovo nismo čuli u vjeri posljednjoj, ovo nije ništa drugo već izmišljotina;');
INSERT INTO "bs_darassalam" VALUES(38,8,'38:8','Zašto baš njemu, između nas, da bude poslata Opomena!?” Ali, oni u Opomenu Moju sumnjaju zato što kaznu Moju nisu iskusili.');
INSERT INTO "bs_darassalam" VALUES(38,9,'38:9','Ili, da nisu u njih riznice milosti Gospodara tvoga, Silnoga i obilnog Darovatelja?!');
INSERT INTO "bs_darassalam" VALUES(38,10,'38:10','Ili, oni možda imaju vlast na nebesima i na Zemlji i nad onim što je između njih?! - neka se, onda, uz konopce popnu!');
INSERT INTO "bs_darassalam" VALUES(38,11,'38:11','Oni su samo neznatna vojska udruženih skupina koja će tamo biti poražena.');
INSERT INTO "bs_darassalam" VALUES(38,12,'38:12','I prije njih su narod Nuhov, i Ad, i faraon – vlasnik moćnih vojski – poricali...');
INSERT INTO "bs_darassalam" VALUES(38,13,'38:13','i Semud i Liltov narod i stanovnici Ejke; oni su baš bili udružene skupine!');
INSERT INTO "bs_darassalam" VALUES(38,14,'38:14','svi su oni poslanike u laž utjerivali i kaznu Moju zaslužili.');
INSERT INTO "bs_darassalam" VALUES(38,15,'38:15','A i ovi ne čekaju do jedan zvuk roga koji neće biti potrebno ponoviti.');
INSERT INTO "bs_darassalam" VALUES(38,16,'38:16','I govore: “Gospodaru naš, požuri, kazni nas, prije Dana u kojem će se račun polagati!”');
INSERT INTO "bs_darassalam" VALUES(38,17,'38:17','Ti otrpi ono što oni govore i sjeti se roba Našeg, Davuda, čvrstog u vjeri, koji se uvijek Allahu obraćao.');
INSERT INTO "bs_darassalam" VALUES(38,18,'38:18','Mi smo brda potčinili da zajedno s njime uvečer i u osvit Allaha veličaju...');
INSERT INTO "bs_darassalam" VALUES(38,19,'38:19','a i ptice okupljene – svi su oni zbog njegova hvaljenja hvalu ponavljali.');
INSERT INTO "bs_darassalam" VALUES(38,20,'38:20','I učvrstili smo carstvo njegovo i dali mu vjerovjesništvo i sposobnost da rasuđuje.');
INSERT INTO "bs_darassalam" VALUES(38,21,'38:21','A je li do tebe doprla vijest o parničarima kad su preko zida Hrama prešli...');
INSERT INTO "bs_darassalam" VALUES(38,22,'38:22','kad su Davudu upali, pa se on njih uplašio. “Ne boj se”, rekli su, “mi smo dvojica parničara, jedan drugom smo nažao učinili, pa nam po pravdi presudi. Ne budi pristrasan, i na najbolje nas rješenje uputi.');
INSERT INTO "bs_darassalam" VALUES(38,23,'38:23','Ovaj prijatelj moj ima devedeset i devet ovaca, a ja samo jednu, i on mi reče: ‘Daj ti nju meni!’ – i u prepirci me pobijedi.”');
INSERT INTO "bs_darassalam" VALUES(38,24,'38:24','“Učinio ti je, doista, krivo!”, Davud reče, “time što je tražio da tvoju ovcu doda ovcama svojim. Mnogi ortaci čine nepravdu jedni drugima, ne čine jedino oni koji vjeruju i rade dobra djela; a takvih je malo.” I Davud se uvjeri da smo Mi baš njega na kušnju stavili, pa oprost od Gospodara svog zamoli, pade licem na tlo i pokaja se.');
INSERT INTO "bs_darassalam" VALUES(38,25,'38:25','I Mi smo mu to oprostili, i on je, doista, blizak Nama i divno prebivalište ga čeka.');
INSERT INTO "bs_darassalam" VALUES(38,26,'38:26','O Davude, Mi smo te halifom na Zemlji učinili, zato sudi ljudima po pravdi i ne povodi se za strašću da te ne odvede s Allahovog puta. One koji skreću s Allahovog puta čeka teška patnja na onom svijetu zato što su zaboravljali na Dan u kojem će se račun polagati.');
INSERT INTO "bs_darassalam" VALUES(38,27,'38:27','Mi nismo uzalud stvorili nebo i Zemlju i ono što je između njih. Tako misle nevjernici, pa teško nevjernicima kad budu u Vatri!');
INSERT INTO "bs_darassalam" VALUES(38,28,'38:28','Zar ćemo postupiti s onima koji vjeruju i čine dobro kao s onima koji prave nered na Zemlji ili zar ćemo postupiti s onima koji se grijeha klone isto kao i s griješnicima?');
INSERT INTO "bs_darassalam" VALUES(38,29,'38:29','Knjiga koju ti objavljujemo blagoslovljena je da bi oni o riječima njezinim razmislili i da bi oni koji su razumom obdareni pouku primili.');
INSERT INTO "bs_darassalam" VALUES(38,30,'38:30','Mi smo Davudu poklonili Sulejmana, divan je on rob bio i mnogo se kajao!');
INSERT INTO "bs_darassalam" VALUES(38,31,'38:31','Kad pred njega u suton bijahu izvedeni punokrvni konji koji su na tri noge stajali, a četvrtom jedva zemlju doticali...');
INSERT INTO "bs_darassalam" VALUES(38,32,'38:32','on reče: “Umjesto da mislim na Gospodara svog, ja pokazujem ljubav prema blagu!”, i oni se izgubiše iz vida.');
INSERT INTO "bs_darassalam" VALUES(38,33,'38:33','"Vratite mi ih!", i on ih poče gladiti po nogama i vratovima.');
INSERT INTO "bs_darassalam" VALUES(38,34,'38:34','Mi smo Sulejmana u iskušenje doveli i na njegov prijesto tijelo bacili, pa se on pokaja.');
INSERT INTO "bs_darassalam" VALUES(38,35,'38:35','“Gospodaru moj”, rekao je, “oprosti mi i daruj mi vlast kakvu niko, osim mene, neće imati! Ti uistinu bogato daruješ!”');
INSERT INTO "bs_darassalam" VALUES(38,36,'38:36','I Mi smo dali da mu služe: vjetar – koji je prema zapovijedi njegovoj blago puhao onamo kuda je on htio...');
INSERT INTO "bs_darassalam" VALUES(38,37,'38:37','i šejtani, sve graditelji i gnjurci...');
INSERT INTO "bs_darassalam" VALUES(38,38,'38:38','i drugi u bukagije okovani.');
INSERT INTO "bs_darassalam" VALUES(38,39,'38:39','“Ovo je Naš dar, pa ti oslobodi ili zadrži, nećeš zbog toga odgovarati!”');
INSERT INTO "bs_darassalam" VALUES(38,40,'38:40','On je, doista, blizak Nama i čeka ga krasno prebivalište.');
INSERT INTO "bs_darassalam" VALUES(38,41,'38:41','I sjeti se roba Našeg Ejjuba, kada je Gospodaru svome zavapio: "Šejtan me pogodio nedaćama i patnjom!"');
INSERT INTO "bs_darassalam" VALUES(38,42,'38:42','“Udri nogom o zemlju – eto hladne vode za kupanje i piće!”');
INSERT INTO "bs_darassalam" VALUES(38,43,'38:43','I Mi smo mu iz milosti Naše čeljad njegovu darovali i još toliko uz njih, da bude pouka za one koji imaju pameti.');
INSERT INTO "bs_darassalam" VALUES(38,44,'38:44','"I uzmi rukom svojom snop i njime udari, samo zakletvu ne prekrši." Mi smo, zaista, našli da je on strpljiv; divan je rob on bio i stalno se Allahu pokajnički obraćao!');
INSERT INTO "bs_darassalam" VALUES(38,45,'38:45','I sjeti se robova Naših Ibrahima i Ishaka i Jakuba, sve u vjeri čvrstih i dalekovidnih.');
INSERT INTO "bs_darassalam" VALUES(38,46,'38:46','Mi ih posebno obdarismo vrlinom jednom: da im je uvijek bio na umu onaj svijet.');
INSERT INTO "bs_darassalam" VALUES(38,47,'38:47','I oni su, zaista, u Nas od onih odabranih dobrih ljudi.');
INSERT INTO "bs_darassalam" VALUES(38,48,'38:48','I sjeti se Ismaila i El-Jese''a i Zulkifla, svi su oni bili čestiti.');
INSERT INTO "bs_darassalam" VALUES(38,49,'38:49','Ovo je lijep spomen! A one koji se grijeha budu klonili čeka divno prebivalište...');
INSERT INTO "bs_darassalam" VALUES(38,50,'38:50','edenski vrtovi, čije će kapije biti za njih otvorene...');
INSERT INTO "bs_darassalam" VALUES(38,51,'38:51','u kojima će se odmarati i raznovrsno voće i piće tražiti.');
INSERT INTO "bs_darassalam" VALUES(38,52,'38:52','Pored njih će biti hurije, istih godina, koje će preda se gledati.');
INSERT INTO "bs_darassalam" VALUES(38,53,'38:53','“To je ono što vam se obećava za Dan u kome će se račun polagati...');
INSERT INTO "bs_darassalam" VALUES(38,54,'38:54','to će, doista, blagodat Naša biti, koja nikad neće prestajati!”');
INSERT INTO "bs_darassalam" VALUES(38,55,'38:55','Eto toliko! A one koji budu zlo činili čeka najgore prebivalište...');
INSERT INTO "bs_darassalam" VALUES(38,56,'38:56','Džehennem, u kojem će gorjeti – a grozne li postelje!');
INSERT INTO "bs_darassalam" VALUES(38,57,'38:57','Eto toliko! Pa neka okušaju vodu ključalu i kapljevinu smrdljivu...');
INSERT INTO "bs_darassalam" VALUES(38,58,'38:58','i druge slične ovima muke, mnogostruke.');
INSERT INTO "bs_darassalam" VALUES(38,59,'38:59','“Ova će gomila zajedno s vama tiskajući se u Džehennem ući!” “Ne bilo im prostrano! U vatri će oni, doista, gorjeti!”');
INSERT INTO "bs_darassalam" VALUES(38,60,'38:60','“Vama ne bilo prostrano!”, reći će oni. “Vi ste nam ovo pripremili, a grozna li boravišta!”');
INSERT INTO "bs_darassalam" VALUES(38,61,'38:61','“Gospodaru naš”, reći će, “udvostruči patnju u Vatri onima koji su nam ovo priredili!”');
INSERT INTO "bs_darassalam" VALUES(38,62,'38:62','I govorit će: “Zašto ne vidimo ljude koje smo u zle ubrajali...');
INSERT INTO "bs_darassalam" VALUES(38,63,'38:63','i koje smo ismijavali? Da nam se nisu iz vida izgubili?”');
INSERT INTO "bs_darassalam" VALUES(38,64,'38:64','Istina je, sigurno, da će se stanovnici Džehennema među sobom raspravljati.');
INSERT INTO "bs_darassalam" VALUES(38,65,'38:65','“Ja sam opominjatelj samo”, ti reci, “i nema istinskog božanstva osim Allaha, Jedinog i Moćnog...');
INSERT INTO "bs_darassalam" VALUES(38,66,'38:66','Gospodar nebesa i Zemlje i onog što je između njih, Silnog, Onog Koji prašta.”');
INSERT INTO "bs_darassalam" VALUES(38,67,'38:67','Reci: “Ovo je vijest velika...');
INSERT INTO "bs_darassalam" VALUES(38,68,'38:68','a vi od nje glave okrećete.');
INSERT INTO "bs_darassalam" VALUES(38,69,'38:69','Ja nisam ništa znao o melekima uzvišenim kad su se raspravljali...');
INSERT INTO "bs_darassalam" VALUES(38,70,'38:70','meni se objavljuje samo da jasno opominjem.”');
INSERT INTO "bs_darassalam" VALUES(38,71,'38:71','I kad je melekima Gospodar tvoj rekao: “Stvorit ću čovjeka od ilovače...');
INSERT INTO "bs_darassalam" VALUES(38,72,'38:72','Pa kad mu savršen oblik dadnem i dušu u njega udahnem, vi mu se poklonite!”');
INSERT INTO "bs_darassalam" VALUES(38,73,'38:73','Meleki su se, svi do posljednjeg, zajedno poklonili...');
INSERT INTO "bs_darassalam" VALUES(38,74,'38:74','osim Iblisa – on se uzoholio i postao nevjernik.');
INSERT INTO "bs_darassalam" VALUES(38,75,'38:75','“O Iblisu”, rekao je On, “šta te navelo da se ne pokloniš onom koga sam dvjema rukama Svojim stvorio? Jesi li se uzoholio ili misliš da si uzvišen?”');
INSERT INTO "bs_darassalam" VALUES(38,76,'38:76','“Bolji sam od njega”, rekao je on, “mene si stvorio od vatre a njega od ilovače.”');
INSERT INTO "bs_darassalam" VALUES(38,77,'38:77','“E, izlazi iz Dženneta!”, reče On. “Proklet da si...');
INSERT INTO "bs_darassalam" VALUES(38,78,'38:78','Moje će te prokletstvo do Sudnjeg dana pratiti!”');
INSERT INTO "bs_darassalam" VALUES(38,79,'38:79','“Gospodaru moj”, reče on, “daj mi vremena do Dana kad će oni oživljeni biti!”');
INSERT INTO "bs_darassalam" VALUES(38,80,'38:80','“Dajem ti”, reče On...');
INSERT INTO "bs_darassalam" VALUES(38,81,'38:81','“do Dana već određenog.”');
INSERT INTO "bs_darassalam" VALUES(38,82,'38:82','“E tako mi dostojanstva Tvog”, reče, “sigurno ću ih sve na stranputicu navesti...');
INSERT INTO "bs_darassalam" VALUES(38,83,'38:83','osim Tvojih među njima robova iskrenih!”');
INSERT INTO "bs_darassalam" VALUES(38,84,'38:84','“Istinom se kunem i istinu Ja govorim”, Allah reče...');
INSERT INTO "bs_darassalam" VALUES(38,85,'38:85','“sigurno ću svima, tobom i onima koji se budu poveli za tobom, Džehennem napuniti!”');
INSERT INTO "bs_darassalam" VALUES(38,86,'38:86','Reci: “Ne tražim ja od vas za ovo nikakvu nagradu i ja nisam izvještačen...');
INSERT INTO "bs_darassalam" VALUES(38,87,'38:87','Kur’an je doista svijetu cijelom opomena...');
INSERT INTO "bs_darassalam" VALUES(38,88,'38:88','i vi ćete uskoro saznati njegovu poruku!”');
INSERT INTO "bs_darassalam" VALUES(39,1,'39:1','Knjigu objavljuje Allah, Silni i Mudri!');
INSERT INTO "bs_darassalam" VALUES(39,2,'39:2','Mi ti, doista, objavljujemo Knjigu, pravu istinu, zato se klanjaj samo Allahu iskreno Mu ispovijedajući vjeru!');
INSERT INTO "bs_darassalam" VALUES(39,3,'39:3','Iskreno ispovijedanje vjere dug je prema Allahu! A oni koji pored Njega uzimaju zaštitnike govoreći: "Mi ih obožavamo samo zato da bi nas što više Allahu približili" Allah će njima, zaista, presuditi o onome u čemu su se oni razilazili. Allah nikako neće uputiti na Pravi put onoga ko je lažljivac i nevjernik.');
INSERT INTO "bs_darassalam" VALUES(39,4,'39:4','Da je Allah htio imati dijete, izabrao bi, između onih koje je stvorio, onog koga bi On htio. Hvaljen neka je On – On je Allah, Jedini i Svemoćni!');
INSERT INTO "bs_darassalam" VALUES(39,5,'39:5','Nebesa i Zemlju je s ciljem stvorio. On noću zavija dan i danom zavija noć, On je Sunce i Mjesec potčinio, svako se kreće do roka određenog. On je Silni, On mnogo prašta!');
INSERT INTO "bs_darassalam" VALUES(39,6,'39:6','On vas od jednog čovjeka stvara – a od njega je drúgu njegovu stvorio – i On vam je dao osam vrsta stoke. On vas stvara u utrobama matera vaših, dajući vam likove, jedan za drugim, u tri tmine. To vam je, eto, Allah, Gospodar vaš, Njegova je vlast, nema boga osim Njega – pa kuda se onda odmećete?');
INSERT INTO "bs_darassalam" VALUES(39,7,'39:7','Ako vi budete nevjernici – pa Allah od vas ne zavisi, ali On nije zadovoljan ako su robovi Njegovi nevjernici, a zadovoljan je vama ako budete zahvalni. A nijedan grešnik neće nositi grijehe drugoga! Poslije ćete se Gospodaru svom vratiti, pa će vas On o onom šta ste radili obavijestiti, jer On dobro zna svačije misli.');
INSERT INTO "bs_darassalam" VALUES(39,8,'39:8','Kad čovjeka nevolja snađe, Gospodaru svom se moli, Njemu se obraća, a onda, pošto mu Allah milost svoju daruje, zaboravi Onog Kome se prije molio, i druge Njemu jednakim smatra, da bi s puta Njegova na stranputicu odvodio. Reci: “Uživaj neko vrijeme u nevjerovanju svom, bit ćeš, sigurno, stanovnik u Vatri!”');
INSERT INTO "bs_darassalam" VALUES(39,9,'39:9','Zar je takav onaj koji u noćnim satima u molitvi vrijeme provodi, padajući licem na tlo i stojeći, strahujući od onog svijeta i nadajući se milosti Gospodara svog? Reci: “Zar su isti oni koji znaju i oni koji ne znaju? Samo oni koji pameti imaju pouku primaju!”');
INSERT INTO "bs_darassalam" VALUES(39,10,'39:10','Reci: “O robovi Moji koji vjerujete, bojte se Gospodara svog! One koji na ovom svijetu dobra djela budu činili čeka nagrada, a Allahova zemlja je prostrana – samo oni koji budu strpljivi bit će bez obračuna nagrađeni.”');
INSERT INTO "bs_darassalam" VALUES(39,11,'39:11','Reci: “Meni se naređuje da Allaha obožavam, iskreno Mu ispovijedajući vjeru...');
INSERT INTO "bs_darassalam" VALUES(39,12,'39:12','i naređuje mi se da budem prvi musliman.”');
INSERT INTO "bs_darassalam" VALUES(39,13,'39:13','Reci: “Ja se bojim patnje na Velikom danu, ako budem Gospodaru svom neposlušan.”');
INSERT INTO "bs_darassalam" VALUES(39,14,'39:14','Reci: “Samo se Allahu klanjam, iskreno Mu ispovijedajući vjeru svoju...');
INSERT INTO "bs_darassalam" VALUES(39,15,'39:15','a vi se, pored Njega, klanjajte kome hoćete!” Reci: “Stradat će, uistinu, oni koji na Sudnjem danu izgube i sebe i porodice svoje. Eto, to je pravo stradanje!”');
INSERT INTO "bs_darassalam" VALUES(39,16,'39:16','Nad njima će biti naslage vatre, a i ispod njih naslage – time Allah straši robove Svoje. O robovi Moji, bojte se Mene!');
INSERT INTO "bs_darassalam" VALUES(39,17,'39:17','Za one koji izbjegavaju da se kumirima klanjaju i koji se Allahu obraćaju – njima su namijenjene radosne vijesti, zato obraduj robove Moje...');
INSERT INTO "bs_darassalam" VALUES(39,18,'39:18','koji Kur’an slušaju i slijede ono najljepše u njemu; njima je Allah na Pravi put ukazao i oni su pametni.');
INSERT INTO "bs_darassalam" VALUES(39,19,'39:19','Zar ti da spasiš onog koji je zaslužio kaznu, onog koji će biti u Vatri?');
INSERT INTO "bs_darassalam" VALUES(39,20,'39:20','A one koji se Gospodara svog boje čekaju odaje, sve jedne iznad drugih sagrađene, ispod kojih rijeke teku – obećanje je Allahovo, a Allah neće obećanje prekršiti.');
INSERT INTO "bs_darassalam" VALUES(39,21,'39:21','Zar ne vidiš da Allah spušta s neba kišu pa je u izvore u zemlji razvodi, a onda pomoću nje raznobojno bilje izvodi, zatim se ono osuši i ti ga vidiš požutjela, i najzad ga skrši? To je, doista, pouka za one koji su pametni.');
INSERT INTO "bs_darassalam" VALUES(39,22,'39:22','Zar je isti onaj čije je srce Allah učinio sklonim islamu, pa on slijedi svjetlo Gospodara svog? Teško onima čija su srca neosjetljiva kad se spomene Allah, oni su u pravoj zabludi!');
INSERT INTO "bs_darassalam" VALUES(39,23,'39:23','Allah objavljuje najljepši govor, Knjigu sličnu po smislu, čije se poruke ponavljaju, zbog kojih podilazi jeza one koji se Gospodara svog boje, a kad se spomene ime Allahovo, kože njihove i srca njihova se smiruju. Ona je Allahov Pravi put na koji On ukazuje onom kome On hoće. A onog koga Allah ostavi u zabludi niko na Pravi put neće moći uputiti.');
INSERT INTO "bs_darassalam" VALUES(39,24,'39:24','Zar je isti onaj koji će se na Sudnjem danu licem svojim zaklanjati od strahovite patnje? “Trpite ono što ste zaslužili!”, mnogobošcima će se reći.');
INSERT INTO "bs_darassalam" VALUES(39,25,'39:25','Oni prije njih koji su poricali, pa ih je stigla kazna odakle nisu očekivali.');
INSERT INTO "bs_darassalam" VALUES(39,26,'39:26','Allah je dao da osjete poniženje u životu na ovom svijetu, a patnja na Ahiretu bit će, zaista, još gora – kad bi oni samo znali!');
INSERT INTO "bs_darassalam" VALUES(39,27,'39:27','Mi u ovom Kur’anu navodimo ljudima svakojake primjere da bi pouku primili...');
INSERT INTO "bs_darassalam" VALUES(39,28,'39:28','u Kur''anu na arapskom jeziku, u kome nema nikakve proturječnosti, da bi se čuvali.');
INSERT INTO "bs_darassalam" VALUES(39,29,'39:29','Allah navodi kao primjer čovjeka koji je u vlasti ortakā oko koga se oni otimaju i čovjeka koji je u vlasti samo jednog čovjeka – je li položaj njih dvojice isti? Hvaljen neka je Allah, nije, ali većina njih ne zna.');
INSERT INTO "bs_darassalam" VALUES(39,30,'39:30','Ti ćeš, zacijelo, umrijeti, a i oni će, također, pomrijeti...');
INSERT INTO "bs_darassalam" VALUES(39,31,'39:31','i poslije, na Sudnjem danu, pred Gospodarom svojim ćete se jedan s drugim prepirati.');
INSERT INTO "bs_darassalam" VALUES(39,32,'39:32','Ima li nepravednijeg od onog koji o Allahu govori laži i smatra da je laž istina kad mu ona dolazi? Zar u Džehennemu prebivalište za nevjernike neće biti?');
INSERT INTO "bs_darassalam" VALUES(39,33,'39:33','A onaj koji donosi istinu i oni koji u nju vjeruju, oni su bogobojazni...');
INSERT INTO "bs_darassalam" VALUES(39,34,'39:34','sve što zažele u Gospodara svog će naći – to će biti nagrada onima koji su dobra djela činili...');
INSERT INTO "bs_darassalam" VALUES(39,35,'39:35','da bi Allah preko ružnih postupaka njihovih prešao, i da bi ih za lijepa djela koja su radili nagradio.');
INSERT INTO "bs_darassalam" VALUES(39,36,'39:36','Zar Allah sam nije dovoljan robu Svom? A oni te plaše onima kojima se, pored Njega, klanjaju. Onog koga Allah odvede u zabludu niko ne može na Pravi put uputiti...');
INSERT INTO "bs_darassalam" VALUES(39,37,'39:37','a onog koga On na Pravi put uputi niko ne može u zabludu odvesti. Zar Allah nije silan i zar se ne osvećuje?');
INSERT INTO "bs_darassalam" VALUES(39,38,'39:38','Ako ih upitaš ko je stvorio nebesa i Zemlju, sigurno će reći: “Allah!”, a ti reci: “Mislite li vi da bi oni kojima se, pored Allaha, klanjate mogli otkloniti štetu, ako Allah hoće da im je učini ili da bi mogli zadržati milost Njegovu, ako On hoće da im je podari?” Reci: “Meni je dovoljan Allah, u Njega se pouzdaju oni koji se pouzdavaju.”');
INSERT INTO "bs_darassalam" VALUES(39,39,'39:39','Reci: “O narode moj, ti postupaj onako kako postupaš, a i ja ću postupati, pa ćete znati...');
INSERT INTO "bs_darassalam" VALUES(39,40,'39:40','koga će stići patnja koja će ga poniziti, i koga će patnja vječna snaći.”i saznat ćete, zaista, kome će doći kazna koja će ga poniziti i koga će vječna patnja snaći."');
INSERT INTO "bs_darassalam" VALUES(39,41,'39:41','Mi ti objavljujemo Knjigu zbog svih ljudi – samu istinu. Onaj ko bude išao Pravim putem sebi će koristiti, a onaj ko bude išao stranputicom sebi će nauditi, ti nisi njima tutor.');
INSERT INTO "bs_darassalam" VALUES(39,42,'39:42','Allah uzima duše u času njihove smrti, a i onih koji spavaju, a koji još umrli nisu, pa zadržava one kojima je odredio da umru, a ostavlja one druge do roka određenog. To su, zaista, dokazi za one koji razmišljaju.');
INSERT INTO "bs_darassalam" VALUES(39,43,'39:43','Oni bez Allahova odobrenja posrednike uzimaju. Reci: “Zar i onda kad su bez ikakve moći i kad ništa ne razumiju!”');
INSERT INTO "bs_darassalam" VALUES(39,44,'39:44','Reci: “Niko ne može bez Njegove volje posredovati, vlast na nebesima i na Zemlji Njegova je, a poslije ćete se Njemu vratiti.”');
INSERT INTO "bs_darassalam" VALUES(39,45,'39:45','Kad se Allah samo spomene, grče se srca onih koji u onaj svijet ne vjeruju, a kad se spomenu oni kojima se oni pored Njega klanjaju, odjednom ih radost obuzme.');
INSERT INTO "bs_darassalam" VALUES(39,46,'39:46','Reci: “Allahu, Stvoritelju nebesa i Zemlje, Ti Koji znaš nevidljivi i vidljivi svijet, Ti ćeš robovima Svojim presuditi u onom oko čega su se razilazili!”');
INSERT INTO "bs_darassalam" VALUES(39,47,'39:47','Kad bi mnogobošci imali sve što je na Zemlji, i još toliko, na Sudnjem danu oni bi sve to dali da bi se teške patnje iskupili. A Allah će ih kazniti kaznom kakvu nisu mogli ni naslutiti...');
INSERT INTO "bs_darassalam" VALUES(39,48,'39:48','i predočit će im se hrđava djela njihova koja su radili i snaći će ih baš ono čemu su se izrugivali.');
INSERT INTO "bs_darassalam" VALUES(39,49,'39:49','Kad čovjeka kakva nevolja snađe, Nama se moli; a kad mu Mi poslije blagodat pružimo, onda govori: “Ovo mi je dato zato što sam to zaslužio.” A nije tako, to je samo kušnja, ali većina njih ne zna.');
INSERT INTO "bs_darassalam" VALUES(39,50,'39:50','Tako su govorili i oni prije njih, pa im nije koristilo ono što su bili stekli...');
INSERT INTO "bs_darassalam" VALUES(39,51,'39:51','i stigla ih je kazna za ono što su radili. A i one među ovima koji nepravdu čine – stići će kazna za ono što čine, oni neće umaći.');
INSERT INTO "bs_darassalam" VALUES(39,52,'39:52','Kako ne znaju da Allah daje obilje onom kome hoće, i da ga uskraćuje – to su, zaista, pouke narodu koji vjeruje.');
INSERT INTO "bs_darassalam" VALUES(39,53,'39:53','Reci: “O robovi Moji koji ste se prema sebi ogriješili, ne gubite nadu u Allahovu milost! Allah, sigurno, sve grijehe oprašta. On, doista, mnogo prašta i On je milostiv.”');
INSERT INTO "bs_darassalam" VALUES(39,54,'39:54','I povratite se Gospodaru svom i pokorite Mu se prije nego što vam kazna dođe – poslije vam niko neće u pomoć priskočiti.');
INSERT INTO "bs_darassalam" VALUES(39,55,'39:55','I slijedite ono najljepše, ono što vam Gospodar vaš objavljuje, prije nego što vam iznenada kazna dođe, za čiji dolazak nećete znati...');
INSERT INTO "bs_darassalam" VALUES(39,56,'39:56','da čovjek ne bi uzviknuo: “Teško meni, koliko sam samo dužnosti prema Allahu propustio, čak sam se i izrugivao!”');
INSERT INTO "bs_darassalam" VALUES(39,57,'39:57','Ili da ne bi rekao: “Da me je Allah Pravim putem uputio, sigurno bih se Njegove kazne sačuvao!”');
INSERT INTO "bs_darassalam" VALUES(39,58,'39:58','Ili da ne bi rekao kad doživi patnju: “Da mi se samo vratiti – dobra djela bih činio!”');
INSERT INTO "bs_darassalam" VALUES(39,59,'39:59','“Nikad! Dolazile su ti pouke Moje, pa si ih poricao i oholio se, i nevjernik si bio.”');
INSERT INTO "bs_darassalam" VALUES(39,60,'39:60','Na Sudnjem danu vidjet ćeš pocrnjela lica onih koji su o Allahu laži govorili. A zar u Džehennemu neće biti boravište oholih?');
INSERT INTO "bs_darassalam" VALUES(39,61,'39:61','Allah će spasiti one koji su njegova naređenja izvršavali, a Njegovih se zabrana klonili, i oni će postići ono što su željeli; zlo ih se neće doticati i oni neće tugovati.');
INSERT INTO "bs_darassalam" VALUES(39,62,'39:62','Allah je Stvoritelj svega i On upravlja svim...');
INSERT INTO "bs_darassalam" VALUES(39,63,'39:63','u Njega su ključevi riznica nebesa i Zemlje! A oni koji u Allahove dokaze ne vjeruju, oni će biti izgubljeni!');
INSERT INTO "bs_darassalam" VALUES(39,64,'39:64','Reci: “Zar od mene tražite da se nekom drugom, osim Allahu, klanjam, o neznalice!?”');
INSERT INTO "bs_darassalam" VALUES(39,65,'39:65','A tebi, i onima prije tebe objavljeno je: “Ako budeš druge Allahu ravnim smatrao, tvoja djela će sigurno propasti, a ti ćeš izgubljen biti.”');
INSERT INTO "bs_darassalam" VALUES(39,66,'39:66','Nego, Allahu se jedino klanjaj i budi zahvalan!');
INSERT INTO "bs_darassalam" VALUES(39,67,'39:67','Oni ne veličaju Allaha onako kako Ga treba veličati, a čitava Zemlja će na Sudnjem danu u ruci Njegovoj biti, a nebesa će u desnici Njegovoj smotana ostati. Hvaljen neka je On i vrlo visoko iznad onih koje Njemu smatraju ravnim!');
INSERT INTO "bs_darassalam" VALUES(39,68,'39:68','I u rog će se puhnuti, i umrijet će oni na nebesima i oni na Zemlji, ostat će samo oni koje bude Allah odabrao. Poslije će se u rog po drugi put puhnuti i oni će, odjednom, ustati i čekati.');
INSERT INTO "bs_darassalam" VALUES(39,69,'39:69','I Zemlja će svjetlošću Gospodara svog zasjati i Knjiga će se postaviti, vjerovjesnici i svjedoci će se dovesti, i po pravdi će im se svima presuditi, nikom se neće nepravda učiniti...');
INSERT INTO "bs_darassalam" VALUES(39,70,'39:70','svako će dobiti ono što je zaslužio, jer On dobro zna šta je ko radio.');
INSERT INTO "bs_darassalam" VALUES(39,71,'39:71','Oni koji nisu vjerovali u gomilama će u Džehennem biti natjerani. I kad do njega dođu, kapije njegove će se pootvarati i čuvari njegovi će ih upitati: “Zar vam nisu dolazili vaši poslanici, koji su vam ajete Gospodara vašeg kazivali i opominjali vas da ćete ovaj vaš Dan doživjeti?” “Jesu”, reći će oni, “ali još je davno određeno da će nevjernici kažnjeni biti.”');
INSERT INTO "bs_darassalam" VALUES(39,72,'39:72','I reći će se: “Ulazite na džehennemske kapije, vječno ćete u njemu boraviti!” Grozna li prebivališta onima koji su se oholili!');
INSERT INTO "bs_darassalam" VALUES(39,73,'39:73','A oni koji su se Gospodara svog bojali u povorkama će u Džennet biti povedeni. I kad do njega dođu – a kapije njegove već širom otvorene – čuvari njegovi će im reći: “Mir vama, od grijeha ste čisti, zato uđite u njega, u njemu ćete vječno boraviti!”');
INSERT INTO "bs_darassalam" VALUES(39,74,'39:74','I oni će reći: “Hvala Allahu Koji nam je obećanje Svoje ispunio i u Džennetu nam mjesto darovao, da se u njemu krećemo kuda hoćemo!” Divne li nagrade onima koji su se trudili!');
INSERT INTO "bs_darassalam" VALUES(39,75,'39:75','I vidjet ćeš meleke kako Prijesto okružuju, veličajući i hvaleći Gospodara svog. I svima će se po pravdi presuditi i reći će se: “Hvaljen neka je Allah, Gospodar svjetova!”');
INSERT INTO "bs_darassalam" VALUES(40,1,'40:1','Ha Mīm.');
INSERT INTO "bs_darassalam" VALUES(40,2,'40:2','Knjigu objavljuje Allah, Silni i Sveznajući...');
INSERT INTO "bs_darassalam" VALUES(40,3,'40:3','Koji oprašta grijehe i prima pokajanje, Koji strahovito kažnjava i obilno nagrađuje. Drugog boga osim Njega nema, Njemu se sve vraća.');
INSERT INTO "bs_darassalam" VALUES(40,4,'40:4','O Allahovim ajetima raspravljaju samo oni koji ne vjeruju, pa neka te ne obmanjuje to što se oni po svijetu kreću.');
INSERT INTO "bs_darassalam" VALUES(40,5,'40:5','I Nuhov je narod prije njih poricao, a i poslije njega oni koji su se bili protiv poslanikā urotili. Svaki narod nastojao je domoći se svog poslanika i trudio se neistinom ugušiti istinu, pa sam ga Ja kažnjavao – a kakva je bila kazna Moja!');
INSERT INTO "bs_darassalam" VALUES(40,6,'40:6','I tako će se riječ Gospodara tvog ispuniti da će oni koji nisu htjeli vjerovati – stanovnici u vatri biti.');
INSERT INTO "bs_darassalam" VALUES(40,7,'40:7','Meleki koji drže Prijesto i oni koji su oko njega veličaju i hvale Gospodara svog i vjeruju u Njega i mole se da budu oprošteni grijesi vjernicima: “Gospodaru naš, Ti sve obuhvataš milošću i znanjem; zato oprosti onima koji su se pokajali i koji slijede Tvoj put i sačuvaj ih patnje u Vatri!');
INSERT INTO "bs_darassalam" VALUES(40,8,'40:8','Gospodaru naš, uvedi ih u edenske vrtove, koje si im obećao, i pretke njihove i žene njihove i potomstvo njihovo – one koji su bili dobri. – Ti si, uistinu, silan i mudar.');
INSERT INTO "bs_darassalam" VALUES(40,9,'40:9','I poštedi ih kazne zbog ružnih djela, jer koga Ti tog Dana poštediš kazne zbog ružnih djela – Ti si mu se smilovao, a to će, zaista, veliki uspjeh biti!”');
INSERT INTO "bs_darassalam" VALUES(40,10,'40:10','Onima koji nisu vjerovali doviknut će se: “Allahova odvratnost prema vama kad ste, pozivani da vjerujete, a ostali nevjernici – bila je, doista, veća od vaše odvratnosti sad prema sebi.”');
INSERT INTO "bs_darassalam" VALUES(40,11,'40:11','“Gospodaru naš”, reći će oni, “dva si nas puta usmrtio i dva si nas puta oživio. Mi priznajemo grijehe naše, pa ima li ikakva načina da se izađe?”');
INSERT INTO "bs_darassalam" VALUES(40,12,'40:12','“To vam je zato što niste vjerovali kad se pozivalo Allahu Jedinom, a vjerovali ste ako bi Mu se neko drugi smatrao jednakim! Odluka pripada jedino Allahu, Uzvišenom i Velikom.”');
INSERT INTO "bs_darassalam" VALUES(40,13,'40:13','On vam pokazuje znamenja Svoja i spušta vam opskrbu s neba, a pouku će prihvatiti samo onaj koji se Njemu obraća.');
INSERT INTO "bs_darassalam" VALUES(40,14,'40:14','Klanjate se zato Allahu, iskreno Mu ispovijedajući vjeru – pa makar to nevjernicima bilo mrsko!');
INSERT INTO "bs_darassalam" VALUES(40,15,'40:15','On je vlasnik visokih stupnjeva, Gospodar Arša, Koji šalje Objavu, riječi Svoje, kome hoće od robova Svojih da upozori na Dan susreta međusobnog...');
INSERT INTO "bs_darassalam" VALUES(40,16,'40:16','na Dan kad će se oni pojaviti, kad Allahu neće o njima ništa skriveno biti. “Ko će imati vlast tog Dana? Allah, Jedini i Svemoćni!”');
INSERT INTO "bs_darassalam" VALUES(40,17,'40:17','Svaki će čovjek tog Dana prema zasluzi kažnjen ili nagrađen biti. Tog Dana neće biti nepravde! Allah će, zaista, brzo obračunati.');
INSERT INTO "bs_darassalam" VALUES(40,18,'40:18','I upozori ih na bliski Sudnji dan kad će srca do grkljana doprijeti i popriječiti se, kad nevjernici ni prisna prijatelja ni posrednika neće imati koji će uslišan biti.');
INSERT INTO "bs_darassalam" VALUES(40,19,'40:19','On zna poglede koji kriomice u ono što je zabranjeno gledaju, a i ono što grudi kriju.');
INSERT INTO "bs_darassalam" VALUES(40,20,'40:20','Allah će po pravdi presuditi, a oni kojima se oni, pored Njega, klanjaju neće ni po čemu suditi. Allah, zaista, sve čuje i sve vidi.');
INSERT INTO "bs_darassalam" VALUES(40,21,'40:21','Zašto oni ne putuju po svijetu pa da vide kako su završili oni prije njih? Bili su od njih moćniji i više su spomenika na Zemlji ostavili, ali ih je Allah, zbog grijehova njihovih, kaznio i niko ih od Allahove kazne nije odbranio.');
INSERT INTO "bs_darassalam" VALUES(40,22,'40:22','Allah ih je kaznio zato što su poricali jasne dokaze koje su im poslanici njihovi donosili, On je zaista Moćan, On strahovito kažnjava.');
INSERT INTO "bs_darassalam" VALUES(40,23,'40:23','Mi smo poslali Musaa, sa znamenjima Našim i dokazom jasnim...');
INSERT INTO "bs_darassalam" VALUES(40,24,'40:24','faraonu i Hamanu i Karunu, ali su oni rekli: “Čarobnjak i lažov!”');
INSERT INTO "bs_darassalam" VALUES(40,25,'40:25','A kad im je on donio istinu od Nas, rekli su: “Ubijajte mušku djecu onih koji vjeruju u ono što on govori, a ostavljajte u životu njihovu žensku djecu!” Ali, lukavstva nevjernikā uvijek su uzaludna.');
INSERT INTO "bs_darassalam" VALUES(40,26,'40:26','“Pustite vi mene”, reče faraon, “da ubijem Musaa, a on neka traži pomoć od Gospodara svog, jer se bojim da vam on vjeru vašu ne izmijeni ili da u zemlji nered ne izazove.”');
INSERT INTO "bs_darassalam" VALUES(40,27,'40:27','Musa reče: “Molim Gospodara svog i Gospodara vašeg da me zaštiti od svakog oholog koji ne vjeruje u Dan u kome će se račun polagati!”');
INSERT INTO "bs_darassalam" VALUES(40,28,'40:28','A jedan čovjek, vjernik, iz porodice faraonove, koji je krio vjerovanje svoje, reče: “Zar da ubijete čovjeka zato što govori: ‘Gospodar moj je Allah!’, onog koji vam je donio jasne dokaze od Gospodara vašeg? Ako je lažov, njegova će laž njemu nauditi, a ako govori istinu, onda će vas stići barem nešto od onog čime vam prijeti – jer Allah neće ukazati na Pravi put onom koji u zlu pretjeruje i koji mnogo laže.');
INSERT INTO "bs_darassalam" VALUES(40,29,'40:29','O narode moj, danas vama pripada vlast i vi ste na vrhovima u zemlji, ali ko će nas odbraniti od Allahove kazne ako nas ona stigne?” A faraon reče: “Savjetujem vam samo ono što mislim, a na Pravi put samo ću vas ja izvesti.”');
INSERT INTO "bs_darassalam" VALUES(40,30,'40:30','A onda onaj vjernik reče: “O narode moj, bojim se da i vas ne stigne ono što je stiglo narode koji su se protiv poslanika bili urotili...');
INSERT INTO "bs_darassalam" VALUES(40,31,'40:31','kao što je to bilo s Nuhovim narodom i Adom i Semudom i onima poslije njih. – A Allah nije nepravedan robovima Svojim.');
INSERT INTO "bs_darassalam" VALUES(40,32,'40:32','O narode moj, plašim se šta će biti s vama na Dan kad budete jedni druge dozivali...');
INSERT INTO "bs_darassalam" VALUES(40,33,'40:33','na Dan kad budete uzmičući bježali, kad vas od Allaha neće moći niko odbraniti. A onog koga Allah u zabludi ostavi, toga neće niko uputiti.');
INSERT INTO "bs_darassalam" VALUES(40,34,'40:34','Jusuf vam je, još davno, donio jasne dokaze, ali ste vi stalno sumnjali u ono što vam je donio. A kad je on umro, vi ste rekli: ‘Allah više neće poslije njega poslati poslanika!’ Eto tako Allah odvodi u zabludu svakog ko u zlu pretjeruje i sumnja...');
INSERT INTO "bs_darassalam" VALUES(40,35,'40:35','one koji o Allahovim znamenjima raspravljaju, iako im nikakav dokaz nije došao, pa izazivaju još veću Allahovu mržnju i mržnju vjernikā. Tako Allah pečati srce svakog oholog i nasilnog.”');
INSERT INTO "bs_darassalam" VALUES(40,36,'40:36','“O Hamane”, reče faraon, “sagradi mi jedan toranj ne bih li stigao do staza...');
INSERT INTO "bs_darassalam" VALUES(40,37,'40:37','staza nebeskih, ne bih li se popeo do Musaova Boga – a ja smatram da je on, zaista, lažac.” I eto tako su se faraonu njegova ružna djela učinila lijepim i on je bio odvraćen od Pravog puta, a lukavstvo se faraonovo završilo na njegovu štetu.');
INSERT INTO "bs_darassalam" VALUES(40,38,'40:38','I onda onaj vjernik reče: “O narode moj, ugledajte se na mene, ja ću vam na Pravi put ukazati!');
INSERT INTO "bs_darassalam" VALUES(40,39,'40:39','O narode moj, život na ovom svijetu samo je prolazno uživanje, a onaj je svijet, zaista, Kuća vječna.');
INSERT INTO "bs_darassalam" VALUES(40,40,'40:40','Ko učini zlo, bit će prema njemu kažnjen, a ko učini dobro – bio muškarac ili žena, a vjernik je – u Džennet će; u njemu će imati u obilju svega, bez računa.');
INSERT INTO "bs_darassalam" VALUES(40,41,'40:41','O narode moj, šta je ovo!? Ja vas pozivam da vas spasim, a vi mene pozivate u Vatru...');
INSERT INTO "bs_darassalam" VALUES(40,42,'40:42','pozivate me da ne vjerujem u Allaha i da prihvatim Njemu ravnim onog o kome baš ništa ne znam, a ja vas pozivam Silnom, Onom Koji mnogo prašta.');
INSERT INTO "bs_darassalam" VALUES(40,43,'40:43','Nema nimalo sumnje u to da se oni kojima me pozivate neće nikom ni na ovom ni na onom svijetu odazvati i da ćemo se Allahu vratiti i da će mnogobošci stanovnici u Ognju biti.');
INSERT INTO "bs_darassalam" VALUES(40,44,'40:44','Tad ćete se sigurno mojih riječi sjetiti! A ja Allahu prepuštam svoj slučaj. Allah, uistinu, robove Svoje vidi.”');
INSERT INTO "bs_darassalam" VALUES(40,45,'40:45','I Allah ga je sačuvao nevolje koju su mu oni snovali, a faraonove ljude zla kob zadesi.');
INSERT INTO "bs_darassalam" VALUES(40,46,'40:46','Oni će se ujutro i navečer u vatri pržiti. A kad nastupi Čas: “Uvedite faraonove ljude u patnju najtežu!”');
INSERT INTO "bs_darassalam" VALUES(40,47,'40:47','I kad se u vatri budu prepirali, pa oni koji su bili potlačeni reknu glavešinama svojim: “Mi smo se za vama povodili, možete li nas makar malo vatre osloboditi?”...');
INSERT INTO "bs_darassalam" VALUES(40,48,'40:48','onda će glavešine reći: “Evo nas, svi smo u njoj, jer Allah je presudio robovima Svojim.”');
INSERT INTO "bs_darassalam" VALUES(40,49,'40:49','I oni koji će u vatri biti govorit će stražarima džehennemskim: “Zamolite Gospodara svog da nam bar jedan dan patnju ublaži!”');
INSERT INTO "bs_darassalam" VALUES(40,50,'40:50','“A zar vam poslanici vaši nisu jasne dokaze donosili?”, upitat će oni. “Jesu!”, odgovorit će. “Molite onda vi!”, reći će oni. Ali će molba onih koji nisu vjerovali uzaludna biti.');
INSERT INTO "bs_darassalam" VALUES(40,51,'40:51','Mi ćemo, doista, pomoći poslanike Naše i vjernike u životu na ovom svijetu, a i na Dan kad se dignu svjedoci...');
INSERT INTO "bs_darassalam" VALUES(40,52,'40:52','na Dan kad krivcima pravdanja njihova neće od koristi biti – njih će prokletstvo i najgore prebivalište čekati.');
INSERT INTO "bs_darassalam" VALUES(40,53,'40:53','Mi smo Musau uputstvo dali i sinovima Israilovim u nasljedstvo Knjigu ostavili...');
INSERT INTO "bs_darassalam" VALUES(40,54,'40:54','da bude putokaz i opomena onima koji pameti budu imali.');
INSERT INTO "bs_darassalam" VALUES(40,55,'40:55','Zato ti budi strpljiv – Allahovo je obećanje istina – i moli da ti budu oprošteni tvoji grijesi, i Gospodara svog krajem i početkom dana veličaj i hvali!');
INSERT INTO "bs_darassalam" VALUES(40,56,'40:56','Oni koji o Allahovim znamenjima raspravljaju, iako im nikakav dokaz nije došao, u srcima njihovim je samo oholost koja ih neće dovesti do cilja željenog, zato moli od Allaha zaštitu, jer On uistinu sve čuje i sve vidi.');
INSERT INTO "bs_darassalam" VALUES(40,57,'40:57','Stvaranje nebesa i Zemlje sigurno je veće nego stvaranje roda ljudskog, ali većina ljudi ne zna.');
INSERT INTO "bs_darassalam" VALUES(40,58,'40:58','Nisu jednaki slijepac i onaj koji vidi, nisu jednaki vjernici koji dobra djela čine i zlotvori. – Kako vas malo prima pouku!');
INSERT INTO "bs_darassalam" VALUES(40,59,'40:59','Doći će Čas oživljenja, u to nema nimalo sumnje, ali većina ljudi neće da vjeruje.');
INSERT INTO "bs_darassalam" VALUES(40,60,'40:60','Gospodar je vaš rekao: “Pozovite Me i zamolite, Ja ću vam se odazvati! Oni koji iz oholosti neće da Mi se klanjaju – ući će, sigurno, u Džehennem poniženi.”');
INSERT INTO "bs_darassalam" VALUES(40,61,'40:61','Allah vam je dao noć da se u njoj odmarate, a dan da vidite. Allah je neizmjerno dobar prema ljudima, ali većina ljudi neće da zahvaljuje.');
INSERT INTO "bs_darassalam" VALUES(40,62,'40:62','To vam je, eto, Allah, Gospodar vaš, Stvoritelj svega, drugog boga osim Njega nema, pa kuda se onda odmećete?');
INSERT INTO "bs_darassalam" VALUES(40,63,'40:63','Tako su se odmetali i oni koji su Allahove dokaze poricali.');
INSERT INTO "bs_darassalam" VALUES(40,64,'40:64','Allah vam je učinio Zemlju prebivalištem, a nebo zdanjem, i On vam obličje daje i likove vaše čini lijepim, i jelima vas opskrbljuje ukusnim. To je Allah, Gospodar vaš, i neka je uzvišen Allah, Gospodar svjetova!');
INSERT INTO "bs_darassalam" VALUES(40,65,'40:65','On je Živi, nema Boga osim Njega, zato se samo Njemu klanjajte, iskreno Mu ispovijedajući vjeru: “Neka je hvaljen Allah, Gospodar svjetova!”');
INSERT INTO "bs_darassalam" VALUES(40,66,'40:66','Reci: “Meni je zabranjeno, o tome su mi došli jasni dokazi od Gospodara moga, da se klanjam onima kojima se, pored Allaha, klanjate, a naređeno mi je da se pokoravam Gospodaru svjetova.');
INSERT INTO "bs_darassalam" VALUES(40,67,'40:67','On vas stvara od zemlje, zatim od kapi sjemena, zatim od ugruška, zatim čini da se kao dojenčad rađate, i da do muževnog doba stignete, i da starci postanete – a neki od vas umiru prije – i da do suđenog časa poživite kako biste mogli shvatiti.');
INSERT INTO "bs_darassalam" VALUES(40,68,'40:68','Život i smrt Njegovo su djelo! A kad nešto odluči, samo za to rekne: ‘Budi!’ – i ono bude.”');
INSERT INTO "bs_darassalam" VALUES(40,69,'40:69','Zar ne vidiš kako se okreću oni koji o Allahovim dokazima raspravljaju?');
INSERT INTO "bs_darassalam" VALUES(40,70,'40:70','Oni koji poriču Knjigu i ono što smo slali po poslanicima – saznat će posljedice toga...');
INSERT INTO "bs_darassalam" VALUES(40,71,'40:71','kad s okovima o vratu i sindžirima budu vučeni...');
INSERT INTO "bs_darassalam" VALUES(40,72,'40:72','po ključaloj vodi, a zatim u vatri prženi...');
INSERT INTO "bs_darassalam" VALUES(40,73,'40:73','i potom upitani: “Gdje su oni koje ste Allahu pridruživali...');
INSERT INTO "bs_darassalam" VALUES(40,74,'40:74','a ne Allahu?” “Izgubili su nam se iz vida”, odgovorit će. “Ta mi se prije, ustvari, nismo nikom ni klanjali.” Eto tako Allah odvodi u zabludu nevjernike.');
INSERT INTO "bs_darassalam" VALUES(40,75,'40:75','To vam je zato što ste bez ikakva osnova na Zemlji bahati bili i što ste likovali.');
INSERT INTO "bs_darassalam" VALUES(40,76,'40:76','Ulazite kroz kapije Džehennema, u njemu ćete vječno ostati – a ružno je prebivalište oholih!"');
INSERT INTO "bs_darassalam" VALUES(40,77,'40:77','"Zato ti budi strpljiv, Allahova prijetnja će se, sigurno, obistiniti! Bilo da ti pokažemo dio onog čime im Mi prijetimo, bilo da ti prije život oduzmemo, Nama će se oni vratiti."');
INSERT INTO "bs_darassalam" VALUES(40,78,'40:78','I prije tebe smo poslanike slali, o nekima od njih smo ti kazivali, a o nekima ti nismo kazivali. I nijedan poslanik nije mogao učiniti nikakvo čudo bez Allahove volje. A kad se Allahova prijetnja ispuni, bit će presuđeno po pravdi i tad će nevaljali nastradati.');
INSERT INTO "bs_darassalam" VALUES(40,79,'40:79','Allah je za vas stvorio domaće životinje – da na nekima jašete, a neke da jedete.');
INSERT INTO "bs_darassalam" VALUES(40,80,'40:80','Vi od njih koristi imate i vi na njima za vas važne potrebe ostvarujete – i na njima i na lađama vi se prevozite.');
INSERT INTO "bs_darassalam" VALUES(40,81,'40:81','On vam pokazuje dokaze moći Svoje, pa koje od Allahovih dokaza ne priznajete?');
INSERT INTO "bs_darassalam" VALUES(40,82,'40:82','Zašto oni ne putuju po svijetu, pa da vide kako su završili oni prije njih? Oni su bili od njih mnogobrojniji i jači i više su spomenika na Zemlji ostavili, i ništa im nije koristilo ono što su stekli.');
INSERT INTO "bs_darassalam" VALUES(40,83,'40:83','Kad su im poslanici njihovi jasne dokaze donosili, oni su se znanjem koje su imali dičili i snašlo ih je ono čemu su se stalno rugali.');
INSERT INTO "bs_darassalam" VALUES(40,84,'40:84','A kad bi kaznu Našu doživjeli, onda bi oni govorili: “Mi vjerujemo u Allaha, u Njega Jedinog, a odričemo se onih koje smo Njemu ravnim smatrali!”');
INSERT INTO "bs_darassalam" VALUES(40,85,'40:85','Ali im vjerovanje njihovo, kad bi kaznu Našu doživjeli, ne bi nimalo bilo od koristi, prema Allahovu zakonu koji je vrijedio za sve robove Njegove koji su bili i nestali – i tad bi nevjernici stradali.');
INSERT INTO "bs_darassalam" VALUES(41,1,'41:1','Ha Mīm.');
INSERT INTO "bs_darassalam" VALUES(41,2,'41:2','Objava je od Milostivog, Samilosnog...');
INSERT INTO "bs_darassalam" VALUES(41,3,'41:3','Knjiga čiji su ajeti jasno izloženi, Kur’an na arapskom jeziku za ljude koji znaju...');
INSERT INTO "bs_darassalam" VALUES(41,4,'41:4','vjesnik radosnih vijesti i opomena – pa opet većina njih glavu okreće, neće ni da čuje.');
INSERT INTO "bs_darassalam" VALUES(41,5,'41:5','“Srca su naša”, govore oni, “pod pokrivačima, daleka od onog čemu nas ti pozivaš, i mi smo gluhi za to, a između nas i tebe je pregrada, pa ti radi, i mi ćemo raditi.”');
INSERT INTO "bs_darassalam" VALUES(41,6,'41:6','Reci: “Ja sam čovjek kao i vi, samo meni se objavljuje da je vaš Bog samo jedan Bog, zato se Njemu iskreno klanjate i od Njega oprosta tražite! A teško onima koji Njemu druge ravnim smatraju...');
INSERT INTO "bs_darassalam" VALUES(41,7,'41:7','koji zekat ne daju i koji u onaj svijet ne vjeruju!');
INSERT INTO "bs_darassalam" VALUES(41,8,'41:8','One koji vjeruju i dobra djela čine zbilja čeka nagrada neprekidna.”');
INSERT INTO "bs_darassalam" VALUES(41,9,'41:9','Reci: “Zar, zaista, nećete da vjerujete u Onog Koji je u dva dana Zemlju stvorio – i još Mu druge ravnim smatrate? To je Gospodar svjetova!”');
INSERT INTO "bs_darassalam" VALUES(41,10,'41:10','On je nepomična brda po njoj stvorio i blagoslovljenom je učinio i proizvode njezine na njoj odredio, sve to u četiri dana – ovo je objašnjenje za one koji pitaju...');
INSERT INTO "bs_darassalam" VALUES(41,11,'41:11','zatim se nebeskim visinama uputio dok je nebo još maglina bilo, pa njemu i Zemlji rekao: “Pojavite se milom ili silom!” “Pojavljujemo se drage volje!”, odgovorili su...');
INSERT INTO "bs_darassalam" VALUES(41,12,'41:12','pa ih u dva dana, kao sedam nebesa, stvorio, i odredio šta će se u svakom nebu nalaziti. A nebo najbliže sjajnim smo zvijezdama ukrasili i nad njim Mi bdijemo. To je odredba Silnog i Sveznajućeg.');
INSERT INTO "bs_darassalam" VALUES(41,13,'41:13','A ako glave okrenu, ti reci: “Opominjem vas munjom onakvom kakva je pogodila Ada i Semuda...');
INSERT INTO "bs_darassalam" VALUES(41,14,'41:14','kad su im sa svih strana poslanici njihovi dolazili i govorili: ‘Ne klanjajte se nikom do Allahu!’ – a oni odgovarali: ‘Da je Gospodar naš htio, On bi meleke poslao, mi ne vjerujemo u ono što je po vama poslato.’”');
INSERT INTO "bs_darassalam" VALUES(41,15,'41:15','Ad se bez ikakva osnova bio na Zemlji uzoholio. “Ko je od nas jači?”, govorili su. A zar nisu znali da je Allah, Koji ih je stvorio, jači od njih – a i znamenja su Naša poricali.');
INSERT INTO "bs_darassalam" VALUES(41,16,'41:16','I Mi poslasmo protiv njih, u danima nesretnim, vjetar ledeni, da bismo im još na ovom svijetu dali da osjete sramnu patnju – patnja na onom svijetu bit će, zaista, još sramnija – i niko im neće u pomoć priteći.');
INSERT INTO "bs_darassalam" VALUES(41,17,'41:17','I Semudu smo na Pravi put ukazivali, ali njima je bila milija sljepoća od Pravog puta, pa ih je stigla sramna kazna od munje, prema onom kako su zaslužili...');
INSERT INTO "bs_darassalam" VALUES(41,18,'41:18','a one koji su vjerovali i Allaha se bojali Mi smo spasili.');
INSERT INTO "bs_darassalam" VALUES(41,19,'41:19','A na Dan kad Allahovi neprijatelji u vatru budu potjerani – oni prvi bit će zadržani, da bi ih sustigli ostali...');
INSERT INTO "bs_darassalam" VALUES(41,20,'41:20','i kad dođu do nje, uši njihove, i oči njihove, i kože njihove svjedočit će protiv njih o onom što su radili.');
INSERT INTO "bs_darassalam" VALUES(41,21,'41:21','“Zašto svjedočite protiv nas!?”, upitat će oni kože svoje. “Allah, Koji je dao sposobnost govora svakom biću, obdario je darom govora i nas”, odgovorit će. “On vas je prvi put stvorio i Njemu ste se, evo, vratili.');
INSERT INTO "bs_darassalam" VALUES(41,22,'41:22','Vi se niste krili zato da ne bi uši vaše i oči vaše i kože vaše protiv vas svjedočile, već zato što ste vjerovali da Allah neće saznati mnogo štošta što ste radili.');
INSERT INTO "bs_darassalam" VALUES(41,23,'41:23','I to vaše uvjerenje, koje ste o Gospodaru svom imali, upropastilo vas je i sad ste nastradali.”');
INSERT INTO "bs_darassalam" VALUES(41,24,'41:24','Pa i ako budu trpjeli, njihovo će boravište Vatra biti. A ako budu tražili naklonost Allahovu, njihovoj molbi neće se udovoljiti.');
INSERT INTO "bs_darassalam" VALUES(41,25,'41:25','Mi smo im bili odredili loše drugove koji su im lijepim prikazivali i ono što su uradili i ono što će uraditi. I na njima se obistinilo ono što je rečeno za narode, džine i ljude koji su prije njih bili i nestali – doista su nastradali.');
INSERT INTO "bs_darassalam" VALUES(41,26,'41:26','Oni koji ne vjeruju govore: “Ne slušajte ovaj Kur’an, nego pravite buku da biste ga nadvikali!”');
INSERT INTO "bs_darassalam" VALUES(41,27,'41:27','Zato ćemo Mi, sigurno, dati da nevjernici iskuse nesnosnu patnju, i kaznit ćemo ih najgorom kaznom za djela koja su počinili.');
INSERT INTO "bs_darassalam" VALUES(41,28,'41:28','To je kazna za Allahove neprijatelje, vatra u kojoj će im vječna kuća biti, kao naplata što su dokaze Naše stalno poricali.');
INSERT INTO "bs_darassalam" VALUES(41,29,'41:29','I nevjernici će reći: “Gospodaru naš, pokaži nam džine i ljude, one koji su nas zaveli, da ih stavimo pod noge naše, neka budu najdonji.”');
INSERT INTO "bs_darassalam" VALUES(41,30,'41:30','Onima koji govore: “Gospodar naš je Allah” pa poslije ostanu pri tome, dolaze meleki: “Ne bojte se i ne žalostite se, i radujte se Džennetu, koji vam je obećan.');
INSERT INTO "bs_darassalam" VALUES(41,31,'41:31','Mi smo zaštitnici vaši u životu na ovom svijetu, a i na onom. U njemu ćete imati sve ono što duše vaše zažele i što god zatražite imat ćete...');
INSERT INTO "bs_darassalam" VALUES(41,32,'41:32','bit ćete počašćeni od Onog Koji prašta i Koji je milostiv.”');
INSERT INTO "bs_darassalam" VALUES(41,33,'41:33','A ko govori ljepše od onog koji poziva Allahu, koji dobra djela čini i koji govori: “Ja sam doista musliman!”');
INSERT INTO "bs_darassalam" VALUES(41,34,'41:34','Dobro i zlo nisu isto! Zlo dobrim uzvrati, pa će ti dušmanin tvoj odjednom prisni prijatelj postati.');
INSERT INTO "bs_darassalam" VALUES(41,35,'41:35','To mogu postići samo strpljivi, to mogu postići samo vrlo sretni.');
INSERT INTO "bs_darassalam" VALUES(41,36,'41:36','A kad te šejtan pokuša na zle misli navesti, ti zatraži utočište u Allaha, jer On, uistinu, sve čuje i zna sve.');
INSERT INTO "bs_darassalam" VALUES(41,37,'41:37','Među znamenjima Njegovim jesu noć i dan, i Sunce i Mjesec. Ne padajte licem na tlo ni pred Suncem ni pred Mjesecom, već padajte licem na tlo pred Allahom Koji ih je stvorio, ako želite da se samo Njemu jedinom klanjate.');
INSERT INTO "bs_darassalam" VALUES(41,38,'41:38','A ako te oni neće poslušati – pa oni koji su kod Gospodara tvog hvale Ga i noću i danju i ne dosađuju se.');
INSERT INTO "bs_darassalam" VALUES(41,39,'41:39','Jedno od znamenja Njegovih jest i to što ti vidiš suhu zemlju, a kad na nju spustimo kišu, ona se pokrene i uzbuja. Onaj Koji njoj daje život oživit će, sigurno, i umrle, jer je On kadar sve.');
INSERT INTO "bs_darassalam" VALUES(41,40,'41:40','Oni koji riječi Naše izvrću neće se, doista, od Nas sakriti. Pa hoće li biti ugodnije onom koji u Vatru bude bačen ili onom koji na Sudnji dan dođe smiren? Radite šta hoćete, On zaista vidi šta vi radite.');
INSERT INTO "bs_darassalam" VALUES(41,41,'41:41','Oni koji ne vjeruju u Kur’an, pošto im je objavljen! A on je, zaista, Knjiga zaštićena...');
INSERT INTO "bs_darassalam" VALUES(41,42,'41:42','laž joj je strana, bilo s koje strane, ona je Objava od Mudrog i hvale dostojnog.');
INSERT INTO "bs_darassalam" VALUES(41,43,'41:43','Tebi se neće reći ništa što već nije rečeno poslanicima prije tebe. Gospodar tvoj zaista prašta, a i bolno kažnjava.');
INSERT INTO "bs_darassalam" VALUES(41,44,'41:44','A da Kur’an objavljujemo na tuđem jeziku, oni bi, sigurno, rekli: “Trebalo je da su mu ajeti razumljivi. Zar jezik tuđ, a onaj kome se objavljuje Arap je?” Reci: “On je vjernicima uputstvo i lijek. A oni koji neće da vjeruju – i gluhi su i slijepi, kao da se iz daleka mjesta dozivaju.”');
INSERT INTO "bs_darassalam" VALUES(41,45,'41:45','I Musau smo Knjigu dali, pa su se zbog nje podvojili. A da nije Riječi Gospodara tvog prije izrečene, sa ovima bi bilo već svršeno, jer oni mnogo u Kur’an sumnjaju.');
INSERT INTO "bs_darassalam" VALUES(41,46,'41:46','Ko čini dobro, u svoju korist čini, a ko radi zlo, na svoju štetu radi. – A Gospodar tvoj nije nepravedan prema robovima Svojim.');
INSERT INTO "bs_darassalam" VALUES(41,47,'41:47','Samo On zna kad će Smak svijeta nastupiti. A ni plodovi ne izlaze izcvjetnih čaški svojih, i nijedno žensko ne zanese i ne rodi, a da On to ne zna. A na Dan kad ih On upita: “Gdje su oni koje ste Meni u obožavanju pridruživali?”, oni će odgovoriti: “Javljamo Ti da niko od nas to ne tvrdi.”');
INSERT INTO "bs_darassalam" VALUES(41,48,'41:48','I neće biti onih koje su prije molili i uvjerit će se da nikud ne mogu umaći.');
INSERT INTO "bs_darassalam" VALUES(41,49,'41:49','Čovjeku ne dosadi da dobro traži, a kada ga zlo zadesi, onda očajava i nadu gubi.');
INSERT INTO "bs_darassalam" VALUES(41,50,'41:50','Kad ga obaspemo milošću Našom, poslije nesreće koja ga zadesi, on govori: “Ovo sam i zaslužio, i ja ne mislim da će Čas sudnji doći. A ako budem Gospodaru svome vraćen, kod Njega me čeka samo dobro i Džennet.” A Mi ćemo one koji nisu vjerovali, sigurno, o onom što su radili obavijestiti i da iskuse patnju tešku – doista ćemo im dati.');
INSERT INTO "bs_darassalam" VALUES(41,51,'41:51','Kada čovjeku milost Svoju darujemo, on se okreće i udalji se oholo, a kad ga nevolja dotakne, onda se dugo moli.');
INSERT INTO "bs_darassalam" VALUES(41,52,'41:52','Reci: “Šta mislite, ako je Kur’an od Allaha, a vi u njega nećete da vjerujete – ko je onda u većoj zabludi od onoga koji je u protivrječju dalekom od Istine?”');
INSERT INTO "bs_darassalam" VALUES(41,53,'41:53','Mi ćemo im pokazati znakove Naše u daljini – na obzorjima, a i u njima samim, dok im ne bude sasvim jasno da je Kur’an istina. A zar nije dovoljno to što je Gospodar tvoj svemu svjedok?');
INSERT INTO "bs_darassalam" VALUES(41,54,'41:54','Oni sumnjaju da će pred Gospodara svoga stati, a On, zaista, obuhvata sve.');
INSERT INTO "bs_darassalam" VALUES(42,1,'42:1','Ha-mim.');
INSERT INTO "bs_darassalam" VALUES(42,2,'42:2','‘Ajn-sin-kaf.');
INSERT INTO "bs_darassalam" VALUES(42,3,'42:3','Eto, tako Allah, Silni i Mudri, objavljuje tebi, kao i onima prije tebe.');
INSERT INTO "bs_darassalam" VALUES(42,4,'42:4','Njegovo je sve što je na nebesima i sve što je na Zemlji, i On je Svevišnji, Veličanstveni!');
INSERT INTO "bs_darassalam" VALUES(42,5,'42:5','Gotovo da se nebesa odozgo raspadnu! A meleci veličaju i hvale Gospodara svoga i mole oprost za one koji su na Zemlji. Nije li Allah, zaista, Oprostitelj grijeha, Milostivi?!');
INSERT INTO "bs_darassalam" VALUES(42,6,'42:6','Allah motri na one koji, pored Njega, zaštitnike uzimaju, a ti nisi za njihovu uputu zadužen.');
INSERT INTO "bs_darassalam" VALUES(42,7,'42:7','Eto, tako Mi tebi objavljujemo Kur’an, na arapskom jeziku, da biupozoravao Majku naselja i one oko nje i upozorio na Dan sakupljanja – u koji nema nikakve sumnje. Jedni će u Džennet, a drugi u Džehennem.');
INSERT INTO "bs_darassalam" VALUES(42,8,'42:8','A da Allah hoće, učinio bi ih sljedbenicima jedne vjere, ali, On u milostSvoju uvodi koga On hoće, a zulumćari nemaju ni zaštitnika, ni pomagača.');
INSERT INTO "bs_darassalam" VALUES(42,9,'42:9','Zar oni da druge, a ne Njega, za zaštitnike uzimaju?! Pa Allah – baš On je jedini Zaštitnik, On oživljava mrtve i On nad svime ima moć.');
INSERT INTO "bs_darassalam" VALUES(42,10,'42:10','A u čemu god se vi razišli, presuda za to u Allaha je! To vam je, eto, Allah, Gospodar moj – na Njega se ja oslanjam i Njemu se obraćam;');
INSERT INTO "bs_darassalam" VALUES(42,11,'42:11','Stvoritelj nebesa i Zemlje bez prethodnog primjera! Učinio vam je od vaše vrste parove i od stoke parove. Tako vas rasprostire. Ništa nije kao On! On je Svečujući i Svevideći.');
INSERT INTO "bs_darassalam" VALUES(42,12,'42:12','U Njega su ključevi nebesa i Zemlje, On daje opskrbu u izobilju komehoće, a i uskraćuje; On, uistinu, zna sve.');
INSERT INTO "bs_darassalam" VALUES(42,13,'42:13','On vam propisuje u vjeri isto ono što je naredio Nuhu i ono štoobjavljujemo tebi, i ono što smo naredili Ibrahimu i Musau i Isau: “Pravu vjeru ispovijedajte i u tome se ne podvajajte!” Teško je višebošcima ono u što ih pozivaš. Allah odabire za Svoju vjeru onoga koga On hoće i upućuje u nju onoga ko Mu se iskreno obrati.');
INSERT INTO "bs_darassalam" VALUES(42,14,'42:14','A oni su se, baš onda kad im je došlo znanje, iz zlobe međusobne podvojili. I da nije Riječi Gospodara tvoga prije izrečene o odgađanju kazne do roka određenog, njima bi bilo već presuđeno. A oni kojima se poslije njih Knjiga u nasljedstvo ostavlja, spram nje su u sumnji velikoj.');
INSERT INTO "bs_darassalam" VALUES(42,15,'42:15','K tome ti pozivaj i budi ustrajan, onako kako ti se naređuje, i ne slijedihirove njihove, i reci: “Ja vjerujem u Knjige koje je Allah objavio i naređeno mi je da među vama pravedan budem; Allah je i naš i vaš Gospodar, nama naša, a vama vaša djela; nema prepiranja između nas i vas; Allah će nas sve sabrati, a kod Njega je konačno odredište.');
INSERT INTO "bs_darassalam" VALUES(42,16,'42:16','A oni koji se o Allahu raspravljaju nakon što je Njegov poziv prihvaćen– njihovi dokazi su ništavni kod njihova Gospodara; na njima je gnjev injima pripada patnja žestoka.');
INSERT INTO "bs_darassalam" VALUES(42,17,'42:17','Allah je Onaj Koji je Knjigu s Istinom i Mjerilo objavio! A šta ti znaš –možda je Čas kijameta blizu!');
INSERT INTO "bs_darassalam" VALUES(42,18,'42:18','Požuruju ga oni koji u njega ne vjeruju, a oni koji vjeruju, zbog njegastrahuju i da je istina zbilja znaju. Eto, doista su oni koji raspravljaju oČasu kijameta u velikoj zabludi!');
INSERT INTO "bs_darassalam" VALUES(42,19,'42:19','Allah je dobrostiv prema robovima Svojim; On daje opskrbu kome hoće i On je Moćni i Silni.');
INSERT INTO "bs_darassalam" VALUES(42,20,'42:20','Onome ko bude želio nagradu na onom svijetu - umnogostručićemo mu je, a onome ko bude želio nagradu na ovom svijetu - daćemo mu je, ali mu na onom svijetu nema udjela.');
INSERT INTO "bs_darassalam" VALUES(42,21,'42:21','Zar oni da imaju ortake koji im propisuju da vjeruju ono što Allah nijenaredio? Da nije Riječi odluke, njima bi već bilo presuđeno. Azulumćarima, doista, pripada patnja bolna.');
INSERT INTO "bs_darassalam" VALUES(42,22,'42:22','Vidjet ćeš zulumćare kako strepe zbog onoga što su činili, a to će ih,sigurno, stići; a oni koji su vjerovali i dobra djela činili bit će u divnimdžennetskim baščama, sve što zažele imat će u Gospodara svoga; bit će to blagodat velika.');
INSERT INTO "bs_darassalam" VALUES(42,23,'42:23','To je ono čime Allah obveseljava Svoje robove koji vjeruju i čine dobradjela. Reci: “Ne tražim ja za ovo nikakvu nagradu od vas, osim što tražim pažnju rodbinsku.” A ko učini dobro, Mi ćemo njemu za to povećati dobro. Doista je Allah Onaj Koji oprašta grijehe i zahvalan je.');
INSERT INTO "bs_darassalam" VALUES(42,24,'42:24','Zar oni da govore: “On izmišlja o Allahu laži!” A ako hoće, Allah će tvoje srce zapečatiti! Allah poništava neistinu i utvrđuje Istinu riječima Svojim; On dobro zna šta se u grudima krije.');
INSERT INTO "bs_darassalam" VALUES(42,25,'42:25','On je Onaj Koji prima pokajanje od robova Svojih i prašta loše postupke i zna šta radite;');
INSERT INTO "bs_darassalam" VALUES(42,26,'42:26','odaziva se onima koji vjeruju i čine dobra djela i iz dobrote Svoje im iviše pruža, a nevjernike čeka žestoka patnja.');
INSERT INTO "bs_darassalam" VALUES(42,27,'42:27','A kad bi Allah Svojim robovima preobilnu opskrbu davao, oni bi se naZemlji osilili, ali On je daje s mjerom, onoliko koliko hoće! Zaista, On upotpunosti zna i vidi robove Svoje.');
INSERT INTO "bs_darassalam" VALUES(42,28,'42:28','On je i Onaj Koji spušta kišu kad oni izgube svaku nadu i rasprostire milost Svoju; On je, uistinu, Zaštitnik, Hvaljeni.');
INSERT INTO "bs_darassalam" VALUES(42,29,'42:29','Među znakovima Njegovim je stvaranje nebesa i Zemlje i živih bića koja je rasijao po njima; On ih je kadar sve sabrati kad bude htio.');
INSERT INTO "bs_darassalam" VALUES(42,30,'42:30','Nesreća koja vas zadesi je zbog onoga što su ruke vaše stekle, a mnogo toga On i oprosti.');
INSERT INTO "bs_darassalam" VALUES(42,31,'42:31','A vi Mu na Zemlji nećete moći umaći, i vi osim Allaha nemate ni zaštitnika ni pomagača!');
INSERT INTO "bs_darassalam" VALUES(42,32,'42:32','I među znakovima Njegovim su i lađe nalik na brda koje morem plove.');
INSERT INTO "bs_darassalam" VALUES(42,33,'42:33','Ako želi, On umiri vjetar i one na površini njegovoj ostaju nepokretne –doista, u tome ima znakova za svakog strpljivog, zahvalnog –');
INSERT INTO "bs_darassalam" VALUES(42,34,'42:34','ili ih potopi zbog onog što su zaradili – a mnogo toga On i oprosti,');
INSERT INTO "bs_darassalam" VALUES(42,35,'42:35','da bi oni koji raspravljaju o ajetima i znakovima Našim znali da nemajukud umaći.');
INSERT INTO "bs_darassalam" VALUES(42,36,'42:36','Ono što vam je dato, samo je uživanje u životu dunjalučkom, a ono što je u Allaha – bolje je i trajnije za one koji vjeruju i na Gospodara svoga se oslanjaju;');
INSERT INTO "bs_darassalam" VALUES(42,37,'42:37','i za one koji se klone velikih grijeha i razvrata i koji, kad se rasrde,opraštaju;');
INSERT INTO "bs_darassalam" VALUES(42,38,'42:38','i za one koji se Gospodaru svome odazivaju, i namaz klanjaju, i o poslovima svojim se dogovaraju, a dio od onoga čime smo ih opskrbili udjeljuju,');
INSERT INTO "bs_darassalam" VALUES(42,39,'42:39','i za one koji, kada ugnjetavanju budu izvrgnuti, uzvrate.');
INSERT INTO "bs_darassalam" VALUES(42,40,'42:40','Kazna za zlo je slično zlo, a onoga koji oprosti i izmiri se Allah će nagraditi; On, uistinu, ne voli zulumćare.');
INSERT INTO "bs_darassalam" VALUES(42,41,'42:41','A onaj ko uzvrati nakon što mu je zulum učinjen, pa takvi ne mogu bitikažnjeni.');
INSERT INTO "bs_darassalam" VALUES(42,42,'42:42','Bit će kažnjeni oni koji ljude tlače i bez ikakva osnova red na Zemljinarušavaju; njih čeka bolna patnja.');
INSERT INTO "bs_darassalam" VALUES(42,43,'42:43','A ko se strpi i oprosti, doista, to u istinske postupke spada.');
INSERT INTO "bs_darassalam" VALUES(42,44,'42:44','A kome Allah da da je u zabludi, tome pomagača poslije Njega neće biti! I ti ćeš vidjeti kako će zulumćari, kad ugledaju patnju, povikati: “Postoji li ikakav način da budemo opet vraćeni?”');
INSERT INTO "bs_darassalam" VALUES(42,45,'42:45','I vidjet ćeš kako se poniženi i klonuli Vatri izlažu, a oni je kradomicegledaju. Oni koji su vjerovali, reći će: “Doista su stradalnici oni koji naKijametskom danu i sebe i porodice svoje upropaste!” A zar zulumćarineće biti u neprestanoj patnji?!');
INSERT INTO "bs_darassalam" VALUES(42,46,'42:46','Njima neće biti zaštitnika da im pomognu, osim Allaha; a kome Allah da da je u zabludi, takav put spasa neće ni imati!');
INSERT INTO "bs_darassalam" VALUES(42,47,'42:47','Odazovite se Gospodaru svome prije nego što dođe Dan koji Allah neće zaustaviti. Taj Dan utočišta nećete imati i nećete moći poricati.');
INSERT INTO "bs_darassalam" VALUES(42,48,'42:48','A ako se okrenu – pa, Mi tebe nismo ni poslali da nad njima bdiješ, ti si dužan samo saopćiti. Kad čovjeku damo da milost Našu osjeti, on joj se obraduje, a kad ih zadesi kakva nesreća zbog onoga što su uradile ruke njihove, onda čovjek blagodati ne priznaje.');
INSERT INTO "bs_darassalam" VALUES(42,49,'42:49','Allahova je vlast na nebesima i na Zemlji. On stvara šta hoće! Daruježensku djecu kome hoće, a kome hoće – mušku,');
INSERT INTO "bs_darassalam" VALUES(42,50,'42:50','ili im daje i mušku i žensku djecu, a kome hoće, da da je bez poroda; On je, uistinu, Onaj Koji sve zna i svemoćan je.');
INSERT INTO "bs_darassalam" VALUES(42,51,'42:51','Nijednom čovjeku nije dato da mu se Allah obraća, osim objavom, ili iza zastora, ili da pošalje izaslanika, pa s dozvolom Njegovom objavi ono što On hoće. On je, zaista, uzvišen i mudar.');
INSERT INTO "bs_darassalam" VALUES(42,52,'42:52','Tako Mi i tebi objavljujemo Kur’an iz odredbe Naše. Ti nisi znao šta jeKnjiga, niti šta je vjerovanje, ali smo je Mi učinili svjetlom pomoću kojegupućujemo one robove Naše koje želimo. Ti, zaista, ukazuješ na Pravi put,');
INSERT INTO "bs_darassalam" VALUES(42,53,'42:53','na put Allahov, Kome pripada sve što je na nebesima i sve što je na Zemlji. I, eto, Allahu se sve vraća!');
INSERT INTO "bs_darassalam" VALUES(43,1,'43:1','Ha-mim.');
INSERT INTO "bs_darassalam" VALUES(43,2,'43:2','Tako Mi Knjige jasne,');
INSERT INTO "bs_darassalam" VALUES(43,3,'43:3','Mi je objavljujemo kao Kur’an na arapskom jeziku da biste razumjeli,');
INSERT INTO "bs_darassalam" VALUES(43,4,'43:4','a on je u Glavnoj knjizi, u Nas, cijenjen i savršen.');
INSERT INTO "bs_darassalam" VALUES(43,5,'43:5','Zar da odustanemo Opomenu vam saopćavati zato što svaku mjeru zla prelazite?');
INSERT INTO "bs_darassalam" VALUES(43,6,'43:6','A koliko smo Mi prijašnjim narodima vjerovjesnika poslali');
INSERT INTO "bs_darassalam" VALUES(43,7,'43:7','i nijedan im vjerovjesnik nije došao, a da ga nisu izrugivali!');
INSERT INTO "bs_darassalam" VALUES(43,8,'43:8','Zato smo uništavali one koji su od ovih moćniji bili, a prethodio je primjer prijašnjih naroda.');
INSERT INTO "bs_darassalam" VALUES(43,9,'43:9','Pa ako ih upitaš ko je stvorio nebesa i Zemlju, oni će, sigurno, reći: “Stvorio ih je Silni i Sveznajući!”');
INSERT INTO "bs_darassalam" VALUES(43,10,'43:10','Onaj Koji vam je Zemlju kolijevkom učinio i po njoj vam prolaze stvorioda biste se usmjeravali;');
INSERT INTO "bs_darassalam" VALUES(43,11,'43:11','Onaj Koji s neba s mjerom spušta vodu, pomoću koje u život vraćamomrtve predjele – tako ćete i vi biti iz grobova izvedeni.');
INSERT INTO "bs_darassalam" VALUES(43,12,'43:12','Onaj Koji stvara stvorenja svake vrste i daje vam lađe i stoku da nanjima putujete,');
INSERT INTO "bs_darassalam" VALUES(43,13,'43:13','da se na palubama i leđima njihovim smjestite i da se, potom, kada sesmjestite na njih, sjetite blagodati Gospodara svoga i da reknete: “Slavljen i uzvišen neka je Onaj Koji nam je ovo potčinio, mi to sami ne bismo mogli postići');
INSERT INTO "bs_darassalam" VALUES(43,14,'43:14','i mi ćemo se, sigurno, Gospodaru svome vratiti!”');
INSERT INTO "bs_darassalam" VALUES(43,15,'43:15','A oni Njemu jedan dio Njegovih robova pripisuju. Čovjek je, zaista,očevidni nezahvalnik.');
INSERT INTO "bs_darassalam" VALUES(43,16,'43:16','Zar da između onih koje On stvara uzima Sebi kćeri, a vama odabiresinove?!');
INSERT INTO "bs_darassalam" VALUES(43,17,'43:17','A kad neko od njih bude obaviješten o onome što Svemilosnom sličnim pripisuje, lice mu se ojađenom pomrači.');
INSERT INTO "bs_darassalam" VALUES(43,18,'43:18','Zar da ono koje u nakitu biva podizano i koje pri sporenju nejasno zbori pripisuju Allahu?!');
INSERT INTO "bs_darassalam" VALUES(43,19,'43:19','Oni meleke, koji su robovi Svemilosnog, ženskinjem smatraju. Da nisuprisustvovali stvaranju njihovu?! Njihova tvrdnja bit će zapisana i oni će biti pitani!');
INSERT INTO "bs_darassalam" VALUES(43,20,'43:20','I oni vele: “Da je htio Svemilosni, njih ne bismo obožavali!” Oni o tomeništa ne znaju, oni samo lažu.');
INSERT INTO "bs_darassalam" VALUES(43,21,'43:21','Ili smo im dali Knjigu prije toga pa se nje pridržavaju?!');
INSERT INTO "bs_darassalam" VALUES(43,22,'43:22','Oni čak govore: “Mi smo zatekli pretke naše kako ispovijedaju vjeru i,prateći njihove tragove, mi smo na Pravom putu.”');
INSERT INTO "bs_darassalam" VALUES(43,23,'43:23','Isto tako, prije tebe, Mi ni u jedan grad nismo poslanika poslali, a daoni koji su raskošno živjeli nisu govorili: “Zatekli smo pretke naše kakoispovijedaju vjeru i mi tragove njihove slijedimo.”');
INSERT INTO "bs_darassalam" VALUES(43,24,'43:24','“Zar i onda”, govorio bi on, “kad vam ja donosim bolju od one koju ste od predaka vaših upamtili?” A oni bi odgovarali: “Ne vjerujemo mi u ono što je po vama poslano!”');
INSERT INTO "bs_darassalam" VALUES(43,25,'43:25','I Mi smo ih kažnjavali, pa vidi kakav je bio kraj onih koji su poslanike ulaž ugonili.');
INSERT INTO "bs_darassalam" VALUES(43,26,'43:26','A kad Ibrahim reče ocu svome i narodu svome: “Nemam ja ništa s onima koje vi obožavate,');
INSERT INTO "bs_darassalam" VALUES(43,27,'43:27','osim s Onim Koji me je stvorio, jer će me On, doista, na Pravi put uputiti.”');
INSERT INTO "bs_darassalam" VALUES(43,28,'43:28','On to učini riječju trajnom za potomstvo svoje da bi se vratili.');
INSERT INTO "bs_darassalam" VALUES(43,29,'43:29','A Ja sam čak dopustio ovima, a i precima njihovim da uživaju sve dok im nije došla Istina i Poslanik očevidni.');
INSERT INTO "bs_darassalam" VALUES(43,30,'43:30','A kad im je Istina došla, rekoše: “Ovo je vradžbina, i mi u nju nikako nevjerujemo!”');
INSERT INTO "bs_darassalam" VALUES(43,31,'43:31','I rekoše: “Zašto ovaj Kur’an nije objavljen nekom velikom čovjeku iz ova dva grada?!”');
INSERT INTO "bs_darassalam" VALUES(43,32,'43:32','A da oni ne raspodjeljuju milost Gospodara tvoga?! Mi raspodjeljujemomeđu njima ono što im je potrebno za život na dunjaluku i Mi jedne naddrugima uzdižemo po stepenima da bi jedni druge uzimali da im služe. A milost Gospodara tvoga bolja je od onoga što oni gomilaju.');
INSERT INTO "bs_darassalam" VALUES(43,33,'43:33','A da neće svi ljudi postati jedan ummet – nevjernici, Mi bismo krovovekuća onih koji ne vjeruju u Svemilosnog od srebra učinili, a i stepenice uz koje se penju,');
INSERT INTO "bs_darassalam" VALUES(43,34,'43:34','i vrata kuća njihovih, i divane na kojima se odmaraju,');
INSERT INTO "bs_darassalam" VALUES(43,35,'43:35','i ukrase od zlata bismo im dali, jer sve je to samo uživanje u životudunjalučkom, a ahiret, u Gospodara tvoga, bit će za bogobojazne.');
INSERT INTO "bs_darassalam" VALUES(43,36,'43:36','Onome ko se bude slijepim pravio pred Opomenom Svemilosnog, Mićemo šejtana natovariti, pa će mu on nerazdvojni drug postati;');
INSERT INTO "bs_darassalam" VALUES(43,37,'43:37','oni – šejtani ljude će od Pravog puta odvraćati, a oni će misliti da su naPravom putu.');
INSERT INTO "bs_darassalam" VALUES(43,38,'43:38','I kada koji od njih dođe pred Nas, reći će: “Kamo sreće da je između mene i tebe bila tolika razdaljina kolika je između istoka i zapada! Kako si ti bio zao drug!”');
INSERT INTO "bs_darassalam" VALUES(43,39,'43:39','Taj dan neće vam koristiti to što ćete u patnji zajedno biti kad stezulum činili.');
INSERT INTO "bs_darassalam" VALUES(43,40,'43:40','Zar ti da dozoveš gluhe i uputiš slijepe i one koji su u očitoj zabludi?!');
INSERT INTO "bs_darassalam" VALUES(43,41,'43:41','Ako bismo ti dušu uzeli, njih bismo, sigurno, kaznili,');
INSERT INTO "bs_darassalam" VALUES(43,42,'43:42','ili, kad bismo ti htjeli pokazati ono što smo im obećali, pa Mi nad njima,zaista, potpunu moć imamo.');
INSERT INTO "bs_darassalam" VALUES(43,43,'43:43','Zato se drži onoga što ti se objavljuje, a ti si, uistinu, na Pravom putu.');
INSERT INTO "bs_darassalam" VALUES(43,44,'43:44','Kur’an je, zaista, čast i tebi i narodu tvome; pa bit ćete pitani!');
INSERT INTO "bs_darassalam" VALUES(43,45,'43:45','A pitaj one koje smo prije tebe od Naših poslanika poslali – da li smo Mi naređivali da se, osim Svemilosnog, božanstva obožavaju?');
INSERT INTO "bs_darassalam" VALUES(43,46,'43:46','Još davno Mi smo poslali Musaa sa znamenjima Našim faraonu iglavešinama njegovim, i on je rekao: “Ja sam, doista, Gospodara svjetova poslanik!”');
INSERT INTO "bs_darassalam" VALUES(43,47,'43:47','I pošto im je donio znakove Naše, oni su ih, odjednom, počeli ismijavati.');
INSERT INTO "bs_darassalam" VALUES(43,48,'43:48','A nismo im pokazivali nijedan znak a da nije veći bio nego prethodni! Ipatnji smo ih podvrgli, ne bi li se povratili!');
INSERT INTO "bs_darassalam" VALUES(43,49,'43:49','A oni su govorili: “O ti, čarobnjače! Moli za nas Gospodara svoga, natemelju tebi datog obećanja, mi ćemo, sigurno, Pravim putem poći!”');
INSERT INTO "bs_darassalam" VALUES(43,50,'43:50','A čim bismo ih nevolje oslobodili, začas bi obavezu prekršili.');
INSERT INTO "bs_darassalam" VALUES(43,51,'43:51','I faraon obznani narodu svome: “O narode moj”, reče on, “zar meni nepripada vlast u Misiru i ove rijeke koje ispred mene teku – ne vidite li?!”');
INSERT INTO "bs_darassalam" VALUES(43,52,'43:52','Svakako sam ja bolji od ovoga bijednika koji jedva razgovijetno govori!');
INSERT INTO "bs_darassalam" VALUES(43,53,'43:53','Zašto mu nisu stavljene narukvice od zlata, ili zašto zajedno s njim nisu došli meleci?');
INSERT INTO "bs_darassalam" VALUES(43,54,'43:54','I on lahkoumnim učini narod svoj, pa mu se pokoriše; oni su, doista, bili narod buntovni.');
INSERT INTO "bs_darassalam" VALUES(43,55,'43:55','A kad izazvaše Naš gnjev, Mi ih kaznismo i sve ih potopismo,');
INSERT INTO "bs_darassalam" VALUES(43,56,'43:56','i učinismo ih primjerom i poukom narodima kasnijim.');
INSERT INTO "bs_darassalam" VALUES(43,57,'43:57','A kad je narodu tvome kao primjer naveden sin Merjemin, odjednom su oni, zbog toga, uz smijeh zagalamili');
INSERT INTO "bs_darassalam" VALUES(43,58,'43:58','i rekli: “Da li su naša božanstva bolja ili on?” A naveli su ti ga kao primjer samo zato da bi se prepirali. Štaviše, oni su narod svađalački.');
INSERT INTO "bs_darassalam" VALUES(43,59,'43:59','On je bio samo rob na koga smo Mi blagodati prosuli i učinili ga primjerom sinovima Israilovim.');
INSERT INTO "bs_darassalam" VALUES(43,60,'43:60','A da hoćemo, Mi bismo učinili da vas na Zemlji meleci naslijede;');
INSERT INTO "bs_darassalam" VALUES(43,61,'43:61','zaista je on predznak Smaka svijeta, zato nikako ne sumnjajte u njega i slijedite Mene! Ovo je Pravi put,');
INSERT INTO "bs_darassalam" VALUES(43,62,'43:62','i neka vas šejtan nikako ne odvrati; on vam je, doista, neprijatelj otvoreni.');
INSERT INTO "bs_darassalam" VALUES(43,63,'43:63','A kad je Isa očite znakove donio, on je rekao: “Donosim vam mudrost idolazim da vam objasnim ono u čemu se razilazite. Zato se Allaha bojte i meni se pokoravajte.');
INSERT INTO "bs_darassalam" VALUES(43,64,'43:64','Allah je i moj i vaš Gospodar, pa Njemu ibadet činite! Ovo je Pravi put!”');
INSERT INTO "bs_darassalam" VALUES(43,65,'43:65','Ali su se stranke iz redova njihovih podvojile. Pa teško onima koji suzulum učinili od kazne na Danu bolnome!');
INSERT INTO "bs_darassalam" VALUES(43,66,'43:66','Čekaju li oni drugo doli da im Smak svijeta dođe nenadano, i da oni i ne osjete?!');
INSERT INTO "bs_darassalam" VALUES(43,67,'43:67','Taj dan prijatelji će jedni drugima neprijatelji biti, osim bogobojaznih.');
INSERT INTO "bs_darassalam" VALUES(43,68,'43:68','“O robovi Moji, za vas danas straha neće biti, niti ćete tugovati!”');
INSERT INTO "bs_darassalam" VALUES(43,69,'43:69','A onima koji su u ajete Naše vjerovali i koji su muslimani bili – bit ćerečeno:');
INSERT INTO "bs_darassalam" VALUES(43,70,'43:70','“Uđite u Džennet, vi i supružnici vaši, radosni!”');
INSERT INTO "bs_darassalam" VALUES(43,71,'43:71','Oni će biti služeni iz posuda i čaša od zlata. U njemu će biti sve što duše zažele i čime se oči naslađuju, i u njemu ćete vječno boraviti.');
INSERT INTO "bs_darassalam" VALUES(43,72,'43:72','Eto, to je Džennet, koji ste u nasljedstvo dobili za ono što ste radili;');
INSERT INTO "bs_darassalam" VALUES(43,73,'43:73','u njemu ćete raznovrsnog voća imati koje ćete jesti.');
INSERT INTO "bs_darassalam" VALUES(43,74,'43:74','Doista će prestupnici u patnji džehennemskoj vječno ostati,');
INSERT INTO "bs_darassalam" VALUES(43,75,'43:75','ona im neće ublažena biti i nikakve nade u spas neće imati. Oni će u njoj očajni biti.');
INSERT INTO "bs_darassalam" VALUES(43,76,'43:76','Nismo im Mi zulum učinili, nego su oni baš zulumćari bili.');
INSERT INTO "bs_darassalam" VALUES(43,77,'43:77','Oni će dozivati: “O Malik! Neka nas Gospodar tvoj dokrajči!”, a on ćereći: “Doista vi tu ostajete!”');
INSERT INTO "bs_darassalam" VALUES(43,78,'43:78','Mi smo vam slali Istinu, ali većina vas istinu prezire.');
INSERT INTO "bs_darassalam" VALUES(43,79,'43:79','Ako oni pletu zamke, i Mi ćemo zamke njima postaviti.');
INSERT INTO "bs_darassalam" VALUES(43,80,'43:80','Zar oni misle da Mi ne čujemo šta oni nasamo razgovaraju i kako se među sobom dogovaraju? Čujemo Mi, a izaslanici Naši, koji su uz njih, zapisuju.');
INSERT INTO "bs_darassalam" VALUES(43,81,'43:81','Reci: “Nema Milostivi djeteta, a ja sam prvi koji Mu ibadet čini!”');
INSERT INTO "bs_darassalam" VALUES(43,82,'43:82','Neka je slavljen i uzvišen Gospodar nebesa i Zemlje, i Gospodar Arša, od onoga kako Ga oni opisuju!');
INSERT INTO "bs_darassalam" VALUES(43,83,'43:83','Pa, ti ih ostavi neka se upuštaju u zabludu i zabavljaju dok ne dožive Dan kojim im se prijeti.');
INSERT INTO "bs_darassalam" VALUES(43,84,'43:84','On je Onaj Koji je i na nebu Bog, a i na Zemlji Bog. On je Mudri iSveznajući!');
INSERT INTO "bs_darassalam" VALUES(43,85,'43:85','I neka je uzvišen Onaj Čija je vlast na nebesima i na Zemlji, i između njih, On jedini zna kada će Smak svijeta biti i Njemu ćete se svi vratiti!');
INSERT INTO "bs_darassalam" VALUES(43,86,'43:86','Oni koje oni, pored Njega, mole – neće se moći za druge zauzimati; moći će samo oni koji Istinu priznaju, oni koji znaju.');
INSERT INTO "bs_darassalam" VALUES(43,87,'43:87','A ako ih zapitaš ko ih je stvorio, sigurno će reći: “Allah!” Pa kuda se,onda, odmeću?!');
INSERT INTO "bs_darassalam" VALUES(43,88,'43:88','A Poslanik reče: “Gospodaru moj, ovo su, zaista, ljudi koji ne vjeruju!”');
INSERT INTO "bs_darassalam" VALUES(43,89,'43:89','Pa ti se okreni od njih i reci: “Selam!” Sigurno će oni znati!');
INSERT INTO "bs_darassalam" VALUES(44,1,'44:1','Ha-mim.');
INSERT INTO "bs_darassalam" VALUES(44,2,'44:2','Tako Mi Knjige jasne,');
INSERT INTO "bs_darassalam" VALUES(44,3,'44:3','Mi smo je objavili u Blagoslovljenoj noći i Mi, doista, upozoravamo;');
INSERT INTO "bs_darassalam" VALUES(44,4,'44:4','u njoj se raščlanjuje svaka odredba savršena.');
INSERT INTO "bs_darassalam" VALUES(44,5,'44:5','po zapovijedi Našoj! Mi smo, zaista, slali poslanike');
INSERT INTO "bs_darassalam" VALUES(44,6,'44:6','kao milost Gospodara tvoga – a On je, uistinu, Svečujući i Sveznajući –');
INSERT INTO "bs_darassalam" VALUES(44,7,'44:7','Gospodara nebesa i Zemlje i onoga što je između njih – ako čvrsto vjerujete,');
INSERT INTO "bs_darassalam" VALUES(44,8,'44:8','drugog boga osim Njega nema; On život i smrt daje – Gospodar vaš iGospodar vaših predaka davnih!');
INSERT INTO "bs_darassalam" VALUES(44,9,'44:9','Ali, oni sumnjaju i zabavljaju se.');
INSERT INTO "bs_darassalam" VALUES(44,10,'44:10','Pa ti zato sačekaj Dan kad će se na nebu vidljivi dim pojaviti');
INSERT INTO "bs_darassalam" VALUES(44,11,'44:11','koji će ljude prekriti: “Ovo je bolna patnja!');
INSERT INTO "bs_darassalam" VALUES(44,12,'44:12','Gospodaru naš, otkloni patnju od nas, mi ćemo, sigurno, vjerovati!”');
INSERT INTO "bs_darassalam" VALUES(44,13,'44:13','A kako da tada pouku prime, a već im je bio došao Poslanik jasni,');
INSERT INTO "bs_darassalam" VALUES(44,14,'44:14','od koga se oni potom okrenuše i rekoše: “Poučenī – umno poremećenī!”');
INSERT INTO "bs_darassalam" VALUES(44,15,'44:15','A kad bismo malo patnju otklonili, vi biste se sigurno u nevjerovanjevratili;');
INSERT INTO "bs_darassalam" VALUES(44,16,'44:16','ali na Dan kada ih najžešće zgrabimo, zbilja ćemo ih kazniti.');
INSERT INTO "bs_darassalam" VALUES(44,17,'44:17','A Mi smo i prije njih faraonov narod u iskušenje stavili, i došao im jeplemeniti poslanik');
INSERT INTO "bs_darassalam" VALUES(44,18,'44:18','“Predajte meni Allahove robove! Ja sam vam, zaista, poslanik povjerljivi');
INSERT INTO "bs_darassalam" VALUES(44,19,'44:19','i ne uzdižite se iznad Allaha, ja vam donosim dokaz očevidni,');
INSERT INTO "bs_darassalam" VALUES(44,20,'44:20','i ja se obraćam i svome i vašem Gospodaru da me ne gađate,');
INSERT INTO "bs_darassalam" VALUES(44,21,'44:21','a ako mi ne vjerujete, onda me na miru ostavite!”');
INSERT INTO "bs_darassalam" VALUES(44,22,'44:22','I on pozva u pomoć Gospodara svoga: “Ovo je, doista, narod prestupnički!”');
INSERT INTO "bs_darassalam" VALUES(44,23,'44:23','“Izvedi noću robove Moje, vi ćete slijeđeni biti,');
INSERT INTO "bs_darassalam" VALUES(44,24,'44:24','i ostavi more nek miruje, oni su vojska koja će, zaista, potopljena biti.”');
INSERT INTO "bs_darassalam" VALUES(44,25,'44:25','I koliko ostaviše bašča i izvora,');
INSERT INTO "bs_darassalam" VALUES(44,26,'44:26','i njiva zasijanih i boravišta divnih,');
INSERT INTO "bs_darassalam" VALUES(44,27,'44:27','i blagodati u kojima su uživali!');
INSERT INTO "bs_darassalam" VALUES(44,28,'44:28','Tako to bi, i Mi smo to u nasljedstvo drugima ostavili,');
INSERT INTO "bs_darassalam" VALUES(44,29,'44:29','ni nebo ih ni Zemlja nisu oplakivali i nisu bili sačekani.');
INSERT INTO "bs_darassalam" VALUES(44,30,'44:30','A sinove smo Israilove ponižavajuće patnje spasili,');
INSERT INTO "bs_darassalam" VALUES(44,31,'44:31','od faraona – on je bio zbilja nadmen, jedan od onih koji su u zlu svakumjeru prevršili,');
INSERT INTO "bs_darassalam" VALUES(44,32,'44:32','i znajući kakvi su, nad svjetovima smo ih odabrali');
INSERT INTO "bs_darassalam" VALUES(44,33,'44:33','i neka smo im znamenja puna očite kušnje dali.');
INSERT INTO "bs_darassalam" VALUES(44,34,'44:34','A oni, doista, govore:');
INSERT INTO "bs_darassalam" VALUES(44,35,'44:35','“Postoji samo naša prva smrt, mi nećemo biti oživljeni;');
INSERT INTO "bs_darassalam" VALUES(44,36,'44:36','pretke naše nam dovedite ako je istina to što govorite!”');
INSERT INTO "bs_darassalam" VALUES(44,37,'44:37','Da li su bolji oni ili narod Tubbe‘ov i oni prije njega? Njih smo uništilijer su prestupnici bili.');
INSERT INTO "bs_darassalam" VALUES(44,38,'44:38','Mi nismo stvorili nebesa i Zemlju i ono što je između njih igrajući se.');
INSERT INTO "bs_darassalam" VALUES(44,39,'44:39','Mi smo ih stvorili s Istinom, ali većina njih ne zna.');
INSERT INTO "bs_darassalam" VALUES(44,40,'44:40','Zbilja će Dan suda svima njima rok određeni biti,');
INSERT INTO "bs_darassalam" VALUES(44,41,'44:41','Dan kada bližnji neće nimalo od koristi bližnjem biti, niti će biti pomognuti,');
INSERT INTO "bs_darassalam" VALUES(44,42,'44:42','osim onih kojima se Allah smiluje. On je, uistinu, Silni i Milostivi.');
INSERT INTO "bs_darassalam" VALUES(44,43,'44:43','A drvo zekkum zbilja je');
INSERT INTO "bs_darassalam" VALUES(44,44,'44:44','hrana griješnika,');
INSERT INTO "bs_darassalam" VALUES(44,45,'44:45','kao uljani talog u trbuhu će vreti,');
INSERT INTO "bs_darassalam" VALUES(44,46,'44:46','kao što voda ključala vrije.');
INSERT INTO "bs_darassalam" VALUES(44,47,'44:47','“Ščepajte ga i usred Ognja odvucite,');
INSERT INTO "bs_darassalam" VALUES(44,48,'44:48','a zatim mu, za kaznu, na glavu vodu koja ključa izlijevajte!');
INSERT INTO "bs_darassalam" VALUES(44,49,'44:49','Kušaj, ta ti si baš “moćan” i “poštovan”!');
INSERT INTO "bs_darassalam" VALUES(44,50,'44:50','Ovo je, zaista, ono u što ste sumnjali!”');
INSERT INTO "bs_darassalam" VALUES(44,51,'44:51','A bogobojazni će, zaista, na sigurnom mjestu biti,');
INSERT INTO "bs_darassalam" VALUES(44,52,'44:52','usred bašča i izvora,');
INSERT INTO "bs_darassalam" VALUES(44,53,'44:53','u dibu i kadifu obučeni i jedni prema drugima okrenuti.');
INSERT INTO "bs_darassalam" VALUES(44,54,'44:54','Eto, tako će biti i Mi ćemo ih hurijama, krupnih očiju, ženiti.');
INSERT INTO "bs_darassalam" VALUES(44,55,'44:55','Tamo će moći, sigurni, koju hoće vrstu voća tražiti;');
INSERT INTO "bs_darassalam" VALUES(44,56,'44:56','tu, poslije one prve smrti, smrt više neće okusiti i On će ih patnje u Ognju sačuvati,');
INSERT INTO "bs_darassalam" VALUES(44,57,'44:57','blagodat će to od Gospodara tvoga biti; to će, zaista, biti uspjeh veliki!');
INSERT INTO "bs_darassalam" VALUES(44,58,'44:58','A njega – Kur’an, lahkim smo učinili, na jeziku tvome, da bi se oni prisjetili.');
INSERT INTO "bs_darassalam" VALUES(44,59,'44:59','Ti zato čekaj, a i oni čekaju!');
INSERT INTO "bs_darassalam" VALUES(45,1,'45:1','Ha-mim.');
INSERT INTO "bs_darassalam" VALUES(45,2,'45:2','Knjigu objavljuje Allah, Silni i Mudri!');
INSERT INTO "bs_darassalam" VALUES(45,3,'45:3','Na nebesima i na Zemlji, zaista, postoje znakovi za vjernike.');
INSERT INTO "bs_darassalam" VALUES(45,4,'45:4','U stvaranju vas i živih bića koja je razasuo znakovi su za ljude koji sučvrsto uvjereni;');
INSERT INTO "bs_darassalam" VALUES(45,5,'45:5','i u smjeni noći i dana, i opskrbi – kiši koju Allah s neba spušta da pomoću nje zemlju, nakon mrtvila njezina, oživi, i usmjeravanju vjetrova – znakovi su za ljude koji razumiju.');
INSERT INTO "bs_darassalam" VALUES(45,6,'45:6','To su Allahovi ajeti i znakovi koje ti kao istinu navodimo, pa u koje će, ako ne u Allahove riječi i znakove Njegove, oni vjerovati?');
INSERT INTO "bs_darassalam" VALUES(45,7,'45:7','Teško svakom lašcu, velikom griješniku,');
INSERT INTO "bs_darassalam" VALUES(45,8,'45:8','koji čuje Allahove ajete kad mu se uče, zatim ustraje u nevjerovanju oholeći se, kao da ih ni čuo nije – njemu patnju bolnu navijesti.');
INSERT INTO "bs_darassalam" VALUES(45,9,'45:9','A kad sazna za neke Naše ajete, on im se ruga. Takve ponižavajuća patnja čeka.');
INSERT INTO "bs_darassalam" VALUES(45,10,'45:10','Pred njima je Džehennem, i ni od kakve koristi im neće biti ono što sustekli, ni oni koje su, pored Allaha, za zaštitnike uzeli, i njih čeka patnjagolema.');
INSERT INTO "bs_darassalam" VALUES(45,11,'45:11','Ovo je Uputa, a one koji ne vjeruju u ajete i znakove Gospodara svogačeka bolna, najveća kazna.');
INSERT INTO "bs_darassalam" VALUES(45,12,'45:12','Allah je Onaj Koji vam je potčinio more da bi lađe, odredbom Njegovom, po njemu plovile, da biste mogli tražiti blagodati Njegove i da biste bili zahvalni.');
INSERT INTO "bs_darassalam" VALUES(45,13,'45:13','I potčinio vam je ono što je na nebesima i na Zemlji, sve je od Njega! To su, zaista, znaci za ljude koji razmišljaju.');
INSERT INTO "bs_darassalam" VALUES(45,14,'45:14','Reci vjernicima da oproste onima koji ne očekuju Allahove dane u kojima će On nagraditi ljude za ono što su radili.');
INSERT INTO "bs_darassalam" VALUES(45,15,'45:15','Onaj koji čini dobro – sebi ga čini, a onaj koji čini zlo – protiv sebe radi; a na kraju ćete se Gospodaru svome vratiti.');
INSERT INTO "bs_darassalam" VALUES(45,16,'45:16','Sinovima smo Israilovim Knjigu, i vlast, i vjerovjesništvo dali, i iz ljepotaih opskrbili, i nad ostalim svjetovima ih odlikovali!');
INSERT INTO "bs_darassalam" VALUES(45,17,'45:17','I jasne smo im dokaze o vjeri dali, a oni su se podvojili baš onda kad su do saznanja došli, i to zbog neprijateljstva i međusobne zavisti. Gospodar tvoj će im na Kijametskom danu, sigurno, presuditi u vezi s onim u čemu su se razilazili.');
INSERT INTO "bs_darassalam" VALUES(45,18,'45:18','A tebi smo poslije odredili da u vjeri ideš pravcem određenim, zato gaslijedi i ne povodi se za hirovima onih koji ne znaju,');
INSERT INTO "bs_darassalam" VALUES(45,19,'45:19','oni te, zbilja, od Allaha nimalo odbraniti ne mogu! Zulumćari su jednidrugima zaštitnici, a Allah je zaštitnik bogobojaznih.');
INSERT INTO "bs_darassalam" VALUES(45,20,'45:20','Ovaj Kur’an putokaz je ljudima, i uputa i milost onima koji čvrsto vjeruju.');
INSERT INTO "bs_darassalam" VALUES(45,21,'45:21','Misle li oni koji čine zla djela da ćemo s njima postupiti jednako kao sonima koji vjeruju i dobra djela čine, da će u ovom životu i nakon smrti biti isti?! Kako loše rasuđuju!');
INSERT INTO "bs_darassalam" VALUES(45,22,'45:22','A Allah je nebesa i Zemlju s Istinom stvorio i zato da bi svaka osoba bila nagrađena ili kažnjena prema onom što je zaradila i njima zulum učinjen neće biti.');
INSERT INTO "bs_darassalam" VALUES(45,23,'45:23','Šta misliš o onome koji je svoju strast za boga svoga uzeo, onome kome je Allah, a on svjestan bio, dao da je u zabludi, i sluh njegov i srce njegovo zapečatio, a pred oči njegove koprenu stavio? Ko će ga, ako neće Allah, na Pravi put uputiti? Zašto se ne prisjetite?');
INSERT INTO "bs_darassalam" VALUES(45,24,'45:24','I oni vele: “Postoji samo život naš zemaljski, živimo i umiremo, jedino nas vrijeme uništi.” A oni o tome baš ništa ne znaju, oni samo nagađaju.');
INSERT INTO "bs_darassalam" VALUES(45,25,'45:25','A kad im se Naši ajeti jasni kazuju, drugog dokaza nemaju osim što kažu: “Pretke naše u život povratite ako je istina to što govorite.”');
INSERT INTO "bs_darassalam" VALUES(45,26,'45:26','Reci: “Allah vam daje život, zatim će učiniti da poumirete, a poslije će vas na Kijametskom danu sabrati, u to nema nikakve sumnje, ali većina ljudi ne zna.”');
INSERT INTO "bs_darassalam" VALUES(45,27,'45:27','Allahova je vlast na nebesima i na Zemlji! A na dan kad nastupi Časoživljenja, taj dan oni koji su laži slijedili bit će izgubljeni.');
INSERT INTO "bs_darassalam" VALUES(45,28,'45:28','I vidjet ćeš sve ummete kako kleče; svaki ummet bit će pozvan premasvojoj Knjizi: “Danas ćete biti nagrađeni ili kažnjeni prema tome kako ste postupali!”');
INSERT INTO "bs_darassalam" VALUES(45,29,'45:29','Ova Knjiga Naša o vama će samo istinu reći, jer smo naredili da se zapiše sve što ste radili.');
INSERT INTO "bs_darassalam" VALUES(45,30,'45:30','One koji su vjerovali i dobra djela činili Gospodar njihov u milost Svoju će uvesti. To će biti uspjeh očiti!');
INSERT INTO "bs_darassalam" VALUES(45,31,'45:31','A onima koji nisu vjerovali: “Zar vam ajeti Moji nisu kazivani, ali vi ste se oholili i narod prestupnički bili?”');
INSERT INTO "bs_darassalam" VALUES(45,32,'45:32','Kad se govorilo: “Allahovo je obećanje, zaista, istina! I u Čas oživljenjanema nimalo sumnje”, vi ste govorili: “Mi ne znamo šta je Čas oživljenja, mi samo zamišljamo, mi nismo uvjereni.”');
INSERT INTO "bs_darassalam" VALUES(45,33,'45:33','I ukazat će im se gnusna djela njihova i sa svih strana će ih okružiti ono čemu su se rugali...');
INSERT INTO "bs_darassalam" VALUES(45,34,'45:34','i reći će se: “Danas ćemo Mi vas zaboraviti kao što ste vi zaboravili da ćete ovaj vaš Dan doživjeti, vatra će vam prebivalište biti i niko vam neće moći pomoći...');
INSERT INTO "bs_darassalam" VALUES(45,35,'45:35','zato što ste se Allahovim riječima rugali i što vas je život na Zemlji bio obmanuo.” Od tog Dana ih iz džehennema niko neće izvući, niti će se od njih tražiti da odobrovolje Allaha.');
INSERT INTO "bs_darassalam" VALUES(45,36,'45:36','Pa neka je hvaljen Allah, Gospodar nebesa i Gospodar Zemlje, Gospodar svih svjetova!');
INSERT INTO "bs_darassalam" VALUES(45,37,'45:37','Njemu ponos i dika na nebesima i na Zemlji, On je Silni i Mudri!');
INSERT INTO "bs_darassalam" VALUES(46,1,'46:1','Hā Mīm.');
INSERT INTO "bs_darassalam" VALUES(46,2,'46:2','Knjigu objavljuje Allah, Silni i Mudri!');
INSERT INTO "bs_darassalam" VALUES(46,3,'46:3','Mi smo nebesa i Zemlju i ono što je između njih mudro stvorili i do roka određenog, ali nevjernici okreću glave od onog čime im se prijeti.');
INSERT INTO "bs_darassalam" VALUES(46,4,'46:4','Reci: “Kažite vi meni ili pokažite mi šta su stvorili na Zemlji i imaju li ikakva udjela u nebesima oni kojima se vi, pored Allaha, klanjate? Donesite mi Knjigu objavljenu prije ove ili samo kakav ostatak znanja, ako istinu govorite.”');
INSERT INTO "bs_darassalam" VALUES(46,5,'46:5','Ko je u većoj zabludi od onih koji se, umjesto Allahu, klanjaju onima koji im se do Sudnjeg dana neće odazvati i koji su prema njihovim molbama ravnodušni?');
INSERT INTO "bs_darassalam" VALUES(46,6,'46:6','Kad ljudi budu sabrani, oni će im biti neprijatelji i poreći će da su im se klanjali.');
INSERT INTO "bs_darassalam" VALUES(46,7,'46:7','A kad se ovima Naši jasni ajeti kazuju, onda oni koji ne vjeruju govore o Istini čim im dođe: “Ovo je prava čarolija!”');
INSERT INTO "bs_darassalam" VALUES(46,8,'46:8','Zar oni da govore: “On ga izmišlja!” Reci: “Pa ako ga izmišljam, vi mene od kazne Allahove nećete moći odbraniti. On dobro zna šta o Kur’anu govorite. On je dovoljan svjedok i meni i vama. On prašta i samilostan je.”');
INSERT INTO "bs_darassalam" VALUES(46,9,'46:9','Reci: “Ja nisam prvi poslanik i ne znam šta će biti sa mnom ili s vama. Ja slijedim samo ono što mi se objavljuje i samo sam dužan otvoreno opominjati.”');
INSERT INTO "bs_darassalam" VALUES(46,10,'46:10','Reci: “Kažite vi meni šta će s vama biti ako je Kur’an od Allaha, a vi u njega nećete da vjerujete, i ako je jedan od sinova Israilovih posvjedočio da je i on od Allaha, pa on povjerovao, a vi se uzoholili.” Allah, zaista, neće ukazati na Pravi put narodu koji sebi nepravdu čini.');
INSERT INTO "bs_darassalam" VALUES(46,11,'46:11','I govore nevjernici o vjernicima: “Da je kakvo dobro, nas oni u tome ne bi pretekli.” A kako pomoću njega Pravi put nisu našli, sigurno će reći: “Ovo je još davna izmišljotina.”');
INSERT INTO "bs_darassalam" VALUES(46,12,'46:12','Knjiga Musaova bila je putovođa i milost prije njega. A ovo je Knjiga na arapskom jeziku koja nju potvrđuje da bi opomenula one koji rade zlo i obradovala one koji čine dobro.');
INSERT INTO "bs_darassalam" VALUES(46,13,'46:13','Oni koji govore: “Naš Gospodar je Allah!” i istraju na Pravom putu, neka se ničeg ne boje i ni za čim neka ne tuguju!');
INSERT INTO "bs_darassalam" VALUES(46,14,'46:14','Oni će stanovnici dženneta biti i u njemu će vječno boraviti, i to će im biti nagrada za ono što su radili.');
INSERT INTO "bs_darassalam" VALUES(46,15,'46:15','Čovjeka smo zadužili da roditeljima svojim čini dobro. Majka ga njegova s mukom nosi i u mukama ga rađa, nosi ga i doji trideset mjeseci. A kad dospije u muževno doba i kad dostigne četrdeset godina, on rekne: “Gospodaru moj, dozvoli mi da Ti budem zahvalan na blagodati koju si darovao meni i roditeljima mojim, i pomozi mi da činim dobra djela kojima ćeš zadovoljan biti, i učini dobrim potomke moje – ja se, zaista, kajem i odan sam Tebi.”');
INSERT INTO "bs_darassalam" VALUES(46,16,'46:16','Eto, od takvih ćemo Mi dobra djela koja su radili primiti, a preko ružnih postupaka njihovih preći; od stanovnika će Dženneta oni biti, istinito ćemo obećanje koje im je dato ispuniti.');
INSERT INTO "bs_darassalam" VALUES(46,17,'46:17','A onaj koji roditeljima svojim kaže: “Ih, što mi prijetite da ću biti oživljen kad su prije mene toliki narodi bili i nestali!” – a oni, zazivajući Allaha u pomoć, govore: “Teško tebi, vjeruj, Allahova će se prijetnja, doista, obistiniti!” On odgovara: “To su izmišljotine naroda drevnih!”');
INSERT INTO "bs_darassalam" VALUES(46,18,'46:18','Na takvima se treba ispuniti Riječ rečena o narodima: džinima i ljudima koji su prije njih bili i nestali, jer oni su, zaista, nastradali.');
INSERT INTO "bs_darassalam" VALUES(46,19,'46:19','Za sve će biti posebni stepeni, prema tome kako su radili, da ih nagradi ili kazni za djela njihova – nepravda im se neće učiniti.');
INSERT INTO "bs_darassalam" VALUES(46,20,'46:20','A na Dan kad oni koji nisu vjerovali pred vatrom budu zaustavljeni: “Vi ste u svom životu na Zemlji sve svoje naslade iskoristili i u njima uživali, a danas sramnom patnjom bit ćete kažnjeni zato što ste se na Zemlji, bez ikakva osnova, oholo ponašali i što ste raskalašeni bili.”');
INSERT INTO "bs_darassalam" VALUES(46,21,'46:21','I spomeni brata Adova, kad je narod svoj u el-Ahkafu opominjao – a bilo je i prije njega i poslije njega poslanika: “Činite ibadet samo Allahu, ja, zaista, strahujem da ćete biti u mukama na Velikom danu!"');
INSERT INTO "bs_darassalam" VALUES(46,22,'46:22','oni su odgovarali: “Zar si nama došao da nas od božanstava naših odvratiš? Neka se, eto, ostvare prijetnje tvoje, ako istinu govoriš!”');
INSERT INTO "bs_darassalam" VALUES(46,23,'46:23','“Samo Allah zna kad će to biti”, on bi govorio, “ja vam kazujem ono što mi se objavljuje, a vi ste, vidim, narod u neznanju ogrezao.”');
INSERT INTO "bs_darassalam" VALUES(46,24,'46:24','I kad ugledaše na obzorju oblak, koji se prema dolinama njihovim kretao, povikaše: “Ovaj nam oblak kišu donosi!” “Ne, to je ono što ste požurivali: vjetar koji vam bolnu patnju nosi!"');
INSERT INTO "bs_darassalam" VALUES(46,25,'46:25','koji, voljom Gospodara svog, sve ruši.” I ujutro su se vidjele samo nastambe njihove – tako Mi kažnjavamo narod griješni.');
INSERT INTO "bs_darassalam" VALUES(46,26,'46:26','Njima smo dali mogućnosti koje vama nismo dali. I sluh i vid i razum smo im dali, ali im ni sluh njihov ni vid njihov ni razum njihov nisu ni od kakve koristi bili, jer su Allahove dokaze poricali – i sa svih strana ih je okružilo ono čemu su se ismijavali.');
INSERT INTO "bs_darassalam" VALUES(46,27,'46:27','Neke gradove oko vas još davno smo uništili, a objasnili smo im bili na razne načine dokaze, ne bi li se pokajali.');
INSERT INTO "bs_darassalam" VALUES(46,28,'46:28','A zašto im nisu pomogli oni koje su, pored Allaha, prihvatili da im budu posrednici i bogovi? Ali, njih nije bilo. To su bile samo klevete njihove i laži.');
INSERT INTO "bs_darassalam" VALUES(46,29,'46:29','Kad ti poslasmo nekoliko džina da Kur’an slušaju, kad dođoše da ga čuju, oni rekoše: “Ćutite i pomno slušajte!” A kad se završi, vratiše se narodu svom da opominju.');
INSERT INTO "bs_darassalam" VALUES(46,30,'46:30','“O narode naš”, govorili su, “mi smo slušali Knjigu koja se poslije Musaa objavljuje, koja potvrđuje da su istinite i one prije nje, i koja ka istini i na Pravi put upućuje.');
INSERT INTO "bs_darassalam" VALUES(46,31,'46:31','O narode naš, odazovite se Allahovu glasniku i vjerujte u Allaha, On će vam neke grijehe vaše oprostiti i vas od patnje neizdržljive zaštititi!”');
INSERT INTO "bs_darassalam" VALUES(46,32,'46:32','A oni koji se ne odazovu Allahovu glasniku, takvi Mu na Zemlji neće umaći i mimo Njega zaštitnika neće naći. Oni su u velikoj zabludi.”');
INSERT INTO "bs_darassalam" VALUES(46,33,'46:33','Zar ne znaju da je Allah – Koji je nebesa i Zemlju stvorio i Koji se nije nimalo umorio dok ih je stvarao – kadar oživiti mrtve? Jest, On sve može.');
INSERT INTO "bs_darassalam" VALUES(46,34,'46:34','A na Dan kad oni koji nisu vjerovali pred vatrom budu zaustavljeni: “Zar ovo nije istina?” – odgovorit će: “Jest, Gospodara nam našeg!” “E pa iskusite patnju”, reći će On, “jer ste stalno poricali.”');
INSERT INTO "bs_darassalam" VALUES(46,35,'46:35','Ti izdrži kao što su izdržali odlučni poslanici i ne traži da im kazna što prije dođe! A onog Dana kad dožive ono čime im se prijeti, učinit će im se da su ostali samo jedan dio dana. Ovo je obznana! A zar će ko drugi biti uništen do narod griješnički!?');
INSERT INTO "bs_darassalam" VALUES(47,1,'47:1','Allah će poništiti djela onih koji ne vjeruju i od puta Njegova odvraćaju.');
INSERT INTO "bs_darassalam" VALUES(47,2,'47:2','A onima koji vjeruju i dobra djela čine i vjeruju u ono što se objavljuje Muhammedu – a to je istina od Gospodara njihova – On će preko hrđavih postupaka njihovih preći i prilike će njihove poboljšati...');
INSERT INTO "bs_darassalam" VALUES(47,3,'47:3','zato što nevjernici slijede neistinu, a vjernici slijede istinu od Gospodara svog. Tako Allah navodi zbog ljudi primjere njihove.');
INSERT INTO "bs_darassalam" VALUES(47,4,'47:4','Kad se u borbi s nevjernicima sretnete po šijama ih udarajte sve dok ih ne oslabite, a onda ih vežite, i poslije, ili ih velikodušno sužanjstva oslobodite ili otkupninu zahtijevajte, sve dok borba ne prestane. Tako učinite! Da Allah hoće, On bi im se osvetio, ali On vas želi iskušati jedne pomoću drugih. On neće poništiti djela onih koji na Allahovu putu poginu...');
INSERT INTO "bs_darassalam" VALUES(47,5,'47:5','i On će ih, sigurno, uputiti i prilike njihove poboljšati...');
INSERT INTO "bs_darassalam" VALUES(47,6,'47:6','i u Džennet ih uvesti, o kome ih je već upoznao.');
INSERT INTO "bs_darassalam" VALUES(47,7,'47:7','O vjernici, ako Allaha pomognete, i On će vama pomoći i korake vaše učvrstiti.');
INSERT INTO "bs_darassalam" VALUES(47,8,'47:8','A onima koji ne vjeruju – propast njima! On neće djela njihova prihvatiti...');
INSERT INTO "bs_darassalam" VALUES(47,9,'47:9','zato što ne vole ono što Allah objavljuje, i On će djela njihova poništiti.');
INSERT INTO "bs_darassalam" VALUES(47,10,'47:10','Zašto oni ne idu po svijetu da vide kakav je bio kraj onih prije njih? Njih je Allah istrijebio, a to čeka i ostale nevjernike...');
INSERT INTO "bs_darassalam" VALUES(47,11,'47:11','zato što je Allah zaštitnik onih koji vjeruju i što nevjernici zaštitnika nemaju.');
INSERT INTO "bs_darassalam" VALUES(47,12,'47:12','Allah će one koji vjeruju i dobra djela čine uvesti u džennetske bašče, kroz koje rijeke teku. A oni koji ne vjeruju – koji se naslađuju i žderu kao što stoka ždere – njihovo će prebivalište Vatra biti!');
INSERT INTO "bs_darassalam" VALUES(47,13,'47:13','A koliko smo gradova razorili, mnogo moćnijih od tvog grada koji te protjerao, i nije bilo nikog da im pomogne.');
INSERT INTO "bs_darassalam" VALUES(47,14,'47:14','Zar je onaj koji ima jasnu predstavu o Gospodaru svom kao onaj kojem se lijepim čine ružna djela njegova i koji se za strastima svojim povodi?');
INSERT INTO "bs_darassalam" VALUES(47,15,'47:15','Zar je Džennet, koji je obećan onima koji se Allaha boje, u kome su rijeke od vode neustajale i rijeke od mlijeka nepromijenjena okusa, i rijeke od vina, prijatna onima koji piju, i rijeke od meda procijeđenog i gdje ima voća svakovrsnog i oprosta od Gospodara njihova – zar je to isto što i patnja koja čeka one koji će u Vatri vječno boraviti, koji će se uzavrelom vodom pojiti, koja će im crijeva kidati!?');
INSERT INTO "bs_darassalam" VALUES(47,16,'47:16','Ima onih koji te dolaze slušati, a čim se od tebe udalje, pitaju one kojima je dato znanje: “Šta ono on maloprije reče?” To su oni čija je srca Allah zapečatio i koji se za strastima svojim povode.');
INSERT INTO "bs_darassalam" VALUES(47,17,'47:17','A one koji su na Pravom putu On će i dalje voditi i nadahnut će ih kako će se Vatre sačuvati.');
INSERT INTO "bs_darassalam" VALUES(47,18,'47:18','Zar oni čekaju da im Smak svijeta iznenada dođe, a već su predznaci njegovi tu? A šta će im koristiti opomena kad im on dođe?');
INSERT INTO "bs_darassalam" VALUES(47,19,'47:19','Znaj da nema istinskog božanstva osim Allaha! Traži oprosta za svoje grijehe i za vjernike i za vjernice! Allah zna kud se krećete i gdje boravite.');
INSERT INTO "bs_darassalam" VALUES(47,20,'47:20','A oni koji vjeruju govore: “Zašto se ne objavi jedna sura?” A kad bī objavljena jedna sura i u njoj spomenuta borba, ti si mogao vidjeti one čija su srca bolesna kako te gledaju pogledom čovjeka pred smrt onesviješćenog! Teško njima!');
INSERT INTO "bs_darassalam" VALUES(47,21,'47:21','Bolje bi im bilo da su poslušni i da govore razumne besjede! A kad je borba već propisana, bolje bi im bilo da su prema Allahu iskreni.');
INSERT INTO "bs_darassalam" VALUES(47,22,'47:22','Zar i vi ne biste, kad biste se vlasti dočepali, nered na Zemlji činili i rodbinske veze kidali!');
INSERT INTO "bs_darassalam" VALUES(47,23,'47:23','To su oni koje je Allah prokleo i gluhim i slijepim ih učinio.');
INSERT INTO "bs_darassalam" VALUES(47,24,'47:24','Kako oni ne razmisle o Kur’anu, ili su im na srcima katanci!?');
INSERT INTO "bs_darassalam" VALUES(47,25,'47:25','One koji su od vjere svoje otpali, pošto im je bio jasan Pravi put, šejtan je na grijeh navodio i lažnu im nadu ulijevao.');
INSERT INTO "bs_darassalam" VALUES(47,26,'47:26','To je zato što su govorili onima koji ne vole ono što Allah objavljuje: “Mi ćemo vam se u nekim stvarima pokoravati.” A Allah dobro zna njihove tajne.');
INSERT INTO "bs_darassalam" VALUES(47,27,'47:27','A kako će tek biti kad im meleki budu duše uzimali udarajući ih po obrazima i po leđima njihovim!');
INSERT INTO "bs_darassalam" VALUES(47,28,'47:28','To će biti zato što su ono što izaziva Allahovu srdžbu slijedili, a ono čime je On zadovoljan prezirali; On će djela njihova poništiti.');
INSERT INTO "bs_darassalam" VALUES(47,29,'47:29','Zar oni bolesnih srca misle da Allah zlobe njihove na vidjelo neće iznijeti?');
INSERT INTO "bs_darassalam" VALUES(47,30,'47:30','A da hoćemo, Mi bismo ti ih, uistinu, pokazali i ti bi ih, sigurno, po biljezima njihovim poznao. Ali, ti ćeš ih poznati po načinu govora njihova. – A Allah zna postupke vaše.');
INSERT INTO "bs_darassalam" VALUES(47,31,'47:31','Mi ćemo vas provjeravati sve dok ne ukažemo na borce i postojane među vama, a i vijesti o vama ćemo provjeravati.');
INSERT INTO "bs_darassalam" VALUES(47,32,'47:32','Oni koji ne vjeruju i od Allahovog puta odvraćaju, koji su Poslanikovi protivnici, kad im je već postao jasan Pravi put, zaista neće nimalo Allahu nauditi. On će djela njihova poništiti.');
INSERT INTO "bs_darassalam" VALUES(47,33,'47:33','O vjernici, pokoravajte se Allahu i pokoravajte se Poslaniku, i ne ništite djela svoja!');
INSERT INTO "bs_darassalam" VALUES(47,34,'47:34','Onima koji ne vjeruju i od Allahova puta odvraćaju, pa poslije kao nevjernici umiru, Allah, zaista, neće oprostiti.');
INSERT INTO "bs_darassalam" VALUES(47,35,'47:35','I ne budite kukavice i ne nudite primirje kad ste jači, jer Allah je s vama. On vas neće nagrada za djela vaša lišiti.');
INSERT INTO "bs_darassalam" VALUES(47,36,'47:36','Život na ovom svijetu je samo igra i zabava! A ako budete vjerovali i grijehe izbjegavali, On će vas nagraditi i imanja vaša od vas neće tražiti.');
INSERT INTO "bs_darassalam" VALUES(47,37,'47:37','Ako bi ih od vas tražio i prema vama uporan bio, postali biste škrti i On bi učinio vidljivim vaše kivnje.');
INSERT INTO "bs_darassalam" VALUES(47,38,'47:38','Vi se, eto, pozivate da trošite na Allahovom putu, ali neki su od vas škrti – a ko škrtari, na svoju štetu škrtari, jer Allah je bogat, a vi ste siromašni. A ako glave okrenete, On će vas drugim narodom zamijeniti, koji onda kao što ste vi neće biti.');
INSERT INTO "bs_darassalam" VALUES(48,1,'48:1','Mi ćemo ti dati sigurnu pobjedu...');
INSERT INTO "bs_darassalam" VALUES(48,2,'48:2','da bi ti Allah ranije i kasnije krivice oprostio, da bi blagodat Svoju tebi potpunom učinio, da bi te na Pravi put uputio...');
INSERT INTO "bs_darassalam" VALUES(48,3,'48:3','i da bi te Allah pobjedonosnom pomoći pomogao.');
INSERT INTO "bs_darassalam" VALUES(48,4,'48:4','On uliva smirenost u srca vjernika da bi još više učvrstili vjerovanje koje imaju – a vojske nebesa i Zemlje Allahove su – Allah sve zna i mudar je...');
INSERT INTO "bs_darassalam" VALUES(48,5,'48:5','da bi vjernike i vjernice uveo u džennetske bašče, kroz koje rijeke teku, u kojima će vječno boraviti, i da bi prešao preko hrđavih postupaka njihovih – a to je u Allaha veliki uspjeh...');
INSERT INTO "bs_darassalam" VALUES(48,6,'48:6','i da bi kaznio licemjere i licemjerke i mnogobošce i mnogoboškinje koji o Allahu zlo misle – neka zlo njih snađe! Allah se na njih rasrdio i prokleo ih i pripremio im Džehennem, a grozno je on boravište!');
INSERT INTO "bs_darassalam" VALUES(48,7,'48:7','Allahove su vojske nebesa i Zemlje. – Allah je silan i mudar!');
INSERT INTO "bs_darassalam" VALUES(48,8,'48:8','Mi šaljemo tebe kao svjedoka i kao donosioca radosnih vijesti i kao onog koji treba opominjati...');
INSERT INTO "bs_darassalam" VALUES(48,9,'48:9','da u Allaha i Poslanika Njegova vjerujete, i da vjeru Njegovu pomognete, i da Ga veličate i da Ga ujutro i navečer hvalite.');
INSERT INTO "bs_darassalam" VALUES(48,10,'48:10','Oni koji su se zakleli na vjernost – zakleli su se, doista, na vjernost samome Allahu, Allahova je ruka iznad ruku njihovih! Onaj ko prekrši zakletvu – krši je na svoju štetu, a ko ispuni ono na šta se obavezao Allahu, On će mu dati veliku nagradu.');
INSERT INTO "bs_darassalam" VALUES(48,11,'48:11','Govorit će ti beduini koji su izostali: “Zadržala su nas stada naša i porodice naše, pa zamoli za nas oprost!” Oni govore jezicima svojim ono što nije u srcima njihovim. Reci: “Pa ko može promijeniti Allahovu odluku, ako vam On hoće nauditi, ili ako vam hoće kakvo dobro učiniti?” Allah zna ono što radite.');
INSERT INTO "bs_darassalam" VALUES(48,12,'48:12','Vi ste mislili da se Poslanik i vjernici nikad neće vratiti porodicama svojim; vaša su srca bila zadovoljna zbog toga i vi ste na najgore pomišljali, vi ste narod u duši pokvaren.');
INSERT INTO "bs_darassalam" VALUES(48,13,'48:13','Onaj ko ne vjeruje u Allaha i Poslanika Njegova – pa Mi smo za nevjernike razbuktali oganj pripremili!');
INSERT INTO "bs_darassalam" VALUES(48,14,'48:14','Allahova je vlast na nebesima i na Zemlji, On prašta kome On hoće, a kažnjava koga hoće. – Allah mnogo prašta i samilostan je.');
INSERT INTO "bs_darassalam" VALUES(48,15,'48:15','Oni koji su izostali sigurno će reći kad pođete da plijen uzmete: “Pustite i nas da vas pratimo!” – da bi izmijenili Allahove riječi. Reci: “Vi nas nećete pratiti, to je još prije Allah rekao!” – a oni će reći: “Nije tako, nego, vi nama zavidite.” A nije ni to, već oni malo šta razumiju.');
INSERT INTO "bs_darassalam" VALUES(48,16,'48:16','Reci beduinima koji su izostali: “Bit ćete pozvani da se borite protiv naroda veoma hrabrog i moćnog, sve dok se ne pokori. Pa ako poslušate, Allah će vam lijepu nagradu dati. A ako izbjegnete, kao što ste i dosad izbjegavali, na muke će vas nesnosne staviti.”');
INSERT INTO "bs_darassalam" VALUES(48,17,'48:17','Nije grijeh slijepom, ni hromom, ni bolesnom! Onog koji se pokorava Allahu i Poslaniku Njegovu On će u džennetske bašče, kroz koje teku rijeke, uvesti, a onog ko leđa okrene, patnjom će nesnosnom kazniti.');
INSERT INTO "bs_darassalam" VALUES(48,18,'48:18','Allah je zadovoljan onim vjernicima koji su ti se pod drvetom na vjernost zakleli. On je znao šta je u srcima njihovim, pa je spustio smirenost na njih, i nagradit će ih skorom pobjedom...');
INSERT INTO "bs_darassalam" VALUES(48,19,'48:19','i bogatim plijenom koji će uzeti – jer Allah je silan i mudar.');
INSERT INTO "bs_darassalam" VALUES(48,20,'48:20','Allah vam obećava bogat plijen koji ćete uzeti, a s ovim je požurio i ruke ljudi je od vas zadržao – da bi to bio poučan primjer za vjernike i da bi vam na Pravi put ukazao...');
INSERT INTO "bs_darassalam" VALUES(48,21,'48:21','i drugi, koji niste bili u stanju uzeti – Allah vam ga je dao, jer Allah sve može.');
INSERT INTO "bs_darassalam" VALUES(48,22,'48:22','A da su nevjernici s vama borbu zametnuli, sigurno bi se u bijeg dali i poslije ne bi zaštitnika ni pomagača našli...');
INSERT INTO "bs_darassalam" VALUES(48,23,'48:23','prema Allahovu zakonu, koji odvazda važi, a ti nećeš vidjeti da se Allahov zakon promijeni.');
INSERT INTO "bs_darassalam" VALUES(48,24,'48:24','On je zadržao ruke njihove od vas i vaše od njih usred Meke, i to nakon što vam je pružio mogućnost da ih pobijedite. – A Allah dobro vidi ono što vi radite.');
INSERT INTO "bs_darassalam" VALUES(48,25,'48:25','Oni ne vjeruju i brane vam da pristupite Časnom hramu, i da kurbani koje vodite sa sobom do mjesta svojih stignu. I da nije bilo bojazni da ćete pobiti vjernike, muškarce i žene, koje ne poznajete, pa tako, i ne znajući, zbog njih sramotu doživjeti – Mi bismo vam ih prepustili. A nismo ih prepustili ni zato da bi Allah u milost Svoju uveo onog koga hoće. A da su oni bili odvojeni, doista bismo bolnom kaznom kaznili one među njima koji nisu vjerovali.');
INSERT INTO "bs_darassalam" VALUES(48,26,'48:26','Kad su nevjernici punili svoja srca žarom, žarom paganskim, Allah je spustio smirenost Svoju na Poslanika Svog i na vjernike i obavezao ih da ispunjavaju ono zbog čega će postati pravi vjernici – a oni i jesu najpreči i najdostojniji za to. – A Allah sve zna.');
INSERT INTO "bs_darassalam" VALUES(48,27,'48:27','Allah će obistiniti san Poslanika Svog da ćete, sigurno, u Časni hram ući sigurni – ako Allah bude htio – neki obrijanih glava, a neki podrezanih kosa, bez straha. On je ono što vi niste znali znao i zato vam je, prije toga, nedavnu pobjedu dao.');
INSERT INTO "bs_darassalam" VALUES(48,28,'48:28','On je poslao Poslanika Svog s Uputom i vjerom istinitom da bi je uzdigao iznad svih vjera. A Allah je dovoljan Svjedok!');
INSERT INTO "bs_darassalam" VALUES(48,29,'48:29','Muhammed je Allahov Poslanik, a njegovi su sljedbenici strogi prema nevjernicima, a samilosni među sobom; vidiš ih kako se klanjaju i licem na tle padaju želeći Allahovu nagradu i zadovoljstvo – na licima su im znaci, tragovi od padanja licem na tlo. Tako su opisani u Tevratu. A u Indžilu: oni su kao biljka kad izdanak svoj izbaci pa ga onda učvrsti, i on ojača, i ispravi se na svojoj stabljici izazivajući divljenje sijača – da bi On s vjernicima najedio nevjernike. A onima koji vjeruju i dobra djela čine Allah obećava oprost i nagradu veliku.');
INSERT INTO "bs_darassalam" VALUES(49,1,'49:1','O vi koji vjerujete, ne istupajte ispred Allaha i Poslanika Njegova, i bojte se Allaha! Allah je, zaista, Onaj Koji sve čuje i Onaj Koji sve zna.');
INSERT INTO "bs_darassalam" VALUES(49,2,'49:2','O vi koji vjerujete, ne dižite glasove svoje iznad Vjerovjesnikova glasa i ne razgovarajte s njim glasno, kao što glasno jedan s drugim razgovarate, da ne bi bila poništena vaša djela, a da vi i ne osjetite.');
INSERT INTO "bs_darassalam" VALUES(49,3,'49:3','One koji utišaju glasove svoje pred Allahovim Poslanikom – a to su oni čija je srca Allah prekalio u čestitosti – čeka oprost i nagrada velika.');
INSERT INTO "bs_darassalam" VALUES(49,4,'49:4','Većina onih koji te dozivaju ispred soba nije dovoljno pametna.');
INSERT INTO "bs_darassalam" VALUES(49,5,'49:5','A da su se oni strpjeli dok im ti sam izađeš, bilo bi im bolje. – A Allah prašta i samilostan je.');
INSERT INTO "bs_darassalam" VALUES(49,6,'49:6','O vjernici, ako vam nekakav nepošten čovjek donese kakvu vijest, dobro je provjerite, da u neznanju nekom zlo ne učinite, pa da se zbog onog što ste učinili pokajete.');
INSERT INTO "bs_darassalam" VALUES(49,7,'49:7','I znajte da je među vama Allahov Poslanik – kad bi vas u mnogo čemu poslušao, doista biste nastradali – ali Allah je nekima od vas pravo vjerovanje omilio i u srcima ga vašim lijepim prikazao, a nezahvalnost i neposlušnost vam omrazio. Takvi su na Pravom putu...');
INSERT INTO "bs_darassalam" VALUES(49,8,'49:8','dobrotom i blagodati Allahovom. – A Allah sve zna i mudar je.');
INSERT INTO "bs_darassalam" VALUES(49,9,'49:9','Ako se dvije skupine vjernika sukobe, izmirite ih. A ako jedna od njih ipak učini nasilje drugoj, onda se borite protiv one koja je učinila nasilje sve dok se Allahovim propisima ne prikloni. Pa ako se prikloni, onda ih nepristrasno izmirite i budite pravedni. – Allah zaista pravedne voli.');
INSERT INTO "bs_darassalam" VALUES(49,10,'49:10','Vjernici su samo braća, zato pomirite vaša dva brata i bojte se Allaha da bi vam se milost ukazala.');
INSERT INTO "bs_darassalam" VALUES(49,11,'49:11','O vjernici, neka se muškarci jedni drugima ne rugaju, možda su oni bolji od njih, a ni žene drugim ženama, možda su one bolje od njih. I ne kudite jedni druge i ne zovite jedni druge ružnim nadimcima! Kako je ružno da se vjernici spominju podrugljivim nadimcima! A oni koji se ne pokaju – sami sebi čine nepravdu.');
INSERT INTO "bs_darassalam" VALUES(49,12,'49:12','O vjernici, klonite se mnogih sumnjičenja, neka su sumnjičenja, zaista, grijeh. I ne uhodite jedni druge i ne ogovarajte jedni druge! Zar bi nekom od vas bilo drago jesti meso umrloga brata svog – a vama je to odvratno – zato se bojte Allaha. Allah, zaista, prima pokajanje i samilostan je.');
INSERT INTO "bs_darassalam" VALUES(49,13,'49:13','O ljudi, Mi vas od jednog čovjeka i jedne žene stvaramo i na narode i plemena vas dijelimo da biste se upoznali. Najugledniji je kod Allaha onaj koji Ga se najviše boji. Allah, uistinu, sve zna i nije Mu skriveno ništa.');
INSERT INTO "bs_darassalam" VALUES(49,14,'49:14','Neki beduini govore: “Mi vjerujemo!” Reci: “Vi ne vjerujete, ali recite: ‘Mi se pokoravamo!’ – jer u srca vaša prava vjera još nije ušla. A ako Allaha i Njegovog Poslanika budete slušali, On vam nimalo neće umanjiti nagradu za djela vaša.” – Allah, uistinu, prašta i samilostan je.');
INSERT INTO "bs_darassalam" VALUES(49,15,'49:15','Pravi su vjernici samo oni koji u Allaha i Poslanika Njegova vjeruju, i poslije više ne sumnjaju, i bore se na Allahovom putu imecima svojim i životima svojim. Oni su iskreni!');
INSERT INTO "bs_darassalam" VALUES(49,16,'49:16','Reci: "Zar vi da obavještavate Allaha o vjerovanju svom, kad Allah zna sve što je na nebesima i na Zemlji! Allah sve zna.”');
INSERT INTO "bs_darassalam" VALUES(49,17,'49:17','Oni ti predbacuju što su primili islam. Reci: “Ne predbacujte mi što ste islam primili. Naprotiv, Allah je vama milost podario time što vas je u pravu vjeru uputio, ako iskreno govorite."');
INSERT INTO "bs_darassalam" VALUES(49,18,'49:18','Allah, sigurno, zna tajne nebesa i Zemlje i Allah dobro vidi ono što radite.”');
INSERT INTO "bs_darassalam" VALUES(50,1,'50:1','Kāf. Tako Mi Kur’ana slavnog...');
INSERT INTO "bs_darassalam" VALUES(50,2,'50:2','oni se čude što im je došao jedan od njih da ih opominje, pa nevjernici govore: “To je čudna stvar...');
INSERT INTO "bs_darassalam" VALUES(50,3,'50:3','zar kad pomremo i zemlja postanemo!? Nezamisliv je to povratak!”');
INSERT INTO "bs_darassalam" VALUES(50,4,'50:4','Mi znamo šta će od njih zemlja oduzeti, u Nas je Knjiga u kojoj se sve čuva.');
INSERT INTO "bs_darassalam" VALUES(50,5,'50:5','Oni, međutim, poriču istinu koja im dolazi i smeteni su.');
INSERT INTO "bs_darassalam" VALUES(50,6,'50:6','A zašto ne pogledaju nebo iznad sebe – kako smo ga sazdali i ukrasili i kako u njemu nema nereda!?');
INSERT INTO "bs_darassalam" VALUES(50,7,'50:7','A Zemlju smo rasprostrli i po njoj nepomična brda pobacali i dali da iz nje niče raznovrsno prekrasno bilje...');
INSERT INTO "bs_darassalam" VALUES(50,8,'50:8','da bi razmislio i opomenuo se svaki rob koji se Gospodaru svom obraća.');
INSERT INTO "bs_darassalam" VALUES(50,9,'50:9','Mi s neba spuštamo vodu kao blagoslov i činimo da, uz pomoć njenu, niču vrtovi i žito koje se ženje...');
INSERT INTO "bs_darassalam" VALUES(50,10,'50:10','i visoke palme u kojih su zameci nagomilani jedni iznad drugih...');
INSERT INTO "bs_darassalam" VALUES(50,11,'50:11','kao hranu robovima, i Mi njome oživljavamo mrtav predio – takvo će biti i oživljenje.');
INSERT INTO "bs_darassalam" VALUES(50,12,'50:12','Prije njih poricali su narod Nuhov, i stanovnici er-Ressa, i Semud...');
INSERT INTO "bs_darassalam" VALUES(50,13,'50:13','i Ad, i narod faraonov, i narod Lutov...');
INSERT INTO "bs_darassalam" VALUES(50,14,'50:14','i stanovnici Ejke i narod Tubba – svi su oni poslanike lažnim smatrali i kaznu Moju zaslužili.');
INSERT INTO "bs_darassalam" VALUES(50,15,'50:15','Pa zar smo prilikom prvog stvaranja malaksali? Ne, ali oni u ponovno stvaranje sumnjaju.');
INSERT INTO "bs_darassalam" VALUES(50,16,'50:16','Mi stvaramo čovjeka i znamo šta mu sve duša njegova haje, jer Mi smo njemu bliži od vratne žile kucavice.');
INSERT INTO "bs_darassalam" VALUES(50,17,'50:17','Kad se dvojica sastanu i sjednu jedan s desne, a drugi s lijeve strane...');
INSERT INTO "bs_darassalam" VALUES(50,18,'50:18','on ne izusti ni jednu riječ, a da pored njega nije prisutan onaj koji bdije.');
INSERT INTO "bs_darassalam" VALUES(50,19,'50:19','Smrtne će muke zbilja doći – to je nešto od čega ne možeš pobjeći...');
INSERT INTO "bs_darassalam" VALUES(50,20,'50:20','i u rog će se puhnuti, to je Dan kojim se prijeti...');
INSERT INTO "bs_darassalam" VALUES(50,21,'50:21','i svako će doći, a s njime i vodič i svjedok.');
INSERT INTO "bs_darassalam" VALUES(50,22,'50:22','“Ti nisi mario za ovo, pa smo ti skinuli koprenu tvoju, danas ti je oštar vid.”');
INSERT INTO "bs_darassalam" VALUES(50,23,'50:23','A drug će njegov reći: “Ovo što je kod mene spremno je.”');
INSERT INTO "bs_darassalam" VALUES(50,24,'50:24','“Bacite vas dvojica u džehennem svakog nezahvalnika, inadžiju...');
INSERT INTO "bs_darassalam" VALUES(50,25,'50:25','koji je branio da se čine dobra djela i koji je nasilnik i podozriv bio...');
INSERT INTO "bs_darassalam" VALUES(50,26,'50:26','koji je pored Allaha u drugog boga vjerovao – zato ga bacite u patnju najtežu!”');
INSERT INTO "bs_darassalam" VALUES(50,27,'50:27','A drug će njegov reći: “Gospodaru naš, ja ga nisam silom zaveo, sam je u velikoj zabludi bio.”');
INSERT INTO "bs_darassalam" VALUES(50,28,'50:28','“Ne prepirite se preda Mnom!”, reći će On, “još davno sam vam zaprijetio...');
INSERT INTO "bs_darassalam" VALUES(50,29,'50:29','Moja se Riječ ne mijenja i Ja nisam prema robovima Svojim nepravičan.”');
INSERT INTO "bs_darassalam" VALUES(50,30,'50:30','Na Dan kad upitamo Džehennem: “Jesi li se napunio?” – on će odgovoriti: “Ima li još?”');
INSERT INTO "bs_darassalam" VALUES(50,31,'50:31','A džennet će biti primaknut čestitima, neće biti ni od jednog daleko...');
INSERT INTO "bs_darassalam" VALUES(50,32,'50:32','“Ovo je ono što vam je obećano, svakom onom koji se kajao i čuvao...');
INSERT INTO "bs_darassalam" VALUES(50,33,'50:33','koji se Milostivog bojao, iako Ga nije vidio, i koji je srce odano donio.');
INSERT INTO "bs_darassalam" VALUES(50,34,'50:34','Uđite u njega, u miru, ovo je Dan vječni!”');
INSERT INTO "bs_darassalam" VALUES(50,35,'50:35','U njemu će imati što god zažele – a od Nas i više.');
INSERT INTO "bs_darassalam" VALUES(50,36,'50:36','A koliko smo naroda prije ovih uništili, koji su moćniji od njih bili, pa su po svijetu utočište tražili – od propasti spasa ima li!?');
INSERT INTO "bs_darassalam" VALUES(50,37,'50:37','U tome je, zaista, pouka za onog ko razum ima ili ko sluša a priseban je.');
INSERT INTO "bs_darassalam" VALUES(50,38,'50:38','Mi smo stvorili nebesa i Zemlju i ono što je između njih za šest vremenskih razdoblja (dana), i nije Nas ophrvao nikakav umor.');
INSERT INTO "bs_darassalam" VALUES(50,39,'50:39','Zato strpljivo podnosi ono što oni govore i veličaj Gospodara svog i zahvaljuj Mu prije sunčeva izlaska i prije zalaska...');
INSERT INTO "bs_darassalam" VALUES(50,40,'50:40','i veličaj Ga noću i poslije obavljanja molitvi.');
INSERT INTO "bs_darassalam" VALUES(50,41,'50:41','I osluškuj! Dan kad će glasnik pozvati iz mjesta koje je blizu...');
INSERT INTO "bs_darassalam" VALUES(50,42,'50:42','Dan kad će oni čuti istinit glas – to će biti Dan oživljenja.');
INSERT INTO "bs_darassalam" VALUES(50,43,'50:43','Mi, zaista, dajemo život i dajemo smrt, i sve će se Nama vratiti!');
INSERT INTO "bs_darassalam" VALUES(50,44,'50:44','A na Dan kad će nad njima zemlja popucati, oni će žurno izaći – bit će to oživljenje, za Nas lahko.');
INSERT INTO "bs_darassalam" VALUES(50,45,'50:45','Mi dobro znamo šta oni govore. Ti ih ne možeš prisiliti, nego podsjeti Kur’anom onog koji se prijetnje Moje boji!');
INSERT INTO "bs_darassalam" VALUES(51,1,'51:1','Tako Mi onih koji pūšū snažno...');
INSERT INTO "bs_darassalam" VALUES(51,2,'51:2','i onih koji teret nose...');
INSERT INTO "bs_darassalam" VALUES(51,3,'51:3','i onih koji plove lahko...');
INSERT INTO "bs_darassalam" VALUES(51,4,'51:4','i onih koji naredbe provode...');
INSERT INTO "bs_darassalam" VALUES(51,5,'51:5','istina je, zaista, ono čime vam se prijeti...');
INSERT INTO "bs_darassalam" VALUES(51,6,'51:6','nagrada i kazna sigurno će biti!');
INSERT INTO "bs_darassalam" VALUES(51,7,'51:7','Tako Mi neba punog zvjezdanih puteva...');
INSERT INTO "bs_darassalam" VALUES(51,8,'51:8','vi govorite nejednako...');
INSERT INTO "bs_darassalam" VALUES(51,9,'51:9','od njega se odvraća onaj za kog se znalo da će se odvratiti.');
INSERT INTO "bs_darassalam" VALUES(51,10,'51:10','Neka prokleti budu lažljivci...');
INSERT INTO "bs_darassalam" VALUES(51,11,'51:11','koji su, utonuli u neznanje, ravnodušni!');
INSERT INTO "bs_darassalam" VALUES(51,12,'51:12','Oni pitaju: “Kad će Dan sudnji?”');
INSERT INTO "bs_darassalam" VALUES(51,13,'51:13','Onog Dana kad se u vatri budu pržili!');
INSERT INTO "bs_darassalam" VALUES(51,14,'51:14','“Iskusite kaznu svoju – to je ono što ste požurivali!”');
INSERT INTO "bs_darassalam" VALUES(51,15,'51:15','Oni koji su se Allaha bojali u džennetskim će baščama, među izvorima, boraviti...');
INSERT INTO "bs_darassalam" VALUES(51,16,'51:16','primat će ono što im Gospodar njihov bude darovao, jer oni su prije toga dobra djela činili...');
INSERT INTO "bs_darassalam" VALUES(51,17,'51:17','noću su malo spavali...');
INSERT INTO "bs_darassalam" VALUES(51,18,'51:18','i u praskozorje oprost od grijeha molili...');
INSERT INTO "bs_darassalam" VALUES(51,19,'51:19','a u imecima njihovim bio je udio i za onog koji prosi i za onog koji ne prosi.');
INSERT INTO "bs_darassalam" VALUES(51,20,'51:20','Na Zemlji su dokazi za one koji čvrsto vjeruju...');
INSERT INTO "bs_darassalam" VALUES(51,21,'51:21','a i u vama samima – zar ne vidite?');
INSERT INTO "bs_darassalam" VALUES(51,22,'51:22','A na nebu je opskrba vaša i ono što vam se obećava.');
INSERT INTO "bs_darassalam" VALUES(51,23,'51:23','I tako Mi Gospodara neba i Zemlje to je istina, kao što je istina da govorite!');
INSERT INTO "bs_darassalam" VALUES(51,24,'51:24','Je li doprla do tebe vijest o uvaženim gostima Ibrahimovim?');
INSERT INTO "bs_darassalam" VALUES(51,25,'51:25','Kad mu oni uđoše i rekoše: “Mir vama!” – i on reče: “Mir vama, ljudi neznani!”');
INSERT INTO "bs_darassalam" VALUES(51,26,'51:26','I on neprimjetno ode ukućanima svojim i donese debelo tele...');
INSERT INTO "bs_darassalam" VALUES(51,27,'51:27','i primače im ga: “Zar nećete jesti?”, upita...');
INSERT INTO "bs_darassalam" VALUES(51,28,'51:28','osjetivši od njih u duši zebnju. “Ne boj se!”, rekoše i obradovaše ga dječakom koji će učen biti.');
INSERT INTO "bs_darassalam" VALUES(51,29,'51:29','I pojavi se žena njegova uzvikujući i po licu se udari od čuda, rekavši: “Zar ja, stara, nerotkinja!?”');
INSERT INTO "bs_darassalam" VALUES(51,30,'51:30','“Tako je odredio Gospodar tvoj”, rekoše oni, “On je mudar i sveznajući.”');
INSERT INTO "bs_darassalam" VALUES(51,31,'51:31','"A što vi hoćete, o izaslanici?", upita Ibrahim.');
INSERT INTO "bs_darassalam" VALUES(51,32,'51:32','"Poslani smo narodu prestupničkom", rekoše,');
INSERT INTO "bs_darassalam" VALUES(51,33,'51:33','"da sručimo na njih grumenje od gline,');
INSERT INTO "bs_darassalam" VALUES(51,34,'51:34','obilježeno u Gospodara tvoga za one koji su u razvratu svaku mjeru prešli."');
INSERT INTO "bs_darassalam" VALUES(51,35,'51:35','Mi iz njega vjernike izvedosmo,');
INSERT INTO "bs_darassalam" VALUES(51,36,'51:36','a u njemu samo jednu kuću muslimansku nađosmo,');
INSERT INTO "bs_darassalam" VALUES(51,37,'51:37','i u njemu, za sve one koji se boje bolne patnje, znak ostavismo.');
INSERT INTO "bs_darassalam" VALUES(51,38,'51:38','I u Musau, također – kada ga s očiglednim dokazom faraonu poslasmo,');
INSERT INTO "bs_darassalam" VALUES(51,39,'51:39','a on, oholeći se, okrenu se nastranu i reče: “Čarobnjak je ili lud.”');
INSERT INTO "bs_darassalam" VALUES(51,40,'51:40','I Mi i njega i vojske njegove dohvatismo, pa ih u more bacismo, jer je bio osudu zaslužio.');
INSERT INTO "bs_darassalam" VALUES(51,41,'51:41','I u Adu – kad na njih vjetar poslasmo u kome nije bilo nikakva dobra;');
INSERT INTO "bs_darassalam" VALUES(51,42,'51:42','pored čega god je prošao, ništa nije poštedio, sve je u gnjilež pretvorio.');
INSERT INTO "bs_darassalam" VALUES(51,43,'51:43','I u Semudu – kad im bi rečeno: “Uživajte još neko vrijeme!”');
INSERT INTO "bs_darassalam" VALUES(51,44,'51:44','Oni se oglušiše o naređenje Gospodara svoga, pa ih uništi strašan glas na oči njihove,');
INSERT INTO "bs_darassalam" VALUES(51,45,'51:45','i ne mogahu se ni dići ni od kazne odbraniti.');
INSERT INTO "bs_darassalam" VALUES(51,46,'51:46','I uništili smo prije narod Nuhov: to, zaista, bijaše narod buntovni.');
INSERT INTO "bs_darassalam" VALUES(51,47,'51:47','Mi smo nebo snagom sazdali, i Mi smo, uistinu, proširitelji');
INSERT INTO "bs_darassalam" VALUES(51,48,'51:48','I Zemlju smo prostrli – tako smo divni Mi Koji smo je prostrli!');
INSERT INTO "bs_darassalam" VALUES(51,49,'51:49','I od svega po par stvaramo da biste se vi prisjećali.');
INSERT INTO "bs_darassalam" VALUES(51,50,'51:50','"Zato požurite Allahu, ja sam vam Njegov upozoritelj jasni!');
INSERT INTO "bs_darassalam" VALUES(51,51,'51:51','I ne pripisujte uz Allaha boga drugoga! Ja sam vam Njegov upozoriteljjasni!"');
INSERT INTO "bs_darassalam" VALUES(51,52,'51:52','I tako je bilo: ni onima prije ovih nije došao nijedan poslanik, a da nisurekli: “Čarobnjak je!” ili “Lud je!”');
INSERT INTO "bs_darassalam" VALUES(51,53,'51:53','Jesu li to oni jedni drugima oporučili? Nisu, nego su oni ljudi koji su u zlu svaku mjeru bili prevršili.');
INSERT INTO "bs_darassalam" VALUES(51,54,'51:54','Pa, od njih ti se okreni, prekoren nećeš biti!');
INSERT INTO "bs_darassalam" VALUES(51,55,'51:55','I opominji! Doista, opomena vjernicima koristi!');
INSERT INTO "bs_darassalam" VALUES(51,56,'51:56','Džine i ljude sam stvorio samo zato da Meni u ibadetu budu.');
INSERT INTO "bs_darassalam" VALUES(51,57,'51:57','Ja ne tražim od njih opskrbu niti želim da me hrane,');
INSERT INTO "bs_darassalam" VALUES(51,58,'51:58','Opskrbitelj je jedino Allah, Snažni i Silni!');
INSERT INTO "bs_darassalam" VALUES(51,59,'51:59','A one koji su zulum činili stići će kazna kao što je stigla i one koji su bilikao oni, i neka Me ne požuruju;');
INSERT INTO "bs_darassalam" VALUES(51,60,'51:60','a, teško nevjernicima na Dan kojim im se prijeti!');
INSERT INTO "bs_darassalam" VALUES(52,1,'52:1','Tako Mi brda Tur,');
INSERT INTO "bs_darassalam" VALUES(52,2,'52:2','i Knjige u retke napisane');
INSERT INTO "bs_darassalam" VALUES(52,3,'52:3','na koži razvijenoj,');
INSERT INTO "bs_darassalam" VALUES(52,4,'52:4','i Bejtu-l-M‘amura');
INSERT INTO "bs_darassalam" VALUES(52,5,'52:5','i svoda uzdignutog');
INSERT INTO "bs_darassalam" VALUES(52,6,'52:6','i vodom mora napunjenog');
INSERT INTO "bs_darassalam" VALUES(52,7,'52:7','kazna Gospodara tvoga sigurno će se dogoditi.');
INSERT INTO "bs_darassalam" VALUES(52,8,'52:8','Niko je neće moći otkloniti');
INSERT INTO "bs_darassalam" VALUES(52,9,'52:9','na Dan kad se nebo silno uzburka,');
INSERT INTO "bs_darassalam" VALUES(52,10,'52:10','a planine s mjesta pomaknu!');
INSERT INTO "bs_darassalam" VALUES(52,11,'52:11','Teško na taj dan onima koji su poricali,');
INSERT INTO "bs_darassalam" VALUES(52,12,'52:12','koji su se u laž upuštali, zabavljajući se!');
INSERT INTO "bs_darassalam" VALUES(52,13,'52:13','Na Dan kada će grubo u vatru džehennemsku biti gurnuti:');
INSERT INTO "bs_darassalam" VALUES(52,14,'52:14','“Ovo je Vatra koju ste poricali');
INSERT INTO "bs_darassalam" VALUES(52,15,'52:15','pa, je li ovo vradžbina ili vi ne vidite?!');
INSERT INTO "bs_darassalam" VALUES(52,16,'52:16','Pržite se u njoj, isto vam je trpjeli ili ne trpjeli – to vam je kazna za onošto ste radili!”');
INSERT INTO "bs_darassalam" VALUES(52,17,'52:17','A bogobojazni će, zbilja, u džennetskim baščama i blagodatima biti,');
INSERT INTO "bs_darassalam" VALUES(52,18,'52:18','i u onom će što im je Gospodar njihov dao uživati – njih će Gospodarnjihov patnje u Ognju sačuvati.');
INSERT INTO "bs_darassalam" VALUES(52,19,'52:19','“Jedite i pijte, i neka vam je prijatno, to je za ono što ste radili.”');
INSERT INTO "bs_darassalam" VALUES(52,20,'52:20','Bit će naslonjeni na divanima poredanim, a vjenčat ćemo ih s hurijamakrupnih očiju.');
INSERT INTO "bs_darassalam" VALUES(52,21,'52:21','Onima koji su vjerovali i za kojima su se djeca njihova u vjerovanju povela priključit ćemo djecu njihovu, a djela njihova nećemo nimalo umanjiti – svaki čovjek je zalog za ono što je uradio.');
INSERT INTO "bs_darassalam" VALUES(52,22,'52:22','Još ćemo ih darovati voćem i mesom kakvo budu željeli,');
INSERT INTO "bs_darassalam" VALUES(52,23,'52:23','jedni drugima će, u njemu, pune čaše dodavati – zbog njih neće biti praznih besjeda i pobuda na grijeh,');
INSERT INTO "bs_darassalam" VALUES(52,24,'52:24','a služit će ih posluga njihova nalik na biser skriveni,');
INSERT INTO "bs_darassalam" VALUES(52,25,'52:25','i primaknut će se jedni drugima i jedni druge pitati:');
INSERT INTO "bs_darassalam" VALUES(52,26,'52:26','“Prije smo među svojim porodicama strahovali,');
INSERT INTO "bs_darassalam" VALUES(52,27,'52:27','pa nam je Allah milost darovao i od patnje u Ognju nas sačuvao.');
INSERT INTO "bs_darassalam" VALUES(52,28,'52:28','Mi smo Ga u dovi molili, On je, doista, Dobročinitelj, Milostivi.”');
INSERT INTO "bs_darassalam" VALUES(52,29,'52:29','Zato ti opominji, jer ti, milošću Gospodara svoga, nisi ni prorok ni lud.');
INSERT INTO "bs_darassalam" VALUES(52,30,'52:30','Zar oni da govore: “Pjesnik je, sačekat ćemo dok potresen nedaćama ne umre.”');
INSERT INTO "bs_darassalam" VALUES(52,31,'52:31','“Pa čekajte”, reci ti, “i ja ću zajedno s vama čekati.”');
INSERT INTO "bs_darassalam" VALUES(52,32,'52:32','Da li im ovo umovi njihovi naređuju?! Ne, nego su oni obijesan narod.');
INSERT INTO "bs_darassalam" VALUES(52,33,'52:33','Zar oni da govore: “Izmišlja ga!” Štaviše, oni ne vjeruju.');
INSERT INTO "bs_darassalam" VALUES(52,34,'52:34','Zato, neka oni sastave govor sličan Kur’anu ako istinu govore.');
INSERT INTO "bs_darassalam" VALUES(52,35,'52:35','Da li su oni bez Stvoritelja stvoreni ili su oni sami sebe stvorili?!');
INSERT INTO "bs_darassalam" VALUES(52,36,'52:36','Da li su oni nebesa i Zemlju stvorili?! Ne, nego oni nisu uvjereni!');
INSERT INTO "bs_darassalam" VALUES(52,37,'52:37','Da li su kod njih riznice Gospodara tvoga ili oni vladaju?!');
INSERT INTO "bs_darassalam" VALUES(52,38,'52:38','Da li oni imaju ljestve, pa na njima prisluškuju?! Neka onaj među njimakoji tvrdi da je nešto čuo donese potvrdu očitu.');
INSERT INTO "bs_darassalam" VALUES(52,39,'52:39','Zar da su za Njega – kćeri, a za vas da su – sinovi?!');
INSERT INTO "bs_darassalam" VALUES(52,40,'52:40','Da li ti tražiš od njih naknadu, pa su nametom opterećeni?');
INSERT INTO "bs_darassalam" VALUES(52,41,'52:41','Ili, da nije u njih znanje o onome što je čulima nedokučivo, pa oniprepisuju?!');
INSERT INTO "bs_darassalam" VALUES(52,42,'52:42','Zar oni spletke žele?! Pa, oni koji ne vjeruju, oni padaju u spletke!');
INSERT INTO "bs_darassalam" VALUES(52,43,'52:43','Zar oni drugog boga osim Allaha da imaju? Uzvišen je Allah od onoga što Mu u obožavanju pridružuju.');
INSERT INTO "bs_darassalam" VALUES(52,44,'52:44','I kad bi vidjeli da komad neba pada, rekli bi: “Oblaci nagomilani.”');
INSERT INTO "bs_darassalam" VALUES(52,45,'52:45','Zato ih pusti dok se ne suoče s Danom u kome će propasti,');
INSERT INTO "bs_darassalam" VALUES(52,46,'52:46','Danom kada im spletke njihove nimalo neće koristiti i kada im niko neće pomoći.');
INSERT INTO "bs_darassalam" VALUES(52,47,'52:47','A za sve koji su zulum činili i druga će kazna prije one biti, ali većina njih ne zna.');
INSERT INTO "bs_darassalam" VALUES(52,48,'52:48','A ti strpljivo čekaj presudu Gospodara svoga, jer ti si pred Našim očima; i veličaj i hvali Gospodara svoga kad ustaješ,');
INSERT INTO "bs_darassalam" VALUES(52,49,'52:49','i noću Ga veličaj i kad se zvijezde gube.');
INSERT INTO "bs_darassalam" VALUES(53,1,'53:1','Tako Mi zvijezde kad zapada');
INSERT INTO "bs_darassalam" VALUES(53,2,'53:2','vaš drug nije s Pravog puta skrenuo i nije zalutao!');
INSERT INTO "bs_darassalam" VALUES(53,3,'53:3','On ne govori po hiru svome.');
INSERT INTO "bs_darassalam" VALUES(53,4,'53:4','To je samo Objava koja mu se obznanjuje.');
INSERT INTO "bs_darassalam" VALUES(53,5,'53:5','Uči ga jedan velikih moći');
INSERT INTO "bs_darassalam" VALUES(53,6,'53:6','snažni, koji se uspravio u liku svome');
INSERT INTO "bs_darassalam" VALUES(53,7,'53:7','na obzorju najvišem');
INSERT INTO "bs_darassalam" VALUES(53,8,'53:8','Zatim se približio, pa nadnio');
INSERT INTO "bs_darassalam" VALUES(53,9,'53:9','blizu koliko dva luka ili bliže');
INSERT INTO "bs_darassalam" VALUES(53,10,'53:10','i objavio robu Njegovu ono što je objavio');
INSERT INTO "bs_darassalam" VALUES(53,11,'53:11','Srce nije poreklo ono što je vidio.');
INSERT INTO "bs_darassalam" VALUES(53,12,'53:12','Pa zašto se prepirete s njim o onom što je vidio?');
INSERT INTO "bs_darassalam" VALUES(53,13,'53:13','On ga je i drugi put vidio.');
INSERT INTO "bs_darassalam" VALUES(53,14,'53:14','Kod Sidretu-l-Muntehaa');
INSERT INTO "bs_darassalam" VALUES(53,15,'53:15','gdje je džennetsko utočište.');
INSERT INTO "bs_darassalam" VALUES(53,16,'53:16','Kad je Sidru prekrilo ono što ju je prekrilo,');
INSERT INTO "bs_darassalam" VALUES(53,17,'53:17','pogled mu nije skrenuo i nije prekoračio.');
INSERT INTO "bs_darassalam" VALUES(53,18,'53:18','Vidio je najveličanstvenije znakove svoga Gospodara.');
INSERT INTO "bs_darassalam" VALUES(53,19,'53:19','Vidjeste li Lata i Uzzaa?!');
INSERT INTO "bs_darassalam" VALUES(53,20,'53:20','I Menata, trećeg, posljednjeg od njih?!');
INSERT INTO "bs_darassalam" VALUES(53,21,'53:21','Zar za vas da su sinovi, a za Njega kćeri?!');
INSERT INTO "bs_darassalam" VALUES(53,22,'53:22','To bi tada bila podjela nepravedna.');
INSERT INTO "bs_darassalam" VALUES(53,23,'53:23','To su samo imena koja ste im vi i preci vaši nadjenuli; Allah o njimanikakav dokaz nije poslao; oni se povode samo za pretpostavkama i onim za čim duše žude, a već im dođe od Gospodara njihova Uputa.');
INSERT INTO "bs_darassalam" VALUES(53,24,'53:24','I zar da čovjek sve što poželi dobije?!');
INSERT INTO "bs_darassalam" VALUES(53,25,'53:25','Pa, Allahu pripada i prvi i drugi svijet.');
INSERT INTO "bs_darassalam" VALUES(53,26,'53:26','A koliko na nebesima ima meleka čije zauzimanje nikome neće biti odkoristi sve dok Allah to ne dozvoli onome kome On hoće i u korist onoga kojim je zadovoljan.');
INSERT INTO "bs_darassalam" VALUES(53,27,'53:27','Oni koji ne vjeruju u ahiret nazivaju meleke imenima ženskim.');
INSERT INTO "bs_darassalam" VALUES(53,28,'53:28','A o tome ništa ne znaju, slijede samo pretpostavke, a pretpostavka Istini baš nimalo ne koristi.');
INSERT INTO "bs_darassalam" VALUES(53,29,'53:29','Pa, ti se okani onoga ko se od Opomene Naše okrenuo i koji samo život dunjalučki želi.');
INSERT INTO "bs_darassalam" VALUES(53,30,'53:30','To je vrhunac njihova znanja – Gospodar tvoj najbolje zna one koji suskrenuli s Njegova puta i On najbolje zna one koji su na Pravom putu.');
INSERT INTO "bs_darassalam" VALUES(53,31,'53:31','Allahovo je sve što je na nebesima i što je na Zemlji, da bi, prema onome kako su radili, kaznio one koji rade zlo, a najljepšom nagradom nagradio one koji čine dobro,');
INSERT INTO "bs_darassalam" VALUES(53,32,'53:32','one koji se klone velikih grijeha i razvrata, a one bezazlene On će oprostiti, jer Gospodar tvoj, zaista, neizmjerno prašta, On najbolje zna sve o vama, otkad vas je stvorio od zemlje i od kad ste bili zameci u utrobama majki vaših; zato se ne hvališite – On najbolje zna svakog onoga koji se grijeha kloni.');
INSERT INTO "bs_darassalam" VALUES(53,33,'53:33','Šta misliš o onome ko se okrenuo');
INSERT INTO "bs_darassalam" VALUES(53,34,'53:34','i malo udijelio, a onda posve prestao udjeljivati?');
INSERT INTO "bs_darassalam" VALUES(53,35,'53:35','Zar on ima znanje o onome što je čulima nedokučivo, pa vidi?!');
INSERT INTO "bs_darassalam" VALUES(53,36,'53:36','Zar on nije obaviješten o onom što se nalazi u listovima Musaovim');
INSERT INTO "bs_darassalam" VALUES(53,37,'53:37','i Ibrahimovim – koji je obaveze potpuno ispunjavao');
INSERT INTO "bs_darassalam" VALUES(53,38,'53:38','da ni jedan griješnik tuđe grijehe neće nositi,');
INSERT INTO "bs_darassalam" VALUES(53,39,'53:39','i da je čovjekovo samo ono što sam uradi,');
INSERT INTO "bs_darassalam" VALUES(53,40,'53:40','i da će trud njegov sigurno primijećen biti,');
INSERT INTO "bs_darassalam" VALUES(53,41,'53:41','i da će prema njemu u potpunosti nagrađen ili kažnjen biti.');
INSERT INTO "bs_darassalam" VALUES(53,42,'53:42','I da će se Gospodaru tvome ponovno vratiti,');
INSERT INTO "bs_darassalam" VALUES(53,43,'53:43','i da On na smijeh i na plač navodi,');
INSERT INTO "bs_darassalam" VALUES(53,44,'53:44','i da On usmrćuje i oživljava,');
INSERT INTO "bs_darassalam" VALUES(53,45,'53:45','i da On par, muško i žensko, stvara');
INSERT INTO "bs_darassalam" VALUES(53,46,'53:46','od kapi sjemena kad se izbaci,');
INSERT INTO "bs_darassalam" VALUES(53,47,'53:47','i da će ih On ponovno oživiti,');
INSERT INTO "bs_darassalam" VALUES(53,48,'53:48','i da On daje bogatstvo i moć da stječu,');
INSERT INTO "bs_darassalam" VALUES(53,49,'53:49','i da je On Sirijusa Gospodar,');
INSERT INTO "bs_darassalam" VALUES(53,50,'53:50','i da je On drevni narod Ad uništio,');
INSERT INTO "bs_darassalam" VALUES(53,51,'53:51','i Semud, i da nikog nije poštedio,');
INSERT INTO "bs_darassalam" VALUES(53,52,'53:52','I uništili smo prije narod Nuhov: to, zaista, bijaše narod buntovni.');
INSERT INTO "bs_darassalam" VALUES(53,53,'53:53','i prevrnuta naselja On je na zemlju sručio,');
INSERT INTO "bs_darassalam" VALUES(53,54,'53:54','i snašlo ih je ono što ih je snašlo.');
INSERT INTO "bs_darassalam" VALUES(53,55,'53:55','Pa, u koju blagodat Gospodara svoga ti još sumnjaš?');
INSERT INTO "bs_darassalam" VALUES(53,56,'53:56','Ovo je upozoritelj iz reda prijašnjih upozoritelja');
INSERT INTO "bs_darassalam" VALUES(53,57,'53:57','Bliski čas se približava,');
INSERT INTO "bs_darassalam" VALUES(53,58,'53:58','osim Allaha niko ga ne može otkriti, niti otkloniti!');
INSERT INTO "bs_darassalam" VALUES(53,59,'53:59','Pa zar se ovom govoru iščuđavate,');
INSERT INTO "bs_darassalam" VALUES(53,60,'53:60','i smijete se, a ne plačete,');
INSERT INTO "bs_darassalam" VALUES(53,61,'53:61','gordo dignutih glava?!');
INSERT INTO "bs_darassalam" VALUES(53,62,'53:62','Zato, na sedždu Allahu padajte i Njemu ibadet činite.');
INSERT INTO "bs_darassalam" VALUES(54,1,'54:1','Bliži se Čas i Mjesec se raspolutio!');
INSERT INTO "bs_darassalam" VALUES(54,2,'54:2','A oni, uvijek kada vide znak, okreću se i govore: “Magija prolazna!”');
INSERT INTO "bs_darassalam" VALUES(54,3,'54:3','Oni ne vjeruju i povode se za hirovima svojim, a sve će kod svoga ciljaskončati');
INSERT INTO "bs_darassalam" VALUES(54,4,'54:4','I došle su im vijesti u kojima je ono što treba da ih odvrati');
INSERT INTO "bs_darassalam" VALUES(54,5,'54:5','– mudrost savršena – pa kako upozoritelji takvima mogu koristiti?!');
INSERT INTO "bs_darassalam" VALUES(54,6,'54:6','Zato se okreni od njih! Na Dan kad ih glasnik pozove na nešto užasno,');
INSERT INTO "bs_darassalam" VALUES(54,7,'54:7','oni će oborenih pogleda iz grobova izlaziti, kao skakavci rasuti,');
INSERT INTO "bs_darassalam" VALUES(54,8,'54:8','žureći ka glasniku. A nevjernici će govoriti: “Ovo je težak dan.”');
INSERT INTO "bs_darassalam" VALUES(54,9,'54:9','Poricao je Nuhov narod prije njih, pa su roba Našeg u laž utjerivaligovoreći: “Luđak!” – i Nuh je odbijen bio.');
INSERT INTO "bs_darassalam" VALUES(54,10,'54:10','I on je Gospodara svoga zamolio: “Ja sam pobijeđen, pa Ti pomozi!”');
INSERT INTO "bs_darassalam" VALUES(54,11,'54:11','I Mi smo kapije nebeske pootvarali vodi koja je neprestano lila,');
INSERT INTO "bs_darassalam" VALUES(54,12,'54:12','i učinili da iz zemlje izvori provriju, i vode su se sastale po onome što jeveć bilo određeno,');
INSERT INTO "bs_darassalam" VALUES(54,13,'54:13','a njega smo nosili na lađi od dasaka i klinova sagrađenoj,');
INSERT INTO "bs_darassalam" VALUES(54,14,'54:14','koja je plovila pred Našim očima, a nagrada je to bila za onoga koji jeporican bio.');
INSERT INTO "bs_darassalam" VALUES(54,15,'54:15','I Mi to ostavismo kao znak – pa ima li ikoga ko bi se prisjetio?');
INSERT INTO "bs_darassalam" VALUES(54,16,'54:16','“Kakva je bila kazna Moja i upozorenje Moje?!”');
INSERT INTO "bs_darassalam" VALUES(54,17,'54:17','A Mi smo Kur’an lahkim za opomenu učinili, pa ima li ikoga ko bi seprisjetio?');
INSERT INTO "bs_darassalam" VALUES(54,18,'54:18','Poricao je Ad, pa kakva je bila kazna Moja i upozorenje Moje?!');
INSERT INTO "bs_darassalam" VALUES(54,19,'54:19','Jednog kobnog dana poslali smo na njih leden vjetar koji je neprestanopuhao');
INSERT INTO "bs_darassalam" VALUES(54,20,'54:20','i ljude dizao kao da su palmina stabla iščupana,');
INSERT INTO "bs_darassalam" VALUES(54,21,'54:21','Kakva je bila kazna Moja i upozorenje Moje?!');
INSERT INTO "bs_darassalam" VALUES(54,22,'54:22','A Mi smo Kur’an olakšali kao opomenu, pa ima li koga ko bi se prisjetio?');
INSERT INTO "bs_darassalam" VALUES(54,23,'54:23','I Semud je upozoritelje poricao.');
INSERT INTO "bs_darassalam" VALUES(54,24,'54:24','"Zar da slijedimo jednog od nas!" - govorili su. "Tada bismo, uistinu, bili u zabludi i bili bismo ludi.');
INSERT INTO "bs_darassalam" VALUES(54,25,'54:25','Zar baš njemu, između nas, da bude poslana Objava?! Ne, on je lažljivac oholi!”');
INSERT INTO "bs_darassalam" VALUES(54,26,'54:26','“Saznat će oni sutra ko je lažljivac oholi!');
INSERT INTO "bs_darassalam" VALUES(54,27,'54:27','Mi ćemo poslati kamilu da bismo ih iskušali, pa pričekaj ih i budi strpljiv.');
INSERT INTO "bs_darassalam" VALUES(54,28,'54:28','I upozori ih da će se voda između njih i nje dijeliti, svakom obroku pristupit će onaj čiji je red!”');
INSERT INTO "bs_darassalam" VALUES(54,29,'54:29','Ali oni pozvaše jednog od svojih, pa se on spremi i prekla je.');
INSERT INTO "bs_darassalam" VALUES(54,30,'54:30','i kakve su bile kazna Moja i opomene Moje');
INSERT INTO "bs_darassalam" VALUES(54,31,'54:31','Mi poslasmo na njih jedan jedini krik i oni postadoše poput zdrobljenogsuhog lišća.');
INSERT INTO "bs_darassalam" VALUES(54,32,'54:32','A Mi smo Kur’an olakšali za opomenu, pa ima li ikoga ko bi se prisjetio?');
INSERT INTO "bs_darassalam" VALUES(54,33,'54:33','Narod Lutov poricao je upozoritelje i upozorenja.');
INSERT INTO "bs_darassalam" VALUES(54,34,'54:34','Na njih vjetar, pun pijeska, poslasmo, samo ne na Lutovu porodicu, nju u svitanje spasismo');
INSERT INTO "bs_darassalam" VALUES(54,35,'54:35','iz milosti Naše. Eto, tako Mi nagrađujemo one koji zahvaljuju.');
INSERT INTO "bs_darassalam" VALUES(54,36,'54:36','A on im je bio prijetio silom Našom, ali su oni u upozoritelje i upozorenja sumnjali.');
INSERT INTO "bs_darassalam" VALUES(54,37,'54:37','Oni su od njega goste njegove tražili, pa smo ih Mi oslijepili: “Iskusitekaznu Moju i upozorenja Moja!”');
INSERT INTO "bs_darassalam" VALUES(54,38,'54:38','A rano izjutra stiže ih kazna neprekidna.');
INSERT INTO "bs_darassalam" VALUES(54,39,'54:39','"Iskusite kaznu Moju i upozorenja Moja!"');
INSERT INTO "bs_darassalam" VALUES(54,40,'54:40','A Mi smo Kur’an olakšali za opomenu, pa ima li ikoga ko bi se prisjetio?!');
INSERT INTO "bs_darassalam" VALUES(54,41,'54:41','I faraonovoj porodici upozoritelji i upozorenja dolaziše,');
INSERT INTO "bs_darassalam" VALUES(54,42,'54:42','ali oni porekoše sve znakove Naše, pa ih Mi kaznismo onako kako kažnjava Silni i Apsolutno Moćni.');
INSERT INTO "bs_darassalam" VALUES(54,43,'54:43','Da li su nevjernici vaši bolji od njih ili vi u knjigama nebeskim imatekakvu garanciju?!');
INSERT INTO "bs_darassalam" VALUES(54,44,'54:44','Zar ovi da govore: “Mi smo skup nepobjedivi!”');
INSERT INTO "bs_darassalam" VALUES(54,45,'54:45','Skup će, sigurno, poražen biti, a oni će se u bijeg dati!');
INSERT INTO "bs_darassalam" VALUES(54,46,'54:46','Međutim, Čas oživljenja im je rok, a Čas oživljenja je užasniji i gorči.');
INSERT INTO "bs_darassalam" VALUES(54,47,'54:47','Prestupnici, doista, u zabludi i patnji bit će');
INSERT INTO "bs_darassalam" VALUES(54,48,'54:48','na Dan kada budu u Vatru odvučeni, s licima dolje okrenutim: “Iskusitedodir Ognja!”');
INSERT INTO "bs_darassalam" VALUES(54,49,'54:49','Mi, doista, sve s određenjem stvaramo');
INSERT INTO "bs_darassalam" VALUES(54,50,'54:50','i naređenje Naše samo je jedna riječ – sve bude u tren oka.');
INSERT INTO "bs_darassalam" VALUES(54,51,'54:51','A Mi smo već vama slične uništili, pa ima li ikoga ko bi se prisjetio?!');
INSERT INTO "bs_darassalam" VALUES(54,52,'54:52','I sve što su uradili u listovima je,');
INSERT INTO "bs_darassalam" VALUES(54,53,'54:53','i sve, i malo i veliko, u retke je stavljeno.');
INSERT INTO "bs_darassalam" VALUES(54,54,'54:54','Oni koji su se Allaha bojali bit će u džennetskim baščama i pored rijeka,');
INSERT INTO "bs_darassalam" VALUES(54,55,'54:55','na mjestu Istine, kod Vladara Apsolutno Moćnoga.');
INSERT INTO "bs_darassalam" VALUES(55,1,'55:1','Svemilosni');
INSERT INTO "bs_darassalam" VALUES(55,2,'55:2','poučava Kur’anu,');
INSERT INTO "bs_darassalam" VALUES(55,3,'55:3','stvara čovjeka,');
INSERT INTO "bs_darassalam" VALUES(55,4,'55:4','uči ga izgovoru');
INSERT INTO "bs_darassalam" VALUES(55,5,'55:5','Sunce i Mjesec po tačno određenom proračunu plove,');
INSERT INTO "bs_darassalam" VALUES(55,6,'55:6','i trava i drveće sedždu čine');
INSERT INTO "bs_darassalam" VALUES(55,7,'55:7','a nebo je digao. I postavio je Mjerilo.');
INSERT INTO "bs_darassalam" VALUES(55,8,'55:8','da ne prelazite granice pravde,');
INSERT INTO "bs_darassalam" VALUES(55,9,'55:9','i pravo mjerite i na tereziji ne zakidajte!');
INSERT INTO "bs_darassalam" VALUES(55,10,'55:10','A Zemlju je za stvorenja razastro,');
INSERT INTO "bs_darassalam" VALUES(55,11,'55:11','na njoj ima voća i palmi s plodom u čaškama,');
INSERT INTO "bs_darassalam" VALUES(55,12,'55:12','i žita sa lišćem i miomirisna cvijeća –');
INSERT INTO "bs_darassalam" VALUES(55,13,'55:13','pa, koju blagodat Gospodara svoga poričete?!');
INSERT INTO "bs_darassalam" VALUES(55,14,'55:14','On je čovjeka od sasušene ilovače, kao što je grnčarija, stvorio,');
INSERT INTO "bs_darassalam" VALUES(55,15,'55:15','a džine od plamena vatre –');
INSERT INTO "bs_darassalam" VALUES(55,16,'55:16','pa, koju blagodat Gospodara svoga poričete?!');
INSERT INTO "bs_darassalam" VALUES(55,17,'55:17','Gospodar dva istoka i dva zapada –');
INSERT INTO "bs_darassalam" VALUES(55,18,'55:18','pa, koju blagodat Gospodara svoga poričete?!');
INSERT INTO "bs_darassalam" VALUES(55,19,'55:19','Pustio je dva mora da se susreću,');
INSERT INTO "bs_darassalam" VALUES(55,20,'55:20','između njih je pregrada i ona se ne miješaju –');
INSERT INTO "bs_darassalam" VALUES(55,21,'55:21','pa, koju blagodat Gospodara svoga poričete?!');
INSERT INTO "bs_darassalam" VALUES(55,22,'55:22','Iz njih se vadi biser i merdžan –');
INSERT INTO "bs_darassalam" VALUES(55,23,'55:23','pa, koju blagodat Gospodara svoga poričete?!');
INSERT INTO "bs_darassalam" VALUES(55,24,'55:24','Njegove su i lađe koje se kao brda visoko po moru uzdižu –');
INSERT INTO "bs_darassalam" VALUES(55,25,'55:25','pa, koju blagodat Gospodara svoga poričete?!');
INSERT INTO "bs_darassalam" VALUES(55,26,'55:26','Sve što je na njoj – Zemlji prolazno je,');
INSERT INTO "bs_darassalam" VALUES(55,27,'55:27','a ostaje Lice tvoga Gospodara, Veličanstveno i Plemenito');
INSERT INTO "bs_darassalam" VALUES(55,28,'55:28','pa, koju blagodat Gospodara svoga poričete?!');
INSERT INTO "bs_darassalam" VALUES(55,29,'55:29','Njemu se mole oni koji su na nebesima i na Zemlji; svakog časa On sezanima nečim');
INSERT INTO "bs_darassalam" VALUES(55,30,'55:30','pa, koju blagodat Gospodara svoga poričete?!');
INSERT INTO "bs_darassalam" VALUES(55,31,'55:31','Pozabavit ćemo se vama, o ljudi i džini!');
INSERT INTO "bs_darassalam" VALUES(55,32,'55:32','pa, koju blagodat Gospodara svoga poričete?!');
INSERT INTO "bs_darassalam" VALUES(55,33,'55:33','O družine džina i ljudi, ako možete kroz slojeve nebesa i Zemlje prodrijeti, prodrite! Moći ćete prodrijeti jedino uz veliku moć');
INSERT INTO "bs_darassalam" VALUES(55,34,'55:34','pa, koju blagodat Gospodara svoga poričete?!');
INSERT INTO "bs_darassalam" VALUES(55,35,'55:35','Na vas će se ognjeni plamen i rastopljen mjed prolivati, i vi se nećete moći odbraniti,');
INSERT INTO "bs_darassalam" VALUES(55,36,'55:36','pa, koju blagodat Gospodara svoga poričete?!');
INSERT INTO "bs_darassalam" VALUES(55,37,'55:37','A kad se nebo razdvoji i postane ružičaste boje kao rastopljeno ulje,');
INSERT INTO "bs_darassalam" VALUES(55,38,'55:38','pa, koju blagodat Gospodara svoga poričete?!');
INSERT INTO "bs_darassalam" VALUES(55,39,'55:39','Taj dan ljudi i džini za grijehe svoje neće biti pitani,');
INSERT INTO "bs_darassalam" VALUES(55,40,'55:40','pa, koju blagodat Gospodara svoga poričete?!');
INSERT INTO "bs_darassalam" VALUES(55,41,'55:41','A prestupnici će se po biljezima svojim poznati, pa će za kose i za noge ščepani biti,');
INSERT INTO "bs_darassalam" VALUES(55,42,'55:42','pa, koju blagodat Gospodara svoga poričete?!');
INSERT INTO "bs_darassalam" VALUES(55,43,'55:43','Evo, ovo je Džehennem, koji su prestupnici poricali!');
INSERT INTO "bs_darassalam" VALUES(55,44,'55:44','Između vatre i uzavrele vode oni će kružiti,');
INSERT INTO "bs_darassalam" VALUES(55,45,'55:45','pa, koju blagodat Gospodara svoga poričete?!');
INSERT INTO "bs_darassalam" VALUES(55,46,'55:46','A onome ko se bude bojao stajanja pred svojim Gospodarom pripast će dva perivoja,');
INSERT INTO "bs_darassalam" VALUES(55,47,'55:47','pa, koju blagodat Gospodara svoga poričete?!');
INSERT INTO "bs_darassalam" VALUES(55,48,'55:48','puna stabala granatih,');
INSERT INTO "bs_darassalam" VALUES(55,49,'55:49','pa, koju blagodat Gospodara svoga poričete?!');
INSERT INTO "bs_darassalam" VALUES(55,50,'55:50','u kojima su dva izvora koja teku –');
INSERT INTO "bs_darassalam" VALUES(55,51,'55:51','pa, koju blagodat Gospodara svoga poričete?!');
INSERT INTO "bs_darassalam" VALUES(55,52,'55:52','u njima će od svakog voća po dvije vrste biti,');
INSERT INTO "bs_darassalam" VALUES(55,53,'55:53','pa, koju blagodat Gospodara svoga poričete?!');
INSERT INTO "bs_darassalam" VALUES(55,54,'55:54','Naslonjeni bit će na postelje čije će postave od kadife biti, a plodovi u oba perivoja nadohvat ruke će stajati,');
INSERT INTO "bs_darassalam" VALUES(55,55,'55:55','pa, koju blagodat Gospodara svoga poričete?!');
INSERT INTO "bs_darassalam" VALUES(55,56,'55:56','U njima će biti one koje preda se gledaju, one koje, prije njih, ni čovjek ni džin nije dodirnuo,');
INSERT INTO "bs_darassalam" VALUES(55,57,'55:57','pa, koju blagodat Gospodara svoga poričete?!');
INSERT INTO "bs_darassalam" VALUES(55,58,'55:58','kao da su one dragulj i merdžan,');
INSERT INTO "bs_darassalam" VALUES(55,59,'55:59','pa, koju blagodat Gospodara svoga poričete?!');
INSERT INTO "bs_darassalam" VALUES(55,60,'55:60','Zar za dobro učinjeno djelo nagrada može biti nešto drugo do dobro?!');
INSERT INTO "bs_darassalam" VALUES(55,61,'55:61','pa, koju blagodat Gospodara svoga poričete?!');
INSERT INTO "bs_darassalam" VALUES(55,62,'55:62','Osim ta dva, biće još dva neznatnija perivoja –');
INSERT INTO "bs_darassalam" VALUES(55,63,'55:63','pa, koju blagodat Gospodara svoga poričete?!');
INSERT INTO "bs_darassalam" VALUES(55,64,'55:64','tamnozelena,');
INSERT INTO "bs_darassalam" VALUES(55,65,'55:65','pa, koju blagodat Gospodara svoga poričete?!');
INSERT INTO "bs_darassalam" VALUES(55,66,'55:66','U njima su dva izvora iz kojih voda kulja;');
INSERT INTO "bs_darassalam" VALUES(55,67,'55:67','pa, koju blagodat Gospodara svoga poričete?!');
INSERT INTO "bs_darassalam" VALUES(55,68,'55:68','U njima će biti voća i palmi i šipaka,');
INSERT INTO "bs_darassalam" VALUES(55,69,'55:69','pa, koju blagodat Gospodara svoga poričete?!');
INSERT INTO "bs_darassalam" VALUES(55,70,'55:70','U njima će biti ljepotica naravi divnih,');
INSERT INTO "bs_darassalam" VALUES(55,71,'55:71','pa, koju blagodat Gospodara svoga poričete?!');
INSERT INTO "bs_darassalam" VALUES(55,72,'55:72','Hurija u šatorima skrivenih,');
INSERT INTO "bs_darassalam" VALUES(55,73,'55:73','pa, koju blagodat Gospodara svoga poričete?!');
INSERT INTO "bs_darassalam" VALUES(55,74,'55:74','Koje prije njih ni čovjek ni džin nije dodirnuo,');
INSERT INTO "bs_darassalam" VALUES(55,75,'55:75','pa, koju blagodat Gospodara svoga poričete?!');
INSERT INTO "bs_darassalam" VALUES(55,76,'55:76','Oni će biti naslonjeni, na prekrivačima zelenim i ćilimima čudesnoukrašenim i lijepim,');
INSERT INTO "bs_darassalam" VALUES(55,77,'55:77','pa, koju blagodat Gospodara svoga poričete?!');
INSERT INTO "bs_darassalam" VALUES(55,78,'55:78','Neka je uzvišeno ime Gospodara tvoga, Veličanstvenog i Plemenitog!');
INSERT INTO "bs_darassalam" VALUES(56,1,'56:1','Kada se Neizbježni događaj zbije,');
INSERT INTO "bs_darassalam" VALUES(56,2,'56:2','događanje njegovo niko neće poricati -');
INSERT INTO "bs_darassalam" VALUES(56,3,'56:3','neke će poniziti, a neke uzvisiti');
INSERT INTO "bs_darassalam" VALUES(56,4,'56:4','Kad se Zemlja jako potrese');
INSERT INTO "bs_darassalam" VALUES(56,5,'56:5','i brda se u komadiće zdrobe,');
INSERT INTO "bs_darassalam" VALUES(56,6,'56:6','i postanu prašina razasuta,');
INSERT INTO "bs_darassalam" VALUES(56,7,'56:7','vas će tri vrste biti:');
INSERT INTO "bs_darassalam" VALUES(56,8,'56:8','oni zdesna – šta će biti s onim zdesna?!');
INSERT INTO "bs_darassalam" VALUES(56,9,'56:9','i oni slijeva – šta će biti s onim slijeva?!');
INSERT INTO "bs_darassalam" VALUES(56,10,'56:10','i oni koji prednjače – oni će prednjačiti! -');
INSERT INTO "bs_darassalam" VALUES(56,11,'56:11','To su oni bliski,');
INSERT INTO "bs_darassalam" VALUES(56,12,'56:12','u džennetskim baščama naslada bit će.');
INSERT INTO "bs_darassalam" VALUES(56,13,'56:13','Bit će ih mnogo iz prvih');
INSERT INTO "bs_darassalam" VALUES(56,14,'56:14','a malo iz zadnjih');
INSERT INTO "bs_darassalam" VALUES(56,15,'56:15','na divanima izvezenim,');
INSERT INTO "bs_darassalam" VALUES(56,16,'56:16','jedni prema drugima na njima će naslonjeni biti;');
INSERT INTO "bs_darassalam" VALUES(56,17,'56:17','služit će ih vječno mladi dječaci,');
INSERT INTO "bs_darassalam" VALUES(56,18,'56:18','sa čašama i ibricima, i peharom punim pića iz izvora tekućeg');
INSERT INTO "bs_darassalam" VALUES(56,19,'56:19','od koga ih glava neće boljeti i zbog koga neće pamet izgubiti,');
INSERT INTO "bs_darassalam" VALUES(56,20,'56:20','i voćem koje će sami birati,');
INSERT INTO "bs_darassalam" VALUES(56,21,'56:21','i mesom ptičijim kakvo budu željeli.');
INSERT INTO "bs_darassalam" VALUES(56,22,'56:22','U njima će biti i hurije očiju krupnih,');
INSERT INTO "bs_darassalam" VALUES(56,23,'56:23','slične biseru u školjkama skrivenom,');
INSERT INTO "bs_darassalam" VALUES(56,24,'56:24','kao nagrada za ono što su činili.');
INSERT INTO "bs_darassalam" VALUES(56,25,'56:25','U njima neće slušati prazne besjede, ni govor griješni,');
INSERT INTO "bs_darassalam" VALUES(56,26,'56:26','nego samo riječi: “Selam, selam!”');
INSERT INTO "bs_darassalam" VALUES(56,27,'56:27','A oni s desne strane – šta će biti s onim s desne strane?!');
INSERT INTO "bs_darassalam" VALUES(56,28,'56:28','Bit će među lotosovim drvećem bez bodlji,');
INSERT INTO "bs_darassalam" VALUES(56,29,'56:29','i među bananama plodovima nanizanim');
INSERT INTO "bs_darassalam" VALUES(56,30,'56:30','i u hladovini prostranoj,');
INSERT INTO "bs_darassalam" VALUES(56,31,'56:31','pored vode tekuće');
INSERT INTO "bs_darassalam" VALUES(56,32,'56:32','i usred voća svakovrsnog');
INSERT INTO "bs_darassalam" VALUES(56,33,'56:33','koje će uvijek imati i koje neće zabranjeno biti');
INSERT INTO "bs_darassalam" VALUES(56,34,'56:34','i na posteljama uzdignutim.');
INSERT INTO "bs_darassalam" VALUES(56,35,'56:35','Stvaranje njihovo, zaista, ćemo obnoviti');
INSERT INTO "bs_darassalam" VALUES(56,36,'56:36','i djevicama ih učiniti,');
INSERT INTO "bs_darassalam" VALUES(56,37,'56:37','te onim koje se umiljavaju muževima svojim i godina istih');
INSERT INTO "bs_darassalam" VALUES(56,38,'56:38','za one s desne strane.');
INSERT INTO "bs_darassalam" VALUES(56,39,'56:39','Bit će ih mnogo iz prvih');
INSERT INTO "bs_darassalam" VALUES(56,40,'56:40','a mnogo iz kasnijih.');
INSERT INTO "bs_darassalam" VALUES(56,41,'56:41','A oni s lijeve strane – šta je s onima s lijeve strane?!');
INSERT INTO "bs_darassalam" VALUES(56,42,'56:42','Oni će biti u vrelom dašku vatre i vodi ključaloj,');
INSERT INTO "bs_darassalam" VALUES(56,43,'56:43','i u sjeni dima mrkog,');
INSERT INTO "bs_darassalam" VALUES(56,44,'56:44','u kojoj neće biti svježine ni bilo kakve ugodnosti.');
INSERT INTO "bs_darassalam" VALUES(56,45,'56:45','Oni su prije toga raskošnim životom živjeli');
INSERT INTO "bs_darassalam" VALUES(56,46,'56:46','i uporno teške grijehe činili');
INSERT INTO "bs_darassalam" VALUES(56,47,'56:47','i govorili: “Zar kad umremo i samo prašina i kosti budemo – zar ćemo,zbilja, biti oživljeni;');
INSERT INTO "bs_darassalam" VALUES(56,48,'56:48','zar i drevni naši preci?!”');
INSERT INTO "bs_darassalam" VALUES(56,49,'56:49','Reci: “I drevni i kasniji,');
INSERT INTO "bs_darassalam" VALUES(56,50,'56:50','u određeno vrijeme, i u Dan određeni bit će sakupljeni,');
INSERT INTO "bs_darassalam" VALUES(56,51,'56:51','i tada ćete vi, o zabludjeli, koji poričete oživljavanje,');
INSERT INTO "bs_darassalam" VALUES(56,52,'56:52','sigurno, s drveta zekkum jesti,');
INSERT INTO "bs_darassalam" VALUES(56,53,'56:53','i njime ćete trbuhe puniti,');
INSERT INTO "bs_darassalam" VALUES(56,54,'56:54','pa zatim na to vodu ključalu piti,');
INSERT INTO "bs_darassalam" VALUES(56,55,'56:55','poput kamila koje ne mogu žeđ ugasiti');
INSERT INTO "bs_darassalam" VALUES(56,56,'56:56','to će im na Dan polaganja računa ugošćenje biti!');
INSERT INTO "bs_darassalam" VALUES(56,57,'56:57','Mi smo vas stvorili – pa zašto ne povjerujete?');
INSERT INTO "bs_darassalam" VALUES(56,58,'56:58','Šta mislite: da li sjemenu koje ubacujete');
INSERT INTO "bs_darassalam" VALUES(56,59,'56:59','vi ili Mi oblik dajemo?');
INSERT INTO "bs_darassalam" VALUES(56,60,'56:60','Mi određujemo kad će ko od vas umrijeti i niko Nas ne može spriječiti');
INSERT INTO "bs_darassalam" VALUES(56,61,'56:61','da likove vaše izmijenimo i da vas iznova u likovima koje vi ne poznajete stvorimo.');
INSERT INTO "bs_darassalam" VALUES(56,62,'56:62','Poznato vam je kako ste prvi put stvoreni, pa zašto se ne urazumite?');
INSERT INTO "bs_darassalam" VALUES(56,63,'56:63','Šta mislite: šta biva s onim što posijete?');
INSERT INTO "bs_darassalam" VALUES(56,64,'56:64','Da li mu vi dajete snagu da niče ili to Mi činimo?');
INSERT INTO "bs_darassalam" VALUES(56,65,'56:65','Ako hoćemo, možemo ga u suho rastinje pretvoriti, pa biste se snebivali:');
INSERT INTO "bs_darassalam" VALUES(56,66,'56:66','“Mi smo, doista, oštećeni,');
INSERT INTO "bs_darassalam" VALUES(56,67,'56:67','čak smo svega lišeni!”');
INSERT INTO "bs_darassalam" VALUES(56,68,'56:68','Šta mislite: vodu koju pijete –');
INSERT INTO "bs_darassalam" VALUES(56,69,'56:69','da li je vi ili Mi iz oblaka spuštamo?');
INSERT INTO "bs_darassalam" VALUES(56,70,'56:70','Ako želimo, možemo je slanom učiniti – pa zašto niste zahvalni?');
INSERT INTO "bs_darassalam" VALUES(56,71,'56:71','Šta mislite: vatru koju palite –');
INSERT INTO "bs_darassalam" VALUES(56,72,'56:72','da li drvo za nju Mi stvaramo ili vi?');
INSERT INTO "bs_darassalam" VALUES(56,73,'56:73','Mi činimo da ona podsjeća i da bude korisna putnicima kada konače;');
INSERT INTO "bs_darassalam" VALUES(56,74,'56:74','zato slavi i veličaj ime Gospodara svoga Veličanstvenog!');
INSERT INTO "bs_darassalam" VALUES(56,75,'56:75','I kunem se položajima zvijezda');
INSERT INTO "bs_darassalam" VALUES(56,76,'56:76','a to je, da znate, zakletva velika!');
INSERT INTO "bs_darassalam" VALUES(56,77,'56:77','On je, zaista, Kur’an plemeniti');
INSERT INTO "bs_darassalam" VALUES(56,78,'56:78','u Knjizi brižljivo čuvanoj.');
INSERT INTO "bs_darassalam" VALUES(56,79,'56:79','Dodirnuti ga smiju samo oni koji su čisti!');
INSERT INTO "bs_darassalam" VALUES(56,80,'56:80','On je objava od Gospodara svjetova,');
INSERT INTO "bs_darassalam" VALUES(56,81,'56:81','pa kako ovaj govor omalovažavate,');
INSERT INTO "bs_darassalam" VALUES(56,82,'56:82','i umjesto zahvalnosti što vam je opskrba darovana, vi u njega ne vjerujete?');
INSERT INTO "bs_darassalam" VALUES(56,83,'56:83','i kad vi budete tada gledali –');
INSERT INTO "bs_darassalam" VALUES(56,84,'56:84','i kad vi budete tada gledali –');
INSERT INTO "bs_darassalam" VALUES(56,85,'56:85','a Mi smo mu bliži od vas, ali vi ne vidite –');
INSERT INTO "bs_darassalam" VALUES(56,86,'56:86','zašto je onda, ako račun nećete polagati,');
INSERT INTO "bs_darassalam" VALUES(56,87,'56:87','ne povratite, ako istinu govorite?');
INSERT INTO "bs_darassalam" VALUES(56,88,'56:88','I ako bude jedan od onih koji su Allahu bliski –');
INSERT INTO "bs_darassalam" VALUES(56,89,'56:89','udobnost i opskrba lijepa i džennetske blagodati pripast će njemu!');
INSERT INTO "bs_darassalam" VALUES(56,90,'56:90','A ako bude jedan od onih koji su s desne strane,');
INSERT INTO "bs_darassalam" VALUES(56,91,'56:91','pa, selam tebi od onih s desne strane!');
INSERT INTO "bs_darassalam" VALUES(56,92,'56:92','A ako bude jedan od onih koji su poricali i u zabludi ostali,');
INSERT INTO "bs_darassalam" VALUES(56,93,'56:93','pa, ključalom vodom bit će ugošćen');
INSERT INTO "bs_darassalam" VALUES(56,94,'56:94','i u Ognju pržen.');
INSERT INTO "bs_darassalam" VALUES(56,95,'56:95','Sama je istina, zbilja, sve ovo!');
INSERT INTO "bs_darassalam" VALUES(56,96,'56:96','Pa, slavi i veličaj ime Gospodara svoga Veličanstvenog!');
INSERT INTO "bs_darassalam" VALUES(57,1,'57:1','Allaha slavi i veliča sve što je na nebesima i na Zemlji, i On je Silni iMudri.');
INSERT INTO "bs_darassalam" VALUES(57,2,'57:2','Njegova je vlast na nebesima i na Zemlji; On život i smrt daje, i On nad svime ima moć.');
INSERT INTO "bs_darassalam" VALUES(57,3,'57:3','On je Prvi i Posljednji, i nad svime Dominantni i u bit svega Pronicljivi; i On zna sve!');
INSERT INTO "bs_darassalam" VALUES(57,4,'57:4','On je Onaj Koji je i nebesa i Zemlju za šest dana stvorio, a zatim se nad Aršom uzvisio; On zna šta u zemlju ulazi i šta iz nje izlazi i šta s neba silazi i šta se prema njemu diže, On je s vama gdje god bili, i sve što radite Allah vidi.');
INSERT INTO "bs_darassalam" VALUES(57,5,'57:5','Njegova je vlast na nebesima i na Zemlji, i Allahu će se sve vratiti!');
INSERT INTO "bs_darassalam" VALUES(57,6,'57:6','On uvodi noć u dan i dan u noć, i On zna što se u grudima skriva.');
INSERT INTO "bs_darassalam" VALUES(57,7,'57:7','Vjerujte u Allaha i Poslanika Njegova i udjeljujte iz onoga što vam je dao da naslijedite, jer one od vas koji budu vjerovali i udjeljivali čeka nagrada velika.');
INSERT INTO "bs_darassalam" VALUES(57,8,'57:8','Kako da ne vjerujete u Allaha, a Poslanik vas zove da vjerujete u Gospodara svoga – a On je od vas već zavjet uzeo – ako ste vjernici');
INSERT INTO "bs_darassalam" VALUES(57,9,'57:9','On robu Svome objavljuje jasne ajete da bi vas iz tmina na svjetlo izveo – a Allah je, uistinu, prema vama milosrdan i milostiv.');
INSERT INTO "bs_darassalam" VALUES(57,10,'57:10','I kako da ne trošite na Allahovu putu, a Allahu pripada nasljedstvo nebesa i Zemlje?! Nisu jednaki oni među vama koji su davali priloge prije pobjede i lično se borili – oni su na višem stupnju od onih koji su poslije davali priloge i lično se borili, a Allah svima obećava nagradu najljepšu; Allah u potpunosti zna ono što radite.');
INSERT INTO "bs_darassalam" VALUES(57,11,'57:11','Ko je taj ko će Allahu lijep zajam dati da bi mu ga On mnogostruko vratio, a uz to će on i nagradu divnu imati?');
INSERT INTO "bs_darassalam" VALUES(57,12,'57:12','Na Dan kad budeš vidio kako se pred vjernicima i vjernicama, i sdesne strane njihove, svjetlo njihovo bude kretalo: “Blago vama danas:džennetske bašče, kroz koje teku rijeke, u kojima ćete vječno boraviti – to je veliki uspjeh!”');
INSERT INTO "bs_darassalam" VALUES(57,13,'57:13','Na Dan kada će licemjeri i licemjerke vjernicima govoriti: “Pričekajtenas da se svjetlom vašim poslužimo!” “Vratite se natrag pa drugo svjetlo potražite?”, bit će rečeno. I između njih će pregrada postavljena biti koja će vrata imati; unutar nje bit će milost, a izvan nje patnja.');
INSERT INTO "bs_darassalam" VALUES(57,14,'57:14','“Zar nismo s vama bili?”, dozivat će ih. “Jeste”, odgovorit će, “ali ste sami sebe zaveli i iščekivali ste i sumnjali ste, i puste želje su vas zavaravale, dok nije došla Allahova odredba, i obmanitelj – šejtan vas o Allahu obmanuo.');
INSERT INTO "bs_darassalam" VALUES(57,15,'57:15','Nikakva otkupnina danas od vas neće primljena biti, a ni od onih koji nisu vjerovali; Vatra će biti prebivalište vaše, ona vam baš odgovara, a užasno je ona krajnje odredište!”');
INSERT INTO "bs_darassalam" VALUES(57,16,'57:16','Zar nije vrijeme da se onima koji vjeruju srca smekšaju zbog spomenaAllaha i Istine koja se objavljuje i da oni ne budu kao oni kojima je jošdavno data Knjiga, pa su srca njihova, zato što je proteklo mnogo vremena, postala nemilosrdna i mnogi su od njih buntovni.');
INSERT INTO "bs_darassalam" VALUES(57,17,'57:17','Znajte da Allah daje život već mrtvoj zemlji! Mi vam objasnismo ajete iznakove da biste razumjeli.');
INSERT INTO "bs_darassalam" VALUES(57,18,'57:18','Onima koji milostinju i zekat budu udjeljivali i onima koje je buduudjeljivale, i koji drage volje Allahu zajam budu davali – mnogostruko će se vratiti i njih čeka nagrada divna.');
INSERT INTO "bs_darassalam" VALUES(57,19,'57:19','A oni koji vjeruju u Allaha i Njegove poslanike – to su oni koji srcemu potpunosti vjeruju! A šehidima – u njihova Gospodara pripada njihova nagrada i svjetlo njihovo! A oni koji ne vjeruju i Naše ajete i znakove poriču, to su oni koji pripadaju Vatri!');
INSERT INTO "bs_darassalam" VALUES(57,20,'57:20','Znajte da je dunjalučki život igra, i zabava, i gizdanje, i međusobnohvalisanje, i nadmetanje u mnoštvu imetka i djece! On je nalik kiši, i bilje od nje sijače zadivi, i ono potom, nakon kiše, uzbuja, ali ga zatim vidiš požutjela i potom sasušeno biva. Na ahiretu patnja teška je, a ima i oprost Allahov i zadovoljstvo. A šta je dunjalučki život doli uživanje varljivo!');
INSERT INTO "bs_darassalam" VALUES(57,21,'57:21','Nadmećite se da u Gospodara svoga zaslužite oprost i Džennet, prostran koliko su nebo i Zemlja prostrani, i pripremljen za one koji u Allaha i poslanike Njegove vjeruju. To je Allahovo obilje koje će dati onome kome On hoće; a Allah je posjednik obilja velikog.');
INSERT INTO "bs_darassalam" VALUES(57,22,'57:22','Nema nevolje koja zadesi Zemlju i vas, a koja nije, prije nego što je damo, zapisana u Knjizi – to je Allahu, uistinu, lahko –');
INSERT INTO "bs_darassalam" VALUES(57,23,'57:23','da ne biste tugovali za onim što vam je promaklo, a i da se ne biste previše radovali onome što vam On dadne. Allah ne voli nikakve razmetljivce, hvalisavce,');
INSERT INTO "bs_darassalam" VALUES(57,24,'57:24','koji škrtare i traže da i ljudi škrti budu. A onaj ko neće da udjeljuje – pa,Allah je, uistinu, Neovisni i Hvaljeni.');
INSERT INTO "bs_darassalam" VALUES(57,25,'57:25','Mi smo izaslanike Naše s jasnim dokazima slali i po njima Knjige i Mjerilo – pravdu slali da bi ljudi pravedno postupali, a gvožđe smo stvorili i spustili, u kome je velika snaga i koje ljudima koristi, a da bi Allah znao one koji pomažu Njega i poslanike Njegove iako ih i ne vide. Allah je, uistinu, moćan i silan.');
INSERT INTO "bs_darassalam" VALUES(57,26,'57:26','Mi smo Nuha i Ibrahima poslali, i nekima od potomaka njihovih objavui vjerovjesništvo dali; neki su od potomaka njihovih na Pravom putu, amnogi od njih su buntovnici.');
INSERT INTO "bs_darassalam" VALUES(57,27,'57:27','Zatim smo, njihovim tragovima, jednog za drugim poslanike Naše slali,dok nismo Isaa, sina Merjemina, poslali. Njemu smo Indžil dali i učinilida u srcima sljedbenika njegovih blagost i samilost budu. A isposništvosu oni sami kao novotariju uveli u želji za Allahovim zadovoljstvom –Mi im ga nismo propisali; već smo im propisali traganje za Allahovimzadovoljstvom, ali oni o njemu nisu vodili brigu onako kako bi trebalo, pa ćemo one među njima koji budu ispravno vjerovali nagraditi, a mnogi od njih su buntovnici.');
INSERT INTO "bs_darassalam" VALUES(57,28,'57:28','O vi koji vjerujete, Allaha se bojte i u Poslanika Njegova vjerujte. On ćevam dvostruku milost Svoju darovati, i dat će vam svjetlo pomoću kojeg ćete ići i oprostit će vam, a Allah je Oprostitelj grijeha i milostiv je.');
INSERT INTO "bs_darassalam" VALUES(57,29,'57:29','I neka sljedbenici Knjige znaju da oni nikakvu Allahovu blagodat nećedobiti, jer je blagodat samo u Allahovoj ruci – daje je onome kome Onhoće; a Allah je posjednik obilja velikog.');
INSERT INTO "bs_darassalam" VALUES(58,1,'58:1','Allah je čuo riječi one koja se s tobom o mužu svome raspravljalai Allahu se jadala, a Allah čuje razgovor vaš međusobni. Allahje, uistinu, Onaj Koji sve čuje i Onaj Koji sve vidi.');
INSERT INTO "bs_darassalam" VALUES(58,2,'58:2','Oni od vas koji ženama svojim reknu da su im leđa njihova poput leđanjihovih majki, a one nisu majke njihove - majke njihove su samo one koje su ih rodile, oni, zaista, govore strašne riječi i neistinu - a Allah je zbilja Onaj Koji grijehe poništava i oprašta.');
INSERT INTO "bs_darassalam" VALUES(58,3,'58:3','Oni koji ženama svojim reknu da su im leđa njihova poput leđa njihovih majki, a onda se povrate u onome što su rekli, dužni su, prije nego što jedni drugo dodirnu, jednog roba ropstva osloboditi. To je ono čime se opominjete - a Allah u potpunosti zna što vi radite.');
INSERT INTO "bs_darassalam" VALUES(58,4,'58:4','Onaj koji ne nađe, dužan je dva mjeseca uzastopce postiti prije nego jedno drugo dodirnu. A onaj ko ne može, dužan je šezdeset siromaha nahraniti, zato da biste potvrdili da u Allaha i Poslanika Njegova vjerujete - to su Allahovi propisi. A nevjernike čeka kazna bolna.');
INSERT INTO "bs_darassalam" VALUES(58,5,'58:5','Doista, oni koji se suprotstavljaju Allahu i Poslaniku Njegovu bit ćeodbačeni i poniženi, kao što su bili odbačeni i poniženi i oni prije njih. A Mi smo objavili jasne ajete i znakove, a nevjernike čeka ponižavajuća patnja.');
INSERT INTO "bs_darassalam" VALUES(58,6,'58:6','na Dan kad ih Allah sve oživi, pa ih obavijesti o onom šta su radili; Allah je o tome račun sveo, a oni su to zaboravili - Allah je svemu svjedok.');
INSERT INTO "bs_darassalam" VALUES(58,7,'58:7','Zar ne znaš da Allah zna sve što je na nebesima i što je na Zemlji?! Nema tajnih razgovora među trojicom, a da On nije četvrti, niti među petericom, a da On nije šesti, ni kad ih je manje, ni kad ih je više, a da On nije s njima gdje god oni bili; On će ih na Kijametskom danu obavijestiti o onom što su radili. Allah zaista sve dobro zna.');
INSERT INTO "bs_darassalam" VALUES(58,8,'58:8','Ne vidje li one kojima je zabranjeno da se sašaptavaju kako se zatim vraćaju onome što im je zabranjeno i sašaptavaju se o grijehu, neprijateljstvu i neposlušnosti prema Poslaniku?! A kad ti dolaze, pozdravljaju te onako kako te Allah nikad nije pozdravio, i među sobomgovore: "Trebalo bi da nas Allah već jednom kazni za ono što govorimo!" Dovoljan će im biti Džehennem! U njemu će gorjeti, a užasno je to krajnje odredište!');
INSERT INTO "bs_darassalam" VALUES(58,9,'58:9','O vi koji vjerujete, kada se među sobom došaptavate, ne došaptavajtese o grijehu, neprijateljstvu i neposlušnosti prema Poslaniku, već se došaptavajte o dobročinstvu i bogobojaznosti, i bojte se Allaha, pred Kojim ćete okupljeni biti.');
INSERT INTO "bs_darassalam" VALUES(58,10,'58:10','Takvo sašaptavanje posao je šejtanski, da bi rastužio one koji vjeruju, a to ne može njima nimalo nauditi, osim ako to Allah dopusti. A vjernicineka se samo u Allaha pouzdaju!');
INSERT INTO "bs_darassalam" VALUES(58,11,'58:11','O vi koji vjerujete, kad vam se kaže: "Načinite mjesta drugima tamo gdje se sjedi", vi načinite, pa i vama će Allah mjesto načiniti; a kad vam se rekne: "Dignite se", vi se dignite, i Allah će na visoke stepeneuzdignuti one među vama koji vjeruju i kojima je dato znanje. A Allah u potpunosti zna ono što radite.');
INSERT INTO "bs_darassalam" VALUES(58,12,'58:12','O vi koji vjerujete, kad hoćete da se s Poslanikom sašaptavate, prije toga sadaku udijelite. To je za vas bolje i čistije. A ako ne nađete ništa - pa, Allah je, zaista, Oprostitelj grijeha, Milostivi.');
INSERT INTO "bs_darassalam" VALUES(58,13,'58:13','Zar vas je strah da prije razgovora svoga sadaku udijelite?! A ako neudijelite i Allah vam oprosti, onda namaz obavljajte i zekat dajite i budite pokorni Allahu i Poslaniku Njegovu! Allah u potpunosti zna ono što vi radite.');
INSERT INTO "bs_darassalam" VALUES(58,14,'58:14','Zar ti ne vidje one koji za prijatelje i zaštitnike uzimaju ljude na koje se Allah rasrdio?! Oni nisu ni vaši ni njihovi, i zaklinju se na laži, a oni znaju.');
INSERT INTO "bs_darassalam" VALUES(58,15,'58:15','Allah je njima pripremio žestoku patnju, a zaista je ružno ono što rade.');
INSERT INTO "bs_darassalam" VALUES(58,16,'58:16','Uzeli su svoje zakletve štitom, i odvraćaju od Allahova puta, pa im zato pripada ponižavajuća patnja.');
INSERT INTO "bs_darassalam" VALUES(58,17,'58:17','Ni imeci njihovi, ni djeca njihova im nimalo neće pomoći kodAllaha, oni će biti stanovnici u Vatri, u njoj će vječno boraviti.');
INSERT INTO "bs_darassalam" VALUES(58,18,'58:18','Na Dan u koji će ih Allah sve oživiti oni će se Njemuzaklinjati, kao što se vama zaklinju, misleći da će im to neštokoristiti. Oni su, doista, pravi lažljivci.');
INSERT INTO "bs_darassalam" VALUES(58,19,'58:19','Njima je ovladao šejtan i učinio da zaborave spominjati Allaha, oni su šejtanova stranka, a zbilja, oni iz stranke šejtanove gubitnici su pravi.');
INSERT INTO "bs_darassalam" VALUES(58,20,'58:20','Oni koji se suprotstavljaju Allahu i Poslaniku Njegovu bit će, sigurno, među onim najponiženijim.');
INSERT INTO "bs_darassalam" VALUES(58,21,'58:21','Allah je zapisao: "Ja i poslanici Moji sigurno ćemo pobijediti!" Allah je, zaista, moćan i silan.');
INSERT INTO "bs_darassalam" VALUES(58,22,'58:22','Nećeš naći da ljudi koji u Allaha i u ahiret vjeruju budu u ljubavi s onima koji se Allahu i Poslaniku Njegovu suprotstavljaju, pa bili to očevi njihovi, ili sinovi njihovi, ili braća njihova, ili rođaci njihovi. Njima je On u srca vjerovanje usadio i svjetlom Svojim ih osnažio, i On ćeih uvesti u džennetske bašče, kroz koje rijeke teku, da u njima vječno ostanu. Allah je njima zadovoljan, a i oni su zadovoljni Njime. Oni su Allahova stranka, adoista, Allahova su stranka oni koji će sigurno uspjeti.');
INSERT INTO "bs_darassalam" VALUES(59,1,'59:1','Allaha slavi i veliča sve što je na nebesima i što je na Zemlji,On je silan i mudar.');
INSERT INTO "bs_darassalam" VALUES(59,2,'59:2','On je Onaj Koji je prilikom prvog progonstva protjerao iz domova njihovih one sljedbenike Knjige koji nisu vjerovali. Vi niste mislili da će otići, a oni su mislili da će ih utvrde njihove od Allaha odbraniti, ali ih je Allah kaznio odakle se nisu nadali i On je u srca njihova strah ulio; vlastitim rukama i rukama vjernika svoje domove su rušili. Zato uzmite iz toga pouku, o vi razboriti!');
INSERT INTO "bs_darassalam" VALUES(59,3,'59:3','A da im Allah nije već bio propisao izgnanstvo, On bi ih drugojpatnji još na dunjaluku podvrgao! A njima na ahiretupripada patnja u Vatri.');
INSERT INTO "bs_darassalam" VALUES(59,4,'59:4','To je zato što su se Allahu i PoslanikuNjegovu suprotstavljali; a onaj ko se Allahu suprotstavlja - paAllah, zaista, žestoko kažnjava.');
INSERT INTO "bs_darassalam" VALUES(59,5,'59:5','To što ste neke palme posjekli ili ih, da uspravno stoje, ostavili - s Allahovim dopuštenjem ste učinili, i zato da On buntovnike ponizi.');
INSERT INTO "bs_darassalam" VALUES(59,6,'59:6','A vi niste kasom tjerali konje ni kamile zbog onoga što je Allahod njih, kao plijen, darovao Poslaniku Svome, nego, Allahprepušta vlast poslanicima Svojim nad onima nad kojimaOn hoće; Allah nad svime ima moć.');
INSERT INTO "bs_darassalam" VALUES(59,7,'59:7','Plijen od stanovnika sela i gradova koji Allah Poslaniku Svome daruje pripada: Allahu i Poslaniku Njegovu, i bližnjima njegovim, i siročadi, i siromašnim, i putnicima namjernicima - da ne bi prelazio samo iz ruku u ruke bogataša vaših; ono što vam Poslanik da, to prihvatite, a ono što vam zabrani - ostavite, i bojte se Allaha. Allah, zaista, žestoko kažnjava.');
INSERT INTO "bs_darassalam" VALUES(59,8,'59:8','I siromašnim muhadžirima koji su iz svojih domova i iz posjeda svojih protjerani, tragajući za Allahovim obiljem i zadovoljstvom, i Allahu i Poslaniku Njegovu pomažu, to su, zaista, iskreni.');
INSERT INTO "bs_darassalam" VALUES(59,9,'59:9','A oni koji su prije njih Medinu nastanili i iman prihvatili,oni vole one koji im doseljavaju, i u grudima svojim nikakvuzavist, zato što im se daje, ne osjećaju, i više vole njima negosebi, mada im je i samima potrebno. A oni koji se uščuvajugramzivosti i tvrdičluka, oni će sigurno uspjeti.');
INSERT INTO "bs_darassalam" VALUES(59,10,'59:10','Oni koji poslije njih dolaze - govore: "Gospodaru naš, oprosti nama ibraći našoj koja su nas u vjeri pretekla i ne dopusti da u srcimanašim bude imalo zlobe prema vjernicima; Gospodaru naš, Tisi, zaista, milosrdan i milostiv."');
INSERT INTO "bs_darassalam" VALUES(59,11,'59:11','Zar ne vidje licemjere kako govore svojim prijateljima, sljedbenicima Knjige koji ne vjeruju: "Ako budete protjerani, mi ćemo sigurno s vamapoći i kad se vas bude ticalo, nikada se nikome nećemo pokoriti; a ako budete napadnuti, sigurno ćemo vam u pomoć priteći", a Allah je svjedok da su oni, zaista, lašci.');
INSERT INTO "bs_darassalam" VALUES(59,12,'59:12','Ako bi bili protjerani, oni ne bi s njima pošli; ako bi bili napadnuti,oni im ne bi u pomoć pritekli; a ako bi im u pomoć pritekli,sigurno bi pobjegli i oni bi bez pomoći ostali.');
INSERT INTO "bs_darassalam" VALUES(59,13,'59:13','Oni se više boje vas nego Allaha, zato što su oni ljudi koji ne shvataju.');
INSERT INTO "bs_darassalam" VALUES(59,14,'59:14','Samo u utvrđenim gradovima ili iza zidina oni se protivvas smiju skupa boriti. Njihovo međusobno neprijateljstvožestoko je. Ti misliš da su oni složni, međutim, srca su njihovarazjedinjena, zato što su to ljudi koji ne razumiju.');
INSERT INTO "bs_darassalam" VALUES(59,15,'59:15','Slični su onima koji su, tu nedavno, svu pogubnost postupaka svojihiskusili - njih čeka patnja bolna.');
INSERT INTO "bs_darassalam" VALUES(59,16,'59:16','Slični su šejtanu kad kaže čovjeku: "Budi nevjernik!", pa kad postane nevjernik, on onda rekne: "Ja više nemam ništa s tobom; ja se, zaista,Allaha, Gopodara svjetova bojim."');
INSERT INTO "bs_darassalam" VALUES(59,17,'59:17','Obojicu ih, na kraju, čeka Vatra u kojoj će vječno boraviti, a to će biti kazna za sve zulumćare.');
INSERT INTO "bs_darassalam" VALUES(59,18,'59:18','O vi koji vjerujete, Allaha se bojte, i neka svaki čovjek gleda šta je za sutra pripremio, i Allaha se bojte. Allah, zaista, u potpunosti zna ono što radite.');
INSERT INTO "bs_darassalam" VALUES(59,19,'59:19','I ne budite kao oni koji su zaboravili Allaha, pa je On učinio dasami sebe zaborave; oni su baš buntovnici.');
INSERT INTO "bs_darassalam" VALUES(59,20,'59:20','Nisu jednaki stanovnici Džehennema i stanovnici Dženneta; stanovnici Dženneta baš su uspješni.');
INSERT INTO "bs_darassalam" VALUES(59,21,'59:21','Da ovaj Kur''an kakvom brdu objavimo, ti bi vidio kako je strahopoštovanja puno i kako bi se od straha pred Allahom raspalo. Takve primjere navodimo ljudima da bi razmislili.');
INSERT INTO "bs_darassalam" VALUES(59,22,'59:22','On je Allah, osim Kojeg nema drugog boga, poznavatelj onog što je čulima nedokučivo, a i onog što je pojavno, Svemilosni, Milostivi.');
INSERT INTO "bs_darassalam" VALUES(59,23,'59:23','On je Allah, osim Kojeg nema drugog boga, Vladar, Blagoslovljeni i Štovani, Savršeni - Onaj Koji je bez nedostataka, Onaj Koji pruža sigurnost, Onaj Koji nad svim bdi, Silni, Uzvišeni, Gordi. Slavljen i Uzvišen je Allah od onoga što mu pridružuju!');
INSERT INTO "bs_darassalam" VALUES(59,24,'59:24','On je Allah, Kreator, Tvorac, Oblikovatelj, On ima najljepša imena. Njega slavi i veliča sve što je na nebesima i na Zemlji, On je Silni i Mudri.');
INSERT INTO "bs_darassalam" VALUES(60,1,'60:1','O vi koji vjerujete! Ne uzimajte Moje neprijatelje i svojeneprijatelje za prisne prijatelje, ljubav im darivajući, a oniporiču Istinu koja došla je vama, i izgone Poslanika i vas zatošto vjerujete u Allaha, vašega Gospodara, ako ste već izišliradi džihada na Putu Mojemu i tražeći zadovoljstvo Moje.Vi njima ljubav tajno iskazujete, a Ja najbolje znam što viskrivate i što obznanjujete. A ko to od vas bude činio, pa - taj je zalutao s Pravog puta.');
INSERT INTO "bs_darassalam" VALUES(60,2,'60:2','Ako vas se oni domognu, bit će neprijatelji vaši i pružit će prema vama, u zloj namjeri, ruke svoje i jezike svoje, i poželjet će da uznevjerujete.');
INSERT INTO "bs_darassalam" VALUES(60,3,'60:3','Ni rodbina vaša ni djeca vaša neće vam biti od koristi, naKijametskom danu On će vas razdvojiti. Allah dobro vidi onošto radite.');
INSERT INTO "bs_darassalam" VALUES(60,4,'60:4','Imali ste dobar uzor u Ibrahimu i u onima kojisu s njim kad su narodu svome rekli: "Mi s vama nemamoništa, a ni s onim što vi, umjesto Allaha, obožavate, mi vasse odričemo, i neprijateljstvo i mržnja između nas stalnoće se javljati sve dok ne budete u Allaha, Njega Jedinog,vjerovali!", ali ne i u Ibrahimovim riječima ocu svome: "Jaću za tebe moliti oprost, ali te ne mogu od Allaha odbraniti.Gospodaru naš, na Tebe se oslanjamo, Tebi se obraćamo i kodTebe je krajnje odredište.');
INSERT INTO "bs_darassalam" VALUES(60,5,'60:5','Gospodaru naš, ne učini da budemo iskušenje onima koji ne vjeruju i oprosti nam, Gospodaru naš! Ti si, zaista, Silni i Mudri.''');
INSERT INTO "bs_darassalam" VALUES(60,6,'60:6','Bijahu oni vama dobar uzor – svakom onom ko se nada Allahu i Posljednjem danu. A svako onaj ko se okrene – pa, Allah je, zaista, Neovisni i Hvaljeni.');
INSERT INTO "bs_darassalam" VALUES(60,7,'60:7','Allah će sigurno uspostaviti ljubav između vas i onih s kojima ste u neprijateljstvu; Allah je svemoćan, i Allah je Onaj Koji oprašta grijehe i milostiv je.');
INSERT INTO "bs_darassalam" VALUES(60,8,'60:8','Allah vam ne zabranjuje da činite dobro i da budete pravedni prema onima koji nisu ratovali protiv vas zbog vjere i koji vas iz domova vaših nisu izgnali - Allah, zaista, voli one koji su pravični,');
INSERT INTO "bs_darassalam" VALUES(60,9,'60:9','ali vam zabranjuje da budete prisni s onima koji su ratovali protivvas zbog vjere i koji su vas iz domova vaših izgnali i koji su pomogli da budete prognani. Oni koji su s njima prisni baš su zulumćari.');
INSERT INTO "bs_darassalam" VALUES(60,10,'60:10','O vi koji vjerujete, kad vam vjernice kao muhadžirke dođu, ispitajte ih; Allah najbolje zna kakvo je vjerovanje njihovo, pa ako se uvjerite da su vjernice, onda ih ne vraćajte nevjernicima; one njima nisu dopuštene,niti su oni njima dopušteni; a njima dajte ono što su potrošili. Nije vam grijeh da se njima ženite kad im vjenčane darove njihove date. U braku nevjernice ne zadržavajte! Tražite ono što ste potrošili, a neka i oni traže ono što su potrošili! To je Allahov sud, On sudi među vama, a Allah je Onaj Koji sve zna i mudar je.');
INSERT INTO "bs_darassalam" VALUES(60,11,'60:11','A ako neka od žena vaših nevjernicimaumakne, i ako vi poslije u borbi plijen zarobite, onda onimačije su žene umakle vjenčane darove koje su im dali namirite.I bojte se Allaha, u Koga vjerujete!');
INSERT INTO "bs_darassalam" VALUES(60,12,'60:12','O vjerovjesniče, kada ti dođu vjernice da ti polože prisegu: da neće Allahu nikoga ravnim smatrati, i da neće krasti, i da neće bludničiti, i da neće djecu svoju ubijati, i da neće muževima tuđu djecu podmetati i da neće ni u čemu što je dobro poslušnost odricati - ti prisegu njihovu prihvati i moli Allaha da im oprosti; Allah, zaista, mnogo prašta, i On je milostiv.');
INSERT INTO "bs_darassalam" VALUES(60,13,'60:13','O vi koji vjerujete, ne uzimajte za prisne prijatelje izaštitnike ljude na koje se Allah rasrdio; oni su izgubili naduda će bilo kakvu nagradu na ahiretu imati, isto kao što sunevjernici izgubili nadu u vezi s onim koji su u grobovima.');
INSERT INTO "bs_darassalam" VALUES(61,1,'61:1','Allaha slavi i veliča ono što je na nebesima i ono što je na Zemlji;On je Silni i Mudri!');
INSERT INTO "bs_darassalam" VALUES(61,2,'61:2','O vi koji vjerujete, zašto govorite ono što ne činite?!');
INSERT INTO "bs_darassalam" VALUES(61,3,'61:3','Kako samo Allah prezire da govorite ono što ne činite!');
INSERT INTO "bs_darassalam" VALUES(61,4,'61:4','Allah, zaista, voli one koji se na Njegovu putu bore u redovima kao da su zdanje utvrđeno.');
INSERT INTO "bs_darassalam" VALUES(61,5,'61:5','Kada Musa reče narodu svome: "O narode moj, zašto me uznemiravate, a dobro znate da sam vam ja Allahov poslanik!", i kad oni skrenuše ustranu, Allah njihova srca skrenu, a Allah neće uputiti narod koji je buntovan.');
INSERT INTO "bs_darassalam" VALUES(61,6,'61:6','A kada Isa, sin Merjemin, reče: “O sinovi Israilovi, ja sam vam Allahovposlanik, da vam potvrdim prije mene objavljeni Tevrat i da vam donesem radosnu vijest o poslaniku čije je ime Ahmed, koji će poslije mene doći.”, i kad im je donio jasne dokaze, oni rekoše: “Ovo je očita vradžbina!”');
INSERT INTO "bs_darassalam" VALUES(61,7,'61:7','A ima li većeg zulumćara od onoga koji o Allahu laži iznosidok se u islam poziva?! A Allah neće uputiti ljude zulumćare.');
INSERT INTO "bs_darassalam" VALUES(61,8,'61:8','Oni žele utrnuti Allahovo svjetlo ustima svojim, a Allah će upotpuniti svjetlo Svoje, makar to mrzili nevjernici.');
INSERT INTO "bs_darassalam" VALUES(61,9,'61:9','On je Onaj Koji je poslao Svog Poslanika s Uputom i Vjerom Istineda bi je uzdigao iznad svih vjera, makar to višebošci mrzili.');
INSERT INTO "bs_darassalam" VALUES(61,10,'61:10','O vi koji vjerujete, hoćete li da vam ukažem na trgovinu, kojaće vas iz patnje bolne spasiti?');
INSERT INTO "bs_darassalam" VALUES(61,11,'61:11','U Allaha i Poslanika Njegova vjerujte i imecima svojim i životima svojim na Allahovu putu se borite - to vam je, da znate, bolje.');
INSERT INTO "bs_darassalam" VALUES(61,12,'61:12','On će vam grijehe vaše oprostiti i uvesti vas u džennetske bašče, kroz koje rijeke teku, i u divne dvorove u vrtovima Adna; to je uspjeh veliki.');
INSERT INTO "bs_darassalam" VALUES(61,13,'61:13','A dat će vam i drugu blagodat koju volite: Allahovu pomoć i skorupobjedu! Zato obraduj vjernike!');
INSERT INTO "bs_darassalam" VALUES(61,14,'61:14','O vi koji vjerujete, budite pomagači Allahovi, kao što je rekao Isa, sin Merjemin, prisnim sljedbenicima: "Ko su moji pomagači u pozivanju Allahu?" Prisni sljedbenici odgovoriše: "Mi smo Allahovi pomagači!" Onda je jedna skupina iz potomaka lsrailovih povjerovala, a druga skupina uznevjerovala! Pa smo one koji su vjerovali protiv njihovih neprijatelja osnažili pa su postali nadmoćni.');
INSERT INTO "bs_darassalam" VALUES(62,1,'62:1','Allaha slavi i veliča sve što je na nebesima i ono što je na Zemlji, Vladara, Blagoslovljenoga i Štovanoga, Silnoga i Mudroga.');
INSERT INTO "bs_darassalam" VALUES(62,2,'62:2','On je nepismenima poslao Poslanika, jednog između njih, da im ajeteNjegove kazuje i da ih očisti i da ih Knjizi i mudrosti nauči, iako su prijebili u očitoj zabludi,');
INSERT INTO "bs_darassalam" VALUES(62,3,'62:3','i drugima koji im se još nisu priključili, On je Silni i Mudri.');
INSERT INTO "bs_darassalam" VALUES(62,4,'62:4','To je Allahova milost, koju On daje onome kome hoće – a Allah jeposjednik obilja velikog.');
INSERT INTO "bs_darassalam" VALUES(62,5,'62:5','Oni kojima je Tevrat da ga nose dat, pa ga potom ne nose, slični su magarcu koji knjige nosi. O, kako su loši oni koji Allahove ajete i znakove poriču! A Allah neće uputiti na Pravi put ljude zulumćare.');
INSERT INTO "bs_darassalam" VALUES(62,6,'62:6','Reci: “O jevreji, ako tvrdite da ste vi od svih ljudi jedini Allahovi miljenici, onda smrt poželite ako istinu govorite.”');
INSERT INTO "bs_darassalam" VALUES(62,7,'62:7','A zbog onoga što ruke njihove čine neće je nikad poželjeti; Allah dobrozna zulumćare.');
INSERT INTO "bs_darassalam" VALUES(62,8,'62:8','Reci: “Smrt od koje bježite zaista će vas stići. Zatim ćete Poznavateljuonoga što je čulima nedokučivo i onoga što je pojavno vraćeni biti i On će vas o onom što ste radili obavijestiti.”');
INSERT INTO "bs_darassalam" VALUES(62,9,'62:9','O vi koji vjerujete, kad u petak na namaz budete pozvani, požurite naspominjanje Allaha i kupoprodaju ostavite, to vam je bolje, ako znate!');
INSERT INTO "bs_darassalam" VALUES(62,10,'62:10','A kad se namaz završi, onda se po zemlji raziđite i za Allahovim dobrima tragajte i Allaha mnogo spominjite da biste uspjeli.');
INSERT INTO "bs_darassalam" VALUES(62,11,'62:11','Ali, kad oni kakvu robu trgovačku ili zabavu ugledaju, pohrle joj i tebeostave sama da stojiš. Reci: “Ono što je u Allaha bolje je i od zabave i od trgovine. A Allah je Opskrbitelj najbolji.”');
INSERT INTO "bs_darassalam" VALUES(63,1,'63:1','Kad ti licemjeri dolaze, oni govore: “Mi svjedočimo da si ti, zaista, Allahov poslanik!”, i Allah zna da si ti, zaista, Njegov poslanik, a Allah svjedoči i da su licemjeri pravi lašci.');
INSERT INTO "bs_darassalam" VALUES(63,2,'63:2','Oni su svoje zakletve kao štit uzeli, pa od Allahova puta odvraćaju. Ružno je, doista, kako postupaju.');
INSERT INTO "bs_darassalam" VALUES(63,3,'63:3','To je zato što su vjernici bili, pa nevjernici postali, i onda su im srcazapečaćena, pa ne shvaćaju.');
INSERT INTO "bs_darassalam" VALUES(63,4,'63:4','Kad ih pogledaš, njihov te izgled zadivljuje; a kad progovore, ti slušaš riječi njihove – međutim, oni su kao šuplji naslonjeni balvani i misle da je svaki povik protiv njih. Oni su pravi neprijatelji, pa ih se pričuvaj! Allah ih ubio, kuda se odmeću!');
INSERT INTO "bs_darassalam" VALUES(63,5,'63:5','A kad im bude rečeno: “Dođite, Allahov Poslanik će moliti da vam seoprosti!”, oni glavama svojim tresu i vidiš ih kako nadmeno odbijaju.');
INSERT INTO "bs_darassalam" VALUES(63,6,'63:6','Isto im je – molio ti oprost za njih ili ne molio, Allah im neće oprostiti.Allah, doista, neće na Pravi put uputiti narod koji je buntovan.');
INSERT INTO "bs_darassalam" VALUES(63,7,'63:7','Oni govore: “Ne udjeljujte ništa onima koji su uz Allahova Poslanika dabi ga napustili!” A riznice nebesa i Zemlje Allahove su, ali licemjeri neshvataju.');
INSERT INTO "bs_darassalam" VALUES(63,8,'63:8','Oni govore: “Ako se vratimo u Medinu, sigurno će jači istjerati iz njeslabijeg!” A snaga je u Allaha i u Poslanika Njegova i u vjernika, ali licemjeri ne znaju.');
INSERT INTO "bs_darassalam" VALUES(63,9,'63:9','O vi koji vjerujete, neka vas imanja vaša i djeca vaša ne zabave odspominjanja Allaha. A oni koji to učine, pravi su gubitnici.');
INSERT INTO "bs_darassalam" VALUES(63,10,'63:10','I udjeljujte od onoga čime vas Mi opskrbljujemo prije nego nekom odvas smrt dođe, pa da onda rekne: “Gospodaru moj, da me još samo kratko vrijeme zadržiš, pa da imetak udjeljujem i da dobar budem!”');
INSERT INTO "bs_darassalam" VALUES(63,11,'63:11','Allah sigurno neće ostaviti u životu nikoga kome smrt njegova dođe. Allah u potpunosti zna ono što vi radite.');
INSERT INTO "bs_darassalam" VALUES(64,1,'64:1','Allaha slavi i veliča sve što je na nebesima i sve što je na Zemlji. Njemu sva vlast i Njemu sva pohvala pripada; On nad svime ima moć!');
INSERT INTO "bs_darassalam" VALUES(64,2,'64:2','On je Onaj Koji vas stvara, pa ima među vama i nevjernika, a ima među vama i vjernika. Sve što vi radite Allah dobro vidi.');
INSERT INTO "bs_darassalam" VALUES(64,3,'64:3','On je nebesa i Zemlju s Istinom stvorio i uobličio vas i likove vaše čininajljepšim, i kod Njega je konačno odredište.');
INSERT INTO "bs_darassalam" VALUES(64,4,'64:4','On zna šta na nebesima i na Zemlji postoji i zna šta krijete i šta pokazujete! Allah dobro zna šta grudi kriju.');
INSERT INTO "bs_darassalam" VALUES(64,5,'64:5','Nije li do vas doprla vijest o onima koji još davno nisu vjerovali, pa sukobnost postupka svoga iskusili – a još ih i kazna bolna čeka?');
INSERT INTO "bs_darassalam" VALUES(64,6,'64:6','To je zato što su govorili, kad su im poslanici njihovi jasne dokaze donosili: “Zar da nas ljudi upućuju?” Pa nisu vjerovali i okretali su se; a Allahu nije niko potreban, Allah je neovisan i hvaljen!');
INSERT INTO "bs_darassalam" VALUES(64,7,'64:7','Nevjernici misle da neće biti oživljeni. Reci: “Hoćete, Gospodara mimoga, sigurno ćete biti oživljeni, pa ćete o onom što ste radili, doista, biti obaviješteni!”, a to je Allahu lahko.');
INSERT INTO "bs_darassalam" VALUES(64,8,'64:8','Zato, vjerujte u Allaha i Poslanika Njegova i u Svjetlo koje objavljujemo, a Allah u potpunosti zna ono što vi radite.');
INSERT INTO "bs_darassalam" VALUES(64,9,'64:9','Na Dan kad vas On sakupi, na Dan zbora, to je Dan međusobne obmane. Ko bude vjerovao i dobra djela činio, On će mu prekriti loša djela njegova i uvest će ga u džennetske bašče, kroz koje rijeke teku, u njima će vječno, zanavijek boraviti. To će uspjeh veliki biti!');
INSERT INTO "bs_darassalam" VALUES(64,10,'64:10','A oni koji nisu vjerovali i koji su ajete i znakove Naše poricali, bit ćestanovnici u Vatri; u njoj će vječno ostati, a to gdje će skončati bit ćeužasno mjesto.');
INSERT INTO "bs_darassalam" VALUES(64,11,'64:11','Nikakva nevolja ne pogađa osim s Allahovom dozvolom! A ko u Allahavjeruje, On će uputiti njegovo srce, a Allah sve dobro zna.');
INSERT INTO "bs_darassalam" VALUES(64,12,'64:12','I pokoravajte se Allahu i Poslaniku Njegovu; a ako se okrenete, pa obaveza Našeg Poslanika je samo jasna obznana!');
INSERT INTO "bs_darassalam" VALUES(64,13,'64:13','Samo je Allah Bog, i neka se vjernici samo u Allaha pouzdaju!');
INSERT INTO "bs_darassalam" VALUES(64,14,'64:14','O vi koji vjerujete, među suprugama vašim i djecom vašom, doista, imate neprijatelja, pa ih se pričuvajte! A ako preko toga pređete i ne korite i oprostite, pa Allah je, zaista, Onaj Koji oprašta grijehe i milostiv je.');
INSERT INTO "bs_darassalam" VALUES(64,15,'64:15','Imanja vaša i djeca vaša samo su iskušenje, a u Allaha je nagrada velika.');
INSERT INTO "bs_darassalam" VALUES(64,16,'64:16','Pa Allaha se bojte koliko god možete, i slušajte i pokoravajte se i udjeljujte za svoje dobro. A oni koji budu sačuvani gramzivosti i tvrdičluka, pa takvi su baš uspjeli.');
INSERT INTO "bs_darassalam" VALUES(64,17,'64:17','A ako Allahu lijep zajam date, On će vam ga uvećati i oprostit će vam.Allah je Zahvalan i Onaj Koji ne žuri s kaznom.');
INSERT INTO "bs_darassalam" VALUES(64,18,'64:18','Poznavatelj onoga što je čulima nedokučivo i onoga što je pojavno, Silni i Mudri.');
INSERT INTO "bs_darassalam" VALUES(65,1,'65:1','O Vjerovjesniče, kad htjednete žene pustiti, vi ih u vrijeme kad su čistepustite, i brojte to vrijeme, i Allaha, Gospodara svoga, bojte se. Ne izgonite ih iz kuća njihovih, a ni one neka ne izlaze, osim ako očito sramno djelo učine. To su Allahove granice. Onaj koji Allahove propise krši, sam sebi je zulum učinio. Šta znaš, Allah će, možda, poslije toga nešto novo odrediti?!');
INSERT INTO "bs_darassalam" VALUES(65,2,'65:2','Pa kada one ispune vrijeme svog čekanja, vi ih ili na lijep način zadržite ili se lijepo od njih konačno rastavite, i kao svjedoke dvojicu svojih pravednih ljudi uzmite, i svjedočenje radi Allaha obavite! To je savjet za onog koji u Allaha i u Posljednji dan vjeruje – a onome koji se Allaha boji, On će izlaz dati');
INSERT INTO "bs_darassalam" VALUES(65,3,'65:3','i opskrbit će ga odakle se i ne nada; onome koji se u Allaha pouzdaje, On mu je dovoljan. Allah će, zaista, ispuniti ono što je odlučio; Allah je svemu već rok odredio.');
INSERT INTO "bs_darassalam" VALUES(65,4,'65:4','A one žene vaše, koje su nadu u mjesečno pranje izgubile – ako sumnjate – one treba da čekaju tri mjeseca, a i one koje ga još nisu dobile! Vrijeme čekanja za trudne žene jeste porod. A onome ko se Allaha boji, On će u svemu lahkoću dati.');
INSERT INTO "bs_darassalam" VALUES(65,5,'65:5','To je Allahov propis koji vam On objavljuje. A onome ko se bude Allahabojao, On će loše postupke pokriti i još mu nagradu uvećati.');
INSERT INTO "bs_darassalam" VALUES(65,6,'65:6','Njih ostavite da stanuju tamo gdje i vi stanujete, prema svojim mogućnostima, i ne činite im teškoće zato da biste na njih pritisak izvršili. Ako su trudne, snosite troškove njihovog izdržavanja sve dok se ne porode; a ako vam djecu doje, onda im dajte naknadu i sporazumite se međusobno na lijep način. A ako nastanu razmirice, neka mu onda druga doji dijete.');
INSERT INTO "bs_darassalam" VALUES(65,7,'65:7','Neka imućan prema bogatstvu svome troši, a onaj koji je u oskudici - prema tome koliko mu je Allah dao, jer Allah nikoga ne zadužuje više nego što mu je dao; Allah će, sigurno, poslije tegobe olakšanje dati.');
INSERT INTO "bs_darassalam" VALUES(65,8,'65:8','A koliko li samo naselja bi što se o zapovijed Gospodara svoga i Njegovih poslanika ogluši?! Pa smo s njima žestoko račun sveli i kaznili ih – a i kaznit ćemo ih – patnjom užasnom!');
INSERT INTO "bs_darassalam" VALUES(65,9,'65:9','Oni su pogubnost postupaka svojih iskusili, a propast je bila – i bit će –njihov kraj.');
INSERT INTO "bs_darassalam" VALUES(65,10,'65:10','Allah je za njih bolnu patnju pripremio; zato se bojte Allaha, o vi koji sterazumom obdareni, vi koji vjerujete! Allah vam je već spustio Opomenu');
INSERT INTO "bs_darassalam" VALUES(65,11,'65:11','i poslao Poslanika, koji vam Allahove ajete jasne kazuje, da bi iz tmina na svjetlo izveo one koji vjeruju i dobra djela čine. A onoga koji bude u Allaha vjerovao i dobro činio, uvest će u džennetske bašče, kroz koje rijeke teku, u njima će vječno, zanavijek ostati; najljepšu opskrbu Allah mu je pripremio.');
INSERT INTO "bs_darassalam" VALUES(65,12,'65:12','Allah je sedam nebesa i isto toliko slojeva zemlje stvorio; Njegova odredba do svih njih dospijeva da biste vi znali da Allah nad svime ima moć i da Allah znanjem Svojim sve obuhvata!');
INSERT INTO "bs_darassalam" VALUES(66,1,'66:1','O Vjerovjesniče, zašto sebi zabranjuješ ono što ti je Allah dozvolio – želeći zadovoljstvo svojih supruga? A Allah je Onaj Koji oprašta grijehe i milostiv je.');
INSERT INTO "bs_darassalam" VALUES(66,2,'66:2','Allah vam je propisao kako da svoje zakletve iskupite; Allah je vaš Gospodar; On je Sveznajući i Mudri.');
INSERT INTO "bs_darassalam" VALUES(66,3,'66:3','A kad Poslanik jednoj supruzi svojoj tajno govor povjeri, pa ga onarazglasi, a Allah mu to obznani, on je upozna s nečim što je kazala, a nešto prešuti. Pa kad je on o tome obavijesti, ona upita: “Ko tebe o tome izvijesti?” On odgovori: “Izvijestio me je Sveznajući, Onaj Koji u potpunosti sve zna.”');
INSERT INTO "bs_darassalam" VALUES(66,4,'66:4','Ako vas dvije učinite pokajanje Allahu, pa, vi ste bile učinile ono zbog čega je trebalo da se pokajete. A ako se protiv njega udružite, pa, Allah je zaštitnik njegov, i Džibril, i čestiti vjernici; najposlije, i svi meleki će mu podrška biti.');
INSERT INTO "bs_darassalam" VALUES(66,5,'66:5','Ako vas on pusti, Gospodar njegov će mu dati, umjesto vas, bolje supruge; muslimanke, mu’minke, pokorne Allahu, pokajnice, pobožne, koje poste, i udavane i djevice.');
INSERT INTO "bs_darassalam" VALUES(66,6,'66:6','O vi koji vjerujete, sebe i porodice svoje čuvajte od Vatre, kojoj su ljudi i kamenje gorivo, o kojoj se meleki strogi i snažni brinu, koji se onome što im Allah zapovjedi ne opiru, i koji ono što im se naredi izvršavaju.');
INSERT INTO "bs_darassalam" VALUES(66,7,'66:7','“O vi koji niste vjerovali, danas se ne pravdajte, bit ćete kažnjeni samo za ono što ste radili.');
INSERT INTO "bs_darassalam" VALUES(66,8,'66:8','O vi koji vjerujete, pokajte se čineći Allahu tevbu iskrenu, ne bi li Gospodar vaš loše postupke vaše izbrisao i da bi vas u džennetske bašče, kroz koje rijeke teku, uveo, na Dan u kojem Allah neće poniziti Vjerovjesnika i one koji su zajedno s njim vjerovali; svjetlo njihovo ići će ispred njih, s njihove desne strane. “Gospodaru naš”, govorit će oni, “učini potpunim svjetlo naše i oprosti nam. Ti, doista, nad svime imaš moć.”');
INSERT INTO "bs_darassalam" VALUES(66,9,'66:9','O Vjerovjesniče, bori se protiv nevjernika i licemjera i budi strog premanjima! Njihovo prebivalište bit će Džehennem, a užasno je to krajnjeodredište!');
INSERT INTO "bs_darassalam" VALUES(66,10,'66:10','Allah navodi kao primjer onih koji ne vjeruju ženu Nuhovu i ženu Lutovu: bile su udate za dva čestita roba Naša, pa su ih iznevjerile, i njih dvojica im neće ništa moći kod Allaha pomoći, i bit će rečeno: “Ulazite vas dvije u Vatru s onima koji u nju ulaze!”');
INSERT INTO "bs_darassalam" VALUES(66,11,'66:11','A Allah navodi kao primjer onih koji vjeruju ženu faraonovu, kad je rekla: “Gospodaru moj, sagradi mi kod Sebe kuću u Džennetu i spasi me od faraona i njegovih postupaka, i izbavi me od ljudi zulumćara!”');
INSERT INTO "bs_darassalam" VALUES(66,12,'66:12','I Merjemu, kćer Imranovu, koja je nevinost svoju sačuvala, pa smo u nju duha Svog udahnuli, i ona je u riječi Gospodara svoga i Knjige Njegove povjerovala i od onih koji su pokorni bila.');
INSERT INTO "bs_darassalam" VALUES(67,1,'67:1','Uzvišen i blagoslovljen je Onaj u Čijoj je ruci vlast i On nad svime imamoć,');
INSERT INTO "bs_darassalam" VALUES(67,2,'67:2','Onaj Koji je stvorio smrt i život da bi iskušao koji će od vas bolja djelaimati; On je Silni, Oprostitelj grijeha,');
INSERT INTO "bs_darassalam" VALUES(67,3,'67:3','Onaj koji je sedam nebesa kao slojeve, jedne iznad drugih, stvorio – ti u onome što Svemilosni stvara ne vidiš nikakva nesklada, pa ponovo pogledaj, vidiš li ikakvu pukotinu?');
INSERT INTO "bs_darassalam" VALUES(67,4,'67:4','Zatim, ponovo više puta pogledaj, pogled će ti se vratiti klonuo i umoran.');
INSERT INTO "bs_darassalam" VALUES(67,5,'67:5','Mi smo najbliže nebo sjajnim svjetiljkama ukrasili i učinili smo ih zagađanje šejtana, i patnju u Ognju im pripremili.');
INSERT INTO "bs_darassalam" VALUES(67,6,'67:6','A onima koji ne vjeruju u Gospodara svoga, pripada patnja u Džehennemu. Užasno je to boravište!');
INSERT INTO "bs_darassalam" VALUES(67,7,'67:7','Kad budu u njega bačeni, hučanje njegovo čut će, i on će ključati,');
INSERT INTO "bs_darassalam" VALUES(67,8,'67:8','gotovo da se od bijesa raspadne. Kad god se koja gomila u njega baci,stražari u njemu će je upitati: “Zar nije niko dolazio da vas upozorava?”');
INSERT INTO "bs_darassalam" VALUES(67,9,'67:9','“Jest, dolazio nam je onaj koji nas je upozoravao”, odgovorit će, “a mi smo poricali i govorili: ‘Allah nije objavio ništa, vi ste u velikoj zabludi!’”');
INSERT INTO "bs_darassalam" VALUES(67,10,'67:10','I reći će: “Da smo slušali ili razmišljali, ne bismo među stanovnicimaOgnja bili!”');
INSERT INTO "bs_darassalam" VALUES(67,11,'67:11','I oni će priznati svoje grijehe, pa daleko bili stanovnici Ognja!');
INSERT INTO "bs_darassalam" VALUES(67,12,'67:12','Onima koji se Gospodara svoga boje i kad ih niko ne vidi – pripada oprost i nagrada velika!');
INSERT INTO "bs_darassalam" VALUES(67,13,'67:13','Govorili vi tiho ili glasno – pa, On, sigurno, zna šta grudi kriju!');
INSERT INTO "bs_darassalam" VALUES(67,14,'67:14','A kako i ne bi znao Onaj Koji stvara, Koji zna i ono što je najsitnije, Onaj Koji u potpunosti sve zna!');
INSERT INTO "bs_darassalam" VALUES(67,15,'67:15','On vam je Zemlju pogodnom učinio, pa hodajte predjelima njezinim ihranite se onim što On daje, ali Njemu se sve vraća.');
INSERT INTO "bs_darassalam" VALUES(67,16,'67:16','Jeste li sigurni da vas Onaj Koji je na nebesima neće u zemlju utjerati kad se ona počne iznenada tresti?!');
INSERT INTO "bs_darassalam" VALUES(67,17,'67:17','Ili ste sigurni da Onaj Koji je na nebesima na vas neće vjetar s kamenjem poslati?! Pa, znat ćete kakvo je upozorenje Moje!');
INSERT INTO "bs_darassalam" VALUES(67,18,'67:18','A poricali su i oni prije njih, pa kakva je bila osuda Moja!');
INSERT INTO "bs_darassalam" VALUES(67,19,'67:19','Zar oni ne vide ptice iznad sebe kako raširenih krila lete, a i skupljaju ih? Samo ih Svemilosni drži; On, zaista, sve dobro vidi.');
INSERT INTO "bs_darassalam" VALUES(67,20,'67:20','I, ko je taj koji će biti vojska vaša i vama pomoći, osim Svemilosnog?!Ništa osim obmane nevjernici nemaju!');
INSERT INTO "bs_darassalam" VALUES(67,21,'67:21','A, i ko je taj koji će vas nahraniti ako On hranu Svoju uskrati?! Ali, oni su ustrajni u osionosti i odbojnosti.');
INSERT INTO "bs_darassalam" VALUES(67,22,'67:22','Da li je bolje upućen onaj koji ide pognut licem prema zemlji ili onaj kojiPravim putem uspravno hodi?');
INSERT INTO "bs_darassalam" VALUES(67,23,'67:23','Reci: “On vas je prvi put stvorio i dao vam sluh, vid i razum, pa kako samo malo zahvaljujete!”');
INSERT INTO "bs_darassalam" VALUES(67,24,'67:24','Reci: “On vas je po Zemlji razasuo i pred Njim ćete biti sakupljeni.”');
INSERT INTO "bs_darassalam" VALUES(67,25,'67:25','A oni govore: “Kad će već jednom ta prijetnja ako istinu govorite?!”');
INSERT INTO "bs_darassalam" VALUES(67,26,'67:26','Reci: “Samo Allah zna! A ja sam samo upozoritelj jasni.”');
INSERT INTO "bs_darassalam" VALUES(67,27,'67:27','Kad je izbliza ugledaju ojađena bit će lica onih koji nisu vjerovali i bit ćerečeno: “Evo, ovo je ono što ste požurivali!”');
INSERT INTO "bs_darassalam" VALUES(67,28,'67:28','Reci: “Kažite vi meni: ako Allah pošalje smrt meni i onima koji su samnom, ili ako nam se smiluje, ko će nevjernike od bolne patnje zaštititi?”');
INSERT INTO "bs_darassalam" VALUES(67,29,'67:29','Reci: “On je Svemilosni, u Njega vjerujemo i na Njega se oslanjamo, avi ćete, sigurno, saznati ko je u očitoj zabludi.”');
INSERT INTO "bs_darassalam" VALUES(67,30,'67:30','Reci: “Šta mislite, ako vode vaše ponornice postanu, ko će vam voduna površinu izvesti?”');
INSERT INTO "bs_darassalam" VALUES(68,1,'68:1','Nun. Tako Mi pera i onoga što oni pišu,');
INSERT INTO "bs_darassalam" VALUES(68,2,'68:2','ti uz blagodati Gospodara svoga nisi lud,');
INSERT INTO "bs_darassalam" VALUES(68,3,'68:3','ti ćeš, sigurno, nagradu nepresušnu imati.');
INSERT INTO "bs_darassalam" VALUES(68,4,'68:4','I, zaista, tvoj ahlak je veličanstven');
INSERT INTO "bs_darassalam" VALUES(68,5,'68:5','i ti ćeš vidjeti, a i oni će vidjeti,');
INSERT INTO "bs_darassalam" VALUES(68,6,'68:6','ko je od vas zaveden!');
INSERT INTO "bs_darassalam" VALUES(68,7,'68:7','Gospodar tvoj najbolje zna ko je s puta Njegova skrenuo i On najbolje zna one koji su na Pravom putu.');
INSERT INTO "bs_darassalam" VALUES(68,8,'68:8','Pa, ne pokoravaj se poricateljima,');
INSERT INTO "bs_darassalam" VALUES(68,9,'68:9','oni bi jedva dočekali da ti popustiš, pa bi i oni popustili,');
INSERT INTO "bs_darassalam" VALUES(68,10,'68:10','i ne slušaj nijednog krivokletnika, prezrena,');
INSERT INTO "bs_darassalam" VALUES(68,11,'68:11','onoga koji tuđe riječi radi smutnje prenosi,');
INSERT INTO "bs_darassalam" VALUES(68,12,'68:12','koji sprečava dobro, onoga koji skrnavi propisanu granicu, griješnika');
INSERT INTO "bs_darassalam" VALUES(68,13,'68:13','surova i, osim toga, po zlu poznata,');
INSERT INTO "bs_darassalam" VALUES(68,14,'68:14','samo zato što ima mnogo imetka i sinova,');
INSERT INTO "bs_darassalam" VALUES(68,15,'68:15','koji govori, kad mu se ajeti Naši kazuju: “To su samo naroda drevnihpriče!”');
INSERT INTO "bs_darassalam" VALUES(68,16,'68:16','Na nosinu ćemo Mi njemu biljeg utisnuti!');
INSERT INTO "bs_darassalam" VALUES(68,17,'68:17','Mi smo ih na kušnju stavili, kao što smo vlasnike jedne bašče na kušnju stavili kad su se zakleli da će je, sigurno, rano izjutra obrati,');
INSERT INTO "bs_darassalam" VALUES(68,18,'68:18','a nisu rekli: “Ako Allah da!”');
INSERT INTO "bs_darassalam" VALUES(68,19,'68:19','I dok su oni spavali, nju od Gospodara tvoga zadesi nesreća');
INSERT INTO "bs_darassalam" VALUES(68,20,'68:20','i ona osvanu opustošena.');
INSERT INTO "bs_darassalam" VALUES(68,21,'68:21','A u zoru oni su jedni druge dozivali:');
INSERT INTO "bs_darassalam" VALUES(68,22,'68:22','“Poranite do zasada svoga ako ga mislite obrati!”');
INSERT INTO "bs_darassalam" VALUES(68,23,'68:23','I oni krenuše tiho razgovarajući:');
INSERT INTO "bs_darassalam" VALUES(68,24,'68:24','“Neka vam danas u nju nikako nijedan siromah ne ulazi!”');
INSERT INTO "bs_darassalam" VALUES(68,25,'68:25','I oni poraniše odlučni da će je moći obrati,');
INSERT INTO "bs_darassalam" VALUES(68,26,'68:26','a kad je ugledaše, povikaše: “Mi smo zalutali,');
INSERT INTO "bs_darassalam" VALUES(68,27,'68:27','štaviše – svega smo lišeni!”');
INSERT INTO "bs_darassalam" VALUES(68,28,'68:28','Ponajbolji između njih reče: “Nisam li vam ja govorio zašto Allaha neslavite!”');
INSERT INTO "bs_darassalam" VALUES(68,29,'68:29','“Slavljen i uzvišen je Gospodar naš!”, rekoše, “mi smo, uistinu, zulumćari bili!”');
INSERT INTO "bs_darassalam" VALUES(68,30,'68:30','I onda počeše jedni druge koriti.');
INSERT INTO "bs_darassalam" VALUES(68,31,'68:31','“Teško nama”, govorili su, “mi smo, zaista, obijesni bili.');
INSERT INTO "bs_darassalam" VALUES(68,32,'68:32','Gospodar naš nam može bolju od nje dati, samo od Gospodara našeg mi se nadamo naknadi!”');
INSERT INTO "bs_darassalam" VALUES(68,33,'68:33','Eto, takva je bila patnja, a na ahiretu je patnja još veća, kad bi samo oni znali!');
INSERT INTO "bs_darassalam" VALUES(68,34,'68:34','Za one koji se budu bojali Allaha bit će, zaista, dženneti uživanja uGospodara njihova,');
INSERT INTO "bs_darassalam" VALUES(68,35,'68:35','zar ćemo muslimane sa prestupnicima izjednačiti?!');
INSERT INTO "bs_darassalam" VALUES(68,36,'68:36','Šta vam je, kako rasuđujete?');
INSERT INTO "bs_darassalam" VALUES(68,37,'68:37','Ili, možda, imate Knjigu, pa u njoj čitate');
INSERT INTO "bs_darassalam" VALUES(68,38,'68:38','da ćete imati ono što vi izaberete?!');
INSERT INTO "bs_darassalam" VALUES(68,39,'68:39','Ili, možda, imate od Nas zakletve koje će do Kijametskog dana vrijediti da ćete ono što vi prosudite imati?!');
INSERT INTO "bs_darassalam" VALUES(68,40,'68:40','Upitaj ih ko je od njih jamac za to.');
INSERT INTO "bs_darassalam" VALUES(68,41,'68:41','Ili, imaju li oni saučesnike?! Pa neka saučesnike svoje dovedu ako govore istinu!');
INSERT INTO "bs_darassalam" VALUES(68,42,'68:42','Na Dan kada se potkoljenica otkrije i kada budu pozvani da licem na tlo padnu, pa ne budu mogli.');
INSERT INTO "bs_darassalam" VALUES(68,43,'68:43','Oborenih pogleda svojih, poklopit će ih poniženje! A bili su pozvani dalicem na tlo padaju dok su zdravi bili.');
INSERT INTO "bs_darassalam" VALUES(68,44,'68:44','Pa, ostavi Mene i one koji ovaj Govor poriču, Mi ćemo ih postepeno,odakle se i ne nadaju, patnji približavati');
INSERT INTO "bs_darassalam" VALUES(68,45,'68:45','i vremena ću im Ja davati, a obmana moja je, zaista, čvrsta!');
INSERT INTO "bs_darassalam" VALUES(68,46,'68:46','Ili ti tražiš od njih nagradu, pa su nametom opterećeni?!');
INSERT INTO "bs_darassalam" VALUES(68,47,'68:47','Ili je kod njih znanje o onome što je čulima nedokučivo, pa oni prepisuju?!');
INSERT INTO "bs_darassalam" VALUES(68,48,'68:48','Ti strpljivo čekaj presudu Gospodara svoga i ne budi kao onaj što u kitu bi, kad je zavapio, a ogorčen je bio,');
INSERT INTO "bs_darassalam" VALUES(68,49,'68:49','i da ga nije stigla Allahova milost, na pusto mjesto bi izbačen bio i prijekor bi zaslužio,');
INSERT INTO "bs_darassalam" VALUES(68,50,'68:50','ali, Gospodar njegov ga je odabrao i učinio ga jednim od onih koji su dobri.');
INSERT INTO "bs_darassalam" VALUES(68,51,'68:51','Gotovo da te nevjernici pogledima svojim obore kad Opomenu slušajugovoreći: “On je, uistinu, lud!”');
INSERT INTO "bs_darassalam" VALUES(68,52,'68:52','A on – Kur’an je samo svijetu cijelom opomena!');
INSERT INTO "bs_darassalam" VALUES(69,1,'69:1','Čas obistinjenja');
INSERT INTO "bs_darassalam" VALUES(69,2,'69:2','šta je Čas obistinjenja?');
INSERT INTO "bs_darassalam" VALUES(69,3,'69:3','I šta ti znaš šta je Čas obistinjenja?!');
INSERT INTO "bs_darassalam" VALUES(69,4,'69:4','Semud i Ad zastrašujući Udar su poricali,');
INSERT INTO "bs_darassalam" VALUES(69,5,'69:5','pa je Semud uništen povikom strahovitim');
INSERT INTO "bs_darassalam" VALUES(69,6,'69:6','a Ad uništen vjetrom ledenim, silovitim.');
INSERT INTO "bs_darassalam" VALUES(69,7,'69:7','Njemu je On vlast nad njima sedam noći i osam dana uzastopnih bioprepustio, pa si u njima ljude povaljane kao šuplja palmina debla vidio,');
INSERT INTO "bs_darassalam" VALUES(69,8,'69:8','i vidiš li da je iko od njih ostao?');
INSERT INTO "bs_darassalam" VALUES(69,9,'69:9','A faraon, i oni prije njega i prevrnuta naselja grijehe činiše,');
INSERT INTO "bs_darassalam" VALUES(69,10,'69:10','pa su bili neposlušni poslaniku Gospodara svoga, pa ih je On dohvatiožestokom kaznom.');
INSERT INTO "bs_darassalam" VALUES(69,11,'69:11','Mi smo vas, kad je voda preplavila sve, u lađi nosili,');
INSERT INTO "bs_darassalam" VALUES(69,12,'69:12','da vam je poukom učinimo i da to od zaborava sačuva uho koje pamti.');
INSERT INTO "bs_darassalam" VALUES(69,13,'69:13','A kad jednom u rog bude puhnuto,');
INSERT INTO "bs_darassalam" VALUES(69,14,'69:14','pa Zemlja i brda budu dignuta i od jednog udara zdrobljena,');
INSERT INTO "bs_darassalam" VALUES(69,15,'69:15','taj dan će se Događaj strahoviti dogoditi,');
INSERT INTO "bs_darassalam" VALUES(69,16,'69:16','i nebo će se razdvojiti – tada će labavo biti');
INSERT INTO "bs_darassalam" VALUES(69,17,'69:17','I meleci će na krajevima njegovim stajati, a Arš Gospodara tvoga će taj dan iznad njih osmerica držati');
INSERT INTO "bs_darassalam" VALUES(69,18,'69:18','Taj dan izloženi bit ćete i nijedna tajna vaša neće skrivena ostati.');
INSERT INTO "bs_darassalam" VALUES(69,19,'69:19','Onaj kome se knjiga njegova u desnu ruku njegovu da, reći će: “Evo vam, čitajte knjigu moju,');
INSERT INTO "bs_darassalam" VALUES(69,20,'69:20','ja sam, zbilja, i mislio da ću račun svoj polagati.”');
INSERT INTO "bs_darassalam" VALUES(69,21,'69:21','I on će biti u životu zadovoljan,');
INSERT INTO "bs_darassalam" VALUES(69,22,'69:22','u Džennetu visokom,');
INSERT INTO "bs_darassalam" VALUES(69,23,'69:23','čiji će plodovi nadohvat ruke biti.');
INSERT INTO "bs_darassalam" VALUES(69,24,'69:24','“Jedite i pijte radosni, za ono što ste u danima minulim zaradili!”');
INSERT INTO "bs_darassalam" VALUES(69,25,'69:25','A onaj kome se knjiga da u lijevu ruku njegovu, reći će: “Kamo sreće da mi knjiga moja ni data nije');
INSERT INTO "bs_darassalam" VALUES(69,26,'69:26','i da ni saznao nisam za obračun svoj!');
INSERT INTO "bs_darassalam" VALUES(69,27,'69:27','Kamo sreće da me je smrt dokrajčila,');
INSERT INTO "bs_darassalam" VALUES(69,28,'69:28','imetak moj mi nije od koristi,');
INSERT INTO "bs_darassalam" VALUES(69,29,'69:29','snage moje nema više!”');
INSERT INTO "bs_darassalam" VALUES(69,30,'69:30','“Zgrabite ga i u okove okujte,');
INSERT INTO "bs_darassalam" VALUES(69,31,'69:31','zatim ga samo u vatri pržite,');
INSERT INTO "bs_darassalam" VALUES(69,32,'69:32','a onda ga u sindžire sedamdeset lakata duge vežite;');
INSERT INTO "bs_darassalam" VALUES(69,33,'69:33','zaista, on u Allaha Velikog nije vjerovao');
INSERT INTO "bs_darassalam" VALUES(69,34,'69:34','i da se nahrani nevoljnik nije podsticao,');
INSERT INTO "bs_darassalam" VALUES(69,35,'69:35','zato on ovdje danas nema prisna prijatelja');
INSERT INTO "bs_darassalam" VALUES(69,36,'69:36','ni drugog jela osim smrdljive kapljevine');
INSERT INTO "bs_darassalam" VALUES(69,37,'69:37','koju će samo nevjernici jesti.”');
INSERT INTO "bs_darassalam" VALUES(69,38,'69:38','A Ja se kunem onim što vidite');
INSERT INTO "bs_darassalam" VALUES(69,39,'69:39','i onim što ne vidite,');
INSERT INTO "bs_darassalam" VALUES(69,40,'69:40','on – Kur’an doista je govor objavljen plemenitom Poslaniku,');
INSERT INTO "bs_darassalam" VALUES(69,41,'69:41','a nije govor nikakva pjesnika – kako vi malo vjerujete!');
INSERT INTO "bs_darassalam" VALUES(69,42,'69:42','I nisu riječi nikakva proroka – kako vi malo razmišljate!');
INSERT INTO "bs_darassalam" VALUES(69,43,'69:43','Objava je on od Gospodara svjetova!');
INSERT INTO "bs_darassalam" VALUES(69,44,'69:44','A da je on o Nama kojekakve riječi iznosio,');
INSERT INTO "bs_darassalam" VALUES(69,45,'69:45','Mi bismo ga desnicom dohvatili,');
INSERT INTO "bs_darassalam" VALUES(69,46,'69:46','a onda mu žilu kucavicu presjekli,');
INSERT INTO "bs_darassalam" VALUES(69,47,'69:47','i niko ga između vas ne bi mogao od toga odbraniti.');
INSERT INTO "bs_darassalam" VALUES(69,48,'69:48','Zaista je on opomena bogobojaznima,');
INSERT INTO "bs_darassalam" VALUES(69,49,'69:49','a Mi, sigurno, znamo da će među vama poricatelja biti.');
INSERT INTO "bs_darassalam" VALUES(69,50,'69:50','I on je, zbilja, jad nevjernicima,');
INSERT INTO "bs_darassalam" VALUES(69,51,'69:51','a on je, doista, Istina sama.');
INSERT INTO "bs_darassalam" VALUES(69,52,'69:52','Zato ti slavi i veličaj ime Gospodara svoga, Veličanstvenoga!');
INSERT INTO "bs_darassalam" VALUES(70,1,'70:1','Neko je hitno zatražio patnju koja će se dogoditi');
INSERT INTO "bs_darassalam" VALUES(70,2,'70:2','nevjernicima – niko nju ne može spriječiti,');
INSERT INTO "bs_darassalam" VALUES(70,3,'70:3','da to Allah, Gospodar uzvišenosti i deredža, ne učini,');
INSERT INTO "bs_darassalam" VALUES(70,4,'70:4','Njemu se penju meleci i Duh u danu koji pedeset hiljada godina traje.');
INSERT INTO "bs_darassalam" VALUES(70,5,'70:5','Ti se lijepo strpi.');
INSERT INTO "bs_darassalam" VALUES(70,6,'70:6','Oni, zaista, misle da je daleko – da se dogoditi neće');
INSERT INTO "bs_darassalam" VALUES(70,7,'70:7','a mi smatramo da je blizu.');
INSERT INTO "bs_darassalam" VALUES(70,8,'70:8','Na dan kada nebo bude kao talog rastopljeni.');
INSERT INTO "bs_darassalam" VALUES(70,9,'70:9','a brda kao vuna raščupana,');
INSERT INTO "bs_darassalam" VALUES(70,10,'70:10','kada bližnji neće bližnjeg ništa pitati,');
INSERT INTO "bs_darassalam" VALUES(70,11,'70:11','iako će jedni druge vidjeti. Prestupnik će htjeti da se od patnje taj daniskupi sinovima svojim,');
INSERT INTO "bs_darassalam" VALUES(70,12,'70:12','i drugom svojom, i bratom svojim,');
INSERT INTO "bs_darassalam" VALUES(70,13,'70:13','i porodicom svojom koja ga štiti,');
INSERT INTO "bs_darassalam" VALUES(70,14,'70:14','i svima ostalima na Zemlji, samo da se izbavi.');
INSERT INTO "bs_darassalam" VALUES(70,15,'70:15','Nikako! Ona je, zaista, buktinja žestoka');
INSERT INTO "bs_darassalam" VALUES(70,16,'70:16','koja će kože guliti,');
INSERT INTO "bs_darassalam" VALUES(70,17,'70:17','zvat će onoga ko se okretao i izbjegavao');
INSERT INTO "bs_darassalam" VALUES(70,18,'70:18','i zgrtao i skrivao');
INSERT INTO "bs_darassalam" VALUES(70,19,'70:19','Čovjek je, uistinu, stvorenje lakomo:');
INSERT INTO "bs_darassalam" VALUES(70,20,'70:20','kada ga nevolja snađe – očajan je,');
INSERT INTO "bs_darassalam" VALUES(70,21,'70:21','a kada ga zadesi dobro, on ga uskraćuje,');
INSERT INTO "bs_darassalam" VALUES(70,22,'70:22','osim klanjača');
INSERT INTO "bs_darassalam" VALUES(70,23,'70:23','koji su u svojim namazima ustrajni');
INSERT INTO "bs_darassalam" VALUES(70,24,'70:24','i oni u čijim imecima ima poznato pravo');
INSERT INTO "bs_darassalam" VALUES(70,25,'70:25','onaj koji prosi i onaj koji je uskraćen a ne prosi');
INSERT INTO "bs_darassalam" VALUES(70,26,'70:26','i oni koji u Sudnji dan vjeruju,');
INSERT INTO "bs_darassalam" VALUES(70,27,'70:27','i oni koji od kazne Allahove strahuju,');
INSERT INTO "bs_darassalam" VALUES(70,28,'70:28','a od kazne Gospodara svoga niko nije siguran,');
INSERT INTO "bs_darassalam" VALUES(70,29,'70:29','i oni koji stidna mjesta svoja čuvaju,');
INSERT INTO "bs_darassalam" VALUES(70,30,'70:30','osim od supruga svojih ili onih koje su u vlasništvu njihovu – oni, doista, prijekor ne zaslužuju.');
INSERT INTO "bs_darassalam" VALUES(70,31,'70:31','Oni koji i pored toga nešto traže, to su baš oni koji prelaze granicu,');
INSERT INTO "bs_darassalam" VALUES(70,32,'70:32','i oni koji na povjerene im amanete i obaveze svoje paze,');
INSERT INTO "bs_darassalam" VALUES(70,33,'70:33','i oni koji iza svojih svjedočenja čvrsto stoje,');
INSERT INTO "bs_darassalam" VALUES(70,34,'70:34','i oni koji na namaze svoje paze,');
INSERT INTO "bs_darassalam" VALUES(70,35,'70:35','oni će u džennetskim baščama biti počašćeni.');
INSERT INTO "bs_darassalam" VALUES(70,36,'70:36','Šta je onima oko tebe koji ne vjeruju pa žure,');
INSERT INTO "bs_darassalam" VALUES(70,37,'70:37','zdesna i slijeva, u gomilama?!');
INSERT INTO "bs_darassalam" VALUES(70,38,'70:38','Zar svaki od njih žudi da u Džennet uživanja uđe?!');
INSERT INTO "bs_darassalam" VALUES(70,39,'70:39','Naprotiv! Mi smo ih stvorili od onoga što oni znaju!');
INSERT INTO "bs_darassalam" VALUES(70,40,'70:40','I Ja se kunem Gospodarom istokâ i zapadâ da ih možemo');
INSERT INTO "bs_darassalam" VALUES(70,41,'70:41','boljim od njih zamijeniti i niko Nas u tome ne može spriječiti.');
INSERT INTO "bs_darassalam" VALUES(70,42,'70:42','Zato ih ostavi neka se u besposlice udubljuju i zabavljaju dok Dan kojim im se prijeti ne dočekaju,');
INSERT INTO "bs_darassalam" VALUES(70,43,'70:43','Dan u kome će žurno, kao da kumirima hrle, iz grobova izići,');
INSERT INTO "bs_darassalam" VALUES(70,44,'70:44','oborenih pogleda i poniženjem ophrvani. To će biti Dan kojim im jeprijećeno!');
INSERT INTO "bs_darassalam" VALUES(71,1,'71:1','Mi smo poslali Nuha narodu njegovu: “Upozoravaj narod svoj prije nego što ga stigne patnja bolna!”');
INSERT INTO "bs_darassalam" VALUES(71,2,'71:2','“O narode moj”, govorio je on, “ja sam vam, zbilja, upozoritelj jasni!');
INSERT INTO "bs_darassalam" VALUES(71,3,'71:3','Allahu ibadet činite, Njega se bojte i meni poslušni budite.');
INSERT INTO "bs_darassalam" VALUES(71,4,'71:4','On će vam grijehe vaše oprostiti i u životu vas do Određenog časa ostaviti, a kada Allahov određeni čas dođe, zaista se neće, neka znate, odgoditi.”');
INSERT INTO "bs_darassalam" VALUES(71,5,'71:5','On reče: “Gospodaru moj, ja sam narod svoj i noću i danju, doista, pozivao,');
INSERT INTO "bs_darassalam" VALUES(71,6,'71:6','ali ga je pozivanje moje još više udaljilo.');
INSERT INTO "bs_darassalam" VALUES(71,7,'71:7','I kad god sam ih pozivao da im oprostiš, prste su svoje u uši stavljali ihaljinama svojim se pokrivali – bili su uporni i pretjerano oholi.');
INSERT INTO "bs_darassalam" VALUES(71,8,'71:8','Zatim sam ih ja otvoreno pozivao,');
INSERT INTO "bs_darassalam" VALUES(71,9,'71:9','onda sam im javno objavljivao i u povjerenju im šaputao,');
INSERT INTO "bs_darassalam" VALUES(71,10,'71:10','i govorio: ‘Tražite od Gospodara svoga oprost. On, doista, mnogo prašta;');
INSERT INTO "bs_darassalam" VALUES(71,11,'71:11','On će vam kišu obilatu slati');
INSERT INTO "bs_darassalam" VALUES(71,12,'71:12','i mnogo vam imanja i sinova dati. I dat će vam bašče, a i rijeke će vam dati.');
INSERT INTO "bs_darassalam" VALUES(71,13,'71:13','Šta vam je, pa se Allahove veličine ne bojite');
INSERT INTO "bs_darassalam" VALUES(71,14,'71:14','a On vas postepeno stvara?!');
INSERT INTO "bs_darassalam" VALUES(71,15,'71:15','Zar ne vidite kako je Allah sedam nebesa, jedno iznad drugog, stvorio');
INSERT INTO "bs_darassalam" VALUES(71,16,'71:16','i na njima Mjesec svijetlim dao, a Sunce svjetiljkom učinio?!');
INSERT INTO "bs_darassalam" VALUES(71,17,'71:17','Allah vas od zemlje poput bilja stvara,');
INSERT INTO "bs_darassalam" VALUES(71,18,'71:18','zatim vas u nju vraća i iz nje će vas, sigurno, izvesti.');
INSERT INTO "bs_darassalam" VALUES(71,19,'71:19','Allah vam je Zemlju učinio ravnom');
INSERT INTO "bs_darassalam" VALUES(71,20,'71:20','da biste po njoj hodili putevima prostranim.’”');
INSERT INTO "bs_darassalam" VALUES(71,21,'71:21','Nuh reče: “Gospodaru moj, oni me ne slušaju i povode se za onima čija bogatstva i djeca samo njihovu propast uvećavaju');
INSERT INTO "bs_darassalam" VALUES(71,22,'71:22','i spletke velike snuju.');
INSERT INTO "bs_darassalam" VALUES(71,23,'71:23','I govore: ‘Nikako božanstva svoja ne ostavljajte, i nikako ni Vedda, niSuvaa, a ni Jegusa, ni Jeuka, ni Nesra ne napuštajte!’');
INSERT INTO "bs_darassalam" VALUES(71,24,'71:24','a već su mnoge u zabludu doveli, pa Ti zulumćarima samo zabludupovećaj!”');
INSERT INTO "bs_darassalam" VALUES(71,25,'71:25','I oni su zbog grijeha svojih potopljeni i u Vatru će biti uvedeni i nikogasebi, mimo Allaha, kao pomagače neće naći.');
INSERT INTO "bs_darassalam" VALUES(71,26,'71:26','I Nuh reče: “Gospodaru moj, ne ostavi na Zemlji nijednog nevjernika,');
INSERT INTO "bs_darassalam" VALUES(71,27,'71:27','jer ako ih ostaviš, oni će robove Tvoje u zabludu zavoditi i samo ćegriješnika i nevjernika rađati!');
INSERT INTO "bs_darassalam" VALUES(71,28,'71:28','Gospodaru moj, oprosti meni, i roditeljima mojim, i onome koji kaovjernik u dom moj uđe, i vjernicima i vjernicama, a zulumćarima samopropast povećaj!”');
INSERT INTO "bs_darassalam" VALUES(72,1,'72:1','Reci: “Meni je objavljeno da je nekoliko džina prisluškivalo i reklo: ‘Mismo, doista, Kur’an divni slušali;');
INSERT INTO "bs_darassalam" VALUES(72,2,'72:2','koji na ono što je ispravno upućuje – i mi smo u njega povjerovali i višenikoga nećemo Gospodaru našem u obožavanju pridruživati,');
INSERT INTO "bs_darassalam" VALUES(72,3,'72:3','a On – uzvišena je moć Gospodara našeg – nije uzeo Sebi ni druge nidjeteta!');
INSERT INTO "bs_darassalam" VALUES(72,4,'72:4','Jedan naš maloumnik o Allahu je veliku laž govorio,');
INSERT INTO "bs_darassalam" VALUES(72,5,'72:5','a mi smo mislili da ni ljudi ni džini o Allahu laži ne govore;');
INSERT INTO "bs_darassalam" VALUES(72,6,'72:6','i bilo je ljudi koji su zaštitu od džina tražili, pa su im još više strah povećali;');
INSERT INTO "bs_darassalam" VALUES(72,7,'72:7','i oni misle, kao što i vi mislite, da Allah nikoga neće oživjeti.’');
INSERT INTO "bs_darassalam" VALUES(72,8,'72:8','I mi smo nebo izvidjeli i utvrdili smo da je moćnih čuvara i plamtećihsvjetlica puno;');
INSERT INTO "bs_darassalam" VALUES(72,9,'72:9','i posjedali bismo po njemu na raznim mjestima da bismo šta čuli, ali ćeonaj, ko sada prisluškuje, na plamteću svjetlicu koja vreba naići;');
INSERT INTO "bs_darassalam" VALUES(72,10,'72:10','i mi ne znamo da li se onima na Zemlji želi zlo ili im Gospodar njihov želi dobro.');
INSERT INTO "bs_darassalam" VALUES(72,11,'72:11','A među nama ima i dobrih i onih koji to nisu, ima nas vrsta različitih;');
INSERT INTO "bs_darassalam" VALUES(72,12,'72:12','i mi znamo da se ne možemo suprotstaviti Allahu na Zemlji, niti Mu umaći.');
INSERT INTO "bs_darassalam" VALUES(72,13,'72:13',' ̋I mi smo, čim smo Uputu – Kur’an čuli, u njega povjerovali; a ko uGospodara svoga vjeruje, ni umanjivanja dobrih, ni dodavanja loših djela ne treba da se boji;');
INSERT INTO "bs_darassalam" VALUES(72,14,'72:14','i ima nas muslimana, a ima nas i zalutalih, a oni koji islam prihvate, ono što je ispravno odabrali su,');
INSERT INTO "bs_darassalam" VALUES(72,15,'72:15','a oni koji skrenuli su, u Džehennemu gorivo bit će.”');
INSERT INTO "bs_darassalam" VALUES(72,16,'72:16','A da su ustrajali na putu Istine, Mi bismo ih vodom obilnom pojili,');
INSERT INTO "bs_darassalam" VALUES(72,17,'72:17','da bismo ih time na kušnju stavili; a onog ko se okrene od OpomeneGospodara svoga – On će u patnju tešku uvesti.');
INSERT INTO "bs_darassalam" VALUES(72,18,'72:18','Mesdžidi jesu Allaha radi, i ne molite, pored Allaha, nikoga!');
INSERT INTO "bs_darassalam" VALUES(72,19,'72:19','A kada je Allahov rob ustao zazivajući Ga, oni su se skoro svi na njega u gomilama okomili');
INSERT INTO "bs_darassalam" VALUES(72,20,'72:20','Reci: “Ja se samo Gospodaru svome molim i nikoga Njemu u obožavanju ne pridružujem.”');
INSERT INTO "bs_darassalam" VALUES(72,21,'72:21','Reci: “Ja nisam u stanju od vas kakvu štetu otkloniti niti nekom od vasneku korist pribaviti.”');
INSERT INTO "bs_darassalam" VALUES(72,22,'72:22','Reci: “Mene niko od Allaha ne može zaštititi; samo u Njega mogu utočište naći,');
INSERT INTO "bs_darassalam" VALUES(72,23,'72:23','samo mogu oglasiti ono što je od Allaha i poslanice Njegove.” A onogakoji Allahu i Poslaniku Njegovu ne bude poslušan – sigurno čeka vatradžehennemska; u njoj će vječno, zauvijek ostati.');
INSERT INTO "bs_darassalam" VALUES(72,24,'72:24','I kada oni vide ono čime im se prijeti, saznat će ko je slabiji i malobrojniji.');
INSERT INTO "bs_darassalam" VALUES(72,25,'72:25','Reci: “Ja ne znam da li će uskoro biti ono čime vam se prijeti ili je Gospodar moj odredio da će to poslije dugo vremena biti.');
INSERT INTO "bs_darassalam" VALUES(72,26,'72:26','Poznavatelj je On onoga što je čulima nedokučivo i On to ne otkrivanikome,');
INSERT INTO "bs_darassalam" VALUES(72,27,'72:27','osim onome za koga je zadovoljan da poslanik bude; On, zaista, i ispred njega i iza njega postavlja one koji će ga čuvati,');
INSERT INTO "bs_darassalam" VALUES(72,28,'72:28','da bi znao da su poslanice Gospodara svoga dostavili; On je obujmio ono što je u njih i baš sve zbrojio.”');
INSERT INTO "bs_darassalam" VALUES(73,1,'73:1','O ti umotani!');
INSERT INTO "bs_darassalam" VALUES(73,2,'73:2','Probdij noć, osim malog dijela;');
INSERT INTO "bs_darassalam" VALUES(73,3,'73:3','polovinu njezinu ili malo manje od nje;');
INSERT INTO "bs_darassalam" VALUES(73,4,'73:4','ili malo više od nje, i uči Kur’an polagahno.');
INSERT INTO "bs_darassalam" VALUES(73,5,'73:5','Mi ćemo ti, doista, teške riječi slati.');
INSERT INTO "bs_darassalam" VALUES(73,6,'73:6','Ustajanje noću, zaista, jače djeluje i izgovor je razboritiji,');
INSERT INTO "bs_darassalam" VALUES(73,7,'73:7','a ti danju, doista, imaš mnogo posla');
INSERT INTO "bs_darassalam" VALUES(73,8,'73:8','I spominji ime Gospodara svoga i Njemu u potpunosti odan budi,');
INSERT INTO "bs_darassalam" VALUES(73,9,'73:9','On je Gospodar istoka i zapada, nema boga osim Njega, Njega uzmi za zaštitnika!');
INSERT INTO "bs_darassalam" VALUES(73,10,'73:10','I otrpi ono što oni govore i izbjegavaj ih na lijep način,');
INSERT INTO "bs_darassalam" VALUES(73,11,'73:11','a prepusti Meni one koji te u laž ugone, koji raskošno žive, i daj im malo vremena;');
INSERT INTO "bs_darassalam" VALUES(73,12,'73:12','bit će u Nas, doista, okova i ognja,');
INSERT INTO "bs_darassalam" VALUES(73,13,'73:13','i jela koje u grlu zastaje, i patnje nesnosne,');
INSERT INTO "bs_darassalam" VALUES(73,14,'73:14','na Dan kada se Zemlja i planine zatresu i planine mehke pješčane dine postanu.');
INSERT INTO "bs_darassalam" VALUES(73,15,'73:15','Mi smo vam, zaista, poslali Poslanika da bi svjedočio protiv vas, isto onako kao što smo i faraonu poslanika poslali,');
INSERT INTO "bs_darassalam" VALUES(73,16,'73:16','ali faraon nije poslušao poslanika, pa smo ga teškom kaznom kaznili.');
INSERT INTO "bs_darassalam" VALUES(73,17,'73:17','Kako ćete se, ako ostanete nevjernici, sačuvati Dana koji će djecu sijedom učiniti?!');
INSERT INTO "bs_darassalam" VALUES(73,18,'73:18','Nebo će se tada rascijepiti i obećanje Njegovo bit će ispunjeno.');
INSERT INTO "bs_darassalam" VALUES(73,19,'73:19','Ovo je, doista, pouka; i ko hoće, put ka Gospodaru svome prihvatit će!');
INSERT INTO "bs_darassalam" VALUES(73,20,'73:20','Gospodar tvoj, sigurno, zna da ti klanjajući provodiš manje od dvijetrećine noći, polovinu njezinu, ili trećinu njezinu, a i skupina od onih kojisu uz tebe. Allah određuje dužinu noći i dana, On zna da vi to nećete moći izdržati pa vam prašta, a vi iz Kur’ana učite ono što je lahko, On zna da će među vama biti bolesnih, i onih koji će po svijetu putovati i Allahove blagodati tražiti, i onih koji će se na Allahovu putu boriti. Pa učite iz njega ono što je lahko, i namaz obavljajte, i zekat dajite i Allahu lijep zajam dajte! A dobro koje za sebe unaprijed date naći ćete kod Allaha da je još bolje i s nagradom većom. I molite Allaha da vam oprosti. Allah je, uistinu, Onaj Koji oprašta grijehe i milostiv je.');
INSERT INTO "bs_darassalam" VALUES(74,1,'74:1','O ti pokriveni!');
INSERT INTO "bs_darassalam" VALUES(74,2,'74:2','Ustani i upozoravaj,');
INSERT INTO "bs_darassalam" VALUES(74,3,'74:3','i Gospodara svoga veličaj!');
INSERT INTO "bs_darassalam" VALUES(74,4,'74:4','I haljine svoje očisti!');
INSERT INTO "bs_darassalam" VALUES(74,5,'74:5','I kumira se kloni!');
INSERT INTO "bs_darassalam" VALUES(74,6,'74:6','I ne čini dobro tražeći za njega više!');
INSERT INTO "bs_darassalam" VALUES(74,7,'74:7','I radi Gospodara svoga trpi!');
INSERT INTO "bs_darassalam" VALUES(74,8,'74:8','A kada u rog bude puhnuto,');
INSERT INTO "bs_darassalam" VALUES(74,9,'74:9','to će tada biti naporan dan,');
INSERT INTO "bs_darassalam" VALUES(74,10,'74:10','nevjernicima on neće biti lahak.');
INSERT INTO "bs_darassalam" VALUES(74,11,'74:11','Ostavi Mene i onoga koga sam Ja samog stvorio');
INSERT INTO "bs_darassalam" VALUES(74,12,'74:12','i imetak mu ogroman dao,');
INSERT INTO "bs_darassalam" VALUES(74,13,'74:13','i sinove koji su s njim,');
INSERT INTO "bs_darassalam" VALUES(74,14,'74:14','i život mu ugodnim učinio,');
INSERT INTO "bs_darassalam" VALUES(74,15,'74:15','i žudi da mu još uvećam!');
INSERT INTO "bs_darassalam" VALUES(74,16,'74:16','Nikako! On, doista, prkosi ajetima Našim,');
INSERT INTO "bs_darassalam" VALUES(74,17,'74:17','a naprtiću Ja njemu teškoće,');
INSERT INTO "bs_darassalam" VALUES(74,18,'74:18','jer je smišljao i računao,');
INSERT INTO "bs_darassalam" VALUES(74,19,'74:19','i, proklet bio, kako je proračunao,');
INSERT INTO "bs_darassalam" VALUES(74,20,'74:20','i još jednom, proklet bio, kako je proračunao!');
INSERT INTO "bs_darassalam" VALUES(74,21,'74:21','Zatim je sagledao,');
INSERT INTO "bs_darassalam" VALUES(74,22,'74:22','pa se onda smrknuo i namrštio,');
INSERT INTO "bs_darassalam" VALUES(74,23,'74:23','i potom se okrenuo i uzoholio,');
INSERT INTO "bs_darassalam" VALUES(74,24,'74:24','i rekao: “Ovo nije ništa drugo do vradžbina koja se nasljeđuje,');
INSERT INTO "bs_darassalam" VALUES(74,25,'74:25','ovo su samo čovjekove riječi!”');
INSERT INTO "bs_darassalam" VALUES(74,26,'74:26','U Sekar ću Ja njega baciti -');
INSERT INTO "bs_darassalam" VALUES(74,27,'74:27','A znaš li ti šta je Sekar?!');
INSERT INTO "bs_darassalam" VALUES(74,28,'74:28','Ništa on neće poštedjeti,');
INSERT INTO "bs_darassalam" VALUES(74,29,'74:29','kože će pržiti, pa crnim učiniti,');
INSERT INTO "bs_darassalam" VALUES(74,30,'74:30','nad njim su devetnaesterica.');
INSERT INTO "bs_darassalam" VALUES(74,31,'74:31','Mi smo čuvarima Vatre samo meleke postavili i odredili njihov broj kaoiskušenje onima koji ne vjeruju – da se oni kojima je Knjiga data uvjere, i da se onima koji vjeruju vjerovanje poveća, i da oni kojima je Knjiga data i oni koji su vjernici ne sumnjaju, i da oni čija su srca bolesna i oni koji su nevjernici kažu: “Šta je Allah htio ovim primjerom?” Tako Allah kome hoće daje da je u zabludi i na Pravi put upućuje koga hoće. A vojske Gospodara tvoga samo On zna. I ona – Vatra ljudima je samo opomena.');
INSERT INTO "bs_darassalam" VALUES(74,32,'74:32','I tako Mi Mjeseca,');
INSERT INTO "bs_darassalam" VALUES(74,33,'74:33','i noći kada mine,');
INSERT INTO "bs_darassalam" VALUES(74,34,'74:34','i zore kada svane,');
INSERT INTO "bs_darassalam" VALUES(74,35,'74:35','to je, zaista, jedna od najvećih nevolja,');
INSERT INTO "bs_darassalam" VALUES(74,36,'74:36','ljudima je opomena;');
INSERT INTO "bs_darassalam" VALUES(74,37,'74:37','onome između vas koji hoće naprijed ići ili zaostati!');
INSERT INTO "bs_darassalam" VALUES(74,38,'74:38','Svaka duša zalog je onoga što je stekla,');
INSERT INTO "bs_darassalam" VALUES(74,39,'74:39','osim onih na desnoj strani,');
INSERT INTO "bs_darassalam" VALUES(74,40,'74:40','oni će se u džennetskim baščama raspitivati');
INSERT INTO "bs_darassalam" VALUES(74,41,'74:41','o prestupnicima.');
INSERT INTO "bs_darassalam" VALUES(74,42,'74:42','“Šta vas je u Sekar dovelo?”');
INSERT INTO "bs_darassalam" VALUES(74,43,'74:43','“Nismo”, reći će, “bili od onih koji su klanjali');
INSERT INTO "bs_darassalam" VALUES(74,44,'74:44','niti smo siromahe hranili.');
INSERT INTO "bs_darassalam" VALUES(74,45,'74:45','I u besposlice smo se sa besposlenjacima upuštali,');
INSERT INTO "bs_darassalam" VALUES(74,46,'74:46','i Sudnji dan smo poricali');
INSERT INTO "bs_darassalam" VALUES(74,47,'74:47','sve dok nam smrt nije došla.”');
INSERT INTO "bs_darassalam" VALUES(74,48,'74:48','Njima posredovanje posrednika neće koristiti.');
INSERT INTO "bs_darassalam" VALUES(74,49,'74:49','Pa zašto se oni okreću od opomene?');
INSERT INTO "bs_darassalam" VALUES(74,50,'74:50','Kao da su divlji magarci preplašeni');
INSERT INTO "bs_darassalam" VALUES(74,51,'74:51','pobjegli od lavova!');
INSERT INTO "bs_darassalam" VALUES(74,52,'74:52','Naprotiv, svaki bi čovjek među njima htio da mu se daju listovi rašireni.');
INSERT INTO "bs_darassalam" VALUES(74,53,'74:53','Nikada, jer oni se ahireta ne plaše!');
INSERT INTO "bs_darassalam" VALUES(74,54,'74:54','Uistinu! Kur’an je opomena,');
INSERT INTO "bs_darassalam" VALUES(74,55,'74:55','i ko hoće, na umu će ga imati,');
INSERT INTO "bs_darassalam" VALUES(74,56,'74:56','a na umu će ga imati samo ako Allah bude htio, On je jedini dostojan da Ga se boje i dostojan je da oprašta.');
INSERT INTO "bs_darassalam" VALUES(75,1,'75:1','Kunem se Kijametskim danom');
INSERT INTO "bs_darassalam" VALUES(75,2,'75:2','i kunem se dušom koja sebe kori.');
INSERT INTO "bs_darassalam" VALUES(75,3,'75:3','Zar čovjek misli da kosti njegove nećemo sakupiti?!');
INSERT INTO "bs_darassalam" VALUES(75,4,'75:4','Svakako, a Mi možemo izravnati jagodice prstiju njegovih.');
INSERT INTO "bs_darassalam" VALUES(75,5,'75:5','Ali, čovjek hoće da negira ono što pred njim je');
INSERT INTO "bs_darassalam" VALUES(75,6,'75:6','pa pita: “Kada će Kijametski dan biti?”');
INSERT INTO "bs_darassalam" VALUES(75,7,'75:7','Kada se pogled zbuni,');
INSERT INTO "bs_darassalam" VALUES(75,8,'75:8','i Mjesec pomrači');
INSERT INTO "bs_darassalam" VALUES(75,9,'75:9','i Sunce i Mjesec smotaju, pa sjaj izgube,');
INSERT INTO "bs_darassalam" VALUES(75,10,'75:10','taj dan čovjek će povikati: “Gdje da se bježi?”');
INSERT INTO "bs_darassalam" VALUES(75,11,'75:11','Ne, nema pribježišta!');
INSERT INTO "bs_darassalam" VALUES(75,12,'75:12','Taj dan tvome je Gospodaru povratak,');
INSERT INTO "bs_darassalam" VALUES(75,13,'75:13','taj dan čovjek će o onome što je pripremio, a što propustio, obaviještenbiti.');
INSERT INTO "bs_darassalam" VALUES(75,14,'75:14','Štaviše, čovjek sebe dobro zna – sam će protiv sebe svjedok biti,');
INSERT INTO "bs_darassalam" VALUES(75,15,'75:15','makar iznosio svoja opravdanja.');
INSERT INTO "bs_darassalam" VALUES(75,16,'75:16','Ne izgovaraj Kur’an jezikom svojim da bi ga što prije zapamtio,');
INSERT INTO "bs_darassalam" VALUES(75,17,'75:17','Mi smo ga dužni sakupiti da bi ga ti čitao.');
INSERT INTO "bs_darassalam" VALUES(75,18,'75:18','A kada ga čitamo, ti prati čitanje njegovo,');
INSERT INTO "bs_darassalam" VALUES(75,19,'75:19','a poslije, Mi smo ga dužni objasniti.');
INSERT INTO "bs_darassalam" VALUES(75,20,'75:20','Ali, naprotiv, vi ovaj prolazni svijet volite,');
INSERT INTO "bs_darassalam" VALUES(75,21,'75:21','a onaj drugi – ahiret zapostavljate.');
INSERT INTO "bs_darassalam" VALUES(75,22,'75:22','Taj dan neka lica blistava će biti,');
INSERT INTO "bs_darassalam" VALUES(75,23,'75:23','u Gospodara svoga će gledati.');
INSERT INTO "bs_darassalam" VALUES(75,24,'75:24','Taj dan neka lica smrknuta će biti,');
INSERT INTO "bs_darassalam" VALUES(75,25,'75:25','znat će da će ih snaći velika nesreća!');
INSERT INTO "bs_darassalam" VALUES(75,26,'75:26','Pazi, kada duša dopre do ključnih kostiju');
INSERT INTO "bs_darassalam" VALUES(75,27,'75:27','i vikne se: "Ima li vidara?",');
INSERT INTO "bs_darassalam" VALUES(75,28,'75:28','i on se uvjeri da je to čas rastanka');
INSERT INTO "bs_darassalam" VALUES(75,29,'75:29','i noga se uz nogu savije.');
INSERT INTO "bs_darassalam" VALUES(75,30,'75:30','Taj dan će Gospodaru tvome privođenje biti.');
INSERT INTO "bs_darassalam" VALUES(75,31,'75:31','Nije vjerovao i nije klanjao,');
INSERT INTO "bs_darassalam" VALUES(75,32,'75:32','nego je poricao i okretao se,');
INSERT INTO "bs_darassalam" VALUES(75,33,'75:33','a onda je svojim oholo odlazio.');
INSERT INTO "bs_darassalam" VALUES(75,34,'75:34','Teško tebi! Teško tebi!');
INSERT INTO "bs_darassalam" VALUES(75,35,'75:35','I još jednom: Teško tebi! Teško tebi!');
INSERT INTO "bs_darassalam" VALUES(75,36,'75:36','Zar čovjek misli da će zaludu ostavljen biti?!');
INSERT INTO "bs_darassalam" VALUES(75,37,'75:37','Zar nije bio kap sjemena koja se ubaci,');
INSERT INTO "bs_darassalam" VALUES(75,38,'75:38','zatim zakvačak kome On onda razmjer odredi i skladnim mu lik učini,');
INSERT INTO "bs_darassalam" VALUES(75,39,'75:39','i od njega dvije vrste, muškarca i ženu, stvori,');
INSERT INTO "bs_darassalam" VALUES(75,40,'75:40','i zar Taj nije kadar mrtve oživjeti?!');
INSERT INTO "bs_darassalam" VALUES(76,1,'76:1','Ne prôđe li čovjeku dio vremena u kojem nije bio spomena vrijedan?!');
INSERT INTO "bs_darassalam" VALUES(76,2,'76:2','Mi čovjeka od smjese sjemena stvaramo da bismo ga na kušnju stavili i činimo da on čuje i vidi.');
INSERT INTO "bs_darassalam" VALUES(76,3,'76:3','Mi mu na Pravi put ukazujemo, pa ili je zahvalan ili nezahvalan.');
INSERT INTO "bs_darassalam" VALUES(76,4,'76:4','Mi smo za nevjernike okove i sindžire i oganj razbuktali pripremili.');
INSERT INTO "bs_darassalam" VALUES(76,5,'76:5','Čestiti će iz pehara piće kamforom začinjeno piti,');
INSERT INTO "bs_darassalam" VALUES(76,6,'76:6','sa izvora iz kojeg će samo Allahovi robovi piti, i koji će kuda hoće bezmuke razvoditi.');
INSERT INTO "bs_darassalam" VALUES(76,7,'76:7','Oni su zavjet ispunili i plašili se dana čija će kob svuda prisutna biti,');
INSERT INTO "bs_darassalam" VALUES(76,8,'76:8','i hranu su davali, mada su je i sami voljeli, siromahu, siročetu i sužnju.');
INSERT INTO "bs_darassalam" VALUES(76,9,'76:9','“Mi vas samo zarad Allahova Lica hranimo, od vas ni priznanja nizahvalnosti ne tražimo!');
INSERT INTO "bs_darassalam" VALUES(76,10,'76:10','Mi se Gospodara našeg bojimo, na Dan kada će lica smrknuta inamrgođena biti.”');
INSERT INTO "bs_darassalam" VALUES(76,11,'76:11','Njih će Allah strahote taj dan sačuvati i blistavost i radost im darovati,');
INSERT INTO "bs_darassalam" VALUES(76,12,'76:12','i Džennetom i svilom ih za ono što su trpjeli nagraditi.');
INSERT INTO "bs_darassalam" VALUES(76,13,'76:13','Naslonjeni na divane, oni u njemu ni Sunce ni veliku hladnoću neće osjetiti,');
INSERT INTO "bs_darassalam" VALUES(76,14,'76:14','u blizini će im hladovina njegova biti, a plodovi njegovi nadohvatruke će im stajati.');
INSERT INTO "bs_darassalam" VALUES(76,15,'76:15','Bit će usluživani iz srebrenih posuda i čaša koje će prozirne biti,');
INSERT INTO "bs_darassalam" VALUES(76,16,'76:16','od srebra prozirnog napravljene, čiju će veličinu prema željama njihovim odrediti.');
INSERT INTO "bs_darassalam" VALUES(76,17,'76:17','U njemu će se iz čaše piće đumbirom začinjeno piti,');
INSERT INTO "bs_darassalam" VALUES(76,18,'76:18','sa izvora u Džennetu, koji se Selsebil zove.');
INSERT INTO "bs_darassalam" VALUES(76,19,'76:19','Služit će ih vječno mlada posluga; da ih vidiš, pomislio bi da su biserprosuti.');
INSERT INTO "bs_darassalam" VALUES(76,20,'76:20','I kud god pogledaš, vidjet ćeš blagostanje i carstvo prostrano.');
INSERT INTO "bs_darassalam" VALUES(76,21,'76:21','Na njima bit će odijela od tanke zelene svile i od brokata, nakićeninarukvicama od srebra, i dat će im Gospodar njihov da piju čisto piće.');
INSERT INTO "bs_darassalam" VALUES(76,22,'76:22','“To vam je nagrada; vaš trud je dostojan hvale bio!”');
INSERT INTO "bs_darassalam" VALUES(76,23,'76:23','Od vremena do vremena Mi objavljujemo Kur’an tebi,');
INSERT INTO "bs_darassalam" VALUES(76,24,'76:24','Zato, izdrži do odluke Gospodara tvoga, i ne slušaj ni griješnika ninevjernika njihova!');
INSERT INTO "bs_darassalam" VALUES(76,25,'76:25','I spominji ime Gospodara svoga ujutro i predvečer,');
INSERT INTO "bs_darassalam" VALUES(76,26,'76:26','i u jednom dijelu noći Njemu na sedždu padaj i dugo Ga noću veličaj.');
INSERT INTO "bs_darassalam" VALUES(76,27,'76:27','A oni život na prolaznom svijetu vole doista, a ništa ih se ne tiče Dan tegobni koji ih čeka.');
INSERT INTO "bs_darassalam" VALUES(76,28,'76:28','Mi ih čvrstog tijela stvaramo i zglobove im povezujemo, a ako htjednemo, zamijenit ćemo ih njima sličnim.');
INSERT INTO "bs_darassalam" VALUES(76,29,'76:29','Ovo je opomena, pa ko hoće držat će se puta koji Gospodaru njegovu vodi.');
INSERT INTO "bs_darassalam" VALUES(76,30,'76:30','a vi ćete htjeti samo ono što Allah hoće. Allah je, uistinu, Onaj Koji svezna i mudar je.');
INSERT INTO "bs_darassalam" VALUES(76,31,'76:31','On koga hoće uvodi u milost Svoju, a zulumćarima je pripremio velikupatnju.');
INSERT INTO "bs_darassalam" VALUES(77,1,'77:1','Tako mi onih koji se jedan za drugim šalju,');
INSERT INTO "bs_darassalam" VALUES(77,2,'77:2','i onih što pušu žestoko,');
INSERT INTO "bs_darassalam" VALUES(77,3,'77:3','i onih koji rasprostiru');
INSERT INTO "bs_darassalam" VALUES(77,4,'77:4','i onih što razdvajaju');
INSERT INTO "bs_darassalam" VALUES(77,5,'77:5','i onih što Opomenu dostavljaju,');
INSERT INTO "bs_darassalam" VALUES(77,6,'77:6','kao opravdanje ili upozorenje,');
INSERT INTO "bs_darassalam" VALUES(77,7,'77:7','sigurno će biti ono čime vam se prijeti!');
INSERT INTO "bs_darassalam" VALUES(77,8,'77:8','Kada zvijezde sjaj izgube,');
INSERT INTO "bs_darassalam" VALUES(77,9,'77:9','i kada se nebo otvori,');
INSERT INTO "bs_darassalam" VALUES(77,10,'77:10','i kada se planine u prah zdrobe,');
INSERT INTO "bs_darassalam" VALUES(77,11,'77:11','i kada se poslanici u određeno vrijeme okupe.');
INSERT INTO "bs_darassalam" VALUES(77,12,'77:12','Do kojeg je to dana odloženo?');
INSERT INTO "bs_darassalam" VALUES(77,13,'77:13','Do Dana sudnjeg!');
INSERT INTO "bs_darassalam" VALUES(77,14,'77:14','A znaš li ti šta je Dan sudnji?');
INSERT INTO "bs_darassalam" VALUES(77,15,'77:15','Teško toga dana poricateljima!');
INSERT INTO "bs_darassalam" VALUES(77,16,'77:16','Zar Mi nismo drevne narode uništili');
INSERT INTO "bs_darassalam" VALUES(77,17,'77:17','i poslije njih i one koji su za njima dolazili?!');
INSERT INTO "bs_darassalam" VALUES(77,18,'77:18','Tako ćemo sa svim prestupnicima postupiti!');
INSERT INTO "bs_darassalam" VALUES(77,19,'77:19','Teško toga dana poricateljima!');
INSERT INTO "bs_darassalam" VALUES(77,20,'77:20','Zar vas od neznatne tekućine ne stvaramo,');
INSERT INTO "bs_darassalam" VALUES(77,21,'77:21','koju na pouzdano mjesto stavljamo');
INSERT INTO "bs_darassalam" VALUES(77,22,'77:22','do roka određenoga?!');
INSERT INTO "bs_darassalam" VALUES(77,23,'77:23','Tako smo odredili, a divni li smo odreditelji!');
INSERT INTO "bs_darassalam" VALUES(77,24,'77:24','Teško toga dana poricateljima!');
INSERT INTO "bs_darassalam" VALUES(77,25,'77:25','Zar Mi nismo učinili Zemlju sabiralištem');
INSERT INTO "bs_darassalam" VALUES(77,26,'77:26','živih i mrtvih');
INSERT INTO "bs_darassalam" VALUES(77,27,'77:27','i na njoj nepomične visoke planine postavili, i zar vam Mi ne dajemo daslatku vodu pijete?!');
INSERT INTO "bs_darassalam" VALUES(77,28,'77:28','Teško toga dana poricateljima!');
INSERT INTO "bs_darassalam" VALUES(77,29,'77:29','Idite prema onome što ste neistinom smatrali.');
INSERT INTO "bs_darassalam" VALUES(77,30,'77:30','Idite prema dimu u tri prama razdvojenom,');
INSERT INTO "bs_darassalam" VALUES(77,31,'77:31','koji hlada neće davati i koji od plamena neće zaklanjati.');
INSERT INTO "bs_darassalam" VALUES(77,32,'77:32','On će kao kule iskre bacati');
INSERT INTO "bs_darassalam" VALUES(77,33,'77:33','kao da su kamile riđe.');
INSERT INTO "bs_darassalam" VALUES(77,34,'77:34','Teško toga dana poricateljima!');
INSERT INTO "bs_darassalam" VALUES(77,35,'77:35','Dan je to u kojem oni neće ni prozboriti');
INSERT INTO "bs_darassalam" VALUES(77,36,'77:36','i pravdanje im neće dozvoljeno biti.');
INSERT INTO "bs_darassalam" VALUES(77,37,'77:37','Teško toga dana poricateljima!');
INSERT INTO "bs_darassalam" VALUES(77,38,'77:38','To je Dan sudnji, i vas i narode drevne ćemo sakupiti,');
INSERT INTO "bs_darassalam" VALUES(77,39,'77:39','pa ako se lukavstvom kakvim budete znali poslužiti, dovijte se protivMene!');
INSERT INTO "bs_darassalam" VALUES(77,40,'77:40','Teško toga dana poricateljima!');
INSERT INTO "bs_darassalam" VALUES(77,41,'77:41','Oni koji su se Allaha bojali bit će među izvorima, u gustim baščama.');
INSERT INTO "bs_darassalam" VALUES(77,42,'77:42','I među voćem koje budu željeli.');
INSERT INTO "bs_darassalam" VALUES(77,43,'77:43','“Jedite i pijte, prijatno nek vam je, zbog onog što ste radili.”');
INSERT INTO "bs_darassalam" VALUES(77,44,'77:44','Tako ćemo Mi one koji čine dobra djela nagraditi.');
INSERT INTO "bs_darassalam" VALUES(77,45,'77:45','Teško toga dana poricateljima!');
INSERT INTO "bs_darassalam" VALUES(77,46,'77:46','“Jedite i naslađujte se, ali zakratko! – Vi ste, zaista, prestupnici!”');
INSERT INTO "bs_darassalam" VALUES(77,47,'77:47','Teško toga dana poricateljima!');
INSERT INTO "bs_darassalam" VALUES(77,48,'77:48','A kad im se kaže: “Namaz obavljajte!”, oni namaz ne obavljaju.');
INSERT INTO "bs_darassalam" VALUES(77,49,'77:49','Teško toga dana poricateljima!');
INSERT INTO "bs_darassalam" VALUES(77,50,'77:50','Pa u koji će govor, ako ne u Kur’an, vjerovati?!');
INSERT INTO "bs_darassalam" VALUES(78,1,'78:1','O čemu oni jedni druge pitaju?!');
INSERT INTO "bs_darassalam" VALUES(78,2,'78:2','O Vijesti velikoj,');
INSERT INTO "bs_darassalam" VALUES(78,3,'78:3','o kojoj se oni razilaze.');
INSERT INTO "bs_darassalam" VALUES(78,4,'78:4','Tako ne treba, oni će saznati sigurno!');
INSERT INTO "bs_darassalam" VALUES(78,5,'78:5','I opet, tako ne treba, oni će saznati sigurno!');
INSERT INTO "bs_darassalam" VALUES(78,6,'78:6','Zar Zemlju posteljom nismo učinili,');
INSERT INTO "bs_darassalam" VALUES(78,7,'78:7','i planine kao klinove,');
INSERT INTO "bs_darassalam" VALUES(78,8,'78:8','i vas kao parove stvorili,');
INSERT INTO "bs_darassalam" VALUES(78,9,'78:9','i vaš san smirajem učinili,');
INSERT INTO "bs_darassalam" VALUES(78,10,'78:10','i noć odjećom učinili,');
INSERT INTO "bs_darassalam" VALUES(78,11,'78:11','i dan za privređivanje odredili,');
INSERT INTO "bs_darassalam" VALUES(78,12,'78:12','i iznad vas sedam čvrstih sazdali,');
INSERT INTO "bs_darassalam" VALUES(78,13,'78:13','i svjetiljku plamteću postavili?!');
INSERT INTO "bs_darassalam" VALUES(78,14,'78:14','Mi iz kišnih oblaka vodu obilnu spuštamo');
INSERT INTO "bs_darassalam" VALUES(78,15,'78:15','da njome žito i bilje izvedemo,');
INSERT INTO "bs_darassalam" VALUES(78,16,'78:16','i bašče guste.');
INSERT INTO "bs_darassalam" VALUES(78,17,'78:17','Dan suda je, zaista, već određen,');
INSERT INTO "bs_darassalam" VALUES(78,18,'78:18','Dan kada će u rog biti puhnuto, pa ćete vi, sve skupina po skupina,dolaziti,');
INSERT INTO "bs_darassalam" VALUES(78,19,'78:19','i nebo će biti otvoreno i mnogo će kapija imati,');
INSERT INTO "bs_darassalam" VALUES(78,20,'78:20','i planine će pokrenute biti i priviđenje će postati.');
INSERT INTO "bs_darassalam" VALUES(78,21,'78:21','Džehennem će, doista, zasjeda biti,');
INSERT INTO "bs_darassalam" VALUES(78,22,'78:22','obijesnima mjesto povratka,');
INSERT INTO "bs_darassalam" VALUES(78,23,'78:23','u kojem će zauvijek ostati,');
INSERT INTO "bs_darassalam" VALUES(78,24,'78:24','u njemu nikakve svježine neće osjetiti, ni pića okusiti,');
INSERT INTO "bs_darassalam" VALUES(78,25,'78:25','osim vrele vode i kapljevine');
INSERT INTO "bs_darassalam" VALUES(78,26,'78:26','kazne prikladne.');
INSERT INTO "bs_darassalam" VALUES(78,27,'78:27','Zaista se oni nisu nadali polaganju računa');
INSERT INTO "bs_darassalam" VALUES(78,28,'78:28','i potpuno lažnim smatrali su Naše ajete i znakove,');
INSERT INTO "bs_darassalam" VALUES(78,29,'78:29','a Mi smo sve pobrojali i zapisali.');
INSERT INTO "bs_darassalam" VALUES(78,30,'78:30','“Pa zato kušajte, a Mi ćemo vam patnju samo povećati!”');
INSERT INTO "bs_darassalam" VALUES(78,31,'78:31','Zaista, bogobojaznima mjesto spasa i uživanja pripada:');
INSERT INTO "bs_darassalam" VALUES(78,32,'78:32','bašče i vinogradi,');
INSERT INTO "bs_darassalam" VALUES(78,33,'78:33','i djevojke mlade, godina istih,');
INSERT INTO "bs_darassalam" VALUES(78,34,'78:34','i pehari puni.');
INSERT INTO "bs_darassalam" VALUES(78,35,'78:35','Tamo prazne besjede i laži neće slušati,');
INSERT INTO "bs_darassalam" VALUES(78,36,'78:36','to im je nagrada od Gospodara tvoga, dar dovoljni.');
INSERT INTO "bs_darassalam" VALUES(78,37,'78:37','Od Gospodara nebesa i Zemlje i onoga što je između njih, Svemilosnoga, Kome neće moći zboriti');
INSERT INTO "bs_darassalam" VALUES(78,38,'78:38','Na Dan kada Duh i meleci budu u redove poredani, oni neće govoriti.Govorit će samo onaj kome Svemilosni dozvoli, a istinu će reći.');
INSERT INTO "bs_darassalam" VALUES(78,39,'78:39','To je Istiniti dan, pa ko hoće, povratak Gospodaru svome prihvatit će.');
INSERT INTO "bs_darassalam" VALUES(78,40,'78:40','Mi vas, zaista, na patnju blisku upozoravamo, na Dan u kojem će čovjek djela ruku svojih vidjeti, a nevjernik uzviknuti: “Kamo sreće da sam prašina bio!”');
INSERT INTO "bs_darassalam" VALUES(79,1,'79:1','Tako Mi onih koji čupaju grubo,');
INSERT INTO "bs_darassalam" VALUES(79,2,'79:2','i onih koji vade blago,');
INSERT INTO "bs_darassalam" VALUES(79,3,'79:3','i onih koji plove brzo,');
INSERT INTO "bs_darassalam" VALUES(79,4,'79:4','i onih koji baš prednjače,');
INSERT INTO "bs_darassalam" VALUES(79,5,'79:5','i onih koji odredbe uređuju,');
INSERT INTO "bs_darassalam" VALUES(79,6,'79:6','na Dan kada ono što potresa potrese,');
INSERT INTO "bs_darassalam" VALUES(79,7,'79:7','za kojim će doći sljedeći,');
INSERT INTO "bs_darassalam" VALUES(79,8,'79:8','srca taj dan bit će uplašena,');
INSERT INTO "bs_darassalam" VALUES(79,9,'79:9','a pogledi njihovi ponizni.');
INSERT INTO "bs_darassalam" VALUES(79,10,'79:10','Govore oni: “Zar ćemo, zaista, u kaburovima biti vraćeni u život?');
INSERT INTO "bs_darassalam" VALUES(79,11,'79:11','Zar kad truhle kosti postanemo?”');
INSERT INTO "bs_darassalam" VALUES(79,12,'79:12','I još kažu: “E, to je, dakle, štetan povratak.”');
INSERT INTO "bs_darassalam" VALUES(79,13,'79:13','A bit će to samo naredba jedna,');
INSERT INTO "bs_darassalam" VALUES(79,14,'79:14','a oni već na površini Zemlje');
INSERT INTO "bs_darassalam" VALUES(79,15,'79:15','Da li je doprla do tebe vijest o Musau,');
INSERT INTO "bs_darassalam" VALUES(79,16,'79:16','kad ga je Gospodar njegov u blagoslovljenoj, čistoj dolini Tuva zovnuo?');
INSERT INTO "bs_darassalam" VALUES(79,17,'79:17','“Idi faraonu, on se, doista, osilio,');
INSERT INTO "bs_darassalam" VALUES(79,18,'79:18','i reci: ‘Da li bi ti da se očistiš');
INSERT INTO "bs_darassalam" VALUES(79,19,'79:19','da te Gospodaru tvome uputim, pa da Ga se bojiš?’”');
INSERT INTO "bs_darassalam" VALUES(79,20,'79:20','I onda mu je najveći znak pokazao,');
INSERT INTO "bs_darassalam" VALUES(79,21,'79:21','ali je on porekao i nije poslušao,');
INSERT INTO "bs_darassalam" VALUES(79,22,'79:22','već se okrenuo i potrudio');
INSERT INTO "bs_darassalam" VALUES(79,23,'79:23','i sabrao i povikao:');
INSERT INTO "bs_darassalam" VALUES(79,24,'79:24','“Ja sam gospodar vaš najveći!”, on je rekao');
INSERT INTO "bs_darassalam" VALUES(79,25,'79:25','I Allah ga je na drugom, a i na ovom svijetu kaznio.');
INSERT INTO "bs_darassalam" VALUES(79,26,'79:26','U tome ima pouka za svakoga koji se bude Allaha bojao!');
INSERT INTO "bs_darassalam" VALUES(79,27,'79:27','A šta je teže: vas ili nebo stvoriti? On ga je sazdao,');
INSERT INTO "bs_darassalam" VALUES(79,28,'79:28','svod njegov visoko digao i usavršio,');
INSERT INTO "bs_darassalam" VALUES(79,29,'79:29','noći njegove mračnim, a dane svijetlim učinio.');
INSERT INTO "bs_darassalam" VALUES(79,30,'79:30','Poslije toga je Zemlju rasprostro,');
INSERT INTO "bs_darassalam" VALUES(79,31,'79:31','iz nje je vodu i pašnjake izveo,');
INSERT INTO "bs_darassalam" VALUES(79,32,'79:32','i planine nepomičnim učinio,');
INSERT INTO "bs_darassalam" VALUES(79,33,'79:33','na uživanje vama i stoci vašoj.');
INSERT INTO "bs_darassalam" VALUES(79,34,'79:34','A kad dođe Nevolja najveća,');
INSERT INTO "bs_darassalam" VALUES(79,35,'79:35','tada će se čovjek prisjetiti onoga što je radio,');
INSERT INTO "bs_darassalam" VALUES(79,36,'79:36','i Oganj će svakome ko vidi biti ukazan.');
INSERT INTO "bs_darassalam" VALUES(79,37,'79:37','Pa, onom ko je osion bio');
INSERT INTO "bs_darassalam" VALUES(79,38,'79:38','i život dunjalučki više volio,');
INSERT INTO "bs_darassalam" VALUES(79,39,'79:39','Oganj će baš prebivalište biti.');
INSERT INTO "bs_darassalam" VALUES(79,40,'79:40','A onom ko je od stajanja pred Gospodarom svojim strepio i dušu odprohtjeva uzdržao,');
INSERT INTO "bs_darassalam" VALUES(79,41,'79:41','Džennet će baš boravište biti.');
INSERT INTO "bs_darassalam" VALUES(79,42,'79:42','Pitaju te o Času kijameta: “Kada će se dogoditi?”');
INSERT INTO "bs_darassalam" VALUES(79,43,'79:43','Šta ti o spomenu njegovom znaš!?');
INSERT INTO "bs_darassalam" VALUES(79,44,'79:44','Znanje o njemu kod Gospodara tvoga skončava.');
INSERT INTO "bs_darassalam" VALUES(79,45,'79:45','Tvoje upozorenje na njega prihvata samo onaj koji Ga se boji,');
INSERT INTO "bs_darassalam" VALUES(79,46,'79:46','a njima će se učiniti, Dan taj, kada ga dožive, da su samo jednoposlijepodne ili jedno jutro na dunjaluku ostali.');
INSERT INTO "bs_darassalam" VALUES(80,1,'80:1','Namrštio se i okrenuo');
INSERT INTO "bs_darassalam" VALUES(80,2,'80:2','zato što je slijepi čovjek njemu prišao.');
INSERT INTO "bs_darassalam" VALUES(80,3,'80:3','A šta ti znaš, možda će se on očistiti,');
INSERT INTO "bs_darassalam" VALUES(80,4,'80:4','ili opomenuti, pa da mu opomena koristi.');
INSERT INTO "bs_darassalam" VALUES(80,5,'80:5','A što se tiče onoga koji je bogat,');
INSERT INTO "bs_darassalam" VALUES(80,6,'80:6','ti se njemu okrećeš,');
INSERT INTO "bs_darassalam" VALUES(80,7,'80:7','a ti nisi odgovoran ako se on ne očisti.');
INSERT INTO "bs_darassalam" VALUES(80,8,'80:8','A što se tiče onoga koji ti je došao žureći,');
INSERT INTO "bs_darassalam" VALUES(80,9,'80:9','a on se boji,');
INSERT INTO "bs_darassalam" VALUES(80,10,'80:10','ti njega zanemaruješ.');
INSERT INTO "bs_darassalam" VALUES(80,11,'80:11','Ne čini tako! Zbilja je ovo opomena,');
INSERT INTO "bs_darassalam" VALUES(80,12,'80:12','pa ko hoće, imat će Ga na umu');
INSERT INTO "bs_darassalam" VALUES(80,13,'80:13','na listovima je cijenjenim');
INSERT INTO "bs_darassalam" VALUES(80,14,'80:14','uzvišenim, čistim,');
INSERT INTO "bs_darassalam" VALUES(80,15,'80:15','u rukama izaslanika');
INSERT INTO "bs_darassalam" VALUES(80,16,'80:16','časnih, čestitih.');
INSERT INTO "bs_darassalam" VALUES(80,17,'80:17','Proklet neka je čovjek! Koliko je samo njegovo poricanje!');
INSERT INTO "bs_darassalam" VALUES(80,18,'80:18','Od čega ga On stvara?');
INSERT INTO "bs_darassalam" VALUES(80,19,'80:19','Od kapi sjemena ga stvara, pa mu određuje.');
INSERT INTO "bs_darassalam" VALUES(80,20,'80:20','I put mu dostupnim učini,');
INSERT INTO "bs_darassalam" VALUES(80,21,'80:21','zatim mu život oduzme i učini da bude sahranjen,');
INSERT INTO "bs_darassalam" VALUES(80,22,'80:22','i poslije će ga, kada On bude htio, oživjeti.');
INSERT INTO "bs_darassalam" VALUES(80,23,'80:23','Uistinu! On još nije ispunio ono što mu je On naredio!');
INSERT INTO "bs_darassalam" VALUES(80,24,'80:24','Neka čovjek pogleda u hranu svoju.');
INSERT INTO "bs_darassalam" VALUES(80,25,'80:25','Mi obilnu kišu prolijevamo,');
INSERT INTO "bs_darassalam" VALUES(80,26,'80:26','zatim zemlju pukotinama cijepamo');
INSERT INTO "bs_darassalam" VALUES(80,27,'80:27','i činimo da iz nje žito izrasta,');
INSERT INTO "bs_darassalam" VALUES(80,28,'80:28','i grožđe i povrće,');
INSERT INTO "bs_darassalam" VALUES(80,29,'80:29','i masline i palme,');
INSERT INTO "bs_darassalam" VALUES(80,30,'80:30','i bašče guste,');
INSERT INTO "bs_darassalam" VALUES(80,31,'80:31','i voće i trave,');
INSERT INTO "bs_darassalam" VALUES(80,32,'80:32','na uživanje vama i stoci vašoj.');
INSERT INTO "bs_darassalam" VALUES(80,33,'80:33','A kad dođe glas zaglušujući,');
INSERT INTO "bs_darassalam" VALUES(80,34,'80:34','na Dan kada će čovjek od brata svoga pobjeći,');
INSERT INTO "bs_darassalam" VALUES(80,35,'80:35','i od majke svoje, i od oca svoga,');
INSERT INTO "bs_darassalam" VALUES(80,36,'80:36','i od druge svoje i sinova svojih,');
INSERT INTO "bs_darassalam" VALUES(80,37,'80:37','Dan taj svaki čovjek će se samo o sebi brinuti,');
INSERT INTO "bs_darassalam" VALUES(80,38,'80:38','neka lica Dan taj bit će blistava,');
INSERT INTO "bs_darassalam" VALUES(80,39,'80:39','nasmijana, radosna,');
INSERT INTO "bs_darassalam" VALUES(80,40,'80:40','a na nekim licima Dan taj bit će prašina,');
INSERT INTO "bs_darassalam" VALUES(80,41,'80:41','tama će ih prekrivati,');
INSERT INTO "bs_darassalam" VALUES(80,42,'80:42','to će, zbilja, nevjernici, razvratnici biti.');
INSERT INTO "bs_darassalam" VALUES(81,1,'81:1','Kada Sunce bude skupljeno i izgubi sjaj,');
INSERT INTO "bs_darassalam" VALUES(81,2,'81:2','i kada zvijezde raštrkane popadaju,');
INSERT INTO "bs_darassalam" VALUES(81,3,'81:3','i kada planine budu pokrenute i smrvljene');
INSERT INTO "bs_darassalam" VALUES(81,4,'81:4','i kada steone kamile budu ostavljene,');
INSERT INTO "bs_darassalam" VALUES(81,5,'81:5','i kada divlje životinje budu sabrane');
INSERT INTO "bs_darassalam" VALUES(81,6,'81:6','i kada mora budu raspaljena');
INSERT INTO "bs_darassalam" VALUES(81,7,'81:7','i kada duše budu sparene,');
INSERT INTO "bs_darassalam" VALUES(81,8,'81:8','i kada djevojčica koja je živa zakopana bude upitana');
INSERT INTO "bs_darassalam" VALUES(81,9,'81:9','zbog kojeg je grijeha ubijena');
INSERT INTO "bs_darassalam" VALUES(81,10,'81:10','i kada listovi budu rašireni');
INSERT INTO "bs_darassalam" VALUES(81,11,'81:11','i kada nebo bude uklonjeno,');
INSERT INTO "bs_darassalam" VALUES(81,12,'81:12','kada Oganj bude razbuktan');
INSERT INTO "bs_darassalam" VALUES(81,13,'81:13','i kada Džennet bude približen');
INSERT INTO "bs_darassalam" VALUES(81,14,'81:14','svako će saznati ono što je pripremio.');
INSERT INTO "bs_darassalam" VALUES(81,15,'81:15','I ne! Kunem se onim što se skriva');
INSERT INTO "bs_darassalam" VALUES(81,16,'81:16','što se kreće i sklanja,');
INSERT INTO "bs_darassalam" VALUES(81,17,'81:17','i noći kad dolazi,');
INSERT INTO "bs_darassalam" VALUES(81,18,'81:18','i zorom kad diše,');
INSERT INTO "bs_darassalam" VALUES(81,19,'81:19','On – Kur’an je, zaista, kazivanje Izaslanika Plemenitog');
INSERT INTO "bs_darassalam" VALUES(81,20,'81:20','Moćnog, kod Gospodara Arša, cijenjenog');
INSERT INTO "bs_darassalam" VALUES(81,21,'81:21','Kome se drugi tamo pokoravaju, povjerljivog!');
INSERT INTO "bs_darassalam" VALUES(81,22,'81:22','A drug vaš nije lud');
INSERT INTO "bs_darassalam" VALUES(81,23,'81:23','on ga je na obzorju jasnom vidio,');
INSERT INTO "bs_darassalam" VALUES(81,24,'81:24','i, kada je u pitanju ono što je čulima nedokučivo, on nije škrt,');
INSERT INTO "bs_darassalam" VALUES(81,25,'81:25','i on nije prokletog šejtana govor');
INSERT INTO "bs_darassalam" VALUES(81,26,'81:26','pa kuda onda idete?!');
INSERT INTO "bs_darassalam" VALUES(81,27,'81:27','On je samo opomena svjetovima,');
INSERT INTO "bs_darassalam" VALUES(81,28,'81:28','onome od vas koji hoće da je na Pravom putu,');
INSERT INTO "bs_darassalam" VALUES(81,29,'81:29','a vi ne možete ništa htjeti ako to Allah, Gospodar svjetova, neće!');
INSERT INTO "bs_darassalam" VALUES(82,1,'82:1','Kada se nebo rascijepi,');
INSERT INTO "bs_darassalam" VALUES(82,2,'82:2','i kada zvijezde popadaju raštrkane,');
INSERT INTO "bs_darassalam" VALUES(82,3,'82:3','i kada mora budu prokuhljala pa se preliju,');
INSERT INTO "bs_darassalam" VALUES(82,4,'82:4','i kada kaburovi budu prevrnuti,');
INSERT INTO "bs_darassalam" VALUES(82,5,'82:5','svako će saznati šta je pripremio, a šta propustio.');
INSERT INTO "bs_darassalam" VALUES(82,6,'82:6','O čovječe, šta te je obmanulo u vezi s tvojim Gospodarom Plemenitim?!');
INSERT INTO "bs_darassalam" VALUES(82,7,'82:7','Koji te je stvorio, pa učinio da si skladan i da si uspravan,');
INSERT INTO "bs_darassalam" VALUES(82,8,'82:8','i kakav je htio lik ti dao.');
INSERT INTO "bs_darassalam" VALUES(82,9,'82:9','Naprotiv! Vi još i Sud poričete,');
INSERT INTO "bs_darassalam" VALUES(82,10,'82:10','a nad vama bdiju čuvari,');
INSERT INTO "bs_darassalam" VALUES(82,11,'82:11','plemeniti pisari');
INSERT INTO "bs_darassalam" VALUES(82,12,'82:12','koji znaju ono što radite.');
INSERT INTO "bs_darassalam" VALUES(82,13,'82:13','Čestiti će, sigurno, u Užitak,');
INSERT INTO "bs_darassalam" VALUES(82,14,'82:14','a razvratnici, sigurno, u Oganj.');
INSERT INTO "bs_darassalam" VALUES(82,15,'82:15','Na Sudnjem danu u njemu će gorjeti');
INSERT INTO "bs_darassalam" VALUES(82,16,'82:16','i više iz njega izbivati se neće.');
INSERT INTO "bs_darassalam" VALUES(82,17,'82:17','A šta ti znaš šta je Sudnji dan?!');
INSERT INTO "bs_darassalam" VALUES(82,18,'82:18','I još jednom: šta znaš šta je Sudnji dan?!');
INSERT INTO "bs_darassalam" VALUES(82,19,'82:19','Dan kada niko nikome neće moći nimalo pomoći, Dana tog vlast ćejedino Allah imati.');
INSERT INTO "bs_darassalam" VALUES(83,1,'83:1','Teško onima koji zakidaju');
INSERT INTO "bs_darassalam" VALUES(83,2,'83:2','koji punu mjeru uzimaju kad od drugih kupuju,');
INSERT INTO "bs_darassalam" VALUES(83,3,'83:3','a kad drugima mjere na litar ili na kantar – zakidaju.');
INSERT INTO "bs_darassalam" VALUES(83,4,'83:4','Zar ne misle takvi da će oživljeni biti');
INSERT INTO "bs_darassalam" VALUES(83,5,'83:5','na Dan veliki,');
INSERT INTO "bs_darassalam" VALUES(83,6,'83:6','na Dan kada će se ljudi Gospodaru svjetova dići?');
INSERT INTO "bs_darassalam" VALUES(83,7,'83:7','Uistinu! Zapisano je razvratnicima da su u Sidždžinu,');
INSERT INTO "bs_darassalam" VALUES(83,8,'83:8','a znaš li ti šta je Sidždžin?!');
INSERT INTO "bs_darassalam" VALUES(83,9,'83:9','Knjiga je ispisana.');
INSERT INTO "bs_darassalam" VALUES(83,10,'83:10','Teško taj dan poricateljima,');
INSERT INTO "bs_darassalam" VALUES(83,11,'83:11','koji su Dan sudnji poricali,');
INSERT INTO "bs_darassalam" VALUES(83,12,'83:12','a njega ne poriče drugi doli svaki prestupnik, griješnik,');
INSERT INTO "bs_darassalam" VALUES(83,13,'83:13','koji je, kada su mu ajeti Naši kazivani, govorio: “Izmišljotine narodadrevnih!”');
INSERT INTO "bs_darassalam" VALUES(83,14,'83:14','Ne, naprotiv! Ono što su radili prekrilo je srca njihova.');
INSERT INTO "bs_darassalam" VALUES(83,15,'83:15','Ne, naprotiv! Zaista, oni će taj dan od viđenja svoga Gospodara zastrtibiti,');
INSERT INTO "bs_darassalam" VALUES(83,16,'83:16','zatim će sigurno u Oganj ući,');
INSERT INTO "bs_darassalam" VALUES(83,17,'83:17','pa će im biti rečeno: “Eto, to je ono što ste poricali!”');
INSERT INTO "bs_darassalam" VALUES(83,18,'83:18','Uistinu! Zapisano je čestitima da su u Illijjunu,');
INSERT INTO "bs_darassalam" VALUES(83,19,'83:19','a znaš li ti šta je Illijjun?');
INSERT INTO "bs_darassalam" VALUES(83,20,'83:20','Knjiga je ispisana.');
INSERT INTO "bs_darassalam" VALUES(83,21,'83:21','Uz nju su prisutni oni koji su bliski.');
INSERT INTO "bs_darassalam" VALUES(83,22,'83:22','Čestiti će, zaista, u Užitku biti,');
INSERT INTO "bs_darassalam" VALUES(83,23,'83:23','sa divana prekrivenih gledati,');
INSERT INTO "bs_darassalam" VALUES(83,24,'83:24','na licima njihovim prepoznat ćeš ozarenost užitkom,');
INSERT INTO "bs_darassalam" VALUES(83,25,'83:25','daće im se pa će piće zapečaćeno piti,');
INSERT INTO "bs_darassalam" VALUES(83,26,'83:26','na čijem kraju misk će biti, i neka se za to natječu oni koji hoće da senatječu!');
INSERT INTO "bs_darassalam" VALUES(83,27,'83:27','A mješavina mu je iz Tesnima,');
INSERT INTO "bs_darassalam" VALUES(83,28,'83:28','vrela iz kojeg će oni bliski piti.');
INSERT INTO "bs_darassalam" VALUES(83,29,'83:29','Zaista su razvratnici ismijavali one koji vjeruju,');
INSERT INTO "bs_darassalam" VALUES(83,30,'83:30','i kada su pored njih prolazili, jedni drugima su namigivali.');
INSERT INTO "bs_darassalam" VALUES(83,31,'83:31','A kad su se porodicama svojim vraćali, vraćali su se šale zbijajući.');
INSERT INTO "bs_darassalam" VALUES(83,32,'83:32','A kad bi ih vidjeli, rekli bi: “Ovi su, doista, zalutali.”');
INSERT INTO "bs_darassalam" VALUES(83,33,'83:33','a oni nisu poslani da ih čuvaju.');
INSERT INTO "bs_darassalam" VALUES(83,34,'83:34','Taj dan oni koji su vjerovali nevjernicima će se podsmijavati,');
INSERT INTO "bs_darassalam" VALUES(83,35,'83:35','sa divana će gledati.');
INSERT INTO "bs_darassalam" VALUES(83,36,'83:36','Zar će nevjernici biti drugačije kažnjeni nego prema onome kako su postupali!');
INSERT INTO "bs_darassalam" VALUES(84,1,'84:1','Kada se nebo rascijepi,');
INSERT INTO "bs_darassalam" VALUES(84,2,'84:2','i posluša Gospodara svoga, a ono će to dužno biti,');
INSERT INTO "bs_darassalam" VALUES(84,3,'84:3','i kada Zemlja bude rastegnuta,');
INSERT INTO "bs_darassalam" VALUES(84,4,'84:4','i izbaci ono što je u njoj, i potpuno se isprazni,');
INSERT INTO "bs_darassalam" VALUES(84,5,'84:5','i posluša Gospodara svoga, a ona će to dužno biti,');
INSERT INTO "bs_darassalam" VALUES(84,6,'84:6','O čovječe, ti se trudiš idući svome Gospodaru, pa ćeš svoj trud naći.');
INSERT INTO "bs_darassalam" VALUES(84,7,'84:7','Onaj kome bude knjiga njegova u desnu ruku njegovu data,');
INSERT INTO "bs_darassalam" VALUES(84,8,'84:8','na lahak obračun naići će');
INSERT INTO "bs_darassalam" VALUES(84,9,'84:9','i svojima će se radostan vratiti;');
INSERT INTO "bs_darassalam" VALUES(84,10,'84:10','a onaj kome bude knjiga njegova iza leđa njegovih data,');
INSERT INTO "bs_darassalam" VALUES(84,11,'84:11','propast će prizivati,');
INSERT INTO "bs_darassalam" VALUES(84,12,'84:12','i u Ognju će gorjeti.');
INSERT INTO "bs_darassalam" VALUES(84,13,'84:13','Zaista je on sa porodicom svojom radostan bio,');
INSERT INTO "bs_darassalam" VALUES(84,14,'84:14','i mislio da se nikada neće vratiti,');
INSERT INTO "bs_darassalam" VALUES(84,15,'84:15','a hoće! Gospodar njegov o njemu, zaista, sve zna!');
INSERT INTO "bs_darassalam" VALUES(84,16,'84:16','Ali ne! Kunem se rumenilom večernjim,');
INSERT INTO "bs_darassalam" VALUES(84,17,'84:17','i noći, i onim što ona sakuplja');
INSERT INTO "bs_darassalam" VALUES(84,18,'84:18','i Mjesecom kada se ispuni,');
INSERT INTO "bs_darassalam" VALUES(84,19,'84:19','vi ćete, sigurno, iz stanja u stanje prelaziti!');
INSERT INTO "bs_darassalam" VALUES(84,20,'84:20','Pa šta im je, da ne vjeruju');
INSERT INTO "bs_darassalam" VALUES(84,21,'84:21','i kad im se Kur’an uči na tlo licem ne padaju?!');
INSERT INTO "bs_darassalam" VALUES(84,22,'84:22','Naprotiv, oni koji ne vjeruju poriču,');
INSERT INTO "bs_darassalam" VALUES(84,23,'84:23','a Allah najbolje zna šta oni u sebi kriju,');
INSERT INTO "bs_darassalam" VALUES(84,24,'84:24','zato im navijesti patnju bolnu!');
INSERT INTO "bs_darassalam" VALUES(84,25,'84:25','A oni koji budu vjerovali i dobra djela činili, imat će nagradu neprekidnu.');
INSERT INTO "bs_darassalam" VALUES(85,1,'85:1','Tako mi neba sa zvijezdama velikim,');
INSERT INTO "bs_darassalam" VALUES(85,2,'85:2','i Dana već obećanog,');
INSERT INTO "bs_darassalam" VALUES(85,3,'85:3','i svjedoka i posvjedočenog,');
INSERT INTO "bs_darassalam" VALUES(85,4,'85:4','prokleti bili vlasnici rovova,');
INSERT INTO "bs_darassalam" VALUES(85,5,'85:5','i vatre s gorivom,');
INSERT INTO "bs_darassalam" VALUES(85,6,'85:6','kada su oko nje sjedili');
INSERT INTO "bs_darassalam" VALUES(85,7,'85:7','i bili svjedoci onoga što su vjernicima radili!');
INSERT INTO "bs_darassalam" VALUES(85,8,'85:8','A svetili su im se samo zato što su u Allaha, Silnoga i Hvaljenoga,vjerovali,');
INSERT INTO "bs_darassalam" VALUES(85,9,'85:9','Čija je vlast i na nebesima i na Zemlji, a Allah je svemu Svjedok.');
INSERT INTO "bs_darassalam" VALUES(85,10,'85:10','Zaista, oni koji su stavljali na kušnju vjernike i vjernice, a zatim se nisupokajali, imat će kaznu u Džehennemu, imat će kaznu ognjenu.');
INSERT INTO "bs_darassalam" VALUES(85,11,'85:11','Zaista, onima koji su vjerovali i radili dobra djela pripadaju baščedžennetske, kroz koje rijeke teku, a to je uspjeh veliki.');
INSERT INTO "bs_darassalam" VALUES(85,12,'85:12','Kazne Gospodara tvoga zaista su žestoke!');
INSERT INTO "bs_darassalam" VALUES(85,13,'85:13','On ni iz čega stvara, i ponovo će to učiniti;');
INSERT INTO "bs_darassalam" VALUES(85,14,'85:14','On je Oprostitelj grijeha, Onaj Koji puno voli');
INSERT INTO "bs_darassalam" VALUES(85,15,'85:15','Gospodar Arša, Veličanstveni.');
INSERT INTO "bs_darassalam" VALUES(85,16,'85:16','On radi šta hoće.');
INSERT INTO "bs_darassalam" VALUES(85,17,'85:17','Da li je do tebe doprla vijest o vojskama,');
INSERT INTO "bs_darassalam" VALUES(85,18,'85:18','o faraonu i Semūdu?');
INSERT INTO "bs_darassalam" VALUES(85,19,'85:19','Ali, ovi koji ne vjeruju stalno poriču,');
INSERT INTO "bs_darassalam" VALUES(85,20,'85:20','a Allah je iza njih, okružuje ih');
INSERT INTO "bs_darassalam" VALUES(85,21,'85:21','Naprotiv! Ovo je Kur’an veličanstveni');
INSERT INTO "bs_darassalam" VALUES(85,22,'85:22','na Ploči pomno čuvanoj.');
INSERT INTO "bs_darassalam" VALUES(86,1,'86:1','Tako mi neba i noćnog posjetioca!');
INSERT INTO "bs_darassalam" VALUES(86,2,'86:2','A znaš li ti šta je to noćni posjetilac?');
INSERT INTO "bs_darassalam" VALUES(86,3,'86:3','Zvijezda prodiruća!');
INSERT INTO "bs_darassalam" VALUES(86,4,'86:4','Nema čovjeka nad kojim neko ne bdije.');
INSERT INTO "bs_darassalam" VALUES(86,5,'86:5','Nek’ čovjek pogleda od čega je stvoren!');
INSERT INTO "bs_darassalam" VALUES(86,6,'86:6','Stvoren je od tekućine koja izbija u mlazu,');
INSERT INTO "bs_darassalam" VALUES(86,7,'86:7','koja između kičme i grudi izlazi,');
INSERT INTO "bs_darassalam" VALUES(86,8,'86:8','i On ga je, zaista, kadar ponovno stvoriti');
INSERT INTO "bs_darassalam" VALUES(86,9,'86:9','onaj dan kada tajne budu otkrivene,');
INSERT INTO "bs_darassalam" VALUES(86,10,'86:10','kada čovjek ni snage ni pomagača neće imati.');
INSERT INTO "bs_darassalam" VALUES(86,11,'86:11','I tako mi neba punog kiše koja se vraća.');
INSERT INTO "bs_darassalam" VALUES(86,12,'86:12','i Zemlje pune raspuknuća,');
INSERT INTO "bs_darassalam" VALUES(86,13,'86:13','Kur’an je, doista, govor koji rastavlja Istinu od neistine,');
INSERT INTO "bs_darassalam" VALUES(86,14,'86:14','lakrdija nikakva nije.');
INSERT INTO "bs_darassalam" VALUES(86,15,'86:15','Oni, zbilja, prave zamke,');
INSERT INTO "bs_darassalam" VALUES(86,16,'86:16','pa i Ja njima zamke pravim');
INSERT INTO "bs_darassalam" VALUES(86,17,'86:17','zato nevjernicima još vremena daj, još koji trenutak ih ostavi.');
INSERT INTO "bs_darassalam" VALUES(87,1,'87:1','Slavi i uzvisuj ime Gospodara svoga Svevišnjeg,');
INSERT INTO "bs_darassalam" VALUES(87,2,'87:2','Koji sve stvara i čini skladnim,');
INSERT INTO "bs_darassalam" VALUES(87,3,'87:3','i Koji određuje, pa upućuje,');
INSERT INTO "bs_darassalam" VALUES(87,4,'87:4','i Koji čini da rastinje iznikne,');
INSERT INTO "bs_darassalam" VALUES(87,5,'87:5','a potom čini da postane suho, potamnjelo.');
INSERT INTO "bs_darassalam" VALUES(87,6,'87:6','Mi ćemo te naučiti da učiš pa ništa nećeš zaboraviti,');
INSERT INTO "bs_darassalam" VALUES(87,7,'87:7','osim onoga što Allah hoće. On, zaista, zna i ono što na javu iznosite i ono što tajite.');
INSERT INTO "bs_darassalam" VALUES(87,8,'87:8','I dostupnim ti učiniti ono što je lahko;');
INSERT INTO "bs_darassalam" VALUES(87,9,'87:9','pa ti opominji ako opomena bude koristila');
INSERT INTO "bs_darassalam" VALUES(87,10,'87:10','opomenut će se onaj koji se Allaha boji,');
INSERT INTO "bs_darassalam" VALUES(87,11,'87:11','a izbjegavat će je onaj najgori,');
INSERT INTO "bs_darassalam" VALUES(87,12,'87:12','koji će u vatri velikoj gorjeti,');
INSERT INTO "bs_darassalam" VALUES(87,13,'87:13','pa u njoj neće ni umrijeti ni živjeti.');
INSERT INTO "bs_darassalam" VALUES(87,14,'87:14','Uspio je onaj koji se očisti');
INSERT INTO "bs_darassalam" VALUES(87,15,'87:15','i spomene ime Gospodara svoga pa klanja!');
INSERT INTO "bs_darassalam" VALUES(87,16,'87:16','Ali, vi više život dunjalučki volite,');
INSERT INTO "bs_darassalam" VALUES(87,17,'87:17','a ahiret je bolji i vječan je,');
INSERT INTO "bs_darassalam" VALUES(87,18,'87:18','To, doista, ima u listovima prvim,');
INSERT INTO "bs_darassalam" VALUES(87,19,'87:19','listovima Ibrāhīmovim i Mūsāovim.');
INSERT INTO "bs_darassalam" VALUES(88,1,'88:1','Da li je doprla do tebe vijest o Općoj nevolji?');
INSERT INTO "bs_darassalam" VALUES(88,2,'88:2','Neka lica Dan taj bit će potištena,');
INSERT INTO "bs_darassalam" VALUES(88,3,'88:3','premorena, napaćena,');
INSERT INTO "bs_darassalam" VALUES(88,4,'88:4','u vatri užarenoj pržena,');
INSERT INTO "bs_darassalam" VALUES(88,5,'88:5','sa vrela uzavrelog napajana,');
INSERT INTO "bs_darassalam" VALUES(88,6,'88:6','kada drugog jela osim trnja neće imati,');
INSERT INTO "bs_darassalam" VALUES(88,7,'88:7','koje neće ni ugojiti ni glad utoliti.');
INSERT INTO "bs_darassalam" VALUES(88,8,'88:8','Neka lica Dan taj bit će radosna,');
INSERT INTO "bs_darassalam" VALUES(88,9,'88:9','trudom svojim zadovoljna,');
INSERT INTO "bs_darassalam" VALUES(88,10,'88:10','u Džennetu visokome,');
INSERT INTO "bs_darassalam" VALUES(88,11,'88:11','u kojem prazne besjede neće slušati!');
INSERT INTO "bs_darassalam" VALUES(88,12,'88:12','U njemu su izvor-vode koje teku,');
INSERT INTO "bs_darassalam" VALUES(88,13,'88:13','u njemu su divani uzdignuti,');
INSERT INTO "bs_darassalam" VALUES(88,14,'88:14','i pehari postavljeni,');
INSERT INTO "bs_darassalam" VALUES(88,15,'88:15','i jastuci poredani');
INSERT INTO "bs_darassalam" VALUES(88,16,'88:16','i ćilimi rašireni.');
INSERT INTO "bs_darassalam" VALUES(88,17,'88:17','Pa, zašto oni ne gledaju kamilu – kako je stvorena,');
INSERT INTO "bs_darassalam" VALUES(88,18,'88:18','i nebo – kako je uzdignuto,');
INSERT INTO "bs_darassalam" VALUES(88,19,'88:19','i planine – kako su postavljene,');
INSERT INTO "bs_darassalam" VALUES(88,20,'88:20','i Zemlju – kako je prostrta?!');
INSERT INTO "bs_darassalam" VALUES(88,21,'88:21','Ti upozoravaj, ti samo i jesi upozoritelj,');
INSERT INTO "bs_darassalam" VALUES(88,22,'88:22','ti nisi onaj koji ih prisiljava!');
INSERT INTO "bs_darassalam" VALUES(88,23,'88:23','A onoga koji se okreće i ne vjeruje,');
INSERT INTO "bs_darassalam" VALUES(88,24,'88:24','njega će Allah najvećom patnjom kazniti.');
INSERT INTO "bs_darassalam" VALUES(88,25,'88:25','Nama će se oni, zaista, vratiti.');
INSERT INTO "bs_darassalam" VALUES(88,26,'88:26','A potom, na Nama je, doista, da račun njihov svedemo!');
INSERT INTO "bs_darassalam" VALUES(89,1,'89:1','Tako mi zore,');
INSERT INTO "bs_darassalam" VALUES(89,2,'89:2','i deset noći,');
INSERT INTO "bs_darassalam" VALUES(89,3,'89:3','i parnog i neparnog,');
INSERT INTO "bs_darassalam" VALUES(89,4,'89:4','i noći kada nestaje,');
INSERT INTO "bs_darassalam" VALUES(89,5,'89:5','zar to pametnom zakletva nije?');
INSERT INTO "bs_darassalam" VALUES(89,6,'89:6','Zar ne znaš šta je Gospodar tvoj sa narodom Ad uradio,');
INSERT INTO "bs_darassalam" VALUES(89,7,'89:7','sa stanovnicima Irema, punog palata na stupovima');
INSERT INTO "bs_darassalam" VALUES(89,8,'89:8','kojima ravna ni u jednoj zemlji nije bilo;');
INSERT INTO "bs_darassalam" VALUES(89,9,'89:9','i sa Semūdom, koji su stijene u dolini klesali,');
INSERT INTO "bs_darassalam" VALUES(89,10,'89:10','a i sa faraonom, koji je vojsku imao,');
INSERT INTO "bs_darassalam" VALUES(89,11,'89:11','koji su na Zemlji obijesni bili,');
INSERT INTO "bs_darassalam" VALUES(89,12,'89:12','i smutnje na njoj umnožili,');
INSERT INTO "bs_darassalam" VALUES(89,13,'89:13','pa je Gospodar tvoj bič patnje na njih spustio,');
INSERT INTO "bs_darassalam" VALUES(89,14,'89:14','a Gospodar tvoj je, zaista, u zasjedi');
INSERT INTO "bs_darassalam" VALUES(89,15,'89:15','A čovjek, kada ga Gospodar njegov iskuša pa mu počast ukaže iblagodatima ga obaspe, rekne: “Gospodar moj mene je počastvovao!”');
INSERT INTO "bs_darassalam" VALUES(89,16,'89:16','A kad ga iskuša i opskrbu njegovu oskudnom učini, onda rekne:“Gospodar moj me je ponizio!”');
INSERT INTO "bs_darassalam" VALUES(89,17,'89:17','A nije tako! Vi pažnju siročetu ne ukazujete,');
INSERT INTO "bs_darassalam" VALUES(89,18,'89:18','i da se puki siromah nahrani – jedan drugoga ne podstičete,');
INSERT INTO "bs_darassalam" VALUES(89,19,'89:19','a nasljedstvo pohlepno jedete');
INSERT INTO "bs_darassalam" VALUES(89,20,'89:20','i imetak pretjerano volite.');
INSERT INTO "bs_darassalam" VALUES(89,21,'89:21','Uistinu, kada Zemlja bude u komadiće zdrobljena,');
INSERT INTO "bs_darassalam" VALUES(89,22,'89:22','i kada dođe Gospodar tvoj, a meleci budu sve red do reda,');
INSERT INTO "bs_darassalam" VALUES(89,23,'89:23','i kad se tog Dana primakne Džehennem; tada će se čovjek sjetiti - a na što mu je sjećanje?! -');
INSERT INTO "bs_darassalam" VALUES(89,24,'89:24','i reći će: “Kamo sreće da sam se za ovaj život pripremio!”');
INSERT INTO "bs_darassalam" VALUES(89,25,'89:25','A Dan taj niko neće kao On kažnjavati');
INSERT INTO "bs_darassalam" VALUES(89,26,'89:26','i niko neće kao On u okove okivati.');
INSERT INTO "bs_darassalam" VALUES(89,27,'89:27','A ti, o dušo smirena,');
INSERT INTO "bs_darassalam" VALUES(89,28,'89:28','vrati se Gospodaru svome zadovoljna, a i da zbog tebe zadovoljstvobude,');
INSERT INTO "bs_darassalam" VALUES(89,29,'89:29','pa uđi među robove Moje');
INSERT INTO "bs_darassalam" VALUES(89,30,'89:30','i uđi u Džennet Moj!');
INSERT INTO "bs_darassalam" VALUES(90,1,'90:1','Kunem se gradom ovim,');
INSERT INTO "bs_darassalam" VALUES(90,2,'90:2','a ti si u ovom gradu oslobođen ograničenja,');
INSERT INTO "bs_darassalam" VALUES(90,3,'90:3','i roditeljem i onim koga je rodio,');
INSERT INTO "bs_darassalam" VALUES(90,4,'90:4','Čovjeka smo stvorili da je u naporu.');
INSERT INTO "bs_darassalam" VALUES(90,5,'90:5','Misli li on da mu niko ništa ne može?');
INSERT INTO "bs_darassalam" VALUES(90,6,'90:6','“Utrošio sam imetak nagomilani!”, govori.');
INSERT INTO "bs_darassalam" VALUES(90,7,'90:7','Zar misli da ga niko vidio nije?');
INSERT INTO "bs_darassalam" VALUES(90,8,'90:8','Zar mu nismo dali oka dva,');
INSERT INTO "bs_darassalam" VALUES(90,9,'90:9','i jezik i usne dvije,');
INSERT INTO "bs_darassalam" VALUES(90,10,'90:10','i dobro i zlo mu objasnili?');
INSERT INTO "bs_darassalam" VALUES(90,11,'90:11','Pa zašto nije savladao uspon?');
INSERT INTO "bs_darassalam" VALUES(90,12,'90:12','A šta ti misliš: šta je to uspon?');
INSERT INTO "bs_darassalam" VALUES(90,13,'90:13','Roba ropstva osloboditi,');
INSERT INTO "bs_darassalam" VALUES(90,14,'90:14','ili, u vrijeme gladi, nahraniti');
INSERT INTO "bs_darassalam" VALUES(90,15,'90:15','siroče bliska roda,');
INSERT INTO "bs_darassalam" VALUES(90,16,'90:16','ili ubogoga nevoljnika,');
INSERT INTO "bs_darassalam" VALUES(90,17,'90:17','A potom da je bio od onih koji vjeruju, koji jedni drugima strpljivostpreporučuju i koji jedni drugima milosrđe preporučuju;');
INSERT INTO "bs_darassalam" VALUES(90,18,'90:18','oni će biti – oni s desne strane!');
INSERT INTO "bs_darassalam" VALUES(90,19,'90:19','A oni koji ne vjeruju u ajete i dokaze Naše, oni će biti – oni s lijevestrane,');
INSERT INTO "bs_darassalam" VALUES(90,20,'90:20','iznad njih će vatra zatvorena biti.');
INSERT INTO "bs_darassalam" VALUES(91,1,'91:1','Tako mi Sunca i svjetla njegova,');
INSERT INTO "bs_darassalam" VALUES(91,2,'91:2','i Mjeseca kada ga prati,');
INSERT INTO "bs_darassalam" VALUES(91,3,'91:3','i dana kada ga vidljivim učini,');
INSERT INTO "bs_darassalam" VALUES(91,4,'91:4','i noći kada ga zakloni,');
INSERT INTO "bs_darassalam" VALUES(91,5,'91:5','i neba i Onoga Koji ga sazda,');
INSERT INTO "bs_darassalam" VALUES(91,6,'91:6','i Zemlje i Onoga Koji je rasprostrije,');
INSERT INTO "bs_darassalam" VALUES(91,7,'91:7','i duše i Onoga Koji je ispravnom stvori,');
INSERT INTO "bs_darassalam" VALUES(91,8,'91:8','pa je nadahnu da zna šta je razvrat, a šta bogobojaznost;');
INSERT INTO "bs_darassalam" VALUES(91,9,'91:9','uspio je onaj ko je očisti,');
INSERT INTO "bs_darassalam" VALUES(91,10,'91:10','a izgubio je onaj ko je zapostavi!');
INSERT INTO "bs_darassalam" VALUES(91,11,'91:11','Narod Semūd je zbog obijesti svoje poricao.');
INSERT INTO "bs_darassalam" VALUES(91,12,'91:12','Kad se jedan nesretnik između njih podigao,');
INSERT INTO "bs_darassalam" VALUES(91,13,'91:13','Allahov poslanik im je doviknuo: “Pazite na Allahovu devu i njen redpojenja!”');
INSERT INTO "bs_darassalam" VALUES(91,14,'91:14','ali su ga u laž utjerali, pa su je zaklali – i Gospodar njihov ih je, zboggrijeha njihovih, uništio i kaznom sve obuhvatio');
INSERT INTO "bs_darassalam" VALUES(91,15,'91:15','ne bojeći se kakve će posljedice.');
INSERT INTO "bs_darassalam" VALUES(92,1,'92:1','Tako mi noći kada sve zakrije,');
INSERT INTO "bs_darassalam" VALUES(92,2,'92:2','i dana kad svane,');
INSERT INTO "bs_darassalam" VALUES(92,3,'92:3','i Onoga Koji muško i žensko stvara,');
INSERT INTO "bs_darassalam" VALUES(92,4,'92:4','vaš trud je, zaista, različit.');
INSERT INTO "bs_darassalam" VALUES(92,5,'92:5','A onome koji udjeljuje i bogobojazan je');
INSERT INTO "bs_darassalam" VALUES(92,6,'92:6','i ono najljepše smatra istinitim,');
INSERT INTO "bs_darassalam" VALUES(92,7,'92:7','njemu ćemo ono što je lahko dostupnim učiniti;');
INSERT INTO "bs_darassalam" VALUES(92,8,'92:8','a onome koji tvrdiči i osjeća se neovisnim,');
INSERT INTO "bs_darassalam" VALUES(92,9,'92:9','i ono najljepše smatra lažnim,');
INSERT INTO "bs_darassalam" VALUES(92,10,'92:10','njemu ćemo ono što je tegobno dostupnim učiniti,');
INSERT INTO "bs_darassalam" VALUES(92,11,'92:11','i imetak njegov mu, kad se strovali, neće koristiti.');
INSERT INTO "bs_darassalam" VALUES(92,12,'92:12','Mi smo dužni ukazati na Pravi put');
INSERT INTO "bs_darassalam" VALUES(92,13,'92:13','i jedino Nama pripada i ahiret i ovaj svijet.');
INSERT INTO "bs_darassalam" VALUES(92,14,'92:14','Zato vas upozoravam na razbuktalu vatru,');
INSERT INTO "bs_darassalam" VALUES(92,15,'92:15','u koju će ući samo najveći nesretnik,');
INSERT INTO "bs_darassalam" VALUES(92,16,'92:16','onaj koji bude poricao i okretao se');
INSERT INTO "bs_darassalam" VALUES(92,17,'92:17','a od nje će odstranjen biti onaj najbogobojazniji,');
INSERT INTO "bs_darassalam" VALUES(92,18,'92:18','onaj koji bude dio imetka svoga udjeljivao da bi se očistio,');
INSERT INTO "bs_darassalam" VALUES(92,19,'92:19','ne očekujući da mu se zahvalnošću uzvrati,');
INSERT INTO "bs_darassalam" VALUES(92,20,'92:20','već jedino žudeći za Licem Gospodara svoga Svevišnjeg,');
INSERT INTO "bs_darassalam" VALUES(92,21,'92:21','i on će, zbilja, zadovoljan biti!');
INSERT INTO "bs_darassalam" VALUES(93,1,'93:1','Tako mi jutra');
INSERT INTO "bs_darassalam" VALUES(93,2,'93:2','i noći kad se utiša,');
INSERT INTO "bs_darassalam" VALUES(93,3,'93:3','Gospodar tvoj nije te ni napustio ni omrznuo!');
INSERT INTO "bs_darassalam" VALUES(93,4,'93:4','Ahiret je, zaista, bolji za tebe od ovog svijeta,');
INSERT INTO "bs_darassalam" VALUES(93,5,'93:5','a Gospodar tvoj će tebi, sigurno, dati, pa ćeš zadovoljan biti.');
INSERT INTO "bs_darassalam" VALUES(93,6,'93:6','Zar te siročetom ne nađe, pa ti utočište dade,');
INSERT INTO "bs_darassalam" VALUES(93,7,'93:7','i zalutalim te nađe, pa te uputi,');
INSERT INTO "bs_darassalam" VALUES(93,8,'93:8','i siromahom te nađe, pa te bogatim učini?');
INSERT INTO "bs_darassalam" VALUES(93,9,'93:9','Zato, siroče ne okahari,');
INSERT INTO "bs_darassalam" VALUES(93,10,'93:10','i onoga ko traži ne odbij,');
INSERT INTO "bs_darassalam" VALUES(93,11,'93:11','i o blagodati Gospodara svoga kazuj!');
INSERT INTO "bs_darassalam" VALUES(94,1,'94:1','Zar grudi tvoje nismo prostranim učinili');
INSERT INTO "bs_darassalam" VALUES(94,2,'94:2','i breme tvoje skinuli,');
INSERT INTO "bs_darassalam" VALUES(94,3,'94:3','koje je pleća tvoja pritiskalo,');
INSERT INTO "bs_darassalam" VALUES(94,4,'94:4','i spomen na tebe visoko uzdigli!');
INSERT INTO "bs_darassalam" VALUES(94,5,'94:5','Pa, zaista, s mukom je olakšanje,');
INSERT INTO "bs_darassalam" VALUES(94,6,'94:6','zaista, s mukom je olakšanje!');
INSERT INTO "bs_darassalam" VALUES(94,7,'94:7','A kad završiš, molitvi se predaj,');
INSERT INTO "bs_darassalam" VALUES(94,8,'94:8','i Gospodaru svome teži!');
INSERT INTO "bs_darassalam" VALUES(95,1,'95:1','Tako mi smokve i masline,');
INSERT INTO "bs_darassalam" VALUES(95,2,'95:2','i Sinajske gore');
INSERT INTO "bs_darassalam" VALUES(95,3,'95:3','i grada ovog sigurnog');
INSERT INTO "bs_darassalam" VALUES(95,4,'95:4','Mi čovjeka stvaramo u najljepšem obliku,');
INSERT INTO "bs_darassalam" VALUES(95,5,'95:5','zatim ćemo ga u najniže nizine vratiti,');
INSERT INTO "bs_darassalam" VALUES(95,6,'95:6','samo ne one koji budu vjerovali i dobra djela činili, njima pripada nagrada neprekidna.');
INSERT INTO "bs_darassalam" VALUES(95,7,'95:7','Pa, šta te, onda, navodi da poričeš obračun?');
INSERT INTO "bs_darassalam" VALUES(95,8,'95:8','Zar Allah nije sudija najpravedniji?!');
INSERT INTO "bs_darassalam" VALUES(96,1,'96:1','Čitaj s imenom Gospodara tvoga Koji stvara,');
INSERT INTO "bs_darassalam" VALUES(96,2,'96:2','stvara čovjeka od zakvačka.');
INSERT INTO "bs_darassalam" VALUES(96,3,'96:3','Čitaj, plemenit je Gospodar tvoj,');
INSERT INTO "bs_darassalam" VALUES(96,4,'96:4','Koji poučava peru,');
INSERT INTO "bs_darassalam" VALUES(96,5,'96:5','Koji čovjeka poučava onome što ne zna.');
INSERT INTO "bs_darassalam" VALUES(96,6,'96:6','Uistinu, čovjek se uzobijesti');
INSERT INTO "bs_darassalam" VALUES(96,7,'96:7','čim se neovisnim osjeti,');
INSERT INTO "bs_darassalam" VALUES(96,8,'96:8','a Gospodaru tvome će se, doista, svi vratiti!');
INSERT INTO "bs_darassalam" VALUES(96,9,'96:9','Vidje li ti onoga koji sprečava');
INSERT INTO "bs_darassalam" VALUES(96,10,'96:10','roba da klanja?');
INSERT INTO "bs_darassalam" VALUES(96,11,'96:11','Šta misliš, ako je on na Pravom putu,');
INSERT INTO "bs_darassalam" VALUES(96,12,'96:12','ili ako bogobojaznost naređuje?!');
INSERT INTO "bs_darassalam" VALUES(96,13,'96:13','Šta misliš, ako on poriče i okreće se,');
INSERT INTO "bs_darassalam" VALUES(96,14,'96:14','zar on ne zna da Allah vidi?!');
INSERT INTO "bs_darassalam" VALUES(96,15,'96:15','Ne valja to! Ako se ne okani, dohvatićemo ga za kiku,');
INSERT INTO "bs_darassalam" VALUES(96,16,'96:16','kiku lažnu i grješnu,');
INSERT INTO "bs_darassalam" VALUES(96,17,'96:17','pa neka on pozove društvo svoje.');
INSERT INTO "bs_darassalam" VALUES(96,18,'96:18','Mi ćemo pozvati zebanije.');
INSERT INTO "bs_darassalam" VALUES(96,19,'96:19','Naprotiv! Ti se njemu ne pokoravaj, već čini sedždu i približi seGospodaru svome');
INSERT INTO "bs_darassalam" VALUES(97,1,'97:1','Mi smo ga objavili u Noći Kadr');
INSERT INTO "bs_darassalam" VALUES(97,2,'97:2','a šta ti misliš šta je Noć Kadr?');
INSERT INTO "bs_darassalam" VALUES(97,3,'97:3','Noć Kadr bolja je od hiljadu mjeseci;');
INSERT INTO "bs_darassalam" VALUES(97,4,'97:4','meleci i Duh, s dozvolom Gospodara svoga, spuštaju se u njoj zbogodluke svake,');
INSERT INTO "bs_darassalam" VALUES(97,5,'97:5','spas je u njoj sve dok zora ne svane.');
INSERT INTO "bs_darassalam" VALUES(98,1,'98:1','Nisu se nevjernici između sljedbenika Knjige i mnogobošci odvojili, sve dok im nije došao dokaz jasni;');
INSERT INTO "bs_darassalam" VALUES(98,2,'98:2','od Allaha Poslanik koji čita listove čiste,');
INSERT INTO "bs_darassalam" VALUES(98,3,'98:3','u kojima su propisi ispravni.');
INSERT INTO "bs_darassalam" VALUES(98,4,'98:4','A podvojili su se oni kojima je data Knjiga baš onda kad im je došaodokaz jasni,');
INSERT INTO "bs_darassalam" VALUES(98,5,'98:5','a naređeno im je da samo Allahu ibadet čine, da Mu iskreno, kaopravovjerni, vjeru ispovijedaju, i da namaz obavljaju, i da zekat udjeljuju; a to je – ispravna vjera.');
INSERT INTO "bs_darassalam" VALUES(98,6,'98:6','Oni sljedbenici Knjige koji ne vjeruju i višebošci, bit će, sigurno, u vatridžehennemskoj, u njoj će vječno ostati: oni su najgora stvorenja.');
INSERT INTO "bs_darassalam" VALUES(98,7,'98:7','A oni koji vjeruju i čine dobra djela – oni su, zbilja, najbolja stvorenja.');
INSERT INTO "bs_darassalam" VALUES(98,8,'98:8','Nagrada za njih od Gospodara njihova su vrtovi Adna, kroz koje rijeketeku, u kojima će vječno, zauvijek boraviti; Allah će biti njima zadovoljan, a i oni će biti Njime zadovoljni. To će biti za onoga koji se bude bojao Gospodara svoga.');
INSERT INTO "bs_darassalam" VALUES(99,1,'99:1','Kad se Zemlja najžešćim potresom svojim zatrese,');
INSERT INTO "bs_darassalam" VALUES(99,2,'99:2','i kada Zemlja izbaci terete svoje,');
INSERT INTO "bs_darassalam" VALUES(99,3,'99:3','i čovjek uzvikne: “Šta joj je?”,');
INSERT INTO "bs_darassalam" VALUES(99,4,'99:4','taj dan ona će vijesti svoje kazivati');
INSERT INTO "bs_darassalam" VALUES(99,5,'99:5','jer će joj Gospodar tvoj dopustiti.');
INSERT INTO "bs_darassalam" VALUES(99,6,'99:6','Taj dan ljudi će se razdvojeni pojaviti da im se pokažu djela njihova:');
INSERT INTO "bs_darassalam" VALUES(99,7,'99:7','Onaj ko bude uradio koliko trun dobra – vidjet će ga,');
INSERT INTO "bs_darassalam" VALUES(99,8,'99:8','a onaj ko bude uradio i koliko trun zla – vidjet će ga.');
INSERT INTO "bs_darassalam" VALUES(100,1,'100:1','Tako mi onih koji dahćući jure,');
INSERT INTO "bs_darassalam" VALUES(100,2,'100:2','pa varnice vrcaju,');
INSERT INTO "bs_darassalam" VALUES(100,3,'100:3','i zorom napadaju,');
INSERT INTO "bs_darassalam" VALUES(100,4,'100:4','i tu dižu prašinu');
INSERT INTO "bs_darassalam" VALUES(100,5,'100:5','pa tu svi zajedno u sredinu prodiru,');
INSERT INTO "bs_darassalam" VALUES(100,6,'100:6','čovjek je, zaista, prema Gospodaru svome poricatelj veliki,');
INSERT INTO "bs_darassalam" VALUES(100,7,'100:7','i sam je on, doista, svjedok toga,');
INSERT INTO "bs_darassalam" VALUES(100,8,'100:8','i on je, zbilja, u ljubavi za dobrima dunjalučkim žestok.');
INSERT INTO "bs_darassalam" VALUES(100,9,'100:9','A zar on ne zna da će, kada kaburovi budu prevrnuti s onim što je u njima,');
INSERT INTO "bs_darassalam" VALUES(100,10,'100:10','i kada iziđe na vidjelo ono što je u srcima,');
INSERT INTO "bs_darassalam" VALUES(100,11,'100:11','Gospodar njihov Dan taj, sigurno, sve o njima znati?!');
INSERT INTO "bs_darassalam" VALUES(101,1,'101:1','Udar zastrašujući!');
INSERT INTO "bs_darassalam" VALUES(101,2,'101:2','Šta je Udar zastrašujući?');
INSERT INTO "bs_darassalam" VALUES(101,3,'101:3','I šta ti znaš šta je Udar zastrašujući?');
INSERT INTO "bs_darassalam" VALUES(101,4,'101:4','Dan taj ljudi će biti kao leptiri raštrkani,');
INSERT INTO "bs_darassalam" VALUES(101,5,'101:5','a planine kao šarena vuna iščupana.');
INSERT INTO "bs_darassalam" VALUES(101,6,'101:6','Onaj čija dobra djela budu teška,');
INSERT INTO "bs_darassalam" VALUES(101,7,'101:7','u ugodnom životu će živjeti,');
INSERT INTO "bs_darassalam" VALUES(101,8,'101:8','a onaj čija dobra djela budu lahka,');
INSERT INTO "bs_darassalam" VALUES(101,9,'101:9','boravište će mu bezdan biti.');
INSERT INTO "bs_darassalam" VALUES(101,10,'101:10','A znaš li ti šta će to biti?');
INSERT INTO "bs_darassalam" VALUES(101,11,'101:11','Vatra užarena!');
INSERT INTO "bs_darassalam" VALUES(102,1,'102:1','Zaokuplja vas zgrtanje imovine');
INSERT INTO "bs_darassalam" VALUES(102,2,'102:2','sve dok grobove ne naselite.');
INSERT INTO "bs_darassalam" VALUES(102,3,'102:3','A ne treba tako, saznat ćete svakako!');
INSERT INTO "bs_darassalam" VALUES(102,4,'102:4','I još jednom, ne treba tako! Saznat ćete sigurno!');
INSERT INTO "bs_darassalam" VALUES(102,5,'102:5','Ne valja tako, kad biste vi znali pouzdano');
INSERT INTO "bs_darassalam" VALUES(102,6,'102:6','Oganj ćete, zaista, vidjeti!');
INSERT INTO "bs_darassalam" VALUES(102,7,'102:7','I još jednom, doista ćete ga vidjeti sasvim jasno!');
INSERT INTO "bs_darassalam" VALUES(102,8,'102:8','Zatim ćete Dan taj o blagodatima biti pitani sigurno!');
INSERT INTO "bs_darassalam" VALUES(103,1,'103:1','Tako mi vremena,');
INSERT INTO "bs_darassalam" VALUES(103,2,'103:2','doista je čovjek na gubitku,');
INSERT INTO "bs_darassalam" VALUES(103,3,'103:3','samo nisu oni koji vjeruju i dobra djela čine, i koji jedni drugima Istinupreporučuju i koji jedni drugima preporučuju strpljenje.');
INSERT INTO "bs_darassalam" VALUES(104,1,'104:1','Teško svakom klevetniku podrugljivcu,');
INSERT INTO "bs_darassalam" VALUES(104,2,'104:2','koji blago gomila i prebrojava ga,');
INSERT INTO "bs_darassalam" VALUES(104,3,'104:3','i misli da će ga blago njegovo besmrtnim učiniti.');
INSERT INTO "bs_darassalam" VALUES(104,4,'104:4','A ne treba tako! On će, sigurno, biti bačen u Hutamu!');
INSERT INTO "bs_darassalam" VALUES(104,5,'104:5','A znaš li ti šta je Hutama?');
INSERT INTO "bs_darassalam" VALUES(104,6,'104:6','Vatra Allahova razbuktala,');
INSERT INTO "bs_darassalam" VALUES(104,7,'104:7','koja će do srca dopirati.');
INSERT INTO "bs_darassalam" VALUES(104,8,'104:8','Ona će nad njima biti zatvorena');
INSERT INTO "bs_darassalam" VALUES(104,9,'104:9','plamenim izduženim stubovima.');
INSERT INTO "bs_darassalam" VALUES(105,1,'105:1','Zar nisi čuo šta je s vlasnicima slona Gospodar tvoj uradio?');
INSERT INTO "bs_darassalam" VALUES(105,2,'105:2','Zar lukavstvo njihovo nije upropastio');
INSERT INTO "bs_darassalam" VALUES(105,3,'105:3','i na njih jata ptica poslao,');
INSERT INTO "bs_darassalam" VALUES(105,4,'105:4','koje su na njih grumenje od gline pečene bacale,');
INSERT INTO "bs_darassalam" VALUES(105,5,'105:5','pa ih kao izjedeno lišće učinio?');
INSERT INTO "bs_darassalam" VALUES(106,1,'106:1','Zbog navike Kurejšija,');
INSERT INTO "bs_darassalam" VALUES(106,2,'106:2','navike njihove da zimi i ljeti putuju,');
INSERT INTO "bs_darassalam" VALUES(106,3,'106:3','neka oni Gospodaru ove Kuće ibadet čine,');
INSERT INTO "bs_darassalam" VALUES(106,4,'106:4','Koji ih gladne hrani i od straha brani.');
INSERT INTO "bs_darassalam" VALUES(107,1,'107:1','Šta misliš o onome koji Sud poriče?');
INSERT INTO "bs_darassalam" VALUES(107,2,'107:2','Pa to je onaj koji grubo odbija siroče,');
INSERT INTO "bs_darassalam" VALUES(107,3,'107:3','i koji da se nahrani siromah – ne podstiče.');
INSERT INTO "bs_darassalam" VALUES(107,4,'107:4','Pa teško klanjačima,');
INSERT INTO "bs_darassalam" VALUES(107,5,'107:5','onima koji su nemarni prema svome namazu,');
INSERT INTO "bs_darassalam" VALUES(107,6,'107:6','koji se samo ljudima pokazuju');
INSERT INTO "bs_darassalam" VALUES(107,7,'107:7','i čak potrepštine koje se na posudbu daju oni ne daju!');
INSERT INTO "bs_darassalam" VALUES(108,1,'108:1','Mi smo ti, uistinu, Kevser dali,');
INSERT INTO "bs_darassalam" VALUES(108,2,'108:2','pa klanjaj Gospodaru svome i kurban kolji,');
INSERT INTO "bs_darassalam" VALUES(108,3,'108:3','onaj koji tebe mrzi – sigurno će on bez spomena ostati.');
INSERT INTO "bs_darassalam" VALUES(109,1,'109:1','Reci: “O vi nevjernici,');
INSERT INTO "bs_darassalam" VALUES(109,2,'109:2','ja ne obožavam ono što vi obožavate,');
INSERT INTO "bs_darassalam" VALUES(109,3,'109:3','niti ste vi obožavaoci Onoga Koga ja obožavam,');
INSERT INTO "bs_darassalam" VALUES(109,4,'109:4','niti sam ja obožavalac onoga što vi obožavate,');
INSERT INTO "bs_darassalam" VALUES(109,5,'109:5','niti ste vi obožavaoci Onoga Koga ja obožavam,');
INSERT INTO "bs_darassalam" VALUES(109,6,'109:6','vama vaša vjera, a meni moja.');
INSERT INTO "bs_darassalam" VALUES(110,1,'110:1','Kada Allahova pomoć i pobjeda dođu,');
INSERT INTO "bs_darassalam" VALUES(110,2,'110:2','i vidiš ljude kako u skupinama u Allahovu vjeru ulaze,');
INSERT INTO "bs_darassalam" VALUES(110,3,'110:3','ti slavi i veličaj Gospodara svoga hvaleći Ga i moli Ga da ti oprosti, On,zaista, pokajanje stalno prima.');
INSERT INTO "bs_darassalam" VALUES(111,1,'111:1','Neka propadnu ruke Ebu Lehebove, i propao je!');
INSERT INTO "bs_darassalam" VALUES(111,2,'111:2','Neće mu biti od koristi imetak njegov, a ni ono što je stekao,');
INSERT INTO "bs_darassalam" VALUES(111,3,'111:3','ući će on, sigurno, u vatru rasplamsalu,');
INSERT INTO "bs_darassalam" VALUES(111,4,'111:4','i žena njegova što drvlje nosi;');
INSERT INTO "bs_darassalam" VALUES(111,5,'111:5','o vratu njenu bit će uže od ličine usukane.');
INSERT INTO "bs_darassalam" VALUES(112,1,'112:1','Reci: “On je Allah – Jedan!');
INSERT INTO "bs_darassalam" VALUES(112,2,'112:2','Allah je Es-Samed!');
INSERT INTO "bs_darassalam" VALUES(112,3,'112:3','Nije rodio i rođen nije,');
INSERT INTO "bs_darassalam" VALUES(112,4,'112:4','i niko Mu ravan nije!”');
INSERT INTO "bs_darassalam" VALUES(113,1,'113:1','Reci: “Utječem se Gospodaru svitanja,');
INSERT INTO "bs_darassalam" VALUES(113,2,'113:2','od zla onoga što On stvara.');
INSERT INTO "bs_darassalam" VALUES(113,3,'113:3','i od zla mrkle noći kada razastre tmine,');
INSERT INTO "bs_darassalam" VALUES(113,4,'113:4','i od zla onih koje u čvorove pušu,');
INSERT INTO "bs_darassalam" VALUES(113,5,'113:5','i od zla zavidnika kad zavidi."');
INSERT INTO "bs_darassalam" VALUES(114,1,'114:1','Reci: “Utječem se Gospodaru ljudi,');
INSERT INTO "bs_darassalam" VALUES(114,2,'114:2','Vladaru ljudi,');
INSERT INTO "bs_darassalam" VALUES(114,3,'114:3','Bogu ljudi,');
INSERT INTO "bs_darassalam" VALUES(114,4,'114:4','od zla šejtana – koji nanosi zle misli pa se skrije,');
INSERT INTO "bs_darassalam" VALUES(114,5,'114:5','koji zle misli unosi svijetu u grudi;');
INSERT INTO "bs_darassalam" VALUES(114,6,'114:6','od džina i od ljudi!"');
