INSERT INTO "chapters_ur" VALUES(1,'کھولنے والی',' اس کا نام ''الفاتحہ’ اس کے مضمون کی مناسبت سے ہے۔ ‘فاتحہ’ اس چیز کو کہتے ہیں جس سے کسی مضمون یا کتاب یا کسی شے کا
INSERT INTO "chapters_ur" VALUES(2,'گائے','اِس سُورۃ کا نام ” بقرة“ اِس لیے ہے کہ اس میں ایک جگہ گائے کا ذکر آیا ہے۔ قرآن کی ہر سورۃ میں اس قدر وسیع مضامین بیان ہوئے  ہیں کہ ان کے لیے مضمون کے لحاظ سے جامع عنوانات تجویز نہیں کیے جاسکتے۔ عربی زبان اگرچہ اپنی لغت کے اعتبار سے نہایت مالدار ہے ، مگر بہر حال ہے تو انسانی زبان ہی۔ انسان جو زبانیں بھی بولتا ہے وہ اس قدر تنگ اور محدود ہیں کہ وہ ایسے الفاظ یا فقرے فراہم نہیں کر سکتیں جو ان وسیع مضامین کے لیےجامع عنوان بن سکتے ہوں۔ اِس لیے نبی صلی اللہ علیہ وسلم نے اللہ تعالیٰ کی رہنمائی سے قرآن کی بیشتر سُورتوں کے لیے عنوانات کے بجائے نام تجویز فرمائے جو محض علامت کا کام دیتے ہیں ۔ اس سُورۃ کو  بقرہ کہنے کا مطلب یہ نہیں ہے کہ اس میں گائے کے مسئلے پر بحث کی گئی ہے بلکہ اس کا مطلب صرف یہ ہے کہ
INSERT INTO "chapters_ur" VALUES(3,'عمران کا خاندان',NULL,' نام: اِس سُورت میں ایک مقام”آلِ عمران“ کا ذکر آیا ہے۔ اسی کو علامت کے طور پر اس کا نام قرار دے دیا گیا ہے۔ زمانہ ٴنزول اور اجزائے مضمون: اِس میں چار تقریریں شامل ہیں : پہلی تقریر آغاز ِ سورت سے چوتھے رکوع کی ابتدائی دو آیتوں تک ہے اور وہ غالباً جنگِ بدر کے بعد قریبی زمانے ہی میں نازل ہوئی ہے۔ دُوسری تقریر آیت اِنَّ اللہَ ا صْطَفیٰٓ اٰدَمَ وَ نُوْحًا وَّ اٰلَ اِبْرٰھَیْمَ وَ اٰلَ عِمْرَانَ عَلَے العٰلَمِیْنَ (اللہ نے آدم اور نوح اور آلِ ابراھیم اور آلِ عمران کو تمام دنیا والوں پر ترجیح دے کر اپنی رسالت کے کام کے لیے منتخب کیا تھا) سے شروع ہوتی ہے اور چھٹے رکوع کے اختتام پر ختم ہوتی ہے۔ یہ سن ۹ ہجری میں وفدِ نَجران کی آمد کے موقع پر نازل ہوئی ۔ تیسری تقریر ساتویں رکوع کے آغاز سے لے کر بارھویں رکوع کے اختتام تک چلتی ہے اور اس کا زمانہ پہلی تقریر سے متصل ہی معلوم ہوتا ہے۔ چوتھی تقریر تیرھویں رکوع سے ختمِ سورت تک جنگِ اُحد کے بعد نازل ہوئی ہے۔ خطاب اور مباحث: اِن مختلف تقریروں کو ملا کر جو چیز ایک مسلسل مضمون بناتی ہے، وہ مقصد و مدّعا اور مرکزی مضمون کی یکسانیت ہے۔ سورت کا خطاب خصُوصیت کے ساتھ دو گروہوں کی طرف ہے: ایک اہلِ کتاب(یہود و نصاریٰ)۔ دُوسرے وہ لوگ جومحمد صلی اللہ علیہ وسلم پر ایمان لائے تھے۔ پہلے گروہ کو اُسی طرز پر مزید تبلیغ کی گئی ہے ۔ جس کا سلسلہ سورہٴ بقرہ میں شروع کیا گیا تھا۔ اُن کی اعتقادی گمراہیوں اور اخلاقی خرابیوں پر تنبیہ کرتے ہوئے انھیں بتایا گیا ہے کہ یہ رسول اور یہ قرآن اُسی دین کی طرف بُلا رہا ہے ، جس کی دعوت شروع سے تمام انبیا دیتے چلے آئے ہیں اور جو فطرت اللہ کے مطابق ایک ہی دین حق ہے۔ اس دین کے سیدھے رستہ سے ہٹ کر جو راہیں تم نے اختیار کی ہیں، وہ خود اُن کتابوں کی رُو سے بھی صحیح نہیں ہیں، جن کو تم کتبِ آسمانی تسلیم کرتے ہو۔ لہٰذا اس صداقت کو قبول کرو جس کے صداقت ہونے سے تم خود بھی انکار نہیں کر سکتے۔ دُوسرے گروہ کو، جواب بہترین اُمت ہونے کی حیثیت سے حق کا علمبردار اور دنیا کی اصلاح کا ذمّہ دار بنایا جا چکا ہے، اُسی سلسلے میں مزید ہدایات دی گئی ہیں ، جو سُورہٴ بقرہ میں شروع ہوا تھا۔ انہیں پچھلی اُمتوں کے مذہبی ا اخلاقی زوال کا عبرتناک نقشہ دکھا کر متنبہ کیا گیا ہے کہ ان کے نقشِ قدم پر چلنے سے بچیں۔ انہیں بتایا گیا ہے کہ ایک مصلح جماعت ہونے کی حیثیت سے وہ کس طرح کام کریں اور اُن اہلِ کتاب اور منافق مسلمانوں کے ساتھ کیا معاملہ کریں، جو خدا کے راستے میں طرح طرح سے رکاوٹیں ڈال رہے ہیں۔ انہیں اپنی اُن کمزوریوں کی اصلاح پر بھی متوجہ کیا گیا ہے، جن کا ظہُور جنگِ اُحد کے سلسلہ میں ہو ا تھا۔ اس طرح یہ سورت نہ صرف آپ اپنے مختلف اجزا میں مسلسل و مربوط ہے، بلکہ سُورہٴ بقرہ کے ساتھ بھی اس کا ایسا قریبی تعلق نظر آتا ہے کہ یہ بالکل اس کا تمتمہ معلوم ہوتی ہے اور یہ محسُوس ہوتا ہے کہ اس کا فطری مقام بقرہ سے متصل ہی ہے۔ شانِ نزُول: سُورت کا تاریخی پس منظر یہ ہے : (١) سُورہٴ بقرہ میں اِس دین ِ حق پر ایمان لانے والوں کو جن آزمائشوں اور مصائب و مشکلات سے قبل از وقت متنبہ کر دیا گیا تھا، وہ پوری شدّت کے ساتھ پیش آچکی تھیں۔ جنگِ بدر میں اگر اہلِ ایمان کو فتح حاصل ہوئی تھی ، لیکن یہ جنگ گویا بِھڑوں کے چھتّے میں پتھر مارنے کی ہم معنی تھی۔ اس اوّلین مسلَّح مقابلے نے عرب کی اُن سب طاقتوں کو چونکا دیا تھا، جو اس نئی تحریک سے عداوت رکھتی تھیں۔ ہر طرف طوفان کے آثار نمایاں ہو رہے تھے ، مسلمانوں پر ایک دائمی خوف اور بے اطمینانی کی حالت طاری تھی اور ایسا محسُوس ہوتا تھا کہ مدینے کی یہ چھوٹی سی بستی، جس نے گرد و پیش کی ساری دنیا سے لڑائی مول لے لی ہے ، صفحہٴ ہستی سے مٹا ڈالی جائے گی۔ اِ ن حالات کا مدینے کی معاشی حالت پر بھی نہایت بُرا اثر پڑ رہا تھا۔ اوّل تو ایک چھوٹے سے قصبے میں جس کی آبادی چند سو گھروں سے زیادہ نہ تھی، یکایک مہاجرین کی ایک بڑی تعداد کے آجانے ہی سے معاشی توازن بگڑ چکا تھا۔ اس پر مزید مصیبت اس حالتِ جنگ کی وجہ سے نازل ہو گئی۔ (۲) ہجرت کے بعد نبی صلی اللہ علیہ وسلم نے اطراف ِ مدینہ کے یہُودی قبائل کے ساتھ جو معاہدے کیے تھے، ان لوگوں نے اُن معاہدات کا ذرہ برابر پاس نہ کیا۔ جنگِ بدر کے موقع پر ان اہلِ کتاب کی ہمدردیاں توحید و نبوت اور کتاب و آخرت کے ماننے والے مسلمانوں کے بجائے بُت پوجنے والے مشرکین کے ساتھ تھیں۔ بدر کے بعد یہ لوگ کھلّم کھلّا قریش اور دُوسرے قبائل ِ عرب کو مسلمانوں کے خلاف جوش دِلا دِلا کر بدلہ لینے پر اُکسانے لگے۔ خصُوصاً بنی نَضِیر کے سردار کَعب بن اشرف نے تو اس سلسلے میں اپنی مخالفانہ کوششوں کو اندھی عداوت، بلکہ کمینہ پن کی حد تک پہنچا دیا۔ اہلِ مدینہ کے ساتھ ان یہودیوں کے ہمسایگی اور دوستی کے جو تعلقات صدیوں سے چلے آرہے تھے، اُن کا پاس و لحاظ بھی انہوں نے اُٹھا دیا۔ آخر کار جب اُن کی شرارتیں اور عہد شکنیاں حد ِ برداشت سے گزر گئیں، تو نبی صلی اللہ علیہ وسلم نے بدر کے چند مہینے بعد بنی قَینُقاع پر، جو ان یہودیوں میں سب سے زیادہ شریر لوگ تھے، حملہ کر دیا اور اُنہیں اِطرافِ مدینہ سے نکال باہر کیا۔ لیکن اس سے دُوسرے یہودی قبائل کی آتشِ عناداَور زیادہ بھڑک اُٹھی۔ انہوں نے مدینے کے منافق مسلمانوں اور حجاز کے مشرک قبیلوں کے ساتھ ساز باز کر کے اسلام اور مسلمانوں کے لیے ہر طرف خطرات ہی خطرات پیدا کر دیے، حتٰی کہ خود نبی صلی اللہ علیہ وسلم کی جان کے متعلق ہر وقت یہ اندیشہ رہنے لگا کہ نہ معلوم کب آپ ؐ پر قاتلانہ حملہ ہوجائے۔ صحابہ ٴ کرام اس زمانے میں بالعموم ہتھیار بند سوتے تھے۔ شبخون کے ڈر سے راتوں کو پہرے دیے جاتے تھے۔ نبی صلی اللہ علیہ وسلم اگر تھوڑی دیر کے لیے بھی کہیں نگاہوں سے اوجھل ہو جاتے تو صحابہ ٴ کرام گھبرا کر آپ ؐ کو ڈھونڈنے کے لیے نکل کھڑے ہوتے تھے۔ (۳) بد ر کی شکست کے بعد قریش کے دلوں میں آپ ہی انتقام کی آگ بھڑ ک رہی تھی کہ اس پر مزید تیل یہودیوں نے چھڑکا۔ نتیجہ یہ ہوا کہ ایک ہی سال بعد مکّے سے تین ہزار کا لشکر ِ جرار مدینے پر حملہ آور ہو گیاا ور اُحد کے دامن میں وہ لڑائی پیش آئی ، جو جنگ اُحد کے نام سے مشہور ہے۔ اس جنگ کے لیے نبی صلی اللہ علیہ وسلم کے ساتھ ایک ہزار آدمی مدینے سے نکلے تھے۔ مگر راستے میں سے تین سو منافق یکایک الگ ہو کر مدینے کی طرف پلٹ گئے۔ اور جو سات سو آدمی آپ ؐ کے ساتھ رہ گئے تھے ، ان میں بھی منافقین کی ایک چھوٹی سی پارٹی شامل رہی ، جس نے دَورانِ جنگ میں مسلمانوں کے درمیان فتنہ برپا کرنے کی ہر ممکن کوشش کی۔ یہ پہلا موقع تھا جب معلوم ہوا کہ مسلمانوں کے اپنے گھر میں اتنے کثیر التعداد مارِ آستین موجود ہیں اور وہ اس طرح باہر کے دُشمنوں کے ساتھ مل کر خود اپنے بھائی بندوں کو نقصان پہنچانے پر تُلے ہوئے ہیں۔ (۴) جنگِ اُحد میں مسلمانوں کو جو شکست ہوئی ، اس میں اگرچہ منافقین کی تدبیروں کا ایک بڑا حصّہ تھا، لیکن اس کے ساتھ مسلمانوں کی اپنی کمزوریوں کا حصّہ بھی کچھ کم نہ تھا اور یہ ایک قدرتی بات تھی کہ ایک خاص طرزِ فکر اور نظامِ اخلاق پر جو جماعت ابھی تازہ تازہ ہی بنی تھی ، جس کی اخلاقی تربیت ابھی مکمل نہ ہو سکی تھی ، اور جسے اپنے عقیدہ و مسلک کی حمایت میں لڑنے کا یہ دُوسرا ہی موقع پیش آیا تھا، اس کے کام میں بعض کمزوریوں کا ظہور بھی ہوتا۔ اس لیے یہ ضرورت پیش آئی کہ جنگ کے بعد اس جنگ کی پوری سرگزشت پر ایک مفصّل تبصرہ کیا جائے اور اس میں اسلامی نقطہ ٴ نظر سے جو کمزوریاں مسلمانوں کے اندر پائی گئی تھیں ، ان میں سے ایک ایک کی نشان دہی کر کے اس کی اصلاح کے متعلق ہدایات دی جائیں۔ اس سلسلے میں یہ بات نظر میں رکھنے کے لائق ہے کہ اس جنگ پر قرآن کا تبصرہ اُن تبصروں سے کتنا مختلف ہے، جو دُنیوی جنرل اپنی لڑائیوں کے بعد کیا کرتے ہیں۔','سید ابو اعلیٰ مودودیؒ -  تفہیم القرآن');
INSERT INTO "chapters_ur" VALUES(4,'عورت',NULL,' زمانہ ٴنزول اور اجزائے مضمون: یہ سُورہ متعدّد خطبوں پر مشتمل ہے جو غالباً سن ۳ ہجری کے اواخر سے لے کر سن ۴ ہجری کے اواخر یا سن ۵ ہجری کے اوائل تک مختلف اوقات میں نازل ہوئے ہیں۔ اگرچہ یہ تعیّن کرنا مشکل ہے کہ کس مقام سے کس مقام تک کی آیات ایک سلسلہ ٴ تقریر میں نازل ہوئی تھیں اور ان کا ٹھیک زمانہٴ نزول کیا ہے ، لیکن بعض احکام اور واقعات کی طرف بعض اشارے ایسے ہیں جن کے نزول کی تاریخیں ہمیں روایات سے معلوم ہوجاتی ہیں اس لیے ان کی مدد سے ہم ان مختلف تقریروں کی ایک سرسری سی حد بندی کر سکتےہیں جن میں یہ احکام اور یہ اشارے واقع ہوئے ہیں۔ مثلاً ہمیں معلوم ہے کہ وراثت کی تقسیم اور یتیموں کے حقوق کے متعلق ہدایات جنگِ اُحد کے بعد نازل ہوئی تھیں جب کہ مسلمانوں کے ستّر آدمی شہید ہو گئے تھے اور مدینہ کی چھوٹی سی بستی میں اس حادثے کی وجہ سے بہت سے گھر وں میں یہ سوال پیدا ہو گیا تھا کہ شہداء کی میراث کس طرح تقسیم کی جائے اور جو یتیم بچّے اُنہوں نے چھوڑے ہیں ، اُن کے مفاد کا تحفظ کیسے ہو۔ اس بنا پر ہم قیاس کر سکتے ہیں کہ ابتدائی چار رکوع اور پانچویں رکوع کی پہلی تین آیتیں اسی زمانہ میں نازل ہوئی ہوں گی۔ روایات میں صلوٰةِ خوف ( عین حالتِ جنگ میں نماز پڑھنے) کا ذکر ہمیں غزوہٴ ذات الرِقاع میں ملتا ہے جو سن ۴ ہجری میں ہوا۔ اس لیے قیاس کیا جا سکتا ہے کہ اسی کے لگ بھگ زمانہ میں وہ خطبہ نازل ہوا ہو گا جس میں اس نماز کی ترکیب بیان کی گئی ہے (رکوع ۱۵)۔ مدینہ سے بنی نَضِیر کا اخراج ربیع الاول سن ۴ ہجری میں ہوا اس لیے غالب گمان یہ ہے کہ وہ خطبہ اس سے پہلے قریبی زمانہ ہی میں نازل ہوا ہوگا جس میں یہودیوں کو آخری تنبیہ کی گئی ہے کہ ” ایمان لے آ ؤ قبل اس کے کہ ہم چہرے بگاڑ کر پیچھے پھیر دیں“۔ پانی نہ ملنے کی وجہ سے تیمم کی اجازت غزوہٴ بنی المُصْطَلِق کے موقع پر دی گئی تھی جو سن ۵ ہجری میں ہوا اس لیے وہ خطبہ جس میں تیمم کا ذکر ہے اسی سے متصل عہد کا سمجھنا چاہیے (رکوع ۷)۔ شانِ نزُول: اِس طرح بحیثیت ِ مجموعی سُورہ کا زمانہٴ نزول معلوم ہو جانے کے بعد ہمیں اس زمانہ کی تاریخ پر ایک نظر ڈال لینی چاہیے تاکہ سُورہ کے مضامین سمجھنے میں اس سے مدد لی جا سکے۔ نبی صلی اللہ علیہ وسلم کے سامنے اُس وقت جو کام تھا اُسے تین بڑے بڑے شعبوں پر تقسیم کیا جا سکتا ہے : ایک اُس نئی منظم اسلامی سوسائیٹی کا نشو نما جس کی بنا ہجرت کے ساتھ ہی مدینہ طیبہ اور اس کے اطراف و جوانب میں پڑ چکی تھی اور جس میں جاہلیت کے پُرانے طریقوں کو مٹا کر اخلاق ، تمدّن، معاشرت ، معیشت اور تدبیر مملکت کے نئے اُصُول رائج کیے جارہے تھے۔ دُوسرے اُس کشمکش کا مقابلہ جو مشرکینِ عرب، یہودی قبائل اور منافقین کی مخالف ِ اصلاح طاقتوں کے ساتھ پوری شدّت سے جاری تھی۔ تیسرے اسلام کی دعوت کو اِن مزاحم طاقتوں کے علی الرغم پھیلانا اور مزید دلوں اور دماغوں کو مسخّر کرنا۔ اللہ تعالیٰ کی جانب سے اس موقع پر جتنے خطبے نازل کیے گئے وہ سب انہی تین شعبوں سے متعلق تھے۔ اسلامی سوسائیٹی کی تنظیم کے لیے سُورہٴ بقرہ میں جو ہدایات دی گئی تھیں ، اب یہ سوسائیٹی ان سے زائد ہدایات کی طالب تھی ، اس لیے سُورہٴ نساء کے اِن خطبوں میں زیادہ تفصیل کے ساتھ بتایا گیا کہ مسلمان اپنی اجتماعی زندگی کو اسلام کے طریق پر کس طرح درست کریں۔ خاندان کی تنظیم کے اُصُول بتائے گئے۔ نکاح پر پابندیاں عائد کی گئیں۔ معاشرت میں عورت اور مرد کے تعلقات کی حد بندی کی گئی۔ یتیموں کے حقوق معیّن کیے گئے۔ وراثت کی تقسیم کا ضابطہ مقرر کیا گیا۔ معاشی معاملات کی درستی کے متعلق ہدایات دی گئیں۔ خانگی جھگڑوں کی اصلاح کا طریقہ سکھایا گیا۔ تعزیری قانون کی بنا ڈالی گئی۔ شراب نوشی پر پابندی عائد کی گئی۔ طہارت و پاکیزگی کے احکام دیے گئے۔ مسلمانوں کو بتایا گیا کہ ایک صالح انسان کا طرزِ عمل خدا اور بندوں کے ساتھ کیسا ہونا چاہیے۔ مسلمانوں کے اندر جماعتی نظم و ضبط (ڈسپلن) قائم کرنے کے متعلق ہدایات دی گئیں۔ اہلِ کتاب کے اخلاقی و مذہبی رویّہ پر تبصرہ کر کے مسلمانوں کو متنبہ کیا گیا کہ اپنی اِن پیش رَو اُمتوں کے نقشِ قدم پر چلنے سے پرہیز کریں۔ منافقین کے طرزِ عمل پر تنقید کر کے سچی ایمانداری کے مقتضیا ت واضح کیے گئے۔ اور ایمان و نفاق کے امتیازی اوصاف کو بالکل نمایاں کر کے رکھ دیا گیا۔ مخالفِ اصلاح طاقتوں سے جو کشمکش برپا تھی اُس نے جنگِ اُحد کے بعد زیادہ نازک صُورت اختیار کر لی تھی۔ اُحد کی شکست نے اطراف و نواح کے مشرک قبائل ، یہُودی ہمسایوں، اور گھر کے منافقوں کی ہمتیں بہت بڑھا دی تھیں اور مسلمان ہر طرف سے خطرات میں گھِر گئے تھے۔ ان حالات میں اللہ تعالیٰ نے ایک طرف پُر جوش خطبوں کے ذریعہ سے مسلمانوں کو مقابلہ کے لیے اُبھارا، اور دُوسری طرف جنگی حالات میں کام کرنے کے لیے انہیں مختلف ضروری ہدایات دیں۔ مدینہ میں منافق اور ضعیف الایمان لوگ ہر قسم کی خوفناک خبریں اُڑا کر بدحواسی پھیلانے کی کوشش کر رہے تھے۔ حکم دیا گیا کہ ہر ایسی خبر ذمہ دار لوگوں تک پہنچائی جائے اور جب تک وہ کسی خبر کی تحقیق نہ کر لیں اس کی اشاعت کو روکا جائے۔ مسلمانوں کو بار بار غزّوات اور سَرِیّوں میں جانا پڑتا تھا اور اکثر ایسے راستوں سے گزرنا ہوتا تھا جہاں پانی فراہم نہ ہو سکتا تھا۔ اجازت دی گئی کہ پانی نہ ملے تو غسل اور وضو دونوں کے بجائے تیمم کر لیا جائے۔ نیز ایسے حالات میں نماز مختصر کرنے کی بھی اجازت دےدی گئی اور جہاں خطرہ سر پر ہو وہاں صلوٰ ةِ خوف ادا کرنے کا طریقہ بتایا گیا۔ عرب کے مختلف علاقوں میں جو مسلمان کافر قبیلوں کے درمیان منتشر تھے اور بسا اوقات جنگ کی لپیٹ میں بھی آجاتے تھے اُن کا معاملہ مسلمانوں کے لیے سخت پریشان کُن تھا ۔ اِس مسئلہ میں ایک طرف اسلامی جماعت کو تفصیلی ہدایات دی گئیں اور دُوسری طرف اُن مسلمانوں کو بھی ہجرت پر اُبھارا گیا تاکہ وہ ہر طرف سے سمٹ کر دارالاسلام میں آجائیں۔ یہُودیوں میں سے بنی نضیر کا رویہ خصُوصیّت کے ساتھ نہایت معاندانہ ہو گیا تھا اور معاہدات کی صریح خلاف ورزی کر کے کھُلم کھُلا دشمنانِ اسلام کا ساتھ دے رہے تھے اور خود مدینہ میں محمد صلی اللہ علیہ وسلم اور آپ ؐ کی جماعت کے خلاف سازشوں کے جال بچھا رہے تھے ۔ ان کی اس روش پر سخت گرفت کی گئی اور انہیں صاف الفاظ میں آخری تنبیہ کر دی گئی۔ اس کے بعد ہی مدینہ سے اُن کا اخراج عمل میں آیا۔ منافقین کے مختلف گروہ مختلف طرزِ عمل رکھتے تھے اور مسلمانوں کے لیے یہ فیصلہ کرنا مشکل تھا کہ کس قسم کے منافقوں سے کیا معاملہ کریں۔ ان سب کو الگ الگ طبقوں میں تقسیم کر کے ہر طبقہ کے منافقوں کے متعلق بتا دیا گیا کہ ان کے ساتھ یہ برتا ؤ ہونا چاہیے۔ غیر جانبدار معاہد قبائل کے ساتھ جو رویہ مسلمانوں کا ہونا چاہیے تھا اس کو بھی واضح کیا گیا۔ سب سے زیادہ اہم چیزیہ تھی کہ مسلمان کا اپنا کیریکٹر بے داغ ہو کیونکہ اس کشمکش میں یہ مٹھی بھر جماعت اگر جیت سکتی تھی تو اپنے اخلاقِ فاضلہ ہی کے زور سے جیت سکتی تھی۔ اس لیے مسلمانوں کو بلند ترین اخلاقیات کی تعلیم دی گئی اور جو کمزوری بھی ان کی جماعت میں ظاہر ہوئی اس پر سخت گرفت کی گئی۔ دعوت و تبلیغ کا پہلو بھی اس سُورہ میں چھُوٹنے نہیں پایا ہے ۔ جاہلیّت کے مقابلہ میں اسلام جس اخلاقی و تمدّنی اصلاح کی طرف دنیا کو بُلا رہا تھا ، اس کی توضیح کرنے کے علاوہ یہُودیوں، عیسائیوں اور مشرکین، تینوں گروہوں کے غلط مذہبی تصوّرات اور غلط اخلاق و اعمال پر اس سُورہ میں تنقید کر کے ان کو دینِ حق کی طرف دعوت دی گئی ہے۔ ','سید ابو اعلیٰ مودودیؒ -  تفہیم القرآن');
INSERT INTO "chapters_ur" VALUES(5,'دسترخوان',NULL,' نام : اِس سُورہ کا نام پندرھویں رکوع کی آیت ھَلْ یَسْتَطِیْعُ رَبُّکَ اَنْ یُّنَزِّلَ عَلَیْنَا مَآ ئِدَۃً مِّنَ الَّمَآ ءِ کے لفظ ”مائدہ“ سے ماخوذ ہے۔ قرآن کی بیشتر سُورتوں کے ناموں کی طرح اس نا م کو بھی سُورۃ کے موضوع سے کوئی خاص تعلق نہیں، محض دُوسری سورتوں سے ممیز کر نے کے لیے اسے علامت کے طور پر اختیار کیا گیا ہے۔ زمانہ ٴنزول : سُورۃ کے مضامین سے ظاہر ہوتا ہے کہ اور روایات سے اس کی تصدیق ہوتی ہے کہ یہ صُلح حدیبیہ کے بعد سن ۶ ہجری کے اواخر یا سن ۷ ہجری کے اوائل میں نازل ہوئی ہے۔ ذی القعدہ سن ۶ ہجری کا واقعہ ہے کہ نبی صلی اللہ علیہ وسلم چودہ سو مسلمانوں کے ساتھ عمرہ ادا کرنے کے لیے مکہ تشریف لے گئے۔ مگر کفّارِ قریش نے عداوت کے جوش میں عرب کی قدیم ترین مذہبی روایات کے خلاف آپؐ کو عمرہ نہ کر نے دیا اور بڑی ردّو کد کے بعد یہ بات قبول کی کہ آئندہ سال آپ ؐ زیارت کے لیے آ سکتے ہیں۔ اس موقع پر ضرورت پیش آئی کہ مسلمانوں کو ایک طرف تو زیارتِ کعبہ کے لیے سفر کے آداب بتائے جائیں تاکہ آئندہ سال عمرے کا سفر پوری اسلامی شان کے ساتھ ہو سکے، اور دُوسری طرف انہیں تاکید کی جائے کہ دشمن کافروں نے ان کو عمرہ سے روک کر جو زیادتی کی ہے اس کے جواب میں وہ خود کوئی ناروا زیادتی نہ کریں، اس لیے کہ بہت سے کافر قبیلوں کے حج کا راستہ اسلامی مقبوضات سے گزرتا تھا اور مسلمانوں کے لیے یہ ممکن تھا کہ جس طرح انہیں زیارتِ کعبہ سے روکا گیا ہے اسی طرح وہ بھی ان کو روک دیں۔ یہی تقریب ہے اُس تمہیدی تقریر کی جس سے اس سُورہ کا آغاز ہوا ہے۔ آگے چل کر تیرھویں رکوع میں پھر اسی مسئلہ کو چھیڑا گیا ہے جو اس بات کا ثبوت ہے کہ پہلے رکوع سے چودھویں رکوع تک ایک ہی سلسلہ ٔ تقریر چل رہا ہے۔ ا س کے علاوہ جو دُوسرے مضامین اس سُورہ میں ہم کو ملتے ہیں وہ بھی سب کے سب اسی دَور کے معلوم ہوتے ہیں۔ بیان کے تسلسل سے غالب گمان یہی ہوتا ہے کہ یہ پوری سُورۃ ایک ہی خطبہ پر مشتمل ہے جو بیک وقت نازل ہو ا ہو گا۔ ہو سکتا ہے کہ متفرق طور پر اس کی بعض آیتیں بعد میں نازل ہوئی ہوں اور مضمون کی مناسبت سے ان کو اِس سورہ میں مختلف مقامات پر پیوست کر دیا گیا ہو، لیکن سلسلۂ بیان میں کہیں کوئی خفیف سا خلا بھی محسوس نہیں ہوتا جس سے یہ قیاس کیا جا سکے کہ یہ سُورہ دو یا تین خطبوں کا مجموعہ ہے۔ شانِ نزُول : سُورۂ آلِ عمران اور سُورہ ٔ نساء کے زمانۂ نزول سے اِس سورہ کے نزول تک پہنچتے پہنچے حالات میں بہت بڑا تغیر واقع ہو چکا تھا۔ یا تو وہ وقت تھا کہ جنگِ اُحُد کے صدمہ نے مسلمانوں کے لیے مدینہ کے قریبی ماحول کو بھی پُر خطر بنا دیا تھا، یا اب یہ وقت آگیا کہ عرب میں اسلام ایک ناقابلِ شکست طاقت نظر آنے لگا اور اسلامی ریاست ایک طرف نجد تک ، دُوسری طرف حُدُودِ شام تک ، تیسری طرف ساحلِ بحرِ احمر تک اور چوتھی طرف مکہ کے قریب تک پھیل گئی۔ اُحُد میں جو زخم مسلمانو ں نے کھایا تھا وہ ان کی ہمتیں توڑنے کے بجائے اُن کے عزم کے لیے ایک اَور تازیانہ ثابت ہوا۔ وہ زخمی شیر کی طرح بپھر کر اُٹھے اور تین سال کی مدّت میں انہوں نے نقشہ بدل کر رکھ دیا۔ ان کی مسلسل جدوجہد اور سرفروشیوں کا ثمرہ یہ تھا کہ مدینہ کے چاروں طرف ڈیڑھ ڈیڑھ دو دو سو میل تک تمام مخالف قبائل کا زور ٹوٹ گیا۔ مدینہ پر جو یہودی خطرہ ہر وقت منڈلاتا رہتا تھا اس کا ہمیشہ کے لیے استیصال ہو گیا اور حجاز میں دوسرے مقامات پر بھی جہاں جہاں یہودی آباد تھے، سب مدینہ کی حکومت کے باج گزار بن گئے ۔ اسلام کو دبانے کے لیے قریش نے آخری کوشش غزوہ ٔ خندق کے موقع پر کی اور اس میں وہ سخت ناکام ہوئے۔ اس کے بعد اہلِ عرب کو اس امر میں کچھ شک نہیں رہا کہ اسلام کی یہ تحریک اب کسی کے مٹائے نہیں مٹ سکتی ۔ اب اسلام محض ایک عقیدہ و مسلک ہی نہ تھا جس کی حکمرانی صرف دلوں اور دماغوں تک محدُود ہو، بلکہ وہ ایک ریاست بھی تھا جس کی حکمرانی عملًا اپنے حُدُود میں رہنے والے تمام لوگوں کی زندگی پر محیط تھی۔ اب مسلمان اس طاقت کے مالک ہو چکے تھے کہ جس مسلک پر وہ ایمان لائے تھے، بے روک ٹوک اس کے مطابق زندگی بسر کر سکیں اور اس کے سوا کسی دوسرے عقیدہ و مسلک یا قانون کو اپنے دائرۂ حیات میں دخل اندا ز نہ ہو نے دیں۔ پھر ان چند برسوں میں اسلامی اُصُول اور نقطہ ٔ نظر کے مطابق مسلمانوں کی اپنی ایک مستقل تہذیب بن چکی تھی جو زندگی کی تمام تفصیلات میں دُوسروں سے الگ اپنی ایک امتیازی شان رکھتی تھی۔ اخلاق، معاشرت، تمدّن، ہر چیز میں اب مسلمان غیر مسلموں سے بالکل ممیز تھے۔ تمام اسلامی مقبوضات میں مساجد اور نماز با جماعت کا نظم قائم ہوگیا تھا۔ ہر بستی اور ہر قبیلے میں امام مقرر تھے۔ اسلامی قوانین ِ دیوانی و فوجداری بڑی حد تک تفصیل کے ساتھ بن چکے تھے اور اپنی عدالتوں کے ذریعہ سے نافذ کے جا رہے تھے۔ لین دین اور خرید و فروخت کے پرانے معاملات بند اور نئے اصلاح شدہ طریقے رائج ہو چکے تھے۔ وراثت کا مستقل ضابطہ بن گیا تھا۔ نکاح اور طلاق کے قوانین، پردۂ شرعی اور استیذان کے احکام، اور زنا و قذف کی سزائیں جاری ہونے سے مسلمانوں کی معاشرتی زندگی ایک خاص سانچے میں ڈھل گئی تھی۔ مسلمانوں کی نشست و برخاست ، بول چال، کھانے پینے، وضع قطع اور رہنے سہنے کے طریقے تک اپنی ایک مستقل شکل اختیار کر چکے تھے ۔ اسلامی زندگی کی ایسی مکمل صُورت گری ہو جانے بعد غیر مسلم دُنیا اس طرف سے قطعی مایوس ہو چکی تھی کہ یہ لوگ، جن کا اپنا ایک الگ تمدّن بن چکا ہے، پھر کبھی اُن میں آملیں گے۔ صُلح حدیبیہ سے پہلے مسلمانوں کے راستہ میں ایک بڑی رکاوٹ یہ تھی کہ وہ کفارِ قریش کے ساتھ ایک مسلسل کشمکش میں اُلجھے ہوئے تھے اور انہیں اپنی دعوت کا دائرہ وسیع کرنے کی مُہلت نہ ملتی تھی۔ اِس رُکاوٹ کو حُدیبیہ کی ظاہری شکست اور حقیقی فتح نے دُور کر دیا۔ اس سے ان کو نہ صرف یہ کہ اپنی ریاست کے حُدُود میں امن میسر آگیا ، بلکہ اتنی مُہلت بھی مل گئے کہ گردوپیش کے علاقوں میں اسلام کی دعوت کے لے کر پھیل جائیں۔ چنانچہ اس کا افتتاح نبی صلی اللہ علیہ وسلم نے ایران، رُوم ، مصر او ر عرب کے بادشاہوں اور رئیسوں کو خطوط لکھ کر کیا اور اس کے ساتھ ہی قبیلوں اور قوموں میں مسلمانوں کے داعی خدا کے بندوں کو اسکے دین کی طرف بُلانے کے لیے پھیل گئے۔ مباحث : یہ حالات تھے جب سُورہ ٔ مائدہ نازل ہوئی۔ یہ سُورہ حسب ذیل تین بڑے بڑے مضامین پر مشتمل ہے: (۱) مسلمانوں کے مذہبی ، تمدنی اور سیاسی زندگی کے متعلق مزید احکام و ہدایات۔ اس سلسلہ میں سفرِ حج کے آداب مقرر کیے گئے، شعائر اللہ کے احترام اور زائرین کعبہ سے عدم تعرّض کا حکم دیا گیا ، کھانے پینے کی چیزوں میں حرام و حلال کے قطعی حُدُود قائم کیے گئے اور دَورِ جاہلیت کی خود ساختہ بندشوں کو توڑ دیا گیا، اہلِ کتاب کے ساتھ کھانے پینے اور ان کی عورتوں سے نکاح کرنے کی اجازت دی گئی، وضو اور غسل اور تیمم کے قاعدے مقرر کیے گئے، بغاوت اور فساد اور سرقہ کی سزائیں معین کی گئیں ، شراب اور جُوئے کو قطعی حرام کر دیا گیا، قسم توڑنے کا کفارہ مقرر کیا گیا، اور قانونِ شہادت میں مزید چند دفعات کا اضافہ کیا گیا۔ (۲) مسلمانوں کو نصیحت۔ اب چونکہ مسلمان ایک حکمران گروہ بن چکے تھے ، ان کے ہاتھ میں طاقت تھی، جس کا نشہ قوموں کے لیے اکثر گمراہی کا سبب بنتا رہا ہے ، مظلومی کا دَورخاتمہ پر تھا اور اس سے زیادہ سخت آزمائش کے دَور میں وہ قدم رکھ رہے تھے ، اس لیے ان کو خطاب کر تے ہوئے بار بار نصیحت کی گئی کہ عدل پر قائم رہیں، اپنے پیش رَو اہلِ کتاب کی روش سے بچیں ، اللہ کی اطاعت و فرماں برداری اور اس کےاحکام کی پیروی کا جو عہد انہوں نے کیا ہے اس پر ثابت قدم رہیں اور یہود و نصاریٰ کی طرح اس کو توڑ کر اُس انجام سے دوچار نہ ہوں جس سے وہ دوچار ہوئے۔ اپنے جملہ معاملات کے فیصلوں میں کتابِ الہٰی کے پابند رہیں ، اور منافقت کی روش سے اجتناب کریں۔ (۳) یہودیوں اورعیسائیوں کو نصیحت ۔ یہُودیوں کا زور اب ٹوٹ چکا تھا اور شمالی عرب کے تقریبًا تمام یہُودی بستیاں مسلمانوں کے زیرِ نگیں آگئی تھیں۔ اِس موقع پر ان کو ایک بار پھر ان کے غلط رویہ پر متنبہ کیا گیا ہے اور انہیں راہِ راست پر آنے کی دعوت دی گئی ہے۔ نیز چونکہ صُلح حدیبیہ کی وجہ سے عرب اور متصل ممالک کی قوموں میں اسلام کی دعوت پھیلانے کا موقع نِکل آیاتھا ا س لیے عیسائیوں کو بھی تفصیل کے ساتھ خطاب کر کے ان کے عقائد کی غلطیاں بتائی گئی ہیں اور انہیں نبی ِ عربی پر ایمان لانے کی دعوت دی گئی ہے۔ ہمسایہ ممالک میں سے جو قومیں بُت پرستی اور مجوسی تھیں ان کو براہِ راست خطاب نہیں کیا گیا ، کیونکہ اُن کی ہدایت کے لیے وہ خطبات کافی تھے جو اُن کے ہم مسلک مشرکینِ عرب کو خطاب کرتے ہوئے مکّہ میں نازل ہو چکے تھے۔','سید ابو اعلیٰ مودودیؒ -  تفہیم القرآن');
INSERT INTO "chapters_ur" VALUES(6,'مویشی',NULL,' نام : اِس سُورہ کے رکوع ۱۶ و ۱۷ میں بعض اَنعام (مویشیوں) کی حُرمت اور بعض کی حِلّت کے متعلق اہلِ عرب کے توہّمات کی تردید کی گئی ہے۔ اِسی مناسبت سے اس کا نام ”اَلانعام“ رکھا گیا ہے۔ زمانہ ٴنزول : اِبنِ عباسؓ کی روایت ہے کہ پُوری سُورہ مکہ میں بیک وقت نازل ہوئی تھی۔ حضرت مُعاذ بن جَبَل کی چچا زاد بہن اَسماء بنت یزید کہتی ہیں کہ ”جب یہ سُورۃ نبی صلی اللہ علیہ وسلم پر نازل ہو رہی تھی اس وقت آپ اُونٹنی پر سوا ر تھے، میں ا س کی نکیل پکڑے ہوئے تھی اور بوجھ کے مارے اونٹنی کا یہ حال ہو رہا تھا کہ معلوم ہوتا تھا کہ اس کی ہڈیاں اب ٹوٹ جائیں گی“۔ روایات میں اس کی بھی تصریح ہے کہ جس رات یہ نازل ہوئی اسی رات کو آپ نے اسے قلمبند کرا دیا۔ اس کے مضامین پر غور کرنے سے صاف معلوم ہوتا ہے کہ یہ سُورۃ مکّی دَور کے آخری زمانہ میں نازل ہوئی ہو گی۔ حضرت اَسماء بنت یزید کی روایت بھی اسی کی تصدیق کرتی ہے۔ کیونکہ موصوفہ انصار میں سے تھیں اور ہجرت کے بعد ایمان لائیں۔ اگر قبولِ اسلام سے پہلے محض بر بناء عقیدت وہ نبی صلی اللہ علیہ وسلم کی خدمت میں مکّہ حاضر ہوئی ہوں گی تو یقینًا یہ حاضری آپ کی مکّی زندگی کے آخری سال ہی میں ہوئی ہو گی ۔ ا س سے پہلے اہلِ یثرب کے ساتھ آپ کے تعلقات اتنے بڑھے ہی نہ تھے کہ وہاں سے کسی عورت کا آپ کی خدمت میں حاضر ہونا ممکن ہوتا۔ شانِ نزُول: ”زمانہ ٔ نزول متعیّن ہو جا نے کے بعد ہم بآسانی اس پَس منظر کو دیکھ سکتے ہیں جس میں یہ خطبہ ارشاد ہو ا ہے۔ اس وقت اللہ کے رسول کو اسلام کی طرف دعوت دیتے ہوئے بارہ سال گزر چکے تھے۔ قریش کی مزاحمت اور ستم گری و جفاکاری انتہا کو پہنچ چکی تھی ۔ اسلام قبول کر نے والوں کی ایک بڑی تعداد ان کے ظلم و ستم سے عاجز آکر ملک چھوڑ چکی تھی اور حَبَش میں مقیم تھی۔ نبی صلی اللہ علیہ وسلم کی تائد و حمایت کے لیے نہ ابو طالب باقی رہے تھے اور نہ حضرت خَدِیجہ ، اس لیے ہر دُنیوی سہارے سے محروم ہو کر آ پ شدید مزاحمتوں کے مقابلہ میں تبلیغِ رسالت کا فرض انجام دے رہے تھے۔ آپ کی تبلیغ کے اثر سے مکّہ میں اور گرد و نواح کے قبائل میں بھی صالح افراد پے در پے اسلام قبول کر تے جا رہے تھے ، لیکن قوم بحییثیتِ مجموعی ردو انکار پر تُلی ہوئی تھی۔ جہاں کوئی شخص اسلام کی طرف ادنیٰ میلان بھی ظاہر کرتا تھا اسے طعن و ملامت ، جسمانی اذیّت اور معاشی و معاشرتی مقاطعہ کا ہدف بننا پڑتا تھا ۔ اس تاریک ماحول میں صرف ایک ہلکی سی شعاع یثرب کی طرف نمودار ہوئی تھی جہاں سے اَوس اور خَزرج کے با اثر لوگ آ کر نبی صلی اللہ علیہ وسلم کے ہاتھ پر بیعت کر چکے تھے اور جہاں کسی اندرونی مزاحمت کے بغیر اسلام پھیلنا شروع ہو گیا تھا۔ مگر اس حقیر سی ابتداء میں مستقبل کے جو امکانات پوشدہ تھے انھیں کوئی ظاہر بیں آنکھ نہ دیکھ سکتی تھی۔ بظاہر دیکھنے والوں کو جو کچھ نظر آتا تھا وہ بس یہ تھا کہ اسلام ایک کمزور سی تحریک ہے جس کی پُشت پر کوئی مادّی طاقت نہیں ، جس کا داعی اپنے خاندان کی ضعیف سی حمایت کے سوا کوئی زور نہیں رکھتا ، اور جسے قبول کرنے والے چند مٹھی بھر بے بس اور منتشر افراد اپنی قوم کے عقیدہ و مسلک سے منحرف ہو کر اس طرح سوسائیٹی سے نکال پھینکے گئے ہیں جیسے پتّے اپنے درخت سے جھڑ کر زمین پر پھیل جائیں۔ مباحث : اِن حالات میں یہ خطبہ ارشاد ہوا ہے اور اس کے مضامین کو سات بڑے بڑے عنوانات پر تقسیم کیا جا سکتا ہے: (۱) شرک کا ابطال اور عقیدہ ٔ توحید کی طرف دعوت، (۲) عقیدۂ آخرت کی تبلیغ اور اس غلط خیا ل کی تردید کہ زندگی جو کچھ ہے بس یہی دنیا کی زندگی ہے، (۳) جاہلیت کے اُن توہّمات کی تردید جن میں لوگ مبتلا تھے، (۴) اُن بڑے بڑے اُصُولِ اخلاق کی تلقین جن پر اسلام سوسائیٹی کی تعمیر کرنا چاہتا تھا، (۵) نبی صلی اللہ علیہ وسلم اور آپ کی دعوت کےخلاف لوگوں کے اعتراضات کا جواب، (۶) طویل جدوجہد کے باوجود دعوت کے نتیجہ خیز نہ ہونے پر آنحضرت صلی اللہ علیہ وسلم اور عام مسلمانوں کے اندر اضطراب اور دل شکستگی کی جو کیفیت پیدا ہو رہی تھی اس پر تسلی، (۷) منکرین اور مخالفین کو ان کی غفلت و سرشاری اور نادانستہ خود کُشی پر نصیحت، تنبیہ اور تہدید۔ لیکن خطبہ کا انداز یہ نہیں ہے کہ ایک ایک عنوان پرالگ الگ یکجا گفتگو کی گئی ہو۔ بلکہ خطبہ ایک دریا کی سی روانی کے ساتھ چلتا جاتا ہے اور اس کے دَوران میں یہ عنوانات مختلف طریقوں سے بار بار چھڑتے ہیں اور ہر بار ایک نئے انداز سے ان پر گفتگو کی جاتی ہے۔ مکی زندگی کے اَدوار : یہاں چونکہ پہلی مرتبہ نظرین کے سامنے ایک مفصل مکّی سُورہ آرہی ہے اس لیے مناسب معلوم ہوتا ہے کہ اس مقام پر ہم مکّی سورتوں کے تاریخی پس منظر کی ایک جامع تشریح کردیں تاکہ آئندہ تمام مکّی سُورتوں کو ، اور ان کی تفسیر کے سلسلہ میں ہمارے اشارات کو سمجھنا آسان ہو جائے۔ جہاں تک مَدنی سُورتوں کا تعلق ہے، ان میں سے تو قریب قریب ہر ایک کا زمانہ ٔ نزول معلوم ہے یا تھوڑی سی کاوش سے متعین کیا جا سکتا ہے۔ بلکہ ان کی تو بکثرت آیتوں کی انفرادی شانِ نزول تک معتبر روایات میں مل جاتی ہے۔ لیکن مکّی سُورتوں کے متعلّق ہمارے پاس اتنے مفصل ذرائع معلومات موجود نہیں ہیں ۔ بہت کم سُورتیں یا آیتیں ایسی ہیں جن کے زمانۂ نزول اور موقعِ نزول کے بارے میں کوئی صحیح و معتبر روایت ملتی ہو۔ کیونکہ اُس زمانہ کی تاریخ اس قدر جزئی تفصیلات کے ساتھ مرتب نہیں ہوئی ہے جیسی کہ مَدنی دَور کی تاریخ ہے۔ اس وجہ سے مکّی سُورتوں کے معاملہ میں ہم کو تاریخی شہادتوں کے بجائے زیادہ تر اُن اندرونی شہادتوں پر اعتماد کرنا پڑتا ہے جو مختلف سُورتوں کے موضوع، مضمون اور اندازِ بیان میں، اور اپنے پس منظر کی طرف اُن کے جلی یا خفی اشارات میں پائی جاتی ہیں۔ اور ظاہر ہے کہ اِس نوعیت کی شہادتوں سے مدد لے کر ایک ایک سورة اور ایک ایک آیت کے متعلق یہ تعیّن نہیں کیا جا سکتا کہ یہ فلاں تاریخ کو، یا فلاں سنہ میں فلاں موقع پر نازل ہو ئی ہے۔ زیادہ صحت کے ساتھ جو کچھ کیا جا سکتا ہے وہ صرف یہ ہے کہ ایک طرف ہم مکّی سُورتوں کی اندروی شہادتوں کو، اور دُوسری طرف نبی صلی اللہ علیہ وسلم کی مکّی زندگی کی تاریخ کو آمنے سامنے رکھیں اور پھر دونوں کا تقابل کرتے ہوئے یہ رائے قائم کریں کہ کون سی سُورۃ کس دَور سے تعلق رکھتی ہے۔ اِس طرزِ تحقیق کو ذہن میں رکھ کر جب ہم نبی صلی اللہ علیہ وسلم کی مکّی زندگی پر نگاہ ڈالتے ہیں تو وہ دعو تِ اسلامی کے نقطۂ نظر سے ہم کو چار بڑے بڑے نمایاں ادوار پر منقسم نظر آتی ہے:پہلا دَور ، آغازِ بعثت سے لے کر اعلانِ نبوت تک، تقریبًا ۳ سال ، جس میں دعوت خفیہ طور سے خاص خاص آدمیوں کو دی جارہی تھی اور عام اہلِ مکّہ کو اس کا علم نہ تھا۔ دوسرا دَور ، اعلانِ نبوت سے لےکر ظلم و ستم اور فتنہ (Persecution) کے آغاز تک ، تقریبًا ۲ سال ، جس میں پہلے مخالفت شروع ہوئی ، پھر اس نے مزاحمت کی شکل اختیار کی، پھر تضحیک، استہزاء، الزامات ، سبّ و شتم، جُھوٹے پروپیگنڈا اور مخالفانہ جتھہ بندی تک نوبت پہنچی، اور بالآخر ان مسلمانوں پر زیادتیاں شروع ہو گئیں جو نسبتًہ زیادہ غریب ، کمزور اور بے یار و مددگار تھے۔تیسرا دَور ، آغازِ فتنہ(سن ۵ نبویؐ) سے لے کر ابو طالب اور حضرت خدیجہؓ کی وفات (سن۱۰ نبویؐ) تک، تقریبًا پانچ چھ سال ۔ اس میں مخالفت انتہائی شدت اختیار کرتی چلی گئی، بہت سے مسلمان کفارِ مکہ کے ظلم و ستم سے تنگ آکر حبش کی طرف ہجرت کر گئے، نبی صلی اللہ علیہ وسلم اور آپ کے خاندان اور باقی ماندہ مسلمانوں کا معاشی و معاشرتی مقاطعہ کیا گیا اور آپ اپنے حامیوں اور ساتھیوں سمیت شعبِ ابی طالب میں محصور کر دیے گئے۔ چوتھا دَور ، سن ۱۰ نبویؐ سے لے کر سن ۱۳ نبویؐ تک تقریبًا ۳ سال ۔ یہ نبی صلی اللہ علیہ وسلم اور آپ کے ساتھیوں کے لیے انتہائی سختی و مصیبت کا زمانہ تھا۔ مکّہ میں آپ کے لیے زندگی دوبھر کر دی گئی تھی، طائف گئے تو وہاں بھی پناہ نہ ملی، حج کے موقع پر عرب کے ایک ایک قبیلہ سے آپ اپیل کرتے رہے کہ وہ آپؐ کی دعوت قبول کرے اور آپؐ کا ساتھ دے مگر ہر طرف سے کورا جواب ہی ملتا رہا۔ اور ادھر اہلِ مکّہ بار بار یہ مشورے کرتے رہے کہ آپؐ کو قتل کردیں یا قید کردیں یا اپنی بستی سے نکال دیں۔آخر کار اللہ کے فضل سے انصار کے دل اسلام کے لیے کھل گئے اور انکی دعوت پر آپؐ نے مدینہ کی طرف ہجرت فرمائی۔ ان میں سےہر دور میں قرآن مجید کی سُورتیں نازل ہوئی ہیں۔ وہ اپنے مضامین اور اندازِ بیان میں دُوسرے دور کی سُورتوں سے مختلف ہیں۔ ان میں بکثرت مقامات پر ایسے اشارات بھی پائے جاتے ہیں جن سے پسِ منظر کے حالات اور واقعات پر صاف روشنی پڑتی ہے۔ ہر دور کی خصوصیات کا اثر اُس دور کے نازل شدہ کلام میں بہت بڑی حد تک نمایاں نظر آتا ہے۔ انہی علامات پر اعتماد کر کے ہم آئندہ ہر مکّی سُورہ کے دیباچہ میں یہ بتائیں گے کہ وہ مکّہ کے کس دور میں نازل ہوئی ہے۔','سید ابو اعلیٰ مودودیؒ -  تفہیم القرآن');
INSERT INTO "chapters_ur" VALUES(7,'اونچی جگہ',NULL,' نام : اس سورہ کا نام اَعراف اس لیے رکھا گیا ہے کہ ا س کی آیات ۴٦۔۴۷ میں اعراف اور اصحاب کا ذکر آیا ہے۔ گویا اسے”سورہ اَعراف“ کہنے کا مطلب یہ ہے کہ”وہ سورہ جس میں اَعراف کا ذکر ہے۔“ زمانہ ٴنزول: اس کے مضامین پر غور کرنے سے بیّن طور پر محسوس ہوتا ہے کہ اس کا زمانہ نزول تقریباً وہی ہے جو سورہ انعام کا ہے ۔ یہ بات تو یقین ساتھ نہیں کہی جا سکتی کہ یہ پہلے نازل ہوئی ہے یا وہ ۔ مگر اندازِ تقریر سے صاف ظاہر ہو رہا ہے کہ ہے یہ اُسی دَور سے متعلق۔ لہذٰااس کے تاریخی پس منظر کو سمجھنے کے لیے اس دیباچہ پر ایک نگاہ ڈال لینا کافی ہوگا جو ہم نے سورہ انعام پر لکھا ہے ۔ مباحث: اس سورہ کی تقریر کا مرکزی مضمون دعوت رسالت ہے ساری گفتگو کا مدّعا یہ ہے کہ مخاطبوں کو خدا کے فرستادہ پیغمبر کی پیروی اختیار کرنے پر آمادہ کیا جائے۔ لیکن اس دعوت میں اِنذار(تنبیہ اور ڈرادے) کا رنگ زیادہ نمایا پایا جاتا ہے، کیونکہ جو لوگ مخاطب ہیں،(یعنی اہلِ مکّہ)انہیں سمجھاتے سمجھاتے ایک طویل زمانہ گزر چکا ہے اور اُن کی گراں گوشی، ہٹ دھرمی اور مخالفانہ ضد اس حد کو پہنچ چکی ہے کہ عنقریب پیغمبر کو اُن سے مخاطبہ بند کر کے دوسروں کی طرف رجوع کرنے کا حکم ملنے والا ہے۔ اس لیے تفہیمی انداز میں قبول رِسالت کی دعوت دینے کے ساتھ ان کو یہ بھی بتایا جا رہا ہے کہ جو روش تم نے اپنے پیغمبر کے مقابلہ میں اختیار کر رکھی ہے ایسی ہی روش تم سے پہلے کی قومیں اپنے پیغمبروں کے مقابلہ میں اختیار کر کے بہت بُرا انجام دیکھ چکی ہیں۔ پھر چونکہ ان پر حجت تمام ہونے کے قریب آگئی ہے اس لیے تقریر کے آخری حصّہ میں دعوت کا رُخ اُن سے ہٹ کر اہلِ کتاب کی طرف پھر گیا ہے اور ایک جگہ تمام دنیا کے لوگوں سے عام خطاب بھی کیا گیا ہے جو اس بات کی علامت ہے کہ اب ہجرت قریب ہے اور وہ دَور جس میں نبی کا خطاب تمام تر اپنے قریب کے لوگوں سے ہوا کرتا ہے، خاتمہ پر آلگا ہے۔ دورانِ تقری میں چونکہ خطاب کا رُخ پہود کی طرف بھی پھر گیا ہے اس لیے ساتھ ساتھ دعوتِ رسالت کے اِ س پہلو کو بھی واضح کر دیا گیا ہے کہ پیغمبر پر ایمان لانے کے بعد اُس کے ساتھ منافقانہ روش اختیا کرنے ، اور سمع و طاعت کا عہد استوار کرنے بعد اُسے توڑ دینے ، اور حق اور باطل کی تمیز سے واقف ہو جانے کے نعد باطل پرستی میں مستفرق رہنے کا انجام کیا ہے۔ سورہ کے آخر میں نبی صلی اللہ علیہ وسلم اور آپ کے صحابہ کو حکمت ِ تبلیغ کے متعلق چند اہم ہدایات دی گئی ہیں اور خصوصیّت کے ساتھ انہیں نصیحت کی گئی ہے کہ مخالفین کی اشتعال انگیزیوں اور چیرہ دستیوں کے مقابلہ میں صبر و ضبط سے کام لیں اور جذبات کے ہیجان میں مبتلا ہو کر کوئی ایسا اقدام نہ کریں جو اصل مقصد کو نقصان پہنچانے والا ہو۔ ','سید ابو اعلیٰ مودودیؒ -  تفہیم القرآن');
INSERT INTO "chapters_ur" VALUES(8,'مال غنیمت',NULL,' زمانہ ٴنزول: یہ سورہ سن ۲ ہجری میں جنگ بدر کے بعد نازل ہوئی ہے اور اس میں اسلام و کفر کی اس پہلی جنگ پر مفصل تبصرہ کیا گیا ہے۔ جہاں تک سورہ کے مضمون پر غور کرنے سے اندازہ ہوتا ہے، غالباً یہ ایک ہی تقریر ہے جو بیک وقت نازل فرمائی گئی ہوگی، مگر ممکن ہے کہ اس کی بعض آیات جنگ بدر ہی سے پیدا شدہ مسائل کے متعلق بعد میں اتری ہوں اور پھر ان کو سلسلہ تقریر میں مناسب جگہوں پر درج کر کے ایک مسلسل تقریر بنا دیا گیا ہو۔ بہر حال کلام میں کہیں کوئی ایسا جوڑ نظر نہیں آتا جس سے یہ گمان کیا جا سکے کہ یہ الگ الگ دو تین خطبوں کا مجموعہ ہے۔ تاریخی پس منظر: قبل اس کے کہ اس سورہ پر تبصرہ کیا جائے، جنگ بدر اور اس سے تعلق رکھنے والے حالات پر ایک تاریخی نگاہ ڈال لینی چاہیے۔ نبی صلی اللہ علیہ وسلم کی دعوت ابتدائی دس بارہ سال میں، جبکہ آپ مکہ معظمہ میں مقیم تھے، اس حیثیت سےا پنی پختگی و استواری ثابت کر چکی تھی کہ ایک طرف اس کی پشت پر ایک بلند سیرت، عالی ظرف اور دانشمند علمبردار موجود تھا جو اپنی شخصیت کا پورا سرمایہ اس کام میں لگا چکا تھا اوراس کے طرزِ عمل سے یہ حقیقت پوری طرح نمایاں ہو چکی تھی کہ وہ اس دعوت کو انتہائی کامیابی کی منزل تک پہنچانے کے لیے اٹل ارادہ رکھتا ہے اور اس مقصد کی راہ میں ہر خطرے کو انگیز کرنے اور ہر مشکل کا مقابلہ کرنے کے لیے تیار ہے۔ دوسری طرف اس دعوت میں خود ایسی کشش تھی کہ وہ دلوں اور دماغوں میں سرایت کرتی چلی جارہی تھی اور جہالت و جاہلیت اور تعصبات کے حصار اس کی راہ روکنے میں نا کا م ثابت ہو رہے تھے۔ اسی وجہ سے عرب کے پرانے نظام جاہلی کی حمایت کرنے والے عناصر، جو ابتداءً اس کو استخفاف کی نظر سے دیکھتے تھے، مکی دور کے آخری زمانہ میں اسے ایک سنجیدہ خطرہ سمجھنے لگے تھے اور اپنا پورا زور اسے کچل دینے میں صرف کر دینا چاہتے تھے۔ لیکن اُس وقت تک چند حیثیات سےاس دعوت میں بہت کچھ کسر باقی تھی: اوّلاً، یہ بات ابھی پوری طرح ثابت نہیں ہوئی تھی کہ اس کو ایسے پیرووں کی ایک کافی تعداد بہم پہنچ گئی ہے جو صرف اس کے ماننے والے ہی نہیں ہیں، بلکہ س کے اصولوں کا سچا عشق بھی رکھتے ہیں، اس کو غالب و نافذ کرنے کی سعی میں اپنی ساری قوتیں اور اپنا تمام سرمایہ زندگی کھپا دینے کے لیے تیار ہیں، اور اس کی خاطر اپنی ہر چیز قربان کر دینے کے لیے ، دنیا بھر سے لڑ جانے کے لیے ، حتیٰ کہ اپنے عزیز ترین رشتوں کو بھی کاٹ پھینکنے کے لیے آمادہ ہیں۔ اگرچہ مکہ میں پیر وانِ اسلام نے قریش کے ظلم و ستم بر داشت کر کے اپنی صداقتِ ایمانی اور اسلام کے ساتھ اپنے تعلق کی مضبوطی کا اچھا خاصا ثبوت دے دیا تھا، مگر ابھی یہ ثابت ہونے کے لیے بہت سی آزمائشیں باقی تھیں کہ دعوت ِ اسلامی کو جانفروش پیرووں کا وہ گروہ میسر آگیا ہے جو اپنے نصب العین کے مقابلہ میں کسی چیز کو بھی عزیز تر نہیں رکھتا۔ ثانیاً، اس دعوت کی آواز اگر چہ سارے ملک میں پھیل گئی تھی، لیک اس کے اثرات منتشر تھے، اس کی فراہم کردہ قوت سارے ملک میں پر اگندہ تھی، اس کو وہ اجتماعی طاقت بہم نہ پہنچی تھی جو پرانے جمے ہوئے نظام جاہلیت سے فیصلہ کن مقابلہ کرنے کے لیے ضروری تھی۔ ثالثاً، اس دعوت نے زمین میں کسی جگہ بھی جڑ نہیں پکڑی تھی بلکہ ابھی تک وہ صرف ہوا میں سرایت کر رہی تھی۔ ملک کا کوئی خطہ ایسا نہیں تھا جہاں وہ قدم جما کر اپنے موقف کو مضبوط کرتی اور پھر آگے بڑھنے کی سعی کرتی ، اُس وقت تک جو مسلمان جہاں بھی تھا اس کی حیثیت نظام کفر شرک میں بالکل ایسی تھی جیسے خالی معدے میں کُنِین ، کہ معدہ ہر وقت اسے اُگل دینے کے یے زور لگا رہا ہو اور قرار پکڑنے کے لیے اس کو جگہ ہی نہ ملتی ہو۔ رابعاً، اس وقت تک اس دعوت کو عملی زندگی کے معاملات اپنے ہاتھ میں لے کر چلانے کا موقع نہیں ملا تھا۔ نہ یہ اپنا تمدن قائم کر سکی تھی، نہ اس نے اپنا نظامِ معیشت و معاشرت اور نظام سیاست مرتب کیا تھا اور نہ دوسری طاقتوں سے اس کے معاملاتِ صلح و جنگ پیش آئے تھے۔ اس لیے نہ تو اُن اخلاقی اصولوں کا مظاہرہ ہو سکا تھا جن پر یہ دعوت زندگی کے پورے نظام کو قائم کرنا اور چلانا چاہتی تھی، اور نہ یہی بات آزمائش کی کسوٹی پر اچھی طرح نمایاں ہوئی تھی کہ اس دعوت کا پیغمبر اور اس کے پیرووں کا گروہ جس چیز کی طرف دنیا کو دعوت دے رہا ہے اس پر عمل کرنے میں وہ خود کس حد تک راستباز ہے۔ بعد کے واقعات نے وہ مواقع پیدا کر دیے جن سے یہ چاروں کمیاں پوری ہو گئیں۔ مکی دور کے آخری تین چار سالوں سے یثرب میں آفتابِ اسلام کی شعاعیں مسلسل پہنچ رہی تھیں اور وہاں کے لوگ متعدد وجوہ سے عرب کے دوسرے قبیلوں کی بہ نسبت زیادہ آسانی کے ساتھ اس روشنی کو قبول کرنے فا رہے تھے۔ آخر کار نبوت کے بارھویں سال حج کے موقع پر ۷۵ نفوس کا ایک وفد نبی صلی اللہ علیہ وسلم سے رات کی تاریکی میں ملا اور اس نے نہ صرف یہ کہ اسلام قبول کیا بلکہ آپ کو اور آپ کے پیرووں کو اپنے شہر میں جگہ دینے پر بھی آمادگی ظاہر کی۔ یہ اسلام کی تاریخ میں ایک انقلابی موقع تھا جسے خدا نے اپنی عنایت سے فراہم کیا اور نبی صلی اللہ علیہ وسلم نے ہاتھ بڑھا کر پکڑ لیا ۔ اہلِ یثرب نبی صلی اللہ علیہ وسلم کو محض ایک پناہ گزیں کی حیثیت سے نہیں بلکہ خدا کے نائب اور اپنے امام و فرمانروا کی حیثیت سے بلا رہے تھے۔ اور اسلام کے پیرووں کوان کا بلاوا اس لیے نہ تھا کہ وہ ایک اجنبی سر زمین میں محض مہاجر ہونے کی حیثیت سے جگہ پا لیں، بلکہ مقصد یہ تھا کہ عرب کے مختلف قبائل اور خطوں میں جو مسلمان منتشر ہیں وہ یثرب میں جمع ہو کر اور یثربی مسلمانوں کے ساتھ مل کر ایک منظم معاشرہ بنا لیں۔ اس طرح یثرب نے در اصل اپنے آپ کو ”مدینةالاسلام“کی حیثیت سے پیش کیا اور نبی صلی اللہ علیہ وسلم نے اسے قبول کر کے عرب میں پہلا وار الاسلام بنا لیا۔ اس پیش کش کے معنی جو کچھ تھے اس سے اہل مدینہ نا واقف نہ تھے۔ اس کے صاف معنی یہ تھے کہ ایک چھوٹا سا قصبہ اپنے آپ کو پرے ملک کی تلواروں اور معاشی و تمدنی بائیکاٹ کے مقابلہ میں پیش کر رہا تھا۔ چنا نچہ بیعتِ عَقَبہ کے موقع پر رات کی اُس مجلس میں اسلام کے ان اولین مددگاروں (انصار) نے اس نتیجہ کو خوب اچھی طرح جان بوجھ کر نبی صلی اللہ علیہ وسلم کے ہاتھ میں ہاتھ دیا تھا۔ عین اس وقت جبکہ بیعت ہو رہی تھی، یثربی وفد کے ایک نوجوان رکن اسعد بن زُرارہ ؓ نے ، جو پورے وفد میں سب سے کم سن شخص تھے، اُٹھ کر کہا:۔ رویدًایا اھل یثرب!اِنا لم نضرب الیہ اکبادِ الا بل الا و نحن نعلم انہ رسول اللہ، و ان اخراجہ الیوم مناوأة للعرب کافة، وقتل خیارکم، وتضکم السیوف۔ فاما انتم قوم تصبرون علیٰ ذٰلک فخذوہ و اجرہ علی اللہ، واما انتم قوم تخافون من انفسکم خیفة فذروہ فبینو ا ذٰلک فھوا عذرلکم عند اللہ۔ ”ٹھیرو اے اہل یثرب ہم لوگ جو ان کے پاس آئے ہیں تو یہ سمجھتے ہوئے آئے ہیں کہ یہ اللہ کے رسول ہیں اور آج انھیں یہاں سے نکال کر لے جانا تمام عرب سے دشمنی مول لینا ہے۔ اس کے نتیجہ میں تمہارے نونہال قتل ہوں گے اور تلواریں تم پر بر سینگی۔ لہٰذا اگر تمہیں اپنی جانیں عزیز ہیں تو پھر چھوڑ دو اور صاف صاف عذر کر دو کیونکہ اس وقت عذر کر دینا خدا کے نزدیک زیادہ قابل قبول ہو سکتا ہے۔“ اسی بات کو وفد کے ایک دوسرے شخص عباس بن عُبادہ بن نَضلَہ نے دوہرایا: اتعلمون علام تبایعون ھٰذا الرجل؟(قالو انعم، قال)انکم تبایعونہ علی حزب الاحمر و الاسود من الناس۔ فان کنتم ترون انکم اذا نھکت اموالکم مصیبة واشرافکم قتلا المتموہ فمن الاٰن فدعوہ، فھوواللہ ان فعلتم خزی الدنیا والاٰخرة و ان کنتم ترون انکم و افون لہ بنا دعو تموہ الیہ علی نھکة الاموال وقتل الاشراف فخذوہ، فھو و اللہ خیر الدنیا و الاٰخرة۔ ”جانتے ہو اس شخص سے کس چیز پر بیعت کر رہے ہو؟ (آوازیں، ہاں جانتے ہیں)تم اس کے ہاتھ پر بیعت کر کے دنیا بھر سے لڑائی مول لے رہے ہو۔ پس اگر تمہارا خیال یہ ہو کہ جب تمہارے مال تباہی کے اور تمہارے اشراف ہلاکت کے خطرے میں پڑجائیں تو تم اسے دشمنوں کے حوالے کر دو گے تو بہتر ہے کہ آج ہی سے اسے چھوڑ دو کیونکہ خدا کی قسم یہ دنیا اور آخرت کی رسوائی ہے۔ اور اگر تمہارا ارادہ یہ ہے کہ جو بلاوا تم اس شخص کو دے رہے ہو اس کو اپنے اموال کی تباہی اور اپنے اشراف کی ہلاکت کے باوجود تبا ہو گے تو بے شک اس کا ہاتھ تھام لو کہ خدا کی قسم یہ دنیا اور آخرت کی بھلائی ہے۔“ اس پر تمام وفد نے بالا تفاق کہا فا نا ناخذہ علیٰ مصیبة الاموال وقتل الاشراف۔ ”ہم اسے لے کر اپنے اموال کو تباہی اور اپنے اشراف کو ہلاکت کے خطرے میں ڈالنے کے لیے تیار ہیں“۔ تب وہ مشہور بیعت واقع ہوئی جسے تاریخ میں بیعت عَقَبہ ثانیہ کہتے ہیں۔ دوسری طرف اہل مکہ کے لیے یہ معاملہ جو معنی رکھتا تھا۔ وہ بھی کسی سے پوشیدہ نہ تھا۔ دراصل اس طرح محمد صلی اللہ علیہ وسلم کو، جن کی زبردست شخصیت اور غیر معمولی قابلیتوں سے قریش کے لوگ واقف ہو چکے تھے، ایک ٹھکانا میسر آرہا تھا۔ اور ان کی قیادت و رہنمائی میں پیرو انِ اسلام ، جن کی عزیمت و استقامت اور فدائیت کو بھی قریش ایک حد تک آزما چکے تھے، ایک منظم جتھے کی صورت میں مجتمع ہوئے جاتے تھے۔ یہ پرانے نظام کے لیے موت کا پیغام تھا۔ نیز مدینہ جیسے مقام پر مسلمانوں کی اس طاقت کےمجتمع ہونے سے قریش کو مزید خطرہ یہ تھا کہ یمن سے شام کی طرف جو تجارتی شاہراہ ساحلِ بحرِ احمر کے کنارے کنارے جاتی تھی، جس کے محفوظ رہنے پر قریش اور دوسرے بڑے بڑے مشرک قبائل کی معاشی زندگی کا انحصار تھا ، وہ مسلمانوں کی زد میں آجاتی تھی اور اس شہ رگ پر ہاتھ ڈال کر مسلمان نظامِ جاہلی کی زندگی دشوار کر سکتے تھے۔ صرف اہل مکہ کی وہ تجارت جو اس شاہراہ کے بل پر چل رہی تھی ڈھائی لاکھ اشرفی سالانہ تک پہنچتی تھی۔ طائف اور دوسرے مقامات کی تجارت اس کے ماسوا تھی۔ قریش ان نتائج کو خوب سمجھتے تھے۔ جس رات بیعتِ عقبہ واقع ہوئی اسی رات اس معالہ کی بھنک اہل مکہ کے کانوں میں پڑی اور پڑتے ہی کھلبلی مچ گئی۔ پہلے تو انھوں نے اہل مدینہ کو نبی صلی اللہ علیہ وسلم سے توڑنے کی کوشش کی۔ پھر جب مسلمان ایک ایک دو دو کر کے مدینہ کی طرف ہجرت کرنے لگے اور قریش کو یقین ہو گیا کہ اب محمد ؐ بھی وہاں منتقل ہو جائیں گے تو وہ اس خطرے کو روکنے کے لیے آخری چارہ کار اختیار کرنے پر آمادہ ہوگئے۔ ہجرت نبوی سے چند ہی روز پہلے قریش کی مجلس شوریٰ منعقد ہوئی جس میں بڑی ردّ وکد کے بعد آخر کار یہ طے پا گیا کہ بنی ہاشم کے سوا تمام خانوادہ ہائے قریش کا ایک ایک آدمی چھانٹا جائے اور یہ سب لوگ مل کر محمد صلی اللہ علیہ وسلم کو قتل کریں تا کہ بنی ہاشم کے لیے تمام خاندانوں سے تنہا لڑنا مشکل ہو جائے اور وہ انتقام کے بجائے کونبہا قبول کرنے پر مجبور ہو جائیں۔ لیکن خدا کے فضل اور نبی صلی اللہ علیہ وسلم کے اعتماد علی اللہ اورحسن تدبیر سے اُن کی یہ چال نا کام ہوگئی اور حضور بخیریت مدینہ پہنچ گئے۔ اس طرح جب قریش کو ہجرت کے روکنے میں نا کامی ہوئی تو انھوں نے مدینہ کے سردار عبداللہ بن اُبیّ کو (جسے ہجرت سے پہلے اہل مدینہ اپنا بادشاہ بنانے کی تیاری کر چکے تھے اور جس کی تمناؤں پر حضور ؐ کے مدینہ پہنچ جانے اور اَوس و خزرَج کی اکثریت کے مسلمان ہو جانے سے پانی پھر چکا تھا) خط لکھا کہ ”تم لوگوں نے ہمارے آدمی کو اپنے ہان پناہ دی ہے، ہم خدا کی قسم کھاتے ہیں کہ یا تو تم خودا س سے لڑو یا اسے نکال دو، ورنہ ہم سب تم پر حملہ آور ہوں گے اور تمہارے مردوں کو قتل اور عورتوں کو لونڈیاں بنا لیں گے“۔ عبداللہ بن اُبیّ اس پر کچھ آمادہ شر ہوا، مگر نبی صلی اللہ علیہ وسلم نے بر وقت اس کے شر کی روک تھام کر دی ۔ پھر سعد بن مَعاذ رئیس مدینہ عُمرے کے لیے مکہ گئے۔ وہاں عین حرم کے دروازے پر ابو جہل نے ان کو ٹوک کر کہا الا اُراک تطوف بمکة اٰمنًاوقداویتم الصُباة وزعمتم انکم تنصرونھم وتعینونھم؟ لو لا انک مع ابی صفوان ما رجعت الیٰ اھلک سالمًا (تم تو ہمارے دین کے مرتدوں کو پناہ دو اوعر ان کی امداد و اعانت کا دم بھر و اور ہم تمہیں اطمینان سے مکہ میں طواف کرنے دیں؟ اگر تم اُمَیّہ بن خَلَف کے مہمان نہ ہوتے تو زندہ یہاں سے نہیں جا سکتے تھے)۔ سعد نے جواب میں کہا واللہ لئن منعتنی ھٰذا الا منعک ما ھوا شد علیک منہ،طریقک علی المدینة (بخدا اگر تم نے مجھے اس چیز سے روکا تو میں تمہیں اُس چیز سے روک دوں گا جو تمہارے لیے اس سے شدید تر ہے، یعنی مدینہ پر سے تمہاری رہ گذر)۔ یہ گویا اہل مکہ کی طرف سے اس بات کا اعلان تھا کہ زیارت بیت اللہ کی راہ مسلمانوں پر بندہے، اور اس کا جواب اہل مدینہ کی طرف سے یہ تھا کہ شامی تجارت کا راستہ مخالفین اسلام کے لیے پر خطر ہے۔ اور فی الواقع اُس وقت مسلمانوں کے لیے اس کے سوا کوئی تدبیر بھی نہ تھی کہ اس تجارتی شاہرہ پر اپنی گرفت مضبوط کریں تا کہ قریش اور وہ دوسرے قبائل جن کا مفاد اس راستہ سے وابستہ تھا اسلام اور مسلمانوں کے ساتھ اپنی معاندانہ و مزاحمانہ پالیسی پر نظر ثانی کرنے کے لیے مجبور ہو جائیں۔ چنانچہ مدینہ پہنچتے ہی نبی صلی اللہ علیہ وسلم نے تو خیز اسلامی سوسائٹی کے ابتدائی نظم و نسق اور اطراف مدینہ کی یہودی آبادیوں کے ساتھ معاملہ طے کرنے کے بعد سب سے پہلے جس چیز پر توجہ منعطف فرمائی وہ اسی شاہراہ کا مسئلہ تھا۔ اس مسئلے میں حضور نے دو اہم تدبیریں اختیار کیں۔ ایک یہ کہ مدینہ اور ساحل بحر احمر کے درمیان اس شاہراہ سے متصل جو قبائل آباد تھے ان کے ساتھ گفت و شنید شروع کی تا کہ وہ حلیفانہ اتحاد یا کم از کم نا طرفداری کے معاہدے کر لیں۔ چنانچہ اس میں آپ کو پوری کامیابی ہوئی۔ سب سے پہلے جُہَینَہ سے، جو ساحل کے قریب پہاڑی علاقہ یَننُع اور ذوالُعشَیرہ سے متصل تھا وفاعی معاونت( Defensive alliance ) کی قرار داد ہوئی۔ پھر سن ۲ ہجری کے وسط میں بنی مُد لِج بھی اس قرارداد میں شریک ہوگئے کیونکہ وہ بنی ضمرہ کے ہمسائے اور حلیف تھے۔ مزید برآں تبلیغ اسلام نے ان قبائل میں اسلام کےحامیوں اور پیرووں کا بھی ایک اچھا خاصا عنصر پیدا کر دیا۔ دوسری تدبیر آپ نے یہ اختیار کی کہ قریش کے قافلوں کو دھمکی دینے کے یے اس شاہراہ پر پیہم چھوٹے چھوٹے دستے بھیجنے شروع کیے اور بعض دستوں کے ساتھ آپ خود بھی تشریف لے گئے۔ پہلے سال اس طرح کے چار دستے گئے جو مغازی کی کتابوں میں سَرِیّہَ حمزہ، سریہ ٕعبیدہ بن حارث ، سریہ سعد بن ابی وقاص اور غزوہ البواء کے نام سے موسوم ہیں۔ 1 اور دوسرے سال کے انتدائی مہینوں میں دو میزید تاختیں اسی جانب کی گئیں جن کو اہل مغازی غزوہ بواط اور غزوہ ذوالعشیرہ کے نام سے یاد کرتے ہیں۔ ان تمام مہموں کی دوخصوصیتیں قابل لحاظ ہیں۔ایک یہ کہ ان میں سے کسی میں نہ تو کشت و خون ہوا اور نہ کوئی قافلہ لوٹا گیا جس سے یہ صاف ظاہر ہوتا ہے کہ ان تاختوں کا اصل مقصود قریش کو ہوا کا رُخ بتانا تا۔ دوسرے یہ کہ ان میں سے کسی تاخت میں بھی حضور ؐ نے اہل مدینہ کا کوئی آدمی نہیں لیا بلکہ تمام دستے خالص مکی مہاجریں سے ہی مرتب فرماتے رہے کہ تاکہ حتٰی کہ الامکان یہ کشمکشش قریش کے اپنے ہی گھر والوں تک محدود رہے اور دوسرے قبیلوں کے اس میں اُلجھنے سے آگ پھیل نہ جائے۔ اُدھر سے اہل مکہ بھی مدینہ کی طرف غارت گرد ستے بھیجتے رہے، چنانچہ انہی میں سے ایک دستے نےکُر زبن جابر الفہری کی قیادت میں عین مدینہ کے قیرب ڈاکہ مارا اور اہل مدینہ کے مویشی لوٹ لیے۔ قریش کی کوشش اس سلسلہ میں یہ رہی کہ دوسرے قبیلوں کو بھی اس کشمکش میں اُلجھا دیں، نیز یہ کہ انہوں نے بات کو محض دھمکی تک محدود نہ رکھا بلکہ لوٹ مار تک نوبت پہنچا دی۔ حالات یہاں تک پہنچ چکے تھے کہ شعبان سن ۲ ہجری(فروری یا مارچ ٦۲۳ ء)میں قریش کا ایک بہت بڑا قافلہ ، جس کے ساتھ تقریباً ۵۰ ہزاراشرفی کا مال تھا اور تیس چالیس سے زیادہ محافظ نہ تھے، شام سے مکہ کی طرف پلٹتے ہوئے اُس علاقہ میں پہنچا جو مدینہ کی زد میں تھا۔ چونکہ مال زیادہ تھا، محافظ کم تھے، اور سابق حالات کی بنا پو خطرہ قوی تھا کہ کہیں مسلمانوں کا کوئی طاقتور دستہ اس پر چھاپہ نہ ماردے، اس لیے سردار قافلہ ابو سفیان نے اس پُر خطر علاقہ میں پہنچتے ہی ایک آدمی کو مکہ کی طرف دوڑادیا تاکہ وہاں سے مدد لے آئے۔ اس شخص نے مکہ پہنچتے ہی عرب کے قدیم قاعدے کے مطابق اپنے اونٹ کے کام کاٹے، اس کی ناک چیر دی، کجادے کو اُلٹ کر رکھ دیا اور اپنا قمیص آگے پیچھے سے پھاڑ کر شور مچانا شروع کر دیا کہ : یا معشر قریش!اللطیمہ الطیمہ، اموالکم مع ابع سفیان قد عرض لھا محمد فی اصحابہ، لا اُریٰ ان تُد رکوھا، الغوث، الغوث ، (قریش والو! اپنے قافلہ تجارت کی خبر لو، تمہارے مال جو ابو سفیان کے ساتھ ہیں، محمد ؐ اپنےآدمی لے کر ان کے در پے ہو گیا ہے، مجھے اُمید نہیں کہ تم انہیں پا سکو گے، دوڑو دوڑو مدد کے لیے)۔ اس پر سارے مکہ میں ہیجان بر پا ہوگیا۔ قریش کے تمام بڑے بڑے سردار جنگ کے لیے تیار ہوگئے۔ تقریباً ایک ہزار مردان جنگی جن میں سے ٦۰۰ زرہ پوش تھے اور جن میں ١۰۰ سواروں کا رسالہ بھی شامل تھا ، پوری شان و شوکت کے ساتھ لڑنے کے لیے چلے۔ ان کے پیش نظرصرف یہی کام نہ تھا کہ اپنے قافلے کو بچالائیں، بلکہ وہ اس اردے سے نکلے تھے کہ اس آئے دن کے خطرے کو ہمیشہ کے لیے ختم کر دیں ، اور مدینہ میں یہ مخالف طاقت جو ابھی نئی نئی مجتمع ہوئی شروع ہوئی ہے اسے کچل ڈالیں، اور اس نواح کے قبائل کو اس حد تک مرعوب کر دیں کہ آئندہ کے لیے یہ تجارتی راستہ بالکل محفوظ ہو جائے۔ اب نبی صلی اللہ علیہ وسلم نے ، جو حالات سے ہمیشہ باخبر رہتے تھے، محسوس فرمایا کہ فیصلہ کی گھڑی آپہنچی ہے اور یہ ٹھیک و ہ وقت ہے جبکہ ایک جسورانہ اقدام اگر نہ کر ڈالا گیا تو تحریک اسلامی ہمیشہ کے لیے بے جان ہو جائے گی، بلکہ بعید نہیں کہ اس تحریک کے لیے سر اُٹھانے کا پھر کوئی موقع ہی باقی نہ رہے نئے دارالہجرت میں آئے ابھی پورے دوسال بھی نہیں ہو ئے ہیں۔ مہاجرین بے سروسامان، انصار ابھی نا آزمودہ ، یہودی قبائل بر سر مخالفت، خود مدینہ میں منافقین و مشرکین کا ایک اچھا خاصا طاقتور عنصر موجود، اور گردوپیش کے تمام قبائل قریش سے مرعوب بھی اور مذہباً ان کے ہمدرد بھی، ایسے حالات میں اگر قریش مدینہ پر حملہ آور ہو جائیں تو ہو سکتا ہے کہ مسلمانوں کی مٹھی بھر جماعت کا خاتمہ ہو جائے۔ لیکن اگر وہ حملہ نہ کریں اور صرف اپنے زور سے قافلے کو بچا کر ہی نکال لے جائیں اور مسلمان دبکے بیٹھے رہیں تب بھی یک لخت مسلمانوں کی ایسی ہوا اُکھڑے گی کہ عرب کا بچہ بچہ ان پر دلیر ہو جائے گا اور ان کے لیے مکل بھر میں پھر کوئی جائے پناہ باقی نہ رہے گی۔ آس پاس کے سارے قبائل قریش کے اشاروں پر کام کرنا شروع کردیں گے۔ مدینہ کے یہودی اور منافقین و مشرکین علی الاعلان سر اُٹھائیں گے اور دارالہجرت میں جینا مشکل کر دیں گے۔ مسلمانوں کا کوئی رعب واثر نہ ہوگا کہ اس کی وجہ سے کسی کو ان کی جان، مال اور آبرو پر ہاتھ ڈالنے میں تامل ہو۔ اس بنا پر نبی صلی اللہ علیہ وسلم نے عزم فرمالیا کہ جو طاقت بھی اس وقت میسر ہے اسے لے کر نکلیں اور میدان میں فیصلہ کریں کہ جینے کا بل بوتا کس میں ہے اور کس میں نہیں ہے۔ اس فیصلہ کن اقدام کا ارادہ کر کے آپ نے انصار و مہاجری کو جمع کیا اور ان کے سامنے ساری پوزیشن صاف صاف رکھ دی کہ ایک طرف شمال میں تجارتی قافلہ ہے اور دوسری طرف جنوب سے قریش کا لشکر چلا آرہا ہے ، اللہ کا وعدہ ہے کہ ان دونوں میں سے کوئی ایک تمہیں مل جائے گا ، بتاؤ تم کس کے مقابلہ پر چلنا چاہتے ہو؟ جواب میں ایک بڑے گروہ کی طرف سے اس خواہش کا اظہار ہوا کہ قافلے پر حملہ کیا جائے۔ لیکن نبی صلی اللہ علیہ وسلم کے پیش نظر کچھ اور تھا اس لیے آپ نے اپنا سوال دُہرایا۔ اس پر مہاجرین میں سے مقداد بن عمرو نے اُٹھ کر کہا یا رسول اللہ ! امض لما امرک اللہ، فانا معک حیثما احببت، لا نقول لک کما قال بنوا سرائیل لموسٰی اذھب انت وربک فقاتلا انا ھٰھُنا قاعدون، وٰلِکن اذھب انت وربک فقاتلا انا معکما مقاتلون مادامت عین منا تطرف۔ ”یا رسول اللہ، جدھر آپ کا رب آپ کو حکم دے رہا ہے اسی طرف چلیے، ہم آپ کے ساتھ ہیں جس طرف بھی آپ جائیں۔ ہم بنی اسرائیل کی طرح یہ کہنے والے نہیں ہیں کہ جاؤ تم اور تمہارا خدا دونوں لڑیں، ہم تو یہاں بیٹھے ہیں۔ نہیں ہم کہتے ہیں چلیے آپ اور آپ کا خدا ، دونوں لڑیں اورہم آپ کے ساتھ جانیں لڑائیں گے جب تک ہم میں سے ایک آنکھ بھی گردش کر رہی ہے۔“مگر لڑائی کا فیصلہ انصار کی رائے معلوم کیے بغیر نہیں کیا جا سکتا تھا، کیونکہ ابھی تک فوجی اقدامات میں ان سے کوئی مدد نہیں لی گئی تھی اور ان کے لیے یہ آزمائش کا پہلا موقع تھا کہ اسلام کی حمایت کا جو عہد انہوں نے اول روز کیا تھا اسے وہ کہاں تک نباہنے کے لیے تیار ہیں۔ اس لیے حضور ؐ نے براہ راست ان کو مخاطب کیے بغیر پھر اپنا سوال دوہرایا۔ اس پر سعد بن معاذ اُٹھے اور انہوں نے عرض کیا شاید حضور کا روئے سخن ہماری طرف ہے؟ فرمایا ہاں۔ انہوں نے کہا لقداٰمنا بک وصدقنا ک و شھد نا اقن ما جئت بہ ھو الحق و اعطینا ک عھودنا و مواثیقنا علی السّمع و الطاعة۔ فامض یا رسول اللہ لما اردت۔ فوالذی بعثک بالحق لو استعر ضت بنا ھٰذا البحر فخضتَہ لخضنا ہ معک وما تخلف منا رجل واحد۔ومانکرہان تلقی بنا عدونا غدًا انا لنصبر عندالحرب صُدُقٌ عند اللقآء ولعل اللہ یریک منا مانقر بہ عینک فسر بِنا علیٰ برکة اللہ۔ ”ہم آپ پر ایمان لائے ہیں، آپ کی تصدیق کر چکے ہیں کہ آپ جو کچھ لائے ہیں وہ حق ہے اور آپ سے سمع وطاعت کا پختہ عہد باندھ چکے ہیں۔ پس اے اللہ کے رسول، جو کچھ آپ نے ارادہ فرما لیا ہے اسے کر گزریے۔ قسم ہے اُس ذات کی جس نے آپ کو حق کے ساتھ بھیجا ہے، اگر آپ ہمیں لے کر سامنے سمندر پر جا پہنچیں اور اس میں اتر جائیں تو ہم آپ کے ساتھ کر دیں گے اور ہم میں سے ایک بھی پیچھے نہ رہے گا۔ہم کو یہ ہر گز ناگوار نہیں ہے کہ آپ کل ہمیں لےکر دشمن سے جا بھڑیں۔ ہم جنگ میں ثابت قدم رہیں گے، مقابلہ میں سچی جاں نثاری دکھائیں گے اور بعید نہیں کہ اللہ آپ کو ہم سے وہ کچھ دکھوادے جسے دیکھ کر آپ کی آنکھیں ٹھنڈی ہو جائیں، پس اللہ کی برکت کے بھروسے پر آپ ہمیں لے چلیں۔“ ان تقریروں کے بعد فیصلہ ہو گیا کہ قافلہ کے بجائے لشکر ِ قریش ہی کے مقابلہ پر چلنا چاہیے۔ لیکن یہ فیصلہ کوئی معمولی فیصلہ نہ تھا ۔ جو لوگ اس تنگ وقت میں لڑائی کے لیے اُٹھے تھے ان کی تعداد ۳ سو سے کچھ زاہد تھی(۸٦ مہاجرم،٦١قبیلہ اوس کے اور١۷۰ قبیلہ خزرج کے )جن میں صرف دو تین کے پاس گھوڑے تھے اور باقی آدمیوں کے لیے ١۷۰ اونٹوں سے زیادہ نہ تھے جن پر تین تین چار چار اشخاص باری باری سے سوار ہوتے تھے۔ سامان جنگ بھی بالکل ناکافی تھا۔ صرف٦۰ آدمیوں کے پاس زرہیں تھیں۔ اسی لیے چند سر فروش فدائیوں کے سوا اکثر آدمی جو اس خطر ناک مہم میں شریک تھے دلوں میں سہم رہے تھے اور انہیں ایسا محسوس ہوتا تھا کہ جانتے بوجھتے موت کےمنہ میں جارہے ہیں۔ مصلحت پرست لوگ جو اگرچہ دائرہ اسلام میں داخل ہو چکے تھے مگر ایسے ایمان کے قائل نہ تھے جس میں جان و مال کا زیاں ہو، اس مہم کو دیوانگی سے تعبیر کر رہے تھے اور ان کا خیال تھا کہ دینی جذبے نے ان لوگوں کو پاگل بنا دیا ہے ۔ مگر نبی اور مومنین صادقین سمجھ چکے تھے کہ یہ وقت جان کی بازی لگانے ہی کا ہے اس لیے اللہ کے بھروسے پر وہ نکل کھڑے ہوئے اور انہوں نے سیدھی جنوبِ مغرب کی راہ لی جدھر سے قریش کا لشکر آرہا تھا۔ حالانکہ اگر ابتدا میں قافلے کو لوٹنا مقصود ہوتا تو شمال مغرب کی راہ لی جاتی۔ ١۷ رمضان کو بدر کے مقام پر فیقین کا مقابلہ ہوا ۔ جس وقت دونوں لشک ایک دوسرے کے مقابل ہوئے اور نبی صلی اللہ علیہ وسلم نے دیکھا کہ تین کافروں کے مقابلے میں ایک مسلمان ہے اور وہ بھی پوری طرح مسلح نہیں ہے، تو خدا کے آگے دعا کے لیے ہاتھ پھیلا دیے اور انتہائی خضوع وتضرع کے ساتھ عرض کرنا شروع کیا اللھم ھٰذہ قریش قد اتت بخیلا ئھا تحاول ان تکذب رسولک، اللھم فنصرک الذی وعدتنی، اللھم ان تھلک ھٰذہ العصابة الیوم لا تعبد” خدایا، یہ ہیں قریش ، اپنے سامان غرور کے ساتھ آئے ہیں تا کہ تیرے رسول کو جھوٹا ثابت کریں، خدا وند! بس اب آجائے تیری وہ مدد جس کا تو نے مجھ سے وعدہ کیا تھا، اے خدا اگر آج یہ مٹھی بھر جماعت ہلاک ہو گئی تو روئے زمین پر پھر تیری عبادت نہ ہوگی۔“ اس معرکہ کار زار میں سب سے زیادہ سخت امتحان مہاجرین مکہ کا تھا جن کے اپنے بھائی بند سامنے صف آرا تھے۔ کسی کا باپ، کسی کا بیٹا، کسی کا چچا ، کسی کا ماموں، کسی کا بھائی اس کی تلوار کی زد میں آ رہا تھا اور اپنے ہاتھوں اپنے جگر کے ٹکڑے کاٹنے پڑ رہے تھے۔ اس کڑی آزمائش سے صرف وہی لوگ گزر سکتے تھے جنہوں نے پوری سنجیدگی کے ساتھ حق سے رشتہ جوڑا ہوا ور باطل کے ساتھ سارے رشتے قطع کر ڈالنے پر تُل گئے ہوں۔ اور انصار کا امتحان بھی کچھ کم سخت نہ تھا۔ اب تک تو انہوں نے عرب کے طاقتور ترین قبیلے، قریش اور اس کے حلیف قبائل کی دشمنی صرف اسی حد تک مول لی تھی کہ ان کے علی الرغم مسلمانوں کو اپنے ہاں پناہ دے دی تھی۔ لیکن اب تو وہ اسلام کی حمایت میں ان کے خلاف لڑنے بھی جا رہے تھے جس کے معنی یہ تھے کہ ایک چھوٹی سی بستی جس کی آبادی چند ہزار نفوس سے زیادہ نہیں ہے، سارے ملک عرب سے لڑائی مول لے رہی ہے۔ یہ جسارتے صرف وہی لوگ کر سکتے تھے جو کسی صداقت پر ایسا ایمان لے آئے ہوں کہ اس کی خاطر اپنے ذاتی مفاد کی انہیں ذرہ برابر پرواہ نہ رہی ہو۔ آخر کار ان لوگوں کی صداقتِ ایمانی خدا کی طرف سے نصرت کا انعام حاصل کرنے میں کامیاب ہوگئی اور قریش اپنے سارے غرور طاقت کے باوجود ان بے سرو سامان فدائیوں کے ہاتھوں شکست کھا گئے۔ ان کے ستر آدمی مارے گئے، ۷۰ قید ہوئے اور ان کا سروسامان غنیمت میں مسلمانوں کے ہاتھ آیا۔ قریش کے بڑے بڑے سردار جوان کے گلہائے سرِسَبَد اور اسلام کی مخالفت تحریک کے روح رواں تھے اس معرکہ میں ختم ہوگئے او ر اس فیصلہ کن فتح عرب میں اسلام کو ایک قابل لحاظ طاقت بنا دیا۔ جیسا کہ ایک مغربی محقق نے لکھا ہے”بدر سے پہلے اسلام محض ایک مذہب اور ریاست تھا، مگر بدر کے بعد وہ مذہب ریاست بلکہ خود ریاست بن گیا“۔ مباحث: یہ ہے وہ عظیم الشان معرکہ جس پر قرآن کی اس سورہ میں تبصرہ کیاگیا ہے۔ مگر اس تبصرے کا انداز تمام اُن تبصروں سے مخطلف ہے جو دنیوی بادشاہ اپنی فوج کی فتحیابی کے بعد کیا کرتے ہیں۔ اس میں سب سے پہلے اُن خامیوں کی نشان دہی کی گئی ہے جو اخلاق حیثیت سے ابھی مسلمانوں میں باقی تھیں تا کہ آئندہ اپنی مزید تکمیل کے لیے سعی کریں۔ پھر ان کو بتایا گیا ہے کہ اس فتح میں تائید الہٰی کا کتنا بڑا حصہ تھا تاکہ وہ اپنی جرأت و شہامت پر نہ پھولیں بلکہ خدا پر توکل اور خدا کے رسول کی اطاعت کا سبق لیں۔ پھر اُس اخلاقی مقصد کو واضح کیاگیا ہے جس کے لیے مسلمانوں کو یہ معرکہ حق و باطل برپا کرنا ہے اور ان اخلاقی صفات کی توضیح کی گئی ہے جن سے اس معرکہ میں انہیں کامیابی حاصل ہو سکتی ہے۔ پھر مشرکین اور منافقین اور یہود اور ان لوگوں کو جو جنگ میں قید ہو کر آئے تھے، نہایت سبق آموز انداز میں خطاب کیا گیا ہے۔ پھر اُن اموال کے متعلق ، جو جنگ میں ہاتھ آئے تھے، مسلمانوں کو ہدایت کی گئی ہے کہ انہیں اپنا مال نہ سمجھیں بلکہ خدا کا مال سمجھیں ، جو کچھ اللہ اس میں سے ان کا حصہ مقرر کر ے اسے شکر یہ کے ساتھ قبول کر لیں اور جو حصہ اللہ اپنے کام اور اپنے غریب بندوں کے امداد کے لیے مقرر کرے اس کو برضاورغبت گوارا کر لیں۔ پھر قانون جنگ و صلح کے متعلق وہ اخلاقی ہدایات دی گئی ہیں جن کی توضیح اس مرحلے میں دعوت اسلامی کے داخل ہوجانے کے بعد ضروری تھی تاکہ مسلمان اپنی صلح و جنگ میں جاہلیت کے طریقوں سے بچیں اور اور دنیا پر ان کی اخلاقی بر تری قئم ہو اور دنیا کو معلوم ہو جائے کہ اسلام اوّل روز سے اخلاق پر عملی زندگی کی بنیاد رکھنے کی جو دعوت دے رہا ہے اس کی تعبیر واقعی عملی زندگی میں کیا ہے۔ پھر اسلامی ریاست کے دستوری قانون کی بعض دفعات بیان کی گئی ہیں جن سے دارالالسلام کے مسلمان با شندوں کی آئینی حیثیت اُن مسلمانوں سے الگ ک دی گئی ہے جو دارالا سلام کے حدود دے باہر رہتے ہوں۔ ','سید ابو اعلیٰ مودودیؒ -  تفہیم القرآن');
INSERT INTO "chapters_ur" VALUES(9,'توبہ',NULL,' نام : یہ سورہ دو ناموں سے مشہور ہے۔ ایک التوبہ دوسرے البراءَۃ ۔ توبہ اس لحاظ سے کہ اس میں ایک جگہ بعض اہلِ ایمان کے قصوروں کی معافی کا ذکر ہے۔ اور براءۃ اس لحاظ سے کہ اس کے آغاز میں مشرکین سے بریٔ الذمہ ہونے کا اعلان ہے۔ بسم اللہ نہ لکھنے کی وجہ : اس سورہ کی ابتداء میں بسم اللہ الرحمٰن الرحیم نہیں لکھی جاتی۔ اس کے متعدد وجوہ مفسرین نے بیان کیے ہیں جن میں بہت کچھ اختلاف ہے۔ مگر صحیح بات وہی ہے جو امام رازی نے لکھی ہے کہ نبی صلی اللہ علیہ وسلم نے خود اس کے آغاز میں بسم اللہ نہیں لکھوائی تھی اس لیے صحابہ کرام نے بھی نہیں لکھی اور بعد کے لوگ بھی اسی کی پیروی کرتے رہے۔ یہ اس با ت کا مزید ایک ثبوت ہے کہ قرآن کو نبی صلی اللہ علیہ وسلم سے جوں کا توں لینے اور جیسا دیا گیا تھا ویسا ہی اس کو محفوظ رکھنے میں کس درجہ احتیاط و اہتمام سے کام لیا گیا ہے۔ زمانہ ٴنزول اور اجزاءِ سوُرہ : ”یہ سورہ تین تقریروں پر مشتمل ہے: پہلی تقریر آغازِ سورہ سے پانچویں رکوع کے آخر تک چلتی ہے ۔ اس کا زمانۂ نزول ذی القعدہ سن ۹ ھجری یا اس کے لگ بھگ ہے۔ نبی صلی اللہ علیہ وسلم اُس سال حضرت ابوبکر ؓ کو امیر الحاج مقرر کر کے مکہ روانہ کر چکے تھے کہ یہ تقریر نازل ہوئی اور حضور نے فوراً سید نا علی ؓ کو ان کے پیچھے بھیجا تا کہ حج کے موقع پر تمام عرب کے نمائندہ اجتماع میں اسے سنائیں اور اس کے مطابق جو طرزِ عمل تجویز کیا گیا تھا اس کا اعلان کر دیں ۔ دوسری تقریر رکوع ۶ کی ابتدا سے رکوع ۹ کے اختتام تک چلتی ہے اور یہ رجب ۹ ھجری یا اس سے کچھ پہلے نازل ہوئی جبکہ نبی صلی اللہ علیہ وسلم غزوۂ تبوک کی تیاری کر رہے تھے۔ اس میں اہلِ ایمان کو جہاد پر اکسایا گیا ہے اور اُن لوگوں کو سختی کے ساتھ ملامت کی گئی ہے جو نفاق یا ضعف ایمان یا سُستی و کاہلی کی وجہ سے راہِ خدا میں جان و مال کا زیاں برداشت کرنے سے جی چرا رہے تھے۔ تیسری تقریر رکوع ۱۰ سے شروع ہو کر سورۃ کے ساتھ ختم ہوتی ہے اور یہ غزوہ ٔ تبوک سے واپسی پر نازل ہوئی۔ اس میں متعدد ٹکڑے ایسے بھی ہیں جو انہی ایام میں مختلف مواقع پر اُترے اور بعد میں نبی صلی اللہ علیہ وسلم نے اشارۂ الہٰی سے ان سب کو یکجا کر کے ایک سلسۂ تقریر میں منسلک کر دیا۔ مگر چونکہ وہ ایک ہی مضمون اور ایک ہی سلسلۂ واقعات سے متعلق ہیں اس لیے ربطِ تقریر میں کہیں خلل نہیں پایا جاتا ۔ اس میں منافقین کی حرکات پر تنبیہ ، غزوۂ تبوک سے پیچھے رہ جانے والوں پر زجر و توبیخ، اور اُن صادق الایمان لوگوں پر ملامت کے ساتھ معانی کا اعلان ہے جو اپنے ایمان میں سچے تو تھے مگر جہاد فی سبیل اللہ میں حصہ لینے سے بار رہے تھے۔ نزول ِ ترتیب کے لحاظ سے پہلی تقریر سب سے آخر میں آنی چاہیے تھی، لیکن مضمون کی اہمیت کے لحاظ سے وہی سب سے مقدم تھی، اس لیے مُصحَف کی ترتیب میں نبی صلی اللہ علیہ وسلم نے اس کو پہلے رکھا اور بقیہ دونوں تقریروں کو مؤخر کر دیا۔ تاریخی پس منظر: زمانۂ نزول کی تعیین کے بعد ہمیں اس سورہ کے تاریخی پس منظر پر ایک نطاہ ڈال لینی چاہیے ۔ جس سلسلہ ٔ واقعات سے اس کے مضامین کا تعلق ہے اس کی ابتدا صلحَ حدَیبیہ سے ہوتی ہے ۔ حدیبیہ تک چھ سال کی مسلسل جدوجہد کا نتیجہ اس شکل میں رونما ہو چکا تھا کہ عرب کے تقریبًا ایک تہائی حصہ میں اسلام ایک منظم سوسائیٹی کا دین ، ایک مکمل تہذیب و تمدّن ، اور ایک کابل با اختیار ریاست بن گیا تھا۔ حدیبیہ کی صلح جب واقع ہوئی تو اس دین کو یہ موقع بھی حاصل ہوگیا کہ اپنے اثرات نسبتًہ زیادہ امن و اطمینان کے ماحول میں ہر چہار طرف پھیلا سکے۔ اس کے بعد واقعات کی رفتار نے دو بڑے راستے اختیار کیے جو آگے چل کر نہایت اہم نتائج پر منتہی ہوئے۔ ان میں سے ایک کا تعلق عرب سے تھا اور دوسرے کا سلطنتِ روم سے۔ عرب کی تسخیر: عرب میں حدیبیہ کے بعد دعوت و تبلیغ اور استحکام قوت کی جو تدبیریں اختیار کی گئیں ان کی بدولت دوسال کے اندر ہی اسلام کا دائرہ ٔاثر اتنا پھیل گیا اور اس کی طاقت اتنی زبردست ہو گئی کہ پرانی جاہلیت اس کے مقابلہ میں بے بس ہو کر رہ گئی۔ آخر کار جب قریش کے زیادہ پر جوش عناصر نے بازی ہرتی دیکھی تو انہیں یاراے ضبط نہ رہا اور انہوں نے حدیبیہ کے معاہدے کو توڑ ڈالا ۔ وہ اس بندش سے آزاد ہو کر اسلام سے ایک آخری فیصلہ کن مقابلہ کرنا چاہتے تھے۔ لیکن نبی صلی اللہ علیہ وسلم نے ان کی اس عہد شکنی کے بعد ان کو سنبھلنے کا کوئی موقع نہ دیا اور اچانک مکہ پر حملہ کر کے رمضان سن ۸ ھجری میں اسے فتح کر لیا۔ اس کے بعد قدیم جاہلی نظام نے آخر ی حرکت مذبوحی حُنَین کے میدان میں کہ جہاں ہَوازِن ،ثَقیف، نَضر، جُشَم اور بعض دوسرے جاہلیت پرست قبائل نے اپنی ساری طاقت لا کر جھونک دی تا کہ اُس اصلاحی انقلاب کو روکیں جو فتح مکہ کے بعد تکمیل کے مرحلے پر پہنچ چکا تھا۔ لیکن یہ حرکت بھی ناکام ہوئی اور حنین کی شکست کے ساتھ عرب کی قسمت کا قطعی فیصلہ ہو گیا کہ اسے اب دارالاسلام بن کر رہنا ہے۔ اس واقعہ پر پورا ایک سال بھی نہ گزرنے پایا کہ عرب کا بیشتر حصہ اسلام کے دائرے میں داخل ہو گیا اور نظام جاہلیت کے صرف چند پراگندہ عناصر ملک کے مختلف گوشوں میں باقی رہ گئے۔ اس نتیجہ کے حد کمال تک پہنچنے میں اُن واقعات سے اور زیادہ مدد ملی جو شمال میں سلطنت روم کی سرحد پر اُسی زمانہ میں پیش آرہے تھے۔ وہاں جس جرأت کے ساتھ نبی صلی اللہ علیہ وسلم ۳۰ ہزار کا زبردست لشکر لے کر گئے اور رومیوں نے آپ کے مقابلہ پر آنے سے پہلو تہی کر کے جو کمزوری دکھائی اس نے تمام عرب پر آپ کی اور آپ کے دین کی دھاک بٹھا دی اور اس کا ثمرہ اس صورت میں ظاہر ہو اکہ تبوک سے واپس آتے ہی حضور کے پاس عرب کے گوشے گوشے سے وفد پر وفد آنے شروع ہو گئے او ر وہ اسلام و اطاعت کا اقرار کرنے لگے۔ چنانچہ اسی کیفیت کو قرآن میں بیان کیا گیا ہے کہ اِذَا جَآءَ نَصْرُ اللہِ وَالْفَتْحُ وَرَ اَیْتَ النَّاسَ یَدْ خُلُوْنَ فِیْ دِیْنِ اللہِ اَفْوَاجًا ۔ ”جب اللہ کی مدد آگئی، اور فتح نصیب ہو ئی اور تُو نے دیکھ لیا کہ لوگ فوج در فوج اسلام میں داخل ہو رہے ہیں“۔ غَزوۂ تبوک: رومی سلطنت کے ساتھ کشمکش کی ابتدا فتح مکہ سے پہلے ہو چکی تھی۔ نبی صلی اللہ علیہ وسلم نے حدیبیہ کے بعد اسلام کی دعوت پھیلانے کے لیے جو وفود عرب کے مختلف حصوں میں بھیجے تھے ان میں سے ایک شمال کی طرف سرحد شام سے متصل قبائل میں بھی گیا تھا۔ یہ لوگ زیادہ تر عیسائی تھَ اور رومی سلطنت کے زیرِ اثر تھے۔ ان لوگوں نے ذات الطَّلح (یا ذات اطلاح) کےمقام پر اس وفد کے ۱۵ آدمیوں کو قتل کر دیا اور صرف رئیس و فد کعب بن عُمَیر غِفَارِی بچ کر واپس آئے۔ اسی زمانہ میں حضور نے بُصریٰ کے رئیس شُرَحبِیل بن عمرو کے نام بھی دعوت اسلام کا بیغام بھیجا تھا، مگر اس نے آپ ککے ایلچی حارث بن عُمَیر کو قتل کر دیا ۔ یہ رئیس بھی عیسائی تھا اور براہِ راست قیصر روم کے احکام کا تابع تھا۔ ان وجوہ سے نبی صلی اللہ علیہ وسلم نے جمادی الاولیٰ سن ۸ ھجری میں تین ہزار مجاہدین کی ایک فوج سرحد شام کی طرف بھیجی تا کہ آئندہ کے لیے یہ علاقہ مسلمانوں کے لیے پرامن ہو جائے اور یہاں کے لوگ مسلمانوں کو بے زور سمجھ کر ان پر زیادتی کرنے کی جرأت نہ کریں۔ یہ فوج جب مَعَان کے قریب پہنچی تو معلوم ہوا کہ شُرحبیل بن عمرو ایک لاکھ کا لشکر لے کر مقابلہ پر آرہا ہے، خود قیصر روم حمِص کے مقام پر موجود ہے اور اس نے اپنے بھائی تھیوڈور کی قیادت میں ایک لاکھ کی مزید فوج روانہ کی ہے۔ لیکن ان خوفناک اطلاعات کے باوجود ۳ ہزار سرفروشوں کا یہ مختصر دستہ آگے بڑھتا چلا گیا اور مُؤتَہ کے مقام پر شرحبیل کی ایک لاکھ فوج سے جا ٹکرایا۔ اس تہوّر کا نتیجہ ییہ ہونا چاہیے تھا کہ مجاہدینِ اسلام بالکل پس جاتے ، لیکن سارا عرب اور تمام شَرقِ اَوسط یہ دیکھ کر ششدر رہ گیا کہ ایک اور ۳۳ کے اِس مقابلہ میں بھی کفار مسلمانوں پر غالب نہ آسکے۔ یہی چیز تھی جس نے شام اور اس سے متصل رہنے والے نیم آزاد عربی قبائل کو ، بلکہ عراق کے قریب رہنے والے نجدی قبائل کو بھی، جو کسری کے زیرِ اثر تھے، اسلام کی طرف متوجہ کر دیا اور وہ ہزاروں کی تعداد میں مسلمان ہوگئے ۔ بنی سُلَیم (جن کے سردار عباس بن مِرد اس سُلمَی تھے) اور اَشجع اور غَطَفان اور ذُبیان اور فزارَ ہ کےلوگ اسی زمانہ میں داخل اسلام ہوئے۔ اور اسی زمانہ میں سلطنت روم کی عربی فوجوں کا ایک کمانڈر فَروَہ بن عَمرو الجُذامی مسلمان ہوا جس نے اپنے ایمان کا ایسا زبردست ثبوت دیاکہ گردوپیش کے سارے علاقے اسے دیکھ کر دنگ رہ گئے ۔ قیصر کو جب فَردَہ کے قبول اسلام کی اطلاع ملی تو اس نے انہیں گرفتار کر ا کے اپنے دربار میں بلوایا اور ان سے کہا کہ دو چیزوں میں سے ایک کو منتخب کر لو۔ یا ترکِ اسلام جس کا نتیجہ میں تم کو نہ صرف رہا کیا جائے گا بلکہ تمہیں اپنے عہدے پر بھی بحال کر دیا جائے گا، یا اسلام جس کے نتیجہ میں تمہیں سزائے موت دی جائے گی۔ انہوں نے ٹھنڈے دل سے اسلام کو چن لیا اور راہِ حق میں جان دے دی ۔ یہی واقعات تھے جنہوں نے قیصر کو اُس ”خطرے “ کی حقیقی اہمیت محسوس کرائی جو عرب سے اُٹھ کر اس کی سلطنت کی طرف بڑھ رہا تھا۔ دوسرے ہی سال قیصر نے مسلمانوں کو غزوۂ مُؤتَہ کی سزا دینے کے لیے سرحد شام پر فوجی تیاریاں شروع کر دیں اور اس کے ماتحت غَسّائی اور دوسرے عرب سردار فوجیں اکٹھی کرنے لگے ۔ نبی صلی اللہ علیہ وسلم اس سے بے خبر نہ تھے ۔ آپ پر وقت ہر اُس چھوٹی سے چھوٹی بات سے بھی خبردار رہتے تھے جس کا اسلامی تحریک پر کچھ بھی موافق یا مخالف اثر پڑتا ہو۔ آپ نے ان تیاریوں کے معنی فورًسمجھ لیے اور بغیر کسی تامل کے قیصر کی عظیم الشان طاقت سے ٹکرانے کا فیصلہ کر لیا۔ اس موقع پر ذرہ برابر بھی کمزوری دکھائی جاتی تو سارا بنا بنایا کام بگڑ جاتا۔ ایک طرف عرب کی دم توڑتی ہوئی جاہلیت ، جس پر حُنَین میں آخری ضرب لگائی جا چکی تھی، پھر جی اُٹھتی۔ دوسری طرف عرب کی دم توڑتی ہوئی جاہلیت، جس پر حُنین میں آخیر ضرب لگائی جا چکی تھی، پھر جی اُٹھتی۔ دوسری طرف مدینہ کے منافقین ، جو ابوعامر راہب کے واسطے سے غَسان کے عیسائی بادشاہ اور خود قیصر کے ساتھ اندرونی ساز باز رکھتے تھے ، اور جنہوں نے اپنی ریشہ دوانیوں پر دین داری کا پردہ ڈالنے کے لیے مدینہ سے متصل ہی مسجد ضرار تعمیر کر رکھی تھی، بغل میں چھُرا گھونپ دیتے۔ سامنے سے قیصر، جس کا دبدبہ ایرانیوں کو شکست دینے کے بعد تمام دور و نزدیک کے علاقوں پر چھا گیا تھا ، حملہ آور ہوتا۔ اور ان تین زبردست خطروں کی متحدہ یورش میں اسلام کی جیتی ہوئی بازی یکایک مات کھا جاتی۔ اس لیے باوجود اس کے کہ ملک میں قحط سالی تھی، گرمی کا موسم پورے شباب پر تھا، فصلیں پکنے کے قریب تھیں ، سواریوں اور سروسامان کا انتظام سخت مشکل تھا، سرمایہ کی بہت کمی تھی اور دنیا کی دو سب سے بڑی طاقتوں میں سے ایک کا مقابلہ درپیش تھا، خدا کے نبی نے یہ دیکھ کر کہ یہ دعوتِ حق کے لیے زندگی و موت کے فیصلہ کی گھڑی ہے، اسی حال میں تیاری جنگ کا اعلانِ عام کر دیا۔ پہلے تمام غزوات میں تو حضور کا قاعدہ تھا کہ آخر وقت تک کسی کو نہ بتاتے تھے کہ کدھر جانا ہے اور کس سے مقابلہ دیپیش ہے، بلکہ مدینے سے نکلنے کے بعد بھی منزل مقصود کی طرف سیدھا راستہ اختیار کرنے کے بجائے پھیر کی راہ سے تشریف لے جاتے تھے۔ لیکن اس موقع پر آپ نے یہ پردہ بھی نہ رکھا اور صاف صاف بتا دیا کہ روم سے مقابلہ ہے اور شام کی طرف جانا ہے۔ اس موقع کی نزاکت کو عرب میں سب ہی محسوس کر رہے تھے۔ جاہلیت ِ قدیمہ کے بچے کھچے عاشقوں کے لیے یہ ایک آخری شعاعِ اُمید تھی اور رُوم و اسلام کی اس ٹکر کے نتیجہ پر وہ بے چینی کے ساتھ نگاہیں لگائے ہوئے تھے۔ کیونکہ وہ خود بھی جانتے تھے کہ اس کے بعد پھر کہیں سے اُمید کی جھلک نہیں دکھائی دینی ہے۔ منافقین نے بھی اپنی آخری بازی اسی پر لگا دی تھی اور وہ اپنی مسجدِ ضرار بنا کر اس انتظار میں تھے کہ شام کی جنگ میں اسلام کی قسمت کا پانسہ پلٹے تو اِدھر اندرونِ ملک میں وہ اپنے فتنہ کا عَلم بلند کریں۔ یہی نہیں بلکہ انہو ں نے اس مہم کو ناکام کرنے کے لیے تمام ممکن تدبیریں بھی استعمال کر ڈالیں۔ ادھر مومنین صادقین کو بھی پورا احسان تھا کہ جس تحریک کے لیے ۲۲ سال سے وہ سربکف رہے ہیں ، اس وقت اس کی قسمت ترازو میں ہے ، اس موقع پر جرأت دکھانے کے معنی یہ ہیں کہ اس تحریک کے لیے ساری دنیا پر چھا جانے کا دروازہ کھل جائے، اور کمزوری دکھانے کے معنی یہ ہیں کہ عرب میں بھی اس کی بساط اُلٹ جائے۔ چنانچہ اسی احساس کے ساتھ ان فدائیانِ حق نے انتہائی جوش و خروش سے جنگ کی تیاری کی۔ سروسامان کی فراہمی میں ہر ایک نے اپنی بساط سے بڑھ کر حصہ لیا۔ حضرت عثمانؓ ااور حضرت عبد الرحمٰن ؓ بن عوف نے بڑی بڑی رقمیں پیش کیں۔ حضرت عمرؓ نے اپنی عمر بھر کی کمائی کا آدھا حصہ لا کر رکھ دیا۔ حضرت ابوبکرؓ نے اپنی ساری پونجی نذر کر دی۔ غریب صحابیوں نے محنت مزدوری کر کر کے جو کچھ کمایا لا کر حضر کر دیا۔ عورتوں نے اپنے زیور اُتار اُتار کر دے دیے۔ سرفروش والنٹیروں کے لشکر کے لشکر ہر طرف سے اُمنڈ اُمنڈ کر آنے شروع ہو ئے اور انہوں نے تقاضا کیا کہ اسلحہ اور سواریوں کا انتظام ہو تو ہماری جانیں قربان ہونے کو حاضر ہیں ۔ جن کو سواریاں نہ مل سکیں وہ روتے تھے اور اپنے اخلاص کی بے تابیوں کا اظہار اس طرح کرتے تھے کہ رسول پاک کا دل بھر آتا تھا۔ یہ موقع عملًا ایمان اور نفاق کے امتیاز کی کسوٹی بن گیا تھا ، حتیٰ کہ اس وقت پیچھے رہ جانے کے معنی یہ تحے کہ اسلام کے ساتھ آدمی کے تعلق کی صداقت ہی مشتبہ ہو جائے ۔ چنانچہ تبوک کی طرف جاتے ہوئے دَورانِ سفر میں جو جو شخص پیچھے رہ جاتا تھا صحابۂ کرام نبی صلی اللہ علیہ وسلم کو اس کی خبر دیتے تھے اور جواب میں حضور برجستہ فرماتے تھے کہ دعوہ فان یک فیہ خیر فسیلحقہ اللہ بکم وان یک غیر ذٰلک فقد ارا حکم اللہ منہ۔ ” جانے دو، اگر اس میں کچھ بھلائی ہے تو اللہ اسے پھر تمہارے ساتھ لا ملائے گا اور اگر کچھ دوسری حالت ہے تو شکر کرو کہ اللہ نے اس کی جھوٹی رفاقت سے تمہیں خلاصی بخشی“۔ رجب سن ۹ ھجری میں نبی صلی اللہ علیہ وسلم ۳۰ ہزار مجاہدین کے ساتھ شام کی طرف روانہ ہوئے جن میں دس ہزار سوار تھے۔ اونٹوں کی اتنی کمی تھی کہ ایک ایک اونٹ پر کئی کئی آدمی بار ی باری سوار ہوتے تھے۔ اس پر گرمی کی شدت اور پانی کی قلت مستزاد ۔ مگر جس عزم صادق کا ثبوت اس نازک موقع پر مسلمانوں نے دیا اس کا ثمرہ تَبُوک پہنچ کر انہیں تقد مل گیا۔ وہاں پہنچ کر انہیں معلوم ہوا کہ قیصر اور اس کے تابعین نے مقابلہ پر آنے کے بجائے اپنی فوجیں سرحد سے ہٹا لی ہیں اور اب کوئی دشمن موجود نہیں ہے کہ اس سے جنگ کی جائے۔ سیرت نگار بالعموم اس واقعہ کو اس انداز سے لکھ جاتے ہیں کہ گویا وہ خبر ہی سرے سے غلط نکلی جو آنحضرت صلی اللہ علیہ وسلم کو رومی افواج کے اجتماع کے متعلق ملی تھی۔ حالانکہ دراصل واقعہ یہ تھا کہ قیصر نے اجتماعِ افواج شروع کیا تھا، لیکن جب آنحضرت صلی اللہ علیہ وسلم اُس کی تیاریاں مکمل ہونے سے پہلے ہی مقابلہ پر پہنچ گئے تو اس نے سرحد سے فوجیں ہٹا لینے کے سوا کوئی چارہ نہ پایا ۔ غزوۂ مُؤ تہ میں ۳ ہزار اور ایک لاکھ کے مقابلہ کی جو شان وہ دیکھ چکا تھا اُس کے بعد اس میں اتنی ہمت نہ تھی کہ خود نبی ؐ کی قیادت میں جہاں ۳۰ ہزار فوج آ رہی ہو وہاں وہ لاکھ دو لاکھ آدمی لے کر میدان میں آجاتا۔ قیصر کے یوں طر ح دے جانے سے جو اخلاقی فتح حاصل ہوئی اس کو نبی صلی اللہ علیہ وسلم نے اس مرحلے پر کافی سمجھا اور بجائے اس کے تبوک سے آگے بڑھ کر سرحد شام میں داخل ہوتے ، آپ نے اس بات کوترجیح دی کہ اس فتح سے انتہائی ممکن سیاسی و حربی فوائد حاصل کر لیں ۔ چنانچہ آپ نے تبوک میں ۲۰ دن ٹھیر کر اُن بہت سی چھوٹی چھوٹی ریاستوں کو جو سلطنت روم اور دارالاسلام کے درمیان واقع تھیں اور اب تک رومیوں کے زیر اثر رہی تھیں، فوجی دباؤ سے سلطنت اسلامی کا باجگذار اور تابع امر بنالیا۔ اس سلسلہ میں دُومَۃ ُ الجَندَل کے عیسائی رئِس اُکَید ِ ربن عبد المالک کِندی ، اَیلہ کے عیسائی رئیس یوحنّا بن رُؤبہ ، اور اسی طرح مَقنا ، جَرباء اور اَذرُح کے نصرانی رؤسا ء نے بھی جزیہ ادا کر کے مدینہ کی تابعیت قبول کی اور اس کا نتیجہ یہ ہوا کہ اسلامی حدود اقتدار براہِ راست رومی سلطنت کی سرحد تک پہنچ گئے اور جن عرب قبائل کو قیاصرۂ روم اب تک عرب کے خلا ف استعمال کرتے رہے تھے، اب ان کا بیشتر حصہ رومیوں کے مقابلہ پر مسلمانوں کا معاون بن گیا۔ پھر اس کا سب سے بڑا فائدہ یہ ہوا کہ سلطنت روم کے ساتھ ایک طویل کشمکش میں اُلجھ جانے سے پہلے اسلام کو عرب پر اپنی گرفت مضبوط کر لینے کا پورا موقع مل گیا۔ تبوک کی اس فتح بلا جنگ نے عرب میں ان لوگوں کی کمر توڑ دی جو اب تک جاہلیت ِ قدیمہ کے بحال ہونے کی آس لگائے بیٹھے تھے، خواہ وہ علانیہ مشرک ہوں یا اسلام کے پردہ میں منافق بنے ہوئے ہوں۔ اس آخری مایوسی نے ان میں سے اکثر و بیشتر کے لیے اس کے سوا کوئی چارہ نہ رہنے دیا کہ اسلام کے دامن میں پناہ لیں اور اگر خود نعمت ایمانی سے بہرہ ور نہ بھی ہوں تو کم از کم ان کی آئندہ نسلیں بالکل اسلام میں جذب ہو جائیں۔ اس کے بعد جو ایک برائے نام اقلیت شرک و جاہلیت میں ثابت قدم رہ گئی ، وہ اتنی بے بس ہوگئِ تھی کہ اُس اصلاحی انقلاب کی تکمیل میں کچھ بھی مانع نہ ہو سکتی تھی جس کے لیے اللہ نے اپنے رسول کو بھیجا تھا۔ مسائل و مباحث: اس پس منظر کو نگاہ میں رکھنے کے بعد ہم بآسانی اُن بڑے بڑے مسائل کا احصاء کر سکتے ہیں جو اس وقت درپیش تھے اور جن سے سورۂ توبہ میں تعُّرض کیا گیا ہے: (۱) اب چونکہ عرب کا نظم و نسق بالکلیہ اہلِ ایمان کے ہاتھ میں آگیا تھا اور تمام مزاحم طاقتیں بے بس ہو چکی تھِں، اس لیے وہ پالیسی واضح طور پر سامنے آجانی چاہیے تھی جو عرب کو مکمل دارالاسلام بنانے کے لیے اختیار کرنی ضروری تھی ، چنانچہ وہ حسبِ ذِل صورت میں پیش کی گئی: الف ۔ عرب سےشرک کو قطعًا مٹا دیا جائے اور قدیم مشرکانہ نظام کا کلی استیصال کر ڈالا جائے تا کہ مرکز ِ اسلام ہمیشہ کے لیے خالص اسلامی مرکز ہو جائے اور کوئی دوسرا عنصر اس کے اسلامی مزاج میں نہ تو خلل انداز ہو سکے اور نہ کسی خطرے کے موقع پر اندرونی فتنہ کا موجب بن سکے۔ اسی غرض کے لیے مشرکین سے براءَت اور ان کے ساتھ معاہدوں کے اختتام کا اعلان کیا گیا۔ ب۔ کعبہ کا انتظام اہلِ ایمان کے ہاتھ میں آجانے کے بعد یہ بالکل نا مناسب تھا کہ جو گھر خالص خدا کی پرستش کے لیے وقت کیا گیا تھا اس میں بدستور شرک ہوتا رہے اور س کی تَولِیَت بھی مشرکین کے قبضہ میں رہے۔ اس لیے حکم دیا گیا کہ آئندہ کعبہ کی تولیت بھی اہلِ توحید کے قبضہ میں رہنی چاہیے اور بیت اللہ کے حدود میں شرک و جاہلیت کی تمام رسمیں بھی بزور بند کر دینی چاہییں ، بلکہ اب مشرکین اس گھر کے قریب پھٹکنے بھی نہ پائیں تا کہ اس بنائے ابراہیمی کے آلودۂ شرک ہونے کا کوئی امکان باقی نہ رہے۔ ج۔ عرب کی تمدنی زندگی میں رسوم جاہلیت کے جو آثار ابھی تک باقی تھے ان کا جدید اسلامی دور میں جاری رہنا کسی طرح درست نہ تھا اس لیے ان کے استیصال کی طرف توجہ دلائی گئی۔ نَسی کا قاعدہ ان رسوم میں سب سے زیادہ بدنما تھا اس لیے اس پر براہِ راست ضرب لگائی گئی اور اسی ضرب سے مسلمانوں کو بتا دیا گیا کہ بقیہ آثار جاہلیت کے ساتھ انہیں کیا کرنا چاہیے۔ (۲) عرب میں اسلام کا مشن پایۂ تکمیل کو پہنچ جانے کے بد دوسرا اہم مرحلہ جوسامنے تھا وہ یہ تھا کہ عرب کے باہر دینِ حق کا دائرۂ اثر پھیلا جائے۔ اس معاملہ میں روم و ایران کی سیاسی قوت سب سے بڑی سدِّ راہ تھی اور ناگزیر تھا کہ عرب کے کام سے فارغ ہوتے ہی اس سے تصادم ہو۔ نیز آگے چل کر دوسرے غیر مسلم سیاسی و تمدّنی نظاموں سے بھی اِسی طرح سابقہ پیش آنا تھا۔ اس لیے مسلمانوں کو ہدایت کی گئِ کہ عرب کے باہر جو لوگ دینِ حق کے پیرو نہیں ہیں ان کو خود مختار انہ فرماں روائی کو بزور شمشیر ختم کردو تا آنکہ وہ اسلامی اقتدار کے تابع ہو کر رہنا قبول کرلیں ۔ جہاں تک دین حق پر ایمان لانے کا تعلق ہے ان کو اختیار ہے کہ ایمان لائیں یا نہ لائیں، لیکن ان کو یہ حق نہیں ہے کہ خدا کی زمین پر اپنا حکم جاری کریں اور انسانی سوسائیٹیوں کی زمام کار اپنے ہاتھ میں رکھ کر اپنی گمراہیوں کو خلقِ خدا پر اور ان کی آنے والی نسلوں پر زبردستی مسلّط کرتےرہیں۔ زیادہ سے زیادہ جس آزادی کے استعمال کا انہیں اختیار دیا جا سکتا ہے وہ بس سی حد تک ہے کہ خود اگر گمراہ رہنا چاہتے ہیں تو رہیں، بشرطیکہ جزیہ دے کر اسلامی اقتدار کے مطیع بنے رہیں۔ (۳) تیسرا اہم مسئلہ منافقین کا تھا جن کا ساتھ اب تک وقتی مصالح کے لحاظ سے چشم پوشی و درگذر کا معاملہ کیا جا رہا تھا۔ اب چونکہ بیرونی خطرات کا دباؤ کم ہو گیا تھا بلکہ گویا نہیں رہا تھا اس لیے حکم دیا گیا کہ آئندہ ان کے ساتھ کوئی نرمی نہ کی جائے اور وہی سخت برتاؤ اِن چھپے ہوئے منکرین حق کے ساتھ بھی ہو جو کھلے منکر ینِ حق کے ساتھ ہوتا ہے ۔ چنانچہ یہی پالیسی تھی جس کے مطابق نبی صلی اللہ علیہ وسلم نے غزوۂ تبوک کی تیاری کے زمانہ میں سُوَ یلِم کے گھر میں آگ لگوا دی جہاں منافقین کا ایک گروہ اس غرض سے جمع ہوتا تھا کہ مسلمانوں کو شرکت جنگ سے باز رکھنے کی کوشش کرے، اور اسی پالیسی کے تحت تبوک سے واپس تشریف لاتے ہی نبی صلی اللہ علیہ وسلم نے پہلا کام یہ کیا کہ مسجد ضرار کو ڈھانے اور جلا دینے کا حکم دے دیا۔ (۴) مومنین صادقین میں اب تک جو تھوڑا بہت ضعفِ عزم باقی تھا اس کا علاج بھی ضروری تھا، کیونکہ اسلام عالمگیر جدوجہد کے مرحلے میں داخل ہونے والا تھا اور اس مرحلہ میں ، جبکہ اکیلے مسلم عرب کو پوری غیر مسلم دنیا سے ٹکرانا تھا، ضعفِ ایمان سے بڑھ کر کوئی اندرونی خطرہ اسلامی جماعت کے لیے نہ ہو سکتا تھا۔ اس لیے جن لوگوں نے تبوک کے موقع پر سستی اور کمزوری دکھائی تھی ان کو نہایت شدت کے ساتھ ملامت کی گئی، پیچھے رہ جانے والوں کے اِس فعل کو کہ وہ بلاعذرِ معقول پیچھے رہ گئے بجائے خود ایک منافقانہ طرزِ عمل ، اور ایمان میں ان کے ناراست ہونے کا ایک بین ثبوت قرار دیا گیا ، اور آئندہ کے لیے پوری صفائی کے ساتھ یہ بات واضح کر دی گئی کہ اعلائے کلمۃ اللہ کی جدوجہد اور کفر و اسلام کی کشمکش ہی وہ اصلی کسوٹی ہے جس پر مومن کا دعوائے ایمان پرکھا جائے گا ۔ جو اِس آویزش میں اسلام کے لیے جان و مال اور وقت و محنت صرف کرنے سے جی چرائے گا اس کا ایمان معتبر ہی نہ گا اور اس پہلے کی کسر کسی دوسرے مذہبی عمل سے پوری نہ ہو سکے گی۔ ان امور کر نظر میں رکھ کر سورۂ توبہ کا مطالعہ کیا جائے تو اس کے تمام مضامین بآسانی سمجھ میں آسکتے ہیں۔ ','سید ابو اعلیٰ مودودیؒ -  تفہیم القرآن');
INSERT INTO "chapters_ur" VALUES(10,'یونس',NULL,' نام : اس سورہ کا نام حسب دستور محض علامت کے طور پر آیت ۹۸ سے لیا گیا ہے جس میں اشارۃً حضرت یونسؑ کا ذکر آیا ہے ۔ سورہ کا موضوع بحث حضرت یونسؑ کا قصہ نہیں ہے۔ مقام نزول : روایات سے معلوم ہوتا ہے اور نفس مضمون سے اس کی تائید ہوتی ہے کہ یہ پوری سورۃ مکّے میں نازل ہو ئی ہے۔ بعض لوگوں کا گمان ہے کہ اس کی بعض آیتیں مدنی دور کی ہیں ، لیکن یہ محض ایک سطحی قیاس ہے۔ سلسلۂ کلام پر غور کرنے سے صاف محسوس ہو جاتا ہے کہ یہ مختلف تقریروں یا مختلف مواقع پر اُتری ہوئی آیتوں کا مجموعہ نہیں ہے بلکہ شروع سے آخر تک ایک ہی مربوط تقریر ہے جو بیک وقت نازل ہوئی ہو گی، اور مضمون کلام اس بات پر صریح دلالت کر رہا ہے کہ یہ مکی دور کا کلام ہے۔ زمانہ ٴنزول: زمانۂ نزول کے متعلق کوئی روایت ہمیں نہیں ملی۔ لیکن مضمون سے ایسا ہی ظاہر ہوتا ہے کہ یہ سورۃ زمانۂ قیام مکہ کے آخر ی دور میں نازل ہوئی ہو گی۔ کیونکہ اس کے اندازِ کلام سے صریح طور پر محسوس ہوتا ہے کہ مخالفین دعوت کی طرف سے مزاحمت پوری شدت اختیار کر چکی ہے، وہ نبی اور پیروانِ نبی کو اپنے درمیان برداشت کرنے کے لیے تیار نہیں ہیں ، ان سے اب یہ امید باقی نہیں رہی ہے کہ تفہیم و تلقین سے راہِ راست پر آجائیں گے، اور اب انہیں اُس انجام سے خبر دار کرنے کا موقع آگیا ہے کہ جو نبی کو آخری اور قطعی طور پر رد کر دینے کی صورت میں انہیں لازمًا دیکھنا ہوگا ۔ مضمون کی یہی خصوصیات ہمیں بتاتی ہیں کہ کونسی سورتیں مکہ کے آخری دور سے تعلق رکھتی ہیں ۔ لیکن اس سورہ میں ہجرت کی طرف بھی کوئی اشارہ نہیں پایا جاتا ، اس لیے اس کا زمانہ ان سورتوں سے پہلے کا سمجھنا چاہیے جن میں کوئی نہ کوئی خفی یا جلی اشارہ ہم کو ہجرت کے متعلق ملتا ہے ۔۔۔۔۔۔ زمانہ کی اِس تعیین کے بعد تاریخی پس منظر بیان کرنے کی ضرورت باقی نہیں رہتی ، کیونکہ اس دور کا تاریخی پس منظر سورۂ اَنعام اور سورۂ اَعراف کے دیباچوں میں بیان کیا جا چکا ہے۔ موضوع: موضوعِ تقریر دعوت، فہمائش اور تنبیہ ہے۔ کلام کا آغاز اس طرح ہوتا ہے کہ: لوگ ایک انسان کے پیغامِ نبوت پیش کرنے پر حیران ہیں اور اسے خواہ مخوا ہ ساحری کا الزام دے رہے ہیں، حالانکہ جو بات وہ پیش کر رہا ہے اس میں کوئی چیز بھی نہ تو عجیب ہی ہے اور نہ سحر و کہانت ہی سے تعلق رکھتی ہے۔ وہ تو دو اہم حقیقتوں سے تم کو آگاہ کر رہا ہے۔ ایک یہ کہ جو خدا اس کائنات کا خالق ہے اور اس کا انتظام عملًا چلا رہا ہے صرف وہی تمہارا مالک و آقا ہے اور تنہا اسی کا یہ حق ہے کہ تم اس کی بندگی کرو۔ دوسرے یہ کہ موجودہ دنیوی زندگی کے بعد زندگی کا ایک اَور دَور آنے والا ہے جس میں توم دوبارہ پیدا کیے جاؤ گے، اپنی موجودہ زندگی کے پورے کارنامے کا حساب دو گے اور ان بنیادی سوال پر چوا یا سزا پاؤگے کہ تم نے اُسی خد ا کو اپنا آقا مان کر اس کے منشا کے مطابق نیک رویہ اختیار کیا یا اس کے خلاف عمل کرتے رہے۔ یہ دونوں حقیقتیں ، جو وہ تمہارے سامنے پیش کر رہا ہے، بجائے خود امر واقعی ہیں خواہ تم مانو یا نہ مانو۔ وہ تمہیں دعوت دیتا ہے کہ تم انہیں مان لو اور اپنی زندگی کو ان کے مطابق بنا لو۔ اس کی یہ دعوت اگر تم قبول کر و گے تو تمہارا اپنا انجام بہتر ہو گا ورنہ خود ہی بُرا نتیجہ دیکھو گے۔ مباحث: اس تمہید کے بعد حسبِ ذیل مباحث ایک خاص ترتیب کے ساتھ سامنے آتے ہیں: (۱) وہ دلائل جو توحیدِ ربوبیت اور حیات اُخروی کے باب میں ایسے لوگوں کو عقل و ضمیر کا اطمینان بخش سکتے ہیں جو جاہلانہ تعصب میں مبتلا نہ ہوں اور جنہیں بحث کی ہار جیت کے بجائے اصل فکر اس بات کی ہو کہ خود غلط بینی اور اس کے بُرے نتائج سے بچیں۔ (۲) اُن غلط فہمیوں کا ازالہ اور اُن غفلتوں پر تنبیہ جو لوگوں کو توحید اور آخرت کا عقیدہ تسلیم کرنے میں مانع ہو رہی تھیں (اور ہمیشہ ہوا کرتی ہیں)۔ (۳) اُن شبہات اور اعتراضات کا جواب جو محمد صلی اللہ علیہ وسلم کی رسالت اور آپ کے لائے ہوئے پیغام کے بارے میں پیش کیے جاتے تھَ۔ (۴) دوسری زندگی میں جو کچھ پیش آنے والا ہے اس کی پیشگی خبر، تاکہ انسان اس سے ہوشیار ہو کر اپنے آج کے طرزِ عمل کو درست کر لے اور بعد میں پچھتانے کی نوبت نہ آئے۔ (۵) اس امر پر تنبیہ کہ دنیا کی موجودہ زندگی دراصل امتحان کی زندگی ہے اور اس امتحان کے لیے تمہارے پاس بس اتنی ہی مہلت ہے جب تک تم اس دنیا میں سانس لے رہے ہو۔ اِس وقت کو اگر تم نے ضائع کر دیا اور نبی کی ہدایت قبول کر کے امتحان کی کامیابی کا سامان نہ کیا تو پھر کوئ دوسرا موقع تمہیں ملنا نہیں ہے۔ اس نبی کا آنا اور اس قرآن کے ذریعہ تم کو علم حقیقت کا بہم پہنچایا جانا وہ بہترین اور ایک ہی موقع ہے جو تمہیں مل رہا ہے۔ اس سے فائدہ نہ اُٹھاؤ گے تو بعد کی ابدی زندگی میں ہمیشہ ہمیشہ پچھتاؤگے۔ (۶) اُن کھلی کھلی جہالتوں اور ضلالتوں پر اشارہ جو لوگوں کی زندگی میں صرف اس وجہ سے پائی جا رہی تھیں کہ وہ خدائی ہدایت کے بغیر جی رہے تھے۔ اس سلسلہ میں نوح علیہ السلام کا قصہ مختصر ًا اور موسیٰ علیہ السلام کا قصہ ذرا تفصیل کے ساتھ بیان کیا گیا ہے جس سے چار باتیں ذہن نشین کر نی مطلوب ہیں ۔اوّل یہ کہ محمد صلی اللہ علیہ وسلم کے ساتھ جو معاملہ تم لوگ کر رہے ہو وہ اس سے ملتا جلتا ہے جو نوح اور موسی ٰ علیہما السلام کے ساتھ تمہارے پیش رو کر چکے ہیں اور یقین رکھو کہ اس طرزِ عمل کا جو انجام وہ دیکھ چکے ہیں وہی تمہیں بھی دیکھنا پڑے گا۔ دوم یہ کہ محمد صلی اللہ علیہ وسلم اور ان کے ساتھیوں کو آج جس بے بسی و کمزوری مکے حال میں تم دیکھ رہے ہو اس سے کہیں یہ نہ سمجھ لینا کہ صورتِ حال ہمیشہ یہی رہے گی۔ تمہیں خبر نہیں ہے کہ ان لوگوں کی پشت پر وہی خدا ہے جو موسیٰ ؑو ہارون ؑ کی پشت پر تھا اور وہ ایسے طریقہ سے حالات کی بساط اُلٹ دیتا ہے جس تک کسی کی نگاہ نہیں پہنچ سکتی ۔ سوم یہ کہ سنھلنے کے لیے جو مہلت خدا تمہیں دے رہا ہے اسے اگر تم نے ضائع کر دیا اور پھر فرعون کی طرح خدا کی پکڑ میں آجانے کے بعد عین آخری لمحے پر توبہ کی تو معاف نہیں کیے جاؤگے۔ چہارم یہ کہ جو لوگ محمد صلی اللہ علیہ وسلم پر ایمان لائے تھے وہ مخالف ماحول کی انتہائی شدت اور اس کے مقابلہ میں اپنی بیچارگی دیکھ کر مایوس نہ ہوں اور انہیں معلوم ہو کہ ان حالات میں ان کو کس طرح کام کرنا چاہیے ۔ نیز وہ اس امر پر بھی متنبہ ہو جائیں کہ جب اللہ تعالیٰ اپنے فضل سے ان کو اِس حالت سے نکال دے تو کہیں وہ اُس روش پر نہ چل پڑیں جو بنی اسرائیل نے مصر سےنجات پا کر اختیار کی۔آخر میں اعلان کیا گیا ہے کہ یہ عقیدہ اور یہ مسلک ہے جس پر چلنے کی اللہ نے اپنے پیغمبر کو ہدایت کی ہے۔ اس میں قطعًا کوئی ترمیم نہیں کی جا سکتی ، جو اسے قبول کرے گا وہ اپنا بھلا کرے گا اور جو اس کو چھوڑ کر غلط راہوں میں بھٹکے گا وہ اپنا ہی کچھ بگاڑ دے گا۔','سید ابو اعلیٰ مودودیؒ -  تفہیم القرآن');
INSERT INTO "chapters_ur" VALUES(11,'حود',NULL,' زمانۂ نزول: اس سورے کے مضمون پر غور کرنے سے ایسا محسوس ہوتا ہے کہ یہ اسی دور میں نازل ہوئی ہو گی جس میں سورۂ ”یونس“ نازل ہوئی تھی۔ بعید نہیں کہ یہ اس کے ساتھ متصلًا ہی نازل ہو ئی ہو، کیونکہ موضوع ِ تقریر وہی ہے ، مگر تنبیہ کا انداز اس سے زیادہ سخت ہے۔ حدیث میں آتا ہے کہ حضرت ابوبکر ؓ نے نبی صلی اللہ علیہ وسلم سے عرض کیا” میں دیکھتا ہوں کہ آپؐ بوڑھے ہوتے جارہے ہیں، اس کی کیا وجہ ہے“؟ جواب میں حضورؐ نے فرمایا شَیَّبتْنی ھُود و اَخواتہا ،” مجھ کو سورہ ہود اور اس کی ہم مضمون سورتوں نے بوڑھا کر دیا ہے“۔ اس سے اندازہ ہوتا ہے کہ نبی صلی اللہ علیہ وسلم کے لیے وہ زمانہ کیسا سخت ہوگا جب کہ ایک طرف کفارِ قریش اپنے تمام ہتھیار وں سے اس دعوتِ حق کو کچل دینے کی کوشش کر رہے تھے اور دوسری طرف اللہ تعالیٰ کی طرف سے یہ پے دَر پے تنبیہات نازل ہو رہی تھیں۔ ان حالات میں آپ کو ہر وقت یہ اندیشہ گھلائے دیتا ہو گا کہ کہیں اللہ کی دی ہوئی مہلت ختم نہ ہو جائے اور وہ آخری ساعت نہ آجائے جب کہ اللہ تعالیٰ کسی قوم کو عذاب میں پکڑ لینے کا فیصلہ فرما دیتا ہے۔ فی الواقع اس سُورے کو پڑھتے ہوئے ایسا محسوس ہوتا ہے کہ جیسے ایک سیلاب کا بند ٹوٹنے کو ہے اور اُس غافل آبادی کا ، جو اس سیلاب کی زد میں آنے والی ہے، آخری تنبیہ کی جا ری ہے۔ موضوع اور مباحث: موضوع تقریر، جیسا کہ ابھی بیا ن کیا جا چکا ہے ، وہی ہے جو سورۂ یونس کا تھا۔ یعنی دعوت، فہمائش اور تنبیہ،لیکن فرق یہ ہے کہ سُورۂ یونس کی بہ نسبت یہاں دعوت مختصر ہے، فہمائش میں استدلال کم اور وعظ و نصیحت زیادہ ہے ، اور تنبیہ مفصل اور پر زور ہے۔ دعوت یہ ہے کہ پیغمبر کی بات مانو، شرک سے باز آجاؤ، سب کی بندگی چھوڑ کر اللہ کے بندے بنو اور اپنی دنیوی زندگی کا سارا نظام آخرت کی جواب دہی کے احساس پر قائم کرو۔ فہمائش یہ ہے کہ حیاتِ دنیا کے ظاہری پہلو پر اعتماد کر کے جن قوموں نے اللہ کے رسولوں کی دعوت کو ٹھکرایا ہے وہ اس سے پہلے نہایت بُرا انجام دیکھ چکی ہیں، اب کیا ضرور ہے کہ تم بھی اسی راہ چلو جسے تاریخ کے مسلسل تجربات قطعی طور پر تباہی کی راہ ثابت کر چکے ہیں۔ تنبیہ یہ ہے کہ عذاب کے آنے میں جو تاخیر ہو رہی ہے یہ دراصل ایک مہلت ہے جو اللہ اپنے فضل سے تمہیں عطا کر رہا ہے ۔ اس مہلت کے اندر اگر تم نہ سنبھلے تو وہ عذاب آئے گا جو کسی کے ٹالے نہ ٹل سکے گا اور اہلِ ایمان کی مٹھی بھر جماعت کو چھوڑ کر تمہاری ساری قوم کو صفحۂ ہستی سے مٹا دے گا۔ اس مضمون کو ادا کرنے کے لیے براہِ راست خطاب کی بہ نسبت قوم نوح ، عاد ، ثمود ، قوم لوط، اصحاب مَدیَن اور قوم فرعون کے قصوں سے زیادہ کام لیا گیا ہے۔ ان قصوں میں خاص طور پر جو بات نمایاں کی گئی ہے وہ یہ ہے کہ خدا جب فیصلہ چُکانے پر آتا ہے تو پھر بالکل بے لاگ طریقہ سے چکاتا ہے۔ اس میں کسی کے ساتھ ذرہ برابر رعایت نہیں ہوتی۔ اس وقت یہ نہیں دیکھا جاتا کہ کون کس کا بیٹا اور کس کا عزیز ہے۔ رحمت صرف اس کے حصہ میں آتی ہے جو راہِ راست پر آگیا ہو، ورنہ خدا کے غضب سے نہ کسی پیغمبر کا بیٹا بچتا ہے اور نہ کسی پیغمبر کی بیوی۔ یہی نہیں بلکہ جب ایمان و کفر کا دو ٹوک فیصلہ ہو رہا ہو تو دین کی فطرت یہ چاہتی ہے کہ خود مومن بھی باپ اور بیٹے اور شوہر اور بیوی کے رشتوں کو بھول جائے اور خدا کی شمشیر عدل کی طرح بالکل بے لاگ ہو کر ایک رشتۂ حق کے سوا ہر دوسرے رشتے کو کاٹ پھنیکے ایسے موقع پر خون اور نسب کی رشتہ داریوں کا ذرہ برابر لحاظ کر جانا اسلام کی روح کے خلاف ہے۔ یہی وہ تعلیم تھی جس کا پورا پورا مظاہرہ تین چار سال بعد مکّہ کے مہاجر مسلمانوں نے جنگ بدر میں کر کے دکھا دیا۔ ','سید ابو اعلیٰ مودودیؒ -  تفہیم القرآن');
INSERT INTO "chapters_ur" VALUES(12,'یوسف',NULL,' زمانہ ٴنزول اور اجزائے مضمون: اس سورے کے مضمون سے مترشح ہوتا ہے کہ یہ بھی زمانہٴ قیام مکہ کے آخری دَور میں نازل ہوئی ہوگی جبکہ قریش کے لوگ اس مسئلے پر غور کررہے تھے کہ نبی صلی اللہ علیہ وسلّم کو قتل کردیں یا جلا وطن کریں یا قید کردیں۔ اُس زمانہ میں بعض کفارِمکہ نے (غالباً یہودیوں کے اشارے پر)نبی صلی اللہ علیہ وسلّم کا امتحان لینے کے لیے آپ سے سوال کیا کہ بنی اسرائیل کے مصر جانے کا کیا سبب ہوا۔ چونکہ اہل عرب اس قصہ سے ناواقف تھے، اس کا نام و نشان تک ان کے ہاں کی روایات میں نہ پایا جاتا تھا، اور خود نبی صلی اللہ علیہ وسلّم کی زبان سے بھی اس سے پہلے کبھی اس کا ذکر نہ سنا گیا تھا، اس لیے انہیں توقع تھی کہ آپ یا تو اس کا مفصل جواب نہ دے سکیں گے، یا اس وقت ٹال مٹول کرکے بعد کسی یہودیی سے پوچھنے کی کوشش کریں گے، اور اس طرح آپ کا بھرم کھل جائیگا۔ لیکن اس امتحان میں انہیں اُلٹی منہ کی کھانی پڑی۔اللہ تعالیٰ نے صرف یہی نہیں کیا کہ فوراً اُسی وقت یوسف علیہ السلام کا یہ پورا قصہ آپ کی زبان پر جاری کردیا، بلکہ برآں اس قصے کو قریش کے اُس معاملہ پر چسپاں بھی کردیا جو وہ برادران یوسف کی طرح آنحضرت صلی اللہ علیہ وسلّم کے ساتھ کررہے تھے۔ مقاصدِنزول: اس طرح یہ قصہ دو اہم مقاصد کے لیے نازل فرمایا گیا تھا : ایک یہ کہ محمد ؐ کی نبوت کا ثبوت، اور وہ بھی مخالفین کا اپنا منہ مانگا ثبوت بہم پہنچایا جائے، اور اُن کے خود تجویز کردہ امتحان میں یہ ثابت کردیا جائے کہ آپ سنی سنائی باتیں بیان نہیں کرتے بلکہ فی الواقع آپ کو وحی کے ذریعہ سے علم حاصل ہوتا ہے۔ اس مقصد کو آیات ۳، ۷ میں بھی صاف صاف واضح کردیا گیا ہے اور آیات ١۰۲ - میں بھی پورے زور کے ساتھ اس کی تصریح کی گئی ہے۔ دوسرےیہ کہ سردارانِ قریش اور محمد ؐ کے درمیان اُس وقت جو معاملہ چل رہا تھا اس پر برادرانِ یوسف اور یوسف ؑ کے قصے کو چسپاں کرتے ہوئے قریش والوں کو بتایا جائے کہ آج تم اپنے بھائی کے ساتھ وہی کچھ کررہے ہو جو یوسف کے بھائیوں نے ان کے ساتھ کیا تھا۔ مگر جس طرح وہ خدا کی مشیت سے لڑنے میں کامیاب نہ ہوئے اور آخرکار اُسی بھائی کے قدموں میں آرہے جس کو انہوں نے کبھی انتہائی بے رحمی کے ساتھ کنویں میں پھینکا تھا، اسی طرح تمہاری زور آمائی بھی خدائی تدبیر کے مقابلے میں کامیاب نہ ہوسکے گی اور ایک دن تمہیں بھی اپنے اِسی بھائی سے رحم و کرم کی بھیک مانگنی پڑے گی جسے آج تم مٹادینے پر تلے ہوئے ہو۔ یہ مقصد بھی سورہ کے آغاز میں صاف صاف بیان کردیا گیا ہے۔ چنانچہ فرمایا لَقَدْکَانَ فِیْ یُوْسُفَ وَاِخوَتِہۤ١اٰیٰتٌ لِّلسَّاۤئِلِینَ۔ ”یوسف اور اس کے بھائیوں کے قصے میں ان پوچھنے والوں کے لیے بڑی نشانیاں ہیں“۔ حقیقت یہ ہے کہ یوسف علیہ السلام کے قصے کو محمد صلی اللہ علیہ وسلّم اور قریش کے معاملے پر چسپاں کرکے قرآن مجید نے گویا ایک صریح پیش گوئی کردی تھی جسے آئندہ دس سال کے واقعات نے حرف بحرف صحیح ثابت کرکے دکھادیا۔اس سورہ کے نزول پر ڈیڑھ دو سال ہی گزرے ہوں گے کہ قریش والوں نے برادرانِ یوسف کی طرح محمد ؐ کے قتل کی سازش کی اور آپ کو مجبوراً ان سے جان بچاکر مکّہ سے نکلنا پڑا۔ پھر اُن کی توقعات کے بالکل خلاف آپ کو بھی جلاوطنی میں ویسا ہی عرہج و اقترار نصیب ہُوا جیسا یوسف ؑ کو ہُوا تھا۔ پھر فتح مکّہ کے موقع پر ٹھیک ٹھیک وہی کچھ پیش آیا جو مصر کے پایہٴ تخت میں یوسف ؑ کے سامنے ان کے بھائیوں کی آخری حضوری کے موقع پر پیش آیا تھا۔ وہاں جب برادرانِ یوسف انتہائی عجز و درماندگی کی حالت میں ان کے آگے ہاتھ پھیلائے کھڑے تھے اور کہہ رہے تھے کہ تَصَدَّقْ عَلَینَا ط اِنَّ اللہَ یَجزِی المُتَصَدِّقِین َ، ”ہم پر صدقہ کیجیئے، اللہ صدقہ کرنے والوں کو نیک جزا دیتا ہے“، تو یوسف ؑ نے انتقام کی قدرت رکھنےکے باوجود انہیں معاف کردیا اور فرمایا لَا تَثرِیبَ عَلَیکُمُالیَومَ، یَغفِرُ اللہُ لَکُم وَھُوَ اَرحَمُ الرّٰحِمِینَ ”آج تم پر کوئی گرفت نہیں، اللہ تمہیں معاف کرے، وہ سب رحم کرنے والوں سے بڑھ کر رحم کرنے والا ہے“۔اسی طرح جب یہاں محمد ؐ کے سامنے شکست خوردہ قریش سرنگوں کھڑے ہوئے تھے اور آنحضرت ؐ ان کے ایک ایک ظلم کا بدلہ لینے پر قادر تھے تو آپ نے ان سے پوچھا ”تمہارا کیا خیال ہے کہ میں تمہارے ساتھ کیا معاملہ کروں گا؟“ انہوں نے عرض کیا اخ کریمٌ و ابن اخ کریمِ ”آپ ایک عالی ظرف بھائی ہیں، اور ایک عالی ظرف بھائی کے بیٹے ہیں“۔اس پر آپ ؐ نے فرمایا فانی اقول لکم کما قال یوسف لاخوتہ، لا تثربَ علیکم الیوم، اذھبوا فانتم الطلقاء ”میں تمہیں وہی جواب دیتا ہوں جو یوسف نے اپنے بھائیوں کو دیا تھا کہ آج تم پر کوئی گرفت نہیں، جاوٴ تمہیں معاف کیا“۔ مباحث و مسائل: یہ دو پہلو تو اس سورہ میں مقصدی حیثیت رکھتے ہیں۔ لیکن اس قصے کو بھی قرآن مجید محض قصہ گوئی و تاریخ نگاری کے طور پر بیان نہیں کرتا بلکہ اپنے قاعدے کے مطابق وہ اسے اپنی اصل دعوت کی تبلیغ میں استعمال کرتا ہے۔ وہ اس پوری داستان میں یہ بات نمایاں کرکے دکھاتا ہے کہ حضرت ابراہیم ؑ ، حضرت اسحاق ؑ ، حضرت یعقوب ؑ اور حضرت یوسف ؑ کا دین وہی تھا جو محمد ؐ کا ہے اور اسی چیز کی طرف وہ بھی دعوت دیتے تھے جس کی طرف آج محمد ؐ دے رہے ہیں۔ پھر وہ ایک طرف حضرت یعقوب ؑ اور حضرت یوسف ؑ کے کردار اور دوسری طرف برادران یوسف ؑ ، قافلہٴ تجار، عزیز مصر، اس کی بیوی، بیگمات مصر اور حکام مصر کے کردار ایک دوسرے کے مقابلہ میں رکھ دیتا ہے اور محض اپنے اندازِ بیان سے سامعین و ناظرین کے سامنے یہ خاموش سوال پیش کرتا ہے کہ دیکھو، ایک نمونے کے کردار تو وہ ہیں جو اسلام، یعنی خدا کی بندگی اور حسابِ آخرت کے یقین سے پیدا ہوتے ہیں، اور دوسرے نمونے کے کردار وہ ہیں جو کفر و جاہلیت اور دنیا پرسی اور خدا و آخرت سے بے نیازی کے سانچوں میں ڈھل کرتیار ہوتے ہیں۔ اب تم خود اپنے ضمیر سے پوچھو کہ وہ ان میں سے کس نمونے کو پسند کرتا ہے۔ پھر اس قصے سے قرآن حکیم ایک اور گہری حقیقت بھی انسان کے ذہن نشین کرتا ہے۔ اور وہ یہ ہے کہ اللہ تعالیٰ جو کام کرنا چاہتا ہے وہ بہرحال پورا ہوکر رہتا ہے۔ انسان اپنی تدبیروں سے اُس کے منصوبوں کو روکنے اور بدلنے میں کبھی کامیاب نہیں ہوسکتا۔ بلکہ بسا اوقات انسان ایک کام اپنے منصوبے کی خاطر کرتا ہے اور سمجھتا ہے کہ میں نے ٹھیک نشانے پر تیر ماردیا مگر نتیجہ میں ثابت ہوتا ہے کہ اللہ نے اسی کے ہاتھوں سے وہ کام لے لیا جو اس کے منصوبے کے خلاف اور اللہ کے منصوبے کے عین مطابق تھا۔ یوسف ؑ کے بھائی جب ان کو کنویں میں پھینک رہے تھے تو ان کا گمان تھا کہ ہم نے اپنی راہ کے کانٹے کو ہمیشہ کے لیے ہٹادیا۔مگر فی الواقع انہوں نے یوسف ؑ کو اُس بام عروج کی پہلی سیڑھی پر اپنے ہاتھوں لاکھڑا کیا جس پر اللہ ان کو پہنچانا چاہتا تھا اور اپنی اس حرکت سے انہوں نے خود اپنے لیے اگر کچھ کمایا تو بس یہ کہ یوسف ؑ کے بام عروج پر پہنچنے کے بعد بجائے اس کے کہ وہ عزت کے ساتھ اپنے بھائی کی ملاقات کو جاتے انہیں ندامت و شرمساری کے ساتھ اسی بھائی کے سامنے سرنگوں ہونا پڑا۔ عزیز مصر کی بیوی یوسف ؑ کو قید خانے بھجواکر اپنے نزدیک تو اُن سے انتقام لے رہی تھی، مگر فی الواقع اس نے ان کے لیے تخت سلطنت پر پہنچنے کا راستہ صاف کیا اور اپنی اس تدبیر سے خود اپنے لیے اس کے سوا کچھ نہ کمایا کہ وقت آنے پر فرمانروانے ملک کی مربّیہ کہلانے کے بجائے اس کو علی الاعلان اپنی خیانت کے اعتراف کی شرمندگی اُٹھانی پڑی۔ یہ محض دوچار مستثنٰی واقعات نہیں ہیں بلکہ تاریخ ایسی بے شمار مثالوں سے بھری پڑی ہے جو اس حقیقت کی گواہی دیتی ہیں کہ اللہ جسے اٹھانا چاہتا ہے، ساری دنیا مل کر بھی اس کو نہیں گراسکتی۔ بلکہ دنیا جس تدبیر کو اس کے گرانے کی نہایت کارگر اور یقینی تدبیر سمجھ کر اختیار کرتی ہے، اللہ اسی تدبیر میں سےاس کے اُٹھنے کی صورتیں نکال دیتا ہے، اور اُن لوگوں کے حصّے میں رسوائی کے سواکچھ نہیں آتا جنہوں نے اسے گرانا چاہا تھا۔ اور اسی طرح اس کے برعکس، خدا جسے گرانا چاہتا ہے اسے کوئی تدبیر سنبھال نہیں سکتی، بلکہ سنبھالنے کی ساری تدبیریں اُلٹی پڑتی ہیں، اور ایسی تدبیریں کرنے والوں کو منہ کی کھانی پڑتی ہے۔ اس حقیقت حال کو اگر کوئی سمجھ لے تو اسے پہلا سبق تو یہ ملے گا کہ انسان کو اپنے مقاصد اور اپنی تدابیر، دونوں میں اُن حدود سے تجاوز نہ کرنا چاہیے جو قانون الہٰی میں اس کے لیے مقرر کردی گئی ہیں۔ کامیابی و ناکامی تو اللہ کے ہاتھ میں ہے۔ لیکن جو شخص پاک مقصد کے لیے سیدھی سیدھی جائز تدبیر کرے گا وہ اگر ناکام بھی ہُوا تو بہرحال ذلت و رسوائی سے دوچار نہ ہوگا۔ اور جو شخص ناپاک مقصد کے لیے ٹیڑھی تدبیریں کرے گا وہ آخرت میں تو یقیناً رسوا ہو گاہی مگر دنیا میں بھی اس کے لیے رسوائی کا خطرہ کچھ کم نہیں ہے۔ دوسرا اہم سبق اس سے توکّل علی اللہ اور تفویض الی اللہ کا ملتا ہے۔ جو لوگ حق اور صداقت کے لیے سعی کررہے ہوں اور دنیا انہیں ہٹادینے پر تُلی ہوئی ہو وہ اگر اس حقیقت کو پیش نظر رکھیں تو انہیں اس سے غیر معمولی تسکین حاصل ہوگی، اور مخالف طاقتوں کی بظاہر نہایت خوفناک تدبیروں کو دیکھ کر وہ قطعاً ہراساں نہ ہوں گے، بلکہ نتائج کو اللہ پر چھوڑتے ہوئے اپنا اخلاقی فرض انجام دیے چلے چائیں گے۔ مگر سب سے بڑا سبق جو اس قصے سے ملتا ہے وہ یہ ہے کہ ایک مرد مومن اگر حقیقی اسلامی سیرت رکھتا ہو اور حکمت سے بھی بہرہ یاب ہو، تو وہ محض اپنے اخلاق کے زور سے ایک پورے ملک کو فتح کرسکتا ہے۔ یوسف ؑ کو دیکھیے۔ ١۷ برس کی عمر، تن تنہا، بے سروسامان، اجنبی ملک اور پھر کمزوری کی انتہا یہ کہ غلام بناکر بیچے گئے ہیں۔ تاریخ کے اُس دور میں غلاموں کی جو حیثیت تھی وہ کسی سے پوشیدہ نہیں۔ اس پر مزید یہ کہ ایک شدید اخلاقی جرم کا الزام لگاکر انہیں جیل بھیج دیا گیا جس کی میعادِ سزا بھی کوئی نہ تھی۔ اس حالت تک گرادیے جانے کے بعد وہ محض اپنے ایمان اور اخلاق کے بل پر اٹھتے ہیں اور بالآخر پورے ملک کو مسخر کرلیتے ہیں۔ تاریخی و جغرافیائی حالات : اس قصے کو سمجھنے کے لیے ضروری ہے کہ مختصراً اس کے متعلق کچھ تاریخی و جغرافی معلومات بھی ناظرین کے پیش نظر رہیں: حضرت یوسف ؑ ، حضرت یعقوب ؑ کے بیٹے، حضرت اسحاق ؑ کے پوتے اور حضرت ابراہیم ؑ کے پرپوتے تھے۔ بائیبل کے بیان کے مطابق (جس کی تائید قرآن کے اشارات سے بھی ہوتی ہے) حضرت یعقوب ؑ کے بارہ بیٹے چار بیویوں سےتھے، حضرت یوسف ؑ اور ان کے چھوٹے بھائی بن یمین ایک بیوی سے ، اور باقی دس دوسری بیویوں سے۔ فلسطین میں حضرت یعقوب ؑ کی جائے قیام حِبرون (موجودہ الخیل)کی وادی میں تھی جہں حضرت اسحاق ؑ اور ان سے پہلے حضرت ابراہیم ؑ رہا کرتے تھے۔ اس کے علاوہ حضرت یعقوب ؑ کی کچھ زمین سِکّمِ (موجودہ نابلس)میں تھی۔ بائیبل کے علماء کی تحقیق اگر درست مانی جائے تو حضرت یوسف ؑ کی پیدائش سن ١۹۰٦قبل مسیح کے لگ بھگ زمانے میں ہوئی اور سن ١۸۹۰ ق م کے قریب زمانے میں وہ واقعہ پیش آیا جس سے اس قصہ کی ابتدا ہوئی ہے، یعنی خواب دیکھنا اور پھر کنوئیں میں پھینکا جانا۔ اس وقت حضرت یوسف ؑ کی عمر سترہ برس کی تھی۔ جس کنویں میں وہ پھینکے گئے وہ بائیبل اور تلمود کی روایات کے مطابق سِکّمِ کے شمال میں دُوتَن (موجودہ دُثان)کے قریب واقع تھا، اور جس قافلے نے انہیں کنویں سے نکالا وہ جلعاد (شرق اُردن)سے آرہا تھا اور مصر کی طرف عازم تھا۔ (جِلعاد کے کھنڈر اب بھی دریائے اردن کے مشرق میں وَادی الیابِس کے کنارے واقع ہیں)۔ مصر پر اس زمانے میں پندرہویں خاندان کی حکومت تھی جو مصری تاریخ میں چروا ہے بادشاہوں (Hyksos Kings) کے نام سے یاد کیا جاتا ہے۔ یہ لوگ عربی النسل تھے اور فلسطین و شام سے مصر جاکر ۲ ہزار برس قبل مسیح کے لگ بھگ زمانہ میں سلطنت مصر پر قابض ہوگئے تھے۔ عرب موٴرخین اور مفسرین قرآن نے ان کے لیے ”عَمالیق“کا نام استعمال کیا ہے جو مصریات کی موجودہ تحقیقات سے ٹھیک مطابقت رکھتا ہے۔ مصر میں یہ لوگ اجنبی حملہ آور کی حیثیت رکھتے تھےاور ملک کی خانگی نزاعات کے سبب سے انہیں وہاں اپنی بادشاہی قا۴م کرنے کا موقع مل گیا تھا۔ یہی سبب ہُوا کہ ان کی حکومت میں حضرت یوسف ؑ کو عروج حاصل کرنے کا موقع ملا اور پھر بنی اسرائیل وہاں ہاتھوں ہاتھ لیے گئے، ملک کے بہترین زرخیز علاقے میں آباد کیے گئے اور ان کو وہاں بڑا اثر و رسوخ حاصل ہوا، کیونکہ وہ ان غیر ملکی حکمرانوں کے ہم جنس تھے۔ پندرھویں صدی قبل مسیح کے اواخر تک یہ لوگ مصر پر قابض رہے اور ان کے زمانے میں ملک کا سارا اقتدار عملاً بنی اسرائیل کے ہاتھ میں رہا۔ اُسی دور کی طرف سورة مائدہ آیت ۲۰ میں اشارہ کیا گیا ہے کہ اِذ جَعَلَ فِیکُم اَنبِیَآءَ وَجَعَلَکُم مُّلُوکاً اس کے بعد ملک میں ایک زبردست قوم پرستانہ تحریک اُٹھی جس نے بِکسُوس اقتدار کا تختہ اُلٹ دیا۔ڈہائی لاکھ کی تعداد میں عمالقہ ملک سے نکال دیے گئے۔ ایک نہایت متعصب قبطی النسل خاندان برسر اقتدار آگیا اور اس نے عمالقہ کے زمانے کی یادگاروں کو چن چن کر مٹادیا اور بنی اسرائیل پر اُن مظالم کا سلسلہ شرہع کیا جن کا ذکر حضرت موسیٰ ؑ کے قصے میں آتا ہے۔ مصری تاریخ سے یہ بھی پتہ چلتا ہے کہ ان چرواہے بادشاہوں نے مصری دیوتاوٴں کو تسلیم نہیں کیا تھا، بلکہ اپنےدیوتا شام سے اپنے ساتھ لائے تھے اور ان کی کوشش یہ تھی کہ مصر میں ان کا مذہب رائج ہو۔ یہی وجہ ہے کہ قرآن مجید حضرت یوسف ؑ کے ہم عصر بادشاہ کو ”فرعون“کے نام سے یاد نہیں کرتا۔ کیونکہ ”فرعون“ مصر کی مذہبی اصطلاح تھی اور یہ لوگ مصری مذہب کے قائل نہ تھے۔ لیکن بائیبل میں غلطی سے اس کو بھی ”فرعون“ہی کا نام دیا گیا ہے۔ شاید اس کے مرتَّب کرنے والے سمجھتے ہوں گے کہ مصر کے سب بادشاہ ”فراعنہ“ہی تھے۔ موجودہ زمانہ کے محققین ، جنہوں نے بائیبل اور مصری تاریخ کا تقابل کیا ہے، عام رائے یہ رکھتے ہیں کہ چرواہے بادشاہوں میں سے جس فرمانروا کا نام مصری تاریخ میں اپوفیس ( Apophis ) ملتا ہے، وہی حضرت یوسف ؑ کا ہم عصر تھا۔ مصر کا دارالسلطنت اُس زمانہ میں ممفِس(منف) تھا جس کے کھنڈر قاہرہ کے جنوب میں ١۴ میل کے فاصلے پر پائے جاتے ہیں۔ حضرت یوسف ؑ ١۷ ، ١۸ سال کی عمر میں وہاں پہنچے۔ دو تین سال عزیز مصر کے گھر رہے۔ آٹھ نو سال جیل میں گزارے۔ ۳۰ سال کی عمر میں ملک کے فرمانروا ہوے اور ۸۰ سال تک بلا شرکت غیرے تمام مملکت مصر پر حکومت کرتے رہے۔ اپنی حکومت کے نویں یا دسویں سال انہوں نے حضرت یعقوب ؑ کو اپنے پورے خاندان کے ساتھ فلسطین سے مصر بلالیا اور اس علاقے میں آباد کیا جو دِمیاط اور قاہرہ کے درمیان واقع ہے۔ بائیبل میں اس علاقے کا جُشَن یا گوشن بتایا گیا ہے۔ حضرت موسیٰ ؑ کے زمانے تک یہ لوگ اسی علاقے میں آباد رہے۔ بائیبل کا بیان ہے کہ حضرت یوسف ؑ نے ایک سو دس سال کی عمر میں وفات پائی اور انتقال کے وقت بنی اسرائیل کو وصیت کی کہ جب تم اس ملک سے نکلو تو میری ہڈیاں اپنے ساتھ لے کر جانا۔یوسف ؑ کے قصے کی جو تفصیلات بائیبل اور تَلمُود میں بیان کی گئی ہیں ان سے قرآن کا بیان بہت کچھ مختلف ہے، مگر قصے کو اہم اجزاء میں تینوں متفق ہیں۔ ہم اپنے حواشی میں حسب ضرورت ان اختلافات کو واضح کرتے جائیں گے۔','سید ابو اعلیٰ مودودیؒ -  تفہیم القرآن');
INSERT INTO "chapters_ur" VALUES(13,'گرج',NULL,' نام : آیت نمبر ۱۳ کے فقرے وَیَسْبِحُ الرَّ عْدُ بَحَمْدِ ہٖ اَالْمَلٰٓئِکَۃُ مِنْ خِیْفَتِہٖ کے لفظ الرد کو اس سورۃ کا نام قرار دیا گیا ہے۔ اس نام کا یہ مطلب نہیں ہے کہ اس سورۃ میں بادل کی گرج کے مسئلے سے بحث کی گئی ہے، بلکہ یہ صرف علامت کے طور پر یہ ظاہر کرتا ہےکہ یہ وہ سورۃ ہے جس میں لفظ الرعد آیا ہے ، یا جس میں رعد کا ذکر آیاہے۔ زمانۂ نزُول: رکوع ۴ اور رکوع۶ کے مضامین شہادت دیتے ہیں کہ یہ سورۃ بھی اسی دور کی ہے جس میں سورہ یونس، ہود، اوراَعراف نازل ہوئی ہیں، یعنی زمانۂ قیام مکہ کا آخری دور۔ اندازِ بیان سے صاف ظاہر ہو رہا ہے کہ نبی صلی اللہ علیہ وسلم کو اسلام کی دعوت دیتے ہوئے ایک مدت دراز گز ر چکی ہے، مخالفین زِک دینے اور آپ کے مشن کو ناکام کر نے کے لیے طرح طرح کی چالیں چلتے رہے ہیں، مومنین بار بار تمنائیں کر رہے ہیں کہ کاش کو ئی معجزہ دکھا کر ہی ان لوگوں کو راہِ راست پر لایا جائے، اور اللہ تعالیٰ مسلمانوں کو سمجھا رہا ہے کہ ایمان کی راہ دکھانے کا یہ طریقہ ہمارے ہاں رائج نہیں ہے اور اگر شمنانِ حق کی رسی دراز کی جا رہی ہے تو یہ ایسی بات نہیں ہے کہ جس سے تم گھبرا اُٹھو۔ پھر آیت ۳۱ سے یہ بھی معلوم ہوتا ہے کہ بار بار کفار کی ہٹ دھرمی کا ایسا مظاہرہ ہو چکا ہے جس کے بعد یہ کہنا بالکل بجا معلوم ہوتا ہے کہ اگر قبروں سے مردے بھی اُٹھ کر آجائیں تو یہ لوگ نہ مانیں گے بلکہ اس واقعے کی بھی کوئی نہ کوئی تاویل کر ڈالیں گے ۔ ان سب باتوًں سے یہی گمان ہوتا ہے کہ یہ سورہ مکہ کے آخری دور میں نازل ہوئی ہو گی۔ مرکزی مضمون: ”سورۃ کا مدعا پہلے ہی آیت میں پیش کر دیا گیا ہے، یعنی یہ کہ جو کچھ محمد صلی اللہ علیہ وسلم پیش کر رہے ہیں وہی حق ہے، مگر یہ لوگوں کی غلطی ہے کہ وہ اسے نہیں مانتے۔ ساری تقریر اسی مرکزی مضمون کے گرد گھومتی ہے۔ اس سلسلے میں بار بار مختلف طریقوں سے توحید ، معاد اور رسالت کی حقانیت ثابت کی گئی ہے ، ان پر ایمان لانے کے اخلاقی و روحانی قوائد سمجھائے گئے ہیں، ان کو نہ ماننے کے نقصانات بتائے گئے ہیں، اور یہ ذہن نشین کیا گیا ہے کہ کفر سراسر ایک حماقت اور جہالت ہے۔ پھر چونکہ اس سارے بیان کا مقصد محض دماغوں کو مطمئن کرنا ہی نہیں ، دلوں کو ایمان کی طرف کھینچنا بھی ہے، اس لیے نرے منطقی استدلال سے کام نہیں لیا گیا ہے بلکہ ایک ایک دلیل اور ایک ایک شہادت کو پیش کرنے کے بعد ٹھہر کر طرح طرح سے تخویف ، ، ترہیب،ترغیب ، اور مشفقانہ تلقین کی گئی ہے تاکہ نادان لوگوں اپنی گمراہانہ ہٹ دھرمی سے باز آجائیں۔ دورانِ تقریر میں جگہ جگہ مخالفین کے اعتراضات کا ذکر کئے بغیر ان کے جوابات دیے گئے ہیں، اور ان شبہات کو رفع کیا گیا ہے جو محمد صلی اللہ علیہ وسلم کی دعوت کے متعلق لوگوں کے دلوں میں پائے جاتے تھے یا مخالفین کی طرف سے ڈالے جاتے تھے۔ اس کے ساتھ اہلِ ایمان کو بھی جو کئی برس کی طویل اور سخت جدوجہد کی وجہ سے تھکے جا رہے تھے اور بے چینی کے ساتھ غیبی امداد کے منتظر تھے ، تسلی دی گئی ہے۔ ','سید ابو اعلیٰ مودودیؒ -  تفہیم القرآن');
INSERT INTO "chapters_ur" VALUES(14,'ابراہیم',NULL,' نام : آیت ۳۵ کے فقرے وَاِذْ قَالَ اِبْرٰ ھِیْمُ رَبِّ اجْعَلْ ھٰذَا لْبَلَدَ اٰ مِنًا، سے ماخوذ ہے۔ اس نام کا مطلب یہ نہیں ہے کہ اس سورہ میں حضرت ابراہیمؑ کی سوانح عمری بیان ہوئی ہے، بلکہ یہ بھی اکثر سورتوں کے ناموں کی طرح علامت کے طور پر ہے۔ یعنی وہ سورہ جس میں ابراہیم علیہ السلام کا ذکر آیا ہے۔ زمانہ ٴنزول : عام اندازِ بیان مکّہ کے آخری دور کی سورتوں کا سا ہے۔ سورۂ رعد سے قریب زمانہ ہی کی نازل شدہ معلوم ہوتی ہے۔ خصُوصًا آیت ۱۳ کے الفاظ وَقَالَ الَّذِیْنَ کَفَرُوْا لِرُسُلِھِمْ لَنُخْرِجَنَّکُمْ مِّنْ اَرْضِنَآ اَوْ لَتَعُوْ دُنَّ فِیْ مِلَّتِنَا (انکار کرنے والوں نے اپنے رسولوں سے کہا کہ یا تو تمہیں ہماری مِلّت میں واپس آنا ہو گا ورنہ ہم تمہیں اپنے ملک سے نکال دیں گے) کا صاف اشارہ اس طرف ہے کہ اُس وقت مکہ میں مسلمانوں پر ظلم و ستم انتہا کو پہنچ چکا تھا اور اہلِ مکہ پچھلی کا فر قوموں کی طرح اپنے ہاں کے اہلِ ایمان کو خارج البلد کر دینے پر تُل گئے تھے ۔ اسی بناپر ان کو وہ دھمکی سنائی گئی جو اُن کے سے رویّہ پر چلنے والی پچھلی قوموں کو دی گئی تھی کہ لَنُھْلِکَنَّ الظّٰلِمِیْن َ (ہم ظالموں کو ہلاکر کر کے رہیں گے) اور اہلِ ایمان کو وہی تسلی دی گئی جو ان کے پیش رووں کو دی جاتی رہی ہے کہ لَنُسْکِنَنَّکُمُ الْاَرْضَ مِنْ م بَعْدِھِمْ (ہم ان ظالموں کو ختم کر نے کے بعد تم ہی کو ا س سر زمین میں آباد کریں گے)۔ مرکزی مضمون اور مُدّعا: جولوگ نبی صلی اللہ علیہ وسلم کی رسالت کو ماننے سے انکار کر رہے تھے اور آپ کی دعوت کو ناکام کر نے کے لیے ہر طرح کی بد تر سے بدتر چالیں چل رہے تھے ان کی فہمائش اور تنبیہ۔ لیکن فہمائش کی بہ نسبت اس سورہ میں تنبیہ اور ملامت اور زجر و توبیخ کا انداز زیادہ تیز ہے۔ اس کی وجہ یہ ہےکہ تفہیم کا حق اس سے پہلے کی سورتوں میں بخوبی ادا کیا جا چکا تھا اور اس کے باوجود کفارِ قریش کی ہٹ دھرمی ، عناد، مزاحمت ، شرارت اور ظلم و جود میں روز بروز اضافہ ہی ہوتا چلا جا رہا تھا۔ ','سید ابو اعلیٰ مودودیؒ -  تفہیم القرآن');
INSERT INTO "chapters_ur" VALUES(15,'پتھر',NULL,' نام: آیت ۸۰ کے فقرے کَذَّبَ اَصْحٰبُ الْحِجْرِ الْمُرْ سَلِیْنَ سے ماخوذ ہے۔ زمانہ ٴنزول: “مضامین اور انداز بیان سے صاف مترشح ہوتا ہے کہ اس سورے کا زمانۂ نزلو سورۂ ابراہیم سے متصل ہے۔ اس کے پس منظر میں دو چیزیں بالکل نمایاں نظر آتی ہیں۔ ایک یہ کہ نبی صلی اللہ علیہ وسلم کو دعوت دیتے ایک مُدّت گزر چکی ہے اور مخاطب قوم کی مسلسل ہٹ دھرمی ، استہزا، مزاحمت اور ظلم و ستم کی حد ہو گئی ہے ، جس کے بعد اب تفہیم کا موقع کم اور تنبیہ کا موقع زیادہ ہے۔ دوسرے یہ کہ اپنی قوم کے کفر و حجود اور مزاحمت کے پہاڑ توڑتے توڑتے نبی صلی اللہ علیہ وسلم تھکے جا رہے ہیں اور دل شکستگی کی کیفیت بار بار آپ پر طاری ہو رہی ہے ، جسے دیکھ کر اللہ تعالیٰ آپ کو تسلی دے رہا ہے اور آپ کی ہمت بندھا رہا ہے۔ موضوع اور مرکزی مضمون: یہی دو مضمون اس سورے میں بیان ہو ئے ہیں۔ یعنی تنبیہ اُن لوگوں کو جو نبی صلی اللہ علیہ وسلم کی دعوت کا انکار کر رہے تھے اور آپ کا مذاق اُڑاتے اور آپ کے کام میں طرح طرح کی مزاحمتیں کرتے تھے ۔ اور تسلی وہمّت افزائی آنحضور صلی اللہ علیہ وسلم کی۔ لیکن اس کا یہ مطلب نہیں کہ یہ سورہ تفہیم اور نصیحت سے خالی ہے۔ قرآن میں کہیں بھی اللہ تعالیٰ نے مجرد تنبیہ ، یا خالص زجرو توبیخ سے کام نہیں لیا ہے۔ سخت سے سخت دھمکیوں اور ملامتوں کے درمیان بھی وہ سمجھانے اور نصیحت کرنے میں کمی نہیں کرتا۔ چنانچہ اس سورے میں بھی ایک طرف توحید کے دلائل کی طرف مختصر اشارے کیے گئے ہیں ، اور دوسری طرف قصہ ٔ آدم و ابلیس سنا کر نصیحت فرمائی گئی ہے۔ ','سید ابو اعلیٰ مودودیؒ -  تفہیم القرآن');
INSERT INTO "chapters_ur" VALUES(16,'شہد کی مکھی',NULL,' نام : آیت ۶۸ کے فقرے وَاَوْ حٰی رَبُّکَ اِلَی النَّحْلِ سے ماخوذ ہے۔ یہ بھی محض علامت ہے نہ کہ موضوع ِ بحث کا عنوان۔ زمانہ ٴنزول : متعدد اندرونی شہادتوں سے اس کے زمانۂ نزول پر روشنی پڑتی ہے۔ مثلًا: آیت ۴۱ کے فقرے وَالَّذِیْنَ ھَاجَرُوْا فِی اللہِ مِنْم بَعْدِ مَا ظُلِمُوْ ا سے صاف معلوم ہوتا ہے کہ اُس وقت ہجرت حبشہ واقع ہو چکی تھی۔ آیت ۱۰۶ مَنْ کَفَرَ بِاللہِ مِنْم بَعْدِ اِیْمَانِہٖ الآ یہ سے معلوم ہوتا ہے کہ اس وقت ظلم و ستم پوری شدت کے ساتھ ہو رہا تھا اور یہ سوال پیدا ہو گیا تھا کہ اگر کوئی شخص ناقابلِ برداشت اذیّت سے مجبور ہو کر کلمہ ٔ کفر کہہ بیٹھے تو اس کا کیا حکم ہے۔ آیات ۱۱۲ – ۱۱۴ وَضَرَ بَ اللہُ مَثَلًا قَرْ یَۃً ۔۔۔۔۔۔ اِنْ کُنْتُمْ اِیَّا ہُ تَعْبُدُوْنَ کا صاف اشارہ اس طرف ہے کہ نبی صلی اللہ علیہ وسلم کی بعثت کے بعد مکّہ میں جو زبردست قحط رُونما ہوا تھا وہ اِس سورے کے نزول کے وقت ختم ہو چکا تھا۔ اِس سورہ میں آیت ۱۱۵۔ ایسی ہے جس کا حوالہ سورۂ اَنعام آیت ۱۱۹ میں دیا گیا ہے، اور دوسری آیت (نمبر ۱۱۸) ایسی ہے جس میں سورۂ اَنعام کی آیت ۱۴۶ کا حوالہ دیا گیا ہے۔ یہ اس بات کی دلیل ہے کہ ان دونوں سورتوں کا نزول قریب العہد ہے۔ ان شہادتوں سے پتہ چلتا ہے کہ اس سورے کا زمانۂ نزول بھی مکّے کا آخری دَور ہی ہے ، اور اسی کی تائید سُورے کے عام اندازِ بیان سے بھی ہوتی ہے۔ موضوع اور مرکزی مضمون: ”شرک کا ابطال ، توحید کا اثبات ، دعوتِ پیغمبر کو نہ ماننے کے بُرے نتائج پر تنبیہ و فہمائش ، اور حق کی مخالفت و مزاحمت پر زجرو توبیخ۔ مُباحِث: “سورے کا آغاز بغیر کسی تمہید کے یک لخت ایک تنبیہی جملے سے ہوتا ہے ۔ کفارِ مکہ بار بار کہتے تھے کہ ”جب ہم تمہیں جھٹلا چکے ہیں اور کھلم کھلا تمہاری مخالفت کر رہے ہیں تو آخر وہ خدا کا عذاب آکیوں نہیں جاتا جس کی تم ہمیں دھمکیاں دیتے ہو“۔ اس بات کو وہ بالکل تکیۂ کلام کی طرح اس لیے دہراتے تھے کہ ان کے نزدیک یہ محمد صلی اللہ علیہ وسلم کے پیغمبر نہ ہونے کا سب سے زیادہ صریح ثبوت تھا۔ اس پر فرمایا کہ بیوقوفو! خدا کا عذاب تو تمہارے سر پر تُلا کھرا ہے، اب اسکے ٹوٹ پڑنے کے لیے جلدی نہ مچاؤ بلکہ جو ذرا سی مہلت باقی ہے اس سے فائدہ اٹھا کہ بات سمجھنے کی کوشش کرو۔ اس کے بعد فورًا ہی تفہیم کی تقریر شروع ہو جاتی ہے اور حسبِ ذیل مضامین بار بار یکے بعد دیگرے سامنے آنے شروع ہوتے ہیں: (۱) دل لگتے دلائل اور آفاق و انفُس کے آثار کی کھلی کھلی شہادتوں سے سمجھا یا جاتا ہے کہ شرک باطل ہے اور توحید ہی حق ہے۔ (۲) منکرین کے اعتراضات ، شکوک ، حجتوں اور حیلوں کا ایک ایک کر کے جواب دیا جاتا ہے۔ (۳) باطل پر اصرار اور حق کے مقابلہ میں استکبار کے بُرے نتائج سے ڈرایا جاتا ہے۔ (۴) اُن اخلاقی اور عملی تغیرات کو مُجمَل مگر دل نشین انداز سے بیان کیا جاتا ہے جو محمد صلی اللہ علیہ وسلم کا لایا ہوا دین انسانی زندگی میں لانا چاہتا ہے ، اور اس سلسلہ میں مشرکین کو بتایا جاتا ہے کہ خدا کو رب ماننا ، جس کا انہیں دعویٰ تھا، محض خالی خولی مان لینا ہی نہیں ہے بلکہ اپنے کچھ تقاضے بھی رکھتا ہے جو عقائد ، اخلاق ا ور عملی زندگی میں نمودار ہونے چاہییں۔ (۵) نبی صلی اللہ علیہ وسلم اور آپ کے ساتھیوں کی ڈھارس بندھائی جاتی ہے اور ساتھ ساتھ یہ بھی بتایا جاتا ہے کہ کفار کی مزاحمتوں اور جفاکاریوں کے مقابلہ میں ان کا رویہ کیا ہونا چاہیے۔ ','سید ابو اعلیٰ مودودیؒ -  تفہیم القرآن');
INSERT INTO "chapters_ur" VALUES(17,'رات کا صفر',NULL,' نام : آیت ۴ کے فقرے وَقَضَیْنَآ اِلیٰ بَنِیْٓ اِسْرَآ ئِیْلَ فِی اْلکِتٰبِ سے ماخوذ ہے۔ مگر اس میں موضوع بحث بنی اسرائیل میں ہیں، بلکہ یہ نام بھی اکثر قرآنی سورتوں کی طرح صرف علامت کے طور پر رکھا گیا ہے۔ زمانہ ٴنزول : پہلی ہی آیت اس بات کی نشان دہی کر دیتی ہے کہ یہ سورت معراج کے موقع پر نازل ہوئی ہے ۔ معراج کا واقعہ حدیث اور سیرت کی اکثر روایات کے مطابق ہجرت سے ایک سال پہلے پیش آیا تھا اس لیے یہ سورت بھی انہی سورتوں میں سے ہے جو مکی دور کے آخری زمانے میں نازل ہوئیں۔ پس منظر: اس وقت نبی صلی اللہ علیہ وسلم کو توحید کی آواز بلند کرتے ہوئے ١۲ سال گزر چکے تھے۔ آپ کے مخالفین آپ کا راستہ روکنے کے لیے سارے جتن کر چکے تھے۔ مگر ان کی تمام مزاحمتوں کے با وجود آپ کی آواز عرب کے گوشے گوشے میں پہنچ گئی تھی۔ عرب کا کوئی قبیلہ ایسا نہ رہا تھا جس میں دو چار آدمی آپ کی دعوت سے متاثر نہ ہو چکے ہوں ۔ خود مکے میں ایسے مخلص لوگوں کا ایک مختصر جتھا بن چکا تھا جو ہر خطرے کو اس دعوت حق کی کامیابی کے لیے انگیز کرنے کو تیار تھے۔ مدینے میں اوس اور خَزرج کے طاقتور قبیلوں کی بڑی تعداد آپ کی حامی بن چکی تھی۔ اب وہ وقت قریب آلگا تھا جب آپ کو مکے سے مدینے کی طرف منتقل ہو جانے اور منتشر مسلمانوں کو سمیٹ کر اسلام کے اصولوں پر ایک ریاست قائم کردینے کا موقع ملنے والا تھا۔ ان حالات میں معراج پیش آئی، اور واپسی پر یہ پیغام نبی صلی اللہ علیہ وسلم نے دنیا کو سنایا۔ موضوع اور مضمون: اس سورت میں تنبیہ، تفہیم اور تعلیم ، تینوں ایک متناسب انداز میں جمع کر دی گئی ہیں۔ تنبیہ، کفار مکہ کو کی گئی ہے کہ بنی اسرائیل اور دوسری قوموں کے انجام سے سبق لو اور خدا کی دی ہوئی مہلت کے اندر ، جس کے ختم ہونے کا زمانہ قریب آلگا ہے، سنبھل جاؤ، اور اس دعوت کو قبول کر لو سے محمد صلی اللہ علیہ وسلم اور قرآن کے ذیعہ سے پیش کیا جا رہا ہے، ورنہ مٹا دیے جاؤ گے اور تمہاری جگہ دوسرے لوگ زمین پر بسائے جائیں گے۔ نیز ضمناً بنی اسرائیل کو بھی، جو ہجرت کے بعد عنقریب زبانِ وحی کے مخاطب ہونے والے تھے، یہ تنبیہ کی گئی ہے کہ پہلے جو سزائین تمہیں مل چکی ہیں اُن سے عبرت حاصل کرو اور اب جو موقع تمہیں محمد صلی اللہ علیہ وسلم کی بعثت سے مل رہا ہے اس سے فائدہ اُٹھاؤ، یہ آخری موقع بھی اگر تم نے کھو دیا اور پھر اپنی سابق روش کا اعادہ کیا تو دردناک انجام سے دوچار ہوگے۔ تفہیم کے پہلو میں بڑے دلنشین طریقے سے سمجھا یا گیا ہے کہ انسانی سعادت و شقاوت اور فلاح و اُن شبہات کو رفع کیا گیا ہے جو ان بنیادی حقیقتوں کے بارے میں کفار مکہ کی طرف سے پیش کیے جاتے تھے۔ اور استدلال کے ساتھ بیچ بیچ میں منکو ین کی جہالتوں پر زجرو توبیخ بھی کی گئی ہے۔ تعلیم کے پہلو میں اخلاق اور تمدن کے وہ بڑے بڑے اصول بیان کیے گئے ہیں جن پر زندگی کے نظام کو قائم کرنا دعوت محمدی کے پیش نظر تھا۔ یہ گویا اسلام کا منشور تھا جو اسلامی ریاست کے قیام سے ایک سال پہلے اہل عرب کے سامنے پیش کیاگیا تھا ۔ اس میں واضح طور پر بتا دیا گیا کہ یہ خاکہ ہے جس پر محمد صلی اللہ علیہ وسلم اپنے ملک کی اور پھر پوری انسانیت کی زندگی کو تعمیر کرنا چاہتے ہیں۔ ان سب باتوں کے ساتھ نبی صلی اللہ علیہ وسلم کو ہدایت کی گئی ہے کہ مشکلات کے اس طوفان میں مضبوطی کے ساتھ ساتھ اپنے موقف پر جمے رہیں اور کفر کے ساتھ مصالحت کا خیا ل تک نہ کریں۔ نیز مسلمانوں کو ، جو کبھی کبھی کفار کے ظلم و ستم اور ان کی کج بحثیوں ، اور ان کے طوفان کذب و افتراء پر بے ساختہ جھنجھلا اُٹھے تھے، تلقین کی گئی ہے کہ پورے صبر وسکون کے ساتھ حالات کا مقابلہ کرتے رہیں اور تبلیغ و اصلاح کے کام میں اپنے جذبات پر قابو رکھیں۔ اس سلسلہ میں اصلاح نفس اور تزکیہ نفس کے لیے اُن کو نماز کا نسخہ بتایا گیا ہے، کہ یہ وہ چیز ہے جو تم کو اُن صفات عالیہ سے متصف کرے گے جس سے راہ ِ حق کے مجاہدوں کو آراستہ ہونا چاہیے۔ روایات سے معلوم ہوتا ہے کہ یہ پہلا موقع ہے جب پنج وقتہ نماز پابندی اوقات کے ساتھ مسلمانوں پر فرض کی گئی۔','سید ابو اعلیٰ مودودیؒ -  تفہیم القرآن');
INSERT INTO "chapters_ur" VALUES(18,'غار',NULL,' نام : اس سورہ کا نام پہلے رکوع کی نویں آیت اِذْ اَوَی الْفِتْیَۃُ اِلَی الْکَھْفِ سے ماخوذ ہے۔ اس نام کا مطلب یہ ہے کہ وہ سورت جس میں کہف کا لفظ آیا ہے۔ زمانۂ نزول : یہاں سے ان سورتوں کا آغاز ہوتا ہے جو مکہ زندگی کے تیسرے دور میں نازل ہوئی ہیں۔ مکی زندگی کو ہم نے چار بڑے بڑے دوروں میں تقسیم کیا ہے جن کی تفصیل سورہ انعام کے دیباچے میں گزر چکی ہے۔ اس تقسیم کے لحاظ سے تیسرا دور تقریباً ۵ نبوی کے آغاز سے شروع ہو کر قریب قریب ۱۰ نبوی تک چلتا ہے۔ اس دور کو جو چیز دوسرے دور سے ممتاز کرتی ہے وہ یہ ہے کہ دوسرے دور میں تو قریش نے نبی صلی اللہ علیہ و سلم اور آپ کی تحریک اور جماعت کو دبانے کے لیے زیادہ تر تضحیک ، اِستہزاء ، اعتراضات، الزامات ، تخویف، اِطماع اور مخالفانہ پروپیگنڈے پر اعتماد کر رکھا تھا، مگر اس تیسرے دور میں انہوں نے ظلم و ستم ، مار پیٹ اور معاشی دباؤ کے ہتھیار پوری سختی کے ساتھ استعمال کیے ، یہاں تک کہ مسلمانوں کی ایک بڑی تعداد کو ملک چھوڑ کر حبش کی طرف نکل جانا پڑا اور باقی ماندہ مسلمانوں کو اور ان کے ساتھ خود نبی صلی اللہ علیہ و سلم اور آپ کے خاندان کو شعب ابی طالب میں محصور کر کیان کا مکمل معاشی اور معاشرتی مقاطعہ کر دیا گیا۔ تاہم اس دور میں دو شخصیتیں ابو طالب اور اُم المومنین حضرت خدیجہؓ ایسی تھیں جن کے ذاتی اثر کی وجہ سے قریش کے دو بڑے خاندان بنی صلی اللہ علیہ و سلم کی پشت پناہی کر رہے تھے۔ ۱۰ نبوی میں ان دونوں کی آنکھیں بند ہوتے ہی یہ دور ختم ہو گیا اور چوتھا دور شروع ہوا جس میں مسلمانوں پر مکے کی زندگی تنگ کر دی گئی یہاں تک کہ آخر کار نبی صلی اللہ علیہ و سلم سمیت تمام مسلمانوں کو مکہ سے نکل جانا پڑا۔ سورہ کہف کے مضمون پر غور کرنے سے اندازہ ہوتا ہے کہ یہ تیسرے دور کے آغاز میں نازل ہوئی ہوگی جبکہ ظلم و ستم اور مزاحمت نے شدت تو اختیار کر لی بھی، مگر ابھی ہجرت حبشہ واقع نہ ہوئی تھی۔ اس وقت جو مسلمان ستائے جا رہے تھے ان کو اصحاب کہف کا قصہ سنایا گیا تا کہ ان کی ہمت بندھے اور انہیں معلوم ہو کہ اہل ایمان اپنا ایمان بچانے کے لیے اس سے پہلے کیا کچھ کر چکے ہیں۔ موضوع اور مضمون: یہ سورہ مشرکین مکہ کے تین سوالات کے جواب میں نازل ہوئی ہے جو انہوں نے نبی صلی اللہ علیہ و سلم کا امتحان لینے کے لیے اہل کتاب کے مشورے سے آپ کے سامنے پیش کیے تھے : اصحاب کہف کون تھے ؟ قصہ خضر کی حقیقت کیا ہے ؟(روایات میں آتا ہے کہ دوسرا سوال روح کے متعلق تھا جس کا جواب سورہ بنی اسرائیل رکوع ۱۰ میں دیا گیا ہے۔ مگر سورہ کہف اور بنی اسرائیل کے زمانہ نزول میں کئی سال کا فرق ہے ، اور سورہ کہف میں دو کے بجائے تین قصے بیان کیے گئے ہیں ، اس لیے ہم سمجھتے کہ دوسرا سوال در اصل قصہ خضر سے متعلق تھا نہ کہ روح سے متعلق۔ خود قرآن میں بھی ایک اشارہ ایسا موجود ہے جس سے ہمارے اس خیال کی تائید ہوتی ہے ’’ملاحظہ ہو حاشیہ نمبر ۶۱‘‘)۔ اور ذوالقرنین کا کیا قصہ ہے ؟ یہ تینوں قصے عیسائیوں اور یہودیوں کی تاریخ سے متعلق تھے۔ حجاز میں ان کا کوئی چرچا نہ تھا۔ اسی لیے اہل کتاب نے امتحان کی غرض سے انکا انتخاب کیا تھا تاکہ یہ بات کھل جائے کہ واقعی محمد صلی اللہ علیہ و سلم کے پاس کوئی غیبی ذریعہ علم ہے یا نہیں۔ مگر اللہ تعالیٰ نے صرف یہی نہیں کہ اپنے نبی کی زبان سے ان کے سوالات کا پورا جواب دیا، بلکہ اُن کے اپنے پُوچھے ہوئے تینوں قصوں کو پوری طرح اس صورت حال پر چسپاں بھی کر دیا جو اس وقت مکہ میں کفر و اسلام کے درمیان در پیش تھی: ۱)۔ اصحاب کہف کے متعلق بتایا کہ وہ اسی توحید کے قائل تھے جس کی دعوت یہ قرآن پیش کر رہا ہے ، اور ان کا حال مکے کے مٹھی بھر مظلوم مسلمانوں کے حال سے اور ان کی قوم کا رویہ کفار قریش کے رویہ سے کچھ مختلف نہ تھا۔ پھر اسی قصے سے اہل ایمان کو یہ سبق دیا کہ اگر کفار کا غلبہ بے پناہ و اور ایک مومن کو ظالم معاشرے میں سانس لینے تک کی مہلت نہ دی جا رہی ہو، تب بھی اس کو باطل کے آگے سر نہ جھکانا چاہئے بلکہ اللہ کے بھروسے پرتن بتقدیر نکل جانا چاہیے۔ اسی سلسلے میں ضمناً کفار مکہ کو یہ بھی بتایا کہ اصحاب کہف کا قصہ عقیدۂ آخرت کی صحت کا ایک ثبوت ہے۔ جس طرح خدا نے اصحاب کہف کو ایک مدت دراز تک موت کی نیند سلانے کے بعد پھر جلا اُٹھایا اُسی طرح اُس کی قدرت سے وہ بعث بعد الموت بھی کچھ بعید نہیں ہے جسے ماننے سے تم انکار کر رہے ہو۔ ۲)۔ اصحاب کہف کے قصے سے راستہ نکال کر اس ظلم و ستم اور تحقیر و تذلیل پر گفتگو شروع کر دی گئی جو مکے کے سردار اور کھاتے پیتے لوگ اپنی بستی کی چھوٹی سی نو مسلم جماعت کے ساتھ برت رہے تھے۔ اس سلسلے میں ایک طرف نبی صلی اللہ علیہ و سلم کو ہدایت کی گئی کہ نہ ان ظالموں سے کوئی مصالحت کرو اور نہ اپنے غریب ساتھیوں کے مقابلے میں ان بڑے بڑے لوگوں کو کوئی اہمیت دو۔ دوسری طرف ان رئیسوں کو نصیحت کی گئی کہ اپنے چند روزہ عیش زندگانی پر نہ پھولو بلکہ ان بھلائیوں کے طالب بنو جو ابدی اور پائدار ہیں۔ ۳)۔ اسی سلسلۂ کلام میں قصہ خضر و موسیٰ کچھ اس انداز سے سنایا گیا کہ اس میں کفار کے سوالات کا جواب بھی تھا اور مومنین کے لیے سامان تسلی بھی۔ اس قصے میں در اصل جو سبق دیا گیا ہے وہ یہ ہے کہ اللہ بکی مشیت کا کارخانہ جن مصلحتوں پر چل رہا ہے وہ چونکہ تمہاری نظر سے پوشیدہ ہیں اس لیے تم بات بات پر حیران ہوتے ہو کہ یہ کیوں ہوا؟ یہ کیا ہو گیا؟ یہ تو بڑا غضب ہوا ! حالانکہ اگر پردہ اُٹھا دیا جائے تو تمہیں خود معلوم ہو جائے کہ یہاں جو کچھ ہو رہا ہے ٹھیک ہو رہا ہے اور بظاہر جس چیز میں برائی نظر آتی ہے ، آخر کار وہ بھی کسی نتیجہ خیر ہی کے لیے ہوتی ہے۔ ۴ )۔ اس کے بعد قصۂ ذوالقرنین ارشاد ہوتا ہے اور اس میں سائلوں کو یہ سبق دیا جاتا ہے کہ تم تو اپنی اتنی ذرا ذرا سی سرداریوں پر پھول رہے ہو ، حالانکہ ذوالقرنین اتنا بڑا فرمانروا اور ایسا زبردست فاتح اور اس قدر عظیم الشان ذرائع کا مالک ہو کر بھی اپنی حقیقت کو نہ بھولا تھا اور اپنے خالق کے آگے ہمیشہ سر تسلیم خم رکھتا تھا۔ نیز یہ کہ تم اپنی ذرا ذرا سی حویلیوں اور بغیچوں کی بہار کو لازوال سمجھ بیٹھے ہو، مگر وہ دنیا کی سب سے زیادہ مستحکم دیوار تحفظ بنا کر بھی یہی سمجھتا تھا کہ اصل بھروسے کے لائق اللہ ہے نہ کہ یہ دیوار۔ اللہ مرضی جب تک ہے یہ دیوار دشمنوں کو روکتی رہے گی۔ اور جب اس کی مرضی کچھ اور ہوگی تو اس دیوار میں رخنوں اور شگافوں کے سوا کچھ نہ رہے گا۔ اس طرح کفار کے امتحانی سوالات کو اّنہی پر پوری طرح اُلٹ دینے کے بعد خاتمۂ کلام میں پھر ان ہی باتوں کو دہرا دیا گیا ہے جو آغاز کلام میں ارشاد ہوئی ہیں ، یعنی یہ کہ توحید اور آخرت سراسر حق ہیں اور تمہاری اپنی بھلائی اسی میں ہے کہ انہیں مانو۔ ان کے مطابق اپنی اصلاح کرو اور خدا کے حضور اپنے آپ کو جواب دہ سمجھتے ہوئے دنیا میں زندگی بسر کرو۔ ایسا نہ کرو گے تو تمہاری اپنی زندگی خراب ہوگی اور تمہارا سب کچھ کیا کرایا اکارت جائے گا۔','سید ابو اعلیٰ مودودیؒ -  تفہیم القرآن');
INSERT INTO "chapters_ur" VALUES(19,'مریم',NULL,' نام: اس سورت کا نام آیت واذ کر فی الکتب مریم سے ماخوذ ہے ۔ مراد یہ ہے کہ وہ سورہ جس میں حضرت مریم کا ذکر آیا ہے۔ زمانہ نزول: اس کا زمانہ نزول ہجرت حبشہ سے پہلے کا ہے ۔ معتبر روایات سے معلوم ہو تا ہے کہ مہاجرین اسلام جب نجاشی کے دربار میں بلائے گئے تھے اس وقت حضرت جعفر نے یہی سورہ بھرے دربار میں تلاوت کی بھی۔ تاریخی پس منظر : جس دور میں یہ سورہ نازل ہوئی اس کے حالات کی طرف ہم کسی حد تک سورہ کہف کے دیباچے میں اشارہ کر چکے ہیں لیکن وہ مختصر اشارہ اس سورے کو اور دور کی دوسری سورتوں کو سمجھنے کے لئے کافی نہیں ہے ۔ اس لئے ہم ذرا اس وقت کے حالات زیادہ تفصیل کے ساتھ بیان کرتے ہیں ۔قریش کے سردار جب تضحیک ، استہزاء اطماع تخویف اور جھوٹے الزامات کی تشہیر سے تحریک اسلامی کو دبانے میں ناکام ہو گئے تو انہوں نے ظلم و ستم ، مار پیٹ اور معاشی دباؤ کے ہتھیار استعمال کرنے شروع کی ے ۔ ہر قبیلے کے لوگوں نے اپنے اپنے قبیلے کے نو مسلموں کو تنگ پکڑا اور طرح طرح سے ستا کر ، قید کر کے بھوک پیاس کی تکلیفیں دے کر ، حتی کہ سخت جسمانی اذیتیں دے دے کر انہیں اسلام چھوڑ نے پر مجبور کرنے کی کوشش کی ۔ اس سلسلے میں خصوصیت کے ساتھ غریب لوگ اور وہ غلام اور سوالی جو قریش والوں کے تحت زیر دست کی حیثیت سے رہتے تھے ، بری طرح پیسے گئے ۔ مثلاً بلال ، عامر بن مہر ، ام عبیس ، زبیرہ ، عمار بن یاسر اور ان کے والدین وغیرہ ہم ، ان لوگوں کو مار مار کر ادھ موا کر دیا جاتا ، بھوکا پیاسا بند رکھا جاتا ، مکے کی تپتی ہوئی ریت چلچلاتی دھوپ میں لٹا دیا جاتا اور سینے پر بھاری پتھر رکھ کر گھنٹوں تڑپا یا جاتا ۔ جو لوگ پیشہ ور تھے ان اس کام لیا جاتا اور اجرت ادا کرنے میں پریشان کیا جاتا ۔ چنانچہ صحیحین میں حضرت خباب بن ارت کی یہ روایت موجود ہے کہ : '''' میں مکے میں لوہار کا کام کرتا تھا ، مجھ سے عاص بن دائل نے کام لیا ، پھر جب میں اس سے اجرت لینے گیا تو اس نے کہا کہ تیری اجرت نہ دونگا جب تک تو محمد کا انکار نہ کرے"اسی طرح جو لوگ تجارت کرتے تھے ان کے کاروبار کر بر باد کرنے کی کوششیں کی جاتیں اور جو معاشرے میں کچھ عزت کا مقام رکھتے ان کو ہر طریقے سے ذلیل و رسوا کیا جاتا ۔ اسی زمانے کا حال بیان کرتے ہوئے حضرت خباب کہتے ہیں کہ ایک روز نبی صلى الله عليه وسلم کعبے کے سائے میں تشریف فرما تھے ۔ میں نے آپ کی خدمت حاضر ہو کر عرض کیا " یا رسول اللہ ۔ اب تو ظلم کی حد ہو گئی ہے ۔ آپ خدا سے دعا فرماتے ؟ " یہ سن کر آپ کا چہرہ مبارک تمتما اٹھا اور آپ نے فرمایا " تم سے پہلے جو اہل ایمان تھے ان پر اس سے زیادہ مظالم ہو چکے ہیں ۔ ان کی ہڈیوں پر لوہے کی کنگھیاں گھسی جاتی تھیں ، ان کے سروں پر رکھ کر آرے چلائے جاتے پھر بھی وہ اپنے دین سے نہ پھر تے تھے ۔ یقین جانو کہ اللہ اس کام کو پورا کر کے رہے گا یہاں تک کہ ایک وقت وہ آئے گا کہ ایک آدمی صنعاء سے حضر موت تک بے کھٹکے سفر کرے گا اور اللہ کے سوا کسی کا خوف نہ ہو گا ، مگر تم لوگ جلد بازی کرتے ہو" (بخاری)۔یہ حالات جب نا قابل برداشت حد تک پہنچ گئے تو رجب 45ہجری عام الفیل (شہ نبوی ) میں حضور نے اپنے اصحاب سے فرمایا کہ لوخرجتم الی ارض الحبشتہ فان بھا ملکالایطیلم عندہ احد وھی ارض صد حتی یجعل اللہ لکم فرجام ما انتم فیہ ۔'''' اچھا ہو کہ تم لوگ نکل کر حبش چلے جاؤ ۔ وہاں ایک ایسا بادشاہ ہے جس کے ہاں کسی پر ظلم نہیں ہوتا اور وہ بھلائی کی سر زمین ہے ۔ جب تک اللہ تمہاری اس مصیبت کو رفع کرنے کی کوئی صورت پیدا کرے ، تم لوگ وہاں ٹھیرے رہو۔''''اس ارشاد کی بنا پر پہلے گیارہ مردوں اور چار خواتین نے حبش کی راہ لی ۔ قریش کے لوگوں نے ساحل تک ان کا پیچھا کیا ، مگر خوش قسمتی سے شعیبہ کے بندر گاہ پر ان کو بر وقت حبش کے لئے کشتی مل گئی اور وہ گرفتار ہونے سے بچ گئے ۔ پھر چند مہینوں کے اندر مزید لوگوں نے ہجرت کی یہاں تک کہ 83 مرد گیارہ عورتیں اور 7 غیر قریشی مسلمان حبش میں جمع ہو گئے اور مکے میں نبی صلى الله عليه وسلم کے ساتھ صرف 40 آدمی رہ گئے ۔اس ہجرت سے مکے کے گھر گھر میں کہرام مچ گیا ، کیونکہ قریش کے بڑے اور چھوٹے خاندانوں میں سے کوئی ایسا نہ تھا جس کے چشم و چراغ ان مہاجرین میں شامل نہ ہوں ۔ کسی کا بیٹا گیا تو کسی کا داماد ، کسی کی بیٹی گئی تو کسی کا بھائی اور کسی کی بہن ۔ ابو جہل کے بھائی اور کسی کی بہن ۔ ابو جہل کے بھائی سلمہ بن ہشام ، اس کے چچا زاد بھائی ہشام بن ابی حذیفہ اور عیاش بن ابی ربیعہ اور اس کی چچا زاد بہن حضرت ام سلمہ ، ابو سفیان کی بیٹی ام حبیبیہ ۔ عتبہ کے بیٹے اور ہند جگر خوار کے سگے بھائی ابو حذیفہ ۔ سہل بن عمرد کی بیٹی سلہلہ ۔ اور اسی طرح دوسرے سردار قریش اور مشہور دشمنان اسلام کے اپنے جگر گوشے دین کی خاطر گھر بار چھوڑ کر نکل کھڑے ہوئے تھے ۔ اسی لئے کوئی گھر نہ تھا جو اس واقعہ سے متاثر نہ ہوا ہو۔ بعض لوگ اس کی وجہ سے اسلام دشمنی میں پلے سے زیادہ سخت ہو گئے ۔ اور بعض کے دلوں پر اس کا اثر ایسا ہوا کہ آخر کار وہ مسلمان ہو کر رہے ۔ چنانچہ حضرت عمر ؓ کی اسلام دشمنی پر پہلی چوٹ اسی واقعہ سے لگی ۔ ان کی ایک قریبی رشتہ دار لیلی بنت حشمہ بیان کرتی ہیں کہ میں ہجرت کے لئے اپنا سامان باندھ رہی تھی ، اور میرے شوہر عامر بن ربیعہ کسی کام سے باہر گئے ہوئے تھے ۔ اتنے میں عمر ؓ آئے اور کھڑے ہو کر میری مشغولیت کو دیکھتے رہے کچھ دیر کے بعد کہنے لگے ''''عبداللہ کی ماں ، جارہی ہو'''' ؟ میں نے کہا '''' ہاں خدا کی قسم تم لوگوں نے ہمیں ستایا۔ خدا کی زمین کھلی پڑی ہے ، اب ہم کسی ایسی جگہ چلے جائیں جہاں خدا ہمیں چین دے " یہ سن کر عمر کے چہرے پر رقت کے ایسے آثار طاری ہوئے جو میں نے کبھی ان پر نہ دیکھے تھے اور وہ بس یہ کہ کر نکل گئے کہ " خدا تمہارے ساتھ ہو" ۔ہجرت کے بعد قریش کے سردار سر جوڑ کر بیٹھے اور انہوں نے طے کیا کہ عبداللہ بن ابی ربیعہ (ابو جہل کے ماں جائے بھائی ) اور عمرو بن عاص کو بہت سے قیمتی تحائف کے ساتھ حبش جائے اور یہ لوگ کیس نہ کسی طرح نجاشی کو اس بات رپ راضی کریں کہ وہ ان مہا جرین کو مکہ واپس بھیج دے ۔ ام المومنین حضرت ام سلمہ نے (جو خود مہاجرین حبشہ میں شامل تھیں ) یہ واقعہ بڑی تفصیل کے ساتھ بیان کیا ہے ۔ وہ فرماتی ہیں کہ قریش کے یہ دونوں ماہر سیاست سفیر ہمارے تعاقب میں حبش ۔ پہلے انہوں نے نجاشی کے اعیان سلطنت میں خو ہدیہ تقسیم کر کے سب کو اس بات پر راضی کر لیا کہ وہ مہاجرین کو واپس کرنے کے لئے نجاشی پر بالاتفاق زور دیں گے ۔ پھر نجاشی سے ملے اور اس کو بیش قیمت نذرانہ دینے کے بعد کہا کہ '''' ہمارے شہر کے چند نادان زور دیں گے۔ پھر نجاشی سے ملے اور اس کو بیش قیمت نذرانہ دینے کے بعد کہا '''' ہمارے شہر کے چند نادان لونڈے بھاگ کر آپ کے ہاں آ گئے ہیں قوم کے اشرف نے ہمیں آپ کے پاس ان کی واپسی کو درخواست کرنے کے لئے بھیجا ہے ۔ یہ لڑ کے ہمارے دین سے نکل گئے ہیں اور آپ کے دین میں بھی داخل نہیں ہوئے ہیں بلکہ انہوں نے ایک نرالا دین نکال لیا نکال لیا ہے'''' ان کا کالم ختم ہوتے ہی اہل در بار ہر طرف سے بولنے لگے کہ " ایسے لوگوں کو ضرور واپس کر دینا چاہیے ، ان کی قوم کے لوگ زیادہ جانتے ہیں کہ ان میں کیا عیب ہے۔ انہیں رکھنا ٹھیک نہیں ہے۔ مگر نجاشی نے بگڑ کر کہا کہ " اس طرح تو میں انہیں حوالے نہیں کروں گا ۔ جن لوگوں نے دوسرے ملک کو چھوڑ کر میرے ملک پر اعتماد کیا اور یہاں پناہ لینے کے لئے آئے ان سے میں بے وفائی نہیں کر سکتا پہلے میں انہیں بلا کر تحقیق کروں گا کہ یہ لوگ ان کے بارے میں جو کچھ کہتے ہیں اس کی حقیقت کیا ہے " چنانچہ نجاشی نے اصحاب رسول اللہ صلی اللہ علیہ و سلم کو اپنے دربار میں بلا بھیجا۔نجاشی کا پیغام پا کر سب مہاجرین جمع ہوئے اور انہوں نے باہم مشورہ کیا کہ بادشاہ کے سامنے کیا کہنا ہے ۔ آخر سب نے بالاتفاق یہ فیصلہ کیا کہ نبی صلی اللہ علیہ و سلم نے جو تعلیم ہمیں دی ہے ہم تو وہی بے کم و کاست پیش کریں گے خواہ نجاشی کریں گے خواہ نجاشی ہمیں رکھے یا نکال دے ۔ دربار میں پہنچے تو چھوٹتے ہی نجاشی نے سوال کیا کہ " یہ تم لوگوں نے کیا کیا کہ اپنی قوم کا دین بھی چھوڑ اور میرے دین داخل نہ ہوئے ، نہ دنیا کے دوسرے ادیان ہی میں سے کسی کو اختیار کیا ؟ آخر یہ تمہارا نیا دین ہے کیا ''''؟ اس پر مہاجرین کی طرف سے جعفر بن ابی طالب نے ایک بر جستہ تقریر کی جس میں پہلے عرب جاہلیت کی دینی ، اخلاقی اور معاشرتی خرابیوں کی بیان کیا ، پھر نبی صلى الله عليه وسلم کی بعثت کا ذکر کر کے بتا یا کہ آپ کیا تعلیمات پیش فرماتے ہیں ، پھر ان مظالم کا ذکر کیا جو آنحضور کی پیروی اختیار کرنے والوں پر قریش کے لوگ ڈھا رہے تھے ، اور کلام اس بات پر ختم کیا دوسرے ملکوں کے بجا ئے ہم ے آپ کے ملک کا رح اس امید پر کیا ہے کہ یہاں ہم پر ظلم نہ ہو گا ۔ نجاشی نے یہ تقریر سن کر کہا کہ ذرا مجھے وہ کلام تو سناؤ جو تم کہتے وہ کہ خدا کی طرف سے تمہارے نبی پر اترا ہے۔حضرت جعفر نے جواب میں سورہ مریم کا وہ ابتدائی حصہ سنا یا جو حضرت یحیی اور حضرت عیسی علیہما اسلام سے متعلق ہے ۔ نجاشی اس کو سنتا رہا اور روتا رہا یہاں تک کہ اس کی ڈاڑھی تر ہو گئی جب حضرت جعفر نے تلاوت ختم کی تو اس نے کہا کہ '''' یقیناً یہ کلام اور جو کچھ عیسی لائے تھے دونوں ایک ہی سر چشمے سےنکلے ہیں ۔ خدا کی قسم میں تمھیں ان لوگوں کے حوالے نہ کروں گا " ۔دوسرے روز عمر و بن العاص نے نجاشی سے کہا کہ '''' ذرا ان لوگوں سے بلا کر یہ تو پوچھے کہ عیسی بن مریم کے بارے میں ان کا عقیدہ کیا ہے ۔ یہ لوگ ان کے متعلق ایک بڑی بات کہتے ہیں '''' نجاشی نے پھر مہاجرین کو بلا بھیجا ۔ مہاجرین کو پہلے سے عمر ذکی کی چال کا علم ہو چکا تھا ۔ انہوں نے جمع ہو کر پھر مشورہ کیا کہ اگر نجاشی نے عیسی ؑ کے بارے میں سوال کیا تو جواب دو گے ؟ موقع بڑا نازک تھا اور سب اس سے پریشان تھے ۔ مگر پھر بھی اصحاب رسول اللہ نے یہی فیصلہ کیا کہ جو کچھ ہوتا ہے ہو جائے ہم تو وہی بات کہیں گے جو اللہ نے فرمائی اور اللہ کے رسول نے سکھائی ۔چنانچہ جب یہ لوگ دربار میں گئے اور نجاشی نے عمر دین العاص کا پیش کردہ سوال ان کے سامنے دہرایا تو جعفر بن ابی طالب نے اٹھ کر بلاتا مل کہا کہ " ھو عبداللہ و رسولہ و رحہ کلمتہ القاھاالی مریم لاعزراء البتول ۔وہ اللہ کے بندے اور اس کے رسول ہیں اور اس کی طرف سے ایک روح اور ایک کلمہ ہیں جسے اللہ نے کنواری مریم پر القا کیا '''' نجاشی نے سن کر ایک تنکا زمین سے اٹھا یا اور کہا '''' خدا کی قسم جو کچھ تم نے کہا ہے عیسی اس سے تنکے کے برابر بھی زیادہ نہیں تھے ۔'''' اس کے بعد نجاشی نے قریش کے بھیجے ہوئے تمام ہدیے یہ کہ کر واپس کر دے کہ میں رشوت نہیں لیتا اور مہاجرین سے کہا کہ تم بالکل اطمینان کے ساتھ رہو۔ موضوع اور مضمون: اس تاریخی پس منظر کو نگاہ میں رکھ کر جب ہم اس سورے کو دیکھتے ہیں تو اس میں اولیں بات نمایاں ہو کر ہمارے سامنے یہ آتی ہے کہ اگر چہ مسلمان ایک مظلوم پناہ گزیں گروہ کی حیثیت سے اپنا وطن چھوڑ کر دوسرے ملک میں جا رہے تھے ، مگر اس حالت میں بھی اللہ تعالی نے ان کو دین کے معاملے میں ذرہ برابر مداہنت کرنے کی تعلیم نہ دی ، بلکہ چلتے وقت زاد راہ کے طور پر یہ سورہ ان کے ساتھ کی تاکہ عیسائیوں کے ملک میں عیسیؑ کی بالکل صحیح حیثیت پیش کریں اور انکے ابن اللہ ہونے کا صاف صاف انکار کر دیں۔ پہلے دو رکوعوں میں یحیی اور عیسی کا قصہ سنا نے کے بعد پھرتی سے رکوع میں حالات زمانہ کی مناسبت سے حضرت ابراہیمؑ کا قصہ سنا یا گیا ہے کیونکہ ایسے ہی حالات میں وہ بھی اپنے باپ اور خاندان اور اہل ملک کے ظلم سے تنگ آ کر وطن سے نکل کھڑے ہوئے تھے۔ اس سے ایک طرف کفار مکہ کو یہ سبق دیا گیا ہے کہ آج ہجرت کرنے والے مسلمان ابراہیمؑ کی پوزیشن میں ہیں اور تم لوگ ان ظالموں کی پوزیشن میں ہو جنہوں نے تمہارے باپ اور پیشواؤ ابراہیم ؑ کو گھر سے نکالا تھا ۔ دوسری طرف مہاجرین کوک یہ بشارت دی گئی ہے کہ جس طرح ابراہیم ؑ وطن سے نکل کر تباہ نہ ہوئے بلکہ اور زیادہ سر بلند ہو گئے ایسا ہی انجام نیک تمہارا انتظار کر رہا ہے۔ اس کے بعد چوتھ رکوع میں دوسرے انبیاء کا ذکر کیا گیا ہے جس سے یہ بتانا مقصود ہے کہ تمام انبیاءؑ وہی دین لے کر آئے تھے جو محمد صلى الله عليه وسلم لائے ہیں ، مگر انبیاء کے گزر جانے کے بعد ان کی امتیں بگڑتی رہی ہیں اور آج مختلف امتوں میں جو گمراہیاں پائی جا رہی ہیں یہ اسی بگاڑ کا نتیجہ ہیں ۔ آخر دو رکوعوں میں کفار مکہ کی گمراہیوں پر سخت تنقید کی گئی ہے اور کلام ختم کرتے ہوئے اہل ایمان کو مژدہ سنا یا گیا ہے کہ دشمنان حق کی سار ی کوششوں کے باوجود بالآخر تم محبوب خلائق ہو کر رہو گے۔','سید ابو اعلیٰ مودودیؒ -  تفہیم القرآن');
INSERT INTO "chapters_ur" VALUES(20,'طٰہٰ',NULL,' زمانۂ نزول : اس سورۃ کا زمانہ نزول سورہ مریم کے زمانے قریب ہی کا ہے۔ ممکن ہے کہ یہ ہجرت حبشہ کے زمانے میں یا اس کے بعد نازل ہوئی ہو۔ بہر حال یہ امر یقینی ہے کہ حضرت عمرؓ کے قبول اسلام سے پہلے یہ نازل ہو چکی تھی۔ ان کے قبول اسلام کی سب سے زیادہ مشہور اور معتبر روایت یہ ہے کہ جب وہ نبی صلی اللہ علیہ و سلم کو قتل کرنے کی نیت سے نکلے تو راستہ میں ایک شخص نے ان سے کہا کہ پہلے اپنے گھر کی خبر لو، تمہاری اپنی بہن اور بہنوئی اس نئے دین میں داخل ہو چکے ہیں۔ یہ سن کر حضرت عمر سیدھے بہن کے گھر پہنچے۔ وہاں ان کی بہن فاطمہؓ بنت خطاب اور ان کے بہنوئی سعید بن زید بیٹھے ہوئے حضرت خبابؓ بن اَرت سے ایک صحیفے کی تعلیم حاصل کر رہے تھے۔ حضرت عمر کے آتے ہی ان کی بہن نے صحیفہ فوراً چھپا لیا۔ مگر حضرت عمر اس کے پڑھنے کی آواز سن چکے تھے۔ انہوں نے پہلے کچھ پوچھ گچھ کی۔ اس کے بعد بہنوئی پر پل پڑے اور مارنا شروع کر دیا۔ بہن نے بچانا چاہا تو انہیں بھی مارا یہاں تک کہ ان کا سر پھٹ گیا۔ آخر کار بہن اور بہنوئی دونوں نے کہا کہ ہاں ، ہم مسلمان ہو چکے ہیں ، تم سے جو کچھ ہو سکے کر لو۔ حضرت عمر اپنی بہن کا خون بہتے دیکھ کر کچھ پشیمان سے ہو گئے اور کہنے لگے کہ اچھا مجھے بھی وہ چیز دکھاؤ جو تم لوگ پڑھ رہے تھے۔ بہن نے پہلے قسم لی کہ وہ اسے پھاڑ نہ دیں گے۔ پھر کہا کہ تم جب تک غسل نہ کر لو، اس پاک صحیفے کو ہاتھ نہیں لگا سکتے۔ حضرت عمرؓ نے غسل کیا اور پھر وہ صحیفہ لے کر پڑھنا شروع کیا۔ اس میں یہی سورہ طٰہٰ لکھی ہوئی تھی۔ پڑھتے پڑھتے یک لخت ان کی زبان سے نکلا ’’ کیا خوب کلام ہے ‘‘۔ یہ سنتے ہیں حضرت خباب بن ارت ، جو ان کی آہٹ پاتے ہی چھپ گئے تھے ، باہر آ گئے اور کہا کہ ’’بخدا، مجھے توقع ہے کہ اللہ تعالیٰ تم سے اپنے نبی کی دعوت پھیلانے میں بڑی خدمت لے گا، کل ہی میں نے نبی صلی اللہ علیہ و سلم کو یہ فرماتے سنا ہے کہ خدایا، ابولحکم بن ہشام (ابو جہل) یا عمر بن خطاب دونوں میں سے کسی کو اسلام کا حامی بنا دے۔ پس اے عمر ، اللہ کی طرف چلو ، اللہ کی طرف چلو‘‘۔ اس فقرے نے رہی سہی کسر پوری کر دی اور اسی وقت حضرت خباب کے ساتھ جا کر حضرت عمرؓ نے نبی صلی اللہ علیہ و سلم کی خدمت میں اسلام قبول کر لیا۔ یہ ہجرت حبشہ سے تھوڑی مدت بعد ہی کا قصہ ہے۔ موضوع و مبحث : سورہ کا آغاز اس طرح ہوتا ہے کہ اے محمدؐ ! یہ قرآن تم پر کچھ اس لیے نازل نہیں کیا گیا ہے کہ خواہ مخواہ بیٹھے بٹھائے تم کو ایک مصیبت میں ڈال دیا جائے۔ تم سے یہ مطالبہ نہیں ہے کہ پتھر کی چٹانوں سے دودھ کی نہر نکالو، نہ ماننے والوں کو منوا کر چھوڑو، اور ہٹ دھرم لوگوں کے سلوں میں ایمان پیدا کر کے دکھاؤ۔ یہ تو بس ایک نصیحت اور یاد دہانی ہے تاکہ جس کے دل میں خدا کا خوف ہو اور جو اس کی پکڑ سے بچنا چاہے وہ سن کر سیدھا ہو جائے۔ یہ مال زمین و آسمان کا کلام ہے۔ اور خدائی اس کے سوا کسی کی نہیں ، یہ دونوں حقیقتیں اپنی جگہ اٹل ہیں ، خواہ کوئی مانے یا نہ مانے۔ اس تمہید کے بعد یکایک حضرت موسیٰ کا قصہ چھیڑ دیا گیا ہے۔ بظاہر یہ محض ایک قصے کی شکل میں بیان ہوا ہے۔ وقت کے حالات کی طرف اس میں کوئی اشارہ تک نہیں ہے۔ مگر جس ماحول میں یہ قصہ سنایا گیا ہے ، اس کے حالات سے مل جل کر یہ اہل مکہ سے کچھ اور باتیں کرتا نظر آتا ہے جو اس کے الفاظ سے نہیں بلکہ اس کے بین السطور سے ادا ہو رہی ہیں۔ان باتوں کی تشریح سے پہلے یہ بات اچھی طرح سمجھ لیجیئے کہ عرب میں کثیر التعداد یہودیوں کی موجود گی اور اہل عرب پر یہودیوں کے علمی و ذہنی تفوق کی وجہ سے ، نیز روم اور حبش کی عیسائی سلطنتوں کے اثر سے بھی، عربوں میں بالعموم حضرت موسیٰ علیہ السلام کو خدا کا نبی تسلیم کیا جاتا تھا۔ اس حقیقت کو نظر میں رکھنے کے بعد اب دیکھیے کہ وہ باتیں کیا ہیں جو اس قصے کے بین السطور سے اہل مکہ کو جتائی گئی ہیں :۔ 1)۔ اللہ تعالیٰ کسی کو نبوت اس طرح عطا نہیں کیا کر تا کہ ڈھول تاشے اور نفیریاں بجا کر ایک خلق اکٹھی کر لی جائے اور پھر باقاعدہ ایک تقریب کی صورت میں یہ اعلان کیا جائے کہ آج سے فلاں شخص کو ہم نے نبی مقرر کیا ہے۔ نبوت تو جس کو بھی دی گئی ہے ، کچھ اسی طرح بصیغۂ راز دی گئی ہے جیسے حضرت موسیٰ کو دی گئی تھی۔ اب تمہیں کیوں اس بات پر اچنبھا ہے کہ محمد صلی اللہ علیہ و سلم یکایک نبی بن کر تمہارے سامنے آ گئے اور اس کا اعلان نہ آسمان سے ہوا نہ زمین پر فرشتوں نے چل پھر کر اس کو ڈھول پیٹا۔ ایسے اعلانات پہلے نبیوں کے تقرر پر کب ہوئے تھے کہ آج ہوتے ؟ 2 )۔ جو بات آج محمد صلی اللہ علیہ و سلم پیش کر رہے ہیں (یعنی توحید اور آخرت) ٹھیک وہی بات منصب نبوت پر مقرر کرتے وقت اللہ تعالیٰ نے موسیٰ علیہ السلام کو سکھائی تھی۔ 3)۔ پھر جس طرح آج محمد صلی اللہ علیہ و سلم کو بغیر کسی سرو سامان اور لاؤ لشکر کے تن تنہا قریش کے مقابلے میں دعوت حق کا عَلم بردار بنا کر کھڑا کر دیا گیا ہے ، ٹھیک اسی طرح موسیٰ علیہ السلام بھی یکایک اتنے بڑے کام پر مامور کر دیے گئے تھے کہ جا کر فرعون جیسے جبار بادشاہ کو سرکشی سے باز آنے کی تلقین کریں۔ کوئی لشکر ان کے ساتھ بھی نہیں بھیجا گیا تھا۔ اللہ تعالیٰ کے معاملے ایسے ہی عجیب ہیں۔ وہ مدین سے مصر جانے والے ایک مسافر کو راہ چلتے پکڑ کر بلا لیتا ہے اور کہتا ہے کہ جا اور وقت کے سب سے بڑے جابر حکمراں سے ٹکرا جا۔ بہت کیا تو اس کی درخواست پر اس کے بھائی کو مدد گار کے طور پر دے دیا۔ کوئی فوج فرّا اور ہاتھی گھوڑے اس کار عظیم کے لیے اس کو نہیں دیئے گئے۔ 4)۔ جو اعتراضات اور شبہات اور الزامات اور مکر و ظلم کے ہتھکنڈے اہل مکہ آج محمد صلی اللہ علیہ و سلم کے مقابلے میں استعمال کر رہے ہیں ان سے بڑھ چڑھ کر وہی سب ہتھیار فرعون نے موسیٰ علیہ السلام کے مقابلے میں استعمال کیے تھے۔ پھر دیکھ لو کہ کس طرح وہ اپنی ساری تدبیروں میں ناکام ہوا اور آخر کا کون غالب آ کر رہا؟ خدا کا بے سرو سامان نبی ؟ یا لاؤ لشکر والا فرعون ؟ اس سلسلہ میں خود مسلمانوں کو بھی ایک غیر ملفوظ تسلی دی گئی ہے کہ اپنی بے سر و سامانی اور کفار قریش کے سرو سامان پر نہ جائیں ، جس کام کے پیچھے خدا کا ہاتھ ہوتا ہے وہ آخر کار غالب ہی ہو کر رہتا ہے۔ اسی کے ساتھ مسلمانوں کے سامنے ساحران مصر کا نمونہ بھی پیش کیا گیا ہے کہ جب حق ان پر منکشف ہو گیا تو وہ بے دھڑک اس پر ایمان لے آئے اور پھر فرعون کے انتقام کا خوف انہیں بال برابر بھی ایمان کی راہ سے نہ ہٹا سکا۔ 5 )۔ آخر میں بنی اسرائیل کی تاریخ سے ایک شہادت پیش کرتے ہوئے یہ بھی بتایا گیا کہ دیوتاؤں اور معبودوں کے گھڑے جانے کی ابتدا کس مضحکہ انگیز طریقے سے ہوا کرتی ہے اور یہ کہ خدا کے نبی اس گھناؤنی چیز کا نام و نشان تک باقی رہنے کے کبھی روادار نہیں ہوئے ہیں۔ پس آج اس شرک اور بت پرستی کی جو مخالفت محمد صلی اللہ علیہ و سلم کر رہے ہیں وہ نبوت کی تاریخ میں کوئی پہلا واقعہ نہیں ہے۔ اس طرح قصۂ موسیٰ کے پیرائے میں ان تمام معاملات پر روشنی ڈالی گئی ہے جو اس وقت ان کی اور نبی صلی اللہ علیہ و سلم کی باہمی کشمکش سے تعلق رکھتے تھے۔ اس کے بعد ایک مختصر وعظ کیا گیا ہے کہ بہر حال یہ قرآن ایک نصیحت اور یاد دہانی ہے جو تمہاری اپنی زبان میں تم کو سمجھانے کے لیے بھیجی گئی ہے۔ اس پر کان دھرو گے اور اس سے سبق لو گے تو اپنا ہی بھلا کرو گے۔نہ مانو گے تو خود برا انجام دیکھو گے۔ پھر آدم علیہ السلام کا قصہ بیان کر کے یہ بات سمجھائی گئی ہے کہ جس روش پر تم لوگ جا رہے ہو یہ دراصل شیطان کی پیروی ہے۔ اَحیاناً شیطان کے بہکائے میں آ جاتا تو خیر ایک وقتی کمزوری ہے جس سے انسان بمشکل ہی بچ سکتا ہے۔ مگر آدمی کے لیے صحیح طریق کار یہ ہے کہ جب اس پر اس کی غلطی واضح کر دی جائے تو وہ اپنے باپ آدم کی طرح صاف صاف اس کا اعتراف کر لے ، توبہ کرے ، اور پھر خدا کی بندگی کی طرف پلٹ آئے۔ غلطی اور اس پر ہٹ اور نصیحت پر نصیحت کیے جانے پر بھی اس سے باز نہ آنا، اپنے پاؤں پر آپ کلہاڑی مارنا ہے جس کا نقصان آدمی کو خود ہی بھگتنا پڑے گا، کسی دوسرے کا کچھ نہ بگڑے گا۔ آخر میں نبی صلی اللہ علیہ و سلم اور مسلمانوں کو سمجھایا گیا ہے کہ ان منکرین حق کے معاملے میں جلدی اور بے صبری نہ کرو۔ خدا کا قاعدہ یہ ہے کہ وہ کسی قوم کو اس کے کفر انکار پر فوراً نہیں پکڑ لیتا بلکہ سنبھلنے کے لیے کافی مہلت دیتا ہے۔ لہٰذا گھبراؤ نہیں۔ صبر کے ساتھ ان لوگوں کی زیادتیاں برداشت کرتے چلے جاؤ۔ اور نصیحت کا حق ادا کرتے رہو۔ اسی سلسلے میں نماز کی تاکید کی گئی ہے تاکہ اہل ایمان میں صبر ، تحمل ، قناعت ، رضا بقضا اور احتساب کی وہ صفا ت پیدا ہوں جو دعوت حق کی خدمت کے لیے مطلوب ہیں۔ ','سید ابو اعلیٰ مودودیؒ -  تفہیم القرآن');
INSERT INTO "chapters_ur" VALUES(21,'پیغمبر',NULL,' نام : اس سورت کا نام کسی خاص آیت سے ماخوذ نہیں ہے ۔ چونکہ اس میں مسلسل بہت سے انبیاء کا ذکر آیا ہے ، اس لیے اس کا نام ’’ الانبیاء‘‘ رکھ دیا گیا۔ یہ بھی موضوع کے لحاظ سے سورۃ کا عنوان نہیں ہے بلکہ محض پہچاننے کے لیے ایک علامت ہے ۔ زمانہ نزول: مضمون اور انداز بیان، دونوں سے یہی معلوم ہوتا ہے کہ اس کا زمانہ نزول مکے کا دور متوسط یعنی ہماری تقسیم کے لحاظ نبی صلی اللہ علیہ وسلم کی مکی زندگی کا تیسرا دور ہے ۔ اس کے پس منظر میں حالات کی وہ کیفیت نہیں پائی جاتی جو آخری دور کی سورتوں میں نمایا ں طور پر محسوس ہوتی ہے ۔ موضوع و مضمون: اس سورہ میں وہ کشمکش زیر بحث ہے جو نبی صلی اللہ علیہ وسلم اور سرداران قریش کے درمیان برپا تھی ۔ وہ لوگ آنحضرتؐ کے دعوائے رسالت اور آپ کی دعوتِ توحید و عقیدہ آخرت پر جو شکوک اور اعتراضات پیش کرتے تھے ان کا جواب دیا گیا ہے ۔ انکی طرف سے آپ کی مخالفت میں جو چالیں چلی جا رہی تھیں ان پر زجر و توبیخ کی گئی ہے اور ان حرکتوں کے برے نتائج سے آگاہ کیا گیا ہے ۔ وہ جس غفلت اور بے پروائی سے آپ کی دعوت کا استقبال کر رہے تھے اس پر متنبہ کیا گیا ہے ۔ اور آخر میں ان کو یہ احساس دلایا گیا ہے کہ جس شخص کو تم اپنے لیے زحمت اور مصیبت سمجھ رہے ہو وہ در اصل تمہارے لیے رحمت بن کر آیا ہے ۔ دوران تقریر میں خاص طور پر جو امور زیر بحث آئے ہیں وہ یہ ہیں : 1) کفار مکہ کی یہ غلط فہمی کہ بشر کبھی رسول نہیں ہو سکتا اور اس بنا پر ان کا نبی صلی اللہ علیہ وسلم کو رسول ماننے سے انکار کرنا ۔۔۔۔۔ اس کا بڑی تفصیل کے ساتھ رد کیا گیا ہے ۔ 2) ان کا آپ پر اور قرآن پر مختلف اور متضاد قسم کے اعتراضات کرنا اور کسی ایک بات پر نہ جمنا ۔۔۔۔۔۔۔۔۔اس پر مختصر مگر نہایت پر زور اور معنی خیز طریقے سے گرفت کی گئی ہے ۔ 3) ان کا یہ تصور کہ زندگی بس ایک کھیل ہے جسے چند روز کھیل کر یونہی ختم ہو جانا ہے ، اس کا کوئی نتیجہ نہیں نکلتا ہے ، کسی حساب کتاب اور جزا و سزا سے سابقہ نہیں پیش آنا ہے ۔ ۔۔۔۔۔۔یہ چیز چونکہ اس عجلت و بے اعتنائی کی اصل جڑ تھی جس کے ساتھ وہ نبی صلی اللہ علیہ وسلم کی دعوت کا استقبال کر رہے تھے ، اس لیے بڑے ہی مؤثر انداز میں اس کا توڑ کیا گیا ہے ۔ 4) شرک پر ان کا اصرار اور توحید کے خلاف ان کا جاہلانہ تعصب جوان کے اور نبی صلی اللہ علیہ وسلم کے درمیان اصل بنائے نزاع تھا ۔۔۔۔۔۔۔۔۔۔۔ اس اصلاح کے لیے شرک کے خلاف اور توحید کے حق میں مختصر مگر بہت وزنی اور دلنشین دلائل دیے گئے ہیں ۔ 5) ان کی یہ غلط فہمی کہ نبی کو بار بار جھٹلانے کے باوجود جب ان پر کوئی عذاب نہیں آتا تو ضرور نبی جھوٹا ہے اور عذاب الٰہی کی وہ وعیدیں جو وہ خدا کی طرف سے ہمیں سناتا ہے محض خالی خولی دھمکیاں ہیں ۔۔۔۔۔۔۔ اس کو استدلال اور نصیحت ، دونوں طریقوں سے رفع کرنے کی کوشش کی گئی ہے ۔ اس کے بعد انبیاء علیہم السلام کی سیرتوں کے اہم واقعات سے چند نظیریں پیش کی گئی ہیں جن سے یہ سمجھانا مقصود ہے کہ تمام وہ پیغمبر جو انسانی تاریخ کے دوران میں خدا کی طرف سے آئے تھے ، انسان تھے اور نبوت کے امتیازی وصف کو چھوڑ کر دوسری صفات میں وہ ویسے ہی انسان ہوتے تھے جیسے دنیا کے عام انسان ہوا کرتے ہیں ۔ اُلوہیت اور خدائی کا ان میں شائبہ تک نہ تھا بلکہ اپنی ہر ضرورت کے لیے وہ خود خدا کے آگے ہاتھ پھیلاتے تھے ۔ اس اس کے ساتھ انہی تاریخی نظیروں سے دو باتیں اور بھی واضح کی گئی ہیں ۔ ایک یہ کہ انبیاء پر طرح طرح کے مصائب آئے ہیں ، اور ان کے مخالفین نے بھی ان کو برباد کرنے کی کوششیں کی ہیں ، مگر آخر کار اللہ تعالیٰ کی طرف سے غیر معمولی طریقوں پر ان کی نصرت فرمائی گئی ہے ۔ دوسرے یہ کہ تمام انبیاء کا دین ایک تھا اور وہ وہی دین تھا جسے محمد صلی اللہ علیہ وسلم پیش کر رہے ہیں ۔ نوع انسانی کا اصل دین یہی ہے ، اور باقی جتنے مذاہب دنیا میں بنے ہیں وہ محض گمراہ انسانوں کے ڈالے ہوئے تفرقے ہیں ۔ آخر میں یہ بتایا گیا ہے کہ انسان کی نجات کا انحصار اسی دین کی پیروی اختیار کرنے پر ہے ۔ جو لوگ اسے قبول کریں گے وہی خدا کی آخری عدالت سے کامیاب نکلیں گے اور زمین کے وارث ہوں گے ۔ اور جو لوگ اسے رد کر دیں گے وہ آخرت میں بد ترین انجام سے دوچار ہوں گے ۔ اللہ تعالیٰ کی یہ بڑی مہربانی ہے کہ وہ فیصلے کے وقت سے پہلے اپنے نبی کے ذریعہ سے لوگوں کو اس حقیقت سے آگاہ کر رہا ہے ۔ نادان ہیں وہ لوگ جو نبی کی آمد کو اپنے لیے رحمت کے بجائے زحمت سمجھ رہے ہیں ۔ ','سید ابو اعلیٰ مودودیؒ -  تفہیم القرآن');
INSERT INTO "chapters_ur" VALUES(22,'حج',NULL,' نام: چوتھے رکوع کی دوسری آیت وَاَذِّنْ فِی النَّاسِ بِا لْحَجِّ سے ماخوذ ہے۔ زمانۂ نزول: اس سورے میں مکی اور مدنی سورتوں کی خصوصیات ملی جلی پائی جاتی ہیں۔ اسی وجہ سے مفسریں میں اس امر پر اختلاف ہوا ہے کہ یہ مکی ہے کہ مدنی۔ لیکن ہمارے نزدیک اس کے مضامین اور انداز بیان کا یہ رنگ اس وجہ سے ہے کہ اس کا ایک حصہ مکی دور کے آخر میں اور دوسرا حصہ مدنی دور کے آغاز میں نازل ہوا ہے۔ اس لیے دونوں ادوار کی خصوصیات اس میں جمع ہو گئی ہیں۔ ابتدائی حصے کا مضمون اور انداز بیان صاف بتاتا ہے کہ یہ مکہ میں نازل ہوا ہے اور اغلب یہ ہے کہ مکہ زندگی کے آخری دور میں ہجرت سے کچھ پہلے نازل ہوا ہو۔ یہ حصہ آیت 24 ( وَھُدُوْ آ اِلَی الظَّیِّبِ مِنَ الْقَوْلِ وَھُدُوْٓ اِلیٰ صِرَاطِ الْحَمِیْدِ ) پر ختم ہوتا ہے۔ اس کے بعد : اِنَّ الَّذِیْنَ کَفَرُوْ ا وَیَصُدُّ وْ نَ عَنْ سَبِیْلِ اللہِ سے یک لخت مضمون کا رنگ بدل جاتا ہے اور صاف محسو ہوتا ہے کہ یہاں سے آخر تک کا حصہ مدینہ طیبہ میں نازل ہوا ہے۔ بعید نہیں کہ یہ ہجرت کے بعد پہلے ہی سال ذی الحجہ میں نازل ہوا ہو، کیونکہ آیت 25 سے 41 تک کا مضمون اسی بات کی نشان دہی کرتا ہے ، اور آیت 39۔ 40 کی شان نزول بھی اس کی مؤید ہے۔ اس وقت مہاجرین ابھی تازہ تازہ ہی اپنے گھر بار چھوڑ کر مدینے میں آئے تھے۔ حج کے زمانے میں ان کو اپنا شہر اور حج کا اجتماع یاد آ رہا ہو گا اور یہ بات بری طرح کھل رہی ہو گی کہ مشرکین قریش نے ان پر مسجد حرام کا راستہ تک بند کر دیا ہے۔ اس زمانے میں وہ اس بات کے بھی منتظر ہوں گے کہ جن ظالموں نے ان کو گھروں سے نکالا ، مسجد حرام کی زیارت سے محروم کیا ، اور خدا کا راستہ اختیار کرنے پر ان کی زندگی تک دشوار کر دی ، ان کے خلاف جنگ کرنے کی اجازت مل جائے۔ یہ ٹھیک نفسیاتی موقع تھا ان آیات کے نزول کا۔ ان میں پہلے تو حج کا ذکر کرتے ہوئے یہ بتایا گیا ہے کہ یہ مسجد حرام اس لیے بنائی گئی تھی اور یہ حج کا طریقہ اس لیے شروع کیا گیا تھا کہ دنیا میں کدائے واحد کی بندگی کی جائے، مگر آج وہاں شرک ہو رہا ہے اور خدائے واحد کی بندگی کرنے والوں کے لیے اس کے راستے بند کر دیے گئے ہیں۔ اس کے بعد مسلمانوں کو اجازت دے دی گئی ہے کہ وہ ان ظالموں کے خلاف جنگ کریں اور انہیں بے دخل کر کے ملک میں وہ نظام صالح قائم کریں جس میں برائیاں دبںا اور نیکیاں فروغ پائیں۔ ابن عباس ، مجاہد ، عَرْوَہ بن زُبَیر ، زید بن اسلم، مقاتل بن حیان ، قَآتَدہ اور دوسرے اکابر مفسرین کا بیان ہے کہ یہ پہلی آیت ہے جس میں مسلمانوں کو جنگ کی اجازت دی گئی۔ اور حدیث و سیرت کی روایات سے ثابت ہے کہ اس اجازت کے بعد فوراً ہی قریش کے خلاف عملی سرگرمیاں شروع کر دی گئیں اور پہلی مہم صفر 2 ھ میں ساحل بحر احمر کی طرف روانہ ہوئی جو غزوہ ود ان یا غزوہ اَبَواء کے نام سے مشہور ہے۔ موضوع و مبحث: اس سورہ میں تین گروہ مخاطب ہیں۔ مشرکین مکہ، مذبذب اور متردد مسلمان، اور مومنین صادقین۔ مشرکین سے خطاب کی ابتدا مکے میں کی گئی اور مدینے میں اس کا سلسلہ پورا کیا گیا۔ اس خطاب میں ان کو پورے زور کے ساتھ متنبہ کیا گیا ہے کہ تم نے ضد اور ہٹ دھرمی کے ساتھ اپنے بے بنیاد جاہلانہ خا لات پر اصرار کیا، خدا کو چھوڑ کر ان معبودوں پر اعتماد کیا جن کے پاس کوئی طاقت نہیں ہے ، اور خدا کے رسول کو جھٹلا دیا۔ اب تمہارا انجام وہی کچھ ہو کر رہے گا جو تم سے پہلے اس روش پر چلنے والوں کا ہو چکا ہے۔ نبی کو جھٹلا کر اور اپنی قوم کے صالح ترین عنصر کو نشانہ ستم بنا کر تم نے اپنی ہی کچھ بگاڑی۔ اس کے نتیجے میں خدا کا جو غضب تم پر نازل ہو گا اس سے تمہارے بناوٹی معبود تمہیں نہ بچا سکیں گے۔ اس تنبیہ و اِنذار کے ساتھ افہام و تفہیم کا پہلو بالکل خالی نہیں چھوڑ دیا گیا ہے۔ پوری سورۃ میں جگہ جگہ تذکیر اور نصیحت بھی ہے اور شرک کے خلاف اور توحید و آخرت کے حق میں مؤثر دلائل بھی پیش کیے گئے ہیں۔ مذبذب مسلمان ، جو خدا کی بندگی قبول تو کر چکے تھے مگر اس راہ میں کوئی خطرہ برداشت کرنے کے لیے تیار نہ تھے ، ان کو خطاب کرتے ہوئے سخت سرزنش کی گئی ہے۔ ان سے کہا گیا ہے کہ یہ آخر کیسا ایمان ہے کہ راحت، مسرت ، عیش نصیب ہو تو خدا تمہارا خدا اور تم اس کے بندے۔ مگر جہاں خدا کی راہ میں مصیبت آئی اور سختیاں جھیلنی پڑیں ، پھر نہ خدا تمہارا خدا رہا اور نہ تم اس کے بندے رہے۔ حالانکہ تم اپنی اس روش سے کسی ایسی مصیبت اور نقصان اور تکلیف کو نہیں ٹال سکتے جو خدا نے تمہارے نصیب میں لکھ دی ہو۔ اہل ایمان سے خاکب دو طریقوں پر کیا گیا ہے۔ ایک خطاب ایسا ہے جس میں وہ خود بھی مخاطب ہیں اور عرب کی رائے عام بھی۔ اور دوسرے خطاب میں صرف اہل ایمان مخاطب ہیں۔ پہلے خطاب میں مشرکین مکہ کی اس روش پر گرفت کی گئی ہے کہ انہوں نے مسلمانوں کے لیے مسجد حرام کا راستہ بند کر دیا ہے ، حالانکہ مسجد حرام ان کی ذاتی جائداد نہیں ہے اور وہ کسی کو حج سے روکنے کا حق نہیں رکھتے۔ یہ اعتراض نہ صرف یہ کہ بجائے خود حق بجانب تھا، بلکہ سیاسی حیثیت سے یہ قریش کے خلاف ایک بہت بڑا حربہ بھی تھا۔ اس سے عرب کے تمام دوسرے قبائل کے ذہن میں یہ سوال پیدا کر دیا گیا کہ قریش حرم کے مجاور ہیں یا مالک ؟ اگر آج اپنی ذاتی دشمنی کی بنا پر وہ ایک گروہ کو حج سے روک دیتے ہیں اور اس کو برداشت کر لیا جاتا ہے تو کیا بعید ہے کہ کل جس سے بھی ان کے تعلقات خراب ہوں اس کو وہ حدود حرم میں داخل ہونے سے روک دیں اور اس کا عمرہ و حج بند کر دیں۔ اس سلسلے میں مسجد حرام کی تاریخ بیان کرتے ہوئے ایک طرف یہ بتایا گیا ہے کہ ابراہیم علیہ السلام نے جب خدا کے حکم سے اس کو تعمیر کیا تھا تو سب لوگوں کو حج کا اذن عام دیا تھا، اور وہاں اول روز سے مقامی باشندوں اور باہر سے آنے والوں کے حقوق یکساں قرار دیے گئے تھے۔ دوسری طرف یہ بتایا گیا ہے کہ گھر شرک کے لیے نہیں بلکہ خدائے واحد کی بندگی کے لیے تعمیر ہوا تھا۔ اب یہ کیا غضب ہے کہ وہاں ایک خدا کی بندگی تو ہو ممنوع اور بتوں کی پرستش کے لیے ہو پوری آزادی۔ دوسرے خطاب میں مسلمانوں کو قریش کے ظلم کا جواب طاقت سے دینے کی اجازت عطا کی گئی ہے اور ساتھ ساتھ ان کو یہ بھی بتایا گیا ہے کہ جب تمہیں اقتدار حاصل ہو تو تمہاری روش کیا ہونی چاہیے اور اپنی حکومت میں تم کو کس مقصد کے لیے کام کرنا چاہیے۔ یہ مضمون سورہ کے وسط میں بھی ہے اور آخر میں بھی۔ آخر میں گروہ اہل ایمان کے لیے ’’ مسلم‘’‘ کے نام کا باقاعدہ اعلان کرتے ہوئے یہ فرمایا گیا ہے کہ ابراہیمؑ کے اصل جانشین تم لوگ ہو ، تمہیں اس خدمت کے لیے منتخب کر لیا گیا ہے کہ دنیا میں شہادت علی الناس کے مقام پر کھڑے ہو، اب تمہیں اقامت صلوٰۃ ، ایتائے زکوٰۃ اور فعل الخیرات سے اپنی زندگی کو بہترین نمونے کی زندگی بنانا چاہیے۔ اس موقعہ پر سورہ بقرہ اور سورہ انفال کے دیباچوں پر بھی نگاہ ڈال لی جائے تو سمجھنے میں زیادہ سہولت ہو گی۔ ','سید ابو اعلیٰ مودودیؒ -  تفہیم القرآن');
INSERT INTO "chapters_ur" VALUES(23,'مومنین',NULL,' نام: پہلی ہی آیت اَدْ اَ فْلَحَ الْمُؤْ مِنُوْنَ سے ماخوذ ہے۔ زمانۂ نزول: انداز بیان اور مضامین ، دونوں سے یہی معلوم ہوتا ہے کہ اس سورے کا زمانہ نزول مکے کا دور متوسط ہے۔ پس منظر میں صاف محسوس ہوتا ہے کہ اگرچہ نبی صلی اللہ علیہ وسلم اور کفار کے درمیان سخت کشمکش برپا ہے ، لیکن ابھی کفار کے ظلم و ستم نے پورا زور نہیں پکڑا ہے۔ آیت 75۔ 76 سے صاف طور پر یہ شہادت ملتی ہے کہ یہ مکے کے اس قحط کی شدت کے زمانے میں نازل ہوئی ہے جو معتبر روایات کی رو سے اسی دور متوسط میں برپا ہوا تھا۔ عروہ بن زُبیر کی ایک روایت سے معلوم ہوتا ہے کہ اس وقت حضرت عمر ایمان لا چکے تھے۔ وہ عبدالرحمٰن بن عبدالقاری کے حوالہ سے حضرت عمرؓ کا یہ قول نقل کرتے ہیں کہ یہ سورۃ ان کے سامنے نازل ہوئی ہے۔ وہ خود نزول وحی کی کیفیت کو نبی صلی اللہ علیہ وسلم پر طاری ہوتے دیکھ رہے تھے ، اور جب حضورؐ اس سے فارغ ہوئے تو آپ نے فرمایا کہ مجھ پر اس وقت دس ایسی آیتیں نازل ہوئی ہیں کہ اگر کوئی ان کے معیار پر پورا اتر جائے تو یقیناً جنت میں جائے گا، پھر آپ نے اس سورے کی ابتدائی آیات سنائیں (احمد، ترمذی ، نسائی، حاکم)۔ موضوع اور مباحث: اتباع رسول کی دعوت اس سورت کا مرکزی مضمون ہے اور پوری تقریر اسی مرکز کے گرد گھومتی ہے۔ آغاز کلام اس طرح ہوتا ہے کہ جن لوگوں نے اس پیغمبر کی بات مان لی ہے ، ان کے اندر یہ اور یہ اوصاف پیدا ہو رہے ہیں ، اور یقیناً ایسے ہی لوگ دنیا و آخرت کی فلاح کے مستحق ہیں۔ اس کے بعد انسان کی پیدائش، آسمان و زمین کی پیدائش، نباتات و حیوانات کی پیدائش، اور دوسرے آثار کائنات کی طرف توجہ دلائی گئی ہے ، جس سے مقصود یہ ذہن نشین کرنا ہے کہ توحید اور معاد کی جن حقیقتوں کو ماننے کے لیے یہ پیغمبر تم سے کہتا ہے ان کے برحق ہونے پر تمہارا اپنا وجود اور یہ پورا نظام عالم گواہ ہے۔ پھر انبیاء علیہم السلام اور ان کی امتوں کے قصے شروع کیے گئے ہیں ، جو بظاہر تو قصے ہی نظر آتے ہیں ، لیکن در اصل اس پیرائے میں چند باتیں سامعین کو سمجھائی گئی ہیں : اول یہ کہ آج تم لوگ محمد صلی اللہ علیہ وسلم کی دعوت پر جو شبہات و اعتراضات وارد کر رہے ہو وہ کچھ نئے نہیں ہیں۔ پہلے بھی جو انبیاء دنیا میں آئے تھے ، جن کو تم خود فرستادہ الہٰی مانتے ہو، ان سب پر ان کے زمانے کے جاہلوں نے یہی اعتراضات کیے تےی۔ اب دیکھ لو کہ تاریخ کا سبق کیا بتا رہا ہے۔ اعتراضات کرنے والے بر حق تھے یا انبیاء؟ دوم یہ کہ توحید و آخرت کے متعلق جو تعلیم محمد صلی اللہ علیہ وسلم دے رہے ہیں یہی تعلیم ہر زمانے کے انبیاء نے دی ہے۔ اس سے مختلف کوئی نرالی چیز آج نہیں پیش کی جا رہی ہے جو کبھی دنیا نے نہ سنی ہو۔ سوم یہ کہ جن قوموں نے انبیاء کی بات سن کر نہ دی اور ان کی مخالفت پر اصرار کیا وہ آخر کار تباہ ہو کر رہیں۔ چہارم یہ کہ خدا کی طرف سے ہر زمانے میں ایک ہی دین آتا رہا ہے اور تمام انبیاء ایک ہی امت کے لوگ تھے۔ اس دین واحد کے سوا جو مختلف مذاہب تم لوگ دنیا میں دیکھ رہے ہو یہ سب لوگوں کے طبع زاد ہیں۔ ان میں سے کوئی بھی منجانب اللہ نہیں ہے۔ ان قصوں کے بعد لوگوں کو یہ بتایا گیا ہے کہ دنیوی خوش حالی ، مال و دولت، آل داد لاد چشم و خَدَمِ قوت و اقتدار وہ چیزیں نہیں ہیں جو کسی شخص یا گروہ کے راہ راست پر ہونے کی یقینی علامت ہوں اور اس بات کی دلیل قرار دی جائیں کہ خدا اس پر مہربان ہے اور اس کا رویہ خدا کو محبوب ہے۔ اسی طرح کسی کا غریب اور خستہ حال ہونا بھی اس بات کا ثبوت نہیں ہے کہ خدا اس سے اور اس کے رویے سے ناراض ہے۔ اصل چیز جس پر خد اکے ہاں محبوب یا مغضوب ہونے کا مدار ہے وہ آدمی کا ایمان اور اس کی خدا ترسی و راست بازی ہے۔ یہ باتیں اس لیے ارشاد ہوئی ہیں کہ نبی صلی اللہ علیہ وسلم کی دعوت کے مقابلے میں اس وقت جو مزاحمت ہو رہی تھی اس کے علم بردار سب کے سب مکے کے شیوخ اور بڑے بڑے سردار تھے۔ وہ اپنی جگہ خود بھی یہ گھمنڈ رکھتے تھے ، اور ان کے زیر اثر لوگ بھی اس غلط فہمی میں مبتلا تھے کہ نعمتوں کی بارش جن لوگوں پر ہو رہی ہے اور جو بڑھتے ہیں چلے جا رہے ہیں ان پر ضرور خدا اور دیوتاؤں کا کرم ہے۔ رہے یہ ٹوٹے مارے لوگ جو محمد کے ساتھ ہیں ، ان کی تو حالت خود ہی یہ بتا رہی ہے کہ خدا ان کے ساتھ نہیں ہے ، اور دیوتاؤں کی مار ہی ان پر پڑی ہوئی ہے۔ اس کے بعد اہل مکہ کو مختلف پہلوؤں سے نبی صلی اللہ علیہ وسلم کی نبوت پر مطمئن کرنے کی کوشش کی گئی ہے۔ پھر ان کو بتایا گیا ہے کہ یہ قحط جو تم پر نازل ہوا ہے ، یہ ایک تنبیہ ہے۔ بہتر ہے کہ اس کو دیکھ کر سنبھلو اور راہ راست پر آ جاؤ۔ ورنہ اس کے بعد سخت تر سزا آئے گی جس پر بلبلا اٹھو گے۔ پھر ان کو از سر نو ان آثار کی طرف توجہ دلائی گئی ہے جو کائنات میں اور خود ان کے اپنے وجود میں موجود ہیں۔ مدعا یہ ہے کہ آنکھیں کھول کر دیکھو، جس توحید اور جس حیات بعد الموت کی حقیقت سے یہ پیغمبر تک کو آگاہ کر رہا ہ ، کیا ہر طرف اس کی شہادت دینے والے آثار پھیلے ہوئے نہیں ہیں ؟ کیا تمہاری عقل اور فطرت اس کی صحت و صداقت پر گواہی نہیں دیتی ؟ پھر نبی صلی اللہ علیہ وسلم کو ہدایت کی گئی ہے کہ خواہ یہ لوگ تمہارے مقابلے میں کیسا ہی برا رویہ اختیار کریں ، تم بھلے طریقوں ہی سے مدافعت کرنا۔ شیطان کبھی تم کو جوش میں لا کر برائی کا جواب برائی سے دینے پر آمادہ نہ کرنے پائے۔ خاتمہ کلام پر مخالفینِ حق کو آخرت کی باز پرس سے ڈرایا گیا ہے اور انہیں متنبہ کیا گیا ہے کہ جو کچھ تم دعوت حق و راس کے پیروؤں کے ساتھ کر رہے ہو اس کا سخت حساب تم سے لیا جائے گا۔ ','سید ابو اعلیٰ مودودیؒ -  تفہیم القرآن');
INSERT INTO "chapters_ur" VALUES(24,'نور',NULL,' نام: پانچویں رکوع کی پہلی آیت اَللہُ نُوْرُ السَّمٰوٰتِ وَالْاَرْ ضِ سے ماخوذ ہے۔ زمانہ نزول: یہ امر متفق علیہ ہے کہ یہ سورت غزوہ بنی الْمُصْطَلِق کے بعد نازل ہوئی ہے۔ خود قرآن کے بیان سے ظاہر ہوتا ہے کہ اس کا نزول واقعہ اِفْک کے سلسلے میں ہوا ہے (جس کا ذکر تفصیل کے ساتھ دوسرے اور تیسرے رکوع میں آیا ہے ) اور وہ تمام معتبر روایات کی رو سے غزوہ بنی المطلق کے سفر میں پیش آیا تھا۔ لیکن اختلاف اس امر میں ہے کہ آیا یہ غزوہ 5 ہجری میں غزوہ اَحزاب سے پہلے ہوا تھا یا 6 میں غزوہ احزاب کے بعد۔ اصل واقعہ کیا ہے ، اس کی تحقیق اس لیے ضروری ہے کہ پردے کے احکام قرآن مجید کی دو ہی سورتوں میں آئے ہیں ، ایک یہ سورت، دوسری سورہ احزاب جس کا نزول بالاتفاق غزوہ احزاب کے موقع پر ہوا ہے۔ اب اگر غزوہ احزاب پہلے ہو تو اس کے معنی یہ ہی کہ پردے کے احکام کی ابتدا ان ہدایات سے ہوئی جو سورہ احزاب میں وارد ہوئی ہیں ، اور تکمیل ان احکام سے ہوئی جو اس سورت میں آئے ہیں۔ اور اگر غزوہ بنی المصطلق پہلے ہو تو احکام کی ترتیب الٹ جاتی ہے اور آغاز سورہ نور سے مان کر تکمیل سورہ احزاب والے احکام پر ماننی پڑتی ہے۔ اس طرح اس حکمت تشریع کا سمجھنا مشکل ہو جاتا ہے جو احکام حجاب میں پائی جاتی ہے۔ اسی غرض کے لیے ہم آگے بڑھنے سے پہلے زمانہ نزول کی تحقیق کر لینا ضروری سمجھتے ہیں۔ ابن سعد کا بیان ہے کہ غزوہ بنی المصطلق شعبان 5 ہجری میں پیش آیا اور پھر ذی القعدہ 5 ھ میں غزوہ احزاب (یا غزوہ خندق ) واقع ہوا۔ اس کی تائید میں سب سے بڑی شہادت یہ ہے کہ واقعہ افک کے سلسلے میں حضرت عائشہؓ سے جو روایات مروی ہیں ان میں سے بعض میں حضرت سعد بن عبادہ اور سعد بن معاذ کے جھگڑے کا ذکر آتا ہے ، اور تمام معتبر روایات کی رو سے حضرت سعد بن معاذ کا انتقال غزوہ بن قریظہ میں ہوا تھا جس کا زمانہ واقع غزوہ احزاب کے متصلاً بعد ہے ، لہٰذا 6 ھ میں ان کے موجود ہونے کا کوئی امکان نہیں۔ دوسری طرف محمد بن اسحاق کا بیان ہے کہ غزوہ احزاب شوال 5 ھ کا واقعہ ہے اور غزوہ بنی المصطلق شعبان 6 ھ کا۔ اس کی تائید وہ کثیر التعداد معتبر روایات کرتی ہیں جواس سلسلہ میں حضرت عائشہؓ اور دوسرے لوگوں سے مروی ہیں۔ ان سے معلوم ہوتا ہے کہ واقعہ افک سے پہلے احکام حجاب نازل ہو چکے تھے ، اور وہ سورہ احزاب میں پائے جاتے ہیں۔ ان سے یہ بھی معلوم ہوتا ہے کہ اس وقت حضرت زینبؓ سے نبی صلی اللہ علیہ و سلم کا نکاح ہو چکا تھا، اور وہ غزوہ احزاب کے بعد ذی القعدہ 5 ھ کا واقعہ ہے اور سورہ احزاب میں اس کا بھی ذکر آتا ہے۔ علاوہ بریں ان روایات سے یہ بھی معلوم ہوتا ہے کہ حضرت زینب کی بہن حمْنَہ بنت جحش نے حضرت عائشہؓ پر تہمت لگانے میں محض اس وجہ سے حصہ لیا تھا کہ حضرت عائشہؓ ان کی بہن کی سوکن تھیں ، اور ظاہر ہے کہ بہن کی سوکن کے خلاف اس طرح کے جذبات پیدا ہونے کے لیے سوکناپے کا رشتہ شروع ہونے کے بعد کچھ نہ کچھ مدت درکار ہوتی ہے۔ یہ سب شہادتیں ابن اسحاق کی روایت کو مضبوط کر دیتی ہیں۔ اس روایت کو قبول کرنے میں صرف یہ چیز مانع ہوتی ہے کہ واقعہ افک کے زمانے میں حضرت سعد بن معاذ کی موجودگی کا ذکر آیا ہے۔ مگر اس مشکل کو جو چیز رفع کر دیتی ہے وہ یہ ہے کہ اس واقعہ کے متعلق حضرت عائشہؓ سے جو روایات مروی ہیں ان میں سے بعض میں حضرت سعد بن معاذ کا ذکر ہے اور بعض میں ان کے بجائے حضرت اُسَید بن حُضَیر کا۔ اور یہ دوسری روایت ان دوسرے واقعات کے ساتھ پوری طرح مطابق ہو جاتی ہے جو اس سلسلے میں خود حضرت عائشہؓ ہی سے مروی ہیں۔ ورنہ محض سعد بن معاذ کے زمانہ حیات سے مطابق کرنے کی خاطر اگر غزوہ بنی المصطلق اور قصہ افک کو غزوہ احزاب و قریظہ سے پہلے کے واقعات مان لیا جائے تو اس پیچیدگی کا کوئی حل نہیں ملتا کہ پھر آیت حجاب کا نزول اور نکاح زینبؓ کا واقعہ اس سے بھی پہلے پیش آنا چاہیے ، حالانکہ قرآن اور کثیر التعداد روایات صحیحہ ، دونوں اس پر شاہد ہیں کہ نکاح زینبؓ اور حکم حجاب احزاب و قریْظَہ کے بعد کے واقعات ہیں۔ اسی بنا پر ابن حزم اور ابن قیم اور بعض دوسرے محققین نے محمد بن اسحٰق کی روایت ہی کو صحیح قرار دیا ہے ، اور ہم بھی اس کی صحیح سمجھتے ہیں۔ تاریخی پس منظر : اب یہ تحقیق ہو جانے کے بعد کہ سورہ نور 6 ہجری کے نصف آخر میں سورہ احزاب کے کئی مہینے بعد نازل ہوئی ہے ، ہمیں ان حالات پر ایک نگاہ ڈال لینی چاہیے جن میں اس کا نزول ہوا۔ جنگ بدر کی فتح سے عرب میں تحریک اسلامی کا جو عروج شروع ہوا تھا وہ غزوہ خندق تک پہنچتے پہنچتے اس حد تک بڑھ چکا تھا کہ مشرکین، یہود، منافقین اور متربصین ، سب ہی یہ محسوس کرنے لگے تھے کہ اس نو خیز طاقت کو محض ہتھیاروں اور فوجوں کے بل پر شکست نہیں دی جا سکتی۔ جنگ خندق میں ہ لوگ متحد ہو کر 10 ہزار فوج کے ساتھ مدینے پر چڑھ آئے تھے ، مگر ایک مہینے تک سر مارنے کے بعد آخر کار ناکام ہو کر چلے گئے اور ان کے جاتے ہیں نبی صلی اللہ علیہ و سلم نے علی الاعلان فرما دیا، لن تغزوکم قریش بعد عامکم ھٰزا ، ولکنکم تغزو نَھم (ابن ہشام، جلد 3 ، 266)، ’’ اس سال بعد اب قریش تم پر چڑھائی نہیں کریں گے بلکہ تم ان پر چڑھائی کرو گے ‘‘۔ یہ گویا اس امر کا اعلان تھا کہ مخالف اسلام طاقتوں کی قوت اقدام ختم ہو چکی ہے ، اب اسلام بچاؤ کی نہیں بلکہ اقدام کی لڑائی لٹے گا اور کفر کو اقدام کے بجائے بچاؤ کی لڑائی لڑنی پڑے گی۔ یہ حالات کا بالکل صحیح جائزہ تھا جسے دوسرا فریق بھی اچھی طرح محسوس کر رہا تھا۔ اسلام کے اس روز افزوں عروج کی اصل وجہ مسلمانوں کی تعداد نہ تھی۔ بدر سے خندق تک ہر لڑائی میں کفار ان سے کئی گئی زیادہ قوت لے کر آئے تھے ، اور مردم شماری کے لحاظ ے بھی مسلمان اس وقت تک عرب میں بمشکل 1/10فی صدی تھے۔ اس عروج کی وجہ مسلمانوں کے اسلحہ کی بر تری بھی نہ تھی۔ ہر طرح کے سازو سامان میں کفار ہی کا پلہ بھاری تھا۔ معاشی طاقت اور اثر و رسوخ کے اعتبار سے بھی مسلمانوں کا ان سے کوئی مقابلہ نہ تھا۔ ان کے پاس تمام عرب کے معاشی وسائل تھے ، اور مسلمان بھوکوں مر رہے تھے۔ ان کی پشت پر تمام عرب کے مشرک اور اہل کتاب قبائل تھے ، اور مسلمان ایک نئے دین کی دعوت دے کر قدیم نظام کے سارے حامیوں کی ہمدردیاں کھو چکے تھے۔ ان حالات میں جو چیز مسلمانوں کو برابر آگے بڑھائے لیے جا رہی تھی، وہ در اصل مسلمانوں کی اخلاقی بر تری تھی جسے تمام دشمنان اسلام خود بھی محسوس کر رہے تھے۔ ایک طرف وہ دیکھتے تھے کہ نبی صلی اللہ علیہ و سلم اور صحابہ کرام کےسم داغ سیرتیں ہیں جن کی طہارت و پاکیزگی اور مضبوطی دلوں کو مسخر کرتی چلی جا رہی ہے۔ اور دوسری طرف انہیں صاف نظر آ رہا تھا کہ انفرادی و اجتماعی اخلاق کی طہارت نے مسلمانوں کے اندر کمال درجے کا اتارد اور نظم و ضبط بھی پیدا کر دیا ہے جس کے سامنے مشرکین اور یہود کا ڈھیلا نظام جماعت امن اور جنگ دونوں حالتوں میں شکست کھاتا چلا جاتا ہے۔ کمینہ خصلت لوگوں کا خاصہ ہوتا ہے کہ جب وہ دوسرے کی خوبیاں اور اپنی کمزوریاں صریح طور پر دیکھ لیتے ہیں ، اور یہ بھی جان لیتے ہیں کہ اس کی خوبیاں اسے بڑھا رہی ہیں اور ان کی اپنی کمزوریاں انہیں گرا رہی ہیں ، تو انہیں یہ فکر لاحق نہیں ہوتی کہ اپنی کمزوریاں دور کریں اور اس کی خوبیاں اخذ کریں ، بلکہ وہ اس فکر میں لگ جاتے ہیں کہ جس طرح بھی ہو سکے اس کے اندر بھی اپنے ہی جیسی برائیاں پیدا کر دیں ، بلکہ وہ اس فکر میں لگ جاتے ہیں کہ جس طرح بھی ہو سکے اس کے اندر بھی اپنے ہی جیسی برائیاں پیدا کر دیں ، اور یہ نہ ہو سکے تو کم از کم اس کے اوپر خوب گندگی اچھالیں تاکہ دنیا کو اس کی خوبیاں بے داغ نظر نہ آئیں۔ یہی ذہنیت تھی جس نے اس مرحلے پر دشمانان اسلام کی سرگرمیوں کا رخ جنگی کار روائیوں سے ہٹا کر رذیلانہ حملوں اور داخلہ فتنہ انگیزیوں کی طرف پھیر دیا۔ اور چونکہ یہ خدمت باہر کے دشمنوں کی بہ نسبت خود مسلمانوں کے اندر کے منافقین زیادہ اچھی طرح انجام دے سکتے تھے ، اس لیے بلارادہ یا بلا ارادہ طریق کار یہ قرار پایا کہ مدینہ کے منافقین اندر سے فتنے اٹھائیں اور یہود و مشرکین باہر سے ان کا زیادہ سے زیادہ فائدہ اٹھانے کی کوشش کریں۔ اس نئی تدبیر کا پہلا ظہور ذی القعدہ 5 ھ میں ہوا جب کہ نبی صلی اللہ علیہ و سلم نے عرب سے تَبْنِیَت (دوسرے کے بیٹے کو اپنا بیٹا بنانا اور خاندان میں اسے بالکل صُلبی بیٹے کی حیثیت دے دینا۔ ) کی جاہلانہ رسم کا خاتمہ کرنے کے لیے خود اپنے متبنیٰ(زیدؓ بن حارثہ) کی مطَلَّقہ بیوی (زینبؓ بنت حجش) سے نکاح کیا۔ اس موقع پر مدینے کے منافقین پروپیگنڈا کا ایک طوفان عظیم لے کر اٹھ کھڑے ہوئے اور باہر سے یہود و مشرکین نے بھی ان کی آواز میں آواز ملا کر افترا پردازیاں شروع کر دیں۔ انہوں نے عجیب عجیب قصے گھڑ گھڑ کر پھیلا دیے کہ محمد (صلی اللہ علیہ و سلم) کس طرح اپنے منہ بولے بیٹے کی بیوی کو دیکھ کر اس پر عاشق ہو گئے ، اور کس طرح بیٹے کو ان کی عشق کا علم ہوا اور وہ طالق دے کر بیوی سے دست بردار ہو گیا، اور پھر کس طرح انہوں نے خود اپنی بہو سے بیاہ کر لیا۔ یہ قصے اس کثرت سے پھیلائے گئے کہ مسلمان تک ان کے اثرات سے نہ بچ سکے۔ چنانچہ محدثین اور مفسرین کے ایک گروہ نے حضرت زینبؓ اور زید کے متعلق جو روایتیں نقل کی ہیں ان میں آج تک ان من گھڑت قصوں کے اجزا پائے جاتے ہیں اور مستشرقین مغرب ان کو خوب نمک مرچ لگا کر اپنی کتابوں میں پیش کرتے ہیں۔ حالانکہ حضرت زینبؓ نبی صلی اللہ علیہ و سلم کی حقیقی پھوپھی (اُمَیمہ بنت عبدالمطلب) کی صاحبزادی تھیں بچپن سے جوانی تک ان کی ساری عمر حضورؐ کی آنکھوں کے سامنے گزری تھی، ان کو اتفاقاً ایک روز دیکھ لینے اور معاذ اللہ ان پر عاشق ہو جانے کا کوئی سوال ہی پیدا نہیں ہوتا۔ پھر اس واقعہ سے ایک ہی سال پہلے نبی صلی اللہ علیہ و سلم نے خود ان کو مجبور کر کے حضرت زید سے ان کی شادی ی تھی۔ ان کے بھائی عبداللہ بن حجش اس شادی سے ناراض تھے۔ خود حضرت زینبؓ اس پر راضی نہ تھیں ، کیونکہ ایک آزاد کردہ غلام کی بیوی بننا قریش کے شریف ترین گھرانے کی بیٹی طبعاً قبول نہ کر سکتی تھی۔ مگر نبی صلی اللہ علیہ و سلم نے صرف اس لیے کہ مسلمانوں میں معاشرتی مساوات قائم کرنے کی ابتدا خود اپنے خاندان سے کریں ، نہیں حکماً اس پر راضی کیا تھا۔ یہ ساری باتیں دوست اور دشمن سب کو معلوم تھیں ، اور یہ بھی کسی سے چھپا ہوا نہ تھا کہ حضرت زینب کا احساس فخر نسبی ہی وہ اصل وجہ تھی جس کی بنا پر ان کا اور زید بن حارثہ کا نباہ نہ ہو سکا اور آخر کار طلاق تک نوبت پہنچی۔ مگر اس کے باوجود بے شرم افترا پروازوں نے نبی صلی اللہ علیہ و سلم پر بد ترین اخلاقی الزامات لگائے اور ان کو اس کثرت سے رواج دیا کہ آج تک انکا یہ پروپیگنڈا اپنا رنگ دکھا رہا ہے۔ اس کے بعد دوسرا حملہ غزوہ بنی الصطَلِق کے موقع پر کیا گیا ، اور یہ پہلے سے بھی زیادہ سخت تھا۔ بنی المصطلق قبیلہ بنی خزاعہ کی ایک شاخ تھی جو ساحل بحر احمر پر جدے اور رابع کے درمیان قدید کے علاقے میں رہتی تھی۔ اس کے چشمے کا نام مریسیع تھا جس کے آس پاس اس قبیلے کے لوگ آباد تھے۔ اس مناسبت سے احادیثمیں اس مہم کا نام غزوہ رُرَیسیع بھی آیا ہے۔ نقشے سے اس کی صحیح جائے وقوع معلوم ہو سکتی ہے۔ شعبان 6ھ میں نبی صلی اللہ علیہ و سلم کو اطلاع ملی کہ یہ لوگ مسلمانوں کے خلاف جنگ کی تیاریاں کر رہے ہیں اور دوسرے قبائل کو بھی جمع کرنے کی کوشش میں لگے ہوئے ہیں۔ یہ اطلاع پاتے ہی آپ ایک لشکر لے کر ان کی طرف روانہ ہو گئے تاکہ فتنے کے سر اٹھانے سے پہلے ہی اسے کچل دیا جائے۔ اس مہم میں عبد اللہ بن ابی بھی منافقوں کی ایک بڑی تعداد لے کر آپ کے ساتھ ہو گیا۔ ابن سعد کا بیان ہے کہ اس سے پہلے کسی جنگ میں منافقین اس کثرت سے شامل نہ ہوئے تھے۔ مریسیع کے مقام پر آنحضرت نے اچانک دشمن کو جالیا۔ اور تھوڑی سی رد و خورد کے بعد پورے قبیلے کو مال اسباب سمیت گرفتار کر لیا۔ اس مہم سے فارغ ہو کر بھی مریسیع ہی پر لشکر اسلام پڑاؤ ڈالے ہوئے تھا کہ ایک روز حضرت عمرؓ کے ایک ملازم (جَہْجَاہ بن مسعود غفاری) اور قبیلہ خزرج کے ایک حلیف (سِنَان بن دَبر جُہنِی) کے درمیان پانی پر جھگڑا ہو گیا۔ ایک نے انصار کو پکارا۔ دوسرے نے مہاجرین کو آواز دی۔ لوگ دونوں طرف سے جمع ہو گئے اور معاملہ رفع دفع کر دیا گیا۔ لیکن عبداللہ بن اُبی نے جو انصار قبیلہ خزرج سے تعلق رکھتا تھا، بات کا بتنگڑ بنا دیا۔ اس نے انصار کو یہ کہ ہ کہ ہ کر بھڑکانا شروع کیا کہ ’’ یہ مہاجرین ہم پر ٹوٹ پڑے ہیں اور ہمارے حریف بن بیٹھے ہیں۔ ہماری اور ان قریشی کنگلوں کی مثال ایسی ہے کہ کتے کو پال تاکہ تجھی کو بھنبھوڑ کھائے۔ یہ سب کچھ تمہارا اپنا کیا دھرا ہے۔ تم لوگوں نے خود ہی انہیں لا کر اپنے ہاں بسایا ہے اور ان کو اپنے مال و جائداد میں حصہ دار بنایا ہے۔ آج اگر تم ان سے ہاتھ کھینچ لو تو یہ چلتے پھرتے نظر آئیں ‘‘۔ پھر اس نے قسم کھا کر کہا کہ ’’ مدینے واپس پہنچنے کے بعد جو ہم میں سے عزت والا ہے وہ ذلیل لوگوں کو نکال باہر کر دے (سورہ منافقون میں اللہ تعالیٰ نے خود اس کا یہ قول نقل فرمایا ہے ) گا ‘‘۔ اس کی ان باتوں کی اطلاع جب نبی صلی اللہ علیہ و سلم کو پہنچی تو حضرت عمرؓ نے مشورہ دیا کہ اس شخص کو قتل کرا دینا چاہیے۔ مگر حضور نے فرمایا: فکیف یا عمر اذا تحدث الناس ان محمد ایقتل اصحابہ (عمر، دنیا کیا کہے گی کہ محمدؐ خود اپنے ہی ساتھیوں کو قتل کر رہا ہے )۔ پھر آپ نے فوراً ہی اس مقام سے کوچ کر حکم دے دیا اور دوسرے دن دوپہر تک کسی جگہ پڑاؤ نہ کیا ، تاکہ لوگ خوب تھک جائیں اور کسی کو بیٹھ کر چہ میگوئیاں کرنے اور سننے کی مہلت نہ ملے۔ راستے میں سید بن حضَیر نے عرض کیا ’’ یا نبی اللہ، آج آپ نے اپنے معمول کے خلاف نا وقت کوچ کا حکم دے دیا؟‘‘ آپ نے جواب دیا۔ ’’تم نے سنا نہیں کہ تمہارے صاحب نے کیا باتیں کی ہیں ’’۔ انہوں نے پوچھا ’’ کون صاحب؟‘‘ آپ نے فرمایا’’ عبد اللہ بن اُبی ‘‘۔ انہوں نے عرض کیا ’’ یا رسول اللہ ، اس شخص سے رعایت فرمائیے ، آپ جب مدینے تشریف لائے ہیں تو ہم لوگ اس اپنا بادشاہ بنانے کا فیصلہ کر چکے تھے اور اس کے لیے تاج تیار ہو رہا تھا۔ آپ کی آمد سے اس کا بنا بنایا کھیل بگڑ گیا۔ اسی کی جلن وہ نکال رہا ہے ‘‘۔ یہ شوشہ ابھی تازہ ہی تھا کہ اسی سفر میں اس نے ایک اور خطرناک فتنہ اٹھا دیا، اور فتنہ بھی ایسا کہ اگر نبی صلی اللہ علیہ و سلم اور آپ کے جاں نثار صحابہ کمال درجہ ضبط و تحمل اور حکمت و دانائی سے کام نہ لیتے و مدینے کی نو خیز مسلم سوسائٹی میں سخت خانہ جنگی بر پا ہو جاتی۔ یہ حضرت عائشہؓ پر تہمت کا فتنہ تھا۔ اس کا واقعہ خود ان ہی کی زبان سے سنیے جس سے پوری صورت حال سامنے آ جائے گی۔ بیچ بیچ میں جو امور تشریح طلب ہوں گے انہیں ہم دوسری معتبر روایات کی مدد سے قوسین میں بڑھاتے جائیں گے تاکہ جناب صدیقہؓ کے تسلسل بیان میں خلل نہ واقعہ وہ۔ فرماتی ہیں : ’’ رسول اللہ صلی اللہ علیہ و سلم کا قاعدہ تھا کہ جب آپ سفر پر جانے لگتے تو قرعہ ڈال کر فیصلہ فرماتے کہ آپ کی بیویوں میں سے کون آپ کے ساتھ جائے (اس قرعہ اندازی کی نوعیت لاٹری کی سی نہ تھی۔ در اصل تمام بیویوں کے حقوق برابر کے تھے۔ ان میں سے کسی کو کسی پر ترجیح دینے کی کوئی معقول وجہ نہ تھی۔ اب اگر نبی صلی اللہ علیہ و سلم خود کسی کو انتخاب کرتے تو دوسری بیویوں کی دل شکنی ہوتی ، اور ان میں باہم رشک و رقابت پیدا ہونے کے لیے بھی یہ ایک محرک بن جاتا۔ اس لیے آپ قرعہ اندازی سے اس کا فیصلہ فرماتے تھے۔ شریعت میں قرعہ اندازی ایسی ہی سورتوں کے لیے ہے جب کہ چند آدمیوں کا جائز حق بالکل برابر ہو، اور کسی کو کسی پر ترجیح دینے کے لیے کوئی معقول وجہ موجود نہ ہو، مگر حق کسی ایک ہی کو دیا جا سکتا ہو۔ )غزوہ نبی المصطلق کے موقع پر قرعہ میرے نام نکلا اور میں آپ کے ساتھ گئی۔ واپسی پر جب ہم مدینے کے قریب تھے ، ایک منزل پر رات کے وقت رسول اللہ صلی اللہ علیہ و سلم نے پڑاؤ کیا ، اور بھی رات کا کچھ حصہ باقی تھا کہ کوچ کی تیاریاں شروع ہو گئیں۔ میں اٹھ کر رفع حاجت کے لیے گئی، اور جب پلٹنے لگی تو قیام گاہ کے قریب پہنچ کر مجھے محسوس ہوا کہ میرے گلے کا ہار ٹوٹ کر کہیں گر پڑا ہے۔ میں اسے تلاش کرنے میں لگ گئی ، اور اتنے میں قافلہ روانہ ہو گیا۔ قاعدہ یہ تھا کہ میں کوچ کے وقت اپنے ہودے میں بیٹھ جاتی تھی اور چار آدمی اسے اٹھا کر اونٹ پر رکھ دیتے تھے۔ ہم عورتیں اس زمانے میں غذا کی کمی کے سبب سے بہت ہلکی پھلکی تھیں۔ میرا ہودہ اٹھاتے وقت لوگوں کو یہ محسوس ہی نہ ہوا کہ میں اس میں نہیں ہوں۔ وہ بے خبری میں خالی ہودہ اونٹ پر رکھ کر روانہ ہو گئے۔ میں جن ہار لے کر پلٹی تو وہاں کوئی نہ تھا۔ آخر اپنی چادر اوڑھ کر وہیں لیٹ گئی اور دل میں سوچ لیا کہ آگے جا کر جب یہ لوگ مجھ نہ پائیں گے تو خود ہی ڈھونڈتے ہوئے آ جائیں گے۔ اسی حالت میں مجھ کو نیند آ گئی۔ صبح کے وقت سفوان بن معلو سُلَمِی اس جگہ سے گزرے جہاں میں سو رہی تھی اور مجھے دیکھتے ہیں پہچان گئے ، کیونکہ پردے کا حکم آنے سے پہلے وہ مجھے بارہا دیکھ چکے تھے۔ (یہ صاحب بدری صحابیوں میں سے تھے۔ ان کو صبح دیر تک سونے کے عادت [ابوداؤد اور دوسری کتب سنن میں یہ ذکر آتا ہے کہ ان کی بیوی نے نبی صلی اللہ علیہ و سلم سے ان کی شکایت کی تھی کہ یہ کبھی صبح کی نماز وقت پر نہیں پڑھتے۔ انہوں نے عذر پیش کیا کہ یا رسول اللہ یہ میرا خاندانی عیب ہے ، دیر تک سوتے رہنے کی اس کمزوری کو میں کسی طرح دور نہیں کر سکتا۔ اس پر آپ نے فرمایا کہ اچھا جب آنکھ کھلے نماز ادا کر لیا کرو۔ بعض محدثین نے ان کے قافلے سے پیچھے رہ جانے کی وجہ بیان کی ہے۔ مگر بعض دوسرے محدثین اس کی وجہ یہ بیان کرتے ہیں کہ نبی صلی اللہ علیہ و سلم نے ان کو اس خدمت پر مقرر کیا تھا کہ رات کے اندھیرے میں کوچ کرنے کی وجہ سے اگر کسی کی کوئی چیز چھوٹ گئی ہو تو صبح اسے تلاش کر کے لیتے آئیں ] تھی ، اس لیے یہ بھی لشکر گاہ میں کہیں پڑے سوتے رہ گئے تھے اور اب اٹھ کر مدینے جا رہے تھے ) مجھے دیکھ کر انہوں نے اونٹ روک لیا اور بے ساختہ ان کی زبان سے نکلا’’اِنَّا لِلہِ وَاِنَّآ اِلَیْہِ رَاجِعُوْنَ ، رسول اللہ صلی اللہ علیہ و سلم کی بیوی یہیں رہ گئیں ‘‘۔ اس آواز سے میری آنکھ کھل گئی اور میں نے اٹھ کر فوراً اپنے منہ پر چادر ڈال لی۔ انہوں نے مجھ سے کوئی بات نہ کی ، لا کر اپنا اونٹ میرے پاس بٹھا دیا اور الگ ہٹ کر کھڑے ہو گئے۔ میں اونٹ پر سوار ہو گئی اور وہ نکیل پکڑ کر روانہ ہو گئے۔ دوپہر کے قریب ہم نے لشکر کو جالیا جب کہ وہ ابھی ایک جگہ جا کر ٹھیرا ہی تھا اور لشکر والوں کو ابھی یہ پتہ نہ چلا تھا کہ میں پیچھے چھوٹ گئی ہوں۔ اس پر بہتان اٹھانے والوں نے بہتان اٹھا دیے اور ان میں سب سے پیش پیش عبداللہ بن ابی تھا۔ مگر میں اس سے بے خبر تھی کہ مجھ پر کیا باتیں بن رہی ہیں۔ (دوسری روایات میں آیا ہے کہ جس وقت صفوان کے اونٹ پر حضرت عائشہؓ لشکر گاہ میں پہنچیں اور معلوم ہوا کہ آپ اس طرح پیچھے چھوٹ گئی تھیں اسی وقت عبداللہ بن ابی پکارا اٹھا کہ ’’ خدا کی قسم یہ بچ کر نہیں آئی ہے ، لو دیکھو، تمہارے نبی کی بیوی نے رات ایک اور شخص کے ساتھ گزاری اور اب وہ اسے علانیہ لیے چلا آ رہا ہے ‘‘) ’’مدینے پہنچ کر میں بیمار ہو گئی اور ایک مہینے کے قریب پلنگ پر پڑی رہی۔ شہر میں اس بہتان کی خبریں اُڑ رہی تھیں ، رسول اللہ صلی اللہ علیہ و سلم کے کانوں تک بھی بات پہنچ چکی تھی، مگر مجھے کچھ پتہ نہ تھا۔ البتہ جو چیز مجھے کھٹکتی تھی وہ یہ کہ رسول اللہ علیہ و سلم کی وہ توجہ میری طرف نہ تھی جو بیماری کے زمانے میں ہوا کرتی تھی۔ آپ گھر میں آتے تو بس گھر والوں سے یہ پوچھ کر رہ جاتے :کیف تیکم(کیسی ہیں یہ؟)۔ خود مجھ سے کوئی کلام نہ کرتے۔ اس سے مجھے شبہ ہوتا کہ کوئی بات ہے ضرور۔ آخر آپ سے اجازت لے کر میں اپنی ماں کے گھر چلی گئی تاکہ وہ میری تیمار داری اچھی طرح کر سکیں۔ ایک روز رات کے وقت حجت کے لیے میں مدینے کے باہر گئی۔ اس وقت تک ہمارے گھروں میں یہ بیت الخلا نہ تھے اور ہم لوگ جنگل ہی جایا کرتے تھے۔ میرے ساتھ مِسْطَح بن اُثاثہ کی ماں بھی تھیں جو میرے والد کی خالہ زاد بہن تھیں۔ (دوسری روایات سے معلوم ہوتا ہے کہ اس پورے خاندان کی کفالت حضرت ابو بکر صدیقؓ نے اپنے ذمے لے رکھی تھی، مگر اس احسان کے باوجود مسطح بھی ان لوگوں میں شریک ہو گئے تھے جو حضرت عائشہؓ کے خلاف اس بہتان کو پھیلا رہے تھے )۔ راستے میں ان کو ٹھوکر لگی اور بے ساختہ ان کی زبان سے نکلا غارت ہو مسطح۔ میں نے کہ اچھی ماں ہو جو بیٹے کو کوستی ہو، اور بیٹا بھی وہ جس نے جنگ بدر میں حصہ لیا ہے۔ انہوں نے کہا’’ بٹیا ، کیا تجھے اس کی باتوں کی کچھ خبر نہیں ؟ پھر انہوں نے سارا قصہ سنایا کہ افتا پرواز لوگ میرے متعلق کیا باتیں اڑا رہے ہیں۔ (منافقین کے سوا خود مسلمانوں میں سے جو لوگ اس فتنے میں شامل ہو گئے تھے ان میں مسطح، حسَّان بن ثابت مشہور شاعر السلام ، اور حنہہ بنت حجش ، حضرت زینبؓ کی بہن کا حصہ سب سے نمایاں تھا)۔ یہ داستان سن کر میرا خون خشک ہو گیا ، وہ حاجت بھی بھول گئی جس کے لیے آئی تھی، سیدھی گھر گئی اور رات بھر رو رو کر کاٹی‘‘۔ آگے چل کر حضرت عائشہؓ فرماتی ہیں ، ’’میرے پیچھے رسول اللہ لی اللہ علیہ و سلم نے علیؓ اور اسامہؓ بن زید کو بلایا اور ان سے مشورہ طلب کیا۔ اسامہ نے میرے حق میں کلمہ خیر کہا اور عرض کیا ’’ یا رسول اللہ، بھلائی کے سوا آپ کی بیوی میں کوئی چیز ہم نے نہیں پائی۔ یہ سب کچھ کذب اور باطل ہے جو اڑایا جا رہا ہے ‘‘۔ رہے علیؓ تو انہوں نے کہا ’’ یا رسول اللہ عورتوں کی کمی نہیں ہے ، آپ اس کی جگہ دوسری بیوی کر سکتے ہیں ، اور حقیق کرنا چاہیں تو خدمت کار لونڈی کو بلا کر حالات دریافت فرمائیں ‘‘۔ چنانچہ خدمت گار کو بلایا گیا اور پوچھ گچھ کی گئی۔ اس نے کہا’’ اس خدا کی قسم جس نے آپ کو حق کے ستھ بھیجا ہے ، میں نے ان میں کوئی برائی نہیں دیکھی جس پر حرف رکھا جا سکے۔ بس اتنا عیب ہے کہ میں آٹا گوندھ کر کسی کام کو جاتی ہوں اور کہہ جاتی ہوں کہ بیوی ذرا آٹے کا خیال رکھنا ، مگر وہ سو جاتی ہیں اور بکری آکر آٹا کھا جاتی ہے ‘‘۔ اسی روز رسول اللہ صلی اللہ علیہ و سلم نے خطبہ میں فرمایا’’ مسلمانو ! کون ہے جو اس شخص کے حملوں سے میری عزت بچائے جس نے میرے گھر والوں پر الزامات لگا کر مجھے اذیت پہنچانے کی حد کر دی ہے۔ بخدا میں نے نہ تو اپنی بیوی ہی میں کوئی برائی دیکھی ہے ، اور نہ اس شخص میں جس کے متعلق تہمت لگائی جاتی ہے۔ وہ تو کبھی میری غیر موجودگی میں میرے گھر آیا بھی نہیں ‘‘۔ اس پراسید بن حُضیر (بعض روایات میں سعد بن مُعاذ)(غالباً اس اختلاف کی وجہ یہ ہے کہ حضرت عائشہ نے نام لینے کے بجائے سید اوس کے الفاظ استعمال فرمائے ہوں گے کسی راوی نے اس سے مراد حضرت معاذ کو سمجھ لیا، کیونکہ اپنی زندگی میں وہی قبیلہ اوس کے سردار تھے اور تاریخ میں وہی اس حیثیت سے زیادہ مشہور ہیں۔ حالانکہ در اصل اس واقعہ کے وقت ان کے چچا زاد بھائی اسید بن حضیر اوس کے سردار تھے ) نے اٹھ کر کہا ’’یا رسول اللہ ، اگر وہ ہمارے قبیلے کا آدمی ہے تو ہم اس کی گردن مار دیں ، اور اگر ہمارے بھائی خزرجیوں میں سے ہے تو آپ حکم دیں ، ہم تعمیل کے لیے حاضر ہیں ‘‘۔ یہ سنتے ہی سعد بن عبادہ، رئیس خَزْرَج اٹھ کھڑے ہوئے اور کہنے لگے ’’جھوٹ کہتے ہو، تم ہر گز اسے نہیں مار سکتے۔ تم اس کی گردن کانے کا نام صرف اس لیے لے رہے ہو کہ وہ خزرج میں سے ہے۔ اگر وہ تمہارے قبیلے کا آدمی ہوتا تو تم کبھی یہ نہ کہتے کہ ہم اس کی گردن مار دیں (حضرت سعد بن عبادہ اگرچہ نہایت صالح اور مخلص مسلمانوں میں سے تھے ، نبی صلی اللہ علیہ و سلم سے گہری عقیدت و محبت رکھتے تھے ، اور مدینے میں جن لوگوں کے ذریعہ سے اسلام پھیلا تھا ان میں ایک نمایاں شخص وہ بھی تھے ، لیکن ان سب خوبیوں کے باوجود ان کے اندر قومی حمیت [اور عرب میں اس وقت قوم کے معنی قبیلے کے تھے ] بہت زیادہ تھی۔ اسی وجہ سے انہوں نے عبداللہ بن ابی کی پشت پناہی کی، کیونکہ وہ ان کے قبیلے کا آدمی تھا۔ اسی وجہ سے فتح مکہ کے موقع پر ان کی زبان سے یہ فقرہ نکل گیا کہ ’’ الیوم یوم الملحمہ، الیوم تستحل الحرمہ ’’آج کشت و خون کا دن ہے۔ آج یہاں کی حرمت حلال کی جائے گی‘‘ ، اور اس پر عتاب فرما کر حضورؐ نے ان سے لشکر کا جھنڈا واپس لے لیا۔ پھر آخر کار یہی وہ سبب تھا جس کی وجہ سے انہوں نے حضورؐ کی وفات کے بعد سقیفہ بنی ساعدہ میں یہ دعویٰ کیا کہ خلافت انصار کا حق ہے ، اور جب ان کی بات نہ چلی اور انصار و مہاجرین سب نے حضرت ابوبکر کے ہاتھ پر بیعت کر لی تو تنہا وہی ایک تھے جنہوں نے بیعت سے انکار کر دیا اور مرتے دم تک قریشی خلیفہ کی خلافت تسلیم نہ کی [ملاحظہ ہو الاصابہ لابن حجر، اور الاستیعاب لا بن بعدالبر، ذکر سعد بن عبادہ۔ صفحہ10۔ 11]) گے ‘‘۔ اسید بن حضیر نے جواب میں کہا ’’ تم منافق ہو اسی لیے منافقوں کی حمایت کرتے ہو‘‘۔ اس پر مسجد نبوی میں ایک ہنگامہ برپا ہو گیا، حالانکہ رسول اللہ صلی اللہ علیہ و سلم منبر پر تشریف رکھتے تھے۔ قریب تھا کہ اَوس اور خزرج مسجد ہی میں لڑ پڑتے ، مگر رسول اللہ صلی اللہ علیہ و سلم نے ان کو ٹھنڈا کیا اور پھر منبر سے اتر آئے ‘‘۔ حضرت عائشہؓ کے قصے کی باقی تفصیلات ہم اثنائے تفسیر میں اس جگہ نقل کریں گے جہاں اللہ تعالیٰ کی طرف سے ان کی برأت نازل ہوئی ہے۔ یہاں جو کچھ بتانا چاہتے ہیں وہ یہ ہے کہ عبداللہ بن ابی نے یہ شوشہ چھوڑ کر بیک وقت کئی شکار کرنے کی کوشش کی۔ ایک طرف اس نے رسول اللہ صلی اللہ علیہ و سلم اور حضرت ابو بکر صدیق کی عزت پر حملہ کیا۔ دوسری طرف اس نے اسلامی تحریک کے بلند ترین اخلاقی وقار کو گرانے کی کو شش کی۔ تیسری طرف اس نے یہ ایک ایسی چنگاری پھینکی تھی کہ اگر اسلام اپنے پیروؤں کی کایا نہ پلٹ چکا ہوتا تو مہاجرین اور انصار، اور خود انصار کے بھی دونوں قبیلے آپس میں لڑ مرتے۔ موضوع اور مباحث: یہ تھے وہ حالات جن میں پہلے حملے کے موقع پر سورہ احزاب کے آخری 6 رکوع نازل ہوئے اور دوسرے حملے کے موقع پر یہ سورہ نور اتری۔ اس پس منظر کو نگاہ میں رکھ کر ان دونوں سورتوں کا ترتیب وار مطالعہ کیا جائے تو وہ حکمت اچھی طرح سمجھ میں آ جاتی ہے جو ان کے احکام میں مضمر ہے۔منافقین مسلمانوں کو اس میدان میں شکست دینا چاہتے تھے جو ان کے تفوق کا اصل میدان تھا۔ اللہ تعالیٰ نے ، بجائے اس کے کہ وہاں کے اخلاقی حملوں پر ایک غضبناک تقریر فرماتا، یا مسلمانوں کو جوابی حملے کرنے پر اکساتا، تمام تر توجہ مسلمانوں کو یہ تعلیم دیتے پر صرف فرمائی کہ تمہارے اخلاقی محاذ میں جہاں جہاں رخنے موجود ہیں ان کو بھرو اور اس محاذ کو اور زیادہ مضبوط کر لو۔ ابھی آپ دیکھ چکے ہیں کہ نکاح زینبؓ کے موقع پر منافقین اور کفار نے کیا طوفان اٹھایا تھا۔ اب ذرا سورہ احزاب نکال کر پڑھیے ، وہاں آپ دیکھیں گے کہ ٹھیک اسی طوفان کا زمانہ تھا جب کہ معاشرتی اصلاح کے متعلق حسب ذیل ہدایات دی گئیں : 1 ۔ ازواج مطہرات کو حکم دیا گیا کہ اپنے گھروں میں وقار کے ساتھ بیٹھو، بناؤ سنگھار کر کے باہر نہ نکلو، اور غیر مردوں سے گفتگو کرنے کا اتفاق ہو تو دبی زبان سے بات نہ کرو کہ کوئی شخص بے جا توقعات قائم کرے (آیات 32۔ 33 ) 2 ۔ حضور کے گھروں میں غیر مردوں کے بلا اجازت داخل ہو جانے کو روک دیا گیا، اور ہدایت کی گئی کہ ازواج مطہرات سے کوئی چیز مانگنی ہو تو پردے کے پیچھے سے مانگو۔ (آیت 53 )۔ 3 ۔ غیر محرم مردوں اور محرم رشتہ داروں کے درمیان فرق قائم کیا گیا اور حکم دیا گیا کہ ازواج مہرات کے صرف محرم رشتہ دار ہی آزادی کے ساتھ آپ کے گھروں میں آ جا سکتے ہیں۔ (آیت 55)۔ 4 ۔ مسلمانوں کو بتایاگیاکہ نبی کی بیویاں تمہاری مائیں ہیں اور ٹھیک اسی طرح ایک مسلمان کے لیے ابداً حرام ہیں جس طرح اس کی حقیقی ماں ہوتی ہے۔ اس لیے ان کے بارے میں ہر مسلمان اپنی نیت کو بالکل پاک رکھے۔ (آیت 53۔ 54 )۔ 5 ۔ مسلمانوں کو متنبہ کر دیا گیا کہ نبیؐ کو اذیت دینا دنیا اور آخرت میں خدا کی لعنت اور رسوا کن عذاب کا موجب ہے ، اور اسی طرح کسی مسلمان کی عزت پر حملہ کرنا اور اس پر ناحق الزام لگانا بھی سخت گناہ ہے (آیت 57۔ 58 ) 6 ۔ تمام مسلمان عورتوں کو حکم دے دیا گیا کہ جب باہر نکلنے کی ضرورت پیش آئے تو چادروں سے اپنے آپ کو اچھی طرح ڈھانک کر اور گھونگھٹ ڈال کر نکلا کریں (آیت 59)۔پھر جب واقعہ اِفک سے مدینے کے معاشرے میں ایک ہلچل برپا ہوئی تو یہ سورہ نور اخلاق، معاشرت اور قانون کے ایسے احکام و ہدایات کے ساتھ نازل فرمائی گئی جن کا مقصد یہ تھ کہ اول تو مسلم معاشرے کو برائیوں کی پیدا وار اور ان کے پھیلاؤ سے محفوظ رکھا جائے ، اور اگر وہ پیدا ہو ہی جائیں تو پھر ان کا پورا پورا تدارک کیا جائے۔ ان احکام و ہدایات کو ہم اسی ترتیب کے ساتھ یہاں خلاصۃً درج کرتے ہیں جس کے ساتھ وہ اس سورے میں نازل ہوئے ہیں۔ تاکہ پڑھنے والے اندازہ کر سکیں کہ قرآن ٹھیک نفسیاتی موقع پر انسانی زندگی کی اصلاح و تعمیر کے لیے کس طرح قانونی، اخلاقی، اور معاشرتی تدبیر بیک وقت تجویز کرتا ہے : 1) ۔ زنا، جسے معاشرتی جرم پہلے ہی قرار دیا جا چکا تھا (سورہ نساء، آیات 15۔ 16 )، اب اس کو فوجداری جرم قرار دے کر اس کی سزا سو 100 کوڑے مقرر کر دی گئی۔ 2 ) ۔ بد کار مردوں اور عورتوں سے معاشرتی مقاطعے کا حکم دیا گیا اور ان کے ساتھ رشتہ مناکحت جوڑنے سے اہل ایمان کو منع کر دیا گیا۔ 3 ) ۔ جو شخص دوسرے پر زنا کا الزام لگائے اور پھر ثبوت میں چار گواہ نہ پیش کر سکے ،اس کے لیے 80 کوڑوں کی سزا مقرر کی گئی۔ 4) ۔ شوہر اگر بیوی پر تہمت لگائے تو اس کے لیے لِعان کا قاعدہ مقرر کیا گیا۔ 5) ۔ حضرت عائشہ پر منافقین کے جھوٹے الزام کی تردید کرتے ہوئے یہ ہدایت کی گئی کہ آنکھیں بند کر کے ہر شریف آدمی کے خلاف ہر قسم کی تہمتیں قبول نہ کر لیا کرو، اور نہ ان کو پھیلاتے پھرو۔ اس طرح کی افواہیں ا گر اڑ رہی ہوں تو انہیں دبانا اور ان کا سد باب کرنا چاہیے ، نہ یہ ایک منہ سے لے کر دوسرا منہ اسے آگے پھونکنا شروع کر دے۔ اسی سلسلے میں یہ بات ایک اصولی حقیقت کے طور پر سمجھائی گئی کہ طیب آدمی کا جوڑ طیب عورت سے ہی لگ سکتا ہے ، خبیث عورت کے اطوار سے اس کا مزاج چند روز بھی موافقت نہیں کر سکتا۔ اور ایسا ہی حال طیب عورت کا بھی ہوتا ہے کہ اس کی روح طیب مرد ہی سے موافقت کر سکتی ہے نہ کہ خبیث سے۔ اب اگر رسولؐ کو تم جانتے ہو کہ وہ ایک طیب ، بلکہ اطیب انسان ہیں تو کس طرح یہ بات تمہاری عقل میں سما گئی کہ ایک خبیث عورت ان کی محبوب ترین رفیقہ حیات بن سکتی تھی۔ جو عورت عملاً زنا تک کر گزرے اس کے عام اطوار کب ایسے ہو سکتے ہیں کہ رسولؐ جیسا پاکیزہ انسان اس کے ساتھ یوں نباہ کرے۔ پس صرف یہ بات کہ ایک کمینہ آدمی نے ایک بیہودہ الزام کسی پر لگا دیا ہے ، اسے قابل قبول کیا معنی قابل توجہ اور ممکن الوقوع سمجھ لینے کے لیے بھی کافی نہیں ہے۔ آنکھیں کھول کر دیکھو کہ الزام لگانے والا ہے کون اور الزام لگا کس پر رہا ہے۔ 6) ۔ جو لوگ بے ہودہ خبریں اور بری افواہیں پھیلائیں اور مسلم معاشرے میں فحش اور فواحش کو رواج دینے کی کوشش کریں ، ان کے متعلق بتایا گیا کہ وہ ہمت افزائی کے نہیں بلکہ سزا کے مستحق ہیں۔ 7) ۔ یہ قاعدہ کلیہ مقرر کیا گیا کہ مسلم معاشرے میں اجتماعی تعلقات کی بنیاد باہمی حسن ظن پر ہونی چاہیے۔ ہر شخص بے گناہ سمجھا جائے جب تک کہ اس کے گنہگار ہونے کا ثبوت نہ ملے۔ نہ یہکہ ہر شخص گناہ گار سمجھا جائے جب تک کہ اس کا بے گناہ ہونا ثابت نہ ہو جائے۔ 8) ۔ لوگوں کو عام ہدایت کی گئی کہ ایک دوسرے کے گھروں میں بے تکلف نہ گھس جایا کریں بلکہ اجازت لے کر جائیں۔ 9) عورتوں اور مردوں کو غضِّ بصر کا حکم دیا گیا اور ایک دوسرے کو گھورنے یا جھانک تاک کرنے سے منع کر دیا گیا۔ 10) ۔ عورتوں کو حکم دیا گیا کہ اپنے گھروں میں سر اور سینہ ڈھانک کر رکھیں۔ 11 ) عورتوں کو یہ بھی حکم دیا گیا کہ اپنے محرم رشتہ داروں اور گھر کے خادموں کے سوا کسی کے سامنے بن سنور کر نہ آئیں۔ 12) ۔ ان کو یہ بھی حکم دیا گیا کہ باہر نکلیں تو نہ صرف یہ کہ اپنے بناؤ سنگھار کو چھپا کر نکلیں ، بلکہ بجنے والے زیور بھی پہن کر نہ نکلیں۔ 13 ) معاشرے میں عورتوں اور مردوں کے بن بیاہے بیٹھے رہنے کا طریقہ ناپسندیدہ قرار دیا گیا اور حکم دیا گیا کہ غیر شادی شدہ لوگوں کے نکاح کیے جائیں ، حتّیٰٰ کہ لونڈیوں ور غلاموں کو بھی بن بیاہا نہ رہنے دیا جائے۔ اس لیے کہ تجرد فحش آفریں بھی ہوتا ہے اور فحش پذیر بھی۔ مجرد لوگ اور کچھ نہیں تو بری خبریں سننے اور پھیلانے ہی میں دلچسپی لینے لگتے ہیں۔ 14 ) ۔ لونڈیوں ور غلاموں کی آزادی کے لیے مکاتَبت کی راہ نکال دی گئی اور مالکوں کے علاوہ دوسروں کو بھی حکم دیا گیا کہ مُکاتب غلاموں اور لونڈیوں کی مالی مدد کریں۔ 15) ۔ لونڈیوں سے کسب کرانا ممنوع قرار دیا گیا۔ عرب میں یہ پیشہ لونڈیوں ہی سے کرانے کا رواج تھا،اس لیے اس کی ممانعت دراصل قحبہ گری کی قانونی بندش تھی۔ 16) گھریلو معاشرت میں خانگی ملازموں اور نابالغ بچوں کے لیے یہ قاعدہ مقرر کیا گیا کہ وہ خلوت کے اوقات میں (یعنی صبح ، دوپہر اور رات کے وقت ) گھر کے کسی مرد یا عورت کے کمرے میں اچانک نہ گھس جایا کریں۔ اولاد تک کو اجازت لے کر آنے کی عادت ڈالی جائے۔ 17) ۔ بوڑھی عورتوں کو یہ رعایت دی گئی کہ اگر وہ اپنے گھر میں سر سے اوڑھیر اتار کر رکھ دیں تو مضائقہ نہیں ، مگر حکم دیا گیا کہ تَبَرُّج (جن ٹھن کر اپنے آپ کو دکھانے ) سے بچیں۔ نیز انہیں نصیحت کی گئی ہ بڑھاپے میں بھی اگر وہ اوڑھنیاں اپنے اوپر ڈالے ہی رہیں تو بہتر ہے۔ 18) اندھے ، لنگڑے ، لولے ، اور بیمار کو یہ رعایت دی گئی کہ وہ کھانے کی کوئی چیز کسی کے ہاں سے بلا اجازت کھا لے تو اس کا شمار چوری اور خیانت میں نہ ہو گا۔ اس پر کوئی گرفت نہ کی جائے۔ 19) ۔ قریبی عزیزوں اور بے تکلف دوستوں کو یہ حق دیا گیا کہ وہ ایک دوسرے کے ہاں بلا اجازت بھی کھا سکتے ہیں ، اور یہ ایسا ہی ہے جیسے وہ اپنے گھر میں کھا سکتے ہیں۔ اس طرح معاشرے کے افراد کو ایک دوسرے سے قریب تر کر دیا گیا ور ان کے درمیان سے بیگانگی کے پردے ہٹا دیے گئے تاکہ آپس کی محبت بڑھے اور باہمی اخلاص کے رابطے ان رخنوں کو بند کر دیں جن سے کوئی فتنہ پرواز پھوٹ ڈال سکتا ہوَان ہدایات کے ساتھ ساتھ منافقین اور مومنین اور مومنین کی وہ کھُلی کھُلی علامتیں بیان کر دی گئیں جن سے ہر مسلمان یہ جان سکے کہ معاشرے میں مخلص اہل ایمان کون لوگ ہیں اور منافق کون۔ دوسری طرف مسلمانوں کے جماعتی نظم و ضبط کو اور کس دیا گیا اور اس کے لیے چند مزید ضابطے بنا دیے گئے تاکہ وہ طاقت اور زیادہ مضبوط ہو جائے جس سے غیظ کاا کر کفار و منافقین فساد انگیزیاں کر رہے تھے۔اس تمام بحث میں نمایاں چیز دیکھنے کی یہ ہے کہ پوری سورہ نور اس تلخی سے خالی ہے جو شرمناک اور بیہودہ حملوں کے جواب میں پیدا ہوا کرتی ہے۔ ایک طرف ان حالات کو دیکھیے جن میں یہ سورت نازل ہوئی ہے۔ اور دوسری طرف سورت کے مضامین اور انداز کلام کو دیکھیے۔ اس قدر اشتعال انگیز صورت حال میں کیسے ٹھنڈے طریقے سے قانون سازی کی جا رہی ہے ، مصلحانہ احکام دیے جا رہے ہیں ، حکیمانہ ہدایات دی جا رہی ہیں ، اور تعلیم و نصیحت کا حق ادا کیا جا رہا ہے۔ اس سے صرف یہی سبق نہیں ملتا کہ ہم کو فتنوں کے مقابلے میں سخت سے سخت اشتعال کے مواقع پربھی کس طرح ٹنڈدے تدبّر اور عالی ظرفی اور حکمت سے کام لینا چاہیے ، بلکہ اس سے اس امر کا ثبوت بھی ملتا ہے کہ یہ کلام محمد صلی اللہ علیہ و سلم کا اپنا تصنیف کردہ نہیں ہے ، کسی ایسی ہستی کا نازل کیا ہوا ہے جو بہت بلند مقام سے انسانی حالات اور معاملات کا مشاہدہ کر رہی ہے اور اپنی ذات میں ان حالات و معاملات سے غیر متاثر رہ کر خالص ہدایت و رہنمائی کا منصب اد کر رہی ہے۔ اگر یہ آنحضرتؐ کا اپنا کلام ہوتا تو آپ کی انتہائی بلند نظری کے باوجود اس میں اس فطری تلخی کا کچھ نہ کچھ اثر تو ضرور پایا جاتا جو خود اپنی عزت و ناموس پر کمینہ حملوں کو سن کر ایک شریف آدمی کے جذبات میں لازماً پیدا ہو جایا کرتی ہے۔','سید ابو اعلیٰ مودودیؒ -  تفہیم القرآن');
INSERT INTO "chapters_ur" VALUES(25,'صحیح اور غلط میں فرق کرنے والی کتاب',NULL,' نام : پہلی ہی آیت تَبَاَرَکَ الَّذِیْ نَزَّ لَ الْفُرْقَانَ سے ماخوذ ہے ۔ یہ بھی قرآن کی اکثر سورتوں کے ناموں کی طرح علامت کے طور پر ہے نہ کہ عنوان مضمون کے طور پر ۔ تاہم مضمون سورہ کے ساتھ یہ نام ایک قریبی مناسبت رکھتا ہے جیسا کہ آگے چل کر معلوم ہو گا۔ زمانۂ نزول : انداز بیان اور مضامین پر غور کرنے سے صاف محسوس ہوتا ہے کہ اس زمانہ نزول بھی وہی ہے جو سورہ مومنون وغیرہ کا ہے ، یعنی زمانہ قیام مکہ کا دور متوسط۔ ابن جریر اور امام رازی نے ضحاک بن مزاجِ اور مقاتل بن سلیمان کی یہ روایت نقل کی ہے کہ یہ سورت سورہ نساء سے 8 سال پہلے اتری تھی۔ اس حساب سے بھی اس کا زمانہ نزول وہی دور متوسط قرار پاتا ہے ۔ (ابن جریر، جلد 19، صفحہ 28 ۔30 ۔ تفسیر کبیر ، جلد 6 ، صفحہ 358 ) ۔ موضوع و مباحث : اس میں ان شبہات و اعتراضات پر کلام کیا گیا ہے جو قرآن ، اور محمد صلی اللہ علیہ و سلم کی نبوت ، اور آپ کی پیش کر دہ تعلیم پر کفار مکہ کی طرف سے پیش کیے جاتے تھے ۔ ان میں سے ایک ایک کا جچا تلا جواب دیا گیا ہے اور ساتھ ساتھ دعوت حق سے منہ موڑنے کے برے نتائج بھی صاف صاف بتائے گئے ہیں ۔ آخر میں سورہ مومنون کی طرح اہل ایمان کی اخلاقی خوبیوں کا ایک نقشہ کھینچ کر عوام الناس کے سامنے رکھ دیا گیا ہے کہ اس کسوٹی پر کس کر دیکھ لو ، کون کھوٹا ہے اور کون کھرا۔ ایک طرف اس سیرت و کردار کے لوگ ہیں جو محمد صلی اللہ علیہ و سلم کی تعلیم سے اب تک تیار ہوئے ہیں اور آئندہ تیار کرنے کی کوشش ہو رہی ہے ۔ دوسری طرف وہ نمونہ اخلاق ہے جو عم اہل عرب میں پایا جاتا ہے اور جسے بر قرار رکھنے کے لیے جاہلیت کے علمبردار ایڑی چوٹی کا زور لگا رہے ہیں ۔ اب خود فیصلہ کرو کہ ان دونوں نمونوں میں سے کے پسند کرتے ہو؟ یہ ایک غیر مفوکظ سوال تھا جو عرب کے ہر باشندے کے سامنے رکھ دیا گیا، اور چند سال کے اندر ایک چھوٹی سی اقلیت کو چھوڑ کر ساری قوم نے اس کا جو جواب دیا وہ جریدہ روز گار پر ثبت ہو چکا ہے ۔ ','سید ابو اعلیٰ مودودیؒ -  تفہیم القرآن');
INSERT INTO "chapters_ur" VALUES(26,'شعراٗ',NULL,' نام: آیت 224 وَالشُّعَرَ آءُیَتَّبِعُھُمُ الْغَا وٗ نَ O سے ماخوذ ہے۔ زمانۂ نزول: مضمون ور انداز بیان سے محسوس ہوتا ہے اور روایات اس کی تائید کرتی ہیں کہ اس سورے کا زمانہ نزول مکہ کا دور متوسط ہے۔ ابن عباسؓ کا بیان ہے کہ پہلے سورہ طٰہٰ نازل ہوئی پھر واقعہ اور اس کے بعد الشعراء (روح المعانی جلد 19 صفحہ 64)۔ اور سورہ طٰہٰ کے متعلق یہ معلوم ہے کہ وہ حضرت عمر رضی اللہ عنہ کے قبول اسلام سے پہلے نازل ہو چکی تھی۔ موضوع اور مباحث: تقریر کا پس منظر یہ ہے کہ کفار مکہ نبی صلی اللہ علیہ و سلم کی تبلیغ و تذکیر کا مقابلہ پیہم جحود و انکار سے کر رہے تھے اور اس کے لیے طرح طرح کے بہا نے تراشے چلے جاتے تھے۔ کبھی کہتے کہ تم نے ہمیں کوئی نشانی تو دکھائی ہی نہیں ، پھر ہمیں کیسے یقین آئے کہ تم نبی ہو۔ کبھی آپ کو شاعر اور کاہن قرار دے کر آپ کی تعلیم و تلقین کو باتوں میں اڑا دینے کی کوشش کرتے۔ اور کبھی آپ کو شاعر اور کاہن قرار دے کر آپ کی تعلیم و تلقین کو باتوں میں اڑا دینے کی کوشش کرتے۔ اور کبھی یہ کہہ کر آپ کے مشن کا استخفاف کرتے کہ ان کے پیرو یا تو چند نادان نوجوان ہیں ، یا پھر ہمارے معاشرے کے ادنی طبقات کے لوگ، حالانکہ اگر اس تعلیم میں کوئی جان ہوتی تو اشراف قوم اور شیوخ اس کو قبول کرتے۔ نبی صلی اللہ علیہ و سلم ان لوگوں کو معقول دلائل کے ساتھ ان کے عقائد کی غلطی اور توحید و معاد کی صداقت سمجھانے کی کوشش کرتے کرتے تھکے جاتے تھے ، مگر وہ ہٹ دھرمی کی نت نئی صورتیں اختیار کرتے نہ تھکتے تھے۔ یہی چیز آنحضورؐ کے لیے سوہان روح بنی ہوئی تھی اور اس غم میں آپ کی جان گھلی جاتی تھی۔ان حالات میں یہ سورت نازل ہوئی۔ کلام کا آغاز اس طرح ہوتا ہے کہ تم ان کے پےچھ ہی جان کیوں گھلاتے ہو؟ ان کے ایمان نہ لانے کی وجہ یہ نہیں ہے کہ انہوں نے کوئی نشانی نہیں دیکھی ہے ، بلکہ اس کی وجہ پہ ہے کہ یہ ہٹ دھرم ہیں ، سمجھانے سے نہیں ماننا چاہتے ، کسی ایسی نشانی کے طالب میں جو زبر دستی ان کی گردنیں جھکا دے ، اور وہ نشانی اپنے وقت پر جب آ جائے گی تو انہیں خود معلوم ہو جائے گا کہ جو بات انہیں سمجھائی جا رہی تھی وہ کیسی برحق تھی۔ اس تمہید کے بعد دسویں رکوع تک جو مضمون مسلسل بیان ہوا ہے وہ یہ ہے کہ طالب حق لوگوں کے لیے تو خدا کی زمین پر ہر طرف نشانیاں ہی نشانیاں پھیلی ہوئی ہیں جنہیں دیکھ کر وہ حقیقت کو پہچان سکتے ہیں۔ لیکن ہٹ دھرم لوگ کبھی کسی چیز و دیکھ کر بھی ایمان نہیں لائے ہیں ، نہ آفاق کی نشانیاں دیکھ کر اور نہ انبیاء کے معجزات دیکھ کر یہ وہ تو ہمیشہ اس وقت تک اپنی ضلالت پر جمے رہے ہیں جب تک خدا کے عذاب نے آ کر ان کو گرفت میں نہیں لے لیا ہے۔ اسی مناسبت سے تاریخ کی سات قوموں کے حالات پیش کیے گئے ہیں جنہوں نے اسی ہٹ دھرمی سے کام لیا تھا جس سے کفار مکہ کام لے رہے تھے۔ اور اس تاریخی بیان کے ضمن میں چند باتیں ذہن نشین کرائی گئی ہیں :۔اول یہ کہ نشانیاں دو طرح کی ہیں۔ ایک قسم کی نشانیاں وہ ہیں جو خدا کی زمین پر ہر طرف پھیلی ہوئی ہیں ، جنہیں دیکھ کر ہر صاحب عقل آدمی تحقیق کر سکتا ہے کہ نبی جس چیز کی طرف بلا رہا ہے وہ حق ہے یا نہیں۔ دوسری قسم کی نشانیاں وہ ہیں جو فرعون اور اسکی قوم نے دیکھیں ، قوم نوح نے دیکھیں ، عاد اور ثمود نے دیکھیں ، قوم لوط اور اصحاب الاَیکہ نے دیکھیں۔ اب یہ فیصلہ کرنا خود کفار کا اپنا کام ہے کہ وہ کس قسم کی نشانی دیکھا چاہتے ہیں۔دوم یہ کہ ہر زمانے میں کفار کی ذہنیت ایک سی رہی ہے۔ ان کی حجتیں ایک ہی طرح کی تھیں۔ ان کے اعتراضات یکساں تھے۔ ایمان نہ لانے کے لیے ان کے حیلے اور بہانے یکساں تھے۔ اور آخر کار ان کا انجام بھی یکساں ہی رہا۔ اس کے برعکس ہر زمانے میں انبیاء کی تعلیم ایک تھی۔ ان کی سیرت و اخلاق کا رنگ ایک تھا۔ اپنے مخالفوں کے مقابلے میں ان کی دلیل و حجت کا انداز ایک تھا۔ اور ان سب کے ساتھ اللہ کی رحمت کا معاملہ بھی ایک تھا۔ یہ دونوں نمونے تاریخ میں موجود ہیں۔ کفار خود دیکھ سکتے ہیں کہ ان کی اپنی تصویر کس نمونے سے ملتی ہے اور محمد صلی اللہ علیہ و سلم کی ذات میں کس نمونے کی علامات پائی جاتی ہیں۔تیسری بات جو بار بار دہرائی گئی ہے وہ یہ ہے کہ خدا زبردست، قادر و توانا بھی ہے اور رحیم بھی۔ تاریخ میں اس کے قہر کی مثالیں بھی موجود ہیں اور رحمت کی بھی۔ اب یہ بات لوگوں کو خود ہی طے کرنی چاہیے کہ وہ اپنے آپ کو اس کے رحم کا مستحق بناتے ہیں یا قہر کا۔آخری رکوع میں اس بحث کو سمیٹتے ہوئے کہا گیا ہے کہ تم لوگ اگر نشانیاں ہی دیکھنا چاہتے ہو، تو آخر وہ خوفناک نشانیاں دیکھنے پر کیوں اصرار کرتے ہو جو تباہ شدہ قوموں نے دیکھی ہیں۔ اس قرآن کو دیکھو جو تمہاری اپنی زبان میں ہے۔ محمد صلی اللہ علیہ و سلم کو دیکھو۔ ان کے ساتھیوں کو دیکھو۔ کیا یہ کلام کسی شیطان یا جن کا کلام ہو سکتا ہے ؟ کیا اس کلام کا پیش کرنے والا تمہیں کاہن نظر آتا ہے ؟ کیا محمدؐ اور ان کے اصحاب تمہیں ویسے ہی نظر آتے ہیں جیسے شاعر اور ان کے ہم مشرب ہوا کرتے ہیں ؟ ضدّم ضدّا کی بات دوسری ہے ، مگر اپنے دلوں کو ٹٹول کر دیکھو کہ وہ کیا شہادت دیتے ہیں۔ اگر دلوں میں تم خود جانتے ہو کہ کہانت اور شاعری سے اس کا کوئی دور کا واسطہ بھی نہیں ہے تو پھر یہ بھی جان لو کہ تم ظلم کر رہے ہو اور ظالموں کا سا انجام دیکھ کر رہو گے۔','سید ابو اعلیٰ مودودیؒ -  تفہیم القرآن');
INSERT INTO "chapters_ur" VALUES(27,'چیونٹی',NULL,' نام : دوسرے رکوع کی جوتھی آیت میں وَادِالنَّملِ کا ذکر آیاہے۔سورةکا نام اسی سےماخوذ ہے۔ یعنی وہ سورة جس میں النمل کا ذکر آیا ہے۔ یا جس میں النمل کا لفظ وارد ہوا ہے۔ زمانہٴ نزول : مضمون اور انداز بیان مکہ کے دور متوسط کی سورتوں سے پوری مشابہت رکھتا ہے۔ اور اس کی تائید روایات سے بھی ہوتی ہے۔ ابن عباس رضی اللہ تعالیٰ عنہ اور جابر بن زید کا بیان ہے کہ ” پہلے سورہٴ شعراء نازل ہوئی، پھر النمل ، پھر القصص۔ “ موضوع اور مباحث : یہ سورة دو خطبوں پر مشتمل ہے۔ پہلا خطبہ آغاز سورة سے چوتھے رکوع کے خاتمے تک ہے۔ اور دوسرا خطبہ پانچوں رکوع کی ابتدا سے سورة کے اختتام تک۔ پہلے خطبے میں بتایا گیا ہے کہ قرآن کے رہنمائی سے صرف وہی لوگ فائدہ اٹھا سکتے ہیں ، اور اس کی بشارتوں کے مستحق بھی وہی صرف وہی لوگ ہیں جو ان حقیقتوں کو تسلیم کریں جنہیں یہ کتاب اس کائنات کی بنیادی حقیقتوں کی حیثیت سے پیش کرتی ہے۔ اور پھر مان لینے کے بعد اپنی عملی زندگی میں بھی اطاعت و اتباع کا رویہ اختیار کریں۔ لیکن اس راہ پر آنے اور چلنے میں جو چیز سب سے بڑھ کر مانع ہوتی ہے ہو انکار آخرت ہے۔کیونکہ یہ آدمی کو غیر ذمہ دار بندہ نفس اور فریفتہ حیات دنیا بنادیتا ہے۔ جس کے بعد آدمی کا خدا کے آگے جھکنا اور اپنے نفس کے خواہشات پر اخلاقی پابندیاں برداشت کرنا ممکن نہیں رہتا۔ اس تمہید کے بعد تین قسم کی سیرتوں کے نمونے پیش کیے گئے ہیں۔ ایک نمونہ فرعون اور سرداران قوم ثمود اور سرکشان قوم لوط کا ہے جن کی سیرت فکر آخرت سے بے نیازی اور نتیجتًہ نفس کی بندگی سے تعمیر ہوئی تھی ۔ یہ لوگ کسی نشانی کو دیکھ کر بھی ایمان لانے کو تیار نہ ہوئے ۔ یہ الٹے ان لوگوں کے دشمن ہو گئے جنہوں نے ان کو خیر و صلاح کی طرف بلایا۔ انہوں نے اپنے ان بدکاریوں پربھی پورا اصرار کیا جن کا گھناونا پن کسی صاحب عقل سے بھی پوشیدہ نہیں ہے۔ انہیں عذاب الٰہی میں گرفتار ہونے سے ایک لمحہ پہلے تک بھی ہوش نہ آیا۔ دوسرا نمونہ حضرت سلیمان علیہ السلام کا ہے جن کو خدانے دولت، حکومت، اور شوکت و حشمت سے اس پیمانے پر نوازا تھا کہ کفار مکہ کے سردار اس کا خواب بھی نہ دیکھ سکتے تھے۔لیکن اس کے باوجود چونکہ وہ اپنے آپ کو خدا کے آگے جوابدہ سمجھتے تھے، اور انہیں احساس تھا کہ انہیں جو کچھ بھی حاصل ہے خدا کی عطا سے حاصل ہے ، اس لئے ان کا سر ہر وقت منعم حقیقی کے آگے جھکا رہتا تھا اور کبر نفس کا کوئی شائبہ تک ان کی سیرت و کردار میں نہ پایا جاتا تھا۔ تیسرا نمونہ ملکہ سبا کا ہے جو تاریخ عرب کی نہایت مشہور دولت مند قوم پر حکمران تھی۔ اس کے پاس وہ تمام اسباب جمع تھے جو کسی انسان کو غرورنفس میں مبتلا کر سکتے ہیں جن چیزوں کے بل پر کوئی انسان گھمنڈ کر سکتا ہے وہ سرداران قریش کی بہ نسبت لاکھوں درجہ زیادہ اسے حاصل تھیں۔ پھر وہ ایک مشرک قوم سے تعلق رکھتی تھی۔ تقلید آبائی کی بنا پر بھی، اور اپنی قوم میں اپنی سرداری برقرار کھنے کی خاطر بھی، اس کے لئے دین شرک کو چھوڑ کر دین توحید اختیار کرنا اس سے بہت زیادہ مشکل تھا جتنا کسی عام مشرک کے لئے ہو سکتا ہے لیکن جب اس پر حق واضح ہوگیا تو کوئی چیز اسے قبول حق سے نہ روک سکی ، کیونکہ اس کے گمراہی محض ایک مشرک ماحول میں آنکھ کھولنے کی وجہ سے تھی۔ نفس کی بندگی اور خواہشات کی غلامی کا مرض اس پر مسلط نہ تھا۔ خدا کے حضور جواب دہی کا احساس سے اس کا ضمیر فارغ نہیں تھا۔ دوسرے خطبے میں سب سے پہلے کائنات کے چند نمایاں ترین مشہود حقائق کی طرف اشارہ کر کے کفار مکہ سے پے در پے سوال کیا گیا ہے کہ بتاوٴ ، یہ حقائق اس شرک کی شہادت دے رہے ہیں جس میں تم مبتلا ہو، یا اس توحید پر گواہ ہیں جس کی دعوت اس قرآن میں تمہیں دی جارہی ہے ؟ اس کے بعد کفار کے اصل مرض پر انگلی رکھ دی گئی ہے کہ جس چیز نے ان کو اندھا بنا رکھا ہے، جس کی وجہ سے وہ سب کچھ دیکھ کر بھی کچھ نہیں دیکھتے اور سب کچھ سن کر بھی کچھ نہیں سنتے وہ در اصل آخرت کا انکار ہے ۔ اسی چیز نے ان کے لئے زندگی کے کسی مسئلے میں بھی کوئی سنجیدگی باقی نہیں چھوڑی ہے ۔ کیونکہ جب ان کے نزدیک آخر کار سب کچھ مٹی ہوجاناہے،اور حیات دنیا کی اس ساری تگ و دو کا حاصل کچھ بھی نہیں ہے، تو آدمی کے لئے پھر حق اور باطل سب یکساں ہیں۔اس کے لئے اس سوال میں سرے سے کوئی اہمیت ہی نہیں رہتی کہ اس کا نظام حیات راستی پر قائم ہے یا ناراستی پر۔ لیکن اس بحث سے مقصود یاس نہیں ہے کہ جب یہ لوگ غفلت میں مگن ہیں تو انہیں دعوت دینا بے کار ہے ۔ بلکہ دراصل اس سے مقصود سونے والوں کو جھنجوڑ کر جگانا ہے۔اس لئے چھٹے اور ساتویں رکوع میں پے در پے وہ باتیں ارشاد فرمائی گئی ہیں جو لوگوں میں آخرت کا احساس بیدار کریں اس سے غفلت برتنے کے نتائج پر متنبہ کریں، اور انہیں اس کی آمد کا اس طرح یقین دلائیں جس طرح ایک آدمی اپنی آنکھوں دیکھی بات کا اس شخص کو یقین دلاتا ہے جس نے اسے نہیں دیکھا ہے۔ خاتمہ کلام میں قرآن کی اصل دعوت ، یعنی خدائے واحد کی بندگی کی دعوت نہایت مختصر، مگر انتہائی موٴثر انداز میں پیش کر کے لوگوں کو خبردار کیا گیا ہے کہ اسے قبول کرنا تمہارے اپنے لئے نافع اور اسے رد کرنا تمہارے اپنے لئے ہی نقصان دہ ہے ۔ اسے ماننے کے لئے اگر خدا کی وہ نشانیاں دیکھنے کا انتظار کرو گے جن کے سامنے آجانے کے بعد مانے بغیر چارہ نہ رہے گا۔ تو یاد رکھو کہ وہ فیصلے کا وقت ہوگا۔ اس وقت ماننے سے کچھ حاصل نہ ہوگا۔','سید ابو اعلیٰ مودودیؒ -  تفہیم القرآن');
INSERT INTO "chapters_ur" VALUES(28,'قصے',NULL,' نام : آیت نمبر ۲۵ کے اس فقرے سے ماخوذ ہے : وَ قَصَّ عَلیہ القَصَص َ، یعنی وہ سورة جس میں القصص کا لفظ آیا ہے ۔ لغت کے اعتبار سے قصص کے معنی ترتیب وار واقعات بیان کرنے کے ہیں۔ اس لحاظ سے یہ لفظ باعتبار معنی بھی اس سورة کا عنوان ہو سکتاہے ، کیونکہ اس میں حضرت موسیٰ کا مفصل قصہ بیان ہوا ہے۔ زمانہٴ نزول : سورة نمل کے دیباچے میں ابن عباس اور جابر بن زید رضی اللہ تعالیٰ عنہما کا یہ قول ہم نقل کر چکے ہیں کہ سورة شعراء ، سورة النمل اور سورة القصص یکے بعد دیگرے نازل ہوئیں ہیں ۔ زبان، اندازبیان اور مضامین سے بھی یہی محسوس ہوتا ہے کہ ان تینوں سورتوں کا زمانہٴنزول قریب قریب ایک ہی ہے۔ اوراس لحاظ سے بھی ان تینوں میں قریبی تعلق ہے کہ حضرت موسیٰ علیہ السلام کے قصے کے مختلف اجزاء جو ان میں بیان کے گئے ہیں وہ باہم مل کر ایک پورا قصہ بن جاتے ہیں ۔ سورة شعراءمیں نبوت کا منصب قبول کرنے سے معذرت کرتے ہوئے حضرت موسیٰ عرض کرتےہیں کہ ” قوم فرعون کا ایک جرم میرے ذمہ ہے جس کی وجہ سے میں ڈرتا ہوں کہ وہاں جاوٴں گا تو وہ مجھے قتل کر دیں گے “ پھر جب حضرت موسیٰ علیہ السلام فرعون کے ہاں تشریف لے جاتے ہیں تو وہ کہتا ہے ” کیا ہم نے اپنے ہاں تجھے بچہ سا نہیں پالا تھا ، اور تو ہمارے ہاں چند سال رہا پھر کر گیا وہ کچھ جو کچھ کہ کرگیا۔ “ ان دونوں باتوں کی کوئی تفصیل وہاں نہیں بیان کی گئی ۔ اس سورة میں اسے بتفصیل بیان کیا گیا ہے ۔ اسی طرح سورة نمل میں قصہ یکایک اس بات سے شروع ہوگیا کہ حضرت موسیٰ اپنے اہل و عیال کولے کر جارہے تھے، اور اچانک انھوں نے ایک آگ دیکھی ، وہاں اس کی کوئی تفصیل نہیں ملتی کہ یہ کیسا سفر تھا ، کہاں سے وہ آرہے تھے اور کدھر جارہے تھے۔ یہ تفصیل اس سورة میں بیان ہوئی ہے ۔ اس طرح یہ تینوں سورتیں مل کر قصہ موسیٰ علیہ السلام کی تکمیل کر دیتی ہیں۔ موضوع اور مباحث : اس کا موضوع ان شبہات اور اعتراضات کو رفع کرنا ہے جو نبی صلی اللہ علیہ وسلم کی رسالت پر وارد کیے جا رہے تھے، اور ان عذرات کو قطع کرنا ہے جو آپ پر ایمان نہ لانے کے لئے پیش کیے جاتے تھے۔ اس غرض کے لئے سب سے پہلے حضرت موسیٰ علیہ السلام کا قصہ بیان کیا گیا ہے جو زمانہٴنزول کے حالات سے مل کر خود بخود چند حقیقتیں سامع کے ذہن نشین کردیتاہے : اول یہ کہ اللہ تعالیٰ جو کچھ کرنا چاہتا ہے ، اس کے لئے وہ غیر محسوس طریقے سے اسباب وذرائع فراہم کر دیتا ہے۔ جس بچے کے ہاتھوں آخر کار فرعون کا تختہ الٹنا تھا، اسے اللہ نے خود فرعون ہی کے گھر میں اس کے اپنے ہاتھوں پرورش کرادیا اور فرعون یہ نہ جان سکا کہ وہ کسے پرورش کر رہاہے۔ اس خدا کی مشیت سے کون لڑ سکتا ہے اور کس کی چالیں اس کے مقابلے میں کامیاب ہو سکتی ہیں۔ دوسرے یہ کہ نبوت کسی شخص کو کسی بڑے جشن اور زمین اور آسمان سے کسی بھاری اعلان کے ساتھ نہیں دی جاتی۔ تم کو حیرت ہے کہ محمد (صلی اللہ علیہ وسلم)کو چپکے سے یہ نبوت کہاں سے مل گئی اور بیٹھے بٹھائے یہ نبی کیسے بن گئے۔ مگر جن موسیٰ (علیہ السلام )کا تم خود حوالہ دیتے ہو لَو لَا اُدتِیَ مِثلَ ماَ اُو تِیَ مُو سیٰ، (آیت ۴۸)،انہیں بھی اسی طرح راہ چلتے نبوت مل گئی تھی اور کسی کو کانوں کان خبر بھی نہ ہوئی تھی کہ آج طور سینا کی سنسان وادی میں کیا واقعہ پیش آگیا۔ موسیٰ علیہ السلام خود ایک لمحے پہلے تک نہ جانتے تھے کہ انھیں کیا چیز ملنے والی ہے۔ آگ لینے چلے تھے اور پیمبری مل گئی۔ تیسرے یہ کہ جس بندے سے خدا کوئی کام لینا چاہتا ہے وہ بغیر کسی لاوٴ لشکر اور سرو سامان کے اٹھتا ہے ۔کوئی اس کا مدد گار نہیں ہوتا، کوئی طاقت بظاہر اس کے پاس نہیں ہوتی، مگر بڑے بڑے لاوٴلشکر اور سروسامان والے آخر کار اس کے مقابلے میں دھرے کے دھرے رہ جاتے ہیں ۔ جو نسبت آج تم اپنے اور محمد(صلی اللہ علیہ وسلم) کے درمیان پا رہے ہو اس سے بہت زیادہ فرق موسیٰ (علیہ السلام)اور فرعون کی طاقت کے درمیان تھا۔ مگر دیکھ لو کون جیتا اور کون ہارا۔ چوتھے یہ کہ تم لوگ بار بار موسیٰ علیہ السلام کا حوالہ دیتے ہو کہ ” محمد (صلی اللہ علیہ وسلم)کو وہ کچھ کیوں نہ دیا گیا جو موسیٰ علیہ السلام کی دیا گیا تھا “ یعنی عصا اور ید بیضا اور دوسرے کھلے کھلے معجزے ۔ گو یا تم ایمان لانے کو تو تیار بیٹھے ہو ، بس انتظار ہے تو یہ کہ تمہیں وہ معجزے دکھائے جائیں جو موسیٰ علیہ السلام نے فرعون کو دکھائے تھے۔مگر تمہیں کچھ معلوم بھی ہے کہ جن لوگوں کو وہ معجزے دکھائے گئے تھے انھوں نے کیا کیا تھا؟ وہ انہیں دیکھ کر بھی ایمان نہ لائے ۔ انہوں نے کہا تو یہ کہا کہ یہ جادو ہے۔کیونکہ وہ حق کے خلاف ہٹ دھرمی اور عناد میں مبتلا تھے۔ اسی مرض میں آج تم مبتلا ہو ۔ کیا تم اسی طرح کے معجزے دیکھ کر ایمان لے آوٴ گے؟ پھر تمہیں کچھ یہ بھی خبر ہے کہ جن لوگوں نے وہ معجزے دیکھ کر حق کا انکار کیاتھا ان کا انجام کیا ہوا؟ آخر کار اللہ نے انہیں تباہ کرکے چھوڑا۔ اب کیا تم بھی ہٹ دھرمی کے ساتھ معجزے مانگ کر اپنی شامت بلانا چاہتے ہو؟ یہ وہ باتیں ہیں جو کسی تصریح کے بغیر آپ سے آپ ہر اس شخص کے ذہن میں اتر جاتی ہیں جو مکے کے کافرانہ ماحول میں اس قصے کو سنتا تھا، کیونکہ اس وقت محمد صلی اللہ علیہ وسلم اور کفار مکہ کے درمیان ویسی ہی ایک کشمکش برپا تھی جیسی اس سے پہلے فرعون اور حضرت موسیٰ علیہ السلام کے درمیان برپا ہوچکی تھی، اور ان حالات میں یہ قصہ سنانے کے معنی یہ تھے کہ اس کا ہر جز وقت کے حالات پر خود بخود چسپاں ہوتا چلا جائے، خواہ ایک لفظ بھی ایسا نہ کہا جائے جس سے معلوم ہو کہ قصے کا کون سا جز اس وقت کے کس معاملے پر چسپاں ہورہا ہے۔ اس کے بعد پانچویں رکوع سے اصل موضوع پر براہ راست کلام شروع ہوتا ہے۔ پہلے اس بات کو محمد صلی اللہ علیہ وسلم کی نبوت کا ایک ثبوت قرار دیا جاتاہے کہ آپ امی ہونے کے باوجود دوہزار برس پہلے گزرا ہوا ایک تاریخی واقعہ اس تفصیل کے ساتھ من و عن سنا رہے ہیں ۔ حالانکہ آپ کے شہر اور آپ کے برادری کے لوگ خوب جانتے تھے کہ آپ کے پاس ان معلومات کے حاصل ہونے کا کوئی ایسا ذریعہ نہیں ہے جس کی وہ نشاندہی کر سکیں۔ پھر آپ کے نبی بنائے جانے کو ان لوگوں کے حق میں اللہ کی ایک رحمت قرار دیا جاتا ہے کہ وہ غفلت میں پڑے ہوئے تھے اور اللہ نے ان کے لئے ہدایت کا انتظام کیا۔ پھر ان کے اس اعتراض کا جواب دیا جاتا ہے جو وہ بار بار پیش کرتے تھے کہ ” یہ نبی صلی اللہ علیہ وسلم وہ معجزے کیوں نہ لایا جو اس سے پہلے موسیٰ علیہ السلام لائے تھے۔ “ ان سے کہاجاتا ہے کہ موسیٰ علیہ السلام ، جن کے متعلق تم خود مان رہے ہو کہ وہ خدا کی طرف سے معجزے لائے تھے، انہی کو تم نے کب مانا ہے کہ اب اس نبی (صلی اللہ علیہ وسلم)سے معجزے کا مطالبہ کرتے ہو؟ خواہشات نفس کی بندگی نہ کروتو حق اب بھی تمہیں نظر آسکتا ہے۔ لیکن اگر اس مرض میں تم مبتلا رہو تو خواہ کوئی معجزہ آجائے، تمہاری آنکھیں نہیں کھل سکتیں۔ پھر کفار مکہ کو اس واقعہ پر عبرت اور شرم دلائی گئی جو اسی زمانے میں پیش آیا تھا کہ باہر سے کچھ عیسائی مکہ آئے اور نبی صلی اللہ علیہ وسلم سے قرآن سن کر ایمان لے آئے، مگر مکہ کے لوگ اپنے گھر کی اس نعمت سے مستفید تو کیا ہوتے، ان کے ابو جہل نے الٹی ان لوگوں کی کھلم کھلا بے عزتی کی۔ آخر میں کفار مکہ کے اس اصل عذر کی لیا جاتا ہے جو نبی صلی اللہ علیہ وسلم کی بات نہ ماننے کے لئے وہ پیش کرتے تھے۔ ان کا کہنا یہ تھا کہ اگر ہم اہل عرب دین شرک کو چھوڑ کر اس نئے دین توحید کو قبول کرلیں تو یکا یک اس ملک سے ہماری مذہبی، سیاسی اور معاشی چودھراہٹ ختم ہوجائے گی اور ہمارا حال یہ ہوگا کہ عرب کے سب سے زیادہ با اثر قبیلے کی حیثیت کھو کر اس سر زمین میں ہمارے لئے کوئی جائے پناہ تک باقی نہ رہے گی۔ یہ چونکہ سرداران قریش کی حق دشمنی کا اصل محرک تھا اور باقی سارے شبہات و اعتراضات محض بہانے تھے جو وہ عوام کو فریب دینے کے لئے تراشتے تھے، اس لئے اللہ تعالیٰ نے اس پر آخری سورة تک مفصل کلام فرمایا ہے اور اس کے ایک ایک پہلو پر روشنی ڈال کر نہایت حکیمانہ طریقے سے ان تمام بنیادی امراض کا مداوا کیا ہے جن کی وجہ سے یہ لوگ حق اور باطل کا فیصلہ دنیوی مفاد کے نقطہٴ نظر سے کرتے تھے۔ ','سید ابو اعلیٰ مودودیؒ -  تفہیم القرآن');
INSERT INTO "chapters_ur" VALUES(29,'مکڑی',NULL,' نام : آیت کے فقرے مَثَلُ الَّذِینَ التَّخَذُوا مِن دُونِ اللہِ اَو لِیَاءَ کَمَثَلِ العَنکَبُوتِ سے ماخوذ ہے۔مطلب یہ ہے کہ یہ وہ سورت ہے جس میں لفظ ” عنکبوت “ آیا ہے۔ زمانہٴ نزول : آیات۵٦ تا ٦۰ سے صاف مترشح ہوتا ہے کہ یہ سورة ہجرت حبشہ سے کچھ پہلے نازل ہوئی تھی۔ باقی مضامین کی اندرونی شہادت بھی اسی کی تائید کرتی ہے، کیونکہ پس منظر میں اسی زمانے کے حالات جھلکتے ہیں۔ بعض مفسرین نے صرف اس دلیل کی بنا پر کہ اس میں منافقین کا ذکر آیا ہے اور نفاق کا ظہور مدینہ میں ہوا ہے، یہ قیاس قائم کر لیا کہ اس سورة کی ابتدائی دس آیات مدنی ہیں اور باقی سورة مکی ہے۔ حالانکہ یہاں جن لوگوں کے نفاق کا ذکر ہے وہ وہ لوگ ہیں جو کفار کے ظلم و ستم اور شدید جسمانی اذیتوں کے ڈر سے منافقانہ روش اختیار کررہے تھے، اور ظاہر ہے کہ اس نوعیت کا نفاق مکہ ہی میں ہوسکتا تھا نہ کہ مدنیہ میں ۔ اسی طرح بعض دوسرے مفسرین نے یہ دیکھ کر کہ اس سورة میں مسلمانوں کو ہجرت کرنے کی تلقین کی گئی ہے، اسے مکہ کی آخری نازل شدہ سورت قرار دیا ہے ۔ حالانکہ مدینہ طیبہ کی طرف ہجرت کرنے سے پہلے مسلمان حبشہ کی طرف بھی ہجرت کرچکے تھے۔ یہ تمام قیاسات دراصل کسی روات پر مبنی نہیں ہیں بلکہ صرف مضامین کی اندرونی شہادت پر ان کی بنا رکھی گئی ہے۔ اور یہ اندرونی شہادت، اگر پوری سورت کے مضامین پر بحیثیت مجموعی نگاہ ڈالی جائے، مکہ کے آخری دور کی نہیں بلکہ اس دور کے حالات کی نشاندہی کرتی ہے جس میں ہجرت حبشہ واقع ہوئی تھی۔ موضوع ومضمون : سورة کو پڑھتے ہوئے محسوس ہوتا ہے کہ اس کے نزول کا زمانہ مکہ معظمہ میں مسلمانوں پر بڑے مصائب و شدائد کا زمانہ تھا۔ کفار کی طرف سے اسلام کی مخالفت پورے زور و شور سے ہورہی تھی اور ایمان لانے والوں پر سخت ظلم و ستم توڑے جارہے تھے۔ ان حالات میں اللہ تعالیٰ نے یہ سورة ایک طرف صادق الایمان لوگوں میں عزم و ہمت اور استقامت پیدا کرنے کے لئے ، اور دوسری طرف ضعیف الایمان لوگوں کو شرم دلانے کے لئے نازل فرمائی۔ اس کے ساتھ کفار مکہ کوبھی اس میں سخت تہدید کی گئی کہ اپنے حق میں اس انجام کو دعوت نہ دیں جو عداوت حق کا طریقہ اختیار کرنے والے ہر زمانے میں دیکھتے رہے ہیں۔ اس سلسلے میں ان سوالات کا جواب بھی دیا گیا ہے جو بعض نوجوانوں کو اس وقت پیش آرہے تھے ۔ مثلا ان کے والدین ان پر زور ڈالتے تھے کہ تم محمد (صلی اللہ علیہ وسلم)کا ساتھ چھوڑ دو اور ہمارے دین پر قائم رہو۔ جس قرآن پر تم ایمان لائے ہو اس میں بھی تو یہی لکھا ہے کہ ماں باپ کا حق سب سے زیادہ ہے ۔ تو ہم جو کچھ کہتے ہیں اسے مانو ورنہ تم خود اپنے ہی ایمان کے خلاف کام کرو گے۔ اس کا جواب آیت ۸ میں دیا گیا ہے۔ اسی طرح بعض نو مسلموں سے ان کے قبیلے کے لوگ کہتے تھے کہ عذاب ثواب ہماری گردن پر، تم ہمارا کہنا مانو اور اس شخص سے الگ ہوجاوٴ۔ اگر خدا تمہیں پکڑے گا تو ہم خود آگے بڑھ کر کہہ دیں گے کہ صاحب، ان بے چاروں کا کچھ قصور نہیں ، ان کو ہم نے ایمان چھوڑے پر مجبور کیا تھا، اس لئے آپ ہمیں پکڑلیں۔ اس کا جواب آیات ١۲۔١۳ میں دیا گیا ہے۔ جو قصے اس سورة میں بیان کئے گئے ہیں ان میں بھی زیادہ تر یہی پہلو نمایاں ہے کہ پچھلے انبیاء کو دیکھو، کیسی کیسی سختیاں ان پر گزریں اور کتنی کتنی مدت وہ ستائے گئے۔ پھر آخر کار اللہ کی طرف سے اس کی مدد ہوئی۔ اس لئے گھبراو ٴ نہیں۔ اللہ کی مدد ضرور آئے گی، مگر آزمائش کا ایک دور گزرنا ضروری ہے۔ مسلمانوں کو یہ سبق دینے کے ساتھ کفار مکہ کو بھی ان قصوں میں متنبہ کیا گیا ہے کہ اگر خدا کی طرف سے پکڑ ہونے میں دیر لگ رہی ہے تو یہ نہ سمجھ بیٹھو کہ کبھی پکڑ ہوگی ہی نہیں ۔ پچھلی تباہ شدہ قوموں کے نشانات تمہارےسامنے ہیں ۔ دیکھ لو آخر کار ان کی شامت آکر رہی اور خدا نے اپنے نبیوں کی مدد کی ۔ پھر مسلمانوں کو ہدایت کی گئی کہ اگر ظلم و ستم تمہارے لئے ناقابل برداشت ہوجائے تو ایمان چھوڑنے کے بجائے گھر بار چھوڑ کر نکل جاوٴ۔ خدا کی زمین وسیع ہے۔ جہاں خدا کی بندگی کر سکو وہاں چلے جاوٴ۔ ان سب باتوں کے ساتھ کفار کی تفہیم کا پہلو بھی چھوٹنے نہیں پایا ہے۔ توحید اور معاد، دونوں حقیقتوں کو دلائل کے ساتھ ان کے ذہن نشین کرانے کی کوشش کی گئی ہے، شرک کا ابطال کیا گیا ہے، ١ور آثار کائنات کی طرف توجہ دلا کر ان کو بتایا گیا ہے کہ یہ سب نشانات اس تعلیم کی تصدیق کر رہے ہیں جو ہمارا نبی (صلی اللہ علیہ وسلم)تمہارے سامنے پیش کررہا ہے۔','سید ابو اعلیٰ مودودیؒ -  تفہیم القرآن');
INSERT INTO "chapters_ur" VALUES(30,'روم',NULL,' نام : پہلی ہی آیت کے لفظ غُلِبَتِ الرُّوم سے ماخوذ ہے۔ زمانہٴ نزول : آغاز ہی میں جس تاریخی واقعہ کا ذکر کیا گیا ہے اس سے زمانہ نزول قطعی طور پر متعین ہو جاتا ہے۔ اس میں ارشاد ہوا ہے کہ ”قریب کی سر زمین میں رومی مغلوب ہوگئے ہیں۔“&gt; اس زمانے میں عرب سے متصل رومی مقبوضات اردن، شام اور فلسطین تھے اور ان علاقوں میں رومیوں پر ایرانیوں کا غلبہ ۶۱۵ء میں مکمل ہوا تھا۔ اس لئے پوری صحت کے ساتھ یہ کہا جا سکتا ہے کہ یہ سورة اسی سال نازل ہوئی تھی، اور یہ وہی سال تھا جس میں ہجرت حبشہ واقع ہوئی تھی۔ تاریخی پس منظر : جو پیش گوئی اس سورة کی ابتدائی آیات میں کی گئی ہے وہ قرآن مجید کے کلام الہٰی ہونے اور محمد صلی اللہ علیہ وسلم کے رسول برحق ہونے کی نمایاں ترین شہادتوں میں سے ایک ہے۔ اسے سمجھنے کے لئے ضروری ہے کہ ان تاریخی واقعات پر ایک تفصیلی نگاہ ڈالی جائے جو ان آیات سے تعلق رکھتے ہیں۔ نبی صلی اللہ علیہ وسلم کی نبوت سے ۸ سال پہلے کا واقعہ ہے کہ قیصر روم ماریس (Maurice) کے خلاف بغاوت ہوئی اور ایک شخص فوکاس (Phocas) تخت سلطنت پر قابض ہوگیا۔اس شخص نے پہلے تو قیصر کی آنکھوں کے سامنے اس کے پانچ بیٹوں کو قتل کرایا، پھر خود قیصر کو قتل کراکے باپ بیٹوں کےسر قسطنطنیہ میں بر سر عام لٹکوا دیے، اور اس کے چند روز بعد اس کی بیوی اور تین لڑکیوں کو بھی مروا ڈالا۔ اس واقعہ سے ایران کے بادشاہ خسرو پرویز کو روم پر حملہ آور ہونے کے لئے بہترین اخلاقی بہانہ مل گیا۔ قیصر ماریس اس کا محسن تھا۔ اسی کی مدد سے پرویز کو ایران کا تخت نصیب ہوا تھا۔ اس بنا پر اس نے اعلان کیا کہ میں غاصب فوکاس سے اس ظلم کا بدلہ لوں گا جو اس نے میرے مجازی باپ اور اس کی اولاد پر ڈھایا ہے۔ ۶۰۳ء میں اس نے سلطنت روم کے خلاف جنگ کا آغاز کیا اور چند سال کے اندر وہ فوکاس کی فوجوں کو پے درپے شکستیں دیتا ہوا ایک طرف ایشیائے کوچک میں ایڈیسا (موجودہ : اورفا)تک اور دوسری طرف شام میں حَلَب اورانطاکیہ تک پہنچ گیا۔ روم کے اعیان سلطنت یہ دیکھ کر کہ فوکاس ملک کو نہیں بچا سکتا، افریقہ کے گورنر سے مدد کے طالب ہوئے۔ اس نے بیٹے ہرقل (Heracllus) کو ایک طاقتور بیڑے کے ساتھ قسطنطنیہ بھیج دیا۔ اس کے پہنچتے ہی فوکاس معزول کردیا گیا، اس کی جگہ ہرقل قیصر بنایا گیا، اور اس نے برسر اقتدار آکر فوکاس کے ساتھ وہی کچھ کیا جو اس نے ماریس کے ساتھ کیا تھا۔ یہ ۶۱۰ء کا واقعہ ہے، اور وہی سال ہے جس میں نبی صلی اللہ علیہ وسلم اللہ تعالیٰ کی طرف سے منصب نبوت پر سرفراز ہوئے۔ خسرو پرویز نے جس اخلاقی بہانے کو بنیاد بناکر جنگ چھڑی تھی، فوکاس کا عزل اور قتل کے بعد وہ ختم ہوچکا تھا۔ اگر واقعی اس کی جنگ کا مقصد غاصب فوکاس سے اس کے ظلم کا بدلہ لینا ہوتا تو اس کے مارے جانے پر اسے نئے قیصر سے صلح کر لینی چاہیےتھی۔ مگراس نے پھر بھی جنگ جاری رکھی، اور اب اس جنگ کو اس نے مجوسیت اور مسیحیت کی مذہبی جنگ کا رنگ دے دیا۔ عیسائیوں کے جن فرقوں کو رومی سلطنت کے سرکاری کلیسا نے ملحدقرار دے کر سالہا سال سے تختہٴ مشق ستم بنا رکھا تھا (یعنی نسطوری اور یعقوبی وغیرہ)ان کی ساری ہمدردیاں بھی مجوسی حملہ آوروں کے ساتھ ہوگئیں۔ اور یہودیوں نے بھی مجوسیوں کا ساتھ دیا، حتیٰ کہ خسرو پرویز کی فوج میں بھرتی ہونے والے یہوویوں کی تعداد ۲۶ ہزار تک پہنچ گئی۔ ہرقل آکر اس سیلاب کو نہ روک سکا۔ تخت نشین ہوتے ہی پہلی اطلاع جو اسے مشرق سے ملی وہ انطاکیہ پر ایرانی قبضے کی تھی۔ اس کے بعد ۲۱۳ء میں دمشق فتح ہوا۔ پھر ۶۱۴ء میں بیت المقدس پر قبضہ کر کے ایرانیوں نے مسیحی دنیا پر قیامت ڈھادی۔ ۹۰ ہزار عیسائی اس شہر میں قتل کیے گئے۔ ان کا سب سے زیادہ مقدس کلیسا، کینستہ القیامہ (Holy Sepulchre) برباد کردیا گیا۔ اصلی صلیب، جس کے متعلق عیسائیوں کا عقیدہ تھا کہ اسی پر مسیح نے جان دی تھی، مجوسیوں نے چھین کر مدائن پہنچا دی ۔ لاٹ پادری زکریاہ کو بھی پکڑ لے گئے اور شہر کے تمام بڑے بڑے گرجوں کو انہوں نے مسمار کر دیا ۔ اس فتح کا نشہ جس بری طرح خسرو پرویز پر چڑھا تھا اس کا اندازہ اس خط سے ہوتا ہے جو اس نے بیت المقدس سے ہرقل کو لکھا تھا ۔ اس میں وہ کہتا ہے : ” سب خداوٴں سے بڑے خدا، تمام روئے زمین کے مالک خسرو کی طرف سے اس کے کمینہ اور بے شعور بندے ہرقل کے نام، تو کہتا ہے کہ تجھے اپنے رب پر بھروسہ ہے ۔ کیوں نہ تیرے رب نے یروشلم کو میرے ہاتھ سے بچا لیا؟ “ اس فتح کے بعد ایک سال کے اندر اندر ایرانی فوجیں اردن، فلسطین اور جزیرہ نمائے سینا کے پورے علاقے پر قابض ہوکر حدود مصر تک پہنچ گئیں ۔ یہ وہ زمانہ تھا جب مکہ معظمہ میں ایک اور اس سے بدرجہا زیادہ تاریخی اہمیت رکھنے والی جنگ برپاتھی۔ یہاں توحید کے علم بردار سیدنا محمد صلی اللہ علیہ وسلم کی قیادت میں، اور شرک کے پیرو کار سرداران قریش کی رہنمائی میں ایک دوسرے سے برسرجنگ تھے، اور نوبت یہاں تک پہنچ گئی تھی کہ ۶۱۵ء میں مسلمانوں کی ایک بڑی تعداد کو اپنا گھر بار چھوڑ کر حبش کی عیسائی سلطنت میں (جو روم کی حلیف تھی)پناہ لینی پڑی ۔ اس وقت سلطنت روم پر ایران کے غلبے کا چرچا ہر زبان پر تھا۔ مکے کے مشرکین اس پر بغلیں بجا رہے تھے اور مسلمانوں سے کہتے تھے کہ دیکھو ایران کے آتش پرست فتح پا رہے ہیں اور وحی اور رسالت کو ماننے والے عیسائی شکست پر شکست کھاتے چلے جا رہے ہیں ۔ اسی طرح ہم عرب کے بت پرست بھی تمہیں اور تمہارے دین کو مٹا کر رکھ دیں گے۔ ان حالات میں قرآن مجید کی یہ سورة نازل ہوئی اور اس میں یہ پیشین گوئی کی گئی کہ ” قریب کی سر زمین میں رومی مغلوب ہوگئے ہیں ، مگر اس مغلوبیت کے بعد چند سال کے اندر اندر ہی وہ غالب آجائیں گے ، اور وہ دن وہ ہوگا جب کہ اللہ کی دی ہوئی فتح سے اہل ایمان خوش ہورہے ہونگے۔ “ اس میں ایک کے بجائے دو پیشین گوئیاں تھیں ۔ ایک یہ کہ رومیوں کو غلبہ نصیب ہوگا۔ دوسری یہ کہ مسلمانوں کو بھی اسی زمانے میں فتح حاصل ہوگی۔ بظاہر دور دور تک کہیں اس کے آثار موجود نہ تھے کہ ان میں سے کوئی ایک پیشین گوئی بھی چند سال کے اندر پوری ہوجائے گی۔ ایک طرف مٹھی بھر مسلمان تھے جو مکے میں مارے اور کھدیڑے جارہے تھے۔ اور اس پیشین گوئی کے بعد بھی آٹھ سال تک ان کے لئے غلبہ و فتح کا کوئی امکان کسی کو نظر نہ آتا تھا۔ دوسری طرف روم کی مغلوبیت روز بروز بڑھتی چلی گئی۔سن ۶۱۹ء تک پورا مصر ایران کے قبضہ میں چلا گیا اور مجوسی فوجوں نے طرابلس کے قریب پہنچ کر اپنے جھنڈے گاڑ دیئے۔ ایشیائے کوچک میں ایرانی فوجیں رومیوں مارتی دباتی باسفورس کے کنارے تک پہنچ گئیں اور سن ۶۱۷ء میں انہوں نے عین قسطنطنیہ کے سامنے خلقدون ( Chalcedon ،موجودہ قاضی کوئی )پر قبضہ کر لیا۔ قیصر نے خسرو کے پاس ایلچی بھیج کر نہایت عاجزی کے ساتھ درخواست کی کہ میں ہر قیمت پر صلہ کرنے کے لئے تیار ہوں۔ مگر اس نے جواب دیا کہ ” اب میں قیصر کو اس وقت تک امان نہ دونگا جب تک وہ پابزنجیر میرے سامنے حاضر نہ ہو اور اپنے خدائے مصلوب کو چھوڑ کر خداوند آتش کی بندگی نہ اختیار کر لے۔ “ آخر کار قیصر اس حد تک شکست خوردہ ہوگیا کہ اس نے قسطنطنیہ چھوڑ کر قرطاجنہ ( Carthage ، موجودہ ٹیونس)منتقل ہوجانے کا ارادہ کر لیا۔غرض انگریز موٴرخ گبن کے بقول، قرآن مجید کی اس پیشین گوئی کے بعد بھی سات آٹھ برس تک حالات ایسے تھےکہ کوئی شخص یہ تصور تک نہ کر سکتا تھا کہ رومی سلطنت ایران پر غالب آجائی گی، بلکہ غلبہ تو درکنار اس وقت تو کسی کو یہ امید بھی نہ تھی کہ اب یہ سلطنت زندہ رہ جائے گی۔(۱)قرآن کی یہ آیات جب نازل ہوئیں تو کفار مکہ نے ان کا خوب مذاق اڑایا اور ابی بن خلف نے حضرت ابو بکر سے شرط بدی کہ اگر تین سال کے اندر رومی غالب آگئے تو دس اونٹ میں دوں گا ورنہ دس اونٹ تم کو دینے ہوں گے۔ نبی صلی اللہ علیہ وسلم کو اس شرط کا علم ہوا تو آپ صلی اللہ علیہ وسلم نے فرمایا کہ قرآن میں فِی بِضعِ سِنِین کے الفاظ آئے ہیں، اور عربی زبان میں بِضع کا اطلاق دس سال سے کم پر ہوتا ہے، اس لئے دس سال کے اندر کی شرط کرو اور اونٹوں کی تعداد بڑھا کر سو (۱۰۰) کردو۔ چنانچہ حضرت ابو بکر رضی اللہ تعالیٰ نے اُبَیّ سے پھر بات کی اور نئے سرےسے یہ شرط طے ہوئی کہ دس سال کے اندر قریقین میں سے جس کی بات غلط ثابت ہوگی وہ سو(۱۰۰)اونٹ دے گا۔ سن ۶۲۴ء میں اِدھر نبی صلی اللہ علیہ وسلم ہجرت کرکے مدینہ طیبہ تشریف لے گئے، اور اُدھر قیصر ہرقل خاموشی کے ساتھ قسطنطنیہ سے بحر اسود کے راستے طرابزون کی طرف روانہ ہوا جہاں اس نے ایران پر پشت کی طرف سے حملہ کرنے کی تیاری کی۔ اس جوابی حملے کی تیاری کے لئے قیصر نے کلیسا سے روپیہ مانگا اور مسیحی کلیسا کے اُسقُف اعظم سر جیس (Sergius) نے مسیحیت کی مجو سیت سے بچانے کے لئے گرجاوٴں کے نذرانوں کی جمع شدہ دولت سود پر قرض دی۔ ہرقل نے اپنا حملہ سن ۶۲۳ء میں ارمینیا سے شروع کیا اور دوسرے سال سن ۶۲۴ء میں اس نے آذربیجان میں گھس کر زرتشت کے مقام پیدائش ارمیاہ (Clorumia) کو تباہ کر دیا اور ایرانیوں کے سب سے بڑے آتش کدے کی اینٹ سے اینٹ بجا دی۔ خدا کی قدرت کا کرشمہ دیکھیے کہ یہی وہ سال تھا جس میں مسلمانوں کو بدر کے مقام پر پہلی مرتبہ مشرکین کے مقابلے میں فیصلہ کن فتح نصیب ہوئی۔اس طرح وہ دونوں پیشین گوئیاں جو سورة روم میں کی گئی تھیں، دس سال کی مدت ختم ہونے سے پہلے بیک وقت پوری ہوگئیں۔ پھر روم کی فوجیں ایرانیوں کو مسلسل دباتی چلی گئیں۔ نینویٰ کی فیصلہ کن لڑائی (سن ۶۲۷ء)میں انہوں نے سلطنت ایران کی کمر توڑدی۔ اس کے بعد شاہان ایران کی قیام گاہ دستگرد (دَسکرة الملک)کو تباہ کر دیا اور آگے بڑھ کر ہرقل کے لشکر طیسفون (Ctesiphon) کے سامنے پہنچ گئے جو اس وقت ایران کا دارالسلطنت تھا۔ سن ۶۲۸ء میں خسرو پرویز کے خلاف گھر میں بغاوت رونما ہوئی، وہ قید کر لیا گیا، اس کی آنکھوں کے سامنے اس کے ۱۸ بیٹے قتل کردیے گئے، اور چند روز بعد وہ خود قید کی سختیوں سے ہلاک ہوگیا۔ یہی سال تھا جس میں صلح حدیبیہ واقع ہوئی جسے قرآن ” فتح عظیم “ کے نام سے تعبیر کرتا ہے، اور یہی سال تھا جس میں خسرو کے بیٹے قباد ثانی نے تمام رومی مقبوضات سے دست بردار ہو کر اور اصلی صلیب واپس کر کے روم سے صلح کر لی۔سن ۶۲۹ء میں قیصر ” مقدس صلیب “ کو اس کی جگہ رکھنے کے لئے خود بیت المقدس گیا، اور اسی سال نبی صلی اللہ علیہ وسلم عُمرة القضا ادا کرنے کے لئے ہجرت کے بعد پہلی مرتبہ مکہ معظمہ میں داخل ہوئے۔ اس کے بعد کسی کے لئے بھی اس امر میں شبہہ کی گنجائش باقی نہ رہی کہ قرآن کی پیشین گوئی بالکل سچی تھی۔عرب کے بکثرت مشرکین اس پر ایمان لے آئے۔ اُبَیّ بن خَلف کے وارثوں کو ہار مان کر شرط کے اونٹ ابو بکر صدیق رضی اللہ تعالیٰ عنہ کے حوالے کرنے پڑے۔ وہ انہیں لے کر نبی صلی اللہ علیہ وسلم کی خدمت میں حاضر ہوئے۔ آپ نے حکم دیا کہ انھیں صدقہ کر دیا جائے۔ کیونکہ شرط اس وقت ہوئی تھی جب شریعت میں جوئے کی حرمت کا حکم نہیں آیا تھا، مگر اب حرمت کا حکم آچکا تھا، اس لئے حربی کافروں سے شرط کا مال لے لینے کی اجازت تو دے دی گئی مگر ہدایت کی گئی کہ اسے خود استعمال کرنے کے بجائے صدقہ کر دیا جائے۔ موضوع اور مضمون: اس سورة میں کلام کا آغاز اس بات سے کیا گیا ہے کہ آج رومی مغلوب ہوگئے ہیں اور ساری دنیا یہ سمجھ رہی ہے کہ اس سلطنت کا خاتمہ قریب ہے، مگر چند سال نہ گزرنے پائیں گے کہ پانسہ پلٹ جائے گا اور جو مغلوب ہے وہ غالب ہوجائے گا۔ اس تمہید سے یہ مضمون نکل آیاکہ انسان اپنی سطح بینی کی وجہ سے وہی کچھ دیکھتا ہے ، جو بظاہر اس کی آنکھوں کے سامنے ہوتا ہے، مگر اس ظاہر کے پردے کے پیچھے جو کچھ ہے اس کی اسے خبر نہیں ہوتی۔ یہ ظاہر بینی جب دنیا کے ذرا ذرا سے معاملات میں غلط فہمیوں اور غلط اندازوں کی موجب ہوتی ہے، اور جبکہ محض اتنی سی بات نہ جاننے کی وجہ سے کہ ” کل کیا ہونے والا ہے “ آدمی غلط تخمینے لگا بیٹھتا ہے، تو پھر بحیثیت مجموعی پوری زندگی کے معاملے میں ظاہر حیات دنیا پر اعتماد کر بیٹھنا اور اسی کی بنیاد پر اپنے پورے سرمایہ حیات کو داوٴ پر لگا دینا کتنی بڑی غلطی ہے۔ اس طرح روم اور ایران کے معاملےسے تقریر کا رخ آخرت کے مضمون کی طرف پھرجاتا ہے ۔ اور مسلسل تین رکوعوں تک طریقے طریقے سے یہ سمجھانے کی کوشش کی جاتی ہے کہ آخرت ممکن بھی ہے، معقول بھی ہے، اس کی ضرورت بھی ہے، اورانسانی زندگی کے نظام کو درست رکھنے کے لئے بھی یہ ضروری ہے کہ آدمی آخرت کا یقین رکھ کر اپنی موجودہ زندگی کا پروگرام اختیار کرے، ورنہ وہی غلط ہوگی جو ظاہر پر اعتماد کرلینے سے واقع ہوا کرتی ہے۔ اس سلسلے میں آخرت پر استدلال کرتے ہوئے کائنات کے جن آثار کو شہادت میں پیش کیا گیا ہے وہ بعینہ وہی آثار ہیں جو توحید پر بھی دلالت کرتے ہیں۔ اس لئے چوتھے رکوع کے آغاز سے تقریر کا رخ توحید کے اثبات اور شرک کے ابطال کی طرف پھر جاتا ہے اور بتایا جاتا ہے کہ انسان کے لئے فطری دین اس کے سوا کچھ نہیں ہے کہ وہ بالکل یکسو ہوکر خدائے واحد کی بندگی کرے۔شرک فطرت کائنات اور فطرت انسان کے خلاف ہے، اسی لئے جہاں بھی انسان نے اس گمراہی کو اختیار کیا ہے وہاں فساد رونما ہوا ہے ۔ اس موقع پر پھر اس فساد عظیم کی طرف، جو اس وقت دنیا کی دو سب سے بڑی سلطنتوں کے درمیان جنگ کی بدولت برپا تھا، اشارہ کیا گیا ہے اور بتایا گیا ہے کہ یہ فساد شرک کے نتائج میں سے ہے اور پچھلی انسانی تاریخ میں بھی جتنی قومیں مبتلائے فساد ہوئیں ہیں وہ سب بھی مشرک ہی تھیں۔ خاتمہ کلام پر تمثیل کے پیرایہ میں لوگوں کو سمجھایا گیا ہے کہ جس طرح مردہ پڑی ہوئی زمین خدا کی بھیجی ہوئی بارش سے یکایک جی اٹھتی ہے اور زندگی و بہارکے خزانے اگلنے شروع کردیتی ہے، اسی طرح خدا کی بھیجی ہوئی وحی اور نبوت بھی مردہ پڑی ہوئی انسانیت کے حق میں ایک باران رحمت ہے جس کانزول اس کے لئے زندگی اور نشو ونما اور خیر و فلاح کا موجب ہوتا ہے ۔ اس موقع سے فائدہ اٹھاوٴ گے تو یہی عرب کی سونی زمین رحمت الہٰی سے لہلہا اٹھے گی اور ساری بھلائی تمہارے اپنے لئے ہی ہوگی۔ اس سے فائدہ نہ اُٹھاو گے تو اپنا ہی نقصان کرو گے، پھر پچھتانے کا کچھ حاصل نہ ہوگا اور تلافی کا کوئی موقع تمہیں میسر نہ آئے گا۔ ','سید ابو اعلیٰ مودودیؒ -  تفہیم القرآن');
INSERT INTO "chapters_ur" VALUES(31,'لقمان',NULL,' نام : اس سورہ کے دوسرے رکوع میں وہ نصیحتیں نقل کی گئی ہیں جو لقمان حکیم نے اپنے بیٹے کو کی تھیں۔ اسی مناسبت سے اس کا نام لقمان رکھا گیا ہے ۔ زمانۂ نزول : اس کے مضامین پر غور کرنے سے صاف محسوس ہوتا ہے کہ یہ اس زمانے میں نازل ہوئی ہے جب اسلامی دعوت کو دبانے اور روکنے کے لے جبر و ظلم کا آغاز ہو چکا تھا اور ہر طرح کے ہتھکنڈے استعمال کیے جانے لگے تھے لیکن ابھی طوفانِ مخالفت نے پوری شدت اختیار نہ کی تھی۔ اس کی نشان دہی آیت ۱۴۔۱۵ سے ہوتی ہے جس میں نئے نئے مسلمان ہونے والے نوجوانوں کو بتایا گیا ہے کہ والدین کے حقوق تو بے شک خدا کے بعد سب سے نہ مانو یہی بات سورۂ عنکبوت میں بھی ارشاد ہوئی ہے جس سے معلوم ہوتا ہے کہ دونوں سورتیں ایک ہی دَور میں نازل ہوئی ہیں۔ لیکن دونوں کے مجموعی انداز بیان اور مضمون پر غور کرنے سے اندازہ ہوتا ہے کہ سورۂ لقمان پہلے نازل ہوئی ہے ، اس لیے کہ اس کے پس منظر میں کسی شدید مخالفت کا نشان نہیں ملتا، اور اس کے برعکس سورۂ عنکبوت کو پڑھتے ہوئے محسوس ہوتا ہے کہ اس کے نزول کے زمانہ میں مسلمانوں پر سخت ظلم و ستم ہو رہا تھا۔ موضوع و مضمون: اس سورہ میں لوگوں کو شرک کی لغویت و نامعقولیت اور توحید کی صداقت و معقولیت سمجھائی گئی ہے ، اور انہیں دعوت دی گئی ہے کہ باپ دادا کی اندھی تقلید چھوڑ دیں، کھلے دل سے اس تعلیم پر غور کریں جو محمد صلی اللہ علیہ و سلم خداوندِ عالم کی طرف سے پیش کر رہے ہیں، اور کھلی آنکھوں سے دیکھیں کر ہر طرف کائنات میں اور خود ان کے اپنے نفس میں کیسے کیسے صریح آثار اس کی سچائی پر شہادت دے رہے ہیں۔ اس سلسلے میں یہ بھی بتایا گیا ہے کہ یہ کوئی نئی آواز نہیں ہے جو دنیا میں یا خود دیارِ عرب میں پہلی مرتبہ ہی اٹھی ہو اور لوگوں کے لیے بالکل نامانوس ہو۔ پہلے بھی جو لوگ علم و عقل اور حکمت و دانائی رکھتے تھے وہ یہی باتیں کہتے تھے جو آج محمد صلی اللہ علیہ و سلم کہہ رہے ہیں۔ تمہارے اپنے ہی ملک میں لقمان نامی حکیم گزر چکا ہے جس کی حکمت و دانش کے افسانے تمہارے ہاں مشہور ہیں، جس کی ضرب الامثال اور جس کے حکیمانہ مقولوں کو تم اپنی گفتگوؤں میں نقل کرتے ہو، جس کا ذکر تمہارے شاعر اور خطیب اکثر کیا کرتے ہیں۔ اب خود دیکھ لو کہ وہ کس عقیدے اور کن اخلاقیات کی تعلیم دیتا تھا۔','سید ابو اعلیٰ مودودیؒ -  تفہیم القرآن');
INSERT INTO "chapters_ur" VALUES(32,'سجدہ',NULL,' نام: آیت ۱۵ میں سجدہ کا جو مضمون آیا ہے اسی کو سورہ کا عنوان قرار دیا گیا ہے ۔ زمانۂ نزول: انداز بیاں سے ایسا محسوس ہوتا ہے کہ اس کا زمانۂ نزول مکّہ کے دَور متوسّط ہے ، اور اس کا بھی ابتدائی زمانہ، کیوں کہ اس کلام کے پس منظر میں ظلم و ستم کی وہ شدّت نظر نہیں آتی جو بعد کے ادوار کی سورتوں کے پیچھے نظر آتی ہے ۔ موضوع اور مباحث: سُورہ کا موضوع توحید آخرت اور رسالت کے متعلق لوگوں کے شبہات کو رفع کرنا اور ان تینوں حقیقتوں پر ایمان کی دعوت دینا ہے ۔ کفارِ مکہ نبی صلی اللہ علیہ و سلم کے متعلق آپس میں چرچے کر رہے تھے کہ یہ شخص عجیب عجیب باتیں گھڑ گھڑ کر سُنارہا ہے ۔ کبھی مرنے کے بعد کی خبریں دیتا ہے اور کہتاہے مٹی میں رَل مِل جانے کے بعد تم پھر اُٹھائے جاؤگے اور حساب کتاب ہو گا اور دوزخ ہو گی اور جنت ہو گی کبھی کہتا ہے کہ یہ دیوی دیوتا اور بزرگ کوئی چیز نہیں ہیں ، بس اکیلا ایک خدا ہی معبُود ہے ۔ کبھی کہتا ہے کہ میں خدا کا رسول ہوں ، آسمان سے مجھ پر وحی آتی ہے اور یہ کلام جو میں تم کو سنا رہا ہوں ، میرا کلام نہیں بلکہ خدا کا کلام ہے ۔ یہ عجیب افسانے ہیں جو یہ شخص ہمیں سُنا رہا ہے ۔۔ انہی باتوں کا جواب اس سورہ کا موضوع بحث ہے ۔ اِس جواب میں کفّار سے کہا گیا ہے کہ بلا شک و ریب یہ خدا ہی کا کلام ہے اور اس لیے نازل کیا گیا ہے کہ نبوّت کے فیض سے محروم، غفلت میں پڑی ہوئی ایک قوم کو چونکایا جائے ۔اِسے تم اِفتراء کیسے کہ سکتے ہو جب کہ اس کا منزَّل من اللہ ہونا ظاہر و باہر ہے ۔ پھر ان سے فرمایا گیا ہے کہ یہ قرآن جن حقیقتوں کو تمہارے سامنے پیش کرتا ہے ، عقل سے کام لے کر خود سوچو کہ ان میں کیا چیز اچنبھے کی ہے ۔آسمان و زمین کے انتظام کو دیکھو، خود اپنی پیدائش اور بناوٹ پر غور کرو ،کیا یہ سب کچھ اُس تعلیم کی صداقت پر شاہد نہیں ہے او اس نبی کی زبان سے اِس قرآن میں تم کو دی جا رہی ہے ؟ یہ نظام کائنات توحید پر دلالت کر رہا ہے یا شرک پر؟ اور اس سارے نظام کو دیکھ کر اور خود اپنی پیدائش پر نگاہ ڈال کر کیا تمہاری عقل یہی گواہی دیتی ہے کہ جس نے اب تمہیں پیدا کر رکھا ہے وہ پھر تمہیں پیدا نہ کر سکے گا ؟ پھر عالمِ آخرت کا نقشہ کھینچا گیا ہے اور ایمان کے ثمرات ا ور کفر کے نتائج و عواقب بیان کر کے یہ ترغیب دلائی گئی ہے کہ لوگ بُرا انجام سامنے آنے سے پہلے کفر چھوڑ دیں اور قرآن کی اس تعلیم کو قبول کر لیں جسے مان کر خود ان کی اپنی ہی عاقبت درست ہو گی ۔ پھر ان کو بتایا گیا ہے کہ یہ اللہ کی بڑی رحمت ہے کہ وہ انسان کے قصوروں پر یکایک آخری اور فیصلہ کُن عذاب میں اسے نہیں پکڑ لیتا بلکہ اُس سے پہلے چھوٹی چھوٹی تکلیفیں مصیبتیں ، آفات اور نقصانات بھیجتا رہتا ہے ۔ ہلکی ہلکی چوٹیں لگاتا رہتا ہے ، تاکہ اُسے تنبیہ ہو اور اس کی آنکھیں کھل جائیں ۔ آدمی اگر ان ابتدائی چوٹوں ہی سے ہوش میں آ جائے تو اس کے حق میں بہتر ہے ۔ پھر فرمایا کہ دُنیا میں یہ کوئی پہلا اور انوکھا واقعہ تو نہیں ہے کہ ایک شخص پر خدا کی طرف سے کتاب آئی ہو ۔ اس سے پہلے آخر موسیٰ (علیہ السلام ) پر بھی کتاب آئی تھی جسے تم سب لو گ جانتے ہو ۔یہ آخر کونسی ایسی بات ہے کہ اس پر تم لوگ یوں کان کھڑے کر رہے ہو یقین مانو کہ یہ کتاب خدا ہی کی طرف سے آئی ہے اور خوب سمجھ لو کہ اب پھر وہی کچھ ہو گا جو موسیٰ کے عہد میں ہو چکا ہے ۔ امامت و پیشوائی اب انہی کو نصیب ہو گی جو اس کتابِ الہٰی کو مان لیں گے ۔ اسے رد کر دینے والوں کے لیے ناکامی مقدر ہو چکی ہے ۔ پھر کفّارِ مکّہ سے کہا گیا ہے کہ اپنے تجارتی سفروں کے دَوران میں تم جن پچھلی تباہ شدہ قوموں کی بستیوں پر سے گزرتے ہو ان کا انجام دیکھ لو ، کیا یہی انجام تم اپنے لیے پسند کرتے ہو؟ ظاہر سے دھوکہ نہ کھاؤ ۔آج تم دیکھ رہے ہو کہ محمد(صلی اللہ علیہ و سلم)کی بات چند لڑکوں اور چند غلاموں اور غریب لوگوں کے سوا کوئی نہیں سُن رہا ہے اور ہر طرف سے ان پر طعن اور ملامت اور پھبتیوں کی بارش ہو رہی ہے ۔ اس سے تم یہ سمجھ بیٹھے ہو کہ یہ چلنے والی بات نہیں ہے ، چار دن چلے گی اور پھر ختم ہو جائے گی۔ لیکن یہ تمہاری نظر کا دھوکا ہے ، کیا یہ تمہارا رات دن کا مشاہدہ نہیں ہے کہ آج ایک زمین بالکل بے آب و گیاہ پڑی ہے جسے دیکھ کر گمان تک نہیں ہو تا کہ اس کے پیٹ میں روئیدگی کے خزانے چھپے ہوئے ہیں ، مگر کل ایک ہی بارش میں وہ اس طرح بھَبک اُٹھتی ہے کہ اس کے چپّے چپّے سے جو کی طاقتیں پھوٹنی شروع ہو جاتی ہیں ۔ خاتمۂ کلام پر نبی صلی اللہ علیہ و سلم کو خطاب کر کے فرمایا گیا ہے کہ یہ لوگ تمہاری باتیں سُن کر مذاق اُڑاتے ہیں اور پوچھتے ہیں کہ حضرت، یہ فیصلہ کُن فتح آپ کو کب نصیب ہونے والی ہے ، ذرا تاریخ تو ارشاد ہو۔ ان سے کہو کہ جب ہمارے اور تمہارے فیصلے کا وقت آ جائے گا اس وقت ماننا تمہارے لیے کچھ بھی مفید نہ ہو گا۔ ماننا ہے تو اب مان لو، اور آخری فیصلے ہی کا انتظار کرنا ہے تو بیٹھے انتظار کرتے رہو۔ ','سید ابو اعلیٰ مودودیؒ -  تفہیم القرآن');
INSERT INTO "chapters_ur" VALUES(33,'متحد گروہ',NULL,' نام: آیت ۲۰ کے فقرہ یَحْسَبُونَ الْاَحْزَابَ لَمْ یَذْھَبُوْا سے ماخوذ ہے۔ زمانۂ نزول: اس سورۃ کے مضامین تین اہم واقعات سے بحث کرتے ہیں۔ ایک غزوۂ اَحزاب جو شوال ۵ ھجری میں پیش آیا۔ دوسرے غزوۂ بنی قُرَیْظَہ جو ذالقعدہ ۵ ھجری میں پیش آیا۔تیسرے حضرت زینب سے نبی صلی اللہ علیہ و سلم کا نکاح جو اسی سال ذالقعدہ میں ہوا۔ ان تاریخی واقعات سے سورۃ کا زمانۂ نزول ٹھیک متعیّن ہو جاتا ہے۔ تاریخی پس منظر: جنگِ اُحْد (شوّال ۳ ھ) میں نبی صلی اللہ علیہ و سلم کے مقرر کیے ہوئے تیر اندازوں کی غلطی سے لشکر اسلام کو جو شکست نصیب ہو گئی تھی اس کی وجہ سے مشرکین عرب ، یہود اور مُنافقین کی ہمّتیں بہت بڑھ گئی تھیں اور نہیں اُمید بندھ چلی تھی کہ وہ اسلام اور مسلمانوں کا قلع قمع کرنے میں کامیاب ہو جائیں گے۔ ان بڑھتے ہوئے حوصلوں کا اندازہ ان واقعات سے ہو سکتا ہے جو اُحْد کے بعد پہلے ہی سال میں پیش آئے۔جنگ اُحْد پر دو مہینوں سے زیادہ نہ گزرے تھے کہ نجد کے قبیلۂ بنی اَسَدْ نے مدینہ طیّبہ پر چھاپا مارنے کی تیاریاں کیں اور نبی صلی اللہ علیہ و سلم کو ان کی روک تھام کے لیے سَرِیَّۂابو سَلَمہ(اصطلاح میں سَرِیہَّ اس فوجی مہم کو کہتے ہیں جس میں نبی صلی اللہ علیہ و سلم خود شریک نہ ہوتے تھے۔ اور غزْوہ اُس جنگ یا مہم کو کہا جاتا ہے جس میں حُضورؐ خود قیادت فرماتے تھے ) بھیجنا پڑا۔ پھر صفر ۴ھ میں قبائل عَضَل اور قارَہ نے حضُورؐ سے چند آدمی مانگے تاکہ وہ ان علاقہ میں جا کر لوگوں کو دینِ اسلام کی تعلیم دیں۔ حُضورؐ نے چھ اصحاب کو ان کے ساتھ کر دیا۔مگر رَجیع(جدّہ اور رابِغ کے درمیان ) پہنچ کر وہ لوگ قبیلۂ ھُذَیل کے کفار کو ان بے بس مبلّغین پر چڑھا لائے ، ان میں سے چار کو قتل کر دیا ، اور دو صاحبوں (حضرت خُبَیب بن عَدِی اور حضرت زید بن الدَّثِنَّہ ) کو لے جا کر مکّۂ معظمہ میں دشمنوں کے ہاتھ فروخت کر دیا۔ پھر اسی ماہِ صفر میں بنی عامر کے ایک سردار کی درخواست پر حضورؐ نے ایک اور وفد جو چالیس (یا بقول بعض ۷۰)انصاری نوجوانوں پر مشتمل تھا ، نجد کی طرف روانہ کیا۔مگر ان کے ساتھ بھی غدّاری کی گئی اور بنی سُلیم کے قبائل عُصَیَّہ اور رِعْل اور ذکْو ان نے بِئر مَعُونہ کے مقام پر اچانک نرغہ کر کے ان سب کو قتل کر دیا۔ اسی دوران میں مدینے کا یہودی قبیلہ بنی النَّضِیر دلیر ہو کر مسلسل بد عہدیاں کرتا رہا ، یہاں تک کہ ربیع الاوّل ۴ ھ میں اُس نے خود نبی صلی اللہ علیہ و سلم کو شہید کر دینے کی سازش تک کر ڈالی۔ پھر جمادی الاولیٰ ۴ھ میں بنی غَطَفان کے دو قبیلوں ، بنو ثَعْلَبَہ اور بنو مُحَارِب نے مدینہ پر حملے کی تیاریاں کیں اور حضُورؐ کو خود ان کی روک تھام کے لیے جانا پڑا۔ اس طرح جنگ اُحْد کی شکست سے جو ہَوا اُکھڑی تھی وہ مسلسل سات آٹھ مہینے تک اپنا رنگ دکھاتی رہی۔ لیکن وہ صرف محمد صلی اللہ علیہ و سلم کا عزم و تدبّر اور صحابۂ کرام کا جذبۂ فدا کاری تھا جس نے تھوڑی مدّت کے اندر ہی حالات کا رُخ بدل کر رکھ دیا۔ عربوں کے معاشی مقاطعہ نے اہل مدینہ کے لیے جینا دشوار کر رکھا تھا۔گرد و پیش کے تمام مشرک قبائل چیرہ دست ہو رہے تھے۔ خود مدینہ کے اندر یہود اور منافقین مارِ آستین بنے ہوئے تھے۔ مگر ان مٹھی بھر مومنین صادقین نے رسولؐ خُدا کی قیادت میں پے درپے ایسے اقدامات کیے جن سے عرب میں اسلام کا رعب صرف بحال ہی نہیں ہو گیا ، بلکہ پہلے سے زیادہ بڑھ گیا۔ جنگِ احزاب سے پہلے کے غَزوات: ان میں سے اوّلین اقدام وہ تھا جو جنگ اُحُد کے فوراً ہی بعد کیا گیا۔جنگ کے ٹھیک دوسرے روز جبکہ بکثرت مسلمان زخمی تھے اور بہت سے عزیز ترین اقارب کی شہادت پر کہرام برپا تھا اور رسول صلی اللہ علیہ و سلم خود بھی زخمی اور حضرت حمزہؓ کی شہادت پر دِلفگار تھے ، حضُورؐ نے اسلام کے فِدائیوں کو پُکارا کہ لشکر کفّار کے تعاقب میں چلنا ہے تاکہ وہ کہیں راستے سے پلٹ کر پھر مدینے پر حملہ آور نہ ہو جائیں۔ حضورؐ کا اندازہ بالکل صحیح تھا کہ کفّار قریش ہاتھ آئی ہوئی فتح کا کوئی فائدہ اُٹھائے بغیر واپس تو چلے گئے ہیں ،لیکن راستے میں جب کسی جگہ ٹھیریں گے تو اپنی اس حماقت پر نادم ہوں گے اور دوبارہ مدینے پر چڑھ آئیں گے۔ اس بناء پر آپ نے ان کے تعاقب کا فیصلہ کیا اور فوراً ۳۶۰ جاں نثار آپؐ کے ساتھ چلنے کے لیے تیار ہو گئے۔ مکّہ کے راستے میں جب حَمْرأالاسد پہنچ کر آپؐ نے تین روز تک پڑاؤ کیا تو ایک ہمدرد غیر مسلم کے ذریعہ سے آپ کو معلوم ہو گیا کہ ابو سُفْیان اپنے ۲۹۷۸ آدمیوں کے ساتھ مدینے سے ۳۶ میل دور الرَّوحأ کے مقام پر ٹھیرا ہوا تھا اور یہ لوگ فی الواقع اپنی غلطی کو محسوس کر کے پھ پلٹ آنا چاہتے تھے ، لیکن یہ سُن کر ان کی ہمت ٹوٹ گئی کہ رسول اللہ صلی اللہ علیہ و سلم ایک لشکر لیے ہوئے ان کے تعاقب میں چلے آ رہے ہیں۔اس اس کارروائی کا صرف یہی فائدہ نہیں ہوا کہ قریش کے بڑھے ہوئے حوصلے پست ہو گئے ، بلکہ گرد و پیش کے دشمنوں کو بھی یہ معلوم ہو گیا کہ مسلمانوں کی قیادت ایک انتہائی بیدار مغز اور اولوالعزم ہستی کر رہی ہے اور مسلمان اس کے اشارے پر کٹ مرنے کے لیے ہر وقت تیا رہیں۔(مزید تفصیل کے لیے ملاحظہ ہو تفہیم القرآن جلد اوّل ، صفحات ۲۲۹۔۲۳۰۔۳۰۳) پھر جوں ہی بنی اسد نے مدینے پر چھاپہ مارنے کی تیاریاں شروع کیں ، حضورؐ کے مخبروں نے بروقت آپ کو اُن کے اِرادوں سے با خبر کر دیا۔قبل اس کے کہ وہ چڑھ آتے ، آپ نے حضرت ابو سَلَمہ (اُمّ المومنین حضرت اُم سلَمہؓ کے پہلے شوہر)کی قیادت میں ڈیڑھ سو آدمیوں کا ایک لشکر ان کی سرکوبی کے لیے بھیج دیا۔ یہ فوج اچانک ان کے سَر پر پہنچ گئی۔ بدحواسی کے عالم میں وہ اپنا سب کچھ چھوڑ کر بھاگ نکلے اور ان کا سارا مال اسباب مسلمانوں کے ہاتھ لگ گیا۔ اس کے بعد بنی النَّضیر کی باری آئی۔ جس روز انہوں نے نبی صلی اللہ علیہ و سلم کو شہید کرنے کی سازش کی اور اس کا راز فاش ہوا اُسی روز آپؐ نے ان کو نوٹس دے دیا کہ دس دن کے اندر مدینے سے نِکل جاؤ ، اس کے بعد تم میں سے جو یہاں پایا جائے گا قتل کر دیا جائے گا۔ منافقین مدینہ کے سردار عبداللہ بن اُبیّ نے ان کو تَڑی دی کہ ڈٹ جاؤ اور مدینہ چھوڑنے سے انکار کر دو ، میں دو ہزار آدمیوں کے ساتھ تمہاری مدد کروں گا، بنی قُرَیظہ تمہاری مدد کریں گے اور نجد سے بنی غَطَفان بھی تمہاری مدد کے لیے آئیں گے۔ ان باتوں میں آ کر اُنہوں نے حضورؐ کو کہلا بھیجا کہ ہم اپنا علاقہ نہیں چھوڑیں گے ، آپ سے جو کچھ کر لیجئے ،حضورؐ نے نوٹس کی میعاد ختم ہوتے ہی ان کا محاصرہ کر لیا اور ان کے حامیوں میں سے کسی کی یہ ہمت نہ پڑی کہ مدد کو آتا۔ آخر کار انہوں نے اس شرط پر ہتھیار ڈال دئے کہ ان میں سے ہر تین آدمی ایک اونٹ پر جو کچھ لاد کر لے جا سکتے ہیں لے جائیں اور باقی سب کچھ مدینہ ہی میں چھوڑ جائیں گے۔ اس طرح مضافات مدینہ کا وہ پورا محلہ جس میں بنی نَضیر رہتے تھے ، ان کے باغات اور گَڑھیوں اور سر و سامان سمیت مسلمانوں کے ہاتھ آگیا اور اس بد عہد قبیلے کے لو گ خیبر ، وادی القُریٰ اور شام میں تتّر بتّر ہو گئے۔ پھر آپؐ نے بنی غَطَفان کی طرف توجہ کی جو مدینے پر حملہ آور ہونے کے لیے پر تول رہے تھے۔ آپ چار سو کا لشکر لے کر نکلے اور ذات الرِّقاع کے مقام پر اس کو جالیا۔ اس اچانک حملے نے ان کے حواس باختہ کر دیے اور کسی جنگ کے بغیر وہ اپنے گھر بار اور مال اسباب چھوڑ کر پہاڑوں میں منتشر ہو گئے۔ اس کے بعد شعبان ۴ ہجری میں آپ ابو سفیان کے اُس چیلنج کا جواب دینے کے لیے نکلے جو اس نے اُحد سے پلٹتے ہوئے دیا تھا۔ خاتمۂ جنگ پر اُس نے نبی صلی اللہ علیہ و سلم اور مسلمانوں کی طرف رُخ کر کے اعلان کیا تھا کہ ان موعد کم بد رللعام المقبل(آئندہ سال بدع کے مقام پر ہمارا تمہارا پھر مقابلہ ہو گا)اور حضورؐ نے جواب میں ایک صحابی کے ذریعہ سے یہ اعلان کرا دیا تھا کہ نعم ، ھی بیننا وبینیک موعد(ٹھیک ہے ، یہ بات ہمارے اور تیرے درمیان طے ہو گئی )۔ اس قرار داد کے مطابق طے شدہ وقت پر آپ ۱۵ سو صحابیوں کو لے کر بدر کے مقام پر پہنچ گئے۔ اُدھر سے ابو سُفیان دو ہزار کا لشکر لے کر چلا مگر مُرَّالظَّہران (موجودہ وادیِ فاطمہ ) سے آگے بڑھنے کی ہمّت نہ کر سکا۔ حضورؐ نے بدر میں آٹھ دن اس کا انتظار کیا اور اس دوران میں مسلمان تجارت کر کے ایک درہم کے دو درہم کماتے رہے۔ اس واقعہ سے وہ دھاک جو اُحُد میں اُکھڑی تھی پہلے سے زیادہ جم گئی۔اس نے پورے عرب پر یہ بات کھول دی کہ اب تنہا قریش محمد صلی اللہ علیہ و سلم کے مقابلے کی طاقت نہیں رکھتے۔(اس کی مزید تفصیل کے لیے ملاحظہ ہو تفہیم القرآن ،جلد اوّل ،ص ۳۰۴) اس دھاک میں ایک اور واقعہ نے مزید اضافہ کیا۔ عرب اور شام کی سرحد پر دُدمتہ الجَنْدَل (موجودہ الجَوف )ایک اہم مقام تھا جہاں اے عراق اور مصر و شام کے درمیان عرب کے تجارتی قافلے گزرتے تھے۔ اس مقام کے لوگ قافلوں کو تنگ کرتے اور اکثر لوٹ لیتے تھے۔ نبی صلّی اللہ علیہ و سلم ربیع الاوّل ۵ ھ میں ایک ہزار کا لشکر لے کر ان کی تادیب کے لیے خود تشریف لے گئے۔وہ آپؐ کے مقابلے کی ہمت نہ کر سکے اور بستی چھوڑ کر بھاگ نکلے۔اس سے پورے شمالی عرب پر اسلام کی ہیبت بیٹھ گئی اور قبائل نے یہ سمجھ لیا کہ مدینے میں جو زبردست طاقت پیدا ہوئی ہے اس کا مقابلہ اب ایک دو قبیلوں کے بس کا کام نہیں ہے۔ یہ حالات تھے جن میں غزوۂ احزاب پیش آیا ، یہ غزوہ دراصل عرب کے بہت سے قبائل کا ایک مشترک حملہ تھا جو مدینے کی اس طاقت کو کچل دینے کے لئے کیا گیا تھا۔۔۔اس کے تحریک بنی النَّضِیر کے اُن لیڈروں نے کے تھی جو جلا وطن ہو کر خیبر میں مقیم ہو گئے تھے۔انہوں نے دَورہ کر کے قریش اور غَلْطفان اور ہُذَیل اور دوسرے بہت سے قبائل کو اس بات پر آمادہ کیا کہ اب مِل کر بہت بڑی جمیعت کے ساتھ مدینے پر ٹوٹ پڑیں۔ چنانچہ ان کی کوششوں اے شوال ۵ ھ میں قبائل عرب کی اتنی بڑی جمیعت اس چھوٹی سی بستی پر حملہ آور ہو گئی جو اس سے پہلے عرب میں کبھی جمع نہ ہوئی تھی۔ اس میں شمال کی طرف بنی النَّضِیر اور قَیْنقُاع کے وہ یہودی آئے جو مدینے سی جلا وطن ہو کر خیبر اور وادی القُریٰ میں آباد ہوئے تھے۔ مشرق کی طرف سے غَطَفان کے قبائل(بنو سُلَیم،فَزارہ،مُرَّہ،اَشجع،سَعد اورا َسَد وغیرہ)نے پیش قدمی کی۔ اور جنوب کی طرف سے قریش اپنے حلیفوں کی ایک بھاری جمیعت لے کر آگے بڑھے۔ مجموعی طور پر ان کی تعداد دس بارہ ہزار تھی۔ یہ حملہ اگر اچانک ہوتا تو سخت تباہ کُن ہوتا۔لیکن نبی صلی اللہ علیہ و سلم مدینہ طیّبہ میں بے خبر بیٹھے ہوئے نہ تھے بالکہ آپ کے خبر رساں اور تحریک اسلامی کے ہمدرد اور متاثرین جو تمام قبائل میں موجود تھے ، آپ کو دشمنوں کی نقل و حرکت سے برابر مطلع کرتے رہتے تھے (یہ قوم پرست جتھوں کے مقابلے میں ایک نظریاتی تحریک کی فوقیت کا ایک اہم سبب ہوتا ہے۔ قوم پرست جتھے صرف اپنی قوم کے افراد کی تائید و حمایت ہی پر انحصار رکھتے ہیں۔ لیکن ایک اُصولی و نظریاتی تحریک اپنی دعوت سے ہر سمت میں بڑھتی ہے اور خود ان جتھوں کے اندر سے اپنے حامی نکال لاتی ہے۔ )قبل اس کے کہ یہ جمِّ غفیر آپ کے شہر پہنچتا،آپ نے چھ دن کے اندر مدینہ کے شمال غربی رُخ پر ایک خندق کھُدوا لی اور کوہ سَلْع کو پشت پر لے کر تین ہزار فوج کے ساتھ خندق کی پناہ میں مدافعت کے لیے تیار ہو گئے۔ مدینہ کے جنوب میں باغات اس کثرت سے تھے (اور اب بھی ہیں ) کہ اس جانب سے کوئی حملہ اس پر نہ ہو سکتا تھا۔ مشرق میں حَرّات (لادے کی چٹانیں ) ہیں جن پر سے کوئی اجتماعی فوج کشی آسانی کے ساتھ نہیں ہو سکتی۔ یہی کیفیت مغربی جنوبی گوشے کی بھی ہے۔ اس لیے حملہ صرف اُحُد کے مشرقی اور مغربی گوشوں سے ہو سکتا تھا اور اسی جانب حضورؐ نے خندق کھدوا کر شہر کو محفوظ کر لیا تھا۔ یہ چیز سرے سے کفار کے جنگی نقشے میں تھی ہی نہیں کہ انہیں مدینے کے باہر خندق سے سابقہ پیش آئے گا، کیونکہ اہل عرب اس طریقِ دفاع سے نا آشنا تھے۔ نا چار انہیں جاڑے کے زمانے میں ایک طویل محاصرے کے لیے تیار ہونا پڑا جس کے لیے وہ گھروں سے تیار ہو کر نہیں آئے تھے۔ اس کے بعد کفار کے لیے صرف ایک ہی تدبیر باقی رہ گئی تھی ، اور وہ یہ کہ بنی قُر یظہ کے یہودی قبیلے کو غدّاری پر آمادہ کریں جو مدینہ طیّبہ کے جنوب مشرقی گوشے میں رہتا تھا۔ چوں کہ اس قبیلے سی مسلمانوں کا باقاعدہ حلیفانہ معاہدہ تھا جس کی رو سے مدینہ پر حملہ ہونے کی صورت میں وہ مسلمانوں کے ساتھ مل کر مدافعت کرنے کا پابند تھا ، اس لیے مسلمانوں نے اس طرف سی بے فکر ہو کر اپنے بال بچّے اُن گڑھیوں میں بھجو دیے تھے جو بن قریظہ کی جانب تھیں اور اُدھر مدافعت کا کوئی انتظام نہ کیا تھا۔ کفار نے اسلامی دفاع کے اس کمزور پہلو کو بھانپ لیا۔اُن کی طرف سے بنی النَّضیر کا یہودی سردار حُیّی بن اخْطَب بنی قُریظہ کے پاس بھیجا گیا تاکہ انہیں معاہدہ توڑ کر جنگ میں شامل ہونے پر آمادہ کرے۔ ابتداءً اُنہوں نے اس سے انکار کیا اور صاف صاف کہہ دیا کہ ہمارا محمد(صلی اللہ علیہ و سلم )سے معاہدہ ہے اور آج تک کبھی ہمیں ان سی کوئی شکایت پیدا نہیں ہوئی ہے۔ لیکن جب ابنِ اخطب نے ان سے کہا کہ ’’دیکھو ، میں اس وقت عرب کی متحدہ طاقت اس شخص پر چڑھا لایا ہوں ، یہ اسے ختم کر دینے کا نادر موقع ہے ، اس کو اگر تم نے کھو دیا تو پھر دوسرا کوئی موقع نہ مل سکے گا‘‘،تو ذہن کی اسلام دشمنی اخلاق کے پاس و لحاظ پر غالب آ گئی اور بنی قریظہ عہد توڑنے پر آمادہ ہو گئے۔ نبی صلی اللہ علیہ و سلم اس معاملے سے بھی بے خبر نہ تھے۔ آپؐ کو بر وقت اس کی اطلاع مل گئی اور آپؐ نے فوراً انصار کے سرداروں (سعد بن غُبّادہ،سعد بن مُعاذ ،عبداللہ بن رواعہ اور خَوّات بن جُبیر )کو ان کے پاس تحقیقِ حال اور فہمائش کے لیے بھیجا۔ چلتے وقت آپؐ نے اُن کو ہدایت فرمائی کہ اگر بنی قُریظہ عہد پر قائم رہیں تو آ کر سارے لشکر کے سامنے علی ا لاعلان یہ خبر سُنا دینا۔ لیکن اگر وہ نقضِ عہد پر مُصر ہوں صرف مجھ کو اشارۃً اس کی اطلاع دے دینا تاکہ عام مسلمان یہ بات سُن کر پست ہمّت نہ ہو جائیں۔ یہ حضرات وہاں پہنچے تو بنی قریظہ کو پوری خباثت پر آمادہ پایا اور انہوں نے برملا ان سی کہہ دیا کہ لا عقد بیننا وبین محمد ولا عھد۔ ’’ہمارے محمدؐ کے درمیان کوئی عہد پیما نہیں ہے ‘‘،۔اس جواب کو سن کر وہ لشکرِ اسلام میں واپس آئے اور اشارۃً حضورؐ سے عرض کر دیا :عَضَل وقارَہ۔ یعنی قبیلہ عَضَل وقارَہ نے رَجیع کے مقام پر مبلّغین اسلام کے وفد سے جو غدّاری کی تھی ، وہی کچھ اب بنی قریظہ کر رہے ہیں۔ یہ خبر بہت جلد ی مدینہ کے مسلمانوں میں پھیل گئی اور ان کے اندر اس سے سخت اضطراب پیدا ہو گیا۔ کیوں کہ اب وہ دونوں طرف سے گھیرے میں آ گئے تھے اور ان کے شہر کا وہ حصّہ خطرے میں پڑ گیا تھا جدھر دفاع کا بھی کوئی انتظام نہ تھا اور سب کے بال بچّے بھی اسی جانب تھے۔ اس پر منافقین کی سرگرمیاں اور تیز ہو گئیں اور انہوں نے اہل ایمان کے حوصلے پست کرنے کے لیے طرح طرح کے نفسیاتی حملے شروع کر دیے۔ کسی نے کہا کہ ’’ ہم سے تو وعدے تو قیصر و کسریٰ کے ملک فتح ہو جانے کے کیے جا رہے تھے ،اور حال یہ ہے کہ ہم رفع حاجت کے لیے بھی نہیں نکل سکتے۔‘‘ کسی نے یہ کہہ کر خندق کے محاذ سے رخصت مانگی کہ اب تو ہمارے گھر ہی خطرے میں پڑ گئے ہیں ہمیں جا کر اُن کی حفاظت کرنی ہے۔ کسی نے یہاں تک خفیہ پروپگنڈا شروع کر دیا کہ حملہ آوروں سے اپنا معاملہ درست کر لو اور محمد صلی اللہ علیہ و سلم کو ان کے حوالے کر دو۔ یہ ایسی شدید آزمائش کا وقت تھا جس میں ہر اس شخص کا پردہ فاش ہو گیا جس کے دِل میں ذرّہ برابر بھی نفاق موجود تھا۔ صرف صادق و مخلص اہل ایمان ہی تھے جو اس کڑے وقت میں بھی فدا کاری کے عزم پر ثابت قدم رہے۔ نبی صلی اللہ علیہ و سلم نے اس نازک موقع پر بنی غطفان سے صلح کی بات چیت شروع کی اور ان کو اس بات پر آمادہ کرنا چاہا کہ مدینے کے پھلوں کی پیداوار کا تیسرا حصّہ لے کر واپس چلے جائیں۔ لیکن جب انصار کے سرداروں (سعدؓبن عُبادہ اور سعدؓ بن مُعاذ) سے آپؐ نے اِن شرائط صلح کے متعلق مشورہ طلب کیا تو انہوں نے عرض کیا ’’ یا رسول اللہ ، یہ آپؐ کی خواہش ہے کہ ہم ایسا کریں ؟ یا یہ اللہ کا حکم ہے کہ ہمارے لیے اسے قبول کرنے کے سوا چارہ نہیں ہے ؟ یا آپؐ ہمیں بچانے کے لیے یہ تجویز فرما رہے ہیں ؟ آپؐ نے جواب دیا ’’ میں صرف تم لوگوں کو بچانے کے لیے ایسا کر رہا ہوں ، کیوں کہ میں دیکھ رہا ہوں کہ سارا عرب متحد ہو کر تم پر پِل پڑا ہے ، میں چاہتا ہوں کہ ان کو ایک دوسرے سے توڑ دوں۔‘‘اس پر دونوں سرداروں نے بالاتفاق کہا کہ ’’ اگر آپؐ ہماری خاطر یہ معاہدہ کر رہے ہیں تو اسے ختم کر دیجیے۔ یہ قبیلے ہم سے اُس وقت بھی ایک حبّہ خراج کے طور پر کبھی نہ لے سکے تھے جب ہم مشرک تھے۔ اور اب تو اللہ اور اس کے رسول پر ایمان لانے کا شرف ہمیں حاصل ہے۔ کیا اب یہ ہم سے خراج لیں گے ؟ ہمارے اور ان کے درمیان اب صرف تلوار ہی ہے ،یہاں تک کہ اللہ ہمارا اور ان کا فیصلہ کر دے ‘‘۔ یہ کہہ کر انہوں نے معاہدے کے اس مسودے کو چاک کر دیا جس پر ابھی دستخط نہ ہوئے تھے۔ اسی دوران میں قبیلۂ غَطَفا ن کی شاخ اشجَع کے ایک صاحب نَعیم بن مسعُود مسلمان ہو کر حضورؐ کے پاس حاضر ہوئے اور عرض کیا کہ ابھی تک کسی کو بھی میرے قبول اسلام کا علم نہیں ہے ، آپؐ مجھ سے اس وقت جو خدمت لینا چاہیں میں اسے انجام دے سکتا ہوں۔ حضورؐ نے فرمایا ، تم جا کر دشمنوں میں پھوٹ ڈالنے کی کوئی تدبیر کرو(اسی موقعہ پر حضورؐ نے فرمایا تھا اَلْحَرْبُ خُدْ عَۃ۔یعنی جنگ میں دھوکہ دینا جائز ہے۔)چنانچہ وہ پہلے بنی قُریظہ کے پاس گئے جن سے ان کا بہت میل جول تھا ،اور ان سے کہا کہ قریش اور غطفان تو محاصرے سے تنگ آ کر واپس بھی جا سکتے ہیں ، ان کا کچھ نہ بگڑے گا ، مگر تمہیں مسلمانوں کے ساتھ اسی جگہ رہنا ہے ، وہ لوگ اگر چلے گئے تو تمہارا کیا بنے گا۔ میری رائے یہ ہے کہ تم اس وقت تک جنگ میں حصّہ نہ لو جب تک اِن باہر سے آئے ہوئے قبائل کے چند نمایاں آدمی تمہارے پاس یرغمال کے طور پر نہ بھیج دیے جائیں۔ یہ بات بنی قریظہ کے دِل میں اُتر گئی اور انہوں نے متحدہ محاذ کے قبائل سے یرغمال طلب کرنے کا فیصلہ کر لیا۔ پھر یہ صاحب قریش اور غطفان کے سرداروں کے پاس گئے اور ان سے کہا کہ بنی قریظہ کچھ ڈھیلے پڑتے نظر آ رہے ہیں ،بعید نہیں کہ وہ تم سے یرغمال کے طور پر کچھ آدمی مانگیں اور انہیں محمد صلی اللہ علیہ و سلم کے حوالے کر کے اپنا معاملہ صاف کر لیں۔ اس لیے ذرا ان کے ساتھ ہوشیاری سے معاملہ کرنا۔اس سے متحدہ محاذ کے لیڈر بنی قُریظہ کی طرف سے کھٹک گئے اور انہوں نے قُرظی سرداروں کو پیغام بھیجا کہ اس طویل محاصرے سے اب ہم تنگ آ گئے ہیں ، اب ایک فیصلہ کن لڑائی ہو جانی چاہیے ، کل تم اُدھر سے حملہ کرو اور ہم اِدھر سی یکبارگی مسلمانوں پر ٹوٹ پڑتے ہیں۔ بنی قُریظہ نے جواب میں کہلا بھیجا کہ آپ لوگ اپنے چند نمایاں آدمی یرغمال کے طور پر ہمارے حوالہ نہ کر دیں ، ہم جنگ کا خطرہ مول نہیں لے سکتے۔ اس جواب سے متحدہ محاذ کے لیڈروں کو یقین آگیا کہ نعیم کی بات سچی تھی۔ انہوں نے یرغمال دینے سے انکار کر دیا اور اس سے بنی قُریظہ نے سمجھ لیا کہ نعیم نے ہم ٹھیک مشورہ دیا تھا۔ اِس طرح یہ جنگی چال بہت کامیاب ثابت ہوئی اور اس نے دشمنوں کے کیمپ میں پھوٹ ڈال دی۔ اب محاصرہ پچیس دن سے زیادہ طویل ہو چکا تھا۔ سردی کا زمانا تھا۔ اتنے بڑے لشکر کے لیے پانی اور غذا اور چارے کی فراہمی بھی مشکل تر ہوتی چلی جا رہی تھی۔ اور پھوٹ پڑ جانے سی بھی محاصرین کے حوصلے پست ہو چکے تھے۔ اس حالت میں یکایک ایک رات سخت آندھی کی زور سے دشمنوں کے خیمے الٹ گئے اور ان کے اندر شدید افراتفری برپا ہو گئی۔ قدرت خداوندی کا یہ کاری وار وہ نہ سہہ سکے۔ راتوں رات ہر ایک نے اپنے گھر کی راہ لی اور صبح جب مسلمان اٹھے تو میدان میں ایک دشمن بھی موجود نہ تھا۔ نبی صلی اللہ علیہ و سلم نے میدان کو دشمنوں سے خالی دیکھ کر فوراً ارشاد فرمایا: لن تغزوکم قریش بعد عا مکم ھٰذا ولکنکم تغز ونھم۔ یعنی ’’اب قریش کے لوگ تم پر کبھی چڑھائی نہ کر سکیں گے۔ اب تم ان پر چڑھائی کرو گے ‘‘۔ یہ حالات کے بالکل صحیح اندازہ تھا۔ قریش ہی نہیں ، سارے دشمن قبائل متحدہ ہو کر اسلام کے خلاف اپنا آخری داؤ چل چکے تھے۔ اس میں ہار جانے کے بعد اب ان میں یہ ہمت ہی باقی نہ رہی تھی کہ مدینے پر حملہ آور ہونے کی جرأت کر سکتے۔ اب حملے ( offensive ) کی قوت دشمنوں سے مسلمانوں کی طرف منتقل ہو چکی تھی۔ خندق سے پلٹ کر جب حضورؐ گھر پہنچے تو ظہر کے وقت جبرئیلؑ نے آ کر حکم سنایا کہ ابھی ہتھیار نہ کھولے جائیں ، بنی قریظہ کا معاملہ باقی ہے ، ان سے بھی اسی وقت نمٹ لینا چاہئے۔ یہ حکم پاتے ہیں حضورؐ نے فوراً اعلان فرمایا کہ ’’جو کوئی سمع و طاعت پر قائم ہو وہ عصر کی نماز اس وقت تک نہ پڑھے جب تک دیار بنی قریظہ پر نہ پہنچ جائے ‘‘۔ اس اعلان کے ساتھ ہی آپؐ نے حضرت علیؓ کو ایک دستے کے ساتھ مقدمۃ الجیش کے طور پر بنی قریظہ کی طرف روانہ کر دیا۔ وہ جب وہاں پہنچے تو یہودیوں نے کوٹھوں پر چڑھ کر نبی صلى الله عليه وسلم اور مسلمانوں پر گالیوں کی بوچھار کر دی، لیکن یہ بدزبانی ان کو اس جرم عظیم کے خمیازے سے کیسے بچا سکتی تھی کہ انہوں نے عین لڑائی کے وقت معاہدہ توڑ ڈالا اور حملہ آوروں سے مل کر مدینے کی پوری آبادی کو ہلاکت کے خطرے میں مبتلا کر دیا۔ حضرت علیؓ کے دستے کو دیکھ کر وہ سمجھے تھے کہ یہ محض دھمکانے آئے ہیں۔ لیکن جب حضورؐ کی قیادت میں پورا اسلامی لشکر وہاں پہنچ گیا اور ان کی بستی کا محاصرہ کر لیا گیا تو ان کے ہاتھوں کے طوطے اُڑ گئے۔ محاصرہ کی شدت کو وہ دو تین ہفتوں سے زیادہ برداشت نہ کر سکے اور آخر کار انہوں نے اس شرط پر اپنے آپ کو نبیصلى الله عليه وسلم کے حوالے کر دیا کہ وہ قبلہ اَوس کے سردار حضرت سعد بن معاذ رضی اللہ عنہ ان کے حق میں جو فیصلہ بھی کر دیں گے اس فریقین مان لیں گے۔ اُنہوں نے حضرت سعد بن مُعاذ رضی اللہ عنہ ان کے حق میں جو فیصلہ بھی کر دیں گے اسے فریقین مان لیں گے۔ انہوں نے حضرت سعدؓ کو اس امید پر حُکم بنایا تھا کہ زمانۂ جاہلیت میں اَوس اور بنی قریظہ کے درمیان جو حلیفانہ تعلقات مدّتوں سے چلے آ رہے تھے وہ ان کا لحاظ کریں گے اور اُنہیں بھی اسی طرح مدینہ سے نِکل جانے دیں گے جس طرح پہلے بنی قَیْنقُا ع اور بنی النضیر کو نِکل جانے دیا گیا تھا۔ خود قبیلۂ اَوس کے لوگ بھی حضرت سعد ؓ سے تقاضا کر رہے تھے کہ اپنے حلیفوں کے ساتھ نرمی برتیں۔ لیکن حضرت سعدؓ ابھی بھی دیکھ چکے تھے کہ پہلے جن دو یہودی قبیلوں کو مدینہ سے نِکل جانے کا موقع دیا تھا وہ کس طرح ساری گرد و پیش کے قبائل کو بھڑکا کر مدینے پر دس بارہ ہزار کا لشکر چڑھا لائے تھے۔ اور یہ معاملہ بھی ان کی سامنے تھا کہ اس آخری یہودی قبیلے نی عین بیرونی حملے کے موقع پر بد عہدی کر کے اہل مدینہ کو تباہ کر دینے کا کیا سامان کیا تھا۔ اس لیے انہوں نے فیصلہ دیا کہ بنی قریظہ کے تمام مرد قتل کر دیے جائیں ، عورتوں اور بچوں کو غلام بنا لیا جائے ، اور ان کی تمام املاک مسلمانوں میں تقسیم کر دی جائیں۔ اس فیصلے پر عمل کیا گیا اور جب بنی قریظہ کی گڑھیوں میں مسلمان داخل ہوئے تو انہیں پتہ چلا کہ جنگِ احزاب میں حصہ لینے کے لیے ان غدّاروں نے پندرہ سو تلواریں ، تین سو زرہیں ، دو ہزار نیزے اور پندرہ سو ڈھالیں فراہم کی تھیں۔ اگر اللہ کی تائید مسلمانوں کے شامل حال نہ ہوتی تو یہ سارا جنگی سامان عین وقت مدینہ پر عقب سے حملہ کرنی کے لیے استعمال ہوتا جبکہ مشرکین یکبارگی خندق پار کر کے ٹوٹ پڑنے کی تیاریاں کر رہے تھے۔ س انکشاف کے بعد تو اس امر میں شک کرنے کی کوئی گنجائش ہی نہ رہی کہ حضرت سعدؓ نے ان لوگوں کے معاملہ میں جو فیصلہ دیا وہ بالکل حق تھا۔ معاشرتی اصلاحات : جنگ اُحُد اور جنگ احزاب کے درمیان ، دو سال کا یہ زمانہ اگر چہ ایسے ہنگاموں کا زمانہ تھا جن کی بدولت نبی صلی اللہ علیہ و سلم اور آپؐ کے اصحاب کو ایک دن کی لیے بھی امن اور اطمینان نصیب نہ ہوا۔ لیکن اس پوری مدّت میں نئے مسلم معاشرے کی تعمیر ، اور ہر پہلو میں زندگی کی اصلاح کا کا م برابر جاری رہا یہی زمانہ تھا جس میں مسلمانوں قوانین نکاح و طلاق قریب قریب مکمل ہو گئے اور وراثت کا قانون بنا ، شراب اور جوئے کو حرام کیا گیا ، اور معیشت و معاشرت کے دوسرے بہت سے پہلوؤں میں نئے ضابطے نافذ کئے گئے۔ اس سلسلے کا ایک اہم مسئلہ جو اصلاح کا تقاضا کر رہا تھا تَبْنِیتْ(گود لینے یا بیٹا بنانے ) کا مسئلہ تھا۔ عرب کی لوگ جس بچے کو متبنیٰ بنا لیتے تھے وہ بالکل اُن کی حقیقی اولاد کی طرح سمجھا جاتا تھا۔ اسے وراثت ملتی تھی۔ اس سی منہ بولی ماں اور منہ بولی بہنیں وہی خلا ملا رکھتی تھیں جو حقیقی بیٹے اور بھائی سے رکھا جاتا ہے۔ اس کے ساتھ منہ بولے باپ کے مر جانے کے بعد اس کی بیوہ کا نکاح اسی طرح ناجائز سمجھا جاتا تھا جس طرح سگی بہن اور حقیقی ماں کے ساتھ کسے کا نکاح حرام ہوتا ہے۔ اور یہی معاملہ اس صورت میں بھی کیا جاتا تھا جب منہ بولا بیٹا مر جائے یا اپنی بیوی کو طلاق دے دے۔ منہ بولے باپ کے لیے وہ عورت سگی بہو کی طرح سمجھی جاتی تھی۔ یہ رسم قدم قدم پر نکاح اور طلاق اور وراثت کے اُن قوانین سے ٹکراتی تھی جو اللہ تعالیٰ نے سورۂ بقرہ اور سورۂ نساء میں مقرر فرمائے تھے اُن کی رُو سے جو لوگ حقیقت میں وراثت کے حق دار تھے یہ رسم ان کا حق مار کر ایک ایسے شخص دلواتی تھی جو سرے سے کوئی حق نہ رکھتا تھا۔ اُن کی رُو سے جن عورتوں اور مردوں کے درمیان رشتۂ نکاح حلال تھا، یہ رسم ان کے باہمی نکاح کو حرام کرتی تھی۔ اور سب سے زیادہ یہ کہ اسلامی قانون جن بد اخلاقیوں کا سدِّ باب کرنا چاہتا تھا ، یہ رسم ان کے پھیلنے میں مدد گار کیوں کہ رسم کے طور پر منہ بولے رشتے میں خواہ کتنا ہی تقدُّس پیدا کر دیا جائے ، بہر حال منہ بولی ماں ، منہ بولی بہن اور منہ بولی بیٹی حقیقی ماں اور بیٹی کی طرح نہیں ہو سکتی۔ ان مصنوعی رشتوں کے رسمی تقدّس پر بھروسہ کر کے مردوں اور عورتوں کے درمیان جب حقیقی رشتہ داروں کا سا خلا ملا ہو تو وہ بُرے نتائج پیدا کیے بغیر نہیں رہ سکتا۔ ان وجوہ اے اسلامی قانونِ نِکاح و طلاق ، قانونِ وراثت اور قانونِ حرمتِ زنا کا یہ تقاضا تھا کہ متبنّیٰ کو حقیقی اولاد کی طرح سمجھنے کے تخیّل کا قطعی استیصال کر دیا جائے۔ لیکن یہ تخیّل محض ایک قانونی حکم کے طور پر اتنی سی بات کر دینے سے ختم نہیں ہو سکتا تھا کہ۔۔’’ منہ بولا رشتہ کوئی حقیقی رشتہ نہیں ہے۔‘‘صدیوں کے جمے ہوئے تعصبّات اور اوہام محض اقوال سے نہیں بدل جاتے۔ حکماً لوگ اس بات کو مان بھی لیتے کہ یہ رشتے حقیقی رشتے نہیں ہیں ، پھر بھی منہ بولی ماں اور منہ بولے بیٹے کے درمیان منہ بولے بھائی اور بہن کے درمیان ، منہ بولے باپ اور بیٹی کے درمیان ، منہ بولے خسر اور بہو کے درمیان نکاح کو لوگ مکروہ سمجھتے رہتے۔ نیز ان کے درمیان خلا ملا بھی کچھ نہ کچھ باقی رہ جاتا۔ اس لیے ناگزیر تھا کہ یہ رسم عملاً توڑی جائے ، اور خود رسول صلی اللہ علیہ و سلم بنفسِ نفیس اس کو توڑیں۔ کیوں کہ جو کام حضورؐ نے خود کیا ہو، اور اللہ کے حکم سے کیا ہو، اس کے متعلق کسی مسلمان کے ذہن میں کراہت کا تصوّر باقی نہ رہ سکتا تھا۔ اسی بنا پر جنگِ احزاب سے کچھ پہلے نبی صلی اللہ علیہ و سلم کو اللہ تعالیٰ کی طرف سے اشارہ کیا گیا کہ آپ اپنے منہ بولے بیٹے زید بن حارِثہ رضی اللہ عنہ کی مطلقہ بیوی سے خود نکاح کر لیں ، اور اس حکم کی تعمیل آپ نے محاصرۂ بنی قریظہ کے زمانی میں فرمائی۔ (غالباً تا خیر کی وجہ یہ تھی کہ عدّت ختم ہونے کا انتظار تھا، اور اسی دَوران میں جنگی مصروفیات پیش آگئی تھیں )۔یہ کام ہونا تھا کہ حضورؐ کے خِلاف پروپیگنڈے کا ایک طوفان یکلخت اُٹھ کھڑا ہوا۔ مشرکین اور منافقین اور یہود سب آپ کی پے درپے کامیابیوں سے جلے بیٹھے تھے۔ اُحُد کے بعد احزاب اور نبی قریظہ تک دو سال کی مدّت میں جس طرح وہ زک پر زک اُٹھا تے چلے گئے تھے اس کی وجہ ان کے دلوں میں آگ لگ رہی تھی۔ وہ اس بات سے بھی مایوس ہو چکے تھے کہ اب وہ کھلے میدان میں لڑ کر کبھی آپ کو زیر کر سکیں گے۔ اس لیے انہوں نے اس نکاح کے معاملے کو اپنے لیے ایک خدا داد موقع سمجھا اور خیال کیا کہ اب ہم محمد (صلی اللہ علیہ و سلم ) کی اُس اخلاقی برتری کو ختم کر سکیں گے جو اُن کی طاقت اور اُن کی کامیابیوں کا اصل راز ہے چنانچہ یہ افسانے تراشے کہ (معاذاللہ) محمد صلی اللہ علیہ و سلم بہو کو دیکھ کر عاشق ہو گئے تھے ، بیٹے کو اس تعلقِ خاطر کا علم ہو گیا ، اس نے بیوی کو طلاق دے دی ، اور باپ نے اس کے بہو سے بیَاہ رچا لیا۔ حالاں کہ یہ بات صریحاً لغو تھی۔ حضرت زینبؓ حضورؐ کی پھوپھی زاد بہن تھیں۔ بچپن سے جوانی تک اُن کی ساری عمر آپ کے سامنے گزری تھی۔ کسی وقت ان کو دیکھ کر عاشق ہو جانے کا سوال ہی کہاں پیدا ہوتا تھا۔ پھر آپؐ نے خود اصرار کر کے حضرت زیدؓ سے ان کا نکاح کرایا تھا۔ ان کا سارا خاندان اس پر راضی نہ تھا کہ قریش کے اتنے اُونچے گھرانے کی لڑکی ایک آزاد کردہ غلام سے بیاہی جائے۔ خود حضرت زینب ؓ بھی اس رشتے سے ناخوش تھیں۔ مگر حضورؐ کے حکم سے سب مجبور ہو گئے ، اور حضرت زیدؓ کے ساتھ ان کی شادی کر کے عرب میں اس امر کی پہلی مثال پیش کر دی گئی کہ اسلام ایک آزاد کردہ غلام کو اُٹھا کر شرفائے قریش کے برابر لے آیا ہے۔ اگر فی الواقع حضورؐ کا کوئی میلان حضرت زینبؓ کی جانب ہوتا تو زید بن حارِثہ سے ان کا نِکاح کرنے کی ضرورت ہی کیا تھی، آپ خود ان سے نِکاح کر سکتے تھے۔ لیکن بے حیا مخالفین نے ان سارے حقائق کے مو جود ہوتے یہ عشق کے افسانے تصنیف کیے ، خوب نمک مرچ لگا لگا کر ان کو پھیلایا اور اس پروپیگنڈے کا صور اس زور سے پھونکا کہ خود مسلمانوں کے اندر بھی ان کی گھڑی ہوئی روایات پھیل گئیں۔ یہ بات کہ دشمنوں کے تصنیف کیے ہوئے یہ افسانے مسلمانوں کی زبانوں پر چڑھنے سے بھی نہ رُکے اس امر کی کھلی ہوئی علامت تھی کہ معاشرے میں شہوانیت کا عنصر حدِّ اعتدال سے بڑھا ہوا تھا۔ یہ خرابی اگر موجود نہ ہوتی تو ممکن نہ تھا کہ ذہن ایسی پاک ہستی کے متعلق ایسے بے سر و پا اور اس قدر گھناؤنے افسانوں کی طرف ادنیٰ التفات بھی کرتے ، کجا کہ زبانیں ان کو دُہرانے لگتیں۔ یہ ٹھیک موقع تھا جبکہ اسلامی معاشرے میں اُن اصلاحی احکام کے نفاذ کی ابتدا کی گئی جو ’’ حجاب‘‘ (پردے ) کے عنوان سے بیان کیے جاتے ہیں۔ اِ ن اصلاحات کا آغاز اس سورے سے کیا گیا ، اور ان کی تکمیل ایک سال بعد سورۂ نور میں کی گئی، جبکہ حضرت عائشہؓ پر بہتان کا فتنہ کھڑا ہوا۔( مزید تفصیل کے لیے ملاحظہ ہو تفسیر سُورہ نور ، دیباچہ)۔ حضور کی خانگی زندگی کے معاملات:اسی زمانہ میں دو مسئلے اور بھی توجہ طلب تھے۔ اگر چہ بظاہر ان کا تعلق نبی صلی اللہ علیہ و سلم کی خانگی زندگی سے تھا ، مگر جو ذات اپنی جان خدا کے دین کو پروان چڑھانے کے لیے کھپا رہی تھی اور ہمہ تن اس کارِ عظیم میں منہمک تھی اُس کے لیے خانگی زندگی کا سکون فراہم کرنا، اور اس کو پریشانیوں سی بچانا، اور اس کو لوگوں کے شکوک و شبہات سے محفوظ رکھنا بھی خود دین ہی کے مفاد کے لیے ضروری تھا۔ اس لیے اللہ تعالیٰ نی سرکاری طور پر ان دونوں مسئلوں کو بھی اپنے ہاتھ میں لے لیا۔ پہلا مسئلہ یہ تھا کہ نبی صلی اللہ علیہ و سلم اُس وقت مالی حیثیت سے تنگ حال تھے۔ ابتدائی چار سال تک تو آپؐکا کوئی ذریعۂ آمدنی تھا ہی نہیں۔ ۴ھ میں بنی النضیر کی جلا وطنی کے بعد ان کی متروکہ زمینوں کا ایک حصّہ اللہ تعالیٰ کے حکم سے آپؐ کی ضروریات کے لیے مخصوص کر دیا گیا۔ مگر وہ آپؐ کے کنبے کے لیے کافی نہ تھا۔ ادھر منصبِ رسالت کے فرائض اتنے بھاری تھے کہ وہ آپؐ کے جسم اور دِل و دِماغ کے ساری طاقتیں اور آپ کے اوقات کا ایک ایک لمحہ سونتے ڈال رہے تھے اور آپ اپنی معاش کے لیے ذرہ برابر بھی کوئی فکر یا کوشش نہ کر سکتے تھے۔ اِن حالات میں جب آپؐ ازواجِ مطہّرات خرچ کی تنگی کے باعث آپؐ کے سکون طبع میں خلل انداز ہوتی تھیں تو اس سے آپؐ کے ذہن پر دُہرا بار پڑ جاتا تھا۔ دوسرا مسئلہ یہ تھا کہ حضرت زینب ؓ کے ساتھ نکاح کرنے سے پہلے آپؐ کی چار بیویاں موجود تھیں۔ حضرت سَودہؓ، حضرت عائشہ ؓ، حضرت حَفْصہ ؓ اور حضرت اُمِّ سَلَمہؓ۔ اُمّ المومنین حضرت زینبؓ آپؐ کی پانچویں بیوی تھیں۔ اس پر مخالفین نے یہ اعتراض اُٹھایا ، اور مسلمانوں کے دِلوں میں بھی اس سے شبہات اُبھرنے لگے کہ دوسروں کے لیے تو بیک وقت چار بیویاں رکھنا ممنوع ٹھیرا دیا گیا ہے ، مگر خود نبی صلی اللہ علیہ و سلم نے یہ پانچویں بیوی کیسے کر لی۔ موضوع اور مباحث: یہ مسائل تھے جو سُورۂ احزاب کے نزول کے زمانے میں پیش آئے تھے اور انہی پر اس سورے میں کلام فرمایا گیا ہے۔ اس کے مضامین پر غور کرنے ، اور پس منظر کو نِگاہ میں رکھنے سے صاف معلوم ہوتا ہی کہ یہ پوری سورۃ ایک خطبہ نہیں ہے جو بیک وقت نازل ہوا ہو ، بلکہ یہ متعدد احکام و فرامین اور خطبات پر مشتمل ہے جو اُس زمانہ کے اہم واقعات کے سلسلے میں یکے بعد دیگرے نازل ہوئے اور پھر یک جا جمع کر کے ایک سورۃ کی شکل میں مرتب کر دیے گئے۔ اس کے حسبِ ذیل اجزاء صاف طور پر ممیز نظر آتے ہیں۔ ۱۔پہلا رکوع غزوۂ احزاب سے کچھ پہلے نازل شدہ معلوم ہوتا ہی۔ تاریخی پس منظر کو نِگاہ میں رکھ کر دیکھا جائے تواس رکوع کو پڑھتے ہوئے صاف محسوس ہوتا ہے کہ اس کے نزول کے وقت حضرت زیدؓ حضرت زینب ؓ کو طلاق دے چکے تھے۔ نبی صلی اللہ علیہ و سلم اس ضرورت کو محسوس فرما رہے تھے کہ متبنیٰ کے بارے میں جاہلیت کے تصوّرات اور اوہام و رسوم کو مٹایا جائے ، اور آپؐ کو یہ بھی محسوس ہو رہا تھا کہ لوگ منہ بولے رشتوں کے معاملے میں محض جذباتی بنیادوں پر جس قسم کے نازک اور گہرے تصوّرات رکھتے ہیں وہ اس وقت تک ہر گز نہ مٹ سکیں گے جب تک آپؐ خود آگے بڑھ کر اس رسم کو نہ توڑ دیں۔لیکن اس کے ساتھ ہے آپؐ اس بنا پر سخت متردّد تھے اور قدم بڑھاتے ہوئے ہچکچا رہے تھے کہ اگر اس موقع پر آپؐ نے حضرت زیدؓ کی مطلّقہ بیوی سے نِکاح کیا تو اسلام کے خلاف ہنگامہ اُٹھانے کے لیے منافقین اور یہود اور مشرکین کو، جو پہلے ہی بھرے بیٹھے ہیں ، ایک زبردست شوشہ ہاتھ آ جائے گا۔ اس موقع پر رکوع اوّل کی آیات نازل ہوئیں۔ ۔ رکوع دوّم و سوّم میں غزوۂ احزاب اور غزوۂ بنی قریظہ پر تبصرہ فرمایا گیا ہے۔ یہ اس بات کی کھُلی علامت ہے کہ یہ دونوں رکوع ان لڑائیوں کے بعد نازل ہوئے ہیں۔ ۳۔ چوتھے رکوع کے آغاز سے آیت ۳۵ تک کی تقریر دو مضامین پر مشتمل ہے۔ پہلے حصّہ میں نبی صلی اللہ علیہ و سلم کی ازواج کو ، جو اس تنگی و عُسرت کے زمانے میں بے صبر ہو رہی تھیں ، اللہ تعالیٰ نے نوٹِس دیا ہے کہ دنیا اور اس کی زینت، اور خدا و رسول اور آخرت میں سے کسی ایک کو انتخاب کر لو۔ اگر تمہیں پہلی چیز مطلوب ہے تو صاف کہہ دو ، تمہیں ایک دن کے لیے بھی اس تنگی میں مبتلا نہ رکھا جائے گا بلکہ بخوشی رخصت کر دیا جائے گا۔ اور اگر دوسری چیز پسند ہے تو صبر کے ساتھ اللہ اور اس کے رسو ل کا ساتھ دو۔ دوسرے حصّے میں اُس معاشرتی اصلاح کی طرف پہلا قدم اُٹھایا گیا جس کی ضرورت اسلام کے سانچے میں ڈھلے ہوئے ذہن اب خود محسوس کرنے لگے تھے۔ اس سلسلہ میں اصلاح کی ابتدا نبی صلی اللہ علیہ و سلم کے گھر سے کرتے ہوئے ازواجِ مطہرات کو حکم دیا گیا کہ تبرُّجِ جاہلیّت سے پرہیز کریں ، وقار کے ساتھ اپنے گھروں میں بیٹھیں اور غیر مردوں کے ساتھ بات چیت کرنے میں سخت احتیاط ملحوظ رکھیں۔ یہ پردے کے احکام کا آغاز تھا۔ ۔ آیت ۳۶ سے ۴۸ تک کا مضمون حضرت زینبؓ کے ساتھ حضورؐ کے نِکاح کے سلسلہ میں ہے۔ اس میں ان تمام اعتراضات کا جواب دیا گیا ہے جو مخالفین کی طرف سے اس نِکاح پر کیے جا رہے تھے ، اُن تمام شبہات کو رفع کیا گیا ہے جو مسلمانوں کی دِلوں میں ڈالنے کی کوشش کی جا رہی تھی، مسلمانوں کو بتایا گیا ہے کہ حضورؐ کا مرتبہ و مقام کیا ہے ، اور خود حضورؐ کو کفار و منافقین کے جھوٹے پروپیگنڈے پر صبر کی تلقین فرمائی گئی ہے۔ ۵۔ آیت ۴۹میں طلاق کے قانون کی ایک دفعہ بیان ہوئی ہے۔ یہ ایک منفرد آیت ہے جو غالباً انہی واقعات کے سلسلے میں کسی موقع پر نازل ہوئی تھی۔ ۔ آیت ۵۰۔ ۵۲ میں نبی صلی اللہ علیہ و سلم کے لیے نکاح کا خاص ضابطہ بیان کیا گیا ہے۔ اس میں یہ بات واضح کر دی گئی کہ حضورؐ اُن متعدد پابندیوں سے مستثنیٰ ہیں جو ازدواجی زندگی کے معاملہ میں عام مسلمانوں پر عائد کی گئی ہیں۔ ۷۔ آیت ۵۳۔ ۵۵ میں معاشرتی اصلاح کا دوسرا قدم اُٹھایا گیا۔ یہ حسب ذیل احکام پر مشتمل ہے : نبی صلی اللہ علیہ و سلم کے گھروں میں غیر مردوں کی آمد و رفت پر پابندی۔ ملاقات اور دعوت کا ضابطہ۔ ازواج مطہرّات کے بارے میں یہ قانون کہ گھروں میں صرف ان کے قریبی رشتہ دار آ سکتے ہیں ، باقی رہے غیر مرد، تو انہیں اگر کوئی بات کہنی ہو یا کوئی چیز مانگنی ہو تو پردے کے پیچھے سے کہیں یا مانگیں۔ نبی صلی اللہ علیہ و سلم کی ازواج کے بارے میں یہ حکم کہ وہ مسلمانوں کے لیے ماں کی طرح حرام ہیں ا ور حضورؐ کے بعد بھی ان میں سے کسی کے ساتھ کسے مسلمان کا نکاح نہیں ہو سکتا۔ ۸۔ آیت ۵۶۔۵۷ میں اُن چہ میگوئیوں پر سخت تنبیہ کی گئی ہے جو نبی صلی اللہ علیہ و سلم کے نکاح اور آپ کی خانگی زندگی پر کی جا رہی تھیں اور اہل ایمان کو ہدایت کی گئی ہے کہ وہ دشمنوں کی اس عیب چینی سے اپنے دامن بچائیں اور اپنے نبیؐ پر درود بھیجیں۔ نیز یہ تلقین بھی کی گئی ہے کہ نبیؐ تو درکنار ، اہل ایمان کو تو عام مسلمانوں پر بھی تہمتیں لگانے اور الزامات عائد کرنے سے کلّی اجتناب کرنا چاہیے۔ ۔ آیت ۵۹ میں معاشرتی اصلاح کا تیسرا قدم اُٹھایا گیا ہے۔اس میں تمام مسلمان عورتوں کو یہ حکم دیا گیا ہے کہ وہ جب گھروں سے باہر نکلیں تو چادروں سے اپنے آپ کو ڈھانک کر اور گھونگٹ ڈال کر نِکلیں۔ اس کے بعد آخر سورۃ تک افواہ بازی کی اُس مہم (Whispering Campaign) پر سخت زجر و توبیخ کی گئی ہے جو منافقین اور اراذل نے اس وقت برپا کر رکھی تھی۔ ','سید ابو اعلیٰ مودودیؒ -  تفہیم القرآن');
INSERT INTO "chapters_ur" VALUES(34,'سبا',NULL,' نام: آیت 15 کے فقرے لقد کان لسبا فی مسکنھم آیۃ سے ماخوذ ہے۔ مراد یہ ہے کہ وہ سورہ جس میں سبا کا ذکر آیا ہے۔ زمانۂ نزول : اس کے نزول کا ٹھیک زمانہ کسی معتبر روایت سے معلوم نہیں ہوتا۔ البتہ انداز بیان سے محسوس ہوتا ہے کہ یا تو مکہ کا دورِ متوسط ہے یا دور اول۔ اور اگر دورِ متوسط ہے تو غالباً اس کا ابتدائی زمانہ ہے جبکہ ظلم و ستم کی شدت شروع نہ ہوئی تھی اور ابھی صرف تضحیک استہزاء، افواہی جنگ، جھوٹے الزامات اور وسوسہ اندازیوں سے اسلام کی تحریک کو دبانے کی کوشش کی جا رہی تھی۔ موضوع اور مضمون: اس سورۂ میں کفار کے ان اعتراضات کا جواب دیا گیا ہے جو وہ نبی صلی اللہ علیہ و آلہ و سلم کی دعوت ِ توحید و آخرت پر اور خود آپ صلی اللہ علیہ و آلہ و سلم کی نبوت پر زیادہ تر طنز و تمسخر اور بیہودہ الزامات کی شکل میں پیش کرتے تھے۔ ان اعتراضات کا جواب کہیں تو ان کو نقل کر کے دیا گیا ہے، اور کہیں تقریر سے خود یہ ظاہر ہو جاتا ہے کہ یہ کس اعتراض کا جواب ہے۔ جوابات اکثر و بیشتر تفہیم و تذکیر اور استدلال کے انداز میں ہیں، لیکن کہیں کفار کو ان کی ہٹ دھرمی کے بُرے انجام سے ڈرایا بھی گیا ہے۔ اسی سلسلہ میں حضرت داؤدؑو سلیمانؑ اور قومِ سبا کے قصے اس غرض کے لیے بیان کیے گئے ہیں کہ تمہارے سامنے تاریخ کی یہ دونوں مثالیں موجود ہیں۔ ایک طرف حضرت داؤدؑ اور سلیمانؑ ہیں جن کو اللہ تعالیٰ نے بڑی طاقتیں بخشیں اور وہ شوکت و حشمت عطا کی جو پہلے کم ہی کسی کو ملی ہے، مگر یہ سب کچھ پاکر وہ کبر و غرور میں مبتلا نہ ہوئے بلکہ اپنے رب کے خلاف بغاوت کرنے کے بجائے اس کے شکر گذار بندے ہی بنے رہے۔ اور دوسری طرف سبا کی قوم ہے جسے اللہ نے جب اپنی نعمتوں سے نوازا تو وہ پھول گئی اور آخر کار اس طرح پارہ پارہ ہوئی کہ اس کے بس افسانے ہی اب دنیا میں باقی رہ گئے ہیں۔ ان دونوں مثالوں کو سامنے رکھ کر خود رائے قائم کر لو کہ توحید و آخرت کے یقین اور شکر نعمت کے جذبے سے جو زندگی بنتی ہے وہ زیادہ بہتر یا وہ زندگی جو کفر و شرک اور انکارِ آخرت اور دنیا پرستی کی بنیاد پر بنتی ہے۔','سید ابو اعلیٰ مودودیؒ -  تفہیم القرآن');
INSERT INTO "chapters_ur" VALUES(35,'پیدا کرنے والا',NULL,' نام: پہلی ہی آیت کا لفظ ’’فاطر‘‘ اس سورہ کا عنوان قرار دیا گیا ہے جس کے معنی صرف یہ ہیں کہ یہ وہ سورۃ ہے جس میں فاطر کا لفظ آیا ہے۔ دوسرا نام ’’ المَلَائکہ‘‘ بھی ہے اور یہ لفظ بھی پہلی آیت میں وارد ہوا ہے۔ زمانۂ نزول: اندازِ کلام کی اندرونی شہادت سے مترشح ہوتا ہے کہ اس سورت کے نزول کا زمانہ غالباً مکہ معظمہ کا دور متوسط ہے، اور اس کا بھی وہ حصہ جس میں مخالفت اچھی خاصی شدّت اختیار کر چکی تھی اور نبی صلی اللہ علیہ و سلم کی دعوت کو ناکام کرنے کے لیے ہر طرح کی بُری سے بری چالیں چلی جا رہی تھیں۔ موضوع و مضمون: کلام کا مدعا یہ ہے کہ نبی صلی اللہ علیہ و سلم کی دعوتِ توحید کے مقابلہ میں جو رویہ اس وقت اہل مکہ اور ان کے سرداروں نے اختیار کر رکھا تھا اس پر ناصحانہ انداز میں ان کو تنبیہ و ملامت بھی کی جائے اور معلمانہ انداز میں فہمائش بھی۔ مضمون کا خلاصہ یہ ہے کہ نادانو، یہ نبی جس راہ کی طرف تم کو بلا رہا ہے اس میں تمہارا اپنا بھلا ہے۔ اس پر تمہارا غصہ، اور تمہاری مکاریاں اور چال بازیاں، اور اس کو ناکام کرنے کے لیے تمہاری تدبیریں دراصل اس کے خلاف نہیں بلکہ تمہارے اپنے خلاف پڑ رہی ہیں۔ اس کی بات نہ مانو گی تو اپنا ہی کچھ بگاڑو گے، اس کا کچھ نہ بگاڑو گے۔ وہ جو کچھ تم سے کہ رہا ہے اس پر غور تو کرو، آخر اس میں غلط کیا بات ہے۔ وہ شرک کی تردید کرتا ہے۔ تم خود آنکھیں کھول کر دیکھو، کیا شرک کے لیے دنیا میں کوئی معقول بنیاد موجود ہے؟ وہ توحید کی دعوت دیتا ہے۔ تم خود عقل سے کام لے کر غور کرو، کیا للہ فاطر السمٰوات و الارض کے سوا کہیں کوئی ایسی ہستی پائی جاتی ہے جو خدائی صفات اور اختیارات رکھتی ہو؟ وہ تم سے کہتا ہے کہ تم اس دنیا میں غیر ذمہ دار نہیں ہو بلکہ تمہیں اپنے خدا کو اپنے اعمال کا حساب دینا ہے اور اس دنیوی زندگی کے بعد ایک اور زندگی ہے جس میں ہر ایک کو اپنے کیے کا نتیجہ دیکھنا ہو گا۔ تم خود سوچو کہ اس پر تمہارے شبہات اور اچنبھے کس قد ر بے اصل ہیں۔ کیا تمہاری آنکھیں رات دن اعادہ خلق کا مشاہدہ نہیں کر رہی ہیں؟پھر تمہارا ہی اعادہ اس خدا کے لیے کیوں ناممکن ہو جس نے تم کو ایک ذرا سے نطفے سے پیدا کر دیا۔ کیا تمہاری عقل یہ گواہی نہیں دیتی کہ بھلے اور بُرے کو یکساں نہ ہونا چاہیے؟ پھر تم ہی بتاؤ کہ معقول بات کیا ہے؟ یہ کہ بھلے اور برے کا انجام یکساں ہو، یعنی مٹی میں ملنا اور فنا ہو جانا؟ یا یہ کہ بھلے کو بھلا اور برے کو برا بدلہ ملے؟ اب اگر ان سراسر معقول اور مبنی بر حقیقت باتوں کو تم نہیں مانتے اور جھوٹے خداؤں کی بندگی نہیں چھوڑتے اور اپنے آپ کو غیر ذمہ دار سمجھتے ہوئے شتر بے مہا رہی کی طرح دنیا میں جینا چاہتے ہو تو اس میں نبی کا کیا نقصان ہے۔ شامت تو تمہاری اپنی ہی آئے گی۔ نبی پر صرف سمجھانے کی ذمہ داری تھی، اور وہ اس نے ادا کر دی۔ سلسلہ کلام میں بار بار نبی صلی اللہ علیہ و سلم کو تسلی دی گئی ہے کہ آپ جب نصیحت کا حق پوری طرح ادا کر رہے ہیں تو گمراہی پر اصرار کرنے والوں کے راہ راست قبول نہ کرنے کی کوئی ذمہ داری آپ کے اوپر عائد نہیں ہوتی۔ اس کے ساتھ آپ کو یہ بھی سمجھایا گیا ہے کہ جو لوگ نہیں ماننا چاہتے ان کے رویے پر نہ آپ غمگین ہوں اور نہ انہیں راہ راست پر لانے کی فکر میں اپنی جان گھلائیں۔ اس کے بجائے آپ اپنی توجہات ان لوگوں پر صرف کریں جو بات سننے کے لیے تیار ہیں۔ ایمان قبول کرنے والوں کو بھی اسی سلسلے میں بڑی بشارتیں دی گئی ہیں تا کہ ان کے دل مضبوط ہوں اور وہ اللہ کے وعدوں پر اعتماد کر کے راہ حق میں ثابت قدم رہیں۔','سید ابو اعلیٰ مودودیؒ -  تفہیم القرآن');
INSERT INTO "chapters_ur" VALUES(36,'یٰسٓ',NULL,' نام: آغاز ہی کے دو حرفوں کو اس سورے کا نام قرار دیا گیا ہے ۔ زمانۂ نزول: انداز بیان پر غور کرنے سے محسوس ہوتا ہے کہ اس سورہ کا نزول یا تو مکہ کے دَورِ متوسط کا آخری زمانہ ہے ، یا پھر یہ زمانہ قیام مکہ کے آخری دور کی سورتوں میں سے ہے ۔ موضوع و مضمون: کلام کا مدعا کفار قریش کو نبوت محمدؐی پر ایمان نہ لانے اور ظلم و استزاء سے اس کا مقابلہ کرنے کے انجام سے ڈرانا ہے ۔ اس میں اِنذار کا پہلو غالب اور نمایاں ہے مگر بار بار اِنذار کے ساتھ استدلال سے تفہیم بھی کی گئی ہے ۔ استدلال تین امور پر کیا گیا ہے : توحید پر آثار کائنات اور عقل عام سے ، آخرت پر آثار کائنات، عقل عام اور خود انسان کے اپنے وجود سے ، اور رسالت محمؐدی کی صداقت پر اس بات سے کہ آپ تبلیغ رسالت میں یہ ساری مشقت محض بے غرضانہ برداشت کر رہے تھے ، اور اس امر سے کہ جن باتوں کی طرف آپ لوگوں کو دعوت دے رہے تھے وہ سراسر معقول تھیں اور انہیں قبول کرنے میں لوگوں کا اپنا بھلا تھا۔ اس استدلال کی قوت پر زجر و توبیخ اور ملامت و تنبیہ کے مضامین نہایت زور دار طریقہ سے بار بار ارشاد ہوئے ہیں تاکہ دلوں کے قفل ٹوٹیں اور جن کے اندر قبول حق کی تھوڑی سے صلاحیت بھی ہو وہ متاثر ہوئے بغیر نہ رہ سکیں۔ امام احمد، ابوداؤد، نسائی، ابن ماجہ اور طبرانی وغیرہ نے مَعقِل بن یَسار سے روایت کیا ہے کہ نبی صلی اللہ علیہ و سلم نے فرمایا یٰس الب القرآن، یعنی یہ سورہ قرآن کا دل ہے ۔ یہ اسی طرح کی تشبیہ ہے جس طرح سورہ فاتحہ کو اُم القرآن فرمایا گیا ہے ۔ فاتحہ کو ام القرآن قرار دینے کی وجہ یہ ہے کہ اس میں قرآن مجید کی پوری تعلیم کا خلاصہ آگیا ہے ۔ اور یٰس کو قرآن دھڑکتا ہو دل اس لیے فرمایا گیا ہے کہ وہ قرآن کی دعوت کو نہایت پر زور طریقے سے پیش کرتی ہے جس سے جمود ٹوٹتا اور روح میں حرکت پیدا ہوتی ہے ۔ انہی حضرت مَعقِل بن یسار سے امام احمد، ابو داؤد اور ابن ماجہ نے یہ روایت بھی نقل کی ہے کہ حضؐور نے فرمایا : اقرءواسُورۃ یٰس علیٰ موتا کم۔’’ اپنے مرنے والوں پر سورہ یٰس پڑھا کرو۔‘‘ اس کی مسلحت یہ ہے کہ مرتے وت مسلمان کے ذہن میں نہ صرف یہ کہ تمام اسلامی عقائد تازہ ہو جائیں، بلکہ خصوصیت کے ساتھ اس کے سامنے عالم آخرت کا پورا نقشہ بھی آ جائے اور وہ جان لے کہ حیات دنیا کی منزل سے گزر کر اب آگے کن منزلوں سے اس کو سابقہ پیش آنے والا ہے ۔ اس مصلحت کی تکمیل کے لیے مناسب یہ معلوم ہوتا ہے کہ غیر عربی داں آدمی کو سورہ یٰس سنانے کے ساتھ اس کا ترجمہ بھی سنا دیا جائے تاکہ تذکیر کا حق پوری طرح ادا ہو جائے ۔ ','سید ابو اعلیٰ مودودیؒ -  تفہیم القرآن');
INSERT INTO "chapters_ur" VALUES(37,'صف باندھنے والے',NULL,' نام: پہلی ہی آیت کے لفظ و الصافات سے ماخوذ ہے ۔ زمانۂ نزول : مضامین اور طرز کلام سے مترشح ہوتا ہے کہ یہ سورت غالباً مکّی دور کے وسط میں ، بلکہ شاید اس دور متوسط کے بھی آخری زمانہ میں نازل ہوئی ہے ۔ اندازِ بیان صاف بتا رہا ہے کہ پس منظر میں مخالفت پوری شدت کے ساتھ برپا ہے اور نبی و اصحاب نبی صلی اللہ علیہ و سلم کو نہایت دل شکن حالات سے سابقہ در پیش ہے ۔ موضوع و مضمون: اس وقت نبی صلی اللہ علیہ و سلم کی دعوتِ توحید و آخرت کا جواب جس تمسخر اور استہزاء کے ساتھ دیا جا رہا تھا، اور آپ کے دعوائے رسالت کو تسلیم کرنے سے جس شدت کے ساتھ انکار کیا جا رہا تھا ، اس پر کفار مکہ کو نہایت پر زور طریقہ سے تنبیہ کی گئی ہے اور آخر میں انہیں صاف صاف خبردار کر دیا گیا ہے کہ عنقریب یہی پیغمبر، جس کا تم مذاق اُڑا رہے ہو، تمہارے دیکھتے دیکھتے تم پر غالب آ جائے گا اور تم اللہ کے لشکر کو خود اپنے گھر کے صحن میں اُترا ہوا پاؤ گے (آیت نمبر 171 تا 179)۔ یہ نوٹس اس زمانے میں دیا گیا تھا جب نبی صلی اللہ علیہ و سلم کی کامیابی کے آثار دور دور کہیں نظر نہ آتے تھے ۔ مسلمان (جن کو ان آیات میں اللہ کا لشکر کہا گیا ہے )بُری طرح ظلم و ستم کا نشانہ بن رہے تھے ۔ان کی تین چوتھائی تعداد ملک چھوڑ کر نکل گئی تھی۔ نبی صلی اللہ علیہ و سلم کے ساتھ بمشکل 40۔ 50 صحابہ مکہ میں رہ گئے تھا اور انتہائی بے بسی کے ساتھ ہر طرح کی زیادتیاں برداشت کر رہے تھے ۔ ان حالات میں ظاہر اسباب کو دیکھتے ہوئے کوئی شخص یہ باور نہ کر سکتا تھا کہ غلبہ آخر کار محمد صلی اللہ علیہ و سلم اور آپ کی مٹھی بھر سر و سامان جماعت کو نصیب ہو گا۔ بلکہ دیکھنے والے تو یہ سمجھ رہے تھے کہ یہ تحریک مکے کی گھاٹیوں ہی میں دفن ہو کر رہ جائے گی۔ لیکن 15 ۔ 16 سال سے زیادہ مدت نہ گزری تھی کہ فتح مکہ کے موقع پر ٹھیک وہی کچھ پیش آگیا جس سے کفار کو خبردار کیا گیا تھا۔ تنبیہ کے ساتھ ساتھ اللہ تعالیٰ نے اس سورہ میں تفہیم اور ترغیب کا حق بھی پورے توازن کے ساتھ ادا فرمایا ہے ۔ توحید اور آخرت کے عقیدے کی صحت پر منحصر دل نشین دلائل دیے ہیں ، مشرکین کے عقائد پر تنقید کر کے بتایا ہے کہ وہ کیسی لغو باتوں پر ایمان لائے بیٹھے ہیں ، ان گمراہیوں کے برے نتائج سے آگاہ کیا ہے اور یہ بھی بتایا ہے کہ ایمان و عمل صالح کے نتائج کس ادر شاندار ہیں ۔ پھر اسی سلسلے میں پچھلی تاریخ کی مثالیں دی ہیں جن سے معلوم ہوتا ہے کہ اللہ تعالیٰ کا اپنے انبیاء کے ساتھ اور ان کی قوموں کے ساتھ کیا معاملہ رہا ہے ، کس کس طرح اس نے اپنے وفادار بندوں کو نوازا ہے اور کس طرح ان کے جھٹلانے والوں کو سزا دی ہے ۔ جو تاریخی قصے اس سورہ میں بیان کیے گئے ہیں ان میں سب سے زیادہ سبق آموز حضرت ابراہیم علیہ السلام کی حیات طیّبہ کا یہ اہم واقعہ ہے کہ وہ اللہ تعالیٰ کا ایک اشارہ پاتے ہی اپنے اکلوتے بیٹے کو قربان کرنے پر آمادہ ہو گئے تھے ۔اس میں صرف ان کفار قریش ہی کے لیے سبق نہ تھا جو حضرت ابراہیمؑ کے ساتھ اپنے نسبی تعلق پر فخر کرتے پھرتے تھے ،بلکہ ان مسلمانوں کے لیے بھی سبق تھا جو اللہ اور اس کے رسول پر ایمان لائے تھے ۔ یہ واقعہ سنا کر انہیں بتا دیا گیا کہ اسلام کی حقیقت اور اس کی اصلی روح کیا ہے ، اور اسے اپنا دین بنا لینے کے بعد ایک مومن صادق کو کس طرح اللہ کی رضا پر اپنا سب کچھ قربان کر دینے کے لیے تیار ہو جانا چاہیے ۔سورہ کی آخری آیات محض کفار کے لیے تنبیہ ہی نہ تھیں بلکہ ان اہل ایمان کے لیے بشارت بھی تھیں جو نبی صلی اللہ علیہ و سلم کی تائید و حمایت میں انتہائی حوصلہ شکن حالات کا مقابلہ کر رہے تھے ۔ انہیں یہ آیات سنا کر خوشخبری دے دی گئی کہ آغاز کار میں جن مصائب سے انہیں سابقہ پیش آ رہا ہے ان پر گھبرائیں نہیں ، آخر کار غلبہ ان ہی کو نصیب ہو گا، اور باطل کے وہ علمبردار جو اس وقت غالب نظر آ رہے ہیں ، انہی کے ہاتھوں مغلوب و مفتوح ہو کر رہیں گے ۔ چند ہی سال بعد واقعات نے بتا دیا کہ یہ محض خالی تسلی نہ تھی بلکہ ایک ہونے والا واقعہ تھا جس کی پیشگی خبر دے کر ان کے دل مضبوط کیے گئے تھے ۔','سید ابو اعلیٰ مودودیؒ -  تفہیم القرآن');
INSERT INTO "chapters_ur" VALUES(38,'صٓ',NULL,' نام: آغاز ہی کے حرف ص سورہ کا نام قرار دیا گیا ہے ۔ زمانہ نزول: جیسا کہ آگے چل کر بتایا جائے گا، بعض روایات کی رو سے یہ سورۃ اس زمانے میں نازل ہوئی تھی جب نبی صلی اللہ علیہ و سلم نے مکہ معظمہ میں علانیہ دعوت کا آغاز کیا تھا اور قریش کے سرداروں میں اس پر کھلبلی مچ گئی تھی۔ اس لحاظ سے اس کا زمانہ نزول تقریباً نبوت کا چوتھا سال قرار پاتا ہے ۔ بعض دوسرے روایات اسے حضرت عمر کے ایمان لانے کے بعد کا واقعہ بتاتی ہیں ، اور معلوم ہے کہ وہ ہجرت حبشہ کے بعد ایمان لائے تھے ۔ ایک اور سلسلہ روایات سے معلوم ہوتا ہے کہ ابوطالب کے آخری مرض کے زمانہ میں وہ معاملہ پیش آیا تھا جس پر یہ سورۃ نازل ہوئی۔ اسے اگر صحیح مانا جائے تو اس کا زمانہ نزول نبوت کا دسواں یا گیارہواں سال ہے ۔ تاریخی پس منظر : امام احمد، نسائی، ترمذی، ابن جریر، ابن ابی شیبہ، ابن ابی حاتم اور محمد بن اسحاق وغیرہ نے جو روایات نقل کی ہیں ان کا خلاصہ یہ ہے کہ جب ابو طالب بیمار ہوئے اور قریش کے سرداروں نے محسوس کیا کہ اب یہ ان کا آخری وقت ہے تو انہوں نے آپس میں مشورہ کیا کہ چل کر شیخ سے بات کرنی چاہیے ۔ وہ ہمارا اور اپنے بھتیجے کا جھگڑا چکا جائیں تو اچھا ہے ۔ کہیں ایسا نہ ہو کہ ان کا انتقال ہو جائے اور ان کے بعد ہم محمد (صلی اللہ علیہ و سلم) کے ساتھ کوئی سخت معاملہ کریں اور عرب کے لوگ ہمیں طعنہ دیں کہ جب تک شیخ زندہ تھا، یہ لوگ اس کا لحاظ کرتے رہے ، اب اس کے مرنے کے بعد ان لوگو ں نے اس کے بھتیجے پر ہاتھ ڈالا ہے ۔ اس رائے پر سب کا اتفاق ہو گیا اور تقریباً 25 سرداران قریش، جن میں ابو جہل، ابو سفیان، امیہ بن خلف، عاص بن وائل، اسود بن المطلب، عقنہ بن ابی معیط، عتبہ اور شیبہ شامل تھے ، ابو طالب کے پاس پہنچے ۔ ان لوگوں نے پہلے تو حسب معمول نبی صلی اللہ علیہ و سلم کے خلاف اپنی شکایات بیان کیں ، پھر کہا ہم آپ کے سامنے ایک انصاف کی بات پیش کرنے آئے ہیں ۔ آپ کا بھتیجا ہمیں ہمارے دین پر چھوڑ دے اور ہم اسے اس کے دین پر چھوڑے دیتے ہیں ۔ وہ جس معبود کی عبادت کرنا چاہے کرے ، ہمیں اس سے کوئی تعرض نہیں ، مگر وہ ہمارے معبودوں کی مذمت نہ کرے اور یہ کوشش نہ کرتا پھرے کہ ہم اپنے معبودوں کو چھوڑ دیں ۔ اس شرط پر آپ ہم سے اس کی صلح کرا دیں ۔ ابو طالب نے نبی صلی اللہ علیہ و سلم کو بلایا اور آپ سے کہا کہ بھتیجے ، یہ تمہاری قوم کے لوگ میرے پاس آئے ہیں ۔ ان کی خواہش ہے کہ تم ایک منصفانہ بات پر ان سے اتفاق کر لو تاکہ تمہارا اور ان کا جھگڑا ختم ہو جائے ۔ پھر انہیں نے وہ بات حضورؐ کو بتائی جو سرداران قریش نے ان سے کہی تھی۔ نبی صلی اللہ علیہ و سلم نے جواب میں فرمایا، چچا جان، میں تو ان کے سامنے ایک ایسا کلمہ پیش کرتا ہوں جسے اگر یہ مان لیں تو عرب ان کا تابع فرمان اور عجم اور کا باج گزار ہو جائے ( حضورؐ کے اس ارشاد کو مختلف راویوں نے مختلف الفاظ میں نقل کیا ہے ۔ ایک روایت یہ ہے کہ آپ نے فرمایا انید اھم علیٰ کلمۃ واحدۃ یقولو نھا تدین لھم بھا العرت و تؤدّی الیھم بھا العجم الجزیرۃ۔ دوسری روایت میں الفاظ یہ ہیں : ادعو ھم الیٰ ان یتکلمو ا بکلمۃ تدین لھم بھا العرب ویملکون بھا لعجم۔ ایک اور روایت میں ہے کہ آپؐ نے ابو طالب کے بجائے قریش کے لوگوں کو خطاب کر کے فرمایا : کلمۃ واحدۃ تعطو نیھا تملکون بھا العرب و تدین لکم بھا لعجم اور ایک روایت کے الفاظ یہ ہیں : ارأیتم ان اعطیتکم کلمۃ تکلمتم بھا ملکتم بھا العرب و دانت لکم بھا العجم۔ ان لفظی اختلافات کے باوجود مدعا سب کا یکساں ہے ، یعنی حضورؐ نے ان سے کہا کہ اگر میں ایک ایسا کلمہ تمہارے سامنے پیش کروں جسے قبول کر کے تم عرب و عجم کے مالک ہو جاؤ گے تو بتاؤ کہ یہ زیادہ بہتر بات ہے یا وہ جسے تم انصار کی بات کہہ کر میرے سامنے پیش کر رہے ہو؟ تمہاری بھلائی اس کلمے کو مان لینے میں ہے یا اس میں کہ جس حالت میں تم پڑے ہو اس میں تم کو پڑا رہنے دوں اور بس اپنی جگہ آپ ہی اپنے خدا کی عبادت کرتا رہوں؟یہ)۔ یہ سن کر پہلے تو وہ لوگ سٹ پٹا گئے ۔ ان کی سمجھ میں نہ آتا تھا کہ آخر کیا کہہ کر ایسے ایک مفید کلمے تو رد کر دیں ۔ پھر کچھ سنبھل کر بولے ، تم ایک کلمہ کہتے ہو، ہم ایسے دس کلمے کہنے کس تیار ہیں ، مگر یہ تو بتاؤ کہ وہ کلمہ کیا ہ؟ آپ نے فرمایا لَآ اِلٰہَ اِلَّا ا للہ۔ اس پر وہ سب یک بارگی اٹھ کھڑے ہوئے اور وہ باتیں کہتے ہوئے نکل گئے جو اس سورۃ کے ابتدائی حصے میں اللہ تعالیٰ نے نقل کی ہیں ۔ ابن سعد نے طبقات میں یہ سارا قصہ اسی طرح بیان کیا ہے جس طرح اوپر مذکور ہوا، مگر ان کی روایت کے مطابق یہ ابو طالب کی مرض وفات کا نہیں بلکہ اس وقت کا واقعہ ہے جب حضورؐ نے دعوتِ عام کی ابتدا کی تھی اور مکہ میں پے در پے یہ خبریں پھیلنی شروع ہو گئی تھیں کہ آج فلاں آدمی مسلمان ہوا اور کل فلاں ۔ اس وقت سرداران قریش یکے بعد دیگرے کئی وفد ابو طالب کے پاس لے کر پہنچے تھے تاکہ وہ محمد صلی اللہ علیہ و سلم کو اس تبلیغ سے روک دیں اور انہی وفود میں سے ایک وفد کے ساتھ یہ گفتگو ہوئی تھی۔ زَمخشری، رازی نیسا بوری اور بعض دوسرے مفسرین کہتے ہیں کہ یہ وفد ابو طالب کے پاس اس وقت گیا تھا جب حضرت عمرؓ کے ایمان لانے پر سرداران قریش بوکھلا گئے تھے ، لیکن کتب روایت میں سے کسی میں اس کا حوالہ ہمیں نہیں مل سکا ہے اور نہ ان مفسرین نے اپنے ماخذ کا حوالہ دیا ہے ۔ تا ہم اگر یہ صحیح ہو تو یہ ہے سمجھ میں آنے والی بات۔ اس لیے کہ کفار قریش پہلے ہی یہ دیکھ کر گھبرائے ہوئے تھے کہ اسلام کی دعوت لے کر ان کے درمیان سے ایک ایسا شخص اُٹھا ہے جو اپنی شرافت، نے داغ سیرت اور دانائی و سنجیدگی کے اعتبار سے ساری قوم میں اپنا جواب نہیں رکھتا۔ اور پھر اس کا دست راست ابو بکر جیسا آدمی ہے جسے مکے اور اس کے اطراف کا بچہ بچہ ایک نہایت شریف، راستباز اور ذکی انسان کی حیثیت سے جانتا ہے ۔ اب جو انہوں نے دیکھا ہو گا کہ عمر بن خطاب جیسا جری اور صاحب عزم آدمی بھی ان دونوں سے جا ملا ہے تو یقیناً انہیں محسوس ہوا ہو گا کہ خطرہ حدّ برداشت سے گزرتا جا رہا ہے ۔ موضوع اور مباحث: اوپر جس مجلس کا ذکر کیا گیا ہے اسی پر تبصرے سے اس سورۃ کا آغاز ہوا ہے ۔ کفار اور نبی صلی اللہ علیہ و سلم کی گفتگو کو بنیاد بنا کر اللہ تعالیٰ نے بتایا ہے کہ ان لوگوں کے انکار کی اصل وجہ دعوت اسلامی کا کوئی نقص نہیں ہے بلکہ ان کا اپنا تکبر اور حسد اور تقلید اعمیٰ پر اصرار ہے ۔ یہ اس کے لیے تیار نہیں ہیں کہ اپنی ہی برادری کے ایک آدمی کو خدا کا نبی مان کر اس کی پیروی قبول کر لیں ۔ یہ انہی جاہلانہ تخیلات پر جمے رہنا چاہتے ہیں جن پر انہوں نے اپنے قریب کے زمانے کے لوگوں کو پایا ہے ، اور جب اس جہالت کے پردے کو چاک کر کے ایک شخص ان کے سامنے اصل حقیقت کو پیش کرتا ہے تو یہ اس پر کان کھڑے کرتے ہیں اور اسے عجیب بات بلکہ نرالی اور انہونی بات قرار دیتے ہیں ۔ ان کے نزدیک توحید اور آخرت کا تخیل محض ناقابل قبول ہی نہیں ہے بلکہ ایک ایسا تخیل ہے جس کا بس مذاق ہی اُڑایا جا سکتا ہے ۔ اس کے بعد اللہ تعالیٰ نے سورہ کے ابتدائی حصے میں بھی اور آخری فقروں میں بھی کفار کو صاف صاف متنبہ کیا ہے کہ جس شخص کا تم آج مذاق اڑا رہے ہو اور جس کی رہنمائی قبول کرنے سے تم کو آج سخت انکار ہے ، عنقریب وہی غالب آ کر رہے گا اور وہ وقت دور نہیں ہے جب اسی شہر مکہ میں ، جہاں تم اس کو نیچا دکھانے کے لیے ایڑی چوٹی کا زور لگا رہے ہو، اس کے آگے تم سب سرنگوں نظر آؤ گے ۔ پھر پے در پے 9 پیغمبروں کا ذکر کر کے ، جن میں حضرت داؤد و سلیمانؑ کا قصہ زیادہ مفصل ہے ، اللہ تعالیٰ نے یہ بات معین کے ذہن نشین کرائی ہے کہ اس کا قانون عدل بالکل بے لاگ ہے ، اس کے ہاں انسان کا صحیح رویہ ہی مقبول ہے ، بے جا بات خواہ کوئی بھی کرے وہ اس پر گرفت کرتا ہے ، اور اس کے ہاں وہی لوگ پسند کیے جاتے ہیں جو لغزش پر اصرار نہ کریں بلکہ اس پر متنبہ ہوتے ہی تائب ہو جائیں اور دنیا میں آخرت کی جواب دہی کو یاد رکھتے ہوئے زندگی بسر کریں ۔ اس کے بعد فرماں بردار بندوں اور سرکش بندوں کے اس انجام کا نقشہ کھینچا گیا ہے جو وہ علم آخرت میں دیکھنے والے ہیں اور اس سلسلے میں کفار کو دو باتیں خاص طور پر بتائی گئی ہیں ۔ ایک یہ کہ آج جن سرداروں اور پیشواؤں کے پیچھے جاہل لوگ اندھے بن کر ضلالت کی راہ پر چلے جا رہے ہیں ، کل وہی جہنم میں اپنے پیروؤں سے پہلے پہنچے ہوئے ہوں گے اور دونوں ایک دوسرے کو کوس رہے ہوں گے ۔ دوسرے یہ کہ آج جن اہل ایمان کو یہ لوگ ذلیل و خوار سمجھ رہے ہیں ، کل یہ آنکھیں پھاڑ پھاڑ کر حیرت کے ساتھ دیکھیں گے کہ ان کا جہنم میں کہیں نام و نشان تک نہیں ہے اور یہ خود اس کے عذاب میں گرفتار ہیں ۔ آخر میں قصہ آدم و ابلیس کا ذکر فرمایا گیا ہے اور اس سے مقصود کفار قریش کو یہ بتانا ہے کہ محمد صلی اللہ علیہ و سلم کے آگے جھکنے سے جو تکبر تمہیں مانع ہو رہا ہے وہی تکبر آدم کے آگے جھکنے سے ابلیس کو بھی مانع ہوا تھا۔ خدا نے جو مرتبہ آدم کو دیا تھا اس پر ابلیس نے حسد کیا اور حکم خدا کے مقابلے میں سرکشی اختیار کر کے لعنت کا مستحق ہوا۔ اسی طرح جو مرتبہ خدا نے محمد صلی اللہ علیہ و سلم کو دیا ہے اس پر تم حسد کر رہے ہو اور اس بات کے لیے تیار نہیں ہو کہ جسے خدا نے رسول مقرر کیا ہے اس کی اطاعت کرو، اس لیے جو انجام ابلیس کا ہونا ہے وہی آخر کار تمہارا بھی ہونا ہے ۔','سید ابو اعلیٰ مودودیؒ -  تفہیم القرآن');
INSERT INTO "chapters_ur" VALUES(39,'گروہ',NULL,' نام: اس سورہ کا نام آیات نمبر 71 و 73 ( وَسِیْقَ الَّذِیْنَ کَفَرُ وْ آ اِلیٰ جَھَنَّمَ زُمَراً اور وَ سِیْقَ الَّذِیْنَ اتَّقَوْ ارَبَّھُمْ اِلَی ا لْجَنَّۃٍ زُمَراً ) سے ماخوذ ہے۔ مطلب یہ ہے کہ وہ سورہ جس میں لفظ زمر آیا ہے۔ زمانۂ نزول: آیت نمبر 10 ( وَاَرْضُ اللہِ وَاسِعَۃٌ ) سے اس امر کی طرف صاف اشارہ نکلتا ہے کہ یہ سورۃ ہجرت حبشہ سے پہلے نازل ہوئی تھی۔ بعض روایات میں یہ تصریح آئی ہے کہ اس آیت کا نزول حضرت جعفرؓ بن ابی طالب اور ان کے ساتھیوں کے حق میں ہوا تھا جبکہ انہوں نے حبشہ کی طرف ہجرت کا عزم کیا (روح المعانی، جلد 23، صفحہ 226) موضوع اور مضمون: یہ پوری سورت ایک بہترین اور انتہائی مؤثر خطبہ ہے جو ہجرت حبشہ سے کچھ پہلے مکہ معظمہ کی ظلم و تشدد سے بھری ہوئی اور عناد و مخالفت سے لبریز فضا میں دیا گیا تھا۔ یہ ایک وعظ ہے جس کے مخاطب زیادہ تر کفارِ قریش ہیں ، اگر چہ کہیں کہیں اہل ایمان سے بھی خطاب کیا گیا ہے۔ اس میں دعوت محمدی کا اصل مقصود بتایا گیا ہے ، اور وہ یہ ہے کہ انسان خالص اللہ کی بندگی اختیار کرے اور کسی دوسرے کی طاعت و عبادت سے اپنی خدا پرستی کو آلودہ نہ کرے۔ اس اصل الاصول کو بار بار مختلف انداز سے پیش کرتے ہوئے نہایت زور دار طریقے پر توحید کی حقانیت اور اسے ماننے کے عمدہ نتائج، اور شرک کی غلطی اور اس پر جمے رہنے کے بُرے نتائج کو واضح کیا گیا ہے ، اور لوگوں کو دعوت دی گئی ہے کہ وہ اپنی غلط روش سے باز آ کر اپنے رب کی رحمت کی طرف پلٹ آئیں۔ اسی سلسلے میں اہل ایمان کو ہدایت فرمائی گئی ہے کہ اگر اللہ کی بندگی لے لیے ایک جگہ تنگ ہو گئی ہے تو اس کی زمین وسیع ہے ، اپنا دین بچانے کے لیے کسی اور طرف نکل کھڑے ہو، اللہ تمہارے صبر کا اجر دے گا۔ دوسرے طرف نبی صلی اللہ علیہ و سلم سے فرمایا گیا ہے کہ ان کفار کو اس طرف سے بالکل مایوس کر دو کہ ان کا ظلم و ستم کبھی تم کو اس راہ سے پھیر سکے گا اور ان سے صاف صاف کہہ دو کہ تم میرا راستہ روکنے کے لیے جو کچھ بھی کرنا چاہتے ہو کر ڈالو، میں اپنا یہ کام جاری رکھوں گا۔','سید ابو اعلیٰ مودودیؒ -  تفہیم القرآن');
INSERT INTO "chapters_ur" VALUES(40,'بخشنے والا',NULL,' نام: آیت 28 کے فقرے وَ قَالَ رَجُلٌ مُّؤْمِنٌ مِّنْ اٰلِ فِرْعَوْنَ سے ماخوذ ہے، یعنی وہ سورہ جس میں اس خاص مومن کا ذکر آیا ہے۔ زمانۂ نزول: ابن عباس اور جابر بن زید کا بیان ہے کہ یہ سورۃ زمر کے بعد متصلاً نازل ہوئی ہے اور اس کا جو مقام قرآن مجید کی موجودہ ترتیب میں ہے وہی ترتیب نزول کے اعتبار سے بھی ہے۔ حالات نزول: جن حالات میں یہ سورہ نازل لوئی ہے اُن کی طرف صاف اشارات اِس کے مضمون میں موجود ہیں۔ کفار مکہ نے اُس وقت نبی صلی اللہ علیہ و سلم کے خلاف دو طرح کی کارروائیا ں شروع کر رکھی تھیں۔ ایک یہ کہ ہر طرف جھگڑے اور بحثیں چھیڑ کر، طرح طرح کے اُلٹے سیدھے سوالات اّٹھا کر، اور نت نئے الزامات لگا کر قرآن کی تعلیم اور اسلام کی دعوت اور خود نبی صلی اللہ علیہ و سلم کے بارے میں اتنے شبہات اور وسوسے لوگوں کے دلوں میں پیدا کر دیے جائیں کہ ان کو صاف کرتے کرتے آخر کار حضورؐ اور اہل ایمان زچ ہو جائیں۔ دوسرے یہ کہ آپ کو قتل کر دینے کے لیے زمین ہموار کی جائے۔ چنانچہ اس غرض کے لیے وہ پیہم سازشیں کر رہے تھے، اور ایک مرتبہ تو عملاً انہوں نے اس کا اقدام کر بھی ڈالا تھا۔ بخاری میں حضرت عبداللہ بن عُمژوبن عاص کی روایت ہے کہ ایک روز نبی صلی اللہ علیہ و سلم حرم میں نماز پڑھ رہے تھے۔ یکایک عُقبہ بن ابی مُعَیط آگے بڑھا اورس نے آپ کی گردن میں کپڑا ڈال کر اسے بَل دینا شروع کر دیا تاکہ گلا گھونٹ کر آپؐ کو مار ڈالے مگر عین وقت پر حضرت ابوبکر پہنچ گئے اور انہوں نے دھکا دے کر اسے ہٹا دیا۔ حضرت عبداللہ کا بیان ہے کہ جس وقت ابو بکر صدّیق اُس ظالم سے کشمکش کر رہے تھے اس وقت اُن کی زبان پر یہ الفاظ جاری تھے کہ اَتَقْتُلُوْ نَ رَجُلاً اَنْ یَّقُوْلَ رَ بِّیَ اللہ (کیا تم ایک شخص کو صرف اِس قصور میں مارے ڈالتے ہو کہ وہ کہتا ہے میرا رب اللہ ہے؟)۔ تھوڑے اختلاف کے ساتھ یہ واقعہ سیرت ابن ہشام میں بھی منقول ہو ا ہے اور نَسَائی اور ابن ابی حاتم نے بھی اسے روایت کیا ہے۔ موضوع اور مباحث: صورت حال کے اِن دونوں پہلوؤں کو آغاز تقریر ہی میں صاف صاف بیان کر دیا گیا ہے اور پھر آگے کی پوری تقریر انہی دونوں پر ایک انتہائی مؤثر اور سبق آموز تبصرہ ہے۔ قتل کی سازشوں کے جواب میں مومنِ آل فرعون کا قصّہ سنایا گیا ہے ( آیت 23 تا 55) اور اس قصے کے پیرائے میں تین گروہوں کو تین مختلف سبق دیے گئے ہیں : 1۔ کفّار کو بتایا گیا ہے کہ جو تم محمد صلی اللہ علیہ و سلم کے ساتھ کرنا چاہتے ہو یہی کچھ اپنی طاقت کے بھروسے پر فرعون حضرت موسیٰ کے ساتھ کرنا چاہتے تھا، اب کیا یہ حرکتیں کر کے تم بھی اُسی انجام سے دوچار ہونا چاہتے ہو جس سے وہ دوچار ہوا؟ ۔ محمد صلی اللہ علیہ و سلم اور آپ کے پیروؤں کو سبق دیا گیا ہے کہ یہ ظالم بظاہر خواہ کتنے ہی بالا دست اور چہرہ دست ہوں، اور ان کے مقابلہ میں تم خواہ کتنے ہی کمزور اور بے بس ہو، مگر تمہیں یقین رکھنا چاہیے کہ جس خدا کے دین کا بول بالا کرنے کے لیے تم کام کر رہے ہو اس کی طاقت ہر دوسری طاقت پر بھاری ہے۔ لہٰذا جو بڑی خوفناک دھمکی بھی یہ تمہیں دے سکتے ہیں، اس کے جواب میں بس خدا کی پناہ مانگ لو اور اس کے بعد بالکل بے خوف ہو کر اپنے کام میں لگ جاؤ۔ خدا پرست کے پاس ظالم کی ہر دھمکی کا بس ایک جواب ہے، اور وہ ہے اِنِّیْ عُذْتُ بِرَبّیِ وَ بِرَبِّکُمْ مِّنْ کُلِّ مُتَکَبِّرٍ لَّا یُؤْمِنُ بِیَوْمِ الْحِسَابِ۔ اس طرح خدا کے بھروسے پر خطرات سے بے پروا ہو کر کام کرو گے تو آخر کار اس کی نصرت آ کر رہے گی اور آج کے فرعون بھی وہی کچھ دیکھ لیں گے جو کل کے فرعون دیکھ چکے ہیں۔ وہ وقت آنے تک ظلم و ستم کے جو طوفان بھی اُمڈ کر آئیں انہیں صبر کے ساتھ تمہیں برداشت کرنا ہو گا۔ ۔ ان دو گروہوں کے علاوہ ایک تیسرا گروہ بھی معاشرے میں موجود تھا، اور وہ ان لوگوں کا گروہ تھا جو دلوں میں جان چکے تھے کہ حق محمد صلی اللہ علیہ و سلم کے ساتھ ہے اور کفّار قریش سراسر زیادتی کر رہے ہیں۔ مگر یہ جان لینے کے باوجود وہ خاموشی کے ساتھ حق و باطل کی اس کشمکش کا تماشا دیکھ رہے تھے۔ اللہ تعالیٰ نے اس موقع پر اُن کے ضمیر کو جھنجھوڑا ہے اور انہیں بتایا ہے کہ جب حق کے دشمن علانیہ تمہاری آنکھوں کے سامنے اتنا بڑا ظالمانہ اقدام کرنے پر تُل گئے ہیں تو حیف ہے تم پر اگر اب بھی تم بیٹھے تماشا ہی دیکھتے رہو۔اس حالت میں جس شخص کا ضمیر بالکل مر نہ چکا ہو اُسے تو اُٹھ کر وہ فرض انجام دینا چاہیے جو فرعون کے بھرے دربار میں اُس کے اپنے درباریوں میں سے ایک راستباز آدمی نے اُس وقت انجام دیا تھا جب فرعون نے حضرت موسیٰ کو قتل کرنا چاہا تھا۔ جو مصلحتیں تمہیں زبان کھولنے سے باز رکھ رہی ہیں، یہی مصلحتیں اُس شخص کے آگے بھی راستہ روک کر کھڑی ہوئی تھیں۔ مگر اُس نے اُفَوِّ ضُ اَمْرِیْ اِلَی اللہ کہہ کر ان ساری مصلحتوں کو ٹھکرا دیا، اور اس کے بعد دیکھ لو کہ فرعون اُس کا کچھ نہ بگاڑ سکا۔ اب رہا کفار کا وہ مجادلہ جو حق کو نیچا دکھانے کے لیے مکہ معظمہ میں شب و روز جاری تھا، تو اس کے جواب میں ایک طرف دلائل سے توحید اور آخرت کے اُن عقائد کا بر حق ہونا ثابت کیا گیا ہے جو محمد صلی اللہ علیہ و سلم اور کفار کے درمیان اصل بنائے نزاع تھے، اور یہ حقیقت صاف کھول کر رکھ دی گئی ہے کہ یہ لوگ کسی علم اور کسی دلیل و حجت کے بغیر سچائیوں کے خلاف خواہ مخواہ جھگڑ رہے ہیں۔ دوسرے طرف اُ ن اصل محرکات کو بے نقاب کیا گیا ہے جن کی بنا پر سردارانِ قریش اس قدر سرگرمی کے ساتھ نبی صلی اللہ علیہ و سلم کے خلاف بر سر پیکار تھے۔ بظاہر ا نہوں نے یہ ڈھونگ رچا رکھا تھا کہ حضورؐ کی تعلیم اور آپؐ کے دعوائے نبوت پر انہیں حقیقی اعتراضات ہیں جن کی وجہ سے وہ ان باتوں کو نہیں مان رہے ہیں۔ لیکن درحقیقت یہ ان کے لیے محض ایک جنگ اقتدار تھی۔ آیت 56 میں یہ بات کسی لاگ لپیٹ کے بغیر اُن سے صاف کہہ دی گئی ہے کہ تمہارے انکار کی اصل وجہ وہ کبر ہے جو تمہارے دلوں میں بھرا ہوا ہے۔ تم سمجھتے ہو کہ اگر لوگ محمد صلی اللہ علیہ و سلم کی نبوت تسلیم کر لیں گے تو تمہاری بڑائی قائم نہ رہ سکے گی۔ اسی وجہ سے تم ان کو زک دینے کے لیے ایڑی چوٹی کا زور لگا رہے ہو۔اسی سلسلے میں کفار کو پے در پے تنبیہات کی گئی ہیں کہ اگر اللہ کی آیات کے مقابلے میں مجادلہ کرنے سے باز نہ آؤ گے تو اُسی انجام سے دوچار ہو گے جس سے پچھلی قومیں دوچار ہو چکی ہیں اور اس سے بدتر انجام تمہارے لیے آخرت میں مقدر ہے۔ اُس وقت تم پچھتاؤ گے، مگر اس وقت کا پچھتانا تمہارے لیے کچھ بھی نافع نہ ہو گا۔','سید ابو اعلیٰ مودودیؒ -  تفہیم القرآن');
INSERT INTO "chapters_ur" VALUES(41,'مفصل',NULL,' نام: اس سورہ کا نام دو لفظوں سے مرکب ہے۔ ایک حٰم، دوسرے السجدہ۔ مطلب یہ ہے کہ وہ سورۃ جس کا آغاز حٰم سے ہوتا ہے اور جس میں ایک مقام پر آیت سجدہ آئی ہے۔ زمانۂ نزول: معتبر روایات کی رو سے اس کا زمانہ نزول حضرت حمزہؓ کے ایمان لانے کے بعد اور حضرت عمرؓ کے ایمان لانے سے پہلے ہے۔ نبی صلی اللہ علیہ و سلم کے قدیم ترین سیرت نگار محمد بن اسحاق نے مشہور تابعی محمد بن کعب القرظی کے حوالہ سے یہ قصہ نقل کیا ہے کہ ایک دفعہ قریش کے کچھ سردار مسجد حرام میں محفل جماٴئے بیٹھے تھے اور مسجد کے ایک دوسرے گوشے میں رسول اللہ صلی اللہ علیہ و سلم تنہا تشریف رکھتے تھے۔ یہ وہ زمانہ تھا جب حضرت حمزہ ایمان لا چکے تھے اور قریش کے لوگ مسلمانوں کی جمعیت میں روز افزوں اضافہ دیکھ دیکھ کر پریشان ہو رہے تھے۔ اس موقع پر عُتبہ بن ربیعہ (ابو سفیان کے خسر ) نے سرداران قریش سے کہا کہ صاحبو، اگر آپ لوگ پسند کریں تو میں جا کر محمد (صلی اللہ علیہ و سلم) سے بات کروں اور ان کے سامنے چند تجویزیں رکھوں ، شاید کہ وہ ان میں سے کسی کو مان لیں اور ہم بھی اسے قبول کر لیں اور اس طرح وہ ہماری مخالفت سے باز آ جائیں ۔ سب حاضرین نے اس سے اتفاق کیا اور عتبہ اٹھ کر نبی صلی اللہ علیہ و سلم کے پاس جا بیٹھا۔ آپؐ اس کی طرف متوجہ ہوٴئے تو اس نے کہا’’ بھتیجے، تم اپنی قوم میں اپنے نسب اور خاندان کے اعتبار سے جو حیثیت رکھتے ہو وہ تمہیں معلوم ہے۔ مگر تم اپنی قوم پر ایک بڑی مصیبت لے آٴئے ہو۔ تم نے جماعت میں تفرقہ ڈال دیا۔ ساری قوم کو بے وقوف ٹھیرایا۔ قوم کے دین اور اس کے معبودوں کی برائی کی۔ اور ایسی باتیں کرنے لگے جن کے معنی یہ ہیں کہ ہم سب کے باپ دادا کافر تھے۔ اب ذرا میری بات سنو۔ میں کچھ تجویزیں تمہارے سامنے رکھتا ہوں ۔ ان پر غور کرو۔ شاید کہ ان میں سے کسی کو تم قبول کر لو‘‘۔ رسول صلی اللہ علیہ و سلم نے فرمایا ابولولید، آپ کہیں ، میں سنوں گا۔ اس نے کہا، ‘‘ بھتیجے، یہ کام جو تم نے شروع کیا ہے، اس سے اگر تمہارا مقصد مال حاصل کرنا ہے تو ہم سب مل کر تم کو اتنا کچھ دیے دیتے ہیں کہ تم ہم میں سب سے زیادہ مالدار ہو جاؤ۔ اگر اس سے اپنی بڑائی چاہتے ہو تو ہم تمہیں اپنا سردار بناٴئے لیتے ہیں ۔ اور اگر تم پر کوئی جِن آتا ہے جسے تم خود دفع کرنے پر قادر نہیں ہو تو ہم بہترین اطبا ء بلواتے ہیں اور اپنے خرچ پر تمہارا علاج کراتے ہیں ۔‘‘ عُتبہ یہ باتیں کرتا رہا اور حضورؐ خاموش سنتے رہے۔ پھر آپ نے فرمایا، ابوالولید آپ کو جو کچھ کہنا تھا کہہ چکے؟ اس نے کہا، ہاں ۔، آپؐ نے فرمایا اچھا، اب میری سنو۔ اس کے بعد آپ نے بسم اللہ الرحمٰن الرحیم پڑھ کر اسی سورۃ کی تلاوت شروع کی اور عتبہ اپنے دونوں ہاتھ پیچھے زمین پر ٹیکے غور سے سنتا رہا۔ آیت سجدہ (آیت 38) پر پہنچ کر آپؐ نے سجدہ کیا، پھر سر اٹھا کر فرمایا، ’’ اے ابوالید، میرا جواب آپ نے سن لیا، اب آپ جانیں اور آپ کا کام۔‘‘ عتبہ اٹھ کر سرداران قریش کی مجلس کی طرف چلا تو لوگوں نے دور سے اس کو دیکھتے ہی کہا، خدا کی قسم، عتبہ کا چہرہ بدلا ہوا ہے، یہ وہ صورت نہیں ہے جسے لے کر یہ گیا تھا۔پھر جب وہ آ کر بیٹھا تو لوگوں نے کہا : کیا سُن آٴئے؟ اس نے کہا : ’’ بخدا، میں نے ایسا کلام سنا کہ کبھی اس سے پہلے نہ سنا تھا۔ خدا کی قسم، نہ یہ شعر ہے، نہ سحر ہے نہ کہانت۔ اے سردارانِ قریش، میری بات مانو اور اس شخص کو اس کے حال پر چھوڑ دو۔ میں سمجھتا ہوں کہ یہ کلام کچھ رنگ لا کر رہے گا۔ فرض کرو، اگر عرب اس پر غالب آ گئے تو اپنے بھائی کے خلاف ہاتھ اٹھانے سے تم بچ جاؤ گے اور دوسرے اس سے نمٹ لیں گے۔ لیکن اگر وہ عرب پر غالب آگیا تو اس کی بادشاہی تمہاری بادشاہی، اور اس کی عزت تمہاری عزت ہی ہو گی۔‘‘ سرداران قریش اس کی یہ بات سنتے ہی بول اٹھے، ’’ ولید کے ابّا، آخر اس کا جادو تم پر بھی چل گیا۔‘‘ عتبہ نے کہا، میری جو راٴئے تھی وہ میں نے تمہیں بتا دی، اب تمہارا جو جی چاہے کرتے رہو (ابن ہشام، جلد 1، ص 313۔ 314) اس قصے کو متعدد دوسرے محدثین نے حضرت جاب بن عبداللہ سے بھی مختلف طریقوں سے نقل کیا ہے، جن میں تھوڑا بہت لفظی اختلاف ہے۔ ان میں سے بعض میں یہ بھی آیا ہے کہ جب حضورؐ تلاوت کرتے ہوٴئے آیت : فَاِنْ اَعْرَضُوْ ا فَقُلْ اَنْذَرْ تُکُمْ صَا عِقُۃً مِّثْلَ صَاعِقَۃِ عَادٍ وَّ ثَمُوْدَ (اب اگر یہ لوگ منہ موڑتے ہیں تو ان سے کہہ دو کہ میں تمہیں عاد اور ثمود کے عذاب جیسے ایک اچانک ٹوٹ پڑنے والے عذاب سے ڈراتا ہوں ) پر پہنچے تو عتبہ نے بے اختیار آپ کے منہ پر ہاتھ رکھ دیا اور کہا ’’خدا کے لیے اپنی قوم پر رحم کرو‘‘۔ بعد میں اس نے سرداران قریش کے سامنے اپنے اس فعل کی وجہ یہ بیان کی کہ ’’ آپ لوگ جانتے ہیں ، محمدؐ کی زبان سے جو بات نکلتی ہے پوری ہو کر رہتی ہے، اس لیے میں ڈر گیا کہ کہیں ہم پر عذاب نازل نہ ہو جاٴئے‘‘۔(تفصیلات کے لیے ملاحظہ ہو تفسیر ابن کثیر، ج 4، ص 90۔91۔البدایہ والنہایہ،ج3، ص 62)۔ موضوع اور مضمون: عتبہ کی اس گفتگو کے جواب میں جو تقریر اللہ تعالیٰ کی طرف سے نازل ہوئی، اس میں ان بیہودہ باتوں کی طرف سرے سے کوئی التفات نہ کیا گیا جو اس نے نبی صلی اللہ علیہ و سلم سے کہی تھیں ۔ اس لیے کہ جو کچھ اس نے کہا تھا وہ دراصل حضورؐ کی نیت اور آپ کی عقل پر حملہ تھا۔ اس کی ساری باتوں کے پیچھے یہ مفروضہ کام کر رہا تھا کہ حضورؐ کے نبی، اور قرآن کے وحی ہونے کا تو بہر حال کوئی امکان نہیں ہے، اب لامحالہ آپؐ کی اس دعوت کا محرک یا تو مال و دولت اور حکومت و اقتدار حاصل کرنے کا جذبہ ہے، یا پھر، معاذاللہ، آپؐ کی عقل پر فتور آگیا ہے۔ پہلی صورت میں وہ آپ سے سودے بازی کرنا چاہتا تھا، اور دوسری صورت میں یہ کہہ کر آپ کی توہین کر رہا تھا کہ ہم اپنے خرچ پر آپ کی دیوانگی کا علاج کراٴئے دیتے ہیں ۔ ظاہر ہے کہ اس طرح کی بیہودگیوں پر جب مخالفین اتر آئیں تو ایک شریف آدمی کا کام ان کا جواب دینا نہیں ہے بلکہ اس کا کام یہ ہے کہ ان کو قطعی نظر انداز کر کے اپنی جو بات کہنی ہو کہے۔ عتبہ کی باتوں سے صرف نظر کرتے ہوٴئے اس سورہ میں اس مخالفت کو موضوع بحث بنایا گیا ہے جو قرآن مجید کی دعوت کو زک دینے کے لیے کفار مکہ کی طرف سے اس وقت انتہائی ہٹ دھرمی اور بد اخلاقی کے ساتھ کی جا رہی تھی۔ وہ نبی صلی اللہ علیہ و سلم سے کہتے تھے، آپ خواہ کچھ کریں ، ہم آپ کی کوئی بات سن کر نہ دی گے۔ ہم نے اپنے دلوں پر غلاف چڑھا لیے ہیں ۔ اپنے کان بند کر لیے ہیں ۔ ہمارے اور آپ کے درمیان ایک دیوار حائل ہو گئی ہے جو ہمیں اور آپ کو کبھی نہ ملنے دے گی۔ انہوں نے آپ کو صاف صاف نوٹس دے دیا تھا کہ آپ اپنی اس دعوت کا کام جاری رکھیے، ہم آپ کی مخالفت میں جو کچھ ہم سے ہو سکے گا کریں گے۔ انہوں نے آپ کو زک دینے کے لیے کام کا یہ نقشہ بنایا تھا کہ جب بھی آپ یا آپ کے پیروؤں میں سے کوئی عام لوگوں کو قرآن سنانے کی کوشش کرے، فوراً ہنگامہ برپا کر دیا جاٴئے اور اتنا شور مچایا جاٴئے کہ کان پڑی آواز نہ سنائی دے۔ وہ پوری سرگرمی کے ساتھ اس کام میں لگے ہوٴئے تھے کہ قرآن مجید کی آیات کو اُلٹے معنی پہنا کر عوام میں طرح طرح کی غلط فہمیاں پھیلائیں ۔ بات کچھ کہی جاتی تھی اور وہ اسے بناتے کچھ تھے۔ سیدھی بات میں ٹیڑھ نکالتے تھے۔ سیاق و سباق سے الگ کرک ے کوئی لفظ کہیں سے اور کوئی فقرہ کہیں سے لے اُڑتے اور اس کے ساتھ ا پنی طرف سے چار باتیں ملا کر نٴئے نٴئے مضامین پیدا کرتے تھے تاکہ قرآن اور اس کے پیش کرنے والے رسول کے متعلق لوگوں کی راٴئے خراب کی جاٴئے۔ عجیب عجیب قسم کے اعتراضات جڑتے تھے جن کا ایک نمونہ اس سورہ میں پیش کیا گیا ہے۔ کہتے تھے کہ ایک عرب اگر عربی زبان میں کوئی کلام سناتا ہے تو اس میں معجزے کی کیا بات ہوئی؟ عربی تو اس کی مادری زبان ہے۔ اپنی مادری زبان میں جس کا جی چاہے ایک کلام تصنیف کر لے اور دعویٰ کر دے کہ وہ اس پر خدا کی طرف سے نازل ہوا ہے۔معجزہ تو جب ہوتا کہ یہ شخص کسی دوسری زبان میں ، جسے یہ نہیں جانتا، یکایک اٹھ کر فصیح و بلیغ تقریر کر ڈالتا۔ تب یہ سمجھا جا سکتا تھا کہ یہ اس کا اپنا کلام نہیں ہے بلکہ اوپر کہیں سے اس پر نازل ہو رہا ہے۔ اس اندھی اور بہری مخالفت کے جواب میں جو کچھ فرمایا گیا ہے اس کا ماحصل یہ ہے : (1)۔ یہ خدا ہی کا نازل کردہ کلام ہے اور عربی زبان ہی میں ہے۔ جو حقیقتیں اس میں صاف صاف کھول کر بیان کی گئی ہیں ، جاہل لوگ ان کے اندر علم کی کوئی روشنی نہیں پاتے، مگر سمجھ بوجھ رکھنے والے اس روشنی کو دیکھ بھی رہے ہیں اور اس سے فائدہ بھی اٹھا رہے ہیں ۔ یہ تو خدا کی رحمت ہے کہ اس نے انسان کی رہنمائی کے لیے یہ کلام نازل کیا۔ کوئی اسے زحمت سمجھتا ہے تو یہ اس کی اپنی بد نصیبی ہے۔ خوش خبری ہے ان لوگوں کے لیے جواس سے فائدہ اٹھائیں ، اور ڈرنا چاہیے ان لوگو کو جو اس سے منہ موڑ لیں ۔ (2)۔ تم نے اگر اپنے دلوں پر غلاف چڑھا لیے ہیں اور اپنے کان بہرے کر لیے ہیں تو نبی کے سپرد یہ کام نہیں کیا گیا ہے کہ جو نہیں سننا چاہتا اسے سناٴئے اور جو نہیں سمجھنا چاہتا اس کے دل میں زبردستی اپنی بات اتارے۔ وہ تو تمہارے ہی جیسا ایک انسان ہے۔ سننے والوں ہی کو سنا سکتا ہے اور سمجھنے والوں ہی کو سمجھا سکتا ہے۔ (3)۔ تم چاہے اپنی آنکھیں اور کان بند کر لو اور اپنے دلوں پر غلاف چڑھا لو، مگر حقیقت یہی ہے کہ تمہارا خدا بس ایک ہی ہے اور تم کسی دوسرے کے بندے نہیں ہو۔ تمہاری ضد سے یہ حقیقت بہر حال نہیں بدل سکتی۔ مان لو گے اور اس کے مطابق اپنا عمل درست کر لو گے تو اپنا ہی بھلا کرو گے۔ نہ مانو گے تو خود ہی تباہی سے دوچار ہو گے۔ (4)۔ تمہیں کچھ احساس بھی ہے کہ یہ شرک اور کفر تم کس کے ساتھ کر رہے ہو؟ اس خدا کے ساتھ جس نے یہ اَتھاہ کائنات بنائی ہے، جو زمین و آسمان کا خالق ہے، جس کی پیدا کی ہوئی برکتوں سے اس زمین میں تم فائدہ اٹھا رہے ہو، اور جس کے مہیا کیے ہوٴئے رزق پر تم پل رہے ہو۔ اس کا شریک تم اس کی حقیر مخلوقات کو بناتے ہو، اور سمجھانے کی کوشش کی جاتی ہے تو ضد میں آ کر منہ موڑتے ہو۔ (5)۔اچھا، نہیں مانتے تو خبردار ہو جاؤ کہ تم پر اسی طرح کا عذاب اچانک ٹوٹ پڑنے کے لیے تیار ہے جیسا عاد اور ثمود پر آیا تھا۔ اور یہ عذاب بھی تمہارے جرم کی آخری سزا نہ ہو گا، بلکہ آگے میدان حشر کی باز پرس اور جہنم کی آگ ہے۔ (6)۔ بڑا ہی بدقسمت ہے وہ انسان جس کے ساتھ ایسے شیاطین جِن و انس لگ جائیں جو اسے ہر طرف ہرا ہی ہرا دکھاتے رہیں ، اسکی حماقتوں کو اس کے سامنے خوشنما بنا کر پیش کریں اور اسے کبھی خود صحیح بات سوچنے دیں ، نہ کسی دوسرے سے سننے دیں ۔ اس طرح کے نادان لوگ آج تو یہاں ایک دوسرے بڑھاوے چڑھاوے دے رہے ہیں ، اور ہر ایک دوسرے کی شہ پا کر نہلے پر دہلا مار رہا ہے، مگر قیامت کے راز جب شامت آٴئے گی تو ان میں سے ہر ایک کہے گا کہ جن لوگوں نے مجھے بہکایا تھا وہ میرے ہاتھ لگ جائیں تو انہیں پاؤں تلے روند ڈالوں ۔ (7)۔ یہ قرآن ایک اٹل کتاب ہے۔ اسے تم اپنی گھٹیا چالوں اور اپنے جھوٹ کے ہتھیاروں سے شکست نہیں دے سکتے۔ باطل خواہ سامنے سے آٴئے یا در پردہ اور بالواسطہ حملہ آور ہو، اسے زک دینے میں کبھی کامیاب نہ ہو سکے گا۔ (8)۔ آج تمہاری اپنی زبان میں یہ قرآن پیش کیا جا رہا ہے تاکہ تم اسے سمجھ سکو تو تم یہ کسی عجمی زبان میں آنا چاہیے تھا۔ لیکن اگر ہم تمہاری ہدایت کے لیے عجمی مذاق ہے، عرب قوم کی ہدایت کے لیے عجمی زبان میں کلام فرمایا جا رہا ہے جسے یہاں کوئی نہیں سمجھتا۔ اس کے معنی یہ ہیں کہ تمہیں دراصل ہدایت مطلوب ہی نہیں ہے۔ نہ ماننے کے لیے نت نئے بہانے تراش رہے ہو۔ (9)۔کبھی تم نے یہ بھی سوچا کہ اگر فی الواقع حقیقت یہی نکلی کہ یہ قرآن خدا کی طرف سے ہے تو اس کا انکار کر کے اور اس کی مخالفت میں اتنی دور تک جا کر تم کس انجام سے دوچار ہو گے۔ (10)۔ آج تم نہیں مان رہے ہو، مگر عنقریب تم اپنی آنکھوں سے دیکھ لو گے کہ اس قرآن کی دعوت تمام آفاق پر چھا گئی ہے،اور تم خود اس سے مغلوب ہو چکے ہو۔ اس وقت تمہیں پتہ چل جاٴئے گا کہ جو کچھ تم سے کہا جا رہا تھا، وہ حق تھا۔ مخالفین کو یہ جوابات دینے کے ساتھ ان مسائل کی طرف بھی توجہ فرمائی گئی ہے جو اس شدید مزاحمت کے ماحول میں اہل ایمان کو اور خود نبی صلی اللہ علیہ و سلم کو در پیش تھے۔ ایمان لانے والوں کے لیے اس وقت تبلیغ کرنا تو در کنار، ایمان کے راستے پر قائم رہنا بھی سخت دشوار ہو رہا تھا، اور ہر اس شخص کی جان عذاب میں آ جاتی تھی جس کے متعلق یہ ظاہر ہو جاتا تھا کہ وہ مسلمان ہو گیا ہے۔ دشمنوں کی خوفناک جتھہ بندی اور ہر طرف چھائی ہوئی طاقت کے مقابلے میں وہ اپنے آپ کو بالکل بے بس اور بے یار و مدد گا محسوس کر رہے تھے۔ اس حالت میں اول تو یہ کہہ کر ان کی ہمت بندھائی گئی کہ تم حقیقت میں بے یار مدد گار نہیں ہو، بلکہ جو شخص بھی ایک دفعہ خدا کو اپنا رب مان کر اس عقیدے اور مسلک پر مضبوطی کے ساتھ جم جاتا ہے، خدا کے فرشتے اس پر نازل ہوتے ہیں اور دنیا سے لے کر آخرت تک اس کا ساتھ دینے ہیں ۔ پھر یہ فرما کر انکا حوصلہ بڑھایا گیا کہ بہترین ہے وہ انسان جو خود نیک عمل کرے، دوسروں کو خدا کی طرف بلاٴئے، اور ڈٹ کر کہے کہ میں مسلمان ہوں ۔ نبی صلی اللہ علیہ و سلم کے سامنے اس وقت جو سوال سخت پریشان کن بنا ہوا تھا وہ یہ تھا کہ جب اس دعوت کی راہ میں ایسے سنگ گراں حائل ہیں تو ان چٹانوں سے تبلیغ کا راستہ آخر کیسے نکالا جاٴئے َ اس سوال کا حل آپ کو یہ بتایا گیا کہ یہ نمائشی چٹانیں بظاہر بڑی سخت نظر آتی ہیں ،مگر اخلاق حسنہ کا ہتھیار وہ ہتھیار ہے جو انہیں توڑ کر اور پگھلا کر رکھ دے گا۔صبر کے ساتھ اس سے کام لو، اور جب کبھی شیطان اشتعال دلا کر کسی دوسرے ہتھیار سے کام لینے پر اکساٴئے تو خدا سے پناہ مانگو۔','سید ابو اعلیٰ مودودیؒ -  تفہیم القرآن');
INSERT INTO "chapters_ur" VALUES(42,'شورٰی',NULL,' نام: آیت 38 کے فقرے وَاَمْرُ ھُمْ شُوْریٰ بَیْنَھُمْ سے ماخوذ ہے۔ اس کا نام کا مطلب ہے کہ وہ سورہ جس میں لفظ شوریٰ آیا ہے۔ زمانۂ نزول: کسی معتبر روایت سے معلوم نہیں ہو سکا ہے۔ لیکن اس مضمون پر غور کرنے سے صاف محسوس ہوتا ہے کہ یہ سورہ حٰم السجدہ کے متصلاً بعد نازل ہوئی ہو گی، کیونکہ یہ ایک طرح سے بالکل اس کا تتمہ نظر آتی ہے۔ اس کیفیت کو ہر وہ شخص خود محسوس کر لے گا جو پہلے سورہ حٰم السجدہ کو بغور پڑھے اور پھر اس سورے کی تلاوت کرے۔ وہ دیکھے گا کہ اس سورۃ میں سرداران قریش کی اندھی بہری مخالفت پر بڑی کاری ضربیں لگائی گئی تھیں ، تاکہ مکہ معظمہ اور اس کے گرد و پیش کے علاقے میں جس کسی کے اندر بھی اخلاق، شرافت اور معقولیت کی کوئی حس باقی ہو وہ جان لے کہ قوم کے بڑے لوگ کس قدر بے جا طریقے سے محمد صلی اللہ علیہ و سلم کی مخالفت کر رہے ہیں ، اور ان کے مقابلہ میں آپ کی بات کتنی سنجیدہ، آپ کا موقف کتنا معقول اور آپ کا رویہ کیسا شریفانہ ہے۔ اس تنبیہ کے معاً بعد یہ سورۃ نازل کی گئی جس نے تفہیم کا حق ادا کر دیا اور ایسے دل نشین انداز میں دعوت محمدی کی حقیقت سمجھائی جس کا اثر قبول نہ کرنا کسی ایسے شخص کے جس میں نہ تھا جو حق پسندی کا کچھ بھی مادہ اپنے اندر رکھتا ہو اور جاہلیت کی گمراہیوں کے عشق میں بالکل اندھا نہ ہو چکا ہو۔ موضوع اور مضمون: بات کا آغاز اس طرح کیا گیا ہے کہ تم لوگ ہمارے نبی کی پیش کردہ باتوں پر یہ کیا چہ میگوئیاں کرتے پھر رہے ہو۔ یہ باتیں کوئی نئی اور نرالی نہیں ہیں ، نہ یہی کوئی نادر واقعہ ہے جو تاریخ میں پہلی ہی مرتبہ پیش آیا ہو کہ ایک شخص پر خدا کی طرف سے وحی آئے اور اسے نبی نوع انسان کی رہنمائی کے لیے ہدایات دی جائیں۔ ایسی ہی وحی ، اسی طرح کی ہدایات کے ساتھ اللہ تعالیٰ اس سے پہلے انبیاء علیہم السلام پر پے در پے بھیجتا رہ ہے۔ اور نرالی، اچنبھے کے قابل بات یہ نہیں ہے کہ آسمان و زمین کے مالک کو معبود اور حاکم مانا جائے، بلکہ یہ ہے کہ اس کے بندے ہو کر، اس کی خدائی میں رہتے ہوئے کسی دوسرے کی خداوندی تسلیم کی جائے۔ تم توحید پیش کرنے والے پر بگڑ پڑیں تو کچھ بعید نہیں۔ تمہاری اس جسارت پر فرشتے حیران ہیں اور ہر وقت ڈورے ہیں کہ نہ معلوم کب تم پر خدا کا غضب ٹوٹ پڑے۔ اس کے بعد لوگوں کو بتایا گیا ہے کہ نبوت پر کسی شخص کا مقرر کیا جانا، اور اس شخص کا اپنے آپ کو نبی کی حیثیت سے پیش کرنا یہ معنی نہیں رکھتا کہ وہ خلق خدا کی قسمتوں کا مالک بنا دیا گیا ہے اور اسی دعوے کے ساتھ وہ میدان میں آیا ہے۔ قسمتیں تو اللہ نے اپنے ہی ہاتھ میں رکھی ہیں۔ نبی صرف غافلوں کو چونکا نے اور بھٹکے ہوؤں کو راستہ بتانے آیا ہے۔ اس کی بات نہ ماننے والوں کا محاسبہ کرنا اور انہیں عذاب دینا یا نہ دینا اللہ کا اپنا کام ہے۔ یہ کام نبی کے سپرد نہیں کر دیا گیا ہے۔ لہٰذا اس غلط فہمی کو اپنے دماغ سے نکال دو کہ نبی اس طرح کے کسی دعوے کے ساتھ آیا ہے جیسے دعوے تمہارے ہاں کے نام نہاد مذہبی پیشوا اور پیر فقیر کیا کرتے ہیں کہ جو ان کی بات نہ مانے گا، یا ان کی شان میں گستاخی کرے گا وہ اسے جلا کر بھسم کر دیں گے۔ اسی سلسلے میں لوگوں کو یہ بھی بتایا گیا ہے کہ نبی تمہاری بد خواہی کے لیے نہیں آیا ہے ، بلکہ ہو تو ایک خیر خواہ ہے جو تمہیں خبردار کر رہا ہے کہ جس راہ پر تم جا رہے ہو اس میں تمہاری اپنی تباہی ہے۔ پھر اس مسئلے کی حقیقت سمجھائی گئی ہے کہ اللہ نے سارے انسانوں کو پیدائشی طور پر راست رو کیوں نہ بنا دیا اور یہ مجال اختلاف کیوں رکھی جس کی وجہ سے لوگ فکر و عمل کے ہر الٹے سیدھے راستے پر چل پڑتے ہیں۔ بتایا گیا کہ اسی چیز کی بدولت تو یہ امکان پیدا ہوا ہے کہ انسان اللہ کی اس رحمت خاص کو پا سکے جو دوسری بے اختیار مخلوقات کے لیے نہیں ہے بلکہ صرف اس ذی اختیار مخلوق کے لیے ہے جو جبلی طور پر نہیں ، شعوری طور پر اپنے اختیار سے اللہ کو اپنا ولی (Patron guardian )بنائے۔ یہ روش جو انسان اختیار کرتا ہے اسے اللہ تعالیٰ سہارا دے کر ، اس کی رہنمائی کر کے ، اسے حسن عمل کی توفیق دے کر، اپنی رحمت خاص میں داخل کر لیتا ہے۔ اور جو انسان اپنے اختیار کو غلط استعمال کر کے ان کو ولی بناتا ہے جو در حقیقت ولی نہیں ہیں اور نہیں ہو سکتے ، وہ اس رحمت سے محروم ہو جاتے ہیں۔ اسی سلسلے میں یہ بھی بتایا گیا ہے کہ انسان کا اور ساری مخلوقات کا ولی حقیقت میں اللہ ہی ہے۔ دوسرے نہ حقیقت میں ولی ہیں ، نہ ان میں یہ طاقت ہے کہ ولایت کا حق ادا کر سکیں۔ انسان کی کامیابی کا مدار اسی پر ہے کہ وہ اپنے اختیار سے ولی کا انتخاب کرنے میں غلطی نہ کرے اور اسی کو اپنا ولی بنائے جو در حقیقت ولی ہے۔ اس کے بعد یہ بتایا گیا ہے کہ جس دین کو محمد صلی اللہ علیہ و سلم پیش کر رہے ہیں وہ حقیقت میں ہے کیا : اس کی اولین بنیاد یہ ہے کہ اللہ تعالیٰ چونکہ کائنات اور انسان کا خالق، مالک اور ولی حقیقی ہے ، اس لیے وہی انسان کا حاکم بھی ہے ، اور اسی کا یہ حق ہے کہ انسان کو دین اور شریعت (اعتقاد و عمل کا نظام) دے اور انسانی اختلافات کا فیصلہ کر کے بتائے کہ حق کیا ہے اور ناحق کیا۔ دوسری کسی ہستی کو انسان کے لیے شارع (Lawgiver) بننے کا سرے سے حق ہی نہیں ہے۔ با لفاظ دیگر فطری حاکمیت کی طرح تشریعی حاکمیت بھی اللہ کے لیے مخصوص ہے۔ انسان یا کوئی غیر اللہ اس حاکمیت کا حامل نہیں ہو سکتا۔ اور اگر کوئی شخص اللہ کی اس حاکمیت کو نہیں مانتا تو اس کا اللہ کی محض فطری حاکمیت کا ماننا لا حاصل ہے۔ اسی بنیاد پر اللہ تعالیٰ نے ابتدا سے انسان کے لیے ایک دین مقرر کیا ہے۔ وہ ایک ہی دین تھا جو ہر زمانے میں تمام انبیاء کو دیا جاتا رہا۔ کوئی نبی بھی اپنے کسی الگ مذہب کا بانی نہیں تھا۔ وہی ایک دین اول روز سے نسل انسانی کے لیے اللہ تعالیٰ کی طرف سے مقرر ہوتا رہا ہے ، اور سارے انبیاء اسی کے پیرو اور داعی رہے ہیں۔ وہ دین کبھی محض مان کر بیٹھ جانے کے لیے نہیں بھیجا گیا، بلکہ ہمیشہ اس غرض کے لیے بھیجا گیا ہے کہ زمین پر وہی قائم اور رائج اور نافذ ہو، اور اللہ کے ملک میں اللہ کے دین کے سوا کسی اور کے ساختہ و پرداختہ دین کا سکّہ نہ چلے۔ انبیاء علیہم السلام اس دین کی محض تبلیغ پر نہیں بلکہ اسے قائم کرنے کی خدمت پر مامور کیے گئے تھے۔ نوع انسانی کا اصل دین یہی تھا ، مگر انبیاء کے بعد ہمیشہ یہ ہوتا رہا کہ خود غرض لوگ اس کے اندر اپنی خود پسندی ، خود رائی اور خود نمائی کے باعث اپنے مفاد کی خاطر تفرقے برپا کر کر کے نئے نئے مذہب نکالتے رہے۔دنیا میں یہ جتنے بھی مختلف مذہب پائے جاتے ہیں ، سب اسی ایک دین کو بگاڑ کر پیدا کیے گئے ہیں۔ اب محمد صلی اللہ علیہ و سلم اس لیے بھیجے گئے ہیں کہ ان متفرق طریقوں اور مصنوعی مذہبوں اور انسانی ساخت کے دینوں کی جگہ وہی اصل دین لوگوں کے سامنے پیش کریں اور اسی کو قائم کرنے کی کوشش کریں۔ اس پر خدا کا شکر ادا کرنے کے بجائے اگر تم الٹے بگڑتے ہو اور لڑنے کو دوڑتے ہو تو یہ تمہاری نادانی ہے۔ تمہاری اس حماقت کی وجہ سے نبی اپنا کام نہیں چھوڑ دے گا۔ وہ اس بات پر مامور ہے کہ پوری استقامت کے ساتھ اپنے موقف پر جم جائے اور اس کام کو پورا کرے جس پر وہ مامور ہوا ہے۔ اس سے یہ امید نہ رکھو کہ وہ تمھیں راضی کرنے کے لیے دین میں انہی اوہام و خرافات اور جاہلیت کی رسموں اور طور طریقوں کے لیے کوئی گنجائش نکالے گا جن سے خدا کا دین پہلے خراب کیا جاتا رہا ہے۔ تم لوگوں کو یہ احساس نہیں ہے کہ اللہ کے دین کو چھوڑ کر غیر اللہ کے بنائے ہوئے دین و آئین کو اختیار کرنا اللہ کے مقابلے میں کتنی بڑی جسارت ہے۔ تم اپنے نزدیک اسے دنیا کا معمول سمجھ رہے ہو، اور تمھیں اس میں کوئی قباحت نظر نہیں آتی۔ مگر اللہ کے نزدیک یہ بدترین شرک اور شدید ترین جرم ہے جس کی سخت سزا اُن سب کو بھگتنی پڑے گی جنہوں نے اللہ کی زمین پر اپنا دین جاری کیا اور جنہوں نے ان کے دین کی پیروی اور اطاعت کی۔ اس طرح دین کا ایک صاف اور واضح تصور پیش کرنے کے بعد فرمایا گیا ہے کہ تم لوگوں کو سمجھا کر راہ راست پر لانے کے لیے جو بہتر سے بہتر طریقہ ممکن تھا وہ استعمال کیا جا چکا۔ایک طرف اللہ نے اپنی کتاب نازل فرمائی جو نہایت دل نشین طریقے سے تمہاری اپنی زبان میں تمھیں حقیقت بتا رہی ہے۔ اور دوسری طرح محمد صلی اللہ علیہ و سلم اور ان کے اصحاب کی زندگیاں تمہاری آنکھوں کے سامنے موجود ہیں جنہیں دیکھ کر تم جان سکتے ہو کہ اس کتاب کی رہنمائی میں کیسے انسان تیار ہوتے ہیں۔ اس پر بھی اگر تم ہدایت نہ پاؤ تو پھر دنیا میں کوئی چیز تمھیں راہ راست پر نہیں لا سکتی۔ اس کا نتیجہ تو پھر یہی ہے کہ تمھیں اسی گمراہی میں پڑا رہنے دیا جائے جس میں تم صدیوں سے مبتلا ہو، اور اسی انجام سے تم کو دوچار کر دیا جائے جو ایسے گمراہوں کے لیے اللہ کے ہاں مقدر ہے۔ ان حقائق کو بیان کرتے ہوئے بیچ بیچ میں اختصار کے ساتھ توحید اور آخرت کے دلائل دیے گئے ہیں دنیا پرستی کے نتائج پر متنبہ کیا گیا ہے ، آخرت کی سزا سے ڈرایا گیا ہے اور کفار کی ان اخلاقی کمزوریوں پر گرفت کی گئی ہے جو ہدایت سے ان کے منہ موڑنے کا اصل سبب تھیں۔ پھر کلام کو ختم کرتے ہوئے دو اہم باتیں ارشاد فرمائی گئی ہیں : ایک یہ کہ محمد صلی اللہ علیہ و سلم کا اپنی زندگی کے ابتدائی چالیس سال میں ’’ کتاب‘‘ کے تصور سے بالکل خالی الذہن اور ایمان کے مسائل و مباحث سے قطعی نا واقف رہنا، اور پھر یکایک ان دونوں چیزوں کو لے کر دنیا کے سامنے آ جانا ، آپ کا اپنی پیش کردہ تعلیم کو خدا کی تعلیم قرار دینا یہ معنی نہیں رکھتا کہ آپ خدا سے رو در رو کلام کرنے کے مدعی ہیں ، بلکہ خدا نے یہ تعلیم تمام انبیاء کی طرح آپ کو بھی تین طریقوں سے دی ہے۔ ایک وحی، دوسرے پردے کے پیچھے سے آواز ، اور تیسرے فرشتے کے ذریعہ سے پیغام۔ یہ وضاحت اس لیے کی گئی کہ مخالفین یہ الزام تراشی نہ کر سکیں کہ حضور خدا سے رو در رو کلام کرنے کا دعویٰ کر رہے ہیں ، اور حق پسند لوگ یہ جان لیں کہ اللہ تعالیٰ کی طرف سے جو انسان نبوت کے منصب پر سرفراز کیا گیا ہو اسے کن طریقوں سے ہدایت دی جاتی ہیں۔ ','سید ابو اعلیٰ مودودیؒ -  تفہیم القرآن');
INSERT INTO "chapters_ur" VALUES(43,'سونے کی چمک',NULL,' نام: آیت 35 کے لفظ : وَزُخْرُ فاً سے ماخوذ ہے ۔ مطلب یہ ہے کہ وہ سورہ جس میں لفظ زُخْرُفْ آیا ہے ۔ زمانۂ نزول: کسی معتبر روایت سے معلوم نہیں ہوسکا ہے ۔ لیکن اس کے مضامین پر غور کرنے سے صاف محسوس ہوتا ہے کہ یہ سورہ بھی اسی زمانے میں نزول ہوئی ہے جس میں المومن ،حٰم السجدہ اور الشوریٰ نازل ہوئیں۔ یہ ایک ہی سلسلے کی سورتیں معلوم ہوتی ہیں جن کا نزول اس وقت سے شروع ہوا جب کفار مکہ نبی صلی اللہ علیہ و سلم کی جان کے درپے ہوگئے تھے ۔ شب و روز اپنی محفلوں میں بیٹھ بیٹھ کر مشورے کر رہے تھے کہ آپ کو کس طرح ختم کیا جائے، اور ایک حملہ آپ کی جان پر ہو بھی چکا تھا۔ اس صورت حال کی طرف آیات 79۔80۔ میں صاف اشارہ موجود ہے ۔ موضوع اور مباحث: اس سورے میں پورے زور کے ساتھ قریش اور اہل عرب کے ان جاہلانہ عقائد و اوہام پر تنقید کی گئی ہے جن پر وہ اصرار کیے چلے جا رہے تھے ، اور نہایت محکم و دل نشین طریقے سے ان کی نا معقولیت کا پردہ فاش کیا گیا ہے ، تا کہ معاشرے کا ہر فرد، جس کے اندر کچھ بھی معقولیت موجود ہو، یہ سوچنے پر مجبور ہو جائے کہ آخر یہ کیسی جہالتیں ہیں جن سے ہماری قوم بری طرح چمٹی ہوئی ہے ، اور جو شخص ہمیں ان کے چکر سے نکالنے کی کوشش کر رہا ہے اس کے پیچھے ہاتھ دھو کر پڑ گئی ہے ۔ کلام کا آغاز اس طرح کیا گیا ہے کہ تم لوگ اپنی شرارتوں کے بل پر یہ چاہتے ہو کہ نزول روک دیا جائے، مگر اللہ نے کبھی اشرار کی وجہ سے انبیاء کی بعثت اور کتابوں کی تنزیل نہیں روکی ہے ، بلکہ ان ظالموں کو ہلاک کر دیا ہے جس اس کی ہدایت کا راستہ روک کر کھڑے ہوئے تھے ۔ یہی کچھ وہ اب بھی کرے گا۔ آگے چل کر آیات 41۔43۔ اور 79۔80 میں یہ مضمون پھر دہرایا گیا ہے ۔ جو لوگ نبی صلی اللہ علیہ و سلم کی جان کے درپے تھے ان کو سناتے ہوئے حضور سے فرمایا گیا ہے کہ تم خواہ زندہ رہو یا نہ رہو، ان ظالموں کو ہم سزا دے کر رہیں گے ۔ اور خود ان لوگوں کو صاف صاف متنبہ کر دیا گیا ہے کہ اگر تم نے ہمارے نبی کے خلاف ایک اقدام کا فیصلہ کر لیا ہے تو ہم بھی پھر ایک فیصلہ کن قدم اٹھائیں گے ۔ اس کے بعد بتایا گیا ہے کہ وہ مذہب کیا ہے جسے یہ لوگ سینے سے لگائے ہوئے ہیں، اور وہ دلائل کیا ہیں جن کے بل بوتے پر یہ محمد صلی اللہ علیہ و سلم کا مقابلہ کر رہے ہیں۔ خود مانتے ہیں کہ زمین و آسمان کا، اور ان کا اپنا اور ان کے معبودوں کا خالق اللہ تعالیٰ ہی ہے ۔ یہ بھی جانتے اور مانتے ہیں کہ جن نعمتوں سے یہ فائدہ اٹھا رہے ہیں وہ سب اللہ کی دی ہوئی ہیں۔ پھر بھی دوسروں کو اللہ کے ساتھ خدائی میں شریک کرنے پر اصرار کیے چلے جاتے ہیں۔ بندوں کو اللہ کی اولاد قرار دیتے ہیں ۔اور اولاد بھی بیٹیاں جنہیں خود اپنے لیے ننگ و عار سمجھتے ہیں۔ فرشتوں کو انہوں نے دیویاں قرار دے رکھا ہے ۔ ان کے بت عورتوں کی شکل کے بنا رکھے ہیں۔ انہیں زنانہ کپڑے اور زیور پہناتے ہیں اور کہتے ہیں کہ یہ اللہ کی بیٹیاں ہیں۔ ان کی عبادت کرتے ہیں اور ان ہی سے منتیں اور مرادیں مانگتے ہیں۔ آخر انہیں کیسے معلوم ہوا کہ فرشتے عورتیں ہیں ؟ ان جہالتوں پر ٹوکا جاتا ہے تو تقدیر کا بہانہ پیش کرتے ہیں اور کہتے ہیں کہ اگر اللہ ہمارے اس کام کو پسند نہ کرتا تو ہم کیسے ان بتوں کی پرستش کر سکتے تھے ۔ حالانکہ اللہ کی پسند اور نا پسند معلوم ہونے کا ذریعہ اس کی کتابیں ہیں نہ کہ وہ کام جو دنیا میں اس کی مشیت کے تحت ہو رہے ہیں۔ مشیت کے تحت تو ایک بت پرستی ہی نہیں ، چوری ، زنا، ڈاکہ، قتل، سب ہی کچھ ہو رہا ہے ۔ کیا اس دلیل سے ہر اس برائی کو جائز و برحق قرار دیا جائے گا جو دنیا میں ہو رہی ہے ؟ پوچھا جاتا ہے کہ اپنے اس شرک کے لیے تمہارے پاس اس غلط دلیل کے سوا کوئی اور سند بھی ہے ، تو جواب دیتے ہیں کہ باپ دادا سے یہ کام یوں ہی ہوتا چلا آ رہا ہے ۔ گویا ان کے نزدیک کسی مذہب کے حق ہونے کے لیے یہ کافی دلیل ہے ۔ حالانکہ ابراہیم علیہ السلام ، جن کی اولاد ہونے پر ہی ان کے سارے فخر و امتیاز کا مدار ہے ، باپ دادا کے مذہب کو لات کار کر گھر سے نکل گئے تھے اور انہوں نے اسلاف کی ایسی اندھی تقلید کو رد کر دیا تھا جس کا ساتھ کوئی دلیل معقول نہ دیتی ہو۔ پھر اگر ان لوگوں کو اسلاف کی تقلید ہی کرنی تھی تو اس کے لیے بھی اپنے بزرگ ترین اسلاف، ابراہیم و اسماعیل علیہما السلام کو چھوڑ کر انہوں نے اپنے جاہل ترین اسلاف کا انتخاب کیا ! ان سے کہا جاتا ہے کہ کیا کبھی کسی نبی نے اور خدا کی طرف سے آئی ہوئی کسی کتاب نے بھی یہ تعلیم دی ہے کہ اللہ کے ساتھ دوسرے بھی عبادت کے مستحق ہیں، تو یہ عیسائیوں کے اس فعل کو دلیل میں پیش کرتے ہیں کہ انہوں نے عیسیٰ ابن مریم کو ابنُ اللہ مانا اور ان کے پرستش کی ۔ حالانکہ سوال یہ نہ تھا کہ کسی نبی کی امت نے شرک کیا ہے یا نہیں،بلکہ یہ تھا کہ خود کسی نبی نے شرک کی تعلیم دی ہے ۔ ؟ عیسیٰ ابن مریمؑ نے کب کہا تھا کہ میں خدا کا بیٹا ہوں اور تم میری عبادت کرو۔ ان کی اپنی تعلیم تو وہی تھی جو دنیا کے ہر بنی نے دی ہے کہ میرا رب بھی اللہ ہے اور تمہارا رب بھی ، اسی کی تم عبادت کرو۔ محمد صلی اللہ علیہ و سلم کی رسالت تسلیم کرنے میں انہیں تامل ہے تو اس بنا پر کہ ان پاس مال و دولت اور ریاست وجاہت تو ہے ہی نہیں۔ کہتے ہیں کہا اگر خدا ہمارے ہاں کسی کو نبی بنانا چاہتا تو ہمارے دونوں شہروں (مکہ و طائف ) کے بڑے آدمیوں میں سے کسی کو بناتا ۔ اسی بنا پر فرعون نے بھی حضرت موسیٰ کو حقیر جانا تھا اور کہا تھا کہ آسمان کا بادشاہ اگر مجھ زمین کے بادشاہ کے پاس کوئی ایلچی بھیجتا تو اسے سونے کے کنگن پہنا کر، فرشتوں کی ایک فوج اس کی اردلی میں دے کر بھیجتا۔ یہ فقیر کہاں سے آ کھڑا ہوا ؟ فضیلت مجھے حاصل ہے کہ مصر کی بادشاہی میری ہے اور دریائے نیل کی نہریں میری ماتحتی میں چل رہی ہیں۔ یہ شخص میرے مقابلے میں کیا حیثیت رکھتا ہے کہ نہ مال رکھتا ہے نہ اقتدار۔ اس طرح کفار کی ایک ایک جاہلانہ بات پر تنقید کرنے اور اس کے نہایت معقول و مد لل جوابات دینے کے بعد آخر میں صاف صاف کہا گیا ہ کہ نہ خدا کی کوئی اولاد ہے ، نہ آسمان و زمین کے خدا الگ الگ ہیں، نہ اللہ کے ہاں کوئی ایسا شفیع ہے جو جان بوجھ کر گمراہی اختیار کرنے والوں کے اس کی سزا سے بچا سکے اللہ کی ذات اس سے منزّہ ہے کہ کوئی اسکی اولاد ہو۔ وہی اکیلا ساری کائنات کا خدا ہے ، باقی سب اس کے بندے ہیں نہ کہ اس کے ساتھ خدائی صفات و اختیارات میں شریک۔ اور شفاعت اس کے ہاں صرف وہی کر سکتے ہیں جو خود حق پرست ہوں، اور ان ہی کے لیے کر سکتے ہیں جنہوں نے دنیا میں حق پرستی اختیار کی ہو۔ ','سید ابو اعلیٰ مودودیؒ -  تفہیم القرآن');
INSERT INTO "chapters_ur" VALUES(44,'دھواں',NULL,' نام: آیت نمبر 10 ، یَوْ مَ تَأ تِی السَّمَآ ءُ بِدُ خَانٍ مُّبِینٍ کے لفظ دُخان کو اس سورۃ کا عنوان بنایا گیا ہے، یعنی یہ وہ سورۃ ہے جس میں لفظ دخان وارد ہوا ہے۔ زمانۂ نزول: اس زمانہ نزول بھی کسی معتبر روایت سے معلوم نہیں ہوتا ، مگر مضامین کی اندرونی شہادت بتاتی ہے کہ یہ بھی اسی دور میں نازل ہوئی ہے جس میں سورہ زُخْرُف اور اس سے پہلے کی چند سورتیں نازل ہوئی تھیں، البتہ یہ ان سے کچھ متأخّر ہے۔ تاریخی پس منظر یہ ہے کہ جب کفار کی مخالفانہ روش شدید سے شدید تر ہوتی چلی گئی تو نبی صلی اللہ علیہ وسلم نے دعا کی کہ خدایا ، یوسفؑ کے قحط جیسے ایک قحط سے میری مدد فرما ۔ حضورؐ کا خیال یہ تھا کہ جب ان لوگوں پر مصیبت پڑے گی تو انہیں خدا یاد آئے گا اور ان کے دل نصیحت قبول کرنے کے لیے نرم پڑ جائیں گے۔ اللہ تعالیٰ نے آپ کی دعا قبول فرمائی اور سارے علاقے میں ایسے زور کا قحط پڑا کہ لوگ بلبلا اٹھے۔ آخر کر بعض سرداران قریش ، جن میں حضرت عبداللہ بن مسعود نے خاص طور پر ابو سفیان کا نام لیا ہے، حضور کے پاس آئے اور آپ سے درخواست کی کہ اپنی قوم کو اس بلا سے نجات دلانے کے لیے اللہ سے دعا کریں۔ یہی موقع ہے جب اللہ تعالیٰ نے یہ سورۃ نازل فرمائی ۔ موضوع اور مباحث: اس موقع پر کفار مکہ کی فہمائش اور تنبیہ کے لیے جو خطبہ نبی صلی اللہ علیہ وسلم پر نازل فرمایا گیا اس کی تمہید چند اہم مباحث پر مشتمل ہے : اول یہ کہ تم لوگ اس قرآن کو محمد صلی اللہ علیہ وسلم کی تصنیف سمجھنے میں غلطی کر رہے ہو۔ یہ کتاب تو اپنی ذات میں خود اس امر کی بیّن شہادت ہے کہ یہ کسی انسان کی نہیں بلکہ خداوند عالم کی کتاب ہے۔ دوسرے یہ کہ تم اس کتاب کی قدر و قیمت سمجھنے میں بھی غلطی کر رہے ہو۔ تمہارے نزدیک یہ ایک بلا ہے جو تم پر نازل ہو گئی ہے۔ حالانکہ در حقیقت وہ گھڑی انتہائی مبارک گھڑی تھی جب اللہ تعالیٰ نے سراسر اپنی رحمت کی بنا پر تمہارے ہاں اپنا رسول بھیجنے اور اپنی کتاب نازل کرنے کا فیصلہ فرمایا۔ تیسرے یہ کہ تم اپنی نادانی سے اس غلط فہمی میں پڑے ہوئے ہو کہ اس رسول اور اس کتاب سے لڑ کر تم جیت جاؤ گے ۔ حالانکہ اس رسول کی بعثت اور اس کتاب کی تنزیل اس ساعت خاص میں ہوئی ہے جب اللہ تعالیٰ قسمتوں کے فیصلے فرمایا کرتا ہے۔ اور اللہ کے فیصلے بودے نہیں ہوتے کہ جس کا جی چاہے انہیں بدل ڈالے، نہ وہ کسی جہالت و نادانی پر مبنی ہوتے ہیں کہ ان میں غلطی اور خامی کا کوئی احتمال ہو۔ وہ تو اس فرمانروائے کائنات کے پختہ اور اٹل فیصلے ہوتے ہیں جو سمیع و علیم اور حکیم ہے۔ ان سے لڑنا کوئی کھیل نہیں ہے۔ چوتھے یہ کہ اللہ کو خود بھی زمین و آسمان اور کائنات کی ہر چیز کا مالک و پروردگار مانتے ہو اور یہ بھی مانتے ہو کہ زندگی و موت اسی کے اختیار میں ہے۔ مگر اس کے باوجود تمہیں دوسروں کو معبود بنانے پر اصرار ہے اور اس کے لیے حجت تمہارے پاس اس کے سوا کچھ نہیں ہے کہ باپ دادا کے وقتوں سے یہی کام ہوتا چلا آ رہا ہے ۔ حالانکہ اگر کوئی شخص شعور کے ساتھ یہ یقین رکھتا ہو کہ اللہ ہی مالک و پروردگار اور زندگی و موت کا مختار ہے تو اسے کبھی یہ شبہ تک لاحق نہیں ہو سکتا کہ معبود ہونے کے مستحق اس کے سوا یا اس کے ساتھ دوسرے بھی ہو سکتے ہیں۔ تمہارے باپ دادا نے اگر یہ حماقت کی تھی تو کوئی وجہ نہیں کہ تم بھی آنکھیں بند کر کے اسی کا ارتکاب کرتے چلے جاؤ ۔ حقیقت میں تو ان کا رب بھی اکیلا وہی خدا تھا جو تمہارا رب ہے ، اور انہیں بھی اسی ایک کی بندگی کرنی چاہیے تھی جس کی بندگی تمہیں کرنی چاہیے۔ پانچویں یہ کہ اللہ کی ربوبیت و رحمت کا تقاضا صرف یہی نہیں ہے کہ تمہارا پیٹ پالے بلکہ یہ بھی ہے کہ تمہاری رہنمائی کا انتظام کرے۔ اسی رہنمائی کے لیے اس نے رسول بھیجا ہے اور کتاب نازل کی ہے۔ اس تمہید کے بعد اس قحط کے معاملے کو لیا گیا ہے جو اس وقت در پیش تھا۔ جیسا کہ ہم اوپر بیان کر چکے ہیں، یہ قحط نبی صلی اللہ علیہ وسلم کی استدعا پر آیا تھا، اور حضورؐ نے اس کے لیے دعا اس خیال سےکی تھی کہ مصیبت پڑے گی تو کفار کی اکڑی ہوئی گردنیں ڈھیلی پڑ جائیں گی ، شاید کہ پھر حرف نصیحت ان پر کار گر ہو۔ یہ توقع اس وقت کسی حد تک پوری ہوتی نظر آ رہی تھی، کیونکہ بڑے بڑے ہیکڑ دشمنان حق کال کے مارے پکار اٹھے تھے کہ پروردگار، یہ عذاب ہم پر سے ٹال دے تو ہم ایمان لے آئیں گے۔ اس پر ایک طرف نبی صلی اللہ علیہ وسلم ظاہرسے فرمایا گیا ہے کہ ایسی مصیبتوں سےیہ لوگ کہاں سبق لینے والے ہیں ، انہوں نے جب اس رسول کی طرف سے منہ موڑ لیا جس کی زندگی سے، جس کے کردار سے اور جس کے کام اور کلام سے علانیہ ہو رہا ہے کہ وہ یقیناً خدا کا رسول ہے، تو اب محض ایک قحط ان کی غفلت کیسے دور کر دے گا۔ دوسرےطرف کفار کو مخاطب کرتے ہوئے ارشاد ہوا ہے کہ تم بالکل جھوٹ کہتے ہو کہ یہ عذاب تم پر سے ٹال دیا جائے تو تم ایمان لے آؤ گے ۔ ہم اس عذاب کو ہٹائے دیتے ہیں، ابھی معلوم ہوا جاتا ہے کہ تم اپنے اس وعدے میں کتنے سچے ہو۔ تمہارے سر پر تو شامت کھیل رہی ہے ۔ تم ایک بڑی ضرب مانگ رہے ہو، ہلکی چوٹوں سے تمہارا دماغ درست نہیں ہو گا۔ اسی سلسلے میں آگے چل کر فرعون اور اس کی قوم کا حوالہ دیا گیا ہے کہ ان لوگوں کو بھی ٹھیک یہی آزمائش پیش آئی تھی جس سے اب کفار قریش کے سرداروں کو سابقہ پڑا ہے۔ ان کے پاس بھی ایسا ہی ایک معزز رسول آیا تھا۔انہوں نے بھی وہ صریح علامات اور نشانیاں دیکھ لی تھیں جن سے اس کا مامور من اللہ ہونا صاف ظاہر ہو رہا تھا۔ وہ بھی نشانی پر نشانی دیکھتے چلے گئے مگر اپنی ضد سے باز نہ آئے ۔یہاں تک کہ آخر کار رسول کی جان لینے کے درپے ہو گئے اور نتیجہ وہ کچھ دیکھا جو ہمیشہ کے لیے سامان عبرت بن گیا۔ اس کے بعد دوسرا موضوع آخرت کا لیا گیا ہے جس سےکفار مکہ کو شدت کے ساتھ انکار تھا۔ وہ کہتے تھے کہ ہم نے کسی کو مرنے کے بعد دوبارہ اٹھ کر آتے نہیں دیکھا ہے، تم اگر دوسری زندگی کے دعوے میں سچے ہو تا اٹھا لاؤ ہمارے باپ دادا کو۔ اس کے جواب میں عقیدہ آخرت کی دو دلیلیں مختصر طور پر دی گئی ہیں۔ ایک یہ کہ اس عقیدے کا انکار ہمیشہ اخلاق کے لیے تباہ کن ثابت ہوتا رہا ہے ۔ دوسرے یہ کہ کائنات کسی کھلنڈرے کا کھلونا نہیں ہے ، بلکہ ایک حکیمانہ نظام ہے ، اور حکیم کا کوئی کام عبث نہیں ہوتا ۔ پھر کفار کے اس مطالبہ کا کہ اٹھا لاؤ ہمارے باپ دادا کو، یہ جواب دیا گیا ہے کہ یہ کام روز روز ہر ایک کے مطالبہ پر نہیں ہو گا بلکہ اس کے لیے اللہ نے ایک وقت مقرر فرما دیا ہے جب وہ تمام نوع انسانی بیک وقت جمع کرے گا اور اپنی عدالت میں ان کا محاسبہ فرمائے گا۔ اس وقت کی اگر کسی کو فکر کرنی ہو تو کر لے، کیونکہ وہاں کوئی نہ اپنے زور پر بچ سکے گا نہ کسی کے بچائے بچے گا۔ اللہ کی اس عدالت کا ذکر کرتے ہوئے بتایا گیا ہے کہ جو لوگ وہاں مجرم قرار پائیں گے ان کا انجام کیا ہو گا، اور جو وہاں سے کامیاب ہو کر نکلیں گے وہ کیا انعام پائیں گے۔پھر یہ کہہ کر بات ختم کر دی گئی ہے کہ تم لوگوں کو سمجھانے کے لیے یہ قرآن صاف سیدھی زبان میں اور تمہاری اپنی زبان میں نازل کر دیا گیا ہے، اب اگر تم سمجھانے سے نہیں سمجھتے اور انجام بدی دیکھنے پر مصر ہو تو انتظار کو، ہمارا نبی بھی منتظر ہے، جو کچھ ہونا ہے وہ اپنے وقت پر سامنے آ جائے گا۔ ','سید ابو اعلیٰ مودودیؒ -  تفہیم القرآن');
INSERT INTO "chapters_ur" VALUES(45,'زانوٗوں کے بل گری ہوئی',NULL,' نام: آیت 28 کے فقرے وَتَرٰی کُلَّ اُمَّۃٍ جَاثِیَۃً سے ماخوذ ہے۔ مطلب یہ ہے کہ وہ سورۃ جس میں لفظ جاثیہ آیا ہے ۔ زمانہ نزول : اس سورۃ کا زمانہ نزول بھی کسی معتبر روایت میں بیان نہیں ہوا ہے۔ مگر اس کے مضامین سے صاف محسوس ہوتا ہے کہ یہ سورہ دُخان کے بعد قریبی زمانے میں نازل ہوئی ہے۔ دونوں سورتوں کے مضامین میں ایسی مشابہت ہے جس سے یہ دونوں توام نظر آتی ہیں۔ موضوع اور مباحث: اس کا موضوع توحید و آخرت کے متعلق کفار مکہ کے شبہات و اعتراضات کا جواب دینا اور اس رویے پر ان کو متنبہ کرنا ہے جو انہوں نے قرآن کی دعوت کے مقابلے میں اختیار کر رکھا تھا۔ کلام کا آغاز توحید کے دلائل سے کیا گیا ہے۔ اس سلسلے میں انسان کے اپنے وجود سے لے کر زمین و آسمان تک ہر طرف پھیلی ہوئی بے شمار نشانیوں کی طرف اشارہ کر کے بتایا گیا ہے کہ تم جدھر بھی نگاہ اٹھا کر دیکھو،ہر چیز اسی توحید کی شہادت دے رہی ہے جسے ماننے سے تم انکار کر رہے ہو۔ یہ طرح طرح کے حیوانات، یہ شب و روز، یہ بارشیں اور ان سے اگنے والی نباتات، یہ ہوائیں، اور یہ انسان کی اپنی پیدائش، ان ساری چیزوں کو اگر کوئی شخص آنکھیں کھول کر دیکھے اور کسی تعصب کے بغیر اپنی عقل کو سیدھے طریقہ سے استعمال کر کے ان پر غور کرے تو یہ نشانیاں سے اس امر کا یقین دلانے کے لیے بالکل کافی ہیں کہ یہ کائنات بے خدا نہیں ہے، نہ بہت سے خداؤں کی خدائی میں چل رہی ہے، بلکہ ایک ہی خدا نے اسے بنایا ہے، اور وہی اکیلا اس کا مدبر اور فرماں روا ہے۔ البتہ اس شخص کی بات دوسرے ہے جو نہ ماننے کی قسم کھا کر بیٹھ گیا ہو، یا شکوک و شبہات ہی میں پڑے رہنے کا فیصلہ کر چکا ہو۔ اسے دنیا میں کہیں سے بھی یقین و ایمان کی دولت حاصل نہیں ہوسکتی َ آگے چل کر دوسرے رکوع کی ابتدا میں پھر فرمایا گیا ہے کہ انسان اس دنیا میں جتنی چیزوں سے کام لے رہا ہے، اور جو بے حد حساب اشیاء اور قوتیں اس کائنات میں اس کے مفاد کی خدمت کر رہی ہیں، وہ آپ سے آپ کہیں سے نہیں آ گئی ہیں، نہ دیویوں اور دیوتاؤں نے انہیں فراہم کیا ہے، بلکہ وہ ایک ہی خدا ہے جس نے یہ سب کچھ اپنے پاس سے اس کو بخشا اور اس کے لیے مسخر کر دیا ہے۔ کوئی شخص صحیح غور و فکر سے کام لے تو اس کی اپنی عقل ہی پکار اٹھے گی کہ وہی خدا انسان کا محسن ہے اور اسی کا یہ حق ہے کہ انسان اس کا شکر گزار ہو۔ اس کے بعد کفار مکہ کو اس ہٹ دھرمی، استکبار، استہزاء اور اصرار علی الکفر پر سخت ملامت کی گئی ہے جس سے وہ قرآن کی دعوت کا مقابلہ کر رہے تھے، اور انہیں خبردار کیا گیا ہے کہ یہ قرآن وہی نعمت لے کر آیا ہے جو پہلے بنی اسرائیل کو دی گئی تھی، جس کی بدولت وہ تمام اقوام علام پر فضیلت کے مستحق ہوئے تھے۔ انہوں نے جب اس نعمت کی ناقدری کی اور دین میں اختلاف کر کے اسے کھو دیا، تو اب یہ دولت تمہارے ہاں بھیجی گئی ہے۔ یہ ایک ایسا ہدایت نامہ ہے جو دین کی صاف شاہراہ انسان کو دکھاتا ہے۔ جو لوگ اپنی جہالت و حماقت سے اس کو رد کریں گے وہ اپنی ہی تباہی کا سامان کریں گے۔ اور خدا کی تائید و رحمت کے مستحق صرف وہ لوگ ہوں گے جو اس کی پیروی قبول کر کے تقویٰ کی روش پر قائم ہو جائیں۔ اسی سلسلے میں رسول اللہ صلی اللہ علیہ و سلم کے متبعین کو ہدایت کی گئی ہے کہ یہ خدا سے بے خوف لوگ تمہارے ساتھ جو بیہودگیاں کر رہے ہیں ان پر در گزر اور تحمل سے کام لو۔ تم صبر کرو گے تو خدا خود ان سے نمٹے گا اور تمہیں اس صبر کا اجر عطا فرمائے گا۔ پھر عقیدہ آخرت کے متعلق کفار کے جاہلانہ خیالات پر کلام کیا گیا ہے۔وہ کہتے تھے کہ زندگی بس اسی دنیا کی زندگی ہے، اس کے بعد کوئی دوسرے زندگی نہیں ہے۔ ہم گردش ایام سے بس اسی طرح مرتے ہیں جس طرح ایک گھڑی چلتے چلتے رک جائے۔ موت کے بعد کوئی روح باقی نہیں رہتی جسے قبض کیا جاتا ہو اور پھر کسی وقت دوبارہ لا کر انسانی جسم میں پھونک دیا جائے۔ اس چیز کا اگر تمہیں دعویٰ ہے تو ہمارے مرے ہوئے آبا و اجداد کو زندہ کر کے دکھاؤ۔ اس کے جواب میں اللہ تعالیٰ نے پے در پے چند دلائل ارشاد فرمائے ہیں: ایک یہ کہ تم یہ بات کسی علم کی بنا پر نہیں کہہ رہے ہو بلکہ محض گمان کی بنیاد پر اتنا بڑا حکم لگا بیٹھے ہو۔ کیا فی الواقع تمہیں یہ علم ہے کہ مرنے کے بعد کوئی دوسری زندگی نہیں ہے اور روحیں قبض نہیں کی جاتیں بلکہ فنا ہو جاتی ہیں؟ دوسرے یہ کہ تمہارے اس دعوے کی بنیاد زیادہ سے زیادہ بس یہ ہے کہ تم نے کسی مرنے والے کو اٹھ کر دنیا میں آتے نہیں دیکھا ہے۔ کیا یہ بات اتنا بڑا دعویٰ کر دینے کے لیے کافی ہے کہ مرنے والے پھر کبھی نہیں اٹھیں گے؟ کیا تمہارے تجربے اور مشاہدے میں کسی چیز کا نہ آنا یہ معنی رکھتا ہے کہ تمہیں اس چیز کے نہ ہونے کا علم حاصل ہے؟ تیسرے یہ کہ یہ بات سراسر عقل اور انصاف کے خلاف ہے کہ نیک اور بد، فرمانبردار اور نافرمان، ظالم اور مظلوم، آخرت کار سب یکساں کر دیے جائیں، کسی بھلائی کا کوئی اچھا نتیجہ اور کسی برائی کا کوئی برا نتیجہ نہ نکلے، نہ کسی مظلوم کی داد رسی ہو اور نہ کوئی ظالم اپنے کیے کی سزا پائے، بلکہ سب ایک ہی انجام سے دوچار ہوں۔ خدا کی اس کائنات کے متعلق جس نے یہ تصور قائم کیا ہے اس نے بڑا ہی غلط تصور قائم کیا ہے۔ اس تصور کو ظالم اور بد کار لوگ تو اس لیے اختیار کرتے ہیں کہ وہ اپنے افعال کا برا نتیجہ نہیں دیکھنا چاہتے، لیکن خدا کی یہ خدائی اندھیر نگری نہیں ہے بلکہ یہ ایک برحق نظام ہے جس میں نیک و بد کو بالآخر یکساں کر دینے کا ظلم ہر گز نہیں ہو سکتا۔ چوتھے یہ کہ انکار آخرت کا عقیدہ اخلاق کے لیے سخت تباہ کن ہے۔ اس کو اختیار وہی لوگ کرتے ہیں جو اپنے نفس کے بندے بنے ہوئے ہیں، اور اس لیے کرتے ہیں کہ انہیں بندگیٔ نفس کی کھلی چھوٹ مل جائے۔ پھر جب وہ اس عقیدے کو اختیار کر لیتے ہیں تو یہ انہیں گمراہ سے گمراہ تر کرتا چلا جاتا ہے، یہاں تک کہاں کی اخلاقی حِس بالکل مردہ ہو جاتی ہے اور ہدایت کے تمام دروازے ان کے لیے بند ہو جاتے ہیں۔ یہ دلائل دینے کے بعد اللہ تعالیٰ پورے زور کے ساتھ فرماتا ہے کہ جس طرح تم آپ سے آپ زندہ نہیں ہو گئے ہو، بلکہ ہمارے زندہ کرنے سے زندہ ہوئے ہو، اسی طرح تم آپ سے آپ نہیں مر جاتے، بلکہ ہمارے موت دینے سے مرتے ہو، اور ایک وقت یقیناً ایسا آنا ہے جب تم سب بیک وقت جمع کیے جاؤ گے۔ اس بات کو اگر آج تم اپنی جہالت و نادانی سے نہیں مانتے تو نہ مانو، جب وہ وقت آ جائے گا تو تم خود اپنی آنکھوں سے دیکھ لو گے کہ اپنے خدا کے حضور پیش ہو اور تمہاری پورا نامہ اعمال بے کم و کاست تیار ہے جو تمہارے ایک ایک کرتوت کی شہادت دے رہا ہے۔ اس وقت تم کو معلوم ہو جائے گا کہ عقیدہ آخرت کا یہ انکار اور اس کا یہ مذاق جو تم اُڑا رہے ہو، تمہیں کس قدر مہنگا پڑا ہے۔ ','سید ابو اعلیٰ مودودیؒ -  تفہیم القرآن');
INSERT INTO "chapters_ur" VALUES(46,'ریگستان',NULL,' نام: آیت نمبر 21 کے فقرے اِذْاَنْذَ رَقَوْمَہٗ بِالْاَحْقَافِ سے ماخوذ ہے۔ زمانۂ نزول: ایک تاریخی واقعہ سے متعین ہو جاتا ہے جس کا ذکر آیت 29۔32 میں آ یا ہے۔ ان آیات میں جِنوں کے آ نے اور قرآن سن کر واپس جانے کا جو واقعہ بیان ہوا ہے وہ حدیث وسیرت کی متفق علیہ روایت کی رو سے اس وقت پیش آیا تھا جب نبی صلی اللہ علیہ و سلم طائف سے مکہ معظمہ کی طرف پلٹتے ہوئے نَخْلہ کے مقام پر ٹھہرے تھے، اور تمام معتبر تاریخی روایت کے مطابق آپ کے طائف تشریف لے جانے کا واقعہ ہجرت سے تین سال پہلے کا ہے، لہٰذا یہ متعین ہو جاتا ہے کہ یہ سورۃ 10نبوی کے آخر، یا 11نبوی میں نازل ہوئی۔ تاریخی منظر: نبوی حضورؐ کی حیات طیبہ میں انتہائی سختی کا سال تھا۔ تین برس سے قریش کے تمام قبیلوں نے مل کر نبی ہاشم اور مسلمانوں کا مکمل مقاطعہ ر رکھا تھا اور حضور اپنے خاندان اور اپنے اصحاب کے ساتھ شعیب ابی طالب (شعب ابی طالب مکہ معظمہ کے ایک محلے کا نام تھا جس میں بنی ہاشم رہا کرتے تھے۔ شعب عربی زبان میں گھاٹی کو کہتے ہیں۔ چونکہ یہ محلہ کوہ اب قبیس کی گھاٹیوں میں سے ایک گھاٹی میں واقع تھا، اور ابو طالب نبی ہاشم کے سردار تھے، اس لیے اسے شعب ابی طالب کہا جاتا تھا۔ مکہ معظمہ میں جو مکان آج مقامی روایات کے مطابق نبی صلیی اللہ علیہ و سلم کے مقام پیدائش کی حیثیت سے معروف ہے،اسی کے قریب یہ گھاٹی واقع تھی۔ اب اسے شعب علی یا شعب بنی ہاشم کہتے ہیں۔)میں محور تھے۔ قریش کے لوگوں نے ہر طرف سے اس محلے کی ناکہ بندی کر رکھی تھی جس سے گزر کر کسی قسم کی رسد اندر نہ پہنچ سکتی تھی۔ صرف حج کے زمانے میں یہ محصورین نکل کر کچھ خریداری کر سکتے تھے۔ مگر ابو لہب جب بھی ان میں سے کسی کو بازار کی طرف، یا کسی تجارتی قافلے کی طرف جاتے دیکھتا، پکار کر تاجروں سے کہہ دیتا کہ جو چیز یہ خریدنا چاہیں اس کی قیمت اتنی زیادہ بتاؤ کہ یہ نہ خرید سکیں، پھر وہ چیز ہاشم کی کمر توڑ کر رکھ دی تھی اور ان پر ایسے ایسے سخت وقت گزر گئے تھے جن میں بسا اوقات گھاس اور پتے کھانے کی نوبت آ جاتی تھی۔ خدا خدا کر کے یہ محاصرہ اس سال ٹوٹا ہی تھا کہ حضورؐ کے چچا ابو طالب، جو دس سال سے آپؐ کے لیے ڈھال بنے ہوئے تھے، وفات پا گئے، اور اس سانحے پر بمشکل ایک مہینہ گزرا تھا کہ آپ کی رفیقہ حیات حضرت خدیجہ بھی انتقال فرما گئیں جن کی ذات آغاز نبوت سے لے کر اس وقت تک آپ کے لیے وجہ سکون و تسلی بنی رہی تھی۔ ان پے درپے صدموں اور تکلیفوں کی وجہ سے حضورؐ اس سال کو عام الحزن (رنج و غم کا سال ) فرمایا کرتے تھے۔ حضرت خدیجہ اور ابوطالب کی وفات کے بعد کفار مکہ نبی صلی اللہ ولیہ و سلم کے مقابلے میں اور زیادہ دلیر ہو گئے۔ پہلے سے زیادہ آپ کو تنگ کرنے لگے۔ حتیٰ کہ آپ کا گھر سے باہر نکلنا بھی مشکل ہو گیا۔ اسی زمانہ کا یہ واقعہ ابن ہشام نے بیان کیا ہے کہ ایک روز قریم کے اوباشوں میں سے ایک شخص نے سر بازار آپ کے سر پر مٹی پھینک دی۔ آخر کار آپ اس ارادے سے طائف تشریف لے گئے کہ بنی ثَقیف کو اسلام کی طرف دعوت دین اور اگر وہ اسلام نہ قبول کریں تو انہیں کم از کم اس بات پر آمادہ کریں کہ وہ آپ کو اپنے ہاں چین سے بیٹھ کر کام کرنے کا موقع دے دیں۔ آپ کو اس وقت کوئی سواری تک میسر نہ تھی۔ مکہ سے طائف تک کا سارا سفر آپ نے پیدل طے کیا۔ بعض روایات کی رو سے آپ تنہا تشریف لے گئے تھے، اور بعض روایات کے مطابق آپ کے ساتھ صرف حضرت زید بن حارثہ تھے۔ وہاں پہنچ کر چند روز آپؐ نے قیام کیا اور ثقیف کے سرداروں اور معززین میں سے ایک ایک کے پاس جا کر بات کی۔ مگر انہوں نے نہ صرف یہ کہ آپ کی کوئی بات نہ مانی، بلکہ آپ کو صاف صاف نوٹس دے دیا کہ ان کے شہر سے نکل جائیں، کیونکہ ان کو اندیشہ ہو گیا تھا کہ کہیں آپ کی تبلیغ ان کے نوجوانوں کو ’’ بگاڑ‘‘ نہ دے۔ مجبوراً آپ کو طائف چھوڑ دینا پڑا۔ جب آپ وہاں سے نکلنے لگے تو ثقیف کے سرداروں نے اپنے ہاں کے لفنگوں کو آپ کے پیچھے لگا دیا۔ وہ راستے کے دونوں طرف دور تک آپ پر آوازے کستے، گالیاں دیتے اور پتھر مارتے چلے گئے، یہاں تک کہ آپ زخموں سے چور ہو گئے اور آپ کی جوتیاں خون سے بھر گئیں۔ اس حالت میں آپ طائف کے باہر ایک باغ کی دیوار سے سائے میں بیٹھ گئے اور اپنے رب سے عرض کیا: ’’خداوندا، میں تیرے ہی حضور اپنی بے بسی و بے چارگی اور لوگوں کی نگاہ میں اپنی بے قدری کا شکوہ کرتا ہوں۔ اے ارحم الراحمین، تو سارے ہی کمزوروں کا رب ہے اور میرا رب بھی تو ہی ہے۔ مجھے کس کے حوالے کر رہا ہے؟ کیا کسی بیگانے کے حوالے جو مجھ سے درشتی کے ساتھ پیش آئے؟ یا کسی دشمن کے حوالے جو مجھ پر قابو پالے؟ اگر تو مجھ سے ناراض نہیں ہے تو مجھ کسی مصیبت کی پروا نہیں، مگر تیری طرف سے عافیت مجھے نصیب ہو جائے تو اس میں میرے لیے زیادہ کشادگی ہے۔ میں پناہ مانگتا ہوں تیری ذات کے اس نور کی جو اندھیرے میں اجالا اور دنیا اور آخرت کے معاملات کو درست کرتا ہے، مجھے اس سے بچا لے کہ تیرا غضب مجھ پر نازل ہو یا میں تیرے عتاب کا مستحق ہو جاؤں۔تیری مرضی پر راضی ہوں یہاں تک کہ تو مجھ سے راضی ہو جائے۔ کوئی زور اور طاقت تیرے بغیر نہیں‘‘ (ابن ہشام، ج 2،ص 62) دل شکستہ و غمگین پلٹ کر جب آپ قرن المنازل کے قریب پہنچے تو محسوس ہوا کہ آسمان پر ایک بادل سا چھایا ہوا ہے۔ نظر اٹھا کر دیکھا تو جبریل علیہ السلام سامنے تھے۔ انہیں نے پکار کر کہا ’’ آپ کی قوم نے جو کچھ آپ کو جواب دیا ہے اللہ نے اسے سن لیا۔ اب یہ پہاڑوں کا منتظم فرشتہ اللہ نے بھیجا ہے، آپ جو حکم دینا چاہیں اسے دے سکتے ہیں‘‘۔ پھر پہاڑوں کے فرشتے نے آپ کو سلام کر کے عرض کیا ’’آپ فرمائیں تو دونوں طرف کے پہاڑ ان لوگوں پر الٹ دوں؟‘‘ آپ نے جواب دیا،’’ نہیں، بلکہ میں امید رکھتا ہوں کہ اللہ ان کی نسل سے وہ لوگ پیدا کرے گا جو اللہ وحدہٗ لاشریک کی بندگی کریں گے‘‘۔ (بخاری، بدء الخلق، ذکر الملائکہ۔۔ مسلم،کتاب المغازی۔ نسائی، البعوث) اس کے بعد آپ چند روز نخلہ کے مقام پر جا کر ٹھیر گئے۔ پریشان تھے کہ اب کیسے مکہ واپس جاؤں۔ طائف میں جو کچھ گزری ہے اس کی خبریں وہاں پہنچ چکی ہوں گی۔اس کے بعد تو کفار پہلے سے بھی زیادہ دلیر ہو جائیں گے۔ ان ہی ایام میں ایک روز رات کو آپ نماز میں قرآن مجید کی تلاوت فرما رہے تھے کہ جِنوں کے ایک گروہ کا ادھر سے گزر ہوا، انہوں نے قرآن سُنا،ایمان لائے، واپس جا کر اپنی قوم میں اسلام کی تبلیغ شروع کر دی، اور اللہ تعالیٰ نے اپنے نبی کو یہ خوش خبری سنائی انسان چاہے آپ کی دعوت سے بھاگ رہے ہوں، مگر بہت سے جِن اس کے گرویدہ ہو گئے ہیں اور وہ اسے اپنی جنس میں پھیلا رہے ہیں۔ موضوع اور مباحث: یہ حالات تھے جن میں یہ سورت نازل ہوئی۔ جو شخص بھی ایک طرف ان حالات نزول کو دیکھے گا اور دوسری طرف اس سورۃ کو بغور پڑھے گا اسے اس امر میں کوئی شبہ نہ رہے گا کہ فی الواقع یہ محمد صلی اللہ علیہ و سلم کا کلام نہیں ہے بلکہ ’’ اِس کا نزول اللہ زبردست اور دانا کی طرف سے ہے۔’’ اس لیے کہ اول سے آخر تک پوری سورۃ میں کہیں ان انسانی جذبات و تأثرات کا ایک ادنیٰ شائبہ تک نہیں پایا جاتا جو ان حالات سے گزرنے والے انسان کے اندر فطری طور پر پیدا ہوتے ہیں۔ اگر یہ محمد صلی اللہ علیہ و سلم کا کلام ہوتا، جنہیں پے در پے صدمات اور مصائب کے بے پناہ ہجوم اور طائف کے تازہ ترین چرکے نے خستہ حالی کی انتہا کو پہنچا دیا تھا، تو اس سورے میں کہیں تو ان کیفیات کا عکس نظر آتا جو اس وقت آپ کے دل پر گزر رہی تھیں۔اوپر ہم نے حضورؐ کی دعا نقل کی ہے، اسے دیکھیے۔ وہ آپ کا اپنا کلام ہے۔ اس کا لفظ لفظ ان کیفیات سے لبریز ہے۔ مگر یہ سورۃ جو اسی زمانے اور ان ہی حالات میں آپ ہی کی زبان مبارک سے ادا ہوئی ہے ان کے ہر اثر سے قطعی خالی ہے۔ سورۃ کا موضوع کفار کو ان گمراہیوں کے نتائج سے خبردار کرنا ہے جن میں وہ نہ صرف مبتلا تھے، بلکہ بڑے اصرار اور غرور و استکبار کے ساتھ ان پر جمے ہوئے تھے اور الٹا اس شخص کو ہدف ملامت بنا رہے تھے جو انہیں ان گمراہیوں سے نکالنے کے لیے کوشاں تھا۔ ان کے نزدیک دنیا کی حیثیت محض ایک بے مقصد کھلونے کی تھی اور اس کے اندر اپنے آپ کو وہ غیر جواب دہ مخلوق سمجھ رہے تھے۔ توحید کی دعوت ان کے خیال میں باطل تھی اور انہیں اصرار تھا کہ ان کے معبود واقعی خدا کے شریک ہیں۔ وہ قرآن کے متعلق یہ ماننے کو تیار نہ تھے کہ یہ خداوند عالم کا کلام ہے۔ رسالت کا ایک عجیب جاہلانہ تصور ان کے ذہن میں تھا اور اس کی بنا پر محمد صلی اللہ علیہ و سلم کے دعوائے رسالت کو جانچنے کے لیے وہ طرح طرح کے نرالے معیار تجویز کر رہے تھے۔ان کے نزدیک اسلام کے بر حق نہ ہونے کا ایک بڑا ثبوت یہ تھا کہ ان کے شیوخ اور بڑے بڑے قبائلی سردار اور ان کی قوم کے بوجھ بجھکڑ اسے نہیں مان رہے ہیں،اور صرف چند نوجوان، چند غریب لوگ اور چند غلام ہی اس پر ایمان لائے ہیں۔ وہ قیامت، اور زندگی بعد موت، اور جزا و سزا کی باتوں کو ایک من گھڑت افسانہ سمجھتے تھے، اس ان کا خیال تھا کہ ان چیزوں کا وقوع خارج از امکان ہے۔ اس سورۃ میں بالاختصار ان ہی گمراہیوں میں سے ایک ایک کی مدلل تردید کی گئی ہے اور کفار کو خبردار کیا گیا ہے کہ تم اگر عقل و دلیل سے حقیقت کو سمجھنے کی کوشش کرنے کے بجائے تعصب اور ہٹ دھرمی سے کام لے کر قرآن کی دعوت اور محمد صلی اللہ علیہ و سلم کی رسالت کو رد کر دو گے تو آپ اپنا ہی انجام خراب کرو گے۔ ','سید ابو اعلیٰ مودودیؒ -  تفہیم القرآن');
INSERT INTO "chapters_ur" VALUES(47,'محمدﷺ',NULL,' نام: آیت نمبر 2 کے فقرے و آمنو بما نزل علی محمد سے ماخوذ ہے۔ مراد یہ ہے کہ یہ وہ سورت ہے جس میں محمد صلی اللہ علیہ و آلہ و سلم کا اسم گرامی آیا ہے۔ اس کے علاوہ اس کا ایک اور مشہور نام "قتال" بھی ہے جو آیت 20 کے فقرے و ذکر فیھا القتال سے ماخوذ ہے۔ زمانۂ نزول: اس کے مضامین یہ شہادت دیتے ہیں کہ یہ ہجرت کے بعد مدینہ طیبہ میں اس وقت نازل ہوئی جب جنگ کا حکم تو دیا جا چکا تھا مگر ابھی جنگ عملاً شروع ہوئی نہ تھی۔ تاریخی پس منظر: جس زمانے میں یہ سورت نازل ہوئی ہے اس وقت صورتحال یہ تھی کہ مکہ معظمہ میں خاص طور پر اور عرب کی سرزمین میں بالعموم ہر جگہ مسلمانوں کو ظلم و ستم کا نشانہ بنایا جا رہا تھا اور ان پر عرصہ حیات تنگ کر دیا گیا تھا۔ مسلمان ہر طرف سے سمٹ کر مدینہ طیبہ کے دار الامان میں جمع ہو گئے تھے، مگر کفار قریش یہاں بھی ان کو چین سے بیٹھنے دینے کے لیے تیار نہ تھے۔ مدینے کی چھوٹی سی بستی ہر طرف سے کفار کے نرغے میں گھری ہوئی تھی اور وہ اسے مٹا دینے پر تلے ہوئے تھے۔ مسلمانوں کے لیے اس حالات میں دو ہی چارہ کار باقی رہ گئے تھے۔ یا تو وہ دین حق کی دعوت و تبلیغ ہی سے نہیں بلکہ اس کی پیروی تک سے دست بردار ہو کر جاہلیت کے آگے سپر ڈال دیں، یا پھر مرنے مارنے کے لیے اٹھ کھڑے ہوں اور سر دھڑ کی بازی لگا کر ہمیشہ کے لیے اس امر کا فیصلہ کر دیں کہ عرب کی سرزمین میں اسلام کو رہنا ہے یا جاہلیت کو۔ اللہ تعالٰی نے اس موقع پر مسلمانوں کو اسی عزیمت کی راہ دکھائی جو اہل ایمان کے لیے ایک ہی راہ ہے۔ اس نے پہلے سورہ حج (آیت 39) میں ان کو جنگ کی اجازت دی، اور پھر سورہ بقرہ (آیت 190) میں اس کا حکم دے دیا۔ مگر اس وقت ہر شخص جانتا تھا کہ ان حالات میں جنگ کے معنی کیا ہیں۔ مدینے میں اہل ایمان کی ایک مٹھی بھر جمعیت تھی جو پورے ایک ہزار مردان جنگی بھی فراہم کرنے کے قابل نہ تھی، اور اس سے کہا جا رہا تھا کہ سارے عرب کی جاہلیت سے ٹکرا جانے کے لیے تلوار لے کر کھڑی ہو جائے۔ پھر لڑائی کے لیے جس سر و سامان کی ضرورت تھی وہ اپنا پیٹ کاٹ کر بھی ایک ایسی ہستی مشکل سے ہی فراہم کر سکتی تھی جس کے اندر سینکڑوں بے خانماں مہاجر ابھی پوری طرح بسے بھی نہ تھے اور چاروں طرف سے اہل عرب نے معاشی مقاطعہ کر کے اس کی کمر توڑ رکھی تھی۔ موضوع اور مضمون : یہ حالات تھے جن میں یہ سورت نازل فرمائی گئی۔ اس کا موضوع اہل ایمان کو جنگ کے لیے تیار کرنا اور ان کو اس سلسلے میں ابتدائی ہدایات دینا تھا۔ اسی مناسبت سے اس کا نام سورہ قتال بھی رکھا گیا ہے۔ اس میں ترتیب وار حسب ذیل مضامین ارشاد ہوئے ہیں: آغاز میں بتایا گیا ہے کہ اس وقت دو گروہوں کے درمیان مقابلہ درپیش ہے۔ ایک گروہ کی پوزیشن یہ ہے کہ وہ حق سے ماننے سے انکار کر چکا ہے اور اللہ کے راستے میں سدِ راہ بن کر کھڑا ہو گیا ہے اور دوسرے گروہ کی پوزیشن یہ ہے کہ وہ اس حق کو مان گیا ہے جو اللہ تعالٰی کی طرف سے اس کے بندے محمد صلی اللہ علیہ و آلہ و سلم پر نازل ہوا تھا۔ اب اللہ تعالٰی کا دو ٹوک فیصلہ یہ ہے کہ پہلے گروہ کی تمام سعی و عمل کو اس نے رائیگاں کر دیا اور دوسرے گروہ کے حالات درست کر دیے۔ اس کے بعد مسلمانوں کو ابتدائی جنگی ہدایات دی گئی ہیں۔ ان کو اللہ کی مدد اور رہنمائی کا یقین دلایا گیا ہے۔ ان کو اللہ کی راہ میں قربانیاں کرنے پر بہترین اجر کی امید دلائی گئی ہے اور انہیں اطمینان دلایا گیا ہے کہ راہ حق میں ان کی کوششیں رائیگاں نہیں جائیں گی بلکہ دنیا سے لے کر آخرت تک وہ ان کا اچھے سے اچھا پھل پائیں گے۔ پھر کفار کے متعلق بتایا گیا ہے کہ وہ اللہ کی تائید و رہنمائی سے محروم ہیں۔ ان کی کوئی تدبیر اہل ایمان کے مقابلے میں کارگر نہ ہوگی اور دنیا میں بھی اور آخرت میں بھی بہت برا انجام دیکھیں گے۔ انہوں نے اللہ کے نبی کو مکہ سے نکال کر یہ سمجھا کہ انہیں بڑی کامیابی نصیب ہوئی ہے حالانکہ دراصل یہ کام کر کے انہوں نے اپنی تباہی کو خود اپنے اوپر دعوت دے دی۔ اس کے بعد منافقین کی طرف روئے سخن پھرتا ہے جو جنگ کا حکم آنے سے پہلے تو بڑے مسلمان بنے پھرتے تھے، مگر یہ حکم آ جانے کے بعد ان کے ہوش اڑ گئے تھے اور وہ اپنی عافیت کی فکر میں کفار سے ساز باز کرنے لگے تھے تاکہ اپنے آپ کو جنگ کے خطرات سے بچا لیں۔ ان کو صاف صاف خبردار کیا گیا ہے کہ اللہ اور اس کے دین کے معاملے میں منافقت اختیار کرنے والوں کا کوئی عمل بھی اللہ کے ہاں مقبول نہیں ہے۔ یہاں تو بنیادی سوال جس پر تمام مدعیان ایمان کی آزمائش ہو رہی ہے یہ ہے کہ آدمی حق کے ساتھ ہے یا باطل کے ساتھ؟ اس کی ہمدردیاں اسلام اور مسلمانوں کے ساتھ ہیں یا کفر اور کفار کے ساتھ؟ وہ اپنی ذات اور اپنے مفاد کو عزیز رکھتا ہے یا اس حق کو جس پر ایمان لانے کا وہ دعویٰ کر رہا ہے؟ اس آزمائش میں جو شخص کھوٹا نکلتا ہے وہ مومن ہی نہیں ہے، کجا کہ اس کی نماز اور اس کا روزہ اور اس کی زکوٰۃ خدا کے ہاں کسی اجر کی مستحق ہو۔ پھر مسلمانوں کو تلقین کی گئی ہے کہ وہ اپنی قلت تعداد اور بے سر و سامانی اور کفار کی کثرت اور ان کے سر و سامان کی فراوانی دیکھ کر ہمت نہ ہاریں، ان کے آگے صلح کی پیشکش کر کے کمزوری کا اظہار نہ کریں جس سے ان کی جراتیں اسلام اور مسلمانوں کے مقابلے میں اور زیادہ بڑھ جائیں، بلکہ اللہ کے بھروسے پر اٹھیں اور کفر کے اس پہاڑ سے ٹکرا جائیں۔ اللہ مسلمانوں کے ساتھ ہے، وہی غالب رہیں گے اور یہ پہاڑ ان سے ٹکرا کر پاش پاش ہو جائے گا۔ آخر میں مسلمانوں کو انفاق فی سبیل اللہ کی دعوت دی گئی ہے۔ اگرچہ اس وقت مسلمانوں کی معاشی حالت بہت پتلی تھی، مگر سامنے مسئلہ یہ درپیش تھا کہ عرب میں اسلام اور مسلمانوں کو زندہ رہنا ہے یا نہیں۔ اس مسئلے کی اہمیت و نزاکت کا تقاضا یہ تھا کہ مسلمانوں اپنے آپ کو اور اپنے دین کو کفر کے غلبہ سے بچانے اور اللہ کے دین کو غالب کرنے کے لیے اپنی جانیں بھی لڑائیں اور جنگی تیاری میں اپنے مالی وسائل بھی پوری امکانی حد تک کھپا دیں۔ اس لیےمسلمانوں سے فرمایا گیا کہ اس وقت جو شخص بھی بخل سے کام لے گا وہ دراصل اللہ کا کچھ نہ بگاڑے گا بلکہ خود اپنے آپ ہی کو ہلاکت کے خطرے میں ڈال لے گا۔ اللہ تو انسانوں کا محتاج نہیں ہے۔ اس کے دین کی خاطر قربانیاں دینے سے ایک گروہ اگر جی چرائے گا تو اللہ اسے ہٹا کر دوسرا گروہ اس کی جگہ لے آئے گا ','سید ابو اعلیٰ مودودیؒ -  تفہیم القرآن');
INSERT INTO "chapters_ur" VALUES(48,'فتح',NULL,' نام: پہلی ہی آیت کے الفاظ انا فتحنا لک فتحا مبینا سے ماخوذ ہے۔ یہ محض اس سورت کا نام ہی نہیں بلکہ مضمون کے لحاظ سے بھی اس کا عنوان ہے کیونکہ اس میں اس فتح عظیم پر کلام کیا گیا ہے جو صلح حدیبیہ کی شکل میں اللہ تعالٰی نے حضور نبی کریم صلی اللہ علیہ و آلہ و سلم اور مسلمانوں کو عطا فرمائی تھی۔ زمانۂ نزول: روایات اس پر متفق ہیں کہ اس کا نزول ذی القعدہ 6ھ میں اس وقت ہوا تھا جب آپ کفار مکہ سے صلح حدیبیہ کا معاہدہ کرنے کے بعد مدینہ منورہ واپس تشریف لے جا رہے تھے تاریخ پس منظر: جن واقعات کے سلسلے میں یہ سورت نازل ہوئی ان کی ابتدائی اس طرح ہوئی ہے کہ ایک روز رسول اللہ صلی اللہ علیہ و آلہ و سلم نے خواب میں دیکھا کہ آپ اپنے اصحاب کے ساتھ مکہ معظمہ تشریف لے گئے ہیں اور وہاں عمرہ ادا فرمایا ہے۔ پیغمبر کا خواب ظاہر ہے کہ محض خواب و خیال نہ ہو سکتا تھا وہ تو وحی کی اقسام میں سے ایک قسم ہے اور آگے چل کر آیت 27 میں اللہ تعالٰی نے توثیق کر دی ہے کہ یہ خواب ہم نے اپنے رسول کو دکھایا تھا۔ اس لیے در حقیقت یہ نرا خواب نہ تھا بلکہ ایک الٰہی اشارہ تھا جس کی پیروی کرنا حضور کے لیے ضروری تھا۔ بظاہر اسباب اس ہدایت پر عمل کرنے کی کوئی صورت ممکن نظر نہ آتی تھی۔ کفار قریش نے 6 سال سے مسلمانوں کے لیے بیت اللہ کا راستہ بند کر رکھا تھا اور اس پوری مدت میں کسی مسلمان کو انہوں نے حج اور عمرے تک کے لیے حدود حرم کے قریب نہ پھٹکنے دیا تھا۔ اب آخر یہ کیسے توقع کی جا سکتی تھی کہ وہ رسول اللہ صلی اللہ علیہ و آلہ و سلم کو صحابہ کی ایک جمعیت کے ساتھ مکہ میں داخل ہونے دیں گے۔ عمرے کا احرام باندھ کر جنگی ساز و سامان ساتھ لیے ہوئے نکلنا گویا خود لڑائی کو دعوت دینا تھا اور غیر مسلح جانے کے معنی اپنی اور اپنے ساتھیوں کی جان خطرے میں ڈالنے کے تھے۔ ان حالات میں کوئی شخص یہ نہ سمجھ سکتا تھا کہ اللہ تعالٰی کے اس اشارے پر عمل کیا جائے تو کیسے۔ مگر پیغمبر کا منصب یہ تھا کہ اس کا رب جو حکم بھی اس کو دے وہ بے کھٹکے اس پر عمل کر گذرے۔ اس لیے رسول اللہ صلی اللہ علیہ و آلہ و سلم نے بلا تامل اپنا خواب صحابہ کرام کو سنا کر سفر کی تیاری شروع کر دی۔ آس پاس کے قبائل میں بھی آپ نے اعلان عام کرا دیا کہ ہم عمرے کے لیے جا رہے ہیں جو ہمارے ساتھ چلنا چاہے وہ آ جائے۔ جن لوگوں کی نگاہ ظاہری اسباب پر تھی انہوں نے سمجھا کہ یہ لوگ موت کے منہ میں جا رہے ہیں۔ ان میں سے کوئی آپ کے ساتھ چلنے پر آمادہ نہ ہوا۔ مگر جو اللہ اور اس کے رسول پر سچا ایمان رکھتے تھے انہیں اس امر کی کوئی پروا نہ تھی کہ انجام کیا ہوگا۔ ان کے لیے بس یہ کافی تھا کہ اللہ کا اشارہ ہے اور اس کا رسول تعمیلِ حکم کے لیے اٹھ کھڑا ہوا ہے۔ اس کے بعد کوئی چیز ان کو رسول خدا کا ساتھ دینے سے روک نہ سکتی تھی۔ 1400 صحابی حضور صلی اللہ علیہ و آلہ و سلم کی معیت میں اس نہایت خطرناک سفر پر جانے کے لیے تیار ہو گئے۔ ذی القعدہ 6ھ کے آغاز میں یہ مبارک قافلہ مدینہ سے روانہ ہوا۔ ذوالحلیفہ پہنچ کر سب نے عمرے کا احرام باندھا۔ قربانی کے لیے 70 اونٹ ساتھ لیے جن کی گردنوں میں ھدی کی علامت کے طور پر قلاوے پڑے ہوئے تھے۔ پرتلوں میں صرف ایک ایک تلوار رکھ لی جس کی تمام زائرین حرم کو عرب کے معروف قاعدے کے مطابق اجازت تھی اور اس کے سوا کوئی سامان جنگ ساتھ نہ لیا۔ اس طرح یہ قافلہ لبیک لبیک کی صدائیں بلند کرتا ہوا بیت اللہ کی طرف چل پڑا۔ اس وقت مکہ اور مدینے کے تعلقات کی جو نوعیت تھی، عرب کا بچہ بچہ اس کو جانتا تھا۔ ابھی پچھلے سال ہی تو شوال 5ھ میں قریش نے قبائل عرب کی متحدہ طاقت کے ساتھ مدینے پر چڑھائی کی تھی اور غزوہ احزاب کا مشہور معرکہ پیش آ چکا تھا۔ اس لیے جب رسول اللہ صلی اللہ علیہ و آلہ و سلم اتنے بڑے قافلے کے ساتھ اپنے خون کے پیاسے دشمنوں کے گھر کی طرف روانہ ہوئے تو پورے عرب کی نگاہیں اس عجیب سفر کی طرف مرکوز ہو گئیں اور لوگوں نے یہ بھی دیکھ لیا کہ یہ قافلہ لڑنے کے لیے نہیں جا رہا ہے بلکہ ماہ حرام میں، احرام باندھ کر، ھدی کے اونٹ ساتھ لیے ہوئے بیت اللہ کا طواف کرنے جا رہا ہے اور قطعی طور پر غیر مسلح ہے۔ قریش کے لوگوں کو حضور صلی اللہ علیہ و آلہ و سلم کے اس اقدام نے سخت پریشانی میں ڈال دیا۔ ذی القعدہ کا مہینہ ان حرام مہینوں میں سے تھا جو صد ہا برس سے عرب میں حج و زیارت کے لیے محترم سمجھے جا تےتھے۔ اس مہینے میں جو قافلہ احرام باندھ کر حج یا عمرے کے لیے جا رہا ہو اسے روکنے کا کسی کو حق نہ تھا، حتٰی کہ کسی قبیلے سے اس کی دشمنی بھی ہو تو عرب کے مسلمہ قوانین کی رو سے وہ اپنے علاقے سے اس کے گذرنے میں مانع نہ ہو سکتا تھا۔ قریش کے لوگ اس الجھن میں پڑ گئے کہ اگر ہم مدینے کے اس قافلے پر حملہ کر کے اسے مکہ معظمہ میں داخل ہونے سے روکتے ہیں تو پورے ملک میں اس پر شور مچ جائے گا۔ عرب کا ہر شخص پکار اٹھے گا کہ یہ سراسر زیادتی ہے۔ تمام قبائل عرب یہ سمجھیں گے کہ ہم خانہ کعبہ کے مالک بن بیٹھے ہیں۔ ہر قبیلہ اس تشویش میں مبتلا ہو جائے گا کہ آئندہ کسی کو حج اور عمرہ کرنے دینا یا نہ کرنے دینا اب ہماری مرضی پر موقوف ہے، جس سے بھی ہم ناراض ہو گے اسے بیت اللہ کی زیارت کرنے سے اسی طرح روک دیں گے جس طرح آج مدینے کے ان زائرین کو روک رہے ہیں۔ یہ ایسی غلطی ہوگی کہ جس سے سارا عرب ہم سے منحرف ہو جائے گا۔ لیکن اگر ہم محمد صلی اللہ علیہ و آلہ و سلم کو اتنے بڑے قافلے کے ساتھ بخیریت اپنے شہر میں داخل ہو جانے دیتے ہیں تو پورے ملک میں ہماری ہوا اکھڑ جائے گی اور لوگ کہیں گے کہ ہم محمد صلی اللہ علیہ و آلہ و سلم سے مرعوب ہو گئے۔ آخر کار بڑی شش و پنج کے بعد ان کی جاہلانہ حمیت ہی ان پر غالب آ کر رہی اور انہوں نے اپنی ناک کی خاطر یہ فیصلہ کر لیا کہ کسی قیمت پر بھی اس قافلے کو شہر میں داخل نہیں ہونے دینا ہے۔ رسول اللہ صلی اللہ علیہ و آلہ و سلم نے بنی کب کے ایک شخص کو مخبر کی حیثیت سے آگے بھیج رکھا تھا تاکہ وہ قریش کے ارادوں اور ان کی نقل و حرکت سے آپ کو بروقت مطلع کرتا رہے۔ جب آپ عسفان پہنچے تو اس نے آکر آپ کو اطلاع دی کہ قریش کے لوگ پوری تیاری کے ساتھ ذی طویٰ کے مقام پر پہنچ گئے ہیں اور خالد بن ولید کو انہوں نے 200 سواروں کے ساتھ کراع الغمیم کی طرف آگے بھیج دیا ہے تاکہ وہ آپ کا راستہ روکیں۔ قریش کی چال یہ تھی کہ کسی نہ کسی طرح آنحضرت کے ساتھیوں سے چھیڑ چھاڑ کر کے ان کو اشتعال دلائیں اور پھر اگر لڑائی ہو جائے تو پورے ملک میں یہ مشہور کر دیں کہ یہ لوگ دراصل آئے تھے لڑنے کے لیے، مگر بہانہ انہوں نے عمرے کا کیا تھا اور احرام محض دھوکہ دینے کے لیے باندھ رکھا تھا۔ رسول اللہ صلی اللہ علیہ و آلہ و سلم نے یہ اطلاع پاتے ہی فوراً راستہ بدل دیا اور ایک نہایت دشوار گذار راستہ سے سخت مشقت اٹھا کر حدیبیہ کے مقام پر پہنچ گئے جو عین حرم کی سرحد پر واقع تھا۔ یہاں بنی خزاعہ کا سردار بدیل بن ورقا اپنے قبیلے کے چند آدمیوں کے ساتھ آپ کے پاس آیا اور اس نے پوچھا کہ آپ کس غرض کے لیے آئے ہیں؟ آپ نے فرمایا ہم کسی سے لڑنے نہیں آئے، صرف بیت اللہ کی زیارت اور اس کا طواف ہمارے پیش نظر ہے۔ یہی بات ان لوگوں نے جا کر قریش کے سرداروں کو بتا دی اور ان کو مشورہ دیا کہ وہ ان زائرین حرم کا راستہ نہ روکیں۔ مگر وہ اپنی ضد پر اڑے رہے اور انہوں نے احابیش کے سردار حلی بن علقمہ کو حضور صلی اللہ علیہ و آلہ و سلمکے پاس بھیجا تاکہ وہ آپ کو واپس جانے پر آمادہ کرے۔ سردارانِ قریش کا مقصد یہ تھا کہ جب محمد صلی اللہ علیہ و آلہ و سلم اس کی بات نہ مانیں گے تو وہ ان سے ناراض ہو کر پلٹے گا اور پھر احابیش کی پوری طاقت ہمارے ساتھ ہوگی۔مگر جب اس نے اپنی آنکھوں سے دیکھ لیا کہ سارا قافلہ احرام بند ہے، ھدی کے اونٹ سامنے کھڑے ہیں جن کی گردنوں میں قلاوے پڑے ہوئے ہیں، اور یہ لوگ لڑنے کے لیے نہیں بلکہ بیت اللہ کا طواف کرنے کے لیے آئے ہیں تو حضور صلی اللہ علیہ و آلہ و سلم سے کوئی بات کیے بغیر مکہ کی طرف پلٹ گیا اور اس نے جا کر قریش کے سرداروں سے صاف صاف کہہ دیا کہ یہ لوگ بیت اللہ کی عظمت مان کر اس کی زیارت کے لیے آئے ہیں اگر تم ان کو روکو گے تو احابیش اس کام میں تمہارا ساتھ ہرگز نہ دیں گے۔ ہم تمہارے حلیف اس لیے نہیں بنے ہیں کہ تم حرمتوں کو پامال کرو اور ہم اس میں تمہاری حمایت کریں۔ پھر قریش کی طرف سے عروہ بن مسعود ثقفی آیا اور اس نے اپنے نزدیک بڑی اونچ نیچ سمجھا کر رسول اللہ صلی اللہ علیہ و آلہ و سلم کو اس بات پر آمادہ کرنا چاہا کہ آپ مکہ میں داخل ہونے کے ارادے سے باز آجائیں، مگر آپ نے اس کو بھی وہی جواب دیا جو بنی خزاعہ کے سردار کو دیا تھا کہ ہم لڑائی کے ارادے سے نہیں آئے ہیں بلکہ بیت اللہ کی تعظیم کرنے والے بن کر ایک دینی فریضہ بجا لانے کے لیے آئے ہیں۔ واپس جا کر عروہ نے قریش کے لوگوں سے کہا کہ میں قیصر و کسریٰ اور نجاشی کے درباروں میں بھی گیا ہوں، مگر خدا کی قسم، میں نے اصحاب محمد کو جس طرح محمد (صلی اللہ علیہ و آلہ و سلم) کا فدائی دیکھا ہے ایسا منظر کسی بڑے سے بڑے بادشاہ کے ہاں بھی نہیں دیکھا۔ ان لوگوں کا حال تو یہ ہے کہ محمد وضو کرتے ہیں تو ان کے اصحاب پانی کا ایک قطرہ تک زمین پر نہیں گرنے دیتے اور سب اپنے جسم پر کپڑوں پر مل لیتے ہیں۔ اب تو لوگ سوچ لو کہ تمہارا مقابلہ کس سے ہے۔ اس دوران جبکہ ایلچیوں کی آمدورفت اور گفت و شنید کا یہ سلسلہ جاری تھا، قریش کے لوگ بار بار یہ کوشش کرتے رہے کہ چپکے سے حضور صلی اللہ علیہ و آلہ و سلم کے کیمپ پر چھاپے مار کر صحابہ کو اشتعال دلائیں اور کسی نہ کسی طرح ان سے کوئی ایسا اقدام کرا لیں جس سے لڑائی کا بہانہ ہاتھ آ جائے۔ مگر ہر مرتبہ صحابہ کے صبر و ضبط اور حضور صلی اللہ علیہ و آلہ و سلمکی حکمت و فراست نے ان کی ساری تدبیروں کو ناکام کر دیا۔ ایک دفعہ ان کے چالیس پچاس آدمی رات کے وقت آئے اور مسلمانوں کے پڑاؤ پر پتھر اور تیر برسانے لگے۔ صحابہ نے ان سب کو گرفتار کر کے حضور صلی اللہ علیہ و آلہ و سلم کے سامنے پیش کر دیا۔ مگر آپ نے ان سب کو چھوڑ دیا۔ ایک اور موقع پر تنعیم کی طرف سے 80 آدمی عین نماز فجر کے وقت آئے اور انہوں نے اچانک چھاپہ مار دیا۔ یہ لوگ بھی پکڑے گئے، مگر حضور صلی اللہ علیہ و آلہ و سلمنے انہیں بھی رہا کر دیا۔ اس طرح قریش کی اپنی ہر چال اور ہر تدبیر میں ناکامی ہوتی چلی گئی۔ آخر کار حضور صلی اللہ علیہ و آلہ و سلم نے خود اپنی طرف سے حضرت عثمان غنی رضی اللہ عنہ کو ایلچی بنا کر مکہ بھیجا اور ان کے ذریعے سے سرداران قریش کو یہ پیغام دیا کہ ہم جنگ کے لیے نہیں بلکہ زیارت کے لیے ھدی ساتھ لے کر آئے ہیں، طواف اور قربانی کر کے واپس چلے جائیں گے۔ مگر وہ لوگ نہ مانے اور حضرت عثمان رضی اللہ عنہ کو مکہ ہی میں روک لیا۔ اس دوران یہ خبر اڑ گئی کہ حضرت عثمان رضی اللہ عنہ قتل کر دیے گئے ہیں، اور ان کے واپس نہ آنے سے مسلمانوں کو یقین ہو گیا کہ یہ خبر سچی ہے۔ اب مزید تحمل کا کوئی موقع نہ تھا۔ مکہ میں داخلہ کی بات تو دوسری تھی، اس کے لیے طاقت کا استعمال ہر گز پیش نظر نہ تھا۔ مگر جب نوبت سفیر کے قتل تک پہنچ گئی تو پھر اس کے سوا کوئی چارہ باقی نہ رہا کہ مسلمان جنگ کے لیے تیار ہو جائیں۔ چنانچہ رسول اللہ صلی اللہ علیہ و آلہ و سلمنے اپنے تمام ساتھیوں کو جمع کیا اور ان سے اس بات پر بیعت لی کہ اب یہاں سے ہم مرتے دم تک پیچھے نہ ہٹیں گے۔ موقع کی نزاکت نگاہ میں ہو تو آدمی سمجھ سکتا ہے کہ یہ کوئی معمولی بیعت نہ تھی۔ مسلمان صرف 1400 تھے اور کسی سامان جنگ کے بغیر آئے تھے۔ اپنے مرکز سے ڈھائی سو میل دور، عین مکہ کی سرحد پر ٹھیرے ہوئے تھے، جہاں دشمن اپنی پوری طاقت کے ساتھ ان پر حملہ آور ہو سکتا تھا اور گرد و پیش سے اپنے حامی قبیلوں کو لا کر بھی انہیں گھیرے میں لے سکتا تھا۔ اس کے باوجود ایک شخص کے سوا پورا قافلہ نبی صلی اللہ علیہ و آلہ و سلم کے ہاتھ پر مرنے مارنے کی بیعت کرنے کے لیے بلا تامل آمادہ ہو گیا۔ اس سے بڑھ کر ان لوگوں کو اخلاص ایمانی اور راہ خدا میں ان کی فدائیت کا اور کیا ثبوت ہو سکتا ہے۔ یہی وہ بیعت ہے جو بیعت رضوان کے نام سے تاریخ اسلام میں مشہور ہے۔ بعد میں معلوم ہوا کہ حضرت عثمان رضی اللہ عنہ کے قتل کی خبر غلطی تھی۔ وہ خود بھی واپس آ گئے اور قریش کی طرف سے سہیل بن عمرو کی قیادت میں ایک وفد بھی صلح کی بات چیت کرنے کے لیے حضور صلی اللہ علیہ و آلہ و سلم کے کیمپ میں پہنچ گیا۔ اب قریش اپنی اس ضد سے ہٹ گئے تھے کہ وہ حضور صلی اللہ علیہ و آلہ و سلم کو اور آپ کے ساتھیوں کو سرے سے مکہ میں داخل ہی نہ ہونے دیں گے۔ البتہ اپنی ناک بچانے کے لیے ان کا صرف یہ اصرار تھا کہ آپ اس سال واپس چلے جائیں، آئندہ سال آپ عمرے کے لیے آ سکتے ہیں۔ طویل گفت و شنید کے بعد جن شرائط پر صلح نامہ لکھا گیا وہ یہ تھیں: 1. دس سال تک فریقین کے درمیان جنگ بند رہے گی، اور ایک دوسرے کے خلاف خفیہ اور علانیہ کوئی کاروائی نہ کی جائے گی۔ 2. اس دوران قریش کا جو شخص اپنے ولی کی اجازت کے بغیر بھاگ کر محمد صلی اللہ علیہ و آلہ و سلم کے پاس جائے گا اسے آپ واپس کر دیں گے اور آپ کے ساتھیوں میں سے جو شخص قریش کے پاس چلا جائے گا اسے وہ واپس نہ کریں گے۔ . قبائل عرب میں سے جو قبیلہ بھی فریقین میں سے کسی ایک کا حلیف بن کر اس معاہدے میں شامل ہونا چاہے گا اسے اس کا اختیار ہوگا۔ 4. محمد صلی اللہ علیہ و آلہ و سلم اس سال واپس جائیں گے اور آئندہ سال وہ عمرے کے لیے آ کر تین دن مکہ میں ٹھیر سکتے ہیں، بشرطیکہ پرتلوں میں صرف ایک ایک تلوار لے کر آئیں اور کوئی سامان حرب ساتھ نہ لائیں۔ ان تین دنوں میں اہل مکہ ان کے لیے شہر خالی کر دیں گے (تاکہ کسی تصادم کی نوبت نہ آئے)۔ مگر واپس جاتے ہوئے وہ یہاں کے کسی شخص کو اپنے ساتھ لے جانے کے مجاز نہ ہوں گے۔جس وقت اس معاہدے کی شرائط طے ہو رہی تھیں، مسلمانوں کا پورا لشکر سخت مضطرب تھا۔ کوئی شخص بھی ان مصلحتوں کو نہیں سمجھ رہا تھا جنہیں نگاہ میں رکھ کر نبی صلی اللہ علیہ و آلہ و سلم یہ شرائط قبول فرما رہے تھے۔ کسی کی نظر اتنی دور رس نہ تھی کہ اس صلح کے نتیجے میں جو خیر عظیم رونما ہونے والی تھی اسے دیکھ سکے۔ کفار قریش اسے اپنی کامیابی سمجھ رہے تھے اور مسلمان اس پر بے تاب تھے کہ ہم آخر دب کر یہ ذلیل شرائط کیوں قبول کریں۔ حضرت عمر فاروق رضی اللہ عنہ جیسے بالغ النظر مدبر تک کا یہ حال تھا کہ وہ کہتے ہیں کہ مسلمان ہونے کے بعد کبھی میرے دل میں شک نے راہ نہ پائی تھی،مگر اس موقع پر میں بھی اس سے محفوظ نہ رہ سکا۔ وہ بے چین ہو کر حضرت ابو بکر صدیق رضی اللہ عنہ کے پاس گئے اور کہا "کیا حضور صلی اللہ علیہ و آلہ و سلم اللہ کے رسول نہیں ہیں؟ کیا ہم مسلمان نہیں ہیں؟ کیا یہ لوگ مشرک نہیں ہیں؟ پھر آخر ہم اپنے دین کے معاملے میں یہ ذلت کیوں اختیار کریں؟" انہوں نے جواب دیا "اے عمر! وہ اللہ کے رسول ہیں اور اللہ ان کو ہر گز ضائع نہ کرے گا"۔ پھر ان سے صبر نہ ہوا جاکر یہی سوالات خود رسول اللہ صلی اللہ علیہ و آلہ و سلم سے بھی کیے اور حضور صلی اللہ علیہ و آلہ و سلم نے بھی ان کو ویسا ہی جواب دیا جیسا حضرت ابو بکر رضی اللہ عنہ نے دیا تھا۔ بعد میں حضرت عمر مدتوں اس پر نوافل اور صدقات ادا کرتے رہے تاکہ اللہ تعالٰی اس گستاخی کو معاف فرما دے جو اس روز ان سے شان رسالت میں ہو گئی تھی۔ سب سے زیادہ دو باتیں اس معاہدے میں لوگوں کو بری طرح کَھل رہی تھیں۔ ایک شرط نمبر 2 جس کے متعلق لوگ کہتے تھے کہ یہ صریح نامساوی شرط ہے۔ اگر مکہ سے بھاگ کر آنے والوں کو ہم واپس کریں تو مدینہ سے بھاگ کر جانے والے کو کیوں نہ واپس کریں؟ حضور صلی اللہ علیہ و آلہ و سلم نے اس پر فرمایا جو ہمارے ہاں سے بھاگ کر ان کے پاس چلا جائے وہ آخر ہمارے کس کام کا ہے؟ اللہ اسے ہم سے دور ہی رکھے۔ اور جو ان کے ہاں سے بھاگ کر ہمارے پاس آ جائے اسے اگر ہم واپس کر دیں گے تو اللہ اس کے لیے خلاصی کی کوئی اور صورت پیدا فرما دے گا۔ دوسری چیز جو لوگوں کے دلوں میں کھٹک رہی تھی وہ چوتھی شرط تھی۔ مسلمان یہ سمجھ رہے تھے کہ اسے ماننے کے معنی یہ ہیں کہ تمام عرب کے سامنے گویا ہم ناکام واپس جا رہے ہیں۔ مزید براں یہ سوال بھی دلوں میں خلش پیدا کر رہا تھا کہ حضورصلی اللہ علیہ و آلہ و سلم نے خواب میں دیکھا تھا کہ ہم مکہ میں طواف کر رہے ہیں،مگر یہاں تو ہم طواف کیے بغیر واپس جانے کی شرط مان رہے ہیں۔ حضورصلی اللہ علیہ و آلہ و سلم نے اس پر لوگوں کو سمجھا کہ خواب میں آخر اسی سال طواف کرنے کی صراحت تو نہ تھی۔ شرائطِ صلح کے مطابق اس سال نہیں تو اگلے سال انشاء اللہ طواف ہوگا۔ جلتی پر تیل کا کام جس واقعہ نے کیا وہ یہ تھا کہ عین اس وقت جب صلح کا معاہدہ لکھا جا رہا تھا، سہیل بن عمرو کے اپنے صاحبزادے ابو جندل، جو مسلمان ہو چکے تھے اور کفار مکہ نے ان کو قید کر رکھا تھا، کسی نہ کسی طرح بھاگ کر حضور صلی اللہ علیہ و آلہ و سلم کے کیمپ میں پہنچ گئے۔ ان کے پاؤں میں بیڑیاں تھیں اور جسم پر تشدد کے نشانات تھے۔ انہوں نے حضور صلی اللہ علیہ و آلہ و سلم سے فریاد کی کہ مجھے اس حبس بے جا سے نجات دلائی جائے۔ صحابہ کرام کے لیے یہ حالت دیکھ کر ضبط کرنا مشکل ہو گیا۔ مگر سہیل بن عمرو نے کہا کہ صلح نامے کی تحریر چاہے مکمل نہ ہوئی ہو، شرائط تو ہمارے اور آپ کے درمیان طے ہو چکی ہیں، اس لیے اس لڑکے کو میرے حوالے کیا جائے۔ رسول اللہ صلی اللہ علیہ و آلہ و سلم نے اس کی حجت تسلیم فرما لی اور ابو جندل ظالموں کے حوالے کر دیے گئے۔ صلح سے فارغ ہو کر حضور صلی اللہ علیہ و آلہ و سلم نے صحابہ سے فرمایا کہ اب یہیں قربانی کر کے سر منڈواؤ اور احرام ختم کر دو۔ مگر کوئی اپنی جگہ سے نہ ہلا۔ حضور صلی اللہ علیہ و آلہ و سلم نے تین مرتبہ حکم دیا، مگر صحابہ پر اس وقت رنج و غم اور دل شکستگی کا ایسا شدید غلبہ تھا کہ انہوں نے اپنی جگہ سے حرکت نہ کی۔ حضورصلی اللہ علیہ و آلہ و سلم کے پورے دور رسالت میں اس ایک موقع کے سوا کبھی یہ صورت پیش نہ آئی تھی کہ آپ صحابہ کو حکم دیں اور وہ اس کی تعمیل کے لیے دوڑ نہ پڑیں۔ حضور صلی اللہ علیہ و آلہ و سلم کو اس پر سخت صدمہ ہوا اور آپ نے اپنے خیمے میں جا کر ام المومنین حضرت ام سلمہ سے اپنی کبیدہ خاطری کا اظہار فرمایا۔ انہوں نےعرض کیا کہ آپ بس خاموشی کے ساتھ تشریف لے جا کر خود اپنا اونٹ ذبح فرمائیں اور حجام کو بلا کر اپنا سر منڈوا لیں۔ اس کے بعد لوگ خود بخود آپ کے عمل کی پیروی کریں گے اور سمجھ لیں گے کہ جو فیصلہ ہو چکا ہے وہ اب بدلنے والا نہیں ہے۔ چنانچہ ایسا ہی ہوا اور آپ کے فعل کو دیکھ کر لوگوں نے بھی قربانیاں کر لیں، سر منڈوا لیے یا بال ترشوا لیے اور احرام سے نکل آئے۔ مگر دل ان کے غم سے کٹے جا رہے تھے۔ اس کے بعد جب یہ قافلہ حدیبیہ کی صلح کو اپنی شکست اور ذلت سمجھتا ہوا مدینہ کی طرف واپس جا رہا تھا، اس وقت ضجنان کے مقام پر (یا بقول بعض کراع الغمیم کے مقام پر) یہ سورت نازل ہوئی، جس نے مسلمانوں کو بتایا کہ یہ صلح جس کو وہ شکست سمجھ رہے ہیں دراصل فتح عظیم ہے۔ اس کے نازل ہونے کے بعد حضور صلی اللہ علیہ و آلہ و سلم نے مسلمانوں کو جمع کیا اور فرمایا آج مجھ پر وہ چیز نازل ہوئی ہے جو میرے لیے دنیا و مافیہا سے زیادہ قیمتی ہے۔ پھر یہ سورت آپ نے تلاوت فرمائی اور خاص طور پر حضرت عمر رضی اللہ عنہ کو بلا کر اسے سنایا کیونکہ وہ سب سے زیادہ رنجیدہ تھے۔ اگرچہ اہل ایمان تو اللہ تعالٰی کا یہ ارشاد سن کر ہی مطمئن ہو گئے تھے، مگر کچھ زیادہ مدت نہ گذری تھی کہ اس صلح کے فوائدایک ایک کرتے کھلتے چلے گئے یہاں تک کہ کسی کو بھی اس امر میں شک نہ رہا کہ فی الواقع یہ صلح ایک عظیم الشان فتح تھی۔ 1. اس میں پہلی مرتبہ عرب میں اسلامی ریاست کا وجود باقاعدہ تسلیم کیا گیا۔ اس سے پہلے تک عربوں کی نگاہ میں محمد صلی اللہ علیہ و آلہ و سلم اور آپ کے ساتھیوں کی حیثیت محض قریش اور قبائل عرب کے خلاف خروج کرنے والے ایک گروہ کی تھی اور ان کو برادری باہر ( Outlaw ) سمجھتے تھے۔ اب خود قریش ہی نے آپ سے معاہدہ کر کے سلطنت اسلامی کے مقبوضات پر آپ کا اقتدار مان لیا اور قبائل عرب کے لیے یہ دروازہ بھی کھول دیا کہ ان دونوں سیاسی طاقتوں میں سےجس کے ساتھ چاہیں حلیفانہ معاہدات کرلیں۔ 2. مسلمانوں کے لیے زیارت بیت اللہ کا حق تسلیم کر کے قریش نے آپ سے آپ گویا یہ بھی مان لیا کہ اسلام کوئی بے دینی نہیں ہے جیسا کہ وہ اب تک کہتے چلے آ رہے تھے، بلکہ عرب کے مسلمہ ادیان میں سے ایک ہے اور دوسرے عربوں کی طرح اس کے پیرو بھی حج و عمرہ کے مناسک ادا کرنے کا حق رکھتے ہیں۔ اس سے اہل عرب کے دلوں کی وہ نفرت کم ہو گئی جو قریش کے پروپیگنڈا سے اسلام کے خلاف پیدا ہو گئی تھی۔ . دس سال کے لیے جنگ بندی کا معاہدہ ہو جانے سے مسلمانوں کو امن میسر آ گیا اور انہوں نے عرب تمام اطراف و نواح میں پھیل کر اس تیزی سے اسلام کی اشاعت کی کہ صلح حدیبیہ سے پہلے پورے 19 سال میں اتنے آدمی مسلمان نہ ہوئے تھے جتنے اس کے بعد دو سال کے اندر ہو گئے۔ یہ اسی صلح کی برکت تھی کہ یا تو وہ وقت تھا جب حدیبیہ کے مقام پر حضور صلی اللہ علیہ و آلہ و سلم کے ساتھ 1400 آدمی آئے تھے، یا دو ہی سال کے بعد جب قریش کی عہد شکنی کے نتیجے میں حضور صلی اللہ علیہ و آلہ و سلم نے مکہ پر چڑھائی کی تو دس ہزار کا لشکر آپ کے ہمرکاب تھا۔ 4. قریش کی طرف سے جنگ بند ہوجانے کے بعد آنحضرت صلی اللہ علیہ و آلہ و سلم کو یہ موقع مل گیا کہ اپنے مقبوضات میں اسلامی حکومت کو اچھی طرح مستحکم کر لیں اور اسلامی قانون کے اجراء سے مسلم معاشرے کو ایک مکمل تہذیب و تمدن بنا دیں۔ یہی و ہ نعمت عظمٰی ہے جس کے متعلق اللہ تعالٰی نے سورہ مائدہ کی آیت 3 میں فرمایا کہ "آج میں نے تمہارے دین کو تمہارے لیے مکمل کر دیا ہے اور اپنی نعمت تم پر تمام کر دی ہے اور تمہارے لیے اسلام کو تمہارے دین کی حیثیت سے قبول کرلیا ہے"۔ . قریش سے صلح کے بعد جنوب کی طرف سے اطمینان نصیب ہو جانے کا فائدہ یہ بھی ہوا کہ مسلمانوں نے شمال{زیر}} عرب اور وسطِ عرب کی تمام مخالف طاقتوں کو با آسانی مسخر کر لیا۔ صلح حدیبیہ پر تین ہی مہینے گذرے تھے کہ یہودیوں کا سب سے بڑا گڑھ خیبر فتح ہو گیا اور اس کے بعد فدک، وادی القریٰ، تیما اور تبوک کی یہودی بستیاں اسلام کے زیر نگیں آتی چلی گئیں۔ پھر وسطِ عرب کے وہ تمام قبیلے بھی، جو یہود و قریش کے ساتھ گٹھ جوڑ رکھتے تھے، ایک ایک کر کے تابع فرمان ہو گئے۔ اس طرح حدیبیہ کی صلح نے دو ہی سال کے اندر عرب میں قوت کا توازن اتنا بدل دیا کہ قریش اور مشرکین کی طاقت دب کر رہ گئی اور اسلام کا غلبہ یقینی ہو گیا۔ یہ تھیں وہ برکات جو مسلمانوں کو اس صلح سے حاصل ہوئیں جسے وہ اپنی ناکامی اور قریش اپنی کامیابی سمجھ رہے تھے۔ سب سے زیادہ جو چیز اس صلح میں مسلمانوں کو ناگوار ہوئی تھی اور جسے قریش اپنی جیت سمجھا تھا کہ مکہ سے بھاگ کر مدینہ جانے والوں کو واپس کر دیا جائے گا اور مدینہ سے بھاگ کر مکہ جانے والوں کو واپس نہ کیا جائے گا۔ مگر تھوڑی ہی مدت گذری تھی کہ یہ معاملہ بھی قریش پر الٹا پڑا اور تجربہ نے بتا دیا کہ نبی صلی اللہ علیہ و آلہ و سلم کی نگاہِ دور رس نے اس کے کن نتائج کو دیکھ کر یہ شرط قبول کی تھی۔ صلح کے کچھ دنوں بعد مکہ سے ایک مسلمان ابو بصیر قریش کی قید سے بھاگ نکلے اور مدینہ پہنچے۔ قریش نے ان کی واپسی کا مطالبہ کیا اور حضور صلی اللہ علیہ و آلہ و سلم نے معاہدے کے مطابق انہیں ان لوگوں کے حوالے کر دیا جو ان کی گرفتاری کے لیے مکہ سے بھیجے گئے تھے۔ مگر مکہ جاتے ہوئے راستے میں وہ پھر ان کی گرفت سے بچ نکلے اور ساحل بحیرہ احمر کے اس راستے پر جا بیٹھے جس سے قریش کے تجارتی قافلے گذرتے تھے۔ اس کے بعد جس مسلمان کو بھی قریش کی قید سے بھاگ نکلنے کا موقع ملتا وہ مدینہ جانے کے بجائے ابو بصیر کر ٹھکانے پر پہنچ جاتا، یہاں تک کہ 70 آدمی جمع ہو گئے اور انہوں نے قریش کے قافلوں پر چھاپے مار مار کر ان کا ناطقہ تنگ کر دیا۔ آخر کار قریش نے خود رسول اللہ صلی اللہ علیہ و آلہ و سلم سے درخواست کی کہ ان لوگوں کو مدینہ بلا لیں اور حدیبیہ کے معاہدے کی وہ شرط آپ سے آپ ساقط ہو گئی۔ یہ تاریخی پس منظر نگاہ میں رکھ کر اس سورت کو پڑھا جائے تو اسے اچھی طرح سمجھا جا سکتا ہے۔ ','سید ابو اعلیٰ مودودیؒ -  تفہیم القرآن');
INSERT INTO "chapters_ur" VALUES(49,'حجرے',NULL,' نام: آیت 4 کے فقرے ان الذین ینا دونک من ورآء الحجرات سے ماخوذ ہے۔ مراد یہ ہے کہ وہ سورت جس میں لفظ الحجرات آیا ہے۔ زمانۂ نزول: یہ بات روایات سے بھی معلوم ہوتی ہے اور سورت کے مضامین بھی اس کی تائید کرتے ہیں کہ یہ سورت مختلف مواقع پر نازل شدہ احکام و ہدایات کا مجموعہ ہے جنہیں مضمون کی مناسبت سے یکجا کر دیا گیا ہے۔ علاوہ بریں روایات سے یہ بھی معلوم ہوتا ہے کہ ان میں اکثر احکام مدینہ طیبہ کے آخری دور میں نازل ہوئے ہیں۔ مثلاً آیت 4 کے متعلق مفسرین کا بیان ہے کہ یہ بنی تمیم کے بارے میں نازل ہوئی تھی جن کے وفد نے آ کر ازواج مطہرات کے حجروں کے باہر سے نبی صلی اللہ علیہ و آلہ و سلم کو پکارنا شروع کر دیا تھا، اور تمام کتب سیرت میں اس وفد کی آمد کا زمانہ 9ھ بیان کیا گیا ہے۔ اس طرح آیت 6 کے متعلق حدیث کی بکثرت روایات سے معلوم ہوتا ہے کہ یہ ولید بن عقبہ کے بارے میں نازل ہوئی تھی جنہیں رسول اللہ صلی اللہ علیہ و آلہ و سلم نے بنی المصطلق سے زکوٰۃ وصول کر کے لانے کے لیے بھیجا تھا اور یہ بات معلوم ہے کہ ولید بن عقبہ فتح مکہ کے موقع پر مسلمان ہوئے۔ موضوع و مباحث: اس سورت کا موضوع مسلمانوں کو ان آداب کی تعلیم دینا ہے جو اہل ایمان کے شایان شان ہیں۔ابتدائی پانچ آیتوں میں ان کو وہ ادب سکھایا گیا ہے جو انہیں اللہ اور اس کے رسول کے معاملے میں ملحوظ رکھنا چاہیے۔پھر یہ ہدایت دی گئی ہے کہ ہر خبر پر یقین کر لینا اور اس پر کوئی کارروائی کر گذرنا مناسب نہیں ہے۔ اگر کسی شخص یا گروہ یا قوم کے خلاف کوئی اطلاع ملے تو غور سے دیکھنا چاہیے کہ خبر ملنے کا ذریعہ قابل اعتماد ہے یا نہیں۔ قابل اعتماد نہ ہو تو اس پر کارروائی کرنے سے پہلے تحقیق کر لینا چاہیے کہ خبر صحیح ہے یا نہیں۔اس کے بعد بتایا گیا ہے کہ اگر کسی وقت مسلمانوں کے دو گروہ آپس میں لڑ پڑیں تو اس صورت میں دوسرے مسلمانوں کو کیا طرز عمل اختیار کرنا چاہیے۔پھر مسلمانوں کو ان برائیوں سے بچنے کی تاکید کی گئی ہے جو اجتماعی زندگی میں فساد برپا کرتی ہیں اور جن کی وجہ سے آپس کے تعلقات خراب ہوتے ہیں۔ ایک دوسرے کا مذاق اڑانا، ایک دوسرے پر طعن کرنا، ایک دوسرے کے برے برے نام رکھنا، بد گمانیاں کرنا، دوسرے کے حالات کی کھوج کرید کرنا، لوگوں کو پیٹھ پیچھے ان کی برائیاں کرنا، یہ وہ افعال ہیں جو بجائے خود بھی گناہ ہیں اور معاشرے میں بگاڑ بھی پیدا کرتے ہیں۔ اللہ تعالیٰ نے نام بنام ان کا ذکر فرما کر انہیں حرام قرار دے دیا ہے۔اس کے بعد ان قومی اور نسلی امتیازات پر ضرب لگائی گئی ہے جو دنیا میں عالمگیر فسادات کے موجب ہوتے ہیں۔ قوموں اور قبیلوں اور خاندانوں کا اپنے شرف پر فخر و غرور، اور دوسروں کو اپنے سے کمتر سمجھنا، اور اپنی بڑائی قائم کرنے کے لیے دوسروں کو گرانا، ان اہم اسباب میں سے ہے جن کی بدولت دنیا ظلم سے بھر گئی ہے۔ اللہ تعالیٰ نے ایک مختصر سی آیت فرما کر اس برائی کی جڑ کاٹ دی ہے کہ تمام انسان ایک ہی اصل سے پیدا ہوئے ہیں اور قوموں اور قبیلوں میں ان کا تقسیم ہونا تعارف کے لیے ہے نہ کہ تفاخر کے لیے، اور ایک انسان پر دوسرے انسان کی فوقیت کے لیے اخلاقی فضیلت کے سوا اور کوئی جائز بنیاد نہیں ہے۔آخر میں لوگوں کو بتایا گیا ہے کہ اصل چیز ایمان کا زبانی دعویٰ نہیں ہے بلکہ سچے دل سے اللہ اور اس کے رسول کو ماننا، عملاً فرمانبردار بن کر رہنا، اور خلوص کے ساتھ اللہ کی راہ میں اپنی جان و مال کھپا دینا ہے۔ حقیقی مومن وہی ہیں جو یہ روش اختیار کرتے ہیں۔ رہے وہ لوگ جو دل کی تصدیق کے بغیر محض زبان سے اسلام کا اقرار کرتے ہیں اور پھر ایسا رویہ اختیار کرتے ہیں کہ گویا اسلام قبول کر کے انہوں نے کوئی احسان کیا ہے، تو دنیا میں ان کا شمار مسلمانوں میں ہو سکتا ہے، معاشرے میں ان کے ساتھ مسلمانوں کا سا سلوک بھی کیا جا سکتا ہے، مگر اللہ کے ہاں وہ مومن قرار نہیں پا سکتے۔','سید ابو اعلیٰ مودودیؒ -  تفہیم القرآن');
INSERT INTO "chapters_ur" VALUES(50,'قٓ',NULL,' نام: آغاز ہی کے حرف ’’ق‘‘ سے ماخوذ ہے۔ مطلب یہ ہے کہ وہ سورۃ جس کا افتتاح حرف ق سے ہوتا ہے۔ زمانۂ نزول: کسی معتبر روایت سے یہ پتہ چلتا کہ یہ ٹھیک کس زمانہ میں نازل ہوئی ہے، مگر مضامین پر غور کرنے سے محسوس ہوتا ہے کہ اس کا زمانہ نزول مکہ معظمہ کا دوسرا دور ہے جو نبوت کے تیسرے سال سے شروع ہو کر پانچویں سال تک رہا۔ اس سورہ کی خصوصیات ہم سورہ انعام کے دیباچہ میں بیان کر چکے ہیں۔ ان خصوصیات کے لحاظ سے اندازاً یہ قیاس کیا جا سکتا ہے کہ سورہ پانچویں سال میں نازل ہوئی ہوگی جب کہ کفار کی مخالفت اچھی خاصی شدت اختیار کر چکی تھی، مگر ابھی ظلم و ستم کا آغاز نہیں ہوا تھا۔ موضوع اور مباحث: معتبر روایات سے معلوم ہوتا ہے کہ رسول اللہ صلی اللہ علیہ و سلم اکثر عیدین کی نمازوں میں اس سورۃ کی تلاوت فرمایا کرتے تھے۔ ایک خاتون ام ہشام بن حارثہ، جو حضورؐ کی پڑوسن تھیں، بیان کرتی ہیں کہ مجھے سورہ ق یاد ہی اسطرح ہوئی کہ میں جمعہ کے خطبوں میں اکثر آپ کی زبان مبارک سے اس کو سنتی تھی۔ بعض اور روایت میں آیا ہے کہ فجر کی نماز میں بھی آ پ بکثرت اس کو پڑھا کرتے تھے۔ اس سے یہ بات واضح ہے کہ حضورؐ کی نگاہ میں یہ ایک بڑی اہم سورۃ تھی۔ اس لیے آپ زیادہ سے زیادہ لوگوں تک بار بار اس کے مضامین پہنچانے کا اہتمام فرماتے تھے۔ اس اہمیت کی وجہ سورۃ کو بغور پڑھنے سے بآسانی سمجھ میں آ جاتی ہے۔ پوری سورۃ کا موضوع آخرت ہے۔ رسول اللہ صلی علیہ و سلم نے جب مکہ معظمہ میں اپنی دعوت کا آغاز کیا تو لوگوں کو سب سے زیادہ اچنبھا آپ کی جس بات پر ہوا وہ یہ تھی کہ مرنے کے بعد انسان دوبارہ اٹھائے جائیں گے اور ان کو اپنے اعمال کا حساب دینا ہو گا۔ لوگ کہتے تھے یہ یہ تو بالکل انہونی بات ہے۔ عقل باور نہیں کرتی کہ ایسا ہو سکتا ہے،آخر یہ کیسے ممکن ہے کہ جب ہمارا ذرہ ذرہ زین میں منتشر ہو چکا ہو تو ان پراگندہ اجزاء کو ہزارہا برس گزرنے کے بعد پھر اکٹھا کر کے ہمارا یہی جسم از سر نو بنا دیا جائے اور ہم زندہ ہو کر اٹھ گھڑے ہوں؟ اس کے جواب میں اللہ تعالیٰ کی طرف سے یہ تقریر نازل ہوئی۔ اس میں بڑے مختصر طریقے سے چھوٹے چھوٹے فقروں میں ایک طرف آخرت کے امکان اور اس کے وقوع پر دلائل دیے گئے ہیں، اور دوسری طرف لوگوں کو خبر دار کیا گیا ہے کہ تم خواہ تعجب کرو، یا بعید از عقل سمجھو، یا جھٹلاؤ، بہرحال اس سے حقیقت نہیں بدل سکتی حقیقت اور قطعی اٹل حقیقت یہ ہے کہ تمہارے جسم کا ایک ایک ذرہ جو زمین میں منتشر ہوتا ہے، اس کے متعلق اللہ کو معلوم ہے کہ وہ کہاں گیا ہے اور کس حال میں کس جگہ موجود ہے۔ اللہ تعالیٰ کا ایک اشارہ اس کے لیے کافی ہے کہ یہ تمام منتشر ذرات پھر جمع ہو جائیں اور تم کو اسی طرح دوبارہ بنا کھڑا کیا جائے جیسے پہلے بنایا گیا تھا۔ اسی طرح تمہارا یہ خیال کہ تم یہاں شتر بے کہار بنا کر چھوڑ دیے گئے ہو اور کسی کے سامنے تمہیں جواب دہی نہیں کرنی ہے، ایک غلط فہمی کے سوا کچھ نہیں ہے امر واقعہ یہ ہے کہ اللہ تعالیٰ براہ راست خود بھی تمہارے ہر قول و فعل سے، بلکہ تمہارے دل میں گزرنے والے خیالات تک سے واقف ہے، اور اس کے فرشتے بھی تم میں سے ہر شخص کے ساتھ لگے ہوئے تمام حرکات و سکنات کا ریکارڈ محفوظ کر رہے ہیں۔ جب وقت آئے گا تو ایک پکار پر تم بالکل اسی طرح نکل کھڑے ہو گے جس طرح بارش کا ایک چھینٹا پڑتے ہی زمین سے نباتات کی کونپلیں پھوٹ نکلتی ہیں۔ اس وقت یہ غفلت کا پردہ جو آج تمہاری عقل پر پڑا ہوا ہے، تمہارے سامنے سے ہٹ جائے گا اور تم اپنی آنکھوں سے وہ سب کچھ دیکھ لو گے جس کا آج انکار کر رہے ہو۔ اس وقت تمہیں معلوم ہو جائے گا کہ تم دنیا میں غیر ذمہ دار نہیں تھے بلکہ ذمہ دار اور جواب دہ تھے۔ جزا و سزا، عذاب و ثواب اور جنت و دوزخ جنہیں آج فسانہ عجائب سمجھ رہے ہو، اس وقت یہ ساری چیزیں تمہاری مشہود حقیقتیں ہونگی۔ حق سے عناد کی پاداش میں اسی جہنم کے اندر پھینکے جاؤ گے جسے آج عقل سے بعید سمجھتے ہو، اور خدائے رحمان سے ڈر کر راہ راست کی طرف پلٹنے والے تمہاری آنکھوں کے سامنے اسی جنت میں جائیں گے جس کا ذکر سن کر آج تمہیں تعجب ہو رہا ہے۔ ','سید ابو اعلیٰ مودودیؒ -  تفہیم القرآن');
INSERT INTO "chapters_ur" VALUES(51,'بکھیرنے والیاں',NULL,' نام: پہلے ہی لفظ وَالذّٰرِیَات سے ماخوذ ہے۔ مراد یہ ہے کہ وہ سورۃ جس کی ابتدا لفظ الذاریات سے ہوتی ہے۔ زمانۂ نزول : مضامین اور انداز بیان سے صاف محسوس ہوتا ہے کہ یہ سورۃ اس زمانے میں نازل ہوئی ہے جب نبی صلی اللہ علیہ و سلم کی دعوت کا مقابلہ تکذیب و استہزاء اور جھوٹے الزامات تو بڑے زور شور کے ساتھ ہو رہا تھا، مگر ابھی ظلم و تشدد کی چکی چلنی شروع نہیں ہوئی تھی۔ اس لیے یہ بھی اسی دور کی نازل شدہ معلوم ہوتی ہے جس میں سورہ ’’ق‘‘ نازل ہوئی ہے۔ موضوع اور مباحث: س کا بڑا حصہ آخرت کے موضوع پر ہے، اور آخر میں توحید کی دعوت پیش کی گئی ہے۔ اس کے ساتھ لوگوں کو اس بات پر بھی متنبہ کیا گیا ہے کہ انبیاء علیہم السلام کی بات نہ ماننا اور اپنے جاہلانہ تصورات پر اصرار کرنا خود انہی قوموں کے لیے تباہ کن ثابت ہوا ہے جنہوں نے یہ روش اختیار کی ہے۔ آخرت کے متعلق جو بات اس سورہ کے چھوٹے چھوٹے مگر نہایت پُر معنی فقروں میں بیان کی گئی ہے وہ یہ ہے کہ انسانی زندگی مآل و انجام کے بارے میں لوگوں کے مختلف اور متضاد عقیدے خود اس بات کا صریح ثبت ہیں کہ ان میں سے کوئی عقیدہ بھی علم پر مبنی نہیں ہے بلکہ ہر ایک نے قیاسات دوڑا کر اپنی جگہ جو نظریہ قائم کر لیا اسی کو وہ اپنا عقیدہ بنا کر بیٹھ گیا۔ کسی نے سمجھا کہ زندگی بعد موت نہیں ہو گی۔ کسی نے اس کو مانا تو تناسخ کی شکل میں مانا۔ کسی نے حیات بڑے اور اہم ترین بنیادی مسئلے پر، جس کے بارے میں آدمی کی رائے کا غلط ہو جانا اس کی پوری زندگی کو غلط کر کے رکھ دیتا ہے اور ہمیشہ ہمیشہ کے لیے اس کے مستقبل کو برباد کر ڈالتا ہے، علم کے بغیر محض قیاسات کی بنا پر کوئی عقیدہ بنا لینا ایک تباہ کن حماقت ہے۔ اس کے معنی یہ ہیں کہ آدمی ایک بہت بڑی غلط فہمی میں مبتلا رہ کر ساری عمر جاہلانہ غفلت میں گزار دے اور مرنے کے بعد اچانک ایک ایسی صورت حال سے دو چار ہو جس کے لیے اس نے قطعاً کوئی تیاری نہ کی تھی۔ ایسے مسئلے کے بارے میں صحیح رائے قائم کرنے کا بس ایک ہی راستہ ہے، اور وہ یہ ہے کہ انسان کا آخرت کے متعلق جو علم خدا کی طرف سے اس کا نبی دے رہا ہے اس پر وہ سنجیدگی کے ساتھ غور کرے اور زمین و آسمان کے نظام اور خود اپنے وجود پر نگاہ ڈال کر کھلی آنکھوں سے دیکھے کہ کیا اس علم کے صحیح ہونے کی شہادت ہر طرف موجود نہیں ہے؟ اس سلسلے میں ہوا اور بارش کے انتظام کو، زمین کی ساخت اور اس کی مخلوقات کو، انسان کے اپنے نفس کو، آسمان کی تخلیق کو، اور دنیا کی تمام اشیاء کے جوڑوں کی شکل میں بنائے جانے کو آخرت کی شہادت کے طور پر پیش کیا گیا ہے، اور انسانی تاریخ سے مثالیں دے کر بتایا گیا ہے کہ سلطنت کائنات کا مزاج کس طرح ایک قانون مکافات کا مقتضی نظر آ رہا ہے۔ اس کے بعد بڑے مختصر انداز میں توحید کی دعوت دیتے ہوئے فرمایا گیا ہے کہ تمہارے خالق نے تم کو دوسروں کی بندگی کے لیے نہیں بلکہ اپنی بندگی کے لیے پیدا کیا ہے۔ وہ تمہارے بناوٹی معبودوں کی طرح نہیں ہے جو تم رزق لیتے ہیں اور تمہاری مدد کے بغیر جن کی خدائی نہیں چل سکتی۔وہ ایسا معبود ہے جو سب کا رزاق ہے، کسی سے رزق لینے کا محتاج نہیں، اور جس کی خدائی خود اس کے اپنے بل بوتے پر چل رہی ہے۔ اسی سلسلے میں یہ بھی بتایا گیا ہے کہ انبیاء علیہم السلام کا مقابلہ جب بھی کیا گیا ہے کسی معقول بنیاد پر نہیں بلکہ اسی ضد اور ہٹ دھرمی اور جاہلانہ غرور کی بنیاد پر کیا گیا ہے جو آج محمد صلی اللہ علیہ و سلم کے ساتھ برتی جا رہی ہے۔ اور اس کی محرک بجز سر کشی کے اور کوئی چیز نہیں ہے۔ پھر محمد صلی اللہ علیہ و سلم کو ہدایت فرمائی گئی ہے کہ ان سرکشوں کی طرف التفات نہ کریں اور اپنی دعوت و تذکیر کا کام کیے جائیں، کیونکہ وہ ان لوگوں کے لیے چاہے نافع نہ ہو، مگر ایمان لانے والوں کے لیے نافع ہے۔ رہے وہ ظالم جو اپنی سرکشی پر مصر رہیں، تو ان سے پہلے اسی روش پر چلنے والے اپنے حصے کا عذاب پا چکے ہیں اور ان کے حصے کا عذاب تیار ہے۔','سید ابو اعلیٰ مودودیؒ -  تفہیم القرآن');
INSERT INTO "chapters_ur" VALUES(52,'طور پہاڑ',NULL,' نام: پہلے ہی لفظ ’’وَالطُّوْرِ ‘‘ سے ماخوذ ہے ۔ زمانۂ نزول: مضامین کی اندرونی شہادت سے اندازہ ہوتا ہے کہ یہ بھی کیہ معظمہ کے اسی دور میں نازل ہوئی ہے جس میں سورہ ذاریات نازل ہوئی تھی۔ اس کی پڑھتے ہوئے یہ تو ضرور محسوس ہوتا ہے کہ اس کے نزول کے زمانے میں نبی صلی اللہ علیہ و سلم کے خلاف اعتراضات اور الزامات کی بوچھاڑ ہو رہی تھی، مگر یہ محسوس نہیں ہوتا کہ ظلم و ستم کی چکی زور شور سے چلنی شروع ہو گئی تھی۔ موضوع اور مباحث: اس کے پہلے رکوع کا موضوع آخرت ہے ۔ سورہ ذاریات میں اس کے امکان اور وجوب اور وقوع کے دلائل دیے جاچکے تھے ، اس لیے یہاں ان کا اعادہ نہیں کیا گیا ہے ، البتہ آخرت کی شہادت دینے والے چند حقائق و آثار کی قسم کھا کر پورے زور کے ساتھ یہ فر مایا گیا ہے کہ وہ یقیناً واقع ہو کر رہے گی اور کسی میں یہ طاقت نہیں ہے کہ اسے برپا ہونے سے روک دے ۔ پھر یہ بتایا گیا ہے کہ جب وہ پیش آئے گی تو اس کے جھٹلانے والوں کا انجام کیا ہوگا، اور اسے مان کر تقویٰ کی روش اختیار کر لینے والے کس طرح اللہ کے انعامات سے سرفراز ہوں گے ۔اس کے بعد دوسرے رکوع میں سرداران قریش کے اس رویے پر تنقید کی گئی ہے جو وہ رسول اللہ صلی اللہ علیہ و سلم کی دعوت کے مقابلے میں اختیار کیے ہوئے تھے ۔ وہ آپ کو کبھی کاہن، کبھی مجنون اور کبھی شاعر قرار دے کر عوام الناس کو آپ کے خلاف بہکاتے تھے تاکہ لوگ آپ کے لائے ہوئے پیغام کی طرف سنجیدگی سے توجہ نہ کریں ۔ وہ آپ کی ذات کو اپنے حق میں ایک بلائے ناگہانی سمجھتے تھے اور علانیہ کہتے تھے کہ کوئی آفت ان پر نازل ہو جائے تو ہمارا ان سے پیچھا چھوٹے ۔ وہ آپ پر الزام لگاتے تھے کہ یہ قرآن آپ خود گھڑ گھڑ کر خدا کے نام سے پیش کر رہے ہیں اور یہ معاذاللہ ایک فریب ہے جو آپ نے بنا رکھا ہے ۔ وہ بار بار طنز کرتے تھے کہ خدا کو نبوت کے لیے ملے بھی تو بس یہ صاحب ملے ۔ وہ آپ کی دعوت و تبلیغ سے ایسی بیزاری کا اظہار کرتے تھے جیسے آپ کچھ مانگنے کے لیے ان کے پیچھے پڑے ہوئے ہیں اور وہ اپنی جان چھڑانے کے لیے آپ سے منہ چھپاتے پھرتے ہیں ۔ وہ آپس میں بیٹھ بیٹھ کر سوچتے تھے کہ آپ کے خلاف کیا چال ایسی چلی جائے جس سے آپ کی اس دعوت کا خاتمہ ہو جائے۔ اور یہ سب کچھ کرتے ہوئے انہیں اس امر کا کوئی احساس نہ تھا کہ وہ کیسے جاہلانہ عقائد میں مبتلا ہیں جن کی تاریکی سے لوگوں کو نکالنے کے لیے محمد صلی اللہ علیہ و سلم بالکل بے غرضانہ اپنی جان کھپا رہے ہیں ۔ اللہ تعالیٰ نے ان کے اسی رویے پر تنقید کرتے ہوئے پے در پے کچھ سوالات کیے ہیں جن میں سے ہر سوال یا تو ان کے کسی اعتراض کا جواب ہے یا ان کی کسی جہالت پر تبصرہ۔ پھر فرمایا ہے کہ ان لوگوں کو آپ کی نبوت کا قائل کرنے کے لیے کوئی معجزہ دکھانا قطعی لا حاصل ہے ، کیونکہ یہ ایسے ہٹ دھرم لوگ ہیں انہیں خواہ کچھ بھی دکھا دیا جائے، یہ اس کی کوئی تاویل کر کے ایمان لانے سے گریز کر جائیں گے ۔اس رکوع کے آغاز میں بھی رسول اللہ صلی اللہ علیہ و سلم کو یہ ہدایت فرمائی گئی ہے کہ ان مخالفین و معاندین کے الزامات و اعتراضات کی پروا کیے بغیر اپنی دعوت و تذکیر کا کام مسلسل جاری رکھیں ، اور آخر میں بھی آپ کو تاکید فرمائی گئی ہے کہ صبر کے ساتھ ان مزاحمتوں کا مقابلہ کیے چلے جائیں یہاں تک کہ اللہ تعالیٰ کا فیصلہ آ جائے۔ اس کے ساتھ آپ کو اطمینان دلایا گیا ہے کہ آپ کے رب نے آپ کو دشمنان حق کے مقابلے میں اٹھا کر اپنے حال پر چھوڑ نہیں دیا ہے بلکہ وہ برابر آپ کی نگہبانی کر رہا ہے ۔ جب تک اس کے فیصلے کی گھڑی آئے، آپ سب کچھ برداشت کرتے رہیں اور اپنے رب کی حمد و تسبیح سے وہ قوت حاصل کرتے رہیں جو ایسے حالات میں اللہ کا کام کرنے کے لیے درکار ہوتی ہے ۔','سید ابو اعلیٰ مودودیؒ -  تفہیم القرآن');
INSERT INTO "chapters_ur" VALUES(53,'ستارہ',NULL,' نام: پہلے ہی لفظ ہی وَالنجم سے ماخوذ ہے۔ یہ بھی مضمون کے لحاظ سے سورۃ کا عنوان نہیں ہے بلکہ محض علامت کے طور پر اس کا نام قرار دیا گیا ہے ۔ زمانۂ نزول: بخاری، مسلم، ابوداؤد اور نسائی میں حضرت عبداللہ بن مسعود کی روایت ہے کہ اَوَّلُ سَوْرَۃٍ اُنْزِلَتْ فِیھا سجدۃٌ النجم (پہلی سورۃ جس میں آیت سجدہ نازل ہوئی النجم ہے )۔ اس حدیث کے جو اجزاء اسود بن یزید، ابواسحاق، اور زُہَیر بن معاویہ کی روایات میں حضرت ابن مسعود سے منقول ہوئے ہیں ان سے معلوم ہوتا ہے کہ یہ قرآن مجید کی وہ پہلی سورۃ ہے جسے رسول اللہ صلی اللہ علیہ و سلم نے قریش کے ایک مجمع عام میں ( اور ابن مردوؤیہ کی روایت کے مطابق حَرَم میں)سنایا تھا۔ مجمع میں کافر اور مومن سب موجود تھے ۔آخر میں جب آپ نے آیت سجدہ پڑھ کر سجدہ فرمایا تو تمام حاضرین آپ کے ساتھ سجدے میں گر گئے اور مشرکین کے وہ بڑے بڑے سردار تک، جو مخالفت میں پیش پیش تھے سجدہ کیے بغیر نہ رہ سکے ۔ ابن مسعد و رضی اللہ تعالیٰ عنہ بیان کرتے ہیں کہ میں نے کفار میں سے سرف ایک شخص امیہ بن خَلَف کو دیکھا کہ اس نے سجدہ کرنے کے بجائے کچھ مٹی اٹھا کر اپنی پیشانی سے لگا لی اور کہا کہ میرے لیے بس یہی کافی ہے ۔ بعد میں میری آنکھوں نے دیکھا کہ وہ کفر کی حالت میں قتل ہوا۔ اس واقعہ دے دوسرے عینی شاہد حضرت مُطلب بن ابی وَداعَہ ہیں جو اس وقت تک مسلمان نہ ہوئے تھے ۔ نسائی اور مُسند احمد میں ان کا اپنی بیان یہ نقل ہوا ہے کہ جب حضورؐ نے سورہ نجم پڑھ کر سجدہ فرمایا اور سب حاضرین آپ کے ساتھ سجدے میں گر گئے تو میں نے سجدہ نہ کیا، اور اسی کی تلافی اب میں اس طرح کرتا ہوں کہ اس سورے کی تلاوت کے وقت سجدہ کبھی نہیں چھوڑتا۔ ابن سعد کا بیان ہے کہ اس سے پہلے رجب 5 نبوی میں صحابہ کرام کی ایک مختصر سی جماعت حبش میں طرف ہجرت کر چکی تھی۔ پھر جب اسی سال رمضان میں یہ واقعہ پیش آیا کہ رسول اللہ صلی اللہ علیہ و سلم نے قریش کے مجمع عام میں سورہ نجم کی تلاوت فرمائی اور کافر و مومن سب آپ کے ساتھ سجدے میں گر گئے، تو حبش کے مہاجرین تک یہ قصہ اس شکل میں پہنچا کہ کفارمکہ مسلمان ہو گئے ہیں۔ اس خبر کو سن کر ان میں سے کچھ لوگ شوال 5 نبوی میں مکہ واپس آ گئے۔مگر یہاں آ کر معلوم ہوا کہ ظلم کی چکی اسی طرح چل رہی ہے جس طرح پہلے چل رہی تھی، آخر کار دوسرے ہجرت حبشہ واقع ہوئی جس میں پہلی ہجرت سے بھی زیادہ لوگ مکہ چھوڑ کر چلے گئے۔ اس طرح یہ بات قریب قریب یقینی طور پر معلوم ہو جاتی ہے کہ یہ سورۃ رمضان 5 نبوی میں نازل ہوئی ہے ۔ تاریخی پس منظر: زمانہ نزول کی اس تفصیل سے معلوم ہو جاتا ہے کہ وہ کیا حالات تھے جن میں یہ سورہ نازل ہوئی۔ ابتدائے بعثت کے بعد سے پانچ سال تک رسول اللہ صلی اللہ علیہ و سلم صرف نجی صحبتوں اور مخصوص مجلسوں ہی میں اللہ کا کلام سنا سنا کر لوگوں کو اللہ کے دین کی طرف دعوت دیتے رہے تھے ۔ اس پوری مدت میں آپ کو کبھی کسی مجمع عام میں قرآن سنانے کا موقعنہ مل سکا تھا، کیونکہ کفار کی سخت مزاحمت اس میں مانع تھی۔ ان کو اس امر کا خوب اندازہ تھا کہ آپ کی شخصیت اور آپ کی تبلیغ میں سک بال کہ کشش، اور قرآن مجید کی آیات میں کس غضب کی تاثیر ہے ۔اس لیے وہ کوشش کرتے تھے کہ اس کلام کو نہ خود نہیں نہ کسی کو سننے دیں، اور آپ کے خلاف طرح طرح کی غلط فہمیاں پھیلا کر محض اپنے جھوٹے پروپیگنڈے کے زور سے آپ کی دعوت کو دبا دیں۔ اس غرض کے لیے ایک طرف تو وہ جگہ جگہ یہ مشہور کرتے پھر رہے تھے کہ محمد صلی اللہ علیہ و سلم بہک گئے ہیں اور لوگوں کو گمراہ کرنے کے درپے ہیں۔ دوسری طرف ان کا یہ مستقل طریق کار تھا کہ جہاں بھی آپ قرآن سنانے کی کوشش کریں وہاں شور مچا دیا جائے تاکہ لوگ یہ جان ہی نہ سکیں کہ وہ بات کیا ہے جس کی بنا پر آپ کو گمراہ اور بہکا ہوا آدمی قرار دیا جا رہا ہے ۔ ان حالات میں ایک روز رسول اللہ صلی اللہ علیہ و سلم حرم پاک میں جہاں قریش کے لوگوں کا ایک بڑا مجمع موجود تھا، یکایک تقریر کرنے کھڑے ہو گئے اور اس وقت اللہ تعالیٰ کی طرف سے آپ کی زبان مبارک پر یہ خطبہ جاری ہوا جو سورہ نجم کی صورت میں ہمارے سامنے ہے ۔اس کلام کی شدت تاثیر کا حال یہ تھا کہ جب آپ نے اسے سنانا شروع کیا تو مخالفین کو اس پر شور مچانے کا ہوش ہی نہ رہا، اور خاتمے پر جب آپ نے سجدہ فرمایا تو وہ بھی سجدے میں گر گئے۔ بعد میں انہیں سخت پریشانی لاحق ہوئی کہ یہ ہم سے کیا کمزوری سرد ہو گئی، اور لوگوں نے بھی انہیں اس پر مطعون کرنا شروع کیا کہ دوسروں کو تو یہ کلام سننے سے منع کرتے تھے ، آج خود اسے نہ صرف کان لگا کر سنا بلکہ محمد صلی اللہ علیہ و سلم کے ساتھ سجدہ بھی کر گزرے ۔ آخر کار انہوں نے یہ بات بنا کر اپنا پیچھا چھڑایا کہ صاحب ہمارے کانوں نے تو : اَفَرْءَیْتُمُ اللّٰتَ وَا لْعزّیٰ۔ وَمَنٰوۃَ الثَّا لِثَۃَ الْقُخْریٰ کے بعد محمدؐ کی زبان سے یہ الفاظ سنے تھے تلک الغرانقۃ العُلیٰ، وان شفا گتھنا لتر جیٰ (یہ بلند مرتبہ دیویاں ہیں اور ان کی شفاعت ضرور متوقع ہے )، اس لیے ہم نے سمجھا کہ محمدؐ ہمارے طریقے پر واپس آ گئے ہیں۔ حالانکہ کوئی پاگل آدمی ہی یہ سوچ سکتا تھا کہ اس پوری سورۃ کے سیاق و سباق میں ان فقروں کی بھی کوئی جگہ ہو سکتی ہے جو ان کا دعویٰ تھا کہ ان کے کانوں نے سنے ہیں(تفصیل کے لیے ملاحظہ ہو تفہیم القرآن، جلد سوم،الحج، حواشی 96 تا 101 )۔ موضوع اور مضمون: تقریر کا موضوع کفار مکہ کو اس رویے کی غلطی پر متنبہ کرنا ہے جو وہ قرآن اور محمد صلی اللہ علیہ و سلم کے مقابلے میں اختیار کیے ہوئے تھے ۔ کلام کا آغاز اس طرح فرمایا گیا ہے کہ محمد صلی اللہ علیہ و سلم بہکے اور بھٹکے ہوئے آدمی نہیں ہیں، جیسا کہ تم ان کے متعلق مشہور کرتے پر رہے ہو، اور نہ اسلام کی یہ تعلیم اور دعوت انہوں نے خود اپنے دل سے گھڑ لی ہے ، جیسا کہ تم اپنے نزدیک سمجھے بیٹھے ہو، بلکہ جو کچھ وہ پیش کر رہے ہیں وہ خالص وحی ہے جو ان پر نازل کی جاتی ہے ۔ جن حقیقتوں کو وہ تمہارے سامنے بیان کرتے ہیں وہ ان کے اپنے قیاس و گمان کی آفریدہ نہیں ہیں بلکہ ان کی آنکھوں دیکھی حقیقتیں ہیں۔ انہوں نے اس فرشتے کو خود دیکھا ہے جس کے ذریعہ سے ان کو یہ علم دیا جاتا ہے ۔ انہیں اپنے رب کی عظیم نشانیوں کا براہ راست مشاہدہ کرایا گیا ہے ۔وہ جو کچھ کہہ رہے ہیں سوچ کر نہیں دیکھ کر کہہ رہے ہیں۔ ان سے تمہارا جھگڑنا بالکل ایسا ہی ہے جیسے کوئی اندھا آنکھوں والے سے اس چیز پر جھگڑے جو اسے نظر نہیں آتی اور آنکھوں والے کو نظر آتی ہے ۔ اس کے بعد علی الترتیب تین مضامین ارشاد ہوئے ہیں: اوّلاً سامعین کو سمجھایا گیا ہے کہ جس دین کی تم پیروی کر رہے ہو اس کی بنیاد محض گمان اور من مانے مفروضات پر قائم ہے ۔ تم نے لات اور منات اور عزّیٰ جیسی چند دیویوں کے معبود بنا رکھا ہے ، حالانکہ اُلوہیت میں برائے نام بھی ان کا کوئی حصہ نہیں ہے ۔ تم نے فرشتوں کو اللہ کی بیٹیاں قرار دے رکھا ہے ،حالانکہ خود اپنے لی تم بیٹی کو عار سمجھتے ہو۔ تم نے اپنے نزدیک یہ فرض کر لیا ہے کہ تمہارے یہ معبود اللہ تعالیٰ سے تمہارے کام بنوا سکتے ہیں، حالانکہ تمام ملائکہ، مقربین مل کر بھی اللہ سے اپنی کوئی بات نہیں منوا سکتے ۔ اس طرح کے عقائد جو تم نے اختیار کر رکھے ہیں، ان میں سے کوئی عقیدہ بھی کسی علم اور دلیل پر مبنی نہیں ہے ، بلکہ کچھ خواہشات ہیں جن کی خاطر تم بعض اوہام کو حقیقت سمجھ بیٹھے ہو۔ یہ ایک بہت بڑی بنیادی غلطی ہے جس میں تم لوگ مبتلا ہو۔ دین وہی صحیح ہے جو حقیقت کے مطابق ہو۔ اور حقیقت لوگوں کی خواہشات کی تابع نہیں ہوا کرتی کہ جسے وہ حقیقت سمجھ بیٹھیں وہی حقیقت ہو جائے۔ اس سے مطابقت کے لیے قیاس و گمان کام نہیں دیتا، بلکہ اس کے لیے علم درکار ہے ۔ وہ علم تمہارے سامنے پیش کیا جاتا ہے تو تم اس سے منہ موڑتے ہو اور الٹا اس شخص کو گمراہ ٹھیراتے ہو جو تمہیں صحیح بات بتا رہا ہے ۔ اس غلطی میں تمہارے مبتلا ہونے کی اصل وجہ یہ ہے کہ تمہیں آخرت کی کوئی فکر نہیں ہے ، بس دنیا ہی تمہاری مطلوب بنی ہوئی ہے ۔ اس لیے نہ تمہیں حقیقت کو کوئی طلب ہے ،نہ اس بات کی کوئی پروا کہ جن عقائد کی تم پیروی کر رہے ہو وہ حق کے مطابق ہیں یا نہیں۔ ثانیاً لوگوں کو یہ بتایا گیا ہے کہ اللہ ہی ساری کائنات کا مالک و مختار ہے ۔ راست رو وہ ہے جو اس کے راستے پر ہو، اور گمراہ وہ جو اس کی راہ سے ہٹا ہوا ہو۔ گمراہ کی گمراہی اور راست رو کی راست روی اس سے چھپی ہوئی نہیں ہے ۔ ہر ایک کے عمل کو وہ جانتا ہے اور اس کے ہاں لازماً برائی کا بدلہ برا اور بھلائی کا بدلہ بھلا مل کر رہنا ہے ۔ اصل فیصلہ اس پر نہیں ہوتا کہ تم اپنے زعم میں اپنے آپ کو کیا سمجھتے ہو اور اپنی زبان سے اپنی پاکیزگی کے کتنے لمبے چوڑے دعوے کرتے ہو، بلکہ فیصلہ اس اس پر ہونا ہے کہ خدا کے علم میں تم متقی ہو یا نہیں۔ اگر تم بڑے بڑے گناہوں سے اجتناب کرو تو اس کی رحمت اتنی وسیع ہے کہ چھوٹے چھوٹے قصوروں سے وہ در گزر فرمائے گا۔ ثالثاً، دین حق کے وہ چند بنیادی امور لوگوں کے سامنے پیش کیے گئے ہیں جو قرآن مجید کے نزول سے صدہا برس پہلے حضرت ابراہیم اور حضرت موسیٰ کے صحیفوں میں بیان ہو چکے تھے ، تاکہ لوگ اس غلط فہمی میں نہ رہی کہ محمد صلی اللہ علیہ و سلم کوئی نیا اور نرالا دین لے آئے ہیں، بلکہ ان کو معلوم ہو جائے کہ یہ وہ اصلی حقائق ہیں جو ہمیشہ سے خدا کے نبی بیان کرتے چلے آئے ہیں۔ اس کے ساتھ ان ہی صحیفوں سے یہ بات بھی نقل کر دی گئی ہے کہ عاد اور ثمود اور قوم نوح اور قوم لوط کی تباہی اتفاقی حوادث کا نتیجہ نہ تھی بلکہ اللہ تعالیٰ نے اسی ظلم و طغیان کی پاداش میں ان کو ہلاک کیا تھا جس سے باز آنے پر کفار مکہ کسی طرح آمادہ نہیں ہو رہے ہیں۔ یہ مضامین ارشاد فرمانے کے بعد تقریر کا خاتمہ اس بات پر کیا گیا ہے کہ فیصلے کی گھڑی قریب آ لگی ہے جسے کوئی ٹالنے والا نہیں ہے ۔ اس گھڑی کے آنے سے پہلے محمد صلی اللہ علیہ و سلم اور قرآن مجید کے ذریعہ سے تم لوگوں کو اسی طرح خبردار کیا جا رہا ہے جس طرح پہلے لوگوں کے خبردار کیا گیا تھا۔اب کیا یہی وہ بات ہے جو تمہیں انوکھی لگتی ہے ؟ جس کی تم ہنسی اڑاتے ہو؟ باز آ جاؤ اپنی اس روش سے جھک جاؤ اللہ کے سامنے اور اسی کی بندگی کرو۔ یہی وہ مؤثر خاتمہ کلام تھا جسے سن کر کٹے سے کٹے منکرین بھی ضبط نہ کر سکے اور رسول اللہ صلی اللہ علیہ و سلم نے جب کلام الٰہی کے یہ فقرے ادا کر کے سجدہ کیا تو وہ بھی بے اختیار سجدے میں گر گئے۔','سید ابو اعلیٰ مودودیؒ -  تفہیم القرآن');
INSERT INTO "chapters_ur" VALUES(54,'چاند',NULL,' نام: پہلی ہی آیت کے فقرہ وَانْشَقَّ الْقَمَر سے ماخوذ ہے ۔ مطلب یہ ہے کہ وہ سورۃ جس میں لفظ القمر آیا ہے ۔ زمانۂ نزول: اس میں شقّ القمر کے واقعہ کا ذکر آیا ہے جس سے اس کا زمانہ نزول متعین ہو جاتا ہے ۔ محدثین و مفسرین کا اس پر اتفاق ہے کہ یہ واقعہ ہجرت سے تقریباً پانچ سال پہلے مکہ معظمہ میں منیٰ کے مقام پر پیش آیا تھا۔ موضوع اور مضمون: اس میں کفار مکہ کو اس ہٹ دھرمی پر متنبہ کیا گیا ہے جو انہوں نے رسول اللہ صلی اللہ علیہ و سلم کی دعوت کے مقابلہ میں اختیار کر رکھی تھی۔ شق القمر کا حیرت انگیز واقعہ اس بات کا صریح نشان تھا کہ وہ قیامت جس کے آنے خبر رسول اللہ صلی اللہ علیہ و سلم دے رہے تھے ، فی الواقع برپا ہو سکتی ہے ، اور اس کی آمد کا وقت قریب آ لگا ہے ۔ چاند جیسا عظیم الشان کرہ ان کی آنکھوں کے سامنے پھٹا تھا۔ اس کے دونوں ٹکڑے الگ ہو کر ایک دوسرے سے اتنی فور چلے گئے تھے کہ دیکھنے والوں کو ایک ٹکڑا پہاڑ کے ایک طرف اور دوسرا ٹکڑا دوسری طرف نظر آیا تھا۔ پھر آن کی آن میں وہ دونوں پھر مل گئے تھے ۔ یہ اس بات کا کھلا ثبوت تھا کہ نظام عالم ازلی و ابدی اور غیر فانی نہیں ہے ۔ وہ درہم برہم ہو سکتا ہے ۔ بڑے بڑے ستارے اور سیارے پھٹ سکتے ہیں ۔ بکھر سکتے ہیں ۔ ایک دوسرے سے ٹکرا سکتے ہیں ۔ اور وہ سب کچھ ہو سکتا ہے جس کا نقشہ قیامت کی تفصیلات بیان کرتے ہوئے قرآن میں کھینچا گیا ہے ۔ یہی نہیں ، بلکہ یہ اس امر کا پتا بھی دے رہا تھا کہ نظام عالم کے درہم برہم ہونے کا آغاز ہو گیا ہے اور وہ وقت قریب ہے جب قیامت برپا ہو گی۔ نبی صلی اللہ علیہ و سلم نے اسی حیثیت سے لوگوں کو اس وقعہ کی طرف توجہ دلائی اور فرمایا، دیکھو اور گواہ رہو۔ مگر کفار نے اسے جادو کا کرشمہ قرار یا اور اپنے انکار پر جمے رہے ۔ اسی ہٹ دھرمی پر اس سورہ میں انہیں ملامت کی گئی ہے ۔کلام کا آغاز کرتے ہوئے فرمایا گیا کی یہ لوگ نہ سمجھانے سے مانتے ہیں ۔ اب یہ اسی وقت مانیں گے جب قیامت فی الواقع برپا ہو جائے گی اور قبروں سے نکل کو یہ داورِ محشر کی طرف دوڑے جا رہے ہوں گے ۔اس کے بعد ان کے سامنے قوم نوح، عاد،قوم لوط ،اور آل فرعون کا حال مختصر الفاظ میں بیان کر کے بتایا گیا ہے کہ خدا کے بھیجے ہوئے رسولوں کی تنبیہات کو جھٹلا کر یہ قومیں کس درد ناک عذاب سے دو چار ہوئیں ، اور ایک ایک قوم کا قصہ بیان کرنے کے بعد بار بار یہ بات دہرائی گئی ہے کہ یہ قرآن نصیحت کا آسان ذریعہ ہے جس سے اگر کوئی قوم سبق لے کر راہ راست پر آ جائے تو ان عذابوں کی نوبت نہیں آسکتی جو ان قوموں پر نازل ہوئے۔ اب آخر یہ کیا حماقت ہے کہ اس آسان ذریعہ سے نصیحت قبول کرنے کے بجائے کوئی اسی پر اصرار کرے کہ عذاب دیکھے بغیر نہ مانے گا۔اس طرح پچھلی قوموں کی تاریخ سے عبرتناک مثالیں دینے کے بعد کفار مکہ کو خطاب کر کے فرمایا گیا ہے کہ جس طرزِ عمل پر دوسری قومیں سزا پا چکی ہیں وہی طرز عمل اگر تم اختیار کرو تو آخر تم کیوں نہ سزا پاؤ گے ؟ کیا تمہارے کچھ سُرخاب کے پر لگے ہوئے ہیں کہ تمہارے ساتھ دوسروں سے مختلف معاملہ کیا جائے ؟ یا کوئی خاص معافی نامہ تمہارے پاس لکھا ہوا آگیا ہے کہ جس جرم پر دوسرے پکڑے گئے ہیں وہی تم کرو گے تو تمہیں نہ پکڑا جائے گا؟ اور اگر تم اپنی جمیعت پر پھولے ہوئے ہو تو عنقریب تمہاری یہ جمیعت شکست کھا کر بھاگتی نظر آئے گی، اور اس سے زیادہ سخت معاملہ تمہارے ساتھ قیامت کے روز ہو گا۔ آخر میں کفار کو بتایا گیا ہے کہ اللہ تعالیٰ کو قیامت لانے کے لیے کیس بڑی تیاری کی حاجت نہیں ہے ۔ اس کا بس ایک حکم ہوتے ہی پلک جھپکاتے وہ برپا ہو جائے گی۔ مگر ہر چیز کی طرح نظام عالم اور نوع انسانی کی بھی ایک تقدیر ہے ۔ اس تقدیر کے لحاظ سے جو وقت اس کام کے لیے مقرر ہے اسی وقت پر وہ لوگا۔ یہ نہیں ہو سکتا کہ جب کوئی چیلنج کرے اس کو قائل کرنے کے لیے قیامت لا کھڑی کی جائے ۔ اس کو آتے نہ دیکھ کر تم سرکشی اختیار کرو گے تو اپنی شامت اعمال کا نتیجہ بھگتو گے ۔ تمہارا کچّا چٹھا خدا کے ہاں تیار ہو رہا ہے جس میں تمہاری کوئی چھوٹی یا بڑی حرکت ثبت ہونے سے رہ نہیں گئی ہے ۔','سید ابو اعلیٰ مودودیؒ -  تفہیم القرآن');
INSERT INTO "chapters_ur" VALUES(55,'مہربان',NULL,' نام: پہلے ہی لفظ کو اس سورہ کا نام قرار دیا گیا ہے۔ اس کا مطلب یہ ہے کہ یہ سورۃ ہے جو لفظ الرحمٰن سے شروع ہوتی ہے۔ تاہم اس نام کو سورۃ کے مضمون سے بھی گہری مناسبت ہے، کیونکہ اس میں شروع سے آخر تک اللہ تعالیٰ کی سفت رحمت کے مظاہر و ثمرات کا ذکر فرمایا گیا ہے۔ زمانۂ نزول: علمائے تفسیر بالعموم اس سورۃ کو مکی قرار دیتے ہیں۔ اگر چہ بعض روایات میں حضرت عبداللہ بن عباس اور عکرمہ اور قتادہ سے یہ قول منقول ہے کہ یہ سورۃ مدنی ہے، لیکن اول تو انہی بزرگوں سے بعض روایات اس کے خلاف بھی منقول ہوئی ہیں، دوسرے اس کا مضمون مدنی سورتوں کی بہ نسبت مکی سورتوں سے زیادہ مشابہ ہے، بلکہ اپنے مضمون کے لحاظ سے یہ مکہ کے بھی ابتدائی دور کی معلوم ہوتی ہے۔ اور مزید براں متعدد معتبر روایات سے اس امر کا ثبوت ملتا ہے کہ یہ مکہ معظمہ ہی میں ہجرت سے کئی سال قبل نازل ہوئی تھی۔ مسند احمد میں حضرت اسماء بنت ابو بکر رضی اللہ عنہما کی روایت ہے کہ ’‘میں نے رسول اللہ صلی اللہ علیہ و سلم کو حرم میں خانہ کعبہ کے اس گوشے کی طرف رخ کر کے نماز پڑھتے دیکھا جس میں حجر اسود نصب ہے۔ یہ اس زمانے کی بات ہے جبکہ ابھی فَاصْدَعْ بِمَا تُؤْمَرْ (جس چیز کا تمہیں حکم دیا جا رہا ہے اسے ہانکے پکارے کہہ دو) کا فرمان الٰہی نازل نہیں ہوا تھا۔ کہ مشرکین اس نماز میں آپ کی زبان سے فَبِاَئِ اٰلَآءِ رَبِّکُمَا تُکَذِّبٰنِ کے الفاظ سن رہے تھے ‘‘۔ اس سے معلوم ہوا کہ یہ سورۃ سورہ الحجر سے پہلے نازل ہو چکی تھی۔ البزار، ابن جریر،ابن المنذ،ر، دار قظنی (فیالفراد)، ابن مردویہ، اور الخطیب(فی التاریخ) نے حضرت عبداللہ بن عمر سے یہ روایت نقل کی ہے کہ ایک مرتبہ رسول اللہ صلی اللہ علیہ و سلم نے سورہ رحمٰن خود تلاوت فرمائی، یا آپ کے سامنے یہ سورۃ پڑھی گئی۔ پھر آپ نے لوگوں سے فرمایا کہ ’’ کیا وجہ ہے کہ میں تم سے ویسا اچھا جواب نہیں سن رہا ہوں جیسا جِنوں نے اپنے رب کو دیا تھا‘‘؟ لوگوں نے عرض کیا وہ کیا جواب تھا؟ آپ نے فرمایا کہ ’’ جب میں اللہ تعالیٰ کا ارشاد فَبِاَیِّ اٰلَآ ءِرَبِّکُمَا تُکَذِّبٰنِ پڑھتا تو جن اس کے جواب میں کہتے جاتے تھے کہ لَآ بِشَیءٍ مِّنْ نِعْمَۃِ رَبِّنَا نُکَذِّبُ ، ’’رب کی کسی نعمت کو نہیں جھٹلاتے ‘‘۔ اسی سے ملتا جلتا مضمون ترمذی، حاکم اور حافظ ابوبکر بزار نے حضرت جابر بن عبداللہ سے نقل کیا ہے۔ ان کی روایت میں یہ الفاظ ہیں کہ جب لوگ سورہ رحمٰن کو سن کر خاموش رہے تو حضورؐ نے فرمایا لقد قرأتھا علی الجن لیلۃ الجن فکانوا احسن مردوداً منکم، کنت کلما اتیت علیٰ قولہ "فَبِاَیِّ اٰلَآ ءِرَبِّکُمَا تُکَذِّبٰنِ" قالو لا بِشَیْءٍ مِنْ نِعَمِکَ رَبَّنَا نُکَذِّب فَلَکَ الحْمد ۔۔ یعنی ’’ میں نے یہ سورۃ جِنوں کو سنائی تھی جس میں وہ قرآن سننے کے لیے جمع ہوئے تھے۔وہ اس کا جواب تم سے بہتر دے رہے تھے۔ جب میں اللہ تعالیٰ کے اس ارشاد پر پہنچتا تھا کہ اے جن و انس تم اپنے رب کی کن کن نعمتوں کو جھٹلاؤ گے، تو وہ اس کے جواب میں کہتے تھے کہ اے ہمارے پروردگار، ہم تیری کسی نعمت کو نہیں جھٹلاتے، حمد تیرے ہی لیے ہے۔‘‘ اس روایت سے معلوم ہوا کہ سورہ احقاف (آیات 29۔ 32) میں رسول اللہ صلی اللہ علیہ و سلم کی زبان مبارک سے جنوں کے قرآن سننے کا جو واقعہ بیان کیا گیا ہے، اس موقع پر حضورؐ نماز میں سورہ رحمٰن تلاوت فرما رہے تھے۔ یہ 10 نبوی کا واقعہ ہے جب آپ سفر طائف سے واپسی پر نخلہ میں کچھ مدت ٹھیرے تھے۔ اگر چہ بعض دوسری روایات میں یہ بیان کیا گیا ہے کہ اس موقع پر رسول اللہ صلی اللہ علیہ و سلم کو یہ معلوم نہ تھا کہ جن آپ سے قرآن سن رہے ہیں بلکہ بعد میں اللہ تعالیٰ نے آپ کو خبر دی کہ وہ آپ کی تلاوت سن رہے تھے، لیکن یہ بات بعید از قیاس نہیں ہے کہ جس طرح اللہ تعالیٰ نے حضورؐ کو جِنوں کی سماعت قرآن پر مطلع فرمایا تھا اسی طرح اللہ تعالیٰ ہی نے آپ کو یہ اطلاع بھی دے دی ہو کہ سورہ رحمٰن سنتے وقت وہ اس کا کیا جواب دیتے جا رہے تھے۔ ان روایات سے تو صرف اسی قدر معلوم ہوتا ہے کہ رحمٰن سور حجر اور سورہ احقاف سے پہلے نازل ہو چکی تھی۔ اس کے بعد ایک اور روایت ہمارے سامنے آتی ہے جس سے پتہ چلتا ہے کہ یہ مکہ معظمہ کے ابتدائی دور کی نازل شدہ سورتوں میں سے ہے۔ ابن اسحاق حضرت عروہ بن زبیر سے یہ واقعہ نقل کرتے ہیں کہ ایک روز صحابہ کرام نے آپس میں کہا کہ قریش نے کبھی کسی کو علانیہ بآواز بلند قرآن پڑھتے نہیں سنا ہے، ہم میں کون ہے جو ایک دفعہ ان کو یہ کلام پاک سنا ڈالے؟ حضرت عبداللہ بن مسعود نے کہا میں یہ کام کرتا ہوں۔ صحابہ نے کہا ہمیں ڈر ہے کہ وہ تم پر زیادتی کریں گے۔ ہمارے خیال میں کسی ایسے شخص کو یہ کام کرنا چاہیے جس کا خاندان زبردست ہو، تاکہ اگر قریش کے لوگ اس پر دست درازی کریں تو اس کے خاندان والے اس کی حمایت پر اٹھ کھڑے ہوں۔ حضرت عبداللہ نے فرمایا مجھے یہ کا کر ڈالنے دو، معرا محافظ اللہ ہے۔ پھر وہ دن چڑھے حرم میں پہنچے جبکہ قریش کے سردار وہاں اپنی اپنی مجلسوں میں بیٹھے تھے۔ حضرت عبداللہ نے مقام ابراہیم پر پہنچ کر پورے زور سے سورہ رحمٰن کی تلاوت شروع کر دی۔ قریش کے لوگ پہلے تو سوچتے رہے کہ عبداللہ کیا کہہ رہے ہیں۔ پھر جب انہیں پتہ چلا کہ یہ وہ کلام ہے جسے محمد صلی اللہ علیہ و سلم خدا کے کلام کی حیثیت سے پیش کرتے ہیں تو وہ ان پر ٹوٹ پڑے اور ان کے منہ پر تھپڑ مارنے لگے۔ مگر حضرت عبداللہ نے پروانہ کی۔ پٹتے جاتے تھے اور پڑھتے جاتے تھے۔ جب تک ان کے دم میں دم رہا قرآن سنائے چلے گئے۔ آخر کار جب وہ اپنا سوجا ہوا منہ لے کر پلٹے تو ساتھیوں نے کہا ہمیں اسی چیز کا ڈر تھا۔ انہوں نے جواب دیا۔ آج سے بڑھ کر یہ خدا کے دشمن میرے لیے کبھی ہلکے نہ تھے، تم کہو تو کل پھر انہیں قرآن سناؤں۔ سب نے کہا، بس اتنا ہی کافی ہے۔ جو کچھ وہ نہیں سننا چاہتے تھے وہ تم نے انہیں سنا دیا (سیرۃ ابن ہشام، جلد اول، ص 336)۔ موضوع اور مضمون: قرآن مجید کی یہ ایک ہی سورۃ ہے جس میں انسان کے ساتھ زمین کی دوسری با اختیار مخلوق، جنوں کو بھی براہ راست خطاب کیا گیا ہے، اور دونوں کو اللہ تعالیٰ کی قدرت کے کمالات، اس کے بے حد و حساب احسانات، اس کے مقابلہ کہ ان کی عاجزی و بے بسی اور اس کے حضور ان کی جوابدہی کا احساس دلا کر اس کی نافرمانی کے انجام بد سے ڈرایا گیا ہے اور فرمانبرداری کے بہترین نتائج سے آگاہ کیا گیا ہے۔اگر چہ قرآن پاک میں متعدد مقامات پر ایسی تصریحات موجود ہیں جن سے معلوم ہوتا ہے کہ انسانوں کی طرح جن بھی ایک ذی اختیار اور جواب دہ مخلوق ہیں جنہیں کفر و ایمان اور طاعت و عصیان کی آزادی مخشی گئی ہے، اور ان میں بھی انسانوں ہی کی طرح کافر و مومن اور مطیع و سرکش پائے جاتے ہیں، اور ان کے اندر بھی ایسے گروہ موجود ہیں جو انبیاء علیہم السلام اور کتب آسمانی پر ایمان لائے ہیں، لیکن یہ سورۃ اس امر کی قطعی صراحت کرتی ہے کہ رسول اللہ صلی اللہ علیہ و سلم اور قرآن مجید کی دعوت جن اور انس دونوں کے لیے ہے اور حضورؐ کی رسالت صرف انسانوں تک محدود نہیں ہے۔ سورۃ کے آغاز میں تو خطاب کا رخ انسانوں کی طرف ہی ہے، کیونکہ زمین کی خلافت ان ہی کو حاصل ہے، خدا کے رسول ان ہی میں سے آئے ہیں، اور خدا کی کتابیں ان ہی زبانوں میں نازل کی گئی ہیں، لیکن آگے چل کر آیت 13 سے انسان اور جن دونوں کی یکساں مخاطب کیا گیا ہے، اور ایک ہی دعوت دونوں کے سامنے پیش کی گئی ہے۔ سورۃ کے مضامین چھوٹے چھوٹے فقروں میں ایک خاص ترتیب سے ارشاد ہوئے ہیں : آیت 1 سے 4 تک یہ مضمون بیان فرمایا گیا ہے کہ اس قرآن کی تعلیم سے نوع انسانی کی ہدایت کا سامان کرے، کیونکہ انسان کو ایک ذی عقل و شعور مخلوق کی حیثیت سے اسی نے پیدا کیا ہے۔ آیت 5۔6 میں بتایا گیا ہے کہ کائنات کا سرا نظام اللہ تعالیٰ کی فرمانروائی میں چل رہا ہے اور زمین و آسمان کی ہر چیز اس کی تابع فرمان ہے۔ یہاں کوئی دوسرا نہیں ہے جس کی خدائی چل رہی ہو۔ آیت 7۔9 میں ایک دوسری اہم حقیقت یہ بیان کی گئی ہے کہ اللہ تعالیٰ نے کائنات کے اس پورے نظام کو ٹھیک ٹھیک تَوازن کے ساتھ عدل پر قائم کیا ہے اور اس نظام کی فطرت یہ چاہتی ہے کہ اس میں رہنے والے اپنے حدود اختیار میں بھی عدل ہی پر قائم ہوں اور تَوازن نہ بگاڑیں۔ آیت 10 سے 25 تک اللہ تعالیٰ کی قدرت کے عجائب و کمالات بیان کرنے کے ساتھ ساتھ اس کی ان نعمتوں کی طرف اشارے گئے ہیں جن سے انسان اور جن متمتع ہو رہے ہیں۔ آیت 26 سے 30 تک انسان اور جن دونوں کو یہ حقیقت یاد دلائی گئی ہے کہ اس کائنات میں ایک خدا کے سوا کوئی غیر فانی اور لازوال نہیں، اور چھوٹے سے بڑے تک کوئی موجود ایسا نہیں جو اپنے وجود اور ضروریات وجود کے لیے خدا کا محتاج نہ ہو۔ زمین سے لے کر آسمانوں تک شب و روز جو کچھ بھی ہو رہا ہے اسی کی کار فرمائی سے ہو رہا ہے۔ آیت 31 سے 36 تک ان دونوں گروہوں کو خبر دار کیا گیا ہے کہ عنقریب وہ وقت آنے والا ہے جب تم سے باز پرس کی جائے گی۔ اس باز پرس سے بچ کر تم کہیں نہیں جا سکتے۔ خدا کی خدائی تمہیں ہر طرف سے گھیرے ہوئے ہے۔اس سے نکل کر بھاگ جانا تمہارے بس میں نہیں ہے۔ اگر تم اس گھمنڈ میں مبتلا ہو کہ اس سے بھاگ سکتے ہو تو بھاگ دیکھو۔ آیات 37۔38 میں بتا گیا ہے کہ یہ باز پرس قیامت کے روز ہونے والی ہے۔ آیت 39 سے 45 تک ان مجرم انسانوں اور جنوں کا انجام بتایا گیا ہے جو دنیا میں اللہ تعالیٰ کی نافرمانی کرتے رہے ہیں۔ اور آیت 46 سے آخرت سورت تک تفصیل کے ساتھ وہ انعامات بیان کیے گئے ہیں جو آخرت میں ان نیک انسانوں اور جنوں کو عطا کیے جائیں گے جنہوں نے دنیا میں خدا ترسی کی زندگی بسر کی ہے اور یہ سمجھتے ہوئے کام کیا ہے کہ ہمیں ایک روز اپنے رب کے سامنے پیش ہو کر اپنے اعمال کا حساب دینا ہے۔ یہ پوری تقریر خطابت کی زبان میں ہے۔ ایک پر جوش اور نہایت بلیغ خطبہ ہے جس کے دوران میں اللہ تعالیٰ کی قدرت کے ایک ایک عجوبے، اور اس کی عطا کردہ نعمتوں میں سے ایک ایک نعمت، اور اس کی سلطانی و قہاری کے مظاہر میں سے ایک ایک مظہر، اور اس کی جزاء و سزا کی تفصیلات میں سے ایک ایک چیز کو بیان کر کے بار بار جب و انس سے سوال کیا گیا ہے کہ فَبِاَیِّ اٰلَآ ءِ رَبِّکُمَا تُکَذِّ بَا نِ۔ آگے چل کر ہم اس کی وضاحت کریں گے کہ آلاء ایک سویع المعنی لفظ ہے جس کو اس خطبے میں مختلف معنوں میں استعمال کیا گیا ہے، اور جن و انس سے یہ سوال ہر جگہ موقع و محل کے لحاظ سے اپنا ایک خاص مفہوم رکھتا ہے۔','سید ابو اعلیٰ مودودیؒ -  تفہیم القرآن');
INSERT INTO "chapters_ur" VALUES(56,'واقعہ ہونے والی قیامت',NULL,' نام: پہلی ہی آیت کے لفظ اَلْواقِعَہ کو اس سورت کا نام قرار دیا گیا ہے۔ زمانہ نزول: حضرت عبداللہ بن عباس کے سورتوں کی جو ترتیب نزول بیان کی ہے اس میں وہ فرما رہے ہیں کہ پہلے سورہ طٰہٰ نازل ہوئی، پھر الواقعہ اور اس کے بعد الشعراء(الاتْقان للسُّیوطی)۔ یہی ترتیب عکرمہ نے بھی بیان کی ہے (بیہقی، دلائل النبوۃ)۔ اس کی تائید اس قصہ سے بھی ہوتی ہے جو حضرت عمر رضی اللہ عنہ کے ایمان لانے کے بارے میں ابن ہشام نے ابن اسحاق سے نقل کیا ہے۔ اس میں یہ ذکر آتا ہے کہ جب حضرت عمرؓ اپنی بہن کے گھر میں داخل ہوئے تو سورہ طٰہٰ پڑھی جا رہی تھی۔ ان کی آہٹ سن کر ان لوگوں کو قرآن کے اوراق چھپا دیے۔ حضرت عمرؓ پہلے تو بہنوئی پر پِل پڑے اور جب بہن ان کو بچانے آئیں تو ان کو بھی مارا یہاں تک کہ ان سر پھٹ گیا۔ بہن کا خون بہتے دیکھ کر حضرت عمرؓ کو سخت ندامت ہوئی،اور انہوں نے کہا، اچھا مجھے وہ صحیفہ دکھاؤ جسے تم نے چھپا لیا ہے۔ دیکھوں تو سہی اس میں کیا لکھا ہے۔ بہن نے کہا ’’آپ اپنے شرک کی وجہ سے نجس ہیں، وانہٗ لا یمسھا الا اطاھر ، ’’ اس صحیفے کو صرف طاہر آدمی ہی ہاتھ لگا سکتا ہے ‘‘۔ چنانچہ حضرت عمرؓ نے اٹھ کر غسل کیا اور پھر اس صحیفے کو لے کر پڑھا۔ اس سے معلوم ہوا کہ اس وقت سورہ واقعہ نازل ہو چکی تھی، کیونکہ اسی میں آیت لا یمسہ الا المطھرون وارد ہوئی ہے۔ اور یہ تاریخی طور پر ثابت ہے کہ حضرت عمرؓ ہجرت حبشہ کے بعد 5 نبوی میں ایمان لائے ہیں۔ موضوع اور مضمون: اس کا موضوع آخرت، توحید اور قرآن کے متعلق کفار مکہ کے شبہات کی تردید ہے۔ وہ سب سے زیادہ جس چیز کو ناقابل یقین قرار دیتے تھے وہ یہ تھی کہ کبھی قیامت بر پا ہوگی جو میں زمین و آسمان کا سارا نظام درہم برہم ہو جائے گا اور پھر تمام مزے ہوئے انسان دوبارہ جِلا اٹھائے جائیں گے اور ان کا محاسبہ ہو گا اور نیک انسان جنت کے باغوں میں رکھے جائیں گے اور گناہ گار انسان دوزخ میں ڈالے جائیں گے۔ ان کا کہنا یہ تھا کہ یہ سب خیالی باتیں ہیں جن کا عالم واقعہ میں پیش آنا غیر ممکن ہے۔ اس کے جواب میں فرمایا کہ جب وہ واقعہ پیش آ جائے گا اس وقت کوئی یہ جھوٹ بولنے والا نہ ہو گا کہ وہ پیش نہیں آیا ہے، نہ کسی کی یہ طاقت ہو گی کہ اسے آتے آتے روک دے، یا واقعہ سے غیر واقعہ بنا دے۔ اس وقت لازماً تمام انسان تین طبقات میں تقسیم ہو جائیں گے۔ ایک، سابقین۔ دوسرے، عام صالحین، تیسرے وہ لوگ جو آخرت کے منکر رہے اور مرتے دم تک کفر و شرک اور گناہ کبیرہ پر جمے رہے۔ ان تینوں طبقات کے ساتھ جو معاملہ ہو گا اسے تفصیل کے ساتھ آیت 7 سے 56 تک بیان کیا گیا ہے۔ اس کے بعد آیت 57 سے آیت 74 تک اسلام کے ان دونوں بنیادی عقائد کی صداقت پر پے درپے دلائل دیے گئے ہیں جن کو ماننے کفار انکار کر رہے تھے، یعنی توحید اور آخرت۔ان دلائل میں زمین و آسمان کی دوسری تمام چیزوں کو چھوڑ کر انسان کو خود اس کے اپنے وجود کی طرف اور اس غذا کی طرف جسے وہ کھاتا ہے اور اس پانی کی طرف جسے وہ پیچا ہے اور اس آگ کی طرف جس سے وہ اپنا کھانا پکاتا ہے، توجہ دلائی گئی ہے۔ اور اسے اس سوال پر غور و فکر کی دعوت دی گئی ہے کہ تو جس خدا کے بنانے سے بنا ہے اور جس کے دیے ہوئے سامان زیست پر پل رہا ہے اس کے مقابلے میں خود مختار ہونے، یا اس کے سوا کسی اور کی بندگی بجا لانے کا آخر تجھے حق کیا ہے؟ اور اس کے متعلق تو نے یہ کیسے گمان کر لیا کہ وہ ایک دفعہ تجھے وجود میں لے آنے کے بعد ایسا عاجز و درماندہ ہو جاتا ہے کہ دوبارہ تجھ کو وجود میں لانا چاہے بھی تو نہیں لا سکتا؟ پھر آیت 75 سے 82 تک قرآن کے بارے میں ان کے شکوک کی تردید کی گئی ہے اور ان کو یہ احساس دلایا گیا ہے کہ بد نصیبو، یہ عظیم الشان نعمت تمہارے پاس آئی ہے اور تم نے اپنا حصہ اس نعمت میں یہ رکھا ہے کہ اسے جھٹلاتے ہو اور اس سے فائدہ اٹھانے کے بجائے الٹی بے اعتنائی برتتے ہو۔ قرآن کی صداقت پر دو مختصر سے فقروں میں یہ بے نظیر دلیل پیش کی گئی ہے کہ اس پر کوئی غور کرے تو اس کے اندر ویسا ہی محکم نظام پائے گا جیسا کائنات کے تاروں اور سیاروں کا نظام محکم ہے، اور یہی اس بات کی دلیل ہے کہ اس کا مصنف وہی ہے جس نے کائنات کا یہ نظام بنایا ہے۔ پھر کفار سے کہا گیا ہے کہ یہ کتاب اس نوشتہ تقدیر میں ثبت ہے جو مخلوقات کی دست رس سے باہر ہے۔ تم سمجھتے ہو کہ اسے محمد صلی اللہ علیہ و سلم کے پاس شیاطین لاتے ہیں، حالانکہ لوح محفوظ سے محمد صلی اللہ علیہ و سلم تک جس ذریعہ سے یہ پہنچتی ہے اس میں پاک نفس فرشتوں کے سوا کسی کا ذرہ برابر بھی کوئی دخل نہیں ہے۔ آخر میں انسان کو بتایا گیا ہے کہ تو کتنی ہی لن ترانیاں ہانکے اور اپنی خود مختاری کے گھمنڈ میں کتنا ہی حقائق کی طرف سے اندھا ہو جائے، مگر موت کا وقت تیری آنکھیں کھول دینے کے لیے کافی ہے۔ اس وقت تو بالکل بے بس ہوتا ہے۔اپنے ماں باپ کو نہیں بچا سکتا۔اپنی اولاد کو نہیں بچا سکتا۔ اپنے پیروں اور پیشواؤں اور محبوب ترین لیڈروں کو نہیں بچا سکتا۔ سب تیری آنکھوں کے سامنے مرتے ہیں اوور تو دیکھتا رہ جاتا ہے۔ اگر کوئی بالا تر طاقت تیرے اوپر فرمانروا نہیں ہے اور تیرا یہ زعم درست ہے کہ دنیا میں جس تو ہی تو ہے، کوئی خدا نہیں ہے، تو کسی مرنے والے کی نکلتی ہوئی جان کو پلٹا کیوں نہیں لاتا؟ جس طرح تو اس معاملہ میں بے بس ہے اسی طرح خدا کے محاسبے اور اس کی جزا و سزا کو بھی روک دینا تیرے اختیار میں نہیں ہے۔ تو خواہ مانے یا نہ مانے، موت کے بعد ہر مرنے والا اپنا انجام دیکھ کر رہے گا۔ مقربین میں سے ہو تو مقربین کا انجام دیکھے گا۔ صالحین میں سے ہو تو صالحین کا انجام دیکھے گا۔ اور جھٹلانے والے گمراہوں میں سے ہو تو وہ انجام دیکھے گا جو ایسے مجرموں کے لیے مقدر ہے۔ ','سید ابو اعلیٰ مودودیؒ -  تفہیم القرآن');
INSERT INTO "chapters_ur" VALUES(57,'فولاد',NULL,' نام: آیت 25 کے فقرے وَاَنْزَلْنَا الْحَدِیْد سے ماخوذ ہے ۔ زمانۂ نزول: یہ بالاتفاق مدنی سورت ہے اور اس کے مضامین پر غور کرنے سے محسوس ہوتا ہے کہ غالباً یہ جنگ احد اور صلح حدیبیہ کے درمیان کسی زمانہ میں نازل ہوئی ہے ۔ وہی زمانہ تھا جب مدینہ کی مختصر سی اسلامی ریاست کو ہر طرف سے کفار نے اپنے نرغے میں لے رکھا تھا اور سخت بے رسو سامانی کی حالت میں اہل ایمان کی مٹھی بھر جماعت پورے عرب کی طاقت کا مقابلہ کر رہی تھی۔ اس حالت میں اسلام کو اپنے پردوں سے صرف جانی قربانی ہی درکار نہ تھی بلکہ مالی قربانی بھی درکار تھی، اور اس سورۃ میں اسی قربانی کے لیے پر زور اپیل کی گئی ہے ۔ اس قیاس کو آیت 10 مزید تقویت پہنچاتی ہے جس میں اللہ تعالیٰ نے اہل ایمان کی جماعت کو خطاب کر کے فرمایا ہے کہ فتح کے بعد جو لوگ اپنے مال خرچ کریں گے اور خدا کی راہ میں جنگ کریں گے وہ ان لوگوں کے برابر کبھی نہیں ہو سکتے جو فتح سے پہلے جان و مال کی قربانیاں دیں ۔ اس اسی کی تائید حضرت اَنَسؓ کی وہ روایت کرتی ہے جسے ابن مردویہ نے نقل کیا ہے اَلَمْ یَأنِ لِلَّذِیْنَ اٰمَنُوْ اَنْ تَخْشَعَ قُلُوبُھُمْ لِذِکْرِ اللہِ کے متعلق فرماتے ہیں کہ نزول قرآن کے آغاز سے 17 برس بعد اہل ایمان درمیان قرار پاتا ہے ۔ موضوع اور مضمون: اس کا موضوع انفاق فی سبیل اللہ کی تلقین ہے ۔ اسلام کی تاریخ کے اس نازک ترین دور میں ، جبکہ عرب کی جاہلیت سے اسلام کا فیصلہ کن معرکہ برپا تھا، یہ سورۃ اس غرض کے لیے نازل فرمائی گئی تھی کہ مسلمانوں کو خاص طور مالی قربانیوں کے لیے آمادہ کیا جائے اور یہ بات ان کے ذہن نشین کرائی جائے کہ ایمان محض زبانی اقرار اور کچھ ظاہری اعمال کا نام نہیں ہے بلکہ اللہ اور اس کے دین کے لیے مخلص ہونا اس کی اصل روح اور حقیقت ہے ۔ جو شخص اس روح سے خالی ہو اور خدا اور اس کے دین کے مقابلہ میں اپنی جان و مال اور مفاد کو عزیز تر رکھے اس کا اقرار ایمان کھوکھلا ہے جس کی کوئی قدر و قیمت خدا کے ہاں نہیں ہے ۔ اس مقصد کے لیے سب سے پہلے اللہ تعالیٰ کی صفات بیان کی گئی ہیں تاکہ سامعین کو اچھی طرح یہ احساس ہو جائے کہ کس عظیم ہستی کی طرف سے ان کو مخاطب کیا جا رہا ہے ۔ اس کے بعد حسب ذیل مضامین سلسلہ وار ارشاد ہوئے ہیں ؛ ایمان کا لازمی تقاضا یہ ہے کہ آدمی راہ خدا میں مال صرف کرنے سے پہلو تہی نہ کرے ۔ ایسا کرنا صرف ایمان ہی کے منافی نہیں ہے بلکہ حقیقت کے اعتبار سے بھی غلط ہے ۔ کیونکہ یہ مال در اصل خدا ہی کا مال ہے جس پر تم کو خلیفہ کی حیثیت سے تصرف کے اختیارات دیے گئے ہیں ۔ کل یہی مال دوسروں کے پاس تھا، آج تمہارے پاس ہے ، اور کل کسی اور کے پاس چلا جائے گا۔ آخر کار اسے خدا ہی کے پاس رہ جانا ہے جو کائنات کی ہر چیز کا وارث ہے ۔ تمہارے کام اس مال کا کوئی حصہ اگر آسکتا ہے تو صرف وہ جسے تم اپنے زمانہ تصرف میں خدا کے کام پر لگا دو۔ خدا کی راہ میں جان و مال کی قربانی دینا اگرچہ ہر حال میں قابل قدر ہے ، مگر ان قربانیوں کی ادر و قیمت مواقع کی نزاکت کے لحاظ سے متعین ہوتی ہے ۔ ایک موقع وہ ہوتا ہے جب کفر کی طاقت بڑی زبردست ہو اور ہر وقت یہ خطرہ ہو کہ کہیں اسلام اس کے مقابلہ میں مغلوب نہ ہو جائے۔ دوسرا موقع وہ ہوتا ہے جب کفر و اسلام کی کشمکش میں اسلام کی طاقت کا پلڑا بھاری ہو جائے اور ایمان کو دشمنان حق کے مقابلہ میں فتح نصیب ہو رہی ہو۔ یہ دونوں حالتیں اپنی اہمیت کے لحاظ سے یکساں نہیں ہیَ اس لیے جو قربانیاں ان مختلف حالتوں میں دی جائیں وہ بھی اپنی قیمت میں برابر نہیں ہیں ۔ جو لوگ اسلام کے ضعف کی حالت میں اس کو سربلند کرنے کے لیے جانیں لڑائیں اور مال صرف کریں ان کے درجہ کو وہ لوگ نہیں پہنچ سکتے جو اسلام کے غلبے کی حالت میں اس کو مزید فروغ دینے کے لیے جان و مال قربان کریں ۔ راہ حق میں جو مال بھی صرف کیا جائے وہ اللہ کے ذمے قرض ہے ، اور اللہ اسے نہ صرف یہ کہ کئی گنا بڑھا چڑھا کر واپس دے گا بلکہ اپنی طرف سے مزید اجر بھی عنایت فرمائے گا۔ آخرت میں نور انہی اہل ایمان کو نصیب ہو گا جنہوں نے راہ خدا میں اپنا مال خرچ کیا ہو۔ رہے وہ منافق جو دنیا میں اپنے ہی مفاد کو دیکھتے رہے اور جنہیں اس بات کی کوئی پروا نہیں رہی کہ حق غالب ہوتا ہے یا باطل، وہ خواہ دنیا کی اس زندگی میں مومنوں کے ساتھ ملے جلے رہے ہوں ، مگر آخرت میں ان کو مومنوں سے الگ کر دیا جائے گا، نور سے وہ محروم ہوں گے اور ان کا حشر کافروں کے ساتھ ہو گا۔ مسلمانوں کو ان اہل کتاب کی طرح نہ ہو جاتا چاہیے جن کی عمریں دنیا پرستی میں بیت گئی ہیں اور جن کے دل زمانہ دراز کی غفلتوں سے پتھر ہو گئے ہیں۔ وہ مومن ہی کیا جس کا دل خدا کے ذکر سے نہ پگھلے اور اس کے نازل کردہ حق کے آگے نہ جھکے ۔ اللہ کے نزدیک صدیق اور شہید صرف وہ اہل ایمان ہیں جو اپنا مال کسی جذبہ ریا کے بغیر صدق دل سے اس کی راہ میں صرف کرتے ہیں ۔ دنیا کی زندگی محض چند روز کی بہار اور ایک متاع غرور ہے ۔ یہاں کا کھیل کود، یہاں کی دلچسپیاں ،یہاں کی آرائش و زیبائش، یہاں کی بڑائیوں پر فخر، اور یہاں کا دھن و دولت، جس میں لوگ ایک دوسرے سے بڑھ جانے کی کوششیں کرتے ہیں ، سب کچھ نا پائدار ہے ۔ اس کی مثال اس کھیتی کی سی ہے جو پہلے سر سبز ہوتی ہے ، پھر زرد پڑ جاتی ہے اور آخر کار ٹھیس بن کر رہ جاتی ہے ۔ پائیدار زندگی دراصل آخرت کی زندگی ہے جہاں بڑے نتائج نکلنے والے ہیں ۔ تمہیں ایک دوسرے سے آگے نکلنے کی کوشش کرنی ہے تو یہ کوشش جنت کی طرف دوڑنے میں صرف کرو۔ دنیا میں راحت اور مصیبت جو بھی آتی ہے اللہ کے پہلے ہوئے فیصلے کے مطابق آتی ہے ۔ مومن کا کردار یہ ہونا چاہیے کہ مصیبت آئے تو ہمت نہ ہار بیٹھے ، اور راحت آئے تو اترا نہ جائے۔ یہ تو ایک منافق اور کافر کا کردار ہے کہ اللہ اس کو نعمت بخشے تو وہ اپنی جگہ پھول جائے، فخر جتانے لگے ، اور اسی نعمت دینے والے خدا کے کام میں خرچ کرتے ہوئے خود بھی تنگ دلی دکھائے اور دوسروں کو بھی بخل کرنے کا مشورہ دے ۔ اللہ نے اپنے رسول کھلی کھلی نشانیوں اور کتاب اور میزان عدل کا سر نیچا کرنے کے لیے طاقت استعمال کی کائے۔ اس طرح اللہ یہ دیکھنا چاہتا ہے کہ انسانوں میں سے کون لگ ایسے نکلتے ہیں جو اس کے این کی حمایت و نصرت کے لیے اٹھ کھڑے ہوں اور اس کی خاطر جان لڑا دیں ۔ یہ مواقع اللہ نے تمہاری اپنی ہی ترقی و سرفرازی کے لیے پیدا کیے ہیں ، سرنہ اللہ اپنے کام کے لیے کسی کا محتاج نہیں ہے ۔ اللہ تعالیٰ کی طرف سے پہلے انبیاء آتے رہے جن کی دعوت سے کچھ لوگ راہ راست پر آئے اور اکثر فاسق بنے رہے ۔ پھر عیسیٰ علیہ السلام آئے جن کی تعلیم سے لوگوں میں بہت سی اخلاقی خوبیاں پیدا ہوئیں ، مگر ان کی امت نے رہبانیت کی بدعت اختیار کر لی۔ اب اللہ تعالیٰ نے محمد صلی اللہ علیہ و سلم کو بھیجا ہے ۔ ان پر جو لوگ ایمان لائیں گے اور خدا سے ڈرتے ہوئے زندگی بسر کریں گے ، اللہ ان کو اپنی رحمت کا دہرا حصہ دے گا اور انہیں وہ جور بخشے گا جس سے دنیا کی زندگی میں وہ ہر ہر قدم پر ٹیڑھے راستوں کے درمیان سیدھی راہ صاف دیکھ کر چل سکیں گے ۔ اہل کتاب چاہے اپنے آپ کو اللہ کے فضل کا ٹھیکہ دار سمجھتے رہیں ، مگر اللہ کا فضل اس کے اپنے ہی ہاتھ میں ہے ، اسے اختیار ہے جسے چاہے اپنے فضل سے نواز دے ۔ یہ ہے ان مضامین کا خلاصہ جو اس سورت میں ایک ترتیب کے ساتھ مسلسل بیان ہوئے ہیں ۔','سید ابو اعلیٰ مودودیؒ -  تفہیم القرآن');
INSERT INTO "chapters_ur" VALUES(58,'جھگڑنے والی',NULL,' نام: اس سورۃ کا نام المجادَلہ بھی ہے المجادِلہ بھی۔ یہ نام پہلی ہی آیت کے لفظ تُجَادِلُکَ سے ماخوذ ہے۔ چونکہ سورۃ کے آغاز میں ان خاتون کا ذکر آیا ہے جنہوں نے اپنے شوہر کے ظہار کا قضیہ رسول اللہ صلى الله عليه وسلم کے سامنے پیش کر کے بار بار اصرار کیا تھا کہ آپ کوئی ایسی صورت بتائیں جس سے ان کی اور ان کے بچوں کی زندگی تباہ ہونے سے بچ جائے، اور اللہ تعالیٰ نے ان کے اصرار کو لفظ مجادلہ سے تعبیر فرمایا ہے، اس لیے یہی اس سورۃ کا نام قرار دیا گیا۔ اس کو اگر مجادَلہ پڑھا جائے تو اس کے معنی ہوں گے ’’بحث و تکرار‘‘ اور مُجادِلہ پڑھا جائے تو معنی ہوں گے ’’بحث و تکرار کرنے والی ‘‘۔ زمانۂ نزول : کسی روایت میں اس امر کی تصریح نہیں کی گئی ہے کہ مجادلہ کا یہ واقعہ کب پیش آیا تھا۔ اگر ایک علامت اس سورہ کے مضمون میں ایسی ہے جس کی بنا پر یہ بات تعین کے ساتھ کہی جا سکتی ہے کہ اس کی زمانہ غزوۂ احزاب (شوال ۵ ھ ) کے بعد کا ہے۔ سورۂ احزاب میں اللہ تعالیٰ نے منہ بولے بیٹے کے حقیقی بیٹا ہونے کی نفی کرتے ہوئے صرف یہ اشارہ فرما کر چھوڑ دیا تھا کہ وَمَا جَعلَ ازْوَاجَکُم الّیٰٔ تظٰھِرون منھنّ امّھتکم (اور اللہ نے تمہاری ان بیویوں کو جن سے تم ظِہار کرتے ہوئے تمہاری مائیں نہیں بنا دیا ہے )۔ مگر اس میں یہ نہیں بتایا گیا تھا کہ ظِہار کرنا کوئی گناہ یا جرم ہے، اور یہ بتایا گیا تھا کہ اس فعل کا شرعی حکم کیا ہے۔ بخلا اس کے اس سورہ میں ظِہار کا پورا قانون بیان کر دیا گیا ہے۔ اس سے معلوم ہوتا ہے کہ یہ مفصل احکام اس مجمل ہدایت کے بعد نازل ہوئے ہیں۔ موضوع اور مباحث : اس سورۃ میں مسلمانوں کو ان مختلف مسائل کے متعلق ہدایت دی گئی ہیں جو اس وقت درپیش تھے۔ آغاز سورۃ سے آیت ۶ تک ظِہار کے شرعی احکام بیان کیے گئے ہیں، اور اس کے ساتھ مسلمانوں کو پوری سختی کے ساتھ متنبہ کیا گیا ہے کہ اسلام کے بعد بھی جاہلیت کے طریقوں پر قائم رہنا اور اللہ کی مقرر کی ہوئی حدوں کو توڑنا، یا ان کی پابندی سے انکار کرنا، یا ان کے مقابلہ میں خود اپنی مرضی سے کچھ اور قاعدے اور قوانین بنا لینا، قطعی طور پر ایمان کے منافی حرکت ہے، جس کی سزا دنیا میں بھی ذلت و رسوائی ہے اور آخرت میں بھی اس پر سخت باز پرس ہونی ہے۔ آیات ۷ تا ۱۰ میں منافقین کی اس روش پر گرفت کی گئی ہے کہ وہ آپس میں خفیہ سرگرمیاں کر کے طرح طرح کی شرارتوں کے منصوبے بناتے تھے، اور ان کے دلوں میں جو بغض چھپا ہوا تھا اس کی بنا رسول صلى الله عليه وسلم کو یہودیوں کی طرح ایسے طریقے سلام کرتے تھے جس سے دعا کے بجائے بددعا کا پہلو نکلتا تھا۔ اس سلسلہ میں مسلمانوں کو تسلی دی گئی ہے کہ منافقین کی یہ سرگوشیاں تمہارا کچھ نہیں بگاڑ سکتیں، اس لیے تم اللہ کے بھروسے پر اپنا کام کرتے رہو۔ا ور اس کے ساتھ ان کو یہ اخلاقی تعلیم بھی دی گئی ہے کہ سچے اہل ایمان کا کام گناہ اور ظلم و زیادتی اور رسول کی نافرمانی کے لیے سرگوشیاں کرنا نہیں ہے، وہ اگر آپس میں بیٹھ کر تخلیے میں کوئی بات کریں بھی تو وہ نیکی اور تقویٰ کی بات ہونی چاہیئے۔ آیت ۱۱ تا ۱۳ میں مسلمانوں کو مجلسی تہذیب کے کچھ آداب سکھائے گئے ہیں اور بعض ایسے معاشرتی عیوب کو دور کرنے کے لیے ہدایات دی گئی ہیں جو پہلے بھی لوگوں میں پائے جاتے اور آج بھی پائے جاتے ہیں کہ کسی مجلس میں اگر بہت سے لوگ بیٹھے ہوئے ہوں اور باہر سے کچھ لوگ آ جائیں تو پہلے سے بیٹھے ہوئے اصحاب اتنی سی زحمت گوارا نہیں کرتے کہ ذرا سمٹ کر بیٹھ جائیں اور دوسروں کے لیے گنجائش پیدا کر دیں۔ نتیجہ یہ ہوتا ہے کہ بعد کے آنے والے کھڑے رہ جاتے ہیں، یا دہلیز میں بیٹھنے پر مجور ہوتے ہیں، یا واپس چلے جاتے ہیں، یا یہ دیکھ کر کہ مجلس میں ابھی کافی گنجائش موجود ہے، حاضرین کے اوپر سے پھاند تے ہوئے اندر گھستے ہیں۔ یہ صورتِ حال نبیصلى الله عليه وسلم کی مجلسوں میں اکثر پیش آتی رہتی تھی۔ اس لیے اللہ تعالیٰ نے لوگوں کو یہ ہدایت فرمائیں کہ اپنی مجلسوں میں خود غرضی اور تنگ دلی کا مظاہرہ نہ کیا کریں بلکہ بعد کے آنے والوں کو کھلے دل سے جگہ دے دیا کریں۔ اسی طرح ایک عیب لوگوں میں یہ بھی ہوتا ہے کہ کسی کے ہاں (خصوصاً کسی اہم شخصیت کے ہاں ) جاتے ہیں تو جم کر بیٹھ جاتے ہیں اور اس بات کا کچھ خیال نہیں کرتے کہ ضرورت سے زیادہ اس کا وقت لینا اس کے لیے باعث زحمت ہو گا۔ اگر وہ کہے کہ حضرت اب تشریف لے جائیے تو برا مانتے ہیں۔ ان کو چھوڑ کر اٹھ جائے تو بد اخلاقی کی شکایت کرتے ہیں۔ اشارے کنایے سے ان کو بتائے کہ اب کچھ دوسرے ضروری کاموں کے لیے اس کو وقت ملنا چاہئے تو سنی اَن سنی کر جاتے ہیں۔ لوگوں کے اس طرز عمل سے خود نبیصلى الله عليه وسلم کو بھی سابقہ پیش آتا تھا اور آپ کی صحبت سے فائدہ اٹھانے کے شوق میں اللہ کے بندے اس بات کا لحاظ نہیں کرتے تھے کہ وہ بہت زیادہ قیمتی کاموں کا نقصان کر رہے ہیں۔ آخر کار اللہ تعالیٰ نے یہ تکلیف دہ عادت چھڑانے کے لیے حکم دیا کہ جب مجلس برخاست کرنے کے لیے کہا جائے تو اٹھ جایا کرو۔ ایک اور عجیب لوگوں میں یہ بھی تھا کہ ایک ایک آدمی آ کر خواہ مخواہ حضور سے تخلیہ میں بات کرنے کی خواہش کرتا تھا یا مجلس عام میں یہ چاہتا تھا کہ آپ کے قریب جا کر سرگوشی کے انداز میں آپ سے بات کرے۔ یہ چیز حضورصلى الله عليه وسلم کے لیے بھی تکلیف دہ تھی اور دوسرے لوگ جو مجلس میں موجود ہوتے، ان کو بھی ناگوار ہوتی تھی۔ اس لیے اللہ تعالیٰ نے یہ پابندی لگا دی کہ جو شخص بھی آپ سے علیحدگی میں بات کرنا چاہے وہ پہلے صدقہ دے۔ اس سے مقصود صرف یہ تھا کہ لوگوں کو اس بری عادت پر متنبہ کیا جائے تاکہ وہ اسے چھوڑ دیں۔ چنانچہ یہ پابندی بس تھوڑی دیر تک باقی رکھی گئی اور جب لوگوں نے اپنا طرز عمل درست کر لیا تو اسے منسوخ کر دیا گیا۔ آیت ۱۴ سے آخر سورہ تک مسلم معاشرے کے لوگوں کو جن میں مخلص اہل ایمان اور منافقین اور مذبذبین سب ملے جلے تھے، بالکل دو ٹوک طریقے سے بتایا گیا کہ دین میں آدمی کے مخلص ہونے کا معیار کیا ہے۔ ایک قسم کے مسلمان وہ ہیں جو اسلام کے دشمنوں سے دوستی رکھتے ہیں، اپنے مفاد کی خاطر اس دین سے غداری کرنے میں کوئی تامل نہیں کرے جس پر ایمان رکھنے کا دعویٰ کرتے ہیں، اور اسلام کے خلاف طرح طرح کے شبہات اور وسوسے پھیلا کر اللہ کے بندوں کو اللہ کی راہ پر آنے سے روکتے ہیں، مگر چونکہ وہ مسلمانوں کے گروہ میں شامل ہیں، اس لیے ان کا جھوٹا اقرار ایمان ان کے لیے ڈھال کا کام دیتا ہے۔ دوسری قسم کے مسلمان وہ ہیں جو اللہ کے دین کے معاملہ میں کسی اور کا لحاظ تو درکنا، خود اپنے باپ، بھائی، اور اولاد اور خاندان تک کی پروا نہیں کرتے۔ ان کا حال یہ ہے کہ جو خدا اور رسول اور اس کے دین کا دشمن ہے اس کے لیے ان کے دل میں کوئی محبت نہیں ہے۔ اللہ تعالیٰ نے ان آیات میں صاف فرما دیا ہے کہ پہلی قسم کے لوگ چاہے کتنی ہی قسمیں کھا کھا کر اپنے مسلمان ہونے کا یقین دلائیں، درحقیقت وہ شیطان کی پارٹی کے لوگ ہیں، اور اللہ کی پارٹی میں شامل ہونے کا شرف صرف دوسری قسم کے مسلمانوں کو حاصل ہے۔ وہی سچے مومن ہیں۔ ان ہی سے اللہ راضی ہے۔ فلاح وہی پانے والے ہیں۔','سید ابو اعلیٰ مودودیؒ -  تفہیم القرآن');
INSERT INTO "chapters_ur" VALUES(59,'حشر',NULL,' نام: دوسری آیت کے فقرے اَخْرَجَ الَّذِیْنَ کَفَرُو ا مِنْ اَھْلِ الْکِتٰبِ مِنْ دِیَارِھِمْ لِاَوَّلِ الْحَشْرِ سے ماخوذ ہے۔ مراد یہ ہے کہ یہ وہ سورۃ ہے جس میں لفظ الحشر آیا ہے۔ زمانۂ نزول: بخاری و مسلم میں حضرت سعید بن جُبیر کی روایت ہے کہ میں نے حضرت عبداللہ بن عباسؓ سے سور حشر کے متعلق پوچھا تو انہوں نے بتایا کہ یہ غزوہ بنی نضیر کے بارے میں نازل ہوئی تھی جس طرح سورہ انفال غزوہ بدر کے بارے میں نازل ہوئی۔ حضرت سعید بن جبیر کی دوسری روایت میں بن عباس رضی اللہ عنہ کے الفاظ یہ ہیں کہ : قل سورۃ النَّضِیْر یعنی یوں کہو کہ یہ سورہ نضیر ہے۔ یہی بات مجاہد، قتادہ، زہری، ابن زید، یزید بن رومان، محمد بن اسحاق وغیرہ حضرات سے بھی مروی ہے۔ ان سب کا متفقہ بیان یہ ہے کہ اس میں جن اہل کتاب کے نکالے جانے کا ذکر ہے ان سے مراد نبی النضیر ہی ہیں۔ یزید بن رومان، مجاہد اور محمد بن اسحاق کا قول یہ ہے کہ از اول تا آخر یہ پوری سورۃ اسی غزوہ کے بارے میں نازل ہوئی ہے۔ اب رہا یہ سوال کہ یہ غزوہ کب واقع ہوا تھا؟ امام زہری نے اس کے متعلق عُرہ بن زبیر کے حوالہ سے بیان کیا ہے کہ یہ جنگ بدر کے چھ مہینے بعد ہوا ہے۔ لیکن ابن سعد، ابن ہشام اور بَلَاذُرِی اسے ربیع الاول 4 ہجری کا واقعہ بتاتے ہیں، اور یہی صحیح ہے۔ کیوں کہ تمام روایات اس امر میں متفق ہیں یہ غزوہ بِئرمعونہ کے سانحہ کے بعد پیش آیا تھا، اور یہ بات بھی تاریخی طور پر ثابت ہے کہ بئَر معونہ کا سانحہ جنگ احد کے بعد رو نما ہوا ہے نہ کہ اس سے پہلے۔ تاریخی پس منظر: اس سورہ کے مضامین کو اچھی طرح سمجھنے کے لیے ضروری ہے کہ مدینہ طیبہ اور حجاز کے یہودیوں کی تاریخ پر ایک نگاہ ڈال لی جائے، کیوں کہ اس کے بغیر آدمی ٹھیک ٹھیک یہ نہیں جان سکتا کہ رسول اللہ صلی اللہ علیہ و سلم نے آخر کار ان کے مختلف قبائل کے ساتھ جو معاملہ کیا اس کے حقیقی اسباب کیا تھے۔عرب کے یہودیوں کی کوئی مستند تاریخ دنیا میں موجود نہیں ہے۔ انہوں نے خود اپنی کوئی ایسی تحریر کسی کتاب یا کتبے کی شکل میں نہیں چھوڑی ہے جس سے ان کے ماضی پر کوئی روشنی پڑ سکے۔ اور عرب سے باہر کے یہودی مؤرخین و مصنفین نے ان کا کوئی ذکر نہیں کیا ہے جس کی وجہ یہ بیان کی جاتی ہے کہ جزیرۃ العرب میں آ کر وہ اپنے بقیہ ابنائے ملت سے بچھڑ گئے تھے، اور دنیا کے یہودی سرے سے ان کو اپنوں میں شمار ہی نہیں کرتے تھے، کیونکہ انہوں نے عبرانی تہذیب، زبان، حتیٰ کہ نام تک چھوڑ کر عربیت اختیار کر لی تھی۔ حجاز کے آثار قدیمہ میں جو کتبات ملے ہیں ان میں پہلی صدی عیسوی سے قبل یہودیوں کا کوئی نشان نہیں ملتا، اور ان میں بھی صرف چند یہودی نام ہی پائے جاتے ہیں۔ اس لیے یہود عرب کی تاریخ کا بیشتر انحصار ان زبانی روایات پر ہے جو اہل عرب میں مشہور تھیں، اور ان میں اچھا خاصا حصہ خود یہودیوں کا اپنا پھیلا ہوا تھا۔حجاز کے یہودیوں کا یہ دعویٰ تھا کہ سب سے پہلے وہ حضرت موسیٰ علیہ السلام کے آخر عہد میں یہاں آ کر آباد ہوئے تھے۔ اس کا قصہ وہ یہ بیان کرتے تھے کہ حضرت موسیٰؑ نے ایک لشکر یثرب کے علاقے سے عَمالقہ کو نکالنے کے لیے بھیجا تھا اور اسے حکم دیا تھا کہ اس قوم کے کسی شخص کو زندہ نہ چھوڑیں، بنی اسرائیل کے اس لشکر نے یہاں آ کر فرمان نبی کی تعمیل کی، مگر عمالقہ کے بادشاہ کا ایک لڑکا بڑا خوبصورت جوان تھا، اسے انہوں نے زندہ رہنے دیا اور اس کو ساتھ لیے ہوئے فلسطین واپس پہنچے۔ اس وقت حضرت موسیٰ کا انتقال ہو چکا تھا۔ ان کے جانشینوں نے اس بات پر سخت اعتراض کیا کہ ایک عمالیقی کو زندہ چھوڑ دینا نبی کے فرمان اور شریعت موسوی کے احکام کی صریح خلاف ورزی ہے۔ اس بنا پر انہوں نے اس لشکر کو اپنی جماعت سے خارج کر دیا، اور اسے مجبوراً یثرب واپس آ کر یہیں بس جانا پڑا (کتاب الاغانی، ج 19، ص 94)۔ اس طرح یہودی گویا اس بات کے مدعی تھے کہ وہ 12 سو برس قبل مسیح سے یہاں آباد ہیں۔ لیکن در حقیقت اس کا کوئی ثبوت نہیں ہے، اور اغلب یہ ہے کہ یہودیوں نے یہ افسانہ اس لیے گھڑا تھا کہ اہل عرب پر اپنے قدیم الاصل اور عالی نسب ہونے کی دھونس جمائیں۔دوسری یہودی مہاجرت، خود یہودیوں کی اپنی روایت کے مطابق 587 قبل مسیح میں ہوئی جبکہ بابل کے بادشاہ بخت نصر نے بیت المقدس کو تباہ کر کے یہودیوں کو دنیا بھر میں تتر بتر کر دیا تھا۔ عرب کے یہودی کہتے تھے کہ اس زمانے میں ہمارے متعدد قبائل آ کر وادی القریٰ، تیماء اور یثرب میں آباد ہو گئے تھے (فُتُوح البلدان، البالاذری)۔ لیکن اس کا بھی کوئی تاریخی ثبوت نہیں ہے۔ بعید نہیں کہ اس سے بھی وہ اپنی قدامت ثابت کرنا چاہتے ہوں۔در حقیقت جو بات ثابت ہے وہ یہ ہے کہ جب 70 عیسوی میں رومیوں نے فلسطین میں یہودیوں کا قتل عام کیا، اور پھر 132 ء میں انہیں اس سر زمین سے بالکل نکال باہر کیا، اس دور میں بہت سے یہودی قبائل بھاگ کر حجاز میں پناہ گزین ہوئے تھے، کیونکہ یہ علاقہ فلسطین کے جنوب میں متصل ہی واقع تھا۔ یہاں آ کر انہوں نے جہاں جہاں چشمے اور سر سبز مقامات دیکھے، وہاں ٹھیر گئے اور پھر رفتہ رفتہ اپنے جوڑ توڑ اور سود خواری کے ذریعہ سے ان پر قبضہ جما لیا۔ ایلہ، مقنا، تبوک، تیماء، وادی القریٰ، خدک، اور خیبر پر ان کا تسلط اسی دور میں قائم ہوا۔ اور بنی قرَیظہ، بنی نضیر، بنی یَہدَل، اور بنی قَینُقَاع بھی اسی دور میں آ کر یثرب پر قابض ہوئے۔یثرب میں آباد ہونے والے قبائل میں سے بنی نضیر اور بنی قریظہ زیادہ ممتاز تھے، کیونکہ وہ کاہنوں (Priests) یا (Co hens)کے طبقہ میں سے تھے، انہیں یہودیوں میں عالی نسب مانا جاتا تھا اور ان کو اپنی ملت میں مذہبی ریاست حاصل تھی۔ یہ لوگ جب مدینہ میں آ کر آباد ہوئے اس وقت کچھ دوسرے عرب قبائل یہاں رہتے تھے جن کو انہوں نے دبا لیا اور عملاً اس سر سبز و شاداب مقام کے مالک بن بیٹھے۔ اس کے تقریباً تین صدی بعد 450ء یا 451 ء میں یمن کے اس سیلاب عظیم کا واقعہ پیش آیا جس کا ذکر سورہ سبا کے دوسرے رکوع میں گزر چکا ہے۔ اس سیلاب کی وجہ سے قوم سبا کے مختلف قبیلے یمن سے نکل کر عرب کے اطراف میں پھیل جانے پر مجبور ہوئے۔ ان میں سے غسانی شام میں، لخمی جبرہ (عراق) میں، بنی خزاعہ جدہ و مکہ کے درمیان، اور اَوس و خزرج یثرب میں جا کر آباد ہوئے۔ یثرب پر چونکہ یہودی چھائے ہوئے تھے، اس لیے انہوں نے اول اول اوس و خزرج کی دال نہ گلنے دی اور یہ دونوں عرب قبیلے چار و ناچار بنجر زمینوں پر بس گے جہاں ان کو قوت الایموت بھی مشکل سے حاصل ہوتا تھا۔ آخر کر ان کے سرداروں میں سے ایک شخص اپنے غسانی بھائیوں سے مدد مانگنے کے لیے شام گیا اور وہاں سے ایک لشکر لا کر اس نے یہودیوں کا زور توڑ دیا۔ اس طرح اوس، و خزرج کو یثرب پر پورا غلبہ حاصل ہو گیا۔ یہودیوں کے دو بڑے قبیلے، بنی نضیر اور بنی قریظہ شہر کے باہر جا کر بسنے پر مجبور ہو گئے۔ تیسرے قبیلے بنی قَلْینقاع کی چونکہ ان دونوں قبیلوں سے ان بن تھی اس لیے وہ شہر کے اندر ہی مقیم رہا، مگر یہاں رہنے کے لیے اسے قبیلہ خزرج کی پناہ لینی پڑی۔ اور اس کے مقابلہ میں بنی نضیر و بنی قریظہ نے قبیلہ اوس کی پناہ لی تاکہ اطراف یثرب میں امن کے ساتھ رہ سکیں۔ ذیل کے نقشہ سے واضح ہو گا کہ اس نئے انتظام کے ماتحت یثرب اور اس کے نواح میں یہودی بستیاں کہاں کہاں تھیں۔رسول اللہ صلی اللہ علیہ و سلم کی تشریف آوری سے پہلے، آغاز ہجرت تک، حجاز میں عموماً اور یثرب میں خصوصاً یہودیوں کی پوزیشن کے نمایاں خد و خال یہ تھے :زبان، لباس، تہزیب، تمدن، ہر لحاظ سے انہوں نے پوری طرح عربیت کا رنگ اختیار کر لیا تھا، حتیٰ کہ ان کی غالب اکثریت کے نام تک عربی ہو گئے تھے۔ 12 یہودی قبیلے جو حجاز میں آباد ہوئے تھے، ان میں سے بنی زَعُوراء کے سوا کسی قبیلے کا نام عبرانی نہ تھا۔ان کے چند گنے چنے علماء کے سوا کوئی عبرانی جانتا تک نہ تھا۔ زمانہ جاہلیت کے یہودی شاعروں کا جو کلام ہمیں ملتا ہے ان کی زبان اور خیالات اور مضامین میں شعرائے عرب سے الگ کوئی امتیازی شان نہیں پائی جاتی جو انہیں ممیز کرتی ہو۔ ان کے اور عربوں کے درمیان شادی بیاہ تک کے تعلقات قائم ہو چکے تھے۔ در حقیقت ان میں اور عام عربوں میں دین کے سوا کوئی فرق باقی نہ رہا تھا۔ لیکن ان ساری باتوں کے باوجود وہ عربوں میں جذب بالکل نہ ہوئے تھے، اور انہوں نے شدت کے ساتھ اپنی یہودی عصبیت برقرار رکھی تھی۔ یہ ظاہری عربیت انہوں نے صرف اس لیے اختیار کی تھی کہ اس کے بغیر وہ عرب میں نہ رہ سکتے تھے۔ان کی اس عربیت کی وجہ سے مغربی مستشرقین کو یہ دھوکا ہوا ہے کہ شاید یہ بنی اسرائیل نہ تھے بلکہ یہودی مذہب قبول کرنے والے عرب تھے، یا کم از کم ان کی اکثریت عرب یہودیوں پر مشتمل تھی۔ لیکن اس امر کا کوئی تاریخی ثبوت نہیں ملتا کہ یہودیوں نے حجاز میں کبھی کوئی تبلیغی سرگرمی دکھائی ہو، یا ان کے علماء نصرانی پادریوں اور مشنریوں کی طرح اہل عرب کو دین یہود کی طرف دعوت دیتے ہوں۔ اس کے برعکس ہم یہ دیکھتے ہیں کہ ان کے اندر اسرائیلیت کا شدید تعصب اور نسلی فخر و غرور پایا جاتا تھا۔ اہل عرب کو وہ اُمّی (Gentiles)کہتے تھے، جس کے معنی صرف ان پڑھ کے نہیں بلکہ وحشی اور جاہل کے تھے۔ ان کا عقیدہ یہ تھا کہ ان امیوں کو وہ انسانی حقوق حاصل نہیں ہیں جو اسرائیلیوں کے لیے ہیں اور ان کا مال ہر جائز و نا جائز طریقے سے مار کھانا اسرائیلیوں کے لیے حلال و طیب ہے۔ سرداران عرب کے ماسوا، عام عربوں کو وہ اس قابل نہ سمجھتے تھے کہ انہیں دین یہود میں داخل کر کے برابر کا درجہ دے دیں۔ تاریخی طور پر اس کا کوئی ثبوت نہیں ملتا، نہ روایات عرب میں ایسی کوئی شہادت ملتی ہے کہ کسی عرب قبیلے یا کسی بڑے خاندان نے یہودیت قبول کی ہو۔ البتہ بعض افراد کا ذکر ضرور ملتا ہے جو یہودی ہو گئے تھے۔ ویسے بھی یہودیوں کو تبلیغ دین کے بجائے صرف اپنے کاروبار سے دلچسپی تھی۔ اسی لیے حجاز میں یہودیت ایک دین کی حیثیت سے نہیں پھیلی بلکہ محض چند اسرائیلی قبیلوں کا سرمایہ فخر و ناز ہی بنی رہی۔ البتہ یہودی علماء نے تعویذ گنڈوں اور فال گیر اور جادو گری کا کاروبار خوب چمکا رکھا تھا جس کی وجہ سے عربوں پر ان کے ’’ علم‘‘ اور ’’عمل‘‘ کی دھاک بیٹھی ہوئی تھی۔ معاشی حیثیت سے ان کی پوزیشن عرب قبائل کی بہ نسبت زیادہ مضبوط تھی۔ چونکہ وہ فلسطین و شام کے زیادہ متمدن علاقوں سے آئے تھے، اس لیے وہ بہت سے ایسے فنون جانتے تھے جو اہل عرب میں رائج نہ تھے۔ اور باہر کی دنیا سے ان کے کاروباری تعلقات بھی تھے۔ ان وجوہ سے یثرب اور بالائی حجاز میں غلے کی در آمد اور یہا سے چھوہاروں کی برآمد ان کے ہاتھ میں آگئی تھی۔ مرغ بانی اور ماہی گیری پر بھی زیادہ تر انہی کا قبضہ تھ۔ پارچہ بانی کا کام بھی ان کے ہاں ہوتا تھا۔ جگہ جگہ میخانے بھی انہوں نے قائم کر رکھے تھے جہاں شام سے شراب لا کر فروخت کی جاتی تھی۔ نبی قینقاع زیادہ تر سنار اور لوہار اور ظروف سازی کا پیشہ کرتے تھے۔ اس سارے بَنَج بیوپار میں یہ یہودی بے تحاشا منافع خوری کرتے تھے۔ لیکن ان کا سب سے بڑا کاروبار سود خواری کا تھا جس کے جال میں انہوں نے گرد و پیش کی عرب آبادیوں کو پھانس رکھا تھا،اور خاص طور پر عرب قبائل کے شیوخ اور سردار، جنہیں قرض لے لے کر ٹھاٹھ جمانے اور شیخی بگھارنے کی بیماری لگی ہوئی تھی، ان کے پھندے میں پھنسے ہوئے تھے۔ یہ بھاری شرح سود پر قرضے دیتے، اور پھر سود در سود کا چکر چلاتے تھے جس کی گرفت میں آ جانے کے بعد مشکل ہی سے کوئی نکل سکتا تھا۔ اس طرح انہوں نے عربوں کو معاشی حیثیت سے کھوکھلا کر رکھا تھا، مگر اس کا فطری نتیجہ یہ بھی تھا کہ عربوں میں بالعموم ان کے خلاف ایک گہری نفرت پائی جاتی تھی۔ان کے تجارتی اور مالی مفادات کا تقاضا یہ تھا کہ عربوں میں کسی کے دوست بن کر کسی سے نہ بگاڑیں اور نہ ان کی باہمی لڑائیوں میں حصہ لیں۔ لیکن دوسری طرف ان کے مفاد ہی کا تقاضا یہ بھی تھا کہ عربوں کو باہم متحد نہ ہونے دیں، اور انہیں ایک دوسرے سے لڑاتے رہیں، کیونکہ وہ اس بات کو جانتے تھے کہ جب بھی عرب قبیلے باہم متحد ہوئے، وہ ان بڑی بڑی جائدادوں اور باغات اور سرسبز زمینوں پر انہیں قابض نہ رہنے دیں گے جو انہوں نے اپنی منافع خوری اور سود خواری سے پیدا کی تھیں۔ مزید برآں اپنی حفاظت کے لیے ان کے ہر قبیلے کو کسی نہ کسی طاقت ور عرب قبیلے سے حلیفانہ تعلقات بھی قائم کرنے پڑتے تھے، تاکہ کوئی دوسرا زبردست قبیلہ ان پر ہاتھ نہ ڈال سکے۔ اس بنا پر بارہا انہیں نہ صرف ان عرب قبائل کی باہمی لڑائیوں میں حصہ لینا پڑتا تھا، بلکہ بسا اوقات ایک یہودی قبیلہ اپنے حلیف عرب قبیلہ کے ساتھ مل کر کسی دوسرے یہودی قبیلے کے خلاف جنگ آزما ہو جاتا تھا جس کے حلیفانہ تعلقات فریق مخالف سے ہوتے تھے۔ یثرب میں بنی قیرظہ اور بنی نضیر اوس کے حلیف تھے اور بنی قینقاع خزرج کے۔ ہجرت سے تھوڑی مدت پہلے اوس اور خزرج کے درمیان جو خونریز لڑائی بُعاث کے مقام پر ہوئی تھی اس میں یہ اپنے اپنے حلیفوں کے ساتھ مل کر ایک دوسرے سے نبرد آزما ہوئے تھے۔یہ حالات تھے جب مدینے میں اسلام پہنچا اور بالآخر رسول اللہ صلی اللہ علیہ و سلم کی تشریف آوری کے بعد وہاں ایک اسلامی ریاست وجود میں آئی۔ آپ نے اس ریاست کو قائم کرتے ہی جو اولین کام کیے ان میں سے ایک یہ تھا کہ اوس اور خزرج اور مہاجرین کو ملا کر ایک برادری بنائی، اور دوسرا یہ تھا کہ اس مسلم معاشرے اور یہودیوں کے درمیان واضح شرائط پر ایک معاہدہ طے کیا جس میں اس امر کی ضمانت دی گئی تھی کہ کوئی کسی کے حقوق پر دست درازی نہ کرے گا اور بیرونی دشمنوں کے مقابلے میں یہ سب متحدہ دفاع کریں گے۔ اس معاہدے کے چند اہم فقرے یہ ہیں جن سے صاف معلوم ہوتا ہے کہ یہود اور مسلمانوں نے ایک دوسرے کے ساتھ تعلقات میں کن امور کی پابندی قبول کی تھی:ان علی الیھود نفقتھم و علی المسلمین نفقتھم، وان بینھم النصر علیٰ من حارب اھل ھٰذہ الصحیفۃ، وان بینہم النصح النصیحۃ والبردون الاٹم، وانہ لم یا ٹم امرؤٌ بحلیفہ، وان الجصر للمظلوم، وان الیھود ینفقون مع المؤمنین مادامو امحاربین، وان یثرب حرام جو فھا لا ھل ھٰذہ الصحیفۃ .........وانہ ما کان بین اھل ھٰذہ الصحیفۃ من حَدَثٍ اوا شتجار یخاف فساعہ فان مردہ الی اللہ عزو جل و الیٰ محمد رسولاللہ .........وانہ لا تجار قریش ولا من نصرھا، وان بینھم النصر علی من دھم یثرب۔ علی کل اناس حصتہم من جانبھم الذی قِبَلَھم۔ (ابن ہشام،ج 2، ص 147 تا 150 )’’یہ کہ یہودی اپنا خرچ اٹھائیں گے اور مسلمان اپنا خرچ، اور یہ کہ اس معاہدے کے شرکاء حملہ آور کے مقابلہ میں ایک دوسرے کی مدد کے پابند ہوں گے۔ اور یہ کہ وہ خلوص کے ساتھ ایک دوسرے کی خیر خواہی کریں گے اور ان کے درمیان نیکی و حق رسانی کا تعلق ہو گا نہ کہ گناہ اور زیادتی کا، اور یہ کہ کوئی اپنے حلیف کے ساتھ زیادتی نہیں کرے گا، اور یہ کہ مظلوم کی حمایت کی جائے گی، اور یہ کہ جب تک جنگ رہے یہودی مسلمانوں کے ساتھ مل کر اس کے مصارف اٹھائیں گے، اور یہ کہ اس معاہدے کے شرکاء پر یثرب میں کسی نوعیت کا فتنہ و فساد کرنا حرام ہے، اور یہ کہ اس معاہدے کے شرکاء کے درمیان اگر کوئی ایسا قضیہ یا اختلاف رونما ہو جس سے فساد کا خطرہ ہو تو اس کا فیصلہ اللہ کے قانون کے مطابق محمد رسول اللہ کریں گے، ............اور یہ کہ قریش اور اس کے حامیوں کو پناہ نہیں دی جائے گی اور یہ کہ یثرب پر جو بھی حملہ آسر ہو اس کے مقابلے میں شرکاء معاہدہ ایک دوسرے کی مدد کریں گے .........ہر فریق اپنی جانب کے علاقے کی مدافعت کا ذمہ دار ہو گا۔یہ ایک قطعی اور واضح معاہدہ تھا جس کی شرائط یہودیوں نے خود قبول کی تھیں۔ لیکن بہت جلدی انہوں نے رسول اللہ صلی اللہ علیہ و سلم اور اسلام اور مسلمانوں کے خلاف معاندانہ روش کا اظہار شروع کر دیا اور ان کا عناد روز بروز سخت سے سخت تر ہوتا چلا گیا۔ اس کے بڑے بڑے وجوہ تین تھے۔ایک یہ کہ وہ رسول اللہ صلی اللہ علیہ و سلم کو محض ایک رئیس قوم دیکھنا چاہتے تھے جو ان کے ساتھ بس ایک سیاسی معاہدہ کر کے رہ جائے اور صرف اپنے گروہ کے دنیوی مفاد سے سروکار رکھے مگر انہوں نے دیکھا کہ آپ تو اللہ اور آخرت اور رسالت اور کتاب پر ایمان لانے کی دعوت دے رہے ہیں (جس میں کود ان کے اپنے رسولوں اور کتابوں پر ایمان لانا بھی شامل تھا) اور معصیت چھوڑ کر ان احکام الہیٰ کی اطاعت اختیار کرنے اور ان اخلاقی حدود کی پابندی کرنے کی طرف بلا رہے ہیں جن کی طرف خود ان کے انبیاء بھی دنیا کو بلاتے رہے ہیں۔ یہ چیز ان کو سخت ناگوار تھی۔ ان کو خطرہ پیدا ہو گیا کہ یہ عالمگیر اصولی تحریک اگر چل پڑی تو اس کا سیلاب ان کی جامد مذہبیت اور ان کی نسلی قومیت کو بہا لے جائے گا۔دوسرے یہ کہ اوس و خزرج اور مہاجرین کو بھائی بھائی بنتے دیکھ کر، اور یہ دیکھ کر کہ گرد و پیش کے عرب قبائل میں سے بھی جو لوگ اسلام کی اس دعوت کو قبول کر رہے ہیں وہ سب مدینے کی اس اسلامی برادری میں شامل ہو کر ایک ملت بنتے جا رہے ہیں، انہیں یہ خطرہ پیدا ہو گیا کہ صدیوں سے اپنی سلامتی اور اپنے مفادات کی ترقی کے لیے انہوں نے عرب قبیلوں میں پھوٹ ڈال کر اپنا الو سیدھا کرنے کی جو پالیسی اختیار کر رکھی تھی وہ اب اس نئے نظام میں نہ چل سکے گی بلکہ اب ان کو عربوں کی ایک متحدہ طاقت سے سابقہ پیش آئے گا جس کے آگے ان کی چالیں کامیاب نہ ہو سکیں گی۔تیسرے یہ کہ معاشرے اور تمدن کی جو اصلاح رسول اللہ صلی اللہ علیہ و سلم کر رہے تھے اس میں کاروبار اور لین دین کے تمام نا جائز طریقوں کا سد باب شامل تھا، اور اب سے بڑھ کر یہ کہ سود کو بھی آپ ناپاک کمائی اور حرام خوری قرار دے رہے تھے جس سے انہیں خطرہ تھا کہ اگر عرب پر آپ کی فرمانروائی قائم ہو گئی تو آپ اسے قانوناً ممنوع کر دیں گے۔ اس میں ان کو اپنی موت نظر آتی تھی۔ان وجوہ سے انہوں نے حضورؐ کی مخالفت کو اپنا قومی نصب العین بنا لیا۔ آپ کو زک دینے کے لیے کوئی چال، کوئی تدبیر اور کوئی ہتھکنڈا استعمال کرنے میں ان کو ذرہ برابر تامل نہ تھا۔ وہ آپ کے خلاف طرح طرح کی جھوٹی باتیں پھیلاتے تھے تاکہ لوگ آپ سے بد گمان ہو جائیں۔ اسلام قبول کرنے والوں کے دلوں میں ہر قسم کے شکوک و شبہات اور وسوسے ڈالتے تھے تاکہ وہ اس دین سے بر گشتہ ہو جائیں۔ خود جھوٹ موٹ کا اسلام قبول کرنے کے بعد مرتد ہو جاتے تھے تاکہ لوگوں میں اسلام اور رسول صلی اللہ علیہ و سلم کے خلاف زیادہ سے زیادہ غلط فہمیاں پھیلائی جا سکیں۔ فتنے بر پا کرنے کے لیے منافقین سے ساز باز کرتے تھے۔ ہر اس شخص اور گروہ اور قبیلے سے رابطہ پیدا کرتے تھے جو اسلام کا دشمن ہوتا تھا۔ مسلمانوں کے اندر پھوٹ ڈالنے اور ان کو آپس میں لڑا دینے کے لیے ایڑی چوٹی کا زور لگا دیتے تھے۔ اوس اور خزرج کے لوگ خاص طور پر ان کے ہدف تھے جن سے ان کے مدتہائے دراز کے تعلقات چلے آ رہے تھے۔ جنگ بعاث کے تذکرے چھیڑ چھیڑ کر وہ ان کو پرانی دشمنیاں یاد دلانے کی کوشش کرتے تھے تاکہ ان کے درمیان پھر ایک دفعہ تلوار چل جائے اور اخوت کا وہ رشتہ تار تار ہو جائے جس میں اسلام نے ان کو باندھ دیا تھا۔ مسلمانوں کو مالی حیثیت سے تنگ کرنے کے لیے بھی وہ ہر قسم کی دھاندلیاں کرتے تھے۔ جن لوگوں سے ان کا پہلے سے لین دین تھا، ان میں سے جونہی کوئی شخص اسلام قبول کرتا وہ اس کو نقصان پہنچانے کے درپے ہو جاتے تھے۔ اگر اس سے کچھ لینا ہوتا تو تقاضے کر کر کے اس کا ناک میں دم کر دیتے، اور اگر اسے کچھ دینا ہوتا تو اس کی رقم مار کھاتے تھے اور علانیہ کہتے تھے کہ جب ہم نے تم سے معاملہ کیا تھا اس وقت تمہارا دین کچھ اور تھا، اب چونکہ تم نے اپنا دین بدل دیا ہے اس لیے ہم پر تمہارا کوئی حق باقی نہیں ہے۔اس کی متعدد مثالیں تفسیر طبری تفسیر نیسابوری، تفسیر طبرسی اور تفسیر روح المعانی میں سورہ آل عمران، آیت 75 کی تشریح کرتے ہوئے نقل کی گئی ہیں۔معاہدے کے خلاف یہ کھلی کھلی معاندانہ روش تو جنگ بدر سے پہلے ہی وہ اختیار کر چکے تھے۔ مگر جب بدر میں رسول اللہ صلی اللہ علیہ و سلم اور مسلمانوں کو قریش پر فتح مبین حاصل ہوٹی تو وہ تلملا اٹھے اور ان کے بغض کی آگ اور زیادہ بھڑک اٹھی۔ اس جنگ سے وہ یہ امید لگائے بیٹھے تھے کہ قریش کی طاقت سے ٹکرا کر مسلمانوں کا خاتمہ ہو جائے گا۔ اسی لیے انہوں نے فتح اسلام کی خبر پہنچنے سے پہلے مدینے میں یہ افواہیں اڑانی شروع کر دی تھیں کہ رسول اللہ صلی اللہ علیہ و سلم شہید ہو گئے، اور مسلمانوں کو شکست فاش ہوئی، اور اب ابوجہل کی قیادت میں قریش کا لشکر مدینے کی طرف بڑھا چلا آ رہا ہے۔ لیکن جب نتیجہ ان کی امیدوں اور تمناؤں کے خلاف نکلا تو وہ غم اور غصے کے مارے پھٹ پڑے۔ بنی نضیر کا سردار کعب بن اشرف چیخ اٹھا کہ ’’ خدا کی قَسم اگر محمدؐ نے ان اشراف عرب کو قتل کر دیا ہے تو زمین کا پیٹ ہمارے لیے اس کی پیٹھ سے زیادہ بہتر ہے ‘‘۔ پھر وہ مکہ پہنچا اور بدر میں جو سرداران قریش مارے گئے تھے ان کے نہایت اشتعال انگیز مرثیے کہہ کر مکہ والوں کو انتقام پر اکسایا۔ پھر مدینہ واپس آ کر اس نے اپنے دل کی جلن نکالنے کے لیے ایسی غزلیں کہنی شروع کیں جن میں مسلمان شرفاء کے ساتھ عشق کیا گیا تھا۔ آخر کار اس کی شرارتوں سے تنگ آ کر رسول اللہ صلی اللہ علیہ و سلم نے ربیع الاول 3 ھ میں محمد بن مسلمہ انصاری کو بھیج کر اسے قتل کرا دیا (ابن سعد، ابن ہشام، تاریخ طبری)۔یہودیوں کا پہلا قبیلہ جس نے اجتماعی طور پر جنگ بدر کے بعد کھلم کھلا اپنا معاہدہ توڑ دیا، بنی قینقاع تھا، یہ لوگ خود شہر مدینہ کے اندر ایک محلہ میں آباد تھے اور چونکہ یہ سنار، لوہار اور ظروف ساز تھے، اس لیے ان کے بازار میں اہل مدینہ کثرت سے جانا آنا پڑتا تھا۔ ان کو اپنی شجاعت پر بڑا ناز تھا۔ آہن گر ہونے کی وجہ سے ان کا بچہ بچہ مسلح تھا۔ سات سو مردان جنگی ان کے اندر موجود تھے۔ اور ان کو اس بات کا بھی زعم تھا کہ قبیلہ خزرج سے ان کے پرانے حلیفانہ تعلقات تھے اور خزرج کا سردار عبداللہ بن ابی ان کا پشتی بان تھا۔ بدر کے واقعہ سے یہ اس قدر مشتعل ہوئے کہ انہوں نے اپنے بازار میں آنے جانے والے مسلمانوں کو ستا نا، اور خاص طور پر ان کی عورتوں کو چھیڑنا شروع کر دیا۔ رفتہ رفتہ نوبت یہاں تک پہنچی کہ ایک روز ان کے بازار میں ایک مسلمان عورت کو بر سر عام برہنہ کر دیا گیا۔ اس پر سخت جھگڑا ہوا اور ہنگامے میں ایک مسلمان اور ایک یہودی قتل ہو گیا۔جب حالات اس حد کو پہنچ گئے تو رسول اللہ صلی اللہ علیہو سلم ان کے محلہ میں تشریف لے گئے اور ان کو جمع کر کے آپ نے ان کو راہ راست پر آنے کی تلقین فرمائی۔ مگر انہوں نے جواب دیا’’ اے محمدؐ، تم نے شاید ہمیں بھی قریش سمجھا ہے؟ وہ لڑنا نہیں جانتے تھے،اس لیے تم نے انہیں مار لیا۔ ہم سے سابقہ پیش آئے گا تو تمہیں معلوم ہو جائے گا کہ مرد کیسے ہوتے ہیں۔ ‘‘ یہ گویا صاف صاف اعلان جنگ تھا۔ آخر کار رسول اللہ علیہ و سلم نے شوال (اور بروایت بعض ذی القعدہ) 2 ھ کے آخر میں ان کے محلہ کا محاصرہ کر لیا۔ صرف پندرہ روز ہی یہ محاصرہ رہا تھا کہ انہوں نے ہتھیار ڈال دیے اور ان کے تمام قابل جنگ آدمی باندھ لیے گئے۔ اب عبداللہ بن ابی ان کی حمایت کے لیے اٹھ کھڑا ہوا اور اس نے سخت اصرار کیا کہ آپ انہیں معاف کر دیں۔ چنانچہ حضور نے اس کی درخواست قبول کر کے یہ فیصلہ فرما دیا کہ بنی قینقاع اپنا سب مال، اسلحہ اور آلات صنعت چھوڑ کر مدینہ سے نکل جائیں (ابن سعد، ابن ہشام، تاریخ طبری)۔ ان دو سخت اقدامات (یعنی بنی قینقاع کے اخراج اور کعب بن اشرف کے قتل) سے کچھ مدت تک یہودی اتنے خوف زدہ رہے کہ انہیں کوئی مزید شرارت کرنے کی ہمت نہ ہوئی۔ مگر اس کے بعد شوال 3 ھ میں قریش کے لوگ جنگ بدر کا بدلہ لینے کے لیے بڑی تیاریوں کے ساتھ مدینہ پر چڑھ کر آئے، اور ان یہودیوں نے دیکھا کہ قریش کی تین ہزار فوج کے مقابلہ میں رسول اللہ صلی اللہ علیہ و سلم کے ساتھ صرف ایک ہزار آدمی لڑنے کے لیے نکلے ہیں، اور ان میں سے بھی تین سو منافقین الگ ہو کر پلٹ آئے ہیں، تو انہوں نے معاہدے کی پہلی اور صریح خلاف ورزی اس طرح کی کہ مدینہ کی مدافعت میں آپ کے ساتھ شریک نہ ہوئے، حالانکہ وہ اس کے پابند تھے۔ پھر جب معرکہ احد میں مسلمانوں کو نقصان عظیم پہنچا تو ان کی جرأتیں اور بڑھ گئیں، یہاں تک کہ بنی نضیر نے رسول اللہ صلی اللہ علیہ و سلم کو قتل کرنے کے لیے باقاعدہ ایک سازش کی جو عین وقت پر ناکام ہو گئی اس واقعہ کی تفصیل یہ ہے کہ بئر معونہ کے سانحہ (صفر 4 ھ) کے بعد عمرو بن امیہ ضمری نے انتقامی کار روائی کے طور پر غلطی سے بنی عامر کے دو آدمیوں کو قتل کر دیا جو دراصل ایک معاہِلہ قبیلہ سے تعلق رکھتے تھے مگر عمرو نے ان کو دشمن قبیلہ کے آدمی سمجھ لیا تھا۔ اس غلطی کی وجہ سے ان کا خون بہا مسلمانوں پر واجب آگیا تھا، اور چونکہ بنی عامر کے ساتھ معاہدے میں بنی نضیر بھی شریک تھ، اس لیے رسول اللہ صلی اللہ علیہ و سلم چند صحابہ کے ساتھ خود ان کی بستی میں تشریف لے گئے تاکہ خون بہا کی ادائیگی میں ان کو بھی شرکت کی دعوت دین۔ وہاں انہوں نے آپ کو چکنی چپڑی باتوں میں لگا یا اور اندر ہی اندر یہ سازش کی کہ ایک شخص اس مکان کی چھت پر سے آپ کے اوپر ایک بھاری پتھر گرا دے جس کی دیوار کے سائے میں آپ تشریف فرما تھے۔ مگر قبل اس کے کہ وہ اپنی اس تدبیر پر عمل کرتے، اللہ تعالیٰ نے آپ کو بر وقت خبردار کر دیا، اور آپ فوراً وہاں سے اٹھ کر مدینہ واپس تشریف لے آئے۔اب ان کے ساتھ کسی رعایت کا سوال باقی نہ رہا۔ حضورؐ نے ان کو بلاتا خیر یہ الٹی میٹم بھیج دیا کہ تم نے جو غداری کرنی چاہی تھی وہ میرے علم میں آ گئی ہے۔ لہٰذا دس دن کے اندر مدینہ سے نکل جاؤ، اس کے بعد اگر تم یہاں ٹھیرے رہے تو جو شخص بھی تمہاری بستی میں پایا جائے گا۔ اس کی گردن مار دی جائے گی۔ دوسری طرف عبداللہ بن ابی نے ان کو پیغام بھیجا کہ میں دو ہزار آدمیوں سے تمہاری مدد کروں گا، بنی قریظہ اور بنی غطفان بھی تمہاری مدد کو آئیں گے، تم ڈٹ جاؤ اور ہر گز اپنی جگہ نہ چھوڑو۔ اس جھوٹے بھروسے پر انہوں نے حضورؐ کے الٹی میٹم کا یہ جواب دیا کہ ہم یہاں سے ہیں نکلیں گے، آپ سے جو کچھ ہو سکے کر لیجیے۔ اس پر ربیع الاول 4 ھ میں رسول اللہ صلی اللہ علیہ و سلم نے ان کا محاصرہ کر لیا، اور صرف چند روز کے محاصرہ کے بعد (جس کی مدت بعض روایات میں چھ دن اور بعض میں پندرہ دن آئی ہے )وہ اس شرط پر مدینہ چھوڑ دینے کے لیے راضی ہو گئے کہ اسلحہ کے سوا جو کچھ بھی وہ اپنے اونٹوں پر لاد کر لے جا سکیں گے لے جائیں گے۔ اس طرح یہودیوں کے اس دوسرے شریر قبیلے سے مدینہ کی سر زمین خالی کرالی گئی۔ ان میں سے صرف دو آدمی مسلمان ہو کر یہاں ٹھیر گئے۔ باقی شام اور خیبر کی طرف نکل گئے۔یہی واقعہ ہے جس سے اس سورہ میں بحث کی گئی ہے۔ موضوع اور مضامین: سورۃ کا موضوع، جیسا کہ اوپر بیان ہوا، جنگ بنی نضیر پر تبصرہ ہے۔ اس میں بحیثیت مجموعی چار مضامین بیان ہوئے ہیں۔ (1)۔ پہلی چار آیتوں میں دنیا کو اس انجام سے عبرت دلائی گئی ہے جو ابھی ابھی بنی نضیر نے دیکھا تھا۔ ایک بڑا قبیلہ جو کے افراد کی تعداد اس وقت مسلمانوں کی تعداد سے کچھ کم نہ تھی، جو مال و دولت میں مسلمانوں سے بہت بڑھا ہوا تھا، جس کے پاس جنگی سامان کی بھی کمی نہ تھی، جس کی گڑھیاں بڑی مضبوط تھیں، صرف چند روز کے محاصرے کی تاب بی نہ لاسکا اور بغیر اس کے کہ کسی ایک آدمی کے قتل کی بھی نوبت آئی ہوتی وہ اپنی صدیوں کی جمائی بستی چھوڑ کر جلا وطنی قبول کرنے پر آمادہ ہو گیا۔ اللہ تعالیٰ نے بتایا ہے کہ یہ مسلمانوں کی طاقت کا کرشمہ نہیں تھا بلکہ اس بات کا نتیجہ تھا کہ وہ اللہ اور اس کے رسول سے نبر آزما ہوئے تھے اور جو لوگ اللہ کی طاقت سے ٹکرانے کی جرأت کریں وہ ایسے ہی انجام سے دوچار ہوتے ہیں۔ (2)۔ آیت 5 میں قانون جنگ کا یہ قاعدہ بیان کیا گیا ہے کہ جنگی ضروریات کے لیے دشمن ے علاقے میں جو تخریبی کار روائی کی جائے وہ فساد فی الارض کی تعریف میں نہیں آتی۔ (3)۔ آیت 6 سے 10 تک یہ بتایا گیا ہے کہ ان ممالک کی زمینوں اور جائدادوں کا بندوبست کس طرح کیا جائے جو جنگ یا صلح کے نتیجے میں اسلامی حکومت کے زیر نگیں آئیں۔ چونکہ یہ پہلا موقع تھا کہ ایک مفتوحہ علاقہ مسلمانوں کے قبضہ میں آیا اس لیے یہاں اس کا قانون بیان کر دیا گیا۔ (4)۔ آیت 11 سے 17 تک منافقین کے اس رویہ پر تبصرہ کیا گیا ہے جو انہوں نے جنگ بنی نضیر کے موقع پر اختیار کیا تھا، اور ان اسباب کی نشان دہی کی گئی ہے جو در حقیقت ان کے اس رویہ کی تہ میں کام کر رہے تھے۔ (5)۔ آخری رکوع پورا ایک نصیحت ہے جس کے مخاطب وہ تمام لوگ ہیں جو ایمان کا دعویٰ کر کے مسلمانوں کی گروہ میں شامل ہو گئے ہوں، مگر ایمان کی اصل روح سے خالی رہیں۔ اس میں ان کو بتایا گیا ہے کہ ایمان کا اصل تقاضا کیا ہے، تقویٰ اور فسق میں حقیقی فرق کیا ہے، جو قرآن کو ماننے کا دعویٰ کر رہے ہیں اس کی اہمیت کیا ہے، اور جس خدا پر ایمان لانے کا وہ اقرار کرتے ہیں وہ کن صفات کا حامل ہے۔ ','سید ابو اعلیٰ مودودیؒ -  تفہیم القرآن');
INSERT INTO "chapters_ur" VALUES(60,'امتحان لی جانے والی',NULL,' نام: اس سورہ کی آیت نمبر 10 میں حکم دیا گیا ہے کہ جو عورتیں ہجرت کر کے آئیں اور مسلمان ہونے کا دعویٰ کریں ان کا امتحان لیا جائے۔ اسی مناسبت سے اس کا نام الممتحنہ رکھا گیا ہے۔ اس کا تلفظ ممتحنہ بھی کیا جاتا ہے اور ممتحنہ بھی۔ پہلے تلفظ کے لحاظ سے معنی ہیں ’’ وہ عورت جس کا امتحان لیا جائے‘‘۔ اور دوسرے تلفظ کے لحاظ سے معنی ہیں ’’ امتحان لینے والی سورۃ‘‘۔ زمانہ نزول: اس میں دو ایسے معاملات پر کلام فرمایا گیا ہے جن کا زمانہ تاریخی طور پر معلوم ہے۔ پہلا معاملہ حضرت حاطب بن ابی بلْتَعَہ کا ہے جنہوں نے فتح مکہ سے کچھ مدت پہلے ایک خفیہ خط کے ذریعہ سے قریش کے سرداروں کو رسول اللہ صلی اللہ علیہ و سلم کے اس ارادے کی اطلاع بھیجی تھی کہ آپ ان پر حملہ کرنے والے ہیں۔ اور دوسرا معاملہ ان مسلمان عورتوں کا ہے جو صلح حدیبیہ کے بعد مکہ سے ہجرت کے کر کے مدینہ آنے لگی تھیں اور ان کے بارے میں یہ سوال پیدا ہو گیا تھا کہ شرائط صلح کی رو سے مسلمان مردوں کی طرح کیا ان عورتوں کو بھی کفار کے حوالہ کر دیا جائے؟ ان دو معاملات کے ذکر سے یہ بات قطعی طور پر متعین ہو جاتی ہے کہ یہ سورہ صلح حدیبیہ اور فتح مکہ کے درمیانی دور میں نازل ہوئی ہے ان کے علاوہ ایک تیسرا معاملہ بھی ہے جس کا ذکر سورۃ کے آخر میں آیا ہے، اور وہ یہ کہ جب عورتیں ایمان لا کر بیعت کے لیے رسول اللہ صلی اللہ علیہ و سلم کی خدمت میں حاضر ہوں تو آپ ان سے کن باتوں کا عہد لیں۔ اس حصے کے متعلق بھی قیاس یہی ہے کہ یہ بھی فتح مکہ سے کچھ پہلے نازل ہوا ہے کیونکہ فتح مکہ کے بعد قریش کے مردوں کی طرح ان کی عورتیں بھی بہت بڑی تعداد میں بیک وقت داخل اسلام ہونے والی تھیں اور اسی موقع پر یہ ضرورت پیش آئی تھی کہ اجتماعی طور پر ان سے عہد لیا جائے۔ موضوع اور مباحث: اس سورۃ کے تین حصے ہیں : پہلا حصہ آغاز سورہ سے آیت 9 تک چلتا ہے اور سورۃ کے خاتمہ پر آیت 13 بھی اسی سے تعلق رکھتی ہے۔ اس میں حضرت حاطب بن ابی بلتعہ کے اس فعل پر سخت گرفت کی گئی ہے کہ انہوں نے محض اپنے اہل و عیال کو بچانے کی خاطر رسول اللہ صلی اللہ علیہ و سلم کے ایک نہایت اہم جنگی راز سے دشمنوں کو خبر دار کرنے کی کوشش کی تھی جسے اگر بر وقت ناکام نہ کر دیا گیا ہوتا تو فتح مکہ کے موقع پر بڑا کشت و خون ہوتا، مسلمانوں کی بھی بہت سی قیمتی جانیں ضائع ہوتیں، قریش کے بھی بہت سے وہ لوگ مارے جاتے جو بعد میں اسلام کی عظیم خدمات انجام دینے والے تھے، وہ تمام فوائد بھی ضائع ہو جاتے جو مکہ کو پُر امن طریقہ سے فتح کرنے کی صورت میں حاصل ہو سکتے تھے، اور اتنے عظیم نقصانات صرف اس وجہ سے ہوتے کہ مسلمانوں میں سے ایک شخص اپنے بال بچوں کو جنگ کے خطرات سے محفوظ رکھنا چاہتا تھا۔ اس شدید غلطی پر تنبیہ فرماتے ہوئے اللہ تعالیٰ نے تمام اہل ایمان کو یہ تعلیم دی ہے کہ کسی مومن کو کسی حال میں اور کسی غرض کے لیے بھی اسلام کے دشمن کافروں کے ساتھ محبت اور دوسری کا تعلق نہ رکھنا چاہیے اور کوئی ایسا کام نہ کرنا چاہیے جو کفر و اسلام کی کشمکش میں کفار کے لیے مفید ہو۔ البتہ جو کافر اسلام اور مسلمانوں کے خلاف عملاً دشمنی اور ایذا رسانی کا برتاؤ نہ کر رہے ہوں ان کے ساتھ احسان کا رویہ اختیار کرنے میں کوئی مضائقہ نہیں ہے۔ دوسرا حصۃ آیات 10۔11 پر مشتمل ہے۔ اس میں ایک اہم معاشرتی مسئلے کا فیصلہ کیا گیا ہے جو اس وقت بری پیچیدگی پیدا کر رہا تھا۔ مکہ میں بہت سی مسلمان عورتیں ایسی تھیں جن کے شوہر کافر تھے اور وہ کسی نہ کسی طرح ہجرت کر کے مدینہ پہنچ جاتی تھیں۔ اسی طرح مدینہ میں بہت سے مسلمان مر د ایسے تھے جن بیویاں کفر تھیں اور وہ مکہ ہی میں رہ گئی تھیں۔ ان کے بارے میں یہ سوال پیدا ہوتا تھا کہ ان کے درمیان رشتہ ازدواج باقی ہے یا نہیں۔ اللہ تعالیٰ نے اس کا ہمیشہ ہمیشہ کے لیے یہ فیصلہ فرما دیا کہ مسلمان عورت کے لیے کافر شوہر حلال نہیں ہے، اور مسلمان مرد کے لیے بھی یہ جائز نہیں کہ وہ مشرک بیوی کو اپنے نکاح میں رکھے۔ یہ فیصلہ بڑے اہم قانونی نتائج رکھتا ہے جن کی تفصیل ہم آگے اپنے حواشی میں بیان کریں گے۔ تیسرا حصہ آیت 12 پر مشتمل ہے جس میں رسول اللہ صلی اللہ علیہ و سلم کو ہدایت فرمائی گئی ہے کہ جو عورتیں اسلام قبول کریں ان سے آپ ان بڑی بڑی برائیوں سے بچنے کا عہد لیں جو جاہلیت عرب کے معاشرے میں عورتوں کے اندر پھیلی ہوئی تھیں اور اس بات کا اقرار کرائیں کہ آئندہ وہ بھلائی کے ان تمام طریقوں کی پیروی کریں گی جن کا حکم اللہ کے رسول کی طرف سے ان کو دیا جائے۔ ','سید ابو اعلیٰ مودودیؒ -  تفہیم القرآن');
INSERT INTO "chapters_ur" VALUES(61,'صف',NULL,' نام: چوتھی آیت کے فقرے یُقَاتِلُوْنَ فِیْ سَبِیْلِہٖ صَفّاً سے ماخوذ ہے ۔ مراد یہ ہے کہ وہ سورۃ ہے جس میں لفظ صف آیا ہے ۔ زمانہ نزول: کسی معتبر روایت سے اس کا زمانہ نزول معلوم نہیں ہو سکا۔ لیکن اس کے مضامین پر غور کرنے سے اندازہ ہوتا ہے کہ یہ غالباً جنگ احد کے متصل زمانے میں نازل ہوئی ہو گی، کیونکہ اس کے بین السطور میں جن حالات کی طرف اشارہ محسوس ہوتا ہے وہ اسی دور میں پائے جاتے تھے ۔ موضوع اور مضمون: اس کا موضوع ہے مسلمانوں کو بھی مخاطب کیا گیا ہے ، اور ان لوگوں کو بھی جو ایمان کا جھوٹا دعویٰ کر کے اسلام میں داخل ہو گئے تھے ، اور ان کو بھی جو مخلص تھے ۔ بعض آیات کا خطاب پہلے دونوں گروہوں سے ہے ، بعض میں صرف منافقین مخاطب ہیں ، اور بعض کا روئے سخن صرف مخلصین کی طرف ہے ۔ انداز کلام سے خود معلوم ہو جاتا ہے کہ کہاں کون مخاطب ہے ۔آغاز میں تمام ایمان لانے والوں کو خبردار کیا گیا ہے کہ اللہ تعالیٰ کی نگاہ میں نہایت مبغوض ہیں وہ لوگ جو کہیں کچھ اور کریں کچھ، اور نہایت محبوب ہیں وہ لوگ جو راہ حق میں لڑنے کے لیے سیسہ پلائی ہوئی دیوار کی طرح ڈٹ کر کھڑے ہوں ۔پھر آیت 5 سے 7 تک رسول اللہ صلی اللہ علیہ و سلم کی امت کے لوگوں کو متنبہ کیا گیا ہے کہ اپنے رسول اور اپنے دین کے ساتھ تمہاری روش وہ نہ ہونی چاہیے جو موسیٰ علیہ السلام اور عیسیٰ علیہ السلام کے ساتھ بنی اسرائیل نے اختیار کی۔ حضرت موسیٰ علیہ السلام اور عیسیٰ علیہ السلام کے ساتھ بنی اسرائیل نے اختیار کی۔حضرت موسیٰ کو وہ خدا کا رسول جاننے کے باوجود جیتے جی تنگ کرتے رہے ، اور حضرت عیسیٰ سے کھلی کھلی نشانیاں دیکھ لینے کے باوجود وہ ان کو جھٹلانے سے باز نہ آئے۔ نتیجہ اس کا یہ ہوا کہ اس قوم کے مزاج کا سانچہ ہے ٹیڑھا ہو کر رہ گیا اور اس سے ہدایت کی توفیق سلب ہو گئے۔ یہ کوئی ایسی قابل رشک حالت نہیں ہے کہ کوئی دوسری قوم اس میں مبتلا ہونے کی تمنا کرے ۔پھر آیت 8۔9 میں پوری ٹھدی کے ساتھ اعلان کیا گیا کہ یہود و نصاریٰ اور ان سے ساز باز رکھنے والے منافقین اللہ کے اس نور کو بجھانے کی چاہے کتنی ہی کوشش کر لیں ، یہ پوری آپ و تاب کے ساتھ دنیا میں پھیل کر رہے گا اور مشرکین کو خواہ کتنا ہی ناگوار ہو، رسول بر حق کا لایا ہوا دین ہر دوسرے دین پر غالب آ کر رہے گا۔اس کے بعد آیات 10 سے 13 میں اہل ایمان کو بتایا گیا ہے کہ دنیا اور آخرت میں کامیابی کی راہ صرف ایک ہے ، اور وہ یہ ہے کہ اللہ اور اس کے رسول پر سچے دل سے ایمان لاؤ اور اللہ کی راہ میں جان و مال سے جہاد کرو۔ آخرت میں اس کا ثمرہ خدا کے عذاب سے نجات، گناہوں کی مغفرت، اور ہمیشہ ہمیشہ کے لیے جنت کا حصول ہے ، اور دنیا میں اس کا انعام خدا کی تائید و نصرت اور فتح و ظفر ہے ۔آخر میں اہل ایمان کو تلقین کی گئی ہے کہ جس طرح حضرت عیسیٰ علیہ السلام کے حواریوں نے اللہ کی راہ میں ان کا ساتھ دیا تھا اس طرح وہ بھی ’’ انصار اللہ‘‘ بنیں تاکہ کافروں کے مقابلہ میں ان کو بھی اسی طرح اللہ کی تائیش حاصل ہو جس طرح پہلے ایمان لانے والوں کو حاصل ہوئی تھی۔','سید ابو اعلیٰ مودودیؒ -  تفہیم القرآن');
INSERT INTO "chapters_ur" VALUES(62,'جمعہ',NULL,' نام: آیت 9 کے فقرے اِذَا نُوْدِیَ لِصَّلوٰۃِ مِنْ یَّوْمِ الْجُمُعَۃِ سے ماخوذ ہے۔ اگر چہ اس سورہ میں نماز جمعہ کے احکام بھی بیان کیے گئے ہیں، لیکن ’’جمعہ‘‘ بحیثیت مجموعی اس کے مضامین کا عنوان نہیں، بلکہ دوسری سورتوں کے ناموں کی طرح یہ نام بھی علامت ہی کے طور پر ہے۔ زمانہ نزول: پہلے رکوع کا زمانہ نزول 7 ھ سے ، اور غالباً یہ فتح خیبر کے موقع پر یا اس کے بعد قریبی زمانے میں نازل ہوا ہے۔ بخاری، مسلم، ترمذی، نَسائی اور ابن جریر نے حضرت ابو ہریرہ کی یہ روایت نقل کی ہے کہ ہم حضورؐ کی خدمت میں بیٹھے ہوئے تھے جب یہ آیات نازل ہوئیں۔ حضرت ابو ہریرہؓ کے متعلق یہ بات تاریخ سے ثابت ہے کہ وہ صلح حُدَیْبیہ کے بعد اور فتح خیبر سے پہلے ایمان لائے تھے۔ اور خیبر کی فتح ابن ہشام کے بقول محرم، اور ابن سعد کے بقول جمادی الاُولیٰ 7 ھ میں ہوئی ہے۔ پس قرین قیاس یہ ہے کہ یہودیوں کے اس آخری گڈھ کو فتح کرنے کے بعد اللہ تعالیٰ نے ان کو خطاب کرتے ہوئے یہ آیات نازل فرمائی ہوں گی، یا پھر ان کا نزول اس وقت ہوا ہو گا جب خیبر کا انجام دیکھ کر شمالی حجاز کی تمام یہودی بستیاں اسلامی حکومت کی تابع فرمان بن گئی تھیں۔ دوسرا رکوع ہجرت کے بعد قریبی زمانے ہی میں نازل ہوا ہے۔ کیونکہ حضورؐ نے مدینہ طیبہ پہنچتے ہی پانچویں روز جمعہ قائم کر دیا تھا، اور اس رکوع کی آخری آیت میں جس واقعہ کی طرف اشارہ کیا گیا ہے وہ صاف بتا رہا ہے کہ وہ اقامت جمعہ کا سلسلہ شروع ہونے کے بعد لازماً کسی ایسے زمانے ہی میں پیش آیا ہو گا جب لوگوں کو دینی اجتماعات کے آداب کی پوری تربیت ابھی نہیں ملی تھی۔ موضوع اور مضامین: جیسا کہ اوپر ہم بیان کر چکے ہیں، اس سورہ کے دو رکوع دو الگ زمانوں میں نازل ہوئے ہیں۔ اسی لیے دونوں کے موضوع الگ ہیں اور مخاطب بھی الگ۔ اگر چہ ان کے درمیان ایک نوع کی مناسبت ہے جس کی بنا پر انہیں ایک سورہ میں جمع کیا گیا ہے ، لیکن مناسبت سمجھنے سے پہلے ہمیں دونوں کے موضوعات کو الگ الگ سمجھ لینا چاہیے۔پہلا رکوع اس وقت نازل ہوا جب یہودیوں کی وہ تمام کوششیں ناکام ہو چکی تھیں جو اسلام کی دعوت کا راستہ روکنے کے لیے پچھلے چھ سال کے دوران میں انہوں نے کی تھیں۔ پہلے مدینہ میں ان کے تین تین طاقتور قبیلے رسول اللہ صلی اللہ علیہ و سلم کو نیچا دکھانے کے لیے ایڑی چوٹی تک کا زور لگاتے رہے اور نتیجہ یہ دیکھا کہ ایک قبیلہ پوری طرح تباہ ہو گیا اور دو قبیلوں کو جلا وطن ہونا پڑا۔ پھر وہ سازشیں کر کے عرب کے بہت سے قبائل کو مدینے پر چڑھا لائے ، مگر غزوہ احزاب میں ان سب نے منہ کی کھائی۔ اس کے بعد ان کا سب سے بڑا گڈھ خیبر رہ گیا تھا جہاں مدینہ سے نکلے ہوئے یہودیوں کی بھی بڑی تعداد جمع ہو گئی تھی۔ ان آیات کے نزول کے وقت وہ بھی بغیر کسی غیر معمولی زحمت کے فتح ہو گیا، اور یہودیوں نے خود درخواست کر کے وہاں مسلمانوں کے کاشتکاروں کی حیثیت سے رہنا قبول کر لیا۔ اس آخری شکست کے بعد عرب میں یہودی طاقت کا بالکل خاتمہ ہو گیا۔ وادی القریٰ، فَدَک، تَیما، تبوک، سب ایک ایک کر کے ہتھیار ڈالتے چلے گئے ، یہاں تک کہ عرب کے تمام یہودی اسی اسلام کی رعایا بن کر رہ گئے جس کے وجود کو برداشت کرنا تو درکنار، جس کا نام سننا تک انہیں گوارا نہ تھا۔ یہ موقع تھا جب اللہ تعالیٰ نے اس سورہ میں ایک مرتبہ پھر ان کو خطاب فرمایا، اور غالباً یہ آخری خطاب تھا جو قرآن مجید میں ان سے کیا گیا۔ اس میں انہیں مخاطب کر کے تین باتیں فرمائی گئی ہیں:1)۔ تم نے اس رسول کو اس لیے ماننے سے انکار کیا کہ یہ اس قوم میں مبعوث ہوا تھا جسے تم حقارت کے ساتھ ’’ اُمّی ‘‘ کہتے ہو۔ تمہارا زعم باطل یہ تھا کہ رسول لازماً تمہاری اپنی قوم ہی کا ہونا چاہیے۔ تم یہ فیصلہ کیے بیٹھے تھے کہ تمہاری قوم سے باہر کا جو شخص رسالت کا دعویٰ کرے وہ ضرور جھوٹا ہے ، کیونکہ یہ منصب تمہاری نسل کے لیے مختص ہو چکا ہے اور ’’ امیوں‘‘ میں کبھی کوئی رسول نہیں آ سکتا۔ لیکن اللہ نے انہی امیوں میں سے ایک رسول اٹھایا ہے جو تمہاری آنکھوں کے سامنے اس کی کتاب سنا رہا ہے ، نفوس کا تزکیہ کر رہا ہے ، اور ان لوگوں کو ہدایت دے رہا ہے جن کی گمراہی کا حال تم خود جانتے ہو۔ یہ اللہ کا فضل ہے جسے چاہے دے۔ اس کے فضل پر تمہارا اجارہ نہیں ہے کہ جسے تم دلوانا چاہو اسی کو وہ دے اور جسے تم محروم رکھنا چاہو اسے وہ محروم رکھے۔2)۔ تم کو تَوراۃ کا حامل بنایا گیا تھا، مگر تم نے اس کی ذمہ داری نہ سمجھی، نہ ادا کی۔ تمہارا حال اس گدھے کا سا ہے جس کی پیٹھ پر کتابیں لدی ہوئی ہوں اور اسے کچھ نہیں معلوم کہ وہ کس چیز کا بار اٹھائے ہوئے ہے۔ بلکہ تمہاری حالت گدھے سے بھی بد تر ہے۔ وہ تو سمجھ بوجھ نہیں رکھتا، مگر تم سمجھ بوجھ رکھتے ہو اور پھر کتاب اللہ کے حامل ہونے کی ذمہ داری سے فرار ہی نہیں کرتے ، دانستہ اللہ کی آیات کو جھٹلانے سے بھی باز نہیں رہتے۔ اور اس پر تمہارے نام لکھ دی گئی ہے۔ گویا تمہاری رائے یہ ہے کہ خواہ تم اللہ کے پیغام کا حق ادا کرو یا نہ کرو، بہر حال اللہ اس کا پابند ہے کہ وہ اپنے پیغام کا حامل تمہارے سوا کسی کو نہ بنائے !3)۔ تم اگر واقعی اللہ کے چہیتے ہوتے اور تمہیں اگر یقین ہوتا کہ اس کے ہاں تمہارے لیے بڑی عزت اور قدر و منزلت کا مقام محفوظ ہے تو تمہیں موت کا ایسا خوف نہ ہوتا کہ ذلت کی زندگی قبول ہے مگر موت کسی طرح قبول نہیں۔ یہی موت کا خوف ہی تو ہے جس کی بدولت پچھلے چند سالوں میں تم شکست پر شکست کھاتے چلے گئے ہو۔ تمہارا ضمیر خوب جانتا ہے کہ ان کرتوتوں کے ساتھ مرو گے تو اللہ کے ہاں اس سے زیادہ ذلیل و خوار ہو گے جتنے دنیا میں ہو رہے ہو۔یہ ہے پہلے رکوع کا مضمون۔ اس کے بعد دوسرا رکوع، جو کئی سال پہلے نازل ہوا تھا، اس سورہ میں لا کر اس لیے شامل کیا گیا ہے کہ اللہ تعالیٰ نے یہودیوں کے سَبْت کے مقابلہ میں مسلمانوں کو جمعہ کے ساتھ وہ معاملہ نہ کریں جو یہودیوں نے سبت کے ساتھ کیا تھا۔ یہ رکوع اس وقت نازل ہوا تھا جب مدینے میں ایک روز عین نماز جمعہ کے وقت ایک تجارتی قاجلہ آیا اور اس کے ڈھول تاشوں کی آواز سن کر 12 آدمیوں کے سوا تمام حاضرین مسجد نبوی سے قافلے کی طرف دوڑ گئے ، حالانکہ اس وقت رسول اللہ صلی اللہ علیہ و سلم خطبہ ارشاد فرما رہے تھے۔ اس پر یہ حکم دیا گیا کہ جمعہ کی اذان ہونے کے بعد ہر قسم کی خرید و فروخت اور ہر دوسری مصروفیت حرام ہے۔ اہل ایمان کا کام یہ ہے کہ اس وقت سب کام جھوڑ چھاڑ کر اللہ کے ذکر کی طرف دوڑیں۔ البتہ جب نماز ختم ہو جائے تو انہیں حق ہے کہ اپنے کاروبار چلانے کے لیے زمین میں پھیل جائیں۔ احکام جمعہ کے بارے میں یہ رکوع ایک مستقل سورۃ بھی بنایا جایا سکتا تھا، اور کسی دوسری سورۃ میں بھی شامل کیا جا سکتا تھا۔ لیکن ایسا کرنے کے بجائے خاص طور پر اسے یہاں ان آیات کے ساتھ لا کر ملا گیا جن میں یہودیوںکو ان کے انجام بد کے اسباب پر متنبہ کیا گیا ہے۔ اس کی حکمت ہمارے نزدیک وہی ہے جو اوپر ہم نے بیان کی ہے۔','سید ابو اعلیٰ مودودیؒ -  تفہیم القرآن');
INSERT INTO "chapters_ur" VALUES(63,'منافقین',NULL,' نام: پہلی آیت کے فقرہ اِذَاجَآءَکَ الْمُنٰفِقُوْنَ سے ماخوذ ہے۔ یہ اس سورۃ کا نام بھی ہے اور اس کے مضمون کا عنوان بھی، کیونکہ اس میں منافقین ہی کے طرز عمل پر تبصرہ کیا گیا ہے۔ زمانہ نزول: جیسا کہ ہم آگے چل کر بتائیں گے ، یہ سورۃ غزوہ بنی المُصطَلِق سے رسول اللہ صلی اللہ علیہ و سلم کی واپسی پر یا تو دوران سفر میں نازل ہوئی ہے ، یا حضورؐ کے مدینہ طیبہ پہنچنے کے بعد فوراً ہی اس کا نزول ہوا ہے۔ اور ہم سورہ نور کے دیباچے میں یہ بات بتحقیق بیان کر چکے ہیں کہ غزوہ بنی المصطلق شعبان 6 ھجری میں واقع ہوا تھا۔ اس طرح اس کی تاریخ نزول ٹھیک ٹھیک متعین ہو جاتی ہے۔ تاریخی پس منظر: جس خاص واقعہ کے بارے میں یہ سورۃ نازل ہوئی ہے اس کا ذکر کرنے سے پہلے یہ ضروری ہے کہ مدینے کے منافقین کی تاریخ پر ایک نگاہ ڈال لی جائے ، کیونکہ جو واقعہ اس موقع پر پیش آیا تھا وہ محض ایک اتفاقی حادثہ نہ تھا بلکہ اس کے پیچھے ایک پورا سلسلۂ واقعات تھا جو بالآخر اس نوبت تک پہنچا۔ مدینہ طیبہ میں رسول اللہ صلی اللہ علیہ و سلم کی تشریف آوری سے پہلے اَوس اور خز رج کے قبیلے آپس کی خانہ جنگیوں سے تھک کر ایک شخص کی قیادت و سیادت پر قریب قریب متفق ہو چکے تھے اور اس بات کی تیاریاں کر رہے تھے کہ اس کو اپنا بادشاہ بنا کر باقاعدہ اس کی تاج پوشی کی رسم ادا کر دیں ، حتیٰ کہ اس کے لیے تاج بنا بھی لیا گیا تھا۔ یہ قبیلہ خز رج کا رئیس عبداللہ بن اُبی بن سَلول تھا۔ محمد بن اسحاق کا بیان ہے کہ قبیلہ خز رج میں اس کی بزرگی بالکل متفق علیہ تھی،اور اَوس و خز رج اس سے پہلے کبھی ایک شخص کی قیادت پر جمع نہیں ہوئے تھے (ابن ہشام، ج 2 ص 234)۔ اس صورت حال میں اسلام کا چرچا مدینے پہنچا اور ان دونوں قبیلوں کے با اثر آدمی مسلمان ہونے شروع ہو گئے۔ ہجرت سے پہلے بیعت عَقَبہ ثانیہ کے موقع پر جب رسول اللہ صلی اللہ علیہ و سلم کو مدینہ طیبہ تشریف لانے کی دعوت دی جا رہی تھی اس وقت حضرت عباس بن عبادہ بن نَضْلَۂ انصاری اس دعوت کو صرف اس مصلحت سے مؤخر کرنا چاہتے تھے کہ عبداللہ بن ابی بھی بیعت اور دعوت میں شریک ہو جائے ، تاکہ مدینہ بالاتفاق اسلام کا مرکز بن سکے۔ لیکن جو وفد بیعت کے لیے حاضر ہوا تھا اس نے اس مصلحت کو کوئی اہمیت نہ دی اور اس کے تمام شرکاء جن میں دونوں قبیلوں کے 75 آدمی شامل تھے ، ہر خطرہ مول لے کر حضورؐ کو دعوت دینے کے لیے تیار ہو گئے (ابن ہشام، ج 2، ص 89)۔ اس واقعہ کی تفصیلات ہم سورہ انفال کے دیباچے میں بیان کر چکے ہیں۔ اس کے بعد جب حضورؐ مدینے پہنچے تو انصار کے ہر گھرانے میں اسلام اتنا پھیل چکا تھا کہ عبداللہ بن ابی بے بس ہو گیا اور اس کو اپنی سرداری بچانے کی اس کے سوا کوئی صورت نظر نہ آئی کہ خود بھی مسلمان ہو جائے۔ چنانچہ وہ اپنے ان بہت سے حامیوں کے ساتھ، جن میں دونوں قبیلوں کے شیوخ اور سردار شامل تھے ، داخل اسلام ہو گیا، حالانکہ دل ان سب کے جل رہے تھے ، اور خاص طور پر ابن ابی کو اس بات کا سخت غم تھا کہ رسول اللہ صلی اللہ علیہ و سلم نے اس کی بادشاہی چھین لی ہے۔ کئی سال تک اس کا یہ منافقانہ ایمان اور اپنی ریاست چھن جانے کا یہ غم طرح طرح کے رنگ دکھاتا رہا۔ ایک طرف حال یہ تھا کہ ہر جمعہ کو جب رسول اللہ صلی اللہ علیہ و سلم خطبہ ارشاد فرمانے کے لیے بیٹھتے تو عبداللہ بن ابی اٹھ کر کہتا کہ ’’ حضرت، یہ اللہ کے رسول آپ کے درمیان موجود ہیں جن کی ذات سے اللہ نے آپ کو عزت اور شرف بخشا ہے ، لہٰذا آپ ان کی تائید کریں اور جو کچھ یہ فرماتے ہیں اسے غور سے سنیں اور ان کی اطاعت کریں ‘‘ (ابن ہشام، ج 3،ص 111)۔ دوسری طرف کیفیت یہ تھی کہ روز بروز اس کی منافقت کا پردہ چاک ہوتا چلا جا رہا تھا اور مخلص مسلمانوں پر یہ بات کھلتی چلی جاتی تھی کہ وہ اس کے ساتھی اسلام اور رسول اللہ صلی اللہ علیہ و سلم اور گروہ اہل ایمان سے سخت بغض رکھتے ہیں۔ ایک مرتبہ حضورؐ کسی راستے سے گزر رہے تھے کہ ابن ابی نے آپ کے ساتھ بد تمیزی کی۔ آپ نے حضرت سعد بن عبادہ سے اس کی شکایت فرائی تو انہوں نے عرض کیا، ’’ یا رسول اللہ، اس شخص کے ساتھ نرمی برتیے آپ کی تشریف آوری سے پہلے ہم اس کے لیے تاج شاہی تیار کر رہے تھے ، اب یہ سمجھتا ہے کہ آپ نے اس سے بادشاہی چھین لی ہے ‘‘ (ابن ہشام، ج2، ص 237۔ 238 )۔ جنگ بدر کے بعد جب یہود نبی قینقاع کی صریح بد عہدی اور بلا اشتعال سرکشی پر رسول اللہ صلی اللہ علیہ و سلم نے ان پر چڑھائی کی تو یہ شخص ان کی حمایت پر اٹھ کھڑا ہوا اور حضورؐ کی زرہ پکڑ کر کہنے لگا کہ ’’یہ سات سو مردان جنگی، جو ہر دشمن کے مقابلے میں میرا ساتھ دیتے رہے ہیں ، آج ایک دن میں آپ انہیں ختم کر ڈالنا چاہتے ہیں ؟ خدا کی قَسم، میں آپ کو ہر گز نہیں چھوڑوں گا جب تک آپ میرے ان حلیفوں کو معاف نہ کر دیں گے ‘‘ (ابن ہشام، ج 3، ص 51۔52)۔ جنگ احد کے موقع پر اس شخص نے صریح غداری کی اور عین وقت پر اپنے تین سو ساتھیوں کو لے کر میدان جنگ سے الٹا واپس آ گیا۔ جس نازک گھڑی میں اس نے یہ حرکت کی تھی اس کی نزاکت کا اندازہ اس بات سے کیا جا سکتا ہے کہ قریش کے لوگ تین ہزار کا لشکر لے کر مدینے پر چڑھ آئے تھے ، اور رسول اللہ صلی اللہ علیہ و سلم ان کے مقابلے میں صرف ایک ہزار آدمی ساتھ لے کر مدافعت کے لیے نکلے تھے۔ ان ایک ہزار میں سے بھی یہ منافق تین سو آدمی توڑ لایا اور حضورصلى الله عليه وسلم کو صرف سات سو کی جمعیت کے ساتھ تین ہزار دشمنوں کا مقابلہ کرنا پڑا۔ اس واقعہ کے بعد مدینے کے عام مسلمانوں کو یقین کے ساتھ یہ معلوم ہو گیا کہ یہ شخص قطعی منافق ہے ، اور اس کے وہ ساتھی بھی پہچان لیے گئے جو منافقت میں اس کے شریک کار تھے۔ اسی بنا پر جنگ احد کے بعد جب پہلا جمعہ آیا اور یہ شخص حضورؐ کے خطبہ سے پہلے حسب معمول تقریر کرنے کے لیے اٹھا تو لوگوں نے اس کا دامن کھینچ کر کہا ’’ بیٹھ جاؤ، تم یہ باتیں کرنے کے اہل نہیں ہو‘‘۔ مدینے میں یہ پہلا موقع تھا کہ علانیہ اس شخص کی تذلیل کی گئی۔ اس پر برہم ہو کر وہ لوگوں کی گردنوں پر سے کودتا پھاندتا مسجد سے نکل گیا۔ مسجد کے دروازے پر بعض انصاریوں نے اس سے کہا ’’ یہ کیا حرکت کر رہے ہو، واپس چلو اور رسول اللہ صلی اللہ علیہ و سلم سے استغفار کی درخواست کرو‘‘۔ اس نے بگڑ کر جواب دیا ’’ میں ان سے کوئی استغفار نہیں کرانا چاہتا‘‘(ابن ہشام، ج 3، ص 111)۔ پھر 4 ہجری میں غزوہ بنی النضیر پیش آیا اور اس موقع پر اس شخص نے اور اس کے ساتھیوں نے اور بھی زیادہ کھل کر اسلام کے خلاف اعدائے اسلام کی حمایت کی۔ ایک طرف رسول اللہ صلی اللہ علیہ و سلم اور آپ کے جاں نثار صحابہ ان یہودی دشمنوں سے جنگ کی تیاری کر رہے تھے ، اور دوسری طرف یہ منافقین اندر ہی اندر یہودیوں کو پیغام بھیج رہے تھے کہ ڈٹے رہو، ہم تمہارے ساتھ ہیں ، تم سے جنگ کی جائے گی تو ہم تمہاری مدد کریں گے اور تم کو نکالا جائے گا تو ہم تمہارے ساتھ نکلیں گے۔ اس خفیہ ساز باز کا راز اللہ تعالیٰ نے خود کھول دیا، جیسا کہ سورہ حشر کے دوسرے رکوع میں گزر چکا ہے۔ لیکن اس کی اور اس کے ساتھیوں کی اتنی پردہ دری ہو جانے کے باوجود جس وجہ سے رسول اللہ صلی اللہ علیہ و سلم اس کے ساتھ در گزر کا معاملہ فرما رہے تھے وہ یہ تھی کہ منافقین کا ایک بڑا جتھا اس کے ساتھ تھا۔ اَوس اور خز رج دونوں قبیلوں کے بہت سے سردار اس کے حامی تھے۔ مدینے کی آبادی میں کم از کم ایک تہائی تعداد اس کے ساتھیوں کی موجود تھی، جیسا کہ غزوہ احد کے موقع پر ظاہر ہو چکا تھا۔ ایسی حالت میں یہ کسی طرح مناسب نہ تھا کہ باہر کے دشمنوں سے لڑائی کے ساتھ ساتھ اندر کے ان دشمنوں سے بھی جنگ مول لے لی جاتی۔ اسی بنا پر ان کی منافقت کا حال جانتے ہوئے بھی حضورؐ ایک مدت تک ان کے ساتھ ان کے ظاہری دعوائے ایمان کے لحاظ سے معاملہ فرماتے رہے۔ دوسری طرف یہ لوگ بھی نہ اتنی طاقت رکھتے تھے ، نہ ہمت کہ علانیہ کافر بن کر اہل ایمان سے لڑ لیتے ، یا کسی حملہ آور دشمن کے ساتھ کھلم کھلا مل کر میدان میں آ جاتے۔ بظاہر وہ اپنا ایک مضبوط جتھا بنائے ہوئے تھے مگر ان کے اندر وہ کمزور یاں موجود تھیں جن کا نقشہ اللہ تعالیٰ نے سورہ حشر کی آیات 12۔ 14 میں صاف صاف کھینچ کر رکھ دیا ہے۔ اس لیے وہ مسلمان بنے رہنے میں ہی اپنی خیر سمجھتے تھے۔ مسجدوں میں آتے تھے۔ نمازیں پڑھتے تھے۔ زکوٰۃ بھی دے ڈالتے تھے۔ زبان سے ایمان کے وہ لمبے چوڑے دعوے کرتے تھے جن کے کرنے کی مخلص مسلمانوں کو کبھی ضرورت پیش نہ آ تی تھی۔ ان کے پاس اپنی ہر منافقانہ حرکت کے لیے ہزار جھوٹی توجہیں موجود تھیں جن سے وہ خاص طور پر اپنے ہم قبیلہ انصار کو یہ دھو کا دینے کی کوشش کرتے رہتے تھے کہ ہم تمہارے ساتھ ہیں۔ ان تدبیروں سے وہ اپنے آپ کو ان نقصانات سے بھی بچا رہے تھے جو انصار کی برادری سے الگ ہو جانے کی صورت میں ان کو پہنچ سکتے تھے ، اور فتنہ پروازی کے ان مواقع سے بھی فائدہ اٹھا رہے تھے جو اس برادری میں شامل رہ کر انہیں مل سکتے تھے۔ یہی وہ اسباب تھے جن کی وجہ سے عبداللہ بنا ابی اور اس کے ساتھی منافقین کو غزوہ بنی المصْطَلِق کی مہم میں رسول اللہ صلی اللہ علیہ و سلم کے ساتھ جانے کا موقع مل گیا، اور انہوں نے بیک وقت دو ایسے عظیم فتنے اٹھا دیے جو مسلمانوں کی جمعیت کو بالکل پارہ پارہ کر سکتے تھے۔ مگر قرآن پاک کی تعلیم اور رسول اللہ صلی اللہ علیہ و سلم کی صحبت سے اہل ایمان کو جو بہترین تربیت ملی تھی اس کی بدولت ان دونوں فتنوں کا بر وقت قلع قمع ہو گیا اور یہ منافقین الٹے خود ہی رسوا ہو کر رہ گئے۔ ان میں سے ایک فتنہ وہ تھا جس کا ذکر سورہ نور میں گزر چکا ہے۔ اور دوسرا فتنہ یہ ہے کہ جس کا اس سورہ میں ذکر کیا گیا ہے۔ اس واقعہ کو بخاری، مسلم، احمد، نسائی، ترمذی، بیہقی، طبرانی، ابن مردویہ، عبدالرزاق، ابن جریر طبری، ابن سعد اور محمد بن اسحاق نے بکثرت سندوں سے نقل کیا ہے۔ بعض روایات میں اس مہم کا نام نہیں لیا گیا ہے جس میں یہ پیش آیا تھا: اور بعض میں اسے غزوہ تبوک کا واقعہ بیان کیا گیا ہے۔ مگر مغازی اور سیر کے علماء اس بات پر متفق ہیں کہ یہ واقعہ غزوہ بنی المصطلق کے موقع پر پیش آیا تھا۔ صورت واقعہ تمام روایات کو جمع کرنے سے یہ معلوم ہوتی ہے : بنی المصطلق کو شکست دینے کے بعد ابھی لشکر اسلام اس بستی میں ٹھیرا ہوا تھا جو مریسبع نامی کنویں پر آباد تھی کہ یکایک پانی پر دو صاحبوں کا جھگڑا ہو گیا۔ ان میں سے ایک کا نام جہجاہ بن مسعود غِفاری تھا جو حضرت عمرؓ کے ملازم تھے اور ان کا گھوڑا سنبھالنے کی خدمت انجام دیتے تھے۔ اور دوسرے صاحب سنان بن وَبَرالجہنی ؎ تھے جن کا قبیلہ خز رج کے ایک قبیلے کا حلیف تھا۔ زبانی ترش کلامی سے گزر کر نوبت ہاتھا پائی تک پہنچی اور جہجاہ نے سنان کے ایک لات رسید کر دی جسے اپنی قدیم یمنی روایات کی بنا پر انصار سخت توہین و تذلیل سمجھتے تھے۔ اس پر سنان نے انصار کو مدد کے لیے پکارا، اور جہجاہ نے مہاجرین کو آواز دی۔ ابن ابی نے اس جھگڑے کی خبر سنتے ہیں اَوس اور خز رج کے لوگوں کو بھڑکانا اور چیخنا شروع کر دیا کہ دوڑو اور اپنے حلیف کی مدد کرو۔ ادھر سے کچھ مہاجرین بھی نکل آئے قریب تھا کہ بات بڑھ جاتی اور اسی جگہ انصار و مہاجرین آپس میں لڑ پڑتے جہاں ابھی ابھی وہ مل کر ایک دشمن قبیلے سے لڑے تھے اور اسے شکست دے کر ابھی اسی کے علاقے میں ٹھیرے ہوئے تھے۔ لیکن یہ شور سن کر رسول اللہ صلی اللہ علیہ و سلم نکل آئے اور آپ نے فرمایا : ما بالُ دعوی الجاھلیۃ؟ مالکم ولدعوۃ الجاھلیۃ؟ دعوھا نانھا مُنْتِنَۃ‘‘۔ یہ جاہلیت کی پکار کیسی؟ تم لوگ کہاں اور یہ جاہلیت کی پکار کہاں ؟ اسے چھوڑ دو، یہ بڑی گندی چیز ہے ‘‘۔ ؎ اس پر دونوں طرف کے صالح لوگوں نے آگے بڑھ کر معاملہ رفع دفع کا دیا اور سِنان نے جہجاہ کو معاف کر کے صلح کر لی۔ اس کے بعد ہر وہ شخص جس کے دل میں نفاق تھا عبداللہ بن ابی کے پاس پہنچا اور ان لوگوں نے جمع ہو کر اس سے کہا کہ ’’اب تک تو تم سے امیدیں وابستہ تھیں اور تم مدافعت کر رہے تھے ، مگر اب معلوم ہوتا ہے کہ تم ہمارے مقابلے میں ان کنگلوں ۳؎ کے مدد گار بن گئے ہو ‘‘۔ ابن ابی پہلے ہی کھول رہا تھا۔ ان باتوں سے وہ اور بھی زیادہ بھڑک اٹھا کہنے لگا ’’ یہ سب کچھ تمہارا اپنا ہی کیا دھرا ہے۔ تم نے ان لوگوں کو اپنے ملک میں جگہ دی، ان پر اپنے مال تقسیم کیے ، یہاں تک کہ اب یہ پھل پھول کر خود ہمارے ہی حریف بن گئے۔ ہماری اور ان قریش کے کنگلوں (یا اصحاب محمدؐ ) کی حالت پر یہ مثل صادق آتی ہے کہ اپنے کتے کو کھلا پلا کر موٹا کرتا کہ تجھی کو پھاڑ کھائے۔ تم لوگ ان سے ہاتھ روک لو تو یہ چلتے پھرتے نظر آئیں۔ خدا کی قسم، مدینے واپس پہنچ کر ہم میں سے جو عزت والا بنے وہ ذلیل کو نکال دے گا‘‘۔ مجلس میں اتفاق سے حضرت زید بن ارقم بھی موجود تھے جو اس وقت ایک کم عمر لڑکے تھے۔ انہوں نے یہ باتیں سن کر اپنے چچا سے ان کا ذکر کی، اور ان کے چچا نے جو انصار کے رئیسوں میں سے تھے ، جا کر رسول اللہ صلی اللہ علیہ و سلم کی خدمت میں سارا واقعہ بیان کردیا۔ حضرت نے زید کو بلا کر دریافت کیا تو انہوں نے جو کچھ سنا تھا من و عن دہرا دیا۔ ۴؎ حضورؐ نے فرمایا شاید تم ابن ابی سے ناراض ہو۔ ممکن ہے تم سے سننے میں کچھ غلطی ہو گئی ہو۔ ممکن ہے تمہیں شبہ ہو گیا ہو کہ ابن ابی یہ کہہ رہا ہے۔ مگر زید نے عرض کیا نہیں حضور، خدا کی قسم میں نے اس کو یہ باتیں کہتے سنا ہے۔ اس پر حضورؐ نے ابن ابی کو بلا کر پوچھا تو وہ صاف مکر گیا اور قسمیں کھانے لگا کہ میں نے یہ باتیں ہر گز نہیں کہیں۔ انصار کے لوگوں نے بھی کہا کہ حضورؐ، لڑکے کی بات ہے۔ شاید اسے وہم ہو گیا ہو۔ یہ ہمارا شیخ اور بزرگ ہے۔ اس کے مقابلے میں ایک لڑکے کی بات کا اعتبار نہ فرمایئے۔ قبیلے کے بڑے بوڑھوں نے زید کو بھی ملامت کی اور وہ بیچارے رنجیدہ ہو کر اپنی جگہ بیٹھ رہے۔ مگر حضورؐ زید کو بھی جانتے تھے اور عبداللہ بن ابی کو بھی، اس لیے آپ سمجھ گئے کہ اصل بات کیا ہے۔ حضرت عمرؓ کو اس کا علم ہوا تو انہوں نے آ کر عرض کیا ’’ مجھے اجازت دیجیے کہ اس منافق کی گردن اڑا دوں۔ یا اگر مجھے یہ جازت دینا مناسب خیال نہیں فرماتے تو خود انصار ہی میں سے معاذ بن جبل، یا عبّاد بن بشر، یا سعد بن معاذ، یا محمد بن مسلمہ کو حکم دیجیے ۵؎ ۔ کہ وہ اسے قتل کر دیں ‘‘۔ مگر حضورؐ نے فرمایا، ’’ ایسا نہ کرو، لوگ کہیں گے کہ محمدؐ کے معمول کے لحاظ سے وہ کوچ کا وقت نہ تھا۔ مسلسل 30 گھنٹے چلتے رہے یہاں تک کہ لوگ تھک کر چور ہو گئے۔ پھر آپ نے ایک جگہ پڑاؤ کیا اور تھکے ہوئے لوگ زمین پر کمر ٹکاتے ہی سو گئے۔ یہ آپ نے اس لیے کیا کہ جو کچھ مُرَیْسیْع کے کنوئیں پر پیش آیا تھا اس کے اثرات لوگوں کے ذہن سے محو ہو جائیں۔ راستے میں انصار کے ایک سردار حضرت اُسید بن حضیر آپ سے ملے اور عرض کیا ’’ یا رسول اللہ، آج آپ نے ایسے وقت کوچ کا حکم دیا جو سفر کے لیے موزوں نہ تھا اور آپ کبھی ایسے وقت میں سفر کا آغاز نہیں فرمایا کرتے تھے ‘‘؟ حضورؐ نے جواب دیا، ’’ تم نے سنا نہیں کہ تمہارے ان صاحب نے کیا گوہر افشانی کی ہے ؟’’ انہوں نے پوچھا کون صاحب؟ فرمایا عبداللہ بن ابی۔ انہوں نے پوچھا اس نے کیا کہا؟ فرمایا ’’ اس نے کہا ہے کہ مدینہ پہنچ کر عزت والا ذلیل کو نکال باہر کرے گا ‘‘۔ انہوں نے عرض کیا، ’’ یا رسول اللہ، خدا کی قسم، عزت والے تو آپ ہیں اور ذلیل وہ ہے ، آپ جب چاہیں اسے نکال سکتے ہیں ‘‘۔ رفتہ رفتہ یہ بات تمام انصار میں پھیل گئی اور ان میں ابن ابی کے خلاف سخت غصہ پیدا ہو گیا۔ لوگوں نے ابن ابی سے کہا جا رک رسول اللہ صلی اللہ علیہ و سلم سے معافی مانگو۔مگر اس نے تڑخ کر جواب دیا ’’ تم نے کہا کہ ان پر ایمان لاؤ۔ میں ایمان لے آیا تم جے کہا کہ اپنے مال کی زکوٰۃ دو۔ میں نے زکوٰۃ بھی دے دی۔ اب بس یہ کسر رہ گئی ہے کہ میں محمدؐ کو سجدہ کروں ‘‘۔ ان باتوں سے اس کے خلاف مومنین انصار کی ناراضی اور زیادہ بڑھ گئی اور ہر طرف سے اس پر پھٹکار پڑنے لگی۔ جب یہ قافلہ مدینہ طیبہ میں داخل ہونے لگا تو عبداللہ بن ابی کے صاحبزادے ، بن کا نام بھی عبداللہ ہی تھا، تلوار سونت کر باپ کے آگے کھڑے ہو گئے اور بولے ،’’ آپ نے کہا تھا کہ مدینہ واپس پہنچ کر عزت والا ذیل کو نکال دے گا، اب آپ کو معلوم ہو جائے گا کہ عزت آپ کی ہے یا اللہ اور اس کے رسولؐ کی۔ خدا کی قسم، آپ مدینہ میں داخل نہیں ہو سکتے جب تک رسول اللہ صلی اللہ علیہ و سلم آپ کو اجازت نہ دیں ’’۔ اس پر ابن ابی چیخ اٹھا،’’ خز رج کے لوگو ! ذرا دیکھو، میرا بیٹا ہی مجھے مدینہ میں داخل ہونے سے روک رہا ہے ‘‘۔ لوگوں نے یہ خبر حضورؐ تک پہنچائی اور آپ نے فرمایا ’’ عبداللہ سے کہو، اپنے باپ کو گھر آنے دے ‘‘۔ عبد اللہؓ نے کہا ’’ ان کا حکم ہے تو اب آپ داخل ہو سکتے ہیں ‘‘۔ اس وقت حضورؐ نے حضرت عمرؓ سے فرمایا، ’’ کیوں عم، اب تمہارا کیا خیال ہے ؟ جس وقت تم نے کہا تھا کہ مجھے اس کو قتل کرنے کی اجازت دیجیے اس وقت اگر تم اسے قتل کر دیتے تو بہت سی ناکیں اس پر پھڑ کنے لگتیں۔ آج اگر میں اس کے قتل کا حکم دوں تو اسے قتل تک کیا جا سکتا ہے ‘‘۔ حضرت عمرؓ نے عرض کیا، ’’ خدا کی قسم اب مجھے معلوم ہو گیا کہ اللہ کے رسول کی بات میری بات سے زیادہ مبنی بر حکمت تھی‘‘۔ ۶؎ یہ تھے وہ حالات جن میں یہ سورت، اغلب یہ ہے کہ حضورؐ کے مدینہ پہنچنے کے بعد نازل ہوئی۔ ۱؎ دونوں اصحاب کے نام مختلف روایات میں مختلف بیان کیے گئے ہیں۔ ہم نے یہ نام ابن ہشام کی روایت سے لیے ہیں واپس ۲؎ یہ ایک بڑی اہم بات ہے جو اس موقع پر حضورؐ نے ارشاد فرمائی۔ اسلام کی صحیح روح کو سمجھنے کے لیے اسے ٹھیک ٹھیک سمجھ لینا ضروری ہے۔ اسلام کا طریقہ یہ ہے کہ دو آدمی اگر اپنے جھگڑے میں لوگوں کو مدد کے لیے پکارنا چاہیں تو وہ کہیں ، مسلمانو، آؤ اور ہماری مدد کرو، یا یہ کہ لوگو ہماری مدد کے لیے پکارنا چاہیں تو وہ کہیں ، مسلمانو، آؤ اور ہماری مدد کرو، یا یہ کہ لوگو ہماری مدد کے لیے آؤ۔ لیکن اگر ان میں سے ہر ایک اپنے اپنے قبیلے ، یا برادری، یا نسل و رنگ، یا علاقے کے نام پر لوگوں کو پکارتا ہے تو یہ جاہلیت کی پکار ہے ، اور اس پکار پر لبیک کہہ کر آنے والے اگر یہ نہیں دیکھتے کہ ظالم کون ہے اور مظلوم کون، اور حق و انصاف کی بنا پر مظلوم کی حمایت کرنے کے بجائے اپنے اپنے گروہ کے آدمی کی حمایت میں ایک دوسرے سے بر سر پیکار ہو جاتے ہیں تو یہ جاہلیت کا فعل ہے جس سے دنیا میں فساد برپا ہوتا ہے۔ اسی لیے رسول اللہ صلی اللہ علیہ و سلم نے اسے گندی اور گھناؤنی چیز قرار دیا اور مسلمانوں سے فرمایا کہ تمہارا اس جاہلیت کی پکار سے کیا واسطہ؟ تم اسلام کی بنیاد پر ایک ملت بنے تھے ، اب یہ انصار اور مہاجر کے نام پر تمہیں کیسے پکارا جا رہا ہے ، اور اس پکار پر تم کہاں دوڑے جا رہے ہو؟ علامہ سُہیلی نے روض الانف میں لکھا ہے کہ فقہائے اسلام نے کسی جھگڑے یا اختلاف میں جاہلیت کی پکار بلند کرنے کو ایک فوج داری جرم قرار دیا ہے۔ ایک گروہ اس کی سزا پچاس ضرب تازیانہ قرار دیتا ہے۔ دوسرا گروہ دس ضرب تجویز کرتا ہے ، اور تیسرا گروہ کہتا ہے کہ اس کی سزا حالات کی مناسبت سے دی جانی چاہیے۔ بعض حالات میں صرف زجر و توبیخ کافی ہے ، بعض دوسرے حالات میں ایسی پکار بلند کرنے والے کو قید کرنا چاہیے ، اور اگر یہ زیادہ شر انگیز ہو تو اس کے مرتکب کو سزائے تازیانہ دینی چاہیے۔ واپس ۳؎ مدینہ کے منافقین ان تمام لوگوں کو جز اسلام قبول کر کر کے مدینہ میں آ رہے تھے ، ’’جلابیب‘‘ کہا کرتے تھے۔ لغوی معنی تو اس لفظ کے گلیم پوش یا موٹے جھوٹے کپڑے پہننے والے کے ہیں ، مگر اصل مفہوم جس میں وہ لوگ غریب مہاجرین کی تذلیل کے لیے یہ لفظ استعمال کرتے تھے ، کنگلے کے لفظ سے زیادہ صحیح طور پر ادا ہوتا ہے۔ واپس ۴؎ فقہاء نے اس سے یہ حکم اخذ کیا ہے کہ ایک شخص کی بری بات دوسرے شخص تک پہنچانا اگر کسی دینی، اخلاقی یا ملی مصلحت کے لیے ہو تو یہ چغلی کی تعریف میں نہیں آتا۔ شریعت میں جس چغل خوری کو حرام کیا گیا ہے وہ فساد کی غرض سے اور لوگوں کو آپس میں لڑانے کے لیے چغلی کھانا ہے۔ واپس ۵؎ مختلف روایات انصاری بزرگوں کے نام آئے ہیں جن کے متعلق حضرت عمرؓ نے عرض کیا تھا کہ آپ ان میں سے کسی شخص سے یہ خدمت لے لیں اگر مجھ سے اس لیے یہ کام لینا مناسب خیال نہیں فرماتے کہ میں مہاجر ہوں ، میرے ہاتھوں اس کے مارے جانے سے فتنے بھڑک اٹھنے کا امکان ہے واپس ۶؎ اس سے دو اہم شرعی مسئلوں پر روشنی پڑتی ہے۔ ایک یہ کہ جو طرز عمل ابن بی نے اختیار کیا تھا، اگر کوئی شخص مسلم ملت میں رہتے ہوئے اس طرح کا رویہ اختیار کرے تو وہ قتل کا مستحق ہے۔ دوسرے یہ کہ محض قانوناً کسی شخص کے مستحق قتل ہو جانے سے یہ لازم نہیں آتا کہ ضرور اسے قتل ہی کر دیا جائے۔ ایسے کسی فیصلے سے پہلے یہ دیکھنا چاہیے کہ آیا اس کا قتل کسی عظیم تر فتنے کا موجب تو نہ بن جائے گا۔ حالات سے آنکھیں بن کر کے قانون کا اندھا دھند استعمال بعض اوقات اس مقصد کے خلاف بالکل اُلٹا نتیجہ پیدا کر دیتا ہے جس کے لیے قانون استعمال کیا جاتا ہے۔ اگر ایک منافق اور مفسد آدمی کے پیچھے کوئی قابل لحاظ سیاسی طاقت موجود ہو تو اسے سزا دے کر مزید فتنوں کو سر اٹھانے کا موقع دینے سے بہتر یہ ہے کہ حکمت اور تدبر کے ساتھ اس اصل سیاسی طاقت کا استیصال کر دیا جائے جس کے بل پر وہ شرارت کر رہا ہو۔ یہی مصلحت تھے جس کی بنا پر حضورؐ نے عبداللہ بنا بی کو اس وقت بھی سزا نہ دی جب آپ اسے سزا دینے پر قادر تھے ، بلکہ اس کے ساتھ برابر نرمی کا سلوک کرتے رہے ، یہاں تک کہ دو تین سال کے اندر مدینہ میں منافقین کا زور ہمیشہ کے لیے ٹوٹ گیا۔ واپس ','سید ابو اعلیٰ مودودیؒ -  تفہیم القرآن');
INSERT INTO "chapters_ur" VALUES(64,'ہار جیت',NULL,' نام: آیت نمبر 9 کے فقرے ذٰلِکَ یَوْمُ التَّغَابُن سے ماخوذ ہے۔ یعنی وہ سورۃ جس میں لفظ تغابن آیا ہے۔ زمانۂ نزول: مقاتل اور کَلْبی کہتے ہیں کہ اس کا کچھ حصہ مکی ہے اور کچھ مدنی۔ حضرت عبداللہ بن عباس اور عطاء بن یسار کہتے ہیں کہ ابتدا سے آیت 13 تک مکی ہے اور آیت 14 سے آخر سورۃ تک مدنی۔ مگر مفسرین کی اکثریت پوری سورۃ کو مدنی قرار دیتی ہے۔ اگر چہ اسمیں کوئی اشارہ ایسا نہیں پایا جاتا جس سے اس کا زمانہ نزول متعین کیا جاسکتا ہو، لیکن مضمون کلام پر غور کرنے سے اندازہ ہوتا ہے کہ غالباً یہ مدینہ طیبہ کے ابتدائی دور میں نازل ہوئی ہو گی۔ اسی وجہ سے اس میں کچھ رنگ مکی سورتوں کا سا اور کچھ مدنی سورتوں کا سا پایا جاتا ہے۔ موضوع اور مضمون: اس سورہ کا موضوع ایمان و طاعت کی دعوت اور اخلاق حسنہ کی تعلیم ہے۔ کلام کی ترتیب یہ ہے کہ پہلی چار آیتوں کا خطاب تمام انسانوں سے ہے ، پھر آیت 5 سے 10 تک ان لوگوں سے خطاب کیا گیا ہے جو قرآن کی دعوت کو نہیں مانتے ، اور اس کے بعد نمبر 11 سے آخر تک کی آیات کا روئے سخن ان لوگوں کی طرف ہے جو اس دعوت کو مانتے ہیں۔ تمام انسانوں کو خطاب کر کے چند مختصر فقروں میں انہیں چار بنیادی حقیقتوں سے آگاہ کیا گیا ہے : اول یہ کہ یہ کائنات، جس میں تم رہتے ہو، بے خدا نہیں ہے بلکہ اس کا خالق اور مالک اور فرمانروا ایک ایسا قادر مطلق خدا ہے جس کے کامل اور بے عیب ہونے کی شہادت اس کائنات کی ہر چیز دے رہی ہے۔ دوسرے یہ کہ یہ کائنات بے مقصد اور بے حکمت نہیں ہے بلکہ اس کے خالق نے اسے سراسر بر حق پیدا کیا ہے۔ یہاں اس غلط فہمی میں نہ رہو کہ یہ ایک فضول تماشا ہے جو عبث ہی شروع ہوا اور عبث ہی ختم ہو جائے گا۔ تیسرے یہ کہ تمہیں جس بہترین صورت کے ساتھ خدا نے پیدا کیا ہے اور پھر جسطرح کفر و ایمان کا اختیار تم پر چھوڑ دیا ہے ، یہ کوئی لا حاصل اور لایعنی کام نہیں ہے کہ تم خواہ کفر اختیار کرو یا ایمان، دونوں صورتوں میں اس کا کوئی نتیجہ بر آمد نہ ہو۔ در اصل خدا یہ دیکھ رہا ہے کہ تم اپنے اس اختیار کوکس طرح استعمال کرتے ہو۔ چوتھے یہ کہ تم غیر ذمہ دار اور غیر جواب دہ نہیں ہو۔ آخر کار تمہیں اپنے خالق کی طرف پلٹ کر جانا ہے اور اس ہستی سے تمہیں سابقہ پیش آنا ہے جو کائنات کی ہر چیز سے واقف ہے ، جس سے تمہاری کوئی بات پوشیدہ نہیں ، جس پر دلوں کے چھپے ہوئے خیالات تک روشن ہیں۔ کائنات اور انسان کی حقیقت کے بارے میں یہ چار بنیادی باتیں بیان کرنے کے بعد کلام کا رخ ان لوگوں کی طرف مڑتا ہے جنہوں نے کفر کی راہ اختیار کی ہے ، اور انہیں تاریخ کے اس منظر کی طرف توجہ دلائی جاتی ہے جو پوری انسانی تاریخ میں مسلسل نظر آتا ہے کہ قوموں پر قومیں اٹھتی ہیں اور بالآخر تباہی سے دوچار ہوتی ہیں۔ انسان اپنی عقل سے اس منظر کی ہزار توجہیں کرتا رہا ہے ، مگر اللہ تعالیٰ اصل حقیقت بتاتا ہے اور وہ یہ ہے کہ قوموں کی تباہی کے بنیادی اسباب صرف دو تھے : ایک یہ کہ اس نے جن رسولوں کو ان کی ہدایت کے لیے بھیجا تھا، ان کی بات ماننے سے انہوں نے انکار کیا۔ اس کا نتیجہ یہ ہوا کہ اللہ نے بھی انہیں ان کے حال پر چھوڑ دیا اور وہ خود ہی اپنے فلسفے گھڑ گھڑ کر ایک گمراہی سے دوسری گمراہی میں بھٹکتی چلی گئیں۔ دوسرے یہ کہ انہوں نے آخرت کے عقیدے کو بھی رد کر دیا اور اپنے زعم میں یہ سمجھ لیا کہ جو کچھ ہے جس یہی دنیا کی زندگی ہے ، اس کے بعد کوئی اور زندگی نہیں ہے جس میں ہی اپنے خدا کے سامنے اپنے اعمال کا جواب دینا ہو۔ اس چیز نے ان کے پورے رویہ زندگی کو بگاڑ کر رکھ دیا اور ان کے اخلاق و کردار کی گندگی اس حد تک بڑھتی چلی گئی کہ آخر کار خدا کے عذاب ہی نے آ کر دنیا کو ان کے وجود سے پاک کیا۔ تاریخ انسانی کے یہ دو سبق آموز حقائق بیان کر کے منکرین حق کو دعوت دی جاتی ہے کہ وہ ہوش میں آئیں اور اگر پچھلی قوموں کا سا انجام نہیں دیکھنا چاہتے تو اللہ اور اس کے رسول اور اس نور ہدایت پر ایمان لے آئیں جو اللہ نے قرآن مجید کی صورت میں نازل فرمایا ہے۔ اس کے ساتھ ان کو خبردار کیا جاتا ہے کہ آخر کار وہ دن آنے والا ہے جب تمام اولین و آخرین ایک جگہ جمع کیے جائیں گے اور تم میں سے ہر ایک کا غبن سب کے سامنے کھل جائےگا۔ پھر ہمیشہ ہمیشہ کے لیے تمام انسانوں کی قسمت کا فیصلہ اسی بنیاد پر کیا جائے گا کہ ایمان و عمل صالح کی راہ کس نے اختیار کی تھی، اور کفر و تکذیب کی رہ پر کون چلا تھا۔ پہلا گروہ ابدی جنت کا حق دار ہو گا اور دوسرے گروہ کے حصے میں دائمی جہنم آئے گی۔ اس کے بعد ایمان کی راہ اختیار کرنے والوں کو مخاطب کر کے چند اہم ہدایات انہیں دی جاتی ہیں : ایک یہ کہ دنیا میں جو مصیبت بھی آتی ہے ، اللہ کے اذن سے آتی ہے۔ ایسے حالات میں جو شخص ایمان پر ثابت قدم رہے ، اللہ اس کے دل کو ہدایت بخشتا ہے ، ورنہ گھبراہٹ یا جھنجلاہٹ میں مبتلا ہو کر جو آدمی ایمان کی راہ سے ہٹ جائے، اس کی مصیبت تو اللہ کے اذن کے بغیر دور نہیں ہو سکتی، البتہ وہ ایک اور مصیبت، جو سب سے بڑی مصیبت ہے ، مول لے لیتا ہے اور وہ یہ ہے کہ اس کا دل اللہ کی ہدایت سے محروم ہو جاتا ہے۔ '' دوسرے یہ کہ مومن کا کام صرف ایمان لے آنا ہی نہیں ہے بلکہ ایمان لانے کے بعد اسے عملاً اللہ اور اس کے رسول کی اطاعت کرنی چاہیے۔ اطاعت سے اگر وہ رو گردانی اختیار کرے گا تو اپنے نقصان کا وہ خود ذمہ دار ہو گا۔ کیونکہ رسول اللہ صلی اللہ علیہ و سلم حق پہنچا کر بری الذمہ ہو چکے ہیں۔ تیسرے یہ کہ مومن کا اعتماد اپنی طاقت یا دنیا کی کسی طاقت پر نہیں بلکہ صرف اللہ پر ہونا چاہیے۔ چوتھے یہ کہ مومن کے لیے اس کا مال اور اس کے اہل و عیال ایک بہت بڑی آزمائش ہیں کیونکہ زیادہ تر انہی کی محبت انسان کو ایمان و طاعت کی راہ سے منحرف کرتی ہے۔ اس لیے اہل ایمان کو اپنے اہل و عیال سے ہوشیار رہنا چاہیے کہ وہ بالواسطہ یا بلا واسطہ ان کے حق میں راہ خدا کے رہزن نہ بننے پائیں ، اور انہیں اپنا مال خدا کی راہ میں خرچ کرنا چاہیے تاکہ ان کا جنس زر پر ستی کے فتنوں سے محفوظ رہے۔ پانچویں یہ کہ ہر انسان اپنی استطاعت کی حد تک ہی مکلف ہے۔ اللہ تعالیٰ کا مطالبہ یہ نہیں ہے کہ آدمی اپنی استطاعت سے بڑھ کر کام کرے۔ البتہ مومن کو جس بات کی کوشش کرنی چاہیے وہ یہ ہے کہ اپنی حد تک خدا سے ڈرتے ہوئے زندگی بسر کرنے میں وہ کوئی کسر اٹھا نہ رکھے اور اس کی گفتار، کردار اور معاملات اس کی اپنی کوتاہی کے باعث حدود اللہ سے متجاوز نہ ہو جائیں۔ ','سید ابو اعلیٰ مودودیؒ -  تفہیم القرآن');
INSERT INTO "chapters_ur" VALUES(65,'طلاق',NULL,' نام : اس سورہ کا نام ہی الطلاق نہیں ہے ، بلکہ یہ اس کے مضمون کا عنوان بھی ہے ، کیونکہ اس طلاق ہی کے احکام بیان ہوئے ہیں۔ حضرت عبداللہ بن مسعودؓ نے اسے سورۃ النساء القصری بھی کہا ہے، یعنی چھوٹی سورہ نساء۔ زمانۂ نزول : حضرت عبداللہ بن مسعودؓ صراحت فرمائی ہے ، اور سورہ کے مضمون کی اندرونی شہادت بھی یہی ظاہر کرتی ہے کہ اس کا نزول لازماً سورہ بقرہ کی ان آیات کے بعد ہوا ہے جن میں طلاق پہلی مرتبہ دیئے گئے تھے ۔ اگر چہ یہ تعین کرنا مشکل ہے کہ اس کا زمانہ نزول کیا ہے لیکن بہر حال روایات سے اتنا ضرور معلوم ہوتا ہے کہ جب سورہ بقرہ کے احکام کو سمجھنے میں لوگ غلطیاں کرنے لگے ، اور عملاً بھی ان سے غلطیوں کا صدور ہونے لگا، تب اللہ تعالی نے ان کی اصلاح کے لئے یہ ہدایت نازل فرمائیں۔ موضوع اور مضمون: اس سورہ کے احکام کو سمجھنے کے لیے ضروری ہے کہ ان ہدایات کو پھر سے ذہن میں تازہ کر لیا جائے جو طلاق اور عدت کے متعلق اس سے پہلے قرآن مجید میں بیان ہو چکی ہیں: اَطَّلَاقُ مَرَّتٰنِ فَاِمْسَکٌ بِمَعْرُوْفٍ اَوْ تَسْرِیْحٌ بِاِحْسِانٍ (البقرہ۔ 229) ’’طلاق دو بار ہے ، پھر یا تو سیدھی طرح عورت کو روک لیا جائے یا بھلے طریقے سے رخصت کر دیا جائے ‘‘ وَالْمُطَلَّقٰتُ یَتَرَبَّصْنَ بِاَنْفُسِھِنَّ ثَلٰثَۃَ قُرُوْءٍ ۔۔۔۔۔ وَبُعُوْلَتُھُنَّ اَحَقُّ بِرَدِّھِنَّ فِیْ ذٰلِکَ اِنْ اَرَادُوْا اِصْلَاحاً (البقرہ۔ 228 )۔ ’’ اور مطلقہ عورتیں (طلاق کے بعد ) تین حیض تک اپنے آپ کو روکے رکھیں۔۔۔۔۔۔۔اور ان کے شوہر اس مدت میں ان کو (اپنی زوجیت میں ) واپس لے لینے کے حق دار ہیں اگر وہ اصلاح پر آمادہ ہوں‘‘۔ فَاِنْ طَلَّقَھَا فَلَا تَحِلُّ لَہٗ مِنْ بَعْدُ حَتٰی تَنْکِحَ زَوْجاً غَیْرَہٗ ۔۔۔۔۔۔۔۔۔۔۔ (البقرہ۔ 130)۔ ’’پھر اگر وہ (تیسری بار) اس کو طلاق دے دے تو اس کے بعد وہ اس کے لیے حلال نہ ہو گی یہاں تک کہ اس عورت کا نکاح کسی اور سے ہو جائے ۔۔۔۔۔۔۔ ‘‘۔ اِذٰا نَکَحْتُمُ الْمُؤٰمِنٰتِ ثُمَّ طَلَّقْتُمُوْھُنَّ مِنْ قَبْلِ اَنْ تَمَسُّوْھُنَّ فَمَا لَکُمْ عَلَیْھِنَّ مِنْ عِدَّۃٍ تَعْتَدُّوْنَھَا (الاحزاب۔ 49)۔ ’’ جب تم مومن عورتوں سے نکا ح کرو اور پھر انہیں ہاتھ لگانے سے پہلے طلاق دے دو تو تمہارے لیے ان پر کوئی عدت لازم نہیں ہے جس کے پورے ہونے کا تم مطالبہ کر سکو‘‘۔ وَالَّذِیْنَ یُتَوَفَّوْنَ مِنْکُمْ وَ یَذَرُوْنَ اَزْوَاجاً یَّتَبَّرَبَّصْنَ بِاَنْفُسِھِنَّ اَرْبَعَۃَ اَشْھُرِ وَّ عَشْراً (البقرہ۔234)۔ ’’ اور تم میں سے جو لوگ مر جائیں اور پیچھے بیویاں چھوڑ جائیں تو وہ عورتیں چار مہینے دس دن تک اپنے آپ کو روکے رکھیں‘‘۔ ان آیات میں جو قواعد مقرر کیے گئے تھے وہ یہ تھے : )۔ ایک مرد زیادہ سے زیادہ اپنی بیوی کو تین طلاق دے سکتا ہے ۔ )۔ ایک یا دو طلاق دینے کی صورت میں عدت کے اندر شوہر کو رجوع کا حق رہتا ہے اور عدت گزر جانے کے بعد وہی مرد و عورت پھر نکاح کرنا چاہیں تو کر سکتے ہیں، اس کے لیے تحلیل کی کوئی شرط نہیں ہے ۔ لیکن اگر مرد تین طلاق دے دے تو عدت کے اندر رجوع کا حق ساقط ہو جاتا ہے ، اور دوبارہ نکاح بھی اس وقت تک نہیں ہو سکتا جب تک عورت کا نکاح کسی اور مرد سے نہ ہو جائے اور وہ کبھی اپنی مرضی سے اس کو طلاق نہ دے دے ۔ )۔ مدخولہ عورت، جس کو حیض آتا ہو، اس کی عدت یہ ہے کہ اسے طلاق کے بعد تین مرتبہ حیض آجائے ۔ ایک طلاق یا دو طلاق کی صورت میں اس عدت کے معنی یہ ہیں کہ عورت ابھی تک اس شخص کی زوجیت میں ہے اور وہ عدت کے اندر اس سے رجوع کر سکتا ہے ۔ لیکن اگر مرد تین طلاق دے چکا ہو تو یہ عدت رجوع کی گنجائش کے لیے نہیں ہے بلکہ صرف اس لیے ہے کہ اس کے ختم ہونے سے پہلے عورت کسی اور شخص سے نکاح نہیں کر سکتی۔ )۔ غیر مدخولہ عورت، جسے ہاتھ لگانے سے پہلے ہی طلاق دے دی جائے ، اس کے لیے کوئی عدت نہیں ہے ۔ وہ چاہے تو طلاق کے بعد فوراً نکاح کر سکتی ہے ۔ )۔ جس عورت کا شوہر مر جائے اس کی عدت چار مہینے دس دن ہے ۔ اب یہ بات اچھی طرح سمجھ لینی چاہیے کہ سورہ طلاق ان قواعد میں سے کسی قاعدے کو منسوخ کرنے یا اس میں ترمیم کرنے کے لیے نازل نہیں ہوئی ہے ، بلکہ دو مقاصد کے لیے نازل ہوئی ہے ۔ ایک یہ کہ مرد کو طلاق کا جو اختیار دیا گیا ہے اس استعمال کرنے کے ایسے حکیمانہ طریقے بتائے جائیں جن سے حتی الامکان علیٰحدگی کی نوبت نہ آنے پائے ، اور علیٰحدگی ہو تو بدرجہ آخر ایسی حالت میں ہو جبکہ باہمی موافقت کے سارے امکانات ختم ہو چکے ہوں۔ کیونکہ خدا کی شریعت میں طلاق کی گنجائش صرف ایک ناگزیر ضرورت کے طور پر رکھی گئی ہے ، ورنہ اللہ تعالیٰ اس بات کو سخت ناپسند فرماتا ہے کہ ایک مرد اور ایک عورت کے درمیان جو ازدواجی تعلق قائم ہو چکا ہو وہ پھر کبھی ٹوٹ جائے ۔ نبی صلی اللہ علیہ و سلم کا ارشاد ہے کہ مَا احَلَ اللہ شیئاً ابغضَ الیہ من الطلاق۔ ’’ اللہ نے کسی ایسی چیز کو حلال نہیں کیا ہے جو طلاق سے بڑھ کر اسے ناپسند ہو‘‘(ابو داؤد)۔ اور ابغض الحلال الی اللہ عزّ و جَل الطلاق۔ ’’ تمام حلال چیزوں میں اللہ کو سب سے زیادہ ناپسند طلاق ہے ‘‘۔ (ابو داؤد)۔ دوسرا مقصد یہ ہے کہ سورہ بقرہ کے احکام کے بعد جو مزید مسائل جواب طلب باقی رہ گئے تھے ان کا جواب دے کر اسلام کے عائلی قانون کے اس شعبہ کی تکمیل کر دی جائے ۔ اس سلسلے میں یہ بتایا گیا ہے کہ جن مدخولہ عورتوں کو حیض آنا بند ہو گیا ہو، یا جنہیں ابھی حیض آنا شروع ہی نہ ہوا ہو، طلاق کی صورت میں ان کی عدت کیا ہو گی۔ اور جو عورت حاملہ ہو اسے اگر طلاق دے دی جائے یا اس کا شوہر مر جائے تو اس کی عدت کی مدت کیا ہے ۔ اور مختلف قسم کی مطلقہ عورتوں کے نفقہ اور سکونت کا انتظام کس طرح کیا جائے ۔ ','سید ابو اعلیٰ مودودیؒ -  تفہیم القرآن');
INSERT INTO "chapters_ur" VALUES(66,'حرمت',NULL,' نام: پہلی ہی آیت کے الفاظ لِمَ تُحَرِّمُ سے ماخوذ ہے۔ یہ بھی اس کے مضامین کا عنوان نہیں ہے، بلکہ اس نام سے مُراد یہ ہے کہ یہ وہ سورہ ہے جس میں تحریم کے واقعہ کا ذکر آیا ہے۔ زمانۂ نزول: اِس میں تحریم کے جس واقعہ کا ذکر کیا گیا ہے اس کے متعلق احادیث کی روایات میں دو خواتین کا ذخر آیا ہے جو اُس وقت حضورؐ کے حرم میں تھیں ۔ ایک حضرت صفیہؓ ۔ دوسری حضرت ماریہِ قِبْطِیّہؓ ۔ ان میں سے ایک ، یعنی حضرت صَفِیّہ فتح خیبر کے بعد حضورؐ کے نکاح میں آئیں، اور خیبر کی فتح بالاتفاق سن ۷ ہجری میں ہوئی ہے۔ دوسری خاتون ماریہ کو سن ۷ ہجری میں مصر کے فرمانروا مُقَوْقِس نے حضورؐکی خدمت میں ارسال کیا تھا اور اُن کے بطن سے ذی الحجہ سن ۸ ہجری میں حضورؐکے فرزند حضرت ابراہیمؓ پیدا ہوئے تھے۔ ان تاریخی واقعات سے یہ بات قریب قریب متعین ہو جاتی ہے کہ اس سُورہ کا نزول سن ۷ ہجری یا سن ۸ ہجری کے دوران میں کسی وقت ہوا ہے۔ موضوع اور مباحث: یہ ایک بڑی اہم سُورۃ ہے جس میں رسول اللہ صلی اللہ علیہ وسلم کی ازواجِ مطہرات کے متعلق بعض واقعات کی طرف اشارہ کرتے ہوئے چند مہمّاتِ مسائل پر روشنی ڈالی گئی ہے۔ ایک یہ کہ حلال و حرام اور جائز و ناجائز کے حدود مقرر کرنے کے اختیارات قطعی طور پر اللہ تعالیٰ کے ہاتھ میں ہیں، اور عام انسان تو درکنار، خود اللہ تعالیٰ کے نبیؐ کی طرف بھی اُن کا کوئی حصّہ منتقل نہیں کیا گیا ہے۔ نبی بحیثیتِ نبی اگر کسی چیز کو حرام یا حلا ل قرار دے سکتا ہے تو صرف اُس صورت میں جبکہ اللہ تعالیٰ کی طرف سے اس کا اشارہ ہو، قطعِ نظر اس سے کہ وہ اشارہ قرآن مجید میں نازل ہو ا ہو، یا وحی خفی کے طور پر کیا گیا ہو۔ لیکن بطورِ خود اللہ تعالیٰ کی مباح کی ہوئی کسی چیز کو حرام کر لینے کا مجاز نبی بھی نہیں ہے کجا کہ کوئی اور شخص ہو سکے۔ دوسرے یہ کہ انسانی معاشرہ میں نبی کا مقام انتہائی نازک مقام ہے۔ ایک معمولی بات بھی، جو کسی دوسرے انسان کی زندگی میں پیش آئے تو چنداں اہمیت نہیں رکھتی ، نبی کی زندگی میں اگر پیش آجائے تو وہ قانون کی حیثیت اختیار کر جاتی ہے۔ اس لیے اللہ تعالیٰ کی طرف سے انبیاء علیہم السّلام کی زندگی پر ایسی کڑی نگرانی رکھی گئی ہے کہ ان کا کوئی ادنیٰ اِقدام بھِ منشاء الہٰی سے ہٹا ہوا نہ ہو۔ ایسا کوئی فعل بھی اگر نبی سے صادر ہوا ہے تو اس کی فوراً اصلاح کر دی گئی ہے تا کہ اسلامی قانون اور اس کے اصول اپنی بالکل صحیح صورت میں نہ صرف خدا کی کتاب ، بلکہ نبی کے اُسوہ ٔ حسنہ کی صورت میں بھی خدا کے بندوں تک پہنچ جائیں اور ان میں ذرّہ برابر بھی کوئی چیز ایسی شامل نہ ہو نے پائے جو منشاءِ الہٰی سے مطابقت نہ رکھتی ہو۔ تیسری بات جو مذکورۂ بالا نکتہ سے خود بخود نکلتی ہے وہ یہ ہے کہ ایک ذرا سی بات پر جب نبی صلی اللہ علیہ وسلم کو ٹوک دیا گیا اور نہ صرف اس کی اصلاح کی گئی بلکہ اسے ریکارڈ پر بھی لے آیا گیا ، تو یہ چیز قطعی طور پر ہمارے دل میں یہ اطمینان پیدا کر دیتی ہے کہ حضورؐکی حیاتِ طیّبہ میں جو اعمال و افعال اور جو احکام و ہدایات بھی ہمیں اب ملتے ہیں ، اور جن پر اللہ تعالیٰ کی طرف سے کوئی گرفت یا اصلاح یریکارڈ پر موجود نہیں ہے، وہ سراسر حق ہیں، اللہ کی مرضی سے پُوری مطابقت رکھتے ہیں ، اور ہم پُورے اعتماد کے ساتھ ان سے ہدایت و رہنمائی حاصل کر سکتے ہیں۔ چوتھی بات جو اِس کلام میں ہمارے سامنے آتی ہے وہ یہ ہے کہ جس رسولِ مقدس کی عزت و حُرمت کو اللہ تعالیٰ خود اپنے بندوں کے حق میں لازمہ ٔ ایمان قرار دیتا ہے اُسی کے متعلق اِس سُورہ میں بیان کیا گیا ہے کہ اُس نے اپنی بیویوں کے خوش کرنے کے لیے ایک مرتبہ اللہ کی حلال کی ہوئی ایک چیز اپنے اوپر حرام کر لی۔ اور جن ازواج مطہرات کو اللہ تعالیٰ خود تمام اہلِ ایمان کی ماں قرار دیتا ہے اور جن کے احترام کا اس نے خود مسلمانوں کو حکم دیا ہے انہی کو اس نے بعض غلطیوں پر اِس سُورۃ میں شدّت سے تنبیہ فرمائی ہے۔ پھر نبی پر یہ گرفت اور ازواجِ مطہرات کو یہ تنبیہ بھی خفیہ طور پر نہیں کی گئی بلکہ اُس کتاب میں درج کر دی گئی جسے تمام اُمّت کو ہمیشہ ہمیشہ تلاوت کرنا ہے۔ ظاہر ہے کہ کتاب اللہ میں اِس ذکر کا منشا نہ یہ تھا ، نہ یہ ہو سکتا تھا کہ اللہ تعالیٰ اپنے رسول اور امہات المومنین کو اہلِ ایمان کی نگاہوں سے گرا دینا چاہتا تھا۔ اور یہ بھی ظاہر ہے کہ قرآن پاک کی یہ سورۃ پڑھ کر کسی مسلمان کےدل سے ان کا احترام اُٹھ نہیں گیا ہے۔ اب قرآن میں یہ ذکر لانے کی مصلحت اس کے سوا اور کیا ہو سکتی ہے کہ اللہ تعالیٰ اہلِ ایمان کو اپنے بزرگوں کے احترام کی صحیح حدوں سے آشنا کرنا چاہتا ہے ۔ نبی، نبی ہے، خدا نہیں ہے کہ اس سے کوئی لغزش نہ ہو۔ نبی کا احترام اس بنا پر نہیں ہے کہ اس سے لغزش کا صدور نا ممکن ہے بلکہ اس بنا پر ہے کہ وہ مرضی الہٰی کا مکمل نمائندہ ہے اور اس کی ادنیٰ سی لغزش کو بھی اللہ نے اصلاح کیے بغیر نہیں چھوڑا ہے جس سے ہمیں یہ اطمینان نصیب ہو جاتا ہے کہ نبی کا چھوڑا ہوا اُسوۂ حسنہ اللہ کی مرضی کی پُوری نمائندگی کر رہا ہے۔ اسی طرح صحابۂ کرام ہوں یا ازواجِ مطہرات ، یہ سب انسان تھے، فرشتے یا فوق البشر نہ تھے۔ اُن سے غلطیوں کا صدور ہو سکتا تھا۔ اُن کو جو مرتبہ بھی حاصل ہوا اِس وجہ سے ہوا کہ اللہ کی رہنما ئی اور اللہ کے رسول ؐ کی تربیت نے ان کو انسانیت کا بہترین نمونہ بنا دیا تھا ۔ اُن کو جو کچھ بھی احترام ہے اسی بنا پر ہے، نہ کہ اِس مفروضے پر کہ وہ کچھ ایسی ہستیاں تھیں جو غلطیوں سے بالکل مبرّا تھیں۔ اِسی وجہ سے نبی صلی اللہ علیہ وسلم کے عہدِ مبارک میں صحابہ یا ازواج مطہرات سے بشریت کی بنا پر جب بھی کسی غلطی کا صدور ہوا اُس پر ٹوکا گیا۔ اُن کی بعض غلطیوں کی اصلاح حضورؐنے کی جس کا ذکر احادیث میں بکثرت مقامات پر آیا ہے۔ اور بعض غلطیوں کا ذکر قرآن مجید میں کر کے اللہ تعالیٰ نے خود ان کی اصلاح کی تاکہ مسلمان کبھی بزرگوں کے احترام کا کوئی ایسا مبالغہ آمیز تصوّر نہ قائم کر لیں جو انہیں انسانیت کے مقام سے اٹھا کر دیویوں اور دیوتاو ٔ ں کے مقام پر پہنچا دے۔ آپ قرآن پاک کا مطالعہ آنکھیں کھول کر کریں تو اِس کی پے در پے مثالیں آپ کے سامنےآئیں گی۔ سُورۂ آلِ عمران میں جنگِ اُحُد کا ذکر کرتے ہوئے صحابۂ کرام کو مخاطب کر کے فرمایا: ”اللہ نے(تائید و نصرت) کا جو وعدہ تم سے کیا تھا وہ تو اُس نے پُورا کر دیا جبکہ اُس کے اذن سے تم اُن کو قتل کر رہے تھے۔ یہاں تک کہ جب تم نے کمزوری دکھائی اور اپنے کام میں باہم اختلاف کیا اور جو نہی کہ وہ چیز اللہ نے تمہیں دکھائی جس کی محبت میں تم گرفتار تھے(یعنی مالِ غنیمت) تم حکم کی نافرمانی کر بیٹھے، تم میں سے کوئی دنیا کا طالب تھا اور کوئی آخرت کا طلب گار ، تب اللہ نے تمہیں اُن کے مقابلہ میں پسپا کر دیا تاکہ تمہاری آزمائش کرے۔ اور حق یہ ہے کہ اللہ نے تمہیں معاف کر دیا، اللہ مومنوں پر بڑا فضل فرمانے والا ہے“(آیت ۱۵۲)۔ سُورۂ نُور میں حضرت عائشہ پر تُہمت کا ذکر کرتے ہوئے صحابہ ؓ سے فرمایا گیا: ”ایسا کیوں نہ ہوا کہ جب تم لوگوں نے اِسے سُنا تھا اُسی وقت مومن مرد اور عورتیں ، سب اپنے آپ سے نیک گمان کرتے اور کہہ دیتے کہ یہ تو صریح بہتا ن ہے؟ ۔۔۔۔ اگر تم لوگوں پر دُنیا اور آخرت میں اللہ کا فضل اور رحم و کرم نہ ہوتا تو جن باتوں میں تم پڑ گئے تھے ان کی پاداش میں بڑا عذاب تمہیں آلیتا۔ ذرا غور کرو ، جب تمہاری ایک زبان سے دوسری زبان اِس قصّے کو لیتی چلی جا رہی تھی اور تم اپنے منہ سے وہ کچھ کہے جا رہے تھے جس کے متعلق تمہیں کوئی علم نہ تھا۔ تم اِسے ایک معمولی بات سمجھ رہے تھے حالانکہ اللہ کے نزدیک یہ بڑی بات تھی۔ کیوں نہ اِسے سنتے ہی تم نے کہہ دیا کہ ہمیں ایسی بات زبان سے نکالنا زیب نہیں دیتا، سبحان اللہ، یہ تو ایک بہتانِ عظیم ہے؟ اللہ تم کو نصیحت کرتا ہے کہ آئندہ کبھی ایسی حرکت نہ کرنا اگر تم مومن ہو “(آیات ۱۲تا ۱۷)۔ سُورہ ٔ اَحزاب میں ازواجِ مطہرات کو خطاب کرتے ہوئے ارشاد ہوا: ”اَے نبی اپنی بیویوں سے کہو، اگر تم دنیا اور اس کی زینت چاہتی ہو تو آؤ ، میں تمہیں کچھ دے دلا کر بھلے طریقے سے رخصت کر دوں۔ اور اگر تم اللہ اور اس کے رسول اور آخرت کی طلبگار ہو تو جان لو کہ تم میں سے جو نیکو کار ہیں اللہ نے ان کے لیے بڑا اجر مہیا کر رکھا ہے “(آیات ۲۸ تا ۲۹)۔ سُورۂ جُمعہ میں صحابہ ؓ کے متعلق فرمایا: ”جب انہوں نے کاروبارِ تجارت یا کھیل تماشا دیکھا تو اس کی طرف دوڑ گئے اور (اے نبی) تم کو(خطبے میں) کھڑا چھوڑ دیا۔ اِن سے کہو کہ جو کچھ اللہ کے پاس ہے وہ کھیل تماشے اور تجارت سے بہتر ہے اور اللہ بہترین رزق دینے والا ہے“(آیت۱۱)۔ سُورہ ٔ مُمتَحِنہ میں ایک بدری صحابی حضرت حاطِب ؓ بن ابی بَلْتَعَہ کے اِس فعل پر سخت گرفت کی گئی کہ انہوں نے فتح مکہ سے پہلے حضورؐکے حملے کی خُفیہ اطلاع کفّارِ قریش کو بھیج دی تھی۔ یہ ساری مثالیں خود قرآن میں موجود ہیں، اُسی قرآن میں جس میں اللہ تعالیٰ نے صحابہ اور ازواج مطہرات کے فضل و شرف کو خود بیان فرمایا ہے اور اُنہیں رضی اللہ عنہم و رَضُوا عَنہ کا پروانہء خوشنودی عطافر مایا ہے۔ بزرگوں کے احترام کی یہی مبنی بر اعتدال تعلیم تھی جس نے مسلمانوں کو انسان پرستی کے اُس ھاوِیہ میں گرنے سے بچایا جس میں یہود و نصاریٰ گر گئے۔ اور اسی کا نتیجہ ہے کہ حدیث، تفسیر اور تاریخ کے موضوعات پر جن اکابرِ اہلِ سنّت نے کتابیں مرتّب کی ہیں ان میں جہاں صحابہء کرام اور ازواجِ مطہرات اور دوسرے بزرگوں کے فضائل و کمالات بیان کیے گئے ہیں، ان کی کمزوریوں اور لغزشوں اور غلطیوں کے واقعات بیان کرنے میں بھی تامّل نہیں کیا گیا ہے، حالانکہ آج کے مدعیانِ احترام کی بہ نسبت وہ اِن بزرگوں کے زیادہ قدر شناس تھے اور اِن سے زیادہ حدودِ احترام کو جانتے تھے۔ پانچویں بات جو اس سُورہ میں کھول کر بیان کی گئی ہے وہ یہ ہے کہ اللہ کا دین بالکل بے لاگ ہے۔ اس میں ہر شخص کے لیے صرف وہی کچھ ہے جس کا وہ اپنے ایمان اور اعمال کے لحاظ سے مستحق ہو۔ کسی بڑی سے بڑی ہستی کے ساتھ نسبت بھی اس کے لیے قطعًا نافع نہیں ہے اور کسی بُری سے بُری ہستی کے ساتھ نسبت بھی اُسے کوئی نقصان نہیں پہنچا سکتی۔ اس معاملہ میں خاص طور پر ازواجِ مطہرات کے سامنے تین قسم کی عورتوں کو بطورِ مثال پیش کیا گیا ہے۔ ایک مثال حضرت نوح ؑ ارو حضرت لُوط ؑ کی بیویوں کی ہے ، جو اگر ایمان لاتیں اور اپنے جلیل القدر شوہروں کا ساتھ دیتیں تو ان کا مقام امّتِ مُسلِمہ میں وہی ہوتا جو نبی صلی اللہ علیہ وسلم کی ازواجِ مطہرات کا ہے۔ لیکن چونکہ انہوں نے اِس کے برعکس رویّہ اختیار کیا، اس لیے انبیاء کی بیویاں ہونا ان کے کچھ کام نہ آیا اور وہ جہنّم کی مستحق ہوئیں۔ دوسری مثال فرعون کی بیوی کی ہے۔ جو اگرچہ ایک بدترین دشمنِ خدا کی بیوی تھیں ، لیکن چونکہ وہ ایمان لے آئیں اور انہوں نے قومِ فرعون کے عمل سے اپنے عمل کا راستہ الگ کر لیا، اس لیے فرعون جیسے اکفر الکا فرین کی بیوی ہونا بھی اُن کے لیے کسی نقصان کا موجب نہ ہوا اور اللہ تعالیٰ نے انہیں جنت کا مستحق بنا دیا۔ تیسری مثال حضرت مریم علیہا السّلام کی ہے جنہیں یہ مرتبہءعظیم اس لیے ملا کہ اللہ نے جس شدید آزمائش میں انہیں ڈالنے کا فیصلہ فرمایا تھا اس کے لیے انہوں نے سرِ تسلیم خم کر دیا۔ حضرت مریم ؑ کے سوا دنیا میں کسی شریف اور نیک لڑکی کو کبھی ایسی سخت آزمائش میں نہیں ڈالا گیا کہ کنوار پنے کی حالت میں اللہ کے حکم سے اس کو معجزے کے طور پر حاملہ کر دیا گیا ہو اور اُسے بتا دیا گیا ہو کہ اُس کا رب اُس سے کیا خدمت لینا چاہتا ہے۔ جب حضرت مریم ؑ نے اِس پر کوئی واویلا نہ کیا بلکہ ایک سچّی مومنہ کی حیثیت سے وہ سب کچھ برداشت کرنا قبول کر لیا جو اللہ کی مرضی پُوری کرنے کے لیے برداشت کرنا ناگزیر تھا، تب اللہ نے ان کو سیّدۃ النسَآ ء فی الجنۃ (مُسند احمد) کے مرتبہ ء عالی پر سرفراز فرمایا۔ اِن اُمور کے علاوہ ایک اور اہم حقیقت جو اِس سُورۃ سے ہمیں معلوم ہوتی ہے وہ یہ ہے کہ اللہ تعالیٰ کی طرف سے نبی صلی اللہ علیہ وسلم کے پاس صرف وہی علم نہیں آتا تھا جو قرآن میں درج ہوا ہے ، بلکہ آپؐ کو وحی کے ذریعہ سے دوسری باتوں کا علم بھی دیا جاتا تھا جو قرآن میں درج نہیں کیا گیا ہے۔ اس کی صریح دلیل اِس سُورۃ کی آیت ۳ ہے۔ اُس میں بتایا گیا ہے کہ نبی صلی اللہ علیہ وسلم نے اپنی ازواجِ مطہرات میں سے ایک بیوی سے راز میں ایک بات کہی اور اُنہوں نے وہ کسی اور کو بتا دی۔ اس پر اللہ تعالیٰ نے نبی صلی اللہ علیہ وسلم کو مطلع کر دیا۔ پھر جب حضورؐ نے اِس غلطی پر اپنی اُن بیوی کو تنبیہ فرمائی ، اور انہوں نے پوچھا کہ آپؐ کو میری یہ غلطی کس نے بتائی تو حضورؐنے جواب دیا کہ مجھے علیم و خبیر ہستی نے اس کی خبر دی ہے۔ اب سوال یہ ہے کہ پُورے قرآن میں کہاں وہ آیت ہے جس میں اللہ تعالیٰ نے فرمایا ہو کہ”اَے نبی، تم نے اپنے بیوی سے راز میں جو بات کہی تھی وہ اُس نے کسی اور پر ، یا فلاں شخص پر ظاہر کر دی ہے“؟ اگر ایسی کوئی آیت قرآن میں نہیں ہے ، اور ظاہر ہے کہ نہیں ہے، تو یہ اِس بات کا صریح ثبوت ہے کہ قرآن کے علاوہ بھی نبی صلی اللہ علیہ وسلم پر وحی کا نزول ہوتا تھا۔ اس سے منکرینِ حدیث کا یہ دعویٰ بالکل باطل ہو جاتا ہے کہ نبی صلی اللہ علیہ وسلم پر قرآن کے سوا اور کوئی وحی نہیں آتی تھی۔ ','سید ابو اعلیٰ مودودیؒ -  تفہیم القرآن');
INSERT INTO "chapters_ur" VALUES(67,'بادشاہ','',' نام : پہلےفقرے تَبَارَکَ الَّذِی بِیَدِہِ المُلکُ کے لفظ المُلک کو اس سورہ کا نام قرار دیا گیا ہے۔ زمانہٴ نزول : کسی مُعتبر روایت سے یہ نہیں معلوم ہوتا کہ یہ کس زمانے میں نازل ہوئی ہے، مگر مضامین اور اندازِ بیان سے صاف معلوم ہوتا ہے کہ یہ مکہ معظمہ کے ابتدائی دور کی نازل شدہ سورتوں میں سے ہے۔ موضوع اور مضمون: اس میں ایک طرف مختصر طریقے سے اسلام کی تعلیمات کا تعارف کرایا گیا ہے اور دوسری طرف بڑے بڑے موثر انداز میں اُن لوگوں کو چونکا یا گیا ہے۔ جو غفلت میں پڑے ہوئے تھے ۔ یہ مکہ معظمہ کی ابتدائی سورتوں کی خصوصیت ہے کہ وہ اسلام کی ساری تعلیمات اور رسول اللہ صلی اللہ علیہ وسلم کے مقصدِ بعثت کو پیش کر تی ہیں، مگر تفصیل کے ساتھ نہیں بلکہ اختصار کے ساتھ، تا کہ وہ بتدریج لوگوں کے ذہن نشین ہوتی چلی جائیں۔ اس کے ساتھ ان میں زیادہ تر زور اس بات پر صرف کیا جاتا ہے کہ لوگوں کی غفلت دور کی جائے، ان کو سوچنے پر مجبور کیا جائے ، اور ان کے سوئے ہوئے ضمیر کو بیدار کیا جائے۔ پہلی پانچ آیتوں میں انسان کو احساس دلایا گیا ہے کہ وہ جس کائنات میں رہتا ہے وہ ایک انتہائی منظم اور محکم سلطنت ہے جس میں ڈھونڈے سے بھی کوئی عیب یا نقص یا خلل تلاش نہیں کیا جا سکتا۔ اس سلطنت کو عدم سے وجود میں بھی اللہ تعالیٰ ہی لایا ہے اور اس کی تدبیر وانتظام اور فرمانروائی کے تمام اختیار ات بھی با لکل یہ اللہ ہی کے ہا تھ میں ہیں اور اس کی قدرت لامحدود ہے۔ اس کے ساتھ انسان کو یہ بھی بتایا گیا ہے کہ اس انتہائی حکیمانہ نظام میں وہ بے مقصد پیدا نہیں کر دیا گیا ہے بلکہ یہاں اسے امتحان کے لیے بھیجا گیا ہے ، اور اس امتحان میں وہ اپنے حسنِ عمل ہی سے کامیاب ہوسکتا ہے۔ آیت ۶ سے ۱۱ تک کفر کے وہ ہولناک نتائج بیان کیے گئے ہیں جو آخرت میں نکلنے والے ہیں اور لوگوں کو بتایاگیا ہے کہ اللہ تعالیٰ نے اپنے انبیاء کو بھیج کر تمہیں اسی دنیا میں ان نتائج سے خبر دار کر دیا ہے۔ اب اگر یہاں تم انبیاء کی بات مان کر اپنا رویہ درست کر و گے تو آخرت میں تمہیں خود اعتراف کرنا پڑے گا کہ جو سزا تم کو دی جارہی ہے فی الواقع تم اُس کے مستحق ہو۔ آیت ۱۲ سے ۱۴ تک یہ حقیقت ذہن نشین کرائی گئی ہے کہ خالق اپنی مخلوق سے بے خبر نہیں ہو سکتا۔ وہ تمہاری ہر کھلی اور چھپی بات، حتٰی کہ تمہارے دل کے خیالات تک سے واقف ہے۔ لہٰذااخلاق کی صحیح بنیاد ہے کہ انسان اُس اَن دیکھے خدا کی باز پرس سے ڈر کر برائی سے بچے، خواہ دنیا میں کوئی طاقت اُس پر گرفت کرنے والی ہو یا نہ ہو اور دنیا میں اس سے کسی نقصان کا امکان ہو یا نہ ہو۔ یہ طرز عمل جو لوگ اختیار کریں گے وہی آخرت میں بخشش اور اجرِ عظیم کے مستحق ہونگے۔ آیت ۱۵ سے ۲۳ تک اُن پیش یا اُفتادہ حقیقتوں کی طرف جنہیں انسان دنیا کے معمولات سمجھ کر قابل توجہ شمار نہیں کرتا، پے در پے اشارے کر کے اُن پر سوچنے کی دعوت دی گئی ہے ۔ فرمایا گیا ہے کہ اس زمین کو دیکھو جس پر تم اطمینان سے چل پھر رہے ہو اور جس سے اپنا رزق حاصل کر رہے ہو۔ خدا ہی نے اسے تمہارے لیے تابع کر رکھا ہے ، ورنہ کسی وقت بھی اس زمین میں ایسا زلزلہ آسکتا ہے کہ تم پیوندِ خاک ہو جاؤ، یا ہوا کا ایسا طوفان آسکتا ہے جو تمہیں تہس نہس کر کے رکھ دے۔ اپنے اوپر اڑنے والے پرندوں کو دیکھو۔ خدا ہی تو ہے جو انہیں فضا مہیں تھامے ہوئے ہے۔ اپنے تمام ذرائع و وسائل پر نگاہ ڈال کر دیکھو۔ خدا اگر تمہیں عذاب میں مبتلا کرنا چاہے تو کون تمہیں اس سے بچا سکتا اورخدا اگر تمہارے لیے رزق کے دروازے بند کر دے تو کون انہیں کھول سکتا ہے ؟ یہ ساری چیزیں تمہیں حقیقت سے آگاہ کرنے کے لیے موجود ہیں۔ مگر انہیں تم حیوانات کی طرح دیکھتے ہو جو مشاہدات سے نتائج اخذ کر نے کی صلاحیت نہیں رکھتے، اور اس سماعت و بینائی اور ان سوچنے سمجھنے والے دماغوں سے کام نہیں لیتے جو انسان ہونے کی حیثیت سے خدا نے تمہیں دیے ہیں ۔ اسی وجہ سے راہ راست تمہیں نظر نہیں آتی۔ آیت ۲۴ سے ۲۷ تک بتایا گیا ہے کہ آخر کار تمہیں لازماً اپنے خدا کے حضور حاضر ہونا ہے۔ نبی کا کام یہ نہیں ہے کہ تمہیں اُس کے آنے کا وقت اور تاریخ بتائے۔ اس کا کام بس یہ ہے کہ تمہیں اُس آنے والے وقت سے پیشگی خبردار کردے۔ تم آج اُس کی بات نہیں مانتے اور مطالبہ کرتے ہو کہ وقت لا کر تمہیں دکھا دیا جائے۔ مگر جب وہ آجائے گا اور تم آنکھوں سے اسے دیکھ لوگے تو تمہارے ہوش اُڑ جائیں گے۔ اس وقت تم سے کہا جائے گا کہ یہی ہے وہ چیز جسے جلدی لے آنے کا تم مطالبہ کر رہے تھے۔ آیت ۲۸ اور ۲۹ میں کفار مکہ کی اُن باتوں کا جواب دیاگیا ہے جو وہ نبی صلی اللہ علیہ وسلم اور آپؐ کے ساتھیوں کے خلاف کرتے تھے۔ وہ حضورؐ کو کوستے تھے اور آپ کے لیے اور اہل ایمان کے لیے ہلاکت کی دعائیں مانگتے تھے۔ اس پر فرمایا گیا ہے کہ تمہیں راہ راست کی طرف بلانے والے خواہ ہلاک ہوں یا اللہ اُن پر رحم کرے ، اس سے آخر تمہاری قسمت کیسے بدل جائے گی؟ تم اپنی فکر کرو کہ خدا کا عذاب اگر تم پر آجائے تو کون تمہیں بچائے گا؟ جو لوگ خدا پر ایمان لائے ہیں اور جنہوں نے اُس پر توکل کیا ہے۔ انہیں تم گمراہ سمجھ رہے ہو۔ ایک وقت آئے گا جب یہ بات کھل جائے گی کہ حقیقت میں گمراہ کون تھا۔ آخر میں لوگوں کے سامنے یہ سوال رکھ دیا گیا ہے اور اسی پر سوچنے کے لیے انہین چھوڑ دیا گیا ہے کہ عرب کے صحراؤں اور پہاڑی علاقوں میں ، جہاں تمہاری زندگی کا سارا انحصار اُس پانی پر ہے جو کسی جگہ زمین سے نکل آیا ہے، وہاں اگر یہ پانی زمین میں اتر کر غائب ہوجائے تو خدا کے سوا کون تمہیں یہ آبِ حیات لا کر دے سکتا ہے؟ ','سید ابو اعلیٰ مودودیؒ -  تفہیم القرآن');
INSERT INTO "chapters_ur" VALUES(68,'قلم',NULL,' نام: اس کا نام سورہ "ن" بھی ہے اور "القلم" بھی۔ دونوں الفاظ سورۃ کے آغاز ہی میں موجود ہیں۔ زمانۂ نزول: یہ بھی مکہ معظمہ کے ابتدائی دور کی نازل شدہ سُورتوں میں سے ہے، مگر اس کے مضمون سے یہ بات مترشح ہوتی ہے کہ یہ اُس زمانے میں نازل ہوئی تھی جب مکہ میں رسول اللہ صلی اللہ علیہ وسلم کی مخالفت اچھی خاصی شدت اختیار کر چکی تھی۔ موضوع اور مضمون: اس میں تین مضامین بیان ہوئے ہیں۔ مخالفین کے اعتراضات کا جواب ، اُن کو تنبیہ اور نصیحت ، اور رسول اللہ صلی اللہ علیہ وسلم کو صبر و استقامت کی تلقین۔ آغازِ کلام میں رسول اللہ صلی اللہ علیہ وسلم سے ارشاد ہوا ہے کہ یہ کفار تم کو دیوانہ کہتے ہیں، حالانکہ جو کتاب تم پیش کر رہے ہو اور اخلاق کے جس اعلیٰ مرتبے پر تم فائز ہو وہ خود ان کے اس جھوٹ کی تردید کے لیے کافی ہے۔ عنقریب وہ وقت آنے والا ہے جب سب ہی دیکھ لیں گے کہ دیوانہ کون تھا اور فرزانہ کون۔ لہٰذا مخالفت کا جو طوفان تمہارے خلاف اٹھایا جا رہا ہے اس کا دباؤ ہر گز قبول نہ کرو۔ دراصل یہ ساری باتیں اِس لیے کی جارہی ہیں کہ تم کسی نہ کسی طرح دب کر ان سے مصالحت (Compromise) کرنے کے لیے تیار ہو جاؤ۔ پھر عوام کی آنکھیں کھولنے کے لیے نام لیے بغیر مخالفین میں سے ایک نمایاں شخص کا کردار پیش کیا گیا ہے جسے اہلِ مکہ خوب جانتے تھے۔ اُس وقت رسول اللہ صلی اللہ علیہ وسلم کی پاکیزہ اخلاق بھی سب کے سامنے تھے ، اور ہر دیکھنے والا یہ بھی دیکھ سکتا تھا کہ آپ کی مخالفت میں مکہ کے جو سردار پیش پیش ہیں اُن میں کسی سیرت و کردار کے لوگ شامل ہیں۔ اس کے بعد آیت ۱۷ سے ۲۳ تک ایک باغ والوں کی مثال پیش کی گئی ہے جنہوں نے اللہ سے نعمت پا کر اُس کی نا شکری کی اور اُن کے اندر جو شخص سب سے بہتر تھا اس کی نصیحت بر وقت نہ مانی، آخر کار وہ اُس نعمت سے محروم ہو گئے اور اُن کی آنکھیں اُس وقت کھلیں جب اُن کا سب کچھ بر باد کر چکاتھا۔ یہ مثال دے کر اہلِ مکہ کو متنبہ کیا گیا ہے کہ رسول اللہ صلی اللہ علیہ وسلم کی بعثت سے تم بھی اُسی آزمائش میں پڑ گئے ہو جس میں وہ باغ والے پڑے تھے۔ اگر ان کی بات نہ مانوگے تو دنیا میں بھی عذاب بھگتو گے اور آخرت کا عذاب اس سے بھی زیادہ بڑا ہے۔ پھر آیت ۳۴ سے ۴۷ تک مسلسل کفار کو فہمائش کی گئی ہے جس میں کہیں تو خطاب براہ راست اُن سے ہے اور کہیں رسول اللہ صلی اللہ علیہ وسلم کو مخاطب کرتے ہوئے دراصل تنبیہ اُن کو کی گئی ہے ۔ اس سلسلہ میں جو باتیں ارشاد ہوئی ہیں ان کا خلاصہ یہ ہے کہ آخرت کی بھلائی لازماً اُنہی لوگوں کے لیے ہے جنہوں نے دنیا میں خدا ترسی کے ساتھ زندگی بسر کی ہے ۔ یہ بات سراسر عقل کے خلاف ہے کہ اللہ تعالیٰ کے ہاں فرمانبرداروں کا انجام وہ ہو جو مجرموں کا ہو نا چاہیے۔ کفار کی یہ غلط فہمی قطعی بے بنیاد ہے کہ خدا اُن کے ساتھ وہ معاملہ کرے گا جو وہ خود اپنے لیے تجویز کرتے ہیں، حالانکہ اِس کے لیے اُنہیں کوئی ضمانت حاصل نہیں ہے ۔جن لوگوں کو دنیا میں خدا کے آگے جھکنے کی دعوت دی جا رہی ہے اور وہ اس سے انکار کرتے ہیں، قیامت کے روز وہ سجدہ کرنا چاہیں گے بھی تو نہ کر سکیں گے اور ذلت کا انجام انہیں دیکھنا پڑیگا۔ قرآن کو جھٹلا کو وہ خدا کے عذاب سے بچ نہیں سکتے ۔ اُنہیں جو ڈھیل دی جا رہی ہے اس سے وہ دھوکے میں پڑ گئے ہیں۔ وہ سمجھتے ہیں کہ اس تکذیب کے با وجود جب اُن پر عذاب نہیں آرہا ہے تو وہ صحیح راستے پر ہیں، حالانکہ وہ بے خبری میں ہلاکت کی راہ پر چلے جا رہے ہیں۔ اُن کے پاس رسول کی مخالفت کے لیے کوئی معقول وجہ نہیں ہے ، کیونکہ وہ ایک بے غرض مبلغ ہے، اپنی ذات کے لیے اُن سے کچھ نہیں مانگ رہا ہے، اور وہ یہ دعویٰ بھی نہیں کر سکتے کہ اُنہیں اُس کے رسول نہ ہونے اور اُس کی باتوں کے غلط ہونے کا علم حاصل ہے۔ آخر میں رسول اللہ صلی اللہ علیہ وسلم کو ہدایت فرمائی گئی ہے کہ اللہ کا فیصلہ آنے تک جو سختیاں بھی تبلیغ دین کی راہ میں پیش آئیں اُن کو صبر کے ساتھ برداشت کرتے چلے جائیں اور اُس بے صبری سے بچیں جو یونس علیہ السلام کے لیے ابتلا کی موجب بنی تھی۔ ','سید ابو اعلیٰ مودودیؒ -  تفہیم القرآن');
INSERT INTO "chapters_ur" VALUES(69,'حق ہونے والی',NULL,' نام : سورة کے پہلےہی لفظ کو اس کا نام قرار دیا گیا ہے۔ زمانۂ نزول : یہ بھی مکہ معظمہ کے ابتدائی دور کی نازل شدہ سورتوں میں سے ہے اور اس کے مضامین سے معلو م ہوتا ہے کہ یہ اُس زمانے میں نازل ہوئی تھی جب رسول اللہ صلی اللہ علیہ وسلم کی مخالفت تو شروع ہو چکی تھی، مگر اس نے ابھی زیادہ شدت نہ اختیار کی تھی۔ مسند احمد میں حضرت عمر ؓ کی روایت ہے کہ اسلام سے پہلے ایک روز میں رسول اللہ صلی اللہ علیہ وسلم کو ستانے کے لیے گھر سے نکلا مگر آپ مجھ سے پہلے مسجد حرام میں داخل ہو چکے تھے۔ میں پہنچا تو آپ نماز میں سورہ الحاقہ پڑھ رہے تھے ۔ میں آپؐ کے پیچھے کھڑا ہو گیا اور سننے لگا۔ قرآن کی شانِ کلام پر میں حیران ہو رہا تھا کہ میرے دل میں یکایک خیال آیا کہ یہ شخص ضرور شاعر ہے جیسا کہ قریش کہتے ہیں۔ فوراً ہی حضورؐ کی زبان سے یہ الفاظ ادا ہوئے”یہ ایک رسولِ کریمؐ کا قول ہے کسی شاعر کا قول نہیں ہے“۔ میں نے اپنے دل میں کہا شاعر نہیں تو پھر کاہن ہے۔ اُسی وقت زبان مبارک پر یہ الفاظ جاری ہوئے”اور نہ کسی کاہن کا قول ہے ۔ تم لوگ کم ہی غور کرتے ہو۔ یہ تو رب العالمین کی طرف سے نازل ہوا ہے“۔ یہ سن کر اسلام میرے دل میں گہرا اُتر گیا۔ حضرت عمرؓ کی اس روایت سے معلوم ہوتا ہے کہ یہ سورة اُن کے قبولِ اسلام سے بہت پہلے نازل ہو چکی تھی، کیونکہ اس واقعہ کے بعد بھی ایک مدت تک وہ ایمان نہیں لائے تھے اور وقتاً فوقتاً متعدد واقعات اُن کو اسلام سے متاثر کرتے رہے تھے، یہاں تک کہ اپنی بہن کے گھر میں اُن کے دل پر وہ آخری ضرب لگی جس نے ان کو ایمان کی منزل پر پہنچا دیا (تفصیلات کے لیے ملاحظہ ہو تفہیم القرآن، جلد سوم،دیباچہ سورہ مریم۔ جلدپنجم، دیباچہ سورہ واقعہ)۔ موضوع اور مضمون: اس کا پہلا رکوع آخرت کے بیان میں ہے، اور دوسرا رکوع قرآن کے مُنزَّل من اللہ اور محمد صلی للہ علیہ وسلم کے رسول ِ برحق ہونے کے بارے میں۔ پہلے رکوع کا آغاز اس بات سے ہوا ہے کہ قیامت کا آنا اور آخرت کا بر پا ہونا ایک ایسی حقیقت ہے جو ضرور پیش آکر رہنی ہے۔ پھر آیت۴ سے ۱۲ تک یہ بتایا گیا ہے کہ پہلے جن قوموں نے بھی آخرت کا انکار کیا ہے وہ آخر کار خدا کے عذاب کی مستحق ہو کر رہی ہیں۔ اس کے بعد آیت ۱۷ تک قیامت کا نقشہ کھینچا گیا ہے کہ وہ کس طرح بر پا ہوگی۔ پھر آیت۱۸ سے ۳۷ تک وہ اصل مقصد بیان کیا گیا ہے جس کے لیے اللہ تعالیٰ نے دنیا کی موجودہ زندگی کے بعد نوعِ انسانی کے لیے ایک دوسری زندگی مقدر فرمائی ہے۔ اس میں بتایا گیا ہے کہ اُس روز تمام انسان اپنے رب کی عدالت میں پیش ہوں گے جہاں اُن کا کوئی راز چھپا نہ رہ جائے گا۔ ہر ایک کا نامہ اعمال اس کے ہاتھ میں دے دیا جائے گا۔ جن لوگوں نے دنیامیں یہ سمجھتے ہوئے زندگی بسر کی تھی کہ ایک دن اُنہیں اپنے رب کو اپنا حساب دینا ہے، اور جنہوں نے دنیا کی زندگی میں نیک عمل کر کےاپنی آخرت کی بھلائی کے لیے پیشگی سامان کر لیا تھا، وہ اپنا حساب پاک دیکھ کر خوش ہو جائیں گے اور انہیں جنت کا ابدی عیش نصیب ہو گا۔ اس کے بر عکس جن لوگوں نے خدا کا حق مانا نہ بندوں کا حق ادا کیا، انہیں خدا کی پکڑ سے بچانے والا کوئی نہ ہوگا اور وہ جہنم کے عذاب میں مبتلا ہو جائیں گے ۔ دوسرے رکوع میں کفارِ مکہ کو خطاب کرتے ہوئے فرمایا گیا ہے کہ تم اس قرآن کو ایک شاعر اور کاہن کا کلام کہتے ہو، حالانکہ یہ اللہ کا نازل کردہ کلام ہے جو ایک رسولِ کریم کی زبان سے ادا ہو رہا ہے۔ رسول اس کلام میں اپنی طرف سے ایک لفظ گھٹانے یا بڑھا نے کا اختیار نہیں رکھتا ۔ اگر وہ اس میں اپنی من گھڑت کوئی چیز شامل کر دے تو ہم اُس کی رگِ گردن(یا رگِ دل)کاٹ دیں۔ یہ ایک یقینی برحق کلا م ہے اور جو لوگ اسے جھٹلائیں گے انہیں آخر کار پچھتانا پڑے گا۔ ','سید ابو اعلیٰ مودودیؒ -  تفہیم القرآن');
INSERT INTO "chapters_ur" VALUES(70,'عروج',NULL,' نام : تیسری آیت کے لفظ ذِی المَعَارِج سے ماخوذ ہے۔ زمانۂ نزول : اس کے مضامین شہادت دیتے ہیں کہ اس کا نزول بھی قریب قریب اُنہی حالات میں ہوا ہے جن میں سورہ الحاقّہ نازل ہوئی تھی۔ موضوع اور مضمون: اس میں اُن کفار کو تنبیہ اور نصیحت کی گئی ہے جو قیامت اور آخرت اور دوزخ اور جنت کی خبروں کا مذاق اڑاتے تھے اور رسول اللہ صلی اللہ علیہ وسلم کو چلینج دیتے تھے۔ کہ اگر تم سچے ہو اور تمہیں جُھٹلا کر ہم عذابِ جہنم کے مستحق ہو چکے ہیں تو لے آؤ وہ قیامت جس سے تم ہمیں ڈراتے ہو۔ اس سورة کی ساری تقریر اسی چیلنج کے جواب میں ہے۔ ابتدا میں ارشاد ہوا کہ مانگنے والا عذاب مانتا ہے۔ وہ عذاب انکار کرنے والوں پر ضرور واقع ہو کر رہے گا۔اور جب وہ واقع ہوگا تو اسے کوئی دفع نہ کر سکے گا، مگر وہ اپنے وقت پر واقع ہوگا۔ اللہ کے ہاں دیر ہے اندھیر نہیں ہے۔ لہٰذا اِن کے مذاق اڑانے پر صبر کرو۔ یہ اُسے دور دیکھ رہے ہیں اور ہم اسے قریب دیکھ رہے ہیں۔ پھر بتا یا گیا کہ قیامت جس کے جلدی لے آنے کا مطالبہ یہ لوگ ہنسی اور کھیل کے طور پر کر رہے ہیں کیسی سخت چیز ہے اور جب وہ آئے گی تو اِن مجرمین کا کیسا بُرا حشر ہو گا۔ اُس وقت یہ اپنے بیوی بچوں، اور اپنے قریب ترین رشتہ داروں تک کو فدیہ میں دے ڈالنے کے لیے تیار ہو جائیں گے تا کہ کسی طرح عذاب سے بچ نکلیں، مگر نہ بچ سکیں گے۔ اس کے بعد لوگوں کوآگاہ کیا گیا ہےکہ اُس روز انسانوںکی قسمت فیصلہ سراسر اُن ے عقیدے اور اخلاق و اعمال کی بنیا پر کیا جائےگا۔ جن لوگو نے دنیا میں حق سے منہ موڑا ہے اور مال سمیٹ سمیٹ کر اور سنیت سنیت کر رکھا ہے و ہ جہنم کے مستحق ہوں گے۔ ا ور جنہوں ن یہاں خدا کے عذاب کا خوف کیا ہے،آخرت کو مانا ہے، نماز کی پابندی کی ہے، اپنے مال سے خدا کے محتاج بندوں کا حق ادا کیاہے،بدکاریوں سےدامن پاک کر رکھا ہے، امانت میں خیانت نہیں کی ہے، عہدوپیمان اور قول و قرار کا پاس کیا ہے او ر گواہی میں راست بازی پر قائم رہے ہیں وہ جنت میں عزت کی جگہ پائیں گے۔ آخر میں مکہ کہ ان کفار کوجو رسول اللہ صلی اللہ علیہ وسلم کو دیکھ کر آپ کا مذاق اڑانے کے لیے چاروں طرف سے ٹوٹے پڑتے تھے، خبر دار کیا گیا ہے کہ اگر تم نہ مانو گے تو اللہ تعالیٰ تمہاری جگہ دوسرے لوگوں کو لے آئے گا ۔ اور رسول اللہ صلی اللہ علیہ وسلم کو تلقین کی گئی ہے کہ اِن کے تمسخُر کی پروا نہ کریں، یہ اگر قیامت ہی کی ذلت دیکھنے پر مُصر ہیں تو انہیں اپنے بیہودہ مشغلوں میں پڑا رہنے دیں، اپنا بُرا انجام یہ خود دیکھ لیں گے۔ ','سید ابو اعلیٰ مودودیؒ -  تفہیم القرآن');
INSERT INTO "chapters_ur" VALUES(71,'نوح',NULL,' نام : ”نوح“ اس سورة کا نام بھی ہے اور اس کے مضمون کا عنوان بھی، کیونکہ اس میں از اوّل تا آخر حضرت نوح علیہ السلام ہی کا قصہ بیان کیا گیا ہے۔ زمانۂ نزول : یہ بھی مکہ معظمہ کے ابتدائی در کی نازل شدہ سورتوں میں سے ہے، مگر اس کے مضمون کی داخلی شہادت اس امر کی نشان دہی کرتی ے کہ یہ اُس زمانے میں ناز ل ہوئی تھی جب رسول اللہ صلی اللہ علیہ وسلم کی دعوت و تبلیغ کے مقابلہ میں کفارِ مکہ کی مخالفت اچھی خاصی شدت اختیار کر چکی تھی۔ موضوع اور مضمون: اس میں حضرت نوحؑ کا قصہ گوئی کی خاطر بیان نہیں کیا گیا ہے، بلکہ اسے مقصود کارِ مکہ کو متنبہ کرنا ہے کہ تم محمد صلی اللہ علیہ وسلم کے ساتھ وہی رویہ اختیار کر رہے ہو جو حضرت نوحؑ کے ساتھ ان کی قوم نے اختیا ر کیا تھا، اور اس رویے سے اگرتم باز نہ آئے تو تمہیں بھی وہی انجام دیکھنا پڑے گا جو اُن لوگوں نےدیکھا۔ یہ بات پُوری سورة میں کہیں صاف الفاظ میں نہیں کہی گئی ہے، لیکن جس موقع پراور جن حالات میں یہ قصہ اہل مکہ کوسنایا گیاہے اُس پس منظر میں خود بخود یہ مضمون اِس سے مترشح ہوتا ہے۔ پہلی آیت میں بتایا گیا ہے کہ حضرت نوحؑ کو جب اللہ تعالیٰ نے رسالت کے منصب پر مامور فرمایا تھا اُس وقت کیا خدمت اُن کے سپرد کی گئی تھی۔ آیات ۲۔۴ میں مختصراً یہ بتا یا گیا ہے کہ اُنہوں نے اپنی دعوت کا آغاز کس طرح کیا اور اپنی قوم کے لوگوں کے سامنے کیا بات پیش کی۔ پھر مدتہائے درازتک دعوت و تبلیغ کی زحمتیں اُٹھانے کے بعد جو رُوداد حضرت نوح ؑ نے اپنے رب کے حضور پیش کی وہ آیات۵۔۲۰ میں بیان کی گئی ہے۔ اس میں وہ عرض کرتے ہیں کہ کس کس طرح انہوں نے اپنی قوم کو راہِ راست پر لانے کی کوشش کیں اور قوم نے ان کا مقابلہ کس ہٹ دھرمی سے کیا۔ اس کے بعد حضرت نوحؑ کی آخری گزارش آیات ۲۱۔۲۴ میں درج کی گئی ہے جس میں وہ اپنے رب سے عرض کرتے ہیں کہ یہ قوم میری بات قطعی طور پر رد کر چکی ہے، اس نے اپنی تکمیل اپنے رئیسوں کے ہاتھ میں دے دی ہے، اور اُنہوں نے بہت بڑا مکر کا جال پھیلا رکھا ہے، اب وقت آگیا ہے کہ اِن لوگوں نے ہدایت کی توفیق سلب کر لی جائے۔ یہ حضرت نوحؑ کی طرف سے کسی بے صبری کا مظاہرہ نہ تھا بلکہ صدیوں تک انتہائی صبر آزما حالات میں تبلیغ کا فریضہ انجام دینے کے بعد جب وہ اپنی قوم سے پوری طرح مایوس ہو گئے تو انہوں نے یہ رائے قائم کی کہ اب اس کے قوم کے راہ راست پر آنے کا کوئی امکان باقی نہیں ہے۔ یہ رائے ٹھیک ٹھیک اللہ تعالیٰ کے اپنے فیصلے کے مطابق تھی۔ چنانچہ اس کے متصلاً بعد آیت ۲۵ میں ارشاد ہوا ہے کہ اُس قوم پر اس کے کرتوتوں کی وجہ سے خدا کا عذاب نازل ہوگیا۔ آخری آیات میں نوح علیہ السلام کی وہ دعا درج کی گئی ہے جو انہوں نے عین نزولِ عذاب کے وقت اپنے رب سے مانگی تھی۔ اس میں وہ اپنے لیے اور سب اہلِ ایمان کے لیے مغفرت طلب کر تے ہیں، اور اپنی قوم کے کافروں کے بارے میں اللہ تعالیٰ سے عرض کرتے ہیں کہ ان میں سے کسی کو زمین پر بسنے کے لیے جیتا نہ چھوڑا جائے، کیونکہ ان کے اندر اب کوئی خیر باقی نہیں رہی ہے، ان کی نسل سے جو بھی اٹھے گا کافر اور فاجر ہی اٹھے گا۔ اس سورہ کا مطالعہ کرتے ہوئے حضرت نوحؑ کے قصّے کی وہ تفصیلات نگاہ میں رہنی چاہییں جو اس سے پہلے قرآن مجید میں بیان ہو چکی ہیں۔ ملاحظہ ہو الاعراف، آیات۵۹ تا۶۴۔ یونس،۷۱،۷۳۔ ہود،۲۵تا۴۹۔ المومنون، ۲۳تا ۳۱۔ الشُعراء،۱۰۵تا۱۲۲۔ العنکبوت،۱۴،۱۵۔الصّافات،۷۵تا۸۲۔ القمر،۹تا۱۶۔ ','سید ابو اعلیٰ مودودیؒ -  تفہیم القرآن');
INSERT INTO "chapters_ur" VALUES(72,'جن',NULL,' نام : ”الجن“اس سورہ کا نام بھی ہے اور اس کے مضامین کا عنوان بھی، کیونکہ اس میں جِنوں کے قرآن سُن کر جانے اور اپنی قوم میں اسلام کی تبلیغ کرنے کا واقعہ تفصیل کے ساتھ بیا ن کیا گیا ہے۔ زمانۂ نزول : بخاری اور مسلم میں حضرت عبداللہ بن عباس ؓ کی روایت ہے کہ رسول اللہ صلی اللہ علیہ وسلم اپنے چند اصحاب کے ساتھ بازارِ عُکاظ تشریف لے جا رہے تھے، راستے میں نَخلہ کے مقام پر آپ ؐ نے صبح کی نماز پڑھائی، اُس وقت جِنوں کا یک گروہ اُدھر سے گزر رہا تھا، تلاوت کی آواز سُن کر وہ ٹھیر گیا اور غور سے قرآن سنتا رہا۔ اِسی واقعہ کا ذکر اس سورہ میں کیا گیا ہے۔ اکثر مفسرین نے اس روایت کی بنا پر یہ سمجھا ہے کہ یہ حضور ؐ کے مشہور سفر طائف کا واوقعہ ہے جو ہجرت سے تین سال پہلےسن ۱۰ نبوی میں پیش آیا تھا۔ لیکن یہ قیاس متعدد وجوہ سے صحیح نہیں ہے۔ طائف کے اُس سفر میں جِنوں کے قرآن سننے کا جو واقعہ پیش آیا تھا اُس کا قصّہ سورہ اَحقاف آیات ۲۹۔۳۲ میں بیان کیا گیا ہے۔ اُن آیات پر ایک نگاہ ڈالنے ہی سے معلوم ہو جاتا ہے کہ اُس موقع پر جو جن قرآن مجید سُن کر ایمان لائے تھے وہ پہلے سے حضرت موسیٰ اور سابق کتبِ آسمانی پر ایمان رکھتے تھے۔اس کے برعکس اِس سورہ کی آیات۲۔۷ سے صاف ظاہر ہوتا ہے کہ اِس موقع پر قرآن سننے والے جِن مشرکین اور منکرین آخرت و رسالت میں سے تھے۔ پھر یہ بات تاریخ سے ثابت ہے کہ طائف کے اُس سفر کے متعلق ابنِ عباس فرما رہے ہیں کہ اِس میں چند صحابہ آپ کے ساتھ تھے۔ مزید برآں روایات اِس بات پر بھی متفق ہیں کہ اُس سفر میں جِنوں نے قرآن اُس وقت سنا تھا جب حضور ؐ طائف سے مکہ واپس تشریف لاتے ہوئے نخلہ میں ٹھیرے تھے۔ اور اِس سفر میں ابنِ عباس کی روایت کہ مطابق جِنوں کے قرآن سننے کا واقعہ اُس وقت پیش آیا جب آپ مکہ سے عُکاظ تشریف لے جارہے تھے۔ اِن وجوہ سے صحیح بات یہ معلوم ہوتی ہے کہ سورہ اَحقاف اور سورہ جن میں ایک ہی واقعہ کا ذکر نہیں کیا گیا ہے بلکہ یہ دو الگ واقعات تھے جو دو مختلف سفروں میں پیش آئے تھے۔ جہاں تک سورہ اَحقاف کا تعلق ہے ، اُس میں جس واققعہ کا ذکر کیا گیا ہے اس کے بارے میں روایات متفق ہیں کہ وہ سن ۱۰ نبوی کے سفر طائف میں پیش آیا تھا۔ اب رہا یہ سوال کہ یہ دوسرا واقعہ کس زمانے میں پیش آیا ، اس کا کوئی جواب ہمیں ابن عباس ؓ کی روایت سے نہیں ملتا ، نہ کسی اور تاریخی روایت سے یہ معلوم ہوتا ہے کہ رسول اللہ صلی اللہ علیہ وسلم صحابہ کی ایک جماعت کے ساتھ عُکاظ کے بازار میں کب تشریف لے گئے تھے۔ البتہ اس سورہ کی آیات ۸۔۱۰ پر غور کرنے سے محسوس ہوتا ہے کہ یہ نبوت کے ابتدائی دور کا واقعہ ہی ہوسکتا ہے۔اِن آیات میں بیان کیا گیا ہے کہ رسول اللہ صلی اللہ علیہ وسلم کی بِعثت سے پہلے جن عالمِ بالا کی خبریں معلوم کرنے کے لیے آسمان میں سُن گُن لینے کا کوئی نہ کوئی موقع پالیتے تھے ، مگر اس کے بعد یکا یک انہوں نے دیکھا کہ ہر طرف فرشتوں کے سخت پہلے لگ گئے ہیں اور شہابوں کی بارش ہورہی ہے جس کی وجہ سے کہیں اُن کو ایسی جگہ نہیں ملتی جہاں ٹھیر کر وہ کوئی بِھنَک پا سکیں۔ اِ سے اُن کو یہ معلوم کرنے کی فکر لاحق ہوئی کہ زمین میں ایسا کیا واقعہ پیش آیا ہے یا آنے والا ہے جس کے لیے یہ سخت انتظامات کیے گئے ہیں۔ غالباً اُسی وقت سے جنوں کے بہت سے گروہ اِس تلاش میں پھرتے رہے ہوں گے اور اُن میں سے ایک گروہ نے رسول اللہ صلی اللہ علیہ وسلم کی زبانِ مبارک سے قرآن سُن کر یہ رائے قائم کی ہوگی کہ یہی وہ چیز ہے جس کی خاطر جنوں پر عالمِ بالا کے تمام دروازے بند کر دیے گئے ہیں۔ جن کی حقیقت : اس سورے کا مطالعہ شروع کرنے سے پہلے یہ جان لینا ضروری ہے کہ جِنوں کی حقیقت کیا ہے تا کہ ذہن کسی اُلجھن کے شکارہ نہ ہوں۔ موجودہ زمانے کے بہت سے لوگ اِس غلط فہمی میں مبتلا ہیں کہ جِن کسی حقیقی چیز کا نام نہیں ہے بلکہ یہ بھی پُرانے زمانے کے اَوہام و خُرافات میں سے ایک بے بنیاد خیا ل ہے ۔ یہ رائے اُنہوں نے کچھ اِ س بنا پر قائم نہیں کی ہے کہ کائنات کی ساری حقیقتوں کو وہ جان چکے ہیں اور اُنہیں یہ معلوم ہو گیا ہے کہ جِن کہیں موجود نہیں ہیں۔ ایسے علم کا دعوٰی وہ خود بھی نہیں کر سکتے ۔ مگر اُنہوں نے بلا دلیل یہ فرض کر لیا ہے کہ کائنات میں بس وہی کچھ موجود ہے جو اُن کو محسوس ہوتا ہے۔ حالانکہ انسان کے محسوسات کا دائرہ اِس عظیم کائنات کی وسعت کے مقابلہ میں وہ نسبت بھی نہیں رکھتا جو سمندر کے مقابلے میں قطرے کی نسبت ہے۔ یہاں جو شخص یہ سمجھتا ہے کہ جو کچھ محسوس نہیں ہے وہ موجود نہیں ہے، اور جو موجود ہے اسے لازماً محسوس ہونا چاہیے، وہ دراصل خود اپنے ذہن کی تنگی کا ثبوت دیتا ہے ۔ یہ طرزِ فکر اختیا ر کر لیا جائے تو ایک جِن ہی کیا، انسان کسی ایسی حقیقت کو بھی نہیں مان سکتا جو براہِ راست اُس کے تجربے اور مشاہدے میں نہ آتی ہو اور اُس کے لیے خدا تک کا وجود قابلِ تسلیم نہیں ہے کجا کہ وہ کسی اور غیر محسوس حقیقت کا تسلیم کرے۔ مسلمانوں میں سے جو لوگ اِس طرز فکر سے متاثر ہیں، مگر قرآن کا انکار بھی نہیں کر سکتے انہوں نے جِن اور ابلیس اور شیطان کے متعلق قرآن کے صاف صاف بیانات کو طرح طرح کی تاویلات کا تختہ مشق بنا یا ہے ۔ وہ کہتے ہیں کہ اِس سے مراد کوئی ایسی پوشیدہ مخلوق نہیں ہے جو اپنا ایک مستقل وجود رکھتی ہو، بلکہ کہیں تو اس سے مراد انسان کی اپنی بہیمی قوتیں ہیں جنہیں شیطان کہا گیا ہے، اور کہیں اس سے مراد وحشی اور جنگلی اور پہاڑی قومیں ہیں، اور کہیں اس سے مراد وہ لوگ ہیں جو چُھپ چُھپ کر قرآن سنا کرتے تھے۔ لیکن قرآن مجید کے ارشادات اس معاملہ میں اس قدر صاف اور صریح ہیں کہ اِن تاویلات کے لیے اُن کے اندر کوئی ادنیٰ سی گنجائش بھی نہیں ہے۔ قرآن میں ایک جگہ نہیں ، بکثرت مقامات پر جن اور انسان کا ذکر اس حیثیت سے کیا گیا ہے کہ یہ دو الگ قسم کی مخلوقات ہیں۔ مثال کے طور پر ملاحظہ ہو سورہ اعراف، آیت ۳۷۔ہود،۱۱۹۔حٰم السجدہ، آیایت۲۵،۲۹۔ الاحقاف،۱۸۔الذّاریات،۵۶۔الناس،۶۔ اور سورہ رحمان تو پوری کی پوری اس پر ایسی صریح شہادت دیتی ہے کہ جنوں کو انسانوں کی کوئی قسم سمجھنے کی کوئی گنجائش ہی نہیں چھوڑتی۔ سورہ اعراف،آیت۱۲۔سورہ حجر،آیات۲۶۔۲۷، اور سورہ رحمان،آیات۱۴۔۱۵ میں صاف صاف بتایا گیا ہے کہ انسان کا مادہ تخلیق مٹی ہے اور جِنوں کا مادہ تخلیق آگ۔ سورہ حِجر آیت۲۷ میں صراحت کی گئی ہے کہ جن انسان سے پہلے پیدا کیے گئے تھے۔ اسی بات پر قصہ آدم و ابلیس شہادت دیتا ہے جو قرآن میںسات مقامات پر بیان ہوا ہے اور ہر جگہ اُس سے یہ ثابت ہوتا ہے کہ انسان کی تخلیق کے وقت ابلیس موجود تھا۔ نیز سورہ کہف آیت۵۰ میں بتایا گیا ہے کہ ابلیس جِنوں میں سے ہے۔ سورہ اعراف آیت ۲۷ میں بالفاظِ صریح یہ کہا گیا ہے کہ جن انسانوں کو دیکھتے ہیں مگر انسان اُن کو نہیں دیکھتے۔ سورہ حِجر آیات۱۶۔۱۸، سورہ صافّات، آیات۶۔۱۰، اور سورہ ملک آیت ۵ میں بتا یا گیا ہے کہ جِن اگرچہ عالمِ بالا کی طرف پرواز کر سکتے ہیں ، مگر ایک حدسے آگے نہیں جا سکتے ۔ اُس سے اوپر جانے کی کوشش کریں اور ملاء اعلیٰ کی باتیں سننا چاہیں تو انہیں روک دیا جاتا ہے ۔ چوری چُھپے سُن گُن لیں تو شہاب ثاقب ان کو مار بھگاتے ہیں۔ اِس سے مشرکین عرب کے اِس خیال کی تردید کی گئی ہے کہ جِن غیب کا علم رکھتے ہیں یا خدائی کے اَسرار تک انہیں کوئی رسائی حاصل ہے ۔ اِسی غلط خیال کی تردید سورہ سبا آیت ۱۴ میں بھی کی گئی ہے۔ سورہ بقرہ ، آیات۳۰۔۳۴ اور سورہ کہف آیت۵۰ سے معلوم ہوتا ہے کہ زمین کی خلافت اللہ تعالیٰ نے انسان کو دی ہے اور انسان جِنون سے افضل مخلوق ہیں۔ اگرچہ بعض غیر معمولی طاقتیں جنوں کو بھی بخشی گئی ہیں جن کی ایک مثال ہمیں سورہ نمل آیت ۷ میں ملتی ہے ، لیکن اسی طرح بعض طاقتیں حیوانات کو بھی انسان سے زیادہ ملی ہیں، اور وہ اس بات کی کوئی دلیل نہیں ہیں کہ جانوروں کو انسان پر فضیلت حاصل ہے۔ قرآن یہ بھی بتاتا ہے کہ جِن انسان کی طرح ایک با اختیار مخلوق ہے اور اُس کو طاعت و معصیت اور کفر و ایمان کا ویسا ہی اختیار دیا گیا ہے جیسا انسان کو دیا گیا ہے ۔ اِس پر ابلیس کا قصہ اور سورہ اَحقاف اور سورہ جن میں بعض جِنوں کے ایمان لانے کا واقعہ صریح دلالت کرتا ہے۔ قرآن میں بیسیوں مقامات پر یہ حقیقت بھی بیان کی گئی ہے کہ ابلیس نے تخلیق آدم کے وقت ہی یہ عزم کر لیا تھا کہ وہ نوع انسانی کو گمراہ کرنے کی کوشش کرے گااور اُسی وقت سے شیاطینِ جِن انسان کو گمراہ کرنے کے درپے ہیں، مگر وہ اپس پر مُسلّط ہو کر زبردستی اُس سے کوئی کام کر ا لینے کی طاقت نہیں رکھتے ، بلکہ وہ اُس کے دل میں وَسوَسے ڈالتے ہیں، اُس کو بہکاتے ہیں اور بدی و گمراہی کو اس کے سامنے خوشنما بنا کر پیش کرتے ہیں۔ مثال کے طور پر حسبِ ذیل آیات ملاحظہ ہوں : ں النساء، ۱۱۷تا ۱۲۰۔الاعراف،ااتا۱۷۔ابراہیم،۲۲۔الحجر۳۰تا۴۲۔النحل،۹۸تا۱۰۰۔بنی اسرائیل،۶۱تا۶۵۔ قرآن میں یہ بھی بتایاگیا ہے کہ مشرکینِ عرب زمانہ جاہلیت میں جِنوں کو خدا کا شریک ٹھیراتے تھے، اُن کی عبادت کرتے تھے، اور اُن کا نسب خدا سے ملاتے تھے۔ ملاحظہ ہو الانعام، آیت ۱۰۰۔سبا، آیات۴تا۴۱۔ الصّٰفّٰت،۱۵۸۔ اِن تفصیلات سے یہ بات بالکل واضح ہو جاتی ہے کہ جِن اپنا ایک مستقل خارجی وجود رکھتے ہیں اور وہ انسان سے الگ ایک دوسری ہی نوع کی پوشیدہ مخلوق ہیں۔ اُن کی پُر اسرار صفات کی وجہ سے جاہل لوگوں نے ان کی ہستی اور ان کی طاقتوں کے متعلق بڑے مبالغہ آمیز تصورات قائم کر رکھے ہیں، حتیٰ کہ ان کی پرستش تک کر ڈالی گئی ہے، مگر قرآن نے ان کی اصل حقیقت پوری طرح کھول کر بیان کر دی ہے جس سے معلوم ہوجاتا ہے کہ وہ کیا ہیں اور کیا نہیں ہیں۔ موضوع اور مباحث: اس سورہ میں پہلی آیت سے لے کر آیت ۱۵ تک یہ بتایا گیا ہے کہ جِنوں کے ایک گروہ نے قرآن سُن کر اُس کا کیا اثر لیا اور پھر واپس جا کر اپنی قوم کے دوسرے جِنوں سے کیا کیا باتیں کہیں۔ اِس سلسلے میں اللہ تعالیٰ نے اُن کی ساری گفتگو نقل نہیں کی ہے، بلکہ صرف وہ خاص خاص باتیں نقل فرمائی ہیں جو قابلِ ذکر تھیں۔ اسی لیے طرزِ بیان ایک مسلسل گفتگو کا سا نہیں ہے، بلکہ اُن کے مختلف فقروں کو اِس طرح نقل کیا گیا ہے کہ انہوں نےیہ کہا اور یہ کہا۔جِنوں کی زبان سے نکلے ہوئے اِن فقروں کو اگر آدمی بغور پڑھے تو بآسانی یہ بات سمجھ میں آجاتی ہے کہ اُن کے ایمان لانے کے اِس واقعے اور اپنی قوم کے ساتھ اُن کی اِس گفتگو کا ذکر قرآن میں کس غرض کے لیے کیا گیا ہے۔ ہم نے اپنے حواشی میں ان کے اقوال کی جو تشریحات کی ہیں وہ اس کا مقصد سمجھنے میں مزید مددگار ہوں گی۔ اس کے بعد آیت ۱۶ سے ۱۸ تک لوگوں کو فہمائش کی گئی ہے کہ وہ شرک سے باز آجائیں اور راہِ راست پر ثابت قدمی کے ساتھ چلیں تو ان پر نعمتوں کی بارش ہوگی ورنہ اللہ کی بھیجی ہوئی نصیحت سے مُنہ موڑنے کا انجام یہ ہو گا کہ وہ سخت عذاب سے دو چار ہونگے۔ پھر آیت ۱۹ سے ۲۳ تک کفارِ مکّہ کو اس بات پر ملامت کی گئی ہے کہ جب اللہ کا رسول دعوت الی اللہ کی آواز بلند کرتا ہے تو وہ اِس پر ٹوٹ پڑنے کے لیے تیار ہوجاتے ہیں، حالانکہ رسول کا کام صرف اللہ کے پیغامات پہنچا دینا ہے۔ پھرآیات۲۴۔۲۵ میں کفار کو متنبہ کیا گیا ہے کہ آج وہ رسول کو بے یاومددگار دیکھ کر اسے دبا لینے کی کوشش کر رہے ہیں، لیکن ایک وقت آئے گا جب انہیں معلوم ہو جائے گا کہ اصل میں بے یار و مدد گار کون ہے۔ وہ وقت دور ہے یا قریب، رسول کو اس کا علم نہیں ہے، مگر بہرحال اسے آنا ضرور ہے۔ آخر میں لوگوں کو بتایا گیا ہے کہ عالم الغیب صرف اللہ تعالیٰ ہے ۔ رسول کو صرف وہ علم حاصل ہوتا ہے جو اللہ تعالیٰ اسے دینا چاہتا ہے۔ یہ علم اُن امور سے متعلق ہوتا ہے جو فرائض رسالت کی انجام دہی کے لیے ضروری ہوتے ہیں۔ اور یہ علم ایسے محفوظ طریقہ سے دیا جاتا ہے جس میں کسی بیرونی مداخلت کا امکان نہیں ہوتا۔ ','سید ابو اعلیٰ مودودیؒ -  تفہیم القرآن');
INSERT INTO "chapters_ur" VALUES(73,'کپڑا لپیٹنے والا',NULL,' نام : پہلی آیات کے لفظ المُزّمِّل کو اس سُورہ کا نام قرار دیا گیا ہے۔ یہ صرف نام ہے اس کے مضامین کا عنوان نہیں ہے۔ زمانۂ نزول : اس سورۃ کے دو رکوع دو الگ زمانوں میں نازل ہوئے ہیں۔ پہلا رکُوع بالاتفاق مکّی ہے۔ اس کے مضامین اوراحادیث کی روایات دونوں سے یہی بات معلوم ہوتی ہے۔ رہا یہ سوال کہ مکّی زندگی کے کس دور میں نازل ہوا ہے، اِس کا جواب ہمیں روایات سے تو نہیں ملتا، لیکن اِس رکوع کے مضامین کی داخلی شہادت اِس کا زمانہ متعین کرنے میں بڑی مدد دیتی ہے: اوّلاً، اِس میں رسول اللہ صلی اللہ علیہ وسلم کو ہدایت فرمائی گئی ہے کہ آپ راتوں کواٹھ کر اللہ کی عبادت کیا کریں تا کہ آپ کے اندر نبوّت کے بارِ عظیم کو اٹھانے اوراس کی ذمہ داریاں ادا کرنے کی قوت پیدا ہو۔ اس سے معلوم ہوا کہ یہ حکم حضورؐ کی نبوت کے ابتدائی دور ہی میں نازل ہوا ہوگا جبکہ اللہ تعالیٰ کی طرف سے اِس منصب کے لیے آپ کی تربیت کی جا رہی تھی۔ ثانیاً، اِس میں حکم دیا گیا ہے کہ نمازِ تہّجد میں آدھی آدھی رات یا اس سے کچھ کم و بیش قرآن مجید کی تلاوت کی جائے۔ یہ ارشاد خود بخود اِس بات پر دلالت کرتا ہے کہ اُس وقت قرآن مجید کا کم از کم اتنا حصّہ نازل ہو چکا تھا کہ اس کی طویل قرأت کی جا سکے۔ ثالثاً ، اِس میں رسول اللہ صلی اللہ علیہ وسلم کو مخالفین کی زیادیتوں پر صبر کی تلقین کی گئی ہے اور کفّارِ مکّہ کو عذاب کی دھمکی دی گئی ہے۔ اِس سے ظاہر ہوتا ہے کہ یہ رکوع اُس زمانے میں نازل ہوا ہے جب رسول اللہ صلی اللہ علیہ وسلم اسلام کی عَلانیہ تبلیغ شروع کر چکے تھے اور مکّہ میں آپ کی مخالفت زور پکڑ چکی تھی۔ دوسرے رکوع کے متعلق اگرچہ بہت سے مفسّرین نے یہ خیا ل ظاہر کیا ہے کہ وہ بھی مکہ ہی میں نازل ہوا ہے، لیکن بعض دوسرے مفسرین نے اسے مدنی قرار دیا ہے، اور اس رکوع کے مضامین سے اِسی خیا ل کی تائید ہوتی ہے، کیونکہ اس میں قتال فی سبیل اللہ کا ذکر ہے، اور ظاہر ہے کہ مکہ میں اس کا کوئی سوال پیدا نہ ہوتا تھا، اور اس میں فرض زکوٰۃ ادا کرنے کا بھی حکم دیا گیا ہے، اور یہ بات ثابت ہے کہ زکوٰۃ ایک مخصوص شرح اور نصاب کے ساتھ مدینہ میں فرض ہوئی ہے۔ موضوع اور مضامین: پہلی سات آیات میں رسول اللہ صلی علیہ وسلم کو حکم دیا گیا ہے کہ جس کارِ عظیم کا بار آپ پر ڈالا گیا ہے اس کی ذمّہ داریاں سنبھالنے کے لیے آپ اپنے آپ کو تیار کریں، اور اُس کی عملی صورت یہ بتائی گئی ہےکہ راتوں کو اُٹھ کر آپ آدھی آدھی رات، یا اس سے کچھ کم و بیش نماز پڑھا کریں۔ آیت ۸ سے ۱۴ تک حضورؐ کو تلقین کی گئی ہے کہ سب سے کٹ کر اُس خدا کے ہور میں جو ساری کائنات کا مالک ہے۔ اپنے سارے معاملات اُسی کے سُپرد کرکے مطمئن ہو جائیں۔ مخالفین جو باتین آپ کے خلاف بنا رہے ہیں ان ر صبر کریں، اُن کے منہ نہ لگیں اور ان کا معاملہ خدا پر چھوڑ دیں کہ وہی اُن سے نمٹ لے گا۔ اس کے بعد آیات ۱۴ سے ۱۹ تک مکّہ کے اُن لوگوں کو جو رسول اللہ صلی اللہ علیہ وسلم کی مخالفت کر رہے تھے، مُتَنَبِّہ کیا گیا ہے کہ ہم نے اُسی طرح تمہاری طرف ایک رسول بھیجا ہے جس طرح فرعون کی طرف بھیجا تھا، پھر دیکھ لو کہ جب فرعون نے اللہ کے رسول کی بات نہ مانی تو وہ کس انجام سے دو چار ہوا۔ اگر فرض کر لو کہ دنیا میں تم پر کوئی عذاب نہ آیا تو قیامت کے روز تم کفر کی سزا سے کیسے بچ نکلو گے؟ یہ پہلے رکوع کے مضامین ہیں۔ دوسرا رکوع حضرت سعید بن جُبیَر کی روایت کے مطابق اُس کے دس سال بعد نازل ہوا اور اس میں نمازِ تہجّد کے متعلق اُس ابتدائی حکم کے اندر تخفیف کر دی گئی جو پہلے رکوع کے آغاز میں دیا گیا تھا ۔ اب یہ حکم دیا گیا کہ جہاں تک تہجّد کی نماز کا تعلق ہے وہ تو جتنی بآسانی پڑھی جا سکے پڑھ لیا کرو، لیکن مسلمانوں کو اصل اہتمام جس چیز کا کرنا چاہیے وہ یہ ہے کہ پنجوقتہ فرض نماز پُوری پا بندی کے ساتھ قائم رکھیں، فریضہ زکوٰۃ ٹھیک ٹھیک ادا کرتے رہیں اور اللہ کی راہ میں اپنا مال خلوصِ نیت کے ساتھ صرف کریں۔ آخر میں مسلمانوں کو تلقین کی گئی ہے کہ جو بھلائی کے کام تم دنیا میں انجام دو گے وہ ضائع نہیں جائیں گے بلکہ اُن کی حیثیت اُس سامان کی سی ہے جو ایک مسافر اپنی مستقل قیام گاہ پرپہلے سے بھیج دیتا ہے۔ اللہ کے ہاں پہنچ کر تم وہ سب کچھ موجود پاِؤ گے جو دنیا میں تم نے آگے روانہ کیا ہے ، اور پیشگی سامان نہ صرف یہ کہ اُس سامان سے بہت بہتر ہے جو تمہیں دنیا میں چھوڑ جانا ہے، بلکہ اللہ کے ہاں تمہیں اپنے بھیجے ہوئے اصل مال سے بڑھ کر بہت بڑا اجر بھی ملے گا۔ ','سید ابو اعلیٰ مودودیؒ -  تفہیم القرآن');
INSERT INTO "chapters_ur" VALUES(74,'کپڑا اوڑھنے والا','',' نام : &nbsp;پہلی آیت کے لفظ الْمُدَّثِّر کو اِس سورہ کا نام قرار دیا گیا ہے۔ یہ بھی صرف نام ہے، مضامین کا عنوان نہیں ہے۔ زمانۂ نزول : &nbsp; اس کی پہلی سات آیات مکّہ معظمہ کے بالکل ابتدائی دور کی نازل شدہ ہیں۔ بعض روایات جو بخاری، مسلم،ترمذی اور مسند احمد وغیرہ ہیں حضرت جابر بن عبس اللہ سے منقول ہیں اُن میں تو یہاں تک کہا گیا ہے کہ یہ قرآن مجید کی اوّلین آیات ہیں جو رسول صلی اللہ علیہ وسلم پر&nbsp;نازل ہوئیں۔ لیکن امّت میں یہ بات قریب قریب با لاتفاق مسلم ہیں کہ پہلی وحی جو حضورؐ پر نازل ہوئی وہ اِقْرَأْ بِاسْمِ رَبِّکَ الَّذِیْ خَلَقَ سے مَا لَمْ یَعْلَمْ تک ہے۔ البتہ صحیح روایات سے جو کچھ ثابت ہے وہ یہ ہے کہ اِس وحی کے بعد کچھ مدّت تک رسول اللہ صلی اللہ علیہ وسلم پر کوئی وحی نازل نہیں ہوئی، پھر اس وقفہ کے بعد جب از سرِ نو نزول وحی کا سلسلہ شروع ہوا تو اُس کا آغاز سُورہ مُدَّثِّر کی اِنہی آیات سے ہوا تھا۔ امام زُہرْیؒ اِس کی تفصیل یُوں بیان کرتے ہیں: ”ایک مدت تک رسول اللہ صلی اللہ علیہ وسلم پر وحی&nbsp;کا نزول بند رہا اور اُس زمانہ میں آپ پر اس قدر شدید غم کی کیفیت طاری رہی کہ بعض اوقات آپ پہاڑوں کی چوٹیوں پر چڑھ کر اپنے آپ کو گرا دینے کے لیے آمادہ ہو جاتے تھے۔ لیکن جب کبھی آپ کسی چوٹی کے کنارے پر&nbsp;پہنچتے جبریل علیہ السّلام نمودار ہو کر آپ سے کہتے کہ آپ اللہ کے نبی ہیں۔ اس سے آپ کے دل کو سکون حاصل ہو جاتا تھا اور وہ اضطراب کی کیفیت دُور ہو جاتی تھی۔“(ابن جریر) ”رسول اللہ صلی اللہ علیہ وسلم نے فَتْرۃُ الوحی(وحی بند رہنے کے زمانے) کا ذکر کرتے ہوئے بیان فرمایا: ایک روز میں راستے سے گزر رہا تھا۔ یکایک میں نے آسمان اور زمین کے درمیان ایک کرسی پر بیٹھا ہوا ہے۔ میں یہ دیکھ کر سخت&nbsp;دہشت زدہ ہو گیا اور گھر پہنچ کر میں نے کہا مجھے اُڑھا ؤ، مجھے اُڑھاؤ۔ چنانچہ گھر والوں نے مجھ پر لحاف (یا کمبل) اُڑھادیا۔ اُس وقت اللہ نے وحی نازل کی یٰاَیُّھَا الْمُدَّثِّرُ ....۔ پھر لگا تار مجھ پر وحی کا نزول شروع ہو گیا“۔(بخاری۔ مسلم۔مُسنَد احمد۔ ابن جریر)۔ سوُرۃ کا باقی ماندہ حصہ آیت ۸ سے آخر تک اُس وقت نازل ہوا جب اسلام کی عَلانیہ تبلیغ شروع ہو جانے کے بعد مکّہ میں پہلی مرتبہ حج کا موقع آیا۔ اس کا مفصل واقعہ سیرت ابنِ ہشام میں بیان کیا گیا ہے جسے آگے چل کر ہم نقل کریں گے۔ موضوع اور مضمون: جیسا کہ اوپر بیان کیا گیا ہے ، رسول اللہ صلی اللہ علیہ وسلم پر پہلی وحی جو بھیجی گئی تھی وہ سورہ عَلَق کی ابتدائی پانچ آیات پر مشتمل تھی جس میں صرف یہ فرمایاگیا تھا کہ : ”پڑھو اپنے رب کے نام سے جس نے پیدا کیا ، ایک لوتھڑے سے انسان کی تخلیق کی۔ پڑھو، اور تمہارا رب بڑا کریم ہے، جس نے قلم سے علم سکھایا، انسان کو وہ علم دیا ہے جسے وہ نہ جانتا تھا۔“ یہ نزول وحی کا پہلا تجربہ تھا جو اچانک حضور ؐ کو پیش آیا تھا ۔ اِس پیغام میں آپ کو یہ نہیں بتایا گیا تھا کہ آپ کس کا رِ عظیم پر مامور ہوئے ہیں اور آگے کیا کچھ آپ کو کرنا ہے، بلکہ صرف ایک ابتدائی تعارف کرا کے آپ کو کچھ مدّت کے لیے چھوڑ دیا گیا تھا تا کہ آپ کی طبیعت پر جو شدید بار اِس پہلے تجر بے سے پڑا ہے اس کا (اثر دُور ہو جائے اور آپ ذہنی طور پر آئندہ وحی وصول کرنے اور نبوت کے فرائض سنبھالنے کے لیے تیار ہوجائیں۔ اس وقفہ کے بعد جب دوبارہ نزولِ وحی کا سلسلہ شروع ہوا تو اِس سورہ کی ابتدائی سات آیتیں نازل کی گئیں اور ان میں پہلی مرتبہ آپ کو یہ حکم دیا گیا کہ آپ اٹھیں اور خلقِ خدا کو اُس روش کے انجام سے ڈرائیں جس پر وہ چل رہی ہے، اور اِس دنیا میں ، جہاں دوسروں کی بڑائی کے ڈنکے بج رہے ہیں، خدا کی بڑائی کا اعلان کریں۔ اس کے ساتھ آپ کو ہدایت فرمائی گئی کہ اب جو کام آپ کو کرنا ہے اس کا تقاضہ یہ ہے کہ آپ کی زندگی ہر لحاظ سے انتہائی پا کیزہ ہو اور آپ تمام دنیوی فائدوں سے قطع نظر کر کے کامل اخلاص کے ساتھ خلقِ خدا کی اصلاح کا فریضہ انجام دیں۔ پھر آخڑی فقرے میں آپ کی تلقین کی گئی کہ اِس فریضہ کی انجام&nbsp;دہی میں جو مشکلات اور مصائب بھی پیش آئیں اُن پر آپ اپنے رب کی خاطر صبر کریں۔ اس فرمانِ الہٰی کی تعمیل میں جب رسول اللہ صلی اللہ علیہ وسلم نے اسلام کی تبلیغ شروع کی اور قرآن مجید کی پے در پے نازل ہونے والی سورتوں کو آپ نے سنانا شروع کیا تو مکّہ میں کھلبلی مچ گئی اور مخالفتوں کا&nbsp;ایک طوفان اٹھ کھڑا ہوا۔ چند مہینے اِس حال پر گزرے تھے کہ حج کا زمانہ آگیا اور مکّہ کے لوگوں کو یہ فکر لاحق ہو ئی کہ اِس موقع پر تمام عرب سے حاجیوں کے قافلے آئیں گے ، اگر محمد صلی اللہ علیہ وسلم نے اِن قافلوں کی قیام گاہوں پر جا جا کر آنے والے حاجیوں سے ملاقاتیں کیں اور حج کے اجتماعات میں جگہ جگہ کھڑے ہو کر قرآن&nbsp;جیسا بے نظیر اور مؤثر کلام سنانا شروع کر دیا، تو عرب کےہر گوشے تک ان کی دعوت پہنچ جائے گی اور نہ معلوم کون کون اِس سے متاثر ہو جائے۔ اس لیے قریش کے سرداروں نےایک کانفرنس کی جس میں طے کیا گیا&nbsp;کہ حاجیوں کے آتے ہی اُن کے اندر رسول اللہ صلی اللہ علیہ وسلم کے خلاف پر وپیگنڈا شروع کر دیا جائے۔اِس پر اتفاق ہو جانے کے بعد ولید بن مُغیرہ نے حاضرین سے کہا کہ اگر آپ لوگوں نے محمد صلی اللہ علیہ وسلم، کے متعلق مختلف باتیں لوگوں سے کہں تو ہم سب کا اعتبار جاتا رہے گا۔ اس لیے کوئی ایک بات طے کر لیجیے جسے سب با لاتفاق کہیں۔ کچھ لوگوں نے کہا ہم محمد صلی اللہ علیہ وسلم کو کاہن کہیں گے۔ ولید نے کہا، نہیں ، خدا کی قسم وہ کاہن نہیں ہیں، ہم نے کاہنوں کو دیکھا ہے، جیسی باتیں وہ گُنگناتے ہیں اور جس طرح کے فقرے وہ جوڑتے ہیں، قرآن کو ان سے&nbsp;کوئی&nbsp;دُور کی نسبت بھی نہیں ہے۔ کچھ اور لوگ بولے، انہیں مجنون کہا جائے۔ ولید نے کہا وہ مجنون بھی نہیں ہیں۔ ہم نے دیوانے اور پاگل دیکھے ہیں۔ اس حالت میں آدمی جیسی بہکی بہکی باتیں اور الٹی سیدھی حرکات کرتا ہے وہ کسی سے چھپی ہوئی نہیں ہیں۔ کون باور کرے گا کہ محمدؐ جو کلام پیش کرتے ہیں وہ دیوانگی کی بڑ ہے یا جنون کے دورے میں آدمی&nbsp;یہ باتیں کر سکتا ہے؟ لوگوں نے کہا اچھا تو پھر ہم شاعر کہیں گے۔ ولید نے کہا ، وہ شاعر بھی نہیں ہیں۔ ہم شعر کی ساری اقسام سے واقف ہیں۔ اِس کلام پر شاعری کی قسم کا اطلاق بھی نہیں ہو سکتا۔ لوگ بولے، تو ان کو ساحر کہا جائے۔ ولید نے کہا وہساحر بھی نہیں ہیں۔ جادوگروں کو ہم جانتے ہیں اور اپنے جادو کے لیے جو طریقے وہ اختیار کرتے ہیں ان سے بھی ہم واقف ہیں۔ یہ بات بھی محمد ؐ پر چسپا ں نہیں ہوتی ۔ پھر&nbsp;ولید نے کہا اِن باتوں میں سے جو بات بھی کرو گے لوگ اس کو نارواالزام سمجھیں گے۔ خدا کی قسم اس کلام میں بڑی حلاوت ہے، اس کی جڑ بڑی گہری اور اس کی ڈالیاں بڑی ثمر دار ہیں۔ اس پر ابو جہم ولید کے سر ہوگیا اور اس نے کہا تمہاری قوم تم سے راضی نہ ہو گی جب تک تم محمد ؐ کے بارے میں کوئی بات نہ کہو۔ اس نے کہا اچھا مجھے سوچ لینے دو۔ پھر سوچ سوچ کر بولا قریب ترین بات جو کہی جا سکتی ہے وہ یہ ہے کہ تم عرب کے لوگوں سے کہو یہ شخص جادوگر ہے۔ یہ ایسا کلام پیش کر رہا ہے جو آدمی کو اُس کے بات، بھائی، بیوی بچو ں اور سارے خاندان سے جدا کردیتا ہے۔ ولید کی اِس بات کو سب نے قبول کر لیا۔ پھر ایک منصوبے کے مطابق حج کے زمانے میں قریش کے وفود حاجیوں کے درمیان پھیل گئے اور انہوں نے آنے والے زائرین کو خبر دار کرنا شروع کیا کہ یہاں ایک ایسا شخص اُٹھ کھڑا ہوا ہے جو بڑا جادوگر ہے اور اس کا جادو خاندانوں میں تفریق ڈال دیتا ہے، اُس سے ہوشیار رہنا۔ مگر اس کا نتیجہ یہ ہوا کہ قریش نے رسول اللہ صلی اللہ علیہ وسلم کا نام خود ہی سارے عرب میں مشہور کر دیا(سیرۃ ابن ہشام، جلد اوّل، صفحہ ۲۸۸۔۲۸۹۔ اِس قصے کا یہ حصّہ کہ ابو جہل کے اصرار پر ولید نے یہ بات کہی تھی عِکْرِمہ کی روایت سے ابن جریرنے اپنی تفسیر میں نقل کیا ہے)۔ یہی واقعہ ہے جس پر اس سُورہ کے دوسرے حصّہ میں تبصرہ کیا گیا ہے ۔ اُس کے مضامین کی ترتیب یہ ہے: آیت ۸ سے ۱۰ تک منکرینِ حق کو خبر دار کیا گیا ہے کہ آج جو کچھ وہ کر رہے ہیں ا س کا بُرا انجام وہ قیامت کے روز دیکھ لیں گے۔ آیت ۱۱ سے ۲۶ تک ولید بن مُغِیرہ کا نام لیے بغیر یہ بتایا گیا ہے کہ اللہ تعالیٰ نے اِس شخص کو کیا کچھ نعمتیں دی تھیں اور اُنکا جواب اِس نے کیسی حق دشمنی کے ساتھ دیا ہے۔ اس سلسلے میں اُص کی ذہنی کشمکس کی پُوری تصویر کھینچ دی گئی ہے کہ ایک طرف دل میں وہ محمد صلی اللہ علیہ وسلم اور قرآن کی صداقت کا قائل ہو چکا تھا، مگر دوسری طرف اپنی قوم میں اپنی ریاست ووجاہت کو بھی خطرے میں نہ ڈالنا چاہتا تھا ، اس لیے نہ صرف یہ کہ وہ ایمان لانے سے باز رہا ، بلکہ کافی دیر تک اپنے ضمیر سے لڑنے جھگڑنے کے بعد آخر کار یہ بات بنا کر لا یا کہ خلق ِ خدا کو اِس کلام پر ایمان لانے سے باز رکھنے کے لیے اِسے جادو قرار دینا چاہیے۔ اُس کی اِس صریح بد باطنی کا بے نقاب کر کے فرمایاگیا ہے کہ اپنے اِس کرتوت کے بعد بھی یہ شخص چاہتا ہے کہ اِسے مزید انعامات سے نوازا جائے، حالانکہ اب یہ انعام کا نہیں بلکہ دوزخ کا سزاوار ہو چکا ہے۔ اس کے بعد آیت ۲۷ سے۴۸ تک دوزخ کی ہولناکیوں کا ذکر کیا گیا ہے اور یہ بتایا گیا ہے کہ کس اخلاق اور کر دار کے لوگ اس کے مستحق ہیں۔ پھر آیات ۴۹۔۵۳ میں کفار کے مرض کی اصل جڑ بتا دی گئی ہے کہ وہ چونکہ آخرت سے بے خوف ہیں اور اِسی دنیا کی زندگی کو سب کچھ سمجھتے ہیں، اس لیے وہ قرآن سے اس طرح بھاگتے ہیں جیسے شیر سے ڈر کر جنگلی گدھے بھاگے جا رہے ہوں ، اور ایمان لانے کے لیے طرح طرح کی غیر معقول شرطیں پیش کرتے ہیں، حالانکہ خٔاہ ان کی کوئی شرط بھی پوری کر دی جاِئے، انکارِ آخرت کے ساتھ وہ ایمان کی راہ پر ایک قدم بھی نہیں بڑھ سکتے۔ &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;آخر میں صاف صاف فرما دیا گیا ہے کہ خدا کو کسی کے ایمان کی کوئی ضروت نہیں پڑ گئی ہے کہ وہ اس کی شرطین پُوری کرتا پھرے۔ قرآن کو عام نصیحت ہے جو سب کے سامنے پیش کر دی گئی ہے۔ اب جس کا جی چاہے اس کو قبول کر لے۔ خدا اِس کا مستحق ہے کہ لوگ اُس کی نافرمانی سے ڈریں، اور اسی یہ شان ہے کہ جو شخص بھی تقوٰی اور خدا ترسی کو رویّہ اختیار کر لے اسے وہ معاف کر دیتا ہے خواہ وہ پہلے کتنی ہی نافرمانیا ں کر چکا ہو۔ ','سید ابو اعلیٰ مودودیؒ -  تفہیم القرآن');
INSERT INTO "chapters_ur" VALUES(75,'قیامۃ',NULL,' نام : پہلی ہی آیت کے لفظ اَلْقِیٰمَۃ کو اس سُورہ کا نام قرار دیا گیا ہے ، اور یہ صرف نام ہی نہیں ہے۔بلکہ اس سُورہ کا عنوان بھی ہے۔ کیونکہ اس میں قیامت ہی پر بحث کی گئی ہے۔ زمانۂ نزول : اگرچہ کسی روایت سے اِس کا زمانہ نزول معلوم نہیں ہوتا ، لیکن اِس کے مضمون میں ایک داخلی شہادت ایسی موجود ہے جس سے معلوم ہوتا ہے کہ یہ بالکل ابتدائی زمانہ کی نازل شدہ سورتوں میں سے ہے۔ آیت ۱۵ کے بعد یکایک سلسلہ کلام توڑ کر رسول اللہ صلی اللہ علیہ وسلم سے فرمایا جاتا ہے کہ”اس وحی کو جلدی جلدی یاد کرنے کے لیے اپنی زبان کو حرکت نہ دو، اِس کو یاد کرا دینا اور پڑھوا دینا ہمارے ذمّہ ہے، لہٰذا جب ہم اِسے پڑھ رہے ہوں اس وقت تم اس کی قرأت کو غور سے سنتے رہو، پھر اس کا مطلب سمجھا دنیا بھی ہمارے ہی ذمّہ ہے“۔ اس کے بعد آیت ۲۰ سے پھر وہی مضمون شروع ہوجاتا ہے جو ابتدا سے آیت ۱۵ تک چلا آ رہا تھا۔ یہ جملہ معترضہ اپنے موقع و محل سے بھی اور روایات کی رُو سے بھی اِس بنا پر دورانِ کلام میں وارد ہوا ہے کہ جس وقت حضرت جبریلؑ یہ سورہ حضورؐ کو سنا رہے تھے اُس وقت آپ اِس اندیشے سے کہ کہیں بعد میں بھول نہ جائیں، اس کے الفاظ اپنی زبان مبارک سے دُہراتے جا رہے تھے۔ اِس سے معلوم ہوتا ہے کہ یہ واقعہ اُس زمانہ کا ہے جب آنحضرت صلی اللہ علیہ وسلم کو نزولِ وحی کا نیا نیا تجربہ ہو رہا تھا اور ابھی آپ کو وحی اخذ کرنے کی عادت اچھی طرح نہیں پڑی تھی۔ قرآن مجید میں اس کی دو مثالیں اور بھی ملتی ہیں۔ ایک سورہ طٰہٰ میں جہاں رسول اللہ صلی اللہ علیہ وسلم سے فرمایا گیا ہے وَلاَ تَجْعَلْ بِالْقُرْاٰنِ مِنْ قَبْلِ اَنْ یُّقْضٰیٓ اِلَیْکَ وَحْیُہٗ، ”اور دیکھو، قرآن پڑھنے میں جلدی نہ کیا کرو جب تک کہ تمہاری طرف اس کی وحی تکمیل کو نہ پہنچ جائے“(آیت۱۱۴)۔دوسرے سورہ اعلیٰ میں جہاں حضور ؐ کو اطمینان دلا یا گیا ہے کہ سَنُقْرِ ئُکَ فَلاَ تَنْسیٰ،”ہم عنقریب تم کو پڑھو ا دینگے پھر تم بھولو گے نہیں“(آیت۶)۔ بعد میں جب حضور ؐ کو وحی اخذ کرنے کی اچھی طرح مشق ہو گئی تو اِس طرح کی ہدایات دینے کی کوئی ضرورت باقی نہیں رہی۔ اسی لیے قرآن میں اِن تین مقامات کے سوا اِس کی کوئی اور مثال نہیں ملتی۔ موضوع اور مضمون: یہاں سے آخرِ کلام اللہ تک جو سُورتیں پائی جاتی ہیں ان میں سے اکثر اپنے مضمون اور انداز بیان سے اُس زمانہ کی نازل شدہ معلوم ہوتی ہیں جب سورہ مُدثِّر کی ابتدائی سات آیات کے بعد نزول قرآن کا سلسلہ بارش کی طرح شروع ہوا اور پے در پے نازل ہونے والی سُورتوں میں ایسے پُرزور اور مؤثر طریقہ سے نہایت جامع اور مختصر فقروں میں اسلام اور اس کے بنیادی عقائد اور اخلاقی تعلیمات کو پیش کیا گیا اور اہل مکہ کو ان کی گمراہیوں پر متنبہ کیا گیا جس سے قریش کے سردار بَوکھلا گئے اور پہلا حج آنے سے پہلے حضورؐ کو زک دینے کی تدبیریں سوچنے کے لیے انہوں نے وہ کانفرنس منعقد کی جس کا ذکر ہم سورہ مدّثِر کے دیباچہ میں کر چکے ہیں۔ اس سُورہ میں منکرینِ آخرت کو خطاب کر کے ان کے ایک ایک شبہ اور ایک ایک اعتراض کا جواب دیا گیا ہے، بڑے مضبوط دلائل کے ساتھ قیامت اور آخرت کے امکان، وقوع اور وجوب کا ثبوت دیا گیا ہے، اور یہ بھی صاف صاف بتا دیا گیا ہے کہ جو لوگ بھی آخرت کا انکا ر کرتے ہیں ان کے انکار کی اصل وجہ یہ نہیں ہے کہ ان کی عقل اسے ناممکن سمجھتی ہے، بلکہ اس کا اصل محِرّک یہ ہے کہ ان کی خوا ہشاتِ نفس اسے ماننا نہیں چاہتیں۔ اس کے ساتھ لوگوں کو خبر دار کر دیا گیا ہے کہ جس وقت کے آنے کا تم انکار کر رہے ہو وہ آکر رہے گا، تمہارا سب کیا دھرا تمہارے سامنے لا کر رکھ دیا جائے گا، اور حقیقت میں تو اپنا نامہ اعمال دیکھنے سے بھی پہلے تم میں سے ہر شخص کو خود معلوم ہوگا کہ وہ دنیا میں کیا کر کے آیا ہے، کیونکہ کوئی شخص بھی اپنے آپ سے ناواقف نہیں ہوتا، خواہ وہ دنیا کو بہکانے اور اپنے ضمیر کو بہلانے کے لیے اپنی حرکات کے لیے کتنے ہی بہانے اور عذرات تراشتا رہے۔','سید ابو اعلیٰ مودودیؒ -  تفہیم القرآن');
INSERT INTO "chapters_ur" VALUES(76,'انسان',NULL,' نام : اس سورۃ کا نام ”الدَّہر“بھی ہے اور” الانسان“بھی ۔ دونوں نام پہلی ہی آیت کے الفاظ ھَلْ اَتٰی عَلَی الْاِنْسَانِ اور حِیْنٌ مِّنَ الدَّھْرِ سے ماخوذ ہیں۔ زمانۂ نزول : اکثر مفسرین اس کو مکّی قرار دیتے ہیں۔علامہ زمحشری، امام رازی ، قاضی بَیضاوی، علامہ نظام الدین نَیسا بوری،حافظ ابن کثیر اور دوسرے بہت سے مفسرین نے اسے مکی ہی لکھا ہے، اور علامہ آلوسی کہتے ہیں کہ یہی جمہور کا قول ہے۔ لیکن بعض دوسرے مفسرین نے پوری سُورہ کو مدنی کہا ہے، اور بعض کا قول یہ ہے کہ یہ سُورہ ہے تو مکّی، مگرآیات۸تا۱۰ مدینے میں نازل ہوئی ہیں۔ جہاں تک اِس سورۃ کے مضامین اور اندازِ بیان کا تعلق ہے ، وہ مدنی سُورتوں کے مضامین اور اندازِ بیان سے بہت مختلف ہے، بلکہ اس پر غور کرنے سے تو صاف محسوس ہوتا ہے کہ یہ نہ صرف مکّی ہے بلکہ مکّہ معظمہ کے بھی اُس دور میں نازل ہوئی ہے جو سورہ مُدَّثِّر کی ابتدائی سات آیا تکے بعد شروع ہوا تھا۔ رہیں آیات ۸ تا ۱۰( وَیُطْعِمُوْنَ الطَّعَامَ سے لے کر یَوْمًا عَبُوْسًا قَمْطَرِیْراً تک) تو وہ پُوری سُورۃ کے سلسلہ میں بیان میں اِس طرح پیوست ہیں کہ سیاق وسباق کے ساتھ کوئی اُن کو پڑھے تو ہر گز یہ محسوس نہیں کر سکتا کہ اِن سے پہلے اور بعد کا مضمون تو ۱۵۔۱۶ سال پہلے نازل ہوا تھا اور اُس کے کئی سال بعد نازل ہونے والی یہ تین آیتیں یہاں لا کر ثبت کر دی گئیں۔ دراصل جس بنا پر اِس سورۃ کے ، یا اس کے بعض آیات کے مدنی ہونے کا خیال پیدا ہوا ہے وہ ایک روایت ہے جو عطاء نے ابن عباس رضی اللہ عنہ سے نقل کی ہے۔ وہ کہتے ہیں کہ ایک مرتبہ حضرات حسن وحسین رضی اللہ تعالی عنہما بیما ر ہو گئے۔ رسول اللہ صلی اللہ علیہ وسلم اور بہت سے صحابہ ؓ ان کی عیادت کے لیے تشریف لے گئے۔ بعض صحابہؓ نے حضرت علیؓ کو مشورہ دیا کہ آپ دونوں بچوں کی شفا کے لیے اللہ تعالیٰ سے کوئی نذر مانیں۔ چنانچہ حضرت علی ؓ، حضرت فاطمہ ؓ اور ان کی خادمہ فِضّہؓ نے نذر مانی کہ اگر اللہ نے دونوں بچوں کو شفاء عطا فرمادی تو یہ سب شُکرانے کے طور پر تین دن کے روزے رکھیں گے۔ اللہ فضل ہوا کہ دونوں دتندرست ہوگئے اور تینوں صاحبوں نے نذر کے روزے رکھنے شروع کر دیے۔ حضرت علی ؓ کے گھر میں کھانے کو کچھ نہ تھا۔ انہوں نے تین صاع جَو قرض لیے( اور ایک روایت میں ہے کہ محنت مزدوری کر کے حاصل کیے)۔ پہلا روزہ کھول کر جب کھانے کے لیے بیٹھے تو ایک مسکین نے کھانا مانگا۔ گھر والوں نے سارا کھانا اسے دے دیا اور خود پانی پی کر سو رہے۔ دوسرے دن پھر افطار کے بعد کھانے کے لیے بیٹھے تو ایک یتیم آگیا اور اس نے سوال کیا۔ اُس روز بھی سارا کھانا انہوں نے اُس کو دے دیا اور پانی پی کر سو رہے۔ تیسرے دن روزہ کھول کر ابھی کھانے کے لیے بیٹھے ہی تھے کہ ایک قیدی نے آکر وہی سوال کر دیا اور اُس روز کا بھی پُورا کھانا اسے دے دیا گیا۔ چوتھے روز حضرت علیؓ دونوں بچوں کو لیے ہوئے رسول اللہ صلی اللہ علیہ وسلم کی خدمت میں حاضر ہوئے تو حضور ؐ نے دیکھا کہ بُھوک کی شدّت سے تینوں باپ بیٹوں کا بُرا حال ہورہا ہے۔ آپؐ اُٹھ کر اُن کے ساتھ حضرت فاطمہؓ کے گھر پہنچے تو دیکھا کہ وہ بھی ایک کونے میں بھوک سے نڈھال پڑی ہیں۔ یہ حال دیکھ کر حضورؐ پر رقت طاری ہو گئی۔ اتنے میں جبریل علیہ السلام حاضر ہوئے اور انہوں نے عرض کیا کہ لیجیے، اللہ تعالیٰ نے آپ کے اہلِ بیت کے معاملہ میں آپ کو مبارک باد دی ہے ۔ حضورؐ نے پوچھا وہ کیا ہے؟ انہوں نے جواب میں یہ پوری سورۃ آپ کو پڑھ کر سنائی (ابن مہران کی روایت میں ہے کہ آیت اِنَّ الْاَبْرَارَ یَشْرَبُوْنَ سے لے کر آخر تک کی آیات سنائیں۔ مگر ابن مَرْ دُویہ نے ابن عباس سے جو روایت نقل کی ہے اس میں صرف یہ بیان کیا گیا ہے کہ آیت وَیُطْعِمُوْنَ الطَّعَامَ. .... حضرت علی اور حضرت فاطمہ رضی اللہ تعالی ٰ عنہما کے بارے میں نازل ہوئی ہے، اِس قصّے کا اُس میں کوئی ذکر نہیں ہے۔ یہ پُورا قصّہ علی بن احمد الواحِدی نے اپنی تفسیر البسیط میں بیان کیا ہے اور غالباً اُسی سے زَمَخْشَری، رازی اور نَیسا بوری وغرہ ہم نے اس سے نقل کیا ہے۔ یہ روایت اوّل تو سند کے لحاظ سے نہایت کمزور ہے۔ پھر درایت کے لحاظ سے دیکھیے تو یہ بات عجیب معلوم ہوتی ہے کہ ایک مسکین، ایک یتیم اور ایک قیدی اگر آکر کھانا مانگتا ہے تو گھر کے پانچوں افراد کا پُورا کھانا اس کو دے دینے کی کیا معقول وجہ ہو سکتی ہے؟ ایک آدمی کا کھانا اس کو دے کر گھر کے پانچ افراد چار آدمیوں کے کھانے پر اکتفا کر سکتے تھے۔ پھر یہ بھی باور کرنا مشکل ہے کہ دو بچے جو ابھی ابھی بیماری سےاٹھے تھے اور کمزوری کی حالت میں تھے، انہیں بھی تین دن بھوکا رکھنے کو حضرت علی ؓ اور حضرت فاطمہؓ جیسی کامل فہیم دین رکھنے والی ہستیوں نے نیکی کا کام سمجھا ہوگا۔ اِس کے علاوہ قیدیوں کے معاملہ میں طریقہ اسلامی حکومت کے دَور میں کبھی نہیں رہا کہ انہیں بھیک مانگنے کے لیے چھوڑ دیا جائے۔ وہ اگر حکومت کی قید میں ہوتے تو حکومت ان کی خوراک اور لباس کا انتظام کرتی تھی، اور کسی شخص کے سُپرد کیے جاتے تو وہ شخص انہیں کھلانے پلانے کا ذمّہ دار ہوتا تھا۔ اس لیے مدینہ طیبہ میں یہ بات ممکن نہ تھی کہ کوئی قیدی بھیک مانگنے کے لیے نکلتا۔ تاہم اِن تمام نقلی اور عقلی کمزوریوں کو نظر انداز کرکے اگر اِس قصّے کو بالکل صحیح ہی مان لیا جائے تو زیادہ سے زیادہ اِس سے جو کچھ معلوم ہوتا ہے وہ صرف یہ ہے کہ جب آل محمد صلی اللہ علیہ وسلم سے اِس نیک عمل کا صُدُور ہوا تو جبریلؑ نے آکر حضورؐ کو خوشخبری سنائی کہ اللہ تعالیٰ کے ہاں آپ کے اہلِ بیت کا یہ فعل بہت مقبول ہوا ہے، کیونکہ اُنہوں نے ٹھیک وہی پسند یدہ کام کیا ہے جس کی تعریف اللہ تعالی ٰ نے سُورہ دہر کی اِن آیات میں فرمائی ہے۔ اِس سے یہ لازم نہیں آتا کہ یہ آیات نازل بھی اِسی موقع پر ہوئی تھیں۔ شانِ نزول کے بارے میں بہت سی روایات کا حال یہی ہے کہ کسی آیت کے متعلق جب یہ کہا جاتا ہے کہ فلاں موقع پر نازل ہوئی تھی تو دراصل اس سے مُراد یہ نہیں ہوتی کہ جب یہ واقعہ پیش آیا اُسی وقت یہ آیت نازل ہوئی تھی بلکہ مراد یہ ہوتی ہے کہ یہ آیت اِس واقعہ پر ٹھیک چسپاں ہوتی ہے۔ امام سُیُوطیِ نے اِتقان میں حافظ ابنِ تَیِمیہؒ کا یہ قول نقل کیا ہے کہ ”راوی جب یہ کہتےہیں کہ یہ آیت فلاں معاملہ میں نازل ہوئی ہے تو کبھی اس سے مراد یہ ہوتی ہے کہ وہی معاملہ اس کے نزول کا سبب ہے ، اور کبھی اس کا مطلب یہ ہوتا ہے کہ معاملہ اس آیت کے حکم میں داخل ہے اگرچہ وہ اس کے نزول کا سبب نہ ہو“۔ آگے چل کر وہ امام بدرالدین زَرْکَشِی کا قول اُن کی کتاب البُرہان فی علوم القرآن سے نقل کرتے ہیں کہ ”صحابہؓ اور تابعین کی یہ عادت معروف ہے کہ ان میں سے کوئی شخص جب یہ کہتا ہے کہ یہ آیت فلاں معاملہ میں نازل ہوئی تھی تو اس کا مطلب یہ ہوتا ہے کہ اس آیت کا حکم اس معاملہ پر چسپاں ہوتا ہے ، نہ یہ ک وہی اِس واقعہ کے نزول کا سبب ہے۔پس دراصل اُس کی نوعیت آیت کے حکم سے استدلال کی ہوتی ہے نہ کہ بیانِ واقعہ کی“(الاِتقان فی علوم القرآن، جلد اوّل، صفجہ ۳۱، طبع۱۹۲۹ء)۔ موضوع اور مضمون: اِس سُورہ کا موضوع انسان کو دنیا میں اُس کی حقیقی حیثیت سے آگاہ کرنا اور یہ بتا نا ہے کہ اگر وہ اپنی اِس حیثیت کو ٹھیک ٹھیک سمجھ کر شکر کا رَویّہ اختیار کر ے تو اس کا انجام کیا ہو گا اور کفر کی راہ چلے تو کس انجام سے وہ دو چار ہوگا۔ قرآن کی بڑی سُورتوں میں تو یہ مضمون بڑی تفصیل کے ساتھ بیان کیا گیا ہے، لیکن ابتدائی مکّی دور کی سُورتوں کا یہ خاص اندازِ بیان ہے کہ جو باتیں بعد کے دَور میں مفصّل ارشاد ہوئی ہیں، وہی اِس دور میں بڑے مختصر مگر انتہائی مؤثر طریقے سے ذہن نشین کرائی گئی ہیں اور ایسے چھوٹے چھوٹے خوبصورت فقرے استعمال کیے گئے ہیں جو سننے والوں کی زبان پر خود بخود چڑھ جائیں۔ اس میں سب سے پہلے انسان کو یاد دلایا گیا ہے کہ ایک وقت ایسا تھا جب وہ کچھ نہ تھا، پھر ایک مخلوط نطفے سے اُس کی ایسی حقیر سی ابتدا کی گئی کہ اُس کی ماں تک کو خبر نہ تھی کہ اُس کے وجود کی بنا پڑگئی ہے اور کوئی اُس خوردبینی وجود کو دیکھ کر یہ نہیں کہہ سکتا تھا کہ یہ کوئی انسان ہے جو آگے چل کر اِس زمین پر اشرف المخلوقات بننے والا ہے۔ اس کے بعد انسان کو خبر دار کیا گیا ہے کہ تیری تخلیق اِس طرح کر کے تجھے یہ کچھ ہم نے اس لیے بنایا ہے کہ ہم دنیا میں رکھ کر تیرا امتحان لینا چاہتے ہیں۔ اسی لیے دوسری مخلوقات کے بر عکس تجھے ہوش گوش رکھنے والا بنایا گیا اور تیرے سامنے شُکر اور کُفر کے دونوں راستے کھول کر رکھ دیے گئے تا کہ یہاں کام کرنے کا جو وقت تجھے دیا گیا ہے اس میں تو دکھا دے کہ اس امتحان سے تو شاکر بندہ بن کر نکلا ہے یا کافر بندہ بن کر۔ پھر صرف ایک آیت میں دو ٹوک طریقہ سے بتا دیا گیا ہے کہ جو لوگ اِس امتحان سے کافرین کر نکلیں گے اُنہیں آخرت میں کیا انجام دیکھنا ہوگا۔ اس کے بعد آیت نمبر ۵ سے ۲۲ تک مسلسل اُن انعامات کی تفصیل بیان کی گئی ہے جن سے وہ لوگ اپنے رب کے ہاں نوازے جائیں گے جنہوں نے یہاں بندگی کا حق ادا کیا ہے۔ اِن آیات میں صرف اُن کی بہترین جزاء بتا نے ہی پر اکتفا نہیں کیا گیا ہے بلکہ مختصراً یہ بھی بتا دیا گیا ہے کہ اُن کے وہ کیا اعمال ہیں جن کی بنا پر وہ اِس جزا کے مستحق ہوں گے ۔ مکی دور کی ابتدائی سورتوں کی خصوصیات میں سے ایک نمایاں خصوصیت یہ بھی ہے کہ اُن میں اسلام کے بنیادی عقائد اور تصورات کا مختصر تعارف کرنے کے ساتھ ساتھ کہیں وہ اخلاقی اوصاف اور نیک اعمال بیان کیے گئے ہیں جو اسلام کی نگاہ میں قابلِ قدر ہیں، اور کہیں اعمال و اخلاق کی اُن برائیوں کا ذکر کیا گیا ہے جن سے اسلام انسان کو پاک کرنا چاہتا ہے۔ اور یہ دونوں چیزیں اِس لحاظ سے بیان نہیں کی گئی ہیں کہ ان کا کیا اچھا یا بُرا نتیجہ دنیا کی اِس عارضی زندگی میں نکلتا ہے، بلکہ صرف اِس حیثیت سے اُن کا ذکر کیا گیا ہے کہ آخرت کی ابدی اور پائیدار زندگی میں اُن کا مستقل نتیجہ کیا ہو گا قطع نظر اس سے کہ دنیا میں کوئی بُری صفت مفید ہو یا کوئی اچھی صفت نقصان دہ ثابت ہو۔ یہ پہلے رکوع کا مضمون ہے۔ اس کے بعد دوسرے رکوع میں رسول اللہ صلی اللہ علیہ وسلم کو مخاطب کرکے تین باتیں ارشاد فرمائی گئی ہیں۔ ایک یہ کہ دراصل یہ ہم ہی ہیں جو اِ س قرآن کو تھوڑا تھوڑا کر کے تم پر نازل کر رہے ہیں، اور اس سے مقصود حضورؐ کو نہیں بلکہ کفار کو خبر دار کرنا ہے کہ یہ قرآں محمد صلی اللہ علیہ وسلم خود اپنے دل سے نہیں گھڑ رہے ہیں بلکہ اس کے نازل کرنے والے”ہم“ہیں اور ہماری حکمت ہی اِس کے مقتضی ہے کہ اسے یک بار گی نہیں بلکہ تھوڑا تھوڑا کر کے نازل کریں۔ دوسری بات حضورؐ سے یہ فرمائی گئی ہے کہ تمہارے رب کا فیصلہ صادر ہونے میں خواہ کتنی ہی دیر لگے، اور اس دوران میں تم پر خواہ کچھ ہی گزر جائے، بہر حال تم صبر کے ساتھ اپنا فریضہ رسالت انجام دیے چلے جا ؤ اور کبھی اِن بد عمل اور مُنکرِ حق لوگوں میں سے کسی کے دبا ؤ میں نہ آ ؤ۔ تیسری بات آپ سے یہ فرمائی گئی ہے کہ شب و روز اللہ کو یاد کرو، نماز پڑھو اور راتیں اللہ کی عبادت میں گزار رو،کیونکہ یہی وہ چیز ہے جس سے کفر کی طغیانی کے مقابلہ میں اللہ کی طرف بلانے والوں کو ثابت قدمی نصیب ہوتی ہے۔ پھر ایک فقرے میں کُفّار کے غَلَط رَویّے کی اصل وجہ بیان کی گئی ہے کہ وہ آخرت کو بُھول کر دنیا پر فریفتہ ہوگئے ہیں ، اور دوسرے فقرے میں اُن کو متنبہ کیا گیا ہے کہ تم خود نہیں بن گئے ہو، ہم نے تمہیں بنایا ہے، یہ چوڑے چکلے سینے اور مضبوط ہاتھ پا ؤں تم نے خود اپنے لیےنہیں بنا لیے ہیں، ان کے بنا نے والے بھی ہم ہی ہیں، اور یہ بات ہر وقت ہماری قدرت میں ہے کہ جو کچھ ہم تمہارے ساتھ کرنا چاہیں کر سکتے ہیں۔ تمہاری شکلیں بگاڑ سکتے ہیں۔ تمہیں ہلاک کرکے کوئی دوسری قوم تمہاری جگہ لا سکتے ہیں۔ تمہیں مار کر دوبارہ جس شکل میں چاہیں تمہیں پیدا کر سکتے ہیں۔ آخر میں کلام اس بات پر ختم کیا گیا ہے یہ کہ ایک کلمہ نصیحت ہے ، اب جس کا جی چاہے اسے قبول کر کے اپنے رب کا راستہ اختیار کر لے۔ مگر دنیا میں انسان کی چاہت ہی سکب کچھ نہیں ہے۔ کسی کی چاہت بھی پوری نہیں ہو سکتی جب تک اللہ نہ چاہے، اور اللہ کی چاہت اندھا دُھند نہیں ہے، وہ جو کچھ بھی چاہتا ہے اپنے علم اور اپنی حکمت کی بنا پر چاہتا ہے۔ اِس علم اور حکمت کی بنا پر جسے وہ اپنی رحمت کا مستحق سمجھتا ہے اسے اپنی رحمت میں داخل کر لیتا ہے ، اور جسے وہ ظالم پاتا ہے اس کے لیے درد ناک عذاب کا انتظام اس نے کر رکھا ہے۔','سید ابو اعلیٰ مودودیؒ -  تفہیم القرآن');
INSERT INTO "chapters_ur" VALUES(77,'بھیجی جانے والی ہوائیں',NULL,' نام : پہلی آیت کے لفظ والمُرسَلٰتِ کو اس سورۃ کا نام قرار دیا گیا ہے۔ زمانۂ نزول : اس سورۃ کا پُورا مضمون یہ ظاہر کر رہا ہے کہ یہ مکہ معظمہ کے ابتدائی دَور میں نازل ہوئی ہے۔ اس سے پہلے کی دو سُورتیں سورۂ قیامہ، اور سورہ دہر، اور اس کے بعد کی دو سورتیں، سورہ نبأ اور سورۂ نازِ عات اگر ملا کر پڑھی جائیں تو صاف محسوس ہوتا ہے کہ یہ سب ایک ہی دور کی نازل شدہ سورتیں ہیں اور ایک ہی مضمون ہے جس کو اِن میں مختلف پیرایوں سے اہل مکہ ذہن نشین کرایا گیا ہے۔ موضوع اور مضمون: اس کا موضوع قیامت اور آخرت کا اثبات ، اور اُن نتائج سے لوگوں کو خبر دار کرنا ہے جو اِن حقائق کے انکار اور اقرار سے آخر کار بر آمد ہونگے۔ پہلی سات آیتوں میں ہواؤں کے انتظام کو اِس حقیقت پر گواہ قرار دیا گیا ہے کہ قرآن اور محمد صلی اللہ علیہ وسلم جس قیامت کے آنے کی خبر دے رہے ہیں وہ ضرور واقع ہو کر رہے گی۔ ان میں استدلال یہ ہے کہ جس قادر ِ مطلق نے زمین پر یہ حیرت انگیز انتظام قائم کیا ہے اُس کی قدرت قیامت بر پا کرنے سے عاجز نہیں ہو سکتی، اور جو صریح حکمت اِس انتظام میں کار فرما نظر آ رہی ہے وہ اس بات کی شہادت دیتی ہے کہ آخرت ضرور ہونی چاہیے، کیونکہ حکیم کا کوئی فعل عبث اور بے مقصد نہیں ہو سکتا ، اور آخرت نہ ہو تو اِس کے معنی یہ ہیں کہ یہ سارا کارخانہ ہستی سراسر فضول ہے۔ اہلِ مکہ بار بار کہتے تھے کہ جس قیامت سے تم ہم کو ڈرا رہے ہو اسے لا کر دکھاؤ تب ہم اسے مانیں گے۔ آیت ۸ سے ۱۵ تک اُن کے اِس مطالبے کا ذکر کیے بغیر اس کا جواب دیا گیا ہے کہ وہ کوئی کھیل یا تماشا تو نہیں ہے کہ جب کوئی مسخر ا اسے دکھانے کا مطالبہ کر ے اسی وقت وہ فوراً دکھا دیا جائے۔ وہ تو تمام نوعِ انسانی، اور س کے تمام افراد کے مقدمے کے فیصلے کا دن ہے۔ اُس کے لیے اللہ تعالیٰ نے ایک خاص وقت مقرر کر رکھا ہے۔ اُسی وقت پر وہ آئے گا۔ اور جب آئے گا تو ایسی ہولناک شکل میں آئے گا کہ آج جو لوگ مذاق کے طور پر اس کا مطالبہ کر رہے ہیں اس وقت اُن کے حواس باختہ ہو جائیں گے۔ اُس وقت اُنہی رسولوں کی شہادت پر ان کے مقدمے کا فیصلہ ہو گا جن کی دی ہوئی خبر کو یہ منکرین آج بڑی بے باکی کے ساتھ جُھٹلا رہے ہیں، پھر انہیں خود پتہ چل جائے گا کہ انہوں نے کس طرح خود اپنے ہاتھوں اپنی تباہی کا سامان کیا ہے۔ آیت ۱۶ سے ۲۸ تک مسلسل قیامت اور آخرت کے وقوع اور وجوب کے دلائل دیے گئے ہیں۔ ان میں بتایا گیا ہے کہ انسان کی اپنی تاریخ، اس کی اپنی پیدائش ، اور جس زمین پر وہ زندگی بسر کر رہا ہے اس کی اپنی ساخت اس بات کی شہادت دے رہی ہے کہ قیامت کا آنا اور عالمِ آخرت کا بر پا ہو نا ممکن بھی ہے اور اللہ تعالیٰ کی حکمت کاتقاضا بھی ۔ انسانی تاریخ بتا رہی ہے کہ جن قوموں نے بھی آخرت کا انکار کیا وہ آخر کار بگڑیں اور تباہی سے دو چار ہوئیں۔ اس کے معنی یہ ہیں کہ آخرت ایک ایسی حقیقت ہے جس سے کسی قوم کا رویہ اگر متصادم ہو تو اُس کا انجام وہی ہوتاہے جو اُس اندھے کا انجام ہوتا ہے جو سامنے آتی ہوئی گاڑی کے مقابلے میں بگ ٹُٹ چلا رہا ہو۔ اور اس کے معنی یہ بھی ہیں کہ کائنات کی سلطنت میں صرف قوانین طبیعی (Physical Law) ہی کار فرما نہیں ہیں بلکہ ایک قانونِ اخلاقی (Moral Law) بھی کام کر رہا ہے جس کے تحت خود اس دنیا میں بھی مُکافات ِ عمل کا سلسلہ جاری ہے ۔ لیکن دنیا کی موجودہ زندگی میں یہ مُکافات چونکہ اپنی کامل ومکمل صورت میں واقع نہیں ہو رہی ہے اس لیے کائنات کا اخلاقی قانون لازماً یہ تقاضا کرتا ہے کہ کوئی وقت ایسا آئے جب یہ بھر پُور طریقے سے واقع ہو اور اُن تمام بھلائیوں اور بُرائیوں کی پُوری جزا وسزا دی جائے جو یہاں جزا سے محروم رہ گئی ہیں یا سزا سے بچ نکلی ہیں ۔ اس کے لیے نا گزیر ہے کہ موت کے بعد دوسری زندگی ہو، اور انسان کی پیدائش دنیا میں جس طرح ہوتی ہے اس پر اگر انسان غور کرے تو اس کی عقل۔۔۔۔بشرطیکہ وہ سلیم ہو۔۔۔۔ اس بات کو ماننے سے انکار نہیں کر سکتی کہ جس خدا نے یک حقیر نطفہ سے ابتداء کر کے اُسے پُورا آدمی بنایا ہے اُس کے لیے اُسی آدمی کو پھر پید کر دینا یقیناً ممکن ہے۔ زندگی بھر انسان جس زمین پر رہتا ہے، مرنے کے بعد اس کے اجزائے جسم کہیں غائب نہیں ہو جاتے، اسی زمین پر اُن کا ایک ایک ذرہ موجود رہتا ہے۔ اسی زمین کے خزانوں سے وہ بنتا اور پھلتا اور پھولتا اور پر ورش پاتا ہے، اور پھر اسی زمین کے خزانوں میں واپس جمع ہو جا تا ہے۔ جس خدا نے اُسے پہلے زمین کےان خزانوں سے نکالا تھا وہی اُن میں جمع ہوجانے کے بعد اُسے پھر اُن سے نکال لا سکتا ہے۔ اُس کی قدرت پر غور کرو تو تم اِس سے انکار نہیں کر سکتے کہ وہ ایسا کر سکتا ہے۔ اور اس کی حکمت پر غور کرو تو تم اس سے بھی انکار نہیں کر سکتے کہ زمین پر جو اختیارات اُس نے تمہیں دیے ہیں اُن کے صحیح اور غلط استعمال کا حساب لینا یقیناً اُس کی حکمت کا تقاضا ہے اور بلا حساب چھوڑ دینا سراسر حکمت کے خلاف ہے۔ اس کے بعد آیات۲۸تا۴۰ تک آخرت کے منکرین کا، اور ۴۱ سے ۴۵ تک اُن لوگوں کا انجام بیان کیا گیا ہے جنہوں نے اُس پر ایمان لا کر دنیا میں اپنی عاقبت سنوارنے کی کوشش کی ہے ، اور عقائد و افکار، اخلاق واعمال، اور سیرت و کردار کی اُن بُرائیوں سے اجتناب کیا ہے جوچاہے آدمی کی دنیا بناتی ہوں مگر اس کی عاقبت خراب کر دینے والی ہوں۔ آخر میں منکرین ِ آخرت اور خدا کی بندگی سے منہ موڑنے والوں کو متنبہ کیا گیا ہے کہ دنیا کی چند روزہ زندگی میں جو کچھ مزے اڑانے ہیں اڑالو، آخر کار تمہارا انجام سخت تباہ کن ہوگا۔ اور بات اس پر ختم کی گئی ہے کہ اس قرآن سے بھی جو شخص ہدایت نہ پائے اسے پھر دنیا میں کوئی چیز ہدایت نہیں دے سکتی۔ ','سید ابو اعلیٰ مودودیؒ -  تفہیم القرآن');
INSERT INTO "chapters_ur" VALUES(78,'خبر',NULL,' نام : دوسری آیت کے فقرے عَنِ النَّبَاِ الْعَظِیْمِ کے لفظ اَلنَّبَا کو اس کا نام قرار دیا گیا ہے، اور یہ صرف نام ہی نہیں ہے بلکہ اس سُورۃ کے مضامین کا عنوان بھی ہے، کیونکہ نَبا سے مراد قیامت اور آخرت کی خبر ہے اور سُورۃ میں ساری بحث اسی پر کی گئی ہے۔ زمانۂ نزول : جیسا کہ ہم سُورہ مُرْسَلات کے دیباچے میں بیان کر چکے ہیں، سورہ قیامت سے سورہ نازعات تک سب کا مضمون ایک دوسرے سے مشابہ ہے اور یہ سب مکہ معظمہ کے ابتدائی دَور کے نازل شدہ معلوم ہوتی ہیں۔ موضوع اور مضمون: اس کا مضمون بھی وہی ہے جو سُورہ مُرسَلات کا ہے، یعنی قیامت اور آخرت کا اِثبات ، اور اُس کو ماننے یا نہ ماننے کے نتاَئج سے لوگوں کو خبر دار کرنا۔ مکہ معظمہ میں جب اوّل اوّل رسول اللہ صلی اللہ علیہ وسلم نے اسلام کی تبلیغ کا آغاز کیا تو اس کی بنیاد تین چیزیں تھیں۔ ایک یہ بات کہ اللہ کے ساتھ کسی کوخدائی میں شریک نہ مانا جائے۔ دوسری یہ کہ آپؐ کو اللہ نے اپنا رسول مقرر کیا ہے۔ تیسری یہ کہ اِس دنیا کا ایک روز خاتمہ ہوجائے گا اور اس کے بعد ایک دوسرا عالم بر پا ہو گا جس میں تمام اوّلین و آخرین دوبارہ زندہ کر کے اُسی جسم کے ساتھ اُٹھائے جائیں گے جس میں رہ کر انہوں نے دنیا میں کام کیا تھا، پھر ان کے عقائد اور اعمال کا حساب لیا جائے گا اور اس محاسبہ میں جو لوگ مومن و صالح ثابت ہونگے وہ ہمیشہ کے لیے جنت میں جائیں گے اور جو کافر و فاسق ہوں گے وہ ہمیشہ کے لیے دوزخ میں رہیں گے۔ ان تینوں باتو ں میں سے پہلی بات اگرچہ اہلِ مکہ کو سخت نا گوار تھی، لیکن بہر حال وہ اللہ تعالیٰ کی ہستی کے منکر نہ تھے، اُس کے ربِّ اعلیٰ اور خالق و رازق ہونے کو بھی مانتے تھے، اور یہ بھی تسلیم کرتے تھے کہ دوسری جن جن ہستیوں کو وہ معبُود قرار دیتے ہیں وہ اللہ ہی کی مخلوق ہیں، اِس لیے جھگڑا صرف اِس امر میں تھا کہ خدائی کی صفات و اختیارات میں اور الُو ہیت کی ذات میں اُن کی کوئی شرکت ہے یا نہیں ۔ دوسری بات کو مکے کے لوگ ماننے کے لیے تیار نہ تھے، مگر اس امر سے انکار کرنا اُن کے لیے ممکن نہ تھا کہ چالیس سال تک جو زندگی حضورؐ نے دعوائے رسالت سے پہلے اُنہی کے درمیان گزار ی تھی، اس میں انہوں نے کبھی آپ کو جُھوٹا یا فریب کار، یا نفسانی اغراض کے لیے ناجائز طریقے اختیار کرنے والا نہ پایا تھا۔ وہ خود آپ کی دانائی و فرزانگی، سلامت روی اور اخلاق کی بلندی کے قائل و متعرف تھے۔ اس لیے ہزار بہانے اور الزامات تراشنے کے با وجود اُنہیں دوسروں کو باور کرانا تو درکنار اپنی جگہ خود بھی یہ باور کرنے میں سخت مشکل پیش آرہی تھی کہ حضورؐ سارے معاملات میں تو راستباز ہیں مگر صرف رسالت کے دعوے میں معاذ اللہ جُھوٹے ہیں۔ اس طرح پہلی دو باتیں اہلِ مکہ کے لیے دراصل اُتنی زیادہ اُلجھن کی مُوجب نہ تھیں جتنی تیسری بات تھی۔ اُس کو جب اُن کے سامنے پیش کیا گیا تو انہوں نے سب سے زیادہ اُسی کا مذاق اُڑایا، اُسی پر سب سے بڑھ کر حیرانی اور تعجب کا اظہار کیا، اور اُسے بالکل بعید از عقل و امکان سمجھ کر جگہ جگہ اس کے ناقابلِ یقین بلکہ نا قابلِ تصوّر ہونے کے چرچے شروع کر دیے۔ مگر اسلام کی راہ پر اُن کو لانے کے لیے یہ قطعی ناگزیر تھا کہ آخرت کا عقیدہ اُن کے ذہن میں اُتار ا جائے، کیونکہ اِس عقیدے کو مانے بغیر یہ ممکن ہی نہ تھا کہ حق اور باطل کے معاملہ میں اُن کے طرزِ فکر سنجیدہ ہو سکتا، خیر وشر کے معاملہ میں اُن کا معیارِ اقدار بدل سکتا، اور وہ دنیا پرستی کی راہ چھوڑ کر اُس راہ پر ایک قدم بھی چل سکتے جس پر اسلام اُن کو چلانا چاہتا تھا۔ یہی وجہ ہے کہ مکّہ معظمہ کے ابتدائی دور کی سُورتوں میں زیادہ تر روز آخرت کا عقیدہ دلوں میں بٹھانے پر صرف کیا گیا ہے، البتہ اس کے لیے دلائل ایسے انداز سے دیے گئے ہیں جن سے توحید کا تصور بھی خود بخود ذہن نشین ہو تا چلا جاتا ہے، اور بیچ بیچ میں رسول اللہ صلی اللہ علیہ وسلم اور قرآن کے بر حق ہونے کے دلائل بھی مختصراً دے دیے گئے ہیں۔ اِس دور کی سُورتوں میں آخرت کے مضمون کی اِس تکرار کا سبب اچھی طرح سمجھ لینے کے بعد اب اِس سُورۃ کے مضامین پر ایک نگاہ ڈال لیجیے۔ اس میں سب سے پہلے اُن چرچوں اور چہ میگوئیوں کی طرف اشارہ کیا گیا ہے جو قیامت کی خبر سُن کر مکّہ کے ہر کوچہ و بازار اور ہلِ مکہ کی ہر محفل میں ہو رہی تھیں۔ اس کے بعد انکار کرنے والوں سے پوچھا گیا ہے کہ کیا تمہیں یہ زمین نظر نہیں اتی جسے ہم نے تمہارے لیے فرش بنا رکھا ہے؟ کیا یہ بلند و بالا پہاڑ تمہیں نظر نہیں آتے جنہیں ہم نے زمین میں گاڑ رکھا ہے؟ کیا تم اپنے آپ کو نہیں دیکھتے کہ کس طرح ہم نے تمہیں مردوں اور عورتوں کے جو ڑوں کی شکل میں پیدا کیا ہے؟ کیا تم اپنی نیند کو نہیں دیکھتے جس کے ذریعہ سے ہم نے تم کو دنیا میں کام کرنے کے قابل بنائے رکھنے کے لیے ہر چند گھنٹوں کی محنت کے بعد ہر چند گھنٹے آرام لینے پر مجبور کر رکھا ہے؟ کیا تم رات اور دن کی آمد ورفت کو نہیں دیکھتے جسے ٹھیک تمہاری ضرورت کے مطابق ہم باقاعدگی کے ساتھ مسلسل جاری رکھے ہوئے ہیں؟ کیا تم اپنے اوپر آسمانوں کے مضبوط بندھے ہوئے نظام کو نہیں دیکھتے ؟ کیا یہ سُورج تمہیں نظر نہیں آتا جس کی بدولت تمہیں روشنی اور حرارت میسر آ رہی ہے؟ کیا تم اُن بارشوں کو نہیں دیکھتے جو بادلوں سے برس رہی ہیں اور اُن کے ذریعہ سے غلّے اور سبزیاں اور گھنے باغ اگ رہے ہیں؟ یہ ساری چیزیں کیا تمہیں یہی بتا رہی ہیں کہ جس قادرِ مطلق نے اِن کو پیدا کیا ہے اُس کی قدرت قیامت لانے اور آخرت بر پا کرنے سے عاجز ہے؟ اور اِس پُورے کا رخانے میں جو کمال درجے کی حکمت و دانائی صریحاً کار فرما ہے کیا اس کو دیکھتے ہوئے تمہاری سمجھ میں یہی آتا ہے کہ اِس کارخانے کا ایک ایک جُز اور ایک ایک فعل تو با مقصد ہے مگر بجائے خود پُورا کارخانے بے مقصد ہے؟ آخر اس سے زیادہ لغو اور بے معنی بات کیا ہو سکتی ہے کہ اس کارخانے میں انسان کو پیش دست( Foreman ) کے منصب پر مامور کر کے اسے یہاں بڑے وسیع اختیارات تو دے دیے جائیں مگر جب وہ اپنا کام پورا کر کے یہاں سے رخصت ہو تو اسے یونہی چھوڑ دیا جائے؟ نہ کام بنا نے پر پنشن اور انعام ، نہ کام بگاڑنے پر باز پُرس اور سزا؟ یہ دلائل دینے کے بعد پُورے زور کے ساتھ فرمایا گیا ہے کہ فیصلے کا دن یقیناً اپنے مقرر وقت پر آ کر رہے گا۔صُور میں بس ایک پُھونک مارنے کی دیر ہے ، وہ سب کچھ جس کی تمہیں خبر دی جا رہی ہے سامنے آجائے گا اور تم آج چاہے مانو یا نہ مانو، اُس وقت جہاں جہاں بھی تم مرے پڑے ہو گے وہاں سے فوج درفوج اپنا حساب دینے کے لیے نکل آؤ گے۔ تمہار اانکار اس واقعہ کو پیش آنے سے نہیں روک سکتا۔ اس کے بعد آیت ۲۱ سے ۳۰ تک بتا یا گیا ہے کہ جو لوگ حساب کتاب کی توقع نہیں رکھتے اور جنہوں نے ہماری آیات کو جُھٹلا دیا ہے، ان کا ایک ایک کرتوت گن گن کر ہمارے ہاں لکھا ہوا ہے، اور ان کو خبر لینے کے لیے جہنم گھات لگا ئے ہوئے تیار ہے جہاں ان کے اعمال کا بھر پور بدلہ انہیں دے دیا جائے گا۔ پھر آیات ۳۱ سے ۳۲ تک ان لوگوں کی بہترین جزا بتائی گئی ہے جنہوں نے اپنے آپ کو ذمہ دار و جواب وہ سمجھ کر دنیا میں اپنی آخرت درست کرنے کی پہلے ہی فکر کر لی ہے، اور انہیں اطمینان دلایا گیا ہے کہ انہیں ان کی خدمات کا صرف اجر ہی نہیں دیا جائے گا بلکہ اس سے زیادہ کافی انعام بھی دیا جائے گا۔ آخر میں خدا کی عدالت کا نقش کھینچا گیا ہے کہ وہاں کسی کے اَڑ کر بیٹھ جانے اور اپنے تنوسلین کو بخشواکر چھوڑ نے کا کیا سوال، کوئی بلا اجازت زبا ن تک نہ کھول سکے گا، اور اجازت بھی اس شرط کےساتھ ملے گی کہ جس کے حق میں سفارش کا اِذن ہو صرف اس کے لیے سفارش کرے اور سفارشی میں کوئی بےجابات نہ کہے۔ نیز سفارش کی اجازت صرف ان لوگوں کے حق میں دی جائے گی جو دنیا میں کلمہ حق کے قائل رہے ہیں اور محض گناہ گار ہیں۔ خدا کے باغی اور حق کے منکر کسی سفارشی کے مستحق نہ ہوں گے۔ پھر کلام کو اس تنبیہ پر ختم کیا گیا ہے کہ جس دن کے آنے کی خبر دی جا رہی ہے اس کا آنا بر حق ہے، اسے دور نہ سمجھو، وہ قریب ہی آئے گا ، اب جس کا جی چاہے اسے مان کر اپنے رب کا راستہ اختیار کر لے۔ لیکن اس تنبیہ کے باوجود جو اس کا انکار کرے گا اس کا سارا کیا دھرا اس کے سامنے آجائے گا اور پھر وہ پچھتا پچھتا کر کہے گا کہ کاش میں دنیا میں پیدا ہی نہ ہوتا اس وقت اس کا یہ احساس اس دنیا کے بارے میں ہو گا جس پر وہ آج لٹو ہو رہا ہے۔ ','سید ابو اعلیٰ مودودیؒ -  تفہیم القرآن');
INSERT INTO "chapters_ur" VALUES(79,'زور سے کھینچنے والے',NULL,' نام : پہلے ہی لفظ وَ النّٰزِعٰتِ سے ماخوذ ہے۔ زمانۂ نزول : حضرت عبداللہ بن عباس کا بیان ہےکہ یہ سُورہ نبا ، ( عَمَّ یَتَسَآءَلُوْنَ ) کے بعد نازل ہوئی ہے۔ اس کا مضمون بھی یہ بتا رہا ہے کہ یہ ابتدائی زمانے کی نازل شدہ سورتوں میں سے ہے۔ موضوع اور مضمون: اس کاموضوع قیامت اور زندگی بعد موت کا اثبات ہے اور ساتھ ساتھ اس بات پر تنبیہ بھی کہ خدا کے رسول کو جُھٹلانے کا انجام کیا ہوتا ہے۔ آغازِ کلام میں موت کے وقت جان نکالنے والے، اور اللہ کے احکام کو بلا تا خیر بجا لانے والے، اور حکمِ الہٰی کے مطابق ساری کائنات کا انتظام کرنے والے فرشتوں کی قسم کھا کر یہ یقین دلایا گیا ہے کہ قیامت ضرور واقع ہوگی اور موت کے بعد دوسری زندگی ضرور پیش آکر رہےگی۔ کیونکہ جن فرشتوں کے ہاتھوں آج جان نکالی جاتی ہے، اُنہی کےہاتھوں دوبارہ جان ڈالی بھی جا سکتی ہے، اور جو فرشتے آج اللہ کے حکم کی تعمیل بلا تاخیر بجا لاتے اور کائنات کا انتظام چلاتے ہیں، وہی فرشتے کل اُسی خدا کے حکم سے کائنات کا یہ نظام درہم برہم بھی کر سکتے ہیں اور ایک دوسرا نظام قائم بھی کر سکتے ہیں۔ اس کے بعد لوگوں کو یہ بتا یا گیا ہے کہ یہ کام ، جسے تم بالکل نا ممکن سمجھتے ہو ، اللہ تعالیٰ کے لیے سرے سے کوئی دشوار کام ہی نہیں ہے جس کے لیے کسی بڑی تیاری کی ضرورت ہو۔ بس ایک جھٹکا دنیا کے اِس نظام کو درہم بر ہم کر دے گا ، اور ایک دوسرا جھٹکا اِس کے لیے بالکل کافی ہو گا کہ دوسری دنیا میں یکایک تم اپنے آپ کو زندہ موجود پاؤ۔ اُس وقت وہی لوگ جو اس کا انکار کر رہے تھے، خوف سے کانپ رہے ہوں گے اور سہمی ہوئی نگاہوں سے وہ سب کچھ ہوتے دیکھ رہے ہونگے جس کو وہ اپنے نزدیک نا ممکن سمجھتے تھے۔ پھر حضرت موسیٰ اور فرعون کا قصّہ مختصراً بیاب کر کے لوگوں کو خبر دار کیا گیا ہے کہ رسول کو جھٹلانے اور اس کی ہدایت و رہنمائی کو رد کرنے اور چالبازیوں سے اس کو شکست دینے کی کوشش کا کیا انجام فرعون دیکھ چکا ہے۔ اُس سے عبرت حاصل کر کے اِس روش سے باز نہ آؤ گے تو وہی انجام تمہیں بھی دیکھنا پڑے گا۔ اس کے بعد آیت ۲۷ سے ۲۳ تک آخرت اور حیات بعد الموت کے دلائل بیان کیے گئے ہیں۔ اس سلسلے میں پہلے منکرین سے پوچھا گیا ہے کہ تمہیں دوبارہ پیدا کر دینا زیادہ سخت کام ہے یا اِس عظیم کائنات کو پیدا کر نا جو عالمِ بالا میں اپنے بے حدو حساب ستاروں اور سیاروں کے ساتھ پھیلی ہوئی ہے؟ جس خدا کے لیے یہ کام مشکل نہ تھا اس کے لیے تمہاری بارِ د گر تخلیق آخر کیوں مشکل ہوگی؟ صرف ایک فقرے میں امکانِ آخرت کی یہ مُیکت دلیل پیش کرنے کے بعد زمین اور اُس سرو سامان کی طرف توجہ دلائی گئی ہے جو زمین میں انسان اور حیوان کی زیست کے لیے فراہم کیا گیا ہے اور جس کی ہر چیز اس بات کی شہادت دے رہی ہے کہ وہ بڑی حکمت کے ساتھ کسی نہ کسی مقصد کو پُورا کرنے کے لیے بنائی گئی ہے۔ یہ اشارہ کر کے اس سوال کو انسان کی عقل پر چھوڑ دیا گیا ہے کہ وہ خود اپنی جگہ سوچ کر رائے قائم کر دے کہ آیا اس حکیمانہ نظام میں انسان جیسی مخلوق کو اختیارات اور ذمہ داریاں سونپ کر اُس کا محاسبہ کرنا زیادہ متقضائے حکمت نظر آتا ہے، یا یہ کہ وہ زمین میں ہر طرح کے کام کر کے مر جائے اور خاک میں مل کر ہمیشہ کے لیے فنا ہوجائے اور کبھی اُس سے حساب نہ لیا جائے کہ اِن اختیارات کو اس نے کیسے استعمال کیا اور اِن ذمہ داریوں کو کس طرح ادا کیا؟ اس سوال پر بحث کرنے کے بجائے آیات۳۴۔۴۱ میں یہ بتایا گیا ہے کہ جب آخرت بر پا ہوگی تو انسان کے دائمی اور ابدی مستقبل کا فیصلہ اِس بنیاد پر ہوگا کہ کس نے دنیا میں حدِّ بندگی سے تجاوز کر کے اپنے خدا سے سر کشی کی اور دنیا ہی کے فائدوں اور لذّتوں کو مقصود بنا لیا، اور کس نے اپنے رب کے سامنے کھڑے ہونے کا خوف کیا اور نفس کی ناجائز خواہشات کو پورا کرنے سے احتراز کیا۔ یہ بات خود بخود اوپر کے سوال کا صحیح جوب ہر اُس شخص کو بتا دیتی ہے جو ضد اور ہٹ دھرمی سےپاک ہو کر ایمانداری کے ساتھ اُس پر غور کرے۔ کیونکہ انسان کو دنیا میں اختیارات اور ذمّہ داریاں سونپنے کا بالکل عقلی، منطقی اور اخلاقی تقاضا یہی ہے کہ اِسی بنیاد پر آخر کار اُس کا محاسبہ کیا جائے اور اسے جزا یا سزا دی جائے۔ آخر میں کفّارِ مکہ کے اِس سوال کا جواب دیا گیا ہے کہ وہ قیامت آئے گی کب؟ یہ سوال وہ رسول اللہ صلی اللہ علیہ وسلم سے بار بار کرتے تھے۔ جواب میں فرمایا گیا ہے کہ اُس وقت کا علم اللہ کے سوا کسی کو نہیں ہے۔ رسول کا کام صرف خبر دار کر دینا ہے کہ وہ وقت آئے گا ضرور۔۔ اب جس کا جی چاہے اس کے آنے کا خوف کر کے اپنا رویّہ درست کر لے ، اور جس کا جی چاہے بے خوف ہو کر شتر بے مہا ر کی طرح چلتا رہے۔ جب وہ وقت آجائے گا تو وہی لوگ جو اس دنیا کی زندگی پر مرے مٹتے تھے اور اسی کو سب کچھ سمجھتے تھے، یہ محسوس کریں گے کہ دنیا میں وہ صرف گھڑی بھر ٹھیر ے تھے۔ اُس وقت انہیں معلوم ہو گا کہ اس چند روزہ زندگی کی خاطر انہوں نے کس طرح ہمیشہ کے لیے اپنا مستقبل بر باد کر لیا۔ ','سید ابو اعلیٰ مودودیؒ -  تفہیم القرآن');
INSERT INTO "chapters_ur" VALUES(80,'تیوری چڑہائی',NULL,' نام : پہلے ہی لفظ عَبَسَ کو اس سورہ کا نام قرار دیا گیا ہے۔ زمانۂ نزول : مفسرین و محدّثین نے بالاتفاق اِس سُورہ کا سببِ نزول یہ بیان کیا ہے کہ ایک مرتبہ رسول اللہ صلی اللہ علیہ وسلم کی مجلس میں مکّہ معظمہ کے چند بڑے سردار بیٹھے ہوئے تھے اور حضورؐ اُن کو اسلام قبول کرنے پر آمادہ کرنے کی کوشش فرمارہے تھے۔ اتنے میں ابنِ اُمِّ مکتوم نامی ایک نابینا حضورؐ کی خدمت میں حاضر ہوئے اور انہوں نے آپ سے اسلام کے متعلق کچھ پوچھنا چاہا۔ حضورؐ کو ان کی یہ مداخلت ناگوار ہوئی اور آپؐ نے اُن سے بے رُخی برتی۔ اِس پر اللہ تعالیٰ کی طرف سے یہ سُورہ نازل ہوئی۔ اِس تاریخی واقعہ سے اِس سُورہ کا زمانہ نزول بآسانی متعیّن ہو جاتا ہے۔ اوّلاً یہ بات ثابت ہے کہ حضرت ابن ام مکتوم بالکل ابتدائی دور کے اسلام لانے والوں میں سے ہیں۔ حافظ ابنِ حَجَر اور حافظ ابن کثیر تصریح کرتے ہیں اَسْلَمَ بِمَکَّۃَ قَدِیْماً، اور ھُوَ مِمَّنْ اَسْلَمَ قَدِیْماً، یعنی یہ اُن لوگوں میں سے تھے جو مکّہ معظمہ میں بہت پہلے اسلام لائے تھے۔ ثانیاً حدیث کی جن روایات میں یہ واقعہ بیان کیا گیا ہے ان میں سے بعض سے معلوم ہوتا ہے کہ اُس وقت وہ اسلام لا چکے تھے اور بعض سے ظا رہ ہوتا ہے کہ اسلام کی طرف مائل ہوچکے تھے اور تلاشِ حق میں حضورؐ کے پاس آئے تھے۔ حضرت عائشہؓ کا بیان ہے کہ اُنہوں نے آکر عرض کیا تھا: یارسول اللہ ، ارشدنی، ”یارسول اللہ، مجھے سیدھا راستہ بتائے“(ترمذی،حاکم،ابن حبان، ابن جریر، ابو یعلیٰ)۔ حضرت عبداللہ بن عابس کی روایت ہے کہ وہ آکر قرآن کی ایک آیت کا مطلب پوچھنے لگے اور حضورؐ سے عرض کیا یارسول اللہ علّمنی ممّا علّمک اللہ، ” یارسول اللہ، مجھے وہ علم سکھائیے جو اللہ نے آپ کو سکھایا ہے“(ابن جریر، ابن ابی حاتم)۔ اِن بیانات سے معلوم ہوتا ہے کہ وہ حضورؐ کو خدا کا رسول اور قرآن کو خدا کی کتاب تسلیم کرچکے تھے۔ دوسری طرف ابنِ زید آیت کے ۳ الفاظ لَعَلَّہٗ یَزَّکّٰی کا مطلب لَعَلَّہٗ یُسْلم ” شاید کہ وہ اسلام قبول کر لے“بیان کرتے ہیں (ابن جریر)۔ اور اللہ تعالیٰ کا اپنا یہ ارشاد بھی کہ”تمہیں کیا خبر، شاید وہ سُدھر جائے یا نصیحت پر دھیان دے اور نصیحت کرنا اُس کے لیے نافع ہو“؟ اور یہ کہ ”جو خود تمہارے پاس دوڑ آتا ہے اور وہ ڈر رہا ہوتا ہے، اُس سے تم بے رُخی برتتے ہو“ اِس بات کی طرف اشارہ کرتا ہے کہ اُس وقت اُن کے اندر طلب ِ حق کا گہرا جذبہ پیدا ہو چکا تھا، وہ حضورؐ ہی کو ہدایت کا منبع سمجھ کر آپ کی خدمت میں اس لیے حاضر ہوئے تھے کہ اُن کی یہ طلب یہیں سے پُوری ہوگی، اور یہ بات ان کی حالت سے ظارہ ہو رہی تھی کہ انہیں ہدایت دی جائے تو وہ اس سے مستفید ہوں گے۔ ثالثاً حضورؐ کی مجلس میں جو لوگ اُس وقت بیٹھے تھے ، مختلف روایات میں اُن کے ناموں کی صراحت کی گئی ہے۔اس فہرست میں ہمیں عُتْبہ، شَیْبہ، ابو جہل ، اُمیَّہ ، اُبَیّ بن خلف جیسے بد ترین دشمنانِ اسلام کے نام ملتے ہیں ۔ اس سے معلو م ہوتا ہے کہ واقعہ اُس زمانے میں پیش آیاتھا جب رسول اللہ صلی اللہ علیہ وسلم کے ساتھ اِن لوگوں کا میل جول ابھی باقی تھا اور کشمکش اتنی بہ بڑھی تھی کہ آپ کے ہاں اُن کی آمد و رفت اور آپ کے ساتھ ان کی ملاقاتوں کا سلسلہ بند ہو گیا ہو۔ یہ سب امور اس پر دلالت کرتے ہیں کہ یہ سُورہ بہت ابتدائی زمانے کی نازل شُدہ سُورتوں میں سے ہے۔ موضوع اور مضمون: بظاہر کلام کے آغاز کا اندازِ بیان دیکھ کر آدمی یہ محسوس کرتا ہے کہ نا بینا سے بے رُخی برتنے اور بڑے بڑے سرداروں کی طرف توجہ کرنے کی بنا پر اِس سورہ میں نبی صلی اللہ علیہ وسلم پر عتاب فرمایا گیا ہے۔ لیکن پُوری سُورۃ پر مجموعی حیثیت سے غور کیا جائے تو معلوم ہوتا ہے کہ دراصل عِتاب کفارِ قریش کے اُن سرداروں پر کیا گیا ہے جو اپنے تکبُّر اور ہٹ دھرمی اور صداقت سے بے نیازی کی بنا پر رسول اللہ صلی اللہ علیہ وسلم کی تبلیغِ حق کو حقارت کے ساتھ رد کر رہے تھے، اور حضورؐ کو تبلیغ کا صحیح طریقہ بتانے کے ساتھ ساتھ اُس طریقے کی غلطی سمجھائی گئی ہے جو اپنی رسالت کے کام کی ابتدا میں آپ کو اختیار فرمار ہے تھے۔ آپ کا ایک نابینا سے بے رُخی بر تنا اور سردارانِ قریش کی طرف توجہ کرنا کچھ اس بنا پر نہ تھا کہ آپ بڑے لوگوں کا معزّز اور ایک بیچارے نابینا کو حقیر سمجھتے تھے، اور معاذ اللہ یہ کوئی کج خُلقی آپؐ کے اندر پائی جاتی تھی جس پر اللہ تعالیٰ نے گرفت فرمائی۔ بلکہ معاملہ کی اصل نوعیت یہ ہے کہ ایک داعی جب اپنی عورت کا آغاز کرنے لگتا ہے تو فطری طور پر اس کا رُحجان اِس طرف ہوتا ہے کہ قوم کے با ثر لوگ اس کی دعوت قبول کر لیں تا کہ کام آسان ہو جائے، ورنہ عام بے اثر، معذور یا کمزور لوگوں میں دعوت پھیل بھی جائے تو اس سے کوئی بڑا فرق نہیں پڑ سکتا۔ قریب قریب یہی طرز عمل ابتدا میں رسول اللہ صلی اللہ علیہ وسلم نے بھی اختیار فرمایا تھا جس کا محرّک سراسر اخلاص اور دعوت ِ حق کو فروغ دینے کا جذبہ تھا نہ کہ بڑے لوگو ں کو تعظیم اور چھوٹے لوگوں کی تحقیر کا تخیُّل۔ لیکن اللہ تعالی ٰ نے آپ کو سمجھا یا کہ اسلامی دعوت کو صحیح طریقہ یہ نہیں ہے، بلکہ اِس دعوت کے نقطئہ نظر سے ہر وہ انسا ن اہمیت رکھتا ہے جو طالبِ حق ہو، چاہے وہ کیسا ہی کمزور ، بے اثر، یا معذور ہو، اور ہر وہ شخص غیر اہم ہے جو حق سے بے نیازی برتے، خواہ وہ معاشرے میں کتنا ہی بڑا مقام رکھتا ہو۔ اس لیے آپ اسلام کی تعلیمات تو ہانکے پکارے سب کو سنا ئیں ، مگر آپ کی توجہ کے اصل مستحق وہ لوگ ہیں جن میں قبولِ حق کی آمادگی پائی جاتی ہو، اور آپ کی بلند پایہ دعوت کے مقام سے یہ بات فروتر ہے کہ آپ اِسے اُن مغرور لوگوں کے آگے پیش کریں جو اپنی بڑائی کے گھمنڈ میں یہ سمجھتے ہوں کہ اُن کو آپ کی نہیں بلکہ آپ کو اُن کی ضرورت ہے۔ یہ آغاز سورہ سے آیت ۱۶ تک کا مضمون ہے۔ اس کے بعد آیت ۱۷ سے اسے براہِ راست عتاب کا رُخ ان کفار کی طرف پھر جاتا ہے جو رسول اللہ صلی اللہ علیہ وسلم کی دعوت کو رد کر رہے تھے۔ اس میں پہلے اُس رویّے پر اُنہیں ملامت کی گئی ہے جو وہ اپنے خالق و رازق پر وردگار کے مقابلے میں برت رہے تھے، اور آخر میں ان کو خبر دار کیا گیا ہے کہ قیامت کے روز وہ اپنی اس روش کا کیسا ہولناک انجام دیکھنے والے ہیں۔ ','سید ابو اعلیٰ مودودیؒ -  تفہیم القرآن');
INSERT INTO "chapters_ur" VALUES(81,'لپیٹنا',NULL,' نام : پہلی ہی آیت کے لفظ کُوِّرَتْ سے ماخوذ ہے۔ کُوِّرَتْ تکویر سے صیغہ ماضی مجہول ہے جس کے معنی ہیں لپیٹی گئی۔ اس نام سے مراد یہ ہے کہ وہ سورت جس میں لپیٹنے کا ذکر آیاہے۔ زمانۂ نزول : مضمون اور اندازِ بیان سے صاف محسوس ہوتا ہے کہ مکّہ معظمہ کے ابتدائی دور کی نازل شدہ سورتوں میں سے ہے۔ موضوع اور مضمون: اس کے دو موضوع ہیں۔ ایک آخرت، دوسرے رسالت۔ پہلی چھ آیتوں میں قیامت کے پہلے مرحلے کاذکر کیا گیا ہے جب سورج بےنور ہو جائے گا، ستارے بکھر جائیں گے، پہاڑی زمین سے اکھڑ کر اڑنے لگیں گے، لوگوں کو اپنی عزیز ترین چیزوں تک کا ہوش نہ رہیگا، جنگلوں کے جانور بد حواس ہو کر اکھٹے ہو جائی گے اور سمندر بھڑک اٹھیں گے۔ پھر سات آیتوں میں دوسرے مر حلے کا ذکر ہے جب روحیں از سر نو جسموں کے ساتھ جودی جائیں گی، نامہ اعمال کھولے جائیں گے، جرائم کی باز پرس ہوگی، آسمان کے سارے پردے ہٹ جائیں گے اور دوزخ جنّت سب چیزیں نگاہوں کے سامنے عیاں ہو جائیں گی۔ آخرت کا یہ سارا نقشہ کھینچنے کے بعد یہ کہہ کر انسان کو سوچنے کے لیے چھوڑ دیا گیا ہے کہ اُس وقت ہر شخص کو خود ہی معلو م ہو جائے گا کہ وہ کیا لے کر آیاہے۔ اس کے بعد رسالت کا مضمون لیا گیا ہے ۔ اس میں اہلِ مکّہ سے کہا گیا ہے کہ محمد صلی اللہ علیہ وسلم جو کچھ تمہارے سامنے پیش کر رہے ہیں وہ کسی دیوانے کی بڑ نہیں ہے، نہ کسی شیطان کا ڈالا ہوا وسوسہ ہے، بلکہ خدا کے بھیجے ہوئے ایک بزرگ، عالی مقام اور امانت دار پیغام بر کا بیان ہے جسے محمد صلی اللہ علیہ وسلم نے کھلے آسمان کے اُفق پر دن کی روشنی میں اپنی آنکھوں سے دیکھا ہے۔ اِ س تعلیم سے منہ موڑ کر آخر تم کدھر چلے جا رہے ہو؟','سید ابو اعلیٰ مودودیؒ -  تفہیم القرآن');
INSERT INTO "chapters_ur" VALUES(82,'ٹکڑے ہونا',NULL,' نام : پہلی ہی آیت کے لفظ اِنْفَطَرَتْ سے ماخوذ ہے۔ اِنفِطار مصدر ہے جس کے معنی پھٹ جانے کے ہیں۔ اس نام کا مطلب یہ ہے کہ یہ وہ سورت ہےجس میں آسمان پھٹ جانے کا ذکر آیا ہے۔ زمانۂ نزول : اس کا اور سورہ تکویر کا مضمون ایک دوسرے سے نہایت مشابہ ہے۔ اس سے معلوم ہوتا ہے کہ دونوں سورتیں قریب قریب ایک ہی زمانے میں نازل ہوئی ہیں۔ موضوع اور مضمون: اس کاموضوع آخرت ہے۔ مُسند احمد، ترمذی، ابن المُنذر، طَبرانی، حاکم اور ابن مردُویہ کی روایت ہے کہ حضرت عبد اللہؓ بن عمر نے رسول اللہ صلی اللہ علیہ وسلم کا یہ ارشاد بیان کیا: مَنْ سَرَّہٗ اَن ینظر الیٰ یوم القیمٰۃ کانہ رأی عین فلیقرأ اِذَا الشَّمْسُ کُوِّرَتْ، وَاِذَ السَّمَآءُ انْفَطَرَتْ، وَاِذَ السَّمَآءُ انْشَقَّتْ۔ "جو شخص چاہتا ہو کہ روز قیامت کو اِس طرح دیکھ لے جیسے آنکھوں سے دیکھا جاتا ہے تو وہ سورہ تکویر اور سورہ اِنفِطار، اور سورہ انشقاق کو پڑھ لے"۔ اس میں سب سے پہلے روزِ قیامت کا نقشہ کھینچا گیا ہے اور یہ بتایا گیا ہے کہ جب وہ پیش آجائیگا تو ہر شخص کے سامنے اس کا کیا دھرا سب آجائے گا۔ اس کے بعد انسان کو احساس دلایا گیا ہے کہ جس رب نے تجھ کو وجود بخشا اور جس کے فضل و کرم کی وجہ سے آج تو سب مخلوقات سے بہتر جسم اور اعضاء لیے پھرتا ہے، اس کے بارے میں یہ دھوکا تجھے کہاں سے لگ گیا کہ وہ صرف کرم ہی کرنے والا ہے، انصاف کرنے والا نہیں ہے؟ اُس کے کرم کے معنی یہ تو نہیں ہیں کہ تو اس کے انصاف سے بے خوف ہوجائے۔ پھر انسان کو خبر دار کیا گیا ہے کہ تو کسی غلط فہمی میں مبتلا نہ رہ، تیرا پورا نامہ اعمال کیا جا رہا ہے۔ اور نہایت معتبر کاتب ہر وقت تیری تمام حرکات و سکنات کو نوٹ کر رہے ہیں۔ آخر میں پورے زور کے ساتھ کہا گیا ہے کہ یقیناً روز جزا برپا ہونے والا ہے جس میں نیک لوگوں کو جنّت کا عیش اور بد لوگوں کو جہنم کا عذاب نصیب ہو گا۔اس روز کوئی کسی کے کام نہ آسکے گا ، فیصلے کے اختیارات بالکل اللہ کے ہاتھ میں ہونگے۔','سید ابو اعلیٰ مودودیؒ -  تفہیم القرآن');
INSERT INTO "chapters_ur" VALUES(83,'ناپ تول میں کمی کرنے والے',NULL,' نام : پہلی ہی آیت وَیْلٌ لِّلْمُطَفِّفِیْنَ سے ماخوذ ہے۔ زمانۂ نزول : اس کے اندازِبیان اور مضامین سے صاف معلوم ہوتا ہے کہ یہ مکہ معظمہ کے ابتدائی دور میں نازل ہوئی ہے جب اہل مکہ کے ذہن میں آخرت کا عقیدہ بٹھانے کے لیے پے در پے سورتیں نازل ہورہی تھیں، اور اس کا نزول اُس زمانے میں ہوا ہے جب اہل مکہ نے سڑکوں پر، بازاروں میں اور مجلسو ں میں مسلمانوں پر آوازے کسنے اور ان کی توہین ذنذیل کرنے کا سلسلہ شروع کر دیا تھا، مگر ظلم و ستم اور مار پیٹ کا دورا بھی شروع نہیں ہوا تھا۔۔ بعض مفسرین نے اس سورہ کو مدنی قرار دیا ہے۔اِس غلط فہمی کی وجہ دراصل ابن عباسؓ کی یہ روایت ہے کہ جب نبی صلی اللہ علیہ وسلم مدینے تشریف لائے تو یہاں کے لوگوں میں کم ناپنے اور تولنے کا مرض بُری طرح پھیلا ہوا تھا۔ پھر اللہ تعالیٰ نے وَیْلٌ لِّلْمُطَفِّفِیْنَ نازل کی اور لو گ بہت اچھی طرح ناپنے تولنے لگے (نسائی، ابن ماجہ، ابن مردویہ، ابن جریر، بیقہی فی شُعَب الایمان)۔ لیکن جیسا کہ اس سے پہلے ہم سورہ دَہر کے دیباچے میں بیان کر چکے ہیں، صحابہ اور تابعین کا یہ عام طریقہ تھا کہ ایک آیت جس معاملہ پر چسپاں ہوتی ہو اس کے متعلق وہ یوں کہا کرتے تھے کہ یہ فلاں معاملہ میں نازل ہوئی ہے۔ اس لیے ابن عباس کی روایت سے جو کچھ ثابت ہوتا ہے وہ صرف یہ ہے کہ جب ہجرت کے بعد نبی صلی اللہ علیہ وسلم نے مدینہ کے لوگوں میں یہ بُری عادت پھیلی ہوئی پائی تو اللہ تعالیٰ کے حکم سے آپ نے یہ سورت ان کو سنائی اور اِس سے اُن کے معاملات درست ہوگئے۔ موضوع اور مضمون: اس کاموضوع بھی آخرت ہے۔ پہلی چھ آیتوں میں اُس عام بے ایمانی پر گرفت کی گئی ہے جو کاروباری لوگوں میں بکثرت پھیلی ہوئی تھی کہ دوسروں سے لینا ہوتا تھا تو پورا ناپ کر اور تول کر لیتے تھے، مگر جب دوسروں کو دینا ہوتا تو ناپ تول میں ہر ایک کو کچھ نہ کچھ گھاٹا دیتے تھے۔ معاشرے کی بے شمار خرابیوں میں سے اِس ایک خرابی کو، جس کی قباحت سے کوئی انکار نہ کر سکتا تھا، بطورِ مثال لے کر یہ بتایا گیا ہے کہ یہ آخرت سے غفلت کا لازمی نتیجہ ہے۔ جب تک لوگوں کو یہ احساس نہ ہو کہ ایک روز خدا کے سامنے پیش ہونا ہے اور کوڑی کوڑی کا حساب دینا ہے اُس وقت تک یہ ممکن ہی نہیں ہے کہ وہ اپنے معاملات میں کامل راستبازی اختیار کر سکیں۔ کوئی شخص دیانت داری کو”اچھی پالیسی“سمجھ کر بعض چھوٹے چھوٹے معاملات میں دیانت برت بھی لے تو ایسے مواقع پر وہ کبھی دیانت نہیں برت سکتا جہاں بے ایمانی ایک”مفید پالیسی“ثابت ہوتی ہو۔ آدمی کے اندر سچّی اور مستقل دیانت داری اگر پیدا ہو سکتی ہے تو صرف خدا کے خوف اور آخرت پر یقین ہی سے ہو سکتی ہے، کیونکہ اِس صورت میں دیانت ایک ”پالیسی“نہیں بلکہ”فریضہ“قرار پاتی ہے اور آدمی کے اُس پر قائم رہنے یا نہ رہنے کا انحصار دنیا میں اس کے مفید یا غیر مفید ہونے پر نہیں رہتا ۔ اس طر ح اخلاق کے ساتھ عقیدہ آخرت کا تعلق نہایت مؤثر اور دل نشین طریقہ سے واضح کرنے کے بعد آیت ۷ سے ۱۷ تک بتایا گیا ہے کہ بد کار لوگوں کے نامہ اعمال پہلے ہی جرائم پیشہ لوگوں کے رجسٹر( Black list ) میں درج ہورہے ہیں اور آخرت میں ان کو سخت تباہی سے دو چار ہونا ہے۔پھرآیت ۱۸ سے۲۸ تک نیک لوگوں کا بہترین انجام بیان کیا گیا ہے اور یہ بتایا گیا ہے کہ ان کے اعمال نامے بلند پایہ لوگوں کے رجسٹر میں درج ہورہے ہیں جس پر مقرب فرشتے مامور ہیں۔ آخر میں اہل ایمان کو تسلی دی گئی ہے اور اس کے ساتھ کفار کو خبر دار بھی کیا گیا ہے کہ آج جو لوگ ایمان لانے والوں کی تذلیل کر رہے ہیں، قیامت کے روز یہی مجرم لوگ اپنی اِس روش کا بہت بُرا انجام دیکھیں گے اور یہی ایمان لانے والے ان مجرموں کا بُرا انجام دیکھ کر اپنی آنکھیں ٹھنڈی کریں گے۔ ','سید ابو اعلیٰ مودودیؒ -  تفہیم القرآن');
INSERT INTO "chapters_ur" VALUES(84,'پھٹنا',NULL,' نام : پہلی ہی آیت کے لفظ اِنْشَقَّتْ سے ماخوذ ہے۔ اِنْشِقاق مصدر ہے جس کے معنی پھٹ جانے کے ہیں اور اس کانام کا مطلب یہ ہے کہ یہ وہ سورۃ ہے جس میں آسمان کے پھٹنے کا ذکر آیا ہے۔ زمانۂ نزول : یہ بھی مکّہ معظمہ کے ابتدائی دور کی نازل شدہ سورتوں میں سے ہے۔ اس کے مضمون کی داخلی شہادت یہ بتا رہی ہے کہ ابھی ظلم و ستم کا دور شروع نہیں ہوا تھا، البتہ قرآن کی دعوت کو مکّہ میں بر ملا جُھٹلا یا جا رہا تھا اور لوگ یہ ماننے سے انکار کر رہے تھے کہ کبھی قیامت بر پا ہوگی اور اُنہیں اپنے خدا کے سامنے جواب دہی کے لیے حاضر ہونا پڑے گا۔ موضوع اور مضمون: اس کاموضوع قیامت اور آخرت ہے۔ پہلی پانچ آیتوں میں نہ صرف قیامت کی کیفیت بیان کی گئی ہے بلکہ اس کے بر حق ہونے کی دلیل بھی دے دی گئی ہے۔ اُس کی کیفیت یہ بتائی گئی ہے کہ اُس روز آسمان پھٹ جائے گا، زمین پھیلا کر ہموار میدان بنا دی جائے گی، جو کچھ زمین کے پیٹ میں ہے(یعنی مردہ انسانوں کے اجزائے بدن اور ان کے اعمال کی شہادتیں) سب کو نکال کر وہ باہر پھینک دے گی، حتی کہ اس کے اندر کچھ باقی نہ رہے گا۔ اور اس کی دلیل یہ دی گئی ہے کہ آسمان و زمین کے لیے اُن کے رب کا حکم یہی ہوگا اور چونکہ دونوں اُس کی مخلوق ہیں اس لیے وہ اس کے حکم سے سرتابی نہیں کر سکتے، اُن کے لیے حق یہی ہے کہ وہ اپنے رب کے حکم کی تعمیل کریں۔ اس کے بعد آیت ۶ سے ۱۹ تک بتایا گیا ہے کہ انسان کو خواہ اِس کا شعور ہو یا نہ ہو ، بہرحال وہ اُس منزل کی طرف چار و ناچار چلا جا رہا ہے جہاں اُسے اپنے رب کے آگے پیش ہونا ہے ۔ پھر سب انسان دو حصوں میں بٹ جائیں گے۔ ایک ، وہ جن کا نامہ اعمال سیدھے ہاتھ میں دیا جائے گا۔ وہ کسی سخت حساب فہمی کے بغیر معاف کر دیے جائیں گے۔ دوسرے وہ جن کا نامہ اعمال پیٹھ کے پیچھے دیا جائے گا۔ وہ چاہیں گے کہ کسی طرح انہیں موت آجائے، مگر مرنے کے بجائے وہ جہنم میں جھونک دیے جائیں گے۔ ان کا یہ انجام اس لیے ہو گا کہ وہ دنیا میں اِس غلط فہمی پر مگن رہے کہ کبھی خدا کے سامنے جواب دہی کے لیے حاضر ہونا نہیں ہے۔ حالانکہ ان کا رب ان کے سارے اعمال کو دیکھ رہا تھا اور کوئی وجہ نہ تھی کہ وہ ان اعمال کی باز پرس سے چھوٹ جائیں۔ اُن کا دنیا کی زندگی سے آخرت کی جزا وسزا تک درجہ بدرجہ پہنچنا اُتنا ہی یقینی ہے جتنا سورج ڈوبنے کے بعد شفق کا نمودار ہونا، دن کے بعد رات کا آنا اور اس میں انسان اور حیوانات کا اپنے اپنے بسیروں کی طرف پلٹنا، اور چاند کا ہلال سے بڑھ کر ماہِ کامل بننا یقینی ہے۔ آخر میں اُن کفار کو درف ناک سزا کی خبر دے دی گئی ہے جو قرآن کو سن کر خدا کے آگے جھکنے کے بجائے الٹی تکذیب کرتے ہیں، اور اُن لوگوں کو بے حساب اجر کا مژدہ سنا دیا گیا ہے جو ایمان لا کر نیک عمل کرتے ہیں۔ ','سید ابو اعلیٰ مودودیؒ -  تفہیم القرآن');
INSERT INTO "chapters_ur" VALUES(85,'برج',NULL,' نام : پہلی آیت کے لفظ اَلْبُرُوْج کو اس کا نام قرار دیا گیا ہے۔ زمانۂ نزول : اس کا مضمون خود یہ بتا رہا ہے کہ یہ سورۃ مکہ معظمہ کے اس دور میں نازل ہوئی ہے جب ظلم و ستم پوری شدت کے ساتھ برپا تھا اور کفار مکہ مسلمانوں کو سخت سے سخت عذاب دے کر ایمان سے پھیر دینے کی کوشش کر رہے تھے۔ موضوع اور مضمون: اس کاموضوع کفار کو اس ظلم و ستم کے بُرے انجام سے خبر دار کرنا ہے جو وہ ایمان لانے والوں پر توڑ رہے تھے، اور اہلِ ایمان کو یہ تسلی دینا ہے کہ اگر وہ ان مظالم کے مقابلے میں ثابت قدم رہیں گے تو ان کو اس کا بہترین اجر ملے گا اور اللہ تعالیٰ ظالموں سے بدلہ لےگا۔ اس سلسلے میں سب سے پہلے اصحاب الاُخدود کا قصہ سنایا گیا ہے جنہوں نے ایمان لانے والوں کو آگے سے بھرے ہوئے گڑھوں میں پھینک پھینک کر جلا دیا تھا۔ اور اس قصے کے پیرا ے میں چند باتیں مومنوں اور کافروں کے ذہن نشین کرائی گئی ہیں۔ ایک یہ کہ جس طرح اصحاب الاُخدود خدا کی لعنت اور اس کی مار کے مستحق ہوئے اسی طرح سردارانِ مکہ بھی اس کے مستحق بن رہے ہیں۔ دوسرے یہ کہ جس طرح ایمان لانے والوں نے اس وقت آگ کے گڑھوں میں گر کر جان دے دینا قبول کر لیا تھا اور ایمان سے پھرنا قبول نہیں کیا تھا، اسی طرح اب بھی اہلِ ایمان کو چاہیے کہ ہر سخت سے سخت عذاب بھگت لیں مگر ایمان کی راہ سے نہ ہٹیں ۔تیسرے یہ کہ جس خدا کے ماننے پر کافر بگڑتے اور اہلِ ایمان اصرار کرتے ہیں وہ سب پر غالب ہے، زمین و آسمان کی سلطنت کا مالق ہے، اپنی ذات میں آپ حمد کا مستحق ہے، اور وہ دونوں گروہوں کے حال کو دیکھ رہا ہے ، اس لیے یہ عمل یقینی ہے کہ کافروں کو نہ صرف ان کے کفر کی سزا جہنم کی صورت میں ملے ، بلکہ اس پر مزید ان کے ظلم کی سزا بھی ان کو آگ کے چَرکے دینے کی شکل میں بھگتنی پڑے ۔ اسی طرح یہ امر بھی یقینی ہے کہ ایمان لا کر نیک عمل کرنے والے جنت میں جائیں ، اور یہی بڑی کامیابی ہے۔ پھر کفار کو خبر دار کیا گیا ہے کہ خدا کی پکڑ بڑی سخت ہے، اگر تم اپنے جتّھے کی طاقت کے زعم میں مبتلا ہو تو تم سے بڑے جتھے فرعون اور ثمو د کے پاس تھے، ان کے لشکروں کا جو انجام ہوا ان سے سبق حاصل کرو۔ خدا کی قدرت تم پر اس طرح محیط ہے کہ اُس کے گھیرے سے تم نکل نہیں سکتے ، اور قرآن، جس کی تکذیب پر تم تُلے ہوئے ہو، اس کی ہر بات اٹل ہے ، وہ اس لوحِ محفوظ میں ثَبْت ہے، جس کا لکھا کسی کے بدلے نہیں بدل سکتا۔ ','سید ابو اعلیٰ مودودیؒ -  تفہیم القرآن');
INSERT INTO "chapters_ur" VALUES(86,'رات کو آنے والا',NULL,' نام : پہلی آیت کے لفظ الطّارق کو اس کا نام قرار دیا گیا ہے۔ زمانۂ نزول : اس کے مضمون کا اندازِ بیان مکّہ معظمہ کی ابتدائی سورتوں سے ملتا جُلتا ہے ، مگر یہ اُس زمانے کی نازل شدہ ہے جب کفار مکہ قرآن اور محمد صلی اللہ علیہ وسلم کی دعوت کو زک دینے کے لیے ہر طرح کی چالیں چل رہے تھے۔ موضوع اور مضمون: اس میں وہ مضمون بیان کیے گئے ہیں۔ ایک یہ کہ انسان کو مرنے کے بعد، خدا کے سامنے حاضر ہونا ہے۔ دوسرے یہ کہ قرآن ایک قولِ فصیل ہے جسے کفار کی کوئی چال اور تدبیر زک نہیں دے سکتی۔ سب سے پہلے آسمان کے تاروں کو اِس بات کی شہادت میں پیش کیا گیا ہے کہ کائنات کی کوئی چیز ایسی نہیں ہے جو ایک ہستی کی نگہبانی کے بغیر اپنی جگہ قائم اور باقی رہ سکتی ہو۔ پھر انسان کا خود اس کی اپنی ذات کی طرف توجہ دلائی گئی ہے کہ کس طرح نطفے کی ایک بوند سے اُس کو وجود میں لایا گیا اور جیتا جاگتا انسان بنا دیا گیا۔ اس کےبعد فرمایا گیا ہے کہ جو خدا اِس طرح اُسے وجود میں لا یا ہے وہ یقیناً اُس کو دوبارہ پیدا کرنے پر قادر ہے۔ اور یہ دوبارہ پیدائش اِس غرض کے لیے ہوگی کہ انسان کے اُن تمام رازوں کی جانچ پڑتال کی جائے جن پر دنیا میں پردہ پڑا رہ گیا تھا۔ اُس وقت اپنے اعمال کے نتائج بھگتنے سے انسان نہ اپنے بل بوتے پر بچ سکے گا اور نہ کوئی اُس کی مدد کو آسکے گا۔ خاتمہ کلام پر ارشاد ہوا ہے کہ جس طرح آسمان سے بارش کا برسنا اور زمین سے درختوں اور فصلوں کا اُگنا کوئی کھیل نہیں بلکہ ایک سنجیدہ کام ہے ، اُسی طرح قرآن میں جو حقائق بیان کیے گئے ہیں وہ بھی کوئی ہنسی مذاق نہیں ہیں بلکہ پختہ اور اٹل باتیں ہیں۔ کفار اس غلط فہمی میں ہیں کہ اُن کی چالیں اِس قرآن کی دعوت کو زک دے دیں گی، مگر انہیں خبر نہیں ہے کہ اللہ بھی ایک تدبیر میں لگا ہوا ہے اور اس کی تدبیر کے آگے کفار کی چالین سب دھری کی دھری رہ جائیں گی۔ پھرا یک فقرے میں رسول اللہ صلی اللہ علیہ وسلم کو یہ تسلی اور درپردہ کفار کو یہ دھمکی دے کر بات ختم کر دی گئی ہے کہ آپؑ ذرا صبر سے کام لیں اور کچھ مدت کفار کو اپنی سی کر لینے دیں، زیادہ دیر نہ گزرے گی کہ انہیں خود معلوم ہو جائے گا کہ ان کی چالیں قرآن کو زک دیتی ہیں یا قرآن اُسی جگہ غالب آکر رہتا ہے جہاں یہ اُسے زک دینے کی کوشش کر رہے ہیں۔','سید ابو اعلیٰ مودودیؒ -  تفہیم القرآن');
INSERT INTO "chapters_ur" VALUES(87,'اعلی',NULL,' نام : پہلی ہی آیت سَبِّحِ اسْمَ رَبِّکَ الْاَعْلیٰ کے لفظ اَلْاَعْلیٰ کو اس سورہ کا نام قرار دیا گیا ہے۔ زمانۂ نزول : اِس کے مضمون سے بھی یہ معلوم ہوتا ہے کہ یہ بالکل ابتدائی دور کی نازل شدہ سورتوں میں سے ہے، اور آیت نمبر ۶ کے یہ الفاظ بھی کہ ” ہم تمہیں پڑھوا دیں گے ، پھر تم نہیں بھُولو گے“ یہ بتاتے ہیں کہ یہ اُس زمانے میں نازل ہوئی تھی جب رسول اللہ صلی اللہ علیہ وسلم کو ابھی وحی اخذ کرنے کی اچھی طرح مشق نہیں ہوئی تھی اور نزولِ وحی کے وقت آپ کو اندیشہ ہوتا تھا کہ کہیں میں اُس کے الفاظ بھول نہ جاؤں۔ اِس آیت کے ساتھ اگر سورۂ طٰہٰ کی آیت ۱۱۴، اور سورۂ قیامہ کی آیات ۱۹-۱۶ کو ملا کر دیکھا جائے ، اور تینوں آیتوں کے اندازِ بیان اور موقع و محل پر بھی غو ر کیا جائے تو واقعات کی ترتیب یہ معلوم ہوتی ہے کہ سب سے پہلے اِس سُورہ میں حضور ؐ کو اطمینان دلایا گیا کہ آپ فکر نہ کریں، ہم یہ کلام آپ کو پڑھوا دیں گے اور آپ اِسے نہ بھولیں گے۔ پھر ایک مدت کے بعد ، دوسرے موقع پر جب سورۂ قیامہ نازل ہو رہی تھی، حضور بے اختیار الفاظِ وحی کو دُہرانے لگے۔ اُس وقت فرمایا گیا کہ ” اے نبی، اِس وحی کو جلدی جلدی یاد کرنے کے لیے اپنی زبان کو حرکت نہ دو، اِس کو یاد کر ا دینا اور پڑھوا دینا ہمارے ذمّہ ہے، لہٰذا جب ہم اِسے پڑھ رہے ہوں اُس وقت تم اِس کی قرأت کو غور سے سنتے رہو، پھر اِس کا مطلب سمجھا دینا بھی ہمارے ہی ذمّہ ہے۔“ آخری مرتبہ سورۂ طٰہٰ کے نزول کے موقع پر حضور کو پھر بتقاضائے بشریت اندیشہ لاحق ہوا کہ یہ ۱۱۳ آیتیں جو متواتر نازل ہوئی ہیں اِن میں سے کوئی چیز میرے حافظے سے نکل نہ جائے اور آپ اُن کو یاد کرنے کی کوشش کرنے لگے۔ اس پر فرمایا گیا ” اور قرآن پڑھنے میں جلدی نہ کیا کرو جب تک تمہاری طرف اس کی وحی تکمیل کو نہ پہنچ جائے۔“ اِس کے بعد پھر کبھی اِس کی نوبت نہیں آئی کہ حضور ؐ کو ایسا کوئی خطرہ لاحق ہوتا، کیونکہ اِن تین مقامات کے سوا کوئی چوتھا مقام قرآن میں ایسا نہیں ہے جہاں اِس معاملہ کی طرف کوئی اشارہ پایا جاتا ہو۔ موضوع اور مضمون: اِس چھوٹی سی سورۃ کے تین موضوع ہیں۔ توحید۔ نبی صلی اللہ علیہ وسلم کو ہدایات ۔ اور آخرت۔ پہلی آیت میں توحید کی تعلیم کو اِس ایک فقرے میں سمیٹ دیا گیا ہے کہ اللہ تعالیٰ کے نام کی تسبیح کی جائے، یعنی اُس کو کسی ایسے نام سے یا د نہ کیا جائے جو اپنے اندر کسی قسم کے نقص، عیب، کمزوری یا مخلوقات سے تشبیہ کا کوئی پہلو رکھتا ہو۔ کیونکہ دنیا میں جتنے بھی فاسد عقائد پیدا ہوئے ہیں اُن سب کی جڑ اللہ تعالیٰ کے متعلق کوئی نہ کوئی غلط تصور ہے جس نے اُس ذاتِ پاک کے لیے کسی غلط نام کی شکل اختیار کی ہے۔ لہٰذا عقیدے کی تصحیح کے لیے سب سے مقدم یہ ہے کہ اللہ جل شانہ کو صرف اُن اسماء حسنیٰ ہی سے یا د کیا جائے جو اُس کے لیے موزوں اور مناسب ہیں۔ اِس کے بعد تین آیتوں میں بتایا گیا ہے کہ تمہارا رب، جس کے نام کی تسبیح کا حکم دیا جا رہا ہے، وہ ہے جس نے کائنات کی ہر چیز کو پیدا کیا، اُس کا تناسب قائم کیا، اُس کی تقدیر بنائی، اُسے وہ کام انجام دینے کی راہ بتائی جس کے لیے وہ پیدا کی گئی ہے، اور تم اپنی آنکھوں سے اُس کی قدرت کا یہ کرشمہ دیکھ رہے ہو کہ وہ زمین پر نباتات کو پیدا بھی کرتا ہے اور پھر انہیں خس و خاشاک بھی بنا دیتا ہے۔ کوئی ہستی نہ بَہار لانے پر قادر ہے نہ خزاں کو آنے سے روک سکتی ہے۔ پھر دو آیتوں میں رسول اللہ صلی اللہ علیہ وسلم کو ہدایت فرمائی گئی ہے کہ آپ اِس فکر میں نہ پڑیں کہ یہ قرآن جو اپ پر نازل کیا جا رہا ہے ، یہ لفظ بلفظ آپ کو یاد کیسے رہے گا۔ اس کو آپ کے حافظے میں محفوظ کر دینا ہمارا کام ہے، اور اس کا محفوظ رہنا آپ کے کسی ذاتی کمال کا نتیجہ نہیں بلکہ ہمارے فضل کا نتیجہ ہے، ورنہ ہم چاہیں تو اسے بھُلا دیں۔ اِس کے بعد رسول اللہ صلی اللہ علیہ وسلم سے فرمایا گیا ہے کہ آپ کے سپرد ہر ایک کو راہِ راست پر لے آنے کا کام نہیں کیا گیا ہے بلکہ آپ کا کام بس حق کی تبلیغ کر دینا ہے ، اور تبلیغ کا سیدھا سادھا طریقہ یہ ہے کہ جو نصیحت سننے اور قبول کر نے کے لیے تیار ہوا اُسے نصیحت کی جائے اور جو اُس کے لیے تیار نہ ہو اُس کے پیچھے نہ پڑا جائے۔ جس کے دل میں گمراہی کے انجامِ بد کا خوف ہو گا وہ حق بات کو سن کر قبول کر لے گا اور جو بدبخت اُسے سننے اور قبول کرنے سے گریز کرے گا وہ اپنا بُرا انجام خود دیکھ لے گا۔ آخر میں کلام کو اِس بات پر ختم کیا گیا ہے کہ فلاح صرف اُن لوگوں کے لیے ہے جو عقائد، اخلاق اور اعمال کی پاکیزگی اختیار کریں اور اپنے رب کا نام یاد کر کے نماز پڑھیں۔ لیکن لوگوں کا حال یہ ہے کہ انہیں ساری فکر بس اِسی دنیا کے آرام و آسائش اور فائدوں اور لذتوں کی ہے ، حالانکہ اصل فکر آخرت کی ہونی چاہیے، کیونکہ دنیا فانی ہے اور آخرت باقی، اور دنیا کی نعمتوں سے آخرت کی نعمتیں بدرجہا بڑھ کر ہیں۔ یہ حقیقت صرف قرآن ہی میں نہیں بتائی جارہی ہے ، بلکہ حضرت ابراہیم ؑ اور حضرت موسیٰ ؑ کے صحیفوں میں بھی انسان کو اِسی حقیقت سے آگاہ کیا گیا تھا۔','سید ابو اعلیٰ مودودیؒ -  تفہیم القرآن');
INSERT INTO "chapters_ur" VALUES(88,'چھا جانے والی',NULL,' نام : پہلی ہی آیت کے لفظ الغاشیۃ کو اس سُورہ کا نام قرار دیا گیا ہے۔ زمانۂ نزول : سورۃ کا پورا مضمون اِس امر پر دلالت کرتا ہے کہ یہ بھی ابتدائی زمانہ کی نازل شدہ سورتوں میں سے ہے، مگر یہ وہ زمانہ تھا جب حضور ؐ تبلیغِ عام شروع کر چکے تھے اور مکّہ کے لوگ بالعموم اُسے سُن سُن کر نظر انداز کیے جا رہے تھے۔ موضوع اور مضمون: اِس کے موضوع کو سمجھنے کے لیے یہ بات نگاہ میں رہنی چاہیے کہ ابتدائی زمانہ میں رسول اللہ صلی اللہ علیہ وسلم کی تبلیغ زیادہ تر دو ہی باتیں لوگوں کے ذہن نشین کرنے پر مرکوز تھی۔ ایک توحید ،دوسرے آخرت۔ اور اہلِ مکہ اِن دونوں باتوں کو قبول کرنے سے انکار کر رہے تھے ۔ اِس پس منظر کو سمجھ لینے کے بعد اب اِس سورہ کے مضمون اور اندازِ بیان پر غور کیجیے۔ اس میں سب سے پہلےغفلت میں پڑے ہوئے لوگوں کو چَونکانے کے لیے اچانک اُن کے سامنے یہ سوال پیش کیا گیا ہے کہ تمہیں اُ س وقت کی بھی کچھ خبر ہے جب سارے عالَم پر چھا جانے والی ایک آفت نازل ہو گی؟ اِس کے بعد فوراً ہی یہ تفصیل بیان کرنی شروع کر دی گئی ہے کہ اُس وقت سارے انسان دو مختلف گروہوں میں تقسیم ہو کر دو مختلف انجام دیکھیں گے۔ ایک وہ جو جہنّم میں جائیں گے اور اُنہیں ایسے اور ایسے سخت عذاب جھیلنے ہوں گے۔ دوسرے وہ جو عالی مقام جنّت میں جائیں گے اور اُن کو ایسی اور ایسی نعمتیں میسّر ہوں گی۔ اس طرح لوگوں کو چونکانے کے بعد یکلخت مضمون تبدیل ہوتا ہے اور سوال کیا جاتا ہے کہ یہ لوگ جو قرآن کی تعلیم ِ توحید اور خبرِ آخرت کو سُن کر نا ک بھوں چڑھا رہے ہیں، اپنے سامنے کی اُن چیزوں کو نہیں دیکھتے جن سے ہر وقت انہیں سابقہ پیش آتا ہے؟ عرب کے سحرا میں جن اُونٹوں پر ان کی ساری زندگی کا انحصار ہے، کبھی یہ لوگ غور نہیں کرتے کہ یہ کیسے ٹھیک اُنہی خصوصیات کے مطابق بن گئے جیسی خصوصیات کے جانور کی ضرورت ان کی صحرائی زندگی کے لیے تھی؟ اپنے سفروں میں جب یہ چلتے ہیں تو انہیں یا آسمان نظر آتا ہے، یا پہاڑ، یا زمین ۔ انہی تین چیزوں پر یہ غو ر کریں۔ اوپر یہ آسمان کیسے چھا گیا؟ سامنے یہ پہاڑ کیسے کھڑے ہو گئے؟ نیچے یہ زمین کیسے بِچھ گئی؟ کیا یہ سب کسی قادرِ مطلق صانعِ حکیم کی کاریگری کے بغیر ہو گیا ہے؟ اگر یہ مانتے ہیں کہ ایک خالق نے بڑی حکمت اور بڑی قدرت کےساتھ ان چیزوں کو بنایا ہے ، اور کوئی دوسرا ان کی تخلیق میں شریک نہیں ہے ، تو اسی کو اکیلا ربّ ماننے سے انہیں کیوں انکار ہے؟ اور اگر یہ مانتے ہیں کہ وہ خدا یہ سب کچھ پیدا کرنے پر قادر تھا ، تو آخر کس معقول دلیل سے انہیں یہ ماننے میں تأمُّل ہے کہ وہی خدا قیامت لانے پر بھی قادر ہے؟ انسانوں کو دوبارہ پیدا کرنے پر بھی قادر ہے؟ جنت اور دوزخ بنانے پر بھی قادر ہے؟ اس مختصر اور نہایت معقول استدلال سے بات سمجھانے کے بعد کفّار کی طرف سے رُخ پھیر کر نبی صلی اللہ علیہ وسلم کو مخاطب کیا جاتا ہے اور آپ ؐ سے ارشاد ہوتا ہے کہ یہ لوگ نہیں مانتے تو نہ مانیں ، تم اِن پر تو جبّار بنا کر تو مسلط کیے گئے نہیں ہو کہ زبردستی ان سے منوا کر ہی چھوڑو۔ تمہارا کام نصیحت کرنا ہے، سو تم نصیحت کیے جاؤ۔ آخر کار انہیں آنا ہمارے ہی پاس ہے۔ اُس وقت ہم ان سے پورا پورا حساب لے لیں گے اور نہ ماننے والوں کو بھاری سزا دیں گے۔ ','سید ابو اعلیٰ مودودیؒ -  تفہیم القرآن');
INSERT INTO "chapters_ur" VALUES(89,'فجر',NULL,' نام : پہلے ہی لفظ وَالْفَجْر کو اس کا نام قرار دیا گیا ہے۔ زمانۂ نزول : اِس کے مضامین سے ظاہر ہوتا ہے کہ یہ اُس زمانے میں نازل ہوئی تھی جب مکّۂ معظمہ میں اسلام قبول کرنے والوں کے خلاف ظلم کی چکّی چلنی شروع ہو چکی تھی۔ اس بنا پر اہلِ مکّہ کو عاد اور ثمود اور فرعون کے انجام سے خبردار کیا گیا ہے۔ موضوع اور مضمون: اِس کا موضوع آخرت کی جزا اور سزا کا اثبات ہے جس کا اہلِ مکہ انکار کر رہے تھے۔ اِس مقصد کے لیے جس طرح ترتیب وار استدلال کیا گیا ہے، اس کو اسی ترتیب کے ساتھ غور سے دیکھیے۔ سب سے پہلے فجر اور دس راتوں اور جُفت اور طاق ، اور رخصت ہوتی ہوئی رات کی قسم کھا کر سامعین سے سوال کیا گیا ہے کہ جس بات کا تم انکار کر رہے ہو اس کے برحق ہونے کی شہادت دینے کے لیے کیا یہ چیزیں کافی نہیں ہیں؟ آگے حواشی میں ان چاروں چیزوں کی جو تشریح ہم نے کی ہے اس سے آپ کو معلوم ہو جائے گا کہ یہ چیزیں اُس باقاعدگی کی علامت ہیں جو شب و روز کے نظام میں پائی جاتی ہیں ، اور ان کی قسم کھا کر یہ سوال اس معنی میں کیا گیا ہے کہ خدا کے قائم کیے ہوئے اِس حکیمانہ نظام کو دیکھنے کے بعد بھی کیا اِس امر کی شہادت دینے کے لیے کسی اور چیز کی ضرورت باقی رہ جاتی ہے کہ یہ نظام جس خدا نے قائم کیا ہے اُس کی قدرت سے یہ بعید نہیں ہے کہ وہ آخرت برپا کرے، اور اس کی حکمت کا یہ تقاضا ہے کہ انسان سے اس کے اعمال کی باز پُرس کرے ؟ اس کے بعد انسانی تاریخ سے استدلال کرتے ہوئے بطورِ مثال عاد اور ثمود اور فرعون کے انجام کو پیش کیا گیا ہے کہ جب وہ حد سے گزر گئے اور زمین میں انہوں نے بہت فساد مچایا تو اللہ کے عذاب کا کوڑا اُن پر برس گیا۔ یہ اِس بات کی علامت ہے کہ کائنات کا نظام کچھ اندھی بہری طاقتیں نہیں چلا رہی ہیں، نہ یہ دنیا کسی چَوپَٹ راجا کی اندھیر نگری ہے ، بلکہ ایک فرمانروائے حکیم و دانا اس پر حکمرانی کر رہا ہے جس کی حکمت اور عدل کا یہ تقاضا خود اس دنیا میں انسانی تاریخ کے اندر مسلسل نظر آتا ہے۔ کہ عقل اور اخلاقی حِس دے کر جس مخلوق کو اس نے یہاں تصرَّف کے اختیارات دیے ہیں اس کا محاسبہ کرے اور اسے جزا اور سزا دے۔ اس کے بعد انسانی معاشرے کی عام اخلاقی حالت کا جائزہ لیا گیا ہے جس میں عربِ جاہلیت کی حالت تو اُس وقت سب کے سامنے عملًا نمایاں تھی، اور خصوصیت کے ساتھ اس کے دو پہلوؤں پر تنقید کی گئی ہے۔ایکؔ لوگوں کا مادّہ پرستانہ نطقۂ نظر جس کی بنا پر وہ اخلاقی بھلائی اور برائی کو نظر انداز کر کے محض دنیا کی دولت اور جاہ و منزلت کے حصول یا فقدان کو عزّت و ذلّت کا معیار قرار دیے بیٹھے تھے اور اس بات کو بھول گئے تھے کہ نہ دولت مندی کوئی انعام ہے ، نہ رزق کی تنگی کوئی سزا، بلکہ اللہ تعالیٰ ان دونوں حالتوں میں انسان کا امتحان لے رہا ہے کہ دولت پا کر وہ کیا رویّہ اختیار کرتا ہے اور تنگد ستی میں مبتلا ہو کر کس روش پر چل پڑتا ہے۔ دوسرےؔ ، لوگوں کا یہ طرزِ عمل کہ یتیم بچہ باپ کے مرتے ہی ان کے ہاں کس مپرسی میں مبتلا ہو جاتا ہے، غریبوں کا کوئی پرسانِ حال نہیں ہوتا، جس کا بس چلتا ہے مُردے کی ساری میراث سمیٹ کر بیٹھ جاتا ہے اور کمزور حقداروں کو دَھتا بتا دیتا ہے، اور مال کی حِرص لوگوں کو ایک ایسی نہ بُجھنے والی پیاس کی طرح لگی ہوئی ہے کہ خواہ کتنا ہی مال مل جائے ان کا دل سیر نہیں ہوتا۔ اس تنقید سے مقصود لوگوں کو اس بات کا قائل کرنا ہے کہ دنیا کی زندگی میں جن انسانوں کا یہ طرزِ عمل ہے ان کا محاسبہ آخر کیوں کہ ہو۔ پھر کلام کو اس بات پر ختم کیا گیا ہے کہ محاسبہ ہو گا اور ضرور ہو گا اور وہ اس روز ہو گا جب اللہ تعالیٰ کی عدالت قائم ہو گی۔ اس وقت جزا و سزا کا انکار کرنے والوں کی سمجھ وہ بات آجائے گی جسے آج وہ سمجھانے سے نہیں مان رہے ہیں، مگر اُس وقت سمجھنے کا کوئی فائدہ نہ ہوگا۔ منکر انسان ہاتھ مَلتا رہ جائے گا کہ کاش میں نے دنیا میں اِس دن کے لیے کوئی سامان کیا ہوتا۔ مگر یہ ندامت اُسے خدا کی سزا سے نہ بچا سکے گی۔ البتہ جن انسانوں نے دنیا میں پورے اطمینانِ قلب کے ساتھ اُس حق کو قبول کر لیا ہو گا جسے آسمانی صحیفے اور خدا کے انبیاء پیش کر رہے تھے، خدا اُن سے راضی ہوگا اور وہ خدا کے عطا کر دہ اجر سے راضی ہوں گے، انہیں دعوت دی جائے گی کہ وہ اپنے ربّ کے پسندیدہ بندوں میں شامل ہوں اور جنّت میں داخل ہو جائیں۔ ','سید ابو اعلیٰ مودودیؒ -  تفہیم القرآن');
INSERT INTO "chapters_ur" VALUES(90,'شہر',NULL,' نام : پہلی ہی آیت لَآ اُقْسِمُ بِھٰذَا الْبَلَدِ کے لفظ البلد کو اِس کا نام قرار دیا گیا ہے۔ زمانۂ نزول : اس کا مضمون اور اندازِ بیان مکۂ معظمہ کے ابتدائی دور کی سورتوں کا سا ہے، مگر ایک اشارہ اس میں ایسا موجود ہے جو پتہ دیتا ہے کہ اس کے نزول کا زمانہ وہ تھا جب کفارِ مکّہ رسول اللہ صلی اللہ علیہ وسلم کی دشمنی پر تُل گئے تھے اور آپ ؐ کے خلاف ہر ظلم و زیادتی کو انہوں نے اپنے لیے حلال کر لیا تھا۔ موضوع اور مضمون: اِس سورے میں ایک بہت بڑے مضمون کو چند مختصر جملوں میں سمیٹ دیا گیا ہے اور یہ قرآن کا کمالِ ایجاز ہے کہ ایک پورا نظریہ ٔ حیات ، جسےمشکل سے ایک ضحیم کتاب میں بیان کیا جا سکتا تھا، اِس چھوٹی سی سورۃ کے چھوٹے چھوٹے فقروں میں نہایت مؤثر طریقہ سے بیان کر دیا گیا ہے۔ اس کا موضوع دنیا میں انسان کی، اور انسان کے لیے دنیا کی صحیح حیثیت سمجھانا اور یہ بتانا ہے کہ خدا نے انسان کے لیے سعادت اور شقاوت کے دونوں راستے کھول کر رکھ دیے ہیں، اُن کو دیکھنے اور اُن پر چلنے کے وسائل بھی اُسے فراہم کر دیے ہیں، اور اب یہ انسان کی اپنی کوشش اور محنت پر موقوف ہے کہ وہ سعادت کی راہ چل کر اچھے انجام کو پہنچتا ہے یا شقاوت کی راہ اختیار کر کے بُرے انجام سے دوچار ہوتا ہے۔ سب سے پہلے شہر مکّہ اور اس میں رسول اللہ صلی اللہ علیہ وسلم پر گزرنے والے مصائب اور پوری اولادِ آدم ؑ کی حالت کو اِس حقیقت پر گواہ کی حیثیت سے پیش کیا گیا ہے کہ یہ دنیا انسان کے لیے آرام گاہ نہیں ہےجس میں وہ مزے اڑانے کے لیے پیدا کیا گیا ہو ، بلکہ یہاں اس کی پیدائش ہی مشقت کی حالت میں ہوئی ہے۔ اِس مضمون کو اگر سورۂ نجم کی آیت ۳۹ لَیْسَ لِلْاِنْسَانِ اِلَّا مَا سَعٰی کے ساتھ ملا کر دیکھا جائے تو بات بالکل واضح ہو جاتی ہے کہ اِس کارگاہ دنیا میں انسان کے مستقبل کا انحصار اس کی سعی و کوشش اور محنت و مشقت پر ہے۔ اِس کے بعد انسان کی ہ غلط فہمی دور کی گئی ہے کہ یہاں بس وہی وہ ہے اور اوپر کوئی بالاتر طاقت نہیں ہے جو اُس کے کام کی نگرانی کرنے والی اور اُس پر مواخذہ کرنے والی ہو۔ پھر انسان کے بہت جاہلانہ اخلاقی تصورات میں سے ایک چیز کو بطور مثال لے کر بتایا گیا ہے کہ دنیا میں اُس نے بڑائی اور فضیلت کے کیسے غلط معیار تجویز کر رکھے ہیں۔ جو شخص اپنی کبریائی کی نمائش کے لیے ڈھیروں مال لٹاتا ہے وہ خود بھی اپنی اِن شاہ خرچیوں پر فخر کرتا ہے اور لوگ بھی اسے خوب داد دیتے ہیں، حالانکہ جو ہستی اُس کے کام کی نگرانی کر رہی ہے وہ یہ دیکھتی ہے کہ اُس نے یہ مال کن طریقوں سے حاصل کیا اور کن راستوں میں کس نیت اور کن اغراض کے لیے خرچ کیا۔ اِس کے بعد اللہ تعالیٰ فرماتا ہے کہ ہم نے انسان کو علم کے ذرائع اور سوچنے سمجھنے کی صلاحیتیں دے کر اُس کے سامنے بھلائی اور بُرائی کے دونوں راستے کھول کر رکھ دیے ہیں۔ ایک راستہ وہ ہے جو اخلاق کی پستیوں کی طرف جاتا ہے اور اُس پر جانے کے لیے کوئی تکلیف نہیں اٹھانی پڑتی بلکہ نفس کو خوب لذّت حاصل ہوتی ہے۔ دوسرا راستہ اخلاق کی بلندیوں کی طرف جاتا ہے جو ایک دشوار گزار گھاٹی کی طرح ہے کہ اُس پر چلنے کے لیے آدمی کو اپنے نفس پر جبر کرنا پڑتا ہے۔ یہ انسان کی کمزوری ہے کہ وہ اِس گھاٹی پر چڑھنے کے بہ نسبت کھَڈ میں لُڑھکنے کو ترجیح دیتا ہے۔ پھر اللہ تعالیٰ نے بتایا ہے کہ وہ گھاٹی کیا ہے جس سے گزر کر آدمی بلندیوں کی طرف جا سکتا ہے۔ وہ یہ ہے کہ ریا اور فخر اور نمائش کے خرچ چھوڑ کر آدمی اپنا مال یتیموں اور مسکینوں کی مدد پر خرچ کرے، اللہ اور اس کے دین پر ایمان لائے، اور ایمان لانے والوں کے گروہ میں شامل ہو کر ایک ایسے معاشرے کی تشکیل میں حصہ لے جو صبر کے ساتھ حق پرستی کے تقاضوں کو پورا کرنے والا اور خلق پر رحم کھانے والا ہو۔ اِس راستے پر چلنے والوں کا انجام یہ ہے کہ آدمی اللہ کی رحمتوں کا مستحق ہو، اور اس کے برعکس دوسرا راستہ اختیار کرنے والوں کا انجام دوزخ کی آگ ہے جس سے نکلنے کے سارے دروازے بند ہیں۔ ','سید ابو اعلیٰ مودودیؒ -  تفہیم القرآن');
INSERT INTO "chapters_ur" VALUES(91,'آفتاب',NULL,' نام : پہلے ہی لفظ الشمس کو اس کا نام قرار دیا گیا ہے۔ زمانۂ نزول : مضمون اور اندازِ بیان سے معلوم ہوتا ہے کہ یہ سورۃ بھی مکہ ٔ معظمہ کے ابتدائی دور میں نازل ہوئی ہے ۔ مگر اس کا نزول اُس زمانے میں ہوا ہے جب مکّہ میں رسول اللہ صلی اللہ علیہ وسلم کی مخالفت خوب زور پکڑ چکی تھی۔ موضوع اور مضمون: اِس کا موضوع نیکی اور بدی کا فرق سمجھانا اور اُن لوگوں کو بُرے انجام سے ڈرانا ہے جو اس فرق کو سمجھنے سے انکار اور بدی کی راہ چلنے پر اصرار کرتے ہیں۔ مضمون کےلحاظ سے یہ سورۃ دو حصّوں پر مشتمل ہے۔ پہلا حصّہ سورۃ کے آغاز سے شروع ہو کر آیت ۱۰ پر ختم ہوتا ہے ، اور دوسرا حصّہ آیت ۱۱ سے آخر تک چلتا ہے۔ پہلے حصّہ میں تین باتیں سمجھائی گئی ہیں۔ ایک یہ کہ جس طرح سورج اور چاند، دن اور رات، زمین اور آسمان ایک دوسرے سے مختلف اور اپنے آثار و نتائج میں متضاد ہیں، اسی طرح نیکی اور بدی بھی ایک دوسرے سے مختلف اور اپنے آثار و نتائج میں متضاد ہیں۔ یہ دونوں نہ اپنی شکل میں یکساں ہیں اور نہ ان کے نتائج یکساں ہو سکتے ہیں۔ دوسرے یہ کہ اللہ تعالیٰ نے نفسِ انسانی کو جسم ، حواس اور ذہن کی قوتیں دے کر دنیا میں بالکل بے خبر نہیں چھوڑ دیا ہے بلکہ ایک فطری الہام کے ذریعہ سے اُس کے لاشعور میں نیکی اور بدی کا فرق، بھلے اور بُرے کا امتیاز، اور خیر کے خیر اور شر کے شر ہونے کا احساس اُتار دیا ہے۔ تیسرے یہ کہ انسان کے مستقبل کا انحصار اِس پر ہے کہ اس کے اندر تمیز، ارادے اور فیصلے کی جو قوتیں اللہ نے رکھ دی ہیں ان کو استعمال کر کے وہ اپنے نفس کے اچھے اور بُرے رُحجانات میں سے کس کو ابھارتا اور کس کو دباتا ہے۔ اگر وہ اچھے رُحجانات کو ابھارے اور بُرے رُحجانات سے اپنے نفس کو پاک کرے تو فلاح پائے گا۔ اور اس کے برعکس اگر وہ نفس کی اچھائی کو دبائے اور بُرائی کو اُبھارے تو نامراد ہو گا۔ دوسرے حصّے میں قومِ ثمود کی تاریخی نظیر کو پیش کرتے ہوئے رسالت کی اہمیت سمجھائی گئی ہے ۔ رسول دنیا میں اس لیے بھیجا جاتا ہے کہ بھلائی اور بُرائی کا جو الہامی علم اللہ نے انسان کی فطرت میں رکھ دیا ہے وہ بجائے خود انسان کی ہدایت کے لیے کافی نہیں ہے ، بلکہ اس کو پوری طرح نہ سمجھنے کی وجہ سے ہی انسان خیر و شر کے غلط فلسفے اور معیار تجویز کر کر کے گمراہ ہوتا رہا ہے۔ اس بنا پر اللہ تعالیٰ نے اُس فطری الہام کی مدد کے لیے انبیاء علیہم السلام پر واضح اور صاف صاف وحی نازل فرمائی تاکہ وہ لوگوں کو کھول کر بتائیں کہ نیکی کیا ہے اور بدی کیا ہے۔ ایسے ہی ایک نبی حضرت صالح علیہ السلام قومِ ثمود کی طرف بھیجے گئے تھے۔ مگر وہ اپنے نفس کی بُرائی میں غرق ہو کر اتنی سرکش ہو گئی تھی کہ اُس نے اُن کو جھُٹلا دیا، اور اُس کا منہ مانگا معجزہ جب اُنہوں نے ایک اونٹنی کی شکل میں پیش کیا تو اُن کی تنبیہ کے باوجود اُس قوم کے ایک شریر ترین آدمی نے ساری قوم کی خواہش اور طلب کے مطابق اسے بھی قتل کر دیا۔ اِس کا نتیجہ آخر کار یہ ہوا کہ پوری قوم تباہ کر کے رکھ دی گئی۔ ثمود کا یہ قصہ پیش کرتے ہوئے پوری سورہ میں کہیں یہ نہیں کہا گیا ہے کہ اے قومِ قریش، اگر تم ثمود کی طرح اپنے نبی محمد صلی اللہ علیہ وسلم کو جھٹلاؤ گے تو وہی انجام دیکھو گے جو ثمود نے دیکھا ہے۔ مکّہ میں اُس وقت حالات وہی موجود تھے جو صالح علیہ السلام کے مقابلہ میں قومِ ثمود کے اشرار نے پیدا کر رکھے تھے۔ اس لیے اُن حالات میں یہ قصہ سنا دینا بجائے خود اہلِ مکّہ کو یہ سمجھا دینے کے لیے کافی تھا کہ ثمود کی یہ تاریخی نظیر اُن پر کس طرح چسپاں ہو رہی ہے۔ ','سید ابو اعلیٰ مودودیؒ -  تفہیم القرآن');
INSERT INTO "chapters_ur" VALUES(92,'رات',NULL,' نام : پہلے ہی لفظ وَالَّیْل کو اس سُورہ کا نام قرار دیا گیا ہے۔ زمانۂ نزول : اس کا مضمون سورۂ شمس سے اِس قدر مشابہ ہے کہ یہ دونوں سورتیں ایک دوسرے کی تفسیر محسوس ہوتی ہیں ۔ ایک ہی بات ہے جسے سورۂ شمس میں ایک طریقہ سے سمجھایا گیا ہے اور اِس سورہ میں دوسرے طریقے سے ۔ اِس سے اندازہ ہوتاہے کہ یہ دونوں قریب قریب ایک ہی زمانے میں نازل ہو ئی ہیں۔ موضوع اور مضمون: اِس کا موضوع زندگی کے دو مختلف راستوں کا فرق اور ان کے انجام اور نتائج کا اختلاف بیان کرنا ہے ۔ مضمون کے لحاظ سے یہ سورۃ دو حصّوں پر مشتمل ہے۔ پہلا حصّہ آغاز سے آیت ۱۱ تک ہے ، اور دوسرا حصّہ آیت ۱۲ سے آخر تک۔ پہلے حصّہ میں سب سے پہلے یہ بتایا گیا ہے کہ نوع انسانی کے افراد ، اقوام اور گروہ دنیا میں جو سعی و عمل بھی کر رہے ہیں ، وہ لازمًا اپنی اخلاقی نوعیت کے لحاظ سے اُسی طرح مختلف ہیں جس طرح دن رات سے اور نر مادہ سے مختلف ہے۔ اِس کے بعد قرآن کی مختصر سورتوں کے عام انداز بیان کے مطابق تین اخلاقی خصوصیات ایک نوعیت کی، اور تین اخلاقی خصوصیات دوسری نوعیت کی سعی و عمل کے ایک وسیع مجموعے میں سے لے کر بطور نمونہ پیش کی گئی ہیں جنہیں سن کر ہر شخص بڑی آسانی کے ساتھ یہ اندازہ لگا سکتا ہے کہ ایک قسم کی خصوصیات کس طرزِ زندگی کی نمائندگی کرتی ہیں اور دوسری قسم کی خصوصیات اُس کے بر عکس کس دوسرے طرزِ زندگی کی علامات ہیں۔ یہ دونوں نمونے ایسے چھوٹے چھوٹے خوبصورت جچے تُلے فقروں میں بیان کیے گئے ہیں کہ سنتے ہی آدمی کے دل میں اتر جائیں اور زبان پر چڑھ جائیں۔پہلی قسم کی خصوصیات یہ ہیں کہ آدمی مال دے ، خدا ترسی و پرہیز گاری اختیار کرے اور بھلائی کو بھلائی مانے۔ دوسری قسم کی خصوصیات یہ ہیں کہ وہ بخل کرے، خدا کی رضا اور ناراضی کی فکر سے بے پروا ہو جائے اور بھلی بات کو جھُٹلا دے۔ پھر بتایا گیا کہ یہ دو طرزِ عمل جو صریحًا ایک دوسرے سے مختلف ہیں، اپنے نتائج کے اعتبار سے ہر گز یکساں نہیں ہیں، بلکہ جس قدر یہ اپنی نوعیت میں متضاد ہیں اسی قدر اِن کے نتائج بھی متضاد ہیں۔ پہلے طرزِ عمل کو جو شخص یا گروہ اختیار کرے گا اللہ تعالیٰ اس کے لیے زندگی کے صاف اور سیدھے راستے کو سہل کر دے گا یہاں تک کہ اس کے لیے نیکی کرنا آسان اور بدی کرنا مشکل ہو جائے گا ۔ اور دوسرے طرزِ عمل کو جو بھی اختیار کرے گا اللہ تعالیٰ اس کے لیے زندگی کے بِکَٹ اور سخت راستے کو سہل کر دے گا یہاں تک کہ اس کے لیے بد آسان اور نیکی مشکل ہو جائے گی۔ اِس بیان کو ایک نہایت مؤثر اور تیر کی طرح دل میں پیوست ہو جانے والے جملے پر ختم کیا گیا ہے کہ دنیا کا یہ مال جس کے پیچھے آدمی جان دیے دیتا ہے ، آخر قبر میں تو اُس کے ساتھ جانے والا نہیں ہے ، مرنے کے بعد یہ اُس کے کس کام آئے گا؟ دوسرے حصّے میں بھی اِسی اختصار کے ساتھ تین حقیقتیں بیان کی گئی ہیں ۔ ایک یہ کہ اللہ نے دنیا کی اِس امتحان گاہ میں انسان کو بے خبر نہیں چھوڑا ہے بلکہ اُس نے یہ بتا دینا اپنے ذمّہ لیا ہے کہ زندگی کے مختلف راستوں میں سے سیدھا راستہ کو ن سا ہے ۔ اس کے ساتھ یہ کہنے کی ضرورت نہ تھی کہ اپنا رسول اور اپنی کتاب بھیج کر اُس نے اپنی یہ ذمہ داری ادا کردی ہے ، کیونکہ رسول ؐ اور قرآن، دونوں ہدایت دینے کے لیے سب کے سامنے موجود تھے۔ دوسری حقیقت یہ بیان کی گئی ہے کہ دنیا اور آخرت دونوں کا مالک اللہ ہی ہے۔ دنیا مانگو گے تو بھی اسی سے ملے گی اور آخرت مانگو گے تو اس کا دینے والا بھی وہی ہے۔ یہ فیصلہ کرنا تمہارا اپنا کام ہے کہ تم اُس سے کیا مانگتے ہو۔ تیسری حقیقت یہ بیان کی گئی ہے کہ جو بدبخت اُس بھلائی کو جُھٹلائے گا جسے رسول اور کتاب کے ذریعہ سے پیش کیا جا رہا ہے ، اور اُس سے منہ پھیرے گا اُس کے لیے بھڑکتی ہوئی آگ تیار ہے۔ اور جو خدا ترس آدمی پوری بے غرضی کے ساتھ محض اپنے ربّ کی رضا جوئی کی خاطر اپنا مال راہِ خیر میں صرف کرے گا اُس کا ربّ اُس سے راضی ہو گا اور اسے اتنا کچھ دے گا کہ وہ خوش ہو جائے گا۔','سید ابو اعلیٰ مودودیؒ -  تفہیم القرآن');
INSERT INTO "chapters_ur" VALUES(93,'دن کا چڑھنا',NULL,' نام : پہلے ہی لفظ وَالضُّحٰی کو اس سورہ کا نام قرار دیا گیا ہے۔ زمانۂ نزول : اس کا مضمون صاف بتا رہا ہے کہ یہ مکّۂ معظمہ کے بالکل ابتدائی دور میں نازل ہوئی ہے۔ روایات سے بھی معلوم ہوتا ہے کہ کچھ مدت تک وحی کے نزول کا سلسلہ بند رہا تھا جس سے حضورؐ سخت پریشان ہو گئے تھے اور بار بار آپؐ کو یہ اندیشہ لاقح ہو رہا تھا کہ کہیں مجھ سے کوئی ایسا قصور تو نہیں ہو گیا جس کی وجہ سے میرا ربّ مجھ سے ناراض ہو گیا ہے اور اس نے مجھے چھوڑ دیا ہے۔ اس پر آپ ؐ کو اطمینان دلایا گیا کہ وحی کے نزول کا سلسلہ کسی ناراضی کی بنا پر نہیں روکا گیا تھا، بلکہ اس میں وہی مصلحت کار فرما تھی جو روزِ روشن کے بعد رات کا سکون طاری کرنے میں کار فرما ہوتی ہے۔ یعنی وحی کی تیز روشنی اگر آپؐ پر برابر پڑتی رہتی تو آپؐ کے اعصاب اسے برداشت نہ کر سکتے ، اس لیے بیچ میں وقفہ دیا گیا تا کہ آپ ؐ کو سکون مل جائے ۔ یہ کیفیت حضورؐ پر نبوت کے ابتدائی دور میں گزرتی تھی جبکہ ابھی آپؐ کو وحی کے نزول کی شدّت برداشت کرنے کی عادت نہیں پڑی تھی، اِس بنا پر بیچ بیچ میں وقفہ دینا ضروری ہوتا تھا۔ اس کی وضاحت ہم سورۂ مدَّثِّر کے دیباچے میں کر چکے ہیں۔ اور سورۂ مُزَّمِّل حاشیہ ۵ میں ہم یہ بھی بیان کر چکے ہیں کہ نزولِ وحی کا کس قدر شدید بار آپ ؐ کےاعصاب پر پڑتا تھا۔ بعد میں جب حضور ؐ کے اندر اس بار کو برداشت کرنے کا تحمُّل پیدا ہو گیا تو طویل وقفے دینے کی ضرورت باقی نہیں رہی۔ موضوع اور مضمون: اِس کا موضوع رسول اللہ صلی اللہ علیہ وسلم کو تسلّی دینا ہے اور مقصد اُس پریشانی کو دور کرنا ہے جو نزولِ وحی کا سلسلہ رک جانے سے آپ ؐ کو لاحق ہوگئی تھی۔ سب سے پہلے روزِ روشن اور سکونِ شب کی قسم کھا کر آپؐ کو اطمینان دلایا گیا ہے کہ آپ ؐ کے ربّ نے آپ کو ہر گز نہیں چھوڑا ہے اور نہ وہ آپؐ سے ناراض ہوا ہے۔ اس کے بعد آپؐ کو خوشخبری دی گئی ہے کہ دعوتِ اسلامی کے ابتدائی دور میں جن شدید مشکلات سے آپ ؐ کو سابقہ پیش آرہا ہے یہ تھوڑے دنوں کی بات ہے ۔ آپؐ کے لیے ہر بعد کا دور پہلے دور سے بہتر ہو تا چلا جائے گا اور کچھ زیادہ دیر نہ گزرے گی کہ اللہ تعالیٰ آپؐ پر اپنی عطا و بخشش کی ایسی بارش کرے گا جس سے آپ ؐ خوش ہو جائیں گے۔ یہ قرآن کی اُن صریح پیشنگوئیوں میں سے ایک ہے جو بعد میں حرف بحرف پوری ہوئیں ، حالانکہ جس وقت یہ پیشنگوئی کی گئی تھی اُس وقت کہیں دُور دُور بھی اِس کے آثار نظر نہ آتے تھے کہ مکّہ میں جو بے یار و مددگار انسان پوری قوم کی جاہلیّت کے مقابلے میں برسر پیکار ہو گیا ہے اُسے اِتنی حیرت انگیز کامیابی نصیب ہوگی۔ اس کے بعد اللہ تعالیٰ نے اپنے حبیب صلی اللہ علیہ وسلم سے فرمایا ہے کہ تمہیں یہ پریشانی کیسے لاحق ہو گئی کہ ہم نے تمہیں چھوڑ دیا ہے اور ہم تم سے ناراض ہو گئے ہیں۔ ہم تو تمہارے روزِ پیدائش سے مسلسل تم پر مہربانیاں کرتے چلے آرہے ہیں ۔ تم یتیم پیدا ہوئے تھے ، ہم نے تمہاری پرورش اور خبر گیری کا بہترین انتظام کر دیا۔ تم ناواقف ِ راہ تھے ، ہم نے تمہیں راستہ بتایا ۔ تم نادار تھے، ہم نے تمہیں مالدار بنایا۔ یہ ساری باتیں صاف بتا رہی ہیں کہ تم ابتداء سے ہمارے منظورِ نظر ہو اور ہمارا فضل و کرم مستقل طور پر تمہارے شاملِ حال ہے۔ اس مقام پر سورۂ طٰہٰ ، آیات ۳۷ تا ۴۲ کو بھی نگاہ میں رکھا جائے جہاں حضرت موسیٰ ؑ کو فرعون جیسے جبّار کے مقابلہ میں بھیجتے وقت اللہ تعالیٰ نے اُن کی پریشانی دور کرنے کے لیے انہیں بتایا ہے کہ کس طرح تمہاری پیدائش کے وقت سے ہماری مہربانیاں تمہارے شامل حال رہی ہیں ، اس لیے تم اطمینان رکھو کہ اِس خوفناک مہم میں تم اکیلے نہ ہو گے بلکہ ہمارا فضل تمہارے ساتھ ہوگا۔ آخر میں اللہ تعالیٰ نے نبی صلی اللہ علیہ وسلم کو بتایا ہے کہ جو احسانات ہم نے تم پر کیے ہیں ان کے جواب میں خلق خدا کے ساتھ تمہارا کیا برتاؤ ہونا چاہیے ، اور ہماری نعمتوں کا شکر تمہیں کس طرح ادا کرنا چاہیے۔','سید ابو اعلیٰ مودودیؒ -  تفہیم القرآن');
INSERT INTO "chapters_ur" VALUES(94,'تسکین',NULL,' نام : پہلے ہی فقرے کو اس سورہ کا نام قرار دیا گیا ہے۔ زمانۂ نزول : اس کا مضمون سورۂ ضحٰی سے اس قدر ملتا جلتا ہے کہ یہ دونوں سُورتیں قریب قریب ایک ہی زمانے اور ایک جیسے حالات میں نازل شدہ معلوم ہوتی ہیں۔ حضرت عبد اللہ بن عباس ؓ فرماتے ہیں کہ یہ مکّۂ معظمہ میں والضحٰی کے بعد نازل ہوئی ہے۔ موضوع اور مضمون: اس کا مقصد و مدعا بھی رسول اللہ صلی اللہ علیہ وسلم کو تسلّی دینا ہے۔ نبوّت سے پہلے حضور ؐ کو کبھی اُن حالات سے سابقہ پیش نہ آیا تھا جن کا سامنا نبوّت کے بعد دعوتِ اسلامی کا آغاز کرتے ہی یکایک آپؐ کو کرنا پڑا۔ یہ خود آپؐ کی زندگی میں ایک انقلابِ عظیم تھا جس کا کوئی اندازہ آپؐ کو قبلِ نبوت کی زندگی میں نہ تھا۔ اسلام کی تبلیغ آپؐ نے کیا شروع کی کہ دیکھتےدیکھتے وہی معاشرہ آپ ؐ کا دشمن ہو گیا جس میں آپ ؐ پہلے بڑی عزت کی نگاہ سے دیکھے جاتے تھے ۔ وہی رشتہ دار ، دوست ، اہلِ قبیلہ اور اہلِ محلہ آپؐ کو گالیاں دینے لگے جو پہلے آپ ؐ کو ہاتھو ہاتھ لیتے تھے۔ مکّہ میں کوئی آپؐ کی بات سننے کا روادار نہ تھا ۔ راہ چلتے آپ پر آوازے کسے جانے لگے۔ قدم قدم پر آپ ؐ کے سامنے مشکلات ہی مشکلات تھیں۔ اگرچہ رفتہ رفتہ آپؐ کو اِن حالات ، بلکہ اِن سے بھی بدرجہا زیادہ سخت حالات کا مقابلہ کرنے کی عادت پڑ گئی، لیکن ابتدائی زمانہ آپ ؐ کے لیے نہایت دل شکن تھا۔ اِسی بنا پر آپ ؐ کو تسلی دینے کے لیے پہلے سُورۂ ضحٰی نازل کی گئی اور پھر اِس سورت کا نزول ہوا۔ اِس میں اللہ تعالیٰ نے سب سے پہلے آپ ؐ کو بتایا ہے کہ ہم نے آپ ؐ کو تین بہت بڑی نعمتیں عطا کی ہیں جن کی موجودگی میں کوئی وجہ نہیں کہ آپ ؐ دل شکستہ ہوں۔ ایک شرح صدر کی نعمت۔ دوسری نعمت کہ آپ کے اوپر سے ہم نے وہ بھاری بوجھ اتار دیا جو نبوّت سے پہلے آپ ؐ کی کمر توڑے ڈال رہا تھا۔ تیسری رفعِ ذِکر کی نعمت جو آپ ؐ سے بڑھ کر تو درکنار آپ ؐ کے برابر بھی کبھی کسی بندے کو نہیں دی گئی۔ آگے چل کر ہم نے اپنے حواشی میں وضاحت کر دی ہے کہ اِن تینوں نعمتوں سے مراد کیا ہے اور یہ کتنی بڑی نعمتیں ہیں۔ اِس کے بعد ربِّ کائنات اپنے بندے اور رسول صلی اللہ علیہ وسلم کو یہ اطمینان دلاتا ہے کہ مشکلات کا یہ دور ، جس سے آپ ؐ کو سابقہ پیش آ رہا ہے، کوئی بہت لمبا دور نہیں ہے بلکہ اِس تنگی کے ساتھ ہی ساتھ فراخی کا دور بھی لگا چلا آرہا ہے۔ یہ وہی بات ہے جو سورۂ ضحٰی میں اِس طرح فرمائی گئی تھی کہ آپ ؐ کے لیے ہر بعد کا دور پہلے دور سے بہتر ہو گا اور عنقریب آپ ؐ کا ربّ آپ ؐ کو وہ کچھ دے گا جس سے آپ ؐ کا دل خوش ہو جائے گا۔ آخر میں حضور ؐ کو ہدایت فرمائی گئی ہے کہ ابتدائی دور کی اِن سختیوں کا مقابلہ کرنے کی طاقت آپ کے اندر ایک ہی چیزسے پیدا ہوگی، اور وہ یہ ہے کہ جب اپنے مشاغل سے آپ فارغ ہوں تو عبادت کی مشقت و ریاضت میں لگ جائیں اور ہر چیز سے بے نیاز ہو کر صرف اپنے ربّ سے لَو لگائیں۔ یہ وہی ہدایت ہے جو زیادہ تفصیل کے ساتھ حضور ؐ کو سورۂ مزّمِّل آیات ۱ تا ۹ میں دی گئی ہے۔','سید ابو اعلیٰ مودودیؒ -  تفہیم القرآن');
INSERT INTO "chapters_ur" VALUES(95,'انجیر',NULL,' نام : پہلے ہی لفظ اَلتِّیْن کو اس سورہ کا نام قرار دیا گیا ہے۔ زمانۂ نزول : قَتَادَہ کہتے ہیں کہ یہ سورۃ مدنی ہے۔ ابن عباس ؓ سے دو قول منقول ہیں ۔ ایک یہ کہ یہ مکّی ہے اور دوسرا یہ کہ مدنی ہے۔ لیکن جمہور علماء اسے مکّی ہی قرار دیتے ہیں اور اس کے مکّی ہونے کی کھلی ہوئی علامت یہ ہے کہ اِس میں شہرِ مکّہ کے لیے ھٰذَا الْبَلَدِ الْاَمِیْن (یہ پرامن شہر) کے الفاظ استعمال کیے گئے ہیں ۔ ظاہر ہے کہ اگر اس کا نزول مدینہ یں ہوا ہوتا تو مکّہ کے لیے”یہ شہر“ کہنا صحیح نہیں ہو سکتا تھا۔ علاوہ بریں سورت کے مضمون پر غور کرنے سے محسوس ہوتا ہے کہ یہ مکّۂ معظمہ کے بھی ابتدائی دور کی نازل شدہ سورتوں میں سے ہے، کیونکہ اِس میں کوئی نشان اِس امر کا نہیں پایا جاتا کہ اس کے نزول کے وقت کفر و اسلام کی کشمکش برپا ہو چکی تھی، اور اِس کے اندر مکّی دور کی ابتدائی سورتوں کا وہی اندازِ بیان پایا جاتا ہے جس میں نہایت مختصر اور دل نشین طریقہ سے لوگوں کو سمجھایا گیا ہے کہ آخرت کی جزا و سزا ضروری اور سراسر معقول ہے۔ موضوع اور مضمون: اس کا موضوع ہے جزا و سزا کا اِثبات۔ اِس غرض کے لیے سب سے پہلے جلیل القدر انبیاء کے مقاماتِ ظہور کی قَسم کھا کر فرمایا گیا ہے کہ اللہ تعالیٰ نے انسان کو بہترین ساخت سے پیدا کیا ہے ۔ اگرچہ اِس حقیقت کو دوسرے مقامات پر قرآن مجید میں مختلف طریقوں سے بیان کیا گیا ہے۔ مثلاً کہیں فرمایا کہ انسان کو خدا نے زمین میں اپنا خلیفہ بنایا اور فرشتوں کو اس کے آگے سجدہ کرنے کا حکم دیا (البقرہ، ۳۴-۳۰۔ الاَنعام، ۱۶۵۔ الاَعراف،۱۱۔ الحِجْر، ۲۹-۲۸۔ النَّمل، ۶۲۔ صٓ۷۱ تا ۷۳)۔ کہیں فرمایا کہ انسان اُس اَمانتِ الہٰی کا حامل ہوا ہے جسے اٹھانے کی طاقت زمین و آسمان اور پہاڑوں میں بھی نہ تھی (الاَحزاب، ۷۲)۔ کہیں فرمایا کہ ہم نے بنی آدم کو عزت بخشی اور اپنی بہت سے مخلوقات پر فضیلت عطا کی(بنی اسرائیل،۷۰)۔ لیکن یہاں خاص طور پر انبیاء کے مقامات ظہور کی قسم کھا کر یہ فرمانا کہ انسان کو بہترین ساخت پر پیدا کیا گیا ہے ، یہ معنی رکھتا ہے کہ نوعِ انسانی کو اِتنی بہتر ساخت عطا کی گئی کہ اس کے اندر نبوّت جیسے بلند ترین منصب کے حامل لوگ پیدا ہوئے جس سے اونچا منصب خدا کی کسی دوسری مخلوق کو نصیب نہیں ہوا۔ اس کے بعد یہ بتایا گیا ہے کہ انسانوں میں دو قِسمیں پائی جاتی ہیں، ایک وہ جو اس بہترین ساخت پر پیدا ہونے کے بعد بُرائی کی طرف مائل ہوتے ہیں اور اخلاقی پستی میں گرتے گرتے اُس انتہا کو پہنچ جاتے ہیں جہاں اُن سے زیادہ نیچ کوئی دوسری مخلوق نہیں ہوتی، دوسرے وہ جو ایمان و عملِ صالح کا راستہ اختیار کر کے اس گراوٹ سے بچ جاتے ہیں اور اُس مقامِ بلند پر قائم رہتے ہیں جو اُن کے بہترین ساخت پر پیدا ہونے کا لازمی تقاضا ہے۔ نوعِ انسانی میں اِن دونوں قسموں کے لوگوں کا پایا جانا ایک ایسا امرِ واقعی ہے جس سے انکار نہیں کیا جاسکتا، کیونکہ اس کا مشاہدہ انسانی معاشرے میں ہر جگہ ہر وقت ہو رہا ہے۔ آخر اِس امرِ واقعی سے یہ استدلال کیا گیا ہے کہ جب انسانوں میں یہ دو الگ الگ اور ایک دوسرے سے قطعی مختلف قسمیں پائی جاتی ہیں تو پھر جزائے اعمال کا کیسے انکار کیا جا سکتا ہے۔ اگر پستی میں گرنے والوں کو کوئی سزا اور بلندی پر چڑھنے والوں کو کوئی اجر نہ ملے ، اور انجام ِ کار دونوں کا یکساں ہو ، تو اِس کے معنی یہ ہیں کہ خدا کی خدائی میں کوئی انصاف نہیں ہے۔ حالانکہ انسانی فطرت اور انسان کی عقلِ عام یہ تقاضا کرتی ہے کہ جو شخص بھی حاکم ہو وہ انصاف کرے۔ پھر یہ کیسے تصور کیا جا سکتا ہے کہ اللہ ، جو سب حاکموں سے بڑا حاکم ہے وہ انصاف نہیں کرے گا۔ ','سید ابو اعلیٰ مودودیؒ -  تفہیم القرآن');
INSERT INTO "chapters_ur" VALUES(96,'جما ہوا خون',NULL,' نام : دوسری آیت کے لفظ عَلَقَ کو اس سورۃ کا نام قرار دیا گیا ہے۔ زمانۂ نزول : اس سورۃ کے دو حصّے ہیں ۔ پہلا حصّہ اِقْراْ سے شروع ہو کر پانچویں آیت کے الفاظ مَا لَمْ یَعْلَمْ پر ختم ہوتا ہے اور دوسرا حصّہ کَلَّآ اِنَّ الْاِنْسَانَ لَیَطْغٰی سے شروع ہو کر آخر سُورۃ تک چلتا ہے۔ پہلے حصّے کے متعلق علمائے امّت کی عظیم اکثریت اس بات پر متفق ہے کہ یہ سب سے پہلے وحی ہے جو رسول اللہ صلی اللہ علیہ وسلم پر نازل ہوئی۔ اِس معاملہ میں حضرت عائشہؓ نے خود رسول اللہ صلی اللہ علیہ وسلم سے سُن کر آغازِ وحی کا پورا قصہ بیان کیا ہے۔ اِس کے علاوہ ابن عباسؓ ، ابو موسیٰ اشعریؓ اور صحابہ کی ایک جماعت سے یہی بات منقول ہے کہ قرآن کی سب سے پہلی آیات جو حضور ؐ پر نازل ہوئیں وہ یہی تھیں۔ دوسرا حصّہ بعد میں اُس وقت نازل ہوا جب رسول اللہ صلی اللہ علیہ وسلم نے حَرَم میں نماز پڑھنی شروع کی اور ابو جہل نے آپ کو دھمکیاں دے کر اس سے روکنے کی کوشش کی۔ آغازِ وحی: محدّثین نے آغازِ وحی کا قصّہ اپنی اپنی سندوں کے ساتھ امام زُہْری سے ، اور انہوں نے حضرت عُروَہ بن زُبَیر سے اور انہوں نے اپنی خالہ حضرت عائشہؓ سے نقل کیا ہے۔وہ فرماتی ہیں کہ رسول اللہ صلی اللہ علیہ وسلم پر وحی کی ابتدا سچّے(اور بعض روایات میں ہے اچھے) خوابوں کی شکل میں ہوئی۔ آپ جو خواب بھی دیکھتے وہ ایسا ہوتا کہ جیسے آپ دن کی روشنی میں دیکھ رہے ہیں۔ پھر آپ تنہائی پسند ہو گئے اور کئی کئی شب و روز غارِ حرا میں رہ کر عبادت کرنے لگے (حضرت عائشہؓ نے تَحَنُّث کا لفظ استعمال کیا ہے جس کی تشریح امام زُہْرِی نے تعبُّد سے کی ہے۔ یہ کسی طرح کی عبادت تھی جو آپ کرتے تھے، کیونکہ اُس وقت تک اللہ تعالیٰ کی طرف سے آپ کو عبادت کا طریقہ نہیں بتایا گیا تھا)۔ آپ کھانے پینے کا سامان گھر سے لے جا کر وہاں چند روز گزارتے، پھر حضرت خَدِیجہؓ کے پاس واپس آتے اور وہ مزید چند روز کے لیے سامان آپ کے لیے مہیا کر دیتی تھیں۔ ایک روز جبکہ آپ غارِ حرا میں تھے ، یکایک آپ پر وحی نازل ہوئی اور فرشتے نے آکر آپ سے کہا پڑھو۔ اس کے بعد حضرت عائشہؓ خود رسول اللہ صلی اللہ علیہ وسلم کا قول نقل کرتی ہیں کہ میں نے کہا” میں تو پڑھا ہو انہیں ہوں۔“ اس پر فرشتے نے مجھے پکر کر بھینچا یہاں تک کہ میری قوت ِ برداشت جواب دیے لگی۔ پھر اس نے مجھے چھوڑ دیا اور کہا پڑھو۔ میں نے کہا”میں تو پڑھا ہوا نہیں ہوں۔“ اس نے دوبارہ مجھے بھینچا اور میری قوت ِ برداشت جواب دینے لگی۔ پھر اس نے مجھے چھوڑ دیا اور کہا پڑھو۔ میں نے پھر کہا”میں توپڑھا ہوا نہیں ہوں۔“ اس نے تیسری بار مجھے بھینچا یہاں تک کہ میری قوتِ برداشت جواب دینے لگی۔ پھر اُس نے مجھے چھوڑ دیا اور کہا اِقْرَاْ بِا سْمِ رَبِّکَ الَّذِیْ خَلَقَ۔ (پڑھو اپنے ربّ کے نام سے جس نے پیدا کیا) یہاں تک کہ مَا لَمْ یَعْلَمْ ( جسے وہ نہ جانتا تھا) تک پہنچ گیا۔ حضرت عائشہ ؓ فرماتی ہیں کہ اس کے بعد رسول اللہ صلی اللہ علیہ وسلم کا نپتے لرزتے ہوئے وہاں سے پلٹے اور حضرت خَدِیجہؓ کے پاس پہنچ کر کہا” مُجھے اُڑھاوٴ، مجھے اُڑھاوٴ “۔ چنانچہ آپ کو اڑھا دیا گیا۔ جب آپ پر سے خوفزدگی کی کیفیت دور ہو گئی تو آپ نے فرمایا”اے خدیجہؓ ، یہ مجھے کیا ہو گیا ہے۔“ پھر سارا قصّہ آپ نے اُن کو سنایا اور کہا”مجھے اپنی جان کا ڈر ہے۔“ انہوں نے کہا” ہر گز نہیں، آپ خوش ہو جائیے، خدا کی قسم، آپ کو خدا کبھی رسوا نہ کرے گا۔ آپ رشتہ داروں سے نیک سلوک کرتے ہیں، سچ بولتے ہیں ، (ایک روایت میں یہ اضافہ ہے کہ امانتیں ادا کرتے ہیں)، بے سہارا لوگوں کا بار برداشت کرتے ہیں، نادار لوگوں کو کما کر دیتے ہیں، مہمان نوازی کرتے ہیں، اور نیک کاموں میں مدد کرتے ہیں۔“ پھر وہ حضورؐ کو ساتھ لے کر وَرَقَہ بن نَوفَل کے پاس گئیں جو اُن کے چچا زاد بھائی تھے، زمانہء جاہلیت میں عیسائی ہو گئے تھے، عربی اور عبرانی میں انجیل لکھتے تھے، بہت بوڑھے اور نابینا ہو گئے تھے۔ حضرت خدیجہؓ نے اُن سے کہا بھائی جان ، ذرا اپنے بھتیجے کا قصّہ سُنیے۔ وَرَقَہ نے حضورؐ سے کہا بھتیجے تم کو کیا نظر آیا؟ رسول اللہ صلی اللہ علیہ وسلم نے جو کچھ دیکھا تھا وہ بیان کیا۔ وَرَقَہ نے کہا”یہ وہی ناموس (وحی لانے والا فرشتہ) ہے جو اللہ نے موسیٰؑ پر نازل کیا تھا۔ کاش میں آپ کے زمانہء نبوت میں قوی جوان ہوتا۔ کاش میں اُس وقت زندہ رہوں جب آپ کی قوم آپ کو نکالے گی۔“ رسول اللہ صلی اللہ علیہ وسلم نے فرمایا”کیا یہ لوگ مجھے نکال دیں گے؟“ وَرَقَہ نے کہا”ہاں، کبھی ایسا نہیں ہوا کہ کوئی شخص وہ چیز لے کر آیا ہو جو آپ لائے ہیں اور اُس سے دشمنی نہ کی گئی ہو۔ اگر میں نے آپ کا وہ زمانہ پایا تو میں آپ کی پُر زور مدد کروں گا۔“ مگر زیادہ مدت نہ گزری تھی کہ ورقہ کا انتقال ہو گیا۔ یہ قصّہ خود اپنے منہ سے بول رہا ہے کہ فرشتے کی آمد سے ایک لمحہ پہلے تک بھی رسول اللہ صلی اللہ علیہ وسلم اس بات سے خالی الذہن تھے کہ آپ نبی بنائے جانے والے ہیں۔ اِس چیز کا طالب یا مُتَوَقِّع ہونا تو درکنار، آپ کے وہم و گمان میں بھی یہ نہ تھا کہ ایسا کوئی معاملہ آپ کے ساتھ پیش آئے گا۔ وحی کا نزول اور فرشتے کا اِس طرح سامنے آنا آپ کے لیے اچانک ایک حادثہ تھا جس کا پہلا تاثُر آپ کے اوپر وہی ہوا جو ایک بے خبر انسان پر اِتنے بڑے ایک حادثہ کے پیش آنے سے فطری طور پر ہو سکتا ہے ۔ یہی وجہ ہے کہ جب آپ اسلام کی دعوت لے کر اُٹھے تو مکّہ کے لوگوں نے آپ پر ہر طرح کے اعتراضات کیے، مگر اُن میں کوئی یہ کہنے والا نہ تھا کہ ہم کو تو پہلے ہی یہ خطرہ تھا کہ آپ کوئی دعویٰ کرنے والے ہیں کیونکہ آپ ایک مدت سے نبی بننے کی تیاریاں کر رہے تھے۔ اس قصّے سے ایک بات یہ بھی معلوم ہوتی ہے کہ نبوّت سے پہلے آپ کی زندگی کیسی پاکیزہ تھی اور آپ کا کردار کتنا بلند تھا۔ حضرت خدیجہ ؓ کوئی کم سن خاتون نہ تھیں بلکہ اِس واقعہ کے وقت اُن کی عمر ۵۵ سال تھی اور پندرہ سال سے وہ حضورؐ کی شریکِ زندگی تھیں۔ بیوی سے شوہر کی کوئی کمزوری چھپی نہیں رہ سکتی۔ انہوں نے اِس طویل ازدواجی زندگی میں آپ کو اِتنا عالی مرتبہ انسان پایا تھا کہ جب حضورؐ نے اُن کو غارِ حراء میں پیش آنے والا واقعہ سنایا تو بلا تامُل انہوں نے یہ تسلیم کر لیا کہ فی الواقع اللہ کا فرشتہ ہی آپ کے پاس وحی لے کر آیا تھا۔ اِسی طرح وَرَقہ بن نَوفَل بھی مکّہ کے ایک بوڑھے باشندے تھے ، بچپن سے حضورؐ کی زندگی دیکھتے چلے آرہے تھے، اور پندرہ سال کی قریبی رشتہ داری کی بنا پر تو وہ آپ کے حالات سے اور بھی زیادہ گہری واقفیت رکھتے تھے۔ اُنہوں نے بھی جب یہ واقعہ سُنا تو اِسے کوئی وَسْوَسہ نہیں سمجھا بلکہ سنتے ہی کہہ دیا کہ یہ وہی ناموس ہے جو موسیٰ علیہ السلام پر نازل ہوا تھا۔ اِس کے معنی یہ ہیں کہ ان کے نزدیک بھی آپ اِتنے بلند پایہ انسان تھے کہ آپ کا نبوت کے منصب پر سرفراز ہونا کوئی قابلِ تعجب امر نہ تھا۔ دوسرے حصہ کا شانِ نزول : اِس سُورہ کا دوسرا حصّہ اُس وقت نازل ہوا جب رسول اللہ صلی اللہ علیہ وسلم نے حَرَم میں اسلامی طریقہ پر نماز پڑھنی شروع کی اور ابو جہل نے آپ کو ڈرا دھمکا کر اِس سے روکنا چاہا۔ معلوم ایسا ہوتا ہے کہ نبی ہونے کےبعد قبل اِس کے کہ حضورؐ اسلام کی عَلانیہ تبلیغ کا آغاز کرتے، آپ نے حَرَم میں اُ س طریقے پر نماز ادا کرنی شروع کر دی جو اللہ تعالیٰ نے آپ کو سکھائی تھی ، اور یہی وہ چیز تھی جس سے قریش نے پہلی مرتبہ یہ محسوس کیا کہ آپ کسی نئے دین کے پیرو ہو گئے ہیں۔ دوسرے لوگ تو اِسے حیرت ہی کی نگاہ سے دیکھ رہے تھے، مگر ابو جہل کی رگِ جاہلیت اِس پر پھڑک اُٹھی اور اس نے آپ کو دھمکانا شروع کر دیا کہ اِس طریقے پر حرم میں عبادت نہ کریں۔ چنانچہ اس سلسلے میں کئی احادیث حضرت عبداللہ بن عباسؓ اور حضرت ابو ہریرہؓ سے مروی ہیں جن میں ابو جہل کی اِن بیہودگیوں کا ذکر کیا گیا ہے۔ حضرت ابو ہریرہؓ کا بیان ہے کہ ابو جہل نے قریش کے لوگوں سے پوچھا ”کیا محمد (صلی اللہ علیہ وسلم) تمہارے سامنے زمین پر اپنا منہ ٹِکاتے ہیں؟“ لوگوں نے کہا ہاں۔ اس نے کہا” لات اور عُزّیٰ کی قسم، اگر میں نے ان کو اِس طرح نماز پڑھتے ہوئے دیکھ لیا تو ان کی گردن پر پاو ٴں رکھ دوں گا اور ان کا منہ زمین میں رگڑ دوں گا۔“ پھر ایسا ہوا کہ حضورؐ کو نماز پڑھتے دیکھ کر وہ آگے بڑھا تاکہ آپ کی گردن پر پاوٴں رکھے، مگر یکایک لوگوں نے دیکھا کہ وہ پیچھے ہٹ رہا ہے اور اپنا منہ کسی چیز سے بچانے کی کوشش کررہا ہے۔ اُس سے پوچھا گیا کہ یہ تجھے کیا ہو گیا؟ اس نے کہا میرے اور اُن کے درمیان آگ کی ایک خندق اور ایک ہولناک چیز تھی اور کچھ پَر تھے۔ رسول اللہ صلی اللہ علیہ وسلم نے فرمایا کہ اگر وہ میرے قریب پھٹکتا تو ملائکہ اُس کے چیتھڑے اڑا دیتے (احمد، مسلم، نَسائی، ابن جریر، ابن ابی حاتم، ابن المُنْذِر، ابن مَرْدُویَہ، ابو نُعَیم اصفہانی، بَیْہَقی)۔ ابن عبارؓ کی روایت ہے کہ ابو جہل نے کہا اگر میں نے محمد (صلی اللہ علیہ وسلم) کو کعبہ کے پاس نماز پڑھتے دیکھ لیا تو اُن کی گردن پاوٴں تلے دبا دوں گا۔ نبی صلی اللہ علیہ وسلم کو اس کی خبر پہنچی تو آپ نے فرمایا کہ اگر اس نے ایسا کیا تو ملائکہ علانیہ اُسے آپکڑیں گے (بخاری، تِرْمِذِی، نَسائی، ابن جریر، عبد الرزاق، عبد بن حُمَید، ابن المُنذِر، ابن مَرْدُوْیَہ)۔ ابن عباسؓ کی ایک اور روایت ہے کہ رسول اللہ صلی اللہ علیہ وسلم مقام ابراہیم پر نماز پڑھ رہے تھے۔ ابو جہل کا اُدھر سے گزر ہوا تو اس نے کہا اے محمدؐ، کیا میں نے تم کو اِس سے منع نہیں کیا تھا؟ اور اس نے آپ کو دھمکیاں دینی شروع کیں ۔ جواب میں رسول اللہ صلی اللہ علیہ وسلم نے اس کو سختی کے ساتھ جھڑک دیا۔ اِس پر اس نے کہا اے محمدؐ، تم کس بل پر مجھے ڈرا رہے ہو۔ خدا کی قسم، اِس وادی میں میرے حمایتی سب سے زیادہ ہیں۔ (احمد، تِرْمِذی، نَسائی، ابن جریر، ابن ابی شَیْبَہ، ابن المُنذِر، طَبرَانی، ابن مَرْدُوْیَہ)۔ انہی واقعات پر اِس سورہ کا وہ حصّہ نازل ہوا جو کَلَّآ اِنَّ الْاِنْسَانَ لَیَطْغٰی سے شروع ہو تا ہے ۔ قدرتی طور پر اِس حصّے کا مقام وہی ہونا چاہیے تھا جو قرآن کی اِس سورۃ میں رکھا گیا ہے ۔ کیونکہ پہلی وحی نازل ہونے کے بعد اِسلام کا اوّلین اظہار حضورؐ نے غار ہی سے کیا تھا، اور کفّارِ سے آپ کی مُڈبھیڑ کا آغاز بھی اِسی واقعہ سے ہوا تھا۔ ','سید ابو اعلیٰ مودودیؒ -  تفہیم القرآن');
INSERT INTO "chapters_ur" VALUES(97,'شب قدر',NULL,' نام : پہلی ہی آیت کے لفظ اَلقدر کو اس سورہ کا نام قرار دیا گیا ہے۔ زمانۂ نزول : اس کے مکّی اور مدنی ہونے میں اختلاف ہے۔ ابو حَیّان نے البحر المحیط میں دعویٰ کیا ہے کہ اکثر اہلِ علم کے نزدیک یہ مدنی ہے ۔ علی بن احمد الواحدی اپنی تفسیر میں کہتے ہیں کہ یہ پہلی سورۃ جو مدینہ میں نازل ہوئی ہے۔ بخلاف اس کے الماوَرْدِی کہتے ہیں کہ اکثر اہلِ علم کے نزدیک یہ مکّی ہے، اور یہی بات امام سیُوطِی نے اِتْقان میں لکھی ہے۔ ابن مَرْدُویَہ نے ابن عباس ، ابن الزُّبَیر اور حضرت عائشہ سے یہ قول نقل کیا ہے کہ یہ سورۃ مکّہ میں نازل ہوئی تھی۔ سورۃ کے مضمون پر غور کرنے سے بھی یہی محسوس ہوتا ہے کہ اس کو مکّہ ہی میں نازل ہونا چاہیے تھا، جیسا کہ ہم آگے واضح کریں گے۔ موضوع اور مضمون: اس کا موضوع لوگوں کو قرآن کی قدر و قیمت اور اہمیت سے آگاہ کرنا ہے۔ قرآن مجید کی ترتیب میں اِسے سورۂ عَلَق کے بعد رکھنے سے خود یہ ظاہر ہوتا ہے کہ جس کتاب پاک کے نزول کا آغاز سورۂ عَلَق کی ابتدائی پانچ آیات سے ہوا تھا اُسی کے متعلق اِس سورہ میں لوگوں کو بتایا گیا ہے کہ وہ کس تقدیر ساز رات میں نازل ہوئی ہے۔ کیسی جلیل القدر کتاب ہے اور اس کا نزول کیا معنی رکھتا ہے۔ سب سے پہلے اس میں اللہ تعالیٰ نے فرمایا ہے کہ ہم نے اِسے نازل کیا ہے ۔یعنی یہ محمد صلی اللہ علیہ وسلم کی اپنی تصنیف نہیں ہے بلکہ اِس کے نازل کرنے والے ہم ہیں۔ اس کے بعد فرمایا کہ اِس کانزول ہماری طرف سے شبِ قدر میں ہوا ہے۔ شبِ قدر کے دو معنی ہیں اور دونوں ہی یہاں مقصود ہیں۔ ایک یہ کہ یہ وہ رات ہے کہ جس میں تقدیروں کے فیصلے کر دیے جاتےہیں، یا بالفاظِ دیگر یہ کوئی معمولی رات عام راتوں جیسی نہیں ہے، بلکہ یہ قسمتوں کےبنانے اور بگاڑنے کی رات ہے۔ اِس میں اِس کتاب کا نزول محض ایک کتاب کا نزول نہیں ہے بلکہ یہ وہ کام ہے جو نہ صرف قریش ، نہ صرف عرب، بلکہ دنیا کی تقدیر بدل کر رکھ دے گا۔ یہی بات سورۂ دُخان میں بھی فرمائی گئی ہے (ملاحظہ ہو تفہیم القرآن، جلد چہارم، سورۂ دُخان کا دیباچہ اور حاشیہ ۳)۔ دوسرے معنی یہ ہیں کہ یہ بڑی قدر و منزلت اور عظمت و شرف رکھنے والی رات ہے ، اورآگے اس کی تشریح یہ کی گئی ہے کہ یہ ہزار مہینوں سے زیادہ بہتر ہے۔ اِس سے کفّارِ مکّہ کو گویا متنبہ کیا گیا ہے کہ تم اپنی نادانی سے محمد صلی اللہ علیہ وسلم کی پیش کی ہوئی اِس کتاب کو اپنی لیے ایک مصیبت سمجھ رہے ہو اور کوس رہے ہو کہ یہ کیا بلا ہم پر نازل ہو ئی ہے، حالانکہ جس رات کو اِس کے نزول کا فیصلہ صادر کیا گیا وہ اِتنی خیر و برکت والی رات تھی کہ کبھی انسانی تاریخ کے ہزار مہینوں میں بھی انسان کی بھلائی کے لیے وہ کام نہیں ہوا تھا جو اِس رات میں کر دیا گیا۔ یہ بات بھی سُورۂ دُخان آیت ۳ میں ایک دوسرے طریقے سے بیان کی گئی ہے اور اُس سورہ کے دیباچے میں ہم اس کی وضاحت کر چکے ہیں۔ آخر میں بتایا گیا ہے کہ اِس رات کو فرشتے اور جبریل ؑ اپنے ربّ کے اِذن سے ہر حکم لے کر نازل ہوتے ہیں (جسے سورۂ دخان ، آیت۴ میں امرِ حکیم کہا گیاہے) اور وہ شام سے صبح تک سراسر سلامتی کی رات ہوتی ہے ، یعنی اس میں کسی شر کا دخل نہیں ہوتا ، کیونکہ اللہ تعالیٰ کے تمام فیصلے بالآخر بھلائی کے لیے ہوتے ہیں، ان میں کوئی بُرائی مقصود نہیں ہوتی، حتیٰ کہ اگر کسی قوم کو تباہ کرنے کا فیصلہ بھی ہوتا ہے تو خیر کے لیے ہوتا ہے نہ کہ شر کے لیے۔ ','سید ابو اعلیٰ مودودیؒ -  تفہیم القرآن');
INSERT INTO "chapters_ur" VALUES(98,'ثبوت','',' نام : پہلی آیت کے لفظ البیّنہ کو اس سورہ کا نام قرار دیا گیا ہے۔ زمانۂ نزول : اس کے بھی مکّی اور مدنی ہونے میں اختلاف ہے۔ بعض مفسّرین کہتے ہیں کہ جمہور کے نزدیک یہ مکّی ہے اور بعض دوسرے مفسرّین کہتے ہیں کہ جمہور کے نزدیک مدنی ہے۔ ابن الزُّبیر اور عطاء بن یَسار کا قول ہے کہ یہ مدنی ہے ۔ ابن عباس اور قَتَادہ کے دو قول منقول ہیں۔ ایک یہ کہ یہ مکّی ہے، دوسرا یہ کہ مدنی ہے۔ حضرت عائشہ ؓ اِسے مکّی قرار دیتی ہیں۔ ابو حَیّان صاحبِ بحر المحیط اور عبد المنعم ابن الفَرَس صاحبِ احکام القرآن اِس کے مکّی ہونے ہی کو ترجیح دیتے ہیں۔ جہاں تک اِس کے مضمون کا تعلق ہے ، اُس میں کوئی علامت ایسی نہیں پائی جاتی جو اِس کے مکّی یا مدنی ہونے کی طرف اشارہ کرتی ہو۔ موضوع اور مضمون : قرآن مجید کی ترتیب میں اِس کو سورۂ علق اور سورۂ قدر کے بعد رکھنا بہت معنی خیز ہے ۔ سورۂ عَلَق میں پہلی وحی درج کی گئی ہے۔ سورۂ قدر میں بتایا گیا ہے کہ وہ کب نازل ہوئی۔ اور اِس&nbsp;سورہ میں بتایا گیا ہے کہ اِس کتاب ِ پاک کے ساتھ ایک رسول بھیجنا کیوں ضروری تھا۔ سب سے پہلے رسول بھیجنے کی ضرورت بیان کی گئی ہے ، اور وہ یہ کہ&nbsp;دنیا کے لوگ، خواہ وہ اہل ِ کتاب&nbsp;میں سے ہوں یا مشرکین میں سے، جس کفر کی حالت میں مبتلا تھے اُس سے اُن کا نکلنا اِس کے بغیر ممکن نہ تھا کہ ایسا رسول بھیجا جائے جس کا وجود خود اپنی رسالت پر دلیل ِ روشن ہو، اور وہ لوگوں کے سامنے خدا کی کتاب&nbsp;کو اس کی اصلی صورت میں پیش کرے جو باطل کی اُن تمام آمیزشوں سے پاک ہو جن سے پچھلی کتبِ آسمانی کو آلودہ کر دیا گیا ہے اور بالکلراست اور درست تعلیمات پر مشتمل ہو۔ اِس کے بعد&nbsp;اہلِ کتاب کی گمراہیوں کے متعلق وضاحت کی گئی ہے کہ&nbsp;اُن کے اِن مختلف راستوں میں بھٹکنے&nbsp;کی وجہ یہ نہ تھی کہ اللہ تعالیٰ نے اُن کی کوئی رہنمائی نہ کی تھی، بلکہ وہ اِس کے بعد بھٹکے کہ راہِ راست کا بیانِ واضح اُن کے پاس آچکا تھا۔ اِس سے خود بخود یہ نتیجہ نکلتا ہے کہ اپنی گمراہیوں کے وہ خود ذمّہ دار ہیں، اور اب&nbsp;پھر اللہ کے اِس رسول کے ذریعہ سے بیانِ واضح آجانے کے بعد بھی اگر وہ بھٹکتے ہی رہیں گے تو اُن کی ذمّہ داری اور زیادہ بڑھ جائے گی۔ اِسی سلسلے میں یہ بتایا گیا ہے کہ اللہ تعالیٰ کی طرف سے جو انبیاء بھی آئے تھے، اور جو کتابیں بھی بھیجی گئی تھیں ، اُنہوں نے اِس کے سوا کوئی حکم نہیں دیا تھا کہ سب طریقوں کو چھوڑ کر خالص اللہ کی بندگی&nbsp;کا طریقہ اختیار کیا جائے، کسی اور کی عبادت و بندگی اور اطاعت و پرستش کو اس کے ساتھ شامل نہ کیا جائے ، نماز قائم کی جائے اور زکوٰۃ ادا کی جائے۔ یہی ہمیشہ سے ایک صحیح دین رہا ہے۔ اِس سے بھی یہ نتیجہ خود بخود برآمد ہوتا ہے کہ اہلِ کتاب نے اِس اصل دین سے ہٹ کر اپنے مذہبوں میں جن نئی نئی باتوں کا اضافہ کر لیا ہے وہ سب باطل ہیں ، اور اللہ کا یہ رسول جو اَب آیا ہے اُسی اصل دین کی طرف پلٹنے کی اُنہیں دعوت دے رہا ہے۔ آخر میں صاف صاف ارشاد ہوا ہے کہ جو اہلِ کتاب اور مشرکین اِس رسول کو ماننے سے انکار کریں گے وہ بدترینِ خلائق ہیں، اُن کی سزا اَبَدِی جہنّم ہے، اور جو لوگ ایمان لا کر عملِ صالح کا طریقہ اختیار کر لیں گے اور دنیا میں خدا سے ڈرتے ہوئے زندگی بسر کریں گے وہ بہترین خلائق ہیں، اُن کی جزا یہ ہے کہ وہ ہمیشہ ہمیشہ جنت میں رہیں گے، اللہ ان سے راضی ہوا اور وہ اللہ سے راضی ہو گئے۔ ','سید ابو اعلیٰ مودودیؒ -  تفہیم القرآن');
INSERT INTO "chapters_ur" VALUES(99,'زلزلہ',NULL,' نام : پہلی آیت کے لفظ زِلْزَالَھَا سے ماخوذ ہے۔ زمانۂ نزول : اس کے مکّی اور مدنی ہونے میں اختلاف ہے ۔ ابن مسعودؓ ، عطاء ، جابر اور مجاہد کہتے ہیں کہ یہ مکّی ہے اور ابن عباس ؓ کا بھی ایک قول اس کی تائید میں منقول ہے۔ بخلاف اِس کے قَتَادہ اور مُقاتِل کہتے ہیں کہ یہ مدنی ہے اور ابن عباس ؓ سے بھی دوسرا قول اِس کے مدنی ہونے کی تائید میں نقل ہوا ہے۔اِس کے مدنی ہونے پر حضرت ابو سعیدؓ خُدْرِی کی اُس روایت سے استدلال کیا جاتا ہے کہ جو ابن ابی حاتم نے اُن سے نقل کی ہے کہ جب یہ آیت نازل ہوئی کہ فَمَنْ یَّعْمَلْ مِثْقَالَ ذَرَۃٍ خَیْرًا یَّرَہٗ وَمَنْ یَّعْمَلْ مِثْقَالَ ذَرَۃٍ شَرًا یَّرَہٗ تو میں نے عرض کیا ، یا رسول ؐ اللہ کیا میں اپنا عمل دیکھنے والا ہوں؟ حضور ؐ نے فرمایا ہاں۔ میں نے عرض کیا یہ بڑے بڑے گناہ؟ آپ ؐ نے جواب دیا ہاں۔ میں عرض کیا اور یہ چھوٹے چھوٹے گنا ہ بھی؟ حضور ؓ نے فرمایا ہاں۔ اس پر میں نے کہا پھر تو میں مارا گیا۔ حضور ؐ نے فرمایا خوش ہو جاؤ اے ابو سعید، کیونکہ ہر نیکی اپنی جیسی دس نیکیوں کے برابر ہو گی۔ اِس حدیث سے اِس سورہ کے مدنی ہونے پر استدلال کی بِنا یہ ہے کہ حضرت ابو سعید خُدریؓ مدینے کے رہنے والے تھے اور غَزْوۂ اُحد کے بعد سنّ بلوغ کو پہنچے۔ اس لیے اگر یہ سورۃ ان کی موجودگی میں نازل ہو ئی تھی، جیسا کہ ان کے بیان سے ظاہر ہے، تو اسے مدنی ہونا چاہیے۔ لیکن صحابہ اور تابعین کا جو طریقہ آیات اور سورتوں کی شان نزول کے بارے میں تھا، اس کی تشریح اس سے پہلے ہم سورۂ دہر کے دیباچے میں کر چکے ہیں۔ اس لیے کسی صحابی کا یہ کہنا کہ یہ آیت فلاں موقع پر نازل ہوئی، اس بات کا قطعی ثبوت نہیں ہے کہ اس کا نزول اُسی وقت ہوا تھا۔ ہو سکتا ہے کہ حضرت ابو سعید نے ہوش سنبھالنے کےبعد جب پہلی مرتبہ حضور ؐ کی زبان مبارک سے یہ سورۃ سنی ہو اس وقت اِس کے آخرت حصّے سے خوف زدہ ہو کر انہوں نے حضور ؐ سے وہ سوالات کیے ہوں جو اوپر درج کیے گئے ہیں، اور اس واقعہ کو انہوں نے اس طرح بیان کیا ہو کہ جب یہ آیت نازل ہوئی تو میں نے حضور ؐ سے یہ عرض کیا ۔ اگر یہ روایت سامنے نہ ہو تو قرآن کو سمجھ کر پڑھنے والا ہر شخص یہی محسوس کرے گا کہ یہ مکّی سورۃ ہے، بلکہ اس کے مضمون اور اندازِ بیان سے تو اس کو محسوس ہو گا کہ یہ مکّہ کے بھی اُس ابتدائی دور میں نازل ہوئی ہو گی جب نہایت مختصر اور انتہائی دل نشین طریقہ سے اسلام کے بنیادی عقائد لوگوں کے سامنے پیش کیے جا رہے تھے۔ ','سید ابو اعلیٰ مودودیؒ -  تفہیم القرآن');
INSERT INTO "chapters_ur" VALUES(100,'دوڑنے والے گھوڑے',NULL,' نام : پہلے ہی لفظ اَلْعٰدِیٰت کو اس کا نام قرار دیا گیا ہے۔ زمانۂ نزول : اِس کے مکی اور مدنی ہونے میں اختلاف ہے۔ حضرت عبداللہ بن مسعود، جابر ، حسن بصری، عِکْرِمہ اور عطاء کہتے ہیں کہ یہ مکّی ہے۔ حضرت اَنَس بن مالک اور قتادہ کہتےہیں کہ مدنی ہے۔ اور حضرت ابن عباس سے دو قول منقول ہوئے ہیں۔ ایک یہ کہ یہ سورۃ مکی ہے اور دوسرا یہ کہ مدنی ہے۔ لیکن سورۃ کا مضمون اور اندازِ بیان صاف بتا رہا ہے کہ یہ نہ صرف مکّی ہے ، بلکہ مکّہ کے بھی ابتدائی دور میں نازل ہوئی ہے۔ موضوع اور مضمون: اِس کا مقصود لوگوں کو یہ سمجھانا ہے کہ انسان آخرت کا منکر یا اُس سے غافل ہو کر کیسی اخلاقی پستی میں گر جاتا ہے، اور ساتھ ساتھ لوگوں کو اس بات سے خبردار بھی کرنا ہے کہ آخرت میں صرف اُن کے ظاہری افعال ہی کی نہیں بلکہ ان کے دلوں میں چھُپے ہوئے اَسرار تک کی جانچ پڑتا ہوگی۔ اِس مقصد کے لیے عرب میں پھیلی ہوئی اُس عام بد امنی کو دلیل کے طور پر پیش کیا گیا ہے جس سے سارا ملک تنگ آیا ہوا تھا۔ ہر طرف کشت و خون برپا تھا۔ لوٹ مار کا بازار گرم تھا۔ قبیلوں پر قبیلے چھاپے مار رہے تھے اور کوئی شخص بھی رات چین سے نہیں گزار سکتا تھا کیونکہ ہر وقت یہ کھٹکا لگا رہتا تھا کہ کب کوئی دشمن صبح سویرے اس کی بستی پر ٹوٹ پڑے۔ یہ ایک ایسی حالت تھی جسے عرب کے سارے ہی لوگ جانتے تھے اور اس کی قباحت کو محسوس کرتے تھے۔ اگرچہ لُٹنے والا اس پر ماتم کرتا تھا اور لوٹنے والا اس پر خوش ہوتا تھا، لیکن جب کسی وقت لوٹنے والے کی اپنی شامت آجاتی تھی تو وہ بھی محسوس کر لیتا تھا کہ یہ کیسی بُری حالت ہے جس میں ہم لوگ مبتلا ہیں۔ اِس صورت حال کی طرف اشارہ کر کے یہ بتایا گیا ہے کہ موت کے بعد دوسری زندگی اور اپس میں خدا کے حضور جواب دہی سے ناواقف ہو کر انسان اپنے ربّ کا ناشکرا ہو گیا ہے، وہ خدا کی دی ہوئی قوتوں کو ظلم و ستم اور غارت گری کے لیے استعمال کر رہا ہے، وہ مال و دولت کی محبت میں اندھا ہو کر ہر طریقے سے اُسے حاصل کرنے کی کوشش کرتا ہے ، خواہ وہ کیسا ہی ناپاک اور گھِناؤنا طریقہ ہو، اور اُس کی حالت خود اِس بات کی گواہی دے رہی ہے کہ وہ اپنے ربّ کی عطا کی ہوئی قوتوں کا غلط استعمال کر کے اس کی ناشکری کر رہا ہے۔ اُس کی یہ روش ہر گز نہ ہوتی اگر وہ اُس وقت کو جانتا ہوتا جب قبروں سے زندہ ہو کر اُٹھنا ہو گا، اور جب وہ ارادے اور وہ اغراض و مقاصد تک دلوں سے نکال کر سامنے رکھ دیے جائیں گے جن کی تحریک سے اُس نے دنیا میں طرح طرح کے کام کیے تھے۔ اُس وقت انسانوں کے ربّ کو خوب معلوم ہو گا کہ کون کیا کر کے آیا ہے، اور کس کے ساتھ کیا برتاؤ کیا جانا چاہیے۔ ','سید ابو اعلیٰ مودودیؒ -  تفہیم القرآن');
INSERT INTO "chapters_ur" VALUES(101,'کھڑکھڑاہٹ',NULL,' نام : پہلے لفظ اَلْقَارِعَۃِ کو اس کا نام قرار دیا گیا ہے۔ یہ صرف نام ہی نہیں ہے بلکہ اس کے مضمون کا عنوان بھی ہے ، کیونکہ اس میں ساراذکر قیامت ہی کا ہے۔ زمانۂ نزول : اس کے مکّی ہونے میں کوئی اختلاف نہیں ہے ۔ بلکہ اس کے مضمون سے ظاہر ہوتا ہے کہ یہ بھی مکّۂ معظمہ کے ابتدائی دور کی نازل شدہ سورتوں میں سے ہے۔ موضوع اور مضمون: اس کا موضوع ہے قیامت اور آخرت۔ سب سے پہلے لوگوں کو یہ کہہ کر چَونکا یا گیا ہے کہ عظیم حادثہ ! کیا ہے وہ عظیم حادثہ؟ تم کیا جانو کہ وہ عظیم حادثہ کیا ہے؟ اِس طرح سامعین کو کسی ہولناک واقعہ کے پیش آنے کی خبر سننے کے لیے تیار کرنے کے بعد دو فقروں میں ان کے سامنے قیامت کا نقشہ پیش کر دیا گیا ہے کہ اُس روز لوگ گھبراہٹ کے عالم میں اِس طرح ہر طرف بھاگے بھاگے پھر یں گے جیسے روشنی پر آنے والے پروانے بِکھرے ہوئے ہوتے ہیں، اور پہاڑوں کا حال یہ ہو گا کہ وہ اپنی جگہ سے اکھڑ جائیں گے ، ان کی بندش ختم ہو جائے گی اور وہ دُھنکے ہوئے اون کی طرح ہو کر رہ جائیں گے۔ پھر بتایا گیا ہے کہ آخرت میں جب لوگوں کا حساب کرنے کے لیے اللہ تعالیٰ کی عدالت قائم ہو گی تو اُس میں فیصلہ اِس بنیاد پر ہو گا کہ کس شخص کوے نیک اعمال بُرے اعمال سے زیادہ وزنی ہیں ، اور کس کے نیک اعمال کا وزن اس کے بُرے اعمال کی بہ نسبت ہلکا ہے۔ پہلی قسم کے لوگوں کو وہ عیش نصیب ہوگا جس سے وہ خوش ہو جائیں گے ، اور دوسری قسم کے لوگوں کو اُس گہری کھائی میں پھینک دیا جائے گا جو آگ سے بھری ہوئی ہو گی۔ ','سید ابو اعلیٰ مودودیؒ -  تفہیم القرآن');
INSERT INTO "chapters_ur" VALUES(102,'کثرت',NULL,' نام : پہلی آیت کے لفظ التکاثُر کو اس سورۃ کا نام قرار دیا گیا ہے۔ زمانۂ نزول : ابو حَیّان اور شَوکانی کہتے ہیں کہ یہ تمام مفسّرین کے نزدیک مکّی ہے۔ اور امام سیوطِی کا قول ہے کہ مشہور ترین بات یہی ہے کہ یہ مکّی ہے، لیکن بعض روایات ایسی ہیں جن کی بنا پر اسے مدنی کہا گیا ہے، اور وہ یہ ہیں: ابن ابی حاتم نے ابوبُرَیدہؓ کی روایت نقل کی ہے کہ یہ سورۃ انصا کے دو قبیلوں بنی حارثہ اور بنی الحرث کے بارے میں نازل ہوئی۔ دونوں قبیلوں کنے ایک دوسرے کے مقابلے میں پہلے اپنے زندہ آدمیوں کے مَفاخِر بیان کیے، پھر قبر ستان جا کر اپنے اپنے مرے ہوئے لوگوں کے مفاخر پیش کیے۔ اس پر یہ ارشادِ الہٰی نازل ہوا کہ اَلْھٰکُمُ التَّکَاثُر ۔ لیکن شانِ نزول کے بارے میں صحابہ و تابعین کا جو طریقہ تھا ، اُس کو اگر نگاہ میں رکھا جائے تو یہ روایت اِس امر کی دلیل نہیں ہے کہ سُورۂ تکاثُر اسی موقع پر نازل ہوئی تھی ، بلکہ اس سے مراد یہ ہے کہ اِن دونوں قبیلوں کے اِس فعل پر یہ سُورۃ چسپاں ہوتی ہے۔ امام بخاری اور ابن جریر نے حضرت اُبَیّ بن کعبؓ کا یہ قول نقل کیا ہے کہ”ہم رسول اللہ صلی اللہ علیہ وسلم کے اِس ارشاد کو کہ لو ان لابن اٰدم وادیین من مال لتمنّٰی وادیًا ثالثًا ولا یَمْلَأُ جوف ابن اٰدم الّا التراب (اگر آدم زاد کے پاس دو وادیاں بھر کر مال ہو تو وہ تیسری وادی کی تمنّا کرے گا۔ ابن آدم کا پیٹ مٹّی کے سوا کسی چیز سے نہیں بھر سکتا) قرآن میں سے سمجھتے تھے ، یہاں تک کہ اَلْھٰکُمُ التَّکَاثُر نازل ہوئی۔“ اس حدیث کو سورۂ تکاثر کے مدنی ہونے کی دلیل اس بنا پر قرار دیا گیا ہے کہ حضرت اُبَیّ مدینے میں مسلمان ہوئے تھے۔ مگر حضرت اُبَیّ کے اس بیان سے یہ بات واضح نہیں ہوتی کہ صحابہ کرام کس معنی میں حضورؐ کے اس ارشاد کو قرآن میں سے سمجھتے تھے۔ اگر اس کا مطلب یہ ہو کہ اسے قرآن کی ایک آیت سمجھتے تھے تو یہ بات ماننے کے لائق نہیں ہے ، کیونکہ صحابہ کی عظیم اکثریت اُس اصحاب پر مشتمل تھی جو قرآن کے حرف حرف سے واقف تھے، ان کو یہ غلط فہمی کیسے لاحق ہو سکتی تھی کہ یہ حدیث قرآن کی ایک آیت ہے۔ اور اگر قرآن میں سے ہونے کا مطلب قرآن سے ماخوذ ہونا لیا جائے تو اس روایت کا مطلب یہ بھی ہو سکتا ہے کہ مدینۂ طیبہ میں جو اصحاب داخل اسلام ہوئے تھے انہوں نے جب پہلی مرتبہ حضورؐ کی زبانِ مبارک سے یہ سورۃ سُنی تو انہوں نے یہ سمجھا کہ یہ ابھی نازل ہوئی ہے ، اور پھر حضورؐ کے مذکورۂ بالا ارشاد کے متعلق اُن کو یہ خیال ہو اکہ وہ اِسی سورۃ سے ماخوذ ہے۔ ابن جریر ، ترمذی اور ابن المُنذِر وغیر محدثین نے حضرت علیؓ کا یہ قول نقل کیا ہے کہ ”ہم عذابِ قبر کے بارے میں برابر شک میں پڑے رہے یہاں تک کہ اَلْھٰکُمُ التَّکَاثُر نازل ہوئی۔“ اِس کو سُورہ ٔ تکاثر کے مدنی ہونے کے دلیل اس بنا پر قرار دیا گیا ہے کہ عذابِ قبر کا ذکر مدینے ہی میں ہوا تھا ، مکّہ میں اس کا کوئی ذکر نہیں ہوا تھا۔ مگر یہ بات غلط ہے ۔ قرآن کی مکّی سورتوں میں بکثرت مقامات پر قبر کے عذاب کا ایسے صریح الفاظ میں ذکر کیا گیا ہے کہ شک کی گنجائش نہیں رہتی۔ مثال کے طور پر ملاحظہ ہو الانعام، آیت ۹۳۔ النحل، ۲۸۔ المومنون ۱۰۰-۹۹۔ المومن ۴۶-۴۵۔ یہ سب مکّی سورتیں ہیں۔ اس لیے حضرت علیؓ کے ارشاد سے اگر کوئی چیز ثابت ہوتی ہے تو وہ یہ ہے کہ مذکورۂ بالا مکّی سورتوں کے نزول سے پہلے سورۂ تکاثُر نازل ہو چکی تھی ، اور اُس کے نزول نے عذابِ قبر کے بارے میں صحابہ کے شک کو دور کر دیا تھا۔ یہی وجہ ہے کہ اِن روایات کے باوجود مفسّرین کی عظیم اکثریت اس کے مکّی ہونے پر متفق ہے۔ ہمارے نزدیک صرف یہی نہیں کہ یہ مکّی سورۃ ہے، بلکہ اس کا مضمون اور اندازِ بیان یہ بتا رہا ہے کہ یہ مکّے کے ابتدائی دور کی نازل شدہ سورتوں میں سے ہے۔ موضوع اور مضمون: اِس میں لوگوں کو اُس دنیا پرستی کے برے انجام سے خبردار کیا گیا ہے جس کی وجہ سے وہ مرتے دم تک زیادہ سے زیادہ مال و دولت، اور دنیوی فائدے اور لذّتیں اور جوہ و اقتدار حاصل کرنے اور اُس میں ایک دوسرے سے بازی لے جانے ، اور انہی چیزوں کے حصول پر فخر کرنے میں لگے رہتے ہیں ،اور اِس ایک فکر نے اُن کو اس قدر منہمک کر رکھا ہے کہ انہیں اس سے بالاتر کسی چیز کی طرف توجہ کرنے کا ہوش ہی نہیں ہے۔ اِس کے برے انجام پر متنبہ کرنے کے بعد لوگوں کو یہ بتایا گیا ہے کہ یہ نعمتیں جن کو تم یہاں بے فکری کے ساتھ سمیٹ رہے ہو، یہ محض نعمتیں ہی نہیں ہیں بلکہ تمہاری آزمائش کا سامان بھی ہیں۔ ان میں سے ہر نعمت کے بارے میں تم کو آخرت میں جواب دہی کرنی ہو گی۔ ','سید ابو اعلیٰ مودودیؒ -  تفہیم القرآن');
INSERT INTO "chapters_ur" VALUES(103,'زمانہ',NULL,' نام : پہلی آیت کے لفظ العَصْر کو اس کا نام قرار دیا گیا ہے۔ زمانۂ نزول : اگرچہ مجاہد ، قتادہ اور مقاتل نے اسے مدنی کہا ہے ، لیکن مفسرین کی عظیم اکثریت اسے مکّی قرار دیتی ہے۔ اور اس کا مضمون یہ شہادت دیتا ہے کہ یہ مکّہ کے بھی ابتدائی دور میں نازل ہوئی ہو گی۔ جب اسلام کی تعلیم کو مختصر اور انتہائی دلنشین فقروں میں بیان کیا جاتا تھا، تا کہ سُننے والے ایک دفعہ اُن کو سُن کر بھُولنا بھی چاہیں تو نہ بھول سکیں۔ اور وہ آپ سے آپ لوگوں کی زبانوں پر چڑھ جائیں۔ ','سید ابو اعلیٰ مودودیؒ -  تفہیم القرآن');
INSERT INTO "chapters_ur" VALUES(104,'عیب لگانے والا',NULL,' نام : پہلی آیت کے لفظ ھُمَزَہ کو اس سورے کا نام قرار دیا گیا ہے۔ زمانۂ نزول : اس کے مکّی ہونے پر تمام مفسّرین کا اتفاق ہے۔ اور اس کے مضمون اور اندازِ بیان پر غور کرنے سے محسوس ہوتا ہے کہ یہ بھی مکّہ کے ابتدائی دور میں نازل ہونے والی سورتوں میں سے ہے۔ موضوع اور مضمون: اِس میں چند ایسی اخلاقی برائیوں کی مذمت کی گئی ہے جو جاہلیت کے معاشرے میں زرپرست مالداروں کے اندر پائی جاتی تھیں، جنہیں ہر عرب جانتا تھا کہ یہ برائیاں فی الواقع اُس کے معاشرے میں موجود ہیں، اور جن کو سب ہی برا سمجھتے تھے ، کسی کا بھی یہ خیال نہ تھا کہ یہ کوئی خوبیاں ہیں۔ اِس گھناؤنے کردار کو پیش کرنے کے بعد یہ بتایا گیا ہے کہ آخرت میں اُن لوگوں کا کیا انجام ہو گا جن کا یہ کردار ہے۔ یہ دونوں باتیں (یعنی ایک طرف یہ کردار اور دوسری طرف آخرت میں اُس کا یہ انجام) ایسے انداز سے بیان کی گئی ہیں جس سے سامع کا ذہن خود بخود اِس نتیجے پر پہنچ جائے کہ اِس طرح کے کردار کا یہی انجام ہونا چاہیے ، اور چونکہ دنیا میں ایسے کردار والوں کو کوئی سزا نہیں ملتی، بلکہ وہ پھلتے پھولتے ہی نظر آتے ہیں، اس لیے آخرت کا برپا ہونا قطعی ناگزیر ہے۔ اِس سورۃ کو اگر اُن سورتوں کے تَسَلْسُل میں رکھ کر دیکھا جائے جو سُورۂ زِلْزال سے یہاں تک چلی آرہی ہیں تو آدمی بڑی اچھی طر ح یہ سمجھ سکتا ہے کہ مکّہ معظّمہ کے ابتدائی دور میں کس طریقہ سے اسلام کے عقائد اور اُس کی اخلاقی تعلیمات کو لوگوں کے ذہن نشین کیا گیا تھا۔ سورۂ زِلزال میں بتایا گیا کہ آخرت میں انسان کا پورا نامۂ اعمال اُس کے سامنے رکھ دیا جائے گا اور کوئی ذرّہ برابر نیکی یا بدی بھی ایسی نہ ہو گی جو اس نے دنیا میں کی ہو اور وہ وہاں اُس کے سامنے نہ آجائے۔ سورۂ عادیات میں اُس لوٹ مار، کُشت و خون اور غارت گری کی طرف اشارہ کیا گیا جو عرب میں ہر طرف برپا تھی، پھر یہ احساس دلانے کے بعد کہ خدا کی دی ہوئی طاقتوں کا یہ استعمال اُس کی بہت بڑی ناشکری ہے ، لوگوں کو یہ بتایا گیا ہے کہ معاملہ اِسی دنیا میں ختم نہیں ہو جائے گا ، بلکہ موت کے بعد دوسری زندگی میں تمہارے افعال ہی کی نہیں ، تمہاری نیتوں تک کی جانچ پڑتا کی جائے گی اور تمہارا ربّ خوب جانتا ہے کہ کون آدمی کس سلوک کا مستحق ہے۔ سورۂ قارعہ میں قیامت کا نقشہ پیش کرنے کے بعدلوگوں کو خبردار کیا گیا ہے کہ آخرت میں انسان کے اچھے یا برے انجام کا انحصار اِس پر ہوگا کہ اُس کی نیکیوں کا پلڑا بھاری ہے یا ہلکا۔ سورۂ تکاثُر میں اُس مادّہ پرستانہ ذہنیت پر گرفت کی گئی جس کی وجہ سے لوگ مرتے دم تک بس دنیا کے فائدے اور لذّتیں اور عیش و آرام اور جاہ و منزلت زیادہ سے زیادہ حاصل کرنے اور ایک دوسرے سے بڑھ جانے کی کوشش میں لگے رہتے ہیں ، پھر اِس غفلت کے برے انجام سے آگاہ کر کے لوگوں کو بتایا گیا کہ یہ دنیا کوئی خوانِ یَغما نہیں ہے کہ اُس تم جتنا اور جس طرح چاہو ہاتھ مارو، بلکہ ایک ایک نعمت جو یہاں تمہیں مل رہی ہے اُس کے لیے تمہیں اپنے ربّ کو جواب دینا ہو گا کہ اسے تم نے کیسے حاصل کیا ، اور حاصل کر کے اس کو کس طرح استعمال کیا۔ سورۂ عصر میں بالکل دوٹوک طریقے سے بتا دیا گیا کہ نوعِ انسانی کا ایک ایک فرد، ایک ایک گروہ، ایک ایک قوم، حتیٰ کہ پوری دنیائے انسانیت خسارے میں ہے اگر اُس کے افراد میں ایمان و عمل صالح نہ ہو اور اس کے معاشرے میں حق کی نصیحت اور صبر کی تلقین کا رواجِ عام نہ ہو۔ اِس کے معًا بعد سورۂ ھُمَزہ آتی ہے جس میں جاہلیت کی سرداری کا ایک نمونہ پیش کر کے لوگوں کے سامنے گویا یہ سوال رکھ دیا گیا کہ یہ کردار آخر خسارے کا موجب کیوں نہ ہو؟','سید ابو اعلیٰ مودودیؒ -  تفہیم القرآن');
INSERT INTO "chapters_ur" VALUES(105,'ہاتھی',NULL,' نام : پہلی ہی آیت کے لفظ اَصْحٰبِ الْفِیْل سے ماخوذ ہے۔ زمانۂ نزول : یہ سورۃ بالاتفاق مکّی ہے۔ اور اِس کے تاریخی پَسْ منظر کو اگر نگاہ میں رکھ کر دیکھا جائے تو محسوس ہوتا ہے کہ اِس کا نزول مکّہ ٔ معظمہ کے بھی ابتدائی دور میں ہوا ہوگا۔ تاریخی پسِ منظر : اِس سے پہلے تفسیر سُورۂ بروج حاشیہ ۴ میں ہم بیان کر چکے ہیں کہ ، نَجْر ان میں یمن کے یہودی فرمانروا ذُونُو اس نے پیروانِ مسیح علیہ السلام پر جو ظلم کیا تھا اُس کا بدلہ لینے کے لیے حَبَش کی عیسائی سلطنت نے یمن پر حملہ کر کے حِمیْرَی حکومت کا خاتمہ کر دیا تھا اور سن ۵۲۵ء میں اِس پورے علاقے پر حبشی حکومت قائم ہو گئی تھی۔ یہ ساری کارروائی دراصل قسطنطنیہ کی رومی سلطنت اور حَبَش کی حکومت کے باہم تعاون سے ہوئی تھی، کیونکہ حبشیوں کے پاس اُس زمانے میں کوئی قابل ذکر بحری بیڑا نہ تھا۔ بیڑا رومیوں نے فراہم کیا اور حَبَش نے اپنی ۷۰ ہزار فوج اُسی کے ذریعہ سے یمن کے ساحل پر اتاری ۔ آگے کے معاملات سمجھنے کے لیے یہ بات ابتداء ہی میں جان لینی چاہیے کہ یہ سب کچھ محض مذہبی جذبے سے نہیں ہوا تھا بلکہ اِس کے پیچھے معاشی و سیاسی اغراض بھی کام کر رہی تھیں، بلکہ غالباً وہی اِس کی اصل محرک تھیں اور عیسائی مظلومین کے خون کا انتقام ایک بہانے سے زیادہ کچھ نہ تھا۔ رومی سلطنت جب سے مصر و شام پر قابض ہوئی تھی اُسی وقت سے اُس کی یہ کوشش تھی کہ مشرقی افریقہ ، ہندوستان، انڈونیشیا وغیرہ ممالک اور رومی مقبوضات کے درمیان جس تجارت پر عرب صدیوں سے قابض چلے آرہے تھے ، اُسے عربوں کے قبضے سے نکال کر وہ خود اپنے قبضے میں لے لے ، تا کہ اُس کے مَنافِع پورے کے پورے اُسے حاصل ہوں اور عرب تاجروں کا واسطہ درمیان سے ہٹ جائے۔ اِس مقصد کے لیے سن ۲۴ قبل ِ مسیح میں قیصر آگسٹَس نے ایک بڑی فوج رومی جنرل ایلیَس گالوس (Aelius Gallus ) کی قیادت میں عرب کے مغربی ساحل پر اتار دی تھی تاکہ وہ اُس بحری راستے پر قابض ہو جائے جو جنوبی عرب سے شام کی طرف جاتا تھا۔ (اِس شاہراہ کا نقشہ ہم نے تفہیم القرآن، جلد دوم میں صفجہ ۱۲۲ پر درج کیا ہے)۔ لیکن عرب کے شدید جغرافی حالات نے اس مہم کو ناکام کر دیا۔ اس کے بعد رومی اپنا جنگی بیڑہ بحر احمر میں لے آئے اور انہوں نے عربوں کی اُس تجارت کو ختم کر دیا جو وہ سمندر کے راستے کرتے تھے ، اور صرف بَرّی راستہ اُن کے لیے باقی رہ گیا۔ اِسی بَرّی راستے کو قبضے میں لینے کے لیے اُنہوں نے حبش کی عیسائی حکومت سے گٹھ جوڑ کیا اور بحری بیڑے سے اُس کی مدد کر کے اُس کو یمن پر قابض کرادیا۔ یمن پر جو حبشی فوج حملہ آور ہوئی تھی اس کے متعلق عرب مورخین کے بیانات مختلف ہیں۔ حافظ ابنِ کثیر نے لکھا ہے کہ وہ دو امیروں کی قیادت میں تھی، ایک اَرْیاط، دوسرا اَبْرَھہ ۔ اور محمد بن اسحاق کی روایت ہے کہ اس فوج کا امیر اَرْیاط تھا، اور ابرھہ اُس میں شامل تھا ۔ پھر دونوں اس بات پر متفق ہیں کہ ابرھہ اور اریاط باہم لڑ پڑے، مقابلے میں اَریاط مارا گیا ، ابرھہ ملک پر قابض ہو گیا اور پھر اُس نے شاہِ حبش کو اس بات پر راضی کر لیا کہ وہ اسی کو یمن پر اپنا نائب کر دے۔ اس کے برعکس یونانی اور سُریانی مورخین کا بیان ہے کہ فتحِ یمن کے بعد جب حبشیوں نے مزاحمت کرنے والے یمنی سرداروں کو ایک ایک کر کے قتل کر نا شروع کر دیا تو اُن میں سے ایک سردار السُّمَیْفِع اَشْوَع (جسے یونانی مورخین Esymphaeus لکھتے ہیں) میں نے حبشی فوج نے اُس کے خلاف بغاوت کر دی اور ابرھہ کو اس کی جگہ گورنر بنا دیا ۔ یہ شخص حبش کی بندرگاہ اَدولیس کے ایک یونانی تاجر کا غلا م تھا جو اپنی ہوشیاری سے یمن پر قبضہ کرنے والی حبشی فوج میں بڑا اثر و رسوخ حاصل کر گیا تھا۔ شاہ حبش نے اس کی سرکوبی کے لیے جو فوجیں بھیجیں وہ یا اس سے مل گئیں یا اس نے ان کو شکست دیدی۔ آخر کار شاہ حبش کےت مرنے کے بعد اُس کے جانشین نے اس کو یمن پر اپنا نائب السلطنت تسلیم کر لیا (یونانی مورخین اُس کا نام اَبرامِس Abrames اور سُریانی مورخین ابراھام Abraham لکھتے ہیں۔ ابرھہ غالبًا اسی کا حبشی تلفظ ہے ، کیونکہ عربی میں تو اس کا تلفظ ابراہیم ہے)۔ یہ شخص رفتہ رفتہ یمن کا خودمختار بادشاہ بن گیا ، مگر برائے نام اس نے شاہ حبش کی بالادستی تسلیم کررکھی تھی اور اپنے آپ کو مُفَوَّض المَلِک (نائب بادشاہ) لکھتا تھا۔ اُس نے جو اثر و رسوخ حاصل کر لیا تھا اُس کا اندازہ اِس امر سے کیا جا سکتا ہے کہ جب سن ۵۴۳عیسوی میں وہ سدِّ مارِب کی مَرمَّت سے فارغ ہوا تو اس نے ایک عظیم الشان جشن منایا جس میں قیصر روم ، شاہِ ایران ، شاہ حِیْرَہ اور شاہِ غَسّان کے سُفرا شریک ہوئے۔ اِس کا مفصل تذکرہ اُس کتبے میں درج ہے جو ابرھہ نے سدّ مارب پر لگایا تھا۔ یہ کتبہ آج بھی موجود ہے اور گلیزر (Glaser ) نے اس کو نقل کیا ہے (مزید تفصیلات کے لیے ملاحظہ ہو تفہیم القرآن ، جلد چہارم، تفسیر سورۂ سبا، حاشیہ ۳۷)۔ یمن میں پوری طرح اپنا اقتدار مضبوط کر لینے کے بعد اَبرھہ نے اُس مقصد کے لیے کام شروع کر دیا جو اِس مہم کی ابتدا سے رومی سلطنت اور اُس کے حلیف حبشی عیسائیوں کے پیشِ نظر تھا، یعنی ایک طرف عرب میں عیسائیت پھیلانا اور دوسری طرف اُس تجارت پر قبضہ کرنا جو بلادِ مشرق اور رومی مقبوضات کے درمیان عربوں کے ذریعہ سے ہوتی تھی۔ یہ ضرورت اِس بنا پر اور بڑھ گئی تھی کہ ایران کی ساسانی سلطنت کے ساتھ روم کی کشمکشِ اقتدار نے بلادِ مشرق سے رومی تجارت کے دوسرے تمام راستے بند کر دیے تھے۔ ابرھہ نے اِس مقصد کے لیے یمن کے دارالسلطنت صنعاء میں ایک عظیم الشان کلیسا تعمیر کرایا جس کا ذکر عرب مورخین نے اَلْقَلِیس یا اَلقُلَّیس کے نام سے کیا ہے (یہ یونانی لفظ Ekklesia کا معرّب ہے اور اردو کا لفظ کلیسا بھی اسی یونانی لفظ سے ماخوذ ہے)۔ محمد بن اسحاق کی روایت ہے کہ اس کام کی تکمیل کے بعد اُس نے شاہِ حبش کو لکھا کہ میں عربوں کا حج کعبہ سے اِس کلیسا کی طرف موڑے بغیر نہ رہوں ۔گا ۱؎ ابن کثیر نے لکھا ہے کہ اُس نے یمن میں علیٰ الاعلان اپنے اِس ارادے کا اظہار کیا اور اس کی منادی کرادی۔ اُس کو مکّہ پر حملہ کرنے اور کعبے کے منہدم کر دینے کا بہانہ مل جائے۔ محمد بن اسحاق کا بیان ہے کہ اُس کے اِس اعلان پر غضبناک ہو کر ایک عرب نے کسی نہ کسی طرح کلیسا میں گھس کر رفع حاجت کر ڈالی۔ ابن کثیر کہتے ہیں کہ یہ فعل ایک قریشی نے کیا تھا۔ اور مُقاتِل بن سلیمان کی روایت ہے کہ قریش کے بعض نوجوانوں نے جا کر کلیسا میں آگ لگا دی تھی۔ اِن میں سے کوئی واقعہ بھی اگر پیش آیا ہو تو کوئی قابلِ تعجب امر نہیں ہے ، کیونکہ ابرھہ کا یہ اعلان یقیناً سخت اشتعال انگیز تھا اور قدیم جاہلیت کے دور میں اس پر کسی عرب، یا قریشی کا ، یا چند قریشی نوجوانوں کا مشتعل ہو کر کلیسا کو گندا کر دینا یا اس میں آگ لگا دینا کوئی ناقابل فہم بات نہیں ہے۔ لیکن یہ بھی کچھ بعید نہیں کہ ابرھہ نے خود اپنے کسی آدمی سے خفیہ طور پر ایسی کوئی حرکت کرائی ہو تاکہ اُسے مکّہ پر چڑھائی کرنے کا بہانا مل جائے اور اس طرح وہ قریش کو تباہ اور تمام اہلِ عرب کو مرعوب کر کے اپنے دونوں مقصد حاصل کر لے۔ بہر حال دونوں صورتوں میں سے جو صورت بھی ہو، جب ابرھہ کے پاس یہ رپورٹ پہنچی کہ کعبے کے معتقدین نے اس کے کلیسا کی یہ توہین کی ہے تو اس نے قسم کھائی کہ میں اُس وقت تک چین نہ لوں گا جب تک کعبے کو ڈھا نہ دوں۔ اِس کے بعد وہ سن ۵۷۰ یا ۵۷۱عیسوی میں ۶۰ ہزار فوج اور ۱۳ ہاتھی (اور بروایت بعض ۹ ہاتھی) لے کر مکّہ کی طرف روانہ ہوا۔ راستے میں پہلے یمن کے ایک سردار ذُو نَفْر نے عربوں کا ایک لشکر جمع کر کے اس کی مزاحمت کی ، مگر وہ شکست کھا کر گرفتار ہوگیا۔ پھر خَتْعَم کے علاقے میں ایک عرب سردار نُفَیل بن حبیب خَتْعَی اپنے قبیلے کو لے کر مقابلے پر آیا، مگر وہ بھی شکست کھا کر گرفتار ہو گیا اور اس نے اپنی جان بچانے کے لیے بَدْرَقے کی خدمت انجام دینا قبول کر لیا۔ طائف کے قریب پہنچا تو بنی ثَقِیف نے محسوس کیا کہ اِتنی بڑی طاقت کا وہ مقابلہ نہ کر سکیں گے، اور ان کو خطرہ لاحق ہوا کہ کہیں وہ اُن کے معبود لات کا مندر بھی تباہ نہ کر دے۔ چنانچہ اُن کا سردار مسعود ایک وفد لے کر ابرھہ سے ملا اور اس نے کہا کہ ہمارا بُت کدہ وہ معبد نہیں ہے جسے آپ ڈھانے آئے ہیں ، وہ تو مکّہ میں ہے ، اس لیے آپ ہمارے معبد کو چھوڑ دیں، ہم مکّہ کا راستہ بتانے کے لیے آپ کو بَدْرَقہ فراہم کیے دیتے ہیں۔ ابرھہ نے یہ بات قبول کر لی اور بنی ثَقِیف نے ابورِ غال نامی ایک آدمی کو اس کے ساتھ کر دیا۔ جب مکّہ تین کوس دور رہ گیا تو الُمغَسَّ (یا المُغَسِّ) نامی مقام پر پہنچ کر ابورِغال مر گیا ، اور عرب مدتوں تک اس کی قبر پر سنگ باری کرتے رہے۔ بنی ثقیف کو بھی وہ سالہاسال تک طعنے دیتے رہے کہ اُنہوں نے لات کے مندر کو بچانے کے لیے بیت اللہ پر حملہ کر نے والوں سے تعاون کیا۔ محمد بن اسحاق کی روایت ہے کہ المغمسّ سے ابرھہ نے اپنے مقدمۃ الجیش کو آگے بڑھایا اور وہ اہلِ تِہامَہ اور قریش کے بہت سے مویشی لوٹ لے گیا جن میں رسول اللہ صلی اللہ علیہ وسلم کے دادا عبد المَطَّلِب کے بھی دو سو اونٹ تھے۔ اس کے بعد اس نے اپنے ایک ایلچی کو مکّہ بھیجا اور اس کے ذریعہ سے اہلِ مکّہ کو یہ پیغام دیا کہ میں تم سے لڑنے نہیں آیا ہوں ، بلکہ اِس گھر (کعبہ) کو ڈھانے آیا ہوں۔ اگر تم نہ لڑو تو میں تمہاری جان و مال سے کوئی تعرُّض نہ کروں گا۔ نیز اس نے اپنے ایلچی کو ہدایت کی کہ اہلِ مکّہ اگر بات کرنا چاہیں تو ان کے سردار کو میرے پاس لے آنا۔ مکّے کے سب سے بڑے سردار اُس وقت عبد المطلب تھے۔ ایلچی نے ان سے مل کر ابرھہ کا پیغام پہنچایا۔ اُنہوں نے کہا کہ ہم میں ابرھہ سے لڑنے کی طاقت نہیں ہے۔ یہ اللہ کا گھر ہے، وہ چا ہے گا تو اپنے گھر کو بچا لے گا۔ ایلچی نے کہا کہ آپ میرے ساتھ ابرھہ کے پاس چلیں۔ وہ اس پر راضی ہو گئے اور اس کے ساتھ چلے گئے۔ وہ اِس قدر وجیہ اور شاندار شخص تھے کہ اُن کو دیکھ کر ابرھہ بہت متاثر ہوا اور اپنے تخت سے اُتر کر ان کے پاس آکر بیٹھ گیا۔ پھر پوچھا کہ آپ کیا چاہتے ہیں۔ انہوں نے کہا کہ میرے جو اونٹ پکڑ لیے گئے ہیں وہ مجھے واپس دے دیے جائیں۔ ابرھہ نے کہا کہ آپ کو دیکھ کر تو میں بہت متأ ثر ہوا تھا، مگر آپ کی اِس بات نے آپ کو میری نظر سے گرا دیا کہ آپ اپنے اونٹوں کا مطالبہ کر رہے ہیں اور یہ گھر جو آپ کا اور آپ کے دینِ آبائی کا مرجع ہے ، اِس کے بارے میں کچھ نہیں کہتے۔ انہوں نے کہا میں تو صرف اپنے اونٹوں کا مالک ہوں اور اُنہی کے بارے میں آپ سے درخواست کر رہا ہوں۔ رہا یہ گھر، تو اِس کا ایک ربّ ہے، وہ اِس کی حفاظت خود کر لے گا۔ ابرھہ نے جواب دیا وہ اس کو مجھ سے نہ بچا سکے گا۔ عبد المطلب نے کہا آپ جانیں اور وہ جانے۔ یہ کہہ کر وہ ابرھہ کے پاس سے اُٹھ آئے اور اُس نے اُن کے اونٹ واپس کر دیے۔ ابنِ عباس ؓ کی روایت اِس سے مختلف ہے۔ اُس میں اونٹوں کے مطالبے کا کوئی ذکر نہیں ہے۔ عبد بن حُمَید، ابن المُنْذِر، ابن مَرْدُوْیَہ، حاکم، ابو نُعَیم اور بَیْہَقی نے اُن سے جو روایات نقل کی ہیں اُن میں وہ بیان کرتے ہیں کہ جب ابرھہ الصِّفَاح کے مقام پر پہنچا (جو عَرَفات اور طائف کے پہاڑوں کے درمیان حدودِ حَرَم کے قریب واقع ہے) تو عبد المطّلب خود اُس کے پاس گئے اور اس سے کہا کہ آپ کو یہاں تک آنے کی کیا ضرورت تھی؟ آپ کو اگر کوئی چیز مطلوب تھی تو ہمیں کہلا بھیجتے، ہم خود لے کر آپ کے پاس حاضر ہو جاتے۔ اس نے کہا کہ میں سنا ہے یہ گھر امن کا گھر ہے، میں اس کا امن ختم کرنے آیا ہوں۔ عبد المطلب نے کہا یہ اللہ کا گھر ہے ، آج تک اُس نے کسی کو اِس پر مسلّط نہیں ہونے دیا ہے۔ ابرھہ نے جواب دیا ہم اسے منہدم کیے بغیر نہ پلٹیں گے۔ عبد المطلب نے کہا آپ جو کچھ چاہیں ہم سے لے لیں اور واپس چلے جائیں ۔ مگر ابرھہ نے انکار کر دیا اور عبد المطلب کو پیچھے چھوڑ کر اپنے لشکر کو آگے بڑھنے کا حکم دیا۔ دونوں روایتوں کے اِس اختلاف کو اگر ہم اپنے جگہ رہنے دیں اور کسی کو کسی پر ترجیح نہ دیں، تو اِن میں سے جو صورت بھی پیش آئی ہو، بہر حال یہ امر بالکل واضح ہے کہ مکّہ اور اس کے آ س پاس کے قبائل اتنی بڑی فوج سے لڑ کر کعبے کو بچانے کی طاقت نہ رکھتے تھے۔ اس لیے یہ بالکل قابلِ فہم بات ہے کہ قریش نے اُس کی مزاحمت کی کوئی کوشش نہ کی۔ قریش کے لوگ تو جنگِ اَحزاب کے موقع پر مشرک اور یہودی قبائل کو ساتھ ملا کر زیادہ سے زیادہ دس بارہ ہزار کی جمعیت فراہم کر سکتے تھے۔ وہ ۶۰ ہزار فوج کا مقابلہ کیسے کر سکتے تھے۔ محمد بن اسحاق بیان کرتے ہیں کہ ابرھہ کی لشکر گاہ سے واپس آکر عبد المطلب نے قریش والوں سے کہا کہ اپنے بال بچوں کو لے کر پہاڑوں میں چلے جائیں تاکہ ان کا قتلِ عام نہ ہو جائے ۔ پھر وہ اور قریش کے چند سردار حَرَم میں حاضر ہوئے اور کعبے کے دروازے کا کُنڈا پکڑ کر انہوں نے اللہ تعالیٰ سے دعائیں مانگیں کہ وہ اپنے گھر اور اُس کے خادموں کی حفاظت فرمائے۔ اُس وقت خانۂ کعبہ میں ۳۶۰ بُت موجود تھے، مگر یہ لوگ اُس نازک گھڑی میں اُن سب کو بھول گئے اور انہوں نے صرف اللہ کے آگے دستِ سوال پھیلایا۔ اُن کی جو دعائیں تاریخوں میں منقول ہوئی ہیں ان میں اللہ واحد کے سوا کسی دوسرے کا نام تک نہیں پایا جاتا۔ ابن ہشام نے سیرت میں عبد المطّلب کے جو اشعار نقل کیے ہیں وہ یہ ہیں: لا ھُمّ ان العبد یمنع رحلہ فامنع حِلالکْ خدایا: بندہ اپنے گھر کی حفاظت کرتا ہے ، تو بھی اپنے گھر کی حفاظت فرما لا یغلبنّ صلیبھم و مِحالھم غدوًا مِحالک کل اُن کی صلیب اور اُن کی تدبیر تیری تدبیر کے مقابلے میں غالب نہ آنے پائے ان کنت تارکھم و قِبلتنا فا مر ما بدالکْ اگر تو ان کو اور ہمارے قبیلے کو اپنے حال پر چھوڑ دینا چاہتا ہے تو جو تُو چاہے کر سُہَیلی نے روض الانف میں اس سلسلے کا یہ شعر بھی نقل کیا ہے: وانصر نا علیٰ اٰل الصّلیبِ وعابد یہ الیوم اٰلک صلیب کی آل اور اس کے پرستاروں کے مقابلے میں آج اپنی آل کی مدد فرما یا ربّ لا ارجولھم سوا کا یا ربّ فامنع منہم حِما کا اے میرے ربّ تیرے سوا میں اُن کے مقابلے میں کسی سے امید نہیں رکھتا۔ اے میرے ربّ ان سے اپنے حرم کی حفاظت کر ان عدوّ البیت من عاد اکا امنعھم ان یخربوا قراکا اِس گھر کا دشمن تیرا دشمن ہے ۔ اپنی بستی کو تباہ کرنے سے ان کو روک یہ دعائیں مانگ کر عبد المطلب اور ان کے ساتھی بھی پہاڑوں میں چلے گئے ، اور دوسرے روز ابرھہ مکّے میں داخل ہونے کے لیے آگے بڑھا، مگر اُس کا خاص ہاتھی محمود ، جو آگے آگے تھا، یکایک بیٹھ گیا۔ اس کو بہت تَبَر مارے گئے، آنکَسوں سے کَچوکے دیے گئے، یہاں تک کہ اسے زخمی کر دیا گیا ، مگر وہ نہ ہلا۔ اُسے جنوب، شمال، مشرق کی طرف موڑ کر چلانے کی کوشش کی جاتی تو وہ دوڑنے لگتا، مگر مکّے کی طرف موڑا جاتا تو وہ فوراً بیٹھ جاتا اور کسی طرح آگے بڑھنے کے لیے تیار نہ ہوتا۔ اِتنے میں پرندوں کے جھُنڈ کے جھُنڈ اپنی چونچوں اور پنجوں میں سنگریزے لیے ہوئے آئے اور انہوں نے اِس لشکر پر اُن سنگریزوں کی بارش کر دی۔ جس پر بھی یہ کنکر گرتے اس کا جسم گلنا شروع شروع ہو جاتا۔ محمد بن اسحاق اور عِکْرِمہ کی روایت ہے کہ یہ چیچک کا مرض تھا اور بلادِ عرب میں سب سے پہلے چیچک اسی سال دیکھی گئی۔ ابن عباس ؓ کی روایت ہے کہ جس پر کوئی کنکری گرتی اسے سخت کھُجلی لاحق ہو جاتی اور کھُجاتے ہی جِلد پھٹتی اور گوشت جھڑنا شروع ہو جاتا۔ ابن عباس ؓ کی دوسری روایت یہ ہے کہ گوشت اور خون پانی کی طرح بہنے لگتا اور ہڈیاں نکل آتی تھیں۔ خود ابرھہ کے ساتھ بھی یہی ہوا۔ اُس کا جسم ٹکڑے ٹکڑے ہو کر گر رہا تھا اور جہاں سے کوئی ٹکڑا گرتا وہاں سے پیپ اور لہو بہنے لگتا۔ افراتفری میں اِن لوگوں نے یمن کی طرف بھاگنا شروع کیا۔ نُفَیل بن حبیب خَشْعَمی کو ، جسے یہ لوگ بدرقہ بنا کر بلادِ خَشْعَم سے پکڑ لائے تھے، تلاش کر کے انہوں نے کہا کہ واپسی کا راستہ بتائے ۔ مگر اُس نے صاف انکار کر دیا اور کہا: این المفرّ و الا لٰہ الطالبْ والا شرم المغلوب لیس الغالبْ اب بھاگنے کی جگہ کہاں ہے جبکہ خدا تعاقب کر رہا ہے اور نکٹا (ابرھہ) مغلوب ہے، غالب نہیں ہے۔ اس بھگدڑ میں جگہ جگہ یہ لوگ گِر گِر کر مرتے رہے۔ عطاء بن یَسار کی روایت ہے کہ سب کے سب اُسی وقت ہلاک نہیں ہو گئے، بلکہ کچھ تو وہیں ھلاک ہوئے اور کچھ بھاگتے ہوئے راستے بھر گِرتے چلے گئے۔ ابرھہ بھی بلادِ خَشْعَم پہنچ کر مرا ۲؎ ۔یہ واقعہ مُزْدَلفَہ اور منیٰ کے درمیان وادی مُحَصَّب کے قریب مُحَسِّر کے مقام پر پیش آیا تھا۔ صحیح مسلم اور ابو داؤد کی روایت کے مطابق رسول اللہ صلی اللہ علیہ وسلم کے حجۃ الوداع کا جو قصّہ امام جعفر صادق نے اپنے والد ماجد امام محمد باقر سے اور انہوں نے حضرت جابر بن عبد اللہ سے نقل کیا ہے اس میں وہ بیان فرماتے ہیں کہ رسول اللہ صلی اللہ علیہ وسلم جب مُزْدَلِفَہ سے منیٰ کی طرف چلے تو مُحَسِّر کی وادی میں آپ نے رفتار تیز کر دی۔ امام نَوَوی اس کی شرح کرتے ہوئے لکھتے ہیں کہ اصحاب الفیل کا واقعہ اِسی جگہ پیش آیا تھا، اس لیے سنّت یہی ہے کہ آدمی یہاں سے جلدی گزر جائے۔ موطّا ء میں امام مالک روایت کرتے ہیں کہ حضور ؐ نے فرمایا کہ مزدلفہ پورا کا پورا ٹھیرنے کا مقام ہے، مگر محسّر کی وادی میں نہ ٹھیرا جائے۔ نُفَیل بن حبیب کے جو اشعار ابن اسحاق نے نقل کیے ہیں ان میں وہ اِس واقعہ کا آنکھوں دیکھا حال بیان کرتا ہے: رُدَینۃُ لو رأیتِ ولا تَریہ لدٰی جنب المحصَّب مارأینا اے رُدَینَہ کاش تو دیکھتی، اور تو نہیں دیکھ سکے گی جو کچھ ہم نے وادی محصَّب کے قریب دیکھا حمدتُ اللہ اذا بصرت طیرًا وخفت حجارۃ تلقٰی علینا میں نے اللہ کا شکر کیا جب میں نے پرندوں کو دیکھا اور مجھے ڈر لگ رہا تھا کہ کہیں پتھر ہم پر نہ آپڑیں وکلّ القوم یسأل عن نُفَیل کان علیّ للحبشان دَینا ان لوگوں میں سے ہر ایک نُفَیل کو ڈھونڈ رہا تھا، گویا کہ میرے اوپر حبشیوں کا کوئی قرض آتا تھا یہ اتنا بڑا واقعہ تھا جس کی تمام عرب میں شہرت ہو گئی اور اس پر بہت سے شعراء نے قصائد کہے۔ ان قصائد میں یہ بات بالکل نمایاں ہے کہ سب نے اسے اللہ تعالیٰ کی قدرت کا اعجاز قرار دیا اور کہیں اشارۃً و کنایۃً بھی یہ نہیں کہا کہ اس میں اُن بُتوں کا بھی کوئی دخل تھا جو کعبہ میں پوجے جاتے تھے۔ مثال کے طور پر عبد اللہ ابن الزِّبَعْریٰ کہتا ہے: ستّون الفالم یوْ بوا ارضھم ولم یعش بعد الایاب سقیمہا ۶۰ ہزار تھے جو اپنی سرزمین کی طرف واپس نہ جا سکے اور نہ واپس ہونے کے بعد ان کا بیمار (ابرھہ) زندہ رہا کانت بھا عاد و جرھم قبلہم واللہ من فوق العباد یقیمہا یہاں ان سے پہلے عاد اور جُرْھُم تھے۔ اور اللہ بندوں کے اوپر موجود ہے جو اُسے قائم رکھے ہوئے ہےابو قَیس بن اَسْلَت کہتا ہے: فقوموا فصلّوا ربّکم و تمسّحوا بارکان ھٰذا البیت بین الاخاشب اٹھو اور اپنے رب کی عبادت کرو اور مکّہ و منیٰ کی پہاڑیوں کے درمیان بیت اللہ کے کونوں کو مسح کرو فلما اتاکم نصر ذی العرش ردّھم جنود الملیک بین ساف وحاصب جب عرش والے کی مدد تمہیں پہنچی تو اس بادشاہ کے لشکروں نے ان لوگوں کو اِس حال میں پھیر دیا کہ کوئی خاک میں پڑا تھا اور کوئی سنگسار کیا ہوا تھا یہی نہیں بلکہ حضرت اُمّ ھانی اور حضرت زُبَیر بن العَّوام کی روایت ہے کہ رسول اللہ صلی اللہ علیہ وسلم نے فرمایا، قریش نے ۱۰ سال (اور بروایت ِ بعض سات سال) تک اللہ وحدہٗ لاشریک کے سوا کسی کی عبادت نہ کی۔ امّ ھانی کی روایت امام بخاری نے اپنی تاریخ میں اور طَبَرانی ، حاکم ، ابن مَرْدُوْیَہ اور بَیْہَقِی نے اپنی کتب حدیث میں نقل کی ہے ۔ حضرت زُبَیر کا بیان طَبرَانی اور ابن مردویہ اور ابن عساکر نے روایت کیا ہے۔ اور اِس کی تائید ِ مزید حضرت سعید بن المُسَیَّب کی اُس مُرْسَل روایت سے ہوتی ہے جو خطیب بغدادی نے اپنی تاریخ میں درج کی ہے۔ جس سال یہ واقعہ پیش آیا، اہلِ عرب اُسے عامُ الفِیل (ہاتھیوں کا سال) کہتے ہیں ، اور اُسی سال رسول اللہ صلی اللہ علیہ وسلم کی ولادتِ مبارکہ ہوئی۔ محدّثین اور مورخین کا اس بات پر قریب قریب اتفاق ہے کہ اصحابُ الفیل کا واقعہ محرم میں پیش آیا تھا اور حضورؐ کی ولادت ربیع الاول میں ہوئی تھی۔ اکثریت یہ کہتی ہے کہ آپؐ کی ولادت واقعہ ٔ فیل کے ۵۰ دن بعد ہوئی۔ مقصودِ کلام : جو تاریخی تفصیلات اوپر درج کی گئی ہیں ان کو نگاہ میں رکھ کر سورۂ فیل پرغور کیا جائے تو یہ بات چھی طرح سمجھ میں آجاتی ہے کہ اِس سورۂ میں اِس قدر اختصار کے ساتھ صرف اصحاب الفیل پر اللہ تعالیٰ کے عذاب کا ذکر کر دینے پر کیوں اکتفا کیا گیاہے۔ واقعہ کچھ بہت پرانا نہ تھا ۔ مکّے کا بچہ بچہ اس کو جانتا تھا۔ عرب کے لوگ عام طور پر اس سے واقف تھے۔ تمام اہلِ عرب اِس بات کے قائل تھے کہ ابرھہ کے اِس حملے سے کعبے کی حفاظت کسی دیوی یا دیوتا نے نہیں بلکہ اللہ تعالیٰ نے کی تھی۔ اللہ ہی سے قریش کے سرداروں نے مدد کے لیے دعائیں مانگی تھیں۔ اور چند سال تک قریش کے لوگ اِس واقعہ سے اس قدر متأثر رہے تھے کہ اُنہوں نے اللہ کے سوا کسی کی عبادت نہ کی تھی۔ اس لیے سورۂ فیل میں اِن تفصیلات کے ذکر کی حاجت نہ تھی، بلکہ صرف اس واقعے کو یاد دلانا کافی تھا ، تاکہ قریش کے لوگ خصوصًا ، اور اہلِ عرب عمومًا، اپنے دلوں میں اس بات پر غور کریں کہ محمد صلی اللہ علیہ وسلم جس چیز کی طرف دعوت دے رہےہیں وہ آخر اِس کے سوا اور کیا ہے کہ تمام دوسرے معبودوں کو چھوڑ کر صرف اللہ وحدہ لاشریک کی عبادت کی جائے ۔ نیز وہ یہ بھی سوچ لیں کہ اگر اِس دعوتِ حق کو دبانے کے لیے اُنہوں نے زور زبردستی سے کام لیا تو جس خدا نے اصحاب الفیل کا تہس نہس کیا تھا اسی کے غضب میں وہ گرفتار ہوں گے۔ ؎ یمن پر سیاسی اقتدار حاصل کر نے کے بعد عیسائیوں کی مسلسل یہ کوشش رہی کہ کعبہ کے مقابلے میں ایک دوسرا کعبہ بنائیں اور عرب میں اُس کی مرکزیت قائم کر دیں۔ چنانچہ انہوں نے نَجران میں بھی ایک کعبہ بنا یا تھا جس کا ذکر ہم تفسیر سورۂ بروج حاشیہ ۴ میں کر چکے ہیں واپس ۲؎ اللہ تعالیٰ نے حبشیوں کو صرف یہی سزا دینے پر اکتفا نہ کیا، بلکہ تین چار سال کے اندر یمن سے حبشی اقتدار ہمیشہ کے لیے ختم کر دیا۔ تاریخ سے معلوم ہوتا ہے کہ واقعۂ فیل کے بعد یمن میں اُن کی طاقت بالکل ٹوٹ گئے، جگہ جگہ یمنی سردار علم بغاوت لے کر اُٹھ کھڑے ہوئے، پھر ایک یمنی سردار سیف بن ذِی یَزََن نے شاہ ایران سے فوجی مدد طلب کر لی اور ایران کی صرف ایک ہزار فوج جو چھ جہازوں کے ساتھ آئی تھی، حبشی حکومت کا خاتمہ کردینے کے لیے کافی ہو گئی۔ یہ سن ۵۷۵ عیسوی کا واقعہ ہے۔ واپس ','سید ابو اعلیٰ مودودیؒ -  تفہیم القرآن');
INSERT INTO "chapters_ur" VALUES(106,'قریش',NULL,' نام : پہلی ہی آیت کے لفظ قریش کو اس سورہ کا نام قرار دیا گیا ہے۔ زمانۂ نزول : اگرچہ ضُحّاک اور کَلْبِی نے اِس کو مدنی کہا ہے، لیکن مفسّرین کی عظیم اکثریت اس کے مکّی ہونے پر متفق ہے، اور اس کے مکّی ہونے کی کھلی شہادت خود اِس سورہ کے الفاظ رَبَّ ھٰذَا الْبَیْتِ (اس گھر کے ربّ) میں موجود ہے۔ اگر یہ مدینہ میں نازل ہوتی تو خانہ کعبہ کے لیے ، "اِس گھر" کے الفاظ کیسے موزوں ہو سکتے تھے؟ بلکہ اِس مضمون کا سورۂ فیل کے مضمون سے اتنا گہرا تعلق ہے کہ غالبًا اِس کا نزول اُس کے متصلاً بعد ہی ہوا ہو گا۔ دونوں دراصل ایک ہی سورۃ ہیں۔ اِس خیال کو تقویت اِن روایات کی بنا پر ملی ہے کہ حضرت اُبَیّ بن کَعْب کے مُصْحَف میں یہ دونوں ایک ساتھ لکھی ہوئی تھیں اور درمیان میں بسم اللہ مرقوم نہ تھی۔ نیز یہ کہ حضرت عمرؓ نے ایک مرتبہ کسی فصل کے بغیر اِن دونوں کو ملا کر نماز میں پڑھا تھا۔ لیکن یہ رائے اِس وجہ سے قابلِ قبول نہیں کہ صحابۂ کرام کی عظیم تعداد کے تعاون سے سیّدنا عثمان رضی اللہ عنہ نے قرآن مجید کے جو نسخے سرکاری طور پر لکھوا کر بلادِ اسلام کے مراکز میں بھجوائے تھے ان میں دونوں کے درمیان بسم اللہ درج تھی ، اور اُس وقت سے آج تک تمام دنیا کے مَصاحِف میں یہ الگ الگ سورتوں کی حیثیت ہی سے لکھی جاتی رہی ہیں۔ مزید برآں دونوں سورتوں کا اندازِ بیان ایک دوسرے سے اِس قدر مختلف ہے کہ یہ عَلانیہ دو الگ سورتیں نظر آتی ہیں۔ تاریخی پسِ منظر : اِس سُورۃ کو ٹھیک ٹھیک سمجھنے کے لیے ضروری ہے کہ اُس تاریخی پَسْ منظر کو نگاہ میں رکھا جائے جس سے اِس کے مضمون اور سورۂ فیل کے مضمون کا گہرا تعلق ہے۔ قریش کا قبیلہ نبی صلی اللہ علیہ وسلم کے جدِّ اعلیٰ قُصَیّ بن کِلاب کے زمانے تک حجاز میں منتشر تھا ۔ سب سے پہلے قُضَیّ نے اُس کے مکّے میں جمع کیا اور بیت اللہ کی تَولِیَت اِس قبیلے کے ہاتھ میں آگئی۔ اِسی بنا پر قُصَیّ کو مُجَمِعّ (جمع کرنے والے) کا لقب دیا گیا۔ اِس شخص نے اپنے اعلیٰ درجہ کے تدبُّر سے مکّہ میں ایک شہر ریاست کی بنیاد رکھی ، اور جملہ اطرافِ عرب سے آنے والے حاجیوں کی خدم کا بہترین انتظام کیا جس کی بدولت رفتہ رفتہ عرب کے تمام قبائل اور تمام علاقوں میں قریش کا اثر و رسوخ قائم ہو تا چلا گیا ۔ قُصَیّ کے بعد اس کے بیٹوں عبد مَناف اور عبد الدّار کے درمیان مکّہ کی ریاست کے مناصِب تقسیم ہوگئے، مگر دونوں میں سے عبد مناف کو اپنے باپ ہی کے زمانے میں زیادہ ناموری حاصل ہو چکی تھی اور عرب میں اُس کا شرف تسلیم کیا جانے لگا تھا۔ عبد مناف کے چار بیٹے تھے۔ ہاشِم، عبدِ شمس، مُطَّلِب اور نَوفَل۔ ان میں سے ہاشِم عبد المطَّلِب کے والد اور رسول اللہ صلی اللہ علیہ وسلم کے پردادا کو سب سے پہلے یہ خیال پیدا ہوا کہ اُس بین الاقوامی تجارت میں حصہ لیا جائے جو عرب کے راستے بلادِ مشرق اور شام و مصر کے درمیان ہوتی تھی ، اور ساتھ ساتھ اہلِ عرب کی ضروریات کا سامان بھی خرید کر لایا جائے تا کہ راستہ کے قبائل اُن سے مال خریدیں ، اور مکّے کی منڈی میں اندرونِ ملک کے تُجّار خریداری کے لیے آنے لگیں۔ یہ وہ زمانہ تھا جب ایران کی ساسانی حکومت اُس بین الاقوامی تجارت پر اپنا تسلُّط قائم کر چکی تھی جو شمالی علاقوں اور خلیج فارس کے راستوں سے رومی سلطنت اور بلادِ مشرق کے درمیان ہوتی تھی۔ اس لیے جنوبی عرب سے بحر احمر کے ساحل کے ساتھ ساتھ جو تجارتی راستہ شام و مصر کی طرف جاتا تھا اُس کا کاروبار بہت چمک اُٹھا تھا۔ دوسرے عربی قافلوں کی بہ نسبت قریش کو یہ سہولت حاصل تھی کہ راستے کے تمام قبائل بیتُ اللہ کے خُدّام ہونے کی حیثیت سے ان کا احترام کرتے تھے۔ حج کے زمانے میں نہایت فیّاضی کے ساتھ حاجیوں کی جو خدمت قریش کے لوگ کرتے تھے اس کی بنا پر سب اُن کے احسان مند تھے۔ اُنہیں اِس امر کا کوی خطرہ نہ تھا کہ راستے میں کہیں اُن کے قافلوں پر ڈاکہ مارا جائے گا۔ راستے کے قبائل اُن سے رھگذر کے وہ بھاری ٹیکس بھی وصول نہ کرسکتے تھے جو دوسرے قافلوں سے طلب کیا جاتا تھا۔ ہاشِم نے اِنہی تمام پہلووں کو دیکھ کر تجارت کی اسکیم بنائی اور اپنی اِس اسکیم میں اپنے باقی تینوں بھائیوں کو شامل کیا۔ شام کے غَسّانی بادشاہ سے ہاشم نے ، حبش کے بادشاہ سے عبدِ شمس نے ، یمنی امراء سے مُطَّلِب نے اور عراق و فارس ی حکومتوں سے نَوفَل نے تجارتی مراعات حاصلی کیں۔ اِس طرح اِن لوگوں کی تجارت بڑی تیزی سے ترقی کرتی چلی گئی۔ اِسی بنا پر یہ چاروں بھائی مُتَّجِرین (تجارت پیشہ) کے نام سے مشہور ہو گئے، اور جو روابط انہوں نے گردو پیش کے قبائل اور ریاستوں سے قائم کیے تھے اُن کی بنا پر اِن کو اَصحابُ الْاِیْلاف بھی کہا جاتا ہے جِس کے لفظی معنی "اُلفت پیدا کرنے والوں" کے ہیں۔ اس کاروبار کی وجہ سے قریش کے لوگوں کو شام، مصر ، عراق، ایران، یمن اور حبش کے ممالک سے تعلقات کے وہ مواقع حاصل ہوئے، اور مختلف ملکوں کی ثقافت و تہذیب سے براہِ راست سابقہ پیش آنے کے باعث اُن کا معیارِ دانش و بینش اتنا بلند ہوتا چلا گیا کہ عرب کا کوئی دوسرا قبیلہ اُن کی ٹکّر کا نہ رہا۔ مال و دولت کے اعتبار سے بھی وہ عرب میں سب پر فائق ہوگئے اور مکّہ جزیرۃ العرب کا سب سے زیادہ اہم تجارتی مرکز بن گیا۔ اِن بین الاقوامی تعلقات کا ایک بڑا فائدہ یہ بھی ہوا کہ عراق سے یہ لوگ وہ رسم الخط لے کر آئے جو بعد میں قرآن مجید لکھنے کے لیے استعمال ہوا۔ عرب کے کسی دوسرے قبیلے میں اتنے پڑھے لکھے لوگ نہ تھے جتنے قریش میں تھے۔ انہی وجوہ سے نبی صلی اللہ علیہ وسلم نے فرمایا تھا کہ قریش قادۃ الناس " قریش لوگوں کے لیڈر ہیں" (مُسند احمد، مرویّات عَمرْ وبن العاص)۔ اور حضرت علیؓ کی روایت بَیْہقَی میں ہے کہ حضور ؐ نے فرمایا کان ھٰذا الامر فی حِمْیَرَ فنزعہ اللہ منہم وجعلہ فی قریش " پہلے عرب کی سرداری قبیلہ ٔ حِمیْر والوں کو حاصل تھی، پھر اللہ تعالیٰ نے وہ ان سے سلب کر کے قریش کو دے دی"۔ قریش اِسی طرح ترقی پر ترقی کرتے چلے جا رہے تھے کہ مکّہ پر ابرھہ کی چڑھائی کا واقعہ پیش آگیا۔ اگر اُس وقت اَبرھہ اِس شہرِ مقدّس کو فتح کر نے اور کعبے کو ڈھادیے میں کامیاب ہو جاتا تو عرب میں قریش ہی کی نہیں، خود کعبہ کی دھاک بھی ختم ہو جاتی ۔ زمانۂ جاہلیت کے عرب کا یہ عقیدہ متزلزل ہو جاتا کہ یہ گھر واقعی بیت الہ ہے۔ قریش کو اس گھر کے خادم ہونے کی حیثیت سے جو احترام پورے ملک میں حاصل تھا وہ یک لخت ختم ہو جاتا ۔ مکّہ تک حبشیوں کی پیش قدمی کے بعد رومی سلطنت آگے بڑھ کر شام اور مکّہ کے درمیان کا تجارتی راستہ بھی اپنے قبضے میں لے لیتی۔ اور قریش اُس سے زیادہ خستہ حالی میں مبتلا ہو جاتے جس میں وہ قُصَیّ بن کِلاب سے پہلے مبتلا تھے۔ لیکن جب اللہ تعالیٰ نے اپنی قدرت کا یہ کرشمہ دکھایا کہ پرندوں کے لشکروں نے سنگریزے مار مار کر ابرھہ کی لائی ہوئی ۶۰ ہزار حبشی فوج کو تباہ و برباد کر دیا ، اور مکّہ سے یمن تک سارے راستے میں جگہ جگہ اِس تباہ شدہ فوج کے آدمی گِر گِر کر مرتے چلے گئے تو کعبہ کے بیتُ اللہ ہونے پر تمام اہلِ عرب کا ایمان پہلے سے بدرجہا زیادہ مضبوط ہو گیا ، اور اُس کے ساتھ قریش کی دھاک بھی ملک بھر میں پہلے سے زیادہ قائم ہوگئی۔ اب عربوں کو یقین ہوگیا کہ اِن لوگوں پر اللہ کا فضلِ خاص ہے۔ وہ بے کھٹکے عرب کے ہر حصّے میں جاتے اور اپنے تجارتی قافلے لے کر ہر علاقے سے گزرتے۔ کسی کی یہ جرأت نہ تھی کہ اُن کو چھیڑتا۔ اُنہیں چھیڑنا تو درکنار، اُن کی امان میں کوئی غیر قریشی بھی ہوتا تو اُس سے کوئی تعّرُض نہ کیا جاتا تھا۔ مقصودِ کلام : نبی صلی اللہ علیہ وسلم کی بِعثت کے زمانہ میں یہ حالات چونکہ سب ہی کو معلوم تھے ، اِس لیے اُن کے ذکر کی حاجت نہ تھی۔ یہی وجہ ہے کہ اِس سورۃ کے چار مختضر فقروں میں قریش سے صرف اِتنی بات کہنے پر اکتفا کیا گیا کہ جب تم خود اِس گھر (خانہ ٔ کعبہ) کو بُتوں کا نہیں بلکہ اللہ کا گھر مانتے ہو، اور جب تمہیں اچھی طرح معلوم ہے کہ وہ اللہ ہی ہے جس نے تمہیں اِس گھر کےطفیل یہ امن عطا کیا ، تمہاری تجارتوں کو یہ فروغ بخشا، اور تمہیں فاقہ زدگی سے بچا کر یہ خوشحالی نصیب فرمائی، تو تمہیں اُسی کی عبادت کرنی چاہیے۔ ','سید ابو اعلیٰ مودودیؒ -  تفہیم القرآن');
INSERT INTO "chapters_ur" VALUES(107,'تھوڑی سی چیز',NULL,' نام : آخری آیت کے آخرلفظ الماعون کو اس سورہ کا نام قرار دیا گیا ہے۔ زمانۂ نزول : ابنِ مَرْدُوْیَہ نے ابن عباس اور ابن الزُّبَیر رضی اللہ عنہما کا قول نقل کیاہے کہ یہ سورہ مکّی ہے ، اور یہی قول عطاء اور جابر کا بھی ہے ۔ لیکن ابو حَیان نے البحر المُحِیط میں ابن عباس اور قَتادہ اور ضحّاک کا یہ قول نقل کیا ہے کہ یہ مدینہ میں نازل ہوئی ہے۔ ہمارے نزدیک خود اِس سورہ کے اندر ایک داخلی شہادت ایسی موجود ہے جو اس کے مدنی ہونے پر دلالت کرتی ہے ، اور وہ یہ ہے کہ اِس میں اُن نماز پڑھنے والوں کو تباہی کی وعید سُنائی گئی ہے جو اپنی نمازوں سے غفلت برتتے اور دکھاوے کے لیے نماز پڑھتے ہیں ۔ منافقین کی یہ قسم مدینے ہی میں پائی جاتی تھی ، کیونکہ وہیں اسلام اور اہلِ اسلام کو یہ قوّت حاصل ہوئی تھی کہ بہت سے لوگوں کو مصلحۃً ایمان لانا پڑا تھا اور وہ مجبوراً مسجد میں آتے تھے، جماعت میں شریک ہوتے تھے اور دکھاوے کی نمازیں پڑھتے تھے، تاکہ اُنہیں مسلمانوں میں شمار کیا جائے ، اس کے برعکس مکّے میں ایسے حالات سِرے سے موجود ہی نہ تھے کہ وہاں کسی کو دکھاوے کی نماز پڑھنی پڑتی۔ وہاں تو اہلِ ایمان کے لیے نمازِ با جماعت کا اہتمام بھی مشکل تھا۔ اُن کو چھُپ چھُپ کر نما ز پڑھنی پڑتی تھی اور کوئی عَلانیہ پڑھتا تھا تو جان پر کھیل کر پڑھتا تھا۔ منافقین کی جو قسم وہاں پائی جاتی تھی وہ ریاکارانہ ایمان لانے اور دکھاوے کی نمازیں پڑھنے والوں کی نہیں، بلکہ اُن لوگوں کی تھی جو رسول اللہ صلی اللہ صلی اللہ علیہ وسلم کے برسرحق ہونے کو جان اور مان گئے تھے ، مگر اُن میں سے کوئی اپنی ریاست و وجاہت اور مشیخت کو برقرار رکھنے کی خاطر اسلام قبول کرنے سے گریز کر رہا تھا اور کوئی یہ خطرہ مول لینے کے لیے تیار نہ تھا کہ مسلمان ہو کر اُن مصائب میں مبتلا ہو جائے جن میں وہ ایمان لانے والوں کو اپنی آنکھوں کے سامنے مبتلا دیکھ رہا تھا۔ مکّی دور کے منافقین کی یہ حالت سورۂ عنکبوت ، آیات ۱۱-۱۰ میں بیان کی گئی ہے (تشریح کے لیے ملاحظہ ہو تفہیم القرآن، جلد سوم، العنکبوت، حواشی ۱۳ تا ۱۶)۔ موضوع اور مضمون: اس کا موضوع یہ بتاتا ہے کہ آخرت پر ایمان نہ لانا انسان کے اندر کس قسم کے ا خلاق پیدا کرتا ہے۔ آیت ۲ اور ۳ میں اُن کفار کی حالت بیان کی گئی ہے جو عَلانیہ آخرت کو جھٹلاتے ہیں۔ اور آخری چار آیتوں میں اُن منافقین کا حال بیان کیا گیا ہے جو بظاہر مسلمان ہیں ، مگر دل میں آخرت اور اُس کی جزا و سزا اور اُس کے ثواب و عِقاب کا کوئی تصور نہیں رکھتے۔ مجموعی طور پر دونوں قسم کے گروہوں کے طرزِ عمل کو بیان کرنے سے مقصود یہ حقیقت لوگوں کے ذہن نشین کرنا ہے کہ انسان کے اندر ایک مضبوط اور مستحکم پاکیزہ کردار عقیدۂ آخرت کے بغیر پیدا نہیں ہو سکتا۔ ','سید ابو اعلیٰ مودودیؒ -  تفہیم القرآن');
INSERT INTO "chapters_ur" VALUES(108,'جنت کی نہر',NULL,' نام: اِنَّآ اَعْطَیْنٰکَ الْکَوْثَرَ کے لفظ الکوثر کو اس کا نام قرار دیا گیا ہے۔ زمانۂ نزول: ابن مَرْدُوْیَہ نے حضرت عبد اللہ بن عباسؓ ، حضرت عبداللہ بن الزبیرؓ اور حضرت عائشہؓ صدیقہ سے نقل کیا ہے کہ یہ سورۃ مکّی ہے ، کَلْبی اور مُقاتِل بھی اسے مکّی کہتے ہیں، اور جمہور مفسّرین کا قول بھی یہی ہے۔ لیکن حضرت حسن بصری، عِکْرِمَہ ، مجاہداور قَتادہ اِس کو مدنی قرار دیتے ہیں، امام سیوطی نے اِتْقان میں اِسی قول کو صحیح ٹھیرایا ہے ، اور امام نَوَوِی نے شرح مسلم میں اِسی کو ترجیح دی ہے۔ وجہ اِس کی وہ روایت ہے جو امام احمد، مسلم ، ابوداؤد، نَسائی، ابن ابی شَیْبَہ، ابن المُنذِر، ابن مردویہ اور بیہقی وغیرہ محدّثین نے حضرت اَنَس بن مالک سے نقل کی ہے کہ حضورؐ ہمارے درمیان تشریف فرما تھے۔ اتنے میں آپ کو کچھ اُونگھ سی طاری ہوئی، پھر آپ نےمُسکراتے ہوئے سرِ مبارک اُٹھایا۔ بعض روایات میں ہے کہ لوگوں نے پوچھا آپ کس بات پر تبسم فرما رہے ہیں؟ اور بعض میں ہے کہ آپ نے خود لوگوں سے فرمایا اِس وقت میرے اوپر ایک سورۃ نازل ہوئی ہے۔ پھر بسم اللہ الرحمٰن الرحیم پڑھ کر سورۂ کوثر پڑھی۔ اس کے بعد آپ نے پوچھا جانتے ہو کہ کوثر کیا ہے؟ لوگوں نے عرض کیا کہ اللہ اور اس کے رسول کو زیادہ معلوم ہے۔ فرمایا وہ ایک نہر ہے جو میرے ربّ نے مجھے جنت میں عطا کی ہے (اس کی تفصیل آگے ”کوثر“ کی تشریح میں آرہی ہے)۔ اِس روایت سے اِس سورہ کے مدنی ہونے پر اِس وجہ سے استدلال کیا گیا ہے کہ حضرت اَنَس ؓ مکّہ میں نہیں بلکہ مدینے میں تھے، اور اُن کا یہ کہنا کہ ہماری موجودگی میں یہ سورۃ نازل ہوئی، اس بات ی دلیل ہے کہ یہ مدنی ہے۔ مگر اول تو اِنہی حضرت انس سے امام احمد، بخاری، مسلم، ابو داؤد، تِرْمِذی، اور ابن جریر نے یہ روایات نقل کی ہیں کہ جنت کی یہ نہر (کوثر) رسول اللہ صلی اللہ علیہ وسلم کو معراج میں دکھائی جا چکی تھی، اور سب کو معلوم ہے کہ معراج ہجرت سے پہلے مکّہ میں ہوئی تھی۔ دوسرے، جب معراج میں آپ کو اللہ تعالیٰ کے اِس عطیہ کی نہ صرف خبر دی جا چکی تھی بلکہ اس کا مشاہدہ بھی کرا دیا گیا تھا تو کوئی وجہ نہ تھی کہ حضور کو اُس خوشخبری دینے کے لیے مدینہ طیّبہ میں سورۂ کوثر نازل کی جاتی۔ تیسرے، اگر صحابہ کے ایک مجمع میں حضور ؐ نے خود سُورۂ کوثر کے نزول کی وہ خبر دی ہوتی جو حضرت اَنَس ؓ کی مذکورۂ بالا روایت میں بیان ہوئی ہے اور اُس کا مطلب یہ ہوتا کہ پہلی مرتبہ یہ سورۃ اِسی وقت نازل ہوئی ہے ، تو کس طرح ممکن تھا کہ حضرت عائشہ ، حضرت عبد اللہ بن عباس اور حضرت عبد اللہ بن زبیر جیسے باخبر صحابہ اِس سورۃ کو مکّی قرار دیتے اور جمہورِ مفسّرین اس کے مکّی ہونے کے قائی ہو جاتے؟ اس معاملہ پر غور کیا جائے تو حضرت انس کی روایت میں یہ خلاصاف محسوس ہوتا ہے کہ اپس میں یہ تفصیل بیان نہیں ہوئی ہے کہ جس مجلس میں حضور ؐ نے بات ارشاد فرمائی تھی اُس میں پہلے سے کیا گفتگو چل رہی تھی۔ ممکن ہے کہ اُس وقت حضور ؐ کسی مسئلے پر کچھ ارشاد فرما رہے ہوں ، اُس کے دوران میں وحی کے ذریعے سے آپ کو مطلع کیا گیا ہو کہ اِس مسئلے پر سورۂ کوثر سے روشنی پڑتی ہے ، اور آپ نے اِسی بات کا ذکر یوں فرمایا ہو کہ مجھ پر یہ سورۃ نازل ہوئی ہے۔ اِس قسم کے واقعات متعدد مواقع پر پیش آئے ہیں جن کی بنا مفسّرین نے بعض آیات کے متعلق کہا ہے کہ وہ دو مرتبہ نازل ہوئی ہیں اس دوسرے نزول کا مطلب دراصل یہ ہوتا ہے کہ آیت تو پہلے نازل ہو چکی تھی، مگر دوسری بار کسی موقع پر حضورؐ کو بذریعۂ وحی اُسی آیت کی طرف توجہ دلائی گئی۔ ایسی روایات میں کسی آیت کے نزول کا ذکر یہ فیصلہ کرنے کے لیے کافی نہیں ہوتا کہ وہ مکّی ہے یا مدنی، اور اس کا اصل نزول فی الواقع کس زمانے میں ہوا تھا۔ تاریخی پَسْ منظر : اِس سے پہلے سورۂ ضُحٰی اور سورۂ الم نشرح میں آپ دیکھ چکے ہیں کہ نبوّت کے ابتدائی دور میں جب رسول اللہ صلی اللہ علیہ وسلم شدید ترین مشکلات سے گزر رہے تھے ، پوری قوم دشمنی پر تلی ہوئی تھی ، مزاحمتوں کے پہاڑ راستے میں حائل تھے، مخالفت کا طوفان برپا تھا، اور حضورؐ اور آپ کے چند مٹھی بھر ساتھیوں کو دور دور تک کہیں کامیابی کے آثار نظر نہیں آتے تھے، اُس وقت آپ کو تسلی دینے اور آپ کی ہمت بندھانے کے لیے اللہ تعالیٰ نے متعدد آیات نازل فرمائیں۔ سُورۂ ضحٰی میں فرمایا وَلَلْاٰخِرَۃُ خَیْرُ لَّکَ مِنَ الْاُوْلیٰo وَلَسَوْفَ یُعْطِیْکَ رَبُّکَ فَتَرْضٰیo ”اور یقینًا تمہارے لیے بعد کا دور (یعنی ہر بعد کا دور) پہلے دور سے بہتر ہے اور عنقریب تمہارا رب تمہیں وہ کچھ دے گا جس سے تم خوش ہو جاؤ گے“۔ اور الم نشرح میں فرمایا کہ وَرَفَعْنَا لَکَ ذِکْرَکَ ”اور ہم نے تمہارا آوازہ بلند کردیا“۔ یعنی دشمن تمہیں ملک بھر میں بدنام کرتے پھر رہے ہیں مگر ہم نے اُن کے علیٰ الرَّغْم تمہارا نام روشن کر نے اور تمہیں ناموَری عطا کرنے کا سامان کر دیا ہے۔ اور فَاِنَّ مَعَ الْعُسْرِ یُسْرًا o اِنَّ مَعَ الْعُسْرِ یُسْرًا o ”پس حقیقت یہ ہے کہ تنگی کے ساتھ فراخی بھی ہے، یقینًا تنگی کے ساتھ فراخی بھی ہے“۔ یعنی اِس وقت حالات کی سختیوں سے پریشان نہ ہو ، عنقریب یہ مصائب کا دور ختم ہونے والا ہے اور کامیابیوں کا دور آنے ہی والا ہے۔ ایسے ہی حالات تھے جن میں سورۂ کوثر نازل کر کے اللہ تعالیٰ نے حضورؐ کو تسلی بھی دی اور آپ کے مخالفین کے تباہ و برباد ہونے کی پیشنگوئی بھی فرمائی۔ قریش کے کفّار کہتے تھے کہ محمد (صلی اللہ علیہ وسلم) ساری قوم سے کٹ گئے ہیں اور اُن کی حیثیت ایک بے کس اور بے یار و مددگار انسان کی سی ہو گئی ہے۔ عِکْرِمَہ کی روایت ہے کہ جب حضورؐ نبی بنائے گئے اور آپ نے قریش کو اسلام کی دعوت دینی شروع کی تو قریش کے لوگ کہنے لگے بَتِر محمدٌ مِنّا (ابن جریر) یعنی محمدؐ اپنی قوم سے کٹ کر ایسے ہو گئے ہیں جیسے کوئی درخت اپنی جڑ سے کٹ گیا ہو اور متوقَّع یہی ہو کہ کچھ مدت بعد وہ سُوکھ کر پیوند خاک ہو جائے گا۔ محمد بن اسحاق کہتے ہیں کہ مکّہ کے سردار عاص بن وائل سَہْمِی کے سامنے جب رسول اللہ صلی اللہ علیہ وسلم کا ذکر کیا جاتا تو وہ کہتا:”اجی چھوڑو اُنہیں، وہ تو ایک ابتر (جڑکٹے) آدمی ہیں، ان کو کوئی اولادِ نرینہ نہیں، مر جائیں گے تو کوئی ان کا نام لیوا نہ ہو گا“۔ شِمر بن عطیہ کہ بیان ہے کہ عُقْبَہ بن ابی مُعَیط بھی ایسی ہی باتیں حضورؐ کے متعلق کہا کرتا تھا (ابن جریر)۔ ابن عباسؓ کی روایت ہے کہ ایک دفعہ کعب بن اشرف(مدینہ کا یہودی سردار) مکہ آیا تو قریش کے سرداروں نے اس سے کہا الا ترٰی الیٰ ھٰذا الصبّی المنبتر من قومہ یزعم انہ خیر منّا و نحن اھل الحجیج و اھل السدانۃ واھل السقایۃ۔ ”بھلا دیکھو تو سہی اِس لڑکے کو جو اپنی قوم سے کٹ گیا ہے اور سمجھتا ہے کہ یہ ہم سے بہتر ہے، حالانکہ ہم حج اور سدانت اور سقایت کے منتظم ہیں“ (بَزّار)۔ اسی واقعہ کے متعلق عِکْرِمَہ کی روایت یہ ہے کہ قریش والوں نے حضورؐ کے لیے الصُّنْبورُ الْمُنْبَتِر من قومہ  کے الفاظ استعمال کیے تھے، یعنی ”کمزور ، بے یار و مددگار اور بے اولاد آدمی جو اپنی قوم سے کٹ گیا ہے“(ابن جریر)۔ ابن سعد اور ابن عَساکِر کی روایت ہے کہ حضرت عبد اللہ بن عباسؓ نے فرمایا کہ رسول اللہ صلی اللہ علیہ وسلم کے سب سے بڑے صاحبزادے قاسمؓ تھے، ان سے چھوٹی حضرت زینب ؓ تھیں، ان سے چھوٹے حضرت عبدؓاللہ تھے، پھر علی الترتیب تین صاحبزادیاں اُمِّ کُلثوم ؓ، فاطمہؓ اور رُقَیَّہ ؓ تھیں۔ ان میں سے پہلے حضرت قاسم کا انتقال ہوا، پھر حضرت عبداللہ نے بھی وفات پائی۔ اس پر عاص بن وائل نے کہا”اُن کی نسل ختم ہو گئی۔ اب وہ ابتر ہیں“ (یعنی ان کی جڑ کٹ گئی)۔ بعض روایات میں یہ اضافہ ہے کہ عاص نے کہا ان محمدًا بتر لا ابن لہ یقومُ مقامہ بعد ہ فا ذا مات انقطع ذکرہ و استر حتم منہ ۔” محمدؐ ابتر ہیں، ان کا کوئی بیٹا نہیں ہے جو ان کا قائم مقام بنے ، جب وہ مر جائیں گے تو ان کا نام دنیا سے مٹ جائے گا اور ان سے تمہارا پیچھا چھوٹ جائے گا“۔ عبد بن حُمَید نے ابن عباسؓ کی جو روایت نقل کی ہے اس سے معلوم ہوتا ہے کہ حضورؐ کے صاحبزادے عبد اللہ کی وفات پر ابو جہل نے بھی ایسی ہی باتیں کہی تھیں۔ شَمِر بن عطیہ سے ابن ابی حاتم کی روایت ہے کہ حضورؐ کے اِس غم پر خوشی مناتے ہوئے ایسے ہی کمینہ پن کا مظاہرہ عُقْبہ بن ابی مُعَیط نے کیا تھا۔ عطاء کہتے ہیں کہ جب حضورؐ کے دوسرے صاحبزادے کا انتقال ہوا تو حضورؐ کا اپنا چچا ابولہب (جس کا گھر بالکل حضورؐ کے گھر سے متصل تھا) دوڑا ہوا مشرکین کے پاس گیا اور اُن کو یہ ”خوشخبری“ دی کہ بَتِر محمّدٌ اللّیْلۃ۔ ” آج رات محمدؐ لا ولد ہوگئے یا ان کی جڑ کٹ گئی“۔ یہ تھے وہ انتہائی دل شکن حالات جن میں سورۂ کوثر حضورؐ پر نازل کی گئی۔ قریش اس لیے آپ سے بگڑے تھے کہ آپ صرف اللہ ہی کی بندگی و عبادت کرتے تھے اور ان کے شرک کو آپ نے عَلانیہ رد کر دیا تھا۔ اِسی وجہ سے پوری قوم میں جو مرتبہ و مقام آپ کو نبوت سے پہلے حاصل تھا وہ آپ سے چھین لیا گیا تھا اور آپ گویا برادری سے کاٹ پھینکے گئے تھے۔ آپ کے چند مٹھی بھر ساتھی سب بے یار و مددگار تھے اور مارے کھدیڑے جا رہے تھے۔ اس پر مزید آپ پر ایک کے بعد ایک بیٹے کی وفات سے غموں کا پہاڑ ٹوٹ پڑا تھا۔ اس موقع پر عزیزوں، رشتہ داروں، قبیلے اور برادری کے لوگوں اور ہمسایوں کی طرف سے ہمدردی و تعزیت کے بجائے ہو خوشیاں منائی جا رہی تھیں ، اور وہ باتیں بنائی جا رہی تھیں جو ایک ایسے شریف انسان کے لیے دل توڑ دینے والی تھیں جس نے اپنے تو اپنے، غیروں تک سے ہمیشہ انتہائی نیک سلوک کیا تھا۔ اس پر اللہ تعالیٰ نے آپ کو اِس مختصر ترین سورۃ کے ایک فقرے میں وہ خوشخبری دی جس سے بڑی خوش خبری دنیا کے کسی انسان کو کبھی نہیں دی گئی۔ اور ساتھ ساتھ یہ فیصلہ بھی سنا دیا کہ آپ کی مخالفت کرنے والوں ہی کی جڑ کٹ جائے گی۔ ','سید ابو اعلیٰ مودودیؒ -  تفہیم القرآن');
INSERT INTO "chapters_ur" VALUES(109,'کافرین',NULL,' نام : پہلی ہی آیت قُلْ یٰٓااَیُّھَا الْکٰفِرُوْنَ کے لفظ الکافرون کو اِس سورہ کا نام قرار دیا گیا ہے۔ زمانۂ نزول : حضرت عبد اللہ بن مسعود، حضرت حسن بصری اور عِکْرِمَہ کہتے ہیں کہ یہ سورۃ مکّی ہے، حضرت عبد اللہ بن زبیر کہتے ہیں مدّنی ہے۔ اور حضرت عبد اللہ بن عباس اور قَتَادہ سے دو قول منقول ہوئے ہیں۔ ایک یہ کہ یہ مکّی ہے اور دوسرا یہ کہ مدنی ہے۔ لیکن جمہور مفسرین کے نزدیک یہ مکّی سورۃ ہے، اور اس کا مضمون خود اس کے مکّی ہونے پر دلالت کر رہاہے۔ تاریخی پَسْ منظر : مکّہ معظمہ میں ایک دور ایسا گزرا ہے جب نبی صلی اللہ علیہ وسلم کی دعوتِ اسلام کے خلاف قریش کے مشرک معاشرے میں مخالفت کا طوفان تو برپا ہو چکا تھا ، لیکن ابھی قریش کے سردار اِس بات سے بالکل مایوس نہیں ہوئے تھے کہ حضورؐ کو کسی نہ کسی طرح مصالحت پر آمادہ کیا جا سکے گا ۔ اس لیے وقتاً فوقتاً وہ آپ کے پاس مصالحت کی مختلف تجویزیں لے لے کر آتے رہتے تھے تا کہ آپ اُن میں سے کسی کو مان لیں اور وہ نزع ختم ہو جائے جو آپ کے اور اُن کے درمیان رُونما ہو چکی تھی۔ اس سلسلے میں متعدد روایات احادیث میں منقول ہوئی ہیں:حضرت عبد اللہ بن عباس ؓ کی روایت ہے کہ قریش کے لوگوں نے رسول اللہ صلی اللہ علیہ وسلم سے کہا ہم آپ کو اتنا مال دے دیتے ہیں کہ آپ مکّہ کے سب سے زیادہ دولت مند آدمی بن جائیں، آپ جس عورت کو پسند کریں اس سے آپ کی شادی کیے دیتے ہیں، ہم آپ کے پیچھے چلنے کے لیے تیار ہیں، آپ بس ہماری یہ بات مان لیں کہ ہمارے معبودوں کی بُرائی کرنے سے باز رہیں۔ اگر یہ آپ کو منظور نہیں، تو ہم ایک اور تجویز آپ کے سامنے پیش کرتے ہیں جس میں آپ کی بھی بھلائی ہے اور ہماری بھی۔ حضور ؐ نے پوچھا وہ کیا ہے؟ انہوں نے کہا ایک سال آپ ہمارے معبود و ں لات اور عُزّیٰ کی عبادت کریں اور ایک سال ہم آپ کے معبود کی عبادت کریں۔ حضورؐ نے فرمایا اچھا،ٹھہرو، میں دیکھتا ہوں کہ میرے ربّ کی طرف سے کیا حکم آتا ہے ۔ ۱؎ اس پر وحی نازل ہوئی قُلْ یٰٓا اَیُّھَا الْکٰفِرُوْنَ ۔۔۔۔۔۔۔۔ اور یہ کہ قُلْ اَفَغَیْرَ اللہِ تَاْ مُرُوْنِّیْٓ اَعْبُدُ اَیُّھَا الْجٰعِلُوْنَ o (الزُّمر، آیت۶۴)۔ ”اِن سے کہو، اے نادانو، کیا تم مجھ سے یہ کہتے ہو کہ اللہ کے سوا میں کسی اور کی عبادت کروں؟“ (ابن جریر۔ ابن ابی حاتم۔ طَبَرانی)۔ ابن عباسؓ کی ایک اور روایت یہ ہے کہ قریش کے لوگوں نے حضورؐ سے کہا ” اے محمدؐ ، اگر تم ہمارے معبود بتوں کو چوم لو تو ہم تمہارے معبود کی عبادت کریں گے۔“ اس پر یہ سورۃ نازل ہوئی(عبد بن حُمَید)۔ سعید بن مینا ء (ابو البَخْتَرِی کے آزاد کردہ غلام ، کی روایت ہے کہ ولید بن مُغِیْرہ ، عاص بن وائل، اَسْوَد بن المُطَّلِب اور اُمَیَّہ بن خَلَف رسول اللہ صلی اللہ علیہ وسلم سے ملے اور آپ سے کہا ”اے محمدؐ، آؤ ہم تمہارے معبود کی عبادت کریتے ہیں اور تم ہمارے معبودوں کی عبادت کرو اورہم اپنے سارے کاموں میں تمہیں شریک کے لیتے ہیں۔ اگر وہ چیز جو تم لے کر آئے ہو اُس سے بہتر ہوئی جو ہمارے پاس ہے تو ہم تمہارے ساتھ اُس میں شریک ہوں گے اور اپنا حصہ اُس سے پالیں گے۔ اور اگر وہ چیز جو ہمارے پاس ہے اُس سے بہتر ہوئی جو تم لائے ہو تو تم ہمارے ساتھ اس میں شریک ہو گے اور اس سے اپنا حصّہ پا لو گے۔ “ اس پر اللہ تعالیٰ نے یہ وحی نازل فرمائی کہ قُلْ یٰٓااَیُّھَا الْکٰفِرُوْنَ ۔۔۔۔۔۔۔۔(ابن جریر و ابن ابی حاتم ابن ہشام نے بھی سیرت میں اس واقعہ کو نقل کیا ہے) وَہْب بن مُنَبِّہ کی روایت ہے کہ قریش کے لوگوں نے رسول اللہ صلی اللہ علیہ وسلم سے کہا اگر آپ پسند کریں تو ایک سال ہم آپ کے دین میں داخل ہو جائیں گے اور ایک سال آپ ہمارے دین میں داخل ہو جایا کریں(عبد بن حُمَید ابن ابی حاتم)۔ اِن روایات سے معلوم ہوتا ہے کہ ایک مرتبہ ایک ہی مجلس میں نہیں بلکہ مختلف اوقات میں مختلف مواقع پر کفّارِ قریش نے حضورؐ کے سامنے اِس قسم کی تجویزیں پیش کی تھیں اور اس بات کی ضرور ت تھی کہ ایک دفعہ دو ٹوک جواب دے کر اُن کی اِس امید کو ہمیشہ کے لیے ختم کر دیا جائے کہ رسول اللہ صلی اللہ علیہ وسلم دین کے معاملہ میں کچھ دو اور کچھ لو کے طریقے پر اُن سے کوئی مصالحت کر لیں گے۔ موضوع اور مضمون: اِس پس منظر کو نگاہ میں رکھ کر دیکھا جائے تو معلوم ہوتا ہے کہ یہ سورۃ مذہبی رواداری کی تلقین کے لیے نازل نہیں ہوئی تھی، جیسا کہ آج کل کے بعض لوگ خیال کرتے ہیں ، بلکہ اِس لیے نازل ہوئی تھی کہ کفّار کے دین اور اُن کی پوجا پاٹ اور اُن کے معبودوں سے قطعی براءت ، بیزاری اور لاتعلقی کا اعلان کر دیا جائے اور اُنہیں بتا دیا جائے کہ دینِ کفر اور دینِ اسلام ایک دوسرے سے بالکل الگ ہیں، اُن کے باہم مل جانے کا سرے سے کوئی سوال ہی پیدا نہیں ہوتا۔ یہ بات اگرچہ ابتداءً قریش کے کفار کو مُخاطَب کر کے اُن کی تجاویز مصالحت کے جواب میں کہی گئی تھی، لیکن یہ اُنہی تک محدود نہیں ہے بلکہ اسے قرآن میں درج کر کے تمام مسلمانوں کو قیامت تک کے لیے یہ تعلیم دے دی گئی ہے کہ دینِ کفر جہاں جس شکل میں بھی ہے اُن کو اس سے قول اور عمل میں براءت کا اظہار چاہیے اور بلا رو رعایت کہہ دینا چاہیے کہ دین کے معاملہ میں وہ کافروں سے کسی قسم کی مداہنت یا مصالحت نہیں کر سکتے۔ اسی لیے یہ سورۃ اُس وقت بھی پڑھی جاتی رہی جب وہ لوگ مر کھپ گئے تھے جن کی باتوں کے جواب میں اِسے نازل فرمایا گیا تھا ، اور وہ لوگ بھی مسلمان ہونے کے بعد اسے پڑھتے رہے جو اِس کے نزول کے زمانے میں کافر و مشرک تھے، اور اُن کے گزر جانے کے صدیوں بعد آج بھی مسلمان اس کو پڑھتے ہیں کیونکہ کفر اور کافری سے بیزاری و لاتعلقی ایمان کا دائمی تقاضا ہے۔ رسول اللہ صلی اللہ علیہ وسلم کی نگاہ میں اِس سورہ کی کیا اہمیت تھی ، اس کا اندازہ ذیل کی چند احادیث سے کیا جا سکتا ہے: حضرت عبد اللہ بن عمر رضی اللہ عنہ کی روایت ہے کہ میں نے بار ہا حضورؐ کو فجر کی نماز سے پہلے اور مغرب کی نماز کے بعد کی دورکعتوں میں قُلْ یٰٓااَیُّھَا الْکٰفِرُوْنَ َ اور قُلْ ھُوَ اللہُ اَحَد پڑھتے دیکھا ہے۔(اِس مضمون کی متعدد روایات کچھ لفظی اختلافات کے ساتھ امام احمد، تِرْمذی، نَسائی، ابن ماجہ، ابن حِبّان اور ابن مَردویَہ نے ابن عمرؓ سے نقل کی ہیں)۔ حضرت خَبّاب ؓ کہتے ہیں کہ نبی صلی اللہ علیہ وسلم نے مجھ سے فرمایا کہ جب تم سونے کے لیے اپنے بستر پر لیٹو تو قُلْ یٰٓااَیُّھَا الْکٰفِرُوْنَ پڑھ لیا کرو، اور حضورؐ کا خود بھی یہ طریقہ تھا کہ جب آپؐ سونے کے لیے لیٹتے تو یہ سورۃ پڑھ لیا کرتے تھے(بزّار، طَبَرانی، ابن مردویہ) ابنِ عباسؓ کا بیان ہے کہ رسول اللہ صلی اللہ علیہ وسلم نے لوگوں سے فرمایا ”میں تمہیں بتاؤں وہ کلمہ جو تم کو شرک سے محفوظ رکھنے والا ہے؟ وہ یہ ہے کہ سوتے وقت قُلْ قُلْ یٰٓااَیُّھَا الْکٰفِرُوْنَ پڑھ لیا کرو (ابو یَعلیٰ، طَبَرانی)۔ حضرت اَنَسؓ کہتے ہیں کہ رسول اللہ صلی اللہ علیہ وسلم نے حضرت مُعاذ بن جَبَل سے فرمایا سوتے وقت قُلْ یٰٓااَیُّھَا الْکٰفِرُوْنَ پڑھ لیا کرو کیونکہ یہ شرک سے براءت ہے(بَیْہَقِی فی الشعب)۔ فَرْدَہ بن نَوفَل اور عبد الرحمان بن نَوفَل ، دونوں کا بیان ہے کہ ان کے والد نوفل بن معاویہؓ الاَشْجَعی نے رسول اللہ صلی اللہ علیہ وسلم سے عرض کیا کہ مجھے کوئی ایسی چیز بتا دیجیئے جسے میں سوتے وقت پڑھ لیا کروں۔ آپ نے فرمایا قُلْ یٰٓااَیُّھَا الْکٰفِرُوْنَ آخر تک پڑھ کر سو جایا کرو، کیونکہ یہ شرک سے براءت ہے(مُسند احمد، ابو داؤد، تِرمِذی، نَسائی، ابن ابی شَیبہ، حاکم ، ابن مَرْدُوْیَہ، بِیْہَقِی فی الشعب) ۔ ایسی ہی درخواست حضرت زید بن ؓ حارثہ کے بھائی حضرت جَبَلہ بنؓ حارثہ نے حضور سے کی تھی اور ان کو بھی آپ نے یہی جواب دیا تھا ( مُسند احمد، طَبَرانی)۔ ؎ اس کا یہ مطلب نہیں ہے کہ رسول اللہ صلی اللہ علیہ وسلم کسی درجہ میں بھی اس تجویز کو قابلِ قبول کیا معنی قابلِ غور بھی سمجھتے تھے، اور آپؐ نے معاذ اللہ کفار کو یہ جواب اس امید پر دیا تھا کہ شاید اللہ تعالیٰ کی طرف سے اس کی منظوری آجائے۔ بلکہ دراصل یہ بات بالکل ایسی ہی تھی جیسے کسی ماتحت افسر کے سامنے کوئی بے جا مطالبہ پیش کیا جائے اور وہ جانتا ہو کہ اس کی حکومت کے لیے یہ مطالبہ قابلِ قبول نہیں ہے ، مگر وہ خود صاف انکار کر دینے کے بجائے مطالبہ کرنے والوں سے کہے کہ میں آپ کی درخواست اوپر بھیجے دیتا ہوں ، جو کچھ وہاں سے جواب آئے گا وہ آپ کو بتادوں گا۔ اس سے فرق یہ واقع ہوتا ہے کہ ماتحت افسر اگر خود ہی انکار کر دے تو لوگوں کا اصرار جاری رہتا ہے، لیکن اگر وہ بتائے کہ اوپر سے حکومت کا جواب ہی تمہارے مطالبہ کے خلاف آیا ہے تو لوگ مایوس ہو جاتے ہیں۔ واپس ','سید ابو اعلیٰ مودودیؒ -  تفہیم القرآن');
INSERT INTO "chapters_ur" VALUES(110,'نصرت',NULL,' نام : پہلی آیت اَذَا جَآءَ نَصْرُ اللہِ کے لفظ نصر کو اِس سورہ کا نام قرار دیا گیا ہے۔ زمانۂ نزول : حضرت عبداللہؓ بن عباس کا بیان ہے کہ یہ قرآن مجید کی آخری سورت ہے، یعنی اس کے بعد کوئی مکمّل سورہ حضورؐ پر نازل نہیں ہوئی۔ ؎ (مسلم، نَسائی، طَبَرانی، ابن ابی شَیبہ، ابن مَرْدُوْیَہ)۔ حضرت عبد اللہ بن عمر ؓ کی روایت ہے کہ یہ سورت حَجّۃ الوداع کے موقع پر ایّامِ تشریق کے وسط میں بمقامِ منٰی نازل ہوئی اور اس کے بعد حضور ؐ نے اپنی اونٹنی پر سوار ہو کر اپنا مشہور خطبہ ارشاد فرمایا (تِرمِذی، بَزّار، بَیْہَقِی، ابن ابی شَیبہ، عبد بن حُمَید، ابو یعلیٰ، ابن مَرْدُوْیَہ)۔ بیہقی نے کتاب الحج میں حضرت سَرّ اء بنت نَبْہان کی روایت سے حضورؐ کا وہ خطبہ نقل کیا ہے جو آپ نے اِس موقع پر ارشاد فرمایا تھا۔ وہ کہتی ہیں کہ: ” میں نے حَجّۃ الوداع میں حضور ؐ کو یہ فرماتے سنا کہ لوگو جانتے ہو کہ یہ کونسا دن ہے؟ لوگوں نے عرض کیا اللہ اور اس کے رسول کو زیادہ علم ہے ۔ فرمایا یہ ایّامِ تشریق کے بیچ کا دن ہے۔ پھر آپؐ نے پوچھا جانتے ہو یہ کونسا مقام ہے ؟ لوگوں نے عرض کیا اللہ اور اس کے رسول کو زیادہ علم ہے۔ فرمایا یہ مَشْعَرِ حرام ہے۔ پھر حضور ؐ نے فرمایا کہ میں نہیں جانتا، شاید اِس کے بعد میں تم سے مل نہ سکوں۔ خبردار رہو، تمہارے خون اور تمہاری عزتیں ایک دوسرے پر اُسی طرح حرام ہیں جس طرح یہ دن اور یہ مقام حرام ہے ، یہاں تک کہ تم اپنے ربّ کے سامنے حاضر ہو اور وہ تم سے تمہارے اعمال کے بارے میں سوال کرے۔ سنو، یہ بات تم میں سے قریب والا دُور والے تک پہنچا دے۔ سُنو، کیا میں نے تمہیں پہنچا دیا؟ اِس کے بعد جب ہم لوگ مدینہ واپس ہوئے تو کچھ زیادہ دن نہ گزرے تھے کہ حضورؐ کا انتقال ہو گیا۔“ اِن دونوں روایتوں کو ملا کر دیکھا جائے تو معلوم ہوتا ہے کہ سورۂ نَصر کے نزول اور رسول اللہ صلی اللہ علیہ وسلم کی وفات کے درمیان ۳ مہینے کچھ دن کا فصل تھا ، کیونکہ تاریخ کی رو سے حَجۃ الوداع اور حضورؐ کے وِصال کے درمیان اتنا ہی زمانہ گزرا تھا۔ ابن عباسؓ کا بیان ہے کہ جب یہ سورۃ نازل ہوئی تو حضورؐ نے فرمایا مجھے میری وفات کی خبر دے دی گئی ہے اور میرا وقت آن پورا ہوا ( مُسند احمد، ابن جریر، ابن المُنْذِر، ابن مردویہ)۔ دوسری روایات جو حضرت عبد اللہ بن عباسؓ سے منقول ہوئی ہیں اُن میں بیان کیا گیا ہےکہ اِس سورۃ کے نزول سے حضورؐ نے یہ سمجھ لیا تھا کہ آپؐ کو دنیا سے رخصت ہونے کی اطلاع دے دی گئی ہے (مُسند احمد، ابن جریر، طَبَرانی، نَسائی، ابن ابی حاتم، ابن مردویہ)۔ ام المومنین حضرت امِّ حبیبہ ؓ فرماتی ہیں کہ جب یہ سورۃ نازل ہوئی تو حضورؐ نے فرمایا اس سال میرا انتقال ہو نے والا ہے۔ یہ بات سُن کر حضرت فاطمہ ؓ رو دیں۔ اِس پر آپؐ نے فرمایا میرے خاندان میں سے تم ہی سب سے پہلے مجھ سے آکر ملو گی۔ یہ سُن کر وہ ہنس دیں (ابن ابی حاتم، ابن مردویہ)۔ قریب قریب اِسی مضمون کی روایت بیہقی نے ابن عباسؓ سے نقل کی ہے۔ ابن عباسؓ فرماتے ہیں کہ حضرت عمر رضی اللہ عنہ مجھے غَزْوَہ ٔ بدر میں شریک ہونے والے بڑے بڑَ شیوخ کے ساتھ اپنی مجلس میں بلاتے تھے۔ یہ بات بعض بزرگوں کو نا گوار گزری اور انہوں نے کہا کہ ہمارے لڑکے بھی تو اِسی لڑکے جیسے ہیں، اِس کو خاص طور پر کیوں ہمارے ساتھ شریک ِ مجلس کیا جاتا ہے؟ (امام بخاری اور ابن جریر نے تصریح کی ہے کہ یہ بات کہنے والے حضرت عبد الرحمانؓ بن عَوف تھے)۔ حضرت عمرؓ نے فرمایا کہ علم کے لحاظ سے اِس کا جو مقام ہے وہ آپ لوگ جانتے ہیں۔ پھر ایک روز اُنہوں نے شیوخِ بدر کو بلایا اور مجھے بھی اُن کے ساتھ بُلا لیا۔ میں سمجھ گیا کہ آج مجھے یہ دکھانے کے لیے بلایا گیا ہے کہ مجھ کو ان کی مجلس میں کیوں شریک کیا جاتا ہے۔ دورانِ گفتگو میں حضرت عمرؓ نے شیوخ بدر سے پوچھا کہ آپ حضرات اِذَا جَآ ءَ نَصْرُ اللہِ وَالْفَتْحُ کے بارے میں کیا کہتے ہیں؟ بعض نے کہا اس میں ہمیں حکم دیا گیا ہے کہ جب اللہ کی نصرت آئے اور ہم کو فتح نصیب ہو تو ہم اللہ کی حمد اور اس سے استغفار کریں۔ بعض نے کہا اس سے مراد شہروں اور قلعوں کی فتح ہے۔ بعض خاموش رہے۔ اس کے بعد حضرت عمرؓ نے کہا ابن عباسؓ کیا تم بھی یہی کہتے ہو؟ میں نے کہا، نہیں ۔ انہوں نے پوچھا پھر تم کیا کہتے ہو؟ میں نے عرض کیا اس سے مراد رسول اللہ صلی اللہ علیہ وسلم کی اَجل ہے۔ اس میں حضورؐ کو خبر دی گئی ہے کہ جب اللہ کی نصرت آجائے اور فتح نصیب ہو جائے تو یہ اس بات کی علامت ہے کہ آپؐ کا وقت آن پورا ہوا ، اِس کے بعد آپؐ اللہ کی حمد اور استغفار کریں۔ اس پر حضرت عمرؓ نے فرمایا میں بھی اُس کے سوا کچھ نہیں جانتا جو تم نے کہا ہے۔ ایک روایت میں اس پر یہ اضافہ ہے کہ حضرت عمرؓ نے شیوخِ بدر سے فرمایا آپ لوگ مجھے کیسے ملامت کرتے ہیں جبکہ اِس لڑکے کو اس مجلس میں شریک کرنے کی وجہ آپ نے دیکھ لی (بخاری، مُسند احمد، تِرْمِذی، ابن جریر، ابن مردویہ، بَغَوِی، بَیْہَقِی، ابن المُنْذِر)۔ موضوع اور مضمون : جیسا کہ مندرجۂ بالا روایات سے معلوم ہوتا ہے ، اِس سورہ میں اللہ تعالیٰ نے رسول اللہ صلی اللہ علیہ وسلم کو یہ بتا دیا تھا کہ جب عرب میں اسلام کی فتح مکمل ہوجائے اور لوگ اللہ کے دین میں فوج در فوج داخل ہونے لگیں تو اس کے معنی یہ ہیں کہ وہ کام مکمل ہو گیا جس کے لیے آپؐ دنیا میں بھیجے گئے تھے۔ اس کے بعد آپؐ کو حکم دیا گیا کہ آپؐ اللہ کی حمد اور اس کی تسبیح کرنے میں مشغول ہو جائیں کہ اُس کے فضل سے آپؐ اتنا بڑا کام انجام دینے میں کامیاب ہو ئے ، اور اُس سے دعا کریں کہ اِس خدمت کی انجام دہی میں جو بھول چوک یا کوتاہی بھی آپؐ سے ہوئی ہو اُسے وہ معاف فرمادے۔ اِس مقام پر آدمی غور کرے تو دیکھ سکتا ہے کہ ایک نبی اور ایک عام دنیوی رہنما کے درمیان کتنا عظیم فرق ہے۔ کسی دنیوی رہنما کو اگر اپنی زندگی ہی میں وہ انقلابِ عظیم برپا کرنے میں کامیابی نصیب ہو جائے جس کے لیے وہ کام کرنے اٹھا ہو تو اس کے لیے یہ جشن منانے اور اپنی قیادت پر فخر کرنے کا موقع ہوتا ہے۔ لیکن یہاں اللہ کے پیغمبر کو ہم دیکھتے ہیں کہ اُس نے ۲۳ سال کی مختصر مدت میں ایک پوری قوم کے عقائد، افکار، عادات، اخلاق، تمدّن، تہذیب ، معاشرت، معیشت، سیاست اور حربی قابلیت کو بالکل بدل ڈالا اور جہالت و جاہلیت میں ڈوبی ہوئی قوم کو اُٹھا کر اِس قابل بنا دیا کہ وہ دنیا کو مسخّر کرڈالے اور اقوامِ عالم کی امام بن جائے، مگر ایسا عظیم کارنامہ اُس کے ہاتھوں انجام پانے کے بعد اُسے جشن منانے کا نہیں بلکہ اللہ کی حمد اور تسبیح کرنے اور اُس سے مغفرت کی دعا کرنے کا حکم دیا جاتا ہے ، اور وہ پوری عاجزی کے ساتھ اس حکم کی تعمیل میں لگ جاتا ہے۔ حضرت عائشہؓ فرماتی ہیں کہ رسول اللہ صلی اللہ علیہ وسلم اپنی وفات سے پہلے سُبْحٰنَکَ اللّٰھُمَّ وَبِحَمْدِکَ اَسْتَغْفِرُکَ وَاَتُوْ بُ اِلَیْکَ (بعض روایات میں الفاظ یہ ہیں سُبْحَانَ اللہِ وَبَحَمْدِہٖ اَسْتَغْفِرُاللہَ وَاَتُوْبُ اِلَیْہِ ) کثرت سے پڑھا کرتے تھے ۔ میں عرض کیا یا رسول اللہ یہ کیسے کلمات ہیں جو آپؐ نے اب پڑھنے شروع کر دیے ہیں؟ فرمایا میرے لیے ایک علامت مقرر کر دی گئی ہے کہ جب میں اُسے دیکھوں تو یہ الفاظ کہا کروں اور وہ ہے اَذَا جَآءَ نَصْرُ اللہِ وَالْفَتَحُ (مُسند احمد، مسلم ، ابن جریر، ابن المنذر، ابن مردویہ)۔ اسی سے ملتی جُلتی بعض روایات میں حضرت عائشہؓ کا بیان ہے کہ آپؐ اپنے رکوع و سجود میں بکثرت یہ الفاظ کہتے تھے سُبْحٰنَکَ اللّٰھُمَّ وَبِحَمْدِکَ، اَللّٰھُمَّ اغْفِرْلِیْ۔ یہ قرآن (یعنی سورۂ نصر) کی تاویل تھی جو آپؐ نے فرمائی تھی (بخاری، مسلم، ابو داؤد، نَسائی، ابن ماجہ، ابن جریر)۔ حضرت اُمِّ سَلَمہؓ فرمانی ہیں کہ رسول اللہ صلی اللہ علیہ وسلم کی زبانِ مبار ک پر آپؐ کے آخری زمانۂ حیات میں اُٹھتے بیٹھتے اور جاتے آتے یہ الفاظ جاری رہتے: سُبْحَانَ اللہِ وَبَحَمْدِہٖ میں نے ایک روز پوچھا کہ یا رسول اللہ آپؐ کثرت سے یہ ذکر کیوں کرتے رہتے ہیں ؟ فرمایا مجھے اس کا حکم دیا گیا ہے پھر آپؐ نے یہ سورۃ پڑھی(ابن جریر)۔ حضرت عبد اللہ بن مسعودؓ کی روایت ہے کہ جب یہ سورۃ نازل ہوئی تو رسول اللہ صلی اللہ علیہ وسلم کثرت سے یہ ذکر فرماتے رہتے: سُبْحٰنَکَ اللّٰھُمَّ وَبِحَمْدِکَ، اَللّٰھُمَّ اغْفِرْلِیْ، سُبْحَا نَکَ رَبَّنَا وَبِحَمْدِکَ، اَللّٰھُمَّ اغْفِرْلِیْ ، اِنَّکَ اَنْتَ التَّوَّابُ الْغَفُوْرُ۔ (ابن جریر ، مُسند احمد، ابن ابی حاتم)۔ ابن عباسؓ کا بیان ہے کہ اس سورت کے نازل ہونے کے بعد رسول اللہ صلی اللہ علیہ وسلم آخرت کے لیے محنت و ریاضت کرنے میں اِس قدر شدّت کے ساتھ مشغول ہوگئے جتنے اس سے پہلے کبھی نہ ہوئے تھے (نَسائی، طَبَرانی، ابن ابی حاتم، ابن مردویہ)۔ ؎ مختلف روایات سے معلوم ہوتا ہے کہ اِس کے بعد بعض آیات نازل ہوئی ہیں۔ لیکن اس امر میں اختلاف ہے کہ قرآن کی وہ آیت کون سی ہے ، جو حضورؐ پر سب سے آخر میں نازل ہوئی۔ بخاری و مسلم میں حضرت براءؓ بن عازِب کی روایت یہ ہے کہ وہ سورۂ نساء کی آخری آیت یَسْتَفْتُوْنَکَ ، قُلِ اللہُ یُفْتِیْکُمْ فِی الْکَلٰلَۃِ ہے۔ امام بخاری نے ابن عباسؓ کا قول نقل کیا ہے کہ آیت ربوٰ یعنی جس آیت میں سود کی حرمت کا حکم دیا گیا ہے ، قرآن کی سب سے آخری آیت ہے، بلکہ حضرت عمرؓ کا قول یہ ہے کہ یہ سب سے آخر میں نازہ ہونے والی آیات میں سے ہے۔ ابو عبید نے فضائل القرآن میں امام زُہری کا، اور ابن جریر نے اپنی تفسیر میں حضرت سعید بن المُسیَّب کا قول نقل کیا ہے کہ آیتِ ربوٰ اور آیتِ دَین (یعنی سورۂ بقرہ رکوع ۳۹،۳۸) قرآن میں نازل ہونے والی آخری آیات ہیں۔ نَسائی، ابن مردویہ اور ابن جریر نے حضرت عبد اللہ ؓ بن عباس کا ایک دوسرا قول نقل کیا ہے کہ وَاتَّقُوْا یَوْمًا تُرْجَعُوْنَ فِیْہِ (البقرہ، ۲۸۱) قرآن کی آخری آیت ہے۔ الفِرْیابی نے اپنی تفسیر میں ابن عباس کا جو قول نقل کیا ہے ، اس میں یہ اضافہ ہے کہ یہ آیت حضورؐ کی وفات سے ۸۱ دن پہلے نازل ہوئی تھی اور سعید بن جبیر کا قول جو ابن ابی حاتم نے نقل کیا ہے ، اس میں اس آیت کے نزول اور حضورؐ کی وفات کے درمیان صرف ۹ دن کا فصل بیان کیا گیا ہے۔ امام احمد کی مُسند اور امام حاکم کی المستدرک میں حضرت اُبی بن کعب کی روایت یہ ہے کہ سورۂ توبہ کی آیات ۱۲۸، ۱۲۹ سب سے آخر میں نازل ہوئی ہیں۔ واپس ','سید ابو اعلیٰ مودودیؒ -  تفہیم القرآن');
INSERT INTO "chapters_ur" VALUES(111,'خوب بٹی ہوئی',NULL,' نام: پہلی آیت کے لفظ لَھَب کو اس سُورہ کا نام قرار دیا گیا ہے۔ زمانۂ نزول: اِس کے مکّی ہونے میں تو مفسرین کے درمیان کوئی اختلاف نہیں ہے، لیکن ٹھیک ٹھیک یہ متعیّن کرنا مشکل ہے کہ مکّی دور کے کس زمانے میں یہ نازل ہوئی تھی۔ البتّہ ابو لہب کا جو کردار رسول اللہ صلی اللہ علیہ وسلم اور آپ کی دعوتِ حق کے خلاف تھا اُس کو دیکھتے ہوئے یہ اندازہ کیا جا سکتا ہے کہ اِس سورہ کا نزول اُس زمانے میں ہوا ہو گا جب وہ حضورؐ کی عداوت میں حد سے گزر گیا تھا اور اُس کا رویّہ اسلام کی راہ میں ایک بڑی رکاوٹ بن رہا تھا۔ بعید نہیں کہ اِس کا نزول اُس زمانے میں ہوا ہو جب رسول اللہ صلی اللہ علیہ وسلم اور آپؐ کے خاندان والوں کا مقاطعہ کر کے قریش کے لوگوں نے اُن کو شعبِ ابی طالب میں محصور کر دیا تھا اور تنہا ابو لہب ہی ایسا شخص تھا جس نے اپنے خاندان والوں کو چھوڑ کر دشمنوں کا ساتھ دیا تھا۔ ہمارے اِس قیاس کی بنا یہ ہے کہ ابو لہب حضورؐ کا چچا تھا، اور بھتیجے کی زبان سے چچا کی کھلّم کھلا مَذَمَّت کرانا اُس وقت تک مناسب نہ ہو سکتا تھا جب تک چچا کی حد سے گزری ہوئی زیادتیاں عَلانیہ سب کے سامنے نہ آگئی ہوں۔ اِس سے پہلے اگر ابتداء ہی میں یہ سورۃ نازل کر دی گئی ہوتی تو لوگ اس کو اخلاقی حیثیت سے معیوب سمجھتے کہ بھتیجا اپنے چچا کی اِس طرح مذمّت کرے۔ پَسْ منظر : قرآن مجید میں یہ ایک ہی مقام ہے جہاں دشمنانِ اسلام میں سے کسی شخص کا نام لے کر اُس کی مَذَمَّت کی گئی ہے، حالانکہ مکّے میں بھی، اور ہجرت کے بعد مدینہ میں بھی بہت سے لوگ ایسے تھے جو اسلام اور محمد صلی اللہ علیہ وسلم کی عداوت میں ابو لہب سے کسی طرح کم نہ تھے۔ سوال یہ ہے کہ اِس شخص کی وہ کیا خصوصیت تھی جس کی بنا پر اس کا نام لے کر اس کی مذمت کی گئی ؟ اِس بات کو سمجھنے کے لیے ضروری ہے کہ اُس وقت کے عربی معاشرے کو سمجھا جائے، اور اُس میں ابو لہب کے کردار کو دیکھا جائے۔ قدیم زمانے میں چونکہ پورے ملکِ عرب میں ہر طرف بد امنی ، غارت گری اور طوائف الملوکی پھیلی ہوئی تھی ، اور صدیوں سے حالت یہ تھی کہ کسی شخص کے لیے اُس کے خاندان اور خونی رشتہ داروں کی حمایت کے سوا جان و مال اور عزت و آبرو کے تحفظ کی کوئی ضمانت نہ تھی، اس لیے عربی معاشرے کی اخلاقی قدروں میں صلۂ رحمی (یعنی رشتہ داروں کے ساتھ حسنِ سلوک ) کو بڑی اہمیت حاصل تھی، اور قطعِ رحمی کو بہت بڑا پاپ سمجھا جاتا تھا۔ عرب کی اِنہی روایات کا یہ اثر تھا کہ رسول اللہ صلی اللہ علیہ وسلم جب اسلام کی دعوت لے کر اُٹھے تو قریش کے دوسرے خاندانوں اور ان کے سرداروں نے تو حضورؐ کی شدید مخالفت کی، مگر بنی ہاشم اور بنی المُطَّلِب (ہاشم کے بھائی مُطّلِب کی اولاد) نے نہ صرف یہ کہ آپؐ کی مخالفت نہیں کی، بلہ وہ کھُلّم کھلا آپؐ کی حمایت کرتے رہے، حالانکہ ان میں سے اکثر لوگ آپؐ کی نبوّت پر ایمان نہیں لائے تھے۔ قریش کے دوسرے خاندان خود بھی حضورؐ کے اِن خونی رشتہ داروں کی حمایت کو عرب کی اخلاقی روایات کے عین مطابق سمجھتے تھے ، اسی وجہ سے انہوں نے کبھی بنی ہاشم اور بنی المُطَّلِب کو یہ طعنہ نہیں دیا کہ تم ایک دوسرا دین پیش کرنے والے شخص کی حمایت کر کے اپنے دینِ آبائی سے منحرف ہو گئے ہو۔ وہ اِس بات کو جانتے اور مانتے تھے کہ اپنے خاندان کے ایک فرد کو وہ کسی حالت میں اُس کے دشمنوں کے حوالے نہیں کر سکتے، اور اُ ن کا اپنے عزیز کی پشتیبانی کرنا قریش اور اہلِ عرب سب کے نزدیک بالکل ایک فطری امر تھا۔ اِس اخلاقی اصول کو ، جسے زمانۂ جاہلیت میں بھی عرب کے لوگ واجب الاحترام سمجھتے تھے، صرف ایک شخص نے اسلام کی دشمنی میں توڑ ڈالا ، اور وہ تھا ابو لہب بن عبد المطّلِب۔ یہ رسول اللہ صلی اللہ علیہ وسلم کا چچا تھا۔ حضورؐ کے والد ماجد اور یہ ایک ہی باپ کے بیٹے تھے۔ عرب میں چچا کو باپ کی جگہ سمجھا جاتا تھا، خصوصًا جبکہ بھتیجے، کا باپ وفات پا چکا ہو تو عربی معاشرے میں چچا سے یہ توقع کی جاتی تھی کہ وہ بھتیجے کو اپنی اولاد کی طرح عزیز رکھے گا۔ لیکن اِس شخص نے اسلام کی دشمنی اور کفر کی محبت میں اِن تمام عربی روایات کو پامال کر دیا۔ ابن عباسؓ سے متعدد سندوں کے ساتھ یہ روایت محدثین نے نقل کی ہے کہ جب رسول اللہ صلی اللہ علیہ وسلم کو دعوتِ عام پیش کرنے کا حکم دیا گیا اور قرآن مجید میں یہ ہدایت نازل ہوئی کہ آپ اپنے قریب ترین عزیزوں کو سب سے پہلے خدا کے عذاب سے ڈرائیں تو آپ نے صبح سویرے کوہ صفا پر چڑھ کر بلند آواز سے پکارا، یا صبا حاہ (ہائے صبح کی آفت)۔ عرب میں یہ صدا وہ شخص لگاتا تھا جو صبح کے جھُٹ پُٹے میں کسی دشمن کو اپنے قبیلے پر حملہ کرنے کے لیے آتے دیکھ لیتا تھا۔ حضورؐ کی یہ آواز سُن کر لوگوں نے دریافت کیا کہ یہ کون پکار رہا ہے۔ بتایا گیا یہ محمد(صلی اللہ علیہ وسلم) کی آواز ہے۔ اس پر قریش کے تمام خاندانوں کے لوگ آپؐ کی طرف دوڑ پڑے۔ جو خود آسکتا تھا وہ خود آیا، اور جو نہ آسکتا تھا اُس نے اپنی طرف سے کسی کو بھیج دیا۔ جب سب جمع ہو گئے تو آپؐ نے قریش کے ایک ایک خاندا ن کا نام لے لے کر پکارا، اے بنی ہاشم، اے بنی عبد المطلب، اے بنی فِہر، اے بنی فلاں، اے بنی فلاں، اگر میں تمہیں یہ بتاؤں کہ پہاڑ کے پیچھے ایک لشکر تم پر حملہ کرنے کے لیے تیار ہے تو تم میری بات سچ مانو گے؟ لوگوں نے کہا ہاں، ہمیں کبھی تم سے جھوٹ سننے کا تجربہ نہیں ہوا ہے۔ آپؐ نے فرمایا تو میں تمہیں خبردار کرتا ہوں کہ آگے سخت عذاب آرہا ہے۔ اس پر قبل اِس کے کہ کوئی اور بولتا ، حضورؐ کے اپنے چچا ابو لہب نے کہا تَبًّا لَکَ اَلِھٰذَا جَمَعْتَنَا ؟”ستیاناس جائے تیرا ، کیا اس لیے تُو نے ہمیں جمع کیا تھا؟“ ایک روایت میں یہ بھی ہے کہ اس نے پتھر اٹھایا تا کہ رسول اللہ صلی اللہ علیہ وسلم پر کھینچ مارے (مُسند احمد، بخاری ، مسلم، ترمذی، ابن جریر وغیرہ)۔ اِبن زَید کی روایت ہے کہ ابو لہب نے رسول اللہ صلی اللہ علیہ وسلم سے ایک روز پوچھا اگر میں تمہارے دین کو مان لوں تو مجھے کیا ملے گا؟ آپؐ نے فرمایا جو اور سب ایمان لانے والوں کو ملے گا۔ اس نے کہا میرے لیے کوئی فضیلت نہیں ہے؟ حضورؐ نے فرمایا اور آپ کیا چاہتے ہی؟ اس پر وہ بولا تَبًّا لِھٰذَا الدِّیْنِ تَبًّا اَنْ اَکُوْنَ وَھٰٓئُو لَا ءِ سَوَآءً۔ ”ناس جائے اِس دین کا جس میں میں اور یہ دوسرے لوگ برابر ہوں“ (ابن جریر)۔ مکّہ میں ابو لہب حضورؐ کا قریب ترین ہمسایہ تھا۔ دونوں کے گھر ایک دیوار بیچ واقع تھے۔ اُس کے علاوہ حَکَم بن عاص (مروان کا باپ) عُقْبہ بن ابی مُعَیط، عَدِی بن حَمْر اء اور ابن الاَصْداءِ الہُذَلِی بھی آپ کے ہمسائے تھے۔ یہ لوگ گھر میں بھی حضورؐ کو چین نہیں لینے دیتے تھے۔ آپ کبھی نماز پڑھ رہے ہوتے تو یہ اوپر سے بکری کا اوجھ آپؐ پر پھینک دیتے ۔ کبھی صحن میں کھانا پک رہا ہوتا تو یہ ہنڈیا پر غلاظت پھینک دیتے ۔ حضورؐ باہر نکل کر ان لوگوں سے فرماتے ”اے بنی عبد مَناف ، یہ کیسی ہمسایگی ہے؟“۔ ابو لہب کی بیوی امِّ جمیل (ابو سفیان کی بہن) نے تو یہ مستقل وتیرہ ہی اختیار کر رکھا تھا کہ راتوں کو آپؐ کے گھر کے دروازے پر خاردار جھاڑیاں لا کر ڈال دیتی ، تاکہ صبح سویرے جب آپؐ یا آپؐ کے بچے باہر نکلیں تو کوئی کانٹا پاؤں میں چُبھ جائے(بَیْہَقِی، ابن ابی حاتم، ابن جریر، ابن عَساکِر، ابن ہِشام)۔ نبوت سے پہلے رسول اللہ صلی اللہ علیہ وسلم کی دو صاحبزادیاں ابو لہب کے دو بیٹوں عُتْبَہ اور عُتَیبہ سے بیاہی ہوئی تھیں۔ نبوت کے بعد جب حضورؐ نے اسلام کی طرف دعوت دینی شروع کی تو اِس شخص نے اپنے دونوں بیٹوں سے کہا کہ میرے لیے تم سے ملنا حرام ہے اگر تم محمد (صلی اللہ علیہ وسلم) کی بیٹیوں کو طلاق نہ دے دو ۔ چنانچہ دونوں نے طلاق دے دی۔ اور عُتَیبہ تو جہالت میں اس قدر آگے بڑھ گیا کہ ایک روز حضورؐ کے سامنے آکر اس نے کہا کہ میں النَّجْمِ اذَا ھَوٰی اور اَلَّذِیْ دَنَا فَتَدَلّٰی کا انکار کرتا ہوں، اور یہ کہہ کر اس نے حضورؐ کی طرف تھوکا جو آپؐ پر نہیں پڑا۔ حضورؐ نے فرمایا خدایا، اِس پر اپنے کتّوں میں ست ایک کتّے کو مسلّط کر دے۔ اِس کے بعد عتیبہ اپنے باپ کے ساتھ شام کے سفر پر روانہ ہو گیا۔ دَورانِ سفر میں ایک ایسی جگہ قافلے نے پڑاؤ کیا جہاں مقامی لوگوں نے بتایا کہ راتوں کو درندے آتے ہیں۔ ابو لہب نے اپنے ساتھی اہلِ قریش سے کہا کہ میرے بیٹے کی حفاظت کا کچھ انتظام کر و، کیونکہ مجھے محمد (صلی اللہ علیہ وسلم) کی بددعا کا خو ف ہے۔ اس پر قافلے والوں نے عُتَیبہ کے گرد ہر طرف اپنے اُونٹ بٹھا دیے اور پڑ کر سو رہے۔ رات کو ایک شیر آیا اور اونٹوں کے حلقے میں سے گزر کر اُس نے عتیبہ کو پھاڑ کھایا۔ (الاستِعیاب لابن عبد الَبّر، الاِ صا بہ بن حَجْر، دلائل النبوۃ لا بی نعیم الاصفہانی، روض الانف للسُّہَیلی۔ روایات میں یہ اختلاف ہے کہ بعض راوی طلاق کے معاملے کو اعلانِ نبوّت کے بعد کا واقعہ بیان کرتے ہیں اور بعض کہتے ہیں کہ یہ تَبَّتْ یَدَآ اَبِیْ لَھَبٍ کے نزول کے بعد پیش آیا تھا۔ اس امر میں بھی اختلاف ہے کہ یہ ابو لہب کا لڑکا عُتْبہ تھا یا عُتَیبہ ۔ لیکن یہ بات ثابت ہے کہ فتح مکّہ کے بعد عُتبَہ نے اسلام قبول کر کے حضورؐ کے دست مبارک پر بیعت کی۔ اس لیے صحیح بات یہی ہے کہ یہ لڑکا عُتَیبہ تھا)۔ اُس کے خبثِ نفس کا یہ حال تھا کہ جب رسول اللہ صلی اللہ علیہ وسلم کے صاحبزادے حضرت قاسم کے بعد دوسرے صاحبزادے حضرت عبد اللہ کا بھی انتقال ہوگیا تو یہ اپنے بھتیجے کے غم میں شریک ہونے کے بجائے خوشی خوشی دوڑا ہوا قریش کے سرداروں کے پاس پہنچا اور اُن کو خبر دی کہ لو آج محمد (صلی اللہ علیہ وسلم) بے نام و نشان ہو گئے۔ اُس کی اِس حرکت کا ذکر ہم سورۂ کوثر کی تفسیر میں کر چکے ہیں۔ رسول اللہ صلی اللہ علیہ وسلم جہاں جہاں بھی اسلام کی دعوت دینے کے لیے تشریف لے جاتے ، یہ آپؐ کے پیچھے پیچھے جاتا اور لوگوں کو آپؐ کی بات سننے سے روکتا۔ رَبِیعہؓ بن عَبّاد الدِّیْلیِ بیان کرتے ہیں کہ میں نو عمر تھا جب اپنے باپ کے ساتھ ذوالمجاز کے بازار میں گیا۔ وہاں میں نے رسول اللہ صلی اللہ علیہ وسلم کو دیکھا آپؐ کہہ رہے تھے، ” لوگوٗ کہو اللہ کے سوا کوئی معبود نہیں ہے، فلاح پاؤگے۔“ اور آپؐ کے پیچھے پیچھے ایک شخص کہتا جا رہا تھا کہ ”یہ جھوٹا ہے ، دین آبائی سے پھر گیا ہے۔“ میں نے پوچھا یہ کون شخص ہے؟ لوگوں نے کہا یہ ان کا چچا ابو لہب ہے(مُسند احمد، بَیْہَقِی)۔ دوسری روایت انہی حضرت ربیعہؓ سے یہ ہے کہ میں نے رسول اللہ صلی اللہ علیہ وسلم کو دیکھا کہ آپؐ ایک ایک قبیلے کے پڑاؤ پر جاتے ہیں اور فرماتے ہیں ” اے بنی فلاں، میں تمہاری طرف اللہ کا رسول ہوں۔ تمہیں ہدایت کرتا ہوں کہ صرف اللہ کی عبادت کرو اور اس کے ساتھ کسی کو شریک نہ کرو۔ تم میری تصدیق کرو اور میرا ساتھ دو تا کہ میں وہ کام پورا کروں جس کے لیے اللہ نے مجھے بھیجا ہے۔“ آپؐ کے پیچھے پیچھے ایک اور شخص آتا ہے اور وہ کہتا ہے کہ ”اے بنی فلاں، یہ تم کو لات اور عُزّیٰ سے پھیر کر اُس بدعت اور گمراہی کی طرف لے جانا چاہتا ہے جسے یہ لے کر آیا ہے۔ اس کی بات پرگز نہ مانو اور اس کی پیروی نہ کرو۔“ میں نے اپنے باپ سے پوچھا یہ کون ہے۔ انہوں نے کہا یہ ان کا چچا ابو لہب ہے(مُسند احمد، طَبَرانی)۔ طارقؓ بن عبد اللہ المُحارِبی کی روایت بھی اسی سے ملتی جلتی ہے ۔ وہ کہتے ہیں میں نے ذوالمجاز کے بازار میں دیکھا کہ رسول اللہ صلی اللہ علیہ وسلم لوگوں سے کہتے جاتے ہیں کہ ”لوگو، لا الٰہ الا اللہ کہو، فلاح پاؤ گے۔“ اور پیچھے ایک شخص ہے جو آپؐ کوپتھر مار رہا ہے، یہاں تک کہ آپؐ کی ایڑیاں خون سے تر ہو گئی ہیں، اور وہ کہتا جاتا ہے کہ ” یہ جھوٹا ہے، اس کی بات نہ مانو۔“ میں نے لوگوں سے پوچھا یہ کون ہے ؟ لوگوں نے کہا یہ ان کا چچا ابولہب ہے(ترمذی)۔ نبوت کے ساتویں سال جب قریش کے تمام خاندانوں نے بنی ہاشم اور بنی مطّلب کا معاشرتی اور معاشی مقاطعہ کیا اور یہ دونوں خاندان رسول اللہ صلی اللہ علیہ وسلم کی حمایت پر ثابت قدم رہتے ہوئے شِعْبِ ابی طالب میں محصور ہو گئے تو تنہا یہی ابولہب تھا جس نے اپنے خاندان کا ساتھ دینے کے بجائے کفّارِ قریش کا ساتھ دیا۔ یہ مقاطعہ تین سال تک جاری رہا اور اس دوران میں بنی ہاشم اور بنی المُطَّلِب پر فاقوں کی نوبت آگئی۔ مگر ابو لہب کا حال یہ تھا کہ جب مکّہ میں کوئی تجارتی قافلہ آتا اور شِعبِ ابی طالب کے محصورین میں سے کوئی خوراک کا سامان خریدنے کے لیے اس کے پاس جاتا تو یہ تاجروں سے پکار کر کہتا کہ اِن سے اتنی قیمت مانگو کہ یہ خرید نہ سکیں ، تمہیں جو خسارہ بھی ہو گا اسے میں پورا کروں گا۔ چنانچہ وہ بے تحاشا قیمت طلب کرتے اور خریدار بیچارہ اپنے بھوک سے تڑپتے ہوئے بال بچوں کے پاس خالی ہاتھ پلٹ جاتا۔ پھر ابولہب اُنہی تاجروں سے وہی چیزیں بازار کے بھاؤ خرید لیتا(ابن سعد و ابن ہِشام)۔ یہ اس شخص کی حرکات تھیں جن کی بنا پر اس سورۃ میں نام لے کر اس کی مذمت کی گئی۔ خاص طور پر اس کی ضرورت اس لیے تھی کہ مکّہ سے باہر کے اہلِ عرب جو حج کے لیے آتے، یا مختلف مقامات پر لگنے والے بازاروں میں جمع ہوتے، اُن کے سامنے جب رسول اللہ صلی اللہ علیہ وسلم کا اپنا چچا آپؐ کے پیچھے لگ کر آپؐ کی مخالفت کرتا، تو وہ عرب کی معروف روایات کے لحاظ سے یہ بات خلافِ توقع سمجھتے تھے کہ کوئی چچا بلا وجہ دوسروں کے سامنے خود اپنے بھتیجے کو برا بھلا کہے اور اُسے پتھر مارے اور اس پر الزام تراشیاں کرے۔ اِس وجہ سے وہ ابو لہب کی بات سے متأثر ہو کر رسول اللہ صلی اللہ علیہ وسلم کے بارے میں شک میں پڑ جاتے ۔ مگر جب یہ سورۃ نازل ہوئی اور ابو لہب نے غصّے میں بپھر کر اَول فَول بکنا شروع کر دیا تو لوگوں کو معلوم ہو گیا کہ رسول اللہ صلی اللہ علیہ وسلم کی مخالفت میں اِس شخص کا قول قابلِ اعتبار نہیں ہے، کیونکہ یہ اپنے بھتیجے کی دشمنی میں دیوانہ ہو رہا ہے۔ اِس کے علاوہ نام لے کر جب آپؐ کے چچا کی مذمت کی گئی تو لوگوں کی یہ توقع ہمیشہ کے لیے ختم ہو گئی کہ رسول اللہ صلی اللہ علیہ وسلم دین کے معاملہ میں کسی کا لحاظ کر کے کوئی مداہنت برت سکتے ہیں۔ جب علی الاعلان رسول کے اپنے چچا کی خبر لے ڈالی گئی تو لوگ سمجھ گئے کہ یہاں کسی لاگ لپیٹ کی گنجائش نہیں ہے۔ غیر اپنا ہو سکتا ہے اگر ایمان لے آئے، اور اپنا غیر ہو جاتا ہے اگر کفر کرے۔ اِس معاملہ میں فلاں ابنِ فلاں کوئی چیز نہیں ہے۔ ','سید ابو اعلیٰ مودودیؒ -  تفہیم القرآن');
INSERT INTO "chapters_ur" VALUES(112,'اخلاص',NULL,' نام: اَلاِخْلَاص اِس سورہ کا محض نام ہی نہیں ہے بلکہ اِس کے مضمون کا عنوان بھی ہے ، کیونکہ اس میں خالص توحید بیان کی گئی ہے ۔ قرآن مجید کی دوسری سورتوں میں تو بالعموم کسی ایسے لفظ کو اُن کا نام قرار دیا گیا ہے جو اُن میں وارد ہوا ہو، لیکن اِس سورہ میں لفظ اِخلاص کہیں وارد نہیں ہوا ہے۔ اِس کو یہ نام اِس کے معنی کے لحاظ سے دیا گیا ہے۔ جو شخص بھی اِس کو سمجھ کر اِس کی تعلیم پر ایمان لے آئے گا وہ شرک سے خلاصی پا جائے گا۔ زمانۂ نزول: اِس کے مکّی اور مدنی ہونے میں اختلاف ہے ، اور یہ اختلاف اُن روایات کی بنا پر ہے جو اِس کے سببِ نزول کے بارے میں منقول ہوئی ہیں۔ ذیل میں ہم اُن کو سلسلہ وار درج کرتے ہیں۔ (۱) حضرت عبدؓ اللہ بن مسعود کی روایت ہے کہ قریش کے لوگوں نے رسول اللہ صلی اللہ علیہ وسلم سے کہا کہ اپنے ربّ کا نَسب ہمیں بتائیے۔ اس پر یہ سورہ نازل ہوئی (طَبَرانی)۔ (۲) ابو العالیہ نے حضرت اُبَیّ بن کعب ؓ کے حوالہ سے بیان کیا ہے کہ مشرکین نے رسول اللہ صلی اللہ علیہ وسلم سے کہا کہ اپنے رب کا نسب ہمیں بتائیے ، اس پر اللہ تعالیٰ نے یہ سورۃ نازل فرمائی( مُسند احمد، ابن ابی حاتم، ابن جریر، تِرْمِذی، بخاری فی التاریخ، ابن المُنذِر، حاکم ، بیہقی)۔ تِرْمِذی نے اِسی مضمون کی ایک روایت ابو العالیہ سے نقل کی ہے جس میں حضرت اُبَیّ بن کعب ؓ کا حوالہ نہیں ہے اور اُسے صحیح تر کہا ہے۔ (۳) حضرت جابرؓ بن عبد اللہ کا بیان ہے کہ ایک اَعرابی نے (اور بعض روایات میں ہے کہ لوگوں نے) نبی صلی اللہ علیہ وسلم سے کہا کہ اپنے رب کا نسب ہمیں بتائیے، اس پر اللہ تعالیٰ نے یہ سورت نازل فرمائی (ابویَعلیٰ، ابن جریر، ابن المُنذِر، طَبَرانیفی الاَوسط، بَیْہقی، ابو نُعَیم فی الحِیْلہ)۔ (۴) عِکْرِمَہ نے ابن عباسؓ سے روایت نقل کی ہے کہ یہودیوں کا ایک گروہ رسول اللہ صلی اللہ علیہ وسلم کی خدمت میں حاضر ہوا جس میں کَعب بن اشرف اور حُیَیّ بن اَخْطَب وغیرہ شامل تھے اور اُنہوں نے کہا ”اے محمد(صلی اللہ علیہ وسلم) ہمیں بتائیے کہ آپ کا وہ ربّ کیسا ہے جس نے آپ کو بھیجا ہے۔“ اس پر اللہ تعالیٰ نے یہ سورت نازل فرمائی (ابن ابی حاتم ، ابن عَدِی، بیہقی فی الاسماء و الصفات)۔ اِن کے علاوہ مزید چند روایات ابن تَیمیہ نے اپنی تفسیر سورۂ اخلاص میں نقل کی ہیں جو یہ ہیں: (۵) حضرت اَنَس کا بیان ہے کہ خَیبر کے کچھ یہودی رسول اللہ صلی اللہ علیہ وسلم کی خدمت میں حاضر ہوئے اور انہوں نے کہا ”اے ابو القاسمؐ ، اللہ نے ملائکہ کو نورِ حجاب سے ، آدم کو مٹی کے سڑے ہوئے گارے سے، ابلیس کو آگ کے شعلے سے ، آسمان کو دھوئیں سے ،اور زمین کو پانی کے جھاگ سے بنایا، اب ہمیں اپنے ربّ کے متعلق بتائیے (کہ وہ کس چیز سے بنا ہے)۔“ رسول اللہ صلی اللہ علیہ وسلم نے اس بات کا کوئی جواب نہ دیا ۔ پھر جبریل ؑ آئے اور انہوں نے کہا اے محمدؐ، ان سے کہیے ھُوَ اللہُ اَحَدٌ ۔۔۔۔۔۔۔ (۶) عامِر بن الطُّفَیل نے حضورؐ سے کہا ” اے محمدؐ، آپ کس چیز کی طرف ہمیں بلاتے ہیں ؟“ آپؐ نے فرمایا اللہ کی طرف۔ عامر نے کہا ،” اچھا تو اُس کی کیفیت مجھے بتائیے۔ وہ سونے سے بنا ہوا ہے یا چاندی سے یا لوہے سے؟“ اس پر یہ سورۃ نازل ہوئی۔ (۷) ضَحّاک اور قَتادہ اور مُقاتِل کا بیان ہے کہ یہودیوں کے کچھ علماء حضورؐ کے پاس آئے اور انہوں نے کہا ”اے محمدؐ، اپنے رب کی کیفیت ہمیں بتائیے، شاید کہ ہم آپ پر ایمان لے آئیں۔ اللہ نے اپنی صفت توراۃ میں نازل کی ہے۔ آپ بتائیے کہ وہ کس چیز سے بنا ہے؟ کس جنس سے ہے ؟ سونے سے بنا ہے یا تانبے سے، یا پیتل سے، یا لوہے سے ، یا چاندی سے؟ اور کیا وہ کھاتا ہے اور پیتا ہے؟ اور کس سے اُ س نے دنیا وراثت میں پائی ہے اور اُس کے بعد کون اُس کا وارث ہو گا؟“ اِس پر اللہ تعالیٰ نے یہ سورت نازل فرمائی۔ (۸) ابن عباسؓ کی روایت ہے کہ نَجران کے عیسائیوں کا ایک وفد سات پادریوں کے ساتھ نبی صلی اللہ علیہ وسلم کی خدمت میں حاضر ہوا اور اُس نے حضورؐ سے کہا ”ہمیں بتائیے آپ کا ربّ کیسا ہے ، کس چیز کا بنا ہے؟“ آپ نے فرمایا میرا ربّ کسی چیز سے نہیں بنا ہے۔ وہ تمام اشیاء سے جدا ہے۔ اِس پر اللہ تعالیٰ نے یہ سورت نازل فرمائی۔ اِن روایات سے معلوم ہوتا ہے کہ مختلف مواقع پر مختلف لوگوں نے رسول اللہ صلی اللہ علیہ وسلم سے اُس معبود کی ماہیت اور کیفیت دریافت کی تھی جس کی بندگی و عبادت کی طرف آپؐ لوگوں کو دعوت دے رہے تھے ، اور ہر موقع پر آپؐ نے اللہ تعالیٰ کے حکم سے اُن کو جواب میں یہی سورت سنائی تھی۔ سب سے پہلے یہ سوال مکّہ میں قریش کے مشرکین نے آپ سے کیا اور اس کے جواب میں یہ سورت نازل ہوئی۔ اس کے بعد مدینۂ طیّبہ میں کبھی یہودیوں نے ، کبھی عیسائیوں نے ، اور کبھی عرب کے دوسرے لوگوں نے حضورؐ سے اِسی نوعیت کے سوالات کیے اور ہر مرتبہ اللہ تعالیٰ کی طرف سے اشارہ ہوا کہ جواب میں یہی سورت آپ اُن کو سنا دیں۔ اِن روایات میں سے ہر ایک میں یہ جو کہا گیا ہے کہ اِس موقع پر یہ سورت نازل ہوئی تھی ، اِس سے کسی کو یہ خیال نہ ہونا چاہیے کہ یہ سب روایتیں باہم متضاد ہیں۔ اصل بات یہ ہے کہ کسی مسئلے کے بارے میں اگر پہلے سے کوئی آیت یا سورۃ نازل شدہ موجود ہوتی تھی تو بعد میں جب کبھی حضورؐ کے سامنے وہی مسئلہ پیش کیا جاتا، اللہ تعالیٰ کی طرف سے ہدایت آجاتی تھی کہ اس کا جواب فلاں آیت یا سورۃ میں ہے، یا اِس کے جواب میں وہ آیت یا سورۃ لوگوں کو پڑھ کر سنا دی جائے۔ احادیث کے راوی اس چیز کو یوں بیان فرماتے ہیں کہ جب فلاں معاملہ پیش آیا، یا فلاں سوال کیا گیا تو یہ آیت یا سورۃ نازل ہوئی۔ اِس کو تکرار ِ نزول سے بھی تعبیر کیا جاتا ہے، یعنی ایک آیت یا سورۃ کا کئی مرتبہ نازل ہونا۔ پس صحیح بات یہ ہے کہ یہ سورۃ دراصل مکّی ہے بلکہ اِس کے مضمون پر غور کرنے سے محسوس ہوتا ہے کہ یہ مکّہ کے بھی ابتدائی دور میں نازل ہوئی ہے جب اللہ تعالیٰ کی ذات و صفات کے بیان میں قرآن کی مفصل آیات ابھی نازل نہیں ہوئی تھیں، اور رسول اللہ صلی اللہ علیہ وسلم کی دعوت الی اللہ کو سن کر لوگ یہ معلوم کرنا چاہتے تھے کہ آخر آپ کا وہ رب ہے کیسا جس کی بندگی و عبادت کی طرف آپ لوگو ں کو بلا رہے ہیں۔ اِس کے بالکل ابتدائی دور کی نازل شدہ سورت ہونے کا ایک ثبوت یہ بھی ہے کہ مکّہ میں جب حضرت بلال کا آقا اُمَیَّہ بن خَلَف اُن کو دھوپ میں تپتی ہوئی ریت پر لٹا کر ایک بڑا سا پتھر اُن کی چھاتی پر رکھ دیتا تھا تو وہ اَحَدٌ اَحَدٌ پکارتے تھے۔ یہ لفظ اَحَد اِسی سورہ سے ماخوذ تھا۔ موضوع اور مضمون : شانِ نزول کے بارے میں جو روایات اوپر درج کی گئی ہیں اُن پر ایک نگاہ ڈالنے سے معلوم ہو جاتا ہے کہ جب رسول اللہ صلی اللہ علیہ وسلم توحید کی دعوت لے کراُٹھے تھے اُس وقت دُنیا کے مذہبی تصورات کیا تھے۔ بت پرست مشرکین اُن خداؤں کو پوج رہے تھے جو لکڑی ، پتھر، سونے ، چاندی وغیرہ مختلف چیزوں کے بنے ہوئے تھے۔ شکل، صورت اور جسم رکھتے تھے۔ دیویوں اور دیوتاؤں کی باقاعدہ نسل چلتی تھی۔ کوئی دیوی بے شوہر نہ تھے اور کوئی دیوتا بے زوجہ نہ تھا۔ اُن کو کھانے پینے کی ضرورت بھی لاحق ہوتی تھی اور اُن کے پرستار اُن کے لیے اس کا انتظام کرتے تھے۔ مشرکین کی ایک بڑی تعداد اس بات کی قائل تھی کہ خدا انسانی شکل میں ظہور کرتا ہے اور کچھ لوگ اُس کے اَوتار ہوتے ہیں۔ عیسائی اگرچہ ایک خدا کو ماننے کے مدعی تھے، مگر اُن کا خدا بھی کم از کم ایک بیٹا تو رکھتا ہی تھا ، اور باپ بیٹے کے ساتھ خدائی میں روح القُدس کو بھی حصہ دار ہونے کا شرف حاصل تھا۔ حتیٰ کہ خدا کی ماں بھی ہوتی تھی اور اس کی ساس بھی ۔ یہودی بھی ایک خدا کو ماننے کا دعویٰ کرتے تھے، مگر اُن کا خدا بھی مادّیت اور جسمانیت اور دوسری انسانی صفات سے خالی نہ تھا۔ وہ ٹہلتا تھا۔ انسانی شکل میں نمودار ہوتا تھا۔ اپنے کسی بندے سے کُشتی بھی لڑ لیتا تھا۔ اور ایک عدد بیٹے (عُزَیر) کا باپ بھی تھا۔ اِن مذہبی گروہوں کے علاوہ مجوسی آتش پرست تھے اور صابِئی ستارہ پرست۔ اس حالت میں جب اللہ وحدہٗ لاشریک کو ماننے کی دعوت لوگوں کو دی گئی تو اُن کے ذہن میں یہ سوالات پیدا ہونا ایک لازمی امر تھا کہ وہ ربّ ہے کس قسم کا جسے تمام ارباب اور معبودوں کو چھوڑ کر تنہا ایک ہی ربّ اور معبود تسلیم کرنے کی دعوت دی جارہی ہے۔ قرآن مجید کا یہ اعجاز ہے کہ اُس نے اِن سوالات کا جواب چند الفاظ میں دے کر اللہ کی ہستی کا ایسا واضح تصور پیش کر دیا جو تمام مشرکانہ تصورات کا قلع قمع کردیتا ہے اور اُس کی ذات کے ساتھ مخلوقات کی صفات میں سے کسی صفت کی آلودگی کے لیے کوئی گنجائش باقی نہیں رہنے دیتا۔ اہمیت اور فضیلت : یہی وجہ ہے کہ رسول اللہ صلی اللہ علیہ وسلم کی نگاہ میں اِس سورت کی بڑی عظمت تھی اور آپؐ مختلف طریقوں سے مسلمانوں کو اِس کی اہمیت محسوس کراتے تھے، تاکہ وہ کثرت سے اِس کو پڑھیں اور عام الناس میں اِسے پھیلائیں، کیونکہ یہ اسلام کے اولین بنیادی عقیدے(توحید) کو چار ایسے مختصر فقروں میں بیان کر دیتی ہے جو فوراً انسان کے ذہن نشین ہو جاتے ہیں اور آسانی سے زبانوں پر چڑھ جاتے ہیں۔ احادیث میں کثرت سے یہ روایات بیان ہوئی ہیں کہ حضورؐ نے مختلف مواقع پر مختلف طریقوں سے لوگوں کو بتایا کہ یہ سورت ایک تہائی قرآن کے برابر ہے۔ بخاری، مسلم، ابوداؤد، نَسائی ، تِرمِذی، ابن ماجہ، مُسند احمد، طَبَرانی، وغیرہ میں اِس مضمون کی متعدد احادیث ابو سعید خُدری، ابو ہریرہ، ابو ایوب انصاری، ابو الدَّ رْدا، مُعاذ بن جَبَل، جابر بن عبداللہ، اُبَیّ بن کَعب، امّ کُلثوم بنت عُقْبَہ بن ابی مُعَیط، ابنِ عمر ، ابن مسعود، قَتادہ بن النُعمان، اَنَس بن مالک اور ابو مسعود رضی اللہ تعالیٰ عنہم اجمعین سے منقول ہوئی ہیں مفسّرین نے حضورؐ کے اِس ارشاد کی بہت سی توجیہات بیان کی ہیں۔ مگر ہمارے نزدیک سیدھی اور صاف بات یہ ہے کہ قرآن مجید جس دین کو پیش کرتا ہے اُس کی بنیا د تین عقیدے ہیں۔ ایک توحید۔ دوسرے رسالت اور تیسرے آخرت۔ یہ سورۃ چونکہ خالص توحید کو بیان کرتی ہے اس لیے رسول اللہ صلی اللہ علیہ وسلم نے اس کو ایک تہائی قرآن کے برابر قرار دیا۔ حضرت عائشہؓ کی یہ روایت بخاری و مسلم اور بعض دوسری کتبِ حدیث میں نقل ہوئی ہے کہ رسول اللہ صلی اللہ علیہ وسلم نے ایک صاحب کو ایک مُہم پر سردار بنا کر بھیجا اور اس پورے سفر کے دوران میں اُن کا مستقل طریقہ یہ رہا کہ ہر نماز میں وہ قُلْ ھُوَ اللہُ اَحَدٌo پر قرأت ختم کرتے تھے۔ واپسی پر اُن کے ساتھیوں نے حضورؐ سے اِس کا ذکر کیا ۔ آپ نے فرمایا اُن سے پوچھو کہ وہ ایسا کیوں کرتے تھے ۔ اُن کے پوچھا گیا تو انہوں نے کہا کہ اس میں رحمان کی صفت بیان کی گئی ہے اس لیے اس کا پڑھنا مجھے بہت محبوب ہے۔ حضورؐ نے یہ بات سُنی تو لوگوں سے فرمایا اخبرو ہ ان اللہ تعالیٰ یحبُّہٗ ”اُن کو خبر دے دو کہ اللہ تعالیٰ اُنہیں محبوب رکھتا ہے۔“ اِسی سے ملتا جلتا واقعہ بخاری میں حضرت اَنَس سے مَروی ہے ۔ وہ فرماتے ہیں کہ انصار میں سے ایک صاحب مسجدِ قُبا میں نماز پڑھاتے تھے اور اُن کا طریقہ یہ تھا کہ ہر رکعت میں پہلے قُلْ ھُوَ اللہُ اَحَدٌ o پڑھتے، پھر کوئی اور سورت تلاوت کرتے ۔ لوگوں نے اس پر اعتراض کیا اور ان سے کہا یہ تم کیا کرتے ہو کہ قُلْ ھُوَ اللہُ پڑھنے کے بعد اسے کافی نہ سمجھ کر کوئی اور سورت بھی اُس کے ساتھ ملا لیتے ہو؟ یہ ٹھیک نہیں ہے۔ یا تو صرف اِسی کو پڑھو ، اور یا اِسے چھوڑ کر کوئی اور سورت پڑھو۔ اُنہوں نے کہا میں اِسے نہیں چھوڑ سکتا، تم چاہو تو میں تمہیں نماز پڑھاؤں ورنہ امامت چھوڑ دوں۔ لیکن لوگ اُن کی جگہ کسی اور کو امام بنانا بھی پسند نہیں کرتے تھے۔ آخر کار معاملہ حضورؐ کے سامنے پیش کیا گیا۔ آپؐ نے ان سے پوچھا کہ تمہارے ساتھی جو کچھ چاہتے ہیں اُسے قبول کر نے میں تم کو کیا امر مانع ہے؟ تمہیں ہر رکعت میں یہ سورت پڑھنے پر کس چیز نے آمادہ کیا؟ انہوں نے عرض کیا کہ مجھے اِس سے بہت محبت ہے۔ آپؐ نے فرمایا حُبُّکَ اِیَّاھَا اَدْخلک الجَنَّۃَ ” اِس سورت سے تمہاری محبت نے تمہیں جنّت میں داخل کر دیا۔“ ','سید ابو اعلیٰ مودودیؒ -  تفہیم القرآن');
INSERT INTO "chapters_ur" VALUES(113,'صبح',NULL,' نام: اگرچہ قرآن مجید کی یہ آخری دو سورتیں بجائے خود الگ الگ ہیں، اور مُصْحَف میں الگ ناموں ہی سے لکھی ہوئی ہیں ، لیکن اِن کے درمیان باہم اتنا گہرا تعلق ہے، اور اِن کے مضامین ایک دوسرے سے اِتنی قریبی مناسبت رکھتے ہیں کہ اِن کا ایک مشترک نام”مُعَوِّذَتَیْن“ (پناہ مانگنے والی دو سورتیں) رکھا گیا ہے۔ امام بَیْہَقی نے دلائلِ نبوت میں لکھا ہے کہ یہ نازل بھی ایک ساتھ ہی ہوئی ہیں، اِسی وجہ سے دونوں کا مجموعی نام مُعَوِّذَتَیْن ہے۔ ہم یہاں دونوں پر ایک ہی دیباچہ لکھ رہے ہیں کیونکہ ان سے متعلقہ مسائل و مباحث بالکل یکساں ہیں۔ البتہ آگے ان کی ترجمانی و تفسیر الگ الگ کی جائے گی۔ زمانۂ نزول: حضرت حسن بصری، عِکْرِمہ ، عطاء اور جابر بن زید کہتے ہیں کہ یہ سورتیں مکّی ہیں۔ حضرت عبداللہ بن عباسؓ سے بھی ایک روایت یہی ہے۔ مگر اُن سے دوسری روایت یہ ہے کہ یہ مدنی ہیں اور یہی قول حضرت عبداللہ بن زبیر ؓ قَتادہ کا بھی ہے۔ اِس دوسرے قول کو جو روایات تقویت پہنچاتی ہیں ان میں سے ایک مسلم،تِرمذی، نَسائی اور مُسندِ امام احمد بن حنبل میں حضرت عُقْبَہؓ بن عامِر کی یہ حدیث ہے کہ رسول اللہ صلی اللہ علیہ وسلّم نے ایک روز مجھ سے فرمایا المُ تراٰیاتٍ اُنزِلَت اللیلۃ، لم یُرَمثلھن، اَعُوّذُ بِرَبّ الْفَلَقِ، اَعُوّذُ بِرَبِّ النَّاسِ۔ ”تمہیں کچھ پتہ ہے کہ آج رات مجھ پر کیسی آیات نازل ہوئی ہیں ؟ یہ بے مثل آیات ہیں ۔ اعوذ بربّ الفلق اور اعوذ بربّ النّاس “۔ یہ حدیث اس بنا پر اِن سورتوں کے مدنی ہونے کہ دلیل ہے کہ حضرت عُقْبہ ؓ بن عامر ہجرت کے بعد مدینہ طیبہ میں ایمان لائے تھے، جیسا کہ ابو داوٴد اور نسائی نے خود اُن کے اپنے بیان سے نقل کیا ہے۔ دوسری روایات جو اِس قول کی تقویت کی موجب نبی ہیں وہ ابن سعد، محیُّ السُّنَّہ بَغَوِی، امام نَسَفِی، امام بَیْہقَیِ، حافظ ابن حَجرَ، حافظ بدر الدین عَینی، عَبْد بن حُمیَد و غَیر ہم کی نقل کردہ یہ روایات ہیں کہ جب مدینے میں یہود نے رسول اللہ صلی اللہ علیہ وسلّم پر جادو کیا تھا اور اس کے اثر سے حضورؐ بیمار ہوگئے تھے اس وقت یہ سورتیں نازل ہوئی تھیں۔ ابن سعد نے واقِدی کے حوالہ سے بیان کیا ہے کہ یہ سن ۷ ہجری کا واقعہ ہے۔ اِسی بنا پر سُفیان بن عُیَیْنَہ نے بھی اِن سورتوں کو مدنہ کہا ہے۔ لیکن جیسا کہ ہم سورہٴ اخلاص کے دیباچے میں بیان کرچکے ہیں ، کسی سورۃ یا آیت کے متعلق جب یہ کہا جاتا ہے کہ وہ فلاں موقع پر نازل ہوئی تھی تو اس کا مطلب لازماً یہی نہیں ہوتا کہ وہ پہلی مرتبہ اُسی موقع پر نازل ہوئی تھی، بلکہ بعض اوقات ایسا ہوا ہے کہ ایک سورت یا آیت پہلے نازل ہوچکی ہوتی تھی، اور پھر کوئی خاص واقعہ یا صورتِ حال پیش آنے پر اللہ تعالیٰ کی طرف سے اُسی کی طرف دوبارہ بلکہ کبھی کبھی بار بار حضور ؐ کو توجہ دلائی جاتی تھی۔ ہمارے نزدیک ایسا ہی معاملہ مُعَوِّذَتَیْن کا بھی ہے۔ اِن کا مضمون صاف بتارہا ہے کہ یہ ابتداءً مکّہ میں اُس وقت نازل ہوئی ہوں گی جب وہاں حضور ؐ کی مخالفت خوب زور پکڑ چکی تھی۔ بعد میں جب مدینہ طیبہ میں منافقین، یہود اور مشرکین کی مخالفت کے طوفان اٹھے تو حضورؐ کو پھر اِنہی دونوں سورتوں کے پڑھنے کی تلقین کی گئی جیسا کہ حضرت عقبہؓ بن عامر کی مندرجہٴ بالا روایت میں ذکر آیا ہے۔ اسی کے بعد جب آپ پر جادو کیا گیا اور آپ کی علالتِ مزاج نے شدّت اختیار کی تو اللہ کے حکم سے جبریل علیہ السلام نے آکر پھر یہی سورتیں پڑھنے کی آپ کو ہدایت کی۔ اس لیے ہمارے نزدیک اُن مفسّرین کا بیان ہی زیادہ معتبر ہے جو اِن دونوں سورتوں کو مکّی قرار دیتے ہیں۔ جادو کے معاملہ کے ساتھ اِن کو مخصوص سمجھنے میں تو یہ امر بھی مانع ہے کہ اُس کے ساتھ صرف سورہٴ فلق کی صرف ایک آیت وَمِنْ شَرِّ النَّفّٰثٰتِ فِی الْعُقَدِ ہی تعلق رکھتی ہے، سورہٴ فَلَق کی باقی آیات اور پوری سورہٴ الناس کا اِس معاملہ سے براہِ راست کوئی تعلق نہیں ہے۔ موضوع اور مضمون : مکہٴ معظمہ میں یہ دونوں سورتیں جن حالات میں نازل ہوئی تھیں وہ یہ تھے کہ اسلام کی دعوت شروع ہوتے ہی ایسا محسوس ہونے لگا تھا کہ رسول اللہ صلی اللہ علیہ وسلّم نے گویا بِھڑوں کے چھتّے میں ہاتھ ڈال دیا ہے۔ جوں جوں آپ کی دعوت پھیلتی گئی، کفّارِ قریش کی مخالفت بھی شدید ہوتی چلی گئی۔ جب تک اُنہیں یہ امید رہی کہ شاید وہ کسی طرح کی سود سے بازی کرکے، یا بہلا پُھسلاکر آپ کو اس کام سے باز رکھ سکیں گے، اُس وقت تک تو پھر بھی عَناد کی شدّت میں کچھ کمی رہی۔ لیکن جب حضور ؐ نے اُن کو اس طرف سے بالکل مایوس کردیا کہ آپ ان کےساتھ دین کے معاملہ میں کوئی مصالحت کرنے پر آمادہ ہوسکیں گے، اور سورہٴ کافرون میں صاف صاف اُن سے کہہ دیا گیا کہ جن کی بندگی تم کرتے ہو اُن کی بندگی کرنے والا میں نہیں ہوں، اور جس کی بندگی میں کرتا ہوں اُس کی بندگی کرنے والے تم نہیں ہو، اس لیے میرا راستہ الگ ہے اور تمہارا راستہ الگ، تو کفار کی دشمنی اپنے پورے عروج پر پہنچ گئی۔ خصوصیت کے ساتھ جن خاندانوں کے افراد (مردوں یا عورتوں، لڑکوں یا لڑکیوں) نے اسلام قبول کرلیا تھا ان کے دلوں میں تو حضور کے خلاف ہر وقت بھٹّیاں سُلگتی رہتی تھیں۔ گھر گھر آپ کو کوسا جارہا تھا۔ خفیہ مشورے کیے جارہے تھے کہ کسی وقت رات کو چھپ کر آپ کو قتل کردیا جائے تاکہ بنی ہاشم کو قاتل کا پتہ نہ چل سکے اور وہ بدلہ نہ لے سکیں ۔ آپ کے خلاف جادو ٹونے کیے جارہے تھے تاکہ آپ یا تو وفات پاجائیں یا سخت بیمار پڑجائیں، یا دیوانے ہوجائیں۔ شیاطینِ جِنّ و انس ہر طرف پھیل گئے تھے تاکہ عوام کے دلوں میں آپ کے خلاف اور آپ کے لائے ہوئے دین اور قرآن کے خلاف کوئی نہ کوئی وسوسہ ڈال دیں جس سے لوگ بدگمان ہوکر آپ سے دور بھاگنے لگیں۔ بہت سے لوگوں میں حسد کی آگ بھی جل رہی تھی، کیونکہ وہ اپنے سوا، یا اپنے قبیلے کے کسی آدمی کے سوا، دوسرے کسی شخص کا چراغ جلتے نہ دیکھ سکتے تھے۔ مثال کے طور پر، ابو جَہل جس بنا پر رسول اللہ صلی اللہ علیہ وسلّم کی مخالفت میں حد سے بڑھتا چلا جاتا تھا اس کی وجہ وہ خود یہ بیان کرتا ہے کہ ”ہمارا اور بنی عبدِ مَناف (یعنی رسول اللہ صلی اللہ علیہ وسلّم کے خاندان) کا باہم مقابلہ تھا۔ اُنہوں نے کھانے کِھلائے تو ہم نے بھی کِھلائے۔ اُنہوں نے لوگوں کو سواریاں دیں تو ہم نے بھی دیں۔ اُنہوں نے عطیّے دیے تو ہم نے بحی دیے۔ یہاں تک کہ وہ اور ہم جب عزت و شرف میں برابر کی ٹکّر ہوگئے تو اب وہ کہتے ہیں کہ ہم میں ایک نبی ہے جس پر آسمان سے وحی اترتی ہے۔ بھلا اس میدان میں ہم کیسے اُن کا مقابلہ کرسکتے ہیں؟ خدا کی قسم ہم ہرگز اِس کو نہ مانیں گے اور نہ اس کی تصدیق کریں گے“ (ابنِ ہشام، جلد اول، ص ۳۳۷ -۳۳۸)۔اِن حالات میں رسول اللہ صلی اللہ علیہ وسلّم سے فرمایا گیا کہ اِن لوگوں سے کہہ دو کہ میں پناہ مانگتا ہوں طلوعِ صبح کے رب کی، تمام مخلوقات کے شر سے، رات کے اندھیرے اور جادوگروں اور جادوگرنیوں کے شر سے، اور ھاسدوں کے شر سے۔ اور ان سے کہہ دو کہ میں پناہ مانگتا ہوں انسانوں کے رب، انسانوں کے بادشاہ اور انسانوں کے معبود کی ہر اُس وسوسہ انداز کے شر سے جو بار بار پلٹ کر آتا ہے اور لوگوں کے دلوں میں وسوسے ڈالتا ہے، خواہ وہ شیاطینِ جن میں سے ہو یا شیاطینِ اِنس میں سے۔ یہ اُسی طرح کی بات ہے جیسی حضرت موسٰی نے اُس وقت فرمائی تھی جب فرعون نے بھرے دربار میں اُن کے قتل کا ارادہ ظاہر کیا تھا کہ اِنِّی عُذْتُ بِرَبِّیْ وَرَبِّکُمْ مِّنْ کُلِّ مُتَکَبِّرٍ لَّا یُوٴمِنُ بِیَوْمِالْحِسَبِ، ”میں نے اپنے اور تمہارے رب کی پناہ لےلی ہے ہر اُس متکبِّر کے مقابلے میں جو روزِ حساب پر ایمان نہیں رکھتا“(المومن، ۲۷) وَانِّیْ عُذْتُ بِرَبِّیْ وَرَبِّکُمْ اَنْ تَرْ جُمُوْنِ، ”اور میں نے اپنے اور تمہارے رب کی پناہ لے لی ہے اِس بات سے کہ تم مجھ پر حملہ آور ہو“۔ (الدُّخان -۲۰)۔دونوں مواقع پر اللہ کے اِن جلیل القدر پیغمبروں کا مقابلہ بڑی بے سروسامانی کی حالت میں بڑے سروسامان اور وسائل و ذرائع اور قوت و شوکت رکھنے والوں سے تھا۔ دونوں مواقع پر وہ طاقت ور دشمنوں کے آگے اپنی دعوتِ حق پر ڈٹ گئے درانحالیکہ اُن کے پاس کوئی مادّی طاقت ایسی نہ تھی جس کے بل پر وہ اُن کا مقابلہ کرسکتے۔ اور دونوں مواقع پر انہوں نے دشمنوں کی دھمکیوں اور خطرناک تدبیروں اور مُعانِدانہ چالوں کو یہ کہہ کر نظر انداز کردیا کہ تمہارے مقابلے میں ہم نے ربّ کائنات کی پناہ لے لی ہے۔ ظاہر ہے کہ یہ اولوالعزمی اور ثابت قدمی وہی شخص دکھا سکتا ہے جس کو یہ یقین ہوکہ اُس رب کی طاقت سب سے بڑی طاقت ہے، اُس کے مقابلے میں دنیا کی ساری طاقتیں ہیچ ہیں، اور اس کی پناہ جسے حاصل ہو اس کا کوئی کچھ نہیں بگاڑسکتا۔ وہی یہ کہہ سکتا ہے کہ میں کلمہٴ حق کے اعلان سے ہرگز نہیں ہٹوں گا، تم جو چاہو کرلو، مجھے اِس کی کوئی پروا نہیں، کیونکہ میں تمہارے اور اپنے اور ساری کائنات کے رب کی پناہ لے چکا ہوں۔ معّوذتین کی قُرآنیت : ان دونوں سورتوں کے موضوع اور مضمون کو سمجھنے کے لیے تو اتنی بحث ہی کافی ہے جو اوپر کی جاچکی ہے۔ لیکن چونکہ حدیث و تفسیر کی کتابوں میں اِن کے متعلق تین ایسے مباحث آگئے ہیں جو دلوں میں شبہات پیدا کرسکتے ہیں، اس لیے ہم اُن کو بھی صاف کردینا ضروری سمجھتے ہیں۔ ان میں سے اوّلین قابلِ توجہ مسئلہ یہ ہے کہ آیا اِن دونوں سورتوں کا قرآنی سورتیں ہونا قطعی طور پر ثابت ہے، یا اس میں کسی شک کی گنجائش ہے؟ یہ سوال اس لیے پیدا ہوا کہ حضرت عبداللہؓ بن مسعود جیسے عظیم المرتبہ صحابی سے متعدد روایتوں میں یہ بات منقول ہوئی ہے کہ وہ اِن دونوں سورتوں کو قرآن کی سورتیں نہیں مانتے تھے اور اپنے مُصْحَف سے اُنہوں نے اِن کو ساقط کردیا تھا۔ امام احمد، بَزّار، طَبَرانی، ابن مَرْ دُوْیَہ، ابو یَعلیٰ، عبداللہ بن احمد بن حَنَبل، حُمَیدی، ابو نُعیم، ابن حِبّان، وغیرہ محدّثین نے مختلف سندوں سے، اور اکثر و بیشتر صحیح سندوں سے یہ بات حضرت ابنِ مسعود سے نقل کی ہے۔ اِن روایات میں نہ صرف یہ کہا گیا ہے کہ اِن سورتوں کو مُصحف سے ساقط کردیتے تھے، بلکہ یہ بھی بیان کیا گیا ہے کہ وہ کہتے تھے ”قرآن کے ساتھ وہ چیزیں نہ ملاوٴ جو قرآن کا جُز نہیں ہیں۔ یہ دونوں قرآن میں شامل نہیں ہیں۔ یہ تو ایک حکم تھا جو بنی صلی اللہ علیہ وسلّم کو دیا گیا تھا کہ آپ اِن الفاظ میں خدا کی پناہ مانگیں“۔ بعض روایات میں اِس پر یہ اضافہ بھی ہے کہ وہ اِن سورتوں کو نماز میں نہیں پڑھتے تھے۔اِن روایات کی بنا پر مخالفینِ اِسلام کو قرآن کے بارے میں یہ شبہات اُبھارنے کا موقع مل گیا کہ معاذاللہ یہ کتاب تحریف سے محفوظ نہیں ہے بلکہ اِس میں جب یہ دوسورتیں ابنِ مسعودؓ جیسے صحابی کے بیان کے مطابق اِلحاقی ہیں تو نہ معلوم اور کیا کیا حذف و اضافے اِس کے اندر ہوئے ہوں گے۔ اِس طعن سے پیچھا چھڑانے کے لیے قاضی ابوبکر الباقِلّانِی اور قاضی عِیاض وغیرہ نے یہ تاویل کی کہ ابنِ مسعودؓ مُعَوِّذتَین کی قرانیت کے منکر نہ تھے بلکہ صرف ان کو مُصْحَف میں درج کرنے سے انکار کرتے تھے، کیونکہ اُن کے نزدیک مُصْحَف میں صرف وہی چیز درج کی جانی چاہیے تھی جس کے ثبت کرنے کی رسول اللہ صلی اللہ علیہ وسلّم نے اجازت دی ہو، اور ابن مسعودؓ تک یہ اطلاع نہیں پہنچی تھی کہ حضورؐ نے اس کی اجازت دی ہے۔ لیکن یہ تاویل درست نہیں ہے، کیونکہ صحیح سندوں کے ساتھ یہ بات ثابت ہے کہ ابن مسعودؓ نے اِن کے قرآنی سورتیں ہونے کا انکار کیا ہے۔ کچھ دوسرے بزرگوں ، مثلاً مام نَوَوی، امام ابن حَزْم اور امام فخرالدین رازی نے سرے سے اِس بات ہی کو جھوٹ اور باطل قرار دیا ہے کہ ابن مسعود ؓ نے ایسی کوئی بات کہی ہے۔ مگر مستند تاریخی حقائق کو بلا سند رد کردینا کوئی علمی طریقہ نہیں ہے۔اب سوال یہ ہے کہ ابن مسعود ؓ کی اِن روایات سے قرآن پر جو طعن وارد ہوتا ہے اس کا صحیح رو کیا ہے؟ اِس سوال کے کئی جواب ہیں جن کو ہم سلسلہ وار درج کرتے ہیں: (۱) حافظ بَزّار نے اپنی مُسند میں ابن مسعودؓ کی یہ روایات نقل کرنے کے بعد لکھا ہے کہ اپنی اِس رائے میں وہ بالکل منفرد ہیں۔ صحابہ میں سے کسی نے بھی اُن کے اِس قول کی تائید نہیں کی ہے۔ (۲) تمام صحابہ کے اتفاق سے خلیفہٴ ثالث سیدنا عثمان رضی اللہ عنہ نے قرآن مجید کے جو نسخے مرتّب کروائے تھے اور خلافتِ اسلامیہ کی طرف سے جن کو دنیائے اسلام کے مراکز میں سرکاری طور پر بھیجا تھا، اُن میں یہ دونوں سورتیں درج تھیں۔ (۳) رسول اللہ صلی اللہ علیہ وسلّم کے عہدمبارک سے آج تک تمام دنیائے اسلام کا جس مُصْحَف پر اجماع ہے اُس میں یہ دونوں سورتیں درج ہیں۔ تنہا عبداللہ بن مسعودؓ کی رائے، اُن کی جلالت قدر کے باوجود، اِس عظیم اِجماع کے مقابلے میں کوئی وزن نہیں رکھتی۔ (۴) رسول اللہ صلی اللہ علیہ وسلّم سے نہایت صحیح و معتبرا حادیث کے مطابق یہ ثابت ہے کہ آپ نے اِن سورتوں کو نماز میں خود پڑھا ہے، دوسروں کو پڑھنے کی ہدایت فرمائی ہے اور قرآن کی سورتوں کی حیثیت ہی سے لوگوں کو اِن کی تعلیم دی ہے۔ مثال کے طور پر ذیل کی احادیث ملاحظہ ہوں: مسلم، احمد، تِرْمِذِی، اور نَسائی کے حوالہ سے حضرت عُقْبہ بن عامِرؓ کی یہ روایت ہم اوپر نقل کرچکے ہیں کہ حضورؐ نے سورہٴ ناس کے متعلق اُن سے یہ فرمایا کہ آج رات یہ آیات مجھ پر نازل ہوئی ہیں۔ نَسائی کی ایک روایت عُقْبہ بن عامِر ؓ سے یہ ہے کہ رسول اللہ صلی اللہ علیہ وسلّم نے یہ دونوں سورتیں صبح کی نماز میں پڑھیں۔ ابن حِبّان نے اِنہی حضرت عُقْبہ سے روایت نقل کی ہے کہ حضورؐ نے اُن سے فرمایا ”اگر ممکن ہو تو تمہاری نمازوں سے اِن دونوں سورتوں کی قراءت چھوٹنے نہ پائے“۔ سعید بن منصور نے حضرت مُعاذ بن حَبَلؓ سے روایت نقل کی ہے کہ حضورؐ نے صبح کی نماز میں یہ دونوں سورتیں پڑھیں۔ امام احمد اپنی مُسند میں صحیح سند کے ساتھ ایک اور صحابی کی یہ روایت لائے ہیں کہ حضورؐ نے اُن سے فرمایا جب تمج نماز پڑھو تو اس میں یہ دونوں سورتیں پڑھا کرو۔ مُسند احمد، ابوداوٴد اور نَسائی میں عُقْبہ بن عامِرؓ کی یہ روایت آئی ہے کہ حضورؐ نے اُن سے فرمایا ”کیا میں دو ایسی سورتیں تمہیں نہ سکھاوٴں جو اُن بہترین سورتوں میں سے ہیں جنہیں لوگ پڑھتے ہیں؟“انہوں نے عرض کیا ضرور یا رسول اللہ۔ اس پر حضورؐ نے ان کو یہی معوّذتین پڑھائیں۔ پھر نماز کھڑی ہوئی تو حضور صلی اللہ علیہ وسلم نے یہی دو سورتیں اس میں بھی پڑھیں۔ اور نماز کے بعد پلٹ کر جب آپ صلی اللہ علیہ وسلم اُن کے پاس سے گزرے تو فرمایا ”اے عقب، کیسا پایا تم نے“؟ اور اس کے بعد اُن کو ہدایت فرمائی کہ جب تم سونے لگو اور جب سو کر اٹھو تو اِن سورتوں کو پڑھا کرو۔مُسند احمد، ابو داود، ترمذی اور نسائی میں عُقبہ بن عامر کی ایک روایت یہ ہے کہ حضور صلی اللہ علیہ وسلم نے ان کو ہر نماز کے بعد معوّ ذات (یعنی قل ہو اللہ احد اور معوّذتین) پڑھنے کی تلقین کی۔ نسائی، ابن مردویہ اور حاکم نے عُقبہ بن عامر کی یہ روایت بھی نقل کی ہے کہ ایک مرتبہ حضور صلی اللہ علیہ وسلم سواری پر چلے جا رہے تھے اور میں آپ کے قدم مبارک پر ہاتھ رکھے ہوئے ساتھ ساتھ چل رہا تھا۔ میں نے عرض کیا مجھے سورہ ہود یا سورہ یوسف سکھا دیجیے۔ فرمایا”اللہ کے نزدیک بندے کے لیے قُلْ اَعُوْذُ بِرَبِّ الْفَلَقِ سے زیادہ نافع کوئی چیز نہیں ہے“۔ عبد اللہ بن عابس الجُہَنِی کی روایت نسائی، بَیْقَہی، لَغَوی اور ابن سعد نے نقل کی ہے کہ حضور صلی اللہ علیہ وسلم نے مجھ سے فرمایا”ابن عابس،کیا میں تمہیں نہ بتاوں کہ پناہ مانگنے والوں نے جتنی چیزوں کے ذریعہ سے اللہ کی پناہ مانگی ہے ان میں سب سے افضل کون سی چیزیں ہیں؟“ میں نے عرض کیا ضرور یا رسول اللہ صلی اللہ علیہ وسلم۔ فرمایا” قُلْ اَعُوْذُ بِرَبِّ الْفَلَقِ اور قُلْ اَعُوْذُ بِرَبِّ النَّاسِ، یہ دونوں سورتیں“ابن مردویہ نے حضرت امّ سلمہ کی روایت نقل کی ہے کہ اللہ کو جو سورتیں سب سے زیادہ پسند ہیں وہ قل اعوذ بربّ الفلق اور قل اعوذ بربّ الناس ہیں۔ یہاں یہ سوال پیدا ہوتا ہے کہ حضرت عبداللہ رضی اللہ تعالیٰ عنہ بن مسعود کو یہ غلط فہمی آخر کیسے لاحق ہوئی کہ یہ دونوں قرآن مجید کی سورتیں نہیں ہیں؟ اِ س کا جواب ہمیں دو روایتوں کو جمع کر کے دیکھنے سے ملتا ہے۔ ایک یہ روایت کہ حضرت عبد اللہ بن مسعود کہتے تھے کہ یہ تو ایک حکم تھا جو رسول اللہ صلی اللہ علیہ وسلم کو دیا گیا تھا کہ آپ اِس طرح تعوّذ کیا کریں۔ دوسری وہ روایت جو کئی مختلف سندوں سے امام بخاری نے صحیح البخاری میں ، امام احمد نے اپنی مُسند میں۔ حافظ ابو بکر الحُمیدی نے اپنی سُند میں، ابو نُعَیم نے اپنی المُسْتَخْرج میں اور نسائی نے اپنی سُنن میں زربن حُبَیش کے حوالے سے تھوڑے تھوڑے لفظی اختلاف کے ساتھ حضرت اُبَیّ بن کعب رضی اللہ تعالیٰ عنہ سے ، جو علم قرآن کے لحاظ سے صحابہ کرام میں ایک ممتاز مقام رکھتے تھے، نقل کی ہے۔ زرین حُبَیش کا بیان ہے کہ میں نے حضرت اُبَیّ رضی اللہ تعالیٰ عنہ سے کہا کہ آپ کے بھائی عبد اللہ رضی اللہ تعالیٰ عنہ بن مسعود ایسا اور ایسا کہتے ہیں۔ آپ ان کے اس اقوال کے متعلق کیا کہتے ہیں؟ انہوں نے جواب دیا کہ ”میں نے رسول اللہ صلی اللہ علیہ وسلم سےاس کے بارے میں سوال کیا تھا۔ حضور صلی اللہ علیہ وسلم نے فرمایا کہ مجھ سے کہا گیا قُل، تو میں نے بھی کہا قُل۔ اس لیے ہم بھی اُسی طرح کہتے ہیں جس طرح حضور صلی اللہ علیہ وسلم کہتے تھے“۔ امام احمد کی روایت میں حضرت اُبَیّ کے الفاظ یہ ہیں: ”میں شہادت دیتا ہوں کہ رسول اللہ صلی اللہ علیہ وسلم نے مجھے بتایا کہ جبریل علیہ السلام نے آپ سے قُل اعوذ برب الفلق کہا تھا اس لیے اپ نے بھی ایسا ہی کہا، اورا نہوں نے قُل اعوذ برب الناس کہا تھا اس لیے آپ نے بھی ایسا ہی کیا۔ لہٰذا ہم بھی اُسی طرح کہتے ہیں جس طرح حضور صلی اللہ علیہ وسلم نے کہا۔“ان دونوں روایتوں پر غور کیجیے تو معلوم ہوگا کہ حضرت عبداللہ بن مسعود رضی اللہ تعالیٰ عنہ کو دونوں سورتوں میں لفظ قُل(کہو) دیکھ کر یہ غلط فہمی ہوئی کہ رسول اللہ صلی اللہ علیہ وسلم کو اعوذ برب الفلق اور اعوذ برب الناس کہنے کا حکم دیا گیا تھا۔ لیکن انہوں نے حضور صلی اللہ علیہ وسلم سے اِس کے متعلق سوال کرنے کی ضرورت محسوس نہ کی۔ حضرت اُبَیّ بن کعب رضی اللہ تعالیٰ عنہ کے ذہن میں بھی اس کے متعلق سوال پیدا ہوا اور انہوں نے حضور صلی اللہ علیہ وسلم سے اِس کو پوچھ لیا۔ حضور صلی اللہ علیہ وسلم نے بتایا کہ جبریل علیہ السلام نے چونکہ قُل کہا تھا اس لیے میں بھی قُل کہتا ہوں۔ اس بات کو یوں سمجھیے کہ اگر کسی کو حکم دینا مقصود ہواور اس سے کہا جائے کہ”میں پناہ مانگتاہوں“،تو وہ حکم کی تعمیل میں یہ نہیں کہے گا کہ ”کہو، میں پناہ مانگتا ہوں“، بلکہ وہ ”کہو“کا لفظ ساقط کر کے”میں پناہ مانگتا ہوں“کہے گا۔ بخلاف اس کے اگر کسی کو بالا دست حاکم کا پیگام بر اِن الفاظ میں پیغام پہنچائے کہ”کہو، میں پناہ مانگتا ہوں“ اور یہ پیغام اُسے اپنے تک رکھنے کے لیے نہیں بلکہ دوسروں تک پہنچانے کے لیے دیا جائے تو وہ لوگوں تک پیغام کے الفاظ کوجوں کا توں پہنچائے گا، اُس میں سے کوئی چیز ساقط کرنے کا مجاز نہ ہوگا۔پس اِن دونوں سورتوں کی ابتدا لفظ قُل سے ہونا اس بات کا صریح ثبوت ہے کہ یہ کلام دی ہے جسے حضور صلی اللہ علیہ وسلم کو دیا گیا ہو۔ قرآن مجید میں اِن دونوں سورتوں کے علاوہ ۱۳۳۰ آیتیں ایسی ہیں جو لفظ قُل(کہو)سے شروع ہوئی ہیں۔ ان سب میں قُل کا ہونا اس بات کی علامت ہے کہ یہ کلامِ وحی ہے جسے اُنہی الفاظ میں پہنچانا حضور صلی اللہ علیہ وسلم کے ذمہ فرض تھا جن الفاظ میں یہ آپ پر نازل کیا گیا تھا، اور اُسے قرآن میں درج نہ کیا جاتا بلکہ حضور صلی اللہ علیہ وسلم صرف اِس حکم کی تعمیل میں وہ بات کہہ دینے پر اکتفا فرماتے جسے کہنے کا آپ کو حکم دیا گیا تھا۔ اس مقام پر اگر آدمی کچھ غور کرے تو اُس کی سمجھ میں یہ بات اچھی طرح آ سکتی ہے کہ صحابہ کرام کو بے خطا سمجھنا اور اُن کی کسی بات کے لیے غلط کا لفظ سنتے ہی توہین صحابہ کا شور مچا دینا کس قدر بے جا حرکت ہے۔ یہاں آپ دیکھ رہے کہ حضرت عبد اللہ بن مسعود رضی اللہ تعالیٰ عنہ جیسے جلیل القدر صحابی سے قرآن کی دوسورتوں کے بارے میں کتنی بڑی چُوک ہوگئی۔ ایسی چوک اگر اتنے عظیم مرتبہ کے صحابی سے ہوسکتی ہے تو دوسروں سے بھی کوئی چوک ہو جاتی ممکن ہے۔ ہم علمی تحقیق کے لیے اُس چھان بین بھی کر سکتے ہیں، اور کسی صحابی کو کوئی بات یا چند باتیں غلط ہوں تو انہیں غلط بھی کہہ سکتے ہیں۔ البتہ سخت ظالم ہو گا وہ شخص جو غلط کہنے سے آگے بڑھ کر اُن پر زبان طعن دراز کرے۔ اِنہی معوّذتین کے بارے میں مفسرین و محدثین نے ابن مسعود رضی اللہ تعالیٰ عنہ کی رائے کو غلط کہا ہے، مگر کسی نے یہ کہنے کی جرأت نہیں کی کہ قرآن کی دوسورتوں کا انکار کر کے معاذ اللہ وہ کافر ہو گئے تھے۔ حضورؐ پر جادو کا اثر: دوسرا مسئلہ جو اِن سورتوں کے معاملہ میں پیدا ہوتا ہے وہ یہ ہے کہ روایات کی رو سے حضورؐ پر جادو کیا گیا تھا، اور اس کے اثر سے آپ بیمار ہوگئے تھے، اور اس اثر کو دور کرنے کے لیے جبریل علیہ السلام نے آکر آپ کو یہ سورتیں پڑھنے کی ہدایت کی تھی۔ اس پر قدیم اور جدید زمانے کے بہت سے عقلیت پسندوں نے اعتراض کیا ہے کہ روایات اگر مان لی جائیں تو شریعت ساری کی ساری مُشْتَبہ ہو جاتی ہے ۔ کیونکہ اگر نبی پر جادو کا اثر ہوسکتا تھا، اور ان روایات کی رو سے ہوگیا تھا، تو ہم نہیں کہہ سکتے کہ مخالفین نے جادو کے زور پر نبی سے کیا کیا کہلوا اور کروالیا ہو، اور اُس کی دی ہوئی تعلیم میں کتنی چیزیں خدا کی طرف سے ہوں اور کتنی جادو کے زیر اثر۔ یہی نہیں بلکہ وہ کہتے ہیں کہ اس بات کو سچ مان لینے کے بعد تو یہ بھی نہیں کہا جا سکتا کہ جادو ہی کے ذریعہ سے نبی کو نبوت کے دعوے پر اُکسایا گیا ہو اور نبی نے غلط فہمی میں مبتلا ہو کر یہ سمجھ لیا ہو کہ اُس کے پاس فرشتہ آیا ہے۔ اُن کا استدلال یہ بھی ہ ہے کہ یہ سِحر زدہ آدمی ہے ( یَقُوْلُ الظّٰلِمُؤنَ اِنْ تَتَّبِعُوْنَ اِلّاَ رَجُلًا مَّسْحُوْراً۔ بنی اسرائیل ۴۷)، مگر یہ احادیث کفارہ کے الزام کی تصدیق کرتی ہیں کہ واقعی نبی پرسِحر کا اثر ہوا تھا۔ اس مسئلے کی تحقیق کے لیے ضروری ہے کہ سب سے پہلے یہ دیکھا جائے کہ کیا درحقیقت مستند تاریخی روایات کی رو سے یہ ثابت ہے کہ رسول اللہ صلی اللہ علیہ وسلم پر جادو کا اثر ہوا تھا؟ اور اگر ہوا تھا تو وہ کیا تھا اور کس حد تک تھا؟ اس کے بعد یہ دیکھا جائے کہ جو کچھ تاریخ سے ثابت ہے اس پر اعتراضات وارد بھی ہوتے ہیں یا نہیں جو کیے گئے ہیں؟ قرونِ اولیٰ کے مسلمان علماء کی یہ انتہائی راستبازی تھی کہ انہوں نے اپنے خیالات اور مزعومات کے مطابق تاریخ کو مسخ کرنے یا حقائق پر پردہ ڈالنے کی کوئی کوشش نہیں کی، بلکہ جو کچھ تاریخی طور پر ثابت تھا اسے جوں کا توں بعد کی نسلوں تک پہنچا دیا اور اُس بات کی کوئی پروا نہیں کی کہ اِن حقائق سے اگر کوئی اُلٹے نتائج نکالنے پر اتر آئے تو اُن کا فراہم کردہ یہ مواد کس طرح اُس کے کام آسکتا ہے۔ اب اگر ایک بات نہایت مُستند اور کثیر تاریخی ذرائع سے ثابت ہو تو کسی دیانت دار صاحبِ علم کے لیے نہ تو یہ درست ہے کہ وہ اِس بنا پر تاریخ کا انکار کردے کہ اُس کو مان لینے سے اُس کے نزدیک فلاں فلاں قباحتیں رونما ہوتی ہیں، اور نہ یہی درست ہے کہ جتنی بات تاریخ سے ثابت ہے اس کو قیاسات کے گھوڑے دوڑا کر اُس اصلی حد سے پھیلانے اور بڑھانے کی کوشش کرے۔ اس کے بجائے اُس کا کام یہ ہے کہ تاریخ کو تاریخ کی حیثیت سے مان لے اور پھر دیکھے کہ اُس سے فی الواقع کیا ثابت ہوتا ہے اور کیا نہیں ہوتا۔ جہاں تک تاریخی حیثیت کا تعلق ہے نبی صلی اللہ علیہ وسلم پر جادو کا اثر ہونے کا واقعہ قطعی طور پر ثابت ہے اور علمی تنقید سے اُس کا اگر غلط ثابت کیا جاسکتا ہو تو پھر دنیا کا کوئی تاریخی واقعہ بھی صحیح ثابت نہیں کیا جا سکتا۔ اسے حضرت عائشہؓ ، حضرت زیدؓبن ارقم اور حضرت عبد اللہؓبن عباس سے بخاری، مسلم، نَسائی، ابن ماجہ، امام احمد، عبد الرزاق، حُمَیدی، بیقہی،طَبرانی، ابن سَعد، ابن مردویہ، ابن ابی شیبہ، حاکم ، عبد بن حمید وغیرہ محدثین نے اتنی مختلف اور کثیر التعداد سندوں سے نقل کیا ہے کہ اُس کا نفس مضمون تو اُتر کی حد کو پہنچا ہوا ہے، اگرچہ ایک ایک روایت بجائے خود خبرِ واحد ہے۔ اس کی تفصیلات جو روایات میں آئی ہیں انہیں ہم مجموعی طور پر تمام روایات سے مرتب کرکے ایک مربوط واقعہ کی صورت میں یہاں درج کرتے ہیں۔ صلح حُدیبیہ کے بعد جب نبی صلی اللہ علیہ وسلم مدینہ واپش تشریف لائے تو محرم۷؁ھ میں خیبر سے یہودیوں کا ایک وفد مدینہ آیا اور ایک مشہور جا دوگر لَبید بن اَعصم سے ملا جو انصار کے قبیلہ بنی زُرَیق سے تعلق رکھتا تھا۔ ؎ان لوگوں نے اُس سے کہا کہ محمد صلی اللہ علیہ وسلم نے ہمارے ساتھ جو کچھ کیا ہے تو تمہیں معلوم ہے۔ ہم نے اُن پر بہت جادو کرنے کی کوشش کی، مگر کوئی کامیابی نہیں ہوئی۔ اب ہم تمہارے پاس آئے ہیں، کیونکہ تم ہم سے بڑے جادوگر ہو۔ لو، یہ تین اشرفیاں حاضر ہیں، انہیں قبول کر و اور محمدؐ پر ایک زور کا جادو کردو۔ اُس زمانے میں حضورؐ کے ہاں ایک یہودی لڑکا خدمت گار تھا۔ اُس سے سازباز کر کے اِن لوگوں نے حضورؐ کی کنگھی کا ایک ٹکڑا حاصل کر لیا جس میں آپ کے موئے مبارک تھے۔ انہی بالوں اور کنگھی کے دندانوں پر جادو کیا گیا۔ بعض روایات میں یہ ہے کہ لَبید بن اعصم نے خود جادو کیا تھا، اور بعض میں یہ ہے کہ اس کی بہنیں اس سے زیادہ جادوگرنیاں تھیں، اُن سے اُس نے جادو کروایا تھا۔ بہر حال ان دنوں صورتوں میں سے جو صورت بھی ہو، اس جادو کو ایک نَر کھجور کے خوشے کے غلاف ؎ میں رکھ کر لَبید نے بنی زُریق کے کنویں ذَرْوان یا ذی اَزوان نامی کی تہ میں ایک پتھر کے نیچے دبا دیا۔ اس جادو کا اثر نبی صلی اللہ علیہ وسلم پر ہوتے ہوتے پورا ایک سال لگا، دوسری ششماہی میں کچھ تغیرِ مزاج محسوس ہونا شروع ہوا، آخری چالیس دن سخت اور آخری تین دن زیادہ سخت گزرے۔ مگر اس کا زیادہ سے زیادہ جو اثر حضورؐ پر ہوا وہ بس یہ تھا کہ آپ گھلتے چلے جا رہے تھے، کسی کام کے متعلق خیال فرماتے کہ ہو کر لیا ہے مگر نہیں کیا ہوتا تھا، اپنی اَزواج کے متعلق خیال فرماتے کہ آپ ان کے پاس گئے ہیں مگر نہیں گئے ہوتے تھے، اور بعض اوقات آپ کو اپنی نظر پر بھی شبہ ہوتا تھا کہ کسی چیز کو دیکھا ہے مگر نہیں دیکھا ہوتا تھا۔ یہ تمام اثرات آپ کی ذات تک محدود رہے ، حتی کہ دوسرے لوگوں کو یہ معلوم تک نہ ہو سکا کہ آپ پر کیا گزررہی ہے۔ رہی آپ کے نبی ہونے کی حیثیت تو اُس میں آپ کے فرائض کے اندر کوئی خلل واقع نہ ہونے پایا۔ کسی روایت میں یہ نہیں ہے کہ اُس زمانے میں آپ قرآن کی کوئی آیت بھول گئے ہوں، یا کوئی آیت آپ نے غلط پڑھ ڈالی ہو، یا اپنی صحبتوں میں اور اپنے وعظوں اور خطبوں میں آپ کی تعلیمات کے اندر کوئی فرق واقع ہوگیا ہو، یا کوئی ایسا کلام آپ نے وحی کی حیثیت سے پیش کر دیا ہو جو فی الواقع آپ پر نازل نہ ہوا ہو، یا نماز آپ سے چھوٹ گئی ہو اور اس کے متعلق بھی کبھی آپ نے سمجھ لیا ہو کہ پڑھ لی ہے مگر نہ پڑھی ہو۔ ایسی کوئی بات معاذ اللہ پیش آجاتی تو دھوم مچ جاتی، اور پورا ملک عرب اس سے واقف ہو جاتا کہ جس نبی کی کوئی طاقت چت نہ کس سکی تھی اسے ایک جادو گر نے چت کر دیا۔ لیکن آپ کی حیثیت نبوت اس سے بالکل غیر متاثر رہی اور صرف اپنی ذاتی زندگی میں آپ اپنی جگہ اسے محسوس کرکے پریشان ہوتے رہے ۔ آخر کار ایک روز آپ حضرت عائشہؓ کے ہاں تھے کہ آپ نےبار بار اللہ تعالیٰ سے دعا مانگی۔ اسی حالت میں نیند آگئی یا غنودگی طاری ہوئی اور پھر بیدار ہو کر آپ نے حضرت عائشہؓ سے کہا کہ میں نے جو بات اپنے رب سے پوچھی تھی وہ اس نے مجھے بتا دی ہے۔ حضرت عائشہؓ نے عرض کیا وہ کیا بات ہے؟ آپ نے فرمایا دو آدمی (یعنی فرشتے دو آدمیوں کی صورت میں) میرے پاس آئے۔ ایک سرھانے کی طرف تھا اور دوسرا پائینتی کی طرف۔ ایک نے پوچھا انہیں کیا ہوا؟ دوسرے نے جواب دیا ان پر جادو ہوا ہے۔ اُس نے پوچھا کس نے کیا ہے؟ جواب دیا لَبِید بن اَعصم نے۔ پوچھا کس چیز میں کیا ہے؟ جواب دیا کنگھی اور بالوں میں ایک نَرکھجورخوشے کے غلاف کے اندر۔ پوچھا وہ کہا ں ہے؟ جواب دیابنی زُریق کے کنویں ذی اَرْوان (یا ذَرْوان) کی تہ کے پتھر کے نیچے ہے۔ پوچھا اب اس کے لیے کیا کیا جائے؟ جواب دیا کہ کنویں کا پانی سونت کر دیا جائے اور پھر پتھر کی نیچے سے اُس کو نکالا جائے۔ اس کے بعد نبی صلی اللہ علیہ وسلم نے حضرت علیؓ، حضرت عَمّار بن یاسرؓ اور حضرت زُبیرؓ کو بھیجا۔ ان کے ساتھ جُبَیر بنؓ اِیاس الزّرقی اور قیسؓ بن مُحِسن الزّرقی (یعنی بنی زُریق کے یہ دو اصحاب) بھی شامل ہوگئے۔ بعد میں حضورؐ خود بھی چند اصحاب کے ساتھ وہاں پہنچ گئے۔ پانی نکالا گیا اور وہ غلاف بر آمد کر لیا گیا۔ اُ س میں کنگھی اور بالوں کے ساتھ ایک تانت کے اندر گیارہ گرھیں پڑی ہونی تھیں اور موم کا ایک پُتلا تھا جس میں سُوئیاں چُبھوئی ہوئی تھیں۔ جبریل علیہ السلام نے آکر بتایا کہ آپ معوّذَتین پڑھیں۔ چنانچہ آپ ایک ایک آیت پڑھتے جاتے اور اس کے ساتھ ایک ایک گرہ کھولی جاتی اور پُتلے میں سے ایک ایک سوئی نکالی جاتی رہی۔ خاتمہ تک پہنچتے ہی ساری گرھیں کھل گئیں، ساری سوئیاں نکل گئیں، اور آپ جادو کے اثر سے نکل کر بالکل ایسے ہوگئے جیسے کوئی شخص بندھا ہواتھا، پھر کھل گیا۔ اس کے بعد آپ نے لَبید کو بلا کر باز پرس کی۔ اُس نے اپنے قصور کا اعتراف کر لیا اور آپ نے اس کو چھوڑ دیا، کیونکہ اپنی ذات کے لیے آپ نے کبھی کس سے انتقام نہیں لیا۔ یہی نہیں بلکہ آپ نے اِ س معاملہ کا چرچا کرنے سے بھِ یہ کہہ کر انکار کر دیا کہ مجھے اللہ نے شفا دے دی ہے ، اب میں نہیں چاہتا کہ کسی کے خلاف لوگوں کو بھڑکاؤں۔ یہ ہے سارا قصہ اس جادو کا۔ اس میں کوئی چیز ایسی نہیں ہے جو آپ کے منصبِ نبوت میں قادِ ح ہو۔ ذاتی حیثیت سے اگر آپ کو زخمی کیا جا سکتا تھا جیسا کہ جنگِ اُحد میں ہوا، اگر آپ گھوڑے سے گِر کر چوٹ کھا سکتے تھے، جیسا کہ احادیث سے ثابت ہے ، اگر آپ کو بچھو کاٹ سکتا تھا، جیسا کہ کچھ اور احادیث میں وارد ہوا ہے، اور ان میں سے کوئی چیز بھی اُس تحفظ کے مُنافی نہیں ہے جس کا نبی ہونے کی حیثیت سے اللہ تعالیٰ نے آپ سے وعدہ کیا تھا، تو آپ اپنی ذاتی حیثیت میں جادو کے اثر سے بیمار بھی ہو سکتے تھے۔ نبی پر جادو کا اثر ہوسکتا ہے، یہ بات تو قرآن مجید سے بھی ثابت ہے۔ سورہ اعراف میں فرعون کے جادوگروں کے متعلق بیان ہوا ہے کہ حضرت موسیٰ کے مقابلے میں جب وہ آئے تو اُنہوں نے ہزار ہا آدمیوں کے اُس پورے مجمع کی نگاہوں میں جادو کر دیا جو وہاں دونوں کا مقابلہ دیکھنے کے لیے جمع ہوا تھا ( سَحَرُوْٓ اَعْیُنْ النَّاسِ۔ آیت ۱۱۶)، اور سورہ طٰہٰ میں ہے کہ جو لاٹھیاں اور رسیاں انہوں نے پھینکی تھیں ان کے متعلق عام لوگوں ہی نے نہیں حضرت موسیٰ نے بھی یہی سمجھا کہ وہ اُن کی طرف سانپوں کی طرح ددوڑی چلی آرہی ہیں اور اس سے حضرت موسیٰ خوف زدہ ہوگئے، یہاں تک کہ اللہ تعالیٰ نے ان پر وحی نازل کی کہ خوف نہ کرو تم ہی غالب رہو گے ، ذرا اپنا عصا پھینکو (فَاِذَا حِبَالُھُمْ وَعِصِیُّھُمْ یُخَیَّلُ اِلَیْہِ اِنَّھَا تَسْعیٰ۔ فَاَوْجَسَ فِیْ نَفْسِہٖ خِیْفَۃً مُّوْسیٰ، قُلْنَا لَا تَخَفْ اِنَّکَ اَنْتَ الْا عْلیٰ، وَاَلْقِ مَا فِیْ یَمِیْنِکَ۔ آیات۶۶ تا ۶۹)۔ رہا یہ اعتراض کہ یہ تو کفارِ مکہ کے اُس القام کی تصدیق ہوگئی کہ نبی صلی اللہ علیہ وسلم کو وہ سحر زدہ آدمی کہتے تھے، تو اس کا جوا ب یہ ہے کہ کفار آپ کو سحر زدہ آدی اس معنی میں نہیں کہتے تھے کہ آپ کسی جادوگر کے اثر سے بیمار ہوگئے ہیں، بلکہ اس معنی میں کہتے تھے کہ کسی جادو گر نے معاذ اللہ آپ کو یہ پاگل کر دیا ہے اور اِسی پاگل پن میں آپ نبوت کا دعویٰ کر بیٹھے ہیں اور جنت و دوزخ کے افسانے سنا رہے ہیں۔ اب ظاہر ہے کہ یہ اعتراض ایسے معاملہ پر سرے سے چسپاں ہی نہیں ہوتا جس کے متعلق تاریخ سے یہ ثابت ہے کہ جادو کا اثر صرف ذاتِ محمدؐ پر ہوا تھا، نبوت محمدؐ اس سے بالکل غیر متاثر رہی۔ اس سلسلے میں یہ بات بھی قابل ذکر ہے کہ جو لوگ جادو کو محض اَوہام کے قبیل کی چیز قرار دیتے ہیں اُن کی یہ رائےصرف اس وجہ سے ہے کہ جادو کے اثرات کی کوئی سائنٹفک توجیہ نہیں کی جاسکتی۔ لیکن دنیا میں بہت سی چیزیں ایسی ہیں جو تجربے اور مشاہدے میں آتی ہیں، مگر سائنٹفک طریقہ سے یہ بیان نہیں کیا جا سکتا کہ وہ کیسے رونما ہوتی ہیں۔ اس طرح کی توجیہ پر اگر ہم قادر نہیں ہیں تو اس سے یہ لازم نہیں آتا کہ اُس چیز ہی کا انکار کر دیا جائے جس کی ہم توجیہ نہیں کر سکتے۔ جادو دراصل ایک نفسیاتی اثر ہے جو نفس سے گزر کر جسم کو بھی اُسی طرح متاثر کر سکتا ہے جس طرح جسمانی اثرات جسم سے گزر کر نفس کو متاثر کرتے ہوہیں، مثال کے طور پر خوف ایک نفسیاتی چیز ہے ، مگر اس کا اثر جسم پر یہ ہوتا ہے کہ رونگٹے کھڑے ہو جاتے ہیں اور بدن میں تھر تھری چھوٹ جاتی ہے۔ دراصل جادو سے حقیقت تبدیل نہیں ہوتی، مگر انسان کا نفس اور اس کے حواس اُس سے متاثر ہو کر یہ محسوس کرنے لگتے ہیں کہ حقیقت تبدیل ہو گئی ہے۔ حضرت موسیٰ کی طرف جادو گروں نے جو لاٹھیاں اور رسیاں پھینکی تھیں وہ واقعی سانپ نہیں بن گئی تھیں، لیکن ہزاروں کے مجمع کی آنکھوں پر ایسای جادو ہوا کہ سب نے انہیں سانپ ہی محسوس کیا، اور حضرت موسیٰ تک کے حواس جادو کی اِس تاثیر سے محفوظ نہ رہ سکے۔ اسی طرح قرآن (البقرہ، آیت۱۰۲) میں بیان کیا گیا ہے کہ بابِل میں ھاروت اور ماروت سے لوگ ایسا جادو سیکھتے تھے جو شوہر اور بیوی میں جدائی ڈال دے۔ یہ بھی ایک نفسیاتی اثر تھا ، اور ظاہر ہے کہ اگ تجربے سے لوگوں کواس عمل کی کامیابی معلوم نہ ہوتی تو وہ اس کے خریدار نہ بن سکتے تھے۔ بلا شبہ یہ بات اپنی جگہ بالکل درست ہے کہ بندوق کی گولی اور ہوائی جہاز سے گرنے والے بن کی طرح جادو کا مؤثر ہونا بھی اللہ کے اذن کےبغیر ممکن نہیں ہے، مگر جو چیز ہزارہا سال سے انسان کے تجربے اور مشاہدے میں آرہی ہو اس کے وجود کو جُھٹلا دینا محض ایک ہٹ دھرمی ہے۔ اسلام میں جھاڑ پھونک کی حیثیت: تیسرا مسئلہ اِن سورتوں کے معاملہ میں یہ پیدا ہوتا ہے کہ آیا جھاڑ پھونک کی اسلام میں کوئی گنجائش ہے؟ اور یہ کہ جھاڑ پھونک بجائے خود مؤثر بھی ہے یا نہیں؟ یہ سوال اس لیے پیدا ہوتا ہے کہ بکثرت صحیح احادیث میں یہ ذکر آیا ہے کہ رسول اللہ صلی اللہ علیہ وسلم ہر رات کو سوتے وقت، اور خاص طور پر بیماری کی حالت میں معوّذتین، یا بعض روایات کے مطابق مُعوّذات (یعنی قل ہو اللہ اور معوِّذتین) تین مرتبہ پڑھ کر اپنے دونوں ہاتھوں میں پھونکتے اور سر سے لے کر پاؤں تک پورے جسم پر، جہاں جہاں تک بھی آپ کے ہاتھ پہنچ سکتے، انہیں پھیرتے تھے۔آخر بیماری میں جب آپ کے لیے خود ایسا کرنا ممکن نہ رہا تو حضرت عائشہ نے یہ سورتیں (بطور خود یا حضورؐ کے حکم سے) پڑھیں اور آپ کے دست مبارک کی برکت کے خیال سے آپ ہی کے ہاتھ لے کر آپ کے جسم پر پھیرے ۔ اس مضمون کی روایات صحیح سندوں کے ساتھ بخاری، مسلم، نسائی، ابن ماجہ، ابو داؤد اور مؤطا امام مالک میں خود حضرت عائشہ سے مروی ہیں جن سے بڑھ کر کوئی بھی حضورؐ کی خانگی زندگی سے واقف نہ ہو سکتا تھا۔ اِس معاملہ میں پہلے مسئلہ شرعی اچھی طرح سمجھ لینا چاہیے۔ احادیث میں حضرت عبد اللہ بن عباسؓ کی طویل روایت آئی ہے جس کے آخر میں حضورؐ فرماتے ہیں کہ میری امت کے و ہ لوگ بلا احساب جنت میں داخل ہوں گے جو نہ داغنے کا علاج کراتے ہیں، نہ جھاڑ پھونک کراتے ہیں، نہ فال لیتے ہیں ، بلکہ اپنے رب پر توکل کرتے ہیں (مسلم)۔ حضرت مُغِرہؓ بن شُعبہ کی روایت ہے کہ حضورؐ نے فرمایا جس نے داغنے سے علاج کرایا اور جھاڑ پھونک کرائی وہ اللہ پر توکل سے بے تعلق ہو گیا (ترمذی)۔ حضڑت عبد اللہ بن مسعود کی روایت ہے کہ رسول اللہ صلی اللہ علیہ وسلم دس چیزوں کو نا پسند فرماتے تھے جن میں سے ایک جھاڑ پھونک بھی ہے سوائے معوّذتین یا معوّذات کے (ابو داؤد، احمد، نسائی، ابن حبان، حاکم)۔ بعض احادیث سے یہ بھی معلوم ہوتا ہے کہ ابتدا میں حضورؐ نے جھاڑ پھونک سے بالکل منع فرما دیا تھا، لیکن بعد میں اِس شرط کے ساتھ اس کی اجازت دے دی کہ اس میں شرک نہ ہو، اللہ کے پاک ناموں یا اس کے کلام سے جھاڑا جائے،کلام ایسا ہو جو سجھ میں آئے اور یہ معلوم کیا جا سکے کہ اس میں کوئی گناہ کی چیز نہیں ہے، اور بھروسہ جھاڑ پھونک پر نہ کیا جائے کہ وہ بجائے خود شفا دینے والی ہے، بلکہ اللہ پر اعتماد کیا جائے کہ وہ چاہے گا تو اسے نافع بنا دے گا۔ یہ مسئلہ شرعی واضح ہوجانے کے بعد اب دیکھیے کہ احادیث اِس بارے میں کیا کہتی ہیں: طبرانی نے صغیر میں حضرت علیؓ کی روایت نقل کی ہے کہ حضورؐ کو ایک دفعہ نماز کی حالت میں بچھو نے کاٹ لیا۔ جب آپ نماز سے فارغ ہوئے تو فرمایا بچھو پر خدا کی لعنت، یہ نہ کسی نمازی کو چھوڑتا ہے نہ کسی اور کو۔ پھر پانی اور نمک منگوایا اور جہاں بچھو نے کاٹا تھا وہاں آپ نمکین پانی ملتے جاتے اور قل یا ایہا الکافرون، قل ھو اللہ احد، قل اعوذ برب الفلق اور قل اعوذ برب الناس پڑھتے جاتے تھے۔ ان عباس کی یہ روایت بھی احادیث میں آئی ہے کہ نبی صلی اللہ علیہ وسلم حضرت حسنؓ اور حضرت حسینؓ پر یہ دعا پڑھتے تھے اُعِیْذُکُمَا بِکَلِمَاتِ اللہِ التَّامَّۃِ مِنْ کُلِّ شَیْطَانٍ وَّھَا مَّۃٍ وَّمِنْ کُلِّ عَیْنٍ لَّا مَّۃٍ۔ ”میں تم کو اللہ کے بے عیب کلمات کی پناہ میں دیتا ہوں ہر شیطان اور موذی سے اور ہر نظر بد سے“(بخاری، مسند احمد،ترمذی اور ابن ماجہ)۔ عثمان بن ابی العاص الثَقَفی کے متعلق مسلم، مُوَطَّا، طبرانی اور حاکم میں تھوڑے لفظی اختلاف کے ساتھ یہ روایت آءی ہے کہ انہوں نے رسول اللہ صلی اللہ علیہ وسلم سے شکایت کی کہ میں جب سے مسلمان ہوا ہوں مجھے ایک درد محسوس ہوتا ہے جو مجھ کو مارے ڈالتا ہے۔ آپ نے فرمایا اپنا سیدھا ہاتھ اُس جگہ پر رکھو جہاں درد ہوتا ہے ، پھر تین مرتبہ بسم اللہ کہو اور سات مرتبہ یہ کہتے ہوئے ہاتھ پھیرو کہ اَعُوْذُ بِاللہِ وَ قُدْرَتِہِ مِنْ شَرِّ مَآ اَجِدُ وَ اُحَاذِرُ، ”میں اللہ اور اس کی قدرت کی پناہ مانگتا ہوں اُس چیز کے شر سے جس کو میں محسوس کرتا ہوں اور جس کے لاحق ہونے کا مجھے خوف ہے“۔ موطاء میں اس پر یہ اضافہ ہے کہ عثمانؓ بن ابی العاص نے کہا کہ اس کے بعد میرا وہ درد جاتا رہا ، اور اِسی چیز کی تعلیم میں اپنے گھر والوں کو دیتا ہوں۔ مُسند احمد اور طحاوی میں طَلقْ بن علیؓ کی روایت ہے کہ مجھے رسول اللہ صلی اللہ علیہ وسلم کی موجودگی میں بچھو نے کاٹ لیا۔حضورؐ نے مجھ پر پڑھ کر پھونکا اور اس جگہ پر ہاتھ پھیرا۔ مسلم میں ابو سعیدؓ خُدری کی روایت ہے کہ ایک مرتبہ نبی صلی اللہ علیہ وسلم بیمار ہوئے تو جبریل نے آکر پوچھا ”اے محمدؐ، کیا آپ بیمار ہوگئے“؟آپ نے فرمایا ہاں۔ انہوں نے کہا بِاسْمِ اللہِ اَرْقِیْکَ مِنْ کُلِّ شَیٍْ یُّؤْذِیْکَ مِنْ شَرِّ کُلِّ نَفْسٍ اَوْعِیْنٍ حَا سِدٍ، اَللہُ یَشْفِیْکَ بِاسْمِ اللہِ اَرْقِیْکَ، ”میں اللہ کے نام پر آپ کو جھاڑتا ہوں ہر اُس چیز سے جو آپ کو اذیت دے اور ہر نفس اور حاسد کی نظر کے شر سے، اللہ آپ کو شفا دے ، میں اُس کےنام پر آپ کو جھاڑتا ہوں۔“اسی سے ملتی جلتی روایت مُسند احمد میں حضرت عُبادہ بن صامت سے منقول ہے کہ حضورؐ بیمار تھے۔ میں عیادت کے لیے گیا تو آپ کو سخت تکلیف میں پایا۔ شام کو گیا تو آپ بالک تندرست تھے۔ میں نے اس قدر جلدی تندرست ہوجانے کی وجہ پوچھی تو فرمایا کہ جبریلؑ آئے تھے اور انہوں نے مجھے چند کلمات سے جھاڑا۔ پھر آپ نے قریب قریب اُسی طرح کے الفاظ اُن کو سُنائے جو اوپر والی حدیث میں نقل کیے گئے ہیں۔ حضرت عائشہؓ سے بھی مسلم اور مسند احمد میں ایسی ہی روایت نقل کی گئی ہے۔ امام احمد نے اپنی مُسند میں حضرت حفصہؓ ام المومنین کی روایت نقل کی ہے کہ ایک روز نبی صلی اللہ علیہ وسلم میرے ہاں آئے اور میرے پاس ایک خاتون شفا ؎ نامی بیٹھی تھیں جو نَمِلہ (ذُباب) کو جھاڑا کرتی تھیں۔ حضورؐ نے فرمایا حفصہؓ کو بھی وہ عمل سکھا دو۔ خود شفاؓ بنت عبداللہ کی یہ روایت امام احمد، ابو داؤد اور نسائی نے نقل کی ہے کہ حضورؐ نے مجھ سے فرمایا کہ تم نے حفصہ کو جس طرح لکھنا پڑھنا سکھایا ہے نَمِلہ کا جھاڑنا بھی سکھا دو۔ مسلم میں عوف بنؓ مالک اشجعی کی روایت ہے کہ جاہلیت کے زمانے میں ہم لوگ جھاڑ پھونک کیا کرتے تھے۔ ہم نے رسول اللہ صلی اللہ علیہ وسلم سے پوچھا کہ اس معاملہ میں حضورؐ کی رائے کیا ہے۔ حضورؐ نے فرمایا جن چیزوں سے تم جھاڑتے تھے وہ میرے سامنے پیش کرو، جھاڑنے میں مضائقہ نہیں ہے جب تک اُس میں شرک نہ ہو۔ مسلم، مسند احمد، اور ابن ماجہ میں حضرت جابر بن عبد اللہؓ کی روایت ہے کہ رسول اللہ صلی اللہ علیہ ولم نے جھاڑ پھونک سے روک دیا تھا۔ پھر حضرت عمر و بن حزم کے خاندان کے لوگ آئے اور کہا کہ ہمارے پاس ایک عمل تھا جس سے ہم بچھو (یا سانپ) کاٹے کو جھاڑتے تھے۔ مگر آپ نے اِس کام سے منع فرما دیا ہے۔ پھر انہوں نے وہ چیز آپ کو سنائی جو وہ پڑھتے تھے۔ آپ نے فرمایا”اس میں تو کوئی مضائقہ نہیں پاتا، تم میں سے جو شخص اپنے کسی بھائی کو فائدہ پہنچا سکتا ہے وہ ضرور پہنچائے“۔ جابر بن ؓعبداللہ کی دوسری حدیث مسلم میں یہ ہے کہ آلِ حَزْم کے پاس سانپ کاٹے کا عمل تھا اور حضورؐ نے ان کو اس کی اجازت دیدی۔ اس کی تائید مسلم، مسند احمد، ار ابن ماجہ میں حضرت عائشہؓ کی یہ روایت بھی کرتی ہے کہ حضورؐ نے انصار کے ایک خاندان کو ہر زہریلے جانور کے کاٹے کو جھاڑنے کی اجازی مرحمت فرمائی۔ مُسند احمد اور ترمذی اور مسلم اور ابن ماجہ میں حضرت انس سے بھی اس سے ملتی جلتی روایات نقل کی گئی ہیں جن میں حضورؐ نے زہریلے جانوروں کے کاٹے،اور ذُباب کے مرض اور نظرِ بد کے جھاڑنے کی اجازت دی۔ مُسند احمد ، ترمذی، ابن ماجہ اور حاکم نے حضرت عُمیرؓ مولیٰ اٰبِی اللَّحم سے یہ روایت نقل کی ہے کہ جاہلیت کے زمانے میں میرے پاس ایک عمل تھا جس سے میں جھاڑا کرتا تھا۔ میں نے رسول اللہ صلی اللہ علیہ وسلم کے سامنے اسے پیش کیا۔ آپ نے فرمایا فلاں فلاں چیزیں اس میں سے نکال دو، باقی سے تم جھاڑ سکتے ہو۔ مُوطاء میں ہے کہ حضرت ابو بکر اپنی صاحبزادی حضرت عائشہ کے گھر تشریف لے گئے تو دیکھا کہ وہ بیمار ہیں اور ایک یہودیہ اُن کو جھاڑ رہی ہے۔ اس پر انہوں نے فرمایا کہ کتاب اللہ پڑھ کر جھاڑ۔ اس سے معلوم ہوا کہ اہلِ کتاب اگر توراۃ یا انجیل کی آیات پڑھ کر جھاڑیں تب بھی یہ جائز ہے۔ رہا یہ سوال کہ آیا جھاڑ پھونک مفید بھی ہے یا نہیں ، تو اس کا جواب یہ ہے کہ رسول اللہ صلی اللہ علیہ وسلم نے دوا ور علاج سے نہ صرف یہ کہ کبھِ منع نہیں فرمایا، بلکہ خود فرمایا کہ ہر مرض کی دوا اللہ نے پیدا کی ہے اور تم لوگ دوا کیا کرو۔ حضورؐ نے خود لوگوں کو بعض امراض کے علاج بتائے ہیں، جیسا کہ احادیث میں کتاب الطب کو دیکھنے سے معلوم ہو سکتا ہے ۔ لیکن دوا بھی اللہ ہی کے حکم اور اذن سے نافع ہوتی ہے ، ورنہ اگر دوا اور طبی معالجہ ہرحال میں نافع ہوتا تو ہسپتالوں میں کوئی نہ مرتا۔ اب اگر دوا اور علاج کرنے کے ساتھ اللہ کے کلام اور اس کے اسمائے حسنٰی سے بھی استفادہ کیا جائے، یا ایسی جگہ جہاں کوئی طبی امداد میسر نہ ہو اللہ ہی کی طرف رجوع کر کے اس کے کلام اور اسماء و صفات سے استعانت کی جائے تو یہ مادّہ پرستوں کے سوا کسی کی عقل کے بھی خلاف نہیں ہے۔ ؎ البتہ یہ صحیح نہیں ہے کہ دوا اور علاج کو، جہاں وہ میسر ہو، جان بوجھ کر چھوڑ دیا جائے، اور صرف جھاڑ پھونک سے کام لینے ہی پر اکتفا کیا جائے، اور کچھ لوگ علیات اور تعویذوں کے مَطب کھول کر بیٹھ جائیں اور اسی کو کمائی کا ذریعہ بنا لیں۔ اس معاملہ میں بہت سے لوگ حضرت ابو سعیدؓ خدری کی اُس روایت سے استدلال کرتے ہیں جو بخاری، مسلم ، ترمذی، مسند احمد، ابو داؤد اور ابن ماجہ میں منقول ہوئی ہے اور اس کی تائید بخاری میں ابن عباسؓ کی بھی ایک روایت کرتی ہے۔ اس میں یہ بیان ہوا ہے کہ حضورؐ نے ایک مہم پر اپنے چند اصحاب کو بھیجا جن میں حضرت ابو سعیدؓ خُدری بھی تھے ۔ یہ حضرات راستہ میں عرب کے ایک قبیلے کی بستی پر جا کر ٹھیرے اور انہوں نے قبیلے والوں سے کہا کہ ہماری میز بانی کرو۔ انہوں نے انکار کر دیا۔ اتنے میں قبیلے کے سردار کو بچھو نے کاٹ لیا، اور وہ لوگ اِن مسافروں کے پاس آئے اور کہا کہ تمہارے پاس کوئی دوا یا عمل ہے جس سے تم ہمارے سردار کا علاج کر دو؟ حضرت ابو سعید نے کہا ہے تو سہی مگر چونکہ تم نے ہماری میزبانی سے انکار کیا ہے اس لیے جب تک تم کچھ دینا نہ کرو، ہم اس کا علاج نہیں کریں گے۔ انہوں نے بکریوں کا ایک ریور(بعض روایات میں ہے ۳۰ بکریاں) دینے کا وعدہ کیا اور ابو سعیدؓ نے جا کر اس پر سورۃ الفاتحہ پڑھنی شروع کی اور لعاب دھن اس پر ملتے گئے۔ ؎ آخر کار بچھو کا اثر زائل ہو گیا اور قبیلے والوں نے جتنی بکریاں دینے کا وعدہ کیا تھا وہ لا کر دے دیں۔ مگر ان حضرات نے آپس میں کہا ان بکریوں سے کوئی فائدہ نہ اٹھاؤ جب تک رسول اللہ صلی اللہ علیہ وسلم سے پوچھ نہ لیا جائے۔ نہ معلوم اس کام پر اجر لینا جائز ہے یا نہیں۔ چنانچہ یہ لوگ حضورؐ کی خدمت میں حاضر ہوئے اور ماجرا عرض کیا۔ حضورؐ نے مسکرا کر فرمایا”تمہیں کیسے معلوم ہوا کہ یہ سورہ جھاڑنے کے کام بھی آسکتی ہے؟ بکریاں لے لو اور ان میں میرا حصہ بھی لگاؤ“۔ لیکن اِس حدیث سے تعویذ، گنڈے اور جھاڑ پھونک کے مطلب چلانے کا جواز نکالنے سے پہلے عرب کے اُن حالات کا نگاہ میں رکھنا چاہیے جن میں حضرت ابو سعید خُدری نے یہ کام کیا تھا اور حضورؐ نے اسے نہ صرف جائز رکھا تھا، بلکہ یہ بھی فرمایا تھا کہ میرا حصہ بھی لگاؤ، تا کہ اس کے جواز و عدم ِ جواز کے معاملہ میں اِن اصحاب کے دلوں میں کوئی شبہ باقی نہ رہے ۔ عرب کے حالات اُس زمانے میں بھی یہ تھے اور آج تک یہ ہیں کہ پچاس پچاس ، سوسو، ڈیڑھ ڈیڑھ سو میل تک آدمی کو ایک بستی سے چل کر دوسری بستی نہیں ملتی ۔ بستیاں بھی اُس وقت ایسی نہ تھیں جن میں ہوٹل، سرائے یا کھانے کی دوکانیں موجود ہوں اور مسافر کئی کئی روز کی مسافت طے کر کے جب وہاں پہنچے تو سامانِ خوردنوش خرید سکے۔ ان حالات میں یہ بات عرب کے معروف اصولِ اخلاق میں شامل تھی کہ مسافر جب کسی بستی پر پہنچیں تو بستی کے لوگ ان کی میزبانی کریں۔ اس سے انکار کے معنی بسا اوقات مسافروں کے لیے مَوت کے ہوتے تھے، اور عرب میں اِس طرز عمل کو معیوب سمجھا جاتا تھا۔ اسی لیے رسول اللہ صلی اللہ علیہ وسلم نے اپنے صحابہ کے اس فعل کو جائز رکھا کہ جب قبیلے والوں نے میز بانی سے انکار کر دیا تھا تو ان کے سردار کا علاج کرنے سے انہوں نے بھی انکار کر دیا، اور اس شرط پر اس کا علاج کرنے پر راضی ہوئے کہ وہ ان کو کچھ دینا کریں۔ پھر جب ان میں سے ایک صاحب نے اللہ کے بھروسے پر سورہ فاتحہ اُس سردار پر پڑھی اور وہ اس سے اچھا ہوگیا تو طے شدہ اجرت قبیلے والوں نے لا کر دے دی اور حضورؐ نے اس اجرت کے لینے کو حلال و طیب قرار دیا۔ بخار ی میں اس واقعہ کے متعلق حضرت عبداللہ بن عباس کی جو روایت ہے اس میں حضورؐ کے الفاظ یہ ہیں کہ اِنَّ احقَّ ما اخذ تم علیہ اجراً کتاب اللہِ، یعنی بجائے اس کے کہ تم کوئی اور عمل کرتے، تمہارے لیے یہ زیادہ برحق بات تھی کہ تم نے اللہ کی کتاب پڑھ کر اس پر اجرت لی۔ یہ آپ نے اس لیے فرمایا کہ دوسرے تمام عملیات سے اللہ کا کلام بڑھ کر ہے، علا وہ بریں اس طرح عرب کے اُس قبیلے پر حق تبلیغ بھی ادا ہو گیا کہ انہیں اس کلام کی برکت معلوم ہو گئی جو اللہ کی طرف سے نبی صلی اللہ علیہ وسلم لائے ہیں۔ اِس واقعہ کو اُن لوگوں کے لیے نظیر قرار نہیں دیا جا سکتا جو شہروں اور قصبوں میں بیٹھ کر جھاڑ پھونک کے مطب چلاتے ہیں اور اسی کو انہوں نے وسیلہ معاش بنا رکھا ہے۔ اس کی کوئی نظیر نبی کریم صلی اللہ علیہ وسلم یا صحابہ و تابعین اور ائمہ سلف کے ہاں نہیں ملتی۔ سورہ فاتحہ اور اِن سورتوں کی مناسبت: آخری چیز جو معوّذتین کے بارے میں قابل توجہ ہے وہ قرآن کے آغاز اور اختتام کی مناسبت ہے۔اگر چہ قرآن مجید ترتیب نزول پر مرتب نہیں کیا گیا ہے، مگر ۲۳ سال کے دوران میں مختلف حالات اور مواقع اور ضروریات کے لحاظ سے نازل ہونے والی آیات اور سورتوں کو رسول اللہ صلی اللہ علیہ وسلم نے بطور خود نہیں بلکہ ان کے نازل کرنے والے خدا کے حکم سے اس شکل میں مرتب فرمایا جس میں ہم اب اس کو پاتے ہیں۔ اس ترتیب کے لحاظ سے قرآن کا آغاز سورہ فاتحہ سے ہوتا ہے اور اختتام معوّذتین پر ۔اب ذرا دونوں پر ایک نگاہ ڈالیے ۔ آغاز میں اللہ رب العالمین، رحمٰن الرحیم ، اور مالک یوم الدین کی حمد و ثناہ کر کے بندہ عرض کرتا ہے کہ آپ ہی کی میں بندگی کرتا ہوں اور آپ ہی سے مدد چاہتا ہوں، اور سب سے بڑی مدد جو مجھے درکار ہے وہ یہ ہے کہ مجھے سیدھا راستہ بتائیے۔ جو اب میں اللہ تعالیٰ کی طرف سے سیدھا راستہ دکھانے کے لیے اسے پورا قرآن دیا جاتا ہے، اور اس کو ختم اس بات پر کیا جاتا ہے کہ بندہ اللہ تعالیٰ سے جو رب الفلق ، رب الناس ، مالک الناس اور الہ الناس ہے ، عرض کرتا ہے کہ میں ہر مخلوق کے ہر فتنے اور شر سے محفوظ رہنے کے لیے آپ ہی کی پناہ لیتا ہوں، ااور خصوصیت کے ساتھ شیاطین جن و انس کے وسوسوں سے آپ کی پناہ مانگتا ہوں، کیونکہ رائے راست کی پیروری میں وہی سب سے زیادہ مانع ہوتے ہیں۔ اس آغاز کے ساتھ یہ اختتام جو مناسبت رکھتا ہے وہ کسی صاحب نظر سے پوشیدہ نہیں رہ سکتی ؎ بعض راویوں نے اُسے یہودی کہا ہے ، اور بعض نے منافق اور یہود کا حلیف۔ لیکن اس پر سب متفق ہیں کہ وہ بنی زُریق میں سے تھا، اور یہ سب کو معلوم ہے کہ بنی زُریق یہودیوں کا کوئی قبیلہ نہ تھا بلکہ خَزْرج میں سے انصا ر کا ایک قبیلہ تھا۔ اس لیےیا تو وہ اُن لوگوںمیں سے تھا جو اہلِ مدینہ میں سے یہودی ہوگئے تھے، یا یہود کا حلیف ہونے کی بنا پر بعض لوگوں نے اسے بھی یہودی شمار کرلیا۔ تاہم اس کے لیے منافق کا لفظ استعمال ہونے سے یہ معلوم ہوتا ہے کہ بظاہر و ہ مسلمان بنا ہوا تھا۔ واپس ۲؎ ابتدا میں کھجور کا خوشہ ایک غلاف کے اندر ہوتا ہے ۔ اور نَر کھجور کے غلا ف کا رنگ انسان کے رنگ سے ملتا جلتا ہوتا ہے۔ اور اس کی بو انسان کے مادہ منویہ جیسی ہوتی ہے۔ واپس ۳؎ اِن خاتون کا اصل نام لیلیٰ تھا ۔ مگر شفابنتؓ عبد اللہ کے نام سے مشہور تھیں ۔ ہجرت سے پہلے ایمان لائیں۔ قریش کے خاندان بنی عَدِی سے ان کا تعلق تھا۔ یہ وہی خاندان ہے جس کے ایک فرد حضرت عمرؓ تھے۔ اِس طرح یہ حضرت حفصہؓ کی رشتہ داری ہوتی تھیں۔ واپس ۴؎ مادہ پرست دنیا کے بھی بہت سے ڈاکٹروں نے اعتراف کیا ہے کہ دعا اور رجوع الی اللہ مریضوں کی شفا یابی میں بہت کار گرچیز ہے ۔ اور ا ِس کا خود مجھے ذاتی طور پر اپنی زندگی میں دو مرتبہ تجربہ ہوا ہے۔ ۱۹۴۸؁ء میں جب مجھے نظر بند کیا گیا تو چند روز بعد ایک پتھری میرے مثانے میں آکر اَڑ گئی اور ۱۶ گھنٹے تک پیشاب بند رہا۔ میں نے اللہ تعالیٰ سے دعا کی کہ میں ظالموں سے علاج کی درخواست نہیں کرنا چاہتا، تو ہی میرا علاج فرمادے۔ چنانچہ وہ پتھری پیشاب کے راستے سے ہٹ گئی اور ۲۰ برس تک ہٹی رہی یہاں تک کہ ۱۹۶۸؁ء میں اس نے پھر تکلیف دی اور اس کو آپریشن کر کے نکالا گیا۔ دوسری مرتبہ جب ۱۹۵۳؁ء میں مجھے گرفتار کیا گیا تو میری دونوں پنڈلیاں کئی مہینے سے داد کی سخت تکلیف میں مبتلا تھیں اور کسی علاج سے آرام نہیں آرہا تھا۔ گرفتاری کے بعد میں نے اللہ تعالیٰ سے پھر وہی دعا کی جو ۱۹۴۸؁ء میں کی تھی اور کسی علاج کے دوا کےبغیر پنڈلیاں داد سے بالکل صاف ہو گئیں۔ آج تک پھر کبھی وہ بیماری مجھے نہیں ہوئی۔ واپس ۵؎ اکثر روایات میں یہ صراحت نہیں ہے کہ یہ عمل کرنے والے حضرت ابو سعید تھے۔ بلکہ ان میں یہ صراحت بھی نہیں ہے کہ حضرت ابو سعید خود اس مہم میں شریک تھے۔ لیکن ترمذی کی روایت میں دونوں باتوں کی صراحت ہے۔ واپس ','سید ابو اعلیٰ مودودیؒ -  تفہیم القرآن');
