INSERT INTO "diyanet" VALUES(26,92,'26:92','Onlara: &quot;Allah''ı bırakıp taptıklarınız nerededir. Size yardım ediyorlar mı veya kendilerine yardımları dokunuyor mu?&quot; denilir.');
