INSERT INTO "diyanet" VALUES(26,111,'26:111','&quot;Sana mı inanacağız? Sana en rezil kimseler uymaktadır&quot; dediler.');
INSERT INTO "diyanet" VALUES(26,112,'26:112','Nuh: &quot;Onların yaptıkları hakkında bir bilgim yoktur; hesabları Rabbime aittir, düşünsenize! Ben inananları kovacak değilim. Ben sadece açıkça uyarıcıyım&quot; dedi.');
INSERT INTO "diyanet" VALUES(26,113,'26:113','Nuh: &quot;Onların yaptıkları hakkında bir bilgim yoktur; hesabları Rabbime aittir, düşünsenize! Ben inananları kovacak değilim. Ben sadece açıkça uyarıcıyım&quot; dedi.');
