INSERT INTO "diyanet" VALUES(26,116,'26:116','&quot;Ey Nuh! Eğer bu işe son vermezsen, şüphesiz taşlanacaklardan olacaksın&quot; dediler.');
INSERT INTO "diyanet" VALUES(26,117,'26:117','Nuh: &quot;Rabbim! Milletim beni yalanladı. Benimle onların arasında Sen hüküm ver. Beni ve beraberimdeki inananları kurtar&quot; dedi.');
INSERT INTO "diyanet" VALUES(26,118,'26:118','Nuh: &quot;Rabbim! Milletim beni yalanladı. Benimle onların arasında Sen hüküm ver. Beni ve beraberimdeki inananları kurtar&quot; dedi.');
INSERT INTO "diyanet" VALUES(26,119,'26:119','Bunun üzerine onu ve beraberinde bulunanları, dolu bir gemi içinde taşıyarak kurtardık.');
INSERT INTO "diyanet" VALUES(26,120,'26:120','Sonra de geride kalanları suda boğduk.');
INSERT INTO "diyanet" VALUES(26,121,'26:121','Doğrusu bunda bir ders vardır, ama çoğu inanmamıştır.');
