INSERT INTO "diyanet" VALUES(26,153,'26:153','&quot;Sen şüphesiz büyülenmişin birisin; bizim gibi bir insandan başka birşey değilsin. Eğer doğru sözlü isen bir belge getir&quot; dediler.');
