INSERT INTO "diyanet" VALUES(26,155,'26:155','Salih: &quot; İşte belge bu devedir. Kuyudan su içmek hakkı belirli bir gün onun ve belirli bir gün de sizindir; sakın ona bir kötülük yapmayın, yoksa sizi büyük günün azabı yakalar&quot; dedi.');
