INSERT INTO "diyanet" VALUES(56,35,'56:35','Biz ceylan gözlüleri, defterleri sağdan verilenler için yeniden yaratmışızdır; onları bakire, eşlerine düşkün ve hepsini bir yaşta kılmışızdır.');
INSERT INTO "diyanet" VALUES(56,36,'56:36','Biz ceylan gözlüleri, defterleri sağdan verilenler için yeniden yaratmışızdır; onları bakire, eşlerine düşkün ve hepsini bir yaşta kılmışızdır.');
INSERT INTO "diyanet" VALUES(56,37,'56:37','Biz ceylan gözlüleri, defterleri sağdan verilenler için yeniden yaratmışızdır; onları bakire, eşlerine düşkün ve hepsini bir yaşta kılmışızdır.');
INSERT INTO "diyanet" VALUES(56,38,'56:38','Biz ceylan gözlüleri, defterleri sağdan verilenler için yeniden yaratmışızdır; onları bakire, eşlerine düşkün ve hepsini bir yaşta kılmışızdır.');
INSERT INTO "diyanet" VALUES(56,39,'56:39','Bunların bir kısmı eski ümmetlerden, bir kısmı da sonrakilerdendir.');
INSERT INTO "diyanet" VALUES(56,40,'56:40','Bunların bir kısmı eski ümmetlerden, bir kısmı da sonrakilerdendir.');
INSERT INTO "diyanet" VALUES(56,41,'56:41','Defterleri soldan verilenler; ne yazık o solculara!');
INSERT INTO "diyanet" VALUES(56,42,'56:42','İnsanın içine işleyen bir sıcaklık ve kaynar su içinde, serinliği ve hoşluğu olmayan kara bir dumanın gölgesinde bulunurlar.');
INSERT INTO "diyanet" VALUES(56,43,'56:43','İnsanın içine işleyen bir sıcaklık ve kaynar su içinde, serinliği ve hoşluğu olmayan kara bir dumanın gölgesinde bulunurlar.');
INSERT INTO "diyanet" VALUES(56,44,'56:44','İnsanın içine işleyen bir sıcaklık ve kaynar su içinde, serinliği ve hoşluğu olmayan kara bir dumanın gölgesinde bulunurlar.');
INSERT INTO "diyanet" VALUES(56,45,'56:45','Çünkü onlar, bundan önce, dünyada, nimet içinde bulunurlar iken, büyük günah işlemekte direnir dururlardı.');
INSERT INTO "diyanet" VALUES(56,46,'56:46','Çünkü onlar, bundan önce, dünyada, nimet içinde bulunurlar iken, büyük günah işlemekte direnir dururlardı.');
INSERT INTO "diyanet" VALUES(56,47,'56:47','Şöyle söylerlerdi: &quot;Öldüğümüzde, toprak ve kemik yığını olduğumuzda mı, biz mi tekrar dirileceğiz?&quot;');
INSERT INTO "diyanet" VALUES(56,48,'56:48','&quot;Önce gelip geçmiş babalarımız da mı?&quot;');
INSERT INTO "diyanet" VALUES(56,49,'56:49','De ki: &quot;Şüphesiz öncekiler de, sonrakiler de belli bir günün belirli bir vaktinde toplanacaklardır.&quot;');
INSERT INTO "diyanet" VALUES(56,50,'56:50','De ki: &quot;Şüphesiz öncekiler de, sonrakiler de belli bir günün belirli bir vaktinde toplanacaklardır.&quot;');
INSERT INTO "diyanet" VALUES(56,51,'56:51','Sonra, siz ey sapıklar, yalanlayanlar!');
INSERT INTO "diyanet" VALUES(56,52,'56:52','Doğrusu bir zakkum ağacından yiyeceksiniz.');
INSERT INTO "diyanet" VALUES(56,53,'56:53','Karınlarınızı onunla dolduracaksınız;');
INSERT INTO "diyanet" VALUES(56,54,'56:54','Onun üzerine kaynar su içeceksiniz;');
INSERT INTO "diyanet" VALUES(56,55,'56:55','Hem de susamış develerin suya saldırışı gibi içeceksiniz;');
INSERT INTO "diyanet" VALUES(56,56,'56:56','İşte onlara, ceza günü sunulacak konukluk budur.');
INSERT INTO "diyanet" VALUES(56,57,'56:57','Sizi yaratan Biziz; hala tasdik etmez misiniz?');
INSERT INTO "diyanet" VALUES(56,58,'56:58','Söyleyin; akıttığınız meniden insanı yaratan siz misiniz, yoksa Biz mi yaratmaktayız?');
INSERT INTO "diyanet" VALUES(56,59,'56:59','Söyleyin; akıttığınız meniden insanı yaratan siz misiniz, yoksa Biz mi yaratmaktayız?');
INSERT INTO "diyanet" VALUES(56,60,'56:60','Ölümü aranızda Biz tayin ettik; sizi ortadan kaldırıp benzerlerinizi yerinize getirmeyi, sizi bilmediğiniz şekilde var etmeyi dilesek kimse önümüze geçemez.');
INSERT INTO "diyanet" VALUES(56,61,'56:61','Ölümü aranızda Biz tayin ettik; sizi ortadan kaldırıp benzerlerinizi yerinize getirmeyi, sizi bilmediğiniz şekilde var etmeyi dilesek kimse önümüze geçemez.');
INSERT INTO "diyanet" VALUES(56,62,'56:62','And olsun ki, ilk yaratmayı bilirsiniz, yine de düşünmez misiniz?');
INSERT INTO "diyanet" VALUES(56,63,'56:63','Söyleyin, ektiklerinizi yerden bitirenler sizler misiniz, yoksa Biz mi bitiriyoruz?');
INSERT INTO "diyanet" VALUES(56,64,'56:64','Söyleyin, ektiklerinizi yerden bitirenler sizler misiniz, yoksa Biz mi bitiriyoruz?');
INSERT INTO "diyanet" VALUES(56,65,'56:65','Dilersek Biz onu çerçöp yaparız, şaşar kalırsınız; &quot;Doğrusu borç altına girdik, hatta yoksun kaldık&quot;.');
INSERT INTO "diyanet" VALUES(56,66,'56:66','Dilersek Biz onu çerçöp yaparız, şaşar kalırsınız; &quot;Doğrusu borç altına girdik, hatta yoksun kaldık&quot;.');
INSERT INTO "diyanet" VALUES(56,67,'56:67','Dilersek Biz onu çerçöp yaparız, şaşar kalırsınız; &quot;Doğrusu borç altına girdik, hatta yoksun kaldık&quot;.');
INSERT INTO "diyanet" VALUES(56,68,'56:68','Söyleyin; içtiğiniz suyu buluttan indirenler sizler misiniz yoksa onu Biz mi indiririz?');
INSERT INTO "diyanet" VALUES(56,69,'56:69','Söyleyin; içtiğiniz suyu buluttan indirenler sizler misiniz yoksa onu Biz mi indiririz?');
INSERT INTO "diyanet" VALUES(56,70,'56:70','Dileseydik onu acılaştırırdık; hala şükretmez misiniz?');
INSERT INTO "diyanet" VALUES(56,71,'56:71','Söyleyin; yaktığınız ateşin ağacını var eden sizler misiniz, yoksa onu Biz mi var ederiz?');
INSERT INTO "diyanet" VALUES(56,72,'56:72','Söyleyin; yaktığınız ateşin ağacını var eden sizler misiniz, yoksa onu Biz mi var ederiz?');
INSERT INTO "diyanet" VALUES(56,73,'56:73','Biz onu bir ibret ve çölde konaklayanlar için yararlı kıldık.');
INSERT INTO "diyanet" VALUES(56,74,'56:74','Öyleyse çok büyük Rabbinin adını tesbih et.');
INSERT INTO "diyanet" VALUES(56,75,'56:75','Hayır; yıldızların yerleri üzerine yemin ederim; ki bunun ne büyük yemin olduğunu bir bilseniz!');
INSERT INTO "diyanet" VALUES(56,76,'56:76','Hayır; yıldızların yerleri üzerine yemin ederim; ki bunun ne büyük yemin olduğunu bir bilseniz!');
INSERT INTO "diyanet" VALUES(56,77,'56:77','Doğrusu bu Kitap, sadece arınmış olanların dokunabileceği, saklı bir Kitap''da mevcutken Alemlerin Rabbi tarafından indirilmiş olan Kuranı Kerim''dir.');
INSERT INTO "diyanet" VALUES(56,78,'56:78','Doğrusu bu Kitap, sadece arınmış olanların dokunabileceği, saklı bir Kitap''da mevcutken Alemlerin Rabbi tarafından indirilmiş olan Kuranı Kerim''dir.');
INSERT INTO "diyanet" VALUES(56,79,'56:79','Doğrusu bu Kitap, sadece arınmış olanların dokunabileceği, saklı bir Kitap''da mevcutken Alemlerin Rabbi tarafından indirilmiş olan Kuranı Kerim''dir.');
INSERT INTO "diyanet" VALUES(56,80,'56:80','Doğrusu bu Kitap, sadece arınmış olanların dokunabileceği, saklı bir Kitap''da mevcutken Alemlerin Rabbi tarafından indirilmiş olan Kuranı Kerim''dir.');
INSERT INTO "diyanet" VALUES(56,81,'56:81','Siz bu sözü mü hor görüyorsunuz?');
INSERT INTO "diyanet" VALUES(56,82,'56:82','Rızkınıza şükredeceğiniz yere onu vereni mi yalanlıyorsunuz?');
INSERT INTO "diyanet" VALUES(56,83,'56:83','Kişinin canı boğaza dayanınca ve siz o zaman bakıp kalırken, Biz o kişiye sizden daha yakınızdır, ama görmezsiniz.');
INSERT INTO "diyanet" VALUES(56,84,'56:84','Kişinin canı boğaza dayanınca ve siz o zaman bakıp kalırken, Biz o kişiye sizden daha yakınızdır, ama görmezsiniz.');
INSERT INTO "diyanet" VALUES(56,85,'56:85','Kişinin canı boğaza dayanınca ve siz o zaman bakıp kalırken, Biz o kişiye sizden daha yakınızdır, ama görmezsiniz.');
INSERT INTO "diyanet" VALUES(56,86,'56:86','Siz dirilip yaptıklarınıza karşılık görmeyecekseniz ve eğer bu sözünüzde samimi iseniz, o çıkmak üzere olan canı geri çevirsenize!');
INSERT INTO "diyanet" VALUES(56,87,'56:87','Siz dirilip yaptıklarınıza karşılık görmeyecekseniz ve eğer bu sözünüzde samimi iseniz, o çıkmak üzere olan canı geri çevirsenize!');
INSERT INTO "diyanet" VALUES(56,88,'56:88','Eğer ölen o kişi, gözdelerden ise, rahatlık, hoşluk ve nimet cenneti onundur.');
INSERT INTO "diyanet" VALUES(56,89,'56:89','Eğer ölen o kişi, gözdelerden ise, rahatlık, hoşluk ve nimet cenneti onundur.');
INSERT INTO "diyanet" VALUES(56,90,'56:90','Eğer defteri sağdan verilenlerden ise,');
INSERT INTO "diyanet" VALUES(56,91,'56:91','&quot;Ey sağcılardan olan kişi, sana selam olsun!&quot; denir.');
INSERT INTO "diyanet" VALUES(56,92,'56:92','Eğer, sapık yalancılardan ise,');
INSERT INTO "diyanet" VALUES(56,93,'56:93','Ona kaynar sudan konukluk sunulur.');
INSERT INTO "diyanet" VALUES(56,94,'56:94','Cehenneme sokulur.');
INSERT INTO "diyanet" VALUES(56,95,'56:95','Doğrusu kesin gerçek budur.');
INSERT INTO "diyanet" VALUES(56,96,'56:96','Öyleyse çok büyük Rabbinin adını tesbih et.');
INSERT INTO "diyanet" VALUES(57,1,'57:1','Göklerde ve yerde olanlar Allah''ı tesbih ederler. O güçlüdür, Hakim''dir.');
INSERT INTO "diyanet" VALUES(57,2,'57:2','Göklerin ve yerin hükümranlığı O''nundur; diriltir, öldürür. O, her şeye Kadir''dir.');
INSERT INTO "diyanet" VALUES(57,3,'57:3','O her şeyden öncedir; kendisinden sonraya hiçbir şeyin kalmayacağı son''dur; varlığı aşikardır; gerçek mahiyeti insan için gizlidir. O her şeyi bilir.');
INSERT INTO "diyanet" VALUES(57,4,'57:4','Gökleri ve yeri altı günde yaratan, sonra arşa hükmeden, yere gireni ve ondan çıkanı, gökten ineni ve oraya yükseleni bilen O''dur. Nerede olursanız olun, O, sizinle beraberdir. Allah yaptıklarınızı görür.');
INSERT INTO "diyanet" VALUES(57,5,'57:5','Göklerin ve yerin hükümranlığı O''nundur. Bütün işler Allah''a döndürülür.');
INSERT INTO "diyanet" VALUES(57,6,'57:6','Geceyi gündüze katar, gündüzü geceye katar; O kalblerde olanı bilendir.');
INSERT INTO "diyanet" VALUES(57,7,'57:7','Ey insanlar! Allah''a ve Peygamberine inanın; sizi varis kıldığı şeylerden sarfedin; aranızdan, inanıp da sarfeden kimselere büyük ecir vardır');
INSERT INTO "diyanet" VALUES(57,8,'57:8','Peygamber sizi, Rabbinize inanmaya çağırdığı halde, Allah''a niçin inanmazsınız? Hem O, sizden söz almıştı, inanmışlar iseniz; bu çağrıya koşun.');
INSERT INTO "diyanet" VALUES(57,9,'57:9','Sizi karanlıklardan aydınlığa çıkarmak için kuluna, apaçık ayetler indiren O''dur. Doğrusu Allah size karşı şefkatlidir, merhametlidir.');
INSERT INTO "diyanet" VALUES(57,10,'57:10','Göklerin ve yerin mirasçısı Allah olduğu halde, Allah yolunda siz niçin sarf etmiyorsunuz? İçinizden Mekke''nin fethinden önce sarfeden ve savaşan kimseler, daha sonra sarfedip savaşan kimselerle bir değildirler, öncekiler daha üstün derecededirler. Allah, hepsine cenneti vadetmiştir. Allah, işlediklerinizden haberdardır.');
INSERT INTO "diyanet" VALUES(57,11,'57:11','Allah''a kim güzel bir ödünç takdiminde bulunursa, Allah karşılığını kat kat verir, ona cömertçe verilecek bir ecir de vardır.');
INSERT INTO "diyanet" VALUES(57,12,'57:12','İnanmış erkek ve kadınları, defterleri sağdan verilmiş ve ışıkları önlerinde olarak giderken gördüğün gün onlara şöyle denecektir: &quot;Müjde; bugün içlerinden ırmaklar akan, içinde temelli kalacağınız cennetler sizindir.&quot; İşte bu büyük kurtuluştur.');
INSERT INTO "diyanet" VALUES(57,13,'57:13','İkiyüzlü erkek ve kadınlar müminlere: &quot;Bizi de gözetin; ışığınızdan faydalanalım&quot; dedikleri gün, onlara: &quot;Ardınıza dönün de ışık arayın&quot; denir; inananlarla ikiyüzlüler arasına, kapısının içinde rahmet ve dışında azap olan bir sur çekilir.');
INSERT INTO "diyanet" VALUES(57,14,'57:14','İkiyüzlüler, inananlara: &quot;Biz sizinle beraber değil miydik&quot; diye seslenirler. Onlar: &quot;Evet öyle; fakat sizler kendinizi aldattınız, bize pusu kurdunuz, Allah''ın buyruğu gelene kadar dinde şüpheye düştünüz; sizi kuruntular aldattı; sizi şeytanlar Allah''a karşı da ayarttı.&quot;');
INSERT INTO "diyanet" VALUES(57,15,'57:15','Bugün sizden ve inkar edenlerden fidye kabul edilmez; varacağınız yer ateştir, layığınız orasıdır; ne kötü bir dönüştür!');
INSERT INTO "diyanet" VALUES(57,16,'57:16','İnananların gönüllerinin Allah''ı anması ve O''ndan inen gerçeğe içten bağlanması zamanı daha gelmedi mi? Onlar, daha önce kendilerine kitap verilenler gibi olmasınlar; onların üzerinden uzun zaman geçti de kalbleri katılaştı; çoğu, yoldan çıkmış kimselerdir.');
INSERT INTO "diyanet" VALUES(57,17,'57:17','Allah''ın, yeryüzünü ölümünden sonra dirilttiğini bilin; size, akledesiniz diye açık açık deliller anlattık.');
INSERT INTO "diyanet" VALUES(57,18,'57:18','Doğrusu, sadaka veren erkek ve kadınlara, Allah''a güzel bir takdimde bulunanlara kat kat karşılık verilir; onlara cömertçe verilecek bir ecir vardır.');
INSERT INTO "diyanet" VALUES(57,19,'57:19','Allah''a ve peygamberlerine inananlara, dosdoğru olanlara ve Allah yolunda şehit düşenlere, işte onlara, Rableri katında nur ve ecir vardır. İnkar edip ayetlerimizi yalanlayanlar, işte onlar da, cehennemlik olanlardır.');
INSERT INTO "diyanet" VALUES(57,20,'57:20','Bilin ki, dünya hayatı oyun, oyalanma, süslenme, aranızda övünme ve daha çok mal ve çocuk sahibi olmaktan ibarettir. Bu, yağmurun bitirdiği, ekicilerin de hoşuna giden bir bitkiye benzer; sonra kurur, sapsarı olduğu görülür, sonra çerçöp olur. Ahirette çetin azap da vardır. Allah''ın hoşnudluğu ve bağışlaması da vardır; dünya hayatı ise sadece aldatıcı bir geçinmedir.');
INSERT INTO "diyanet" VALUES(57,21,'57:21','Ey İnsanlar! Rabbiniz tarafından bağışlanmaya, Allah''a ve Peygamberine inananlar için hazırlanmış, genişliği yerle göğün genişliği kadar olan cennete koşusun; bu Allah''ın dilediğine verdiği lütfudur. Allah, büyük lütuf sahibidir.');
INSERT INTO "diyanet" VALUES(57,22,'57:22','Yeryüzüne ve sizin başınıza gelen herhangi bir musibet yoktur ki biz onu yaratmadan önce o, Kitap''da bulunmasın. Doğrusu bu Allah''a kolaydır.');
INSERT INTO "diyanet" VALUES(57,23,'57:23','Bu, kaybettiğinize üzülmemeniz ve Allah''ın size verdiği nimetlerle şımarmamanız içindir. Allah, kendini beğenip öğünen hiç kimseyi sevmez;');
INSERT INTO "diyanet" VALUES(57,24,'57:24','Bunlar cimrilik ederler ve insanlara da cimrilik yapmalarını söylerler. Allah''ın buyruğundan kim yüz çevirirse bilsin ki, Allah şüphesiz müstağni ve övülmeğe layık olandır.');
INSERT INTO "diyanet" VALUES(57,25,'57:25','And olsun ki peygamberlerimizi belgelerle gönderdik; insanların doğru (adaletli) hareket etmeleri için peygamberlere kitap ve ölçü indirdik; pek sert olan ve insanlara birçok faydası bulunan demiri de indirdik. Bu, Allah''ın dinine ve peygamberlerine görmeksizin yardım edenleri meydana çıkarması içindir. Doğrusu Allah kuvvetlidir, güçlüdür.');
INSERT INTO "diyanet" VALUES(57,26,'57:26','And olsun ki Nuh''u ve İbrahim''i Biz gönderdik; ikisinin soyundan gelenlere peygamberlik ve kitap verdik; soylarından gelenlerin kimi doğru yoldadır, birçoğu da yoldan çıkmıştır.');
INSERT INTO "diyanet" VALUES(57,27,'57:27','Onların izleri üzerinden peygamberlerimizi ard arda gönderdik; Meryem oğlu İsa''yı da ardlarından gönderdik ve ona İncil''i verdik; ona uyanların gönüllerine şefkat ve merhamet duyguları koyduk; üzerlerine bizim gerekli kılmadığımız fakat kendilerinin güya Allah''ın rızasını kazanmak için ortaya attıkları ruhbaniyete bile gereği gibi riayet etmediler; içlerinde inanmış olan kimselere ecirlerini verdik; ama çoğu yoldan çıkmışlardır.');
INSERT INTO "diyanet" VALUES(57,28,'57:28','Ey inananlar! Allah''tan sakının, Peygamberine inanın ki, Allah size rahmetini iki kat versin; size ışığında yürüyeceğiniz bir ışık var etsin; sizi bağışlasın; Allah bağışlayandır, acıyandır.');
INSERT INTO "diyanet" VALUES(57,29,'57:29','Kitap ehli bilsinler ki, Allah''ın lütfundan hiçbir şey elde edemezler (bu lütfa malik değillerdir); lütuf Allah''ın elindedir, onu dilediğine verir; Allah büyük lütuf sahibidir.');
INSERT INTO "diyanet" VALUES(58,1,'58:1','Kocası hakkında seninle tartışan ve Allah''a şikayette bulunan kadının sözünü Allah işitmiştir; esasen Allah konuşmanızı işitir. Doğrusu Allah işitendir, görendir.');
INSERT INTO "diyanet" VALUES(58,2,'58:2','İçinizde karılarını &quot;zıhar&quot; yapanlar bilsinler ki, karıları anneleri değildir; anneleri ancak, onları doğuranlardır. Doğrusu söyledikleri kötü ve asılsız bir sözdür. Allah şüphesiz affedendir, bağışlayandır.');
INSERT INTO "diyanet" VALUES(58,3,'58:3','Karılarını zıhar yoluyla boşamak isteyip, sonra sözlerinden dönenlerin, ailesiyle temas etmeden bir köle azad etmeleri gerekir. Size bu hususta böylece öğüt verilmektedir. Allah, işlediklerinizden haberdardır.');
INSERT INTO "diyanet" VALUES(58,4,'58:4','Azad edecek köle bulamayanın, ailesiyle temastan önce iki ay birbiri peşinden oruç tutması gerekir. Buna gücü yetmeyen, altmış düşkünü doyurur. Bu kolaylık, Allah''a ve Peygamberine inanmış olmanızdan ötürüdür; bunlar, Allah''ın koyduğu sınırlardır; inkar edenler için can yakıcı azap vardır.');
INSERT INTO "diyanet" VALUES(58,5,'58:5','Allah''a ve Peygamberine karşı gelenler, kendilerinden öncekiler nasıl alçaltıldı ise öyle alçaltılacaklardır. Biz, apaçık ayetler indirmişizdir, bunları inkar edene alçaltıcı azap vardır.');
INSERT INTO "diyanet" VALUES(58,6,'58:6','Allah onların hepsini dirilttiği gün, kendilerine işlediklerini haber verir; Allah onları bir bir saymıştır, fakat kendileri unutmuşlardır. Allah her şeye şahiddir.');
INSERT INTO "diyanet" VALUES(58,7,'58:7','Göklerde olanları da, yerde olanları da Allah''ın bildiğini bilmez misin? Üç kişinin gizli bulunduğu yerde dördüncü mutlaka O''dur; beş kişinin gizli bulunduğu yerde altıncıları mutlaka O''dur; bunlardan az veya çok, ne olursa olsunlar nerede bulunurlarsa bulunsunlar, mutlak onlarla beraberdir. Sonra, kıyamet günü, işlediklerini onlara haber verir. Doğrusu Allah her şeyi bilendir.');
INSERT INTO "diyanet" VALUES(58,8,'58:8','Gizli toplantıdan menedilen, sonra menolundukları şeyi yapmaya kalkışarak günah işlemek, düşmanlık etmek ve Peygambere karşı gelmek konusunda gizli gizli konuşanları görmedin mi? Sana geldiklerinde, Allah''ın seni selamlamadığı bir şekilde seni selamlarlar; içlerinden, &quot;Gerçekten peygamber olsaydı Allah''ın bizi, söylediklerimizden ötürü, cezalandırması gerekmez miydi?&quot; derler. Cehennem onlara yeter. Oraya girerler, ne kötü dönüştür!');
INSERT INTO "diyanet" VALUES(58,9,'58:9','Ey inananlar! Gizli konuştuğunuz zaman, günah işlemeyi, düşmanlık etmeyi ve Peygambere karşı gelmeyi fısıldaşmayın; iyilik yapmak ve Allah''a karşı gelmekten sakınmayı konuşun; kıyamet günü huzurunda toplanacağınız Allah''tan sakının.');
INSERT INTO "diyanet" VALUES(58,10,'58:10','Gizli toplantılar inananları üzmek için şeytanın istediği şeydir; Allah''ın izni olmadıkça şeytan onlara bir zarar veremez; inananlar yalnız Allah''a güvensinler.');
INSERT INTO "diyanet" VALUES(58,11,'58:11','Ey inananlar! Toplantılarda, size, &quot;Yer açın&quot; denince yer açın ki Allah da size genişlik versin; &quot;Kalkın&quot; denildiği zaman da hemen kalkın ki, Allah, içinizden inanmış olanları ve kendilerine ilim verilenleri derecelerle yükseltsin. Allah işlediklerinizden haberdardır.');
INSERT INTO "diyanet" VALUES(58,12,'58:12','Ey inananlar! Peygamberle hususi olarak konuşacağınızda, bu konuşmanızdan önce fakirlere sadaka veriniz; bu, sizin daha iyi ve daha temiz olmanız içindir. Eğer sadaka verecek bir şey bulamazsanız üzülmeyiniz. Allah şüphesiz bağışlayandır, acıyandır.');
INSERT INTO "diyanet" VALUES(58,13,'58:13','Hususi konuşmanızdan önce sadaka vermekten ürktünüz mü ki bunu yerine getirmediniz? Ama Allah, tevbenizi kabul etmiştir. Öyleyse namazı kılın, zekatı verin, Allah''a ve Peygamberine itaat edin. Allah, işlediklerinizden haberdardır.');
INSERT INTO "diyanet" VALUES(58,14,'58:14','Allah''ın gazabettiği milleti dost edinen münafıkları görmedin mi? Onlar ne sizdendir ne de onlardan, bile bile, yalan yere yemin etmektedirler.');
INSERT INTO "diyanet" VALUES(58,15,'58:15','Allah, onlara çetin bir azap hazırlamıştır. İşledikleri şey ne kötüdür!');
INSERT INTO "diyanet" VALUES(58,16,'58:16','Yeminlerini kalkan edindiler de, Allah yolundan alıkoydular; onlara alçaltıcı bir azap vardır.');
INSERT INTO "diyanet" VALUES(58,17,'58:17','Malları ve çocukları, onlara, Allah katında bir fayda sağlamaz. Onlar cehennemliklerdir, orada temelli kalacaklardır.');
INSERT INTO "diyanet" VALUES(58,18,'58:18','Allah, onların hepsini tekrar dirilttiği gün, size yemin ettikleri gibi O''na yemin ederler; kendilerine bir yarar sağlayacağını sanırlar. Dikkat edin; onlar şüphesiz yalancıdırlar.');
INSERT INTO "diyanet" VALUES(58,19,'58:19','Şeytan onların başlarına dikilip Allah''ı anmayı unutturmuştur. İşte onlar şeytanın taraftarlarıdır. İyi bilin; şeytanın taraftarları elbette hüsrandadırlar.');
INSERT INTO "diyanet" VALUES(58,20,'58:20','Allah''a ve Peygamberine karşı gelenler; işte onlar, en alçak kimselerle beraberdirler.');
INSERT INTO "diyanet" VALUES(58,21,'58:21','Allah, &quot;And olsun ki Ben ve peygamberlerim üstün geleceğiz&quot; diye yazmıştır. Doğrusu Allah kuvvetlidir, güçlüdür.');
INSERT INTO "diyanet" VALUES(58,22,'58:22','Allah''a ve ahiret gününe inanan bir milletin, babaları veya oğulları veya kardeşleri ya da akrabaları olsa bile Allah''a ve Peygamberine karşı gelenlere, sevgi beslediklerini görmezsin. İşte Allah, imanı bunların kalblerine yazmış, katından bir nur ile onları desteklemiştir. Onları, içlerinden ırmaklar akan, içinde temelli kalacakları cennetlere koyar. Allah onlardan hoşnut olmuştur, onlar da Allah''tan hoşnut olmuştur. İşte bunlar, Allah''tan yana olanlardır. İyi bilin ki, saadete erecek olanlar, Allah''tan yana olanlardır.');
INSERT INTO "diyanet" VALUES(59,1,'59:1','Göklerde olanlar da yerde olanlar da Allah''ı tesbih ederler. O güçlüdür, Hakim''dir.');
INSERT INTO "diyanet" VALUES(59,2,'59:2','Kitap ehlinden inkarcı olanları ilk sürgünde yurtlarından çıkaran O''dur. Oysa ey inananlar! Çıkacaklarını sanmamıştınız, onlar da, kalelerinin kendilerini Allah''tan koruyacağını sanmışlardı. Ama Allah''ın azabı onlara beklemedikleri yerden geldi, kalblerine korku saldı; evlerini kendi elleriyle ve inananların elleriyle yıkıyorlardı. Ey akıl sahipleri! Ders alın.');
INSERT INTO "diyanet" VALUES(59,3,'59:3','Allah onlara sürülmeyi yazmamış olsaydı, dünyada başka şekilde azap verecekti. Ahirette onlara ateş azabı vardır.');
INSERT INTO "diyanet" VALUES(59,4,'59:4','Bu, Allah''a ve Peygamberine karşı gelmelerinden ötürüdür. Kim Allah''a karşı gelirse bilsin ki Allah''ın cezalandırması şüphesiz çetindir.');
INSERT INTO "diyanet" VALUES(59,5,'59:5','İnkarcı kitap ehlinin yurtlarında hurma ağaçlarını kesmeniz veya onları kesmeyip gövdeleri üzerinde ayakta bırakmanız Allah''ın izniyledir. Allah yoldan çıkanları böylece rezilliğe uğratır.');
INSERT INTO "diyanet" VALUES(59,6,'59:6','Ey inananlar! Onların mallarından, Allah''ın Peygamberine verdiği şeyler için siz ne at ve ne de deve sürdünüz; fakat Allah peygamberlerine, dilediği kimselere karşı üstünlük verir. Allah herşeye Kadir''dir.');
INSERT INTO "diyanet" VALUES(59,7,'59:7','Allah''ın, fethedilen memleketler halkının mallarından Peygamberine verdikleri; Allah, Peygamber, yakınlar, yetimler, yoksullar ve yolda kalmışlar içindir; ta ki içinizdeki zenginler arasında elden ele dolaşan bir devlet olmasın. Peygamber size ne verirse onu alın, sizi neden menederse ondan geri durun; Allah''tan sakının, doğrusu Allah''ın cezalandırması çetindir.');
INSERT INTO "diyanet" VALUES(59,8,'59:8','Allah''ın verdiği bu ganimet malları bilhassa; yurtlarından ve mallarından edilmiş olan, Allah''tan bir lütuf ve rıza dileyen, Allah''ın dinine ve Peygamberine yardım eden muhacir fakirlerindir. İşte doğru olanlar bunlardır.');
INSERT INTO "diyanet" VALUES(59,9,'59:9','Daha önceden Medine''yi yurt edinmiş ve gönüllerine imanı yerleştirmiş olan kimseler, kendilerine hicret edip gelenleri severler; onlara verilenler karşısında içlerinde bir çekememezlik hissetmezler; kendileri zaruret içinde bulunsalar bile onları kendilerinden önde tutarlar. Nefsinin tamahkarlığından korunabilmiş kimseler, işte onlar saadete erenlerdir.');
INSERT INTO "diyanet" VALUES(59,10,'59:10','Onlardan sonra gelenler: &quot;Rabbimiz! Bizi ve bizden önce inanmış olan kardeşlerimizi bağışla; kalbimizde müminlere karşı kin bırakma; Rabbimiz! Şüphesiz Sen şefkatlisin, merhametlisin&quot; derler.');
INSERT INTO "diyanet" VALUES(59,11,'59:11','Münafıkların, kitap ehlinin inkarcılarından olan kardeşlerine: &quot;Eğer siz yurdunuzdan çıkarılırsanız and olsun ki, biz de sizinle beraber çıkarız; sizin aleyhinizde kimseye asla uymayız; eğer savaşa tutuşursanız mutlaka size yardım ederiz&quot; dediklerini görmedin mi? Allah onların yalancı olduklarına şahidlik eder.');
INSERT INTO "diyanet" VALUES(59,12,'59:12','Onlar çıkarılmış olsalar, and olsun ki, onlarla beraber çıkmazlar; savaşa tutuşmuş olsalar, and olsun ki, onlara yardıma koşmazlar; onlara yardıma gitseler, mutlaka geri dönüp kaçarlar, sonra yardım da görmezler.');
INSERT INTO "diyanet" VALUES(59,13,'59:13','Ey inananlar! Onların yüreklerine korku salan, Allah''tan çok sizlersiniz; çünkü onlar, anlamayan kimselerdir.');
INSERT INTO "diyanet" VALUES(59,14,'59:14','Onlar sizinle toplu olarak, ancak surla çevrilmiş kasabalar içinde veya duvarlar arkasından savaşı kabul edebilirler. Kendi aralarındaki çekişmeleri ise serttir; onları birlik sanırsın, oysa kalbleri birbirinden ayrıdır. Bu, akletmeyen bir topluluk olmalarındandır.');
INSERT INTO "diyanet" VALUES(59,15,'59:15','Onların durumu, kendilerinden az zaman önce geçmiş ve işlerinin karşılığını tatmış olanların durumu gibidir. Onlara can yakıcı azap vardır.');
INSERT INTO "diyanet" VALUES(59,16,'59:16','İkiyüzlülerin durumu insana: &quot;İnkar et!&quot; deyip, insan da inkar edince: &quot;Doğrusu ben senden uzağım; Alemlerin Rabbi olan Allah''tan korkarım&quot; diyen şeytanın durumu gibidir.');
INSERT INTO "diyanet" VALUES(59,17,'59:17','İkisinin sonucu da, içinde temelli kalacakları ateş olacaktır. Zalimlerin cezası budur.');
INSERT INTO "diyanet" VALUES(59,18,'59:18','Ey inananlar! Allah''tan sakının; herkes yarına ne hazırladığına baksın; Allah''tan sakının, çünkü Allah işlediklerinizden haberdardır.');
INSERT INTO "diyanet" VALUES(59,19,'59:19','Allah''ı unutup da, Allah''ın da kendilerini kendilerine unutturduğu kimseler gibi olmayın; onlar, yoldan çıkmış kimselerdir.');
INSERT INTO "diyanet" VALUES(59,20,'59:20','Cehennemliklerle cennetlikler bir değildir. Kurtuluşa ermiş kimseler cennetliklerdir.');
INSERT INTO "diyanet" VALUES(59,21,'59:21','Eğer Biz Kuran''ı bir dağa indirmiş olsaydık, sen, onun, Allah korkusuyla başeğerek parça parça olduğunu görürdün. Bu misalleri, insanlar düşünsünler diye veriyoruz.');
INSERT INTO "diyanet" VALUES(59,22,'59:22','O, görüleni de görülmeyeni de bilen, kendisinden başka tanrı olmayan Allah''tır. O, acıyıcı olandır, acıyandır.');
INSERT INTO "diyanet" VALUES(59,23,'59:23','O, kendisinden başka tanrı olmayan, hükümran, çok kutsal; esenlik veren, güvenlik veren, görüp gözeten, güçlü, buyruğunu herşeye geçiren, ulu olan, Allah''tır. Allah onların koştukları eşlerden (ortaklardan) münezzehtir.');
INSERT INTO "diyanet" VALUES(59,24,'59:24','O, vareden, güzel yaratan, yarattıklarına şekil veren, en güzel adlar kendisinin olan Allah''tır. Göklerde ve yerde olanlar O''nu tesbih ederler. O güçlüdür, Hakim''dir.');
INSERT INTO "diyanet" VALUES(60,1,'60:1','Ey inananlar! Benim de düşmanım, sizin de düşmanınız olanları dost edinmeyin. Onlar, size gelen gerçeği inkar etmişken, onlara sevgi gösteriyorsunuz; oysa onlar, Rabbiniz olan Allah''a inandığınızdan ötürü sizi ve Peygamberi yurdunuzdan çıkarıyorlar. Eğer sizler Benim yolumda savaşmak ve rızamı kazanmak için çıkmışsanız onlara nasıl sevgi gösterirsiniz? Ben, sizin gizlediğinizi de, açığa vurduğunuzu da bilirim. İçinizden onlara sevgi gösteren kimse, şüphesiz doğru yoldan sapmıştır.');
INSERT INTO "diyanet" VALUES(60,2,'60:2','Eğer sizi elegeçirirlerse sizin onlara gösterdiğiniz sevgiyi göstermezler, size düşman olurlar, ellerini ve dillerini fenalık etmek için uzatırlar, keşke inkar etseniz isterler.');
INSERT INTO "diyanet" VALUES(60,3,'60:3','Yakınlarınız ve çocuklarınız size kıyamet gününde bir fayda veremezler. Allah onlarla sizi ayırır. Allah işlediklerinizi görendir.');
INSERT INTO "diyanet" VALUES(60,4,'60:4','İbrahim ve onunla beraber olanlarda, sizin için uyulacak güzel bir örnek vardır. Onlar milletlerine şöyle demişlerdi: &quot;Biz sizden ve Allah''tan başka taptıklarınızdan uzağız; sizin dininizi inkar ediyoruz; bizimle sizin aranızda yalnız Allah''a inanmanıza kadar ebedi düşmanlık ve öfke başgöstermiştir.&quot; -Yalnız, İbrahim''in, babasına: &quot;And olsun ki, senin için mağfiret dileyeceğim, fakat sana Allah''tan gelecek herhangi bir şeyi savmaya gücüm yetmez&quot; sözü bu örneğin dışındadır- &quot;Rabbimiz! Sana güvendik, Sana yöneldik; dönüş Sanadır.&quot;');
INSERT INTO "diyanet" VALUES(60,5,'60:5','&quot;Rabbimiz! Bizi, inkar edenlerle deneme; bizi bağışla, doğrusu Sen, güçlü olan, Hakim olansın.&quot;');
INSERT INTO "diyanet" VALUES(60,6,'60:6','And olsun ki, sizlerden, Allah''ı ve ahiret gününü uman kimse için, bunlarda güzel örnekler vardır. Kim yüz çevirirse kendi aleyhine olur, doğrusu Allah müstağnidir, övülmeğe layıktır.');
INSERT INTO "diyanet" VALUES(60,7,'60:7','Allah''ın sizinle, düşmanlık gösterdiğiniz kimseler arasında bir sevgi yaratması umulur; Allah Kadir''dir, Allah bağışlayandır, acıyandır.');
INSERT INTO "diyanet" VALUES(60,8,'60:8','Allah, din uğrunda sizinle savaşmayan, sizi yurdunuzdan çıkarmayan kimselere iyilik yapmanızı ve onlara karşı adil davranmanızı yasak kılmaz; doğrusu Allah adil olanları sever.');
INSERT INTO "diyanet" VALUES(60,9,'60:9','Allah, ancak sizinle din uğrunda savaşanları, sizi yurtlarınızdan çıkaranları ve çıkarılmanıza yardım edenleri dost edinmenizi yasak eder; kim onları dost edinirse, işte onlar zalimdir.');
INSERT INTO "diyanet" VALUES(60,10,'60:10','Ey inananlar! İnanmış kadınlar hicret ederek size gelirlerse onları deneyin, hicretlerinin sebebini inceleyin. Allah onların imanlarını çok iyi bilir. Onların mümin kadınlar olduklarını öğrenirseniz, inkarcılara geri çevirmeyin. Bu kadınlar, o inkarcılara helal değildir Onlar da bunlara helal olmazlar. İnkarcıların bu kadınlara verdikleri mehirleri iade edin: Bu kadınların mehirlerini kendilerine verdiğiniz zaman, onlarla evlenmenizde bir engel yoktur. İnkarcı kadınları nikahınızda tutmayın; onlara verdiğiniz mehri isteyin; inkarcı erkekler de hicret eden mümin kadınlara verdikleri mehirleri istesinler. Allah''ın hükmü budur; aranızda O hükmeder. Allah bilendir, Hakim''dir.');
INSERT INTO "diyanet" VALUES(60,11,'60:11','Ey mümin erkekler! Eğer inkar eden (kafir olan) eşlerinize sarfettiklerinizden inkarcılara bir şey geçecek olursa ganimetten, eşleri giden mümin erkeklere sarfettikleri miktar kadarını verin. İnandığınız Allah''a karşı gelmekten sakının.');
INSERT INTO "diyanet" VALUES(60,12,'60:12','Ey Peygamber! İnanmış kadınlar, Allah''a hiçbir ortak koşmamak, hırsızlık yapmamak, zina etmemek, çocuklarını öldürmemek, başkasının çocuğunu sahiplenerek kocasına isnadda bulunmamak ve uygun olanı işlemekte sana karşı gelmemek şartıyla sana beyat etmek üzere geldikleri zaman, onları kabul et; onlara Allah''tan bağışlanma dile; doğrusu Allah, bağışlayandır, acıyandır.');
INSERT INTO "diyanet" VALUES(60,13,'60:13','Ey inananlar! Allah''ın gazabına uğramış milleti dost edinmeyin; inkarcıların kabirde bulunan kimselerden umutlarını kestikleri gibi, onlar da, ahiretten umutlarını kesmişlerdir.');
INSERT INTO "diyanet" VALUES(61,1,'61:1','Göklerde olanlar da yerde olanlar da Allah''ı tesbih ederler. O, güçlüdür, Hakim''dir.');
INSERT INTO "diyanet" VALUES(61,2,'61:2','Ey inananlar! Yapmayacağınız şeyi niçin söylersiniz?');
INSERT INTO "diyanet" VALUES(61,3,'61:3','Yapmayacağınız şeyi söylemeniz, Allah katında büyük gazaba sebep olur.');
INSERT INTO "diyanet" VALUES(61,4,'61:4','Doğrusu Allah, kendi uğrunda, kenetlenmiş bir duvar gibi, saf halinde çarpışanları sever.');
INSERT INTO "diyanet" VALUES(61,5,'61:5','Musa milletine: &quot;Ey milletim! Beni niçin incitirsiniz? Oysa, benim size gönderilmiş Allah''ın bir peygamberi olduğumu biliyorsunuz&quot; demişti. Ama onlar yoldan sapınca, Allah da onların kalblerini saptırmıştı. Allah, yoldan çıkan milleti doğru yola eriştirmez.');
INSERT INTO "diyanet" VALUES(61,6,'61:6','Meryem oğlu İsa: &quot;Ey İsrailoğulları! Doğrusu ben, benden önce gelmiş olan Tevrat''ı doğrulayan, benden sonra gelecek ve adı Ahmet olacak bir peygamberi müjdeleyen, Allah''ın size gönderilmiş bir peygamberiyim&quot; demişti. Ama o elçi, kendilerine belgelerle geldiği zaman: &quot;Bu, apaçık bir sihirdir&quot; demişlerdi.');
INSERT INTO "diyanet" VALUES(61,7,'61:7','Müslüman olmağa çağırılmışken gelmeyip Allah''a karşı yalan uydurandan daha zalim kimdir? Allah, zalim olan milleti doğru yola eriştirmez.');
INSERT INTO "diyanet" VALUES(61,8,'61:8','Ağızlarıyla Allah''ın nurunu söndürmek isterler. İnkarcılar ne kadar istemeseler de, Allah nurunu, dinini tamamlayacaktır.');
INSERT INTO "diyanet" VALUES(61,9,'61:9','Ortak koşanlar istemese de, dinini bütün dinlerden üstün kılmak için, Peygamberini, doğruluk rehberi Kuran ve gerçek dinle gönderen O''dur.');
INSERT INTO "diyanet" VALUES(61,10,'61:10','Ey inananlar! Sizi can yakıcı bir azabdan kurtaracak, kazançlı bir yolu size göstereyim mi?');
INSERT INTO "diyanet" VALUES(61,11,'61:11','Allah''a ve Peygamberine inanırsınız; Allah yolunda canlarınızla, mallarınızla cihat edersiniz; bilseniz, bu sizin için en iyi yoldur.');
INSERT INTO "diyanet" VALUES(61,12,'61:12','Böyle yaparsanız, Allah günahlarınızı size bağışlar, sizi, içlerinden ırmaklar akan cennetlere, Adn cennetlerinde hoş yerlere koyar. Büyük kurtuluş budur.');
INSERT INTO "diyanet" VALUES(61,13,'61:13','Bundan başka, sevdiğiniz bir şey daha: Allah katından bir yardım ve yakın bir zafer vardır. İnananlara müjde ver.');
INSERT INTO "diyanet" VALUES(61,14,'61:14','Ey inananlar! Allah''ın dininin yardımcıları olun. Nitekim, Meryem oğlu İsa, Havarilere: &quot;Allah''a giden yolda yardımcılarım kimlerdir?&quot; deyince, Havariler: &quot;Allah''ın dininin yardımcıları biziz&quot; demişlerdi. İsrailoğullarının bir takımı böylece inanmış, bir takımı da inkar etmişti; ama Biz, inananları düşmanlarına karşı destekledik de üstün geldiler.');
INSERT INTO "diyanet" VALUES(62,1,'62:1','Göklerde olanlar ve yerde bulunanlar, hükümran, çok kutsal, güçlü ve Hakim olan Allah''ı tesbih ederler.');
INSERT INTO "diyanet" VALUES(62,2,'62:2','Kitapsız (okuma-yazma bilmeyen) kimseler arasından, kendilerine ayetlerini okuyan, onları arıtan, onlara Kitabı ve hikmeti öğreten bir Peygamber gönderen O''dur. Onlar, daha önce, şüphesiz apaçık bir sapıklık içinde idiler.');
INSERT INTO "diyanet" VALUES(62,3,'62:3','Onlardan başkalarına da -ki henüz onlara katılmamışlardır- Kitap ve hikmeti öğretmek üzere, Peygamberi gönderen Allah''tır. O, güçlüdür, Hakim''dir.');
INSERT INTO "diyanet" VALUES(62,4,'62:4','Bu, Allah''ın dilediğine verdiği lütfüdür. Allah, büyük lütuf sahibidir.');
INSERT INTO "diyanet" VALUES(62,5,'62:5','Kendilerine Tevrat öğretildiği halde, onun gereğini yapmayanların durumu, sırtına kitap yüklenmiş merkebin durumu gibidir. Allah''ın ayetlerini yalanlayan kimselerin durumu ne kötüdür! Allah zalimleri doğru yola eriştirmez.');
INSERT INTO "diyanet" VALUES(62,6,'62:6','De ki: &quot;Ey Yahudiler! Bütün insanlar bir yana, yalnız kendinizin Allah''ın dostları olduğunuzu iddia ediyorsanız ve bunda samimi iseniz, ölümü dilesenize!&quot;');
INSERT INTO "diyanet" VALUES(62,7,'62:7','Yaptıklarından ötürü, ölümü asla dileyemezler. Allah, zalimleri bilendir.');
INSERT INTO "diyanet" VALUES(62,8,'62:8','De ki: &quot;Doğrusu kendisinden kaçtığınız ölüm mutlaka karşınıza çıkacaktır; sonra; görüleni de görülmeyeni de bilen Allah''a döndürüleceksiniz, O size işlediklerinizi haber verecektir.&quot;');
INSERT INTO "diyanet" VALUES(62,9,'62:9','Ey inananlar! Cuma günü namaz için ezan okunduğu zaman Allah''ı anmaya koşun; alım satımı bırakın; bilseniz, bu sizin için daha iyidir.');
INSERT INTO "diyanet" VALUES(62,10,'62:10','Namaz bitince yeryüzüne yayılın; Allah''ın lütfundan rızık isteyin; Allah''ı çok anın ki saadete erişesiniz.');
INSERT INTO "diyanet" VALUES(62,11,'62:11','Onlar bir kazanç veya bir eğlence gördüklerinde, seni ayakta bırakarak oraya yöneldiler. De ki: &quot;Allah katında olan, eğlenceden de kazançtan da hayırlıdır. Allah, rızık verenlerin en iyisidir.&quot;');
INSERT INTO "diyanet" VALUES(63,1,'63:1','İkiyüzlüler sana gelince: &quot;Senin şüphesiz Allah''ın Peygamberi olduğuna şehadet ederiz&quot; derler. Allah, senin kendisinin peygamberi olduğunu bilir; bunun yanında Allah, ikiyüzlülerin yalancı olduklarını da bilir.');
INSERT INTO "diyanet" VALUES(63,2,'63:2','Onlar, yeminlerini kalkan edinerek Allah''ın yolundan alıkoyarlar. İşledikleri işler gerçekten ne kötüdür!');
INSERT INTO "diyanet" VALUES(63,3,'63:3','Bu, önce inanıp sonra inkar etmiş olmalarındandır. Bu yüzden kalbleri mühürlenmiştir; artık anlamazlar.');
INSERT INTO "diyanet" VALUES(63,4,'63:4','Onlara baktığın zaman cüsseleri hoşuna gider; konuşurlarsa sözlerini dinlersin; tıpkı, sıralanmış kof kütük gibidirler; her çığlığı kendi aleyhlerine sayarlar; onlar düşmandır, onlardan çekin; Allah canlarını alsın, nasıl da aldatılıp döndürülüyorlar.');
INSERT INTO "diyanet" VALUES(63,5,'63:5','Onlara: &quot;Gelin de Allah''ın Peygamberi sizin için mağfiret dilesin&quot; dendiği zaman, başlarını çevirirler; büyüklük taslayarak yüz çevirdiklerini görürsün.');
INSERT INTO "diyanet" VALUES(63,6,'63:6','Onlar için, bağışlanma dilesen de dilemesen de birdir; Allah onları bağışlamayacaktır. Doğrusu Allah, yoldan çıkmış milleti doğru yola eriştirmez.');
INSERT INTO "diyanet" VALUES(63,7,'63:7','Bunlar: &quot;Allah''ın Peygamberinin yanında bulunanlara bir şey vermeyin de dağılıp gitsinler&quot; diyen kimselerdir. Oysa göklerin ve yerin hazineleri Allah''ındır, ama ikiyüzlüler bu gerçeği anlamazlar.');
INSERT INTO "diyanet" VALUES(63,8,'63:8','&quot;Eğer bu savaşdan Medine''ye dönersek, şerefli kimseler alçakları and olsun ki, oradan çıkaracaktır&quot; diyorlardı. Oysa, şeref Allah''ın, Peygamberinin ve inananlarındır, ama ikiyüzlüler bu gerçeği bilmezler.');
INSERT INTO "diyanet" VALUES(63,9,'63:9','Ey inananlar! Sizi, mallarınız ve çocuklarınız Allah''ı anmaktan alıkoymasın; böyle olanlar hüsrana uğrayanlardır.');
INSERT INTO "diyanet" VALUES(63,10,'63:10','Birine ölüm gelip de: &quot;Rabbim! Beni yakın bir süreye kadar ertelesen de, sadaka versem, iyilerden olsam&quot; diyeceği zaman gelmezden önce, size verdiğimiz rızıklardan sarfedin.');
INSERT INTO "diyanet" VALUES(63,11,'63:11','Bir canın eceli gelip çatınca, Allah onu asla geri bırakmaz; Allah, işlediklerinizden haberdardır.');
INSERT INTO "diyanet" VALUES(64,1,'64:1','Göklerde olanlar ve yerde bulunanlar Allah''ı tesbih ederler. Hükümranlık O''nundur, Övülmek O''na mahsustur. O herşeye Kadir''dir.');
INSERT INTO "diyanet" VALUES(64,2,'64:2','Sizi yaratan O''dur; kiminiz inkarcı kiminiz mümindir. Allah yaptıklarınızı gören''dir.');
INSERT INTO "diyanet" VALUES(64,3,'64:3','Gökleri ve yeri gerektiği gibi yaratmıştır. Size şekil vermiş ve şeklinizi güzel yapmıştır. Dönüş O''nadır.');
INSERT INTO "diyanet" VALUES(64,4,'64:4','Göklerde ve yerde olanları bilir; gizlediklerinizi de açığa vurduklarınızı da bilir; Allah, kalblerde olanı da bilendir.');
INSERT INTO "diyanet" VALUES(64,5,'64:5','Daha önce inkar edip de, inkarlarının karşılığını tadan kimselerin haberi size gelmedi mi? Onlara, can yakıcı azap vardır.');
INSERT INTO "diyanet" VALUES(64,6,'64:6','Bu, kendilerine peygamberleri belgelerle geldiğinde: &quot;Bizi doğru yola bir insan mı eriştirecek?&quot; diyerek inkar edip gerçeğe yüz çevirmelerinden ötürüdür. Allah hiçbir şeye muhtaç olmadığını ortaya koymuştur. Allah müstağnidir, övülmeğe layık olandır.');
INSERT INTO "diyanet" VALUES(64,7,'64:7','İnkar edenler, tekrar dirilmeyeceklerini ileri sürerler. De ki: &quot;Evet; Rabbime and olsun ki, şüphesiz diriltileceksiniz ve sonra, yaptıklarınız size bildirilecektir. Bu, Allah''a kolaydır.&quot;');
INSERT INTO "diyanet" VALUES(64,8,'64:8','Öyleyse Allah''a, Peygamberine ve indirdiğimiz nura, Kuran''a inanın; Allah işlediklerinizden haberdardır.');
INSERT INTO "diyanet" VALUES(64,9,'64:9','Toplanma günü için, sizi bir araya getirdiği zaman, işte o, kimin aldandığının ortaya çıkacağı gündür; Allah''a kim inanmış ve yararlı iş işlemişse, Allah onun kötülüklerini örter, onu içinde temelli ve sonsuz kalacağı, içlerinden ırmaklar akan cennetlere koyar; büyük kurtuluş işte budur.');
INSERT INTO "diyanet" VALUES(64,10,'64:10','İnkar edip, ayetlerimizi yalanlayanlar, işte onlar da ateşliklerdir, orada temellidirler. Ne kötü bir dönüştür!');
INSERT INTO "diyanet" VALUES(64,11,'64:11','Başa gelen hiçbir musibet Allah''ın izni olmaksızın olamaz; Allah''a kim inanırsa onun gönlünü doğruya yöneltir. Allah herşeyi bilendir.');
INSERT INTO "diyanet" VALUES(64,12,'64:12','Allah''a itaat edin; eğer bundan yüz çevirirseniz bilin ki Peygamberimize düşen apaçık tebliğdir.');
INSERT INTO "diyanet" VALUES(64,13,'64:13','Allah vardır, O''ndan başka tanrı yoktur. İnananlar yalnız Allah ''a güvensinler.');
INSERT INTO "diyanet" VALUES(64,14,'64:14','Ey inananlar! Eşleriniz ve çocuklarınızdan size düşmanlık edenler olur, onlardan sakının; ama, siz affeder, suçlarını örter ve bağışlarsanız bilin ki Allah da bağışlar ve acır.');
INSERT INTO "diyanet" VALUES(64,15,'64:15','Doğrusu mallarınız ve çocuklarınız bir imtihandır. Büyük ecir ise Allah katındadır.');
INSERT INTO "diyanet" VALUES(64,16,'64:16','Allah''a karşı gelmekten gücünüzün yettiği kadar sakının, buyruklarını dinleyin, itaat edin; kendinizin iyiliğine olarak mallarınızdan sarfedin; nefsinin tamahkarlığından korunan kimseler, işte onlar saadete erenlerdir.');
INSERT INTO "diyanet" VALUES(64,17,'64:17','Eğer Allah''a güzel bir ödünç takdiminde bulunursanız, onu sizin için kat kat yapar ve sizi bağışlar; Allah, şükrün karşılığını verendir; Halim''dir.');
INSERT INTO "diyanet" VALUES(64,18,'64:18','Görüleni görülmeyeni bilendir, güçlüdür. Hakim''dir.');
INSERT INTO "diyanet" VALUES(65,1,'65:1','Ey Peygamber! Kadınları boşayacağınızda, onları, iddetlerini gözeterek boşayın ve iddeti sayın; Rabbiniz olan Allah''tan sakının; onları, apaçık bir hayasızlık yapmaları hali bir yana evlerinden çıkarmayın, onlar da çıkmasınlar. Bunlar, Allah''ın sınırlarıdır. Allah''ın sınırlarını kim aşarsa, şüphesiz, kendine yazık etmiş olur. Bilmezsin, olur ki, Allah bunun ardından bir hal meydana getirir.');
INSERT INTO "diyanet" VALUES(65,2,'65:2','Kadınların iddet süreleri biteceğinde, onları ya uygun bir şekilde alıkoyun, ya da onlardan ayrılın; içinizden de iki adil şahit getirin; şahidliği Allah için yapın; işte bu, Allah''a ve ahiret gününe inanan kimseye verilen öğüttür. Allah, kendisine karşı gelmekten sakınan kimseye kurtuluş yolu sağlar, ona beklemediği yerden rızık verir. Allah''a güvenen kimseye O yeter. Allah, buyruğunu yerine getirendir. Allah her şey için bir ölçü var etmiştir.');
INSERT INTO "diyanet" VALUES(65,3,'65:3','Kadınların iddet süreleri biteceğinde, onları ya uygun bir şekilde alıkoyun, ya da onlardan ayrılın; içinizden de iki adil şahit getirin; şahidliği Allah için yapın; işte bu, Allah''a ve ahiret gününe inanan kimseye verilen öğüttür. Allah, kendisine karşı gelmekten sakınan kimseye kurtuluş yolu sağlar, ona beklemediği yerden rızık verir. Allah''a güvenen kimseye O yeter. Allah, buyruğunu yerine getirendir. Allah her şey için bir ölçü var etmiştir.');
INSERT INTO "diyanet" VALUES(65,4,'65:4','Kadınlarınızdan ay hali görmekten kesilenler ile henüz ay hali görmemiş olanların iddetleri hususunda şüpheye düşerseniz, bilin ki, onların iddet beklemesi üç aydır; gebe olanların iddeti, doğurmaları ile tamamlanır. Allah, buyruğuna karşı gelmekten sakınan kimseye işinde kolaylık verir.');
INSERT INTO "diyanet" VALUES(65,5,'65:5','Bu, Allah''ın size indirmiş olduğu buyruğudur. Kim Allah''ın buyruğuna karşı gelmekten sakınırsa, O, onun kötülüklerini örter, ecrini büyültür.');
INSERT INTO "diyanet" VALUES(65,6,'65:6','Boşadığınız, fakat iddeti dolmamış kadınları gücünüz nispetinde, kendi oturduğunuz yerde oturtun. Onları sıkıntıya sokmak için zarar vermeye kalkışmayın. Eğer hamile iseler, doğurmalarına kadar nafakalarını verin. Çocuğu sizin için emzirirlerse, onlara ücretlerini ödeyin; aranızda uygun bir şekilde anlaşın; eğer güçlükle karşılaşırsanız çocuğu başka bir kadın emzirebilir.');
INSERT INTO "diyanet" VALUES(65,7,'65:7','Varlıklı olan kimse, nafakayı varlığına göre versin; rızkı ancak kendisine yetecek kadar verilmiş olan kimse, Allah''ın kendisine verdiğinden versin; Allah kimseye, verdiği rızkı aşan bir yük yüklemez. Allah, güçlükten sonra kolaylık verir.');
INSERT INTO "diyanet" VALUES(65,8,'65:8','Rablerinin ve O''nun peygamberlerinin buyruğundan çıkan nice kasabalar halkını Biz, çetin bir hesaba çekmiş, onları, görülmedik bir azaba uğratmışızdır.');
INSERT INTO "diyanet" VALUES(65,9,'65:9','Onlar, işlerinin karşılığını tattılar; işlerinin sonu hüsran oldu.');
INSERT INTO "diyanet" VALUES(65,10,'65:10','Allah onlara çetin bir azap hazırlamıştır. Ey inanmış olan akıl sahipleri! Allah''tan sakının; Allah size Kuran''ı indirmiştir.');
INSERT INTO "diyanet" VALUES(65,11,'65:11','İnanıp yararlı işler işleyenleri karanlıklardan aydınlığa çıkarmak üzere, size Allah''ın apaçık ayetlerini okuyan bir Peygamber göndermiştir. Kim Allah''a inanır ve yararlı iş işlerse, Allah onu, içinde temelli ve sonsuz kalınacak, içlerinden ırmaklarakan cennetlere koyar. Allah ona gerçekten güzel rızık vermiştir.');
INSERT INTO "diyanet" VALUES(65,12,'65:12','Yedi göğü ve yerden de bir o kadarını yaratan Allah''tır, Allah''ın herşeye Kadir olduğunu ve Allah''ın ilminin herşeyi kuşattığını bilmeniz için Allah''ın buyruğu bunlar arasında iner durur.');
INSERT INTO "diyanet" VALUES(66,1,'66:1','Eşlerinin rızasını gözeterek, Allah''ın sana helal kıldığı şeyi niçin kendine yasak ediyorsun? Allah bağışlayandır, acıyandır.');
INSERT INTO "diyanet" VALUES(66,2,'66:2','Allah şüphesiz size, yeminlerinizi keffaretle geri almanızı meşru kılmıştır. Allah sizin dostunuzdur. O, bilendir, Hakim''dir.');
INSERT INTO "diyanet" VALUES(66,3,'66:3','Peygamber, eşlerinden birine gizlice bir söz söylemişti. O, bunu Peygamberin diğer bir eşine haber verince, Allah da Peygambere durumu bildirmiş, o da bir kısmını yüzüne vurmuş bir kısmını yüzüne vurmaktan geri durmuştu. Eşine, gizlice söylediği şeyibaşkasına nakletmiş olduğunu bildirince, eşi: &quot;Bunu sana kim haber verdi?&quot; demiş, o da: &quot;Bana, herşeyi bilen ve herşeyden haberdar olan Allah haber verdi&quot; demişti.');
INSERT INTO "diyanet" VALUES(66,4,'66:4','Ey Peygamber''in eşleri! Eğer ikiniz de Allah''a tevbe ederseniz, kaymış olan kalpleriniz düzelmiş olur. Eğer eşinizin aleyhinde yardımlaşarak bir şey yapmağa kalkarsanız, bilin ki Allah onun dostu, bundan başka Cebrail, iyi müminler ve melekler de yardımcısıdır.');
INSERT INTO "diyanet" VALUES(66,5,'66:5','Ey Peygamber''in eşleri! Eğer o sizi boşarsa, Rabbi ona; sizden daha iyi olan, kendini Allah''a veren, inanan, boyun eğen, tevbe eden, kulluk eden, oruç tutan, dul ve bakire eşler verebilir.');
INSERT INTO "diyanet" VALUES(66,6,'66:6','Ey inananlar! Kendinizi ve çoluk çocuğunuzu cehennem ateşinden koruyun; onun yakıtı, insanlar ve taşlardır; görevlileri, Allah''ın kendilerine verdiği emirlere baş kaldırmayan, kendilerine buyrulanları yerine getiren pek haşin meleklerdir.');
INSERT INTO "diyanet" VALUES(66,7,'66:7','&quot;Ey inkar edenler! Bugün özür beyan etmeyin, ancak işlediklerinizin karşılığını görmektesiniz&quot; denir.');
INSERT INTO "diyanet" VALUES(66,8,'66:8','Ey inananlar! Yürekten tevbe ederek Allah''a dönün ki, Rabbiniz kötülüklerinizi örtsün, sizi, içlerinden ırmaklar akan cennetlere koysun. Allah''ın Peygamberini ve onunla beraber olan müminleri utandırmayacağı o gün, ışıkları önlerinde ve defterleri sağlarından verilmiş olarak yürürler ve: &quot;Rabbimiz! Işığımızı tamamla, bizi bağışla, doğrusu Sen herşeye Kadir''sin&quot; derler.');
INSERT INTO "diyanet" VALUES(66,9,'66:9','Ey Peygamber! İnkarcılarla ve ikiyüzlülerle savaş; onlara karşı sert davran. Onların varacakları yer cehennemdir, ne kötü dönüştür!...');
INSERT INTO "diyanet" VALUES(66,10,'66:10','Allah, inkar edenlere, Nuh''un karısıyla Lut''un karısını misal gösterir: Onlar, kullarımızdan iki iyi kulun nikahı altında iken onlara karşı hainlik edip inkarlarını gizlemişlerdi de iki peygamber Allah''tan gelen azabı onlardan savamamışlardı. O iki kadına: &quot;Cehenneme girenlerle beraber siz de girin&quot; dendi.');
INSERT INTO "diyanet" VALUES(66,11,'66:11','Allah, inanlara Firavun''un karısını misal gösterir: O: &quot;Rabbim! Katından bana cennette bir ev yap; beni Firavun''dan ve onun işlediklerinden kurtar; beni zalim milletten kurtar&quot; demişti.');
INSERT INTO "diyanet" VALUES(66,12,'66:12','Mahrem yerini korumuş olan İmran kızı Meryem de bir misaldir. Ona ruhumuzdan üflemiştik; Rabbinin sözlerini ve kitablarını tasdik etmişti; o, Bize gönülden itaat edenlerdendi.');
INSERT INTO "diyanet" VALUES(67,1,'67:1','Hükümranlık elinde olan Allah yücedir ve O herşeye Kadir''dir.');
INSERT INTO "diyanet" VALUES(67,2,'67:2','Hanginizin daha iyi iş işlediğini belirtmek için, ölümü ve dirimi (hayatı) yaratan O''dur. O, güçlüdür, bağışlayandır.');
INSERT INTO "diyanet" VALUES(67,3,'67:3','Gökleri yedi kat üzerine yaratan O''dur. Rahman''ın bu yaratmasında bir düzensizlik bulamazsın. Gözünü bir çevir bak, bir çatlak görebilir misin?');
INSERT INTO "diyanet" VALUES(67,4,'67:4','Bir aksaklık bulmak için gözünü tekrar tekrar çevir bak; ama göz umduğunu bulamayıp bitkin ve yorgun düşer.');
INSERT INTO "diyanet" VALUES(67,5,'67:5','And olsun ki, yakın göğü kandillerle donattık, onları şeytanlar için taşlamalar yaptık ve şeytanlara çılgın alev azabını hazırladık.');
INSERT INTO "diyanet" VALUES(67,6,'67:6','Rablerini inkar eden kimseler için cehennem azabı vardır. Ne kötü bir dönüştür!');
INSERT INTO "diyanet" VALUES(67,7,'67:7','Oraya atıldıkları zaman, onun kaynarken çıkardığı uğultuyu işitirler.');
INSERT INTO "diyanet" VALUES(67,8,'67:8','Nerede ise öfkesinden paralanacak! İçine her bir topluluğun atılmasında, bekçileri onlara: &quot;Size bir uyarıcı gelmemiş miydi?&quot; diye sorarlar.');
INSERT INTO "diyanet" VALUES(67,9,'67:9','Onlar: &quot;Evet; doğrusu bize bir uyarıcı geldi, fakat biz yalanladık ve Allah hiçbir şey indirmemiştir, siz büyük bir sapıklık içindesiniz demiştik&quot; derler.');
INSERT INTO "diyanet" VALUES(67,10,'67:10','&quot;Eğer kulak vermiş veya akletmiş olsaydık, çılgın alevli cehennemlikler içinde olmazdık&quot; derler.');
INSERT INTO "diyanet" VALUES(67,11,'67:11','Böylece, günahlarını itiraf ederler. Çılgın alevli cehennemlikler yok olsunlar!');
INSERT INTO "diyanet" VALUES(67,12,'67:12','Doğrusu, görünmediği halde Rablerinden korkanlara, onlara, bağışlanma ve büyük ecir vardır.');
INSERT INTO "diyanet" VALUES(67,13,'67:13','Sizler, sözlerinizi gizleseniz de açıklasanız da birdir; O, kalblerde olanı bilir.');
INSERT INTO "diyanet" VALUES(67,14,'67:14','Yaratan bilmez olur mu? O, Latif''tir, haberdardır.');
INSERT INTO "diyanet" VALUES(67,15,'67:15','Yeryüzünü, size boyun eğdiren O''dur; öyleyse yerin sırtlarında dolaşın, Allah''ın verdiği rızıktan yiyin; sonunda dönüş O''nadır.');
INSERT INTO "diyanet" VALUES(67,16,'67:16','Gökte olanın sizi yerin dibine geçirmesinden güvende misiniz? O zaman, yer, sarsıldıkça sarsılır.');
INSERT INTO "diyanet" VALUES(67,17,'67:17','Gökte olanın başınıza taş yağdırmasından güvende misiniz? Benim uyarmamın nasıl olduğunu yakında bileceksiniz.');
INSERT INTO "diyanet" VALUES(67,18,'67:18','And olsun ki, bunlardan öncekiler de yalanlamışlardı. Beni inkar etmek nasılmış?');
INSERT INTO "diyanet" VALUES(67,19,'67:19','Üzerlerinde kanat çırpan dizi dizi kuşları görmezler mi? Onları havada Rahman olan Allah''tan başkası tutmuyor; doğrusu, O, herşeyi görendir.');
INSERT INTO "diyanet" VALUES(67,20,'67:20','Yahut, Rahman olan Allah''ın dışında size yardımda bulunabilecek taraftarlarınız kimdir? İnkarcılar sadece aldanmaktadırlar.');
INSERT INTO "diyanet" VALUES(67,21,'67:21','Allah size verdiği rızkı kesiverirse, size rızık verecek başka kim vardır? Hayır; onlar, azgınlık ve nefrette direnmektedirler.');
INSERT INTO "diyanet" VALUES(67,22,'67:22','Yüzükoyun sürünen mi, yoksa doğru yolda düpedüz yürüyen mi daha doğru yoldadır?');
INSERT INTO "diyanet" VALUES(67,23,'67:23','De ki: &quot;Sizi yaratan sizin için kulaklar, gözler ve kalbler var eden O''dur. Ne az şükrediyorsunuz!&quot;');
INSERT INTO "diyanet" VALUES(67,24,'67:24','Sizi yerde yaratıp yayan O''dur ve O''nun huzurunda toplanacaksınız.');
INSERT INTO "diyanet" VALUES(67,25,'67:25','&quot;Doğru sözlü iseniz bildirin bu azap sözü ne zamandır?&quot; derler.');
INSERT INTO "diyanet" VALUES(67,26,'67:26','De ki: &quot;Onu bilmek ancak Allah''a mahsustur. Ben sadece apaçık bir uyarıcıyım.&quot;');
INSERT INTO "diyanet" VALUES(67,27,'67:27','Azabı yaklaşırken gördükleri zaman, inkar edenlerin yüzleri çirkinleşip kararır; onlara: &quot;Sizin arayıp durduğunuz işte budur&quot; denir.');
INSERT INTO "diyanet" VALUES(67,28,'67:28','De ki: &quot;Allah, beni ve benimle beraber bulunanları isterse yok eder veya isterse merhamet eder; söyleyin, bu takdirde inkarcıları, can yakıcı azabdan kim alıkoyabilir?&quot;');
INSERT INTO "diyanet" VALUES(67,29,'67:29','De ki: &quot;Bizim inandığımız ve kendisine güvendiğimiz, Rahman olan Allah''tır. Kimin apaçık bir sapıklıkta olduğunu yakında bileceksiniz.&quot;');
INSERT INTO "diyanet" VALUES(67,30,'67:30','De ki: &quot;Suyunuz yere batarsa, söyleyin, size kim temiz bir su kaynağı getirebilir?&quot;');
INSERT INTO "diyanet" VALUES(68,1,'68:1','Nun; kalem ve onunla yazılanlara and olsun ki, sen Rabbinin nimetine uğramış bir kimsesin, deli (cinlenmiş) değilsin.');
INSERT INTO "diyanet" VALUES(68,2,'68:2','Nun; kalem ve onunla yazılanlara and olsun ki, sen Rabbinin nimetine uğramış bir kimsesin, deli (cinlenmiş) değilsin.');
INSERT INTO "diyanet" VALUES(68,3,'68:3','Doğrusu sana kesintisiz bir ecir vardır.');
INSERT INTO "diyanet" VALUES(68,4,'68:4','Şüphesiz sen büyük bir ahlaka sahipsindir.');
INSERT INTO "diyanet" VALUES(68,5,'68:5','Hanginizin aklından zoru olduğunu yakında sen de göreceksin, onlar da görecekler.');
INSERT INTO "diyanet" VALUES(68,6,'68:6','Hanginizin aklından zoru olduğunu yakında sen de göreceksin, onlar da görecekler.');
INSERT INTO "diyanet" VALUES(68,7,'68:7','Doğrusu senin Rabbin, yolundan sapıtanları çok iyi bilir; O, doğru yolda olanları da çok iyi bilir.');
INSERT INTO "diyanet" VALUES(68,8,'68:8','Bundan böyle, yalanlayanlara itaat etme;');
INSERT INTO "diyanet" VALUES(68,9,'68:9','(Onlar sana indirilen ayetlerden beğenmediklerini bırakman suretiyle senin) kendilerine yumuşak davranmanı isterler; böyle yapsan, onlar da seni över, yumuşak davranırlar.');
INSERT INTO "diyanet" VALUES(68,10,'68:10','Diliyle iğneleyen, kovuculuk eden, iyiliği daima önleyen, aşırı giden, suç işleyen, çok yemin eden alçak zorbaya, bütün bunlar dışında bir de soysuzlukla damgalanmış kimseye, mal ve oğulları vardır diye aldırış etmeyesin.');
INSERT INTO "diyanet" VALUES(68,11,'68:11','Diliyle iğneleyen, kovuculuk eden, iyiliği daima önleyen, aşırı giden, suç işleyen, çok yemin eden alçak zorbaya, bütün bunlar dışında bir de soysuzlukla damgalanmış kimseye, mal ve oğulları vardır diye aldırış etmeyesin.');
INSERT INTO "diyanet" VALUES(68,12,'68:12','Diliyle iğneleyen, kovuculuk eden, iyiliği daima önleyen, aşırı giden, suç işleyen, çok yemin eden alçak zorbaya, bütün bunlar dışında bir de soysuzlukla damgalanmış kimseye, mal ve oğulları vardır diye aldırış etmeyesin.');
INSERT INTO "diyanet" VALUES(68,13,'68:13','Diliyle iğneleyen, kovuculuk eden, iyiliği daima önleyen, aşırı giden, suç işleyen, çok yemin eden alçak zorbaya, bütün bunlar dışında bir de soysuzlukla damgalanmış kimseye, mal ve oğulları vardır diye aldırış etmeyesin.');
INSERT INTO "diyanet" VALUES(68,14,'68:14','Diliyle iğneleyen, kovuculuk eden, iyiliği daima önleyen, aşırı giden, suç işleyen, çok yemin eden alçak zorbaya, bütün bunlar dışında bir de soysuzlukla damgalanmış kimseye, mal ve oğulları vardır diye aldırış etmeyesin.');
INSERT INTO "diyanet" VALUES(68,15,'68:15','Ayetlerimiz ona okunduğu zaman: &quot;Öncekilerin masalları&quot; der.');
INSERT INTO "diyanet" VALUES(68,16,'68:16','Onun havada olan burnunu yakında yere sürteceğiz.');
INSERT INTO "diyanet" VALUES(68,17,'68:17','Biz bunları, vaktiyle bahçe sahiplerini denediğimiz gibi denedik. Sahipleri daha sabah olmadan, bahçeyi devşireceklerine bir istisna payı bırakmaksızın yemin etmişlerdi.');
INSERT INTO "diyanet" VALUES(68,18,'68:18','Biz bunları, vaktiyle bahçe sahiplerini denediğimiz gibi denedik. Sahipleri daha sabah olmadan, bahçeyi devşireceklerine bir istisna payı bırakmaksızın yemin etmişlerdi.');
INSERT INTO "diyanet" VALUES(68,19,'68:19','Ama onlar daha uykudayken Rabbinin katından gönderilen bir salgın o bahçeyi sarıvermişti de bahçe kapkara kesilmişti.');
INSERT INTO "diyanet" VALUES(68,20,'68:20','Ama onlar daha uykudayken Rabbinin katından gönderilen bir salgın o bahçeyi sarıvermişti de bahçe kapkara kesilmişti.');
INSERT INTO "diyanet" VALUES(68,21,'68:21','Sabah erken: &quot;Ürünlerinizi devşirecekseniz erken çıkın&quot; diye birbirlerine seslendiler.');
INSERT INTO "diyanet" VALUES(68,22,'68:22','Sabah erken: &quot;Ürünlerinizi devşirecekseniz erken çıkın&quot; diye birbirlerine seslendiler.');
INSERT INTO "diyanet" VALUES(68,23,'68:23','&quot;Bugün orada, hiçbir düşkün kimse yanımıza sokulmasın&quot; diye gizli gizli konuşarak yürüyorlardı.');
INSERT INTO "diyanet" VALUES(68,24,'68:24','&quot;Bugün orada, hiçbir düşkün kimse yanımıza sokulmasın&quot; diye gizli gizli konuşarak yürüyorlardı.');
INSERT INTO "diyanet" VALUES(68,25,'68:25','Yoksullara yardım etmeye güçleri yeterken böyle konuşarak erkenden gittiler.');
INSERT INTO "diyanet" VALUES(68,26,'68:26','Bahçeyi gördüklerinde: &quot;Herhalde yolumuzu şaşırmış olacağız; belki de biz yoksun bırakıldık&quot; dediler.');
INSERT INTO "diyanet" VALUES(68,27,'68:27','Bahçeyi gördüklerinde: &quot;Herhalde yolumuzu şaşırmış olacağız; belki de biz yoksun bırakıldık&quot; dediler.');
INSERT INTO "diyanet" VALUES(68,28,'68:28','Ortancaları: &quot;Ben size Allah''ı anmanız gerekmez mi, dememiş miydim?&quot; dedi.');
INSERT INTO "diyanet" VALUES(68,29,'68:29','&quot;Rabbimizi tenzih ederiz; doğrusu biz yazık etmiştik&quot; dediler.');
INSERT INTO "diyanet" VALUES(68,30,'68:30','Birbirlerini yermeye başladılar.');
INSERT INTO "diyanet" VALUES(68,31,'68:31','Sonra şöyle dediler: &quot;Yazıklar olsun bize; doğrusu azgınlık edenlerdendik.&quot;');
INSERT INTO "diyanet" VALUES(68,32,'68:32','&quot;Belki Rabbimiz bize bundan daha iyisini verir; doğrusu artık, Rabbimizden dilemekteyiz.&quot;');
INSERT INTO "diyanet" VALUES(68,33,'68:33','İşte azap böyledir; ama ahiret azabı daha büyüktür; keşke bilseler!');
INSERT INTO "diyanet" VALUES(68,34,'68:34','Allah''a karşı gelmekten sakınanlara, Rableri katında nimet cennetleri vardır.');
INSERT INTO "diyanet" VALUES(68,35,'68:35','Kendilerini Allah''a vermiş olanları hiç suçlular gibi tutar mıyız?');
INSERT INTO "diyanet" VALUES(68,36,'68:36','Ne oluyorsunuz? Ne biçim hükmediyorsunuz?');
INSERT INTO "diyanet" VALUES(68,37,'68:37','Yoksa okuduğunuz bir kitabınız mı var?');
INSERT INTO "diyanet" VALUES(68,38,'68:38','Seçtikleriniz herhalde orada olacaktır.');
INSERT INTO "diyanet" VALUES(68,39,'68:39','Yoksa aleyhimizde, kıyamet gününe kadar süregidecek ahidleriniz mi var ki, kendinize hükmettikleriniz sizin olacaktır?');
INSERT INTO "diyanet" VALUES(68,40,'68:40','Sor onlara: &quot;Bunu kim üzerine alır?&quot;');
INSERT INTO "diyanet" VALUES(68,41,'68:41','Yoksa onların ortakları mı vardır? Doğru sözlü iseler ortaklarını getirsinler.');
INSERT INTO "diyanet" VALUES(68,42,'68:42','O gün işin dehşetinden baldırlar açılır; gözleri dönmüş olarak yüzlerini zillet bürür; secdeye çağırılırlar ama buna güçleri yetmez. Oysa, kendileri sapasağlam oldukları zaman secdeye çağırılmışlardı.');
INSERT INTO "diyanet" VALUES(68,43,'68:43','O gün işin dehşetinden baldırlar açılır; gözleri dönmüş olarak yüzlerini zillet bürür; secdeye çağırılırlar ama buna güçleri yetmez. Oysa, kendileri sapasağlam oldukları zaman secdeye çağırılmışlardı.');
INSERT INTO "diyanet" VALUES(68,44,'68:44','Kuran''ı yalanlayanları Bana bırak; Biz onları bilmedikleri yerden yavaş yavaş azaba yaklaştıracağız.');
INSERT INTO "diyanet" VALUES(68,45,'68:45','Onlara mehil veriyorum; doğrusu Benim tuzağım sağlamdır.');
INSERT INTO "diyanet" VALUES(68,46,'68:46','Yoksa, sen onlardan ücret istiyorsun da, ağır bir borç altında mı kalıyorlar? Elbette hayır.');
INSERT INTO "diyanet" VALUES(68,47,'68:47','Yoksa, gaybın bilgisi kendilerinin katında da onlar mı yazıyorlar?');
INSERT INTO "diyanet" VALUES(68,48,'68:48','Sen Rabbinin hükmüne kadar sabret; balık sahibi (Yunus) gibi olma, o, pek üzgün olarak Rabbine seslenmişti.');
INSERT INTO "diyanet" VALUES(68,49,'68:49','Rabbinin katından ona bir nimet ulaşmasaydı, kınanmış olarak sahile atılacaktı.');
INSERT INTO "diyanet" VALUES(68,50,'68:50','Rabbi onu seçip iyilerden kıldı. Doğrusu inkar edenler, Kuran''ı dinlediklerinde nerdeyse seni gözleriyle yıkıp devireceklerdi. &quot;O delidir&quot; diyorlardı.');
INSERT INTO "diyanet" VALUES(68,51,'68:51','Rabbi onu seçip iyilerden kıldı. Doğrusu inkar edenler, Kuran''ı dinlediklerinde nerdeyse seni gözleriyle yıkıp devireceklerdi. &quot;O delidir&quot; diyorlardı.');
INSERT INTO "diyanet" VALUES(68,52,'68:52','Oysa Kuran, alemler için bir öğütten başka bir şey değildir.');
INSERT INTO "diyanet" VALUES(69,1,'69:1','Gerçekleşecek olan!');
INSERT INTO "diyanet" VALUES(69,2,'69:2','Nedir o gerçekleşecek olan gün?');
INSERT INTO "diyanet" VALUES(69,3,'69:3','Gerçekleşecek olanın ne olduğunu sana ne bildirir?');
INSERT INTO "diyanet" VALUES(69,4,'69:4','Semud ve Ad milletleri tepelerine inecek bu gerçeği yalanladılar.');
INSERT INTO "diyanet" VALUES(69,5,'69:5','Bu yüzden Semud milleti zorlu bir sarsıntı ile yok edildi.');
INSERT INTO "diyanet" VALUES(69,6,'69:6','Ad milleti de bu yüzden önünde durulmaz, dondurucu bir rüzgarla yok edildi.');
INSERT INTO "diyanet" VALUES(69,7,'69:7','Allah onların kökünü kesmek üzere, üzerlerine o rüzgarı yedi gece sekiz gün, estirdi. Halkın, kökünden çıkarılmış hurma kütükleri gibi yere yıkıldıklarını görürsün.');
INSERT INTO "diyanet" VALUES(69,8,'69:8','Onlardan arda kalmış bir şey görür müsün?');
INSERT INTO "diyanet" VALUES(69,9,'69:9','Firavun, ondan öncekiler ve alt üst olmuş kasabalarda oturanlar da suç işlemişlerdi.');
INSERT INTO "diyanet" VALUES(69,10,'69:10','Rabbinin peygamberine baş kaldırmışlardı. Bunun üzerine Rableri onları şiddeti arttıkça artan bir şekilde yakaladı.');
INSERT INTO "diyanet" VALUES(69,11,'69:11','Su taştığı vakit, size bir ibret olmak üzere, anlayışlı kulaklar anlasın diye süzülen gemide, sizi Biz taşımışızdır.');
INSERT INTO "diyanet" VALUES(69,12,'69:12','Su taştığı vakit, size bir ibret olmak üzere, anlayışlı kulaklar anlasın diye süzülen gemide, sizi Biz taşımışızdır.');
INSERT INTO "diyanet" VALUES(69,13,'69:13','Sura bir üfürüş üfürüldüğü, yer ve dağlar kaldırılıp bir vuruşla birbirine çarpıldığı zaman, işte o gün olacak olur, kıyamet kopar.');
INSERT INTO "diyanet" VALUES(69,14,'69:14','Sura bir üfürüş üfürüldüğü, yer ve dağlar kaldırılıp bir vuruşla birbirine çarpıldığı zaman, işte o gün olacak olur, kıyamet kopar.');
INSERT INTO "diyanet" VALUES(69,15,'69:15','Sura bir üfürüş üfürüldüğü, yer ve dağlar kaldırılıp bir vuruşla birbirine çarpıldığı zaman, işte o gün olacak olur, kıyamet kopar.');
INSERT INTO "diyanet" VALUES(69,16,'69:16','Gök yarılır; o gün düzeni bozulur.');
INSERT INTO "diyanet" VALUES(69,17,'69:17','Melekler onun çevresindedirler; o gün Rabbinin arşını onlardan başka sekiz tanesi yüklenir.');
INSERT INTO "diyanet" VALUES(69,18,'69:18','O gün siz huzura alınırsınız, hiçbir şeyiniz gizli kalmaz.');
INSERT INTO "diyanet" VALUES(69,19,'69:19','Kitabı sağından verilen; &quot;Alın, kitabımı okuyun, doğrusu bir hesaplaşma ile karşılaşacağımı umuyordum&quot; der.');
INSERT INTO "diyanet" VALUES(69,20,'69:20','Kitabı sağından verilen; &quot;Alın, kitabımı okuyun, doğrusu bir hesaplaşma ile karşılaşacağımı umuyordum&quot; der.');
INSERT INTO "diyanet" VALUES(69,21,'69:21','Artık o, meyveleri sarkmış, yüksek bir bahçede, hoş bir yaşayış içindedir.');
INSERT INTO "diyanet" VALUES(69,22,'69:22','Artık o, meyveleri sarkmış, yüksek bir bahçede, hoş bir yaşayış içindedir.');
INSERT INTO "diyanet" VALUES(69,23,'69:23','Artık o, meyveleri sarkmış, yüksek bir bahçede, hoş bir yaşayış içindedir.');
INSERT INTO "diyanet" VALUES(69,24,'69:24','Onlara şöyle denir: &quot;Geçmiş günlerde, peşinen işlediklerinize karşılık afiyetle yiyiniz içiniz.&quot;');
INSERT INTO "diyanet" VALUES(69,25,'69:25','Fakat kitabı kendisine solundan verilen kimse: &quot;Kitabım keşke bana verilmeseydi; keşke hesabımın ne olduğunu bilmeseydim; bu iş keşke son bulmuş olsaydı; malım bana fayda vermedi; gücüm de kalmadı&quot; der.');
INSERT INTO "diyanet" VALUES(69,26,'69:26','Fakat kitabı kendisine solundan verilen kimse: &quot;Kitabım keşke bana verilmeseydi; keşke hesabımın ne olduğunu bilmeseydim; bu iş keşke son bulmuş olsaydı; malım bana fayda vermedi; gücüm de kalmadı&quot; der.');
INSERT INTO "diyanet" VALUES(69,27,'69:27','Fakat kitabı kendisine solundan verilen kimse: &quot;Kitabım keşke bana verilmeseydi; keşke hesabımın ne olduğunu bilmeseydim; bu iş keşke son bulmuş olsaydı; malım bana fayda vermedi; gücüm de kalmadı&quot; der.');
INSERT INTO "diyanet" VALUES(69,28,'69:28','Fakat kitabı kendisine solundan verilen kimse: &quot;Kitabım keşke bana verilmeseydi; keşke hesabımın ne olduğunu bilmeseydim; bu iş keşke son bulmuş olsaydı; malım bana fayda vermedi; gücüm de kalmadı&quot; der.');
