INSERT INTO "diyanet" VALUES(70,19,'70:19','İnsan gerçekten pek huysuz yaratılmıştır:');
INSERT INTO "diyanet" VALUES(70,20,'70:20','Başına bir fenalık gelince feryat eder,');
INSERT INTO "diyanet" VALUES(70,21,'70:21','Bir iyiliğe uğrarsa onu herkesten meneder;');
INSERT INTO "diyanet" VALUES(70,22,'70:22','Ancak namaz kılıp namazlarında yoksul ve yoksuna belirli bir hak tanıyanlar, ceza gününü doğrulayanlar, Rablerinin azabından korkanlar böyle değildir.');
