INSERT INTO "finnish" VALUES(1,1,'1:1','﻿Aloitan JUMALAN, laupiaan Armahtajan, nimeen.');
INSERT INTO "finnish" VALUES(1,2,'1:2','Ylistys Jumalalle, maailmojen Valtiaalle,');
INSERT INTO "finnish" VALUES(1,3,'1:3','armolahjojen Antajalle, laupiaalle Ohjaajalle ja Siunaajalle,');
INSERT INTO "finnish" VALUES(1,4,'1:4','jonka hallussa yksin on Tilinteon päivä.');
INSERT INTO "finnish" VALUES(1,5,'1:5','Sinua ainoata palvelemme, Sinua huudamme avuksi.');
INSERT INTO "finnish" VALUES(1,6,'1:6','Ohjaa meidät oikeata tietä,');
INSERT INTO "finnish" VALUES(1,7,'1:7','niiden tietä, joiden osana on Sinun mielisuosiosi ei niiden tietä, jotka ovat Sinun vihasi alaisia, eikä niiden,jotka harhaan eksyvät.');
INSERT INTO "finnish" VALUES(2,1,'2:1','Alif. Laam. Miim. Tämä Pyhä kirja, siitä ei ole epäilystäkään on');
INSERT INTO "finnish" VALUES(2,2,'2:2','opastus jumalaapelkääväisille ja pahaa karttaville ,');
INSERT INTO "finnish" VALUES(2,3,'2:3','niille, jotka uskovat siihen, mikä ei näy, elävät rukoillen ja tuovat uhria siitä, mitä Me olemme heille suoneet,');
INSERT INTO "finnish" VALUES(2,4,'2:4','niille, jotka uskovat siihen, mikä on ylhäältä ilmoitettu sinulle (Muhammed), sekä siihen, mikä on ilmoitettu ennen sinua, ja jotka varmassa luottamuksessa odottavat tulevaista elämää.');
INSERT INTO "finnish" VALUES(2,5,'2:5','Nämä elävät Herransa johdatuksessa; nämä ovat onnellisia.');
INSERT INTO "finnish" VALUES(2,6,'2:6','Jumalattomille taasen on totisesti samantekevää, varoitatko heitä vai et; he eivät usko.');
INSERT INTO "finnish" VALUES(2,7,'2:7','Jumala on sinetillä sulkenut heidän sydämensä ja korvansa, ja heidän silmiään peittää verho; niin, heitä kohtaa kammottava rangaistus.');
INSERT INTO "finnish" VALUES(2,8,'2:8','Muutamat ihmiset sanovat kyllä: »Me uskomme Jumalaan ja Viimeiseen päivään», eivätkä kuitenkaan usko.');
INSERT INTO "finnish" VALUES(2,9,'2:9','He tahtovat pettää Jumalaa ja niitä, jotka uskovat, mutta he eivät petä ketään muita kuin itsensä, eivätkä käsitä sitä.');
INSERT INTO "finnish" VALUES(2,10,'2:10','Sairaus turmelee heidän sydämensä, ja Jumala pahentaa heidän sairautensa; niin, heitä odottaa tuskallinen rangaistus, koska he ovat valehdelleet.');
INSERT INTO "finnish" VALUES(2,11,'2:11','Kun heille sanotaan: »Alkää aiheuttako pahennusta maan päällä», he vastaavat: »Mehän vain uudistamme asiat oikeiksi.»');
INSERT INTO "finnish" VALUES(2,12,'2:12','ivätkö juuri sellaiset tosiaan ole niitä, joista paha paisuu? Mutta he eivät sitä tajua.');
INSERT INTO "finnish" VALUES(2,13,'2:13','Ja kun heille sanotaan: »Uskokaa kuten nuo ihmiset uskovat», he vastaavat: »Onko meidän uskottava kuten hullut uskovat?» Mutta eivätkö todellisuudessa juuri he itse ole hulluja? Vaan sitäkään he eivät älyä.');
INSERT INTO "finnish" VALUES(2,14,'2:14','Kohdatessaan uskovaisia he sanovat: »Me uskomme», mutta kun he erkanevat taas saatanainsa pariin, he selittävät: »Tietysti olemme teikäläisiä, me vain teimme pilkkaa.»');
INSERT INTO "finnish" VALUES(2,15,'2:15','Mutta Jumala tekeekin pilkkaa heistä itsestään, jättää heidät yltymään pahuudessaan ja vaeltamaan sokeasti yhä syvempään häpeään.');
INSERT INTO "finnish" VALUES(2,16,'2:16','He ovat niitä, jotka oikean johdatuksen vaihtavat harhatiehen, eikä se vaihtokauppa ole heille voittoa tuottava, vaan he jäävät kokonaan ilman Jumalan Opastusta.');
INSERT INTO "finnish" VALUES(2,17,'2:17','Heidän laitansa on kuin niiden, jotka sytyttävät itselleen tulen, mutta kun se on valaissut mitä heidän ympärillään on, tempaa Jumala pois valon ja jättää kaikki pimeyteen, niin etteivät he voi nähdä; Hän jättää heidät');
INSERT INTO "finnish" VALUES(2,18,'2:18','mykiksi, kuuroiksi ja sokeiksi, niin etteivät he voi palata.');
INSERT INTO "finnish" VALUES(2,19,'2:19','Tai kuten on ukkossateen yllättäessä, joka tuo mukanaan pimeyden, jyrinän ja salaman; he tukkivat sormin korvansa ukkosen jylinältä ja pelkäävät saavansa kuoliniskun salamoista - Jumalalla on uskottomat vallassaan - ja');
INSERT INTO "finnish" VALUES(2,20,'2:20','salama on riistämäisillään heiltä näön. Niin usein kuin se välähtää, liikkuvat he sen valossa, mutta kun pimeys taas lankeaa heidän ylitsensä, pysähtyvät he. Jos Jumala tahtoisi, voisi Hän ottaa heiltä kokonaan kuulon ja näön; katso, Jumala on totisesti kaikkivaltias.');
INSERT INTO "finnish" VALUES(2,21,'2:21','Oi, ihmiset, palvelkaa Herraanne, joka on luonut sekä teidät että ne, jotka ovat eläneet ennen teitä, jotta pelastuisitte pahasta!');
INSERT INTO "finnish" VALUES(2,22,'2:22','Jumala on tehnyt maan teille lepopaikaksi ja kaartanut taivaan ylitsenne, Hän antaa vettä taivaasta ja sillä kasvattaa hedelmiä ravinnoksenne. Älkää siis asettako Jumalan rinnalle muita Hänen vertaisikseen vastoin parempaa tietoanne.');
INSERT INTO "finnish" VALUES(2,23,'2:23','jos epäilette sitä, mitä Me ilmoitamme palvelijallemme (Muhammedille), niin aikaansaakaa tämän kaltainen kirjoitus ja kutsukaa Jumalan vertaisia todistajia, jos tahdotte pysyä totuudessa;');
INSERT INTO "finnish" VALUES(2,24,'2:24','mutta jollette tee niin - ja sitä ette koskaan voi tehdä silloin kavahtakaa tulta, joka on valmistettu uskottomille ja jonka polttoaineena ovat ihmiset ja kivet.');
INSERT INTO "finnish" VALUES(2,25,'2:25','julista niille (oi Muhammed), jotka uskovat ja tekevät hyviä töitä, se riemullinen sanoma, että heitä odottavat huvitarhat, joissa purot solisevat. Niin usein kuin he nauttivat hedelmiä sieltä ravinnokseen, he sanovat: »Tämä oli muinoin meidän ruokaamme» - vaikkakin heille tarjotaan se vain samankaltaisena.Siellä odottavat heitä puhtaat puolisot ja siellä he viihtyvät ikuisesti.');
INSERT INTO "finnish" VALUES(2,26,'2:26','Jumala ei häpeä käyttää vertauskuvana vaikkapa hyttystä tai jotakin sen yläpuolella olevaa . Ne jotka uskovat, tietävät, että siinä on heidän Herransa ilmoittama totuus, kun taas uskottomat kummastelevat: »Mitähän Jumala tarkoittaa moisella vertauksella?» Hän sillä eksyttää monta ja opastaa monta, mutta muut eivät eksy eivätkä joudu kadotukseen kuin jumalattomat,');
INSERT INTO "finnish" VALUES(2,27,'2:27','jotka rikkovat liiton Jumalan kanssa, sen jälkeen kuin se on vahvistettu, erottavat, mitä Jumala on määrännyt yhdistettäväksi, ja aiheuttavat pahaa maan päällä, he ovat niitä, jotka joutuvat onnettomuuteen.');
INSERT INTO "finnish" VALUES(2,28,'2:28','Kuinka voitte kieltää Jumalan te, jotka olitte kuolleita ja joille Hän antoi elämän? Aikanaan Hän antaa teidän kuolla, sitten Hän herättää taas teidät elämään, ja lopulta palajatte jälleen Hänen luoksensa.');
INSERT INTO "finnish" VALUES(2,29,'2:29','Hän on se, joka on luonut teille kaiken, mitä on maan päällä. Sitten Hän nousi taivaaseen ja muodosti sen seitsemäksi taivaaksi; niin, Hän on kaikkitietävä.');
INSERT INTO "finnish" VALUES(2,30,'2:30','Ja kun Herrasi puhui enkeleille: »Katso, Minä aion totisesti asettaa sijaisen maan päälle», he kysyivät: »Aiotko asettaa sinne jonkun, joka siellä aiheuttaa vääryyttä ja vuodattaa verta, meidän ylistäessämme Sinua ja julistaessamme Sinun pyhyyttäsi?» Hän vastasi: »Minä tiedän totisesti, mitä te ette tiedä.»');
INSERT INTO "finnish" VALUES(2,31,'2:31','Ja Hän opetti ihmiselle (Aadamille) kaikki nimet ja nimitettävät; sitten näytti Hän kaikki enkeleille ja sanoi: »Lausukaa näiden nimet Minulle, jos olette todellisuudesta selvillä.»');
INSERT INTO "finnish" VALUES(2,32,'2:32','He vastasivat: »Sinulle yksin kunnia! Meillä ei ole muuta tietoa, kuin minkä olet opettanut meille, Sinä ainoa olet totisesti Tietäjä, Viisas.»');
INSERT INTO "finnish" VALUES(2,33,'2:33','Silloin Hän sanoi: »Ihminen (Aadam), lausu niiden nimet heille!» ja kun hän lausui niiden nimet heille, sanoi Hän: »Enkö ole teille sanonut, että Minä tunnen totisesti salat taivaassa ja maan päällä, ja Minä tiedän, mitä te ilmaisette ja mitä salaatte?»');
INSERT INTO "finnish" VALUES(2,34,'2:34','Ja kun Me sanoimme enkeleille: »Kumartukaa maahan Ihmisen (Aadamin) edessä», heittäytyivät he maahan kaikki paitsi Saatana. Hän kieltäytyi ylpeyksissään ja joutui jumalattomien lukuun.');
INSERT INTO "finnish" VALUES(2,35,'2:35','Me sanoimme vielä: »Aadam, asu Paratiisissa, sinä ja vaimosi, ja nauttikaa sieltä vapaasti ravintoa, mistä haluatte, mutta älkää lähestykö tätä puuta, jottette tulisi väärintekijöiksi.»');
INSERT INTO "finnish" VALUES(2,36,'2:36','Mutta Saatana sai heidät poikkeamaan tästä ja vietteli heidät ulos siitä autuaasta olotilasta, jossa he olivat, ja Me sanoimme: »Syöksykää alas! Toinen teistä olkoon toisen vihollinen! Kuitenkin saatte ajallisen olinpaikan ja toimeentulonne maan päällä.»');
INSERT INTO "finnish" VALUES(2,37,'2:37','Tällöin vastaanotti Aadam ilmestyksen sanat Herraltansa, ja tämä sääli häntä: katso, Hän on totisesti sääliväinen, laupias.');
INSERT INTO "finnish" VALUES(2,38,'2:38','Me sanoimme: »Syöksykää täältä alas kaikki!» Totisesti tulee teille Minulta kuitenkin vielä Johdatus, ja jotka Minun johdatustani seuraavat, niitä ei mikään pelko valtaa, eikä heidän tarvitse murehtia.');
INSERT INTO "finnish" VALUES(2,39,'2:39','Mutta ne, jotka ovat uskottomia ja pitävät ilmestyksemme valheena, ovat Tulen omia ja pysyvät siinä iankaikkisesti.');
INSERT INTO "finnish" VALUES(2,40,'2:40','Israelilaiset, muistakaa Minun mielisuosiotani, jonka olen antanut tulla osaksenne, ja täyttäkää osuutenne liitossa, niin olen Minä täyttävä oman osani liitossa, ja peljätkää yksin Minua');
INSERT INTO "finnish" VALUES(2,41,'2:41','ja uskokaa siihen, minkä olen ilmoittanut (Muhammedille), vahvistaen sillä ennestään teillä jo olevat Pyhät kirjoitukset; älkää olko ensimmäisiä epäuskossa älkääkä vaihtako Minun ilmoitustani mitättömiin arvoihin, vaan kunnioittaen peljätkää yksin Minua.');
INSERT INTO "finnish" VALUES(2,42,'2:42','Älkää verhotko totuutta valheella älkääkä salatko totuutta vastoin parempaa tietoanne,');
INSERT INTO "finnish" VALUES(2,43,'2:43','vaan harjoittakaa rukoilemista, antakaa almuja ja kumartakaa yhdessä niiden kanssa, jotka Minua kumartavat.');
INSERT INTO "finnish" VALUES(2,44,'2:44','Kuinka te kehoittaisitte ihmisiä vanhurskauteen, itse unohtaessanne oman sielunne? Tehän tutkitte Pyhää kirjaa, eikö teillä ole ymmärrystä?');
INSERT INTO "finnish" VALUES(2,45,'2:45','Etsikää apua kärsivällisyydestä ja rukouksesta. Tämä on totisesti vaikea tehtävä muille paitsi nöyräsydämisille,');
INSERT INTO "finnish" VALUES(2,46,'2:46','jotka tietävät, että he kohtaavat Herransa ja että he palaavat Hänen luokseen.');
INSERT INTO "finnish" VALUES(2,47,'2:47','Israelilaiset! Muistakaa armoani, jota olen teille osoittanut, ja että olen suosinut teitä ja korottanut teidät yli kaikkien kansojen.');
INSERT INTO "finnish" VALUES(2,48,'2:48','Ja varokaa sitä päivää, jolloin yksikään sielu ei voi suorittaa vähäisintäkään hyvitystä toisen puolesta, jolloin ei mitään välitystä hyväksytä eikä mitään lunnaita vastaanoteta, eikä kukaan saa toisen apua.');
INSERT INTO "finnish" VALUES(2,49,'2:49','ja muistakaa, kuinka Me pelastimme teidät faraon kansan käsistä, egyptiläisten käsistä, jotka vaivasivat teitä julmilla rasituksilla, tappoivat poikanne ja antoivat vain tytärtenne elää. Tämä oli Herranne sallima kauhea koettelemus.');
INSERT INTO "finnish" VALUES(2,50,'2:50','Entä kun Me jaoimme meren kahtia edessänne ja pelastimme teidät, mutta hukutimme faraon kansan silmienne nähden?');
INSERT INTO "finnish" VALUES(2,51,'2:51','Entä kun Me määräsimme Mooseksen viipymään edessämme neljäkymmentä vuorokautta, ja sillä aikaa kun hän oli poissa, te valitsitte (epäjumalaksenne) vasikan, koska olitte uppiniskaisia?');
INSERT INTO "finnish" VALUES(2,52,'2:52','Senkin jälkeen annoimme teille anteeksi, josta teidän olisi oltava kiitollisia.');
INSERT INTO "finnish" VALUES(2,53,'2:53','Entä silloin, kun annoimme Moosekselle Lain, jotta oppisitte hylkäämään pahan ja valitsemaan hyvän ja teidät ohjattaisiin oikeata tietä,');
INSERT INTO "finnish" VALUES(2,54,'2:54','ja kun Mooses sanoi kansallensa: »Kansani, totisesti olette langenneet syntiin tekemällä itsellenne vasikan; kääntykää siis katuen Luojanne puoleen ja surmatkaa syylliset keskuudessanne. Siten on teidän parempi Luojanne edessä, ja Hän armahtaa teitä, Hän on totisesti armollinen ja laupias.»');
INSERT INTO "finnish" VALUES(2,55,'2:55','Entä kun te sanoitte: »Mooses, me emme usko sinua, ennenkuin silmillämme näemme Jumalan», ja taivaan tuli yllätti teidät tuijottaessanne,');
INSERT INTO "finnish" VALUES(2,56,'2:56','ja sitten Me sallimme salamanlyömien tointua eloon, jotta ehkä oppisitte olemaan kiitollisia.');
INSERT INTO "finnish" VALUES(2,57,'2:57','Ja Me levitimme suojaksenne pilven ja lähetimme teille mannaa ja viiriäisiä. »Syökää sitä hyvää, mitä olemme teille suoneet.» Eivät israelilaiset synnillänsä Meitä vahingoittaneet vaan itseänsä.');
INSERT INTO "finnish" VALUES(2,58,'2:58','Entä kun Me sanoimme: »Astukaa tähän kaupunkiin ja nauttikaa runsaasti löytämäänne ravintoa»? Astukaa sisään portista kumartaen nöyrästi ja sanokaa: » (Synti) taakkamme lievennettäköön!» jotta antaisimme teille synnit anteeksi ja siunaisimme niitä, jotka tekevät hyvää.');
INSERT INTO "finnish" VALUES(2,59,'2:59','Mutta ne,jotka rikkoivat (Jumalan käskyn), vaihtoivat tämän sanan toiseen, kuin mikä oli sanottu heille, ja näille me lähetimme rangaistuksen taivaasta, koska he olivat uppiniskaisia.');
INSERT INTO "finnish" VALUES(2,60,'2:60','Entä kun Mooses pyysi vettä janoiselle kansalleen, ja Me sanoimme: »Lyö sauvallasi kallioon!» ja kaksitoista lähdettä pulppusi kivestä, niin että joka sukukunta tiesi juomapaikkansa? »Syökää ja juokaa Jumalan antimista, älkääkä kavalasti saako aikaan pahennusta maan päällä.»');
INSERT INTO "finnish" VALUES(2,61,'2:61','Entä kun te sanoitte: »Mooses, me emme jaksa syödä aina samaa ravintoa; huuda siksi Herraasi puolestamme, että hän anaisi meille, mitä maa kasvaa, kuten vihanneksia, kurkkuja, leipäviljaa, herneitä ja punasipulia»? Hän vastasi: »Haluatteko saada huonompaa paremman sijasta? Menkää alas Egyptiin, siellä voitte saada mitä haluatte.» Viheliäisyys ja kurjuus kasaantuivat heidän päällensä, ja he joutuivat Jumalan vihan alaisiksi. Tämä tapahtui siksi, että he kielsivät Jumalan ilmoituksen ja vääryydessään surmasivat profeetat; siksi että he olivat tottelemattomia ja kapinallisia.');
INSERT INTO "finnish" VALUES(2,62,'2:62','(Koraaniin) uskovat ja myöskin ne jotka ovat juutalaisia, kristittyjä, saabalaisia, jos he uskovat Jumalaan ja Viimeiseen päivään ja elävät hurskaasti, saavat totisesti palkkansa Herransa luona; ei mikään pelko valtaa heitä, eikä heidän tarvitse murehtia.');
INSERT INTO "finnish" VALUES(2,63,'2:63','Muistakaa, kuinka Me teimme liiton kanssanne (Siinain) vuoren kohotessa ylitsenne ja sanoimme: »Pitäkää lujasti kiinni siitä, minkä olemme antanut teille, ja muistakaa mitä se sisältää, jotta karttaisitte pahaa.»');
INSERT INTO "finnish" VALUES(2,64,'2:64','Tämänkin jälkeen te käännyitte pois, ja ilman Jumalan armoa ja laupeutta olisitte joutuneet kadotettujen joukkoon.');
INSERT INTO "finnish" VALUES(2,65,'2:65','Tehän tiedätte joukossanne ne, jotka rikkovat sapatin ja joille Me sanoimme: »Muuttukaa halveksituiksi apinoiksi!»');
INSERT INTO "finnish" VALUES(2,66,'2:66','Niin teimme heistä pelottavan esimerkin nykyiselle sukupolvelle ja tuleville sekä varoituksen jumalaapelkääväisille.');
INSERT INTO "finnish" VALUES(2,67,'2:67','Entä kun Mooses sanoi kansalleen: »Jumala käskee teitä uhraamaan hiehon"»? He kysyivät: »Teetkö pilkkaa meistä?» Hän vastasi: »Jumala varjelkoon minua olemasta niin hullu.» ,');
INSERT INTO "finnish" VALUES(2,68,'2:68','Silloin sanoivat he: »Rukoile Jumalaasi puolestamme, että Hän näyttäisi meille, millainen hiehon tulee olla.» Hän vastasi: »Hän sanoo totisesti: Sen tulee olla lehmä, jolla ei vielä ole ollut vasikkaa, mutta joka ei myöskään ole liian nuori, vaan siltä väliltä. Tehkää siis, mitä teille on käsketty.»');
INSERT INTO "finnish" VALUES(2,69,'2:69','Silloin he sanoivat: »Rukoile Herraasi puolestamme, jotta hän selittää meille, millainen sen tulee olla väriltään.» Hän vastasi: »Hän sanoo totisesti: Sen tulee olla ruskeanpunainen, kirkas väriltään, joka miellyttää katsojaa.»');
INSERT INTO "finnish" VALUES(2,70,'2:70','He sanoivat: »Huuda Herraasi puolestamme, niin hän näyttää meille, mikä on se oikea hieho; monet hiehothan näyttävät meistä toistensa kaltaisilta, mutta jos Jumala tahtoo, voimme varmasti osua oikeaan.»');
INSERT INTO "finnish" VALUES(2,71,'2:71','Mooses vastasi: »Hän sanoo: Totisesti on sen oltava ikeestä vapaa hieho, jota ei ole käytetty maan kyntämiseen eikä pellon kastelemiseen, ilman vammaa ja vikaa.» Silloin he sanoivat: »Nyt ilmaisit kaiken totuuden.» Sen jälkeen he teurastivat hiehon, mutta olivat olleet vähällä lyödä laimin Jumalan määräykset.');
INSERT INTO "finnish" VALUES(2,72,'2:72','Ja muistatteko, kun olitte surmanneet miehen ja kiistelitte toistenne kanssa syyllisyydestä, mutta Jumala tahtoi tuoda ilmi, mitä te salailitte.');
INSERT INTO "finnish" VALUES(2,73,'2:73','ja niin Me sanoimme: »Koskettakaa ruumista jollakin osalla uhrattavaa hiehoa!» Sillä tavalla Jumala herättää kuolleet eloon ja näyttää teille tunnusmerkkejään, jotta olisitte ymmärtäväisiä.');
INSERT INTO "finnish" VALUES(2,74,'2:74','Senkin jälkeen paatuivat sydämenne ja tulivat kivikoviksi, jopa kiveäkin kovemmaksi, sillä kivien joukossa on totisesti sellaisia, joista purot kohisevat esiin, sellaisia, jotka halkeavat, niin että vesi virtaa niistä, ja sellaisia, jotka Jumalan edessä syöksyvät maahan pelosta. Mutta Jumalalta ei jää salaan mitään, mitä teette.');
INSERT INTO "finnish" VALUES(2,75,'2:75','Toivotteko te, että he (juutalaiset) uskovat teitä? Johan osa heistä aikanaan kuuli Jumalan sanan, ja ymmärrettyäänkin sen he vääristelivät sitä vastoin parempaa tietoaan.');
INSERT INTO "finnish" VALUES(2,76,'2:76','Kun he tapaavat uskovaisia, he sanovat: »Me uskomme.» Mutta kun he ovat toistensa seurassa, he sanovat: »Kerrotteko heille(muslimeille), mitä Jumala on ilmoittanut teille, että he siitä kiistelisivät kanssanne Herranne edessä. Ettekö te tätä ymmärrä?»');
INSERT INTO "finnish" VALUES(2,77,'2:77','Eivätkö he sitten älyä, että Jumala tietää, mitä he salaavat ja mitä julistavat?');
INSERT INTO "finnish" VALUES(2,78,'2:78','Heidän joukossaan on oppimattomiakin, jotka eivät tunne Pyhää kirjaa muuta kuin kuulopuheista. Heille se on vain arvelua. Voi niitä, jotka kirjoittavat kirjoituksensa omin käsin ja sanovat: »Tämä on Jumalalta», hankkiakseen itsellensä siten pientä ansiota!');
INSERT INTO "finnish" VALUES(2,79,'2:79','Voi heitä heidän käsiensä kirjoituksen tähden, ja voi heitä heidän ansionsa tähden!');
INSERT INTO "finnish" VALUES(2,80,'2:80','He sanovat: »Ei tuli meitä ahdista kuin joinakuina päivinä.» Sano: »Oletteko tehneet sopimuksen Jumalan kanssa? - liittoansa Jumala kylläkään ei riko - vai puhutteko jotain Jumalasta noin vain, vaikk''ette tiedäkään?»');
INSERT INTO "finnish" VALUES(2,81,'2:81','Eivät asiat ole niin, vaan ne, jotka ovat omaksuneet pahan ja joita heidän syntinsä piirittävät, joutuvat Tulen omiksi ja jäävät siihen iankaikkisesti.');
INSERT INTO "finnish" VALUES(2,82,'2:82','Mutta niistä, jotka uskovat ja tekevät hyviä töitä, tulee Paratiisin asukkaita ikuisiksi ajoiksi.');
INSERT INTO "finnish" VALUES(2,83,'2:83','Entä kun Me solmimme liiton israelilaisten kanssa sanoen: »Teidän ei tule palvella ketään muuta kuin Jumalaa, kohdelkaa hyvin vanhempianne ja omaisianne, isättömiä ja köyhiä, hyvä olkoon puheenne kaikille ihmisille, harjoittakaa rukoilemista ja antakaa almuja!» Mutta te käännyitte pois ja luovuitte, muutamia lukuunottamatta.');
INSERT INTO "finnish" VALUES(2,84,'2:84','Entä kun Me solmimme liiton kanssanne sanoen: »Alkää vuodattako toistenne verta, älkääkä karkoittako toisianne kodeistanne», ja te vahvistitte sen omalla todistuksellanne?');
INSERT INTO "finnish" VALUES(2,85,'2:85','ja te kuitenkin surmaatte toisianne ja ajatte osan lähimmäisiänne mieron tielle, tukien toisianne synnissä ja ilkitöissä heitä vastaan. Sotavankeudesta te kylläkin heitä lunastelette, mutta vääryyttähän oli jo se, että heidät karkoititte. Uskotteko siis vain osaan Pyhästä kirjasta ja hylkäätte toisen osan? Mikä muu on sellaisten palkka, jotka niin tekevät, kuin häpeä maallisessa elämässä; ja Ylösnousemuksen päivänä tuomitaan heidät perin julmaan rangaistukseen, sillä Jumala ei ole välinpitämätön siitä, mitä te teette.');
INSERT INTO "finnish" VALUES(2,86,'2:86','He ovat niitä, jotka keinottelevat itselleen tämän maailman elämää tulevan kustannuksella; heidän rangaistustaan ei lievennetä, eikä kukaan ole puoltava heitä.');
INSERT INTO "finnish" VALUES(2,87,'2:87','Olemmehan totisesti antanut Moosekselle Pyhän kirjan ja lähettänyt profeetat hänen jälkeensä, ja Me olemme antanut Jeesukselle, Marian pojalle, selvät todistukset ja tukenut häntä Pyhällä hengellä . Mutta joka kerta kun profeetta on tullut luoksenne, tuoden mukanaan sellaista, mikä ei teitä miellytä, olette käyneet röyhkeiksi ja pitäneet toisia heistä valehtelijoina, toisia surmanneetkin.');
INSERT INTO "finnish" VALUES(2,88,'2:88','He sanovat: »Sydämemme ovat ympärileikkaamattomia.» Niin, Jumala on kironnut heidät heidän epäuskonsa tähden, sillä vähän on heidän joukossaan niitä, jotka uskovat.');
INSERT INTO "finnish" VALUES(2,89,'2:89','Ja kun heille tuli Pyhä kirja Jumalalta , vahvistaen toisen, minkä he jo omistivat, - olivathan he vastikään rukoilleet itselleen voittoa epäuskoisista - kun siis nyt se, minkä he tunsivat todeksi, tuli heidän osalleen, eivät he uskoneet sitä. Jumalan kirous uskottomien yli!');
INSERT INTO "finnish" VALUES(2,90,'2:90','Mihin surkuteltavaan hintaan ovatkaan he myyneet sielunsa hylätessään Jumalan ilmoituksen, katsoessaan karsaasti sitä, että Jumala armonsa runsaudesta lähettää ilmestyksiään kenelle tahtoo palvelijoittensa joukossa! He ovat koonneet ylitsensä vihaa vihan päälle. Uskottomia odottaa häpeällinen rangaistus.');
INSERT INTO "finnish" VALUES(2,91,'2:91','Kun heille sanotaan: »Uskokaa siihen, mitä Jumala on tuonut ilmi», he vastaavat: »Me uskomme siihen, mitä meille on ilmoitettu. Ja kohta he torjuvat sen, mikä myöhemmin on ilmoitettu, vaikka se on itse totuus, vahvistus siihen, minkä he jo omistavat.');
INSERT INTO "finnish" VALUES(2,92,'2:92','Sano (heille, Muhammed): »Miksi te surmasitte Jumalan profeettoja jo muinoin, jos muka olitte uskovaisia?»');
INSERT INTO "finnish" VALUES(2,93,'2:93','Mooseskin tuli luoksenne mukanaan selvät todistukset (Jumalan kaikkivaltiudesta), mutta kun hän oli poissa, te teitte vasikankuvan palvoaksenne, koska olitte jumalattomia.');
INSERT INTO "finnish" VALUES(2,94,'2:94','Entä kun Me solmimme liiton kanssanne (Siinain) vuoren kohotessa ylitsenne ja sanoimme: »Pysykää lujasti siinä, minkä olemme ilmoittanut teille, ja kuulkaa (sanojamme) »! He vastasivat: »Me kuulemme ja uhmailemme». Heidän sydämensä kiintyi vasikan palvontaan, ja he hylkäsivät Meidän liittomme. Sano (heille): »Surkeata on se, mitä uskonne teille tarjoaa, jos muka olette uskovaisia.» Sano: »Jos tulevan elämän asumukset Jumalan luona ovat todella tarkoitetut ainoastaan teille eikä muille ihmisille (kuten väitätte), niin toivokaa itsellenne kuolemaa (sillä onhan teidän halattava majan muuttoa), jos pysytte totuudessa.»');
INSERT INTO "finnish" VALUES(2,95,'2:95','Mutta he eivät tule koskaan halajamaan sitä, koske he pelkäävät kättensä töiden seurauksia. Jumala tuntee väärintekijät.');
INSERT INTO "finnish" VALUES(2,96,'2:96','ja sinä havaitset heidät kiintyneiksi tähän elämään enemmän kuin muut ihmiset, enemmän kuin itse epäjumalanpalvelijatkaan. Moni heistä toivoisi elävänsä tuhat vuotta; mutta eihän lainkaan vapauttaisi häntä rangaistuksesta se, että elämä pidennettäisiin. Jumala näkee, mitä he tekevät.');
INSERT INTO "finnish" VALUES(2,97,'2:97','Sano (ihmisille, Muhammed): »Jos joku on Gabrielin vihollinen, niin tietäköön, että juuri Gabriel Jumalan toimesta on tämän Kirjoituksen valaissut sydämellesi vahvistaakseen sillä, mitä oli ilmoitettu ennen sitä, opastukseksi ja ilosanomaksi uskovaisille.»');
INSERT INTO "finnish" VALUES(2,98,'2:98','Jos joku on Jumalan, hänen enkeleittensä, apostoleittensa ja Gabrielin tai Mikaelin vihollinen, niin totisesti Jumala (itse) on uskottomien vihollinen.');
INSERT INTO "finnish" VALUES(2,99,'2:99','Olemmehan totisesti ilmaissut kauttasi selvät tunnusmerkit, eivätkä ketkään muut kuin turmeltuneet kiellä niitä.');
INSERT INTO "finnish" VALUES(2,100,'2:100','Käykö aina niin, että kun teette liiton, osa teistä hylkää sen? Totisesti useimmat heistä eivät usko.');
INSERT INTO "finnish" VALUES(2,101,'2:101','Ja kun Jumalan lähettiläs tulee heidän luokseen vahvistaen sen, minkä he jo omistavat, niin osa heistä, jotka olivat saaneet Pyhän kirjan, heittää Jumalan kirjan selkänsä taakse, aivankuin eivät tietäisi mitään.');
INSERT INTO "finnish" VALUES(2,102,'2:102','Ja he noudattavat sitä, mitä saatanat vääristelivät Salomon hallituksen aikana. Kuitenkaan ei Salomo ollut uskoton, vaan saatanat olivat luopioita sekä opettivat ihmisille noituutta ja sellaista, mikä oli ilmaistu molemmille Baabelin enkeleille Haarutille ja Maarutille. Nämä kaksi eivät sentään opettaneet ketään sanomatta: »Me olemme teille vain kiusaukseksi; älkää siis epäilkö (Jumalan johdatusta).» Noilta kahdelta he oppivat sellaista, jolla he kylvävät eripuraisuutta miehen ja hänen vaimonsa välille; mutta eivät he vahingoita silläkään ketään Jumalan sallimatta. Kuitenkin he oppivat sellaista, joka vahingoittaa heitä eikä hyödytä. Totta kai he tietävät, että sillä, joka hankkii loitsuja, ei totisesti ole mitään (hyvää) osaa kuolemanjälkeisessä elämässä. Mistä surkeasta hinnasta myyvätkään he sielunsa; jospa he vain tietäisivät sen!');
INSERT INTO "finnish" VALUES(2,103,'2:103','Mutta jos he olisivat uskoneet ja karttaneet pahaa, niin olisi palkkio Jumalalta ollut parempi. Jospa he vain tietäisivät sen!');
INSERT INTO "finnish" VALUES(2,104,'2:104','Te jotka uskotte, älkää sanoko (Profeetalle): »Ota vaari meistä», vaan sanokaa: »Katso meihin», ja olkaa te kuulijoita. Niin, uskottomia odottaa tuskallinen rangaistus.');
INSERT INTO "finnish" VALUES(2,105,'2:105','Eivät uskottomat niiden joukossa, joilla on Pyhä kirja, eivätkä monijumalaiset soisi, että mitään hyvää lähetetään teille Herraltanne, mutta osoittaessaan laupeutta Jumala valitsee kenet tahtoo; niin, Jumala on rajaton armonsa runsaudessa.');
INSERT INTO "finnish" VALUES(2,106,'2:106','Kumotessamme jonkun kohdan ilmoituksestamme tai antaessamme sen unohtua me asetamme paremman tai samanlaisen tilalle. Etkö tiedä, että Jumala on joka asiassa kaikkivaltias?');
INSERT INTO "finnish" VALUES(2,107,'2:107','Etkö tiedä, että taivasten ja maan yliherruus kuuluu Jumalalle? Paitsi Jumalaa ei teillä ole ketään suojelijaa eikä auttajaa.');
INSERT INTO "finnish" VALUES(2,108,'2:108','Vai tahdotteko tehdä kysymyksiä profeetallenne samalla tavalla, kuin muinoin kyseltiin kaikenlaista Moosekselta? Huomatkoon se, joka valitsee epäuskon uskon asemesta, että hän eksyy oikealta tieltä.');
INSERT INTO "finnish" VALUES(2,109,'2:109','Monet niiden joukossa, joilla on Pyhä kirja, toivoisivat voivansa saattaa teidät taas epäuskoisiksi sen jälkeen, kun te kerran olette tulleet uskoon, koska he ovat kateellisia, vaikka totuus onkin selvinnyt heille. Olkaa heitä kohtaan sääliväisiä ja kärsivällisiä , kunnes Jumala sanoo sanansa. Katso, Jumala on totisesti kaikkivoipa.');
INSERT INTO "finnish" VALUES(2,110,'2:110','Rukoilkaa säännöllisesti ja antakaa almuja! Kaiken hyvän, minkä teette sieluanne varten, te löydätte Jumalan luona. Jumala näkee, mitä ikinä te teette.');
INSERT INTO "finnish" VALUES(2,111,'2:111','Sanovatpa he: »Eivät ketkään muut pääse Paratiisiin kuin juutalaiset ja kristityt.» Sellainen on heidän mielihalunsa. Sano: »Esittäkää todistukset väitteenne puolesta, jos tahdotte pysyä totuudessa.»');
INSERT INTO "finnish" VALUES(2,112,'2:112','Toisin käy sen, joka uskossa alistuu Jumalan tahtoon tehden hyvää; hän saa palkan Herraltansa: ei mikään pelko valtaa heitä, eikä heidän tarvitse murehtia.');
INSERT INTO "finnish" VALUES(2,113,'2:113','Juutalaiset sanovat: »Kristityt eivät noudata todellisia perusteita», ja kristityt sanovat: »Juutalaisilla ei ole oikeaa perustusta»; kuitenkin he lukevat Pyhää kirjaa. Samalla tavalla ovat puhuneet nekin, jotka eivät mitään tiedä. Mutta Ylösnousemuksen päivänä Jumala on ratkaiseva heidän välisensä kiistaasiat.');
INSERT INTO "finnish" VALUES(2,114,'2:114','Kuka tekee suurempaa vääryyttä kuin se, joka estää toisia lähestymästä Jumalan pyhäkköjä, jottei niissä Hänen nimeään mainittaisi, ja aiheuttaa niiden sortumisen. Heille itselleen ei voi suoda pääsyä niihin muuten kuin pelosta vavisten.');
INSERT INTO "finnish" VALUES(2,115,'2:115','Heitä odottaa häpeä tässä elämässä ja kauhea rangaistus tulevassa.');
INSERT INTO "finnish" VALUES(2,116,'2:116','Jumalalle kuuluu Itä ja Länsi, ja mihin käännyttekin, ovat Jumalan kasvot siellä; katso, Jumala sulkee olemukseensa kaiken ja tietää kaiken.');
INSERT INTO "finnish" VALUES(2,117,'2:117','Sanovatpa he: »Jumala on ottanut itselleen Pojan». Ei, Hänelle yksin kunnia! Totisesti kaikki taivaassa ja maan päällä on Hänen; kaikki ovat Hänelle kuuliaiset.');
INSERT INTO "finnish" VALUES(2,118,'2:118','Hän on taivaan ja maan Luoja; kun Hän päättää jonkun asian, sanoo Hän ainoastaan »Tapahdu!» ja niin tapahtuu. Mutta ne jotka eivät mitään tiedä, sanovat: »Miksi ei Jumala puhu meille, tai miksi emme saa tunnusmerkkiä?» Niin puhuivat nekin, jotka ennen heitä elivät; heidän sydämensä ovat samankaltaiset. Me olemme osoittanut merkit ihmisille, jotka pysyvät varmoissa tiedoissa.');
INSERT INTO "finnish" VALUES(2,119,'2:119','Sinut, Muhammed, olemme lähettänyt totuuden ilmoittajaksi, ilosanoman julistajaksi ja varoittajaksi, eikä sinun tarvitse olla vastuussa Helvetin tuleen joutuvista.');
INSERT INTO "finnish" VALUES(2,120,'2:120','Mutta juutalaiset ja kristityt eivät mielisty sinuun, paitsi ehkä kun noudattaisit heidän oppiaan. Sano: »Jumalan oma ohjaus on totisesti oikea Johdatus.» Ja jos sinä seuraat heidän pyyteitään kaiken sen jälkeen, mikä on osaksesi tullut, et sinä totisesti ole löytävä suojelijaa etkä auttajaa Jumalassa.');
INSERT INTO "finnish" VALUES(2,121,'2:121','Ne, joille Pyhän kirjan olemme antanut ja jotka lukevat sitä, niinkuin sitä lukea tulee, uskovat siihen, mutta ne jotka kieltävät sen, ovat kadotuksen omat.');
INSERT INTO "finnish" VALUES(2,122,'2:122','Israelilaiset, muistakaa armoani, jota Minä olen teidän ylitsenne vuodattanut, sekä että olen korottanut teidät kaikkien luotujen yli.');
INSERT INTO "finnish" VALUES(2,123,'2:123','Ja pelätkää sitä päivää, jolloin yksikään sielu ei voi tehdä vähäisintäkään toisen puolesta, jolloin ei mitään lunnaita oteta vastaan eikä mitään välitystä hyväksytä, eikä kukaan saa toiselta mitään apua.');
INSERT INTO "finnish" VALUES(2,124,'2:124','Entä kun Herra koetteli Aabrahamia käskyillään, ja tämä täytti ne? Hän sanoi: »Tahdon totisesti tehdä sinut ihmisten uskonnolliseksi johtajaksi.» Aabraham kysyi: »Teetkö samoin (ihmisten uskonnollisen johtajat) jälkeläisistäni?» Hän vastasi: »Minun liittoni ei ulotu väärämielisiin.»');
INSERT INTO "finnish" VALUES(2,125,'2:125','Entä kun Me määräsimme temppelin (Mekassa) ihmisten pyhäköksi ja turvapaikaksi sanoen: »Ottakaa rukoushuoneeksenne paikka, mihin Aabraham pysähtyi (rukoilemaan)»? Me velvoitimme Aabrahamin ja Ismaelin puhdistamaan pyhäkön niille, jotka kulkueissa käyvät sen ympäri, jotka vetäytyvät sinne mietiskelyyn, jotka siellä kumartavat ja heittäytyvät maahan.');
INSERT INTO "finnish" VALUES(2,126,'2:126','Entä kun Aabraham sanoi: »Herrani, tee tämä turvalliseksi seuduksi ja suo hedelmiäsi sen asukkaille, jos he uskovat Jumalaan ja Viimeiseen päivään?» Hän vastasi: »Senkin, joka on epäuskoinen, annan Minä nauttia lyhyen ajan; sitten olen syöksevä hänet Tulen kidutukseen, joka on surkea matkan pää.»');
INSERT INTO "finnish" VALUES(2,127,'2:127','Kun Aabraham ja Ismael laskivat Herran huoneen perustuksen, sanoivat he: »Herramme, ota vastaan tämä meiltä! Sinä, yksin Sinä, suljet kaiken kuuloosi ja tietoosi.');
INSERT INTO "finnish" VALUES(2,128,'2:128','Herra, tee meidät molemmat Sinulle alamaisiksi (muslimeiksi) ja tee jälkeläisemme yhdeksi kansaksi ja seurakunnaksi, joka on Sinulle alamainen. Osoita meille hartaudenharjoituksemme ja sääli meitä. Sinä yksin olet totisesti sääliväinen, Armahtaja.');
INSERT INTO "finnish" VALUES(2,129,'2:129','Herra, lähetä myös profeetta heidän omasta keskuudestaan heidän luokseen julistamaan heille ihmeelliset ilmoituksesi, tunnusmerkkisi, opettamaan heille Pyhää kirjaa sekä viisautta ja tekemään heidät puhtaaksi. Sillä Sinä yksin olet totisesti mahtava, viisas.»');
INSERT INTO "finnish" VALUES(2,130,'2:130','Kukapa muu hylkää Aabrahamin uskonnon kuin se, joka pitää houkkana itseään? Olemmehan Me valinnut hänet tässä elämässä, ja kuoleman jälkeen on Aabraham totisesti kuuluva vanhurskasten joukkoon.');
INSERT INTO "finnish" VALUES(2,131,'2:131','Kun hänen Herransa sanoi hänelle: »Alistu!» hän vastasi: »Olen antautunut maailmojen Herran alamaiseksi».');
INSERT INTO "finnish" VALUES(2,132,'2:132','Ja Aabraham velvoitti poikansakin tähän, ja samoin Jaakob sanoen:»Poikani, Jumala on totisesti valinnut (tosi) uskonnon teille; älkää siis kuolko, ennenkuin olette siihen alistuneet» .');
INSERT INTO "finnish" VALUES(2,133,'2:133','Tai olitteko läsnä todistajina, kun Jaakobin kuolema lähestyi ja hän sanoi pojalleen: »Ketä te aiotte palvella kuolemani jälkeen?» He vastasivat: »Me palvelemme sinun ja isiesi Aabrahamin, Ismaelin ja Iisakin Jumalaa, yhtä ainoata Jumalaa, ja olemme Hänelle alamaisia.»');
INSERT INTO "finnish" VALUES(2,134,'2:134','Se sukupolvi on siirtynyt pois. He saivat, mitä olivat ansainneet. Te saatte, mitä te ansaitsette, ettekä vastaa siitä, mitä he tekivät.');
INSERT INTO "finnish" VALUES(2,135,'2:135','Sanovatpa he: »Kääntykää juutalaisiksi tai kristityiksi, niin tulette oikealle tielle.» Vastatkaa: »Ei, me seuraamme Aabrahamin oppia, koska hän oli oikeauskoinen (haniifi) eikä kuulunut monijumalaisiin.»');
INSERT INTO "finnish" VALUES(2,136,'2:136','Sanokaa: »Me uskomme Jumalaan ja siihen, mikä on ilmoitettu meille ja mikä on ilmoitettu Aabrahamille, Ismaelille, Iisakille ja Jaakobille ja Israelin sukukunnille,');
INSERT INTO "finnish" VALUES(2,137,'2:137','siihen, mitä Mooses ja Jeesus ovat (Jumalalta ilmoitettavaksi) saaneet ja mitä profeetat ovat saaneet julistettavaksi Herraltansa. Emme tee mitään erotusta yhdenkään heidän välillään, olemme antautuneet Hänelle alamaisiksi.» Jos he sitten uskovat samoin kuin te, niin he ovat oikealla tiellä, mutta jos he kääntyvät pois, niin he ovat luopioita. Jumala on oleva riittävä suojasi heitä vastaan, sillä Hän on kuuleva ja tietävä.');
INSERT INTO "finnish" VALUES(2,138,'2:138','Me olemme Jumalan merkitsemiä, ja keneltä on parempi saada tuntomerkkinsä kuin Jumalalta? Me olemme Hänen palvelijoitaan.');
INSERT INTO "finnish" VALUES(2,139,'2:139','Sano (Pyhät kirjoitukset omaaville): »Kiistelittekö kanssamme Jumalasta, kun hän on sekä meidän Herramme että teidän Herranne? Meillä on omat tekomme ja teillä on omat tekonne, ja me turvaudumme Häneen vilpittömästi.»');
INSERT INTO "finnish" VALUES(2,140,'2:140','Vai sanotteko: »Aabraham, Ismael, Iisak ja jaakob ja Israelin sukukunnat olivat totisesti joko juutalaisia tai kristittyjä?» Sano: »Kumpi tietää paremmin, te vai Jumala? Ja kuka on väärämielisempi kuin se, joka salaa todistuksen, jonka on saanut Jumalalta? Eikä Jumala ole välinpitämätön siitä, mitä te teette.»');
INSERT INTO "finnish" VALUES(2,141,'2:141','Se sukupolvi on siirtynyt pois. Se on saanut, mitä oli ansainnut; te saatte, mitä te ansaitsette. Ettekä te joudu vastaamaan siitä, mitä he tekivät.');
INSERT INTO "finnish" VALUES(2,142,'2:142','Mielettömät ihmiset tulevat sanomaan: »Mikä on kääntänyt heidät pois heidän kiblastaan, jota päin he ennen suuntautuivat rukoillessaan?» Sano: »Jumalalle kuuluu Itä ja Länsi. Hän johtaa kenet tahtoo oikealle tielle.»');
INSERT INTO "finnish" VALUES(2,143,'2:143','Siten Me olemme tehnyt teidät (arabialaiset) välittäjäkansaksi, että te olisitte todistajia ihmisten edessä ja profeetta Muhammed olisi todistajana teidän edessänne. Kiblan, johon te ennen suuntauduitte, määräsimme ainoastaan erottaaksemme sen, joka seuraa profeettaa, siitä, joka peräytyy hänen luotaan takaisin. ja se oli totisesti vaikea koetus, paitsi niille, joita Jumala johtaa; mutta Jumalan tarkoitus ei ollut tehdä uskoanne turhaksi, sillä Jumala on totisesti lempeä ja laupias ihmisiä kohtaan.');
INSERT INTO "finnish" VALUES(2,144,'2:144','Olemme nähnyt, kuinka kasvosi (rukoillessasi) harhailivat sinne tänne taivasta kohti, mutta vastedes tahdomme totisesti kääntää kasvosi Kiblaan päin, joka on sinua tyydyttävä. Käännä siis kasvosi Rauhoitetun temppelin suuntaan. Ja te, muslimit, missä olettekaan, kääntäkää rukoillessaane kasvonne sinne. Katso, ne jotka Pyhän kirjan ovat saaneet, tietävät totisesti,että tämä (ilmoitus) on heidän Herraltansa, eikä Jumala ole välinpitämätön siitä, mitä he tekevät. Vaikka sinä näytätkin niille, jotka ovat Pyhän kirjan saaneet, kaikkia mahdollisia tunnusmerkkejä, eivät he suuntaudu sinun kiblasi mukaan.');
INSERT INTO "finnish" VALUES(2,145,'2:145','Sinäkään et voi suuntautua heidän kiblansa mukaan, eivät eräät heistäkään suuntaudu toisten kiblan mukaan, mutta jos sinä suuntaudut heidän toivomustensa mukaan kaiken sen tiedon jälkeen, mikä osaksesi on tullut, niin kuulut totisesti väärämielisiin.');
INSERT INTO "finnish" VALUES(2,146,'2:146','Ne (profeetat), joille olemme Pyhän kirjan antanut, tuntevat sen yhtä hyvin, kuin he tuntevat omat poikansa, mutta osa heistä salaa totuuden vastoin parempaa tietoaan.');
INSERT INTO "finnish" VALUES(2,147,'2:147','Totuus tulee Herraltasi; siksi älä kuulu epäilijöiden joukkoon.');
INSERT INTO "finnish" VALUES(2,148,'2:148','Jokaisella on nyt määrätty suunta, johon hän kääntyy (rukoillessaan); niin kilpailkaa nyt siis toistenne kanssa hyvissä teoissa. Missä olettekin, on Jumala johdattava teidät yhteen; Jumala on totisesti kaikkivaltias.');
INSERT INTO "finnish" VALUES(2,149,'2:149','Mistä tuletkin, käännä kasvosi Rauhoitetun temppelin suuntaan, sillä tämä on totuus Herraltasi, eikä Jumala ole välinpitämätön siitä, mitä te teette.');
INSERT INTO "finnish" VALUES(2,150,'2:150','Mistä tuletkin, käännä kasvosi Rauhoitetun temppelin suuntaan, ja missä olettekin, muslimit, kääntäkää kasvonne sinne rukoillessanne, jottei muille ihmisille jäisi mitään sanomista teitä vastaan, paitsi sellaisille, jotka väärin tekevät. Mutta älkää pelätkö heitä, vaan pelätkää Minua ja tehkää tämä siksi, että Minä voisin saattaa suosioni teille täydelliseksi, jotta te pysyisitte oikealla tiellä.');
INSERT INTO "finnish" VALUES(2,151,'2:151','Me olemme lähettäneet profeetan teidän omasta keskuudestanne luoksenne, jotta hän julistaisi teille tunnusmerkkimme ja tekisi teidät puhtaaksi, opettaisi teille Pyhää kirjaa sekä viisautta ja opettaisi teille, mitä ette ennen tietäneet.');
INSERT INTO "finnish" VALUES(2,152,'2:152','Muistakaa siis Minua, niin Minä olen muistava teitä. Osoittakaa Minulle kiitollisuutta älkääkä kieltäkö minua.');
INSERT INTO "finnish" VALUES(2,153,'2:153','Te jotka uskotte, etsikää apua kärsivällisyydestä ja rukouksesta. Jumala on totisesti kärsivällisten kanssa.');
INSERT INTO "finnish" VALUES(2,154,'2:154','Älkääkä sanoko »kuolleiksi» niitä, jotka surmataan Jumalan asian tähden. Ei, hehän elävät, vaikka te ette sitä tajua.');
INSERT INTO "finnish" VALUES(2,155,'2:155','Totisesti Me koettelemme teitä pelolla, nälällä, omaisuuden, hengen ja sadon menetyksellä. Kuitenkin, Muhammed, vie kärsivällisille riemullinen sanoma.');
INSERT INTO "finnish" VALUES(2,156,'2:156','Niille, jotka koettelemusten heitä kohdatessa sanovat: »Me kuulumme totisesti Jumalalle ja hänen luokseen me palaamme»,');
INSERT INTO "finnish" VALUES(2,157,'2:157','sellaisille tulee siunausta ja laupeutta heidän Herraltaan, ja nämä ovat niitä, jotka ovat oikealla tiellä.');
INSERT INTO "finnish" VALUES(2,158,'2:158','Katso, as-Safan ja al-Marwan kukkulat kuuluvat Jumalan muistomerkkeihin, ja sille, joka tekee pyhiinvaelluksen (Kaaban) temppeliin tai käy siellä, ei siis lueta synniksi, jos hän suorittaa kävelyt niiden ympäri (vaikka pakanatkin niin tekevät). Jos joku vapaaehtoisesti tekee hyvää, niin Jumala totisesti muistaa sen, Hän on kaikkitietävä.');
INSERT INTO "finnish" VALUES(2,159,'2:159','Ne jotka salaavat selvät todistukset ja Meidän ilmoittamamme johdatuksen, senjälkeen kun olemme selittänyt ne (tässä) Pyhässä kirjassa, heidät kiroaa Jumala ja heitä kiroavat kaikki, joilla on valta kirota,');
INSERT INTO "finnish" VALUES(2,160,'2:160','paitsi jos he kääntyvät, tekevät parannuksen ja tuovat totuuden julki. Minä säälin katuvia, koska olen säälivä, armollinen.');
INSERT INTO "finnish" VALUES(2,161,'2:161','Niitten yllä, jotka ovat uskottomia ja kuolevat epäuskossaan, lepää totisesti sekä Jumalan ja enkelien että kaikkien ihmisten kirous');
INSERT INTO "finnish" VALUES(2,162,'2:162','ja se pysyy ikuisesti, eikä heidän rangaistustansa lievennetä eikä heille suoda armonaikaa.');
INSERT INTO "finnish" VALUES(2,163,'2:163','Teidän Jumalanne on ainoa Jumala; ei ole muuta Jumalaa kuin Hän, laupias, armollinen.');
INSERT INTO "finnish" VALUES(2,164,'2:164','Katso, taivasten ja maan järjestyksessä, yön ja päivän vaihtelussa, laivojen purjehtimisessa merellä niiden kuljettaessa sellaista, joka hyödyttää ihmisiä, vedessä, jonka Jumala taivaasta lähettää ja jolla hän elvyttää maan sen horrostilan jälkeen, kaikenlaisten eläinten leviämisessä yli maan, tuulten suunnassa ja pilvien nöyrässä kulussa taivaan ja maan välillä, on totisesti tunnusmerkkejä (Jumalan kaikkivaltiudesta) ihmisille, joilla on ymmärrystä.');
INSERT INTO "finnish" VALUES(2,165,'2:165','Ihmisten joukossa on niitä, jotka Jumalan rinnalle asettavat palveltavikseen muita, joita he rakastavat niin, kuin heidän tulisi vain Jumalaa rakastaa; mutta ne, jotka uskovat, rakastavat Jumalaa yli kaiken. Jospa vain ne, jotka tekevät väärin, käsittäisivät, että kaikki valta kuuluu Jumalalle ja että Jumala on ankara rangaistessaan! He saavat osakseen tuomion (sinä päivänä),');
INSERT INTO "finnish" VALUES(2,166,'2:166','jolloin ne, jotka on otettu esikuviksi, luopuvat niistä, jotka seurasivat heitä, jolloin he saavat kokea rangaistuksen ja yhteys heidän välillään katkeaa.');
INSERT INTO "finnish" VALUES(2,167,'2:167','ja ne, jotka olivat seuranneet esikuviaan, sanovat: »Olisipa meillä joku palaamisen mahdollisuus, niin me luopuisimme heistä, kuten he ovat luopuneet meistä.» Niin on Jumala heidän kauhukseen näyttävä heille heidän omat tekonsa, eivätkä he tule pääsemään pois Tulesta.');
INSERT INTO "finnish" VALUES(2,168,'2:168','Oi ihmiset, syökää kaikkea maan päällä, mikä on sallittua ja hyvää, älkääkä seuratko saatanan jälkiä. Hän on totisesti teidän leppymätön vihollisenne.');
INSERT INTO "finnish" VALUES(2,169,'2:169','Hän käskee teitä vain syntiin ja törkeyksiin sekä opettaa väittämään Jumalasta sitä, mitä ette tiedä.');
INSERT INTO "finnish" VALUES(2,170,'2:170','Kun heille sanotaan: »Noudattakaa, mitä Jumala on ilmaissut», he vastaavat: »Ei, me tahdomme noudattaa niitä tapoja, jotka olemme havainneet isillämme olleen.» Entä silloinkin, kun heidän isänsä eivät mitään ymmärtäneet eivätkä olleet oikealla tiellä?');
INSERT INTO "finnish" VALUES(2,171,'2:171','Profeetan suhde niihin, jotka ovat uskottomia, on sama kuin sen, joka kutsuu olentoja, jotka eivät erota muuta kuin äänen ja huudon, jotka ovat kuuroja, mykkiä ja sokeita, niin etteivät mitään ymmärrä.');
INSERT INTO "finnish" VALUES(2,172,'2:172','Te jotka uskotte, syökää sitä hyvää, jota Me teille suonut olemme, ja kantakaa Jumalalle kiitokseksenne, jos hän on teille se, ketä todella palvelette.');
INSERT INTO "finnish" VALUES(2,173,'2:173','Hän on ainoastaan kieltänyt teitä syömästä itsestään kuolleen tai pedon repimän eläimen lihaa, verta, sianlihaa ja sellaista, jota teurastettaessa on mainittu jonkun toisen kuin Jumalan nimi. Mutta jos jotakuta hätä pakottaa, eikä hänellä ole siihen himoa eikä ole rikollista aikomusta, niin se ei ole mikään synti hänelle; katso, Jumala on armahtavainen ja laupias.');
INSERT INTO "finnish" VALUES(2,174,'2:174','Ne, jotka salaavat jotain Pyhästä kirjasta, joka on Jumalan Ilmoitus, ja vaihtavat sen mitättömän arvoihin, eivät totisesti tule täyttämään vatsaansa muulla kuin tulella. Jumala ei ole puhuva heille Ylösnousemuksen päivänä eikä vapahtava heitä, vaan heitä odottaa tuskallinen rangaistus.');
INSERT INTO "finnish" VALUES(2,175,'2:175','Nämä ovat niitä, jotka harhaan käyden ovat Johdatuksesta luopuneet ja vaihtaneet anteeksiannon rangaistukseen; kuinka itsepintaisesta he pyrkivätkään Tuleen!');
INSERT INTO "finnish" VALUES(2,176,'2:176','ja tämä tapahtuu siksi, että Jumala on lähettänyt Kirjan, joka sisältää totuuden; ne jotka rakentavat riitoja tämän Kirjan johdosta, menevät pitkälle uhmassa.');
INSERT INTO "finnish" VALUES(2,177,'2:177','Hurskautta ei ole se, että käännätte kasvonne Itään tai Länteen, vaan hurskaita ovat ne, jotka uskovat Jumalaan ja Viimeiseen päivään, enkeleihin, Pyhään kirjaan ja profeettoihin ja rakkaudesta Häneen antavat omaisuudestaan omaisilleen, orvoille, köyhille, vaeltajille, kerjäläisille ja orjien vapauttamiseksi sekä harrastavat oikeata rukouselämää, antavat almuja, tehtyään jonkun sopimuksen täyttävät sen, jotka ovat kärsivällisiä ahdistuksessa, vastoinkäymisessä ja puutteen aikana; nämä ovat niitä, jotka ovat vanhurskaita, niin, nämä ovat jumalaapelkääväisiä.');
INSERT INTO "finnish" VALUES(2,178,'2:178','Teille, jotka uskotte, on määrätty verenkosto murhatapauksissa: vapaa mies vapaasta, orja orjasta ja nainen naisesta, mutta jos joku saa sen jotenkin muuten anteeksi (loukatulta) veljeltä, on se järjestettävä asianmukaisella tavalla ja hyvitettävä runsaalla mitalla.');
INSERT INTO "finnish" VALUES(2,179,'2:179','Tämä on lievitys ja laupeudenosoitus Herraltamme, mutta sitä, joka tämän jälkeen rikkoo, odottaa tuskallinen rangaistus. Verenkosto varjelee teidän henkeänne, jotta te, joilla on ymmärrystä, karttaisitte pahaa.');
INSERT INTO "finnish" VALUES(2,180,'2:180','Teille on määrätty, että kun joku teistä on kuolemansairaana ja jättää jälkeensä jotakin omaisuutta, niin hän laatikoon rehellisesti testamentin vanhempien ja lähimpien omaisten hyväksi. Tämä on kaikkien pahaa karttavien velvollisuus.');
INSERT INTO "finnish" VALUES(2,181,'2:181','Jos joku muuttaa testamenttia, sen jälkeen kuin on sen kuullut, luetaan se synniksi vain niille, jotka sitä muuttavat. Katso, Jumala on totisesti kuuleva, tietävä.');
INSERT INTO "finnish" VALUES(2,182,'2:182','Mutta jos joku pelkää testamentin tekijän olevan harhassa tai väärässä ja oikoo asioita rakentaakseen rauhaa (perillisten välillä), niin ei sitä lueta hänelle synniksi. Jumala on totisesti armahtavainen ja laupias.');
INSERT INTO "finnish" VALUES(2,183,'2:183','Teidän, jotka uskotte, tulee paastota, samoin kuin piti paastota niidenkin, jotka ennen teitä elivät, jotta karttaisitte pahaa.');
INSERT INTO "finnish" VALUES(2,184,'2:184','(Paastottava on) määrätyt päivät. Mutta jos joku teistä on sairas tai matkoilla, on hänen paastottava joinakin muina päivinä, ja niiden, jotka siihen pystyvät, on paaston korvaukseksi ravittava joitakin köyhiä. Jos joku vapaaehtoisesti tekee hyvän työn, on se hänelle parempi. Mutta paastoaminen on teille kuitenkin kaikkein parasta (s.o. paastoamalla parhaiten täytätte Jumalan tahdon); tämä olkoon sanottu teille tiedoksi.');
INSERT INTO "finnish" VALUES(2,185,'2:185','»Ramadaan» on se kuukausi, jolloin Koraani annettiin opastukseksi ihmisille ja selväksi todistukseksi (Jumalan) johdatuksesta, jotta hylättäisiin paha ja valittaisiin hyvä; tämän kuukauden aikana paastotkoon se teistä, joka on kotioloissaan, mutta jos joku on sairas tai, matkoilla, on hänen sen sijaan (muuna aikana) paastottava yhtä monta muuta päivää. Jumala tahtoo tehdä sen teille helpoksi eikä tarkoita sitä teille taakaksi, - jotta paastopäivienne luku täyttyisi ja te ylistäisitte Jumalaa Hänen johdatuksensa tähden sekä olisitte Hänelle kiitollisia.');
INSERT INTO "finnish" VALUES(2,186,'2:186','Kun palvelijana sinulta kysyvät jotakin Minusta, olen totisesti lähellä; Minä kuulen anovaisen rukouksen, kun hän huutaa Minua avukseen. Niin kuulkoot hekin Minun kutsuni ja luottakoot Minuun, jotta he seuraisivat oikeata Johdatusta.');
INSERT INTO "finnish" VALUES(2,187,'2:187','Paastonaikana teidän on sallittu öisin olla vaimojenne luona; he ovat teidän verhonne, ja te olette heidän verhonsa. Jumala tietää, että te aikaisemmin kiusasitte itseänne pidättyväisyydellä; siksi on Hän säälinyt teitä, Hän on suvaitsevainen. Olkaa siis yhteydessä heidän kanssaan ja etsikää sitä, minkä Jmala on teille määrännyt; syökää ja juokaa aamuhämäriin asti, kunnes vaalea lanka selvästi erottuu mustasta. Mutta sitten noudattakaa tarkoin paastoa yön tuloon saakka älkääkä koskeko heihin, vaan omistautukaa hartaudenharjoituksille rukoushuoneissa. Nämä ovat Jumalan asettamat rajat; älkää siis sinä aikana koskeko heihin. Niin selittää Jumala ihmisille tunnusmerkkinsä, jotta te karttaisitte pahaa.');
INSERT INTO "finnish" VALUES(2,188,'2:188','Älkää keskenänne kuluttako omaisuuttanne turhuuteen älkääkä sillä lahjoko tuomareita, jotta syntisesti ja vastoin parempaa tietoanne saisitte nauttia jotakin toisten omaisuudesta.');
INSERT INTO "finnish" VALUES(2,189,'2:189','He tiedustelevat sinulta, Muhammed, uuden kuun merkitystä. Sano: Kuunvaiheet ovat määrätyt ihmisten ajanlaskua ja toivioretkiä varten. Hurskautta ei ole se, että te menette taloon takatietä (kuten taikojen palvojat ajoittain), vaan hurskaita ovat ne, jotka karttavat pahaa. Käykää taloon portin kautta ja pelätkää Jumalaa, jotta menestyisitte.');
INSERT INTO "finnish" VALUES(2,190,'2:190','Taistelkaa Jumalan retkellä niitä vastaan, jotka vastaanne sotaa käyvät, mutta itse älkää aiheetta hyökätkö. Totisesti Jumala ei rakasta rauhanrikkojia.');
INSERT INTO "finnish" VALUES(2,191,'2:191','Surmatkaa heidät, missä heidät tapaattekin, ja karkoittakaa heidät joka paikasta, mistä he ajoivat pois teidät, sillä pakanuuteen käännyttäminen on pahempi kuin murha. Mutta älkää taistelko heitä vastaan Rauhoitetun temppelin lähellä, jolleivät he hyökkää päällenne siellä; mutta jos he hyökkäävät, niin tappakaa heidät. Sellainen on oleva uskottomien palkka.');
INSERT INTO "finnish" VALUES(2,192,'2:192','Mutta jos he lakkaavat taistelemasta, niin on Jumala totisesti armahtavainen ja laupias.');
INSERT INTO "finnish" VALUES(2,193,'2:193','Taistelkaa heitä vastaan, kunnes pakanuuden viettelys lakkaa ja keskuudessamme vakiintuu ainoan Jumalan palveleminen. Mutta jos he herkeävät, älköön vallitko vihollisuutta muita kuin uskottomia vastaan.');
INSERT INTO "finnish" VALUES(2,194,'2:194','Rauhoitettu kuukausi kostetaan (sen rikkojille) rauhoitetulla kuukaudella ja pyhitetyt paikat vastaavilla. ja jos joku ryhtyy sotaan teitä vastaan, niin ahdistakaa häntä samalla tavalla, kuin hän ahdisti teitä. Täyttäkää velvollisuutenne Jumalaa kohtaan ja tietäkää, että Jumala on niiden kanssa, jotka karttavat pahaa.');
INSERT INTO "finnish" VALUES(2,195,'2:195','Uhratkaa omaisuuttanne Jumalan asian hyväksi; älkää syöksykö turmioon, tehkää hyvää. Katso, Jumala rakastaa hyväntekijöitä.');
INSERT INTO "finnish" VALUES(2,196,'2:196','Suorittakaa pyhiinvaelluksenne, käykää Jumalan kunniaksi pyhässä paikassa (Mekassa). Mutta jos olette estetyt, niin lähettäkää jotakin uhrattavaa, mikä on helposti saatavissa; älkääkä ajelko päätänne, ennenkuin uhrinne on tullut perille määräpaikkaansa. Mutta jos joku teistä on sairas tai päässä olevan vamman vaivaama, niin hän hyvittäköön saman paastolla, almujenannolla tai uhrina. Ja kun sitten olette turvassa vihollisilta, niin se, joka rajoittuu käyntiin pyhäkössä, lykäten pyhiinvaelluksensa, antakoon jonkun uhrin, joka on helposti saatavissa; se taas, joka sitä ei voi hankkia, paastotkoon kolme päivää (vastaisen pyhiinvaelluksensa aikana) ja seitsemän palattuaan,yhteensä kymmenen päivää. Tämä koskee sitä, jonka omaiset eivät ole Rauhoitetun pyhäkön alueella; täyttäkää velvollisuutenne Jumalaa kohtaan ja tietäkää, että Jumala on ankara rangaistessaan.');
INSERT INTO "finnish" VALUES(2,197,'2:197','Pyhiinvaellus tapahtuu vain määrättyinä kuukausina; jos joku päättää suorittaa pyhän matkan näiden kuukausien aikana, olkoon häneltä yhteys naiseen, laittomuus ja riita pyhiinvaelluksen aikana kielletty. Mitä hyvää teettekään, Jumala tietää sen. Ja varustautukaa matkaeväillä. Paras matkaeväs on jumalanpelko; täyttäkää velvollisuutenne Minua kohtaan te, joilla on ymmärrys.');
INSERT INTO "finnish" VALUES(2,198,'2:198','Ei ole mikään synti koettaa hankkia (kaupanteolla) itsellenne jotakin Herranne rikkauksista. Mutta kun te joukoissa kiiruhdatte alas Arafaatilta, niin rukoilkaa Jumalaa rauhoitetulla muistopaikalla. Muistakaa Häntä silloin Hänen johdatuksensa tähden, sillä ennen te totisesti kuuluitte eksyneisiin.');
INSERT INTO "finnish" VALUES(2,199,'2:199','Kiiruhtakaa sitten eteenpäin samasta paikasta kuin vaeltajain joukot ja rukoilkaa Jumalalta anteeksiantoa. Katso, Jumala on armahtavainen ja laupias.');
INSERT INTO "finnish" VALUES(2,200,'2:200','Ja kun olette suorittaneet hartaudenharjoituksenne, niin muistakaa Jumalaa, samoinkuin muistelitte isiänne , jopa vielä hartaammin. Ihmisten joukossa on sellaisia, jotka sanovat: »Herramme, anna meille kaikkea tässä elämässä!» Nämä eivät saa mitään osaa tulevassa.');
INSERT INTO "finnish" VALUES(2,201,'2:201','Mutta on sellaisiakin, jotka sanovat: »Herra, anna meille hyvää tässä elämässä ja hyvää tulevassa ja varjele meitä Tulen rangaistuksesta!»');
INSERT INTO "finnish" VALUES(2,202,'2:202','Näille on varattu osansa siitä, mitä ovat ansainneet, sillä Jumala on nopea tilinteossa.');
INSERT INTO "finnish" VALUES(2,203,'2:203','Muistelkaa Jumalaa määrättyinä päivinä. Jos joku rientää paria päivää varhemmin, niin ei hänelle sitä viaksi lueta, tai jos joku viivähtää, ei hänellekään sitä syyksi lueta, kunhan hän vain karttaa pahaa. Täyttäkää velvollisuutenne Jumalaa kohtaan ja tietäkää, että teidät kootaan Hänen luokseen.');
INSERT INTO "finnish" VALUES(2,204,'2:204','Ihmisten joukosta tapaat sellaisenkin, jonka puhe maallisesta elämästä miellyttää sinua ja joka kutsuu Jumalaa sen todistajaksi, mikä on hänen sydämessään, vaikka hän onkin sitkein vastustajasi.');
INSERT INTO "finnish" VALUES(2,205,'2:205','Ja niin pian kuin hän on kääntänyt selkänsä, kiiruhtaa hän saamaan aikaan pahennusta tässä maassa ja levittämään jumalattomuutta turmellakseen sekä kylvöt että karjan, mutta Jumala ei rakasta pahuutta.');
INSERT INTO "finnish" VALUES(2,206,'2:206','Kun hänelle sanotaan: »Pelkää Jumalaa!» vetää ylpeys hänet syntiin. Helvetissä maksetaan hänelle hänen ansionsa mukaan. Mikä surkea olinpaikka!');
INSERT INTO "finnish" VALUES(2,207,'2:207','Ihmisten joukossa tapaat sellaisenkin, joka myy itsensä (orjaksi) tehdäkseen Jumalalle otollisen työn; ja Jumala on lempeä orjiaan kohtaan.');
INSERT INTO "finnish" VALUES(2,208,'2:208','Te, jotka uskotte, tulkaa kaikki kuuliaisiksi Hänelle älkääkä seuratko saatanan jälkiä. Hän on totisesti teidän ilmeinen vihollisenne.');
INSERT INTO "finnish" VALUES(2,209,'2:209','Ja jos te rupeatte luopumaan uskosta, senjälkeen kuin olette saaneet selvät todistukset, niin tietäkää, että Jumala on mahtava ja viisas.');
INSERT INTO "finnish" VALUES(2,210,'2:210','Eivätkö he (epäuskoiset) juuri sitä odota, että Jumala tulisi heidän luokseen pilvien varjossa enkeleineen? Silloinhan asia jo olisi ratkaistu. Ei, kaikki asiat palaavat Jumalan tuomittaviksi.');
INSERT INTO "finnish" VALUES(2,211,'2:211','Kysy Israelin lapsilta, kuinka monta selvää tunnusmerkkiä olemme antanut heille. Mutta jos joku vaihtaa pois Jumalan armon, senjälkeen kuin se on tullut hänen osakseen, on Jumala totisesti ankara rankaisemaan.');
INSERT INTO "finnish" VALUES(2,212,'2:212','Maallista elämää ihailevat ne, jotka ovat uskottomia ja pilkkaavat uskovaisia. Mutta ne, jotka täyttävät velvollisuutensa Jumalaa kohtaan, joutuvat Ylösnousemisen päivänä heidän yläpuolelleen. Jumala antaa runsaasti kenelle tahtoo.');
INSERT INTO "finnish" VALUES(2,213,'2:213','Ihmisten muodostaessa yhden ainoan uskonnollisen yhdyskunnan Jumala lähetti profeettoja ilosanoman tuojiksi ja varoittajiksi ja ilmoitti heidän kauttaan Pyhän kirjan, jonka totuus kykeni ratkaisemaan kaiken, mistä ihmiset kiistelivät. Ja juuri ne, joille Kirja annettiin, riitelivät siitä keskinäisen vihansa vuoksi, vaikka olivat saaneet selvät todistukset. Mutta uskovaisia Jumalan tahto johti siihen totuuteen, josta nuo keskenänsä kiistelivät; sillä Jumala ohjaa kenet tahtoo oikealle tielle.');
INSERT INTO "finnish" VALUES(2,214,'2:214','Vai luuletteko, että saatte astua Paratiisiin, vaikka teille ei ole vielä tapahtunut mitään sellaista kuin niille, jotka ovat nukkuneet pois ennen teitä? Hätä ja vastoinkäymiset kohtasivat heitä; oli kuin maanjäristys olisi viskellyt heitä, niin että Jumalan lähettiläs ja uskovaiset hänen ympärillään huusivat: »Milloin tulee Jumalan apu?» Katso, Jumalan avun täytyy jo olla lähellä.');
INSERT INTO "finnish" VALUES(2,215,'2:215','He kysyvät sinulta, Muhammed, kenelle ja mitä heidän on pois annettava. Sano: »Se, minkä pois lahjoitatte, antakaa vanhemmillenne, läheisille omaisillenne, orvoille, köyhille ja vaeltajille, ja mitä hyvää teette, Jumala tietää sen totisesti.»');
INSERT INTO "finnish" VALUES(2,216,'2:216','Teidät on määrätty sotimaan, vaikka se on teille vastenmielistä. Mutta voi sattua, että teillä on vastenmielisyys jotakin asiaa kohtaan, vaikka se on teille hyödyllinen, ja voipa sattua, että teillä on halu johonkin, vaikka se on vahingollista teille. Jumala tietää, mutta te ette tiedä.');
INSERT INTO "finnish" VALUES(2,217,'2:217','He kysyvät sinulta, Muhammed, onko taisteltava rauhoitetun kuukauden aikana. Sano: »Taisteleminen sen aikana on raskas synti; mutta käännyttäminen Jumalan tieltä, Jumalan kiel~ täminen, Rauhoitetun temppelin häpäiseminen ja kävijöiden ajaminen pois sieltä on vielä pahempaa Jumalan silmissä. Sillä pakanuuteen käännyttäminen on pahempi kuin murha.» He eivät lakkaa sotimasta teitä vastaan, ennenkuin ovat saaneet, mikäli voivat, teidät luopumaan uskonnostanne. Mutta niiden teot, jotka luopuvat uskonnostaan ja kuolevat uskottomuudessaan, tulevat olemaan turhia sekä tässä että tulevassa elämässä, heistä tulee Tulen asukkaita ja he pysyvät siinä iankaikkisesti.');
INSERT INTO "finnish" VALUES(2,218,'2:218','Jotka uskovat ja jotka ovat kotiseutunsa jättäneet vainottuina ja taistelleet Jumalan retkellä, nämä voivat totisesti luottaa Jumalan laupeuteen, sillä Jumala on armollinen ja laupias.');
INSERT INTO "finnish" VALUES(2,219,'2:219','He kysyvät sinulta, onko viini ja uhkapeli sallittua. Sano: »Molempiin sisältyy suuri synti, vaikkakin niistä on joitakin etuja ihmisille ; kuitenkin on synti suurempi kuin hyöty.» Vielä he kysyvät, mitä heidän on lahjoitettava pois. Sano: »Se, mitä teillä on liikaa». Niin selittää Jumala tunnusmerkit teille. jotta osaisitte miettiä');
INSERT INTO "finnish" VALUES(2,220,'2:220','tätä ja tulevaista elämää. He kysyvät sinulta myös, miten on meneteltävä orpojen suhteen. Sano: »Parasta on keventää heidän kohtaloaan». Jos elämänolot yhdistävät teidät heihin, niin ovat he teidän veljiänne. Jumala erottaa sen, joka pahentaa sitä, joka parantaa, ja jos Jumalasi tahtoisi, voisi Hän teidätkin masentaa; Jumala on totisesti mahtava, viisas.');
INSERT INTO "finnish" VALUES(2,221,'2:221','Älkää naiko monijumalaisia naisia, elleivät he käänny uskoon. Uskovainen orjatar on totisesti parempi kuin monijumalainen vapaa nainen, vaikkapa tämä miellyttäisi teitä enemmän. Älkääkä antako tyttäriänne vaimoiksi monijumalaisille, ennenkuin he kääntyvät uskoon. Uskovainen orja on totisesti parempi kuin vapaa monijumalainen, vaikkapa jälkimmäinen miellyttäisi teitä enemmän. Nämä johdattavat teidät Tuleen, mutta Jumala kutsuu Paratiisiin ja armahdukseen ja selittää tunnusmerkkinsä ihmisille, jotta he pitäisivät ne mielessään.');
INSERT INTO "finnish" VALUES(2,222,'2:222','He kysyvät sinulta, Muhammed, miten on meneteltävä naisen kuukautispuhdistuksen aikana. Sano: »Se on heidän pahoinvointinsa; karttakaa siis naisia puhdistuksen aikana älkääkä lähestykö heitä, ennenkuin ovat puhdistuneet. Mutta kun he ovat puhdistuneet, käykää heidän luonaan, kuten Jumala on määrännyt. Jumala rakastaa totisesti Hänen puoleensa kääntyneitä ja rakastaa niitä, jotka pitävät itseään puhtaina.»');
INSERT INTO "finnish" VALUES(2,223,'2:223','Vaimonne ovat teidän vaimonne, käykää vainiollanne mielenne mukaan, mutta suorittakaa myös ensin jotakin hyvää sielujenne puolesta. Pelätkää Jumalaa ja tietäkää, että tulette kohtaamaan hänet. Julista, Muhammed, uskovaisille ilosanomaa!');
INSERT INTO "finnish" VALUES(2,224,'2:224','Älkää siten vannoko Jumalan kautta, että se estäisi teitä olemasta hurskaita, pelkäämästä Jumalaa ja tekemästä oikeutta ihmisille: onhan Jumala kuuleva ja tietävä.');
INSERT INTO "finnish" VALUES(2,225,'2:225','Jumala ei rankaise teitä ajattelemattomasta sanasta valoissanne , vaan hän rankaisee teitä siitä, minkä teidän sydämenne on omaksunut, sillä Jumala on sääliväinen ja lempeä.');
INSERT INTO "finnish" VALUES(2,226,'2:226','Ne, jotka vannovat pysyvänsä erillään vaimoistaan, odottakoot neljän kuukauden ajan; jos he muuttavat mielensä, niin on Jumala totisesti sääliväinen ja laupias.');
INSERT INTO "finnish" VALUES(2,227,'2:227','Mutta jos he pysyvät eropäätöksessään, niin on Jumala totisesti kuuleva, tietävä.');
INSERT INTO "finnish" VALUES(2,228,'2:228','Hylättyjen vaimojen tulee odottaa kolme puhdistusaikaa, eikä heidän ole sallittu salata, mitä Jumala ehkä on luonut heidän kohdussaan, jos he uskovat Jumalaan ja Viimeiseen päivään, ja heidän miestensä tulisi mieluimmin ottaa heidät takaisin tänä aikana, jos he toivovat sovintoa. Naisilla on sama oikeus miehiin nähden kuin miehillä heihin, kuten on kohtuullista; kuitenkin ovat miehet naisiin verrattuina korkeammassa asemassa. Niin, Jumala on mahtava ja viisas.');
INSERT INTO "finnish" VALUES(2,229,'2:229','Tällainen koe-ero on sallittu kaksi kertaa; sitten joko pitäkää vaimo luonanne kunniallisesti tai antakaa hänen mennä runsain lahjoin. Teidän ei ole sallittu pidättää mitään siitä, mitä olette heille antaneet, paitsi jos molemmat pelkäävät, etteivät he (jatkuvassa yhdyselämässä) voi noudattaa Jumalan säädöksiä (vastenmielisyyden ja riitojen tähden). jos te todellakin pelkäätte, että kumpikaan ei voi (jatkuvassa yhdyselämässä) noudattaa Jumalan säädöksiä, niin ei ole synniksi kummallekaan, vaikka vaimo ostaa itsensä vapaaksi. Nämä ovat Jumalan säädöksiä, siksi älkää rikkoko niitä, sillä ne, jotka rikkovat Jumalan säädöksiä vastaan, ovat väärintekijöitä.');
INSERT INTO "finnish" VALUES(2,230,'2:230','Jos joku siis erottaa vaimonsa (kolmannen kerran), ei hänen ole sallittu jälkeenpäin ottaa vaimoa takaisin, ennenkuin tämä on ollut naimisissa toisen miehen kanssa; mutta jos tämä hylkää vaimon, niin eivät entiset aviopuolisot tee syntiä, jos he palaavat toistensa luo, mikäli uskovat voivansa noudattaa Jumalan säädöksiä. Nämä ovat Jumalan säädökset; Hän selittää ne ihmisille, joilla on ymmärrys.');
INSERT INTO "finnish" VALUES(2,231,'2:231','Ja kun olette erottaneet vaimon ja tämä on odottanut määrätyn aikansa, niin joko pitäkää hänet luonanne kunniallisesti tai antakaa hänen mennä sovinnossa. Älkää pidättäkö häntä väkivalloin eikä tehden vääryyttä, sillä niin menettelevä tekee väärin omaa sieluaan kohtaan. Älkää pilkatko Jumalan tunnusmerkkejä, vaan muistakaa osaksenne tullutta Jumalan armoa, älkääkä unohtako Pyhää kirjaa ja viisautta, jonka Hän on antanut teille varoittaakseen teitä. Pelätkää Jumalaa ja tietäkää, että Jumala on kaikesta selvillä.');
INSERT INTO "finnish" VALUES(2,232,'2:232','Ja kun olette eronneet vaimoista ja nämä ovat odottaneet määrätyn aikansa, älkää estäkö heitä menemästä naimisiin uusien miestensä kanssa, jos he keskenään ovat siitä sopineet, kuten kohtuullista on. Tämä on varoitus sille teistä, joka uskoo Jumalaan ja Viimeiseen päivään. Tämä on sopivinta ja puhtainta teille itsellenne. Sen Jumala tietää; te ette tiedä.');
INSERT INTO "finnish" VALUES(2,233,'2:233','Äidit imettäkööt lapsiaan kahden täyden vuoden ajan, jos isä tahtoo imettämiskauden tulevan umpeen. Isän velvollisuus on ruokkia ja pukea heidät, kuten kohtuullista on: ketään ei ole velvoitettava yli hänen mahdollisuuksiensa, äidin ei tarvitse kärsiä mitään pakkoa lapsensa tähden, eikä isän lapsensa tähden, ja sama koskee isän perijää. Mutta jos he molemminpuolisen sopimuksen ja neuvottelujen jälkeen sopivat lapsen vieroittamisesta, niin siinä ei kumpikaan tee mitään syntiä. Ja jos haluatte antaa vieraan imettää lapsianne, niin ette syntiä tee, kun maksatte mitä teidän kohtuuden mukaisesti on suoritettava; pelätkää Jumalaa ja tietäkää, että Jumala näkee teidän tekonne.');
INSERT INTO "finnish" VALUES(2,234,'2:234','Jos jotkut keskuudestanne kuolevat ja jättävät vaimoja, niin odottakoot nämä neljä kuukautta ja kymmenen päivää omissa oloissaan; mutta kun ovat odottaneet määrätyn aikansa, niin ei teille lueta synniksi sitä, miten he säädyllisesti itseensä nähden menettelevät. Jumala on tietoinen teidän teoistanne.');
INSERT INTO "finnish" VALUES(2,235,'2:235','Eikä teille lueta synniksi, jos näille naisille naimatarjouksenne esitätte julkisesti tai sitä mielessänne haudotte. Jumala tietää, että te heitä ajattelette, mutta älkää kuitenkaan heidän kanssaan salassa jutelko, ellette lausu tavanmukaista naimatarjousta. Älkää solmiko aviollista liittoa, ennenkuin määrätty aika on umpeen kulunut, ja tietäkää, että Jumala näkee, mitä teillä on mielessä. Sentähden kavahtakaa Häntä ja tietäkää, että Jumala on armahtavainen ja lempeä.');
INSERT INTO "finnish" VALUES(2,236,'2:236','Ette tee mitään syntiä, jos hylkäätte vaimon, johon ette ole koskeneet tai jolle ette ole suorittaneet huomenlahjaksi määrättyä osaa. Mutta huolehtikaa sellaisten tarpeista (rikas varojensa mukaan ja köyhä varojensa mukaan), niinkuin on katsottava kohtuulliseksi niille, jotka hyvää tekevät.');
INSERT INTO "finnish" VALUES(2,237,'2:237','Jos te eroatte heistä, ennenkuin olette heihin koskeneet, mutta olette suorittaneet määrätyn osuuden heille, niin kuuluu heille puolet siitä, mitä olette suorittaneet, jolleivät he itse tai mies, jonka toimesta avioliitto solmittiin, tee jotain myönnytystä. Tekemällä myönnytyksiä lähestytte totista jumalisuutta; älkääkä unohtako olla jalomielisiä toisianne kohtaan. Jumala näkee, mitä te teette.');
INSERT INTO "finnish" VALUES(2,238,'2:238','Pitäkää vaari rukouksistanne, myös keskimmäisestä, ja olkaa hartaina Jumalan edessä.');
INSERT INTO "finnish" VALUES(2,239,'2:239','Jos teillä on vaaran pelko, niin rukoilkaa vaikkapa käydessänne tai ratsun selässä, mutta kun olette turvassa, niin rukoilkaa Jumalaa siten, kuin Hän on opettanut teille sen, mitä ette ennen tietäneet.');
INSERT INTO "finnish" VALUES(2,240,'2:240','Ne teistä, jotka ovat kuolemaisillaan ja joilta jää vaimoja, testamentatkoot vaimoineen toimeentulon yhdeksi vuodeksi, jottei heidän tarvitse lähteä muualle; mutta jos he itse lähtevät, niin ei teille lueta synniksi sitä, miten he asianmukaisesti itseensä nähden menettelevät; onhan Jumala mahtava ja viisas.');
INSERT INTO "finnish" VALUES(2,241,'2:241','Erotetuille vaimoille samaten kuuluu riittävä huolto, koska tämä on jumalaapelkääväisten velvollisuus.');
INSERT INTO "finnish" VALUES(2,242,'2:242','Niin selittää Jumala tunnusmerkkejään teille, jotta toimisitte ymmärtäväisesti.');
INSERT INTO "finnish" VALUES(2,243,'2:243','Etkö pidä mielessäsi niitä, jotka muinoin tuhatlukuisina lähtivät kodeistaan peläten kuolemaa? Jumala sanoi heille: »Kuolkaa!» ja herätti heidät sitten eloon taas. Jumala totisesti antaa runsaita armolahjoja ihmisille, mutta useimmat ihmiset ovat kiittämättömiä.');
INSERT INTO "finnish" VALUES(2,244,'2:244','Taistelkaa Jumalan retkellä ja tietäkää, että Jumala on kuuleva, tietävä.');
INSERT INTO "finnish" VALUES(2,245,'2:245','Kuka antaa Jumalalle jalon lainan, jotta Hän saisi tilaisuuden palauttaa sen moninkertaisella? Sillä Jumalasta riippuu, pidättääkö Hän vai avaa runsautensa, ja Hänen luokseen te tulette palajamaan.');
INSERT INTO "finnish" VALUES(2,246,'2:246','Muista israelilaisten vanhimmat Mooseksen aikojen jälkeen, jotka sanoivat eräälle keskuudessaan olevalle profeetalle: »Aseta meille kuningas, niin taistelemme Jumalan asian puolesta». Hän sanoi: »Mutta entä jos jäättekin pois taistelusta, jos taistelu teille määrätään?» He sanoivat: »Miksemme taistelisi Jumalan asian puolesta, kun olemme joka tapauksessa karkoitetut kodeistamme lapsinemme?» Mutta kun heidät määrättiin taistelemaan, kääntyivät he pois, paitsi muutamat harvat. Mutta Jumala tuntee väärintekijät.');
INSERT INTO "finnish" VALUES(2,247,'2:247','Ja heidän profeettansa sanoi heille: »Katso, Jumala on asettanut Saulin teidän kuninkaaksenne». He kysyivät: »Kuinka hän voi tulla kuninkaaksi meitä hallitsemaan, kun meillä on suuremmat oikeudet kuninkaan arvoon kuin hänellä, eikä hänellä ole edes rikkauttakaan?» Hän vastasi: »Katso, Jumala on valinnut hänet teidän hallitsijaksenne ja runsaasti varustanut hänet tiedolla ja ruumiinvoimilla, sillä Jumala antaa hallitusvallan kenelle tahtoo». Niin, Jumala sulkee kaikki olemukseensa ja tietoonsa.');
INSERT INTO "finnish" VALUES(2,248,'2:248','Ja heidän profeettansa sanoi heille: »Katso, hänen kuninkuutensa tunnusmerkki on se, että liitonarkki, enkeleiden kantamana, on tuleva luoksenne sisältäen Herranne sekiinan ja Mooseksen sekä Aaronin suvun jälkeensä jättämiä pyhiä esineitä». Tässä on tunnusmerkki teille, jos totisesti olette uskovaisia.');
INSERT INTO "finnish" VALUES(2,249,'2:249','Kun nyt Saul lähti liikkeelle sotajoukkojensa kera, sanoi hän: »Jumala on totisesti koetteleva teitä joelle tultuanne; se, joka juo siitä, ei kuulu minun miehiini (niihin kuuluu se, joka ei maista siitä yhtään) - paitsi se, joka ainoastaan ottaa kulauksen kämmenellään.» ja niin joivat he siitä paitsi muutamat harvat. Ja kun hän ja hänen uskonveljensä olivat kulkeneet puron yli, sanoivat he: »Tänään meillä ei ole voimia Goljatia ja hänen joukkojaan vastaan», mutta ne, jotka uskoivat kohtaavansa Jumalan, huudahtivat: »Kuinka usein onkaan pieni joukko Jumalan avulla voittanut suuren sotaväen!» Onhan Jumala kestävien kanssa.');
INSERT INTO "finnish" VALUES(2,250,'2:250','Kun he hyökkäsivät Goljatia ja hänen sotajoukkojaan vastaan, he huusivat: »Herra, suo meille lujuutta, vahvista askeleemme ja auta meidät saamaan voitto uskottomasta kansasta!»');
INSERT INTO "finnish" VALUES(2,251,'2:251','Ja Jumalan sallimuksesta he ajoivat heidät pakoon; David surmasi Goljatin, ja Jumala antoi hänelle kuninkuuden ja viisauden ja opetti häntä tahtonsa mukaan. Jollei Jumala masentaisi ihmisiä toistensa kautta, niin olisi maa sekasorron tilassa, mutta Jumala on koko luomakunnan Hyväntekijä.');
INSERT INTO "finnish" VALUES(2,252,'2:252','Nämä ovat Jumalan tunnusmerkkejä, jotka Me julistamme sinulle, Muhammed, totuudessa, sillä sinä kuulut totisesti Meidän lähettiläittemme joukkoon.');
INSERT INTO "finnish" VALUES(2,253,'2:253','Näistä lähettiläistä olemme eräille antanut korkeamman aseman kuin toisille. Ylennettyjen joukossa oli niitä, joiden kanssa Jumala on puhunut, ja toisia, jotka Hän on korkealle korottanut. Olemme antanut Jeesukselle, Marian pojalle, selvät todistukset ja tukenut häntä Pyhyyden hengellä; ja jos Jumala olisi tahtonut, niin ne jotka seurasivat heitä, eivät olisi taistelleet toisiaan vastaan, senjälkeen kuin selvät todistukset olivat tulleet heidän osakseen. Mutta he olivat eripuraisia: heidän keskuudessaan oli uskovaisia ja uskottomia,- ja jos Jumala olisi tahtonut, eivät he olisi taistelleet toisiansa vastaan, mutta Jumala tekee, mitä haluaa.');
INSERT INTO "finnish" VALUES(2,254,'2:254','Te, jotka uskotte, uhratkaa siitä, mitä olemme teille suonut, ennenkuin tulee päivä, jolloin ei kaupanteko, ystävyys eikä välitys ole mahdollinen. Uskottomat ovat väärintekijöitä.');
INSERT INTO "finnish" VALUES(2,255,'2:255','Ei ole muuta jumalaa kuin Jumala, elävä, iankaikkinen. Häntä ei saa valtaansa uneliaisuus eikä uni; Hänelle kuuluu kaikki, mikä on taivaassa ja maan päällä. Kuka on se, joka astuisi välittäjäksi Hänen edessään Hänen sallimattaan? Hän tietää, mitä heille on vastedes tuleva ja mitä heillä on ollut menneisyydessä, mutta Hänen viisaudestaan he eivät käsitä muuta kuin minkä Hän tahtoo. Hänen valtaistuimensa käsittää taivaat ja maan, eikä niiden voimassapysyttäminen ole Hänelle taakka; niin, Hän on ylhäinen, valtava.');
INSERT INTO "finnish" VALUES(2,256,'2:256','Uskonnossa ei ole mitään pakkoa, oikea tie on selvästi erotettu harhateistä, ja se, joka hylkää saatanan ja uskoo Jumalaan, hän pitää kiinni mitä lujimmasta kädensijasta, joka ei murru. Jumalahan on kuuleva, tietävä.');
INSERT INTO "finnish" VALUES(2,257,'2:257','Jumala on niiden suojelija, jotka uskovat; Hän johtaa heidät pimeydestä valoon. Mutta uskottomien suojelijoita ovat pahat henget; he vetävät heidät valosta pimeyteen. Heistä tulee Tulen asukkaita ja he pysyvät siinä iankaikkisesti.');
INSERT INTO "finnish" VALUES(2,258,'2:258','Muista hänet, joka kiisteli Aabrahamin kanssa hänen Herrastaan siksi, että Jumala oli antanut hänelle kuningaskunnan. Aabraham sanoi: »Herrani antaa elämän ja kuoleman.» Hän vastasi: »Minäkin annan elämän ja kuoleman.» Silloin Aabraham sanoi: »Katso, Jumala antaa auringon nousta idästä; anna sinä sen nousta lännestä!» ja jumalaton jäi sanattomaksi. Niin, Hän ei johda jumalattomia ihmisiä.');
INSERT INTO "finnish" VALUES(2,259,'2:259','Tai muistele ihmistä, joka kulki erään raunioiksi sortuneen kaupungin ohi. Hän sanoi: »Kuinka voi Jumala antaa elämän tälle paikalle sen perikadon jälkeen?» Mutta Jumala antoi hänen kuolla sadaksi vuodeksi ja herätti hänet sitten eloon ja sanoi: »Kuinka kauan olet viipynyt täällä?» Hän vastasi: »Olen ehkä viipynyt päivän tai osan päivästä.» Silloin sanoi Jumala: »Ei, olet viipynyt sata vuotta; katso ruokaasi ja juomaasi. Se ei ole pilaantunut, ja katso aasiasi. Tämä on tapahtunut, jotta Me tekisimme sinut tunnusmerkiksi ihmisille. Katso, kuinka Me kokoamme luut ja sitten peitämme ne lihalla.» ja kun tämä selvisi hänelle, sanoi hän: »Minä tiedän, että Jumala on kaikkivaltias.»');
INSERT INTO "finnish" VALUES(2,260,'2:260','Entä kun Aabraham sanoi: »Herra, näytä minulle, kuinka teet kuolleet eläviksi!» Hän kysyi: »Etkö sinä sitten usko siihen?» Hän vastasi: »Kyllä, mutta jotta sydämeni rauhoittuisi.» Silloin Hän sanoi: »Ota neljä lintua ja leikkaa ne palasiksi, aseta sitten kappale niistä joka vuorelle ja kutsu sitten niitä, niin ne kiiruhtavat luoksesi nopeasti; ja tiedä, että Jumala on mahtava, viisas!»');
INSERT INTO "finnish" VALUES(2,261,'2:261','Niitten laita, jotka uhraavat omaisuutensa Jumalan asian hyväksi, on sama kuin viljanjyvän, joka kasvattaa seitsemän tähkää ja jokaiseen tähkään sata jyvää, sillä Jumala antaa kenelle tahtoo kaksinkertaisesti; niin, Jumala sulkee itseensä kaiken ja tietää kaikki.');
INSERT INTO "finnish" VALUES(2,262,'2:262','Ne, jotka uhraavat omaisuutensa Jumalan asialle eivätkä jälkeenpäin kadu eivätkä moiti lahjoitustaan, saavat palkkansa Herraltaan: ei heitä mikään pelko vaivaa, eikä heidän tarvitse murehtia.');
INSERT INTO "finnish" VALUES(2,263,'2:263','Sovinnollinen puhe ja anteeksianto on parempi kuin almu, jota seuraa solvaus; niin, Jumala on ylenpalttisen rikas ja lempeä.');
INSERT INTO "finnish" VALUES(2,264,'2:264','Te, jotka uskotte, älkää tehkö tyhjäksi almujanne jälkimoitteilla ja solvauksilla niinkuin se, joka luovuttaa omaisuutensa näyttääkseen ihmisten silmissä hurskaalta, mutta ei usko Jumalaan eikä Viimeiseen päivään; hänen laitansa on sama kuin kivipaaden, jonka päällä on hitunen multaa ja johon sadekuuro lankeaa jättäen sen aivan paljaaksi. Lopulta he eivät saa omakseen mitään tuloistaan. Jumala ei johda uskottomia ihmisiä.');
INSERT INTO "finnish" VALUES(2,265,'2:265','Ne taasen, jotka uhraavat varojaan ikävöiden Jumalan mielisuosiota ja vahvistaakseen sieluaan, ovat kukkulalla sijait~ sevan puutarhan kaltaisia, johon sadekuuro lankeaa, tuottaen kaksinkertaisen sadon. Ja jollei sadekuuro lankea, niin laskeutuu sille ainakin kaste. Jumala näkee, mitä te teette.');
INSERT INTO "finnish" VALUES(2,266,'2:266','Toivoisikohan kukaan teistä, että hänellä olisi puutarha, jossa on palmuja ja viiniköynnöksiä sekä kaikenlaisia hedelmiä ja jossa purot solisevat, ja että vanhuus yllättäisi hänet hänen pienten lastensa ollessa vielä turvattomia ja salamoiva hirmumyrsky hävittäisi ja polttaisi poroksi sen (puutarhan)? Näin Jumala selittää teille tunnusmerkkinsä, jotta miettisitte.');
INSERT INTO "finnish" VALUES(2,267,'2:267','Te, jotka uskotte, uhratkaa parasta, mitä olette hankkineet, ja sitä, mitä Me olemme kasvattanut teille maasta, älkääkä etsikö huonointa uhritarkoituksiinne, koska ette itsekään ota vastaan sellaista muuten kuin halveksien. Tietäkää, että Jumalan on kaikki, rikkaus ja kunnia.');
INSERT INTO "finnish" VALUES(2,268,'2:268','Saatana uhkaa teitä köyhyydellä ja houkuttelee teitä halpamaisuuksiin, mutta Jumala lupaa teille anteeksiantonsa ja runsautensa. Jumala sulkee olemukseensa kaiken ja tietää kaiken.');
INSERT INTO "finnish" VALUES(2,269,'2:269','Hän antaa viisautta kenelle tahtoo, ja kenelle viisaus on annettu, hän on saanut suuren aarteen, mutta vain ymmärtäväiset ovat tästä täysin selvillä.');
INSERT INTO "finnish" VALUES(2,270,'2:270','ja mitä almuja annattekin tai uhrilupauksia teette, tietää Jumala sen totisesti, mutta väärin-tekijät jäävät ilman auttajaa.');
INSERT INTO "finnish" VALUES(2,271,'2:271','Jos te annatte almunne julkisesti, on se hyvä asia, mutta jos te annatte ne salassa köyhille, on se vielä parempi itsellenne ja sovittaa osan pahoista teoistanne. Jumala tietää teidän tekonne.');
INSERT INTO "finnish" VALUES(2,272,'2:272','Sinun velvollisuutesi, Muhammed, ei ole ohjata heitä (uskottomia), Jumala ohjaa ketä haluaa. Mitä hyvää annattekin pois, on se omaksi parhaaksenne, kun ette sillä muuhun pyri kuin Jumalan kasvojen eteen; ja mitä hyvää uhraattekin, tulee se teille täysin korvatuksi, teidän kärsimättä mitään vääryyttä.');
INSERT INTO "finnish" VALUES(2,273,'2:273','(Uhrilahjat tulevat) köyhien hyväksi, joita osanotto Jumalan (sota)retkeen estää vaeltamasta ympäri maata (liikeasioissa) ja joita tietämättömät pitävät varakkaina, kun he eivät ole vaateliaita. Sinä erotat heidät tuntomerkeistään: eivät he anoessaan ihmisiltä käy tungettelevaisiksi. Mitä hyvää uhraattekin, tietää Jumala sen totisesti.');
INSERT INTO "finnish" VALUES(2,274,'2:274','Ne, jotka uhraavat varojaan sekä päivin että öin, salaisesti ja julkisesti, löytävät palkkansa Herraltaan, ei heitä pelko vaivaa, eikä heidän tarvitse murehtia.');
INSERT INTO "finnish" VALUES(2,275,'2:275','Mutta ne, jotka ahnehtivat voittoa koronkiskomisesta, kokevat samanlaisen ylösnousemuksen kuin hän, jonka Saatana on villinnyt tartunnallaan. Tämä sentähden, että he sanovat: »Kauppavoittohan on samaa kuin koronkiskominen.» Mutta Jumala on sallinut kaupan ja kieltänyt koronkiskomisen. Sille, joka saa ojennuksen Herraltaan ja luopuu paheesta, suodaan anteeksi se, mikä jo on mennyttä, ja hänen asiansa ovat Jumalan käsissä. Mutta ne, jotka taas entiseen ryhtyvät, ovat Tulen omia, ja he pysyvät siinä iankaikkisesti.');
INSERT INTO "finnish" VALUES(2,276,'2:276','Jumala tekee koronkiskomisen tyhjäksi, mutta almujen hedelmät Hän tekee moninkertaisiksi. Hän ei rakasta jumalatonta eikä rikollista.');
INSERT INTO "finnish" VALUES(2,277,'2:277','Katso, ne jotka uskovat ja tekevät hyviä töitä, viettävät rukouselämää ja suorittavat köyhille osuutensa, löytävät totisesti palkkansa Herraltaan; ei mikään pelko vaivaa heitä, eikä heidän tarvitse murehtia.');
INSERT INTO "finnish" VALUES(2,278,'2:278','Te uskovaiset, pelätkää Jumalaa ja antakaa anteeksi vielä perimätön osa koroista velallisillenne, jos olette oikeita uskovaisia.');
INSERT INTO "finnish" VALUES(2,279,'2:279','Jollette niin tee, niin tietäkää Jumalan ja Hänen lähettiläänsä olevan sodassa teitä vastaan. Mutta jos teette parannuksen, saatte pitää pääoman. Älkää tehkö vääryyttä, niin ei sitä teillekään tehdä.');
INSERT INTO "finnish" VALUES(2,280,'2:280','Jos joku (velallinen) on ahdingossa, saakoon lykkäystä, kunnes hänen tilansa paranee, mutta parempi teille olisi antaa velka anteeksi; jospa vain sen tajuaisitte.');
INSERT INTO "finnish" VALUES(2,281,'2:281','Ja varokaa sitä päivää, jolloin teidän on palattava Jumalan luo. Silloin on jokainen sielu saapa täyden mitan mukaan, mitä on ansainnut, eikä ketään väärin tuomita.');
INSERT INTO "finnish" VALUES(2,282,'2:282','Te uskovaiset, kun teette maksusitoumuksen määrätyksiajaksi, niin tehkää se kirjallisesti. Kirjuri merkitköön puolueettomasti muistiin keskinäisen sopimuksenne, älköönkä kukaan kirjuri kieltäytykö kirjoittamasta sen mukaan, kuin Jumala on opettanut häntä. Siten siis on hänen kirjoitettava, ja se, joka velaksi ottaa, sanelkoon Herraansa Jumalaa peläten, älköönkä vähintäkään pois jättäkö. Jos kuitenkin velantekijä on älyltään tai vastuultaan vajavainen tai ei itse kykene sanelemaan, silloin hänen etujensa puoltaja sanelkoon sen oikeudenmukaisesti. Kutsukaa tähän tilaisuuteen kaksi mieleistänne miestä todistajiksi. Jollei satu olemaan kahta miestä, todistakoon mies ja kaksi naista niiden joukosta, jotka hyväksytte todistajiksi, jotta jos yhden muisti pettäisi, toinen kuitenkin muistaisi oikein. Ja kun heidät todistajiksi kutsutaan, älkööt he kieltäytykö. Älkää jättäkö kirjoittamatta velkasopimusta, olkoon se pieni tai suuri, ja merkitkää sen eräytymisaika. Tämä on oikeudenmukaisinta Jumalan edessä, varmimmin todistettavissa ja paras tapa välttää keskinäisiä epäluuloja, paitsi milloin kyseessä on liikeasia, jossa tavara siirtyy kädestä käteen. Tällöin ei lueta teille syyksi, jos jätätte kirjoittamatta sen muistiin. Todistajia hankkikaa kuitenkin myydessänne toinen toisellenne, eikä hankaluutta saa tuottaa kirjurille enempää kuin todistajillekaan. Jos teette jotain sellaista, on se totisesti jumalatonta teidän puoleltanne. Pelätkää Jumalaa! Jumala opettaa teitä ja Jumala tietää kaikki.');
INSERT INTO "finnish" VALUES(2,283,'2:283','Mutta jos olette matkoilla ettekä voi löytää kirjuria, voitte vastaanottaa pantteja. Ja jos toinen teistä uskoo toiselle jotakin, silloin myös se, jolle on uskottu, täyttäköön sen, johon on sitoutunut, ja pelätköön hän Jumalaa, Herraansa. Älkää salatko todistusta! Jos joku salaa sen, on hänen sydämensä totisesti turmeltunut. Jumala tietää mitä te teette.');
INSERT INTO "finnish" VALUES(2,284,'2:284','Jumalalle kuuluu kaikki taivaassa ja maan päällä, ja ilmaisetteko vai salaatte, mitä teillä on mielessä, vaatii Jumala teidät tilille siitä. Hän antaa anteeksi kenelle tahtoo ja rankaisee ketä tahtoo, sillä Jumala on kaikkivaltias.');
INSERT INTO "finnish" VALUES(2,285,'2:285','Profeetta uskoo siihen, minkä hänen Herransa on hänelle ilmoittanut, samoin uskovaiset; jokainen heistä uskoo Jumalaan, Hänen enkeleihinsä ja Hänen lähettiläisiinsä. Me emme tee erotusta Hänen profeettainsa välillä, ja he sanovat: »Me kuulemme ja tottelemme. Sinulla on (meille) anteeksiantamus, oi Herra! Niin, Sinussa on määränpää.»');
INSERT INTO "finnish" VALUES(2,286,'2:286','Jumala ei ketään rasita yli voimien; mitä kukin on aikaansaanut, on hänen edukseen, ja häntä vastaan nousee vain se, minkä hän itse on aiheuttanut. Herramme, älä rankaise meitä, jos hairahdumme tai rikomme! Herra, älä pane kannettavaksemme sellaista taakkaa, kuin annoit niiden osaksi, jotka ovat ennen meitä eläneet! Herra, älä pakota meitä kantamaan sitä, mihin eivät voimamme riitä! Ole pitkämielinen, anna anteeksi ja armahda meitä! Olet suojelijamme; auta meitä uskottomia vastaan!');
INSERT INTO "finnish" VALUES(3,1,'3:1','Alif. Laam. Miim');
INSERT INTO "finnish" VALUES(3,2,'3:2','Ei ole muuta jumalaa kuin Jumala, iäti elävä, kaikki ylläpitävä.');
INSERT INTO "finnish" VALUES(3,3,'3:3','Hän on ylhäältä antanut sinulle, Muhammed, Pyhä kirjan, joka on totuus, vahvistaakseen mitä on ollut ennen sitä; aikaisemmin Hän on lähettänyt Lain ja Evankeliumin opastukseksi ihmisille ja antanut Johdatuksen,jotta he hylkäisivät pahan ja valitsisivat hyvän. Niitä, jotka eivät usko Jumalan tunnusmerkkeihin, odottaa totisesti ankara rangaistus, Jumala on mahtava ja kykenee kostamaan.');
INSERT INTO "finnish" VALUES(3,4,'3:4','Jumalalta ei ole mitään salattua maan päällä eikä taivaassa.');
INSERT INTO "finnish" VALUES(3,5,'3:5','Hän muovaa teidät äidin kohdussa tahtonsa mukaan. Ei ole muuta jumalaa kuin Hän, kaikkivaltias, viisas.');
INSERT INTO "finnish" VALUES(3,6,'3:6','Hän on sinulle, Muhammed, antanut Pyhän kirjan; se sisältää selviä ilmoituksia, joilla on vain yksi merkitys - ne muodostavat Kirjan perustan - ja toisia, jotka ovat vertauskuvallisia. Ne, jotka sydämessänsä pyrkivät harhaan, kiinnittävät huomionsa kaikkeen vertauskuvalliseen aiheuttaakseen erimielisyyksiä ja sepittääkseen selityksiä. Mutta selityksen tuntee ainoastaan Jumala. Ja ne, joilla on varma tieto, sanovat: »Me uskomme siihen,kaikki on tullut Herraltamme», sillä todella sen tajuavat vain ymmärtäväiset.');
INSERT INTO "finnish" VALUES(3,7,'3:7','Herra, älä salli sydämemme eksyä totuudesta, senjälkeen kuin olet antanut meille Opastuksesi, vaan suo meille laupeutesi! Sinä yksin olet totisesti kaiken Antaja.');
INSERT INTO "finnish" VALUES(3,8,'3:8','Sinä, Herramme, olet totisesti kokoava ihmiset päivänä, josta ei ole epäilystäkään; katso, Jumala ei petä lupaustansa.');
INSERT INTO "finnish" VALUES(3,9,'3:9','(Sinä päivänä) eivät hyödytä vähääkään uskottomia heidän rikkautensa eivätkä heidän lapsensa Jumalan edessä; he joutuvat Tulen omiksi.');
INSERT INTO "finnish" VALUES(3,10,'3:10','Niinpä oli faraon kansankin laita ja niiden, jotka ennen heitä pitivät Meidän tunnusmerkkejämme vaiheena. Jumala tuhosi heidät heidän syntiensä tähden. Jumala on ankara rangaistessaan.');
INSERT INTO "finnish" VALUES(3,11,'3:11','Sano niille, jotka ovat uskottomia: »Te joudutte tappiolle ja teidät karkoitetaan Helvettiin». Mikä surkea olinpaikka!');
INSERT INTO "finnish" VALUES(3,12,'3:12','Olihan teille jo tunnusmerkkinä kaksi sotajoukkoa, jotka kohtasivat toisensa , toinen taisteli Jumalan asian puolesta, toinen oli uskoton. Toiset arvioivat silmämäärin vihollisensa kaksi kertaa lukuisammiksi, kuin he olivat, sillä Jumala tukee voimallaan ketä tahtoo. Tässä on totisesti esimerkki niille, joilla on silmät nähdä.');
INSERT INTO "finnish" VALUES(3,13,'3:13','Ihmiset etsivät nautintoa naisista, lapsista, kootuista kulta ja hopea-aarteista, parhaina pidetyistä hevosista, karjasta ja pelloista; nämä ovat vain maallisen elämän nautintoja, mutta Jumalan luona tulee olemaan ihanin asunto.');
INSERT INTO "finnish" VALUES(3,14,'3:14','Sano: »Onko minun ilmoitettava teille jotakin parempaa kuin tämä?» Ne jotka pelkäävät Jumalaa, löytävät Herransa luona ikiviihtyisät puutarhat, joissa virrat vyöryvät, heitä odot~ tavat siellä puhtaat puolisot ja Jumalan mielisuosio. Jumala näkee palvelijansa,');
INSERT INTO "finnish" VALUES(3,15,'3:15','jotka sanovat: » Herra, me uskomme totisesti; anna anteeksi meille syntimme ja varjele meitä Tulen tuskilta», .');
INSERT INTO "finnish" VALUES(3,16,'3:16','jotka ovat kärsivällisiä, totuudellisia, hurskaita, vieraanvaraisia, jotka aamuhämärissä rukoilevat syntejään anteeksi.');
INSERT INTO "finnish" VALUES(3,17,'3:17','Jumala todistaa, ettei ole muuta jumalaa kuin Hän. Samoin enkelitkin ja ihmiset, joilla on tieto ja jotka pysyvät totuudessa, todistavat: »Ei ole muuta jumalaa kuin Hän, mahtava, viisas».');
INSERT INTO "finnish" VALUES(3,18,'3:18','Oikea uskonto Jumalan edessä on Islaam. Ne, jotka ovat saaneet Pyhän kirjan, eivät kiistelleet aikaisemmin kuin vasta sen jälkeen, kun heidän osakseen oli tullut tieto; silloin he alkoivat kiistellä keskenään kadehtien toisiaan. Kuka ikinä kieltää Jumalan tunnusmerkit, kokee, miten nopea Jumala on vaatiessaan tilille.');
INSERT INTO "finnish" VALUES(3,19,'3:19','ja jos he kiistelevät kanssasi, niin sano: »Minä olen kokonaan alistunut Jumalan tahtoon, samoin nekin, jotka seuraavat minua». ja sano niille, jotka ovat saaneet Pyhän kirjan, ja tietämättömille :»Alistutteko Jumalan tahtoon (liityttekö Islaamiin)?» ja jos he alistuvat, ovat he oikealla tiellä, mutta jos he kääntävät selkänsä, on sinun velvollisuutesi ainoastaan Jumalan ilmoituksen julistaminen; Jumala näkee palvelijansa.');
INSERT INTO "finnish" VALUES(3,20,'3:20','Niille, jotka eivät usko Jumalan tunnusmerkkeihin ja jotka ilman syytä surmaavat profeetat sekä oikeamielisyyttä harrastavat ihmiset, tulee sinun julistaa sanomaa tuskallisesta rangaistuksesta.');
INSERT INTO "finnish" VALUES(3,21,'3:21','He ovat niitä, joiden työt raukeavat tyhjiin sekä tässä että tulevassa elämässä, eikä heillä tule olemaan puoltajaa.');
INSERT INTO "finnish" VALUES(3,22,'3:22','Etkö nähnyt, kuinka ne jotka ovat saaneet osan Pyhästä kirjasta , kiistellessään vetoavat Jumalan Kirjaan, jotta se ratkaisisi heidän riitansa, ja kuinka jotkut heistä kääntyvät pois siitä.');
INSERT INTO "finnish" VALUES(3,23,'3:23','ja tämä tapahtuu siksi, että he sanovat: »Ei tuli meitä ahdista kuin joinakuina päivinä », ja heidän itse keksimänsä ajatukset pettävät heidät heidän uskossaan.');
INSERT INTO "finnish" VALUES(3,24,'3:24','Ja kuinka heidän käykään, kun Me kokoamme heidät sinä päivänä, josta ei ole epäilystäkään ja jolloin jokainen sielu saa täyden palkan ansionsa mukaan, eikä ketään väärin tuomita?');
INSERT INTO "finnish" VALUES(3,25,'3:25','Sano: »Oi Jumala, valtakuntain Valtias, Sinä annat valtakunnan kenelle tahdot ja otat sen pois keneltä tahdot, Sinä korotat kenet tahdot ja alennat kenet tahdot. Sinun kädessäsi on hyvyys, Sinä olet totisesti kaikkivaltias.');
INSERT INTO "finnish" VALUES(3,26,'3:26','Sinä annat yön seurata päivää ja päivän seurata yötä, Sinä saat elävän versomaan kuolleesta ja teet kuolleen elävästä, ja Sinä annat elatuksen kenelle tahdot, määrää panematta.»');
INSERT INTO "finnish" VALUES(3,27,'3:27','Uskovaiset älkööt ottako uskottomia ystävikseen uskovaisten asemesta. Jos joku niin tekee, ei hänellä ole mitään puolustusta odotettavana Jumalalta. Teillä ei ole mitään pelättävää heidän puoleltaan . Jumala kehoittaa teitä pelkäämään vain Häntä, ja Hänen luoksensa te palaatte. Sano:');
INSERT INTO "finnish" VALUES(3,28,'3:28','»Salaattepa te, mitä teillä on sydämessänne, tai ilmaisette sen, Jumala sen kuitenkin tietää.» Hän tietää, mitä on taivaassa ja mitä on maan päällä; Jumala on kaikkivaltias.');
INSERT INTO "finnish" VALUES(3,29,'3:29','Sinä päivänä, jolloin jokainen sielu löytää edestään sen, mitä hyvää on tehnyt, ja samoin sen, mitä pahaa on tehnyt, hän on toivova, että olisi syvä kuilu hänen ja hänen pahojen tekojensa välillä. Jumala varoittaa teitä, että vain Häntä on pelättävä. ja Jumala on lempeä palvelijoitansa kohtaan.');
INSERT INTO "finnish" VALUES(3,30,'3:30','Sano: »Jos te rakastatte Jumalaa, niin seuratkaa minua, ja Jumala on rakastava teitä ja antava anteeksi teille syntinne, sillä Jumala on sääliväinen ja laupias».');
INSERT INTO "finnish" VALUES(3,31,'3:31','Sano: »Totelkaa Jumalaa ja profeettaa! Sillä jos käännytte pois, niin tietäkää, että Jumala totisesti ei rakasta uskottomia.»');
INSERT INTO "finnish" VALUES(3,32,'3:32','Katso, Jumala on yli kaikkien kansojen valinnut Aadamin ja Nooan, Aabrahamin suvun ja Imraanin suvun');
INSERT INTO "finnish" VALUES(3,33,'3:33','toinen toisensa jälkeläisinä, koska toinen polveutuu toisesta. Jumala on kuuleva, tietävä.');
INSERT INTO "finnish" VALUES(3,34,'3:34','Entä kun Imraanin vaimo sanoi: »Herra, minä olen Sinulle luvannut ja kokonaan pyhittänyt, minkä kohdussani kannan; ota se siis vastaan minulta. Sinä olet totisesti kuuleva, tietävä».');
INSERT INTO "finnish" VALUES(3,35,'3:35','Ja kun hän oli synnyttänyt, sanoi hän: »Herra, katso, olen synnyttänyt tyttölapsen» - ja Jumalahan tiesi parhaiten, minkä hän oli synnyttänyt, sillä miespuolinen ei ole sama kuin naispuolinen lapsi - »ja olen antanut hänelle nimen Maria ja minä asetan hänet ja hänen jälkeläisensä Sinun suojelukseesi, varjele heitä kirotulta Saatanalta».');
INSERT INTO "finnish" VALUES(3,36,'3:36','Niin vastaanotti hänen Herransa hänet (Marian) suosiollisesti ja antoi hänen kasvaa ihanasti, uskoen hänet Sakarian huostaan; ja joka kerta kun Sakaria astui hänen luokseen pyhäkköön, havaitsi hän ruokaa hänen edessään ja sanoi: »Maria, mistä tämä tulee sinulle?» Hän vastasi: »Se tulee Jumalalta; Jumala huolehtii kenestä tahtoo, määrää panematta».');
INSERT INTO "finnish" VALUES(3,37,'3:37','Siellä rukoili Sakaria Herraansa. Hän sanoi: »Herra, suo armostasi minulle siunattu perillinen! Sinä totisesti kuulet rukouksen.»');
INSERT INTO "finnish" VALUES(3,38,'3:38','ja niin huusivat enkelit hänelle sinne, missä hän seisoi pyhäkössä rukoilemassa: »Jumala iloksesi ilmoittaa sinulle (syntyvän) Johanneksen, josta tulee Jumalan totisen sanan vahvistaja, suuri, pidättyväinen ja vanhurskas profeetta».');
INSERT INTO "finnish" VALUES(3,39,'3:39','Ja Sakaria sanoi: »Herra, kuinka minä voin saada pojan, kun vanhuus on jo saavuttanut minut ja vaimoni on hedelmätön?» Enkeli vastasi: »Olkoonpa niinkin, Jumala tekee tahtonsa mukaan.»');
INSERT INTO "finnish" VALUES(3,40,'3:40','Silloin hän sanoi: »Herra, anna minulle tunnusmerkki!» Hän vastasi: »Sinun tunnusmerkkisi olkoon, ettet voi kolmeen vuorokauteen puhua ihmisille muuten kuin viittomalla. Rukoile ahkerasti Herraasi ja ylistä Häntä illoin ja aamuin!»');
INSERT INTO "finnish" VALUES(3,41,'3:41','Ja katso, enkelit sanoivat: »Maria, Jumala on totisesti valinnut, puhdistanut ja pyhittänyt sinut; Hän on korottanut sinut koko luomakunnan naisten yli.');
INSERT INTO "finnish" VALUES(3,42,'3:42','Maria, ole hurskas Herrasi palvelija, heittäydy maahan ja kumarra niiden kanssa, jotka Häntä kumartavat.»');
INSERT INTO "finnish" VALUES(3,43,'3:43','Tämä on, Muhammed, ilmoitus salatuista asioista, ja sinulle Me sen ilmaisemme, sillä sinä et ollut heidän kanssaan, kun he kirjoituspuikoillaan heittivät arpaa siitä, kuka saisi huolehdittavaksensa Marian, etkä sinä ollut myöhemminkään heidän joukossaan, kun he kiistelivät keskenään.');
INSERT INTO "finnish" VALUES(3,44,'3:44','Ja katso, enkelit sanoivat: »Maria, Jumala ilmoittaa sinulle hyvän sanoman, että hänen Sanansa tulee sinulle. Hänen nimensä on oleva Messias, Jeesus, Marian poika, hän on kuuluisa tässä ja tulevassa elämässä, yksi niistä, jotka korotetaan Jumalan läheisyyteen.');
INSERT INTO "finnish" VALUES(3,45,'3:45','Hän on puhuva ihmisille kehdostaan samoin kuin täysiikäisenä ja on oleva yksi vanhurskaista.»');
INSERT INTO "finnish" VALUES(3,46,'3:46','Maria sanoi: »Herra, kuinka minä voin saada lapsen, kun ei yksikään ihminen ole minuun koskenut?» Enkeli vastasi: »Jumala luo, mitä tahtoo; kun hän päättää jonkun asian, sanoo hän vain: ''Tapahtukoon!'' ja niin tapahtuu.');
INSERT INTO "finnish" VALUES(3,47,'3:47','Ja Jumala on opettava hänelle Pyhän kirjan, viisauden, Lain ja Evankeliumin');
INSERT INTO "finnish" VALUES(3,48,'3:48','ja lähettävä hänet profeetaksi israelilaisten luo. Hän on sanova heille: »Olen tullut luoksenne tuoden tunnusmerkkejä Herraltanne. Katso, minä muovailen savesta teille linnun muotoisen ja puhallan siihen, ja siitä tulee Jumalan sallimuksesta elävä lintu; minä olen Jumalan tahdosta parantava sokeana syntyneen ja pitaalisen ja olen herättävä kuolleet eloon. Ja minä sanon teille, mitä te tulette syömään ja mitä panemaan tallelle taloissanne. Tässä on totisesti tunnusmerkki teille, jos olette uskovaisia.');
INSERT INTO "finnish" VALUES(3,49,'3:49','Samoin tulen vahvistamaan todeksi sen, mikä on ollut ennen minua kirjoitettuna Laissa, ja sallimaan teille osan siitä, mikä tähän asti on ollut teiltä kiellettyä, niin, minä tulen luoksenne tuoden tunnusmerkit Herraltanne. Pelätkää siis Jumalaa ja totelkaa minua!');
INSERT INTO "finnish" VALUES(3,50,'3:50','Jumala on totisesti minun Herrani ja teidän Herranne, palvelkaa siis Häntä. Tämä on oikea tie.»');
INSERT INTO "finnish" VALUES(3,51,'3:51','Ja kun Jeesus havaitsi heissä epäuskoa, sanoi hän: »Ketkä haluavat olla minun seuraajiani Jumalan työssä?» Opetuslapset vastasivat: »Me olemme Jumalan seuraajia, me uskomme Jumalaan; todista, että olemme Hänen tahtoonsa ja johtoonsa alistuneet.');
INSERT INTO "finnish" VALUES(3,52,'3:52','Herramme, Me uskomme siihen, mitä olet ilmoittanut, ja me seuraamme Sinun profeettaasi; merkitse meidät siis tunnustajien joukkoon.»');
INSERT INTO "finnish" VALUES(3,53,'3:53','He (epäuskoiset juutalaiset) juonittelivat, mutta Jumalakin juonitteli (heitä vastaan); ja Jumala on todella taitavin Suunnittelija.');
INSERT INTO "finnish" VALUES(3,54,'3:54','Ja Jumala sanoi: »Jeesus, katso, Minä otan sinut, sitten olen ylentävä sinut luokseni ja vapauttava sinut niistä, jotka ovat uskottomia, sekä asettava ne, jotka sinua seuraavat, uskottomien yläpuolelle aina Ylösnousemuksen päivään saakka. Aikanaan on teidän kaikkien palattava minun luokseni, ja minä olen ratkaiseva sen, mistä te keskenänne kiistelitte.');
INSERT INTO "finnish" VALUES(3,55,'3:55','Mitä niihin tulee, jotka eivät uskoneet, olen kurittava heitä raskaalla rangaistuksella sekä tässä että tulevassa elämässä; eivätkä he löydä ketään auttajaa.»');
INSERT INTO "finnish" VALUES(3,56,'3:56','Mutta uskovaisille ja hyviä töitä harjoittaville Hän on antava heidän täyden palkkansa. Jumala ei rakasta väärintekijöitä.');
INSERT INTO "finnish" VALUES(3,57,'3:57','Tämän me julistamme sinulle Muhammed, tunnusmerkkinä ja viisauden tietona, jotka ovat Koraanissa.');
INSERT INTO "finnish" VALUES(3,58,'3:58','Katso, Jeesus on Jumalan edessä samankaltainen kuin Aadam; Hän loi hänet (Aadamin) maan tomusta, sitten Hän sanoi hänelle: »Ole!» ja hän oli (elävä sielu).');
INSERT INTO "finnish" VALUES(3,59,'3:59','Tämä totuus tulee Herraltasi, Muhammed, siksi älä kuulu epäileviin.');
INSERT INTO "finnish" VALUES(3,60,'3:60','Jos joku tästä kiistelee kanssasi, sen jälkeen kuin (tämä täydellinen) tieto on osaksesi tullut, niin sano: »Tulkaa, kutsukaamme meidän poikamme ja teidän poikanne, meidän vaimomme ja teidän vaimonne, kokoontukaamme me itse ja te itse; ja sitten kukin velvoittakaamme itsemme Jumalan edessä ja huutakaamme Jumalan kirousta niiden yli, jotka valehtelevat!»');
INSERT INTO "finnish" VALUES(3,61,'3:61','Katso, tämä sanoma on kauttaaltaan totuutta, eikä ole muuta jumalaa kuin Jumala; Hän on totisesti mahtava, viisas.');
INSERT INTO "finnish" VALUES(3,62,'3:62','Mutta jos he kääntyvät pois, niin totisesti Jumala tuntee turmeluksen aiheuttajat.');
INSERT INTO "finnish" VALUES(3,63,'3:63','Sano (juutalaisille ja kristityille) : »Te Pyhän kirjan omistajat, tulkaa yhteisymmärrykseen meidän kanssamme (uskonnon asiasta), että meillä olisi samoin kuin teilläkin, ettemme palvele muuta kuin ainoata Jumalaa emmekä aseta ketään Hänen rinnalleen, emmekä kukaan pidä toistamme herrana, (emme pidä herrana ketään muuta) paitsi Jumalaa.» Jos he kääntyvät pois, niin sanokaa: »Olkaa todistajina, että me olemme Jumalalle alamaisia!»');
INSERT INTO "finnish" VALUES(3,64,'3:64','Te Pyhän kirjan omistavat, miksi te kiistelette Aabrahamista? Laki ja Evankeliumihan lähetettiin vasta hänen jälkeensä ); eikö teillä ole ymmärrystä?');
INSERT INTO "finnish" VALUES(3,65,'3:65','Kuulkaa, jospa kiistelisitte sellaisesta, josta teillä on tietoa; mutta miksi kiistelette sellaisesta, josta teillä ei ole tietoa? Jumala tietää, mutta te ette tiedä.');
INSERT INTO "finnish" VALUES(3,66,'3:66','Aabraham ei ollut juutalainen eikä kristitty, vaan hän oli hurskas (haniifi) ja Jumalalle alamainen (muslimi) eikä kuulunut monijumalaisiin.');
INSERT INTO "finnish" VALUES(3,67,'3:67','Ihmisiä, jotka voivat lähinnä vedota Aabrahamiin, ovat totisesti ne, jotka seuraavat Häntä kuten tämäkin profeetta (Muhammed), ja ne, jotka (hänen kerallaan) uskovat; ja Jumala on uskovaisten Suojelija.');
INSERT INTO "finnish" VALUES(3,68,'3:68','Eräät niistä, jotka ovat saaneet Pyhän kirjan, toivoisivat voivansa eksyttää teidät, mutta he eivät eksytä ketään muita kuin juuri itsensä, eivätkä he aavista sitä.');
INSERT INTO "finnish" VALUES(3,69,'3:69','Te, joilla on Pyhä kirja, miksi ette usko Jumalan tunnusmerkkeihin, vaikka itse olette niiden silminnäkijöitä?');
INSERT INTO "finnish" VALUES(3,70,'3:70','Te, joilla on Pyhä kirja, miksi verhoatte totuuden valheella ja salaatte totuuden vastoin parempaa tietoanne?');
INSERT INTO "finnish" VALUES(3,71,'3:71','Osa niistä, jotka ovat saaneet Pyhän kirjan, sanoo: »Uskokaa päivän noustessa siihen, mikä on ilmoitettu uskovaisille (muslimeille), ja kieltäkää se päivän päättyessä; näin ehkä saamme heidät puolellemme.»');
INSERT INTO "finnish" VALUES(3,72,'3:72','Älkää uskoko muita kuin uskontoanne todella noudattavia! - Sano: »Oikea johdatus on totisesti Jumalan johdatus». Älkääkä uskoko, että joku on saanut saman (ilmoituksen) kuin te tai että he voivat kiistellä kanssanne Herranne edessä. Sano: »Kaikki runsaus on Jumalan kädessä, hän antaa sen kenelle tahtoo. Jumala sulkee kaiken olemukseensa ja tietää kaikki.»');
INSERT INTO "finnish" VALUES(3,73,'3:73','Hän antaa laupeutensa sille, kenet Hän valitsee. Jumala on rajaton runsaudessaan.');
INSERT INTO "finnish" VALUES(3,74,'3:74','Niiden keskuudessa, jotka ovat saaneet Pyhän kirjan, on sellaisia, että jos uskot heille säilytettäväksi suuren määrän kultaa, he palauttavat sen sinulle, ja heidän keskuudessaan on sellaisiakin, jotka, jos uskot heille denaarin, eivät palauta sitä sinulle, ellet sitä vaatimalla vaadi. Näin on siksi, että he sanovat: »Meillä ei ole mitään velvollisuuksia tietämättömiä (pakanoita) kohtaan». Noin he kieräilevät Jumalaan vedoten ja tietävät sen itse.');
INSERT INTO "finnish" VALUES(3,75,'3:75','Ei niin, vaan ainoastaan se on (Jumalan edessä hurskas), joka täyttää velvollisuutensa ja karttaa pahaa. Katso, Jumala rakastaa ainoastaan hurskaita.');
INSERT INTO "finnish" VALUES(3,76,'3:76','Totisesti niillä, jotka liittonsa Jumalan kanssa ja valansa vaihtavat vähäpätöisiin arvoihin, ei tule olemaan autuutta tulevassa elämässä. Jumala ei puhu heille eikä katso heihin Ylösnousemuksen päivänä eikä vanhurskauta heitä, vaan heidän osanaan on tuskallinen rangaistus.');
INSERT INTO "finnish" VALUES(3,77,'3:77','Katso, heidän joukossaan on sellaisia, jotka puhuessaan vääristelevät Pyhää kirjaa, jotta te luulisitte Pyhän kirjan sanoiksi sellaistakin, mikä siihen ei sisälly; he sanovat: »Se tulee Jumalalta», vaikka se ei tule Jumalalta, ja vedoten Jumalaan he valehtelevat vastoin parempaa tietoaan.');
INSERT INTO "finnish" VALUES(3,78,'3:78','Ei voi ihminen, jolle Jumala on antanut Pyhän kirjan, viisauden ja profeetantehtävän, sanoa ihmisille: »Olkaa minun palvelijoitani samalla tavalla, kuin olette Jumalan palvelijoita». Ei, vaan (hän sanoo:) »Olkaa Jumalan palvelijoita, koska tunnette Pyhän kirjan ja tutkitte sitä.»');
INSERT INTO "finnish" VALUES(3,79,'3:79','Eikä hän käske teitä ottamaan enkeleitä tai profeettoja herroiksenne. Kehoittaisiko hän teitä väärään uskoon, senjälkeen kuin olette tulleet Jumalan tahdon alaisiksi (muslimeiksi)?');
INSERT INTO "finnish" VALUES(3,80,'3:80','Aikoinaan, kun Jumala teki liiton profeettojen kanssa, Hän sanoi: »Katso, näin paljon annan teille Pyhiä kirjoituksia ja viisautta, ja sitten tulee lähettiläs ja vahvistaa todeksi sen, mikä teillä jo on; teidän on totisesti uskottava häneen ja autettava häntä». Hän sanoi vielä: »Tunnustatteko ja otatteko vastaan liittoni tällä ehdolla?» He vastasivat: »Me tunnustamme sen». Silloin Hän sanoi: »Olkaa siis tämän todistajat. Minä myöskin olen todistajana teidän rinnallanne.»');
INSERT INTO "finnish" VALUES(3,81,'3:81','Senvuoksi ketkä ikinä tämän jälkeen luopuvat, he ovat jumalattomia.');
INSERT INTO "finnish" VALUES(3,82,'3:82','Jotakin muuta Jumalan uskontoako he vielä etsisivät, vaikka kaikki taivaassa ja maan päällä ovat Hänelle alamaisia tahtoen tai tahtomattaan, ja Hänen luokseen on heidän palattava?');
INSERT INTO "finnish" VALUES(3,83,'3:83','Sano: »Me uskomme Jumalaan, siihen, mitä on ylhäältä ilmoitettu meille ja mitä on ilmoitettu Aabrahamille, Ismaelille, Iisakille ja Jaakobille sekä (Israelin) sukukunnille, siihen, mitä Mooses, Jeesus ja profeetat ovat saaneet Herraltansa; me emme tee mitään erotusta heidän välillään, ja Hänen tahtoonsa ja johdatukseensa olemme alistuneet».');
INSERT INTO "finnish" VALUES(3,84,'3:84','Mutta jos joku pitää uskontonaan jotakin muuta kuin Islaamia , niin Jumala ei sitä koskaan hyväksy, ja kuoleman jälkeen hän on joutuva kadotettujen joukkoon.');
INSERT INTO "finnish" VALUES(3,85,'3:85','Kuinka Jumala ohjaisi ihmisiä, jotka ovat tulleet uskottomiksi, senjälkeen kuin he olivat uskossa ja tunnustivat Muhammedin oikeaksi profeetaksi ja (Jumalan kaikkivallan) selvät todistukset heille näytettiin? Jumala ei ohjaa jumalattomia ihmisiä.');
INSERT INTO "finnish" VALUES(3,86,'3:86','Näiden palkaksi tulee Jumalan, enkelien ja ihmisten kirous.');
INSERT INTO "finnish" VALUES(3,87,'3:87','Sen alaisiksi he jäävät ikuisesti, heidän rangaistustaan ei lievennetä eikä heihin kiinnitetä (sen enempää) huomiota.');
INSERT INTO "finnish" VALUES(3,88,'3:88','(Tästä kohtalosta vapautuvat vain ne), jotka myöhemmin katuvat ja tekevät parannuksen, sillä Jumala on sääliväinen ja laupias.');
INSERT INTO "finnish" VALUES(3,89,'3:89','Mutta jotka oltuaan uskossa ovat tulleet uskottomiksi ja sitten yhä yltyvät uskottomuudessaan, niiden kääntymystä ei totisesti hyväksytä; he jäävät eksyneiksi.');
INSERT INTO "finnish" VALUES(3,90,'3:90','Niistä, jotka ovat olleet uskottomia ja kuolevat epäuskossaan, ei yhdenkään sielun lunnaiksi hyväksytä edes koko maailman täyttä kultaa, heitä odottaa tuskallinen rangaistus, eivätkä he löydä ketään puoltajaa.');
INSERT INTO "finnish" VALUES(3,91,'3:91','Ette koskaan saavuta tosihurskautta, ennenkuin uhraatte sellaista, mikä on teille rakasta; ja mitä uhraattekin, Jumala sen tietää totisesti.');
INSERT INTO "finnish" VALUES(3,92,'3:92','Ennenkuin Laki annettiin ylhäältä, oli kaikki ruoka sallittu israelilaisille paitsi se, minkä Israel itse kielsi itseltään. Sano: »Ottakaa esille Mooseksen kirjat ja lukekaa sieltä, mikäli tahdotte pysyä totuudessa.»');
INSERT INTO "finnish" VALUES(3,93,'3:93','Ja ketkä tämän jälkeen sepittävät valheita Jumalaan vedoten, ne ovat väärintekijöitä, jumalattomia.');
INSERT INTO "finnish" VALUES(3,94,'3:94','Sano: »Jumalan puhe on totuutta; noudattakaa siis Aabrahamin oppia, koska hän oli hurskas (haniifi) eikä kuulunut monijumalaisiin.»');
INSERT INTO "finnish" VALUES(3,95,'3:95','Ensimmäinen rukoushuone, joka perustettiin ihmisille, oli totisesti se, joka on Bekassa (Mekassa) siunauksena ja opastuksena koko maailmalle.');
INSERT INTO "finnish" VALUES(3,96,'3:96','Siellä on ilmeisiä tunnusmerkkejä: Aabrahamin rukouspaikka; ja se, joka astuu sinne, on varmassa turvassa. Ja pyhiinvaellus tähän huoneeseen on ihmisten velvollisuus Jumalaa kohtaan, jokaisen, ken suinkin voi matkan tehdä. Mutta jos joku käy uskottomaksi, niin katso, Jumala tulee toimeen vaikkapa ilman koko maailmaa.');
INSERT INTO "finnish" VALUES(3,97,'3:97','Sano: »Te, jotka omistatte Pyhän kirjan, miksi ette usko Jumalan tunnusmerkkeihin, vaikka Jumala on teidän tekojenne todistaja?');
INSERT INTO "finnish" VALUES(3,98,'3:98','Sano: »Te, joilla on Pyhä kirja, miksi työnnätte uskovaisia pois Jumalan tieltä ja pyritte tekemään sen mutkikkaaksi, vaikka olette varmoja (sen suoruudesta)? Mutta Jumala ei ole välinpitämätön siitä, mitä te teette.»');
INSERT INTO "finnish" VALUES(3,99,'3:99','Te, jotka uskotte, jos tottelette eräitä niistä, jotka ovat Pyhän kirjan saaneet, tekevät he taas teidät uskottomiksi, teidän jo oltuanne uskossa.');
INSERT INTO "finnish" VALUES(3,100,'3:100','Kuinka voittekaan olla uskottomia, kun Jumalan tunnusmerkit luetellaan teille ja Hänen profeettansa on teidän keskuudessanne? Joka turvautuu Jumalaan, hänet johdetaan varmasti (kulkemaan) oikeata tietä.');
INSERT INTO "finnish" VALUES(3,101,'3:101','Te, jotka uskotte, pelätkää Jumalaa niin, kuin Häntä on pelättävä, eikä teidän pidä kuoleman, ennenkuin olette tulleet Hänen tahtonsa alaisiksi (muslimeiksi).');
INSERT INTO "finnish" VALUES(3,102,'3:102','Pitäkää kiinni kaikki Jumalan (pelastus)köydestä älkääkä hajaantuko, vaan muistakaa Jumalan armoa teitä kohtaan silloin, kun olitte toistenne vihollisia ja Hän lähensi sydämenne, niin että te Hänen armonsa kautta tulitte aivan kuin veljiksi keskeänne. Te olitte tulikuilun partaalla, mutta Hän pelasti teidät siitä. Siten selittää Jumala tunnusmerkkinsä teille, jotta tulisitte oikealle tielle');
INSERT INTO "finnish" VALUES(3,103,'3:103','ja jotta teistä tulisi seurakunta, joka kutsuu ihmisiä hyvyyteen, säätää heille oikeat elämäntavat ja estää heitä pahuudesta; sellaiset ihmiset tulevat menestymään.');
INSERT INTO "finnish" VALUES(3,104,'3:104','Älkää te olko niiden kaltaisia, jotka saatuaan selvät todistukset erottautuivat toisistaan ja riitelivät toistensa kanssa. Sellaisia odottaa kauhea rangaistus');
INSERT INTO "finnish" VALUES(3,105,'3:105','sinä (Ylösnousemuksen) päivänä, jolloin toisten kasvot kirkastuvat ja toisten mustuvat (synkkenevät) ja niille, joiden kasvot synkkenevät, sanotaan: »Ettekö te ole niitä, jotka tulitte uskottomiksi oltuanne uskossa? Niin, maistakaa nyt rangaistusta luopumisenne tähden!»');
INSERT INTO "finnish" VALUES(3,106,'3:106','ja ne, joiden kasvot kirkastuvat, heidät suljetaan Jumalan laupeuteen, ja he pysyvät siinä iankaikkisesti.');
INSERT INTO "finnish" VALUES(3,107,'3:107','Nämä ovat Jumalan ihmeellisiä ilmoituksia, jotka julistamme sinulle totuudessa; Jumala ei halua tehdä väärin maailmoille (ihmisille).');
INSERT INTO "finnish" VALUES(3,108,'3:108','Jumalalle kuuluu kaikki taivaassa ja maan päällä, ja Jumalan luo kaikki on palaava.');
INSERT INTO "finnish" VALUES(3,109,'3:109','Te olette paras seurakunta, mikä on muodostunut ihmisten keskuuteen. Te määräätte ihmisille oikeat elämäntavat, estätte heitä pahuudesta ja uskotte Jumalaan. Jos Pyhän kirjan saaneet olisivat uskoneet, olisi se ollut heille parempi; heidän keskuudessaan on uskovaisia, mutta useimmat heistä ovat jumalattomia.');
INSERT INTO "finnish" VALUES(3,110,'3:110','He eivät voi aiheuttaa teille kuin vähän vahinkoa, ja jos he käyvät sotimaan teitä vastaan, kääntyvät he pakoon eivätkä sen jälkeen saa apua.');
INSERT INTO "finnish" VALUES(3,111,'3:111','Kurjuus on masentava heidät, missä ikinä he ovat, paitsi jos he pitävät liiton Jumalan kanssa ja sopimuksen ihmisten kanssa ; (muuten) he vetävät päällensä Jumalan vihan, ja viheliäisyys peittää heidät. Kaikki tämä tapahtuu siksi, että he hylkäsivät Jumalan tunnusmerkit ja vääryydessään surmasivat profeetat, sekä siksi, että he olivat uppiniskaisia ja tekivät rikoksia.');
INSERT INTO "finnish" VALUES(3,112,'3:112','Pyhän kirjan saaneista eivät kuitenkaan kaikki ole samanlaisia; heidän keskuudessaan on vakaa joukko (ihmisiä), jotka yön aikana lukevat Jumalan ihmeellisiä ilmoituksia ja Häntä kumartavat.');
INSERT INTO "finnish" VALUES(3,113,'3:113','He uskovat Jumalaan ja Viimeiseen päivään, harrastavat hyvää ja estävät ihmisiä pahuudesta, kilvoittelevat keskenään hyvissä töissä; nämä kuuluvat vanhurskaisiin.');
INSERT INTO "finnish" VALUES(3,114,'3:114','Mitä hyvää he tekevätkin, ei heiltä koskaan riistetä sen hedelmää, sillä Jumala tuntee ne, jotka Häntä pelkäävät.');
INSERT INTO "finnish" VALUES(3,115,'3:115','Niitä, jotka ovat uskottomia, eivät totisesti heidän rikkautensa eivätkä heidän lapsensa vähääkään hyödytä Jumalan edessä; he joutuvat Tulen omiksi ja pysyvät siinä iankaikkisesti.');
INSERT INTO "finnish" VALUES(3,116,'3:116','Minkä he antavat pois tässä maallisessa elämässä, sen laita on sama kuin purevan kylmän viiman, joka kiitää jumalattomien ihmisten vainioiden yli ja tuhoaa heidän satonsa. Ei Jumala tee heille vääryyttä, vaan itse he tekevät vääryyttä itselleen.');
INSERT INTO "finnish" VALUES(3,117,'3:117','Te, jotka uskotte, älkää antautuko avomieliseen ystävyyteen muiden kuin teikäläisten kanssa; muut eivät jätä turmelematta teitä. He halajavat teidän perikatoanne, heidän vihansa on jo pursunut heidän suustansa; mutta se, mikä heidän sydämissään piilee, on vielä pahempaa. Me olemme nyt selittänyt tunnusmerkkimme teille, jotta ehkä ymmärtäisitte.');
INSERT INTO "finnish" VALUES(3,118,'3:118','Katso, te olette sellaisia, jotka rakastatte heitä, mutta he eivät rakasta teitä; te uskotte koko Pyhään kirjaan, ja kun he tapaavat teidät, he sanovat: »Me uskomme», mutta omissa oloissaan he pureskelevat sormiansa raivoissaan teitä vastaan. Sano: »Kuolkaa raivoonne! Jumala tuntee totisesti sydäntenne sisimmät aivoitukset.»');
INSERT INTO "finnish" VALUES(3,119,'3:119','Jos jotain hyvää tapahtuu teille, kadehtivat he sitä, mutta jos jokin paha kohtaa teitä, siitä he iloitsevat. Jos olette kärsivällisiä ja pelkäätte Jumalaa, ei heidän kavaluutensa voi vahingoitta teitä vähääkään; Jumala sulkee huomioonsa kaikki heidän tekonsa.');
INSERT INTO "finnish" VALUES(3,120,'3:120','Entä kun sinä, Muhammed, varhain eräänä aamuna jätit kotiväkesi sijoittaaksesi uskovaiset taistelupaikoilleen (Jumala kaikki kuuli ja tiesi) ja');
INSERT INTO "finnish" VALUES(3,121,'3:121','kun joukostanne kaksi osastoa oli menettää rohkeutensa (mutta Jumala oli heidän tukensa; uskovaiset turvautuvat Jumalaan).');
INSERT INTO "finnish" VALUES(3,122,'3:122','Olihan Jumala auttanut teitä Bedrin taistelussa , kun olitte huonommassa asemassa; antakaa siis Jumalalle kunnia, jotta olisitte kiitollisia!');
INSERT INTO "finnish" VALUES(3,123,'3:123','Silloin sinä sanoit uskovaisille: »Eikö teille riitä, että Herranne tukee teitä kolmella tuhannella Ylhäältä lähetetyllä enkelillä?»');
INSERT INTO "finnish" VALUES(3,124,'3:124','Niin, jos olette kärsivällisiä ja pelkäätte Jumalaa ja viholliset yllättävät teidät äkkirynnäköllä, on Herranne tukeva teitä viidellä tuhannella enkelinä, joilla on erityiset merkit.');
INSERT INTO "finnish" VALUES(3,125,'3:125','Tämän antoi Jumala teille rohkaisevana ilosanomana, jotta sydämenne rauhoittuisivat sen kautta - apu tulee ainoastaan Jumalalta, mahtavalta viisaalta -');
INSERT INTO "finnish" VALUES(3,126,'3:126','jotta Hän murskaisi uskottomat ja kaataisi heidät, niin että he peräytyisivät saavuttamatta sitä, mitä tahtoivat.');
INSERT INTO "finnish" VALUES(3,127,'3:127','Sinua, Muhammed, ei koske vähääkään, armahtaako Hän heitä vai rankaisee, sillä he ovat väärintekijöitä.');
INSERT INTO "finnish" VALUES(3,128,'3:128','Jumalalle kuuluu kaikki taivaassa ja maan päällä. Hän antaa anteeksi kenelle tahtoo ja rankaisee ketä tahtoo; niin, Jumala on sääliväinen ja laupias.');
INSERT INTO "finnish" VALUES(3,129,'3:129','Te, jotka uskotte, älkää koronkiskomisella ahnehtiko kaksinkertaisia tuloja, vaan pelätkää Jumalaa, jotta olisitte onnellisia');
INSERT INTO "finnish" VALUES(3,130,'3:130','Ja kavahtakaa Tulta, joka on valmistettu uskottomille,');
INSERT INTO "finnish" VALUES(3,131,'3:131','totelkaa Jumalaa ja profeettaa, jotta teidän osaksenne tulisi laupeus.');
INSERT INTO "finnish" VALUES(3,132,'3:132','ja pyrkikää kilvan saavuttamaan Herranne anteeksianto ja Paratiisi, joka on yhtä ääretön kuin taivaat ja maa, ja on valmistettu jumalaapelkääväisille,');
INSERT INTO "finnish" VALUES(3,133,'3:133','jotka antavat uhrilahjoja sekä varakkaina että köyhinä ollessaan, jotka hillitsevät vihansa ja antavat anteeksi ihmisille; niin, Jumala rakastaa niitä, jotka hyvää tekevät.');
INSERT INTO "finnish" VALUES(3,134,'3:134','Ja jotka, tehtyään jotain alhaista tai rikottuaan itseään vastaan, muistavat Jumalan ja anovat syntiään anteeksi (ja kukahan muu kuin Jumala voi antaa synnit anteeksi?) eivätkä vastoin parempaa tietoaan paadu siinä, mitä ovat tehneet,');
INSERT INTO "finnish" VALUES(3,135,'3:135','niiden palkka on oleva heidän Herransa anteeksianto ja huvitarhat, joissa purot solisevat, joissa he ikuisesti viihtyvät; niin, minkä ihanan palkan saavatkaan hyvyyden harjoittajat!');
INSERT INTO "finnish" VALUES(3,136,'3:136','Jumalan kurinpitoa on ollut jo ennen teitä; vaeltakaa ympäri maata ja tarkatkaa, millaisen lopun ne saivat, jotka vääristelivät totuuden (sanoman) valheeksi.');
INSERT INTO "finnish" VALUES(3,137,'3:137','Tämä on selitys ihmisille, opastus ja ojennus jumalaapelkääväisille.');
INSERT INTO "finnish" VALUES(3,138,'3:138','Älkää väsykö älkääkä murehtiko; te voitatte, jos olette (tosi-)uskovaisia!');
INSERT INTO "finnish" VALUES(3,139,'3:139','jos teitä kohtasi isku, niin on yhtä ankara isku kohdannut (uskottomia) ihmisiä tätä ennen . Me annamme ihmisten kesken päivien vaihdella, jotta Jumala tulisi tuntemaan ne, jotka uskovat, ja voisi hankkia itselleen todistajia teidän keskuudestanne - väärintekijöitä ei Jumala rakasta -');
INSERT INTO "finnish" VALUES(3,140,'3:140','ja jotta Jumala voisi koetella uskovaisia ja tuhota uskottomat.');
INSERT INTO "finnish" VALUES(3,141,'3:141','Vai luuletteko, että saatte astua Paratiisiin, ennenkuin Jumala tuntee ne teistä, jotka olivat taisteluvalmiit, ja ennenkuin Hän tuntee ne, jotka ovat kestäviä?');
INSERT INTO "finnish" VALUES(3,142,'3:142','Toivoittehan kuolemaa, ennenkuin kohtasitte sen (kentällä), mutta nyt olette nähneet sen omin silmin.');
INSERT INTO "finnish" VALUES(3,143,'3:143','Muhammed on vain lähettiläs; edesmenneet profeetat ovat ennen häntä olleet (Jumalan) lähettiläitä; jos hän kuolee tai kaatuu, peräydyttekö te silloin? Jos joku peräytyy, ei hän voi vahingoittaa Jumalaa vähääkään, mutta kiitolliset Jumala on palkitseva.');
INSERT INTO "finnish" VALUES(3,144,'3:144','Ei yksikään sielu kuole muuten kuin Jumalan sallimuksesta, Kohtalon kirjassa määrätyllä hetkellä. Sille, joka toivoo itselleen palkkaa tässä elämässä, annamme sen mukaan, ja sille, joka toivoo palkkaa tulevassa elämässä, annamme hänen toivomuksensa mukaan, ja Me olemme palkitseva kiitolliset.');
INSERT INTO "finnish" VALUES(3,145,'3:145','Monta oli profeettaa, joiden keralla taisteli joukko uskollisia (seuraajia); eivät he lannistuneet mistään, mikä kohtasi heitä Jumalan sotaretkellä, eivät he horjuneet eivätkä masentuneet. ja Jumala rakastaa kestäviä.');
INSERT INTO "finnish" VALUES(3,146,'3:146','Ei heillä muuta ollut sanottavaa kuin: »Herra, anna anteeksi meidän syntimme ja harhaotteet tehtävässämme, anna lujuutta meidän askeleillemme ja auta meitä saamaan voitto uskottomista vastustajistamme.»');
INSERT INTO "finnish" VALUES(3,147,'3:147','ja Jumala antoi heille palkan tässä elämässä ja ihanan palkan tulevassa; katso, Jumala rakastaa hyväätekeviä.');
INSERT INTO "finnish" VALUES(3,148,'3:148','Jos te jotka uskotte, olette epäuskoisille kuuliaisia, saattavat he teidät peräytymään, ja te palaatte häviön kärsien.');
INSERT INTO "finnish" VALUES(3,149,'3:149','Mutta onhan Jumala suojelijanne, niin, Hän on paras auttaja.');
INSERT INTO "finnish" VALUES(3,150,'3:150','Me lähetämme äkkiä pelästyksen epäuskoisten sydämiin, koska he Jumalan rinnalla pitävät muita jumaluusolentoja, joiden palvelemiseen Hän ei ole antanut mitään oikeutta. Heidän asunnokseen tulee Tuli; kauhea on jumalattomien olinpaikka.');
INSERT INTO "finnish" VALUES(3,151,'3:151','Onhan Jumala pitänyt teille antamansa lupauksen, kun Hänen avullaan olitte voitokkaita, kunnes osoittauduitte pelkureiksi, kiistelitte keskenänne määräyksistä ja olitte tottelemattomia, senjälkeen kuin Hän jo näytti teille mitä toivoitte (taistelun tuloksen). Jotkut teistä halusivat tämän elämän hyvyyttä ja toiset teistä halusivat kuoleman jälkeistä elämää. Siksi Hän käänsi teidät pakoon vihollistenne edessä koetellaksensa teitä. Kuitenkin Hän on jo armahtanut teitä, Jumala on runsas jalomielisyydessään uskovaisia kohtaan.');
INSERT INTO "finnish" VALUES(3,152,'3:152','Kun läksitte taistelukentällä toisistanne välittämättä ja profeetta huusi jälkeenne (kutsuen taistelemaan), lähetti Hän teille palkkioksi ahdinkoa ahdingon lisäksi, jottette murehtisi sitä (saalista), minkä olitte menettäneet, tai (iskua) mikä oli kohdannut teitä. Katso, Jumala tietää teidän työnne.');
INSERT INTO "finnish" VALUES(3,153,'3:153','Ahdinkonne jälkeen Hän lähetti teille turvallisuuden tunteen ja unen, joka valtasi osan teistä. Toiset teistä taas kiihoittivat itseään väärillä kuvitteluilla Jumalasta. He sanoivat: »Onko meillä oikeastaan tekemistä tämän asian kanssa?» Sano: »Koko asia on Jumalan». He salasivat sydämessään jotakin, jota eivät sinulle ilmaisseet, ja sanoivat: »Jos meille olisi tarkoitettu jokin tulos tästä asiasta, ei meitä olisi lyöty täällä». Sano: »Vaikka olisitte olleet taloissanne, niin totisesti olisivat ne, joiden kuolema oli määrätty, saaneet surmansa kentälle. Tämä (sallittiin sen vuoksi, että Jumala koettelisi, mitä kätkeytyy rintaanne, ja tutkisi sen, mikä on teidän sydämissänne.» Jumala tuntee sydänten sisimmät aivoitukset.');
INSERT INTO "finnish" VALUES(3,154,'3:154','Katso, ne teistä, jotka kääntyivät pakoon sinä päivänä, kun molemmat sotajoukot kohtasivat toisensa, saattoi Saatana lankeamaan jonkun heidän tekonsa tähden; mutta Jumala on armahtanut heitä, Jumalahan on totisesti anteeksiantava ja lempeä.');
INSERT INTO "finnish" VALUES(3,155,'3:155','Te, jotka uskotte, älkää olko niiden kaltaisia, jotka ovat uskottomia ja sanovat veljistään, kun he ovat matkalla tai lähtevät sotaan: »Jos he olisivat jääneet luoksemme, eivät he olisi kuolleet tai kaatuneet.» Jumala vain sallii sen, mikä on tapahtunut, kirvellä heidän sydämissään. Jumala antaa sekä elämän että kuoleman, ja Jumala näkee mitä teette.');
INSERT INTO "finnish" VALUES(3,156,'3:156','ja jos kaadutte tai kuolette Jumalan sotaretkellä, niin on totisesti Jumalan anteeksianto ja lupaus parempi kuin kaikki se maallinen, mitä olette saaneet kootuksi.');
INSERT INTO "finnish" VALUES(3,157,'3:157','Ja jos muuten kuolette tai sodassa kaadutte, niin Jumalan luokse teidän on kokoonnuttava.');
INSERT INTO "finnish" VALUES(3,158,'3:158','Jumalan laupeuden vuoksi olet menetellyt lempeästi heitä kohtaan, Muhammed, mutta jos olisit ollut ankara ja kovasydäminen, olisivat he eronneet sinusta. Ole siis suvaitsevainen heitä kohtaan, rukoile heille anteeksiantoa ja neuvottele heidän kanssaan asioista. Ja kun olet päätöksesi tehnyt, luota Jumalaan! Katso, Jumala rakastaa niitä, jotka Häneen turvaavat.');
INSERT INTO "finnish" VALUES(3,159,'3:159','Jos Jumala auttaa teitä, ei teitä kukaan voita, ja jos Hän vetäytyy pois, kuka silloin voi teitä auttaa? Niin, Jumalaan turvatkoot uskovaiset!');
INSERT INTO "finnish" VALUES(3,160,'3:160','Eikä profeetan sovi kavaltaa mitään, sillä sen, joka kavaltaa, on Ylösnousemuksen päivänä tuotava julki, mitä hän on kavaltanut. Silloin on jokainen sielu saapa täyden ansionsa mukaan, eikä ketään väärin tuomita.');
INSERT INTO "finnish" VALUES(3,161,'3:161','Luuletteko hänen, joka tavoittelee sitä, mikä on Jumalalle otollista, osoittautuvan sellaiseksi, joka on vetänyt ylitsensä Jumalan vihan ja jonka olinpaikka on oleva Helvetti, se surkea määränpää?');
INSERT INTO "finnish" VALUES(3,162,'3:162','Jumalan luona heillä on erilainen arvoasema; näkeehän Jumala, mitä kukin tekee.');
INSERT INTO "finnish" VALUES(3,163,'3:163','Totisesti Jumala on ollut armollinen uskovaisia kohtaan lähettäessään heille apostolin heidän keskuudestaan, joka julistaa heille Hänen tunnusmerkkejään, puhdistaa heidät ja opettaa Pyhää kirjaa ja viisautta heille, jotka aikaisemmin elivät ilmeisessä harhauskossa.');
INSERT INTO "finnish" VALUES(3,164,'3:164','Ja kun isku kohtasi teitä teidän annettuanne (vastustajillenne) kaksi kertaa raskaamman iskun, miksi sanoitte: »Mistä tämä johtuu?» Sano (heille, Muhammed): »Se johtuu teistä itsestänne». Jumalahan on kaikkivaltias.');
INSERT INTO "finnish" VALUES(3,165,'3:165','Ja se, mikä sattui teille sinä päivänä, kun molemmat joukot kohtasivat toisensa, tapahtui Jumalan sallimuksesta ja jotta Hän tuntisi uskovaiset');
INSERT INTO "finnish" VALUES(3,166,'3:166','ja tuntisi teeskentelijät, ne, joille sanottiin, »Tulkaa ja taistelkaa Jumalan asian puolesta tai torjukaa heidät!» ja he vastasivat: »Jos osaisimme taistella, niin seuraisimme teitä.» Sinä päivänä he olivat lähempänä uskottomuutta kuin uskoa. Huulillaan he sanoivat sellaista, mitä ei ollut heidän sydämissänsä, mutta Jumala tuntee parhaiten, mitä he salaavat.');
INSERT INTO "finnish" VALUES(3,167,'3:167','Niille, jotka itse istuessaan kotona sanoivat (taistelevista) veljistään: »Jos he olisivat kuulleet meitä, eivät he olisi kaatuneet», (heille) sano: »Torjukaa sitten kuolema omalta kohdaltanne, jos puhutte täyttä totta!»');
INSERT INTO "finnish" VALUES(3,168,'3:168','Mutta älkää pitäkö kuolleina niitä, jotka ovat kaatuneet Jumalan sotaretkellä. Ei, he elävät Herransa luona, saaden kukin osansa,');
INSERT INTO "finnish" VALUES(3,169,'3:169','riemuiten siitä, mitä Jumala on runsaudestaan heidän ylitseen vuodattanut, ja iloiten niistä, jotka seuraavat heidän jälkiänsä, mutta eivät vielä ole heitä saavuttaneet. Ei mikään pelko ole saava heitä valtaansa, eikä heidän tarvitse murehtia.');
INSERT INTO "finnish" VALUES(3,170,'3:170','He iloitsevat Jumalan armosta ja mielisuosiosta ja siitä, ettei Jumala anna uskovaisten palkan mennä hukkaan.');
INSERT INTO "finnish" VALUES(3,171,'3:171','Hyvää tekeviä ja Jumalaa pelkääviä niiden joukossa, jotka tottelivat Jumalaa ja profeettaa, kun isku heitä kohtasi, odottaa suuri palkka,');
INSERT INTO "finnish" VALUES(3,172,'3:172','niitä, joille ihmiset sanoivat, että pelottavat sotavoimat olivat kokoontuneet heitä vastaan, mutta tämä vain lisäsi heidän uskoaan, ja he vastasivat: »Jumalassa on meille (voimaa) kylliksi. Hän on paras tukemme ja turvamme.»');
INSERT INTO "finnish" VALUES(3,173,'3:173','He palasivat Jumalan armon turvissa mitään pahaa kohtaamatta. He etsivät Jumalan mielisuosiota, ja Jumala on ylenmäärin suosiollinen.');
INSERT INTO "finnish" VALUES(3,174,'3:174','Tosin Saatana peloittelee teitä ystävillänsä, mutta älkää pelätkö heitä, vaan pelätkää Minua, jos olette uskovaisia!');
INSERT INTO "finnish" VALUES(3,175,'3:175','Ne, jotka kevein askelin käyvät uskottomuuteen, älkööt katkeroittako sinua; he eivät voi vähääkään vahingoittaa Jumalaa. Jumala ei näe hyväksi antaa heille mitään osaa tulevassa elämässä; heitä odottaa kauhea rangaistus.');
INSERT INTO "finnish" VALUES(3,176,'3:176','Ne, jotka vaihtavat uskon uskottomuuteen, eivät totisesti voi vahingoittaa Jumalaa; heitä uhkaa tuskallinen rangaistus.');
INSERT INTO "finnish" VALUES(3,177,'3:177','Uskottomat älkööt luulko, että elinkautinen armonaika, jonka Me heille annamme, on heille onneksi; Me annamme heille aikaa ainoastaan siksi, että heidän syntinsä enenisivät. Katso, heitä odottaa häpeällinen rangaistus.');
INSERT INTO "finnish" VALUES(3,178,'3:178','Jumala ei aio jättää uskovaisia siihen tilaan, jossa te nyt olette, siihen asti kunnes Hän erottaa väärät hyvistä. Eikä Jumala aio ilmaista teille silmiltä salattua, vaan Jumala valitsee (siihen tarkoitukseen) kenet tahtoo lähettiläittensä joukosta. Uskokaa siis Jumalaan ja Hänen lähettiläihinsä, sillä jos uskotte ja pelkäätte Jumalaa, saatte runsaan palkinnon.');
INSERT INTO "finnish" VALUES(3,179,'3:179','Ne, jotka ovat saitoja sen suhteen, mitä Jumala on runsaudessaan heille antanut, älkööt luulko, että se on hyödyksi heille; ei, se on heille vahingoksi. Ylösnousemuksen päivänä ne asiat, joissa he olivat saitoja, tulevat kaulakahleina kuristamaan heitä. Jumalalle kuuluvat taivasten ja maan perinnöt, ja Jumala tietää teidän tekonne.');
INSERT INTO "finnish" VALUES(3,180,'3:180','Jumala on kuullut niidenkin puheen, jotka sanoivat (pilkallisesti, kun heitä kehoitettiin lahjoittamaan Herralle): »Jumala on köyhä, ja me olemme rikkaita». Me merkitsemme muistiin heidän sanansa ja kuinka he syyttä surmasivat profeetat, ja tulemme sanomaan: »Maistakaa Tulen tuskaa!»');
INSERT INTO "finnish" VALUES(3,181,'3:181','Ja tämä on teille seurauksena omien kättenne töistä. Jumala ei ole julma palvelijoillensa.');
INSERT INTO "finnish" VALUES(3,182,'3:182','Niille, jotka väittävät: »Jumalahan on sallinut meidän olla uskomatta mihinkään lähettilääseen, ennenkuin hän suorittaa uhrin, jonka (taivaan) tuli kuluttaa», sano (heille Muhammed): »Onhan ennen minua tullut luoksenne lähettiläitä suorittaen ihmetekoja ja tehden sellaista, josta te mainitsette; miksi te olette sitten heidätkin surmanneet, jos nyt tahdotte pysyä totuudessa?»');
INSERT INTO "finnish" VALUES(3,183,'3:183','Ja jos he pitävät sinua valehtelijana, niin onhan jo ennen sinua pidetty valehtelijoina lähettiläitä, jotka ovat ihmeitä tehneet sekä tuoneet Psalmit ja Valaisevan kirjan.');
INSERT INTO "finnish" VALUES(3,184,'3:184','Jokainen sielu on maistava kuoleman, ja teille maksetaan Ylösnousemuksen päivänä oikea palkkanne, mutta se, joka välttää Tulen ja saa astua Paratiisiin, on autuas, jota vastoin maailman elämä on vain pettävää tavaraa.');
INSERT INTO "finnish" VALUES(3,185,'3:185','Sekä teidän omaisuutenne että teidät itsenne tullaan totisesti panemaan koetukselle, ja te saatte kuulla paljon pahaa sekä niiltä, jotka ovat Pyhän kirjan saaneet ennen teitä, että monijumalaisilta, mutta jos olette kestäviä ja vältätte pahaa, niin on se sankaruutta elämänvaiheissa.');
INSERT INTO "finnish" VALUES(3,186,'3:186','Jumala asetti velvollisuuden niille, jotka Pyhän kirjan saivat (juutalaisille), näin sanoen: »Teidän tulee selittää sitä ihmisille eikä salata sitä». Mutta he heittivät sen selkänsä taakse ja vaihtoivat sen mitättömiin arvoihin. Mikä surkea vaihtokauppa!');
INSERT INTO "finnish" VALUES(3,187,'3:187','Älä luule, että ne, jotka kerskuvat teoistaan ja kärkkyvät ylistystä siitä, mitä eivät ole tehneet, (älä luule) että he säästyvät kuritukselta. Ei, heitä odottaa tuskallinen rangaistus.');
INSERT INTO "finnish" VALUES(3,188,'3:188','Jumalalle kuuluu taivasten ja maan herruus, totisesti Jumala on kaikkivaltias.');
INSERT INTO "finnish" VALUES(3,189,'3:189','Taivasten ja maan luominen sekä yön ja päivän vaihtelu ovat totisesti tunnusmerkkejä niille, joilla on ymmärrystä,');
INSERT INTO "finnish" VALUES(3,190,'3:190','jotka muistavat Jumalaa seisoessaan, istuessaan ja maatessaan ja miettivät taivasten ja maan luomista sanoen: »Herra, Sinä et ole luonut tätä turhaan. Kunnia olkoon yksin Sinulle! Säästä meitä Tulen tuskilta!');
INSERT INTO "finnish" VALUES(3,191,'3:191','Herra, sen, jonka heität Tuleen, hänet olet syössyt häpeään. Ja turmeltuneet jäävät ilman auttajaa.');
INSERT INTO "finnish" VALUES(3,192,'3:192','Herra, me olemme kuulleet julistajan kehoittavan meitä uskoon sanoen: »Uskokaa Herraanne!» ja me olemme uskoneet. Siksi, Herra, anna anteeksi meidän syntimme, pyyhi pois pahat työmme ja anna meidän kuolla vanhurskasten kuolemalla.');
INSERT INTO "finnish" VALUES(3,193,'3:193','Ja anna meille, Herra, mitä olet luvannut meille lähettiläittesi kautta, äläkä salli meidän joutua häpeään Ylösnousemuksen päivänä! Ethän petä lupaustasi.»');
INSERT INTO "finnish" VALUES(3,194,'3:194','Niin kuuli heidän Herransa heitä (ja sanoi): »Minä en anna tekijän työn mennä hukkaan teidän keskuudessanne, olkoon hän mies tai nainen, toinen toisestannehan te polveudutte. Myöskin niiltä, jotka ovat lähteneet kodeistaan tai jotka on karkoitettu sieltä, ja jotka ovat kärsineet Minun asiani puolesta, taistelleet ja kaatuneet, olen totisesti pois pyyhkivä heidän pahat työnsä ja päästävä heidät huvitarhoihin, joissa purot solisevat. Tämä on oleva heille palkkana Jumalalta; sillä Jumala palkitsee parhaiten.');
INSERT INTO "finnish" VALUES(3,195,'3:195','Älä anna kotiseutusi (Mekan) uskottomien rikkauksien häikäistä itseäsi, Muhammed!');
INSERT INTO "finnish" VALUES(3,196,'3:196','Niistä on lyhytaikainen ilo, sitten on Helvetti oleva heidän kotinsa. Mikä surkea olinpaikka!');
INSERT INTO "finnish" VALUES(3,197,'3:197','Mutta niitä, jotka pelkäävät Herraansa, odottavat huvitarhat, joissa purot solisevat, ja siellä he ikuisesti viihtyvät. Sellaiset ovat Jumalan armolahjat; niin, Jumalalla on tallella kaikkein parasta vanhurskaille.');
INSERT INTO "finnish" VALUES(3,198,'3:198','Katso, Pyhän kirjan omaavien joukossa on sellaisia, jotka uskovat Jumalaan, siihen, mikä on ilmoitettu teille ja mikä on ilmoitettu heille, jotka nöyrtyvät Jumalan edessä eivätkä vaihda Jumalan tunnusmerkkejä mitättömän arvoihin.');
INSERT INTO "finnish" VALUES(3,199,'3:199','Nämä totisesti saavat palkkansa Herralta; Jumala on nopea tekemään tiliä.');
INSERT INTO "finnish" VALUES(3,200,'3:200','Te jotka uskotte, olkaa kärsivällisiä, kilvoitelkaa keskenänne kärsivällisyydessä, olkaa lujia ja pelätkää Jumalaa, jotta tulisitte onnellisiksi!');
INSERT INTO "finnish" VALUES(4,1,'4:1','Ihmiset, pitäkää kunniassa Herraanne, joka on teidät luonut yhdestä hengestä ja hänestä luonut hänen puolisonsa ja näistä molemmista antanut lukuisain miesten ja naisten levitä maailmaan. Pitäkää kunniassa Jumalaa, jonka nimessä toinen toiseltanne anotte ja vaaditte; myöskin kunnioittakaa verisiteitä (niitä kohtuja, jotka ovat kantaneet teidät). Katso, Jumala valvoo ja vartioi teidän elämäänne.');
INSERT INTO "finnish" VALUES(4,2,'4:2','Ja antakaa orvoille heidän omaisuutensa; älkää (holhoojina) vaihtako hyvää huonoksi älkääkä kuluttako heidän omaisuuttansa sekoittamalla sitä omaanne. Se olisi totisesti suuri synti.');
INSERT INTO "finnish" VALUES(4,3,'4:3','Jos pelkäätte, ett''ette voi menetellä oikeudenmukaisesti orpoja kohtaan, naikaa silloin ne naispuoliset (orpojen joukosta), joista pidätte, kaksi, kolme tai neljä, mutta jos pelkäätte, ett'' ette voisi olla tasapuolisia noin useata kohtaan, niin naikaa vain yksi ainoa heistä tai niistä orjattarista, joita omistatte. Tämä on mukavin tapa välttää vääryyttä.');
INSERT INTO "finnish" VALUES(4,4,'4:4','Jättäkää suosiolla vaimoillennekin lahjaksi heidän myötäjäisensä , mutta jos he hyvästä tahdostaan luovuttavat teille jotakin takaisin, niin pitäkää se ilomielin hyvänänne.');
INSERT INTO "finnish" VALUES(4,5,'4:5','Älkää antako ymmärtämättömille alaikäisille heidän omaisuuttansa, jonka Jumala on antanut hallittavaksenne, vaan elättäkää heitä sillä ja vaatettakaa heidät ja puhukaa heille suosiollisesti.');
INSERT INTO "finnish" VALUES(4,6,'4:6','Ja seuratkaa orpojen (henkistä) kehitystä, kunnes he saavuttavat naimaiän, ja jos silloin havaitsette heidät tervejärkisiksi, niin luovuttakaa heille itselleen heidän omaisuutensa. Varokaa tuhlaamasta sitä kevytmielisesti ja hätiköiden, ennenkuin he ovat täysi-ikäisiä. Rikas (holhooja) jättäköön holhokkien omaisuuden käyttämättä, ja se, joka itse on köyhä, kuluttakoon siitä vain kohtuulliseksi harkitun osan. Kun te luovutatte heille heidän omaisuutensa, niin olkoot teillä todistajat läsnä. Jumala valvoo tarkasti tilien tekoa.');
INSERT INTO "finnish" VALUES(4,7,'4:7','Miehiset perijät saakoot osan siitä, mitä vanhemmat ja lähimmät omaiset ovat jälkeensä jättäneet, ja naispuolisetkin perilliset saakoot osan siitä, mitä vanhemmat ja lähimmät omaiset ovat jättäneet, olkoon perintö vähäinen tai suuri - laki määrää siitä kullekin hänen osuutensa.');
INSERT INTO "finnish" VALUES(4,8,'4:8','Kun kaukaiset sukulaiset, orvot ja köyhät ovat läsnä perinnönjaossa, niin antakaa heillekin jotain ja puhukaa heille ystävällisesti.');
INSERT INTO "finnish" VALUES(4,9,'4:9','Ja ne, jotka itse olisivat huolestuneita, jos jättäisivät jälkeensä pieniä lapsia, varokoot (vääryydentekoa holhokeilleen), pelätkööt Jumalaa ja puhukoot mikä on totta.');
INSERT INTO "finnish" VALUES(4,10,'4:10','Ne, jotka vääryydellä nielevät orpojen omaisuuden, nielevät totisesti vain tulta vatsaansa, ja he tulevat Helvetin tulessa palamaan.');
INSERT INTO "finnish" VALUES(4,11,'4:11','Jumala määrää lastenne suhteen: Miehinen perillinen saakoon yhtä paljon kuin kaksi naispuolista yhteensä, mutta jos näitä tyttölapsia on enemmän kuin kaksi, niin saakoot he kaksi kolmattaosaa siitä, mitä vainaja on jälkeensä jättänyt, ja jos on yksi ainoa, saakoon hän puolet. Vainajan vanhemmat saakoot kumpikin kuudennen osan hänen jäämistöstään, jos häneltä jäi lapsia; mutta jos häneltä ei jäänyt lapsia, vaan vanhemmat yksin perivät hänet, niin saakoon äiti kolmannen osan, ja jos hänellä (vainajalla) on veljiä, niin saakoon äiti kuudennen osan, kaikki tämä kuitenkin vasta senjälkeen, kuin testamentin määräämät erät tai velka on vähennetty. Mitä tulee teidän vanhempiinne ja lapsiinne, niin ette voi tietää, kummat saattavat olla teille hyödyllisemmät. Kaikki tämä on Jumalan määräys; Jumala on totisesti tietävä, viisas.');
INSERT INTO "finnish" VALUES(4,12,'4:12','Ja teille kuuluu puolet siitä, mitä vaimonne ovat jälkeensä jättäneet, jollei heillä ole lapsia; mutta jos heillä on lapsia, tulee osallenne neljäs osa vaimojenne jäämistöstä, senjälkeen kuin heidän mahdolliset testamenttimääräyksensä ovat täytetyt tai velka on vähennetty. He (vaimot) saakoot puolestaan neljännen osan siitä, mitä te olette jälkeenne jättäneet, jollei teiltä jää lapsia; mutta jos teiltä jää lapsia, niin tulee heidän osalleen kahdeksas osa teidän jäämistöstänne, senjälkeen kuin testamentin määräykset on täytetty tai velka on vähennetty. jos miehellä tai naisella, joka jättää perinnön jälkeensä (ei ole vanhempia eikä lapsia, mutta) on veli tai sisar, niin saakoot kumpikin kuudennen osan, senjälkeen kuin testamentin määräykset on täytetty tai velka on vähennetty, loukkaamatta kenenkään oikeutta. Tämä on Jumalan säännös; katso, Jumala on tietävä, lempeä.');
INSERT INTO "finnish" VALUES(4,13,'4:13','Nämä ovat Jumalan asetukset. Sen, joka tottelee Jumalaa ja Hänen lähettilästään, Hän on päästävä huvitarhoihin, joissa purot solisevat ja jossa hän ikuisesti on viihtyvä, tämä on suuri autuus.');
INSERT INTO "finnish" VALUES(4,14,'4:14','Mutta sen, joka on tottelematon Jumalalle ja Hänen lähettiläälleen ja rikkoo Hänen asetuksiaan, Hän on johdattava Tuleen, jossa hän iankaikkisesti on pysyvä; häntä odottaa häpeällinen rangaistus.');
INSERT INTO "finnish" VALUES(4,15,'4:15','Niitä teidän naisianne vastaan, jotka tekevät huorin, on teidän kutsuttava joukostanne neljä todistajaa; jos he todistavat heidät syyllisiksi, niin pitäkää heidät eristettyinä asunnoissanne, kunnes kuolema heidät korjaa tai Jumala osoittaa heitä varten jonkun muun mahdollisuuden (uuden lainsäädännön kautta).');
INSERT INTO "finnish" VALUES(4,16,'4:16','Sellaiseen häpeään syyllistyviä on kumpaakin rangaistava, mutta jos he katuvat ja tekevät parannuksen, niin jättäkää heidät rauhaan. Katso, Jumala on sääliväinen ja laupias.');
INSERT INTO "finnish" VALUES(4,17,'4:17','Sääliä riittää Jumalalta ainoastaan niille, jotka tekevät pahaa tietämättömyydessään, mutta sitten viipymättä katuvat. Näitä on Jumala säälivä, sillä Jumala on tietävä, viisas.');
INSERT INTO "finnish" VALUES(4,18,'4:18','Mutta mitään armoa ei ole niille, jotka tekevät pahoja töitä aina siihen saakka, kunnes ovat kuolemaisillaan ja silloin sanovat: »Nyt minä teen totisesti parannuksen», eikä niille, jotka kuolevat uskottomuudessa; heille Me olemme valmistanut tuskallisen rangaistuksen.');
INSERT INTO "finnish" VALUES(4,19,'4:19','Teidän, jotka uskotte, ei ole lupa ottaa (edesmenneiden sukulaistenne) naisilta, vastoin heidän tahtoaan, heidän perintöään, eikä estää heitä menemästä naimisiin toisen kanssa, ottaaksenne heiltä takaisin osan siitä, minkä olette heille antaneet, paitsi jos he syyllistyvät ilmeiseen huoruuteen; vaan kohdelkaa heitä hyväntahtoisesti. Jos tunnette vastenmielisyyttä heitä kohtaan, niin voi olla, että vastenmielisyytenne kohdistuukin sellaiseen henkilöön, johon Jumala on kätkenyt paljon hyvää.');
INSERT INTO "finnish" VALUES(4,20,'4:20','Jos haluatte ottaa (uuden) vaimon toisen (erottamanne) sijaan ja olette antaneet jälkimäiselle vaikkapa suurenkin rahasumman, niin älkää ottako mitään siitä takaisin. Kuinka anastaisittekaan sitä väärin syytöksin ja ilmeisesti syntiä tehden?');
INSERT INTO "finnish" VALUES(4,21,'4:21','Ja kuinka voisittekaan anastaa sen, kun olette viettäneet läheistä yhdyselämää toistenne kanssa ja he (vaimonne) ovat saaneet teidän juhlalliset liittolupauksenne?');
INSERT INTO "finnish" VALUES(4,22,'4:22','Älkää naiko niitä naisia, jotka isänne ovat naineet (ja hylänneet); poikkeus sallittakoon jo aikoja sitten tapahtuneeseen nähden. Sellainen on totisesti irstasta ja inhottavaa; se on oikeiden elämäntapojen turmelemista.');
INSERT INTO "finnish" VALUES(4,23,'4:23','Kiellettyjä teiltä ovat äitinne, tyttärenne ja siskonne, sekä isän- että äidinpuoleiset tätinne, veljentyttärenne ja sisarentyttärenne, imettäjänne, jotka teitä ovat imettäneet, ja heidän tyttärensä, vaimojenne äidit ja tytärpuolenne, jotka ovat holhouksenne alaisia ja (aikoinaan) syntyneet vaimoista, joiden kanssa olette yhteydessä olleet - mutta jos ette ole olleet heidän kanssaan yhteydessä, niin ei teille lueta synniksi (naida tytärpuoli) - ja edelleen omaa vertanne olevien poikienne vaimot. Kielletty on myös naimisiin meno kahden sisaren kanssa samalla kertaa - poikkeus on sallittu jo aikaa sitten tapahtuneeseen nähden. Katso, Jumala on pitkämielinen ja laupias.');
INSERT INTO "finnish" VALUES(4,24,'4:24','Samoin (ovat kielletyt teiltä) kaikki naidut naiset lukuunottamatta niitä orjattaria, jotka ovat joutuneet haltuunne (oston tai sodan kautta). Nämä rajat on Jumala teille määrännyt. Mutta kaikki, mitä tämä ei rajoita, on teille sallittua, joten voitte omaisuudellanne tavoitella muita naisia, elääksenne heidän kanssaan aviossa, harjoittamatta haureutta. Mutta teidän on annettava aviolahja niille, joilta etsitte yhdyselämän nautintoa. Siitä ei teille syntiä koidu, mitä ehkä yli velvollisuuden (s.o. aviolahjan) sovitte keskenänne; Jumala on kaikissa asioissa tietävä, viisas.');
INSERT INTO "finnish" VALUES(4,25,'4:25','Kenellä teistä ei ole varaa naida vartioituna kasvatettua uskovaista naista, ottakoon vaimokseen jonkun niistä uskovaisista palvelijattarista (orjattarista), jotka ovat vallassanne (heimonne keskuudessa), sillä Jumala tutkii parhaiten uskonne, ja te polveudutte toinen toisistanne. Naikaa heidät isäntiensä luvalla ja antakaa heille lahja sen mukaan, mikä kohtuullista on, ja heidän on elettävä kunniallisten naisten tavoin, niiden tavoin, jotka eivät harjoita haureutta julkisesti eivätkä omista rakastajaa salassa. Jos he, ollessaan naimisissa, rikkovat häpeällisesti avioliittoa vastaan, on heidän kärsittävä puolet siitä rangaistuksesta, mikä on säädetty vartioituina kasvatetuille naisille. Tämä on (sanottu) sille joukossanne, joka karttaa paheellisuutta; kuitenkin parempi olisi teille pidättyväisyys. Jumala on pitkämielinen ja laupias.');
INSERT INTO "finnish" VALUES(4,26,'4:26','Jumala tahtoo selittää tämän teille ja ohjata teidät niiden tielle, jotka ovat ennen teitä eläneet, ja armossaan kääntyä teidän katuvaisten puoleen. Jumala on tietävä, viisas.');
INSERT INTO "finnish" VALUES(4,27,'4:27','Jumala tahtoo peittää teidät laupeudellaan, mutta ne, jotka seuraavat himojansa, tahtovat, että te kääntyisitte kauas harhateille.');
INSERT INTO "finnish" VALUES(4,28,'4:28','Jumala tahtoo tehdä teidän taakkanne helpoksi, sillä ihminen on luotu heikoksi.');
INSERT INTO "finnish" VALUES(4,29,'4:29','Te, jotka uskotte, älkää kuluttako keskenänne omaisuuttanne turhuuksiin , paitsi jos (joku menettää sitä) jonkin liikeasian kautta, joka perustui keskinäiseen sopimukseen; älkääkä surmaa tehkö . Jumala on totisesti laupias teitä kohtaan,');
INSERT INTO "finnish" VALUES(4,30,'4:30','mutta sen, joka näin tekee vihamielisyydessä ja vääryydessä, Me olemme Tulessa polttava, sillä se on helppoa Jumalalle.');
INSERT INTO "finnish" VALUES(4,31,'4:31','Jos kartatte raskaita syntejä, jotka ehdottomasti kielletään teiltä, niin Me pyyhimme pois teidän pahat tekonne ja johdatamme teidät kunniaportista Paratiisiin.');
INSERT INTO "finnish" VALUES(4,32,'4:32','Älkää himoitko sellaista, johon nähden Jumala on asettanut jotkut toisten suhteen korkeampaan asemaan. Miehille riittäköön rikkautta siitä, mitä he ovat ansaitsemalla hankkineet, ja naisille siitä, mitä he ovat ansainneet. (Kadehtimatta toisianne) rukoilkaa (lahjoja) Jumalalta Hänen runsaudestaan.Katso, Jumala on totisesti kaikkitietävä.');
INSERT INTO "finnish" VALUES(4,33,'4:33','Jokaisen kohdalta Me olemme määrännyt perilliset sille,minkä vanhemmat ja lähimmät omaiset ovat jälkeensä jättäneet. Niille, joiden kanssa olette solmineet valallisen liiton, on teidän myös annettava heidän perintöosansa; Jumala on totisesti kaiken todistaja.');
INSERT INTO "finnish" VALUES(4,34,'4:34','Miehet olkoot naisten esimiehiä, koska Jumala on asettanut heidät näihin nähden korkeampaan asemaan, ja myöskin niiden suoritusten tähden, joita miesten on omaisuudestaan naisten hyväksi tehtävä. Hyveelliset naiset ovat alistuvaisia ja vaalivat huolellisesti, kätkössä, kaikkea sitä, minkä Jumala on kätköön tarkoittanut. Mitä niihin tulee, joiden puolelta kohtaatte uppiniskaisuutta, niin varoittakaa heitä, erottakaa heidät vuoteestanne ja kurittakaa heitä, mutta jos he silloin tottelevat teitä, niin älkää etsikö riitaa heidän kanssaan. Katso, Jumala on ylevä ja suuri.');
INSERT INTO "finnish" VALUES(4,35,'4:35','jos pelkäätte välien rikkoutumista miehen ja vaimon kesken, niin lähettäkää (heidän luokseen) erotuomariksi joku miehen suvusta ja toinen vaimon suvusta. Jos he toivovat sovintoa, niin Jumala saa aikaan yhteisymmärryksen heidän välillänsä; Jumala on totisesti tietävä, viisas.');
INSERT INTO "finnish" VALUES(4,36,'4:36','Palvelkaa Jumalaa älkääkä mitään asettako Hänen rinnalleen. Kohdelkaa hyvin vanhempianne, samoin läheisiä omaisianne, orpoja ja köyhiä, naapuria, joka on teille sukua, ja naapuria, joka ei ole teille sukua, matkakumppania, vaeltajaa ja orjia, jotka ovat vallassanne. Katso, Jumala ei rakasta niitä, jotka ovat väkivaltaisia ja kerskailevia,');
INSERT INTO "finnish" VALUES(4,37,'4:37','jotka ovat saitoja ja herättävät toisissakin ahneutta sekä vaikenevat siitä, minkä Jumala on runsaudessaan heille suonut. Jumalattomille olemme valmistanut häpeällisen rangaistuksen.');
INSERT INTO "finnish" VALUES(4,38,'4:38','Eikä niitäkään (Jumala rakasta), jotka uhraavat varojaan ulkokultaisuudessa ihmisten nähden, mutta eivät usko Jumalaan eivätkä Viimeiseen päivään. Kuka ottaa Saatanan kumppanikseen, saa hänestä julman kumppanin.');
INSERT INTO "finnish" VALUES(4,39,'4:39','Mutta mitä (pelkoa) olisi niillä, jotka uskovat Jumalaan ja Viimeiseen päivään ja tuovat rehellisesti uhrin siitä, mitä Jumala on heille suonut? Jumala tuntee heidät (kaikkine töineen).');
INSERT INTO "finnish" VALUES(4,40,'4:40','Totisesti Jumala ei tee kenellekään vääryyttä hitusenkaan vertaa, ja jos on olemassa hyvä työ, palkitsee Hän sen kaksinkertaisesti ja antaa luonansa suuren korvauksen.');
INSERT INTO "finnish" VALUES(4,41,'4:41','Mutta kuinka käykään, kun Me tuomme esiin todistajan jokaisesta kansasta ja asetamme sinut, Muhammed, todistajaksi heitä vastaan?');
INSERT INTO "finnish" VALUES(4,42,'4:42','Sinä päivänä ne, jotka olivat uskottomia ja tottelemattomia profeetalle, toivovat, että maa heidät peittäisi, eivätkä he pysty salaamaan mitään Jumalalta.');
INSERT INTO "finnish" VALUES(4,43,'4:43','Te, jotka uskotte, älkää käykö rukoilemaan ollessanne humalassa, ennenkuin tiedätte mitä sanotte, älkääkä tahraantuneina, ennenkuin olette peseytyneet, paitsi jos olette matkoilla. Jos olette sairaita tai matkalla tai tulette käymälästä tai olette olleet yhteydessä naisten kanssa eikä teillä ole vettä saatavissa, niin ottakaa hienoa, puhdasta hiekkaa ja hangatkaa kasvonne ja kätenne sillä. Jumala on lempeä ja armahtava.');
INSERT INTO "finnish" VALUES(4,44,'4:44','Etkö ole huomannut, miten ne, jotka ovat saaneet osan Pyhää kirjaa, kartuttavat harhauskoa ja soisivat teidänkin (muslimien) eksyvän oikealta tieltä.');
INSERT INTO "finnish" VALUES(4,45,'4:45','Mutta Jumala tuntee parhaiten teidän vihollisenne; ja Jumala on kyllin hyvä Suojelija, Jumala on kyllin hyvä Auttaja.');
INSERT INTO "finnish" VALUES(4,46,'4:46','Juutalaisuuteen taipuvien keskuudessa on sellaisia, jotka sekoittavat sanat pois niiden oikeasta yhteydestä ja ääntäen niitä väärin parjaavat kielillään Uskoa (Islaamia) sanoen: »samicnaa wa casainaa» ja »ismac ghaira musmacin» ja »raacinaa» . Jos he sanoisivat »samicnaa wa atacnaa» ja »ismac» ja »unzurnaa» , niin olisi se heille parempi ja oikeampi, mutta Jumala on kironnut heidät heidän uskottomuutenta tähden; ainoastaan harvat heistä ovat uskovaisia.');
INSERT INTO "finnish" VALUES(4,47,'4:47','Te jotka olette saaneet Pyhän kirjan, uskokaa siihen, mitä Me olemme ilmoittanut (Koraanissa) vahvistaen sen, minkä jo omistatte (Vanhan testamentin), (uskokaa) ennenkuin Me synkennämme ja vääristämme teidän kasvojenne piirteet tai kiroamme teidät samoin kuin Me kirosimme sapatinrikkojat , sillä Jumalan käskyt käyvät varmasti täytäntöön.');
INSERT INTO "finnish" VALUES(4,48,'4:48','Totisesti Jumala ei anna anteeksi, että palvellaan jotakuta Hänen vertaisenaan. Hän saattaa antaa anteeksi, kenelle tahtoo, muun kaiken paitsi tämän, sillä se, joka asettaa Jumalalle vertaisia, tekee kauhean synnin.');
INSERT INTO "finnish" VALUES(4,49,'4:49','Etkö ole katsellut sellaisia, jotka tekeytyvät synnittömiksi? Ei, Jumala yksin vanhurskauttaa kenet tahtoo, eikä heille tehdä hituistakaan vääryyttä.');
INSERT INTO "finnish" VALUES(4,50,'4:50','Katso, kuinka he keksivät valheita Jumalasta. Tämä itsessään on jo julkea synti.');
INSERT INTO "finnish" VALUES(4,51,'4:51','Etkö ole katsellut niitä (Arabian juutalaisia), jotka ovat saaneet osan Pyhästä kirjasta? He uskovat Dshibt- ja Taaguutepäjumaliin ja sanovat monijumalaisista: »Nämä ovat paremmalla tiellä kuin uskovaiset (muslimit)».');
INSERT INTO "finnish" VALUES(4,52,'4:52','He ovat niitä, jotka Jumala on kironnut, ja sille, jonka Jumala on kironnut, sinä et koskaan ole löytävä auttajaa.');
INSERT INTO "finnish" VALUES(4,53,'4:53','Olisiko heilläkin osansa (Messiaan) valtakunnassa? Silloin he eivät antaisi (siitä) ihmisille vähääkään .');
INSERT INTO "finnish" VALUES(4,54,'4:54','Tai kadehtivatko he ihmisiä siitä, mitä Jumala runsaudessaan on heille suonut? Olemmehan Me lähettänyt Aabrahamin jälkeläisille Pyhän kirjan ja viisauden, vieläpä annoimme heille mahtavan valtakunnan.');
INSERT INTO "finnish" VALUES(4,55,'4:55','Osa heistä otti sen vastaan uskoen, ja osa heistä suhtautui siihen epäuskoisesti. Helvetissä riittää tulta heidän palamistansa varten.');
INSERT INTO "finnish" VALUES(4,56,'4:56','Ne, jotka hylkivät Meidän ilmoitustamme, poltamme Me totisesti Tulessa. Niin usein kuin heidän ihonsa kärventyy, annamme sen sijalle uuden, jotta he maistaisivat tuskaa, Jumala on totisesti mahtava ja viisas.');
INSERT INTO "finnish" VALUES(4,57,'4:57','Mutta ne, jotka uskovat ja tekevät hyviä töitä, päästämme Me huvitarhoihin, joissa purot solisevat, ja he viihtyvät siellä ikuisesti; siellä odottavat heitä puhtaat puolisot, ja Me sijoitamme heidät vilpoisiin varjopaikkoihin.');
INSERT INTO "finnish" VALUES(4,58,'4:58','Katso, Jumala käskee teidän palauttaa uskotun omaisuuden sen omistajalle ja tuomitsemaan rehellisesti, kun tuomitsette ihmisten kesken. Ihanaa on totisesti se, mihin Jumala teitä kutsuu; katso, Jumala on kuuleva, näkevä.');
INSERT INTO "finnish" VALUES(4,59,'4:59','Te, jotka uskotte, totelkaa Jumalaa ja totelkaa profeettaa ja niitä teidän keskuudessanne, joilla on arvovalta. jos kiistelette jostakin, niin alistakaa se Jumalan ja Hänen lähettiläänsä ratkaistavaksi, jos todellakin uskotte Jumalaan ja Viimeiseen päivään. Tämä on parasta teille ja edullisintakin.');
INSERT INTO "finnish" VALUES(4,60,'4:60','Etkö ole katsellut niitä, jotka väittävät uskovansa siihen, mikä on ilmoitettu sinulle ja mikä on ilmoitettu ennen sinua? He haluavat (kiistellessään) vedota Taaguut~epäjumalaan, vaikka heidät on velvoitettu luopumaan hänestä. Mutta Saatana tahtoo johdattaa heidät äärimmäiseen harhaan.');
INSERT INTO "finnish" VALUES(4,61,'4:61','Ja kun heille sanotaan: »Tulkaa tänne Jumalan ilmoituksen ja profeetan eteen!» sinä näet ulkokultaisten kääntyvän pois heittäen sinulle niskojaan.');
INSERT INTO "finnish" VALUES(4,62,'4:62','Mutta kuinka käy, kun joku koettelemus panee heidät ahtaalle heidän kättensä töiden tähden? Silloin he tulevat sinun luoksesi vannoen Jumalan kautta: »Emme muuta tavoitelleet kuin hyvää ja sopua.»');
INSERT INTO "finnish" VALUES(4,63,'4:63','He ovat niitä, joiden sydämen salat Jumala tuntee; sentähden vastusta heitä, ojenna heitä ja puhu heille selvää kieltä, joka tunkee heidän sielunsa lävitse.');
INSERT INTO "finnish" VALUES(4,64,'4:64','Siksihän Me lähetimme profeettoja, että heitä Jumalan määräyksestä toteltaisiin; mutta jos he rikottuaan itseään vastaan tulevat luoksesi ja rukoilevat Jumalalta armahdusta ja profeetalta auttavaa välitystä, niin he havaitsevat Jumalan sääliväiseksi ja laupiaaksi.');
INSERT INTO "finnish" VALUES(4,65,'4:65','Ei, vannon Jumalasi kautta, ett''eivät he ole tosiuskovaisia, ennenkuin ottavat sinut erotuomariksi keskinäisissä kiistoissaan tuntematta sisimmässään mielikarvautta sinun langettamasi päätöksen johdosta ja täydellisesti alistuen.');
INSERT INTO "finnish" VALUES(4,66,'4:66','Jos Me olisimme antanut heille määräyksen: »Uhratkaa henkenne tai lähtekää kodeistanne!» ei monikaan olisi niin tehnyt; jos he tekisivät sen, mitä heille on säädetty, niin olisi se heille parempi ja vahvistaisi heitä.');
INSERT INTO "finnish" VALUES(4,67,'4:67','ja silloin Me antaisimme totisesti heille suuren palkinnon luonamme');
INSERT INTO "finnish" VALUES(4,68,'4:68','ja opastaisimme heidät varmasti oikealle tielle.');
INSERT INTO "finnish" VALUES(4,69,'4:69','Ne jotka tottelevat Jumalaa ja Hänen lähettilästänsä, pääsevät niiden pariin, joille Jumala on osoittanut mielisuosionsa, nimittäin profeettojen, pyhimysten, marttyyrien ja vanhurskaiden luo; kuinka ihanaa on olla heidän seurassaan!');
INSERT INTO "finnish" VALUES(4,70,'4:70','Tämä on Jumalan suosion runsaus, ja Jumalan viisaus on riittävä.');
INSERT INTO "finnish" VALUES(4,71,'4:71','Te, jotka uskotte, varustautukaa sotaretkeä varten ja lähtekää liikkeelle osastoittain tai lähtekää kaikki yhdessä.');
INSERT INTO "finnish" VALUES(4,72,'4:72','Katso, keskuudessanne on joku sellainenkin, joka vitkastelee, ja jos teille sattuu vastoinkäyminen, hän sanoo: »Jumala on kääntänyt kaiken minun parhaakseni, koska en ollut paikalla heidän kanssaan.»');
INSERT INTO "finnish" VALUES(4,73,'4:73','Mutta jos Jumalan armo (s.o. voitto) tulee teidän osaksenne, silloin hän varmasti huudahtaa (aivan kuin te olisitte vieroksuneet häntä): »Olisinpa päässyt heidän pariinsa, niin olisin perinyt suuren voiton.»');
INSERT INTO "finnish" VALUES(4,74,'4:74','Taistelkoot siis Jumalan retkellä ne, jotka tämän elämän hinnalla tavoittelevat tulevaa. Sille, joka taistelee Jumalan retkellä, kaatuupa hän tai voittaa, annamme Me jalon palkinnon.');
INSERT INTO "finnish" VALUES(4,75,'4:75','Ja, kuinka olisittekaan taistelematta Jumalan asian puolesta ja niiden heikkojen miesten, naisten ja lasten puolesta, jotka huutavat: »Herra, johda meidät pois tästä kaupungista , pois siinä asuvien sortajain luota, lähetä meille suojelija, anna meille puolustaja!');
INSERT INTO "finnish" VALUES(4,76,'4:76','Uskovaiset taistelevat Jumalan asian puolesta, ja ne jotka ovat uskottomia, taistelevat Taaguutin asian puolesta; taistelkaa te siis Saatanan kannattajia vastaan. Saatanan sotajuonet ovat hatarat.');
INSERT INTO "finnish" VALUES(4,77,'4:77','Etkö ole katsellut niitä, joille sanottiin: »Pitäkää kätenne loitolla , harjoittakaa rukoilemista ja antakaa almuja.» Ja kun heidät sitten määrättiin taistelemaan, niin toiset heistä pelkäsivät ihmisiä yhtä paljon tai enemmän kuin Jumalaa ja sanoivat: »Herramme, miksi olet määrännyt meidät taistelemaan? Miksi et anna meille lykkäystä vielä joksikin aikaa?» Sano (heille, Muhammed): »Tämän elämän lohtu on vähäinen, mutta tuleva elämä on parempi niille, jotka pelkäävät Jumalaa. Eikä teille tapahdu hituistakan vääryyttä.');
INSERT INTO "finnish" VALUES(4,78,'4:78','Missä ikinä olettekin, on kuolema saavuttava teidät, vaikkapa asuisitte korkeissa torneissa.» Jos jokin onni heitä kohtaa, sanovat he: »Tämä tulee Jumalalta», mutta jos heille sattuu pahaa, sanovat he: »Tämä on sinusta lähtöisin (Muhammed).» Sano: »Kaikki tulee Jumalalta.» Mutta mikä vaivaa näitä ihmisiä, etteivät he ymmärrä tulkita tapahtumia?');
INSERT INTO "finnish" VALUES(4,79,'4:79','Kaikki hyvä, mikä osaksesi tulee (ihminen), tulee Jumalalta, ja kaikki paha, mikä sinua kohtaa, johtuu sinusta itsestäsi. Me olemme lähettänyt sinut (Muhammed) profeettana ihmisten luo; Jumala on kyllin riittävä todistaja.');
INSERT INTO "finnish" VALUES(4,80,'4:80','Se, joka tottelee profeettaa, tottelee Jumalaa; mutta jos joku kääntyy pois, emme ole lähettänyt sinua sellaisia vartioimaan.');
INSERT INTO "finnish" VALUES(4,81,'4:81','He sanovat: »Olemme kuuliaisia», mutta mentyään pois sinun luotasi hautovat muutamat heistä öisin päässään aivan muuta, kuin mitä sinä sanot. Jumala merkitsee muistiin kaiken, mitä he punovat; käännä kasvosi pois heistä ja luota Jumalaan! Sinulle riittää Jumala puolustajaksi.');
INSERT INTO "finnish" VALUES(4,82,'4:82','Eivätkö he sitten syvenny Koraaniin? Jos se ei olisi Jumalasta lähtöisin, niin he löytäisivät siitä lukuisia ristiriitaisuuksia.');
INSERT INTO "finnish" VALUES(4,83,'4:83','Ja kun heille tulee jokin tieto, joka herättää turvallisuuden tai pelon tunteen, he suoraa päätä levittävät sitä, sen sijaan että alistaisivat sen profeetan ja niiden (henkilöiden) tutkittavaksi, joilla on arvovaltaa; silloin arvostelukykyiset heidän joukossaan saisivat varman tiedon näiltä. Ilman Jumalan armoa ja laupeutta olisitte te, harvoja lukuunottamatta, jo käyneet Saatanaa seuraamaan.');
INSERT INTO "finnish" VALUES(4,84,'4:84','Taistele siis, Muhammed, Jumalan asian puolesta! Ainoastaan itsestäsi olet vastuussa. Kannusta oikeauskoisia! Ehkä Jumala lannistaa uskottomain mahdin. Jumala on mahtavin voimassa ja ankarin rangaistessa.');
INSERT INTO "finnish" VALUES(4,85,'4:85','Se, joka ryhtyy välittämään hyvässä asiassa, on saava osansa siitä, ja se, joka toimii välittäjänä pahassa, kantaa myös seuraukset siitä: katso, Jumala on kaikkivaltias.');
INSERT INTO "finnish" VALUES(4,86,'4:86','Kun teitä tervehditään (hyvällä) tervehdyksellä, niin tervehtikää takaisin vielä paremmalla tai vastatkaa samalla. Katso, Jumala pitää lukua kaikesta.');
INSERT INTO "finnish" VALUES(4,87,'4:87','Ei ole muuta jumalaa kuin Jumala. Hän on totisesti kokoava teidät Ylösnousemuksen päivänä, siitä ei ole epäilystäkään. Kuka on totuudellisempi sanoissaan kuin Jumala?');
INSERT INTO "finnish" VALUES(4,88,'4:88','Miksi jakaudutte kahteen ryhmään, kun on kysymys teeskentelijöistä? Jumala on syössyt heidät epäuskoon heidän tekojensa tähden. Haluatteko taluttaa niitä, jotka Jumala on eksyttänyt? Sille, jonka Jumala on eksyttänyt, sinä et pysty tietä löytämään.');
INSERT INTO "finnish" VALUES(4,89,'4:89','He toivoisivat, että te tulisitte uskottomiksi, aivan kuin he itse ovat, ja siten heidän kaltaisikseen. Älkää ottako itsellenne ystäviä heidän joukostaan, ennenkuin he lähtevät kodeistaan Jumalan retkelle. Jos he palaavat entiseen vihamielisyyteensä, niin ottakaa heidät kiinni ja surmatkaa, missä heidät kohtaattekin, älkääkä ottako itsellenne suojelijaa tai auttajaa heidän joukostaan.');
INSERT INTO "finnish" VALUES(4,90,'4:90','Poikkeus tehdään niihin nähden, jotka etsivät turvaa joltakin teidän kanssanne liitossa olevalta heimolta tai jotka ovat tulleet luoksenne, koska heidän sydämensä kieltää heitä taistelemasta teitä vastaan tai omaa kansaansa vastaan. Jos Jumala olisi tahtonut, olisi Hän voinut antaa heille ylivoiman, ja he olisivat varmasti sotineet teitä vastaan; mutta jos he pysyvät loitolla teistä eivätkä taistele teitä vastaan, vaan tarjoavat teille rauhaa, niin ei Jumala salli teidän hyökkäävän heidän kimppuunsa.');
INSERT INTO "finnish" VALUES(4,91,'4:91','Te tulette tekemisiin toisten kanssa, jotka pyrkivät saavuttaman sekä teidän luottamuksenne että oman kansansa luottamuksen, mutta niin usein kuin heitä vietellään kapinaan, lankeavat he. Jolleivät he pysy loitolla, elleivät tarjoa teille rauhaa ja laske aseitaan, niin ottakaa heidät kiinni ja surmatkaa, missä hyvänsä heidät kohtaattekin, sillä näiden suhteen olemme antanut teille täyden vallan.');
INSERT INTO "finnish" VALUES(4,92,'4:92','Uskovainen ei saa surmata toista uskovaista, jollei se ehkä tapahdu vahingossa. Jos joku vahingossa surmaa uskovaisen, sovittakoon sen vapauttamalla yhden uskovaisen orjan ja maksamalla täyden henkisakon surmatun suvulle, jolleivät he häntä siitä vapauta. Jos surmattu kuului viholliskansaan, mutta oli uskovainen, niin surmaaja sovittakoon sen vapauttamalla yhden uskovaisen orjan. Jos hän kuului kansaan, jonka kanssa te olette liitossa, sovittakoon surmaaja asian täydellä henkisakolla hänen suvulleen ja vapauttamalla yhden uskovaisen orjan. Se taas, jolla ei ole varoja orjan lunastamiseen, paastotkoon kaksi kuukautta peräkkäin. Tämä on Jumalan sovitus katumuksen kautta. Jumala on tietävä, viisas.');
INSERT INTO "finnish" VALUES(4,93,'4:93','Mutta sen palkka, joka tahallisesti surmaa uskovaisen, on iankaikkisesta oleva Helvetti, sillä Jumala on vihastunut häneen, kironnut hänet ja valmistanut hänelle peloittavan rangaistuksen.');
INSERT INTO "finnish" VALUES(4,94,'4:94','Te uskovaiset, kun lähdette Jumalan retkelle, niin tehkää erotus (muslimien ja uskottomien välillä), jotta ette kenellekään, joka tervehtii teitä (sanalla " Rauha") sanoisi: Sinä et ole uskovainen, tahtoen saada hänen omaisuutensa . Jumalan luona on saalista yllin kyllin. Sellaisia (kuin he nyt ovat) olette te ennen olleet, mutta Jumala on ollut armollinen teitä kohtaan; noudattakaa siis erotusta (uskovan ja uskottoman välillä)! Jumala totisesti aina tietää teidän tekonne.');
INSERT INTO "finnish" VALUES(4,95,'4:95','Ne uskovaisista, jotka (retken aikana) istuvat kotona, minkään vamman heitä siihen pakottamana, eivät tule toisten vertaisiksi, jotka kilvoittelevat Jumalan retkellä pannen alttiiksi omaisuutensa ja henkensä. Jumala on korottanut ne, jotka panevat omaisuutensa ja elämänsä vaaralle alttiiksi, arvossa niiden yläpuolelle, jotka kotona istuvat. Molemmille on Jumalalla hyviä lupauksia, mutta ne, jotka taistelevat, on Jumala palkitseva runsaasti yli niiden, jotka pysyvät paikoillaan.');
INSERT INTO "finnish" VALUES(4,96,'4:96','Hänellä on palkintojen arvoasteina anteeksianto ja laupeus. Jumala on pitkämielinen ja laupias.');
INSERT INTO "finnish" VALUES(4,97,'4:97','Niiltä, joita enkelit korjaavat täältä pois, kun he ovat rikkoneet itseään vastaan, kysyvät he (enkelit) totisesti: »Mitä osaa te esititte?» He vastaavat: »Me olimme sorrettuja maan päällä». Silloin he sanovat: »Eikö Jumalan maa ole tarpeeksi avara, jotta olisitte voineet siirtyä muualle?» Helvetti - mikä kauhea määränpää! - tulee asuinsijaksi näille,');
INSERT INTO "finnish" VALUES(4,98,'4:98','lukuunottamatta niitä miehiä, naisia ja lapsia, jotka ovat todella heikkoja, eivätkä osaa suunnitella (pakoa pois uskottomien parista) tai saada opastusta tiellä.');
INSERT INTO "finnish" VALUES(4,99,'4:99','Ehkä Jumala on säälivä heitä, sillä Jumala on lempeä ja pitkämielinen.');
INSERT INTO "finnish" VALUES(4,100,'4:100','Se joka siirtyy pois asuinsijoiltaan Jumalan asian vuoksi, on löytävä monta turvapaikkaa ja kaikkea yllinkyllin maan päällä, ja jos sen, joka jättää kotinsa turvautuen Jumalaan ja Hänen profeettaansa, yllättääkin kuolema, on hänen palkkansa Jumalalla tallella: Jumala on armollinen ja laupias.');
INSERT INTO "finnish" VALUES(4,101,'4:101','Kun olette vaelluksella, ei teille lueta syyksi, jos lyhennätte rukoustanne pelätessänne uskottoman hyökkäävän kimppuunne; uskottomat ovat totisesti teidän ilmeisiä vihollisianne.');
INSERT INTO "finnish" VALUES(4,102,'4:102','Ja kun sinä olet (Muhammed) heidän keskuudessaan ja asetat heidät rukoilemaan, niin seisköön vain osa (rukoilemassa) sinun kanssasi, kuitenkin olkoot heillä aseet mukanaan. Ja kun he ovat tehneet kumarruksensa, siirtykööt he jälkijoukkoon, ja tulkoon toinen osasto, joka ei vielä ole rukoillut, ja rukoilkoon sinun kanssasi; kuitenkin olkoot he varuillaan ja pitäkööt aseensa saapuvilla. Ne, jotka ovat uskottomia, toivoisivat, ettette pitäisi huolta aseistanne ja kuormastostanne, jotta he voisivat yhtäkkiä syöksyä päällenne, mutta teille ei lueta syyksi, jos panette pois aseenne sateen teitä rasittaessa tai sairaina ollessanne. Mutta noudattakaa yleensä varovaisuutta. Jumala on totisesti valmistanut uskottomille häpeällisen rangaistuksen.');
INSERT INTO "finnish" VALUES(4,103,'4:103','Kun olette suorittaneet hartautenne, niin muistelkaa Jumalaa sekä seisoessanne että istuessanne ja maatessanne. Ja kun olette turvassa, niin suorittakaa varsinaiset rukousmenot. Uskovaisille on totisesti säädetty rukoustoimitukset määrättyinä hetkinä.');
INSERT INTO "finnish" VALUES(4,104,'4:104','Älkää hellittäkö uskottomien takaa-ajoa. Jos te näette vaivaa, niin kärsivät hekin, kuten te kärsitte, ja te sitäpaitsi toivotte Jumalalta sitä, mistä heillä ei ole toivoa; niin, Jumala on tietävä, viisas.');
INSERT INTO "finnish" VALUES(4,105,'4:105','Me olemme ylhäältä antanut sinulle Pyhän kirjan, joka sisältää totuuden, jotta ihmisten kesken tuomitsisit sen mukaan, kuin Jumala on sinulle osoittanut. Älä ole kavalain puolustaja,');
INSERT INTO "finnish" VALUES(4,106,'4:106','vaan pyydä Jumalalta anteeksiantoa. Jumala on totisesti armahtava ja laupias.');
INSERT INTO "finnish" VALUES(4,107,'4:107','Äläkä puhu niiden puolesta, jotka itseään pettävät. Jumala ei totisesti rakasta sitä, joka on petollinen ja syntinen.');
INSERT INTO "finnish" VALUES(4,108,'4:108','He kätkeytyvät ihmisiltä, mutta eivät voi kätkeytyä Jumalalta; Hän on heidän luonaan, kun he öisin puhuvat sellaista, mikä ei Häntä miellytä, sillä Jumala piirittää kaikki heidän tekonsa tiedollaan.');
INSERT INTO "finnish" VALUES(4,109,'4:109','Katso, te olette puolustelleet heitä maallisessa elämässä, mutta kuka on puhuva heidän puolestaan Jumalan edessä Ylösnousemuksen päivänä tai kuka on oleva heidän suojelijansa?');
INSERT INTO "finnish" VALUES(4,110,'4:110','Se, joka tekee jotakin pahaa tai menettelee väärin omaa sieluansa kohtaan ja sitten pyytää Jumalalta anteeksi, on huomaava Jumalan pitkämieliseksi ja laupiaaksi.');
INSERT INTO "finnish" VALUES(4,111,'4:111','Kuka ikinä tekee synnin, tekee sen ainoastaan itseänsä vastaan; Jumalahan on tietävä, viisas.');
INSERT INTO "finnish" VALUES(4,112,'4:112','Ja se, joka tekee vahingon tai synnin ja sitten työntää syyn viattoman niskoille, kuormittaa itsensä panettelulla ja ilmeisellä synnillä.');
INSERT INTO "finnish" VALUES(4,113,'4:113','Ja ilman Jumalan mielisuosiota ja laupeutta sinua kohtaan (Muhammed), olisivat muutamat heistä päätöksensä mukaisesti eksyttäneet sinut, mutta he eksyttävät ainoastaan itsensä eivätkä vahingoita sinua vähääkään. Jumala on ojentanut sinulle Pyhän kirjan ja viisauden sekä opettanut sinulle sen, mitä et tietänyt, niin, Jumalan mielisuosio sinua kohtaan on ollut suuri.');
INSERT INTO "finnish" VALUES(4,114,'4:114','Useimmat heidän salaisista keskusteluistaan eivät sisällä mitään hyvää. Toisin on hänen laitansa, joka kehoittaa antamaan almuja, hyvää harjoittamaan tai sopua rakentamaan ihmisten kesken, sillä sille, joka näin tekee ikävöiden Jumalan mielisuosiota, annamme Me suuren palkinnon.');
INSERT INTO "finnish" VALUES(4,115,'4:115','Mutta sen, joka ryhtyy vastustamaan profeettaa, senjälkeen kuin oikea tie on hänelle selvinnyt, ja seuraa jotakin muuta tietä kuin uskovaisten, annamme Me saavuttaa, mitä hän kääntyi tavoittelemaan. Me saatamme hänet Helvettiin; mikä kauhea määränpää!');
INSERT INTO "finnish" VALUES(4,116,'4:116','Totisesti, Jumala ei anna anteeksi, että jotakuta muuta Hänen rinnallaan jumaloidaan. Kaiken muun paitsi tämän antaa Hän anteeksi kenelle tahtoo, sillä se, joka asettaa jonkun Jumalan rinnalle, on eksynyt äärimmäiseen harhaan.');
INSERT INTO "finnish" VALUES(4,117,'4:117','Paitsi Häntä he rukoilevat epäjumalia, nimittäin naispuolisia, mutta siten he turvautuvat vain kapinalliseen Saatanaan,');
INSERT INTO "finnish" VALUES(4,118,'4:118','jonka Jumala on kironnut ja joka uhkasi: »Olen totisesti anastava määrätyn osan Sinun palvelijoistasi, olen johdattava heidät harhaan');
INSERT INTO "finnish" VALUES(4,119,'4:119','ja herättävä heissä himoja; olen totisesti käskevä heitä (taikatarkoituksessa) leikkaamaan kotieläinten korvia ja olen käskevä heitä pilaamaan Jumalan luomia.» Mutta se, joka ottaa Saatanan suojelijakseen Jumalan sijasta, hukuttaa sielunsa ilmeiseen kadotukseen.');
INSERT INTO "finnish" VALUES(4,120,'4:120','Hän antaa heille lupauksia ja synnyttää heissä himoja, mutta Saatana antaa ainoastaan petollisia lupauksia.');
INSERT INTO "finnish" VALUES(4,121,'4:121','Heidän olinpaikkansa on oleva Helvetti, eivätkä he löydä pääsyä sieltä.');
INSERT INTO "finnish" VALUES(4,122,'4:122','Mutta niiden, jotka uskovat ja tekevät hyviä töitä, Me annamme astua huvitarhoihin, joissa purot solisevat, ja joissa he iankaikkisesti viihtyvät. Tämä on Jumalan totinen lupaus, ja kuka voi olla totuudellisempi sanoissaan kuin Jumala?');
INSERT INTO "finnish" VALUES(4,123,'4:123','Tämä ei riipu teidän toivomuksistanne eikä niidenkään, jotka ovat Pyhän kirjan saaneet . Vaan se joka vääryyttä tekee, saa pahan palkan, eikä hän löydä puolustajaa eikä auttajaa Jumalaa vastaan.');
INSERT INTO "finnish" VALUES(4,124,'4:124','Mutta ne, jotka tekevät hyviä töitä, olkootpa miehiä tai naisia, ja jotka ovat uskovaisia, nämä astuvat Paratiisiin eivätkä kärsi hituistakaan vääryyttä .');
INSERT INTO "finnish" VALUES(4,125,'4:125','Kenellä on parempi uskonto kuin sillä, joka kaikkinensa alistuu Jumalan tahtoon, tekee hyvää ja seuraa vanhurskaan Aabrahamin oppia? Itse Jumala otti Aabrahamin ystäväkseen.');
INSERT INTO "finnish" VALUES(4,126,'4:126','Jumalalle kuuluu kaikki taivaassa ja maan päällä, ja Jumalalla on kaikki vallassaan.');
INSERT INTO "finnish" VALUES(4,127,'4:127','He kysyvät sinulta neuvoa naisten suhteen. Sano: »Jumala on ohjeensa antanut; heidän suhteensa teille luetaan Pyhästä kirjasta määräyksiä orpotytöistä, joille ette anna sitä, mikä on heille asetettu, ja joita ette halua naidai , samoinkuin niistä lapsista, jotka ovat alaikäisiä, että nimittäin teidän on noudatettava oikeutta orpoja kohtaan. Mitä hyvää teettekin, Jumala tietää sen totisesti.»');
INSERT INTO "finnish" VALUES(4,128,'4:128','Ja jos nainen pelkää miehensä pahoinpitelyä tai hylkimistä, niin ei kummallekaan lueta synniksi, jos he hyvällä sopivat asian keskenään, sillä sovinto on paras. Mutta (miesten) mielet valtaa helposti ahneus. Jos teette hyvää ja pelkäätte Jumalaa, niin Jumala totisesti tietää teidän tekonne.');
INSERT INTO "finnish" VALUES(4,129,'4:129','Ette koskaan kykene kohtelemaan kaikkia vaimojanne samalla tavalla, vaikka sitä hartaasti toivoisittekin. Älkää kuitenkaan kääntykö koko mieltymyksellänne yhtäänne, jättäen toisen itsekseen aivan kuin johonkin välitilaan. Jos teette hyvää ja kartatte pahaa, niin Jumala on totisesti armollinen ja laupias.');
INSERT INTO "finnish" VALUES(4,130,'4:130','Mutta jos he (mies ja vaimo) eroavat, voi Jumala antaa molemmille ylenmäärin rakkaudestaan, sillä Jumala on kaikkivaltias ja kaikkitietävä.');
INSERT INTO "finnish" VALUES(4,131,'4:131','Jumalalle kuuluu kaikki taivaassa ja maan päällä. Me olemme määrännyt sekä niille, jotka ovat saaneet Pyhän kirjan ennen teitä, että teille itsellenne: »Antakaa kunnia Jumalalle!» Mutta jos olette uskottomia, niin kuuluu Jumalalle totisesti kaikki taivaassa ja maan päällä; niin, Jumala on itsessään rajaton, kaiken kunnian omistaja.');
INSERT INTO "finnish" VALUES(4,132,'4:132','Jumalalle kuuluu kaikki taivaassa ja maan päällä, ja Jumala riittää teidän suojelijaksenne.');
INSERT INTO "finnish" VALUES(4,133,'4:133','Jos Hän tahtoo, voi Hän hävittää teidät, ihmiset, ja luoda toiset sijalle, sillä Jumalalla on sekin vallassaan.');
INSERT INTO "finnish" VALUES(4,134,'4:134','Jos joku toivoo palkkaa tässä elämässä, niin onhan Jumalan luona sekä tämän että tulevan elämän palkka; niin, Jumala on kuuleva, näkevä.');
INSERT INTO "finnish" VALUES(4,135,'4:135','Te uskovaiset, pysykää horjumatta totuudessa, todistajina Jumalan edessä silloinkin, kun se on haitaksi teille itsellenne tai vanhemmillenne tai lähimmille omaisillenne. Olkoonpa (asia) rikkaan tai köyhän, Jumala on kumpaakin lähinnä. Älkää noudattako intohimojanne, niin että luisutte pois totuudesta, sillä jos kierrätte totuutta tai jätätte jotakin todistamatta, niin Jumala totisesti tietää teidän tekonne.');
INSERT INTO "finnish" VALUES(4,136,'4:136','Te, jotka uskotte, uskokaa Jumalaan ja Hänen profeettaansa, siihen Pyhään kirjaan, jonka Hän on hänelle ylhäältä antanut, ja siihen Pyhään kirjaan, jonka Hän ennen lähetti, sillä se, joka kieltää Jumalan ja Hänen enkelinsä, Hänen antamansa Pyhät kirjoitukset ja Hänen profeettansa sekä Viimeisen päivän, on pitkälle eksynyt harhaan.');
INSERT INTO "finnish" VALUES(4,137,'4:137','Mitä taas tulee niihin, jotka ensin uskoivat ja sitten luopuivat, ja taas kääntyivät, mutta sitten tulivat uskottomiksi ja lopulta paisuivat jumalattomuudessaan, ei Jumala totisesti anna heille anteeksi eikä ohjaa heitä oikealle tielle.');
INSERT INTO "finnish" VALUES(4,138,'4:138','Vie kaksimielisille sanoma, että heitä odottaa tuskallinen rangaistus.');
INSERT INTO "finnish" VALUES(4,139,'4:139','Ne, jotka ottavat uskottomia ystävikseen uskovaisten asemesta, etsivätkö he heiltä kunniaa? Kaikki kunniahan on Jumalan hallussa.');
INSERT INTO "finnish" VALUES(4,140,'4:140','Hän on ilmoittanut teille (tässä) Pyhässä kirjassa: »Kun kuulette Jumalan tunnusmerkkejä kiellettävän ja pilkattavan, älkää istuko heidän kanssaan, ennenkuin he ehkä siirtyvät puhumaan jostakin toisesta aiheesta.» Sillä muuten olisitte heidän kaltaisiaan. Jumala on totisesti kokoava ulkokultaiset ja uskottomat yhteen Helvettiin.');
INSERT INTO "finnish" VALUES(4,141,'4:141','Teidän suhteenne he vaanivat tilaisuutta, ja jos saatte voiton Jumalan avulla, he sanovat: »Emmekö me ole teidän puolellanne?» Jos taas uskottomilla on menestystä, he sanovat: »Eikö meillä ollut ylivoima, ja me kuitenkin suojelimme teitä uskovaisia vastaan?» Mutta Jumala on tuomitseva teidän välillänne Ylösnousemuksen päivänä eikä Jumala totisesti ole koskaan antava uskottomille mitään menestystä uskovaisia vastaan.');
INSERT INTO "finnish" VALUES(4,142,'4:142','Katso, teeskentelijät koettavat pettää Jumalaa, mutta juuri Hän pettää heidät. Kun he asettuvat rukoilemaan, he seisovat veltosti, tekeytyvät hurskaiksi ihmisten silmissä, mutta todellisuudessa vähän välittävät Jumalasta.');
INSERT INTO "finnish" VALUES(4,143,'4:143','Horjuessaan kahden tien välillä he eivät kuulu sinne eivätkä tänne. ja sille, jonka Jumala antaa eksyä, et sinä, Muhammed, ole löytävä pelastustietä.');
INSERT INTO "finnish" VALUES(4,144,'4:144','Te, jotka uskotte, älkää ottako uskottomia ystäviksenne uskovaisten sijasta! Tahdotteko siten antaa Jumalalle kiistämättömän rankaisuvallan itseenne näihden?');
INSERT INTO "finnish" VALUES(4,145,'4:145','Teeskentelijät totisesti joutuvat Tulen pohjimmaiseen kuiluun, etkä sinä löydä heille ketään auttajaa,');
INSERT INTO "finnish" VALUES(4,146,'4:146','paitsi, jos he katuvat ja tekevät parannuksen, pitävät kiinni Jumalasta ja Jumalalle näyttävät uskonsa vilpittömyyden. Nämä tulevat olemaan uskovaisten kanssa, ja uskovaisille Jumala on antava suuren palkinnon.');
INSERT INTO "finnish" VALUES(4,147,'4:147','Mitä syytä olisi Jumalalla rangaista teitä, jos te olette kiitollisia ja uskotte (Häneen)? Jumala siunaa ja palkitsee vähäiset teot ja tietää kaiken.');
INSERT INTO "finnish" VALUES(4,148,'4:148','Jumala ei rakasta sitä, että ihmisistä levitetään pahoja huhuja, paitsi milloin joku on kärsinyt vääryyttä; niin, Jumala on kuuleva, tietävä.');
INSERT INTO "finnish" VALUES(4,149,'4:149','Jos te jotakin hyvää ihmisistä ilmaisette tai toisten virheitä salaatte tai pahaa annatte anteeksi, niin on Jumala totisesti sääliväinen ja mahtava.');
INSERT INTO "finnish" VALUES(4,150,'4:150','Ne, jotka kieltävät Jumalan ja hänen profeettansa ja tekevät eron Jumalan lähettämien profeettain välillä sanoen: »Me uskomme eräisiin, mutta emme usko toisiin heistä» ja koettavat löytää keskitien,');
INSERT INTO "finnish" VALUES(4,151,'4:151','ne ovat todella uskottomia, ja uskottomille Me olemme valmistanut häpeällisen rangaistuksen.');
INSERT INTO "finnish" VALUES(4,152,'4:152','Mutta niille, jotka uskovat Jumalaan ja Hänen lähettiläihinsä tekemättä mitään erotusta heidän välillään, Me olemme antava heidän palkkansa; niin, Jumala on armollinen ja laupias.');
INSERT INTO "finnish" VALUES(4,153,'4:153','Ne, jotka ovat saaneet Pyhän kirjan, vaativat, että sinä heille loihtisit Kirjan taivaasta, mutta Moosekselta vaativat he muinoin vielä enemmän ja sanoivat: »Anna meidän silmillämme nähdä Jumala!» Ja salama iski heihin heidän jumalattomuutensa tähden. Sitten he valitsivat vasikan epäjumalakseen, huolimatta (Jumalan herruuden) selvistä todistuksista, jotka heidän osakseen olivat tulleet, mutta me annoimme heille anteeksi tämän ja annoimme Moosekselle avoimet valtuudet.');
INSERT INTO "finnish" VALUES(4,154,'4:154','Ja (Siinain) vuoren kohotessa heidän ylitseen Me solmimme liiton ja sanoimme heille: »Astuessanne portista kaupunkiin kumartakaa maahan». Lisäksi sanoimme heille: »Alkää rikkoko sapattia!» ja otimme heiltä lujan vakuutuksen.');
INSERT INTO "finnish" VALUES(4,155,'4:155','Mutta Jumala hylkäsi heidät siksi, että he rikkoivat liittonsa ja kielsivät Jumalan tunnusmerkit, surmasivat syyttä profeetat ja sanoivat: »Meidän sydämemme ovat ympärileikkaamattomat» - Jumala on sulkenut ne sinetillään heidän uskottomuutensa tähden, niin että vain harvat uskovat -');
INSERT INTO "finnish" VALUES(4,156,'4:156','ja siksi, etteivät he uskoneet (Jeesukseen), vaan levittivät kauhistuttavaa parjausta Mariasta');
INSERT INTO "finnish" VALUES(4,157,'4:157','ja sanoivat: »Me olemme totisesti surmanneet Messiaan, Jeesuksen, Marian pojan, Jumalan lähettilään - mutta he eivät olleet surmanneet eivätkä ristiinnaulinneet häntä, vaan siltä vain näytti heistä , ja ne, jotka siitä kiistelevät, ovat itsekin epätietoisia: heillä ei ollut varmaa tietoa, vaan he kehittelivät ainoastaan olettamuksia. Todellisuudessa he eivät ole surmanneet häntä (lopullisesti),');
INSERT INTO "finnish" VALUES(4,158,'4:158','vaan Jumala on ottanut hänet luokseen, sillä Jumala on mahtava ja viisas.');
INSERT INTO "finnish" VALUES(4,159,'4:159','Eikä Pyhän kirjan omaavien joukossa ole ketään, jolla ei olisi oikeata uskoa häneen ennen kuolemaansa , (muuten) Ylösnousemuksen päivänä hän on todistava heitä vastaan.');
INSERT INTO "finnish" VALUES(4,160,'4:160','ja juutalaisten jumalattomuuden tähden Me olemme kieltänyt heiltä eräitä hyviä ruokia, jotka ennen olivat heille sallittuja, ja tämän olemme tehnyt varsinkin siksi, että he estävät niin monia pääsemästä Jumalan tielle');
INSERT INTO "finnish" VALUES(4,161,'4:161','ja harjoittavat koronkiskomista, vaikka se on kielletty, ja käyttävät väärin toisten ihmisten omaisuutta; niin, uskottomille heidän joukossaan olemme valmistanut tuskallisen rangaistuksen.');
INSERT INTO "finnish" VALUES(4,162,'4:162','Mutta niille heistä , joilla on perusteelliset tiedot ja jotka uskovat siihen, mikä sinulle on ilmoitettu, ja siihen, mikä on ennen sinua lähetetty, ja varsinkin niille, jotka hartaasti rukoilevat, antavat almuja, uskovat Jumalaan ja Viimeiseen päivään, Me annamme suuren palkinnon.');
INSERT INTO "finnish" VALUES(4,163,'4:163','Me olemme totisesti antanut sinulle ilmestykset, samoinkuin annoimme Nooalle ja profeetoille hänen jälkeensä; samoin Me olemme antanut ilmestyksiä Aabrahamille, Ismaelille, lisakille, Jaakobille, Israelin sukukunnille, Jeesukselle, Jobille, Joonalle, Aaronille ja Salomolle; Daavidille olemme antanut Psalttarin.');
INSERT INTO "finnish" VALUES(4,164,'4:164','Toisista Jumalan lähettiläistä Me olemme sinulle maininnut, toisista emme ole sinulle maininnut: Mooseksen kanssa Jumala puhui itse henkilökohtaisesti.');
INSERT INTO "finnish" VALUES(4,165,'4:165','Oli profeettoja, jotka toivat ihmisille ilosanoman ja (toisia, jotka) varoittivat, jottei ihmisillä heidän lähettämisensä jälkeen olisi mitään veruketta Jumalaa vastaan; Jumala on, näet, mahtava ja viisas.');
INSERT INTO "finnish" VALUES(4,166,'4:166','Jumala itse todistaa sen, minkä Hän on sinulle ilmoittanut; Hän on ilmoittanut sen viisaudessaan. Samoin enkelit todistavat sen, ja Jumala riittää todistajaksi.');
INSERT INTO "finnish" VALUES(4,167,'4:167','Ne, jotka ovat uskottomia ja estelevät (muitakin) Jumalan tieltä, ovat totisesti menneet pitkälle harhaan.');
INSERT INTO "finnish" VALUES(4,168,'4:168','Niille, jotka ovat uskottomia ja tekevät vääryyttä, ei Jumala totisesti anna anteeksi eikä ohjaa heitä muulle tielle');
INSERT INTO "finnish" VALUES(4,169,'4:169','kuin sille, joka päättyy Helvettiin, jossa he iäti pysyvät: ja tämä on helppoa Jumalalle.');
INSERT INTO "finnish" VALUES(4,170,'4:170','Ihmiset, nyt on profeetta tullut teidän luoksenne tuoden totuuden Herraltanne; uskokaa siis omaksi hyödyksenne! Mutta jos ette usko, niin totisesti on sittenkin Jumalan vallassa kaikki taivaassa ja maan päällä; katso, Jumala on tietävä, viisas.');
INSERT INTO "finnish" VALUES(4,171,'4:171','Te, joille on annettu Pyhä kirja, älkää menkö liiallisuuksiin uskonnossanne älkääkä sanoko Jumalasta muuta kuin totta. Messias, Jeesus, Marian poika, on ainoastaan Jumalan profeetta ja Hänen Marialle lähettämänsä Sana sekä Hänestä (hänen päällensä) tullut Henki; uskokaa siis Jumalaan ja Hänen lähettiläisiinsä älkääkä sanoko: »Kolminaisuus!» Ei, lopettakaa (Kolminaisuudesta puhuminen) omaksi hyödyksenne! Jumala on yksi ainoa Jumala. Luoksepääsemätön on Hänen majesteettiutensa, jotta Hänellä soveltuisi olemaan poika. Hänelle kuuluu kaikki taivaassa ja maan päällä; Jumala yksin riittää Suojelijaksi.');
INSERT INTO "finnish" VALUES(4,172,'4:172','Messias ei koskaan ylenkatso osaansa: olla Jumalan palvelija, eivätkä sitä tee enkelitkään, jotka saavat lähestyä Häntä. Mutta ne, jotka ylenkatsovat Hänen palvelemisensa ja ovat röyhkeitä, Hän on kaikki kokoava eteensä.');
INSERT INTO "finnish" VALUES(4,173,'4:173','ja silloin niille, jotka uskovat ja tekevät hyviä töitä, Hän on antava täyden palkan, lisäten vielä heille sulasta suosiostaan, mutta mitä niihin tulee, jotka ylenkatsoivat Hänet ja olivat röyhkeitä, niin Hän rankaisee heitä tuskallisella rangaistuksella, eivätkä he löydä ketään suojelijaa eikä auttajaa Jumalaa vastaan.');
INSERT INTO "finnish" VALUES(4,174,'4:174','Ihmiset, nyt on osaksenne tullut todistus Herraltanne. ja Me olemme lähettänyt kirkkaan valon luoksenne.');
INSERT INTO "finnish" VALUES(4,175,'4:175','Mitä niihin tulee, jotka uskovat Jumalaan ja lujasti kiintyvät Häneen, niin Hän on sulkeva heidät laupeuteensa ja suosioonsa ja ohjaava heidät luokseen oikeata tietä.');
INSERT INTO "finnish" VALUES(4,176,'4:176','He pyytävät sinulta lähempiä ohjeita (perinnönjakoon). Sano: »Jumala on määritellyt suhteenne etäisempiin sukulaisiinne. Jos mies kuolee lapsetonna, mutta hänellä on sisar, niin on sisar saava puolet hänen jäämistöstään; samoin olisi hän perinyt sisarensa, jollei tällä olisi ollut lasta; mutta jos sisaria on kaksi, niin he perivät kaksi kolmattaosaa hänen jäämistöstään, ja jos sisaruksia on kumpaakin sukupuolta, on mies saava yhtä paljon kuin kaksi naista. Jumala saattaa teille tämän selväksi, jotta ette virheitä tekisi, katso, Jumala on kaikkitietävä.');
INSERT INTO "finnish" VALUES(5,1,'5:1','Te, jotka uskotte, täyttäkää velvollisuutenne uskollisesti! Sallitut ovat teille (ravinnoksi) karjanne eläimet, paitsi ne, joista (tässä kirjassa) sanotaan, samoin on kielletty (pyhiinvaellusretkellä) riistakin, jota pyhiinvaelluspukunne ei salli teidän metsästää. Katso, Jumala säätää niinkuin tahtoo.');
INSERT INTO "finnish" VALUES(5,2,'5:2','Te. jotka uskotte, älkää aiheuttako Jumalan säätämien perinnäistapojen höltymistä, olipa kyseessä pyhitetty kuukausi, uhrit, köynnökset tai retkikunnat, jotka pyrkivät Rauhoitettuun temppeliin etsien Jumalan armoa ja mielisuosiota. Mutta kun olette (pyhiinvaellukselta) palanneet ja pukeutuneet tavallisiin vaatteisiinne, saatte pyytää riistaa. Älköön viha niitä vastaan jotka tekivät esteitä teille teidän matkatessanne Rauhoitettuun pyhäkköön, johtako teitä vääryyden tekoihin, vaan auttakaa toisianne hyvyyteen ja hurskauden noudattamiseen. Älkää tukeko toisianne synnissä ja rikkomuksissa, vaan täyttäkää Jumalalle velvollisuutenne. Katso, Jumala on ankara rangaistessaan.');
INSERT INTO "finnish" VALUES(5,3,'5:3','Kielletyt ovat teiltä itsestään kuolleet eläimet, veri, sianliha, ja (eläimet), joiden teurastus on tapahtunut jonkun muun nimeen kuin Jumalan , samoin kuristumalla tai iskuvammoista kuolleet eläimet tai pudotessaan surmansa saaneet, sarvilla kuoliaaksi pusketut, petojen raatelemat, ellette teurastamalla ole tehneet niitä nautittaviksi, ja vihdoin epäjumalille uhratut elukat. Kielletty on teitä jakamasta keskenänne (uhrielukoita) arpomalla nuolia. Tämä on pakanallista. Nykyään ei uskottomilla ole toivoa turmella teidän uskontoanne, joten älkää heitä pelätkö, pelätkää Minua! Tänä päivänä Minä olen saattanut täydelliseksi teidän uskontonne ja sallinut mielisuosioni ylenpalttisesti tulla teidän osaksenne, olen armossani antanut Islaamin teidän uskonnoksenne. Kuka nälän pakottamana, ei tahallaan, rikkoo (äskenmainittuja) kieltojamme vastaan, (hänelle) Jumala on anteeksiantava, laupias.');
INSERT INTO "finnish" VALUES(5,4,'5:4','He kysyvät sinulta (Muhammed), mikä heille on (ravinnoksi) sallittua. Sano: (kaikki) hyvät ravintoaineet ovat teille sallittuja. Myös (on teille sallittu) niiden petolintujen saalis, jotka olette riistaa pyydystämään opettaneet aivan kuin koiranne. Jumalan teille neuvoman taidon mukaan; syökää mitä ne pyydystävät teille, tehkää se Jumalan nimeen ja jumalanpelossa. Katso, Jumala on nopea vaatimaan tilille.');
INSERT INTO "finnish" VALUES(5,5,'5:5','Nykyään ovat (kaikki) hyvät nautittavat teille sallitut. Pyhän kirjoituksen saaneiden käyttämät ruoat ovat luvalliset teille ja teidän ruokanne ovat luvalliset heille. Samoin ovat (sallitut teille) hyveelliset oikeauskoisten naiset ja hyveelliset naiset kansoista, joille ennen teitä Kirjoitukset annettiin; teidän on annettava heille (säädetyt) aviolahjat ja elettävä heidän kanssaan kunniallisesti, karttaen haureutta, ottamatta heitä vain rakastajattariksenne. Ken uskontonsa pettää, hänen työnsä raukeaa tyhjiin ja hän joutuu kadotettujen pariin tulevassa elämässä.');
INSERT INTO "finnish" VALUES(5,6,'5:6','Te, jotka uskotte! Noustessanne rukoilemaan peskää kasvonne ja kätenne kyynärpäätä myöten, pyyhkikää päänne ja peskää jalkanne nilkkoja myöten. Ja jos olette tahriintuneet, tehkää itsenne puhtaaksi. Ja jos olette sairaina tai matkalla tai joku teistä tulee käymälästä, tai olette olleet yhteydessä naisten kanssa, eikä vettä ole saatavissa, niin hangatkaa kasvonne ja kätenne hienolla, puhtaalla hiekalla. Jumala ei sälytä päällenne hankaluuksia, mutta tahtoo, että vaeltaisitte puhtaudessa ja että Hänen armonsa teissä tulisi täydelliseksi, jotta olisitte kiitolliset.');
INSERT INTO "finnish" VALUES(5,7,'5:7','Muistakaa Jumalan armoa ja Hänen liittoaan, jonka Hän teki kanssanne, teidän sanoessanne: »Me kuulemme ja me tottelemme»; ja täyttäkää velvollisuutenne Jumalaa kohtaan. Jumala totisesti tietää tekonne.');
INSERT INTO "finnish" VALUES(5,8,'5:8','Te, jotka uskotte! Nouskaa( rohkeina) todistamaan oikeamielisesti Jumalan asian puolesta, älkääkä antako ihmisten vihamielisyyden estää teitä menettelemästä oikein. Olkaa oikeamielisiä, - se on lähinnä hurskautta, - ja kunnioittakaa Jumalaa, sillä totisesti Jumala tietää mitä teette.');
INSERT INTO "finnish" VALUES(5,9,'5:9','Jumala on antanut lupauksensa niille, jotka uskovat ja hyviä töitä tekevät: heidän on anteeksiantamus ja suuri palkka.');
INSERT INTO "finnish" VALUES(5,10,'5:10','Ja ne, jotka eivät usko ja kieltävät Meidän ilmoituksemme, ovat tulen omia.');
INSERT INTO "finnish" VALUES(5,11,'5:11','Te, jotka uskotte! Muistakaa Jumalan suosiota kohtaanne, kun eräs heimo yritti käydä käsiksi teihin, mutta Hän piti sen kädet loitolla. Ja antakaa kunnia Jumalalle. Jumalaan pankoot oikeauskoiset turvansa.');
INSERT INTO "finnish" VALUES(5,12,'5:12','Jumala teki muinoin liiton Israelin lasten kanssa, ja Me korotimme heidän keskuudestaan kaksitoista johtomiestä, ja Jumala sanoi: »Katso Minä olen kanssanne. Jos te suoritatte hartautenne säännöllisesti, annatte almuja, uskotte lähettiläihini, tuette heitä ja lainaatte Jumalalle jalon korottoman lainan, niin Minä totisesti annan syntinne anteeksi ja päästän teidät huvi~ tarhoihin, joissa purot solisevat. ja se teistä, joka tämän jälkeen ei usko, joutuu harhaan oikealta tieltä.»');
INSERT INTO "finnish" VALUES(5,13,'5:13','Ja siksi, että he rikkoivat liittonsa, Me olemme kironnut heidät ja kovettanut heidän sydämensä. He sotkevat Kirjoituksen sanoja ja ovat unohtaneet osan siitä, mitä heille opetettiin. Lakkaamatta havaitset kieroa menettelyä kaikkien heidän puoleltansa, lukuunottamatta muutamia harvoja. Mutta siedä heitä ja anna anteeksi; katso, Jumala rakastaa jalomielisiä.');
INSERT INTO "finnish" VALUES(5,14,'5:14','Niidenkin kanssa, jotka sanovat: »Katso, Me olemme kristittyjä», Me teimme liiton, mutta he unohtivat osan siitä, mitä heille opetettiin. Sentähden Me herätimme heissä kaunan ja vihan toisiansa vastaan aina Ylösnousemuspäivään asti, jolloin Jumala tekee heille selviksi heidän kättensä työt.');
INSERT INTO "finnish" VALUES(5,15,'5:15','Te, joiden ovat Kirjoitukset! Nyt on tullut luoksenne Meidän lähettiläämme tähdentäen teille paljon siitä, minkä talletitte Kirjoituksessa, ja jättäen paljon syrjään. Nyt on teille tullut Jumalalta valo ja selvä Kirjoitus,');
INSERT INTO "finnish" VALUES(5,16,'5:16','jonka mukaisesti Jumala ohjaa rauhan poluille niitä, jotka etsivät Hänen mielisuosiotaan. Pimeydestä tahtoo Hän päästää heidät valkeuteen ja opastaa heidät oikealle tielle.');
INSERT INTO "finnish" VALUES(5,17,'5:17','Ne ovat todella epäuskoisia, jotka sanovat: »Katso, Jumala on Messias, Marian poika.» Sano heille: »Kuka sitten voi mitään Jumalaa vastaan, jos Hän tahtoo hukuttaa vaikkapa Messiaan, Marian pojan, ja hänen äitinsä ja ylipäänsä jok''ainoan maan päällä. Jumalan on herruus taivaassa ja maassa ja niiden välisessä avaruudessa. Hän luo mitä tahtoo, ja Jumala on kaikkivoipa.');
INSERT INTO "finnish" VALUES(5,18,'5:18','Juutalaiset ja kristityt väittävät: »Me olemme Jumalan poikia ja lempilapsia.» Sano: »Miksi Hän sitten kurittaa teitä syntienne tähden? Ei, te olette vain Hänen luomiaan kuolevaisia. Hän armahtaa, ketä Hän tahtoo, ja Hän kurittaa, ketä tahtoo. Jumalan on yliherruus taivaassa ja maan päällä ja niiden välillä, Hän on määränpää.»');
INSERT INTO "finnish" VALUES(5,19,'5:19','Te, joiden ovat Kirjoitukset! Nyt on Meidän lähettiläämme tullut luoksenne tekemään kaiken selväksi tauon jälkeen, jolloin ei ollut lähettiläitä, jottette sanoisi: »Ei tullut luoksemme yhtään ilosanoman tuojaa eikä varoittajaa.» Nyt on ilosanoman tuoja ja varoittaja tullut teille. Jumala on kaikkivoipa.');
INSERT INTO "finnish" VALUES(5,20,'5:20','Entä kun Mooses sanoi kansalleen: »Kansani! Muistakaa Jumalan suosiota kohtaanne; kuinka Hän lähetti keskuuteenne profeettoja ja asetti teille kuninkaita ja antoi teille sen, (mitä) Hän ei antanut kenellekään (muulle) luoduistaan.');
INSERT INTO "finnish" VALUES(5,21,'5:21','Kansani, kulkekaa sisälle Jumalan luvattuun pyhään maahan. Älkää kääntykö pakoon, muutoin varmaan joudutte perikatoon.');
INSERT INTO "finnish" VALUES(5,22,'5:22','He sanoivat: »Mooses, katso, jättiläiskansaa asustaa siellä, emmekä me pääse sinne, ennenkuin he poistuvat. Kun he ovat lähteneet, menemme me sinne.»');
INSERT INTO "finnish" VALUES(5,23,'5:23','Silloin puhui kaksi heistä, jotka pelkäsivät Herraa ja olivat Jumalan suosiossa: »Käykää sisään portin kautta heidän luokseen, ja kun olette sisälle päässeet, olette voittavat. Siis pankaa luottamuksenne (Jumalaan), jos olette uskovaisia.»');
INSERT INTO "finnish" VALUES(5,24,'5:24','He sanoivat: »Emme koskaan käy sisälle niin kauan kuin he siellä ovat. Mene sinä ja sinun Herrasi ja taistelkaa! Me jäämme tänne.»');
INSERT INTO "finnish" VALUES(5,25,'5:25','Hän sanoi: »Herra! Minulla ei ole valtaa (enää) muiden kuin itseni ja veljeni suhteen; tule Sinä erotuomariksi meidän ja väärintekijäin välille.»');
INSERT INTO "finnish" VALUES(5,26,'5:26','(Heidän Herransa) sanoi: »Tämän tähden olkoon se heiltä kielletty maa neljäkymmentä vuotta, jotka heidän on erämaassa vaellettava. Älä siis katkeroidu väärintekijäkansan vuoksi.');
INSERT INTO "finnish" VALUES(5,27,'5:27','Mutta lue heille totuuden mukaisesti kertomus Aadamin kahdesta pojasta, kuinka kumpikin heistä uhrasi ja toisen uhri vastaanotettiin, toisen uhria ei vastaanotettu. (Toinen) sanoi: »Totisesti, minä surmaan sinut», ja toinen vastasi: »Jumala ottaa vastaan vain niiden uhrin, jotka karttavat pahaa.');
INSERT INTO "finnish" VALUES(5,28,'5:28','Vaikka nostaisitkin kätesi minua vastaan surmataksesi minut, en minä nosta kättäni surmatakseni sinua, katso, minä pelkään Jumalaa, luomakunnan Herraa.');
INSERT INTO "finnish" VALUES(5,29,'5:29','Katso, ennemmin suon Sinun kantaa rangaistuksen synnistä minua vastaan sekä omista synneistäsi ja tulla tulen omaksi.Se on pahantekijän palkka.»');
INSERT INTO "finnish" VALUES(5,30,'5:30','Mutta (toisen) intohimo pakotti hänet tappamaan veljensä; hän surmasi hänet ja tuli kadotettujen lukuun.');
INSERT INTO "finnish" VALUES(5,31,'5:31','Ja Jumala lähetti korpin, joka raapi maata näyttääkseen hänelle, kuinka hänen veljensä alaston ruumis olisi kätkettävä. Mutta hän sanoi: »Voi minua! Pitääkö minun olla tuon korpin kaltainen ja kätkeä veljeni ruumis?» Ja hän tuli katuvaisten lukuun.');
INSERT INTO "finnish" VALUES(5,32,'5:32','Tämän johdosta annoimme Israelin lapsille säädöksen, että kuka ikinä ottaa hengiltä ihmisen muutoin kuin verikostoksi tai pahuuden ehkäisemiseksi maassa, hän on kuin olisi tappanut kaikki ihmiset; ja ken ikinä pelastaa yhden hengen, hän on kuin olisi pelastanut kaikkien ihmisten hengen.Meidän lähettiläämme tulivat aikoinaan heidän luokseen selvin todistuksin (Jumalan yliherruudesta), mutta katso, senkin jälkeen monet ihmisistä tulivat irstaiksi maan päällä.');
INSERT INTO "finnish" VALUES(5,33,'5:33','Ainoa palkka niille, jotka sotivat Jumalaa ja Hänen lähettilästään vastaan sekä levittävät turmiota maassa, on että heidät tapetaan tai ristiinnaulitaan, tai heidän kätensä ja jalkansa hakataan poikki eri puolilta, ristiin, tai heidät karkoitetaan maasta. Sellainen on heidän häväistyksensä tässä elämässä, ja tulevassa heille on kauhea kuritus,');
INSERT INTO "finnish" VALUES(5,34,'5:34','lukuunottamatta niitä, jotka katuvat, ennenkuin te saatte heidät valtaansa. Sillä tietäkää, että Jumala on pitkämielinen, laupias.');
INSERT INTO "finnish" VALUES(5,35,'5:35','Te, jotka uskotte! Pitäkää Jumala kunniassa ja etsikää Hänen läheisyyttään sekä ponnistelkaa Hänen retkellään, jotta olisitte onnellisia.');
INSERT INTO "finnish" VALUES(5,36,'5:36','Mitä tulee uskottomiin, niin totisesti, vaikka heidän olisi kaikki, mitä maassa on, ja yhtä paljon lisää heidän lunastamisekseen ylösnousemuspäivänä, ei sitä heiltä vastaanotettaisi. He ovat tuskallisen rangaistuksen omat.');
INSERT INTO "finnish" VALUES(5,37,'5:37','He pyrkivät pois tulesta, mutta eivät pääse. Heidän rangaistuksensa on pysyvä.');
INSERT INTO "finnish" VALUES(5,38,'5:38','Varkailta taasen, olkootpa mies~ tai naispuolisia, leikatkaa kädet. Se on palkka heidän omista teoistaan ja muita varoittava kuritus Jumalalta. Jumala on mahtava, viisas.');
INSERT INTO "finnish" VALUES(5,39,'5:39','Mutta ken tekee katumuksen ja parannuksen pahoista teoistaan, häntä kohtaan on Jumala sääliväinen. Jumala on totisesti armahtava, laupias.');
INSERT INTO "finnish" VALUES(5,40,'5:40','Etkö tiedä, että Jumalalle kuuluu herruus taivaassa ja maan päällä? Hän rankaisee ketä tahtoo ja armahtaa ketä tahtoo. Jumala on Kaikkivaltias.');
INSERT INTO "finnish" VALUES(5,41,'5:41','Lähettiläs, älä katkeroidu niistä, jotka kilpailevat keskenään epäuskosta, jotka huulillaan tunnustavat: »Me uskomme», mutta joiden sydämet eivät usko, älä myöskään (katkeroidu) juutalaisista, jotka mielellään kuuntelevat valheita, kuuntelevat vieraita ihmisiä, jotka eivät tule sinun luoksesi. He tempaavat Kirjoitusten sanoja pois kohdaltaan ja sanovat: »Jos ne teille näin annetaan, ottakaa ne vastaan; ellei, niin varokaa!» Jos kenet Jumala määrää syntiin, et sinä (yrityksilläsi) voi lainkaan häntä auttaa Jumalaa vastaan. Ne ovat sellaisia, joiden sydämiä Jumala ei tahdo puhdistaa. Tässä elämässä saavat he osakseen häpeätä ja tulevassa kauhean kurituksen.');
INSERT INTO "finnish" VALUES(5,42,'5:42','He kärkkyvät (kuulla) valheita! He ahnehtivat laitonta voittoa! Jos he tulevat luoksesi (Muhammed), tuomitse heidän riidassaan tai epää se heiltä. Jos kieltäydyt (tuomitsemasta), eivät he voi sinua vahingoittaa, mutta jos tuomitset, tee se oikeudenmukaisesti. Katso, Jumala rakastaa oikeudenmukaisia.');
INSERT INTO "finnish" VALUES(5,43,'5:43','Kuinka tulevatkaan he luoksesi tuomittavaksi, vaikka omistavat Tooran, joka sisältää Jumalan tuomiot? Joka tapauksessa menettelevät he toisin jälkeenpäin. Eiväthän tällaiset ole uskovaisia.');
INSERT INTO "finnish" VALUES(5,44,'5:44','Olemmehan totisesti ylhäältä lähettänyt Tooran, jossa on johdatus ja valkeus. Senhän mukaan juutalaisia tuomitsivat profeetat - jotka kuuluivat muslimeihin - sekä opettajat ja kirjanoppineet, koska heidät oli määrätty pitämään huolta Jumalan kirjasta, jonka (eläviä) todistajia he olivat. Älkää siis pelätkö ihmisiä, vaan pelätkää ainoastaan Minua. Tunnusmerkkiämme älkää vaihtako mitättömään. Joka ei käytä Jumalan ilmestystä oikein, on uskoton.');
INSERT INTO "finnish" VALUES(5,45,'5:45','Me määräsimme heille siinä: henki hengestä, silmä silmästä nenä nenästä, korva korvasta ja hammas hampaasta samoin kuin haava haavasta; mutta ken ehättää (kostamaan) tuomiotta, jääköön enemmättä hyvityksettä; kuka taasen ei tuominnut niin kuin Jumalan ilmoitus määrittelee, kuuluu väärintekijöihin.');
INSERT INTO "finnish" VALUES(5,46,'5:46','Ja Me lähetimme profeettain jälkeen Jeesuksen, Marian pojan, vahvistaen sen, mikä ennen Häntä oli sanottu Toorassa; Hänelle Me annoimme evankeliumin, joka sisälsi johdatuksen ja valkeuden sekä varmensi sen, mikä edellä oli sanottu Toorassa, - ollen myös johdatukseksi ja kehoitukseksi niille, jotka karttavat pahaa.');
INSERT INTO "finnish" VALUES(5,47,'5:47','Ja evankeliumin seuraajien piti tuomita sen mukaisesti, kuin Jumala oli siinä ilmoittanut; mutta kuka ikinä ei tuominnut Jumalan ilmoituksen mukaisesti, kuuluu väärintekijöihin.');
INSERT INTO "finnish" VALUES(5,48,'5:48','Näin Me olemme ilmaissut sinulle (Muhammed) Totuuden kirjan varmentaaksemme edeltäkäyneet kirjoitukset ja säilyttääksemme niiden tarkkuuden; arvostele siis niitä Jumalan ilmoituksen mukaisesti, äläkä seuraa sellaisten alhaisia vaistoja, jotka luopuvat sinulle ilmaistusta totuudesta; kullekin (heimollenne) Me sääsimme omat lakinsa ja tapansa. Jos olisi ollut Jumalan tahto, Hän olisi muovaillut teidät kaikki yhdeksi kansaksi, ellei Hän olisi tahtonut koetella teitä erikseen annetuin totuuksin; sentähden rientäkää kilvan toistenne kanssa hyveiden harjoittamiseen. Kaikkien on teidän palattava Jumalan eteen, ja Hän kyllä antaa teille lopullisen selvyyden siitä, mistä olitte erimielisiä.');
INSERT INTO "finnish" VALUES(5,49,'5:49','Siten on sinun (Muhammed) arvosteltava heitä sen mukaan kuin Jumala on ilmoittanut, äläkä seuraa heidän alhaisia viettejään, vaan ole heidän suhteensa varuillasi, etteivät he viettelisi sinua pois osastakaan niitä totuuksia, jotka Jumala on sinulle ilmoittanut. Mutta jos he uskostaan luopuvat, tiedä silloin, että Jumala aikoo rangaista heitä jostakin heidän vääryydestänsä; totisesti ovat monet kansasta lainrikkojia.');
INSERT INTO "finnish" VALUES(5,50,'5:50','Pimeyden päivien lakiako he sitten takaisin kaipaavat? Kuka muu kuin Jumala on parempi tuomari niille, jotka ovat uskossaan varmoja.');
INSERT INTO "finnish" VALUES(5,51,'5:51','Te, jotka uskotte! Älkää lyöttäytykö juutalaisten ja kristittyjen ystäviksi, - he ovat ystäviä vain toisilleen, ja joka ottaa heidät liittolaisikseen, on yksi heistä. Jumala ei totisesti ulota johdatustaan väärämielisiin.');
INSERT INTO "finnish" VALUES(5,52,'5:52','Mutta heidän ystävikseen näette te rientävän sellaisten, joiden sydämiä tauti tärvelee ja jotka sanovat: »Me pelkäämme onnettomuuden meitä kohtaavan.» Nyt voikin tapahtua, että Jumala antaa (muslimeille) voiton heistä tai lähettää rangaistuksen suoraan Itseltään, niin että he joutuvat katumaan sitä, mitä sisimmässään salailivat.');
INSERT INTO "finnish" VALUES(5,53,'5:53','Tällöin oikeauskoiset sanovat: »Nämäkö ovat niitä, jotka vannoivat Jumalan nimeen pyhimmät valansa, että varmasti kuuluvat teihin?» Heidän tekonsa tulevat mitättömiksi ja itse he joutuvat häviölle.');
INSERT INTO "finnish" VALUES(5,54,'5:54','Te, jotka uskotte! Jos teistä tulee uskonluopioita, silloin Jumala nostaa itselleen kansan, jota Hän rakastaa ja joka rakastaa Häntä, ollen sävyisä oikeauskoisia kohtaan, urhea uskottomia vastaan, eteenpäin pyrkivä Jumalan tiellä, pelkäämättä arvostelijain moitteita. Tällainen on Jumalan armo, Hän osoittaa sen kenelle suvaitsee, sillä Jumala on ääretön, tietävä.');
INSERT INTO "finnish" VALUES(5,55,'5:55','Teidän liittolaisenne ovat siis: Jumala ja Hänen lähettiläänsä sekä oikeauskoiset, nimittäin ne, jotka rukouksessa pysyvät ja jakelevat asetettuja almuja Jumalaa nöyrästi palvellen.');
INSERT INTO "finnish" VALUES(5,56,'5:56','Ne taas, jotka ovat liitossa Jumalan ja Hänen lähettiläänsä sekä oikeauskoisten kanssa, ovat totisesti sillä puolella, jolle Jumala suo voiton.');
INSERT INTO "finnish" VALUES(5,57,'5:57','Te, jotka uskotte! Niiden joukosta, joille Kirjoitus oli ennen teitä annettu, älkää ottako sellaisia ystäviksenne, jotka pitävät pilkkana tai leikintekona teidän uskontoanne, älkääkä myös jumalattomien joukosta, vaan osoittakaa hurskautenne Jumalalle, jos olette uskovaisia.');
INSERT INTO "finnish" VALUES(5,58,'5:58','Kun te kokoonnutte rukoukseen, he ottavat sen pilan ja leikin kannalta, sillä he ovat totisesti ymmärtämätöntä väkeä.');
INSERT INTO "finnish" VALUES(5,59,'5:59','Sano: »Voi, te Kirjoitusten kansa, siitäkö te meitä arvostelette, että me uskomme Jumalaan sekä siihen, mikä meille ylhäältä lähetettiin ja mikä on ennen meitä ylhäältä lähetetty? Totisesti ovat useimmat teistä uppiniskaisia.»');
INSERT INTO "finnish" VALUES(5,60,'5:60','Sano: »Onko minun lueteltava teille eräitä, jotka Jumalan kostoa ovat vieläkin enemmän kokeneet? Näitä ovat ne, joilta Jumala on riistänyt suosionsa ja jättänyt vihansa heidän päälleen.Ne, joiden keskuuteen Hän on siittänyt apinoita ja sikoja ja jotka paholaista palvelevat, - nämä ovat vielä kurjempia ja etäämmälle eksyneitä oikealta tieltä.»');
INSERT INTO "finnish" VALUES(5,61,'5:61','Kun he tulevat luoksenne, he sanovat: »Me uskomme», ja saattavathan he todellakin tulla tässä luulossaan ja mennä tiehensä samassa harhassa, mutta Jumala tietää parhaiten, mikä heissä piilee,');
INSERT INTO "finnish" VALUES(5,62,'5:62','ja sinä tulet näkemään enimpäin heistä yhdessä rientävän synteihin ja rikkomuksiin sekä syövän kiellettyä. Pahaa on totisesti se, mitä he tekevät.');
INSERT INTO "finnish" VALUES(5,63,'5:63','Miksi eivät heidän opettajansa ja viisaat estä heitä puhumasta synnillistä ja syömästä kiellettyä? Pahaa on totisesti se mitä he tekevät.');
INSERT INTO "finnish" VALUES(5,64,'5:64','Ja juutalaiset sanovat: »Jumalan kädet ovat sidotut.» Heidän kätensä sidotut ovat, ja he ovat jääneet ilman Jumalan suosiota, koska niin sanovat. Ei, Hänen molemmat kätensä ovat ojennetut, mutta Hän jakelee niin kuin Hän suvaitsee. Ja sekin, minkä Herrasi on sinulle ylhäältä ilmoittanut, vain yllyttää useimpia heistä irstaisuuteen ja uskottomuuteen. Siksi Me olemmekin kylvänyt kaunaa ja vihaa heidän keskuuteensa aina ylösnousemuksen päivään asti. Joka kerta, kun he sytyttävät sodan liekin, Jumala sammuttaa sen, ja he harhailevat maan päällä pahaa tehden, eikä Jumala rakasta niitä, jotka pahaa tekevät.');
INSERT INTO "finnish" VALUES(5,65,'5:65','Jospa Kirjoitusten kansa todellakin uskoisi ja hartautta harjoittaisi, Me varmasti pyyhkisimme heidän pahuutensa heistä ja Me totisesti sallisimme heidän astua autuuden tarhoihin.');
INSERT INTO "finnish" VALUES(5,66,'5:66','Ja jos he olisivat pitäneet kiinni Toorasta ja evankeliumista sekä siitä, minkä heidän Herransa on ylhäältä ilmaissut, olisivat he totisesti saaneet nauttia siunausta ylhäältä samoinkuin maasta tulevaa. Heidän joukossaan on oikeamielisiäkin, mutta kelvotonta on se, mitä suurin osa heistä tekee.');
INSERT INTO "finnish" VALUES(5,67,'5:67','Lähettiläs! Julista sitä, minkä Herrasi on sinulle ylhäältä lähettänyt, sillä jos et niin tee, et voi pitää itseäsi lainkaan Hänen sanansaattajanaan. Kyllä Jumala varjelee sinut ihmisiltä. Jumala ei totisesti ulota johdatustaan uskottomiin.');
INSERT INTO "finnish" VALUES(5,68,'5:68','Sano: »Te Kirjoitusten kansa, teidän perustanne on kestämätön, ellette pidä kiinni Toorasta ja evankeliumista sekä siitä, minkä teille Herranne on ylhäältä ilmaissut.» Se, mikä on sinulle ylhäältä ilmoitettu, vain yllyttää useimpia heistä irstaisuuteen ja uskottomuuteen. Älä siis murehdi uskottomasta kansasta.');
INSERT INTO "finnish" VALUES(5,69,'5:69','Niiden suhteen, jotka uskovat (Koraaniin), sekä juutalaisten ja sabealaisten samoin kuin kristittyjen suhteen olkoon niin, että joka uskoo Jumalaan ja ylösnousemuksen päivään ja tekee hyvää, sitä ei pelko valtaa eikä hän joudu murheeseen.');
INSERT INTO "finnish" VALUES(5,70,'5:70','Me teimme liiton Israelin lasten kanssa, ja Me lähetimme heidän luoksensa sananjulistajia; ja koska ikinä heidän tykönsä tuli profeetta tuoden sanoman, joka ei ollut heille mieleen, niin joitakuita niistä he kutsuivat valehtelijoiksi, ja toisia he surmasivat.');
INSERT INTO "finnish" VALUES(5,71,'5:71','Ja he luulivat, ettei heille ole rangaistusta, ja yltyivät sokeudessaan ja kuuroudessaan; vielä sittenkin Jumala kääntyi heidän puoleensa laupiaasti, mutta monet heistä tulivat aivan sokeiksi ja kuuroiksi; kyllä Jumala näkee, mitä he tekevät.');
INSERT INTO "finnish" VALUES(5,72,'5:72','Totisesti ne ovat harhauskossa, jotka sanovat: »Jumala on Messias, Marian poika»; mutta Messias sanoi: »Oi, Israelin lapset! Palvelkaa Jumalaa, - minun Herraani ja teidän Herraanne.» Totisesti, kuka ikinä rinnastaa (muita) Jumalaan, häneltä Jumala kieltää pääsyn autuuden puutarhaan, ja Hänen asuinsijansa on tuli; siellä ei ole auttajia väärintekijöille.');
INSERT INTO "finnish" VALUES(5,73,'5:73','Totisesti ne ovat harhauskossa, jotka sanovat: »Jumala on kolmas kolmesta.» Eihän ole muuta jumalaa kuin ainoa Jumala, ja elleivät he lakkaa niin sanomasta, lankeaa tuskallinen kuritus niiden ylitse heidän keskuudessaan, jotka ovat väärässä uskossa.');
INSERT INTO "finnish" VALUES(5,74,'5:74','Eivätkö he aio kääntyä Jumalan puoleen ja anoa Häneltä anteeksiantoa? Onhan Jumala armahtavainen, laupias.');
INSERT INTO "finnish" VALUES(5,75,'5:75','Messias, Marian poika, on vain Jumalan lähettiläs; lähettiläät ennen häntä elivät vain aikansa; ja hänen äitinsä oli todellinen nainen; hehän sitäpaitsi molemmat söivät maallista ravintoa. Katso (Muhammed), miten Me teemme heille selviksi sanomamme, ja huomaa, miten he kääntyvät pois (totuudesta).');
INSERT INTO "finnish" VALUES(5,76,'5:76','Sano: »Palveletteko Jumalan ohella sellaista, jonka vallassa ei ole tuottaa teille vahinkoa eikä hyötyä?»');
INSERT INTO "finnish" VALUES(5,77,'5:77','Sano: »Oi, Pyhän kirjan johdattamat! Älkää menkö uskonnossanne säädyttömiin liiallisuuksiin älkääkä seuratko niiden alhaisia viettejä, jotka muinoin harhaantuivat ja eksyttivät monta sekä joutuivat pois oikealta tieltä.»');
INSERT INTO "finnish" VALUES(5,78,'5:78','Israelin lasten keskuudessa sekä Daavid että Jeesus, Marian poika, manasivat niitä, jotka olivat epäuskossa; näin tapahtui, sillä he eivät totelleet ja menivät liiallisuuksiin.');
INSERT INTO "finnish" VALUES(5,79,'5:79','Heillä ei ollut tapana kieltää toinen toiseltaan inhottavaa tekoja, (joita) he tekivät; todella olivat heidän tekonsa pahat.');
INSERT INTO "finnish" VALUES(5,80,'5:80','Monien heistä (juutalaisista) te näette hierovan ystävyyttä uskottomien kanssa; on totisesti nurjaa se, mitä heidän sielunsa ovat edeltäpäin lähettäneet (tulevaan maailmaan); Jumala on vihastunut heihin, ja kurituksen alaisina saavat he olla.');
INSERT INTO "finnish" VALUES(5,81,'5:81','Jospa he olisivat uskoneet Jumalaan ja profeettaan sekä siihen, mikä hänelle oli ilmaistu, he eivät olisi lyöttäytyneet niiden ystäviksi; mutta enin osa heistä on lainrikkojia.');
INSERT INTO "finnish" VALUES(5,82,'5:82','Kiivaimmat oikeauskoisten vihamiehet totisesti tapaatte juutalaisista ja monijumalaisista, ja oikeauskoisten ystävyyttä lähinnä olevissa varmasti tapaatte sellaisia, jotka sanovat: »Me olemme kristittyjä»; tämä johtuu siitä, että heidän joukossaan on pappeja ja munkkeja ja että he eivät käyttäydy pöyhkeästi.');
INSERT INTO "finnish" VALUES(5,83,'5:83','Ja kun nämä kuulevat, mitä profeetalle on ylhäältä ilmoitettu (Koraani) havaitsette heidän silmäinsä vettyvän kyyneleistä sen totuuden vuoksi, jonka he tunnustavat; he sanovat: »Herra! Me uskomme, lue siis meidät totuuden todistajain joukkoon!');
INSERT INTO "finnish" VALUES(5,84,'5:84','Ja mitä (syytä) on meillä olla uskomatta Jumalaan ja siihen totuuteen, joka meille ylhäältä on tullut, koska me vakavasti toivomme, että meidän Herramme sallisi meidän astua hyveellisten kanssa (tulevaan elämään)?»');
INSERT INTO "finnish" VALUES(5,85,'5:85','Sentähden Jumala palkitsi heitä siitä, mitä he sanoivat, antamalla heidän elää puutarhoissa, joissa joet virtaavat, ja tämä on palkinto niille, jotka (toisille) hyvää tekevät.');
INSERT INTO "finnish" VALUES(5,86,'5:86','Mutta ne, jotka valitsevat epäuskon ja hylkäävät Meidän sanomamme, ovat tulen omia.');
INSERT INTO "finnish" VALUES(5,87,'5:87','Te, jotka uskotte! Älkää kieltäkö (itseltänne) sitä hyvää,minkä Jumala on teille luvalliseksi julistanut, älkää myöskään menkö liiallisuuksiin, - totisesti ei Jumala rakasta niitä, jotka ylittävät rajansa.');
INSERT INTO "finnish" VALUES(5,88,'5:88','Syökää sallittuja Jumalan hyviä antimia ja ottakaa vaarin velvollisuuksistanne Jumalaa kohtaan, johon te uskotte.');
INSERT INTO "finnish" VALUES(5,89,'5:89','Ei Jumala vaadi teitä tilille siitä, mikä on ajattelematonta teidän valoissanne, mutta Hän vaatii teitä tilille vannomisesta vastoin parempaa tietoanne; tämän syntinne lunnaiksi on teidän ravittava kymmenen köyhää kohtuullisella ruoalla, jolla te perheitännekin ruokitte, tai vaatetettava heidät tai vapautettava joku orjuudesta; kuka taasen on varaton, hänen on paastottava kolme päivää; tämä olkoon teidän (ajattelemattomasti) vannomienne valain sovintouhri; pitäkää vaari valoistanne. Näin selvittää teille Jumala sanomaansa, jotta olisitte kiitollisia.');
INSERT INTO "finnish" VALUES(5,90,'5:90','Te, jotka uskotte! Päihdyttävät juomat ja uhkapelit ja (uhripalvelus) pystytettyjen kivien edessä sekä nuolilla arpominen ovat pelkkää riettautta, paholaisen aikaansaamaa; karttakaa kaikkea tätä, jotta olisitte onnelliset.');
INSERT INTO "finnish" VALUES(5,91,'5:91','Saatana vain pyrkii kylvämään kaunaa ja vihaa teidän keskuuteenne väkijuomien ja uhkapelin avulla pitääkseen teidät loitolla Jumalan muistelemisesta ja rukoilemisesta; ettekö sitä vastustaisi?');
INSERT INTO "finnish" VALUES(5,92,'5:92','Totelkaa Jumalaa, totelkaa lähettilästä ja ottakaa vaari itsestänne; mutta jos te pois käännytte, niin tietäkää, ettei Meidän lähettiläämme ole velvollinen ilmoittamaan enempää kuin selvän sanomamme.');
INSERT INTO "finnish" VALUES(5,93,'5:93','Niille, jotka uskovat ja tekevät hyvää, ei lueta viaksi, mitä he syövät, kun vain he ottavat vaarin velvollisuudestaan, pysyvät uskossa ja harjoittavat hyviä töitä, - ovathan he huolellisia hartauden harjoittamisessa ja uskossa sekä tekevät hyvää muille; Jumala rakastaa niitä, jotka muille hyvää tekevät.');
INSERT INTO "finnish" VALUES(5,94,'5:94','Te, jotka uskotte! Jmala on totisesti koetteleva teitä riistalla, johon teidän kätenne ja keihäänne voivat ulottua, jotta Jumala tietäisi, kuka Häntä salassa kunnioittaa; mutta joka tässä suhteessa kiellon rikkoo, hän on saava tuskallisen kurituksen.');
INSERT INTO "finnish" VALUES(5,95,'5:95','Te, jotka uskotte! Älkää tappako riistaa ollessanne pyhiinvaelluksella; kuka ikinä teistä tahallansa pyytää sitä, joutuu hyvittämään sen karjastaan vastaavalla eläimellä, ja kaksi oikeudenmukaista henkilöä keskuudestanne tuomitkoon sen tuotavaksi uhrina Kaabaan tai käytettäväksi lunnaina köyhien ravitsemiseen; muuten on se hyvitettävä vastaavalla paastolla; täten syyllinen saa kokea tekonsa pahan seurauksen; Jumala antaa anteeksi sen, mikä näin on sivuutettu, mutta Jumala lyö kurituksellaan sitä, joka rikkoo uudelleen; onhan Jumala mahtava, koston Herra.');
INSERT INTO "finnish" VALUES(5,96,'5:96','Sallittua on teille meren riista ja sen ravintoantimet, itsenne sekä (pyhiin) vaeltajain ylläpidoksi; maan riista on kiellettyä teiltä vain ollessanne pyhiinvaelluksella; pitäkää vaari velvollisuudestanne Jumalaa kohtaan, jonka eteen teidät kootaan.');
INSERT INTO "finnish" VALUES(5,97,'5:97','Jumala on tehnyt Kaabasta pyhitetyn temppelin, kansan rauhoitetun paikan, asettanut pyhän kuukauden ja uhritoimitukset sekä uhrit köynnöksineen; kaiken tämän siksi, että tietäisitte Jumalan käsittävän kaiken, mitä on taivaassa ja mitä on maassa, ja että Jumala tuntee kaikki asiat.');
INSERT INTO "finnish" VALUES(5,98,'5:98','Tietäkää, että Jumala on ankara rankaisemaan pahaa, ja samalla Jumala on anteeksiantava, armollinen.');
INSERT INTO "finnish" VALUES(5,99,'5:99','Enempää ei vaadita lähettiläältä kuin että hän julistaa (Jumalan sanomaa); ja Jumala tietää, mitä te teette julkisesti ja mitä salaatte.');
INSERT INTO "finnish" VALUES(5,100,'5:100','Sano: »Ei mikään paha vedä vertoja hyvälle, vaikka teitä voi miellyttää syntinen yltäkylläisyys; pitäkää siis vaari (velvollisuudestanne) Jumalaa kohtaan, te ymmärtäväiset miehet, jotta teillä olisi menestystä.');
INSERT INTO "finnish" VALUES(5,101,'5:101','Te, jotka uskotte! Älkää kyselkö asioita, jotka - jos ne teille julistettaisiin - voisivat teitä vaivata; ja jos te kyselette niitä Koraanin julistuksen yhteydessä, niin ne teille ilmoitetaan; Jumala suo tämän anteeksi, onhan Jumala armahtavainen ja lempeä.');
INSERT INTO "finnish" VALUES(5,102,'5:102','Ennen teitä elänyt kansa myöskin kyseli sellaisia asioita, ja silloin heistä tuli epäuskoisia.');
INSERT INTO "finnish" VALUES(5,103,'5:103','Jumala ei ole käskenyt (tehdä) bahiraa eikä såibaa eikä wasilaa eikä haamia, mutta harhauskoiset sepittelevät valheita Jumalasta, eikä monikaan heistä mitään ymmärrä.');
INSERT INTO "finnish" VALUES(5,104,'5:104','Ja kun heille sanotaan: »Käykäämme siihen, minkä Jumala on ilmaissut, ja liittykäämme profeettaan!», he sanovat: »Meille riittää se, mitä tiedämme isiemme palvelleen.» Niinkö! Vaikka heidän isänsä eivät mitään tienneet eivätkä totuuden tietä kulkeneet.');
INSERT INTO "finnish" VALUES(5,105,'5:105','Te, otka uskotte! Ottakaa vaari sieluistanne! Se, joka itse on harhassa, ei voi teitä vahingoittaa, kun vain te olette oikealla tiellä; Jumalan eteen on teidän palattava kaikkien, Hän teille luettelee tekonne.');
INSERT INTO "finnish" VALUES(5,106,'5:106','Te, jotka uskotte! Kun kuolema jotakuta teistä lähestyy ja testamentin teko on käsillä, kutsukaa keskuudestanne kaksi oikeamielistä miestä todistajiksi asioissanne, tai sitten kaksi vierasta henkilöä, jos olette matkoilla ja osanne on siellä kuolla; pyytäkää heitä (todistajia) viipymään jälkeen rukouksen, jos heitä epäilette, ja heidän on kummankin vannottava Jumalan nimeen sanoen: »Me emme tule vaatimaan siitä maksua, vaikka olisikin joku sukulainen (keneltä vaatia), emmekä me salaa Jumalan nimeen annettavaamme todistusta; kuuluisimme totisesti silloin syntisiin.');
INSERT INTO "finnish" VALUES(5,107,'5:107','Jos käy (myöhemmin) ilmi, että he molemmat ovat tehneet vääryyttä, on kahden muun astuttava heidän sijaansa niiden joukosta, joilla on heitä vastaan syytös, kahden lähimmän sukulaisen; näiden on nyt vannottava Jumalan nimeen: »Totisesti on meidän todistuksemme oikeampi kuin niiden kahden, emmekä missään ole totuutta loukanneet, sillä kuuluisimmehan silloin väärintekijöihin.»');
INSERT INTO "finnish" VALUES(5,108,'5:108','Näin on oikeampi menetellä, jotta (testamentin) todistajat esittäisivät oikean todistuksen, peläten, että heidän valansa jälkeen muutkin vannovat valansa asiassa; pitäkää vaari velvollisuudestanne Jumalaa kohtaan ja olkaa kuuliaiset; Jumala ei johdata lainrikkojia.');
INSERT INTO "finnish" VALUES(5,109,'5:109','Sinä päivänä, jolloin Jumala kokoaa profeettansa, tullaan sanomaan: »Mitkä vastaukset teille annettiin?» He sanovat: »Meillä ei ole mitään tietoa, totisesti Sinä olet suuri näkymättömien asioiden tietäjä.»');
INSERT INTO "finnish" VALUES(5,110,'5:110','Kun Jumala sanoo: »Oi, Jeesus, Marian poika! Muistatko Minun suosiotani sinua ja äitiäsi kohtaan, kun minä vahvistin (sinua) pyhällä ilmestyksellä; (niin että sinä puhuit) ihmisille sekä kehdossa että vanhempana; kun Minä opetin sinulle Kirjoitukset ja viisauden, joka on Toorassa ja evankeliumissa ja kun sinä (muovailit) savea linnun näköiseksi - minun luvallani, ja sinä puhalsit hengen siihen ja minä sallin sen tulla linnuksi; ja kun sinä paransit sokeita ja spitaalisia minun luvallani, ja kun minä hillitsin Israelin lapset sinun tullessasi heidän luokseen selvin todistein, jolloin epäuskoiset heidän joukossaan sanoivat: »Eihän tämä ole muuta kuin ilmeistä noituutta.»');
INSERT INTO "finnish" VALUES(5,111,'5:111','Ja kun Minä annoin opetuslapsille ilmestykseni sanoen: »Uskokaa Minuun ja Minun lähettilääseeni!» he vastasivat: »Me uskomme ja tunnustamme, että me alistumme (Jumalan tahtoon).»');
INSERT INTO "finnish" VALUES(5,112,'5:112','Ja kun opetuslapset sanoivat: »Jeesus, Marian poika, eikö Herrasi suostu lähettämään meille pöytää taivaasta?», niin Hän sanoi- »Pitäkää vaari velvollisuudestanne Jumalaa kohtaan, jos olette uskovaisia.»');
INSERT INTO "finnish" VALUES(5,113,'5:113','He sanoivat: »Me haluamme ravita itsemme sen ruoalla, jotta meidän sydämemme saisi rauhan, jotta me uskoisimme sinun puhuneen meille totta ja jotta me voisimme sitä edelleen todistaa.»');
INSERT INTO "finnish" VALUES(5,114,'5:114','Jeesus, Marian poika, sanoi: »Jumala, meidän Herramme ! Lähetä meille pöytä taivaasta alati uudistuvaksi tyydytykseksemme, niin hyvin ensimmäiselle kuin viimeisellekin meistä, lähetä meille tunnusmerkki itsestäsi ja suo meille elatus! Sillä Sinä olet paras huoltaja.»');
INSERT INTO "finnish" VALUES(5,115,'5:115','Jumala sanoi: »Totisesti lähetän Minä sen teille, mutta kuka ikinä teistä sen jälkeen horjuu uskossaan, häntä minä kuritan totisesti sellaisella rangaistuksella, jolla en ole ainoatakaan muuta kansaa kurittanut.»');
INSERT INTO "finnish" VALUES(5,116,'5:116','Ja kun Jumala sanoo: »Oi Jeesus, Marian poika! Oletko sanonut ihmisille: »Ottakaa minut ja minun äitini kahdeksi jumalaksenne Jumalan ohella», hän (Jeesus) vastaa: »Ylistys olkoon Sinulle, en voinut sanoa sellaista, johon minulla ei ollut oikeutta; jos olisin niin sanonut, Sinä olisit varmasti sen tietänyt; Sinä tiedät, mitä on minun mielessäni, mutta minä en tiedä, mitkä ovat Sinun aivoituksesi: totisesti olet Sinä suuri näkymättömien asioiden tuntija.');
INSERT INTO "finnish" VALUES(5,117,'5:117','En minä sanonut heille muuta, kuin minkä Sinä minulle uskoit: »Palvelkaa Jumalaa, minun Herraani ja teidän - Herraanne! ja minä pidin vaarin heistä niin kauan, kuin minä olin heidän keskuudessaan, mutta kun Sinä annoit minun kuolla, Sinusta tuli heidän kaitsijansa, ja Sinä olet kaiken tapahtuvan todistaja.');
INSERT INTO "finnish" VALUES(5,118,'5:118','Jos Sinä suvaitset kurittaa heitä, niin ovathan he totisesti Sinun palvelijoitasi, ja jos Sinä suvaitset heille anteeksi antaa, niin olethan Sinä mahtava ja viisas.»');
INSERT INTO "finnish" VALUES(5,119,'5:119','Ja Jumala sanoo: »Tämä on päivä, jolloin heidän totuutensa hyödyttää totuudessa pysyviä; nämä saavat asustaa puutarhoissa, joissa purot solisevat, ja viipyä niissä iäti. Heillä on Jumalan mielisuosio, ja he ovat autuaat Jumalassaan; tämä on ylin onni.');
INSERT INTO "finnish" VALUES(5,120,'5:120','Jumalan on kuninkuus taivaassa ja maan päällä ja kaiken yli, mikä niissä on; Hänen vallassaan on kaikki.');
INSERT INTO "finnish" VALUES(6,1,'6:1','Ylistys ja kunnia Jumalalle, joka on luonut taivaat ja maan, säätänyt pimeyden ja valon. Kuitenkin asettavat uskottomat muitakin Herransa rinnalle.');
INSERT INTO "finnish" VALUES(6,2,'6:2','Hänhän loi teidät savesta; ja Hän myös rajoitti aikanne. Hänen vallassaan ovat aikojen ääret. Sittenkin te epäilette.');
INSERT INTO "finnish" VALUES(6,3,'6:3','Niin, Hän on Jumala taivaassa ja maan päällä; Hän tietää, mitä salaatte ja mitä ilmaisette, ja Hän tietää mitä ansaitsette.');
INSERT INTO "finnish" VALUES(6,4,'6:4','Eikä yksikään heidän Herransa tunnusmerkeistä saavuta heitä niin, etteivät he kääntyisi siitä pois.');
INSERT INTO "finnish" VALUES(6,5,'6:5','Ovathan he vääristelleet totuuden sen tullessa heidän luoksensa, mutta pian on heille tuleva sanoma siitä, mitä he ovat pilkkanaan pitäneet.');
INSERT INTO "finnish" VALUES(6,6,'6:6','Eivätkö he sitten näe, kuinka monet sukupolvet, joiden Me olimme sallinut juurtua maan päällä lujemmin kuin teidän, Me olemme hävittänyt ennen heitä? Olemme kaatanut taivaasta heidän ylitseen runsaita sateita ja antanut jokien virrata heidän alangoillaan, mutta sitten hävitimme heidät heidän syntiensä tähden ja annoimme toisen sukupolven nousta heidän jälkeensä.');
INSERT INTO "finnish" VALUES(6,7,'6:7','Vaikka olisimme lähettänyt sinulle, Muhammed, Kirjoituksen paperilla, ja vaikka he olisivat saaneet sitä käsin kosketella, niin olisivat he, uskottomat, kuitenkin sanoneet: »Tämä ei ole muuta kuin ilmeistä noituutta.»');
INSERT INTO "finnish" VALUES(6,8,'6:8','Sanovathan he. »Miksi ei enkeliä ole lähetetty heidän luoksensa?» Mutta jos olisimme lähettänyt enkelin, olisi asiassa tuomiokin tapahtunut, eikä olisi jätetty heille enempää harkinta-aikaa.');
INSERT INTO "finnish" VALUES(6,9,'6:9','Ja jos olisimme käyttänyt enkeliä sanansaattajana, niin olisimme kuitenkin pukenut hänet ihmishahmoon ja siten pannut alttiiksi samoille epäilyksille, joita heillä nyt on.');
INSERT INTO "finnish" VALUES(6,10,'6:10','Onhan pilkattu (Jumalan )lähettiläitäkin ennen Sinua, mutta sitten onkin se, mistä he pilkkaa tekivät, kohdannut ilkkujat.');
INSERT INTO "finnish" VALUES(6,11,'6:11','Sano:»Vaeltakaa ympäri maata ja nähkää, minkä lopun saivat ne, jotka pitivät totuutta valheena!»');
INSERT INTO "finnish" VALUES(6,12,'6:12','Sano:»Kenelle kuulu kaikki taivaassa ja maan päällä?».Vastaa:»Jumalalle». Laupeuden hoidon on Hän pidättänyt itselleen; Hän on totisesti kokoava teidät yhteen ylösnousemuksen päivänä, siitä ei ole epäilystäkään. Mutta ne, jotka sielunsa turmelevat, eivät usko.');
INSERT INTO "finnish" VALUES(6,13,'6:13','Kaikki, mikä on olemassa yöllä ja päivällä, kuuluu Hänelle; Hän on kuuleva, tietävä.');
INSERT INTO "finnish" VALUES(6,14,'6:14','Sano:»Valitsisinko suojelijakseni jonkun muun kuin Jumalan, taivaitten ja maan Luojan, hänet,joka ravitsee eikä itse ravintoa nauti?» Sano:»Minut on totisesti määrätty ensimmäiseksi, joka Hänen tahtoonsa alistuu. Älä kuulu epäjumalien palvelijoihin.»');
INSERT INTO "finnish" VALUES(6,15,'6:15','Sano:» Jos uhmailen Herraani, saan pelätä rangaistuspäivän tuskaa.»');
INSERT INTO "finnish" VALUES(6,16,'6:16','Hänet, jonka ohi rangaistus käy sinä päivänä, on Herra todella armahtanut; tämä on ilmeinen onni.');
INSERT INTO "finnish" VALUES(6,17,'6:17','Ja jos Jumala saattaa kohtalosi kovaksi, ei ole ketään paitsi Hän itse, joka sen lievittäisi, mutta jos Hän vuodattaa ylitsesi hyvyyttä, (ken voisi sen pilata, kun) Hänellä on kaikki vallassaan.');
INSERT INTO "finnish" VALUES(6,18,'6:18','Hän on kaikkivoipa orjiensa valtias; Hän on viisas, kaikki tietävä.');
INSERT INTO "finnish" VALUES(6,19,'6:19','Sano:» Mikä olisi painavin todistus?» Sano:» Jumala on todistaja minun ja teidän välillänne, ja minulle on tämä Koraani ilmestyksenä annettu, jotta minä sen kautta varoittaisin teitä ja jokaista, jonka se saavuttaa. Todistaisitteko silloin todellakin, että on muitakin jumalia kuin Jumala?» Sano: »Sellaista en todista.» Sano: »Hän on ainoa Jumala, enkä totisesti vastaa siitä, mitä te Hänen rinnalleen olette keksineet.»');
INSERT INTO "finnish" VALUES(6,20,'6:20','Ne, joille Me olemme antanut Pyhän Kirjoituksen, tuntevat hänet (Muhammedin) yhtä hyvin kuin omat poikansa, mutta ne, jotka turmelevat sielunsa, eivät usko.');
INSERT INTO "finnish" VALUES(6,21,'6:21','Kuka tekee suurempaa vääryyttä kuin se, joka sepittää valhetta Jumalasta tai vääristelee Hänen tunnusmerkkinsä valheeksi? Totisesti ei väärintekijöille tule hyvin käymään.');
INSERT INTO "finnish" VALUES(6,22,'6:22','Sinä päivänä, jolloin Me kokoamme heidät kaikki yhteen, sanomme Me niille, jotka asettavat muita Jumalan rinnalle: »Missä ovat nyt ne Jumalan vertaiset, joilla olette uskotelleet?»');
INSERT INTO "finnish" VALUES(6,23,'6:23','Eivätkä he silloin keksi muuta puolustusta kuin sanovat: »Jumalan, Herramme nimeen, me emme ole epäjumalia Hänen rinnalleen asettaneet.»');
INSERT INTO "finnish" VALUES(6,24,'6:24','Katso, kuinka he sotkeutuvat valheisiinsa, ja kuinka kaikki, mitä he ovat sepittäneet, luhistuen pettää heidät!');
INSERT INTO "finnish" VALUES(6,25,'6:25','Heidän joukossaan on sellaisiakin, jotka sinua kuuntelevat, mutta Me olemme verholla peittäneet heidän sydämensä, niin etteivät he voi ymmärtää sitä (Koraania) ja heidän korvansa ovat tukitut, ja vaikka he näkisivät mitä tunnusmerkkejä, eivät he usko niihin, niin että uskottomat tullessaan luoksesi väittelemään kanssasi sanovat: »Nämä ovat vain niitä entisten ihmisten juttuja.»');
INSERT INTO "finnish" VALUES(6,26,'6:26','He estelevät muita totuuteen pyrkijöitä ja itse karttavat sitä, mutta he turmelevat vain itsensä eivätkä sitä tajua.');
INSERT INTO "finnish" VALUES(6,27,'6:27','Jospa voisit nähdä, kun heidät asetetaan tulen eteen ja he sanovat: »Voi, että voisimme palata takaisin! Silloin emme väittäisi Herramme tunnusmerkkejä valheeksi, vaan kuuluisimme oikeauskoisiin!»');
INSERT INTO "finnish" VALUES(6,28,'6:28','Heille kirkastetaan se, minkä he ennen salasivat, mutta vaikka he saisivatkin palata, kääntyisivät he kuitenkin siihen, mikä on heiltä kielletty; totisesti he ovat valehtelijoita.');
INSERT INTO "finnish" VALUES(6,29,'6:29','Sanovatpa he: »Ei ole muuta kuin tämä nykyinen elämämme eikä meitä herätetä kuolleista.»');
INSERT INTO "finnish" VALUES(6,30,'6:30','Jospa voisit nähdä, kun heidät asetetaan Herransa eteen! Hän on sanova: »Eikö tämä ole totinen todellisuus?» He vastaavat: »On, Herramme nimessä!» Hän on jatkava: »Maistakaa siis rangaistusta, koska ette uskoneet!»');
INSERT INTO "finnish" VALUES(6,31,'6:31','Kadotuksen omia ovat todella ne, jotka pitävät Jumalan kohtaamista valheena, kunnes se hetki heidät yllättää ja he puhkeavat sanomaan: »Voi meitä laiminlyöntimme tähden!» He saavat kantaa taakkansa selässään. Eikö ole julma taakka, jota he joutuvat kantamaan?');
INSERT INTO "finnish" VALUES(6,32,'6:32','Maallinen elämä on vain leikkiä ja pilaa; taivaallinen koti on totisesti parempi niille, jotka Jumalaa pelkäävät. Ettekö sittenkään käsitä?');
INSERT INTO "finnish" VALUES(6,33,'6:33','Me tiedämme (Muhammed), että heidän puheensa sinua totisesti surettavat; he eivät tosin väitä sinua valehtelijaksi, mutta väärämieliset kieltävät Jumalan tunnusmerkit.');
INSERT INTO "finnish" VALUES(6,34,'6:34','Jo ennen sinua lähettiläitämme on pidetty valehtelijoina, mutta he ovat kärsivällisesti kestäneet sen, että heitä on paneteltu ja vainottu, kunnes apumme on heidät saavuttanut; kukaan ei voi Jumalan sanaa muuttaa. Ja sinähän jo tunnet paljon (entisten) lähettiläitten sanomasta.');
INSERT INTO "finnish" VALUES(6,35,'6:35','Ja jos heidän ynseytensä on sinusta liian masentavaa, niin koeta, ehkä löydät reiän maan uumeniin tai tikapuut taivaaseen, jos haluat kaikille kelpaavaa tunnusmerkkiä, mutta jos Jumala olisi tahtonut, Hän kyllä olisi saanut heidät liittymään johdatukseensa; älä ole siis järjettömän kaltainen!');
INSERT INTO "finnish" VALUES(6,36,'6:36','Vain ne ovat alttiita, jotka Häntä kuuntelevat; mutta kuolleet on Jumala herättävä; silloin palaavat he Hänen luokseen.');
INSERT INTO "finnish" VALUES(6,37,'6:37','He sanovat vielä: »Miksi ei hänen Herransa ole lähettänyt hänelle tunnusmerkkiä?» Sano: »Jumalalla on totisesti valta lähettää tunnusmerkki ylhäältä, mutta useimmat heistä eivät sitä tunne.»');
INSERT INTO "finnish" VALUES(6,38,'6:38','Ei ole eläintä maan päällä, ei lintua, joka kahdella siivellään lentää, jotka eivät olisi kaikkeuteen kuuluvaisia kuten tekin; emme mitään ole jättänyt pois (luomislakiemme) kirjasta. Sitten kootaan heidät yhteen Herransa luo.');
INSERT INTO "finnish" VALUES(6,39,'6:39','Mutta ne, jotka väittävät tunnusmerkkimme valheeksi, ovat kuuroja, mykkiä ja pimeyden vallassa; Jumala johtaa harhaan kenet tahtoo ja opastaa oikealle tielle kenet suvaitsee.');
INSERT INTO "finnish" VALUES(6,40,'6:40','Sano: »Uskotteko te, että Jumalan rangaistuksen kohdatessa teitä tai tuomiohetken teidät yllättäessä te huudatte avuksenne muuta kuin Jumalaa? (Vastatkaa niin kuin on), jos totuutta rakastatte.');
INSERT INTO "finnish" VALUES(6,41,'6:41','Ei, Häntä yksin olette te avuksenne huutavat, ja Hän on, jos tahtoo, torjuva sen, mitä te torjumaan pyydätte, ja te tulette unohtamaan sen, minkä asetatte Hänen rinnalleen.');
INSERT INTO "finnish" VALUES(6,42,'6:42','Olemmehan lähettänyt (ilmoituksemme) kansoille ennen sinua ja koetellut niitä vaivalla ja vastoinkäymisellä, jotta ne nöyrtyisivät.');
INSERT INTO "finnish" VALUES(6,43,'6:43','Jospa he olisivat nöyrtyneet, kun lähettämämme vaiva kohtasi heidät! Mutta heidän sydämensä paatuivat ja Saatana kaunisti heille kaikki heidän tekonsa.');
INSERT INTO "finnish" VALUES(6,44,'6:44','Kun he olivat unohtaneet saamansa varoitukset, Me avasimme kaikki portit heille, kunnes Me lopuksi äkkiä yllätimme heidät, juuri kun he saamastaan eniten iloitsivat, ja silloin, katso, epätoivo valtasi heidät.');
INSERT INTO "finnish" VALUES(6,45,'6:45','Niin hävitettiin juurineen väärintekijät viimeiseen mieheen; ylistys ja kunnia Jumalalle, maailmojen Herralle!');
INSERT INTO "finnish" VALUES(6,46,'6:46','Sano: »Näyttääkö teistä siltä, jos Jumala riistää teiltä kuulon ja näön ja sulkee sinetillään sydämenne, että on olemassa joku muu kuin Jumala, joka voisi ne teille takaisin antaa?» Katso, miten moninaisia vertauskuvia Me käytämme. Mutta he kääntyvät pois.');
INSERT INTO "finnish" VALUES(6,47,'6:47','Sano: »Mitä arvelette, jos Jumalan rangaistus kohtaa teidät odottamatta tai ilmeisesti, hävitetäänkö muita kuin väärämieliset ihmiset?»');
INSERT INTO "finnish" VALUES(6,48,'6:48','Me lähetämme lähettiläitä vain ilosanoman tuojiksi ja varoittajiksi, mutta niiden, jotka uskovat ja oikein tekevät, ei tarvitse pelätä eikä murehtia.');
INSERT INTO "finnish" VALUES(6,49,'6:49','Mutta niitä, jotka väittävät tunnusmerkkimme valheeksi, on rangaistus kohtaava heidän jumalattomuutensa tähden.');
INSERT INTO "finnish" VALUES(6,50,'6:50','Sano: »En minä väitä teille, että omistan kaikki Jumalan aatteet tai että tunnen silmiltä salatun, enkä minä sano teille olevani henkiolento, enkeli; minä vain noudatan sitä, mikä minulle on ilmoitettua» Sano: »Ovatko sokea ja näkevä yhdenvertaisia? Eikö teillä ole arvostelukykyä?»');
INSERT INTO "finnish" VALUES(6,51,'6:51','Varoita tällä (Koraanilla) niitä, jotka pelkäävät, että heidät kootaan Herransa eteen, eikä heillä paitsi Häntä ole mitään puoltajaa eikä välittäjää, jotta he pelkäisivät Jumalaa.');
INSERT INTO "finnish" VALUES(6,52,'6:52','Aläkä karkoita pois niitä, jotka rukoilevat Herraansa aamuin ja illoin halaten Hänen läheisyyttään. Ei ole sinun asiasi tehdä tiliä heistä, eivätkä he ole vastuussa sinusta; karkoittaessasi heidät tulisit sinäkin väärintekijäksi.');
INSERT INTO "finnish" VALUES(6,53,'6:53','Me koettelemme heitä toistensa kautta aina siihen saakka, että he sanovat: »Nämäkö ovat niitä joukostamme, joille Jumala on suonut armonsa?» Eikö Jumala arvaa, ketkä ovat kiitollisia?');
INSERT INTO "finnish" VALUES(6,54,'6:54','Kun luoksesi tulevat ne, jotka uskovat tunnusmerkkeihimme, sano: »Rauha olkoon teille! Herranne on pidättänyt itselleen laupeuden: jos joku teistä tekee väärin ymmärtämättömyydessään ja sitten kääntyy ja tekee oikein, niin on Hän armollinen ja lempeä.»');
INSERT INTO "finnish" VALUES(6,55,'6:55','Siten selitämme Me tunnusmerkkimme, jotta väärintekijöiden tie tunnettaisiin.');
INSERT INTO "finnish" VALUES(6,56,'6:56','Sano: »Minua on totisesti kielletty palvelemasta niitä, joita te rukoilette Jumalan vertaisina.» Sano: »En noudata teidän päähänpistojanne; silloin olisin joutunut eksyksiin enkä kuuluisi niihin, jotka oikeata johdatusta seuraavat.»');
INSERT INTO "finnish" VALUES(6,57,'6:57','Sano: »Minä luotan totisesti Herrani selvään johdatukseen, mutta te sen hylkäätte. Ei ole minun vallassani se, mitä te haluatte jouduttaa. Ratkaisu kuuluu yksin Jumalalle; Hän pitää huolen oikeudesta ja on paras tuomari.»');
INSERT INTO "finnish" VALUES(6,58,'6:58','Sano: »Jos olisi minun vallassani se, mitä te haluatte jouduttaa, olisi asia ratkaistu minun ja teidän välillänne; mutta Jumala tuntee väärintekijät paremmin (kuin minä).»');
INSERT INTO "finnish" VALUES(6,59,'6:59','Hänen hallussaan ovat kaiken näkymättömän avaimet; ei kukaan muu sitä tunne kuin Hän yksin. Hän tietää kaiken maan päällä ja meressä; Hänen tietämättään ei ainoakaan lehti putoa, ei piile yksikään jyvä maan uumenissa eikä ole kosteata eikä kuivaa, joka ei olisi selvästi (Jumalan) kirjoissa.');
INSERT INTO "finnish" VALUES(6,60,'6:60','Hän se ottaa teidät kokonaan haltuunsa yöksi ja tietää toimenne päivällä. Vihdoin on hän teidät herättävä, jotta täyttyisi määrätty aika. Silloin on teidän palattava Hänen luokseen, ja Hän on paljastava teille entiset tekonne.');
INSERT INTO "finnish" VALUES(6,61,'6:61','Hän on kaikkivoipa orjiensa Valtias, ja Hän lähettää suojelijansa luoksenne, kunnes kuoleman jonkun teistä kohdatessa enkelimme tekevät hänelle täyden palveluksen, mitään laiminlyömättä.');
INSERT INTO "finnish" VALUES(6,62,'6:62','Sitten palaavat he Jumalan luo, joka totisesti on heidän Valtiaansa; eikö tuomio ole Hänen? Eikö Hän ole nopein tilinteoissa?»');
INSERT INTO "finnish" VALUES(6,63,'6:63','Sano: »Kuka pelastaa teidät maan ja meren kauhuista (pimeydestä), kun rukoilette Häneltä apua nöyryydessä ja hiljaisuudessa? Koska Hän meidät niistä pelastaa, tulee meidän totisesti olla kiitollisia.»');
INSERT INTO "finnish" VALUES(6,64,'6:64','Sano: »Jumala pelastaa teidät niistä ja kaikista muista ahdingoista, ja kuitenkin asetatte te muita Hänen rinnalleen.»');
INSERT INTO "finnish" VALUES(6,65,'6:65','Sano: »Hänellä on valta lähettää teille rangaistus ylhäältä tai nostaa se teille jalkojenne alta tai saattaa teidät hajaannukseen ja lahkoihin tai antaa teidän maistaa toistenne mielivaltaa.» Katso, miten vaihtelevia kuvauksia me esitämme, jotta he käsittäisivät!');
INSERT INTO "finnish" VALUES(6,66,'6:66','Mutta sinun kansasi pitää sitä (Koraania) valheena, vaikka se on itse totuus. Sano: »Minä en ole teidän hoitajanne;');
INSERT INTO "finnish" VALUES(6,67,'6:67','jokaisella tapahtumalla on määrätty hetkensä, ja te tulette kokemaan sen aikanansa.»');
INSERT INTO "finnish" VALUES(6,68,'6:68','Ja kun näet niitä, jotka puhuvat joutavia tunnusmerkeistämme, niin käänny pois heistä, kunnes he rupeavat pohtimaan jotakin muuta, mutta jos Saatana saisi sinut unohtamaan (tämän säännön), niin älä kuitenkaan jää istumaan vääräuskoisen seuraan, kun se taas muistiisi tulee!');
INSERT INTO "finnish" VALUES(6,69,'6:69','Jumalaapelkääväiset eivät ole vastuussa heistä, mutta varoittakoot heitä, jotta hekin pelkäisivät Jumalaa.');
INSERT INTO "finnish" VALUES(6,70,'6:70','Jätä ne, joiden uskonto on leikintekoa ja ajanvietettä ja jotka maallinen elämä saa pauloihinsa, mutta varoita heitä tällä (Koraanilla), jottei yksikään sielu hukkuisi pahojen tekojensa tähden. Eihän sillä ole, paitsi Jumalaa, ketään suojelijaa eikä opastajaa, ja vaikka se tarjoaisikin korvausta kaikesta, minkä voi korvata, ei sitä vastaanotettaisi. Niitä, jotka hukkuvat pahojen töittensä tähden, odottaa kiehuva juoma ja tuskallinen rangaistus, siksi että he jumalattomuutta harjoittavat.');
INSERT INTO "finnish" VALUES(6,71,'6:71','Sano: »Rukoilisimmeko Jumalan asemesta sellaista, mikä ei voi meitä hyödyttää eikä vahingoittaa? Ja senjälkeen, kun Jumala on meitä johdattanut, kääntyisimmekö pois aivan kuin se, jonka pahat henget ovat eksyttäneet, niin että hän tulee aivan neuvottomaksi maan päällä, vaikka hänellä on ystäviä, jotka kutsuvat häntä oikealle tielle näin sanoen: »Tule luoksemme!» Sano: »Jumalan johdatus on totisesti oikea johdatus, ja meidät on määrätty alistumaan maailmojen Valtiaan alamaisiksi.»');
INSERT INTO "finnish" VALUES(6,72,'6:72','Ja pysykää rukouksessa sekä Herran pelossa, sillä Hänen luoksensa teidän on kokoonnuttava.');
INSERT INTO "finnish" VALUES(6,73,'6:73','Hän totuudessaan on luonut taivaat ja maan, ja sinä päivänä, jolloin Hän sanoo: »Tapahtukoon!» tapahtuu niin. Hänen sanansa on totuus. Hänen on valta sinä päivänä, jolloin ylösnousemuksen pasuunaa puhalletaan. Hän tuntee kaiken salatun ja kaiken julkisen; Hän on viisas ja kaikkitietävä.');
INSERT INTO "finnish" VALUES(6,74,'6:74','Entä kun Aabraham sanoi isälleen Azarille: »Pidätkö epäjumalia Jumalan vertaisina? Näen totisesti, että sinä ja kansasi olette ilmeisessa harhassa.»');
INSERT INTO "finnish" VALUES(6,75,'6:75','Ja tällä tavoin näytimme Me Aabrahamille taivaitten ja maan valtakunnan, jotta hän kuuluisi uskostaan varmoihin.');
INSERT INTO "finnish" VALUES(6,76,'6:76','Kun yö pimeni hänen ylleen, havaitsi hän tähden ja sanoi: »Tämä kai on Herrani», mutta kun se laski, sanoi hän: »En rakasta niitä, jotka painuvat alas.»');
INSERT INTO "finnish" VALUES(6,77,'6:77','Ja kun hän näki kuun nousevan, sanoi hän: »Tämä kai on Herrani», mutta kun se laski, sanoi hän: »Jollei Herrani johda minua, joudun minä totisesti harhaan.»');
INSERT INTO "finnish" VALUES(6,78,'6:78','Ja kun hän näki auringon nousevan, sanoi hän: »Tämä kai on Herrani, onhan se suurin.» Mutta kun se laski, sanoi hän: »Kansani, minulla ei ole tekemistä.minkään kanssa, jonka te asetatte Jumalan rinnalle.');
INSERT INTO "finnish" VALUES(6,79,'6:79','Minä käännän kasvoni sen puoleen, joka on luonut taivaat ja maan, olen oikeauskoinen enkä monijumalainen.»');
INSERT INTO "finnish" VALUES(6,80,'6:80','Mutta hänen kansansa kiisteli hänen kanssaan, ja hän sanoi: »Kiistelettekö kanssani Jumalasta, vaikka Hän johdattaa minua? En minä pelkää niitä, mitä asetatte Hänen rinnalleen, mutta minulla on Herran pelko täyttääkseni Hänen tahtonsa. Minun Herrani viisaus vallitsee kaikkea, ettekö te tosiaan sitä ymmärrä?');
INSERT INTO "finnish" VALUES(6,81,'6:81','Kuinka voisin pelätä sitä, minkä te Hänen rinnalleen asetatte, kun te ette edes pelkää asettaa Jumalan rinnalle sellaista, jota Hän ei ole ylhäältä ilmoittanut? Kumpi uskonsuunta paremmin ansaitsee olla rauhassa, vastatkaa, jos pystytte asioita käsittämään?»');
INSERT INTO "finnish" VALUES(6,82,'6:82','Ne, jotka uskovat eivätkä pimennä uskoaan väärillä teoilla, pysyvät levollisina ja nauttivat oikeata Johdatusta.');
INSERT INTO "finnish" VALUES(6,83,'6:83','Nämä olivat todistukset, jotka annoimme Aabrahamille hänen kansaansa vastaan. Ne, jotka haluamme, korotamme Me ylempään tietoon; Sinun Herrasi on totisesti viisas, tietävä.');
INSERT INTO "finnish" VALUES(6,84,'6:84','Ja me annoimme hänelle Iisakin ja Jaakobin - kumpaakin Me johdatimme - ja Noakia olimme muinoin johdattaneet - ja hänen jälkeläisistään Daavidia, Salomonia, Jobia, Joosefia, Moosesta ja Aaronia - samoin kuin heidät palkitsemme ne, jotka hyvää tekevät -');
INSERT INTO "finnish" VALUES(6,85,'6:85','Sakarias, Johannes, Jeesus, Elias, - kaikki olivat he vanhurskaita.');
INSERT INTO "finnish" VALUES(6,86,'6:86','Myös Ismael, Elisa, Joonas ja Loot; jokaisen näistä korotimme Me muiden aikalaistensa yläpuolelle.');
INSERT INTO "finnish" VALUES(6,87,'6:87','Samoin muutamia heidän isistään, heidän jälkeläisistään ja veljistään; Me valitsimme heidät ja johdatimme oikealle tielle.');
INSERT INTO "finnish" VALUES(6,88,'6:88','Tämä on Jumalan johdatus; jolla hän valintansa mukaan ohjaa palvelijoitaan, mutta jos he asettavat muita Hänen rinnalleen, niin totisesti osoittautuvat turhiksi heidän aherruksensa.');
INSERT INTO "finnish" VALUES(6,89,'6:89','Nämä ovat niitä, joille olemme antanut Pyhän kirjoituksen, viisauden ja profeetan kutsumuksen. Mutta jos nämä eivät niihin usko, olemme Me totisesti antava ne kansalle, joka ei niitä epäile.');
INSERT INTO "finnish" VALUES(6,90,'6:90','Nämä ovat väkeä, jota Jumala on johdattanut; seuraa siis heille suotua opastusta. Sano: »Minä en pyydä palkkaa teiltä; tämä on ainoastaan muistutus muulle maailmalle.»');
INSERT INTO "finnish" VALUES(6,91,'6:91','He eivät antaneet Jumalalle sitä arvoa, jonka Hän ansaitsee, kun he sanoivat: »Jumala ei ole lähettänyt ylhäältä mitään (ilmoitusta) kenellekään ihmisolennolle.» Sano: »Kuka lähetti sitten Pyhän kirjoituksen, jonka Mooses toi valoksi ja johdatukseksi ihmisille? Te panitte sen muistiin hajanaisina kirjoituksina, joista osan teitte tunnetuksi ja paljon salasitte, vaikka itse opittekin sen, mitä ette tietäneet, - ette te eivätkä isänne.» - Sano: »Jumala» (sen ilmoitti) ja jätä heidät sitten huvittelemaan joutavilla jutuillaan.');
INSERT INTO "finnish" VALUES(6,92,'6:92','Tämä on siunattu kirjoitus, jonka Me lähetimme ylhäältä; se vahvistaa aikaisemmin olleen ja opastaa sinua varoittaessasi kaikkien kaupunkien äitiä (Mekkaa) ja niitä, jotka sen ympärillä asuvat. Ne, jotka uskovat tulevaan elämään, uskovat siihen; he pitävät tarkoin vaarin rukouksistaan.');
INSERT INTO "finnish" VALUES(6,93,'6:93','Ken tekee suurempaa vääryyttä, kuin se, joka sepittää valhetta Jumalaa vastaan tai sanoo: »Olen saanut ilmestyksen», vaikkei hänelle mitään ilmestystä ole annettu; tai se, joka sanoo: »Minäkin voin ilmoittaa samanlaista, kuin minkä Jumala on ylhäältä ilmoittanut.» Jospa voisit nähdä väärintekijät heidän ollessaan kuolemantuskissa ja enkelien ojentaessa kätensä sanoen: »Luovuttakaa henkenne! Tänä päivänä saatte häpeällisen rangaistuksen siitä, mitä valheellisesti ja perättömästi olette Jumalasta puhuneet ja ylpeinä ylenkatsoneet Hänen tunnusmerkkinsä!');
INSERT INTO "finnish" VALUES(6,94,'6:94','Olettehan nyt saapuneet luoksemme alastomina, sellaisina, joiksi alussa loimme teidät, ja jättäneet taaksenne kaiken, minkä olimme hoitoonne uskoneet, emmekä Me näe ympärillänne yhtäkään kannattajistanne, joitten väititte olleen teidän uskonsuuntaanne. Todellakin ovat nyt siteet välillänne katkenneet ja luulottelunne on pettänyt teidät.»');
INSERT INTO "finnish" VALUES(6,95,'6:95','Jumala antaa totisesti jyvän ja (taatelin)kiven itää, Hän synnyttää elämää siitä, mikä on kuollut, mutta Hän aikaansaa myös kuolemaa elävästä; tällainen on Jumala - keneen te sitten turvaudutte vastahakoisuudessanne?');
INSERT INTO "finnish" VALUES(6,96,'6:96','Hän, joka antaa aamun sarastaa ja on määrännyt yön lepoajaksi sekä auringon ja kuun ajanlaskemista varten; tämä on Kaikkivaltiaan ja Kaikkitietävän järjestely.');
INSERT INTO "finnish" VALUES(6,97,'6:97','Hän teille on luonut tähdet johtamaan teitä maan ja meren pimeydessä. Me olemme nyt selittänyt tunnusmerkit ymmärtäville ihmisille.');
INSERT INTO "finnish" VALUES(6,98,'6:98','Hän teidät on luonut yhdestä olennosta, ja te olette saaneet asuinsijan sekä lepopaikan. Olemme nyt selittänyt tunnusmerkit ymmärtäväisille ihmisille.');
INSERT INTO "finnish" VALUES(6,99,'6:99','Hän se antaa taivaasta vettä; siitä Me aikaansaamme kaiken kasvullisuuden alun ja siitä vihreyden, josta taasen synnytämme jyvät tähkään; ja taatelintertut kasvavat palmuissa riippuen tupistaan; samoin kasvatamme Me viinitarhat, öljypuut ja granaattiomenat, toistensa kaltaiset yhtä hyvin kuin erilaiset. Katsokaa niitten hedelmiä, kun ne muodostuvat ja kun ne kypsyvät! Tässä on kyllin tunnusmerkkejä ihmisille, jotka uskovat.');
INSERT INTO "finnish" VALUES(6,100,'6:100','Ovatpa he tehneet dzinneistä Jumalan vertaisia, vaikka Hän on ne luonut; samoin ovat he tietämättömyydessään keksineet Hänelle poikia ja tyttäriä. Hänelle yksin kaikki kunnia! Onhan Hän suunnattomasti yläpuolella sen, mitä he Hänestä väittävät.');
INSERT INTO "finnish" VALUES(6,101,'6:101','Kuinka voisikaan taivaitten ja maan Luojalla olla lapsi, kun Hänellä ei ole puolisoa? Onhan Hän luonut kaiken, ja Hänen tietonsa tunkee kaiken lävitse.');
INSERT INTO "finnish" VALUES(6,102,'6:102','Tällainen on Jumala, teidän Herranne; ei ole muuta Jumalaa kuin Hän, kaiken Luoja. Palvelkaa siis Häntä, Hän on kaiken olevaisen kaitsija.');
INSERT INTO "finnish" VALUES(6,103,'6:103','Katseet eivät Häntä saavuta, mutta Hän tavoittaa kaikki katseet; Hän on kaikista tarkkanäköisin, kaikkitietävä.');
INSERT INTO "finnish" VALUES(6,104,'6:104','Niin olette nyt saaneet todisteet Herraltanne; ken ne oivaltaa, sille koituu se hänen sielunsa hyödyksi, mutta ken on sokea, sille se on hänen omaksi vahingokseen. Ja (sano): >En minä ole vartijanne.»');
INSERT INTO "finnish" VALUES(6,105,'6:105','Niin ilmoitamme Me olemuksemme vertauskuvin, jotta he (sinulle, Muhammed,) sanovat: »Sinä olet tutkinut», samalla selittääksemme sen (Koraanin) ymmärtäväisille ihmisille.');
INSERT INTO "finnish" VALUES(6,106,'6:106','Noudata sitä, mikä on Herrasi sinulle ilmoittamaa - ei ole muuta jumalaa paitsi Hän - ja käänny pois monijumalaisista.');
INSERT INTO "finnish" VALUES(6,107,'6:107','Jos Jumala olisi tahtonut, eivät he olisi kyenneet asettamaan muita Hänen rinnalleen; mutta Me emme ole asettanut sinua heidän vartijakseen, etkä sinä ole heidän kaitsijaisa,');
INSERT INTO "finnish" VALUES(6,108,'6:108','Älkää häväiskö niitä, joita he rukoilevat Jumalan rinnalla, jotteivät he kiivaudessaan ja ymmärtämättömyydessään häväisisi Jumalaa. Niin olemme jokaiselle kansalle kaunistanut heidän tekonsa; onhan heidän senjälkeen palattava Herransa luo, ja Hän on julistava heille, mitä he ovat tehneet.');
INSERT INTO "finnish" VALUES(6,109,'6:109','He ovat vannoneet Jumalan nimeen pyhimmät valansa, että jos tunnusmerkki olisi tullut heille, olisivat he totisesti uskoneet siihen. Sano: »Tunnusmerkit kuuluvat ainoastaan Jumalalle, mutta ettekö te (muslimit) tiedä, että he eivät usko, vaikka saavat niitä?»');
INSERT INTO "finnish" VALUES(6,110,'6:110','Ja Me käännämme pois heidän sydämensä ja katseensa, samoin kuin he tekivät silloin, kun he eivät uskoneet ensimmäisellä kerralla; ja Me jätämme heidät sokeina harhailemaan uppiniskaisuudessaan.');
INSERT INTO "finnish" VALUES(6,111,'6:111','Ja vaikka antaisimme enkelien laskeutua heidän luokseen ja vaikka kuolleet puhuisivat heille ja Me keräisimme kaiken mahdollisen takeeksi heidän ympärilleen, eivät he kuitenkaan uskoisi, ellei Jumala sitä tahtoisi; sillä useimmat heistä eivät ymmärrä.');
INSERT INTO "finnish" VALUES(6,112,'6:112','Niinmuodoin olemme Me jokaiselle profeetalle asettanut vastustajia, nimittäin riivaajia ihmisten ja dzinnien joukosta, toinen toistaan ne innoittavat ulkokultaisiin valheisiin johtaakseen ihmisiä harhaan, mutta jos Herrasi olisi tahtonut, eivät he olisi (kyenneet) sitä tekemään. Jätä heidät siis kaikkine keksintöineen!');
INSERT INTO "finnish" VALUES(6,113,'6:113','Ja tämä siksi, että niiden sydämet, jotka eivät usko tulevaan elämään, kallistuisivat niiden (valheiden) puoleen ja mieltyisivät niihin ja saisivat yhä ansaita pahaa paikkaansa.');
INSERT INTO "finnish" VALUES(6,114,'6:114','Toivoisinko minä sitten erotuomariksi ketään muuta kuin Jumalaa; Hänhän teille on lähettänyt ylhäältä Pyhän Kirjoituksen sellaisena, että sen voi selittää. Ja ne, joille Me annoimme Kirjoituksen, tietävät, että Herrasi on sen totisesti lähettänyt ylhäältä. Älä kuulu siis, (Muhammed,) epäröiviin.');
INSERT INTO "finnish" VALUES(6,115,'6:115','Herrasi sanat ovat täydellisiä totuudessa ja oikeudessa; kukaan ei voi Hänen sanojaan muuttaa. Hän on kaikkikuuleva, kaikkitietävä.');
INSERT INTO "finnish" VALUES(6,116,'6:116','Mutta jos tottelet niiden enemmistöä, jotka maan päällä ovat, johtavat he sinut pois Jumalan tieltä; he eivät noudata muuta kuin yleisiä käsityksiä, eivätkä tee muuta kuin luulottelevat.');
INSERT INTO "finnish" VALUES(6,117,'6:117','Sinun Herrasi tietää todella parhaiten, kuka eksyy Hänen tieltään, ja Hän tietää myös parhaiten ne, jotka pysyvät oikeassa johdatuksessa!');
INSERT INTO "finnish" VALUES(6,118,'6:118','Syökää siis sitä, jonka yli Jumalan nimi on lausuttu, jos uskotte Hänen tunnusmerkkeihinsä!');
INSERT INTO "finnish" VALUES(6,119,'6:119','Ja mitä syytä teillä on olla syömättä sitä, minkä yli Jumalan nimi on lausuttu, kun Hän on teille jo selittänyt, mikä on teiltä kiellettyä, paitsi jos teidät siihen pakotetaan? Mutta monet ihmiset saavat totisesti toisetkin eksymään turhamaisuutensa ja tietämättömyytensä vuoksi. Herrasi tuntee parhaiten ne, jotka rikkovat.');
INSERT INTO "finnish" VALUES(6,120,'6:120','Jättäkää julkiset ja salaiset synnit. Ne, jotka tekevät syntiä, saavat palkan tekojensa mukaan.');
INSERT INTO "finnish" VALUES(6,121,'6:121','Älkääkä syökö sellaista, jonka yli ei Jumalan nimeä ole lausuttu; se on totisesti inhottavaa. Saatanat kyllä kehoittavat kannattajiaan riitelemään kanssanne, ja jos te (muslimit) tottelette heitä, tulee teistä monijumalaisia.');
INSERT INTO "finnish" VALUES(6,122,'6:122','Onkohan se, joka oli kuollut ja jonka Me herätimme eloon ja jolle annoimme valon vaeltaakseen ihmisten keskuudessa, sen kaltainen, joka pimeydessä vaeltaa voimatta päästä pois siitä? Näin kaunistamme Me uskottomille heidän tekonsa.');
INSERT INTO "finnish" VALUES(6,123,'6:123','Ja niin olemme jokaisessa kaupungissa asettanut pahoille omat johtajansa heidän vehkeilläkseen siellä, mutta he vehkeilevät vain itseään vastaan eivätkä sitä tajua.');
INSERT INTO "finnish" VALUES(6,124,'6:124','Ja tunnusmerkin tullessa heille sanovat he: »Emme ennen usko, kuin saamme samankaltaisen, kuin Jumalan lähettiläätkin ovat saaneet.» Jumala tietää kuitenkin parhaiten, kenelle Hän uskoo sanomansa. Häväistys Jumalan edessä ja raskas rangaistus on kohtaava väärintekijät vehkeilyjensä tähden.');
INSERT INTO "finnish" VALUES(6,125,'6:125','Ketä Jumala tahtoo johdattaa, hänen rintansa avaa Hän Islaamille, ja kenen hän antaa eksyä, hänen rintansa Hän tekee niin ahtaaksi ja ahdistetuksi, kuin hänen täytyisi kiivetä taivaaseen. Siten saattaa Jumala häpeään ne, jotka eivät usko.');
INSERT INTO "finnish" VALUES(6,126,'6:126','Ja tämä on Herrasi oikea tie (Koraani ja Islaamin uskonto); nyt olemme selittänyt tunnusmerkkimme ihmisille, jotka ottavat niistä vaarin.');
INSERT INTO "finnish" VALUES(6,127,'6:127','Heidän ovat rauhan asunnot heidän Herransa luona, ja Hän on heidän suojelijansa palkinnoksi heidän teoistaan.');
INSERT INTO "finnish" VALUES(6,128,'6:128','Ja sinä päivänä, jolloin Hän kerää ne toiset yhteen (hän sanoo): »Te dzinnien suku, olettepa te paljon ihmisiä mukaanne temmanneet.» Ja heidän kannattajansa ihmisten joukossa sanovat: »Herra, meillä oli hyötyä toisistamme, mutta nyt olemme saavuttaneet rajan, jonka Sinä olet meille määrännyt.» Mutta Hän on sanova: »Tuli on oleva asumuksenne ainiaaksi, jollei Jumala mieltään muuta. Totisesti on sinun Herrasi viisas, tietävä.');
INSERT INTO "finnish" VALUES(6,129,'6:129','Ja siten annamme Me väärintekijöiden avustaa toisiaan ansaitakseen tekojensa mukaan.');
INSERT INTO "finnish" VALUES(6,130,'6:130','Te dzinnien ja ihmisten suku! Eivätkö lähettilääni omasta keskuudestanne astuneet eteenne ilmaistakseen teille Minun tunnusmerkkini ja varoittaakseen teitä, että tämä päivä teidät kohtaa? He sanovat silloin: »Me todistamme itseämme vastaan.» Niin on maallinen elämä heidät pettänyt, ja he todistavat itseään vastaan, että he eivät uskoneet.');
INSERT INTO "finnish" VALUES(6,131,'6:131','Ja tämä siksi, ettei Herrasi hävitä mitään kaupunkia sen pahojen tekojen tähden, ennenkuin asukkaat ovat saaneet varoituksen (pahuudestaan).');
INSERT INTO "finnish" VALUES(6,132,'6:132','Ja jokaista varten on eri asteita aina hänen tekojensa mukaan; eihän Herrasi ole tietämätön siitä, mitä he tekevät.');
INSERT INTO "finnish" VALUES(6,133,'6:133','Sinun Herrasi on rajaton, armon ruhtinas. Jos Hän tahtoo, pyyhkäisee Hän sinut pois ja asettaa seuraajaksesi kenet tahtoo, samoin kuin Hän on synnyttänyt sinut toisten ihmisten jälkeläisistä.');
INSERT INTO "finnish" VALUES(6,134,'6:134','Se, mikä teille on määrätty, on totisesti tapahtuva, ettekä te voi sitä välttää.');
INSERT INTO "finnish" VALUES(6,135,'6:135','Sano (heille, Muhammed,): »Kansani, toimikaa kykynne mukaan; minä toimin myöskin; aikanaan te tulette tietämään, kuka meistä lopuksi saa pysyvän asunnon. Väärintekijät eivät totisesti tule onnellisiksi.»');
INSERT INTO "finnish" VALUES(6,136,'6:136','He erottavat Jumalalle osan siitä laihosta ja karjasta, jotka Hän on enentänyt, ja sanovat harhauskossaan: »Tämä kuuluu Jumalalle ja tämä Hänen vertaisilleen.» Ja se, mikä kuuluu Hänen vertaisilleen, ei joudu Jumalalle, mutta se, mikä kuuluu Jumalalle, joutuu Hänen vertaisilleen. Viheliäinen on heidän tuomionsa.');
INSERT INTO "finnish" VALUES(6,137,'6:137','Siten ovat useat monijumalaiset noilta sivujumaliltaan saaneet houkutuksen lasten surmaamiseen; tämä saattaa heidät kadotukseen ja samentaa heidän uskonsa, mutta jos Jumala olisi tahtonut, eivät he olisi saattaneet tehdä niin. Jätä heidät siis kaikkine keksintöineen!');
INSERT INTO "finnish" VALUES(6,138,'6:138','He sanovat harhauskossaan: »Tämä karja ja sato ovat kielletyt; ei kukaan muu saa nauttia niitä kuin ne, joiden me sallimme.» Heillä on myös karjaa, joka ei saa kantaa taakkoja, ja karjaa, jonka yli he eivät lausu Jumalan nimeä; näin sepittävät he valheita Häntä vastaan. Hän on pian antava heille heidän sepustustensa mukaan.');
INSERT INTO "finnish" VALUES(6,139,'6:139','He sanovat edelleen: »Mikä on tämän karjan kohdussa, on kuuluva miehillemme, mutta kielletty naisiltamme; kuolleena syntyneisiin taasen on heillä yhdenlainen oikeus. Jumala kyllä mittaa heille takaisin heidän säädöstensä mukaan; Hän on totisesti viisas, tietävä.');
INSERT INTO "finnish" VALUES(6,140,'6:140','Kadotuksen omia ovat ne, jotka surmaavat lapsensa mielettömyydessään ja tietämättömyydessään sekä kieltäytyvät siitä, minkä Jumala on heille suonut, sepittäen valheita Jumalasta. He ovat harhautuneita eivätkä taivu oikeaan johdatukseen.');
INSERT INTO "finnish" VALUES(6,141,'6:141','Hän se on kasvattanut puutarhat, sekä ihmisten suunnittelemat että luonnolliset, palmut ja erilaiset viljalaihot, öljypuut ja omenat, joitten maku ja muodot ovat erilaisia - syökää niitten hedelmiä, kun ne hedelmän kantavat, ja antakaa mikä on kohtuullista elonkorjuupäivänä, mutta älkää tuhlatko; Hän ei totisesti rakasta tuhlaavaisia -');
INSERT INTO "finnish" VALUES(6,142,'6:142','Ja karjassanne ovat toiset kuormia varten ja toiset teurastettavia, ja toisten villat ovat hyödyksenne; tämän luoja on Hän; syökää mitä Jumala on teille suonut, älkääkä seuratko saatanan jälkiä; hän on totisesti teidän ilmi vihollisenne.');
INSERT INTO "finnish" VALUES(6,143,'6:143','Kun on kahdeksan uros- ja naaraspuolta: kaksi lammasparia, kaksi vuohiparia - sano: »Onko Hän kieltänyt teiltä molemmat koiraat tai molemmat naaraat tai sen, mikä on tuleva kummankin naaraan kohdusta? Ratkaiskaa tämä taidolla, jos rakastatte totuutta!»-');
INSERT INTO "finnish" VALUES(6,144,'6:144','Ja kun on kameleja kaksi ja nautaeläimiä kaksi, sano: »Onko Hän kieltänyt teiltä molemmat koiraat tai molemmat naaraat tai sen, minkä molempien naaraitten kohdut sisältävät? Vai olitteko todistamassa, että Jumala siten määräsi teille?» Ja kuka tekee enemmän väärin kuin se, joka sepittää valheita Jumalasta johtaakseen ihmiset harhaan heidän tietämättömyydessään? Jumala ei totisesti johdata väärintekijöitä.');
INSERT INTO "finnish" VALUES(6,145,'6:145','Sano: »Kaikesta, mitä minulle Jumala on ilmoittanut, en löydä mitään muuta, mikä olisi kiellettyä ihmiseltä, joka aikoo itseään ravita, kuin sen, mikä on itsestään kuollutta, tai valutettua verta tai sian lihaa, sillä tämä kaikki on epäpuhdasta; myös se on syötäväksi epäpyhää, minkä yli on (teurastettaessa) lausuttu jonkun muun kuin Jumalan nimi.» Mutta jos jotakuta pakottaa (tällaista syömään) hätä eikä tottelemattomuus eikä mielivalta, on Herrasi totisesti anteeksiantava ja laupias.');
INSERT INTO "finnish" VALUES(6,146,'6:146','Juutalaisilta olemme kieltänyt kaikki sorkkaeläimet, ja nautaeläimistä ja lampaista olemme kieltänyt heiltä kaiken rasvan paitsi sen, minkä niitten selkä tai sisukset sisältävät tai mikä on kiinni luissa. Täten olemme vastannut heidän napinaansa; Me olemme totisesti oikeudenmukainen.');
INSERT INTO "finnish" VALUES(6,147,'6:147','Ja jos he pitävät sinua valehtelijana, sano: »Herranne on armossaan runsas, kuitenkaan ei Hänen ankaruutensa jätä rauhaan väärintekijöitä.»');
INSERT INTO "finnish" VALUES(6,148,'6:148','Ne, jotka asettavat muita Jumalan rinnalle, sanovat: »Jos Jumala olisi tahtonut, emme olisi asettaneet muita Hänen vertaisikseen, eivätkä meidän isämmekään, emmekä olisi mitään kieltoja asettaneet.» Samoin teeskentelivät ne, jotka ennen heitä elivät, kunnes he saivat maistaa ankaruuttamme. Sano: »Onko teillä mitään tietoa meille esiintuo- tavaksi? Te seuraatte vain mielikuvitustanne, ettekä muuta tee kuin luulottelette.»');
INSERT INTO "finnish" VALUES(6,149,'6:149','Sano: »Jumalan todistus on ratkaiseva, ja jos Hän olisi tahtonut, olisi Hän johdattanut teidät kaikki.»');
INSERT INTO "finnish" VALUES(6,150,'6:150','Sano: »Tulkaa tänne todistajinenne, jotta he näyttäisivät toteen, että Jumala on sen tai sen kieltänyt!» Mutta jos he silloin todistavat, niin älä todista heidän kanssaan äläkä noudata niiden päähänpistoja, jotka pitävät tunnusmerkkimme vaiheena eivätkä usko iankaikkiseen elämään sekä pitävät muita Herransa vertaisina.');
INSERT INTO "finnish" VALUES(6,151,'6:151','Sano: »Tulkaa, minä luettelen teille mitä Herranne on teiltä kieltänyt: Älkää asettako mitään Hänen rinnalleen! Vanhempianne on teidän hyvin kohdeltava, lapsianne ette saa surmata köyhyyden pelosta. - Me huolehdimme sekä teistä että heistä. Haureutta karttakaa sekä julkista että salaista. Surmata ette saa sitä elämää, jonka Jumala on määrännyt loukkaamattomaksi, paitsi lain nojalla. Tämän on Hän määrännyt teille, jotta ymmärtäisitte.');
INSERT INTO "finnish" VALUES(6,152,'6:152','Älkää koskeko orvon omaisuuteen, paitsi hänen edukseen, ennenkuin hän on tullut täyteen ikään! Antakaa rehellisesti täyden mitan ja täyden painon mukaan. Me emme kuormita yhtään sielua hänen kykynsä yli. Puheissanne olkaa oikeamielisiä, vaikka omainenkin olisi kysymyksessä, ja täyttäkää lupauksenne Jumalaa kohtaan! Tämän on Hän määrännyt teille, jotta olisitte kaikesta selvillä.');
INSERT INTO "finnish" VALUES(6,153,'6:153','Tämä on Minun oikea tieni; sitä siis vaeltakaa! Älkää käykö muita teitä, sillä ne eksyttävät teidät Hänen tieltään! Tämän on Hän määrännyt teille, jotta pelkäisitte Jumalaa.»');
INSERT INTO "finnish" VALUES(6,154,'6:154','Edelleen olemme Moosekselle antanut Pyhän kirjoituksen, täydellisen (siunauksen) sille, joka hyvää tekee, - kaiken selitykseksi, johdatukseksi ja armonlahjaksi, jotta he uskoisivat kohtaavansa Herransa.');
INSERT INTO "finnish" VALUES(6,155,'6:155','Ja tämä (Koraani) on siunattu Kirjoitus, jonka Me olemme lähettänyt ylhäältä; noudattakaa siis sitä ja pelätkää Jumalaa, jotta osaksenne tulisi laupeus.');
INSERT INTO "finnish" VALUES(6,156,'6:156','Ettette sanoisi: »Pyhä kirjoitus on lähetetty vain kahdelle kansalle ennen meitä, ja totisesti olimme liian tietämättömiä käsittääksemme sen»,');
INSERT INTO "finnish" VALUES(6,157,'6:157','tai ettette sanoisi: »Jos Kirjoitus olisi lähetetty meille, olisimme totisesti tulleet paremmin johdatetuiksi kuin he», niin on nyt osaksenne tullut selvä todistus Herraltamme, johdatus ja armonosoitus, ja ken tekee enemmän väärin kuin se, joka vääristelee Jumalan tunnusmerkit valheeksi ja on penseä niitä kohtaan. Me rankaisemme niitä, jotka ovat penseitä tunnusmerkkejämme kohtaan, mitä pahimmalla rangaistuksella, siksi että he niin tekivät.');
INSERT INTO "finnish" VALUES(6,158,'6:158','Odottavatko he jotakin muuta kuin sitä, että enkelit tulisivat heidän luokseen, tai että Herrasi tulisi tai että joku Herrasi tunnusmerkeistä tulisi? Sinä päivänä, jolloin joku Herrasi tunnusmerkeistä tulee julki, ei usko ole hyödyttävä sitä sielua, joka ei ennen ole uskonut tai mitään tehnyt uskonsa hyväksi. Sano: »Odottakaa! Mekin totisesti odotamme.»');
INSERT INTO "finnish" VALUES(6,159,'6:159','Niiden kanssa, jotka ovat hajoittaneet uskontonsa ja muodostaneet lahkoja, ei sinulla (Muhammed) totisesti ole mitään tekemistä. Heidän asiansa kuuluu vain Jumalalle; kerran on Hän julistava heille heidän tekonsa.');
INSERT INTO "finnish" VALUES(6,160,'6:160','Se, joka tekee hyvän työn, saa kymmenkertaisesti takaisin. Ja se, joka tekee pahan teon, palkitaan ainoastaan sen mukaan, heidän kärsimättänsä mitään vääryyttä.');
INSERT INTO "finnish" VALUES(6,161,'6:161','Sano: »Minut on Herrani totisesti johdattanut oikealle tielle, uskoon, joka pysyy, Aabrahamin oppiin, hänen, joka oli hurskas (haanif) eikä monijumalainen.»');
INSERT INTO "finnish" VALUES(6,162,'6:162','Sano: »Minun rukoukseni ja hartauteni, minun elämäni ja kuolemani kuuluvat Jumalalle, koko maailman Herralle;');
INSERT INTO "finnish" VALUES(6,163,'6:163','Hänellä ei ole ketään vertaista. Tämä on minulle määrätty, ja minä olen ensimmäinen Jumalan alamaisista.»');
INSERT INTO "finnish" VALUES(6,164,'6:164','Sano: »Toivoisinko itselleni ketään muuta Herrakseni kuin Jumalan, kaiken olevaisen valtiaan? Jokainen sielu tekee kaikki vain omaan tiliinsä, kukaan ei kanna vierasta kuormaa. Senjälkeen palaatte te Herranne luo, ja Hän on puhuva teille siitä, mistä olitte erimielisiä.');
INSERT INTO "finnish" VALUES(6,165,'6:165','Hän se on asettanut teidät sijaisiksi maan päälle ja kohottanut jotkut teistä useita asteita toisten yläpuolelle koetellakseen teitä sen suhteen, mitä on teille antanut. Sinun Herrasi on totisesti nopea rankaisemaan, mutta Hän on totisesti myös anteeksiantava ja laupias.');
INSERT INTO "finnish" VALUES(7,1,'7:1','Alif, Laam, Miim, Saad.');
INSERT INTO "finnish" VALUES(7,2,'7:2','Tämä Kirjoitus on sinulle ylhäältä lähetetty, - siitä älköön olko huolta povessasi - varoittaaksesi ihmisiä sen perusteella, ja oikeauskoisille on se muistutukseksi.');
INSERT INTO "finnish" VALUES(7,3,'7:3','Noudattakaa sitä, mikä on Herranne teille lähettämää, älkääkä paitsi Häntä seuratko keitäkään muita, liittokumppaneiksi otaksuttuja. Mutta vähät pidätte te mielessänne!');
INSERT INTO "finnish" VALUES(7,4,'7:4','Kuinka monta kaupunkia olemmekaan hävittänyt! Ja rangaistuksemme kohtasi heidät yöllä tai heidän päivällislepohetkenään.');
INSERT INTO "finnish" VALUES(7,5,'7:5','Ja rangaistuksemme kohdatessa heidät eivät he voineet muuta sanoa puolustuksekseen kuin: »Olemme totisesti väärintekijöitä!»');
INSERT INTO "finnish" VALUES(7,6,'7:6','Ja totisesti alistamme kuulusteltaviksemme ne, joille (sanamme julistajia) lähetämme, ja totisesti kuulustelemme myös lähettiläitämme,');
INSERT INTO "finnish" VALUES(7,7,'7:7','ja tietäen asiat me osoitamme heille kaikki (heidän tekonsa); emmehän Me (heidän luotaan) poissa ollut.');
INSERT INTO "finnish" VALUES(7,8,'7:8','Sinä päivänä punnitaan itse Totuuden punnuksilla, ja ne, joiden vaakakuppi painuu raskaana, ovat onnellisia.');
INSERT INTO "finnish" VALUES(7,9,'7:9','Mutta ne, joiden vaakakuppi jää keveäksi, ovat sielunsa kadottaneita ihmisiä, koska suhtautuivat väärin Meidän tunnusmerkkeihimme.');
INSERT INTO "finnish" VALUES(7,10,'7:10','Olemmehan suonut teille vallan maan päällä ja taannut teille siellä elinehdot. Mutta vähiin raukeavat teidän kiitoksenne.');
INSERT INTO "finnish" VALUES(7,11,'7:11','Mehän totisesti loimme teidät ja sitten muodostimme teidät; senjälkeen sanoimme enkeleille: »Langetkaa kumartaen ihmisen (Aadamin) eteen!» Niin heittäytyivät he kaikki maahan paitsi Iblis (tyytymätön); hän ei kumartanut.');
INSERT INTO "finnish" VALUES(7,12,'7:12','Hän (Jumala) sanoi: »Mikä estää Sinua kumartamasta, kun Minä sinua käsken?» Hän vastasi: »Minä olen parempi kuin hän; Sinä olet luonut minut tulesta, mutta hänet Sinä olet luonut savesta.»');
INSERT INTO "finnish" VALUES(7,13,'7:13','Silloin sanoi Hän: »Syöksy alas täältä! Sinun ei sovi olla ylpeä. Pois täältä! Totisesti kuulut viheliäisiin.»');
INSERT INTO "finnish" VALUES(7,14,'7:14','Tämä vastasi: »Pidennä minun aikaani siihen päivään asti, kunnes heidät herätetään kuolleista!»');
INSERT INTO "finnish" VALUES(7,15,'7:15','Jumala sanoi: »Katso, olet saava odotusaikaa.»');
INSERT INTO "finnish" VALUES(7,16,'7:16','Tämä vastasi: »Koska olet saattanut minut pois (oikealta)tieltä, olen minä totisesti väijyvä niitä, jotka vaeltavat Sinun oikeata tietäsi.');
INSERT INTO "finnish" VALUES(7,17,'7:17','Ja silloin käyn heidän kimppuunsa sekä edestä että takaa, sekä oikealta että vasemmalta, ja useimmat heistä eivät tule olemaan Sinulle kiitollisia.»');
INSERT INTO "finnish" VALUES(7,18,'7:18','Silloin sanoi Hän: »Pois täältä, Sinä kunniaton ja karkoitettu! Ja mitä tulee niihin heistä, jotka seuraavat sinua, kyllä täytän helvetin teillä kaikilla.');
INSERT INTO "finnish" VALUES(7,19,'7:19','Mutta Sinä, ihminen (Aadam), asusta itse paratiisissa vaimosi kanssa! Ja syökää mistä haluatte, mutta älkää lähestykö tätä puuta, ettette tule väärintekijöiksi!»');
INSERT INTO "finnish" VALUES(7,20,'7:20','Silloin Saatana vihjaili heille, että hän voi ilmaista heille sen, mikä heidän alastomuudestaan oli heiltä salattu, ja sanoi: »Teidän Herranne ei ole minkään muun vuoksi kieltänyt teitä nauttimasta tästä puusta kuin ainoastaan siksi, ettette muuttuisi kahdeksi enkeliksi tai kuolemattomiksi.»');
INSERT INTO "finnish" VALUES(7,21,'7:21','Ja hän vannoi heidän edessään sanoen: »Olen toden totta teidän oikea neuvonantajanne.»');
INSERT INTO "finnish" VALUES(7,22,'7:22','Niin vietteli hän heidät viekkaudellaan, ja kun he maistoivat puusta, tulivat ilmi heidän hävettävät ominaisuutensa ja he alkoivat verhota itseään paratiisin lehvillä. Mutta heidän Herransa huusi heille: »Enkö ole kieltänyt teiltä sitä puuta ja sanonut teille Saatanan olevan ilmeisen vihollisenne?»');
INSERT INTO "finnish" VALUES(7,23,'7:23','He sanoivat: »Herramme, olemme väärin tehneet sielujamme kohtaan, ja jollet Sinä anna meille anteeksi ja armahda meitä, olemme me totisesti kadotetut.»');
INSERT INTO "finnish" VALUES(7,24,'7:24','Silloin sanoi Hän: »Syöksykää alas, ja toinen teistä olkoon toisensa vihollinen. Joksikin ajaksi saatte te olinpaikan ja elatuksen maan päällä.»');
INSERT INTO "finnish" VALUES(7,25,'7:25','Hän sanoi edelleen: »Maan päällä tulette te elämään, maan päällä kuolemaan, ja maasta tulette ylösnousemaan.»');
INSERT INTO "finnish" VALUES(7,26,'7:26','Te ihmisen (Aadamin) lapset! Me olemme lähettänyt teille vaatteet - verhotaksenne alastomuutenne - sekä juhlapuvun; mutta jumalanpelko on paras puku. Tämä on eräs Jumalan tunnusmerkeistä, jotta he ottaisivat varoituksesta vaarin.');
INSERT INTO "finnish" VALUES(7,27,'7:27','Te ihmisen (Aadamin) lapset, älkää antako Saatanan vietellä teitä samalla tavalla, kuin hän aiheutti teidän (ensimmäisten) vanhempainne karkoituksen paratiisista, riistäen heiltä (viattomuutensa) verhon näyttääkseen heille heidän hävettävät ominaisuutensa. Katso, hän ja hänen joukkonsa näkevät teidät sieltä käsin, missä te ette heitä näe. Katso, me olemme asettanut pahat henget niiden liittolaisiksi, jotka eivät usko.');
INSERT INTO "finnish" VALUES(7,28,'7:28','Ja suorittaessaan jotakin häpeällistä he sanovat: »Olemme havainneet esi~isiemme tehneen niin, ja Jumala on sen meille siten asettanut.» Sano: »Jumala ei määrää mitään häpeällistä; panetteko Jumalan tiliin asioita, joita ette tiedä?»');
INSERT INTO "finnish" VALUES(7,29,'7:29','Sano: »Herrani on asettanut oikeuden. Hurskaat olkoot siis kasvonne (koko olemuksenne) joka rukoushetkenä ja Häntä ainoata huutakaa avuksi hartaasti palvoen. Sellaisina, joiksi Hän alussa loi teidät, palaatte te jälleen (Hänen) luoksensa.');
INSERT INTO "finnish" VALUES(7,30,'7:30','Toisia teistä on Hän johdattanut ja toiset on Hän saattanut ansaittuun harhaan; he ovat totisesti ottaneet paholaisia suojelijoikseen Jumalan asemesta ja kuvittelevat, että ovat oikealla tiellä.');
INSERT INTO "finnish" VALUES(7,31,'7:31','Te ihmisen (Aadamin) lapset, pukeutukaa juhlapukuihinne jokaisena hartaushetkenänne ja syökää sekä juokaa, mutta älkää tuhlatko,sillä Hän ei rakasta tuhlaavaisia.');
INSERT INTO "finnish" VALUES(7,32,'7:32','Sano: »Kuka on kieltänyt Jumalan kauniit vaatteet, jotka Hän on hankkinut palvelijoineen, sekä hyvät ruoat?» Sano: »Ne on tarkoitettu uskovaisille maallisessa vaelluksessaan ja yksinomaan heille - ylösnousemuksenpäivänä.» Näin selitämme tunnusmerkit ihmisille, jotka ymmärtävät.');
INSERT INTO "finnish" VALUES(7,33,'7:33','Sano: »Seuraavat on minun Herrani kieltänyt: kaiken haureuden, niin julkisen kuin salaisenkin, ja synnin sekä aiheettoman kapinoimisen, sekä sen, että te Jumalan vertaisiksi teette sellaista,jota Hän ei ole asettanut, ja väitätte Jumalalle ominaiseksi sellaista, mitä ette tiedä.»');
INSERT INTO "finnish" VALUES(7,34,'7:34','Ja jokaisella yhteisöllä on määräaikansa, ja kun määräaika on käsissä, eivät he voi tunnillakaan lykätä tai jouduttaa sitä.');
INSERT INTO "finnish" VALUES(7,35,'7:35','Te ihmisen (Aadamin) lapset! Jos luoksenne tulee Minun lähettiläitäni teidän omasta keskuudestanne julistamaan teille tunnusmerkkejäni, ei mikään pelko ole valtaava Jumalan palvelijoita ja hyvää tekeviä, eikä heidän tarvitse murehtia.');
INSERT INTO "finnish" VALUES(7,36,'7:36','Mutta ne, jotka väittävät Minun tunnusmerkkini valheeksi ja suhtautuvat niihin ylimielisesti, joutuvat tulen omiksi ja pysyvät siinä iäti.');
INSERT INTO "finnish" VALUES(7,37,'7:37','Kuka tekee enemmän väärin kuin se, joka sepittää valhetta Jumalasta tai vääristelee Hänen tunnusmerkkinsä? Nämä tulevat saamaan osansa siitä, mikä on määrätty, kunnes lähettiläämme tulevat heidän luokseen antaakseen heille täydellä mitalla ja sanovat: »Missä ovat ne, joita olette rukoilleet Jumalan ohella?» Ja nuo vastaavat: »He ovat pettäneet meidät» ja siten todistavat itsestään, etteivät uskoneet.');
INSERT INTO "finnish" VALUES(7,38,'7:38','Hän on sanova: »Menkää tuleen niitten dzinnien ja ihmisten joukkoon, jotka ennen teitä sinne joutuivat. Joka kerta, kun sukupolvi astuu sinne, kiroaa se lähinnä edellistä, kunnes kaikki ovat kerääntyneet sinne ja viimeiset sanovat aikaisemmista: »Herra, nämä ovat eksyttäneet meidät; anna heille kaksinkertainen tuska tulessa!» Ja Hän on vastaava: »Jokainen teistä on ansainnut kaksinkertaisesti, mutta te ette tiedä siitä.»');
INSERT INTO "finnish" VALUES(7,39,'7:39','Ja aikaisemmin menneet sanovat myöhäisemmille: »Teillä ei ole mitään etuoikeutta meihin verraten; maistakaa nyt rangaistusta omista teoistanne.»');
INSERT INTO "finnish" VALUES(7,40,'7:40','Niille, jotka sanovat tunnusmerkkimme valheeksi ja ovat liian ylpeitä vastaanottamaan niitä, eivät taivaan portit totisestikaan avaudu, eivätkä he astu paratiisiin ennenkuin kameli käy neulansilmän läpi. Siten palkitsemme Me väärintekijät.');
INSERT INTO "finnish" VALUES(7,41,'7:41','Helvetti on heille kehto ja peite. Niin palkitsemme Me väärintekijät.');
INSERT INTO "finnish" VALUES(7,42,'7:42','Mutta ne, jotka uskovat ja tekevät hyviä töitä - Me emme kuormita mitään sielua voimiensa yli - tulevat paratiisista osal~ lisiksi ja asustavat siellä iäti.');
INSERT INTO "finnish" VALUES(7,43,'7:43','Ja Me poistamme kaiken kateuden heidän rinnastaan, purot solisevat heidän jalkojensa juurella, ja he sanovat: »Ylistys ja kunnia Jumalalle, joka on johdattanut meidät tähän olotilaan; emmehän olisi löytäneet oikeata tietä, ellei Jumala olisi johdattanut meitä. Totisesti Herramme lähettiläät toivat meille totuuden.» Ja heille huutaa ääni: »Tämä on paratiisi; olette sen perineet töittenne ansiosta.»');
INSERT INTO "finnish" VALUES(7,44,'7:44','Ja paratiisin asukkaat huutavat silloin tulen asukkaille: »Nyt olemme todenneet Herramme lupaukset tosiksi; oletteko tekin todenneet Herranne lupaukset tosiksi?» Ja ne vastaavat: »Olemme», ja eräs huutaja huutaa silloin heidän joukostaan: »Jumalan kirous väärintekijöille,');
INSERT INTO "finnish" VALUES(7,45,'7:45','jotka luopuivat Jumalan tiestä ja pyrkivät sitä vääristämään eivätkä uskoneet iankaikkiseen elämään.»');
INSERT INTO "finnish" VALUES(7,46,'7:46','Ja niiden (paratiisin ja tulen) välillä on raja, ja rajamuurilla seisoo miehiä, jotka tuntevat jokaisen hänen tuntomerkeistään, ja he huutavat paratiisin asukkaille: »Rauha teille!» Mutta he eivät saa astua sisään, vaikka toivovat saavansa.');
INSERT INTO "finnish" VALUES(7,47,'7:47','Ja kun heidän katseensa kääntyvät tuleen ja kohtaavat sen asukkaita, sanovat he: »Herramme, älä saata meitä väärintekijöitten joukkoon!»');
INSERT INTO "finnish" VALUES(7,48,'7:48','Ja rajamuurilla seisovat huutavat niille, jotka he tuntevat tunnusmerkeistä, sanoen: »Mitä hyötyä teille nyt tuli paljoudestanne ja ylpeilystänne?');
INSERT INTO "finnish" VALUES(7,49,'7:49','Näitäkö tarkoitatte vannoessanne, ettei Jumala heille armoaan osoita?» (ja toisille he sanovat:) »Astukaa sisään paratiisiin, ei pelko eikä murhe vaivaa teitä.»');
INSERT INTO "finnish" VALUES(7,50,'7:50','Ja tulen asukkaat huutavat silloin paratiisin asukkaille: »Vuodattakaa ylitsemme vähän vettä tai jotakin muuta, mitä Jumala on suonut teille!» Nämä vastaavat: »Jumala on totisesti kieltänyt sen uskottomilta,');
INSERT INTO "finnish" VALUES(7,51,'7:51','niiltä, jotka pitivät uskontoaan leikkinä ja turhuutena ja joita maallinen elämä vietteli.» Tänään Me hylkäämme heidät, kuten hekin aikanaan hylkäsivät tämän Meidän kohtauspäivämme ja kielsivät tunnusmerkkimme.');
INSERT INTO "finnish" VALUES(7,52,'7:52','Olemmehan lähettänyt heille Pyhän Kirjoituksen, jonka olemme selittänyt johdatukseksi ja armonosoitukseksi ihmisille, jotka uskovat.');
INSERT INTO "finnish" VALUES(7,53,'7:53','Odottavatko he jotakin muuta kuin sen täyttymystä? Sinä päivänä, jolloin se täyttyy, sanovat ne, jotka ennen olivat sen hylänneet: »Totisesti olivat ne Herramme lähettiläitä, jotka toivat luoksemme totuuden. Onko meillä puoltajia, jotka jotakin voisivat tehdä hyväksemme, tai voisimmeko palata, tehdäksemme toisin kuin ennen menettelimme?» Totisesti nämä ovat kadottaneet sielunsa, ja se, minkä he kuvittelivat, on pettänyt heidät.');
INSERT INTO "finnish" VALUES(7,54,'7:54','Teidän Herranne on totisesti Jumala, joka on luonut taivaat ja maan kuudessa päivässä ja noussut sitten valtaistuimelleen. Hän antaa yön peittää päivän, - niin että toinen herkeämättä ajaa toistansa takaa. Aurinko, kuu ja tähdet tottelevat Hänen käskyään. Eikö luomakunta ja valta ole Hänen? Siunattu on Jumala, kaikkien maailmojen Herra.');
INSERT INTO "finnish" VALUES(7,55,'7:55','Rukoilkaa Herraanne nöyryydessä ja hiljaisuudessa. Hän ei totisesti rakasta väärintekijöitä.');
INSERT INTO "finnish" VALUES(7,56,'7:56','Älkää aikaansaako turmelusta maan päällä senjälkeen, kun kaikki on saatettu järjestykseen, vaan rukoilkaa Häntä peläten ja kaivaten. Jumalan armo on totisesti niitä lähellä, jotka hyvää tekevät.');
INSERT INTO "finnish" VALUES(7,57,'7:57','Hän se lähettää tuulet armonsa sanansaattajiksi, niin että ne ottavat kantaakseen raskaita pilviä, joita Me ajamme autioituneeseen seutuun ja sen yli vuodatamme niistä vettä, jonka avulla kasvatamme kaikenlaisia hedelmiä. Samalla tavalla herätämme kuolleet, jotta te ottaisitte varoituksesta vaarin.');
INSERT INTO "finnish" VALUES(7,58,'7:58','Hyvä maaperä antaa sadon Herransa käskystä, mutta huono ei anna kuin kurjuutta. Siten teemme erilaisin vertauksin tunnusmerkkimme selviksi kiitollisille ihmisille.');
INSERT INTO "finnish" VALUES(7,59,'7:59','Totisesti lähetimme jo Nooan kansansa luo, ja hän sanoi: »Kansani, palvelkaa Jumalaa; teillä ei ole muuta jumalaa kuin Hän, minä pelkään, että rangaistus lankeaa ylitsenne sinä suurena päivänä.');
INSERT INTO "finnish" VALUES(7,60,'7:60','Hänen kansansa päälliköt vastasivat: »Me näemme tosiaankin, että olet ilmeisesti harhassa.»');
INSERT INTO "finnish" VALUES(7,61,'7:61','Silloin sanoi hän: »Kansani, en ole harhassa, vaan olen koko maailman Herran lähettiläs.');
INSERT INTO "finnish" VALUES(7,62,'7:62','Olen tuomassa teille Herrani sanomaa; neuvon teitä omaksi parhaaksenne, ja Jumalalta olen saanut tietää, mitä te ette tiedä.');
INSERT INTO "finnish" VALUES(7,63,'7:63','Sitäkö ihmettelette, että mies omasta keskuudestanne tuo teille Herraltanne muistutuksen, varoittaakseen teitä, jotta harjoittaisitte hartautta ja ehkä saisitte armon osaksenne?»');
INSERT INTO "finnish" VALUES(7,64,'7:64','Mutta he pitivät häntä valehtelijana, ja niin pelastimme Me hänet ja hänen seuralaisensa arkkiin ja hukutimme veteen ne, jotka väittivät tunnusmerkkimme valheeksi; he olivat totisesti sokaistuja ihmisiä.');
INSERT INTO "finnish" VALUES(7,65,'7:65','Sitten lähetimme Aadin (kansan) luo heidän veljensä Huudin . Hän sanoi: »Kansani, palvelkaa Jumalaa! Teillä ei ole muuta jumalaa kuin Hän; eikö teillä ole jumalanpelkoa?»');
INSERT INTO "finnish" VALUES(7,66,'7:66','Hänen kansansa uskottomien johtajat vastasivat: »Näemme totisesti, että sinä olet mieletön, ja pidämme sinua valehtelijana.»');
INSERT INTO "finnish" VALUES(7,67,'7:67','Silloin sanoi hän: »Kansani, minä en ole mieletön, vaan olen koko maailman Herran lähettiläs.');
INSERT INTO "finnish" VALUES(7,68,'7:68','Olen tuomassa teille Herrani sanomaa; tahdon teidän parastanne ja olen luotettava neuvojanne.');
INSERT INTO "finnish" VALUES(7,69,'7:69','Ihmettelette ehkä sitä, että mies omasta keskuudestanne on saanut luodakseen muistutuksen Herraltanne varoittaakseen teitä. Muistakaa, kuinka Hän asetti teidät Nooan kansan seuraajiksi ja antoi teidän ruumiiltanne niin suuresti kehittyä, ja muistakaa Jumalan hyvät työt, jotta tulisitte onnellisiksi!»');
INSERT INTO "finnish" VALUES(7,70,'7:70','He vastasivat: »Oletko tullut luoksemme, jotta me palvelisimme yksinomaan Jumalaa ja hylkäisimme ne, joita meidän esi-isämme palvelivat? Jos niin on, osoita meille sitten kaikki, minkä lupaat meille, jos olet totuudessa.»');
INSERT INTO "finnish" VALUES(7,71,'7:71','Hän sanoi: »Teitä tulee kohtaamaan onnettomuus ja Herranne kiivaus. Haluatteko kiistellä kanssani niistä nimistä, joita, olette keksineet, te ja teidän isänne, mutta joihin Jumala ei ole antanut teille mitään valtuutta? Odottakaa siis. Katso, minä odotan kanssanne.»');
INSERT INTO "finnish" VALUES(7,72,'7:72','Ja niin pelastimme Me armossamme hänet ja ne, jotka seurasivat häntä, ja hävitimme juurineen ne, jotka vääristelivät tunnusmerkkimme eivätkä uskoneet.');
INSERT INTO "finnish" VALUES(7,73,'7:73','Niin lähetimme Me Tamuudin luo heidän veljensä Saalihin. Hän sanoi: »Kansani, palvelkaa Jumalaa! Teillä ei ole muuta jumalaa kuin Hän; onhan luoksenne tullut jo selvä todistus Herraltanne. Tämä Jumalan naaraskameli on tunnusmerkiksi teille; antakaa siis sen käydä Jumalan laitumella älkääkä tehkö sille mitään pahaa, ettei teitä kohtaisi tuskallinen rangaistus.');
INSERT INTO "finnish" VALUES(7,74,'7:74','Muistakaa, kuinka Hän teki teidät Aadin seuraajaksi ja antoi teille turvapaikan maan päällä, rakentaaksenne itsellenne linnoja sen tasangoilla ja hakataksenne vuoriin asuntoja. Ja muistakaa Jumalan armolahjat älkääkä aikaansaako turmelusta maan päällä!»');
INSERT INTO "finnish" VALUES(7,75,'7:75','Silloin sanoivat hänen kansansa ylpeät päälliköt niille, joita pidettiin liian heikkoina, - niille heistä, jotka olivat tulleet uskoon: »Tiedättekö, onko Saalih Herransa lähettiläs?» He vastasivat: »Me uskomme totisesti siihen sanomaan, jota hänet on lähetetty tuomaan.»');
INSERT INTO "finnish" VALUES(7,76,'7:76','Silloin sanoivat ylpeät: »Me emme totisesti usko siihen, mihin te uskotte.»');
INSERT INTO "finnish" VALUES(7,77,'7:77','Niin silpoivat he naaraskamelin ja rikkoivat Herransa käskyn ja sanoivat: »Saalih, tee meille mitä uhkaat, jos olet lähettiläs.»');
INSERT INTO "finnish" VALUES(7,78,'7:78','Ja niin yllätti maanjäristys heidät, ja seuraavana aamuna makasivat he kuolleina kodeissaan.');
INSERT INTO "finnish" VALUES(7,79,'7:79','Ja hän kääntyi pois heistä ja sanoi: »Kansani, olen totisesti esittänyt Herrani sanoman teille, ja neuvonut teitä parhaaksenne, mutta te ette rakasta neuvojia.»');
INSERT INTO "finnish" VALUES(7,80,'7:80','Ja Loot oli Meidän lähettiläämme, kun hän puhui kansalleen: »Harjoitatteko irstaisuutta, jota kukaan koko maailmassa ei ole tehnyt ennen teitä?');
INSERT INTO "finnish" VALUES(7,81,'7:81','Totisesti ryhdytte himossanne miehiin mieluummin kuin naisiin. Te olette irstasta väkeä.»');
INSERT INTO "finnish" VALUES(7,82,'7:82','Ja hänen kansansa vastaus ei ollut parempi kuin: »Ajakaa heidät pois kaupungistanne, sillä he ovat niitä ihmisiä, jotka harjoittavat puhtautta.»');
INSERT INTO "finnish" VALUES(7,83,'7:83','Silloin pelastimme Me hänet ja hänen sukunsa paitsi hänen vaimoaan, joka jäi jälkeen.');
INSERT INTO "finnish" VALUES(7,84,'7:84','Ja Me annoimme sateen peittää ne toiset, katso minkä lopun väärintekijät saivat!');
INSERT INTO "finnish" VALUES(7,85,'7:85','Ja Midianin kansan luo (lähetimme Me) heidän veljensä Soeibin. Hän sanoi: »Kansani, palvelkaa Jumalaa! Teillä ei ole muuta Jumalaa kuin Hän; luoksenne on jo tullut selvä todistus Herraltanne. Antakaa siis täysi mitta ja täysi paino, älkää anastako ihmisiltä heidän tavaroitaan älkääkä aikaansaako maan päällä mitään turmiollista kaiken tultua järjestetyksi. Tämä on teille edullisinta, jos uskotte.');
INSERT INTO "finnish" VALUES(7,86,'7:86','Älkääkä väijykö kaikilla mahdollisilla teillä, uhaten ja kääntäen pois Jumalan tieltä niitä, jotka uskovat Häneen, älkääkä pyrkikö sitä tekemään mutkaiseksi! Ja muistakaa, että ollessanne vähälukuisia Hän enensi teidän joukkonne, ja katsokaa, minkä lopun saivat väärintekijät.');
INSERT INTO "finnish" VALUES(7,87,'7:87','Jos toiset teistä uskovat siihen sanomaan, jota tuomaan minut lähetettiin, ja toiset eivät usko, niin odottakaa kärsivällisesti, kunnes Jumala ratkaisee asianne; sillä Hän on paras tuomari.»');
INSERT INTO "finnish" VALUES(7,88,'7:88','Ylpeiden johtomiehet hänen kansansa keskuudessa sanoivat: »Me ajamme totisesti pois kaupungistamme sekä sinut Soeib että ne, jotka sinun tavallasi uskovat, tai sitten palaatte meidän uskontoomme.» Hän sanoi: »Siihenkö, joka on meille vastenmielinen?»');
INSERT INTO "finnish" VALUES(7,89,'7:89','Sepittäisimmehän siten valheen Jumalasta, jos palaisimme teidän uskontoonne Jumalan pelastettua meidät siitä. Me emme palaa luoksenne vaan teemme niin kuin Jumala tahtoo. Herramme hallitsee kaiken olevaisen tiedollaan; Jumalaan me luotamme. Herramme, ratkaise totuudessasi (kiista) meidän ja kansamme välillä, Sinä olet paras ratkaisija.»');
INSERT INTO "finnish" VALUES(7,90,'7:90','Ja uskottomien johtomiehet hänen kansansa keskuudessa sanoivat: »Totisesti, jos seuraatte Soeibia, olette kadotuksen omia.»');
INSERT INTO "finnish" VALUES(7,91,'7:91','Silloin yllätti maanjäristys heidät, ja seuraavana aamuna makasivat he kuolleina olinpaikoissaan.');
INSERT INTO "finnish" VALUES(7,92,'7:92','Ikäänkuin niitä, jotka väittivät Soeihia valehtelijaksi, ei koskaan olisi ollutkaan; ne, jotka pitivät Soeibia valehtelijana, perivät häviön.');
INSERT INTO "finnish" VALUES(7,93,'7:93','Ja hän kääntyi pois heistä ja sanoi: »Kansani, olenhan saattanut teille Herrani sanoman ja neuvonut teitä hyvään; miksi kantaisin murhetta epäuskoisten ihmisten takia?»');
INSERT INTO "finnish" VALUES(7,94,'7:94','Emme Me koskaan ole lähettänyt ketään profeettaa mihinkään kaupunkiin koettelematta sen asukkaita surulla ja vastoinkäymisellä, jotta he nöyrtyisivät.');
INSERT INTO "finnish" VALUES(7,95,'7:95','Senjälkeen Me vaihdoimme pahan hyvään, niin että he paisuivat ja sanoivat: »Niinhän esi~isäimmekin elämä kulki surusta iloon.» Ja silloin Me yllätimme heidät äkkiarvaamatta,eivätkä he huomanneet sitä.');
INSERT INTO "finnish" VALUES(7,96,'7:96','Mutta jos kaupunkien asukkaat olisivat pysyneet uskossa ja jumalanpelossa, olisimme Me totisesti siunanneet heitä runsaasti sekä taivaasta että maasta käsin; mutta he punoivat valhetta, ja silloin Me yllätimme heidät ansionsa mukaan.');
INSERT INTO "finnish" VALUES(7,97,'7:97','Ovatko kaupunkien asukkaat varmat siitä, ettei Meidän voimamme heidän ylitsensä lankea yön aikaan heidän nukkuessaan?');
INSERT INTO "finnish" VALUES(7,98,'7:98','Ovatko kaupunkien asukkaat turvassa Meidän voimaltamme valoisan päivän aikaan huvitellessaan?');
INSERT INTO "finnish" VALUES(7,99,'7:99','Ja ovatko he sitten turvassa Jumalan suunnitelmilta? Eihän kukaan kuvittele olevansa turvassa Jumalan aikeilta paitsi perikatoon kulkevat ihmiset.');
INSERT INTO "finnish" VALUES(7,100,'7:100','Eikö niille, jotka perivät maan sen entisten asukkaitten jälkeen, ole selvinnyt, että jos hyväksi näkisimme, voisimme Me heitä rangaista heidän synneistänsä ja sulkea sinetillä heidän sydämensä, niin etteivät he johdatustamme omaksua voisi!');
INSERT INTO "finnish" VALUES(7,101,'7:101','Nämä ovat kaupungit, joiden tarinan Me olemme kertonut sinulle, Muhammed. Heidän lähettiläänsä tulivat heidän luokseen tuoden selvät todistukset, mutta he eivät kyenneet uskomaan siihen, minkä he ennen olivat vääräksi väittäneet. Niin sulkee Jumala epäuskoisten sydämet.');
INSERT INTO "finnish" VALUES(7,102,'7:102','Havaitsimme, ettei heidän enemmistönsä ollut liitolle uskollinen, vaan suurin osa heistä oli suorastaan jumalattomia.');
INSERT INTO "finnish" VALUES(7,103,'7:103','Heidän jälkeensä lähetimme Mooseksen tunnusmerkkeinemme Faraon ja hänen päälliköittensä luo, mutta he olivat nurjamielisiä noita merkkejämme kohtaan. Ja katso, minkälaatuisen lopun saivat nuo väärintekijät!');
INSERT INTO "finnish" VALUES(7,104,'7:104','Mooses sanoi: »Oi, Farao, olen totisesti maailmojen Herran lähettiläs.');
INSERT INTO "finnish" VALUES(7,105,'7:105','En voi puhua muuta kuin totuutta Jumalasta. Olen tuonut luoksenne Herrani lähettämät selvät todistukset, - lähetä siis Israelin lapset kanssani.');
INSERT INTO "finnish" VALUES(7,106,'7:106','Hän vastasi: »Jos olet tullut jokin tunnusmerkki mukanasi, näytä se, mikäli totta puhut!»');
INSERT INTO "finnish" VALUES(7,107,'7:107','Niin heitti hän sauvansa maahan, ja katso, siitä tuli ilmeinen käärme.');
INSERT INTO "finnish" VALUES(7,108,'7:108','Ja hän ojensi kätensä, ja se näytti katselijoista vaikealta.');
INSERT INTO "finnish" VALUES(7,109,'7:109','Silloin sanoivat Faraon kansan päälliköt: »Tämä on totisesti taitava noita,');
INSERT INTO "finnish" VALUES(7,110,'7:110','joka aikoo loihtia teidät maastanne. Mitä neuvotte tässä pulmassa?»');
INSERT INTO "finnish" VALUES(7,111,'7:111','He (neuvonantajat) vastasivat: »Anna hänen ja hänen veljensä odottaa ja lähetä sanansaattajia kaupunkeihin');
INSERT INTO "finnish" VALUES(7,112,'7:112','noutamaan tänne luoksesi kaikki taitavat noidat.»');
INSERT INTO "finnish" VALUES(7,113,'7:113','Ja noidat tulivat Faraon luo ja sanoivat: »Saammehan kai palkinnon, jos pääsemme voitolle?»');
INSERT INTO "finnish" VALUES(7,114,'7:114','Hän vastasi: »Saatte, ja totisesti pääsette lähimpään seurueeseeni.»');
INSERT INTO "finnish" VALUES(7,115,'7:115','Silloin sanoivat he: »Mooses, heitätkö sinä ensin, vai mekö aloitamme?»');
INSERT INTO "finnish" VALUES(7,116,'7:116','Hän vastasi: »Aloittakaa te!» Ja heitettyään (sauvansa) he lumosivat ihmisten silmät ja säikähdyttivät heidät, ja hirveä oli heidän loihtutaitonsa.');
INSERT INTO "finnish" VALUES(7,117,'7:117','Mutta Me annoimme käskyn Moosekselle: »Heitä sauvasi!» Ja katso, se nieli kaiken, minkä he olivat loihtineet esiin.');
INSERT INTO "finnish" VALUES(7,118,'7:118','Siten ilmaisi totuus itsensä, ja kaikki, mitä he tekivät, meni tyhjiin.');
INSERT INTO "finnish" VALUES(7,119,'7:119','Ja he tulivat siinä paikassa voitetuiksi ja peräytyivät masentuneina.');
INSERT INTO "finnish" VALUES(7,120,'7:120','Ja noidat lankesivat maahan');
INSERT INTO "finnish" VALUES(7,121,'7:121','huudahtaen: »Me uskomme maailmojen Herraan,');
INSERT INTO "finnish" VALUES(7,122,'7:122','Mooseksen ja Aaronin Herraan.»');
INSERT INTO "finnish" VALUES(7,123,'7:123','Silloin sanoi Farao: »Uskotteko häneen ennenkuin Minä annan teille luvan? Katso, tällaisenko juonen olette keksineet kaupunkia vastaan karkoittaaksenne sen asukkaat. Mutta te saatte vielä kokea!');
INSERT INTO "finnish" VALUES(7,124,'7:124','Totisesti hakkautan poikki kätenne ja jalkanne, toisen toiselta puolelta; senjälkeen naulitsen teidät kaikki riippumaan.»');
INSERT INTO "finnish" VALUES(7,125,'7:125','He sanoivat: »Nyt on meidän totisesti palattava Herramme luo.');
INSERT INTO "finnish" VALUES(7,126,'7:126','Sinä purat vihaasi meitä vastaan vain siksi, että me uskomme Herramme tunnusmerkkeihin, kun ne meille lähetettiin. Herra! Täytä meidät kärsivällisyydellä ja auta meitä kuolemaan Sinulle alistuneina (muslimeina).»');
INSERT INTO "finnish" VALUES(7,127,'7:127','Ja Faraon kansan päälliköt sanoivat: »Sallitko sinä Mooseksen ja hänen kansansa aikaansaada maassa turmelusta ja hylätä sinut ja sinun Jumalasi?» Hän sanoi: »Me surmaamme heidän poikansa ja annamme heidän tyttäriensä elää; katso, meillä on oleva valta heidän ylitseen.»');
INSERT INTO "finnish" VALUES(7,128,'7:128','Ja Mooses sanoi kansalleen: »Huutakaa Jumalaa avuksenne ja olkaa lujia; katso, Jumalan on maa; Hän antaa sen perinnöksi niille orjistaan, joille tahtoo, ja hyvä loppu kuuluu niille, jotka pysyvät (Hänelle) uskollisina.»');
INSERT INTO "finnish" VALUES(7,129,'7:129','He vastasivat: »Me kärsimme vääryyttä ennenkuin tulit luoksemme ja samoin tulosi jälkeen». Hän sanoi: »Ehkä Herranne hävittää vihollisenne ja tekee teidät hänen seuraajiksensa maan päällä, sillä hän haluaa nähdä, kuinka te käyttäydytte.»');
INSERT INTO "finnish" VALUES(7,130,'7:130','Olemmehan rangaissut Faraon kansaa nälkävuosilla ja hedelmien menetyksellä, jotta se ottaisi varoituksesta vaarin.');
INSERT INTO "finnish" VALUES(7,131,'7:131','Ja kun jotakin hyvää tuli heidän osakseen, he sanoivat: »Tämähän kuuluikin meille.» Mutta kun paha kohtasi heitä, työnsivät he syyn Mooseksen ja hänen kannattajiensa niskoille. Mutta eikö heidän kohtalonsa riippunut vain Jumalasta? Kuitenkaan ei suurin osa heistä sitä tajunnut.');
INSERT INTO "finnish" VALUES(7,132,'7:132','Ja he sanoivat (Moosekselle): »Näytä mikä tunnusmerkki tahansa lumotaksesi meidät, emme me kuitenkaan usko sinuun.»');
INSERT INTO "finnish" VALUES(7,133,'7:133','Silloin lähetimme Me heille vedenpaisumuksen, heinäsirkkoja, syöpäläisiä, sammakoita ja verta; koko sarjan selviä tunnusmerkkejämme, mutta syntinen kansa paisui ylpeydessään.');
INSERT INTO "finnish" VALUES(7,134,'7:134','Ja aina kun vaiva kohtasi heitä, sanoivat he: »Mooses, rukoile puolestamme Herraasi, koska Hän on tehnyt liiton kanssasi. Katso, jos poistat tämän vaivan, niin uskomme sinuun ja totisesti päästämme Israelin lapset menemään kanssasi.»');
INSERT INTO "finnish" VALUES(7,135,'7:135','Mutta niin pian kuin Me olimme vapauttanut heidät vaivasta ja heille pantu määräaika oli mennyt umpeen, rikkoivat he lupauksensa.');
INSERT INTO "finnish" VALUES(7,136,'7:136','Silloin langetimme Me tuomion heille ja hukutimme heidät veteen, koska he väittivät tunnusmerkkimme valheeksi ja ylenkatsoivat ne.');
INSERT INTO "finnish" VALUES(7,137,'7:137','Ja kansalle, jota he nöyryyttivät, annoimme Me perinnöksi sekä itäiset että läntiset rajamaat, maan, jonka olimme siunannut; ja niin täyttyi Herrasi ihana sana Israelin lasten suhteen, siksi että he kestivät lujina. Ja Me hävitimme Faraon ja hänen kansansa työt ja rakennukset.');
INSERT INTO "finnish" VALUES(7,138,'7:138','Ja Me johdimme Israelin lapset meren halki, ja he tulivat erään kansan luo, joka palveli epäjumaliaan. He sanoivat: »Mooses, tee meille jumala, heidän jumaliensa kaltainen.» Hän sanoi: »Totisesti olette te kansa, joka harhoihin mieltyy.»');
INSERT INTO "finnish" VALUES(7,139,'7:139','Katso, perikatoon on joutuva kaikki, johon nuo turvaavat, ja turhuutta ovat heidän tekonsa.');
INSERT INTO "finnish" VALUES(7,140,'7:140','Hän sanoi: »Teillekö etsisin toista jumalaa kuin Jumalan, joka teidät korotti maailman kansojen yli?»');
INSERT INTO "finnish" VALUES(7,141,'7:141','Ja (muistakaa) kuinka Me pelastimme teidät Faraon kansan käsistä; he rääkkäsivät teitä hirveimmällä tavalla, surmasivat poikanne, jättäen tyttärenne elämään. Totisesti oli tämä raskas koettelemus Herraltanne.');
INSERT INTO "finnish" VALUES(7,142,'7:142','Me määräsimme Mooseksen kolmeksikymmeneksi vuorokaudeksi yksinäisyyteen ja lisäsimme niitä kymmenellä, niin että hänen Herransa määräämä aika oli neljäkymmentä vuorokautta. Ja Mooses sanoi veljelleen Aaronille: »Ole sijaisenani kansani keskuudessa! Tee, mikä oikein on, äläkä antaudu väärintekijäin tielle!»');
INSERT INTO "finnish" VALUES(7,143,'7:143','Ja kun Mooses tuli Meidän määräämäämme paikkaan ja hänen Herransa puhutteli häntä, sanoi hän: »Herrani, ilmesty minulle, jotta saisin katsella Sinua!» Herra vastasi: »Et kykene katselemaan Minua; mutta katsele vuorta, ja jos se seisoo lujana paikallaan, saat sinä nähdä minut», ja kun hänen Herransa peitti vuoren kirkkaudellaan, raukesi se hajalleen, ja Mooses lankesi pyörtyneenä maahan. Tultuaan tajuihinsa sanoi hän: »Ylistys olkoon Sinulle! Katuen käännyn puoleesi ja olen ensimmäinen oikeauskoisista.»');
INSERT INTO "finnish" VALUES(7,144,'7:144','Hän vastasi: »Mooses, katso, sinulle antamillani ilmoituksilla ja sanomilla olen valinnut sinut ennen kaikkia ihmisiä. Ota vastaan, minkä olen sinulle antanut, ja ole kiitollinen!»');
INSERT INTO "finnish" VALUES(7,145,'7:145','Ja Me kirjoitimme hänelle lauluihin kaikki varteen otettavat asiat sekä kaikki selitykset sanoen: »Pidä nämä lujasti ja käske kansaasi valitsemaan paras! Pian Me näytämme Sinulle jumalattomain osan.»');
INSERT INTO "finnish" VALUES(7,146,'7:146','Olen kääntävä pois tunnusmerkeistäni ne, jotka aiheetta ylpeilevät maan päällä; vaikka he näkisivät kaikki mahdolliset tunnusmerkit, eivät he usko niihin, ja jos he saavat nähdä vanhurskauden tien, eivät he omaksu sitä, mutta jos he näkevät harhatien, ottavat he sen omakseen. Ja näin siksi, että he pitävät tunnusmerkkimme vaiheena ja ylenkatsovat ne.');
INSERT INTO "finnish" VALUES(7,147,'7:147','Jotka valheeksi väittävät Meidän tunnusmerkkimme ja iankaikkisen elämän, heidän työnsä raukeavat tyhjiin. Palkittaisiinko heitä toisin kuin tekojensa mukaan?');
INSERT INTO "finnish" VALUES(7,148,'7:148','Ja Mooseksen kansa, hänen poissaolonsa aikana, teki koristuksistaan itselleen (epäjumalakseen) vasikan, joka ammui. Eikö heidän mieleensä johtunut, ettei se osannut puhua heille eikä johtaa heitä (oikein) vaeltamaan. He omaksuivat sen ja tulivat väärintekijöiksi.');
INSERT INTO "finnish" VALUES(7,149,'7:149','Mutta kun he kavahtivat ja huomasivat eksyneensä, sanoivat he: »Ellei Herramme armahda meitä ja anna meille anteeksi, olemme totisesti kadotuksen omat.»');
INSERT INTO "finnish" VALUES(7,150,'7:150','Ja tullessaan vihaisena ja murheellisella kansansa luo Mooses sanoi: »Kuinka kurjaa onkaan se, mihin poissaollessani ryhdyitte! Pyrittekö sivuuttamaan Herranne käskyjä?» Ja hän paiskasi taulut käsistään, tarttui veljensä päähän ja tempasi hänet puoleensa. Tämä (Aaron) sanoi: »Äitini poika, katso, kansa väheksyi minun mahtini ja oli vähällä surmata minut. Siksi älä anna vihollisten iloita onnettomuudestani äläkä työnnä minua väärintekijöitten joukkoon.»');
INSERT INTO "finnish" VALUES(7,151,'7:151','Hän sanoi: »Herrani, anna anteeksi minulle ja veljelleni ja sulje meidät laupeuteesi; sillä sinä olet armeliain armahtavien joukossa.»');
INSERT INTO "finnish" VALUES(7,152,'7:152','(Herra sanoi): »Niitä, jotka tekivät itselleen vasikan, kohtaa totisesti heidän Herransa viha ja kurjuus maallisessa elä~ mässä. Siten palkitsemme ne, jotka (valheita) sepittävät.');
INSERT INTO "finnish" VALUES(7,153,'7:153','Mutta jos ne, jotka pahaa tekevät, kääntyvät ja tulevat uskoon, on sinun Herrasi totisesti jälkeenpäin anteeksiantava ja armelias.');
INSERT INTO "finnish" VALUES(7,154,'7:154','Kun Mooseksen viha oli lauhtunut, otti hän maasta taulut, joihin oli piirretty johdatuksen ja armon sanat niille, jotka Her~ raansa pelkäävät.');
INSERT INTO "finnish" VALUES(7,155,'7:155','Ja Mooses valitsi seitsemänkymmentä miestä kansastaan Meidän määräämäksemme ajaksi, ja kun maanjäristys yllätti heidät, hän sanoi: »Herra, jos niin olisit tahtonut, olisit voinut jo aikaa sitten surmata heidät ja minutkin. Surmaisitko nyt meidät sen tähden, mitä hullut joukossamme ovat tehneet? Jumalani! heidän toimensa tahtoasi vastaan on sinun puoleltasi vain koettelemus, jolla saatat harhaan kenet tahdot ja johdatat kenet tahdot. Olet Suojelijamme; anna meille anteeksi ja armahda meitä. Sinä, joka olet paras kaikista armahtajista!');
INSERT INTO "finnish" VALUES(7,156,'7:156','Ja anna meille käsky kaikkeen hyvään tässä maailmassa ja tulevassa elämässä; katso, luoksesi olemme me palanneet ja näyttämäsi oikean tien omistimme.» Hän vastasi: »Kuritukseni kohtaa ketä tahdon, mutta laupeuteni voittaa kaiken; sen määrään ohjeeksi niille, jotka hurskautta harjoittavat ja antavat almuja, sekä niille, jotka uskovat tunnusmerkkeihimme»,');
INSERT INTO "finnish" VALUES(7,157,'7:157','niille, jotka seuraavat tätä lähettilästä, oppimatonta profeettaa, josta he löytävät kuvauksen Toorassa ja Evankeliumissa. Hän tulee vaatimaan heiltä hyviä tekoja ja kieltämään vääryyden, sallimaan heille mikä puhdasta on ja kieltämään saastaisen; hän on vapauttava heidät kuormasta ja kahleista, joihin heidät on kytketty. Jotka uskovat häneen, kunnioittavat ja auttavat häntä sekä seuraavat sitä valoa, joka on lähetetty hänen mukanaan, ne ovat onnellisia.');
INSERT INTO "finnish" VALUES(7,158,'7:158','Sano: »Ihmissuku! Olen totisesti Jumalan lähettiläs teille kaikille, taivaassa ja maan päällä; Hänen, jonka on valta taivaassa ja maan päällä; ei ole muuta jumalaa kuin Hän, Hän antaa elämän ja kuoleman. Uskokaa siis Jumalaan ja Hänen lähettilääseensä, oppimattomaan profeettaan, joka uskoo Jumalaan ja Hänen sanoihinsa, ja seuratkaa Häntä, jotta tulisitte oikealle tielle!»');
INSERT INTO "finnish" VALUES(7,159,'7:159','Mooseksen kansan keskuudessa on joukko, joka johtaa (muita)totuudessa ja sen mukaan jakaa oikeutta.');
INSERT INTO "finnish" VALUES(7,160,'7:160','Me olemme jakanut heidät kahteentoista heimoon ja ryhmään. Ja Me annoimme Moosekselle seuraavan ilmestyksen hänen pyytäessään vettä kansalleen: »Iske sauvallasi kallioon!» Silloin kaksitoista lähdettä pulppusi siitä esille; jokainen heimo löysi vedensaantipaikkansa. Ja Me annoimme pilven varjostaa heitä ja lähetimme heille ylhäältä mannaa ja viiriäisiä. - »Syökää sitä hyvää, minkä olemme teille suonut!» Emmekä Me tehnyt heille vääryyttä, vaan he tekivät sitä itselleen.');
INSERT INTO "finnish" VALUES(7,161,'7:161','Ja kun heille sanottiin: »Asukaa tässä kaupungissa ja ottakaa siitä ravintonne, minkä haluatte, sanokaa: Hittatun (syntien päästö) ja astukaa sisään portista kumartaen, niin annamme anteeksi pahuutenne ja enennämme armomme niille, jotka hyvää tekevät.»');
INSERT INTO "finnish" VALUES(7,162,'7:162','Silloin vääryydentekijät heidän joukossaan väänsivät tämän sanan toiseksi, jota heille ei oltu sanottu. Siksi lähetimme heille rangaistuksen ylhäältä, että he väärin tekivät.');
INSERT INTO "finnish" VALUES(7,163,'7:163','Ja kysele heiltä, Muhammed, kaupungista, joka oli meren rannalla, silloin kun he rikkoivat sapatin; sillä sapattipäivänä heidän kalansa tulivat parvittain veden pintaan heidän luokseen, mutta sinä päivänä, jolloin he eivät sapattia viettäneet, ne eivät tulleet. Niin koettelimme Me heitä sen vuoksi, että he olivat tottelemattomia.');
INSERT INTO "finnish" VALUES(7,164,'7:164','Ja kun eräs ryhmä heistä sanoi: »Miksi varoitatte te ihmisiä, jotka Jumala aikoo hukuttaa tai joille Hän lähettää ankaran vitsauksen?» he vastasivat: »Välttyäksemme Herranne soimauksilta ja jotta he karttaisivat pahaa.»');
INSERT INTO "finnish" VALUES(7,165,'7:165','Ja kun he unohtivat saamansa varoitukset, pelastimme Me ne, jotka kielsivät vääryyden, ja kuritimme vääryydentekijöitä ankaralla vitsauksella heidän jumalattomuutensa tähden.');
INSERT INTO "finnish" VALUES(7,166,'7:166','Ja heidän kerskuessaan teoista, jotka heiltä olivat kielletyt, sanoimme Me heille: »Muuttukaa inhottaviksi apinoiksi!»');
INSERT INTO "finnish" VALUES(7,167,'7:167','Ja (muistele), miten sinun Herrasi ilmoitti heille, että Hän aikoi aina ylösnousemuksen päivään asti lähettää heille (kurittajia), jotka rasittavat heitä mitä pahimmilla vaivoilla. Sinun Herrasi on totisesti nopea rankaisemaan, mutta Hän on myös armahtavainen ja laupias.');
INSERT INTO "finnish" VALUES(7,168,'7:168','Ja Me jaoimme heidät eri ryhmiksi maan päällä. Heidän joukossaan on vanhurskaita ja on sellaisia, jotka ovat kaukana hyvästä, ja Me koettelimme heitä sekä hyvällä että huonolla (onnella), jotta he kääntyisivät (luoksemme).');
INSERT INTO "finnish" VALUES(7,169,'7:169','Heitä seurasivat heidän jälkeläisensä, jotka perivät Pyhän kirjoituksen; he kävivät käsiksi tämän maailman hyvään sanoen: »Kyllä se suodaan meille anteeksi», ja jos taaskin sattui samanlaatuista hyvää heidän kohdalleen, tarttuivat he siihen. Eikö kirjoituksen liitto velvoittanut heitä puhumaan Jumalasta ainoastaan sitä, mikä on totuutta? Ja he ovat lukeneet, mitä siinä on kirjoitettuna! Mutta tulevan elämän asumus on kuitenkin parempi Jumalaa pelkääväisille; eikö teillä tosiaankaan ole ymmärrystä?');
INSERT INTO "finnish" VALUES(7,170,'7:170','On niitä, jotka pysyvät lujasti kiinni kirjoituksessa ja harjoittavat hartautta; totisesti emme tuhlaa pois niiden palkkaa,jotka hyvää,rakentavat.');
INSERT INTO "finnish" VALUES(7,171,'7:171','Ja kun hajoitimme vuoren heidän yläpuolellaan, aivan kuin se olisi ollut pilvi, ja he luulivat sen luhistuvan heidän päälleen (Me sanoimme): »Ottakaa vastaan, ja pysykää lujasti siinä, minkä olemme teille voimassamme antanut, ja ajatelkaa sen sisällystä, jotta karttaisitte pahaa.»');
INSERT INTO "finnish" VALUES(7,172,'7:172','Ja kun sinun Herrasi herätti ihmisen (Aadamin) lasten lanteista jälkeläisiä heille ja haastoi heidät todistamaan itseään vastaan (sanoen): »Enkö ole Herranne?» he vastasivat: »Kyllä, me olemme sen todistajia», niin tarkoitti tämä sitä, ettette te ylösnousemuspäivänä voisi sanoa: »Me olimme totisesti tietämättömiä tästä»');
INSERT INTO "finnish" VALUES(7,173,'7:173','tai ettette voisi sanoa: »Äitimme ja isämme ennen meitä kumarsivat muita Jumalan rinnalla, ja olemme vain heidän jälkeläisiään. Hävitätkö meidät sen tähden, mitä harhain palvojat tekivät?»');
INSERT INTO "finnish" VALUES(7,174,'7:174','Tällä tavoin selvitämme Me tunnusmerkkimme, jotta he kääntyisivät (puoleemme).');
INSERT INTO "finnish" VALUES(7,175,'7:175','Lue heille tarina ihmisistä, joille Me annoimme tunnusmerkkimme ja jotka työnsivät ne luotaan. Ja saatana saavutti heidät, ja heistä tuli sellaisia, joiden tie vie harhaan.');
INSERT INTO "finnish" VALUES(7,176,'7:176','Ja jos olisimme tahtonut, olisimme Me totisesti korottanut hänet niillä (tunnusmerkeillämme), mutta hän kiintyi maallisiin ja seurasi himojaan. Hän käyttäytyi kuin koira: jos käyt sitä kohti, pursuu sen kieli ulos suusta, ja jos jätät sen rauhaan, pursuu sen kieli suusta. Näin on laita myös ihmisten, jotka vääntävät tunnusmerkkimme valheeksi; kerro heille siksi tämä kertomus, jotta he ajattelisivat.');
INSERT INTO "finnish" VALUES(7,177,'7:177','Kurja on niiden ihmisten laita, jotka pitävät tunnusmerkkejämme vaiheena ja tekevät vääryyttä sieluilleen.');
INSERT INTO "finnish" VALUES(7,178,'7:178','Se, jota Jumala johdattaa, on kulkeva oikeata tietä, mutta ne, joiden Hän antaa joutua harhaan, ovat kadotuksen omat.');
INSERT INTO "finnish" VALUES(7,179,'7:179','Ja totisesti olemme helvetin omiksi luonut joukon dzinnejä ja ihmisiä. Heillä on sydämet, joilla he eivät ymmärrä, heillä on silmät, joilla he eivät näe, ja heillä on korvat, joilla he eivät kuule; he ovat karjan kaltaisia, ei, he kulkevat vielä pahemmin harhaan; he ovat välinpitämättömiä.');
INSERT INTO "finnish" VALUES(7,180,'7:180','Ja Jumalan ovat kaikkein jaloimmat nimet (ominaisuudet). Sentähden rukoilkaa Häntä niillä ja karttakaa niitä, jotka väärinkäyttävät Hänen nimiään! Kyllä he saavat palkan ansioittensa mukaan.');
INSERT INTO "finnish" VALUES(7,181,'7:181','Ja niiden keskuudessa, jotka olemme luonut, on joukko, joka johtaa ihmisiä totuudessa ja sen mukaisesti jakaa oikeutta.');
INSERT INTO "finnish" VALUES(7,182,'7:182','Mutta niitä, jotka pitävät tunnusmerkkimme valheena, kuljetamme Me pois askel askeleelta, tavalla, jota he eivät havaitse.');
INSERT INTO "finnish" VALUES(7,183,'7:183','Ja vaikka suvaitsenkin heitä, on Minun suunnitelmani horjumaton.');
INSERT INTO "finnish" VALUES(7,184,'7:184','Eivätkö he ymmärrä, ettei heidän kumppaninsa (Muhammed) suinkaan ole mieletön; hän on vain julkinen varoittaja.');
INSERT INTO "finnish" VALUES(7,185,'7:185','Eivätkö he ole tarkastelleet taivaitten ja maan hallintoa ja kaikkea, minkä Jumala on luonut (ja huomanneet) että heidän aikansa jo lähenee loppuaan? Ja mihin sanomaan he tämän (Koraanin) jälkeen aikovat uskoa?');
INSERT INTO "finnish" VALUES(7,186,'7:186','Sillä niille, joiden Jumala antaa kulkea harhaan, ei ole opasta; ja Hän jättää heidät menehtymään uhmailuihinsa.');
INSERT INTO "finnish" VALUES(7,187,'7:187','He kysyvät sinulta hetkeä, jolloin se tapahtuu. Sano: »Sen tietää ainoastaan Jumala, ja Hän yksin on tekevä aikanaan sen tunnetuksi. Raskaana se painaa taivasta ja maata; ja odottamatta on se yllättävä teidät. He kysyvät sinulta aivan kuin sinä sen tietäisit. Sano: »Jumala yksin sen tietää; mutta enimmät ihmiset eivät tiedä mitään.»');
INSERT INTO "finnish" VALUES(7,188,'7:188','Sano: »Ei ole minun vallassani itse itseäni hyödyttää tai vahingoittaa, kaikki on Jumalan vallassa; mutta jos tuntisin salatun, ympäröisin itseni hyvällä, eikä paha minuun ulottuisi. Minä olen vain varoittaja ja ilosanoman saattaja uskovaisille.');
INSERT INTO "finnish" VALUES(7,189,'7:189','Hän on luonut teidät yhdestä ainoasta ihmisestä, ja hänestä teki Hän hänen vaimonsa, jotta hän (mies) löytäisi hänestä virkistyksensä; ja kun hän oli maannut vaimonsa, kantoi tämä kevyesti taakkansa ja liikkui vapaasti. Kun se tuli raskaaksi, rukoilivat he Jumalaa, Herraansa: »Jos annat meille terveen (lapsen), olemme me sinulle totisesti kiitollisia!»');
INSERT INTO "finnish" VALUES(7,190,'7:190','Ja senjälkeen kun Hän oli antanut heille terveen (lapsen), kumarsivat he toistakin (jumalia) Hänen rinnallaan hyvän lahjan antajina. Mutta Jumala on paljon korkeampi, kuin mitä he asettavat Hänen rinnalleen.');
INSERT INTO "finnish" VALUES(7,191,'7:191','Asettaisivatko he Hänen rinnalleen sellaisia, jotka eivät voi mitään luoda, kun ovat itsekin Hänen luomiaan');
INSERT INTO "finnish" VALUES(7,192,'7:192','eivätkä voi heitä auttaa, kun eivät voi auttaa edes itseäänkään?');
INSERT INTO "finnish" VALUES(7,193,'7:193','Ja jos te kutsutte heitä alistumaan johdatukseen, eivät he seuraa teitä; on samantekevää teille, kutsutteko heitä vai vaikenette.');
INSERT INTO "finnish" VALUES(7,194,'7:194','Katso, ne, joita te palvelette Jumalan lisäksi, ovat kaltaisianne orjia. Rukoilkaa siis heitä, ja vastatkoot he teille, jos tarkoitatte täyttä totta!');
INSERT INTO "finnish" VALUES(7,195,'7:195','Onko heillä (epäjumalilla) jalat kävelläkseen? Tai onko heillä kädet tarttuakseen? Tai onko heillä silmät nähdäkseen tai korvat kuullakseen? Sano: »Rukoilkaa jumalanvertaisianne, punokaa juonenne minua vastaan älkääkä minua säästäkö.');
INSERT INTO "finnish" VALUES(7,196,'7:196','Katso, minun liittolaisena on Jumala, Hän, joka on lähettänyt Pyhän kirjoituksen ylhäältä; Hän suojelee kaikkia vanhurskaita.');
INSERT INTO "finnish" VALUES(7,197,'7:197','Mutta ne, joita te rukoilette Hänen vertaisinaan, eivät voi auttaa teitä eivätkä edes itseäänkään.»');
INSERT INTO "finnish" VALUES(7,198,'7:198','Ja jos te vaaditte heitä seuraamaan johdatusta, eivät he kuule, ja sinä näet heidän tarkastelevan sinua näkemättä mitään.');
INSERT INTO "finnish" VALUES(7,199,'7:199','Ole sääliväinen, vaadi kohtuutta ja karta ymmärtämättömiä!');
INSERT INTO "finnish" VALUES(7,200,'7:200','Ja kun joku Saatanan pistopuhe haavoittaa mieltäsi, niin pakene Jumalan turviin! Hän on totisesti kuuleva, tietävä.');
INSERT INTO "finnish" VALUES(7,201,'7:201','Totisesti, kun hurskauden harjoittajia kohtaa joku Saatanan kiusaus, muistavat he (Hänet), ja katso, silloin valkenee heille (johdatus).');
INSERT INTO "finnish" VALUES(7,202,'7:202','Heidän veljensä viekoittelee hän yhä edemmäksi harhaan eikä hellitä.');
INSERT INTO "finnish" VALUES(7,203,'7:203','Ja kun et tuo heille mitään tunnusmerkkiä, he sanovat: »Miksi et ole valinnut mitään?» Sano: »Minä noudatan vain sitä, minkä Herrani ilmoittaa minulle.» Tämä on valkeus Herraltamme, johdatukseksi ja armonosoitukseksi uskovaisille.');
INSERT INTO "finnish" VALUES(7,204,'7:204','Kun Koraania luetaan, kuunnelkaa tarkkaan ja olkaa vaiti, jotta saisitte armon osaksenne.');
INSERT INTO "finnish" VALUES(7,205,'7:205','Ja rukoile Herraasi sielusi nöyryydessä ja pelossa suuritta sanoitta sekä aamuin että illoin, äläkä ole penseä!');
INSERT INTO "finnish" VALUES(7,206,'7:206','Katso niitä, jotka ovat Herrasi luona; ei ylpeys saa heitä lakkaamaan palvelemasta Häntä, vaan he ylistävät Häntä ja kumartavat Häntä.');
INSERT INTO "finnish" VALUES(8,1,'8:1','He kysyvät Sinulta sotasaalista; sano: »Sotasaalis kuuluu Jumalalle ja lähettiläälle.» Pelätkää siis Jumalaa ja järjestäkää kaikki sovussa keskenänne; ja totelkaa Jumalaa ja Hänen lähettilästään, jos olette oikeauskoisia.');
INSERT INTO "finnish" VALUES(8,2,'8:2','Ainoastaan ne ovat oikeauskoisia, joiden sydämet valtaa pelko Jumalan nimeä lausuttaessa, joiden usko kasvaa, kun Meidän tunnusmerkkimme heille luetaan, ja jotka turvaavat Herraansa.');
INSERT INTO "finnish" VALUES(8,3,'8:3','Ne, jotka pysyvät rukouksessa ja lahjoittavat muille siitä, mitä Me olemme heille suonut.');
INSERT INTO "finnish" VALUES(8,4,'8:4','Nämä ovat totisia oikeauskoisia. Heitä varten on eri arvoasteet heidän Herransa luona sekä anteeksiantamus ja jalomielinen huolenpito.');
INSERT INTO "finnish" VALUES(8,5,'8:5','(Ajattele esimerkiksi), kuinka Herrasi antoi sinun (Muhammedin) lähteä kotiseudultasi vakavaan tehtävään; ja katso, osa uskovaisista oli totisesti haluton (seuraamaan).');
INSERT INTO "finnish" VALUES(8,6,'8:6','He väittelivät kanssasi (asian) totuudesta, senjälkeen kun se oli selvästi heille näytetty; aivan kuin olisi heitä viety kuolemaan ja he olisivat siihen tuijottaneet.');
INSERT INTO "finnish" VALUES(8,7,'8:7','Ja kuinka Jumala lupasi, että toinen kahdesta (vihollisen) joukosta on oleva teidän, ja te toivoitte, että aseeton joukko lankeaisi teidän osallenne, mutta Jumala tahtoi vahvistaa sanojensa totuuden ja hävittää uskottomat viimeiseen mieheen');
INSERT INTO "finnish" VALUES(8,8,'8:8','vahvistaakseen totuuden ja kumotakseen valheen, vaikkakaan se ei miellyttäisi syntisiä!');
INSERT INTO "finnish" VALUES(8,9,'8:9','Ja kuinka teidän huutaessanne apua Herraltanne Hän kuuli rukouksenne ja sanoi: »Totisesti autan teitä tuhannella enkelillä toinen toisensa jälkeen.»');
INSERT INTO "finnish" VALUES(8,10,'8:10','Ja tämän teki Jumala ainoastaan jotta se olisi ilosanoma teille ja jotta sydämenne rauhoittuisivat, sillä apu tulee vain Jumalalta, katso, Jumala on mahtava ja viisas.');
INSERT INTO "finnish" VALUES(8,11,'8:11','Entä kun Hän antoi teidän vaipua lyhyeen uneen, joka oli kuin Hänen lähettämänsä virkistys, ja lähetti vettä taivaasta puhdistaakseen teidät, poistaakseen paholaisen saastutuksen teistä ja vahvistaakseen sydämenne sekä lujittaakseen askeleenne.');
INSERT INTO "finnish" VALUES(8,12,'8:12','Muista, kuinka Herrasi julisti enkeleille: »Olen kanssanne; tukekaa siis niitä, jotka uskovat. Täytän pian uskottomien sydämet pelolla; silloin hakatkaa poikki heidän kaulansa ja heidän kaikki sormensa.»');
INSERT INTO "finnish" VALUES(8,13,'8:13','Ja tämä siksi, että he niskoittelevat Jumalaa ja Hänen lähettilästään vastaan, sillä jos joku niskoittelee Jumalaa ja Hänen lähettilästään vastaan, on Jumala totisesti ankara rangaistessaan.');
INSERT INTO "finnish" VALUES(8,14,'8:14','Tässä on rangaistus teille; maistakaa sitä siis! Uskottomia varten on tulen tuska.');
INSERT INTO "finnish" VALUES(8,15,'8:15','Te, jotka uskotte. kun kohtaatte uskottomia taistelutantereella, älkää kääntäkö heille selkäänne.');
INSERT INTO "finnish" VALUES(8,16,'8:16','Sillä se, joka sinä päivänä kääntää heille selkänsä, paitsi sotaliikkeen yhteydessä tai yhtyessään omiensa ryhmään, vetää totisesti päällensä Jumalan vihan; hänen olinpaikakseen tulee helvetti, ja kurjaa on joutua sinne.');
INSERT INTO "finnish" VALUES(8,17,'8:17','Ette te (muslimit) surmanneet heitä, vaan Jumala heidät surmasi; etkä sinä (Muhammed) ampunut, kun joustasi käytit, vaan Jumala ampui, sillä hän halusi koetella oikeauskoisia suosiollaan. Jumala on totisesti kuuleva, tietävä.');
INSERT INTO "finnish" VALUES(8,18,'8:18','Niin on laita; Jumala uskottomien juonet herpaisee.');
INSERT INTO "finnish" VALUES(8,19,'8:19','(Uskottomat!) Jos te toivotte ratkaisua, niin totisesti on tuomio jo langennut, mutta jos luovutte vastustuksestanne, on se teille parempi. Mutta jos kuitenkin palaatte (sotajalalle), palaamme Mekin. Ja teidän sotajoukkonne ei auta teitä vähääkään, miten lukuisa se lieneekin. Jumala on totisesti oikeauskoisten kanssa.');
INSERT INTO "finnish" VALUES(8,20,'8:20','Te, jotka uskotte, totelkaa Jumalaa ja Hänen lähettilästään älkääkä luopuko hänestä, koska kuulette hänen puhuvan.');
INSERT INTO "finnish" VALUES(8,21,'8:21','Älkääkä olko niiden kaltaisia, jotka sanovat: »Me kuuntelemme», vaikkeivat kuuntelekaan.');
INSERT INTO "finnish" VALUES(8,22,'8:22','Katso, kaikista elollisista kurjimpia ovat Jumalan silmissä ne kuurot ja mykät, jotka eivät ymmärrä.');
INSERT INTO "finnish" VALUES(8,23,'8:23','Ja jos Jumala olisi huomannut heissä jotakin hyvää, totisesti Hän olisi antanut heille kuulon. Mutta jos Hän olisi antanut heidän kuulla, kääntyisivät he pois, sillä he ovat penseitä.');
INSERT INTO "finnish" VALUES(8,24,'8:24','Te, jotka uskotte, totelkaa Jumalaa ja Hänen lähettilästään, kun Hän kutsuu teitä sen ääreen, mikä teidät eläviksi tekee, ja tietäkää, että Jumala hiipii ihmisen ja hänen sydämensä väliin ja että te tulette kokoontumaan Hänen luoksensa.');
INSERT INTO "finnish" VALUES(8,25,'8:25','Ja kavahtakaa kapinoimista; sen rangaistus ei kohtaa vain väärintekijöitä joukossanne, ja tietäkää, että Jumala on ankara selvittelyissään!');
INSERT INTO "finnish" VALUES(8,26,'8:26','Ja muistakaa, että kun olitte harvalukuisia ja halveksittuja maan päällä ja pelkäsitte, että ihmiset raivaisivat teidät tieltään, silloin Hän antoi teille suojan ja vahvisti teitä avullaan sekä varusti teidät hyvillä antimillaan, jotta olisitte kiitolliset!');
INSERT INTO "finnish" VALUES(8,27,'8:27','Te, jotka uskotte, älkää loukatko Jumalaa ja lähettilästä, älkääkä pettäkö sanaanne tietoisesti.');
INSERT INTO "finnish" VALUES(8,28,'8:28','Ja tietäkää, että rikkautenne ja lapsenne ovat Teille koetus, ja että suuri palkinto odottaa Jumalan luona!');
INSERT INTO "finnish" VALUES(8,29,'8:29','Te, jotka uskotte, jos pelkäätte Jumalaa, antaa Hän teille kyvyn tuntea oikean ja väärän, sovittaa pahat tekonne ja antaa teille anteeksi; Jumala on rajaton runsaudessaan.');
INSERT INTO "finnish" VALUES(8,30,'8:30','Entä kun uskottomat vehkeilivät sinua vastaan, aikoen vangita sinut tai surmata sinut tai karkoittaa sinut! He tekivät suunnitelmiaan, mutta Jumala teki omiaan, ja Jumala on paras suunnittelija.');
INSERT INTO "finnish" VALUES(8,31,'8:31','Ja kun heille luetaan tunnusmerkkejämme, sanovat he: »Olemme jo kylliksi kuulleet; jos tahtoisimme, voisimme loihtia samanlaisia; nämä eivät ole muuta kuin vanhan ajan taruja.»');
INSERT INTO "finnish" VALUES(8,32,'8:32','Entä kun he sanoivat: »Jumala, jos tämä on sinun lähettämäsi totuus, niin anna kivien sataa taivaasta päällemme tai koettele meitä tuskallisella rangaistuksella!»');
INSERT INTO "finnish" VALUES(8,33,'8:33','Mutta Jumala ei tahtonut rangaista heitä sinun ollessasi heidän joukossaan; eikä Hän tahtonut heitä rangaista senkään vuoksi, että he pyytelivät Häneltä anteeksi.');
INSERT INTO "finnish" VALUES(8,34,'8:34','Ei kuitenkaan ole syytä Jumalan olla heitä rankaisematta, sillä he estävät uskovaisia käymästä pyhässä moskeijassa olematta itse sen vartijoita. Sen vartijoita ovat ainoastaan hurskaat. Kuitenkaan ei suurin osa heistä sitä tiedä.');
INSERT INTO "finnish" VALUES(8,35,'8:35','Ja heidän rukouksensa temppelin liepeillä ei ole muuta kuin viheltämistä ja käsien pauketta. Siksi maistakaa tuskaa uskottomuutenne tähden!');
INSERT INTO "finnish" VALUES(8,36,'8:36','Epäuskoiset kuluttavat varojaan käännyttääkseen ihmisiä pois Jumalan tieltä. He kuluttakoot, jälkeenpäin saavat he katua, ja lopuksi heidät voitetaan.Ja ne, jotka eivät usko, kootaan helvettiin,');
INSERT INTO "finnish" VALUES(8,37,'8:37','että Jumala voisi erottaa saastaiset puhtaista ja asettaa saastaiset päällekkäin; Hän kokoaa heidät kasaan ja heittää helvettiin. Nämä ovat kadotukseen tuomittuja.');
INSERT INTO "finnish" VALUES(8,38,'8:38','Sano niille, jotka eivät usko, että jos he herkeävät epäuskostaan, annetaan heille heidän entiset tekonsa anteeksi, mutta jos lankeavat uudelleen, niin on entisten rangaistus heidän silmiensä edessä.');
INSERT INTO "finnish" VALUES(8,39,'8:39','Ja taistelkaa heitä vastaan, kunnes kaikki kiusaukset taukoavat ja kunnes Jumalan tuomiota noudatetaan kaikessa. Mutta jos he taas mielensä muuttavat, kyllä Jumala näkee, mitä he tekevät.');
INSERT INTO "finnish" VALUES(8,40,'8:40','Ja jos he kääntävät teille selkänsä, niin tietäkää, että Jumala on suojelijanne, paras liittolaisenne ja auttajanne.');
INSERT INTO "finnish" VALUES(8,41,'8:41','Ja tietäkää, että hankkimastanne sotasaaliista kuuluu viides osa Jumalalle, toisin sanoen Hänen lähettiläälleen ja tämän lähiomaisille, orvoille, köyhille ja vaeltajille, jos uskotte Jumalaan ja siihen, minkä lähetimme palvelijallemme ratkaisun päivänä, sinä päivänä, jolloin molemmat sotajoukot kohtasivat toisensa. Jumalan on totisesti kaikki mahti.');
INSERT INTO "finnish" VALUES(8,42,'8:42','Muistakaa, miten te olitte tämänpuoleisella rinteellä ja he tuonpuoleisella ja karavaani oli alapuolellanne. Ja jos te olisitte solmineet taistelusopimuksen, olisitte te varmaan rikkoneet välipuheen; mutta asia oli toisin, jotta Jumala saattoi toteuttaa sen, mikä oli tapahtuva, jotta sillä, joka menetti henkensä, olisi menehtyessään selvä todistus edessään, ja jotta se, joka jäi elämään, eläisi selvän todistuksen perusteella; Jumala on, toden totta, kuuleva, tietävä.');
INSERT INTO "finnish" VALUES(8,43,'8:43','Muistakaa, miten Jumala unessa antoi heidän (vastustajain) sinusta näyttää harvalukuisilta. Sillä jos Hän olisi antanut sinun nähdä heidät lukuisiksi, väkesi olisi varmasti masentunut, ja te olisitte riidelleet asiasta keskenänne; mutta Jumala varjeli teidät. Hän tuntee totisesti sydänten sisimmät aivoitukset.');
INSERT INTO "finnish" VALUES(8,44,'8:44','Ja kun te iskitte yhteen, Hän antoi heidän teidän silmissänne näyttää vähälukuisilta ja teki teidät harvalukuisiksi heidän silmissään, jotta Jumala toteuttaisi asian, jonka tuli tapahtua. Ja Jumalan luo palaa kaikki olevainen.');
INSERT INTO "finnish" VALUES(8,45,'8:45','Te uskovaiset, kun te kohtaatte sotajoukon, olkaa lujia ja rukoilkaa alati Jumalaa, jotta menestyisitte.');
INSERT INTO "finnish" VALUES(8,46,'8:46','Ja totelkaa Jumalaa ja Hänen lähettilästään, älkääkä kiistelkö, jotta ette lannistuisi ja menettäisi sisuanne, vaan olkaa itsepintaiset; Jumala on totisesti sitkeiden puolella.');
INSERT INTO "finnish" VALUES(8,47,'8:47','Älkää olko niitten kaltaisia, jotka kerskaten lähtivät liikkeelle asunnoistaan, jotta ihmiset heidät näkisivät ja he saisivat muut luopumaan Jumalan retkestä; Jumala piirittää heidän tekonsa.');
INSERT INTO "finnish" VALUES(8,48,'8:48','Entä kun pahahenki näytti heille heidän tekonsa kelpo teoiksi ja sanoi: »Yksikään ihminen ei ole voittava teitä tänään, ja minä olen teidän liittolaisenne.» Mutta kun molemmat sotajoukot näkivät toisensa vastatusten, teki hän (pahahenki) täyskäännöksen ja sanoi: »Pysyn totisesti erossa teistä, sillä minä näen, mitä te ette näe. Totisesti saa Jumala minut pelkäämään, sillä Jumala on ankara rankaisija.»');
INSERT INTO "finnish" VALUES(8,49,'8:49','Muistakaa, kuinka tekopyhät ja sydämeltään sairaat sanoivat: »Heidän uskontonsa on pettänyt heidät (muslimit).» Mutta jos joku turvaa Jumalaan, on Jumala totisesti mahtava ja viisas.');
INSERT INTO "finnish" VALUES(8,50,'8:50','Jospa voisit nähdä heidät, kun enkelit antavat uskottomille täyden ansion mukaan. Kuinka he lyövät heitä kasvoihin ja selkään ja sanovat: »Maistakaa polttavaa tuskaa!');
INSERT INTO "finnish" VALUES(8,51,'8:51','Ja tämä on käsienne töitten ansiosta ja siksi, että Jumala jakaa palvelijoineen täyttä oikeutta.»');
INSERT INTO "finnish" VALUES(8,52,'8:52','Samoin kävi Faraon kansalle ja heidän edeltäjilleen, jotka kielsivät Jumalan tunnusmerkit: Jumala kävi heihin käsiksi heidän syntiensä tähden; katso, Jumala on totisesti väkevä ja ankara rangaistessaan.');
INSERT INTO "finnish" VALUES(8,53,'8:53','Ja tämä siksi, että Jumala ei peruuta armoa, jolla Hän on joitakuita ihmisiä siunannut, ennenkuin nämä luopuvat siitä, mikä on heidän omissa sydämissään. Ja siksi, että Jumala on kuuleva, tietävä.');
INSERT INTO "finnish" VALUES(8,54,'8:54','Samoin kävi Faraon kansalle ja niille, jotka väittivät Herransa tunnusmerkit valheeksi ennen heitä. Me hävitimme heidät heidän syntiensä tähden ja me hukutimme Faraon kansan, sillä kaikki olivat väärintekijöitä.');
INSERT INTO "finnish" VALUES(8,55,'8:55','Katso, kurjimmat elolliset olennot ovat Jumalan silmissä epäuskoon kääntyneet, nuo, jotka eivät uskoa tahdo.');
INSERT INTO "finnish" VALUES(8,56,'8:56','Niihin kuuluvat nekin, jotka tekivät kanssasi sopimuksen ja sitten rikkoivat sopimuksensa joka kerta, eivätkä mitään kunnioita.');
INSERT INTO "finnish" VALUES(8,57,'8:57','Jos saat heidät sodassa vangiksi, tee heistä varoittava esimerkki, jotta heidän seuraajansa ottaisivat varoituksesta vaarin.');
INSERT INTO "finnish" VALUES(8,58,'8:58','Ja jos pelkäät petosta jonkun kansan puolelta, niin heitä heille takaisin sopimuksensa samalla tavalla. Katso, Jumala ei rakasta petollisia.');
INSERT INTO "finnish" VALUES(8,59,'8:59','Äläkä salli uskottomien ajatella pääsevänsä voitolle. Eivät he osaansa vältä.');
INSERT INTO "finnish" VALUES(8,60,'8:60','Ja varustakaa (muslimit) heitä vastaan niin suuri sotavoima ja hyvin ravittuja ratsuja kuin vain voitte, säikyttääksenne siten sekä Jumalan vastustajat että omat vihollisenne ja myöskin muut, joita ette tunne, mutta jotka Jumala tuntee. Ja mitä kulutattekin Jumalan tiellä ja asialla, se korvataan teille täydelleen, eikä teille tule mitään vääryyttä tapahtumaan.');
INSERT INTO "finnish" VALUES(8,61,'8:61','Mutta jos he ovat taipuvaisia rauhaan, niin ole myöskin sinä taipuvainen siihen ja turvaa Jumalaan! Hän on totisesti kuuleva, tietävä kaiken.');
INSERT INTO "finnish" VALUES(8,62,'8:62','Ja jos he tahtovat pettää sinut, niin Jumalan tuki totisesti riittää sinulle. Hän vahvistaa sinua avullaan ja oikeauskoisten avulla.');
INSERT INTO "finnish" VALUES(8,63,'8:63','Ja Hän on pannut rakkauden heidän sydämiinsä. Vaikka olisit antanut pois kaiken, mitä on maan päällä, et olisi voinut panna rakkautta heidän sydämiinsä, mutta Jumala on pannut niihin rakkauden. Hän on totisesti mahtava, viisas.');
INSERT INTO "finnish" VALUES(8,64,'8:64','Oi profeetta, Jumalassa ja niissä oikeauskoisissa, jotka sinua seuraavat, on sinulle kyllin.');
INSERT INTO "finnish" VALUES(8,65,'8:65','Oi Profeetta, innostuta oikeauskoiset taisteluun! jos joukossanne on edes kaksikymmentä sitkeätä, voittavat he kaksisataa, ja jos joukossanne on sata, voittavat he tuhat niistä, jotka eivät usko, siksi että nämä ovat ihmisiä, joilta puuttuu ymmärrys.');
INSERT INTO "finnish" VALUES(8,66,'8:66','Nyt on Jumala keventänyt kuormanne, sillä Hän tiesi, että teissä oli heikkoutta. Ja jos joukossanne on sata kestävää, voittavat he kaksisataa, ja jos joukossanne on tuhat, voittavat he Jumalan avulla kaksituhatta; sillä Jumala on kestäväisten kanssa.');
INSERT INTO "finnish" VALUES(8,67,'8:67','Ei profeetalta voi vaatia, että hänellä olisi vankeja, ennenkuin verenvuodatus on tapahtunut maan päällä. Te halajatte tämän maailman hyvyyttä, mutta Jumala tahtoo teille tulevaa elämää, ja Jumala on mahtava, viisas.');
INSERT INTO "finnish" VALUES(8,68,'8:68','Jollei Jumalan käsky olisi tullut jo ennemmin, olisi teitä kohdannut hirveä rangaistus kaikesta siitä, minkä olitte ottaneet.');
INSERT INTO "finnish" VALUES(8,69,'8:69','Nauttikaa nyt saamastanne saaliista, siitä, mikä on luvallista ja hyvää, ja antakaa kunnia Jumalalle! Jumala on totisesti anteeksiantava, armollinen.');
INSERT INTO "finnish" VALUES(8,70,'8:70','Oi profeetta! Sano hallussanne oleville vangeille: »Jos Jumala havaitsee jotakin hyvää sydämissänne, antaa Hän teille parempaa, kuin se oli, mikä teiltä on otettu, ja armahtaa teidät, sillä Jumala on anteeksiantava, armollinen.»');
INSERT INTO "finnish" VALUES(8,71,'8:71','Mutta jos he aikovat pettää sinut, niin ovat he jo ennen olleet petollisia Jumalaa kohtaan. Siksi antoikin Hän eräiden heistä joutua valtaasi; Jumala on tietävä, viisas.');
INSERT INTO "finnish" VALUES(8,72,'8:72','Totisesti ne, jotka uskoivat ja lähtivät kodeistaan (Jumalan sotaretkelle) ja panivat alttiiksi omaisuutensa ja elämänsä Jumalan asian puolesta, sekä ne, jotka antoivat (heille) suojaa ja apua, kaikki nämä ovat toistensa liittolaisia. Mutta niiden kanssa, jotka uskovat, mutta eivät lähde liikkeelle, ette ole liitossa, ennenkuin he lähtevät kodeistaan, mutta jos he uskon asiassa anovat apuanne, on teidän velvollisuutenne auttaa heitä, paitsi sellaista kansaa vastaan, jonka kanssa teillä on liitto; Jumala näkee tekonne.');
INSERT INTO "finnish" VALUES(8,73,'8:73','Uskottomatkin ovat toistensa liittolaisia. Jos te ette auta toisianne, syntyy vaino ja suuri turmelus maan päällä.');
INSERT INTO "finnish" VALUES(8,74,'8:74','Ne, jotka uskovat ja ovat lähteneet kodeistaan liikkeelle sekä taistelleet Jumalan retkellä, ja ne, jotka ovat antaneet heille suojaa ja apua, ovat totisesti oikeauskoisia; heidän palkkansa on armo ja heidän osansa ihana.');
INSERT INTO "finnish" VALUES(8,75,'8:75','Ja ne, jotka jälkeenpäin ovat omaksuneet uskon ja lähteneet retkelle ja taistelleet teidän rinnallanne, kuuluvat teihin. Ja verisukulaiset ovat Pyhän kirjoituksen mukaan lähimpiä toisilleen: Jumala on totisesti kaikkitietävä.');
INSERT INTO "finnish" VALUES(9,1,'9:1','Tämä on Jumalan ja Hänen lähettiläänsä (julistama) vastuuvapaus niiden pakanain suhteen, joiden kanssa te (muslimit) olette tehneet sopimuksen.');
INSERT INTO "finnish" VALUES(9,2,'9:2','Kulkekaa sentähden maassa vapaasti neljä kuukautta ja tietäkää, että olette Jumalan ulottuvilla sekä että Jumala saattaa uskottomat häpeään.');
INSERT INTO "finnish" VALUES(9,3,'9:3','Tämä on Jumalan ja Hänen lähettiläänsä julistus ihmisille suuren toivioretken päivänä, että Jumalaa ei sido kosketus pakanain kanssa, samoin kuin se ei sido Hänen lähettilästänsäkään. Ja jos käännytte (Hänen puoleensa), on se teille parhaaksi, jos kuitenkin käännätte selkänne, niin tietäkää, ettette voi välttää Jumalaa! Ja julista niille, jotka eivät usko, sanoma tuskallisesta rangaistuksesta,');
INSERT INTO "finnish" VALUES(9,4,'9:4','lukuunottamatta niitä monijumalaisia, joiden kanssa olette tehneet sopimuksen, kun he eivät ole millään rikkoneet teitä vastaan eivätkä auttaneet ketään muuta teitä vastaan. Täyttäkää siis sopimuksenne heidän kanssaan määräaikaan asti. Jumala totisesti rakastaa kunniantuntoisia.');
INSERT INTO "finnish" VALUES(9,5,'9:5','Mutta kun pyhät kuukaudet ovat kuluneet, niin vuodattakaa pakanain verta missä tahansa heitä tapaattekin. Ahdistakaa ja piirittäkää heitä ja väijykää heitä kaikkialla, mutta jos he kääntyvät (oikeaan Jumalan uskoon), rukoilevat ja antavat almut, niin päästäkää heidät menemään. Jumala on totisesti anteeksiantava ja armollinen.');
INSERT INTO "finnish" VALUES(9,6,'9:6','Ja jos joku monijumalaisista anoo suojaasi, niin suo se hänelle, kunnes hän saa kuulla Herran sanan. Senjälkeen anna hänen päästä turvaan. Sillä he ovat ihmisiä, jotka eivät (parempaa) tiedä.');
INSERT INTO "finnish" VALUES(9,7,'9:7','Miten voisi Jumalaa ja Hänen lähettilästään sitoa sopimus pakanain kanssa? Paitsi niiden, joiden kanssa olette tehneet sopimuksen pyhän temppelin seutuvilla. Ja niin kauan kuin he pysyvät (sopimukselle) uskollisina, on teidänkin pysyttävä uskollisina heitä kohtaan. Jumala totisesti rakastaa hurskaita.');
INSERT INTO "finnish" VALUES(9,8,'9:8','Kuinka? Jos he saisivat teistä voiton, eivät he välittäisi ystävyyssuhteestä eikä sopimuksesta kanssanne. Puheellaan koettavat he olla mieliksenne, mutta heidän sydämensä ovat kuitenkin teitä vastaan, ja suurin osa heistä on jumalattomia.');
INSERT INTO "finnish" VALUES(9,9,'9:9','Mitättömästä hinnasta he ovat luopuneet Jumalan tunnusmerkeistä ja estelevät (muita) Jumalan tieltä; kauheita ovat totisesti heidän tekonsa.');
INSERT INTO "finnish" VALUES(9,10,'9:10','He eivät pidä lukua sukulaisuudesta eivätkä liittosopimuksesta uskovaisten kanssa, sillä he ovat niitä, jotka (liitot) rikkovat.');
INSERT INTO "finnish" VALUES(9,11,'9:11','Mutta jos he kääntyvät (Jumalan alamaisiksi), rukoilevat ja antavat säädetyt almut, ovat he uskonveljiänne. Ja Me selvitämme tunnusmerkkimme ihmisille, joilla on ymmärrystä.');
INSERT INTO "finnish" VALUES(9,12,'9:12','Mutta jos he sopimuksenteon jälkeen rikkovat valansa ja pilkkaavat teitä uskonne vuoksi, niin taistelkaa sellaisten uskottomien johtomiehiä vastaan - heille eivät totisesti valat mitään merkitse - jotta he luopuisivat puuhistaan.');
INSERT INTO "finnish" VALUES(9,13,'9:13','Miksi ette taistelisi ihmisiä vastaan, jotka ovat rikkoneet valansa ja aikoneet karkoittaa lähettilään, etenkin kun he ovat ensimmäisinä aloittaneet (vihollisuudet). Pelkäättekö heitä? Onhan kuitenkin Jumala paremmin ansainnut sen, että te Häntä pelkäätte, jos olette oikeauskoisia.');
INSERT INTO "finnish" VALUES(9,14,'9:14','Taistelkaa heitä vastaan, Jumala rankaisee heitä teidän kädellänne ja saattaa heidät häpeään; Hän auttaa teitä vastustaessanne heitä ja parantaa oikeauskoisten rinnat.');
INSERT INTO "finnish" VALUES(9,15,'9:15','Ja Hän poistaa vihan heidän sydämistään, sillä Jumala säälii ketä tahtoo; Jumala on tietävä, viisas.');
INSERT INTO "finnish" VALUES(9,16,'9:16','Vai arveletteko tulevanne hylätyiksi ennenkuin Jumala ehtii erottaa, ketkä joukostanne taistelivat eivätkä tunnustaneet ystävikseen ketään muuta kuin Jumalan, Hänen lähettiläänsä ja oikeauskoiset? Ja Jumala tuntee tekonne.');
INSERT INTO "finnish" VALUES(9,17,'9:17','Monijumalaisten (pakanain) ei sovi käydä Jumalan temppelissä, koska heidän epäuskonsa todistaa heitä vastaan. Turhiksi osoittautuvat heidän tekonsa, ja tulessa saavat he ikuisesti olla.');
INSERT INTO "finnish" VALUES(9,18,'9:18','Jumalan temppelissä saa vain se käydä, joka uskoo Jumalaan ja viimeiseen päivään ja harjoittaa rukoilemista ja antaa säädettyjä almuja eikä pelkää ketään muuta kuin Jumalaa; siksi nämä ovat niitä, jotka saavat toivoa olevansa johdatuksen omia.');
INSERT INTO "finnish" VALUES(9,19,'9:19','Pidättekö juomaveden antamista toivioretkeläisille, pyhäkön rakentamista (tai siellä käymistä) samanarvoisena kuin uskoa Jumalaan ja viimeiseen päivään ja taistelua Jumalan retkellä? Eivät ne ole samanarvoisia Jumalan silmissä, eikä Jumala johda väärintekijöitä.');
INSERT INTO "finnish" VALUES(9,20,'9:20','Ne, jotka uskovat ja lähtevät kodeistaan liikkeelle ja panevat alttiiksi omaisuutensa ja elämänsä Jumalan asian puolesta, saavuttavat korkeamman asteen Jumalan luona; nämä ovat voitollisia.');
INSERT INTO "finnish" VALUES(9,21,'9:21','Heidän Herransa julistaa heille ilosanoman laupeudestaan, mielisuosiostaan ja huvipuutarhoista, joissa he tulevat olemaan ikuisesti autuaita,');
INSERT INTO "finnish" VALUES(9,22,'9:22','jääden sinne ainiaaksi, Jumalan luona on totisesti suuri palkinto.');
INSERT INTO "finnish" VALUES(9,23,'9:23','Te, jotka uskotte, älkää ottako isiänne ja veljiänne ystäviksenne, jos he rakastavat epäuskoa enemmän kuin uskoa, sillä se teistä, joka heihin liittyy, kuuluu väärintekijöihin.');
INSERT INTO "finnish" VALUES(9,24,'9:24','Sano: »Jos isänne, poikanne, veljenne, vaimonne, omaisenne, hankkimanne omaisuus ja kauppa, jonka pysähtymistä pelkäätte, sekä asunnot, joihin olette mieltyneet, ovat teille rakkaampia kuin Jumala ja Hänen lähettiläänsä ja taistelu Hänen asiansa puolesta, niin odottakaa, kunnes Jumala tulee ja sanoo sanansa. Sillä Jumala ei pidä uppiniskaisista.»');
INSERT INTO "finnish" VALUES(9,25,'9:25','Totisesti Jumala on auttanut teitä jo monella taistelukentällä ja Honain päivänä, kun ylpeilitte monilukuisuudestanne, joka kuitenkaan ei auttanut teitä vähääkään; ja maa tuli teille ahtaaksi laajuudestaan huolimatta; ja te käännyitte pakoon.');
INSERT INTO "finnish" VALUES(9,26,'9:26','Silloin lähetti Jumala tyyneytensä lähettiläälle ja oikeauskoisille, Hän lähetti sotajoukkoja, joita te ette nähneet, ja rankaisi uskottomat. Tämä on uskottomien palkka.');
INSERT INTO "finnish" VALUES(9,27,'9:27','Sen jälkeen armahtaa Jumala kenet tahtoo, sillä Jumala on anteeksiantava, armollinen.');
INSERT INTO "finnish" VALUES(9,28,'9:28','Te, jotka uskotte, katsokaa, miten monijumalaiset ovat kokonaan saastaisia. Siksi älkää salliko heidän lähestyä pyhää temppeliä tämän sopimusvuoden jälkeen. Ja, jos sentähden pelkäätte köyhtyvänne, on Jumala runsaudessaan ajoissa huolehtiva teistä, jos Hän tahtoo; Jumala on totisesti tietävä, viisas.');
INSERT INTO "finnish" VALUES(9,29,'9:29','Taistelkaa niitä vastaan, jotka, vaikka ovat saaneet Kirjoituksen, eivät usko Jumalaan eivätkä viimeiseen päivään, eivät pyhitä sitä, minkä Jumala ja Hänen lähettiläänsä ovat selittäneet pyhäksi, eivätkä tunnusta totista uskontoa, taistelkaa, kunnes he auliisti maksavat veronsa ja tunnustavat alistuvansa.');
INSERT INTO "finnish" VALUES(9,30,'9:30','Juutalaiset sanovat: »Esra on Jumalan poika», ja kristityt sanovat: »Messias on Jumalan poika». Tätä he hokevat suullaan. He jäljittelevät niitä, jotka ennen olivat uskottomia. Jumalan ynseys heitä rasittakoon! Kuinka ovatkaan he etääntyneet!');
INSERT INTO "finnish" VALUES(9,31,'9:31','Paitsi Jumalaa ovat he ottaneet herroikseen kirjanoppineensa ja munkkinsa, samaten Messiaan, Marian pojan, vaikka heidän tuli palvella vain yhtä ainoata Jumalaa. Ei ole muuta Jumalaa kuin Hän. Ylistettäköön Häntä ja olkoon Hän kaukana siitä, minkä he asettavat hänen rinnalleen!');
INSERT INTO "finnish" VALUES(9,32,'9:32','Mielellään he puhaltaisivat sammuksiin Jumalan valon, mutta Jumala ei hellitä ennenkuin on saattanut valonsa täydelliseksi, vaikka uskottomat närkästyisivätkin.');
INSERT INTO "finnish" VALUES(9,33,'9:33','Juuri Hän on lähettänyt sanansa julistajan ja tämän mukana johdatuksensa sekä totuuden uskonnon, asettaakseen sen jok''ainoan muun uskonnon yli, vaikka se Jumalattomia närkästyttää.');
INSERT INTO "finnish" VALUES(9,34,'9:34','Te, jotka uskotte, katsokaa! Monet kirjanoppineet ja munkit kuluttavat ihmisten omaisuutta vääryyttä tehden sekä kääntävät muita Jumalan tieltä. Niille, jotka haalivat kultaa ja hopeaa eivätkä käytä sitä Jumalan asian hyväksi, julista tuskallista tuomiota');
INSERT INTO "finnish" VALUES(9,35,'9:35','sinä päivänä, jolloin nämä aineet hehkuvat helvetin tulessa ja niillä heidän otsaansa, kylkiinsä ja selkäänsä painetaan polttomerkki: »Tämän te olette hankkineet itsellenne. Maistakaa nyt, mitä olette koonneet!»');
INSERT INTO "finnish" VALUES(9,36,'9:36','Katso, kuukausien luku Jumalan luona on kaksitoista, niinkuin Jumala määräsi sinä päivänä, jolloin Hän loi taivaat ja maan. Neljä niistä on pyhitetty: tämä on oikea uskonto. Siksi älkää turmelko itseänne niiden aikana, ja taistelkaa pakanoita vastaan, kuten he taistelevat teitä kaikkia vastaan. Ja tietäkää, että Jumala on niiden kanssa, jotka hartautta harjoittavat.');
INSERT INTO "finnish" VALUES(9,37,'9:37','Pyhitetyn kuukauden lykkääminen on vain epäuskon päivien venyttämistä; tämän kautta uskottomat joutuvat harhaan. He selittävät sen luvalliseksi toisena vuonna, ja toisena taas julistavat pyhitetyksi, saadakseen riittävän luvun kuukausia, jotka Jumala on määrännyt ja pyhittänyt; näin sallivat he sen, minkä Jumala on kieltänyt. He kaunistelevat pahat tekonsa, mutta Jumala ei anna johdatustaan uskottomille ihmisille.');
INSERT INTO "finnish" VALUES(9,38,'9:38','Te, jotka uskotte! Miksi te niin kiinteästi maahan juututte, kun teitä käsketään lähtemään Jumalan retkelle? Oletteko enemmän mieltyneitä tämän maailman elämään kuin tulevan? Tämän maailman mukavuus on vain vähäpätöistä verrattuna tulevaan.');
INSERT INTO "finnish" VALUES(9,39,'9:39','Jos ette lähde Jumalan retkelle, Hän on langettava teille tuskallisen tuomion ja valitseva sijaanne toisen kansan, ettekä te kykene vahingoittamaan Häntä vähääkään. Sillä Jumala pystyy tekemään kaikki halunsa mukaan.');
INSERT INTO "finnish" VALUES(9,40,'9:40','Jos te ette auta häntä (Muhammedia), niin on Jumala kuitenkin jo antanut hänelle apunsa, kun uskottomat karkoittivat hänet erään toisen kanssa ja nämä kaksi olivat luolassa ja hän sanoi toverilleen: »Alä murehdi, onhan Jumala totisesti kanssamme!» Silloin Jumala vuodatti tyvenen rauhansa hänen mieleensä ja vahvisti häntä näkymättömin sotajoukoin, ja Hän teki uskottomain sanan mitättömäksi; mutta Jumalan sana pysyy ylinnä; Jumala on mahtava, viisas.');
INSERT INTO "finnish" VALUES(9,41,'9:41','Lähtekää retkelle, kevyesti ja myös raskaasti aseistetut, ja pankaa alttiiksi varallisuutenne ja henkenne Jumalan asian hyväksi! Tämä on parasta teille, jospa sen vain tietäisitte.');
INSERT INTO "finnish" VALUES(9,42,'9:42','Jos olisi ollut tarjolla kyllin helppo saalis ja lyhyt retki, he olisivat varmaan seuranneet sinua, mutta retki näytti heistä liian pitkälti. He vannovat vielä Jumalan nimeen: »Jos olisimme kyenneet, olisimme varmasti lähteneet kanssasi.» He turmelevat sielunsa, ja Jumala tietää, että he ovat todella valehtelijoita.');
INSERT INTO "finnish" VALUES(9,43,'9:43','Jumala antakoon sinulle anteeksi! Miksi annoit heille luvan jäädä jo ennenkuin sinulle selvisi, ketkä puhuivat totta, ja opit tuntemaan valehtelijat?');
INSERT INTO "finnish" VALUES(9,44,'9:44','Jotka uskovat Jumalaan ja viimeiseen päivään, eivät pyydä sinulta lupaa olla panematta alttiiksi omaisuuttansa ja henkeänsä. Jumala tuntee ne, jotka hartaasti palvelevat Häntä.');
INSERT INTO "finnish" VALUES(9,45,'9:45','Ainoastaan ne pyytävät sinulta lupaa, jotka eivät usko Jumalaan eivätkä viimeiseen päivään ja joiden sydämet ovat täynnä epäilyä; epäilyksessään horjuvat he sinne tänne.');
INSERT INTO "finnish" VALUES(9,46,'9:46','Jos he olisivat halunneet lähteä retkelle, he olisivat varmasti ryhtyneet varusteluihin, mutta Jumalalle oli vastenmielinen heidän lähtönsä ja Hän teki heidät hitaiksi, ja heille sanottiin: »Jääkää niiden luokse, jotka paikoillaan pysyvät!»');
INSERT INTO "finnish" VALUES(9,47,'9:47','Jos he olisivat lähteneet teidän kanssanne, he eivät olisi muuta lisänneet kuin huolianne ja kohentaneet keskuudessanne eripuraisuutta, ja joukossanne olisi ollut heitä kuuntelevia. Jumala tuntee väärintekijät.');
INSERT INTO "finnish" VALUES(9,48,'9:48','Yrittiväthän he jo aikaisemmin kiihoittaa riitoihin ja hautoivat juonia sinua vastaan, kunnes totuus tuli ilmi ja Jumalan määräykset astuivat voimaan, vaikka he olivat sitä vastaan.');
INSERT INTO "finnish" VALUES(9,49,'9:49','Heidän joukossaan on eräs, joka sanoo: »Salli minun jäädä pois, äläkä kiusaa minua!» Eivätkö juuri he ole jo langenneet kiusaukseen? Totisesti helvetti on nielevä uskottomat.');
INSERT INTO "finnish" VALUES(9,50,'9:50','Jos sinulle käy hyvin, se vihastuttaa heitä, ja jos sinulle sattuu vastoinkäyminen, sanovat he: »Me katsoimme eteemme jo ennakolta», ja he kääntävät selkänsä ja iloitsevat.');
INSERT INTO "finnish" VALUES(9,51,'9:51','Sano: »Meille ei tapahdu mitään muuta, kuin minkä Jumala on meille säätänyt. Hän on meidän suojelijamme. Häneen uskovaiset luottakoot!»');
INSERT INTO "finnish" VALUES(9,52,'9:52','Sano: »Ette te odota meille muuta, kuin jompaakumpaa kahdesta parhaimmasta asiasta.» Ja me odotamme, että Jumala on teille lähettävä kärsimyksen joko suoraan tai meidän käsiemme kautta. Antakaa siis ajan näyttää; kyllä me odotamme niinkuin tekin!');
INSERT INTO "finnish" VALUES(9,53,'9:53','Sano: »Uhratkaa omastanne tahtoen tai tahtomattanne, ei sitä teiltä milloinkaan oteta vastaan. Totisesti te olette jumalatonta väkeä.»');
INSERT INTO "finnish" VALUES(9,54,'9:54','Ei kuitenkaan ole muuta, mikä estää ottamasta vastaan heidän osuuttansa kuin se, että he eivät usko Jumalaan ja hänen lähettilääseensä, ovat hitaita rukoilemaan ja suorittavat osuutensa vastahakoisesti.');
INSERT INTO "finnish" VALUES(9,55,'9:55','Älä siis ihmettele heidän rikkauksiaan äläkä heidän lapsiaan. Jumala tahtoo vain rangaista heitä maallisella elämällä ja antaa heidän henkensä lähteä heidän ollessaan uskottomia.');
INSERT INTO "finnish" VALUES(9,56,'9:56','He vannovat Jumalan nimeen kuuluvansa teihin, mutta he eivät kuulu teihin, vaan ovat ihmisiä, jotka osoittautuvat pelkureiksi.');
INSERT INTO "finnish" VALUES(9,57,'9:57','Jos he vain olisivat löytäneet jonkun suojan tai luolan tai piilopaikan, he olisivat kääntyneet pakoon ja rientäneet sinne mitä suurimmalla kiireellä.');
INSERT INTO "finnish" VALUES(9,58,'9:58','Ja heistä ovat lähtöisin ne, jotka moittivat sinua almujen takia. Jos heille niitä annat, he ovat tyytyväisiä, mutta ellet heille niitä anna, niin he tuota pikaa närkästyvät.');
INSERT INTO "finnish" VALUES(9,59,'9:59','Jospa he tyytyisivät siihen, mitä Jumala ja Hänen lähettiläänsä ovat heille antaneet, ja sanoisivat: »Jumalassa on meille yltäkyllin, Jumala antaa meille pian hyvyydestään, kuten Hänen lähettiläänsäkin. Jumalaanhan me luotamme.»');
INSERT INTO "finnish" VALUES(9,60,'9:60','Almut ovat vain köyhiä ja puutteessa olevia varten, sekä niille, jotka toimivat hyväntekeväisyyden palveluksessa; myös niitä varten, joiden sielut on voitettava (Jumalan) rakkauteen; edelleen orjien ja velkaorjien lunnaiksi; avustukseksi Jumalan retkelle ja niinikään maankulkijalle. Tämä on Jumalan säätämä velvollisuus, sillä Jumala on kaikkitietävä, viisas.');
INSERT INTO "finnish" VALUES(9,61,'9:61','Heidän joukossaan on niitä, jotka panettelevat profeettaa ja sanovat: »Hän on herkkäuskoinen». Sano: »Se hän on teidän parhaaksenne, hän uskoo Jumalaan ja luottaa oikeauskoisiin. Hän armahtaa niitä teistä, jotka uskovat.» Mutta niitä, jotka panettelevat Jumalan lähettilästä, kohtaa tuskallinen rangaistus.');
INSERT INTO "finnish" VALUES(9,62,'9:62','Teidän edessänne he vannovat Jumalan nimeen teidät tyydyttääkseen, mutta parempi olisi, että he tyydyttäisivät Jumalaa ja Hänen lähettilästänsä olemalla uskovaisia.');
INSERT INTO "finnish" VALUES(9,63,'9:63','Eivätkö he tiedä, että ken tahansa vastustaa Jumalaa ja Hänen lähettilästään, sitä totisesti odottaa tuli ja helvetti, jonne hän jää ainaiseksi. Tämä on suuri häpeä.');
INSERT INTO "finnish" VALUES(9,64,'9:64','Ulkokullatut pelkäävät, että heitä vastaan julkaistaan suura, joka paljastaa heidän sydämensä. Sano: »Pilkatkaa vain, kyllä Jumala on toteuttava sen, mitä te pelkäätte!»');
INSERT INTO "finnish" VALUES(9,65,'9:65','Jos heiltä kyselet, he varmaan vastaavat: »Me vain juttelimme ja laskimme leikkiä». Sano: »Jumalastako ja Hänen tunnusmerkeistään sekä lähettiläästäänkö te teitte pilkkaa?»');
INSERT INTO "finnish" VALUES(9,66,'9:66','Älkää puolustelko itseänne. Olettehan vajonneet epäuskoon senjälkeen kuin olitte uskossa. Jos me annammekin anteeksi osalle teistä, niin rankaisemme toista osaa, koska he ovat rikollisia.');
INSERT INTO "finnish" VALUES(9,67,'9:67','Ulkokullatut, sekä miehet että naiset, ovat kaikki samankaltaisia. He rohkaisevat tekemään pahaa ja ehkäisevät hyvän tekemisen sekä kieltävät köyhältä almun. He eivät välittäneet Jumalasta, ja siksi Hän ei välitä heistä. Totisesti ulkokullatut ovat tottelemattomia.');
INSERT INTO "finnish" VALUES(9,68,'9:68','Jumala on varannut ulkokullatuille, sekä miehille että naisille, samoin kuin uskottomille helvetin tulen olinpaikaksi. Siinä on heille kylliksi. Jumalan tuomio on heidän päällään, ja heille kuuluu ikuinen tuska,');
INSERT INTO "finnish" VALUES(9,69,'9:69','niinkuin toisille ennen teitä, jotka olivat väkevämmät kuin te ja joilla oli enemmän rikkautta ja lapsia. Aikanaan he nauttivat osastaan; niin tekin nautitte omasta osastanne hetken, kuten ne, jotka ennen teitä nauttivat. Ja te puhutte turhia niinkuin hekin puhuivat. Sellaisia ovat ne, joiden työt ovat menneet hukkaan tässä maailmassa ja tulevassa. Sellaisia ovat ne, jotka häviöön joutuvat.');
INSERT INTO "finnish" VALUES(9,70,'9:70','Eikö kertomus heidän edeltäjistään ole tullut heidän tietoonsa - Nooan ja Aadin ja Thamuudin kansasta, Aabrahamin kansasta, Midianin asukkaista ja tuhoutuneista kaupungeista? Heille lähetetyt profeetat tulivat heidän luokseen tuoden selvät todistukset. Sillä Jumala ei tehnyt vääryyttä heille, vaan he itse tekivät itselleen vääryyttä.');
INSERT INTO "finnish" VALUES(9,71,'9:71','Uskovaiset, miehet ja naiset, ovat toinen toistensa liittolaisia; he rohkaisevat hyvän tekemiseen ja kieltävät tekemästä pahaa; ja he harjoittavat hartautta sekä antavat säädettyjä almuja, ja he tottelevat Jumalaa ja Hänen lähettilästään. He ovat niitä, joille Jumala osoittaa laupeukensa. Katso, Jumala on väkevä, viisas.');
INSERT INTO "finnish" VALUES(9,72,'9:72','Jumala on varannut uskovaisille, miehille ja naisille, puutarhoja, joiden liepeillä purot solisevat ja joissa he iäti saavat oleskellä - Eedenin puutarhojen siunattuina asukkaina. Ja suurin kaikesta on Jumalan mielisuosio; se on korkein saavutus.');
INSERT INTO "finnish" VALUES(9,73,'9:73','Oi, profeetta, taistele uskottomia ja ulkokullattuja vastaan, ole ankara heitä kohtaan. Heidän viimeinen asuntonsa on oleva helvetti, onneton määränpää.');
INSERT INTO "finnish" VALUES(9,74,'9:74','He vannovat Jumalan nimeen, etteivät ole mitään väärää puhuneet. Kuitenkin he todella puhuivat epäuskon sanoja ja olivat uskottomia omaksuttuaan Islaamin. He suunnittelivat hankkivansa sellaista, mitä eivät kyenneet saavuttamaan; eivätkä he muuta tavoitetta löytäneet, paitsi että Jumala sekä Hänen lähettiläänsä heitä armostaan rikastuttaisivat. Parempi heille olisi, jos he kääntyisivät, mutta jos he luopuvat, niin Jumala langettaa heille tuskallisen tuomion tässä maailmassa ja tulevassa, eikä heillä ole suojelijaa eikä auttajaa tämän maan päällä.');
INSERT INTO "finnish" VALUES(9,75,'9:75','Heidän joukossaan on niitä, jotka ovat tehneet liiton Jumalan kanssa: »Jos Hän antaa meille suosiotaan, niin me totisesti harjoitamme armeliaisuutta ja hyviä tekoja.»');
INSERT INTO "finnish" VALUES(9,76,'9:76','Mutta kun Hän antoi heille suosiotaan, he tulivat itariksi ja kääntyivät penseästi pois.');
INSERT INTO "finnish" VALUES(9,77,'9:77','Siksi antaa Hän ulkokultaisuuden pysyä heidän sydämissään siihen päivään saakka, jolloin he kohtaavat Hänet, koska he pettivät Jumalaa siinä, mitä Hänelle olivat luvanneet, ja koska he olivat valehtelijoita.');
INSERT INTO "finnish" VALUES(9,78,'9:78','Eivätkö he tiedä, että Jumala tuntee heidän salaisuutensa ja vehkeilynsä ja että Jumala tietää kaikki salaiset asiat?');
INSERT INTO "finnish" VALUES(9,79,'9:79','Jotkut ylenkatsovat sellaisia uskovaisia (muslimeja), jotka voittaakseen Jumalan armon antavat almuja, ja sellaisia, jotka eivät voi muuta antaa kuin ruumiinsa työn, näitä he ivailevat; Jumala on ivaileva heitä itseään. Heitä odottaa tuskallinen tuomio.');
INSERT INTO "finnish" VALUES(9,80,'9:80','Pyydä heille anteeksiantoa tai ole pyytämättä; vaikka sinä seitsemänkymmentä kertaa pyytäisit heille anteeksiantoa, ei Jumala heille anteeksi anna. Sillä he eivät uskoneet Jumalaan ja Hänen lähettilääseensä, ja Jumala ei ohjaa uskottomia.');
INSERT INTO "finnish" VALUES(9,81,'9:81','(Retkeltä) pois jääneet istuivat ja iloitsivat Jumalan lähettilään poissa ollessa eivätkä tahtoneet panna alttiiksi omaisuuttaan ja henkeään Jumalan asian puolesta. Ja he sanoivat: »Alkää lähtekö tällaisessa helteessä!» Sano: »Helvetin helle on vielä hehkuvampi.» Jospa he sen vain käsittäisivät.');
INSERT INTO "finnish" VALUES(9,82,'9:82','Naurakoot he hieman: ansaittuna palkkanaan he saavat vielä paljon itkeä.');
INSERT INTO "finnish" VALUES(9,83,'9:83','Jos Jumala sallii sinun palata heidän joukkoonsa, ja he pyytävät sinulta lupaa lähteä retkelle, sano heille silloin: »Ette milloinkaan enää saa lähteä minun mukanani ettekä taistella minun kanssani vihollista vastaan. Tyydyittehän ensimmäisellä kerralla paikoillanne istumiseen. Istukaa siis edelleenkin kotiinjääneiden kanssa.»');
INSERT INTO "finnish" VALUES(9,84,'9:84','Älä milloinkaan rukoile heikäläisen puolesta, joka kuolee, äläkä pysähdy hänen hautansa ääreen. Totisesti he eivät uskoneet Jumalaan eivätkä Hänen sanansaattajaansa, ja he kuolivat, koska he olivat tottelemattomia.');
INSERT INTO "finnish" VALUES(9,85,'9:85','Älä ihaile heidän rikkauksiaan äläkä heidän lapsiaan. Jumala tahtoo vain niillä rangaista heitä tässä maailmassa, ja että heidän henkensä lähtisi heidän yhä ollessaan uskottomia.');
INSERT INTO "finnish" VALUES(9,86,'9:86','Kun maan päälle lähetettiin näin kuuluva suura: »Uskokaa Jumalaan ja taistelkaa Hänen sanansaattajansa rinnalla», niin rikkaat heidän joukossaan yhä pyytävät sinulta lupaa jäädä pois ja sanovat: »Salli meidän jäädä niiden luokse, jotka pysyvät alallaan.»');
INSERT INTO "finnish" VALUES(9,87,'9:87','He ovat tyytyväisiä saadessaan jäädä kotona olevien pariin, ja heidän sydämensä ovat sinetöidyt, niin että he eivät mitään käsitä.');
INSERT INTO "finnish" VALUES(9,88,'9:88','Mutta lähettiläs, ja ne, jotka hänen kanssaan uskovat, panevat alttiiksi omaisuutensa ja henkensä. Sellaisille ihmisille on käyvä hyvin, ja he ovat onnellisia.');
INSERT INTO "finnish" VALUES(9,89,'9:89','Jumala on valmistanut heille puutarhat, joiden liepeillä purot solisevat ja joissa he alati saavat asua. Tämä on ylin onni.');
INSERT INTO "finnish" VALUES(9,90,'9:90','Eräät beduiineista keksivät syitä saadakseen luvan jäädä pois, ja ne, jotka kielsivät Jumalan ja Hänen lähettiläänsä, pysyivät kotona. Hän lähettää pian tuskallisen tuomion niille, jotka olivat uskottomia.');
INSERT INTO "finnish" VALUES(9,91,'9:91','Niitä ei ole soimattava kotiinjäämisestä), jotka ovat heikkoja tai sairaita tai joilla ei ole mitä uhrata, jos he vain ovat vilpittömiä Jumalaa ja Hänen lähettilästään kohtaan. Myöskään hyvää tekeviä kohtaan ei ole moitetta. Jumala on anteeksiantavainen, armoliinen.');
INSERT INTO "finnish" VALUES(9,92,'9:92','Ei myöskään niitä soimata, jotka tulivat luoksesi pyytäen sinua huolehtimaan heidän varusteistaan ja joille sinä vastasit: »Minulla ei ole, millä teidät retkelle varustaisin.» He kääntyivät pois silmät kyynelissä, suruissaan siitä, että eivät voineet hankkia tarvittavia varoja.');
INSERT INTO "finnish" VALUES(9,93,'9:93','Moitetta on vain niitä kohtaan, jotka pyytävät sinulta lupaa jäädä kotiin, vaikka ovat rikkaita. He ovat tyytyväisiä jäädessään kotona pysyvien seuraan. Jumala on sinetöinyt heidän sydämensä niin, että he ovat tietoa vailla.');
INSERT INTO "finnish" VALUES(9,94,'9:94','He puolustelevat itseään teidän edessänne, kun te palaatte heidän luokseen. Sanokaa: »Alkää puolustelko itseänne, me emme kuitenkaan teitä usko. Jumala on jo meille ilmoittanut teidän salaiset tarkoituksenne; Hän on oivaltava teidän tekonne, ja samoin Hänen lähettiläänsä. Silloin te joudutte Hänen luokseen, joka tietää näkymättömät yhtä hyvin kuin näkyväisetkin asiat, ja Hän on osoittava teille teidän tekonne.');
INSERT INTO "finnish" VALUES(9,95,'9:95','He vannottavat teitä Jumalan nimeen, kun te palaatte heidän luokseen, olemaan heille kaunaa kantamatta. Senvuoksi jättäkää heidät rauhaan. Totisesti he ovat saastaisia, ja heidän olinpaikkansa on oleva helvetti heidän tekojensa palkkana.');
INSERT INTO "finnish" VALUES(9,96,'9:96','He vannottavat teitä olemaan heihin tyytyväisiä, mutta vaikka te olisittekin, niin Jumala ei totisesti voi olla tyytyväinen tottelemattomiin ihmisiin.');
INSERT INTO "finnish" VALUES(9,97,'9:97','Beduiinit ovat piintyneimpiä epäuskoon ja ulkokultaisuuteen eivätkä ole taipuvaisia tuntemaan niitä lakeja, jotka Jumala on julistanut lähettiläänsä kautta. Jumala on kaikkitietävä, viisas.');
INSERT INTO "finnish" VALUES(9,98,'9:98','Muutamat beduiinit pitävät antiosuuksiaan sakkona ja odottavat onnen kääntyvän teitä vastaan. Heille itselleen on käyvä huonosti. Jumala kuulee ja tietää kaiken.');
INSERT INTO "finnish" VALUES(9,99,'9:99','Mutta muutamat beduiinit uskovat Jumalaan ja viimeiseen päivään ja pitävät osuuksiaan keinona päästä lähemmäksi Jumalaa ja saada Hänen lähettiläänsä siunaukset. Katsokaa, totisesti he tällä tavoin pääsevät lähemmäksi. Jumala on sulkeva heidät armoonsa. Totisesti, Jumala on anteeksiantavainen, armollinen.');
INSERT INTO "finnish" VALUES(9,100,'9:100','Niihin, jotka ensimmäisinä ja etumaisina jättivät kotinsa, sekä avustajiin ja niihin, jotka seurasivat heitä hyvän teossa, heihin kaikkiin Jumala on tyytyväinen ja he ovat tyytyväiset Häneen. Hän on valmistanut heille puutarhat, joissa purot solisevat ja joissa he saavat alati asua. Tämä on ylin onni.');
INSERT INTO "finnish" VALUES(9,101,'9:101','Beduiinien joukossa, jotka asuvat teidän ympärillänne, on ulkokultaisia, samoin myös kaupungin asukkaiden keskuudessa. He pysyvät ulkokultaisuudessaan. Te ette tunne heitä, mutta me heidät tunnemme. Me saatamme heidät kahdesti kärsimään, ja senjälkeen heitä kohtaa tuskallinen tuomio.');
INSERT INTO "finnish" VALUES(9,102,'9:102','On myös toisia, jotka tunnustavat syntinsä. He sekoittavat teon, joka on hyvä, toiseen, joka on paha. Jumala saattaa kääntää tämän heidän hyväkseen. Totisesti, Jumala on anteeksiantavainen, armollinen.');
INSERT INTO "finnish" VALUES(9,103,'9:103','Ota vastaan uhrilahjoja heidän varoistaan, että voisit puhdistaa heidät ja samalla vanhurskauttaa, ja siunaa heitä; totisesti sinun siunauksesi on heille onneksi. Jumala kuulee ja tietää kaiken.');
INSERT INTO "finnish" VALUES(9,104,'9:104','Eivätkö he tiedä, että Jumala ottaa vastaan palvelijainsa katumuksen ja uhrilahjat? Ja että Jumala on heltyväinen, armollinen?');
INSERT INTO "finnish" VALUES(9,105,'9:105','Sano: »Toimikaa! Jumala on näkevä tekonne, Hänen lähettiläänsä ja uskovaiset näkevät samoin. Ja teidät viedään pian Hänen luokseen, joka tietää näkymättömät ja näkyvät, ja Hän osoittaa teille silloin teidän tekonne.»');
INSERT INTO "finnish" VALUES(9,106,'9:106','On toisia, jotka odottavat Jumalan käskyä siitä riippumatta, saattaako Hän heidät kärsimään vai heltyykö Hän heille; niin, Jumala on kaikkitietävä, viisas.');
INSERT INTO "finnish" VALUES(9,107,'9:107','Ja on niitä, jotka ovat rakentaneet temppelin saadakseen aikaan pahennusta ja epäuskoa ja epäsopua uskovaisten keskuudessa, väijytyspaikan niille, jotka ennen ovat taistelleet Jumalaa ja Hänen lähettilästään vastaan. Ja totisesti he tulevat vannomaan: »Me tarkoitimme vain hyvää.» Mutta Jumala todistaa, että he totisesti ovat valehtelijoita.');
INSERT INTO "finnish" VALUES(9,108,'9:108','Älä milloinkaan esiinny siinä temppelissä. Temppeli, joka ensimmäisestä päivästä alkaen perustettiin Jumalan kunnioitukselle, ansaitsee totisesti paremmin, että sinä siinä esiinnyt. Siinä on miehiä, jotka halajavat tulla puhdistetuiksi, ja Jumala rakastaa puhtaita.');
INSERT INTO "finnish" VALUES(9,109,'9:109','Kumpi on parempi, sekö, joka on laskenut perustuksensa Jumalaan ja Hänen mielisuosioonsa, vai sekö, joka on laskenut perustuksensa luhistuvalle partaalle, niin että se hänen kanssaan sortuu helvetin tuleen? Jumala ei ohjaa väärämielisiä ihmisiä.');
INSERT INTO "finnish" VALUES(9,110,'9:110','Ja rakennus, jonka he ovat rakentaneet, ei ole heidän sydämiensä epäilyä poistava ennenkuin Jumala murtaa heidän sydämensä, sillä Jumala on kaikkitietävä, viisas.');
INSERT INTO "finnish" VALUES(9,111,'9:111','Totisesti, Jumala on lunastanut uskovaisilta heidän henkensä ja omaisuutensa antamalla niiden korvaukseksi paratiisin puutarhan, joka on heitä varten. He taistelevat Jumalan asian puolesta: he surmaavat ja heitä surmataan, kuten Hän on sitovasti luvannut toorassa, evankeliumissa ja Koraanissa, ja kuka täyttää uskollisemmin lupauksensa kuin Jumala? Olkaa siis onnelliset liittosopimuksestanne, jonka olette tehneet Hänen kanssaan. Juuri tämä on ylin onni.');
INSERT INTO "finnish" VALUES(9,112,'9:112','Niille, jotka kääntyvät Jumalan puoleen, niille, jotka palvelevat Häntä, niille, jotka ylistävät Häntä, niille, jotka parantavat, niille, jotka kumartuvat rukoilemaan, niille, jotka vaipuvat maahan, niille, jotka käskevät tehdä hyvää ja kieltävät pahan tekemisen, ja niille, jotka suojelevat Jumalan lakeja - julista uskoville iloista sanomaa.');
INSERT INTO "finnish" VALUES(9,113,'9:113','On hyödytöntä profeetan ja uskovaisten rukoilla pakanoitten puolesta, vaikkapa he olisivat sukulaisiakin, senjälkeen kuin näille on ilmoitettu, että he ovat helvetin omia.');
INSERT INTO "finnish" VALUES(9,114,'9:114','Kun Aabraham pyysi isälleen anteeksiantoa, se tapahtui lupauksen takia, jonka oli hänelle antanut. Mutta kun hänelle oli ilmoitettu, että hänen isänsä oli Jumalan vihollinen, Aabraham lähti pois hänen luotaan. Totisesti, Aabraham oli hurskas ja lempeä.');
INSERT INTO "finnish" VALUES(9,115,'9:115','Jumala ei tahdo saattaa ihmisiä kadotukseen sen jälkeen, kun Hän on heitä ohjannut,ennenkuin Hän tekee heille selväksi, mitä heidän on varottava. Totisesti, Jumala tietää kaikki.');
INSERT INTO "finnish" VALUES(9,116,'9:116','Totisesti, Jumala on olemassa. Hänelle kuuluvat taivasten ja maan valtakunnat, Hän antaa elämän ja Hän antaa kuoleman, ja paitsi Jumalaa teillä ei ole muuta suojelijaa eikä auttajaa.');
INSERT INTO "finnish" VALUES(9,117,'9:117','Jumala on totisesti suosioliinen profeettaa ja niitä kohtaan, jotka jättivät kotinsa, sekä auttajia kohtaan, jotka seurasivat häntä ahdistuksen hetkenä, senjälkeen kuin muutamien sydämet olivat luopumaisillaan hänestä. Silloin Hän lähestyi heitä, sillä Hän on heitä kohtaan rakastavainen, armollinen.');
INSERT INTO "finnish" VALUES(9,118,'9:118','Samoin niitä kolmea henkilöä kohtaan, jotka nämä jättivät taaksensa; jopa siinä määrin, että kun maa suuruudestaan huolimatta tuntui heistä ahtaalta ja heidän sielunsa olivat tuskaiset ja heille selvisi, ettei ollut muualla pakopaikkaa Jumalalta kuin Hänen Itsensä luona, silloin Hän lähestyi heitä, että he olisivat kääntyneet Hänen puoleensa. Totisesti Jumala on heltyväinen, armollisin.');
INSERT INTO "finnish" VALUES(9,119,'9:119','Te, jotka uskotte, kunnioittakaa Jumalaa ja olkaa totuutta rakastavien seurassa.');
INSERT INTO "finnish" VALUES(9,120,'9:120','Ei ole soveliasta Medinan asukkaille ja niille beduiineille, jotka asuvat ympäristössä, jäädä pois Jumalan lähettilään retkeltä ja huolehtia enemmän omasta kuin Hänen hengestään. Eivät he kärsi janoa, eivät tuskaa eivätkä nälkää Jumalan retkellä; eivät he samoa sillä tavoin minkään maan halki, että se aiheuttaisi murhetta uskottomille, eivät he liioin hanki mitään sotasaalista viholliselta merkitsemättä sitä itselleen hyväksi teoksi. Totisesti, Jumalan huostassa eivät joudu hukkaan niiden ansiot, jotka hyvää tekevät.');
INSERT INTO "finnish" VALUES(9,121,'9:121','Ei näiden tarvitse kantaa mitään kuluja, ei suuria eikä pieniä, eikä samota yli yhdenkään tasangon, ilman ettei sitä luettaisi heidän hyväkseen eikä Jumala palkitsisi heille sitä, mitä he parastansa ovat tehneet.');
INSERT INTO "finnish" VALUES(9,122,'9:122','Ei ole mahdollista kaikkien uskovaisten lähteä yhdessä retkelle; mutta miksipä ei osa jokaisesta heimosta lähtisi liikkeelle, avartuakseen uskossa ja voidakseen kotiuduttuaan varoittaa kansaansa, jotta se olisi valveilla.');
INSERT INTO "finnish" VALUES(9,123,'9:123','Te, jotka uskotte, taistelkaa niitä uskottomia vastaan, jotka ovat lähellänne, jotta he kokisivat teidän voimanne, ja tietäkää, että Jumala on niiden kanssa, jotka hartautta harjoittavat.');
INSERT INTO "finnish" VALUES(9,124,'9:124','Kun suura lähetetään ylhäältä, niin jotkut heistä sanovat: »Kenelle teistä se on lisännyt uskoa?» Mitä uskovaisiin tulee, se on todella heidän uskoaan enentänyt, ja he ovat onnellisia.');
INSERT INTO "finnish" VALUES(9,125,'9:125','Mutta niissä, joiden sydämiä tauti tärvelee, se toden totta lisää saastaisuutta saastaisuuden päälle, ja he kuolevat ollen yhä epäuskossa.');
INSERT INTO "finnish" VALUES(9,126,'9:126','Eivätkö he huomaa, että heitä koetellaan kerran tai kahdesti vuodessa? Sittenkään he eivät tee kääntymystä eivätkä mitään mieleensä paina.');
INSERT INTO "finnish" VALUES(9,127,'9:127','Ja kun suura lähetetään ylhäältä, jotkut heistä tuijottavat toisiin ja kysyvät: »Havaitsetteko jonkun katseet päällänne?» Sitten he siirtyvät muuanne. Jumala on kääntänyt pois heidän sydämensä, koska he ovat ihmisiä, jotka eivät ymmärrä.');
INSERT INTO "finnish" VALUES(9,128,'9:128','Totisesti, teidän luoksenne on tullut lähettiläs omasta keskuudestanne. Häneen koskee, jos teillä on murhetta, hän huolehtii teidän hyvinvoinnistanne ja hän on rakastavainen, armollinen uskovaisia kohtaan.');
INSERT INTO "finnish" VALUES(9,129,'9:129','Jos he kääntyvät takaisin, sano silloin: »Jumala on minun kaikkeni, ei ole muuta jumaluutta kuin Hän. Häneen asetan luottamukseni. Hän on Suurivaltaisin Herra.»');
INSERT INTO "finnish" VALUES(10,1,'10:1','Alif, Laam, Raa . Nämä ovat viisauden Kirjan säkeistöjä.');
INSERT INTO "finnish" VALUES(10,2,'10:2','Pitävätkö ihmiset kummana, että Me olemme antanut käskyvaltamme eräälle heistä ja sanonut: »Varoita ihmiskuntaa ja julista ilosanomaa niille, jotka uskovat, että heillä on varma paikka Herransa luona.» Uskottomat sanovat: »Aivan varmasti tämä mies on ilmeinen velho.»');
INSERT INTO "finnish" VALUES(10,3,'10:3','Totisesti teidän Herranne on Jumala. Hän, joka loi taivaat ja maan kuudessa jaksossa ja on senjälkeen pysyttänyt valtansa lujana ja ohjaa hallitustaan. Ei ole ketään toista valtiasta (hänen rinnallaan) paitsi mikäli Hän suvaitsee. Tämä on Jumala, teidän Herranne, palvelkaa siis Häntä. Ettekö sitä muistaisi?');
INSERT INTO "finnish" VALUES(10,4,'10:4','Hänen luokseen on teidän kaikkien palattava. Jumalan lupaus on varma. Totisesti, Hän luo olennot ja antaa sitten niiden palata takaisin, jotta Hän voisi palkita ne, jotka uskovat ja oikeamielisesti tekevät hyvää. Mutta niitä varten, jotka ovat antautuneet epäuskoon, on varattu kiehuva juoma ja tuskallinen tuomio, koska he ovat olleet uskottomia.');
INSERT INTO "finnish" VALUES(10,5,'10:5','Juuri Hän saattaa auringon paistamaan ja kuun loistamaan ja on määrännyt sille asentonsa, niin että te voitte tietää vuosien kulun ja ajanlaskun. Kaiken tämän Hän on tehnyt totuudessa. Hän selittää merkkinsä ihmisille, joilla on ymmärrystä.');
INSERT INTO "finnish" VALUES(10,6,'10:6','Yön ja päivän vuorottelussa ja kaikessa, minkä Jumala on luonut taivaassa ja maan päällä, näkyvät selvät merkit niille, jotka harjoittavat hurskautta.');
INSERT INTO "finnish" VALUES(10,7,'10:7','Totisesti on niitäkin, jotka eivät odota Meidän tapaamistamme, vaan tyytyvät tämän maailman elämään ja ovat siitä löytäneet viihtymyksensä; ja on niitä, jotka eivät välitä Meidän merkeistämme.');
INSERT INTO "finnish" VALUES(10,8,'10:8','Sellaisten olinpaikkana on oleva tuli palkaksi heidän teoistaan.');
INSERT INTO "finnish" VALUES(10,9,'10:9','Mutta niitä, jotka uskovat ja suorittavat hyviä tekoja, heidän Jumalansa ohjaa heidän uskonsa kautta. Purot solisevat heidän jalkojensa juuressa autuaissa puutarhoissa.');
INSERT INTO "finnish" VALUES(10,10,'10:10','Siellä kuuluu heidän rukouksensa: »Kunnia olkoon Sinulle, oi Jumala!» Ja heidän tervehdyksensä siellä on oleva: »Rauha!» Ja heidän rukoustensa loppu on oleva: »Ylistys kuuluu Jumalalle, kaikkien maailmojen Herralle.»');
INSERT INTO "finnish" VALUES(10,11,'10:11','Jos Jumala edistäisi ihmisille pahaa yhtä kärkkäästi, kuin he tavoittelevat hyvää, niin heidän loppunsa olisi todella jo määrätty, mutta Me jätämme ne, jotka eivät usko kohtaavansa Meitä, yksinään ja sokeina vaeltamaan sokeudessaan.');
INSERT INTO "finnish" VALUES(10,12,'10:12','Ja kun ihminen joutuu murheeseen, hän huutaa Meitä joko maahan vaipuneena tai istuen tai nousten seisaalleen, mutta kun olemme poistanut hänen vaivansa, hän lähtee tiehensä, aivan kuin ei olisi ikinä huutanutkaan Meitä luokseen vaivan kourissa. Tällä tavoin väärintekijät kaunistelevat tekonsa.');
INSERT INTO "finnish" VALUES(10,13,'10:13','Me olemme hävittänyt jo ennen teitä eläneitä sukupolvia, kun he tekivät väärin. Heidän aikansa profeetat tulivat heidän luokseen, mukanaan selvät todistukset, mutta he eivät ottaneet uskoakseen. Tällä tavoin Me palkitsemme niitä, jotka ovat syntisiä.');
INSERT INTO "finnish" VALUES(10,14,'10:14','Senjälkeen Me teimme teistä heidän seuraajiaan maan päälle nähdäksemme, kuinka te toimitte.');
INSERT INTO "finnish" VALUES(10,15,'10:15','Ja kun Meidän selvät merkkimme on heille ilmoitettu, sanovat ne, jotka eivät usko kohtaavansa Meitä: »Tuokaa toinen Koraani tai muuttakaa tätä.» Sano: »Ei ole minun asiani muuttaa sitä omasta halustani, noudatan vain minulle annettua ilmoitusta. Pelkään totisesti, että jos olen tottelematon Herralleni, minua on kohtaava viimeisen päivän tuomio.»');
INSERT INTO "finnish" VALUES(10,16,'10:16','Sano: »Jos Jumala olisi tahtonut, minä en olisi sitä teille ilmoittanut enkä tehnyt tunnetuksi; olinhan jo sitä ennen elänyt keskuudessanne suuren osan elämääni. Ettekö jo ymmärrä?»');
INSERT INTO "finnish" VALUES(10,17,'10:17','Kuka tekee enemmän väärin kuin se, joka puhuu valhetta Jumalaa vastaan tai vääristelee Hänen merkkinsä? Totisesti, syntisellä ei ole menestystä.');
INSERT INTO "finnish" VALUES(10,18,'10:18','Jumalan sijasta he palvelevat sellaista, mikä ei heitä vahingoita eikä hyödytä, ja he sanovat: »Nämä ovat meidän välittäjiämme Jumalan luona.» Sano: »Ilmoitatteko Jumalalle jotakin uutta, mitä Hän ei tiedä taivaassa ja maan päällä?» Jumalalle olkoon kunnia! Kaukana Hän on siitä, minkä he asettavat Hänen rinnalleen.');
INSERT INTO "finnish" VALUES(10,19,'10:19','Koko ihmiskunta oli yksi ainoa yhteisö, mutta he tulivat erimielisiksi, ja ellei Herran sana olisi edeltä käynyt, olisi asia, josta he kiistelivät, ratkaistu välien selvityksellä.');
INSERT INTO "finnish" VALUES(10,20,'10:20','He sanovat: »Miksi hän ei ole saanut merkkiä Herraltaan?» Sano silloin: »Näkymätön kuuluu yksin Jumalalle. Siksi odottakaa, minä olen odottava kanssanne.»');
INSERT INTO "finnish" VALUES(10,21,'10:21','Kun olemme antanut ihmisten maistaa armoa heitä kohdanneen iskun jälkeen, niin katso, he alkavat vehkeillä Meidän merkkejämme vastaan. Sano: »Jumala on nopein suunnitelmain teossa.» Totisesti, lähettiläämme kirjoittavat muistiin heidän vehkeilynsä.');
INSERT INTO "finnish" VALUES(10,22,'10:22','Juuri Hän antaa teidän matkustaa maitse ja meritse. Mutta kun te olette laivoissa, ja laivat matkustajineen lähtevät purjehtimaan suotuisassa tuulessa ja he siitä iloitsevat, silloin hirmumyrsky yllättää heidät, aallot vyöryvät kaikilta tahoilta heitä kohti ja he luulevat olevansa tuhon omat. Silloin he huutavat Jumalaa ehdottomasti uskoen Häneen: »Jos Sinä pelastat meidät tästä, niin me totisesti olemme kiitollisia.»');
INSERT INTO "finnish" VALUES(10,23,'10:23','Mutta kun Hän heidät pelastaa, niin katso, maihin päästyään he hillittömästi rehentelevät. Voi teitä, ihmiset! Teidän pöyhkeilynne on vain tuhoksi itsellenne. Lyhyen aikaa saatte nauttia maallisesta elämästä, senjälkeen te palaatte Meidän luoksemme, ja Me osoitamme teille, mitä olette tehneet.');
INSERT INTO "finnish" VALUES(10,24,'10:24','Tämän maailman elämä on kuin vesi, jonka lähetämme ylhäältä maan päälle, niin että se imeytyy maan kasvullisuuteen, josta ihmiset ja karja saavat ravintonsa. Kun maa pukeutuu kultaisiin pukimiinsa ja koristeisiinsa ja sen omistajat luulevat olevansa kaiken tämän herroja, silloin tulee Meidän käskymme, yöllä tahi päivällä, ja Me niitämme kaiken maan tasalle, aivan kuin äsken ei mitään olisi ollutkaan. Tällä tavoin Me selitämme merkit ihmisille, jotka ajattelevat.');
INSERT INTO "finnish" VALUES(10,25,'10:25','Jumala kutsuu teidät rauhan asuntoon ja ohjaa, kenet Hän tahtoo, oikealle tielle.');
INSERT INTO "finnish" VALUES(10,26,'10:26','Jotka hyvää tekevät, heillä on hyvääkin parempi osa tallella: heidän kasvojaan ei varjosta synkkyys eikä häpeä. He ovat paratiisin perillisiä ja saavat siellä ikuisesti asua.');
INSERT INTO "finnish" VALUES(10,27,'10:27','Mutta jotka pahaa tekevät, heidän palkkansa on oleva yhtä paha, ja häpeä peittää heidät. Heillä ei ole ketään suojelijat Jumalaa vastaan; ikäänkuin öistä pimeyttä leviää heidän kasvoilleen. He ovat tulen omia, ja siinä he saavat ikuisesti pysyä.');
INSERT INTO "finnish" VALUES(10,28,'10:28','Ja sinä päivänä, jolloin Me kokoamme heidät kaikki yhteen, sanomme niille, jotka ovat asettaneet muita jumalia Jumalan rinnalle: »Asettukaa alallenne, te ja teidän epäjumalanne!» Senjälkeen saatamme heidät riitoihin keskenään, ja heidän epäjumalansa sanovat: »Ette te meitä palvelleet.');
INSERT INTO "finnish" VALUES(10,29,'10:29','Jumala on pätevä todistaja teidän ja meidän välillämme, että me emme edes tietäneet teidän palvelemisestanne.»');
INSERT INTO "finnish" VALUES(10,30,'10:30','Siellä jokainen sielu saa kokea, mitä on valmistanut, ja heidät viedään kaikki Jumalan, heidän todellisen Herransa eteen, ja heidän väärät jumalansa katoavat heiltä.');
INSERT INTO "finnish" VALUES(10,31,'10:31','Sano: »Kuka hankkii teille ravinnon taivaasta ja maan päältä? Tai kellä on kuulo ja näkö vallassaan? Ja kuka tekee kuolleista eläviä ja elävistä kuolleita? Kuka harjoittaa käskyvaltaansa?» He vastaavat silloin: »Jumala.» Sano sinä: »Miksi ette siis palvelisi Häntä?»');
INSERT INTO "finnish" VALUES(10,32,'10:32','Hän on teidän todellinen Herranne, ja mitä jää teille, hylättyänne totuuden, muuta kuin eksymys? Sentähden, mistä olettekaan luopuneet pois?');
INSERT INTO "finnish" VALUES(10,33,'10:33','Näin on Herrasi sana lausunut oikean tuomion tottelemattomista: »Totisesti, he eivät usko.»');
INSERT INTO "finnish" VALUES(10,34,'10:34','Sano: »Kuka teidän epäjumalistanne pystyy saamaan aikaan luomakunnan ja sitten palauttamaan sen alkulähteeseensä?» Sano: »Jumala alkuunpanee luomakunnan ja sitten kutsuu sen palaamaan takaisin. Kuinka te siis olette ymmärtämättömiä?»');
INSERT INTO "finnish" VALUES(10,35,'10:35','Sano: »Kuka teidän epäjumalistanne voi ohjata totuuteen?» Sano: »Jumala ohjaa totuuteen.» Kenellä siis on parempi oikeus saada seuraajia, Hänelläkö, joka ohjaa totuuteen, vaiko hänellä, joka ei voi ketään ohjata; jota ohjataan itseään? Miten on siis teidän laitanne? Miten te tuomitsette?');
INSERT INTO "finnish" VALUES(10,36,'10:36','Useimmat heistä seuraavat vain kuvitelmiaan, mutta totisesti kuvittelut eivät kelpaa mihinkään, kun totuudesta on kysymys. Jumala tietää totisesti, mitä he tekevät.');
INSERT INTO "finnish" VALUES(10,37,'10:37','Ja tätä Koraania ei olisi yksikään muu kyennyt aikaansaamaan paitsi Jumala. Se on vahvistus sille, mikä ilmaistiin jo sitä ennen, ja kirjoituksen selitys, joka totisesti on kaikkien maailmojen Herralta saatu.');
INSERT INTO "finnish" VALUES(10,38,'10:38','Väittävätkö he, että hän (Muhammed) on sepittänyt sen? Sano silloin: »Tuokaa sitten suura, joka on tämän kaltainen, ja huutakaa avuksenne ketä voitte Jumalan asemesta, jos olette oikeassa.»');
INSERT INTO "finnish" VALUES(10,39,'10:39','Ei, he selittävät valheeksi sen, mitä he eivät ymmärrä eivätkä tunne ja mitä heille ei ole selitetty. Samalla tavoin vääristelivät totuutta ennen heitä eläneet, mutta katso, millainen oli väärämielisten loppu.');
INSERT INTO "finnish" VALUES(10,40,'10:40','Jokunen heistä tosin siihen (Koraaniin) uskoo, ja jokunen heistä ei siihen usko; mutta Herrasi tuntee parhaiten ne, jotka pahaa tekevät.');
INSERT INTO "finnish" VALUES(10,41,'10:41','Jos he pitävät sinua valehtelijana, sano heille: »Minun tekoni ovat minun, ja teidän tekonne ovat teidän; te ette ole vastuussa minun teoistani, ja minua eivät sido teidän tekonne.»');
INSERT INTO "finnish" VALUES(10,42,'10:42','Jotkut heistä kuuntelevat sinua; kykenetkö saamaan kuurot kuuleviksi, vaikkapa he eivät ymmärtäisikään?');
INSERT INTO "finnish" VALUES(10,43,'10:43','Jokunen heistä katsoo sinuun; kykenetkö ohjaamaan sokeita, vaikkapa heillä ei olisi valoa?');
INSERT INTO "finnish" VALUES(10,44,'10:44','Totisesti, Jumala ei tee vähäisintäkään vääryyttä ihmissuvulle, mutta ihmiset tekevät vääryyttä itselleen.');
INSERT INTO "finnish" VALUES(10,45,'10:45','Ja sinä päivänä, jolloin Hän kokoaa heidät yhteen, ja on kuin heillä olisi aikaa vain tunnin verran, he tuntevat toisensa. Totisesti, niiden on häviö, jotka väittivät valheeksi Jumalan tulemisen ja joita ei ohjattu oikealle tielle.');
INSERT INTO "finnish" VALUES(10,46,'10:46','Ja joko sitten Me näytämme sinulle jotakin siitä, minkä olemme heille varannut, tai annamme sinulle sitä ennen täyden palkkasi, joka tapauksessa he palaavat luoksemme. Ja silloin Jumala on heidän tekojensa todistaja.');
INSERT INTO "finnish" VALUES(10,47,'10:47','Jokaista kansaa varten on lähettiläs, ja kun heidän lähettiläänsä saapuu, ratkaistaan heidän asiansa oikeudenmukaisesti eikä heille tehdä vääryyttä.');
INSERT INTO "finnish" VALUES(10,48,'10:48','Ja he sanovat: »Milloin tämä lupaus täytetään, sanokaa se, jos olette luotettavia?»');
INSERT INTO "finnish" VALUES(10,49,'10:49','Sano: »Minulla ei ole valtaa vahingoittaa eikä hyödyttää itseäni, paitsi miten Jumala suvaitsee.» Jokaisella kansalla on määräaikansa. Kun heidän aikansa tulee, he eivät voi siirtää sitä tuntiakaan tuonnemmaksi eivätkä liioin jouduttaa sen saapumista.');
INSERT INTO "finnish" VALUES(10,50,'10:50','Sano: »Jos Jumalan rangaistus yllättäisi teidät yöllä tai päivällä, niin soisivatko syntiset sen itselleen nopeammin ehtivän?');
INSERT INTO "finnish" VALUES(10,51,'10:51','Vai uskotteko siihen vasta silloin, kun se teidät saavuttaa?» Sano: »Nyt te uskotte, vaikka jo olitte halunneet sen tuloa jouduttaa.»');
INSERT INTO "finnish" VALUES(10,52,'10:52','Silloin sanotaan väärämielisille: »Maistakaa ikuista tuskaa; teidät palkitaan vain sen mukaan, mitä olette ansainneet.»');
INSERT INTO "finnish" VALUES(10,53,'10:53','Ja he pyytävät sinulta vahvistusta: »Onko se totta?» Sano: »On, Herran nimessä, se on täyttä totta; ja te ette voi paeta.»');
INSERT INTO "finnish" VALUES(10,54,'10:54','Jos mikä hyvänsä sielu, joka on tehnyt vääryyttä, omistaisi kaiken, mitä maan päällä on, hän tarjoaisi sen varmasti lunnaiksi puolestaan, ja kun he näkevät tuomion, he ilmaisevat katumuksensa. Heidän asiansa ratkaistaan totuuden mukaan, eikä heille vääryyttä tehdä.');
INSERT INTO "finnish" VALUES(10,55,'10:55','Totisesti, eikö kaikki, mikä on taivaassa ja maan päällä, kuulu Jumalalle? Totisesti, eikö Jumalan lupaus pysy? Mutta useimmat heistä ovat tietoa vailla.');
INSERT INTO "finnish" VALUES(10,56,'10:56','Hän antaa elämän, ja Hän antaa kuoleman, ja Hänen luokseen olette kaikki luodut palaamaan.');
INSERT INTO "finnish" VALUES(10,57,'10:57','Oi ihmiset! Totisesti, olette saaneet Herraltanne kehoituksen ja parannuksen rintanne vaivoihin sekä johdatuksen ja laupeuden uskovaisille.');
INSERT INTO "finnish" VALUES(10,58,'10:58','Sano: »Jumalan mielisuosiosta ja Hänen armostaan.» Tästä ihmiset iloitkoot. Tämä on parempi kuin kaikki, mitä he ovat ahnehtineet.');
INSERT INTO "finnish" VALUES(10,59,'10:59','Sano: »Oletteko ajatelleet, mitä Jumala on teille lähettänyt ravinnoksi? Toisia aineita te pidätte lain sallimina, toisia kiellettyinä.» Sano: »Onko Jumala antanut teille luvan tehdä näin, vai sepitättekö valhetta Jumalaa vastaan?»');
INSERT INTO "finnish" VALUES(10,60,'10:60','Ja jotka sepittävät valhetta Jumalaa vastaan, millaiseksi kuvittelevat he ylösnousemuksen päivän? Totisesti, Jumala on armon Herra ihmisiä kohtaan, mutta useimmat heistä eivät ole kiitollisia.');
INSERT INTO "finnish" VALUES(10,61,'10:61','Ja sinä (oi Muhammed!) et toimi missään asioissa etkä lue Jumalan puolesta julki osaakaan Koraanista, ettekä te ihmiset suorita tekoakaan Meidän pitämättä teistä vaaria sitä tehdessänne. Ei edes pienintä hiukkasta maan päällä tai avaruudessa voida kätkeä sinun Herrasi katseilta, eikä ole olemassa sitä pienempää eikä suurempaa, jota ei olisi selvässä (Jumalan tiedon) Kirjassa.');
INSERT INTO "finnish" VALUES(10,62,'10:62','Totisesti niillä, jotka ovat lähellä Jumalaa, ei ole mitään pelkoa, eikä heidän tarvitse murehtia.');
INSERT INTO "finnish" VALUES(10,63,'10:63','Jotka uskovat Jumalaan ja harjoittavat hartautta,');
INSERT INTO "finnish" VALUES(10,64,'10:64','heille kuuluvat ilon sanomat tämän maailman elämässä ja tulevassa. Jumalan sanoja ei voida muuttaa. Tämä on heidän voitonriemunsa.');
INSERT INTO "finnish" VALUES(10,65,'10:65','Älköön heidän puheensa sinua surettako. Totisesti, kunnia kuuluu kokonaan Jumalalle; Hän on kaikkikuuleva, kaikkitietävä.');
INSERT INTO "finnish" VALUES(10,66,'10:66','Totisesti, eivätkö kaikki, taivaassa olevat ja maan päällä olevat, kuulu Jumalalle? Ja ne, jotka huutavat Jumalan asemesta epäjumalia, seuraavat vain kuvitelmiaan eivätkä tee muuta kuin valehtelevat.');
INSERT INTO "finnish" VALUES(10,67,'10:67','Juuri Jumala on luonut yön voidaksemme silloin levätä ja päivän valon lähteeksi. Totisesti, tässä on selvät merkit niille. jotka tarkkaavat.');
INSERT INTO "finnish" VALUES(10,68,'10:68','He sanovat: »Jumala on hankkinut itselleen lapsen.» Hänelle olkoon kunnia. Hän on riippumaton. Hänelle kuuluu kaikki, mikä on taivaassa ja maan päällä. Teillä ei ole mitään valtuutta sanoa näin: vai puhutteko Jumalasta sellaista, mitä ette tiedä?');
INSERT INTO "finnish" VALUES(10,69,'10:69','Sano: »Totisesti, ne jotka sepittävät valhetta Jumalaa vastaan, eivät menesty.»');
INSERT INTO "finnish" VALUES(10,70,'10:70','Heidän nautintonsa tässä maailmassa on ajallista, ja sitten heidän on palattava takaisin Meidän luoksemme. Silloin Me annamme heidän maistaa ankaraa tuskaa epäuskonsa takia.');
INSERT INTO "finnish" VALUES(10,71,'10:71','Esitä heille kertomus Nooasta, kun hän sanoi kansalleen: »Oi kansani! Jos minun esiintymiseni ja muistutukseni Jumalan merkeistä ovat teille raskaat, koska minä luotan Jumalaan, niin toimikaa silloin yhdessä ja huutakaa epäjumalianne, älkääkä epäröikö hankkeessanne, vaan pankaa se täytäntöön minua vastaan viivyttelemättä!');
INSERT INTO "finnish" VALUES(10,72,'10:72','Jos te sitten käännytte takaisin, en vaadi mitään hyvitystä. Minun hyvitykseni on yksin Jumalan käsissä, onhan minun sallittu kuulua oikeauskoisiin (muslimeihin).»');
INSERT INTO "finnish" VALUES(10,73,'10:73','Mutta he valehtelivat häntä vastaan, ja silloin Me pelastimme hänet ja hänen kanssaan arkissa olleet. Me teimme heistä suvun jatkajat ja hukutimme ne, jotka selittivät Meidän merkkimme valheeksi. Katso siis, millainen oli niiden loppu, joita oli varoitettu.');
INSERT INTO "finnish" VALUES(10,74,'10:74','Sittemmin nostimme Me hänen jälkeensä profeettoja kansojensa keskuuteen, ja he toivat näille selvät todistukset, mutta eivät nämä ottaneet uskoakseen siihen, mitä olivat aikaisemmin kutsuneet valheeksi. Tällä tavoin Me sinetöimme niiden sydämet, jotka syntiä tekevät.');
INSERT INTO "finnish" VALUES(10,75,'10:75','Heidän jälkeensä annoimme Me Mooseksen ja Aaronin mennä Faraon ja hänen päällikköjensä luo mukanaan Meidän merkkimme, mutta he suhtautuivat niihin ylimielisesti ollen syntisiä ihmisiä.');
INSERT INTO "finnish" VALUES(10,76,'10:76','Niinpä kun Me lähetimme totuuden heidän luokseen, he sanoivat: »Aivan varmasti tämä on ilmeistä taikuutta.»');
INSERT INTO "finnish" VALUES(10,77,'10:77','Mooses sanoi: »Näinkö puhutte totuudesta, kun se saavuttaa teidät? Onko tämä taikuutta? Taikurit eivät milloinkaan menesty.»');
INSERT INTO "finnish" VALUES(10,78,'10:78','He sanoivat: »Oletko tullut luoksemme käännyttääksesi meidät pois siitä, mihin olemme nähneet esi~isiemme turvautuneen, ja saattaaksesi maan valtiuden teidän kahden käsiin? Me emme aio teihin uskoa.»');
INSERT INTO "finnish" VALUES(10,79,'10:79','Ja Farao sanoi: »Tuokaa eteeni jokainoa taitava noita!»');
INSERT INTO "finnish" VALUES(10,80,'10:80','Ja kun taikurit olivat saapuneet, Mooses sanoi heille: »Heittäkää maahan, mitä aiotte heittää!»');
INSERT INTO "finnish" VALUES(10,81,'10:81','Kun he olivat niin tehneet, Mooses sanoi: »Se, mitä olette saaneet aikaan, on noituutta. Totisesti, Jumala on tekevä sen mitättömäksi. Totisesti, Jumala ei edistä pahuudentekijäin toimia.»');
INSERT INTO "finnish" VALUES(10,82,'10:82','Jumala on vahvistava totuuden sanainsa voimalla, vastoin syntisten tahtoakin.');
INSERT INTO "finnish" VALUES(10,83,'10:83','Mutta paitsi eräitä hänen kansansa nuoria eivät ketkään uskoneet Moosekseen, koska he pelkäsivät Faraon ja hänen johtomiestensä vainoavan heitä. Farao oli todellakin itsevaltias maassa ja hän kuului niihin, jotka toimivat hillittömästi.');
INSERT INTO "finnish" VALUES(10,84,'10:84','Mooses sanoi: »Oi kansani, jos uskotte Jumalaan, luottakaa Häneen, mikäli olette oikeauskoisia.»');
INSERT INTO "finnish" VALUES(10,85,'10:85','Ja niin he sanoivat: »Jumalaan me luotamme. Oi Herramme, älä salli tämän väärämielisen kansan meitä vainota!');
INSERT INTO "finnish" VALUES(10,86,'10:86','Pelasta meidät armossasi tästä uskottomasta kansasta!»');
INSERT INTO "finnish" VALUES(10,87,'10:87','Me annoimme Moosekselle ja hänen veljelleen tämän ilmoituksen: »Te kaksi, varustakaa kansallenne taloja Egyptissä, tehkää niistä oikeita rukoushuoneita ja ylläpitäkää hartauden harjoittamista sekä julistakaa iloista sanomaa oikeauskoisille!»');
INSERT INTO "finnish" VALUES(10,88,'10:88','Mooses sanoi: »Herra! Totisesti olet suonut Faraolle ja hänen johtomiehilleen hyvinvointia ja rikkauksia tämän maailman elämässä. Herra, he saavat kansan luopumaan Sinun tieltäsi. Herra, pyyhi pois heidän rikkautensa ja koveta heidän sydämensä, niin että he eivät usko, ennen kuin näkevät tuskallisen tuomionsa! »');
INSERT INTO "finnish" VALUES(10,89,'10:89','Hän sanoi: »Sinun rukouksesi on jo kuultu. Pysykää te kaksi siis lujina älkääkä seuratko niiden menoa, jotka ovat tietoa vailla.»');
INSERT INTO "finnish" VALUES(10,90,'10:90','Me annoimme Israelin lasten kulkea meren poikki, ja silloin Farao sotajoukkoineen seurasi heitä sorron- ja vallanhaluisena, kunnes hän joutui veden valtaan ja sanoi: »Minä uskon, ettei ole olemassa muuta jumaluutta kuin Hän yksin, johon Israelin lapset uskovat. Minä myös tunnustaudun oikeauskoiseksi.»');
INSERT INTO "finnish" VALUES(10,91,'10:91','»Nyt kylläkin! Mutta ennen olet ollut tottelematon ja kuulunut väärintekijöihin.');
INSERT INTO "finnish" VALUES(10,92,'10:92','Siksi Me tahdomme tänä päivänä pelastaa pinnalle ruumiisi, jotta olisit muistomerkkinä sille, joka on sinun seuraajasi.» Mutta useimmat ihmiset eivät välitä Meidän merkeistämme.');
INSERT INTO "finnish" VALUES(10,93,'10:93','Me todella järjestimme Israelin lapsille hyvät asuinsijat ja varustimme heidät hyvällä ravinnolla, eivätkä he olleet eripuraisia ennen kuin paisuivat tiedossaan. Totisesti, Herrasi on ylösnousemuksen päivänä ratkaiseva sen, mistä he kiistelivät.');
INSERT INTO "finnish" VALUES(10,94,'10:94','Jos sinä epäilet sitä, minkä olemme sinulle ylhäältä lähettänyt, niin kysy niiltä, jotka ennen sinua lukivat Kirjaa. Totuus tuli sinulle Herraltasi; sentähden älä kuulu niihin, jotka kiistelevät.');
INSERT INTO "finnish" VALUES(10,95,'10:95','Äläkä kuulu niihin, jotka selittävät valheeksi Jumalan merkit, ettet joutuisi kadotettujen joukkoon.');
INSERT INTO "finnish" VALUES(10,96,'10:96','Totisesti, ne, joita vastaan Herran sana on tullut sitovaksi, eivät usko,');
INSERT INTO "finnish" VALUES(10,97,'10:97','vaikkapa kaikkinaisia merkkejä heille annettaisiin, ennenkuin he näkevät tuskallisen tuomionsa.');
INSERT INTO "finnish" VALUES(10,98,'10:98','Minkä tähden ei yksikään kaupunki, lukuunottamatta Joonaan kansaa, uskonut ajoissa, niin että sen uskosta olisi koitunut jotakin hyötyä? Kun he uskoivat, niin Me päästimme heidät häpeällisen tuskan alta tässä maallisessa elämässä ja annoimme heidän nauttia jonkin aikaa.');
INSERT INTO "finnish" VALUES(10,99,'10:99','Jos Herrasi olisi niin tahtonut, niin kaikki, jotka maan päällä ovat, olisivat yhdessä uskoneet. Tahdotko sinä pakottaa ihmisiä tulemaan oikeauskoisiksi?');
INSERT INTO "finnish" VALUES(10,100,'10:100','Ei yksikään sielu voi uskoa muuten kuin Jumalan tieten. Ja Hän langettaa rangaistuksen niille, jotka ovat ymmärtämättömiä.');
INSERT INTO "finnish" VALUES(10,101,'10:101','Sano: »Katso mitä on taivaassa ja maan päällä!» Mutta merkit ja varoitukset eivät auta vähääkään ihmisiä, jotka eivät usko.');
INSERT INTO "finnish" VALUES(10,102,'10:102','He odottavat ainoastaan sellaista, mitä tapahtui heidän edeltäjiensä päivinä. Sano: »Odottakaa siis! Totisesti minä olen odottava teidän rinnallanne.»');
INSERT INTO "finnish" VALUES(10,103,'10:103','Silloin Me pelastamme lähettiläämme ja ne, jotka uskovat. Meidän velvollisuutemme on pelastaa uskovaiset.');
INSERT INTO "finnish" VALUES(10,104,'10:104','Sano: »Oi ihmiset! jos epäilette minun uskoani, niin tietäkää, että minä en palvele niitä, joita te palvelette Jumalan vertaisina, vaan minä palvelen Jumalaa, joka on antava teille täyden palkkanne, ja minut on määrätty kuulumaan niihin, jotka uskovat.»');
INSERT INTO "finnish" VALUES(10,105,'10:105','Sinut on myös määrätty elämään totisessa uskossa, mieleltäsi vilpittömänä. Äläkä kuulu pakanoitten joukkoon.');
INSERT INTO "finnish" VALUES(10,106,'10:106','Älä huuda avuksesi Jumalan vertaisena sellaista, mikä ei sinua hyödytä eikä vahingoita, sillä jos näin teet, olet kuuluva väärämielisten joukkoon.');
INSERT INTO "finnish" VALUES(10,107,'10:107','Jos Jumala lyö sinua jollakin vaivalla, ei kukaan muu paitsi Hän voi sitä pois ottaa. Ja jos Hän tarkoittaa sinulle jotakin hyvää, ei kukaan voi Hänen armoaan pois kääntää. Hän antaa sen kohdata palvelijoistaan ketä Hän suvaitsee. Sillä Hänellä on kaikki anteeksiantamus ja kaikki armo.');
INSERT INTO "finnish" VALUES(10,108,'10:108','Sano: »Oi ihmiset! Totisesti, Herranne on lähettänyt teille totuuden. Ketä tahansa ohjataankin, on se johdatusta hänen oman sielunsa hyväksi, mutta kuka ikinä menee kadotukseen, hän hukkuu vastoin omaa parastaan. En minä ole teidän vartijanne.»');
INSERT INTO "finnish" VALUES(10,109,'10:109','Seuraa sitä, mihin olet määrätty, ja pysy lujana kunnes Jumala on tuomitseva, sillä Hän on tuomareista paras.');
INSERT INTO "finnish" VALUES(11,1,'11:1','Alif. Laam. Raa. Tämä on kirja, jonka säkeiden sisältö on vahvistettu, sillä ne on selittänyt Hän, joka on viisas, tietävä;');
INSERT INTO "finnish" VALUES(11,2,'11:2','jotta ette palvelisi ketään muuta kuin Jumalaa. Totisesti, minä olen varoittaja ja ilosanoman tuoja teille Hänen luotaan.');
INSERT INTO "finnish" VALUES(11,3,'11:3','Anokaa Herraltanne anteeksiantoa, kääntykää sitten Hänen puoleensa, niin Hän on hankkiva teille runsaan huolenpidon määräajaksi ja vuodattava armonsa jokaiselle, joka laupeudessa vaeltaa. Mutta jos luovutte pois, silloin totisesti pelkään, että tuomiopäivän rangaistus lankeaa päällenne.');
INSERT INTO "finnish" VALUES(11,4,'11:4','Jumalan luokse on teidän jälleen palattava, ja Hän kykenee tekemään kaiken, mitä tahtoo.');
INSERT INTO "finnish" VALUES(11,5,'11:5','Katso, eivätkö he todella kaksin kerroin peitä rintaansa salatakseen Häneltä vihamielisyytensä. Kavahtakaa, kun he kääriytyvät vaatteisiinsa. Jumala tietää, mitä he salailevat ja mitä ilmaisevat. Totisesti, Hän tietää, mitä heidän rintaansa on kätkettynä.');
INSERT INTO "finnish" VALUES(11,6,'11:6','Ei ole ainoatakaan eläintä tämän maan päällä, jota Jumala ei ruokkisi, ja Hän tietää sen tyyssijan ja kotipaikan. Kaikki tämä on selvillä (Jumalan tiedon) Kirjassa.');
INSERT INTO "finnish" VALUES(11,7,'11:7','Juuri Hän on luonut taivaat ja maan kuutena kautena, ja hänen valtaistuimensa on vesien yllä, niin että hän voi teitä koetella, nähdäkseen kuka teistä on paras teoissa. Ja jos sanot: »Totisesti, teidät herätetään kuolleista», niin ne, jotka ovat antautuneet epäuskon valtaan, vastaavat yhtä varmasti: »Tämähän on ilmeistä noituutta.»');
INSERT INTO "finnish" VALUES(11,8,'11:8','Ja jos Me viivytämme heidän tuomiotaan jonkin aikaa, niin he varmasti ilkkuvat: »Mikähän sitä estelee?» Totisesti sinä päivänä, jolloin se heidät saavuttaa, ei sitä voida torjua. Ja se, jolle he nauroivat, on lankeava heidän päällensä.');
INSERT INTO "finnish" VALUES(11,9,'11:9','Jos Me annamme jonkun ihmisen maistaa armoamme ja sitten otamme sen häneltä pois, totisesti hän silloin käy epätoivoiseksi ja kiittämättömäksi.');
INSERT INTO "finnish" VALUES(11,10,'11:10','Ja jos annamme hänen maistaa siunausta häntä kohdanneen iskun jälkeen, niin hän kerskuu: »Paha lähti loitolle minusta.» Hän käy todella omahyväiseksi, pöyhkeileväksi.');
INSERT INTO "finnish" VALUES(11,11,'11:11','Toisin ne, jotka ovat kärsivällisiä ja hyviä tekoja harjoittavat; he ovat niitä, joille kuuluu anteeksiantamus ja suuri palkinto.');
INSERT INTO "finnish" VALUES(11,12,'11:12','Tahdotko sinä luopua jostakin sinulle määrätystä tehtävästä, ja ahdistaako rintaasi syystä, että he sanovat: »Miksi ei ole aarretta lähetetty hänelle tai miksi ei enkeli tullut hänen kanssaan?» Sinä olet ainoastaan varoittaja, ja Jumala valvoo yli kaiken.');
INSERT INTO "finnish" VALUES(11,13,'11:13','Tai jos he sanovat: »Hän on kaiken keksinyt», vastaa heille: »Tuokaa sitten kymmenen samanlaista sepittämäänne suuraa ja huutakaa avuksenne ketä voitte Jumalan asemesta, jos olette totuudessa.»');
INSERT INTO "finnish" VALUES(11,14,'11:14','Jos he tällöin eivät vastaa sinulle, silloin tiedä, että se (Koraani) totisesti on lähetetty maan päälle Jumalan viisautena ja että muuta jumaluutta ei ole olemassa kuin Hän. Oletteko siis oikeauskoisia (muslimeja)?');
INSERT INTO "finnish" VALUES(11,15,'11:15','Ketkä ikinä tavoittelevat tämän maailman elämää ja sen koreutta, heille Me annamme heidän tekojensa täyden ansion mukaan jo tässä elämässä, eikä mitään siitä heiltä vähennetä.');
INSERT INTO "finnish" VALUES(11,16,'11:16','He ovat ihmisiä, joille tulevassa elämässä ei ole varattuna muuta kuin tuli, jonka piirissä heidän teoillaan ei ole mitään arvoa, ja kaikki, mitä he toimittivat, raukeaa turhaan.');
INSERT INTO "finnish" VALUES(11,17,'11:17','Heidän kaltaisensa ei ole hän, joka nojautuu Herransa selvään todistukseen ja jolle Hänen todistajansa lukee sitä (Koraania); tämän edellä oli jo Mooseksen Kirja johdatuksena ja armona. Tämän (Mooseksen Kirjan) noudattajat uskovat siihenkin (Koraaniin). Ja heimolle, joka ei siihen usko, on tuli varattu asumukseksi. Älköön Sinulla siis olko mitään epäilyksiä tässä asiassa. Totisesti, tämä on Herrasi ilmoitus, mutta useimmat eivät siihen usko.');
INSERT INTO "finnish" VALUES(11,18,'11:18','Ja kuka on nurjamielisempi kuin se, joka puhuu valhetta Jumalaa vastaan? Sellaiset viedään Herransa eteen, ja todistajat lausuvat: »He ovat niitä, jotka puhuivat valhetta Herraansa vastaan.» Eikö ole totta, että Jumalan kirous painaa väärämielisiä?');
INSERT INTO "finnish" VALUES(11,19,'11:19','He pitävät ihmisiä loitolla Jumalan polulta ja yrittävät tehdä sen mutkaiseksi. He ovat juuri niitä, jotka eivät usko tulevaan elämään.');
INSERT INTO "finnish" VALUES(11,20,'11:20','Nämä ihmiset eivät voi paeta minnekään maan päältä, eivätkä he Jumalan sijaan löydä muita auttajia. Kaksinkertainen on oleva heidän rangaistuksensa, koska eivät he ymmärtäneet kuulla eivätkä nähdä.');
INSERT INTO "finnish" VALUES(11,21,'11:21','Nämä kuuluvat niihin, jotka ovat sielunsa kadottaneet, ja heidän keksimänsä harhakuvat häviävät heiltä olemattomiin.');
INSERT INTO "finnish" VALUES(11,22,'11:22','He ovat kiistämättä niitä, jotka kärsivät pahimman häviön tulevassa elämässä.');
INSERT INTO "finnish" VALUES(11,23,'11:23','Mutta ne, jotka uskovat ja tekevät hyvää sekä pysyvät nöyrinä Herransa edessä, ovat paratiisin perijöitä; siellä he saavat asua.');
INSERT INTO "finnish" VALUES(11,24,'11:24','Nämä kahdenlaiset ihmiset ovat verrattavissa toisiinsa niinkuin sokeat ja kuurot näkeviin ja kuuleviin. Voivatko nämä olla toistensa tasolla? Ettekö ymmärrä sitä?');
INSERT INTO "finnish" VALUES(11,25,'11:25','Me lähetimme Nooan hänen kansansa luo, ja hän sanoi: »Minä olen totisesti varoittava teitä selvästi,');
INSERT INTO "finnish" VALUES(11,26,'11:26','ettette palvelisi ketään muuta kuin Jumalaa. Totisesti, minä pelkään sitä tuomiota, joka tuskan päivänä teille langetetaan.»');
INSERT INTO "finnish" VALUES(11,27,'11:27','Silloin hänen kansansa uskottomain johtomiehet sanoivat: »Huomaamme, että olet vain meidän kaltaisemme ihminen; huomaamme myös, että sinua seuraavat vain meistä halvimmat, joilla on pintapuolinen arvostelukyky; ja me näemme, ettet ole meitä etevämpi. Itse asiassa me pidämme sinua valehtelijana.»');
INSERT INTO "finnish" VALUES(11,28,'11:28','Hän sanoi: »Oi kansani, oletteko ottaneet huomioon, että minä saatan nojautua Herraltani tulleisiin selviin todistuksiin? Hän on osoittanut minulle armoaan, mutta se on teidän katseiltanne kätketty. Tulisiko meidän sitten väkisin tyrkyttää teille sitä, kun kerran olette vastahakoisia?');
INSERT INTO "finnish" VALUES(11,29,'11:29','Oi kansani! En myöskään pyydä sen vuoksi teiltä mitään rikkauksia. Minun palkkani on yksin Jumalan huostassa, enkä aio karkoittaa niitä, jotka uskovat. Totisesti, he saavat kohdata Herransa, mutta minä näen, että te olette tietämätöntä kansaa.');
INSERT INTO "finnish" VALUES(11,30,'11:30','Oi kansani! Kuka auttaa minua Jumalaa vastaan, jos ajan heidät pois? Ettekö jo ymmärrä?');
INSERT INTO "finnish" VALUES(11,31,'11:31','Enkä sano teille, että minulla on Jumalan aarteet, en myöskään tiedä näkymättömiä asioita enkä sano olevani enkeli. En liioin sano niistä, jotka teidän silmissänne ovat halveksittavia, ettei Jumala suo heille mitään hyvää. Jumala tietää parhaiten, mitä heillä on sisimmässään; totisesti, jos toisin puhuisin, olisin yksi väärämielisistä.»');
INSERT INTO "finnish" VALUES(11,32,'11:32','He sanoivat: »Oi Nooa! Olet väitellyt kanssamme etkä väsy kanssamme väittelemään, osoita siis, mitä meille lupaat, jos olet totuudellinen.');
INSERT INTO "finnish" VALUES(11,33,'11:33','Hän sanoi: »Jumala yksin on sen osoittava, jos tahtoo, ettekä te voi Häntä väistää.');
INSERT INTO "finnish" VALUES(11,34,'11:34','Eikä minun neuvomiseni teitä hyödytä, vaikkapa miten pyrkisin teitä neuvomaan, jos Jumala tahtoisi eksyttää teidät tieltä. Hän on teidän Herranne, ja hänen luokseen on teidän jälleen palattava.»');
INSERT INTO "finnish" VALUES(11,35,'11:35','Jos he sanovat: »Hän on tuon keksinyt», niin vastaa: »Jos olen sen itse keksinyt, niin se olkoon minun syntini, mutta minä olen viaton teidän tekemiinne synteihin.»');
INSERT INTO "finnish" VALUES(11,36,'11:36','Ja Nooalle ilmoitettiin: »Kukaan kansastasi ei ole uskova lukuunottamatta niitä, jotka jo ennestään ovat uskoneet. Älä ole huolissasi heidän teoistaan.');
INSERT INTO "finnish" VALUES(11,37,'11:37','Rakenna arkki Meidän silmiemme edessä ja Meidän käskystämme, äläkä puhu minulle niistä, jotka väärin tekevät. Totisesti heidät hukutetaan.»');
INSERT INTO "finnish" VALUES(11,38,'11:38','Ja hän rakensi arkin, ja joka kerran, kun hänen kansansa johtomiehet kulkivat hänen ohitseen, he nauroivat hänelle. Hän sanoi: »Jos nauratte meille, niin totisesti me tulemme vielä nauramaan teille, kuten te nyt teette.');
INSERT INTO "finnish" VALUES(11,39,'11:39','Silloin tulette tietämään, kenet yllättää häpeällinen rangaistus ja kenelle langetetaan ikuisen tuskan tuomio.»');
INSERT INTO "finnish" VALUES(11,40,'11:40','Näin oli siihen saakka, kunnes Meidän käskymme annettiin ja tulivuori purkautui, ja Me sanoimme: »Kuljeta arkkiin kaksi jokaista lajia, uros ja naaras, sekä perheesi, paitsi niitä, joista määrättiin jo aikaisemmin, ynnä kaikki uskovaiset.» Ja niitä, jotka uskoivat hänen kanssaan, oli vain jokunen.');
INSERT INTO "finnish" VALUES(11,41,'11:41','Ja Nooa sanoi: »Astukaa arkkiin, ja Jumalan nimessä se purjehtikoon ja määränpäähänsä pysähtyköön. Totisesti, Herrani on anteeksiantavainen, armollinen.»');
INSERT INTO "finnish" VALUES(11,42,'11:42','Ja se purjehti heidän kanssaan keskellä vuorenkorkuisia aaltoja, ja Nooa huusi pojalleen, joka oli rannalla: »Oi poikani! Tule meidän mukaamme, äläkä ole niiden kanssa, jotka eivät usko.»');
INSERT INTO "finnish" VALUES(11,43,'11:43','Hän vastasi: »Lähden turvaan jollekin vuorelle, se suojelee minut vedeltä.» Nooa sanoi: »Tänään ei ole kukaan muu Jumalan käskyltä suojassa kuin se, jolle Hän osoittaa armoaan.» Ja aalto erotti heidät toisistaan, ja hän joutui niiden joukkoon, jotka hukkuivat.');
INSERT INTO "finnish" VALUES(11,44,'11:44','Ja myöhemmin kuului käsky: »Maa, niele vetesi! Taivas, lakkaa satamasta!» Silloin vesi laskeutui ja käsky oli täytetty. Ja arkki pysähtyi al-Dzuudin kohdalla. Ja sana kuului: »Pois väärämieliset ihmiset!»');
INSERT INTO "finnish" VALUES(11,45,'11:45','Nooa huusi Herralleen ja sanoi: »Totisesti, poikani kuuluu perheeseeni, ja Sinun lupauksesi on totta, ja Sinä olet tuomareilta oikeamielisin.»');
INSERT INTO "finnish" VALUES(11,46,'11:46','Hän sanoi: »Oi Nooa! Totisesti, hän on perheesi ulkopuolella; hänen tekonsa ovat pahat; älä siis pyydä Minulta sellaista, mistä sinulla ei ole tietoa. Totisesti, Minä varoitan sinua. ettet joutuisi tietämättömien joukkoon.»');
INSERT INTO "finnish" VALUES(11,47,'11:47','Nooa sanoi: »Herrani, varjele minua pyytämästä Sinulta sellaista, mistä minulla ei ole mitään tietoa. Ja ellet Sinä anna minulle anteeksi ja osoita minulle armoasi, olen kuuluva kadotettujen joukkoon.»');
INSERT INTO "finnish" VALUES(11,48,'11:48','Silloin kuului ääni: »Oi Nooa! Lähde rauhassa luotamme, ja siunattuja olkaa sinä ja ne kansat, jotka syntyvät sinun seurassasi olevista. Ja kansoja on tuleva, joille annamme nautintoja, mutta joita sitten on kohtaava Meidän tuskallinen tuomiomme.»');
INSERT INTO "finnish" VALUES(11,49,'11:49','Tässä on kerrottu muutamista tapahtumista, joita et ole nähnyt, mutta jotka Me sinulle ilmoitamme. Et sinä eikä sinun kansasi niistä ennen tiennyt. Ole siis kärsivällinen! Totisesti, määränpää kuuluu niille, jotka Jumalaa pelkäävät.');
INSERT INTO "finnish" VALUES(11,50,'11:50','Aadin heimon luokse Me lähetimme heidän veljensä Huudin. Tämä sanoi: »Oi kansani, palvelkaa Jumalaa, teillä ei ole muuta jumaluutta kuin Hän; te elätte harhakuvitelmissanne.');
INSERT INTO "finnish" VALUES(11,51,'11:51','Oi kansani! En pyydä siitä teiltä mitään palkkaa; minun palkkani on Jumalan asia, joka on minut luonut. Ettekö jo ymmärrä?');
INSERT INTO "finnish" VALUES(11,52,'11:52','Oi kansani! Pyytäkää anteeksiantoa Herraltanne ja kääntykää sitten Hänen puoleensa. Hän antaa taivaan lähettää teille runsaan sateen ja Hän antaa teille uutta voimaa entisen lisäksi. Älkää enää kääntykö syntiä tekemään.»');
INSERT INTO "finnish" VALUES(11,53,'11:53','He sanoivat: »Oi Huud! Sinä et ole tuonut meille mitään selviä todistuksia, joten emme aio puheittesi perusteella hylätä jumaliamme emmekä voi sinua uskoa.');
INSERT INTO "finnish" VALUES(11,54,'11:54','Emme sano muuta kuin että jotkut jumalistamme ovat saaneet sinut pahuuden valtaan.» Hän sanoi: »Totisesti, minä huudan Jumalan todistajakseni, ja todistakaa tekin, että minulla ei ole mitään osuutta siihen, jonka te asetatte Jumalan rinnalle.');
INSERT INTO "finnish" VALUES(11,55,'11:55','Jättäkää Jumala eroon; saattakaa sitten suunnitelmanne minua vastaan päätökseen viivyttelemättä.');
INSERT INTO "finnish" VALUES(11,56,'11:56','Totisesti, minä luotan Jumalaan, minun ja teidänkin Herraanne. Ei ole yhtäkään olentoa, johon Hän ei voi tarttua tukasta. Totisesti, minun Herrani kulkee oikeata tietä.');
INSERT INTO "finnish" VALUES(11,57,'11:57','Ja jos te sitten käännytte pois, minä olen jo joka tapauksessa ilmoittanut teille sen sanoman, jota tuomaan minut luoksenne lähetettiin. Herrani on asettava teidän seuraajaksenne toisen kansan, ja te ette voi vahingoittaa Häntä vähääkään. Totisesti, Herrani valvoo kaikkea.»');
INSERT INTO "finnish" VALUES(11,58,'11:58','Ja kun Meidän käskymme seurasi, Me armossamme pelastimme Huudin ja ne, jotka hänen laillaan uskoivat; ja Me pelastimme heidät hirvittävästä rangaistuksesta.');
INSERT INTO "finnish" VALUES(11,59,'11:59','He olivat Aadin heimoa, jotka hylkäsivät Herransa merkit ja olivat tottelemattomia Hänen lähettiläilleen, mutta seurasivat jokaisen totuutta vihaavan itsevaltiaan käskyjä.');
INSERT INTO "finnish" VALUES(11,60,'11:60','Heitä on kohtaava kirous tässä maailmassa ja ylösnousemuksen päivänä. Eikö ole totta, että Aad ei tahtonut uskoa Herraansa? Hävitköön Aad, Huudin kansa.');
INSERT INTO "finnish" VALUES(11,61,'11:61','Ja Tamuudin heimon luokse Me lähetimme heidän veljensä Saalihin. Tämä sanoi: »Oi kansani! Palvelkaa Jumalaa; teillä ei ole muita jumaluuksia kuin Hän. Hän loi teidät tästä maasta ja antoi teidän siinä asua. Sentähden pyytäkää anteeksiantamusta Häneltä ja palatkaa sitten Hänen luoksensa. Totisesti, Herrani on lähellä, Hän vastaa teille.»');
INSERT INTO "finnish" VALUES(11,62,'11:62','He sanoivat: »Oi Saalih, sinä olet ollut keskuudessamme tätä ennenkin. Sinuun panimme toivomme; kiellätkö sinä meitä palvelemasta sitä, mitä isämme palvelivat? Ja me toden teolla epäilemme suuresti sitä, mihin meitä kehoitat.»');
INSERT INTO "finnish" VALUES(11,63,'11:63','Hän sanoi: »Oi kansani! Oletteko ajatelleet sitä, että jos minä nojaudun Herrani selvään todistukseen ja Hän on suonut minulle omaa armoaan, kuka silloin auttaisi minua Jumalaa vastaan, jos olisin Hänelle tottelematon? Sentähden te vain saatatte minut turmioon.');
INSERT INTO "finnish" VALUES(11,64,'11:64','Oi kansani, tämä naaraskameli on Jumalan, se on merkkinä teille; antakaa sen käydä Jumalan laitumella älkää tehkö sille mitään vahinkoa, ettei pikainen rangaistus teitä yllättäisi.»');
INSERT INTO "finnish" VALUES(11,65,'11:65','Mutta he katkaisivat sen polvijänteet; silloin Saalih sanoi: »Nauttikaa elämästä kolme päivää sisällä asunnoissanne. Tämä on lupaus, jota ei ole mahdollista väittää vääräksi.»');
INSERT INTO "finnish" VALUES(11,66,'11:66','Sitten, kun Meidän käskymme täyttyi, Me armossamme pelastimme Saalihin ja ne, jotka hänen laillaan uskoivat, tuon päivän häpeästä. Totisesti, sinun Herrasi on kaikkivaltias, kaikkivoipa.');
INSERT INTO "finnish" VALUES(11,67,'11:67','Ja jylisevä melu yllätti ne, jotka olivat väärin tehneet; heistä tuli vainajia, joiden ruumiit viruivat heidän kodeissaan,');
INSERT INTO "finnish" VALUES(11,68,'11:68','ikäänkuin he eivät olisi milloinkaan siellä eläneet. Eikö ole totta, että Tamuud ei uskonut Herraansa? Hävitköön Tamuud!');
INSERT INTO "finnish" VALUES(11,69,'11:69','Ja totisesti, Meidän lähettiläämme tulivat Aabrahamin luokse tuoden iloisia uutisia; he tervehtivät: »Rauha!» Hän vastasi: »Rauha!» ja kotvan kuluttua hän tarjosi heille paistettua vasikkaa.');
INSERT INTO "finnish" VALUES(11,70,'11:70','Mutta kun hän näki, etteivät heidän kätensä ojentuneet sitä kohti, hän oudoksui heitä ja tunsi pelkoa heitä kohtaan. He sanoivat: »Älä pelkää, totisesti, meidät on lähetetty Lootin kansan luokse.»');
INSERT INTO "finnish" VALUES(11,71,'11:71','Ja Aabrahamin vaimo seisoi myös siinä ja sattui nauramaan; silloin ilmoitimme hänelle iloisen lupauksen Iisakista ja Iisakin pojasta Jaakobista.');
INSERT INTO "finnish" VALUES(11,72,'11:72','Hän sanoi: »Voi minua, voinko minä synnyttää, vaikka olen näin vanha, ja mieheni tässä on myös iäkäs. Tämä on tosiaankin merkillinen asia.»');
INSERT INTO "finnish" VALUES(11,73,'11:73','He vastasivat: »Ihmetteletkö sinä Herrasi määräystä? Jumalan armo ja Hänen siunauksensa tulkoot teidän, tämän huonekunnan osaksi. Totisesti, Hän on ylistetty, ylevä.»');
INSERT INTO "finnish" VALUES(11,74,'11:74','Kun Aabrahamin pelko oli haihtunut ja hän oli saanut kuulla iloisesta sanomasta, hän alkoi kiistellä kanssamme Lootin kansasta.');
INSERT INTO "finnish" VALUES(11,75,'11:75','Totisesti, Aabraham oli sangen lempeä, sääliväinen ja hurskas.');
INSERT INTO "finnish" VALUES(11,76,'11:76','»Oi, Aabraham, jätä tämä asia omaan menoonsa, totisesti Herrasi käsky on tullut, ja Lootin kansan on kohtaava tuomio, jota ei voi väistää.»');
INSERT INTO "finnish" VALUES(11,77,'11:77','Ja kun lähettiläämme menivät Lootin luokse, tämä oli kansansa takia ahdistuksessa ja halusi sydämessään heitä auttaa ja sanoi: »Tämä on tuskan päivä.»');
INSERT INTO "finnish" VALUES(11,78,'11:78','Ja hänen kansansa ryntäsi hillittömästi hänen luokseen, ja he olivat jo ennestään harjaantuneet ilkitekoihin. Loot sanoi: »Oi, kansani, tässä ovat tyttäreni! He ovat puhtaampia teitä varten, kunnioittakaa siis Jumalaa, älkääkä häväiskö minua vieraitteni edessä. Eikö yhtään siveätä miestä ole joukossanne?»');
INSERT INTO "finnish" VALUES(11,79,'11:79','He sanoivat: »Sinä tiedät perin hyvin, ettei meillä ole mitään oikeutta tyttäriisi, ja tiedät myös aivan hyvin. mitä me haluamme.»');
INSERT INTO "finnish" VALUES(11,80,'11:80','Hän sanoi: »Toivon, että minulla olisi voimaa vastustaa teitä tai luja tuki, mihin turvautuisin.»');
INSERT INTO "finnish" VALUES(11,81,'11:81','Lähettiläät sanoivat: »Oi Loot, me olemme totisesti Herrasi lähettiläitä, vihollisesi eivät ikinä kykene ojentamaan käsiänsä sinua vastaan. Lähde siis matkaan perheinesi, niin kauan kuin vielä on yötä jäljellä, äläkä salli kenenkään seuralaisistasi paitsi vaimosi katsoa taakseen. Totisesti, se mitä tapahtuu heille, on myös tapahtuva hänelle. Aamu on oleva heidän määrähetkensä. Eikö aamu olekin lähellä?»');
INSERT INTO "finnish" VALUES(11,82,'11:82','Ja kun Meidän määrämme täyttyi, Me käänsimme heidän asuinsijansa ylösalaisin ja annoimme sataa heidän päälleen tulisia kiviä, kerroksen toisensa, päälle;');
INSERT INTO "finnish" VALUES(11,83,'11:83','ne oli Herrasi tarkasti merkinnyt. Ja tuo paikka ei sijaitse kaukana näistä väärämielisistä ihmisistä.');
INSERT INTO "finnish" VALUES(11,84,'11:84','Ja Midianin (heimon) luo Me lähetimme heidän veljensä Shuaibin. Hän sanoi: »Oi, kansani! Palvelkaa Jumalaa, teillä ei ole muuta jumaluutta kuin Hän. Älkää antako vajavaista mittaa tai painoa. Minä kyllä näen teidän hyvinvointinne, ja minä totisesti pelkään, että suuren päivän tuomio kohtaa teidät.');
INSERT INTO "finnish" VALUES(11,85,'11:85','Oi kansani! Antakaa täysi mitta ja punnitkaa rehellisesti. Älkää kavaltako ihmisten omaa älkääkä levittäkö turmelusta maan päällä pahaa tehden.');
INSERT INTO "finnish" VALUES(11,86,'11:86','Jumalan suoma ansio on parasta teille, jos kuulutte uskovaisiin, ja minä en ole teidän vartijanne.»');
INSERT INTO "finnish" VALUES(11,87,'11:87','He sanoivat: »Oi, Shuaib! Vaatiiko sinun rukouksesi, että me luopuisimme siitä, mitä isämme palvelivat, tai ettemme menettelisi omaisuutemme suhteen mielemme mukaan? Totisesti olet lempeä, hurskas.»');
INSERT INTO "finnish" VALUES(11,88,'11:88','Hän lausui: »Oi kansani! Tietäkää, että minä voin nojautua Herrani selvään todistukseen ja Hän on varannut minulle ihanan palkinnon. Minä en halua kilpailla kanssanne sellaisessa, mistä olen teitä kieltänyt. Edistän ainoastaan teidän parastanne niin paljon kuin kykenen. Ja minun menestykseni mitta on yksin Jumalan vallassa. Häneen minä luotan ja Hänen luokseen minä palaan.');
INSERT INTO "finnish" VALUES(11,89,'11:89','Oi kansani! Älkää antako vastahakoisuutenne minua kohtaan tuottaa teille samanlaisia onnettomuuksia kuin ne, jotka kohtasivat Nooan kansaa tai Huudin kansaa tahi Saalihin kansaa; ja Lootin kansa ei ole kaukana teistä.');
INSERT INTO "finnish" VALUES(11,90,'11:90','Anokaa siis anteeksiantoa Herraltanne ja kääntykää sitten Hänen puoleensa. Totisesti, minun Herrani on armollinen, lempeä. »');
INSERT INTO "finnish" VALUES(11,91,'11:91','He sanoivat: »Oi Shuaib! Me emme ymmärrä paljoakaan siitä, mitä puhut, ja me huomaamme sinut heikoksi rinnallamme ja vain heimosi tähden emme ole kivittäneet sinua kuoliaaksi, sillä sinä et mahda mitään meitä vastaan.»');
INSERT INTO "finnish" VALUES(11,92,'11:92','Hän lausui: »Oi kansani, onko heimoni teidän mielestänne mahtavampi kuin Jumala? Hänet te olette halveksien heittäneet selkänne taakse. Totisesti, minun Herrallani on vallassaan kaikki, mitä te teette.');
INSERT INTO "finnish" VALUES(11,93,'11:93','Oi kansani, toimikaa edelleen paikoillanne, niin toimin totisesti minäkin. Aikanaan tulette tietämään, kenet kohtaa tuomio, joka saa hänet häpeämään, ja huomaatte myös, kuka on valehtelija. Odottakaa siis; totisesti, teidän rinnallanne odotan minäkin.»');
INSERT INTO "finnish" VALUES(11,94,'11:94','Ja kun Meidän määrämme täyttyi, me pelastimme armostamme Shuaibin ja hänen kanssaan uskovat. Ja jylisevä melu yllätti ne, jotka olivat väärin tehneet, niin että heidän ruumiinsa paiskautuivat maahan heidän kodeissaan,');
INSERT INTO "finnish" VALUES(11,95,'11:95','ikäänkuin eivät olisi milloinkaan siellä eläneetkään. Eikö tämä ole totta? »Hävitköön Midianin kansa, kuten kävi myös Tamuudin.»');
INSERT INTO "finnish" VALUES(11,96,'11:96','Ja totisesti Me lähetimme Mooseksen tunnusmerkkimme ja selvät valtuudet mukanaan');
INSERT INTO "finnish" VALUES(11,97,'11:97','Faraon ja hänen johtomiestensä luokse; mutta he noudattivat vain Faraon käskyä, mutta Faraon käsky ei ollut oikeamielinen.');
INSERT INTO "finnish" VALUES(11,98,'11:98','Hän on johtava kansaansa ylösnousemuksen päivänä ja saattava heidät tulen omiksi; ja turmioon käy heidän kulkunsa.');
INSERT INTO "finnish" VALUES(11,99,'11:99','Ja kirous seuraa heitä tässä elämässä sekä ylösnousemuksen päivänä: paha on oleva se palkka, joka heille maksetaan.');
INSERT INTO "finnish" VALUES(11,100,'11:100','Nämä ovat kertomuksia kaupungeista, joita olemme sinulle esittänyt, ja jotkut näistä kaupungeista ovat vielä pystyssä ja toiset taas raunioina.');
INSERT INTO "finnish" VALUES(11,101,'11:101','Me emme tehnyt niille vääryyttä, vaan itse ne tuhosivat itsensä; sillä mitään hyötyä heillä ei ollut jumalista, joita he huusivat avukseen Jumalan sijasta, kun Herrasi käsky heidät saavutti. Nuo jumalat jouduttivat vain heidän turmiotaan.');
INSERT INTO "finnish" VALUES(11,102,'11:102','Sellainen on Herrasi kuritus, kun Hän tarttuu kaupunkiin, joka on väärämielinen. Totisesti, Hänen kurituksensa on tuskallinen, ankara.');
INSERT INTO "finnish" VALUES(11,103,'11:103','Tässä on totisesti merkki sille, joka pelkää tulevaista rangaistusta. Tuona päivänä ihmiskunta kootaan yhteen, ja se on todistuksien päivä.');
INSERT INTO "finnish" VALUES(11,104,'11:104','Me siirrämme sen tulon vain määräajaksi.');
INSERT INTO "finnish" VALUES(11,105,'11:105','Kun tuo päivä saapuu, silloin ei ainoakaan voi puhua muuten kuin Hänen luvallaan, niin että jotkut heistä kokevat ahdistusta, toiset onnea.');
INSERT INTO "finnish" VALUES(11,106,'11:106','Onnettomien osana on joutua tuleen, jossa he saavat parkua ja voihkia,');
INSERT INTO "finnish" VALUES(11,107,'11:107','pysyen siellä niin kauan kuin taivaat ja maa ovat olemassa, ellei Herrasi toisin salli. Totisesti, sinun Herrasi saattaa tehdä kaiken, mitä haluaa.');
INSERT INTO "finnish" VALUES(11,108,'11:108','Mutta onnellisten osana on oleva puutarha, jossa he saavat viipyä niin kauan kuin taivaat ja maa ovat olemassa, ellei Herrasi toisin suvaitse. Tämä on lahja, jota ei pois oteta.');
INSERT INTO "finnish" VALUES(11,109,'11:109','Siksi älä ole epätietoinen siitä, mitä nämä ihmiset palvelevat. He palvelevat vain sitä, mitä heidän isänsä palvelivat ennen heitä. Ja Me annamme heille heidän täyden palkkansa, mitään vähentämättä.');
INSERT INTO "finnish" VALUES(11,110,'11:110','Totisesti, Me annoimme Moosekselle Kirjan, ja sitten he joutuivat sen johdosta kiistaan. Ja ellei Herrasi sana olisi ollut jo aikaisemmin annettu, olisi asia ratkaistu heidän välillään, mutta he epäilivät sitä kuitenkin.');
INSERT INTO "finnish" VALUES(11,111,'11:111','Sinun Herrasi on todella maksava kaikille täyden palkan heidän teoistaan. Totisesti, Hän tietää, mitä he tekevät.');
INSERT INTO "finnish" VALUES(11,112,'11:112','Pysy siis lujana, kuten sinua on käsketty, samoin kuin hänkin, joka sinun laillasi on kääntynyt Jumalan puoleen, älkääkä olko nurjamielisiä. Totisesti, Hän näkee, mitä te teette.');
INSERT INTO "finnish" VALUES(11,113,'11:113','Älkää turvautuko niihin, jotka tekevät väärin, muuten joudutte tulen saaliiksi, ja älköön teillä paitsi Jumalaa olko muita ystäviä, sillä silloin te ette saa apua.');
INSERT INTO "finnish" VALUES(11,114,'11:114','Ja suorittakaa rukouksenne päivän alkaessa ja sen päättyessä sekä yön ensi hetkinä. Totisesti, hyvät teot poistavat pahat. Tämä on kehoituksena niille, jotka muistavat.');
INSERT INTO "finnish" VALUES(11,115,'11:115','Pysy lujana. sillä Jumala ei haaskaa niiden palkkaa, jotka hyvää tekevät.');
INSERT INTO "finnish" VALUES(11,116,'11:116','Miksi ei siis ennen teitä eläneiden sukupolvien keskuudessa ollut ihmisiä, joilla olisi ollut kylliksi viisautta kieltääkseen toisia pahuutta tekemästä maan päällä, lukuunottamatta niitä harvoja heistä, jotka pelastimme; mutta väärämieliset seurasivat varakkaita, ja he olivat syntisiä.');
INSERT INTO "finnish" VALUES(11,117,'11:117','Eikä ollut Herrasi mielen mukaista tuhota ainoatakaan kaupunkia epäoikeudenmukaisesti, jos sen asukkaat olivat hyviä.');
INSERT INTO "finnish" VALUES(11,118,'11:118','Ja mikäli Herrasi olisi tahtonut, Hän olisi voinut saattaa koko ihmissuvun yhdeksi ainoaksi yhteisöksi. Mutta he jatkavat kiistelemistään,');
INSERT INTO "finnish" VALUES(11,119,'11:119','lukuunottomatta niitä, joille Herrasi on armollinen. Ja sitä varten Hän on heidät luonut. Herrasi sana on käynyt toteen: »Totisesti, olen täyttävä helvetin sekä dzinneillä että ihmisillä.»');
INSERT INTO "finnish" VALUES(11,120,'11:120','Ja kaikki, mitä Me kerromme sinulle lähettiläistä, on tarkoitettu vahvistamaan sinun sydäntäsi; ja tämän (suuran) kautta sinä saat tietää totuuden, ja uskovaisille se on kehoituksena ja muistutuksena.');
INSERT INTO "finnish" VALUES(11,121,'11:121','Ja sano niille, jotka eivät usko: »Tehkää edelleenkin työtä kukin paikallanne, totisesti, me teemme myös.');
INSERT INTO "finnish" VALUES(11,122,'11:122','Ja odottakaa; totisesti, mekin odotamme.»');
INSERT INTO "finnish" VALUES(11,123,'11:123','Ja Jumalalle kuuluvat näkymättömät asiat niin taivaassa kuin maan päällä, ja Hänen tykönsä on kaikki palaava. Palvelkaa siis Häntä ja luottakaa Häneen. Sinun Herrasi ei ole välinpitämätön siitä, mitä teet.');
INSERT INTO "finnish" VALUES(12,1,'12:1','Alif. Laam. Raa. Nämä ovat selvän Kirjan säkeistöjä.');
INSERT INTO "finnish" VALUES(12,2,'12:2','Olemme totisesti lähettänyt ylhäältä tämän arabialaisen Koraanin, jotta te voisitte ymmärtää.');
INSERT INTO "finnish" VALUES(12,3,'12:3','Me aiomme kertoa sinulle erään kertomuksista parhaimpia ilmaisten sen sinulle tällä Koraanilla, sillä tätä ennen sinä kuuluit niiden joukkoon, jotka olivat tietämättömiä.');
INSERT INTO "finnish" VALUES(12,4,'12:4','Kun Joosef lausui isälleen: »Oi isäni, olen totisesti nähnyt yksitoista kiertotähteä sekä auringon ja kuun; olen nähnyt niiden kumartuvan edessäni», niin');
INSERT INTO "finnish" VALUES(12,5,'12:5','tämä sanoi: »Oi poikani! Älä kerro näkyäsi veljillesi, muuten he ryhtyvät salahankkeisiin sinua vastaan. Saatana on totisesti ihmissuvun ilmeinen vihollinen.');
INSERT INTO "finnish" VALUES(12,6,'12:6','Tällä tavoin on Herrasi valitseva sinut ja opettava sinulle näiden tapahtumien selityksen, ja Hän on täysin käsin siunaava sinut ja Jaakobin lapset, niinkuin Hän tätä ennen siunasi esi-isäsi Aabrahamin ja Iisakin. Totisesti, sinun Herrasi on tietävä, viisas.»');
INSERT INTO "finnish" VALUES(12,7,'12:7','Joosefin ja hänen veljiensä kertomuksessa on todella selvät merkit niille, jotka tutkistelevat.');
INSERT INTO "finnish" VALUES(12,8,'12:8','Niinpä he lausuivat: »Todellakin Joosef veljensä kera on isällemme rakkaampi kuin me, vaikka me kuulumme samaan veljien joukkoon. Totisesti, isämme on ilmeisessä harhassa.');
INSERT INTO "finnish" VALUES(12,9,'12:9','Surmatkaa Joosef tai karkoittakaa hänet jonnekin vieraaseen maahan, jotta isänne kasvot kokonaan kääntyisivät teidän puoleenne ja teistä saattaisi senjälkeen tulla onnellisia.»');
INSERT INTO "finnish" VALUES(12,10,'12:10','Muuan heidän joukostaan sanoi: »Alkää surmatko Joosefia, vaan laskekaa hänet jonkun kaivon pohjalle, jolloin jotkut matkamiehet kenties ottavat hänet mukaansa, jos tahdotte hankkeenne toteuttaa.»');
INSERT INTO "finnish" VALUES(12,11,'12:11','He sanoivat: »Oi isämme, mikä on syynä siihen, ettet usko meidän huostaamme Joosefia? Suommehan totisesti hänelle vain hyvää.');
INSERT INTO "finnish" VALUES(12,12,'12:12','Lähetä hänet huomenna kanssamme, niin hän voi huvitella ja leikkiä; kyllä me vartioimme häntä visusti.»');
INSERT INTO "finnish" VALUES(12,13,'12:13','Hän vastasi: »Minua todellakin huolestuttaa lähettää hänet teidän kanssanne, ja pelkään suden syövän hänet, jos ette pidä huolta hänestä.»');
INSERT INTO "finnish" VALUES(12,14,'12:14','He lausuivat: »Jos susi hänet söisi, vaikka meitä on näin monta, niin me silloin hukassa ensiksi olisimme.»');
INSERT INTO "finnish" VALUES(12,15,'12:15','Kun he sitten ottivat hänet mukaansa ja sopivat siitä, että laskevat hänet kaivon pohjalle, niin Me ilmoitimme hänelle silloin: »Totisesti, Sinä olet vielä kerran kertova heille tästä heidän teostaan, kun he eivät sinua tunne.»');
INSERT INTO "finnish" VALUES(12,16,'12:16','Ja illansuussa he palasivat isänsä luo itkien.');
INSERT INTO "finnish" VALUES(12,17,'12:17','He sanoivat: »Oi isämme! Me juoksimme kilpaa keskenämme ja jätimme Joosefin matkatavaroittemme luokse, ja silloin susi söi hänet; mutta sinä et ota uskoaksesi meitä, vaikka puhumme totta.»');
INSERT INTO "finnish" VALUES(12,18,'12:18','Ja he toivat hänen paitansa, jonka olivat tahrineet muulla verellä. Jaakob sanoi: »Te olette keskenänne suunnitelleet tämän. Minun on nyt totisesti hillittävä mieleni, ja minä rukoilen Jumalalta apua kestääkseni asian, josta kerrotte.»');
INSERT INTO "finnish" VALUES(12,19,'12:19','Ja matkue saapui niille tienoin ja lähetti kaivolle vedenkantajan, joka laski sankonsa siihen. Hän huudahti: »Mikä onni! Täällähän on poika.» He ottivat hänet talteen kuin kauppatavaran, mutta Jumala tiesi, mitä he tekivät.');
INSERT INTO "finnish" VALUES(12,20,'12:20','Ja he möivät hänet halvalla hinnalla, muutamista hopearahoista; sillä hän oli heille rasitukseksi.');
INSERT INTO "finnish" VALUES(12,21,'12:21','Ja egyptiläinen, joka hänet osti, sanoi vaimolleen: »Pidä hänet vieraan arvossa! Hänestä saattaa olla meille hyötyä, tai ehkäpä otamme hänet omaksi pojaksemme.» Tällä tavoin Me annoimme Joosefille tuossa maassa olinpaikan opettaaksemme hänelle näiden tapahtumien selityksen. Jumala ilmaisee itsensä käskyssään, mutta useimmat ihmiset eivät sitä ymmärrä.');
INSERT INTO "finnish" VALUES(12,22,'12:22','Ja kun Joosef pääsi miehuutensa täyteen voimaan, Me annoimme hänelle viisauden ja ymmärryksen. Juuri tällä tavoin me palkitsemme ne, jotka hyvää tekevät.');
INSERT INTO "finnish" VALUES(12,23,'12:23','Ja nainen, jonka talossa Joosef asui, halusi tämän antautuvan hänelle, ja hän sulki ovet ja sanoi: »Tule luokseni, joudu.» Hän vastasi: »Jumala on minun turvani. Totisesti, Herrani on suonut minulle asumuksista parhaan, ja väärämieliset eivät milloinkaan menesty.»');
INSERT INTO "finnish" VALUES(12,24,'12:24','Ja nainen oli lujasti päättänyt saada Joosefin, ja tämä olisi suostunut häneen, ellei olisi nähnyt Herransa voimaa. Tämä tapahtui siinä tarkoituksessa, että Me olisimme voinut torjua synnin ja riettauden hänen luotaan. Totisesti, hän oli Meidän uskollisia palvelijoitamme.');
INSERT INTO "finnish" VALUES(12,25,'12:25','Ja he juoksivat kilpaa ovelle. Silloin nainen repi hänen paitansa rikki takaa, ja he tapasivat molemmat hänen aviomiehensä lähellä ovea. Nainen huudahti: »Mikä muu on sellaisen palkka, joka toivoo pahaa vaimollesi, kuin vankila tai tuskallinen rangaistus?»');
INSERT INTO "finnish" VALUES(12,26,'12:26','Joosef sanoi: »Hän se halusi vietellä minut.» Eräs vaimon huonekuntaan kuuluva todisti: »Jos hänen paitansa on revitty edestä, niin nainen puhuu totta ja mies valehtelee.');
INSERT INTO "finnish" VALUES(12,27,'12:27','Mutta jos hänen paitansa on revitty takaa, niin nainen valehtelee ja mies puhuu totta.»');
INSERT INTO "finnish" VALUES(12,28,'12:28','Kun aviomies siis huomasi, että paita oli revitty takaa, hän lausui: »Totisesti, tämä on sinun naisellisia juoniasi. Teidän viekkautenne on suuri.');
INSERT INTO "finnish" VALUES(12,29,'12:29','Oi Joosef! Älä välitä tästä. Ja sinä, vaimo, pyydä anteeksi syntiäsi! Totisesti, sinä kuulut syntisten joukkoon.»');
INSERT INTO "finnish" VALUES(12,30,'12:30','Ja kaupungin naiset sanoivat: »Päällikön vaimo haluaa nuoren palvelijansa antautuvan hänelle, hän on mielettömästi rakastunut mieheen. Totisesti, huomaamme hänen selvästi hairahtuneen.»');
INSERT INTO "finnish" VALUES(12,31,'12:31','Mutta kun vaimo kuuli heidän puheistaan, hän kutsutti heidät luokseen ja valmisti heille aterian, ja hän antoi kullekin veitsen ja sanoi: »Joosef, tule heidän eteensä.» Kun he näkivät hänet, he ylistivät häntä suuresti, haavoittivat käsiään ja huudahtivat: »Pyhä Jumala! Tämä ei ole ihmisolento. Hänhän ei voi olla muu kuin ylevä enkeli.»');
INSERT INTO "finnish" VALUES(12,32,'12:32','Vaimo lausui: »Juuri hänen tähtensä te soimasitte minua, ja totta on, että minä halusin hänen antautuvan itselleni, mutta hän varjeli itsensä. Mutta jos hän ei tee sitä, mitä minä vaadin, niin totisesti hänet pannaan vankilaan ja hän on kuuluva kunniattomien joukkoon.»');
INSERT INTO "finnish" VALUES(12,33,'12:33','Joosef sanoi: »Herra, vankila on minulle mieluisampi kuin se, mihin he minua viettelevät, ja ellet Sinä käännä minusta heidän aikeitaan, niin he houkuttelevat minut puoleensa, ja joudun pimitettyjen joukkoon.»');
INSERT INTO "finnish" VALUES(12,34,'12:34','Senvuoksi Jumala kuuli hänen rukouksensa ja torjui hänestä heidän aikeensa. Totisesti, Hän on kaikkikuuleva, kaikkitietävä.');
INSERT INTO "finnish" VALUES(12,35,'12:35','Silloin he kaikista merkeistä huolimatta harkitsivat parhaaksi panna hänet joksikin aikaa vankilaan.');
INSERT INTO "finnish" VALUES(12,36,'12:36','Ja kaksi nuorta miestä joutui vankilaan hänen kanssaan. Toinen heistä sanoi: »Uneksin pusertavani viiniä.» Ja toinen lausui: »Minä uneksin kantavani leipää pääni päällä ja lintujen syövän sitä. Selitä tämä meille; me näemme totisesti, että olet niitä, jotka tekevät hyvää.»');
INSERT INTO "finnish" VALUES(12,37,'12:37','Joosef vastasi: »Teille kahdelle ei ennätetä tuoda sitä ruokaa, jota ravinnoksenne annetaan, sillä minä olen lopettanut unienne selityksen ennenkuin teille tuodaan ruokaa. Sillä tämän Herrani on minulle opettanut. Minä olen luopunut sellaisten ihmisten opista, jotka eivät usko Jumalaan ja ovat niitä, jotka kieltävät tulevan elämän.');
INSERT INTO "finnish" VALUES(12,38,'12:38','Minä seuraan isieni Aabrahamin, Iisakin ja Jaakobin uskoa. Meidän ei ole sallittua asettaa ketään Jumalan rinnalle. Tämä on Jumalan armo meille ja koko ihmissuvulle, mutta useimmat eivät ole kiitollisia.');
INSERT INTO "finnish" VALUES(12,39,'12:39','Vankitoverini! Onko parempi, että on useita eri jumalia vai yksi ainoa Jumala, kaikkivaltias!');
INSERT INTO "finnish" VALUES(12,40,'12:40','Ne, joita Jumalan ohella palvelette, ovat pelkkiä nimiä, jotka te ja teidän esi-isänne ovat keksineet; Jumala ei ole siihen ylhäältä valtuutta antanut. Tuomio kuuluu Jumalalle yksin. Hän on määrännyt, ettette ketään muuta palvele kuin yksin Häntä. Tämä on oikea usko, mutta useimmat ihmiset eivät sitä käsitä.');
INSERT INTO "finnish" VALUES(12,41,'12:41','Vankitoverini! Toinen teistä on vielä tarjoava viiniä herransa juotavaksi, mutta toinen hirtetään, ja linnut tulevat syömään hänen päätään. Näin on päätetty asia, jota kyselitte.»');
INSERT INTO "finnish" VALUES(12,42,'12:42','Ja hän sanoi sille näistä kahdesta, jonka luuli pelastuvan: »Muistuta minusta herrallesi!» Mutta saatana sai hänet unohtamaan herrallensa muistuttamisen; niin Joosef sai jäädä vankilaan useiksi vuosiksi.');
INSERT INTO "finnish" VALUES(12,43,'12:43','Ja kuningas sanoi: »Näin unta seitsemästä lihavasta lehmästä, ja seitsemästä laihasta, jotka söivät edelliset; ja näin seitsemän vihantaa tähkää ja yhtä monta kuivaa. Oi johtomieheni, selittäkää minulle näkyni, jos pystytte unia tulkitsemaan.»');
INSERT INTO "finnish" VALUES(12,44,'12:44','He vastasivat: »Nämä ovat sekavia unia, emmekä me pysty selittämään sellaisia.»');
INSERT INTO "finnish" VALUES(12,45,'12:45','Silloin se, joka oli pelastunut (vankilasta), muisti nyt pitkän ajan kuluttua asian ja sanoi: »Olen ilmoittava teille selityksen, lähettäkää minut matkaan (Joosefin luo).»');
INSERT INTO "finnish" VALUES(12,46,'12:46','»Joosef! Sinä, joka puhut totuutta! Selitä meille uni seitsemästä lihavasta lehmästä, jotka seitsemän laihaa söi, sekä seitsemästä vihannasta tähkästä ja seitsemästä kuivasta, voidakseni palata heidän luokseen ja he saisivat tietää selityksenä');
INSERT INTO "finnish" VALUES(12,47,'12:47','Hän vastasi: »Teidän tulee hoitaa viljelyksenne seitsemänä vuonna kuten tavallisesti, mutta minkä korjaatte, jättäkää tähkiin, paitsi pientä osaa, jonka syötte.');
INSERT INTO "finnish" VALUES(12,48,'12:48','Senjälkeen on tuleva seitsemän hyvin ankaraa vuotta; ne nielevät sen, minkä olitte panneet niiden varalle säästöön, paitsi pientä osaa, joka teidän tulee varata siemeneksi.');
INSERT INTO "finnish" VALUES(12,49,'12:49','Näiden jälkeen on tuleva vuosi, jolloin ihmiset saavat sadetta ja voivat pusertaa rypäleitä.»');
INSERT INTO "finnish" VALUES(12,50,'12:50','Ja kuningas lausui: »Tuokaa hänet luokseni!» Mutta kun lähetti tuli hänen tykönsä, hän sanoi: »Palaa takaisin herrasi luo ja kysy häneltä, kuinka on niiden naisten laita, jotka haavoittivat käsiään. Totisesti, Herrani tuntee hyvin heidän aikeensa.»');
INSERT INTO "finnish" VALUES(12,51,'12:51','Ja kuningas sanoi: »Mitä ajattelitte, kun halusitte Joosefin vietellä?» He vastasivat: »Pyhä Jumala, me emme tiedä hänestä mitään pahaa!» Päämiehen vaimo lausui: »Nyt on totuus tullut ilmi. Minä halusin hänet vietellä, hän kuuluu rehellisiin.');
INSERT INTO "finnish" VALUES(12,52,'12:52','Sanon tämän siksi, että hän tietäisi, etten minä valehtele hänen poissaollessaan, ja koska Jumala ei anna johdatustaan niille, jotka ovat petollisia.');
INSERT INTO "finnish" VALUES(12,53,'12:53','Enkä voi sanoa, että sieluni olisi vapaa (synnistä),sillä totisesti sielu on altis pahaan, paitsi milloin Herrani osoittaa armoaan. Totisesti, Herrani on anteeksiantavainen, armollinen.»');
INSERT INTO "finnish" VALUES(12,54,'12:54','Silloin kuningas sanoi: »Tuokaa Joosef luokseni. Aion ottaa hänet omaan palvelukseeni.» Ja puhuttuaan hänen kanssaan kuningas sanoi: »Sinulla on tästä päivästä valtaa ja luottamusta luonamme.»');
INSERT INTO "finnish" VALUES(12,55,'12:55','Joosef vastasi: »Määrää valtakunnan aarteet minun hoitooni, minä olen totisesti valpas, viisas.»');
INSERT INTO "finnish" VALUES(12,56,'12:56','Näin Me annoimme Joosefille maassa aseman, niin että hän saattoi asettua minne halusi. Me osoitamme armoamme kenelle tahdomme, emmekä haaskaa niiden palkkaa, jotka hyvää tekevät.');
INSERT INTO "finnish" VALUES(12,57,'12:57','Ja totisesti, niiden palkka tulevana päivänä on oleva suurempi, jotka uskovat ja harjoittavat hartautta.');
INSERT INTO "finnish" VALUES(12,58,'12:58','Ja Joosefin veljet saapuivat hänen luokseen; he astuivat hänen eteensä, jolloin hän tunsi heidät, mutta he eivät tunteneet häntä.');
INSERT INTO "finnish" VALUES(12,59,'12:59','Ja kun hän oli saanut valmiiksi heidän kuormansa, hän lausui: »Tuokaa luokseni veljenne, joka on isänne luona; ettekö näe, että minä annan täyden mitan ja olen vieraanvarainen isäntä?');
INSERT INTO "finnish" VALUES(12,60,'12:60','Mutta ellette tuo häntä luokseni, en teille mitään mittaa, älkääkä silloin lähestykö minua.»');
INSERT INTO "finnish" VALUES(12,61,'12:61','He vastasivat: »Tahdomme kernaasti pyytää hänen isältään häntä mukaamme, teemme totisesti niin.»');
INSERT INTO "finnish" VALUES(12,62,'12:62','Ja Joosef sanoi palvelijoilleen: »Pankaa heidän rahansa heidän matkatavaroittensa joukkoon, niin että he huomaavat sen, kun he palaavat kotiinsa, jotta he palaisivat takaisin.»');
INSERT INTO "finnish" VALUES(12,63,'12:63','Kun he sitten tulivat isänsä luokse, he sanoivat: »Oi isämme, meiltä on evätty (enempi) viljanosto; lähetä siksi veljemme meidän mukanamme, jotta meille viljaa mitattaisiin; me vartioimme häntä huolellisesti.»');
INSERT INTO "finnish" VALUES(12,64,'12:64','Hän vastasi: »Voinko uskoa hänet huostaanne paremmin kuin aikaisemmin teille uskoin hänen veljensä. Mutta Jumala on paras vartija; Hän on laupiaista armollisin.»');
INSERT INTO "finnish" VALUES(12,65,'12:65','Ja kun he avasivat tavaransa, he huomasivat rahansa heille palautetuiksi. He sanoivat: »Oi isämme, saatammeko toivoa enempää? Tässä ovat palautetut rahamme, nyt hankimme viljaa perheellemme, huolehdimme veljestämme ja saamme yhden kamelin kuorman enemmän. Tämä määrä ei riitä.»');
INSERT INTO "finnish" VALUES(12,66,'12:66','Isä lausui: »En aio lähettää häntä kanssanne, ennenkuin Jumalan edessä lupaatte tuoda hänet varmasti takaisin minulle, elleivät olosuhteet teitä siitä estä.» Kun he olivat antaneet hänelle vakuutuksensa, hän sanoi: »Jumala pitäköön voimassa sen, mitä sanoimme.»');
INSERT INTO "finnish" VALUES(12,67,'12:67','Ja hän sanoi vielä: »Oi poikani, älkää menkö kaupunkiin samasta portista, vaan menkää eri porteista; ja minä en voi auttaa teitä vähääkään Jumalaa vastaan. Ratkaisu on yksin Jumalan käsissä. Häneen minä asetan luottamukseni, ja Häneen luottakoot kaikki, jotka toivoen luottavat.»');
INSERT INTO "finnish" VALUES(12,68,'12:68','Ja kun he menivät sisälle kaupunkiin siten kuin heidän isänsä oli määrännyt, se ei voinut heitä vähääkään auttaa Jumalaa vastaan; mutta täyttyi Jaakobin sydämessä asunut toivomus. Totisesti, hänellä oli tieto, koska Me olimme häntä opettanut, mutta useimmat ihmiset eivät sitä käsitä.');
INSERT INTO "finnish" VALUES(12,69,'12:69','Ja kun he saapuivat Joosefin eteen, hän otti (nuorimman) veljensä luokseen ja sanoi: »Minä olen todella sinun veljesi, siksi älä murehdi sitä, mitä he ovat tehneet.»');
INSERT INTO "finnish" VALUES(12,70,'12:70','Ja kun hän antoi valmistaa heidän kuormiaan, eräs (palvelijoista) kätki pikarin hänen veljensä matkatavaroihin. Senjälkeen eräs huutaja huusi: »Hoi, matkamiehet! Te olette varmasti varkaita!»');
INSERT INTO "finnish" VALUES(12,71,'12:71','He kääntyivät taakseen ja sanoivat: »Mitä teiltä on kadonnut?»');
INSERT INTO "finnish" VALUES(12,72,'12:72','He sanoivat: »Meiltä on kadonnut kuninkaan hopeapikari, ja ken hankkii sen takaisin, saa palkinnokseen kamelin kuorman; sen minä takaan.»');
INSERT INTO "finnish" VALUES(12,73,'12:73','He sanoivat: »Jumalan nimessä, te tiedätte varsin hyvin. että me emme ole tulleet tänne pahaa tekemään ja ettemme ole varkaita.»');
INSERT INTO "finnish" VALUES(12,74,'12:74','Miehet kysyivät: »Minkä korvauksen sitten annatte, jos valehtelette?»');
INSERT INTO "finnish" VALUES(12,75,'12:75','Veljekset vastasivat: »Kenen kuormasta pikari löydetään, saa itse olla korvauksena. Täten me palkitsemme kelvottomia.»');
INSERT INTO "finnish" VALUES(12,76,'12:76','Silloin mies alkoi etsiä ensin heidän säkeistään ja sitten nuorimman veljen, ja hän veti sen esiin tämän säkistä. Tällä tavoin Me annoimme Joosefin turvautua juoneen; Hän ei olisi voinut pidättää veljeään kuninkaan lain nojalla, ellei Jumala olisi niin tahtonut. Me kohotamme arvossa kenet tahdomme. Ja kaikkien tietävien yläpuolella on Kaikkitietävä.');
INSERT INTO "finnish" VALUES(12,77,'12:77','He sanoivat: »Jos hän varastaa, niin totisesti on hänen veljensä varastanut jo ennen häntä.» Silloin Joosef kätki oikean asianlaidan sydämeensä, eikä ilmaissut sitä heille. Hän sanoi itsekseen: »Te olette itse sitäkin pahempia, mutta Jumala tuntee parhaiten teidän syytöksenne.»');
INSERT INTO "finnish" VALUES(12,78,'12:78','Veljet sanoivat: »Oi herra, hänellä on vanha isä, hyvin iäkäs. Ota siis joku meistä tämän asemesta, sillä totisesti me havaitsemme, että olet hyvä ihminen.»');
INSERT INTO "finnish" VALUES(12,79,'12:79','Hän vastasi: »Jumala varjelkoon meitä pidättämästä ketään muuta kuin sen, jonka hallusta löysimme tavaramme. Jos tekisimme niin, olisimme totisesti väärämielisiä.»');
INSERT INTO "finnish" VALUES(12,80,'12:80','Menetettyään toivon veljensä pelastamisesta he vetäytyivät syrjään neuvottelemaan keskenänsä. Vanhin heistä lausui: »Ettekö muista, että isänne otti teiltä Jumalan edessä lupauksen ja että jo aikaisemmin rikoitte Joosefin suhteen? En ikinä aio lähteä täältä ennen kuin isäni niin käskee tai Jumala tuomitsee asian hyväkseni, sillä Hän on paras tuomari.');
INSERT INTO "finnish" VALUES(12,81,'12:81','Palatkaa te isänne luo ja sanokaa: »Isä, totisesti poikasi on tehnyt varkauden, ja me emme todistaneet muuta kuin minkä tiesimme emmekä voineet vartioida näkymättömiä asioita.');
INSERT INTO "finnish" VALUES(12,82,'12:82','Kysy kaupungista, jossa olimme, tai matkueelta, jonka mukana tulimme. Luota siihen, että me puhumme täyttä totta.»');
INSERT INTO "finnish" VALUES(12,83,'12:83','Hän (isä) vastasi: »Ei, te olette itse keksineet tämän jutun. Minun on kestettävä tämä kärsivällisesti, Ehkä Jumala palauttaa heidät kaikki yhdessä. Totisesti, Hän on kaikkitietävä, kaikista viisain.»');
INSERT INTO "finnish" VALUES(12,84,'12:84','Ja hän kääntyi heistä pois ja sanoi: »Voi, miten surenkaan Joosefia!» Ja hänen silmänsä olivat tulvillaan kyyneleitä sen murheen vuoksi, jonka hän tukahdutti.');
INSERT INTO "finnish" VALUES(12,85,'12:85','He sanoivat: »Jumalan nimessä! Sinä et herkeä muistelemasta Joosefia, ennen kuin kokonaan menehdyt tai kuolet.»');
INSERT INTO "finnish" VALUES(12,86,'12:86','Hän vastasi: »Minä ilmaisen vain Jumalalle suuren suruni ja tuskani, ja Häneltä saan tietää sellaista, mitä te ette tiedä.');
INSERT INTO "finnish" VALUES(12,87,'12:87','Poikani, lähtekää etsimään Joosefia ja hänen veljeään, älkääkä epäilkö Jumalan armoa. Totisesti, eivät ketkään muut kuin uskottomat menetä luottamustaan Jumalan armoon.»');
INSERT INTO "finnish" VALUES(12,88,'12:88','Palattuaan tämän jälkeen Joosefin luokse he sanoivat: »Armollinen herra, onnettomuus on kohdannut meitä ja kansaamme, ja meillä on mukanamme vain hieman varoja. Mutta anna meille kuitenkin täysi mitta, ole meille armelias, sillä totisesti Jumala on palkitseva armeliaita.»');
INSERT INTO "finnish" VALUES(12,89,'12:89','Hän vastasi: »Tiedättekö, mitä teitte Joosefille ja hänen veljelleen, kun olitte ymmärtämättömiä?»');
INSERT INTO "finnish" VALUES(12,90,'12:90','He sanoivat: »Oletko sinä todella Joosef?» Hän vastasi. »Minä olen Joosef, ja tämä on veljeni. Jumala on ollut meitä kohtaan hyvä. Totisesti, Jumala ei tee tyhjäksi niiden palkintoa, jotka hyvää tekevät, kuten ei niidenkään, jotka hartautta harjoittavat ja ovat kärsivällisiä');
INSERT INTO "finnish" VALUES(12,91,'12:91','He sanoivat: »Jumalan nimessä, Hän on nostanut sinut meidän yläpuolellemme, ja me olemme olleet totisesti syntisiä.»');
INSERT INTO "finnish" VALUES(12,92,'12:92','Hän vastasi: »Älköön teitä soimattako tänä päivänä. Jumala on antava teille anteeksi, sillä Hän on laupiaista armollisin.');
INSERT INTO "finnish" VALUES(12,93,'12:93','Ottakaa tämä paitani ja asettakaa se isäni kasvoille, niin hän on saava näkönsä takaisin. Ja tuokaa koko perheenne minun luokseni.»');
INSERT INTO "finnish" VALUES(12,94,'12:94','Kun matkue (vasta) lähti, heidän isänsä (kotona) lausui: »Totisesti, minä olen tuntevinani Joosefin tuoksun, mutta te ehkä sanotte, että minä olen järjiltäni.»');
INSERT INTO "finnish" VALUES(12,95,'12:95','He (kotiväki) vastasivat: »Jumalan nimessä, sinä olet totisesti yhä vanhan luulosi vallassa.»');
INSERT INTO "finnish" VALUES(12,96,'12:96','Kun sitten ilosanoman tuoja saapui ja asetti paidan Jaakobin kasvoille, hän sai näkönsä takaisin. Hän lausui: »Enkö teille sanonut, että sain Jumalalta tietää sellaista, mitä te ette tienneet?»');
INSERT INTO "finnish" VALUES(12,97,'12:97','He sanoivat: »Oi isämme, ano anteeksi syntejämme, sillä me olemme totisesti syntisiä.»');
INSERT INTO "finnish" VALUES(12,98,'12:98','Hän vastasi: »Minä olen aikanani anova teille anteeksiantoa Herraltani. Totisesti, Hän on kaikille anteeksiantavainen, armollinen.»');
INSERT INTO "finnish" VALUES(12,99,'12:99','Kun he sitten saapuivat Joosefin eteen, tämä otti vanhempansa luokseen ja sanoi: »Asettukaa Egyptiin täydessä turvassa, Jumalan tahdon mukaan.»');
INSERT INTO "finnish" VALUES(12,100,'12:100','Ja hän asetti vanhempansa valtaistuimelle, mutta he kumartuivat maahan hänen edessään. Hän lausui: »Oi isäni! Tämä on selitys vanhaan uneeni, Jumala on antanut sen käydä toteen. Hän teki hyvin minua kohtaan, kun Hän vapautti minut vankilasta ja toi teidät erämaasta tänne, senjälkeen kuin saatana oli nostanut riidan minun ja veljieni välille. Totisesti, Herrani tietää tarkoin, mitä hän tahtoo. Hän on totisesti kaikkitietävä, viisain kaikista.');
INSERT INTO "finnish" VALUES(12,101,'12:101','Herra! Sinä olet jo antanut minulle valtaa ja opettanut minua selittämään tapaukset. Taivaitten ja maan Luoja! Sinä olet auttajana tässä maailmassa ja tulevassa. Salli minun kuolla oikeauskoisena (muslimina) ja liitä minut hyvien joukkoon!»');
INSERT INTO "finnish" VALUES(12,102,'12:102','Tämä on muuan sinulle tuntemattomista kertomuksista, joita olemme sinulle esittänyt, sillä sinä et ollut heidän lähellään, kun he suunnittelivat tekojaan ja punoivat juoniaan.');
INSERT INTO "finnish" VALUES(12,103,'12:103','Mutta useimmat ihmiset eivät usko sinun tahdostasi huolimatta.');
INSERT INTO "finnish" VALUES(12,104,'12:104','Et sinä liioin pyydä heiltä palkkaa sentähden. Tämä on vain kehoitus kaikille maailman ihmisille.');
INSERT INTO "finnish" VALUES(12,105,'12:105','Kuinka monta merkkiä onkaan taivaassa ja maan päällä, joiden ohi he kulkevat niistä piittaamatta!');
INSERT INTO "finnish" VALUES(12,106,'12:106','Ja useimmat heistä eivät usko yhteen Jumalaan, vaan asettavat muita jumalia Hänen vertaisikseen.');
INSERT INTO "finnish" VALUES(12,107,'12:107','Ovatko he turvassa Jumalan kaikkia kohtaavalta tuomiolta tahi viimeisen hetken äkilliseltä saapumiselta silloin, kun he eivät sitä aavista?');
INSERT INTO "finnish" VALUES(12,108,'12:108','Sano (oi Muhammed): »Tämä on minun tieni, minä kutsun teitä Jumalan luo, sillä minulla on johdatus, kuten niilläkin, jotka minua seuraavat. Kunnia olkoon Jumalalle. Minä en ole kuuluva pakanoitten joukkoon.»');
INSERT INTO "finnish" VALUES(12,109,'12:109','Ennen sinua Me lähetimme ainoastaan kaupunkien asukkaitten keskuudesta miehiä, joille ilmoitimme käskymme. Eivätkö he ole kiertäneet maailmaa ja nähneet, millaisen lopun saivat ne, jotka ennen heitä elivät? Niiden tuleva koti on parempi, jotka hartautta harjoittavat. Ettekö jo ymmärrä?');
INSERT INTO "finnish" VALUES(12,110,'12:110','Kun lähettiläämme joutuivat epätoivoon ja kansa alkoi luulla, että heille oli puhuttu valheita, silloin Me tulimme heidän avukseen. Niin Me pelastimme kenet halusimme, eikä Meidän rangaistustamme voida torjua saavuttamasta syntisiä ihmisiä.');
INSERT INTO "finnish" VALUES(12,111,'12:111','Totisesti, näissä kertomuksissa on opetus ymmärtäväisille ihmisille. Tämä ei ole sepitetty satu, vaan vahvistus ennen tapahtuneelle, selitys kaikkeen sekä johdatus ja armonosoitus niille, jotka uskovat.');
INSERT INTO "finnish" VALUES(13,1,'13:1','Alif. Laam. Miim. Raa. Nämä ovat (Pyhän) Kirjan säkeistöjä. Ja minkä Herra on sinulle lähettänyt, se on totta. Mutta useimmat eivät usko.');
INSERT INTO "finnish" VALUES(13,2,'13:2','Jumala on taivaat korkealle rakentanut ilman näkyviä pylväitä. Sitten Hän nousi valtaistuimelleen. Hän alisti auringon ja kuun noudattamaan lakejansa, niin että kumpikin kulkee määrättyä rataansa. Hän suunnittelee käskynsä ja Hän selittää merkit, jotta tulisitte vakuutetuiksi Herranne tulemisesta.');
INSERT INTO "finnish" VALUES(13,3,'13:3','Ja juuri Hän on levittänyt maan avaraksi ja sijoittanut siihen vuoria ja jokia. Ja kaikkia hedelmiä Hän on maan päälle luonut, parittain kumpaakin sukupuolta. Hän antaa yön peittää päivän. Totisesti, nämä ovat merkkejä niille, jotka ajattelevat.');
INSERT INTO "finnish" VALUES(13,4,'13:4','Ja maan päällä on laajoja alueita, jotka liittyvät toinen toiseensa, viinitarhoja, viljapeltoja ja paimupuita sekä yhteen että erilleen kasvanein juurin, ja kaikkia niitä kastelee sama vesi. Ja me erotamme niistä toisia hyvän makunsa vuoksi syötäviksi. Totisesti, nämä ovat merkkejä niille, joilla on ymmärrystä.');
INSERT INTO "finnish" VALUES(13,5,'13:5','Jos mikään saa sinut ihmetyksen valtaan, niin ihmeellistä on se, että he lausuvat: »Tosiaankin! Luodaanko meidät uudelleen tultuamme tomuksi?» Nämä ovat niitä ihmisiä, jotka eivät usko Jumalaan ja joilla on kahleet kaulassa. He ovat tulen omia, siihen he saavat jäädä.');
INSERT INTO "finnish" VALUES(13,6,'13:6','Ja he pyytävät sinua edistämään pahaa hyvän kustannuksella, vaikka heillä on silmäinsä edessä esimerkkejä menneiltä ajoilta. Totisesti, Herrasi on armon Herra ihmisiä kohtaan huolimatta heidän rikkomuksistaan, ja totisesti Herrasi on ankara rangaistuksessaan.');
INSERT INTO "finnish" VALUES(13,7,'13:7','Ja uskottomat kysyvät: »Miksi hänen Herransa ei ole lähettänyt hänelle merkkiä?» Totisesti, sinä olet vain varoittaja, ja jokaisella kansalla on johtajansa.');
INSERT INTO "finnish" VALUES(13,8,'13:8','Jumala tietää, mitä jokainen nainen kantaa ja kuinka kohdut kutistuvat ja laajenevat. Ja kaikki asiat saavat Hänessä oikean mittansa.');
INSERT INTO "finnish" VALUES(13,9,'13:9','Hän on niin hyvin näkymättömän kuin näkyväisenkin tuntija, kaikkein suurin, kaikkein korkein.');
INSERT INTO "finnish" VALUES(13,10,'13:10','Yhdenarvoisia ovat hänen edessään ne, jotka salassa puhuvat, ja ne, jotka suorittavat sen julkisesti, ne, jotka kätkeytyvät öisin, ja ne, jotka kulkevat ulkosalla päivisin.');
INSERT INTO "finnish" VALUES(13,11,'13:11','Jokaisen edellä ja jäljessä käy suojelijoita, jotka vartioivat häntä Jumalan käskystä. Totisesti, Jumala ei muuta ihmisten kohtaloa, elleivät he itse muuta sitä, mitä heidän sydämissään on; ja kun Jumala haluaa pahaa jollekin kansalle, niin ei sitä voida pois torjua, eikä kukaan muu kuin Hän voi heitä auttaa.');
INSERT INTO "finnish" VALUES(13,12,'13:12','Juuri Hän synnyttää salaman: pelon ja toivon lähteeksi teitä varten, ja Hän muodostaa raskaat pilvet.');
INSERT INTO "finnish" VALUES(13,13,'13:13','Ukkonen julistaa Hänen ylistystänsä, niin myös enkelit, Häntä kunnioittaen. Ja hän singahduttaa salamat keneen tahtoo; ja kuitenkin he kiistelevät Jumalasta, mutta Hän on väkevä voimassaan.');
INSERT INTO "finnish" VALUES(13,14,'13:14','Hänelle osoitettakoon totinen rukous; ja ne, joita he huutavat avukseen Jumalan sijasta, eivät kykene heille vastaamaan millään tavoin, vaan heidän laitansa on kuten sen, joka ojentaa molemmat kätensä vettä kohti, jotta se tulisi hänen suuhunsa, mutta se ei tule. Uskottomien avunhuuto on pelkkää harhaa.');
INSERT INTO "finnish" VALUES(13,15,'13:15','Jumalan edessä kumartuu maahan kaikki, mitä ikinä on taivaassa ja maan päällä, tahtoen tai tahtomattaan, samoin kuin tekevät varjotkin sekä aamulla että illalla.');
INSERT INTO "finnish" VALUES(13,16,'13:16','Sano: »Kuka on taivaitten ja maan Herra?» Sano: »Se on Jumala.» Sano: »Oletteko sitten Hänen rinnalleen ottaneet itsellenne auttajia, jotka eivät edes itseään hallitse eivätkä voi aikaansaada hyvää eivätkä pahaa?» Sano: »Ovatko sokea ja näkevä toistensa vertaisia? Tai ovatko pimeyden varjot ja valo toistensa kaltaisia? Tahi ovatko he asettaneet Jumalan rinnalle kilpailijoita, jotka ovat luoneet samoin kuin Jumalakin, niin että Jumalan luomistyö on heidän silmissään käynyt epävarmaksi?» Sano: »Jumala on kaiken Luoja, ja Hän on ainoa, kaikkivaltias.»');
INSERT INTO "finnish" VALUES(13,17,'13:17','Hän lähettää sateen taivaasta maahan, niin että veden paljous virtaa laaksoissa, kussakin sen laajuuden mukaan, ja tulva kuljettaa mukanaan kuohuvaa vaahtoa. Samanlaista kuonavaahtoa syntyy myös siitä, mitä he sulattavat tulessa valmistaakseen koristeita ja muita esineitä. Täten Jumala vertailee totuutta ja valhetta: senvuoksi vaahto heitetään pois roskajätteenä, mutta jäljelle jää se, mitä ihmiset voivat hyödykseen käyttää. Näin Jumala vertailee asioiden kelvollisuutta.');
INSERT INTO "finnish" VALUES(13,18,'13:18','Niille, jotka vastaavat Herransa kutsuun, on varattuna hyvä osa. Ja jos niillä, jotka eivät vastanneet Hänelle, olisi kaikki, mitä maan päällä on, sekä saman verran lisää, niin he totisesti uhraisivat sen lunastaakseen sillä itsensä. Näitä ihmisiä varten on ankara tilinteko, ja heidän asuinsijansa on oleva helvetti, tuskallinen paikka, jonne he jäävät.');
INSERT INTO "finnish" VALUES(13,19,'13:19','Onko se sokean kaltainen, joka tietää todeksi, mitä sinulle maan päälle Herrasi on lähettänyt. Ainoastaan ne, joilla on ymmärrystä, ottavat vaarin;');
INSERT INTO "finnish" VALUES(13,20,'13:20','ne, jotka täyttävät Jumalan kanssa tehdyn sopimuksen eivätkä riko sitoumuksiaan;');
INSERT INTO "finnish" VALUES(13,21,'13:21','ja ne, jotka yhdistävät, minkä Jumala on yhdistämään käskenyt, ja pelkäävät Herraansa ja välttävät ankaraa tilintekoa;');
INSERT INTO "finnish" VALUES(13,22,'13:22','ja ne, jotka kestävät lujina tavoitellessaan Herransa läheisyyttä, hartaasti rukoilevat ja antavat almuja Meidän heille suomistamme varoista sekä salassa että julkisesti ja torjuvat pahan hyvällä. Nämä ovat niitä, joita varten on lopullinen koti:');
INSERT INTO "finnish" VALUES(13,23,'13:23','He saavat astua autuuden puutarhoihin, ja samoin ne heidän vanhemmistaan, puolisoistaan ja jälkeläisistään, jotka ovat hurskaita. Ja enkelit käyvät sisään heidän luokseen kaikista ovista.');
INSERT INTO "finnish" VALUES(13,24,'13:24','»Rauha olkoon teille, koska kestitte lujina. Miten ihana onkaan lopullinen koti!»');
INSERT INTO "finnish" VALUES(13,25,'13:25','Mutta niitä, jotka rikkovat sopimuksensa Jumalan kanssa, ensin siihen sitouduttuaan, ja erottavat, minkä Jumala on määrännyt yhdistettäväksi, ja tekevät pahaa maan päällä, heitä kohtaa kirous ja heille on varattu tuskien koti.');
INSERT INTO "finnish" VALUES(13,26,'13:26','Jumala antaa runsaan palkan kenelle tahtoo ja mittaa sen yli reunain. He iloitsevat tämän maailman elämästä; mutta tämän maailman elämä on ainoastaan hetken nautintoa verrattuna tulevaan.');
INSERT INTO "finnish" VALUES(13,27,'13:27','Ja uskottomat sanovat: »Miksi hänelle ei ole hänen Herransa lähettänyt tunnusmerkkiä?» Vastaa: »Jumala antaa joutua kadotukseen kenen haluaa, ja Hän ohjaa luoksensa sen, joka on siihen taipuvainen.»');
INSERT INTO "finnish" VALUES(13,28,'13:28','On niitä, jotka uskovat ja joiden sydämet löytävät rauhan heidän muistaessaan Jumalaa. Eikö ole totta, että sydämet löytävät rauhan Jumalassa?');
INSERT INTO "finnish" VALUES(13,29,'13:29','Onnellinen on niiden olotila, jotka uskovat ja tekevät hyvää, ja he saavat palata ihanille asuinsijoille.');
INSERT INTO "finnish" VALUES(13,30,'13:30','Täten Me olemme lähettänyt sinut kansan luokse, jota ennen jo moni kansa on elänyt aikansa, esittämään sille sen, mitä Me olemme sinulle ilmoittanut, mutta he eivät usko Armahtajaan. Sano: »Hän on Herrani! Ei ole muuta Jumalaa kuin Hän. Häneen minä asetan luottamukseni, ja Hänen luokseen minä palaan.»');
INSERT INTO "finnish" VALUES(13,31,'13:31','Ja jos olisi ollut Koraani, jonka voimalla vuoria olisi siirretty tai maa olisi leikattu kappaleiksi tai kuollut saatu puhumaan, niin olisimme kyllä lähettänyt sen maan päälle. Totuus on se, että kaikki käskyvalta kuuluu täydellisesti Jumalalle. Eivätkö uskovaiset tiedä, että jos Jumala olisi niin tahtonut, Hän olisi totisesti johdattanut koko ihmissuvun? Ja ne, jotka ovat valinneet epäuskon, saavat jatkuvasti salamaniskuja päähänsä tekojensa vuoksi, tai salama on iskevä heidän kotiensa läheisyyteen, kunnes Jumalan lupaus käy toteen. Totisesti, Jumala ei ole rikkova lupaustaan.');
INSERT INTO "finnish" VALUES(13,32,'13:32','Toden totta, sinun edelläsi käyneitä lähettiläitä pilkattiin, Minä annoin uskottomille armonaikaa, jonka jälkeen tartuin heihin. Mikä oli silloin tuloksena?');
INSERT INTO "finnish" VALUES(13,33,'13:33','Hän valvoo jokaista sielua, joka saa palkan tekojensa mukaan. Ja kuitenkin he asettavat kilpailijoita Jumalan rinnalle. Sano: »Mainitkaa niiden ominaisuudet! Vai osaatteko kertoa Hänelle sellaista, mitä Hän ei tiedä tämän maan päällä olevankaan? Vai onko se pelkkiä sanoja uskottelun vuoksi?» »Ei, uskottomista näyttävät heidän kuvittelunsa kauniilta, ja heidät on suljettu pois oikealta tieltä. Ja sillä, kenen Jumala antaa eksyä kadotukseen, ei ole ketään opasta.');
INSERT INTO "finnish" VALUES(13,34,'13:34','Heillä on tuska jo tässä maailmassa, ja tuleva tuomio on totisesti vielä tuskallisempi; eikä heillä Jumalaa vastaan ole ketään pelastajaa.');
INSERT INTO "finnish" VALUES(13,35,'13:35','Tämänkaltainen on puutarha, joka on luvattu niille, jotka hartautta harjoittavat: sitä huuhtelevat purot, sen hedelmät ovat ikuisia, kuten sen viihtyisä varjokin. Tämä on niiden määränpää, jotka hartautta harjoittavat, mutta uskottomien loppuna on oleva tuli.');
INSERT INTO "finnish" VALUES(13,36,'13:36','Ja ne, joille on annettu Kirja, ovat iloisia siitä, mitä sinulle on lähetetty, mutta jotkut heimot kieltävät osan siitä. Sano: »Minut on vain määrätty palvelemaan Jumalaa eikä asettamaan ketään Hänen rinnalleen. Häntä minä huudan avukseni, ja Hänen luoksensa minä olen palaava.»');
INSERT INTO "finnish" VALUES(13,37,'13:37','Näin Me olemme lähettänyt sen maan päälle ohjeeksi arabiankielellä. Mutta jos aiot seurata heidän himojaan kaikesta siitä tiedosta huolimatta, mikä osaksesi on tullut, niin Jumalaa vastaan sinulla ei ole oleva ainoatakaan ystävää eikä pelastajaa.');
INSERT INTO "finnish" VALUES(13,38,'13:38','Totisesti, me olemme lähettänyt lähettiläitä jo ennen sinua, ja Me annoimme heille vaimoja ja lapsia. Mutta lähettilään asia ei ole näytellä tunnusmerkkejä, paitsi Jumalan luvalla. Jokaista tehtävää varten on määräys.');
INSERT INTO "finnish" VALUES(13,39,'13:39','Jumala pyyhkii pois tahi vahvistaa minkä haluaa, ja Hänellä on alkuperäinen (Jumalan tiedon) Kirja.');
INSERT INTO "finnish" VALUES(13,40,'13:40','Ja joko me näytämme sinulle jotakin siitä, minkä olemme heille varannut, tai annamme sinulle täyden palkkasi, joka tapauksessa sinun tehtäväsi on julistaa sanomaa, ja Meidän asiamme on vaatia tilinteko.');
INSERT INTO "finnish" VALUES(13,41,'13:41','Eivätkö he ajattele, että Me lähestymme tätä maata ja teemme sen rajoja ahtaammiksi. Kun Jumala tuomitsee, niin Hänen tuomiotaan ei voida torjua. Hän on nopea tilinteossaan.');
INSERT INTO "finnish" VALUES(13,42,'13:42','Totisesti, heidän edeltäjänsä tekivät suunnitelmiaan, mutta suunnitelmat kokonaisuudessaan kuuluvat Jumalalle. Hän tietää, mitä kukin sielu tekee, ja uskottomat saavat pian huomata, kenelle kuuluu lopullinen koti.');
INSERT INTO "finnish" VALUES(13,43,'13:43','Ja jos uskottomat sanovat: »Sinua ei ole lähetetty», niin vastaa: »Jumala olkoon todistajana minun ja teidän välillänne, ja olkoon jokainen, joka tuntee Kirjan.»');
INSERT INTO "finnish" VALUES(14,1,'14:1','Aliif, Laam, Raa. Tämä on Kirja, jonka Me olemme sinulle lähettänyt, jotta voisit johtaa ihmissuvun Herran luvalla pimeydestä vaikeuteen, kaikkivaltiaan, ylistetyn tielle.');
INSERT INTO "finnish" VALUES(14,2,'14:2','Yksin Jumalalle kuuluu kaikki, mikä on taivaassa, ja kaikki, mikä on maan päällä. Voi uskottomia, sillä heitä kohtaa ankara tuska.');
INSERT INTO "finnish" VALUES(14,3,'14:3','He pitävät parempana tämän maailman elämää kuin tulevaa, estävät toisia kulkemasta Jumalan tietä ja koettavat tehdä sen mutkaiseksi. He ovat kauaksi harhaantuneet.');
INSERT INTO "finnish" VALUES(14,4,'14:4','Me emme lähetä ainoatakaan lähettilästä, joka ei puhuisi kansansa kieltä voidakseen asiat selvittää. Jumala antaa kenen haluaa joutua kadotukseen, ja Hän antaa johdatuksensa kenelle tahtoo. Hän on kaikkivaltias, kaikkiviisas.');
INSERT INTO "finnish" VALUES(14,5,'14:5','Ja totisesti Me lähetimme Mooseksen mukanaan Meidän merkkimme ja sanoimme hänelle: »Johdata kansasi pimeydestä vaikeuteen ja muistuta heitä Jumalan päivistä.» Totisesti, tässä on merkit jokaiselle, jolla on runsaasti kestävyyttä ja kiitollisuutta.');
INSERT INTO "finnish" VALUES(14,6,'14:6','Ja Mooses lausuikin kansalleen: »Muistakaa, miten Jumala siunasi teitä, kun Hän pelasti teidät Faraon kansan keskuudesta, joka oli saattanut teidät mitä suurimpiin kärsimyksiin; he surmasivat poikanne ja antoivat naistenne elää!» Tällä tavoin Herranne kuritti teitä ankarasti.');
INSERT INTO "finnish" VALUES(14,7,'14:7','Ja Herranne julisti teille: »Totisesti, jos olette kiitollisia, niin annan teille lisää, mutta jos olette uskottomia, niin Minun tuomioni on oleva mitä ankarin.»');
INSERT INTO "finnish" VALUES(14,8,'14:8','Ja Mooses lausui: »Jos te olisittekin uskottomia - te ja kaikki, jotka maan päällä asuvat - niin Jumala on totisesti riippumaton, ylistetty.»');
INSERT INTO "finnish" VALUES(14,9,'14:9','Eikö teille ole kerrottu ennen teitä eläneistä - Nooan ja Aadin ja Tamuudin kansoista, ja heidän jälkeensä eläneistä? Vain Jumala yksin heidät tuntee. Heidän lähettiläänsä tulivat heidän luokseen selvin tunnusmerkein, mutta he sulkivat käsillään heidän suunsa ja sanoivat: »Totisesti, me emme usko siihen, mitä tuomaan teidät on lähetetty, ja epäilemme sitä, mihin meitä kutsutte hämmentääksenne meitä.»');
INSERT INTO "finnish" VALUES(14,10,'14:10','Heidän sananjulistajansa lausuivat: »Epäilettekö Jumalaa, taivaitten ja maan Luojaa. Hän kutsuu teitä antaakseen teille syntinne anteeksi, ja Hän odottaa teitä määrättyyn hetkeen saakka.» He vastasivat: »Te olette vain ihmisolentoja kuten mekin. Te haluatte kääntää meidät siitä, mitä isämme palvelivat; tuokaa siksi meille selvä todistus.»');
INSERT INTO "finnish" VALUES(14,11,'14:11','Heidän lähettiläänsä vastasivat heille: »Me olemme vain samanlaisia ihmisolentoja kuin tekin, mutta Jumala on hyvä niille palvelijoillensa, joille tahtoo olla. Me emme saata hankkia itsellemme arvovaltaa muutoin kuin Jumalan luvalla; ja Jumalaan uskovaiset luottakoot.');
INSERT INTO "finnish" VALUES(14,12,'14:12','Ja mitä syytä meillä olisi olla luottamatta Jumalaan, joka on johtanut meitä teillämme; ja totisesti me pysymme lujina siitä pahasta huolimatta, mitä te meille tuotatte. Jumalaan luottakoot ne, jotka keneenkään luottavat.');
INSERT INTO "finnish" VALUES(14,13,'14:13','Ja uskottomat sanoivat sananjulistajilleen: »Totisesti, me ajamme teidät pois maastamme, tai teidän on palattava meidän uskoomme.» Silloin heidän Herransa ilmoitti heille: »Totisesti, Me hävitämme ne, jotka ovat väärämielisiä,');
INSERT INTO "finnish" VALUES(14,14,'14:14','ja heidän jälkeensä Me annamme teille heidän asuinsijansa maan päällä. Näin teemme niille, joilla on Jumalanpelko seistessään Minun edessäni ja jotka kavahtavat Minun uhkaustani.»');
INSERT INTO "finnish" VALUES(14,15,'14:15','Ja lähettiläät anoivat ratkaisua, ja jokainen hirmuvaltias, totuuden vastustaja joutui kadotukseen.');
INSERT INTO "finnish" VALUES(14,16,'14:16','Hän on joutuva helvettiin, ja hänet pannaan juomaan saastaista vettä.');
INSERT INTO "finnish" VALUES(14,17,'14:17','Hän ottaa sitä siemauksen toisensa jälkeen, mutta tuskin hän kykenee sitä nielemään, ja kuolema uhkaa häntä joka taholta, mutta hän ei kuole. Tätä seuraa vielä ankarampi tuomio.');
INSERT INTO "finnish" VALUES(14,18,'14:18','Näin on niiden laita, jotka käyvät uskottomiksi Herrallensa: heidän tekonsa ovat kuin tuhka, johon tuuli on puhaltanut myrskyisenä päivänä. He eivät kykene hallitsemaan tekojaan. Tämä on suurta harhaantumista.');
INSERT INTO "finnish" VALUES(14,19,'14:19','Etkö ole ajatellut, että Jumala on luonut taivaat ja maan totuuden voimalla? Hän voi jos tahtoo tuhota teidät ja aikaansaada uuden luomisen.');
INSERT INTO "finnish" VALUES(14,20,'14:20','Se ei tuota mitään vaikeutta Jumalalle.');
INSERT INTO "finnish" VALUES(14,21,'14:21','Ja he saapuvat kaikki Jumalan eteen, ja heikot sanovat niille, jotka ovat olleet ylpeitä: »Totisesti me seurasimme teitä voitteko auttaa meitä välttämään edes osan Jumalan rangaistusta?» He vastaavat: »Jos Jumala olisi antanut meille johdatuksensa, niin me olisimme voineet ohjata teitä. Meille on yhdentekevää, huudammeko vai pysymmekö kärsivällisinä; meille ei ole mitään pelastusta.»');
INSERT INTO "finnish" VALUES(14,22,'14:22','Kun asia on ratkaistu, niin saatana on sanova: »Totisesti, Jumala antoi teille lupauksen, joka oli tosi, ja minä myöskin annoin teille lupauksen, mutta minä rikoin sen, sillä minulla ei ollut teihin muuta valtaa kuin kutsua teitä, ja te vastasitte kutsuuni. Älkää siksi soimatko minua, vaan soimatkaa itseänne. Minä en voi pelastaa teitä, ettekä te kykene pelastamaan minua. Minä kiellän ehdottomasti kaiken sen, mihin aikaisemmin teitte minut osalliseksi.» Totisesti, väärämielisiä kohtaa tuskallinen tuomio.');
INSERT INTO "finnish" VALUES(14,23,'14:23','Mutta ne, jotka uskovat ja harjoittavat hyviä tekoja, saavat astua puutarhoihin, joissa purot solisevat, ja siellä he saavat asua Herransa luvalla. Heidän tervehdyksensä on siellä oleva: »Rauha!»');
INSERT INTO "finnish" VALUES(14,24,'14:24','Etkö ole huomannut, miten Jumala esittää vertauksen pyhästä sanasta? Se on kuin oivallinen puu, jonka juuret ovat lujassa ja jonka oksat ojentuvat taivaaseen.');
INSERT INTO "finnish" VALUES(14,25,'14:25','Herransa käskystä se tuottaa hedelmiä jokaisena vuodenaikana. Ja Jumala esittää edelleen vertauksia ihmisille, että he painaisivat ne mieleensä.');
INSERT INTO "finnish" VALUES(14,26,'14:26','Ja pahuuden sana on verrattavissa kelvottomaan puuhun, joka on kiskaistu irti maasta eikä voi seisoa pystyssä.');
INSERT INTO "finnish" VALUES(14,27,'14:27','Jumala vahvistaa uskovaisia vakevällä sanalla sekä tämän maailman elämässä että tulevassa. Ja Jumala antaa väärämielisten joutua kadotukseen, sillä Jumala tekee tahtonsa mukaan.');
INSERT INTO "finnish" VALUES(14,28,'14:28','Etkö ole tarkannut niitä, jotka ovat palkinneet kiittämättömyydellä Jumalan siunauksen ja johtaneet kansansa turmioon.');
INSERT INTO "finnish" VALUES(14,29,'14:29','He joutuvat helvettiin, ja se on tukala asuinpaikka.');
INSERT INTO "finnish" VALUES(14,30,'14:30','Ja he ovat asettaneet Jumalalle vertaisia johtaakseen ihmisiä harhaan Hänen tieltään. Sano: »Nauttikaa aikanne; totisesti te olette menossa suoraan tuleen.»');
INSERT INTO "finnish" VALUES(14,31,'14:31','Sano uskovaisille palvelijoilleni, että he edelleen suorittavat rukouksensa ja antavat sekä salassa että julkisesti almuja siitä, mitä Me olemme heille suonut, ennenkuin saapuu päivä, jolloin ei enää ole vaihtokauppaa tai salaista suosimista.');
INSERT INTO "finnish" VALUES(14,32,'14:32','Jumala on luonut taivaat ja maan ja Hän lähettää taivaasta sateen maan päälle ja siten kasvattaa hedelmiä teidän ravinnoksenne. Hän alistaa valtaanne laivan, niin että se Hänen käskystään purjehtii merellä, ja myös virrat Hän on tehnyt teille alamaisiksi.');
INSERT INTO "finnish" VALUES(14,33,'14:33','Auringon ja kuun Hän on pannut teitä palvelemaan, ja ne liikkuvat määrättyjä lakeja noudattaen. Ja teidän hyväksenne Hän on säätänyt yön ja päivän.');
INSERT INTO "finnish" VALUES(14,34,'14:34','Hän antaa teille kaikkea, mitä Häneltä pyydätte; ja jos teidän pitäisi laskea Jumalan armonosoitukset, niin ette kykenisi niitä luettelemaan. Totisesti, ihminen on perin väärämielinen, kiittämätön.');
INSERT INTO "finnish" VALUES(14,35,'14:35','Muista, mitä Aabraham lausui: »Herra! Tee tämä kaupunki turvalliseksi ja pelasta minut ja lapseni palvelemasta epäjumalia.');
INSERT INTO "finnish" VALUES(14,36,'14:36','Herra, he ovat totisesti johtaneet suuren joukon ihmisiä kadotukseen, mutta ken ikinä seuraa minua, hän kuuluu joukkooni, ja jos joku ei minua tottele, niin Sinä totisesti olet anteeksiantavainen, armollinen.');
INSERT INTO "finnish" VALUES(14,37,'14:37','Herra, minä olen asettanut eräitä jälkeläisiäni asumaan tähän laaksoon, jossa ei ole maanviljelystä, Sinun Pyhäkkösi läheisyyteen, Herra, jotta he saattaisivat suorittaa rukouksensa. Anna siis ihmisten sydänten taipua heidän puoleensa ja suo heille hedelmiä, jotta he olisivat kiitollisia.');
INSERT INTO "finnish" VALUES(14,38,'14:38','Herra! Totisesti Sinä tiedät, mitä me salaamme ja mitä me tuomme julki. Jumalalta ei ole mikään salattua, ei maan päällä eikä avaruudessa.');
INSERT INTO "finnish" VALUES(14,39,'14:39','Kaikki ylistys kuuluu Jumalalle, joka vanhuudessani antoi minulle Ismaelin ja lisakin. Totisesti, Herra kuulee rukouksen.');
INSERT INTO "finnish" VALUES(14,40,'14:40','Herra, suo, että minä horjumatta suorittaisin rukoukseni ja samoin jälkeläiseni; Herra, kuule rukoukseni.');
INSERT INTO "finnish" VALUES(14,41,'14:41','Herra! Anna anteeksi minulle ja vanhemmilleni sekä uskovaisille tuomion päivänä.»');
INSERT INTO "finnish" VALUES(14,42,'14:42','Älä luule, että Jumala välinpitämättömänä katselee väärämielisten tekoja. Hän ainoastaan siirtää heidän tuomionsa siihen päivään, jolloin heidän katseensa tulee liikkumattomaksi.');
INSERT INTO "finnish" VALUES(14,43,'14:43','Silloin he ryntäävät eteenpäin päät kohotettuina eivätkä kykene liikuttamaan silmiään, ja heidän sydämensä tulevat tyhjiksi.');
INSERT INTO "finnish" VALUES(14,44,'14:44','Varoita ihmissukua siitä päivästä, jolloin tuomio on sitä kohtaava; väärämieliset sanovat silloin: »Oi Herra! Anna meille armo lyhyeksi ajaksi, jotta voisimme vastata kutsuusi ja seurata lähettiläitä.» »Ettekö tätä ennen vannoneet, ettei mikään vetäisi teitä turmioon?');
INSERT INTO "finnish" VALUES(14,45,'14:45','Ja te asuitte niiden asumuksissa, jotka turmelivat sielunsa. Ja teille ilmoitettiin selvästi, miten Me sellaisia kohtelemme, ja totisesti Me esitimme siitä teille esimerkkejä.»');
INSERT INTO "finnish" VALUES(14,46,'14:46','Ja totisesti, he laativat suunnitelmiaan, mutta Jumalan vallassa ovat heidän hankkeensa, eikä niillä olisi vuoria kyetty siirtämään.');
INSERT INTO "finnish" VALUES(14,47,'14:47','Älkää siis luulko, että Jumala rikkoo lähettiläilleen antamansa lupaukset. Totisesti, Jumala on mahtava. Hänellä on valta saattaa asiat oikealle tolalleen.');
INSERT INTO "finnish" VALUES(14,48,'14:48','Se tapahtuu sinä päivänä, jolloin maa uudeksi vaihtuu ja samoin taivaat, ja jolloin ihmiset seisovat Jumalan, ainoan, kaikkivaltiaan edessä.');
INSERT INTO "finnish" VALUES(14,49,'14:49','Olet näkevä, miten syntiset sinä päivänä kytketään kahleilla yhteen.');
INSERT INTO "finnish" VALUES(14,50,'14:50','Tulikiventuoksuiset ovat heidän vaatteensa, ja tuli peittää heidän kasvonsa,');
INSERT INTO "finnish" VALUES(14,51,'14:51','sillä Jumala maksaa jokaiselle sielulle ansion mukaan. Totisesti, Jumala on tilinteossaan nopea.');
INSERT INTO "finnish" VALUES(14,52,'14:52','Tämä on sanoma koko ihmissuvulle, jotta se ottaisi vaarin ja tietäisi, että Hän on ainoa Jumala, ja että ymmärtäväiset tämän muistaisivat.');
INSERT INTO "finnish" VALUES(15,1,'15:1','Aliif, Laam, Raa. Nämä ovat kirjan ja selvän Koraanin säkeistöjä.');
INSERT INTO "finnish" VALUES(15,2,'15:2','On tuleva aika, jolloin epäuskoon antautuneet toivoisivat olevansa muslimeja.');
INSERT INTO "finnish" VALUES(15,3,'15:3','Anna heidän syödä ja nauttia aikansa, ja johtakoon heidän toiveensa heidät harhaan. Mutta tulevaisuudessa he kyllä sen älyävät.');
INSERT INTO "finnish" VALUES(15,4,'15:4','Emme ole hävittänyt ainoatakaan kaupunkia ilmoittamatta sille sen määräaikaa.');
INSERT INTO "finnish" VALUES(15,5,'15:5','Ei yksikään kansa voi kiirehtiä aikansa täyttymistä eikä sitä viivyttää.');
INSERT INTO "finnish" VALUES(15,6,'15:6','Ja he sanovat: »Voi sinua, jolle kehoitus on ylhäältä lähetetty, sinä olet selvästi houkkio.');
INSERT INTO "finnish" VALUES(15,7,'15:7','Miksi et tuo luoksemme enkeleitä, jos kerran puhut täyttä totta?»');
INSERT INTO "finnish" VALUES(15,8,'15:8','Emme Me lähetä maan päälle enkeleitä muuten kuin totuutta pystyttämään, mutta kun tämä tapahtuu, he eivät saa enää armoa.');
INSERT INTO "finnish" VALUES(15,9,'15:9','Totisesti, Me itse lähetämme maan päälle kehoituksen, ja Me myös seisomme sen tukena.');
INSERT INTO "finnish" VALUES(15,10,'15:10','Ja totisesti, jo ennen sinua Me lähetimme sananjulistajia muinaisten heimojen luokse.');
INSERT INTO "finnish" VALUES(15,11,'15:11','Mutta ainoatakaan lähettilästä ei saapunut heidän luoksensa, jota he eivät olisi pilkanneet.');
INSERT INTO "finnish" VALUES(15,12,'15:12','Näin me annamme tämän (pilkkaamisen) juurtua syntisten sydämiin.');
INSERT INTO "finnish" VALUES(15,13,'15:13','He eivät usko Koraaniin, ja kuitenkin heillä on tiedossa edeltäjiensä kohtalo.');
INSERT INTO "finnish" VALUES(15,14,'15:14','Ja jos Me avaisimme oven taivaaseen ja he kiipeäisivät sinne,');
INSERT INTO "finnish" VALUES(15,15,'15:15','niin he varmasti sanoisivat: »Silmämme ovat vain huienneet, tai pikemminkin me olemme lumouksen vallassa.»');
INSERT INTO "finnish" VALUES(15,16,'15:16','Totisesti, Me olemme muodostanut tähtikuvioita taivaalle ja tehnyt sen kauniiksi katselijoille.');
INSERT INTO "finnish" VALUES(15,17,'15:17','Ja Me sitä vartioimme jokaista karkoitettua pahaahenkeä vastaan.');
INSERT INTO "finnish" VALUES(15,18,'15:18','Mutta joka haluaa kuunnella (sanaa) salakähmäisesti, sitä seuraa tulinen liekki.');
INSERT INTO "finnish" VALUES(15,19,'15:19','Me olemme tehnyt maan avaraksi ja sijoittanut vuoret sen päälle, ja Me annamme kaikkea siinä kasvaa sopivan määrän.');
INSERT INTO "finnish" VALUES(15,20,'15:20','Ja maasta Me olemme hankkinut ravinnon teille, kuten niillekin, joita te ette ruoki.');
INSERT INTO "finnish" VALUES(15,21,'15:21','Ei ole olemassa mitään, jota ei Meidän luonamme olisi valtavissa varastoissa, mutta Me lähetämme sitä maan päälle vain tietyin mitoin.');
INSERT INTO "finnish" VALUES(15,22,'15:22','Me annamme myös hedelmällisten tuulien puhaltaa ja lähetämme sadetta taivaasta maan päälle. Sitten annamme teidän juoda sitä, mutta sen varastot eivät ole hallussanne.');
INSERT INTO "finnish" VALUES(15,23,'15:23','Ja totisesti, Me itse annamme elämän ja Me annamme kuoleman, ja Me itse olemme kaiken perilliset.');
INSERT INTO "finnish" VALUES(15,24,'15:24','Me tunnemme tarkoin ne teistä, jotka kulkevat etumaisina, ja me tunnemme myös ne, jotka jäävät loppupäähän.');
INSERT INTO "finnish" VALUES(15,25,'15:25','Totisesti, Herrasi on vielä kokoava heidät yhteen. Hän on viisas, tietävä.');
INSERT INTO "finnish" VALUES(15,26,'15:26','Totisesti, Me olemme luonut ihmisen valajan savesta, mustasta, haisevasta liejusta.');
INSERT INTO "finnish" VALUES(15,27,'15:27','Ja dzinnin me loimme jo aikaisemmin palavasta tulesta.');
INSERT INTO "finnish" VALUES(15,28,'15:28','Muistakaa, mitä Jumala sanoi enkeleille: »Totisesti, aion luoda ihmisolennon valajan savesta, mustasta, haisevasta liejusta.');
INSERT INTO "finnish" VALUES(15,29,'15:29','Ja kun olen saattanut hänen muotonsa täydelliseksi ja puhaltanut häneen Henkeni, kumartukaa maahan hänen edessään.»');
INSERT INTO "finnish" VALUES(15,30,'15:30','Niin kaikki enkelit yhdessä kumartuivat.');
INSERT INTO "finnish" VALUES(15,31,'15:31','Mutta saatana ei tehnyt näin. Hän ei yhtynyt kumartajiin.');
INSERT INTO "finnish" VALUES(15,32,'15:32','Jumala lausui: »Oi saatana. Mistä syystä et yhdy niihin, jotka kumartavat?»');
INSERT INTO "finnish" VALUES(15,33,'15:33','Hän vastasi: »Ei ole soveliasta minun kumartaa ihmisolentoa, jonka Sinä olet luonut valajan savesta, mustasta, haisevasta liejusta.»');
INSERT INTO "finnish" VALUES(15,34,'15:34','Jumala sanoi: »Lähde siis täältä, sillä sinä olet totisesti harhaantunut.');
INSERT INTO "finnish" VALUES(15,35,'15:35','Ole totisesti kirottu tuomion päivään saakka.»');
INSERT INTO "finnish" VALUES(15,36,'15:36','Hän sanoi: »Herra, anna minlle aikaa siihen päivään asti, jolloin heidät herätetään kuolleista.»');
INSERT INTO "finnish" VALUES(15,37,'15:37','Jumala vastasi: »Saat kuulua niihin, joille suodaan aikaa');
INSERT INTO "finnish" VALUES(15,38,'15:38','määräpäivään saakka.»');
INSERT INTO "finnish" VALUES(15,39,'15:39','Hän sanoi: »Herra, koska olet aiheuttanut lankeemukseni, aion kaunistaa tekoni ihmisille maan päällä ja saattaa heidät kaikki lankeamaan');
INSERT INTO "finnish" VALUES(15,40,'15:40','paitsi Sinun vilpittömiä palvelijoitasi.»');
INSERT INTO "finnish" VALUES(15,41,'15:41','Jumala vastasi: »Se on suorin tie luokseni.');
INSERT INTO "finnish" VALUES(15,42,'15:42','Totisesti, palvelijoihini sinulla ei ole mitään valtaa, lukuunottamatta sellaisia, jotka oikealta tieltä harhaantuneina seuraavat sinua.');
INSERT INTO "finnish" VALUES(15,43,'15:43','Helvetti on totisesti määrätty heidän kaikkien olinpaikakseen.');
INSERT INTO "finnish" VALUES(15,44,'15:44','Sinne johtaa seitsemän ovea, ja jokaisesta ovesta käy sisään osa heistä.»');
INSERT INTO "finnish" VALUES(15,45,'15:45','Totisesti, ne jotka hartautta harjoittavat, saavat asua puutarhoissa ja lähteiden luona.');
INSERT INTO "finnish" VALUES(15,46,'15:46','»Käykää sisään rauhassa ja turvallisina!»');
INSERT INTO "finnish" VALUES(15,47,'15:47','Me poistamme heidän rinnastaan kaiken vihan, ja veljellisesti, toisiinsa kääntyneinä, he istuvat valtaistuimilla.');
INSERT INTO "finnish" VALUES(15,48,'15:48','Mitään rasituksia heidän ei siellä tarvitse kestää, eikä heitä sieltä pois ajeta.');
INSERT INTO "finnish" VALUES(15,49,'15:49','Ilmoita palvelijoilleni, että Minä olen totisesti kaiken anteeksiantava, kaikkein armollisin.');
INSERT INTO "finnish" VALUES(15,50,'15:50','Ja ilmoita myös, että Minun tuomioni on tuskallisin tuomio.');
INSERT INTO "finnish" VALUES(15,51,'15:51','Ja kerro heille Aabrahamin vieraista,');
INSERT INTO "finnish" VALUES(15,52,'15:52','jotka astuessaan hänen luokseen sanoivat: »Rauha», ja hän vastasi: »Totisesti, minä pelkään teitä.»');
INSERT INTO "finnish" VALUES(15,53,'15:53','He lausuivat: »Alä pelkää, me tuomme sinulle iloisen ennustuksen viisaasta pojasta.»');
INSERT INTO "finnish" VALUES(15,54,'15:54','Hän sanoi: »Tuotteko minulle ennustuksen siitä huolimatta, että vanhuus on minut jo saavuttanut? Mistä aiotte minulle ilosanoman kertoa?»');
INSERT INTO "finnish" VALUES(15,55,'15:55','He vastasivat: »Tuomme sinulle toden sanoman; älä siis kuulu niihin, jotka joutuvat epätoivoon.»');
INSERT INTO "finnish" VALUES(15,56,'15:56','Hän sanoi: »Kuka muu epäilisi Jumalan armoa kuin kadotetut?»');
INSERT INTO "finnish" VALUES(15,57,'15:57','Hän lausui edelleen: »Mikä on siis asianne, lähettiläät?»');
INSERT INTO "finnish" VALUES(15,58,'15:58','He vastasivat: »Meidät on lähetetty syntisten ihmisten luo');
INSERT INTO "finnish" VALUES(15,59,'15:59','lukuunottamatta Lootin perhettä, josta me totisesti pelastamme kaikki toiset');
INSERT INTO "finnish" VALUES(15,60,'15:60','paitsi hänen vaimoansa; olemme päättäneet, että hän totisesti on kuuluva niiden joukkoon, jotka jäävät tielle.»');
INSERT INTO "finnish" VALUES(15,61,'15:61','Kun siis samat lähettiläät saapuivat Lootin perheen luo,');
INSERT INTO "finnish" VALUES(15,62,'15:62','niin hän sanoi: »Olette varmasti vieraita ihmisiä täällä.»');
INSERT INTO "finnish" VALUES(15,63,'15:63','He vastasivat: »Olemme tulleet luoksesi sen johdosta, mistä kansasi on kiistellyt.');
INSERT INTO "finnish" VALUES(15,64,'15:64','Olemme tuoneet sinulle totuuden ja puhumme varmasti totta.');
INSERT INTO "finnish" VALUES(15,65,'15:65','Sentähden lähde pois perheesi kanssa, kun on vielä osa yöstä jäljellä, ja kulje itse heidän jäljessään äläkä salli kenenkään katsoa taakseen, vaan kulje sinne, mihin teidät on määrätty.');
INSERT INTO "finnish" VALUES(15,66,'15:66','Ja me ilmoitimme hänelle ratkaisumme tässä asiassa: »Viimeinenkin tämän kansan jäsen,on tuhottu, ennenkuin he aamulla nousevat.»');
INSERT INTO "finnish" VALUES(15,67,'15:67','Ja kaupungin asukkaat saapuivat riemuiten hänen luokseen.');
INSERT INTO "finnish" VALUES(15,68,'15:68','Hän lausui: »Totisesti, nämä ovat vieraitani, älkää siis häpäiskö minua.');
INSERT INTO "finnish" VALUES(15,69,'15:69','Pelätkää Jumalaa älkääkä tuottako minulle häpeää.');
INSERT INTO "finnish" VALUES(15,70,'15:70','He sanoivat: »Emmekö ole kieltäneet sinua vastaanottamasta vieraita ihmisiä?»');
INSERT INTO "finnish" VALUES(15,71,'15:71','Hän sanoi: »Tässä ovat tyttäreni, mikäli aiotte jotakin tehdä.»');
INSERT INTO "finnish" VALUES(15,72,'15:72','Elämäsi kautta, oi Muhammed, totisesti he olivat päihtyneet sokeasta himostaan.');
INSERT INTO "finnish" VALUES(15,73,'15:73','Mutta aamun koitteessa heidät yllätti kauhistuttava pauhina.');
INSERT INTO "finnish" VALUES(15,74,'15:74','Ja me käänsimme kaupungin ylösalaisin ja annoimme tulisten kivien sataa heidän päällensä.');
INSERT INTO "finnish" VALUES(15,75,'15:75','Totisesti, tämä on selvä merkki niille, jotka ottavat niistä vaarin.');
INSERT INTO "finnish" VALUES(15,76,'15:76','Ja tuo kaupunki sijaitsee tien varrella, joka yhä on olemassa.');
INSERT INTO "finnish" VALUES(15,77,'15:77','Totisesti, tässä on merkki uskovaisille.');
INSERT INTO "finnish" VALUES(15,78,'15:78','Myös metsän asukkaat olivat sangen väärämielisiä ihmisiä.');
INSERT INTO "finnish" VALUES(15,79,'15:79','Senvuoksi Me annoimme heidän maistaa rangaistusta, ja totisesti, nämä molemmat kaupungit sijaitsevat yleisen tien varrella.');
INSERT INTO "finnish" VALUES(15,80,'15:80','Totisesti, kalliolla asuvat väittivät lähettiläitä valehtelijoiksi.');
INSERT INTO "finnish" VALUES(15,81,'15:81','Ja Me annoimme heille tunnusmerkkejämme, mutta he kääntyivät niistä pois.');
INSERT INTO "finnish" VALUES(15,82,'15:82','Heillä oli tapana hakata talonsa vuoriin ollakseen turvassa.');
INSERT INTO "finnish" VALUES(15,83,'15:83','Mutta kauhistuttava pauhina yllätti heidät aamulla.');
INSERT INTO "finnish" VALUES(15,84,'15:84','Ja se, mitä he olivat tehneet, ei heitä auttanut.');
INSERT INTO "finnish" VALUES(15,85,'15:85','Totuudessa Me olemme luonut taivaat ja maan ja mitä niiden välillä on. Totisesti, Hetki on tuleva, anna siksi heille jalosti anteeksi heidän loukkauksensa.');
INSERT INTO "finnish" VALUES(15,86,'15:86','Totisesti, Herrasi on suuri luova mestari, kaikkitietävä.');
INSERT INTO "finnish" VALUES(15,87,'15:87','Olemmehan antanut sinulle seitsemän säkeistöä useasti luettavaksi sekä suuren Koraanin.');
INSERT INTO "finnish" VALUES(15,88,'15:88','Älä käännä silmiäsi siihen, josta Me olemme antanut heidän nauttia, äläkä murehdi heidän tähtensä, vaan ojenna kätesi uskovaisille.');
INSERT INTO "finnish" VALUES(15,89,'15:89','Sano heille: »Totisesti, minä olen julkinen varoittaja.»');
INSERT INTO "finnish" VALUES(15,90,'15:90','Kuten Me lähetimme tuomion niille, jotka riitelivät, ja niille,');
INSERT INTO "finnish" VALUES(15,91,'15:91','jotka repivät Koraanin palasiksi.');
INSERT INTO "finnish" VALUES(15,92,'15:92','Herrasi nimessä Me heiltä kaikilta vielä vaadimme tilin siitä,');
INSERT INTO "finnish" VALUES(15,93,'15:93','mitä he ovat tehneet.');
INSERT INTO "finnish" VALUES(15,94,'15:94','Sentähden julista heille selvin sanoin, mitä sinulle on määrätty, ja käänny pois pakanoista.');
INSERT INTO "finnish" VALUES(15,95,'15:95','Totisesti, Me olemme sinulle riittävä turva pilkkaajia vastaan.');
INSERT INTO "finnish" VALUES(15,96,'15:96','He asettavat Jumalan rinnalle toisen Jumalan, mutta aikanaan he saavat kokea.');
INSERT INTO "finnish" VALUES(15,97,'15:97','Tiedämme hyvin, että heidän puheensa ahdistaa rintaasi.');
INSERT INTO "finnish" VALUES(15,98,'15:98','Sentähden julista Herrasi kunniaa ylistäen Häntä ja lukeudu niiden joukkoon, jotka kumartuvat maahan.');
INSERT INTO "finnish" VALUES(15,99,'15:99','Ja palvele Herraasi siihen asti, kunnes kuolema luoksesi saapuu.');
INSERT INTO "finnish" VALUES(16,1,'16:1','Jumalan käsky on tulossa, älkää siis pyytäkö sen jouduttamista. Kunnia olkoon Hänelle! Hän on korkealla sen yläpuolella, mitä he asettavat Hänen rinnalleen.');
INSERT INTO "finnish" VALUES(16,2,'16:2','Hän lähettää maan päälle enkeleitä tuomaan Hänen sanansa hengen niille palvelijoistaan, joille parhaaksi näkee, näin sanoen: »Varoittakaa ihmisiä, ettei ole muuta Jumalaa kuin Minä; sentähden palvelkaa Minua.»');
INSERT INTO "finnish" VALUES(16,3,'16:3','Hän on luonut taivaat ja maan totuudessa. Hän on korkealla sen yläpuolella, mitä he Hänen rinnalleen asettavat.');
INSERT INTO "finnish" VALUES(16,4,'16:4','Hän on luonut ihmisen siemenestä, ja katso, hänestä tulee kuitenkin epäilijä.');
INSERT INTO "finnish" VALUES(16,5,'16:5','Ja hän on luonut karjan; siitä te saatte lämpimiä vaatteita ja muuta hyödyllistä, ja osan siitä te käytätte ravinnoksenne.');
INSERT INTO "finnish" VALUES(16,6,'16:6','Teille on kunniaksi, kun ajatte sitä kotiin ja kun viette sitä laitumelle.');
INSERT INTO "finnish" VALUES(16,7,'16:7','Ja eläimet kantavat kuormanne kaupunkiin, jonne ette pääsisi muuten kuin suurin ruumiillisin ponnistuksin. Totisesti, Herra on sangen rakastavainen, armollinen.');
INSERT INTO "finnish" VALUES(16,8,'16:8','Ja Hän on luonut hevoset ja muulit ja aasit, jotta voisitte niillä ratsastaa sekä myöskin ylvästellä. Hän on luonut sellaistakin, mitä ette tiedä.');
INSERT INTO "finnish" VALUES(16,9,'16:9','Oikea tie on Jumalan vallassa, mutta muutamat kulkevat harhaan. Jos Jumala olisi tahtonut, Hän olisi totisesti voinut antaa johdatuksensa teille kaikille.');
INSERT INTO "finnish" VALUES(16,10,'16:10','Hän lähettää teille sateen taivaasta, siitä te saatte juoda ja se kasvattaa puita, joiden luona teillä on laitumenne.');
INSERT INTO "finnish" VALUES(16,11,'16:11','Sateen Hän saattaa kasvattamaan teille peltojen viljan, oliivit, taatelipuut, viinirypäleet ja kaikenlaiset hedelmät. Totisesti, tässä on tunnusmerkkejä niille, jotka ajattelevat.');
INSERT INTO "finnish" VALUES(16,12,'16:12','Ja Hän on teitä varten säätänyt yön ja päivän, luonut auringon ja kuun. Ja tähdet noudattavat Hänen käskyjään. Totisesti, tässä on merkkejä niille, joilla on ymmärrystä.');
INSERT INTO "finnish" VALUES(16,13,'16:13','Ja Hän on luonut teitä varten yli maan levinneet erilaiset hyödykkeet. Totisesti, tässä on merkki niille, jotka ottavat vaarin.');
INSERT INTO "finnish" VALUES(16,14,'16:14','Ja juuri Hän hallitsee merta, niin että saatte siitä ravinnoksenne tuoretta lihaa ja etsitte merestä koristeita, joita kannatte. Ja sinä näet laivojen halkovan merta. Näin voitte turvautua Hänen armoonsa ja tuoda Hänelle kiitoksenne.');
INSERT INTO "finnish" VALUES(16,15,'16:15','Ja Hän on luonut maahan vuoret, ettei se horjuisi allanne, ja virrat sekä tiet teitä opastamaan.');
INSERT INTO "finnish" VALUES(16,16,'16:16','Samaten viitat. Ja myöskin tähtien mukaan ihmiset löytävät suuntansa.');
INSERT INTO "finnish" VALUES(16,17,'16:17','Voiko siis Luoja olla samankaltainen kuin se, joka ei kykene luomaan? Eikö tämäkään mieleenne painu?');
INSERT INTO "finnish" VALUES(16,18,'16:18','Ja jos käytte laskemaan Jumalan armonosoituksia, niin ette kykene niitä luettelemaan. Toden totta - Jumala on anteeksiantavainen, armollinen.');
INSERT INTO "finnish" VALUES(16,19,'16:19','Jumala tietää, mitä te salaatte ja mitä te ilmaisette.');
INSERT INTO "finnish" VALUES(16,20,'16:20','Ja ne, joita ihmiset huutavat avukseen Jumalan sijaan, eivät voi mitään luoda, sillä he itse ovat luotuja.');
INSERT INTO "finnish" VALUES(16,21,'16:21','Kuolleita he ovat, eivät eläviä; eivätkä he tiedä, milloin heidän ylösnousemuksensa tapahtuu.');
INSERT INTO "finnish" VALUES(16,22,'16:22','Teidän Jumalanne on ainoa Jumala. Niiden sydämet, jotka eivät usko tulevaan elämään, vierovat totuutta, ja he paisuvat ylpeydestään.');
INSERT INTO "finnish" VALUES(16,23,'16:23','Epäilemättä Jumala tietää, mitä te salaatte ja mitä te ilmaisette. Totisesti, Hän ei rakasta ylpeitä.');
INSERT INTO "finnish" VALUES(16,24,'16:24','Ja kun heille sanotaan: »Mitä Herranne on lähettänyt maan päälle?» he vastaavat: »Menneiden polvien tarinoita.»');
INSERT INTO "finnish" VALUES(16,25,'16:25','Tämän tähden he saavat ylösnousemuksen päivänä kantaa oman kuormansa kokonaan, ja osan niidenkin kuormasta, jotka tietämättömyydessään heidän johtaminaan joutuivat kadotukseen. Eikö heidän kuormansa ole raskas?');
INSERT INTO "finnish" VALUES(16,26,'16:26','Totisesti, heidän edeltäjänsäkin punoivat juoniaan, mutta Jumala kukisti maahan heidän rakennuksensa perustuksiltaan, jolloin katot sortuivat heidän päällensä, ja Hän lähetti heille turmion sieltä, mistä he eivät osanneet sitä odottaakaan.');
INSERT INTO "finnish" VALUES(16,27,'16:27','Senjälkeen Hän on ylösnousemuksen päivänä saattava heidät häpeään, ja Hän on lausuva: »Missä ovat Minun vertaiseni, joiden kumartamisessa te olitte niin innokkaita?» Ne, joille on suotu ymmärrystä, vastaavat: »Totisesti, tänä päivänä tulkoon häpeä ja paha uskottomien osaksi.»');
INSERT INTO "finnish" VALUES(16,28,'16:28','Enkelit antavat ansaitun palkan näille, jotka ovat itse tehneet vääryyttä itselleen. Silloin he tarjoutuvat alistumaan sanoen: »Emmehän ole tehneet mitään pahaa.» »Kyllä Jumala tietää tarkoin, mitä te teitte.');
INSERT INTO "finnish" VALUES(16,29,'16:29','Sentähden astukaa sisään helvetin porteista ja pysykää siellä ikuisesti. Totisesti, tukala on niiden tyyssija, jotka ovat ylpeitä.»');
INSERT INTO "finnish" VALUES(16,30,'16:30','Mutta niiltä, jotka harjoittavat hartautta, kysytään: »Mitä on Herranne lähettänyt maan päälle?» He vastaavat: »Hyvää.» Niiden, jotka tekevät hyvää, on hyvä olla tässä maailmassa, mutta tulevan maailman koti on vielä parempi. Kuinka ihana onkaan niiden koti, jotka hartautta harjoittavat.');
INSERT INTO "finnish" VALUES(16,31,'16:31','He saavat astua autuaitten puutarhoihin, joissa purot solisevat, ja siellä on heillä kaikki, mitä voivat toivoa. Tällä tavoin Jumala palkitsee niitä, jotka hartautta harjoittavat.');
INSERT INTO "finnish" VALUES(16,32,'16:32','Enkelit maksavat heille täyden palkan, heidän hyvyytensä tähden, ja lausuvat: »Rauha olkoon teille; astukaa puutarhaan palkinnoksi siitä, mitä olette tehneet!»');
INSERT INTO "finnish" VALUES(16,33,'16:33','He eivät odota muuta kuin enkelien tuloa heidän luokseen tai että Herrasi käsky saapuisi. Näin odottivat nekin, jotka ennen heitä elivät. Jumala ei tehnyt heille vääryyttä, vaan itse he tekivät vääryyttä itselleen.');
INSERT INTO "finnish" VALUES(16,34,'16:34','Sillä paha, jota he harjoittivat, lankesi heidän päälleen, ja se, mitä he olivat pilkanneet, kohdistui heihin itseensä.');
INSERT INTO "finnish" VALUES(16,35,'16:35','Ja ne, jotka ovat asettaneet muitakin Jumalan rinnalle, sanovat: »Jos Jumala olisi tahtonut, me emme olisi palvelleet ketään muuta kuin Häntä, emme me eivätkä esi-isämme, emmekä myöskään olisi pitäneet pyhänä ketään muuta kuin Häntä.» Näin tekivät myös ne, jotka ennen heitä elivät. Mutta ovatko lähettiläät vastuussa muusta kuin sanomansa julistamisesta?');
INSERT INTO "finnish" VALUES(16,36,'16:36','Totisesti olemme kutsunut jokaisen kansan keskuudesta lähettilään näin lausumaan: »Palvelkaa Jumalaa ja välttäkää syntisiä.» Jotkut ovat Jumalan johdattamia ja toiset ovat kulkeneet harhaan. Jatkakaa siis vaellustanne maan päällä ja katsokaa, millaisen lopun saavat ne, jotka sepittävät valheita.');
INSERT INTO "finnish" VALUES(16,37,'16:37','Vaikka sinä, oi Muhammed, kuinka innokkaasti tahdot heille johdatusta, niin Jumala ei ole ohjaava niitä, jotka kulkevat kadotukseen, eikä heillä ole ketään auttajaa.');
INSERT INTO "finnish" VALUES(16,38,'16:38','Ja Jumalan nimeen he vannovat pyhimmät valansa: »Jumala ei ole herättävä kuolleita.» Kuitenkin tämä on Jumalan totinen, sitova lupaus, vaikka useimmat eivät sitä käsitä.');
INSERT INTO "finnish" VALUES(16,39,'16:39','Hän on sen tekevä, jotta Hän saattaisi selittää heille sen, mistä he kiistelivät, ja myös sentähden, että uskottomiksi käyneet havaitsisivat olleensa valehtelijoita.');
INSERT INTO "finnish" VALUES(16,40,'16:40','Kun jotakin haluamme, Meidän tarvitsee vain lausua: »Tapahtukoon», ja niin tapahtuu.');
INSERT INTO "finnish" VALUES(16,41,'16:41','Ja jotka Jumalan asian vuoksi ovat jättäneet kotinsa vääryyttä kärsittyään, heille Me totisesti varaamme (jo) tässä maailmassa ihanan asunnon. Mutta tulevan elämän palkka on oleva vielä suurempi; jospa he vain sen käsittäisivät!');
INSERT INTO "finnish" VALUES(16,42,'16:42','Heihin kuuluvat ne, jotka kestävät lujina ja luottavat Herraansa.');
INSERT INTO "finnish" VALUES(16,43,'16:43','Ennen sinua Me lähetimme sananjulistajiksi vain sellaisia, joille annoimme käskymme; kysykää siis niiltä, jotka ovat saaneet muistutuksen, jos ette itse tiedä;');
INSERT INTO "finnish" VALUES(16,44,'16:44','ja heillä oli mukanaan selvät tunnusmerkit ja kirjoitukset. Ja Me olemme lähettänyt sinulle ylhäältä tämän muistomerkin, jotta selittäisit ihmisille, mitä heille on lähetetty, ja että he sen vuoksi ajattelisivat.');
INSERT INTO "finnish" VALUES(16,45,'16:45','Ovatko pahojen juonien punojat varmoja, ettei Jumala avaa maata heidän altaan tai ettei turmio tule heille sieltä, mistä he eivät aavistakaan?');
INSERT INTO "finnish" VALUES(16,46,'16:46','Tahi ettei Hän voi tavoittaa heitä heidän retkillään, niin että he eivät saata paeta?');
INSERT INTO "finnish" VALUES(16,47,'16:47','Tai ettei Hän voi tarttua heihin ensin heitä varoitettuaan? Mutta totisesti, Herrasi on sangen rakastava, armahtavainen.');
INSERT INTO "finnish" VALUES(16,48,'16:48','Eivätkö he edes tarkastele Jumalan luomia olentoja, miten niiden varjot oikealta ja vasemmalta kumartuvat Jumalan edessä ja lankeavat maahan.');
INSERT INTO "finnish" VALUES(16,49,'16:49','Jumalan edessä kumartuvat kaikki olennot taivaassa ja maan päällä, samoin myös enkelit ylpeyttä tuntematta.');
INSERT INTO "finnish" VALUES(16,50,'16:50','He pelkäävät Herraansa, joka on heidän yläpuolellaan, ja he tekevät sen, mihin heidät on määrätty.');
INSERT INTO "finnish" VALUES(16,51,'16:51','Ja Jumala sanoo: »Alkää ottako itsellenne kahdenlaisia jumalia. Hän Itse on ainoa Jumala, siksi pelätkää vain Minua.»');
INSERT INTO "finnish" VALUES(16,52,'16:52','Hänelle kuuluu kaikki, mikä on taivaassa ja maan päällä, ja Häntä on alati toteltava. Palveletteko jotakuta toista kuin Jumalaa?');
INSERT INTO "finnish" VALUES(16,53,'16:53','Kaikki siunaus, minkä saatte, tulee Jumalalta, ja kun teitä kohtaa isku, niin juuri Häntä te avuksenne huudatte.');
INSERT INTO "finnish" VALUES(16,54,'16:54','Kun Hän sitten poistaa vaivanne, niin katso, osa teistä asettaa kuitenkin epäjumalia Herransa rinnalle.');
INSERT INTO "finnish" VALUES(16,55,'16:55','Niin he kieltävät sen, minkä olemme heille antanut. Nauttikaa siitä! Aikananne saatte kokea.');
INSERT INTO "finnish" VALUES(16,56,'16:56','Ja osan antimista, jotka olemme heille suonut, he määräävät sellaisille, joita eivät tunne. Kautta Jumalan, te saatte totisesti vielä vastata keksimästänne.');
INSERT INTO "finnish" VALUES(16,57,'16:57','Ja he keksivät Jumalalle tyttäriä. Tulkoon Hänelle kunnia, ja heille se, mitä toivovat.');
INSERT INTO "finnish" VALUES(16,58,'16:58','Kun jollekin heistä ilmoitetaan tyttölapsen syntymästä, niin hänen kasvonsa synkkenevät ja hän on aivan murheen vallassa.');
INSERT INTO "finnish" VALUES(16,59,'16:59','Hän kätkeytyy heimolaisiltaan sen pahan sanoman vuoksi, joka hänelle on ilmoitettu. Omistaisiko hän lapsen välittämättä häpeästä vai kantaisiko sen elävänä hiekkaan? Eikö heidän tuomionsa ole julma?');
INSERT INTO "finnish" VALUES(16,60,'16:60','Tukala on niiden olo, jotka eivät usko tulevaan elämään, mutta yleviä ovat Jumalan ominaisuudet, hän on kaikkivaltias, kaikkein viisain.');
INSERT INTO "finnish" VALUES(16,61,'16:61','Jos Jumala kävisi käsiksi ihmissukuun sen väärintekojen tähden, Häneltä ei jäisi ainoatakaan elävää olentoa maan päälle, mutta Hän antaa heille aikaa määrähetkeen saakka, niin että kun heidän hetkensä lyö, he eivät kykene sitä tuntiakaan viivyttämään eivätkä jouduttamaan.');
INSERT INTO "finnish" VALUES(16,62,'16:62','Ja he keksivät Jumalan osaksi sellaista, mitä he hylkivät, ja heidän kielensä valehtelevat, että heille on varattu hyvä osa. Heille on eittämättä varattu tuli, ja sinne heidät totisesti ajetaan.');
INSERT INTO "finnish" VALUES(16,63,'16:63','Kautta Jumalan, Me jo ennen sinua lähetimme sananjulistajia kansojen keskuuteen, mutta saatana kaunisteli heille heidän tekojaan, niin että hän vielä tänä päivänä on heidän liittolaisensa, ja heitä odottaa tuskallinen tuomio.');
INSERT INTO "finnish" VALUES(16,64,'16:64','Ja Me olemme ylhäältä lähettänyt sinulle tämän Kirjan ainoastaan siksi, että sinä selvittäisit heille sen, mistä he kiistelevät, sekä johdatukseksi ja armonosoitukseksi niille, jotka uskovat.');
INSERT INTO "finnish" VALUES(16,65,'16:65','Ja Jumala lähettää taivaasta sateen maan päälle ja virvoittaa sillä kuolleen maan jälleen eloon. Totisesti, tässä on vertauskuva niille, jotka kuulevat.');
INSERT INTO "finnish" VALUES(16,66,'16:66','Totisesti teille on karjastannekin opetus. Me annamme juomaa Teille sen sisälmyksistä, siitä, mitä niiden suolisto ja veri sisältää, puhdasta maitoa, joka tuntuu juodessa suloiselta.');
INSERT INTO "finnish" VALUES(16,67,'16:67','Samoin on laita palmupuiden ja viiniköynnösten hedelmien, joista te valmistatte päihdyttäviä juomia ja oivallista ravintoa. Totisesti, tässä on merkki sellaisille, jotka oivaltavat.');
INSERT INTO "finnish" VALUES(16,68,'16:68','Ja Herrasi antaa käskynsä myös mehiläiselle, sanoen: »Valmista itsellesi pesiä vuoriin ja puihin ja siihen, minkä ihmiset rakentavat.');
INSERT INTO "finnish" VALUES(16,69,'16:69','Syö sitten kaikkia hedelmiä ja liiku Herrasi osoittamilla teillä.» Mehiläinen erittää väriltään vaihtelevaa juomaa, joka on lääkettä ihmisille. Totisesti, tässä on merkki niille, jotka ajattelevat.');
INSERT INTO "finnish" VALUES(16,70,'16:70','Jumala on luonut teidät, ja sitten Hän antaa teille täyden palkkanne. Jotkut teistä saavat elää viheliäisimpään vanhuuteen saakka, niin että he eivät tiedä mitään, vaikka heillä aikaisemmin oli tietoa. Totisesti, Jumala on tietävä, mahtava.');
INSERT INTO "finnish" VALUES(16,71,'16:71','Ja Jumala on korottanut jotkut teistä yli toisten varallisuudessa, mutta nämä varakkaat eivät luovuta rikkauksistaan heidän vallassaan oleville orjille, jotta nämä voisivat tässä olla tasavertaisia. Kieltävätkö he näin Jumalan siunauksen?');
INSERT INTO "finnish" VALUES(16,72,'16:72','Ja Jumala antaa teille vaimoja omasta keskuudestanne, ja vaimojenne kautta Hän antaa teille poikia ja pojanpoikia. Myöskin Hän varustaa teidät hyvällä ravinnolla. Uskovatko he siis valheeseen ja kieltävät Jumalan siunauksen?');
INSERT INTO "finnish" VALUES(16,73,'16:73','Paitsi Jumalaa he palvelevat sellaisia, jotka ovat voimattomia hankkimaan heille antimia taivaista tai maan päältä eivätkä kykene auttamaan itseäänkään.');
INSERT INTO "finnish" VALUES(16,74,'16:74','Siksi älkää asettako ketään muuta Jumalan rinnalle, sillä totisesti, Jumala tietää, mutta te ette tiedä.');
INSERT INTO "finnish" VALUES(16,75,'16:75','Jumala asettaa vertauskuvaksi toisen omistaman orjan, jolla ei ole valtaa mihinkään, ja toisaalta ihmisen, jolle Me olemme suonut runsaat antimet, joita hän voi käyttää sekä salassa että julkisesti. Voivatko nämä olla tasavertaisia? Jumalalle kuuluu kaikki ylistys, mutta useimmat heistä eivät älyä mitään.');
INSERT INTO "finnish" VALUES(16,76,'16:76','Ja Jumala asettaa vertailtaviksi kaksi henkilöä: toinen heistä on mykkä, joka ei pysty mihinkään ja on taakkana herralleen; minne tahansa tämä hänet lähettää, hän ei saa aikaan mitään hyvää. Voiko hän olla tasavertainen sen kanssa, joka antaa oikeamielisiä määräyksiä ja itse vaeltaa oikeata tietä?');
INSERT INTO "finnish" VALUES(16,77,'16:77','Jumalalle kuuluu taivaitten ja maan salaisuus; ja tuomion hetki on kuin silmänräpäys tai on sitäkin lyhempi. Totisesti, Jumala kykenee tekemään kaiken, mitä haluaa.');
INSERT INTO "finnish" VALUES(16,78,'16:78','Ja Jumala saattaa teidät ulos äitienne kohduista, teidän siitä mitään tietämättä, ja Hän on antanut teille korvat ja silmät ja sydämen, jotta voisitte Häntä kiittää.');
INSERT INTO "finnish" VALUES(16,79,'16:79','Eivätkö he havaitse, miten linnut noudattavat Jumalan lakeja tyhjässä avaruudessa. Kuka niitä kannattaa paitsi Jumala? Totisesti, tässä on merkkejä niille, jotka uskovat.');
INSERT INTO "finnish" VALUES(16,80,'16:80','Jumala suo teille lepopaikkoja taloissanne ja valmistaa teille eläinten nahoista telttoja, jotka ovat mukavia sekä vaelluksenne päivinä että viipyessänne paikoillanne; ja eläinten villasta ja turkista ja karvasta te saatte aikanaan taloustarpeita ja hyödyllisiä esineitä.');
INSERT INTO "finnish" VALUES(16,81,'16:81','Ja Jumala antaa teille suojaa luomisensa tuotteista; Hän on luonut teille turvapaikkoja vuorille ja Hän antaa teille vaatteet suojaksi kuumuutta vastaan ja varusteet, jotka suojelevat teitä taisteluissa. Tällä tavoin Hän täydellistää siunauksensa teitä kohtaan, jotta olisitte Hänelle alamaisia (muslimeja).');
INSERT INTO "finnish" VALUES(16,82,'16:82','Mutta jos he kääntävät sinulle selkänsä, niin et sinä (Muhammed) ole vastuussa muusta kuin sanoman julistamisesta.');
INSERT INTO "finnish" VALUES(16,83,'16:83','He tunnustavat Jumalan siunauksen, mutta sitten he kieltävät sen, ja enin osa heistä on kiittämättömiä.');
INSERT INTO "finnish" VALUES(16,84,'16:84','Tulee päivä, jolloin Me kutsumme esiin todistajan jokaisesta kansasta, ja silloin uskottomien ei sallita puhua, eikä heidän sallita etsiä armahdusta.');
INSERT INTO "finnish" VALUES(16,85,'16:85','Ja kun väärintekijät kohtaavat rangaistuksensa, he eivät saa siihen mitään lievitystä eikä heille myönnetä armon aikaa.');
INSERT INTO "finnish" VALUES(16,86,'16:86','Ja kun ne, jotka asettivat Jumalalle tasavertaisia, näkevät nämä epäjumalat, he sanovat: »Oi Herra. Nämä ovat epäjumaliamme, joita me Sinun sijaasi huusimme avuksemme.» Mutta nämä vastaavat heille: »Te olette totisesti valehtelijoita.»');
INSERT INTO "finnish" VALUES(16,87,'16:87','Ja tuona päivänä he tarjoutuvat alistumaan Jumalalle, ja mitä he taannoin kuvittelivat, häipyy nyt heidän silmistään.');
INSERT INTO "finnish" VALUES(16,88,'16:88','Niille, jotka valitsevat epäuskon ja estävät ihmisiä Jumalan tietä vaeltamasta, niille Me lisäämme tuskaa tuskan päälle, koska he ovat pahaa tehneet.');
INSERT INTO "finnish" VALUES(16,89,'16:89','Tulee päivä, jolloin Me kutsumme jokaisesta kansasta, niiden omasta keskuudesta, todistajan heitä vastaan, ja senvuoksi Me asetamme sinutkin todistajaksi näitä ihmisiä vastaan. Olemmehan lähettänyt sinulle Kirjan selitykseksi kaikkinaisiin asioihin sekä johdatukseksi ja armoksi ja ilosanomaksi alamaisille (muslimeille).');
INSERT INTO "finnish" VALUES(16,90,'16:90','Totisesti, Jumala käskee jakamaan oikeutta, tekemään hyvää ja harjoittamaan armeliaisuutta sukulaisianne kohtaan; ja Hän kieltää riettaudet, pahuuden ja kapinoimisen; Hän kehoittaa teitä ottamaan vaarin.');
INSERT INTO "finnish" VALUES(16,91,'16:91','Pitäkää liittonne Jumalan kanssa, kun olette liiton tehneet, älkääkä rikkoko valojanne, kun olette ne vahvistaneet; olettehan ottaneet Jumalan valvojaksenne. Totisesti, Jumala tietää teidän tekonne.');
INSERT INTO "finnish" VALUES(16,92,'16:92','Älkääkä olko naisen kaltaisia, joka purki lankansa säikeiksi kehrättyään sen ensin lujaksi. Te vannotte valojanne ikäänkuin puhdistuaksenne petollisuudesta omassa keskuudessanne puolueen pyrkiessä alistamaan toisen valtaansa. Jumala vain koettelee teitä tällä tavoin, ja ylösnousemuksen päivänä Hän on selvittävä teille sen, mistä kiistelitte.');
INSERT INTO "finnish" VALUES(16,93,'16:93','Ja jos Jumala olisi niin halunnut, Hän olisi totisesti luonut teidät yhdeksi ainoaksi kansaksi, mutta Hän sallii sen joutua kadotukseen, joka sitä mielii, ja Hän antaa johdatuksensa sille, ken sitä toivoo. Totisesti, te olette kerran vastuussa siitä, mitä olette tehneet.');
INSERT INTO "finnish" VALUES(16,94,'16:94','Älkää vannoko valoja pettääksenne toisianne, ettei jalkanne liukastuisi seistyään vankasti. Te saatte maistaa pahaa, koska olette estäneet toisia kulkemasta Jumalan tietä, ja teitä on kohtaava suuri rangaistus.');
INSERT INTO "finnish" VALUES(16,95,'16:95','Älkää myykö liittoanne Jumalan kanssa pilkkahintaan. Totisesti, se mikä teitä odottaa Jumalan luona, on parasta teille; jospa sen vain tietäisitte.');
INSERT INTO "finnish" VALUES(16,96,'16:96','Se on katoavaista, mitä teillä on, mutta mitä Jumalalla on, se pysyy. Ja totisesti, lujina kestäneille Me maksamme palkan, parhaasta, mitä ovat tehneet.');
INSERT INTO "finnish" VALUES(16,97,'16:97','Ken tahansa tekee hyvää, olkoon hän mies tai nainen, kun hän samalla on uskovainen, hänen Me totisesti annamme viettää onnellista elämää, ja sellaisille Me maksamme palkan parhaasta, mitä ovat tehneet.');
INSERT INTO "finnish" VALUES(16,98,'16:98','Kun siis luet Koraania, niin etsi Jumalan luota turvaa saatanaa, tuota karkoitettua, vastaan.');
INSERT INTO "finnish" VALUES(16,99,'16:99','Totisesti, hänellä ei ole mitään valtaa uskovaisiin, sillä he luottavat Herraansa.');
INSERT INTO "finnish" VALUES(16,100,'16:100','Saatanan valta ulottuu vain niihin, jotka tekevät liiton hänen kanssaan, ja niihin, jotka asettavat toisia jumalia Jumalan rinnalle.');
INSERT INTO "finnish" VALUES(16,101,'16:101','Kun Me vaihdamme jonkun säkeistön toiseen - Jumala tietää parhaiten, mitä Hän ylhäältä lähettää - niin he sanovat: »Sinä olet pelkkä väärentäjä.» Useimmat heistä ovat tietämättömiä.');
INSERT INTO "finnish" VALUES(16,102,'16:102','Sano: totisesti Pyhä Henki on tuonut sen ylhäältä Herraltasi vahvistaakseen uskovaisia sekä johdatukseksi ja ilosanomaksi kuuliaisille (muslimeille).');
INSERT INTO "finnish" VALUES(16,103,'16:103','Tiedämme perin hyvin, että he vastaavat: »Hänen opettajansa on vain ihminen.» Heidän tarkoittamansa miehen äidinkieli on muukalainen, mutta Koraani on selvää arabiankieltä.');
INSERT INTO "finnish" VALUES(16,104,'16:104','Totisesti, niille, jotka eivät usko Jumalan merkkeihin, Hän ei anna johdatustansa, vaan heille on varattu tuskallinen tuomio.');
INSERT INTO "finnish" VALUES(16,105,'16:105','Vain valheita sepittävät ne, jotka eivät usko Jumalan merkkeihin; he ovat valehtelijoita.');
INSERT INTO "finnish" VALUES(16,106,'16:106','Niitä, jotka kieltävät Jumalan ensin Häneen uskottuaan - emme tarkoita niitä, jotka siihen pakotetaan, vaikkakin heidän sydämensä pysyvät uskossa, vaan niitä, jotka avaavat sydämensä epäuskolle - niitä kohtaa Jumalan viha ja niitä kohtaa suuri rangaistus.');
INSERT INTO "finnish" VALUES(16,107,'16:107','Tämä tapahtuu sentähden, että he rakastavat enemmän tämän maailman elämää kuin tulevaa, ja koska Jumala ei anna johdatustansa uskottomille.');
INSERT INTO "finnish" VALUES(16,108,'16:108','Nämä ovat niitä, joiden sydämet ja korvat ja silmät Jumala on sinetöinyt, ja he ovat välinpitämättömiä ihmisiä.');
INSERT INTO "finnish" VALUES(16,109,'16:109','On epäilemättä totta, että he tulevassa elämässä joutuvat kadotukseen.');
INSERT INTO "finnish" VALUES(16,110,'16:110','Mutta totisesti, Herrasi on silloin oleva anteeksiantavainen, armollinen niitä kohtaan, jotka pakenivat kotoaan vainottuina ja jotka sitten taistelivat ja pysyivät lujina.');
INSERT INTO "finnish" VALUES(16,111,'16:111','Tulee päivä, jolloin jokainen sielu saa puhua omasta puolestaan, jolloin jokaiselle sielulle maksetaan täysin ansionsa mukaan, vääryyttä tekemättä.');
INSERT INTO "finnish" VALUES(16,112,'16:112','Ja Jumala asettaa tarkasteltavaksi sellaisen kaupungin kohtalon, joka eli rauhassa ja tyytyväisyydessä ja nautti runsaita tuloja joka taholta, mutta oli kiittämätön Jumalan siunauksesta, niin että Jumala rankaisi sitä nälällä ja kauhistuksella sen asujainten tekojen vuoksi.');
INSERT INTO "finnish" VALUES(16,113,'16:113','Heille oli noussut lähettiläs heidän omasta keskuudestaan, mutta he pitivät häntä valehtelijana, ja siksi heitä kohtasi tuomio, koska he olivat väärämielisiä.');
INSERT INTO "finnish" VALUES(16,114,'16:114','Syökää sitä, mitä Jumala on teille suonut, mikäli se on sallittua ja puhdasta, ja kiittäkää Jumalan siunauksesta, jos Häntä ainoata palvelette.');
INSERT INTO "finnish" VALUES(16,115,'16:115','Jumala on ainoastaan kieltänyt teitä syömästä itsestään kuollutta, verta, sianlihaa sekä sellaista, mikä on teurastettu jonkun muun kuin Jumalan nimeen. Kuitenkin Jumala on anteeksiantavainen, armollinen sellaista kohtaan, joka on tehnyt väärin hädän pakottamana himoitsematta ja ylittämättä säädettyjä rajoja.');
INSERT INTO "finnish" VALUES(16,116,'16:116','Ja kun kielenne ovat taipuvaiset valheeseen, älkää sanoko: »Tämä on sallittua ja tuo on kiellettyä», sepittääksenne valhetta Jumalaa vastaan. Totisesti, niillä ei ole menestystä, jotka Jumalasta valheita sepittelevät.');
INSERT INTO "finnish" VALUES(16,117,'16:117','Lyhyen tuokion he saavat nauttia, ja sitten heitä kohtaa tuskallinen tuomio.');
INSERT INTO "finnish" VALUES(16,118,'16:118','Ja juutalaisilta olemme kieltänyt sen, mistä jo aikaisemmin sinulle mainitsimme; emmekä Me tehnyt heille vääryyttä, vaan he itse tekivät itselleen vääryyttä.');
INSERT INTO "finnish" VALUES(16,119,'16:119','Totisesti, Herrasi on anteeksiantavainen, armollinen niitä kohtaan, jotka tekevät tietämättömyydessään pahaa, mutta sitten kääntyvät ja tekevät hyvää.');
INSERT INTO "finnish" VALUES(16,120,'16:120','Totisesti, Aabraham oli esikuvana ihmissuvulle, kuuliainen Jumalalle, eheä uskossaan eikä lukeutunut pakanoitten joukkoon.');
INSERT INTO "finnish" VALUES(16,121,'16:121','Hän oli kiitollinen Jumalan siunauksesta, ja Jumala valitsi hänet ja johdatti hänet oikealle tielle.');
INSERT INTO "finnish" VALUES(16,122,'16:122','Me soimme hänelle hyvää jo tässä maailmassa. Ja totisesti, tulevassa elämässä hän on kuuluva vanhurskaiden joukkoon.');
INSERT INTO "finnish" VALUES(16,123,'16:123','Sitten Me annoimme sinulle näin kuuluvan käskyn: »Seuraa sinäkin Aabrahamin uskoa, sillä hän ei kuulunut pakanoihin.»');
INSERT INTO "finnish" VALUES(16,124,'16:124','Sabatti määrättiin vain niille, jotka siitä kiistelevät. Ja totisesti, ylösnousemuksen päivänä Herrasi on ratkaiseva heidän keskinäiset kiistansa.');
INSERT INTO "finnish" VALUES(16,125,'16:125','Kutsu heitä Herrasi tielle viisaudella ja sopivilla kehoituksilla ja kumoa heidän puheensa parhain keinoin. Totisesti Hän, sinun Herrasi, parhaiten tuntee ne, jotka harhaantuvat Hänen tieltään, ja Hän tuntee myös parhaiten ne, jotka johdatuksessa vaeltavat.');
INSERT INTO "finnish" VALUES(16,126,'16:126','Ja jos haluatte korvausta, niin perikää se tappiotanne vastaavasti. Mutta jos kestätte kärsivällisinä, niin se on parasta kärsivällisille.');
INSERT INTO "finnish" VALUES(16,127,'16:127','Pysy siis kärsivällisenä, vaikkakin se on mahdollista vain Jumalan avulla. Älä murehdi heidän tähtensä, äläkä kanna huolta heidän juoniensa takia.');
INSERT INTO "finnish" VALUES(16,128,'16:128','Totisesti, Jumala on niiden kanssa, jotka hartautta harjoittavat, ja niiden kanssa, jotka tekevät hyvää.');
INSERT INTO "finnish" VALUES(17,1,'17:1','Kunnia olkoon Hänelle, joka yön aikana kuljetti palvelijansa pyhitetystä moskeijasta toiseen, kaukaiseen - jonka ympäristön olemme siunanneet - jotta hän saisi nähdä joitakin tunnusmerkeistämme. Totisesti, Hän on kaikkikuuleva, kaikkinäkevä.');
INSERT INTO "finnish" VALUES(17,2,'17:2','Ja Me annoimme Moosekselle Kirjan johdatukseksi Israelin lapsille ja lausuimme: »Minun sijaani älkää ottako ketään muuta suojelijaa.');
INSERT INTO "finnish" VALUES(17,3,'17:3','Te olette niiden jälkeläisiä, jotka pelastimme Nooan kanssa. Hän oli totisesti sangen kiitollinen palvelija.»');
INSERT INTO "finnish" VALUES(17,4,'17:4','Kirjassa Me ilmoitimme tämän Israelin lapsille näin sanoen: »Totisesti, te teette maan päällä pahaa toistamiseen ja tulette peräti pöyhkeiksi.»');
INSERT INTO "finnish" VALUES(17,5,'17:5','Kun täyttyi ensimmäinen kahdesta määräajasta, Me lähetimme teitä vastaan urhoolliset palvelijamme, ja he samosivat voitollisesti halki asutustenne. Ja tämä oli varoitus, jonka oli pakko toteutua.');
INSERT INTO "finnish" VALUES(17,6,'17:6','Senjälkeen Me annoimme teille voiton heistä ja siunasimme teitä rakkaudella ja lapsilla sekä lisäsimme teidän lukumääräänne.');
INSERT INTO "finnish" VALUES(17,7,'17:7','Jos teette hyvää, niin teette sen itsellenne, ja jos teette pahaa, niin senkin teette itsellenne. Kun toisen varoituksen aika meni umpeen, niin tapahtui, että palvelijamme peittivät kasvonne murheella, ja he tunkeutuivat moskeijaan kuten ensimmäiselläkin kerralla ja tuhosivat täydellisesti kaiken, minkä löysivät.');
INSERT INTO "finnish" VALUES(17,8,'17:8','Voi tapahtua, että Herranne armahtaa teitä, mutta jos käännytte pois, niin Me teemme samoin ja valmistamme helvetin uskottomien vankeuspaikaksi.');
INSERT INTO "finnish" VALUES(17,9,'17:9','Totisesti, tämä Koraani johtaa kaikkeen oikeaan, ja uskoville, jotka suorittavat hyviä tekoja, se sisältää ilosanoman, että heitä odottaa suuri palkinto.');
INSERT INTO "finnish" VALUES(17,10,'17:10','Ja että Me niille, jotka eivät usko tulevaan elämään, olemme valmistanut tuskallisen rangaistuksen.');
INSERT INTO "finnish" VALUES(17,11,'17:11','Ihminen huutaa ylleen pahaa niin kuin hän rukoilee hyvääkin; niin, ihminen pyrkii hätiköimään.');
INSERT INTO "finnish" VALUES(17,12,'17:12','Me olemme säätänyt yön ja päivän kahdeksi tunnusmerkiksi; yön merkin me pyyhimme näkymättömäksi ja saatamme päivän merkin täyteen valoon, jotta voisitte etsiä Herranne luota ja tietäisitte vuosien kulun ja ajanlaskun. Kaikista asioista Me olemme antanut täydellisen selityksen.');
INSERT INTO "finnish" VALUES(17,13,'17:13','Jokaisen ihmisen kohtalon Me olemme sitonut hänen kaulansa ympäri, ja ylösnousemuksen päivänä Me tuomme hänen eteensä kirjan, jonka hän huomaa avatuksi.');
INSERT INTO "finnish" VALUES(17,14,'17:14','Hänelle lausutaan: »Lue omaa kirjaasi! Tänä päivänä olet itse itsellesi riittävä tilintekijä.»');
INSERT INTO "finnish" VALUES(17,15,'17:15','Jos joku nauttii johdatusta, se tapahtuu vain hänen omaa sieluansa varten, ja jos ken joutuu kadotukseen, sekin tapahtuu yksin hänen sielunsa vahingoksi. Kukaan ei joudu kantamaan toisen kuormaa, emmekä Me rankaise ketään, ennenkuin olemme lähettänyt sananjulistajan.');
INSERT INTO "finnish" VALUES(17,16,'17:16','Ja kun haluamme tuhota jonkun kaupungin, annamme käskyn sen hyvinvoiville asujamille. Mutta he eivät tottele, jolloin tuomion sana käy oikeutetuksi sitä kohtaan, ja Me tuhoamme sen perinpohjaisesti.');
INSERT INTO "finnish" VALUES(17,17,'17:17','Miten monta sukupolvea Me olemmekaan hävittänyt Nooan jälkeen! Herrasi tietää kaikki palvelijainsa synnit, Hän on kaikkinäkevä.');
INSERT INTO "finnish" VALUES(17,18,'17:18','Kun joku havittelee katoavaista, Me joudutamme niitä hänelle niinkuin tahdomme ja kenelle tahdomme. Senjälkeen Me määräämme hänet helvettiin, jonne hän on joutuva häpeän peittämänä ja karkoitettuna.');
INSERT INTO "finnish" VALUES(17,19,'17:19','Mutta ken toivoo tulevaa elämää ja ponnistelee sen saavuttamiseksi, kuten tuleekin, ollen samalla oikeauskoinen, hän kuuluu niihin, joiden ponnistelut saavat palkkansa.');
INSERT INTO "finnish" VALUES(17,20,'17:20','Kaikille - niin hyvin jälkimmäisille kuin edellisille - Me ojennamme Herrasi lahjat, ja Herrasi lahjat ovat rajattomat.');
INSERT INTO "finnish" VALUES(17,21,'17:21','Katso, miten Me korotamme muutamia toisten yläpuolelle, ja tulevassa elämässä eroavaisuudet ja etuoikeudet ovat vielä suuremmat.');
INSERT INTO "finnish" VALUES(17,22,'17:22','Älä ota toista jumalaa Jumalan rinnalle, muuten saatetaan sinut häpeään ja onnettomuuteen.');
INSERT INTO "finnish" VALUES(17,23,'17:23','Ja Herrasi on määrännyt, että palvelette yksin Häntä, ja teidän tulee tehdä hyvää vanhemmillenne. Jos toinen heistä tahi molemmat saavuttavat korkean iän sinun luonasi, älä halveksi äläkä soimaa heitä, vaan puhuttele heitä kunnioittavasti.');
INSERT INTO "finnish" VALUES(17,24,'17:24','Ja myötätunnolla ojenna kätesi heitä kohti ja lausu sävyisästi: »Oi Herra, ole laupias heille, sillä he kasvattivat minua, kun olin pieni.»');
INSERT INTO "finnish" VALUES(17,25,'17:25','Herranne tietää parhaiten, mitä sydämiinne on kätketty. Jos olette hyviä, niin totisesti Hän silloin on anteeksiantavainen niitä kohtaan, jotka kääntyvät (Hänen puoleensa).');
INSERT INTO "finnish" VALUES(17,26,'17:26','Tee sukulaisillesi oikeutta, samoin tarvitseville ja vaeltajille, mutta älä antaudu tuhlailuun.');
INSERT INTO "finnish" VALUES(17,27,'17:27','Totisesti, tuhlarit ovat saatanan veljiä, ja saatana on kiittämätön Herraansa kohtaan.');
INSERT INTO "finnish" VALUES(17,28,'17:28','Mutta jos käännyt pois heistä etsien Herrasi suosiota, jota itsellesi toivot, puhu heille kuitenkin lempeästi.');
INSERT INTO "finnish" VALUES(17,29,'17:29','Älä pidä kättäsi sidottuna selkäsi taakse, mutta älä myöskään ojenna sitä täyteen pituuteensa, muuten saat olla häväistynä (itaruudestasi) tai köyhtyneenä (ylenpalttisuudestasi).');
INSERT INTO "finnish" VALUES(17,30,'17:30','Totisesti, Herrasi suo palkan kenelle haluaa, ja Hän myös mittaa sen määrän. Totisesti, Hän pitää hyvää lukua palvelijoistaan ja on kaikkinäkevä.');
INSERT INTO "finnish" VALUES(17,31,'17:31','Älkää surmatko lapsianne köyhyyden pelosta. Me hankimme heille elatuksen kuten teillekin. Totisesti, heidän surmaamisensa on inhottava synti.');
INSERT INTO "finnish" VALUES(17,32,'17:32','Älkää harjoittako haureutta, sillä sellainen on rivoa ja huonoa elämää.');
INSERT INTO "finnish" VALUES(17,33,'17:33','Älkää syyttä tappako ainoatakaan sielua, sillä Jumala on sen kieltänyt, paitsi oikeuden nimessä. Ja jos joku on surmattu syyttömästi, niin Me annamme vallan hänen sukulaiselleen, mutta älkää salliko tämän mennä liian pitkälle verikostossaan; hän on totisesti saava avun.');
INSERT INTO "finnish" VALUES(17,34,'17:34','Älkää ryhtykö orpojen perintöön paitsi enentääksenne sitä, ennenkuin he tulevat täysi~ikäisiksi. Täyttäkää sopimuksenne, sillä totisesti, te joudutte niistä tilille.');
INSERT INTO "finnish" VALUES(17,35,'17:35','Ja mitatkaa täydellä mitalla, kun mittaatte, ja punnitkaa oikeilla punnuksilla. Tämä on parasta ja ajan pitkään edullisinta.');
INSERT INTO "finnish" VALUES(17,36,'17:36','Älkää noudattako sellaista, mistä teillä ei ole tietoa. Totisesti, korva, silmä ja sydän, kaikki ne joutuvat kerran tilinteolle.');
INSERT INTO "finnish" VALUES(17,37,'17:37','Älä kulje maan päällä kerskaillen. Totisesti, sinä et kykene leikkaamaan maata kahtia etkä kurkottautumaan vuorten huipuille.');
INSERT INTO "finnish" VALUES(17,38,'17:38','Kaikki paha, mikä tässä on kielletty, on vihattavaa Herrasi silmissä.');
INSERT INTO "finnish" VALUES(17,39,'17:39','Nämä kuuluvat niihin viisaisiin sanoihin, joita Herrasi on sinulle ilmoittanut. Älä aseta Jumalan rinnalle ketään muuta jumalaa, muuten sinut häväistynä ja hylättynä heitetään helvettiin.');
INSERT INTO "finnish" VALUES(17,40,'17:40','Olisiko Herranne valikoinut teille poikia ja ottanut Itse tyttäriä enkeleitten joukosta? Totisesti, te puhutte törkeitä sanoja.');
INSERT INTO "finnish" VALUES(17,41,'17:41','Totisesti, Me olemme selittänyt (asioita) monin tavoin tässä Koraanissa, jotta ihmiset ne mieleensä painaisivat. Mutta se lisää vain heidän penseyttään.');
INSERT INTO "finnish" VALUES(17,42,'17:42','Sano; »Jos olisi ollut muitakin jumalia Hänen ohellaan kuten he väittävät - niin he olisivat löytäneet tiensä Kaikkivaltiaan luokse.»');
INSERT INTO "finnish" VALUES(17,43,'17:43','Kunnia olkoon Hänelle yksin; suuressa korkeudessaan Hän on kokonaan sen yläpuolella, mitä he puhuvat.');
INSERT INTO "finnish" VALUES(17,44,'17:44','Seitsemän taivasta ja maa sekä kaikki, mitä niissä on, julistavat Hänen kunniaansa. Eikä ole mitään, mikä ei julistaisi Hänen kunniaansa, mutta te ette käsitä heidän kunnioitustaan. Totisesti, Hän on ylevämielinen, anteeksiantava.');
INSERT INTO "finnish" VALUES(17,45,'17:45','Ja kun esität julkisesti Koraania, me asetamme näkymättömän verhon sinun ja niiden välille, jotka eivät usko tulevaan elämään.');
INSERT INTO "finnish" VALUES(17,46,'17:46','Ja Me panemme peitteen heidän sydäntensä päälle, niin että he eivät tajua sitä, ja teemme heidät korviltansa kuuroiksi. Ja kun sinä muistutat Koraanin kautta, että Hän on ainoa, he kääntävät selkänsä ja rientävät pois.');
INSERT INTO "finnish" VALUES(17,47,'17:47','Me tiedämme parhaiten, miksi he kuuntelevat (Koraania), kun he kääntävät korvansa sinun puoleesi, ja kun he pitävät salaisia neuvottelujaan, ja kun väärämieliset sanovat: »Te seuraatte noituuden lumoissa olevaa miestä.»');
INSERT INTO "finnish" VALUES(17,48,'17:48','Katso, kuinka he keksivät sinusta vertauksia, ja siksi he joutuvat kadotukseen eivätkä kykene löytämään tietä.');
INSERT INTO "finnish" VALUES(17,49,'17:49','Ja he sanovat: »Mitä, herätetäänkö meidät uuteen elämään senjälkeen kuin olemme muuttuneet luiksi ja tomuksi?»');
INSERT INTO "finnish" VALUES(17,50,'17:50','Vastaa: »Vaikkapa muuttuisitte kiviksi tahi raudaksi');
INSERT INTO "finnish" VALUES(17,51,'17:51','taikka joksikin muuksi, mikä mielestänne on kaikkein pahinta.» He sanovat jälleen: »Kuka sitten herättää meidät uudelleen eloon?» Vastaa: »Hän, joka teidät alussa loi.» Silloin he pudistelevat sinulle päätään ja sanovat: »Milloin tämä tapahtuu?» Vastaa: »Se tapahtuu ehkä hyvinkin pian.»');
INSERT INTO "finnish" VALUES(17,52,'17:52','Sinä päivänä Hän on kutsuva teitä, ja te vastaatte ylistäen Häntä ja luulette, että teidän elämänne oli pysähtynyt vain lyhyeksi ajaksi.');
INSERT INTO "finnish" VALUES(17,53,'17:53','Ja sanokaa palvelijoilleni, että he puhuisivat niinkuin on parasta. Totisesti, saatana herättää riitaisuuksia heidän keskuuteensa. Saatana on ihmissuvun ilmeinen vihollinen.');
INSERT INTO "finnish" VALUES(17,54,'17:54','Herranne tuntee teidät parhaiten. Hän osoittaa teille armoansa, jos haluaa, ja Hän voi panna teidät kuritukseen, jos hyväksi näkee. Emme me ole lähettänyt sinua heidän vartijakseen.');
INSERT INTO "finnish" VALUES(17,55,'17:55','Herrasi tuntee myös parhaiten ne, jotka ovat taivaassa ja maan päällä. Ja totisesti, Me olemme korottanut muutamia profeettoja toisten yläpuolelle ja annoimme Daavidille psalmit (kirjoituksen).');
INSERT INTO "finnish" VALUES(17,56,'17:56','Sano: »Kutsukaa avuksenne niitä, joita kuvittelette Jumalan vertaisiksi: heillä ei ole valtaa poistaa teidän vaivojanne eikä muuttaa niiden paikkaa.»');
INSERT INTO "finnish" VALUES(17,57,'17:57','Ne, joita he kutsuvat avukseen, koettavat itse lähestyä Herraansa, nähdäkseen, kuka heistä olisi Häntä lähinnä. He halajavat Hänen armoansa ja pelkäävät Hänen tuomiotansa. Totisesti, Herrasi tuomio on kammottava.');
INSERT INTO "finnish" VALUES(17,58,'17:58','Ei ole ainoatakaan kaupunkia, jota emme tuhoaisi ennen ylösnousemuksen päivää tahi jolle emme langettaisi ankaraa tuomiota. Tämä on kirjoitettu Kirjassa.');
INSERT INTO "finnish" VALUES(17,59,'17:59','Mikään ei estä Meitä lähettämästä merkkejä, mutta on totta, että muinoin eläneet kielsivät ne. Ja Me annoimme Tamuudille naaraskameelin näkyväksi merkiksi, mutta he rääkkäsivät sitä. Siksi Me lähetämme merkkejä vain varoitukseksi.');
INSERT INTO "finnish" VALUES(17,60,'17:60','Muista, mitä Me sinulle lausuimme: »Totisesti, Herrallasi on ihmissuku vallassaan.» Ja ilmestyksen, jonka sinulle näytimme, Me annoimme vain koetukseksi ihmisille, samoin kuin Koraanin kirotun puun. Ja Me varoitimme heitä, mutta se lisää vain heidän suurta kapinallisuuttaan.');
INSERT INTO "finnish" VALUES(17,61,'17:61','Ja kun lausuimme enkeleille: »Kumartakaa ihmistä», niin he kumarsivat kaikki paitsi saatana. Hän sanoi: »Tulisiko minun kumartua sellaisen edessä, jonka Sinä olet savesta luonut?»');
INSERT INTO "finnish" VALUES(17,62,'17:62','Ja edelleen hän lausui: »Sano minulle, häntäkö Sinä enemmän kunnioitat kuin minua? Totisesti, jos Sinä suot minulle aikaa ylösnousemuksen päivään saakka, niin saatan turmioon hänen jälkeläisensä muutamia harvoja lukuunottamatta.»');
INSERT INTO "finnish" VALUES(17,63,'17:63','Hän vastasi: »Mene! Jos joku heistä sinua seuraa, on helvetti oleva palkkanne, ja se on riittävä palkka.');
INSERT INTO "finnish" VALUES(17,64,'17:64','Ja viettele äänelläsi kenet heistä kykenet, ja käy heidän kimppuunsa sekä ratsu- että jalkaväkinesi, ole osallisena heidän omaisuuteensa sekä heidän lapsiinsa ja anna heille lupauksia.» Mutta saatana ei anna lupauksia muuten kuin pettääkseen.');
INSERT INTO "finnish" VALUES(17,65,'17:65','»Mutta minun palvelijoihini sinulla totisesti ei ole mitään käskynvaltaa, ja Herrasi on riittävä vartija.»');
INSERT INTO "finnish" VALUES(17,66,'17:66','Kuka muu kuin Herra työntää laivanne vesille etsiäksenne Hänen armonantimiaan. Totisesti, Hän on armollinen teitä kohtaan.');
INSERT INTO "finnish" VALUES(17,67,'17:67','Ja kun onnettomuus kohtaa teitä merellä, niin silloin muut, joita avuksenne kutsutte, häipyvät silmistänne paitsi Jumala. Mutta kun Hän pelastaa teidät maihin, niin te käännytte Hänestä pois. Ihminen on perin kiittämätön.');
INSERT INTO "finnish" VALUES(17,68,'17:68','Oletteko varmoja, ettei Hän anna maan haudata teitä alleen tai lähetä teille hirmumyrskyä? Silloin ette löydä itsellenne ketään suojelijaa.');
INSERT INTO "finnish" VALUES(17,69,'17:69','Tahi oletteko varmoja, ettei Hän vie teitä joskus uudelleen merelle ja lähetä vastaanne myrskytuulta ja hukuta teitä epäuskonne vuoksi? Silloin ette löydä ketään, joka puolestanne kostaisi Meille.');
INSERT INTO "finnish" VALUES(17,70,'17:70','Totisesti, Me olemme saattanut ihmisen lapsia kunniaan ja antanut heille kulkuneuvoja sekä maalla että merellä. Ja olemme varustanut heidät puhtaalla ravinnolla sekä asettanut heidät useimpien muitten luotujen yläpuolelle.');
INSERT INTO "finnish" VALUES(17,71,'17:71','Tulee päivä, jona kutsumme kaikki kansat johtajineen eteemme tilille. Ja silloin kukin, jolle on annettu oma kirjansa oikeaan käteen, lukee kirjastaan, eikä heille tehdä vääryyttä hivenen vertaa.');
INSERT INTO "finnish" VALUES(17,72,'17:72','Mutta ken on sokea tässä elämässä, on sentakia sokea myös tulevassa elämässä ja aivan harhaantunut oikealta tieltä.');
INSERT INTO "finnish" VALUES(17,73,'17:73','Ja he olisivat halunneet houkutella sinua luopumaan siitä, mitä sinulle olimme ilmaissut, tarkoittaen että olisit keksinyt panetteluja Meitä vastaan, ja katso, siinä tapauksessa he olisivat ottaneet sinut läheiseksi ystäväkseen.');
INSERT INTO "finnish" VALUES(17,74,'17:74','Totisesti, jos emme olisi sinua vahvistanut, olisit heidän puoleensa vähän taipunutkin.');
INSERT INTO "finnish" VALUES(17,75,'17:75','Silloin olisimme antanut sinun maistaa kaksinkertaisesti elämässä ja kaksinkertaisesti kuolemassa, etkä olisi silloin saanut ainoatakaan auttajaa Meitä vastaan.');
INSERT INTO "finnish" VALUES(17,76,'17:76','He olisivat totisesti toivoneet sinun lankeavan, jotta olisivat sitten voineet karkoittaa sinut maasta, mutta siinä tapauksessa he itse eivät olisi viipyneet kauankaan sinun jälkeesi.');
INSERT INTO "finnish" VALUES(17,77,'17:77','Tällainen oli käskymme, kun lähetimme ennen sinua sanamme julistajia, ja sinun suhteesi eivät käskymme muutu.');
INSERT INTO "finnish" VALUES(17,78,'17:78','Suorita rukouksesi, oi Muhammed, päivän noususta yön pimeyteen saakka ja lue Koraania aamun koitteessa. Totisesti, ei aamuhartaus jää kuulematta.');
INSERT INTO "finnish" VALUES(17,79,'17:79','Ole valveilla osa yöstä; tämä olkoon lisätehtäväsi. Ehkä Herrasi nostaa sinut kunniakkaaseen asemaan.');
INSERT INTO "finnish" VALUES(17,80,'17:80','Ja sano: »Herra, salli minun käydä sisälle ja ulos aina totuuden tietä, ja suo minulle voimakasta apuasi.»');
INSERT INTO "finnish" VALUES(17,81,'17:81','Ja sano vielä: »Totuus on tullut ja valhe on häipynyt. Totisesti, valhe on tuomittu katoamaan.»');
INSERT INTO "finnish" VALUES(17,82,'17:82','Ja Me lähetämme ylhäältä sellaista Koraanin sisältöä, mikä on parannukseksi ja armoksi uskovaisille, mutta vain edistää väärämielisten, häviötä.');
INSERT INTO "finnish" VALUES(17,83,'17:83','Ja kun Me lähetämme siunauksemme jollekin ihmiselle, hän kääntyy pois ja vetäytyy syrjään, mutta kun häntä kohtaa onnettomuus, hän joutuu epätoivoon.');
INSERT INTO "finnish" VALUES(17,84,'17:84','Sano: »Jokainen toimii omalla tavallaan, mutta Herranne tietää parhaiten, kuka on helpoimmin johdateltavissa oikealle tielle.»');
INSERT INTO "finnish" VALUES(17,85,'17:85','He tekevät sinulle myöskin kysymyksiä Hengen suhteen. Vastaa: »Henki lähtee Herrani sanoista, mutta teille on annettu sangen vähän oikeata tietoa.»');
INSERT INTO "finnish" VALUES(17,86,'17:86','Ja jos olisimme halunnut, olisimme totisesti voinut peruuttaa sen, minkä olimme sinulle ilmoittanut, etkä olisi silloin löytänyt ketään, joka olisi sinua auttanut Meitä vastaan.');
INSERT INTO "finnish" VALUES(17,87,'17:87','Mutta sait armon Herraltasi. Totisesti, suuri on Hänen laupeutensa sinua kohtaan.');
INSERT INTO "finnish" VALUES(17,88,'17:88','Sano: »Totisesti, vaikka kaikki ihmiset ja dzinnit yhdessä yrittäisivät saada aikaan tämänkaltaisen Koraanin, niin he eivät kykenisi siihen, vaikkapa auttaisivatkin toisiaan.»');
INSERT INTO "finnish" VALUES(17,89,'17:89','Totisesti, Me olemme tässä Koraanissa esittänyt erilaisia vertauksia ihmisille, mutta useimmat kieltäytyvät omaksumasta muuta kuin epäuskon.');
INSERT INTO "finnish" VALUES(17,90,'17:90','Ja he sanovat: »Emme todellakaan usko sinuun, ellet saa lähdettä suihkuamaan maasta;');
INSERT INTO "finnish" VALUES(17,91,'17:91','tai ellet voi aikaansaada itsellesi puutarhaa palmupuineen ja viiniköynnöksineen ja anna jokien virrata sen halki;');
INSERT INTO "finnish" VALUES(17,92,'17:92','tai ellet saa taivasta hajoamaan päällemme, kuten uskot; tai ellet voi tuoda Jumalaa ja enkeleitä eteemme todistajiksi,');
INSERT INTO "finnish" VALUES(17,93,'17:93','tai ellet hanki itsellesi kultaista taloa; tai ellet astu taivaaseen. Mutta emme usko taivaaseenastumiseesi, ellet ylhäältä lähetä meille Kirjaa, jota saatamme lukea.» Vastaa heille: »Kunnia olkoon Herralle, olen vain ihmisolento, pelkkä lähettiläs.»');
INSERT INTO "finnish" VALUES(17,94,'17:94','Mikään muu ei estä ihmisiä uskomasta - johdatuksen saatuaan - kuin se, että he sanovat: »Asettaako Jumala ihmisolennon lähettilääksi?»');
INSERT INTO "finnish" VALUES(17,95,'17:95','Vastaa: »Jos olisi sopivaa, että maan päällä vaeltaisi enkeleitä, olisimme kyllä lähettänyt heille enkelin sanansaattajaksi.»');
INSERT INTO "finnish" VALUES(17,96,'17:96','Sano edelleen: »Jumala on riittävä todistaja minun puolestani teitä vastaan; totisesti, Hän tietää palvelijainsa asiat ja pitää heitä silmällä.»');
INSERT INTO "finnish" VALUES(17,97,'17:97','Ja ken Jumalan johtoon alistuu, nauttii oikeata johdatusta, mutta ken on kadotuksen oma, hänelle et löydä muuta suojelijaa Jumalan asemesta. Ja ylösnousemuksen päivänä Me kokoamme heidät yhteen, ja he makaavat kasvoillaan, sokeina, mykkinä ja kuuroina. Heidän asuntonsa on helvetti; joka kerran, kun sen tuli pienenee, Me lisäämme sen liekkiä.');
INSERT INTO "finnish" VALUES(17,98,'17:98','Tämä on heidän palkkansa, koska he kielsivät Meidän merkkimme ja sanoivat: »Kuinka meidät herätettäisiin uuteen elämään senjälkeen kuin olemme muuttuneet luiksi ja tomuksi?»');
INSERT INTO "finnish" VALUES(17,99,'17:99','Eivätkö he ole vieläkään älynneet, että Jumala, joka on luonut taivaat ja maan, kykenee myös luomaan heidän kaltaisiaan? Olemme myös asettanut heille määräajan, josta ei ole epäilystä, mutta väärämieliset kieltäytyvät omaksumasta muuta kuin epäuskonsa.');
INSERT INTO "finnish" VALUES(17,100,'17:100','Sano: »Jos teillä olisi hallussanne Herrani armoaarteet, niin totisesti pitäisitte ne lukon takana pelosta, että ne ehtyisivät. Ihminen on taipuvainen itaruuteen.»');
INSERT INTO "finnish" VALUES(17,101,'17:101','Totisesti, Me annoimme Moosekselle yhdeksän selvää merkkiä. Kysykää siis Israelin lapsilta, kun hän saapui heidän luokseen ja Farao sanoi hänelle: »Mooses, minusta tuntuu totisesti siltä, että olet noiduttu.»');
INSERT INTO "finnish" VALUES(17,102,'17:102','Hän vastasi: »Totisesti, sinä tiedät, ettei muu kuin taivaitten ja maan Herra ole lähettänyt näitä merkkejä opastukseksi,ja minä uskon, Farao, että sinä kuulut kadotettujen joukkoon.»');
INSERT INTO "finnish" VALUES(17,103,'17:103','Silloin Farao tahtoi ajaa heidät maasta, mutta Me hukutimme hänet ja kaikki hänen mukanaan olleet.');
INSERT INTO "finnish" VALUES(17,104,'17:104','Tämän jälkeen Me sanoimme Israelin lapsille: »Asettukaa asumaan pyhään maahan, ja kun tulevaisuuden lupaus täytetään, Me saatamme teidät kaikki yhteen.»');
INSERT INTO "finnish" VALUES(17,105,'17:105','Me olemme Koraanin lähettänyt ylhäältä totuuden sanomana, ja totuuden sanomana se on saapunut maan päälle, ja Me olemme lähettänyt sinut vain ilosanoman tuojaksi ja varoittajaksi.');
INSERT INTO "finnish" VALUES(17,106,'17:106','Ja tämän Koraanin Me olemme jakanut osiin, jotta vähitellen lukisit sen ihmisille. Ja Me olemme järjestänyt sen julistamisen siten, että se on osa osalta tullut ylhäältä.');
INSERT INTO "finnish" VALUES(17,107,'17:107','Sano: »Voitte uskoa siihen tai olla uskomatta. Totisesti, ne, joille jo sitä ennen on annettu tieto, lankeavat kunnioituksesta kasvoilleen, kun kuulevat sitä luettavana');
INSERT INTO "finnish" VALUES(17,108,'17:108','Ja he lausuvat: »Kunnia olkoon Herrallemme! Hänen lupauksensa on totisesti täyttyvä.»');
INSERT INTO "finnish" VALUES(17,109,'17:109','Ja kyynelsilmin he lankeavat kasvoilleen, sillä se on lisännyt heidän nöyryyttään.');
INSERT INTO "finnish" VALUES(17,110,'17:110','Sano: »Rukoilkaa Häntä Jumalana tai rukoilkaa Häntä armahtajana; millä nimellä Häntä rukoillettekin, niin totta on, että kaikki hyvät nimet kuuluvat Hänelle. Äläkä rukoillessasi korota liiaksi ääntäsi äläkä myöskään laske sitä liian matalaksi, vaan koeta löytää keskitie näiden väliltä.»');
INSERT INTO "finnish" VALUES(17,111,'17:111','Sano myöskin: »Kunnia ja ylistys Jumalalle, joka ei ole hankkinut itselleen lasta ja jolla ei valtasuuruudessaan ole ketään tasavertaista. Ei Hänellä myöskään ole liittolaista alamaisten keskuudessa.» Julistakaa Hänen suuruuttaan ylistäen.');
INSERT INTO "finnish" VALUES(18,1,'18:1','Kaikki ylistys kuuluu Jumalalle, joka on palvelijalleen ylhäältä lähettänyt tämän Kirjan eikä ole päästänyt siihen minkäänlaista vilppiä.');
INSERT INTO "finnish" VALUES(18,2,'18:2','Hän on laatinut sen suorasukaiseksi, jotta se varoittaisi heitä Häneltä tulevasta ankarasta rangaistuksesta, mutta myös toisi ilosanoman uskovaisille, jotka harjoittavat hyviä tekoja, että heitä odottaa ihana palkka, josta he alati saavat nauttia.');
INSERT INTO "finnish" VALUES(18,3,'18:3','Se on varoituksena myös niille, jotka sanovat Jumalan hankkineen lapsen.');
INSERT INTO "finnish" VALUES(18,4,'18:4','Heillä ei ole tietoa enempää kuin heidän esi-isilläänkään.');
INSERT INTO "finnish" VALUES(18,5,'18:5','Heidän suustaan tulee arveluttavaa puhetta: heidän sanansa ovat pelkkää valhetta.');
INSERT INTO "finnish" VALUES(18,6,'18:6','Ehkä sinä ahdistat itseäsi murehtimalla heidän tähtensä, jos he eivät usko tähän Koraaniin.');
INSERT INTO "finnish" VALUES(18,7,'18:7','Kaiken, mitä maan päällä on, Me olemme luonut koristukseksi, koetellaksemme, kuka heistä on teoissaan etevin.');
INSERT INTO "finnish" VALUES(18,8,'18:8','Totisesti, kaiken mitä maan päällä on, Me muutamme autioksi erämaaksi.');
INSERT INTO "finnish" VALUES(18,9,'18:9','Arveletko sinä, että Luolassa asuneet sekä kirjoitustaulu ovat ihmemerkkejämme?');
INSERT INTO "finnish" VALUES(18,10,'18:10','Kun joukko nuoria miehiä pakeni Luolaan huutaen: »Herra, suo meille armosi ja osoita meille oikea suunta yrityksessämme!»');
INSERT INTO "finnish" VALUES(18,11,'18:11','niin Me ummistimme heidän korvansa luolassa useiksi vuosiksi.');
INSERT INTO "finnish" VALUES(18,12,'18:12','Sitten Me saatoimme heidät ennalleen voidaksemme erottaa, kumpi kahdesta ryhmästä osaisi paremmin arvioida siellä viettämänsä ajan pituuden.');
INSERT INTO "finnish" VALUES(18,13,'18:13','Kerromme sinulle nyt totuudenmukaisesti, oi Muhammed, heidän tarinansa. Totisesti, he olivat nuoria miehiä, jotka uskoivat Herraansa, ja me vahvistimme heitä johdatuksessa.');
INSERT INTO "finnish" VALUES(18,14,'18:14','Ja Me vahvistimme heidän sydämiänsä, kun he nousivat ja lausuivat: »Herramme on taivaitten ja maan Herra. Emme milloinkaan huuda avuksemme ketään muuta jumalaa kuin Häntä, sillä jos sellaista tekisimme, puhuisimme törkeätä valhetta.');
INSERT INTO "finnish" VALUES(18,15,'18:15','Kansamme on ottanut Jumalan rinnalle muita jumalia. Miksi he eivät esitä heistä mitään selvää todistusta? Kuka onkaan väärämielisempi kuin se, joka sepittää valheita Jumalasta?');
INSERT INTO "finnish" VALUES(18,16,'18:16','Muistakaa, miten erkanitte heistä ja siitä, mitä he palvelivat Jumalan ohella. - Paetkaa siis luolaan, ja Jumala on osoittava teille armoansa ja antava yrityksenne menestyä.»');
INSERT INTO "finnish" VALUES(18,17,'18:17','Olisit voinut nähdä, miten aurinko noustessaan kääntyi oikealle heidän luolastaan ja laskiessaan jätti heidät vasemmalle puolelleen. Ja he makasivat luolan onkalossa. Tämä on eräs Jumalan merkeistä. Kenelle Jumala antaa johdatuksensa, hän kulkee oikeata tietä, mutta ken on kadotettu, hänelle et löydä ketään auttajaa, joka saisi hänet oikealle tielle.');
INSERT INTO "finnish" VALUES(18,18,'18:18','Voisit ehkä arvella, että he olivat valveilla, vaikka he nukkuivat, sillä Me annoimme heidän kääntyä vuoroin oikealle, vuoroin vasemmalle kyljelleen, ja heidän koiransa makasi käpälät ojennettuina kynnyksellä. Jos sinä olisit katsahtanut heihin, olisit varmasti kääntynyt ympäri ja kiiruhtanut pois, ja mielesi olisi täyttynyt pelolla heidän tähtensä.');
INSERT INTO "finnish" VALUES(18,19,'18:19','Ja sitten Me herätimme heidät, jotta he olisivat voineet kysellä toisiltaan. Eräs heistä lausui: »Kauanko olette viipyneet täällä?» He vastasivat: »Olemme viipyneet vain päivän tai osan siitä.» Toiset sanoivat: »Herranne tietää parhaiten, miten kauan olette viipyneet, mutta lähettäkää joku joukostanne kaupunkiin mukanaan nämä rahat. Hän katsokoon, kenellä kaupungin asukkaista on puhtaimpia elintarpeita, ja tuokoon niistä osan tänne. Mutta olkoon hän varovainen älköönkä mainitko teistä kenellekään.');
INSERT INTO "finnish" VALUES(18,20,'18:20','Sillä totisesti, jos he teidät saavat valtaansa, he kivittävät teidät kuoliaaksi tai pakottavat kääntymään uskoonsa, ja silloin ette voi enää koskaan tehdä hyvää.»');
INSERT INTO "finnish" VALUES(18,21,'18:21','Tällä tavoin Me saatoimme ilmi heidän asiansa, jotta ihmiset, jotka kiistelivät keskenään heistä, tietäisivät, että Jumalan lupaus on tosi ja että viimeisen hetken tulemisesta ei ole epäilystä. He sanoivat: »Pystyttäkää rakennus heidän päällensä! Heidän Herransa tuntee heidät parhaiten. »Ne, jotka olivat määrääviä heidän asioissansa, sanoivat: »Totisesti, me rakennamme moskeijan heidän päällensä.»');
INSERT INTO "finnish" VALUES(18,22,'18:22','He sanovat nyt: »Kolme oli heitä (nukkujia), neljäs oli heidän koiransa.» He sanovat myös: »Viisi oli, kuudes oli heidän koiransa.» He arvailevat näkymättömiä asioita ja sanovat: »Seitsemän oli, ja kahdeksas oli heidän koiransa.» Sano: »Herrani tietää parhaiten heidän lukumääränsä, vain harvat tuntevat heidät. Älä kiistele heidän kanssansa niistä paitsi kenties pätevästä syystä, äläkä keneltäkään kysele heidän asioitaan.');
INSERT INTO "finnish" VALUES(18,23,'18:23','Äläkä puhu mistään näin: »Totisesti, tämän minä olen tekevä huomenna», lisäämättä: »jos Jumala niin sallii.»');
INSERT INTO "finnish" VALUES(18,24,'18:24','Ja milloin ikinä satut sen unohtamaan, niin muista Herraasi ja sano: »Ehkä Herra johdattaa minut tielle, joka on oikeampi kuin tämä.»');
INSERT INTO "finnish" VALUES(18,25,'18:25','Ja he viipyivät luolassaan kolmesataa vuotta ja yhdeksän lisäksi.');
INSERT INTO "finnish" VALUES(18,26,'18:26','Sano: »Jumala tietää parhaiten, miten kauan he viipyivät. Hänelle kuuluvat taivaitten ja maan salaisuudet. Kuinka oivallinen onkaan Hänen näkönsä ja kuinka oivallinen Hänen kuulonsa. Heillä ei ole ketään suojelijaa paitsi Hän, eikä kukaan tiedä Hänen ratkaisuistaan.»');
INSERT INTO "finnish" VALUES(18,27,'18:27','Lue heille, mitä sinulle on ilmoitettu Herrasi kirjasta. Hänen sanaansa ei voida muuttaa, etkä sinä paitsi Häntä löydä mitään turvaa.');
INSERT INTO "finnish" VALUES(18,28,'18:28','Ole sävyisä niitä kohtaan, jotka aamuin ja illoin rukoilevat Herraansa halaten Hänen läheisyyttään. Älkööt silmäsi kääntykö pois heistä himoitessaan tämän maallisen elämän koreuksia. Älä myöskään tottele sitä, jonka sydämen Me olemme sulkenut Meitä ajattelemasta, joka on kulkenut himojensa teitä ja jonka pahatteot ylittävät kaikki rajat.');
INSERT INTO "finnish" VALUES(18,29,'18:29','Sano: »Totuus on tullut Herraltanne; ken tahansa sitä haluaa uskoa, hän uskokoon, ja ken tahansa tahtoo epäillä, antakaa hänen epäillä. Totisesti, väärämielisiä varten Me olemme valmistanut tulen, joka aitauksena heidät ympäröi. Ja jos he rukoilevat apua, niin he avukseen saavat vettä, joka on kuin sulaa vaskea ja polttaa heidän kasvonsa. Kauhea on heidän juomansa ja surkea heidän leposijansa.');
INSERT INTO "finnish" VALUES(18,30,'18:30','Mutta mitä niihin tulee, jotka uskovat ja harjoittavat hyväntekeväisyyttä, niin me totisesti emme haaskaa sen palkkaa, joka hyvää tekee.');
INSERT INTO "finnish" VALUES(18,31,'18:31','Heitä varten ovat autuaat puutarhat, joissa joet virtaavat. Siellä he saavat kantaa kultaisia rannerenkaita ja ovat puettuna vihreihin vaippoihin, jotka ovat hienoa ja paksua silkkiä, ja he istuvat valtaistuimella. Oivallinen on heidän palkkansa ja mitä ihanin heidän leposijansa.');
INSERT INTO "finnish" VALUES(18,32,'18:32','Esitä heille vertaus kahdesta miehestä: Me olimme antanut toiselle heistä kaksi viinitarhaa, joita taatelipalmut ympäröivät, ja niiden välille sijoitimme vihreitä vainioita.');
INSERT INTO "finnish" VALUES(18,33,'18:33','Kumpikin puutarha tuotti hedelmänsä, eikä siellä ollut mitään vajavaista. Ja puutarhojen uomiin Me johdimme virran.');
INSERT INTO "finnish" VALUES(18,34,'18:34','Ja niiden omistajalla oli sadonkorjuun aika. Ja hän lausui naapurilleen keskustellessaan hänen kanssaan: »Totisesti, minulla on enemmän rikkautta kuin sinulla ja olen kunnioitetumpi ihmisten keskuudessa.»');
INSERT INTO "finnish" VALUES(18,35,'18:35','Ja hän astui puutarhaansa väärämielisyys sydämessään. Hän sanoi: »En usko, että tämä milloinkaan häviää,');
INSERT INTO "finnish" VALUES(18,36,'18:36','enkä myöskään, että viimeinen Hetki koskaan tulee. Mutta jos minun kuitenkin on palattava Herrani luo, olen varmasti löytävä paremman paikan kuin tämä.»');
INSERT INTO "finnish" VALUES(18,37,'18:37','Hänen naapurinsa, joka keskusteli hänen kanssaan, vastasi: »Etkö usko Häntä, joka sinut loi tomusta ja sitten siemenestä ja muovaili sinut täydelliseksi ihmiseksi?');
INSERT INTO "finnish" VALUES(18,38,'18:38','Minä pidän Häntä Herranani enkä aseta ketään muuta Hänen rinnalleen.');
INSERT INTO "finnish" VALUES(18,39,'18:39','Ja miksi et sanonut astuessasi puutarhaasi: (tapahtukoon) mitä Jumala tahtoo. Ilman Hänen apuaan minulla ei ole mitään voimaa. Jos arvelet minun olevan köyhemmän mitä rikkauteen ja lapsiin tulee,');
INSERT INTO "finnish" VALUES(18,40,'18:40','niin voi käydä niin, että Herrani antaa minulle parempaa kuin sinun puutarhasi ja lähettää tulen taivaasta sen tuhoksi, niin että se tulee erämaaksi, jossa ei kasva mitään.');
INSERT INTO "finnish" VALUES(18,41,'18:41','Tai Hän voi kuivata sen veden, niin ettet saa sitä mistään.»');
INSERT INTO "finnish" VALUES(18,42,'18:42','Niinpä tuhoutuikin hänen hedelmäsatonsa, ja hän väänteli käsiään ajatellessaan, mitä kaikkea hän siihen oli kuluttanut; myös puut runkoineen sortuivat maahan, ja hän sanoi: »Voi, jospa en olisi asettanut ketään Herrani rinnalle.»');
INSERT INTO "finnish" VALUES(18,43,'18:43','Eikä ollut joukkoa, joka Jumalasta riippumatta olisi häntä auttanut, eikä hän apua saanut.');
INSERT INTO "finnish" VALUES(18,44,'18:44','Katso, apu tulee totuuden Jumalalta: Hän on paras palkitsemisessa ja Hän antaa parhaan lopun.');
INSERT INTO "finnish" VALUES(18,45,'18:45','Esitä heille myös vertaus tämän maailman elämästä! Se on kuin sade, jonka Me taivaasta alas lähetämme, niin että se imeytyy maan kasveihin, mutta sitten kaikki kuivuu korsiksi, jotka tuuli puhaltaa hajalleen. Jumala on väkevä yli kaiken.');
INSERT INTO "finnish" VALUES(18,46,'18:46','Rikkaus ja lapset ovat tämän maailman aarteita, mutta jatkuvat hyvät teot antavat Herrasi edessä paremman palkkion ja toivon.');
INSERT INTO "finnish" VALUES(18,47,'18:47','Ja tulee päivä, jolloin Me saatamme vuoret siirtymään ja jolloin näet maan autiona tasankona, ja silloin Me kokoamme heidät yhteen, emmekä ketään heistä unohda.');
INSERT INTO "finnish" VALUES(18,48,'18:48','Ja heidät asetetaan järjestynein rivein seisomaan Herrasi eteen, ja Me sanomme heille: »Totisesti, nyt tulette eteemme samanlaisina, joiksi Me teidät alussa loimme, mutta te kuvittelitte, ettemme milloinkaan asettaisi teille määräaikaan');
INSERT INTO "finnish" VALUES(18,49,'18:49','Ja sitten otetaan esille kirja, ja sinä näet syntisten pelkäävän sitä, mitä se sisältää. Ja he sanovat: »Voi meitä, millainen kirja tämä onkaan! Siinä ei ole unohdettu pientä eikä suurta, vaan kaikki on siihen merkitty.» Ja silmiensä edessä he näkevät kaiken, mitä ovat tehneet. Herrasi ei tee vääryyttä kenellekään.');
INSERT INTO "finnish" VALUES(18,50,'18:50','Kun Me lausuimme enkeleille: »Kumartakaa ihmistä, niin he kumartuivat kaikki paitsi saatana. Hän oli dzinni ja siksi tottelematon Herransa käskylle. Otatteko siis hänet ja hänen jälkeläisensä suojelijoiksenne mieluummin kuin Minut? He ovat teidän vihollisianne. Onneton on tämä vaihdos väärämielisille.');
INSERT INTO "finnish" VALUES(18,51,'18:51','En kutsunut heitä todistajina läsnäolemaan taivaita ja maata luodessani enkä heitä itseänsäkään luodessani. En myöskään kutsunut kansan viettelijöitä ojentamaan minulle auttavaa kättänsä.');
INSERT INTO "finnish" VALUES(18,52,'18:52','Ja kun se päivä tulee, jolloin Hän sanoo: »Kutsukaa nyt niitä, joita kuvittelette vertaisikseni!» ja he kutsuvat, niin nämä eivät heille vastaa, koska Me nostamme kuolettavan vihan heidän välillensä.');
INSERT INTO "finnish" VALUES(18,53,'18:53','Syntiset näkevät tulen ja tietävät, että heidät syöstään siihen, eivätkä he löydä paikkaa, minne pelastautua.');
INSERT INTO "finnish" VALUES(18,54,'18:54','Ja totisesti, tässä Koraanissa Me olemme ihmissuvulle erilaisin vertauksin selittänyt kaiken, mutta ihminen on luotujen joukossa suurin kiistelijä.');
INSERT INTO "finnish" VALUES(18,55,'18:55','Mikään ei estä ihmisiä uskomasta, saatuaan johdatuksen, eikä anomasta anteeksiantoa Herraltansa ennen kuin muinaisten kansojen kohtalo toistuu heillekin ja tuskantuomio lankeaa heille heidän silmiensä edessä.');
INSERT INTO "finnish" VALUES(18,56,'18:56','Emme Me lähettiläitä muuta varten lähettänyt kuin ilosanomain tuojiksi ja varoittajiksi, mutta epäuskoon antautuneet kiistelevät valheellisin puhein väärentääkseen siten totuuden. Ja he pitävät Minun merkkejäni ja heidän varoittamistaan leikkinä.');
INSERT INTO "finnish" VALUES(18,57,'18:57','Kuka on väärämielisempi kuin se, joka kääntyy pois, kun häntä muistutetaan Herransa merkeistä, ja unohtaa, mitä hänen kaksi kättänsä ovat tehneet? Totisesti, Me olemme pannut verhot heidän sydäntensä ylle, jotta he eivät sitä ymmärtäisi, ja olemme sulkenut heidän korvansa. Ja jos sinä kutsut heitä oikealle tielle, he eivät konsanaan anna itseänsä opastaa.');
INSERT INTO "finnish" VALUES(18,58,'18:58','Herrasi on kaikkein anteeksiantavaisin, laupeuden haltija. Jos Hän olisi käynyt heihin käsiksi heidän tekojensa mukaan, Hän olisi jouduttanut heidän tuomiotaan. Mutta heille on määrätty hetkensä, jota he eivät kykene välttämään.');
INSERT INTO "finnish" VALUES(18,59,'18:59','Ja kun Me hävitimme ne kaupungit, joiden asukkaat olivat tehneet syntiä, se tapahtui siksi, että olimme määrännyt niiden hävityksen hetken.');
INSERT INTO "finnish" VALUES(18,60,'18:60','Ja Mooses lausui nuorelle palvelijalleen: »En pysähdy ennen kuin saavutan kahden meren yhtymiskohdan, vaikka saisin kulkea vuosikausia.»');
INSERT INTO "finnish" VALUES(18,61,'18:61','Mutta kun he saavuttivat yhtymiskohdan, he unohtivat kalansa, niin että se löysi tiensä mereen aukon kautta.');
INSERT INTO "finnish" VALUES(18,62,'18:62','Kun he sitten olivat päässeet siitä ohi, Mooses lausui palvelijalleen: »Ota esille aamiaisemme; totisesti, me olemme rasittuneet matkallamme.»');
INSERT INTO "finnish" VALUES(18,63,'18:63','Palvelija vastasi: »Huomasitko mitään, kun lepäsimme kalliolla? Minä unohdin sinne kalan, eikä kukaan muu kuin saatana saanut minua unohtamaan sitä, ja se löysi tiensä mereen perin ihmeellisellä tavalla.»');
INSERT INTO "finnish" VALUES(18,64,'18:64','Mooses sanoi: »Tuolla on paikka, jota etsimme.» Sitten he palasivat takaisin seuraten jälkiään.');
INSERT INTO "finnish" VALUES(18,65,'18:65','Perillä he tapasivat erään palvelijamme, jolle Me olimme osoittanut armoamme ja jolle olimme opettanut omaa viisauttamme.');
INSERT INTO "finnish" VALUES(18,66,'18:66','Mooses sanoi hänelle: »Saanko minä seurata sinua sillä ehdolla, että opetat minulle sellaista, mitä itsellesi on opetettu Oikeasta Tiestä?»');
INSERT INTO "finnish" VALUES(18,67,'18:67','Hän vastasi: »Totisesti, sinä et kykene pysymään kärsivällisenä minun kanssani.');
INSERT INTO "finnish" VALUES(18,68,'18:68','Ja miten voisitkaan olla kärsivällinen asioissa, joita sinun tietämykseni ei käsitä?»');
INSERT INTO "finnish" VALUES(18,69,'18:69','Mooses lausui: »Tulet kyllä huomaamaan, että minä, jos Jumala niin sallii, pysyn kärsivällisenä enkä ole sinulle tottelematon missään asiassa.»');
INSERT INTO "finnish" VALUES(18,70,'18:70','Hän vastasi: »Jos siis minua seuraat, älä kysele minulta mitään, ennen kuin minä siitä sinulle puhun.»');
INSERT INTO "finnish" VALUES(18,71,'18:71','Sitten he kulkivat tietään, kunnes astuivat erääseen veneeseen, johon hän teki reiän. Mooses kysyi: »Teetkö siihen reiän hukuttaaksesi sen matkustajat? Totisesti, oletpa menetellyt perin omituisesti.»');
INSERT INTO "finnish" VALUES(18,72,'18:72','Toinen vastasi: »Enkö sinulle sanonut, ettet kykenisi tyynesti kestämään minun rinnallani?»');
INSERT INTO "finnish" VALUES(18,73,'18:73','Mooses sanoi: »Älä soimaa minua siitä, minkä unohdin, äläkä tee taakkaani minulle liian vaikeaksi.»');
INSERT INTO "finnish" VALUES(18,74,'18:74','Sitten he jatkoivat matkaansa, kunnes kohtasivat pojan, jonka hän surmasi. Mooses sanoi: »Oletko surmannut viattoman henkilön, joka ei ole ketään tappanut? Totisesti, olet tehnyt pahan työn.»');
INSERT INTO "finnish" VALUES(18,75,'18:75','Hän vastasi: »Enkö sinulle sanonut, ettet kykenisi tyynesti kestämään minun rinnallani?»');
INSERT INTO "finnish" VALUES(18,76,'18:76','Mooses sanoi: »Jos minä tämän jälkeen sinulta vielä jotakin kyselen, älä minua enää opasta. Olet saanut minulta riittävän anteeksipyynnön.»');
INSERT INTO "finnish" VALUES(18,77,'18:77','Sitten he jatkoivat matkaansa, kunnes saapuivat erään kaupungin asukkaitten luokse ja pyysivät heiltä ruokaa, mutta nämä kieltäytyivät ottamasta heitä vieraakseen. Silloin he tapasivat kaupungissa muurin, joka oli sortumaisillaan, ja he korjasivat sen tukevaksi. Mooses lausui: »Jos olisit tahtonut, olisit saanut siitä työpalkan.»');
INSERT INTO "finnish" VALUES(18,78,'18:78','Toinen vastasi: »Tässä tulee meille ero. Annan nyt sinulle selityksen siitä, mitä et kyennyt odottamaan.');
INSERT INTO "finnish" VALUES(18,79,'18:79','Vene kuului köyhille ihmisille, jotka raatavat merellä. Minä halusin vioittaa sen, koska heitä ahdisti kuningas, joka väkivalloin otti haltuunsa kaikki veneet.');
INSERT INTO "finnish" VALUES(18,80,'18:80','Pojalla taas oli uskovaiset vanhemmat, ja me estimme hänet tekemästä heille väkivaltaa kapinallisuudessaan ja uskottomuudessaan.');
INSERT INTO "finnish" VALUES(18,81,'18:81','Samalla me toivoimme, että heidän Herransa antaisi heille hänen asemestaan pojan, joka olisi viattomampi ja lähempänä armoa.');
INSERT INTO "finnish" VALUES(18,82,'18:82','Muuri kuuluu kahdelle kaupungin orvolle pojalle. Muurin juurella on aarre heitä varten, ja heidän isänsä oli hyvä mies. Herrasi tahtoi, että he ensin tulisivat täysi-ikäisiksi ja sitten ottaisivat aarteen Herrasi armona. Enkä minä tehnyt tätä kaikkea omasta aloitteestani: tämä on selitys siihen, mitä sinä et kyennyt odottamaan.»');
INSERT INTO "finnish" VALUES(18,83,'18:83','Ihmiset kyselevät sinulta myös tietoja Kaksisarvisesta. Sano: »Olen kertova teille jotakin hänestä.»');
INSERT INTO "finnish" VALUES(18,84,'18:84','Totisesti, Me perustimme hänen maallisen valtansa ja annoimme hänelle kaikkia apuneuveja.');
INSERT INTO "finnish" VALUES(18,85,'18:85','Hän eteni yhtä suuntaa, kunnes saapui sinne, missä aurinko laskee, ja');
INSERT INTO "finnish" VALUES(18,86,'18:86','näki sen laskevan kuumaan lähteeseen, ja hän tapasi lähistöllä ihmisiä.');
INSERT INTO "finnish" VALUES(18,87,'18:87','Me lausuimme: »Oi Kaksisarvinen. Sinä voit tuottaa heille kärsimyksiä tai saatat löytää jotakin hyvää heistä.» Hän vastasi: »Sitä me kuritamme, joka on väärämielinen, ja sitten lähetämme hänet takaisin Herransa luokse, niin että Hän voi rangaista häntä ankaralla kärsimyksellä.');
INSERT INTO "finnish" VALUES(18,88,'18:88','Mutta sitä, joka uskoo ja tekee hyvää, odottaa runsas palkka, ja lempeällä äänellä Me annamme hänelle käskymme.»');
INSERT INTO "finnish" VALUES(18,89,'18:89','Sitten hän kulki toista suuntaa,');
INSERT INTO "finnish" VALUES(18,90,'18:90','kunnes saapui paikkaan, jossa aurinko nousee, ja huomasi sen nousevan ihmisten yli, joille Me emme ollut antanut mitään varjosuojaa auringon puolelle.');
INSERT INTO "finnish" VALUES(18,91,'18:91','Näin oli asia. Je Me tiesimme täydellisesti, miten hänen laitansa oli.');
INSERT INTO "finnish" VALUES(18,92,'18:92','Sitten hän kulki vielä yhtä suuntaa, kunnes saapui kahden kalliomuurin väliseen laaksoon ja');
INSERT INTO "finnish" VALUES(18,93,'18:93','tapasi näiden vuorten takana ihmisiä, jotka saattoivat tuskin ymmärtää ainoatakaan sanaa.');
INSERT INTO "finnish" VALUES(18,94,'18:94','He sanoivat: »Oi Kaksisarvinen, totisesti, Goog ja Maagog levittävät pahaa maan päällä; onko meidän suoritettava sinulle veroa sillä ehdolla, että rakennat muurin heidän ja meidän välillemme?»');
INSERT INTO "finnish" VALUES(18,95,'18:95','Hän vastasi: »Se (valtakunta veroineen), minkä Herrani on minulle perustanut, on minulle paras, joten riittää, kun autatte minua työvoimalla, niin että voin rakentaa lujan muurin teidän ja heidän välilleen.');
INSERT INTO "finnish" VALUES(18,96,'18:96','Tuokaa minulle rautamöhkäleitä.» (Niitä tuotiin) kunnes hän oli täyttänyt kummankin vuoren välissä olevan solan. Hän sanoi: »Puhaltakaa!» He puhalsivat, kunnes rauta hehkui kuin tuli. Sitten hän sanoi: »Tuokaa nyt minulle sulaa kuparia, jotta voin valaa sen kaiken yli.»');
INSERT INTO "finnish" VALUES(18,97,'18:97','Silloin he (maahantunkijat) eivät pystyneet kiipeämään sen ylitse eivätkä kaivautumaan alitse.');
INSERT INTO "finnish" VALUES(18,98,'18:98','Hän sanoi vielä: »Tämä on Herrani suoma armo, mutta kun Herrani lupaus käy toteen, silloin Hän on murentava sen maan tomuksi, sillä Herrani lupaus on tosi.»');
INSERT INTO "finnish" VALUES(18,99,'18:99','Ja sinä päivänä Me annamme joidenkuiden heistä törmätä toisia vastaan. Silloin puhalletaan pasuunoihin ja Me kokoamme heidät kaikki yhteen.');
INSERT INTO "finnish" VALUES(18,100,'18:100','Ja sinä päivänä Me avaamme helvetin kauhistuttavana näkynä uskottomille.');
INSERT INTO "finnish" VALUES(18,101,'18:101','Heidän silmänsä olivat verhotut Minun varoituksiltani, eivätkä he myöskään voineet niitä kuulla.');
INSERT INTO "finnish" VALUES(18,102,'18:102','Luulevatko uskottomat, että he voivat suojelijoikseen ottaa palvelijoitani Minun sijastani? Totisesti, Me olemme valmistanut helvetin uskottomien asuinpaikaksi.');
INSERT INTO "finnish" VALUES(18,103,'18:103','Sano: »Ilmoitanko teille, ketkä tekojensa vuoksi joutuvat pahimmin häviölle?»');
INSERT INTO "finnish" VALUES(18,104,'18:104','Ne ovat niitä, joiden ponnistelut tämän maailman elämässä ovat menneet hukkaan, vaikka he luulevat tekevänsä hyvää.');
INSERT INTO "finnish" VALUES(18,105,'18:105','Ne ovat niitä, jotka eivät usko Herransa merkkeihin eivätkä Hänen tulemiseensa. Siksi ovat myös heidän tekonsa mitättömiä, niin että Me emme niitä vaa''assa punnitse ylösnousemuksen päivänä.');
INSERT INTO "finnish" VALUES(18,106,'18:106','Senvuoksi heidän palkkansa on oleva helvetti, koska he olivat uskottomia ja koska he pitivät Minun merkkejäni ja lähettiläitäni pilkkana.');
INSERT INTO "finnish" VALUES(18,107,'18:107','Totisesti, niiden asuinpaikoiksi, jotka uskovat ja jotka suorittavat hyviä tekoja, tulevat autuaat puutarhat.');
INSERT INTO "finnish" VALUES(18,108,'18:108','Siellä he saavat alati asua eivätkä halua paikkaansa vaihtaa.');
INSERT INTO "finnish" VALUES(18,109,'18:109','Sano: »Jos meri muuttuisi musteeksi Herrani sanojen kirjoittamista varten, niin totisesti, meri tyhjenisi ennen kuin Herrani sanat loppuisivat, vieläpä vaikka lisäisimme siihen toisen yhtä suuren meren.»');
INSERT INTO "finnish" VALUES(18,110,'18:110','Sano edelleen: »Minä olen ainoastaan teidän kaltaisenne ihmisolento, mutta minulle on ilmoitettu, että teidän Jumalanne on Yksi Jumala. Ken tahansa siis toivoo kohtaavansa Herransa, suorittakoon hyviä tekoja älköönkä ketään muuta palvoko niinkuin Herraansa.»');
INSERT INTO "finnish" VALUES(19,1,'19:1','Kaaf, Haa, Jaa, Ain, Saad.');
INSERT INTO "finnish" VALUES(19,2,'19:2','Tämä on muistutus Herrasi laupeudesta palvelijaansa Sakariaa kohtaan,');
INSERT INTO "finnish" VALUES(19,3,'19:3','kun hän salassa huokasi Herrallensa.');
INSERT INTO "finnish" VALUES(19,4,'19:4','Hän lausui: »Herrani, totisesti minun ruumiini luut ovat heikentyneet ja ikäni vuoksi minun päässäni välkkyvät harmaat hiukset, mutta milloinkaan ei onni ole minua pettänyt rukoillessani Sinua, oi Herrani.');
INSERT INTO "finnish" VALUES(19,5,'19:5','Minä pelkään sukulaisiani, jotka tulevat perijöikseni; onhan vaimoni hedelmätön; sentähden låhjoita minulle jälkeläinen,');
INSERT INTO "finnish" VALUES(19,6,'19:6','joka perisi minut ja myöskin Jaakobin lapset, ja oi Herrani, tee hänestä Sinulle mieluisa.»');
INSERT INTO "finnish" VALUES(19,7,'19:7','Oi Sakarias, totisesti, Me ilmoitamme sinulle ilosanoman pojasta, jonka nimi olkoon Johannes. Emme ole vielä luonut ketään sen nimistä.');
INSERT INTO "finnish" VALUES(19,8,'19:8','Hän sanoi: »Herra, kuinka minä voin saada pojan, kun vaimoni on hedelmätön ja itseni on korkea ikä jo heikentänyt?»');
INSERT INTO "finnish" VALUES(19,9,'19:9','Jumala vastasi: »Niin tapahtuu, sanoo Herrasi, se on helppo asia Minulle. Olenhan jo aikaisemmin luonut sinut tyhjästä.»');
INSERT INTO "finnish" VALUES(19,10,'19:10','Hän sanoi: »Herra, anna minulle jokin tunnusmerkki!» Jumala lausui: »Se olkoon sinulle merkiksi, ettet voi kolmeen vuorokauteen puhua ihmisille mitään, vaikka olet aivan terve.»');
INSERT INTO "finnish" VALUES(19,11,'19:11','Senjälkeen hän astui ulos rukouskammiosta kansansa eteen ja puhui kansalleen merkkien avulla: »Julistakaa Hänen kunniaansa aamuin ja illoin.»');
INSERT INTO "finnish" VALUES(19,12,'19:12','Ja hänen pojalleen sanottiin: »Oi Johannes, ota Kirja vastaan halukkaasti!» ja Me annoimme Hänelle viisautta, kun hän oli vielä lapsi.');
INSERT INTO "finnish" VALUES(19,13,'19:13','Annoimme hänelle myös innoitusta ja viattomuutta omastamme, ja hän kunnioitti vanhempiaan');
INSERT INTO "finnish" VALUES(19,14,'19:14','ja oli hyvä heitä kohtaan. Hän ei ollut kapinallinen eikä tottelematon.');
INSERT INTO "finnish" VALUES(19,15,'19:15','Rauha olkoon hänelle sinä päivänä, jolloin hän syntyi, ja sinä päivänä, jolloin hän kuolee, sekä sinä päivänä, jolloin hänet herätetään kuolleista.');
INSERT INTO "finnish" VALUES(19,16,'19:16','Mainitse Kirjassa myös Mariasta: Hän lähti perheensä luota yksinäiseen paikkaan idässä.');
INSERT INTO "finnish" VALUES(19,17,'19:17','Ja hän otti hunnun, joka verhosi hänet heidän katseiltaan. Senjälkeen Me lähetimme henkemme hänen luokseen, ja se ilmestyi hänelle aivan kuin ihmisolento.');
INSERT INTO "finnish" VALUES(19,18,'19:18','Maria lausui: »Totisesti, minä etsin sinua vastaan turvaa laupeimpaan (Jumalaan) luottaen, jos olet hurskas.»');
INSERT INTO "finnish" VALUES(19,19,'19:19','Hän vastasi: »Minä olen vain Herrasi lähettiläs ja tuon sinulle viattoman pojan.»');
INSERT INTO "finnish" VALUES(19,20,'19:20','Maria sanoi: »Kuinka minä voisin saada pojan, kun kukaan ihminen ei ole minuun koskenut enkä ole siveetön?»');
INSERT INTO "finnish" VALUES(19,21,'19:21','Hän vastasi: »Näin sanoo Herrasi: ''Se on helppo asia Minulle, ja se on oleva merkkinä ihmissuvulle ja armonosoituksena Meiltä; onhan asia jo ratkaistu.''»');
INSERT INTO "finnish" VALUES(19,22,'19:22','Silloin Maria vastaanotti hänen lupauksensa ja siirtyi kauas syrjäiseen paikkaan.');
INSERT INTO "finnish" VALUES(19,23,'19:23','Sitten synnytystuskat pakottivat hänet nojautumaan taatelipalmun runkoon. Hän lausui: »Voi, kun olisin kuollut ennen tätä ja minut olisi kokonaan unohdettu.»');
INSERT INTO "finnish" VALUES(19,24,'19:24','Silloin ääni hänen altaan kutsui häntä ja sanoi: »Älä murehdi, totisesti Herrasi on asettanut lähteen jalkojesi juureen.');
INSERT INTO "finnish" VALUES(19,25,'19:25','Ravista palmun runkoa, niin sinulle putoilee kypsiä taateleita.');
INSERT INTO "finnish" VALUES(19,26,'19:26','Sentähden syö ja juo ja kirkastukoon silmäsi. Ja jos näet jonkun ihmisen, niin sano hänelle: »Totisesti, minä olen Laupeimmalle luvannut paastota enkä siis voi puhua tänään kenenkään ihmisolennon kanssa.»');
INSERT INTO "finnish" VALUES(19,27,'19:27','Sitten hän palasi kansansa luokse kantaen lastaan. He huudahtivat: »Oi Maria, totisesti sinä olet tehnyt sangen pahasti.');
INSERT INTO "finnish" VALUES(19,28,'19:28','Oi Aaronin sisar, sinun isäsi ei ollut kelvoton mies eikä äitisi huono nainen.»');
INSERT INTO "finnish" VALUES(19,29,'19:29','Mutta hän vain osoitti lastaan. He sanoivat: »Kuinka voisimme puhua sellaiselle, joka on vielä kehdossa oleva lapsi?»');
INSERT INTO "finnish" VALUES(19,30,'19:30','Poika vastasi: »Totisesti, minä olen Jumalan palvelija. Hän on antanut minulle Kirjan ja Hän on tehnyt minut profeetaksi.');
INSERT INTO "finnish" VALUES(19,31,'19:31','Hän antaa minulle siunauksensa missä tahansa olenkin, ja Hän on määrännyt minun tehtäväkseni rukoilemisen ja almut niin kauan kuin elän.');
INSERT INTO "finnish" VALUES(19,32,'19:32','(Hän on luonut minut) hyväksi äitiäni kohtaan, eikä Hän ole tehnyt minua kapinalliseksi, onnettomaksi.');
INSERT INTO "finnish" VALUES(19,33,'19:33','Rauha ylläni sinä päivänä, jolloin synnyin, sinä päivänä, jolloin kuolen sekä sinä päivänä, jolloin minut herätetään kuolleista.»');
INSERT INTO "finnish" VALUES(19,34,'19:34','Tällainen on Jeesus, Marian poika. Tämä on tosi sanoma, josta he kuitenkin kiistelevät.');
INSERT INTO "finnish" VALUES(19,35,'19:35','Jumalan ei ole soveliasta hankkia itselleen poikaa; kunnia olkoon Hänelle! Kun Hän päättää jonkin asian, Hänen tarvitsee vain sanoa: »Tapahtukoon!» ja se tapahtuu.');
INSERT INTO "finnish" VALUES(19,36,'19:36','Totisesti, Jumala on minun Herrani ja myöskin teidän Herranne; palvelkaa siis Häntä; tämä on oikea tie.»');
INSERT INTO "finnish" VALUES(19,37,'19:37','Mutta lahkolaiset olivat keskenään erimielisiä; voi uskottomia, kun viimeinen päivä heidät kohtaa!');
INSERT INTO "finnish" VALUES(19,38,'19:38','Kuinka hyvin he kuulevatkaan ja näkevät sinä päivänä, jolloin he astuvat eteemme! Mutta tänä päivänä väärämieliset ovat vielä suuren harhan vallassa.');
INSERT INTO "finnish" VALUES(19,39,'19:39','Varoita heitä murheen päivästä, jolloin asia on jo ratkaistu, koska he ovat välinpitämättömiä ja epäuskoisia.');
INSERT INTO "finnish" VALUES(19,40,'19:40','Totisesti, Me perimme maan ja kaikki, jotka siinä ovat. Ja Meidän luoksemme on heidän palattava.');
INSERT INTO "finnish" VALUES(19,41,'19:41','Mainitse Kirjassa myös Aabrahamista. Hän oli vanhurskas mies sekä profeetta.');
INSERT INTO "finnish" VALUES(19,42,'19:42','Hän lausui isälleen: »Oi isäni, miksi palvelet sellaista, joka ei kuule eikä näe eikä kykene vähääkään sinua auttamaan?');
INSERT INTO "finnish" VALUES(19,43,'19:43','Oi isäni, totisesti minulle on suotu tietoa, jota sinä et ole saanut, sentähden seuraa minua, minä olen johdattava sinut oikealle tielle.');
INSERT INTO "finnish" VALUES(19,44,'19:44','Oi isäni, älä palvele saatanaa. Totisesti, saatana on tottelematon Laupeimmalle.');
INSERT INTO "finnish" VALUES(19,45,'19:45','Oi isäni, minä pelkään, että Laupeimman kuritus on sinua kohtaava ja että sinusta tulee saatanan liittolainen.»');
INSERT INTO "finnish" VALUES(19,46,'19:46','Hän vastasi: »Oletko luopunut minun jumalistani, oi Aabraham? Totisesti, ellet muuta mieltäsi, niin minä olen kivittävä sinut kuoliaaksi. Poistu luotani pitkäksi aikaa.»');
INSERT INTO "finnish" VALUES(19,47,'19:47','Aabraham sanoi: »Rauha olkoon sinulle. Olen rukoileva sinulle Herraltani anteeksiantoa. Totisesti, Hän on lempeä minua kohtaan.');
INSERT INTO "finnish" VALUES(19,48,'19:48','Mutta minä luovun sinusta ja siitä, mitä sinä rukoilet Jumalan asemesta. Minä olen rukoileva Herraani ja ehkäpä en ole suotta vetoava Herraan.»');
INSERT INTO "finnish" VALUES(19,49,'19:49','Kun hän oli vaeltanut pois heidän luotaan ja hylännyt sen, mitä he palvelivat, Me annoimme hänelle lisakin ja Jaakobin ja teimme kummastakin profeetan.');
INSERT INTO "finnish" VALUES(19,50,'19:50','Ja Me soimme heille armoamme ja teimme heistä kuuluisia miehiä, totuuden julistajia.');
INSERT INTO "finnish" VALUES(19,51,'19:51','Mainitse Kirjassa myös Mooseksesta. Totisesti, hän kuului valittujen joukkoon ja hän oli myös lähettiläs ja profeetta.');
INSERT INTO "finnish" VALUES(19,52,'19:52','Me kutsuimme hänet vuoren oikealta puolelta ja sallimme hänen läheltä puhutella Itseämme.');
INSERT INTO "finnish" VALUES(19,53,'19:53','Ja armossamme Me teimme hänen veljestään Aaronista profeetan.');
INSERT INTO "finnish" VALUES(19,54,'19:54','Muista Kirjassa myös Ismaelia. Totisesti, hän oli lupaukselle uskollinen, ja hän oli myös lähettiläs ja profeetta.');
INSERT INTO "finnish" VALUES(19,55,'19:55','Hän määräsi perheensä rukoilemaan ja antamaan almuja ja hän oli Herrallensa mieluinen.');
INSERT INTO "finnish" VALUES(19,56,'19:56','Muista Kirjassa myös Idrisiä. Totisesti, hän oli uskollinen ja hänkin oli profeetta.');
INSERT INTO "finnish" VALUES(19,57,'19:57','Ja Me ylensimme hänet korkeaan asemaan.');
INSERT INTO "finnish" VALUES(19,58,'19:58','Nämä kaikki olivat niitä, joille Me annoimme siunauksemme. He olivat profeettoja Aadamin jälkeläisten joukossa ja niiden, jotka pelastimme Nooan mukana. Samoin Aabrahamin ja Ismaelin jälkeläisten joukossa sekä niiden, joille olemme antanut johdatuksen ja jotka olemme valinnut. Kun heille lueteltiin laupeimman Jumalan tunnusmerkkejä, he lankesivat maahan palvoen ja kyyneliä vuodattaen.');
INSERT INTO "finnish" VALUES(19,59,'19:59','Mutta heidän jälkeensä tuli sellaisia, jotka laiminlöivät rukouksensa ja seurasivat himojaan. Siksi he saavatkin palkan luopumuksestaan.');
INSERT INTO "finnish" VALUES(19,60,'19:60','Mutta ne, jotka kääntyvät Jumalan puoleen ja uskovat ja jotka suorittavat hyviä tekoja, saavat astua puutarhaan, eikä heille tehdä pienintäkään vääryyttä:');
INSERT INTO "finnish" VALUES(19,61,'19:61','he astuvat heille näkymättömiin autuaisiin puutarhoihin, jotka Armahtaja on palvelijoillensa luvannut. Totisesti, Hänen lupauksensa on täyttyvä.');
INSERT INTO "finnish" VALUES(19,62,'19:62','He eivät kuule siellä mitään turhaa puhetta, vaan ainoastaan sanan »Rauha!» Ja ravintonsa he saavat sekä aamuin että illoin.');
INSERT INTO "finnish" VALUES(19,63,'19:63','Tällainen on puutarha, jonka Me annamme perinnöksi niille palvelijoillemme, jotka ovat hurskaita.');
INSERT INTO "finnish" VALUES(19,64,'19:64','Ja enkelit sanovat: »Me laskeudumme maan päälle vain Herrasi käskystä. Hänelle kuuluu kaikki, mikä on edessämme ja mikä on takanamme, ja kaikki, mikä on näiden välissä.» Eikä Herrasi ole huonomuistinen.');
INSERT INTO "finnish" VALUES(19,65,'19:65','Hän on taivaitten ja maan ja kaiken niiden välisen Herra. Sentähden palvelkaa Häntä ja pysykää kestävinä palvelemisessanne. Tunnetko ketään, joka olisi Hänen nimensä kaltainen?');
INSERT INTO "finnish" VALUES(19,66,'19:66','Ja kuitenkin ihminen sanoo: »Kuinka, herätetäänkö minut todellakin kuoltuani jälleen eloon?»');
INSERT INTO "finnish" VALUES(19,67,'19:67','Eikö ihminen lainkaan muista, että Me loimme hänet jo kerran ennen aivan tyhjästä?');
INSERT INTO "finnish" VALUES(19,68,'19:68','Herrasi kautta! Me kokoamme varmasti heidät ja saatanat ja saatamme heidät polvistumaan helvetin ympärille.');
INSERT INTO "finnish" VALUES(19,69,'19:69','Senjälkeen Me erotamme jokaisesta lahkosta ne, jotka kiivaimmin uhmailivat Laupeinta vastaan.');
INSERT INTO "finnish" VALUES(19,70,'19:70','Totisesti, Me tunnemme parhaiten ne, jotka suurimmalla syyllä on syöstävä helvettiin.');
INSERT INTO "finnish" VALUES(19,71,'19:71','Teidän joukossanne ei ole ainoatakaan, joka ei sinne joutuisi. Tämä on Herrasi sitova määräys.');
INSERT INTO "finnish" VALUES(19,72,'19:72','Sitten Me pelastamme ne, jotka harjoittavat hartautta, mutta väärintekijät Me hylkäämme sinne polvillensa.');
INSERT INTO "finnish" VALUES(19,73,'19:73','Ja kun selvät merkkimme on heille ilmoitettu, sanovat epäuskon valtaamat niille, jotka uskovat: »Kumpi puoli on edullisemmassa asemassa ja näyttää lukuisemmalta?»');
INSERT INTO "finnish" VALUES(19,74,'19:74','Kuinka monta ennen heitä elänyttä sukupolvea olemmekaan hävittänyt, vaikka ne olivat heitä rikkaampia ja uhkeampia!');
INSERT INTO "finnish" VALUES(19,75,'19:75','Sano: »Jotka kulkevat harhaannuksissa, niiden päiviä Laupein pidentää, kunnes he näkevät, mitä heille on luvattu, rangaistus taikka tuomion hetki, ja tulevat tietämään, kenen asema on huonoin ja kenen voimat heikoimmat.»');
INSERT INTO "finnish" VALUES(19,76,'19:76','Mutta niille, jotka vaeltavat oikeata tietä, Jumala enentää johdatustansa. Ja pysyväiset hyvät työt saavat Jumalan luona paremman palkan ja paremman turvapaikan.');
INSERT INTO "finnish" VALUES(19,77,'19:77','Oletko nähnyt sellaisen, joka ei usko merkkeihimme ja sanoo: »Totisesti, minulle annetaan rikkautta ja lapsia.»');
INSERT INTO "finnish" VALUES(19,78,'19:78','Onko hän siis saanut tietoa näkymättömistä asioista, tai onko hän hankkinut sitoumuksen kaikkein Laupeimmalta?');
INSERT INTO "finnish" VALUES(19,79,'19:79','Totisesti ei. Me kirjoitamme muistiin hänen sanansa ja pidennämme hänen tuskansa aikaa.');
INSERT INTO "finnish" VALUES(19,80,'19:80','Me perimme häneltä pois sen, mistä hän on kerskaillut, ja yksin hän on astuva eteemme.');
INSERT INTO "finnish" VALUES(19,81,'19:81','He ottavat myös Jumalan rinnalle muita jumalia, jotka muka heitä auttavat.');
INSERT INTO "finnish" VALUES(19,82,'19:82','Mutta nämä kieltävät heille osoitetun jumaloimisen ja kääntyvät heidän vastustajikseen.');
INSERT INTO "finnish" VALUES(19,83,'19:83','Etkö ole huomannut, että Me lähetämme pahat henget uskottomien luokse viettelemään näitä kiusaukseen.');
INSERT INTO "finnish" VALUES(19,84,'19:84','Mutta älä hätäile heidän tähtensä. Me määräämme heille luetut päivänsä.');
INSERT INTO "finnish" VALUES(19,85,'19:85','Tulee päivä, jolloin Me kokoamme hurskaat Laupeimman luokse hänen vieraakseen.');
INSERT INTO "finnish" VALUES(19,86,'19:86','Mutta syntiset me ajamme helvettiin janoisena laumana.');
INSERT INTO "finnish" VALUES(19,87,'19:87','Heillä ei ole ketään puoltajaa, paitsi niillä, jotka ovat sopineet Laupeimman kanssa.');
INSERT INTO "finnish" VALUES(19,88,'19:88','He väittävät, että Armahtaja on hankkinut itselleen lapsen.');
INSERT INTO "finnish" VALUES(19,89,'19:89','Totisesti, te olette puhuneet sangen pahasti.');
INSERT INTO "finnish" VALUES(19,90,'19:90','Taivaat saattavat puhjeta ja maa voi repeytyä kappaleiksi ja vuoret luhistua tomuksi,');
INSERT INTO "finnish" VALUES(19,91,'19:91','koska he väittävät Laupeimman hankkineen lapsen.');
INSERT INTO "finnish" VALUES(19,92,'19:92','Onhan sopimatonta, että Laupeimmalla olisi lapsi.');
INSERT INTO "finnish" VALUES(19,93,'19:93','Taivaissa ja maan päällä ei ole ainoatakaan, joka tulisi Laupeimman luo muuten kuin palvelijana.');
INSERT INTO "finnish" VALUES(19,94,'19:94','Totisesti, Hän tietää heidän lukunsa ja Hän on sen tarkoin laskenut.');
INSERT INTO "finnish" VALUES(19,95,'19:95','Ja ylösnousemuksen päivänä jokainen heistä astuu Hänen eteensä omakohtaisesti.');
INSERT INTO "finnish" VALUES(19,96,'19:96','Totisesti, ne, jotka uskovat ja tekevät hyviä töitä, Jumala on palkitseva rakkaudella.');
INSERT INTO "finnish" VALUES(19,97,'19:97','Senvuoksi Me olemme tehnyt Koraanin helposti ymmärrettäväksi sinun kielelläsi, jotta voisit julistaa ilosanomaa hurskaille ja varoittaa riitaisia.');
INSERT INTO "finnish" VALUES(19,98,'19:98','Kuinka monia ennen heitä eläneitä sukupolvia olemmekaan hävittänyt! Saatatko löytää enää ainoatakaan heidän joukostaan tahi kuulla heistä pienintäkään äännähdystä?');
INSERT INTO "finnish" VALUES(20,1,'20:1','Taa Haa.');
INSERT INTO "finnish" VALUES(20,2,'20:2','Emme ole lähettänyt sinulle Koraania tehdäksemme sinua onnettomaksi,');
INSERT INTO "finnish" VALUES(20,3,'20:3','vaan jotta se olisi kehoitus Jumalaa pelkääville,');
INSERT INTO "finnish" VALUES(20,4,'20:4','Hänen lähettämänsä, joka on luonut taivaan korkeudet ja maan.');
INSERT INTO "finnish" VALUES(20,5,'20:5','Hänelle, Armahtajalle, joka on noussut valtaistuimelleen,');
INSERT INTO "finnish" VALUES(20,6,'20:6','kuuluu kaikki, mitä on taivaassa ja mitä on maan päällä, mitä on niiden välillä ja maan alla.');
INSERT INTO "finnish" VALUES(20,7,'20:7','Siksi ei sinun tarvitse rukoilla kovalla äänellä; sillä Hän tietää yhtä hyvin salaisen ja kätketyn.');
INSERT INTO "finnish" VALUES(20,8,'20:8','Ei ole muita jumalia kuin Jumala; hänelle kuuluvat ihanimmat nimitykset.');
INSERT INTO "finnish" VALUES(20,9,'20:9','Oletko kuullut kertomusta Mooseksesta?');
INSERT INTO "finnish" VALUES(20,10,'20:10','Kun hän näki tulen, hän lausui perheelleen: »Malttakaa! Minä totisesti näen tulen. Ehkä hankin siitä teille virikeliekin tai kenties saan sen kautta johdatuksen.»');
INSERT INTO "finnish" VALUES(20,11,'20:11','Kun hän sitten lähestyi tulta, kuului ääni: »Oi Mooses!');
INSERT INTO "finnish" VALUES(20,12,'20:12','Totisesti, Minä olen sinun Herrasi. Riisu kenkäsi, sillä olet pyhässä Tuwan laaksossa.');
INSERT INTO "finnish" VALUES(20,13,'20:13','Minä olen valinnut sinut, kuule siis, mitä sinulle ilmaistaan.');
INSERT INTO "finnish" VALUES(20,14,'20:14','Totisesti, Minä olen Jumala, eikä ole muuta jumalaa kuin Minä. Sentähden palvele Minua ja suorita rukouksesi pitäen Minut mielessäsi.');
INSERT INTO "finnish" VALUES(20,15,'20:15','Totisesti, hetki lähestyy, Minä vedän sen esiin salasta, ja jokainen sielu saa palkan tekojensa mukaan.');
INSERT INTO "finnish" VALUES(20,16,'20:16','Sentähden älä anna sellaisen, joka ei siihen usko ja seuraa vain himojaan, kääntää pois siitä huomiotasi, muuten joudut turmion omaksi.');
INSERT INTO "finnish" VALUES(20,17,'20:17','Mutta mitä sinulla on oikeassa kädessäsi, Mooses?»');
INSERT INTO "finnish" VALUES(20,18,'20:18','Hän vastasi: »Se on sauvani. Siihen minä nojaudun ja sillä pudistelen lehtiä maahan lampaitani varten ja käytän sitä muihinkin tarkoituksiin.»');
INSERT INTO "finnish" VALUES(20,19,'20:19','Jumala sanoi: »Heitä se maahan, Mooses!»');
INSERT INTO "finnish" VALUES(20,20,'20:20','Niin hän heitti sen maahan, ja katso, siitä tuli mateleva käärme.');
INSERT INTO "finnish" VALUES(20,21,'20:21','Jumala sanoi: »Ota se käteesi äläkä pelkää! Me muutamme sen jälleen entiseen tilaansa.');
INSERT INTO "finnish" VALUES(20,22,'20:22','Ja purista kätesi kainaloosi, niin se käy kokonaan valkeaksi, ei kuitenkaan taudin tartuttamaksi - tämä on toinen merkki.');
INSERT INTO "finnish" VALUES(20,23,'20:23','Tämä siksi, että Me voisimme näyttää sinulle joitakin suurimpia merkkejämme.');
INSERT INTO "finnish" VALUES(20,24,'20:24','Lähde faraon luokse. Totisesti, hän on paisunut yli mittojensa.»');
INSERT INTO "finnish" VALUES(20,25,'20:25','Mooses sanoi: »Herrani, vahvista mieltäni,');
INSERT INTO "finnish" VALUES(20,26,'20:26','järjestä tehtäväni minulle helpoksi');
INSERT INTO "finnish" VALUES(20,27,'20:27','ja avaa kieleni siteet,');
INSERT INTO "finnish" VALUES(20,28,'20:28','jotta he ymmärtäisivät puheeni.');
INSERT INTO "finnish" VALUES(20,29,'20:29','Anna minulle avukseni joku perheestäni,');
INSERT INTO "finnish" VALUES(20,30,'20:30','Aaron veljeni;');
INSERT INTO "finnish" VALUES(20,31,'20:31','ja tue minua hänen kauttansa,');
INSERT INTO "finnish" VALUES(20,32,'20:32','sekä tee hänet osalliseksi tehtävääni,');
INSERT INTO "finnish" VALUES(20,33,'20:33','jotta voisimme suuresti Sinun kunniaasi kirkastaa');
INSERT INTO "finnish" VALUES(20,34,'20:34','ja että voisimme Sinua alati muistaa.');
INSERT INTO "finnish" VALUES(20,35,'20:35','Totisesti, Sinä valvot meitä valppaasti.»');
INSERT INTO "finnish" VALUES(20,36,'20:36','Jumala lausui: »Tapahtukoon sinulle, mitä pyydät, Mooses!');
INSERT INTO "finnish" VALUES(20,37,'20:37','Totisesti, jo kerran aikaisemmin Me olemme kohdistanut sinuun suosiomme,');
INSERT INTO "finnish" VALUES(20,38,'20:38','kun käskimme äitiäsi näin sanoen:');
INSERT INTO "finnish" VALUES(20,39,'20:39','''Laske hänet vasuun ja aseta vasu veteen, niin että virta työntää hänet rannalle, jolloin eräs, joka on sekä Minun että hänen vihollisensa, nostaa hänet ylös.'' Ja Minä olen osoittanut sinulle rakkauttani, jotta sinut, kasvatettaisiin silmieni edessä.');
INSERT INTO "finnish" VALUES(20,40,'20:40','Kun sisareni kulki ohi ja sanoi: ''Näytänkö teille jonkun, joka hänestä huolehtii?'' niin Me toimitimme sinut äitisi huostaan, jotta hänen silmänsä kirkastuisivat ja ettei hän enää murehtisi. Sinä tapoit erään miehen, mutta Me pelastimme sinut hädästä, Me karkaisimme sinua myös kurituksella. Senjälkeen sinä viivyit muutamia vuosia Midianin asukkaitten keskuudessa. Ja nyt saavuit käskystämme tänne, oi Mooses.');
INSERT INTO "finnish" VALUES(20,41,'20:41','Minä olen valinnut sinut Itseäni varten.');
INSERT INTO "finnish" VALUES(20,42,'20:42','Menkää, sinä ja veljesi, viemään Minun merkkejäni ja pitäkää Minut herkeämättä mielessänne.');
INSERT INTO "finnish" VALUES(20,43,'20:43','Lähtekää kumpikin faraon luokse, sillä totisesti, hän on paisunut.');
INSERT INTO "finnish" VALUES(20,44,'20:44','Puhukaa hänelle sitten sävyisästi, ehkä hän ottaa siitä vaarin tai kavahtaa.»');
INSERT INTO "finnish" VALUES(20,45,'20:45','He sanoivat: »Herra, me pelkäämme, että hän kiihtyy pahasti meitä vastaan tai rikkoo Sinua vastaan.»');
INSERT INTO "finnish" VALUES(20,46,'20:46','Jumala vastasi: »Alkää pelätkö, totisesti, Minä olen kanssanne. Minä kuulen ja minä näen.');
INSERT INTO "finnish" VALUES(20,47,'20:47','Menkää sentähden yhdessä hänen eteensä ja sanokaa: ''Totisesti, me olemme Herrasi lähettiläitä. Lähetä siis Israelin lapset meidän mukanamme äläkä aiheuta heille kärsimyksiä. Olemme tulleet sinun luoksesi tuomaan tunnusmerkkejä Herraltasi. Rauha olkoon sille, joka seuraa johdatusta.');
INSERT INTO "finnish" VALUES(20,48,'20:48','Totisesti, meille on ilmoitettu, että rangaistus on varmaan kohtaava sitä, joka puhuu valhetta ja kääntyy pois.''»');
INSERT INTO "finnish" VALUES(20,49,'20:49','Hän sanoi: »Oi Mooses, kuka onkaan teidän kummankin Herra?»');
INSERT INTO "finnish" VALUES(20,50,'20:50','Hän vastasi: »Meidän Herramme on Hän, joka loi kaiken muotoihinsa ja sitten ylläpitää sitä kaitselmuksen kautta.»');
INSERT INTO "finnish" VALUES(20,51,'20:51','Hän sanoi:»Millainen onkaan aikaisempien sukupolvien olotila?»');
INSERT INTO "finnish" VALUES(20,52,'20:52','Mooses vastasi: »Tiedot heistä ovat Herrani luona olevassa Kirjassa. Herrani ei erehdy eikä unohda.');
INSERT INTO "finnish" VALUES(20,53,'20:53','Hän on luonut maan teidän kehdoksenne ja aukonut siihen polkuja teitä varten; ja Hän lähettää teille sateen pilvistä.» Niin olemme myös verhonnut maan monenlaisilla kasveilla.');
INSERT INTO "finnish" VALUES(20,54,'20:54','Syökää niitä itse ja ruokkikaa niillä karjaanne. Totisesti, tässä on merkkejä sellaisille, joilla on ymmärrystä.');
INSERT INTO "finnish" VALUES(20,55,'20:55','Maasta me teidät loimme, maaksi teidät jälleen muutamme ja maasta Me vielä kerran teidät eloon herätämme.');
INSERT INTO "finnish" VALUES(20,56,'20:56','Totisesti, Me näytimme hänelle kaikki merkkimme, mutta hän selitti ne vaiheeksi ja torjui ne.');
INSERT INTO "finnish" VALUES(20,57,'20:57','Hän sanoi: »Oi Mooses, oletko tullut luoksemme karkoittaaksesi meidät taikakeinoillasi pois maastamme?');
INSERT INTO "finnish" VALUES(20,58,'20:58','Siinä tapauksessa aiomme käyttää sinua vastaan samanlaista taikakeinoa. Ehdota siis määrähetki, josta emme poikkea, et sinä emmekä me, ja jolloin kohtaamme toisemme jollakin keskeisellä paikalla.»');
INSERT INTO "finnish" VALUES(20,59,'20:59','Mooses lausui: »Määrähetki olkoon teidän juhlapäivänänne, jolloin kansa kokoontukoon jo aamulla.»');
INSERT INTO "finnish" VALUES(20,60,'20:60','Senjälkeen farao vetäytyi pois, teki suunnitelmansa ja palasi takaisin.');
INSERT INTO "finnish" VALUES(20,61,'20:61','Mooses lausui heille: »Voi teitä! Älkää keksikö juonia Jumalaa vastaan, sillä Hän tuhoaa teidät rangaistuksellaan; totisesti ei ole toivomaansa menestystä sillä, joka juonia punoo.»');
INSERT INTO "finnish" VALUES(20,62,'20:62','Sitten he kiistelivät asiasta keskenänsä ja pitivät salaista neuvottelua.');
INSERT INTO "finnish" VALUES(20,63,'20:63','Jotkut heistä sanoivat: »Nämä kaksi miestä ovat totisesti taikureita, jotka noituudellaan haluavat karkoittaa teidät pois maastanne ja hävittää teidän sivistyneet tapanne.');
INSERT INTO "finnish" VALUES(20,64,'20:64','Senvuoksi saattakaa suunnitelmanne valmiiksi ja astukaa riveihin! Totisesti, onnellinen se, joka tänään voittaa.');
INSERT INTO "finnish" VALUES(20,65,'20:65','He sanoivat: »Oi Mooses, heitätkö sinä, vai teemmekö me sen ensiksi?');
INSERT INTO "finnish" VALUES(20,66,'20:66','Hän vastasi: »Ei, heittäkää te!» Kun he niin tekivät, näytti hänestä, heidän taikuutensa voimasta, aivan kuin heidän köytensä ja sauvansa todella liikkuisivat.');
INSERT INTO "finnish" VALUES(20,67,'20:67','Silloin Mooses tunsi pelkoa sydämessään.');
INSERT INTO "finnish" VALUES(20,68,'20:68','Me sanoimme: »Älä pelkää, sinä pääset varmasti voitolle.');
INSERT INTO "finnish" VALUES(20,69,'20:69','Heitä maahan, mitä sinulla on oikeassa kädessäsi, ja se on nielevä kaiken, mitä he ovat tehneet. He ovat suorittaneet pelkän taikurin tempun, mutta taikuri ei ole koskaan menestyvä, minne tahansa hän meneekin.»');
INSERT INTO "finnish" VALUES(20,70,'20:70','Ja taikurit lankesivat maahan kunnioituksesta ja sanoivat: »Me uskomme Aaronin ja Mooseksen Herraan.»');
INSERT INTO "finnish" VALUES(20,71,'20:71','Silloin lausui farao: »Uskotteko te häneen ennenkuin minä annan teille luvan? Totisesti, hän on teidän mestarinne, joka on opettanut teille noituutta. Sentähden minä olen leikkaava teiltä käden ja jalan vastakkaisilta puolilta ja naulitseva teidät palmunrunkoon. Tctisesti, silloin tiedätte, kuka meistä on kurittamisessa ankarin ja sitkein.»');
INSERT INTO "finnish" VALUES(20,72,'20:72','He vastasivat: »Emme voi todellakaan asettaa sinua korkeammalle kuin sen, mikä meille on ilmaistu selvin tunnusmerkein, emmekä Hänen yläpuolelleen, joka meidät on luonut. Pane siis päätöksesi täytäntöön, kuten aiot. Sinun määräyksesi ulottuvat vain tämän maailman elämään.');
INSERT INTO "finnish" VALUES(20,73,'20:73','Totisesti, me uskomme Herraamme, että Hän voi antaa anteeksi syntimme ja noituuden, mihin sinä meidät pakotit. Jumala on paras ja pysyvin.»');
INSERT INTO "finnish" VALUES(20,74,'20:74','Totisesti, joka saapuu Herransa eteen synnin tilassa, häntä varten on helvetti. Siellä hän ei voi kuolla eikä liioin elää.');
INSERT INTO "finnish" VALUES(20,75,'20:75','Mutta joka saapuu Hänen eteensä uskovaisena, hyviä töitä suoritettuaan, hän kuuluu niihin, joita varten on korkein kunnia,');
INSERT INTO "finnish" VALUES(20,76,'20:76','ikuiset puutarhat, joiden kautta joet virtaavat ja joissa he saavat alati asua. Tällaisen palkinnon saa se, joka tekee parannuksen.');
INSERT INTO "finnish" VALUES(20,77,'20:77','Totisesti, Me annoimme Moosekselle näin kuuluvan käskyn: »Lähde matkaan palvelijaini kanssa yöaikaan ja avaa heille kuiva tie meren halki. Älä pelkää, että sinut saavutetaan, vaan kulje säikkymättä eteenpäin.');
INSERT INTO "finnish" VALUES(20,78,'20:78','Mutta farao ajoi heitä sotajoukkoineen takaa, ja silloin meri peitti heidät alleen, hukuttaen kaikki.');
INSERT INTO "finnish" VALUES(20,79,'20:79','Sillä farao johti kansansa harhaan eikä opastanut sitä oikealle tielle.');
INSERT INTO "finnish" VALUES(20,80,'20:80','»Oi Israelin lapset! Totisesti, Me pelastimme teidät vihollisistanne ja teimme kanssanne liiton vuoren oikealla puolella sekä lähetimme teille mannaa ja peltopyitä.');
INSERT INTO "finnish" VALUES(20,81,'20:81','Syökää kaikkea hyvää, mitä olemme ravinnoksenne suonut, mutta älkää menkö hillittömyyksiin, ettei vihani lankea päällenne, sillä ketä minun vihani kohtaa, hän totisesti hukkuu.');
INSERT INTO "finnish" VALUES(20,82,'20:82','Mutta Minä olen peräti anteeksiantavainen sitä kohtaan, joka kääntyy ja uskoo sekä tekee hyvää ja pysyy jatkuvasti oikealla tiellä.');
INSERT INTO "finnish" VALUES(20,83,'20:83','»Mutta miksi olet kiirehtinyt pois kansasi luota,oi Mooses?»');
INSERT INTO "finnish" VALUES(20,84,'20:84','Hän vastasi: »He seuraavat jälkiäni, ja minä kiiruhdan luoksesi, oi Herra, jotta olisit minuun tyytyväinen.»');
INSERT INTO "finnish" VALUES(20,85,'20:85','Silloin Herra lausui: »Totisesti, olemme koetellut kansaasi sinun poistuttuasi, sillä Saamiri on johdattanut sen harhaan.»');
INSERT INTO "finnish" VALUES(20,86,'20:86','Silloin Mooses palasi kansansa luokse täynnä kiivautta ja murhetta. Hän sanoi: »Oi kansani, eikö Herranne antanut teille ihanan lupauksen? Näyttikö sovittu aika teistä liian pitkältä? Vai toivoitteko Herran vihan lankeavan päällenne, koska rikoitte minulle tekemänne lupauksen?');
INSERT INTO "finnish" VALUES(20,87,'20:87','He vastasivat: »Me emme rikkoneet lupausta omasta tahdostamme, vaan meidät pakotettiin kantamaan raskaat taakat kansan koristeita ja heittämään ne tuleen, ja Saamiri teki samoin.»');
INSERT INTO "finnish" VALUES(20,88,'20:88','Siten Saamiri valmisti heille (kulta)vasikan, josta lähti kumea ääni. Ja he sanoivat: »Tämä on teidän jumalanne ja Mooseksen jumala, mutta hän on sen unohtanut.»');
INSERT INTO "finnish" VALUES(20,89,'20:89','Eivätkö he huomanneet, että se ei puhunut heille ainoatakaan sanaa, ja ettei sillä ollut valtaa tehdä heille vahinkoa eikä hyvääkään?');
INSERT INTO "finnish" VALUES(20,90,'20:90','Tosin oli Aaron jo aikaisemmin heille puhunut: »Oi kansani! Näin olette hankkineet itsellenne koettelemuksen. Mutta totisesti, Herranne on kaikkein armollisin, sentähden seuratkaa minua ja totelkaa käskyäni.»');
INSERT INTO "finnish" VALUES(20,91,'20:91','He vastasivat: »Emme lakkaa sitä palvelemasta, ennen kuin Mooses palaa luoksemme.»');
INSERT INTO "finnish" VALUES(20,92,'20:92','Mooses lausui: »Oi Aaron! Kun näit heidän joutuvan harhaannukseen,');
INSERT INTO "finnish" VALUES(20,93,'20:93','mikä esti sinua seuraamasta minua? Etkö tottele käskyäni?»');
INSERT INTO "finnish" VALUES(20,94,'20:94','Hän vastasi: »Oi äitini poika! Älä tartu partaani äläkä päähäni. Pelkäsin todella sinun sanovan: ''Olet aikaansaanut hajaannusta Israelin lasten keskuudessa odottamatta minun ratkaisuani.''»');
INSERT INTO "finnish" VALUES(20,95,'20:95','Mooses sanoi: »Miten on sinun laitasi, oi Saamiri?»');
INSERT INTO "finnish" VALUES(20,96,'20:96','Hän vastasi: »Minä näin, mitä he eivät nähneet; otin pölyä lähettilään jalanjäljistä, mutta sitten heitin sen pois. Tämä näytti parhaalta mielestäni.»');
INSERT INTO "finnish" VALUES(20,97,'20:97','Mooses lausui: »Mene matkaasi, osasi on tässä elämässä sanoa: ''Älkää minuun koskeko!'' Totisesti, sinua varten on määrähetki, jota et voi välttää. Katso jumalaasi, jota niin kauan palvelit, Me poltamme sen ja sitten sirotamme tuhkan mereen.»');
INSERT INTO "finnish" VALUES(20,98,'20:98','Jumalanne on ainoa Jumala: Hänen rinnallaan ei ole muuta jumalaa. Hän sulkee tietoonsa kaikki asiat.');
INSERT INTO "finnish" VALUES(20,99,'20:99','Näin olemme kertonut sinulle siitä, mitä aikaisemmin on tapahtunut, ja olemme antanut sinulle muistutuksen Itsestämme,');
INSERT INTO "finnish" VALUES(20,100,'20:100','joka kääntyy pois, saa ylösnousemuksen päivänä kantaa taakkansa.');
INSERT INTO "finnish" VALUES(20,101,'20:101','Ikuisesti he saavat sitä tehdä, ja surkea on heidän kuormansa oleva ylösnousemuksen päivänä.');
INSERT INTO "finnish" VALUES(20,102,'20:102','Sinä päivänä puhalletaan pasuunoihin, ja Me kokoamme syntiset sokaistuina.');
INSERT INTO "finnish" VALUES(20,103,'20:103','Hiljaa he silloin puhuvat toisillensa ja sanovat: »Olette viipyneet vain kymmenen (päivää).»)');
INSERT INTO "finnish" VALUES(20,104,'20:104','Me tiedämme parhaiten, mitä he sanovat, kun lujauskoisin heistä lausuu: »Ette ole viipyneet kuin päivän.»');
INSERT INTO "finnish" VALUES(20,105,'20:105','Ja he tekevät sinulle kysymyksiä vuorista. Vastaa: »Herrani on hajoittava ne perustuksiaan myöten.»');
INSERT INTO "finnish" VALUES(20,106,'20:106','Niiden paikalle Hän jättää aution erämaan,');
INSERT INTO "finnish" VALUES(20,107,'20:107','jossa et ole näkevä kohokohtaa etkä kukkulaa.');
INSERT INTO "finnish" VALUES(20,108,'20:108','Sinä päivänä he seuraavat kutsujaa, joka kulkee suoraa tietä. Silloin heidän äänensäkin vaimentuvat laupeimman Jumalan edessä, niin että voit kuulla vain hiljaista sorinaa.');
INSERT INTO "finnish" VALUES(20,109,'20:109','Sinä päivänä ei kenenkään puoltopuheesta ole hyötyä, ellei laupein Jumala häntä hyväksy tai mielisty hänen sanoihinsa.');
INSERT INTO "finnish" VALUES(20,110,'20:110','Hän tietää, mitä heillä on edessään ja mitä takanaan, mutta heidän tietonsa ei tätä käsitä.');
INSERT INTO "finnish" VALUES(20,111,'20:111','Ja kaikki kasvot nöyrtyvät ikuisesti elävän, ikuisesti pysyvän edessä. Ja turmioon joutuu se, joka vääryyttä harjoittaa.');
INSERT INTO "finnish" VALUES(20,112,'20:112','Mutta ken tekee hyvää ja on uskovainen, hänen ei tarvitse pelätä vääryyttä eikä ansionsa menetystä.');
INSERT INTO "finnish" VALUES(20,113,'20:113','Tällä tavoin me olemme lähettänyt maan päälle arabiankielisen Koraanin ja selittänyt eri tavoin niitä seikkoja, joilla heitä on uhattu, jotta he hartautta harjoittaisivat tai tulisivat ymmärtäväisiksi.');
INSERT INTO "finnish" VALUES(20,114,'20:114','Ylevä korkeudessaan on Jumala, Kuningas, Totuus. Mutta, Muhammed, älä pidä kiirettä Koraanin suhteen, ennen kuin se täydellisesti on sinulle ilmoitettu. Sano: »Herra, avarra minua tiedossa!»');
INSERT INTO "finnish" VALUES(20,115,'20:115','Totisesti, jo aikaisemmin Me annoimme käskymme Aadamille, mutta hän unohti sen, emmekä havainnut hänessä lujaa kestävyyttä.');
INSERT INTO "finnish" VALUES(20,116,'20:116','Kun sanomme enkeleille: »Kumartakaa ihmistä (Aadamia)», niin he kumarsivat muut paitsi Iblis (saatana), tämä kieltäytyi.');
INSERT INTO "finnish" VALUES(20,117,'20:117','Senvuoksi sanoimme: »Oi ihminen (Aadam), totisesti, tämä on sinun ja vaimosi vihollinen. Älä anna hänen karkoittaa teitä puutarhasta (paratiisista), sillä silloin tulet onnettomaksi.');
INSERT INTO "finnish" VALUES(20,118,'20:118','Totisesti, puutarhassa sinun ei tarvitse nähdä nälkää eikä olla vaatteita vailla.');
INSERT INTO "finnish" VALUES(20,119,'20:119','Eikä sinun tarvitse olla janoissasi eikä auringonpaahteessa.»');
INSERT INTO "finnish" VALUES(20,120,'20:120','Silloin saatana vietteli heidät sanoen: »Oi ihminen (Aadam), näytänkö sinulle kuolemattomuuden puun ja kuningaskunnan, joka ei häviä?»');
INSERT INTO "finnish" VALUES(20,121,'20:121','Niin kumpikin söi siitä, jolloin heidän alastomuutensa ilmeni heille ja he alkoivat peitellä itseään puutarhan lehdillä. Siten ihminen (Aadam) oli tottelematon Herralleen ja lankesi syntiin.');
INSERT INTO "finnish" VALUES(20,122,'20:122','Mutta senjälkeen hänen Herransa valitsi hänet jälleen, kääntyi hänen puoleensa ja antoi hänelle johdatuksensa.');
INSERT INTO "finnish" VALUES(20,123,'20:123','Hän lausui: »Lähtekää kumpikin täältä maan päälle, ja teistä tulee toistenne viholliset. Kun lähetän teille johdatukseni, niin se, joka sitä seuraa, ei joudu harhaan eikä tule onnettomaksi.');
INSERT INTO "finnish" VALUES(20,124,'20:124','Mutta joka kääntää selkänsä Minun varoitukselleni, hänen on eläminen ahdingossa, ja sokeana hän herää ylösnousemuksen päivään.»');
INSERT INTO "finnish" VALUES(20,125,'20:125','Hän on sanova: »Herra, miksi herätit minut sokeana, vaikka aikaisemmin olin näkevä?»');
INSERT INTO "finnish" VALUES(20,126,'20:126','Herra vastaa: »Niin on. Sinä sait merkkimme, mutta ylenkatsoit ne, ja juuri senvuoksi Me nyt hylkäämme sinut.»');
INSERT INTO "finnish" VALUES(20,127,'20:127','Tällä tavoin me palkitsemme sitä, joka on hillitön eikä usko Herransa ilmoituksiin. Tulevan päivän kuritus on vielä raskaampi ja jatkuvampi.');
INSERT INTO "finnish" VALUES(20,128,'20:128','Eivätkö he tiedä, että olemme tuhonnut niin monta ennen heitä elänyttä sukupolvea, joiden asuinsijoilla he kuljeskelevat? Totisesti tässä on merkkejä niille, joilla on ymmärrystä.');
INSERT INTO "finnish" VALUES(20,129,'20:129','Ellei Herrasi sana olisi edellä käynyt, olisi heidän tuhonsa jo tullut, mutta heidän hetkensä on määrätty.');
INSERT INTO "finnish" VALUES(20,130,'20:130','Siksi kestä kärsivällisesti mitä he puhuvat, ja ylistä Herraasi kiittämällä Häntä ennen auringonnousua ja ennen sen laskua, ja yön hetkinä ylistä Häntä samoin kuin päivän tunteina, jotta tulisit hyvälle mielelle.');
INSERT INTO "finnish" VALUES(20,131,'20:131','Älä käännä silmiäsi tämän maailman elämän loistoa kohti, josta olemme antanut muutamien nauttia, voidaksemme heitä koetella. Sillä Herrasi antimet ovat paremmat ja pysyvämmät.');
INSERT INTO "finnish" VALUES(20,132,'20:132','Velvoita kansaasi rukoilemaan ja ole myös itse siinä uuttera. Me emme pyydä sinulta huolenpitoa. Me pidämme huolen sinusta. Ja hurskauden loppu on hyvä.');
INSERT INTO "finnish" VALUES(20,133,'20:133','He sanovat: »Miksi hän ei tuo meille merkkiä Herraltansa?» Eikö heille todella ole jo annettu selvä vahvistus aikaisempien kirjoituksien suhteen?');
INSERT INTO "finnish" VALUES(20,134,'20:134','Ja jos olisimme saattanut heidät jonkin vitsauksen alaisiksi ennen profeetan tuloa, he olisivat sanoneet: »Herra, miksi et lähettänyt luoksemme sananjulistajaa, jotta olisimme voineet seurata käskyäsi, ennenkuin meidät masennettiin ja häväistiin?»');
INSERT INTO "finnish" VALUES(20,135,'20:135','Vastaa heille: »Kaikki odottavat, sentähden odottakaa tekin, sillä pian tulette tietämään, ketkä ovat oikean tien kulkijoita ja ketkä ovat saaneet johdatuksen.»');
INSERT INTO "finnish" VALUES(21,1,'21:1','Tilinteko lähestyy ihmisiä, mutta suruttomina he kääntyvät pois.');
INSERT INTO "finnish" VALUES(21,2,'21:2','Heidän Herraltaan ei tule yhtäkään uutta varoitusta, jota he kuuntelisivat pilkkaa tekemättä.');
INSERT INTO "finnish" VALUES(21,3,'21:3','Heidän sydämensä ovat huolettomat, ja väärämieliset juttelevat salaa ja sanovat: »Tämä mies on vain ihmisolento kuten tekin. Sallitteko siis hänen lumouksensa vaikuttaa itseenne vaikka havaitsette sen?»');
INSERT INTO "finnish" VALUES(21,4,'21:4','Hän vastasi: »Herrani tietää, mitä puhutaan taivaassa tai maan päällä, koska Hän on kaikkivaltias, kaikkitietävä.»');
INSERT INTO "finnish" VALUES(21,5,'21:5','Mutta he sanovat: »Tämä on vain unennäköjen sekasotkua. Hän on tämän keksinyt. Hän on runoilija. Esittäköön sen vuoksi meille tunnusmerkin, kuten entisetkin lähettiläät tekivät.»');
INSERT INTO "finnish" VALUES(21,6,'21:6','Eiväthän ennen heitä nekään kaupungit uskoneet, jotka me tuhosimme. Ottaisivatko siis nämä uskoakseen?');
INSERT INTO "finnish" VALUES(21,7,'21:7','Ennen sinuakin lähetimme vain ihmisiä, joille annoimme ilmestyksemme. Kysykää siis niiltä, jotka ovat ottaneet varoitusta noudattaakseen, ellette itse tiedä.');
INSERT INTO "finnish" VALUES(21,8,'21:8','Emme tehnyt heistä olentoja, jotka eivät kaivanneet ruokaa, eivätkä he olleet kuolemattomia.');
INSERT INTO "finnish" VALUES(21,9,'21:9','Sen jälkeen täytimme heille antamamme lupauksen ja pelastimme heidät ja kenet tahdoimme, mutta hillittömät Me tuhosimme.');
INSERT INTO "finnish" VALUES(21,10,'21:10','Totisesti, olemme ylhäältä ilmaissut teille kirjan, jossa teitä varoitetaan. Ettekö jo ymmärrä?');
INSERT INTO "finnish" VALUES(21,11,'21:11','Kuinka monta väärämielistä kaupunkia Me hävitimmekään! Ja kutsuimme sitten esiin toisen kansan.');
INSERT INTO "finnish" VALUES(21,12,'21:12','Kun he tunsivat voimamme, niin katso, he alkoivat paeta.');
INSERT INTO "finnish" VALUES(21,13,'21:13','»Älkää paetko, vaan palatkaa sen rikkauden ääreen, joka teille on suotu, ja asuntoihinne, kun teiltä kysytään tekojanne.»');
INSERT INTO "finnish" VALUES(21,14,'21:14','He sanoivat: »Voi meitä, me olimme todella väärämielisiä!»');
INSERT INTO "finnish" VALUES(21,15,'21:15','Ja näin he jatkoivat huutoaan, kunnes Me heidät leikkasimme kuin viljan ja sammutimme kuin tulen.');
INSERT INTO "finnish" VALUES(21,16,'21:16','Emme ole pilan vuoksi luonut taivasta ja maata ja kaikkea niiden välillä olevaa.');
INSERT INTO "finnish" VALUES(21,17,'21:17','Jos olisimme halunnut itsellemme leikkikalun, olisimme voinut sen itsekin tehdä. Mutta emme aio sellaista.');
INSERT INTO "finnish" VALUES(21,18,'21:18','Ei, Me heitämme totuuden valhetta vastaan, niin että se musertaa sen pään, ja katso, se häviää! Mutta voi teitä valheittenne tähden!');
INSERT INTO "finnish" VALUES(21,19,'21:19','Hänelle kuuluu jokainen, joka on taivaassa ja maan päällä. Ja ne, jotka ovat Hänen puolellaan, eivät ole liian ylpeitä Häntä palvelemaan, eivätkä he siihen väsy.');
INSERT INTO "finnish" VALUES(21,20,'21:20','Öin ja päivin he Häntä lakkaamatta ylistävät.');
INSERT INTO "finnish" VALUES(21,21,'21:21','Tai olisivatko he hankkineet maan päältä jumalia, jotka voivat herättää kuolleita?');
INSERT INTO "finnish" VALUES(21,22,'21:22','Jos taivaassa ja maan päällä olisi muita jumalia kuin Herra, niin taivas ja maa olisivat joutuneet sekasortoon. Siksi kunnia olkoon Jumalalle, voiman Herralle, Hän on niiden yläpuolella, joita he Hänen rinnalleen asettavat.');
INSERT INTO "finnish" VALUES(21,23,'21:23','Hänen ei tarvitse vastata siitä, mitä on tehnyt, mutta heidän on kaikesta vastattava.');
INSERT INTO "finnish" VALUES(21,24,'21:24','Ovatko he omaksuneet muita jumalia Hänen rinnalleen? Sano: »Esittäkää todisteenne. Tämä (Koraani) on varoituksena niille, jotka nyt ovat kanssani, sekä niille, jotka olivat ennen minua.» Mutta useimmat heistä eivät tunne totuutta, vaan kääntyvät pois.');
INSERT INTO "finnish" VALUES(21,25,'21:25','Emme lähettänyt ennen sinua ainoatakaan lähettilästä, jolle emme olisi ilmaissut: »Totisesti, ei ole muuta jumalaa kuin Minä, siksi palvelkaa Minua.»');
INSERT INTO "finnish" VALUES(21,26,'21:26','He väittävät Laupeimman hankkineen itselleen pojan. Kunnia yksin Hänelle! He (enkelit) ovat vain kunnioitettavia palvelijoita.');
INSERT INTO "finnish" VALUES(21,27,'21:27','He eivät aloita puhetta ennen Häntä, vaan toimivat Hänen käskyään täyttäen.');
INSERT INTO "finnish" VALUES(21,28,'21:28','Hän tietää, mitä heillä on edessään ja mitä on heidän jäljessään, eivätkä he voi puoltaa ketään muuta kuin sellaista, jonka Hän hyväksyy; ja he pelkäävät Hänen epäsuosiotaan.');
INSERT INTO "finnish" VALUES(21,29,'21:29','Ja jos joku heistä sanoisi: »Totisesti, minä olen jumala kuten Hänkin», niin sellaiselle Me annamme palkaksi helvetin. Tällä tavoin Me palkitsemme vääryyden harjoittajat.');
INSERT INTO "finnish" VALUES(21,30,'21:30','Eivätkö uskottomat huomaa, että taivaat ja maa olivat sulkeutuneet, kunnes Me erotimme ne toisistansa? Vedestä Me olemme luonut kaikki elolliset olennot. Eivätkö he sittenkään usko?');
INSERT INTO "finnish" VALUES(21,31,'21:31','Me asetimme myös vahvoja vuoria maahan, ettei se liikkuisi heidän allansa. Ja Me aukaisimme sen kamaraan leveitä kulkuväyliä, jotta he vaeltaisivat.');
INSERT INTO "finnish" VALUES(21,32,'21:32','Ja Me rakensimme vankan taivaanlaen; kuitenkin he kääntyvät pois sen merkeistä.');
INSERT INTO "finnish" VALUES(21,33,'21:33','Ja juuri Hän on luonut yön ja päivän sekä auringon ja kuun; kaikki kulkevat määrättyä rataansa.');
INSERT INTO "finnish" VALUES(21,34,'21:34','Ainoallekaan ihmiselle ennen sinua emme ole antanut kuolemattomuutta. Jos sinun on kuoltava, pystyisivätkö he siis alati elämään?');
INSERT INTO "finnish" VALUES(21,35,'21:35','Jokaisen sielun on maistettava kuolema. Me koettelemme teitä sekä pahalla että hyvällä saattamalla teidät kiusauksiin. Ja Meidän luoksemme teidän on palattava.');
INSERT INTO "finnish" VALUES(21,36,'21:36','Kun uskottomat sinut näkevät, he pitävät sinua pilkkanaan: »Onko tämä se, joka puhuu teidän jumalistanne?» He ovat niitä, jotka kieltävät Armahtajan varoituksen.');
INSERT INTO "finnish" VALUES(21,37,'21:37','Ihminen on kiireessä luotu. Näytän teille pian merkkini, mutta älkää pyytäkö Minua kiirehtimään!');
INSERT INTO "finnish" VALUES(21,38,'21:38','Ja he sanovat: »Milloin tämä uhkaus sitten täyttyy, jos olet totuudellinen?»');
INSERT INTO "finnish" VALUES(21,39,'21:39','Jospa uskottomat tietäisivät sen hetken tulon, jolloin he eivät kykene torjumaan tulen liekkejä kasvoiltaan eivätkä selästään eivätkä saa mitään apua!');
INSERT INTO "finnish" VALUES(21,40,'21:40','Se on saapuva yht''äkkiä yllättäen heidät. He eivät voi torjua sen tuloa eikä heille suoda vähääkään armonaikaa.');
INSERT INTO "finnish" VALUES(21,41,'21:41','Pilkattiinhan totisesti lähettiläitä ennen sinuakin, mutta pilkkaajat joutuivat sen uhreiksi, mille olivat nauraneet.');
INSERT INTO "finnish" VALUES(21,42,'21:42','Sano: »Kuka voi teitä öin ja päivin suojella Armahtajaa vastaan?» Mutta he kääntävät selkänsä Herraa mainittaessa.');
INSERT INTO "finnish" VALUES(21,43,'21:43','Onko heillä omia jumalia, jotka pystyvät heitä puolustamaan meitä vastaan? He eivät voi auttaa edes itseään eivätkä pysty varjelemaan itseänsä Meiltä.');
INSERT INTO "finnish" VALUES(21,44,'21:44','Me olemme ylläpitänyt nämä ihmiset ja heidän esi~isänsä elämänsä loppuun saakka. Mutta eivätkö he havaitse, että Me piiritämme heidän maatansa supistaen sen rajoja? Toivovatko he pääsevänsä voitolle?');
INSERT INTO "finnish" VALUES(21,45,'21:45','Sano: »Minä varoitan teitä ilmestyksen mukaisesti, mutta kuurot eivät kuule kutsua, kun heitä varoitetaan.»');
INSERT INTO "finnish" VALUES(21,46,'21:46','Totisesti, jos henkäyskin heidän Herransa tuomiosta heitä kohtaisi, he varmasti sanoisivat: »Voi meitä, me olimme totisesti väärämielisiä!»');
INSERT INTO "finnish" VALUES(21,47,'21:47','Ylösnousemuksen päivänä Me käytämme oikeudenmukaista vaakaa, niin että kenellekään ei tehdä pienintäkään vääryyttä. Vaikka jokin teko painaisi sinapinsiemenen verran, niin Me otamme sen huomioon, Me olemme kyllin pätevä tilintekijä.');
INSERT INTO "finnish" VALUES(21,48,'21:48','Totisesti, Me annoimme Moosekselle ja Aaronille arvostelukyvyn valistukseksi sekä varoitukseksi niille, jotka pahaa karttoivat,');
INSERT INTO "finnish" VALUES(21,49,'21:49','Niille, jotka pelkäävät sydämissään Herraansa ja vavisten valmistuvat suureen hetkeen.');
INSERT INTO "finnish" VALUES(21,50,'21:50','Tämä on siunattu kehoitus, jonka olemme lähettänyt ylhäältä. Aiotteko sen siis hylätä?');
INSERT INTO "finnish" VALUES(21,51,'21:51','Totisesti, Me jo ennakolta ohjasimme Aabrahamin oikealle tielle, ja Me tunsimme hänet hyvin.');
INSERT INTO "finnish" VALUES(21,52,'21:52','Jo silloin, kun hän lausui isälleen ja kansalleen: »Mitä ovat nämä epäjumalat, joita te yhä palvelette?»');
INSERT INTO "finnish" VALUES(21,53,'21:53','He vastasivat: »Olemme nähneet jo esi-isiemme niitä palvelleen.»');
INSERT INTO "finnish" VALUES(21,54,'21:54','Hän sanoi: »Totisesti, te ja teidän esi-isänne olette olleet ilmeisessä harhassa.»');
INSERT INTO "finnish" VALUES(21,55,'21:55','He sanoivat: »Puhutko meille totuutta, vai oletko niitä, jotka vain laskevat leikkiä?»');
INSERT INTO "finnish" VALUES(21,56,'21:56','Hän vastasi: »Ei, teidän Herranne on taivaitten ja maan Herra, joka ne on luonut. Minä olen tämän todistaja.');
INSERT INTO "finnish" VALUES(21,57,'21:57','Ja Jumalan nimessä olen totisesti tekevä selvää teidän epäjumalistanne, kun käännytte pois.»');
INSERT INTO "finnish" VALUES(21,58,'21:58','Niin hän mursi ne palasiksi paitsi suurinta niistä, jotta he kenties saattaisivat palata sen eteen.');
INSERT INTO "finnish" VALUES(21,59,'21:59','He sanoivat: »Kuka on tehnyt näin meidän jumalillemme? Hän on totisesti pahantekijä.»');
INSERT INTO "finnish" VALUES(21,60,'21:60','Muutamat sanoivat: »Olemme kuulleet erään Aabrahamnimisen nuorukaisen puhuvan niistä.»');
INSERT INTO "finnish" VALUES(21,61,'21:61','He sanoivat: »Tuokaa hänet sitten kansan silmien eteen ehkä he voivat todistaa.»');
INSERT INTO "finnish" VALUES(21,62,'21:62','He sanoivat: »Oi Aabraham. Sinäkö olet tämän tehnyt jumalillemme?»');
INSERT INTO "finnish" VALUES(21,63,'21:63','Hän vastasi: »Varmaan sen joku teki; mutta onhan tässä heidän päämiehensä jäljellä, kysykää siltä, jos se voi puhua.»');
INSERT INTO "finnish" VALUES(21,64,'21:64','Silloin he miettivät mielessään ja syyttelivät toisiansa: »Totisesti, te olette väärällä tiellä.»');
INSERT INTO "finnish" VALUES(21,65,'21:65','Mutta sitten he joutuivat päästään pyörälle ja sanoivat: »Totisesti, sinähän tiedät, että nämä eivät puhu.»');
INSERT INTO "finnish" VALUES(21,66,'21:66','Hän lausui: »Palvelisitteko siis Jumalan sijasta sellaista, joka ei voi teitä vähääkään auttaa eikä myöskään vahingoittaa?');
INSERT INTO "finnish" VALUES(21,67,'21:67','Häpeä teille ja niille, joita palvelette Jumalan ohella. Ettekö jo ymmärrä?»');
INSERT INTO "finnish" VALUES(21,68,'21:68','He sanoivat: »Polttakaa hänet ja auttakaa jumalianne, jos jotakin aiotte tehdä!»');
INSERT INTO "finnish" VALUES(21,69,'21:69','Me sanoimme: »Oi tuli, pysy kylmänä ja suojele Aabrahamia.»');
INSERT INTO "finnish" VALUES(21,70,'21:70','He vainosivat häntä, mutta Me jätimme heidät häviölle.');
INSERT INTO "finnish" VALUES(21,71,'21:71','Ja Me pelastimme Hänet sekä Lootin maahan, jonka olimme siunannut maan kansoja varten.');
INSERT INTO "finnish" VALUES(21,72,'21:72','Me annoimme hänelle pojan, Iisakin, sekä pojanpojan, Jaakobin, ja teimme heistä kaikista hurskaita.');
INSERT INTO "finnish" VALUES(21,73,'21:73','Ja Me teimme heistä johtajia, jotka ohjasivat kansaa Meidän käskyjemme mukaan. Me määräsimme heidät suorittamaan hyviä tekoja ja uutterasti rukoilemaan sekä antamaan almuja. Ja he palvelivat ainoastaan Meitä.');
INSERT INTO "finnish" VALUES(21,74,'21:74','Ja Lootille Me annoimme viisautta ja tietoa ja pelastimme hänet kaupungista, jonka asukkaat harjoittivat iljettäviä tekoja. Totisesti, he olivat paha ja hillitön kansa.');
INSERT INTO "finnish" VALUES(21,75,'21:75','Me suljimme hänet armoomme; totisesti, hän kuului hyvien pariin.');
INSERT INTO "finnish" VALUES(21,76,'21:76','Entä Nooa. Kun hän huusi meitä muinoin, Me kuulimme häntä ja pelastimme hänet ja hänen perheensä suuresta onnettomuudesta.');
INSERT INTO "finnish" VALUES(21,77,'21:77','Me autoimme häntä kansaa vastaan, joka kielsi Meidän ilmoituksemme; totisesti, he olivat paha kansa, ja Me hukutimme heidät kaikki.');
INSERT INTO "finnish" VALUES(21,78,'21:78','Entä Daavid ja Salomo. Kun he antoivat tuomionsa eräästä pellosta, jonka ihmisten vuohet olivat yön aikana tallanneet, Me olimme heidän tuomionsa todistajana.');
INSERT INTO "finnish" VALUES(21,79,'21:79','Niin saatoimme Salomon ymmärtämään tapauksen ja kummallekin annoimme viisautta ja tietoa. Me alistimme lakeihimme vuoret ja linnut, jotka yhdessä Daavidin kanssa julistivat kunniaamme. Juuri Me saimme tämän aikaan.');
INSERT INTO "finnish" VALUES(21,80,'21:80','Me opetimme häntä myös valmistamaan teille haarniskoja suojaksi taisteluissanne. Mutta oletteko siitä kiitollisia?');
INSERT INTO "finnish" VALUES(21,81,'21:81','Ja Salomon Me opetimme käyttämään myrskytuulia, jotka Hänen käskystänsä puhalsivat sen maan ylitse, jonka olimme siunannut. Me tiedämme täydellisesti kaiken.');
INSERT INTO "finnish" VALUES(21,82,'21:82','Me annoimme myös hänen valtaansa muutamia villikansoja, jotka sukelsivat hänelle helmiä ja tekivät muutakin. Ja Me olimme heidän vartijanaan.');
INSERT INTO "finnish" VALUES(21,83,'21:83','Muistettakoon myös Jobia, joka rukoili Herraansa: »Totisesti, onnettomuus on minua kohdannut, mutta Sinä olet kaikkein armollisin armonosoittajista.»');
INSERT INTO "finnish" VALUES(21,84,'21:84','Sentähden Me kuulimme häntä ja poistimme hänen vaivansa ja annoimme takaisin hänen perheensä sekä lisäksi paljon muuta; kaiken armonamme ja kehoituksena palvelijoillemme.');
INSERT INTO "finnish" VALUES(21,85,'21:85','Entä Ismael ja Idris ja Dzulkifl, jotka kaikki olivat lujia.');
INSERT INTO "finnish" VALUES(21,86,'21:86','Me suljimme heidät armoomme. Totisesti, kaikki he kuuluivat hyvien joukkoon.');
INSERT INTO "finnish" VALUES(21,87,'21:87','Entä sitten Joona, kun hän pyrki pois, täynnä vihaa, luullen, että emme voisi häntä saavuttaa; sitten huusikin hän ahdingostaan: »Ei ole muuta jumalaa kuin Sinä, kunnia olkoon Sinulle. Totisesti, minä olen vääryyttä tehnyt.»');
INSERT INTO "finnish" VALUES(21,88,'21:88','Silloin Me vastasimme hänelle ja vapautimme hänet murheestaan. Tällä tavoin Me pelastamme uskovaiset.');
INSERT INTO "finnish" VALUES(21,89,'21:89','Entä Sakarias. Hän huusi Herraansa: »Älä jätä minua yksin. Sinä olet paras perillinen.»');
INSERT INTO "finnish" VALUES(21,90,'21:90','Silloin Me vastasimme hänelle ja annoimme hänelle Johanneksen sekä teimme hänen vaimonsa hedelmälliseksi. Totisesti, he (entiset profeetat) kilpailivat keskenänsä hyvän tekemisessä ja rukoilivat Meitä toivoen ja peläten. He olivat nöyriä edessämme.');
INSERT INTO "finnish" VALUES(21,91,'21:91','Muistettakoon sitä naista (Mariaa), joka suojeli viattomuuttaan, ja Me puhalsimme häneen henkeämme ja teimme hänet ja hänen poikansa ihmemerkiksi kansoille.');
INSERT INTO "finnish" VALUES(21,92,'21:92','Totisesti, nämä ihmiset kuuluvat teidän uskonyhteyteenne. Ja Minä olen teidän Herranne. Palvelkaa siis Minua!»');
INSERT INTO "finnish" VALUES(21,93,'21:93','Mutta he (toiset) ovat hajaantuneet uskonyhteydestään. Kuitenkin heidän kaikkien on palattava Meidän luoksemme.');
INSERT INTO "finnish" VALUES(21,94,'21:94','Joka suorittaa hyviä tekoja ja on uskovainen, hänen ponnistelunsa eivät jää tunnustusta vaille, vaan Me kirjoitamme ne muistiin.');
INSERT INTO "finnish" VALUES(21,95,'21:95','Ja sellaisen kaupungin asujaimia, jonka olemme tuhonnut, on kielletty palaamasta takaisin.');
INSERT INTO "finnish" VALUES(21,96,'21:96','Kunnes Goog ja Maagog päästetään irti ja ryntäävät alas kaikilta kukkuloilta.');
INSERT INTO "finnish" VALUES(21,97,'21:97','Ja kun totinen lupaus lähenee täyttymystään, silloin uskottomain silmät pysähtyvät tuijottamaan kauhistuneina, ja he sanovat: »Voi meitä, me olimme totisesti välinpitämättömiä tästä; niin, me olimme jumalattomia.»');
INSERT INTO "finnish" VALUES(21,98,'21:98','Totisesti, te ja ne epäjumalat, joita te Jumalan sijasta palvelitte, olette helvetin polttoainetta. Sinne te olette menossa.');
INSERT INTO "finnish" VALUES(21,99,'21:99','Jos ne olisivat olleet jumalia, eivät ne olisi sinne joutuneet, mutta kaikki ne saavat siellä iäti pysyä.');
INSERT INTO "finnish" VALUES(21,100,'21:100','Siellä ne saavat parkua eivätkä ne siellä lohdutusta kuule.');
INSERT INTO "finnish" VALUES(21,101,'21:101','Totisesti, ne, joille olemme jo määrännyt hyvän osan, pysyvät sieltä kaukana.');
INSERT INTO "finnish" VALUES(21,102,'21:102','He eivät kuule sieltä hiiskahdustakaan, vaan saavat syventyä siihen, mihin sydämissään haluavat.');
INSERT INTO "finnish" VALUES(21,103,'21:103','Suuri kauhistus ei heitä hämmennä, vaan enkelit tulevat heitä vastaan ja lausuvat: »Tämä on teidän päivänne, joka teille oli luvattu.»');
INSERT INTO "finnish" VALUES(21,104,'21:104','Sinä päivänä Me käärimme taivaan kokoon, kuten kirjoitukset kääröksi kootaan. Samoin kuin Me alussa loimme oliot, niin saatamme ne takaisin sinne, mistä ovat tulleet. Tämä on Meitä velvoittava lupaus. Totisesti, näin tulemme tekemään.');
INSERT INTO "finnish" VALUES(21,105,'21:105','Olemmehan kirjoittanut Kirjaan varoituksen jälkeen: »Totisesti, hurskaat palvelijani saavat periä maan.»');
INSERT INTO "finnish" VALUES(21,106,'21:106','Tämä on totinen sanoma niille, jotka Minua palvelevat.');
INSERT INTO "finnish" VALUES(21,107,'21:107','Olemme lähettänyt sinut, oi Muhammed, vain armona maailman kansoille.');
INSERT INTO "finnish" VALUES(21,108,'21:108','Sano: »Minut on määrätty julistamaan, että teidän Jumalanne on armon Jumala. Ettekö siis olisi tottelevaisia?»');
INSERT INTO "finnish" VALUES(21,109,'21:109','Mutta jos he kääntyvät pois, sano silloin: »Olen varoittanut teitä kaikkia ilman erotusta enkä tiedä, onko se, mitä teille on määrätty, lähellä vai kaukana.');
INSERT INTO "finnish" VALUES(21,110,'21:110','Totisesti, Hän tietää mitä äänekkäästi puhutaan, ja Hän tietää myös, mitä te salaatte.');
INSERT INTO "finnish" VALUES(21,111,'21:111','En tiedä, tuleeko siitä teille koettelemus vaiko ajallinen nautinto.»');
INSERT INTO "finnish" VALUES(21,112,'21:112','Hän lausui: »Herra, tuomitse oikeudenmukaisesti. Herramme on Armahtaja, jota kutsutaan avuksi niitä vastaan, joita he Hänen vertaisikseen asettavat.»');
INSERT INTO "finnish" VALUES(22,1,'22:1','Oi ihmiset! Kunnioittakaa peläten Herraanne. Totisesti, (tuomion) hetken järkytys on hirmuinen tapahtuma.');
INSERT INTO "finnish" VALUES(22,2,'22:2','Sinä päivänä, jolloin sen tulette näkemään, jokainen imettävä vaimo hämääntyy unohtaen imettämänsä lapsen ja jokainen raskaana oleva nainen synnyttää sikiönsä kesken. Olet näkevä ihmiset ikäänkuin päihtyneinä, mutta he eivät ole humalassa, vaan Jumalan rangaistus tulee ankarana.');
INSERT INTO "finnish" VALUES(22,3,'22:3','Ihmisten joukossa on sellaisia, jotka kiistelevät Jumalasta, vaikka ovat tietoa vailla ja seuraavat jokaista karkoitettua pahaahenkeä;');
INSERT INTO "finnish" VALUES(22,4,'22:4','jollaisesta on kirjoitettu, että ken liittyy häneen, sen hän vie harhaan ja johtaa kohti palavan tulen tuskaa.');
INSERT INTO "finnish" VALUES(22,5,'22:5','Oi ihmiset, jos epäilette kuolleistaheräämistä, niin olemmehan totisesti luonut teidät tomusta, sitten elon siemenestä, sitten hyytyneestä verestä, sitten lihamöhkäleestä, sopusuhtaisesta muodoltaan sekä myös epäsuhtaisesta, jotta kaikkivaltamme teille selviäisi. Minkä haluamme, sen kehitämme kohtuihin määräajaksi. Sitten saatamme teidät lapsina esille ja kasvatamme teitä, jotta saavuttaisitte kypsyysiän. Teidän joukossanne on joku, joka kuolee aikanaan, mutta joku myöskin, joka saavuttaa elämän raihnaisen kauden, jolloin hän ei enää tiedä mitään, vaikka aikaisemmin on tiennyt. Ja katso, maa saattaa olla paahtunut hedelmättömäksi, mutta kun lähetämme sille sateen ylhäältä, se herää ja paisuu ja kasvaa kaikenlaisia ihania yrttejä.');
INSERT INTO "finnish" VALUES(22,6,'22:6','Näin tapahtuu, koska Jumala on Totuus ja koska Hän saa kaiken kuolleen eloon ja koska Hänellä on mahti kaiken yli,');
INSERT INTO "finnish" VALUES(22,7,'22:7','ja koska tuomion hetki on epäilemättä tuleva, sillä Jumala on herättävä ne, jotka on kätketty hautoihin.');
INSERT INTO "finnish" VALUES(22,8,'22:8','Ihmisten keskuudessa on joku, joka kiistelee Jumalasta, vaikka on vailla tietoa ja johdatusta ja valaisevaa kirjaa.');
INSERT INTO "finnish" VALUES(22,9,'22:9','Hän kääntää halveksien selkänsä johtaakseen toisia harhaan Jumalan tieltä. Häntä kohtaa häpeä tässä maailmassa, ja ylösnousemuksen päivänä annamme hänen maistaa palamisen tuskaa.');
INSERT INTO "finnish" VALUES(22,10,'22:10','»Tämä on sinun saatava siitä, mitä omat kätesi ovat edelläkäypinä tekoinasi lähettäneet ja koska Jumala ei tee palvelijoineen vähintäkään vääryyttä.»');
INSERT INTO "finnish" VALUES(22,11,'22:11','Ihmisten joukossa on myös joku, joka palvelee Jumalaa kuin kahden vaiheilla. Jos hänen käy hyvin, hän on tyytyväinen, mutta jos häntä kohtaa koettelemus, hän kääntyy pois ja menettää sekä tämän maailman että tulevan elämän. Näin joudutaan selvästi turmioon.');
INSERT INTO "finnish" VALUES(22,12,'22:12','Jumalan ohella hän rukoilee sellaista, mikä ei tuota hänelle vahinkoa enempää kuin hyötyäkään. Näin joudutaan suureen harhaan.');
INSERT INTO "finnish" VALUES(22,13,'22:13','Hän rukoilee sellaista, joka häntä pikemmin vahingoittaa kuin auttaa. Todellakin, kehno on suojelija ja kehno on myös hänen seuraajansa.');
INSERT INTO "finnish" VALUES(22,14,'22:14','Totisesti, jotka uskovat ja tekevät hyvää, heidät Jumala päästää puutarhoihin, joiden kautta purot virtaavat. Jumala tekee todella mitä haluaa.');
INSERT INTO "finnish" VALUES(22,15,'22:15','Ken ikinä luulee, ettei Jumala auta häntä tässä maailmassa eikä tulevassa, hän kiinnittäköön köyden riippumaan ylhäältä ripustaakseen siihen itsensä. Leikatkoon sitten köyden poikki ja todetkoon, poistiko hänen temppunsa sen, mikä häntä vihastutti.');
INSERT INTO "finnish" VALUES(22,16,'22:16','Näin Me olemme lähettänyt ilmoituksemme selvin vertauskuvin; kyllä Jumala ohjaa sitä, ketä haluaa.');
INSERT INTO "finnish" VALUES(22,17,'22:17','Totisesti, on olemassa sellaisia, jotka uskovat, ja on juutalaisia ja saabalaisia, kristittyjä ja magian harjoittajia sekä myös monijumalaisia. Ylösnousemuksen päivänä Jumala tekee erotuksen heidän kaikkien välillään. Totisesti, Jumala on kaiken todistaja.');
INSERT INTO "finnish" VALUES(22,18,'22:18','Etkö ole havainnut. että Jumalan edessä alistuu kaikki, mikä on taivaassa ja mikä on maan päällä, samaten aurinko ja kuu ja tähdet, samoin vuoret ja puut ja eläimet ja suuri joukko ihmisiä. Mutta monia kohtaan on paikallaan rangaistus. Ja kenet Jumala alentaa, sitä ei kukaan enää saata kunniaan. Totisesti, Jumala tekee mitä hyväksi näkee.');
INSERT INTO "finnish" VALUES(22,19,'22:19','On kaksi ryhmää, jotka kiistelevät Herrastaan. Siksi onkin uskottomille leikattu tuliset vaatteet, ja heidän päänsä ylitse vuodatetaan kiehuvaa vettä.');
INSERT INTO "finnish" VALUES(22,20,'22:20','Se liuottaa heidän sisuksensa sekä myös heidän ihonsa.');
INSERT INTO "finnish" VALUES(22,21,'22:21','Ja heitä piestään rautasauvoin.');
INSERT INTO "finnish" VALUES(22,22,'22:22','Joka kerran, kun he tuskissaan haluavat päästä sieltä pois, heidät työnnetään sinne takaisin, ja heille sanotaan: »Maistakaa palamisen tuskaa.»');
INSERT INTO "finnish" VALUES(22,23,'22:23','Totisesti, jotka uskovat ja harjoittavat hyviä tekoja, heidän Jumala antaa astua puutarhoihin, joiden kautta virrat juoksevat. He saavat kantaa kultaisia rannerenkaita ja helmiä, ja heidän vaatteensa ovat silkkiä.');
INSERT INTO "finnish" VALUES(22,24,'22:24','Heitä opastetaan jalosti puhumaan ja heidät johdatetaan ainoan ylistetyn tielle.');
INSERT INTO "finnish" VALUES(22,25,'22:25','On tosin sellaisiakin, jotka ovat uskottomia ja eksyttävät toisia Jumalan tieltä ja pyhitetystä moskeijasta, johon pääsyn olemme tehnyt yhtä helpoksi paikkakunnan asukkaille kuin erämaista tuleville. Mutta kuka ikinä ryhtyy harjoittamaan petosta ja vääryyttä, hänen Me annamme maistaa tuskallista rangaistusta.');
INSERT INTO "finnish" VALUES(22,26,'22:26','Palauta mieleesi, miten Me osoitimme Aabrahamille temppelin seudun asuinpaikaksi ja lausuimme: »Älä aseta ketään muuta Minun vertaisekseni ja pidä Huoneeni puhtaana niitä varten, jotka kiertävät sen ympäri, jotka seisoen rukoilevat, jotka kumartuvat ja jotka maahan vaipuvat.»');
INSERT INTO "finnish" VALUES(22,27,'22:27','Julista myöskin ihmisille pyhiinvaellusta, että he tulevat luoksesi jalkaisin ja jäntevien kameelien selässä, jotka kestävät vaikeimpiakin taipaleita,');
INSERT INTO "finnish" VALUES(22,28,'22:28','jotta he voisivat itse nähdä nämä paikat ja niiden hyödyn heille itselleen, ja jotta he voisivat kiittää Jumalaa säädettyinä päivinä kaikesta siitä nelijalkaisesta karjasta, jonka Hän on heille suonut, ja sitten syödä itse ja ravita puutteenalaisia ja tarvitsevia.»');
INSERT INTO "finnish" VALUES(22,29,'22:29','Sitten he puhdistautukoot kaikesta epäsiisteydestä ja täyttäkööt lupauksensa sekä kulkekoot vanhan temppelin ympäri.');
INSERT INTO "finnish" VALUES(22,30,'22:30','Näin olkoon. Ja ken pitää kunniassa Jumalan pyhät määräykset, hänelle se on parhaaksi Herransa edessä. Ravinnoksenne saatte käyttää kaikkia nelijalkaisia eläimiä, paitsi niitä, jotka teille erikseen on mainittu. Välttäkää siksi epäjumalien saastaisuutta ja valheellisia puheita.');
INSERT INTO "finnish" VALUES(22,31,'22:31','Olkaa vilpittömiä Jumalan edessä asettamatta mitään Hänen vertaisekseen. Ken muitakin jumaloi, hän on kuin olisi taivaasta pudonnut ja petolinnut olisivat raastaneet hänet mukaansa taikka tuuli heittänyt hänet kaukaiseen paikkaan.');
INSERT INTO "finnish" VALUES(22,32,'22:32','Näin tulee olemaan. Jos joku kunnioittaa Jumalan määräyksiä, tämä johtuu varmasti hänen sydämensä hurskaudesta.');
INSERT INTO "finnish" VALUES(22,33,'22:33','Saatte käyttää hyväksenne karjaa määräajan, sitten se viedään uhrattavaksi vanhaan temppeliin.');
INSERT INTO "finnish" VALUES(22,34,'22:34','Jokaisen uskonnon tunnustajille Me olemme säätänyt uhritoimitukset, jotta he kiitollisina muistaisivat Jumalan nimeä kaiken sen nelijalkaisen karjan vuoksi, jonka olemme heille suonut. Teidän Jumalanne on ainoa Jumala, totelkaa siis Häntä, ja julista ilosanomaa nöyrille;');
INSERT INTO "finnish" VALUES(22,35,'22:35','niille, joiden sydämet väräjävät Jumalaa mainittaessa; niille, jotka pysyvät lujina, mikä tahansa heitä kohtaakin; ja niille, jotka suorittavat rukouksensa ja antavat almuja siitä, mitä olemme heille suonut.');
INSERT INTO "finnish" VALUES(22,36,'22:36','Paljon hyötyä teillä on uhrikameeleista, jotka olemme teille määrännyt merkiksi Jumalan uskonnosta. Siksi lausukaa Jumalan nimeä niiden ylitse, kun ne seisovat riveissä. Kun ne ovat vaipuneet kyljelleen, uhratkaa ne ja syökää niitä ja ravitkaa sitä köyhää, joka ei almua ano, ja myöskin kerjäläisiä. Näin Me olemme alistanut ne valtaanne, jotta osaisitte olla kiitollisia.');
INSERT INTO "finnish" VALUES(22,37,'22:37','Niiden liha ja veri ei kohoa Jumalan tykö, mutta sydämienne hurskaus riittää Hänelle. Näin Me olemme alistanut ne valtaanne, jotta ylistäisitte Jumalaa, koska Hän on antanut teille johdatuksensa. Julistakaa ilosanomaa niille, jotka hyvää tekevät.');
INSERT INTO "finnish" VALUES(22,38,'22:38','Jumala on totisesti karkoittava uskovaisten viholliset. Totisesti, Jumala ei rakasta valapattoa, kiittämätöntä ihmistä.');
INSERT INTO "finnish" VALUES(22,39,'22:39','Niille on annettu lupa taistella, joita vastaan on aloitettu sota, sillä heille on tehty vääryyttä, ja Jumala kyllä kykenee heitäauttamaan;');
INSERT INTO "finnish" VALUES(22,40,'22:40','heitä, jotka syyttä on karkoitettu kotoaan, koska ovat vain lausuneet: »Jumala on meidän Herramme.» Ellei Jumala olisi pitänyt kurissa eräitä ihmisiä toisten avulla, niin raunioiksi olisi tuhottu luostarit, kirkot ja synagogat sekä moskeijat, joissa Jumalan nimeä usein lausutaan. Totisesti, Jumala auttaa sellaista, joka auttaa Hänen asiaansa. Totisesti, Jumala on väkevä, mahtava.');
INSERT INTO "finnish" VALUES(22,41,'22:41','He ovat sellaisia, jotka, jos annamme heille valtaa maassa, suorittavat kyllä rukouksia, antavat säädetyt almut ja käskevät ihmisiä hyviin tekoihin sekä kieltävät heitä pahasta. Mutta Jumalalle kuuluu kaikkien asioiden päätösvalta.');
INSERT INTO "finnish" VALUES(22,42,'22:42','Mutta jos he torjuvat sinut valhettelijana, niin samoin tekivät edeltäjiäsi kohtaan jo aikaisemmin Nooan, Aadin ja Tamuudin kansat,');
INSERT INTO "finnish" VALUES(22,43,'22:43','sekä Aabrahamin ja Lootin kansat yhtä hyvin kuin Midianin miehet.');
INSERT INTO "finnish" VALUES(22,44,'22:44','Moosestakin pidettiin valhettelijana. Mutta Minä annoin uskottomille määräajan, jonka jälkeen yllätin heidät, ja miten ankara olikaan rangaistukseni.');
INSERT INTO "finnish" VALUES(22,45,'22:45','Kuinka moni kaupunki sai Meiltä tuhonsa jumalattomuutensa takia, niin että sen katot ovat sortuneet ja kaivot hylätyt ja linnat autioina.');
INSERT INTO "finnish" VALUES(22,46,'22:46','Eivätkö he ole kiertäneet maata, jotta heillä olisi sydän ymmärtää tai korvat kuulla? Sillä totisesti, eivät heidän silmänsä ole soaistut, vaan sydän heidän rinnassaan.');
INSERT INTO "finnish" VALUES(22,47,'22:47','He pyytävät sinua jouduttamaan tuomiota, mutta Jumala ei riko lupaustaan; onhan Herrallesi yksi päivä kuin tuhat vuotta, joilla te aikaa laskette.');
INSERT INTO "finnish" VALUES(22,48,'22:48','Kuinka monelle kaupungille annoinkaan armonaikaa, koska se oli väärämielinen. Mutta sitten yllätin sen; niin, Minun luokseni kaikki palaa.');
INSERT INTO "finnish" VALUES(22,49,'22:49','Sano: »Oi ihmiset! Tulen pelkkänä varoittajananne.');
INSERT INTO "finnish" VALUES(22,50,'22:50','Niitä varten, jotka uskovat ja hyvää tekevät, on anteeksiannon ihana palkinto.');
INSERT INTO "finnish" VALUES(22,51,'22:51','Mutta jotka yrittävät vastustaa Meidän imoitustamme, saavat paikkansa helvetin tulessa.');
INSERT INTO "finnish" VALUES(22,52,'22:52','Ennen sinua, oi Muhammed, emme ole lähettänyt ainoatakaan lähettilästä tai profeettaa, joiden tarkoitusperiä ei saatana olisi sotkenut. Mutta Jumala tekee tyhjäksi saatanan sekaantumiset. Jumala vahvistaa tunnusmerkkinsä, sillä Jumala on tietävä, viisas.');
INSERT INTO "finnish" VALUES(22,53,'22:53','Jumala muuttaa saatanan juonet koetukseksi niille, joiden sydämet ovat turmeltuneita ja kovia. Totisesti, väärintekijät menevät pitkälle vastahakoisuudessaan.');
INSERT INTO "finnish" VALUES(22,54,'22:54','Jumala tarkoittaa myös, että ne, joille on annettu tietoa, käsittäisivät, että tämä on Herralta tuleva totuus, jotta he uskoisivat siihen ja heidän sydämensä nöyrtyisivät Hänen edessään. Totisesti, Jumala ohjaa uskovaiset oikealle tielle.');
INSERT INTO "finnish" VALUES(22,55,'22:55','Mutta epäuskoiset eivät lakkaa epäilemästä, kunnes heidät yht''äkkiä yllättää määrähetki tai Heitä kohtaa tuhopäivän tuomio.');
INSERT INTO "finnish" VALUES(22,56,'22:56','Sinä päivänä kuninkuus kuuluu Jumalalle. Hän on antava heille kullekin tuomionsa. Silloin ne, jotka uskovat ja tekevät hyvää, pääsevät autuuden puutarhoihin.');
INSERT INTO "finnish" VALUES(22,57,'22:57','Mutta jotka ovat uskottomia ja hylkäävät tunnusmerkkimme, kuuluvat niihin, joita odottaa häpeällinen rangaistus.');
INSERT INTO "finnish" VALUES(22,58,'22:58','Mutta niille, jotka jättivät kotinsa ja lähtivät Jumalan tielle ja sitten surmattiin tai muutoin kuolivat, heille Jumala suo ihanan palkinnon. Totisesti, Jumala on paras huolehtija.');
INSERT INTO "finnish" VALUES(22,59,'22:59','Hän antaa heidän astua paikkaan, joka on heitä miellyttävä. Totisesti, Jumala on tietävä, hyvänsuopa.');
INSERT INTO "finnish" VALUES(22,60,'22:60','Näin on asia. Jos joku kostaa hänelle aiheutetun kärsimyksen samalla mitalla ja häntä sorretaan, niin varmasti Jumala on häntä auttava. Totisesti, Jumala on armollinen, anteeksiantavainen.');
INSERT INTO "finnish" VALUES(22,61,'22:61','Sillä Jumala antaa yön sulautua päivään ja päivän vaipua yöhön. Jumala on kuuleva, näkevä.');
INSERT INTO "finnish" VALUES(22,62,'22:62','Sillä Jumala on Totuus, ja se on valhetta, mitä he rukoilevat Hänen ohellaan, sillä Jumala on kaikista korkein, kaikista suurin.');
INSERT INTO "finnish" VALUES(22,63,'22:63','Etkö huomaa, että Jumala lähettää taivaasta sateen maan päälle, niin että maa rupeaa viheriöimään? Totisesti, Jumala on ylevä, kaikkivoipa.');
INSERT INTO "finnish" VALUES(22,64,'22:64','Hänelle kuuluu kaikki, mitä on taivaassa ja maan päällä. Totisesti, Jumala on rikas, ylistetty.');
INSERT INTO "finnish" VALUES(22,65,'22:65','Etkö ole ajatellut, että teidän hyväksenne Jumala on alistanut lakejansa noudattamaan kaiken, mitä maan päällä on, ja että Hänen käskyjensä mukaan laivat kulkevat merellä? Ja että Hän vain käskyllään pidättää taivasta luhistumasta maahan? Totisesti, Jumala on ihmissukua kohtaan myötämielisin ja armollisin.');
INSERT INTO "finnish" VALUES(22,66,'22:66','Juuri Hän on suonut sinulle elämän ja antaa sitten sinun kuolla herättääkseen sinut uuteen eloon. Mutta ihminen on totisesti kiittämätön.');
INSERT INTO "finnish" VALUES(22,67,'22:67','Jokaiselle kansalle Me olemme säätänyt hartausharjoituksia, joita he noudattavat. Älkööt he siksi kiistelkö kanssasi tästä asiasta. Huuda heitä Herrasi luokse, totisesti sinä kuljet oikeata tietä.');
INSERT INTO "finnish" VALUES(22,68,'22:68','Jos he kiistelevät kanssasi, sano silloin: »Jumala tietää parhaiten, mitä te teette.»');
INSERT INTO "finnish" VALUES(22,69,'22:69','Ylösnousemuksen päivänä on Jumala ratkaiseva kiistanne.');
INSERT INTO "finnish" VALUES(22,70,'22:70','Etkö tiedä, että Jumala tuntee kaiken, mikä on taivaassa ja maan päällä? Totisesti, tämä on Kirjassa. Totisesti, tämä on helppoa Jumalalle.');
INSERT INTO "finnish" VALUES(22,71,'22:71','Ja he palvelevat Jumalan ohella sellaista, jolle emme ole antanut mitään valtaa ja josta heillä ei ole mitään tietoa. Väärämielisillä ei ole ketään auttajaa.');
INSERT INTO "finnish" VALUES(22,72,'22:72','Kun Meidän selvät ilmoituksemme on heille esitetty, voit havaita uskottomien kasvoista vastustusta. He haluaisivat syöksyä niiden kimppuun, jotka kertovat heille merkeistämme. Sano: »Kerronko teille jotakin, mikä on vieläkin pahempaa? Se on tuli. Sen on Jumala luvannut niille, jotka ovat uskottomia. Mikä viheliäinen olinpaikka!»');
INSERT INTO "finnish" VALUES(22,73,'22:73','Oi ihmiset. Kuunnelkaa vertausta, joka esitetään: Totisesti, ne, joita te rukoilette Jumalan ohella, eivät voi luoda edes kärpästä, vaikkapa yhdessä sitä yrittäisivät. Ja jos kärpänen heiltä jotain veisi mukanaan, he eivät voisi saada sitä siltä takaisin; yhtä heikko on moinen avunantaja kuin avunanojakin.');
INSERT INTO "finnish" VALUES(22,74,'22:74','He eivät arvioi Jumalaa niin suureksi, kuin Hänet arvioida tulee. Totisesti, Jumala on valtava, mahtava.');
INSERT INTO "finnish" VALUES(22,75,'22:75','Jumala valitsee lähettiläänsä sekä enkelien että ihmisten keskuudesta. Totisesti, Jumala on kuuleva, näkevä.');
INSERT INTO "finnish" VALUES(22,76,'22:76','Hän tietää, mitä heillä on edessään ja mitä takanaan. Ja Jumalan luokse on kaikkien palattava.');
INSERT INTO "finnish" VALUES(22,77,'22:77','Te, jotka uskotte, kumartakaa, vaipukaa maahan asti, palvelkaa Herraanne ja tehkää hyvää, jotta teillä olisi menestystä.');
INSERT INTO "finnish" VALUES(22,78,'22:78','Kamppailkaa Jumalan tiellä ponnistellen, niinkuin Hänen arvonsa velvoittaa. Hän on teidät valinnut eikä ole uskontoonne asettanut teille mitään ylipääsemätöntä, vaan antanut teille isänne Aabrahamin uskon. Hän on kutsunut teitä muslimeiksi jo aikaisemmin ja myöskin tässä Koraanissa, jotta Sanan julistaja nyt olisi todistajanne ja te todistaisitte ihmissuvusta. Siksi suorittakaa rukouksenne ja antakaa säädetyt almut ja pysykää lujasti Jumalassa. Hän on teidän suojelijanne. Mikä oivallinen suojelija ja mikä oivallinen auttaja!');
INSERT INTO "finnish" VALUES(23,1,'23:1','Totisesti, uskovaiset menestyvät,');
INSERT INTO "finnish" VALUES(23,2,'23:2','ne, jotka nöyrtyvät rukoillessaan,');
INSERT INTO "finnish" VALUES(23,3,'23:3','ja jotka karttavat turhanpäiväistä puhetta,');
INSERT INTO "finnish" VALUES(23,4,'23:4','ja jotka antavat sovituksen almut');
INSERT INTO "finnish" VALUES(23,5,'23:5','ja jotka hillitsevät himojensa jäsenet,');
INSERT INTO "finnish" VALUES(23,6,'23:6','paitsi vaimojensa ja orjattariensa parissa, jolloin heitä ei soimata.');
INSERT INTO "finnish" VALUES(23,7,'23:7','Mutta ne, jotka menevät tätä pitemmälle, he ovat synnintekijöitä.');
INSERT INTO "finnish" VALUES(23,8,'23:8','Uskovaisia ovat myös ne, jotka vartioivat uskottua omaisuutta ja pitävät sopimuksensa;');
INSERT INTO "finnish" VALUES(23,9,'23:9','ja ne, jotka pitävät vaarin rukouksistaan.');
INSERT INTO "finnish" VALUES(23,10,'23:10','Nämä kuuluvat perillisiin.');
INSERT INTO "finnish" VALUES(23,11,'23:11','Paratiisin he saavat periä, ja siellä he tulevat asumaan.');
INSERT INTO "finnish" VALUES(23,12,'23:12','Totisesti muovailimme ihmisen puhtaasta savesta.');
INSERT INTO "finnish" VALUES(23,13,'23:13','Sitten sijoitimme elon siemenen varmaan suojaan.');
INSERT INTO "finnish" VALUES(23,14,'23:14','Senjälkeen kehitimme siemenestä verenalun, ja hyytyneestä verestä kehitimme lihanrungon; lihanrungossa rakensimme luut ja luut ympäröimme lihalla. Tämän jälkeen saatoimme hänet uuteen hahmoon. Siunattu olkoon siis Jumala, luojista paras.');
INSERT INTO "finnish" VALUES(23,15,'23:15','Tämän jälkeen teidän on totisesti kuoltava;');
INSERT INTO "finnish" VALUES(23,16,'23:16','mutta ylösnousemuksen päivänä teidät jälleen herätetään eloon.');
INSERT INTO "finnish" VALUES(23,17,'23:17','Totisesti, yläpuolellenne olemme rakentanut seitsemän taivaan rataa; emme konsanaan jätä huolenpitoa luomisesta.');
INSERT INTO "finnish" VALUES(23,18,'23:18','Pilvistä lähetämme sadetta maan päälle määrän mukaan ja annamme sen jäädä maahan, ja totisesti kykenemme myös sen ottamaan pois.');
INSERT INTO "finnish" VALUES(23,19,'23:19','Veden avulla Me sitten kasvatamme teille puutarhoja palmupuineen ja viiniköynnöksineen. Niistä saatte runsaasti hedelmiä, ja te syötte niitä.');
INSERT INTO "finnish" VALUES(23,20,'23:20','Me kasvatamme myös puun, joka kohoaa Siinain vuorella, ja se tuottaa teille öljyä ja mehua nautittavaksenne.');
INSERT INTO "finnish" VALUES(23,21,'23:21','Myös nelijalkaisissa teille on opetus. Annamme teille juomaa niiden sisuksista; onpa karjasta teille suurta hyötyä myös siten, että voitte sitä syödä.');
INSERT INTO "finnish" VALUES(23,22,'23:22','Se kantaa teitä myös samoin kuin laivatkin.');
INSERT INTO "finnish" VALUES(23,23,'23:23','Ja tapahtui, että Me lähetimme Nooan kansansa luokse, ja hän lausui: »Oi kansani, palvelkaa Jumalaa; teillä ei ole muuta jumalaa kuin Hän. Ettekö siis karttaisi pahaa?');
INSERT INTO "finnish" VALUES(23,24,'23:24','Silloin hänen kansansa uskottomien päämiehet sanoivat: »Hän on vain kuolevainen kuten tekin ja yrittää kohottaa itsensä teidän yläpuolellenne. Jos Jumala olisi tahtonut, Hän olisi tosiaankin voinut lähettää alas enkelinsä. Emme ole tällaisesta kuulleet esi-isiltämme.');
INSERT INTO "finnish" VALUES(23,25,'23:25','Hän on vain hullu mies, siksi sietäkäämme häntä aikansa.»');
INSERT INTO "finnish" VALUES(23,26,'23:26','Nooa lausui: »Herra, auta minua, koska he leimaavat minut valehtelijaksi.»');
INSERT INTO "finnish" VALUES(23,27,'23:27','Silloin Me annoimme hänelle ilmoituksemme näin sanoen: »Rakenna itsellesi arkki silmiemme edessä ja käskyjemme mukaan. Kun sitten sanamme käy toteen ja tuliset vedet tulvivat, niin vie siihen pari kaikkia eläimiä sekä oma perheesi, paitsi niitä, joista sana on jo edeltä käynyt. Älä puhu minulle niistä, jotka ovat jumalattomia. Totisesti, heidät hukutetaan.');
INSERT INTO "finnish" VALUES(23,28,'23:28','Kun sinä ja mukanasi olevat olette turvallisesti asettuneet arkkiin, lausu silloin: »Kaikki ylistys kuuluu Jumalalle, joka on pelastanut meidät jumalattomien ihmisten seasta.»');
INSERT INTO "finnish" VALUES(23,29,'23:29','Ja sano edelleen: »Herrani, suo minulle siunattu ulospääsy, sillä Sinä olet paras turvasatama.»');
INSERT INTO "finnish" VALUES(23,30,'23:30','Totisesti nämä ovat tunnusmerkkejä; totisesti Me koettelemme ihmisiä.');
INSERT INTO "finnish" VALUES(23,31,'23:31','Heidän jälkeensä me loimme uuden sukupolven.');
INSERT INTO "finnish" VALUES(23,32,'23:32','Sitten lähetimme heidän luokseen sananjulistajan heidän keskuudestaan, ja hän lausui: »Palvelkaa Jumalaa, teillä ei ole muuta jumalaa kuin Hän. Ettekö siis Häntä kunnioittaisi?»');
INSERT INTO "finnish" VALUES(23,33,'23:33','Mutta päämiehet hänen kansansa keskuudessa, jotka olivat epäuskoisia ja kielsivät tulevan elämän ja joille olimme antanut hyvinvointia tässä maailmassa, lausuivat: »Hän on vain kuolevainen kuten tekin, hän syö mitä tekin syötte ja juo mitä te juotte.');
INSERT INTO "finnish" VALUES(23,34,'23:34','Jos tottelette kaltaistanne ihmistä, menette totisesti turmioon.');
INSERT INTO "finnish" VALUES(23,35,'23:35','Lupaako hän teille, että kun olette kuolleet; ja muuttuneet tomuksi ja luiksi, niin teidät herätetään jälleen eloon?');
INSERT INTO "finnish" VALUES(23,36,'23:36','Se on mahdotonta, aivan mahdotonta, mitä teille on luvattu.');
INSERT INTO "finnish" VALUES(23,37,'23:37','On olemassa ainoastaan tämän maailman elämä, me kuolemme ja me elämme, eikä meitä herätetä kuolleista.');
INSERT INTO "finnish" VALUES(23,38,'23:38','Hän on vain ihminen, joka on sepittänyt valhetta Jumalaa vastaan, emmekä ota häntä uskoaksemme.»');
INSERT INTO "finnish" VALUES(23,39,'23:39','Hän sanoi: »Herrani, auta minua, koska he leimaavat minut valhettelijaksi.»');
INSERT INTO "finnish" VALUES(23,40,'23:40','Jumala vastasi: »Tuokion kuluttua he totisesti katuvat.»');
INSERT INTO "finnish" VALUES(23,41,'23:41','Niin rangaistus yllätti heidät totuuden voimalla, ja Me huuhdoimme heidät kuin roskajätteet. Siten häipykööt jumalattomat ihmiset.');
INSERT INTO "finnish" VALUES(23,42,'23:42','Sitten Me nostimme heidän jälkeensä uuden sukupolven.');
INSERT INTO "finnish" VALUES(23,43,'23:43','Yksikään kansa ei voi jouduttaa aikansa tuloa eikä sitä siirtää.');
INSERT INTO "finnish" VALUES(23,44,'23:44','Sitten lähetimme sananjulistajia toisen toisensa jälkeen, mutta kun kansa sai lähettilään luokseen, se kielsi hänet, jolloin Me annoimme kansan seurata toistaan ja teimme ne pelkiksi taruiksi. Siten häipykööt pois uskottomat kansat.');
INSERT INTO "finnish" VALUES(23,45,'23:45','Senjälkeen lähetimme Mooseksen ja hänen veljensä Aaronin mukanaan merkkimme ja selvät valtuudet');
INSERT INTO "finnish" VALUES(23,46,'23:46','faraon ja hänen päämiestensä luokse, mutta nämä paisuivat ylpeydestään ja olivat julkeita ihmisiä.');
INSERT INTO "finnish" VALUES(23,47,'23:47','Näin he sanoivat: »Uskoisimmeko kahta meidän kaltaistamme ihmistä, joiden kansalaiset ovat meidän orjiamme?»');
INSERT INTO "finnish" VALUES(23,48,'23:48','Näin he kielsivät lähettiläät ja joutuivat niiden joukkoon, jotka kadotettiin.');
INSERT INTO "finnish" VALUES(23,49,'23:49','Totisesti, Me annoimme Moosekselle Kirjan, jotta he saisivat johdatuksen.');
INSERT INTO "finnish" VALUES(23,50,'23:50','Me asetimme myös Marian pojan ja hänen äitinsä tunnusmerkiksi ja annoimme heille suojaa eräällä ylängöllä, jossa oli sopiva lepopaikka ja kirkas lähde.');
INSERT INTO "finnish" VALUES(23,51,'23:51','Te, jotka olette lähettiläitä, syökää puhdasta ravintoa ja suorittakaa hyviä tekoja. Minä tiedän tarkoin, mitä te teette.');
INSERT INTO "finnish" VALUES(23,52,'23:52','Teidän seurakuntanne on ainoa uskovaisten seurakunta, ja Minä olen teidän Herranne, siksi palvelkaa Minua,');
INSERT INTO "finnish" VALUES(23,53,'23:53','Mutta he ovat hajaantuneet uskonnossaan lahkoiksi, ja jokainen ylvästelee omastaan.');
INSERT INTO "finnish" VALUES(23,54,'23:54','Anna siis heidän joksikin aikaa sokaistua harhaansa.');
INSERT INTO "finnish" VALUES(23,55,'23:55','Luulevatko he, että antaessamme heille rikkautta ja lapsia');
INSERT INTO "finnish" VALUES(23,56,'23:56','joudutamme heille myös hyveitä. Ei, tätä he eivät käsitä.');
INSERT INTO "finnish" VALUES(23,57,'23:57','Totisesti, ne, jotka Herransa pelosta ovat vaarinottavaisia,');
INSERT INTO "finnish" VALUES(23,58,'23:58','jotka uskovat Herransa ilmoituksiin,');
INSERT INTO "finnish" VALUES(23,59,'23:59','jotka eivät palvele ketään Jumalan vertaisena');
INSERT INTO "finnish" VALUES(23,60,'23:60','ja jotka antavat antinsa väräjävin sydämin, tietäen palaavansa Herransa luokse,');
INSERT INTO "finnish" VALUES(23,61,'23:61','kaikki nämä ovat niitä, jotka kilpailevat hyveissä ja ensimmäisinä ne saavuttavat.');
INSERT INTO "finnish" VALUES(23,62,'23:62','Emme pane yhdellekään sielulle kuormaa, jota se ei kykenisi kantamaan, ja Meillä on myöskin Kirja, joka puhuu totuutta, joten heille ei tehdä vääryyttä.');
INSERT INTO "finnish" VALUES(23,63,'23:63','Heidän sydämensä ovat vaipuneet tietämättömyyteen tästä Kirjasta, ja sitäpaitsi he suorittavat muitakin tekoja.');
INSERT INTO "finnish" VALUES(23,64,'23:64','Kunnes Me langetamme tuomiomme yltäkylläisesti eläville heidän keskuudessaan, ja katso, silloin he alkavat huutaa.');
INSERT INTO "finnish" VALUES(23,65,'23:65','Älkää huutako apua tänä päivänä; totisesti ette ole saavat sitä Meitä vastaan.');
INSERT INTO "finnish" VALUES(23,66,'23:66','Totisesti, Minun ilmoitukseni julistettiin teille, mutta te käännyitte selin.');
INSERT INTO "finnish" VALUES(23,67,'23:67','Ylvästellen te vetäydytte etäälle.');
INSERT INTO "finnish" VALUES(23,68,'23:68','Eivätkö he siis ole harkinneet, mitä ovat sanoneet? Vai onko heille sattunut sellaista, mitä ei heidän esi-isillensäkään ole sattunut?');
INSERT INTO "finnish" VALUES(23,69,'23:69','Tai eivätkö he tunne sananjulistajaansa ja siksi hänet kieltävät?');
INSERT INTO "finnish" VALUES(23,70,'23:70','Tai sanovatko, että hänet on vallannut hulluus? Ei, hän on tuonut heille totuuden, mutta useimmat heistä ovat totuutta vastaan.');
INSERT INTO "finnish" VALUES(23,71,'23:71','Jos totuus olisi mukautunut heidän himoihinsa, niin varmasti taivaat ja maa ja kaikki, mitä niissä on, olisivat sortuneet raunioiksi. Ei, olemme tuonut heille varoittajan, mutta he kääntävät hänelle selkänsä.');
INSERT INTO "finnish" VALUES(23,72,'23:72','Vai onko niin, että sinä, oi Muhammed, olet pyytänyt heiltä palkkiota? Mutta onhan Herrasi palkinto paras, sillä Hän on paras huoltaja.');
INSERT INTO "finnish" VALUES(23,73,'23:73','Totisesti, sinä kutsut heitä oikealle tielle.');
INSERT INTO "finnish" VALUES(23,74,'23:74','Mutta totisesti, ne, jotka eivät usko tulevaan elämään, eksyvät tieltä.');
INSERT INTO "finnish" VALUES(23,75,'23:75','Ja vaikkapa Me osoittaisimme heille armoa ja poistaisimme heitä ahdistavan vaivan, niin totisesti, he pysyisivät kurittomuudessaan ja sokeasti jatkaisivat vaellustaan.');
INSERT INTO "finnish" VALUES(23,76,'23:76','Olemme jo yllättänyt heidät rangaistuksella, mutta he eivät nöyryyttäneet sydämiänsä Herransa edessä eivätkä myöskään katuneet.');
INSERT INTO "finnish" VALUES(23,77,'23:77','Mutta kun avaamme heille oven suureen tuskaan, niin katso, silloin he joutuvat epätoivoon.');
INSERT INTO "finnish" VALUES(23,78,'23:78','Jumalahan on antanut teille kuulon ja näön ja sydämen. Mutta niukkoja ovat teidän kiitoksenne.');
INSERT INTO "finnish" VALUES(23,79,'23:79','Hän on levittänyt teidät yli maan, ja Hänen luokseen teidät jälleen kootaan.');
INSERT INTO "finnish" VALUES(23,80,'23:80','Hän antaa elämän ja kuoleman, ja Hänestä riippuu yön ja päivän vuorottelu. Ettekö jo ymmärrä?');
INSERT INTO "finnish" VALUES(23,81,'23:81','Päinvastoin, he puhuvat aivan kuten heidän edeltäjänsäkin.');
INSERT INTO "finnish" VALUES(23,82,'23:82','He sanovat: »Kuinka, kun olemme kuolleet ja muuttuneet tomuksi ja luiksi, herätetäänkö meidät silloin taas eloon?');
INSERT INTO "finnish" VALUES(23,83,'23:83','Totisesti, meille on jo aikaisemmin luvattu samaa, kuten muinaisille esi-isillemmekin; nämä ovat pelkkiä muinaistaruja.»');
INSERT INTO "finnish" VALUES(23,84,'23:84','Sano: »Kenelle kuuluu maa ja kaikki, mitä siinä on, vastatkaa, jos tiedätte?»');
INSERT INTO "finnish" VALUES(23,85,'23:85','He vastaavat nyt: »Jumalalle.» »Sano: »Miksi ette sitten ota vaarin?»');
INSERT INTO "finnish" VALUES(23,86,'23:86','Sano edelleen: »Kuka on seitsemän taivaan ja suurimman vallan Herra?»');
INSERT INTO "finnish" VALUES(23,87,'23:87','He vastaavat taas: »Jumalalle kuuluu kaikki tämä.» Sano: »Miksi ette sitten Häntä palvele?»');
INSERT INTO "finnish" VALUES(23,88,'23:88','Sano vielä: »Kenen kädessä on kaiken valtius ja kuka antaa suojaa, vaikka häntä vastaan ei ole mitään suojaa, sanokaa, jos tiedätte.»');
INSERT INTO "finnish" VALUES(23,89,'23:89','He vastaavat taas: »Tämä kuuluu Jumalalle.» Sano: »Kuinka siis voitte olla noin harhautuneita?»');
INSERT INTO "finnish" VALUES(23,90,'23:90','Olemme tuonut heille totuuden, mutta useimmat heistä ovat totisesti valehtelijoita.');
INSERT INTO "finnish" VALUES(23,91,'23:91','Jumala ei konsanaan ole hankkinut itselleen poikaa, eikä ole muuta jumalaa kuin Hän. Olisihan muutoin jokainen jumala vienyt mukaansa sen, mitä oli luonut, ja muutamat heistä olisivat alistaneet toisia valtaansa. Kunnia olkoon Jumalalle! Hän on yläpuolella kaiken, mitä he kuvittelevat.');
INSERT INTO "finnish" VALUES(23,92,'23:92','Hän tuntee sekä kätketyt että julkiset asiat. Senvuoksi Hän on sen yläpuolella, mitä he hänen ohellaan jumaloivat.');
INSERT INTO "finnish" VALUES(23,93,'23:93','Sano: »Herra, jos näytät minulle, mikä heitä uhkaa,');
INSERT INTO "finnish" VALUES(23,94,'23:94','niin Herra, älä sijoita minua väärämielisten joukkoon.»');
INSERT INTO "finnish" VALUES(23,95,'23:95','Totisesti, Me kykenemme näyttämään sinulle, millä Me heitä uhkaamme.');
INSERT INTO "finnish" VALUES(23,96,'23:96','Torju paha sillä, mikä hyvää on. Me tunnemme parhaiten, mitä he uskottelevat.');
INSERT INTO "finnish" VALUES(23,97,'23:97','Sano myös: »Herra. Sinusta etsin turvapaikkaa pahojen henkien viettelyksiä vastaan.');
INSERT INTO "finnish" VALUES(23,98,'23:98','Sinusta etsin suojaa, oi Herra, etteivät he minua saavuttaisi.»');
INSERT INTO "finnish" VALUES(23,99,'23:99','Aina siihen asti, kunnes kuolema jonkun heistä yllättää ja hän hokee: »Herrani, päästä minut takaisin, päästä minut takaisin,');
INSERT INTO "finnish" VALUES(23,100,'23:100','ehkä voisin tehdä hyvää sellaisessa, minkä olen jättänyt tekemättä.» Ei koskaan. Tämä on vain puhetta, jota hän hokee, mutta heidän takanaan on erottava seinä aina siihen päivään asti, jolloin heidät herätetään kuolleista.');
INSERT INTO "finnish" VALUES(23,101,'23:101','Mutta kun pasuunoihin puhalletaan, niin sinä päivänä heidän välillään ei ole oleva mitään sukulaisuussiteitä, eivätkä he kysele toinen toistansa.');
INSERT INTO "finnish" VALUES(23,102,'23:102','Senvuoksi ne, joiden hyveet painavat vaa''assa eniten, tulevat onnellisiksi.');
INSERT INTO "finnish" VALUES(23,103,'23:103','Mutta joiden ansiot painavat vähän, he ovat niitä, jotka ovat kadottaneet sielunsa. Helvetissä he saavat asua.');
INSERT INTO "finnish" VALUES(23,104,'23:104','Tuli on siellä kärventävä heidän kasvonsa ja pahasti turmeleva heidät.');
INSERT INTO "finnish" VALUES(23,105,'23:105','Eikö Minun ilmoituksiani julistettu teille? Mutta te hyljitte niitä.');
INSERT INTO "finnish" VALUES(23,106,'23:106','He vastaavat: »Herra, meidän onnettomuutemme kävivät yli voimiemme ja me olimme harhaan johdettuja ihmisiä.');
INSERT INTO "finnish" VALUES(23,107,'23:107','Herra, päästä meidät pois täältä. Totisesti, jos taas käännymme pahaan, olemme väärämielisiä.»');
INSERT INTO "finnish" VALUES(23,108,'23:108','Jumala on vastaava: »Menkää tiehenne älkääkä puhuko Minulle!');
INSERT INTO "finnish" VALUES(23,109,'23:109','Olihan totisesti keskuudessanne ryhmä palvelijoitani, jotka lausuivat: ''Herra, me uskomme, anna meille anteeksi ja osoita meille armoasi, sillä Sinä olet paras armahtaja.''');
INSERT INTO "finnish" VALUES(23,110,'23:110','Mutta te piditte heitä pilkkananne, kunnes se sai teidät unohtamaan Minun varoitukseni, ja te nauroitte heitä.');
INSERT INTO "finnish" VALUES(23,111,'23:111','Totisesti Minä tänä päivänä olen heidät palkitseva siitä, että he olivat lujia; he ovat voiton perijöitä.»');
INSERT INTO "finnish" VALUES(23,112,'23:112','Vielä kysyy Hän: »Kuinka monta vuotta viivyitte maan päällä?»');
INSERT INTO "finnish" VALUES(23,113,'23:113','He vastaavat: »Viivyimme kai päivän tai osan siitä. Kysy niiltä, jotka osaavat laskea.»');
INSERT INTO "finnish" VALUES(23,114,'23:114','Hän on sanova: »Olette viipyneet vain lyhyen hetken; jospa sen tietäisitte.');
INSERT INTO "finnish" VALUES(23,115,'23:115','Luulitteko, että olimme luonut teidät turhan vuoksi ja ettei teidän ollut palattava Meidän luoksemme?»');
INSERT INTO "finnish" VALUES(23,116,'23:116','Korkea on Jumala, totuuden kuningas; ei ole muuta jumalaa kuin Hän, ylevän voiman Herra.');
INSERT INTO "finnish" VALUES(23,117,'23:117','Ken rukoilee jotakin muuta jumalaa Herran ohella, hänen osaksensa ei tule valoa, ja hänen on tili tehtävä Jumalalle. Totisesti, uskottomilla ei ole milloinkaan menestystä.');
INSERT INTO "finnish" VALUES(23,118,'23:118','Sano: »Herra! Anna anteeksi ja osoita armoasi, sillä Sinä olet paras armahtajista.»');
INSERT INTO "finnish" VALUES(24,1,'24:1','Tämä on suura, jonka Me olemme ylhäältä ilmaissut välttämättä noudatettavaksi ja jossa on selvät tunnusmerkkimme, jotta ottaisitte ne varteen.');
INSERT INTO "finnish" VALUES(24,2,'24:2','Haureudenharjoittajille antakaa kullekin sata iskua nahkaruoskalla, älkääkä salliko myötätuntonne heitä kohtaan estää täyttämästä Jumalan käskyä, mikäli uskotte Jumalaan ja tulevaan päivään. Joidenkuiden uskovaisten tulee olla todistajina heitä rangaistaessa.');
INSERT INTO "finnish" VALUES(24,3,'24:3','Älköön haureellinen mies naiko muuta kuin haureellisen naisen taikka pakanavaimon, ja älköön haureellinen nainen menkö muulle kuin haureelliselle taikka pakanalliselle miehelle. Tällainen avio on uskovaisilta kielletty.');
INSERT INTO "finnish" VALUES(24,4,'24:4','Jotka syyttävät aviovaimoja haureellisesta elämästä eivätkä tuo neljää todistajaa, heille annettakoon kahdeksankymmentä raipaniskua, älköönkä heidän todistustaan milloinkaan vast''edes kuunneltako, sillä he ovat jumalattomia ihmisiä.');
INSERT INTO "finnish" VALUES(24,5,'24:5','Mutta jotka tämän jälkeen kääntyvät Jumalan puoleen ja tekevät oikein, heille Jumala on anteeksiantavainen, armollinen.');
INSERT INTO "finnish" VALUES(24,6,'24:6','Jotka syyttävät omia vaimojaan, mutta eivät voi esittää muita todistajia kuin itsensä, todistakoot neljä kertaa Jumalan nimeen olevansa ehdottomasti luotettavia');
INSERT INTO "finnish" VALUES(24,7,'24:7','ja viidennellä kerralla, että Jumalan kirous langetkoon heidän päällensä, jos valhettelevat.');
INSERT INTO "finnish" VALUES(24,8,'24:8','Mutta vaimo voi välttyä ruoskimiselta, jos hän neljästi Jumalan nimeen vannoo, että mies puhuu valhetta,');
INSERT INTO "finnish" VALUES(24,9,'24:9','ja viidennellä kerralla, että Jumalan viha häntä kohdatkoon, ellei hän puhu totta.');
INSERT INTO "finnish" VALUES(24,10,'24:10','Mitä tapahtuisikaan, ellei Jumalan laupeus ja Hänen armonsa olisi teidän apunanne ja ellei Jumala olisi anteeksiantavainen, viisas!');
INSERT INTO "finnish" VALUES(24,11,'24:11','Sellaisia, jotka ovat keksineet valheellisia syytöksiä, on kokonainen joukko teidän keskuudessanne. Älkää luulko, että tämä teitä vahingoittaa. Päinvastoin se on teille eduksi. Jokainen heistä saa palkkansa tekemistään synneistä, ja se heistä, joka on pääsyyllinen, on raskaan kurituksen kokeva.');
INSERT INTO "finnish" VALUES(24,12,'24:12','Kun tämän panettelun kuulitte, miksi eivät uskovaiset miehet ja naiset uskoneet hyvää omasta kansastaan ja sanoneet: »Tämä on selvästi valheellinen syytös.»');
INSERT INTO "finnish" VALUES(24,13,'24:13','Miksi panettelijat eivät tuoneet neljää todistajaa tuekseen? Sillä kun he eivät esittäneet todistajia, he ovat Jumalan silmissä valehtelijoita.');
INSERT INTO "finnish" VALUES(24,14,'24:14','Ellei Jumalan laupeus ja Hänen armonsa olisi tukenanne tässä maailmassa ja tulevassa, niin totisesti suuri onnettomuus olisi teitä kohdannut juorupuheen tähden, johon sotkeuduitte,');
INSERT INTO "finnish" VALUES(24,15,'24:15','koska piditte sitä kielellänne ja haastoitte sitä huulillanne, vaikka teillä ei ollut siitä mitään tietoa, ja piditte sitä pikkuasiana, vaikka se oli vakava Jumalan silmissä.');
INSERT INTO "finnish" VALUES(24,16,'24:16','Ja kun sen kuulitte, miksi ette sanoneet: »Meidän asiamme ei ole puhua tästä. Jumala armahtakoon! Tämä on kauhea parjaus.»');
INSERT INTO "finnish" VALUES(24,17,'24:17','Jumala kehoittaa, ettette milloinkaan uudelleen lankeisi sellaiseen, jos olette uskovaisia.');
INSERT INTO "finnish" VALUES(24,18,'24:18','Jumala on myös kirkastava teille merkkinsä, sillä Jumala on tietävä, viisas.');
INSERT INTO "finnish" VALUES(24,19,'24:19','Totisesti, jotka levittävät häväistysjuttuja uskovaisista, niitä kohtaa tuskallinen tuomio tässä maailmassa ja tulevassa, sillä Jumala tietää, mutta te ette tiedä.');
INSERT INTO "finnish" VALUES(24,20,'24:20','Mitä tapahtuisikaan, ellei Jumalan laupeus ja Hänen armonsa olisi teidän yllänne, ja ellei Jumala olisi rakastava, armollinen.');
INSERT INTO "finnish" VALUES(24,21,'24:21','Te, jotka uskotte, älkää seuratko saatanan jälkiä, sillä ken niitä seuraa, häntä saatana käskee riettauteen ja pahaan. Ja ellei Jumalan laupeus ja Hänen armonsa olisi yllänne, niin kukaan teistä ei milloinkaan puhdistuisi, mutta Jumala puhdistaa kenet tahtoo, sillä Jumala on kuuleva, tietävä.');
INSERT INTO "finnish" VALUES(24,22,'24:22','Älkööt ne joukostanne, jotka ovat suosittuja ja varakkaita, kieltäytykö avustuksen antamisesta sukulaisilleen ja tarvitseville sekä niille, jotka ovat jättäneet kotinsa lähtiessään Jumalan retkelle. Olkoot he anteeksiantavia ja suvaitsevia. Ettekö itse halaja, että Jumala antaisi teille anteeksi? Sillä Jumala on anteeksiantavainen, armollinen.');
INSERT INTO "finnish" VALUES(24,23,'24:23','Niitä, jotka syyttävät vapaasti käyttäytyviä, mutta siveitä ja uskovia naisia, kohtaa totisesti kirous tässä maailmassa ja tulevassa, ja heille langetetaan ankara rangaistus.');
INSERT INTO "finnish" VALUES(24,24,'24:24','Sinä päivänä, jolloin heidän kielensä ja kätensä ja jalkansa todistavat heidän tekojansa vastaan,');
INSERT INTO "finnish" VALUES(24,25,'24:25','sinä päivänä Jumala on täydellisesti maksava heille oikeudenmukaisen palkan, ja he tulevat huomaamaan, että Hän, Jumala, on ilmeinen totuus.');
INSERT INTO "finnish" VALUES(24,26,'24:26','Saastaiset naiset ovat saastaisia miehiä varten ja saastaiset miehet saastaisia naisia varten; ja siveät naiset ovat siveitä miehiä ja siveät miehet siveitä naisia varten. Nämä, joita viattomasti syytetään, ovat vapaita siitä, mitä heistä sanotaan. He saavat anteeksiannon ja ihanan palkinnon.');
INSERT INTO "finnish" VALUES(24,27,'24:27','Te, jotka uskotte, älkää astuko muihin taloihin kuin omiinne, ennenkuin olette saaneet niiden asukkaiden suostumuksen ja heitä tervehtineet. Tämä on teille parasta, joten ottakaa se varteen.');
INSERT INTO "finnish" VALUES(24,28,'24:28','Mutta ellette tapaa ketään kotoa, älkää astuko sisään ennenkuin saatte luvan. Ja jos teitä käsketään kääntymään takaisin, niin kääntykää, sillä se on teille säädyllisintä. Jumala tietää, mitä te teette.');
INSERT INTO "finnish" VALUES(24,29,'24:29','Teitä ei soimata, jos astutte asukkaita vailla olevaan taloon, jossa teillä on jotakin toimitettavaa. Jumala tietää, mitä te tuotte julki ja mitä te salaatte.');
INSERT INTO "finnish" VALUES(24,30,'24:30','Sano uskovaisille miehille, että he luovat katseensa maahan ja hillitsevät halujaan. Tämä on heille säädyllisintä, ja Jumala tietää mitä he tekevät.');
INSERT INTO "finnish" VALUES(24,31,'24:31','Sano myös uskovaisille vaimoille, että luovat silmänsä maahan ja hillitsevät halujaan eivätkä näyttele sulouttaan tavallisuudesta poiketen ja että huntu verhoaa heidän povensakin. Älkööt he näyttäkö sulouttaan muille kuin miehilleen tai isilleen tai apilleen tai pojalleen tai poikapuolilleen tai veljilleen tai veljiensä pojille tai sisariensa pojille tai palvelusnaisilleen tai orjattarilleen tai sellaisille palvelijoille, joilla ei ole vetoa naisiin, tai poikasille, jotka eivät ole tietoisia naisten salaisuuksista. Älkööt he siten astuko kulkiessaan, että heidän kätketyt sulonsa havaittaisiin. Kääntykää kaikki Jumalan puoleen, oi uskovaiset, jotta saisitte menestystä!');
INSERT INTO "finnish" VALUES(24,32,'24:32','Päästäkää avioliittoon keskuudessanne olevat naimattomat naiset, ja samoin naittakaa kunnolliset orjanne sekä orjattarenne. Jos he ovat köyhiä, niin Jumala on armostaan antava heille toimeentulon, sillä Jumala on runsaskätinen, tietävä.');
INSERT INTO "finnish" VALUES(24,33,'24:33','Mutta hillitkööt ne itseään, joilla ei ole varoja mennäkseen naimisiin, kunnes Jumala armossaan antaa heidän vaurastua. Ja jos jotkut orjistanne pyytävät vapautuskirjaa, antakaa se heille, jos tiedätte heidän hyvässä ansioituneen, ja antakaa heille jotakin omaisuudestanne, jonka Jumala on teille suonut. Älkää pakottako orjatyttöjänne tämän maailman voitonhimon houkuttelemana haureuteen, jos he haluavat viettää siveätä elämää. Jos joku heidät pakottaa, niin Jumala, koska heidät on pakotettu, on heille anteeksiantavainen, armollinen.');
INSERT INTO "finnish" VALUES(24,34,'24:34','Totisesti, Me olemme jo lähettänyt teille selvät merkit ja varoittavan esimerkin ennen teitä eläneistä sekä kehoituksen niille, jotka hartautta harjoittavat.');
INSERT INTO "finnish" VALUES(24,35,'24:35','Jumala on taivaitten ja maan valo. Hänen valonsa on verrattavissa lamppuun, joka on asetettu seinän syvennykseen. Lamppu on sijoitettu lasipalloon, joka on kuin loistava tähti. Sitä valaisee siunatun öljypuun öljy, joka ei ole idästä eikä lännestä, ja se antaisi kirkkaan valon, vaikka siihen ei tulta pantaisikaan: siinä on valoa valon päällä. Jumala ohjaa valoaan sitä kohti, jonka hän otolliseksi näkee. Jumala esittää myös vertauksia ihmissuvulle, sillä Jumala tietää kaiken.');
INSERT INTO "finnish" VALUES(24,36,'24:36','Tämä valo on myös rakennuksissa, jotka Jumala on määrännyt pystytettäviksi ja joissa Hänen nimeänsä muistetaan. Häntä ylistävät niissä aamuin ja illoin');
INSERT INTO "finnish" VALUES(24,37,'24:37','ihmiset, joita hankinnat ja myynnit eivät estä Jumalaa palvelemasta, rukouksia suorittamasta eikä sovitusalmuja antamasta, sillä he pelkäävät päivää, jolloin sydämet ja silmät levottomasti odottavat,');
INSERT INTO "finnish" VALUES(24,38,'24:38','että Jumala antaisi heille palkan parhaasta, mitä he ovat suorittaneet, ja armostaan soisi heille vielä enemmänkin suosiotaan. Niin, Jumala huolehtiikin kitsastelematta niistä, jotka otollisiksi huomaa.');
INSERT INTO "finnish" VALUES(24,39,'24:39','Uskottomien teot ovat kuin kangastus erämaassa. Janoinen ihminen luulee löytävänsä vettä, mutta kun hän saapuu paikalle, hän ei löydä sieltä mitään. Hän havaitsee joutuneensa Jumalan eteen, joka vaatii tilinteolle ja on maksava hänelle, sillä Jumala on nopea tilinteossaan.');
INSERT INTO "finnish" VALUES(24,40,'24:40','Tai heidän tekonsa ovat kuin täydellinen pimeys meren syvyydessä. Siellä peittää uskottoman aalto, jonka yli vyöryy toinen, ja niiden yllä on synkkä pilvi: pimeyttä pimeyden yllä. Kun hän ojentaa kätensä, niin hän tuskin sitä lainkaan näkee. Sillä kenelle Jumala ei valoa anna, sillä ei valoa ole.');
INSERT INTO "finnish" VALUES(24,41,'24:41','Etkö havaitse, että kaikki, mikä on taivaassa ja maan päällä, julistaa Jumalan kunniaa? Niin tekevät myös linnut, kun ne siipensä levittävät. Hän tuntee jokaisen rukouksen ja ylistämistavan. Jumala tietää, mitä he tekevät.');
INSERT INTO "finnish" VALUES(24,42,'24:42','Jumalalle kuuluu myös taivaitten ja maan kuninkuus, ja Jumalan luokse on kaikkien palattava.');
INSERT INTO "finnish" VALUES(24,43,'24:43','Etkö huomaa, miten Jumala ajelee pilviä, sitten kerää ne yhteen ja sijoittaa ne kerroksittain toistensa päälle, jolloin näet sateen virtaavan niiden uumenista. Hän myös lähettää taivaasta maahan kokonaisia raevuoria ja antaa niiden pudota kenen päälle haluaa ja sivuuttaa ketkä haluaa.');
INSERT INTO "finnish" VALUES(24,44,'24:44','Jumala säätää yön ja päivän vaihtelun. Totisesti, tässä on opetus niille, jotka näkevät.');
INSERT INTO "finnish" VALUES(24,45,'24:45','Jumala on myös luonut kaikki eläimet vedestä. Niiden joukossa on sellaisia, jotka ryömivät vatsallaan, sellaisia, jotka kulkevat kahdella jalalla, ja sellaisia, jotka kulkevat neljällä jalalla. Jumala luo mitä haluaa. Totisesti Jumalalla on valta kaiken yli.');
INSERT INTO "finnish" VALUES(24,46,'24:46','Olemme totisesti lähettänyt maan päälle selvät merkit, ja Jumala johtaa oikealle tielle kenet tahtoo.');
INSERT INTO "finnish" VALUES(24,47,'24:47','He sanovat: »Me uskomme Jumalaan ja lähettilääseen ja me olemme tottelevaisia.» Mutta sitten osa heistä kääntyy pois, ja nämä eivät ole uskovaisia.');
INSERT INTO "finnish" VALUES(24,48,'24:48','Ja kun heidät kutsutaan Jumalan ja Hänen lähettiläänsä luokse, jotta Hän ratkaisisi heidän asiansa, niin katso, osa heistä vetäytyy pois.');
INSERT INTO "finnish" VALUES(24,49,'24:49','Mutta jos heillä olisi oikeus puolellaan, rientäisivät he hänen luoksensa nöyrämielisinä.');
INSERT INTO "finnish" VALUES(24,50,'24:50','Tautiko heidän sydämiään tärvelee, vai onko heillä epäilyksiä vai luulevatko he, että Jumala tai Hänen lähettiläänsä menettelee väärin heitä kohtaan? Ei, he itse ovat väärämielisiä.');
INSERT INTO "finnish" VALUES(24,51,'24:51','Kun uskovaiset kutsutaan Jumalan ja Hänen lähettiläänsä luokse, jotta Hän ratkaisisi heidän asiansa, heidän on yksinkertaisesti vastattava: »Me kuulemme ja tottelemme». Tällaiset ovat niitä, jotka menestyvät.');
INSERT INTO "finnish" VALUES(24,52,'24:52','Ken tottelee Jumalaa ja Hänen lähettilästään, pelkää Jumalaa ja pitää vaarin velvollisuudestaan Häntä kohtaan, hänen kaltaisensa voittavat.');
INSERT INTO "finnish" VALUES(24,53,'24:53','Ja he vannovat Jumalan kautta pyhimmän valansa, että jos sinä, oi Muhammed, annat heille käskyn, he varmasti lähtevät retkelle. Sano: »Älkää vannoko; järkevä totteleminen on parempi. Totisesti, Jumala tietää, mitä te teette.»');
INSERT INTO "finnish" VALUES(24,54,'24:54','Sano edelleen: »Palvelkaa Jumalaa ja totelkaa lähettilästä; jos käännytte pois, niin Muhammed kantaa oman kuormansa ja te saatte kantaa omanne, mutta jos häntä tottelette, niin kuljette oikeassa johdatuksessa. Lähettiläs ei ole vastuussa muusta kuin sanomansa selvästä julistamisesta.');
INSERT INTO "finnish" VALUES(24,55,'24:55','Jumala lupaa tehdä ne, jotka uskovat ja suorittavat hyviä tekoja, uskottomien vallan perijöiksi maan päällä, samoin kuin Hän teki heidän edeltäjänsäkin; ja Hän vahvistaa heidän uskontonsa, jonka Hän heille on hyväksynyt; ja totisesti Hän on vaihtava heidän pelkonsa varmuudeksi: »He palvelevat Minua yhdistelemättä Minuun ketään muuta. Ketkä tämän jälkeen eivät usko, he ovat kurittomia ihmisiä.»');
INSERT INTO "finnish" VALUES(24,56,'24:56','Suorittakaa rukouksenne, antakaa sovitusalmut ja totelkaa lähettilästä, jotta teille osoitettaisiin armo!');
INSERT INTO "finnish" VALUES(24,57,'24:57','Älkää luulko, että uskottomat löytävät pakopaikan maan päältä. Heidän asuinsijansa on oleva tuli, surkea määränpää.');
INSERT INTO "finnish" VALUES(24,58,'24:58','Te, jotka uskotte, antakaa orjienne ja niiden, joilla ei vielä ikänsä vuoksi ole ymmärrystä, pyytää luoksenne pääsyä kolmena ajankohtana päivän mittaan: ennen aamurukousta ja keskipäivällä, jolloin riisutte vaatteenne, sekä iltarukouksen jälkeen. Nämä kolme aikaa viettäkää rauhassa, häiriöttä. Muina aikoina älköön teitä eikä heitäkään moitittako, onhan jonkun liikuttava palvellakseen teitä. Tällä tavoin Jumala ilmaisee teille ohjeitaan, sillä Jumala on tietävä, viisas.');
INSERT INTO "finnish" VALUES(24,59,'24:59','Kun lapsenne ovat ehtineet kypsyneeseen ikään, on heidän pyydettävä pääsyä, kuten heitä aikaisemminkin eläneet tekivät. Tällä tavoin Jumala selvittää teille ohjeitaan - sillä Jumala on tietävä, viisas.');
INSERT INTO "finnish" VALUES(24,60,'24:60','Jo synnyttämiskykynsä menettäneet naiset, joilla ei enää ole aviollisia taipumuksia, saavat riisua vaatteitaan haluamatta näytellä alastomuuttaan, eikä sitä heille synniksi lueta, mutta parempi heille on, jos he siitä pidättäytyvät, sillä Jumala on tietävä, viisas.');
INSERT INTO "finnish" VALUES(24,61,'24:61','Ei ole mikään synti sokealle, ei rammalle, ei sairaalle eikä itsellennekään, jos aterioitte omassa talossanne tai isienne taloissa tai äitienne taloissa tai veljienne taloissa tai siskojenne taloissa tai isänpuolisten setienne ja isänpuolisten tätienne taloissa tai äidinpuolisten setienne ja äidinpuolisten tätienne taloissa tai sellaisissa taloissa, joiden avaimet ovat itsellänne tai ystävienne hallussa. Älköön teitä myöskään soimattako, jos aterioitte toistenne seurassa tai yksinänne. Ja kun astutte johonkin taloon, lausukaa toisillenne siunattu ja vilpitön Jumalan tervehdys. Tällä tavoin Jumala selvittää teille ohjeensa, jotta voisitte ymmärtää.');
INSERT INTO "finnish" VALUES(24,62,'24:62','Todellisia uskovaisia ovat ne, jotka uskovat Jumalaan ja Hänen lähettilääseensä ja jotka toimittaessaan tämän kanssa jotakin yhteistä asiaa eivät lähde pois saamatta häneltä lupaa. Totisesti, ne, jotka pyytävät sinulta tällaista lupaa, ovat niitä, jotka uskovat Jumalaan ja Hänen lähettilääseensä. Kun he pyytävät suostumustasi johonkin yritykseensä, anna se niille heistä, joille haluat, ja rukoile heille Jumalan anteeksiantoa. Totisesti, Jumala on anteeksiantavainen, armollinen.');
INSERT INTO "finnish" VALUES(24,63,'24:63','Älkää puhutelko läsnäolevaa lähettilästä, kuten puhuttelette toisianne. Totisesti, Jumala tuntee ne teistä, jotka pujahtavat salaa tiehensä. Kavahtakoot ne, jotka vastustavat hänen käskyjänsä, ettei jokin onnettomuus heitä kohtaa tai ettei heille langeteta tuskallista tuomiota.');
INSERT INTO "finnish" VALUES(24,64,'24:64','Eikö Jumalan vallassa todella ole kaikki, mitä on taivaassa ja maan päällä? Hän tietää teidän tilanne. Sinä päivänä, jolloin heidän on palattava Jumalan luokse, Hän on ilmoittava heille heidän tekonsa. Sillä Jumala tietää kaiken.');
INSERT INTO "finnish" VALUES(25,1,'25:1','Siunattu olkoon Hän, joka ylhäältä on lähettänyt ymmärryksen palvelijallensa, jotta hän voisi varoittaa kansoja.');
INSERT INTO "finnish" VALUES(25,2,'25:2','Hänelle kuuluu taivaitten ja maan kuninkuus, Hän ei ole hankkinut itselleen lasta eikä Hänen kuninkuuteensa ole ketään osallista ja Hän on myös luonut kaiken ja antanut kullekin ansionsa mukaan.');
INSERT INTO "finnish" VALUES(25,3,'25:3','Mutta ihmiset ovat Hänen rinnalleen omaksuneet muita jumalia, jotka eivät pysty mitään luomaan, koska ne itse ovat luotuja; eikä niillä ole mitään valtaa tehdä heille vahinkoa eikä hyvääkään, eivätkä ne liioin hallitse kuolemaa, elämää eivätkä ylösnousemusta.');
INSERT INTO "finnish" VALUES(25,4,'25:4','Ja uskottomat sanovat: »Tämä on silkkaa valhetta, jonka hän itse on sepittänyt, ja toiset ovat häntä siinä auttaneet.» Niin ryhtyvät he vääryyteen ja valheeseen.');
INSERT INTO "finnish" VALUES(25,5,'25:5','He sanovat edelleen: »Ne ovat muinaistaruja, jotka hän on antanut kirjoittaa muistiin, jotta niitä luettaisiin hänelle aamuin ja illoin.»');
INSERT INTO "finnish" VALUES(25,6,'25:6','Vastaa: »Hän, joka tuntee sekä taivaitten että maan salaisuudet, on ilmaissut ne ylhäältä.» Totisesti Hän on anteeksiantavainen, armollinen.');
INSERT INTO "finnish" VALUES(25,7,'25:7','He sanovat myös: »Millainen tämä lähettiläs onkaan! Hänhän nauttii ravintoa ja kävelee toreilla. Miksi ei hänen luoksensa ole lähetetty enkeliä, joka yhdessä hänen kanssaan voisi olla varoittajana?');
INSERT INTO "finnish" VALUES(25,8,'25:8','Tai miksi hänelle ei ole heitetty aarteita, taikka miksi hänelle ei ole annettu puutarhaa, jonka antimia hän voisi syödä?» Ja jumalattomat sanovat: »Te seuraatte vain miestä, joka on noituuden sokaisema.»');
INSERT INTO "finnish" VALUES(25,9,'25:9','Pane merkille, millaisia vertauksia he sinusta esittävät. Mutta he kulkevat eksyksissä eivätkä pääse oikean tien alkuun.');
INSERT INTO "finnish" VALUES(25,10,'25:10','Siunattu olkoon Hän, joka voi, jos tahtoo, antaa teille parempaa, kuin mitä teillä nyt on. Puutarhoja, joiden kautta joet virtaavat, ja myös palatseja Hän voi teille hankkia,');
INSERT INTO "finnish" VALUES(25,11,'25:11','He kieltävät määrähetken, mutta Me olemme valmistanut roihuavan tulen sille, joka tuomion hetken kieltää.');
INSERT INTO "finnish" VALUES(25,12,'25:12','Kun helvetti on näkevä heidät jo matkan päästä, he kuulevat sieltä parkumista ja räiskinää.');
INSERT INTO "finnish" VALUES(25,13,'25:13','Ja kun heidät heitetään johonkin sen ahtaaseen soppeen sidotuin käsin ja jaloin, niin he rukoilevat kuolemaa.');
INSERT INTO "finnish" VALUES(25,14,'25:14','»Tänä päivänä älkää anoko vain yhtä kuolemaa, vaan anokaa monenkertaista hävitystänne.»');
INSERT INTO "finnish" VALUES(25,15,'25:15','Sano: »Tämäkö on parempi, vai asuminen puutarhassa, joka niille on luvattu, jotka hartautta harjoittavat?» Se on oleva heidän palkkansa ja määränpäänsä.');
INSERT INTO "finnish" VALUES(25,16,'25:16','Siellä heillä on kaikki, mitä haluavat, ja siellä he alati saavat asua. Tämä on Herrasi sitova lupaus, sitä voitte Häneltä kysyä.');
INSERT INTO "finnish" VALUES(25,17,'25:17','Sinä päivänä Hän on kokoava heidät sekä ne, joita he Jumalan ohella palvelivat, ja sanova: »Tekö johdatitte nämä Minun palvelijani harhaan, vai eksyivätkö he tieltä?»');
INSERT INTO "finnish" VALUES(25,18,'25:18','He vastaavat: »Kunnia olkoon Sinulle! Ei ollut meille soveliasta ottaa sinun rinnallesi muita suojelijoita, mutta kun Sinä annoit heille ja heidän esi-isilleen nautintoja, niin he unohtivat varoituksen ja joutuivat turmioon.»');
INSERT INTO "finnish" VALUES(25,19,'25:19','Ja me sanomme: »Sentähden he totisesti ovat vääristelleet teidän puheenne, ettekä voi välttää tuomiota ettekä saada apua. Ja ken tahansa joukostanne on väärintekijä, hänen Me annamme maistaa ankaraa rangaistusta.»');
INSERT INTO "finnish" VALUES(25,20,'25:20','Emme sinun edelläsikään lähettänyt yhtään sananjulistajaa, joka ei olisi nauttinut ravintoa tai kulkenut toreilla. Me olemme myös tehnyt jotkut teistä koetukseksi toisille, nähdäksemme, pysyttekö lujina. Herrasi on alati näkevä.');
INSERT INTO "finnish" VALUES(25,21,'25:21','Ne, jotka eivät odota kohtaavansa Meitä, kyselevät: »Miksi ei enkeleitä ole lähetetty luoksemme maan päälle tai miksi emme näe Herraamme?» He ovat todellakin tulleet kovin itsetietoisiksi ja paisuvat ylimielisyydessään.');
INSERT INTO "finnish" VALUES(25,22,'25:22','Tulee päivä, jolloin he saavat nähdä enkeleitä, mutta sinä päivänä syntisiä eivät odota ilosanomat, ja enkelit sanovat: »Olkoon välillämme ylipääsemätön kielto ja este.»');
INSERT INTO "finnish" VALUES(25,23,'25:23','Me tutkimme heidän tekonsa ja muutamme heidät tomuksi, joka hajallensa viskotaan.');
INSERT INTO "finnish" VALUES(25,24,'25:24','Sinä päivänä ovat puutarhan asukkaat paremmassa olotilassa ja ihanammassa lepopaikassa.');
INSERT INTO "finnish" VALUES(25,25,'25:25','Sinä päivänä taivas purkaa pilvensä ja enkelit lähetetään alas suurina saattueina.');
INSERT INTO "finnish" VALUES(25,26,'25:26','Sinä päivänä on todellinen kuninkuus kuuluva laupeimmalle, ja se on oleva kova päivä uskottomille.');
INSERT INTO "finnish" VALUES(25,27,'25:27','Silloin jumalaton pureskelee käsiään ja sanoo: »Jospa olisin vaeltanut lähettilään tietä!');
INSERT INTO "finnish" VALUES(25,28,'25:28','Voi minua, kunpa en olisi ottanut mokomaa ystäväkseni.');
INSERT INTO "finnish" VALUES(25,29,'25:29','Totisesti, hän sai minut unohtamaan kehoituksen, senjälkeen kun olin, sen saanut.» Saatana jättää ihmisen pulaan.');
INSERT INTO "finnish" VALUES(25,30,'25:30','Lähettiläs on sanova: »Oi Herra! Totisesti, kansani piti tätä Koraania joutavana.»');
INSERT INTO "finnish" VALUES(25,31,'25:31','Tällä tavoin Me olemme antanut jokaiselle profeetalle vihamiehiä syntisten joukossa. Mutta Herrasi on riittävä opas ja riittävä auttaja.');
INSERT INTO "finnish" VALUES(25,32,'25:32','Uskottomat sanovat myös: »Miksi koko Koraania ei ilmoitettu hänelle yhdellä kertaa?» Siksi, että saatoimme sen avulla vahvistaa sydäntäsi, Me esitimme sen jaksoittain.');
INSERT INTO "finnish" VALUES(25,33,'25:33','He eivät voi esittää sinulle ainoatakaan väitettä, josta emme sanoisi sinulle totuutta ja antaisi parasta selitystä.');
INSERT INTO "finnish" VALUES(25,34,'25:34','Niiden osa, jotka yhteenkoottuina lankeavat kasvoilleen helvetissä, on oleva onnettomin ja he ovat etäimmälle eksyneitä.');
INSERT INTO "finnish" VALUES(25,35,'25:35','Totisesti, Me annoimme Moosekselle Pyhän Kirjan ja määräsimme hänen veljensä Aaronin hänen avustajakseen.');
INSERT INTO "finnish" VALUES(25,36,'25:36','Me lausuimme: »Lähtekää kumpikin kansan luokse, joka on kieltänyt Meidän ilmoituksemme.» Viimein Me tuhosimme nämä perin pohjin.');
INSERT INTO "finnish" VALUES(25,37,'25:37','Ja Nooan kansan, kun se hylkäsi lähettiläämme, me hukutimme ja asetimme sen varoitusmerkiksi ihmissuvulle. Me olemme valmistanut jumalattomille tuskallisen rangaistuksen.');
INSERT INTO "finnish" VALUES(25,38,'25:38','Me tuhosimme myös Aadin ja Tamuudin sekä Rassin asukkaat sekä myös monia sukupolvia heidän väliltään.');
INSERT INTO "finnish" VALUES(25,39,'25:39','Jokaiselle Me esitimme vertauksia ja jokaisen Me perinpohjin tuhosimme.');
INSERT INTO "finnish" VALUES(25,40,'25:40','Aivan varmasti he ovat kulkeneet kaupungin ohi, jonka ylitse Me lähetimme hirvittävän sateen. Eivätkö he ole sitä havainneet? Ei, heidänlaisensa eivät odota ylösnousemusta.');
INSERT INTO "finnish" VALUES(25,41,'25:41','Ja nähdessään sinut, Muhammed, he pitävät sinua vain pilkkanaan: »Tämäkö on se, jonka Jumala on lähettänyt sanansa julistajaksi?');
INSERT INTO "finnish" VALUES(25,42,'25:42','Hän oli vähällä saada meidät luopumaan jumalistamme, ellemme olisi riippuneet heissä uskollisesti.» Mutta kun he aikanaan näkevät tuomionsa, he tulevat tietämään, kuka on oikealta tieltä kauimmas eksynyt.');
INSERT INTO "finnish" VALUES(25,43,'25:43','Mikä on ajatuksesi sellaisesta, joka on ottanut oman himonsa jumalakseen? Sinäkö sellaisen suojelijaksi pyrkisit?');
INSERT INTO "finnish" VALUES(25,44,'25:44','Älä usko, että useimmat heistä mitään vaarinottavat tai ymmärtävät. He ovat karjan kaltaisia; ei, he ovat kauemmaksi oikealta tieltä eksyneet kuin karja.');
INSERT INTO "finnish" VALUES(25,45,'25:45','Oletko huomannut, miten Herrasi saa varjon lankeamaan? Hän olisi voinut tehdä sen pysyväksi. Sitten loimme auringon sitä osoittamaan.');
INSERT INTO "finnish" VALUES(25,46,'25:46','Sitten siirrämme sitä hiljalleen Itseämme kohti.');
INSERT INTO "finnish" VALUES(25,47,'25:47','Hän on antanut teille yön peitoksi, unen levoksi ja päivän työajaksi.');
INSERT INTO "finnish" VALUES(25,48,'25:48','Juuri Hän lähettää tuulet tuomaan iloisia armonsanomia, ja pilvistä Me lähetämme puhdasta vettä,');
INSERT INTO "finnish" VALUES(25,49,'25:49','elvyttääksemme kuivuneen maan ja antaaksemme sitä juomaksi , niille, otka olemme luonut, sekä ihmisjoukoille että karjalle.');
INSERT INTO "finnish" VALUES(25,50,'25:50','Totisesti, Me annamme heille sadetta kausittain, jotta he muistaisivat Meitä, mutta useimmat ihmiset eivät ole muuhun taipuvaisia kuin kiittämättömyyteen.');
INSERT INTO "finnish" VALUES(25,51,'25:51','Jos olisimme halunnut, olisimme voinut nostattaa esille varoittajan jokaisesta kaupungista.');
INSERT INTO "finnish" VALUES(25,52,'25:52','Älkää siis suostuko uskottomiin, vaan taistelkaa kaikin voimin heitä vastaan.');
INSERT INTO "finnish" VALUES(25,53,'25:53','Juuri Hän pitää erillään kahdenlaiset vedet. Toinen on makea, janoisia virkistävä vesi, toinen taas suolainen ja katkera, ja kummankin välille Hän on asettanut aitauksen ja ylipääsemättömän esteen.');
INSERT INTO "finnish" VALUES(25,54,'25:54','Juuri Hän on luonut ihmisen vedestä ja antanut hänelle sukulaisuus~ sekä aviositeet, sillä Herrasi on mahtava.');
INSERT INTO "finnish" VALUES(25,55,'25:55','Kuitenkin he palvelevat Jumalan rinnalla sellaista, mikä ei heitä hyödytä eikä vahingoitakaan. Uskoton on mukana taistelussa Herraansa vastaan.');
INSERT INTO "finnish" VALUES(25,56,'25:56','Olemme lähettänyt sinut vain tuomaan ilosanomaa ja myös varoittamaan.');
INSERT INTO "finnish" VALUES(25,57,'25:57','Sano: »En pyydä siitä teiltä mitään palkkaa, mutta ken haluaa suunnata tiensä Herraa kohti, hän tehköön niin.»');
INSERT INTO "finnish" VALUES(25,58,'25:58','Luota ainoaan elävään, joka ei milloinkaan kuole, ja julista Hänen kunniaansa Häntä ylistäen. Hän tietää tarkoin palvelijainsa synnit.');
INSERT INTO "finnish" VALUES(25,59,'25:59','Hän on luonut taivaat ja maan ja kaiken, mikä niiden välillä on, kuudessa kaudessa ja senjälkeen voimallansa hallitsee kaikkea. Hän on armahtaja. Kysy sellaiselta, jolla on tietoa Hänestä.');
INSERT INTO "finnish" VALUES(25,60,'25:60','Kun heille sanotaan: »Alistukaa laupiaan armahtajan edessä», niin he kyselevät: »Mikä sitten on armahtaja? Pitääkö meidän kumartua kenen edessä sinä käsket?» Tämä lisää heidän vastahakoisuuttaan.');
INSERT INTO "finnish" VALUES(25,61,'25:61','Siunattu olkoon Hän, joka on sijoittanut tähtisikermät taivaalle ja asettanut sinne myös päivän soihdun ja paistavan kuun.');
INSERT INTO "finnish" VALUES(25,62,'25:62','Juuri Hän on säätänyt yön ja päivän toisiansa seuraamaan, niitä varten, jotka haluavat siitä ottaa vaarin tai jotka tahtovat kiittää.');
INSERT INTO "finnish" VALUES(25,63,'25:63','Laupeimman armahtajan palvelijoita ovat ne, jotka nöyrinä vaeltavat maan päällä ja tietämättömien heitä puhutellessa lausuvat: »Rauha!»');
INSERT INTO "finnish" VALUES(25,64,'25:64','Sekä ne, jotka viettävät yön Herransa edessä, rukoillen maahan vaipuneina tai seisaaltaan.');
INSERT INTO "finnish" VALUES(25,65,'25:65','Sekä ne, jotka lausuvat: »Herra, torju pois meistä helvetin kuritus; totisesti, ikuinen on sen kidutus.»');
INSERT INTO "finnish" VALUES(25,66,'25:66','Totisesti, se on kauhistuttava leposija ja asuinpaikka.');
INSERT INTO "finnish" VALUES(25,67,'25:67','Armahtajan palvelijoita ovat myös ne, jotka eivät tuhlaa eivätkä ole itaria, vaan kulkevat keskitietä.');
INSERT INTO "finnish" VALUES(25,68,'25:68','Samoin ne, jotka eivät rukoile muita jumalia kuin Jumalaa eivätkä syyttömästi ketään tapa, minkä Jumala on kieltänyt, eivätkä antaudu minkäänlaiseen haureuteen. Ken näin tekee, hän on saava synnin palkan.');
INSERT INTO "finnish" VALUES(25,69,'25:69','Kaksinkertainen on hänen rangaistuksensa oleva ylösnousemuksen päivänä, ja häpeäänsä hän saa ainaiseksi jäädä.');
INSERT INTO "finnish" VALUES(25,70,'25:70','Mutta ken tekee katumuksen ja uskoo ja suorittaa hyviä tekoja, hänen pahat tekonsa Jumala on vaihtava hyviksi, sillä Jumala on anteeksiantavainen, armollinen.');
INSERT INTO "finnish" VALUES(25,71,'25:71','Kuka kääntyy ja tekee hyvää, totisesti, hänen kääntymyksensä Jumalan puoleen on otollinen.');
INSERT INTO "finnish" VALUES(25,72,'25:72','Ne, jotka eivät anna väärää todistusta, ja jotka turhaa puhetta kuullessaan kulkevat arvokkaasti ohi,');
INSERT INTO "finnish" VALUES(25,73,'25:73','jotka saadessaan muistutuksen Herransa tahdosta eivät tekeydy kuuroiksi ja sokeiksi,');
INSERT INTO "finnish" VALUES(25,74,'25:74','ja ne, jotka sanovat: »Herra, suo meille silmien iloa vaimoistamme ja jälkeläisistämme ja tee meistä niiden esikuvia, jotka hartautta harjoittavat,');
INSERT INTO "finnish" VALUES(25,75,'25:75','nämä kaikki palkitaan kestävyydestään ja saavat ylhäiset asuinsijat, joissa heidät otetaan vastaan suloisin tervehdyksin ja rauhantoivotuksin.');
INSERT INTO "finnish" VALUES(25,76,'25:76','Siellä he saavat asua; mikä ihana asuinsija ja lepopaikka!');
INSERT INTO "finnish" VALUES(25,77,'25:77','Sano uskottomille: »Herra ei välitä teistä, ellette Häntä avuksi huuda. Mutta te olette pitäneet totuutta valheena, ja ikuinen tuska on oleva osanne.»');
INSERT INTO "finnish" VALUES(26,1,'26:1','Taa, Siin, Miim. (Laupias, kuuleva, tietävä Jumala.)');
INSERT INTO "finnish" VALUES(26,2,'26:2','Nämä ovat säkeitä Ilmestyksen kirjasta.');
INSERT INTO "finnish" VALUES(26,3,'26:3','Joudutko epätoivoon, koska he eivät tahdo uskoa?');
INSERT INTO "finnish" VALUES(26,4,'26:4','Jos olisimme tahtonut, olisimme voinut lähettää heille taivaasta merkin, jonka edessä heidän niskansa olisivat taipuneet.');
INSERT INTO "finnish" VALUES(26,5,'26:5','Jokaisen uuden merkin tullessa laupiaalta Armahtajalta he kääntävät selkänsä.');
INSERT INTO "finnish" VALUES(26,6,'26:6','Nyt he tosin hylkäävät ne valheena, mutta totuus, jolle he ovat ilkkuneet, on pian heidät yllättävä.');
INSERT INTO "finnish" VALUES(26,7,'26:7','Eivätkö he katso maata; kuinka monenlaista ja oivallista Me olemmekaan siinä kasvattanut!');
INSERT INTO "finnish" VALUES(26,8,'26:8','Totisesti, tässä on heille merkki, mutta useimmat heistä eivät ota uskoakseen.');
INSERT INTO "finnish" VALUES(26,9,'26:9','Totisesti Hän, Herrasi, on mahtava, armollisin.');
INSERT INTO "finnish" VALUES(26,10,'26:10','Muista, miten Herrasi kutsui Mooseksen sanoen: »Mene väärämielisen kansan');
INSERT INTO "finnish" VALUES(26,11,'26:11','faraon kansan luokse. Ehkä se on pahasta luopuva.»');
INSERT INTO "finnish" VALUES(26,12,'26:12','Hän vastasi: »Herra, pelkään heidän minut vääräksi väittävän;');
INSERT INTO "finnish" VALUES(26,13,'26:13','rintaani ahdistaa eikä kieleni ole nopea puhumaan. Sentähden nouda Aaron avustajakseni!');
INSERT INTO "finnish" VALUES(26,14,'26:14','Olen myös rikkonut heitä vastaan, niin että pelkään heidän surmaavan minut.»');
INSERT INTO "finnish" VALUES(26,15,'26:15','Herra sanoi: »Sitä älä pelkää. Lähtekää kumpikin viemään merkkejämme; totisesti, Me olemme teidän tykönänne ja kuulemme.');
INSERT INTO "finnish" VALUES(26,16,'26:16','Menkää siis faraon eteen ja sanokaa: »Totisesti, Me olemme tuomassa maailmojen Herran sanaa,');
INSERT INTO "finnish" VALUES(26,17,'26:17','että päästäisit israelilaiset meidän mukanamme.»');
INSERT INTO "finnish" VALUES(26,18,'26:18','Farao vastasi: »Emmekö me sinua lapsena kasvattaneet keskuudessamme, ja etkö viettänyt luonamme useita elämäsi vuosia?');
INSERT INTO "finnish" VALUES(26,19,'26:19','Mutta sinä teit rikoksen, ja olet perin kiittämätön.»');
INSERT INTO "finnish" VALUES(26,20,'26:20','Hän vastasi: »Tein sen silloin, kun vielä olin oikeata ymmärrystä vailla.');
INSERT INTO "finnish" VALUES(26,21,'26:21','Niin pakeninkin luotanne, koska pelkäsin teitä, mutta sitten on Herra antanut minulle viisautta ja tehnyt minusta sanansaattajansa.');
INSERT INTO "finnish" VALUES(26,22,'26:22','Mutta sitäkö minulle muistutat suosionasi, että olet orjuuttanut israelilaiset?»');
INSERT INTO "finnish" VALUES(26,23,'26:23','Farao sanoi: »Kuka on se »maailmojen Herra»?');
INSERT INTO "finnish" VALUES(26,24,'26:24','Mooses vastasi: »Hän on taivaitten ja maan ja kaiken näiden välillä olevan Herra, jos se teitä vakuuttaa.»');
INSERT INTO "finnish" VALUES(26,25,'26:25','Ja Farao lausui ympärillä oleville: »Ettekö kuule?»');
INSERT INTO "finnish" VALUES(26,26,'26:26','Mooses sanoi: »Hän on teidän ja esi~isienne Herra.»');
INSERT INTO "finnish" VALUES(26,27,'26:27','Farao lausui: »Totisesti, sananjulistaja, joka on luoksenne lähetetty, on hullu mies.»');
INSERT INTO "finnish" VALUES(26,28,'26:28','Mooses sanoi edelleen: »Hän on idän ja lännen ja kaiken näiden välillä olevan Herra; jospa sen vain käsittäisitte.»');
INSERT INTO "finnish" VALUES(26,29,'26:29','Farao sanoi: »Jos otat jumalaksesi jonkun muun kuin minut, panen sinut vankien joukkoon.»');
INSERT INTO "finnish" VALUES(26,30,'26:30','Mooses vastasi: »Entä, jos tuon sinulle ilmeisen todistuksen?»');
INSERT INTO "finnish" VALUES(26,31,'26:31','Hän sanoi: »Tuo se, jos olet totuudessa.»');
INSERT INTO "finnish" VALUES(26,32,'26:32','Silloin Mooses heitti sauvansa maahan, ja katso, se muuttui käärmeeksi.');
INSERT INTO "finnish" VALUES(26,33,'26:33','Sitten hän ojensi kätensä, ja katso, se näytti katsojista vaikealta.');
INSERT INTO "finnish" VALUES(26,34,'26:34','Silloin farao lausui ympärillään oleville päämiehille: »Totta tosiaan, hän on taitava velho,');
INSERT INTO "finnish" VALUES(26,35,'26:35','joka noituudellaan tahtoo karkoittaa teidät maastanne Mitä siis neuvotte?»');
INSERT INTO "finnish" VALUES(26,36,'26:36','He vastasivat: »Anna hänen veljinensä odottaa ja lähetä sanansaattajia kaupunkeihin');
INSERT INTO "finnish" VALUES(26,37,'26:37','noutamaan eteesi kaikki etevät taikurit.');
INSERT INTO "finnish" VALUES(26,38,'26:38','Kun sitten taikurit oli määräpäivänä koottu tiettyyn paikkaan,');
INSERT INTO "finnish" VALUES(26,39,'26:39','niin kansalle ilmoitettiin: »Kerääntykää tekin paikalle,');
INSERT INTO "finnish" VALUES(26,40,'26:40','jotta voimme seurata taikureita; ehkä he voittavat.»');
INSERT INTO "finnish" VALUES(26,41,'26:41','Kun taikurit olivat saapuneet, he lausuivat faraolle: »Saammeko palkinnon, jos voitamme?»');
INSERT INTO "finnish" VALUES(26,42,'26:42','Hän vastasi: »Saatte, ja totisesti kuulutte lähimpieni joukkoon.»');
INSERT INTO "finnish" VALUES(26,43,'26:43','Mooses lausui heille: »Heittäkää maahan, mitä teillä on heittämistä!»');
INSERT INTO "finnish" VALUES(26,44,'26:44','Niin he heittivät maahan köytensä ja sauvansa ja sanoivat: »Totisesti, faraon voimalla voitamme.»');
INSERT INTO "finnish" VALUES(26,45,'26:45','Silloin Mooses heitti maahan oman sauvansa, ja katso, se nieli kaiken, mitä he olivat loihtineet esiin.');
INSERT INTO "finnish" VALUES(26,46,'26:46','Silloin taikurit lankesivat maahan kumartaen.');
INSERT INTO "finnish" VALUES(26,47,'26:47','He sanoivat: »Me uskomme maailmojen Herraan,');
INSERT INTO "finnish" VALUES(26,48,'26:48','Mooseksen ja Aaronin Herraan.»');
INSERT INTO "finnish" VALUES(26,49,'26:49','Farao lausui: »Uskotteko te häneen, ennen kuin minä sen sallin? Varmasti hän on päämiehenne, joka on teille opettanut noituutta, mutta sen te tulette näkemään. Totisesti, hakkautan poikki teidän kätenne ja jalkanne vastakkaisilta puolilta ja naulitsen teidät ristiin kaikki tyynni.»');
INSERT INTO "finnish" VALUES(26,50,'26:50','He vastasivat: »Se ei ole pahaksi; sillä palaamme totisesti Herramme luokse.');
INSERT INTO "finnish" VALUES(26,51,'26:51','Toivomme hartaasti, että Herra antaa meille syntimme anteeksi, koska olemme ensimmäisiä, jotka uskovat.»');
INSERT INTO "finnish" VALUES(26,52,'26:52','Me ilmestyimme Moosekselle sanoen: »Lähde matkaan yöllä palvelijoitteni kanssa, sillä teitä varmasti ajetaan takaa.»');
INSERT INTO "finnish" VALUES(26,53,'26:53','Mutta farao lähetti sanansaattajia kaupunkeihin, ja he sanoivat.');
INSERT INTO "finnish" VALUES(26,54,'26:54','Totisesti, näitä ihmisiä on vain pieni joukko,');
INSERT INTO "finnish" VALUES(26,55,'26:55','ja katso, he ovat meille vihaisia,');
INSERT INTO "finnish" VALUES(26,56,'26:56','mutta meitä on valpas, asekuntoinen joukko.»');
INSERT INTO "finnish" VALUES(26,57,'26:57','Niin Me saimme heidät lähtemään puutarhoista ja lähteiden luota,');
INSERT INTO "finnish" VALUES(26,58,'26:58','aarteittensa äärestä ja upeista asunnoistaan.');
INSERT INTO "finnish" VALUES(26,59,'26:59','Niin tapahtui, mutta tämän kaiken Me annoimme perinnöksi israelilaisille Kanaanin maassa.');
INSERT INTO "finnish" VALUES(26,60,'26:60','Sitten he seurasivat israelilaisia auringon noustessa,');
INSERT INTO "finnish" VALUES(26,61,'26:61','jolloin kumpikin sotajoukko näki toisensa ja Mooseksen seuralaiset sanoivat: »Totisesti, meidät oh yllätetty.»');
INSERT INTO "finnish" VALUES(26,62,'26:62','Mutta Mooses lausui: »Ei suinkaan. Totisesti, Herra on minun kanssani, ja Hän on ohjaava minua.»');
INSERT INTO "finnish" VALUES(26,63,'26:63','Ja me ilmestyimme Moosekselle sanoen: »Lyö sauvallasi merta!» Silloin meri jakautui kahtia, niin että kummallakin puolella kohosi kuin vesiseinä.');
INSERT INTO "finnish" VALUES(26,64,'26:64','Sitten annoimme toisten lähestyä paikkaa.');
INSERT INTO "finnish" VALUES(26,65,'26:65','Ja Me pelastimme Mooseksen ja kaikki ne, jotka olivat hänen kanssaan.');
INSERT INTO "finnish" VALUES(26,66,'26:66','Mutta Me hukutimme nuo toiset.');
INSERT INTO "finnish" VALUES(26,67,'26:67','Totisesti, tässä on merkki, mutta useimmat ihmiset eivät usko.');
INSERT INTO "finnish" VALUES(26,68,'26:68','Totisesti Hän, sinun Herrasi, on kaikkivaltias, kaikkein armollisin.');
INSERT INTO "finnish" VALUES(26,69,'26:69','Kerro heille myös Aabrahamin tarina.');
INSERT INTO "finnish" VALUES(26,70,'26:70','Kun hän puhui isälleen ja kansalleen: »Ketä te palvelette?»');
INSERT INTO "finnish" VALUES(26,71,'26:71','he vastasivat: »Me palvelemme jumalankuvia ja aiomme pysyä niille uskollisina.»');
INSERT INTO "finnish" VALUES(26,72,'26:72','Hän kysyi: »Kuulevatko ne, kun te niitä rukoilette?');
INSERT INTO "finnish" VALUES(26,73,'26:73','Tai onko niistä teille hyötyä tai vahinkoa?»');
INSERT INTO "finnish" VALUES(26,74,'26:74','He vastasivat: »Ei, mutta näimme esi-isiemme niin tehneen.»');
INSERT INTO "finnish" VALUES(26,75,'26:75','Hän sanoi: »Oletteko sitten ajatelleet, mitä te oikeastaan palvelette,');
INSERT INTO "finnish" VALUES(26,76,'26:76','te ja teidän muinaiset esi-isänne?');
INSERT INTO "finnish" VALUES(26,77,'26:77','Sillä totisesti, ne ovat minun vihollisiani kaikki tyynni; mutta ei maailmojen Herra,');
INSERT INTO "finnish" VALUES(26,78,'26:78','Hän, joka minut loi ja antoi sitten johdatuksensa.');
INSERT INTO "finnish" VALUES(26,79,'26:79','Hän joka antaa minulle syötävää sekä juotavaa.');
INSERT INTO "finnish" VALUES(26,80,'26:80','Ja sairastaessani Hän parantaa minut.');
INSERT INTO "finnish" VALUES(26,81,'26:81','Hän, joka saattaa minut kuolemaan ja sitten antaa minulle elämän.');
INSERT INTO "finnish" VALUES(26,82,'26:82','Ja joka toivoni mukaan antaa anteeksi syntini tuomiopäivänä.');
INSERT INTO "finnish" VALUES(26,83,'26:83','Herra! Suo minulle viisautta ja liitä minut hyvien joukkoon!');
INSERT INTO "finnish" VALUES(26,84,'26:84','Jätä minusta totuuden maine tuleviin sukupolviin');
INSERT INTO "finnish" VALUES(26,85,'26:85','ja tee minusta autuuden tarhan perillinen.');
INSERT INTO "finnish" VALUES(26,86,'26:86','Anna anteeksi isälleni, sillä totisesti hän on harhassa.');
INSERT INTO "finnish" VALUES(26,87,'26:87','Älä saata minua häpeään ylösnousemuksen päivänä,');
INSERT INTO "finnish" VALUES(26,88,'26:88','päivänä, jolloin ei rakkaudesta eikä pojista ole mitään hyötyä,');
INSERT INTO "finnish" VALUES(26,89,'26:89','paitsi sille, joka lähestyy Jumalaa hartain sydämin.»');
INSERT INTO "finnish" VALUES(26,90,'26:90','Puutarha avautuu niille, jotka pahaa karttavat,');
INSERT INTO "finnish" VALUES(26,91,'26:91','mutta eksyneiden eteen ilmaantuu helvetti.');
INSERT INTO "finnish" VALUES(26,92,'26:92','Heille sanotaan: »Missä ovat ne, joita te palvelitte');
INSERT INTO "finnish" VALUES(26,93,'26:93','Jumalan sijasta? Voivatko he teitä auttaa tai edes itseänsä suojella?»');
INSERT INTO "finnish" VALUES(26,94,'26:94','Heidät heitetään helvettiin, sekä heidät että ne, jotka eksyivät,');
INSERT INTO "finnish" VALUES(26,95,'26:95','ja saatanan sotajoukko, kaikki tyynni.');
INSERT INTO "finnish" VALUES(26,96,'26:96','Siellä he kiistelevät keskenänsä ja sanovat:');
INSERT INTO "finnish" VALUES(26,97,'26:97','»Jumalan nimessä, olemme todellakin olleet ilmeisen harhan vallassa,');
INSERT INTO "finnish" VALUES(26,98,'26:98','kun me pidimme teitä maailmojen Herran vertaisina.');
INSERT INTO "finnish" VALUES(26,99,'26:99','Juuri syntiset johtivat meidät harhaan.');
INSERT INTO "finnish" VALUES(26,100,'26:100','Ja nyt meillä ei ole ketään auttajaa,');
INSERT INTO "finnish" VALUES(26,101,'26:101','eikä yhtäkään tosi ystävää.');
INSERT INTO "finnish" VALUES(26,102,'26:102','Jos saisimme vielä palata elämään, tulisimme uskovaisiksi.»');
INSERT INTO "finnish" VALUES(26,103,'26:103','Totisesti, tässä on opetus, mutta useimmat heistä eivät usko.');
INSERT INTO "finnish" VALUES(26,104,'26:104','Totisesti, Hän, sinun Herrasi, on kaikkivaltias, kaikkein armollisin.');
INSERT INTO "finnish" VALUES(26,105,'26:105','Nooan kansa piti lähettiläitä valehtelijoina.');
INSERT INTO "finnish" VALUES(26,106,'26:106','Nooa, heidän heimolaisensa, sanoi heille: »Ettekö pelkää Jumalaa?');
INSERT INTO "finnish" VALUES(26,107,'26:107','Totisesti, minä olen teille luotettava sananjulistaja.');
INSERT INTO "finnish" VALUES(26,108,'26:108','Sentähden palvelkaa Jumalaa ja totelkaa minua.');
INSERT INTO "finnish" VALUES(26,109,'26:109','Teiltä en pyydä siitä mitään palkkaa. Minun palkkani on itse maailmaan valtiaan luona.');
INSERT INTO "finnish" VALUES(26,110,'26:110','Pelätkää siis Jumalaa ja totelkaa minua!»');
INSERT INTO "finnish" VALUES(26,111,'26:111','He vastasivat: »Pitäisikö meidän uskoa sinua, kun vain halvimmat ihmiset sinua seuraavat?»');
INSERT INTO "finnish" VALUES(26,112,'26:112','Hän sanoi: »Ei minun tarvitse tietää, mitä on heidän tekojensa takana.');
INSERT INTO "finnish" VALUES(26,113,'26:113','Heidän tutkimisensa on yksin Herran asia, jospa sen ymmärtäisitte!');
INSERT INTO "finnish" VALUES(26,114,'26:114','Minä en aio karkoittaa uskovaisia luotani.');
INSERT INTO "finnish" VALUES(26,115,'26:115','Olen vain julkinen varoittaja.»');
INSERT INTO "finnish" VALUES(26,116,'26:116','He uhkasivat: »Totisesti, ellet jo herkeä, oi Nooa, niin sinut varmasti kivitetään.»');
INSERT INTO "finnish" VALUES(26,117,'26:117','Hän rukoili:»Herra, tosiaankin on kansani minut hylännt.');
INSERT INTO "finnish" VALUES(26,118,'26:118','Sentähden tuomitse Sinä minut ja heidät ja pelasta minut sekä minua seuraavat uskovaiset.»');
INSERT INTO "finnish" VALUES(26,119,'26:119','Silloin Me pelastimme hänet ja hänen kanssansa olleet keräämällä kaikki arkkiin.');
INSERT INTO "finnish" VALUES(26,120,'26:120','Sitten Me hukutimme muut.');
INSERT INTO "finnish" VALUES(26,121,'26:121','Totisesti, tässä on tunnusmerkki, mutta useimmat heistä eivät ota uskoakseen.');
INSERT INTO "finnish" VALUES(26,122,'26:122','Totisesti, Hän, sinun Herrasi, on kaikkivaltias, kaikkein armollisin.');
INSERT INTO "finnish" VALUES(26,123,'26:123','Myös Aadin heimo piti lähettiläitä valehtelijoina.');
INSERT INTO "finnish" VALUES(26,124,'26:124','Heidän heimolaisensa Huud puhui heille: »Ettekö pelkää Jumalaa?');
INSERT INTO "finnish" VALUES(26,125,'26:125','Totisesti, minä olen luotettava sananjulistaja teille.');
INSERT INTO "finnish" VALUES(26,126,'26:126','Siksi palvelkaa Jumalaa ja totelkaa minua.');
INSERT INTO "finnish" VALUES(26,127,'26:127','En pyydä teiltä siitä mitään palkkaa. Minun palkkani on yksin maailmojen valtiaan huostassa.');
INSERT INTO "finnish" VALUES(26,128,'26:128','Rakennatteko turhan tähden joka kukkulalle muistomerkin?');
INSERT INTO "finnish" VALUES(26,129,'26:129','Te pystytätte linnoituksia, ikäänkuin ette koskaan kuolisi.');
INSERT INTO "finnish" VALUES(26,130,'26:130','Ja kun te puututte ihmisiin, niin teette sen kuin väkivaltaiset.');
INSERT INTO "finnish" VALUES(26,131,'26:131','Palvelkaa siis Jumalaa ja totelkaa minua.');
INSERT INTO "finnish" VALUES(26,132,'26:132','Palvelkaa Häntä, joka on teille suonut kaiken.');
INSERT INTO "finnish" VALUES(26,133,'26:133','Hän on lahjoittanut teille runsaasti karjaa ja perillisiä,');
INSERT INTO "finnish" VALUES(26,134,'26:134','puutarhoja ja lähteitä.');
INSERT INTO "finnish" VALUES(26,135,'26:135','Todella pelkään tuomion lankeavan päällenne suurena päivänä.»');
INSERT INTO "finnish" VALUES(26,136,'26:136','He sanoivat: »Emme välitä siitä, varoitatko meitä vai jätätkö varoittamatta.');
INSERT INTO "finnish" VALUES(26,137,'26:137','Tämä kaikki on vain muinaistaru,');
INSERT INTO "finnish" VALUES(26,138,'26:138','eikä meitä rangaistus odota.»');
INSERT INTO "finnish" VALUES(26,139,'26:139','Siten he hylkäsivät hänet, ja siksi Me hävitimme heidät. Totisesti, tässä on opetus, mutta useimmat heistä eivät ota uskoakseen.');
INSERT INTO "finnish" VALUES(26,140,'26:140','Herrasi on Mahtava, Armelias.');
INSERT INTO "finnish" VALUES(26,141,'26:141','Myös Tamuudin heimo piti lähettiläitä valehtelijoina.');
INSERT INTO "finnish" VALUES(26,142,'26:142','Heidän heimolaisensa Saalih puhui heille: »Ettekö pelkää Jumalaa?»');
INSERT INTO "finnish" VALUES(26,143,'26:143','Totisesti, olen teille luotettava sananjulistaja.');
INSERT INTO "finnish" VALUES(26,144,'26:144','Siksi palvelkaa Jumalaa ja totelkaa minua!');
INSERT INTO "finnish" VALUES(26,145,'26:145','En pyydä teiltä siitä mitään palkkaa, sillä minun palkkani on yksin maailmojen Herran huomassa.');
INSERT INTO "finnish" VALUES(26,146,'26:146','Tahdotteko saada turvassa jatkaa nykyistä elämäänne?');
INSERT INTO "finnish" VALUES(26,147,'26:147','Puutarhoissa ja lähteitten äärillä,');
INSERT INTO "finnish" VALUES(26,148,'26:148','viljapelloilla ja hienohedelmäisten palmujen luona,');
INSERT INTO "finnish" VALUES(26,149,'26:149','hakattuanne suurella taidolla asuntonne kallioihin.');
INSERT INTO "finnish" VALUES(26,150,'26:150','Pelätkää siis Jumalaa ja totelkaa minua,');
INSERT INTO "finnish" VALUES(26,151,'26:151','älkääkä noudattako syntisten kutsuja.');
INSERT INTO "finnish" VALUES(26,152,'26:152','He levittävät maassa villitystä eivätkä totuutta rakenna.»');
INSERT INTO "finnish" VALUES(26,153,'26:153','He sanoivat: »Sinä kuulut itse lumottuihin.');
INSERT INTO "finnish" VALUES(26,154,'26:154','Olethan vain meidän kaltaisemme ihminen. Näytä meille sentähden tunnusmerkkisi, jos olet tosissasi.»');
INSERT INTO "finnish" VALUES(26,155,'26:155','Hän vastasi: »Tässä on naaraskameeli, sillä on juottovuoronsa, aivan kuin teillä itsellännekin on määräaikoina vuoronne juoda.');
INSERT INTO "finnish" VALUES(26,156,'26:156','Älkää siis vahingoittako sitä, ettei suuren päivän tuomio lankeaisi päällenne.»');
INSERT INTO "finnish" VALUES(26,157,'26:157','Kuitenkin he katkaisivat sen polvijänteet, mutta saivat katua sitä.');
INSERT INTO "finnish" VALUES(26,158,'26:158','Sillä rangaistus saavutti heidät. Totisesti, tässä on merkki, mutta useimmat heistä eivät ota uskoakseen.');
INSERT INTO "finnish" VALUES(26,159,'26:159','Hän, sinun Herrasi, on kaikkivaltias, kaikkein armollisin.');
INSERT INTO "finnish" VALUES(26,160,'26:160','Myöskin Lootin kansa piti lähettiläitä valehtelijoina.');
INSERT INTO "finnish" VALUES(26,161,'26:161','Heidän heimolaisensa Loot puhui heille: »Ettekö pelkää Jumalaa?');
INSERT INTO "finnish" VALUES(26,162,'26:162','Totisesti, olen teille luotettava sananjulistaja.');
INSERT INTO "finnish" VALUES(26,163,'26:163','Sentähden pelätkää Jumalaa ja totelkaa minua!');
INSERT INTO "finnish" VALUES(26,164,'26:164','En pyydä teiltä siitä mitään palkkaa, sillä palkkani on yksin maailmojen valtiaan huomassa.');
INSERT INTO "finnish" VALUES(26,165,'26:165','Kuinka saatattekaan lähestyä kaiken maailman miehiä,');
INSERT INTO "finnish" VALUES(26,166,'26:166','ja hylkiä vaimojanne, jotka Jumala on teille antanut? Ei, te olette irstaisia ihmisiä.»');
INSERT INTO "finnish" VALUES(26,167,'26:167','He uhkasivat: »Totisesti, ellet jo herkeä, Loot, niin sinut varmasti karkoitetaan.»');
INSERT INTO "finnish" VALUES(26,168,'26:168','Hän lausui: »Totisesti, minä kammoksun teidän tekojanne.');
INSERT INTO "finnish" VALUES(26,169,'26:169','Herra, pelasta minut ja omaiseni heidän hankkeistaan.»');
INSERT INTO "finnish" VALUES(26,170,'26:170','Niin Me pelastimme hänet ja hänen perheensä');
INSERT INTO "finnish" VALUES(26,171,'26:171','lukuunottamatta vanhaa naista, joka jäi heistä jälkeen.');
INSERT INTO "finnish" VALUES(26,172,'26:172','Sitten tuhosimme nuo toiset perinpohjin.');
INSERT INTO "finnish" VALUES(26,173,'26:173','Me annoimme sateen langeta heidän päällensä; se oli hirvittävä noille varoituksen saaneille.');
INSERT INTO "finnish" VALUES(26,174,'26:174','Totisesti, tässä on tunnusmerkki, mutta useimmat heistä eivät ota uskoakseen.');
INSERT INTO "finnish" VALUES(26,175,'26:175','Totisesti, Hän, sinun Herrasi, on kaikkivaltias, kaikkein armollisin.');
INSERT INTO "finnish" VALUES(26,176,'26:176','Myös metsien heimo piti lähettiläitä valehtelijoina.');
INSERT INTO "finnish" VALUES(26,177,'26:177','Shuaih puhui heille: »Ettekö pelkää Jumalaa?');
INSERT INTO "finnish" VALUES(26,178,'26:178','Totisesti, olen teille luotettava sananjulistaja.');
INSERT INTO "finnish" VALUES(26,179,'26:179','Pelätkää siis Jumalaa ja totelkaa minua.');
INSERT INTO "finnish" VALUES(26,180,'26:180','En pyydä teiltä siitä mitään palkkaa, palkkani on yksin maailmojen valtiaan huomassa.');
INSERT INTO "finnish" VALUES(26,181,'26:181','Antakaa täysi mitta, ettette kuuluisi vajavasti mittaaviin.');
INSERT INTO "finnish" VALUES(26,182,'26:182','Punnitkaa myös oikealla puntarilla.');
INSERT INTO "finnish" VALUES(26,183,'26:183','Älkää anastako vääryydellä ihmisten varoja älkääkä levittäkö turmelusta maassa väärillä teoillanne.');
INSERT INTO "finnish" VALUES(26,184,'26:184','Pelätkäå Häntä, joka loi teidät ja entisetkin kansat.»');
INSERT INTO "finnish" VALUES(26,185,'26:185','He vastasivat: »Sinä olet itse lumouksen vallassa.');
INSERT INTO "finnish" VALUES(26,186,'26:186','Olethan vain meidän kaltaisemme ihminen, ja tiedämme sinun tosiaankin valehtelevan.');
INSERT INTO "finnish" VALUES(26,187,'26:187','Anna vaikka taivaan lohkon pudota päällemme, jos olet tosissasi.»');
INSERT INTO "finnish" VALUES(26,188,'26:188','Hän lausui: »Herra tietää, mitä te teette.»');
INSERT INTO "finnish" VALUES(26,189,'26:189','Niin he kielsivät hänet, ja tuomiopäivän rangaistus lankesi heidän päällensä. Se oli todella kauhunpäivän tuomio.');
INSERT INTO "finnish" VALUES(26,190,'26:190','Totisesti, tässä on merkki, mutta useimmat heistä eivät ota uskoakseen.');
INSERT INTO "finnish" VALUES(26,191,'26:191','Katso, Hän, sinun Herrasi, on kaikkivaltias, kaikkein armollisin.');
INSERT INTO "finnish" VALUES(26,192,'26:192','Totisesti, tämä Koraani on ylhäältä, maailmojen Herralta tullut sanoma.');
INSERT INTO "finnish" VALUES(26,193,'26:193','Sen mukana on tullut uskollinen henki');
INSERT INTO "finnish" VALUES(26,194,'26:194','sydämeesi, jotta olisit yksi niistä, jotka varoittavat');
INSERT INTO "finnish" VALUES(26,195,'26:195','selvällä arabiankielellä.');
INSERT INTO "finnish" VALUES(26,196,'26:196','Totisesti, tästä on jo muinaisissa kirjoituksissa.');
INSERT INTO "finnish" VALUES(26,197,'26:197','Eikö siinä ole heille merkki, jotta Israelin kirjanoppineet sen ymmärtäisivät?');
INSERT INTO "finnish" VALUES(26,198,'26:198','Ja jos olisimme ilmaissut sen jollekulle muukalaiselle');
INSERT INTO "finnish" VALUES(26,199,'26:199','ja tämä olisi julistanut sen heille, he eivät olisi sittenkään uskoneet.');
INSERT INTO "finnish" VALUES(26,200,'26:200','Tällä tavoin Me muokkaamme syntisten sydämiä,');
INSERT INTO "finnish" VALUES(26,201,'26:201','vaikka he eivät usko ennenkuin näkevät tuskallisen rangaistuksen.');
INSERT INTO "finnish" VALUES(26,202,'26:202','Se on yllättävä heidät kaikki, kun he eivät aavistakaan.');
INSERT INTO "finnish" VALUES(26,203,'26:203','Silloin he sanovat: »Myönnetäänkö meille ollenkaan armonaikaa?»');
INSERT INTO "finnish" VALUES(26,204,'26:204','Koettavatko he siis jouduttaa Meidän tuomiotamme?');
INSERT INTO "finnish" VALUES(26,205,'26:205','Uskotko, että vaikkapa annamme heidän nauttia jonkun vuoden,');
INSERT INTO "finnish" VALUES(26,206,'26:206','ja heille senjälkeen tulee se, millä heitä on uhattu,');
INSERT INTO "finnish" VALUES(26,207,'26:207','heitä vähääkään hyödyttää se, mistä ovat nauttineet?');
INSERT INTO "finnish" VALUES(26,208,'26:208','Emme ole hävittänyt ainoatakaan kaupunkia lähettämättä sille varoittajia');
INSERT INTO "finnish" VALUES(26,209,'26:209','muistuttamaan. Katso, Me emme ole väärämielinen.');
INSERT INTO "finnish" VALUES(26,210,'26:210','Eivät pahat henget ole tuoneet sitä (Koraania) maan päälle.');
INSERT INTO "finnish" VALUES(26,211,'26:211','Se ei heitä hyödytä, eivätkä he pysty siihen.');
INSERT INTO "finnish" VALUES(26,212,'26:212','Totisesti, he eivät saata edes kuunnella sitä.');
INSERT INTO "finnish" VALUES(26,213,'26:213','Siksi älä rukoile muita jumalia Jumalan ohella, muuten kuulut niihin, joita kohtaa tuomio.');
INSERT INTO "finnish" VALUES(26,214,'26:214','Varoita myös läheisiä sukulaisiasi.');
INSERT INTO "finnish" VALUES(26,215,'26:215','Ja sulje hyvyyteesi ne uskovaiset, jotka sinua seuraavat.');
INSERT INTO "finnish" VALUES(26,216,'26:216','Jos he eivät sinua tottele, sano silloin: »Totisesti, en vastaa teidän teoistanne.»');
INSERT INTO "finnish" VALUES(26,217,'26:217','Luota kaikkivaltiaaseen, kaikkein armollisimpaan.');
INSERT INTO "finnish" VALUES(26,218,'26:218','Häneen, joka näkee sinut, kun nouset rukoilemaan,');
INSERT INTO "finnish" VALUES(26,219,'26:219','ja joka havaitsee askeleesi niiden keskuudessa, jotka kumartavat.');
INSERT INTO "finnish" VALUES(26,220,'26:220','Totisesti, Hän on kaikkikuuleva, kaikkitietävä.');
INSERT INTO "finnish" VALUES(26,221,'26:221','Kerronko teille, kenen päälle pahat henget laskeutuvat?');
INSERT INTO "finnish" VALUES(26,222,'26:222','Ne laskeutuvat jokaisen valheellisen ja syntisen päälle');
INSERT INTO "finnish" VALUES(26,223,'26:223','ja kuiskuttelevat heidän korviinsa, mutta useimmat heistä ovat pettureita,');
INSERT INTO "finnish" VALUES(26,224,'26:224','ja eksyneet seuraavat sellaisia, kuten runoilijoitakin.');
INSERT INTO "finnish" VALUES(26,225,'26:225','Etkö näe heidän harhailevan joka laaksossa, poissa järjiltään.');
INSERT INTO "finnish" VALUES(26,226,'26:226','Ja että he puhuvat sellaista, mitä eivät tee.');
INSERT INTO "finnish" VALUES(26,227,'26:227','Lukuunottamatta niitä heistä, jotka uskovat ja tekevät hyvää ja ahkerasti rukoilevat Jumalaa ja jotka puolustautuvat, kun heitä on sorrettu. Mutta ne, jotka tekevät vääryyttä, saavat pian tietää, millaiseen määränpäähän heidän on saavuttava.');
INSERT INTO "finnish" VALUES(27,1,'27:1','Taa, Siin (laupias, kuuleva Jumala). Nämä ovat Koraanin ja selvän kirjan säkeistöjä,');
INSERT INTO "finnish" VALUES(27,2,'27:2','johdatus ja ilosanoma uskovaisille,');
INSERT INTO "finnish" VALUES(27,3,'27:3','niille, jotka suorittavat rukouksensa, antavat säädetyt almut ja ovat varmoja tulevasta elämästä.');
INSERT INTO "finnish" VALUES(27,4,'27:4','Jotka eivät usko tulevaan elämään, niiden teot Me olemme tosin tehnyt otollisiksi heidän omissa silmissään, ja sokeina he kulkevat eteenpäin.');
INSERT INTO "finnish" VALUES(27,5,'27:5','He ovat niitä, jotka saavat pahan palkan ja joutuvat tulevassa elämässä suurimpaan häviöön.');
INSERT INTO "finnish" VALUES(27,6,'27:6','Totisesti, sinun tehtäväsi on ottaa vastaan Koraani viisauden ja tiedon Jumalalta.');
INSERT INTO "finnish" VALUES(27,7,'27:7','Mooses sanoi perheelleen: »Ihan varmasti näen tulen palavan. Hankin teille siitä tiedon tai sitten tuon palavan kekäleen, jotta voisitte itseänne lämmitellä.»');
INSERT INTO "finnish" VALUES(27,8,'27:8','Kun hän sitten lähestyi sitä, kuului ääni, joka sanoi: »Siunattu on Hän, joka on tulessa ja sen ympärillä. Kunnia olkoon Jumalalle, kaikkien maailmojen Herralle.');
INSERT INTO "finnish" VALUES(27,9,'27:9','Oi Mooses! Totisesti, Minä olen Jumala, kaikkivaltias, kaikkiviisas.');
INSERT INTO "finnish" VALUES(27,10,'27:10','Heitä sauvasi maahan!» Kun hän sitten näki sen kiemurtelevan kuin käärmeen, niin hän kääntyi pakoon eikä katsonut taakseen. »Oi Mooses! älä pelkää, totisesti, Minun läsnäollessani lähettiläät eivät tunne pelkoa.');
INSERT INTO "finnish" VALUES(27,11,'27:11','Ei sekään, joka on tehnyt väärin, mutta sitten tekee pahan sijasta hyvää, sillä olenhan Minä totisesti anteeksiantavainen, armollinen.');
INSERT INTO "finnish" VALUES(27,12,'27:12','Pistä kätesi poveesi, ja se muuttuu valkeaksi, ilman taudin tartuntaa, ja tämä on alku yhdeksästä, faraolle ja hänen kansalleen tarkoitetusta tunnusteosta. Totisesti, he ovat jumalaton kansa.»');
INSERT INTO "finnish" VALUES(27,13,'27:13','Kun Meidän tunnusmerkkimme täydessä selkeydessään tulivat heidän nähtäväkseen, he sanoivat: »Tämä on pelkkää noituutta.»');
INSERT INTO "finnish" VALUES(27,14,'27:14','Ja he kielsivät ne jumalattomuudessaan ja ylpeydessään, vaikka sydämessänsä olivat niistä varmoja. Ja katso, millaisen lopun pahuuden harjoittajat saivat.');
INSERT INTO "finnish" VALUES(27,15,'27:15','Totisesti, Me annoimme Daavidille ja Salomolle ymmärrystä, ja he sanoivat: »Kaikki ylistys kuuluu Jumalalle, joka on korottanut meidät yli useimpain uskollisten palvelijainsa.»');
INSERT INTO "finnish" VALUES(27,16,'27:16','Ja Salomosta tuli Daavidin seuraaja, ja hän sanoi: »Oi ihmiset, meille on opetettu lintujen kieltä ja meille on annettu kaikki, mitä tarvitsemme. Totisesti, tämä on suuri armo.»');
INSERT INTO "finnish" VALUES(27,17,'27:17','Ja hänen sotajoukkonsa, jotka käsittivät raakalaisia sekä sivistyneitä, myöskin lentäväin parvia , koottiin Salomon eteen katselmusta varten,');
INSERT INTO "finnish" VALUES(27,18,'27:18','kunnes joukot saapuivat Namlin eli Muurahaisten laaksoon, jolloin eräs niistä sanoi: »Oi Namlin heimo! Menkää asuntoihinne, etteivät Salomo ja hänen sotajoukkonsa teitä tietämättömyydessään murskaksi tallaisi.»');
INSERT INTO "finnish" VALUES(27,19,'27:19','Silloin Salomo hymyili tälle puheelle ja lausui: »Herra, suo minun kiittää Sinua minulle ja vanhemmilleni antamastasi siunauksesta, ja suo, että voisin tehdä hyvää, mikä Sinua miellyttää, ja anna minun Sinun armosi ansiosta astua hyvien palvelijoittesi pariin.»');
INSERT INTO "finnish" VALUES(27,20,'27:20','Ja hän tarkasteli lentävien parvia ja sanoi: »Mitä tämä merkitsee, en näe Hudhudia; onko hän poissaolevien joukossa?');
INSERT INTO "finnish" VALUES(27,21,'27:21','Totisesti, annan hänelle ankaran rangaistuksen taikka tapan hänet, ellei hän ilmoita minulle pätevää syytä.');
INSERT INTO "finnish" VALUES(27,22,'27:22','Mutta ei kestänyt kauankaan, kun Hudhud saapui ja sanoi: »Olen keksinyt sellaista, mitä sinä et ole huomannut, ja tuon sinulle luotettavia uutisia Saabasta.');
INSERT INTO "finnish" VALUES(27,23,'27:23','Näin totisesti naisen hallitsevan heitä, ja hän oli saanut kaikkea yltäkyllin ja hänellä oli mahtava valtaistuin.');
INSERT INTO "finnish" VALUES(27,24,'27:24','Havaitsin hänen ja hänen kansansa kunnioittavan aurinkoa Jumalan sijasta, ja saatana on tehnyt heidän tekonsa otollisiksi heidän omissa silmissään ja eksyttänyt heidät tieltä, niin että he ovat ilman johdatusta;');
INSERT INTO "finnish" VALUES(27,25,'27:25','eivätkä palvele Jumalaa, joka paljastaa kaiken salatun sekä taivaassa että maan päällä, ja joka tietää, mitä te salaatte ja mitä julki tuotte.');
INSERT INTO "finnish" VALUES(27,26,'27:26','Jumala on olemassa, ei ole muuta jumalaa kuin Hän, suuren voiman Herra.»');
INSERT INTO "finnish" VALUES(27,27,'27:27','Salomo vastasi: »Saamme pian nähdä, oletko puhunut totta, vai oletko valehtelija.');
INSERT INTO "finnish" VALUES(27,28,'27:28','Ota tämä kirjeeni ja vie se heille; mene sitten syrjään ja odota, mitä he vastaavat.»');
INSERT INTO "finnish" VALUES(27,29,'27:29','Ja Saaban kuningatar lausui: »Oi päämiehet! Totisesti, minulle on lähetetty kunnianarvoinen kirje.');
INSERT INTO "finnish" VALUES(27,30,'27:30','Se on Salomolta, ja hän kirjoittaa: ''Aloitan Jumalan, laupiaan Armahtajan nimeen!''');
INSERT INTO "finnish" VALUES(27,31,'27:31','(ja edelleen siinä on): ''älkää ylvästelkö minua vastaan, vaan alistukaa valtaani.''»');
INSERT INTO "finnish" VALUES(27,32,'27:32','Kuningatar lausui: »Päälliköt, neuvokaa minua tässä asiassa. En ratkaise ainoatakaan asiaa, ellette te ole läsnä.»');
INSERT INTO "finnish" VALUES(27,33,'27:33','He vastasivat: »Meillä on suuri mahti ja taistelukyky, mutta käskeminen kuuluu sinulle. Mieti siis, mitä aiot käskeä.»');
INSERT INTO "finnish" VALUES(27,34,'27:34','Hän sanoi: »Totisesti, kun kuninkaat ryntäävät johonkin kaupunkiin, niin he hävittävät sen raunioiksi ja alentavat sen ylhäisimmät asukkaat. Näin juuri he tekevät.');
INSERT INTO "finnish" VALUES(27,35,'27:35','Totisesti aion lähettää heille lahjan ja odotan sitten sanansaattajien tuomaa vastausta.»');
INSERT INTO "finnish" VALUES(27,36,'27:36','Kun lahja tuotiin Salomolle, hän lausui: »Lahjoitatteko minulle rikkautta? Mutta se, mitä Jumala on minulle suonut, on enemmän kuin teidän lahjanne. Niin, olette kai itse ihastuneita lahjastanne.');
INSERT INTO "finnish" VALUES(27,37,'27:37','Viekää se heille takaisin. Viemme heitä vastaan sotajoukkomme, jota he eivät voi vastustaa, ja karkoitamme heidät masennettuina ja häväistyinä.»');
INSERT INTO "finnish" VALUES(27,38,'27:38','Hän puhui edelleen: »Päällikköni! Kuka teistä voi tuoda minulle hänen valtaistuimensa, ennenkuin he alistuvat valtaani?');
INSERT INTO "finnish" VALUES(27,39,'27:39','Eräs väkevä dzinni sanoi: »Tuon sen sinulle ennen kuin nouset paikaltasi; totisesti, minä olen väkevä ja luotettava tähän tehtävään.»');
INSERT INTO "finnish" VALUES(27,40,'27:40','Eräs toinen, joka tunsi (pyhää) Kirjaa, lausui: »Tuon sen sinulle ennen kuin ehdit silmääsi räpäyttää.» Ja kun Salomo näki valtaistuimen edessään, hän lausui: »Tämä tapahtuu Herran armosta; Hän koettelee minua, osaanko olla kiitollinen vai olenko kiittämätön. Ken kiittää, hän tekee sen omaksi parhaakseen, ja jos joku on kiittämätön, niin Herrani on joka tapauksessa suopea, armollinen.»');
INSERT INTO "finnish" VALUES(27,41,'27:41','Hän puhui edelleen: »Muuttakaa hänen valtaistuimensa ulkonäkö hänelle oudoksi, niin saamme nähdä, onko hän oikealla tiellä vai ilman johdatusta.»');
INSERT INTO "finnish" VALUES(27,42,'27:42','Kun kuningatar saapui, niin hänelle sanottiin: »Oliko valtaistuimesi tällainen?» Hän vastasi: »Näyttää siltä, kuin se olisi sama. Meille on annettu totista tietoa jo tätä ennen ja me olemme Jumalalle alamaisia.»');
INSERT INTO "finnish" VALUES(27,43,'27:43','Mutta se, mitä hän palveli Jumalan sijasta, vei häntä harhaan. Totisesti, hän kuului epäuskoiseen kansaan.');
INSERT INTO "finnish" VALUES(27,44,'27:44','Hänelle sanottiin: »Käy sisälle palatsiin!» Kun hän sen näki, hän luuli sen vesialtaaksi ja riisui sen vuoksi jalastaan. Salomo lausui: »Se on vain palatsi, jonka lattia on tehty lasista.» Kuningatar lausui: »Oi Herra, totisesti, olen tehnyt väärin itseäni kohtaan, mutta Salomon kanssa tahdon totella Jumalaa, kaikkien maailmojen Herraa.»');
INSERT INTO "finnish" VALUES(27,45,'27:45','Totisesti, Me lähetimme Tamuudin heimon luokse heidän veljensä Saalihin, joka lausui: »Palvelkaa Jumalaa!» Mutta katso! He jakautuivat kahdeksi riiteleväksi puolueeksi.');
INSERT INTO "finnish" VALUES(27,46,'27:46','Hän sanoi: »Kansalaiseni, miksi haluatte edistää pahaa enemmän kuin hyvää, miksi ette ano Jumalan anteeksiantoa, jotta teille osoitettaisiin armo?»');
INSERT INTO "finnish" VALUES(27,47,'27:47','He vastasivat: »Me pidämme sinua ja seuralaisiasi enteenä meitä kohtaavasta onnettomuudesta.» Hän sanoi: »Teidän kohtalonne on Jumalan huomassa. Mutta te olette kansa, joka on vitsauksen kokeva.»');
INSERT INTO "finnish" VALUES(27,48,'27:48','Ja kaupungissa oli yhdeksän miehen joukkio, joka ympäri maata teki pahaa, eikä lainkaan hyvää.');
INSERT INTO "finnish" VALUES(27,49,'27:49','He sanoivat: »Vannokaamme keskenämme Jumalan nimeen, että me varmasti yöllä käymme hänen ja hänen perheensä kimppuun ja sanomme sitten hänen kostonsa perijälle: ''Emme ole nähneet hänen perheensä surmaamista, puhumme täyttä totta''.»');
INSERT INTO "finnish" VALUES(27,50,'27:50','Ja niin he punoivat juonensa, mutta Me teimme oman suunnitelmamme, josta he eivät tienneet.');
INSERT INTO "finnish" VALUES(27,51,'27:51','Ja katso, mikä oli heidän juoniensa loppu! Me hävitimme heidät ja heidän kansansa kaikki.');
INSERT INTO "finnish" VALUES(27,52,'27:52','Tuossa ovat heidän kotinsa autioina, koska he olivat jumalattomia. Totisesti, tässä on merkki niille, joilla on ymmärrystä.');
INSERT INTO "finnish" VALUES(27,53,'27:53','Ja me pelastimme ne, jotka uskoivat ja hartautta harjoittivat.');
INSERT INTO "finnish" VALUES(27,54,'27:54','Muista Lootia, joka sanoi kansalleen: »Harjoitatteko riettautta, vaikka tiedätte sen synniksi?');
INSERT INTO "finnish" VALUES(27,55,'27:55','Lähestyttekö himoiten miehiä naisten sijasta? Niin, te olette pimeätä väkeä.» ,');
INSERT INTO "finnish" VALUES(27,56,'27:56','Ja mikä olikaan hänen kansansa vastaus: »Ajakaa Lootin perhe pois kaupungista, sillä he luulevat olevansa synnittömiä.»');
INSERT INTO "finnish" VALUES(27,57,'27:57','Sitten Me pelastamme hänet perheinensä paitsi hänen vaimonsa. Tämän määräsimme jälkeenjäävien joukkoon.');
INSERT INTO "finnish" VALUES(27,58,'27:58','Ja Me annoimme sataa heidän päällensä; kauhistuttava oli se sade niille, joita oli varoitettu.');
INSERT INTO "finnish" VALUES(27,59,'27:59','Sano: »Kaikki ylistys kuuluu Jumalalle, ja rauha olkoon Hänen palvelijoillensa, jotka hän on valinnut. Onko Jumala parempi vai se, jota he pitävät hänen vertaisenaan?»');
INSERT INTO "finnish" VALUES(27,60,'27:60','Eikö parempi ole se, joka loi taivaat ja maan ja lähetti pilvistä alas sateen, jolla Me kasvatamme ihania puutarhoja? Ei ole teidän vallassanne saada niiden puita kasvamaan. Onko muuta jumal''olentoa kuin Jumala? Ei, he vain kuvittelevat sellaisia.');
INSERT INTO "finnish" VALUES(27,61,'27:61','Eikö parempi ole se, joka loi maan asuinpaikaksi ja teki siihen virtain uomat, kohotti vuoret ja erotti makean ja suolaisen veden? Onko muuta jumal''olentoa kuin Jumala? Ei, mutta useimmat eivät mitään tiedä.');
INSERT INTO "finnish" VALUES(27,62,'27:62','Eikö parempi ole se, joka vastaa ahdistuksessa olevalle, kun tämä rukoilee häntä, ja joka poistaa hänen vaivansa ja tekee teistä maan perijöitä? Onko muuta jumal''olentoa kuin Jumala? Vähäistä on se, mitä mielessänne pysyy.');
INSERT INTO "finnish" VALUES(27,63,'27:63','Eikö parempi ole se, joka ohjaa teitä pimeydessä sekä maalla että merellä? Kuka lähettää tuulet tuomaan ilosanomaa Hänen saapuvasta armostaan? Onko muuta jumal''olentoa kuin Jumala? Jumala on korkealla sen yläpuolella, mitä he Hänen vertaisenaan pitävät.');
INSERT INTO "finnish" VALUES(27,64,'27:64','Eikö parempi ole se, joka loi elävät olennot ja sitten palauttaa kaiken entiselleen? Kuka antaa teille ravintoa sekä taivaasta että maasta? Onko muuta jumal''olentoa kuin Jumala? Sano: »Tuokaa esiin todisteenne, jos olette vilpittömiä.»');
INSERT INTO "finnish" VALUES(27,65,'27:65','Sano edelleen: »Ei kukaan taivaassa tai maan päällä, paitsi Jumala, tiedä näkymättömiä. Eivätkä he tiedä ylösnousemuksensa hetkeä.');
INSERT INTO "finnish" VALUES(27,66,'27:66','Ei, heillä ei ole niukkaakaan tietoa tulevasta elämästä. Joko he sitä epäilevät tai ovat kokonaan pimitettyjä.');
INSERT INTO "finnish" VALUES(27,67,'27:67','Uskottomat sanovat: »Kun me ja esi-isämme muutumme tomuksi, herätetäänkö meidät todellakin senjälkeen uudelleen eloon?');
INSERT INTO "finnish" VALUES(27,68,'27:68','Näin meille on ennenkin luvattu, sekä myös esi-isillemme, mutta nämä ovat vain muinaistaruja.»');
INSERT INTO "finnish" VALUES(27,69,'27:69','Sano: »Kiertäkää maata ja katsokaa, millaisen lopun syntiset saivat!»');
INSERT INTO "finnish" VALUES(27,70,'27:70','älä ole murheessa heidän tähtensä äläkä ahdistuksessa heidän juoniensa vuoksi.');
INSERT INTO "finnish" VALUES(27,71,'27:71','He sanovat myös: »Milloin tämä uhkaus täytetään, jos kerran puhut totta?»');
INSERT INTO "finnish" VALUES(27,72,'27:72','Vastaa: »Voi sattua, että jokin niistä asioista, joiden tuloa koetatte jouduttaa, seuraa teitä jo kannoillanne.»');
INSERT INTO "finnish" VALUES(27,73,'27:73','Totisesti, Herrasi on armon Herra ihmisille, mutta monetkaan heistä eivät ole kiitollisia.');
INSERT INTO "finnish" VALUES(27,74,'27:74','Totisesti, Herrasi tietää, mitä heidän sydämensä kätkevät ja mitä ilmaisevat.');
INSERT INTO "finnish" VALUES(27,75,'27:75','Taivaassa ja maassa ei ole mitään niin salattua, ettei se löytyisi selkeästä Kirjasta.');
INSERT INTO "finnish" VALUES(27,76,'27:76','Totisesti, tämä Koraani ilmoittaa israelilaisille suurimman osan siitä, mistä he ovat eri mieltä.');
INSERT INTO "finnish" VALUES(27,77,'27:77','Totisesti, se on myös johdatuksena ja armona uskovaisille.');
INSERT INTO "finnish" VALUES(27,78,'27:78','Tuomiollaan Jumala totisesti jakaa heille oikeutta; onhan Hän kaikkivaltias, kaikkitietävä.');
INSERT INTO "finnish" VALUES(27,79,'27:79','Luota siis Jumalaan; silloin olet varman totuuden perustalla.');
INSERT INTO "finnish" VALUES(27,80,'27:80','Totisesti, sinä et voi saada kuolleita kuulemaan, etkä kuuroja äänesi kantamiin, jos he ovat menossa poispäin.');
INSERT INTO "finnish" VALUES(27,81,'27:81','Et voi liioin taluttaa sokeita pois heidän harhastaan. Vain ne saat kuulemaan, jotka uskovat merkkeihimme ja ovat tottelevaisia.');
INSERT INTO "finnish" VALUES(27,82,'27:82','Ja kun uhkauksemme käy heille toteen, Me annamme maasta kasvaa ituja, jotka tautina heihin tarttuvat, koska he eivät ottaneet uskoakseen merkkejämme.');
INSERT INTO "finnish" VALUES(27,83,'27:83','Ja sinä päivänä Me kokoamme yhteen jokaisesta kansasta joukon niitä, jotka kielsivät merkkimme, ja järjestämme heidät riveihin.');
INSERT INTO "finnish" VALUES(27,84,'27:84','Kun he saapuvat Herransa eteen, Hän on lausuva: »Hylkäsittekö Minun tunnusmerkkini, vaikkei niihin ymmärryksenne riittänyt, vai mitä te teitte?»');
INSERT INTO "finnish" VALUES(27,85,'27:85','Ja tuomiomme luetaan heille, koska he ovat syntiä tehneet, eivätkä he saata lausua sanaakaan.');
INSERT INTO "finnish" VALUES(27,86,'27:86','Eivätkö he huomanneet, että olimme säätänyt heille yön lepoa varten ja päivän valaisemaan? Totisesti, nämä ovat merkkejä niille, jotka uskovat.');
INSERT INTO "finnish" VALUES(27,87,'27:87','Ja sinä päivänä puhalletaan pasuunoihin, jolloin kaikki taivaassa ja maan päällä olevat, paitsi ne, joilla on Jumalan mielisuosio, joutuvat kauhun valtaan ja nöyrinä astuvat Hänen eteensä.');
INSERT INTO "finnish" VALUES(27,88,'27:88','Sinä näet myös vuoret ja luulet niiden olevan lujasti paikoillaan, mutta ne häipyvät pois kuin vaeltavat pilvet; niin on ne Jumala asettanut, Hän, joka kaikelle on antanut täydellisen muodon. Totisesti, Hän tietää, mitä te teette.');
INSERT INTO "finnish" VALUES(27,89,'27:89','Joka tuo esiin hyvää, on saava itselleen sitäkin parempaa, ja sellaiset ovat turvassa tuon päivän kauhuilta.');
INSERT INTO "finnish" VALUES(27,90,'27:90','Mutta ken tekee pahaa, heitetään kasvoillensa tuleen. Olisiko teidän palkkanne muu kuin tekojenne mukainen?');
INSERT INTO "finnish" VALUES(27,91,'27:91','Sano: »Totta on, että minua on käsketty palvelemaan tämän kaupungin Herraa, jolle se on pyhitetty ,ja jolle kaikki kuuluu; minut on myös määrätty olemaan muslimi,');
INSERT INTO "finnish" VALUES(27,92,'27:92','sekä julistamaan Koraania. Ken on siis saanut johdatuksen, hän on saanut sen oman sielunsa parhaaksi. Mutta eksyneille sano: »Olen vain yksi varoittajista.»');
INSERT INTO "finnish" VALUES(27,93,'27:93','Sano edelleen: »Kaikki ylistys kuuluu Jumalalle. Olette pian näkevät Hänen merkkinsä ja tulette ne silloin tuntemaan.» Herrasi ei ole välinpitämätön siitä, mitä teette.');
INSERT INTO "finnish" VALUES(28,1,'28:1','Taa, Siin, Miim. (Laupias, kuuleva, tietävä Jumala).');
INSERT INTO "finnish" VALUES(28,2,'28:2','Nämä ovat selvän Kirjan säkeistöjä.');
INSERT INTO "finnish" VALUES(28,3,'28:3','Aiomme kertoa sinulle totuudenmukaisesti osan Mooseksen ja faraon elämästä hyödyksi niille, jotka uskovat.');
INSERT INTO "finnish" VALUES(28,4,'28:4','Totisesti, farao oli maassaan mahtava. Hän jakoi sen asukkaat luokkiin ja koetti heikentää erästä osaa heistä tappamalla miehet ja jättämällä vain naiset eloon. Hän oli todella vääryydentekijä.');
INSERT INTO "finnish" VALUES(28,5,'28:5','Mutta Me tahdoimme osoittaa suosiotamme niille, joita maassa sorrettiin. Tahdoimme tehdä heidät maan johtomiehiksi ja faraon perillisiksi.');
INSERT INTO "finnish" VALUES(28,6,'28:6','Halusimme antaa heille valtaa maassa ja saada faraon ja Haamanin sekä heidän sotajoukkonsa kokemaan sitä, mitä he olivat pelänneet.');
INSERT INTO "finnish" VALUES(28,7,'28:7','Niin Me ilmestyimme Mooseksen äidille näin sanoen: »Imetä häntä, mutta kun alat pelätä hänen puolestaan, niin aseta hänet veteen äläkä pelkää, äläkä murehdi. Totisesti, Me annamme hänet sinulle takaisin ja teemme hänestä lähettilään.»');
INSERT INTO "finnish" VALUES(28,8,'28:8','Sitten faraon väki otti hänet vedestä, ja hänestä oli tuleva heille vihamies ja murheen aihe. Totisesti, farao, Haaman ja heidän sotajoukkonsa olivat synnintekijöitä.');
INSERT INTO "finnish" VALUES(28,9,'28:9','Ja faraon puoliso lausui: »Hän ilahduttaa minun silmäni,ja sinunkin. Älä surmaa häntä, ehkäpä hän voi olla meille hyödyksi tai voimme ottaa hänet pojaksemme.» Mutta he eivät asiaa arvanneet.');
INSERT INTO "finnish" VALUES(28,10,'28:10','Ja seuraavana aamuna Mooseksen äidin sydän oli pakahtumaisillaan. Hän olisi paljastanut salaisuutensa, ellemme Me olisi vahvistanut hänen sydäntään, niin että hän saattoi pysyä uskossa.');
INSERT INTO "finnish" VALUES(28,11,'28:11','Ja hän lausui Mooseksen sisarelle: »Seuraa häntä!» Ja tämä piti syrjästä veljeänsä silmällä heidän huomaamattaan.');
INSERT INTO "finnish" VALUES(28,12,'28:12','Me olimme jo tehnyt hänelle vastenmielisiksi vieraat imettäjät. Silloin sisar lausui: »Näytänkö teille erään perheen, joka on hoivaava häntä ja tarkoittaa hänelle hyvää?»');
INSERT INTO "finnish" VALUES(28,13,'28:13','Siten Me annoimme hänet takaisin äidilleen, jotta tämän silmät kirkastuisivat eikä hän enää murehtisi, vaan tietäisi, että Jumalan lupaus oli tosi. Mutta useimmat ihmisistä ovat tietoa vailla.');
INSERT INTO "finnish" VALUES(28,14,'28:14','Ja kun Mooses oli päässyt miehuuden ikään ja kasvanut voimakkaaksi, Me annoimme hänelle viisautta ja tietoa. Siten me palkitsemme ne, jotka hyvää tekevät.');
INSERT INTO "finnish" VALUES(28,15,'28:15','Ja hän astui kaupunkiin hetkenä, jolloin sen asukkaat eivät sitä vartioineet, ja havaitsi siellä kahden miehen tappelevan keskenänsä; toinen kuului hänen omaan heimoonsa, toinen hänen vihamiehiinsä. Silloin hänen heimolaisensa pyysi häntä avukseen toista vastaan, joka kuului hänen vihollisiinsa. Ja Mooses iski tämän nyrkillään hengiltä. Silloin hän sanoi: »Tämän sai saatana aikaan. Totisesti, hän on vihamies ja ilmeinen viettelijä.»');
INSERT INTO "finnish" VALUES(28,16,'28:16','Hän puhui edelleen: »Herra! Olen tehnyt syntiä itseäni kohtaan, suo se minulle anteeksi!» Ja hän sai anteeksi. Totisesti, Hän antaa kaiken anteeksi. Hän on anteeksiantava, armollinen.');
INSERT INTO "finnish" VALUES(28,17,'28:17','Mooses sanoi: »Herra, koska olet armahtanut minua, en koskaan enää auta syntisiä.»');
INSERT INTO "finnish" VALUES(28,18,'28:18','Seuraavana aamuna hän kulki kaupungissa pelokkaasti ympärillensä tähyillen, ja katso, se, joka oli häntä edellisenä päivänä avukseen huutanut, huusi taaskin häntä auttajakseen. Mooses nuhteli häntä: »Totisesti, sinä olet ilmeisesti väärässä.»');
INSERT INTO "finnish" VALUES(28,19,'28:19','Ja kun hän aikoi taltuttaa sitä, joka oli heidän kummankin vihollinen, niin tämä sanoi: »Tahdotko tappaa minut, Mooses, kuten tapoit eilisen miehen? Haluat väkivalloin maata hallita, etkä kuulua niihin, jotka oikeutta tekevät.»');
INSERT INTO "finnish" VALUES(28,20,'28:20','Silloin saapui kaupungin etäisimmältä laidalta mies juosten, ja hän sanoi: »Mooses! Päämiehet pitävät neuvoa sinut tappaakseen. Lähde pois täältä; totisesti, minä toivon sinulle hyvää.»');
INSERT INTO "finnish" VALUES(28,21,'28:21','Silloin hän lähti pois kaupungista pelon vallassa, ympärillensä tähyillen. Hän huudahti: »Herra, pelasta minut väärämielisten ihmisten keskuudesta!»');
INSERT INTO "finnish" VALUES(28,22,'28:22','Ja kääntäessään kasvonsa Midianin suuntaan hän sanoi: »Ehkä Herra ohjaa minut oikealle tielle.»');
INSERT INTO "finnish" VALUES(28,23,'28:23','Kun hän saapui Midianin kaivon luo, hän tapasi useita miehiä juottamassa elukoitaan. Paitsi heitä hän huomasi kaksi naista, jotka pidättelivät laumaansa. Hän kysyi: »Mitä te kaksi oikein teette?» He vastasivat: »Emme voi juottaa elukoitamme, ennenkuin paimenet poistuvat; isämme on vanha.»');
INSERT INTO "finnish" VALUES(28,24,'28:24','Silloin hän juotti heidän elukkansa heidän puolestaan ja vetäytyi sitten takaisin varjoon ja sanoi: »Herra, olen totisesti kaiken hyvän tarpeessa, minkä minulle mahdat suoda.»');
INSERT INTO "finnish" VALUES(28,25,'28:25','Silloin toinen naisista tuli kainosti kävellen hänen luokseen ja sanoi: »Totisesti, isäni kutsuu sinut luokseen voidakseen kiittää sinua siitä, että juotit elukkamme.» Kun Mooses saapui hänen luoksensa ja kertoi hänelle elämänsä, niin vanhus sanoi: »Älä pelkää, olet pelastunut väärämielisten ihmisten joukosta.»');
INSERT INTO "finnish" VALUES(28,26,'28:26','Toinen tyttäristä pyysi: »Isä! Ota hänet palvelukseesi, totisesti, hän on paras, jonka voit palkata, sangen väkevä ja luotettavaa');
INSERT INTO "finnish" VALUES(28,27,'28:27','Isä lausui: »Totisesti, haluan naittaa sinut toiselle tyttäristäni sillä ehdolla, että olet kahdeksan vuotta palveluksessani. Oletko kymmenen vuotta täyteen, se riippuu itsestäsi, enkä aio kohdella sinua huonosti, olet huomaava minut, jos Jumala niin suo, hyväksi ihmiseksi.»');
INSERT INTO "finnish" VALUES(28,28,'28:28','Mooses vastasi: »Tämä olkoon sopimuksemme, ja minkä ajan otankin täyttääkseni, älköön minulta enää lisää vaadittako. Jumala todistakoon sen, mistä me nyt puhumme.»');
INSERT INTO "finnish" VALUES(28,29,'28:29','Kun Mooses sitten oli palvellut aikansa täyteen ja lähti pois perheinensä, hän huomasi tulen vuoren kupeella. Hän lausui perheelleen: »Malttakaa, minä näen tulen palavan. Ehkä minä voin ottaa teille selvää siitä tai tuoda palavan kekäleen, voidaksenne lämmitellä.»');
INSERT INTO "finnish" VALUES(28,30,'28:30','Kun hän lähestyi sitä, kuului ääni laakson oikealta puolelta, pyhänä paikalla olevasta pensaasta: »Mooses! Minä olen totisesti Jumala, maailmojen valtias.»');
INSERT INTO "finnish" VALUES(28,31,'28:31','Ja edelleen: »Heitä sauvasi maahan!» Ja kun hän näki sen kiemurtelevan kuin käärme, hän kääntyi ympäri paeten taakseen katsomatta. »Oi Mooses, astu eteenpäin pelkäämättä, sillä totisesti olet turvassa.');
INSERT INTO "finnish" VALUES(28,32,'28:32','Pistä kätesi poveesi, niin vedät sen ulos valkoisena, kuitenkin ilman taudin tartuntaa. Äläkä kammoksu kättäsi kylkesi lähellä. Nämä ovat Herrasi kaksi tunnusmerkkiä faraolle ja hänen päämiehilleen. Totisesti he ovat jumalattomia ihmisiä.»');
INSERT INTO "finnish" VALUES(28,33,'28:33','Mooses vastasi: »Herra! Minä surmasin erään heistä ja pelkään senvuoksi, että he surmaavat minut.');
INSERT INTO "finnish" VALUES(28,34,'28:34','Mutta minulla on veljeni Aaron, joka on puheessaan minua taitavampi, lähetä sentähden hänet minun mukaani minua vahvistamaan. Pelkään totisesti, että he pitävät minua valehtelijana.»');
INSERT INTO "finnish" VALUES(28,35,'28:35','Jumala lausui: »Me vahvistamme käsivarttasi veljesi avulla ja annamme teille kummallekin sellaisen vallan, että he eivät kykene mihinkään Meidän tunnustekojamme vastaan. Te kaksi sekä ne, jotka teitä seuraavat, peritte voiton.»');
INSERT INTO "finnish" VALUES(28,36,'28:36','Kun Mooses sitten selkeästi näytti heille tunnusmerkkimme, he sanoivat: »Tämä on vain ovelaa noituutta, emmekä ole kuulleet tällaista sattuneen esi~isiemme aikana.»');
INSERT INTO "finnish" VALUES(28,37,'28:37','Mutta Mooses sanoi: »Herra tietää parhaiten, kuka tulee Hänen lähettämänään ja opastamanaan ja kenellä on määränpäässä hyvä koti. Totisesti, väärämieliset eivät menesty.»');
INSERT INTO "finnish" VALUES(28,38,'28:38','Farao lausui: »Päällikköni, en tiedä teillä olevan muuta jumalaa kuin minä itse. Polta siis, Haaman, minulle tiiliä ja rakenna korkea palatsi, jotta voisin ulottua näkemään Mooseksen Jumalan. Pidän häntä totisesti valehtelijana.»');
INSERT INTO "finnish" VALUES(28,39,'28:39','Ja farao ja hänen sotajoukkonsa ylvästelivät aiheetta maassa ja luulivat, ettei heitä noudettaisi takaisin Meidän eteemme.');
INSERT INTO "finnish" VALUES(28,40,'28:40','Silloin Me tartuimme häneen ja hänen sotajoukkoonsa ja heitimme heidät mereen. Huomaa siis, millaisen lopun saavat jumalattomat.');
INSERT INTO "finnish" VALUES(28,41,'28:41','Ja Me teimme heistä johtomiehiä viettelemään ihmisiä tulen omiksi, ja ylösnousemuksen päivänä heitä ei auteta.');
INSERT INTO "finnish" VALUES(28,42,'28:42','Tässä maailmassa me annoimme kirouksen seurata heitä, ja ylösnousemuksen päivänä he ovat inhottuja.');
INSERT INTO "finnish" VALUES(28,43,'28:43','Totisesti, Me annoimme Moosekselle Kirjan, senjälkeen kun olimme hävittänyt aikaisemmat sukupolvet, valaistukseksi ihmissuvulle, johdatukseksi ja armoksi, jotta he siitä vaarin ottaisivat.');
INSERT INTO "finnish" VALUES(28,44,'28:44','Sinä (Muhammed) et ollut Siinain länsipuolella, kun Me ilmoitimme Moosekselle käskymme, etkä ollut sen todistajana.');
INSERT INTO "finnish" VALUES(28,45,'28:45','Mutta olemme luonut toisia sukupolvia ja suonut niille pitkän elämän. Sinä et myöskään asunut Midianin asukkaitten parissa julistamassa heille Meidän ilmoituksiamme, mutta Meistä ne olivat lähtöisin.');
INSERT INTO "finnish" VALUES(28,46,'28:46','Sinä et ollut myöskään vuoren huipulla, kun Me (Mooseksen) kutsuimme, mutta tämä (Koraani) on Herrasi armonosoitus, jotta voisit varoittaa kansaa, jonka luona ei ketään varoittajaa ennen sinua käynyt; ehkä he ottavat vaarin.');
INSERT INTO "finnish" VALUES(28,47,'28:47','Etteivät he sitten, kun heitä kohtaa onnettomuus heidän kättensä tekojen tähden, sanoisi: »Herra, miksi et lähettänyt luoksemme lähettilästä, niin että olisimme seuranneet merkkejäsi ja olleet uskovaisia?»');
INSERT INTO "finnish" VALUES(28,48,'28:48','Mutta kun lähettämämme totuus nyt ilmestyy heille, he sanovat: »Miksi hän (Muhammed) ei saanut samanlaista ihmeidentekovaltaa kuin Mooseskin? Mutta eivätkö he kieltäneet sitä, mitä Moosekselle aikoinaan annettiin?» He sanoivat: »Nämä kumpikin (Koraani ja Toora) ovat noituuden tuotteita, jotka tukevat toisiaan. Totisesti, emme usko kumpaankaan.»');
INSERT INTO "finnish" VALUES(28,49,'28:49','Sano: »Tuokaa sitten Jumalalta sellainen kirja, joka on näitä kahta parempi ojennusnuora, niin minä seuraan sitä, jos olette oikeassa.»');
INSERT INTO "finnish" VALUES(28,50,'28:50','Mutta jos he eivät vastaa sinulle, tiedä silloin, että he seuraavat vain omia himojaan. Ja ken onkaan pahemmin eksynyt kuin se, joka seuraa omia himojaan saamatta johdatusta Jumalalta. Totisesti, Jumala ei ohjaa väärämielisiä.');
INSERT INTO "finnish" VALUES(28,51,'28:51','Totisesti, olemme jo ennenkin julistanut heille (Koraanin) sanaa moninaisissa yhteyksissä, jotta he ottaisivat siitä vaarin.');
INSERT INTO "finnish" VALUES(28,52,'28:52','Ne, joille Kirja on jo aikaisemmin annettu, uskovat siihen.');
INSERT INTO "finnish" VALUES(28,53,'28:53','Kun sitä heille luetaan, he sanovat: »Me uskomme siihen. Totisesti, se on Herramme lähettämä totuus, ja totisesti me olimme jo tätä ennen Jumalan alamaisia.»');
INSERT INTO "finnish" VALUES(28,54,'28:54','Heidän palkkansa maksetaan heille kahdesti, koska he pysyvät lujina ja vastustavat pahaa hyvällä ja antavat almuja siitä, mitä olemme heille suonut.');
INSERT INTO "finnish" VALUES(28,55,'28:55','Ja kun he kuulevat turhaa puhetta, he kääntyvät siitä pois ja sanovat: »Te niitätte omat tekonne ja me omamme; rauha olkoon teille, mutta me emme pyri tietämättömien seuraan.»');
INSERT INTO "finnish" VALUES(28,56,'28:56','Totisesti, sinä et voi johtaa sitä, jota mielelläsi johtaisit, mutta Jumala antaa johdatuksensa kenelle tahtoo, ja Hän tietää parhaiten, ketkä vaeltavat oikeata tietä.');
INSERT INTO "finnish" VALUES(28,57,'28:57','Ja he sanovat: »Jos sinun kanssasi seuraamme tätä johdatusta, niin me joudumme pois maastamme.» Aivan niin, mutta emmekö Me ole perustanut heitä varten turvallista pyhitettyä paikkaa (Mekkaa), jonne annamme kaikenlaisia hedelmiä heidän ylläpidokseen. Mutta useimmat heistä eivät mitään tiedä.');
INSERT INTO "finnish" VALUES(28,58,'28:58','Kuinka monta kaupunkia, jotka rikkaudessaan ylvästelivät, olemmekaan tuhonnut. Nämä rauniot olivat heidän kotejaan, joita heidän jälkeensä vain vähän on käytetty asuntoina. Ne olemme Me perinyt.');
INSERT INTO "finnish" VALUES(28,59,'28:59','Mutta Herrasi ei koskaan hävittänyt kaupunkeja ennen kuin oli lähettänyt niiden pääpaikkaan lähettilään tulkitsemaan heille Meidän merkkimme. Emmekä Me hävitä kaupunkeja muutoin kuin niiden asukkaitten vääryyksien takia.');
INSERT INTO "finnish" VALUES(28,60,'28:60','Kaikki, mitä teille on annettu, on vain tämän maailman elämän nautintoa ja koreutta. Mutta se, mikä on Jumalan luona, on parempaa ja pysyvämpää. Ettekö jo ymmärrä?');
INSERT INTO "finnish" VALUES(28,61,'28:61','Onko sitten se, jolle Me olemme antanut ihanan lupauksen, mikä hänelle täytetäänkin, sellaisen kaltainen, jolle olemme kylläkin suonut tämän maailman elämän nautintoja, mutta joka ylösnousemuksen päivänä luetaan syntisten joukkoon?');
INSERT INTO "finnish" VALUES(28,62,'28:62','Sinä päivänä Hän kutsuu heitä ja sanoo: »Missä ovat ne, joita te kuvittelitte Minun vertaisikseni?»');
INSERT INTO "finnish" VALUES(28,63,'28:63','Silloin ne, joihin tämä puhe sattuu, sanovat: »Herra, nämä ovat niitä, jotka olemme johtaneet harhaan samalla tavalla kuin itsekin eksyimme. Olemme syyttömiä Sinun edessäsi, he eivät meitä palvelleet.»');
INSERT INTO "finnish" VALUES(28,64,'28:64','Silloin sanotaan: »Kutsukaa liittolaisianne!» Niin he kutsuvat niitä, mutta nämä eivät lainkaan vastaa. He näkevät tuomionsa ja toivovat, että olisivat saaneet johdatuksen.');
INSERT INTO "finnish" VALUES(28,65,'28:65','Tulee päivä, jolloin Hän on heitä kutsuva ja sanova: »Minkä vastauksen annoitte lähettiläille?»');
INSERT INTO "finnish" VALUES(28,66,'28:66','Sinä päivänä he joutuvat sokeuden valtaan eivätkä kykene mitään verukkeita esittämään, eivätkä he myöskään joutavia toisiltaan utele.');
INSERT INTO "finnish" VALUES(28,67,'28:67','Mutta ken kääntyy Jumalan puoleen ja uskoo sekä tekee hyvää, hän toivottavasti kuuluu niiden pariin, jotka menestyvät.');
INSERT INTO "finnish" VALUES(28,68,'28:68','Herrasi voi luoda ja valita mitä haluaa, mutta heillä ei ole valinnan varaa. Kunnia olkoon Jumalalle! Hän on korkealla sen yläpuolella, mitä he Hänen rinnalleen asettavat.');
INSERT INTO "finnish" VALUES(28,69,'28:69','Herrasi tietää myös, mitä heidän povessaan piilee ja mitä he julki tuovat.');
INSERT INTO "finnish" VALUES(28,70,'28:70','Hän on Jumala, ei ole muuta jumalaa kuin Hän. Hänelle kuuluu kaikki ylistys alusta loppuun. Hänelle kuuluu tuomio, ja Hänen luokseen teidän on palattava.');
INSERT INTO "finnish" VALUES(28,71,'28:71','Sano: »Mitä sanoisitte, jos Jumala antaisi teidän olla yön pimeydessä aina ylösnousemuksen päivään saakka? Mikä muu jumala kuin Hän itse voisi antaa teille valoa? Miksi ette siis kuuntele?»');
INSERT INTO "finnish" VALUES(28,72,'28:72','Sano edelleen: »Mitä sanoisitte, jos Jumala antaisi teidän olla päivän valossa aina ylösnousemuksen päivään saakka? Mikä muu jumala kuin Hän voisi antaa teille yön sen aikana levätäksenne? Ettekö jo huomaa?»');
INSERT INTO "finnish" VALUES(28,73,'28:73','Suopeudessaan Hän on säätänyt teitä varten yön ja päivän, niin että voitte levätä sekä etsiä Jumalan armoa ja että osaatte olla kiitollisia.');
INSERT INTO "finnish" VALUES(28,74,'28:74','Tulee päivä, jolloin Hän on heitä kutsuva ja sanova: »Missä ovat ne, joita te kuvittelette Minun vertaisikseni?»');
INSERT INTO "finnish" VALUES(28,75,'28:75','Ja me otamme jokaisesta kansasta todistajan ja lausumme: »Tuokaa esiin todistuksenne.» Silloin he tulevat huomaamaan, että totuus on Jumalassa ja että heidän kuvitelmansa häipyvät heidän näkyvistään.');
INSERT INTO "finnish" VALUES(28,76,'28:76','Totisesti. Koorah kuului Mooseksen kansaan, mutta hän nousi heitä vastaan. Ja Me olimme antanut hänelle niin paljon aarteita, että niiden avaimet olisivat painaneet maahan joukon voimakkaita miehiä. Hänen kansansa lausui hänelle: »Älä riemuitse, totisesti, Jumala ei rakasta niitä, jotka liiaksi rikkaudestaan riemuitsevat.');
INSERT INTO "finnish" VALUES(28,77,'28:77','Vaan sen avulla, mitä Jumala on sinulle antanut, etsi tulevaisuuden kotia, mutta älä unohda osaasi tässä maailmassa. Tee hyvää, kuten Jumalakin on hyvää tehnyt sinulle, äläkä tavoittele maassa pahaa. Totisesti, Jumala ei rakasta niitä, jotka pahaa tekevät.»');
INSERT INTO "finnish" VALUES(28,78,'28:78','Hän sanoi: »Minulle on annettu tämä kaikki sen tiedon vuoksi, joka minulla on.» Eikö hän tiedä, että jo ennen häntä Jumala on tuhonnut sukupolvia, jotka olivat häntä voimassaan väkevämpiä ja määrältään lukuisampia. Mutta syntisiltä ei tarvitse kysellä heidän syntejään.');
INSERT INTO "finnish" VALUES(28,79,'28:79','Sitten hän astui kansansa eteen kaikessa korskeudessaan; silloin ne, jotka halusivat tämän maailman elämää, sanoivat: »Me halajamme samanlaista, mitä Koorahillekin on annettu. Totisesti, hän on saanut suuren onnen.»');
INSERT INTO "finnish" VALUES(28,80,'28:80','Mutta ne, joille oli annettu totista tietoa, sanoivat: »Voi teitä! Jumala antaa paremman palkan sille, joka uskoo ja tekee hyvää. Mutta sen saavat vain ne, jotka lujina kestävät.»');
INSERT INTO "finnish" VALUES(28,81,'28:81','Sitten Me annoimme maan niellä hänet ja hänen kotinsa, eikä kukaan, paitsi Jumala, voinut häntä auttaa, eikä hän apua saanut.');
INSERT INTO "finnish" VALUES(28,82,'28:82','Ja ne, jotka aikaisemmin olivat halunneet hänen asemaansa, alkoivat puhua: »Voi, miten kummallista! Totisesti, Jumala suo antimensa niille palvelijoistaan, joille haluaa, tai myös vähentää niitä. Ellei Jumala olisi ollut hyvä meitä kohtaan, olisimme mekin hautautuneet maan alle. Voi, miten kummallista! Totisesti, uskottomat eivät milloinkaan menesty.»');
INSERT INTO "finnish" VALUES(28,83,'28:83','Tulevaisuuden kodin Me määräämme niitä varten, jotka eivät halua korottaa itseään maan päällä eivätkä halua tehdä pahaa. Parhaimman osan saavat viimein hurskaat.');
INSERT INTO "finnish" VALUES(28,84,'28:84','Sentähden, joka toimittaa hyvää, saa vieläkin parempaa; mutta ken pahaa tekee, sellaiselle maksetaan palkka vain hänen tekojensa mukaan.');
INSERT INTO "finnish" VALUES(28,85,'28:85','Totisesti, Hän, joka on antanut sinulle tämän Koraanin, on johtava sinut takaisin luvattuun paikkaan. Sano: »Herra tuntee parhaiten sekä sen, jolla on johdatus, että sen, joka on ilmeisen harhan vallassa.»');
INSERT INTO "finnish" VALUES(28,86,'28:86','Sinä et odottanut tätä Kirjaa sinulle ilmoitettavan, mutta se on Herrasi armonosoitus. Älä siis milloinkaan tue uskottomia!');
INSERT INTO "finnish" VALUES(28,87,'28:87','Älä salli heidän erottaa itseäsi Jumalan merkeistä, sen jälkeen kuin ne on sinulle lähetetty. Kutsu heitä Jumalan luo, äläkä koskaan kuulu niiden joukkoon, jotka asettavat muita jumalia Jumalan vertaisiksi.');
INSERT INTO "finnish" VALUES(28,88,'28:88','Älä rukoile muuta jumaluutta kuin Jumalaa; ei ole muuta Jumalaa kuin Hän. Kaikki muu on häviävä, paitsi Hän. Hänelle kuuluu tuomio, ja Hänen luoksensa teidän on palattava.');
INSERT INTO "finnish" VALUES(29,1,'29:1','Aliif, Laam, Miim (Minä olen Jumalan tietäjistä parhain).');
INSERT INTO "finnish" VALUES(29,2,'29:2','Luulevatko ihmiset, että heidät sanottuaan »Me uskomme» jätetään rauhaan koettelematta?');
INSERT INTO "finnish" VALUES(29,3,'29:3','Me olemme jo koetellut niitä, jotka ennen heitä elivät, ja totisesti Jumala erottaa ne, jotka ovat totuudellisia, niistä, jotka valehtelevat.');
INSERT INTO "finnish" VALUES(29,4,'29:4','Luulevatko ne, jotka pahaa tekevät, voivansa välttää Meidät? Surkeita ovat heidän erehdyksensä.');
INSERT INTO "finnish" VALUES(29,5,'29:5','Jos joku toivoo kohtaavansa Jumalan, niin totisesti Jumalan saapumisen hetki on tuleva; sillä Hän on kaikkikuuleva, kaikkitietävä.');
INSERT INTO "finnish" VALUES(29,6,'29:6','Ja ken ponnistelee, hän tekee sen oman sielunsa hyväksi. Totisesti, Jumala on riippumaton maailmoista.');
INSERT INTO "finnish" VALUES(29,7,'29:7','Jotka uskovat ja hyvää tekevät, niiden synnit Me pyyhimme pois ja annamme heille palkan parhaasta, mitä he ovat tehneet.');
INSERT INTO "finnish" VALUES(29,8,'29:8','Me olemme myös käskenyt ihmisiä hyvyyteen vanhempiaan kohtaan, mutta jos nämä kaikin voimin koettavat saada sinut asettamaan Minun rinnalleni sellaista, mistä et mitään tiedä, niin älä heitä tottele. Minun luokseni teidän on palattava, ja silloin Minä osoitan teille, mitä olette tehneet.');
INSERT INTO "finnish" VALUES(29,9,'29:9','Jotka uskovat ja hyvää tekevät, heidät Me totisesti liitämme hyvien joukkoon.');
INSERT INTO "finnish" VALUES(29,10,'29:10','Ihmisten keskuudessa on monikin sellainen, joka sanoo: »Me uskomme Jumalaan», mutta kun hän joutuu vaivaan Jumalan asian tähden, niin hän pitää ihmisten aiheuttamaa vainoa yhdenveroisena Jumalan lähettämän kärsimyksen kanssa. Mutta jos Herrasi lähettää voiton, niin he varmasti sanovat: »Totisesti, me olimme teidän kanssanne.» Todellakin, eikö Jumala parhaiten tiedä, mitä koko maailman ihmisten sydämiin on kätkettynä?');
INSERT INTO "finnish" VALUES(29,11,'29:11','Totisesti, Jumala tuntee ne, jotka uskovat, ja Hän tuntee myös ulkokultaiset.');
INSERT INTO "finnish" VALUES(29,12,'29:12','Ja uskottomat sanovat uskovaisille: »Seuratkaa meidän teitämme, ja me kannamme teidän syntinne», mutta he eivät voi kantaa vähintäkään heidän synneistään. Totisesti, he ovat valehtelijoita.');
INSERT INTO "finnish" VALUES(29,13,'29:13','He saavat varmasti kantaa oman kuormansa ja sen lisäksi vielä muita kuormia. Totisesti, ylösnousemuksen päivänä he saavat vastata valheellisista puheistansa.');
INSERT INTO "finnish" VALUES(29,14,'29:14','Totisesti, Me lähetimme Nooan kansansa luokse, ja hän oleskeli siellä viisikymmentä vaille tuhannen vuotta. Sitten vedenpaisumus yllätti heidät, koska he olivat väärämielisiä.');
INSERT INTO "finnish" VALUES(29,15,'29:15','Mutta Me pelastimme hänet ja arkin asukkaat ja teimme siitä merkin maailman kansoille.');
INSERT INTO "finnish" VALUES(29,16,'29:16','Ja muista Aabrahamia, kun hän lausui kansalleen: »Palvelkaa Jumalaa ja kunnioittakaa Häntä. Tämä on teille parasta; jospa sen älyäisitte.»');
INSERT INTO "finnish" VALUES(29,17,'29:17','Ne, joita te palvelette Jumalan sijasta, ovat pelkkiä epäjumalia, ja te sepittelette valheita. Totisesti, ne, joita te palvelette Jumalan sijasta, eivät pysty antamaan teille elatusta. Etsikää sitä siis Jumalalta ja palvelkaa Häntä ja kiittäkää Häntä. Hänen luokseen teidän on palattava.»');
INSERT INTO "finnish" VALUES(29,18,'29:18','Ja jos te kiellätte totuuden, niin ovat monet ennen teitä myös totuuden kieltäneet; lähettilään velvollisuus on vain selvästi julistaa sanomaansa.');
INSERT INTO "finnish" VALUES(29,19,'29:19','Totisesti, eivätkö he ole nähneet, miten Jumala on suorittanut luomistyönsä ja antaa sitten kaiken palata takaisin? Totisesti, Jumalalle se on helppoa.');
INSERT INTO "finnish" VALUES(29,20,'29:20','Sano: »Kulkekaa ympäri maata ja huomatkaa, miten Hän on suorittanut luomistyönsä! Senjälkeen Jumala säätää tulevan kehityksen.» Totisesti, Jumala on kykenevä tekemään kaiken, mitä haluaa.');
INSERT INTO "finnish" VALUES(29,21,'29:21','Hän kurittaa ketä haluaa, ja Hän osoittaa armoaan kenelle haluaa. Ja Hänen luokseen teidän on palattava.');
INSERT INTO "finnish" VALUES(29,22,'29:22','Te ette voi kätkeytyä maahan ettekä taivaisiin, eikä kukaan muu kuin Jumala,voi teitä suojella eikä auttaa.');
INSERT INTO "finnish" VALUES(29,23,'29:23','Niillä, jotka eivät usko Jumalan merkkeihin eivätkä Hänen tulemiseensa, ei ole toivoa Minun armostani; he ovat niitä, joita varten on tuskallinen kuritus.');
INSERT INTO "finnish" VALUES(29,24,'29:24','Eikä Aabrahamin kansalla ollut muuta vastausta kuin tämä: »Tappakaa hänet tai polttakaa hänet!» Mutta Jumala pelasti hänet tulesta. Totisesti, tässä on tunnusmerkki niille, jotka uskovat.');
INSERT INTO "finnish" VALUES(29,25,'29:25','Ja hän lausui: »Olette ottaneet epäjumalia Jumalan rinnalle vain sentakia, että olitte toisillenne mieliksi tämän maailman elämässä, mutta ylösnousemuksen päivänä toiset teistä kieltävät toisensa ja toiset kiroavat toisiansa. Teidän asuinsijanne on oleva tuli, eikä teitä kukaan auta.»');
INSERT INTO "finnish" VALUES(29,26,'29:26','Loot uskoi Häneen. Ja Aabraham lausui: »Totisesti, pakenen Herran luokse! Totisesti, Hän on kaikkivaltias, viisas.»');
INSERT INTO "finnish" VALUES(29,27,'29:27','Me annoimme hänelle Iisakin ja Jaakobin ja hänen jälkeläisillensä profetoimislahjan ja Kirjoitukset. Ja Me annoimme hänelle hänen palkkansa tässä maailmassa, ja totisesti, tulevassa elämässä hän on kuuluva hyvien joukkoon.');
INSERT INTO "finnish" VALUES(29,28,'29:28','Ja Loot lausui kansalleen: »Totisesti, te harjoitatte riettautta, jollaista ei kukaan ennen teitä maailman kansojen joukossa eläneistä ole tehnyt.');
INSERT INTO "finnish" VALUES(29,29,'29:29','Te menette miesten luokse, te harjoitatte maantierosvousta ja teette pahaa kokouksissanne.» Eikä hänen kansallaan ollut muuta vastausta kuin tämä: »Langeta päällemme Jumalan tuomio, jos puhut totta!»');
INSERT INTO "finnish" VALUES(29,30,'29:30','Hän lausui: »Herra, auta minua syntisiä ihmisiä vastaan!»');
INSERT INTO "finnish" VALUES(29,31,'29:31','Kun lähettiläämme tulivat Aabrahamin luokse tuomaan sanomaansa, he ilmoittivat: »Totisesti aiomme tuhota tämän kaupungin asukkaat, sillä he ovat todella väärämielisiä.»');
INSERT INTO "finnish" VALUES(29,32,'29:32','Hän lausui: »Asuuhan myös Loot siellä.» He vastasivat: »Me tiedämme parhaiten, kuka siellä asuu. Aiomme pelastaa hänet perheinensä lukuunottamatta hänen vaimoansa; tämä kuuluu niiden joukkoon, jotka jäävät kaupunkiin.»');
INSERT INTO "finnish" VALUES(29,33,'29:33','Ja kun lähettiläämme saapuivat Lootin luokse, tämä tuli ahdistukseen heidän takiaan eikä kyennyt heitä auttamaan. Mutta he lausuivat: »Älä pelkää äläkä murehdi, me pelastamme sinut ja perheesi lukuunottamatta vaimoasi. Hän kuuluu niihin, jotka jäävät kaupunkiin.');
INSERT INTO "finnish" VALUES(29,34,'29:34','Totisesti, Me lähetämme tämän kaupungin asukkaille onnettomuuden taivaasta heidän rikkomustensa tähden.»');
INSERT INTO "finnish" VALUES(29,35,'29:35','Totisesti, Me olemme jättänyt sen rauniot jäljelle selväksi merkiksi niille, joilla on ymmärrystä.');
INSERT INTO "finnish" VALUES(29,36,'29:36','Midianin heimon luokse Me lähetimme heidän veljensä Shuaibin. Hän lausui: »Kansalaiseni, palvelkaa Jumalaa ja luottakaa ylösnousemuksen päivään älkääkä pahaa tehden maata kiertäkö.»');
INSERT INTO "finnish" VALUES(29,37,'29:37','Mutta he kielsivät hänet, ja silloin heidät yllätti maanjäristys, niin että he aamulla ruumiina viruivat asunnoissaan.');
INSERT INTO "finnish" VALUES(29,38,'29:38','Samoin kävi Aadin ja Tamuudin heimojen. Totisesti, heidän asuinpaikkansa osoittavat sen teille. Saatana teki heidän tekonsa kauniiksi heidän omissa silmissään ja esti heitä siten vaeltamasta oikeata tietä, vaikka heillä olikin käsityskykyä.');
INSERT INTO "finnish" VALUES(29,39,'29:39','Samoin kävi vielä Koorahin ja Faraon ja Haamanin. Totisesti, Mooses saapui heidän luokseen selvin tunnusmerkein, mutta he paisuivat maassaan ylpeydestä eivätkä päässeet Meiltä pakoon.');
INSERT INTO "finnish" VALUES(29,40,'29:40','Niin Me rankaisimme kutakin heistä heidän syntiensä vuoksi. Muutamien päälle Me lähetimme hirmumyrskyn, muutamat yllätti kauhistuttava pauhina, jotkut nieli maa ja eräitä heistä Me hukutimme. Jumala ei ole väärämielinen heitä kohtaan, vaan itse he tekivät itsellensä väärin.');
INSERT INTO "finnish" VALUES(29,41,'29:41','Niiden, jotka ovat ottaneet muita suojelijoita kuin Jumalan, käy kuin hämäkin: se valmistaa asumuksensa, ja totisesti, hämähäkin talo on kaikista taloista hatarin. Jospa he sen vain tietäisivät.');
INSERT INTO "finnish" VALUES(29,42,'29:42','Totisesti, Jumala tietää, mitä he rukoilevat Jumalan asemesta, olkoon se mitä tahansa; sillä Hän on kaikkivaltias, viisas.');
INSERT INTO "finnish" VALUES(29,43,'29:43','Me olemme esittänyt nämä vertaukset ihmissuvun hyväksi, mutta vain viisaat niitä ymmärtävät.');
INSERT INTO "finnish" VALUES(29,44,'29:44','Jumala on totuudessa luonut taivaat ja maan. Totisesti, tässä on merkki uskovaisille.');
INSERT INTO "finnish" VALUES(29,45,'29:45','Julista mitä sinulle on ilmoitettu Kirjassa, ja suorita rukouksesi. Rukous varmasti pelastaa riettaudesta ja pahasta. Ja totisesti, Jumalan muistaminen on parasta, sillä Jumala tietää, mitä te teette.');
INSERT INTO "finnish" VALUES(29,46,'29:46','Väittele siivosti niiden kanssa, joilla on Kirjoitus, mutta vastusta niitä, jotka vääryyttä tekevät, ja lausu: »Me uskomme siihen, mikä on meille ylhäältä lähetetty ja mikä myös teille on lähetetty, ja meidän Jumalamme ja teidän Jumalanne on sama ja me olemme Hänelle alamaisia.»');
INSERT INTO "finnish" VALUES(29,47,'29:47','Tällä tavoin Me ilmaisimme sinulle tämän Kirjan ylhäältä. Ja ne, joille on annettu Kirja, uskovat siihen, ja myöskin eräät näistä arabialaisista uskovat siihen. Vain uskottomat kieltävät Meidän merkkimme.');
INSERT INTO "finnish" VALUES(29,48,'29:48','Tätä ennen sinä et heille julkilukenut yhtäkään muuta kirjaa etkä sellaista kirjoittanut oikealla kädelläsi. Vasta silloinhan nämä väärän väittäjät olisivat oikeutetut epäilemään.»');
INSERT INTO "finnish" VALUES(29,49,'29:49','Ei, (Koraani) on (kuin) sarja selviä merkkejä niiden povessa, joille on annettu totista viisautta, ja vain väärämieliset kieltävät Meidän merkkimme.');
INSERT INTO "finnish" VALUES(29,50,'29:50','He sanovat: »Miksi ei hänen Herransa ole lähettänyt hänelle tunnustekoja ylhäältä?» Sano: »Tunnusteot ovat Jumalan vallassa, ja minä olen vain varoittaja.»');
INSERT INTO "finnish" VALUES(29,51,'29:51','Eikö heille riitä, että olemme lähettänyt sinulle tämän Kirjan, jota heille on luettu? Totisesti, tämä on armo ja kehoitus niille, jotka uskovat.');
INSERT INTO "finnish" VALUES(29,52,'29:52','Sano: »Jumala on riittävä Todistaja teidän ja minun asiassani. Hän tietää, mitä on taivaassa ja maan päällä. Ja ne, jotka uskovat valheeseen ja kieltävät Jumalan, kulkevat harhaan.»');
INSERT INTO "finnish" VALUES(29,53,'29:53','He pyytävät myös sinua jouduttamaan tuomiota. Ellei olisi asetettu määrähetkeä, niin totisesti, tuomio olisi heitä jo kohdannut. Totisesti, se on yllättävä heidät yht''äkkiä, kun he eivät sitä aavistakaan.');
INSERT INTO "finnish" VALUES(29,54,'29:54','He pyytävät sinua jouduttamaan tuomiota, mutta totisesti, helvetti piirittää uskottomat.');
INSERT INTO "finnish" VALUES(29,55,'29:55','Tulee päivä, jolloin tuomio langetetaan heidän ylitseen ylhäältä ja jolloin Hän on sanova: »Maistakaa nyt, mitä olette tehneet.»');
INSERT INTO "finnish" VALUES(29,56,'29:56','Palvelijani, jotka uskotte! Maani on avara, palvelkaa siis Minua ja yksin Minua.');
INSERT INTO "finnish" VALUES(29,57,'29:57','Jokainen sielu saa maistaa kuolemaa, ja sitten teidän on palattava Meidän luoksemme.');
INSERT INTO "finnish" VALUES(29,58,'29:58','Niille, jotka uskovat ja tekevät hyvää, Me totisesti annamme ylhäiset asumukset puutarhoissa, joissa purot solisevat ja joissa he alati saavat asua. Oivallinen palkka on ahertaneilla,');
INSERT INTO "finnish" VALUES(29,59,'29:59','niillä, jotka pysyvät lujina ja luottavat Herraansa.');
INSERT INTO "finnish" VALUES(29,60,'29:60','Kuinka paljon onkaan olentoja, jotka eivät huolehdi elannostaan, mutta Jumala huolehtii heistä kuten teistäkin, sillä Hän on kaikkikuuleva, kaikkitietävä.');
INSERT INTO "finnish" VALUES(29,61,'29:61','Totisesti, jos kysyt heiltä: »Ken on luonut taivaat ja maat sekä alistanut auringon ja kuun lakeihinsa?» niin he vastaavat: »Jumala.» Miksi he siis ovat luopuneet?');
INSERT INTO "finnish" VALUES(29,62,'29:62','Jumala antaa runsaan ylläpidon niille palvelijoillensa, joille haluaa, tai Hän mittaa sen niukasti. Totisesti, Jumala tietää kaiken.');
INSERT INTO "finnish" VALUES(29,63,'29:63','Totisesti, jos kysyt heiltä: »Ken lähettää taivaasta teille sateen ja siten elvyttää kuolleen maan uuteen eloon?» niin varmasti he vastaavat: »Jumala.» Sano: »Kaikki ylistys kuuluu Jumalalle.» Mutta useimmat heistä eivät ymmärrä.');
INSERT INTO "finnish" VALUES(29,64,'29:64','Tämän maailman elämä ei ole muuta kuin leikkiä ja ajanviettoa, mutta totisesti, tulevan elämän koti on todellista elämää; jospa he sen vain älyäisivät!');
INSERT INTO "finnish" VALUES(29,65,'29:65','Kun he astuvat laivaan, he rukoilevat Jumalaa, Häneen täydellisesti uskoen, mutta kun Hän pelastaa heidät maihin, niin katso, he asettavat epäjumalia Jumalan rinnalle.');
INSERT INTO "finnish" VALUES(29,66,'29:66','He ovat kiittämättömiä siitä, mitä Hän on heille antanut, ja haluavat vain nauttia. Mutta aikanansa he tulevat älyämään.');
INSERT INTO "finnish" VALUES(29,67,'29:67','Eivätkö he huomaa, että Me olemme aikaansaaneet Pyhän turvapaikan, (Mekan) rauhoitetun alueen, vaikka muualla ihmisiä ahdistetaan? Yhäkö he uskovat valheeseen ja kieltävät Jumalan siunaukset?');
INSERT INTO "finnish" VALUES(29,68,'29:68','Ken on nurjamielisempi kuin se, joka sepittää valhetta Jumalasta tai vääristelee totuuden, kun se tulee hänen kohdalleen? Eikö helvetissä riitä tilaa uskottomille?');
INSERT INTO "finnish" VALUES(29,69,'29:69','Mutta jotka taistelevat asiamme puolesta, heidät johdatamme teillemme; sillä totisesti, Jumala on niiden kanssa, jotka hyvää tekevät.');
INSERT INTO "finnish" VALUES(30,1,'30:1','Aliif, Laam, Miim. (Minä olen Jumala, parhain tietäjä.)');
INSERT INTO "finnish" VALUES(30,2,'30:2','Roomalaiset on voitettu');
INSERT INTO "finnish" VALUES(30,3,'30:3','lähellä olevassa maassa, mutta lähitulevaisuudessa he tappionsa jälkeen pääsevät voittajiksi');
INSERT INTO "finnish" VALUES(30,4,'30:4','vähemmässä kuin kymmenessä vuodessa. Jumalalle kuuluu käskyvalta alussa ja lopussa, ja tuona päivänä uskovaiset ovat onnellisia');
INSERT INTO "finnish" VALUES(30,5,'30:5','Jumalan avulla. Hän auttaa ketä tahtoo, sillä Hän on kaikkivaltias, kaikkein armollisin.');
INSERT INTO "finnish" VALUES(30,6,'30:6','Tämä on Jumalan lupaus. Jumala ei milloinkaan riko lupaustaan, mutta useimmat ihmiset eivät sitä tiedä.');
INSERT INTO "finnish" VALUES(30,7,'30:7','He tuntevat vain tämän maailman elämän ulkonaisen puolen, mutta tulevasta elämästä he ovat peräti välinpitämättömiä.');
INSERT INTO "finnish" VALUES(30,8,'30:8','Eivätkö he ajattelekin mielessään: »Ei kai Jumala ole luonut vain määräajaksi taivasta ja maata ja kaikkea niiden välillä olevaa.» Mutta totisesti, useimmat ihmiset kieltävät joutuvansa Herransa eteen.');
INSERT INTO "finnish" VALUES(30,9,'30:9','Eivätkö he ole kiertäneet maata ja nähneet, millaisen lopun saivat ennen heitä eläneet? Nämä olivat heitä voimiltaan väkevämpiä, he kaivoivat maata ja rakensivat sen pinnalle enemmän kuin nykyiset, ja heidän lähettiläänsä tulivat heidän luokseen selvin tunnusmerkein. Jumala ei tehnyt heille vääryyttä, mutta itse he tuottivat turmion sieluilleen.');
INSERT INTO "finnish" VALUES(30,10,'30:10','Surkea oli niiden loppu, jotka pahaa tekivät, koska he kielsivät Jumalan merkit ja niitä pilkkasivat.');
INSERT INTO "finnish" VALUES(30,11,'30:11','Jumala on pannut alulle luomistyön ja antaa sitten kaiken toistua, ja Hänen luoksensa teidän on palattava.');
INSERT INTO "finnish" VALUES(30,12,'30:12','Ja sinä päivänä, jolloin hetki lyö, syntiset joutuvat epätoivoon,');
INSERT INTO "finnish" VALUES(30,13,'30:13','eivätkä he suojelusjumalistaan saa ketään auttajaa, ja he kieltävät suojelijansa.');
INSERT INTO "finnish" VALUES(30,14,'30:14','Sinä päivänä, jolloin hetki lyö, sinä päivänä heidät kaikki hajoitetaan.');
INSERT INTO "finnish" VALUES(30,15,'30:15','Silloin ne, jotka nyt uskovat ja tekevät hyvää, ovat tervetulleita puutarhaan.');
INSERT INTO "finnish" VALUES(30,16,'30:16','Mutta jotka eivät uskoneet, vaan kielsivät ilmoituksemme ja Meidän kohtaamisemme tulevassa elämässä, heitä kohtaa kuritus.');
INSERT INTO "finnish" VALUES(30,17,'30:17','Julistakaa siksi Jumalan kunniaa sekä illoin että aamuin!');
INSERT INTO "finnish" VALUES(30,18,'30:18','Hänelle kuuluu kaikki ylistys taivaassa ja maan päällä, sekä illalla että keskipäivällä.');
INSERT INTO "finnish" VALUES(30,19,'30:19','Hän tekee kuolleesta elävän ja elävästä kuolleen, ja Hän elvyttää eloon kuolleen maan. Näin myös teidät (Arabian kansa) herätetään uuteen eloon.');
INSERT INTO "finnish" VALUES(30,20,'30:20','Eräs Hänen merkeistään on se, että Hän on luonut teidät tomusta, ja katso, sitten teistä on tullut kautta maan levinnyt ihmissuku.');
INSERT INTO "finnish" VALUES(30,21,'30:21','Eräs Hänen merkeistään on myös se, että Hän on teistä itsestänne luonut teille puolisot, jotta löytäisitte heistä tyydytyksen, ja Hän on antanut teille keskinäistä rakkautta ja laupeutta. Totisesti, tässä on merkkejä niille, jotka ajattelevat.');
INSERT INTO "finnish" VALUES(30,22,'30:22','Hänen merkkejään on myöskin taivaitten ja maan luominen sekä kieltenne ja ihonvärinne moninaisuus. Totisesti, tässä on merkkejä maailman kansoille.');
INSERT INTO "finnish" VALUES(30,23,'30:23','Hänen merkkejään on myöskin teidän unenne sekä yöllä että päivällä ja teidän pyrkimyksenne hankkia ylläpitonne hänen yltäkylläisyydestään. Totisesti, tässä on merkkejä niille, jotka kuuntelevat.');
INSERT INTO "finnish" VALUES(30,24,'30:24','Hänen merkkejään on edelleen se, että Hän antaa teidän nähdä salaman sekä peläten että toivoen ja että Hän lähettää sateen taivaasta maan päälle, elvyttäen siten kuolleen maan eloon. Totisesti, tässä on merkkejä niille, joilla on ymmärrystä.');
INSERT INTO "finnish" VALUES(30,25,'30:25','Eräs Hänen merkeistään on se, että taivas ja maa pysyvät Hänen käskystään paikoillaan. Kun Hän viimein lähettää teille kutsunsa, teidät noudetaan maan päältä.');
INSERT INTO "finnish" VALUES(30,26,'30:26','Hänelle kuuluvat kaikki, jotka ovat taivaassa ja maan päällä. Kaikki tottelevat Häntä.');
INSERT INTO "finnish" VALUES(30,27,'30:27','Juuri Hän on pannut alulle luomistyön ja antaa sitten kaiken palata takaisin, ja Hänelle tämä on helppoa. Hänelle kuuluvat ylevimmät nimitykset sekä taivaassa että maan päällä, sillä Hän on kaikkivaltias, viisas.');
INSERT INTO "finnish" VALUES(30,28,'30:28','Hän esittää teille vertauksen teistä itsestänne: »Onko teillä ja omistamillanne orjilla yhtäläinen osuus omaisuuteen, minkä Me olemme teille suonut? Varotteko heitä samassa määrin kuin toinen toistanne?» Tällä tavoin Me tuomme julki merkkimme niille, joilla on ymmärrystä.');
INSERT INTO "finnish" VALUES(30,29,'30:29','Ne, jotka tekevät syntiä, seuraavat vain omia halujaan, eikä heillä ole mitään tietoa. Kuka voi ohjata sellaista, jonka Jumala antaa eksyä? Heillä ei ole ketään auttajaa.');
INSERT INTO "finnish" VALUES(30,30,'30:30','Käännä siis kasvosi vilpittömästi oikeata uskoa kohti, jonka Jumala on ihmissukua varten perustanut. Jumalan tekoja ei voida muuttaa. Tämä on oikea uskonto, mutta useimmat ihmiset eivät sitä tiedä.');
INSERT INTO "finnish" VALUES(30,31,'30:31','Turvautukaa Häneen, täyttäen velvollisuutenne Häntä kohtaan. Palvelkaa Häntä, suorittakaa rukouksenne. älkää olko pakanoita,');
INSERT INTO "finnish" VALUES(30,32,'30:32','niitä, jotka ovat pirstoneet uskontonsa ja hajaantuneet moniin lahkoihin, ja kukin ryhmäkunta riemuitsee omasta opistaan.');
INSERT INTO "finnish" VALUES(30,33,'30:33','Kun onnettomuus kohtaa ihmisiä, he rukoilevat Herraa kääntyen Hänen puoleensa, mutta kun Hän antaa heidän maistaa armoansa, niin katso, osa heistä alkaa asettaa muita jumalia Hänen rinnalleen.');
INSERT INTO "finnish" VALUES(30,34,'30:34','Täten he kieltävät sen, minkä olemme heille antanut. Nauttikaa aikanne, kyllä viimein tulette palkkanne saamaan.');
INSERT INTO "finnish" VALUES(30,35,'30:35','Olemmeko lähettänyt heille ketään, joka olisi valtuuttanut heidät asettamaan muita Jumalan rinnalle?');
INSERT INTO "finnish" VALUES(30,36,'30:36','Kun annamme ihmisten maistaa armoa, he iloitsevat siitä, mutta kun heitä kohtaa onnettomuus heidän tekojensa johdosta, niin katso, he joutuvat epätoivoon.');
INSERT INTO "finnish" VALUES(30,37,'30:37','Eivätkö he ole huomanneet, että Jumala suo runsaat antimensa kenelle haluaa, ja että Hän myös voi mitata ne niukasti? Totisesti, tässä on merkkejä niille, jotka uskovat.');
INSERT INTO "finnish" VALUES(30,38,'30:38','Anna siis sukulaiselle, mikä hänelle kuuluu, samoin tarvitseville ja matkamiehille. Tämä on parasta niille, jotka haluavat Jumalan mielisuosiota; ja he ovat niitä, jotka menestyvät.');
INSERT INTO "finnish" VALUES(30,39,'30:39','Minkä te lainaatte korkoa vastaan, jotta se lisääntyisi ihmisten omaisuuksien avulla, se ei ole lisäystä Jumalan silmissä; mutta jos te suoritatte säädetyt almut haluten Jumalan mielisuosiota, niin sellaisten ihmisten palkka moninkertaistuu.');
INSERT INTO "finnish" VALUES(30,40,'30:40','Juuri Jumala on teidät luonut ja sitten huolehtinut teidän elannostanne; sitten hän antaa teidän kuolla ja senjälkeen herättää teidät uuteen eloon. Onko suojelusjumalienne joukossa ketään, joka saattaisi tehdä yhdenkään näistä teoista? Kunnia olkoon Hänelle! Hän on korkealla sen yläpuolella, mitä he Hänen rinnalleen asettavat.');
INSERT INTO "finnish" VALUES(30,41,'30:41','Turmiota tapahtuu sekä maalla että merellä ihmiskätten työnä, jotta Hän voisi antaa ihmisten maistaa tekojensa seurauksia, niin että he tekisivät kääntymyksen.');
INSERT INTO "finnish" VALUES(30,42,'30:42','Sano: »Kulkekaa maalla ja merellä nähdäksenne millaisen lopun saivat ne, jotka ennen elivät; useimmat heistä olivat pakanoita.»');
INSERT INTO "finnish" VALUES(30,43,'30:43','Käännä siis kasvosi oikean uskonnon puoleen, ennenkuin Jumalan päivä saapuu, jota kukaan ei voi välttää. Sinä päivänä heidät hajoitetaan.');
INSERT INTO "finnish" VALUES(30,44,'30:44','Ken valitsee epäuskon, häntä vastaan todistaa hänen epäuskonsa, ja ken ikinä tekee hyvää, hän tekee sitä oman sielunsa hyväksi,');
INSERT INTO "finnish" VALUES(30,45,'30:45','niin että Hän voi armostaan palkita niitä, jotka uskovat ja suorittavat hyviä tekoja. Totisesti, Hän ei rakasta uskottomia.');
INSERT INTO "finnish" VALUES(30,46,'30:46','Eräs Hänen merkeistään on, että Hän lähettää tuulet tuomaan ilosanomaa, antaakseen teidän maistaa Hänen armoaan ja laivojen purjehtia Hänen käskystään ja teidän etsiä Hänen armoaan ja osoittaa kiitollisuutta.');
INSERT INTO "finnish" VALUES(30,47,'30:47','Totisesti, olemme jo ennen sinua lähettänyt lähettiläitä heidän kansojensa luokse, ja nämä saapuivat heidän keskuuteensa selvin tunnusmerkein. Senjälkeen Me langetimme kirouksemme niille, jotka olivat syntisiä, sillä velvollisuutemme oli auttaa uskovaisia.');
INSERT INTO "finnish" VALUES(30,48,'30:48','Juuri Jumala lähettää tuulet, ja nämä nostavat pilviä, jotka Hän levittää taivaalle mielensä mukaan kerroksittain. Sitten näet sateen virtaavan alimmista kerroksista, ja kun se lankeaa Hänen palvelijoittensa ylitse, niin katso, he ovat onnellisia.');
INSERT INTO "finnish" VALUES(30,49,'30:49','Ja vastikään, ennenkuin tämä tapahtui, he olivat masentuneita, vallan epätoivoisia.');
INSERT INTO "finnish" VALUES(30,50,'30:50','Tarkastele siis Jumalan armon merkkejä, miten Hän elvyttää kuolleen maan. Totisesti, tällä tavoin Me annamme kuolleille elämän, ja Hän on kykenevä tekemään kaiken, minkä haluaa.');
INSERT INTO "finnish" VALUES(30,51,'30:51','Jos olisimme lähettänyt maan päälle toisenlaisen tuulen, niin he olisivat nähneet laihonsa kuihtuvan, ja he olisivat senjälkeen olleet kiittämättömiä.');
INSERT INTO "finnish" VALUES(30,52,'30:52','Totisesti, sinä et kykene tekemään kuolleita eläviksi etkä saamaan kuuroja kuulemaan kutsuasi, kun he kokonaan kääntävät sinulle selkänsä.');
INSERT INTO "finnish" VALUES(30,53,'30:53','Et voi myöskään johdattaa sokeita pois eksymyksestään. Totisesti, voit johdattaa vain niitä, jotka uskovat merkkeihimme ja ovat alistuvaisia.');
INSERT INTO "finnish" VALUES(30,54,'30:54','Juuri Jumala on luonut teidät heikoiksi ja antanut senjälkeen teille voimaa, ja miehuuden jälkeen Hän tekee teidät jälleen heikoiksi ja iäkkäiksi. Hän luo, mitä tahtoo, sillä Hän on kaikkitietävä, kaikkivoipa.');
INSERT INTO "finnish" VALUES(30,55,'30:55','Ja sinä päivänä, jolloin hetki lyö, syntiset vannovat, että he eivät viivytelleet kuin tunnin ajan. Näin he valehtelevat.');
INSERT INTO "finnish" VALUES(30,56,'30:56','Ne, joille on annettu totista tietoa ja uskoa, sanovat: »Totisesti, teillä on ollut Jumalan Kirjan mukaan aikaa ylösnousemuksen päivään saakka. Tämä on ylösnousemuksen päivä, mutta te ette tienneet mitään.»');
INSERT INTO "finnish" VALUES(30,57,'30:57','Mutta sinä päivänä anteeksipyynnöt eivät auta niitä, jotka ovat olleet väärämielisiä, eikä heidän sallita enää päästä sovintoon.');
INSERT INTO "finnish" VALUES(30,58,'30:58','Totisesti, olemme jo esittänyt tässä Koraanissa monenlaisia vertauksia ihmissuvun hyväksi. Mutta jos sinä tuot heille merkin, niin totisesti uskottomat lausuvat: »Te olette pelkkiä valehtelijoita.»');
INSERT INTO "finnish" VALUES(30,59,'30:59','Näin Jumala paaduttaa niiden sydämet, jotka eivät halua tietää.');
INSERT INTO "finnish" VALUES(30,60,'30:60','Sentähden pysy sinä lujana; totisesti, Jumalan lupaus on tosi, äläkä anna niiden, joilla ei ole varmuutta, saattaa sinua horjuvaksi.');
INSERT INTO "finnish" VALUES(31,1,'31:1','Aliif, Laam, Miim. (Minä olen Jumala, parhain tietäjä.)');
INSERT INTO "finnish" VALUES(31,2,'31:2','Nämä ovat Viisauden Kirjan jakeita;');
INSERT INTO "finnish" VALUES(31,3,'31:3','johdatukseksi ja armoksi niille, jotka hyvää tekevät.');
INSERT INTO "finnish" VALUES(31,4,'31:4','Niille, jotka suorittavat rukouksensa ja antavat säädetyt almut ja ovat varmat tulevasta elämästä.');
INSERT INTO "finnish" VALUES(31,5,'31:5','He saavat Herraltansa johdatuksen, ja he ovat niitä, jotka menestyvät.');
INSERT INTO "finnish" VALUES(31,6,'31:6','On sellaisiakin, jotka Koraanin vaihtavat joutaviin taruihin eksyttääkseen ihmiset Jumalan tieltä, vaikka heillä ei ole mitään tietoa, ja tehdäkseen pilaa Viisauden Kirjan jakeista. He ovat niitä, joita kohtaa häpeällinen rangaistus.');
INSERT INTO "finnish" VALUES(31,7,'31:7','Kun Meidän merkkimme esitetään tällaiselle, hän kääntää korskeasti selkänsä, ikäänkuin hän ei olisi niitä kuullut, ikäänkuin hänen korvansa olisivat ummessa. Kerro hänelle sentähden tuskallisesta tuomiosta.');
INSERT INTO "finnish" VALUES(31,8,'31:8','Totisesti, jotka uskovat ja tekevät hyvää, heitä varten ovat autuaiden tarhat.');
INSERT INTO "finnish" VALUES(31,9,'31:9','Siellä he saavat alati asua. Jumalan lupaus on totinen, sillä Hän on kaikkivaltias, viisas.');
INSERT INTO "finnish" VALUES(31,10,'31:10','Hän on luonut taivaat ilmantukipylväitä, kuten voit nähdä,ja Hän on sijoittanut vuoria maan kamaralle, jottei se sinua horjuttaisi, ja Hän on levittänyt ympäri maata kaikenlaisia elollisia olentoja. Me lähetämme myös sateen taivaasta maan päälle ja kasvatamme monenlaisia hyödyllisiä kasveja.');
INSERT INTO "finnish" VALUES(31,11,'31:11','Tällainen on Jumalan luomakunta. Näytä nyt minulle, mitä ne, jotka on asetettu Jumalan rinnalle, ovat luoneet. Niin, väärämieliset ovat suuren harhan vallassa.');
INSERT INTO "finnish" VALUES(31,12,'31:12','Totisesti, Me annoimme Lukmaanille viisautta ja sanoimme: »Kiitä Jumalaa. Ken osoittaa kiitollisuuttaan, hän tekee sen oman sielunsa hyväksi. Ja jos joku on kiittämätön, niin totisesti, Jumala on riippumaton, ylistetty.»');
INSERT INTO "finnish" VALUES(31,13,'31:13','Muista, miten Lukmaan antoi pojalleen kehoituksen sanoen: »Poikani, älä aseta ketään Jumalan rinnalle. Totisesti, sellainen on perin väärää.»');
INSERT INTO "finnish" VALUES(31,14,'31:14','Olemme myös määrännyt ihmisen pitämään huolta vanhemmistaan; hänen äitinsä on kantanut häntä äärimmäiseen uupumukseen saakka, ja hänen vieroittamisensa on kestänyt toista vuotta. Kiitä Minua ja molempia vanhempiasi. Minun luokseni on kaikkien palattava.');
INSERT INTO "finnish" VALUES(31,15,'31:15','Mutta jos he pakottavat sinua asettamaan Minun rinnalleni sellaista, mistä sinulla ei ole mitään tietoa, älä heitä silloin tottele; elä kuitenkin heidän kanssaan soveliaasti tässä maailmassa. Seuraa sellaisen tietä, joka kääntyy Minunun puoleeni. Minun luokseni teidän on palattava, ja silloin Minä osoitan teille teidän tekonne.»');
INSERT INTO "finnish" VALUES(31,16,'31:16','Ja Lukmaan lausui edelleen: »Oi poikani! Totisesti, jos sinapinsiemenen painoinen jyvänen on kätketty kiven sisään tai taivaisiin taikka maan päälle, niin Jumala on sen löytävä. Totisesti, Jumala on terävänäköinen, ymmärtävä.');
INSERT INTO "finnish" VALUES(31,17,'31:17','Oi poikani, suorita rukouksesi, kehoita hyvän tekemiseen ja kiellä pahat teot sekä pysy lujana, mitä tahansa sinulle sattuukin. Näin on todella vakavasti määrätty.');
INSERT INTO "finnish" VALUES(31,18,'31:18','Älä ole ylpeä ihmisiä kohtaan, äläkä vaella maassa pöyhkeänä. Totisesti, Jumala ei rakasta omahyväistä kerskuria.');
INSERT INTO "finnish" VALUES(31,19,'31:19','Hillitse itsesi ja puhu sävyisästi. Totisesti, rumin ääni on aasin hirnunta.');
INSERT INTO "finnish" VALUES(31,20,'31:20','Ettekö huomaa, että Jumala on alistanut valtaanne kaikki, mikä on taivaassa ja mikä on maassa, ja siunannut teidät yltäkylläisesti sekä ruumiin että sielun tarpeilla. Mutta ihmisten joukossa on sellaisiakin, jotka kiistelevät Jumalasta, vaikka heillä ei ole tietoa eikä johdatusta eikä mitään valaisevaa kirjaa.');
INSERT INTO "finnish" VALUES(31,21,'31:21','Ja kun heille sanotaan: »Seuratkaa sitä, minkä Jumala on maan päälle lähettänyt, he vastaavat: »Me seuraamme sitä, jota havaitsimme esi-isiemmekin seuraavan.» Mutta entäpä jos saatana siten kutsuu heitä kohti liekehtivän tulen tuomiota?');
INSERT INTO "finnish" VALUES(31,22,'31:22','Ken alistaa koko olemuksensa Jumalalle ja tekee hyvää, hän on tarttunut luotettavimpaan kädensijaan, Jumalassa on kaikkien toimien päätös.');
INSERT INTO "finnish" VALUES(31,23,'31:23','Mutta jos joku on valinnut epäuskon, älä anna hänen epäuskonsa sinua murehduttaa. Meidän luoksemme heidän on palattava, ja silloin Me ilmituomme heille heidän tekonsa. Totisesti, Jumala tietää tarkoin, mitä sydämiin on kätketty.');
INSERT INTO "finnish" VALUES(31,24,'31:24','Me annamme heidän nauttia lyhyen ajan; sitten vedämme heidät ankaralle tuomiolle.');
INSERT INTO "finnish" VALUES(31,25,'31:25','Totisesti, jos kysyt heiltä: »Kuka on luonut taivaat ja maan?» niin he varmasti vastaavat: »Jumala.» Sano: »Kaikki ylistys kuuluu Jumalalle!» Mutta useimmat heistä eivät tiedä mitään.');
INSERT INTO "finnish" VALUES(31,26,'31:26','Jumalalle kuuluu kaikki, mikä on taivaassa ja maan päällä. Totisesti, Jumala on kaikista riippumattomin, kaikkein ylistetyin.');
INSERT INTO "finnish" VALUES(31,27,'31:27','Vaikka kaikki maan pinnalla olevat puut olisivat kyniä ja valtameri sekä seitsemän valtamerta sen lisäksi olisivat mustetta, niin Jumalan sanat eivät loppuisi. Totisesti, Jumala on mahtava, viisas.');
INSERT INTO "finnish" VALUES(31,28,'31:28','Sekä nykyinen olemuksenne että ylösnousemuksenne ovat vain ikäänkuin saman sielun ilmenemismuotoja. Totisesti, Jumala on kuuleva, näkevä.');
INSERT INTO "finnish" VALUES(31,29,'31:29','Etkö näe, että Jumala antaa yön seurata päivää ja päivän yötä, ja Hän säätää auringon ja kuun liikkeet; kumpikin kulkee kohti määrättyä hetkeään, Jumala on tietoinen teidän teoistanne.');
INSERT INTO "finnish" VALUES(31,30,'31:30','Sillä Jumala on totuus, ja se, mitä he Hänen asemestaan rukoilevat, on valhetta; Jumala on kaikkein korkein, kaikkein suurin.');
INSERT INTO "finnish" VALUES(31,31,'31:31','Etkö havaitse, miten laivat Jumalan siunaamina purjehtivat merellä, jotta Hän voisi näyttää teille erään merkeistään? Totisesti, tässä on merkkejä jokaiselle lujana pysyvälle ja kiitolliselle ihmiselle.');
INSERT INTO "finnish" VALUES(31,32,'31:32','Ja kun aallot vyöryvät heidän päälleen kuin vuoret, niin he rukoilevat Jumalaa uskoen Häneen täydellisesti, mutta kun Hän pelastaa heidät maihin, niin vain muutamat heistä kulkevat oikeata tietä. Vain sopimusten rikkojat ja kiittämättömät väittelevät Meidän merkkejämme vastaan.');
INSERT INTO "finnish" VALUES(31,33,'31:33','Oi ihmiset, palvelkaa Herraanne ja varokaa sitä päivää, jolloin isä ei voi auttaa poikaansa eikä poika saata vähääkään tukea isäänsä. Jumalan lupaus on varmasti tosi, älkää siis antako tämän maailman elämän johtaa itseänne harhaan, älkääkä salliko suuren viettelijän eksyttää teitä Jumalan luota.');
INSERT INTO "finnish" VALUES(31,34,'31:34','Totisesti, Jumala tietää tuomion hetken, Hän lähettää sateen maan päälle ja Hän tietää, mitkä ihmiskohtalot äitien kohtuihin kätketyt ovat. Yksikään sielu ei aavista, mitä hänen on huomenna koettava, eikä sitä, missä maassa hän on kuoleva. Totisesti, Jumala on tietävä, tarkkanäköinen.');
INSERT INTO "finnish" VALUES(32,1,'32:1','Aliif, Laam, Miim. (Minä olen Jumala, parhain tietäjä.)');
INSERT INTO "finnish" VALUES(32,2,'32:2','Tämän Kirjan on ylhäältä lähettänyt maailmojen Herra, siitä ei ole epäilystä.');
INSERT INTO "finnish" VALUES(32,3,'32:3','Sanovatko he, että olet sen keksinyt? Ei, se on Herraltasi tullut totuus, jotta voisit varoittaa ihmisiä, joiden luokse ennen sinua ei ole saapunut varoittajaa, ja jotta he saisivat johdatuksen.');
INSERT INTO "finnish" VALUES(32,4,'32:4','Juuri Jumala on luonut taivaat ja maan ja kaiken niiden välillä olevan kuudessa päivässä ja on yhä säilyttänyt lujan mahtinsa. Teillä ei ole muuta suojelijaa eikä auttajaa kuin Hän. Ettekö tätä ota varteen?');
INSERT INTO "finnish" VALUES(32,5,'32:5','Hän lähettää käskynsä ylhäältä avaruudesta maan päälle; sitten se kohoaa Hänen luokseen yhdessä päivässä, joka teidän laskujenne mukaan on tuhannen vuoden pituinen.');
INSERT INTO "finnish" VALUES(32,6,'32:6','Sellainen on Hän, joka tuntee sekä salatut että ilmeiset asiat, Hän on kaikkivaltias, kaikkein armollisin.');
INSERT INTO "finnish" VALUES(32,7,'32:7','Kaiken Hän on tehnyt parhaimmalla tavalla ja aloittanut luomalla ihmisen kosteasta savesta.');
INSERT INTO "finnish" VALUES(32,8,'32:8','Sitten Hän loi hänen siemenensä mitättömän näköisestä nesteestä.');
INSERT INTO "finnish" VALUES(32,9,'32:9','Senjälkeen Hän täydensi hänet ja puhalsi häneen henkensä, ja Hän on antanut teille kuulon ja näön sekä sielun. Mutta niukasti te osoitatte kiitollisuutta.');
INSERT INTO "finnish" VALUES(32,10,'32:10','He kyselevät: »Kuinka, senkö jälkeen kuin me maadumme tomuksi, meidät herätetään taas uuteen eloon?» Niin, he kieltävät Herransa tulemisen.');
INSERT INTO "finnish" VALUES(32,11,'32:11','Sano: »Kuoleman enkeli, joka teitä kaitsee, on maksava teille täyden palkkanne; sitten teidän on palattava Jumalan luokse.»');
INSERT INTO "finnish" VALUES(32,12,'32:12','Jospa voisit nähdä, miten syntiset seisovat Herransa edessä päät vaipuneina ja sanovat: »Oi Herra, me olemme nähneet sekä kuulleet, lähetä meidät siis takaisin, jotta voisimme tehdä hyvää, sillä totisesti, olemme päässeet varmuuteen.»');
INSERT INTO "finnish" VALUES(32,13,'32:13','Jos Me olisimme tahtonut, olisimme totisesti voinut antaa jokaiselle sielulle johdatuksensa, mutta sanani on käyvä toteen: »Totisesti, olen täyttävä helvetin dzinneillä ja ihmisillä sekaisin.»');
INSERT INTO "finnish" VALUES(32,14,'32:14','»Maistakaa siis seurauksia, koska unohdatte tämän päivän saapumisen. Olemme maksanut teille palkan unohduksestanne, ja maistakaa nyt tuomio siitä, mitä olette tehneet.»');
INSERT INTO "finnish" VALUES(32,15,'32:15','Vain ne uskovat merkkeihimme, jotka lankeavat maahan kunnioittaen, kun heitä niistä muistutetaan, ja julistavat Herransa kunniaa Häntä ylistäen, sekä ne, jotka eivät paisu ylpeydestä.');
INSERT INTO "finnish" VALUES(32,16,'32:16','He nousevat vuoteistaan rukoillen Herraansa peläten ja toivoen, ja he antavat almuja siitä, mitä Me olemme heille suonut.');
INSERT INTO "finnish" VALUES(32,17,'32:17','Mutta yksikään sielu ei tiedä, mitä heille on tallessa sellaista, mikä on kirkastava heidän silmänsä, palkkana siitä, mitä ovat tehneet.');
INSERT INTO "finnish" VALUES(32,18,'32:18','Onko uskovainen sellaisen kaltainen, joka on tottelematon? He eivät voi olla samanvertaisia.');
INSERT INTO "finnish" VALUES(32,19,'32:19','Niitä varten, jotka uskovat ja suorittavat hyviä tekoja, on ikuiset puutarhat palkkana siitä, mitä ovat tehneet.');
INSERT INTO "finnish" VALUES(32,20,'32:20','Mutta jotka ovat olleet tottelemattomia, niiden asuinsijana on oleva tuli. Aina kun he haluavat päästä sieltä pois, heidät palautetaan takaisin ja heille sanotaan: »Maistakaa nyt tulen tuskaa, jota väititte valheeksi.»');
INSERT INTO "finnish" VALUES(32,21,'32:21','Totisesti, Me annamme heidän ennen tulevaa, ankaraa tuomiota maistaa lievempää rangaistusta, jotta he kääntyisivät hyvään.');
INSERT INTO "finnish" VALUES(32,22,'32:22','Kuka onkaan väärämielisempi kuin se, joka saadessaan muistutuksen Herransa merkeistä kääntää niille selkänsä? Totisesti, kirouksemme kohtaa syntisiä.');
INSERT INTO "finnish" VALUES(32,23,'32:23','Totisesti, Me annoimme Kirjan Moosekselle - älä epäile sitä! - ja annoimme sen johdatukseksi Israelin lapsille.');
INSERT INTO "finnish" VALUES(32,24,'32:24','Me määräsimme heidän joukostaan myös johtajia, jotka johdattivat kansaa Meidän käskymme mukaan niin kauan kuin he siitä kiinni pitivät ja olivat varmoja merkeistämme.');
INSERT INTO "finnish" VALUES(32,25,'32:25','Totisesti, Hän, sinun Herrasi, on ylösnousemuksen päivänä tuomiollaan ratkaiseva heidän välillään olleet kiistat.');
INSERT INTO "finnish" VALUES(32,26,'32:26','Eivätkö he näe johdatusta siinä, että ennen heitä olemme tuhonnut monia sukupolvia, joiden asuinsijoilla he nyt kuljeskelevat? Totisesti, tässä on merkkejä. Eivätkö he mitään kuule?');
INSERT INTO "finnish" VALUES(32,27,'32:27','Eivätkö he havaitse, miten Me langetamme sateen kuivuneeseen maahan ja sen avulla kasvatamme viljaa, jota he itse sekä heidän karjansa syövät? Eivätkö he mitään näe?');
INSERT INTO "finnish" VALUES(32,28,'32:28','Ja he sanovat: »Milloin tämä ratkaisu tapahtuu, jos kerran puhutte totta?»');
INSERT INTO "finnish" VALUES(32,29,'32:29','Vastaa: »Tuomion päivänä, jolloin usko ei vähääkään hyödytä niitä, jotka nyt ovat uskottomia, eikä heille anneta armon aikaa.»');
INSERT INTO "finnish" VALUES(32,30,'32:30','Käänny siis pois heistä ja odota! Totisesti, he saavat odottaa sinun lainasi.');
INSERT INTO "finnish" VALUES(33,1,'33:1','Oi Profeetta, täytä tarkoin velvollisuutesi Jumalaa kohtaan äläkä mukaudu uskottomien ja tekopyhien toivomuksiin; onhan Jumala totisesti tietävä, viisas;');
INSERT INTO "finnish" VALUES(33,2,'33:2','ja noudata Herralta saamiasi ilmoituksia; Jumala totisesti tietää tekosi;');
INSERT INTO "finnish" VALUES(33,3,'33:3','sekä luota Jumalaan, Jumalan suojelus on täysin riittävä.');
INSERT INTO "finnish" VALUES(33,4,'33:4','Jumala ei ole pannut kenenkään luomansa ihmisen rintaan kahta sydäntä eikä Hän ole luonut vaimojanne yhtäläisiksi äitienne kanssa, vaikkakin te hylkäätte vaimojanne vertaamalla äiteihinne; ei Hän liioin ole tehnyt todellisia poikianne niistä, jotka olette pojiksenne ottaneet - senhän teette vain suunne sanoilla, jota vastoin Jumala (luomistöissään) puhuu totta ja ohjaa oikeaan.');
INSERT INTO "finnish" VALUES(33,5,'33:5','Vahvista (poikien) luonnollisia sukulaisuussuhteita isiinsä, sillä se on otollisempaa Jumalalle, mutta jos et tunne heidän oikeita isiään, pidä heitä uskonveljinä ja ystävinä; ei lueta syyksenne, jos te näissä asioissa erehdytte, paitsi milloin tahallanne niin teette; sillä Jumala on anteeksiantava, laupias.');
INSERT INTO "finnish" VALUES(33,6,'33:6','Profeetta on uskovaisia lähempänä kuin nämä ovat toisiansa, ja profeetan vaimot ovat kuin äidit heille. Jumalan säädökset myöntävät luonnollisille sukulaisille paremman perimisoikeuden omaisten suhteen kuin uskonveljille tahi kodeistaan paenneille; kuitenkin voitte jättää perintöosuuksia ystävillennekin - näin on sanottu Pyhässä Kirjassa.');
INSERT INTO "finnish" VALUES(33,7,'33:7','Aikoinaan Me olemme tehnyt liiton entisten profeettain samoin kuin sinun kanssasi, Nooan, Abrahamin, Mooseksen ja Jeesuksen - Marian pojan - kanssa. Se oli luja liittosopimus.');
INSERT INTO "finnish" VALUES(33,8,'33:8','Jotta Jumala sen perusteella voisi tutkia rehellisten totuutta; Hän on valmistanut epäuskoisille tuskallisen kurituksen.');
INSERT INTO "finnish" VALUES(33,9,'33:9','Te, jotka uskotte, muistelkaa Jumalan suosiota teitä kohtaan, silloin kun vihollisjoukot hyökkäsivät päällenne ja Me niitä vastaan lähetimme tuiman tuulen ja (enkelien) joukot, joita ette nähneet: Jumala tietää, mitä teille tapahtuu.');
INSERT INTO "finnish" VALUES(33,10,'33:10','Vihollisenne hyökkäsivät kimppuunne ylängöltä ja laaksosta, ja teiltä silmät samenivat, sydän nousi kurkkuun ja te aioitte ajatella kaikenlaista Jumalasta.');
INSERT INTO "finnish" VALUES(33,11,'33:11','Silloin koetettiin uskovaisia ankaralla koettelemuksella.');
INSERT INTO "finnish" VALUES(33,12,'33:12','Tekopyhät ja heikkosydämiset alkoivat parjata: »Jumala ja Hänen sananjulistajansa lupailivat meille (voittoa) vain pettääkseen meitä.»');
INSERT INTO "finnish" VALUES(33,13,'33:13','l Eräät heistä sanoivat: »Jasripin kansa, tämä ei ole teidän paikkanne (taistellaksenne), vaan palatkaa koteihinne!» Ja osa heistä pyysi profeetalta lupaa sanoen: »Varmasti meidän kotimme ovat vaarassa», vaikka ne eivät olleet vaarassa, - he vain halusivat paeta pois.');
INSERT INTO "finnish" VALUES(33,14,'33:14','Mutta jos muslimien viholliset olisivat tulleet muulla tavoin (kaupunkiin) ja kehoittaneet näitä yhtymään heidän sotajuoneensa, nämä olisivat varmasti sen tehneet, jopa seuranneet heitä ulos kaupungistakin.');
INSERT INTO "finnish" VALUES(33,15,'33:15','Ja kuitenkin he olivat aikaisemmin antaneet Jumalalle lupauksen, etteivät käänny pakoon; ja heidän on vastattava lupauksestaan Jumalalle.');
INSERT INTO "finnish" VALUES(33,16,'33:16','Sano: »Ei teille ole hyötyä siitä, että yritätte paeta kuolemaa ja taistelua, sillä joka tapauksessa annetaan teille vain vähän armonaikaa.»');
INSERT INTO "finnish" VALUES(33,17,'33:17','Sano: »Kenet löydätte, joka estäisi Jumalan teille pahaakin tekemästä, jos Hän niin tahtoisi, mutta Hänhän tarkoittaa teille hyvää.» Eivät he löydä itselleen ketään suojelijaa eivätkä auttajaa paitsi Jumalan.');
INSERT INTO "finnish" VALUES(33,18,'33:18','Totisesti, Jumala tuntee ne keskuudestanne, jotka estelevät muita ja jotka sanovat veljilleen: »Liittykää meihin!» Tällaiset eivät tule taistelutovereiksemme kuin näön vuoksi,');
INSERT INTO "finnish" VALUES(33,19,'33:19','ollen kaikessa kitsaita muslimeja kohtaan; mutta kun kauhun aika tulee, saatte nähdä, miten he teihin tuijottavat ja heidän silmänsä pyörivät kuin kuoleman kielissä olevien; ja kun vaara on ohi, he soimaavat teitä terävällä kielellään ja kitsastelevat kaikessa, mitä heiltä riittäisi. Nämä eivät usko, ja siksi on Jumala tehnyt tyhjäksi heidän tekonsa, sillä se on helppoa Jumalalle.');
INSERT INTO "finnish" VALUES(33,20,'33:20','He eivät uskoneet vihollisten tulevan, ja jos nämä tulisivat, he mieluummin asuisivat erämaan arabialaisten tykönä, ja jos he sattuisivat olemaan teidän keskuudessanne, he eivät taistelisi kuin vähän, näön vuoksi.');
INSERT INTO "finnish" VALUES(33,21,'33:21','Jumalan sananjulistaja on teille todella erinomainen esikuva, jokaiselle, joka luottaa Jumalaan, uskoo viimeiseen päivään ja alinomaa muistelee Jumalaa.');
INSERT INTO "finnish" VALUES(33,22,'33:22','Kun uskovaiset näkivät liittoutuneet, he sanoivat: »Nyt tapahtuu se, mitä Jumala ja hänen sananjulistajansa meille ennustivat; Jumala ja Hänen sananjulistajansa ovat puhuneet totta.» Ja tämä vain vahvisti heidän uskoaan ja kuuliaisuuttaan.');
INSERT INTO "finnish" VALUES(33,23,'33:23','Oikeauskoisia ovat ne, jotka ovat uskollisia liitolleen Jumalan kanssa; samoin ne, jotka pitävät lupauksensa, ja ne, jotka odottavat Jumalan lupausten täyttymistä; nämä eivät ole muuttuneet;');
INSERT INTO "finnish" VALUES(33,24,'33:24','Jumala voi palkita totuudellisia heidän totuudestaan ja kurittaa teeskentelijöitä mielensä mukaan tahi kääntyä heidän puoleensa laupeudessaan; sillä Jumala on anteeksiantava, laupias.');
INSERT INTO "finnish" VALUES(33,25,'33:25','Jumala torjui uskottomien raivoisan hyökkäyksen; he eivät voittaneet mitään, ja Jumala oli oikeauskoisten suojana taistelussa, sillä Hän on väkevä ja mahtava.');
INSERT INTO "finnish" VALUES(33,26,'33:26','Hän masensi nekin, jotka olivat saaneet kirjoituksen, ja kylvi kauhua heidän sydämiinsä; osan heistä te tapoitte ja loput otitte vangiksi.');
INSERT INTO "finnish" VALUES(33,27,'33:27','Ja Hän antoi teille perinnöksi heidän maansa, asumuksensa, omaisuutensa ja nekin alueet, joihin jalkanne eivät vielä ole astuneet, sillä Jumalalla on valta yli kaiken.');
INSERT INTO "finnish" VALUES(33,28,'33:28','Profeetta, sano vaimoillesi: »Jos te halajatte tämän maailman menoa ja koreutta, niin tulkaa, minä mittaan teille elatusvaroja ja lähetän teidät kuten tapa vaatii,');
INSERT INTO "finnish" VALUES(33,29,'33:29','mutta jos te ikävöitte Jumalaa, Hänen sananjulistajaansa ja viimeisen päivän asumuksia, on Jumala totisesti valmistanut runsaan palkinnon niille te''istä, jotka hyvää tekevät.');
INSERT INTO "finnish" VALUES(33,30,'33:30','Te Profeetan vaimot! Ken ikinä teistä julkeasti loukkaa säädyllisyyttä, hänen rangaistuksensa on oleva kaksinkertainen, sillä se on helppo asia Jumalalle.');
INSERT INTO "finnish" VALUES(33,31,'33:31','Mutta ne teistä, jotka ovat kuuliaisia Jumalalle ja Hänen sananjulistajalleen sekä tekevät hyvää, saavat Meiltä kahdenkertaisen palkan; Me olemme valmistanut heille ihanan osan.');
INSERT INTO "finnish" VALUES(33,32,'33:32','Profeetan vaimot! Te ette ole muiden naisten kaltaisia: jos otatte asemastanne vaarin, älköön puheenne olko hempeätä, jottei se olisi kenellekään kiusaukseksi, vaan käyttäkää sopivaa puhetta.');
INSERT INTO "finnish" VALUES(33,33,'33:33','Ja pysytelkää asunnoissanne älkääkä somistako itseänne entisen pimeyden ajan koruilla; harjoittakaa hartautta, antakaa almuja ja totelkaa Jumalaa sekä hänen sananjulistajaansa. Jumala vain tahtoo poistaa teistä kaiken saastan, te Profeetan perhekunta, ja puhdistaa teidät sisimpäänne asti.');
INSERT INTO "finnish" VALUES(33,34,'33:34','Ja pitäkää mielessänne Jumalan ilmoitukset ja viisaus, jota teille on kodeissanne julkiluettu; Jumala on totisesti tarkkanäköinen, kaikkitietävä.');
INSERT INTO "finnish" VALUES(33,35,'33:35','Jumala on valmistanut anteeksiannon ja runsaan palkan kaikille (Islaamiin) alistuville miehille ja naisille, uskoville miehille ja naisille, kuuliaisille miehille ja naisille, totuudellisille miehille ja naisille, kärsiville miehille ja naisille, sävyisille miehille ja naisille, almuja antaville miehille ja naisille, paastoaville miehille ja naisille, siveille miehille ja naisille, miehille ja naisille, jotka alinomaa Jumalaa muistavat.');
INSERT INTO "finnish" VALUES(33,36,'33:36','Ei ole oikeauskoisella miehellä eikä naisella valinnan varaa sellaisessa asiassa, jonka Jumala ja hänen sananjulistajansa ovat jo ratkaisseet ; kuka ikinä on tottelematon Jumalalle ja Hänen sananjulistajalleen, hän ilmeisesti kulkee harhaan.');
INSERT INTO "finnish" VALUES(33,37,'33:37','Hänelle (Zaidille), joka oli Jumalan ja sinun omassa suosiossasi, sinä sanoit: »Pidä vaimosi luonasi ja noudata tarkoin velvollisuuksiasi Jumalaa kohtaan!» Ja sinä salasit sydämeesi sen, minkä Jumala sitten toi valoon , ja sinä pelkäsit ihmisten mielipidettä, vaikka Jumalalla oli parempi oikeus vaatia, että sinä Häntä pelkäisit. Mutta kun Zaidilta oli mennyt kaikki halu elää hänen kanssaan, Me annoimme hänet sinulle vaimoksi, jotta uskovaisille ei olisi synniksi ottaa aviokseen ottopoikiensa vaimoja, joihin nämä ovat kyllästyneet; sillä Jumalan säädöstä on noudatettava.');
INSERT INTO "finnish" VALUES(33,38,'33:38','Eikä voi olla siinä vääryyttä, että Profeetta menettelee niin kuin Jumala on käskenyt; näin Jumala suhtautui entisiinkin Profeettoihin, Jumalan käsky on ehdoton säädös.');
INSERT INTO "finnish" VALUES(33,39,'33:39','Jumala pitää huolen niistä, jotka julistavat Hänen käskyjään, pelkäävät Häntä, mutta eivät pelkää ketään muita kuin Jumalaa.');
INSERT INTO "finnish" VALUES(33,40,'33:40','Muhammed ei ole kenenkään miehenne isä, vaan Hän on Jumalan sananjulistaja ja profeetoista viimeinen, heidän sinettinsä. Jumala on kaikkitietävä.');
INSERT INTO "finnish" VALUES(33,41,'33:41','Te, jotka uskotte, muistakaa Jumalaa, muistelkaa Häntä alinomaa');
INSERT INTO "finnish" VALUES(33,42,'33:42','ja ylistäkää Häntä aamuin illoin!');
INSERT INTO "finnish" VALUES(33,43,'33:43','Hän lähettää teille siunauksensa, ja samoin tekevät Hänen enkelinsä, jotta Hän voisi ohjata teidät pimeydestä valoon; Hän on laupias uskovaisille.');
INSERT INTO "finnish" VALUES(33,44,'33:44','Sinä päivänä, jolloin he kohtaavat Hänet, he tervehtivät häntä sanalla: »Rauha!» ja Hän on valmistanut heille ihanan palkinnon.');
INSERT INTO "finnish" VALUES(33,45,'33:45','Profeetta, Me olemme totisesti lähettänyt sinut todistajaksi, hyvän sanoman tuojaksi ja varoittajaksi');
INSERT INTO "finnish" VALUES(33,46,'33:46','kutsumaan Jumalan luo Hänen luvallaan ja olemaan palavana soihtuna.');
INSERT INTO "finnish" VALUES(33,47,'33:47','Sinun on julistettava uskovaisille hyvää sanomaa, että heitä odottaa suuri armo Jumalan luona.');
INSERT INTO "finnish" VALUES(33,48,'33:48','Älä sopeudu uskottomien ja tekopyhien kanssa, jätä huomiotta heidän väärämielisyytensä, luota yksin Jumalaan, jonka voima riittää suojelemaan sinua.');
INSERT INTO "finnish" VALUES(33,49,'33:49','Uskovaiset, kun otatte itsellenne uskovaisia vaimoja ja sitten eroatte heistä ryhtymättä heihin, niin teidän ei heidän suhteensa tarvitse noudattaa määräaikaa, vaan luovuttakaa heille elatusvaroja ja erotkaa heistä kuten tapa vaatii.');
INSERT INTO "finnish" VALUES(33,50,'33:50','Profeetta, Me olemme totisesti vahvistanut sinun laillisiksi vaimoiksesi kaikki ne, joille olet myöntänyt lesken osuudet, sekä ne, jotka omistat Jumalan antamina orjattarina sotasaaliista, sekä setäsi tyttäret ja isäsi sisaren tyttäret, myöskin enosi tyttäret ja äitisi sisarten tyttäret, jotka kanssani pakenivat vainottuina uskonnon vuoksi; samoin on laillinen vaimosi sellainen uskovainen nainen, joka pyrkii avioliittoon kanssasi, mikäli suostut naimaan hänet. Nämä säädökset ovat nimenomaan sinua, mutta eivät muita uskovaisia varten; me tiedämme hyvin, mitä olemme heille säätänyt vaimoista ja orjattarista. Älköön siis mikään parjaus sinuun koskeko; Jumala on anteeksiantava, laupias.');
INSERT INTO "finnish" VALUES(33,51,'33:51','Sinä saat syrjäyttää heistä kenet tahdot ja lähestyä ketä suvaitset, jopa niitäkin, joita aikaisemmin olet vieronut, eikä siitä kenelläkään ole sanottavaa; tämä kohtelu on sopivinta, jotta eivät heidän silmänsä kostuisi eivätkä he murehtisi, ja jotta he kaikki tyytyisivät siihen, mitä tahdot heille suoda. Jumala tietää, mitä on sydämissänne, sillä Jumala on tietävä, pitkämielinen.');
INSERT INTO "finnish" VALUES(33,52,'33:52','Tämän jälkeen sinun ei ole sallittu ottaa itsellesi naisia eikä vaihtaa vaimojasi uusiin, vaikka näiden kauneus sinua miellyttäisi, vaan sinun on tyydyttävä laillisiin vaimoihisi; Jumala pitää kaikesta vaarin.');
INSERT INTO "finnish" VALUES(33,53,'33:53','Uskovaiset, ellei lupaa ole annettu, älkää käykö Profeetan taloon aterialle, älkääkä odotelko hänen ruokansa valmistumista, mutta jos teidät on kutsuttu, käykää sisään ja aterioituanne hajaantukaa, pyrkimättä liikaan tuttavallisuuteen. Tämä kaikki vaivaa Profeettaa; häntä ujostuttaa sanoa sitä teille, mutta Jumala ei teitä jätä vaille totuutta. Ja kun yleensä heiltä jotakin pyydätte, tehkää se telttaverhon ulkopuolelta. Tämä on soveliain käyttäytyminen teidän ja heidän sydämensä vuoksi. Ei teidän sovi häiritä Jumalan sananjulistajaa eikä liioin koskaan ottaa hänen entisiä vaimojaan avioksi, sillä se on totisesti viheliäistä Jumalan silmissä.');
INSERT INTO "finnish" VALUES(33,54,'33:54','Jumala näkee kaikki, mitä teette julkisesti tai salassa.');
INSERT INTO "finnish" VALUES(33,55,'33:55','Profeetan vaimoja kohtaan ei ole moittimisoikeutta heidän isillään eikä pojillaan enempää kuin heidän veljillään, veljiensä pojilla, sisartensa pojilla, vielä vähemmän heidän heimonsa naisilla tai orjattarilla. Tästä Jumalan määräyksestä on pidettävä vaari, sillä Jumala kelpaa kaiken todistajaksi.');
INSERT INTO "finnish" VALUES(33,56,'33:56','Totisesti Jumala ja Hänen enkelinsä siunaavat Profeettaa; uskovaiset, rukoilkaa (Jumalan) siunausta hänelle ja tervehtikää häntä arvonsa mukaisesti.');
INSERT INTO "finnish" VALUES(33,57,'33:57','Ne, jotka puhuvat pahaa Jumalasta ja Hänen sananjulistajastaan, Jumala on kironnut tässä maailmassa ja tulevassa elämässä sekä valmistanut heille häpeällisen rangaistuksen.');
INSERT INTO "finnish" VALUES(33,58,'33:58','Ne, jotka aiheettomasti puhuvat pahaa uskovista miehistä ja uskovista naisista, ovat vikapäitä panetteluun ja julkeaan syntiin.');
INSERT INTO "finnish" VALUES(33,59,'33:59','Profeetta, sano vaimoillesi ja tyttärillesi ja uskovaisille naisille, että heidän tulee hunnuttaa kasvonsa; näin on sopivinta, jotta heidät kunniallisiksi naisiksi huomataan, niin ettei heitä tunkeilevasti lähestytä; Jumala on kaikessa viisas ja laupias.');
INSERT INTO "finnish" VALUES(33,60,'33:60','Jos teeskentelijät, sydämessään vilpilliset ja kaupungin yllyttäjät eivät lakkaa pahuutta levittämästä, Me totisesti annamme sinulle vallan heidän suhteensa, niin etteivät he kauankaan saa olla naapureinasi kaupungissa;');
INSERT INTO "finnish" VALUES(33,61,'33:61','sellaiset joutuvat maanpakoon, ja missä ikinä heidät tavataan, heidät pidätetään ja armotta tapetaan.');
INSERT INTO "finnish" VALUES(33,62,'33:62','Näin on Jumala menetellyt ennen muinoin, etkä tule havaitsemaan Hänen tapojensa muuttuneen.');
INSERT INTO "finnish" VALUES(33,63,'33:63','Ihmiset kysyvät sinulta, milloin tämä tapahtuu; sano: »Vain Jumala tietää ajan.» Miten saisin teidät tajuamaan, että hetki voi olla lähellä?');
INSERT INTO "finnish" VALUES(33,64,'33:64','Totisesti Jumala on kironnut uskottomat ja valmistanut heille polttavan tulen,');
INSERT INTO "finnish" VALUES(33,65,'33:65','jossa heidän tuskansa kestävät eivätkä he löydä ketään suojelijaa eivätkä auttajaa.');
INSERT INTO "finnish" VALUES(33,66,'33:66','Sinä päivänä, jolloin heidän kasvonsa painetaan tuleen, he voihkivat: »Voi, jospa olisimme totelleet Jumalaa ja sananjulistajaa!»');
INSERT INTO "finnish" VALUES(33,67,'33:67','Ja he sanovat: »Herra, mehän olimme kuuliaisia johtajillemme ja suurmiehillemme, ja nämä johtivat meidät harhaan oikealta tieltä.');
INSERT INTO "finnish" VALUES(33,68,'33:68','Herra, määrää heille kahdenkertainen rangaistus ja anna kauhean kirouksen kohdata heitä!»');
INSERT INTO "finnish" VALUES(33,69,'33:69','Uskovaiset, älkää olko niiden kaltaisia, jotka puhuivat pahaa Mooseksesta. Mutta Jumala puhdisti hänet heidän parjauksistaan, koska hän oli Jumalalle otollinen.');
INSERT INTO "finnish" VALUES(33,70,'33:70','Uskovaiset, täyttäkää tarkoin velvollisuutenne Jumalaa kohtaan ja puhukaa totuuden sanoja,');
INSERT INTO "finnish" VALUES(33,71,'33:71','jotta hän sallisi töidenne onnistua ja antaisi anteeksi hairahduksenne; jokainen, joka tottelee Jumalaa ja Hänen sananjulistajaansa, saavuttaa varmasti suuren onnen.');
INSERT INTO "finnish" VALUES(33,72,'33:72','Totisesti, Me panimme luottamuksemme taivaisiin ja maahan ja vuoriin, eivätkä ne pettäneet sitä, vaan pelolla sitä toteuttavat, mutta ihminen ei ole osoittautunut sen arvoiseksi, vaan on uskottomuudessaan typerä väärintekijä,');
INSERT INTO "finnish" VALUES(33,73,'33:73','joten Jumala on rankaiseva ulkokultaisia miehiä ja naisia, monijumalaisia miehiä ja naisia, mutta Jumala kääntyy laupeudessaan oikeauskoisiin miehiin ja naisiin, sillä Jumala on anteeksiantava, laupias.');
INSERT INTO "finnish" VALUES(34,1,'34:1','Ylistys ja kunnia Jumalalle, jolle kuuluu kaikki, mitä taivaissa ja maassa on; Hänelle kuuluu tulevienkin aikojen ylistys, sillä Hän on viisas, kaikkitietävä.');
INSERT INTO "finnish" VALUES(34,2,'34:2','Hän tuntee kaiken, mikä joutuu maan uumeniin ja mikä maasta esille nousee, kaiken, mikä tulee taivaista tai sinne nousee; Hän on laupias, anteeksiantava.');
INSERT INTO "finnish" VALUES(34,3,'34:3','Epäuskoiset sanovat: »Ei se (tuomion) hetki meille tule.» Sano: »Kyllä! Kautta Herrani, joka tuntee silmiltä salatut, teille varmasti koittaa (tuomionpäivä).» Ei taivaassa eikä maassa ole mitään Hänen valtansa ulkopuolella, ei hivenenkään verran eikä sitä vähemmän tahi enemmän, vaan kaikki on selvästi (Jumalan) kirjoissa,');
INSERT INTO "finnish" VALUES(34,4,'34:4','joten Hän osaa palkita niitä, jotka uskovat ja hyvää harjoittavat: näille kuuluu anteeksiantamus ja ihana osa.');
INSERT INTO "finnish" VALUES(34,5,'34:5','Mutta niitä, jotka koettavat halventaa Meidän ilmoituksiamme, odottaa tuskallinen ja ankara rangaistus.');
INSERT INTO "finnish" VALUES(34,6,'34:6','Kyllä ne, jotka ovat saaneet Herrasi sinulle lähettämät ilmoitukset, tajuavat, että niissä on totuus, joka ohjaa kaikkivaltiaan, ylistetyn tielle.');
INSERT INTO "finnish" VALUES(34,7,'34:7','Kieltäjät sanovat: »Näytämmekö sinulle miehen, joka sinulle sanoo, että sinun tomusi tyystin hajalle saatetaan ja että sinut sitten varmasti kootaan ja herätetään uudesti luotuna.');
INSERT INTO "finnish" VALUES(34,8,'34:8','Hän on sepittänyt valheen Jumalasta tai hän on järjiltään.», Mutta ne, jotka eivät usko tulevaan elämään, saavat rangaistuksen ja ovat suuressa harhassa.');
INSERT INTO "finnish" VALUES(34,9,'34:9','Eivätkö he ota vaaria maallisista ja taivaallisista, niistä, jotka ovat heillä edessään, tai menneistä ajoista heidän takanaan? Jos Me vain tahdomme, saamme maan nielemään heidät tai taivaan syöksymään heidän päällensä.');
INSERT INTO "finnish" VALUES(34,10,'34:10','Daavidin Me ylensimme korkealle: »Kaiuttakaa, vuoret, ja te, linnut, hänen kiitostaan ja ylistystään!» ja Me panimme raudan taipumaan hänen takojainsa käsissä,');
INSERT INTO "finnish" VALUES(34,11,'34:11','sanoen: »Valmistuta runsaasti haarniskoja ja liitä niiden renkaat tarkasti yhteen. Harjoita myös hyväntekeväisyyttä. Minä tarkkaan ja näen, mitä sinä teet.»');
INSERT INTO "finnish" VALUES(34,12,'34:12','Ja Me teimme tuulen Salomolle alamaiseksi, niin että hänen purjelaivansa yhdessä aamussa kiitivät kuukauden kestävän maamatkan ja illassa niinikään kuukauden taipaleen; ja minkä paljouden sulaa vaskea Me annoimme hänelle! Henkiolennot hänelle töitä suorittivat hänen Herransa käskystä, ja ken ikinä heistä ei Meidän käskyjämme noudattanut, häntä kuritimme polttavalla tulella.');
INSERT INTO "finnish" VALUES(34,13,'34:13','Ne valmistivat hänelle kaikkea, mitä hän toivoi: linnoituksia, kuvapatsaita, avaria maljoja kuin vesialtaat ja keittopatoja, joita ei paikoiltaan siirretä. Daavidin suvun jäsenet, teillä on syytä kiittää! Ja kuitenkin niin harvat palvelijoistani ovat kiitollisia.');
INSERT INTO "finnish" VALUES(34,14,'34:14','Mutta kun Me päätimme, että hänen oli kuoltava, tuli tämä heille siten tiedoksi, että Salomon maallisiin kiintynyt perillinen saattoi valtakunnan hajoamistilaan; ja kun se luhistui, dzinnit havaitsivat, että jos he olisivat olleet näkymättömistä asioista perillä, ei heidän olisi tarvinnut ikeen alla tuskitella.');
INSERT INTO "finnish" VALUES(34,15,'34:15','Saban kaupunki tunnettiin siitä merkistä, että puistot sijaitsivat sen kahden puolen; ei heidän tarvinnut kuin syödä Herranne antimia ja palkita Häntä kiitoksena, niin hyvä oli heidän maansa ja niin laupias oli heille Herra!');
INSERT INTO "finnish" VALUES(34,16,'34:16','Mutta sitten seurasi heidän luopumuksensa, ja Me syöksimme heidän ylitsensä tulvan, jonka voimaa he eivät voineet vastustaa. Heidän kahden puistonsa tilalle Me jätimme heille kaksi, joissa kasvoi vain karvaita hedelmiä tamariskipuiden ja muutaman lootuksen lisäksi.');
INSERT INTO "finnish" VALUES(34,17,'34:17','Näin teimme Me heille heidän epäuskonsa tähden, emmekä Me rankaise muita kuin kiittämättömiä.');
INSERT INTO "finnish" VALUES(34,18,'34:18','Ja heidän kaupunkinsa sekä siunaamiemme kaupunkien välimaille annoimme Me nousta muita kaupunkeja niin tiheään, että toisesta saattoi helposti nähdä toisen, ja näin paloittelimme Me välimatkatkin, että he saattoivat turvallisesti tehdä taivalta, olipa yö tai päivä.');
INSERT INTO "finnish" VALUES(34,19,'34:19','Mutta he alkoivat pyytää: »Herra, tee etäisemmiksi meidän välimatkamme!» Omaksi vahingokseen he näin menettelivät. Ja Me laskimme heidät harhateille ja hajoitimme heidät tyysti hajalleen. Tämän pitäisi riittämän tunnusmerkiksi kaikille kärsivällisille ja kiitollisille.');
INSERT INTO "finnish" VALUES(34,20,'34:20','Totisesti perkele osasi voittaa heidät juonillaan: häntä he seuraavat kaikki, paitsi oikeauskoisten ryhmä.');
INSERT INTO "finnish" VALUES(34,21,'34:21','Ei hänellä kuitenkaan ole valtaa heihin, vaan tämä tapahtuu siksi, että Me erottaisimme jokaisen, joka uskoo tulevaan elämään, toisesta, joka epäilee tätä totuutta, onhan Herrasi kaiken kaitsija.');
INSERT INTO "finnish" VALUES(34,22,'34:22','Sano: »Huutakaa nyt avuksenne niitä jumalia, joita te Jumalan veroisina palvelette; eihän niillä ole vähäisintäkään valtaa taivaassa eikä maan päällä, ei kuulu niille mitään osuutta kumpaankaan, eikä kukaan niistä pysty Häntä tukemaan.');
INSERT INTO "finnish" VALUES(34,23,'34:23','Eikä Hänen edessään kenenkään väliintulo merkitse mitään, paitsi sen, jonka Hän otolliseksi katsoo. Kun pelko heidän sydämistään poistetaan, he kyselevät: »Mitä kaikkea Herrasi on puhunut?» Ja he päättelevät: »Hän sanoi totuuden.» Hänhän on korkein ja ääretön.');
INSERT INTO "finnish" VALUES(34,24,'34:24','Kysy: »Ken suo teille elatuksen taivaasta ja maasta käsin?» Sano: »Jumala. Eikä totisesti voi muuta päätellä, kuin että joko me olemme oikealla tiellä ja te ilmeisessä harhassa tahi päinvastoin.»');
INSERT INTO "finnish" VALUES(34,25,'34:25','Sano: »Ei teitä panna vastaamaan meidän rikromuksistamme, eikä liioin meiltä tiliä vaadita teidän teoistanne.»');
INSERT INTO "finnish" VALUES(34,26,'34:26','Sano: »Meidän Herramme on kokoava kaikki yhteen, ja silloin on Hän totuudessa antava tuomion meille; sillä Hän on tuomareista suurin, kaikkitietävä.»');
INSERT INTO "finnish" VALUES(34,27,'34:27','Sano: »Näyttäkää minulle ne, joita olette Hänen veroisikseen kuvitelleet.» Ei, Hän yksin on Jumala, mahtava, viisas.');
INSERT INTO "finnish" VALUES(34,28,'34:28','Sinutkin Me lähetimme kaikkien ihmisten tykö vain hyvän sanoman julistajana ja varoittajana, mutta sitä ei enin osa heistä ymmärrä.');
INSERT INTO "finnish" VALUES(34,29,'34:29','Ja he tiukkaavat: »Koska siis täyttyy se, mitä uhkaat, jos totuutta ennustat.»');
INSERT INTO "finnish" VALUES(34,30,'34:30','Sano: »Täyttymyksen päivä on teille määrätty, ette saata sitä vähääkään viivyttää ettekä jouduttaa.»');
INSERT INTO "finnish" VALUES(34,31,'34:31','Epäuskoiset sanovat: »Emmehän mitenkään voi uskoa tuohon Koraaniin emmekä aikaisempiin ilmoituksiin.» Mutta jospa näkisit, kun väärintekijät pannaan seisomaan Herransa edessä. He vaihtavat silloin sanoja keskenään, ja ne, joita halpoina pidettiin, sanovat ylpeille: »Teidän tähtenne ei meistä uskovaisia tullut.»');
INSERT INTO "finnish" VALUES(34,32,'34:32','Ylpeät taasen vastaavat halveksituille: »Mekö muka teidät pakotimme pois johdatuksesta sen jälkeen kuin se teille tarjottiin? Te itse syypäät olette.»');
INSERT INTO "finnish" VALUES(34,33,'34:33','Mutta halveksitut inttävät ylpeille: »Yötä päivää te juonia punoitte, silloin kun meitä kehoititte epäilemään Jumalaa ja palvelemaan muita Hänen veroisinaan.» Ja he ilmaisevat katumuksensa, kun saavat nähdä, mikä rangaistus heitä odottaa, ja Me panemme kahleet epäuskoisten kaulaan eikä heille makseta muuta kuin tekojensa mukaan.');
INSERT INTO "finnish" VALUES(34,34,'34:34','Koska ikinä Me lähetimme varoittajan johonkin kaupunkiin, niin suruttomasti siinä eläneet jo julistivat: »Emme todellakaan usko sanomaan, jonka sanot tuovasi.»');
INSERT INTO "finnish" VALUES(34,35,'34:35','Ja he lisäävät: »Onhan meillä enemmän lapsia ja rikkautta, eikä meitä rangaista.»');
INSERT INTO "finnish" VALUES(34,36,'34:36','Sano: »Herrani antaa elämän runsauden kenelle suvaitsee ja myös supistaa sen keneltä tahtoo, mutta useimmat ihmisistä eivät sitä ymmärrä.»');
INSERT INTO "finnish" VALUES(34,37,'34:37','Ei rikkautenne eikä jälkeläistenne runsaus kohota teitä Meidän läheisyyteemme, mutta niille, jotka uskovat ja hyvää tekevät, on kaksinkertainen palkka teoistaan, ja heidän ovat ylhäiset asunnot.');
INSERT INTO "finnish" VALUES(34,38,'34:38','Mutta ne, jotka yrittävät vastustella Meidän ilmoituksiamme, saatetaan kurituksen alaisiksi.');
INSERT INTO "finnish" VALUES(34,39,'34:39','Sano: »Totisesti, Herrani antaa elämän runsauden otollisille palvelijoilleen ja tekee olot ahtaiksi kenelle suvaitsee; mitä uhraattekin, sen Hän teille palkitsee, sillä Hän on paras huoltaja.»');
INSERT INTO "finnish" VALUES(34,40,'34:40','Ja päivänä, jolloin Hän heidät kaikki yhteen kokoaa, kysyy Hän enkeleiltä: »Palvelivatko nämä teitä?»');
INSERT INTO "finnish" VALUES(34,41,'34:41','Ja ne sanovat: »Ylistys yksin Sinulle! Sinä olet meidän suojelijamme eivätkä he. He palvelivat peikkoja, joihin useimmat heistä uskoivat.»');
INSERT INTO "finnish" VALUES(34,42,'34:42','Sinä päivänä ei yksikään teistä kykene toiselleen tuottamaan hyötyä eikä vahinkoa, ja väärintekijöille Me sanomme: »Maistakaa sen saman tulen tuskaa, joka teistä oli vain joutavaa puhetta.»');
INSERT INTO "finnish" VALUES(34,43,'34:43','Ja kun Meidän selvät merkkimme heille ilmoitetaan, he sanovat: »Tämä on ihminen, joka pyrkii vierottamaan teidät siitä. mitä esi~isänne palvelivat.» Ja he lisäävät: »Hän puhuu keksittyjä valheita.» Ja totuudestakin, jota heille tarjotaan, sanovat epäuskoiset: »Tämähän on ilmeistä taikuutta.»');
INSERT INTO "finnish" VALUES(34,44,'34:44','Sillä me emme ole heille yhtään kirjoitusta luettavaksi antanut, emmekä ketään varoittajaa ennen sinua lähettänyt.');
INSERT INTO "finnish" VALUES(34,45,'34:45','Ennen heitä eläneet hylkäsivät totuuden, ja nämä profeettaa vastustavat kureishit eivät ole saaneet edes kymmenettä osaa siitä, jonka Me heidän edeltäjilleen annoimme. Ja sittenkin, millainen olikaan Minun närkästykseni, kun he sananjulistajiamme petkuttajiksi väittivät.');
INSERT INTO "finnish" VALUES(34,46,'34:46','Sano: »Minä kehoitan teitä vain siihen ainoaan, että Jumalan tähden valveutuisitte ja joko parittain tai kukin yksinänne tulisitte Jumalan eteen ja tunnustaisitte, että heimolaisenne ei ole mielipuoli; hän on vain teidän varoittajanne ennen ankaraa rangaistusta.»');
INSERT INTO "finnish" VALUES(34,47,'34:47','Sano: »Mitä palkkiota olen teiltä pyytänyt muuta kuin omaksi parhaaksenne, sillä minulle riittää palkkani Jumalan tykönä; Hän on kaiken todistaja.»');
INSERT INTO "finnish" VALUES(34,48,'34:48','Sano: »Herrani saattaa julki totuuden, Hän on näkymättömien paras tietäjä.»');
INSERT INTO "finnish" VALUES(34,49,'34:49','Totuus on tullut voimaan ja valhe häipyy eikä enää palaa.');
INSERT INTO "finnish" VALUES(34,50,'34:50','Sano: »Jos jossakin suhteessa erehdyn, kärsii erehdyksestäni vain oma sieluni, ja jos oikeaan suuntaan toimin, on minun siitä kiitettävä Herrani ilmoituksia, sillä Hän on totisesti kuuleva ja läsnä oleva.»');
INSERT INTO "finnish" VALUES(34,51,'34:51','Jospa silloin heidät näkisit, kun he joutuvat kauhun valtaan; mutta silloin ei heillä enää ole pelastusta, vaan heidät tavoitetaan läheltä.');
INSERT INTO "finnish" VALUES(34,52,'34:52','Ja he sanovat silloin: »Me uskomme siihen.» Mutta kuinka voivat he enää totuuteen päästä niin etäältä?');
INSERT INTO "finnish" VALUES(34,53,'34:53','Kielsiväthän he sen aikaisemmin.');
INSERT INTO "finnish" VALUES(34,54,'34:54','Ja aita asetetaan estämään heitä saavuttamasta haluamaansa, samoin kuin muinoin tehtiin heidän kaltaisilleen, jotka epäilivät.');
INSERT INTO "finnish" VALUES(35,1,'35:1','Ylistys olkoon Jumalalle, taivaiden ja maan alkuunpanijalle, enkelien luojalle, sanansaattajien, joita kahdet, kolmet ja neljät siipiparit liidättävät. Luomakunnassaan Hän kohtelee luotuja niinkuin suvaitsee; onhan Jumala totisesti kaikkivaltias.');
INSERT INTO "finnish" VALUES(35,2,'35:2','Mitä Jumala suokin ihmisille laupeudessaan, sitä ei kukaan voi estää, ja minkä Jumala pidättää, sitä ei kukaan pysty antamaan, sillä Hän on mahtava ja viisas.');
INSERT INTO "finnish" VALUES(35,3,'35:3','Ihmiset, pitäkää mielessänne Jumalan armo teitä kohtaan. Onko ketään muuta kuin Jumala, joka teille antaisi elatuksen taivaasta ja maasta käsin? Ei ole yhtään muuta jumalaa kuin Hän; miksi siis käännytte pois Hänestä?');
INSERT INTO "finnish" VALUES(35,4,'35:4','Ja jos he kutsuvat sinua petkuttajaksi, niin samoin ennen sinuakin sananjulistajia kutsuttiin, mutta Jumalan luokse kaikki asiat palaavat.');
INSERT INTO "finnish" VALUES(35,5,'35:5','Oi ihmiset, kun Jumalan lupaus on varma totuus, niin älkää salliko tämän maailman viekoitella itseänne! Älkää antako perivihollisenne pettää teitä Jumalan suhteen.');
INSERT INTO "finnish" VALUES(35,6,'35:6','Sillä onhan perkele totisesti teidän vihollisenne, ja sellaisena häntä pitäkää; hän viekoittelee puolelleen, jotta hairahtuneet joutuisivat tulen omiksi.');
INSERT INTO "finnish" VALUES(35,7,'35:7','Jotka epäuskon omaksuvat, saavat ankaran rangaistuksen, mutta jotka uskovat ja hyvää harrastavat, saavat anteeksiantamuksen ja suuren palkan.');
INSERT INTO "finnish" VALUES(35,8,'35:8','Mitä välität siitä, jonka vääryys hänen silmissään niin kaunistuu, että hän sitä hyväksi luulee. Jumala jättää harhaan kenet tahtoo ja johdattaa oikeaan kenet suvaitsee, joten älä näännytä sieluasi murehtimalla tuommoisten puolesta; kyllä Jumala totisesti heidän tekonsa tietää.');
INSERT INTO "finnish" VALUES(35,9,'35:9','Jumalahan lähettää tuulet, jotka nostavat pilvet. Sitten Me lähetämme ne kuolleeseen erämaahan ja annamme elinvoimaa kuihtuneelle maalle; samoin tapahtuu kuolleitten herättäminen.');
INSERT INTO "finnish" VALUES(35,10,'35:10','Kuka haluaa kunniaa, muistakoon, että kaikki kunnia on Jumalan. Hänen tykönsä nousevat hyvät sanat, ja hyvät teot Hän ylentää; mutta niille, jotka pahoja tekoja suunnittelevat, on Hänellä ankara rangaistus, ja heidän juonensa raukeavat.');
INSERT INTO "finnish" VALUES(35,11,'35:11','Jumala loi teidät tomusta, edelleen elämän idusta, sitten teki teidät aviopareiksi; eikä yksikään vaimo tule raskaaksi eikä synnytä Hänen tietämättään; ei liioin kenellekään elolliselle ole hänen ikäänsä pidennetty eikä lyhennetty sen kaiken olematta Jumalan kirjoissa, ja kaikki tämä on helppoa Jumalalle.');
INSERT INTO "finnish" VALUES(35,12,'35:12','Eivätkä vedet ole samanlaisia: makea vesi sammuttaa janon ja on miellyttävää juotavaksi, suolainen polttaa kirpeydellään, ja kuitenkin te kummastakin pyydystätte elävää lihaa ravinnoksenne ja koristuksia kannettavaksenne, ja laivanne kyntävät vesiä hankkiaksenne Hänen runsauttaan ja ollaksenne Hänelle kiitolliset.');
INSERT INTO "finnish" VALUES(35,13,'35:13','Hän lähettää yön peittämään päivän ja Hän lähettää päivän nousemaan yöstä; kumpikin kulkee rataansa säädetyin ajoin; sellainen on Jumala, teidän Herranne. Hänen on kuninkuus, mutta ne, joita te Hänen rinnallaan avuksenne huudatte, eivät omista oljenkorttakaan.');
INSERT INTO "finnish" VALUES(35,14,'35:14','Jos te heitä kutsutte, eivät he sitä kuule, ja vaikkapa kuulisivat, eivät he teille vastaa. Kuolleista herättämisen päivänä he kieltäytyvät jumaluudesta, joka oli vain teidän luulotteluanne. Eihän kukaan muu voi teille olevaisia niin selvittää kuin Hän, ainoa kaikkitietävä.');
INSERT INTO "finnish" VALUES(35,15,'35:15','Ihmiset, te Jumalaa tarvitsette, mutta Hän, ylistetty, on itsellensä kylliksi.');
INSERT INTO "finnish" VALUES(35,16,'35:16','Jos Hän haluaa, Hän korjaa teidät pois ja nostaa uuden ihmispolven.');
INSERT INTO "finnish" VALUES(35,17,'35:17','Ei sellainen ole vaikeata Jumalalle.');
INSERT INTO "finnish" VALUES(35,18,'35:18','Toinen ei pysty toisen taakkaa kantamaan, ja jos joku kuormansa alla huutaisi toista auttamaan, ei käy tämän sitä kantaminen, vaikkapa hän olisi läheinen sukulainen. Sinä varoitat vain niitä, jotka Herraansa salassa pelkäävät ja rukousta harjoittavat. Ken puhdistaa itseään, tekee sen oman sielunsa parhaaksi, Jumalan luo on aikanaan tultava.');
INSERT INTO "finnish" VALUES(35,19,'35:19','Sokea ei ole näkevän kaltainen,');
INSERT INTO "finnish" VALUES(35,20,'35:20','eikä pimeys valkeuden,');
INSERT INTO "finnish" VALUES(35,21,'35:21','eikä varjo helteen kaltainen.');
INSERT INTO "finnish" VALUES(35,22,'35:22','Eivät liioin elävät ja kuolleet ole toistensa veroisia. Jumala kyllä antaa kuulon kenelle vain suvaitsee. Mutta sinä et saata niille kuuloa antaa, jotka haudoissa ovat.');
INSERT INTO "finnish" VALUES(35,23,'35:23','Et ole muuta kuin varoittaja.');
INSERT INTO "finnish" VALUES(35,24,'35:24','Olemme totisesti lähettänyt sinut totuudessa viemään hyvää sanomaa ja varoitusta, eikä vielä ole elänyt kansaa, jonka tykö ei varoittajaa olisi lähetetty.');
INSERT INTO "finnish" VALUES(35,25,'35:25','Jos he sinua valehtelijaksi nimittävät, niin samoin nimittivät muinoiset kansat julistajiaan, vaikka nämä saapuivat heidän luokseen selvin todistuksin ja kirjoituksin.');
INSERT INTO "finnish" VALUES(35,26,'35:26','Silloin kuritin epäuskoisia, ja kuinka ankara olikaan rangaistukseni.');
INSERT INTO "finnish" VALUES(35,27,'35:27','Ettekö huomaa, miten Jumala vuodattaa vettä pilvistä ja siten kasvattaa erilaisia hedelmiä, ja että vuorissakin on suonia, valkoisia ja punaisia, eri vivahduksin, sekä perin mustia.');
INSERT INTO "finnish" VALUES(35,28,'35:28','Samoin on muodoiltaan erilaisia ihmisiä, eläimiä ja karjaa. Vain ne Hänen palvelijoistaan, joilla tieto on, pelkäävät Jumalaa; totisesti Jumala on mahtava, anteeksiantava.');
INSERT INTO "finnish" VALUES(35,29,'35:29','Totisesti ne, jotka julkilukevat Jumalan Kirjaa ja harjoittavat rukoilemista ja uhraavat kaikesta hyvästä, jota Me heille salaisesti tai avoimesti olemme lähettänyt, saavat panna toivonsa voittoon, joka on katoamaton.');
INSERT INTO "finnish" VALUES(35,30,'35:30','Hän on maksava heille täyden palkan ja antava heille enemmänkin armostaan; sillä Hän on laupias, ylenpalttinen palkitsija.');
INSERT INTO "finnish" VALUES(35,31,'35:31','Ja se kaikki, minkä sinulle olemme Koraanista jo ilmoittanut, on totuutta, mikä vahvistaa edelläkäyneet ilmoituksemme. Palvelijoitaan kohtaan Jumala totisesti on vaarinpitävä, kaukonäköinen.');
INSERT INTO "finnish" VALUES(35,32,'35:32','Me kyllä annoimme Pyhän Kirjan perintönä niille, jotka palvelijoittemme keskuudesta valitsimme. Mutta heidän joukossaan on niitä, jotka tuottavat turmiota sielulleen, toisia, jotka valitsevat keskitien, ja on niitä, jotka Jumalan sallimuksesta ovat ensimmäisiä kaikissa hyvissä töissä; tämä on Jumalan suuri armo.');
INSERT INTO "finnish" VALUES(35,33,'35:33','He saavat astua iäisyyden puutarhoihin, jossa he saavat rannerenkaat kullasta ja helmistä ja vaatteet silkistä.');
INSERT INTO "finnish" VALUES(35,34,'35:34','Ja he sanovat: »Kunnia Jumalalle, joka on murheemme poistanut; totisesti on Herramme anteeksiantavainen, ylenpalttinen palkitsija.');
INSERT INTO "finnish" VALUES(35,35,'35:35','Hän on armostaan ottanut meidät kotiin, jossa saamme iäti elää. Ei raatamista tule osaksemme siellä eikä väsymys meitä siellä rasita.»');
INSERT INTO "finnish" VALUES(35,36,'35:36','Mutta kieltäjille on helvetin tuli varattu. Heistä ei siellä loppua tehdä, niin että he kuolisivat, mutta ei myöskään tulen tuskaa heille lievennetä. Näin kostamme Me uskottomille.');
INSERT INTO "finnish" VALUES(35,37,'35:37','Ja sieltä he apua anovat: »Oi Herra, päästä meidät ulos, me teemme hyviä töitä, toisia kuin ennen.» Mutta emmekö Me antanut teidän elää kyllin kauan, jotta kuuliaiset totuudesta vaarin ottaisivat? Ja saapuihan luoksenne varoittaja. Siksi maistakaa tuskaa, ei ole väärintekijöille auttajaa.');
INSERT INTO "finnish" VALUES(35,38,'35:38','Totisesti Jumala tietää kaiken näkymättömän taivaassa ja maan päällä. Totisesti hän tietää kaiken, mikä sydämiin on kätketty.');
INSERT INTO "finnish" VALUES(35,39,'35:39','Hän nosti teidät valta-asemaan maassa. Sentähden jokaisen epäusko kääntyy häntä itseään vastaan, eikä kieltäjille heidän epäuskonsa mitään muuta tuota paitsi Jumalan vihaa, eikä kieltäjille heidän epäuskostaan muuta lisäänny kuin tappiota.');
INSERT INTO "finnish" VALUES(35,40,'35:40','Sano: »Mitä ajattelette suojelijoistanne, joita Jumalan veroisina avuksenne huudatte? Näyttäkää, minkä osan maata he ovat luoneet, tai onko heillä osuus taivaassa, tahi olemmeko Me heille lähettänyt Pyhän Kirjoituksen, jonka selviä totuuksia he noudattaisivat.» Ei, väärintekijät eivät pidä lupauksiaan; niitä he toisilleen antavat vain pettääkseen.');
INSERT INTO "finnish" VALUES(35,41,'35:41','Siksi Jumala taivaita ja maata ylläpitää, etteivät ne olemattomiin vaipuisi, ja jos ne luhistuisivat, ei ole ketään, joka Hänen jälkeensä voisi niitä ylläpitää; totisesti Hän on pitkämielinen, anteeksiantava.');
INSERT INTO "finnish" VALUES(35,42,'35:42','He vannoivat mitä lujimman valan Jumalan nimeen, että jos heidän keskuuteensa varoittaja saapuisi, olisi heillä parempi johdatus kuin millään kansakunnalla; mutta kun varoittaja tuli heidän tykönsä, heissä heräsi vain vastenmielisyys.');
INSERT INTO "finnish" VALUES(35,43,'35:43','Ja he esiintyivät ylpeästi maassaan sekä suunnittelivat pahaa, mutta pahat juonet eivät muita tavoita kuin suunnittelijoitaan. Mitä saattoivatkaan he muuta silloin odottaa kuin muinaisten kieltäjäin kohtaloa? Sillä Jumalan säädöksistä ei mikään horju, eikä Jumalan kaitselmus toiseksi vaihdu.');
INSERT INTO "finnish" VALUES(35,44,'35:44','Eivätkö he ole maita vaeltaneet ja niiden ihmisten loppua havainneet, jotka ennen heitä elivät, ollen heitä mahtavammat? Jumalan katseelta ei jää salaan mikään taivaassa eikä maan päällä; totisesti Hän on tietävä, mahtava.');
INSERT INTO "finnish" VALUES(35,45,'35:45','Ja jos Jumala pyrkisi rankaisemaan ihmisiä heidän ansionsa mukaan, ei jäisi maan kamaralle ainoatakaan elävää luotua, mutta Hän antaa heille armonaikaa määrähetkeen asti; ja kun Hänen tuomionsa hetki koittaa, silloin Jumala oikealla tavalla suhtautuu palvelijoihinsa.');
INSERT INTO "finnish" VALUES(36,1,'36:1','Oi ihminen!');
INSERT INTO "finnish" VALUES(36,2,'36:2','Kautta Koraanin, jonka viisaus täyttää,');
INSERT INTO "finnish" VALUES(36,3,'36:3','olethan totisesti yksi sananjulistajista');
INSERT INTO "finnish" VALUES(36,4,'36:4','ja vaellat oikealla tiellä.');
INSERT INTO "finnish" VALUES(36,5,'36:5','Tässä on ilmoitus mahtavalta, laupiaalta,');
INSERT INTO "finnish" VALUES(36,6,'36:6','jotta voisit varoittaa kansaa, jonka esi-isiä ei varoitettu ja joka on välinpitämätön.');
INSERT INTO "finnish" VALUES(36,7,'36:7','Sana on käynyt todeksi, että enin osa heistä on uskon hylännyt.');
INSERT INTO "finnish" VALUES(36,8,'36:8','Kahleet Me olemme totisesti kietonut heidän kaulansa ympäri aina heidän leukansa korkeudelle, niin että heidän päänsä ovat pystyyn pönkitetyt.');
INSERT INTO "finnish" VALUES(36,9,'36:9','Ja Me olemme asettanut esteen heidän eteensä ja esteen heidän taaksensa sekä heittänyt verhon heidän ylitsensä peittäen heidän näkönsä.');
INSERT INTO "finnish" VALUES(36,10,'36:10','Yhdentekevää on heille varoitatko heitä vai et, sillä he eivät usko.');
INSERT INTO "finnish" VALUES(36,11,'36:11','Varoittaa voi vain sitä, joka nuhteesta vaarin ottaa ja sisimmässään Armahtajaa pelkää; sellaiselle julista anteeksiantoa ja suurta palkintoa.');
INSERT INTO "finnish" VALUES(36,12,'36:12','Totisesti Me herätämme kuolleet eloon ja pidämme kirjaa kaikista heidän teoistaan, jopa heidän jalkojensa jäljistäkin; ja kaiken tämän olemme selvästi kirjoittanut.');
INSERT INTO "finnish" VALUES(36,13,'36:13','Esitä heille vertaus sen kaupungin asukkaista, johon sanansaattajamme saapuivat.');
INSERT INTO "finnish" VALUES(36,14,'36:14','Kun Me lähetimme kaksi heidän luoksensa, torjuivat he pois nämä, ja kun Me vahvistimme heitä kolmannella ja kaikki vakuuttivat: »Me olemme totisesti (Jumalan) julistajia»,');
INSERT INTO "finnish" VALUES(36,15,'36:15','Niin he sanoivat: »Ette ole muuta kuin kuolevaisia, kuten me itsekin, eikä hyvä Jumala ole mitään ilmoitustaan teille uskonut; te vain valehtelette.»');
INSERT INTO "finnish" VALUES(36,16,'36:16','Nämä vastasivat: »Herramme tietää, että varmasti olemme Hänen julistuksensa tuojia teille,');
INSERT INTO "finnish" VALUES(36,17,'36:17','eikä meillä ole muuta mielessämme kuin toimittaa perille selvä viestimme.»');
INSERT INTO "finnish" VALUES(36,18,'36:18','He sanoivat: »Me aavistamme teidän tulostanne koituvan meille pahaa; ellette lakkaa, me kivitämme teidät ja tuotamme teille tuskallisen lopun.»');
INSERT INTO "finnish" VALUES(36,19,'36:19','Nämä selittivät: »Itsestänne teistä kohtalonne johtuu eikä siitä, että me teitä varoitamme. Totisesti olette mieletöntä väkeä.»');
INSERT INTO "finnish" VALUES(36,20,'36:20','Silloin riensi sinne juosten eräs mies ja huusi: »Kansa, ota vaari viestintuojista;');
INSERT INTO "finnish" VALUES(36,21,'36:21','seuratkaa sitä, joka ei pyydä palkkiota teiltä, sillä lähettiläät ovat oikean tien vaeltajia.');
INSERT INTO "finnish" VALUES(36,22,'36:22','Mistä syystä en palvelisi Häntä, joka antoi minulle elämän? Hänen tykönsähän teidätkin jälleen palautetaan.');
INSERT INTO "finnish" VALUES(36,23,'36:23','Omaksuisinko Hänen lisäkseen jumalia, joiden suojelus ei minua vähääkään auttaisi ja jotka eivät kykenisi minua pelastamaan, jos hyvä Jumala minulle lähettäisi koettelemuksen.');
INSERT INTO "finnish" VALUES(36,24,'36:24','Silloinhan olisin ilmeisesti harhassa.');
INSERT INTO "finnish" VALUES(36,25,'36:25','Minä totisesti uskon teidän Herraanne, ja siksi kuulkaa minua!»');
INSERT INTO "finnish" VALUES(36,26,'36:26','Ja sana tuli: »Astu sisään autuuden tarhaan!» Hän sanoi: »Voi, jospa kansani tietäisi,');
INSERT INTO "finnish" VALUES(36,27,'36:27','miksi Herra antoi minulle anteeksi ja liitti minut armoitettujen joukkoon!»');
INSERT INTO "finnish" VALUES(36,28,'36:28','Ja tämän todistajan puhuttua Me emme taivaasta syössyt tämän kansan päälle enkelien rankaisevaa joukkoa; niin emme kostoamme lähetä.');
INSERT INTO "finnish" VALUES(36,29,'36:29','Ei muuta tapahtunut, kuin ääni kuului, ja katso, he vaikenivat.');
INSERT INTO "finnish" VALUES(36,30,'36:30','Voi näitä palvelijoita! Milloin sananjulistaja saapuukin heidän tykönsä, he vain pilkkaavat häntä.');
INSERT INTO "finnish" VALUES(36,31,'36:31','Eivätkö he tiedä, kuinka monta sukupolvea Me olemme jo ennen heitä tuhonnut siksi, etteivät he kääntyneet lähettiläittemme puoleen.');
INSERT INTO "finnish" VALUES(36,32,'36:32','Ja ne kaikki tuodaan varmasti Meidän eteemme.');
INSERT INTO "finnish" VALUES(36,33,'36:33','Heille olkoon tunnusmerkiksi kuollut maa. Me vuodatamme siihen eloa ja nostamme siitä esiin viljaa, jota he syövät.');
INSERT INTO "finnish" VALUES(36,34,'36:34','Ja Me kasvatamme palmupuistoja sekä viinitarhoja ja Me vedämme esille lähteitä sitä kastelemaan,');
INSERT INTO "finnish" VALUES(36,35,'36:35','jotta he söisivät sen hedelmiä sekä mitä heidän kätensä valmistaneet ovat. Eikö heidän siis tule olla kiitollisia?');
INSERT INTO "finnish" VALUES(36,36,'36:36','Ylistys Hänelle, joka loi kaiken, mitä maasta kasvaa, sekä heidän tuntemansa lajit että nekin, joita he eivät tunne.');
INSERT INTO "finnish" VALUES(36,37,'36:37','Yökin on heille tunnusmerkkinä. Yöstä vedämme päivän erilleen, ja katso, pimeys peittää heidät.');
INSERT INTO "finnish" VALUES(36,38,'36:38','Aurinkokin kulkee vain määrätyn ratansa, noudattaen kaikkivaltiaan, kaikkitietävän käskyä.');
INSERT INTO "finnish" VALUES(36,39,'36:39','Kuulle Me olemme sen vaiheet määrännyt, kunnes se muuttuu vanhan, kuivan palmunoksan näköiseksi.');
INSERT INTO "finnish" VALUES(36,40,'36:40','Eikä auringon ole sallittu kulkea kuuta nopeammin, yhtä vähän kuin yön on sallittu niellä päivää; vaan kaikki kulkee omaa rataansa.');
INSERT INTO "finnish" VALUES(36,41,'36:41','Vielä on sekin heille tunnusmerkiksi, että Me annamme laivojen kantaa heidän jälkeläisiään');
INSERT INTO "finnish" VALUES(36,42,'36:42','ja muitakin aluksia olemme heille luonut, joilla he voivat matkustaa,');
INSERT INTO "finnish" VALUES(36,43,'36:43','vaikka Me voisimme, jos tahtoisimme, hukuttaa heidät veteen, eikä heille löytyisi pelastajaa eikä pakotietä,');
INSERT INTO "finnish" VALUES(36,44,'36:44','mutta armostamme Me sallimme heidän elää määräpäivään asti.');
INSERT INTO "finnish" VALUES(36,45,'36:45','Heille sanotaan: »Varokaa sitä, mikä on teillä edessänne, ja varokaa sitä, mikä on takananne, jotta armo tulisi osaksenne.');
INSERT INTO "finnish" VALUES(36,46,'36:46','Ja heidän Herransa viesteistä tuskin yksikään heille saapuu heidän sitä hylkäämättä.');
INSERT INTO "finnish" VALUES(36,47,'36:47','Ja kun heille sanotaan: »Uhratkaa siitä, mitä Jumala on teille suonut!», niin uskottomat sanovat uskovaisille: »Miksi olisi meidän ruokittava niitä, jotka Jumala kuitenkin voisi ravita, jos vain tahtoisi? Te olette ilmeisen harhan vallassa.»');
INSERT INTO "finnish" VALUES(36,48,'36:48','Ja vielä he kysyvät: »Koska oikeastaan tulee tapahtumaan se, millä te meitä uhkaatte, jos totta puhutte?»');
INSERT INTO "finnish" VALUES(36,49,'36:49','Heitä odottaa vain äkillinen ääni, joka heidät yllättää vielä heidän väitellessään keskenänsä.');
INSERT INTO "finnish" VALUES(36,50,'36:50','Eivät he ehdi edes jälkisäädöstänsä tehdä eivätkä perheittensä luo kiiruhtaa.');
INSERT INTO "finnish" VALUES(36,51,'36:51','Ja kun pasuunoihin puhalletaan, niin, katso, he rientävät haudoistaan Herransa luo.');
INSERT INTO "finnish" VALUES(36,52,'36:52','Ja he sanovat: »Voi meitä, kuka on meidät nostanut nukkumasijoiltamme ? Tämänhän Armahtaja meille lupasi, ja Hänen sanansaattajansa ovat julistaneet totuutta.»');
INSERT INTO "finnish" VALUES(36,53,'36:53','Ja vain ääni kajahtaa, ja katso, heidät kaikki tuodaan Meidän eteemme,');
INSERT INTO "finnish" VALUES(36,54,'36:54','eikä sinä päivänä tehdä vähääkään vääryyttä ainoallekaan sielulle; teille punnitaan vain sen mukaan, kuin tekonne ansaitsivat.');
INSERT INTO "finnish" VALUES(36,55,'36:55','Sinä päivänä ovat paratiisin asukkaat autuaallisissa toimissaan.');
INSERT INTO "finnish" VALUES(36,56,'36:56','Vaimojensa seurassa he ovat varjossa leväten istuimillaan.');
INSERT INTO "finnish" VALUES(36,57,'36:57','Siellä nauttivat he hedelmiä ja heillä on kaikkea, mitä haluavat.');
INSERT INTO "finnish" VALUES(36,58,'36:58','»Rauhaa!» on heille laupiaan Herran tervehdys.');
INSERT INTO "finnish" VALUES(36,59,'36:59','Mutta te, väärintekijät, väistykää loitolle tänä päivänä!');
INSERT INTO "finnish" VALUES(36,60,'36:60','Enkö Minä velvoittanut teitä, Aadamin lapset, olemaan palvelematta perkelettä? Totisesti hän on teidän ilmivihollisenne.');
INSERT INTO "finnish" VALUES(36,61,'36:61','Ja enkö sen sijaan käskenyt palvelemaan Minua ollaksenne oikealla tiellä.');
INSERT INTO "finnish" VALUES(36,62,'36:62','Onhan perkele totisesti johtanut harhaan lukuisia sieluja joukostanne. Kuinka ette sittenkään älynneet?');
INSERT INTO "finnish" VALUES(36,63,'36:63','Tämä on helvetti, jolla teitä uhattiin.');
INSERT INTO "finnish" VALUES(36,64,'36:64','Käykää nyt siihen tänä päivänä epäuskonne tähden.');
INSERT INTO "finnish" VALUES(36,65,'36:65','Ja sinä päivänä sinetöimme Me heidän suunsa, mutta heidän kätensä tulevat meille huitoen puhumaan sekä jalkansa heidän ansaitsemastaan todistamaan.');
INSERT INTO "finnish" VALUES(36,66,'36:66','Ja jos Me tahdomme, voimme puhkaista heidän silmänsä; silloin vasta he pyrkisivät löytämään tien, mutta kuinka he näkisivät?');
INSERT INTO "finnish" VALUES(36,67,'36:67','Samoin voisimme varmasti, jos tahtoisimme, heidät siihen paikkaan jähmettää, niin etteivät he voisi edemmäksi päästä eivätkä takaisin palata.');
INSERT INTO "finnish" VALUES(36,68,'36:68','Jos Me jollekulle suomme pitkän iän, Me annamme hänen vartensa köyristyä; eivätkö he ymmärrä?');
INSERT INTO "finnish" VALUES(36,69,'36:69','Emmekä ole hänelle (profeetalle) runoilua opettanut, sillä hänelle se ei ole soveliasta. Hänen sanottavansa on vain varoitus ja selvä Koraani.');
INSERT INTO "finnish" VALUES(36,70,'36:70','Hänen on varoitettava jokaista, joka elää haluaa, ja julistetun sanan on osoittauduttava voimakkaaksi uskottomia vastaan.');
INSERT INTO "finnish" VALUES(36,71,'36:71','Eivätkö he havaitse, että Me olemme luonut heille karjan luontokappaleittemme joukosta ja totuttanut sen heidän alaisuuteensa?');
INSERT INTO "finnish" VALUES(36,72,'36:72','Ja me olemme pannut kotieläimet heitä tottelemaan, niin että he muutamilla ajavat ja muutamia käyttävät ruoaksensa.');
INSERT INTO "finnish" VALUES(36,73,'36:73','Tästä kaikesta heille on etua ja he saavat niistä juomiakin; eivätkö he osaa tästäkään kiittää?');
INSERT INTO "finnish" VALUES(36,74,'36:74','Mutta he ovat omaksuneet itselleen jumalia ainoan Jumalan rinnalle, saadakseen heiltä apua.');
INSERT INTO "finnish" VALUES(36,75,'36:75','Mutta nämä eivät kykene heitä auttamaan, vaikka niitä on laumoittain.');
INSERT INTO "finnish" VALUES(36,76,'36:76','Tästä syystä älä anna heidän puheensa itseäsi huolestuttaa; Me tiedämme, mitä he tekevät salaa ja mitä toimivat julkisesti.');
INSERT INTO "finnish" VALUES(36,77,'36:77','Eikö ihminen ole huomannut, että Me olemme luonut hänet pienestä elonidusta? Ja katso, kohta hän on valmis kanssamme kiistelemään.');
INSERT INTO "finnish" VALUES(36,78,'36:78','Ja hän laittaa Meidän kaltaisiksemme tarkoitettuja kuviaan unohtaen oman luomisensa. Hän sanoo: »Kuka pystyy elämän paluttamaan luihin, kun ne jo ovat lahonneet?»');
INSERT INTO "finnish" VALUES(36,79,'36:79','Sano: »Hän niihin elämän palauttaa, joka ne alkujaankin elämään synnytti, sillä Hän sulkee itseensä koko luomakunnan.');
INSERT INTO "finnish" VALUES(36,80,'36:80','Hän, joka loi teille tulen, joka voi vihreässä puussakin palaa, niin että sytykkeillä sitä jatkaa saatatte.');
INSERT INTO "finnish" VALUES(36,81,'36:81','Eikö Hän, joka loi taivaat ja maan, kykene niiden kaltaisia luomaan? Totisesti! Hänhän on kaiken Luoja, taituri.');
INSERT INTO "finnish" VALUES(36,82,'36:82','Kun Hän jotakin aikoo, riittää, että Hän käskee: »Tulkoon!» niin se tulee.');
INSERT INTO "finnish" VALUES(36,83,'36:83','Sentähden ylistys olkoon Hänelle, jonka käsissä on valta kaiken yli ja jonka tykö kaikki palautetaan aikanaan.');
INSERT INTO "finnish" VALUES(37,1,'37:1','Julistukseni vetoaa niihin, jotka rivistöinä ulos marssivat,');
INSERT INTO "finnish" VALUES(37,2,'37:2','sekä niihin, jotka mielenlujuudella itsensä hillitsevät');
INSERT INTO "finnish" VALUES(37,3,'37:3','ja niihin, jotka (Koraania) julkilukevat Jumalaa muistaen,');
INSERT INTO "finnish" VALUES(37,4,'37:4','totisesti Jumalanne on ainoa Jumala,');
INSERT INTO "finnish" VALUES(37,5,'37:5','taivaiden ja maan Herra sekä kaiken, mikä niiden välisessä avaruudessa on, niidenkin maanäärien Herra, joista päivä nousee.');
INSERT INTO "finnish" VALUES(37,6,'37:6','Me olemme koristanut lähimmän taivaan laen tähdillä,');
INSERT INTO "finnish" VALUES(37,7,'37:7','ja suoja on asetettu jokaista kapinallista henkeä vastaan.');
INSERT INTO "finnish" VALUES(37,8,'37:8','He eivät siedä (muslimien) seurakunnan rukoilemista eivätkä eri tahoilta tulevia arvosteluja,');
INSERT INTO "finnish" VALUES(37,9,'37:9','vaan he joutuvat ankaran rangaistuksen alaisiksi,');
INSERT INTO "finnish" VALUES(37,10,'37:10','ja jos kenen heistä onnistuu harhauttaa joitakuita pois, valaisee kirkas (profeetan viisauden) leimahdus hänen työnsä jäljet.');
INSERT INTO "finnish" VALUES(37,11,'37:11','Vaatikaa heitä vastaamaan, hekö vankemmin ovat luotuja vai ne, jotka Me olemme luonut. Totisesti Me loimme (omamme) vahvasta aineesta.');
INSERT INTO "finnish" VALUES(37,12,'37:12','Ihmeeksesi he vain pilkkaavat sinua');
INSERT INTO "finnish" VALUES(37,13,'37:13','ja varoittavista muistutuksista he eivät välitä,');
INSERT INTO "finnish" VALUES(37,14,'37:14','nähdessään tunnusmerkin he yllyttävät toinen toistaan ivaan');
INSERT INTO "finnish" VALUES(37,15,'37:15','sanoen: »Eihän tämä ole muuta kuin ilmeistä noituutta.');
INSERT INTO "finnish" VALUES(37,16,'37:16','Pitäisikö uskoa siihenkin, että kuoltuamme ja luiksi sekä tomuksi muututtuamme meidät muka varmasti eloon herätetään,');
INSERT INTO "finnish" VALUES(37,17,'37:17','vieläpä muka muinaiset esi-isämmekin?»');
INSERT INTO "finnish" VALUES(37,18,'37:18','Sano: Aivan niin, ja hylkiöiksi silloin joudutte');
INSERT INTO "finnish" VALUES(37,19,'37:19','ja, katso, saatte osaksenne kirouksen.');
INSERT INTO "finnish" VALUES(37,20,'37:20','Silloin voihkitte: »Voi meitä! Tämä on pahan palkkamme päivä! »');
INSERT INTO "finnish" VALUES(37,21,'37:21','Totisesti onkin tämä tuomionpäivä, jota te valheeksi väititte.');
INSERT INTO "finnish" VALUES(37,22,'37:22','Kootkaa yhteen vääryyttä harjoittaneet ja heidän kumppaninsa sekä kaikki ne, joita he palvelivat');
INSERT INTO "finnish" VALUES(37,23,'37:23','Jumalan vertaisina, ja viekää heidät helvetin tielle,');
INSERT INTO "finnish" VALUES(37,24,'37:24','mutta pysäyttäkää heidät, sillä heiltä kysytään:');
INSERT INTO "finnish" VALUES(37,25,'37:25','»Miksi ette nyt auta toisianne?»');
INSERT INTO "finnish" VALUES(37,26,'37:26','Kyllä he sinä päivänä alistuvat');
INSERT INTO "finnish" VALUES(37,27,'37:27','ja jotkut heistä lähestyvät toisia kysellen toinen toiseltaan');
INSERT INTO "finnish" VALUES(37,28,'37:28','ja sanovat: »Tehän aikanaan tulitte luoksemme oikeauskoisina.»');
INSERT INTO "finnish" VALUES(37,29,'37:29','Toiset vastaavat: »Ei, te ette olleet uskovaisia,');
INSERT INTO "finnish" VALUES(37,30,'37:30','eikä meillä ollut teidän suhteenne valtaa, sillä te olitte kuritonta väkeä.');
INSERT INTO "finnish" VALUES(37,31,'37:31','Nyt on Herramme tuomio langennut meille ja saamme totisesti maistaa rangaistusta,');
INSERT INTO "finnish" VALUES(37,32,'37:32','sillä saatoimmehan me teidät harhaan, kun itse olimme harhassa.»');
INSERT INTO "finnish" VALUES(37,33,'37:33','Näin joutuvat he sinä päivänä osallisiksi rangaistuksesta.');
INSERT INTO "finnish" VALUES(37,34,'37:34','Niin me totisesti menettelemme väärintekijäin suhteen.');
INSERT INTO "finnish" VALUES(37,35,'37:35','Totisesti he olivat pöyhkeitä, kun heille sanottiin: »Ei ole muita jumalia kuin Jumala.»');
INSERT INTO "finnish" VALUES(37,36,'37:36','Ja he sanovat: »Meidänkö todella pitäisi luopua jumalistamme mielipuolen runoilijan tähden?»');
INSERT INTO "finnish" VALUES(37,37,'37:37','Sano: »Ei, hän toi mukanaan totuuden ja vahvisti entisten sananjulistajien sanat.»');
INSERT INTO "finnish" VALUES(37,38,'37:38','Nyt saatte totisesti maistaa tuskallista rangaistusta,');
INSERT INTO "finnish" VALUES(37,39,'37:39','eikä teitä tällöin muusta rangaista kuin omista teoistanne.');
INSERT INTO "finnish" VALUES(37,40,'37:40','Toisin on Jumalan palvelijain, puhdistettujen, laita.');
INSERT INTO "finnish" VALUES(37,41,'37:41','Heille annetaan luvattu osansa,');
INSERT INTO "finnish" VALUES(37,42,'37:42','he nauttivat hyvien töittensä hedelmiä ja heidät korotetaan kunniaan,');
INSERT INTO "finnish" VALUES(37,43,'37:43','autuuden tarhoissa');
INSERT INTO "finnish" VALUES(37,44,'37:44','he istuvat kunniaistuimilla, katsoen toistensa kasvoihin,');
INSERT INTO "finnish" VALUES(37,45,'37:45','ja malja pannaan kiertämään kädestä käteen täynnä raikkainta lähdevettä,');
INSERT INTO "finnish" VALUES(37,46,'37:46','kirkasta, ihanaa nautittavaksi.');
INSERT INTO "finnish" VALUES(37,47,'37:47','Siinä ei ole sameutta eikä se heiltä ehdy.');
INSERT INTO "finnish" VALUES(37,48,'37:48','Ja heidän seurassaan on oleva neitsyitä, jotka siveydessään hillitsevät kauniiden silmiensä katseet');
INSERT INTO "finnish" VALUES(37,49,'37:49','kuin olisivat heidän silmänsä hellästi varjeltavia linnun munia.');
INSERT INTO "finnish" VALUES(37,50,'37:50','He tekevät toisilleen kysymyksiä.');
INSERT INTO "finnish" VALUES(37,51,'37:51','Joku heidän joukostaan sanoo: »Totisesti, olihan minulla ystävä,');
INSERT INTO "finnish" VALUES(37,52,'37:52','joka minulta kyseli: ''Kuulutko sinäkin niihin, jotka uskovat sen todeksi?');
INSERT INTO "finnish" VALUES(37,53,'37:53','Viedäänkö meidät todellakin tuomiolle sitten, kun olemme kuolleet ja muuttuneet luiksi ja tomuksi?''»');
INSERT INTO "finnish" VALUES(37,54,'37:54','Ja puhuva jatkaa: »Katso alas!»');
INSERT INTO "finnish" VALUES(37,55,'37:55','Ja tämä katsoo alas ja näkee ystävänsä keskellä helvettiä.');
INSERT INTO "finnish" VALUES(37,56,'37:56','Ja hän huudahtaa: »Jumalan nimessä, vähällä olit saattaa minut turmioon,');
INSERT INTO "finnish" VALUES(37,57,'37:57','sillä ellei Jumala olisi armahtanut laupeudessaan, olisin varmasti joutunut kadotettujen joukkoon.');
INSERT INTO "finnish" VALUES(37,58,'37:58','Eikö meidän siis todellakaan tarvitse kuolla');
INSERT INTO "finnish" VALUES(37,59,'37:59','kuin kerran ja eikö meitä kuriteta?');
INSERT INTO "finnish" VALUES(37,60,'37:60','Totisesti tämä on ylin autuus.');
INSERT INTO "finnish" VALUES(37,61,'37:61','Tällaisen saavuttamiseksi antakaamme kaikkien ponnistella.');
INSERT INTO "finnish" VALUES(37,62,'37:62','Eikö olekin tämä osa parempi kuin niiden, jotka saavat ravintonsa Zakkuum-puusta?»');
INSERT INTO "finnish" VALUES(37,63,'37:63','Tämän Me olemme totisesti tehnyt koettelemukseksi väärintekijöille,');
INSERT INTO "finnish" VALUES(37,64,'37:64','puun, joka kasvaa helvetin pohjasta,');
INSERT INTO "finnish" VALUES(37,65,'37:65','ja sen hedelmät ovat kuin käärmeiden päät.');
INSERT INTO "finnish" VALUES(37,66,'37:66','Tästä puusta he totisesti saavat syödä ja vatsansa täyttää,');
INSERT INTO "finnish" VALUES(37,67,'37:67','ja yhtä varmasti saavat he sen jälkeen juoda kiehuvaa vettä');
INSERT INTO "finnish" VALUES(37,68,'37:68','ja sitten palata taas helvettiin.');
INSERT INTO "finnish" VALUES(37,69,'37:69','Totisesti he aikanaan huomasivat esi~isiensä kulkevan harhaan');
INSERT INTO "finnish" VALUES(37,70,'37:70','ja riensivät kuitenkin seuraamaan heidän jälkiään.');
INSERT INTO "finnish" VALUES(37,71,'37:71','Useimmat sukupolvet ennen heitä kulkivat harhaan,');
INSERT INTO "finnish" VALUES(37,72,'37:72','ja Me kuitenkin lähetimme varoittajia heidän keskuuteensa.');
INSERT INTO "finnish" VALUES(37,73,'37:73','Mutta katsokaa, mikä loppu oli varoitusten saajilla');
INSERT INTO "finnish" VALUES(37,74,'37:74','paitsi Jumalan vilpittömillä palvelijoilla heidän keskuudessaan.');
INSERT INTO "finnish" VALUES(37,75,'37:75','Nooa huusi Meitä avukseen ja koki, kuinka Me rukouksiin vastaamme.');
INSERT INTO "finnish" VALUES(37,76,'37:76','Me vapahdimme hänet seuralaisineen valtavasta hävityksestä');
INSERT INTO "finnish" VALUES(37,77,'37:77','tehden hänen jälkeläisistään suvun jatkajia.');
INSERT INTO "finnish" VALUES(37,78,'37:78','Me ikuistimme hänen maineensa myöhemmissä sukupolvissa,');
INSERT INTO "finnish" VALUES(37,79,'37:79','rauha olkoon Nooalle kaikissa heimoissa.');
INSERT INTO "finnish" VALUES(37,80,'37:80','Näin Me totisesti palkitsemme hyvää tekeviä.');
INSERT INTO "finnish" VALUES(37,81,'37:81','Hän oli totisesti uskollinen palvelijamme.');
INSERT INTO "finnish" VALUES(37,82,'37:82','Muut me hukutimme veteen.');
INSERT INTO "finnish" VALUES(37,83,'37:83','Aabraham kuului totisesti Nooan jälkeläisiin.');
INSERT INTO "finnish" VALUES(37,84,'37:84','Hän lähestyi Herraansa puhtain sydämin');
INSERT INTO "finnish" VALUES(37,85,'37:85','ja sitten puhui isälleen sekä heimolleen: »Ketä palvelette?');
INSERT INTO "finnish" VALUES(37,86,'37:86','Itseännekö tahdotte pettää väärillä jumalilla?');
INSERT INTO "finnish" VALUES(37,87,'37:87','Mikä on ajatuksenne kaikkien maailmojen valtiaasta?»');
INSERT INTO "finnish" VALUES(37,88,'37:88','Sitten hän katsoi tähtiin');
INSERT INTO "finnish" VALUES(37,89,'37:89','ja sanoi: »En voi sietää teidän tähtienpalvontaanne.»');
INSERT INTO "finnish" VALUES(37,90,'37:90','Silloin he käänsivät selkänsä ja poistuivat hänen luotaan.');
INSERT INTO "finnish" VALUES(37,91,'37:91','Ja hän salaa lähestyi heidän epäjumaliaan ivaten niitä: »Ettekö syödäkään taida?');
INSERT INTO "finnish" VALUES(37,92,'37:92','Miksi ette puhu?»');
INSERT INTO "finnish" VALUES(37,93,'37:93','Sitten hän hiipi niiden kimppuun ja iski niitä oikealla kädellään.');
INSERT INTO "finnish" VALUES(37,94,'37:94','Tällöin hänen heimolaisensa kiireesti lähestyivät häntä');
INSERT INTO "finnish" VALUES(37,95,'37:95','ja hän sanoi: »Sellaisiako te jumalinanne palvelette, jotka itse olette veistäneet?');
INSERT INTO "finnish" VALUES(37,96,'37:96','Jumalahan on luonut teidät ja teidän tekeleenne.»');
INSERT INTO "finnish" VALUES(37,97,'37:97','Nämä sanoivat: »Pystyttäkäämme hänelle paalu ja heittäkäämme hänet tuleen!»');
INSERT INTO "finnish" VALUES(37,98,'37:98','Ja he alkoivat vainota häntä, mutta Me pidimme heidät alallaan.');
INSERT INTO "finnish" VALUES(37,99,'37:99','Ja Aabraham sanoi: »Minä pakenen Herrani tykö, sillä Hän ei minua jätä.');
INSERT INTO "finnish" VALUES(37,100,'37:100','Herra, anna minulle hyvän asian jatkaja!»');
INSERT INTO "finnish" VALUES(37,101,'37:101','Ja Me annoimme lupauksen pojasta, jolla on kärsivällisyyttä.');
INSERT INTO "finnish" VALUES(37,102,'37:102','Mutta kun tämä varttui ja kykeni auttamaan häntä, sanoi hän: »Poikani, olen totisesti unessa saanut käskyn uhrata sinut, mikä on ajatuksesi siitä?» Tämä sanoi: »Oi isäni, tee saamasi käskyn mukaan; jos niin on Jumalan tahto, tulet havaitsemaan minut kärsivälliseksi.»');
INSERT INTO "finnish" VALUES(37,103,'37:103','Kun he nyt kumpikin alistuivat ja isä heitti pojan maahan kasvoilleen,');
INSERT INTO "finnish" VALUES(37,104,'37:104','kutsuimme Me häntä nimeltä sanoen: »Aabraham!');
INSERT INTO "finnish" VALUES(37,105,'37:105','Olet antanut unesi käydä toteen, ja Me varmasti palkitsemme niitä, jotka hyvää tekevät.»');
INSERT INTO "finnish" VALUES(37,106,'37:106','Tämä oli totisesti ilmeinen koetus.');
INSERT INTO "finnish" VALUES(37,107,'37:107','Ja Me annoimme hänelle lunastuksen arvokkaalla sijaisuhrilla.');
INSERT INTO "finnish" VALUES(37,108,'37:108','Ja Me ikuistimme hänen maineensa jälkipolvissa näillä sanoilla:');
INSERT INTO "finnish" VALUES(37,109,'37:109','»Rauha olkoon Aabrahamille!»');
INSERT INTO "finnish" VALUES(37,110,'37:110','Näin palkitsemme Me hyvää tekevät.');
INSERT INTO "finnish" VALUES(37,111,'37:111','Hän oli todella Meidän uskollinen palvelijamme.');
INSERT INTO "finnish" VALUES(37,112,'37:112','Ja Me annoimme hänelle hyvän lupauksen Iisakista, josta oli tuleva profeetta Jumalaa pelkääväisille.');
INSERT INTO "finnish" VALUES(37,113,'37:113','Ja Me annoimme siunauksemme vuotaa hänelle sekä Iisakille. Ja heidän jälkeläisissään on sellaisia, jotka hyvää tekevät, mutta myöskin sellaisia, jotka ilmeisesti vahingoittavat omaa sieluaan.');
INSERT INTO "finnish" VALUES(37,114,'37:114','Me ulotimme tosisuosiomme myös Moosekseen ja Aaroniin');
INSERT INTO "finnish" VALUES(37,115,'37:115','ja pelastimme heidät ja heidän kansansa suuresta tuhosta');
INSERT INTO "finnish" VALUES(37,116,'37:116','ja autoimme heitä pääsemään voitolle');
INSERT INTO "finnish" VALUES(37,117,'37:117','sekä annoimme heille molemmille Kirjan, joka saattoi asiat selvyyteen.');
INSERT INTO "finnish" VALUES(37,118,'37:118','Myös johdatimme heidät oikealle tielle');
INSERT INTO "finnish" VALUES(37,119,'37:119','ja siunasimme heidän nimensä jälkipolvissa näillä sanoilla:');
INSERT INTO "finnish" VALUES(37,120,'37:120','»Rauha olkoon Moosekselle ja Aaronille!»');
INSERT INTO "finnish" VALUES(37,121,'37:121','Näin Me palkitsemme hyvää tekevät.');
INSERT INTO "finnish" VALUES(37,122,'37:122','Totisesti he kumpikin olivat uskollisia palvelijoitamme.');
INSERT INTO "finnish" VALUES(37,123,'37:123','Myös Elias totisesti kuului sanamme julistajiin.');
INSERT INTO "finnish" VALUES(37,124,'37:124','Hän sanoi kansalleen: »Kuinka ette karta pahaa?');
INSERT INTO "finnish" VALUES(37,125,'37:125','Kuinka saatattekin huutaa Baalia avuksenne ja hyljätä ylhäisen Luojanne,');
INSERT INTO "finnish" VALUES(37,126,'37:126','Jumalan, joka on Herranne ja esi-isienne Herra ammoisista ajoista.');
INSERT INTO "finnish" VALUES(37,127,'37:127','Mutta he kutsuivat häntä pimittäjäksi, ja siitä syystä heitä varmasti kuritetaan,');
INSERT INTO "finnish" VALUES(37,128,'37:128','ei kuitenkaan Jumalaa pelkääväisiä, puhdistuneita, heidän keskuudessaan.');
INSERT INTO "finnish" VALUES(37,129,'37:129','Ja Me siunasimme hänen nimensä jälkipolvissa näillä sanoilla:');
INSERT INTO "finnish" VALUES(37,130,'37:130','»Rauha olkoon Eliaalle!»');
INSERT INTO "finnish" VALUES(37,131,'37:131','Tällä tavoin palkitsemme Me hyvää tekeviä.');
INSERT INTO "finnish" VALUES(37,132,'37:132','Totisesti hän kuului uskollisiin palvelijoihimme.');
INSERT INTO "finnish" VALUES(37,133,'37:133','Samoin Loot oli totinen sanamme julistaja.');
INSERT INTO "finnish" VALUES(37,134,'37:134','Kun Me pelastimme hänen sukunsa, kaikki');
INSERT INTO "finnish" VALUES(37,135,'37:135','paitsi vanhan vaimon, joka kuului viivyttelijöihin,');
INSERT INTO "finnish" VALUES(37,136,'37:136','tuhosimme Me toiset.');
INSERT INTO "finnish" VALUES(37,137,'37:137','Varmaan olette (heidän tuhonsa paikkojen ohi matkustaneet aamuisin');
INSERT INTO "finnish" VALUES(37,138,'37:138','tai iltaisin. Ettekö siis ota niistä vaaria?');
INSERT INTO "finnish" VALUES(37,139,'37:139','Joona myös oli totisesti sananjulistaja.');
INSERT INTO "finnish" VALUES(37,140,'37:140','Kun hän pakeni lastattuun laivaan,');
INSERT INTO "finnish" VALUES(37,141,'37:141','sai hän heidän kanssaan kokea merihätää ja joutui mereen heitetyksi.');
INSERT INTO "finnish" VALUES(37,142,'37:142','Mutta kala nieli hänet, kun hän oli veden vallassa.');
INSERT INTO "finnish" VALUES(37,143,'37:143','Ja ellei hän olisi kuulunut niihin, jotka Meitä ylistävät,');
INSERT INTO "finnish" VALUES(37,144,'37:144','hän olisi jäänyt sen vatsaan yhteiseen ylösnousemukseen asti.');
INSERT INTO "finnish" VALUES(37,145,'37:145','Mutta Me heitimme hänet merestä autiolle rannalle, ja hän oli sairas.');
INSERT INTO "finnish" VALUES(37,146,'37:146','Ja Me annoimme kurpitsan kasvaa hänen autiolle paikalleen');
INSERT INTO "finnish" VALUES(37,147,'37:147','sekä lähetimme hänet sataantuhanteen nousevan kansan luo, joka eli irstaasti,');
INSERT INTO "finnish" VALUES(37,148,'37:148','ja he tulivat uskoon, minkä tähden Me myönsimme heille armonaikaa.');
INSERT INTO "finnish" VALUES(37,149,'37:149','Kysy nyt heiltä, Muhammed, onko Herrallasi tyttäriä siten kuin heillä on poikia,');
INSERT INTO "finnish" VALUES(37,150,'37:150','tai loimmeko Me heidän silmiensä nähden enkelit naisiksi?');
INSERT INTO "finnish" VALUES(37,151,'37:151','Tietysti on pelkkää heidän omaa valhettaan, kun he väittävät,');
INSERT INTO "finnish" VALUES(37,152,'37:152','että Jumala muka on synnyttänyt lapsia. Totisesti he ovat valehtelijoita!');
INSERT INTO "finnish" VALUES(37,153,'37:153','Ja Hänkö olisi itselleen valinnut tyttäriä mieluummin kuin poikia?');
INSERT INTO "finnish" VALUES(37,154,'37:154','Miten on teidän laitanne? Kuinka voittekaan tuollaista itseksenne päätellä?');
INSERT INTO "finnish" VALUES(37,155,'37:155','Ettekö ota totuutta huomioon?');
INSERT INTO "finnish" VALUES(37,156,'37:156','Vai onko teillä selvä todistus?');
INSERT INTO "finnish" VALUES(37,157,'37:157','Esittäkää sitten kirjoituksenne, jos olette tosissanne.');
INSERT INTO "finnish" VALUES(37,158,'37:158','Vielä väittivät he Jumalan olevan sukua dzinnien kanssa, vaikka itse dzinnit tietävät varmasti saavansa rangaistuksen.');
INSERT INTO "finnish" VALUES(37,159,'37:159','Kunnia olkoon Jumalalle! Hän on kaukana kaikesta, mitä he Hänestä väittävät,');
INSERT INTO "finnish" VALUES(37,160,'37:160','eivät kuitenkaan vilpittömät palvelijat.');
INSERT INTO "finnish" VALUES(37,161,'37:161','Sillä te, jotka tiedätte, ketä palvelette,');
INSERT INTO "finnish" VALUES(37,162,'37:162','ette saata johdattaa ketään tottelemattomuuteen Häntä vastaan,');
INSERT INTO "finnish" VALUES(37,163,'37:163','paitsi helvettiin menossa olevia.');
INSERT INTO "finnish" VALUES(37,164,'37:164','Ei meissä muslimeissa ole ketään, jolla ei olisi määrättyä paikkaansa,');
INSERT INTO "finnish" VALUES(37,165,'37:165','olemmehan totisesti niitä, jotka rivistöinä ulos marssivat,');
INSERT INTO "finnish" VALUES(37,166,'37:166','ja niitä, jotka Jumalan kunniaa julistavat.');
INSERT INTO "finnish" VALUES(37,167,'37:167','Totisesti heillä oli tapana sanoa:');
INSERT INTO "finnish" VALUES(37,168,'37:168','»Jospa olisitte meitä varoittaneet,');
INSERT INTO "finnish" VALUES(37,169,'37:169','meistä olisi varmasti tullut Jumalan vilpittömiä palvelijoita.»');
INSERT INTO "finnish" VALUES(37,170,'37:170','Mutta kun he nyt jäävät epäuskoonsa, niin saavat kokea sen seuraukset.');
INSERT INTO "finnish" VALUES(37,171,'37:171','Onhan Meidän varma sanamme jo julistettu palvelijoillemme, lähettiläille,');
INSERT INTO "finnish" VALUES(37,172,'37:172','heitä totisesti tulemme auttamaan,');
INSERT INTO "finnish" VALUES(37,173,'37:173','ja varmasti Meidän sotajoukkomme on voitokas.');
INSERT INTO "finnish" VALUES(37,174,'37:174','Tästä syystä käänny pois heistä joksikin aikaa.');
INSERT INTO "finnish" VALUES(37,175,'37:175','Mutta tarkasta heitä, ja totisesti he saavat nähdä.');
INSERT INTO "finnish" VALUES(37,176,'37:176','Vai tahtoisivatko he vielä jouduttaakin Meidän kuritustamme');
INSERT INTO "finnish" VALUES(37,177,'37:177','Mutta kun se kerran heitä kohtaa, niin ankea on aamu silloin heille, joita niin varoitettiin.');
INSERT INTO "finnish" VALUES(37,178,'37:178','Käänny siis pois heistä ajaksi.');
INSERT INTO "finnish" VALUES(37,179,'37:179','Mutta tarkasta heitä, ja totisesti he saavat nähdä.');
INSERT INTO "finnish" VALUES(37,180,'37:180','Ylistys olkoon Herrallenne, kunnian Kuninkaalle, joka on yläpuolella kaiken, mitä he Hänestä väittävät.');
INSERT INTO "finnish" VALUES(37,181,'37:181','Ja rauha olkoon sananjulistajille.');
INSERT INTO "finnish" VALUES(37,182,'37:182','Ja kaikki kunnia kuuluu Jumalalle, maailmojen valtiaalle.');
INSERT INTO "finnish" VALUES(38,1,'38:1','Saad. Kautta Koraanin, ylevän todistajan!');
INSERT INTO "finnish" VALUES(38,2,'38:2','Totisesti epäuskoiset ovat omahyväisiä ja itsepintaisia!');
INSERT INTO "finnish" VALUES(38,3,'38:3','Kuinka monta sukupolvea jo ennen heitä Me hävitimmekään! Mutta he parkuivat vasta kun pelastuksen aika jo oli ohi.');
INSERT INTO "finnish" VALUES(38,4,'38:4','Nyt he taas kummeksuvat, että varoittaja on heille tullut heidän omasta keskuudestaan, ja epäuskoiset sanovat: »Tämä on valehtelija, taikuri.');
INSERT INTO "finnish" VALUES(38,5,'38:5','Eikö hän pyri vähentämään jumalia yhdeksi ainoaksi? Kummallista hän toimittelee todellakin.»');
INSERT INTO "finnish" VALUES(38,6,'38:6','Ja johtavat henkilöt heidän keskuudessaan ratkaisevat pulman sanomalla: »Menkää ja pysykää uskollisina entisille jumalillenne, ei tämä hänen yrityksensä onnistu.');
INSERT INTO "finnish" VALUES(38,7,'38:7','Emme koskaan ole tällaista kuulleet isiemme uskossa, tämä on pelkkää väärentämistä.');
INSERT INTO "finnish" VALUES(38,8,'38:8','Onko hän meidän keskuudessamme saanut ilmoituksensa, joiden perusteella hän meitä varoittaa?»');
INSERT INTO "finnish" VALUES(38,9,'38:9','He epäilevät minun varoitustani. He eivät ole maistaneet vielä Minun kuritustani. Heidänkö kädessään ovat Herrani, mahtavan, anteliaan, armonaarteet?');
INSERT INTO "finnish" VALUES(38,10,'38:10','Heidänkö on taivaan ja maan ja niiden välisen avaruuden valtakunta, niin että he voivat paisua rikkaudessaan.');
INSERT INTO "finnish" VALUES(38,11,'38:11','Tällä paikalla tullaan ajamaan pakosalle (muslimeja vastaan) liittoutuneiden vahvat joukot.');
INSERT INTO "finnish" VALUES(38,12,'38:12','Niinhän ennen heitä Nooan, Aadin ja faraon, sotajoukkojen valtiaan, kansat torjuivat pois luotaan Meidän sananjulistajamme,');
INSERT INTO "finnish" VALUES(38,13,'38:13','samoin Tamuud, Lootin kansa ja viidakkojen asukkaat.');
INSERT INTO "finnish" VALUES(38,14,'38:14','Kaikki he kohtelivat sananjulistajiani valehtelijoina; siksi rankaisinkin heitä oikeuden mukaisesti.');
INSERT INTO "finnish" VALUES(38,15,'38:15','Nämäkin viivyttelevät itsepäisesti siksi, kunnes tuomiopäivän huuto kajahtaa, eikä silloin ole yhtään armonaikaa.');
INSERT INTO "finnish" VALUES(38,16,'38:16','Ja he sanovat: »Herra! Joudu mittaamaan meille osamme (rangaistuksesta jo maallisessa elämässä) ennen tuomiopäivää!»');
INSERT INTO "finnish" VALUES(38,17,'38:17','Siedä kärsivällisesti (Muhammed) heidän puheitaan ja muistele palvelijaamme Daavidia, jolle me annoimme voimaa ja joka oli altis kuulemaan ääntämme.');
INSERT INTO "finnish" VALUES(38,18,'38:18','Siksi teimmekin Me vuoret hänelle alamaisiksi julistamaan Jumalan kunniaa illansuussa ja aamun sarastaessa.');
INSERT INTO "finnish" VALUES(38,19,'38:19','Ja linnut kokoontuivat hänen luokseen kuuliaisina.');
INSERT INTO "finnish" VALUES(38,20,'38:20','Me lujitimme hänen valtakuntansa antaen hänelle viisautta ja selvän arvostelukyvyn.');
INSERT INTO "finnish" VALUES(38,21,'38:21','Onko korviinne tullut kertomus siitä, kuinka kaksi riitapuolta nousi hänen huoneeseensa kiiveten yli muurien?');
INSERT INTO "finnish" VALUES(38,22,'38:22','Kun he ilmestyivät Daavidin eteen ja tämä heitä pelästyi, sanoivat he: »Älä pelkää, tässä on kaksi riitapuolta, joista toinen on tehnyt vääryyttä toiselle. Tuomitse siis oikein meidän asiamme, älä rankaise väärin, vaan saata meidät oikeuksiimme.');
INSERT INTO "finnish" VALUES(38,23,'38:23','Tässä on minun veljeni, hänellä oli yhdeksänkymmentä yhdeksän karitsaa ja minulla yksi ainoa. Mutta hän vaati: ''Luovuta se minulle'' - ja hän sai minut suostumaan.»');
INSERT INTO "finnish" VALUES(38,24,'38:24','Daavid sanoi: »Totisesti hän oli väärässä vaatiessaan sinun karitsasi omiensa lisäksi. Varmaan enin osa asiapuolista menettelee väärin toisiaan kohtaan, paitsi ne, jotka uskovat ja hyvää tekevät, mutta harvoin on sellaisia.» Mutta Daavid pääsi selvyyteen siitä, että Me olimme tällä koetellut häntä itseään, ja hän pyysi Herralta anteeksiantoa, lankesi kasvoilleen ja kumartaen yhä uudelleen teki katumusta.');
INSERT INTO "finnish" VALUES(38,25,'38:25','Sentähden Me annoimme hänelle anteeksi ja hän pääsi nauttimaan Meidän läheisyydestämme ylhäisissä asunnoissa.');
INSERT INTO "finnish" VALUES(38,26,'38:26','»Daavid, Me olemme nostanut sinut hallitsijaksi maassasi. Tuomitse siis ihmisiä oikeudenmukaisesti äläkä seuraa mielihalujasi, jotteivät ne johtaisi sinua harhaan Jumalan tieltä. Ne, jotka eksyvät Jumalan tieltä, saavat totisesti kärsiä ankaran rangaistuksen, sillä he ovat tilintekopäivän unohtaneet.»');
INSERT INTO "finnish" VALUES(38,27,'38:27','Emme ole turhaan luonut taivaita ja maata ja kaikkea niiden välillä. Vain epäuskoisilla on sellainen luulo. Mutta voi niitä, jotka eivät usko, sillä he joutuvat tulen kuritukseen!');
INSERT INTO "finnish" VALUES(38,28,'38:28','Vai olisiko meidän kohdeltava uskovaisia, hyvää tekeviä samoin kuin pahuuden levittäjiä maan päällä? Tai rinnastaisimmeko jumalaapelkääväiset väärintekijöihin?');
INSERT INTO "finnish" VALUES(38,29,'38:29','Olemme sinulle (Muhammmed) antanut ylhäältä Pyhän Kirjan, joka on runsaaksi siunaukseksi, jotta he voisivat miettiä sen jakeita ja jotta ymmärtäväiset ottaisivat vaarin.');
INSERT INTO "finnish" VALUES(38,30,'38:30','Daavidille annoimme Salomon, josta tuli altis palvelijamme. Hän totisesti kääntyi kaikessa Meidän puoleemme.');
INSERT INTO "finnish" VALUES(38,31,'38:31','Kun hänen eteensä illalla (rukouksien aikaan) tuotiin kilpahevosia, jotka seistessään ovat alallaan, mutta juostessaan kiitävät nopeasti,');
INSERT INTO "finnish" VALUES(38,32,'38:32','sanoi hän: »Herraa kiittäessäni mieleni iloitsee näistä hyvistä hevosista; ja kun hevoset olivat kiitäneet kilpaa hänen näkyvistään, hän sanoi:');
INSERT INTO "finnish" VALUES(38,33,'38:33','»Tuokaa ne takaisin luokseni!» Ja hän alkoi taputtaa niiden jalkoja ja kaulaa.');
INSERT INTO "finnish" VALUES(38,34,'38:34','Ja Me lähetimme Salomolle koettelemuksia. Kun Me annoimme perilliseksi hänen valtaistuimelleen pelkän tomumajan, hän huusi Herraansa,');
INSERT INTO "finnish" VALUES(38,35,'38:35','sanoen: »Herra, armahda minua ja anna minulle valtakunta, joka ei joudu kenellekään kelvottomalle perinnöksi; Sinä olet totisesti armossasi antelias.»');
INSERT INTO "finnish" VALUES(38,36,'38:36','Me teimme tuulenkin hänen käskyläisekseen. Se totteli hänen tahtoaan ja puhalsi lauhasti milloin hän halusi.');
INSERT INTO "finnish" VALUES(38,37,'38:37','Hänen alamaisiaan olivat myös taiturit, kaikki rakentajat, sukeltajat');
INSERT INTO "finnish" VALUES(38,38,'38:38','ja muut kahleissa olevat.');
INSERT INTO "finnish" VALUES(38,39,'38:39','»Tämä on Meidän vapaa lahjamme, josta voit tuhlata tai pidättää ilman tilintekoa.»');
INSERT INTO "finnish" VALUES(38,40,'38:40','Ja hän pääsi ylhäisiin asuntoihin nauttimaan Meidän läheisyydestämme.');
INSERT INTO "finnish" VALUES(38,41,'38:41','Muistakaa myös palvelijaamme Jobia, joka huusi Herralleen: »Saatana on lyönyt minua vaivalla ja tuskalla!»');
INSERT INTO "finnish" VALUES(38,42,'38:42','(Ja Herra vastasi:) »Polje jalallasi maahan, ja siitä nousee viileä lähde peseytyäksesi ja sammuttaaksesi janosi!»');
INSERT INTO "finnish" VALUES(38,43,'38:43','Ja Me annoimme hänelle takaisin perheensä ja yhtä monta lisäksi armonosoituksenamme ja tunnusmerkkinä ymmärtäväisille.');
INSERT INTO "finnish" VALUES(38,44,'38:44','»Ota haltuusi jonkun verran maallista omaisuutta ja tyydy siihen halaamatta sitä, mikä pettää.» Me havaitsimme Jobin todella kelvolliseksi palvelijaksemme. Herkeämättä kääntyi hän rukoillen Meidän puoleemme.');
INSERT INTO "finnish" VALUES(38,45,'38:45','Muistakaa myös palvelijoitamme Aabrahamia, Iisakkia ja Jaakobia, miehiä, joilla oli voimaa ja viisautta.');
INSERT INTO "finnish" VALUES(38,46,'38:46','Totisesti Me kasvatamme heidät puhtaaksi koko olemukseltaan ja alinomaa pitämään silmällä lopullista määränpäätään.');
INSERT INTO "finnish" VALUES(38,47,'38:47','Ja totisesti he pääsivätkin Meidän tykömme valittuina, parhaina.');
INSERT INTO "finnish" VALUES(38,48,'38:48','Muistakaa Ismaelia, Elisaa ja Hesekieliä, jotka myös parhaitten joukkoon kuuluivat.');
INSERT INTO "finnish" VALUES(38,49,'38:49','Tämä olkoon muistutukseksi ja lupaukseksi, että ylhät asunnot odottavat jumalaapelkääväisiä,');
INSERT INTO "finnish" VALUES(38,50,'38:50','iäisyyden tarhat, joihin ovet ovat heille avoinna,');
INSERT INTO "finnish" VALUES(38,51,'38:51','joissa he saavat leväten aterioida, nauttien kaikenlaisia hedelmiä ja juomia.');
INSERT INTO "finnish" VALUES(38,52,'38:52','Heidän seuraansa liittyvät heidän ikäisensä naiset, jotka siveästi katseensa hillitsevät.');
INSERT INTO "finnish" VALUES(38,53,'38:53','Kaikki tämä luvataan teille tuomionpäiväksi.');
INSERT INTO "finnish" VALUES(38,54,'38:54','Voitte olla varmat näistä meidän lahjoistamme, jotka eivät konsanaan ehdy.');
INSERT INTO "finnish" VALUES(38,55,'38:55','Näin on oleva, mutta yhtä varmasti on valmistettu surkea tyyssija pahoille,');
INSERT INTO "finnish" VALUES(38,56,'38:56','helvetti, johon he saavat astua, kamala kitumispaikka.');
INSERT INTO "finnish" VALUES(38,57,'38:57','Näin pitää olla, heidän on annettava maistaa sitä ja myös kiehuvaa juomaa');
INSERT INTO "finnish" VALUES(38,58,'38:58','sekä muita yhtä tuskallisia rangaistuksia.');
INSERT INTO "finnish" VALUES(38,59,'38:59','Sinne syöksyy päistikkaa ihmispaljous teidän mukananne, eikä kajahda heille tervehdys, kun he tuleen joutuvat.');
INSERT INTO "finnish" VALUES(38,60,'38:60','Nämä sanovat nyt houkuttelijoilleen: »Ei, teitä emme tahdo tuntea, te tämän surkean paikan meille valmistitte.»');
INSERT INTO "finnish" VALUES(38,61,'38:61','He sanovat myös: »Herra, sille, joka tämän meille valmisti, lisää kaksinkertaisiksi tulen tuskat.»');
INSERT INTO "finnish" VALUES(38,62,'38:62','Vielä ihmettelevät he: »Miksi emme täällä näe ihmisiä, joita pidimme paheellisina?');
INSERT INTO "finnish" VALUES(38,63,'38:63','Aiheettako me heitä ylenkatsoimme, vai ovatko silmämme nyt heistä poispäin kääntyneet?»');
INSERT INTO "finnish" VALUES(38,64,'38:64','Sellainen on riitelevien tulen asukkaiden todellinen tila.');
INSERT INTO "finnish" VALUES(38,65,'38:65','Sano: »Minä olen vain varoittaja. Ei ole muuta jumalaa kuin Jumala, ainoa, kaikkivaltias,');
INSERT INTO "finnish" VALUES(38,66,'38:66','taivaiden ja maan ja niiden välisen avaruuden Herra, mahtava, armahtavainen.»');
INSERT INTO "finnish" VALUES(38,67,'38:67','Sano: »Tämä on tärkeä sanoma,');
INSERT INTO "finnish" VALUES(38,68,'38:68','mutta kuitenkin te käännytte poispäin.»');
INSERT INTO "finnish" VALUES(38,69,'38:69','Minulla (Muhammedilla) ei ollut tietoa ylhäisistä valtiaista, kun heidän välillään syntyi kiistaa,');
INSERT INTO "finnish" VALUES(38,70,'38:70','eihän minulle muuta ilmoitettu, kuin että minut lähetettiin varoittajaksi.»');
INSERT INTO "finnish" VALUES(38,71,'38:71','Kun Herra sanoi enkeleille: »Totisesti, tahdon luoda kuolevaisen ihmisen maan tomusta,');
INSERT INTO "finnish" VALUES(38,72,'38:72','ja kun olen tehnyt hänet täydelliseksi sekä puhaltanut häneen henkeni, langetkaa kasvoillenne luvaten hänelle kuuliaisuutta! »');
INSERT INTO "finnish" VALUES(38,73,'38:73','niin enkelit ilmaisivat kuuliaisuutensa, kaikki tyynni,');
INSERT INTO "finnish" VALUES(38,74,'38:74','ei kuitenkaan Iblis, joka oli ylpeä ja kuului uskottomiin.');
INSERT INTO "finnish" VALUES(38,75,'38:75','Jumala sanoi: »Iblis, mikä esti sinua ilmaisemasta kuuliaisuuttasi hänelle, jonka olen omilla käsilläni luonut? Oletko ylpeä vai oletko niin korkealle korotettu?»');
INSERT INTO "finnish" VALUES(38,76,'38:76','Tämä vastasi: »Olen parempi kuin hän: Sinä olet luonut minut tulesta, ja hänet loit maan tomusta.»');
INSERT INTO "finnish" VALUES(38,77,'38:77','Ja Herra sanoi: »Silloin mene tiehesi, sillä totisesti karkoitan sinut,');
INSERT INTO "finnish" VALUES(38,78,'38:78','ja minun kiroukseni langetkoon ylitsesi tuomiopäivään asti.»');
INSERT INTO "finnish" VALUES(38,79,'38:79','Hän sanoi: »Herra, anna minulle aikaa siihen päivään saakka, kunnes heidät uuteen elämään herätetään!»');
INSERT INTO "finnish" VALUES(38,80,'38:80','Herra sanoi: »Olkoon sinulla aikaa');
INSERT INTO "finnish" VALUES(38,81,'38:81','siihen hetkeen asti, joka on tarkoin määrätty.»');
INSERT INTO "finnish" VALUES(38,82,'38:82','Hän sanoi: »Sinulta saamallani voimalla minä totisesti saatan heidät lankeamaan kaikki tyynni,');
INSERT INTO "finnish" VALUES(38,83,'38:83','paitsi vilpittömät palvelijasi heidän keskuudessaan.»');
INSERT INTO "finnish" VALUES(38,84,'38:84','Herra sanoi: »Totuuden nimessä, jonka nyt julistan,');
INSERT INTO "finnish" VALUES(38,85,'38:85','Minä totisesti täytän teillä helvetin, heittäen sinne sinut sekä kaikki ne, jotka sinua seuraavat.»');
INSERT INTO "finnish" VALUES(38,86,'38:86','Sano: »En pyydä teiltä mitään palkkiota, enkä kuulu niihin, jotka teeskentelevät.');
INSERT INTO "finnish" VALUES(38,87,'38:87','Tämä on vain varoitus kansoille,');
INSERT INTO "finnish" VALUES(38,88,'38:88','ja aivan varmaan tulette kaiken kokemaan aikanaan.»');
INSERT INTO "finnish" VALUES(39,1,'39:1','Pyhän Kirjan ilmoitus on tullut mahtavalta, viisaalta Jumalalta.');
INSERT INTO "finnish" VALUES(39,2,'39:2','Me olemme kaikessa totuudessa sinulle ilmoittanut Pyhän Kirjan, joten sinun on palveltava Jumalaa vilpittömässä kuuliaisuudessa Häntä kohtaan.');
INSERT INTO "finnish" VALUES(39,3,'39:3','Tämä vilpitön kuuliaisuus kohdistukoon ainoastaan Jumalaan, mitä taasen niihin tulee, jotka pitävät itsellään Jumalan vertaisia suojelijoita sanoen: »Me palvelemme vain niitä, jotta ne lähentäisivät meitä Jumalaan», Jumala kyllä totisesti heidän riitansa ratkaisee; varma on, ettei Jumala johdata oikeaan valehtelijoita eikä kiittämättömiä.');
INSERT INTO "finnish" VALUES(39,4,'39:4','Jos Jumala olisi halunnut hankkia pojan itselleen, Hän olisi varmasti valinnut niistä luomistaan, joihin Hänellä on mielisuosio. Kunnia olkoon yksin Hänelle! Hän on Jumala, ainoa, kaikkivaltias.');
INSERT INTO "finnish" VALUES(39,5,'39:5','Hän on luonut taivaat ja maan totuuden voimalla. Hän antaa yön verhota päivän ja päivän yllättää yön; Hän on luonut auringon ja kuun palvelemaan itseään, kumpikin kiitää aikansa kohti määränpäätä; Hän on todellakin valtava, suuri anteeksiantamuksessaan.');
INSERT INTO "finnish" VALUES(39,6,'39:6','Hän on luonut teidät kaikki yhdestä olennosta antamalla tälle veroisensa parikseen. Hän on antanut teille kahdeksan elukkaa pareina karjaksenne. Hän antaa teille elämän äitienne kohduissa; tämä on luomistyötä jälkeen luomisen, kolminkertaisessa pimeydessä; sellainen on Jumala, teidän Herranne, Hänen on kuninkuus, ei ole muuta jumalaa kuin Hän, miksi siis kääntyisitte poispäin?');
INSERT INTO "finnish" VALUES(39,7,'39:7','Jos olette kiittämättömiä, niin onhan Jumala ylenpalttisesti itselleen riittävä, tarvitsematta teiltä hituistakaan. Hän ei pidä palvelijainsa kiittämättömyydestä. Mutta jos te olette kiitollisia, Häntä ilahduttaa tämä ominaisuutenne, eikä kukaan joudu toisen kuormaa kantamaan; Herranne tykö on teidän palattava, ja Hän silloin luettelee teille tekonne, sillä Hän totisesti tietää kaiken, mitä povessanne piilee.');
INSERT INTO "finnish" VALUES(39,8,'39:8','Ja kun ahdinko piirittää ihmisen, tämä huutaa Herraansa avukseen kääntyen alinomaa Hänen puoleensa; mutta kun Hän sitten antaa ihmiselle suosiotaan, tämä unohtaa sen, jonka vuoksi hän Häneltä taannoin apua anoi, ja asettaa Jumalan rinnalle kilpailijoita johdattaen siten ihmisiä harhaan Hänen tieltään. Sano: »Nauttikaa nyt vähän aikaa elämästä kiittämättömyydessänne, olettehan kuitenkin tulen omat!»');
INSERT INTO "finnish" VALUES(39,9,'39:9','Vai saisitteko saman osan kuin se, joka palvelee Herraa yönkin tunteina, langeten kasvoilleen tai rukoillen seisoaltaan, joka pitää mielessään tulevaa elämää ja luottaa Herransa armoon? Sano: »Ovatko tietävät ja tietämättömät samanarvoisia? Vain ymmärtäväiset ottavat vaarin.»');
INSERT INTO "finnish" VALUES(39,10,'39:10','Sano: »Palvelijani, te, jotka uskossa vaellatte, täyttäkää tarkoin velvollisuutenne Herraanne kohtaan: Hän on hyvä niille, jotka tässä maailmassa hyvää harrastavat, ja Jumalan maanääret ovat laajat; vain kärsivälliset palkitaan täyden ansionsa mukaan, mittailematta.');
INSERT INTO "finnish" VALUES(39,11,'39:11','Sano: »Minun on käsketty palvella Jumalaa vilpittömässä kuuliaisuudessa,');
INSERT INTO "finnish" VALUES(39,12,'39:12','ja minun on myös käsketty olla ensimmäinen muslimeista.»');
INSERT INTO "finnish" VALUES(39,13,'39:13','Sano: »Jos olisin tottelematon Herralleni, saisin pelätä murhepäivän kuritusta.»');
INSERT INTO "finnish" VALUES(39,14,'39:14','Sano: »Yksin Jumalaa minä palvelen ja olen vilpitön kuuliaisuudessani.»');
INSERT INTO "finnish" VALUES(39,15,'39:15','Palvelkaa siis Hänen rinnallaan ketä mielenne tekee! Sano: »Ne vasta hävinneitä ovat, jotka ylösnousemuspäivänä itsensä ja perheensä kadottavat; se on totisesti ilmeinen häviö.»');
INSERT INTO "finnish" VALUES(39,16,'39:16','Tuli on oleva peitteenä heidän yllänsä, ja tuli levittäytyy heidän alustakseen; niin peloittava on Jumalan varoitus palvelijoilleen; ottakaa siis tarkoin vaari velvollisuuksistanne Minua kohtaan, palvelijana.');
INSERT INTO "finnish" VALUES(39,17,'39:17','Niille, jotka pidättyvät epäjumalien palvelemisesta ja kääntyvät ainoastaan Jumalan puoleen, on varattu hyvä sanoma. Julista siis ilosanomaa Minun palvelijoilleni,');
INSERT INTO "finnish" VALUES(39,18,'39:18','niille, jotka kuulevat sanani ja sitten parhaansa mukaan sitä toteuttavat; sellaisilla on oleva Jumalan johdatus, sellaiset ovat ymmärtäväisiä.');
INSERT INTO "finnish" VALUES(39,19,'39:19','Vai ajatteletko niiden osaa, joita vastaan on kuritustuomio valmiina. Miten voisitkaan pelastaa ketään tulen omaa?');
INSERT INTO "finnish" VALUES(39,20,'39:20','Mutta niille, jotka hartaasti täyttävät velvollisuutensa Jumalaa kohtaan, on varattu ylhäiset sijat, joiden yläpuolellakin avautuu heille valmistettuja sijoja ja joiden alapuolella virtaavat vedet; tällainen on Jumalan lupaus, eikä Jumala lupaustaan petä.');
INSERT INTO "finnish" VALUES(39,21,'39:21','Etkö ole huomannut, kuinka Jumala lähettää vettä ylhäältä pilvistä, sitten antaa sen virrata maahan, taas nousta pinnalle lähteinä; ja veden mukana ilmestyy kaikenlaista vihantaa, joka sitten lakastuu, niin että näet sen kellastuvan; vihdoin Hän antaa sen mureta ja hajota. Tässä on todellakin muistutus ymmärtäväisille.');
INSERT INTO "finnish" VALUES(39,22,'39:22','Luuletteko, että se, jonka sydämen Jumala on avannut Islaamille, niin että hän on tullut Herransa valon levittäjäksi, on kovasydämisten kaltainen. Mutta voi niitä, joiden sydämet ovat paatuneet eivätkä Jumalaa muistele; he ovat ilmeisessä harhassa.');
INSERT INTO "finnish" VALUES(39,23,'39:23','Jumala on ilmoittanut parhaan sanomansa, Kirjan, jonka eri osat toisiinsa sopeutuvat ja josta väliin luetaan sellaista, mikä panee jumalaapelkääväisten ihon väreilemään ja sitten heidän sydämensä heltyen Jumalaa mietiskelemään; sellainen on Jumalan johdatus, Hän ohjaa sillä ketä suvaitsee, ja kenet Jumala jättää harhaan, sille ei löydy opasta.');
INSERT INTO "finnish" VALUES(39,24,'39:24','Minkä arvoinen silloin on se, jonka täytyy ylösnousemuspäivänä omin neuvoin varjella itseään kaameata kuritusta vastaan? Silloin väärintekijöille sanotaan: »Saakaa se, minkä ansaitsitte! »');
INSERT INTO "finnish" VALUES(39,25,'39:25','Heidän edeltäjänsäkin, jotka myös torjuivat luotansa profeetat, yllätti kuritus taholta, jota he eivät osanneet arvata.');
INSERT INTO "finnish" VALUES(39,26,'39:26','Siten pani Jumala heidät maistamaan ahdistusta jo tässäkin elämässä, mutta tulevassa on heille varmasti varattu vielä suurempi kuritus; jospa he sen vain oivaltaisivat!');
INSERT INTO "finnish" VALUES(39,27,'39:27','Tässä Koraanissa Me totisesti olemme esittänyt ihmisille kaikenlaatuisia vertauksia, jotta he voisivat käsittää.');
INSERT INTO "finnish" VALUES(39,28,'39:28','Tämä on arabiankielinen Koraani, ilman mitään kaarteluja, jotta he osaisivat karttaa pahaa.');
INSERT INTO "finnish" VALUES(39,29,'39:29','Jumala esittää vertauksen: Tuossa on ihminen, joka on liittynyt useampiin keskenään eripuraisiin seuralaisiin, ja tässä on ihminen, joka on kokonaan omistanut itsensä yhdelle. Onko näiden kahden tila samanlainen? Ei, kunnia olkoon Jumalalle; mutta enin osa ei tätä tajua.');
INSERT INTO "finnish" VALUES(39,30,'39:30','Mutta kuolema tulee teille ja yhtä varmasti heille,');
INSERT INTO "finnish" VALUES(39,31,'39:31','ja ylösnousemuksen päivänä ratkaistaan kiistanne Herran edessä.');
INSERT INTO "finnish" VALUES(39,32,'39:32','Ken onkaan enemmän väärässä kuin se, joka väittää valheellista Jumalasta ja joka vääristelee totuuden, kun se hänelle ilmoitetaan; eikö muka helvetissä riitä tilaa sellaisille uskottomille?');
INSERT INTO "finnish" VALUES(39,33,'39:33','Mutta ne, jotka tuovat totuuden ja jotka totuutena sen vastaanottavat, nämä ovat pahaa karttavia.');
INSERT INTO "finnish" VALUES(39,34,'39:34','Herransa luona heillä tulee olemaan mitä he haluavat; sellainen on palkkio hyvää tekeville;');
INSERT INTO "finnish" VALUES(39,35,'39:35','Jumala ei lue heidän syykseen pahinta heidän tekemästään, vaan antaa heille palkinnon parhaasta, minkä he tekivät.');
INSERT INTO "finnish" VALUES(39,36,'39:36','Eikö Jumalassa ole kyllin hänen palvelijalleen? Sellaisillako he yrittävät teitä peloitella, joita he Jumalan asemesta palvelevat? Kenet Jumala päästää harhaan, hänelle ei opasta löydy.');
INSERT INTO "finnish" VALUES(39,37,'39:37','Mutta joka elää Jumalan johdatuksessa, häntä ei kukaan saata eksyttää. Eikö Jumala ole mahtava, kaikki palkitseva Herra?');
INSERT INTO "finnish" VALUES(39,38,'39:38','Jos te heiltä kysytte, kuka on luonut taivaat ja maan, he varmaan vastaavat: »Jumala.» Sano: »Ettekö niin ollen voi päätellä, mahtaisivatko ne, joita Jumalan sijasta kumarratte, torjua vahingon, jos Jumala tahtoisi ihmistä vahingoittaa, tai mahtaisivatko he estää Hänen armonsa tulemista, jos Jumala suvaitsisi armoaan osoittaa? Sano: »Minulle on Jumalassani kyllin. Häneen luottavat luottavaiset.»');
INSERT INTO "finnish" VALUES(39,39,'39:39','Sano: »Kansalaiseni, tehkää työtä kukin paikallanne, minäkin olen työni tekevä, ja te tulette oivaltamaan,');
INSERT INTO "finnish" VALUES(39,40,'39:40','kuka kuuluu niihin, joita kohtaa masentava kuritus, ja kuka on loppumattoman rangaistuksen ansainnut.»');
INSERT INTO "finnish" VALUES(39,41,'39:41','Kun Me totisesti olemme sinulle ilmoitusten kautta antanut Pyhän Kirjan ja siinä totuuden ihmisiä varten, niin ken ikinä noudattaa oikeata tietä, hän tekee sen oman sielunsa parhaaksi, ja ken ikinä harhailee, harhailee omaksi turmiokseen, etkä sinä sellaisista ole vastuussa.');
INSERT INTO "finnish" VALUES(39,42,'39:42','Jumala ottaa huostaansa joka ihmisen sielun hänen kuollessaan, ja sellaisilta, joiden aika ei ole kuolla, heidän nukkuessaan. Niiden sielut Hän pidättää, joiden Hän on kuolla määrännyt, ja muiden sielut palauttaa Hän määrähetkeen asti. Tässä on tunnusmerkki ihmisille, jotka mietiskelevät.');
INSERT INTO "finnish" VALUES(39,43,'39:43','Entä ne, jotka ovat Jumalan ohella itselleen puolustajia omaksuneet. Sano: »Ne eivät ole ikinä vallinneet eivätkä mitään ymmärrä.»');
INSERT INTO "finnish" VALUES(39,44,'39:44','Sano: »Jumala yksin kykenee puolustamaan, Hänen on taivasten ja maan valtakunta, Hänen luokseen teidän on palattava.»');
INSERT INTO "finnish" VALUES(39,45,'39:45','Kun Jumalan nimi vain mainitaan, niin tulevan elämän kieltäjäin sydämet kouristuvat, mutta kun niistä puhutaan, joita he Hänen sijastaan kumartavat, katso, se heitä ilahduttaa.');
INSERT INTO "finnish" VALUES(39,46,'39:46','Sano: »Oi, Jumala! Taivaiden ja maan alkuunpanija, silmiltä salatun ja näkyväisten tietäjä! Vain Sinä voit tuomita palvelijoitasi siinä, missä he ovat eri mieltä.»');
INSERT INTO "finnish" VALUES(39,47,'39:47','Vaikkapa väärintekijät omistaisivat kaiken, mitä maailmassa on, ja saman verran lisää, he varmasti tarjoisivat sen lunnaiksi pelastuakseen pahalta, jolla heitä kuritetaan ylösnousemuksen päivänä, ja sen, mikä ei koskaan heidän mieleensä juolahtanut, Jumala nyt ilmaisee heille.');
INSERT INTO "finnish" VALUES(39,48,'39:48','Heidän tekojensa pahat seuraukset selkenevät heille, ja juuri se, mitä he pilkkanaan pitivät, on heitä kohtaava.');
INSERT INTO "finnish" VALUES(39,49,'39:49','Kun isku kohtaa ihmistä, hän huutaa Meitä avukseen: mutta kun Me annamme hänelle armoamme, hän sanoo: »Minä sain sen tietoni avulla.» Ei, häntä vain koetellaan, mutta enin osa heistä ei tajua sitä.');
INSERT INTO "finnish" VALUES(39,50,'39:50','Ennen heitä eläneet myös sanoivat samaa, mutta se, mitä he aikaansaivat, ei heitä auttanut.');
INSERT INTO "finnish" VALUES(39,51,'39:51','Sen sijaan heille jäi pahat seuraukset ansioistaan; kaikille väärintekijöille jäävät pahat seuraukset heidän ansaitsemakseen kuormaksi, eivätkä he sitä voi välttää.');
INSERT INTO "finnish" VALUES(39,52,'39:52','Eivätkö he tiedä, että Jumala kenelle tahtoo enentää elinvaroja ja myös niitä supistaa. Nämä ovat varmoja tunnusmerkkejä uskoville ihmisille.');
INSERT INTO "finnish" VALUES(39,53,'39:53','Sano: »Te palvelijani, jotka olette toimineet sielujenne vahingoksi, älkää olko epätoivoisia Jumalan laupeuden suhteen; aivan varmasti Jumala antaa syntinne kokonaan anteeksi, sillä Hän on totisesti armahtava, laupias.');
INSERT INTO "finnish" VALUES(39,54,'39:54','Mutta kääntykää Herranne puoleen yhä uudelleen ja alistukaa kuuliaisuudessa, ennenkuin kuritus teitä kohtaa, jolloin teitä ei auteta.');
INSERT INTO "finnish" VALUES(39,55,'39:55','Ja noudattakaa parhainta, minkä Herranne ylhäältä on teille ilmoittanut, ennenkuin rangaistus tulee niin äkkiä, että sitä ette edes huomaa,');
INSERT INTO "finnish" VALUES(39,56,'39:56','jottei kukaan joutuisi sanomaan: ''Voi minua, mitä minä laiminlöinkään Jumalaa kohtaan! Varmasti kuuluin minäkin niihin, jotka pilkkasivat!''');
INSERT INTO "finnish" VALUES(39,57,'39:57','Tai ettei kukaan sanoisi: ''Jospa Jumala olisi minua johdattanut, olisin varmasti kuulunut pahaa karttavien lukuun.''');
INSERT INTO "finnish" VALUES(39,58,'39:58','Tai sanoisi nähdessään kurituksen: ''Jospa vain minun sallittaisiin palata maan päälle, kuuluisin hyvää harrastaviin.''');
INSERT INTO "finnish" VALUES(39,59,'39:59','Toden totta, Minun sanomani saavuttivat teidät, mutta te torjuitte ne, olitte ylpeitä ja epäuskoisia.');
INSERT INTO "finnish" VALUES(39,60,'39:60','Ylösnousemuspäivänä olette näkevä ne. jotka Jumalasta valheita puhuivat: heidän kasvonsa mustuvat. Eikö helvetissä riittäisi tilaa ylpeille?');
INSERT INTO "finnish" VALUES(39,61,'39:61','Mutta ne, jotka pahaa karttavat, Jumala vapahtaa, paha ei voi heihin tarttua eikä heidän tarvitse murehtia.');
INSERT INTO "finnish" VALUES(39,62,'39:62','Jumala on kaiken luonut ja pitää kaiken vallassaan.');
INSERT INTO "finnish" VALUES(39,63,'39:63','Hänen ovat taivaiden ja maan aarteet, mutta kaikkensa menettävät ne, jotka eivät usko Jumalan ilmoituksiin.');
INSERT INTO "finnish" VALUES(39,64,'39:64','Sano: »Neuvotteko minua palvelemaan muita kuin Jumalaa, te tietämättömät ihmiset?»');
INSERT INTO "finnish" VALUES(39,65,'39:65','Onhan sinulle ilmoitettu, kuten edeltäjillesikin, että jos sinä ketään muuta paitsi Jumalaa julistat, koko työsi raukeaa tyhjiin ja itse olet varmasti hukassa.');
INSERT INTO "finnish" VALUES(39,66,'39:66','Ei, palvelkaa ainoastaan Jumalaa ja olkaa kiitollisia.');
INSERT INTO "finnish" VALUES(39,67,'39:67','Mutta he eivät ole palvelleet Jumalaa Hänelle tulevalla kunnioituksella, vaikka ylösnousemuspäivänä koko maa on Hänen käsissään ja Hän käärii taivaat oikeaan käteensä. Ylistys olkoon Hänelle, olkoon Hän kohotettu yli kaikkien, joita he Hänen rinnalleen asettavat.');
INSERT INTO "finnish" VALUES(39,68,'39:68','Ja kun pasuunaan puhalletaan, niin kaikki taivaassa ja kaikki maan päällä menevät tainnoksiin paitsi Jumalan vahvistamat; sitten puhalletaan uudestaan, ja katso, he nousevat odottaen.');
INSERT INTO "finnish" VALUES(39,69,'39:69','Ja maa säteilee Herransa valkeutta, ja Pyhä Kirja otetaan esille, niinikään profeetat ja todistajat, ja tuomio langetetaan ihmisille oikeudenmukaisesti, ei heitä väärin tuomita.');
INSERT INTO "finnish" VALUES(39,70,'39:70','Ja joka sielulle maksetaan palkkansa kaikesta, mitä se on tehnyt; Hän tietää kaikki heidän tekonsa.');
INSERT INTO "finnish" VALUES(39,71,'39:71','Ja ne, jotka uskon kielsivät, ajetaan joukoittain helvettiin, kunnes portit avataan ja vartijat heille sanovat: »Eikö teidän luoksenne tullut sananjulistajia omasta keskuudestanne lausuen teille Herransa ilmoituksia ja varoittaen teitä tämän päivän saapumisesta?» He sanovat: »Kyllä.» Mutta olihan uskon kieltäjille valmiina rangaistustuomio.');
INSERT INTO "finnish" VALUES(39,72,'39:72','Ja heille sanotaan: »Käykää helvetin porteista sisään siellä ikuisesti kitumaan!» Kurja on ylpeiden olinpaikka.');
INSERT INTO "finnish" VALUES(39,73,'39:73','Mutta ne, jotka täyttivät Herransa tahdon, opastetaan myöskin joukoittain paratiisiin, jonka portit avautuvat heidän saapuessaan, ja vartijat sanovat heille: »Rauha olkoon teille, te onnelliset, astukaa sisälle ja asukaa täällä ikuisesti!»');
INSERT INTO "finnish" VALUES(39,74,'39:74','Ja he sanovat: »Kaikki kunnia olkoon Jumalan, Hän on hyvyydessään toteuttanut meille lupauksensa ja antanut meidän periä maan, ja me saamme asua paratiisissa missä vain haluamme.» Niin ylenpalttinen on palkkio työnsä tehneille.');
INSERT INTO "finnish" VALUES(39,75,'39:75','Ja sinä saat nähdä enkelien kiertävän valtaistuimen ympäri veisaten ylistystä Herralleen. Kaikki tuomitaan oikeudenmukaisesti ja sanotaan: »Kaikki kunnia olkoon Jumalan, maailmojen valtiaan!»');
INSERT INTO "finnish" VALUES(40,1,'40:1','Ylistetty, kunnian Jumala.');
INSERT INTO "finnish" VALUES(40,2,'40:2','Kirjoituksen on ilmoittanut Jumala, jonka on mahti ja tieto,');
INSERT INTO "finnish" VALUES(40,3,'40:3','joka antaa vikamme anteeksi ja kuulee katumuksemme, rankaisee ankarasti, Jalo Herra; ei ole muuta Jumalaa kuin Hän, Hänen luokseen kaikki palaa.');
INSERT INTO "finnish" VALUES(40,4,'40:4','Jumalan ilmoituksista kiistelevät vain ne, jotka eivät usko: älä sentähden anna sen johtaa sinua harhaan, että he menestyvät maailmassa.');
INSERT INTO "finnish" VALUES(40,5,'40:5','Ennen heitä Nooan kansa ja jälkeentulevat sukupolvet hylkäsivät profeetat; joka kansalla on ollut surmanhankkeita sananjulistajiaan vastaan, ja ne ovat taistelleet valheen avulla tehdäkseen tyhjäksi totuuden. Siksi tuhosin heidät, millainen olikaan silloin Minun kostoni!');
INSERT INTO "finnish" VALUES(40,6,'40:6','Näin osoittautui todeksi uskottomia vastaan se Herrasi sana, että he joutuvat tulen omiksi.');
INSERT INTO "finnish" VALUES(40,7,'40:7','Ne, joille on uskottu valta, ja ne, jotka ympäröivät Häntä, julistavat Herransa kunniaa, uskovat Häneen ja anovat varjelusta uskovaisille: »Herra, Sinä suljet kaiken armoosi ja tietoosi; varjele sentähden niitä, jotka kääntyvät puoleesi ja noudattavat tietäsi; säästä heidät helvetin rangaistuksesta.');
INSERT INTO "finnish" VALUES(40,8,'40:8','Herra, salli heidän asettua niihin ikiaikojen tarhoihin, jotka olet luvannut heille ja niille heidän isillensä, vaimoillensa ja jälkeläisillensä, jotka tekevät hyvää. Totisesti, Sinun on mahti ja viisaus.');
INSERT INTO "finnish" VALUES(40,9,'40:9','Pidätä heidät pahoista teoista! » Sillä sen, joka tässä ajassa pahaa karttaa, Sinä todella armahdat tuomion päivänä; niin, se on suurin autuus.');
INSERT INTO "finnish" VALUES(40,10,'40:10','Totisesti huudetaan niille, jotka eivät usko: »Jumalan viha teitä kohtaan on suurempi kuin omanne, koska teidät kutsuttiin uskoon, mutta te sen hylkäsitte.»');
INSERT INTO "finnish" VALUES(40,11,'40:11','He sanovat: »Herra, kaksi kertaa olet pannut meidät kuolemaan ja kaksi kertaa olet antanut meille elämän; me tunnustamme syntimme, mutta onko nyt meille pelastuksen tietä?»');
INSERT INTO "finnish" VALUES(40,12,'40:12','Näin kävi siksi, että te ette uskoneet, kun oli palveltava yksin Jumalaa; mutta uskoitte kyllä, jos joku muu asetettiin Hänen rinnalleen. Tuomio kuuluu Jumalalle, korkealle, suurelle.');
INSERT INTO "finnish" VALUES(40,13,'40:13','Yksin Hän näyttää teille merkkinsä ja lähettää teille antimensa taivaasta, mutta vain se ottaa niistä vaarin, joka turvautuu Häneen.');
INSERT INTO "finnish" VALUES(40,14,'40:14','Huutakaa siis Jumalaa avuksi ollen Hänelle vilpittömän kuuliaiset, vaikka uskottomat olisivatkin nurjamielisiä.');
INSERT INTO "finnish" VALUES(40,15,'40:15','Hän, joka on kaikkien arvojen yläpuolella, Herra, jolla on valta, lähettää oman henkensä voiman otolliselle palvelijalleen, jotta tämä varoittaisi ihmisiä tuomiopäivästä,');
INSERT INTO "finnish" VALUES(40,16,'40:16','siitä päivästä, jolloin heidän on astuttava esiin. Mikään heidän ajatuksensa ei pysy Jumalalta salassa. Kuka hallitsee tuona päivänä? Jumala, ainoa, kaikkivaltias.');
INSERT INTO "finnish" VALUES(40,17,'40:17','Sinä päivänä jokainen sielu palkitaan ansionsa mukaan; vääryyttä ei kellekään tehdä tuona päivänä. Totisesti, Jumala tekee tilinsä nopeasti.');
INSERT INTO "finnish" VALUES(40,18,'40:18','Ja varoita heitä lähestyvästä päivästä, jolloin sydän nousee kurkkuun sisäisestä ahdingosta. Jumalattomilla ei ole yhtään ystävää eikä puoltajaa, jota kuunneltaisiin.');
INSERT INTO "finnish" VALUES(40,19,'40:19','Hän tuntee silmien vilpin ja sen, mitä mieleen on kätketty.');
INSERT INTO "finnish" VALUES(40,20,'40:20','Jumalan tuomio on vanhurskas, mutta ne, joiden puoleen he kääntyvät Hänen asemestaan, eivät saata lainkaan tuomita; totisesti Jumala on kuuleva, näkevä.');
INSERT INTO "finnish" VALUES(40,21,'40:21','Eivätkö he ole kiertäneet maata ja nähneet, minkälainen on ollut niiden loppu, jotka elivät ennen heitä? Ne olivat aikanaan mahtavampia voimiltaan ja varustuksiltaan kuin nämä, mutta Jumala tuhosi heidät heidän syntiensä tähden; ja heillä ei ollut yhtään puolustajaa Jumalaa vastaan.');
INSERT INTO "finnish" VALUES(40,22,'40:22','Tämä tapahtui siksi, että vaikka sananjulistajat tulivat heidän luokseen selvin todistuksin, he torjuivat ne, ja niin Jumala löi heidät; totisesti Hän on väkevä ja ankara tilinteossaan.');
INSERT INTO "finnish" VALUES(40,23,'40:23','Olemme lähettänyt Mooseksen tunnusmerkkeinemme ja selvin valtuuksin');
INSERT INTO "finnish" VALUES(40,24,'40:24','faraon, Haamanin ja Korahin luo, mutta he sanoivat: »Hän on valehtelija ja noita.»');
INSERT INTO "finnish" VALUES(40,25,'40:25','Kun hän näin toi heille Meiltä totuuden sanoman, sanoivat he: »Surmatkaa niiden pojat, jotka uskovat samoin kuin hän, mutta jättäkää naiset henkiin!» Mutta uskottomien juonet raukesivat tyhjiin.');
INSERT INTO "finnish" VALUES(40,26,'40:26','Ja farao sanoi: »Odottakaa, niin surmaan Mooseksen, ja huutakoon hän avuksi Herraansa! Totisesti pelkään, että hän turmelee uskontonne tai saa aikaan epäjärjestystä maassa.»');
INSERT INTO "finnish" VALUES(40,27,'40:27','Ja Mooses sanoi omilleen, »Totisesti turvaudun Herraani ja teidän Herraanne jokaista vastaan, joka ylpeilee eikä usko tuomion päivään.»');
INSERT INTO "finnish" VALUES(40,28,'40:28','Silloin sanoi eräs faraon väkeen kuuluva uskova mies, joka salasi uskontonsa: »Surmaatteko miehen sentähden, että hän sanoo: ''Jumala on Herrani?'' Hän on totisesti tuonut teille selvät todistukset Herralta. Jos hän valehtelee, lankeaa valhe hänen päällensä, mutta jos hän puhuu totta, niin teille koituu jotakin siitä, millä hän teitä uhkaa. Totisesti, Jumala ei ohjaa sitä, joka on pöyhkeäsanainen ja petollinen.');
INSERT INTO "finnish" VALUES(40,29,'40:29','Kansalaiseni, tänä päivänä on herruus ja valta maassa teidän, mutta kuka auttaa meitä välttymään Jumalan rangaistuksesta, jos se meidät kohtaa?» Mutta farao sanoi: »En neuvo teille muuta kuin sitä, mitä itse pidän hyvänä, enkä kehoita teitä seuraamaan muuta kuin oikeata tietä.»');
INSERT INTO "finnish" VALUES(40,30,'40:30','Mutta tuo uskovainen sanoi: »Kansalaiseni, pelkään teille tapahtuvan jotakin sellaista kuin samanmielisille,');
INSERT INTO "finnish" VALUES(40,31,'40:31','niinkuin tapahtui Nooan kansalle, Aadille, Tamuudille ja niille, jotka elivät heidän jälkeensä; Jumala ei tahdo vääryyttä tehtävän palvelijoilleen.');
INSERT INTO "finnish" VALUES(40,32,'40:32','Kansani, pelkään puolestanne viimeistä tuomiota,');
INSERT INTO "finnish" VALUES(40,33,'40:33','päivää, jolloin käännytte pakoon eikä teillä ole ketään, joka pelastaisi teidät Jumalan vihalta, sillä kenen Jumala antaa kulkea harhaan, sillä ei ole lainkaan opasta.');
INSERT INTO "finnish" VALUES(40,34,'40:34','Totisesti on Joosef tullut aikaisemmin luoksenne selvin todistuksin, mutta te yhä epäilitte hänen sanomaansa, aina siihen saakka, kunnes hän kuoli ja te sanoitte: »Jumala ei enää milloinkaan hänen jälkeensä lähetä sananjulistajaa». Näin saattaa Jumala pöyhkeäsanaiset ja epäilijät harhaan.');
INSERT INTO "finnish" VALUES(40,35,'40:35','Niitä, jotka kiistelevät Jumalan ilmoituksista saamatta Häneltä siihen valtaa, kohtaa ankarana Jumalan ja uskovien viha. Näin sinetöi Jumala jokaisen pöyhkeän ja ylenkatseisen sydämen.');
INSERT INTO "finnish" VALUES(40,36,'40:36','Ja farao sanoi: »Haaman, rakenna minulle torni, jotta minulla olisi tie');
INSERT INTO "finnish" VALUES(40,37,'40:37','ja pääsy taivaaseen saavuttaakseni Mooseksen Jumalan; totisesti pidän häntä valehtelijana.» Näin näyttivät faraosta hänen pahat tekonsa kauniilta ja hän eksyi tieltä; mutta faraon juonet johtivat vain turmioon.');
INSERT INTO "finnish" VALUES(40,38,'40:38','Ja tuo uskovainen sanoi: »Kansalaiseni, seuratkaa minua, minä johdan teidät oikealle tielle.');
INSERT INTO "finnish" VALUES(40,39,'40:39','Kansalaiseni, tämän maailman elämä on vain katoavaa iloa, mutta se, mikä tulee tämän jälkeen, on pysyväinen kotimme.');
INSERT INTO "finnish" VALUES(40,40,'40:40','Se, joka tekee pahan teon, saa sen mukaisen palkan; mutta ne, jotka tekevät hyvää ja uskovat, olkoot he sitten miehiä tai naisia, pääsevät paratiisiin, jossa heitä yltäkyllin ravitaan.');
INSERT INTO "finnish" VALUES(40,41,'40:41','Kansalaiseni, miksi kutsun teitä pelastukseen, vaikka te kutsutte minua tuleen?');
INSERT INTO "finnish" VALUES(40,42,'40:42','Te kehoitatte minua kieltämään Jumalan ja asettamaan Hänen rinnalleen jotakin sellaista, mistä minulla ei ole mitään tietoa, mutta minä kutsun teitä Hänen luokseen, jolla on valta ja armahdus.');
INSERT INTO "finnish" VALUES(40,43,'40:43','Varmaa on, että sillä, jonka puoleen minua kutsutte, ei ole valtaa tässä eikä tulevassa maailmassa, ja että me palaamme Jumalan tykö, mutta väärintekijäin asuinpaikka on tuli.');
INSERT INTO "finnish" VALUES(40,44,'40:44','Muistakaa siis, mitä sanon teille, ja minä uskon asiani Jumalan haltuun. Totisesti, Jumala näkee palvelijansa.»');
INSERT INTO "finnish" VALUES(40,45,'40:45','Ja jumala varjeli häntä heidän pahoilta juoniltaan, ja mitä ankarin rangaistus kohtasi faraon kansan.');
INSERT INTO "finnish" VALUES(40,46,'40:46','Tulen eteen viedään heidät joka aamu ja ilta, ja sinä päivänä, jolloin hetki lyö, julistetaan: »Faraon kansalle antakaa pahin rangaistus!»');
INSERT INTO "finnish" VALUES(40,47,'40:47','Ja kun he riitelevät toinen toisensa kanssa tulessa, sanovat heikot ylvästelijöille: »Totisesti, me olimme teidän seuraajianne; ettekö siis tahdo ottaa meiltä osaa tulen tuskasta?»');
INSERT INTO "finnish" VALUES(40,48,'40:48','Ne, jotka ovat ylpeitä, sanovat: »Totisesti, me olemme kaikki samassa kadotuksessa; Jumala on totisesti jakanut oikeuttaan palvelijoillensa.»');
INSERT INTO "finnish" VALUES(40,49,'40:49','Ja ne, jotka ovat tulessa, sanovat helvetin kaitsijoille: »Anokaa Herraltanne, että Hän lievittäisi kuritustamme edes yhdellä päivällä.»');
INSERT INTO "finnish" VALUES(40,50,'40:50','Nämä vastaavat: »Eivätkö apostolinne ole tulleet tykönne selvin todistein?» He sanovat: »Kyllä», ja vartijat sanovat: »Siis huutakaa apua.» Mutta uskottomien huuto menee hukkaan.');
INSERT INTO "finnish" VALUES(40,51,'40:51','Apostoleitamme ja niitä, jotka uskovat, Me totisesti autamme tämän maailman elämässä ja sinä päivänä, jolloin todistajat nousevat ylös.');
INSERT INTO "finnish" VALUES(40,52,'40:52','Sinä päivänä, jolloin ei auta jumalattomia heidän anteeksipyyntönsä, vaan näiden osana on kirous ja kurja paikka.');
INSERT INTO "finnish" VALUES(40,53,'40:53','Totisesti olemme antanut Moosekselle johdatuksen ja Israelin lapsille perinnöksi pyhän Kirjan.');
INSERT INTO "finnish" VALUES(40,54,'40:54','Ohjeeksi ja muistutukseksi niille, jotka ymmärtävät.');
INSERT INTO "finnish" VALUES(40,55,'40:55','Ole sentähden kärsivällinen. Jumalan lupaus on varmasti tosi; pyydä varjelusta synneistäsi kiittäen ja ylistäen Herraasi aamuin ja illoin.');
INSERT INTO "finnish" VALUES(40,56,'40:56','Ne, jotka ilman valtuutta kiistelevät Jumalan merkeistä, ovat mielessään pelkkää ylpeyttä täynnä ja himoitsevat suuruutta, jota he eivät milloinkaan saavuta. Etsi sentähden turvaa Jumalasta; totisesti Hän on näkevä ja kuuleva.');
INSERT INTO "finnish" VALUES(40,57,'40:57','Totisesti, taivaan ja maan luominen on enemmän kuin ihmisen luominen, mutta useimmat ihmiset ovat tietoa vailla.');
INSERT INTO "finnish" VALUES(40,58,'40:58','Sokea ja näkevä eivät ole toistensa kaltaisia, eivätkä ne, jotka uskovat ja tekevät hyvää, ole pahantekijän kaltaisia. Vähäistä on vaarinottonne.');
INSERT INTO "finnish" VALUES(40,59,'40:59','Totisesti, hetki tulee, siitä ei ole epäilystä, mutta useimmat ihmiset eivät usko.');
INSERT INTO "finnish" VALUES(40,60,'40:60','Teidän Herranne sanoo: »Kutsukaa Minua, Minä vastaan teille; totisesti, ne, jotka ovat liian ylpeitä palvelemaan Minua, joutuvat pian masennettuina helvettiin.»');
INSERT INTO "finnish" VALUES(40,61,'40:61','Jumala teille loi yön levoksi ja päivän silmienne valoksi, totisesti on Jumala ihmisille armollinen, mutta useimmat ihmiset ovat kiittämättömiä.');
INSERT INTO "finnish" VALUES(40,62,'40:62','Tämä on Jumala, teidän Herranne, kaiken Luoja; ei ole muuta jumalaa kuin Hän. Miksi siis käännytte Hänestä pois?');
INSERT INTO "finnish" VALUES(40,63,'40:63','Niin hylkäsivät Hänet myös ne, jotka kielsivät Jumalan ilmoitukset.');
INSERT INTO "finnish" VALUES(40,64,'40:64','Jumala loi maan leposijaksenne ja taivaan sille katokseksi; Hän antoi teille muodon, teki muotonne kauniiksi ja jakeli teille hyvyyttä. Tämä on Jumala, teidän Herranne; siunattu on siis Jumala, maailmojen valtias.');
INSERT INTO "finnish" VALUES(40,65,'40:65','Hän on elävä, ei ole muuta Jumalaa kuin Hän. Kutsukaa siis Häntä, ollen Hänelle vilpittömiä ja kuuliaisia. Ylistys olkoon Jumalalle, maailmojen valtiaalle.');
INSERT INTO "finnish" VALUES(40,66,'40:66','Sano: »Minua on kielletty palvelemasta niitä, joita te Jumalan ohella avuksi huudatte, koska Herra on antanut minulle selvät todistukset ja olen saanut käskyn alistua koko maailman Herran tahtoon.»');
INSERT INTO "finnish" VALUES(40,67,'40:67','Hän on luonut teidät tomusta, sitten pienestä elämänidusta, sitten hyytyneestä verestä; sitten saattaa Hän teidät lapseksi saavuttaaksenne senjälkeen kypsyysiän ja sitten vanhuuden, vaikka joukossanne on muutamia, joiden on kuoltava sitä ennen; näin saavutatte määrätyn ajan, jotta ymmärtäisitte.');
INSERT INTO "finnish" VALUES(40,68,'40:68','Hän se antaa elämän ja lähettää kuoleman; kun Hän päättää jotakin, Hän sanoo vain: »Tulkoon!» ja niin tapahtuu.');
INSERT INTO "finnish" VALUES(40,69,'40:69','Etkö ole nähnyt, kuinka ne, jotka väittelevät Jumalan ilmoituksista, ovat harhaan johdettuja?');
INSERT INTO "finnish" VALUES(40,70,'40:70','Ne, jotka hylkäävät pyhän Kirjan ja sen sanoman, jonka Me olemme lähettänyt sananjulistajamme mukana, tulevat pian huomaamaan erehdyksensä,');
INSERT INTO "finnish" VALUES(40,71,'40:71','kun kahleet ja vitjat pannaan heidän kaulaansa ja heidät raastetaan');
INSERT INTO "finnish" VALUES(40,72,'40:72','kuumaan veteen ja senjälkeen kärvennetään tulella.');
INSERT INTO "finnish" VALUES(40,73,'40:73','Silloin heille sanotaan: »Missä on se, joka teidän oli tapana asettaa Jumalan rinnalle?»');
INSERT INTO "finnish" VALUES(40,74,'40:74','He sanovat: »Ne ovat pettäneet meidät; ei, meillä ei ennen ollut tapana ketään palvella.» Näin Jumala johtaa harhaan uskottomat.');
INSERT INTO "finnish" VALUES(40,75,'40:75','Sillä ilonne maailmassa oli jumalatonta ja käytöksenne pöyhkeätä.');
INSERT INTO "finnish" VALUES(40,76,'40:76','Astukaa sisään helvetin porteista jäädäksenne sinne. Kurja on ylpeäin olinpaikka.');
INSERT INTO "finnish" VALUES(40,77,'40:77','Ole siis kärsivällinen, Jumalan lupaus on totisesti varma. Joko annamme sinun nähdä osan siitä, millä heitä uhkaamme, tai otamme sinut pois, heidän on palattava Meidän tykömme.');
INSERT INTO "finnish" VALUES(40,78,'40:78','Totisesti Me lähetimme sananjulistajia ennen sinua; heidän joukossaan on muutamia, joista olemme sinulle ilmoittanut, ja toisia, joista emme ole sinulle ilmoittanut. Apostolin ei ollut sallittu antaa merkkejä ilman Jumalan lupaa. Kun Jumalan käsky tulee, silloin tuomio totisesti toimeenpannaan, ja ne, jotka pitävät sitä valheena, ovat tuhon omia.');
INSERT INTO "finnish" VALUES(40,79,'40:79','Jumala teille antoi kotieläimet ratsastaaksenne toisilla niistä ja toisista saadaksenne ravintonne.');
INSERT INTO "finnish" VALUES(40,80,'40:80','Niistä on teille hyötyä, niiden avulla toimitatte asianne, sillä ne sekä laivat kantavat teitä.');
INSERT INTO "finnish" VALUES(40,81,'40:81','Hän osoittaa teille tunnusmerkkinsä. Minkä Jumalan merkeistä tahdotte kieltää?');
INSERT INTO "finnish" VALUES(40,82,'40:82','Eivätkö he ole kiertäneet maata ja nähneet, millainen oli niiden loppu, jotka elivät ennen heitä? Nämä olivat maan päällä lukuisammat kuin he ja suuremmat voimaltaan ja varustuksiltaan, mutta heidän saavutuksensa eivät heitä hyödyttäneet.');
INSERT INTO "finnish" VALUES(40,83,'40:83','Kun sitten heidän apostolinsa tulivat heidän luokseen selvin todistuksin, he pöyhkeilivät tiedoillaan, ja se, mitä he ivasivat, kohtasi heitä itseänsä.');
INSERT INTO "finnish" VALUES(40,84,'40:84','Mutta kun he näkivät Meidän kurituksemme, he sanoivat: »Me uskomme vain Jumalaan ja kiellämme sen, minkä asetimme Hänen rinnalleen.»');
INSERT INTO "finnish" VALUES(40,85,'40:85','Mutta heidän uskonsa ei heitä enää hyödyttänyt, kun he jo olivat kokeneet Meidän kostomme. Tämä on Jumalan laki, joka on ollut tehokas Hänen palvelijoihinsa nähden, mutta uskottomat ovat tuhon omat.');
INSERT INTO "finnish" VALUES(41,1,'41:1','Ylistetty, kunnian Jumala.');
INSERT INTO "finnish" VALUES(41,2,'41:2','Katso, tämä on ilmoitus laupiaalta, armolliselta Jumalalta,');
INSERT INTO "finnish" VALUES(41,3,'41:3','kirjoitus, jonka säkeet ovat selvät, arabialainen Koraani kansalle, jolla on tieto,');
INSERT INTO "finnish" VALUES(41,4,'41:4','hyvien sanomain lähetti ja varottaja, mutta useimmat heistä kääntyvät pois, niin etteivät kuule,');
INSERT INTO "finnish" VALUES(41,5,'41:5','ja he sanovat: »Sydämemme ovat suljetut siltä, jonka puoleen kutsut meitä; korvamme ovat kuurot, ja sinun ja meidän välillämme riippuu verho. Toimi siis; kyllä mekin toimimme.»');
INSERT INTO "finnish" VALUES(41,6,'41:6','Sano: »Minä olen vain kuolevainen niinkuin tekin. Minulle on ilmoitettu, että Jumalanne on ainoa Jumala. Seuratkaa siis oikeata tietä, joka johtaa Hänen luokseen, ja anokaa Häneltä anteeksiantoa. Voi monijumalaisia,');
INSERT INTO "finnish" VALUES(41,7,'41:7','niitä, jotka eivät anna almuja eivätkä usko tämänjälkeiseen elämään.»');
INSERT INTO "finnish" VALUES(41,8,'41:8','Ne, jotka uskovat ja tekevät hyviä töitä, saavat totisesti verrattoman palkinnon.');
INSERT INTO "finnish" VALUES(41,9,'41:9','Sano: »Kiellättekö todella Hänet, joka loi maan kahdessa päivässä, ja asetatteko Hänen rinnalleen vertaisia?» Hän on maailmojen valtias.');
INSERT INTO "finnish" VALUES(41,10,'41:10','Hän pystytti vuoret maan pinnalle, siunasi maan ja varusti sen ravinnolla neljässä päivässä, yhtä runsaasti kaikille tarvitsijoille.');
INSERT INTO "finnish" VALUES(41,11,'41:11','Sitten tuli vuoro taivaan, joka oli pelkkää usvaa, ja Hän sanoi sille sekä maalle: »Tulkaa esiin kumpikin, suosiolla tai vastahakoisesti.» Ja ne sanoivat kumpikin: » Me tulemme suosiolla».');
INSERT INTO "finnish" VALUES(41,12,'41:12','Niin Hän järjesti ne kahdessa päivässä seitsemäksi taivaaksi ja ilmoitti kullekin taivaalle sen tehtävän. Ja Me koristimme alemman taivaan loistavin tähdin, jotka panimme sen vartijoiksi; tämä on Hänen asetuksensa, jonka on valta ja tieto.');
INSERT INTO "finnish" VALUES(41,13,'41:13','Mutta jos he kääntyvät pois, sano: »Olen varoittanut teitä kurituksesta, sellaisesta rangaistuksesta kuin Aadin ja Tamuudin.»');
INSERT INTO "finnish" VALUES(41,14,'41:14','Kun näille lähetetyt sananjulistajat tulivat heidän luokseen sekä edestä että takaa sanoen: »Älkää palkelko muuta kuin Jumalaa!» niin he sanoivat: »Jos Herramme olisi tahtonut, Hän olisi varmasti lähettänyt alas enkelinsä; niinpä emme totisesti usko sanomaa, joka on lähetetty teidän mukananne.»');
INSERT INTO "finnish" VALUES(41,15,'41:15','Aadin kansa pöyhkeili suotta ja sanoi: »Kuka on meitä mahtavampi?» Eivätkö he havainneet, että Jumala, joka heidät oli luonut, oli heitä mahtavampi? Ja he kielsivät ilmoituksemme.');
INSERT INTO "finnish" VALUES(41,16,'41:16','Niin Me lähetimme raivoisan tuulen heidän ylitseen useana onnettomuutta tuottavana päivänä antaaksemme heidän maistaa häpeällistä rangaistusta maallisessa elämässä. Mutta totisesti on tulevan elämän rangaistus vielä paljon häpeällisempi, eivätkä he saa mitään apua.');
INSERT INTO "finnish" VALUES(41,17,'41:17','Mitä tulee Tamuudiin, osoitimme heille oikean tien, mutta he valitsivat harhan välittämättä johdatuksesta. Niin kohtasi heitä nöyryyttävän rangaistuksen ruoska heidän ansionsa mukaan.');
INSERT INTO "finnish" VALUES(41,18,'41:18','Mutta niitä armahdimme, jotka kavahtivat pahaa ja uskoivat.');
INSERT INTO "finnish" VALUES(41,19,'41:19','Sinä päivänä, jolloin Jumalan viholliset kootaan tulen eteen, heidät jaetaan ryhmiin.');
INSERT INTO "finnish" VALUES(41,20,'41:20','Ja kun he saapuvat sinne, heidän silmänsä, korvansa ja ihonsa todistavat heitä vastaan siitä, mitä he ovat tehneet.');
INSERT INTO "finnish" VALUES(41,21,'41:21','Ja he sanovat iholle: »Miksi todistat meitä vastaan»? Tämä sanoo: »Jumala, joka antaa kaikille ilmaisukyvyn, on pannut minut puhumaan; Hän on alussa luonut teidät, ja Hänen tykönsä joudutte palaamaan.');
INSERT INTO "finnish" VALUES(41,22,'41:22','Te ette voineet niin kätkeytyä, etteivät korvanne, silmänne ja ihonne todistaneet teitä vastaan, vaikka ajattelitte, että Jumalalle oli tuntematonta suuri osa siitä, minkä te teitte.');
INSERT INTO "finnish" VALUES(41,23,'41:23','Tämä ajatus Herrastanne on syössyt teidät kadotukseen, ja teistä on tullut turmion omia.»');
INSERT INTO "finnish" VALUES(41,24,'41:24','Jos he kestävät, pysyy tuli yhä heidän asuntonaan, ja jos he anovat armoa, eivät he niihin kuulu, joille armoa suodaan.');
INSERT INTO "finnish" VALUES(41,25,'41:25','Me olemme määrännyt heille seuralaisia, jotka kuvailivat kauniiksi sen, mikä odotti heitä, ja myös sen, mikä heillä oli takanaan. Ja niin kävi heidän suhteensa toteen sana, joka dzinnien ja ihmisten keskuudessa jo ennen on sanottu, että he totisesti joutuvat kadotukseen.');
INSERT INTO "finnish" VALUES(41,26,'41:26','Ja ne, jotka eivät usko, sanovat: »Alkää kuunnelko tämän Koraanin lukemista, vaan pitäkää melua; ehkä pääsette voitolle.»');
INSERT INTO "finnish" VALUES(41,27,'41:27','Sentähden Me totisesti annamme uskottomien maistaa ankaraa kuritusta ja totisesti annamme heille palkan pahoista töistä, joita he tekivät.');
INSERT INTO "finnish" VALUES(41,28,'41:28','Jumalan vihollisten palkka on tuli; se on oleva heidän asuntonaan ikuisesti, palkaksi siitä, että he kielsivät Meidän ilmoituksemme.');
INSERT INTO "finnish" VALUES(41,29,'41:29','Ja ne, jotka eivät usko, sanovat: »Herra, osoita meille dzinnien ja ihmisten joukosta ne, jotka johtivat meidät harhaan, jotta saattaisimme polkea heidät jalkoihimme, niin että he alimpaan paikkaan joutuisivat.»');
INSERT INTO "finnish" VALUES(41,30,'41:30','Niiden luokse, jotka sanovat: »Jumala on Herramme» ja pysyvät oikealla tiellä, laskeutuvat enkelit sanoen: »Alkää pelätkö älkääkä olko murheissanne, vaan ottakaa vastaan ilosanoma paratiisista, joka teille on luvattu.');
INSERT INTO "finnish" VALUES(41,31,'41:31','Me olemme teidän suojelijanne sekä tämän maailman elämässä että sitä seuraavassa; siinä olette saavat mitä pyydätte ja mitä sielunne halajaa.');
INSERT INTO "finnish" VALUES(41,32,'41:32','Lahjaksi Häneltä, joka on anteeksiantava, armahtava.');
INSERT INTO "finnish" VALUES(41,33,'41:33','Ken puhuu paremmin kuin se, joka kutsuu muita Jumalan luokse, tekee hyvää ja sanoo: »Totisesti olen Jumalalle alamainen»?');
INSERT INTO "finnish" VALUES(41,34,'41:34','Hyvä ja paha eivät ole samanlaisia. Karkoita paha sillä, mikä on parempaa, ja katso se, jonka kanssa olit vihoissa, on kuin hyvä ystäväsi.');
INSERT INTO "finnish" VALUES(41,35,'41:35','Mutta sen saavuttavat vain pitkämieliset; sen saavat vain ne, joiden osana on suuri onni.');
INSERT INTO "finnish" VALUES(41,36,'41:36','Ja jos Saatana sinua tahtoisi johtaa kiusaukseen, turvaudu Jumalaan; totisesti, Hän on kuuleva, kaikkitietävä.');
INSERT INTO "finnish" VALUES(41,37,'41:37','Hänen tunnusmerkkejään ovat yö ja päivä, aurinko ja kuu. Älkää kumartako aurinkoa ja kuuta, vaan kumartakaa Jumalaa, joka ne loi, jos Häntä tahdotte palvoa!');
INSERT INTO "finnish" VALUES(41,38,'41:38','Mutta jos he osoittautuvat ylpeiksi, niin kyllä ne, jotka ovat Herrasi luona, ylistävät Häntä päivin ja öin, väsymättä.');
INSERT INTO "finnish" VALUES(41,39,'41:39','Hänen merkkejään on sekin, että näette maan kuivuneena, mutta kun Me lähetämme sille vettä, se saa elon ja kasvuvoiman; totisesti Hän, joka antaa sille elon, on antava elämän kuolleille, totisesti Hänellä on valta kaiken yli.');
INSERT INTO "finnish" VALUES(41,40,'41:40','Totisesti eivät ne, jotka poikkeavat oikealta tieltä kieltäen ilmoituksemme, pääse Meiltä piiloon. Kumpi on parempi, sekö, joka on heitetty tuleen, vai se, joka pääsee turvaan ylösnousemuksen päivänä? Tehkää mitä tahdotte, Hän totisesti näkee mitä teette,');
INSERT INTO "finnish" VALUES(41,41,'41:41','näkee ne, jotka eivät usko varoitukseen, kun se tulee heidän luokseen. Totisesti, Koraani on ylevä Kirjoitus,');
INSERT INTO "finnish" VALUES(41,42,'41:42','valhe ei siihen ylety, ei edestä eikä takaa; se on sanoma viisaalta, ylistetyltä.');
INSERT INTO "finnish" VALUES(41,43,'41:43','Sinulle ei sanota mitään muuta, kuin mitä sanottiin jo sananjulistajille ennen sinua; Herrasi voi anteeksi antaa, mutta myös ankarasti rangaista.');
INSERT INTO "finnish" VALUES(41,44,'41:44','Jos Me olisimme tehnyt siitä vieraskielisen Koraanin, niin olisivat he varmasti sanoneet: »Miksi hänen merkkinsä eivät ole selviä? Vierastako kieltä arabialaiselle?» Sano: »Se on niille, jotka uskovat, ojennus ja parannus.» Mutta epäuskoisten korvat ovat kuurot ja heidän silmänsä sokeat; he kuulevat huudot vain heikosti.');
INSERT INTO "finnish" VALUES(41,45,'41:45','Totisesti, Me annoimme Moosekselle Kirjoituksen, mutta siitä on kiistelty. Ja ellei Herrasi olisi toisin määrännyt, olisi tuomio totisesti jo annettu heille; mutta rauhattomina he yhä pelkäävät sitä.');
INSERT INTO "finnish" VALUES(41,46,'41:46','Kuka tahansa tekee hyvää, sille se koituu hänen oman sielunsa hyväksi, ja kuka pahaa tekee, sille koituu se hänen sielunsa vahingoksi. Herrasi ei tee vääryyttä palvelijoilleen.');
INSERT INTO "finnish" VALUES(41,47,'41:47','Hänestä riippuu hetki, ja Hän sen tietää. Ei tule hedelmä kuorestaan, ei nainen kanna eikä synnytä Hänen tietämättään. Ja sinä päivänä, jolloin Hän huutaa heille: »Missä ovat ne, joita vertaisinani palvelitte?» he sanovat: » Me vakuutamme Sinulle, että meidän joukossamme ei ole yhtään niiden todistajaa.»');
INSERT INTO "finnish" VALUES(41,48,'41:48','Ja ne, joita he ennen kumarsivat, häipyvät heiltä, ja he huomaavat, ettei heillä ole pelastuksen mahdollisuutta.');
INSERT INTO "finnish" VALUES(41,49,'41:49','Ihminen ei milloinkaan väsy pyytämään hyvää, mutta jos paha kohtaa häntä, joutuu hän epätoivon valtaan.');
INSERT INTO "finnish" VALUES(41,50,'41:50','Mutta jos Me annamme hänen kokea armoamme ahdistuksen jälkeen, hän sanoo varmasti: »Tämä on minulle kuuluvaa, enkä usko tuomion päivään; mutta jos minut lähetetään takaisin Herrani luo, olen Hänen luonaan varmasti kokeva hyvää.» Mutta totisesti Me osoitamme epäuskoisille, mitä he ovat tehneet, ja annamme heidän kokea kovan rangaistuksen.');
INSERT INTO "finnish" VALUES(41,51,'41:51','Kun Me osoitamme suosiota ihmiselle, hän kääntyy pois ja etääntyy, mutta kun vastoinkäyminen kohtaa häntä, hänen rukouksensa ovat pitkät ja nöyrät.');
INSERT INTO "finnish" VALUES(41,52,'41:52','Sano: »Katso, jos se on Jumalalta, te kiellätte sen; kuka erehtyy enemmän kuin se, joka näin kauaksi harhautuu?»');
INSERT INTO "finnish" VALUES(41,53,'41:53','Me osoitamme heille pian merkkimme sekä kaukaisilla seuduilla että heidän oman kansansa keskuudessa, kunnes totuus kirkastuu heille kokonaan. Eikö riitä, että Herrasi on kaiken todistaja?');
INSERT INTO "finnish" VALUES(41,54,'41:54','Toden totta, he epäilevät joutumistaan Herransa eteen, mutta Hän totisesti ohjaa maailman menoa.');
INSERT INTO "finnish" VALUES(42,1,'42:1','(Haa, Miim) Ylistetty, kunnian Jumala,');
INSERT INTO "finnish" VALUES(42,2,'42:2','Kaikkitietävä ja kaikkivaltias Jumala.');
INSERT INTO "finnish" VALUES(42,3,'42:3','Näin antaa sinulle Jumala, mahtava, viisas, ilmoituksensa kuten antoi niillekin, jotka elivät ennen sinua.');
INSERT INTO "finnish" VALUES(42,4,'42:4','Hänen ovat taivaat ja maat; Hän on korkea, suuri.');
INSERT INTO "finnish" VALUES(42,5,'42:5','Taivas on repeämäisillään ylhäältä saakka, ja enkelit kiittävät ja ylistävät Herraansa ja pyytävät anteeksiantoa niille, jotka ovat maan päällä; totisesti on Jumala armollinen, anteeksiantava.');
INSERT INTO "finnish" VALUES(42,6,'42:6','Ja niitä, jotka Hänen sijaansa ottavat itselleen muita suojelijoita, Jumala vartioi, eivätkä he ole sinun valvontasi alaisia.');
INSERT INTO "finnish" VALUES(42,7,'42:7','Näin olemme sinulle ilmoittanut arabialaisen Koraanin, jotta varoittaisit pääkaupunkia ja sen ympärillä asuvia päivästä, jolloin kaikki kootaan yhteen ja jonka tulosta ei ole epäilystä; osa joutuu paratiisiin, osa helvetin tuleen.');
INSERT INTO "finnish" VALUES(42,8,'42:8','Jos Jumala olisi tahtonut, niin olisi Hän totisesti tehnyt heistä yhden ainoan kansan, mutta Hän sulkee armoonsa kenet haluaa, eikä ole jumalattomilla oleva suojelijaa eikä auttajaa.');
INSERT INTO "finnish" VALUES(42,9,'42:9','Vai ovatko he Hänen lisäkseen varanneet itselleen muita suojelijoita? Vain Jumala on varjelija; Hän antaa kuolleille elämän, ja kaikki on Hänen vallassaan.');
INSERT INTO "finnish" VALUES(42,10,'42:10','Ja mistä asiasta hyvänsä olettekin eri mieltä, sen ratkaisu on Jumalan kädessä; tämä on Jumala, Herrani, Häneen luotan ja Häneen turvaan yhä uudelleen.');
INSERT INTO "finnish" VALUES(42,11,'42:11','Hän on taivaan ja maan Luoja; Hän määräsi teille aviopuolisoita keskuudestanne ja myös eläimille parinsa, siten lisääntyäksenne. Ei ole mitään Hänen vertaistaan; Hän on kuuleva, näkevä.');
INSERT INTO "finnish" VALUES(42,12,'42:12','Hänelle kuuluvat taivaan ja maan aarteet; Hän jakelee elatusta runsaasti tai niukasti tahtonsa mukaan. Hän on totisesti kaikkitietävä.');
INSERT INTO "finnish" VALUES(42,13,'42:13','Hän on teille määrännyt saman uskonnon, jonka Hän asetti Nooalle, saman, jonka Me olemme ilmoittanut sinulle ja määrännyt Aabrahamille, Moosekselle ja Jeesukselle ja joka kuuluu: »Pysykää kuuliaisina uskonnollenne ja välttäkää hajaannusta.» Vaikeaa on epäuskoisille se, mihin sinä heitä kehoitat; Jumala valitsee kenet haluaa ja ohjaa luoksensa sen, joka usein kääntyy Hänen puoleensa.');
INSERT INTO "finnish" VALUES(42,14,'42:14','Eivätkä he hajaantuneet ennen kuin vasta sitten, kun tieto oli tullut heidän osakseen, ja he tekivät sen keskinäisestä kateudesta. Ja ellei Herraltasi jo olisi tullut tietoa määräajasta, olisi totisesti tuomio langetettu heille, mutta ne, joille Kirjoitus heidän jälkeensä annettiin perinnöksi, ovat levottomia ja epäilevät sitä.');
INSERT INTO "finnish" VALUES(42,15,'42:15','Kehoita siis edelleenkin heitä oikeaan uskoon ja käy vakaana oikeaa tietä, niinkuin sinua on käsketty, äläkä noudata heidän alhaisia toiveitaan, vaan sano: »Minä uskon siihen, mitä Jumala on Kirjoituksessa ilmoittanut, ja minut on määrätty jakamaan teille oikeutta. Jumala on meidän ja teidän Herranne. Meillä on oleva tekomme ja teillä tekonne. Riita ei ole tarpeen välillämme; Jumala kokoaa meidät, ja Hänen tykönänsä on matkamme pää.»');
INSERT INTO "finnish" VALUES(42,16,'42:16','Jotka kiistelevät Jumalasta senjälkeen, kun kuuliaisuutta on osoitettu Hänelle, heidän väitteensä on mitätön heidän Herransa edessä, heitä kohtaa viha ja odottaa ankara kuritus.');
INSERT INTO "finnish" VALUES(42,17,'42:17','Jumala lähetti Kirjoituksen, totuuden sanoman ja vaa''an, sekä myös tiedon, että hetki on ehkä lähellä.');
INSERT INTO "finnish" VALUES(42,18,'42:18','Ne, jotka eivät usko siihen, tahtoisivat jouduttaa sitä, ja ne, jotka uskovat, pelkäävät sitä, sillä he tietävät, että se on totuus. Totisesti erehtyvät ne suuresti, jotka uppiniskaisina väittelevät tuosta hetkestä.');
INSERT INTO "finnish" VALUES(42,19,'42:19','Jumala on lempeä palvelijoitaan kohtaan, Hän antaa ravintoa kenelle tahtoo; Hän on voimakas, mahtava.');
INSERT INTO "finnish" VALUES(42,20,'42:20','Sille, joka toivoo saavansa nauttia sadon tulevassa elämässä. Me annamme sen runsaana, ja sille, joka haluaa tämän maailman voittoa, annamme sen, mutta tämän jälkeisessä elämässä ei hänellä ole osuutta.');
INSERT INTO "finnish" VALUES(42,21,'42:21','Vai onko heillä Meidän sijaisiamme, jotka ovat määränneet heille jonkin uskonnon, jota Jumala ei ole asettanut? Ilman tuomion sanomaa olisi päätös heidän suhteensa totisesti jo tehty, ja toden totta, jumalattomien rangaistus on oleva tuskallinen.');
INSERT INTO "finnish" VALUES(42,22,'42:22','Sinä olet näkevä, kuinka jumalattomat pelkäävät sitä, mitä ovat ansainneet, mutta sen täytyy langeta heidän osalleen. Ne, jotka uskovat ja tekevät hyvää, pääsevät paratiisin kedoille ja saavat Herransa luona, mitä tahtovat; tämä on suuri armo.');
INSERT INTO "finnish" VALUES(42,23,'42:23','Tämä on se hyvä sanoma, minkä Jumala antaa palvelijoilleen, niille, jotka uskovat ja tekevät hyviä tekoja. Sano: »En pyydä teiltä siitä muuta palkkaa kuin rakkautta heimolaisianne kohtaan.» Sille, joka ansaitsee hyvää, annamme sitä runsain määrin; totisesti on Jumala anteeksiantava, kiitollinen.');
INSERT INTO "finnish" VALUES(42,24,'42:24','Vai sanovatko he: »Hän on sepittänyt valheita Jumalasta?» Mutta jos Jumala tahtoo, Hän voi tehdä sydämesi kärsivälliseksi. Jumala pyyhkii pois valheen ja lujittaa totuuden sanoillaan; totisesti Hän tietää kaikki ajatuksenne.');
INSERT INTO "finnish" VALUES(42,25,'42:25','Hän ottaa vastaan palvelijainsa katumuksen ja antaa anteeksi pahat teot, ja Hän tietää, mitä te teette.');
INSERT INTO "finnish" VALUES(42,26,'42:26','Hän kuulee niiden rukoukset, jotka uskovat ja tekevät hyviä tekoja, ja jakaa heille runsaasti armoaan, mutta uskottomat saavat ankaran rangaistuksen.');
INSERT INTO "finnish" VALUES(42,27,'42:27','Ja jos Jumala jakaisi palvelijoilleen runsaammin ravintoa, nämä varmasti pöyhistyisivät, mutta Hän lähettää sitä hyväksi näkemänsä määrän. Totisesti, Hän on kaikki näkevä ja tuntee palvelijansa.');
INSERT INTO "finnish" VALUES(42,28,'42:28','Hän lähettää sateen, kun he ovat käyneet epätoivoisiksi, ja levittää armoaan. Hän on suojelija, ylistetty.');
INSERT INTO "finnish" VALUES(42,29,'42:29','Hänen tunnusmerkkejään ovat taivaan ja maan luominen ja ne elävät olennot, jotka Hän asetti kumpaankin. Hänellä on myös valta koota ne yhteen milloin tahtoo.');
INSERT INTO "finnish" VALUES(42,30,'42:30','Mikä tahansa vaiva teitä kohtaakin, se tapahtuu kättenne töiden ansiosta; ja kuitenkin antaa Hän anteeksi paljon.');
INSERT INTO "finnish" VALUES(42,31,'42:31','Te ette voi paeta eikä teillä paitsi Jumalaa ole muuta suojelijaa eikä auttajaa.');
INSERT INTO "finnish" VALUES(42,32,'42:32','Hänen merkkejään ovat merellä laivat, jotka ovat kuin vuoria.');
INSERT INTO "finnish" VALUES(42,33,'42:33','Jos Hän tahtoo, Hän vaimentaa tuulen, niin että laivat seisovat liikkumattomina meren pinnalla. Totisesti tässä on merkkejä jokaiselle, joka on kärsivällinen ja kiitollinen.');
INSERT INTO "finnish" VALUES(42,34,'42:34','Tai Hän antaa niiden hukkua ansionsa mukaan; mutta Hän antaa anteeksi paljon.');
INSERT INTO "finnish" VALUES(42,35,'42:35','Ja ne, jotka väittelevät ilmoituksistamme, tietäkööt, että heillä ei ole mitään turvapaikkaa.');
INSERT INTO "finnish" VALUES(42,36,'42:36','Kaikki, mitä teille siis on annettu, on vain tämän maallisen elämän aarteita; mutta se, mikä odottaa Jumalan tykönä, on parempaa ja kestävämpää niille, jotka uskovat ja turvaavat Herraansa,');
INSERT INTO "finnish" VALUES(42,37,'42:37','niille, jotka karttavat pahoja syntejä ja riettautta ja vihastuessaan antavat anteeksi,');
INSERT INTO "finnish" VALUES(42,38,'42:38','jotka täyttävät Herransa tahdon, rukoillen lakkaamatta, joilla on tapana neuvotella keskenään ja jotka jakelevat siitä, mitä Me olemme heille antanut.');
INSERT INTO "finnish" VALUES(42,39,'42:39','Ja niille, jotka puolustautuvat, kun jokin suuri vääryys kohtaa heitä.');
INSERT INTO "finnish" VALUES(42,40,'42:40','Pahan palkka olkoon samankaltainen rangaistus, mutta jos joku antaa anteeksi ja tekee parannuksen, hän on saava palkkansa Jumalalta; totisesti, Hän ei rakasta jumalattomia.');
INSERT INTO "finnish" VALUES(42,41,'42:41','Ja jos joku puolustaa itseään, kun häntä sorretaan, niin ei ole mitään syytä soimata häntä siitä.');
INSERT INTO "finnish" VALUES(42,42,'42:42','Tuomittakoon vain niitä, jotka sortavat ihmisiä ja väärämielisyydessään kapinoitsevat maan päällä; näillä on oleva tuskallinen rangaistus.');
INSERT INTO "finnish" VALUES(42,43,'42:43','Jos joku on kärsivällinen ja anteeksiantavainen, niin on hän totisesti menetellyt oikein.');
INSERT INTO "finnish" VALUES(42,44,'42:44','Ja kenet Jumala päästää harhaan, sillä ei ole suojelijaa. Ja sinä saat nähdä, kuinka jumalattomat, kun he näkevät kurituksensa, sanovat: »Onko mahdollisuutta pelastua?»');
INSERT INTO "finnish" VALUES(42,45,'42:45','Sinä saat myös nähdä, miten heidät tuodaan esiin, nöyryytyksen masentamina ja katse maahan painuneena. Mutta uskovaiset sanovat: »Totisesti, kadotettuja ovat ne, jotka hukkaavat omat ja perheensä sielut ylösnousemuksen päivänä. Toden totta, jumalattomien on saatava ikuinen rangaistus.');
INSERT INTO "finnish" VALUES(42,46,'42:46','Eikä heillä ole ystäviä, jotka heitä auttaisivat Jumalan sijasta, sillä kenet Jumala saattaa eksyksiin, hän ei löydä tietä.');
INSERT INTO "finnish" VALUES(42,47,'42:47','Kuunnelkaa Herraamme, ennenkuin tulee päivä, josta ei ole oleva pelastusta. Tuona päivänä ei teillä ole pakopaikkaa eikä kieltämisen mahdollisuutta.');
INSERT INTO "finnish" VALUES(42,48,'42:48','Mutta jos he kääntyvät pois, niin Me emme ole lähettänyt sinua heidän valvojakseen; sinun tehtäväsi on vain julistaa ilmoitusta. Ja totisesti, kun annamme ihmisen kokea armoamme, hän iloitsee siitä, mutta jos häntä kättensä töiden tähden kohtaa jokin onnettomuus, tulee hänestä kiittämätön.');
INSERT INTO "finnish" VALUES(42,49,'42:49','Jumalan on valta taivaassa ja maan päällä; Hän luo mitä tahtoo, tahtonsa mukaan suo Hän jollekulle tyttäriä, toiselle poikia.');
INSERT INTO "finnish" VALUES(42,50,'42:50','Tai Hän antaa molempia, poikia sekä tyttäriä, Hän saattaa myös hedelmättömäksi kenet tahtoo. Totisesti Hänellä on tieto ja valta.');
INSERT INTO "finnish" VALUES(42,51,'42:51','Yhdellekään kuolevaiselle ei sovi, että Jumala puhuu hänelle muutoin kuin ilmoituksen kautta tai verhon takaa tai lähettämällä sanansaattajan, joka Hänen luvallaan ilmoittaa Hänen tahtonsa; Hän on totisesti korkea, viisas.');
INSERT INTO "finnish" VALUES(42,52,'42:52','Näin ilmitoimme sinulle pyhän Kirjoituksen käskymme mukaan. Sinä et tiennyt, mitä Kirja oli, etkä mitä usko oli, mutta Me asetimme sen valoksi, jonka avulla voit johtaa sitä palvelijoistamme, jonka otolliseksi näemme; totisesti, sinä näytät pääsyn oikealle polulle,');
INSERT INTO "finnish" VALUES(42,53,'42:53','Jumalan polulle, Jumalan, jolle kuuluu kaikki, mitä on taivaissa ja maassa. Totisesti, Jumalan luokse kaikki lopulta palaa.');
INSERT INTO "finnish" VALUES(43,1,'43:1','Ylistetty olkoon kunnian Jumala.');
INSERT INTO "finnish" VALUES(43,2,'43:2','Pitäkää arvossa Kirjoitus, joka ilmituo totuuden.');
INSERT INTO "finnish" VALUES(43,3,'43:3','Totisesti olemme tehnyt siitä arabialaisen Koraanin, jotta ymmärtäisitte.');
INSERT INTO "finnish" VALUES(43,4,'43:4','Ja se on totisesti ylevä ja viisautta täynnä niinkuin alkuperäinen Kirjoitus Meidän luonamme.');
INSERT INTO "finnish" VALUES(43,5,'43:5','Pitääkö Meidän kokonaan luopua muistuttamasta teitä sentähden, että olette syntistä kansaa?');
INSERT INTO "finnish" VALUES(43,6,'43:6','Kuinka monta profeettaa olemmekaan lähettänyt ihmisille ennen muinoin!');
INSERT INTO "finnish" VALUES(43,7,'43:7','Mutta yksikään profeetta ei käynyt heidän luonaan saamatta osakseen pilkkaa.');
INSERT INTO "finnish" VALUES(43,8,'43:8','Silloin tuhosimme ne, jotka olivat nykyisiä mahtavammat, mutta se, miten kävi ihmisille ennen vanhaan, kuuluu menneisyyteen.');
INSERT INTO "finnish" VALUES(43,9,'43:9','Jos kysyt heiltä, kuka on luonut taivaat ja maan, he vastaavat varmasti: »Mahtava, ainoa tietävä on luonut ne.»');
INSERT INTO "finnish" VALUES(43,10,'43:10','Hän, joka loi maan teille olinpaikaksi ja raivasi sen kamaralle tiet voidaksenne kulkea oikeaan,');
INSERT INTO "finnish" VALUES(43,11,'43:11','Hän, joka lähettää pilvistä vettä määrän mukaan, niin että Me sillä herätämme kuolleen maan eloon - näin teidätkin herätetään -');
INSERT INTO "finnish" VALUES(43,12,'43:12','ja Hän, joka loi kaikki parittain ja hankki teille laivat ja kotieläimet matkataksenne niiden avulla,');
INSERT INTO "finnish" VALUES(43,13,'43:13','voidaksenne turvallisesti istua niiden selässä ja sitten muistaa Herranne armoa, kun niillä kuljette, ja sanoa: »Kunnia olkoon Hänelle, joka saattoi tämän alamaisuuteemme; me emme siihen kyenneet,');
INSERT INTO "finnish" VALUES(43,14,'43:14','Mutta Herramme luokse on meidän totisesti palattava.»');
INSERT INTO "finnish" VALUES(43,15,'43:15','Ja he määräävät Hänelle vain osan Hänen palvelijoistaan; totisesti, ihminen on selvästi kiittämätön.');
INSERT INTO "finnish" VALUES(43,16,'43:16','Onko Hän ottanut luomistaan olennoista tyttäret itselleen ja mieluummin antanut teille pojat?');
INSERT INTO "finnish" VALUES(43,17,'43:17','Ja kun joku heistä saa tiedon sellaisen lapsen syntymästä, minkä katsotaan armolliselle Jumalalle kuuluvan, tummuvat hänen kasvonsa vihasta.');
INSERT INTO "finnish" VALUES(43,18,'43:18','Jumalalleko sellainen lapsi, joka kasvaa koreudessa eikä kiistan tullen saa selvää sanaa suustaan?');
INSERT INTO "finnish" VALUES(43,19,'43:19','He tekevät myös enkeleistä, niistä, jotka ovat laupiaan Jumalan palvelijoita, naisolentoja. Olivatko he edes silminnäkijöinä, kun loimme nämä enkelit? Heidän todistuksensa kirjoitetaan muistiin, ja heitä tullaan kuulustamaan.');
INSERT INTO "finnish" VALUES(43,20,'43:20','He sanovat myös: »Jos laupias Jumala olisi tahtonut, me emme olisi milloinkaan palvelleet niitä.» Heillä ei ole tästä mitään tietoa; he vain valehtelevat,');
INSERT INTO "finnish" VALUES(43,21,'43:21','vai olemmeko Me antanut heille mitään Kirjoitusta sitä ennen, niin että he voivat pitää siitä kiinni?');
INSERT INTO "finnish" VALUES(43,22,'43:22','Ei, he sanovat: »Olemme nähneet isiemme kulkevan määrättyä suuntaa, ja totisesti me olemme seuranneet heidän jälkiään.»');
INSERT INTO "finnish" VALUES(43,23,'43:23','Niin emme myöskään ennen sinua saattanut lähettää varoittajaa mihinkään kaupunkiin suruttomien sanomatta: »Varmasti olemme nähneet isäimme käyneen määrättyä suuntaa, ja totisesti me kuljemme heidän jäljissään.»');
INSERT INTO "finnish" VALUES(43,24,'43:24','Varoittaja sanoi: »Entäpä jos antaisin teille paremman opastajan kuin se, jota isienne näitte seuranneen?» He sanoivat: »Totisesti emme usko siihen sanomaan, mikä on lähetetty sinun mukanasi.»');
INSERT INTO "finnish" VALUES(43,25,'43:25','Silloin määräsimme heille koston, ja katso, minkälainen oli kieltäjäin loppu!');
INSERT INTO "finnish" VALUES(43,26,'43:26','Aabraham sanoi isälleen ja kansalleen: »Totisesti tahdon olla vapaa kaikesta mitä te jumaloitte,');
INSERT INTO "finnish" VALUES(43,27,'43:27','paitsi Hänestä, joka minut on luonut, sillä totisesti, Hän johtaa minua.»');
INSERT INTO "finnish" VALUES(43,28,'43:28','Ja tästä hän teki säännön jälkeläisilleen, jotta he kääntyisivät.');
INSERT INTO "finnish" VALUES(43,29,'43:29','Niin annoin heidän ja heidän isiensä nauttia elämästä, kunnes totuus ja sananjulistaja, joka vahvisti totuuden, tulivat heidän luokseen.');
INSERT INTO "finnish" VALUES(43,30,'43:30','Mutta kun totuus tuli heidän luokseen, he sanoivat: »Tämä on taikuutta, totisesti, emme usko siihen.»');
INSERT INTO "finnish" VALUES(43,31,'43:31','He sanoivat myös: »Miksi ei tätä Koraania ole ilmoitettu johtomiehille kummassakaan kaupungissa?»');
INSERT INTO "finnish" VALUES(43,32,'43:32','Jakelevatko he Herrasi armoa? Me jaamme heille elantonsa maallisessa elämässä ja Me olemme korottanut heistä muutamia arvossa toisten yläpuolelle, jotta toiset eläisivät toisille alamaisina, mutta Herrasi armo on parempi kuin se, mitä he kokoavat.');
INSERT INTO "finnish" VALUES(43,33,'43:33','Elleivät kaikki ihmiset olisi olleet kuin yksi ainoa kansa, Me olisimme antanut niille, jotka eivät usko laupiaaseen Jumalaan, hopeasta katot heidän asumuksiinsa ja portaat, joita he nousevat,');
INSERT INTO "finnish" VALUES(43,34,'43:34','ovet heidän taloihinsa ja vuoteet, joille he laskeutuvat lepäämään,');
INSERT INTO "finnish" VALUES(43,35,'43:35','ja kultaiset koristeet; mutta kaikki nämä ovat vain maallisen elämän tarpeita, ja tämänjälkeinen elämä Herrasi luona on varattu vain niille, jotka karttavat pahaa.');
INSERT INTO "finnish" VALUES(43,36,'43:36','Sille, joka hylkää laupiaan Jumalan muistutuksen, Me annamme saatanan seuralaiseksi.');
INSERT INTO "finnish" VALUES(43,37,'43:37','Tämä totisesti saattaa heidät harhaan polulta. Mutta he luulevat, että heitä ohjataan oikeaa tietä,');
INSERT INTO "finnish" VALUES(43,38,'43:38','kunnes joku heistä tulee Meidän tykömme ja sanoo: »Ah, olisimmepa olleet toisistamme yhtä kaukana kuin itä ja länsi!» Mikä katala seuralainen!');
INSERT INTO "finnish" VALUES(43,39,'43:39','Ja koska teitte väärin, teitä ei tuona päivänä hyödytä se, että muitakin osallistuu rangaistukseenne.');
INSERT INTO "finnish" VALUES(43,40,'43:40','Voitko saada kuurot kuulemaan tai ohjata sokeita ja kokonaan eksyneitä?');
INSERT INTO "finnish" VALUES(43,41,'43:41','Mutta vaikka otamme sinut pois, annamme kuitenkin koston kohdata heitä;');
INSERT INTO "finnish" VALUES(43,42,'43:42','mieluummin Me totisesti tahdomme näyttää sinulle, mitä olemme heille luvannut, sillä totisesti, Meillä on kaikki valta heihin.');
INSERT INTO "finnish" VALUES(43,43,'43:43','Pidä sentähden kiinni siitä, mikä sinulle on ilmoitettu. Olet varmasti oikealla tiellä.');
INSERT INTO "finnish" VALUES(43,44,'43:44','Se on totisesti sinulle ja kansallesi kehoitus, ja teidän on tehtävä siitä pian tili.');
INSERT INTO "finnish" VALUES(43,45,'43:45','Kysy myös niiltä sananjulistajilta, jotka lähetimme ennen sinua, olemmeko milloinkaan määrännyt palvelemaan muita jumalia kuin Armahtajaa.');
INSERT INTO "finnish" VALUES(43,46,'43:46','Totisesti olemme lähettänyt Mooseksen viemään ilmoitustamme faraolle ja hänen päälliköillensä, ja hän sanoi: »Totisesti, olen maailmojen Herran julistaja.»');
INSERT INTO "finnish" VALUES(43,47,'43:47','Mutta kun hän tuli heidän luokseen tuomaan tunnusmerkkejämme, katso, he nauroivat niille.');
INSERT INTO "finnish" VALUES(43,48,'43:48','Ja Me näytimme heille tunnustekoja toinen toistaan suurempia, ja Me yllätimme heidät kurituksella, jotta he kääntyisivät.');
INSERT INTO "finnish" VALUES(43,49,'43:49','Mutta he sanoivat: »Noita, huuda Herraasi avuksemme, niinkuin Hän on sopinut kanssasi; me kuljemme varmasti oikeata tietä.»');
INSERT INTO "finnish" VALUES(43,50,'43:50','Mutta kun Me otimme heidän päältään kurituksen, katso, he rikkoivat lupauksensa.');
INSERT INTO "finnish" VALUES(43,51,'43:51','Ja farao julisti kansalleen: »Kansani, eikö valta Egyptissä kuulu minulle? Niin myös nämä virrat, jotka virtaavat alueellani? Ettekö huomaa?');
INSERT INTO "finnish" VALUES(43,52,'43:52','Olen parempi kuin tämä mies, joka on viheliäinen raukka ja tuskin osaa selvästi puhua.');
INSERT INTO "finnish" VALUES(43,53,'43:53','Miksi ei hänelle ole annettu kultaisia rannerenkaita? Ja miksi ei hänen mukanaan ole tullut enkeleitä?»');
INSERT INTO "finnish" VALUES(43,54,'43:54','Näin yllytti hän kansaansa epäuskoon, ja he tottelivat häntä; he olivat totisesti syntistä kansaa.');
INSERT INTO "finnish" VALUES(43,55,'43:55','Koska he nostattivat vihamme, kostimme heille ja hukutimme heidät kaikki veteen.');
INSERT INTO "finnish" VALUES(43,56,'43:56','Ja asetimme heidät varoitukseksi ja esimerkiksi myöhemmille sukupolville.');
INSERT INTO "finnish" VALUES(43,57,'43:57','Kun Marian poika asetettiin esikuvaksi, nosti kansasi siitä melun,');
INSERT INTO "finnish" VALUES(43,58,'43:58','ja he sanoivat: »Mikä on parempi, meidän jumalamme vai hän? » Tämän he sanoivat sinulle vain saadakseen aikaan väittelyn; niin, he olivat riidanhaluista kansaa.');
INSERT INTO "finnish" VALUES(43,59,'43:59','Hän oli vain palvelija, jolle Me osoitimme armoamme ja jonka Me asetimme esikuvaksi Israelin lapsille.');
INSERT INTO "finnish" VALUES(43,60,'43:60','Ja jos Me tahtoisimme, voisimme luoda joukostanne enkeleitä seuraajiksenne maan päälle.');
INSERT INTO "finnish" VALUES(43,61,'43:61','Hän on totisesti merkki hetkestä; älkää siis epäilkö sitä, vaan seuratkaa minua. Tämä on oikea tie.');
INSERT INTO "finnish" VALUES(43,62,'43:62','Älkää antako saatanan estää teitä; hän on totisesti ilmeinen vihollisenne.');
INSERT INTO "finnish" VALUES(43,63,'43:63','Kun Jeesus tuli selvin todistuksin, hän sanoi: » Olen tullut luoksenne ja tuonut teille viisauden selvittääkseni teille osan siitä, mistä olette eri mieltä. Pitäkää siis huoli velvollisuudestanne Jumalaa kohtaan ja totelkaa minua.');
INSERT INTO "finnish" VALUES(43,64,'43:64','Totisesti on Jumala minun ja teidän Herranne; palvelkaa sentähden Häntä. Tämä on oikea tie.»');
INSERT INTO "finnish" VALUES(43,65,'43:65','Mutta eri ryhmät heidän keskuudessaan riitelivät, ja voi rangaistusta, jonka väärämieliset saavat tuskan päivänä!');
INSERT INTO "finnish" VALUES(43,66,'43:66','Voivatko he odottaa muuta kuin hetkeä, joka yllättää heidät äkkiä, silloin kun he eivät mitään aavista?');
INSERT INTO "finnish" VALUES(43,67,'43:67','Ystävät tulevat tuona päivänä olemaan toinen toistensa vihamiehiä, lukuunottamatta niitä, jotka karttavat pahaa.');
INSERT INTO "finnish" VALUES(43,68,'43:68','Palvelijani, teidän ei tarvitse pelätä eikä murehtia tuota päivää,');
INSERT INTO "finnish" VALUES(43,69,'43:69','teidän, jotka uskoitte ilmoituksiini ja olitte Minulle kuuliaisia!');
INSERT INTO "finnish" VALUES(43,70,'43:70','Käykää paratiisiin, te ja vaimonne, ja teidät tehdään onnellisiksi.');
INSERT INTO "finnish" VALUES(43,71,'43:71','Siellä kiertävät kultaiset kulhot ja pikarit; siellä saavat he, mitä heidän sielunsa halajaa ja mistä heidän silmänsä iloitsevat, ja siellä te saatte asua ikuisesti.');
INSERT INTO "finnish" VALUES(43,72,'43:72','Se on paratiisi, joka on annettu teille perinnöksi siitä, mitä teitte.');
INSERT INTO "finnish" VALUES(43,73,'43:73','Teille on siellä varattu runsaasti hedelmiä syödäksenne.');
INSERT INTO "finnish" VALUES(43,74,'43:74','Syntiset saavat ikuisesti asua helvetin piinassa.');
INSERT INTO "finnish" VALUES(43,75,'43:75','Sitä ei heille huojenneta vaan he joutuvat siellä epätoivoon.');
INSERT INTO "finnish" VALUES(43,76,'43:76','Me emme ole tehnyt heille vääryyttä, vaan he itse tekivät väärin.');
INSERT INTO "finnish" VALUES(43,77,'43:77','Ja he huutavat: »Maalik , anna Herrasi tehdä loppu meistä!» Hän vastaa: »Teidän on totisesti jäätävä sinne.»');
INSERT INTO "finnish" VALUES(43,78,'43:78','Olemmehan lähettänyt teille totuuden, mutta useimmille teistä on totuus vastenmielinen.');
INSERT INTO "finnish" VALUES(43,79,'43:79','Hekö ovat päättäneet jotakin? Totisesti, Meillä on ratkaisu.');
INSERT INTO "finnish" VALUES(43,80,'43:80','Vai ajattelevatko he, että Me emme tiedä, mitä he kätkevät, emmekä kuule heidän salaisia keskustelujaan? Meidän sanansaattajamme heidän luonaan kirjoittavat kaiken muistiin.');
INSERT INTO "finnish" VALUES(43,81,'43:81','Sano: »Jos armollisella Jumalalla olisi poika, olisin ensimmäinen palvelemaan Häntä.»');
INSERT INTO "finnish" VALUES(43,82,'43:82','Kunnia taivaitten ja maan Herralle, jolla on valta ja joka on kaukana siitä, mitä he Hänestä väittävät.');
INSERT INTO "finnish" VALUES(43,83,'43:83','Anna heidän siis antautua valheellisiin jaaritteluihinsa ja ilveilyihinsä, kunnes heidät kohtaa se päivä, jolla heitä uhataan.');
INSERT INTO "finnish" VALUES(43,84,'43:84','Hän on Jumala taivaassa ja maan päällä; niin, Hän on viisas ja Hänen on tieto.');
INSERT INTO "finnish" VALUES(43,85,'43:85','Siunattu on Hän, jonka on valta taivaassa ja maan päällä sekä niiden välillä; Hän tietää, milloin hetki tulee, ja Hänen luoksensa on teidän palattava.');
INSERT INTO "finnish" VALUES(43,86,'43:86','Niillä, joita he Hänen rinnallaan palvelevat, ei ole mitään välittäjän valtuutta, vaan ainoastaan hänellä, joka todistaa totuudesta, ja he tuntevat hänet.');
INSERT INTO "finnish" VALUES(43,87,'43:87','Ja jos kysyt heiltä, kuka on heidät luonut, he varmasti vastaavat: »Jumala.» Miksi he siis kääntyvät pois?');
INSERT INTO "finnish" VALUES(43,88,'43:88','Hänen huutonsa kuuluu: »Herra, totisesti he ovat kansa, joka ei usko.»');
INSERT INTO "finnish" VALUES(43,89,'43:89','Käänny siis pois heistä ja sano: »Rauha», sillä pian he saavat osansa.');
INSERT INTO "finnish" VALUES(44,1,'44:1','Ylistetty, kunnian Jumala!');
INSERT INTO "finnish" VALUES(44,2,'44:2','Pitäkää arvossa Kirjoitus, joka tuo ilmi totuuden.');
INSERT INTO "finnish" VALUES(44,3,'44:3','Totisesti olemme ilmoittanut sen siunattuna yönä; Me totisesti varoitamme herkeämättä.');
INSERT INTO "finnish" VALUES(44,4,'44:4','Siinä kaikki viisaus selvitetään,');
INSERT INTO "finnish" VALUES(44,5,'44:5','niinkuin Me käskemme; totisesti, Me lähetämme sananjulistajat,');
INSERT INTO "finnish" VALUES(44,6,'44:6','armona, joka on lähtöisin Herrastasi; Hän on totisesti kuuleva, tietävä,');
INSERT INTO "finnish" VALUES(44,7,'44:7','Herra taivaassa ja maan päällä sekä niiden välillä. Jospa te sen uskoisitte!');
INSERT INTO "finnish" VALUES(44,8,'44:8','Ei ole muuta Jumalaa kuin Hän; Hän antaa elämän ja tuottaa kuoleman, sinun ja muinaisten esi-isiesi Herra.');
INSERT INTO "finnish" VALUES(44,9,'44:9','Ei, he epäilevät, he tekevät pilkkaa.');
INSERT INTO "finnish" VALUES(44,10,'44:10','Jääkää siis odottamaan sitä päivää, jolloin taivas lähettää suuren savupilven,');
INSERT INTO "finnish" VALUES(44,11,'44:11','joka yllättää ihmiset. Se on tuskallinen rangaistus.');
INSERT INTO "finnish" VALUES(44,12,'44:12','»Herra, poista meiltä rangaistus! Totisesti, me uskomme.»');
INSERT INTO "finnish" VALUES(44,13,'44:13','Miten heitä siis pitää muistuttaa? Heidän luokseen tuli jo sananjulistaja, joka ilmoitti heille totuuden,');
INSERT INTO "finnish" VALUES(44,14,'44:14','mutta he käänsivät hänelle selkänsä sanoen: »Toisten opettama, hullu.»');
INSERT INTO "finnish" VALUES(44,15,'44:15','Me viivytämme rangaistusta vähän aikaa, mutta te tulette varmasti palaamaan vääryyteen.');
INSERT INTO "finnish" VALUES(44,16,'44:16','Päivänä, jolloin Me tartumme heihin väkevällä otteella, Me totisesti määräämme koston.');
INSERT INTO "finnish" VALUES(44,17,'44:17','Ennen heitä panimme totisesti faraon kansan koetukselle, ja sen luokse tuli jalo sananjulistaja');
INSERT INTO "finnish" VALUES(44,18,'44:18','sanoen: »Luovuttakaa minulle Jumalan palvelijat, olen totisesti teidän uskollinen sananjulistajanne.');
INSERT INTO "finnish" VALUES(44,19,'44:19','Älkää nousko Jumalaa vastaan. Minä tulen totisesti luoksenne selvin valtuuksin;');
INSERT INTO "finnish" VALUES(44,20,'44:20','etsin turvaa Häneltä, joka on minun ja teidän Herranne, jotta ette surmaisi minua,');
INSERT INTO "finnish" VALUES(44,21,'44:21','ja ellette usko minua, niin poistukaa luotani!»');
INSERT INTO "finnish" VALUES(44,22,'44:22','Ja hän kutsui avuksi Herraansa, koska he olivat syntisiä.');
INSERT INTO "finnish" VALUES(44,23,'44:23','Lähde siis matkaan palvelijoitteni kanssa öiseen aikaan; teitä tullaan varmasti ajamaan takaa,');
INSERT INTO "finnish" VALUES(44,24,'44:24','mutta jätä meri välillenne. Heidän sotajoukkonsa on totisesti hukkuva.»');
INSERT INTO "finnish" VALUES(44,25,'44:25','Kuinka monta puutarhaa ja lähdettä he saivatkaan jättää jälkeensä,');
INSERT INTO "finnish" VALUES(44,26,'44:26','viljavainioita ja ihania asumuksia');
INSERT INTO "finnish" VALUES(44,27,'44:27','ja hyvinvoinnin, josta he iloitsivat.');
INSERT INTO "finnish" VALUES(44,28,'44:28','Näin kävi, ja Me annoimme sen perinnöksi muille ihmisille.');
INSERT INTO "finnish" VALUES(44,29,'44:29','Mutta ei taivas eikä maa itkenyt heidän tähtensä, eikä heidän tuomionsa täytäntöönpanoa siirretty.');
INSERT INTO "finnish" VALUES(44,30,'44:30','Totisesti Me vapahdimme Israelin lapset häpeällisestä kurituksesta');
INSERT INTO "finnish" VALUES(44,31,'44:31','ja faraosta. Hän oli ylpeä ja kuului häikäilemättömien joukkoon.');
INSERT INTO "finnish" VALUES(44,32,'44:32','Totisesti Me kaikkiviisaudessamme valitsimme heidät kaikista kansakunnista');
INSERT INTO "finnish" VALUES(44,33,'44:33','ja annoimme heille ilmoituksemme, joka sisälsi selvän koetuksen.');
INSERT INTO "finnish" VALUES(44,34,'44:34','Totisesti nämä sanovat:');
INSERT INTO "finnish" VALUES(44,35,'44:35','»Ei ole muuta kuin ensimmäinen kuolemamme, eikä meitä herätetä uuteen eloon');
INSERT INTO "finnish" VALUES(44,36,'44:36','Herättäkääpä meille takaisin isämme, jos puhutte totta!»');
INSERT INTO "finnish" VALUES(44,37,'44:37','Kuka on parempi, hekö vai Tubban kansa ja ne, jotka elivät ennen heitä? Me kukistimme heidät, sillä totisesti he tekivät syntiä.');
INSERT INTO "finnish" VALUES(44,38,'44:38','Me emme vain huvin vuoksi ole luonut taivaita ja maata sekä mitä niiden välillä on,');
INSERT INTO "finnish" VALUES(44,39,'44:39','vaan Me olemme luonut ne täydellä todella, mutta useimmat heistä ovat tietoa vailla.');
INSERT INTO "finnish" VALUES(44,40,'44:40','Totisesti, ratkaisun päivä on heille kaikille määrätty,');
INSERT INTO "finnish" VALUES(44,41,'44:41','se päivä, jolloin ystävä ei ole ystävälle hyödyksi eivätkä he saa lainkaan apua,');
INSERT INTO "finnish" VALUES(44,42,'44:42','paitsi ne, joita Jumala armahtaa. Hän on totisesti mahtava, armollinen.');
INSERT INTO "finnish" VALUES(44,43,'44:43','Totisesti on Zakkuum-puu');
INSERT INTO "finnish" VALUES(44,44,'44:44','syntisten ravintoa.');
INSERT INTO "finnish" VALUES(44,45,'44:45','Kuin öljysakka se kiehuu heidän vatsassaan,');
INSERT INTO "finnish" VALUES(44,46,'44:46','niinkuin kuuma vesi kiehuu.');
INSERT INTO "finnish" VALUES(44,47,'44:47','»Ottakaa hänet kiinni ja raahatkaa hänet keskelle helvettiä!');
INSERT INTO "finnish" VALUES(44,48,'44:48','Kaatakaa sitten hänen päänsä yli kiehuvaa vettä hänelle piinaksi!');
INSERT INTO "finnish" VALUES(44,49,'44:49','»Maista, sinä mahtava ja kunnianarvoisa!»');
INSERT INTO "finnish" VALUES(44,50,'44:50','Totisesti, tämä on se rangaistus, jota te epäilitte.');
INSERT INTO "finnish" VALUES(44,51,'44:51','Totisesti ne, jotka karttavat pahaa, ovat turvallisessa paikassa,');
INSERT INTO "finnish" VALUES(44,52,'44:52','puutarhoissa ja lähteiden partaalla,');
INSERT INTO "finnish" VALUES(44,53,'44:53','puettuina silkkiin ja samettiin, ja katsoen toisiaan kasvoista kasvoihin.');
INSERT INTO "finnish" VALUES(44,54,'44:54','Niin on oleva, ja Me yhdistämme heidät puhtaaseen, ihanaan kumppaniin.');
INSERT INTO "finnish" VALUES(44,55,'44:55','Siellä turvassa on heille tarjolla kaikenlaisia hedelmiä,');
INSERT INTO "finnish" VALUES(44,56,'44:56','ensimmäisen kuoleman lisäksi he eivät siellä maista kuolemaa, ja Hän säästää heiltä helvetin piinan');
INSERT INTO "finnish" VALUES(44,57,'44:57','Herrasi armosta; tämä on suuri autuus.');
INSERT INTO "finnish" VALUES(44,58,'44:58','Me olemme tehnyt sen helposti tajuttavaksi omalla kielelläsi, jotta he ottaisivat varoituksesta vaarin.');
INSERT INTO "finnish" VALUES(44,59,'44:59','Odota siis! Totisesti hekin odottavat.');
INSERT INTO "finnish" VALUES(45,1,'45:1','Ylistetty, kunnian Jumala!');
INSERT INTO "finnish" VALUES(45,2,'45:2','Kirjoituksen on ilmoittanut Jumala, mahtava, viisas.');
INSERT INTO "finnish" VALUES(45,3,'45:3','Totisesti on taivaassa ja maan päällä merkkejä uskoville.');
INSERT INTO "finnish" VALUES(45,4,'45:4','Teidän oma luomisenne ja eläimet, joita Hän levittää ympäri maata, ovat tunnusmerkkejä ihmisille, joilla on varmuus.');
INSERT INTO "finnish" VALUES(45,5,'45:5','Yön ja päivän vaihtelu, sade, jonka Jumala lähettää pilvistä ja jolla Hän virvoittaa kuihtuneen maan, sekä tuulten vaihtelut ovat tunnusmerkkejä ihmisille, joilla on ymmärrystä.');
INSERT INTO "finnish" VALUES(45,6,'45:6','Nämä ovat Jumalan tunnusmerkkejä, jotka Me ilmoitamme sinulle totuudessa, mutta mihin sanomaan uskoisivat he Jumalan ja Hänen ilmoitustensa jälkeen?');
INSERT INTO "finnish" VALUES(45,7,'45:7','Voi kaikkia syntisiä valehtelijoita,');
INSERT INTO "finnish" VALUES(45,8,'45:8','jotka kuulevat Jumalan ilmoitukset lausuttavan itselleen, mutta sitten ylpeydessään paatuvat eivätkä ole niitä kuulevinaan! Julista siis heille sanoma tuskallisesta rangaistuksesta!');
INSERT INTO "finnish" VALUES(45,9,'45:9','Ja kun he saavat kuulla jonkin ilmoituksistamme, tekevät he siitä pilkkaa; nämä tulevat saamaan häpeällisen rangaistuksen.');
INSERT INTO "finnish" VALUES(45,10,'45:10','Heidän edessään on helvetti, ja siellä heitä ei lainkaan hyödytä se, mitä he ovat itselleen hankkineet, eivätkä ne, joita he Jumalan sijaan ottivat suojelijoikseen. Heitä odottaa hirveä rangaistus.');
INSERT INTO "finnish" VALUES(45,11,'45:11','Tämä on oikea johdatus, ja niillä, jotka kieltävät Herransa ilmoitukset, on oleva tuskallinen rangaistus.');
INSERT INTO "finnish" VALUES(45,12,'45:12','Jumala saattoi meren palvelemaan teitä, niin että alukset purjehtivat sitä myöten Hänen käskynsä mukaan, jotta te etsisitte Hänen armoaan ja antaisitte Hänelle kiitoksen.');
INSERT INTO "finnish" VALUES(45,13,'45:13','Hän on myös tehnyt alamaiseksenne kaiken, mitä on taivaissa ja maan päällä: totisesti on tässä tunnusmerkkejä ihmisille jotka ajattelevat.');
INSERT INTO "finnish" VALUES(45,14,'45:14','Sano uskoville, että he antaisivat anteeksi niille, jotka eivät pelkää Jumalan päivää, jolloin Hän palkitsee ihmiset heidän ansioittensa mukaan.');
INSERT INTO "finnish" VALUES(45,15,'45:15','Jos joku tekee hyvää, koituu se hänen oman sielunsa parhaaksi, ja jos joku tekee pahaa, tulee siitä hänelle itselleen vahinkoa; sitten saatetaan teidät takaisin Herranne tykö.');
INSERT INTO "finnish" VALUES(45,16,'45:16','Totisesti annoimme Israelin lapsille Pyhän Kirjan, viisauden ja profeetankutsumuksen, jakelimme heille hyvyyttä ja asetimme heidät muiden kansojen edelle.');
INSERT INTO "finnish" VALUES(45,17,'45:17','Me annoimme heille myös selvät todistukset, ja he olivat erimielisiä ja toisille kateellisia vasta päästyään osallisiksi tiedosta; totisesti Herrasi on tuomitseva heidät ylösnousemuksen päivänä siitä, mistä he kiistelivät.');
INSERT INTO "finnish" VALUES(45,18,'45:18','Sitten annoimme sinun seurata määrättyä suuntaa; noudata siis sitä äläkä niiden alhaisia toiveita, jotka ovat tietoa vailla!');
INSERT INTO "finnish" VALUES(45,19,'45:19','Totisesti he eivät hyödytä sinua vähintäkään Jumalan edessä; jumalattomat ovat toistensa ystäviä, mutta Jumala on niiden ystävä, jotka karttavat pahaa.');
INSERT INTO "finnish" VALUES(45,20,'45:20','Nämä ovat selviä todisteita ihmisille, opastus ja armo niille, joilla on varmuus.');
INSERT INTO "finnish" VALUES(45,21,'45:21','Luulevatko ne, jotka ovat tehneet pahoja töitä, että kohtelen heitä samoin kuin niitä, jotka uskovat ja tekevät hyviä töitä, ja että elämä ja kuolema heille tulee olemaan samanlainen? Kehno on heidän arvostelunsa.');
INSERT INTO "finnish" VALUES(45,22,'45:22','Jumala loi taivaat ja maan totuutta noudattaen, niin että jokainen sielu palkittaisiin ansionsa mukaan ja ettei kenellekään tehtäisi vääryyttä.');
INSERT INTO "finnish" VALUES(45,23,'45:23','Mitä ajattelet ihmisestä, joka pitää alhaista haluaan jumalanaan ja jonka Jumala tietäen antaa harhautua, jonka korvat ja sydämen Hän sinetöi ja jonka silmät Hän peittää verholla? Kuka voi ohjata häntä paitsi Jumala? Ettekö siis tahdo noudattaa kehoitusta?');
INSERT INTO "finnish" VALUES(45,24,'45:24','He sanovat myös: »Ei ole olemassa muuta kuin tämä maallinen elämämme; me elämme ja kuolemme, ja mikään muu kuin aika ei hävitä meitä.» Mutta siitä heillä ei ole mitään tietoa, he ainoastaan lausuvat arveluja.');
INSERT INTO "finnish" VALUES(45,25,'45:25','Ja kun selvät ilmoituksemme sanellaan heille, on heidän vastaväitteinänsä vain tämä: »Herättäkää meille isämme takaisin, jos puhutte totta!»');
INSERT INTO "finnish" VALUES(45,26,'45:26','Sano: »Jumala antaa teille elämän, sitten lähettää Hän teille kuoleman ja sitten kokoaa Hän teidät ylösnousemuksen päivänä. Tämä on varma, mutta useimmilla ihmisillä ei ole siitä tietoa.»');
INSERT INTO "finnish" VALUES(45,27,'45:27','Jumalan on valta taivaassa ja maan päällä, ja sinä päivänä, jolloin hetki tulee, sinä päivänä hukkuvat ne, jotka harjoittavat vääryyttä.');
INSERT INTO "finnish" VALUES(45,28,'45:28','Sinä saat nähdä kaikkien kansojen notkistavan polvensa; jokainen kansa kutsutaan pyhän kirjansa ääreen. »Tänä päivänä saatte palkan siitä, mitä olette tehneet.');
INSERT INTO "finnish" VALUES(45,29,'45:29','Tämä Meidän Kirjamme sanoo teistä totuuden; totisesti, Me kirjoitimme tekonne muistiin.»');
INSERT INTO "finnish" VALUES(45,30,'45:30','Mitä tulee niihin, jotka uskoivat ja tekivät hyviä töitä, ne heidän Herransa sulkee armoonsa; tämä on ylin autuus.');
INSERT INTO "finnish" VALUES(45,31,'45:31','Mitä taas tulee niihin, jotka eivät uskoneet, niin »eikö Minun ilmoituksiani ole luettu teille? Mutta te olitte ylpeitä, niin, te olitte syntisiä ihmisiä.»');
INSERT INTO "finnish" VALUES(45,32,'45:32','Ja kun sanottiin: »Jumalan lupaus on totisesti vanhurskas eikä tuomionhetkestä ole epäilystä», sanoitte te: »Me emme tiedä, millainen hetki se on; me emme luule sen koittavan, sillä se on vain ennakkoluuloa, eikä meillä ole siitä lainkaan varmuutta.»');
INSERT INTO "finnish" VALUES(45,33,'45:33','Mutta paha, mitä he ovat tehneet, tulee heille selväksi, ja se, mitä he pilkkasivat, on kohdistuva heihin itseensä.');
INSERT INTO "finnish" VALUES(45,34,'45:34','Ja heille sanotaan: »Tänään Me hylkäämme teidät, niinkuin te olitte välinpitämättömiä tämän päivän tulemisesta. Teidän olinpaikkanne on tuli, ja siellä teillä ei ole ketään auttajaa.');
INSERT INTO "finnish" VALUES(45,35,'45:35','Näin käy sentähden, että te piditte Jumalan ilmoituksia pilkkana ja että maallinen elämä on houkutellut teidät harhaan.» Mutta tuona päivänä ei heitä vapahdeta eikä heille osoiteta lempeyttä.');
INSERT INTO "finnish" VALUES(45,36,'45:36','Jumalalle kuuluu kaikki kiitos, taivaitten Herralle, maan Herralle, maailmojen Herralle!');
INSERT INTO "finnish" VALUES(45,37,'45:37','Hänen on kunnia taivaassa ja maan päällä: Hän on mahtava, viisas.');
INSERT INTO "finnish" VALUES(46,1,'46:1','Ylistetty, kunnian Jumala.');
INSERT INTO "finnish" VALUES(46,2,'46:2','Pyhän Kirjan ilmoitus on saatu Jumalalta, mahtavalta, viisaalta.');
INSERT INTO "finnish" VALUES(46,3,'46:3','Me olemme luonut taivaat ja maan ja mitä niiden välillä on vain totuudessa ja määrätyksi ajaksi, mutta ne, jotka ovat epäuskoisia, kääntyvät pois saamistaan varoituksista.');
INSERT INTO "finnish" VALUES(46,4,'46:4','Sano: »Oletteko ajatelleet niitä, joita avuksenne huudatte Jumalan sijasta? Osoittakaa minulle, kuinka suuren osan ne ovat luoneet maasta! Tai onko heillä osuutta taivaisiin? Tuokaa minulle kirja, joka on ilmoitettu ennen tätä, tai rippeitä tiedosta, jos rakastatte totuutta!»');
INSERT INTO "finnish" VALUES(46,5,'46:5','Kuka erehtyy enemmän kuin se, joka Jumalan asemesta kutsuu niitä, jotka eivät ennen ylösnousemuksen päivää vastaa hänelle eivätkä huoli hänen kutsustaan?');
INSERT INTO "finnish" VALUES(46,6,'46:6','Ja kun ihmiset kootaan yhteen, tulee heistä näiden vihamiehiä ja he kieltävät näiden palvoneen heitä.');
INSERT INTO "finnish" VALUES(46,7,'46:7','Ja vaikka tunnusmerkkimme selvästi luetaan heille, väittävät epäuskoiset totuudesta, kun se tulee heidän osakseen: »Tämä on selvää noituutta.»');
INSERT INTO "finnish" VALUES(46,8,'46:8','Tai he sanovat: »Hän on keksinyt sen». Sano: »Jos minä olen keksinyt sen, ette te voi enää auttaa minua Jumalan edessä. Hän tietää parhaiten, mitä olette siitä uskotelleet. Hän on pätevä todistaja minun ja teidän asiassanne; niin, Hän antaa anteeksi ja on armollinen.');
INSERT INTO "finnish" VALUES(46,9,'46:9','Sano: »Minä en ole sananjulistajista ensimmäinen enkä tiedä, mitä tulee tapahtumaan minulle tai teille; minä noudatan vain sitä, mikä on ilmoitettu minulle, ja olen vain julkinen varoittaja.»');
INSERT INTO "finnish" VALUES(46,10,'46:10','Sano: »Oletteko ajatelleet, että se tulee Jumalalta ja että te ette usko siihen, mutta eräs todistaja Israelin lasten joukosta on todistanut sen oikeaksi; hän uskoi, mutta te olette ylpeitä.» Totisesti, Jumala ei kaitse jumalatonta kansaa.');
INSERT INTO "finnish" VALUES(46,11,'46:11','Epäuskoiset sanovat uskovaisista: »Jos koraanissa olisi ollut jotakin hyvää, eivät he sen omaksumisessa olisi päässeet meistä edelle», ja koska he eivät tahdo antaa sen ohjata itseään oikeaan, he sanovat: »Se sisältää vain vanhoja valhetaruja.»');
INSERT INTO "finnish" VALUES(46,12,'46:12','Ennen sitä oli jo Mooseksen kirja oppaana ja armonosoituksena, ja tämä kirja vahvistaa sen arabian kielellä, varoitukseksi niille, jotka tekevät vääryyttä, ja hyvänä sanomana niille, joiden teot ovat hyviä.');
INSERT INTO "finnish" VALUES(46,13,'46:13','Totisesti niillä, jotka sanovat: »Jumala on Herramme» ja pysyvät oikealla tiellä, ei ole pelkoa eikä heidän tarvitse murehtia.');
INSERT INTO "finnish" VALUES(46,14,'46:14','Heistä tulee paratiisin asukkaita ikuisiksi ajoiksi palkaksi siitä, mitä he tekivät.');
INSERT INTO "finnish" VALUES(46,15,'46:15','Me olemme myös käskenyt ihmisen tehdä hyvää vanhemmilleen; hänen äitinsä on kantanut hänet tuskalla ja synnyttänyt hänet kivulla, ja kantaminen ja vieroittaminen kestää kolmekymmentä kuukautta; ja kun hän saavuttaa kypsyysiän ja täyttää neljäkymmentä vuotta, hän sanoo: »Herra, suo minun olla kiitollinen siitä armosta, jota olet osoittanut minulle ja vanhemmilleni, suo että pystyisin hyviin tekoihin, jotka miellyttävät Sinua, ja salli kaiken koitua jälkeläisteni parhaaksi; totisesti, minä turvaan Sinuun ja kuulun Sinun alamaisiisi.»');
INSERT INTO "finnish" VALUES(46,16,'46:16','Näiltä vastaanotamme parhaan, mitä he ovat tehneet, ja annamme anteeksi heidän pahat tekonsa, koska he kuuluvat paratiisin asukkaisiin sen vanhurskaan lupauksen mukaan, mikä heille on annettu.');
INSERT INTO "finnish" VALUES(46,17,'46:17','Mutta jos joku sanoo vanhemmilleen: »Häpeä teille! Kuinka lupaatte minulle, että minut herätetään henkiin, kun jo muut sukupolvet ovat kuolleet ennen minua?» niin että he kumpikin huutavat Jumalalta apua: »Voi sinua! Usko, Jumalan lupaus on totisesti varma» ja hän vastaa: »Nämä ovat vain muinaistaruja»,');
INSERT INTO "finnish" VALUES(46,18,'46:18','niin juuri tällaiset ovat niitä, joiden suhteen on käynyt toteen ennustus dzinneistä ja ihmispolvista, jotka ovat kuolleet jo ennen heitä; totisesti ovat he tuhon omat.');
INSERT INTO "finnish" VALUES(46,19,'46:19','Jokaista varten on olemassa eri asteita, aina sen mukaan, mitä he ovat tehneet, jotta minä saattaisin maksaa heille täyden palkan teoistaan, ilman että kukaan kärsisi vääryyttä.');
INSERT INTO "finnish" VALUES(46,20,'46:20','Sinä päivänä, jolloin epäuskoiset saatetaan tulen eteen, kuuluu sana: »Te tuhlasitte kaiken hyvän maallisessa elämässänne, nauttien siitä lyhyen ajan, mutta tänään palkitaan teidät masentavalla kurituksella, sentähden että olitte ilman syytä ylpeitä maan päällä ja teitte syntiä.');
INSERT INTO "finnish" VALUES(46,21,'46:21','Muistakaa Aadin veljeä, kun hän varoitti kansaansa hiekkakunnailla - ja varoittajia on totisesti ollut sekä ennen häntä että hänen jälkeensä - sanoen: »Älkää palvelko ketään muuta kuin Jumalaa! Totisesti pelkään puolestanne kauhun päivän rangaistusta.»');
INSERT INTO "finnish" VALUES(46,22,'46:22','He vastasivat: »Oletko tullut luoksemme kääntääksesi meidät pois jumalistamme? Näytä sitten meille se, millä meitä peloittelet, jos olet niitä, jotka pysyvät totuudessa.»');
INSERT INTO "finnish" VALUES(46,23,'46:23','Silloin hän sanoi: »Vain Jumalalla on tieto, ja minä julistan teille sanomaa, mikä on lähetetty mukanani, mutta huomaan, että olette ymmärtämätöntä kansaa.»');
INSERT INTO "finnish" VALUES(46,24,'46:24','Ja kun he näkivät pilven ilmestyvän taivaalle ja lähestyvän heidän laaksojaan, he sanoivat: »Se on pilvi, joka antaa meille sadetta.» - »Ei, se on se, mitä te halusitte jouduttaa, tuulenpuuska, joka tuo mukanaan tuskallisen rangaistuksen');
INSERT INTO "finnish" VALUES(46,25,'46:25','ja joka hävittää kaiken Herran käskystä.» Ja niin ei heistä seuraavana aamuna näkynyt muuta kuin heidän asumuksensa; näin Me palkitsemme syntisiä ihmisiä.');
INSERT INTO "finnish" VALUES(46,26,'46:26','Tosiaan Me annoimme heille vallan, jota emme ole antanut teille, ja Me annoimme heille kuulon, näön ja sydämen, mutta heidän korvansa, silmänsä ja sydämensä eivät hyödyttäneet heitä vähääkään, koska he kielsivät Jumalan ilmoitukset, ja se, mitä he pilkkasivat, kohtasi heidät.');
INSERT INTO "finnish" VALUES(46,27,'46:27','Olemme todella hävittänyt ympärillänne olevat kaupungit, ja Me uudistamme ilmoituksemme, jotta he kääntyisivät.');
INSERT INTO "finnish" VALUES(46,28,'46:28','Mutta miksi eivät heitä sitten auttaneet ne, joita he pitivät Jumalan ohella jumalina ja välimiehinä? Ei, he ovat pettäneet heidät; tämä oli heidän valheensa ja väärennyksensä.');
INSERT INTO "finnish" VALUES(46,29,'46:29','Ja kun Me ohjasimme osan dzinneistä sinun luoksesi kuulemaan Koraania, sanoivat he tultuaan paikalle: »Kuunnelkaa tarkasti!» ja kun se oli lopussa, palasivat he kansansa luo varoittamaan sitä.');
INSERT INTO "finnish" VALUES(46,30,'46:30','He sanoivat: »Kansalaisemme, me olemme kuulleet Kirjaa, joka on ilmoitettu Mooseksen jälkeen ja joka vahvistaa sen, mitä oli ennen, johtaen totuuteen ja oikealle tielle.');
INSERT INTO "finnish" VALUES(46,31,'46:31','Kansalaisemme, ottakaa vastaan jumalallinen kutsuja ja uskokaa Häneen, niin Hän antaa teille syntinne anteeksi ja suojelee teidät tuskallisesta rangaistuksesta.»');
INSERT INTO "finnish" VALUES(46,32,'46:32','Sillä ne, jotka eivät ota vastaan Jumalan lähettilästä, eivät löydä maan päältä pakopaikkaa eikä heillä hänen sijastaan ole ketään suojelijaa. Nämä ovat selvästi harhaan johdettuja.');
INSERT INTO "finnish" VALUES(46,33,'46:33','Eivätkö he ole ottaneet huomioon sitä, että Jumala, joka loi taivaat ja maan eikä väsynyt niitä luodessaan, voi antaa kuolleille elämän? Niin, Hän on totisesti kaikkivoipa.');
INSERT INTO "finnish" VALUES(46,34,'46:34','Sinä päivänä, jolloin epäuskoiset viedään tulen eteen, kysytään: »Eikö kaikki ollut totta?» He vastaavat: »Kyllä, Herramme nimeen.» Silloin Hän sanoo: »Maistakaa siis rangaistustanne, koska olitte epäuskoisia!»');
INSERT INTO "finnish" VALUES(46,35,'46:35','Kestäkää siis kärsivällisinä, niinkuin sananjulistajat kestivät, älkääkä kiirehtikö heidän tuomiotaan! Sinä päivänä, jolloin he näkevät, mitä heille on luvattu, heistä tuntuu siltä, kuin he eivät olisi odottaneet kauemmin kuin yhden tunnin päivästä. Riittäköön tämä varoitukseksi. Pitäisikö muitten tulla tuhotuiksi kuin syntisten ihmisten?');
INSERT INTO "finnish" VALUES(47,1,'47:1','Jotka eivät usko, vaan kääntyvät pois Jumalan tieltä, niiden teot Hän tekee tyhjäksi.');
INSERT INTO "finnish" VALUES(47,2,'47:2','Mutta jotka tekevät hyvää ja uskovat sen, mitä Muhammedille on ilmoitettu, joka on heidän Herraltaan saatu totuus, heidän pahat tekonsa Hän pyyhkii pois ja parantaa heidän asemansa.');
INSERT INTO "finnish" VALUES(47,3,'47:3','Näin käy siksi, että epäuskoiset seuraavat valheen jälkiä ja uskovaiset noudattavat totuutta, joka tulee heidän Herraltaan. Näin Jumala asettaa ihmisille esikuvia.');
INSERT INTO "finnish" VALUES(47,4,'47:4','Kun siis kohtaatte taistelussa uskottomat, niin nujertakaa heiltä niskat, niin että voitatte heidät! Sitten ottakaa heidät vangiksi ja jälkeenpäin joko suosiollisesti vapauttakaa heidät tai antakaa heidän lunastaa itsensä vapaiksi, kun sota on päättynyt! Niin on oleva. Jos Jumala olisi tahtonut, olisi Hän varmasti voinut kostaa heille, mutta näin Hän tahtoo koetella teitä toistenne avulla. Jotka saavat surmansa Jumalan tiellä, heidän tekojensa ei Hän millään muotoa salli raueta tyhjiin.');
INSERT INTO "finnish" VALUES(47,5,'47:5','Hän ohjaa heitä ja parantaa heidän asemansa,');
INSERT INTO "finnish" VALUES(47,6,'47:6','sekä antaa heidän päästä paratiisiin, jonka Hän on tehnyt heille tunnetuksi.');
INSERT INTO "finnish" VALUES(47,7,'47:7','Jos uskovaiset autatte Jumalan asiaa, Hän auttaa teitä ja tukee askeleitanne.');
INSERT INTO "finnish" VALUES(47,8,'47:8','Mutta niitä, jotka ovat epäuskoisia, odottaa tuho, ja Hän tekee tyhjäksi heidän työnsä.');
INSERT INTO "finnish" VALUES(47,9,'47:9','Näin tapahtuu sentähden, että he ylenkatsoivat Jumalan ilmoituksia, ja niin Hän antaa heidän tekojensa raueta tyhjiin.');
INSERT INTO "finnish" VALUES(47,10,'47:10','Eivätkö he ole matkanneet ympäri maata ja nähneet, minkälaisen lopun saivat ne, jotka elivät ennen heitä? Jumala saattoi heidät perikatoon, ja uskottomilla on oleva sama kohtalo.');
INSERT INTO "finnish" VALUES(47,11,'47:11','Näin tapahtuu, koska Jumala on uskovien suojelija ja koska uskottomilla ei ole ketään suojelijaa.');
INSERT INTO "finnish" VALUES(47,12,'47:12','Totisesti antaa Jumala niiden, jotka uskovat ja tekevät hyvää, käydä sisälle paratiisiin, jonka kautta joet virtaavat, mutta ne, jotka ovat epäuskoisia ja nauttivat ja syövät kuin elukat, heidän olinpaikkansa on tuli.');
INSERT INTO "finnish" VALUES(47,13,'47:13','Miten monta kaupunkia olemmekaan tuhonnut, kaupunkeja, jotka olivat mahtavampia kuin se, joka on karkoittanut sinut, eikä niillä ollut ketään auttajaa.');
INSERT INTO "finnish" VALUES(47,14,'47:14','Onko se, joka on saanut selvän todistuksen Herraltaan niiden kaltainen, joille pahat tekonsa ovat ihanat ja jotka noudattavat alhaisia halujaan.');
INSERT INTO "finnish" VALUES(47,15,'47:15','Tällainen on kuvaus paratiisista, joka on luvattu niille, jotka karttavat pahaa: siellä on virtoja, joiden vesi ei samene, maitovirtoja, joiden maku ei muutu, viinivirtoja, suloisia juoda, ja kirkkaita hunajavirtoja. Ne, jotka siellä asuvat, saavat Herraltaan kaikenlaisia hedelmiä ja anteeksiannon. Ovatko he niinkuin ne, joilla on asuntona tuli ja jotka saavat juoda kiehuvaa vettä, niin että se raastaa rikki heidän sisuksensa?');
INSERT INTO "finnish" VALUES(47,16,'47:16','Jotkut näistä tosin yrittävät kuunnella sinua, mutta kun he lähtevät luotasi, he sanovat niille, jotka ovat saaneet tiedon: »Mitä uutta hän sanoi?» Näiden sydämen Jumala on paaduttanut, ja he seuraavat alhaisia himojaan.');
INSERT INTO "finnish" VALUES(47,17,'47:17','Mutta niille, jotka seuraavat oikeata suuntaa, Hän tuo johdatustaan runsaammin ja antaa heille varjeluksensa.');
INSERT INTO "finnish" VALUES(47,18,'47:18','Mitä muuta epäuskoiset siis odottavat, kuin että hetki tulisi heille äkkiä? Nyt ovat todella sen enteet jo tulleet, mutta mitä apua heillä on kehoituksesta, kun hetki on tullut?');
INSERT INTO "finnish" VALUES(47,19,'47:19','Tiedä siis, ettei ole muuta jumalaa kuin Jumala, ja pyydä anteeksiantoa synneistä itsellesi ja uskoville miehille ja naisille. Jumala tuntee paikan, mihin palaatte ja missä asuntonne on oleva.');
INSERT INTO "finnish" VALUES(47,20,'47:20','Uskovaiset sanovat: »Miksi ei ole yhtään suuraa ilmoitettu?» Mutta kun selkeä suura ilmoitetaan ja siinä mainitaan taistelu, näet kuinka ne, joiden sydämessä on sairaus, katsovat sinuun niinkuin se, jonka valtaa kuoleman uupumus. Voi heitä silloin!');
INSERT INTO "finnish" VALUES(47,21,'47:21','Kuuliaisuus ja lempeä sana sopisi heille paremmin; mutta kun asia on päätöksessä, olisi heille totisesti parempi, jos he pysyisivät Jumalalle uskollisina.');
INSERT INTO "finnish" VALUES(47,22,'47:22','Mutta jos teillä olisi valta, tekisitte varmasti ilkitöitä ja katkoisitte sukulaisuussiteenne.');
INSERT INTO "finnish" VALUES(47,23,'47:23','Nämä Jumala on kironnut ja saattanut kuuroiksi sekä sokaissut heidän silmänsä.');
INSERT INTO "finnish" VALUES(47,24,'47:24','Eivätkö he siis tutkistele Koraania? Eivät, heidän sydämensä ovat lukitut.');
INSERT INTO "finnish" VALUES(47,25,'47:25','Niille, jotka palaavat entiselleen sen jälkeen, kun selvä johdatus on heille annettu, on saatana totisesti tehnyt sen helpoksi asiaksi, ja Hän antaa heille vapaat kädet.');
INSERT INTO "finnish" VALUES(47,26,'47:26','Sillä he sanovat salaa niille, jotka ylenkatsovat Jumalan ilmoituksia: »Me tahdomme totella teitä joissakin asioissa.» Mutta Jumala tuntee heidän salaisuutensa.');
INSERT INTO "finnish" VALUES(47,27,'47:27','Mutta kuinka käy, kun enkelit tuovat heille kuoleman käskyn, lyöden heitä kasvoihin ja selkään?');
INSERT INTO "finnish" VALUES(47,28,'47:28','Sillä he noudattavat sitä, mikä ei miellytä Jumalaa, vaan on Hänen tahtoaan vastaan; siksi Hän on tehnyt tyhjäksi heidän tekonsa.');
INSERT INTO "finnish" VALUES(47,29,'47:29','Vai ajattelevatko ne, joiden sydämessä on sairaus, että Jumala ei tuo ilmi heidän ilkitöitään?');
INSERT INTO "finnish" VALUES(47,30,'47:30','Ja jos Me tahtoisimme, niin olisimme voinut asettaa niin, että olisit totisesti tuntenut heidät tuntomerkeistään; ja totisesti sinä voit tuntea heidät puheestaan; niin, Jumala tuntee tekonne.');
INSERT INTO "finnish" VALUES(47,31,'47:31','Totisesti koettelemme teitä siksi, kunnes olemme oppinut tuntemaan teistä ne, jotka ovat urhoollisia ja kestäviä, koettelemme, kunnes asianne on Meille ilmeinen.');
INSERT INTO "finnish" VALUES(47,32,'47:32','Totisesti, ne, jotka eivät usko, vaan kääntyvät pois Jumalan tieltä vastustaen sananjulistajaa, sitten kun johdatus on heille ilmoitettu, ne eivät voi vahingoittaa Jumalaa millään tavoin, mutta Hän tekee tyhjäksi heidän tekonsa.');
INSERT INTO "finnish" VALUES(47,33,'47:33','Te, jotka uskotte, totelkaa Jumalaa, olkaa kuuliaisia sananjulistajalle älkääkä saattako tekojanne raukeamaan!');
INSERT INTO "finnish" VALUES(47,34,'47:34','Totisesti ne, jotka eivät usko, vaan hylkäävät Jumalan tien, kuolevat, koska he ovat epäuskoisia, eikä Jumala anna heille mitään anteeksi.');
INSERT INTO "finnish" VALUES(47,35,'47:35','Älkää siis olko heikkoja anoen rauhaa, sillä te olette voitolla ja Jumala on kanssanne. Hän ei tee tekojanne mitättömiksi.');
INSERT INTO "finnish" VALUES(47,36,'47:36','Maallinen elämä on vain turhaa ilvettä ja leikkiä, mutta jos te uskotte ja kartatte pahaa, Hän antaa teille palkkanne eikä kysy teiltä mitään tavaraa.');
INSERT INTO "finnish" VALUES(47,37,'47:37','Jos Hän vaatisi teiltä sitä, te olisitte itaria ja Hän saattaisi pahuutenne ilmi.');
INSERT INTO "finnish" VALUES(47,38,'47:38','Katso, te kuulutte niihin, jotka ovat kutsutut uhraamaan varojansa Jumalan asian edistämiseksi, mutta teidän joukossanne on kitsaita; ja jos joku on kitsas, se on vain hänen oman sielunsa vahingoksi. Jumalalta ei mitään puutu, mutta te tarvitsette Häntä, ja jos te käännytte pois, asettaa hän sijaanne toisia, jotka eivät ole niinkuin te.');
INSERT INTO "finnish" VALUES(48,1,'48:1','Totisesti olemme antanut sinulle ilmeisen voiton,');
INSERT INTO "finnish" VALUES(48,2,'48:2','jotta Jumala antaisi sinulle anteeksi syntisi, sekä ne, jotka ovat ennen tehdyt, että ne, jotka ovat jäljellä, ja saattaisi armonsa täydelliseksi sinua kohtaan sekä johtaisi sinut oikealle tielle,');
INSERT INTO "finnish" VALUES(48,3,'48:3','ja jotta Jumala voisi antaa sinulle mahtavaa apuaan.');
INSERT INTO "finnish" VALUES(48,4,'48:4','Hän se lähetti rauhan uskovien sydämiin, jotta heidän uskonsa lisääntyisi - Jumalan ovat taivaitten ja maan sotajoukot, niin, Jumalan on tieto ja viisaus,');
INSERT INTO "finnish" VALUES(48,5,'48:5','jotta Hän antaisi uskovain miesten ja naisten päästä paratiisiin, jonka kautta joet virtaavat, ja asua siellä ikuisesti, ja poistaisi heiltä heidän pahat tekonsa. Tämä on suuri autuus Jumalan luona.');
INSERT INTO "finnish" VALUES(48,6,'48:6','Ja jotta Hän kurittaisi ulkokullatut ja monia jumalia palvelevat, sekä miehet että naiset, ne, joilla on huonoja ajatuksia Jumalasta. Heidän käy huonosti; Jumalan viha on heidän päällään, Hän on kironnut heidät ja valmistanut heitä varten helvetin. Kurja on se kokoontumispaikka.');
INSERT INTO "finnish" VALUES(48,7,'48:7','Jumalalle kuuluvat sotajoukot taivaassa ja maan päällä. Jumala on mahtava, viisas.');
INSERT INTO "finnish" VALUES(48,8,'48:8','Olemme totisesti lähettänyt sinut todistajana, hyvän sanoman viejänä ja varoittajana,');
INSERT INTO "finnish" VALUES(48,9,'48:9','jotta uskoisitte Jumalaan ja Hänen sananjulistajaansa, tukisitte ja kunnioittaisitte häntä sekä julistaisitte Hänen kiitostaan aamuin ja illoin.');
INSERT INTO "finnish" VALUES(48,10,'48:10','Totisesti, ne, jotka vannovat uskollisuutta sinulle, vannovat vain uskollisuutta Jumalalle. Jumalan käsi on heidän kättensä yllä. Sentähden, jos joku rikkoo valansa, hän rikkoo sen vain oman sielunsa vahingoksi, mutta joka täyttää sen, mitä on sopinut Jumalan kanssa, sille Hän antaa suuren palkinnon.');
INSERT INTO "finnish" VALUES(48,11,'48:11','Ne erämaan asukkaat, jotka jäivät retkestä jälkeen, sanovat sinulle: »Omaisuutemme ja perheemme pidättivät meidät työssä, pyydä siis puolestamme anteeksi». He puhuvat kielellään sellaista, mitä ei ole heidän sydämessään. Sano: »Kuka voi torjua pois teistä mitään Jumalan päätöstä, joko Hän sitten tahtoo vahingoittaa teitä tai tehdä teille hyvää?» Ei, Jumalalla on tieto teoistanne.');
INSERT INTO "finnish" VALUES(48,12,'48:12','Niin, te luulitte, että sananjulistaja ja uskovat eivät milloinkaan palaisi omiensa luo, ja tämä ajatus oli teille mieleinen, mutta te ajattelitte pahasti ja te olette perikatoon tuomittu kansa.');
INSERT INTO "finnish" VALUES(48,13,'48:13','Jos joku ei usko Jumalaan ja Hänen sananjulistajaansa, Me olemme totisesti epäuskoisia varten varannut polttavan tulen.');
INSERT INTO "finnish" VALUES(48,14,'48:14','Jumalan on valta taivaassa ja maan päällä. Hän armahtaa ja rankaisee ketä tahtoo; niin, Jumala on anteeksiantava, armollinen.');
INSERT INTO "finnish" VALUES(48,15,'48:15','Jälkeen jäävät sanovat, kun te lähdette retkelle saalista ottamaan: »Antakaa meidän seurata teitä.» He tahtovat muuttaa Jumalan sanan. Sano: »Ette suinkaan saa seurata meitä; niin on Jumala sanonut.» Mutta he sanovat: »Ei, te kadehditte meitä.» Heillä on vain vähän ymmärrystä.');
INSERT INTO "finnish" VALUES(48,16,'48:16','Sano jälkeen jääneille erämaan asukkaille: »Teidät kutsutaan pian taisteluun kansaa vastaan, jonka urhoollisuus on suuri; te taistelette heitä vastaan siksi kunnes he alistuvat. Jos te tottelette, Jumala antaa teille hyvän palkan, mutta jos käännytte pois, kuten ennen, Hän kurittaa teitä tuskallisella rangaistuksella.»');
INSERT INTO "finnish" VALUES(48,17,'48:17','Mutta syytön on, joka sokeuden, rampuuden tai sairauden tähden jää kotiin; ja jos joku on kuuliainen Jumalalle ja Hänen sananjulistajalleen, Hän ottaa hänet paratiisiinsa, jossa virrat ovat vuolaat, mutta sitä, joka kääntyy pois, Hän rankaisee tuskallisella kurituksella.');
INSERT INTO "finnish" VALUES(48,18,'48:18','Totisesti oli Jumala tyytyväinen uskoviin, kun he vannoivat sinulle uskollisuutta puun alla; Hän tiesi, mitä oli heidän sydämissään, ja lähetti heille rauhan sekä palkitsi heidät pikaisella voitolla');
INSERT INTO "finnish" VALUES(48,19,'48:19','ja runsaalla saaliilla, jonka he ottivat. Jumala on mahtava, viisas.');
INSERT INTO "finnish" VALUES(48,20,'48:20','Jumala lupasi teille paljon saalista, jota tulisitte ottamaan, sitten Hän antoi tätä pikaisesti teille ja pidätti teistä ihmisten kädet; tämän Hän teki, jotta se olisi merkki uskoville ja jotta Hän johtaisi teitä oikealla polulla.');
INSERT INTO "finnish" VALUES(48,21,'48:21','Muutakin saalista, jota vielä ette ole voineet saada, Jumala on varannut; totisesti, Jumala on kaikkivoipa.');
INSERT INTO "finnish" VALUES(48,22,'48:22','Jos ne, jotka eivät usko, olisivat taistelleet teitä vastaan, he varmasti olisivat peräytyneet, ja sitten heillä ei olisi ollut ketään suojelijaa eikä auttajaa.');
INSERT INTO "finnish" VALUES(48,23,'48:23','Sellainen on Jumalan käsky, jota on todella noudatettu jo aikaisemminkin, etkä sinä ole löytävä Jumalan käskyä toiseksi muuttuneena.');
INSERT INTO "finnish" VALUES(48,24,'48:24','Hän pidätti heidän kätensä teistä ja teidän kätenne heistä Mekan laaksossa annettuaan teille voiton heistä; niin, Jumala näkee, mitä teette.');
INSERT INTO "finnish" VALUES(48,25,'48:25','Juuri he eivät uskoneet, vaan pidättivät teidät pyhästä temppelistä sekä uhrista, joka oli estetty saapumasta määräpaikkaansa; ellei heidän joukossaan olisi ollut uskovia miehiä ja naisia, jotka te tietämättömyydessänne olisitte saattaneet tallata jalkoihinne ja näin tahtomattanne tehdä itsenne syypäiksi rikokseen, emme olisi pidättänyt kättänne heistä. Näin tapahtui, jotta Jumala saattaisi sulkea armoonsa kenet tahtoo. Jos he olisivat olleet kaukana hajallaan toisistansa, Me olisimme totisesti kurittanut epäuskoisia heidän joukossaan tuskallisella rangaistuksella.');
INSERT INTO "finnish" VALUES(48,26,'48:26','Ne, jotka eivät uskoneet, kantoivat sydämissään tietämättömyyden ynseyttä, mutta Jumala lähetti rauhansa sananjulistajalleen ja uskoville ja käski heitä pitämään sanansa ja karttamaan pahaa, mihin he olivat oikeutettuja ja soveliaita. Jumala on kaikkitietävä.');
INSERT INTO "finnish" VALUES(48,27,'48:27','Jumala on antanut sananjulistajansa näyn täyttyä. Te tulette varmasti astumaan sisään pyhään temppeliin, jos Jumala niin tahtoo, turvassa, pää ajettuna ja hiukset leikattuina, pelkoa tuntematta, sillä Hän tietää, mitä te ette tiedä, ja niin on Hän hankkinut teille pikaisen voiton.');
INSERT INTO "finnish" VALUES(48,28,'48:28','Hän lähetti sananjulistajansa, mukanaan ohjeet ja totuuden uskonto, saattamaan tämän voittoon kaikkien muiden uskontojen rinnalla; niin, Jumala on pätevä todistaja.');
INSERT INTO "finnish" VALUES(48,29,'48:29','Muhammed on Jumalan sananjulistaja, ja ne, jotka ovat hänen kanssaan, ovat järkähtämättömiä uskottomille, mutta laupiaita toisiaan kohtaan. Sinä olet näkevä heidän kumartuvan ja polvistuvan, anoen armoa ja suosiota Jumalalta, ja heillä on merkkejä kasvoissaan paljosta maahan kumartamisesta. Niin kuvataan heitä Toorassa. Ja evankeliumissa he ovat kuin siemenkylvö, mikä työntää esiin vesojaan ja sitten voimistuttaa niitä, niin että ne tulevat vahvoiksi ja ovat lujasti kiinni varsissa ilahduttaen kylvömiehiä, jotta se ärsyttäisi epäuskoisia. Jumala on luvannut niille, jotka uskovat ja tekevät hyvää, anteeksiannon ja suuren palkinnon.');
INSERT INTO "finnish" VALUES(49,1,'49:1','Te, jotka uskotte, älkää estäkö Jumalan ja Hänen sananjulistajansa läsnäoloa, vaan pitäkää huoli velvollisuudestanne Jumalaa kohtaan. Totisesti, Jumala on kuuleva, tietävä.');
INSERT INTO "finnish" VALUES(49,2,'49:2','Te, jotka uskotte, älkää korottako ääntänne profeetan äänen yli älkääkä puhuko hänelle niin äänekkäästi, kuin puhutte toinen toisillenne, jotta tekojanne ei huomaamattanne tehtäisi tyhjiksi.');
INSERT INTO "finnish" VALUES(49,3,'49:3','Totisesti ne, jotka alentavat äänensä Jumalan sananjulistajan edessä, ovat niitä, joiden sydäntä Jumala on koetellut pahan vastustamisessa; he saavat anteeksiannon ja suuren palkinnon.');
INSERT INTO "finnish" VALUES(49,4,'49:4','Useimmat niistä, jotka kutsuvat sinua pois sisähuoneista, ovat totisesti ymmärrystä vailla.');
INSERT INTO "finnish" VALUES(49,5,'49:5','Ja jos he odottaisivat kärsivällisesti siksi, kunnes sinä tulet heidän luokseen, olisi se varmasti heille parempi; niin, Jumala on anteeksiantavainen, armollinen.');
INSERT INTO "finnish" VALUES(49,6,'49:6','Te uskovaiset, jos jumalaton ihminen tulee luoksenne tuoden jonkin sanoman, niin tutkikaa sitä tarkoin, ettette tietämättänne vahingoittaisi ihmisiä ja sitten saisi katua, mitä olette tehneet.');
INSERT INTO "finnish" VALUES(49,7,'49:7','Tietäkää, että keskuudessanne on Jumalan sananjulistaja! Jos hän tottelisi teitä monessa asiassa, te totisesti joutuisitte ahdinkoon, mutta Jumala on tehnyt uskon teille rakkaaksi ja saattanut sen sydämissänne ihanaksi. Hän on myös tehnyt teille epäuskon, rikkomuksen ja tottelemattomuuden inhottavaksi, nämä ovat oikean tien seuraajia');
INSERT INTO "finnish" VALUES(49,8,'49:8','Jumalan armosta ja suosiosta; Jumalan on tieto, viisaus.');
INSERT INTO "finnish" VALUES(49,9,'49:9','Ja jos kaksi uskovaisten ryhmää joutuu riitaan, tehkää sovinto heidän välilleen. Mutta jos toinen niistä menettelee väärin toista kohtaan, taistelkaa väärintekijää vastaan siksi, kunnes hän jälleen mukautuu Jumalan käskyyn; jos hän taipuu tähän, tehkää heidän välillään oikeudenmukainen sovinto ja toimikaa tasapuolisesti. Totisesti, Jumala rakastaa niitä, jotka noudattavat oikeutta.');
INSERT INTO "finnish" VALUES(49,10,'49:10','Uskovaiset ovat veljiä; tehkää sentähden sovinto veljienne kanssa ja huolehtikaa velvollisuudestanne Jumalaa kohtaan, jotta armo tulisi osaksenne.');
INSERT INTO "finnish" VALUES(49,11,'49:11','Te, jotka uskotte, älkää antako ihmisten nauraa toisilleen, olkoonpa, että he ovat näitä parempia; älkööt naiset naurako toisille naisille, vaikka olisivatkin parempia kuin nämä. Älkää etsikö vikoja toisistanne älkääkä kutsuko toisianne pilkkanimillä. Kurjaa on joutua huonoon huutoon, uskon omaksumisen jälkeen, ja ne, jotka eivät käänny, ovat jumalattomia.');
INSERT INTO "finnish" VALUES(49,12,'49:12','Te, jotka uskotte, karttakaa pahoja luuloja, sillä epäileminen on totisesti joissakin tapauksissa synti. Älkää vakoilko älkääkä panetelko toisianne! Haluaako joku teistä syödä kuolleen veljensä lihaa? Sitä kyllä inhoatte. Huolehtikaa velvollisuuksistanne Jumalaa kohtaan! Jumala on armahtavainen ja laupias.');
INSERT INTO "finnish" VALUES(49,13,'49:13','Ihmiset, Me olemme totisesti luonut teidät miehestä ja naisesta ja jakanut teidät heimoihin ja sukuihin, jotta tuntisitte toinen toisenne. Arvokkain teistä Jumalan tykönä on se, joka parhaiten pitää huolen velvollisuudestaan. Jumala on totisesti tietävä, tajuava.');
INSERT INTO "finnish" VALUES(49,14,'49:14','Erämaan asukkaat sanovat: »Me uskomme»; sano: »Te ette usko, vaan sanotte: ''Alistumme'', mutta usko ei ole vielä voittanut sydämiänne. Kuitenkin, jos te tottelette Jumalaa ja Hänen sananjulistajaansa, Hän ei ota teoistanne mitään pois. Totisesti on Jumala anteeksiantava ja laupias.»');
INSERT INTO "finnish" VALUES(49,15,'49:15','Uskovaisia ovat vain ne, jotka uskovat Jumalaan ja Hänen sananjulistajaansa eivätkä epäile, vaan panevat alttiiksi omaisuutensa ja henkensä ponnistellessaan Jumalan tiellä; nämä ovat vanhurskaita.');
INSERT INTO "finnish" VALUES(49,16,'49:16','Sano: »Tekö saatatte Jumalan tietoon uskontonne, vaikka Jumala tietää kaiken, mitä on taivaassa ja maan päällä? Jumala on kaikkitietävä.»');
INSERT INTO "finnish" VALUES(49,17,'49:17','He luulevat saattavansa sinut kiitollisuuden velkaan kääntymällä muslimeiksi. Sano: »Älkää sitoko minua velvoituksiin islaamillanne. Ei, pikemmin olette te velkaa Jumalalle siitä, että Hän ohjaa teidät uskoon, jos rakastatte totuutta.»');
INSERT INTO "finnish" VALUES(49,18,'49:18','Jumala tuntee totisesti näkymättömät asiat taivaassa ja maan päällä; Jumala näkee, mitä te teette.');
INSERT INTO "finnish" VALUES(50,1,'50:1','Kaikkivoipa Jumala ja ylevä Koraani.');
INSERT INTO "finnish" VALUES(50,2,'50:2','He ihmettelevät, että heille on tullut varoittaja heidän omasta keskuudestaan, ja epäuskoiset sanovat: »Olisi ihmeellistä,');
INSERT INTO "finnish" VALUES(50,3,'50:3','että kuoltuamme ja muututtuamme tomuksi jälleen ylösnousisimme. Se olisi ihmeellinen paluu.»');
INSERT INTO "finnish" VALUES(50,4,'50:4','Mehän tiedämme, minkä verran heistä jää maan omaksi, sillä meillä on kirjoitus, joka sisältää kaiken.');
INSERT INTO "finnish" VALUES(50,5,'50:5','Ei, he hylkäsivät totuuden, kun se tuli heille, niin että he nyt ovat sekasorron tilassa.');
INSERT INTO "finnish" VALUES(50,6,'50:6','Eivätkö he siis katso ylös taivaaseen nähdäkseen, miten Me olemme sen rakentanut ja koristanut, niin että siinä ei ole mitään vajaata?');
INSERT INTO "finnish" VALUES(50,7,'50:7','Ja maan Me olemme avartanut sekä sijoittanut siihen vuoria ja kasvattanut siinä kaikenlaisia kauniita kasveja');
INSERT INTO "finnish" VALUES(50,8,'50:8','antaaksemme ymmärryksen ja kehoituksen jokaiselle Jumalan puoleen kääntyvälle palvelijalle.');
INSERT INTO "finnish" VALUES(50,9,'50:9','Me lähetämme myös pilvistä siunatun sateen ja sillä kasvatamme puutarhat ja viljan, joka korjataan,');
INSERT INTO "finnish" VALUES(50,10,'50:10','kookkaat palmut, hedelmäkimput oksissa toinen toisensa yläpuolella');
INSERT INTO "finnish" VALUES(50,11,'50:11','ravinnoksi palvelijoilleni, ja siten annamme elämän kuivaneelle maalle. Sellainen on ylösnousemus.');
INSERT INTO "finnish" VALUES(50,12,'50:12','Ennen heitä Nooan kansa, Ar-Rassin asukkaat, Tamuud,');
INSERT INTO "finnish" VALUES(50,13,'50:13','Aad, Farao ja Lootin veljet hylkäsivät profeetat;');
INSERT INTO "finnish" VALUES(50,14,'50:14','ja viidakon asukkaat sekä Tuuban kansa; kaikki he hylkäsivät sananjulistajani, mutta silloin uhkaukseni kävi toteen.');
INSERT INTO "finnish" VALUES(50,15,'50:15','Väsyimmekö ensimmäisessä luomisessa? Kuitenkin he epäilevät uutta luomista.');
INSERT INTO "finnish" VALUES(50,16,'50:16','Totisesti Me olemme luonut ihmisen ja tiedämme, mitä hänen sielunsa hänelle kuiskaa, ja olemme lähempänä häntä kuin hänen oma elinsuonensa.');
INSERT INTO "finnish" VALUES(50,17,'50:17','On kaksi kuuntelijaa, jotka istuvat oikealla ja vasemmalla puolella kuunnellen.');
INSERT INTO "finnish" VALUES(50,18,'50:18','Eikä ihminen lausu sanaakaan hänen lähettyvillään olevan tarkkaajan kuulematta.');
INSERT INTO "finnish" VALUES(50,19,'50:19','Ja kuoleman hourailu on tuleva ilmaisten totuuden: »Tämä oli se päivä, jota kavahditte.»');
INSERT INTO "finnish" VALUES(50,20,'50:20','Ja pasuunaan puhalletaan; se on uhkausten ja lupauksen päivä.');
INSERT INTO "finnish" VALUES(50,21,'50:21','Ja joka sielu on tuleva seurassaan kiihoittaja ja todistaja.');
INSERT INTO "finnish" VALUES(50,22,'50:22','»Totisesti et ole tästä päivästä välittänyt, mutta nyt olemme poistanut verhon silmiltäsi, niin että näkösi tänään on tarkka.»');
INSERT INTO "finnish" VALUES(50,23,'50:23','Ja hänen seuralaisensa sanoo: »Tässä on se, mitä minulla on valmiina.»');
INSERT INTO "finnish" VALUES(50,24,'50:24','»Heittäkää alas helvettiin jokainen kiittämätön ja kapinallinen,');
INSERT INTO "finnish" VALUES(50,25,'50:25','hyvän kieltäjä, rajain ylittäjä, epäilijä,');
INSERT INTO "finnish" VALUES(50,26,'50:26','joka asettaa muita Jumalan rinnalle! Heittäkää hänet kauheaan kidutukseen!»');
INSERT INTO "finnish" VALUES(50,27,'50:27','Hänen seuralaisensa sanoo: »Herra, en ole vietellyt häntä, vaan hän itse oli suuresti eksynyt.»');
INSERT INTO "finnish" VALUES(50,28,'50:28','Hän vastaa: »Älkää kiistelkö Minun luonani! Olen totisesti varoittanut teitä etukäteen.»');
INSERT INTO "finnish" VALUES(50,29,'50:29','Minun sanani ei muutu, enkä Minä tee vääryyttä palvelijoilleni.»');
INSERT INTO "finnish" VALUES(50,30,'50:30','Sinä päivänä Me sanomme helvetille: »Oletko täynnä?» ja se vastaa: »Tuleeko vielä lisää?»');
INSERT INTO "finnish" VALUES(50,31,'50:31','Ja paratiisi lähestyy niitä, jotka pahaa karttavat.');
INSERT INTO "finnish" VALUES(50,32,'50:32','Tämä on se, mitä teille luvattiin, jokaiselle, joka usein kääntyy Jumalan puoleen ja pitää Hänen käskynsä,');
INSERT INTO "finnish" VALUES(50,33,'50:33','joka pelkää laupiasta Jumalaa salaisestikin ja lähestyy Häntä katuvaisin sydämin.');
INSERT INTO "finnish" VALUES(50,34,'50:34','Astukaa sisään rauhassa! Tämä on ikuisuuden päivä.»');
INSERT INTO "finnish" VALUES(50,35,'50:35','He saavat siellä mitä toivovat ja Meidän luonamme vielä enemmän.');
INSERT INTO "finnish" VALUES(50,36,'50:36','Kuinka monet sukupolvet ennen heitä tuhosimmekaan, sukupolvet, jotka olivat mahtavampia kuin he. Kiertäkää maita, niin näette, onko mitään turvapaikkaa minun kostoltani.');
INSERT INTO "finnish" VALUES(50,37,'50:37','Totisesti tämä on riittävä muistutus sille, jolla on ymmärtäväinen sydän ja joka käyttää kuuloaan ja näkee omilla silmillään.');
INSERT INTO "finnish" VALUES(50,38,'50:38','Totisesti Me olemme kuudessa päivässä luonut taivaat ja maan ja mitä on niiden välillä, eikä väsymys vähääkään vaivannut meitä.');
INSERT INTO "finnish" VALUES(50,39,'50:39','Kestä sentähden kärsivällisesti, mitä he sanovat, ja ylistä ja kiitä Herraa ennen sekä auringon nousua että sen laskua.');
INSERT INTO "finnish" VALUES(50,40,'50:40','Ylistä Häntä yöllä ja rukouksen jälkeen,');
INSERT INTO "finnish" VALUES(50,41,'50:41','ja kuunnelkaa sinä päivänä, jolloin kutsuja kutsuu lähellänne,');
INSERT INTO "finnish" VALUES(50,42,'50:42','päivänä, jolloin he kuulevat totuuden huudon. Se on ylösnousemuksen päivä.');
INSERT INTO "finnish" VALUES(50,43,'50:43','Totisesti, Meistä on elämä ja kuolema lähtöisin ja Meidän luoksemme kaikki palaa.');
INSERT INTO "finnish" VALUES(50,44,'50:44','Päivänä, jolloin maa repeää heidän allaan, he kiirehtivät; silloin tapahtuu kokoaminen, mikä on Meille helppoa.');
INSERT INTO "finnish" VALUES(50,45,'50:45','Me tiedämme parhaiten, mitä he sanovat, ja sinä et ole heidän hallitsijansa. Muistuta siis Koraanin avulla sitä, joka pelkää uhkaustani.');
INSERT INTO "finnish" VALUES(51,1,'51:1','Katso niitä, jotka tuhlaavat hajalle viskoen,');
INSERT INTO "finnish" VALUES(51,2,'51:2','niitä, jotka taakkaa kantavat,');
INSERT INTO "finnish" VALUES(51,3,'51:3','niitä, joiden kulku on kevyttä,');
INSERT INTO "finnish" VALUES(51,4,'51:4','niitä, jotka jakelevat määräyksiä.');
INSERT INTO "finnish" VALUES(51,5,'51:5','Se, millä teitä uhataan, on totista totta,');
INSERT INTO "finnish" VALUES(51,6,'51:6','ja tuomion täytyy totisesti tulla.');
INSERT INTO "finnish" VALUES(51,7,'51:7','Katso taivasta, tähtiratainsa täyttämää.');
INSERT INTO "finnish" VALUES(51,8,'51:8','Totisesti te riitelette puhuessanne toistenne kanssa.');
INSERT INTO "finnish" VALUES(51,9,'51:9','Mutta vain se kääntyy uskosta pois, joka tahtoo kääntyä.');
INSERT INTO "finnish" VALUES(51,10,'51:10','Kirotut olkoot valehtelijat,');
INSERT INTO "finnish" VALUES(51,11,'51:11','jotka tietämättömyytensä kuilussa eivät mistään välitä.');
INSERT INTO "finnish" VALUES(51,12,'51:12','He kysyvät: »Milloin tulee tuomion päivä?»');
INSERT INTO "finnish" VALUES(51,13,'51:13','Se on päivä, jolloin heidät tulessa koetellaan.');
INSERT INTO "finnish" VALUES(51,14,'51:14','Maistakaa nyt rangaistustanne. Tätä halusitte jouduttaa.');
INSERT INTO "finnish" VALUES(51,15,'51:15','Totisesti ovat ne, jotka pahaa karttavat, kerran puutarhoissa ja lähteitten partaalla');
INSERT INTO "finnish" VALUES(51,16,'51:16','ottaen vastaan, mitä heidän Herransa heille antaa. Totisesti tekivät he sitä ennen hyviä tekoja.');
INSERT INTO "finnish" VALUES(51,17,'51:17','Vähän nukkuivat he yleensä yöllä,');
INSERT INTO "finnish" VALUES(51,18,'51:18','ja aamulla he anoivat anteeksiantoa,');
INSERT INTO "finnish" VALUES(51,19,'51:19','heidän omaisuudestaan riitti sopiva osa anojalle ja avuttomalle.');
INSERT INTO "finnish" VALUES(51,20,'51:20','Maan päällä on tunnusmerkkejä niille, joilla on varmuus;');
INSERT INTO "finnish" VALUES(51,21,'51:21','niin myöskin omissa sieluissanne. Ettekö siis näe niitä?');
INSERT INTO "finnish" VALUES(51,22,'51:22','Ja taivaassa on ravintonne sekä se, mikä teille on luvattu.');
INSERT INTO "finnish" VALUES(51,23,'51:23','Taivaitten ja maan Herran nimessä. Tämä on totisesti totuus, yhtä varma kuin teidän vakuutuksenne.');
INSERT INTO "finnish" VALUES(51,24,'51:24','Onko sinulle tullut sanoma Aabrahamin kunnioitetuista vieraista?');
INSERT INTO "finnish" VALUES(51,25,'51:25','He astuivat sisään hänen luokseen ja sanoivat: »Rauha!» Ja hän vastasi: »Rauha, olette vieraita ihmisiä.»');
INSERT INTO "finnish" VALUES(51,26,'51:26','Ja hän hiipi salaa perheensä luokse ja palasi tuoden mukanaan juottovasikan,');
INSERT INTO "finnish" VALUES(51,27,'51:27','jonka hän asetti heidän eteensä sanoen: »Ettekö tahdo syödä?»');
INSERT INTO "finnish" VALUES(51,28,'51:28','Ja hän alkoi pelätä heitä, mutta he sanoivat: »Älä pelkää», ja he ilmoittivat hänelle ilosanoman viisaan pojan syntymästä.');
INSERT INTO "finnish" VALUES(51,29,'51:29','Silloin tuli hänen vaimonsa paikalle suuren murheen vallassa, löi kasvoihinsa ja sanoi: »Vanha hedelmätön nainen.»');
INSERT INTO "finnish" VALUES(51,30,'51:30','He sanoivat: »Sanomamme on Herraltasi, Hänen on totisesti viisaus ja tieto».');
INSERT INTO "finnish" VALUES(51,31,'51:31','Ja Aabraham sanoi: »Mikä on asianne, viestintuojat?»');
INSERT INTO "finnish" VALUES(51,32,'51:32','He vastasivat: »Meidät on totisesti lähetetty syntisten ihmisten luo,');
INSERT INTO "finnish" VALUES(51,33,'51:33','heittämään heidän päälleen tiilikiviä,');
INSERT INTO "finnish" VALUES(51,34,'51:34','jotka Herrasi on varannut jumalattomille,');
INSERT INTO "finnish" VALUES(51,35,'51:35','ja Me olemme erottanut heistä pois ne, jotka uskoivat,');
INSERT INTO "finnish" VALUES(51,36,'51:36','Mutta Me emme löytänyt enempää kuin yhden Jumalalle alamaisen suvun.»');
INSERT INTO "finnish" VALUES(51,37,'51:37','Me jätimme merkin niitä varten, jotka pelkäävät tuskallista rangaistusta.');
INSERT INTO "finnish" VALUES(51,38,'51:38','Kun lähetimme Mooseksen Faraon luo selvin valtuuksin,');
INSERT INTO "finnish" VALUES(51,39,'51:39','niin tämä mahtavuudessaan käänsi hänelle selkänsä ja sanoi: »Noita tai hullu.»');
INSERT INTO "finnish" VALUES(51,40,'51:40','Me tartuimme häneen ja hänen sotajoukkoihinsa ja paiskasimme heidät mereen, koska hänen tekonsa oli rangaistava.');
INSERT INTO "finnish" VALUES(51,41,'51:41','Ja Aadia vastaan Me päästimme tuhoavan tuulen,');
INSERT INTO "finnish" VALUES(51,42,'51:42','joka ei sinne, missä puhalsi, jättänyt mitään jälkeensä, vaan muutti kaiken tuhaksi.');
INSERT INTO "finnish" VALUES(51,43,'51:43','Ja Tamuudille sanottiin: »Nauttikaa hetkinen!»');
INSERT INTO "finnish" VALUES(51,44,'51:44','Mutta he kapinoivat Herransa käskyä vastaan, ja niin iski heihin heidän nähtensä ukkonen.');
INSERT INTO "finnish" VALUES(51,45,'51:45','Eivätkä he kyenneet nousemaan ylös eivätkä puolustautumaan.');
INSERT INTO "finnish" VALUES(51,46,'51:46','Ja Nooan kansan tuhosimme jo aikaisemmin. Totisesti, he olivat syntisiä ihmisiä.');
INSERT INTO "finnish" VALUES(51,47,'51:47','Ja taivaan olemme Me voimallamme kohottanut korkealle ja totisesti tehnyt sen laajaksi.');
INSERT INTO "finnish" VALUES(51,48,'51:48','Maan olemme tehnyt suureksi ja avaraksi. Ja kuinka hyvin olemmekaan sen avartanut!');
INSERT INTO "finnish" VALUES(51,49,'51:49','Kaikkea olemme luonut kaksittain, jotta ottaisitte vaarin.');
INSERT INTO "finnish" VALUES(51,50,'51:50','Paetkaa Jumalan luo! Minä olen totisesti Jumalan lähettämä julkinen varoittajanne.');
INSERT INTO "finnish" VALUES(51,51,'51:51','Älkää asettako Jumalan rinnalle muuta jumalaa. Minä olen totisesti Hänen teitä varten lähettämänsä julkinen varoittaja.');
INSERT INTO "finnish" VALUES(51,52,'51:52','Ei yksikään sananjulistaja ole tullut niiden luokse, jotka ennen heitä elivät, heidän sanomattansa: »Noita tai hullu.»');
INSERT INTO "finnish" VALUES(51,53,'51:53','Ovatko he antaneet tämän toisilleen perinnöksi? Totisesti, he ovat väärämielisiä ihmisiä.');
INSERT INTO "finnish" VALUES(51,54,'51:54','Käänny siis pois heistä, niin ei tätä lueta sinulle viaksi.');
INSERT INTO "finnish" VALUES(51,55,'51:55','Ja jatka kehoituksiasi, sillä totisesti muistutus koituu uskovien hyödyksi.');
INSERT INTO "finnish" VALUES(51,56,'51:56','Olen luonut henkiolennot ja ihmiset vain palvelemaan Itseäni.');
INSERT INTO "finnish" VALUES(51,57,'51:57','En halua heiltä mitään ravintoa enkä vaadi, että he ruokkisivat Minua.');
INSERT INTO "finnish" VALUES(51,58,'51:58','Jumala on totisesti ravinnon jakaja, Herra, jonka on valta ja voima.');
INSERT INTO "finnish" VALUES(51,59,'51:59','Niin on totisesti jumalattomilla oleva sama osa kuin heidän kaltaisillaan; heidän ei sentähden tarvitse pyytää Minua jouduttamaan asiaa.');
INSERT INTO "finnish" VALUES(51,60,'51:60','Voi siis niitä, jotka eivät usko, sen päivän takia, joka heitä uhkaa.');
INSERT INTO "finnish" VALUES(52,1,'52:1','Nimeen Vuoren');
INSERT INTO "finnish" VALUES(52,2,'52:2','ja Pyhän Kirjan,');
INSERT INTO "finnish" VALUES(52,3,'52:3','joka on kirjoitettu levitetylle pergamentille,');
INSERT INTO "finnish" VALUES(52,4,'52:4','ja temppelin, johon pyhiinvaelletaan,');
INSERT INTO "finnish" VALUES(52,5,'52:5','nimeen korkean taivaan laen');
INSERT INTO "finnish" VALUES(52,6,'52:6','ja aavan meren,');
INSERT INTO "finnish" VALUES(52,7,'52:7','totisesti, Herrasi rangaistus on tuleva;');
INSERT INTO "finnish" VALUES(52,8,'52:8','kukaan ei voi sitä välttää.');
INSERT INTO "finnish" VALUES(52,9,'52:9','Päivänä, jolloin taivas vavahtaa paikaltaan');
INSERT INTO "finnish" VALUES(52,10,'52:10','ja vuoret siirtyvät,');
INSERT INTO "finnish" VALUES(52,11,'52:11','voi tuona päivänä niitä, jotka hylkäävät totuuden,');
INSERT INTO "finnish" VALUES(52,12,'52:12','niitä, jotka ilkamoivat pitäen turhia puheita,');
INSERT INTO "finnish" VALUES(52,13,'52:13','päivänä, jona heidät väkisin ajetaan helvetin tuleen!');
INSERT INTO "finnish" VALUES(52,14,'52:14','»Tämä on se tuli, jota te piditte keksintönä.');
INSERT INTO "finnish" VALUES(52,15,'52:15','Onko se noituutta vai ettekö näe?');
INSERT INTO "finnish" VALUES(52,16,'52:16','Käykää sinne ja kestäkää se tai olkaa kestämättä, se on teille yhdentekevää. Te saatte palkan vain siitä, mitä olette tehneet.»');
INSERT INTO "finnish" VALUES(52,17,'52:17','Totisesti ovat ne, jotka karttavat pahaa, puutarhoissa ja autuudessa');
INSERT INTO "finnish" VALUES(52,18,'52:18','iloiten Herransa antimista ja siitä, että heidän Herransa pelasti heidät palavan tulen kidutuksesta.');
INSERT INTO "finnish" VALUES(52,19,'52:19','Syökää ja juokaa iloiten palkaksi siitä, mitä teitte.');
INSERT INTO "finnish" VALUES(52,20,'52:20','He saavat levätä riveihin asetetuilla istuimilla, ja Minä yhdistän heidät puhtaisiin, tummasilmäisiin neitoihin,');
INSERT INTO "finnish" VALUES(52,21,'52:21','ja ne, jotka uskovat ja joiden jälkeläiset seuraavat heitä uskossa, Me yhdistämme jälkeläisiiinsä, ja Me emme vähimmässäkään halvenna heidän töitään. Jokainen ihminen on vastuussa siitä, mitä hän on tehnyt.');
INSERT INTO "finnish" VALUES(52,22,'52:22','Ja Me varustamme heidät yltäkylläisesti hedelmillä ja lihalla heidän toivomuksiensa mukaan.');
INSERT INTO "finnish" VALUES(52,23,'52:23','He ojentavat siellä toisilleen pikarin ilman turhaa puhetta ja syntiä,');
INSERT INTO "finnish" VALUES(52,24,'52:24','ja heidän ympärillään on nuorukaisia, kauniita kuin helmet kuoressaan.');
INSERT INTO "finnish" VALUES(52,25,'52:25','ja muutamat heistä tekevät toisilleen kysymyksiä,');
INSERT INTO "finnish" VALUES(52,26,'52:26','ja he sanovat: »Totisesti pelkäsimme ennen perheittemme vuoksi,');
INSERT INTO "finnish" VALUES(52,27,'52:27','mutta Jumala on ollut meille armollinen, ja Hän on pelastanut meidät polttavan tuulen kurituksesta.»');
INSERT INTO "finnish" VALUES(52,28,'52:28','Totisesti olemme vain Häntä kutsuneet avuksi; Hän on totisesti lempeä, armollinen.');
INSERT INTO "finnish" VALUES(52,29,'52:29','Jatka sentähden kehoituksiasi, sillä Herrasi armosta sinä et ole noita etkä hullu.');
INSERT INTO "finnish" VALUES(52,30,'52:30','Vai sanovatko he: »Runoniekka! Me odotamme mitä aika mukanansa tuo.»');
INSERT INTO "finnish" VALUES(52,31,'52:31','Sano: »Odottakaa, sillä totisesti kuulun minäkin niihin, jotka odottavat.»');
INSERT INTO "finnish" VALUES(52,32,'52:32','Käskeekö heidän järkensä heitä menettelemään näin? Vai ovatko he väärämielisiä ihmisiä?');
INSERT INTO "finnish" VALUES(52,33,'52:33','Vai sanovatko he: »Hän on itse sepittänyt sen?» Niin, he eivät usko.');
INSERT INTO "finnish" VALUES(52,34,'52:34','Antakoot he sitten itse samanlaisen ilmoituksen, jos rakastavat totuutta.');
INSERT INTO "finnish" VALUES(52,35,'52:35','Onko heidät luotu tyhjästä vai ovatko he luojia?');
INSERT INTO "finnish" VALUES(52,36,'52:36','Tai ovatko he luoneet taivaat ja maan? Ei, heillä ei ole varmuutta.');
INSERT INTO "finnish" VALUES(52,37,'52:37','Tai onko heillä hallussaan Herransa aarteet, tai onko heille annettu ehdoton valta?');
INSERT INTO "finnish" VALUES(52,38,'52:38','Tai onko heillä keinot, joilla voivat kuunnella? Tuokoot sitten kuuntelijat selvän todistuksen.');
INSERT INTO "finnish" VALUES(52,39,'52:39','Tai onko Hänellä tyttäriä ja teillä poikia?');
INSERT INTO "finnish" VALUES(52,40,'52:40','Tai pyydätkö sinä heiltä palkkaa, niin että he kantavat velkojen taakkaa.');
INSERT INTO "finnish" VALUES(52,41,'52:41','Tai hallitsevatko he näkymätöntä, niin että he voivat kirjoittaa sen muistiin?');
INSERT INTO "finnish" VALUES(52,42,'52:42','Tai tahtovatko he sotaa? Mutta juuri ne, jotka eivät usko. kärsivät sodassa tappion.');
INSERT INTO "finnish" VALUES(52,43,'52:43','Tai onko heillä joku muu kuin Jumala? Kunnia olkoon Jumalalle, joka on kaukana siitä, mitä he asettavat Hänen rinnalleen.');
INSERT INTO "finnish" VALUES(52,44,'52:44','Ja jos he näkisivät osan taivaasta suistuvan alas, he sanoisivat: »Pilviä päällekkäin!»');
INSERT INTO "finnish" VALUES(52,45,'52:45','Jätä heidät siis oman onnensa nojaan, kunnes heidät kohtaa päivä, jolloin salama iskee heihin.');
INSERT INTO "finnish" VALUES(52,46,'52:46','Päivä, jolloin heidän ponnistelunsa eivät heitä hyödytä eikä heillä ole mitään apua.');
INSERT INTO "finnish" VALUES(52,47,'52:47','Totisesti kuritetaan jumalattomia muullakin tavalla, mutta useimmat heistä ovat tietoa vailla.');
INSERT INTO "finnish" VALUES(52,48,'52:48','Odota kärsivällisesti Herrasi tuomiota, sillä totisesti valvovat silmämme sinua, ja kiitä ja ylistä Herraa aamulla noustessasi.');
INSERT INTO "finnish" VALUES(52,49,'52:49','Myös yöllä anna Hänelle kunnia ja samoin tähtien taivaalta hälvetessä.');
INSERT INTO "finnish" VALUES(53,1,'53:1','Kautta tähden, joka kalpenee taivaalla,');
INSERT INTO "finnish" VALUES(53,2,'53:2','suojelijanne ei erehdy eikä joudu harhaan;');
INSERT INTO "finnish" VALUES(53,3,'53:3','ei hän myöskään puhu omasta tahdostaan.');
INSERT INTO "finnish" VALUES(53,4,'53:4','Tämä on totisesti Jumalan ilmoitus, joka on hänelle ilmaistu.');
INSERT INTO "finnish" VALUES(53,5,'53:5','Herra, jolla on valtava voima, on opettanut häntä.');
INSERT INTO "finnish" VALUES(53,6,'53:6','Hänen asemansa säilyi horjumattomana.');
INSERT INTO "finnish" VALUES(53,7,'53:7','Hän (Muhammed) oli taivaan korkeuksissa,');
INSERT INTO "finnish" VALUES(53,8,'53:8','ja sitten hän lähestyi Herraa ja painui alas,');
INSERT INTO "finnish" VALUES(53,9,'53:9','niin että hän oli kahden jousenkantaman etäisyydellä tai vielä lähempänäkin');
INSERT INTO "finnish" VALUES(53,10,'53:10','ja ilmaisi palvelijalleen sen, mitä ilmaisi.');
INSERT INTO "finnish" VALUES(53,11,'53:11','Ei hänen sydämensä keksinyt sitä, mitä hän näki.');
INSERT INTO "finnish" VALUES(53,12,'53:12','Miksi siis haluatte kiistellä hänen kanssaan siitä, mitä hän on nähnyt?');
INSERT INTO "finnish" VALUES(53,13,'53:13','Totisesti hän on nähnyt Hänet toisessakin ilmestyksessä,');
INSERT INTO "finnish" VALUES(53,14,'53:14','lähellä etäisintä lootuspuuta,');
INSERT INTO "finnish" VALUES(53,15,'53:15','jonka vieressä on ikiasumusten puutarha,');
INSERT INTO "finnish" VALUES(53,16,'53:16','kun lootuspuun verhosi se, mikä kykenee verhoamaan;');
INSERT INTO "finnish" VALUES(53,17,'53:17','katse ei harhaillut eikä pettänyt;');
INSERT INTO "finnish" VALUES(53,18,'53:18','totisesti hän näki suurimman Herransa tunnusmerkeistä.');
INSERT INTO "finnish" VALUES(53,19,'53:19','Oletteko sitten ajatelleet al-Latia ja al~Uzzaa');
INSERT INTO "finnish" VALUES(53,20,'53:20','ja Manatia, kolmatta ja viimeistä?');
INSERT INTO "finnish" VALUES(53,21,'53:21','Kuuluvatko pojat teille ja Hänelle tyttäret?');
INSERT INTO "finnish" VALUES(53,22,'53:22','Mikä nurja jako!');
INSERT INTO "finnish" VALUES(53,23,'53:23','Nämä eivät ole muuta kuin nimiä, joita te olette antaneet heille, te ja esi~isänne, eikä Jumala ole lähettänyt heille mitään valtuutta. He noudattavat vain arveluja ja viettiensä oikkuja, vaikka Jumala totisesti on lähettänyt heille johdatuksen.');
INSERT INTO "finnish" VALUES(53,24,'53:24','Vai tuleeko ihmisen saada kaikki, mitä hän haluaa?');
INSERT INTO "finnish" VALUES(53,25,'53:25','Jumalan on kuitenkin kaikki mennyt ja tuleva.');
INSERT INTO "finnish" VALUES(53,26,'53:26','Kuinka monta sanansaattajahenkeä (enkeliä) onkaan taivaissa, joiden apu ei hyödytä ketään, ellei Jumala anna siihen lupaa sille, joka Häntä miellyttää ja johon Hän on tyytyväinen.');
INSERT INTO "finnish" VALUES(53,27,'53:27','Totisesti vain ne, jotka eivät usko tulevaan elämään, kutsuvat näitä enkeleitä naisten nimillä.');
INSERT INTO "finnish" VALUES(53,28,'53:28','Mutta heillä ei ole siitä mitään tietoa; he seuraavat vain arveluja, mutta arvelu ei totisesti kestä totuuteen verraten.');
INSERT INTO "finnish" VALUES(53,29,'53:29','Käänny siis pois siitä, joka kääntää selkänsä Meidän kehoituksellemme eikä tavoittele muuta kuin tämän maailman elämää!');
INSERT INTO "finnish" VALUES(53,30,'53:30','Tähän kaikki heidän tietonsa tähtää. Totisesti Herrasi tietää parhaiten, kuka on eksynyt Hänen polultaan, ja Hän tietää parhaiten, kuka seuraa ohjausta.');
INSERT INTO "finnish" VALUES(53,31,'53:31','Jumalan on kaikki, mitä on taivaassa ja maan päällä, jotta Hän saisi tuomita pahantekijät heidän tekojensa mukaan ja palkita hyvyydellä niitä, jotka tekevät hyvää.');
INSERT INTO "finnish" VALUES(53,32,'53:32','Niille, jotka karttavat suuria syntejä ja rikkomuksia, joskaan eivät pienempiä hairauksia, niille on totisesti Herrasi anteeksianto avara. Hän tiesi parhaanne, kun Hän loi teidät maan tomusta ja kun lepäsitte äitinne kohdussa. Älkää siis julistako omaa puhtauttanne. Hän tuntee parhaiten sen, joka pelkää Jumalaa.');
INSERT INTO "finnish" VALUES(53,33,'53:33','Oletko ajatellut ihmistä, joka kääntyy pois,');
INSERT INTO "finnish" VALUES(53,34,'53:34','antaa vähän ja on kovasydäminen?');
INSERT INTO "finnish" VALUES(53,35,'53:35','Onko hänellä tietoa salatusta, niin että hän voi nähdä?');
INSERT INTO "finnish" VALUES(53,36,'53:36','Tai eikö hänelle ole ilmoitettu, mitä on Mooseksen kirjoissa,');
INSERT INTO "finnish" VALUES(53,37,'53:37','ja kuinka Aabraham täytti käskyt?');
INSERT INTO "finnish" VALUES(53,38,'53:38','Tiedä, ettei kukaan kuormaa kantava kanna toisen taakkaa');
INSERT INTO "finnish" VALUES(53,39,'53:39','ja että ihmisen osalle ei tule muuta kuin se, mitä hän on tavoitellut,');
INSERT INTO "finnish" VALUES(53,40,'53:40','että hänen pyrkimyksensä saatetaan päivänvaloon,');
INSERT INTO "finnish" VALUES(53,41,'53:41','että hänet sitten palkitaan täysin mitoin,');
INSERT INTO "finnish" VALUES(53,42,'53:42','että Herrassasi, on kaiken loppu,');
INSERT INTO "finnish" VALUES(53,43,'53:43','että Hän antaa ihmiselle hymyn ja itkun,');
INSERT INTO "finnish" VALUES(53,44,'53:44','että Hän antaa elämän ja kuoleman,');
INSERT INTO "finnish" VALUES(53,45,'53:45','että Hän on luonut molemmat sukupuolet - miehen ja naisen -');
INSERT INTO "finnish" VALUES(53,46,'53:46','istutetusta siemenestä,');
INSERT INTO "finnish" VALUES(53,47,'53:47','että Hänen huostassaan on tuleva kehitys,');
INSERT INTO "finnish" VALUES(53,48,'53:48','että Hän antaa rikkauden ja omaisuuden,');
INSERT INTO "finnish" VALUES(53,49,'53:49','että Hän on Siriuksen Herra,');
INSERT INTO "finnish" VALUES(53,50,'53:50','että Hän tuhosi muinaisen Aadin');
INSERT INTO "finnish" VALUES(53,51,'53:51','ja Tamuudin, jota Hän ei armahtanut');
INSERT INTO "finnish" VALUES(53,52,'53:52','ja Nooan kansan sitä ennen. Totisesti he olivat mitä jumalattomimpia ja syntisimpiä ihmisiä.');
INSERT INTO "finnish" VALUES(53,53,'53:53','Hän hajoitti kukistetut kaupungit');
INSERT INTO "finnish" VALUES(53,54,'53:54','ja peitti ne sillä, mikä peittää voi.');
INSERT INTO "finnish" VALUES(53,55,'53:55','Mitä Herrasi armolahjoja tahdot siis epäillä?');
INSERT INTO "finnish" VALUES(53,56,'53:56','Tämä (Muhammed) on entisten varoittajain kaltainen varoittaja.');
INSERT INTO "finnish" VALUES(53,57,'53:57','Tuleva loppu lähenee;');
INSERT INTO "finnish" VALUES(53,58,'53:58','kukaan paitsi Jumala ei voi sitä siirtää.');
INSERT INTO "finnish" VALUES(53,59,'53:59','Ihmettelettekö sitten näitä sanomia,');
INSERT INTO "finnish" VALUES(53,60,'53:60','nauratteko sensijaan että itkisitte,');
INSERT INTO "finnish" VALUES(53,61,'53:61','houkkioita kun olette?');
INSERT INTO "finnish" VALUES(53,62,'53:62','Kumartukaa sentähden Jumalan edessä ja palvelkaa Häntä!');
INSERT INTO "finnish" VALUES(54,1,'54:1','Hetki lähestyy ja kuu jakaantuu.');
INSERT INTO "finnish" VALUES(54,2,'54:2','Mutta jos he näkevät tunnusmerkin, he kääntyvät syrjään ja sanovat: »Valtavaa lumousta!»');
INSERT INTO "finnish" VALUES(54,3,'54:3','ja pitävät sitä valheena ja noudattavat halujaan, mutta kaikella on määrätty aikansa,');
INSERT INTO "finnish" VALUES(54,4,'54:4','totisesti ovat he saaneet monta varoittavaa sanomaa,');
INSERT INTO "finnish" VALUES(54,5,'54:5','täydellisen viisauden sisältäviä varoituksia, mutta varoitukset eivät heitä hyödytä.');
INSERT INTO "finnish" VALUES(54,6,'54:6','Käänny siis pois heistä! Sinä päivänä, jolloin kutsuja kutsuu heidät vastenmieliseen tehtävään,');
INSERT INTO "finnish" VALUES(54,7,'54:7','he astuvat silmät maahan luotuina esiin haudoistaan kuin hajalle siroteltu heinäsirkkaparvi');
INSERT INTO "finnish" VALUES(54,8,'54:8','kiiruhtaen pelokkaina kohtaamaan kutsujaa. Epäuskoiset sanovat: »Tämä on kova päivä.»');
INSERT INTO "finnish" VALUES(54,9,'54:9','Nooan kansa ennen heitä kielsi hänet. Niin, he pitivät palvelijaamme valehtelijana ja sanoivat: »Hän on hullu», ja he puhuivat hänelle ylenkatseellisesti.');
INSERT INTO "finnish" VALUES(54,10,'54:10','Mutta hän huusi avuksi Herraansa sanoen: »Minä olen masentunut, auta Sinä minua!»');
INSERT INTO "finnish" VALUES(54,11,'54:11','Silloin avasimme taivaan portit veden syöstä alas,');
INSERT INTO "finnish" VALUES(54,12,'54:12','ja maasta pursui joukko lähteitä, niin että kaikki vedet yhtyivät, mikä oli ennakolta päätetty.');
INSERT INTO "finnish" VALUES(54,13,'54:13','Ja Me kannoimme häntä laudoista ja nauloista kyhätyllä laivalla,');
INSERT INTO "finnish" VALUES(54,14,'54:14','joka liikkui silmäimme edessä; se oli palkkio hänelle, jonka sanoma hylättiin.');
INSERT INTO "finnish" VALUES(54,15,'54:15','Totisesti Me jätimme sen merkiksi. Mutta missä on se, joka ottaa siitä vaarin?');
INSERT INTO "finnish" VALUES(54,16,'54:16','Millainen oli Minun rangaistukseni ja Minun varoitukseni?');
INSERT INTO "finnish" VALUES(54,17,'54:17','Me teimme totisesti Koraanin helposti muistettavaksi, mutta missä on se, joka haluaa noudattaa sitä?');
INSERT INTO "finnish" VALUES(54,18,'54:18','Aad julisti sanansaattajamme valehtelijaksi, ja millainen olikaan Minun rangaistukseni ja Minun varoitukseni!');
INSERT INTO "finnish" VALUES(54,19,'54:19','Totisesti Me lähetimme pauhaavan myrskyn heidän ylitseen katkerana onnettomuuden päivänä.');
INSERT INTO "finnish" VALUES(54,20,'54:20','Se tempasi mukaansa ihmiset kuin maasta juurineen revityt palmunrungot.');
INSERT INTO "finnish" VALUES(54,21,'54:21','Millainen olikaan Minun rangaistukseni ja Minun varoitukseni!');
INSERT INTO "finnish" VALUES(54,22,'54:22','Totisesti olemme tehnyt Koraanin helpoksi muistaa, mutta missä on se, joka haluaa totella sitä?');
INSERT INTO "finnish" VALUES(54,23,'54:23','Tamuud leimasi varoittajat valehtelijoiksi,');
INSERT INTO "finnish" VALUES(54,24,'54:24','sanoen: »Tuleeko meidän seurata sellaista, joka on vain meikäläinen? Totisesti olisimme silloin erhetyksen ja mielettömyyden vallassa.');
INSERT INTO "finnish" VALUES(54,25,'54:25','Onko kehoitus annettu tehtäväksi jollekulle meidän keskuudestamme? Ei, hän on suuri valehtelija, itsensä ylentäjä.»');
INSERT INTO "finnish" VALUES(54,26,'54:26','Huomenna he saavat tietää, kuka on suuri valehtelija ja itsensä ylistäjä.');
INSERT INTO "finnish" VALUES(54,27,'54:27','Totisesti Me aiomme lähettää naaraskameelin heille koetukseksi. Pidä siis silmällä heitä ja ole kärsivällinen!');
INSERT INTO "finnish" VALUES(54,28,'54:28','Ilmoita heille, että vesi on jaettu heidän keskensä, niin että jokainen on vuorollaan kameelia juottamassa!');
INSERT INTO "finnish" VALUES(54,29,'54:29','Mutta he kutsuivat toverinsa, ja tämä veti esiin miekkansa ja teki eläimestä ramman.');
INSERT INTO "finnish" VALUES(54,30,'54:30','Mutta minkälainen olikaan Minun lähettämäni kuritus ja varoitus!');
INSERT INTO "finnish" VALUES(54,31,'54:31','Totisesti lähetimme heille yhden ainoan jyrähdyksen, ja he olivat kuin vitsoja, joita aidanpunojat käyttävät.');
INSERT INTO "finnish" VALUES(54,32,'54:32','Totisesti olemme tehnyt tämän Koraanin helpoksi muistaa, mutta missä on se, joka tahtoo noudattaa sitä?');
INSERT INTO "finnish" VALUES(54,33,'54:33','Lootin kansa julisti varoittajat valehtelijoiksi.');
INSERT INTO "finnish" VALUES(54,34,'54:34','Totisesti Me lähetimme heille pyörremyrskyn; mutta Lootin perheen pelastimme aamun koittaessa;');
INSERT INTO "finnish" VALUES(54,35,'54:35','se oli Meidän osoittamamme armo. Näin palkitsemme sen, joka on kiitollinen.');
INSERT INTO "finnish" VALUES(54,36,'54:36','Totisesti hän oli varoittanut heitä Meidän väliintulostamme, mutta he epäilivät varoitusta.');
INSERT INTO "finnish" VALUES(54,37,'54:37','He aikoivat viedä pois hänen vieraansa, mutta Me sokaisimme heidän silmänsä: »Kokekaa siis lähettämäni kuritus ja varoitus!»');
INSERT INTO "finnish" VALUES(54,38,'54:38','Ja totisesti, rangaistus, mikä oli heille määrätty, tuli aamulla heidän osakseen.');
INSERT INTO "finnish" VALUES(54,39,'54:39','Tulkaa siis kokemaan lähettämäni rangaistus ja varoitus!');
INSERT INTO "finnish" VALUES(54,40,'54:40','Olemmehan tehnyt Koraanin helpoksi muistaa, mutta missä on se, joka haluaa noudattaa sitä?');
INSERT INTO "finnish" VALUES(54,41,'54:41','Toden totta tulivat varoittajat faraon kansan tykö.');
INSERT INTO "finnish" VALUES(54,42,'54:42','He pitivät kaikkia tunnusmerkkejämme valheena, ja siksi me tartuimme heihin mahtavan, taitavan voimalla.');
INSERT INTO "finnish" VALUES(54,43,'54:43','Ovatko teidän aikanne epäuskoiset parempia kuin nämä? Vai onko Pyhässä kirjassa poikkeus juuri teidän hyväksenne?');
INSERT INTO "finnish" VALUES(54,44,'54:44','Tai sanovatko he: »Me seisomme lujasti yhdessä, autamme joukolla itseämme.»');
INSERT INTO "finnish" VALUES(54,45,'54:45','Pian hajoitetaan tuo joukko, ja he kääntyvät pakoon.');
INSERT INTO "finnish" VALUES(54,46,'54:46','Niin, hetki on heille määrätty, ja se hetki on mitä kovin ja katkerin.');
INSERT INTO "finnish" VALUES(54,47,'54:47','Totisesti ovat syntiset harhaan johdettuja ja mielettömyyden vallassa.');
INSERT INTO "finnish" VALUES(54,48,'54:48','Sinä päivänä, jona heidät suin päin raastetaan tuleen, sanotaan heille: »Kokekaa kuumuuden kouria!»');
INSERT INTO "finnish" VALUES(54,49,'54:49','Olemme totisesti luonut kaiken oman mittansa mukaan,');
INSERT INTO "finnish" VALUES(54,50,'54:50','ja Meidän käskymme on nopea kuin silmänräpäys.');
INSERT INTO "finnish" VALUES(54,51,'54:51','Totisesti olemme kukistanut vertaisenne, mutta onko ketään, joka tahtoisi noudattaa varoitusta?');
INSERT INTO "finnish" VALUES(54,52,'54:52','Kaikki, mitä he tekevät, on Jumalan kirjoissa.');
INSERT INTO "finnish" VALUES(54,53,'54:53','Kaikki, sekä suuri että pieni, on kirjoitettu muistiin.');
INSERT INTO "finnish" VALUES(54,54,'54:54','Totisesti saavat ne, jotka Jumalaa pelkäävät, olla puutarhoissa ja virtojen äärillä,');
INSERT INTO "finnish" VALUES(54,55,'54:55','totuuden asuinsijoissa, lähellä Kuningasta, mahtavaa.');
INSERT INTO "finnish" VALUES(55,1,'55:1','Ar-Rahmän, alati laupias Armahtaja,');
INSERT INTO "finnish" VALUES(55,2,'55:2','on ilmaissut Koraanin.');
INSERT INTO "finnish" VALUES(55,3,'55:3','Hän on luonut ihmisen.');
INSERT INTO "finnish" VALUES(55,4,'55:4','Hän on opettanut tälle puheen lahjan.');
INSERT INTO "finnish" VALUES(55,5,'55:5','Aurinko ja kuu kiertävät ratojaan,');
INSERT INTO "finnish" VALUES(55,6,'55:6','ja yrtit sekä puut palvovat Häntä.');
INSERT INTO "finnish" VALUES(55,7,'55:7','Hän on asettanut taivaan korkealle ja saattanut voimaan tasapainon,');
INSERT INTO "finnish" VALUES(55,8,'55:8','jotta ette tekisi väärin punnitessanne.');
INSERT INTO "finnish" VALUES(55,9,'55:9','Käyttäkää vaakaa oikeudenmukaisesti älkääkä vajaasti mitatko.');
INSERT INTO "finnish" VALUES(55,10,'55:10','Maan Hän on luonut eläviä olentoja varten');
INSERT INTO "finnish" VALUES(55,11,'55:11','ja hedelmät ja taatelit suojaavin verhoin,');
INSERT INTO "finnish" VALUES(55,12,'55:12','viljan akanoineen ja tuoksuvat kukkaset.');
INSERT INTO "finnish" VALUES(55,13,'55:13','Minkä Herranne armotyön siis väitätte valheeksi?');
INSERT INTO "finnish" VALUES(55,14,'55:14','Hän loi ihmisen maan tomusta kuin saviastian;');
INSERT INTO "finnish" VALUES(55,15,'55:15','Hän loi henkiolennot tulen liekistä.');
INSERT INTO "finnish" VALUES(55,16,'55:16','Minkä Herranne armotyön siis väitätte valheeksi?');
INSERT INTO "finnish" VALUES(55,17,'55:17','Hän on idän Herra ja Hän on lännen Herra.');
INSERT INTO "finnish" VALUES(55,18,'55:18','Minkä Herranne armotyön siis väitätte valheeksi?');
INSERT INTO "finnish" VALUES(55,19,'55:19','Hän on avannut kummallekin merelle vapaan vyöryn, niin että ne kohtaavat toisensa,');
INSERT INTO "finnish" VALUES(55,20,'55:20','vaikka niiden välillä onkin raja, jota ne eivät saa ylittää.');
INSERT INTO "finnish" VALUES(55,21,'55:21','Minkä Herranne armotyön siis väitätte valheeksi?');
INSERT INTO "finnish" VALUES(55,22,'55:22','Niistä molemmista saadaan helmiä, isoja ja pieniä.');
INSERT INTO "finnish" VALUES(55,23,'55:23','Minkä Herranne armotyön siis väitätte valheeksi?');
INSERT INTO "finnish" VALUES(55,24,'55:24','Hänen ovat myös alukset, jotka kohoavat merestä kuin vuoret.');
INSERT INTO "finnish" VALUES(55,25,'55:25','Minkä Herranne armotyön siis väitätte valheeksi?');
INSERT INTO "finnish" VALUES(55,26,'55:26','Jokainen kuolee kerran,');
INSERT INTO "finnish" VALUES(55,27,'55:27','mutta Herrasi läsnäolo säilyy aina kunniassa ja ihanuudessa.');
INSERT INTO "finnish" VALUES(55,28,'55:28','Minkä Herranne armotyön siis väitätte valheeksi?');
INSERT INTO "finnish" VALUES(55,29,'55:29','Jokainen, joka on taivaassa ja maan päällä, huutaa Häntä avuksi; Hän on toiminnassa joka hetki.');
INSERT INTO "finnish" VALUES(55,30,'55:30','Minkä Herranne armotyön siis väitätte valheeksi?');
INSERT INTO "finnish" VALUES(55,31,'55:31','Me otamme teidät pian huostaamme, te molemmat vaivatut.');
INSERT INTO "finnish" VALUES(55,32,'55:32','Minkä Herranne armotyön siis väitätte valheeksi?');
INSERT INTO "finnish" VALUES(55,33,'55:33','Te henkien ja ihmisten joukot, jos voitte paeta taivaitten ja maan rajojen ulkopuolelle, niin paetkaa! Te ette voi paeta ilman lupaa.');
INSERT INTO "finnish" VALUES(55,34,'55:34','Minkä Herranne armotyön siis väitätte valheeksi?');
INSERT INTO "finnish" VALUES(55,35,'55:35','Hän lähettää teitä vastaan tulenliekit ja savun, eikä teillä silloin ole mitään apua.');
INSERT INTO "finnish" VALUES(55,36,'55:36','Minkä Herranne armotyön siis väitätte valheeksi?');
INSERT INTO "finnish" VALUES(55,37,'55:37','Ja kun taivas repeää ja tulee ruusunpunaiseksi kuin punainen nahka, -');
INSERT INTO "finnish" VALUES(55,38,'55:38','Minkä Herranne armotyön siis väitätte valheeksi?');
INSERT INTO "finnish" VALUES(55,39,'55:39','tuona päivänä ei kysytä ihmiseltä eikä henkiolennolta hänen syntejään.');
INSERT INTO "finnish" VALUES(55,40,'55:40','Minkä Herranne armotyön siis väitätte valheeksi?');
INSERT INTO "finnish" VALUES(55,41,'55:41','Syylliset tunnetaan kasvojensa ilmeestä, ja heihin tartutaan otsahiuksista ja jaloista.');
INSERT INTO "finnish" VALUES(55,42,'55:42','Minkä Herranne armotyön siis väitätte valheeksi?');
INSERT INTO "finnish" VALUES(55,43,'55:43','Tämä on helvetti, jonka syntiset väittävät valheeksi.');
INSERT INTO "finnish" VALUES(55,44,'55:44','He saavat hoippua sen ja kiehuvan veden väliä.');
INSERT INTO "finnish" VALUES(55,45,'55:45','Minkä Herranne armotyön siis väitätte valheeksi?');
INSERT INTO "finnish" VALUES(55,46,'55:46','Mutta niitä varten, jotka pelkäävät ja kunnioittavat Herransa mahtavuutta, on olemassa kaksi puutarhaa -');
INSERT INTO "finnish" VALUES(55,47,'55:47','minkä Herranne armotyön siis väitätte valheeksi? -');
INSERT INTO "finnish" VALUES(55,48,'55:48','kaksi paratiisia lehväisine puineen -');
INSERT INTO "finnish" VALUES(55,49,'55:49','minkä Herranne armotyön siis väitätte valheeksi?');
INSERT INTO "finnish" VALUES(55,50,'55:50','Niissä kummassakin on kaksi pulppuavaa lähdettä.');
INSERT INTO "finnish" VALUES(55,51,'55:51','Minkä Herranne armotyön siis väitätte valheeksi?');
INSERT INTO "finnish" VALUES(55,52,'55:52','Niissä molemmissa on jokaista hedelmää kahta laatua.');
INSERT INTO "finnish" VALUES(55,53,'55:53','Minkä Herranne armotyön siis väitätte valheeksi?');
INSERT INTO "finnish" VALUES(55,54,'55:54','He saavat siellä levätä matoilla, joiden päällys on kulta~ ja hopeakangasta, molempien puutarhain sato kättensä ulottuvilla.');
INSERT INTO "finnish" VALUES(55,55,'55:55','Minkä Herranne armotyön siis väitätte valheeksi?');
INSERT INTO "finnish" VALUES(55,56,'55:56','Niissä on ujokatseisia kaunottaria, neitoja, joita ennen ei ihminen eikä dzinni ole koskettanut.');
INSERT INTO "finnish" VALUES(55,57,'55:57','Minkä Herranne armotyön siis väitätte valheeksi?');
INSERT INTO "finnish" VALUES(55,58,'55:58','Rubiinien ja helmien kaltaisia.');
INSERT INTO "finnish" VALUES(55,59,'55:59','Minkä Herranne armotyön siis väitätte valheeksi?');
INSERT INTO "finnish" VALUES(55,60,'55:60','Onko hyvyyden palkka muu kuin hyvyys?');
INSERT INTO "finnish" VALUES(55,61,'55:61','Minkä Herranne armotyön siis väitätte valheeksi?');
INSERT INTO "finnish" VALUES(55,62,'55:62','Paitsi näitä molempia on myös kaksi muuta puutarhaa.');
INSERT INTO "finnish" VALUES(55,63,'55:63','Minkä Herranne armotyön siis väitätte valheeksi?');
INSERT INTO "finnish" VALUES(55,64,'55:64','Molemmat puutarhat ovat syvästi tummanvihreitä.');
INSERT INTO "finnish" VALUES(55,65,'55:65','Minkä Herranne armotyön siis väitätte valheeksi?');
INSERT INTO "finnish" VALUES(55,66,'55:66','Niissä on kaksi pulppuavaa lähdettä.');
INSERT INTO "finnish" VALUES(55,67,'55:67','Minkä Herranne armotyön siis väitätte valheeksi?');
INSERT INTO "finnish" VALUES(55,68,'55:68','Niissä on hedelmiä, taateleita ja granaattiomenia.');
INSERT INTO "finnish" VALUES(55,69,'55:69','Minkä Herranne armotyön siis väitätte valheeksi?');
INSERT INTO "finnish" VALUES(55,70,'55:70','Niissä on hyveellisiä kaunottaria.');
INSERT INTO "finnish" VALUES(55,71,'55:71','Minkä Herranne armotyön siis väitätte valheeksi?');
INSERT INTO "finnish" VALUES(55,72,'55:72','Tummasilmäisiä, telttoihin suljettuja.');
INSERT INTO "finnish" VALUES(55,73,'55:73','Minkä Herranne armotyön siis väitätte valheeksi?');
INSERT INTO "finnish" VALUES(55,74,'55:74','Heihin ei ole ihminen eikä dzinni koskenut ennen teitä.');
INSERT INTO "finnish" VALUES(55,75,'55:75','Minkä Herranne armotyön siis väitätte valheeksi?');
INSERT INTO "finnish" VALUES(55,76,'55:76','Siellä he saavat levätä vihreillä pieluksilla ja ihanilla matoilla.');
INSERT INTO "finnish" VALUES(55,77,'55:77','Minkä Herranne armotyön siis väitätte valheeksi?');
INSERT INTO "finnish" VALUES(55,78,'55:78','Siunattu olkoon Herrasi nimi kunniassaan ja ihanuudessaan!');
INSERT INTO "finnish" VALUES(56,1,'56:1','Kun Suuri Tapahtuma tulee,');
INSERT INTO "finnish" VALUES(56,2,'56:2','ei ole ketään, joka voisi sen kieltää.');
INSERT INTO "finnish" VALUES(56,3,'56:3','Se alentaa ja ylentää,');
INSERT INTO "finnish" VALUES(56,4,'56:4','kun maa vapisee ankarasti,');
INSERT INTO "finnish" VALUES(56,5,'56:5','ja vuoret murtuvat sirpaleiksi,');
INSERT INTO "finnish" VALUES(56,6,'56:6','niin että niistä tulee kuin hajoitettua tomua,');
INSERT INTO "finnish" VALUES(56,7,'56:7','ja teitä tulee olemaan kolmea laatua.');
INSERT INTO "finnish" VALUES(56,8,'56:8','Ensiksi ne, jotka ovat oikealla; ketkä ovat ne onnelliset, jotka ovat oikealla puolella?');
INSERT INTO "finnish" VALUES(56,9,'56:9','Ja ne, jotka ovat vasemmalla; ketkä ovat ne onnettomat, jotka ovat vasemmalla puolella?');
INSERT INTO "finnish" VALUES(56,10,'56:10','Ja ensimmäiset pysyvät ensimmäisinä.');
INSERT INTO "finnish" VALUES(56,11,'56:11','Heidät saatetaan Jumalan läheisyyteen');
INSERT INTO "finnish" VALUES(56,12,'56:12','autuuden puutarhoihin -');
INSERT INTO "finnish" VALUES(56,13,'56:13','suuret joukot niitä, jotka aikaisemmin liittyivät profeettaan,');
INSERT INTO "finnish" VALUES(56,14,'56:14','mutta vain pieni määrä myöhemmin liittyneitä.');
INSERT INTO "finnish" VALUES(56,15,'56:15','Siellä he saavat levätä kultakuteisilla istuimilla');
INSERT INTO "finnish" VALUES(56,16,'56:16','toinen toistaan vastapäätä,');
INSERT INTO "finnish" VALUES(56,17,'56:17','ikuisesti nuoret pojat palvelevat heitä,');
INSERT INTO "finnish" VALUES(56,18,'56:18','pikarit, haarikat ja maljat täynnä puhdasta juomaa,');
INSERT INTO "finnish" VALUES(56,19,'56:19','josta he eivät saa päänkipua eivätkä päihdy,');
INSERT INTO "finnish" VALUES(56,20,'56:20','tarjoten hedelmiä, jotka ovat heille mieluisia,');
INSERT INTO "finnish" VALUES(56,21,'56:21','ja linnunlihaa, aivan heidän toivonsa mukaan.');
INSERT INTO "finnish" VALUES(56,22,'56:22','Siellä on kaunosilmäisiä sulottaria,');
INSERT INTO "finnish" VALUES(56,23,'56:23','jotka ovat kuin kätketyt helmet,');
INSERT INTO "finnish" VALUES(56,24,'56:24','palkaksi siitä, mitä he ovat tehneet.');
INSERT INTO "finnish" VALUES(56,25,'56:25','Siellä he eivät saa kuulla turhaa eivätkä syntistä puhetta,');
INSERT INTO "finnish" VALUES(56,26,'56:26','vaan he sanovat: »Rauha, rauha!»');
INSERT INTO "finnish" VALUES(56,27,'56:27','Entä ne, jotka ovat oikealla; keitä ovat ne, jotka ovat oikealla puolella?');
INSERT INTO "finnish" VALUES(56,28,'56:28','He ovat lootuspuiden keskellä, puiden, joissa ei ole okaa,');
INSERT INTO "finnish" VALUES(56,29,'56:29','ja banaanien, joissa on terttu tertun päällä.');
INSERT INTO "finnish" VALUES(56,30,'56:30','Siellä on avaria varjopaikkoja,');
INSERT INTO "finnish" VALUES(56,31,'56:31','vuolaasti virtaavaa vesiä');
INSERT INTO "finnish" VALUES(56,32,'56:32','ja runsaasti hedelmiä,');
INSERT INTO "finnish" VALUES(56,33,'56:33','jotka eivät vähene ja joita ei heiltä evätä');
INSERT INTO "finnish" VALUES(56,34,'56:34','Siellä on neitoja ylväillä lepovuoteillaan.');
INSERT INTO "finnish" VALUES(56,35,'56:35','Totisesti olemme luonut heidät omin käsin');
INSERT INTO "finnish" VALUES(56,36,'56:36','sallien heidän pysyä neitseinä');
INSERT INTO "finnish" VALUES(56,37,'56:37','hellinä ja samanikäisinä,');
INSERT INTO "finnish" VALUES(56,38,'56:38','niitä varten, jotka ovat oikealla puolella,');
INSERT INTO "finnish" VALUES(56,39,'56:39','joista lukuisat kuuluivat aikaisimpiin profeetan kannattajiin,');
INSERT INTO "finnish" VALUES(56,40,'56:40','ja myöhemmin liittyneitä suuri joukko.');
INSERT INTO "finnish" VALUES(56,41,'56:41','Entä ne, jotka ovat vasemmalla; keitä raukkoja ovat ne, jotka ovat vasemmalla puolella?');
INSERT INTO "finnish" VALUES(56,42,'56:42','Heitä tukahduttavat kuumat tuulet ja polttaa kiehuva vesi');
INSERT INTO "finnish" VALUES(56,43,'56:43','sekä pimentää musta savu,');
INSERT INTO "finnish" VALUES(56,44,'56:44','joka ei tuo vilvoitusta eikä virkistystä.');
INSERT INTO "finnish" VALUES(56,45,'56:45','Aikanaan elivät he totisesti yltäkylläisyydessä,');
INSERT INTO "finnish" VALUES(56,46,'56:46','paatuen suurissa synneissään');
INSERT INTO "finnish" VALUES(56,47,'56:47','sanoen toisilleen: »Kun olemme kuolleet ja meistä on tullut tomua ja luita, herätetäänkö meidät todella kuolleista,');
INSERT INTO "finnish" VALUES(56,48,'56:48','tai herätetäänkö esi-isämme?»');
INSERT INTO "finnish" VALUES(56,49,'56:49','Sano: »Sekä edelliset että jälkimmäiset');
INSERT INTO "finnish" VALUES(56,50,'56:50','kootaan yhteen määrättynä hetkenä ja päivänä.»');
INSERT INTO "finnish" VALUES(56,51,'56:51','Te harhapoluille joutuneet, jotka selititte totuuden valheeksi,');
INSERT INTO "finnish" VALUES(56,52,'56:52','totisesti te saatte silloin syödä Zakkuum-puuta');
INSERT INTO "finnish" VALUES(56,53,'56:53','ja täyttää vatsanne sillä,');
INSERT INTO "finnish" VALUES(56,54,'56:54','juoda kiehuvaa vettä sen päälle,');
INSERT INTO "finnish" VALUES(56,55,'56:55','juoda niinkuin janoiset kameelit.');
INSERT INTO "finnish" VALUES(56,56,'56:56','Tässä on heidän kestityksensä tuomiopäivänä.');
INSERT INTO "finnish" VALUES(56,57,'56:57','Me olemme luonut teidät, miksi siis ette tunnusta sitä totuudeksi?');
INSERT INTO "finnish" VALUES(56,58,'56:58','Oletteko ajatelleet siementä, joka antoi teille elämän?');
INSERT INTO "finnish" VALUES(56,59,'56:59','Loitteko te sen vai Mekö olemme sen luonut?');
INSERT INTO "finnish" VALUES(56,60,'56:60','Me olemme asettanut teille kuoleman, eikä mikään estä Meitä');
INSERT INTO "finnish" VALUES(56,61,'56:61','luomasta sijaanne toisenlaisia olentoja, antamalla teille muodon, jota ette tunne.');
INSERT INTO "finnish" VALUES(56,62,'56:62','Olette totisesti tulleet tuntemaan ensimmäisen luomisen, miksi ette ota kehoitusta varteen?');
INSERT INTO "finnish" VALUES(56,63,'56:63','Oletteko ajatelleet kylvöänne?');
INSERT INTO "finnish" VALUES(56,64,'56:64','Tekö saatte sen kasvamaan, vai Mekö sen aikaansaamme?');
INSERT INTO "finnish" VALUES(56,65,'56:65','Jos Me tahtoisimme, voisimme muuttaa sen kuivuneiksi korsiksi, niin että te ihmettelisitte kokonaisen päivän ja sanoisitte:');
INSERT INTO "finnish" VALUES(56,66,'56:66','»Totisesti, velkana on meillä kaikki,');
INSERT INTO "finnish" VALUES(56,67,'56:67','niin - meillä ei ole mitään.»');
INSERT INTO "finnish" VALUES(56,68,'56:68','Mitä ajattelette vedestä, jota juotte?');
INSERT INTO "finnish" VALUES(56,69,'56:69','Tekö sen vuodatatte alas pilvistä, vai Mekö sen teemme?');
INSERT INTO "finnish" VALUES(56,70,'56:70','Jos tahtoisimme, voisimme tehdä sen suolaiseksi; miksi siis ette ole kiitollisia?');
INSERT INTO "finnish" VALUES(56,71,'56:71','Mitä ajattelette tulesta, jonka sytytätte?');
INSERT INTO "finnish" VALUES(56,72,'56:72','Tekö olette kasvattaneet puun sitä varten, vai Mekö olemme sen kasvattanut?');
INSERT INTO "finnish" VALUES(56,73,'56:73','Me olemme asettanut puut erämaan vaeltajalle kehoitukseksi ja hyödyksi.');
INSERT INTO "finnish" VALUES(56,74,'56:74','Ylistä siis suuren Herrasi nimeä,');
INSERT INTO "finnish" VALUES(56,75,'56:75','Vannon tähtien aseman nimeen,');
INSERT INTO "finnish" VALUES(56,76,'56:76','ja se on totisesti suuri vala, jospa sen tietäisitte,');
INSERT INTO "finnish" VALUES(56,77,'56:77','totisesti tämä on ylevä Koraani,');
INSERT INTO "finnish" VALUES(56,78,'56:78','hyvin säilyneenä kirjassa.');
INSERT INTO "finnish" VALUES(56,79,'56:79','Sitä saavat koskettaa vain puhtaat.');
INSERT INTO "finnish" VALUES(56,80,'56:80','Sen lähetti maailmojen Herra.');
INSERT INTO "finnish" VALUES(56,81,'56:81','Tahdotteko sittenkin halveksia tätä Kirjaa');
INSERT INTO "finnish" VALUES(56,82,'56:82','ja aivan kuin elinkeinoksenne väittää sitä valheeksi?');
INSERT INTO "finnish" VALUES(56,83,'56:83','Mutta kun sielu, henkenne ruumiista paetessa, nousee kurkkuun');
INSERT INTO "finnish" VALUES(56,84,'56:84','ja te silloin katselette kuolemaa,');
INSERT INTO "finnish" VALUES(56,85,'56:85','- vaikka olemmehan Me lähempänä kuolevaa kuin te, teidän sitä kuitenkaan näkemättä -');
INSERT INTO "finnish" VALUES(56,86,'56:86','miksi ette te, jotka kiellätte riippuvaisuutenne Jumalasta,');
INSERT INTO "finnish" VALUES(56,87,'56:87','voi silloin torjua sitä kääntymään takaisin, jos totta puhutte?');
INSERT INTO "finnish" VALUES(56,88,'56:88','Jos kuoleva kuuluu niihin, jotka pääsevät lähelle Jumalaa,');
INSERT INTO "finnish" VALUES(56,89,'56:89','tulee hänen osakseen onni, kukkien tuoksu ja autuuden puutarhat.');
INSERT INTO "finnish" VALUES(56,90,'56:90','Jos hän on niitä, jotka joutuvat oikealle puolelle,');
INSERT INTO "finnish" VALUES(56,91,'56:91','on hänen osansa: »Rauha olkoon sinulla ja niillä, jotka ovat oikealla puolella!»');
INSERT INTO "finnish" VALUES(56,92,'56:92','Mutta jos hän kuuluu niihin, jotka väittävät totuutta valheeksi, ja kulkee harhateitä,');
INSERT INTO "finnish" VALUES(56,93,'56:93','on hänen tervetuliaismaljansa kiehuvaa vettä');
INSERT INTO "finnish" VALUES(56,94,'56:94','ja hän palaa ikuisessa tulessa.');
INSERT INTO "finnish" VALUES(56,95,'56:95','Tämä on totisesti varma totuus.');
INSERT INTO "finnish" VALUES(56,96,'56:96','Ylistä siis Herrasi nimeä, suuren.');
INSERT INTO "finnish" VALUES(57,1,'57:1','Kaikki taivaassa ja maan päällä julistaa Jumalan kunniaa, sillä Hän on kaikkivoipa, kaikkitietävä.');
INSERT INTO "finnish" VALUES(57,2,'57:2','Hänen on valta taivaassa ja maan päällä, Hän antaa elämän ja Hän säätää kuoleman; Hän voi tehdä kaiken, mitä tahtoo.');
INSERT INTO "finnish" VALUES(57,3,'57:3','Hän on ensimmäinen ja viimeinen, näkyvä ja näkymätön, sillä Hän tietää kaikki.');
INSERT INTO "finnish" VALUES(57,4,'57:4','Hän on luonut taivaat ja maan kuutena päivänä ja sitten asettunut valtaistuimelle. Hän tietää, mitä painuu maan sisään ja mitä siitä versoo esiin, mitä tulee ylhäältä ja mitä nousee ylös. Hän on kanssanne, missä tahansa olettekin, sillä Jumala näkee, mitä teette.');
INSERT INTO "finnish" VALUES(57,5,'57:5','Hänen on taivaiden ja maan valtakunta, ja Jumalan luokse kaikki palaa.');
INSERT INTO "finnish" VALUES(57,6,'57:6','Hän käskee yön valjeta päiväksi, Hän vajottaa päivän yöhön, ja Hän tuntee svdäntemme salat.');
INSERT INTO "finnish" VALUES(57,7,'57:7','Uskokaa Jumalaan ja Hänen sananjulistajaansa ja antakaa almuja siitä, minkä haltijaksi Me olemme teidät asettanut, sillä niitä teistä, jotka uskovat ja jakelevat, odottaa suuri palkinto.');
INSERT INTO "finnish" VALUES(57,8,'57:8','Miksi epäilisitte? Kehoittaahan Hänen sananjulistajansa teitä uskomaan Herraanne; onhan Hän todellakin tehnyt liiton kanssanne, jos olette uskovaisia.');
INSERT INTO "finnish" VALUES(57,9,'57:9','Hän lähettää selvät merkit palvelijalleen, jotta tämä voisi johtaa teitä pimeydestä valoon; sillä totisesti Jumala rakastaa teitä ja on teille armollinen.');
INSERT INTO "finnish" VALUES(57,10,'57:10','Ja miksi ette uhraisi mitään Jumalan asian hyväksi, sillä Jumalalle kuuluu taivaitten ja maan perintöosuus. Ne teistä, jotka uhrasivat varojaan ennen voittoa ja taistelivat, ovat toisenlaisia kuin ne, jotka eivät niin tehneet. He ovat korkeammassa asemassa kuin ne, jotka uhrasivat vasta jälkeenpäin, vaikka taistelivatkin. Itse kullekin heistä on Jumala luvannut hyvää, ja Jumala tietää, mitä te teette.');
INSERT INTO "finnish" VALUES(57,11,'57:11','Sille, joka antaa Jumalalle uhrin, Hän korvaa sen kaksin verroin. Hänen palkkansa on ihana');
INSERT INTO "finnish" VALUES(57,12,'57:12','sinä päivänä, jolloin olet näkevä uskolliset miehet ja naiset, valo edessään ja oikealla puolellaan - »hyvä sanoma lupaa teille sinä päivänä puutarhoja, joita virrat vilvoittavat, teidän asuaksenne niissä; tämä on suurin autuus» -');
INSERT INTO "finnish" VALUES(57,13,'57:13','sinä päivänä tekopyhät miehet ja naiset sanovat niille, jotka uskovat: »Odottakaa meitä, jotta saamme valoa teidän valostanne.» Silloin sanotaan: »Kääntykää takaisin ja etsikää valoa!» Ja heidän välilleen kohoaa erottava muuri, jossa on portti. Muurin sisäpuolella on armo, mutta sen ulkopuolella odottaa kuritus.');
INSERT INTO "finnish" VALUES(57,14,'57:14','Ja he huutavat: »Emmekö me olleet teidän kanssanne?» Nämä vastaavat: »Kyllä, mutta te saatoitte sielunne kiusaukseen, odotitte ja epäröitte, ja turhat halunne pettivät teitä siihen saakka, kunnes Jumalan tuomio koitti; ja suuri viettelijä sai teidät tottelemattomiksi Jumalaa kohtaan,');
INSERT INTO "finnish" VALUES(57,15,'57:15','niin että tänä päivänä ei oteta vastaan lunnaita teiltä eikä niiltä, jotka valitsivat epäuskon. Teidän on oltava tulessa, se on oleva seuralaisenne; kurja on olinpaikkanne.');
INSERT INTO "finnish" VALUES(57,16,'57:16','Eikö ole aika niiden, jotka uskovat, nöyrtyä sydämissään, muistaa Jumalaa ja Hänen lähettämäänsä totuutta, jotta heidän ei kävisi kuin niiden, joille Kirjoitus annettiin aikaisemmin, niin että heidän aikansa näytti pidennetyltä, mutta jotka paaduttivat sydämensä ja joista useimmista tuli lainrikkojia.');
INSERT INTO "finnish" VALUES(57,17,'57:17','Tietäkää, että Jumala elvyttää maan sen kuihtumuksesta. Me olemme totisesti kirkastaneet teille tunnusmerkit ymmärtääksenne.');
INSERT INTO "finnish" VALUES(57,18,'57:18','Armeliaille miehille ja naisille ja niille, jotka kantavat Jumalalle hyvän uhrin, Hän korvaa sen kaksin verroin, ja heidän palkkansa on oleva ihana.');
INSERT INTO "finnish" VALUES(57,19,'57:19','Ne, jotka uskovat Jumalaan ja Hänen sananjulistajiinsa, ovat todistajia Herransa edessä; he saavat palkkansa ja valonsa; mutta ne, jotka valitsevat epäuskon ja väittävät merkkejämme valheeksi, joutuvat helvetin asukkaiksi.');
INSERT INTO "finnish" VALUES(57,20,'57:20','Tietäkää, että tämän maailman elämä on vain leikkiä, ajanvietettä, koreilua ja keskinäistä kerskailua, rikkauden ja perheen kartuttamista. Se on kuin sadekuuro, jonka aiheuttama vihanta kasvullisuus ilahduttaa viljelijöitä, mutta tämä sitten kuihtuukin, ja sinä näet sen kellastuvan ja lakastuvan. Tulevassa elämässä on kuritus ankara, mutta Jumalan luona myös anteeksianto ja mielisuosio; niin, maallinen elämä on pelkkää pettävää nautintoa.');
INSERT INTO "finnish" VALUES(57,21,'57:21','Kiiruhtakaa kilvan saavuttaaksenne Herranne anteeksiannon ja Paratiisin, jonka avaruus on maan ja taivaan suuruinen ja joka on valmistettu niille, jotka uskovat Jumalaan ja Hänen sananjulistajiinsa! Tämä on Jumalan armo. Hän antaa sen kenelle haluaa, sillä Hän on Herra, jonka armo on suuri.');
INSERT INTO "finnish" VALUES(57,22,'57:22','Mitään ei tapahdu maailmassa eikä teissä itsessänne, jota ei ole merkitty Pyhään kirjaan, ennenkuin Me annamme sen tulla ilmi. Tämä on totisesti helppoa Jumalalle.');
INSERT INTO "finnish" VALUES(57,23,'57:23','Älkää siis murehtiko sitä, mitä olette menettäneet, älkääkä myöskään kerskuko sen perusteella, mitä Hän on teille antanut, sillä Jumala ei rakasta röyhkeätä ja kerskailevaa ihmistä;');
INSERT INTO "finnish" VALUES(57,24,'57:24','ei liioin niitä, jotka ovat kitsaita ja kehoittavat muita ahneuteen. Ja jos joku kääntyy pois, niin totisesti Jumala ei häntä eikä mitään kaipaa.');
INSERT INTO "finnish" VALUES(57,25,'57:25','Olemmehan lähettänyt sanansaattajamme selvin todistein ja heidän mukanaan olemme lähettänyt Pyhän kirjan ja mittaperusteet, jotta ihmiskunta saattaisi noudattaa oikeutta; Me olemme myös antanut raudan, johon on kätketty suuri voima ja josta on hyötyä ihmiskunnalle, jotta Jumala tuntisi ne, jotka sydämestään auttavat Häntä ja Hänen sananjulistajiaan. Totisesti, Jumala on voimakas, mahtava.');
INSERT INTO "finnish" VALUES(57,26,'57:26','Totisesti olemme lähettänyt Nooan ja Aabrahamin ja antanut heidän jälkeläisilleen profeetankutsumuksen ja Pyhän kirjan. Heidän joukossaan on muutamia, jotka vaeltavat vanhurskaasti, mutta useimmat heistä ovat käskyjen rikkojia.');
INSERT INTO "finnish" VALUES(57,27,'57:27','Sitten lähetimme sanansaattajia seuraamaan heidän jälkiään, ja myöhemmin Me lähetimme Jeesuksen, Marian pojan, ja Me annoimme hänelle evankeliumin. Me istutimme myös rakkauden ja armeliaisuuden niiden sydämiin, jotka seuraavat häntä. Luostarilaitoksen he ovat itse keksineet, - Me emme ole antanut heille muuta määräystä kuin että he etsisivät Jumalan mielisuosiota, mutta he eivät noudattaneet sitä niin kuin olisi pitänyt. Silloin annoimme palkkansa niille heistä, jotka uskoivat, mutta useimmat heistä ovat lainrikkojia.');
INSERT INTO "finnish" VALUES(57,28,'57:28','Te, jotka uskotte, pelätkää Jumalaa ja uskokaa Hänen sananjulistajaansa; Hän jakaa teille armoaan kaksin kerroin ja Hän antaa teille valkeuden, jossa voitte vaeltaa; Hän on antava teille anteeksi, sillä Jumala on laupias ja anteeksiantavainen.');
INSERT INTO "finnish" VALUES(57,29,'57:29','Näin on, jotta nekin, jotka ovat saaneet Kirjan, tietäisivät, etteivät he pane rajoja Jumalan armolle, koska Jumalan armo on Hänen kädessään, Hän sulkee siihen kenet tahtoo; niin, Jumala on valtavan armon Herra.');
INSERT INTO "finnish" VALUES(58,1,'58:1','Jumala on jo kuullut hänen vetoomuksensa, joka valitti puolisoansa vastaan, etsien oikeutta Jumalalta, Jumala on kuullut teidän keskustelunne; Jumala on totisesti kuuleva, näkevä.');
INSERT INTO "finnish" VALUES(58,2,'58:2','Jos jotkut teistä hylkäävät vaimonsa sillä verukkeella, että nämä takaapäin ovat kuin äitinsä, niin eiväthän he ole heidän äitejään. Heidän äitejään ovat vain ne, jotka ovat synnyttäneet heidät, ja heidän sanansa totisesti ovat ilkeätä ja valheellista puhetta. Jumala on totisesti armahtava, anteeksiantava.');
INSERT INTO "finnish" VALUES(58,3,'58:3','Niiden, jotka syrjäyttävät vaimonsa verraten heitä äiteihinsä ja sitten haluavat peruuttaa mitä ovat sanoneet, on vapautettava orja, ennenkuin he koskevat toinen toiseensa. Tähän teitä kehoitetaan, ja Jumala tuntee tekonne.');
INSERT INTO "finnish" VALUES(58,4,'58:4','Mutta jos jollakulla ei ole varaa tähän, paastotkoon hän sen asemesta kaksi kuukautta peräkkäin, ennenkuin he koskevat toinen toiseensa. Se taas, jolla ei ole tähän voimaa, ruokkikoon kuusikymmentä köyhää. Näin on, jotta uskoisitte Jumalaan ja Hänen sananjulistajaansa. Tämä on Jumalan laki, ja epäuskoisia odottaa tuska ja vaiva.');
INSERT INTO "finnish" VALUES(58,5,'58:5','Totisesti ne, jotka vastustavat Jumalaa ja Hänen sananjulistajaansa, lyödään maahan niinkuin ne, jotka elivät ennen heitä, ja Me olemme jo lähettänyt siitä selvät todisteet; ja epäuskoisia odottaa häpeällinen kuritus.');
INSERT INTO "finnish" VALUES(58,6,'58:6','Päivänä, jolloin Jumala on herättävä heidät kaikki kuolleista, on Hän ilmoittava heille, mitä he ovat tehneet. Jumala on merkinnyt sen muistiin, vaikka he ovat sen unohtaneet, sillä Jumala näkee kaiken.');
INSERT INTO "finnish" VALUES(58,7,'58:7','Etkö huomaa, että Jumala tietää kaiken, mitä on taivaassa ja maan päällä? Kolmella ei ole salaista keskustelua Hänen olemattaan siinä neljäntenä, eikä viidellä neuvottelua Hänen olemattaan kuudentena; olipa heitä vähemmän tai enemmän, Hän on läsnä missä tahansa he kokoontuvatkin. Ylösnousemuksen päivänä Hän on ilmoittava heille, mitä he ovat tehneet. Totisesti Jumala on kaikkitietävä.');
INSERT INTO "finnish" VALUES(58,8,'58:8','Etkö ole nähnyt niitä, joita on kielletty pitämästä salaisia neuvotteluja, mutta jotka taas ryhtyvät siihen, mikä heiltä kiellettiin, ja salaa yllyttävät toisiaan kapinaan ja tottelemattomuuteen sananjulistajaa vastaan. Kun he tulevat sinun luoksesi, he tervehtivät sinua tavalla, jolla Jumala ei sinua tervehdi, ja sanovat itsekseen: »Miksi ei Jumala rankaise meitä puheistamme?» Helvetti riittää heille rangaistukseksi; sinne he joutuvat, ja se on surkea olinpaikka.');
INSERT INTO "finnish" VALUES(58,9,'58:9','Te uskovaiset, kun pidätte yksityistä neuvottelua, älkää antako synnin yllyttää itseänne kapinaan ja tottelemattomuuteen sananjulistajaa vastaan, vaan pitäkää neuvottelunne hurskaudessa ja Jumalan pelossa ja täyttäkää velvollisuutenne Jumalaa kohtaan, jonka luokse kerran kokoonnutte.');
INSERT INTO "finnish" VALUES(58,10,'58:10','Salaiset neuvottelut ovat lähtöisin saatanasta, joka niillä pyrkii tuottamaan murhetta uskoville, mutta hän ei voi vahingoittaa heitä hituistakaan Jumalan sallimatta, ja Jumalaan uskovaiset luottakoot.');
INSERT INTO "finnish" VALUES(58,11,'58:11','Te uskovaiset, kun teitä pyydetään varaamaan tilaa kokouksissanne, varatkaa sitä runsaasti, Jumala on vuorostaan valmistava tilaa teille. Kun teitä pyydetään nousemaan, nouskaa; Jumala on korottava monta astetta ylemmäksi ne teistä, jotka uskovat, ja ne, joilla on tieto. Jumala tuntee tekonne.');
INSERT INTO "finnish" VALUES(58,12,'58:12','Te uskovaiset, kun pidätte neuvoa sananjulistajan kanssa, antakaa uhrilahja ennen neuvotteluanne. Näin on teille parasta ja oikeinta. Mutta jos teillä ei ole varaa siihen, niin totisesti Jumala on anteeksiantavainen, armollinen.');
INSERT INTO "finnish" VALUES(58,13,'58:13','Pelkäättekö, ettei teillä ole varaa almuihin ennen neuvotteluanne? Jos siis jätätte sen tekemättä, mutta Jumala kääntyy auttaen puoleenne, niin suorittakaa rukoukset ja antakaa säädetyt almut sekä totelkaa Jumalaa ja Hänen sanansaattajaansa, sillä Jumala tietää, mitä te teette.');
INSERT INTO "finnish" VALUES(58,14,'58:14','Oletko ajatellut niitä, jotka turvautuvat kansaan, jolle Jumala on vihastunut? He eivät kuulu teihin ettekä te heihin, vaan he vannovat väärin vastoin parempaa tietoaan.');
INSERT INTO "finnish" VALUES(58,15,'58:15','Jumala on heille varannut ankaran rangaistuksen, kauheaa on totisesti se, mitä he tekevät.');
INSERT INTO "finnish" VALUES(58,16,'58:16','Valojensa varjolla he vieroittavat muita Jumalan tieltä. Sentähden heitä odottaa häpeällinen rangaistus.');
INSERT INTO "finnish" VALUES(58,17,'58:17','Heidän omaisuutensa ja lapsensa eivät hyödytä heitä vähääkään Jumalan edessä; he joutuvat tuleen ja pysyvät siinä ikuisesti.');
INSERT INTO "finnish" VALUES(58,18,'58:18','Sinä päivänä, jona Jumala herättää heidät kaikki kuolleista, he vannovat Hänelle niinkuin ovat vannoneet teille ja luulevat sillä jotakin voittavansa. Eivätkö he totisesti ole valehtelijoita?');
INSERT INTO "finnish" VALUES(58,19,'58:19','Saatana on ottanut vallan heissä ja saanut heidät unohtamaan Jumalan kunnioittamisen; he ovat saatanan omia. Mutta ovathan saatanan omat häviöön tuomittuja.');
INSERT INTO "finnish" VALUES(58,20,'58:20','Totisesti ne, jotka vastustavat Jumalaa ja Hänen sananjulistajaansa, kuuluvat viheliäisimpien joukkoon.');
INSERT INTO "finnish" VALUES(58,21,'58:21','Jumala on sanonut: »Totisesti olen Minä voittava, Minä ja sananjulistajani.» Totisesti on Jumala voimakas, mahtava.');
INSERT INTO "finnish" VALUES(58,22,'58:22','Sellaisten joukosta, jotka uskovat Jumalaan ja viimeiseen päivään, et löydä ihmisiä, jotka rakastavat Jumalan ja Hänen sananjulistajansa vastustajia, vaikkapa nämä olisivat heidän isiään tai poikiaan tai veljiään tai sukulaisiaan. Sellaisten sydämiin Hän on painanut uskonsa ja vahvistanut heitä hengeltään. Hän sallii heidän päästä puutarhoihin, joita virrat vilvoittavat ja joissa he saavat asua iankaikkisesti. Jumala on tyytyväinen heihin ja he Jumalaan. He ovat Jumalan omia. Ja Jumalan omat ovat autuaita.');
INSERT INTO "finnish" VALUES(59,1,'59:1','Kaikki, mitä on taivaassa, ja kaikki, mitä on maassa, julistaa Jumalan kunniaa, sillä Hän on kaikkivoipa, viisas.');
INSERT INTO "finnish" VALUES(59,2,'59:2','Hän ensimmäisessä maastamuutossa karkoitti kodeistaan ne Pyhän kirjan saajista , jotka olivat epäuskoisia. Te ette luulleet, että he siirtyisivät pois, ja he uskoivat, että heidän linnoituksensa suojelisivat heitä Jumalaa vastaan, mutta Jumala tuli heidän luokseen sieltä, mistä he eivät odottaneet, ja nosti pelon heidän sydämiinsä, niin että he hävittivät talonsa omin käsin ja uskovaisten avulla. Ottakaa siis tästä oppiaksenne te, joilla on silmät.');
INSERT INTO "finnish" VALUES(59,3,'59:3','Ja ellei Jumala olisi määrännyt heille karkoitusta, Hän olisi varmasti antanut heille kärsimyksiä tässä maailmassa; ja tulevassa elämässä heitä odottaa tulen tuska.');
INSERT INTO "finnish" VALUES(59,4,'59:4','Näin on, koska he vastustivat Jumalaa ja Hänen sananjulistajaansa; sillä jos joku vastustaa Jumalaa, on Jumala totisesti ankara rangaistessaan.');
INSERT INTO "finnish" VALUES(59,5,'59:5','Jos olette kaataneet jonkun palmun tai jättäneet sen pystyyn juurilleen, on tämä tapahtunut Jumalan tahdosta ja jotta Hän saattaisi tottelemattomat häpeään.');
INSERT INTO "finnish" VALUES(59,6,'59:6','Ja noilta ihmisiltä saatua sotasaalista, jonka Jumala antoi sananjulistajalleen, ei saatu siksi, että te ahdistitte heitä hevosin tai ratsukameelein, vaan Jumala antaa sananjulistajilleen vallan kenen suhteen tahtoo, sillä Jumala voi tehdä kaiken, mitä haluaa.');
INSERT INTO "finnish" VALUES(59,7,'59:7','Ja kaupunkien asukkailta otettu saalis, jonka Jumala on antanut sanansaattajalleen, kuuluu Jumalalle ja Hänen sananjulistajalleen, läheisille sukulaisille, orvoille, köyhille ja maankiertäjille, jotta sitä ei jaettaisi niiden kesken teistä, jotka ovat rikkaita. Mitä tahansa sananjulistaja teille antaa, se ottakaa vastaan, ja mitä tahansa hän teiltä kieltää, siitä pidättykää. Pelätkää Jumalaa, sillä Jumala on ankara rangaistuksessaan.');
INSERT INTO "finnish" VALUES(59,8,'59:8','Osa saaliista on köyhiä varten, jotka pakenivat, jotka häädettiin kodeistaan ja joilta riistettiin omaisuus heidän etsiessään Jumalan armoa ja mielisuosiota ja auttaessaan Jumalaa ja Hänen sananjulistajaansa. Nämä ovat vanhurskaita.');
INSERT INTO "finnish" VALUES(59,9,'59:9','Ne, jotka ovat saaneet kodin ja uskon ennen heitä, rakastavat niitä, jotka pakenevat heidän turviinsa, eivätkä sydämessään kaipaa sitä, mitä näille on annettu. He pitäisivät näitä itseään parempana, vaikka itse eläisivät puutteessa. Ne, jotka varjelevat sielunsa ahneudelta, ovat onnellisia.');
INSERT INTO "finnish" VALUES(59,10,'59:10','Ne, jotka tulevat heidän jälkeensä, sanovat: »Herra, anna anteeksi meille ja meidän veljillemme, jotka ovat olleet ennen meitä uskossa, äläkä salli meidän tuntea kaunaa sydämissämme niitä kohtaan, jotka uskovat. Herra, Sinä olet totisesti lempeä, armollinen.');
INSERT INTO "finnish" VALUES(59,11,'59:11','Ajattale niitä, joista on tullut teeskentelijöitä. He sanovat veljilleen, jotka kuuluvat Pyhän kirjoituksen saaneeseen kansaan, mutta ovat epäuskoisia: »Jos teidät karkoitetaan, niin lähdemme me totisesti teidän kanssanne emmekä milloinkaan tottele kenenkään käskyjä teidän suhteenne, vaan jos teitä vastaan taistellaan, niin totisesti autamme teitä.» Mutta Jumala tietää, että he todellisuudessa ovat valehtelijoita.');
INSERT INTO "finnish" VALUES(59,12,'59:12','Jos nuo karkoitetaan, eivät he lähde heidän kanssaan, ja jos noita vastaan taistellaan, eivät he auta heitä, ja vaikka ryhtyisivätkin auttamaan heitä, niin kääntävät heille varmasti selkänsä, eivätkä he kuitenkaan apua saa.');
INSERT INTO "finnish" VALUES(59,13,'59:13','Totisesti on heillä sydämissään suurempi pelko teitä (muslimeja) kuin Jumalaa kohtaan, sillä he ovat ymmärtämättömiä ihmisiä.');
INSERT INTO "finnish" VALUES(59,14,'59:14','He eivät taistele teitä vastaan joukkona, vaan linnoitetuissa kaupungeissa tai muurien takaa. Heidän taistelurohkeutensa on suuri. Sinä luulet heitä yksimielisiksi, mutta heidän sydämensä ovat jakaantuneet, koska he ovat ihmisiä, joilla ei ole ymmärrystä.');
INSERT INTO "finnish" VALUES(59,15,'59:15','He ovat juuri kuin edeltäjänsä, jotka äsken saivat kokea tekojensa onnettomat seuraukset; heitä odottaa tuska ja vaiva.');
INSERT INTO "finnish" VALUES(59,16,'59:16','Heidän laitansa on kuin saatanan, joka sanoi ihmiselle: »Älä usko», mutta kun tämä luopui uskostaan, hän sanoi: »Totisesti minulla ei ole mitään tekemistä sinun kanssasi; totisesti minä pelkään Jumalaa, kaikkien maailmojen Herraa.»');
INSERT INTO "finnish" VALUES(59,17,'59:17','Mutta kummankin loppu on se, että he joutuvat tuleen ainiaaksi. Tämä on jumalattomien palkka.');
INSERT INTO "finnish" VALUES(59,18,'59:18','Te, jotka uskotte, pelätkää Jumalaa. Ja katsokoon kukin sielu, mitä panee itselleen tallelle tulevaksi päiväksi. Pelätkää Jumalaa! Sillä totisesti Jumala tietää mitä teette.');
INSERT INTO "finnish" VALUES(59,19,'59:19','Älkää olko niinkuin ne, jotka unohtavat Jumalan, niin että Hän antaa heidän unohtaa oman sielunsa. Nämä ovat jumalattomia.');
INSERT INTO "finnish" VALUES(59,20,'59:20','Eivät ole toistensa kaltaisia tulen ja paratiisin asukkaat. Paratiisin asukkaat riemuitsevat autuudesta.');
INSERT INTO "finnish" VALUES(59,21,'59:21','Jos olisimme lähettänyt tämän Koraanin vuorelta, niin olisit varmasti nähnyt sen sortuvan ja pirstoutuvan Jumalan pelosta. Nämä vertaukset lähetämme ihmisille, jotta he mietiskelisivät.');
INSERT INTO "finnish" VALUES(59,22,'59:22','Hän on Jumala, ja paitsi Häntä ei ole muuta jumaluutta; Hän tuntee salatun ja näkyvän. Hän on laupiain, armollisin.');
INSERT INTO "finnish" VALUES(59,23,'59:23','Hän on Jumala, ja paitsi Häntä ei ole muuta jumaluutta, kuningas, pyhä, rauhantuoja, turvanantaja, kaiken suojelija, kaikkivoipa, korkein, kaiken suuruuden alkulähde. Kunnia Jumalalle ainoastaan! Hän on korkealla kaiken yläpuolella, mitä he Hänen rinnalleen asettavat.');
INSERT INTO "finnish" VALUES(59,24,'59:24','Hän on Jumala, luoja, alkuunpanija, muodostaja; Hänelle kuuluvat kaikki erinomaiset nimet. Kaikki taivaassa ja maan päällä ylistää Hänen kunniaansa; sillä Hän on kaikkivoipa, viisas.');
INSERT INTO "finnish" VALUES(60,1,'60:1','Te, jotka uskotte, älkää pitäkö Minun ja omia vihollisianne ystävinänne. Tarjoatteko heille rakkautta, vaikka he ovat jo kieltäneet totuuden, joka on tullut heidän osaksensa; ovat karkoittaneet sananjulistajan samoin kuin teidät itsennekin sentähden, että uskotte Jumalaan, Herraanne. Jos lähdette retkelle lujasti taistellen Minun asiani puolesta ja etsien Minun mielisuosiotani, osoitatteko silloin heille ystävyyttä? Minä tiedän, mitä salaatte ja mitä ilmaisette, ja jokainen teistä, joka tekee niin, on eksynyt oikealta tieltä.');
INSERT INTO "finnish" VALUES(60,2,'60:2','Jos he yllättävät teidät, osoittautuvat he teidän vihollisiksenne ja suuntaavat teihin kätensä ja kielensä pahaa tarkoittaen, ja he toivoisivat teidän olevan uskottomia.');
INSERT INTO "finnish" VALUES(60,3,'60:3','Ylösnousemuksen päivänä eivät läheiset suhteenne, eivät edes lapsennekaan, hyödytä teitä vähääkään; ylösnousemuksen päivänä teidät erotetaan toisistanne; Jumala näkee tekonne.');
INSERT INTO "finnish" VALUES(60,4,'60:4','Totisesti on teille oivana esimerkkinä Aabraham ja ne, jotka häneen liittyivät, kun he sanoivat kansalleen: »Meillä ei ole mitään tekemistä teidän ja sen kanssa, jota palvelette Jumalan rinnalla. Me kiellämme teidät, teidän ja meidän välillämme vallitsee viha aina siihen saakka, kunnes uskotte yhteen Jumalaan.» Mutta isälleen sanoi Aabraham: »Totisesti tahdon pyytää puolestasi anteeksiantoa, mutta en voi tehdä mitään hyväksesi Jumalan edessä.» Herra, Sinuun luotamme, Sinun puoleesi käännymme ja Sinun luoksesi palaamme.');
INSERT INTO "finnish" VALUES(60,5,'60:5','Herra, älä aseta meille kiusaukseksi niitä, jotka eivät usko, ja anna meille anteeksi. Herra, totisesti olet kaikkivoipa, viisas.');
INSERT INTO "finnish" VALUES(60,6,'60:6','Totisesti ovat he hyvä esimerkki teille jokaiselle, joka pelkää Jumalaa ja uskoo tulevaan päivään, mutta jos joku kääntyy pois, on Hän, Jumala, totisesti kaikista riippumaton, ylistetty.');
INSERT INTO "finnish" VALUES(60,7,'60:7','Voi käydä niin, että Jumala aikaansaa ystävyyden teidän ja niiden välille, joita luulette vihollisiksenne, sillä Jumala on voimallinen, Jumala on anteeksiantava, armollinen.');
INSERT INTO "finnish" VALUES(60,8,'60:8','Jumala ei kiellä teitä osoittamasta ystävällisyyttä ja oikeamielisyyttä niitä kohtaan, jotka eivät ole taistelleet teitä vastaan uskontonne tähden eivätkä karkoittaneet teitä kodeistanne; totisesti, Jumala rakastaa oikeamielisiä.');
INSERT INTO "finnish" VALUES(60,9,'60:9','Hän vain kieltää teitä olemasta ystävällisiä niille, jotka ovat taistelleet teitä vastaan uskonne tähden, karkoittaneet teidät kodeistanne ja olleet toisille apuna teitä karkoitettaessa. Ne, jotka osoittavat heille ystävällisyyttä, ovat totisesti jumalattomia.');
INSERT INTO "finnish" VALUES(60,10,'60:10','Te uskovaiset, kun oikeauskoisia naisia tulee luoksenne paettuaan kodeistaan, niin tutkikaa heitä. Jumala tuntee heidän uskonsa parhaiten. Jos sitten havaitsette heidät uskoviksi naisiksi, niin älkää lähettäkö heitä takaisin uskottomien luo. He eivät lain mukaan kuulu näille eivätkä nämä heille. Mutta antakaa miehille, mitä he ovat maksaneet. Eikä teissä ole moitteelle varaa, jos naitte heidät, kun olette maksaneet heille huomenlahjan. Älkää myös pitäkö yllä avioliiton siteitä uskottomiin naisiin, vaan vaatikaa takaisin, mitä olette maksaneet, ja uskottomat miehet vaatikoot samoin, mitä he ovat maksuina vaimoistaan suorittaneet. Tämä on Jumalan päätös; Hän ratkaisee asianne, sillä Jumala on kaikkitietävä, viisas.');
INSERT INTO "finnish" VALUES(60,11,'60:11','Jos vaimoistanne joku pakenee uskottomien luokse, niin saalista saatuanne antakaa niille, joiden vaimot ovat karanneet, yhtä paljon kuin he ovat maksuna suorittaneet, ja täyttäkää velvollisuutenne Jumalalle, johon uskotte.');
INSERT INTO "finnish" VALUES(60,12,'60:12','Profeetta, kun uskovat naiset tulevat luoksesi ja lupaavat pyhästi, etteivät aseta mitään Jumalan rinnalle, etteivät varasta, eivät tee aviorikosta, eivät surmaa lapsiaan, eivät keksi valheita eivätkä ole tottelemattomia Sinua kohtaan siinä, mikä on kohtuullista, niin ota vastaan heidän lupauksensa ja ano Jumalalta heille anteeksiantoa; totisesti Jumala on anteeksiantava, armollinen.');
INSERT INTO "finnish" VALUES(60,13,'60:13','Te, jotka uskotte, älkää suosiko ihmisiä, joihin Jumala on vihastunut. Heillä on tulevan elämän toivoa yhtä vähän kuin uskovat kuolleiden ylösnousemukseen.');
INSERT INTO "finnish" VALUES(61,1,'61:1','Kaikki, mitä on taivaassa ja maassa, julistaa Jumalan kunniaa, sillä Jumala on kaikkivoipa, viisas.');
INSERT INTO "finnish" VALUES(61,2,'61:2','Uskovaiset, miksi lupaatte sanoilla sellaista, mitä ette tee?');
INSERT INTO "finnish" VALUES(61,3,'61:3','Jumalan silmissä on inhottavaa se, että lupaatte sellaista, mitä ette tee.');
INSERT INTO "finnish" VALUES(61,4,'61:4','Jumala rakastaa totisesti niitä, jotka taistelevat Hänen retkillään taistelurintamassa, joka on kuin kivimuuri.');
INSERT INTO "finnish" VALUES(61,5,'61:5','Muistakaa, kuinka Mooses sanoi kansalleen: »Miksi kiusaatte minua, vaikka tiedätte, että olen Jumalan teitä varten lähettämä sananjulistaja?» Mutta kun he halusivat poiketa totuudesta, Jumala antoi heidän sydämensä harhautua, sillä Jumala ei ohjaa väärintekijöitä.');
INSERT INTO "finnish" VALUES(61,6,'61:6','Ja kun Jeesus, Marian poika, sanoi: »Israelin lapset, totisesti olen sananjulistaja, jonka Jumala on lähettänyt teille vahvistamaan Toorasta sen, mikä annettiin ennen minua, ja ilmoittamaan ilosanoman sananjulistajasta, joka on tuleva minun jälkeeni ja jonka nimi on oleva Ahmad.» Mutta kun Ahmad tuli heidän luokseen selvin todistein, he sanoivat: »Tämä on ilmeistä noituutta.»');
INSERT INTO "finnish" VALUES(61,7,'61:7','Kuka on jumalattomampi kuin se, joka keksii valheen Jumalaa vastaan juuri silloin, kun häntä kutsutaan islaamiin? Jumala ei kaitse väärintekijöitä.');
INSERT INTO "finnish" VALUES(61,8,'61:8','He tahtovat suullaan puhaltaa sammuksiin Jumalan kynttilän, mutta Jumala tekee valonsa täydelliseksi, vaikka uskottomat ovat sitä vastaan.');
INSERT INTO "finnish" VALUES(61,9,'61:9','Hän on lähettänyt sananjulistajansa tuomaan opastuksen ja totuuden uskonnon, jotta Hän saattaisi sen voittoon kaikkien muiden uskontojen rinnalla, vaikka monijumalaiset ovatkin sitä vastaan.');
INSERT INTO "finnish" VALUES(61,10,'61:10','Te uskovaiset, osoitanko teille tien, jolla voitte pelastua tuskallisesta kurituksesta?');
INSERT INTO "finnish" VALUES(61,11,'61:11','Uskokaa Jumalaan ja Hänen sananjulistajaansa ja asettakaa voimanne, rikkautenne ja henkenne Jumalan asian palvelukseen; näin on teille parasta, jospa sen ymmärtäisitte.');
INSERT INTO "finnish" VALUES(61,12,'61:12','Hän antaa teille syntinne anteeksi ja sallii teidän astua puutarhoihin, joiden kautta joet virtaavat, ja ihaniin asuntoihin autuuden puutarhoissa; tämä on ylin autuus.');
INSERT INTO "finnish" VALUES(61,13,'61:13','Ja vielä toinen asia, joka on teille mieluinen, teitä odottaa: apu Jumalalta ja lähellä oleva voitto. Julista ilosanoma uskovaisille!');
INSERT INTO "finnish" VALUES(61,14,'61:14','Te uskovaiset, tukekaa Jumalan asiaa, niinkuin Jeesus, Marian poika, sanoi opetuslapsilleen: »Ketkä tahtovat olla auttajiani Jumalan asiassa?» Opetuslapset vastasivat: »Me olemme Jumalan auttajia.» Ja osa Israelin lapsista uskoi, mutta osa ei uskonut; silloin Me autoimme uskovia heidän vihollisiaan vastaan, niin että he pääsivät voitolle.');
INSERT INTO "finnish" VALUES(62,1,'62:1','Kaikki, mitä on taivaassa ja maassa, julistaa Jumalan, kuninkaan, pyhimmän, kaikkivoivan, viisaan kunniaa.');
INSERT INTO "finnish" VALUES(62,2,'62:2','Hän on oppimattoman kansan keskuudesta lähettänyt sananjulistajan, joka esittää heille Hänen tunnusmerkkinsä, puhdistaa heidät ja opettaa heille Pyhää kirjaa ja viisautta, vaikka he tätä ennen elivät ilmeisessä harhassa,');
INSERT INTO "finnish" VALUES(62,3,'62:3','ja lähettää sen toisillekin heistä, jotka eivät vielä ole heihin liittyneet; Jumala on kaikkivoipa, viisas.');
INSERT INTO "finnish" VALUES(62,4,'62:4','Tämä on Jumalan armo. Hän antaa sen kenelle tahtoo, sillä Jumala on valtavan armon Herra.');
INSERT INTO "finnish" VALUES(62,5,'62:5','Niiden, joiden vastuulle oli annettu Toora ja jotka eivät sitä noudattaneet, käy kuin aasin, joka kantoi kirjakuormaa. Pahoin käy niille, jotka hylkäävät Jumalan ilmoitukset, sillä Jumala ei kaitse väärintekijöitä.');
INSERT INTO "finnish" VALUES(62,6,'62:6','Sano: »Juutalaiset, jos vakuutatte olevanne Jumalan suosimia ennen muita ihmisiä, niin toivokaa silloin itsellenne kuolemaa, jos totuus on teille kallis.»');
INSERT INTO "finnish" VALUES(62,7,'62:7','Mutta he eivät milloinkaan toivo sitä kättensä tekojen tähden, jotka heitä tilille odottavat; Jumala tuntee hyvin väärintekijät.');
INSERT INTO "finnish" VALUES(62,8,'62:8','Sano: »Kuolema, jota pakenette, on totisesti kohtaava teidät, ja silloin teidät palautetaan Hänen luokseen, joka tuntee näkymättömän ja näkyvän, ja Hän on paljastava teille, mitä olette tehneet.»');
INSERT INTO "finnish" VALUES(62,9,'62:9','Uskovaiset, kun kuuluu kutsu rukoukseen perjantaina, niin kiiruhtakaa palvelemaan Jumalaa ja jättäkää kauppatoimet. Tämä on teille parasta, jos haluatte ymmärtää.');
INSERT INTO "finnish" VALUES(62,10,'62:10','Mutta kun hartaus on päättynyt, hajaantukaa kukin suunnallenne ja etsikää Jumalan armoa pitäen Hänet lujasti mielessänne, jotta menestyisitte.');
INSERT INTO "finnish" VALUES(62,11,'62:11','Mutta kun he näkevät jossakin kauppaa tehtävän tai huviteltavan, he rientävät sinne ja jättävät sinut yksin seisomaan. Sano: »Se, mikä on Jumalasta, on parempi kuin huvi ja kaupanteko: niin, Jumala on paras huoltaja.»');
INSERT INTO "finnish" VALUES(63,1,'63:1','Kun tekopyhät tulevat luoksesi, he sanovat: »Me todistamme, että sinä varmasti olet Jumalan sananjulistaja.» Jumala tietää, että sinä totisesti olet Hänen sananjulistajansa; samoin Jumala todistaa, että tekopyhät totisesti ovat valehtelijoita.');
INSERT INTO "finnish" VALUES(63,2,'63:2','He ottavat valansa verhokseen, harhauttaen muita pois Jumalan tieltä; nurjat ovat totisesti heidän tekonsa.');
INSERT INTO "finnish" VALUES(63,3,'63:3','Sillä he uskoivat, mutta sitten tulivat epäuskoon, ja senvuoksi on heidän sydämensä paatunut, niin että heillä ei ole ymmärrystä.');
INSERT INTO "finnish" VALUES(63,4,'63:4','Mutta kun katselet heitä, miellyttää heidän muotonsa, ja kun he puhuvat, kuuntelet heidän puhettaan; he ovat kuin tukipylväitä ja kuitenkin pelkäävät, että jokainen sotahuuto koskee heitä. He ovat vihollisia; varo siis heitä. Jumalan tuomio tulkoon heidän osakseen! Miksi he ovat pois kääntyneet?');
INSERT INTO "finnish" VALUES(63,5,'63:5','Ja kun heille sanotaan: »Tulkaa, Jumalan sananjulistaja pyytää teille anteeksiantoa», he pudistavat päätänsä, ja sinä näet heidän pöyhkeinä kääntyvän pois.');
INSERT INTO "finnish" VALUES(63,6,'63:6','Heille on yhdentekevää, pyydätkö heille anteeksiantoa vai et. Milloinkaan ei Jumala anna heille anteeksi, totisesti, Jumala ei kaitse väärintekijöitä.');
INSERT INTO "finnish" VALUES(63,7,'63:7','He ovat niitä, jotka sanovat: »Älkää lahjoittako mitään niiden hyväksi, jotka ovat Jumalan sananjulistajan kanssa, ennenkuin he eroavat hänestä.» Jumalan ovat taivaitten ja maan aarteet, mutta teeskentelijät ovat ymmärrystä vailla.');
INSERT INTO "finnish" VALUES(63,8,'63:8','He sanovat: »Jos me palaamme Medinaan, niin mahtavat varmasti karkoittavat sieltä alhaiset.» Jumalalle kuuluu kuitenkin kaikki mahti, samoin kuin Hänen sanansaattajalleen ja uskoville, mutta tekopyhät eivät sitä tiedä.');
INSERT INTO "finnish" VALUES(63,9,'63:9','Te, jotka uskotte! Älkää antako rikkauksienne ja lastenne vieroittaa itseänne jumalanpelosta. Ne, jotka niin tekevät, ovat kadotuksen omia.');
INSERT INTO "finnish" VALUES(63,10,'63:10','Jakakaa keskenänne, mitä olemme teille antanut, ennenkuin kuolema kohtaa jonkun teistä, niin ettei hän sanoisi: »Herra, jospa olisit antanut minulle vielä vähän elinaikaa, niin olisin tahtonut olla armelias ja kuulua niihin, jotka tekevät hyvää.»');
INSERT INTO "finnish" VALUES(63,11,'63:11','Mutta Jumala ei anna armonaikaa kenellekään, jonka hetki on tullut; niin, Jumala tietää, mitä te teette.');
INSERT INTO "finnish" VALUES(64,1,'64:1','Kaikki, mitä on taivaassa ja maan päällä, julistaa Jumalan kunniaa. Hänen on herruus ja Hänelle kuuluu kiitos, sillä Hän voi tehdä kaiken tahtonsa mukaan.');
INSERT INTO "finnish" VALUES(64,2,'64:2','Hän teidät on luonut. Muutamat teistä eivät usko, toiset uskovat. Jumala näkee, mitä teette.');
INSERT INTO "finnish" VALUES(64,3,'64:3','Hän on luonut taivaat ja maan totuudessa; Hän on luonut myös teidät, kaunismuotoisiksi Hän on teidät muovaillut. Hänen tykönsä kaikki myös palaa.');
INSERT INTO "finnish" VALUES(64,4,'64:4','Hän tietää, mitä on taivaassa ja maan päällä; Hän tietää myös, mitä te salaatte ja mitä ilmaisette, sillä Jumala tuntee sydänten kätköt.');
INSERT INTO "finnish" VALUES(64,5,'64:5','Eikö teille ole kerrottu niistä, jotka ennen teitä uskon hylkäsivät? He saivat kokea tekonsa surkeat seuraukset, ja heitä odottaa tuskallinen kuritus.');
INSERT INTO "finnish" VALUES(64,6,'64:6','Sillä heille lähetetyt sananjulistajat tulivat selvin todistein heidän luoksensa, mutta he sanoivat: »Kuolevaisetko meitä johtaisivat?» He olivat epäuskoisia ja kääntyivät pois, mutta Jumala ei heitä kaipaa, sillä Jumala on kaikesta riippumaton, ylistetty.');
INSERT INTO "finnish" VALUES(64,7,'64:7','Epäuskoiset väittävät, ettei heitä milloinkaan herätetä kuolleista. Sano: »Totisesti, Herran nimeen, teidät herätetään kuolleista, ja silloin teille osoitetaan, mitä te olette tehneet, sillä tämä on Jumalalle helppoa.»');
INSERT INTO "finnish" VALUES(64,8,'64:8','Uskokaa sentähden Jumalaan, Hänen sanansaattajaansa ja valoon, jonka Me olemme lähettänyt, sillä Jumala tietää, mitä te teette,');
INSERT INTO "finnish" VALUES(64,9,'64:9','Päivänä, jona hän teidät kokoaa, kokoontumispäivänä, havahdutte harhoistanne. Mutta jokainen, joka uskoo Jumalaan ja tekee hyvää, saa vapautuksen erehdyksistään ja pääsee puutarhoihin, joita virrat vilvoittavat, ja hän saa asua siellä ikuisesti. Tämä on suurin autuus.');
INSERT INTO "finnish" VALUES(64,10,'64:10','Mutta niistä, jotka hylkäävät uskon ja väittävät valheeksi tunnusmerkkimme, tulee ikuisiksi ajoiksi tulen asukkaita. Mikä kurja määränpää heidän vaellukselleen!');
INSERT INTO "finnish" VALUES(64,11,'64:11','Ei mikään koettelemus kohtaa teitä Jumalan tietämättä; ja joka uskoo Jumalaan, hänen sydäntään Hän ohjaa oikeaan, sillä Jumala on kaikkitietävä.');
INSERT INTO "finnish" VALUES(64,12,'64:12','Olkaa kuuliaisia Jumalalle ja kuuliaisia sananjulistajalle, mutta jos käännytte pois, ei sanansaattajanne ole vastuussa muusta kuin sanoman julistamisesta.');
INSERT INTO "finnish" VALUES(64,13,'64:13','Ei ole muuta jumaluutta kuin Hän, Jumala, ja Jumalaan uskovaiset turvautukoot.');
INSERT INTO "finnish" VALUES(64,14,'64:14','Uskovaiset, varmasti on joku vaimoistanne ja lapsistanne vihollisenne; varokaa siis heitä; jos olette armahtavaisia ja kärsivällisiä, silloin totisesti Jumalakin on anteeksiantavainen, armollinen.');
INSERT INTO "finnish" VALUES(64,15,'64:15','Rikkautenne ja lapsenne ovat teille vain koetuksena, mutta Jumalan luona lopullinen palkkio odottaa.');
INSERT INTO "finnish" VALUES(64,16,'64:16','Sentähden täyttäkää velvollisuutenne Jumalaa kohtaan kykynne mukaan, ottakaa vaarin, totelkaa ja uhratkaa varojanne; se koituu sielujenne parhaaksi! Ketkä ikinä varjelevat sielunsa ahneudelta, heillä on menestystä.');
INSERT INTO "finnish" VALUES(64,17,'64:17','Jos omistatte Jumalalle hyvän uhrin, Hän palkitsee sen teille kaksin verroin ja antaa teille anteeksi, sillä Jumala on palkitsevainen, lempeä.');
INSERT INTO "finnish" VALUES(64,18,'64:18','Hän tuntee näkymättömän ja näkyvän, Hän on kaikkivoipa, viisas.');
INSERT INTO "finnish" VALUES(65,1,'65:1','Profeetta, kun eroatte vaimoistanne, erotkaa heistä ensin määrätyksi ajaksi, laskekaa heille määrätty päivien luku ja noudattakaa velvollisuuttanne Jumalaa, Herraanne, kohtaan. Älkää karkoittako heitä kodeistaan! Älkööt he myöskään itse poistuko sieltä muutoin, kuin jos ovat syypäät ilmeiseen riettauteen. Nämä ovat Jumalan säädökset, ja se, joka rikkoo Jumalan säädökset, tekee totisesti väärin omalle sielulleen. Et voi tietää, haluaako Jumala ehkä jälkeenpäin aikaansaada asiassasi käänteen.');
INSERT INTO "finnish" VALUES(65,2,'65:2','Ja kun vaimojenne koeaika on päättynyt, pitäkää heidät luonanne tai erotkaa heistä ystävällisesti. Ja ottakaa keskuudestanne kaksi oikeamielistä todistajaa ja näyttäkää asia toteen Jumalan edessä. Tämä kehoitus on annettu jokaiselle, joka uskoo Jumalaan ja tulevaan päivään. Sille, joka pelkää Jumalaa, Hän ratkaisee pulman');
INSERT INTO "finnish" VALUES(65,3,'65:3','ja antaa hänelle elatuksen sieltä, mistä hän ei voi sitä odottaakaan. Sille, joka Jumalaan luottaa, Hän on kaikki kaikessa. Totisesti Jumala saavuttaa tarkoituksensa; Jumala on todella antanut kaikelle määränsä.');
INSERT INTO "finnish" VALUES(65,4,'65:4','Niille vaimoistanne, joiden kuukautiset ovat keskeytyneet, tai jos olette tästä epävarmoja, olkoon koekautensa, kolme kuukautta, samoin niille, joille kuukautiset eivät ole alkaneet; mutta raskailla vaimoilla tämä aika kestäköön aina siihen asti, kunnes he ovat synnyttäneet. Sille, joka noudattaa Jumalan tahtoa, Hän helposti ratkaisee kaikki pulmat.');
INSERT INTO "finnish" VALUES(65,5,'65:5','Tämä on Jumalan käsky, jonka Hän on ilmaissut teille, ja sen, joka pelkää Jumalaa, Hän vapahtaa pahoista teoista ja suo hänelle suuren palkinnon.');
INSERT INTO "finnish" VALUES(65,6,'65:6','Antakaa eroavien vaimojenne asua siellä, missä itsekin asutte, varojenne mukaan, älkääkä kohdelko heitä kovasti, etteivät he joudu ahdistukseen. Jos he ovat raskaudentilassa, niin suorittakaa heidän elatuskulunsa siksi, kunnes he ovat synnyttäneet. Ja jos he imettävät teille kuuluvaa lasta, teiltä tulkoon siitä heille korvaus. Järjestäkää sovussa nämä asiat; mutta jos joudutte erimielisyyksiin, niin antakaa imettäminen jollekulle toiselle naiselle.');
INSERT INTO "finnish" VALUES(65,7,'65:7','Rikas mies uhratkoon varojensa runsaudesta, ja se, jonka osa on niukempi, jakakoon siitä, mitä Jumala on hänelle antanut; Jumala ei sälytä kenenkään kannettavaksi raskaampaa kuormaa, kuin minkä Hän on hyväksi nähnyt; vaikeuksien jälkeen Jumala suo huojennuksen.');
INSERT INTO "finnish" VALUES(65,8,'65:8','Miten moni kaupunki kapinoitsikaan Herransa ja Hänen sananjulistajansa käskyä vastaan! Siitä syystä Me ankarasti vaadimme sen tilinteolle ja ojensimme sitä kovalla kurituksella.');
INSERT INTO "finnish" VALUES(65,9,'65:9','Se sai kokea tekojensa onnettomat seuraukset, ja kaiken lopuksi se joutui perikatoon.');
INSERT INTO "finnish" VALUES(65,10,'65:10','Jumala on valmistanut heille ankaran kurituksen. Pelätkää siis Jumalaa, te, joilla on ymmärrys ja jotka uskotte! Jumala on toden totta lähettänyt teille muistuttajan,');
INSERT INTO "finnish" VALUES(65,11,'65:11','sananjulistajan, joka ilmoittaa teille Jumalan selvät tunnusmerkit, jotta ne, jotka uskovat ja hyvää tekevät, saisivat johdatuksen pimeydestä valkeuteen. Ken ikinä uskoo Jumalaan ja tekee hyvää, sille Hän avaa pääsyn puutarhoihin, joissa purot virtaavat, ja sallii hänen asua niissä ikuisesti. Jumala on todella valmistanut hänelle ihanan osan.');
INSERT INTO "finnish" VALUES(65,12,'65:12','Jumala on luonut seitsemän taivasta ja yhtä monta maata. Niiden kautta tulee ylhäältä Jumalan käsky, jotta tietäisitte, että Jumalalla on valta kaiken yli ja että Jumala sulkee kaiken tietoonsa.');
INSERT INTO "finnish" VALUES(66,1,'66:1','Profeetta, miksi haluat, ollaksesi vaimoillesi mieliksi, kieltäytyä sellaisesta, minkä Jumala on tehnyt sinulle luvalliseksi? Onhan Jumala anteeksiantava, laupias.');
INSERT INTO "finnish" VALUES(66,2,'66:2','Onhan Jumala antanut määräykset sovitusuhrista, joka valasta vapauttaa ja Jumala on suojelijanne, sillä Hän on kaikkitietävä, viisas.');
INSERT INTO "finnish" VALUES(66,3,'66:3','Kun profeetta salaisuutena ilmoitti jotakin eräälle vaimoistaan, ja kun tämä sitten ilmaisi sen toisille ja Jumala antoi profeetan kaikesta tietää, moitti tämä vaimoansa osasta, mutta vaikeni osasta. Ja kun hän puhui vaimolleen siitä, tämä kysyi: »Kuka on ilmoittanut sinulle tästä?» Hän vastasi: »Hän, joka on kaikkitietävä, on sen minulle ilmoittanut.»');
INSERT INTO "finnish" VALUES(66,4,'66:4','»Jos te kumpikin, (vaimo) käännytte Jumalan puoleen, se on parhaaksenne; ovathan sydämenne Hänestä luopuneet; mutta jos te kumpikin pidätte yhtä puolta häntä (profeettaa) vastaan, niin on totisesti Jumala hänen suojelijansa ja samoin Gabriel sekä hurskaat uskovaiset ja lisäksi enkelit hänen auttajansa.»');
INSERT INTO "finnish" VALUES(66,5,'66:5','Voi olla, että jos profeetta teistä eroaa, hänen Herransa antaa sijaanne parempia vaimoja kuin te - kuuliaisia, uskollisia, nöyriä, katuvia, Jumalaa palvelevia, paastojen noudattajia, olivatpa he leskiä tai neitsyitä.');
INSERT INTO "finnish" VALUES(66,6,'66:6','Uskovaiset, varjelkaa itsenne ja perheenne tulelta, joka kuluttaa ihmiset ja kivetkin, jota valvomaan on asetettu enkeleitä, heltymättömiä ja mahtavia; he eivät ole tottelemattomia Jumalan käskyille, vaan tekevät mitä heille on määrätty.');
INSERT INTO "finnish" VALUES(66,7,'66:7','Te, jotka ette usko, älkää keksikö anteeksipyyntöjä tuona päivänä! Te saatte vain tekojenne palkan.');
INSERT INTO "finnish" VALUES(66,8,'66:8','Uskovaiset, kääntykää vilpittömästi katuen Jumalan puoleen! Ehkä Herranne vapahtaa teidät pahuudestanne ja sallii teidän astua puutarhoihin, joita virrat vilvoittavat, sinä päivänä, jolloin Jumala ei saata häpeään Profeettaa eikä niitä, jotka uskovat hänen kanssaan; heidän valonsa välkkyy heidän edessään ja oikealla puolellaan, ja he sanovat: »Herra, viritä valomme täydelliseksi ja suojele meitä armossasi, sillä sinä voit totisesti tehdä kaiken, mitä tahdot!»');
INSERT INTO "finnish" VALUES(66,9,'66:9','Profeetta, taistele sitkeästi epäuskoisia ja tekopyhiä vastaan ja ole ankara heitä kohtaan; heidän olinpaikkansa on oleva helvetti. Kurja on moinen määränpää.');
INSERT INTO "finnish" VALUES(66,10,'66:10','Niitä varten, jotka eivät usko, Jumala on asettanut Nooan ja Lootin vaimot esimerkiksi. Nämä kuuluivat kahdelle hurskaalle miehelle palveliioittemme joukosta, mutta pettivät miehiään, ja siksi ei mikään voinut auttaa heitä Jumalaa vastaan, vaan heille sanottiin: »Astukaa tuleen kaikkien tuomittujen kanssa!»');
INSERT INTO "finnish" VALUES(66,11,'66:11','Niitä varten, jotka uskovat, Jumala on asettanut esimerkiksi myös faraon vaimon, joka sanoi: »Herra rakenna minulle huone luoksesi paratiisiin, vapahda minut faraosta ja hänen teoistaan ja päästä minut jumalattomista ihmisistä!»');
INSERT INTO "finnish" VALUES(66,12,'66:12','Esikuvaksi tehtiin myös Maria, Imraanin tytär, joka säilytti puhtautensa ja jonka Me täytimme hengellämme. Hän uskoi Herran ja Hänen Pyhän kirjansa sanoihin, sillä hän kuului nöyrien joukkoon.');
INSERT INTO "finnish" VALUES(67,1,'67:1','Siunattu on Hän, jonka käsissä on valtakunta ja voima yli kaiken olevaisen!');
INSERT INTO "finnish" VALUES(67,2,'67:2','Hän, joka loi elämän ja kuoleman koetellakseen, kuka teistä osoittautuu parhaaksi teoissa. Hän on mahtava, laupias.');
INSERT INTO "finnish" VALUES(67,3,'67:3','Hän on luonut seitsemän taivasta toinen toisensa yläpuolelle. Et voi havaita mitään vikaa Armahtajan luomistyössä. Katsele ympärillesi: voitko huomata mitään vajavaisuutta?');
INSERT INTO "finnish" VALUES(67,4,'67:4','Käänny ylt''ympäri ja katsele yhä edelleen, ja katseesi palaa lähtökohtaan pettyneenä ja väsyneenä.');
INSERT INTO "finnish" VALUES(67,5,'67:5','Olemmehan koristanut koko alimman taivaan valoilla, joista on tullut aseita paholaisia vastaan, näille Me olemme valmistanut helvetin tulen tuskat.');
INSERT INTO "finnish" VALUES(67,6,'67:6','Niitä, jotka eivät usko Herraansa, odottaa helvetin tuska; kurja on tuo määränpää.');
INSERT INTO "finnish" VALUES(67,7,'67:7','Kun heidät heitetään sinne, he kuulevat sen pauhun, kun se kiehuu.');
INSERT INTO "finnish" VALUES(67,8,'67:8','Se on raivoonsa pakahtumaisillaan. Joka kerta, kun sinne heitetään joukko, sen vartijat kysyvät näiltä: »Eikö varoittaja aikanaan tullut luoksenne?»');
INSERT INTO "finnish" VALUES(67,9,'67:9','Ja he vastaavat: »Kyllä, varoittaja tuli totisesti luoksemme, mutta me torjuimme hänet ja sanoimme: ''Jumala ei ole sinulle ilmoitustaan antanut, olet suuressa harhassa.''»');
INSERT INTO "finnish" VALUES(67,10,'67:10','Ja he sanovat: »Jos olisimme ottaneet vaarin ja miettineet, emme olisi joutuneet helvetin asukkaiden joukkoon.»');
INSERT INTO "finnish" VALUES(67,11,'67:11','Niin he tunnustavat syntinsä, mutta etäälle armosta jäävät helvetin tulen asukkaat.');
INSERT INTO "finnish" VALUES(67,12,'67:12','Niitä varten, jotka salassakin pelkäävät ja kunnioittavat Herraansa, on anteeksiantamus ja suuri palkinto.');
INSERT INTO "finnish" VALUES(67,13,'67:13','Joko salaatte sanottavanne tai lausutte sen julki, kyllä Herra varmasti tuntee mielenne.');
INSERT INTO "finnish" VALUES(67,14,'67:14','Eikö Hän sitä tietäisi, joka kaiken loi? Hänhän vähimmänkin havaitsee ja kaiken tajuaa.');
INSERT INTO "finnish" VALUES(67,15,'67:15','Hän on luonut maan alamaiseksenne, jotta voisitte kävellä sen teillä ja syödä sen antimia. Hänen luokseen johtaa ylösnousemus.');
INSERT INTO "finnish" VALUES(67,16,'67:16','Oletteko varmat siitä, ettei Hän, joka on yläpuolellanne, anna maan niellä teitä? Katso, se jo vavahtelee!');
INSERT INTO "finnish" VALUES(67,17,'67:17','Tai oletteko varmat siitä, ettei Hän, joka on teidän yläpuolellanne, lähetä päällenne hirmumyrskyä? Saatte kokea, millainen oli minun varoitukseni.');
INSERT INTO "finnish" VALUES(67,18,'67:18','On kyllä ollut niitä, jotka ennen heitä ovat väittäneet totuutta valheeksi, mutta millainen olikaan kuritukseni!');
INSERT INTO "finnish" VALUES(67,19,'67:19','Eivätkö he näe, miten linnut heidän yläpuolellaan levittävät ja kokoovat siipensä? Kuka muu kuin Armahtaja pitää niitä koholla? Hän totisesti näkee kaiken.');
INSERT INTO "finnish" VALUES(67,20,'67:20','Kuka muu kuin Armahtaja voi teitä auttaa niinkuin suuri sotajoukko? Epäuskoiset ovat sokeita.');
INSERT INTO "finnish" VALUES(67,21,'67:21','Onko ketään, joka teitä ravitsisi, jos Hän ei antaisi teille elatusta? Mutta he vain jatkavat kapinoimistaan ja vastusteluaan.');
INSERT INTO "finnish" VALUES(67,22,'67:22','Kumpi voi paremmin seurata johdatusta, sekö, joka ryömii kasvot maata vasten, vai se, joka etenee oikeata tietä pystyssä päin?');
INSERT INTO "finnish" VALUES(67,23,'67:23','Sano: »Hän on teidät luonut ja antanut teille näön, kuulon ja sydämen. Vähäinen on teidän kiitollisuutenne.»');
INSERT INTO "finnish" VALUES(67,24,'67:24','Sano: »Hän on teidät levittänyt aina maan ääriin asti, ja Hänen luokseen teidät kootaan yhteen.»');
INSERT INTO "finnish" VALUES(67,25,'67:25','He sanovat myös: »Milloin on tämä lupaus täyttyvä, jos puheesi on totta?»');
INSERT INTO "finnish" VALUES(67,26,'67:26','Sano: »Sen tietää Jumala yksin, minä olen vain julkinen varoittaja.»');
INSERT INTO "finnish" VALUES(67,27,'67:27','Mutta kun he näkevät tuhon olevan lähellä, synkkenevät niiden kasvot, jatka eivät usko, ja kuuluu sana: »Tämä on se, mitä te halusitte.»');
INSERT INTO "finnish" VALUES(67,28,'67:28','Sano: »Mitä ajattelette? Vaikkapa Jumala tuhoaisi minut ja ne, jotka ovat minun kanssani, tai armahtaisi meidät, kukapa silloinkaan pelastaisi epäuskoiset tuomion kurituksesta?»');
INSERT INTO "finnish" VALUES(67,29,'67:29','Sano: »Hän on Armahtaja, Me uskomme Häneen ja Häneen Me luotamme; pian saatte tietää, kuka on ilmeisessä harhassa.»');
INSERT INTO "finnish" VALUES(67,30,'67:30','Sano: »Mitä ajattelisitte, jos jonakin aamuna vetenne valuisi kuiviin? Kuka teille silloin voisi hankkia kirkasta, juoksevaa vettä?»');
INSERT INTO "finnish" VALUES(68,1,'68:1','Kynän ja sen nimessä, mitä kirjoitetaan!');
INSERT INTO "finnish" VALUES(68,2,'68:2','Sinä et ole, Herrasi armosta, mikään hourailija,');
INSERT INTO "finnish" VALUES(68,3,'68:3','vaan totisesti odottaa Sinua ehtymätön palkka,');
INSERT INTO "finnish" VALUES(68,4,'68:4','ja totisesti on siveellinen mielenlaatusi jalo.');
INSERT INTO "finnish" VALUES(68,5,'68:5','Pian olet näkevä, ja myöskin he saavat nähdä,');
INSERT INTO "finnish" VALUES(68,6,'68:6','ketä teistä riivaa hulluus.');
INSERT INTO "finnish" VALUES(68,7,'68:7','Totisesti tietää Herrasi parhaiten, kuka poikkeaa Hänen tieltään, ja Hän tietää parhaiten, kuka noudattaa johdatusta.');
INSERT INTO "finnish" VALUES(68,8,'68:8','Älä suostu siis niihin, jotka julistavat totuuden valheeksi!');
INSERT INTO "finnish" VALUES(68,9,'68:9','He toivovat sinun osoittavan velttoutta, jotta myös he voisivat olla velttoja.');
INSERT INTO "finnish" VALUES(68,10,'68:10','Älä tottele ainoatakaan halpamaista vannojaa,');
INSERT INTO "finnish" VALUES(68,11,'68:11','panettelijaa, joka kulkee ympäri pahaa puhuen,');
INSERT INTO "finnish" VALUES(68,12,'68:12','joka kieltää hyvän, rikkoo lakia ja tekee syntiä,');
INSERT INTO "finnish" VALUES(68,13,'68:13','joka lisäksi on alhaissyntyinen, röyhkeä ja ahne,');
INSERT INTO "finnish" VALUES(68,14,'68:14','siksi, että hänellä on rikkautta ja poikia!');
INSERT INTO "finnish" VALUES(68,15,'68:15','Kun tunnusmerkkimme luetellaan hänelle, hän sanoo: »Muinaisjuttuja.»');
INSERT INTO "finnish" VALUES(68,16,'68:16','Me painamme pian poltinmerkkimme hänen nenäkkäisiin kasvoihinsa.');
INSERT INTO "finnish" VALUES(68,17,'68:17','Totisesti koettelemme heitä, niinkuin koettelimme puutarhan omistajia, kun he vannoivat korjaavansa sadon seuraavana päivänä,');
INSERT INTO "finnish" VALUES(68,18,'68:18','mutta eivät varanneet mitään osuutta (köyhille).');
INSERT INTO "finnish" VALUES(68,19,'68:19','Silloin Herrasi lähettämä koettelemus kohtasi puutarhaa heidän nukkuessaan,');
INSERT INTO "finnish" VALUES(68,20,'68:20','niin että se oli musta kuin palon jäljiltä.');
INSERT INTO "finnish" VALUES(68,21,'68:21','Mutta aamulla kutsuivat he toisiaan');
INSERT INTO "finnish" VALUES(68,22,'68:22','sanoen: »Kunkin on käytävä varhain pellolleen, jos mieli korjata sato!»');
INSERT INTO "finnish" VALUES(68,23,'68:23','Ja he lähtivät puhellen hiljaa keskenään:');
INSERT INTO "finnish" VALUES(68,24,'68:24','»Älkää tänään salliko köyhien tulla jälkikeräykseen.»');
INSERT INTO "finnish" VALUES(68,25,'68:25','Ja he lähtivät matkaan aamulla tämä itaruus mielessään,');
INSERT INTO "finnish" VALUES(68,26,'68:26','mutta kun he näkivät mitä oli tapahtunut, he sanoivat: »Varmaankin olemme joutuneet tieltä harhaan.');
INSERT INTO "finnish" VALUES(68,27,'68:27','Ei, meille tuli kato!»');
INSERT INTO "finnish" VALUES(68,28,'68:28','Viisain heistä sanoi: »Enkö sanonut teille: ''Ylistäkää Jumalaa.''»');
INSERT INTO "finnish" VALUES(68,29,'68:29','He vastasivat: »Kunnia Herralle! Totisesti olimme jumalattomia.»');
INSERT INTO "finnish" VALUES(68,30,'68:30','Ja sitten jotkut heistä kääntyivät päivitellen toistensa puoleen');
INSERT INTO "finnish" VALUES(68,31,'68:31','ja sanoivat: »Voi meitä! Olemme totisesti rikkoneet;');
INSERT INTO "finnish" VALUES(68,32,'68:32','ehkä Herra kuitenkin antaa meille jotakin parempaa tämän asemesta; totisesti tahdomme sitä Herraltamme anoa.»');
INSERT INTO "finnish" VALUES(68,33,'68:33','Sellainen oli rangaistus, mutta tulevan elämän rangaistus on totisesti oleva vielä suurempi. Jospa he sen käsittäisivät!');
INSERT INTO "finnish" VALUES(68,34,'68:34','Niille, jotka karttavat pahaa, on totisesti valmistettu autuuden puutarhat Herransa luona.');
INSERT INTO "finnish" VALUES(68,35,'68:35','Onko Meidän kohdeltava Jumalan alamaisia kuten syyllisiä?');
INSERT INTO "finnish" VALUES(68,36,'68:36','Miten voitte tuomita siten?');
INSERT INTO "finnish" VALUES(68,37,'68:37','Vai onko teillä kirja, josta voitte lukea,');
INSERT INTO "finnish" VALUES(68,38,'68:38','että teillä todella on siinä se, mitä pidätte parhaana?');
INSERT INTO "finnish" VALUES(68,39,'68:39','Vai onko teillä Meidän antamamme valalliset vakuutukset, jotka ovat voimassa myös ylösnousemuksen päivänä, että varmasti saatte mitä kuvittelette?');
INSERT INTO "finnish" VALUES(68,40,'68:40','Kysy heiltä, Muhammed, kuka heistä uskaltaa mennä tästä takaukseen.');
INSERT INTO "finnish" VALUES(68,41,'68:41','Vai onko heillä liittolaisia? Anna heidän näyttää liittolaisensa, jos he tarkoittavat totta.');
INSERT INTO "finnish" VALUES(68,42,'68:42','Päivänä, jolloin he ovat suuressa ahdistuksessa, jolloin heidät kutsutaan rukoukseen lankeamaan, mutta eivät kykene sitä tekemään,');
INSERT INTO "finnish" VALUES(68,43,'68:43','he seisovat katse maahan luotuina ja kasvot häpeää uhkuen,koska heidät kutsuttiin polvistumaan heidän ollessaan parhaissa voimissaan.');
INSERT INTO "finnish" VALUES(68,44,'68:44','Siispä jätä Minun pideltävikseni ne, jotka väittävät valheeksi näitä sanomia. Me johdamme heitä askel askeleelta heidän huomaamattaan,');
INSERT INTO "finnish" VALUES(68,45,'68:45','ja Minä annan heille aikaa; onhan totisesti suunnitelmani järkähtämätön.');
INSERT INTO "finnish" VALUES(68,46,'68:46','Vai vaaditko, Muhammed, palkkaa heiltä, niin että he velasta rasittuvat?');
INSERT INTO "finnish" VALUES(68,47,'68:47','Vai onko heillä tieto salatusta, niin että he voivat kirjoittaa sen muistiin?');
INSERT INTO "finnish" VALUES(68,48,'68:48','Odota sentähden järkkymättömänä Herrasi tuomiota äläkä ole niinkuin kalan nielemä mies, joka huusi murheen vallassa.');
INSERT INTO "finnish" VALUES(68,49,'68:49','Ellei hänen Herransa armo olisi tullut hänen osakseen, hän olisi hylättynä varmasti sinkoutunut jollekin autiolle rannalle,');
INSERT INTO "finnish" VALUES(68,50,'68:50','mutta Hänen Herransa valitsi hänet ja asetti hänet vanhurskaitten joukkoon.');
INSERT INTO "finnish" VALUES(68,51,'68:51','Ne, jotka ovat vaipuneet epäuskoon, olisivat katseillaan sinut lävistäneet, kuullessaan kehoituksesi, he sanoivat: »Totisesti, hän on järjiltään.»');
INSERT INTO "finnish" VALUES(68,52,'68:52','Mutta Koraani on vain muistutus koko maailmalle.');
INSERT INTO "finnish" VALUES(69,1,'69:1','Varma tuho!');
INSERT INTO "finnish" VALUES(69,2,'69:2','Mikä on varma tuho?');
INSERT INTO "finnish" VALUES(69,3,'69:3','Ja miten saisi sinut käsittämään, mikä varma tuho on?');
INSERT INTO "finnish" VALUES(69,4,'69:4','Tamuud ja Aad väittivät, ettei tuomiopäivää tule.');
INSERT INTO "finnish" VALUES(69,5,'69:5','Tamuudin kansan tuhosi ärjyvä ukonilma,');
INSERT INTO "finnish" VALUES(69,6,'69:6','Aadin tuhosi raivoisa tuuli, joka puhalsi tavattomalla voimalla;');
INSERT INTO "finnish" VALUES(69,7,'69:7','se raivosi heidän keskuudessaan seitsemän yötä ja seitsemän päivää suurella voimalla, niin että olisit voinut nähdä ihmisten kaatuvan maahan kuin palmujen ontot rungot.');
INSERT INTO "finnish" VALUES(69,8,'69:8','Näetkö heistä jälkeäkään?');
INSERT INTO "finnish" VALUES(69,9,'69:9','Farao ja ne, jotka elivät ennen häntä, sekä hävitetyt kaupungit tekivät myös syntiä');
INSERT INTO "finnish" VALUES(69,10,'69:10','ollen tottelemattomia Herransa sananjulistajalle; siksi Hän kuritti heitä väkevin ottein.');
INSERT INTO "finnish" VALUES(69,11,'69:11','Kun vesi tulvi, Me totisesti annoimme laivan kantaa teitä,');
INSERT INTO "finnish" VALUES(69,12,'69:12','teille ojennukseksi ja jotta kuunteleva korva vaarin ottaisi ja muistaisi sen.');
INSERT INTO "finnish" VALUES(69,13,'69:13','Kun, puhalletaan yhden ainoan kerran pasuunaan');
INSERT INTO "finnish" VALUES(69,14,'69:14','ja maa sekä vuoret kohoavat ja sitten yht''äkkiä vaipuvat murskattuina alas,');
INSERT INTO "finnish" VALUES(69,15,'69:15','sinä päivänä on suuri tuho tapahtuva,');
INSERT INTO "finnish" VALUES(69,16,'69:16','taivas repeää; sinä päivänä se syöksyy alas.');
INSERT INTO "finnish" VALUES(69,17,'69:17','Enkeleitä on oleva ylt''ympäri, ja kahdeksan heistä kantaa yläpuolellaan Herrasi valtaistuinta.');
INSERT INTO "finnish" VALUES(69,18,'69:18','Sinä päivänä teidät tuodaan oikeuden eteen, ja yksikään salaisuutenne ei voi jäädä kätköön.');
INSERT INTO "finnish" VALUES(69,19,'69:19','Se, jolle kirja on annettu hänen oikeaan käteensä, sanoo: »Tulkaa ja lukekaa kirjani!');
INSERT INTO "finnish" VALUES(69,20,'69:20','Totisesti uskon, että koittaa tilinteon päivä.»');
INSERT INTO "finnish" VALUES(69,21,'69:21','Ja hän on saava mieluisan elämän,');
INSERT INTO "finnish" VALUES(69,22,'69:22','ihanassa puutarhassa,');
INSERT INTO "finnish" VALUES(69,23,'69:23','jossa hedelmät ovat käden ulottuvilla.');
INSERT INTO "finnish" VALUES(69,24,'69:24','»Syökää ja juokaa mielenne mukaan palkaksi siitä, mitä olette tehneet menneinä päivinä!»');
INSERT INTO "finnish" VALUES(69,25,'69:25','Ja hän, jolle kirja on annettu vasempaan käteen, sanoo: »Oi, jospa en olisi kirjaani milloinkaan saanut,');
INSERT INTO "finnish" VALUES(69,26,'69:26','ja jospa en olisi koskaan joutunut kokemaan, mitä tilinteko on!');
INSERT INTO "finnish" VALUES(69,27,'69:27','Oi, jospa kuolema olisi ollut kaiken loppu!');
INSERT INTO "finnish" VALUES(69,28,'69:28','Rikkauteni ei minua lainkaan hyödytä,');
INSERT INTO "finnish" VALUES(69,29,'69:29','valtani on minulta häipynyt.»');
INSERT INTO "finnish" VALUES(69,30,'69:30','»Ottakaa hänet, sitokaa hänet');
INSERT INTO "finnish" VALUES(69,31,'69:31','ja heittäkää sitten helvettiin!');
INSERT INTO "finnish" VALUES(69,32,'69:32','Kytkekää hänet kahleisiin, joiden pituus on seitsemänkymmentä kyynärää!');
INSERT INTO "finnish" VALUES(69,33,'69:33','Totisesti, hän ei uskonut Jumalaan, suureen,');
INSERT INTO "finnish" VALUES(69,34,'69:34','eikä kehoittanut ravitsemaan köyhiä;');
INSERT INTO "finnish" VALUES(69,35,'69:35','sentähden hänellä ei ole yhtään ystävää tuona päivänä');
INSERT INTO "finnish" VALUES(69,36,'69:36','eikä muuta ruokaa kuin likavesi,');
INSERT INTO "finnish" VALUES(69,37,'69:37','jota vain jumalattomat nielevät.»');
INSERT INTO "finnish" VALUES(69,38,'69:38','Mutta ei! Vannon kaiken sen nimessä, mitä näette');
INSERT INTO "finnish" VALUES(69,39,'69:39','ja mitä eivät silmänne näe:');
INSERT INTO "finnish" VALUES(69,40,'69:40','Totisesti, se on kunnioitettavan sananjulistajan sana');
INSERT INTO "finnish" VALUES(69,41,'69:41','eikä mitään runoilijan puhetta - vähän te uskotte -');
INSERT INTO "finnish" VALUES(69,42,'69:42','ei se liioin ole mitään ennustelijan puhetta - vähän te käsitätte -');
INSERT INTO "finnish" VALUES(69,43,'69:43','se on ilmoitus Jumalalta, maailman Herralta.');
INSERT INTO "finnish" VALUES(69,44,'69:44','Ja jos profeetta olisi sepitellyt runoja Meistä,');
INSERT INTO "finnish" VALUES(69,45,'69:45','olisimme totisesti tarttunut häntä oikeaan käteen');
INSERT INTO "finnish" VALUES(69,46,'69:46','ja katkaissut häneltä suuren valtasuonen,');
INSERT INTO "finnish" VALUES(69,47,'69:47','teidän kenenkään voimatta Meitä siitä estää.');
INSERT INTO "finnish" VALUES(69,48,'69:48','Tämä on totisesti muistutus niille, jotka karttavat pahaa.');
INSERT INTO "finnish" VALUES(69,49,'69:49','Ja Me totisesti tiedämme, että joukossanne on sellaisia, jotka julistavat totuuden valheeksi,');
INSERT INTO "finnish" VALUES(69,50,'69:50','ja totisesti tuleva tuomio on surkea asia epäuskoisille,');
INSERT INTO "finnish" VALUES(69,51,'69:51','mutta se on totisesti varma todellisuus.');
INSERT INTO "finnish" VALUES(69,52,'69:52','Ylistä siis Herrasi, suuren, nimeä!');
INSERT INTO "finnish" VALUES(70,1,'70:1','Eräs tiedonhaluinen kyseli, millainen on kuritus, joka on tuleva');
INSERT INTO "finnish" VALUES(70,2,'70:2','epäuskoisten osaksi. Ei kukaan voi sitä estää.');
INSERT INTO "finnish" VALUES(70,3,'70:3','Se tulee Jumalalta, nousevain portaitten Herralta.');
INSERT INTO "finnish" VALUES(70,4,'70:4','Enkelit ja henki nousevat Hänen tykönsä päivänä, jonka pituus on viisikymmentä tuhatta vuotta.');
INSERT INTO "finnish" VALUES(70,5,'70:5','Osoita siis jaloa kestävyyttä!');
INSERT INTO "finnish" VALUES(70,6,'70:6','He kuvittelevat sen olevan kaukana,');
INSERT INTO "finnish" VALUES(70,7,'70:7','mutta Me näemme sen läheltä.');
INSERT INTO "finnish" VALUES(70,8,'70:8','Sinä päivänä, jolloin taivas on oleva kuin sulaa kuparia');
INSERT INTO "finnish" VALUES(70,9,'70:9','ja vuoret kuin villaa,');
INSERT INTO "finnish" VALUES(70,10,'70:10','jolloin ystävä ei kysy ystävää,');
INSERT INTO "finnish" VALUES(70,11,'70:11','vaikka he näkevät toisensa. Syyllinen toivoisi tuona päivänä vapautuvansa vaivasta uhraamalla lapsensa,');
INSERT INTO "finnish" VALUES(70,12,'70:12','vaimonsa ja veljensä,');
INSERT INTO "finnish" VALUES(70,13,'70:13','sukulaisensa, jotka antoivat hänelle suojaa,');
INSERT INTO "finnish" VALUES(70,14,'70:14','jopa kaikki, jotka maan päälle on luotu, kunhan hän niiden avulla lunastaisi itsensä.');
INSERT INTO "finnish" VALUES(70,15,'70:15','Ei mitenkään! Sillä katso, tämä on leimuava liekki,');
INSERT INTO "finnish" VALUES(70,16,'70:16','joka raastaa pois ihon');
INSERT INTO "finnish" VALUES(70,17,'70:17','ja vaatii omakseen sen, joka kääntyi pois ja pakeni');
INSERT INTO "finnish" VALUES(70,18,'70:18','ja joka ahnaasti kokosi rikkautta.');
INSERT INTO "finnish" VALUES(70,19,'70:19','Totisesti on ihminen luotu epävakaiseksi mieleltään:');
INSERT INTO "finnish" VALUES(70,20,'70:20','Kun jokin paha kohtaa häntä, hän on kärkäs valittamaan,');
INSERT INTO "finnish" VALUES(70,21,'70:21','mutta kun jotakin hyvää annetaan hänen osalleen, hän on kiittämätön,');
INSERT INTO "finnish" VALUES(70,22,'70:22','lukuunottomatta niitä, jotka rukoilevat,');
INSERT INTO "finnish" VALUES(70,23,'70:23','jotka uskollisesti suorittavat rukouksensa,');
INSERT INTO "finnish" VALUES(70,24,'70:24','niitä, joiden rikkaudessa on määrätty osa');
INSERT INTO "finnish" VALUES(70,25,'70:25','kerjäläistä ja osatonta varten;');
INSERT INTO "finnish" VALUES(70,26,'70:26','niitä, jotka uskovat tuomion päivään,');
INSERT INTO "finnish" VALUES(70,27,'70:27','ja jotka pelkäävät Herransa kuritusta');
INSERT INTO "finnish" VALUES(70,28,'70:28','ei kukaan, totisesti, saata säästyä kuritukselta, joka on heidän Herraltansa tuleva,');
INSERT INTO "finnish" VALUES(70,29,'70:29','sekä lukuunottamatta niitä, jotka hillitsevät halujaan');
INSERT INTO "finnish" VALUES(70,30,'70:30','paitsi vaimojensa ja orjattariensa suhteen, koska heissä silloin ei ole moittimista.');
INSERT INTO "finnish" VALUES(70,31,'70:31','Toisin on niiden, jotka pyrkivät ylittämään tämän, sillä ne ovat lain rikkojia.');
INSERT INTO "finnish" VALUES(70,32,'70:32','Ne, jotka ovat rehellisiä sopimuksissaan ja siinä, mitä heidän huostaansa on uskottu,');
INSERT INTO "finnish" VALUES(70,33,'70:33','jotka ovat vilpittömiä todistaessaan');
INSERT INTO "finnish" VALUES(70,34,'70:34','ja jotka ahkeroivat hartauden harjoituksissa,');
INSERT INTO "finnish" VALUES(70,35,'70:35','saavat elää paratiisin kunniassa.');
INSERT INTO "finnish" VALUES(70,36,'70:36','Mutta mitä ajattelevat uskottomat, jotka avosilmin rientävät sinun edelläsi,');
INSERT INTO "finnish" VALUES(70,37,'70:37','oikealta ja vasemmalta, ryhmä toisensa jälkeen?');
INSERT INTO "finnish" VALUES(70,38,'70:38','Kuvitteleeko heistä kukin pääsevänsä autuuden puutarhaan?');
INSERT INTO "finnish" VALUES(70,39,'70:39','Totisesti ei! Totisesti he tietävät, mistä Me olemme luonut heidät.');
INSERT INTO "finnish" VALUES(70,40,'70:40','Mutta ei! Vannon idän ja lännen Herran nimessä: »Totisesti Me voimme');
INSERT INTO "finnish" VALUES(70,41,'70:41','nostaa heidän sijalleen ne, jotka ovat heitä paremmat, eikä Meitä voi tästä mikään estää.');
INSERT INTO "finnish" VALUES(70,42,'70:42','Anna siis heidän jäädä turhien puheittensa varaan ja laskea leikkiä, kunnes heitä katsoo silmästä silmään se päivä, joka on heille ennustettu.');
INSERT INTO "finnish" VALUES(70,43,'70:43','Sinä päivänä he tulevat esiin haudoistaan kiirehtien, aivan kuin maalia kohti kiitäen,');
INSERT INTO "finnish" VALUES(70,44,'70:44','silmät maahan luotuina, häpeän valtaamina. Tämä on se päivä, joka heille ennustettiin.');
INSERT INTO "finnish" VALUES(71,1,'71:1','Totisesti Me lähetimme Nooan oman kansansa eteen sanoen: »Varoita kansaasi, ennenkuin tuskallinen rangaistus sen yllättää!»');
INSERT INTO "finnish" VALUES(71,2,'71:2','Hän julisti: »Kansa, totisesti olen julkinen varoittajasi.');
INSERT INTO "finnish" VALUES(71,3,'71:3','Palvelkaa Jumalaa, tarkoin täyttäen velvollisuutenne Häntä kohtaan, ja olkaa kuuliaisia minulle,');
INSERT INTO "finnish" VALUES(71,4,'71:4','niin Hän antaa teille syntinne anteeksi ja myöntää teille armonaikaa. Mutta totisesti, kun Jumalan määräaika täyttyy, ei sitä voida pitkittää. Kunpa sen uskoisitte!»');
INSERT INTO "finnish" VALUES(71,5,'71:5','Hän sanoi: »Herra, katso, olen vedonnut kansaani päivin ja öin,');
INSERT INTO "finnish" VALUES(71,6,'71:6','mutta saarnani on heissä vain lisännyt vastahakoisuutta.');
INSERT INTO "finnish" VALUES(71,7,'71:7','Ja katso, vaikka kuinka usein olisin heitä kutsunut, jotta Sinä voisit antaa heille anteeksi, tukkivat he sormin korvansa ja kietoutuvat vaatteisiinsa torjuen kaiken itsepintaisesti ja paisuen ylpeydessään.');
INSERT INTO "finnish" VALUES(71,8,'71:8','Silloin totisesti saarnasin heille äänekkäästi');
INSERT INTO "finnish" VALUES(71,9,'71:9','huusin kaikkien kuullen, haastoinpa heille myös kahden kesken');
INSERT INTO "finnish" VALUES(71,10,'71:10','sanoen: ''Pyytäkää Herraltanne anteeksiantoa - Hän on totisesti armahtavainen -');
INSERT INTO "finnish" VALUES(71,11,'71:11','Hän lähettää päällenne pilvet ja runsaan sateen,');
INSERT INTO "finnish" VALUES(71,12,'71:12','enentää teille rikkautta ja lapsia sekä luo teitä varten paratiisin virtoineen.');
INSERT INTO "finnish" VALUES(71,13,'71:13','Miksi ette pyydä armolahjoja Jumalalta?');
INSERT INTO "finnish" VALUES(71,14,'71:14','Totisesti, Hänhän on luonut teidät eri vaiheiden kautta.');
INSERT INTO "finnish" VALUES(71,15,'71:15','Ettekö näe, kuinka Jumala on luonut seitsemän taivasta toinen toisensa yläpuolelle?');
INSERT INTO "finnish" VALUES(71,16,'71:16','Hän on myös asettanut niille kuun valoksi ja auringon lampuksi.');
INSERT INTO "finnish" VALUES(71,17,'71:17','Jumala on kasvattanut teidät maasta,');
INSERT INTO "finnish" VALUES(71,18,'71:18','Hän käskee teidän myöskin palata siihen ja antaa teille ylösnousemuksen.');
INSERT INTO "finnish" VALUES(71,19,'71:19','Teitä varten Jumala on levittänyt maan ääret');
INSERT INTO "finnish" VALUES(71,20,'71:20','vaeltaaksenne sen avarilla teillä.''»');
INSERT INTO "finnish" VALUES(71,21,'71:21','Nooa sanoi: »Herra, totisesti he ovat olleet minua kohtaan uppiniskaisia ja pitäneet niitä esikuvinaan, joiden rikkaus ja lapset vain lisäävät heidän turmelustaan.');
INSERT INTO "finnish" VALUES(71,22,'71:22','He ovat punoneet monia juonia');
INSERT INTO "finnish" VALUES(71,23,'71:23','ja sanovat: ''Älkää suinkaan hylätkö jumalianne, Waddia ja Suwaa, Jaguutia, Jaukia ja Nasria!''');
INSERT INTO "finnish" VALUES(71,24,'71:24','Näin he ovat totisesti johtaneet monia harhaan, ja villitys käy yhä suuremmaksi jumalattomien keskuudessa.»');
INSERT INTO "finnish" VALUES(71,25,'71:25','Heidät hukutettiin vääryyksiensä tähden, ja heidän oli käytävä tuleen, eikä heillä ollut muita auttajia kuin Jumala.');
INSERT INTO "finnish" VALUES(71,26,'71:26','Ja Nooa sanoi: »Herra, älä jätä maan päälle ainoatakaan jumalatonta asumaan!');
INSERT INTO "finnish" VALUES(71,27,'71:27','Totisesti, jos säästät heistä ketään, he johtavat palvelijasi harhaan ja siittävät irstaita ja epäuskoisia jälkeläisiä.');
INSERT INTO "finnish" VALUES(71,28,'71:28','Herra, anna anteeksi minulle ja vanhemmilleni; niin myös jokaiselle, joka uskossa astuu talooni, sekä kaikille uskoville, miehille ja naisille! Mutta jumalattomille tuota turmio!»');
INSERT INTO "finnish" VALUES(72,1,'72:1','Sano: »Minulle on ilmoitettu, että osa dzinneistä kuunteli ja sanoi: ''Totisesti olemme kuulleet luettavan ihmeellistä Koraania,');
INSERT INTO "finnish" VALUES(72,2,'72:2','joka ohjaa oikeaan, ja me uskomme siihen emmekä koskaan tahdo asettaa ketään muuta Jumalamme rinnalle.');
INSERT INTO "finnish" VALUES(72,3,'72:3','Totisesti Hän ei - ääretön on Herramme majesteetti - ole ottanut vaimoa eikä lasta.');
INSERT INTO "finnish" VALUES(72,4,'72:4','Vain mielettömät joukostamme sepittävät sopimattomia asioita Jumalasta;');
INSERT INTO "finnish" VALUES(72,5,'72:5','totisesti ajattelimme, etteivät ihmiset eivätkä dzinnit lausuisi valhetta Jumalaa vastaan,');
INSERT INTO "finnish" VALUES(72,6,'72:6','ja että ihmisten joukosta muutamilla oli tapana paeta eräiden dzinnien turviin, ja nämä yllyttivät heitä pöyhkeyteen.');
INSERT INTO "finnish" VALUES(72,7,'72:7','Totisesti he luulivat aivan niin kuin te, että Jumala ei herätä ketään kuolleista.');
INSERT INTO "finnish" VALUES(72,8,'72:8','(ja Koraania kuulleet dzinnit lisäsivät:) Me todellakin etsimme nousua taivaaseen, mutta havaitsimme sen olevan täynnä voimakkaita vartijoita ja liekkejä.');
INSERT INTO "finnish" VALUES(72,9,'72:9','Katso, meillä oli tapana istua muutamilla paikoilla kuullaksemme, mutta se, joka nyt yrittää kuunnella, kohtaa hänelle varatun liekin.');
INSERT INTO "finnish" VALUES(72,10,'72:10','Emmekä todella tiedä, onko se pahaa, mikä on tarkoitettu maan päällä eläville, vai tarkoittaako heidän Herransa heille oikeaa ohjausta.');
INSERT INTO "finnish" VALUES(72,11,'72:11','Totisesti on joukossamme niitä, jotka ovat hyviä, ja myös sellaisia, jotka eivät ole; me olemme jakaantuneet eri lahkokuntiin.');
INSERT INTO "finnish" VALUES(72,12,'72:12','Katso, me uskomme, ettemme milloinkaan voi päästä Jumalaa pakoon täällä maan päällä, emmekä pääse Hänestä lentämälläkään.');
INSERT INTO "finnish" VALUES(72,13,'72:13','Totisesti uskoimme johdatukseen, kun saimme kuulla sen; ja sen, joka uskoo Herraan, ei tarvitse pelätä tappiota eikä yllättävää pahaa.');
INSERT INTO "finnish" VALUES(72,14,'72:14','Joukostamme ovat eräät totisesti Herralle alistuneita muslimeja, ja toiset jumalattomia. Ne, jotka ovat muslimeja, ovat niitä, joiden päämääränä on vanhurskaus.');
INSERT INTO "finnish" VALUES(72,15,'72:15','Mitä taasen tulee jumalattomiin, kuluttaa heidät helvetin tuli.');
INSERT INTO "finnish" VALUES(72,16,'72:16','(Sano myös:) Jos he pysyvät oikealla tiellä, me annamme heille juomavettä runsain määrin');
INSERT INTO "finnish" VALUES(72,17,'72:17','koetellaksemme heitä siten; mutta jos joku kääntyy pois unohtaen Herransa, valmistaa Hän hänelle loppumattoman vaivan.');
INSERT INTO "finnish" VALUES(72,18,'72:18','Totisesti, temppelit kuuluvat Jumalalle, älkää siis huutako avuksi ketään muuta kuin Jumalaa!');
INSERT INTO "finnish" VALUES(72,19,'72:19','Ja kun Jumalan palvelija nousi huutaen Hänen puoleensa, olivat he vähällä tallata hänet tungoksessa.');
INSERT INTO "finnish" VALUES(72,20,'72:20','Sano: »Minä käännyn vain Herrani puoleen enkä aseta ketään Hänen rinnalleen.»');
INSERT INTO "finnish" VALUES(72,21,'72:21','Sano: »Totisesti, minulla ei ole valtaa tuottaa teille pahaa eikä hyvää.»');
INSERT INTO "finnish" VALUES(72,22,'72:22','Sano: »Totisesti, ei kukaan voi puolustaa minua asettuen Jumalaa vastaan, eikä minulla ole muuta turvapaikkaa kuin Hänen tykönään;');
INSERT INTO "finnish" VALUES(72,23,'72:23','julistan vain Jumalan ilmoituksen ja Hänen sanomansa.» Jos joku ei noudata Jumalan ja Hänen sananjulistajansa tahtoa, odottaa häntä totisesti helvetin tuli, ja hänen on iäti oltava siinä,');
INSERT INTO "finnish" VALUES(72,24,'72:24','aina siksi, kunnes ihmiset saavat kokea sen, millä heitä uhattiin, ja tulevat tuntemaan, kumman auttaja on heikompi ja lukumäärältään vähäisempi.');
INSERT INTO "finnish" VALUES(72,25,'72:25','Sano: »En tiedä, onko jo lähellä se, millä teitä on uhattu, vai onko Herra siirtänyt sen määräajaksi.');
INSERT INTO "finnish" VALUES(72,26,'72:26','Hän tietää silmiltä salatut, mutta ei ilmoita salaisuuksiaan muille');
INSERT INTO "finnish" VALUES(72,27,'72:27','paitsi valitsemalleen sananjulistajalle, ja tälle Hän asettaa suojelijan käymään hänen edellään ja samoin hänen jäljessään');
INSERT INTO "finnish" VALUES(72,28,'72:28','ollakseen varma, että he todella ovat julistaneet Herransa sanoman; Hän käsittää kaiken, mitä heissä on, ja Hän pitää lukua kaikesta.»');
INSERT INTO "finnish" VALUES(73,1,'73:1','Sinä, joka olet vaippaan kääriytynyt,');
INSERT INTO "finnish" VALUES(73,2,'73:2','nouse yöllä rukoilemaan leväten vain vähän;');
INSERT INTO "finnish" VALUES(73,3,'73:3','valvo puolet yöstä, tai vähennä siitä hetkinen,');
INSERT INTO "finnish" VALUES(73,4,'73:4','tai lisää siihen vähän ja lue selvästi ja tasaisesti Koraania.');
INSERT INTO "finnish" VALUES(73,5,'73:5','Totisesti ilmoitamme sinulle tärkeän sanan.');
INSERT INTO "finnish" VALUES(73,6,'73:6','Yöhartaudet ovat totisesti parhain keino itsensä voittamiseen ja paras tapa hallita puhettaan.');
INSERT INTO "finnish" VALUES(73,7,'73:7','Sillä sinulla on työtä päivät pitkät.');
INSERT INTO "finnish" VALUES(73,8,'73:8','Muista siis Herrasi nimeä öisin ja antaudu kaikella hartaudella palvomaan Häntä');
INSERT INTO "finnish" VALUES(73,9,'73:9','idän ja lännen Herraa, jota paitsi ei ole muuta jumalaa; omaksu siis Hänet suojelijaksesi!');
INSERT INTO "finnish" VALUES(73,10,'73:10','Ole myös järkkymätön huolimatta siitä, mitä he sanovat, ja karta heitä sopivalla tavalla!');
INSERT INTO "finnish" VALUES(73,11,'73:11','Jätä Minun huostaani ne, jotka väittävät totuutta valheeksi ja elävät yltäkylläisyydessä, ja myönnä heille vähän armonaikaa!');
INSERT INTO "finnish" VALUES(73,12,'73:12','Totisesti, Meillä on heidän varalleen kahleet ja helvetin tuli,');
INSERT INTO "finnish" VALUES(73,13,'73:13','ruoka, joka tukkii kurkun, sekä tuskallinen kuritus');
INSERT INTO "finnish" VALUES(73,14,'73:14','sinä päivänä, jolloin maa ja vuoret vapisevat, jolloin vuoret muuttuvat lentäväksi hiekaksi.');
INSERT INTO "finnish" VALUES(73,15,'73:15','Totisesti olemme lähettänyt luoksenne julistajan, joka todistaa teitä vastaan, niinkuin lähetimme sananjulistajan faraon tykö.');
INSERT INTO "finnish" VALUES(73,16,'73:16','Mutta farao ei totellut sanansaattajaa, ja sentähden tartuimme häneen kovalla kädellä.');
INSERT INTO "finnish" VALUES(73,17,'73:17','Kuinka te uskottomat aiotte pelastaa itsenne, kun ette usko päivään, joka saa lastenkin hiukset harmaaksi?');
INSERT INTO "finnish" VALUES(73,18,'73:18','Sinä päivänä taivas repeytyy kappaleiksi, mutta Hänen lupauksensa täyttyy.');
INSERT INTO "finnish" VALUES(73,19,'73:19','Totisesti tämä on kehoitus, salli siis sen, joka haluaa, löytää tie Herransa tykö.');
INSERT INTO "finnish" VALUES(73,20,'73:20','Totisesti Herrasi tietää, että sinä valvot miltei kaksi kolmannesta yöstä, joskus puolet siitä tai joskus kolmanneksen, ja että samoin tekee osa seuraajistasi. Jumala mittaa yön ja päivän. Hän tietää, että teidän voimanne ei siihen riitä; siksi Hän kääntyy puoleenne armollisesti. Lukekaa siis Koraania, sitä, mikä siinä on helppotajuista. Hän tietää, että jotkut joukostanne ovat sairaita ja toiset ovat vaelluksella etsien Jumalan armoa, ja vielä on toisia, jotka taistelevat Jumalan tiellä. Lukekaa siis, mitä siinä on helppoa, mutta harrastakaa rukoilemista ja antakaa säädetyt almut sekä Jumalalle mieluisa uhri, sillä kaiken hyvän, minkä teette sielujenne puolesta, te saatte Jumalalta takaisin parempana ja suurempana. Ja pyytäkää Jumalalta anteeksiantoa! Jumala on totisesti anteeksiantava, armollinen.');
INSERT INTO "finnish" VALUES(74,1,'74:1','Sinä, joka olet viittaan kiedottu,');
INSERT INTO "finnish" VALUES(74,2,'74:2','nouse ja varoita,');
INSERT INTO "finnish" VALUES(74,3,'74:3','ylistä Herraasi,');
INSERT INTO "finnish" VALUES(74,4,'74:4','puhdista vaatteesi,');
INSERT INTO "finnish" VALUES(74,5,'74:5','karta epäpuhdasta,');
INSERT INTO "finnish" VALUES(74,6,'74:6','älä jaa antimia saadaksesi ne runsaampina takaisin,');
INSERT INTO "finnish" VALUES(74,7,'74:7','vaan pysy lujana Jumalan asiassa.');
INSERT INTO "finnish" VALUES(74,8,'74:8','Sillä kun pasuunaan puhalletaan,');
INSERT INTO "finnish" VALUES(74,9,'74:9','on se päivä oleva ankara päivä,');
INSERT INTO "finnish" VALUES(74,10,'74:10','epäuskoisille kaikkea muuta kuin otollinen.');
INSERT INTO "finnish" VALUES(74,11,'74:11','Jätä Minun huostaani hänet, jonka olen luonut,');
INSERT INTO "finnish" VALUES(74,12,'74:12','jolle olen antanut runsaasti rikkauksia');
INSERT INTO "finnish" VALUES(74,13,'74:13','ja poikia silmien iloksi,');
INSERT INTO "finnish" VALUES(74,14,'74:14','jonka asioita olen parhain päin sovitellut');
INSERT INTO "finnish" VALUES(74,15,'74:15','ja joka vain haluaa yhä enemmän.');
INSERT INTO "finnish" VALUES(74,16,'74:16','Mutta totisesti ei! Sillä hän uhmaa tunnusmerkkejämme;');
INSERT INTO "finnish" VALUES(74,17,'74:17','Minä panen hänen kestettäväkseen raskaan kurituksen.');
INSERT INTO "finnish" VALUES(74,18,'74:18','Kylläpä hän mietti ja mietti;');
INSERT INTO "finnish" VALUES(74,19,'74:19','voi, miten hän harkitsikaan!');
INSERT INTO "finnish" VALUES(74,20,'74:20','Ja kuinka hän punnitsi mielessään!');
INSERT INTO "finnish" VALUES(74,21,'74:21','Sitten hän katsahti ympärilleen,');
INSERT INTO "finnish" VALUES(74,22,'74:22','sitten rypisti otsaansa ja näytti tuimalta,');
INSERT INTO "finnish" VALUES(74,23,'74:23','sitten hän kääntyi pois ja paisui ylpeydestä');
INSERT INTO "finnish" VALUES(74,24,'74:24','ja sanoi: »Tämä on pelkkää opittua noituutta,');
INSERT INTO "finnish" VALUES(74,25,'74:25','pelkkiä sanoja kuolevaisen suusta.»');
INSERT INTO "finnish" VALUES(74,26,'74:26','Minä syöksen hänet helvetin liekkeihin.');
INSERT INTO "finnish" VALUES(74,27,'74:27','Mutta kuinka saisin sinut käsittämään, mikä tuo palo on?');
INSERT INTO "finnish" VALUES(74,28,'74:28','Se ei jätä jäljelle mitään eikä säästä ketään;');
INSERT INTO "finnish" VALUES(74,29,'74:29','se kärventää kuolevaisen kokonaan,');
INSERT INTO "finnish" VALUES(74,30,'74:30','ja sen yläpuolella on yhdeksäntoista.');
INSERT INTO "finnish" VALUES(74,31,'74:31','Tulen vartijoiksi asetimme vain enkeleitä, ja heidän lukunsa asetimme vain koetukseksi niille, jotka eivät usko, jotta ne, joille on annettu Pyhä kirja, olisivat varmoja, ja ne, jotka uskovat, kasvaisivat uskossa; jotta Pyhän kirjan saaneet sekä uskovaiset eivät epäilisi, ja taasen ne, joiden sydämissä on sairaus ja epäusko, sanoisivat: »Mitä tarkoittaa Jumala tällä vertauksella?» Näin johtaa Jumala harhaan kenet haluaa ja ohjaa ketä mielii, eikä Herrasi sotajoukkoja tunne kukaan muu kuin Hän itse, tämä on vain muistutus kuolevaisille.');
INSERT INTO "finnish" VALUES(74,32,'74:32','Ei milloinkaan!');
INSERT INTO "finnish" VALUES(74,33,'74:33','Vakuutan kuun ja valkenevan yön nimessä');
INSERT INTO "finnish" VALUES(74,34,'74:34','sekä aamun nimessä, kun sen valkeus sarastaa:');
INSERT INTO "finnish" VALUES(74,35,'74:35','tämä on totisesti ankarampia asioista,');
INSERT INTO "finnish" VALUES(74,36,'74:36','varoitus ihmiselle,');
INSERT INTO "finnish" VALUES(74,37,'74:37','jokaiselle teistä, joka tahtoo päästä eteenpäin tai jäädä jälkeen:');
INSERT INTO "finnish" VALUES(74,38,'74:38','jokainen sielu on vastuussa teoistaan,');
INSERT INTO "finnish" VALUES(74,39,'74:39','paitsi ne, jotka ovat (Jumalan) oikealla puolella.');
INSERT INTO "finnish" VALUES(74,40,'74:40','Ollessaan paratiisissa nämä kyselevät toisiltaan');
INSERT INTO "finnish" VALUES(74,41,'74:41','syntisten kohtaloa:');
INSERT INTO "finnish" VALUES(74,42,'74:42','»Mikä on saattanut teidät tulen ruoaksi?»');
INSERT INTO "finnish" VALUES(74,43,'74:43','Ja nämä vastaavat: »Me emme kuuluneet niihin, jotka rukoilivat,');
INSERT INTO "finnish" VALUES(74,44,'74:44','emmekä niihin, jotka ruokkivat köyhiä,');
INSERT INTO "finnish" VALUES(74,45,'74:45','vaan haastoimme joutavia niiden kanssa, jotka turhia puhuivat;');
INSERT INTO "finnish" VALUES(74,46,'74:46','me pidimme puhetta tuomiopäivästä valheena,');
INSERT INTO "finnish" VALUES(74,47,'74:47','kunnes kuolema yllätti meidät.»');
INSERT INTO "finnish" VALUES(74,48,'74:48','Niinpä ei heitä enää hyödytä puolustajain apu.');
INSERT INTO "finnish" VALUES(74,49,'74:49','Mutta miksi he kääntyvät pois muistuttajasta');
INSERT INTO "finnish" VALUES(74,50,'74:50','kuin säikkyneet aasit,');
INSERT INTO "finnish" VALUES(74,51,'74:51','jotka pakenevat kuullessaan leijonan karjunnan?');
INSERT INTO "finnish" VALUES(74,52,'74:52','Jokainen heistä odottaa erikoiskutsua.');
INSERT INTO "finnish" VALUES(74,53,'74:53','Mutta eiväthän he pelkää tulevaa elämää.');
INSERT INTO "finnish" VALUES(74,54,'74:54','Ei! Tämä on totisesti varoitus,');
INSERT INTO "finnish" VALUES(74,55,'74:55','ja kukin halukas voi painaa sen mieleensä.');
INSERT INTO "finnish" VALUES(74,56,'74:56','Mutta he eivät tahdo pitää sitä mielessään, ellei se ole Jumalan tahto. Hän ansaitsee kunnioituksen, ja Hänen on anteeksiantamus.');
INSERT INTO "finnish" VALUES(75,1,'75:1','Kutsun todistajaksi ylösnousemuksen päivän,');
INSERT INTO "finnish" VALUES(75,2,'75:2','kutsun todistajaksi omantunnon soimaavan äänen!');
INSERT INTO "finnish" VALUES(75,3,'75:3','»Luuleeko ihminen, että Me emme kerran voi koota hänen luitaan?');
INSERT INTO "finnish" VALUES(75,4,'75:4','Kyllä, Me kykenemme uusimaan täydellisesti hänen sormensakin.»');
INSERT INTO "finnish" VALUES(75,5,'75:5','Ihminen tuudittaa itsensä epäuskoon tulevan elämän suhteen.');
INSERT INTO "finnish" VALUES(75,6,'75:6','Hän kysyy: »Milloin ylösnousemuksen päivä koittaa?»');
INSERT INTO "finnish" VALUES(75,7,'75:7','Mutta kun se päivä sumentaa hänen näkönsä');
INSERT INTO "finnish" VALUES(75,8,'75:8','ja kuu pimenee,');
INSERT INTO "finnish" VALUES(75,9,'75:9','kun aurinko ja kuu yhdessä sammuvat,');
INSERT INTO "finnish" VALUES(75,10,'75:10','sinä päivänä ihminen on sanova: »Missä on paikka, mihin voisin paeta?»');
INSERT INTO "finnish" VALUES(75,11,'75:11','Ei! Ei ole pakopaikkaa.');
INSERT INTO "finnish" VALUES(75,12,'75:12','Sinä päivänä on ainoa turvapaikka Jumalan luona.');
INSERT INTO "finnish" VALUES(75,13,'75:13','Sinä päivänä julistetaan ihmiselle, mitä hän on tehnyt ja mitä laiminlyönyt.');
INSERT INTO "finnish" VALUES(75,14,'75:14','Ihminen joutuu todistuskappaleeksi omaa itseään vastaan,');
INSERT INTO "finnish" VALUES(75,15,'75:15','vaikkapa hän miten latelisi puolustelujaan.');
INSERT INTO "finnish" VALUES(75,16,'75:16','Älä liikuta kieltäsi tyrkyttääksesi heille sitä (Koraania) ennen aikojaan.');
INSERT INTO "finnish" VALUES(75,17,'75:17','Totisesti Me huolehdimme sen kokoonpanosta ja julkilukemisesta.');
INSERT INTO "finnish" VALUES(75,18,'75:18','Seuraa siis sitä järjestystä, missä Me sen saatamme julki,');
INSERT INTO "finnish" VALUES(75,19,'75:19','Sillä Meistähän sen tulkintakin on lähtevä.');
INSERT INTO "finnish" VALUES(75,20,'75:20','Ei, te ihmiset rakastatte nykyistä elämäänne');
INSERT INTO "finnish" VALUES(75,21,'75:21','ja lyötte laimin tulevan elämän.');
INSERT INTO "finnish" VALUES(75,22,'75:22','Sinä päivänä säteilevät muutamien kasvot');
INSERT INTO "finnish" VALUES(75,23,'75:23','katsoen kohti Herraa,');
INSERT INTO "finnish" VALUES(75,24,'75:24','ja sinä päivänä synkkenevät toisten ilmeet,');
INSERT INTO "finnish" VALUES(75,25,'75:25','kun he tietävät, että jotakin tuhoisaa heille tapahtuu.');
INSERT INTO "finnish" VALUES(75,26,'75:26','Ei, kun sydän nousee kurkkuun');
INSERT INTO "finnish" VALUES(75,27,'75:27','ja kysytään: »Kuka taikuri nyt pystyy (kaiken torjumaan)?»');
INSERT INTO "finnish" VALUES(75,28,'75:28','Ja ihminen uskoo, että hänen lähtönsä hetki on tullut');
INSERT INTO "finnish" VALUES(75,29,'75:29','ja toinen isku tyrmää hänet toisensa jälkeen,');
INSERT INTO "finnish" VALUES(75,30,'75:30','sinä päivänä käy hänen tiensä kohti Herraa.');
INSERT INTO "finnish" VALUES(75,31,'75:31','Sillä hän ei uskonut eikä rukoillut,');
INSERT INTO "finnish" VALUES(75,32,'75:32','vaan sanoi totuutta valheeksi ja käänsi sille selkänsä,');
INSERT INTO "finnish" VALUES(75,33,'75:33','käyden sitten korskeana omiensa luo.');
INSERT INTO "finnish" VALUES(75,34,'75:34','Voi sinua, voi!');
INSERT INTO "finnish" VALUES(75,35,'75:35','Ja vielä! Voi sinua, voi!');
INSERT INTO "finnish" VALUES(75,36,'75:36','Luuleeko ihminen, että hänen sallitaan kuljeksia ilman määränpäätä?');
INSERT INTO "finnish" VALUES(75,37,'75:37','Eikö hän (aikanaan) ollut pieni siemenestä kylvetty itu?');
INSERT INTO "finnish" VALUES(75,38,'75:38','Sitten tuli hänestä hyytynyttä verta; Jumalahan loi hänet ja teki hänet täydelliseksi');
INSERT INTO "finnish" VALUES(75,39,'75:39','sekä muodosti kaksi sukupuolta, miehen ja naisen.');
INSERT INTO "finnish" VALUES(75,40,'75:40','Eikö Hän kykene herättämään henkiin kuolleita?');
INSERT INTO "finnish" VALUES(76,1,'76:1','Eikö ole kulunut pitkä aika, ennenkuin ihmisestä tuli jotakin mainitsemisen arvoista?');
INSERT INTO "finnish" VALUES(76,2,'76:2','Me olemme totisesti luonut ihmisen siemenestä yhdistäen sen toiseen. Me aiomme kasvattaa häntä, ja niin olemme antanut hänelle kuulon ja näön.');
INSERT INTO "finnish" VALUES(76,3,'76:3','Totisesti olemme ohjannut hänet polulle, olkoonpa hän kiitollinen tai kiittämätön.');
INSERT INTO "finnish" VALUES(76,4,'76:4','Totisesti olemme valmistanut uskottomille kahleet ja ketjut sekä helvetin tulen.');
INSERT INTO "finnish" VALUES(76,5,'76:5','Totisesti saavat hurskaat juoda maljasta, johon on kätketty heidän (sisäinen) vapautuksensa,');
INSERT INTO "finnish" VALUES(76,6,'76:6','lähteestä, josta Jumalan palvelijat juovat runsain määrin.');
INSERT INTO "finnish" VALUES(76,7,'76:7','He täyttävät lupauksensa ja pelkäävät päivää, jonka tuho leviää ylt''ympäri;');
INSERT INTO "finnish" VALUES(76,8,'76:8','rakkaudesta Häneen he ruokkivat köyhää, orpoa ja vangittua.');
INSERT INTO "finnish" VALUES(76,9,'76:9','»Me ruokimme teitä vain Jumalan tähden; me emme vaadi teiltä palkkaa emmekä kiitosta,');
INSERT INTO "finnish" VALUES(76,10,'76:10','totisesti pelkäämme surun ja murheen päivää, jonka Herramme lähettää.»');
INSERT INTO "finnish" VALUES(76,11,'76:11','Siksi Jumala säästää heidät tuon päivän tuholta ja antaa heidän osakseen iloa ja onnea;');
INSERT INTO "finnish" VALUES(76,12,'76:12','Hän palkitsee heitä heidän lujuutensa tähden paratiisilla ja silkkivaatteilla.');
INSERT INTO "finnish" VALUES(76,13,'76:13','Näihin puettuina ja leväten nojakorokkeilla he eivät tunne hellettä eivätkä purevaa kylmää,');
INSERT INTO "finnish" VALUES(76,14,'76:14','vaan puutarhan varjot lepäävät tiheinä heidän yllään, ja sen hedelmät ovat käden ulottuvilla.');
INSERT INTO "finnish" VALUES(76,15,'76:15','Siellä kiertävät hopeamaljat ja kristallipikarit,');
INSERT INTO "finnish" VALUES(76,16,'76:16','jotka ovat kuin läpikuultava lasi ja jotka täytetään runsain mitoin.');
INSERT INTO "finnish" VALUES(76,17,'76:17','He saavat juoda inkiväärillä höystetyn maljan');
INSERT INTO "finnish" VALUES(76,18,'76:18','lähteestä, jota siellä kutsutaan Salsabiliksi.');
INSERT INTO "finnish" VALUES(76,19,'76:19','Ja heidän ympärillään liikkuvat ikinuoret olennot, joita nähdessäsi sinusta tuntuu, kuin he olisivat ylt''ympäri siroiteltuja helmiä.');
INSERT INTO "finnish" VALUES(76,20,'76:20','Ja kun saat nähdä sen, olet näkevä autuuden ja suuren kuningaskunnan.');
INSERT INTO "finnish" VALUES(76,21,'76:21','Heidän vaatetuksenaan on viheriä, hieno silkki ja paksu, kultakudoksinen sametti. He saavat kantaa hopeaisia rannerenkaita, ja heidän Herransa virvoittaa heitä puhtaalla juomalla.');
INSERT INTO "finnish" VALUES(76,22,'76:22','Tämä on totisesti oleva palkkanne, sillä ponnistelunne palkitaan.');
INSERT INTO "finnish" VALUES(76,23,'76:23','Totisesti olemme lähettänyt sinulle tämän Koraanin vähitellen useina ilmoituksina.');
INSERT INTO "finnish" VALUES(76,24,'76:24','Pysy siis lujana odottaen kärsivällisesti Herrasi tuomiota äläkä tottele ketään syntistä tai kiittämätöntä heidän joukostaan.');
INSERT INTO "finnish" VALUES(76,25,'76:25','Ylistä Herrasi nimeä aamuin ja illoin!');
INSERT INTO "finnish" VALUES(76,26,'76:26','Polvistu yön aikana Häntä palvomaan ja ylistä Häntä myöhään yöhön!');
INSERT INTO "finnish" VALUES(76,27,'76:27','Totisesti muut ihmiset rakastavat tätä maallista elämää eivätkä pidä lukua turmion päivästä.');
INSERT INTO "finnish" VALUES(76,28,'76:28','Me olemme luonut heidät ja vahvistanut heidän jäsenensä, ja jos tahdomme, voimme heidän sijaansa aikaansaada täysin heidän kaltaisiaan olentoja.');
INSERT INTO "finnish" VALUES(76,29,'76:29','Totisesti tämä on kehoitus, ja se, joka tahtoo, löytää tien Herransa luokse.');
INSERT INTO "finnish" VALUES(76,30,'76:30','Mutta teitä se ei miellytä, ellei Jumala kuitenkin niin määrää; totisesti, Jumalan on tieto, viisaus.');
INSERT INTO "finnish" VALUES(76,31,'76:31','Hän sulkee armoonsa kenet haluaa, mutta jumalattomille Hän on varannut tuskan ja kurituksen.');
INSERT INTO "finnish" VALUES(77,1,'77:1','Edellä käyneitten nimessä, jotka hyvää julistivat,');
INSERT INTO "finnish" VALUES(77,2,'77:2','niiden nimessä, jotka valhetta loitolle tunkivat,');
INSERT INTO "finnish" VALUES(77,3,'77:3','niiden, jotka (totuutta) ylt''ympäri kylvivät,');
INSERT INTO "finnish" VALUES(77,4,'77:4','joilla on (hyvän ja pahan) erottamisen kyky,');
INSERT INTO "finnish" VALUES(77,5,'77:5','jotka kansaa neuvovat,');
INSERT INTO "finnish" VALUES(77,6,'77:6','joko selittäen tai varoittaen:');
INSERT INTO "finnish" VALUES(77,7,'77:7','Totisesti sen, mitä teille on ennustettu, täytyy tapahtua,');
INSERT INTO "finnish" VALUES(77,8,'77:8','kun tähdet häipyvät,');
INSERT INTO "finnish" VALUES(77,9,'77:9','kun taivas repeilee,');
INSERT INTO "finnish" VALUES(77,10,'77:10','kun vuoret raastetaan hajalle, tomuksi,');
INSERT INTO "finnish" VALUES(77,11,'77:11','ja kun sananjulistajien määräämä hetki saapuu');
INSERT INTO "finnish" VALUES(77,12,'77:12','miksi päiväksi on tuomio määrätty?');
INSERT INTO "finnish" VALUES(77,13,'77:13','Ratkaisun päiväksi.');
INSERT INTO "finnish" VALUES(77,14,'77:14','Miten saada teidät ymmärtämään myös, mitä tuomion päivä sisältää?');
INSERT INTO "finnish" VALUES(77,15,'77:15','Voi tuona päivänä niitä, jotka hylkäsivät totuuden!');
INSERT INTO "finnish" VALUES(77,16,'77:16','Emmekö ole tuhonnut aikaisemminkin eläneitä sukupolvia?');
INSERT INTO "finnish" VALUES(77,17,'77:17','Ja sitten annoimme seuraavien sukupolvien astua heidän sijalleen.');
INSERT INTO "finnish" VALUES(77,18,'77:18','Näin menettelemme syyllisten suhteen.');
INSERT INTO "finnish" VALUES(77,19,'77:19','Voi sinä päivänä niitä, jotka totuuden hylkäsivät!');
INSERT INTO "finnish" VALUES(77,20,'77:20','Emmekö luonut teitä mitättömästä pisarasta?');
INSERT INTO "finnish" VALUES(77,21,'77:21','Sen sijoitimme varmaan säilöön');
INSERT INTO "finnish" VALUES(77,22,'77:22','määrätyksi ajaksi');
INSERT INTO "finnish" VALUES(77,23,'77:23','ja sitten kehitimme sitä sopusuhtaisesti. Miten sopusuhtaiset ovatkaan luomistyömme!');
INSERT INTO "finnish" VALUES(77,24,'77:24','Voi sinä päivänä niitä, jotka hylkivät totuutta!');
INSERT INTO "finnish" VALUES(77,25,'77:25','Emmekö ole luonut maan');
INSERT INTO "finnish" VALUES(77,26,'77:26','kokoamaan helmaansa eläviä ja kuolleita?');
INSERT INTO "finnish" VALUES(77,27,'77:27','Emmekö ole maahan sijoittanut korkeita vuoria ja antanut teille raitista vettä juotavaksi?');
INSERT INTO "finnish" VALUES(77,28,'77:28','Voi sinä päivänä niitä, jotka totuutta hylkivät!');
INSERT INTO "finnish" VALUES(77,29,'77:29','Käykää kohti sitä, jota väititte valheeksi,');
INSERT INTO "finnish" VALUES(77,30,'77:30','käykää kohti synkkää varjoa, joka ulottuu kolmeen suuntaan,');
INSERT INTO "finnish" VALUES(77,31,'77:31','joka ei vilvoita eikä torju tulen liekkejä!');
INSERT INTO "finnish" VALUES(77,32,'77:32','Nämä sinkoavat ylös torninkorkuisina,');
INSERT INTO "finnish" VALUES(77,33,'77:33','hohtavan keltaisina kuin kameelit.');
INSERT INTO "finnish" VALUES(77,34,'77:34','Voi sinä päivänä niitä, jotka totuutta hylkivät!');
INSERT INTO "finnish" VALUES(77,35,'77:35','Tämä on päivä, jona heidän ei sallita puhua');
INSERT INTO "finnish" VALUES(77,36,'77:36','eikä esitellä anteeksipyyntöjä.');
INSERT INTO "finnish" VALUES(77,37,'77:37','Voi sinä päivänä niitä, jotka totuutta hylkivät!');
INSERT INTO "finnish" VALUES(77,38,'77:38','Tämä on tuomion päivä. Silloin Me kokoamme teidät yhteen esi~isienne kanssa.');
INSERT INTO "finnish" VALUES(77,39,'77:39','Jos viekkauteen pystytte, niin yrittäkää sitä Minua vastaan,');
INSERT INTO "finnish" VALUES(77,40,'77:40','sillä voi sinä päivänä totuuden väärentäjiä!');
INSERT INTO "finnish" VALUES(77,41,'77:41','Totisesti saavat ne, jotka pelkäävät Jumalaa, olla varjossa ja lähteitten äärillä');
INSERT INTO "finnish" VALUES(77,42,'77:42','sekä nauttia hedelmiä mielin määrin.');
INSERT INTO "finnish" VALUES(77,43,'77:43','»Syökää ja juokaa nautinnoksenne, palkaksi siitä, mitä olette tehneet!»');
INSERT INTO "finnish" VALUES(77,44,'77:44','Näin todenteolla palkitsemme niitä, jotka tekevät hyvää.');
INSERT INTO "finnish" VALUES(77,45,'77:45','Voi sinä päivänä niitä, jotka sanoivat totuutta valheeksi!');
INSERT INTO "finnish" VALUES(77,46,'77:46','Syökää ja nauttikaa hetken aikaa, te syntiset!');
INSERT INTO "finnish" VALUES(77,47,'77:47','Voi tuona päivänä totuuden väärentäjiä!');
INSERT INTO "finnish" VALUES(77,48,'77:48','Ja kun heille sanotaan: »Kumartakaa!» niin he eivät kumarra.');
INSERT INTO "finnish" VALUES(77,49,'77:49','Voi sinä päivänä niitä, jotka totuutta hylkivät!');
INSERT INTO "finnish" VALUES(77,50,'77:50','Mihin sanomaan he tämän (Koraanin) jälkeen voivat uskoa?');
INSERT INTO "finnish" VALUES(78,1,'78:1','Mitä he kyselevät toinen toiseltaan?');
INSERT INTO "finnish" VALUES(78,2,'78:2','Suurta sanomaako,');
INSERT INTO "finnish" VALUES(78,3,'78:3','josta he ovat eri mieltä?');
INSERT INTO "finnish" VALUES(78,4,'78:4','Ei, pian he saavat sen tietää.');
INSERT INTO "finnish" VALUES(78,5,'78:5','Vieläkin: ei! He tulevat sen pian kokemaan.');
INSERT INTO "finnish" VALUES(78,6,'78:6','Emmekö ole luonut maan lakeudeksi');
INSERT INTO "finnish" VALUES(78,7,'78:7','ja vuoret tukipilareiksi?');
INSERT INTO "finnish" VALUES(78,8,'78:8','Me olemme luonut teidät parittain');
INSERT INTO "finnish" VALUES(78,9,'78:9','ja säätänyt unen teille virkistykseksi,');
INSERT INTO "finnish" VALUES(78,10,'78:10','yön Me loimme teille verhoksi');
INSERT INTO "finnish" VALUES(78,11,'78:11','ja päivän Me annoimme elatuksen hankkimiseen.');
INSERT INTO "finnish" VALUES(78,12,'78:12','Yläpuolellenne olemme asettanut seitsemän vahvaa henkivaltaa');
INSERT INTO "finnish" VALUES(78,13,'78:13','ja laittanut valoa säteilevän lampun;');
INSERT INTO "finnish" VALUES(78,14,'78:14','raskaista pilvistä Me lähetämme vuolaita virtoja');
INSERT INTO "finnish" VALUES(78,15,'78:15','kasvattaaksemme viljan ja kasvit');
INSERT INTO "finnish" VALUES(78,16,'78:16','sekä tiheinä vihannoivat puutarhat.');
INSERT INTO "finnish" VALUES(78,17,'78:17','Totisesti tuomion päivä on määrätty,');
INSERT INTO "finnish" VALUES(78,18,'78:18','päivä, jona puhalletaan pasuunaan ja te nousette joukoittain,');
INSERT INTO "finnish" VALUES(78,19,'78:19','päivä, jolloin taivas aukeaa ja siihen ilmenee joukko portteja,');
INSERT INTO "finnish" VALUES(78,20,'78:20','jolloin vuoret siirtyvät paikoiltaan ja muuttuvat kuin varjoiksi.');
INSERT INTO "finnish" VALUES(78,21,'78:21','Totisesti, helvetti silloin omaansa odottaa,');
INSERT INTO "finnish" VALUES(78,22,'78:22','se on määränpää syntisiä varten,');
INSERT INTO "finnish" VALUES(78,23,'78:23','he viruvat siellä vuosikausia,');
INSERT INTO "finnish" VALUES(78,24,'78:24','saamatta nauttia viileätä juomaa;');
INSERT INTO "finnish" VALUES(78,25,'78:25','heidän juomansa on kiehuvaa tai jääkylmää,');
INSERT INTO "finnish" VALUES(78,26,'78:26','se on palkka, joka vastaa heidän syntejänsä.');
INSERT INTO "finnish" VALUES(78,27,'78:27','He eivät totisesti uskoneet joutuvansa teoistaan tilille,');
INSERT INTO "finnish" VALUES(78,28,'78:28','vaan sanoivat julistustamme valheeksi, totuutta vääristellen.');
INSERT INTO "finnish" VALUES(78,29,'78:29','Mutta meillä on kaikki merkittynä muistiin.');
INSERT INTO "finnish" VALUES(78,30,'78:30','»Maistakaa siis! Me emme lisää teille muuta kuin kuritusta.»');
INSERT INTO "finnish" VALUES(78,31,'78:31','Totisesti odottaa voitto niitä, jotka pelkäävät Jumalaa,');
INSERT INTO "finnish" VALUES(78,32,'78:32','heidän ovat hedelmä- ja viinitarhat,');
INSERT INTO "finnish" VALUES(78,33,'78:33','nuoruutta uhkuvat neidot, heidän ikäisensä,');
INSERT INTO "finnish" VALUES(78,34,'78:34','ja puhdasta viiniä täydet pikarit.');
INSERT INTO "finnish" VALUES(78,35,'78:35','Ei heille siellä turhuutta puhuta eikä valheita jutella.');
INSERT INTO "finnish" VALUES(78,36,'78:36','Se on Herrasi antama palkka, ansioiden mukainen lahja,');
INSERT INTO "finnish" VALUES(78,37,'78:37','Herran, jonka ovat taivaat ja maa ja kaikki niiden välisessä avaruudessa, laupiaan Armahtajan, jota he eivät rohkene puhutella.');
INSERT INTO "finnish" VALUES(78,38,'78:38','Sinä päivänä, jolloin henki ja enkelit seisovat riveissä, eivät muut saa puhua kuin se, jonka Laupias sallii ja joka lausuu totuuden.');
INSERT INTO "finnish" VALUES(78,39,'78:39','Tuo päivä on totuuden päivä, ja se, joka parhaimman valitsee, turvautuu Herraan.');
INSERT INTO "finnish" VALUES(78,40,'78:40','Totisesti olemme varoittanut teitä lähestyvästä kurituksesta, päivästä, jolloin ihminen saa nähdä, mikä on hänen kättensä työ, ja epäuskoinen huudahtaa: »Ah, jospa olisin maan tomua!»');
INSERT INTO "finnish" VALUES(79,1,'79:1','Niiden nimessä, jotka kaikin voimin (jousiaan) jännittävät,');
INSERT INTO "finnish" VALUES(79,2,'79:2','niiden (nuolten), jotka vinhasti eteenpäin syöksyvät,');
INSERT INTO "finnish" VALUES(79,3,'79:3','niiden (ratsujen), jotka nopeasti laukkaavat,');
INSERT INTO "finnish" VALUES(79,4,'79:4','ja jotka ensimmäisinä kiitävät,');
INSERT INTO "finnish" VALUES(79,5,'79:5','sekä vihdoin niiden (johtajaan), jotka voiton turvaavat.');
INSERT INTO "finnish" VALUES(79,6,'79:6','Sinä päivänä, jolloin järkkyvä maa järkkyy');
INSERT INTO "finnish" VALUES(79,7,'79:7','ja se tapahtuu, minkä tuleman pitää,');
INSERT INTO "finnish" VALUES(79,8,'79:8','sinä päivänä sydämet vavahtavat');
INSERT INTO "finnish" VALUES(79,9,'79:9','ja katseet painuvat alas.');
INSERT INTO "finnish" VALUES(79,10,'79:10','He arvelevat: »Palautetaanko meidät todella entiseen tilaamme?');
INSERT INTO "finnish" VALUES(79,11,'79:11','Kuinka! Kun jo luummekin ovat lahonneet!»');
INSERT INTO "finnish" VALUES(79,12,'79:12','He sanovat: »Sellainen paluu olisi turmiota tuottava.»');
INSERT INTO "finnish" VALUES(79,13,'79:13','Mutta kaikki muuttuu pelkäksi huudoksi');
INSERT INTO "finnish" VALUES(79,14,'79:14','ja katso, he heräävät!');
INSERT INTO "finnish" VALUES(79,15,'79:15','Eikö teille julistettu sanomaa Mooseksesta?');
INSERT INTO "finnish" VALUES(79,16,'79:16','Kuinka hänen Herransa kahdesti kutsui häntä pyhässä laaksossa:');
INSERT INTO "finnish" VALUES(79,17,'79:17','»Mene faraon luo! Hän on totisesti synteihin vaipunut.');
INSERT INTO "finnish" VALUES(79,18,'79:18','Ja sano: ''Tahdotko puhdistua?');
INSERT INTO "finnish" VALUES(79,19,'79:19','Minä johdan sinut Herrasi luo, jotta pelkäisit Häntä.''»');
INSERT INTO "finnish" VALUES(79,20,'79:20','Ja hän näytti faraolle suuren tunnusmerkin,');
INSERT INTO "finnish" VALUES(79,21,'79:21','mutta tämä hylkäsi totuuden eikä totellut.');
INSERT INTO "finnish" VALUES(79,22,'79:22','Sitten käänsi hän hänelle äkkiä selkänsä,');
INSERT INTO "finnish" VALUES(79,23,'79:23','kokosi joukon ihmisiä ja huusi heille');
INSERT INTO "finnish" VALUES(79,24,'79:24','sanoen: »Minä olen teidän ylin herranne.»');
INSERT INTO "finnish" VALUES(79,25,'79:25','Silloin Jumala yllätti hänet rangaisten sekä tässä että tulevassa elämässä.');
INSERT INTO "finnish" VALUES(79,26,'79:26','Totisesti on tässä varoittava esimerkki sille, joka pelkää Jumalaa.');
INSERT INTO "finnish" VALUES(79,27,'79:27','Kumpi on vaikeampi luoda, teidät vai taivas, jonka Hän on rakentanut?');
INSERT INTO "finnish" VALUES(79,28,'79:28','Hän on sen korkealle kohottanut ja sille muodon antanut:');
INSERT INTO "finnish" VALUES(79,29,'79:29','yöksi Hän täyttää sen pimeydellä, ja siitä hän vetää taas esiin päivänvalon.');
INSERT INTO "finnish" VALUES(79,30,'79:30','Ja sitten hän ulotti maan avaraksi');
INSERT INTO "finnish" VALUES(79,31,'79:31','ja kohotti sen vedet pinnalle sekä levitti laitumet;');
INSERT INTO "finnish" VALUES(79,32,'79:32','vuoret Hän sijoitti lujasti paikoilleen,');
INSERT INTO "finnish" VALUES(79,33,'79:33','kaiken tämän teidän ja karjanne hyväksi.');
INSERT INTO "finnish" VALUES(79,34,'79:34','Mutta kun valtava tuhon päivä tulee,');
INSERT INTO "finnish" VALUES(79,35,'79:35','päivä, jona ihminen muistaa, mitä hän on tavoitellut,');
INSERT INTO "finnish" VALUES(79,36,'79:36','ja helvetti paljastuu näkeväiselle,');
INSERT INTO "finnish" VALUES(79,37,'79:37','silloin se, joka väärintekijä on');
INSERT INTO "finnish" VALUES(79,38,'79:38','ja pitää parempana maallista elämää,');
INSERT INTO "finnish" VALUES(79,39,'79:39','saa totisesti helvetin asunnokseen.');
INSERT INTO "finnish" VALUES(79,40,'79:40','Mutta se, joka pelkää Herransa tulemista ja hillitsee sielunsa alhaiset himot,');
INSERT INTO "finnish" VALUES(79,41,'79:41','saa totisesti paratiisin asunnokseen.');
INSERT INTO "finnish" VALUES(79,42,'79:42','Tuota hetkeä he kysyvät sinulta; milloin sen pitää tuleman?');
INSERT INTO "finnish" VALUES(79,43,'79:43','Mutta kuinka voit sen sanoa?');
INSERT INTO "finnish" VALUES(79,44,'79:44','Onhan sen tulo Herrasi käsissä.');
INSERT INTO "finnish" VALUES(79,45,'79:45','Sinä olet vain varoittaja jokaiselle, joka sitä kavahtaa.');
INSERT INTO "finnish" VALUES(79,46,'79:46','Päivänä, jolloin he saavat nähdä sen, on heistä kuin he olisivat odottaneet sitä vain illan tai aamun.');
INSERT INTO "finnish" VALUES(80,1,'80:1','Hän (profeetta) rypisti otsaansa ja kääntyi pois,');
INSERT INTO "finnish" VALUES(80,2,'80:2','koska sokea mies tuli hänen luokseen.');
INSERT INTO "finnish" VALUES(80,3,'80:3','Mutta mistä sinä tiedät, eikö hän olisi halunnut tehdä parannusta');
INSERT INTO "finnish" VALUES(80,4,'80:4','tai ottaa opetuksesta vaarin, niin että se olisi koitunut hänen hyväkseen.');
INSERT INTO "finnish" VALUES(80,5,'80:5','Mutta toisin menettelit rikkaan suhteen, joka ei luullut sinua tarvitsevansa;');
INSERT INTO "finnish" VALUES(80,6,'80:6','häntä kunnioittavasti kohtelit,');
INSERT INTO "finnish" VALUES(80,7,'80:7','vaikka et ollutkaan vastuussa hänen parannuksestaan.');
INSERT INTO "finnish" VALUES(80,8,'80:8','Mutta hänestä, joka tulee luoksesi kovassa tuskassa');
INSERT INTO "finnish" VALUES(80,9,'80:9','ja pelossa,');
INSERT INTO "finnish" VALUES(80,10,'80:10','hänestä et pidä väliä.');
INSERT INTO "finnish" VALUES(80,11,'80:11','Totisesti tämä on kehoitus;');
INSERT INTO "finnish" VALUES(80,12,'80:12','ottakoon tämän varteen se, joka parhaan valitsee.');
INSERT INTO "finnish" VALUES(80,13,'80:13','Totisesti ovat tämän kirjoittaneet arvokkaille lehdille,');
INSERT INTO "finnish" VALUES(80,14,'80:14','puhtaille ja yleville,');
INSERT INTO "finnish" VALUES(80,15,'80:15','jalojen ja hurskaitten');
INSERT INTO "finnish" VALUES(80,16,'80:16','kirjoittajien kädet.');
INSERT INTO "finnish" VALUES(80,17,'80:17','Voi ihmistä! Miten kiittämätön hän onkaan!');
INSERT INTO "finnish" VALUES(80,18,'80:18','Mistä on Jumala hänet luonut?');
INSERT INTO "finnish" VALUES(80,19,'80:19','Pienestä elinidusta! Hän on luonut hänet ja kehittänyt hänet elinvoimaiseksi');
INSERT INTO "finnish" VALUES(80,20,'80:20','ja sitten raivannut hänelle tien, jota on helppo käydä.');
INSERT INTO "finnish" VALUES(80,21,'80:21','Sitten antaa Hän hänen kuolla ja määrää leposijan');
INSERT INTO "finnish" VALUES(80,22,'80:22','ja aikanaan, oman päätöksensä mukaan, Hän herättää hänet kuolleista.');
INSERT INTO "finnish" VALUES(80,23,'80:23','Mutta ei ihminen ole noudattanut sitä, mitä Hän on käskenyt hänen tehdä.');
INSERT INTO "finnish" VALUES(80,24,'80:24','Ajatelkoon ihminen edes saamaansa ravintoa!');
INSERT INTO "finnish" VALUES(80,25,'80:25','Totisesti me lähetämme veden vuolaina virtoina,');
INSERT INTO "finnish" VALUES(80,26,'80:26','Me murramme hänelle maan kamaran');
INSERT INTO "finnish" VALUES(80,27,'80:27','ja annamme sen kasvaa viljaa,');
INSERT INTO "finnish" VALUES(80,28,'80:28','rypäleitä ja yrttejä');
INSERT INTO "finnish" VALUES(80,29,'80:29','oliiveja ja palmuja,');
INSERT INTO "finnish" VALUES(80,30,'80:30','tuuheita puutarhoja,');
INSERT INTO "finnish" VALUES(80,31,'80:31','hedelmiä ja laitumia');
INSERT INTO "finnish" VALUES(80,32,'80:32','elatukseksi teille ja karjallenne.');
INSERT INTO "finnish" VALUES(80,33,'80:33','Mutta kun pasuunan mahtava pauhu kuuluu,');
INSERT INTO "finnish" VALUES(80,34,'80:34','sinä päivänä, jolloin mies pakenee veljensä luota,');
INSERT INTO "finnish" VALUES(80,35,'80:35','äitinsä ja isänsä luota,');
INSERT INTO "finnish" VALUES(80,36,'80:36','vaimonsa ja poikiensa luota,');
INSERT INTO "finnish" VALUES(80,37,'80:37','tuona päivänä tulee jokaisen kohdalle asia, joka täyttää hänen mielensä kokonaan.');
INSERT INTO "finnish" VALUES(80,38,'80:38','Toisten kasvot sinä päivänä kirkastuvat,');
INSERT INTO "finnish" VALUES(80,39,'80:39','hymyilevät ja säteilevät onnea;');
INSERT INTO "finnish" VALUES(80,40,'80:40','toisten kasvoja sinä päivänä peittää tomu');
INSERT INTO "finnish" VALUES(80,41,'80:41','ja verhoaa pimeys.');
INSERT INTO "finnish" VALUES(80,42,'80:42','Nämä ovat uskottomia ja väärintekijöitä.');
INSERT INTO "finnish" VALUES(81,1,'81:1','Kun aurinko menee umpeen,');
INSERT INTO "finnish" VALUES(81,2,'81:2','kun tähdet sammuvat,');
INSERT INTO "finnish" VALUES(81,3,'81:3','kun vuoret järkkyvät,');
INSERT INTO "finnish" VALUES(81,4,'81:4','kun kantavat kameelit hylätään,');
INSERT INTO "finnish" VALUES(81,5,'81:5','kun pedot peloitetaan muille tienoille,');
INSERT INTO "finnish" VALUES(81,6,'81:6','kun meret kuohuvat yli äyräittensä,');
INSERT INTO "finnish" VALUES(81,7,'81:7','kun sielut ja ruumiit jälleen yhtyvät');
INSERT INTO "finnish" VALUES(81,8,'81:8','ja kun elävänä haudatulta naiselta kysytään,');
INSERT INTO "finnish" VALUES(81,9,'81:9','minkä synnin vuoksi hänet surmattiin;');
INSERT INTO "finnish" VALUES(81,10,'81:10','kun kirjoitukset avataan,');
INSERT INTO "finnish" VALUES(81,11,'81:11','kun taivaan kansi pyyhkäistään pois,');
INSERT INTO "finnish" VALUES(81,12,'81:12','kun helvetin tuli viritetään');
INSERT INTO "finnish" VALUES(81,13,'81:13','ja kun paratiisi lähestyy,');
INSERT INTO "finnish" VALUES(81,14,'81:14','silloin saa jokainen sielu tietää, mitä se on itselleen valmistanut.');
INSERT INTO "finnish" VALUES(81,15,'81:15','Vannon kautta tähtien,');
INSERT INTO "finnish" VALUES(81,16,'81:16','jotka kulkevat ratojaan ja katoavat;');
INSERT INTO "finnish" VALUES(81,17,'81:17','kautta saapuvan yön');
INSERT INTO "finnish" VALUES(81,18,'81:18','ja valkenevan aamun:');
INSERT INTO "finnish" VALUES(81,19,'81:19','»Tämä on totisesti jalon sananjulistajan sanoma,');
INSERT INTO "finnish" VALUES(81,20,'81:20','sellaisen, jonka valta tulee voiman Herralta,');
INSERT INTO "finnish" VALUES(81,21,'81:21','jota on toteltava ja joka ei luottamusta petä.');
INSERT INTO "finnish" VALUES(81,22,'81:22','Ei teidän heimolaisenne (profeetta) houraile.');
INSERT INTO "finnish" VALUES(81,23,'81:23','Totisesti hän on nähnyt enkeli Gabrielin kirkkaalla taivaanrannalla,');
INSERT INTO "finnish" VALUES(81,24,'81:24','eikä hän ole kitsas näkymättömien asiain salaaja;');
INSERT INTO "finnish" VALUES(81,25,'81:25','ei tämä myöskään ole karkoitetun paholaisen ennustelua.');
INSERT INTO "finnish" VALUES(81,26,'81:26','Mitä kohti siis kuljette?');
INSERT INTO "finnish" VALUES(81,27,'81:27','Tämä ei ole muuta kuin varoitus maailman kansoille,');
INSERT INTO "finnish" VALUES(81,28,'81:28','jokaiselle teistä, joka tahtoo pysytellä oikealla tiellä.');
INSERT INTO "finnish" VALUES(81,29,'81:29','Mutta ette te itse tahdo, ellei Jumala, maailmojen Herra,sitä tahdo.»');
INSERT INTO "finnish" VALUES(82,1,'82:1','Kun taivas rikki repeytyy,');
INSERT INTO "finnish" VALUES(82,2,'82:2','kun tähdet hajalle pirstoutuvat,');
INSERT INTO "finnish" VALUES(82,3,'82:3','kun virrat tulvivat vuolaina,');
INSERT INTO "finnish" VALUES(82,4,'82:4','ja haudat avautuvat,');
INSERT INTO "finnish" VALUES(82,5,'82:5','saa jokainen sielu tietää, mitä on tehnyt ja mitä laiminlyönyt.');
INSERT INTO "finnish" VALUES(82,6,'82:6','Oi, ihminen, mikä on vietellyt sinut jaloa Herraasi vastaan,');
INSERT INTO "finnish" VALUES(82,7,'82:7','joka on luonut sinut, tehnyt sinut täydelliseksi ja sopusuhtaiseksi?');
INSERT INTO "finnish" VALUES(82,8,'82:8','Haluamaansa muotoon Hän on sinut valanut.');
INSERT INTO "finnish" VALUES(82,9,'82:9','Voi, te sanotte tuomiota valheeksi,');
INSERT INTO "finnish" VALUES(82,10,'82:10','mutta totisesti on yläpuolellanne vartijoita,');
INSERT INTO "finnish" VALUES(82,11,'82:11','jaloja kirjoihin merkitsijöitä,');
INSERT INTO "finnish" VALUES(82,12,'82:12','jotka tuntevat tekonne.');
INSERT INTO "finnish" VALUES(82,13,'82:13','Totisesti saavat hurskaat osakseen taivaan autuuden,');
INSERT INTO "finnish" VALUES(82,14,'82:14','ja totisesti joutuvat jumalattomat helvetin tuleen,');
INSERT INTO "finnish" VALUES(82,15,'82:15','he joutuvat sinne tuomion päivänä');
INSERT INTO "finnish" VALUES(82,16,'82:16','eivätkä he voi sitä mitenkään välttää.');
INSERT INTO "finnish" VALUES(82,17,'82:17','Käsitätkö, mikä Tuomion päivä on?');
INSERT INTO "finnish" VALUES(82,18,'82:18','Vieläkin: käsitätkö, mikä Tuomion päivä on?');
INSERT INTO "finnish" VALUES(82,19,'82:19','Se on päivä, jona yksikään sielu ei mahda mitään toisen hyväksi. Herran on kaikki valta sinä päivänä.');
INSERT INTO "finnish" VALUES(83,1,'83:1','Voi väärintekijöitä mitattaessa tai punnittaessa');
INSERT INTO "finnish" VALUES(83,2,'83:2','jotka vaativat täyden mitan, kun mittaavat toisilta omaksi hyväkseen,');
INSERT INTO "finnish" VALUES(83,3,'83:3','mutta antavat vajaan mitan, kun mittaavat tai punnitsevat muille!');
INSERT INTO "finnish" VALUES(83,4,'83:4','Eivätkö nämä ihmiset usko, että heidät herätetään kuolleista');
INSERT INTO "finnish" VALUES(83,5,'83:5','kohtalokkaana päivänä,');
INSERT INTO "finnish" VALUES(83,6,'83:6','päivänä, jona ihmisten on seistävä maailmojen Herran edessä?');
INSERT INTO "finnish" VALUES(83,7,'83:7','Ei! Totisesti on jumalattomien nimiluettelo Sidzinissä.');
INSERT INTO "finnish" VALUES(83,8,'83:8','Mutta tiedätkö, mikä Sidzin on?');
INSERT INTO "finnish" VALUES(83,9,'83:9','Se on selkeästi kirjoitettu kirja.');
INSERT INTO "finnish" VALUES(83,10,'83:10','Voi sinä päivänä niitä, jotka hylkivät totuutta,');
INSERT INTO "finnish" VALUES(83,11,'83:11','niitä, jotka pitävät valheena Tuomion päivää!');
INSERT INTO "finnish" VALUES(83,12,'83:12','Mutta valheena sitä pitää vain julkea syntinen,');
INSERT INTO "finnish" VALUES(83,13,'83:13','joka sanoo, kun ilmoituksemme julistetaan hänelle: »Vanhojen taruja.»');
INSERT INTO "finnish" VALUES(83,14,'83:14','Totisesti ei! Heidän tekonsa ovat kuin ruoste tärvelleet heidän sydämensä.');
INSERT INTO "finnish" VALUES(83,15,'83:15','Totisesti ei heitä päästetä Herransa luokse sinä päivänä,');
INSERT INTO "finnish" VALUES(83,16,'83:16','vaan he saavat astua helvetin ahjoon.');
INSERT INTO "finnish" VALUES(83,17,'83:17','Silloin sanotaan: »Tämä on se, jota te olemattomaksi väititte.»');
INSERT INTO "finnish" VALUES(83,18,'83:18','Mutta hurskaitten nimiluettelo on Illijunissa.');
INSERT INTO "finnish" VALUES(83,19,'83:19','Tiedätkö, mikä Illijun on?');
INSERT INTO "finnish" VALUES(83,20,'83:20','Kirjoitettu kirja korkeuksissa.');
INSERT INTO "finnish" VALUES(83,21,'83:21','Jumalan valiojoukko sen todistaa.');
INSERT INTO "finnish" VALUES(83,22,'83:22','Totisesti saavat hurskaat elää autuudessa,');
INSERT INTO "finnish" VALUES(83,23,'83:23','katsellen ympärilleen lepokorokkeiltaan.');
INSERT INTO "finnish" VALUES(83,24,'83:24','Sinä olet näkevä autuuden kirkkauden heidän kasvoillaan.');
INSERT INTO "finnish" VALUES(83,25,'83:25','He saavat nauttia puhdasta, muilta sinetöityä juomaa,');
INSERT INTO "finnish" VALUES(83,26,'83:26','jonka sinetti on myskiä - tätä juomaa kaikki kaivaten kaivatkoot -');
INSERT INTO "finnish" VALUES(83,27,'83:27','tämä sekoitus tulee ylhäältä,');
INSERT INTO "finnish" VALUES(83,28,'83:28','lähteestä, josta Jumalaa lähelle pääsevät juovat.');
INSERT INTO "finnish" VALUES(83,29,'83:29','Totisesti syntiset nauroivat uskovaisia,');
INSERT INTO "finnish" VALUES(83,30,'83:30','kun he kulkivat näiden ohi, iskivät he toisilleen silmää;');
INSERT INTO "finnish" VALUES(83,31,'83:31','palatessaan omiensa luo he olivat tyytyväisiä');
INSERT INTO "finnish" VALUES(83,32,'83:32','ja heidät nähdessään he sanoivat: »Totisesti nuo ovat harhateillä.»');
INSERT INTO "finnish" VALUES(83,33,'83:33','Mutta totisesti emme Me lähettänyt tuommoisia heidän kaitsijoikseen,');
INSERT INTO "finnish" VALUES(83,34,'83:34','ja sinä päivänä saavat ne, jotka uskoivat, nauraa epäuskoisille');
INSERT INTO "finnish" VALUES(83,35,'83:35','ja katsella ylhäisiltä lepokorokkeilta ympärilleen.');
INSERT INTO "finnish" VALUES(83,36,'83:36','Mutta epäuskoiset saavat totisesti tekojensa palkan.');
INSERT INTO "finnish" VALUES(84,1,'84:1','Kun taivas ratkeaa');
INSERT INTO "finnish" VALUES(84,2,'84:2','kuullessaan Herransa äänen ja totellen sitä,');
INSERT INTO "finnish" VALUES(84,3,'84:3','kun maan ääret tasoittuvat');
INSERT INTO "finnish" VALUES(84,4,'84:4','ja se suistaa ulos sisuksensa ja tyhjentyy,');
INSERT INTO "finnish" VALUES(84,5,'84:5','kuullessaan Herransa äänen ja totellen sitä,');
INSERT INTO "finnish" VALUES(84,6,'84:6','silloin, oi ihminen, on sinun ponnisteltava Herraasi kohti, ja se on kova ponnistelu, kunnes kohtaat Hänet.');
INSERT INTO "finnish" VALUES(84,7,'84:7','Kenelle kirja annetaan oikeaan käteen,');
INSERT INTO "finnish" VALUES(84,8,'84:8','hänen tilintekonsa on oleva helppo,');
INSERT INTO "finnish" VALUES(84,9,'84:9','ja hän palaa omiensa luo iloiten;');
INSERT INTO "finnish" VALUES(84,10,'84:10','mutta toisin on sen, jolle kirja annetaan takaapäin,');
INSERT INTO "finnish" VALUES(84,11,'84:11','hän pyytää itselleen kuolemaa,');
INSERT INTO "finnish" VALUES(84,12,'84:12','mutta hän on astuva helvetin tuleen.');
INSERT INTO "finnish" VALUES(84,13,'84:13','Hän eli kyllä iloisena omiensa keskuudessa,');
INSERT INTO "finnish" VALUES(84,14,'84:14','hän totisesti uskoi, ettei koskaan enää palaisi (haudasta).');
INSERT INTO "finnish" VALUES(84,15,'84:15','Mutta hän olikin alati Herransa näköpiirissä.');
INSERT INTO "finnish" VALUES(84,16,'84:16','Vannon iltaruskon nimessä,');
INSERT INTO "finnish" VALUES(84,17,'84:17','yön nimessä ja sen, mitä yössä liikkuu,');
INSERT INTO "finnish" VALUES(84,18,'84:18','ja kuun nimessä, kun se täydeksi kasvaa:');
INSERT INTO "finnish" VALUES(84,19,'84:19','»Teidän on totisesti siirryttävä olotilasta toiseen.»');
INSERT INTO "finnish" VALUES(84,20,'84:20','Mutta miksi he eivät usko?');
INSERT INTO "finnish" VALUES(84,21,'84:21','Eivätkä kumarra, kun Koraania luetaan heille.');
INSERT INTO "finnish" VALUES(84,22,'84:22','Niin, epäuskoiset pitävät totuutta valheena,');
INSERT INTO "finnish" VALUES(84,23,'84:23','mutta Jumala tuntee parhaiten heidän sydäntensä kätköt.');
INSERT INTO "finnish" VALUES(84,24,'84:24','Julista siis heille sanomaa tuskallisesta kurituksesta!');
INSERT INTO "finnish" VALUES(84,25,'84:25','Mutta ei niille, jotka uskovat ja tekevät hyviä töitä; heitä odottaa ehtymätön palkka.');
INSERT INTO "finnish" VALUES(85,1,'85:1','Nimessä taivaan, joka on täynnä tähtisikermiä,');
INSERT INTO "finnish" VALUES(85,2,'85:2','nimessä ennustetun päivän,');
INSERT INTO "finnish" VALUES(85,3,'85:3','nimessä todistajan ja todistetun,');
INSERT INTO "finnish" VALUES(85,4,'85:4','kirotut olkoot tulikuopan miehet');
INSERT INTO "finnish" VALUES(85,5,'85:5','polttavan tulen äärellä,');
INSERT INTO "finnish" VALUES(85,6,'85:6','kun he istuivat siinä');
INSERT INTO "finnish" VALUES(85,7,'85:7','ja katselivat, mikä on uskovien osa.');
INSERT INTO "finnish" VALUES(85,8,'85:8','He kantoivat kaunaa näitä vastaan vain sentähden, että nämä uskoivat Jumalaan, kaikkivoipaan, ylistettyyn,');
INSERT INTO "finnish" VALUES(85,9,'85:9','Häneen, jonka on valta taivaassa ja maan päällä; niin, Jumala on kaiken silminnäkijä.');
INSERT INTO "finnish" VALUES(85,10,'85:10','Totisesti niitä, jotka vainoavat uskovia, miehiä ja naisia, eivätkä tee parannusta, odottaa helvetin kuritus; niin, heitä odottaa tulen tuska.');
INSERT INTO "finnish" VALUES(85,11,'85:11','Mutta niitä, jotka uskovat ja tekevät hyviä töitä, totisesti odottavat puutarhat vuolaine virtoineen. Tämä on suuri autuus.');
INSERT INTO "finnish" VALUES(85,12,'85:12','Totisesti Herrasi voima on väkevä:');
INSERT INTO "finnish" VALUES(85,13,'85:13','Hänen vallassaan on luominen, Hän herättää jälleen elämään.');
INSERT INTO "finnish" VALUES(85,14,'85:14','Niin, Hän on anteeksiantava, rakastava.');
INSERT INTO "finnish" VALUES(85,15,'85:15','Hallitsevan voiman Herra, ylevä,');
INSERT INTO "finnish" VALUES(85,16,'85:16','mahtava tahtonsa toteuttaja.');
INSERT INTO "finnish" VALUES(85,17,'85:17','Etkö ole kuullut kertomusta sotajoukoista,');
INSERT INTO "finnish" VALUES(85,18,'85:18','jotka faraolla ja Tamuudilla oli?');
INSERT INTO "finnish" VALUES(85,19,'85:19','Niin, epäuskoiset paatuvat hylkimisessään,');
INSERT INTO "finnish" VALUES(85,20,'85:20','mutta Jumala saartaa heidät joka puolelta.');
INSERT INTO "finnish" VALUES(85,21,'85:21','Totisesti tämä on ylevä Koraani,');
INSERT INTO "finnish" VALUES(85,22,'85:22','joka tauluille kirjoitettuna on hyvin säilynyt.');
INSERT INTO "finnish" VALUES(86,1,'86:1','Kautta taivaan ja aamutähden!');
INSERT INTO "finnish" VALUES(86,2,'86:2','Tiedätkö, mikä on aamutähti?');
INSERT INTO "finnish" VALUES(86,3,'86:3','Se on häikäisevän kirkas tähti.');
INSERT INTO "finnish" VALUES(86,4,'86:4','Ei ole sielua, jolla ei olisi suojelijaansa.');
INSERT INTO "finnish" VALUES(86,5,'86:5','Miettiköön ihminen, mistä hänet on luotu!');
INSERT INTO "finnish" VALUES(86,6,'86:6','Hänet on luotu pienestä, pursuavasta pisarasta,');
INSERT INTO "finnish" VALUES(86,7,'86:7','joka on lähtöisin kupeiden ja rintakehän välistä.');
INSERT INTO "finnish" VALUES(86,8,'86:8','Totisesti, Jumala voi herättää hänet jälleen eloon');
INSERT INTO "finnish" VALUES(86,9,'86:9','päivänä, jona kaikki, mikä salassa on, tulee ilmi.');
INSERT INTO "finnish" VALUES(86,10,'86:10','Eikä hänellä ole silloin voimaa eikä auttajaa.');
INSERT INTO "finnish" VALUES(86,11,'86:11','Nimessä taivaan, josta sateet tulevat,');
INSERT INTO "finnish" VALUES(86,12,'86:12','ja maan, joka avaa uumenensa!');
INSERT INTO "finnish" VALUES(86,13,'86:13','Totisesti tämä on ratkaiseva sana,');
INSERT INTO "finnish" VALUES(86,14,'86:14','eikä mitään joutavaa puhetta.');
INSERT INTO "finnish" VALUES(86,15,'86:15','Totisesti heillä on juonensa,');
INSERT INTO "finnish" VALUES(86,16,'86:16','mutta Minulla myös on suunnitelmani.');
INSERT INTO "finnish" VALUES(86,17,'86:17','Anna siis aikaa uskottomille; myönnä heille aikaa hetkiseksi!');
INSERT INTO "finnish" VALUES(87,1,'87:1','Ylistä Herrasi, Korkeimman, nimeä,');
INSERT INTO "finnish" VALUES(87,2,'87:2','Hänen, joka luo ja tekee täydelliseksi,');
INSERT INTO "finnish" VALUES(87,3,'87:3','joka määrää ja ohjaa,');
INSERT INTO "finnish" VALUES(87,4,'87:4','joka kasvattaa viheriän laidunruohon');
INSERT INTO "finnish" VALUES(87,5,'87:5','ja kuivattaa sen jälleen tomun harmaaksi.');
INSERT INTO "finnish" VALUES(87,6,'87:6','Me annamme sinun julkilukea (Koraania), etkä ole mitään unohtava,');
INSERT INTO "finnish" VALUES(87,7,'87:7','paitsi mitä Jumala tahtoo: totisesti Hän tietää, mitä ilmaistaan ja mikä pidetään salassa.');
INSERT INTO "finnish" VALUES(87,8,'87:8','Tahdomme tasoittaa sinun lähetystiesi.');
INSERT INTO "finnish" VALUES(87,9,'87:9','Käy siis eteenpäin varoittaen, totisesti koituu varoituksesta hyvää:');
INSERT INTO "finnish" VALUES(87,10,'87:10','se, joka (Jumalaa) pelkää, ottaa siitä vaarin,');
INSERT INTO "finnish" VALUES(87,11,'87:11','mutta viheliäinen karttaa sitä,');
INSERT INTO "finnish" VALUES(87,12,'87:12','hän joutuu ankarimpaan tuleen,');
INSERT INTO "finnish" VALUES(87,13,'87:13','jossa hän ei elä eikä kuole.');
INSERT INTO "finnish" VALUES(87,14,'87:14','Mutta onnellinen se, joka tekee parannuksen,');
INSERT INTO "finnish" VALUES(87,15,'87:15','ylistää Herransa nimeä ja rukoilee.');
INSERT INTO "finnish" VALUES(87,16,'87:16','Mutta te pidätte parempana tämän maailman elämää,');
INSERT INTO "finnish" VALUES(87,17,'87:17','vaikka tuleva elämä on parempi ja kestävämpi.');
INSERT INTO "finnish" VALUES(87,18,'87:18','Näin on totisesti vanhimmissa kirjoituksissa,');
INSERT INTO "finnish" VALUES(87,19,'87:19','Aabrahamin ja Mooseksen kirjoissa.');
INSERT INTO "finnish" VALUES(88,1,'88:1','Oletko kuullut sanoman musertavasta tapahtumasta?');
INSERT INTO "finnish" VALUES(88,2,'88:2','Sinä päivänä toisten kasvot nöyrtyvät;');
INSERT INTO "finnish" VALUES(88,3,'88:3','väsyen ja nääntyen');
INSERT INTO "finnish" VALUES(88,4,'88:4','he käyvät polttavaan tuleen');
INSERT INTO "finnish" VALUES(88,5,'88:5','ja juovat kiehuvasta lähteestä');
INSERT INTO "finnish" VALUES(88,6,'88:6','saamatta muuta ruokaa kuin ohdakkeista,');
INSERT INTO "finnish" VALUES(88,7,'88:7','joka ei lihota eikä vie nälkää. -');
INSERT INTO "finnish" VALUES(88,8,'88:8','Samana päivänä toisten kasvot loistavat,');
INSERT INTO "finnish" VALUES(88,9,'88:9','ja he ovat tyytyväiset ponnisteluihinsa,');
INSERT INTO "finnish" VALUES(88,10,'88:10','kun saapuvat ylhäisiin puutarhoihin,');
INSERT INTO "finnish" VALUES(88,11,'88:11','joissa eivät kuule turhuuden sanoja.');
INSERT INTO "finnish" VALUES(88,12,'88:12','Siellä on pulppuavia lähteitä,');
INSERT INTO "finnish" VALUES(88,13,'88:13','siellä on korkeita valtaistuimia,');
INSERT INTO "finnish" VALUES(88,14,'88:14','täysiä maljoja,');
INSERT INTO "finnish" VALUES(88,15,'88:15','pieluksia pitkin rivein');
INSERT INTO "finnish" VALUES(88,16,'88:16','ja levitettyjä mattoja.');
INSERT INTO "finnish" VALUES(88,17,'88:17','Eivätkö he näe, kuinka pilvet on luotu,');
INSERT INTO "finnish" VALUES(88,18,'88:18','kuinka taivas on kaartunut korkeaksi,');
INSERT INTO "finnish" VALUES(88,19,'88:19','kuinka vuoret on pystytetty');
INSERT INTO "finnish" VALUES(88,20,'88:20','ja kuinka maa on avaraksi levitetty?');
INSERT INTO "finnish" VALUES(88,21,'88:21','Jatka siis varoituksiasi, sinä olet juuri varoittaja,');
INSERT INTO "finnish" VALUES(88,22,'88:22','etkä ole heidän kaitsijansa.');
INSERT INTO "finnish" VALUES(88,23,'88:23','Mutta sitä, joka kääntää selkänsä ja jää epäuskoonsa,');
INSERT INTO "finnish" VALUES(88,24,'88:24','Jumala kurittaa suurella kärsimyksellä:');
INSERT INTO "finnish" VALUES(88,25,'88:25','totisesti he palaavat Meidän tykömme,');
INSERT INTO "finnish" VALUES(88,26,'88:26','ja Meille on heidän tili tehtävä.');
INSERT INTO "finnish" VALUES(89,1,'89:1','Kautta päivänkoiton');
INSERT INTO "finnish" VALUES(89,2,'89:2','ja kymmenen yön,');
INSERT INTO "finnish" VALUES(89,3,'89:3','kautta (juhlapäivien) luvun ja epäluvun,');
INSERT INTO "finnish" VALUES(89,4,'89:4','kautta yön, jolloin lähdetään (kotimatkalle).');
INSERT INTO "finnish" VALUES(89,5,'89:5','Eikö tässä kaikessa ole minkä nimeen vannoa, jos kuka sen tajuaa?');
INSERT INTO "finnish" VALUES(89,6,'89:6','Etkö ole miettinyt sitä, mitä Herrasi teki Aadille');
INSERT INTO "finnish" VALUES(89,7,'89:7','ja Aramin heimolle, jonka rakennuksia tukivat ylväät pilarit,');
INSERT INTO "finnish" VALUES(89,8,'89:8','sellaiset, joiden vertaisia ei rakennettu muissa kaupungeissa,');
INSERT INTO "finnish" VALUES(89,9,'89:9','(ja mitä tehtiin) Tamuudin kansalle, joka laaksossaan hakkasi asumuksensa suunnattomiin kallioihin');
INSERT INTO "finnish" VALUES(89,10,'89:10','ja faraolle, sotajoukkojen valtiaalle,');
INSERT INTO "finnish" VALUES(89,11,'89:11','noille kaikille,jotka harjoittivat irstaisuutta kaupungeissaan');
INSERT INTO "finnish" VALUES(89,12,'89:12','ja saivat aikaan paljon pahaa.');
INSERT INTO "finnish" VALUES(89,13,'89:13','Sentähden Herrasi lähetti heille kurituksen;');
INSERT INTO "finnish" VALUES(89,14,'89:14','totisesti Herrasi seuraa valppaasti teidänkin menoanne.');
INSERT INTO "finnish" VALUES(89,15,'89:15','Kun ihmisen Herra koettelee häntä antamalla hänelle kunniaa ja osoittamalla hänelle hyvyyttä, niin hän sanoo: »Herra pitää minut kunniassa.»');
INSERT INTO "finnish" VALUES(89,16,'89:16','Mutta kun Hän koettelee häntä toisin, niukentaen hänen elatustaan, silloin hän sanoo: »Herra halveksii minua.»');
INSERT INTO "finnish" VALUES(89,17,'89:17','Ei, te ette pidä orpoa missään arvossa');
INSERT INTO "finnish" VALUES(89,18,'89:18','ettekä kehoita toisianne ruokkimaan köyhiä,');
INSERT INTO "finnish" VALUES(89,19,'89:19','vaan te syötte olemattomiin kaikki heidän perintönsä ettekä mistään pidä väliä ahmiessanne,');
INSERT INTO "finnish" VALUES(89,20,'89:20','ja te rakastatte ylellisyyttä koko sydämestänne.');
INSERT INTO "finnish" VALUES(89,21,'89:21','Ei, kun tärähdykset panevat maan luhistumaan,');
INSERT INTO "finnish" VALUES(89,22,'89:22','kun Herra ilmestyy enkeliparvien seuraamana');
INSERT INTO "finnish" VALUES(89,23,'89:23','ja paljastaa helvetin, sinä päivänä ihminen noudattaisi kehoitusta, mutta miksi hyödyksi on hänelle totteleminen enää?');
INSERT INTO "finnish" VALUES(89,24,'89:24','Hän sanoo: »Voi, jospa olisin huolehtinut tulevasta elämästäni!»');
INSERT INTO "finnish" VALUES(89,25,'89:25','Mutta tuona päivänä ei kukaan rankaise niinkuin Hän,');
INSERT INTO "finnish" VALUES(89,26,'89:26','ei kukaan sido niinkuin Hän.');
INSERT INTO "finnish" VALUES(89,27,'89:27','Sinä levon löytänyt sielu,');
INSERT INTO "finnish" VALUES(89,28,'89:28','palaa Herrasi luo saadaksesi Hänestä lohtusi ja ollaksesi Hänelle otollinen,');
INSERT INTO "finnish" VALUES(89,29,'89:29','astu siis Minun palvelijoitteni joukkoon,');
INSERT INTO "finnish" VALUES(89,30,'89:30','Niin, astu autuuden tarhaan!');
INSERT INTO "finnish" VALUES(90,1,'90:1','Kautta tämän kaupungin');
INSERT INTO "finnish" VALUES(90,2,'90:2','jossa sinä asut');
INSERT INTO "finnish" VALUES(90,3,'90:3','-kautta sukumme Alkuunpanijan ja hänestä syntyneen!');
INSERT INTO "finnish" VALUES(90,4,'90:4','Me olemme luonut ihmisen todellakin elämään vaikeuksissa:');
INSERT INTO "finnish" VALUES(90,5,'90:5','jottei hän luulisi, että kenelläkään ei ole valtaa hänen suhteensa.');
INSERT INTO "finnish" VALUES(90,6,'90:6','Hän sanoo: »Olen tuhlannut paljon varoja» (Profeetan vastustamiseen).');
INSERT INTO "finnish" VALUES(90,7,'90:7','Luuleeko hän, ettei kukaan näe häntä?');
INSERT INTO "finnish" VALUES(90,8,'90:8','Emmekö ole antanut hänelle kaksi silmää,');
INSERT INTO "finnish" VALUES(90,9,'90:9','kielen ja huulet?');
INSERT INTO "finnish" VALUES(90,10,'90:10','ja tehnyt hänelle selväksi kummankin (hyvän ja pahan) tien?');
INSERT INTO "finnish" VALUES(90,11,'90:11','Mutta hän ei yrittänyt nousta kaitaa polkua.');
INSERT INTO "finnish" VALUES(90,12,'90:12','Miten saan sinut käsittämään, mikä kaita polku on?');
INSERT INTO "finnish" VALUES(90,13,'90:13','Se on samaa kuin vapauttaa orja');
INSERT INTO "finnish" VALUES(90,14,'90:14','tai ruokkia nälän päivinä');
INSERT INTO "finnish" VALUES(90,15,'90:15','orpoa ja sukulaista');
INSERT INTO "finnish" VALUES(90,16,'90:16','tai maahan vaipunutta köyhää miestä;');
INSERT INTO "finnish" VALUES(90,17,'90:17','se on samaa kuin niihin kuuluminen, jotka uskovat ja kehoittavat toisiaan kestävyyteen ja laupeuteen.');
INSERT INTO "finnish" VALUES(90,18,'90:18','Nämä ovat niitä, jotka vaeltavat oikealla puolella.');
INSERT INTO "finnish" VALUES(90,19,'90:19','Mutta ne, jotka hylkivät julistamaamme totuutta, kulkevat vasempaa puolta,');
INSERT INTO "finnish" VALUES(90,20,'90:20','ja heidät piirittää tuli.');
INSERT INTO "finnish" VALUES(91,1,'91:1','Kautta auringon ja sen kirkkauden,');
INSERT INTO "finnish" VALUES(91,2,'91:2','kautta valonsa lainaavan kuun,');
INSERT INTO "finnish" VALUES(91,3,'91:3','kautta päivän, joka paljastaa kaiken näkyväisen,');
INSERT INTO "finnish" VALUES(91,4,'91:4','kautta yön, joka sen verhoonsa peittää,');
INSERT INTO "finnish" VALUES(91,5,'91:5','kautta taivaan ja Hänen, joka sen rakensi,');
INSERT INTO "finnish" VALUES(91,6,'91:6','kautta maan ja Hänen, joka sen laajaksi levitti,');
INSERT INTO "finnish" VALUES(91,7,'91:7','kautta sielun ja Hänen, joka teki sen täydelliseksi');
INSERT INTO "finnish" VALUES(91,8,'91:8','ja ilmaisi sille, mikä turmio on totuuden hylkäämisestä ja mikä menestys pahan karttamisesta!');
INSERT INTO "finnish" VALUES(91,9,'91:9','Totisesti autuas on se, joka säilyttää sielunsa puhtaana,');
INSERT INTO "finnish" VALUES(91,10,'91:10','ja totisesti on tuhon oma se, joka sen turmelee!');
INSERT INTO "finnish" VALUES(91,11,'91:11','Tamuud teki syntiä ja väitti totuutta valheeksi.');
INSERT INTO "finnish" VALUES(91,12,'91:12','Kun kurjimmat heidän joukostaan ryhtyivät riettauksiinsa,');
INSERT INTO "finnish" VALUES(91,13,'91:13','silloin Jumalan sananjulistaja puhui heille Jumalan naaraskameelista ja sen juottamisesta,');
INSERT INTO "finnish" VALUES(91,14,'91:14','mutta he kohtelivat häntä kuin valehtelijaa ja katkoivat kameelilta jäsenet, ja heidän Herransa kukisti heidät heidän syntiensä tähden ja tuhosi heidät maan tasalle,');
INSERT INTO "finnish" VALUES(91,15,'91:15','eikä Hän pelännyt seurauksia.');
INSERT INTO "finnish" VALUES(92,1,'92:1','Kautta verhoonsa vetäytyvän yön,');
INSERT INTO "finnish" VALUES(92,2,'92:2','kautta kirkastuvan päivän,');
INSERT INTO "finnish" VALUES(92,3,'92:3','kautta ihmisen luomisen mieheksi ja naiseksi:');
INSERT INTO "finnish" VALUES(92,4,'92:4','totisesti ovat pyrkimyksemme erisuuntaisia.');
INSERT INTO "finnish" VALUES(92,5,'92:5','Joka on antelias ja karttaa pahaa');
INSERT INTO "finnish" VALUES(92,6,'92:6','sekä rakastaa hyvää,');
INSERT INTO "finnish" VALUES(92,7,'92:7','häntä autamme vähitellen saavuttamaan onnellisen päämäärän,');
INSERT INTO "finnish" VALUES(92,8,'92:8','mutta toisin on sen, joka on kitsas, itseänsä täynnä,');
INSERT INTO "finnish" VALUES(92,9,'92:9','eikä hyveestä välitä,');
INSERT INTO "finnish" VALUES(92,10,'92:10','hänen annamme vähitellen joutua pahan valtaan,');
INSERT INTO "finnish" VALUES(92,11,'92:11','eikä hänen rikkautensa hyödytä häntä vähääkään, kun hänet tuhotaan.');
INSERT INTO "finnish" VALUES(92,12,'92:12','Totisesti on Meistä lähtöisin ohjaus oikealle tielle,');
INSERT INTO "finnish" VALUES(92,13,'92:13','Meidän on varmasti sekä tulevaisuus että menneisyys,');
INSERT INTO "finnish" VALUES(92,14,'92:14','sentähden olen varoittanut teitä leimuavasta tulesta,');
INSERT INTO "finnish" VALUES(92,15,'92:15','johon joutuvat vain pahat,');
INSERT INTO "finnish" VALUES(92,16,'92:16','jotka vääristelevät totuutta ja kääntävät sille selkänsä.');
INSERT INTO "finnish" VALUES(92,17,'92:17','Mutta Jumalaa pelkäävä pelastuu siitä,');
INSERT INTO "finnish" VALUES(92,18,'92:18','hän, joka antaa pois rikkautensa puhdistuakseen,');
INSERT INTO "finnish" VALUES(92,19,'92:19','eikä tee hyvää palkkion toivossa,');
INSERT INTO "finnish" VALUES(92,20,'92:20','vaan etsiäkseen Herransa, korkeimman, mielisuosiota.');
INSERT INTO "finnish" VALUES(92,21,'92:21','Totisesti, hän on aikanaan saava palkintonsa.');
INSERT INTO "finnish" VALUES(93,1,'93:1','Kautta päivän varhaishetkien,');
INSERT INTO "finnish" VALUES(93,2,'93:2','kautta yön, joka levittää pimeyttään!');
INSERT INTO "finnish" VALUES(93,3,'93:3','Herrasi ei ole sinua hylännyt eikä ole sinuun tyytymätön;');
INSERT INTO "finnish" VALUES(93,4,'93:4','totisesti on se, mikä tulee, sinulle parempaa kuin se, mikä on mennyttä,');
INSERT INTO "finnish" VALUES(93,5,'93:5','ja totisesti on Herrasi aikanaan oleva sinulle antelias, niin että olet tyytyväinen.');
INSERT INTO "finnish" VALUES(93,6,'93:6','Eikö Hän löytänyt sinut orpona ja antanut sinulle suojaa?');
INSERT INTO "finnish" VALUES(93,7,'93:7','Hän löysi sinut eksyksistä ja ohjasi sinua;');
INSERT INTO "finnish" VALUES(93,8,'93:8','Hän tapasi sinut köyhissä oloissa ja teki sinusta varakkaan miehen.');
INSERT INTO "finnish" VALUES(93,9,'93:9','Mitä siis orpoon tulee, älä häntä sorra,');
INSERT INTO "finnish" VALUES(93,10,'93:10','mitä kerjäläiseen, älä häntä nuhtele,');
INSERT INTO "finnish" VALUES(93,11,'93:11','ja mitä Herrasi siunaukseen, kerro siitä edelleenkin.');
INSERT INTO "finnish" VALUES(94,1,'94:1','Emmekö Me ole avartanut rintaasi?');
INSERT INTO "finnish" VALUES(94,2,'94:2','Emmekö ole sinulta ottanut pois kuormasi,');
INSERT INTO "finnish" VALUES(94,3,'94:3','joka raskaana painoi hartioillasi,');
INSERT INTO "finnish" VALUES(94,4,'94:4','ja korottanut sinun arvoasi?');
INSERT INTO "finnish" VALUES(94,5,'94:5','Totisesti, helpotus seuraa vastuksia,');
INSERT INTO "finnish" VALUES(94,6,'94:6','ja vastoinkäymistä seuraa huojennus.');
INSERT INTO "finnish" VALUES(94,7,'94:7','Kun siis olet vapaa työstä, rukoile ahkerasti');
INSERT INTO "finnish" VALUES(94,8,'94:8','ja palvele Herraasi koko sydämestäsi.');
INSERT INTO "finnish" VALUES(95,1,'95:1','Kautta viikunapuun ja oliivin,');
INSERT INTO "finnish" VALUES(95,2,'95:2','kautta Siinain vuoren');
INSERT INTO "finnish" VALUES(95,3,'95:3','ja tämän pyhitetyn kaupungin.');
INSERT INTO "finnish" VALUES(95,4,'95:4','Totisesti Me loimme ihmisen parhaaseen muotoonsa,');
INSERT INTO "finnish" VALUES(95,5,'95:5','mutta Me kyllä alennammekin hänet alhaista alemmaksi,');
INSERT INTO "finnish" VALUES(95,6,'95:6','lukuunottomatta niitä, jotka uskovat ja hyvää tekevät; niitä odottaa ehtymätön palkka.');
INSERT INTO "finnish" VALUES(95,7,'95:7','Mikä siis saa sinut pitämään tulevaa tuomiota valheena?');
INSERT INTO "finnish" VALUES(95,8,'95:8','Eikö Jumala ole tuomareista parhain?');
INSERT INTO "finnish" VALUES(96,1,'96:1','Lue julki Herrasi nimeen, joka on luonut,');
INSERT INTO "finnish" VALUES(96,2,'96:2','luonut ihmisen hyytyneestä verestä.');
INSERT INTO "finnish" VALUES(96,3,'96:3','Lue! Sillä Herrasi on ylevin;');
INSERT INTO "finnish" VALUES(96,4,'96:4','Hän, joka on opettanut käyttämään kynää,');
INSERT INTO "finnish" VALUES(96,5,'96:5','opettanut ihmiselle sen, mistä tämä ei ennen tiennyt.');
INSERT INTO "finnish" VALUES(96,6,'96:6','Totisesti ihminen tekee syntiä,');
INSERT INTO "finnish" VALUES(96,7,'96:7','koska hän kuvittelee elävänsä omista ansioistaan,');
INSERT INTO "finnish" VALUES(96,8,'96:8','mutta totisesti, tie johtaa hänet jälleen Herransa luo.');
INSERT INTO "finnish" VALUES(96,9,'96:9','Oletko nähnyt sitä, joka kieltää');
INSERT INTO "finnish" VALUES(96,10,'96:10','(Jumalan) palvelijaa rukoilemasta?');
INSERT INTO "finnish" VALUES(96,11,'96:11','Oletko nähnyt, seuraako hän itse johdatusta?');
INSERT INTO "finnish" VALUES(96,12,'96:12','Tai kehoittaako hän jumalanpelkoon?');
INSERT INTO "finnish" VALUES(96,13,'96:13','Oletko huomannut hänen väittävän totuutta valheeksi ja kääntävän sille selkänsä?');
INSERT INTO "finnish" VALUES(96,14,'96:14','Eikö hän tiedä, että Jumala totisesti kaikki näkee?');
INSERT INTO "finnish" VALUES(96,15,'96:15','Ellei hän herkeä sellaisesta, iskemme häntä otsaan,');
INSERT INTO "finnish" VALUES(96,16,'96:16','valheelliseen, syntiseen otsaan.');
INSERT INTO "finnish" VALUES(96,17,'96:17','Kutsukoon hän sitten apureitansa!');
INSERT INTO "finnish" VALUES(96,18,'96:18','Me kutsumme myös soturimme.');
INSERT INTO "finnish" VALUES(96,19,'96:19','Ei! Älä tottele häntä, vaan nöyrry ja lähesty Jumalaa!');
INSERT INTO "finnish" VALUES(97,1,'97:1','Totisesti olemme ilmaissut sen siunattuna yönä.');
INSERT INTO "finnish" VALUES(97,2,'97:2','Mistäpä tietäisit, mikä Voiman yö on !');
INSERT INTO "finnish" VALUES(97,3,'97:3','Siunattu yö on arvokkaampi kuin tuhat kuukautta.');
INSERT INTO "finnish" VALUES(97,4,'97:4','Silloin laskeutuvat enkelit ja suuri henki Herransa käskyn mukaan kaikenlaisiin tehtäviin.');
INSERT INTO "finnish" VALUES(97,5,'97:5','Silloin vallitsee rauha ja kestää aamunkoittoon saakka.');
INSERT INTO "finnish" VALUES(98,1,'98:1','Uskottomat sekä Kirjoituksen saaneiden että pakanain keskuudessa eivät epäilleet, ennenkuin tuli selvä todistus,');
INSERT INTO "finnish" VALUES(98,2,'98:2','Jumalan lähetti, joka luki heille väärentämättömät kirjoitukset,');
INSERT INTO "finnish" VALUES(98,3,'98:3','jotka sisältävät oikeat määräykset.');
INSERT INTO "finnish" VALUES(98,4,'98:4','Ja ne, joille Kirjoitus oli annettu, jakaantuivat lahkokuntiin vasta, kun selvä todistus oli tullut heidän osakseen,');
INSERT INTO "finnish" VALUES(98,5,'98:5','eikä heille annettu muuta käskyä kuin palvella Jumalaa, olla kuuliaisia yksin Hänelle, vilpittöminä suorittaa rukoukset ja uhrata säädetyt almut, sillä tämä on oikea usko.');
INSERT INTO "finnish" VALUES(98,6,'98:6','Totisesti ne, jotka eivät usko, sekä Kirjoituksen saaneet että pakanat, joutuvat helvetin tuleen ja jäävät sinne ikuisiksi ajoiksi. Nämä ovat ihmisistä kurjimpia.');
INSERT INTO "finnish" VALUES(98,7,'98:7','Ne, jotka uskovat ja tekevät hyviä töitä, ovat totisesti parhaita olentoja.');
INSERT INTO "finnish" VALUES(98,8,'98:8','Heidän palkkansa heidän Herransa luona ovat ikiautuuden puutarhat, joita virrat vilvoittavat ja joissa he saavat asua ikuisesti. Heillä on Jumalan mielisuosio, ja heidän onnensa on Jumalassa. Tämä on jokaista varten, joka pelkää Herraansa.');
INSERT INTO "finnish" VALUES(99,1,'99:1','Kun maa järkkyy omasta järinästään,');
INSERT INTO "finnish" VALUES(99,2,'99:2','kun se purkaa esiin kuormansa');
INSERT INTO "finnish" VALUES(99,3,'99:3','ja kun ihminen kysyy: »Mikä sitä vaivaa?»');
INSERT INTO "finnish" VALUES(99,4,'99:4','sinä päivänä on se sanova sanomansa,');
INSERT INTO "finnish" VALUES(99,5,'99:5','kun Herrasi on antanut siihen käskyn;');
INSERT INTO "finnish" VALUES(99,6,'99:6','sinä päivänä astuvat (kuolleet) ihmiset esiin erilaisissa ryhmissä näkemään tekojaan.');
INSERT INTO "finnish" VALUES(99,7,'99:7','Silloin se, joka on tehnyt hyvää hiukkasenkin verran, on sen näkevä,');
INSERT INTO "finnish" VALUES(99,8,'99:8','ja se, joka on tehnyt pahaa hiukkasenkin verran, saa sen havaita.');
INSERT INTO "finnish" VALUES(100,1,'100:1','Kautta korskuvien sotaratsujen,');
INSERT INTO "finnish" VALUES(100,2,'100:2','joiden kavioniskuista säkenöi tulta,');
INSERT INTO "finnish" VALUES(100,3,'100:3','jotka riuhtoutuvat hyökkäykseen aamulla,');
INSERT INTO "finnish" VALUES(100,4,'100:4','nostattavat tomupilven ilmaan');
INSERT INTO "finnish" VALUES(100,5,'100:5','ja syöksyvät sotalaumojen joukkoon,');
INSERT INTO "finnish" VALUES(100,6,'100:6','totisesti, ihminen on kiittämätön Herraansa kohtaan,');
INSERT INTO "finnish" VALUES(100,7,'100:7','hänen on itsensä myönnettävä se.');
INSERT INTO "finnish" VALUES(100,8,'100:8','Miten ahnaasti hän himoitseekaan maallista tavaraa!');
INSERT INTO "finnish" VALUES(100,9,'100:9','Eikö hän tiedä, että tulee hetki, jolloin kaikki haudoista herätetään,');
INSERT INTO "finnish" VALUES(100,10,'100:10','jolloin päivänvaloon tulee kaikki, mikä oli poven kätkössä.');
INSERT INTO "finnish" VALUES(100,11,'100:11','Sinä päivänä heidän Herransa totisesti tuomitsee heidät tietonsa mukaan.');
INSERT INTO "finnish" VALUES(101,1,'101:1','Vyöryvä tuho!');
INSERT INTO "finnish" VALUES(101,2,'101:2','Miten hirveä on vyöryvä tuho!');
INSERT INTO "finnish" VALUES(101,3,'101:3','Ymmärrätkö, kuinka kauhea on vyöryvä tuho?');
INSERT INTO "finnish" VALUES(101,4,'101:4','Päivä, jolloin ihmiset ovat kuin hajoitettu sääksien parvi,');
INSERT INTO "finnish" VALUES(101,5,'101:5','ja vuoret kuin keritty villa.');
INSERT INTO "finnish" VALUES(101,6,'101:6','Sen osana, jonka hyvien tekojen mitta on suuri,');
INSERT INTO "finnish" VALUES(101,7,'101:7','on elämä täynnä tyydytystä,');
INSERT INTO "finnish" VALUES(101,8,'101:8','mutta toisin käy sen, jonka hyvät teot vaa''assa keveiksi havaitaan,');
INSERT INTO "finnish" VALUES(101,9,'101:9','hänen asumuksensa on oleva horna.');
INSERT INTO "finnish" VALUES(101,10,'101:10','Tiedätkö, mikä horna on?');
INSERT INTO "finnish" VALUES(101,11,'101:11','Se on polttava tuli.');
INSERT INTO "finnish" VALUES(102,1,'102:1','Rikkauksien kartuttaminen vieroittaa mielenne (uskosta)');
INSERT INTO "finnish" VALUES(102,2,'102:2','aina siihen saakka, kunnes eteenne avautuu hauta.');
INSERT INTO "finnish" VALUES(102,3,'102:3','Totisesti saatte sen nähdä!');
INSERT INTO "finnish" VALUES(102,4,'102:4','Ja vieläkin kerran te saatte sen nähdä.');
INSERT INTO "finnish" VALUES(102,5,'102:5','Jospa olisitte sen varmuudella tietäneet,');
INSERT INTO "finnish" VALUES(102,6,'102:6','olisitte ilmielävästi nähneet helvetin.');
INSERT INTO "finnish" VALUES(102,7,'102:7','Vielä kerran, totisesti saatte nähdä sen omin silmin.');
INSERT INTO "finnish" VALUES(102,8,'102:8','Silloin, tuona päivänä, teiltä toden totta vaaditaan (aikanaan) annettuja almuja.');
INSERT INTO "finnish" VALUES(103,1,'103:1','Kautta kuluvan ajan,');
INSERT INTO "finnish" VALUES(103,2,'103:2','ihminen on totisesti mennyttä,');
INSERT INTO "finnish" VALUES(103,3,'103:3','lukuunottomatta niitä, jotka uskovat ja hyvää tekevät, soveltavat toisiinsa totuutta ja kehoittavat toisiaan kestävyyteen.');
INSERT INTO "finnish" VALUES(104,1,'104:1','Voi jokaista parjaajaa, panettelijaa,');
INSERT INTO "finnish" VALUES(104,2,'104:2','joka kerää aarteita ja pitää niitä turvanaan,');
INSERT INTO "finnish" VALUES(104,3,'104:3','luullen, että hänen rikkautensa on pysyväistä!');
INSERT INTO "finnish" VALUES(104,4,'104:4','Hänet totisesti singotaan murskaavan surman suuhun.');
INSERT INTO "finnish" VALUES(104,5,'104:5','Käsitätkö, mikä tuo surma on?');
INSERT INTO "finnish" VALUES(104,6,'104:6','Tuli, jonka Jumala on sytyttänyt,');
INSERT INTO "finnish" VALUES(104,7,'104:7','joka leimahtaa syntisiä vastaan.');
INSERT INTO "finnish" VALUES(104,8,'104:8','Se on totisesti piirittävä heidät ylt''ympäri');
INSERT INTO "finnish" VALUES(104,9,'104:9','kuin korkeat pilarit.');
INSERT INTO "finnish" VALUES(105,1,'105:1','Etkö ole Miettinyt mitä Herrasi teki niille, jotka norsuilla hyökkäsivät?');
INSERT INTO "finnish" VALUES(105,2,'105:2','Eikö Hän saattanut sekasortoon heidän sotaretkensä?');
INSERT INTO "finnish" VALUES(105,3,'105:3','Ja lähettänyt heitä vastaan lintuparvia,');
INSERT INTO "finnish" VALUES(105,4,'105:4','jotka sinkoutuivat heihin kovien kivien lailla.');
INSERT INTO "finnish" VALUES(105,5,'105:5','Näin Hän teki heistä kuin madonsyömää pellonsänkeä.');
INSERT INTO "finnish" VALUES(106,1,'106:1','(Näin tapahtui) suosiosta Kureishien heimoa kohtaan,');
INSERT INTO "finnish" VALUES(106,2,'106:2','suojelukseksi heille matkoiltaan talvella ja kesällä.');
INSERT INTO "finnish" VALUES(106,3,'106:3','Palvelkoot he siis tämän huoneen Herraa,');
INSERT INTO "finnish" VALUES(106,4,'106:4','joka sammuttaa heidän nälkänsä ja antaa turvan tunteen heidän pelätessään.');
INSERT INTO "finnish" VALUES(107,1,'107:1','Mitä ajattelet miehestä, joka sanoo tulevaa tuomiota valheeksi?');
INSERT INTO "finnish" VALUES(107,2,'107:2','Hän kohtelee orpoa karkeasti');
INSERT INTO "finnish" VALUES(107,3,'107:3','eikä kehoita ruokkimaan köyhiä.');
INSERT INTO "finnish" VALUES(107,4,'107:4','Voi niitä rukoilevia,');
INSERT INTO "finnish" VALUES(107,5,'107:5','jotka eivät sen enempää rukouksista huolehdi,');
INSERT INTO "finnish" VALUES(107,6,'107:6','jotka hyvää tekevät vain näön vuoksi');
INSERT INTO "finnish" VALUES(107,7,'107:7','ja kitsaasti jättävät almut antamatta!');
INSERT INTO "finnish" VALUES(108,1,'108:1','Me olemme totisesti jakanut sinulle runsaasti hyvää.');
INSERT INTO "finnish" VALUES(108,2,'108:2','Jatka siis rukouksiasi, uhraa Herrallesi.');
INSERT INTO "finnish" VALUES(108,3,'108:3','Vihollisesi totisesti kuuluu niihin, joilta hyvä riistetään.');
INSERT INTO "finnish" VALUES(109,1,'109:1','Sano: »Te, jotka ette usko!');
INSERT INTO "finnish" VALUES(109,2,'109:2','En palvele sitä, mitä te palvelette,');
INSERT INTO "finnish" VALUES(109,3,'109:3','ettekä te palvele Häntä, jota minä palvelen.');
INSERT INTO "finnish" VALUES(109,4,'109:4','Enkä koskaan aio palvella sellaista, mitä te palvelette,');
INSERT INTO "finnish" VALUES(109,5,'109:5','ettekä te koskaan Häntä, jota minä palvelen.');
INSERT INTO "finnish" VALUES(109,6,'109:6','Teillä on tuomionne ja minulla palkkani.»');
INSERT INTO "finnish" VALUES(110,1,'110:1','Kun Jumalalta apu ja voitto tulee,');
INSERT INTO "finnish" VALUES(110,2,'110:2','kun näet ihmisten suurin joukoin liittyvän Jumalan uskontoon,');
INSERT INTO "finnish" VALUES(110,3,'110:3','silloin ylistä Herrasi kunniaa ja ano Hänen anteeksiantoaan. Totisesti, Hän palkitsee moninkertaisesti.');
INSERT INTO "finnish" VALUES(111,1,'111:1','Tuhon omat ovat Abu Lahabin molemmat kädet, ja tuho perii hänet itsensä.');
INSERT INTO "finnish" VALUES(111,2,'111:2','Hänen rikkautensa ja ansionsa eivät häntä vähääkään hyödytä.');
INSERT INTO "finnish" VALUES(111,3,'111:3','Pian hän kärventyy tulen liekeissä,');
INSERT INTO "finnish" VALUES(111,4,'111:4','ja samoin hänen vaimonsa, parjaaja,');
INSERT INTO "finnish" VALUES(111,5,'111:5','jonka kaulaan on heitetty tiukasti punottu köysi.');
INSERT INTO "finnish" VALUES(112,1,'112:1','Sano: »Hän, Jumala on yksi,');
INSERT INTO "finnish" VALUES(112,2,'112:2','ainoa Jumala, kaikkivaltias.');
INSERT INTO "finnish" VALUES(112,3,'112:3','Hän ei ole (toista) synnyttänyt eikä ole (toisesta) syntynyt.');
INSERT INTO "finnish" VALUES(112,4,'112:4','Ketään ei ole Hänen vertaistaan.»');
INSERT INTO "finnish" VALUES(113,1,'113:1','Sano: »Minä etsin turvaa päivänkoiton Herrasta,');
INSERT INTO "finnish" VALUES(113,2,'113:2','sitä pahaa vastaan, mikä on liikkeelle pantu,');
INSERT INTO "finnish" VALUES(113,3,'113:3','ja sitä pahaa vastaan, jonka pimeä yö tuo mukanaan,');
INSERT INTO "finnish" VALUES(113,4,'113:4','niiden pahuutta vastaan, jotka viettelevät päätöksessään lujittuvia,');
INSERT INTO "finnish" VALUES(113,5,'113:5','sekä pahaa vastaan, joka leviää kateellisesta, kun hän kadehtii.»');
INSERT INTO "finnish" VALUES(114,1,'114:1','Sano: »Minä etsin turvaa ihmisten Herralta,');
INSERT INTO "finnish" VALUES(114,2,'114:2','ihmisten Kuninkaalta,');
INSERT INTO "finnish" VALUES(114,3,'114:3','ihmisten Jumalalta,');
INSERT INTO "finnish" VALUES(114,4,'114:4','hiipijän pahoja kuiskauksia vastaan,');
INSERT INTO "finnish" VALUES(114,5,'114:5','hänen, joka herättää pahuutta ihmismielissä,');
INSERT INTO "finnish" VALUES(114,6,'114:6','olkoonpa hän paholaisten tai ihmisten joukosta.»');
