INSERT INTO "habibur_rahman" VALUES(55,56,'55:56','তাত থাকিব স্বামীৰ প্ৰতি দৃষ্টি সীমিতকাৰী ৰূপহীসকল, যিসকলক ইতিপূৰ্বে কোনো মানুহে অথবা কোনো জিনে স্পৰ্শ কৰা নাই।');
INSERT INTO "habibur_rahman" VALUES(55,57,'55:57','এতেকে তোমালোক উভয়ে তোমালোকৰ প্ৰতিপালকৰ কোনটো অনুগ্ৰহ অস্বীকাৰ কৰিবা?');
INSERT INTO "habibur_rahman" VALUES(55,58,'55:58','সিহঁত যেনিবা হীৰা আৰু মুকুতা।');
