INSERT INTO "habibur_rahman" VALUES(56,41,'56:41','আৰু বাওঁফালৰ দল, কিমান যে দুৰ্ভগীয়া বাওঁফালৰ দলটো!');
INSERT INTO "habibur_rahman" VALUES(56,42,'56:42','সিহঁত থাকিব অত্যন্ত উষ্ণ বায়ু আৰু উত্তপ্ত উতলা পানীত,');
INSERT INTO "habibur_rahman" VALUES(56,43,'56:43','আৰু অতি ক''লা ধোঁৱাৰ ছাঁত,');
INSERT INTO "habibur_rahman" VALUES(56,44,'56:44','যিটো শীতলো নহয় আৰু আৰামদায়কো নহয়।');
INSERT INTO "habibur_rahman" VALUES(56,45,'56:45','নিশ্চয় সিহঁত ইতিপূৰ্বে ভোগ-বিলাসত মগ্ন আছিল,');
INSERT INTO "habibur_rahman" VALUES(56,46,'56:46','আৰু সিহঁত অবিৰামভাৱে লিপ্ত আছিল জঘন্য পাপ কামত।');
INSERT INTO "habibur_rahman" VALUES(56,47,'56:47','আৰু সিহঁতে কৈছিল, ‘আমি যেতিয়া মৰি মাটি আৰু হাড়ত পৰিণত হ''ম তেতিয়াও আমাক পুনৰুত্থিত কৰা হ’বনে’?');
INSERT INTO "habibur_rahman" VALUES(56,48,'56:48','‘আৰু আমাৰ পিতৃ-পুৰুষ সকলোকো’?');
INSERT INTO "habibur_rahman" VALUES(56,49,'56:49','কোৱা, ‘নিশ্চয় পূৰ্বৱৰ্তীসকলকো আৰু পৰবৰ্তীসকলকো--');
INSERT INTO "habibur_rahman" VALUES(56,50,'56:50','সকলোকে একত্ৰিত কৰা হ’ব এটা নিৰ্ধাৰিত দিনৰ নিৰ্দিষ্ট সময়ত’।');
INSERT INTO "habibur_rahman" VALUES(56,51,'56:51','তাৰ পিছত, হে বিভ্ৰান্ত অস্বীকাৰকাৰীসকল!');
INSERT INTO "habibur_rahman" VALUES(56,52,'56:52','নিশ্চয় আহাৰ কৰিবা যাক্কুম গছৰ পৰা,');
INSERT INTO "habibur_rahman" VALUES(56,53,'56:53','সেইটোৰেই তোমালোকে পেট ভৰাবা,');
INSERT INTO "habibur_rahman" VALUES(56,54,'56:54','তদুপৰি তাৰ ওপৰত পান কৰিবা উত্তপ্ত উতলা পানী---');
INSERT INTO "habibur_rahman" VALUES(56,55,'56:55','তাৰ পিছত তোমালোকে তৃষ্ণাতুৰ উটৰ দৰে সেই পানী পান কৰিবা।');
INSERT INTO "habibur_rahman" VALUES(56,56,'56:56','প্ৰতিফল দিৱসত এইটোৱে হ’ব সিহঁতৰ আপ্যায়ন।');
INSERT INTO "habibur_rahman" VALUES(56,57,'56:57','আমিয়েই তোমালোকক সৃষ্টি কৰিছোঁ; তেনেহ’লে কিয় তোমালোকে বিশ্বাস নকৰা?');
INSERT INTO "habibur_rahman" VALUES(56,58,'56:58','তোমালোকে ভাবিছানে তোমালোকৰ বীৰ্যপাত সম্পৰ্কে?');
INSERT INTO "habibur_rahman" VALUES(56,59,'56:59','সেইটো তোমালোকে সৃষ্টি কৰা, নে আমি সৃষ্টি কৰোঁ?');
INSERT INTO "habibur_rahman" VALUES(56,60,'56:60','আমিয়েই তোমালোকৰ মাজত মৃত্যু নিৰ্ধাৰণ কৰিছোঁ আৰু আমি অক্ষম নহওঁ--');
INSERT INTO "habibur_rahman" VALUES(56,61,'56:61','তোমালোকৰ স্থানত তোমালোকৰ সদৃশ আনিবলৈ আৰু তোমালোকক এনে এটা আকৃতিত সৃষ্টি কৰিবলৈ যিটো তোমালোকে নাজানা।');
INSERT INTO "habibur_rahman" VALUES(56,62,'56:62','আৰু নিশ্চয় তোমালোকে অৱগত হৈছা প্ৰথম সৃষ্টি সম্পৰ্কে, তেন্তে কিয় তোমালোকে উপদেশ গ্ৰহণ নকৰা?');
INSERT INTO "habibur_rahman" VALUES(56,63,'56:63','তোমালোকে যি বীজ সিঁচা সেই বিষয়ে তোমালোকে ভাবি চাইছানে?');
INSERT INTO "habibur_rahman" VALUES(56,64,'56:64','সেইটো তোমালোকে অংকুৰিত কৰা, নে আমি অংকুৰিত কৰোঁ?');
INSERT INTO "habibur_rahman" VALUES(56,65,'56:65','আমি ইচ্ছা কৰিলে ইয়াক খেৰ-কুটাত পৰিণত কৰিব পাৰোঁ, তেতিয়া তোমালোকে হতাশ হৈ পৰিবা;');
INSERT INTO "habibur_rahman" VALUES(56,66,'56:66','(এই বুলি) ‘নিশ্চয় আমি দায়গ্ৰস্ত হৈ পৰিলোঁ’,');
INSERT INTO "habibur_rahman" VALUES(56,67,'56:67','‘বৰং আমি বঞ্চিত’।');
INSERT INTO "habibur_rahman" VALUES(56,68,'56:68','তোমালোকে যি পানী সেৱন কৰা সেই সম্পৰ্কে কোৱাচোন,');
INSERT INTO "habibur_rahman" VALUES(56,69,'56:69','মেঘৰ পৰা সেইটো তোমালোকে নমাই আনা, নে আমি সেইটো বৰ্ষণ কৰোঁ?');
INSERT INTO "habibur_rahman" VALUES(56,70,'56:70','আমি ইচ্ছা কৰিলে সেইটো লুণীয়া কৰি দিব পাৰোঁ। তথাপিও কিয় তোমালোকে কৃতজ্ঞতা প্ৰকাশ নকৰা?');
INSERT INTO "habibur_rahman" VALUES(56,71,'56:71','তোমালোকে যি জুই জ্বলোৱা সেই বিষয়ে কোৱাচোন,');
INSERT INTO "habibur_rahman" VALUES(56,72,'56:72','তোমালোকে ইয়াৰ গছ সৃষ্টি কৰা, নে আমি সৃষ্টি কৰোঁ?');
INSERT INTO "habibur_rahman" VALUES(56,73,'56:73','ইয়াক আমি কৰিছোঁ এটা স্মাৰক আৰু মৰুভুমিতবাস কৰা লোকৰ প্ৰয়োজনীয় বস্তু।');
INSERT INTO "habibur_rahman" VALUES(56,74,'56:74','এতেকে তুমি তোমাৰ মহান প্ৰতিপালকৰ নামত তছবীহ পাঠ কৰা।');
INSERT INTO "habibur_rahman" VALUES(56,75,'56:75','এতেকে মই শপত কৰিছোঁ নক্ষত্ৰৰাজিৰ অস্তাচলৰ,');
INSERT INTO "habibur_rahman" VALUES(56,76,'56:76','আৰু নিশ্চয় এইটো এটা মহাশপত, যদি তোমালোকে জানিলাহেঁতেন--');
INSERT INTO "habibur_rahman" VALUES(56,77,'56:77','নিশ্চয় এইখন মহিমান্বিত কোৰআন,');
INSERT INTO "habibur_rahman" VALUES(56,78,'56:78','যিখন আছে সুৰক্ষিত কিতাবত (লিপিবদ্ধ)।');
INSERT INTO "habibur_rahman" VALUES(56,79,'56:79','পৱিত্ৰসকলৰ বাহিৰে আন কোনোৱে ইয়াক স্পৰ্শ কৰিব নোৱাৰে।');
INSERT INTO "habibur_rahman" VALUES(56,80,'56:80','এয়া বিশ্বজগতৰ প্ৰতিপালকৰ তৰফৰ পৰা নাযিলকৃত।');
INSERT INTO "habibur_rahman" VALUES(56,81,'56:81','তথাপিও তোমালোকে এই বাণীক তুচ্ছ বুলি ভাবিছা নেকি?');
INSERT INTO "habibur_rahman" VALUES(56,82,'56:82','আৰু তোমালোকে অস্বীকাৰ কৰাকেই তোমালোকৰ জীৱিকা কৰি লৈছা নেকি!');
INSERT INTO "habibur_rahman" VALUES(56,83,'56:83','এতেকে কিয় নহয়—প্ৰাণ যেতিয়া কন্ঠাগত হয়,');
INSERT INTO "habibur_rahman" VALUES(56,84,'56:84','আৰু তেতিয়া তোমালোকে (অসহায় হৈ) চাই থাকা,');
INSERT INTO "habibur_rahman" VALUES(56,85,'56:85','আৰু আমি তোমালোকতকৈও তাৰ বেছি ওচৰত থাকোঁ, কিন্তু তোমালোকে দেখা নোপোৱা।');
INSERT INTO "habibur_rahman" VALUES(56,86,'56:86','এতেকে যদি তোমালোকে হিচাপ-নিকাচ আৰু প্ৰতিফলৰ সন্মুখীন নোহোৱা,');
INSERT INTO "habibur_rahman" VALUES(56,87,'56:87','তেনেহ''লে তোমালোকে সেইটো (আত্মাটো) ঘূৰাই নানা কিয়? যদি তোমালোকে সত্যবাদী!');
INSERT INTO "habibur_rahman" VALUES(56,88,'56:88','তাৰ পিছত সি যদি নৈকট্যপ্ৰাপ্তসকলৰ এজন হয়,');
INSERT INTO "habibur_rahman" VALUES(56,89,'56:89','তেন্তে তাৰ বাবে থাকিব বিশ্ৰাম, উত্তম জীৱনোপকৰণ আৰু সুখময় জান্নাত।');
INSERT INTO "habibur_rahman" VALUES(56,90,'56:90','আৰু সি যদি হয় সোঁফালৰ দলৰ এজন,');
INSERT INTO "habibur_rahman" VALUES(56,91,'56:91','তেন্তে (তাক কোৱা হ’ব) ‘তোমাৰ প্ৰতি ছালাম’, যিহেতু তুমি সোঁফালৰ এজন।');
INSERT INTO "habibur_rahman" VALUES(56,92,'56:92','কিন্তু সি যদি হয় অস্বীকাৰকাৰী, বিভ্ৰান্তসকলৰ এজন,');
INSERT INTO "habibur_rahman" VALUES(56,93,'56:93','তেন্তে তাৰ আতিথ্য কৰা হ’ব উতলা পানীৰ দ্বাৰা,');
INSERT INTO "habibur_rahman" VALUES(56,94,'56:94','আৰু জাহান্নামৰ দহনেৰে;');
INSERT INTO "habibur_rahman" VALUES(56,95,'56:95','নিশ্চয় এইটো সুনিশ্চিত সত্য।');
INSERT INTO "habibur_rahman" VALUES(56,96,'56:96','এতেকে তুমি তোমাৰ মহান প্ৰতিপালকৰ নামত তছবীহ পাঠ কৰা।');
INSERT INTO "habibur_rahman" VALUES(57,1,'57:1','আকাশমণ্ডল আৰু পৃথিৱীত যি আছে সকলোৱে আল্লাহৰ পৱিত্ৰতা আৰু মহিমা ঘোষণা কৰে; আৰু তেওঁ পৰাক্ৰমশালী, প্ৰজ্ঞাময়।');
INSERT INTO "habibur_rahman" VALUES(57,2,'57:2','আকাশমণ্ডল আৰু পৃথিৱীৰ সৰ্বময় কৰ্তৃত্ব তেওঁৰেই; তেৱেঁই জীৱন দান কৰে আৰু তেৱেঁই মৃত্যু প্ৰদান কৰে; আৰু তেৱেঁই সকলো বস্তুৰ ওপৰত ক্ষমতাৱান।');
INSERT INTO "habibur_rahman" VALUES(57,3,'57:3','তেৱেঁই আদি আৰু অন্ত; প্ৰকাশ্য (ওপৰত) আৰু গোপন (ওচৰত) আৰু তেৱেঁই সকলো বস্তু সম্পৰ্কে সম্যক অৱগত।');
INSERT INTO "habibur_rahman" VALUES(57,4,'57:4','তেৱেঁই আকাশসমূহ আৰু পৃথিৱী ছয় দিনত সৃষ্টি কৰিছে; তাৰ পিছত তেওঁ আৰশ্বৰ ওপৰত উঠিছে। তেওঁ সেই সকলোবোৰ জানে যিবোৰ ভূগৰ্ভত প্ৰৱেশ কৰে আৰু যিবোৰ ভূগৰ্ভৰ পৰা ওলায়, আৰু আকাশৰ পৰা যি অৱতীৰ্ণ হয় আৰু যি আকাশলৈ উঠি যায়; আৰু তোমালোক য’তেই নাথাকা কিয়-- তেওঁ তোমালোকৰ লগত আছে, আৰু তোমালোকে যি কৰা সেই সম্পৰ্কে আল্লাহ সম্যক দ্ৰষ্টা।');
INSERT INTO "habibur_rahman" VALUES(57,5,'57:5','আকাশমণ্ডল আৰু পৃথিৱীৰ সকলো কৰ্তৃত্ব তেওঁৰেই আৰু আল্লাহৰ ফালেই সকলো বিষয় প্ৰত্যাবৰ্তিত হয়।');
INSERT INTO "habibur_rahman" VALUES(57,6,'57:6','তেৱেঁই ৰাতিক দিনৰ ভিতৰত প্ৰৱেশ কৰায় আৰু দিনক ৰাতিৰ ভিতৰত প্ৰৱেশ কৰায়; আৰু তেৱেঁই অন্তৰৰ বিষয়সমূহ সম্পৰ্কে সম্যক অৱগত।');
INSERT INTO "habibur_rahman" VALUES(57,7,'57:7','তোমালোকে আল্লাহ আৰু তেওঁৰ ৰাছুলৰ প্ৰতি ঈমান আনা আৰু আল্লাহে তোমালোকক যি সম্পদৰ উত্তৰাধিকাৰী কৰিছে তাৰ পৰা ব্যয় কৰা। এতেকে তোমালোকৰ মাজত যিসকলে ঈমান পোষণ কৰে আৰু (আল্লাহৰ পথত) ব্যয় কৰে তেওঁলোকৰ বাবে আছে মহাপুৰস্কাৰ।');
INSERT INTO "habibur_rahman" VALUES(57,8,'57:8','আৰু তোমালোকৰ কি হ’ল, তোমালোকে আল্লাহৰ প্ৰতি বিশ্বাস নকৰা কিয়? অথচ ৰাছুলে তোমালোকক তোমালোকৰ প্ৰতিপালকৰ প্ৰতি ঈমান আনিবলৈ আহ্বান কৰিছে, আৰু আল্লাহেও তোমালোকৰ পৰা অঙ্গীকাৰ গ্ৰহণ কৰিছে, যদি তোমালোকে মুমিন হোৱা।');
INSERT INTO "habibur_rahman" VALUES(57,9,'57:9','তেৱেঁই নিজ বান্দাৰ প্ৰতি সুস্পষ্ট আয়াতসমূহ অৱতীৰ্ণ কৰে, তোমালোকক অন্ধকাৰৰ পৰা পোহৰলৈ আনাৰ বাবে; আৰু নিশ্চয় আল্লাহ তোমালোকৰ প্ৰতি কৰুণাময়, পৰম দয়ালু।');
INSERT INTO "habibur_rahman" VALUES(57,10,'57:10','আৰু তোমালোকৰ কি হ’ল, তোমালোকে আল্লাহৰ পথত খৰচ নকৰা কিয়? অথচ আকাশমণ্ডল আৰু পৃথিৱীৰ মীৰাছ কেৱল আল্লাহৰেই। তোমালোকৰ মাজত যিসকলে বিজয়ৰ আগত ব্যয় কৰিছে আৰু যুদ্ধ কৰিছে, তেওঁলোক (আৰু পৰবৰ্তীসকল) সমান নহয়। তেওঁলোক সিহঁততকৈ মৰ্যাদাত শ্ৰেষ্ঠ যিসকলে পৰবৰ্তী সময়ত ব্যয় কৰিছে আৰু যুদ্ধ কৰিছে। কিন্তু আল্লাহে উভয়ৰ বাবেই কল্যাণৰ প্ৰতিশ্ৰুতি দিছে; আৰু তোমালোকে যি আমল কৰা সেই সম্পৰ্কে আল্লাহ সবিশেষ অৱগত।');
INSERT INTO "habibur_rahman" VALUES(57,11,'57:11','এনেকুৱা কোন আছে, যিয়ে আল্লাহক উত্তম ঋণ দিব? ফলত তেওঁ তাৰ বাবে ইয়াক বহুগুণে বৃদ্ধি কৰি দিব আৰু তাৰ বাবে আছে সন্মানজনক পুৰস্কাৰ।');
INSERT INTO "habibur_rahman" VALUES(57,12,'57:12','সেইদিনা তুমি মুমিন নৰ-নাৰীসকলক দেখিবলৈ পাবা যে, তেওঁলোকৰ সন্মুখত আৰু সোঁ ফালে তেওঁলোকৰ নূৰ দৌৰি আছে। (কোৱা হ’ব) ‘আজি তোমালোকৰ বাবে সুসংবাদ হৈছে জান্নাত, যাৰ তলত নদীসমূহ প্ৰবাহিত, তাত তোমালোক চিৰস্থায়ী হ’বা, এইটোৱেই মহাসফলতা’।');
INSERT INTO "habibur_rahman" VALUES(57,13,'57:13','সেইদিনা মুনাফিক পুৰুষ আৰু মুনাফিকা নাৰীসকলে ঈমান্দাৰসকলক ক’ব, ‘তোমালোকে আমাৰ বাবে অলপ অপেক্ষা কৰা, যাতে তোমালোকৰ নূৰৰ পৰা আমি অলপ গ্ৰহণ কৰিব পাৰোঁ’। কোৱা হ’ব, ‘তোমালোকে নিজৰ পিছফালে উভতি যোৱা আৰু নূৰৰ সন্ধান কৰাগৈ’। তাৰ পিছত উভয়ৰ মাজত স্থাপন হ’ব এটা প্ৰাচীৰ, য’ত এটা দুৱাৰ থাকিব, যাৰ ভিতৰত থাকিব ৰহমত আৰু বাহিৰত থাকিব শাস্তি।');
INSERT INTO "habibur_rahman" VALUES(57,14,'57:14','মুনাফিকসকলে মুমিনসকলক মাতি ক’ব, ‘আমি তোমালোকৰ লগত নাছিলোনে? তেওঁলোকে ক’ব, ‘হয়, কিন্তু তোমালোকে নিজেই নিজক বিপদগ্ৰস্ত কৰিছা আৰু তোমালোকে প্ৰতীক্ষা কৰিছিলা আৰু সন্দেহ কৰিছিলা আৰু মিছা আকাংক্ষাই তোমালোকক প্ৰতাৰিত কৰি ৰাখিছিল, আৰু অৱশেষত আল্লাহৰ নিৰ্দেশ আহি গ’ল, আৰু (অন্তিম সময়লৈকে) মহাপ্ৰতাৰকে তোমালোকক আল্লাহ সম্পৰ্কে প্ৰতাৰিত কৰিছিল’।');
INSERT INTO "habibur_rahman" VALUES(57,15,'57:15','‘এতেকে আজি তোমালোকৰ পৰা কোনো মুক্তিপণ গ্ৰহণ কৰা নহ’ব আৰু যিসকলে কুফৰী কৰিছিল সিহঁতৰ পৰাও নহয়। জাহান্নামেই তোমালোকৰ আবাসস্থল, এইটোৱেই তোমালোকৰ উপযুক্ত স্থান; আৰু কিমান যে নিকৃষ্ট এই প্ৰত্যাবৰ্তনস্থল!');
INSERT INTO "habibur_rahman" VALUES(57,16,'57:16','যিসকলে ঈমান আনিছে তেওঁলোকৰ হৃদয় আল্লাহৰ স্মৰণত আৰু যি সত্য অৱতীৰ্ণ হৈছে তাৰ বাবে তেওঁলোকৰ অন্তৰ পমি যোৱাৰ সময় হোৱা নাইনে? আৰু তেওঁলোকে যেন সিহঁতৰ দৰে নহয়, যিহঁতক ইয়াৰ আগত কিতাব দিয়া হৈছিল-- তাৰ পিছত বহু কাল অতিবাহিত হোৱাৰ পিছত সিহঁতৰ অন্তৰসমূহ কঠোৰ হৈ পৰিছিল; আৰু সিহঁতৰ অধিকাংশই আছিল ফাছিক্ব।');
INSERT INTO "habibur_rahman" VALUES(57,17,'57:17','জানি থোৱা যে, আল্লাহেই পৃথিৱীক তাৰ মৃত্যুৰ পিছত পুনৰ্জীৱিত কৰে। আমি নিদৰ্শনসমূহ তোমালোকৰ বাবে সুস্পষ্টভাৱে বৰ্ণনা কৰিছোঁ যাতে তোমালোকে বুজিব পাৰা।');
INSERT INTO "habibur_rahman" VALUES(57,18,'57:18','নিশ্চয় দানশীল পুৰুষ আৰু দানশীলা নাৰী আৰু যিসকলে আল্লাহক উত্তম ঋণ দিয়ে, তেওঁলোকক (ইয়াৰ পৰিবৰ্তে) বহু গুণ বৃদ্ধি কৰি দিয়া হ’ব আৰু তেওঁলোকৰ বাবে আছে সন্মানজনক পুৰস্কাৰ।');
INSERT INTO "habibur_rahman" VALUES(57,19,'57:19','আৰু যিসকলে আল্লাহ আৰু তেওঁৰ ৰাছুলৰ প্ৰতি ঈমান আনে, তেওঁলোকেই নিজ প্ৰতিপালকৰ ওচৰত ছিদ্দীক্ব আৰু শ্বহীদ (বুলি বিবেচিত)। তেওঁলোকৰ বাবে আছে তেওঁলোকৰ প্ৰাপ্য পুৰস্কাৰ আৰু নূৰ; আৰু যিসকলে কুফৰী কৰিছে আৰু আমাৰ নিদৰ্শনসমূহ অস্বীকাৰ কৰিছে, সিহঁতেই জাহান্নামৰ অধিবাসী।');
INSERT INTO "habibur_rahman" VALUES(57,20,'57:20','জানি থোৱা যে, নিশ্চয় পৃথিৱীৰ জীৱন খেল-ধেমালি, ক্ৰীড়া-কৌতুক, সৌন্দৰ্য, আৰু তোমালোকৰ পাৰস্পাৰিক গৌৰৱ-অহংকাৰ, ধন-সম্পদ আৰু সন্তান-সন্ততিত প্ৰাচুৰ্য লাভৰ প্ৰতিযোগিতাৰ বাহিৰে আন একো নহয়। ইয়াৰ উদাহৰণ হৈছে বৰষুণৰ দৰে, যাৰ দ্বাৰা উৎপন্ন শস্য-সম্ভাৰে কৃষকসকলক আনন্দিত কৰে, তাৰ পিছত সেইবোৰ শুকাই যায়, তেতিয়া তুমি সেইবোৰক হালধীয়া বৰণত দেখিবলৈ পোৱা, অৱশেষত সেইবোৰ খেৰ-কুটাত পৰিণত হয়; আৰু আখিৰাতত আছে কঠিন শাস্তি আৰু আল্লাহৰ মাগফিৰাত ও সন্তুষ্টি; আৰু পৃথিৱীৰ জীৱন প্ৰতাৰণাৰ বাহিৰে আন একো নহয়।');
INSERT INTO "habibur_rahman" VALUES(57,21,'57:21','তোমালোকে নিজ প্ৰতিপালকৰ ক্ষমা আৰু সেই জান্নাত লাভৰ প্ৰয়াসত প্ৰতিযোগিতাত অৱতীৰ্ণ হোৱা, যাৰ প্ৰশস্ততা আকাশ আৰু পৃথিৱীৰ প্ৰশস্ততাৰ দৰে, যিটো প্ৰস্তুত কৰা হৈছে সেইসকল লোকৰ বাবে যিসকলে আল্লাহ আৰু তেওঁৰ ৰাছুলৰ প্ৰতি ঈমান আনে। এইটো আল্লাহৰ অনুগ্ৰহ, তেওঁ যাক ইচ্ছা তাকেই এইটো প্ৰদান কৰে; আৰু আল্লাহ মহা অনুগ্ৰহশীল।');
INSERT INTO "habibur_rahman" VALUES(57,22,'57:22','পৃথিৱীত আৰু তোমালোকৰ ওপৰত আপতিত হোৱা এনে কোনো বিপদ-আপদ নাই যিটো আমি সেয়া সংঘটিত হোৱাৰ আগতে এখন কিতাপত লিপিবদ্ধ কৰি ৰখা নাই। নিশ্চয় আল্লাহৰ পক্ষে এয়া অতি সহজ।');
INSERT INTO "habibur_rahman" VALUES(57,23,'57:23','এইটো এই বাবে যে, যাতে তোমালোকে যি হেৰাইছা তাৰ প্ৰতি পৰিতাপ নকৰা আৰু তেওঁ তোমালোকক যি প্ৰদান কৰিছে তাৰ বাবে উৎফুল্লিত নোহোৱা। কাৰণ আল্লাহে কোনো উদ্ধত আৰু অহংকাৰী ব্যক্তিক পছন্দ নকৰে।');
INSERT INTO "habibur_rahman" VALUES(57,24,'57:24','যিসকলে কৃপণতা কৰে আৰু মানুহক কৃপণতাৰ নিৰ্দেশ দিয়ে, আৰু যিয়ে মুখ ঘূৰাই লয় (সি জানি লোৱা উচিত) নিশ্চয় আল্লাহ অভাৱমুক্ত, চিৰ প্ৰশংসিত।');
INSERT INTO "habibur_rahman" VALUES(57,25,'57:25','নিশ্চয় আমি আমাৰ ৰাছুলসকলক সুস্পষ্ট প্ৰমাণ সহকাৰে প্ৰেৰণ কৰিছোঁ, আৰু তেওঁলোকৰ লগত দিছোঁ কিতাব আৰু ন্যায়ৰ মানদণ্ড, যাতে মানুহে সুবিচাৰ প্ৰতিষ্ঠা কৰে। আমি আৰু অৱতীৰ্ণ কৰিছোঁ লোহা, য’ত আছে প্ৰচণ্ড শক্তি আৰু মানুহৰ বাবে আছে বহুবিধ কল্যাণ। এয়া এই বাবেই যে, যাতে আল্লাহে প্ৰকাশ কৰি দিয়ে যে, কোনে গায়েব অৱস্থাত তেওঁক আৰু তেওঁৰ ৰাছুলক সহায় কৰে। নিশ্চয় আল্লাহ মহাশক্তিমান, পৰাক্ৰমশালী।');
INSERT INTO "habibur_rahman" VALUES(57,26,'57:26','আৰু নিশ্চয় আমি নূহ আৰু ইব্ৰাহীমক ৰাছুলৰূপে প্ৰেৰণ কৰিছিলোঁ আৰু আমি তেওঁলোকৰ বংশধৰসকলৰ বাবে স্থিৰ কৰিছিলোঁ নবুওৱাত আৰু কিতাব। কিন্তু সিহঁতৰ কিছুমানেহে সৎপথ অৱলম্বন কৰিছিল আৰু সিহঁতৰ অধিকাংশই আছিল ফাছিক্ব।');
INSERT INTO "habibur_rahman" VALUES(57,27,'57:27','ইয়াৰ পিছত আমি তেওঁলোকৰ অনুগামী হিচাপে আমাৰ ৰাছুলসকলক প্ৰেৰণ কৰিছিলোঁ আৰু মাৰয়াম পুত্ৰ ঈছাকো অনুগামী কৰিছিলোঁ, আৰু তেওঁক আমি দিছিলোঁ ইঞ্জিল আৰু তেওঁৰ অনুসাৰীসকলৰ অন্তৰত দিছিলোঁ কৰুণা আৰু দয়া; আৰু বৈৰাগ্যবাদ—এইটো সিহঁতে নিজেই আল্লাহৰ সন্তুষ্টিৰ বাবে প্ৰৱৰ্তন কৰিছিল। আমি এইটো সিহঁতৰ ওপৰত লিপিবদ্ধ কৰা নাছিলোঁ; অথচ এইটোও সিহঁতে যথাযথভাৱে পালন কৰা নাই। কিন্তু সিহঁতৰ মাজত যিসকলে ঈমান আনিছিল, সিহঁতক আমি সিহঁতৰ পুৰস্কাৰ প্ৰদান কৰিছিলোঁ; কিন্তু সিহঁতৰ অধিকাংশই আছিল ফাছিক্ব।');
INSERT INTO "habibur_rahman" VALUES(57,28,'57:28','হে ঈমান্দাৰসকল! তোমালোকে আল্লাহৰ তাক্বৱা অৱলম্বন কৰা আৰু তেওঁৰ ৰাছুলৰ প্ৰতি ঈমান পোষণ কৰা। তেওঁ নিজ অনুগ্ৰহত তোমালোকক দ্বিগুণ পুৰস্কাৰ প্ৰদান কৰিব আৰু তেওঁ তোমালোকক প্ৰদান কৰিব নূৰ, যাৰ সহায়ত তোমালোকে চলিব পাৰিবা আৰু তেওঁ তোমালোকক ক্ষমা কৰিব; আৰু আল্লাহ ক্ষমাশীল, পৰম দয়ালু।');
INSERT INTO "habibur_rahman" VALUES(57,29,'57:29','এয়া এইবাবেই যে, যাতে আহলে কিতাবসকলে জানিব পাৰে যে, আল্লাহৰ সামান্যতম অনুগ্ৰহতো সিহঁতৰ কোনো অধিকাৰ নাই; আৰু নিশ্চয় অনুগ্ৰহ আল্লাহৰেই হাতত, যাক ইচ্ছা তেওঁ তাক প্ৰদান কৰে, আৰু আল্লাহ মহা অনুগ্ৰহশীল।');
INSERT INTO "habibur_rahman" VALUES(58,1,'58:1','আল্লাহে নিশ্চয় শুনিছে সেই নাৰীৰ কথা; যিয়ে তাইৰ স্বামীৰ বিষয়ে তোমাৰ লগত বাদানুবাদ কৰি আছিল আৰু আল্লাহৰ ওচৰত অভিযোগ কৰি আছিল। আল্লাহে তোমালোক দুয়োৰে কথোপকথন শুনিছে; নিশ্চয় আল্লাহ সৰ্বশ্ৰোতা, সৰ্বদ্ৰষ্টা।');
INSERT INTO "habibur_rahman" VALUES(58,2,'58:2','তোমালোকৰ মাজত যিসকলে নিজ স্ত্ৰীসকলৰ লগত যিহাৰ কৰে, সিহঁতৰ স্ত্ৰীসকল সিহঁতৰ মাক হৈ নাযায়, বৰং সিহঁতৰ মাক কেৱল সেইসকল তিৰোতা যিসকলে সিহঁতক জন্ম দিছে; আৰু নিশ্চয় সিহঁতে অসংগত আৰু অসত্য কথা কৈছে; নিশ্চয় আল্লাহ অধিক পাপ মোচনকাৰী আৰু বৰ ক্ষমাশীল।');
INSERT INTO "habibur_rahman" VALUES(58,3,'58:3','আৰু যিসকলে সিহঁতৰ স্ত্ৰীসকলৰ লগত যিহাৰ কৰে, আৰু পিছত সিহঁতৰ উক্তি প্ৰত্যাহাৰ কৰে, তেন্তে ইজনে সিজনক স্পৰ্শ কৰাৰ আগতে এজন দাস মুক্ত কৰিব লাগিব, ইয়াৰ দ্বাৰা তোমালোকক উপদেশ দিয়া হৈছে; আৰু তোমালোকে যি কৰা সেই সম্পৰ্কে আল্লাহ সম্যক অৱহিত।');
INSERT INTO "habibur_rahman" VALUES(58,4,'58:4','কিন্তু যিয়ে এইটো কৰিব নোৱাৰিব, তেন্তে ইজনে সিজনক স্পৰ্শ কৰাৰ আগতে সি একেৰাহে দুমাহ ছিয়াম পালন কৰিব লাগিব;আৰু যিয়ে এইটোও কৰিবলৈ সক্ষম নহয়, সি ষাঠিজন মিছকীনক আহাৰ কৰাব লাগিব;এই বিধান এই বাবে যে, যাতে তোমালোকে আল্লাহ আৰু তেওঁৰ ৰাছুলৰ প্ৰতি ঈমান আনা। এইবোৰ আল্লাহৰ নিৰ্ধাৰিত বিধান; আৰু অস্বীকাৰকাৰী সকলৰ বাবে আছে যন্ত্ৰণাদায়ক শাস্তি।');
INSERT INTO "habibur_rahman" VALUES(58,5,'58:5','নিশ্চয় যিবিলাক মানুহে আল্লাহ আৰু তেওঁৰ ৰাছুলৰ বিৰুদ্ধাচাৰণ কৰে, সিহঁতক অপদস্ত কৰা হ’ব যেনেকৈ অপদস্ত কৰা হৈছিল সিহঁতৰ পূৰ্বৱৰ্তীসকলক; আৰু আমি সুস্পষ্ট আয়াত অৱতীৰ্ণ কৰিছো; আৰু কাফিৰসকলৰ বাবে আছে লাঞ্ছনাদায়ক শাস্তি--');
INSERT INTO "habibur_rahman" VALUES(58,6,'58:6','যিদিনা আল্লাহে সিহঁত সকলোকে পুনৰুজ্জীৱিত কৰি উঠাব, তাৰ পিছত সিহঁতে যি আমল কৰিছিল সেয়া তেওঁ সিহঁতক জনাই দিব; আল্লাহে সেইবোৰ হিচাপ কৰি ৰাখিছে যদিও সিহঁতে পাহৰি গৈছে; আৰু আল্লাহ প্ৰতিটো বস্তুৰ প্ৰত্যক্ষদৰ্শী।');
INSERT INTO "habibur_rahman" VALUES(58,7,'58:7','তুমি লক্ষ্য কৰা নাইনে যে, আকাশমণ্ডল আৰু পৃথিৱীত যি আছে সেয়া আল্লাহে জানে? তিনিজন ব্যক্তিৰ মাজত এনেকুৱা কোনো গোপন পৰামৰ্শ নহয় য’ত চতুৰ্থজন হিচাপে তেওঁ নাথাকে আৰু পাঁচজনৰ মাজতো নহয় য’ত ষষ্টজন হিচাপে তেওঁ নাথাকে। সিহঁত ইয়াতকৈ কম হওঁক বা বেছি হওঁক তেওঁ সিহঁতৰ লগতে আছে সিহঁত য’তেই নাথাকক কিয়। তাৰ পিছত ক্বিয়ামতৰ দিনা তেওঁ সিহঁতক সিহঁতৰ কৃতকৰ্ম সম্পৰ্কে জনাই দিব। নিশ্চয় আল্লাহ সকলো বস্তু সম্পৰ্কে সম্যক অৱগত।');
INSERT INTO "habibur_rahman" VALUES(58,8,'58:8','তুমি সিহঁতক লক্ষ্য কৰা নাইনে, যিসকলক গোপন পৰামৰ্শ কৰিবলৈ নিষেধ কৰা হৈছিল? তাৰ পিছতো সিহঁতে সেইটোৱে পুনৰাবৃত্তি কৰিলে যিটো কৰিবলৈ সিহঁতক নিষেধ কৰা হৈছিল; আৰু সিহঁতে দুষ্কাৰ্য, সীমালঙ্ঘন আৰু ৰাছুলৰ বিৰুদ্ধাচাৰণৰ বাবে গোপন পৰামৰ্শ কৰে। আৰু সিহঁতে যেতিয়া তোমাৰ ওচৰলৈ আহে তেতিয়া সিহঁতে তোমাক এনে কথাৰ দ্বাৰা অভিবাদন জনায় যিটোৰ দ্বাৰা আল্লাহে তোমাক অভিবাদন জনোৱা নাই। সিহঁতে মনে মনে কয়, ‘আমি যি কওঁ তাৰ বাবে আল্লাহে আমাক কিয় শাস্তি নিদিয়ে’? জাহান্নামেই সিহঁতৰ বাবে যথেষ্ট, য’ত সিহঁতে প্ৰৱেশ কৰিব, আৰু কিমান যে নিকৃষ্ট সেই গন্তব্যস্থল!');
INSERT INTO "habibur_rahman" VALUES(58,9,'58:9','হে ঈমান্দাৰসকল! তোমালোকে যেতিয়া গোপনে পৰামৰ্শ কৰিবা তেতিয়া যাতে তোমালোকে গুনাহ, সীমালঙ্ঘন আৰু ৰাছুলৰ বিৰুদ্ধাচাৰণ সম্পৰ্কে গোপন পৰামৰ্শ নকৰা। আৰু তোমালোকে সৎকৰ্ম আৰু তাক্বৱা অৱলম্বনৰ বিষয়ে গোপন পৰামৰ্শ কৰা, লগতে আল্লাহক ভয় কৰা, যাৰ ওচৰত তোমালোকক সমবেত কৰা হ’ব।');
INSERT INTO "habibur_rahman" VALUES(58,10,'58:10','গোপন পৰামৰ্শ কেৱল চয়তানৰ প্ৰৰোচনাত হয়, যাতে মুমিনসকলে কষ্ট পায়। কিন্তু আল্লাহৰ অনুমতিবিহীন চয়তানে তেওঁলোকৰ সামান্যতম ক্ষতি সাধন কৰিবলৈয়ো সক্ষম নহয়। এতেকে মুমিনসকলে কেৱল আল্লাহৰ ওপৰতেই ভৰসা কৰা উচিত।');
INSERT INTO "habibur_rahman" VALUES(58,11,'58:11','হে মুমিনসকল! তোমালোকক যেতিয়া কোৱা হয় ‘বৈঠকত স্থান প্ৰশস্ত কৰি দিয়া’ তেতিয়া তোমালোকে স্থান প্ৰশস্ত কৰি দিবা, আল্লাহে তোমালোকৰ বাবে ঠাই প্ৰশস্ত কৰি দিব; আৰু যেতিয়া তোমালোকক কোৱা হয়, ‘উঠা’ তেতিয়া উঠি যাবা। তোমালোকৰ ভিতৰত যিসকলে ঈমান আনিছে আৰু যিসকলক জ্ঞান দান কৰা হৈছে, আল্লাহে তেওঁলোকক উচ্চ মৰ্যাদা প্ৰদান কৰিব; আৰু তোমালোকে যি কৰা সেই সম্পৰ্কে আল্লাহ সবিশেষ অৱহিত।');
INSERT INTO "habibur_rahman" VALUES(58,12,'58:12','হে মুমিনসকল! তোমালোকে যেতিয়া ৰাছুলৰ সৈতে অকলশৰীয়াকৈ কথা ক’ব বিচৰা, তেতিয়া তোমালোকে এনে কথা কোৱাৰ আগতে কিবা ছাদক্বা পেচ কৰা, এইটোৱে তোমালোকৰ বাবে শ্ৰেয় আৰু পৰিশোধক; কিন্তু যদি তোমালোকে সক্ষম নোহোৱা তেন্তে নিশ্চয় আল্লাহ পৰম ক্ষমাশীল, অতি দয়ালু।');
INSERT INTO "habibur_rahman" VALUES(58,13,'58:13','তোমালোকে ভয় পালা নেকি যে, অকলশৰীয়া পৰামৰ্শৰ আগতে ছাদক্বা পেচ কৰিব লাগিব বুলি? যেতিয়া তোমালোকে এইটো কৰিব নোৱাৰিলা, আৰু আল্লাহেও তোমালোকক ক্ষমা কৰি দিলে, তেন্তে তোমালোকে ছালাত কায়েম কৰা আৰু যাকাত প্ৰদান কৰা লগতে আল্লাহ আৰু তেওঁৰ ৰাছুলৰ আনুগত্য কৰা। তোমালোকে যি কৰা, সেই সম্পৰ্কে আল্লাহ সম্যক অৱগত।');
INSERT INTO "habibur_rahman" VALUES(58,14,'58:14','তুমি সিহঁতৰ প্ৰতি লক্ষ্য কৰা নাইনে, যিহঁতে এনে এটা সম্প্ৰদায়ৰ লগত বন্ধুত্ব কৰে যিসকলৰ ওপৰত আল্লাহ ক্ৰোধান্বিত হৈছে? সিহঁত তোমালোকৰ দলভূক্ত নহয়; আৰু সিহঁতে জানি বুজি মিছা কথাত শপত খায়।');
INSERT INTO "habibur_rahman" VALUES(58,15,'58:15','আল্লাহে সিহঁতৰ বাবে প্ৰস্তুত কৰি ৰাখিছে কঠোৰ শাস্তি। নিশ্চয় সিহঁতে যি কৰিছিল সেয়া কিমান যে গৰ্হিত!');
INSERT INTO "habibur_rahman" VALUES(58,16,'58:16','সিহঁতে নিজৰ শপতবোৰক ঢাল হিচাপে গ্ৰহণ কৰিছে, তাৰ পিছত সিহঁতে আল্লাহৰ পথত বাধা প্ৰদান কৰিছে। ফলত সিহঁতৰ বাবে আছে লাঞ্ছনাদায়ক শাস্তি।');
INSERT INTO "habibur_rahman" VALUES(58,17,'58:17','আল্লাহৰ শাস্তিৰ বিপৰীতে সিহঁতৰ ধন-সম্পদ আৰু সন্তান-সন্ততি সিহঁতৰ কোনো কামত নাহিব; সিহঁতেই জাহান্নামৰ অধিবাসী, তাত সিহঁতে চিৰকাল থাকিব।');
INSERT INTO "habibur_rahman" VALUES(58,18,'58:18','সেইদিনা আল্লাহে সিহঁত সকলোকে পুনৰুত্থিত কৰিব, তেতিয়া সিহঁতে আল্লাহৰ ওচৰত এনেকুৱা শপত কৰিব যেনেকুৱা শপত তোমালোকৰ ওচৰত কৰে আৰু সিহঁতে ভাৱে যে, সিহঁতে ভাল কিবা এটা কৰি আছে। সাৱধান! নিশ্চয় সিহঁতেই প্ৰকৃত মিছলীয়া।');
INSERT INTO "habibur_rahman" VALUES(58,19,'58:19','চয়তানে সিহঁতৰ ওপৰত প্ৰভাৱ বিস্তাৰ কৰিছে; আৰু সিহঁতক আল্লাহৰ স্মৰণ পাহৰাই দিছে। ইহঁতেই চয়তানৰ দল। নিশ্চয় চয়তানৰ দলেই ক্ষতিগ্ৰস্ত।');
INSERT INTO "habibur_rahman" VALUES(58,20,'58:20','নিশ্চয় যিহঁতে আল্লাহ আৰু তেওঁৰ ৰাছুলৰ বিৰুদ্ধাচাৰণ কৰে, সিহঁতেই হ’ব চৰম লাঞ্ছিতসকলৰ অন্তৰ্ভুক্ত।');
INSERT INTO "habibur_rahman" VALUES(58,21,'58:21','আল্লাহে লিখি ৰাখিছে যে, ‘নিশ্চয় ময়েই বিজয়ী হ’ম আৰু মোৰ ৰাছুলসকলেও বিজয়ী হ’ব’। নিশ্চয় আল্লাহ মহাশক্তিমান, মহাপৰাক্ৰমশালী।');
INSERT INTO "habibur_rahman" VALUES(58,22,'58:22','আল্লাহ আৰু আখিৰাতৰ প্ৰতি বিশ্বাস স্থাপন কৰা এনে কোনো সম্প্ৰদায় তুমি নাপাবা, যিসকলে এনে লোকক ভালপায় যিহঁতে আল্লাহ আৰু তেওঁৰ ৰাছুলৰ বিৰুদ্ধাচাৰণ কৰে--- যদিও সিহঁত তেওঁলোকৰ পিতা অথবা পুত্ৰ বা ভাই নাইবা বংশ-পৰিয়ালৰ মানুহেই নহওঁক কিয়! এওঁলোকৰ অন্তৰতেই আল্লাহে ঈমান লিখি দিছে আৰু তেওঁৰ তৰফৰ পৰা ৰূহৰ দ্বাৰা তেওঁলোকক শক্তিশালী কৰিছে। তেওঁলোকক তেওঁ প্ৰৱেশ কৰাব এনে জান্নাতসমূহত যাৰ তলেদি নিজৰাসমূহ প্ৰবাহিত হয়। তাত তেওঁলোকে চিৰকাল থাকিব; আল্লাহে তেওঁলোকৰ প্ৰতি সন্তুষ্ট হৈছে আৰু তেওঁলোকেও তেওঁৰ প্ৰতি সন্তুষ্ট। এওঁলোকেই আল্লাহৰ দল। জানি থোৱা, আল্লাহৰ দলেই সফলতা লাভ কৰিব।');
INSERT INTO "habibur_rahman" VALUES(59,1,'59:1','আকাশমণ্ডল আৰু পৃথিৱীত যি আছে সেই সকলোবোৰেই আল্লাহৰ পৱিত্ৰতা আৰু মহিমা ঘোষণা কৰে; আৰু তেওঁ পৰাক্ৰমশালী প্ৰজ্ঞাময়।');
INSERT INTO "habibur_rahman" VALUES(59,2,'59:2','তেৱেঁই আহলে কিতাবসকলৰ মাজত যিসকলে কুফুৰী কৰিছিল সিহঁতক প্ৰথম সমাবেশৰ বাবে সিহঁতৰ আবাসভূমিৰ পৰা বিতাৰিত কৰিছিল। তোমালোকে কল্পনাও কৰা নাছিলা যে সিহঁতে ওলাই যাব; আৰু সিহঁতেও ভাবিছিল যে, সিহঁতৰ দুৰ্গসমূহে সিহঁতক আল্লাহৰ পৰা ৰক্ষা কৰিব; কিন্তু সিহঁতৰ ওচৰত আল্লাহৰ আযাব এনে ঠাইৰ পৰা আহিলে যিটো সিহঁতেও কল্পনা কৰিব পৰা নাছিল; আৰু তেওঁ সিহঁতৰ অন্তৰত আতঙ্কৰ সঞ্চাৰ কৰিলে। ফলত সিহঁতে সিহঁতৰ ঘৰ-দুৱাৰ নিজ হাতে আৰু মুমিনসকলৰ হাতেৰে ধ্বংস কৰিলে; এতেকে হে দৃষ্টিমান ব্যক্তিসকল! তোমালোকে উপদেশ গ্ৰহণ কৰা।');
INSERT INTO "habibur_rahman" VALUES(59,3,'59:3','আৰু আল্লাহে সিহঁতৰ নিৰ্বাসনদণ্ড লিপিবদ্ধ নকৰিলেও তেওঁ সিহঁতক পৃথিৱীতেই (আন কোনো) শাস্তি দিলেহেঁতেন; আৰু আখিৰাতত সিহঁতৰ বাবে আছে জাহান্নামৰ শাস্তি।');
INSERT INTO "habibur_rahman" VALUES(59,4,'59:4','এয়া এই বাবে যে, নিশ্চয় সিহঁত আল্লাহ আৰু তেওঁৰ ৰাছুলৰ বিৰুদ্ধাচাৰণ কৰিছিল;আৰু কোনোবাই আল্লাহৰ বিৰুদ্ধাচাৰণ কৰিলে, তেন্তে নিশ্চয় আল্লাহ শাস্তি প্ৰদানত অতি কঠোৰ।');
INSERT INTO "habibur_rahman" VALUES(59,5,'59:5','তোমালোকে যিবোৰ খেজুৰ গছ কাটিছা আৰু যিবোৰক কাণ্ডৰ ওপৰত স্থিত অৱস্থাত ৰাখি দিছা, সেয়া আছিল কেৱল আল্লাহৰ অনুমতিক্ৰমে; যাতে তেওঁ ফাছিকসকলক লাঞ্ছিত কৰিব পাৰে।');
INSERT INTO "habibur_rahman" VALUES(59,6,'59:6','আৰু আল্লাহে ইয়াহুদীসকলৰ পৰা তেওঁৰ ৰাছুলক যি ‘ফায়’ (ধন-সম্পদ) অৰ্পন কৰিছে, তাৰ বাবে তোমালোকে ঘোঁৰা অথবা উটত আৰোহণ কৰি কোনো অভিযান কৰা নাছিলা; বৰং আল্লাহে তেওঁৰ ৰাছুলসকলক যাৰ ওপৰত ইচ্ছা কৰ্তৃত্ব প্ৰদান কৰে; আৰু আল্লাহ সকলো বস্তুৰ ওপৰত ক্ষমতাৱান।');
INSERT INTO "habibur_rahman" VALUES(59,7,'59:7','আল্লাহে জনবসতিৰ অধিবাসীসকলৰ পৰা তেওঁৰ ৰাছুলক ‘ফায়’ হিচাপে যি প্ৰদান কৰিছে সেয়া আল্লাহৰ বাবে আৰু ৰাছুলৰ বাবে আৰু আত্মীয়-স্বজনৰ বাবে, ইয়াতীমসকল আৰু মিছকীনসকলৰ বাবে লগতে মুছাফিৰসকলৰ বাবে, যাতে ধন-সম্পদ কেৱল তোমালোকৰ মাজৰ বিত্তশালীসকলৰ মাজতেই আবৰ্তন কৰি নাথাকে। ৰাছুলে তোমালোকক যি দিয়ে সেয়া তোমালোকে গ্ৰহণ কৰা, আৰু যিটোৰ পৰা তেওঁ তোমালোকক নিষেধ কৰে তাৰ পৰা বিৰত থকা আৰু তোমালোকে আল্লাহৰ তাক্বৱা অৱলম্বন কৰা; নিশ্চয় আল্লাহ শাস্তি প্ৰদানত অতি কঠোৰ।');
INSERT INTO "habibur_rahman" VALUES(59,8,'59:8','এই সম্পদ সেইসকল দৰিদ্ৰ মুহাজীৰৰ বাবে যিসকলক নিজ ঘৰ-বাৰী আৰু সা-সম্পত্তিৰ পৰা বিতাৰিত কৰা হৈছে। তেওঁলোকে আল্লাহৰ অনুগ্ৰহ আৰু সন্তুষ্টিৰ অন্বেষণ কৰে আৰু আল্লাহ আৰু তেওঁৰ ৰাছুলক সহায় কৰে। এওঁলোকেই সত্যবাদী।');
INSERT INTO "habibur_rahman" VALUES(59,9,'59:9','আৰু তেওঁলোকৰ বাবেও, যিসকলে মুহাজিৰসকলৰ আগমনৰ আগতে এই নগৰীক নিবাস হিচাপে গ্ৰহণ কৰিছে আৰু ঈমান আনিছে, তেওঁলোকে তেওঁলোকৰ ওচৰত হিজৰত কৰি অহা মানুহবিলাকক ভালপায় আৰু মুহাজিৰসকলক যি দিয়া হৈছে তাৰ বাবে তেওঁলোকে নিজ অন্তৰত কোনো হিংসা পোষণ নকৰে, আৰু তেওঁলোকে অভাৱ থকা সত্ত্বেও নিজৰ ওপৰত তেওঁলোকক অগ্ৰাধিকাৰ দিয়ে। প্ৰকৃততে যিসকলক অন্তৰৰ কৃপণতাৰ পৰা ৰক্ষা কৰা হৈছে, তেওঁলোকেই সাফল্যমণ্ডিত।');
INSERT INTO "habibur_rahman" VALUES(59,10,'59:10','আৰু যিসকল তেওঁলোকৰ পিছত আহিছে তেওঁলোকে কয়, ‘হে আমাৰ প্ৰতিপালক! আমাক আৰু আমাৰ পূৰ্বে ঈমান পোষণ কৰা আমাৰ ভাতৃসকলক ক্ষমা কৰি দিয়া; আৰু যিসকলে ঈমান আনিছে তেওঁলোকৰ বিৰুদ্ধে আমাৰ অন্তৰত কোনো বিদ্বেষ নাৰাখিবা। হে আমাৰ প্ৰতিপালক! নিশ্চয় তুমি দয়াবান, পৰম দয়ালু’।');
INSERT INTO "habibur_rahman" VALUES(59,11,'59:11','তুমি মুনাফিকসকলৰ প্ৰতি লক্ষ্য কৰা নাইনে? সিহঁতে আহলে কিতাবসকলৰ মাজৰ পৰা যিসকলে কুফুৰী কৰিছে সিহঁতৰ সেইসকল ভাতৃক কয়, ‘তোমালোকে যদি বহিষ্কৃত হোৱা, তেন্তে আমিও তোমালোকৰ লগত নিশ্চয় দেশ ত্যাগ কৰিম আৰু আমি তোমালোকৰ বিষয়ে কেতিয়াও কাৰো আনুগত্য নকৰিম আৰু তোমালোকৰ লগত যুদ্ধ হ’লে আমি নিশ্চয় তোমালোকৰ সহায় কৰিম’। কিন্তু আল্লাহে সাক্ষ্য দিয়ে যে, নিশ্চয় সিহঁত ঘোৰ মিছলীয়া।');
INSERT INTO "habibur_rahman" VALUES(59,12,'59:12','প্ৰকৃততে যদি সিহঁত (ইয়াহুদীসকল) বহিষ্কৃত হয় তেন্তে ইহঁতে (মুনাফিকসকলে) কেতিয়াও সিহঁতৰ লগত ওলাই নাযাব আৰু সিহঁতৰ লগত যুদ্ধ কৰা হ’লে ইহঁতে কেতিয়াও সহায় নকৰিব আৰু যদি সিহঁতক কোনো সহায় কৰিবলৈ আহেও তেন্তে সিহঁত নিশ্চয় পৃষ্ঠপ্ৰদৰ্শন কৰিব; তাৰ পিছত সিহঁতে কোনো সহায়েই নাপাব।');
INSERT INTO "habibur_rahman" VALUES(59,13,'59:13','প্ৰকৃতপক্ষে সিহঁতৰ অন্তৰত আল্লাহতকৈ তোমালোকৰ ভয়হে বেছি। এয়া এই বাবে যে, সিহঁত এটা জ্ঞানবোধ নথকা সম্প্ৰদায়।');
INSERT INTO "habibur_rahman" VALUES(59,14,'59:14','সিহঁত সন্মিলিতভাৱে তোমালোকৰ লগত যুদ্ধ কৰিব নোৱাৰিব, যুদ্ধ কৰিলেও সুৰক্ষিত জনবসতিৰ মাজত অৱস্থান কৰি অথবা বেৰৰ আঁৰ লৈহে কৰিব; সিহঁত পাৰস্পাৰিক বিৰোধিতাত অতি কঠোৰ। তুমি সিহঁতক ঐক্যবদ্ধ বুলি ভাবা; কিন্তু সিহঁতৰ অন্তৰসমূহত মিল নাই; এইটো এই বাবেই যে, সিহঁত এটা নিৰ্বোধ সম্প্ৰদায়।');
INSERT INTO "habibur_rahman" VALUES(59,15,'59:15','ইহঁত সেইসকল লোকৰ দৰেই, যিসকলে ইহঁতৰ কিছুকাল পূৰ্বেই নিজৰ কৃতকৰ্মৰ শাস্তি ভোগ কৰিছে, আৰু সিহঁতৰ বাবে আছে যন্ত্ৰণাদায়ক শাস্তি।');
INSERT INTO "habibur_rahman" VALUES(59,16,'59:16','ইহঁতে চয়তানৰ দৰে, সি মানুহক কয়, ‘কুফুৰী কৰা’; তাৰ পিছত যেতিয়া সি কুফুৰী কৰে তেতিয়া সি কয়, ‘তোমাৰ লগত মোৰ কোনো সম্পৰ্ক নাই, নিশ্চয় মই সৃষ্টিজগতৰ প্ৰতিপালক আল্লাহক ভয় কৰো’।');
INSERT INTO "habibur_rahman" VALUES(59,17,'59:17','ফলত সিহঁত দুজনৰ পৰিণাম হ’ব যে, সিহঁত দুজনেই জাহান্নামী; তাত সিহঁত চিৰস্থায়ী হ’ব আৰু এইটোৱে যালিমসকলৰ প্ৰতিদান।');
INSERT INTO "habibur_rahman" VALUES(59,18,'59:18','হে ঈমান্দাৰসকল! তোমালোকে আল্লাহৰ তাক্বৱা অৱলম্বন কৰা; আৰু প্ৰত্যেকৰে চিন্তা কৰা উচিত যে, সি অহাকালিৰ বাবে কি প্ৰেৰণ কৰিছে; আৰু তোমালোকে আল্লাহক ভয় কৰা; তোমালোকে যি কৰা সেই সম্পৰ্কে নিশ্চয় আল্লাহ সবিশেষ অৱহিত।');
INSERT INTO "habibur_rahman" VALUES(59,19,'59:19','তোমালোকে সিহঁতৰ দৰে নহ’বা যিসকলে আল্লাহক পাহৰি গৈছে; ফলত আল্লাহেও সিহঁতক আত্মবিস্মৃত কৰিছে, ইহঁতেই হৈছে ফাছিক্ব।');
INSERT INTO "habibur_rahman" VALUES(59,20,'59:20','জাহান্নামৰ অধিবাসী আৰু জান্নাতৰ অধিবাসী কেতিয়াও সমান নহয়। প্ৰকৃত সফলতাৰ অধিকাৰী কেৱল জান্নাতৰ অধিবাসীসকলহে।');
INSERT INTO "habibur_rahman" VALUES(59,21,'59:21','যদি আমি এই কোৰআনক কোনো পৰ্বতৰ ওপৰত অৱতীৰ্ণ কৰিলোহেঁতেন তেন্তে তুমি ইয়াক আল্লাহৰ ভয়ত বিনীত বিদীৰ্ণ অৱস্থাত দেখিলাহেঁতেন, আৰু আমি এইবোৰ উদাহৰণ বৰ্ণনা কৰো মানুহৰ বাবে যাতে সিহঁতে চিন্তা-গৱেষণা কৰে।');
INSERT INTO "habibur_rahman" VALUES(59,22,'59:22','তেৱেঁই আল্লাহ, তেওঁৰ বাহিৰে আন কোনো সত্য ইলাহ নাই, তেওঁ অদৃশ্য আৰু প্ৰকাশ্য বিষয়সমূহৰ জ্ঞানী; তেওঁ পৰম কৰুণাময় পৰম দয়ালু।');
INSERT INTO "habibur_rahman" VALUES(59,23,'59:23','তেৱেঁই আল্লাহ, তেওঁৰ বাহিৰে আন কোনো সত্য ইলাহ নাই, তেৱেঁই অধিপতি, মহাপৱিত্ৰ, শান্তিৰ উৎস ত্ৰুটিমুক্ত, নিৰাপত্তাদানকাৰী, ৰক্ষক, মহাপৰাক্ৰমশালী, মহাপ্ৰতাপশালী, অতি মহিমান্বিত। সিহঁতে যি অংশীস্থাপন কৰে তাৰ পৰা আল্লাহ পৱিত্ৰ, মহান।');
INSERT INTO "habibur_rahman" VALUES(59,24,'59:24','তেৱেঁই আল্লাহ, স্ৰষ্টা উদ্ভাৱনকৰ্তা, আকাৰ-আকৃতিদানকাৰী, সৰ্বোত্তম নামসমূহ তেওঁৰেই। আকাশমণ্ডল আৰু পৃথিৱীত যি আছে সকলোৱে তেওঁৰ পৱিত্ৰতা আৰু মহিমা ঘোষণা কৰে। তেওঁ পৰাক্ৰমশালী, প্ৰজ্ঞাময়।');
INSERT INTO "habibur_rahman" VALUES(60,1,'60:1','হে ঈমান্দাৰসকল! তোমালোকে মোৰ শত্ৰু আৰু তোমালোকৰ শত্ৰুক বন্ধুৰূপে গ্ৰহণ নকৰিবা, তোমালোকে সিহঁতৰ প্ৰতি বন্ধুত্বৰ বাৰ্তা প্ৰেৰণ কৰিছা, অথচ সিহঁতে, তোমালোকৰ ওচৰত যি সত্য আহিছে, সেইটো প্ৰত্যাখ্যান কৰিছে, ৰাছুলক আৰু তোমালোকক বহিষ্কাৰ কৰিছে এই কাৰণে যে, তোমালোকে তোমালোকৰ প্ৰতিপালক আল্লাহৰ প্ৰতি ঈমান আনিছা। যদি তোমালোকে মোৰ পথত জিহাদ কৰিবলৈ আৰু মোৰ সন্তুষ্টি লাভৰ বাবে ওলাইছা, তেন্তে কিয় তোমালোকে সিহঁতৰ লগত গোপনে বন্ধুত্ব কৰিছা? আৰু তোমালোকে যি গোপন কৰা আৰু তোমালোকে যি প্ৰকাশ কৰা সেই বিষয়ে মই সম্যক অৱগত। তোমালোকৰ মাজত যিয়ে এনেকুৱা কৰিব সি সৰল পথৰ পৰা বিচ্যুত হ’ব।');
INSERT INTO "habibur_rahman" VALUES(60,2,'60:2','সিহঁতে যদি তোমালোকৰ ওপৰত প্ৰধান্য লাভ কৰে তেন্তে সিহঁত তোমালোকৰ শত্ৰু হ’ব লগতে হাত আৰু জিভাৰ দ্বাৰা তোমালোকক অনিষ্ট সাধন কৰিব, আৰু সিহঁতে কামনা কৰে যদি তোমালোকে কুফুৰী কৰিলাহেঁতেন!');
INSERT INTO "habibur_rahman" VALUES(60,3,'60:3','তোমালোকৰ আত্মীয়-স্বজন আৰু সন্তান-সন্ততি ক্বিয়ামতৰ দিনা কোনো উপকাৰ কৰিব নোৱাৰিব। আল্লাহে তোমালোকৰ মাজত ফয়চালা কৰি দিব; আৰু তোমালোকে যি কৰা সেই বিষয়ে আল্লাহ সৰ্বদ্ৰষ্টা।');
INSERT INTO "habibur_rahman" VALUES(60,4,'60:4','নিশ্চয় তোমালোকৰ বাবে ইব্ৰাহীম আৰু তেওঁৰ লগত যিসকল আছিল তেওঁলোকৰ মাজত উত্তম আদৰ্শ আছে। যেতিয়া তেওঁলোকে নিজ সম্প্ৰদায়ক কৈছিল, ‘তোমালোকৰ লগত আৰু তোমালোকে আল্লাহৰ পৰিবৰ্তে যিসকলৰ উপাসনা কৰা সেইসকলৰ পৰা আমি সম্পৰ্কমুক্ত। আমি তোমালোকক অস্বীকাৰ কৰো। আমাৰ আৰু তোমালোকৰ মাজত চিৰকালৰ বাবে শত্ৰুতা আৰু বিদ্বেষ সৃষ্টি হ’ল; যেতিয়ালৈকে তোমালোকে এক আল্লাহৰ প্ৰতি ঈমান পোষণ নকৰিবা’। কিন্তু ইব্ৰাহীমৰ নিজ পিতাৰ প্ৰতি কৰা এই উক্তিটো ব্যতিক্ৰম যে, ‘মই নিশ্চয় আপোনাৰ বাবে ক্ষমা প্ৰাৰ্থনা কৰিম কিন্তু মই আপোনাৰ হকে আল্লাহৰ পৰা কিবা উপকাৰ কৰিবলৈ মোৰ সাধ্য নাই’। (ইব্ৰাহীম আৰু তেওঁৰ অনুসাৰীসকলে কৈছিল) হে আমাৰ প্ৰতিপালক! ‘আমি তোমাৰেই প্ৰতি নিৰ্ভৰ কৰিছো, আৰু তোমাৰেই অভিমুখী হৈছো আৰু তোমাৰ ওচৰলৈকেই আমাৰ প্ৰত্যাৱৰ্তন’।');
INSERT INTO "habibur_rahman" VALUES(60,5,'60:5','‘হে আমাৰ প্ৰতিপালক! তুমি আমাক কাফিৰসকলৰ ফিতনাৰ পাত্ৰ নকৰিবা। হে আমাৰ প্ৰতিপালক! তুমি আমাক ক্ষমা কৰি দিয়া; নিশ্চয় তুমি পৰাক্ৰমশালী, প্ৰজ্ঞাময়’।');
INSERT INTO "habibur_rahman" VALUES(60,6,'60:6','নিশ্চয় তোমালোকৰ বাবে উত্তম আদৰ্শ আছে সেইসকল লোকৰ মাজত, যিসকলে আল্লাহ আৰু শেষ দিৱসৰ প্ৰত্যাশা কৰে; আৰু যিয়ে মুখ ঘূৰাই লয় (সি জানি লোৱা উচিত যে) নিশ্চয় আল্লাহেই হৈছে অভাৱমুক্ত, স্বয়ং প্ৰশংসিত।');
INSERT INTO "habibur_rahman" VALUES(60,7,'60:7','যিসকলৰ লগত তোমালোকৰ শত্ৰুতা আছে সম্ভৱত আল্লাহে সিহঁতৰ আৰু তোমালোকৰ মাজত বন্ধুত্ব সৃষ্টি কৰি দিব; আৰু আল্লাহ ক্ষমতাবান: আৰু আল্লাহ পৰম ক্ষমাশীল, অতি দয়ালু।');
INSERT INTO "habibur_rahman" VALUES(60,8,'60:8','দ্বীনৰ বিষয়ে যিসকলে তোমালোকৰ বিৰুদ্ধে যুদ্ধ কৰা নাই আৰু তোমালোকক স্বদেশৰ পৰা বহিষ্কাৰো কৰা নাই, সিহঁতৰ প্ৰতি মহানুভৱতা দেখুৱাবলৈ আৰু ন্যায়বিচাৰ কৰিবলৈ আল্লাহে তোমালোকক নিষেধ নকৰে। নিশ্চয় আল্লাহ ন্যায়পৰায়ণসকলক ভালপায়।');
INSERT INTO "habibur_rahman" VALUES(60,9,'60:9','আল্লাহে কেৱল সিহঁতৰ লগত বন্ধুত্ব কৰিবলৈ নিষেধ কৰিছে, যিসকলে দ্বীন সম্পৰ্কে তোমালোকৰ বিৰুদ্ধে যুদ্ধ কৰিছে, তোমালোকক স্বদেশৰ পৰা বহিষ্কাৰ কৰিছে আৰু তোমালোকক বহিষ্কাৰ কৰিবলৈ সহায় কৰিছে। সিহঁতৰ লগত যিহঁতে বন্ধুত্ব কৰে সিহঁতেই যালিম।');
INSERT INTO "habibur_rahman" VALUES(60,10,'60:10','হে ঈমান্দাৰসকল! তোমালোকৰ ওচৰত মুমিনা নাৰীসকল হিজৰত কৰি আহিলে তোমালোকে সিহঁতক পৰীক্ষা কৰিবা; আল্লাহেই সিহঁতৰ ঈমান সম্পৰ্কে সম্যক অৱগত। তাৰ পিছত যদি তোমালোকে জানিব পাৰা যে, সিহঁত মুমিনা নাৰী, তেন্তে সিহঁতক কাফিৰসকলৰ ওচৰলৈ উভতাই নিদিবা। মুমিনা নাৰীসকল কাফিৰসকলৰ বাবে বৈধ নহয় আৰু কাফিৰসকলো মুমিনা নাৰীসকলৰ বাবে বৈধ নহয়। কাফিৰসকলে যি খৰচ কৰিছে সেয়া তোমালোকে সিহঁতক উভতাই দিয়া। তাৰ পিছত তোমালোকে সিহঁতক বিবাহ কৰিলে তোমালোকৰ কোনো অপৰাধ নহ’ব যদি তোমালোকে সিহঁতক মোহৰ আদায় দিয়া; আৰু তোমালোকে কাফিৰা নাৰীসকলৰ লগত দাম্পত্য সম্পৰ্ক বজায় নাৰাখিবা। তোমালোকে যি ব্যয় কৰিছা সেয়া উভতাই লবা আৰু কাফিৰসকলে যি ব্যয় কৰিছে সেয়া সিহঁতে বিচাৰি লোৱা উচিত। এইটোৱে আল্লাহৰ বিধান; তেওঁ তোমালোকৰ মাজত ফয়চালা কৰি দিয়ে; আৰু আল্লাহ সৰ্বজ্ঞ, প্ৰজ্ঞাময়।');
INSERT INTO "habibur_rahman" VALUES(60,11,'60:11','আৰু তোমালোকৰ স্ত্ৰীসকলৰ মাজত যদি কোনোবাই হাত এৰাদি কাফিৰসকলৰ ওচৰত গুচি যায়, তাৰ পিছত যদি তোমালোকে যুদ্ধত বিজয়ী হৈ গনীমত লাভ কৰা, তেনেহ’লে যিসকলৰ স্ত্ৰীসকল গুচি গৈছে, সিহঁতক সিহঁতে যি ব্যয় কৰিছে, তাৰ সমপৰিমাণ প্ৰদান কৰা; আৰু তোমালোকে আল্লাহক ভয় কৰা, যাৰ প্ৰতি তোমালোকে বিশ্বাস স্থাপন কৰিছা।');
INSERT INTO "habibur_rahman" VALUES(60,12,'60:12','হে নবী! যেতিয়া মুমিনা নাৰীসকলে তোমাৰ ওচৰত আহি এই সম্পৰ্কে বাইআত কৰে যে, সিহঁতে আল্লাহৰ লগত আন কাকো অংশীদাৰ স্থাপন নকৰিব, চুৰি নকৰিব, ব্যভিচাৰ নকৰিব, নিজ সন্তানসকলক হত্যা নকৰিব, ইচ্ছাকৃতভাৱে নিজে অপবাদ ৰচনা কৰি ৰটনা কৰি নুফুৰিব আৰু সৎকামত তোমাৰ অবাধ্য নহ’ব, তেতিয়া তুমি সিহঁতৰ বাইআত গ্ৰহণ কৰা আৰু সিহঁতৰ বাবে আল্লাহৰ ওচৰত ক্ষমা প্ৰাৰ্থনা কৰা। নিশ্চয় আল্লাহ ক্ষমাশীল, পৰম দয়ালু।');
INSERT INTO "habibur_rahman" VALUES(60,13,'60:13','হে ঈমান্দাৰসকল! তোমালোকে সেই সম্প্ৰদায়ৰ লগত বন্ধুত্ব নকৰিবা, যিসকলৰ প্ৰতি আল্লাহ ক্ৰোধান্বিত হৈছে। সিহঁতে আখিৰাত সম্পৰ্কে নিৰাশ হৈ পৰিছে, যেনেকৈ কাফিৰসকলে কবৰবাসী সম্পৰ্কে নিৰাশ হৈ পৰিছে।');
INSERT INTO "habibur_rahman" VALUES(61,1,'61:1','আকাশমণ্ডল আৰু পৃথিৱীত যি আছে সেই সকলোবোৰেই আল্লাহৰ পৱিত্ৰতা আৰু মহিমা ঘোষণা কৰে; আৰু তেওঁ পৰাক্ৰমশালী প্ৰজ্ঞাময়।');
INSERT INTO "habibur_rahman" VALUES(61,2,'61:2','হে ঈমান্দাৰসকল! তোমালোকে যিটো নকৰা সেইটো কিয় কোৱা?');
INSERT INTO "habibur_rahman" VALUES(61,3,'61:3','তোমালোকে যিটো নকৰা তোমালোকৰ সেইটো কোৱা আল্লাহৰ দৃষ্টিত বৰ অসন্তোষজনক।');
INSERT INTO "habibur_rahman" VALUES(61,4,'61:4','নিশ্চয় আল্লাহ তেওঁলোকক ভালপায়, যিসকলে তেওঁৰ পথত শাৰীবদ্ধভাৱে যুদ্ধ কৰে; যেনিবা সিহঁত গলিত সীহেৰে নিৰ্মিত সুদৃঢ় প্ৰাচীৰ।');
INSERT INTO "habibur_rahman" VALUES(61,5,'61:5','আৰু স্মৰণ কৰা, মূছাই যেতিয়া তেওঁৰ সম্প্ৰদায়ক কৈছিল, ‘হে মোৰ সম্প্ৰদায়! তোমালোকে কিয় মোক কষ্ট দি আছা? অথচ তোমালোকে জানা যে, নিশ্চয় মই তোমালোকৰ প্ৰতি (প্ৰেৰিত) আল্লাহৰ ৰাছুল’। তাৰ পিছত সিহঁতে যেতিয়া বক্ৰ পথ অৱলম্বন কৰিলে তেতিয়া আল্লাহে সিহঁতৰ হৃদয়সমূহক বক্ৰ কৰি দিলে; আৰু আল্লাহে ফাছিক্ব সম্প্ৰদায়ক হিদায়ত নকৰে।');
INSERT INTO "habibur_rahman" VALUES(61,6,'61:6','আৰু স্মৰণ কৰা, যেতিয়া মাৰয়ামৰ পুত্ৰ ঈছাই কৈছিল, হে বনী ইছৰাঈল! ‘নিশ্চয় মই তোমালোকৰ প্ৰতি প্ৰেৰিত আল্লাহৰ ৰাছুল আৰু মোৰ পূৰ্বৱৰ্তী তাওৰাতৰ সত্যায়নকাৰী আৰু এজন ৰাছুলৰ সুসংবাদদাতা, যিজন মোৰ পিছত আহিব, যাৰ নাম আহমদ’। তাৰ পিছত তেওঁ যেতিয়া সুস্পষ্ট নিদৰ্শনসমূহ লৈ আগমন কৰিলে, তেতিয়া সিহঁতে ক’লে, ‘এয়া স্পষ্ট যাদু’।');
INSERT INTO "habibur_rahman" VALUES(61,7,'61:7','সেই ব্যক্তিতকৈ অধিক যালিম আৰু কোন হ’ব পাৰে? যিয়ে আল্লাহ সম্পৰ্কে মিছা ৰচনা কৰে, অথচ তাক ইছলামৰ প্ৰতি আহ্বান কৰা হয়; আৰু আল্লাহে যালিম সম্প্ৰদায়ক হিদায়ত নকৰে।');
INSERT INTO "habibur_rahman" VALUES(61,8,'61:8','সিহঁতে আল্লাহৰ জ্যোতিক মুখেৰে ফুঁ মাৰি নিৰ্বাপিত কৰিব বিচাৰে, আৰু আল্লাহে নিজ জ্যোতিক পূৰ্ণতাদানকাৰী। যদিও কাফিৰসকলে ইয়াক অপছন্দ কৰে।');
INSERT INTO "habibur_rahman" VALUES(61,9,'61:9','তেৱেঁই নিজ ৰাছুলক প্ৰেৰণ কৰিছে হিদায়ত আৰু সত্য দ্বীনসহ সকলো দ্বীনৰ ওপৰত তেওঁক বিজয়ী কৰাৰ বাবে, যদিও মুশ্বৰিকসকলে ইয়াক অপছন্দ কৰে।');
INSERT INTO "habibur_rahman" VALUES(61,10,'61:10','হে ঈমান্দাৰসকল! মই তোমালোকক এনে এটা ব্যৱসায়ৰ সন্ধান দিমনে, যিটো তোমালোকক যন্ত্ৰণাদায়ক শাস্তিৰ পৰা ৰক্ষা কৰিব?');
INSERT INTO "habibur_rahman" VALUES(61,11,'61:11','সেয়া হৈছে, তোমালোকে আল্লাহ আৰু তেওঁৰ ৰাছুলৰ প্ৰতি ঈমান আনিবা আৰু তোমালোকে নিজৰ ধন-সম্পদ আৰু জীৱনৰ দ্বাৰা আল্লাহৰ পথত জিহাদ কৰিবা। এইটোৱেই তোমালোকৰ বাবে কল্যাণকৰ, যদি তোমালোকে জানা।');
INSERT INTO "habibur_rahman" VALUES(61,12,'61:12','তেওঁ তোমালোকৰ বাবে তোমালোকৰ পাপসমূহ ক্ষমা কৰি দিব; আৰু তোমালোকক এনে জান্নাতত প্ৰৱেশ কৰাব যাৰ তলেদি নদীসমূহ প্ৰবাহিত, আৰু চিৰস্থায়ী জান্নাতসমূহৰ উত্তম বাসগৃহসমূহতো (প্ৰৱেশ কৰাব)। এইটোৱে মহাসফলতা।');
INSERT INTO "habibur_rahman" VALUES(61,13,'61:13','লগতে (তেওঁ প্ৰদান কৰিব) আৰু এটা অনুগ্ৰহ, যিটো তোমালোকে পছন্দ কৰা। (অৰ্থাৎ) আল্লাহৰ তৰফৰ পৰা সহায় আৰু নিকটবৰ্তী বিজয়; আৰু তুমি মুমিনসকলক সুসংবাদ দিয়া।');
INSERT INTO "habibur_rahman" VALUES(61,14,'61:14','হে ঈমান্দাৰসকল! তোমালোকে আল্লাহৰ সহায়কাৰী হোৱা, যেনেকৈ মাৰয়ামৰ পুত্ৰ ঈছাই হাৱাৰীসকলক কৈছিল, ‘আল্লাহৰ পথত কোনে মোৰ সহায়ক হ’বা’? হাৱাৰীসকলে কৈছিল, ‘আমিয়েই আল্লাহৰ পথত সহায়কাৰী’। তাৰ পিছত বনী ইছৰাঈলৰ এটা দলে ঈমান আনিলে আৰু আন এটা দলে কুফুৰী কৰিলে। সেয়ে, যিসকলে ঈমান আনিছিল আমি তেওঁলোকক তেওঁলোকৰ শত্ৰুবাহিনীৰ ওপৰত শক্তিশালী কৰিলো। ফলত তেওঁলোক বিজয়ী হ’ল।');
INSERT INTO "habibur_rahman" VALUES(62,1,'62:1','আকাশমণ্ডলত যি যি আছে আৰু পৃথিৱীত যি যি আছে সকলোৱে আল্লাহৰ পৱিত্ৰতা আৰু মহিমা ঘোষণা কৰে, যিজন অধিপতি, মহাপৱিত্ৰ, পৰাক্ৰমশালী, প্ৰজ্ঞাময়।');
INSERT INTO "habibur_rahman" VALUES(62,2,'62:2','তেৱেঁই উম্মীসকলৰ মাজত সিহঁতৰ মাজৰ পৰাই এজন ৰাছুল প্ৰেৰণ কৰিছে, যিজনে সিহঁতৰ ওচৰত তেওঁৰ আয়াতসমূহ তিলাৱাত কৰে, সিহঁতক পৱিত্ৰ কৰে আৰু সিহঁতক কিতাপ ও হিকমতৰ শিক্ষা দিয়ে; যদিও ইতিপূৰ্বে সিহঁত ঘোৰ বিভ্ৰান্তিত আছিল;');
INSERT INTO "habibur_rahman" VALUES(62,3,'62:3','আৰু সিহঁতৰ মাজৰ পৰা অন্যান্যসকলৰ প্ৰতিও, (এই ৰাছুলকেই প্ৰেৰণ কৰা হৈছে) যিসকলে এতিয়াও সিহঁতৰ লগত মিলিত হোৱা নাই আৰু আল্লাহ মহাপৰাক্ৰমশালী, প্ৰজ্ঞাময়।');
INSERT INTO "habibur_rahman" VALUES(62,4,'62:4','এইটো আল্লাহৰ অনুগ্ৰহ, তেওঁ যাক ইচ্ছা তাক সেয়া দান কৰে; আৰু আল্লাহ মহানুগ্ৰহৰ অধিকাৰী।');
INSERT INTO "habibur_rahman" VALUES(62,5,'62:5','যিসকলক তাওৰাতৰ দায়িত্বভাৰ দিয়া হৈছিল, কিন্তু সিহঁতে ইয়াক বহন কৰা নাছিল। সিহঁতৰ উদাহৰণ হৈছে- গাধৰ দৰে! যিয়ে বহু কিতাপৰ বোজা বহন কৰে। কিমান যে নিকৃষ্ট সেই সম্প্ৰদায়ৰ দৃষ্টান্ত যিহঁতে আল্লাহৰ আয়াতসমূহক মিছা বুলি অমান্য কৰে; আৰু আল্লাহে যালিম সম্প্ৰদায়ক হিদায়ত নকৰে।');
INSERT INTO "habibur_rahman" VALUES(62,6,'62:6','কোৱা, হে ইয়াহুদীসকল! যদি তোমালোকে ভাবা যে, আন আন মানুহৰ বাহিৰে অকল তোমালোকেই আল্লাহৰ বন্ধু, তেন্তে তোমালোকে মৃত্যু কামনা কৰা যদি তোমালোকে সত্যবাদী।');
INSERT INTO "habibur_rahman" VALUES(62,7,'62:7','কিন্তু সিহঁতৰ হাতে আগলৈ যি প্ৰেৰণ কৰিছে তাৰ কাৰণে সিহঁতে কেতিয়াও মৃত্যু কামনা নকৰিব; আৰু আল্লাহ যালিমসকলৰ বিষয়ে সম্যক অৱগত।');
INSERT INTO "habibur_rahman" VALUES(62,8,'62:8','কোৱা, যি মৃত্যুৰ পৰা তোমালোকে পলায়ন কৰা সেয়া নিশ্চয় তোমালোকৰ লগত সাক্ষাৎ কৰিব। তাৰ পিছত তোমালোকক প্ৰত্যাবৰ্তন কৰোৱা হ’ব গায়েব আৰু প্ৰকাশ্যৰ জ্ঞান আল্লাহৰ ওচৰত। তেওঁ তোমালোকক জনাই দিব যি তোমালোকে আমল কৰিছিলা।');
INSERT INTO "habibur_rahman" VALUES(62,9,'62:9','হে ঈমান্দাৰসকল! জুমুআৰ দিনা যেতিয়া ছালাতৰ বাবে আহ্বান কৰা হয়, তেতিয়া তোমালোকে আল্লাহৰ স্মৰণৰ ফালে ধাৱিত হোৱা আৰু বেচা-কিনা বন্ধ কৰা। এইটোৱে তোমালোকৰ বাবে সৰ্বোত্তম, যদি তোমালোকে উপলব্ধি কৰা।');
INSERT INTO "habibur_rahman" VALUES(62,10,'62:10','তাৰ পিছত ছালাত সমাপ্ত হ’লে তোমালোকে পৃথিৱীত প্ৰসাৰিত হৈ যোৱা আৰু আল্লাহৰ অনুগ্ৰহ সন্ধান কৰা লগতে আল্লাহক বেছি বেছি স্মৰণ কৰা, যাতে তোমালোকে সফলতা অৰ্জন কৰিব পাৰা।');
INSERT INTO "habibur_rahman" VALUES(62,11,'62:11','আৰু সিহঁতে যেতিয়া ব্যৱসায় অথবা ক্ৰীড়া-কৌতুক দেখা পায় তেতিয়া সিহঁতে তোমাক থিয় অৱস্থাতেই ৰাখি সেইফালে ধাৱিত হয়। কোৱা, আল্লাহৰ ওচৰত যি আছে সেয়া ক্ৰীড়া-কৌতুক আৰু ব্যৱসায়-বাণিজ্যতকৈয়ো উৎকৃষ্ট; আৰু আল্লাহ সৰ্বশ্ৰেষ্ঠ জীৱিকাদাতা।');
INSERT INTO "habibur_rahman" VALUES(63,1,'63:1','যেতিয়া মুনাফিকসকল তোমাৰ ওচৰত আহে তেতিয়া সিহঁতে কয়, আমি সাক্ষ্য দিওঁ যে, ‘নিশ্চয় আপুনি আল্লাহৰ ৰাছুল’; আৰু আল্লাহে জানে যে, তুমি নিশ্চয় তেওঁৰ ৰাছুল আৰু আল্লাহে সাক্ষ্য দিয়ে যে, নিশ্চয় মুনাফিকসকল মিছলীয়া।');
INSERT INTO "habibur_rahman" VALUES(63,2,'63:2','সিহঁতে নিজৰ শপতবোৰক ঢালৰূপে ব্যৱহাৰ কৰে। ফলত সিহঁতে আল্লাহৰ পথৰ পৰা মানুহক নিবৃত্ত কৰে। সিহঁতে যি কৰে সেয়া কিমান যে নিকৃষ্ট!');
INSERT INTO "habibur_rahman" VALUES(63,3,'63:3','এয়া এই বাবেই যে, সিহঁতে ঈমান আনাৰ পিছত কুফুৰী কৰিছে। ফলত সিহঁতৰ অন্তৰত মোহৰ মাৰি দিয়া হৈছে; সেয়ে সিহঁত নুবুজা হৈছে।');
INSERT INTO "habibur_rahman" VALUES(63,4,'63:4','আৰু তুমি যেতিয়া সিহঁতৰ প্ৰতি লক্ষ্য কৰিবা তেতিয়া সিহঁতৰ দেহৰ আকৃতি তোমাক মুগ্ধ কৰিব আৰু সিহঁতে যেতিয়া কথা কয় তুমি সিহঁতৰ কথা আগ্ৰহেৰে শুনি থাকা। সিহঁত বেৰত লগাই থোৱা কাঠৰ খুটাৰ দৰে; সিহঁতে যিকোনো শব্দক নিজৰ বিৰুদ্ধে ধাৰণা কৰে। সিহঁতেই শত্ৰু, এতেকে সিহঁতৰ বিষয়ে সতৰ্ক হোৱা; আল্লাহে সিহঁতক ধ্বংস কৰক! সিহঁতক ক’লৈ উভতাই নিয়া হৈছে!');
INSERT INTO "habibur_rahman" VALUES(63,5,'63:5','আৰু যেতিয়া সিহঁতক কোৱা হয়, ‘আহা, আল্লাহৰ ৰাছুলে তোমালোকৰ বাবে ক্ষমা প্ৰাৰ্থনা কৰিব’, তেতিয়া সিহঁতে মূৰ জুকাৰি অস্বীকৃতি জনায়, আৰু তুমি সিহঁতক অহংকাৰ কৰি বিমুখ হৈ গুচি যোৱা দেখিবলৈ পাবা।');
INSERT INTO "habibur_rahman" VALUES(63,6,'63:6','তুমি সিহঁতৰ বাবে ক্ষমা প্ৰাৰ্থনা কৰা বা নকৰা, উভয়টো সিহঁতৰ বাবে সমান। আল্লাহে সিহঁতক কেতিয়াও ক্ষমা নকৰিব। নিশ্চয় আল্লাহ ফাছিক্ব সম্প্ৰদায়ক হিদায়ত নিদিয়ে।');
INSERT INTO "habibur_rahman" VALUES(63,7,'63:7','সিহঁতে কয়, ‘তোমালোকে আল্লাহৰ ৰাছুলৰ সহচৰসকলৰ বাবে ব্যয় নকৰিবা, যাতে সিহঁত আঁতৰি যায়’। অথচ আকাশমণ্ডল আৰু পৃথিৱীৰ ধন-ভাণ্ডাৰ আল্লাহৰেই, কিন্তু মুনাফিক্বসকলে নুবুজে।');
INSERT INTO "habibur_rahman" VALUES(63,8,'63:8','সিহঁতে কয়, ‘আমি মদীনাত উভতি গ’লে নিশ্চয় তাৰ পৰা শক্তিশালীসকলে দুৰ্বলসকলক বহিষ্কাৰ কৰিব’। অথচ শক্তি-সন্মান কেৱল আল্লাহৰ আৰু তেওঁৰ ৰাছুলৰ আৰু মুমিনসকলৰ। কিন্তু মুনাফিক্বসকলে সেয়া নাজানে।');
INSERT INTO "habibur_rahman" VALUES(63,9,'63:9','হে ঈমান্দাৰসকল! তোমালোকৰ ধন-সম্পদ আৰু সন্তান-সন্ততি যাতে তোমালোকক আল্লাহৰ স্মৰণৰ পৰা উদাসীন নকৰে; আৰু যিহঁত এনেকুৱা উদাসীন হ’ব সিহঁতেই ক্ষতিগ্ৰস্ত।');
INSERT INTO "habibur_rahman" VALUES(63,10,'63:10','আৰু মই তোমালোকক যি জীৱিকা দান কৰিছো তোমালোকৰ ওচৰত মৃত্যু অহাৰ আগতে তাৰ পৰা ব্যয় কৰা, (কাৰণ মৃত্যু অহাৰ পিছত সি ক’ব) হে মোৰ প্ৰতিপালক! ‘মোক আৰু কিছু সময়ৰ বাবে অৱকাশ দিলে মই ছাদক্বা কৰিলোহেঁতেন আৰু সৎকৰ্মপৰায়ণসকলৰ অন্তৰ্ভুক্ত হ’লোহেঁতেন’!');
INSERT INTO "habibur_rahman" VALUES(63,11,'63:11','আৰু যেতিয়া কাৰোবাৰ নিৰ্দিষ্ট সময় উপস্থিত হ’ব, তেতিয়া আল্লাহে তাক কেতিয়াও অৱকাশ নিদিব। তোমালোকে যি আমল কৰা সেই সম্পৰ্কে আল্লাহ সবিশেষ অৱগত।');
INSERT INTO "habibur_rahman" VALUES(64,1,'64:1','আকাশমণ্ডলত যি যি আছে আৰু পৃথিৱীত যি যি আছে সকলোৱে আল্লাহৰ পৱিত্ৰতা আৰু মহিমা ঘোষণা কৰে, তেওঁৰেই আধিপত্য আৰু তেওঁৰেই প্ৰশংসা; আৰু তেওঁ সকলো বস্তুৰ ওপৰত ক্ষমতাৱান।');
INSERT INTO "habibur_rahman" VALUES(64,2,'64:2','তেৱেঁই তোমালোকক সৃষ্টি কৰিছে। তাৰ পিছত তোমালোকৰ মাজত কিছুমান হৈছে কাফিৰ আৰু কিছুমান হৈছে মুমিন; আৰু তোমালোকে যি আমল কৰা সেই সম্পৰ্কে আল্লাহ সম্যক দ্ৰষ্টা।');
INSERT INTO "habibur_rahman" VALUES(64,3,'64:3','তেৱেঁই আকাশমণ্ডল আৰু পৃথিৱীক যথাযথভাৱে সৃষ্টি কৰিছে আৰু তোমালোকক আকৃতি দান কৰিছে, তাৰ পিছত তোমালোকৰ আকৃতি উৎকৃষ্ট কৰিছে; আৰু প্ৰত্যাবৰ্তন তেওঁৰ ফালেই।');
INSERT INTO "habibur_rahman" VALUES(64,4,'64:4','আকাশমণ্ডলত আৰু পৃথিৱীত যি আছে সকলো তেওঁ জানে আৰু তোমালোকে যি গোপন কৰা আৰু যি প্ৰকাশ কৰা সেয়াও তেওঁ জানে; আৰু অন্তৰসমূহত যি আছে সেই বিষয়ে আল্লাহ সম্যক জ্ঞানী।');
INSERT INTO "habibur_rahman" VALUES(64,5,'64:5','ইয়াৰ আগত যিসকলে কুফুৰী কৰিছে সিহঁতৰ সংবাদ তোমালোকৰ ওচৰত আহি পোৱা নাইনে? সিহঁতে নিজ কৰ্মৰ বেয়া পৰিণতি ভোগ কৰিছে আৰু সিহঁতৰ বাবে আছে যন্ত্ৰণাদায়ক শাস্তি।');
INSERT INTO "habibur_rahman" VALUES(64,6,'64:6','এয়া এই বাবেই যে, সিহঁতৰ ওচৰত সিহঁতৰ ৰাছুলসকলে সুস্পষ্ট প্ৰমাণাদিসহ আহিছিল অথচ সিহঁতে কৈছিল, ‘মানুহেই আমাক পথৰ সন্ধান দিব নেকি’? তাৰ পিছত সিহঁতে কুফুৰী কৰিলে আৰু মুখ ঘূৰাই ল’লে; আৰু আল্লাহেও (সিহঁতৰ ঈমানৰ বিষয়ে) ভ্ৰুক্ষেপ নকৰিলে; আৰু আল্লাহ অভাৱমুক্ত, সপ্ৰশংসিত।');
INSERT INTO "habibur_rahman" VALUES(64,7,'64:7','কাফিৰসকলে ধাৰণা কৰে যে, সিহঁতক কেতিয়াও পুনৰুত্থিত কৰা নহ’ব। কোৱা, ‘নিশ্চয় কৰা হ’ব, মোৰ প্ৰতিপালকৰ শপত! তোমালোকক নিশ্চয় পুনৰুত্থিত কৰা হ’ব। তাৰ পিছত তোমালোকে যি কৰিছিলা সেই বিষয়ে তোমালোকক নিশ্চয় অৱগত কৰোৱা হ’ব আৰু এইটো আল্লাহৰ পক্ষে তেনেই সহজ’।');
INSERT INTO "habibur_rahman" VALUES(64,8,'64:8','এতেকে তোমালোকে আল্লাহ আৰু তেওঁৰ ৰাছুলৰ লগতে আমি যি নূৰ অৱতীৰ্ণ কৰিছো তাৰ প্ৰতি ঈমান আনা; আৰু তোমালোকৰ কৃতকৰ্ম সম্পৰ্কে আল্লাহ সবিশেষ অৱহিত।');
INSERT INTO "habibur_rahman" VALUES(64,9,'64:9','স্মৰণ কৰা, যিদিনা তেওঁ তোমালোকক সমাবেশ দিৱসৰ উদ্দেশ্যে সমবেত কৰিব, সেইদিনটোৱে হ’ব ক্ষতিৰ দিন; আৰু যিজনে আল্লাহৰ প্ৰতি ঈমান ৰাখে আৰু সৎকাম কৰে, তেওঁ তাৰ পাপসমূহ মোচন কৰি দিব আৰু তাক জান্নাতত প্ৰৱেশ কৰাব, যাৰ তলত নদীসমূহ প্ৰবাহিত, তাত সিহঁত চিৰস্থায়ী হ’ব। এইটোৱে মহাসাফলতা।');
INSERT INTO "habibur_rahman" VALUES(64,10,'64:10','কিন্তু যিহঁতে কুফুৰী কৰে আৰু আমাৰ আয়াতসমূহক অস্বীকাৰ কৰে সিহঁতেই জাহান্নামৰ অধিবাসী, তাত সিহঁত চিৰকাল অৱস্থান কৰিব। কিমান নিকৃষ্ট সেই প্ৰত্যাবৰ্তনস্থল।');
INSERT INTO "habibur_rahman" VALUES(64,11,'64:11','আল্লাহৰ অনুমতিবিহীন কোনো বিপদেই আপতিত নহয়। যিয়ে আল্লাহক বিশ্বাস কৰে তেওঁ তাৰ অন্তৰক সৎপথত পৰিচালিত কৰে। আল্লাহ সকলো বিষয়ে সম্যক অৱগত।');
INSERT INTO "habibur_rahman" VALUES(64,12,'64:12','তোমালোকে আল্লাহৰ আনুগত্য কৰা আৰু ৰাছুলৰ আনুগত্য কৰা। কিন্তু তোমালোকে যদি মুখ ঘূৰাই লোৱা তেন্তে (জানি থোৱা) মোৰ ৰাছুলৰ দায়িত্ব কেৱল স্পষ্টভাৱে প্ৰচাৰ কৰা।');
INSERT INTO "habibur_rahman" VALUES(64,13,'64:13','আল্লাহ, তেওঁৰ বাহিৰে আন কোনো (সত্য) ইলাহ নাই আৰু আল্লাহৰ ওপৰতেই মুমিনসকলে তাৱাক্কুল কৰা উচিত।');
INSERT INTO "habibur_rahman" VALUES(64,14,'64:14','হে ঈমান্দাৰসকল! তোমালোকৰ স্ত্ৰী-স্বামী আৰু সন্তান-সন্ততিসকলৰ মাজত কিছুমান তোমালোকৰ শত্ৰু; এতেকে সিহঁতৰ বিষয়ে তোমালোকে সতৰ্ক থাকিবা। যদি তোমালোকে সিহঁতক মাৰ্জনা কৰি দিয়া, সিহঁতৰ দোষ-ত্ৰুটি উপেক্ষা কৰা আৰু সিহঁতক ক্ষমা কৰি দিয়া, তেন্তে নিশ্চয় আল্লাহ ক্ষমাশীল, পৰম দয়ালু।');
INSERT INTO "habibur_rahman" VALUES(64,15,'64:15','নিশ্চয় তোমালোকৰ ধন-সম্পদ আৰু সন্তান-সন্ততি কেৱল এটা পৰীক্ষা বিশেষ; আৰু আল্লাহৰ ওচৰতেই আছে মহাপুৰষ্কাৰ।');
INSERT INTO "habibur_rahman" VALUES(64,16,'64:16','এতেকে তোমালোকে যথাসাধ্য আল্লাহৰ তাক্বৱা অৱলম্বন কৰা, শ্ৰৱণ কৰা, আনুগত্য কৰা আৰু তোমালোকৰ নিজৰ কল্যাণৰ বাবে ব্যয় কৰা; আৰু যিসকলৰ অন্তৰ কাৰ্পণ্যৰ পৰা ৰক্ষা কৰা হয়, সিহঁতেই সাফল্যমণ্ডিত।');
INSERT INTO "habibur_rahman" VALUES(64,17,'64:17','যদি তোমালোকে আল্লাহক উত্তম ঋণ প্ৰদান কৰা, তেওঁ তোমালোকৰ বাবে তাক বহু গুণ বৃদ্ধি কৰি দিব আৰু তেওঁ তোমালোকক ক্ষমা কৰিব; আৰু আল্লাহ গুণগ্ৰাহী, পৰম ধৈৰ্যশীল।');
INSERT INTO "habibur_rahman" VALUES(64,18,'64:18','তেওঁ দৃশ্য আৰু অদৃশ্য যাৱতীয় বিষয়ৰ জ্ঞানী, পৰাক্ৰমশালী, প্ৰজ্ঞাময়।');
INSERT INTO "habibur_rahman" VALUES(65,1,'65:1','হে নবী (কোৱা), তোমালোকে যেতিয়া স্ত্ৰীসকলক তালাক দিবা, তেতিয়া সিহঁতক সিহঁতৰ ইদ্দতৰ প্ৰতি লক্ষ্য ৰাখি তালাক দিবা আৰু তোমালোকে ইদ্দতৰ হিচাপ ৰাখিবা; আৰু তোমালোকৰ প্ৰতিপালক আল্লাহৰ তাক্বৱা অৱলম্বন কৰা। তোমালোকে সিহঁতক সিহঁতৰ ঘৰৰ পৰা বহিষ্কাৰ নকৰিবা আৰু সিহঁত নিজেও বাহিৰ নহ’ব, যদি সিহঁতে কোনো স্পষ্ট অশ্লিলতাত লিপ্ত নহয়। এইবোৰ আল্লাহৰ নিৰ্ধাৰিত সীমাৰেখা; আৰু যিয়ে আল্লাহৰ সীমাৰেখা লঙ্ঘন কৰে সি নিজৰ ওপৰত অত্যাচাৰ কৰে। তুমি নাজানা, সম্ভৱত আল্লাহে ইয়াৰ পিছত কোনো উপায় উদ্ভাৱন কৰি দিব।');
INSERT INTO "habibur_rahman" VALUES(65,2,'65:2','তাৰ পিছত যেতিয়া সিহঁতে সিহঁতৰ ইদ্দতৰ শেষ সীমাত উপনীত হ’ব, তেতিয়া তোমালোকে সিহঁতক যথাবিধি ৰাখি দিবা নহ’লে সিহঁতক যথাবিধি পৰিত্যাগ কৰিবা আৰু তোমালোকৰ মাজৰ পৰা দুজন ন্যায়পৰায়ণ লোকক সাক্ষী ৰাখিবা; আৰু তোমালোকে আল্লাহৰ বাবে সঠিকভাৱে সাক্ষ্য দিবা। ইয়াৰ দ্বাৰা তোমালোকৰ মাজত যিয়ে আল্লাহ আৰু শেষ দিৱসৰ প্ৰতি ঈমান ৰাখে তাক উপদেশ দিয়া হৈছে; আৰু যিয়ে আল্লাহৰ তাক্বৱা অৱলম্বন কৰিব আল্লাহে তাৰ বাবে উত্তৰণৰ পথ উলিয়াই দিব,');
INSERT INTO "habibur_rahman" VALUES(65,3,'65:3','আৰু তেওঁ তাক তাৰ ধাৰণাতীত উৎসৰ পৰা জীৱিকা দান কৰিব; আৰু যি ব্যক্তি আল্লাহৰ প্ৰতি তাৱাক্কুল কৰিব তাৰ বাবে আল্লাহেই যথেষ্ট। নিশ্চয় আল্লাহে তেওঁৰ উদ্দেশ্য পূৰণ কৰিবই; নিশ্চয় আল্লাহে প্ৰতিটো বস্তুৰ বাবে এটা সময়সীমা নিৰ্ধাৰণ কৰি দিছে।');
INSERT INTO "habibur_rahman" VALUES(65,4,'65:4','তোমালোকৰ স্ত্ৰীসকলৰ মাজত যিসকলে ঋতুমতি হোৱাৰ কাল অতিক্ৰম কৰিছে, সিহঁতৰ ইদ্দত সম্পৰ্কে যদি তোমালোকে সংশয় কৰা তেনেহ’লে সিহঁতৰ ইদ্দতকাল হ’ব তিনি মাহ আৰু যিসকলে এতিয়াও ঋতুৰ বয়সত উপনীত হোৱা নাই সিহঁতৰো (তিনি মাহ); আৰু গৰ্ভৱতী নাৰীসকলৰ ইদ্দতকাল সন্তান প্ৰসৱ নোহোৱা লৈকে; আৰু যিয়ে আল্লাহৰ তাক্বৱা অৱলম্বন কৰে আল্লাহে তাৰ বাবে তাৰ কৰ্মক সহজ কৰি দিয়ে।');
INSERT INTO "habibur_rahman" VALUES(65,5,'65:5','এইটো আল্লাহৰ বিধান, যিটো তেওঁ তোমালোকৰ প্ৰতি অৱতীৰ্ণ কৰিছে; আৰু যিয়ে আল্লাহৰ তাক্বৱা অৱলম্বন কৰিব তেওঁ তাৰ পাপসমূহ মোচন কৰি দিব আৰু তাক প্ৰদান কৰিব মহাপুৰষ্কাৰ।');
INSERT INTO "habibur_rahman" VALUES(65,6,'65:6','তোমালোকৰ সামৰ্থ অনুযায়ী য’ত তোমালোকে বসবাস কৰা তাতেই সিহঁতকো বাস কৰিবলৈ দিয়া, সিহঁতক সঙ্কটত পেলাবলৈ কষ্ট নিদিবা; আৰু সিহঁত গৰ্ভৱতী থাকিলে সন্তান প্ৰসৱ নকৰালৈকে সিহঁতৰ বাবে ব্যয় কৰা। তাৰ পিছত যদি সিহঁতে তোমালোকৰ সন্তানসকলক পিয়াহ খুৱায় তেনেহ’লে সিহঁতক পাৰিশ্ৰমিক প্ৰদান কৰিবা আৰু (সন্তানৰ কল্যাণ সম্পৰ্কে) তোমালোকে নিজৰ মাজতেই সংগতভাৱে পৰামৰ্শ কৰা; আৰু যদি তোমালোকে পৰস্পৰে ঐক্যমত হ’বলৈ টান পোৱা তেনেহ’লে পিতাৰ পক্ষৰ পৰা আন তিৰোতাই পিয়াহ খুৱাব।');
INSERT INTO "habibur_rahman" VALUES(65,7,'65:7','বিত্তবানে নিজ সামৰ্থ অনুযায়ী ব্যয় কৰিব আৰু যাৰ জীৱনোপকৰণ সীমিত তাক আল্লাহে যি দান কৰিছে তাৰ পৰা ব্যয় কৰিব। আল্লাহে যাক যি সামৰ্থ দিছে তাতকৈ বেছি গুৰুতৰ বোজা তেওঁ তাৰ ওপৰত জাপি নিদিয়ে। নিশ্চয় আল্লাহে কঠিন অৱস্থাৰ পিছত সচ্চলতা প্ৰদান কৰিব।');
INSERT INTO "habibur_rahman" VALUES(65,8,'65:8','আৰু বহুতো জনপদে সিহঁতৰ প্ৰতিপালক আৰু তেওঁৰ ৰাছুলৰ নিৰ্দেশৰ বিৰুদ্ধাচাৰণ কৰিছিল। ফলত আমি সিহঁতৰ পৰা কঠিন হিচাপ লৈছিলো আৰু আমি সিহঁতক কঠিন শাস্তি বিহিছিলো।');
INSERT INTO "habibur_rahman" VALUES(65,9,'65:9','ফলত সিহঁতে নিজ কৃতকৰ্মৰ সোৱাদ পালে; আৰু সিহঁতৰ কৰ্মৰ পৰিণাম আছিল ক্ষতিয়েই ক্ষতি।');
INSERT INTO "habibur_rahman" VALUES(65,10,'65:10','আল্লাহে সিহঁতৰ বাবে কঠিন শাস্তি প্ৰস্তুত কৰি ৰাখিছে। এতেকে তোমালোকে আল্লাহৰ তাক্বৱা অৱলম্বন কৰা, হে বোধসম্পন্ন ব্যক্তিসকল! যিসকলে ঈমান আনিছা। নিশ্চয় আল্লাহে তোমালোকৰ প্ৰতি অৱতীৰ্ণ কৰিছে এটা উপদেশ---');
INSERT INTO "habibur_rahman" VALUES(65,11,'65:11','এজন ৰাছুল, যিয়ে তোমালোকৰ ওচৰত আল্লাহৰ সুস্পষ্ট আয়াতসমূহ তিলাৱাত কৰে, যাতে সেইসকল লোকক অন্ধকাৰৰ পৰা পোহৰলৈ উলিয়াই আনিব পাৰে যিসকলে ঈমান আনিছে আৰু সৎকৰ্ম কৰিছে; আৰু যিয়ে আল্লাহৰ ওপৰত ঈমান আনিব আৰু সৎকাম কৰিব তেওঁ তাক জান্নাতত প্ৰৱেশ কৰাব, যাৰ তলত নদীসমূহ প্ৰবাহিত, তাত সিহঁত চিৰকাল অৱস্থান কৰিব। নিশ্চয় আল্লাহে তাক উত্তম জীৱিকা প্ৰদান কৰিব।');
INSERT INTO "habibur_rahman" VALUES(65,12,'65:12','তেৱেঁই আল্লাহ, যিজনে সাতখন আকাশ সৃষ্টি কৰিছে আৰু অনুৰূপ পৃথিৱীও, এইবোৰৰ মাজত নামি আহে তেওঁৰ নিৰ্দেশ; যাতে তোমালোকে জানিব পাৰা যে, আল্লাহ সকলো বস্তুৰ ওপৰত ক্ষমতাবান আৰু আল্লাহৰ জ্ঞানে সকলো বস্তুকে পৰিবেষ্টন কৰি আছে।');
INSERT INTO "habibur_rahman" VALUES(66,1,'66:1','হে নবী! আল্লাহে তোমাৰ বাবে যি বৈধ কৰিছে সেয়া তুমি নিজৰ বাবে নিষিদ্ধ কৰিছা কিয়? তুমি তোমাৰ স্ত্ৰীসকলৰ সন্তুষ্টি বিচৰা নেকি; আৰু আল্লাহ ক্ষমাশীল, পৰম দয়ালু।');
INSERT INTO "habibur_rahman" VALUES(66,2,'66:2','নিশ্চয় আল্লাহে তোমালোকৰ বাবে শপতৰ পৰা মুক্তি লাভৰ ব্যৱস্থা কৰিছে; আৰু আল্লাহ তোমালোকৰ অভিভাৱক আৰু তেৱেঁই সৰ্বজ্ঞ, প্ৰজ্ঞাময়।');
INSERT INTO "habibur_rahman" VALUES(66,3,'66:3','আৰু স্মৰণ কৰা, যেতিয়া নবীয়ে নিজ স্ত্ৰীসকলৰ এগৰাকীক গোপন কথা এষাৰ কৈছিল; তাৰ পিছত সেইগৰাকীয়ে আন এগৰাকীক জনাই দিছিল আৰু আল্লাহে তেওঁৰ নবীৰ ওচৰত সেয়া প্ৰকাশ কৰি দিলে, তাৰ পিছত নবীয়ে এই বিষয়ে অলপ ব্যক্ত কৰিলে আৰু অলপ অৱজ্ঞা কৰি এৰি দিলে। যেতিয়া নবীয়ে বিষয়টো সেই স্ত্ৰীক জনালে, তেতিয়া স্ত্ৰীগৰাকীয়ে ক’লে, ‘আপোনাক এই কথাষাৰ কোনে জনালে’? নবীয়ে ক’লে, ‘মোক তেওঁ জনাইছে, যিজন সৰ্বজ্ঞ, সম্যক অৱগত’।');
INSERT INTO "habibur_rahman" VALUES(66,4,'66:4','যদি তোমালোক উভয়ে আল্লাহৰ ওচৰত তাওবা কৰা (তেন্তে ই তোমালোকৰ বাবে কল্যাণকৰ), কাৰণ তোমালোকৰ হৃদয় ঢাল খাইছে। কিন্তু যদি তোমালোকে নবীৰ বিৰুদ্ধে ইজনে সিজনক পোষকতা কৰা তেন্তে জানি থোৱা, নিশ্চয় আল্লাহ তেওঁৰ সহায়কাৰী আৰু জীব্ৰীল লগতে সৎকৰ্মশীল মুমিনসকলেও, ইয়াৰ বাহিৰেও আন আন ফিৰিস্তাসকলো তেওঁৰ সহায়কাৰী।');
INSERT INTO "habibur_rahman" VALUES(66,5,'66:5','যদি নবীয়ে তোমালোকক তালাক দিয়ে তেন্তে তেওঁৰ প্ৰতিপালকে সম্ভৱত তোমালোকৰ স্থানত তেওঁক তোমালোকতকৈয়ো উত্তম স্ত্ৰী প্ৰদান কৰিব--- যিসকল হ''ব মুছলিমাহ, মুমিনাহ, অনুগত, তাওবাকাৰীণী, ইবাদতকাৰীণী, ছিয়াম পালনকাৰীণী, অকুমাৰী আৰু কুমাৰী।');
INSERT INTO "habibur_rahman" VALUES(66,6,'66:6','হে ঈমান্দাৰসকল! তোমালোকে নিজকে আৰু তোমালোকৰ পৰিয়ালবৰ্গক জাহান্নামৰ পৰা ৰক্ষা কৰা, যাৰ ইন্ধন হ’ব মানুহ আৰু শীল, য’ত নিয়োজিত আছে নিৰ্মম, কঠোৰস্বভাৱ ফিৰিস্তাসকল, যিসকলে আল্লাহৰ আদেশ অমান্য নকৰে; আৰু সিহঁতে যি কৰিবলৈ আদেশ প্ৰাপ্ত হয় সেইটোৱেই কৰে।');
INSERT INTO "habibur_rahman" VALUES(66,7,'66:7','হে কাফিৰসকল! আজি তোমালোকে ওজৰ পেচ কৰিবলৈ চেষ্টা নকৰিবা। তোমালোকে যি কৰিছিলা তাৰেই প্ৰতিফল তোমালোকক দিয়া হৈছে।');
INSERT INTO "habibur_rahman" VALUES(66,8,'66:8','হে ঈমান্দাৰসকল! তোমালোকে আল্লাহৰ ওচৰত তাওবা কৰা-- বিশুদ্ধ তাওবা; আশা কৰা যায় তোমালোকৰ প্ৰতিপালকে তোমালোকৰ পাপসমূহ মোচন কৰি দিব আৰু তোমালোকক জান্নাতত প্ৰৱেশ কৰাব, যাৰ তলত নদীসমূহ প্ৰবাহিত। সেইদিনা আল্লাহে নবী আৰু তেওঁৰ লগত যিসকলে ঈমান আনিছে তেওঁলোকক লাঞ্ছিত নকৰিব। তেওঁলোকৰ নূৰ তেওঁলোকৰ সন্মুখত আৰু সোঁ হাতে ধাৱিত হ’ব। তেওঁলোকে ক’ব, ‘হে আমাৰ প্ৰতিপালক! আমাৰ বাবে আমাৰ নূৰক পূৰ্ণতা দান কৰা আৰু আমাক ক্ষমা কৰি দিয়া, নিশ্চয় তুমি সকলো বস্তুৰ ওপৰত ক্ষমতাৱান’।');
INSERT INTO "habibur_rahman" VALUES(66,9,'66:9','হে নবী! কাফিৰ আৰু মুনাফিকসকলৰ বিৰুদ্ধে জিহাদ কৰা আৰু সিহঁতৰ প্ৰতি কঠোৰ হোৱা। সিহঁতৰ আশ্ৰয়স্থল জাহান্নাম আৰু সেইটো কিমান যে নিকৃষ্ট গন্তব্যস্থল!');
INSERT INTO "habibur_rahman" VALUES(66,10,'66:10','যিহঁতে কুফুৰী কৰে, সিহঁতৰ বাবে আল্লাহে নূহৰ স্ত্ৰী আৰু লূতৰ স্ত্ৰীৰ উদাহৰণ দিছে; সিহঁত আমাৰ বান্দাসকলৰ মাজৰ দুজন সৎ বান্দাৰ অধীনত আছিল, কিন্তু সিহঁত উভয়ে তেওঁলোকৰ লগত বিশ্বাসঘাতকতা কৰিছিল। ফলত আল্লাহৰ শাস্তিৰ পৰা ৰক্ষা কৰিবলৈ নূহ আৰু লূত সিহঁতৰ কোনো কামত নাহিল, আৰু সিহঁতক কোৱা হ’ল, ‘তোমালোক উভয়ে প্ৰৱেশকাৰীসকলৰ লগত জাহান্নামত প্ৰৱেশ কৰা’।');
INSERT INTO "habibur_rahman" VALUES(66,11,'66:11','আৰু মুমিনসকলৰ বাবে আল্লাহে ফিৰআউনৰ স্ত্ৰীৰ উদাহৰণ দাঙি ধৰিছে, যেতিয়া তেওঁ এই বুলি প্ৰাৰ্থনা কৰিছিল, ‘হে মোৰ প্ৰতিপালক! তোমাৰ ওচৰত মোৰ বাবে জান্নাতত এটা ঘৰ নিৰ্মাণ কৰা আৰু মোক ফিৰআউন আৰু তাৰ কৰ্মৰ পৰা উদ্ধাৰ কৰা আৰু মোক উদ্ধাৰ কৰা যালিম সম্প্ৰদায়ৰ পৰা’।');
INSERT INTO "habibur_rahman" VALUES(66,12,'66:12','(আল্লাহে আৰু উদাহৰণ দিছে) ইমৰাণৰ জীয়েক মাৰয়ামৰ---- যিয়ে নিজ লজ্জাস্থানৰ পৱিত্ৰতা ৰক্ষা কৰিছিল, ফলত আমি তাত আমাৰ ৰূহৰ পৰা ফুঁৱাই দিছিলো আৰু তেওঁ তেওঁৰ প্ৰতিপালকৰ বাণী আৰু তেওঁৰ কিতাপসমূহ সত্য বুলি গ্ৰহণ কৰিছিল আৰু তেওঁ আছিল অনুগতসকলৰ অন্যতম।');
INSERT INTO "habibur_rahman" VALUES(67,1,'67:1','বৰকতময় তেওঁ যাৰ হাতত সৰ্বময় কৰ্তৃত্ব; আৰু তেওঁ সকলো বস্তুৰ ওপৰত ক্ষমতাৱান।');
INSERT INTO "habibur_rahman" VALUES(67,2,'67:2','যিজনে মৃত্যু আৰু জীৱন সৃষ্টি কৰিছে, যাতে তোমালোকক পৰীক্ষা কৰিব পাৰে যে, তোমালোকৰ মাজত কোন আটাইতকৈ উত্তম আমল কৰিব পাৰে? তেওঁ পৰাক্ৰমশালী, অতিক্ষমাশীল।');
INSERT INTO "habibur_rahman" VALUES(67,3,'67:3','যিজনে সাতখন আকাশ স্তৰে স্তৰে সৃষ্টি কৰিছে। ৰহমানৰ সৃষ্টিত তুমি কোনো ত্ৰুটি দেখিবলৈ নাপাবা; তুমি আকৌ ঘুৰি চোৱা, কোনো ত্ৰুটি দেখিবলৈ পাইছানে?');
INSERT INTO "habibur_rahman" VALUES(67,4,'67:4','তাৰ পিছত আকৌ তুমি দ্বিতীয়বাৰ দৃষ্টিপাত কৰা, এই দৃষ্টি ব্যৰ্থ আৰু ক্লান্ত হৈ তোমাৰ ফালে উভতি আহিব।');
INSERT INTO "habibur_rahman" VALUES(67,5,'67:5','নিশ্চয় আমি নিকটৱৰ্তী আকাশক প্ৰদীপপুঞ্জৰ দ্বাৰা সুশোভিত কৰিছো আৰু সেইবোৰক চয়তানসকলৰ প্ৰতি নিক্ষেপৰ উপকৰণ বনাইছো আৰু সিহঁতৰ বাবে প্ৰস্তুত কৰিছো জ্বলন্ত অগ্নিকুণ্ডৰ শাস্তি।');
INSERT INTO "habibur_rahman" VALUES(67,6,'67:6','আৰু যিসকলে নিজ প্ৰতিপালকক অস্বীকাৰ কৰিছে, সিহঁতৰ বাবে আছে জাহান্নামৰ শাস্তি; আৰু কিমান যে নিকৃষ্ট সেই প্ৰত্যাৱৰ্তনস্থল।');
INSERT INTO "habibur_rahman" VALUES(67,7,'67:7','যেতিয়া সিহঁতক তাত নিক্ষেপ কৰা হ''ব, তাত সিহঁতে ইয়াৰ বিকট শব্দ শুনিবলৈ পাব। আৰু ই উথলি উঠিব।');
INSERT INTO "habibur_rahman" VALUES(67,8,'67:8','ক্ৰোধত ই ছিন্ন-ভিন্ন হোৱাৰ উপক্ৰম হ''ব। যেতিয়াই তাত কোনো দলক নিক্ষেপ কৰা হ''ব, ইয়াৰ প্ৰহৰীসকলে সিহঁতক সুধিব, ‘তোমালোকৰ ওচৰত কোনো সতৰ্ককাৰী অহা নাছিল নে’?');
INSERT INTO "habibur_rahman" VALUES(67,9,'67:9','সিহঁতে ক''ব হয়, ''নিশ্চয় আমাৰ ওচৰত সতৰ্ককাৰী আহিছিল। তেতিয়া আমি (তেওঁলোকক) মিথ্যাৰোপ কৰিছিলো আৰু কৈছিলো, ‘আল্লাহে একোৱে অৱতীৰ্ণ কৰা নাই। তোমালোকে মহাবিভ্ৰান্তিত আছা’।');
INSERT INTO "habibur_rahman" VALUES(67,10,'67:10','আৰু সিহঁতে ক''ব, ‘যদি আমি শুনিলোহেঁতেন আৰু বিবেক-বুদ্ধি প্ৰয়োগ কৰিলোহেঁতেন, তেন্তে আমি জ্বলন্ত জুইৰ অধিবাসী নহ''লোহেঁতেন’।');
INSERT INTO "habibur_rahman" VALUES(67,11,'67:11','ফলত সিহঁতে নিজৰ অপৰাধ স্বীকাৰ কৰিব। এতেকে ধ্বংস জ্বলন্ত জুইৰ অধিবাসীসকলৰ বাবে।');
INSERT INTO "habibur_rahman" VALUES(67,12,'67:12','নিশ্চয় যিসকলে নেদেখাকৈ নিজ প্ৰতিপালকক ভয় কৰে তেওঁলোকৰ বাবে আছে ক্ষমা আৰু ডাঙৰ প্ৰতিদান।');
INSERT INTO "habibur_rahman" VALUES(67,13,'67:13','আৰু তোমালোকে তোমালোকৰ কথা গোপনে কোৱা অথবা প্ৰকাশ্যভাৱে কোৱা, নিশ্চয় তেওঁ অন্তৰসমূহত যি আছে সেই বিষয়ে সম্যক অৱগত।');
INSERT INTO "habibur_rahman" VALUES(67,14,'67:14','যিজনে সৃষ্টি কৰিছে, তেৱেঁই নাজানিবনে? অথচ তেওঁ সূক্ষ্মদৰ্শী, সম্যক অৱহিত।');
INSERT INTO "habibur_rahman" VALUES(67,15,'67:15','তেৱেঁই তোমালোকৰ বাবে পৃথিৱীখনক সুগম কৰি দিছে, এতেকে তোমালোকে ইয়াৰ পথ-প্ৰান্তৰত বিচৰণ কৰা আৰু তেওঁৰ জীৱিকাৰ পৰা তোমালোকে আহাৰ কৰা; আৰু তেওঁৰ ওচৰতেই পুনৰুত্থান।');
INSERT INTO "habibur_rahman" VALUES(67,16,'67:16','তোমালোকে তেওঁৰ পৰা নিৰ্ভয় হৈছা নেকি যে, যিজন আকাশত আছে তেওঁ তোমালোকক পৃথিৱীসহ (পুতি পেলাই) ধ্বংস কৰিব আৰু হঠাৎ ই (পৃথিৱীখন) থৰ থৰকৈ কঁপিবলৈ ধৰিব?');
INSERT INTO "habibur_rahman" VALUES(67,17,'67:17','অথবা তোমালোকে ইয়াৰ পৰা নিৰ্ভয় হৈছা নেকি যে, যিজন আকাশত আছে, তেওঁ তোমালোকৰ ওপৰত শিল বৰ্ষণকাৰী প্ৰবল ধুমুহা পঠাব? তেতিয়া তোমালোকে জানিবলৈ পাবা কেনেকুৱা আছিল মোৰ সতৰ্কবাণী!');
INSERT INTO "habibur_rahman" VALUES(67,18,'67:18','আৰু নিশ্চয় সিহঁতৰ পুৰ্বৱৰ্তীসকলেও মিথ্যাৰোপ কৰিছিল। ফলত কেনেকুৱা আছিল মোৰ প্ৰত্যাখ্যান (ইয়াৰ শাস্তি)?');
INSERT INTO "habibur_rahman" VALUES(67,19,'67:19','সিহঁতে সিহঁতৰ ওপৰত (উৰি ফুৰা) চৰাইবোৰৰ প্ৰতি লক্ষ্য কৰা নাইনে, যিবোৰে পাখী মেলি দিয়ে আৰু সংকুচিত কৰে? পৰম কৰুণাময় আল্লাহেই এইবোৰক স্থিৰ কৰি ৰাখে। নিশ্চয় তেওঁ সকলো বস্তুৰ সম্যক দ্ৰষ্টা।');
INSERT INTO "habibur_rahman" VALUES(67,20,'67:20','পৰম কৰুণাময় আল্লাহৰ বাহিৰে তোমালোকৰ এনে কোনো সৈন্যবাহিনী আছেনে, যিসকলে তোমালোকক সহায় কৰিব? কাফিৰসকল আছে মাথোন প্ৰবঞ্চনাৰ মাজত।');
INSERT INTO "habibur_rahman" VALUES(67,21,'67:21','কোন আছে এনেকুৱা, যিজনে তোমালোকক জীৱিকা দান কৰিব যদি তেওঁ তেওঁৰ জীৱিকা বন্ধ কৰি দিয়ে? বৰং সিহঁত অবাধ্যতা আৰু বিমুখতাত আবিচল হৈ আছে।');
INSERT INTO "habibur_rahman" VALUES(67,22,'67:22','যি ব্যক্তি নত হৈ মুখৰ ওপৰত ভৰ দি খোজ কাঢ়ে, সিয়েই অধিক হিদায়ত প্ৰাপ্ত, নে সেই ব্যক্তি, যিজনে পোন হৈ সৰল পথত খোজ কাঢ়ে?');
INSERT INTO "habibur_rahman" VALUES(67,23,'67:23','কোৱা, তেৱেঁই তোমালোকক সৃষ্টি কৰিছে আৰু তোমালোকৰ বাবে শ্ৰৱণ আৰু দৃষ্টিশক্তি লগতে অন্তঃকৰণসমূহ প্ৰদান কৰিছে। তোমালোকে খুবেই কম কৃতজ্ঞতা প্ৰকাশ কৰা।');
INSERT INTO "habibur_rahman" VALUES(67,24,'67:24','কোৱা, তেৱেঁই তোমালোকক পৃথিৱীত সৃষ্টি কৰি প্ৰসাৰিত কৰিছে আৰু তেওঁৰ ওচৰতেই তোমালোকক সমবেত কৰা হ''ব।');
INSERT INTO "habibur_rahman" VALUES(67,25,'67:25','আৰু সিহঁতে কয়, ''তোমালোকে যদি সত্যবাদী হোৱা তেন্তে কোৱাচোন এই প্ৰতিশ্ৰুতি কেতিয়া বাস্তবায়িত হ’ব''?।');
INSERT INTO "habibur_rahman" VALUES(67,26,'67:26','কোৱা, এই বিষয়ৰ জ্ঞান কেৱল আল্লাহৰ ওচৰতেই আছে। আৰু মইতো কেৱল স্পষ্ট সতৰ্ককাৰী মাত্ৰ।');
INSERT INTO "habibur_rahman" VALUES(67,27,'67:27','তাৰ পিছত যেতিয়া সিহঁতে ইয়াক নিচেই ওচৰত দেখা পাব তেতিয়া কাফিৰসকলৰ চেহাৰা ম্লান হৈ পৰিব আৰু কোৱা হ’ব, ‘এইটোৱেই হৈছে সেইটো, যিটো তোমালোকে দাবী কৰিছিলা’।');
INSERT INTO "habibur_rahman" VALUES(67,28,'67:28','কোৱা, কোৱাচোন তোমালোকে যদি আল্লাহে মোক আৰু মোৰ সঙ্গীসকলক ধ্বংস কৰি দিয়ে অথবা আমাৰ প্ৰতি দয়া প্ৰদৰ্শন কৰে, তেন্তে কাফিৰসকলক যন্ত্ৰণাদায়ক শাস্তিৰ পৰা কোনে ৰক্ষা কৰিব?');
INSERT INTO "habibur_rahman" VALUES(67,29,'67:29','কোৱা, তেৱেঁই পৰম কৰুণাময়। আমি তেওঁৰ ওপৰতেই ঈমান আনিছো আৰু তেওঁৰ ওপৰতেই ভৰসা কৰিছো। গতিকে তোমালোকে অনতিপলমে জানিবলৈ পাবা কোনে স্পষ্ট বিভ্ৰান্তিত আছে।');
INSERT INTO "habibur_rahman" VALUES(67,30,'67:30','কোৱা, তোমালোকে ভাবিছানে, যদি তোমালোকৰ পানী ভূগৰ্ভত গুচি যায়, তেতিয়া কোনে তোমালোকক প্ৰবাহমান পানী আনি দিব?');
INSERT INTO "habibur_rahman" VALUES(68,1,'68:1','নূন; কলমৰ শপত আৰু সিহঁতে যি লিখে তাৰ শপত!');
INSERT INTO "habibur_rahman" VALUES(68,2,'68:2','তোমাৰ প্ৰতিপালকৰ অনুগ্ৰহ যে, তুমি উন্মাদ নহয়।');
INSERT INTO "habibur_rahman" VALUES(68,3,'68:3','আৰু নিশ্চয় তোমাৰ বাবে আছে নিৰবিচ্ছিন্ন পুৰষ্কাৰ।');
INSERT INTO "habibur_rahman" VALUES(68,4,'68:4','আৰু নিশ্চয় তুমি মহান চৰিত্ৰৰ উচ্চ শৃঙ্গত অধিষ্ঠিত।');
INSERT INTO "habibur_rahman" VALUES(68,5,'68:5','এতেকে অনতিপলমে তুমি দেখিবলৈ পাবা আৰু সিহঁতেও দেখিবলৈ পাব--');
INSERT INTO "habibur_rahman" VALUES(68,6,'68:6','তোমালোকৰ মাজত কোন বিকাৰগ্ৰস্থ?');
INSERT INTO "habibur_rahman" VALUES(68,7,'68:7','নিশ্চয় তোমাৰ প্ৰতিপালক সম্যক অৱগত সিহঁতৰ বিষয়ে যিহঁতে তেওঁৰ পথৰ পৰা বিচ্যুত হৈছে, আৰু তেওঁ হিদায়ত প্ৰাপ্তসকলৰ বিষয়েও সম্যক পৰিজ্ঞাত।');
INSERT INTO "habibur_rahman" VALUES(68,8,'68:8','গতিকে তুমি মিথ্যাৰোপকাৰীসকলৰ আনুগত্য নকৰিবা।');
INSERT INTO "habibur_rahman" VALUES(68,9,'68:9','সিহঁতে কামনা কৰে যে, তুমি আপোষকামী হোৱা, তেন্তে সিহঁতেও আপোষকামী হ''ব।');
INSERT INTO "habibur_rahman" VALUES(68,10,'68:10','আৰু তুমি আনুগত্য নকৰিবা প্ৰত্যেক এনে ব্যক্তিৰ যিয়ে অধিক শপতকাৰী, লাঞ্ছিত,');
INSERT INTO "habibur_rahman" VALUES(68,11,'68:11','পিঠিৰ আঁৰত নিন্দাকাৰী আৰু যিজনে চোগলখুৰী কৰি ফুৰে,');
INSERT INTO "habibur_rahman" VALUES(68,12,'68:12','কল্যাণৰ কামত বাধা প্ৰদানকাৰী, সীমালঙ্ঘনকাৰী, পাপিষ্ঠ,');
INSERT INTO "habibur_rahman" VALUES(68,13,'68:13','দুষ্ট প্ৰকৃতিৰ, তদুপৰি কুখ্যাত;');
INSERT INTO "habibur_rahman" VALUES(68,14,'68:14','এই বাবেই যে, সি আছিল ধন-সম্পদ আৰু সন্তান-সন্ততিৰ অধিকাৰী।');
INSERT INTO "habibur_rahman" VALUES(68,15,'68:15','যেতিয়া তাৰ ওচৰত আমাৰ আয়াতসমূহ তিলাৱাত কৰা হয় তেতিয়া সি কয়, এইবোৰ পূৰ্বৱৰ্তীসকলৰ কল্প-কাহিনী মাত্ৰ।');
INSERT INTO "habibur_rahman" VALUES(68,16,'68:16','অতি শীঘ্ৰে আমি তাৰ শুৰত (নাকত) দাগ দিম।');
INSERT INTO "habibur_rahman" VALUES(68,17,'68:17','নিশ্চয় আমি ইহঁতক পৰীক্ষা কৰিছো, যেনেকৈ পৰীক্ষা কৰিছিলো উদ্যান-অধিপতিসকলক, যেতিয়া সিহঁতে শপত কৰিছিল যে, অৱশ্যে ৰাতিপুৱাই সিহঁতে বাগানৰ ফল আহৰণ কৰিব।');
INSERT INTO "habibur_rahman" VALUES(68,18,'68:18','আৰু সিহঁতে ‘ইন শ্বা আল্লাহ’ কোৱা নাছিল।');
INSERT INTO "habibur_rahman" VALUES(68,19,'68:19','সেয়ে তোমাৰ প্ৰতিপালকৰ ওচৰৰ পৰা এটা প্ৰদক্ষিণকাৰী (বিপৰ্যয়, জুই) বাগানৰ ওপৰত প্ৰদক্ষিণ কৰিলে, সেই সময়ত সিহঁত আছিল টোপনিত।');
INSERT INTO "habibur_rahman" VALUES(68,20,'68:20','ফলত ই (জুই লাগি) কলাবৰ্ণৰ নিচিনা হৈ গ’ল।');
INSERT INTO "habibur_rahman" VALUES(68,21,'68:21','ৰাতিপুৱাই সিহঁতে ইজনে সিজনক মাতি ক’লে,');
INSERT INTO "habibur_rahman" VALUES(68,22,'68:22','‘যদি তোমালোকে ফল আহৰণ কৰিব বিচৰা তেনেহ’লে পুৱতি নিশাই তোমালোকৰ বাগানলৈ যোৱা’।');
INSERT INTO "habibur_rahman" VALUES(68,23,'68:23','তাৰ পিছত সিহঁতে যাবলৈ ধৰিলে, আৰু নিম্নস্বৰে এই কথা কৈ আছিল--');
INSERT INTO "habibur_rahman" VALUES(68,24,'68:24','যে ‘আজি তাত তোমালোকৰ ওচৰত যাতে কোনো দুখীয়া-অভাৱী প্ৰৱেশ কৰিব নোৱাৰে’।');
INSERT INTO "habibur_rahman" VALUES(68,25,'68:25','আৰু সিহঁতে পুৱতি নিশাই দৃঢ় ইচ্ছা শক্তি লৈ সক্ষম অৱস্থাত (বাগানলৈ) যাত্ৰা কৰিলে।');
INSERT INTO "habibur_rahman" VALUES(68,26,'68:26','তাৰ পিছত সিহঁতে যেতিয়া বাগানখন দেখিবলৈ পালে, তেতিয়া সিহঁতে ক’লে, ‘নিশ্চয় আমি পথ হেৰুৱাইছো’।');
INSERT INTO "habibur_rahman" VALUES(68,27,'68:27','‘বৰং আমি বঞ্চিত’।');
INSERT INTO "habibur_rahman" VALUES(68,28,'68:28','সিহঁতৰ মাজৰ শ্ৰেষ্ঠ ব্যক্তিজনে ক’লে, ‘মই তোমালোকক কোৱা নাছিলোনে যে, তোমালোকে কিয় আল্লাহৰ পৱিত্ৰতা আৰু মহিমা ঘোষণা নকৰা’?');
INSERT INTO "habibur_rahman" VALUES(68,29,'68:29','সিহঁতে ক’লে, ‘আমি আমাৰ প্ৰতিপালকৰ পৱিত্ৰতা ঘোষণা কৰোঁ। নিশ্চয় আমি যালিম আছিলো’।');
INSERT INTO "habibur_rahman" VALUES(68,30,'68:30','তাৰ পিছত সিহঁতে ইজনে সিজনক দোষাৰোপ কৰিবলৈ ধৰিলে।');
INSERT INTO "habibur_rahman" VALUES(68,31,'68:31','সিহঁতে ক’লে, ‘হায়, আমাৰ ধ্বংস! নিশ্চয় আমি সীমালঙ্ঘনকাৰী আছিলো’।');
INSERT INTO "habibur_rahman" VALUES(68,32,'68:32','সম্ভৱতঃ আমাৰ প্ৰতিপালকে আমাক ইয়াতকৈয়ো উৎকৃষ্ট বিনিময় প্ৰদান কৰিব। অৱশ্যে আমি আমাৰ প্ৰতিপালকৰ প্ৰতি আগ্ৰহী।');
INSERT INTO "habibur_rahman" VALUES(68,33,'68:33','এইদৰেই হয় শাস্তি, আৰু আখিৰাতৰ শাস্তি ইয়াতকৈও ডাঙৰ, যদি সিহঁতে জানিলেহেঁতেন।');
INSERT INTO "habibur_rahman" VALUES(68,34,'68:34','নিশ্চয় মুত্তাক্বীসকলৰ বাবে তেওঁলোকৰ প্ৰতিপালকৰ ওচৰত আছে নিয়ামতপূৰ্ণ জান্নাত।');
INSERT INTO "habibur_rahman" VALUES(68,35,'68:35','আমি মুছলিম (অনুগত)সকলক অপৰাধীসকলৰ দৰে গণ্য কৰিম নেকি?');
INSERT INTO "habibur_rahman" VALUES(68,36,'68:36','তোমালোকৰ কি হৈছে, তোমালোকে এইটো কেনেকুৱা সিদ্ধান্ত কৰা?');
INSERT INTO "habibur_rahman" VALUES(68,37,'68:37','তোমালোকৰ ওচৰত কোনো পুথি আছে নেকি য’ত তোমালোকে অধ্যয়ন কৰা--');
INSERT INTO "habibur_rahman" VALUES(68,38,'68:38','যে, নিশ্চয় তোমালোকৰ বাবে তাত এনেকুৱা বস্তু আছে যি তোমালোকে পছন্দ কৰা?');
INSERT INTO "habibur_rahman" VALUES(68,39,'68:39','অথবা তোমালোকৰ বাবে আমাৰ লগত ক্বিয়ামত পৰ্যন্ত বলবৎ থকা এনেকুৱা কোনো অঙ্গীকাৰ আছে নেকি যে, তোমালোকে নিজৰ বাবে যিটোৱে ফয়চালা কৰিবা সেইটোৱে পাবা?');
INSERT INTO "habibur_rahman" VALUES(68,40,'68:40','তুমি সিহঁতক সোধা, সিহঁতৰ মাজত এই দাবীৰ জিম্মাদাৰ কোন?');
INSERT INTO "habibur_rahman" VALUES(68,41,'68:41','অথবা সিহঁতৰ বাবে (আল্লাহৰ লগত) বহুতো অংশীদাৰ আছে নেকি? আছে যদি তেন্তে সিহঁতে সিহঁতৰ অংশীদাৰসকলক উপস্থিত কৰক—যদি সিহঁত সত্যবাদী হয়।');
INSERT INTO "habibur_rahman" VALUES(68,42,'68:42','সেইদিনা ভৰিৰ কলাফুলি উন্মোচিত কৰা হ’ব, সেইদিনা সিহঁতক মাতা হ’ব ছাজদাহ কৰিবলৈ, কিন্তু সিহঁত সক্ষম নহ’ব;');
INSERT INTO "habibur_rahman" VALUES(68,43,'68:43','সিহঁতৰ দৃষ্টিসমূহ অৱনত অৱস্থাত থাকিব, অপমানে সিহঁতক আচ্ছন্ন কৰিব অথচ যেতিয়া সিহঁত নিৰাপদ আছিল তেতিয়া সিহঁতক মাতা হৈছিল ছাজদা কৰিবলৈ (কিন্তু সিহঁতে ছাজদা কৰা নাছিল)।');
INSERT INTO "habibur_rahman" VALUES(68,44,'68:44','এতেকে এৰি দিয়া মোক আৰু সিহঁতক, যিসকলে এই বাণী প্ৰত্যাখ্যান কৰে। আমি সিহঁতক লাহে লাহে এনেকৈ ধৰিম যে, সিহঁতে গমেই নাপাব।');
INSERT INTO "habibur_rahman" VALUES(68,45,'68:45','আৰু মই সিহঁতক অৱকাশ দিম। নিশ্চয় মোৰ কৌশল অত্যন্ত বলিষ্ঠ।');
INSERT INTO "habibur_rahman" VALUES(68,46,'68:46','তুমি সিহঁতৰ ওচৰত পাৰিশ্ৰমিক (বিনিময়) দাবী কৰিছা নেকি, যাৰ ফলত সিহঁতে ঋণৰ কাৰণে ভাৰাক্ৰান্ত হৈ পৰিছে?');
INSERT INTO "habibur_rahman" VALUES(68,47,'68:47','নে সিহঁতৰ ওচৰত গায়েবৰ জ্ঞান আছে, যিটো সিহঁতে লিখি ৰাখে!');
INSERT INTO "habibur_rahman" VALUES(68,48,'68:48','এতেকে তুমি তোমাৰ প্ৰতিপালকৰ আদেশৰ অপেক্ষাত ধৈৰ্য ধাৰণ কৰা, আৰু তুমি মাছৰ গৰাকীৰ দৰে নহ’বা, যেতিয়া তেওঁ বিষাদ আচ্ছন্ন অৱস্থাত আহ্বান কৰিছিল।');
INSERT INTO "habibur_rahman" VALUES(68,49,'68:49','যদি তেওঁৰ প্ৰতিপালকৰ অনুগ্ৰহ তেওঁৰ ওচৰত আহি নাপালেহেঁতেন, তেন্তে তেওঁ লাঞ্ছিত অৱস্থাত উন্মুক্ত প্ৰান্তৰত নিক্ষিপ্ত হ’লেহেঁতেন।');
INSERT INTO "habibur_rahman" VALUES(68,50,'68:50','তাৰ পিছত তেওঁৰ প্ৰতিপালকে তেওঁক মনোনীত কৰিলে আৰু তেওঁক সৎকৰ্মপৰায়ণসকলৰ অন্তৰ্ভূক্ত কৰিলে।');
INSERT INTO "habibur_rahman" VALUES(68,51,'68:51','আৰু কাফিৰসকলে যেতিয়া উপদেশবাণী (কোৰআন) শুনে তেতিয়া সিহঁতে এনেকুৱা দৃষ্টিৰে তোমাৰ ফালে চায় যেনিবা তোমাক বাগৰাই দিব, আৰু সিহঁতে কয়, ‘নিশ্চয় এওঁ এজন উন্মাদ (বলিয়া)’।');
INSERT INTO "habibur_rahman" VALUES(68,52,'68:52','অথচ এই কোৰআন কেৱল বিশ্ববাসীৰ বাবে উপদেশ।');
INSERT INTO "habibur_rahman" VALUES(69,1,'69:1','সেই অৱশ্যম্ভাৱী ঘটনা, (ক্বিয়ামত)');
INSERT INTO "habibur_rahman" VALUES(69,2,'69:2','কি সেই অৱশ্যম্ভাৱী ঘটনা?');
INSERT INTO "habibur_rahman" VALUES(69,3,'69:3','আৰু কিহে তোমাক জনাব সেই অৱশ্যম্ভাৱী ঘটনাটোনো কি?');
INSERT INTO "habibur_rahman" VALUES(69,4,'69:4','ছামুদ আৰু আদ সম্প্ৰদায়ে মিথ্যাৰোপ কৰিছিল ভীতিপ্ৰদ মহাবিপদক।');
INSERT INTO "habibur_rahman" VALUES(69,5,'69:5','সেয়ে ছামুদ সম্প্ৰদায়ক ধ্বংস কৰা হৈছিল এটা প্ৰলয়ংকৰ বিপৰ্যয়কাৰী প্ৰচণ্ড শব্দৰ দ্বাৰা।');
INSERT INTO "habibur_rahman" VALUES(69,6,'69:6','আৰু আদ সম্প্ৰদায়ক ধ্বংস কৰা হৈছিল ঠাণ্ডা প্ৰবল ধুমুহা বতাহৰ দ্বাৰা,');
INSERT INTO "habibur_rahman" VALUES(69,7,'69:7','ইয়াক তেওঁ সাত ৰাতি আৰু আঠ দিন অবিৰামভাৱে সিহঁতৰ ওপৰত প্ৰবাহিত কৰিছিল; ফলত তুমি উক্ত সম্প্ৰদায়ক দেখিবলৈ পালাহেঁতেন---যে সিহঁত এনেকৈ সিচৰিত হৈ পৰি আছে যেনে সাৰশূন্য খেজুৰ গছৰ কাণ্ডহে।');
INSERT INTO "habibur_rahman" VALUES(69,8,'69:8','ইয়াৰ পিছত সিহঁতৰ বাবে তুমি কিবা অৱশিষ্ট দেখিবলৈ পোৱানে?');
INSERT INTO "habibur_rahman" VALUES(69,9,'69:9','আৰু ফিৰআউন, তাৰ পূৰ্বৱৰ্তীসকলে লগতে ওলোটাই দিয়া জনপদবাসীয়ে জঘন্য পাপ কামত লিপ্ত আছিল।');
INSERT INTO "habibur_rahman" VALUES(69,10,'69:10','আৰু সিহঁতে নিজ প্ৰতিপালকৰ ৰাছুলক অমান্য কৰিছিল, ফলত তেওঁ সিহঁতক অত্যন্ত কঠোৰভাৱে গ্ৰেপ্তাৰ কৰিলে।');
INSERT INTO "habibur_rahman" VALUES(69,11,'69:11','যেতিয়া বানপানী বিপদৰ সীমা চেৰাই গৈছিল নিশ্চয় তেতিয়া আমি তোমালোকক নাৱঁত আৰোহণ কৰাইছিলো।');
INSERT INTO "habibur_rahman" VALUES(69,12,'69:12','আমি এইটো কৰিছিলো তোমালোকক এটা স্মৰণীয় শিক্ষা দিয়াৰ বাবে আৰু এই বাবে যে, যাতে শ্ৰুতিধৰ কাণে ইয়াক সংৰক্ষণ কৰি ৰাখে।');
INSERT INTO "habibur_rahman" VALUES(69,13,'69:13','এতেকে যেতিয়া শিঙাত ফু দিয়া হ’ব—কেৱল এটা ফু।');
INSERT INTO "habibur_rahman" VALUES(69,14,'69:14','আৰু পৃথিৱীসহ পৰ্বতসমূহক উঠাই নিয়া হ’ব আৰু মাত্ৰ এটা আঘাতত এইবোৰ চূৰ্ণ-বিচূৰ্ণ হৈ যাব।');
INSERT INTO "habibur_rahman" VALUES(69,15,'69:15','ফলত সেইদিনা মহাঘটনা সংঘটিত হ’ব।');
INSERT INTO "habibur_rahman" VALUES(69,16,'69:16','আৰু আকাশ বিদীৰ্ণ হৈ যাব, ফলত সেইদিনা ই দুৰ্বল-বিক্ষিপ্ত হৈ পৰিব।');
INSERT INTO "habibur_rahman" VALUES(69,17,'69:17','আৰু ফিৰিস্তাসকল আকাশৰ বিভিন্ন প্ৰান্তত থাকিব। সেইদিনা আঠজন ফিৰিস্তাই তোমাৰ প্ৰতিপালকৰ আৰছক বহন কৰিব।');
INSERT INTO "habibur_rahman" VALUES(69,18,'69:18','সেইদিনা তোমালোকক উপস্থিত কৰোৱা হ’ব লগতে তোমালোকৰ কোনো গোপনীয়তাই আৰু গোপন নাথাকিব।');
INSERT INTO "habibur_rahman" VALUES(69,19,'69:19','তেতিয়া যাৰ আমলনামা তাৰ সোঁ হাতত দিয়া হ’ব সি ক’ব, ‘লোৱা, মোৰ আমলনামা পঢ়ি চোৱা’।');
INSERT INTO "habibur_rahman" VALUES(69,20,'69:20','‘মোৰ দৃঢ় বিশ্বাস আছিল যে, মই মোৰ হিচাপৰ সন্মুখীন হম’।');
INSERT INTO "habibur_rahman" VALUES(69,21,'69:21','তেওঁ থাকিব সন্তোষজনক জীৱনত;');
INSERT INTO "habibur_rahman" VALUES(69,22,'69:22','সুউচ্চ জান্নাতত,');
INSERT INTO "habibur_rahman" VALUES(69,23,'69:23','যাৰ ফল-মূল নিচেই ওচৰত ওলমি থাকিব।');
INSERT INTO "habibur_rahman" VALUES(69,24,'69:24','(কোৱা হ’ব) ‘বিগত দিনসমূহত তোমালোকে আগলৈ যি প্ৰেৰণ কৰিছিলা তাৰ বিনিময়ত তোমালোকে তৃপ্তি সহকাৰে খোৱা আৰু পান কৰা’।');
INSERT INTO "habibur_rahman" VALUES(69,25,'69:25','কিন্তু যাৰ আমলনামা বাওঁ হাতত দিয়া হ’ব সি ক’ব, ‘হায়, মোক যদি মোৰ আমলনামা নিদিলেহেঁতেন’!');
INSERT INTO "habibur_rahman" VALUES(69,26,'69:26','‘আৰু যদি মই নাজানিলোহেঁতেন মোৰ হিচাপ!');
INSERT INTO "habibur_rahman" VALUES(69,27,'69:27','‘হায়, মোৰ মৃত্যুৱেই যদি মোৰ চূড়ান্ত ফায়চালা হ’লেহেঁতেন!');
INSERT INTO "habibur_rahman" VALUES(69,28,'69:28','‘মোৰ ধন-সম্পদ মোৰ কোনো কামত নাহিল।');
INSERT INTO "habibur_rahman" VALUES(69,29,'69:29','‘মোৰ ক্ষমতা শক্তিও বিনষ্ট হৈছে’।');
INSERT INTO "habibur_rahman" VALUES(69,30,'69:30','(ফিৰিস্তাসকলক কোৱা হ’ব) ‘ধৰা তাক আৰু গলত পঘা লগাই দিয়া’।');
INSERT INTO "habibur_rahman" VALUES(69,31,'69:31','‘তাৰ পিছত তাক জাহান্নামত নিক্ষেপ কৰা’।');
INSERT INTO "habibur_rahman" VALUES(69,32,'69:32','তাৰ পিছত তাক বান্ধি থোৱা, এনে এডাল শিকলিৰে যাৰ দৈৰ্ঘ সত্তৰ হাত।');
INSERT INTO "habibur_rahman" VALUES(69,33,'69:33','নিশ্চয় সি মহান আল্লাহৰ প্ৰতি ঈমান পোষণ কৰা নাছিল,');
INSERT INTO "habibur_rahman" VALUES(69,34,'69:34','আৰু মিছকিনক খাদ্যদানত উৎসাহিত কৰাও নাছিল।');
INSERT INTO "habibur_rahman" VALUES(69,35,'69:35','এতেকে আজি ইয়াত তাৰ কোনো অন্তৰঙ্গ বন্ধু নাথাকিব।');
INSERT INTO "habibur_rahman" VALUES(69,36,'69:36','আৰু ক্ষত-নিঃসৃত পুঁজৰ বাহিৰে আন কোনো খাদ্য নাথাকিব,');
INSERT INTO "habibur_rahman" VALUES(69,37,'69:37','যিটো অপৰাধীসকলৰ বাহিৰে আন কোনেও নাখাব।');
INSERT INTO "habibur_rahman" VALUES(69,38,'69:38','এতেকে মই শপত কৰো সেইবোৰ বস্তুৰ যিবোৰ তোমালোকে দেখা পোৱা,');
INSERT INTO "habibur_rahman" VALUES(69,39,'69:39','আৰু তাৰো শপত কৰো যিবোৰ তোমালোকে দেখা নোপোৱা;');
INSERT INTO "habibur_rahman" VALUES(69,40,'69:40','নিশ্চয় এয়া (কোৰআন) এক সন্মানিত ৰাছুলৰ (বাহিত) বাণী।');
INSERT INTO "habibur_rahman" VALUES(69,41,'69:41','আৰু এয়া কোনো কবিৰ কথা নহয়; তোমালোকে খুবেই কম ঈমান পোষণ কৰা।');
INSERT INTO "habibur_rahman" VALUES(69,42,'69:42','আৰু এয়া কোনো গণকৰ কথাও নহয়; তোমালোকে খুবেই কম উপদেশ গ্ৰহণ কৰা।');
INSERT INTO "habibur_rahman" VALUES(69,43,'69:43','এয়া বিশ্বজগতৰ প্ৰতিপালকৰ তৰফৰ পৰা নাযিলকৃত।');
INSERT INTO "habibur_rahman" VALUES(69,44,'69:44','যদি তেওঁ আমাৰ নামত কোনো মিছা ৰচনা কৰিলেহেঁতেন,');
INSERT INTO "habibur_rahman" VALUES(69,45,'69:45','তেন্তে নিশ্চয় আমি তাক সোঁ হাতেৰে ধৰিলোহেঁতেন,');
INSERT INTO "habibur_rahman" VALUES(69,46,'69:46','তাৰ পিছত আমি তাৰ হৃদপিণ্ডৰ শিৰা কাটি দিলোহেঁতেন,');
INSERT INTO "habibur_rahman" VALUES(69,47,'69:47','এতেকে তোমালোকৰ মাজত এনে কোনো নাই, যিয়ে তাক ৰক্ষা কৰিব।');
INSERT INTO "habibur_rahman" VALUES(69,48,'69:48','আৰু এয়া (কোৰআন) মুত্তাক্বীসকলৰ বাবে নিশ্চিত এটা উপদেশ।');
INSERT INTO "habibur_rahman" VALUES(69,49,'69:49','আৰু আমি নিশ্চয় জানো যে, তোমালোকৰ মাজত কিছুমান মিথ্যাৰোপকাৰী আছে।');
INSERT INTO "habibur_rahman" VALUES(69,50,'69:50','আৰু এইটো (কোৰআন) নিশ্চয় কাফিৰসকলৰ বাবে এক নিশ্চিত অনুশোচনাৰ কাৰণ।');
INSERT INTO "habibur_rahman" VALUES(69,51,'69:51','আৰু নিশ্চয় এইটো সুনিশ্চিত সত্য।');
INSERT INTO "habibur_rahman" VALUES(69,52,'69:52','এতেকে তুমি তোমাৰ মহান প্ৰতিপালকৰ নামত তছবীহ পাঠ কৰা।');
INSERT INTO "habibur_rahman" VALUES(70,1,'70:1','আবেদনকাৰীয়ে আবেদন কৰিলে, এনে শাস্তি সম্পৰ্কে যিটো আপতিত হ’ব--');
INSERT INTO "habibur_rahman" VALUES(70,2,'70:2','কাফিৰসকলৰ ওপৰত, যাৰ কোনো প্ৰতিৰোধকাৰী নাই।');
INSERT INTO "habibur_rahman" VALUES(70,3,'70:3','এইটো আহিব আল্লাহৰ তৰফৰ পৰা, যিজন ঊৰ্ধ্বাৰোহণৰ খটখটীবোৰৰ (জখলা) অধিকাৰী,');
INSERT INTO "habibur_rahman" VALUES(70,4,'70:4','ফিৰিস্তাসকল আৰু ৰূহ এনে এটা দিনত আল্লাহৰ দিশে ঊৰ্দ্ধগামী হয়, যাৰ পৰিমাণ পঞ্চাশ হাজাৰ বছৰ।');
INSERT INTO "habibur_rahman" VALUES(70,5,'70:5','এতেকে তুমি ধৈৰ্য ধাৰণ কৰা পৰম ধৈৰ্য।');
INSERT INTO "habibur_rahman" VALUES(70,6,'70:6','সিহঁতে এই দিনক বহু দূৰত থকা বুলি ভাৱে।');
INSERT INTO "habibur_rahman" VALUES(70,7,'70:7','আৰু আমি ইয়াক নিচেই ওচৰত দেখি আছো।');
INSERT INTO "habibur_rahman" VALUES(70,8,'70:8','সেইদিনা আকাশ হৈ যাব গলিত ধাতুৰ দৰে,');
INSERT INTO "habibur_rahman" VALUES(70,9,'70:9','আৰু পৰ্বতসমূহ হৈ যাব ৰঙ-বিৰঙৰ ঊণৰ দৰে,');
INSERT INTO "habibur_rahman" VALUES(70,10,'70:10','আৰু কোনো অন্তৰঙ্গ বন্ধুৱে অন্তৰঙ্গ বন্ধুৰ খা-খবৰ নুসুধিব,');
INSERT INTO "habibur_rahman" VALUES(70,11,'70:11','সিহঁতক ইজনে-সিজনৰ দৃষ্টিগোচৰ কৰোৱা হ’ব। অপৰাধীয়ে সেইদিনাৰ শাস্তিৰ বিনিময়ত দিব বিচাৰিব তাৰ সন্তান-সন্ততিক,');
INSERT INTO "habibur_rahman" VALUES(70,12,'70:12','আৰু তাৰ স্ত্ৰী আৰু ভাতৃক,');
INSERT INTO "habibur_rahman" VALUES(70,13,'70:13','আৰু তাৰ জাতি-গোষ্ঠিক, যিসকলে তাক আশ্ৰয় দিছিল,');
INSERT INTO "habibur_rahman" VALUES(70,14,'70:14','আৰু পৃথিৱীত যিসকল আছে সেই সকলোকে (দিব বিচাৰিব), যাতে এই বিনিময়ে তাক মুক্তি দিয়ে।');
INSERT INTO "habibur_rahman" VALUES(70,15,'70:15','কেতিয়াও নহয়, নিশ্চয় এইটো লেলিহান জুই,');
INSERT INTO "habibur_rahman" VALUES(70,16,'70:16','যিয়ে মূৰৰ ছাল খহাই পেলাব।');
INSERT INTO "habibur_rahman" VALUES(70,17,'70:17','জাহান্নামে সেই ব্যক্তিক মাতিব, যিয়ে সত্যৰ প্ৰতি পৃষ্ঠ প্ৰদৰ্শন কৰিছিল আৰু মুখ ঘূৰাই লৈছিল।');
INSERT INTO "habibur_rahman" VALUES(70,18,'70:18','আৰু সম্পদ সঞ্চয় কৰিছিল আৰু সেইবোৰ সংৰক্ষণ কৰি ৰাখিছিল।');
INSERT INTO "habibur_rahman" VALUES(70,19,'70:19','নিশ্চয় মানুহক সৃষ্টি কৰা হৈছে অতিশয় ধৈৰ্যহীন অস্থিৰচিত্তৰূপে।');
INSERT INTO "habibur_rahman" VALUES(70,20,'70:20','যেতিয়া তাক বিপদে স্পৰ্শ কৰে তেতিয়া সি অতিমাত্ৰা উৎকন্ঠিত হৈ পৰে।');
INSERT INTO "habibur_rahman" VALUES(70,21,'70:21','আৰু যেতিয়া তাক কল্যাণে স্পৰ্শ কৰে তেতিয়া সি অতিশয় কৃপণ হৈ পৰে।');
INSERT INTO "habibur_rahman" VALUES(70,22,'70:22','কিন্তু ছালাত প্ৰতিষ্ঠাকাৰীসকলৰ বাহিৰে,');
INSERT INTO "habibur_rahman" VALUES(70,23,'70:23','যিসকলে নিজ ছালাতৰ ক্ষেত্ৰত সদায় প্ৰতিষ্ঠিত,');
INSERT INTO "habibur_rahman" VALUES(70,24,'70:24','আৰু যিসকলৰ ধন-সম্পদত নিৰ্ধাৰিত হক্ব আছে,');
INSERT INTO "habibur_rahman" VALUES(70,25,'70:25','খোজনীয়া-মগনীয়া আৰু বঞ্চিতসকলৰ বাবে,');
INSERT INTO "habibur_rahman" VALUES(70,26,'70:26','আৰু যিসকলে প্ৰতিফল দিৱসক সত্য বুলি বিশ্বাস কৰে।');
INSERT INTO "habibur_rahman" VALUES(70,27,'70:27','আৰু যিসকলে নিজ প্ৰতিপালকৰ শাস্তি সম্পৰ্কে ভীত-সন্ত্ৰস্ত--');
INSERT INTO "habibur_rahman" VALUES(70,28,'70:28','নিশ্চয় তেওঁলোকৰ প্ৰতিপালকৰ শাস্তিৰ পৰা নিৰাপদ হৈ থকা নাযায়;');
INSERT INTO "habibur_rahman" VALUES(70,29,'70:29','আৰু যিসকল নিজৰ যৌনাঙ্গসমূহৰ হিফাজতকাৰী,');
INSERT INTO "habibur_rahman" VALUES(70,30,'70:30','কিন্তু সিহঁতৰ পত্নী অথবা অধিকাৰভুক্ত দাসীৰ বাহিৰে, এনে ক্ষেত্ৰত সিহঁত নিন্দনীয় নহ’ব--');
INSERT INTO "habibur_rahman" VALUES(70,31,'70:31','কিন্তু যিসকলে এওঁলোকৰ বাহিৰে আনক কামনা কৰে, সিহঁতেই সীমালঙ্ঘনকাৰী।');
INSERT INTO "habibur_rahman" VALUES(70,32,'70:32','আৰু যিসকলে নিজৰ আমানত আৰু প্ৰতিশ্ৰুতি ৰক্ষা কৰে,');
INSERT INTO "habibur_rahman" VALUES(70,33,'70:33','আৰু যিসকলে নিজ সাক্ষ্যদানত অটল,');
INSERT INTO "habibur_rahman" VALUES(70,34,'70:34','আৰু যিসকলে নিজৰ ছালাতৰ হিফাজত কৰে,');
INSERT INTO "habibur_rahman" VALUES(70,35,'70:35','তেওঁলোকেই জান্নাতসমূহত সন্মানিত হ’ব।');
INSERT INTO "habibur_rahman" VALUES(70,36,'70:36','কাফিৰসকলৰ কি হ’ল, সিহঁতে দেখুন তোমাৰ ফালে ঢপলীয়াই আহি আছে?');
INSERT INTO "habibur_rahman" VALUES(70,37,'70:37','সোঁ ফালৰ পৰা আৰু বাওঁ ফালৰ পৰা, দলে দলে।');
INSERT INTO "habibur_rahman" VALUES(70,38,'70:38','সিহঁতৰ প্ৰত্যেকেই এইটো আশা কৰে নেকি যে, তাক প্ৰাচুৰ্যময় জান্নাতত প্ৰৱেশ কৰোৱা হ’ব?');
INSERT INTO "habibur_rahman" VALUES(70,39,'70:39','কেতিয়াও নহয়, নিশ্চয় সিহঁতক যিটোৰে সৃষ্টি কৰিছো সেয়া সিহঁতে জানে।');
INSERT INTO "habibur_rahman" VALUES(70,40,'70:40','এতেকে মই শপত কৰিছো উদয়স্থল আৰু অস্তাচলসমূহৰ প্ৰতিপালকৰ, অৱশ্যে আমি সক্ষম,');
INSERT INTO "habibur_rahman" VALUES(70,41,'70:41','সিহঁততকৈ উৎকৃষ্টসকলক সিহঁতৰ স্থলৱৰ্তী কৰিবলৈ, আৰু এনে কৰাত আমি অক্ষম নহয়।');
INSERT INTO "habibur_rahman" VALUES(70,42,'70:42','এতেকে সিহঁতক নিৰৰ্থক কথা-বতৰা আৰু খেল-ধেমালিত মত্ত হৈ থাকিবলৈ দিয়া-- যি দিনৰ প্ৰতিশ্ৰুতি সিহঁতক দিয়া হৈছে সেই দিনৰ সন্মুখীন নোহোৱা পৰ্যন্ত।');
INSERT INTO "habibur_rahman" VALUES(70,43,'70:43','সেইদিনা সিহঁত কবৰৰ পৰা দ্ৰুতবেগত ওলাই আহিব, এনে লাগিব যেনিবা সিহঁতে কোনো লক্ষ্যস্থলৰ ফালে ধাবিত হৈছে,');
INSERT INTO "habibur_rahman" VALUES(70,44,'70:44','সিহঁতৰ দৃষ্টিসমূহ অৱনত অৱস্থাত থাকিব, অপমানে সিহঁতক আচ্ছন্ন কৰিব এইটোৱে সেইদিন যাৰ বিষয়ে সিহঁতক সতৰ্ক কৰা হৈছিল।');
INSERT INTO "habibur_rahman" VALUES(71,1,'71:1','নিশ্চয় আমি নূহক তেওঁৰ সম্প্ৰদায়ৰ প্ৰতি এই নিৰ্দেশসহ প্ৰেৰণ কৰিছিলো যে, ‘সিহঁতৰ ওচৰত যন্ত্ৰণাদায়ক শাস্তি অহাৰ পূৰ্বে তুমি সিহঁতক সতৰ্ক কৰি দিয়া’।');
INSERT INTO "habibur_rahman" VALUES(71,2,'71:2','তেওঁ ক’লে, ‘হে মোৰ সম্প্ৰদায়! নিশ্চয় মই তোমালোকৰ বাবে স্পষ্ট সতৰ্ককাৰী--');
INSERT INTO "habibur_rahman" VALUES(71,3,'71:3','‘এই বিষয়ে যে, তোমালোকে আল্লাহৰ ইবাদত কৰা আৰু তেওঁৰ তাক্বৱা অৱলম্বন কৰা, আৰু মোৰ আনুগত্য কৰা;');
INSERT INTO "habibur_rahman" VALUES(71,4,'71:4','তেওঁ তোমালোকৰ বাবে তোমালোকৰ পাপসমূহ ক্ষমা কৰিব আৰু তোমালোকক এটা নিৰ্দিষ্ট সময় পৰ্যন্ত অৱকাশ দিব। নিশ্চয় আল্লাহৰ নিৰ্ধাৰিত সময় উপস্থিত হ’লে সেয়া (কেতিয়াও) বিলম্ব কৰা নহয়; যদি তোমালোকে এইটো জানিলাহেঁতেন!’');
INSERT INTO "habibur_rahman" VALUES(71,5,'71:5','তেওঁ ক’লে, ‘হে মোৰ প্ৰতিপালক! নিশ্চয় মই মোৰ সম্প্ৰদায়ক দিনে ৰাতিয়ে আহ্বান কৰিছো,');
INSERT INTO "habibur_rahman" VALUES(71,6,'71:6','‘কিন্তু মোৰ আহ্বানত সিহঁতৰ আঁতৰি যোৱাটোহে বৃদ্ধি পাইছে’।');
INSERT INTO "habibur_rahman" VALUES(71,7,'71:7','আৰু যেতিয়াই মই সিহঁতক আহ্বান কৰিছো যাতে তুমি সিহঁতক ক্ষমা কৰা, সিহঁতে নিজৰ কাণত আঙুলিৰে সোপা দিছে আৰু কাপোৰেৰে নিজকে ঢাকি লৈছে, আৰু আঁকোৰগোঁজ হৈ থাকিছে লগতে অতি দাম্ভিকতাৰে ঔদ্ধত্য প্ৰকাশ কৰিছে।');
INSERT INTO "habibur_rahman" VALUES(71,8,'71:8','‘তাৰ পিছতো মই সিহঁতক প্ৰকাশ্য আহ্বান কৰিছো,');
INSERT INTO "habibur_rahman" VALUES(71,9,'71:9','‘আকৌ সিহঁতক মই প্ৰকাশ্য আৰু অতি গোপনীয়ভাৱেও আহ্বান কৰিছো’।');
INSERT INTO "habibur_rahman" VALUES(71,10,'71:10','আৰু কৈছো, তোমালোকৰ প্ৰতিপালকৰ ওচৰত ক্ষমা প্ৰাৰ্থনা কৰা, নিশ্চয় তেওঁ মহাক্ষমাশীল,');
INSERT INTO "habibur_rahman" VALUES(71,11,'71:11','তেওঁ তোমালোকৰ বাবে প্ৰচুৰ পৰিমাণে বৰষুণ বৰ্ষাব,');
INSERT INTO "habibur_rahman" VALUES(71,12,'71:12','‘আৰু তোমালোকক ধন-সম্পদ আৰু সন্তান-সন্ততি প্ৰদান কৰি সহায় কৰিব আৰু তোমালোকৰ বাবে উদ্যান স্থাপন কৰিব আৰু নদ-নদী প্ৰবাহিত কৰিব।');
INSERT INTO "habibur_rahman" VALUES(71,13,'71:13','তোমালোকৰ কি হ''ল, তোমালোকে আল্লাহৰ শ্ৰেষ্ঠত্ব উপলব্ধি নকৰা কিয়?');
INSERT INTO "habibur_rahman" VALUES(71,14,'71:14','‘অথচ তেৱেঁই তোমালোকক সৃষ্টি কৰিছে পৰ্যায়ক্ৰমে।');
INSERT INTO "habibur_rahman" VALUES(71,15,'71:15','তোমালোকে লক্ষ্য কৰা নাইনে, আল্লাহে কেনেকৈ স্তৰে স্তৰে সাতখন আকাশ সৃষ্টি কৰিছে?');
INSERT INTO "habibur_rahman" VALUES(71,16,'71:16','আৰু এইবোৰৰ মাজত চন্দ্ৰক সৃষ্টি কৰিছে পোহৰ হিচাপে আৰু সূৰ্যক বনাইছে প্ৰদীপ।');
INSERT INTO "habibur_rahman" VALUES(71,17,'71:17','‘আৰু আল্লাহে তোমালোকক মাটিৰ পৰা উদ্ভূত কৰিছে,');
INSERT INTO "habibur_rahman" VALUES(71,18,'71:18','‘তাৰ পিছত তেওঁ তোমালোকক তালৈ উভতাই নিব আৰু নিশ্চিতভাৱে তোমালোকক পুনৰুত্থিত কৰিব,');
INSERT INTO "habibur_rahman" VALUES(71,19,'71:19','‘আৰু আল্লাহে পৃথিৱীখনক তোমালোকৰ বাবে বিস্তৃত কৰিছে,');
INSERT INTO "habibur_rahman" VALUES(71,20,'71:20','‘যাতে তোমালোকে তাত প্ৰশস্ত পথত চলাচল কৰিব পাৰা’।');
INSERT INTO "habibur_rahman" VALUES(71,21,'71:21','নুহে ক’লে, ‘হে মোৰ প্ৰতিপালক! সিহঁতে মোক অমান্য কৰিছে আৰু এনে লোকৰ অনুসৰণ কৰিছে যাৰ ধন-সম্পদ আৰু সন্তান-সন্ততিয়ে কেৱল তাৰ ক্ষতিহে বৃদ্ধি কৰিছে’।');
INSERT INTO "habibur_rahman" VALUES(71,22,'71:22','আৰু সিহঁতে ভয়াৱহ ষড়যন্ত্ৰ কৰিছে;');
INSERT INTO "habibur_rahman" VALUES(71,23,'71:23','আৰু সিহঁতে কৈছে, ‘তোমালোকে কেতিয়াও তোমালোকৰ উপাস্যসকলক বৰ্জন নকৰিবা; আৰু পৰিত্যাগ নকৰিবা অদ্দ, ছুৱাআ, ইয়াগুছ, ইয়াউক্ব আৰু নাছৰক’।');
INSERT INTO "habibur_rahman" VALUES(71,24,'71:24','‘নিশ্চয় সিহঁতে বহুলোকক পথভ্ৰষ্ট কৰিছে, আৰু (হে আল্লাহ) তুমি যালিমসকলৰ বাবে ভ্ৰষ্টতাৰ বাহিৰে আন একো বৃদ্ধি নকৰিবা’।');
INSERT INTO "habibur_rahman" VALUES(71,25,'71:25','সিহঁতৰ অপৰাধৰ বাবে সিহঁতক ডুবাই দিয়া হ’ল তাৰ পিছত সিহঁতক জুইত প্ৰৱেশ কৰোৱা হ’ল, এতেকে সিহঁতে আল্লাহৰ বাহিৰে আন কাকো সহায়কাৰী হিচাপে পোৱা নাছিল।');
INSERT INTO "habibur_rahman" VALUES(71,26,'71:26','আৰু নুহে ক’লে, ‘হে মোৰ প্ৰতিপালক! পৃথিৱীৰ কাফিৰসকলৰ মাজৰ পৰা কোনো গৃহবাসীকে অৱশিষ্ট নাৰাখিবা।');
INSERT INTO "habibur_rahman" VALUES(71,27,'71:27','‘যদি তুমি সিহঁতক অৱশিষ্ট ৰাখা তেন্তে সিহঁতে তোমাৰ বান্দাসকলক পথভ্ৰষ্ট কৰিব আৰু সিহঁতে কেৱল দুস্কৃতিকাৰী কাফিৰকহে জন্ম দিব।');
INSERT INTO "habibur_rahman" VALUES(71,28,'71:28','হে মোৰ প্ৰতিপালক! মোক আৰু মোৰ পিতৃ-মাতৃক আৰু যিয়ে মুমিন হৈ মোৰ ঘৰত প্ৰৱেশ কৰিব লগতে মুমিন পুৰুষ আৰু মুমিনা নাৰীসকলক ক্ষমা কৰি দিয়া; আৰু যালিমসকলৰ বাবে ধ্বংসৰ বাহিৰে আন একো বৃদ্ধি নকৰিবা’।');
INSERT INTO "habibur_rahman" VALUES(72,1,'72:1','কোৱা, মোৰ প্ৰতি অহী নাযিল হৈছে যে, নিশ্চয় জিনসকলৰ এটা দলে মনোযোগ সহকাৰে শুনিছে; তাৰ পিছত কৈছে, ‘নিশ্চয় আমি এক বিস্ময়কৰ কোৰআন শুনিছো,');
INSERT INTO "habibur_rahman" VALUES(72,2,'72:2','‘যিটো সত্যৰ পথ দেখুৱায়, সেয়ে আমি তাৰ ওপৰত ঈমান আনিছো। গতিকে আমি কেতিয়াও আমাৰ প্ৰতিপালকৰ লগত আন কাকো অংশী বুলি গণ্য নকৰিম,');
INSERT INTO "habibur_rahman" VALUES(72,3,'72:3','‘আৰু নিশ্চয় আমাৰ প্ৰতিপালকৰ মৰ্যাদা সুউচ্চ’ তেওঁ কোনো সঙ্গিনী আৰু কোনো সন্তানো গ্ৰহণ কৰা নাই।');
INSERT INTO "habibur_rahman" VALUES(72,4,'72:4','‘আৰু আমাৰ মাজৰ নিৰ্বোধসকলে আল্লাহৰ বিষয়ে অবাস্তৱ কথা-বতৰা কৈছিল’।');
INSERT INTO "habibur_rahman" VALUES(72,5,'72:5','‘অথচ আমি ধাৰণা কৰিছিলো যে, মানুহ আৰু জিন জাতিয়ে কেতিয়াও আল্লাহ সম্পৰ্কে মিছা আৰোপ নকৰিব।');
INSERT INTO "habibur_rahman" VALUES(72,6,'72:6','‘আৰু নিশ্চয় কিছুমান মানুহে কিছুমান জিনৰ আশ্ৰয় লৈছিল, ফলত সিহঁতে জিনসকলৰ অহংকাৰ বৃদ্ধি কৰিছিল’।');
INSERT INTO "habibur_rahman" VALUES(72,7,'72:7','‘আৰু নিশ্চয় সিহঁতেও ধাৰণা কৰিছিল যেনেকৈ তোমালোকে ধাৰণা কৰিছা যে, আল্লাহে কাকো কেতিয়াও পুনৰুত্থিত নকৰিব’।');
INSERT INTO "habibur_rahman" VALUES(72,8,'72:8','‘আৰু নিশ্চয় আমি আকাশৰ তথ্য সংগ্ৰহ কৰিব বিচাৰিছিলো, কিন্তু আমি দেখিলো যে, আকাশখন কঠোৰ প্ৰহৰী আৰু উল্কাপিণ্ডৰ দ্বাৰা পৰিপূৰ্ণ;');
INSERT INTO "habibur_rahman" VALUES(72,9,'72:9','‘আৰু আমি আগতে আকাশৰ বিভিন্ন ঘাঁটিত সংবাদ শুনাৰ বাবে বহিছিলো কিন্তু এতিয়া কোনোবাই সংবাদ শুনিবলৈ বিচাৰিলে সি তাৰ ওপৰত নিক্ষেপৰ বাবে প্ৰস্তুত জলন্ত উল্কাপিণ্ডৰ সন্মুখীন হয়।');
INSERT INTO "habibur_rahman" VALUES(72,10,'72:10','‘আৰু নিশ্চয় আমি নাজানো যে, পৃথিৱীবাসীৰ বাবে অমঙ্গল বিচৰা হৈছে, নে সিহঁতৰ প্ৰতিপালকে সিহঁতৰ মঙ্গল বিচাৰিছে।');
INSERT INTO "habibur_rahman" VALUES(72,11,'72:11','আৰু নিশ্চয় আমাৰ মাজৰ কিছুমান হৈছে সৎকৰ্মশীল আৰু কিছুমান ইয়াৰ ব্যতিক্ৰম, আমি আছিলো বিভিন্ন পথৰ অনুসাৰী;');
INSERT INTO "habibur_rahman" VALUES(72,12,'72:12','‘আৰু আমি বুজি পাইছো যে, আমি পৃথিৱীত কেতিয়াও আল্লাহক অপাৰগ কৰিব নোৱাৰিম আৰু পলায়ন কৰিও তেওঁক ব্যৰ্থ কৰিব নোৱাৰিম।');
INSERT INTO "habibur_rahman" VALUES(72,13,'72:13','‘আৰু নিশ্চয় আমি যেতিয়া হিদায়তৰ বাণী শুনিলো, তেতিয়া তাৰ প্ৰতি ঈমান আনিলো। এতেকে যি ব্যক্তি তাৰ প্ৰতিপালকৰ প্ৰতি ঈমান আনিব, তাৰ কোনো ক্ষতি আৰু কোনো অন্যায়ৰ আশংকা নাথাকিব।');
INSERT INTO "habibur_rahman" VALUES(72,14,'72:14','‘আৰু নিশ্চয় আমাৰ মাজত আছে কিছু সংখ্যক আত্মসমৰ্পনকাৰী আৰু কিছু সংখ্যক আছে সীমালংঘনকাৰী; এতেকে যিসকলে ইছলাম গ্ৰহণ কৰিছে সিহঁতেই সঠিক পথ বাছি লৈছে।');
INSERT INTO "habibur_rahman" VALUES(72,15,'72:15','‘আৰু যিসকল সীমালংঘনকাৰী, সিহঁতেই হৈছে জাহান্নামৰ ইন্ধন’।');
INSERT INTO "habibur_rahman" VALUES(72,16,'72:16','আৰু যদি সিহঁতে সঠিক পথত অবিচল থাকিলেহেঁতেন, তেনেহ’লে আমি নিশ্চয় সিহঁতক প্ৰচুৰ পৰিমাণে পানী পান কৰালোহেঁতেন,');
INSERT INTO "habibur_rahman" VALUES(72,17,'72:17','যাতে আমি ইয়াৰ দ্বাৰা সিহঁতক পৰীক্ষা কৰিব পাৰো; আৰু যিয়ে নিজ প্ৰতিপালকৰ স্মৰণৰ পৰা মুখ ঘূৰাই লয়, তাক তেওঁ কঠিন শাস্তিত প্ৰৱেশ কৰাব।');
INSERT INTO "habibur_rahman" VALUES(72,18,'72:18','আৰু নিশ্চয় মছজিদসমূহ আল্লাহৰ বাবেই, গতিকে তোমালোকে আল্লাহৰ লগত আন কাকো নামাতিবা।');
INSERT INTO "habibur_rahman" VALUES(72,19,'72:19','আৰু নিশ্চয় যেতিয়া আল্লাহৰ বান্দাই তেওঁক প্ৰাৰ্থনা কৰিবলৈ থিয় দিলে, তেতিয়া সিহঁতে তেওঁৰ ওচৰত আহি ভিৰ কৰিলে।');
INSERT INTO "habibur_rahman" VALUES(72,20,'72:20','কোৱা, ‘মই কেৱল মোৰ প্ৰতিপালককেই আহ্বান কৰো আৰু তেওঁৰ লগত আন কাকো আংশী নকৰো’।');
INSERT INTO "habibur_rahman" VALUES(72,21,'72:21','কোৱা, ‘নিশ্চয় মই তোমালোকৰ কোনো ক্ষতি আৰু কল্যাণৰ মালিক নহয়’।');
INSERT INTO "habibur_rahman" VALUES(72,22,'72:22','কোৱা, নিশ্চয় আল্লাহৰ পৰা কোনেও মোক ৰক্ষা কৰিব নোৱাৰিব আৰু তেওঁৰ বাহিৰে মই কেতিয়াও কোনো আশ্ৰয় নাপাম।');
INSERT INTO "habibur_rahman" VALUES(72,23,'72:23','‘কেৱল আল্লাহৰ তৰফৰ পৰা তেওঁৰ ৰিছালতৰ বাণী প্ৰচাৰ কৰাটোৱে মোৰ দায়িত্ব। আৰু যিয়ে আল্লাহ আৰু তেওঁৰ ৰাছুলক অমান্য কৰিব তাৰ বাবে আছে জাহান্নামৰ জুই, তাত সিহঁতে চিৰকাল থাকিব’।');
INSERT INTO "habibur_rahman" VALUES(72,24,'72:24','অৱশেষত যেতিয়া সিহঁতে ইয়াক প্ৰত্যক্ষ কৰিব, যি বিষয়ে সিহঁতক সাৱধান কৰা হৈছিল। তেতিয়া সিহঁতে জানিবলৈ পাব যে, সহায়কাৰী হিচাপে কোন বেছি দুৰ্বল আৰু সংখ্যাত কোন আটাইতকৈ কম।');
INSERT INTO "habibur_rahman" VALUES(72,25,'72:25','কোৱা, ‘মই নাজানো তোমালোকক যিটোৰ প্ৰতিশ্ৰুতি দিয়া হৈছে, সেইটো নিকটৱৰ্তী নে মোৰ প্ৰতিপালকে ইয়াৰ বাবে কোনো দীৰ্ঘ ম্যাদ নিৰ্ধাৰণ কৰিব’।');
INSERT INTO "habibur_rahman" VALUES(72,26,'72:26','তেওঁ অদৃশ্য বিষয়ৰ জ্ঞানী, আৰু তেওঁ নিজৰ অদৃশ্যৰ জ্ঞান কাৰো ওচৰত প্ৰকাশ নকৰে,');
INSERT INTO "habibur_rahman" VALUES(72,27,'72:27','কিন্তু তেওঁৰ মনোনীত ৰাছুলৰ বাহিৰে। তেনে ক্ষেত্ৰত আল্লাহে তেওঁৰ ৰাছুলৰ সন্মুখত আৰু পিছত প্ৰহৰী নিযুক্ত কৰে,');
INSERT INTO "habibur_rahman" VALUES(72,28,'72:28','যাতে তেওঁ প্ৰকাশ কৰে যে, নিশ্চয় তেওঁলোকে তেওঁলোকৰ প্ৰতিপালকৰ ৰিছালত পৌঁচাই দিছে। আৰু তেওঁলোকৰ ওচৰত যি আছে সেয়া তেওঁ জ্ঞানৰ দ্বাৰা পৰিবেষ্টন কৰি ৰাখিছে আৰু তেওঁ প্ৰতিটো বস্তু গণনা কৰি হিচাপ কৰি ৰাখিছে।');
INSERT INTO "habibur_rahman" VALUES(73,1,'73:1','হে বস্ত্ৰাবৃত!');
INSERT INTO "habibur_rahman" VALUES(73,2,'73:2','ৰাতি ছালাত আদায় কৰা, কিছু অংশৰ বাহিৰে,');
INSERT INTO "habibur_rahman" VALUES(73,3,'73:3','আধা ৰাতি বা তাতোকৈ অলপ কম;');
INSERT INTO "habibur_rahman" VALUES(73,4,'73:4','অথবা তাতোকৈ অলপ বেছি। আৰু স্পষ্টভাৱে ধীৰে ধীৰে কোৰআন তিলাৱাত কৰা।');
INSERT INTO "habibur_rahman" VALUES(73,5,'73:5','নিশ্চয় আমি তোমাৰ প্ৰতি এটি গধুৰ বাণী অৱতীৰ্ণ কৰিম।');
INSERT INTO "habibur_rahman" VALUES(73,6,'73:6','নিশ্চয় ৰাতি-জাগৰণ আত্মসংযমৰ বাবে অধিক প্ৰবল আৰু বাকস্ফুৰণত অধিক উপযোগী।');
INSERT INTO "habibur_rahman" VALUES(73,7,'73:7','নিশ্চয় দিনৰ ভাগত তোমাৰ বাবে আছে দীৰ্ঘ কৰ্মব্যস্ততা।');
INSERT INTO "habibur_rahman" VALUES(73,8,'73:8','আৰু তুমি তোমাৰ প্ৰতিপালকৰ নাম স্মৰণ কৰা আৰু একনিষ্ঠভাৱে তেওঁৰ প্ৰতি মনোনিবেশ কৰা।');
INSERT INTO "habibur_rahman" VALUES(73,9,'73:9','তেৱেঁই পূৱ আৰু পশ্চিমৰ ৰব, তেওঁৰ বাহিৰে আন কোনো (সত্য) ইলাহ নাই। এতেকে তেওঁকেই তুমি কাৰ্য সম্পাদনকাৰীৰূপে গ্ৰহণ কৰা।');
INSERT INTO "habibur_rahman" VALUES(73,10,'73:10','আৰু মানুহে যি কয় তাৰ ওপৰত তুমি ধৈৰ্য ধাৰণ কৰা আৰু সৌজন্যৰ সৈতে সিহঁতক পৰিহাৰ কৰি চলা।');
INSERT INTO "habibur_rahman" VALUES(73,11,'73:11','আৰু বিলাসী সামগ্ৰীৰ অধিকাৰী মিথ্যাৰোপকাৰীসকলক মোলৈ এৰি দিয়া আৰু সিহঁতক কিছু সময় অৱকাশ দিয়া।');
INSERT INTO "habibur_rahman" VALUES(73,12,'73:12','নিশ্চয় আমাৰ ওচৰত আছে শিকলিসমূহ আৰু প্ৰজ্বলিত জুই,');
INSERT INTO "habibur_rahman" VALUES(73,13,'73:13','আৰু আছে কাইটযুক্ত খাদ্য আৰু যন্ত্ৰণাদায়ক শাস্তি।');
INSERT INTO "habibur_rahman" VALUES(73,14,'73:14','যিদিনা পৃথিৱী আৰু পৰ্বতসমূহ প্ৰকম্পিত হ’ব আৰু পৰ্বতসমূহ বিক্ষিপ্ত চলমান বালীময়ত পৰিণত হ’ব।');
INSERT INTO "habibur_rahman" VALUES(73,15,'73:15','নিশ্চয় আমি তোমালোকৰ বাবে সাক্ষী স্বৰূপে তোমালোকৰ ওচৰত ৰাছুল প্ৰেৰণ কৰিছো যেনেকৈ ফিৰআউনৰ ওচৰত ৰাছুল প্ৰেৰণ কৰিছিলো।');
INSERT INTO "habibur_rahman" VALUES(73,16,'73:16','কিন্তু ফিৰআউনে ৰাছুলক অমান্য কৰিছে, সেয়ে আমি তাক শাস্তি বিহিছো।');
INSERT INTO "habibur_rahman" VALUES(73,17,'73:17','এতেকে তোমালোকে যদি কুফুৰী কৰা তেন্তে তোমালোকে সেই দিনটোত কেনেকৈ আত্ম ৰক্ষা কৰিবা যি দিৱসে শিশুসকলক বৃদ্ধত পৰিণত কৰিব,');
INSERT INTO "habibur_rahman" VALUES(73,18,'73:18','সেই দিনা আকাশখন বিদীৰ্ণ হ’ব, তেওঁৰ প্ৰতিশ্ৰুতি বাস্তবায়িত হ’ব।');
INSERT INTO "habibur_rahman" VALUES(73,19,'73:19','নিশ্চয় এইটো এটা উপদেশ, এতেকে যিয়ে ইচ্ছা কৰে সি নিজ প্ৰতিপালকৰ পথ অৱলম্বন কৰক।');
INSERT INTO "habibur_rahman" VALUES(73,20,'73:20','নিশ্চয় তোমাৰ প্ৰতিপালকে জানে যে, তুমি ছালাতৰ বাবে থিয় দিয়া কেতিয়াবা ৰাতিৰ প্ৰায় দুই-তৃতীয়াংশত, কেতিয়াবা অৰ্ধাংশত আৰু কেতিয়াবা এক-তৃতীয়াংশত আৰু তোমাৰ লগত থকাসকলৰ এটা দলেও থিয় দিয়ে। আৰু আল্লাহেই নিৰ্ধাৰণ কৰে দিন আৰু ৰাতিৰ পৰিমাণ। তেওঁ জানে যে, তোমালোকে ইয়াৰ সঠিক নিৰ্ধাৰণ পালন কৰিব নোৱাৰিবা, সেয়ে তেওঁ তোমালোকক ক্ষমা কৰিলে। এতেকে তোমালোকে কোৰআনৰ পৰা যিমানখিনি সহজ হয় সিমানখিনি পাঠ কৰা। তেওঁ জানে যে, তোমালোকৰ মাজৰ কিছুমান অসুস্থ হৈ পৰিব, আকৌ কিছুমানে আল্লাহৰ অনুগ্ৰহ সন্ধান কৰিবলৈ ভ্ৰমণ কৰিব আৰু আন কিছুমানে আল্লাহৰ পথত যুদ্ধৰত অৱস্থাত থাকিব। সেয়ে তোমালোকে কোৰআনৰ পৰা যিমানখিনি সহজ হয় সেইখিনি পাঠ কৰা; আৰু তোমালোকে ছালাত প্ৰতিষ্ঠা কৰা আৰু যাকাত প্ৰদান কৰা লগতে আল্লাহক উত্তম ঋণ প্ৰদান কৰা। তোমালোকৰ নিজৰ মঙ্গলৰ বাবে তোমালোকে যি আগতীয়াকৈ প্ৰেৰণ কৰিবা সেয়া আল্লাহৰ ওচৰত পাবা। ই উৎকৃষ্টতম আৰু পুৰষ্কাৰ হিচাপে বৰ মহৎ। আল্লাহৰ ওচৰত ক্ষমা প্ৰাৰ্থনা কৰি থাকা; নিশ্চয় আল্লাহ ক্ষমাশীল, পৰম দয়ালু।');
INSERT INTO "habibur_rahman" VALUES(74,1,'74:1','হে বস্ত্ৰাবৃত!');
INSERT INTO "habibur_rahman" VALUES(74,2,'74:2','উঠা, আৰু সতৰ্ক কৰা,');
INSERT INTO "habibur_rahman" VALUES(74,3,'74:3','আৰু তোমাৰ প্ৰতিপালকৰ শ্ৰেষ্ঠত্ব ঘোষণা কৰা।');
INSERT INTO "habibur_rahman" VALUES(74,4,'74:4','আৰু তোমাৰ পোছাক-পৰিচ্ছদ পৱিত্ৰ কৰা,');
INSERT INTO "habibur_rahman" VALUES(74,5,'74:5','আৰু অপৱিত্ৰতা (শ্বিৰ্ক) পৰিহাৰ কৰা,');
INSERT INTO "habibur_rahman" VALUES(74,6,'74:6','আৰু অধিক পোৱাৰ আশাত কাকো অনুগ্ৰহ নকৰিবা।');
INSERT INTO "habibur_rahman" VALUES(74,7,'74:7','আৰু তোমাৰ প্ৰতিপালকৰ বাবেই ধৈৰ্য ধাৰণ কৰা।');
INSERT INTO "habibur_rahman" VALUES(74,8,'74:8','এতেকে যেতিয়া শিঙাত ফুঁ দিয়া হ’ব,');
INSERT INTO "habibur_rahman" VALUES(74,9,'74:9','সেই দিনটো হ’ব এটা সংকটৰ দিন-');
INSERT INTO "habibur_rahman" VALUES(74,10,'74:10','যিটো কাফিৰসকলৰ বাবে সহজ নহয়।');
INSERT INTO "habibur_rahman" VALUES(74,11,'74:11','যাক মই অকলশৰীয়াকৈ সৃষ্টি কৰিছো তাক মোৰ হাতত এৰি দিয়া।');
INSERT INTO "habibur_rahman" VALUES(74,12,'74:12','আৰু যাক মই প্ৰদান কৰিছো বিপুল ধন-সম্পদ,');
INSERT INTO "habibur_rahman" VALUES(74,13,'74:13','আৰু (প্ৰদান কৰিছো) নিত্যসঙ্গী পুত্ৰবোৰ।');
INSERT INTO "habibur_rahman" VALUES(74,14,'74:14','আৰু তাক প্ৰদান কৰিছো স্বাচ্ছন্দ জীৱনৰ প্ৰচুৰ উপকৰণ-');
INSERT INTO "habibur_rahman" VALUES(74,15,'74:15','তথাপিও সি কামনা কৰে যে, মই তাক আৰু অধিক দিওঁ!');
INSERT INTO "habibur_rahman" VALUES(74,16,'74:16','কেতিয়াও নহয়, সি আমাৰ নিদৰ্শনসমূহৰ বিৰুদ্ধাচাৰী।');
INSERT INTO "habibur_rahman" VALUES(74,17,'74:17','অনতিপলমে মই তাক (জাহান্নামৰ পিচল শিলত) আৰোহণ কৰিবলৈ বাধ্য কৰাম।');
INSERT INTO "habibur_rahman" VALUES(74,18,'74:18','নিশ্চয় সি চিন্তা কৰিলে আৰু সিদ্ধান্ত গ্ৰহণ কৰিলে।');
INSERT INTO "habibur_rahman" VALUES(74,19,'74:19','এতেকে সি ধ্বংস হওক! কেনেকৈ সি (এই) সিদ্ধান্ত গ্ৰহণ কৰিলে?');
INSERT INTO "habibur_rahman" VALUES(74,20,'74:20','আকৌ সি ধ্বংস হওক! কেনেকৈ সি (এই) সিদ্ধান্ত গ্ৰহণ কৰিলে?');
INSERT INTO "habibur_rahman" VALUES(74,21,'74:21','তাৰ পিছত সি লক্ষ্য কৰিলে।');
INSERT INTO "habibur_rahman" VALUES(74,22,'74:22','তাৰ পিছত সি ভ্ৰুঁ কোচ খোৱালে আৰু মুখ বিকৃত কৰিলে।');
INSERT INTO "habibur_rahman" VALUES(74,23,'74:23','তাৰ পিছত সি উভতিলে আৰু অহংকাৰ কৰিলে।');
INSERT INTO "habibur_rahman" VALUES(74,24,'74:24','তাৰ পিছত সি ক’লে, ‘এয়া পূৰ্বৰে পৰা চলি অহা যাদুৰ বাহিৰে আন একো নহয়’,');
INSERT INTO "habibur_rahman" VALUES(74,25,'74:25','‘এয়াতো মানুহৰেই কথা’।');
INSERT INTO "habibur_rahman" VALUES(74,26,'74:26','অনতিপলমে মই তাক ছাক্বাৰত (জাহান্নামত) প্ৰৱেশ কৰাম।');
INSERT INTO "habibur_rahman" VALUES(74,27,'74:27','আৰু কিহে তোমাক জনাব ছাক্বাৰ কি?');
INSERT INTO "habibur_rahman" VALUES(74,28,'74:28','ই একো অৱশিষ্ট নাৰাখিব আৰু এৰিও নিদিব।');
INSERT INTO "habibur_rahman" VALUES(74,29,'74:29','ই শৰীৰৰ ছাল জ্বলাই ক''লা কৰি দিব।');
INSERT INTO "habibur_rahman" VALUES(74,30,'74:30','ছাক্বাৰৰ তত্ত্বাৱধানত আছে ঊনৈশজন (প্ৰহৰী)।');
INSERT INTO "habibur_rahman" VALUES(74,31,'74:31','আৰু আমি কেৱল ফিৰিস্তাসকলকেই জাহান্নামৰ তত্ত্বাৱধায়ক বনাইছো; আৰু কাফিৰসকলৰ পৰীক্ষাস্বৰূপ আমি সিহঁতৰ সংখ্যা নিৰ্ধাৰণ কৰিছো যাতে কিতাপপ্ৰাপ্তসকলে দৃঢ় বিশ্বাস কৰে, আৰু মুমিনসকলৰ ঈমান বৃদ্ধি পায় আৰু যাতে আৰু মুমিনসকলে সন্দেহ পোষণ নকৰে; আৰু যাতে ইয়াৰ ফলত যিহঁতৰ অন্তৰত ব্যাধি আছে সিহঁতে আৰু কাফিৰসকলে কয়, আল্লাহে এই (সংখ্যাৰ) উদাহৰণ দি কি বুজাব বিচাৰিছে? এনেকৈ আল্লাহে যাক ইচ্ছা পথভ্ৰষ্ট কৰে আৰু যাক ইচ্ছা তাকেই হিদায়ত প্ৰদান কৰে; আৰু তোমাৰ প্ৰতিপালকৰ সৈন্যবাহিনী সম্পৰ্কে তেওঁৰ বাহিৰে আন কোনোৱে নাজানে; আৰু জাহান্নামৰ এই উদাহৰণ মানুহৰ বাবে এটা উপদেশ মাত্ৰ।');
INSERT INTO "habibur_rahman" VALUES(74,32,'74:32','কেতিয়াও নহয়, চন্দ্ৰৰ শপত,');
INSERT INTO "habibur_rahman" VALUES(74,33,'74:33','ৰাতিৰ শপত, যেতিয়া ইয়াৰ অৱসান ঘটে,');
INSERT INTO "habibur_rahman" VALUES(74,34,'74:34','প্ৰভাতৰ শপত, যেতিয়া ই আলোকোজ্জ্বল হয়-');
INSERT INTO "habibur_rahman" VALUES(74,35,'74:35','নিশ্চয় জাহান্নাম মহাবিপদসমূহৰ অন্যতম,');
INSERT INTO "habibur_rahman" VALUES(74,36,'74:36','মানুহৰ বাবে সতৰ্ককাৰীস্বৰূপ-');
INSERT INTO "habibur_rahman" VALUES(74,37,'74:37','তোমালোকৰ মাজত যিয়ে অগ্ৰসৰ হ’ব বিচাৰে অথবা পিছমুৱা হ’ব বিচাৰে তাৰ বাবে।');
INSERT INTO "habibur_rahman" VALUES(74,38,'74:38','প্ৰত্যেক ব্যক্তি নিজ কৃতকৰ্মৰ বাবে দায়বদ্ধ,');
INSERT INTO "habibur_rahman" VALUES(74,39,'74:39','কিন্তু সোঁপন্থী লোকসকলৰ বাহিৰে,');
INSERT INTO "habibur_rahman" VALUES(74,40,'74:40','জান্নাতত তেওঁলোকে ইজনে সিজনক সুধিব,');
INSERT INTO "habibur_rahman" VALUES(74,41,'74:41','অপৰাধীবিলাকৰ বিষয়ে,');
INSERT INTO "habibur_rahman" VALUES(74,42,'74:42','কিহে তোমালোকক ছাক্বাৰত (জাহান্নামত) নিক্ষেপ কৰিছে?');
INSERT INTO "habibur_rahman" VALUES(74,43,'74:43','সিহঁতে ক’ব, ‘আমি ছালাত আদায়কাৰীসকলৰ অন্তৰ্ভুক্ত নাছিলো,');
INSERT INTO "habibur_rahman" VALUES(74,44,'74:44','‘আৰু আমি দুখীয়াক খাদ্য দান কৰা নাছিলো,');
INSERT INTO "habibur_rahman" VALUES(74,45,'74:45','আৰু আমি অনৰ্থক আলোচনাকাৰীসকলৰ লগত বেহুদা আলচ কৰাত মগ্ন আছিলো।');
INSERT INTO "habibur_rahman" VALUES(74,46,'74:46','আৰু আমি প্ৰতিদান দিৱসক অস্বীকাৰ কৰিছিলো,');
INSERT INTO "habibur_rahman" VALUES(74,47,'74:47','অৱশেষত আমাৰ ওচৰত মৃত্যু আহি পালে’।');
INSERT INTO "habibur_rahman" VALUES(74,48,'74:48','এতেকে ছুপাৰিছকাৰীসকলৰ ছুপাৰিছে সিহঁতৰ কোনো উপকাৰ নকৰিব।');
INSERT INTO "habibur_rahman" VALUES(74,49,'74:49','এতেকে সিহঁতৰ কি হৈছে, কিয় সিহঁতে উপদেশ বাণীৰ পৰা বিমুখ হৈছে?');
INSERT INTO "habibur_rahman" VALUES(74,50,'74:50','সিহঁতে যেন ভীত-সন্ত্ৰস্ত হৈ পলায়নৰত বনৰীয়া গাধ।');
INSERT INTO "habibur_rahman" VALUES(74,51,'74:51','যিবোৰে সিংহৰ ভয়ত পলায়ন কৰিছে।');
INSERT INTO "habibur_rahman" VALUES(74,52,'74:52','বৰং সিহঁতৰ মাজৰ প্ৰত্যেক ব্যক্তিয়েই কামনা কৰে যে তাক উন্মুক্ত গ্ৰন্থ প্ৰদান কৰা হওক।');
INSERT INTO "habibur_rahman" VALUES(74,53,'74:53','কেতিয়াও নহয়, বৰং সিহঁতে আখিৰাতক ভয় নকৰে।');
INSERT INTO "habibur_rahman" VALUES(74,54,'74:54','কেতিয়াও নহয়, নিশ্চয় এই কোৰআন সকলোৰে বাবে উপদেশবাণী।');
INSERT INTO "habibur_rahman" VALUES(74,55,'74:55','গতিকে যিয়ে ইচ্ছা কৰিব সিয়েই ইয়াক স্মৰণ ৰাখিব,');
INSERT INTO "habibur_rahman" VALUES(74,56,'74:56','আৰু আল্লাহৰ ইচ্ছাবিহীন কোনোৱেই উপদেশ গ্ৰহণ কৰিব নোৱাৰে তেৱেঁই ভয়ৰ যোগ্য, কেৱল তেওঁৰেই তাক্বৱা অৱলম্বন কৰা, আৰু তেৱেঁই ক্ষমা কৰাৰ অধিকাৰী।');
INSERT INTO "habibur_rahman" VALUES(75,1,'75:1','মই শপত কৰিছো ক্বিয়ামতৰ দিনৰ,');
INSERT INTO "habibur_rahman" VALUES(75,2,'75:2','মই আৰু শপত কৰিছো আত্মনিন্দাকাৰী আত্মাৰ,');
INSERT INTO "habibur_rahman" VALUES(75,3,'75:3','মানুহে ভাৱে নেকি যে, আমি কেতিয়াও তাৰ অস্থিসমূহ একত্ৰিত কৰিব নোৱাৰিম?');
INSERT INTO "habibur_rahman" VALUES(75,4,'75:4','কিয় নোৱাৰিম, আমি তাৰ আঙুলিৰ অগ্ৰভাগসমূহকো (পাববোৰকো) পুনৰ্বিন্যস্ত কৰিবলৈ সক্ষম।');
INSERT INTO "habibur_rahman" VALUES(75,5,'75:5','বৰং মানুহে তাৰ ভৱিষ্যতেও পাপকৰ্ম কৰিবলৈ বিচাৰে।');
INSERT INTO "habibur_rahman" VALUES(75,6,'75:6','সি প্ৰশ্ন কৰে, ক্বিয়ামতৰ দিনটো কেতিয়া আহিব?');
INSERT INTO "habibur_rahman" VALUES(75,7,'75:7','যেতিয়া চকু স্থিৰ হৈ যাব,');
INSERT INTO "habibur_rahman" VALUES(75,8,'75:8','আৰু চন্দ্ৰ কিৰণবিহীন হৈ পৰিব,');
INSERT INTO "habibur_rahman" VALUES(75,9,'75:9','আৰু যেতিয়া সূৰ্য আৰু চন্দ্ৰক একত্ৰিত কৰা হ’ব-');
INSERT INTO "habibur_rahman" VALUES(75,10,'75:10','সেইদিনা মানুহে ক’ব, ‘আজি পলাবলৈ স্থান ক’ত’?');
INSERT INTO "habibur_rahman" VALUES(75,11,'75:11','কেতিয়াও নহয়, কোনো আশ্ৰয়স্থল নাই।');
INSERT INTO "habibur_rahman" VALUES(75,12,'75:12','সেইদিনা ঠাই হ’ব কেৱল তোমাৰ প্ৰতিপালকৰ ওচৰত।');
INSERT INTO "habibur_rahman" VALUES(75,13,'75:13','সেইদিনা মানুহক অৱহিত কৰোৱা হ’ব সি আগলৈ কি পঠিয়াইছিল আৰু পিছত কি থৈ আহিছে।');
INSERT INTO "habibur_rahman" VALUES(75,14,'75:14','বৰং মানুহে নিজেই নিজৰ বিষয়ে সম্যক অৱগত,');
INSERT INTO "habibur_rahman" VALUES(75,15,'75:15','যদিও সি নানা অজুহাত দেখুৱায়।');
INSERT INTO "habibur_rahman" VALUES(75,16,'75:16','ততাতৈয়াকৈ অহী আয়ত্ত্ব কৰাৰ বাবে তুমি তোমাৰ জিভাক দ্ৰুত সঞ্চালন নকৰিবা।');
INSERT INTO "habibur_rahman" VALUES(75,17,'75:17','নিশ্চয় ইয়াৰ সংৰক্ষণ আৰু পাঠ কৰোৱাৰ দায়িত্ব আমাৰেই।');
INSERT INTO "habibur_rahman" VALUES(75,18,'75:18','এতেকে আমি যেতিয়া ইয়াক পাঠ কৰো তুমি তেতিয়া সেই পাঠৰ অনুসৰণ কৰিবা,');
INSERT INTO "habibur_rahman" VALUES(75,19,'75:19','তাৰ পিছত ইয়াৰ বৰ্ণনাৰ দায়িত্ব নিশ্চিতভাৱে আমাৰেই।');
INSERT INTO "habibur_rahman" VALUES(75,20,'75:20','কেতিয়াও নহয়, বৰং তোমালোকে পৃথিৱীৰ জীৱনক ভাল পোৱা;');
INSERT INTO "habibur_rahman" VALUES(75,21,'75:21','আৰু তোমালোকে আখিৰাতক অৱহেলা কৰা।');
INSERT INTO "habibur_rahman" VALUES(75,22,'75:22','সেইদিনা কিছুমান মুখমণ্ডল উজ্জ্বল হ’ব,');
INSERT INTO "habibur_rahman" VALUES(75,23,'75:23','সিহঁত নিজ প্ৰতিপালকৰ প্ৰতি চাই থাকিব।');
INSERT INTO "habibur_rahman" VALUES(75,24,'75:24','আৰু সেইদিনা কিছুমান মুখমণ্ডল বিবৰ্ণ হৈ পৰিব,');
INSERT INTO "habibur_rahman" VALUES(75,25,'75:25','সিহঁতে ধাৰণা কৰিব যে, এটা ধ্বংসকাৰী বিপৰ্যয় সিহঁতৰ ওপৰত আপতিত হ’ব।');
INSERT INTO "habibur_rahman" VALUES(75,26,'75:26','নিশ্চয়, যেতিয়া প্ৰাণ কন্ঠাগত হ’ব,');
INSERT INTO "habibur_rahman" VALUES(75,27,'75:27','আৰু কোৱা হ’ব, ‘কোনে তাক ৰক্ষা কৰিব’?');
INSERT INTO "habibur_rahman" VALUES(75,28,'75:28','তেতিয়া তাৰ প্ৰত্যেয় হ’ব যে, এইটোৱে বিদায়ক্ষণ।');
INSERT INTO "habibur_rahman" VALUES(75,29,'75:29','আৰু ভৰিৰ লগত ভৰি জোঁট খাই যাব।');
INSERT INTO "habibur_rahman" VALUES(75,30,'75:30','সেইদিনা তোমাৰ প্ৰতিপালকৰ ওচৰতেই সকলোকে খেদি নিয়া হ’ব।');
INSERT INTO "habibur_rahman" VALUES(75,31,'75:31','এতেকে সি বিশ্বাস কৰা নাই আৰু ছালাতো আদায় কৰা নাই।');
INSERT INTO "habibur_rahman" VALUES(75,32,'75:32','বৰং সি সত্য প্ৰত্যাখ্যান কৰিছিল আৰু মুখ ঘূৰাই লৈছিল।');
INSERT INTO "habibur_rahman" VALUES(75,33,'75:33','তাৰ পিছত সি অহংকাৰ কৰি নিজ পৰিয়ালৰ ওচৰলৈ উভতি গৈছিল,');
INSERT INTO "habibur_rahman" VALUES(75,34,'75:34','দুৰ্ভোগ তোমাৰ বাবে আৰু দুৰ্ভোগ!');
INSERT INTO "habibur_rahman" VALUES(75,35,'75:35','আকৌ দুৰ্ভোগ তোমাৰ বাবে আৰু দুৰ্ভোগ!');
INSERT INTO "habibur_rahman" VALUES(75,36,'75:36','মানুহে ভাৱে নেকি যে, তাক এনেই এৰি দিয়া হ’ব?');
INSERT INTO "habibur_rahman" VALUES(75,37,'75:37','সি বীৰ্যৰ শুক্ৰবিন্দু নাছিলনে, যিটো স্খলিত হয়?');
INSERT INTO "habibur_rahman" VALUES(75,38,'75:38','তাৰ পিছত সেইটো ‘আলাক্বাত’ পৰিণত হয়। তাৰ পিছত আল্লাহে তাক সৃষ্টি কৰে আৰু সুঠাম কৰে।');
INSERT INTO "habibur_rahman" VALUES(75,39,'75:39','তাৰ পিছত তেওঁ তাৰ পৰা যোৰ যোৰ হিচাপে নৰ-নাৰী সৃষ্টি কৰে।');
INSERT INTO "habibur_rahman" VALUES(75,40,'75:40','তথাপিও সেই স্ৰষ্টা মৃতক পুনৰ্জীৱিত কৰিবলৈ সক্ষম নহয়নে বাৰু?');
INSERT INTO "habibur_rahman" VALUES(76,1,'76:1','কালপ্ৰবাহত মানুহৰ ওপৰত এনে এটা সময় অহা নাইনে যেতিয়া সি উল্লেখযোগ্য একোৱেই নাছিল?');
INSERT INTO "habibur_rahman" VALUES(76,2,'76:2','আমি মানুহক সৃষ্টি কৰিছো মিশ্ৰ শুক্ৰবিন্দুৰ পৰা, আমি তাক পৰীক্ষা কৰিম, সেয়ে আমি তাক শ্ৰৱণশক্তি আৰু দৃষ্টিশক্তি সম্পন্ন বনাইছো।');
INSERT INTO "habibur_rahman" VALUES(76,3,'76:3','নিশ্চয় আমি তাক পথ নিৰ্দেশ দিছো, হয় সি কৃতজ্ঞ হ’ব, নহয় সি অকৃতজ্ঞ হ’ব।');
INSERT INTO "habibur_rahman" VALUES(76,4,'76:4','নিশ্চয় আমি কাফিৰসকলৰ বাবে প্ৰস্তুত কৰি ৰাখিছো শিকলি, গলবেৰি আৰু লেলিহান জুই।');
INSERT INTO "habibur_rahman" VALUES(76,5,'76:5','নিশ্চয় সৎকৰ্মশীলসকলে পান কৰিব এনে পূৰ্ণপাত্ৰৰ পৰা যাৰ মিশ্ৰন হ’ব কাফূৰৰ লগত।');
INSERT INTO "habibur_rahman" VALUES(76,6,'76:6','এনেকুৱা এটা নিজৰা য’ৰ পৰা আল্লাহৰ বান্দাসকলে পান কৰিব, সিহঁতে ইয়াক নিজ ইচ্ছামতে প্ৰবাহিত কৰিব।');
INSERT INTO "habibur_rahman" VALUES(76,7,'76:7','(কাৰণ) সিহঁতে মান্নত পূৰণ কৰে, আৰু সেই দিনটোক ভয় কৰে যি দিনটোৰ অকল্যাণ হ’ব সুবিস্তৃত।');
INSERT INTO "habibur_rahman" VALUES(76,8,'76:8','তেওঁলোকে খাদ্যৰ প্ৰতি আসক্তি থকা সত্ত্বেও অভাৱগ্ৰস্ত, ইয়াতীম আৰু বন্দীক খাদ্য দান কৰে।');
INSERT INTO "habibur_rahman" VALUES(76,9,'76:9','সিহঁতে কয়, ‘আমি কেৱল আল্লাহৰ সন্তুষ্টিৰ উদ্দেশ্যে তোমালোকক খাদ্য দান কৰো, আমি তোমালোকৰ ওচৰত প্ৰতিদান নিবিচাৰো আৰু কৃতজ্ঞতাও নিবিচাৰো।');
INSERT INTO "habibur_rahman" VALUES(76,10,'76:10','নিশ্চয় আমি আমাৰ প্ৰতিপালকৰ তৰফৰ পৰা এটা ভয়ংকৰ ভীতিপ্ৰদ দিৱসৰ ভয় পোষণ কৰো’।');
INSERT INTO "habibur_rahman" VALUES(76,11,'76:11','ফলত আল্লাহে সিহঁতক ৰক্ষা কৰিব সেই দিনৰ অনিষ্টৰ পৰা আৰু সিহঁতক প্ৰদান কৰিব উজ্জ্বলতা আৰু উৎফুল্লতা।');
INSERT INTO "habibur_rahman" VALUES(76,12,'76:12','আৰু তেওঁলোকৰ ধৈৰ্যৰ পুৰষ্কাৰস্বৰূপে তেওঁ তেওঁলোকক প্ৰদান কৰিব উদ্যান আৰু ৰেচমী বস্ত্ৰ।');
INSERT INTO "habibur_rahman" VALUES(76,13,'76:13','তাত তেওঁলোকে সুসজ্জিত আসনত আউজি বহি থাকিব, তাত তেওঁলোকে প্ৰখৰ গৰম আৰু অত্যাধিক শীতো অনুভৱ নকৰিব।');
INSERT INTO "habibur_rahman" VALUES(76,14,'76:14','তেওঁলোকৰ ওপৰত সন্নিহিত থাকিব উদ্যানৰ ছাঁ আৰু ইয়াৰ ফলমূলৰ থোকবোৰ তেওঁলোকৰ সম্পূৰ্ণ আয়ত্তাধীন কৰা হ’ব।');
INSERT INTO "habibur_rahman" VALUES(76,15,'76:15','আৰু তেওঁলোকক ঘূৰি ঘূৰি পৰিবেশন কৰা হ’ব ৰূপৰ পাত্ৰত আৰু ফটিকৰ দৰে স্বচ্ছ পান পাত্ৰত-');
INSERT INTO "habibur_rahman" VALUES(76,16,'76:16','ৰূপৰ শুভ্ৰ ফটিক পাত্ৰ, সিহঁতে ইয়াৰ পৰিমাণ কৰিব সম্পূৰ্ণ পৰিমিতভাৱে।');
INSERT INTO "habibur_rahman" VALUES(76,17,'76:17','আৰু তাত তেওঁলোকক পান কৰোৱা হ’ব শুকান আদাৰ মিশ্ৰিত পূৰ্ণপাত্ৰ-পানীয়,');
INSERT INTO "habibur_rahman" VALUES(76,18,'76:18','জান্নাতৰ এনেকুৱা এটা নিজৰা যাৰ নাম হ’ব ছালছাবীল।');
INSERT INTO "habibur_rahman" VALUES(76,19,'76:19','আৰু তেওঁলোকৰ চাৰিওফালে প্ৰদক্ষিণ কৰিব চিৰ কিশোৰসকলে; যেতিয়া তুমি সিহঁতক দেখিবা তোমাৰ এনে লাগিব যেন সিহঁত বিক্ষিপ্ত মুকুতা।');
INSERT INTO "habibur_rahman" VALUES(76,20,'76:20','আৰু তুমি যেতিয়া তাত দেখিবা, দেখিবলৈ পাবা স্বাচ্ছন্দ্য আৰু বিৰাট সাম্ৰাজ্য।');
INSERT INTO "habibur_rahman" VALUES(76,21,'76:21','তেওঁলোকৰ আৱৰণ হ’ব সূক্ষ্ম সেউজীয়া ৰেচম আৰু মোটা ৰেচমী পোছাক আৰু তেওঁলোক অলংকৃত হ’ব ৰূপেৰে নিৰ্মিত গামখাৰুৰে, আৰু তেওঁলোকৰ প্ৰতিপালকে তেওঁলোকক পৱিত্ৰ পানীয় পান কৰাব।');
INSERT INTO "habibur_rahman" VALUES(76,22,'76:22','নিশ্চয় এইটোৱে তোমালোকৰ পুৰষ্কাৰ; আৰু তোমালোকৰ কৰ্মপ্ৰচেষ্টা আছিল প্ৰসংশাযোগ্য।');
INSERT INTO "habibur_rahman" VALUES(76,23,'76:23','নিশ্চয় আমি তোমাৰ প্ৰতি পৰ্যায়ক্ৰমে আল-কোৰআন অৱতীৰ্ণ কৰিছো।');
INSERT INTO "habibur_rahman" VALUES(76,24,'76:24','এতেকে তুমি ধৈৰ্যৰ সৈতে তোমাৰ প্ৰতিপালকৰ নিৰ্দেশৰ প্ৰতীক্ষা কৰা আৰু সিহঁতৰ মাজৰ পৰা কোনো পাপিষ্ঠ বা ঘোৰ অকৃতজ্ঞ কাফিৰৰ আনুগত্য নকৰিবা।');
INSERT INTO "habibur_rahman" VALUES(76,25,'76:25','আৰু পুৱা-গধূলি তোমাৰ প্ৰতিপালকৰ নাম স্মৰণ কৰা।');
INSERT INTO "habibur_rahman" VALUES(76,26,'76:26','আৰু ৰাতিৰ একাংশত তেওঁৰ প্ৰতি ছাজদাৱনত হোৱা আৰু ৰাতিৰ দীৰ্ঘ সময় তেওঁৰ পৱিত্ৰতা ঘোষণা আৰু তাছবীহ পাঠ কৰা।');
INSERT INTO "habibur_rahman" VALUES(76,27,'76:27','নিশ্চয় সিহঁত পৃথিৱীৰ জীৱনক ভাল পায় আৰু সিহঁতে সিহঁতৰ সন্মুখৰ কঠিন দিনটোক উপেক্ষা কৰে।');
INSERT INTO "habibur_rahman" VALUES(76,28,'76:28','আমিয়েই সিহঁতক সৃষ্টি কৰিছো আৰু সিহঁতৰ গঠন সুদৃঢ় কৰিছো। আৰু আমি যেতিয়া ইচ্ছা কৰিম তেতিয়াই সিহঁতৰ স্থানত সিহঁতৰ দৰে (কাৰোবাক) আনি পৰিবৰ্তন কৰি দিম।');
INSERT INTO "habibur_rahman" VALUES(76,29,'76:29','নিশ্চয় এইটো এটা উপদেশ; এতেকে যিয়ে ইচ্ছা কৰে সি যেন তাৰ প্ৰতিপালকৰ ফালে যোৱাৰ পথ বাছি লয়।');
INSERT INTO "habibur_rahman" VALUES(76,30,'76:30','আৰু আল্লাহে ইচ্ছা নকৰিলে তোমালোকে ইচ্ছা কৰিবলৈ সক্ষম নহ’বা; নিশ্চয় আল্লাহ মহাজ্ঞানী, প্ৰজ্ঞাময়।');
INSERT INTO "habibur_rahman" VALUES(76,31,'76:31','তেওঁ যাক ইচ্ছা তাকেই নিজ ৰহমতৰ অন্তৰ্ভুক্ত কৰে, আৰু যালিমসকলৰ বাবে তেওঁ প্ৰস্তুত কৰি ৰাখিছে যন্ত্ৰণাদায়ক শাস্তি।');
INSERT INTO "habibur_rahman" VALUES(77,1,'77:1','শপত কল্যাণস্বৰূপ প্ৰেৰিত বতাহৰ,');
INSERT INTO "habibur_rahman" VALUES(77,2,'77:2','তাৰ পিছত প্ৰচণ্ড বেগত প্ৰবাহিত ধুমুহাৰ,');
INSERT INTO "habibur_rahman" VALUES(77,3,'77:3','শপত প্ৰচণ্ড সঞ্চালনকাৰীৰ,');
INSERT INTO "habibur_rahman" VALUES(77,4,'77:4','তাৰ পিছত সুস্পষ্টৰূপে পাৰ্থক্যকাৰীৰ,');
INSERT INTO "habibur_rahman" VALUES(77,5,'77:5','তাৰ পিছত সিহঁতৰ, যিসকলে মানুহৰ অন্তৰত উপদেশ পৌঁচাই দিয়ে---');
INSERT INTO "habibur_rahman" VALUES(77,6,'77:6','ওজৰ-আপত্তি দূৰীকৰণ আৰু সতৰ্ক কৰাৰ বাবে,');
INSERT INTO "habibur_rahman" VALUES(77,7,'77:7','নিশ্চয় তোমালোকক যি প্ৰতিশ্ৰুতি দিয়া হৈছে সেয়া অৱশ্যে সংঘটিত হ’ব।');
INSERT INTO "habibur_rahman" VALUES(77,8,'77:8','যেতিয়া নক্ষত্ৰৰাজি আলোহীন হ’ব,');
INSERT INTO "habibur_rahman" VALUES(77,9,'77:9','আৰু যেতিয়া আকাশখন বিদীৰ্ণ কৰা হ’ব,');
INSERT INTO "habibur_rahman" VALUES(77,10,'77:10','আৰু যেতিয়া পৰ্বতসমূহক চূৰ্ণবিচূৰ্ণ কৰা হ’ব,');
INSERT INTO "habibur_rahman" VALUES(77,11,'77:11','আৰু যেতিয়া ৰাছুলসকলক নিৰ্ধাৰিত সময়ত উপস্থিত কৰা হ’ব,');
INSERT INTO "habibur_rahman" VALUES(77,12,'77:12','কোনটো দিনৰ বাবে এইবোৰ স্থগিত ৰখা হৈছে?');
INSERT INTO "habibur_rahman" VALUES(77,13,'77:13','বিচাৰ দিনৰ বাবে।');
INSERT INTO "habibur_rahman" VALUES(77,14,'77:14','আৰু কিহে তোমাক জনাব বিচাৰ দিৱস কি?');
INSERT INTO "habibur_rahman" VALUES(77,15,'77:15','সেইদিনা ধ্বংস অনিবাৰ্য অস্বীকাৰকাৰীসকলৰ বাবে,');
INSERT INTO "habibur_rahman" VALUES(77,16,'77:16','আমি পূৰ্বৱৰ্তীসকলক ধ্বংস কৰা নাইনে?');
INSERT INTO "habibur_rahman" VALUES(77,17,'77:17','তাৰ পিছত পৰবৰ্তীসকলকো সিহঁতৰ অনুগামী বনাম।');
INSERT INTO "habibur_rahman" VALUES(77,18,'77:18','অপৰাধীবিলাকৰ লগত আমি এনেকুৱা আচৰণেই কৰো।');
INSERT INTO "habibur_rahman" VALUES(77,19,'77:19','সেইদিনা ধ্বংস অনিবাৰ্য অস্বীকাৰকাৰীসকলৰ বাবে,');
INSERT INTO "habibur_rahman" VALUES(77,20,'77:20','আমি তোমালোকক ঘৃণনীয় পানীৰ পৰা সৃষ্টি কৰা নাইনে?');
INSERT INTO "habibur_rahman" VALUES(77,21,'77:21','তাৰ পিছত আমি ইয়াক নিৰাপদ স্থানত সুৰক্ষিত কৰি ৰাখিছো,');
INSERT INTO "habibur_rahman" VALUES(77,22,'77:22','এটা নিৰ্দিষ্ট সময় লৈকে,');
INSERT INTO "habibur_rahman" VALUES(77,23,'77:23','তাৰ পিছত আমি পৰিমাপ কৰিছো, এতেকে আমি কিমান নিপুণ পৰিমাপকাৰী!');
INSERT INTO "habibur_rahman" VALUES(77,24,'77:24','সেইদিনা ধ্বংস অনিবাৰ্য অস্বীকাৰকাৰীসকলৰ বাবে,');
INSERT INTO "habibur_rahman" VALUES(77,25,'77:25','আমি পৃথিৱীখনক ধাৰণকাৰীৰূপে সৃষ্টি কৰা নাইনে?');
INSERT INTO "habibur_rahman" VALUES(77,26,'77:26','জীৱিতসকল আৰু মৃতসকলৰ বাবে?');
INSERT INTO "habibur_rahman" VALUES(77,27,'77:27','আৰু আমি তাত স্থাপন কৰিছো সুদৃঢ় আৰু সুউচ্চ পৰ্বতসমূহ আৰু তোমালোকক পান কৰাইছো তৃ়ষ্ণা নিবাৰণকাৰী মিঠা উপযোগী পানী।');
INSERT INTO "habibur_rahman" VALUES(77,28,'77:28','সেইদিনা ধ্বংস অনিবাৰ্য অস্বীকাৰকাৰীসকলৰ বাবে,');
INSERT INTO "habibur_rahman" VALUES(77,29,'77:29','(সিহঁতক কোৱা হ’ব) তোমালোকে যাক অস্বীকাৰ কৰিছিলা, এতিয়া গতি কৰা তাৰফালে।');
INSERT INTO "habibur_rahman" VALUES(77,30,'77:30','যোৱা তিনিটা শাখা বিশিষ্ট জুইৰ ছাঁৰ পিনে,');
INSERT INTO "habibur_rahman" VALUES(77,31,'77:31','যিটো ছাঁ শীতল নহয় আৰু ই অগ্নিশিখাৰ পৰা ৰক্ষাও নকৰিব,');
INSERT INTO "habibur_rahman" VALUES(77,32,'77:32','নিশ্চয় জাহান্নামে উৎক্ষেপন কৰিব বৃহৎ অট্ৰালিকা সদৃশ ফিৰিঙতি,');
INSERT INTO "habibur_rahman" VALUES(77,33,'77:33','এনেকুৱা যেন হালধীয়া উটৰ শ্ৰেণী,');
INSERT INTO "habibur_rahman" VALUES(77,34,'77:34','সেইদিনা ধ্বংস অনিবাৰ্য অস্বীকাৰকাৰীসকলৰ বাবে,');
INSERT INTO "habibur_rahman" VALUES(77,35,'77:35','এইটো এনেকুৱা দিন যিদিনা সিহঁতে কথা ক’ব নোৱাৰিব,');
INSERT INTO "habibur_rahman" VALUES(77,36,'77:36','আৰু সিহঁতক অজুহাত পেচ কৰাৰ অনুমতিও দিয়া নহ’ব।');
INSERT INTO "habibur_rahman" VALUES(77,37,'77:37','সেইদিনা ধ্বংস অনিবাৰ্য অস্বীকাৰকাৰীসকলৰ বাবে,');
INSERT INTO "habibur_rahman" VALUES(77,38,'77:38','‘এইটো ফয়চালাৰ দিন, আমি একত্ৰিত কৰিছো তোমালোকক আৰু পূৰ্বৱৰ্তীসকলক’।');
INSERT INTO "habibur_rahman" VALUES(77,39,'77:39','এতেকে যদি তোমালোকৰ কোনো কৌশল আছে তেন্তে প্ৰয়োগ কৰা মোৰ বিৰুদ্ধে।');
INSERT INTO "habibur_rahman" VALUES(77,40,'77:40','সেইদিনা ধ্বংস অনিবাৰ্য অস্বীকাৰকাৰীসকলৰ বাবে,');
INSERT INTO "habibur_rahman" VALUES(77,41,'77:41','নিশ্চয় মুত্তাক্বীসকল থাকিব ছাঁৰ তলত আৰু নিজৰা বহুল স্থানত,');
INSERT INTO "habibur_rahman" VALUES(77,42,'77:42','আৰু তেওঁলোকৰ হেঁপাহ অনুযায়ী ফলমূলৰ মাজত।');
INSERT INTO "habibur_rahman" VALUES(77,43,'77:43','(তেওঁলোকক কোৱা হ''ব) তোমালোকে যি আমল কৰিছিলা তাৰ প্ৰতিদানস্বৰূপে তৃপ্তিৰ সৈতে পানাহাৰ কৰা;');
INSERT INTO "habibur_rahman" VALUES(77,44,'77:44','সৎকৰ্মশীলসকলক আমি এনেকুৱাই প্ৰতিদান দি থাকো।');
INSERT INTO "habibur_rahman" VALUES(77,45,'77:45','সেইদিনা ধ্বংস অনিবাৰ্য অস্বীকাৰকাৰীসকলৰ বাবে,');
INSERT INTO "habibur_rahman" VALUES(77,46,'77:46','(হে কাফিৰসকল!) তোমালোকে আহাৰ কৰা আৰু ভোগ কৰা খন্তেকৰ বাবে; নিশ্চয় তোমালোক অপৰাধী।');
INSERT INTO "habibur_rahman" VALUES(77,47,'77:47','সেইদিনা ধ্বংস অনিবাৰ্য অস্বীকাৰকাৰীসকলৰ বাবে,');
INSERT INTO "habibur_rahman" VALUES(77,48,'77:48','যেতিয়া সিহঁতক কোৱা হয়, ‘ৰুকু’ কৰা, (তেতিয়া) সিহঁতে ৰুকু’ নকৰে’।');
INSERT INTO "habibur_rahman" VALUES(77,49,'77:49','সেইদিনা ধ্বংস অনিবাৰ্য অস্বীকাৰকাৰীসকলৰ বাবে,');
INSERT INTO "habibur_rahman" VALUES(77,50,'77:50','এতেকে কোৰআনৰ পৰিবৰ্তে সিহঁতে আৰু কোন বাণীৰ প্ৰতি ঈমান আনিব?');
INSERT INTO "habibur_rahman" VALUES(78,1,'78:1','সিহঁতে কি বিষয়ে পৰস্পৰে সোধ-পোচ কৰি আছে?');
INSERT INTO "habibur_rahman" VALUES(78,2,'78:2','মহা সংবাদটোৰ বিষয়ে,');
INSERT INTO "habibur_rahman" VALUES(78,3,'78:3','যি বিষয়ে সিহঁতে মতানৈক্য কৰিছে।');
INSERT INTO "habibur_rahman" VALUES(78,4,'78:4','কেতিয়াও নহয়, সিহঁতে অতিশীঘ্ৰেই জানিবলৈ পাব।');
INSERT INTO "habibur_rahman" VALUES(78,5,'78:5','তাৰপিছত, কেতিয়াও নহয়, সিহঁতে অতিশীঘ্ৰেই জানিবলৈ পাব।');
INSERT INTO "habibur_rahman" VALUES(78,6,'78:6','আমি পৃথিৱীখনক শয্যা বনোৱা নাইনে?');
INSERT INTO "habibur_rahman" VALUES(78,7,'78:7','আৰু পৰ্বতসমূহক পেৰেক?');
INSERT INTO "habibur_rahman" VALUES(78,8,'78:8','আৰু আমি তোমালোকক সৃষ্টি কৰিছো যোৰা-যোৰা।');
INSERT INTO "habibur_rahman" VALUES(78,9,'78:9','আৰু তোমালোকৰ টোপনিক কৰিছো বিশ্ৰাম।');
INSERT INTO "habibur_rahman" VALUES(78,10,'78:10','আৰু ৰাতিক কৰি দিছো আৱৰণ।');
INSERT INTO "habibur_rahman" VALUES(78,11,'78:11','আৰু দিনক কৰি দিছো জীৱিকা উপাৰ্জনৰ সময়।');
INSERT INTO "habibur_rahman" VALUES(78,12,'78:12','আৰু আমি তোমালোকৰ ওপৰত নিৰ্মান কৰিছো সুদৃঢ় সাতখন আকাশ।');
INSERT INTO "habibur_rahman" VALUES(78,13,'78:13','আৰু আমি সৃষ্টি কৰিছো এটি উজ্জল প্ৰদীপ।');
INSERT INTO "habibur_rahman" VALUES(78,14,'78:14','আৰু আমি বৰ্ষণ কৰিছো মেঘমালাৰ পৰা প্ৰচুৰ পানী।');
INSERT INTO "habibur_rahman" VALUES(78,15,'78:15','যাতে তাৰ দ্বাৰা আমি উৎপন্ন কৰিব পাৰো শস্য আৰু উদ্ভিদ।');
INSERT INTO "habibur_rahman" VALUES(78,16,'78:16','আৰু ঘন সন্নিবিষ্ট উদ্যান।');
INSERT INTO "habibur_rahman" VALUES(78,17,'78:17','নিশ্চয় নিৰ্ধাৰিত আছে বিচাৰৰ দিন।');
INSERT INTO "habibur_rahman" VALUES(78,18,'78:18','সেই দিনা শিঙাত ফুঁ দিয়া হ’ব তেতিয়া তোমালোকে দলে দলে আহিবা।');
INSERT INTO "habibur_rahman" VALUES(78,19,'78:19','আৰু আকাশখন খুলি দিয়া হ’ব, ফলত ই হ’ব বহু দুৱাৰবিশিষ্ট।');
INSERT INTO "habibur_rahman" VALUES(78,20,'78:20','আৰু পৰ্বতসমূহক চালিত কৰা হ’ব, ফলত সেইবোৰ হৈ যাব বালিময় মৰীচিকাৰ দৰে।');
INSERT INTO "habibur_rahman" VALUES(78,21,'78:21','নিশ্চয় জাহান্নাম খাপ পাতি ৰৈ আছে।');
INSERT INTO "habibur_rahman" VALUES(78,22,'78:22','সীমালঙ্ঘনকাৰী সকলৰ বাবে প্ৰত্যাৱৰ্তনস্থল।');
INSERT INTO "habibur_rahman" VALUES(78,23,'78:23','তাত সিহঁতে যুগ যুগ ধৰি অৱস্থান কৰিব।');
INSERT INTO "habibur_rahman" VALUES(78,24,'78:24','তাত সিহঁতে শীতলতা বা উপযুক্ত পানীও ভোগ কৰিব নোৱাৰিব।');
INSERT INTO "habibur_rahman" VALUES(78,25,'78:25','উতলা পানী আৰু দুৰ্গন্ধময় পুঁজৰ বাহিৰে।');
INSERT INTO "habibur_rahman" VALUES(78,26,'78:26','এইটোৱেই উপযুক্ত প্ৰতিফল।');
INSERT INTO "habibur_rahman" VALUES(78,27,'78:27','নিশ্চয় সিহঁতে কেতিয়াও হিচাব-নিকাচৰ আশা কৰা নাছিল।');
INSERT INTO "habibur_rahman" VALUES(78,28,'78:28','আৰু সিহঁতে আমাৰ নিদৰ্শনাৱলীক কঠোৰভাৱে মিথ্যাৰোপ কৰিছিল।');
INSERT INTO "habibur_rahman" VALUES(78,29,'78:29','আৰু আমি প্ৰতিটো বস্তু লিখিতভাৱে সংৰক্ষণ কৰি ৰাখিছো।');
INSERT INTO "habibur_rahman" VALUES(78,30,'78:30','এতেকে ভোগ কৰা, আমি তোমালোকৰ বাবে শাস্তিৰ বাহিৰে আন একো বৃদ্ধি নকৰো।');
INSERT INTO "habibur_rahman" VALUES(78,31,'78:31','নিশ্চয় মুত্তাক্বীসকলৰ বাবে আছে সফলতা।');
INSERT INTO "habibur_rahman" VALUES(78,32,'78:32','উদ্যানসমূহ আৰু আঙ্গুৰসমূহ।');
INSERT INTO "habibur_rahman" VALUES(78,33,'78:33','আৰু সমবয়সীয়া উদ্ভিন্ন নৱ-যুৱতী তৰুণী।');
INSERT INTO "habibur_rahman" VALUES(78,34,'78:34','আৰু পৰিপূৰ্ণ পানপাত্ৰ।');
INSERT INTO "habibur_rahman" VALUES(78,35,'78:35','তাত তেওঁলোকে কোনো অনৰ্থক কথা আৰু মিছা বাক্যও শুনা নাপাব।');
INSERT INTO "habibur_rahman" VALUES(78,36,'78:36','আপোনাৰ প্ৰতিপালকৰ ফালৰ পৰা পুৰষ্কাৰ, যথোচিত দানস্বৰূপ।');
INSERT INTO "habibur_rahman" VALUES(78,37,'78:37','যিজন আকাশসমূহ আৰু পৃথিৱী আৰু এই দুটাৰ মাজত যি আছে সকলোৰে প্ৰতিপালক, পৰম কৰুণাময়, তেওঁৰ ওচৰত আবেদন-নিবেদনৰ শক্তি সিহঁতৰ কাৰো নাথাকিব।');
INSERT INTO "habibur_rahman" VALUES(78,38,'78:38','সেইদিনা ৰূহ আৰু ফিৰিস্তাসকল শাৰীবদ্ধভাৱে থিয় হ’ব; ৰহমানে যাক অনুমতি দিব তাৰ বাহিৰে কোনেও কথা নক’ব, আৰু তেওঁ সঠিক কথা ক’ব।');
INSERT INTO "habibur_rahman" VALUES(78,39,'78:39','এই দিনটো সত্য, এতেকে যাৰ ইচ্ছা সিয়ে তাৰ প্ৰতিপালকৰ ওচৰত আশ্ৰয় গ্ৰহণ কৰক।');
INSERT INTO "habibur_rahman" VALUES(78,40,'78:40','নিশ্চয় আমি তোমালোকক অতি নিকটৱৰ্তী শাস্তি সম্পৰ্কে সতৰ্ক কৰিলো। যিদিনা মানুহে নিজ কৃতকৰ্ম দেখিবলৈ পাব আৰু কাফিৰে ক’ব, হায়! মই যদি মাটি হ''লোহেঁতেন।');
INSERT INTO "habibur_rahman" VALUES(79,1,'79:1','শপত নিৰ্মমভাৱে উত্পটনকাৰীসকলৰ।');
INSERT INTO "habibur_rahman" VALUES(79,2,'79:2','আৰু মৃদুভাৱে বন্ধনমূক্তকাৰীসকলৰ।');
INSERT INTO "habibur_rahman" VALUES(79,3,'79:3','আৰু তীব্ৰগতিত সাঁতুৰি ফুৰাসকলৰ।');
INSERT INTO "habibur_rahman" VALUES(79,4,'79:4','আৰু দ্ৰুতগতিত আগবাঢ়ি যোৱাসকলৰ।');
INSERT INTO "habibur_rahman" VALUES(79,5,'79:5','তাৰ পিছত যাৱতীয় কাম নিৰ্বাহকাৰীসকলৰ।');
INSERT INTO "habibur_rahman" VALUES(79,6,'79:6','সেইদিনা কম্পনকাৰীয়ে প্ৰকম্পিত কৰিব।');
INSERT INTO "habibur_rahman" VALUES(79,7,'79:7','তাক অনুসৰণ কৰিব পৰবৰ্তী কম্পনকাৰী।');
INSERT INTO "habibur_rahman" VALUES(79,8,'79:8','সেইদিনা কিছুমান হৃদয়, ভয়ত সন্ত্ৰস্ত হ’ব।');
INSERT INTO "habibur_rahman" VALUES(79,9,'79:9','সিহঁতৰ দৃষ্টিসমূহ আতংকিত হৈ নত হ’ব।');
INSERT INTO "habibur_rahman" VALUES(79,10,'79:10','সিহঁতে কয়, ‘আমি আকৌ আগৰ অৱস্থাত উভতি যাম নে?');
INSERT INTO "habibur_rahman" VALUES(79,11,'79:11','যেতিয়া আমি চূৰ্ণবিচূৰ্ণ অস্থিত পৰিণত হম’?');
INSERT INTO "habibur_rahman" VALUES(79,12,'79:12','সিহঁতে কয়, ‘যদি এইটোৱে হয় তেন্তে এইটো এটা ক্ষতিকৰ প্ৰত্যাৱৰ্তন’।');
INSERT INTO "habibur_rahman" VALUES(79,13,'79:13','প্ৰকৃততে এইটো এটা বিৰাট শব্দ।');
INSERT INTO "habibur_rahman" VALUES(79,14,'79:14','তত্ক্ষণাত ভূ-পৃষ্ঠত সিহঁতৰ আবিৰ্ভাৱ হ’ব।');
INSERT INTO "habibur_rahman" VALUES(79,15,'79:15','মূছাৰ ঘটনাটো তুমি পাইছানে?');
INSERT INTO "habibur_rahman" VALUES(79,16,'79:16','যেতিয়া তেওঁৰ প্ৰতিপালকে তেওঁক পৱিত্ৰ তুৱা উপত্যকাত মাতি কৈছিল,');
INSERT INTO "habibur_rahman" VALUES(79,17,'79:17','‘ফিৰআউনৰ ওচৰলৈ যোৱা, নিশ্চয় সি সীমালঙ্ঘন কৰিছে,');
INSERT INTO "habibur_rahman" VALUES(79,18,'79:18','তাৰ পিছত তাক কোৱা ‘তুমি পৱিত্ৰ হ’ব বিচৰা নে?');
INSERT INTO "habibur_rahman" VALUES(79,19,'79:19','আৰু মই তোমাক তোমাৰ প্ৰতিপালকৰ পিনে পথপ্ৰদৰ্শন কৰো, যাতে তুমি তেওঁক ভয় কৰা’।');
INSERT INTO "habibur_rahman" VALUES(79,20,'79:20','তাৰ পিছত, তেওঁ তাক মহা নিদৰ্শন দেখুৱালে।');
INSERT INTO "habibur_rahman" VALUES(79,21,'79:21','কিন্তু সি অস্বীকাৰ কৰিলে আৰু অমান্য কৰিলে।');
INSERT INTO "habibur_rahman" VALUES(79,22,'79:22','তাৰ পিছত সি পিছফালে উভতি প্ৰতিবিধানত সচেষ্ট হ’ল।');
INSERT INTO "habibur_rahman" VALUES(79,23,'79:23','তাৰ পিছত সকলোকে একত্ৰিত কৰি ঘোষণা কৰিলে,');
INSERT INTO "habibur_rahman" VALUES(79,24,'79:24','আৰু ক’লে, ‘ময়ে তোমালোকৰ সৰ্বোচ্চ প্ৰতিপালক’।');
INSERT INTO "habibur_rahman" VALUES(79,25,'79:25','অৱশেষত আল্লাহে তাক আখিৰাত আৰু পৃথিৱীৰ দৃষ্টান্তমূলক শাস্তি প্ৰদান কৰিলে।');
INSERT INTO "habibur_rahman" VALUES(79,26,'79:26','নিশ্চয় যিয়ে ভয় কৰে তাৰ বাবে ইয়াত শিক্ষা আছে।');
INSERT INTO "habibur_rahman" VALUES(79,27,'79:27','তোমালোকক সৃষ্টি কৰাটো কঠিন নে আকাশ সৃষ্টি কৰা কঠিন? তেৱেঁই ইয়াক নিৰ্মান কৰিছে।');
INSERT INTO "habibur_rahman" VALUES(79,28,'79:28','তেৱেঁই ইয়াৰ চালখনক সুউচ্চ কৰিছে আৰু সুবিন্যস্ত কৰিছে।');
INSERT INTO "habibur_rahman" VALUES(79,29,'79:29','আৰু তেৱেঁই ইয়াৰ ৰাতিক কৰিছে অন্ধকাৰ আৰু প্ৰকাশ কৰিছে পূৰ্বাহ্নৰ পোহৰ।');
INSERT INTO "habibur_rahman" VALUES(79,30,'79:30','তাৰ পিছত, তেওঁ পৃথিৱীখনক বিস্তৃত কৰিছে।');
INSERT INTO "habibur_rahman" VALUES(79,31,'79:31','তেৱেঁই তাৰ পৰা উলিয়াইছে পানী আৰু তৃণভূমি,');
INSERT INTO "habibur_rahman" VALUES(79,32,'79:32','আৰু পৰ্বতসমূহক তেৱেঁই স্থাপন কৰিছে,');
INSERT INTO "habibur_rahman" VALUES(79,33,'79:33','এইবোৰ তোমালোকৰ আৰু তোমালোকৰ ঘৰচীয়া জন্তুবোৰৰ বাবে জীৱনোপকৰণ স্বৰূপ।');
INSERT INTO "habibur_rahman" VALUES(79,34,'79:34','আৰু যিদিনা মহাসংকট উপস্থিত হ’ব,');
INSERT INTO "habibur_rahman" VALUES(79,35,'79:35','সেইদিনা মানুহে স্মৰণ কৰিব যিটো সি (বিগত দিনত) কৰিছে,');
INSERT INTO "habibur_rahman" VALUES(79,36,'79:36','আৰু প্ৰত্যক্ষকাৰী সকলৰ বাবে জাহান্নাম প্ৰকাশ কৰা হ’ব দৃষ্টিমান।');
INSERT INTO "habibur_rahman" VALUES(79,37,'79:37','এতেকে যিয়ে সীমালঙ্ঘন কৰে,');
INSERT INTO "habibur_rahman" VALUES(79,38,'79:38','আৰু পৃথিৱীৰ জীৱনক অগ্ৰাধিকাৰ দিয়ে,');
INSERT INTO "habibur_rahman" VALUES(79,39,'79:39','নিশ্চয় জাহান্নামেই হ’ব তাৰ আবাসস্থল।');
INSERT INTO "habibur_rahman" VALUES(79,40,'79:40','আৰু যিয়ে তাৰ প্ৰতিপালকৰ সন্মুখত থিয় হ’বলৈ ভয় কৰে আৰু কুপ্ৰবৃত্তিৰ পৰা নিজকে বিৰত ৰাখে,');
INSERT INTO "habibur_rahman" VALUES(79,41,'79:41','নিশ্চয় জান্নাতেই হ’ব তেওঁৰ আবাসস্থল।');
INSERT INTO "habibur_rahman" VALUES(79,42,'79:42','সিহঁতে তোমাক ক্বিয়ামত সম্পৰ্কে প্ৰশ্ন কৰে যে, ই কেতিয়া সংঘটিত হ’ব?');
INSERT INTO "habibur_rahman" VALUES(79,43,'79:43','সেইটো আলোচনা কৰাৰ (প্ৰকৃত) জ্ঞান তোমাৰ আছেনে?');
INSERT INTO "habibur_rahman" VALUES(79,44,'79:44','ইয়াৰ প্ৰকৃত জ্ঞান (কেৱল) তোমাৰ প্ৰতিপালকৰ ওচৰত;');
INSERT INTO "habibur_rahman" VALUES(79,45,'79:45','যিয়ে ইয়াক ভয় কৰে তুমি কেৱল তাৰ বাবে সতৰ্ককাৰী।');
INSERT INTO "habibur_rahman" VALUES(79,46,'79:46','যিদিনা সিহঁতে ইয়াক দেখিব সেইদিনা সিহঁতৰ এইটো ধাৰণা হব যে, সিহঁতে পৃথিৱীত কেৱল এটা সন্ধিয়া অথবা এটা প্ৰভাততকৈ বেছি সময় অৱস্থান কৰা নাছিল।');
INSERT INTO "habibur_rahman" VALUES(80,1,'80:1','তেওঁ বিৰক্ত প্ৰকাশ কৰিলে আৰু মুখ ঘুৰাই ল’লে,');
INSERT INTO "habibur_rahman" VALUES(80,2,'80:2','তেওঁৰ ওচৰত অন্ধ ব্যক্তিজন অহাৰ কাৰণে।');
INSERT INTO "habibur_rahman" VALUES(80,3,'80:3','কিহে তোমাক জনাব যে, হয়তো সি পৰিশুদ্ধ হ’ব,');
INSERT INTO "habibur_rahman" VALUES(80,4,'80:4','অথবা উপদেশ গ্ৰহণ কৰিব আৰু সেই উপদেশ তাৰ বাবে লাভজনক হ’ব।');
INSERT INTO "habibur_rahman" VALUES(80,5,'80:5','আনহাতে যিয়ে নিজকে অমুখাপেক্ষী বুলি ভাৱে,');
INSERT INTO "habibur_rahman" VALUES(80,6,'80:6','তুমি তাৰ প্ৰতি মনোযোগ দিছা।');
INSERT INTO "habibur_rahman" VALUES(80,7,'80:7','অথচ সি পৰিশুদ্ধ নহ’লে তোমাৰ কোনো দায়িত্ব নাই,');
INSERT INTO "habibur_rahman" VALUES(80,8,'80:8','আনহাতে যিয়ে তোমাৰ ওচৰলৈ দৌৰি আহিলে,');
INSERT INTO "habibur_rahman" VALUES(80,9,'80:9','আৰু সি (আল্লাহক) ভয়ো কৰে,');
INSERT INTO "habibur_rahman" VALUES(80,10,'80:10','তাৰ প্ৰতি তুমি উদাসীন হ’লা।');
INSERT INTO "habibur_rahman" VALUES(80,11,'80:11','কেতিয়াও নহয়, নিশ্চয় এইটো উপদেশ বাণী,');
INSERT INTO "habibur_rahman" VALUES(80,12,'80:12','গতিকে যিয়ে ইচ্ছা কৰিব সিয়েই ইয়াক স্মৰণ ৰাখিব,');
INSERT INTO "habibur_rahman" VALUES(80,13,'80:13','এইটো মৰ্যদা সম্পন্ন ছহীফাত লিপিবদ্ধ।');
INSERT INTO "habibur_rahman" VALUES(80,14,'80:14','সমুন্নত, পৱিত্ৰ,');
INSERT INTO "habibur_rahman" VALUES(80,15,'80:15','লেখক বা দূতসকলৰ হাতত।');
INSERT INTO "habibur_rahman" VALUES(80,16,'80:16','(যিসকল) মহাসন্মানিত, পূণ্যবান।');
INSERT INTO "habibur_rahman" VALUES(80,17,'80:17','মানুহ ধ্বংস হওঁক, কিমান যে অকৃতজ্ঞ!');
INSERT INTO "habibur_rahman" VALUES(80,18,'80:18','তেওঁ তাক কি বস্তুৰ পৰা সৃষ্টি কৰিছে?');
INSERT INTO "habibur_rahman" VALUES(80,19,'80:19','শুক্ৰবিন্দুৰ পৰা, তেওঁ তাক সৃষ্টি কৰিছে, পিছত তাৰ পৰিমিত বিকাশ সাধন কৰিছে,');
INSERT INTO "habibur_rahman" VALUES(80,20,'80:20','তাৰ পিছত তাৰ বাবে পথ সহজ কৰি দিছে;');
INSERT INTO "habibur_rahman" VALUES(80,21,'80:21','তাৰ পিছত তাক মৃত্যু প্ৰদান কৰে আৰু কবৰস্থ কৰে।');
INSERT INTO "habibur_rahman" VALUES(80,22,'80:22','আকৌ যেতিয়া তেওঁ ইচ্ছা কৰিব তাক পুনৰ্জীৱিত কৰিব।');
INSERT INTO "habibur_rahman" VALUES(80,23,'80:23','কেতিয়াও নহয়, তেওঁ তাক যি আদেশ কৰিছিল সেয়া সি এতিয়াও পূৰ্ণ কৰা নাই।');
INSERT INTO "habibur_rahman" VALUES(80,24,'80:24','এতেকে মানুহে নিজ খাদ্যৰ প্ৰতি লক্ষ্য কৰা উচিত।');
INSERT INTO "habibur_rahman" VALUES(80,25,'80:25','নিশ্চয় আমি প্ৰচুৰ পৰিমাণে পানী বৰ্ষণ কৰোঁ,');
INSERT INTO "habibur_rahman" VALUES(80,26,'80:26','তাৰ পিছত আমি পৃথিৱীক যথাযথভাৱে বিদীৰ্ণ কৰোঁ;');
INSERT INTO "habibur_rahman" VALUES(80,27,'80:27','তাৰ পিছত আমি তাত উত্পন্ন কৰোঁ শস্য;');
INSERT INTO "habibur_rahman" VALUES(80,28,'80:28','আঙুৰ আৰু শাক-পাচলি,');
INSERT INTO "habibur_rahman" VALUES(80,29,'80:29','যায়তুন আৰু খেজুৰ গছ,');
INSERT INTO "habibur_rahman" VALUES(80,30,'80:30','ঘন গছ-গছনি বিশিষ্ট উদ্যান,');
INSERT INTO "habibur_rahman" VALUES(80,31,'80:31','ফল-মূল আৰু তৃণ-ঘাঁহ,');
INSERT INTO "habibur_rahman" VALUES(80,32,'80:32','এইবোৰ তোমালোকৰ আৰু তোমালোকৰ ঘৰচীয়া জন্তুৰ জীৱনোপকৰণৰ বাবে।');
INSERT INTO "habibur_rahman" VALUES(80,33,'80:33','এতেকে যেতিয়া তীক্ষ্ন (বিকট) শব্দ আহিব,');
INSERT INTO "habibur_rahman" VALUES(80,34,'80:34','সেইদিনা মানুহে পলায়ন কৰিব তাৰ ভাতৃৰ পৰা,');
INSERT INTO "habibur_rahman" VALUES(80,35,'80:35','আৰু তাৰ মাক-বাপেকৰ পৰা,');
INSERT INTO "habibur_rahman" VALUES(80,36,'80:36','তাৰ স্ত্ৰী আৰু সন্তানৰ পৰা,');
INSERT INTO "habibur_rahman" VALUES(80,37,'80:37','সেইদিনা সিহঁত প্ৰত্যকৰে হ’ব, এনে এক গুৰুতৰ অৱস্থা যিটো তাক সম্পূৰ্ণৰূপে ব্যস্ত ৰাখিব।');
INSERT INTO "habibur_rahman" VALUES(80,38,'80:38','সেইদিনা কিছুমান চেহেৰা হ’ব উজ্জ্বল,');
INSERT INTO "habibur_rahman" VALUES(80,39,'80:39','হাঁহিৰে ভৰা, আনন্দত উত্ফুল্লিত,');
INSERT INTO "habibur_rahman" VALUES(80,40,'80:40','আৰু সেইদিনা কিছুমান চেহেৰা ধূলিৰে মলিন হ’ব,');
INSERT INTO "habibur_rahman" VALUES(80,41,'80:41','ক’লা আৱৰণে আৱৰি ধৰিব।');
INSERT INTO "habibur_rahman" VALUES(80,42,'80:42','এওঁলোকেই কাফিৰ, অত্যন্ত পাপী।');
INSERT INTO "habibur_rahman" VALUES(81,1,'81:1','সূৰ্যক যেতিয়া নিষ্প্ৰভ কৰা হ’ব,');
INSERT INTO "habibur_rahman" VALUES(81,2,'81:2','আৰু নক্ষত্ৰৰাজি যেতিয়া খহি পৰিব,');
INSERT INTO "habibur_rahman" VALUES(81,3,'81:3','পৰ্বতসমূহক যেতিয়া সঞ্চালিত কৰা হ’ব,');
INSERT INTO "habibur_rahman" VALUES(81,4,'81:4','আৰু যেতিয়া পূৰ্ণ গাভিনী উটবোৰক অযত্নে এৰি দিয়া হ’ব,');
INSERT INTO "habibur_rahman" VALUES(81,5,'81:5','আৰু যেতিয়া বনৰীয়া পশুবোৰক একত্ৰিত কৰা হ’ব,');
INSERT INTO "habibur_rahman" VALUES(81,6,'81:6','আৰু যেতিয়া সাগৰসমূহক অগ্নিউত্তাল কৰা হ’ব,');
INSERT INTO "habibur_rahman" VALUES(81,7,'81:7','আৰু যেতিয়া আত্মাসমূহক (সমগোত্ৰীয়সকলৰ লগত) মিলিত কৰা হ’ব,');
INSERT INTO "habibur_rahman" VALUES(81,8,'81:8','আৰু যেতিয়া জীৱন্ত সমাধিস্থ কন্যাশিশুক সোধা হ’ব,');
INSERT INTO "habibur_rahman" VALUES(81,9,'81:9','কি অপৰাধত তাইক হত্যা কৰা হৈছিল?');
INSERT INTO "habibur_rahman" VALUES(81,10,'81:10','আৰু যেতিয়া আমলনামাসমূহ উন্মোচিত কৰা হ’ব,');
INSERT INTO "habibur_rahman" VALUES(81,11,'81:11','আৰু যেতিয়া আকাশৰ আৱৰণ অপসাৰিত কৰা হ’ব,');
INSERT INTO "habibur_rahman" VALUES(81,12,'81:12','আৰু যেতিয়া জাহান্নামক ভীষণভাৱে প্ৰজ্জ্বলিত কৰা হ’ব,');
INSERT INTO "habibur_rahman" VALUES(81,13,'81:13','আৰু জান্নাতক যেতিয়া নিকটবৰ্তী কৰা হ’ব,');
INSERT INTO "habibur_rahman" VALUES(81,14,'81:14','তেতিয়া প্ৰত্যেক ব্যক্তিয়েই জানিব সি কি উপস্থিত কৰিছে।');
INSERT INTO "habibur_rahman" VALUES(81,15,'81:15','এতেকে মই শপত কৰিছো আৱৰ্তনশীল নক্ষত্ৰবোৰৰ,');
INSERT INTO "habibur_rahman" VALUES(81,16,'81:16','যিবোৰ গতিশীল, অদৃশ্য,');
INSERT INTO "habibur_rahman" VALUES(81,17,'81:17','আৰু ৰাতিৰ শপত যেতিয়া ই বিদায় লয়,');
INSERT INTO "habibur_rahman" VALUES(81,18,'81:18','আৰু প্ৰভাতৰ শপত যেতিয়া ইয়াৰ আবিৰ্ভাৱ হয়,');
INSERT INTO "habibur_rahman" VALUES(81,19,'81:19','নিশ্চয় এই কোৰআন সন্মানিত ৰাছুলে লৈ অহা বাণী।');
INSERT INTO "habibur_rahman" VALUES(81,20,'81:20','যিজন শক্তিশালী, আৰশ্বৰ মালিকৰ ওচৰত মৰ্যদাসম্পন্ন,');
INSERT INTO "habibur_rahman" VALUES(81,21,'81:21','মান্যবৰ, তাত তেওঁ বিশ্বস্ত।');
INSERT INTO "habibur_rahman" VALUES(81,22,'81:22','আৰু তোমালোকৰ সঙ্গী উন্মাদ নহয়,');
INSERT INTO "habibur_rahman" VALUES(81,23,'81:23','আৰু তেখেতে (নবীয়ে) তেখেতক (জিব্ৰীলক) স্পষ্ট দিগন্তত দেখিছে,');
INSERT INTO "habibur_rahman" VALUES(81,24,'81:24','আৰু তেওঁ গায়েবী বিষয় সম্পৰ্কে কৃপণালিও নকৰে।');
INSERT INTO "habibur_rahman" VALUES(81,25,'81:25','আৰু এয়া কোনো অভিশপ্ত চয়তানৰো বাক্য নহয়।');
INSERT INTO "habibur_rahman" VALUES(81,26,'81:26','এতেকে তোমালোকে কোনফালে গতি কৰিছা?');
INSERT INTO "habibur_rahman" VALUES(81,27,'81:27','এইটো কেৱল সমগ্ৰ জগতবাসীৰ বাবে উপদেশ বাণী,');
INSERT INTO "habibur_rahman" VALUES(81,28,'81:28','তাৰ বাবে, তোমালোকৰ মাজৰ যিয়ে সৰল পথত চলিব বিচাৰে।');
INSERT INTO "habibur_rahman" VALUES(81,29,'81:29','তোমালোকে ইচ্ছা কৰিব নোৱাৰা, যেতিয়া লৈকে বিশ্বজগতৰ প্ৰতিপালক আল্লাহে ইচ্ছা নকৰে।');
INSERT INTO "habibur_rahman" VALUES(82,1,'82:1','যেতিয়া আকাশখন বিদীৰ্ণ হ’ব,');
INSERT INTO "habibur_rahman" VALUES(82,2,'82:2','আৰু যেতিয়া নক্ষত্ৰসমূহ বিক্ষিপ্তভাৱে খহি পৰিব,');
INSERT INTO "habibur_rahman" VALUES(82,3,'82:3','আৰু যেতিয়া সাগৰসমূহক বিস্ফোৰিত কৰা হ’ব,');
INSERT INTO "habibur_rahman" VALUES(82,4,'82:4','আৰু যেতিয়া কবৰসমূহক উন্মোচিত কৰা হ’ব,');
INSERT INTO "habibur_rahman" VALUES(82,5,'82:5','তেতিয়া প্ৰত্যেকেই জানিব, সি কি আগলৈ পঠিয়াইছে আৰু পিছত কি ৰাখি আহিছে।');
INSERT INTO "habibur_rahman" VALUES(82,6,'82:6','হে মানৱ! কিহে তোমাক তোমাৰ প্ৰতিপালক সম্পৰ্কে বিভ্ৰান্ত কৰিছে?');
INSERT INTO "habibur_rahman" VALUES(82,7,'82:7','যিজনে তোমাক সৃষ্টি কৰিছে, তাৰ পিছত তোমাক সুঠাম কৰিছে আৰু সুসামঞ্জস্য কৰিছে,');
INSERT INTO "habibur_rahman" VALUES(82,8,'82:8','আৰু যি আকৃতিত বিচাৰিছে, তেওঁ তোমাক (সেই আকৃতিতে) গঠন কৰিছে।');
INSERT INTO "habibur_rahman" VALUES(82,9,'82:9','কেতিয়াও নহয়, বৰং তোমালোকে প্ৰতিদান দিৱসক অস্বীকাৰ কৰা;');
INSERT INTO "habibur_rahman" VALUES(82,10,'82:10','আৰু নিশ্চয় তোমালোকৰ ওপৰত নিয়োজিত আছে সংৰক্ষকদল;');
INSERT INTO "habibur_rahman" VALUES(82,11,'82:11','সন্মানিত লেখক বৃন্দ;');
INSERT INTO "habibur_rahman" VALUES(82,12,'82:12','তোমালোকে যি কৰা সেয়া তেওঁলোকে জানে।');
INSERT INTO "habibur_rahman" VALUES(82,13,'82:13','নিশ্চয় সত্কৰ্মপৰায়ণসকল থাকিব পৰম সুখ-শান্তিত।');
INSERT INTO "habibur_rahman" VALUES(82,14,'82:14','আৰু নিশ্চয় পাপীসকল থাকিব জাহান্নামত।');
INSERT INTO "habibur_rahman" VALUES(82,15,'82:15','সিহঁতে প্ৰতিদান দিৱসত তাত প্ৰৱেশ কৰিব;');
INSERT INTO "habibur_rahman" VALUES(82,16,'82:16','আৰু সিহঁতে তাৰ পৰা অনুপস্থিত থাকিব নোৱাৰিব।');
INSERT INTO "habibur_rahman" VALUES(82,17,'82:17','আৰু তোমাক কিহে জনাব যে, প্ৰতিদান দিৱস কি?');
INSERT INTO "habibur_rahman" VALUES(82,18,'82:18','আকৌ কৈছো, কিহে তোমাক জনাব প্ৰতিদান দিৱস কি?');
INSERT INTO "habibur_rahman" VALUES(82,19,'82:19','সেইদিনা কোনেও কাৰো বাবে একো কৰাৰ ক্ষমতা নাৰাখিব; আৰু সেইদিনা সকলো বিষয়ৰ কৰ্তৃত্ব হ’ব আল্লাহৰ।');
INSERT INTO "habibur_rahman" VALUES(83,1,'83:1','ধ্বংস সিহঁতৰ বাবে যিহঁতে জোখ-মাখত কম দিয়ে,');
INSERT INTO "habibur_rahman" VALUES(83,2,'83:2','যিহঁতে আনৰ পৰা মাপি লওঁতে পূৰ্ণমাত্ৰাত গ্ৰহণ কৰে,');
INSERT INTO "habibur_rahman" VALUES(83,3,'83:3','আৰু যেতিয়া সিহঁতে মাপি দিয়ে অথবা ওজন কৰি দিয়ে, তেতিয়া কম দিয়ে।');
INSERT INTO "habibur_rahman" VALUES(83,4,'83:4','সিহঁতে বিশ্বাস নকৰে নেকি যে, সিহঁত পুৰনৰুত্থিত হ’ব?');
INSERT INTO "habibur_rahman" VALUES(83,5,'83:5','মহাদিৱসত।');
INSERT INTO "habibur_rahman" VALUES(83,6,'83:6','যিদিনা সকলো মানুহে বিশ্বজগতৰ প্ৰতিপালকৰ সন্মুখত থিয় হ’ব।');
INSERT INTO "habibur_rahman" VALUES(83,7,'83:7','কেতিয়াও নহয়, নিশ্চয় পাপীসকলৰ আমলনামা ছিজ্জিনত আছে।');
INSERT INTO "habibur_rahman" VALUES(83,8,'83:8','আৰু তোমাক কিহে জনাব যে, ছিজ্জীন কি?');
INSERT INTO "habibur_rahman" VALUES(83,9,'83:9','চিহ্নিত আমলনামা।');
INSERT INTO "habibur_rahman" VALUES(83,10,'83:10','সেইদিনা ধ্বংস অনিবাৰ্য অস্বীকাৰকাৰীসকলৰ বাবে,');
INSERT INTO "habibur_rahman" VALUES(83,11,'83:11','যিহঁতে প্ৰতিদান দিৱসক অস্বীকাৰ কৰে।');
INSERT INTO "habibur_rahman" VALUES(83,12,'83:12','আৰু কেৱল প্ৰত্যেক পাপিষ্ঠ সীমালঙ্ঘনকাৰীৰ বাহিৰে আন কোনেও ইয়াক অস্বীকাৰ নকৰে।');
INSERT INTO "habibur_rahman" VALUES(83,13,'83:13','আৰু যেতিয়া আমাৰ আয়াতসমূহ তাৰ ওচৰত তিলাৱত কৰা হয় তেতিয়া সি কয়, (এয়া হৈছে) ‘পুৰ্বৱৰ্তীসকলৰ উপকথা’।');
INSERT INTO "habibur_rahman" VALUES(83,14,'83:14','কেতিয়াও নহয়, সিহঁতৰ কৰ্মৰ ফলত সিহঁতৰ অন্তৰত মামৰ ধৰিছে।');
INSERT INTO "habibur_rahman" VALUES(83,15,'83:15','কেতিয়াও নহয়, নিশ্চয় সিহঁত সেইদিনা নিজ প্ৰতিপালকৰ পৰা পৰ্দাৰ আঁৰত থাকিব।');
INSERT INTO "habibur_rahman" VALUES(83,16,'83:16','তাৰ পিছত নিশ্চয় সিহঁতে প্ৰজ্জ্বলিত জাহান্নামত প্ৰৱেশ কৰিব;');
INSERT INTO "habibur_rahman" VALUES(83,17,'83:17','তাৰ পিছত কোৱা হ’ব, ‘এইটোৱে সেইটো যাক তোমালোকে অস্বীকাৰ কৰিছিলা’।');
INSERT INTO "habibur_rahman" VALUES(83,18,'83:18','কেতিয়াও নহয়, নিশ্চয় পূণ্যৱানসকলৰ আমলনামা ইল্লিয়্যীনত আছে।');
INSERT INTO "habibur_rahman" VALUES(83,19,'83:19','আৰু তোমাক কিহে জনাব যে, ইল্লিয়্যীন কি?');
INSERT INTO "habibur_rahman" VALUES(83,20,'83:20','চিহ্নিত আমলনামা।');
INSERT INTO "habibur_rahman" VALUES(83,21,'83:21','সান্নিধ্যপ্ৰাপ্তসকলেই ইয়াৰ অবলোকন কৰে।');
INSERT INTO "habibur_rahman" VALUES(83,22,'83:22','নিশ্চয় সত্কৰ্মপৰায়ণসকল থাকিব পৰম সুখ-শান্তিত।');
INSERT INTO "habibur_rahman" VALUES(83,23,'83:23','সুসজ্জিত আসনত বহি দৰ্শন কৰিব।');
INSERT INTO "habibur_rahman" VALUES(83,24,'83:24','তুমি সিহঁতৰ মুখমণ্ডলত সূখ-শান্তিৰ উজ্জলতা দেখিবলৈ পাবা,');
INSERT INTO "habibur_rahman" VALUES(83,25,'83:25','সিহঁতক মোহৰ মাৰি থোৱা বিশুদ্ধ পানীয় পান কৰোৱা হ’ব;');
INSERT INTO "habibur_rahman" VALUES(83,26,'83:26','আৰু যাৰ মোহৰ হ''ব মিছকৰ, সেয়ে এই বিষয়ে প্ৰতিযোগীসকলে প্ৰতিযোগীতা কৰা উচিত।');
INSERT INTO "habibur_rahman" VALUES(83,27,'83:27','আৰু ইয়াৰ মিশ্ৰণ হ’ব তাছনীমৰ দ্বাৰা,');
INSERT INTO "habibur_rahman" VALUES(83,28,'83:28','(সেইটো) এটা নিজৰা, যাৰ পৰা সান্নিধ্যসকলে পান কৰিব।');
INSERT INTO "habibur_rahman" VALUES(83,29,'83:29','নিশ্চয় যিহঁতে অপৰাধ কৰিছে সিহঁতে মুমিনসকলক উপহাস কৰিছিল,');
INSERT INTO "habibur_rahman" VALUES(83,30,'83:30','আৰু যেতিয়া সিহঁতে মুমিনসকলৰ ওচৰেদি গৈছিল তেতিয়া সিহঁতে চকু টিপি বিদ্ৰূপ কৰিছিল।');
INSERT INTO "habibur_rahman" VALUES(83,31,'83:31','আৰু যেতিয়া সিহঁতে আপোনজনৰ ওচৰলৈ উভতি আহিছিল তেতিয়া উত্ফল্লিত হৈ উভতিছিল,');
INSERT INTO "habibur_rahman" VALUES(83,32,'83:32','আৰু সিহঁতে যেতিয়া মুমিনসকলক দেখিছিল তেতিয়া কৈছিল, ‘নিশ্চয় ইহঁত পথভ্ৰষ্ট’।');
INSERT INTO "habibur_rahman" VALUES(83,33,'83:33','অথচ সিহঁতক মুমিনসকলৰ তত্ত্ববধায়ক হিচাপে পঠোৱা হোৱা নাই।');
INSERT INTO "habibur_rahman" VALUES(83,34,'83:34','এতেকে আজি মুমিনসকলে উপহাস কৰিব কাফিৰসকলক।');
INSERT INTO "habibur_rahman" VALUES(83,35,'83:35','সুসজ্জিত আসনত বহি দৰ্শন কৰিব।');
INSERT INTO "habibur_rahman" VALUES(83,36,'83:36','কাফিৰসকলে সিহঁতৰ কৃতকৰ্মৰ ফল পালেনে?');
INSERT INTO "habibur_rahman" VALUES(84,1,'84:1','যেতিয়া আকাশখন ভাঙি পৰিব,');
INSERT INTO "habibur_rahman" VALUES(84,2,'84:2','আৰু তাৰ প্ৰতিপালকৰ আদেশ পালন কৰিব আৰু এইটোৱে তাৰ কৰণীয়।');
INSERT INTO "habibur_rahman" VALUES(84,3,'84:3','আৰু যেতিয়া পৃথিৱীক সম্প্ৰসাৰিত কৰা হ’ব।');
INSERT INTO "habibur_rahman" VALUES(84,4,'84:4','আৰু পৃথিৱীয়ে তাৰ ভিতৰত থকা সকলোবোৰ বাহিৰলৈ নিক্ষেপ কৰিব আৰু ই গৰ্ভশূন্য হৈ পৰিব।');
INSERT INTO "habibur_rahman" VALUES(84,5,'84:5','আৰু তাৰ প্ৰতিপালকৰ আদেশ পালন কৰিব আৰু এইটোৱে তাৰ কৰণীয়।');
INSERT INTO "habibur_rahman" VALUES(84,6,'84:6','হে মানৱ! তুমি তোমাৰ প্ৰতিপালকৰ নৈকট্য পাবলৈ কঠোৰ পৰিশ্ৰম কৰিব লাগিব, তেতিয়াহে তেওঁৰ সাক্ষাত লাভ কৰিব পাৰিবা।');
INSERT INTO "habibur_rahman" VALUES(84,7,'84:7','এতেকে যাৰ আমলনামা তাৰ সোঁ হাতত দিয়া হব;');
INSERT INTO "habibur_rahman" VALUES(84,8,'84:8','অত্যন্ত সহজ ভাৱে তাৰ হিচাপ-নিকাচ লোৱা হ’ব।');
INSERT INTO "habibur_rahman" VALUES(84,9,'84:9','আৰু তেওঁ তাৰ আপোনজনৰ ওচৰলৈ প্ৰফুল্লচিত্তে উভতি যাব;');
INSERT INTO "habibur_rahman" VALUES(84,10,'84:10','আৰু যাৰ আমলনামা পিছফালৰ পৰা দিয়া হ’ব,');
INSERT INTO "habibur_rahman" VALUES(84,11,'84:11','সি নিজৰ ধ্বংস আহ্বান কৰিব;');
INSERT INTO "habibur_rahman" VALUES(84,12,'84:12','আৰু সি জ্বলন্ত জুইত প্ৰৱেশ কৰিব;');
INSERT INTO "habibur_rahman" VALUES(84,13,'84:13','নিশ্চয় সি নিজ পৰিয়ালৰ মাজত আনন্দত আছিল,');
INSERT INTO "habibur_rahman" VALUES(84,14,'84:14','নিশ্চয় সি ভাবিছিল যে, সি কেতিয়াও উভতি নাযাব;');
INSERT INTO "habibur_rahman" VALUES(84,15,'84:15','হয়, নিশ্চয় তাৰ প্ৰতিপালক তাৰ ওপৰত সম্যক দৃষ্টি দানকাৰী।');
INSERT INTO "habibur_rahman" VALUES(84,16,'84:16','এতেকে মই শপত কৰিছো পশ্চিম আকাশৰ ৰঙা কিৰণৰ,');
INSERT INTO "habibur_rahman" VALUES(84,17,'84:17','ৰাতিৰ শপত আৰু ই যিবোৰৰ সমাবেশ ঘটায়,');
INSERT INTO "habibur_rahman" VALUES(84,18,'84:18','আৰু চন্দ্ৰৰ শপত, যেতিয়া ই পূৰ্ণতা লাভ কৰে;');
INSERT INTO "habibur_rahman" VALUES(84,19,'84:19','অৱশ্যে তোমালোকে এটা স্তৰৰ পৰা আন এটা স্তৰলৈ আৰোহণ কৰিবা।');
INSERT INTO "habibur_rahman" VALUES(84,20,'84:20','কিন্তু সিহঁতৰ কি হ’ল, সিহঁতে ঈমান পোষণ নকৰে কিয়?');
INSERT INTO "habibur_rahman" VALUES(84,21,'84:21','আৰু যেতিয়া সিহঁতৰ ওচৰত কোৰআন তিলাৱাত কৰা হয় তেতিয়া সিহঁতে ছাজদা নকৰে কিয়?');
INSERT INTO "habibur_rahman" VALUES(84,22,'84:22','বৰং কাফিৰসকলে অস্বীকাৰ কৰে।');
INSERT INTO "habibur_rahman" VALUES(84,23,'84:23','আৰু সিহঁতে (অন্তৰত) যি পোষণ কৰে আল্লাহে সেই বিষয়ে সবিশেষ অৱগত।');
INSERT INTO "habibur_rahman" VALUES(84,24,'84:24','সেয়ে তুমি সিহঁতক যন্ত্ৰণাদায়ক শাস্তিৰ সংবাদ দিয়া;');
INSERT INTO "habibur_rahman" VALUES(84,25,'84:25','কিন্তু যিসকলে ঈমান আনিছে আৰু সত্কৰ্ম কৰিছে তেওঁলোকৰ বাবে আছে নিৰবিচ্ছিন্ন প্ৰতিদান।');
INSERT INTO "habibur_rahman" VALUES(85,1,'85:1','শপত বুৰূজবিশিষ্ট আকাশৰ,');
INSERT INTO "habibur_rahman" VALUES(85,2,'85:2','শপত প্ৰতিশ্ৰুতিকৃত দিনৰ,');
INSERT INTO "habibur_rahman" VALUES(85,3,'85:3','শপত দ্ৰষ্টা আৰু দৃষ্টৰ,');
INSERT INTO "habibur_rahman" VALUES(85,4,'85:4','অভিশপ্ত হৈছিল অগ্নিকুণ্ডৰ অধিপতিসকল,');
INSERT INTO "habibur_rahman" VALUES(85,5,'85:5','য’ত আছিল ইন্ধনপূৰ্ণ জুই।');
INSERT INTO "habibur_rahman" VALUES(85,6,'85:6','যেতিয়া সিহঁত ইয়াৰ কাষত উপবিষ্ট আছিল;');
INSERT INTO "habibur_rahman" VALUES(85,7,'85:7','আৰু সিহঁতে মুমিনসকলৰ লগত যি কৰিছিল সেয়া প্ৰত্যক্ষ কৰিছিল।');
INSERT INTO "habibur_rahman" VALUES(85,8,'85:8','আৰু সিহঁতে তেওঁলোকক নিৰ্যাতন কৰিছিল কেৱল এই কাৰণে যে, তেওঁলোকে ঈমান আনিছিল পৰাক্ৰমশালী আৰু প্ৰশংসাৰ যোগ্য আল্লাহৰ ওপৰত,');
INSERT INTO "habibur_rahman" VALUES(85,9,'85:9','যাৰ অধিকাৰত আছে আকাশ আৰু পৃথিৱীৰ সাৰ্বভৌম কৰ্তৃত্ব; আৰু আল্লাহ সকলো বস্তুৰ প্ৰত্যক্ষদৰ্শী।');
INSERT INTO "habibur_rahman" VALUES(85,10,'85:10','নিশ্চয় যিহঁতে মুমিন পুৰুষ আৰু মুমিনা নাৰীক বিপদাপন্ন কৰিছে তাৰ পিছত তাওবা কৰা নাই, সিহঁতৰ বাবে আছে জাহান্নামৰ শাস্তি, আৰু সিহঁতৰ বাবে আছে দহন যন্ত্ৰণা।');
INSERT INTO "habibur_rahman" VALUES(85,11,'85:11','নিশ্চয় যিসকলে ঈমান আনিছে আৰু সত্কৰ্ম কৰিছে তেওঁলোকৰ বাবে আছে জান্নাত, যাৰ তলত নদী প্ৰবাহিত; এইটোৱে মহাসাফল্য।');
INSERT INTO "habibur_rahman" VALUES(85,12,'85:12','নিশ্চয় তোমাৰ প্ৰতিপালকৰ গ্ৰেপ্তাৰী অতি কঠোৰ।');
INSERT INTO "habibur_rahman" VALUES(85,13,'85:13','তেৱেঁই অস্তিত্ব প্ৰদান কৰে আৰু তেৱেঁই পুনৰাৱৰ্তন ঘটায়,');
INSERT INTO "habibur_rahman" VALUES(85,14,'85:14','নিশ্চয় তেওঁ ক্ষমাশীল, অতিস্নেহশীল।');
INSERT INTO "habibur_rahman" VALUES(85,15,'85:15','আৰছৰ অধিপতি, সন্মানিত।');
INSERT INTO "habibur_rahman" VALUES(85,16,'85:16','তেওঁ যি বিচাৰে সেইটোৱে কৰে।');
INSERT INTO "habibur_rahman" VALUES(85,17,'85:17','তোমাৰ ওচৰত সৈন্যবাহিনীৰ বৃত্তান্ত আহি পাইছেনে?');
INSERT INTO "habibur_rahman" VALUES(85,18,'85:18','ফিৰআউনৰ আৰু ছামূদৰ।');
INSERT INTO "habibur_rahman" VALUES(85,19,'85:19','তথাপিও কাফিৰসকল মিথ্যাৰোপ কৰাত লিপ্ত;');
INSERT INTO "habibur_rahman" VALUES(85,20,'85:20','আৰু আল্লাহে সিহঁতক কেউফালৰ পৰা পৰিবেষ্টন কৰি আছে।');
INSERT INTO "habibur_rahman" VALUES(85,21,'85:21','প্ৰকৃততে ই সন্মানিত কোৰআন,');
INSERT INTO "habibur_rahman" VALUES(85,22,'85:22','সুৰক্ষিত ফলকত লিপিবদ্ধ।');
INSERT INTO "habibur_rahman" VALUES(86,1,'86:1','শপত আকাশৰ আৰু যি ৰাতিত আত্মপ্ৰকাশ কৰে;');
INSERT INTO "habibur_rahman" VALUES(86,2,'86:2','আৰু কিহে তোমাক জনাব যে, ৰাতিত যিয়ে আত্মপ্ৰকাশ কৰে, সেয়ানো কি?');
INSERT INTO "habibur_rahman" VALUES(86,3,'86:3','(সেয়া হৈছে) উজ্জ্বল নক্ষত্ৰ।');
INSERT INTO "habibur_rahman" VALUES(86,4,'86:4','প্ৰত্যেক জীৱৰ ওপৰতেই তত্ত্বাবধায়ক আছে।');
INSERT INTO "habibur_rahman" VALUES(86,5,'86:5','এতেকে মানুহে চিন্তা কৰা উচিত যে, তাক কিহেৰে সৃষ্টি কৰা হৈছে!');
INSERT INTO "habibur_rahman" VALUES(86,6,'86:6','তাক সৃষ্টি কৰা হৈছে দ্ৰুতগতিত নিৰ্গত হোৱা পানীৰে,');
INSERT INTO "habibur_rahman" VALUES(86,7,'86:7','যিটো নিৰ্গত হয় মেৰুদণ্ড আৰু বুকুৰ মাজৰ পৰা।');
INSERT INTO "habibur_rahman" VALUES(86,8,'86:8','নিশ্চয় তেওঁ তাক ওভোতাই আনিবলৈ সক্ষম।');
INSERT INTO "habibur_rahman" VALUES(86,9,'86:9','যিদিনা গোপন বিষয়সমূহ পৰীক্ষা কৰা হ’ব,');
INSERT INTO "habibur_rahman" VALUES(86,10,'86:10','সেইদিনা তাৰ কোনো শক্তি নাথাকিব আৰু কোনো সহায়কাৰীও নাথাকিব।');
INSERT INTO "habibur_rahman" VALUES(86,11,'86:11','শপত আকাশৰ, যিয়ে বৰষুণ ধাৰণ কৰে,');
INSERT INTO "habibur_rahman" VALUES(86,12,'86:12','শপত পৃথিৱীৰ, যিটো বিদীৰ্ণ হয়,');
INSERT INTO "habibur_rahman" VALUES(86,13,'86:13','নিশ্চয় এইটো (আল-কোৰআন) মীমাংসাকাৰী বাণী।');
INSERT INTO "habibur_rahman" VALUES(86,14,'86:14','আৰু এইটো নিৰৰ্থক নহয়।');
INSERT INTO "habibur_rahman" VALUES(86,15,'86:15','নিশ্চয় সিহঁতে ভীষণ ষড়যন্ত্ৰ কৰে,');
INSERT INTO "habibur_rahman" VALUES(86,16,'86:16','আৰু ময়ো ভীষণ কৌশল কৰিছো।');
INSERT INTO "habibur_rahman" VALUES(86,17,'86:17','এতেকে কাফিৰসকলক অৱকাশ দিয়া; সিহঁতক কিছু সময়ৰ বাবে অৱকাশ দিয়া।');
INSERT INTO "habibur_rahman" VALUES(87,1,'87:1','তুমি তোমাৰ সুমহান প্ৰতিপালকৰ নামৰ পৱিত্ৰতা আৰু মহিমা ঘোষণা কৰা,');
INSERT INTO "habibur_rahman" VALUES(87,2,'87:2','যিজনে সৃষ্টি কৰিছে, তাৰ পিছত সুঠাম কৰিছে।');
INSERT INTO "habibur_rahman" VALUES(87,3,'87:3','আৰু যিজনে (ভাগ্য) নিৰ্ধাৰণ কৰিছে তাৰ পিছত সত্পথ প্ৰদৰ্শন কৰিছে,');
INSERT INTO "habibur_rahman" VALUES(87,4,'87:4','আৰু যিজনে তৃণ-লতা উত্পন্ন কৰিছে,');
INSERT INTO "habibur_rahman" VALUES(87,5,'87:5','তাৰ পিছত সেইবোৰক ক''লা আবৰ্জনাত পৰিণত কৰিছে।');
INSERT INTO "habibur_rahman" VALUES(87,6,'87:6','অনতিপলমে আমি তোমাক পাঠ কৰাম, ফলত তুমি নাপাহৰিবা,');
INSERT INTO "habibur_rahman" VALUES(87,7,'87:7','কেৱল আল্লাহে যি বিচাৰে তাৰ বাহিৰে। নিশ্চয় তেওঁ প্ৰকাশ্য আৰু অপ্ৰকাশ্য সকলো জানে।');
INSERT INTO "habibur_rahman" VALUES(87,8,'87:8','আৰু আমি তোমাৰ বাবে সহজ পথ সুগম কৰি দিম।');
INSERT INTO "habibur_rahman" VALUES(87,9,'87:9','এতেকে উপদেশ দিয়া, যদি উপদেশ ফলপ্ৰসূ হয়;');
INSERT INTO "habibur_rahman" VALUES(87,10,'87:10','উপদেশ সিয়েই গ্ৰহণ কৰিব যিয়ে ভয় কৰে।');
INSERT INTO "habibur_rahman" VALUES(87,11,'87:11','আৰু যি হতভাগ্য সি ইয়াক অগ্ৰাহ্য কৰিব,');
INSERT INTO "habibur_rahman" VALUES(87,12,'87:12','সি ভয়াৱহ জুইত প্ৰৱেশ কৰিব।');
INSERT INTO "habibur_rahman" VALUES(87,13,'87:13','তাৰ পিছত সি তাত মৃত্যুবৰণো কৰিব নোৱাৰিব আৰু জীৱিতও থাকিব নোৱাৰিব।');
INSERT INTO "habibur_rahman" VALUES(87,14,'87:14','নিশ্চয় সফলতা লাভ কৰিব যিয়ে আত্মশুদ্ধি কৰিব।');
INSERT INTO "habibur_rahman" VALUES(87,15,'87:15','আৰু তাৰ প্ৰতিপালকৰ নাম স্মৰণ কৰিব আৰু ছালাত আদায় কৰিব।');
INSERT INTO "habibur_rahman" VALUES(87,16,'87:16','কিন্তু তোমালোকে পৃথিৱীৰ জীৱনক প্ৰাধান্য দিয়া,');
INSERT INTO "habibur_rahman" VALUES(87,17,'87:17','অথচ আখিৰাত সৰ্বোত্তম আৰু স্থায়ী।');
INSERT INTO "habibur_rahman" VALUES(87,18,'87:18','নিশ্চয় ইয়াৰ বৰ্ণনা আছে পূৰ্বৱৰ্তী ছহীফাসমূহত,');
INSERT INTO "habibur_rahman" VALUES(87,19,'87:19','ইব্ৰাহীম আৰু মূছাৰ ছহীফাসমূহত।');
INSERT INTO "habibur_rahman" VALUES(88,1,'88:1','তোমাৰ ওচৰত আচ্ছন্নকাৰী (ক্বিয়ামতৰ) সংবাদ আহিছেনে?');
INSERT INTO "habibur_rahman" VALUES(88,2,'88:2','সেইদিনা কিছুমান মুখমণ্ডল হ’ব অৱনত,');
INSERT INTO "habibur_rahman" VALUES(88,3,'88:3','কৰ্মক্লান্ত, পৰিশ্ৰান্ত,');
INSERT INTO "habibur_rahman" VALUES(88,4,'88:4','সিহঁত প্ৰৱেশ কৰিব জ্বলন্ত জুইত;');
INSERT INTO "habibur_rahman" VALUES(88,5,'88:5','উত্তপ্ত উতলা নিজৰাৰ পানী পান কৰিবলৈ দিয়া হ’ব;');
INSERT INTO "habibur_rahman" VALUES(88,6,'88:6','সিহঁতৰ বাবে কাঁইটবিশিষ্ট ঘাঁহৰ বাহিৰে আন কোনো খাদ্য নাথাকিব,');
INSERT INTO "habibur_rahman" VALUES(88,7,'88:7','ই সিহঁতৰ পুষ্টিও সাধন নকৰিব আৰু ক্ষুধাও নিবৃত্ত নকৰিব।');
INSERT INTO "habibur_rahman" VALUES(88,8,'88:8','সেইদিনা কিছুমান মুখমণ্ডল হ’ব আনন্দোজ্জ্বল,');
INSERT INTO "habibur_rahman" VALUES(88,9,'88:9','নিজ কৰ্মৰ সাফল্যত পৰিতৃপ্ত,');
INSERT INTO "habibur_rahman" VALUES(88,10,'88:10','সুউচ্চ জান্নাতত,');
INSERT INTO "habibur_rahman" VALUES(88,11,'88:11','তাত সিহঁতে কোনো অনৰ্থক কথা শুনিবলৈ নাপাব,');
INSERT INTO "habibur_rahman" VALUES(88,12,'88:12','তাত থাকিব প্ৰবাহমান নিজৰা,');
INSERT INTO "habibur_rahman" VALUES(88,13,'88:13','তাত থাকিব উন্নত শয্যাসমূহ,');
INSERT INTO "habibur_rahman" VALUES(88,14,'88:14','আৰু প্ৰস্তুত থাকিব পানপাত্ৰসমূহ,');
INSERT INTO "habibur_rahman" VALUES(88,15,'88:15','আৰু শাৰী শাৰীকৈ ৰখা গাৰুসমূহ,');
INSERT INTO "habibur_rahman" VALUES(88,16,'88:16','আৰু বিস্তৃত পাৰি থোৱা দলিচাসমূহ,');
INSERT INTO "habibur_rahman" VALUES(88,17,'88:17','ইহঁতে উটৰ পিনে দৃষ্টিপাত নকৰে নেকি, কেনেকৈ তাক সৃষ্টি কৰা হৈছে?');
INSERT INTO "habibur_rahman" VALUES(88,18,'88:18','আৰু আকাশৰ ফালে, কেনেকৈ তাক উৰ্দ্ধত স্থাপন কৰা হৈছে?');
INSERT INTO "habibur_rahman" VALUES(88,19,'88:19','আৰু পৰ্বতসমূহৰ ফালে, সেইবোৰ কেনেকৈ প্ৰতিষ্ঠিত কৰা হৈছে?');
INSERT INTO "habibur_rahman" VALUES(88,20,'88:20','আৰু পৃথিৱীখনৰ ফালে, কিদৰে তাক বিস্তৃত কৰা হৈছে?');
INSERT INTO "habibur_rahman" VALUES(88,21,'88:21','এতেকে তুমি উপদেশ দিয়া, তুমিতো কেৱল এজন উপদেশদাতা,');
INSERT INTO "habibur_rahman" VALUES(88,22,'88:22','তুমি সিহঁতৰ ওপৰত শক্তি প্ৰয়োগকাৰী নহয়।');
INSERT INTO "habibur_rahman" VALUES(88,23,'88:23','কিন্তু কোনোবাই মুখ ঘুৰাই ল’লে অথবা কুফৰী কৰিলে,');
INSERT INTO "habibur_rahman" VALUES(88,24,'88:24','তেন্তে আল্লাহে তাক মহাশাস্তি প্ৰদান কৰিব।');
INSERT INTO "habibur_rahman" VALUES(88,25,'88:25','নিশ্চয় আমাৰ ওচৰতেই সিহঁতৰ প্ৰত্যাৱৰ্তন।');
INSERT INTO "habibur_rahman" VALUES(88,26,'88:26','আৰু সিহঁতৰ হিচাপ-নিকাচ আমাৰেই দায়িত্ব।');
INSERT INTO "habibur_rahman" VALUES(89,1,'89:1','ফজৰৰ সময়ৰ শপত,');
INSERT INTO "habibur_rahman" VALUES(89,2,'89:2','শপত দহ ৰাতিৰ,');
INSERT INTO "habibur_rahman" VALUES(89,3,'89:3','শপত যোৰ আৰু বিযোৰৰ,');
INSERT INTO "habibur_rahman" VALUES(89,4,'89:4','আৰু ৰাতিৰ শপত যেতিয়া ই বিদায় লয়,');
INSERT INTO "habibur_rahman" VALUES(89,5,'89:5','ইয়াৰ মাজত বোধসম্পন্ন ব্যক্তিৰ বাবে দৃঢ় প্ৰমাণ নাইনে?');
INSERT INTO "habibur_rahman" VALUES(89,6,'89:6','তুমি দেখা নাইনে, তোমাৰ প্ৰতিপালকে আদ সম্প্ৰদায়ৰ লগত কি আচৰণ কৰিছিল?');
INSERT INTO "habibur_rahman" VALUES(89,7,'89:7','ইৰাম গোত্ৰৰ লগত, যিহঁতে অধিকাৰী আছিল সুউচ্চ প্ৰাসাদৰ?');
INSERT INTO "habibur_rahman" VALUES(89,8,'89:8','যাৰ সমতুল্য (জাতি) কোনো দেশতে সৃষ্টি কৰা হোৱা নাই;');
INSERT INTO "habibur_rahman" VALUES(89,9,'89:9','আৰু ছামূদ সম্প্ৰদায়ৰ লগত (কি আচৰণ কৰিছিল) যিসকলে ভৈয়ামত শিল কাটি ঘৰ নিৰ্মাণ কৰিছিল;');
INSERT INTO "habibur_rahman" VALUES(89,10,'89:10','আৰু শূলদণ্ডৰ অধিকাৰী ফিৰআউনৰ লগত (কি আচৰণ কৰিছিল)');
INSERT INTO "habibur_rahman" VALUES(89,11,'89:11','যিসকলে দেশত সীমালঙ্ঘন কৰিছিল,');
INSERT INTO "habibur_rahman" VALUES(89,12,'89:12','তাৰ পিছত তাত অশান্তি বৃদ্ধি কৰিছিল।');
INSERT INTO "habibur_rahman" VALUES(89,13,'89:13','ফলত তোমাৰ প্ৰতিপালকে সিহঁতৰ ওপৰত শাস্তিৰ চাবুক মাৰিলে।');
INSERT INTO "habibur_rahman" VALUES(89,14,'89:14','নিশ্চয় তোমাৰ প্ৰতিপালকে সতৰ্ক দৃষ্টি ৰাখে।');
INSERT INTO "habibur_rahman" VALUES(89,15,'89:15','আৰু মানুহৰ প্ৰকৃতি এনেকুৱা যে, যেতিয়া তাৰ প্ৰতিপালকে তাক সন্মান আৰু অনুগ্ৰহ প্ৰদান কৰি পৰীক্ষা কৰে, তেতিয়া সি কয়, ‘মোৰ প্ৰতিপালকে মোক সন্মানিত কৰিছে’।');
INSERT INTO "habibur_rahman" VALUES(89,16,'89:16','আৰু যেতিয়া তাক পৰীক্ষা কৰিবলৈ তাৰ জীৱিকা সংকুচিত কৰি দিয়ে, তেতিয়া সি কয়, ‘মোৰ প্ৰতিপালকে মোক লাঞ্ছিত কৰিছে’।');
INSERT INTO "habibur_rahman" VALUES(89,17,'89:17','কেতিয়াও নহয়, বৰং তোমালোকে এতীমক সমাদৰ নকৰা,');
INSERT INTO "habibur_rahman" VALUES(89,18,'89:18','আৰু তোমালোকে মিচকিনক খাদ্য প্ৰদান কৰিবলৈ পৰস্পৰে পৰস্পৰক উত্সাহিত নকৰা,');
INSERT INTO "habibur_rahman" VALUES(89,19,'89:19','আৰু তোমালোকে উত্তৰাধিকাৰৰ সম্পদ সম্পূৰ্ণৰূপে গ্ৰাস কৰি পেলোৱা,');
INSERT INTO "habibur_rahman" VALUES(89,20,'89:20','আৰু তোমালোকে ধন-সম্পদক অতিকৈ ভালপোৱা;');
INSERT INTO "habibur_rahman" VALUES(89,21,'89:21','কেতিয়াও নহয়। যেতিয়া পৃথিৱীখনক চূৰ্ণ-বিচূৰ্ণ কৰা হ’ব,');
INSERT INTO "habibur_rahman" VALUES(89,22,'89:22','আৰু যেতিয়া তোমাৰ প্ৰতিপালকে আগমন কৰিব আৰু ফিৰিস্তাসকল শাৰীবদ্ধভাৱে উপস্থিত হ’ব,');
INSERT INTO "habibur_rahman" VALUES(89,23,'89:23','আৰু সেইদিনা জাহান্নামক উপস্থিত কৰা হ’ব, সেইদিনা মানুহে স্মৰণ কৰিব, কিন্তু এই স্মৰণ তাৰ কোন কামত আহিব?');
INSERT INTO "habibur_rahman" VALUES(89,24,'89:24','(তেতিয়া) সি ক’ব, ‘হায়! এই জীৱনৰ বাবে মই যদি কিবা অগ্ৰিম পঠালোহেঁতেন,');
INSERT INTO "habibur_rahman" VALUES(89,25,'89:25','সেইদিনা তেওঁৰ শাস্তিৰ দৰে শাস্তি কোনেও দিব নোৱাৰিব।');
INSERT INTO "habibur_rahman" VALUES(89,26,'89:26','আৰু তেওঁৰ বান্ধোনৰ দৰে বান্ধোন আন কোনেও দিব নোৱাৰিব।');
INSERT INTO "habibur_rahman" VALUES(89,27,'89:27','হে প্ৰশান্ত আত্মা!');
INSERT INTO "habibur_rahman" VALUES(89,28,'89:28','তুমি তোমাৰ প্ৰতিপালকৰ ওচৰলৈ উভতি আহা সন্তুষ্ট আৰু সন্তোষভাজন হৈ,');
INSERT INTO "habibur_rahman" VALUES(89,29,'89:29','আৰু মোৰ বান্দাসকলৰ অন্তৰ্ভূক্ত হৈ যোৱা,');
INSERT INTO "habibur_rahman" VALUES(89,30,'89:30','আৰু মোৰ জান্নাতত প্ৰৱেশ কৰা।');
INSERT INTO "habibur_rahman" VALUES(90,1,'90:1','মই শপত কৰিছো এই নগৰখনৰ,');
INSERT INTO "habibur_rahman" VALUES(90,2,'90:2','আৰু তুমি এই নগৰৰ অধিবাসী,');
INSERT INTO "habibur_rahman" VALUES(90,3,'90:3','শপত জন্মদাতাৰ আৰু যিটো সি জন্ম দিছে।');
INSERT INTO "habibur_rahman" VALUES(90,4,'90:4','নিসন্দেহে আমি মানুহক সৃষ্টি কৰিছো কঠোৰ দুখ-কষ্টৰ মাজত।');
INSERT INTO "habibur_rahman" VALUES(90,5,'90:5','সি ভাবে নেকি যে, কেতিয়াও তাৰ ওপৰত কোনোৱেই ক্ষমতাবান নহ’ব?');
INSERT INTO "habibur_rahman" VALUES(90,6,'90:6','সি কয়, ''মই প্ৰচুৰ ধন-সম্পদ নাশ কৰিছো''।');
INSERT INTO "habibur_rahman" VALUES(90,7,'90:7','সি ভাবে নেকি যে, কোনেও তাক দেখা নাই?');
INSERT INTO "habibur_rahman" VALUES(90,8,'90:8','তাৰ বাবে আমি দুটা চকু সৃষ্টি কৰা নাইনে?');
INSERT INTO "habibur_rahman" VALUES(90,9,'90:9','এখন জিভা আৰু দুটা ওঁঠ?');
INSERT INTO "habibur_rahman" VALUES(90,10,'90:10','আৰু আমি তাক দুটা পথ দেখুৱাইছো।');
INSERT INTO "habibur_rahman" VALUES(90,11,'90:11','কিন্তু সি দুৰ্গম গিৰিপথত প্ৰৱেশ কৰা নাই।');
INSERT INTO "habibur_rahman" VALUES(90,12,'90:12','আৰু তোমাক কিহে জনাব যে, দুৰ্গম গিৰিপথ কি?');
INSERT INTO "habibur_rahman" VALUES(90,13,'90:13','সেইটো হৈছে, দাসমুক্তি।');
INSERT INTO "habibur_rahman" VALUES(90,14,'90:14','অথবা দুৰ্ভিক্ষৰ দিনত খাদ্যদান কৰা,');
INSERT INTO "habibur_rahman" VALUES(90,15,'90:15','নিকটাত্মীয় এতীমক,');
INSERT INTO "habibur_rahman" VALUES(90,16,'90:16','অথবা দৰিদ্ৰ-নিষ্পেষিত অভাৱগ্ৰস্তক,');
INSERT INTO "habibur_rahman" VALUES(90,17,'90:17','তাৰ পিছত সি এনেলোকৰ অন্তৰ্ভূক্ত হৈ যায় যিসকলে ঈমান আনিছে আৰু পৰস্পৰক ধৈৰ্য ধাৰণ কৰিবলৈ উপদেশ দিছে আৰু উপদেশ দিছে পৰস্পৰক দয়া-অনুগ্ৰহ কৰিবলৈ;');
INSERT INTO "habibur_rahman" VALUES(90,18,'90:18','এওঁলোকেই সৌভাগ্যশালী।');
INSERT INTO "habibur_rahman" VALUES(90,19,'90:19','আৰু যিসকলে আমাৰ আয়াতসমূহক অস্বীকাৰ কৰিছে, সিহঁতেই হতভাগ্য।');
INSERT INTO "habibur_rahman" VALUES(90,20,'90:20','সিহঁত অবৰুদ্ধ জুইত পৰিবেষ্টিত থাকিব।');
INSERT INTO "habibur_rahman" VALUES(91,1,'91:1','শপত সূৰ্যৰ আৰু তাৰ পোহৰৰ,');
INSERT INTO "habibur_rahman" VALUES(91,2,'91:2','শপত চন্দ্ৰৰ, যেতিয়া ই সূৰ্যৰ পিছত আবিৰ্ভাৱ হয়,');
INSERT INTO "habibur_rahman" VALUES(91,3,'91:3','আৰু দিনৰ শপত, যেতিয়া সূৰ্যক প্ৰকাশ কৰে,');
INSERT INTO "habibur_rahman" VALUES(91,4,'91:4','ৰাতিৰ শপত যেতিয়া ই সূৰ্যক আচ্ছাদিত কৰে,');
INSERT INTO "habibur_rahman" VALUES(91,5,'91:5','শপত আকাশৰ আৰু সেই সত্তাৰ যিজনে ইয়াক নিৰ্মাণ কৰিছে,');
INSERT INTO "habibur_rahman" VALUES(91,6,'91:6','পৃথিৱীৰ শপত আৰু যিজনে ইয়াক বিস্তৃত কৰিছে,');
INSERT INTO "habibur_rahman" VALUES(91,7,'91:7','শপত প্ৰাণৰ আৰু যিজনে ইয়াক সুবিন্যস্ত কৰিছে,');
INSERT INTO "habibur_rahman" VALUES(91,8,'91:8','তাৰ পিছত ইয়াক তাৰ সত্কৰ্মৰ আৰু অসত্কৰ্মৰ জ্ঞান দান কৰিছে,');
INSERT INTO "habibur_rahman" VALUES(91,9,'91:9','নিসন্দেহে সিয়েই সফল হৈছে যিয়ে নিজকে পৱিত্ৰ কৰিছে।');
INSERT INTO "habibur_rahman" VALUES(91,10,'91:10','আৰু সিয়েই ব্যৰ্থ হৈছে, যিয়ে নিজকে কলুষিত কৰিছে।');
INSERT INTO "habibur_rahman" VALUES(91,11,'91:11','ছামূদ সম্প্ৰদায়ে অবাধ্যতাবশত অস্বীকাৰ কৰিছিল।');
INSERT INTO "habibur_rahman" VALUES(91,12,'91:12','যেতিয়া সিহঁতৰ মাজৰ হতভাগ্য ব্যক্তিজন তত্পৰ হৈ উঠিল,');
INSERT INTO "habibur_rahman" VALUES(91,13,'91:13','তেতিয়া আল্লাহৰ ৰাছুলে সিহঁতক ক''লে, ''আল্লাহৰ উটজনীক আৰু তাইৰ পানী খোৱা সম্পৰ্কে সাৱধান হোৱা''।');
INSERT INTO "habibur_rahman" VALUES(91,14,'91:14','কিন্তু সিহঁতে অস্বীকাৰ কৰিলে আৰু উটজনীক বধ কৰিলে। ফলত সিহঁতৰ পাপৰ বাবে সিহঁতৰ প্ৰতিপালকে সিহঁতক সমূলে ধ্বংস কৰি একাকাৰ কৰি দিলে।');
INSERT INTO "habibur_rahman" VALUES(91,15,'91:15','আৰু তেওঁ ইয়াৰ পৰিণামক ভয় নকৰে।');
INSERT INTO "habibur_rahman" VALUES(92,1,'92:1','শপত ৰাতিৰ, যেতিয়া ই আচ্ছন্ন কৰে,');
INSERT INTO "habibur_rahman" VALUES(92,2,'92:2','শপত দিনৰ, যেতিয়া ই উজ্জ্বল হয়,');
INSERT INTO "habibur_rahman" VALUES(92,3,'92:3','আৰু শপত সেই সত্তাৰ, যিজনে নৰ আৰু নাৰী সৃষ্টি কৰিছে।');
INSERT INTO "habibur_rahman" VALUES(92,4,'92:4','নিশ্চয় তোমালোকৰ কৰ্মপ্ৰচেষ্টা বিভিন্ন প্ৰকৃতিৰ।');
INSERT INTO "habibur_rahman" VALUES(92,5,'92:5','এতেকে কোনোবাই দান কৰিলে আৰু তাক্বৱা অৱলম্বন কৰিলে,');
INSERT INTO "habibur_rahman" VALUES(92,6,'92:6','আৰু যি উত্তম তাক সত্য বুলি গ্ৰহণ কৰিলে,');
INSERT INTO "habibur_rahman" VALUES(92,7,'92:7','আমি তাৰ বাবে সহজ পথত চলা সুগম কৰি দিম।');
INSERT INTO "habibur_rahman" VALUES(92,8,'92:8','আৰু কোনোবাই কৃপণালি কৰিলে আৰু নিজকে অমুখাপেক্ষী ভাবিলে,');
INSERT INTO "habibur_rahman" VALUES(92,9,'92:9','আৰু যি উত্তম তাক অস্বীকাৰ কৰিলে,');
INSERT INTO "habibur_rahman" VALUES(92,10,'92:10','আমি তাৰ বাবে কঠিন পথত চলা সুগম কৰি দিম।');
INSERT INTO "habibur_rahman" VALUES(92,11,'92:11','আৰু তাৰ সম্পদ তাৰ কোনো কামত নাহিব, যেতিয়া সি ধ্বংস হ’ব।');
INSERT INTO "habibur_rahman" VALUES(92,12,'92:12','নিশ্চয় পথপ্ৰদৰ্শন কৰাটোৱে আমাৰ কৰ্তব্য।');
INSERT INTO "habibur_rahman" VALUES(92,13,'92:13','নিশ্চয় আমিয়েই পৰকাল আৰু ইহকালৰ মালিক।');
INSERT INTO "habibur_rahman" VALUES(92,14,'92:14','এতেকে আমি তোমালোকক লেলিহান জুই সম্পৰ্কে সতৰ্ক কৰি দিছো।');
INSERT INTO "habibur_rahman" VALUES(92,15,'92:15','তাত প্ৰৱেশ কৰিব কেৱল সেই ব্যক্তি যি হ’ব চৰম হতভাগ্য,');
INSERT INTO "habibur_rahman" VALUES(92,16,'92:16','যিয়ে অস্বীকাৰ কৰিছে আৰু মুখ ঘূৰাই লৈছে।');
INSERT INTO "habibur_rahman" VALUES(92,17,'92:17','আৰু ইয়াৰ পৰা দুৰৈত ৰখা হ’ব পৰম মুত্তাক্বী ব্যক্তিক,');
INSERT INTO "habibur_rahman" VALUES(92,18,'92:18','যিয়ে আত্মশুদ্ধিৰ বাবে নিজৰ সম্পদ দান কৰে,');
INSERT INTO "habibur_rahman" VALUES(92,19,'92:19','আৰু তাৰ প্ৰতি কাৰো এনে অনুগ্ৰহ নাই যাৰ প্ৰতিদান দিব লাগিব,');
INSERT INTO "habibur_rahman" VALUES(92,20,'92:20','কেৱল তাৰ মহান প্ৰতিপালকৰ সন্তুষ্টিৰ প্ৰত্যাশাত;');
INSERT INTO "habibur_rahman" VALUES(92,21,'92:21','আৰু অনতিপলমে তেওঁ সন্তুষ্টি লাভ কৰিব।');
INSERT INTO "habibur_rahman" VALUES(93,1,'93:1','পূৰ্বাহ্নৰ শপত,');
INSERT INTO "habibur_rahman" VALUES(93,2,'93:2','শপত ৰাতিৰ, যেতিয়া ই অন্ধকাৰাচ্ছন্ন হয়।');
INSERT INTO "habibur_rahman" VALUES(93,3,'93:3','তোমাৰ প্ৰতিপালকে তোমাক পৰিত্যাগ কৰা নাই আৰু অসন্তুষ্টও হোৱা নাই।');
INSERT INTO "habibur_rahman" VALUES(93,4,'93:4','নিশ্চয় তোমাৰ বাবে পৰবৰ্তী সময় পূৰ্বৱৰ্তী সময়তকৈ উত্তম।');
INSERT INTO "habibur_rahman" VALUES(93,5,'93:5','নিশ্চয় অনতিপলমে তোমাৰ প্ৰতিপালকে তোমাক অনুগ্ৰহ প্ৰদান কৰিব, ফলত তুমি সন্তুষ্ট হ’বা।');
INSERT INTO "habibur_rahman" VALUES(93,6,'93:6','তেওঁ তোমাক অনাথ অৱস্থাত পোৱা নাছিলনে? তাৰ পিছত তেৱেঁই তোমাক আশ্ৰয় দিছে।');
INSERT INTO "habibur_rahman" VALUES(93,7,'93:7','আৰু তেওঁ তোমাক পাইছিল পথহাৰা অৱস্থাত, তাৰ পিছত তেৱেঁই তোমাক পথনিৰ্দেশ দিছে।');
INSERT INTO "habibur_rahman" VALUES(93,8,'93:8','আৰু তেওঁ তোমাক পাইছিল সম্বলহীন দৰিদ্ৰৰূপে, তাৰ পিছত তেৱেঁই অভাৱমুক্ত কৰিলে।');
INSERT INTO "habibur_rahman" VALUES(93,9,'93:9','এতেকে তুমি এতীমৰ (অনাথৰ) প্ৰতি কঠোৰ নহ’বা।');
INSERT INTO "habibur_rahman" VALUES(93,10,'93:10','আৰু ভিখাৰীক ধমক নিদিবা।');
INSERT INTO "habibur_rahman" VALUES(93,11,'93:11','আৰু তোমাৰ প্ৰতিপালকৰ অনুগ্ৰহৰ কথা প্ৰচাৰ কৰা।');
INSERT INTO "habibur_rahman" VALUES(94,1,'94:1','আমি তোমাৰ (কল্যাণৰ) বাবে তোমাৰ বুকু প্ৰশস্ত কৰি দিয়া নাইনে?');
INSERT INTO "habibur_rahman" VALUES(94,2,'94:2','আৰু আমি তোমাৰ পৰা তোমাৰ বোজা অপসাৰণ কৰা নাইনে?');
INSERT INTO "habibur_rahman" VALUES(94,3,'94:3','যি বোজাই তোমাৰ পিঠি ভাঙি পেলাইছিল,');
INSERT INTO "habibur_rahman" VALUES(94,4,'94:4','আৰু আমিয়েই তোমাৰ (মৰ্যদা বৃদ্ধিৰ) বাবে তোমাৰ স্মৰণক সমুন্নত কৰিছো।');
INSERT INTO "habibur_rahman" VALUES(94,5,'94:5','এতেকে দুখৰ লগতেই আছে সুখ');
INSERT INTO "habibur_rahman" VALUES(94,6,'94:6','নিশ্চয় দুখৰ লগতেই আছে সুখ।');
INSERT INTO "habibur_rahman" VALUES(94,7,'94:7','এতেকে যেতিয়াই তুমি অৱসৰ পোৱা তেতিয়াই কঠোৰ ইবাদতত আত্মনিয়োগ কৰা।');
INSERT INTO "habibur_rahman" VALUES(94,8,'94:8','আৰু তোমাৰ প্ৰতিপালকৰ প্ৰতি গভীৰ মনোযোগী হোৱা।');
INSERT INTO "habibur_rahman" VALUES(95,1,'95:1','শপত তীন আৰু যায়তুনৰ,');
INSERT INTO "habibur_rahman" VALUES(95,2,'95:2','শপত চিনাই পৰ্বতৰ,');
INSERT INTO "habibur_rahman" VALUES(95,3,'95:3','শপত এই নিৰাপদ নগৰখনৰ,');
INSERT INTO "habibur_rahman" VALUES(95,4,'95:4','নিশ্চয় আমি মানুহক সৰ্বোত্তম গঠনত সৃষ্টি কৰিছো,');
INSERT INTO "habibur_rahman" VALUES(95,5,'95:5','তাৰ পিছত আমি তাক হীনতাগ্ৰস্থসকলৰ হীনতমত পৰিণত কৰিছো,');
INSERT INTO "habibur_rahman" VALUES(95,6,'95:6','কিন্তু যিসকলে ঈমান আনিছে আৰু সত্কৰ্ম কৰিছে তেওঁলোকৰ বাবে আছে নিৰবিচ্ছিন্ন প্ৰতিদান।');
INSERT INTO "habibur_rahman" VALUES(95,7,'95:7','এতেকে (হে মানৱ!) ইয়াৰ পিছতো কিহে তোমাক কৰ্মফল দিন সম্পৰ্কে অবিশ্বাসী কৰি তুলে?');
INSERT INTO "habibur_rahman" VALUES(95,8,'95:8','আল্লাহেই বিচাৰকসকলৰ মাজত সৰ্বশ্ৰেষ্ঠ বিচাৰক নহয়নে বাৰু?');
INSERT INTO "habibur_rahman" VALUES(96,1,'96:1','পঢ়া তোমাৰ প্ৰতিপালকৰ নামত, যিজনে সৃষ্টি কৰিছে,');
INSERT INTO "habibur_rahman" VALUES(96,2,'96:2','তেওঁ মানুহক আলাক্বৰ পৰা সৃষ্টি কৰিছে।');
INSERT INTO "habibur_rahman" VALUES(96,3,'96:3','পঢ়া, কাৰণ তোমাৰ প্ৰতিপালক মহামহিম।');
INSERT INTO "habibur_rahman" VALUES(96,4,'96:4','যিজনে কলমৰ সহায়ত শিক্ষা দিছে।');
INSERT INTO "habibur_rahman" VALUES(96,5,'96:5','তেওঁ মানুহক এনে শিক্ষা দিছে, যিটো মানুহে জনা নাছিল।');
INSERT INTO "habibur_rahman" VALUES(96,6,'96:6','বাস্তৱ এই যে, মানুহে সীমালঙ্ঘন কৰি থাকে।');
INSERT INTO "habibur_rahman" VALUES(96,7,'96:7','কাৰণ সি নিজকে স্বনিৰ্ভৰশীল বুলি ভাৱে।');
INSERT INTO "habibur_rahman" VALUES(96,8,'96:8','নিশ্চয় তোমাৰ প্ৰতিপালকৰ ওচৰতেই প্ৰত্যাৱৰ্তন।');
INSERT INTO "habibur_rahman" VALUES(96,9,'96:9','তুমি তাক দেখা নাইনে? যিয়ে নিষেধ কৰে।');
INSERT INTO "habibur_rahman" VALUES(96,10,'96:10','এজন বান্দাক, যেতিয়া তেওঁ ছালাত আদায় কৰে।');
INSERT INTO "habibur_rahman" VALUES(96,11,'96:11','কোৱাচোন, যদিও তেওঁ হিদায়তৰ ওপৰত প্ৰতিষ্ঠিত,');
INSERT INTO "habibur_rahman" VALUES(96,12,'96:12','অথবা তাক্বৱাৰ নিৰ্দেশ দিয়ে; (তথাপিও তেওঁক কেনেকৈ বাধা দিয়ে?)');
INSERT INTO "habibur_rahman" VALUES(96,13,'96:13','কোৱাচোন যদি সি (নিষেধকাৰী) অস্বীকাৰ কৰে আৰু মুখ ঘূৰাই লয়,');
INSERT INTO "habibur_rahman" VALUES(96,14,'96:14','সি নাজানে নেকি, নিশ্চয় আল্লাহে দেখি আছে?');
INSERT INTO "habibur_rahman" VALUES(96,15,'96:15','কেতিয়াও নহয়, যদি সি বিৰত নাথাকে, তেন্তে নিশ্চয় আমি তাক আগচুলিত ধৰি টানি আনিম।');
INSERT INTO "habibur_rahman" VALUES(96,16,'96:16','মিছলীয়া, পাপিষ্ঠৰ আগচুলি (কপাল)।');
INSERT INTO "habibur_rahman" VALUES(96,17,'96:17','এতেকে সি নিজ পৰিষদক মাতি আনক।');
INSERT INTO "habibur_rahman" VALUES(96,18,'96:18','অনতিপলমে আমি মাতি আনিম জাহান্নামৰ কঠোৰ প্ৰহৰীসকলক।');
INSERT INTO "habibur_rahman" VALUES(96,19,'96:19','কেতিয়াও নহয়, তুমি তাৰ অনুসৰণ নকৰিবা। আৰু তুমি (তোমাৰ প্ৰতিপালকক) ছাজদা কৰা আৰু নৈকট্য লাভ কৰা।');
INSERT INTO "habibur_rahman" VALUES(97,1,'97:1','নিশ্চয় আমি ইয়াক লাইলাতুল ক্বদৰত অৱতীৰ্ণ কৰিছো;');
INSERT INTO "habibur_rahman" VALUES(97,2,'97:2','তুমি কি জানিবা লাইলাতুল ক্বদৰ কি?');
INSERT INTO "habibur_rahman" VALUES(97,3,'97:3','লাইলাতুল ক্বদৰ এহেজাৰ মাহতকৈয়ো শ্ৰেষ্ঠ।');
INSERT INTO "habibur_rahman" VALUES(97,4,'97:4','সেই ৰাতিত ফিৰিস্তাসকল আৰু ৰূহ তেওঁলোকৰ প্ৰতিপালকৰ অনুমতিক্ৰমে যাৱতীয় সিদ্ধান্ত লৈ অৱতৰণ কৰে।');
INSERT INTO "habibur_rahman" VALUES(97,5,'97:5','শান্তিময় সেই ৰাতি, ফজৰৰ আবিৰ্ভাৱ নোহোৱালৈকে (শান্তি বিস্তৃত থাকে)।');
INSERT INTO "habibur_rahman" VALUES(98,1,'98:1','আহলে কিতাবসকলৰ মাজত যিসকলে কুফৰী কৰিছে আৰু মুশ্বৰিকসকল, সিহঁতৰ ওচৰত সুস্পষ্ট প্ৰমাণ নহালৈকে সিহঁতে বিৰত থাকিবলৈ প্ৰস্তুত নাছিল।');
INSERT INTO "habibur_rahman" VALUES(98,2,'98:2','আল্লাহৰ তৰফৰ পৰা এজন ৰাছুল, যিজনে তিলাৱত কৰি শুনাব পৱিত্ৰ পত্ৰসমূহ,');
INSERT INTO "habibur_rahman" VALUES(98,3,'98:3','য’ত আছে সঠিক বিধিবদ্ধ বিধান।');
INSERT INTO "habibur_rahman" VALUES(98,4,'98:4','আৰু যিসকলক কিতাপ দিয়া হৈছিল সিহঁতে বিভক্ত হ’ল, সিহঁতৰ ওচৰত সুস্পষ্ট প্ৰমাণ অহাৰ পিছত।');
INSERT INTO "habibur_rahman" VALUES(98,5,'98:5','আৰু সিহঁতক কেৱল এই নিৰ্দেশ দিয়া হৈছিল যে, সিহঁতে যেন আল্লাহৰ ইবাদত কৰে তেওঁৰ বাবেই যেন দ্বীনক একনিষ্ঠ কৰে আৰু যেন ছালাত প্ৰতিষ্ঠা কৰে আৰু যাকাত প্ৰদান কৰে, আৰু এইটোৱেই হৈছে সঠিক দ্বীন।');
INSERT INTO "habibur_rahman" VALUES(98,6,'98:6','নিশ্চয় আহলে কিতাবসকলৰ মাজৰ যিসকলে কুফৰী কৰিছে সিহঁত আৰু মুশ্বৰিকসকল জাহান্নামৰ জুইত স্থায়ীভাৱে অৱস্থান কৰিব; ইহঁতেই হৈছে নিকৃষ্ট সৃষ্টি।');
INSERT INTO "habibur_rahman" VALUES(98,7,'98:7','নিশ্চয় যিসকলে ঈমান আনিছে আৰু নেক কাম কৰিছে, তেওঁলোকেই হৈছে সৃষ্টিৰ শ্ৰেষ্ঠ।');
INSERT INTO "habibur_rahman" VALUES(98,8,'98:8','তেওঁলোকৰ প্ৰতিপালকৰ ওচৰত তেওঁলোকৰ পুৰষ্কাৰ আছে স্থায়ী জান্নাত, যাৰ তলত নদীসমূহ প্ৰবাহিত, তাত তেওঁলোকে চিৰকাল থাকিব। আল্লাহ তেওঁলোকৰ প্ৰতি সন্তুষ্ট আৰু তেওঁলোকেও তেওঁৰ প্ৰতি সন্তুষ্ট। এইটো তাৰ বাবে, যিয়ে নিজ প্ৰতিপালকক ভয় কৰে।');
INSERT INTO "habibur_rahman" VALUES(99,1,'99:1','যেতিয়া প্ৰবল কম্পনত পৃথিৱীখন প্ৰকম্পিত হ’ব,');
INSERT INTO "habibur_rahman" VALUES(99,2,'99:2','আৰু পৃথিৱীয়ে তাৰ ভিতৰত থকা বোজাবোৰ উলিয়াই দিব,');
INSERT INTO "habibur_rahman" VALUES(99,3,'99:3','আৰু মানুহে ক’ব, ইয়াৰ কি হ’ল?');
INSERT INTO "habibur_rahman" VALUES(99,4,'99:4','সেইদিনা পৃথিৱীয়ে তাৰ বৃত্তান্ত বৰ্ণনা কৰিব,');
INSERT INTO "habibur_rahman" VALUES(99,5,'99:5','কাৰণ তোমাৰ প্ৰতিপালকে তাক নিৰ্দেশ দিছে,');
INSERT INTO "habibur_rahman" VALUES(99,6,'99:6','সেইদিনা মানুহে ভিন্ ভিন্ দলত ওলাই আহিব যাতে সিহঁতক সিহঁতৰ কৃতকৰ্মবোৰ দেখুৱাব পৰা যায়,');
INSERT INTO "habibur_rahman" VALUES(99,7,'99:7','এতেকে যিয়ে অণু পৰিমাণ সত্কৰ্ম কৰিছে সেয়া সি দেখিবলৈ পাব।');
INSERT INTO "habibur_rahman" VALUES(99,8,'99:8','আৰু যিয়ে অণু পৰিমাণ অসত্কৰ্ম কৰিছে সেয়াও সি দেখিবলৈ পাব।');
INSERT INTO "habibur_rahman" VALUES(100,1,'100:1','শপত ঊৰ্ধ্বশ্বাসত দৌৰি যোৱা ঘোঁৰাসমূহৰ,');
INSERT INTO "habibur_rahman" VALUES(100,2,'100:2','যিবোৰে ক্ষুৰৰ আঘাতত ফিৰিঙতি উৰুৱায়,');
INSERT INTO "habibur_rahman" VALUES(100,3,'100:3','যিবোৰে প্ৰভাতকালত আক্ৰমণ কৰে,');
INSERT INTO "habibur_rahman" VALUES(100,4,'100:4','ফলত সিহঁতে ইয়াৰ দ্বাৰা ধূলি উৰুৱায়;');
INSERT INTO "habibur_rahman" VALUES(100,5,'100:5','আৰু ইয়াৰ দ্বাৰা শত্ৰু দলৰ অভ্যন্তৰত সোমাই পৰে।');
INSERT INTO "habibur_rahman" VALUES(100,6,'100:6','নিশ্চয় মানুহ তাৰ প্ৰতিপালকৰ প্ৰতি অতিকৈ অকৃতজ্ঞ,');
INSERT INTO "habibur_rahman" VALUES(100,7,'100:7','নিশ্চয় সি এই বিষয়ে সাক্ষী,');
INSERT INTO "habibur_rahman" VALUES(100,8,'100:8','আৰু নিশ্চয় সি ধন-সম্পদৰ লোভত প্ৰবল।');
INSERT INTO "habibur_rahman" VALUES(100,9,'100:9','সি নাজানে নেকি যে, কবৰত যি আছে সেয়া উত্থিত হ’ব,');
INSERT INTO "habibur_rahman" VALUES(100,10,'100:10','আৰু অন্তৰত যি আছে সেয়া প্ৰকাশিত হ’ব?');
INSERT INTO "habibur_rahman" VALUES(100,11,'100:11','নিশ্চয় সিহঁতৰ প্ৰতিপালক সেইদিনা সিহঁতৰ অৱস্থা সম্পৰ্কে সবিশেষ অৱহিত।');
INSERT INTO "habibur_rahman" VALUES(101,1,'101:1','ভীতিপ্ৰদ মহা বিপদ।');
INSERT INTO "habibur_rahman" VALUES(101,2,'101:2','ভীতিপ্ৰদ মহা বিপদ কি?');
INSERT INTO "habibur_rahman" VALUES(101,3,'101:3','ভীতিপ্ৰদ মহা বিপদ সম্পৰ্কে তুমি কি জানা?');
INSERT INTO "habibur_rahman" VALUES(101,4,'101:4','সেইদিনা মানুহ হ’ব বিক্ষিপ্ত কীট-পতঙ্গৰ দৰে।');
INSERT INTO "habibur_rahman" VALUES(101,5,'101:5','আৰু পৰ্বতসমূহ হ’ব ধূনা ৰঙ্গিন ঊণৰ দৰে।');
INSERT INTO "habibur_rahman" VALUES(101,6,'101:6','এতেকে যাৰ তৰ্জুসমূহ গধুৰ হ’ব,');
INSERT INTO "habibur_rahman" VALUES(101,7,'101:7','তেওঁ থাকিব সন্তোষজনক জীৱনত;');
INSERT INTO "habibur_rahman" VALUES(101,8,'101:8','আৰু যাৰ তৰ্জুসমূহ পাতল হ’ব,');
INSERT INTO "habibur_rahman" VALUES(101,9,'101:9','তাৰ ঠিকনা হ’ব ‘হা-ৱিয়াহ’।');
INSERT INTO "habibur_rahman" VALUES(101,10,'101:10','আৰু তুমি কি জানা সেইটোনো কি?');
INSERT INTO "habibur_rahman" VALUES(101,11,'101:11','ই এটা অত্যন্ত উত্তপ্ত অগ্নিকুণ্ড।');
INSERT INTO "habibur_rahman" VALUES(102,1,'102:1','প্ৰাচুৰ্যৰ প্ৰতিযোগিতায় তোমালোকক অমনোযোগী কৰি ৰাখিছে।');
INSERT INTO "habibur_rahman" VALUES(102,2,'102:2','যেতিয়ালৈকে তোমালোকে কবৰত উপনীত নোহোৱা।');
INSERT INTO "habibur_rahman" VALUES(102,3,'102:3','কেতিয়াও নহয়, অতি সোনকালে তোমালোকে জানিবলৈ পাবা।');
INSERT INTO "habibur_rahman" VALUES(102,4,'102:4','আকৌ কেতিয়াও নহয়, অতি সোনকালে তোমালোকে জানিবলৈ পাবা।');
INSERT INTO "habibur_rahman" VALUES(102,5,'102:5','কেতিয়াও নহয়, যদি তোমালোকে নিশ্চিতভাৱে জানিলাহেঁতেন।');
INSERT INTO "habibur_rahman" VALUES(102,6,'102:6','নিশ্চয় তোমালোকে জাহীম (জাহান্নাম) দেখিবা।');
INSERT INTO "habibur_rahman" VALUES(102,7,'102:7','আকৌ, নিশ্চয় তোমালোকে ইয়াক পতিয়ন যোৱাকৈ দেখিবা।');
INSERT INTO "habibur_rahman" VALUES(102,8,'102:8','তাৰ পিছত সেইদিনা অৱশ্যই তোমালোকক নিয়ামত সম্পৰ্কে প্ৰশ্ন কৰা হ’ব।');
INSERT INTO "habibur_rahman" VALUES(103,1,'103:1','সময়ৰ শপত,');
INSERT INTO "habibur_rahman" VALUES(103,2,'103:2','নিশ্চয় মানুহ ক্ষতিগ্ৰস্থতাত নিপতিত,');
INSERT INTO "habibur_rahman" VALUES(103,3,'103:3','কিন্তু তেওঁলোকৰ বাহিৰে, যিসকলে ঈমান আনিছে আৰু সত্কাম কৰিছে লগতে (যিসকলে) পৰস্পৰক সত্যৰ উপদেশ দিয়ে আৰু ধৈৰ্য ধাৰণ কৰিবলৈ উত্সাহ দিয়ে।');
INSERT INTO "habibur_rahman" VALUES(104,1,'104:1','দুৰ্ভোগ প্ৰত্যেক সেই ব্যক্তিৰ বাবে যিয়ে সন্মুখত আৰু অনুপস্থিতিত পৰনিন্দা কৰে।');
INSERT INTO "habibur_rahman" VALUES(104,2,'104:2','যিয়ে সম্পদ জমা কৰে আৰু বাৰে বাৰে গণনা কৰে।');
INSERT INTO "habibur_rahman" VALUES(104,3,'104:3','সি ধাৰণা কৰে যে, তাৰ সম্পদে তাক চিৰজীৱি কৰি ৰাখিব।');
INSERT INTO "habibur_rahman" VALUES(104,4,'104:4','কেতিয়াও নহয়, নিশ্চয় সি হুতামাত নিক্ষিপ্ত হ’ব।');
INSERT INTO "habibur_rahman" VALUES(104,5,'104:5','আৰু তুমি জানানে, হুতামা কি?');
INSERT INTO "habibur_rahman" VALUES(104,6,'104:6','ই আল্লাহৰ প্ৰজ্বলিত জুই।');
INSERT INTO "habibur_rahman" VALUES(104,7,'104:7','যিয়ে হৃদপিণ্ডক গ্ৰাস কৰিব।');
INSERT INTO "habibur_rahman" VALUES(104,8,'104:8','নিশ্চয় ই সিহঁতক আৱদ্ধ কৰি ৰাখিব।');
INSERT INTO "habibur_rahman" VALUES(104,9,'104:9','ওখ ওখ স্তম্ভসমূহত।');
INSERT INTO "habibur_rahman" VALUES(105,1,'105:1','তুমি দেখা নাইনে, তোমাৰ প্ৰতিপালকে হাতীৰ অধিপতিসকলৰ লগত কি কৰিছিল?');
INSERT INTO "habibur_rahman" VALUES(105,2,'105:2','তেওঁ সিহঁতৰ ষঢ়যন্ত্ৰ ব্যৰ্থতাত পৰ্যবসিত কৰা নাছিলনে?');
INSERT INTO "habibur_rahman" VALUES(105,3,'105:3','আৰু সিহঁতৰ বিৰুদ্ধে তেওঁ জাকে জাকে চৰাই পঠিওৱা নাছিলনে?');
INSERT INTO "habibur_rahman" VALUES(105,4,'105:4','যিসকলে সিহঁতৰ ওপৰত পকা টান মাটিৰ শিলগুটি নিক্ষেপ কৰিছিল');
INSERT INTO "habibur_rahman" VALUES(105,5,'105:5','ফলত তেওঁ সিহঁতক ভক্ষিত তৃণ-সদৃশ কৰিছিল।');
INSERT INTO "habibur_rahman" VALUES(106,1,'106:1','যিহেতু ক্বুৰাইছসকল অভ্যস্ত,');
INSERT INTO "habibur_rahman" VALUES(106,2,'106:2','শীত আৰু গ্ৰীষ্মকালৰ ভ্ৰমণত সিহঁতৰ আসক্তি আছে।');
INSERT INTO "habibur_rahman" VALUES(106,3,'106:3','এতেকে সিহঁতে যেন এই ঘৰৰ প্ৰতিপালকৰ ইবাদত কৰে।');
INSERT INTO "habibur_rahman" VALUES(106,4,'106:4','যিজনে সিহঁতক ক্ষুধা অৱস্থাত খাদ্য প্ৰদান কৰিছে আৰু সন্ত্ৰাসৰ পৰা নিৰাপত্তা দিছে।');
INSERT INTO "habibur_rahman" VALUES(107,1,'107:1','তুমি তাক দেখিছানে যিয়ে হিচাপ প্ৰতিদানক অস্বীকাৰ কৰে?');
INSERT INTO "habibur_rahman" VALUES(107,2,'107:2','সি সেইজনেই, যিয়ে অনাথক কঠোৰভাৱে খেদি পঠিয়ায়,');
INSERT INTO "habibur_rahman" VALUES(107,3,'107:3','আৰু সি দুখিয়াক খানা খুৱাবলৈ উৎসাহ নিদিয়ে।');
INSERT INTO "habibur_rahman" VALUES(107,4,'107:4','এতেকে ধ্বংস সেইবিধ ছালাত আদায়কাৰীসকলৰ,');
INSERT INTO "habibur_rahman" VALUES(107,5,'107:5','যিসকলে নিজৰ ছালাতত অমনোযোগী,');
INSERT INTO "habibur_rahman" VALUES(107,6,'107:6','যিসকলে আনক দেখুৱাবলৈহে কৰে,');
INSERT INTO "habibur_rahman" VALUES(107,7,'107:7','আৰু সৰু-সুৰা গৃহসামগ্ৰী প্ৰদানত বাধা দিয়ে।');
INSERT INTO "habibur_rahman" VALUES(108,1,'108:1','নিশ্চয় আমি তোমাক আল-কাউছাৰ প্ৰদান কৰিছোঁ।');
INSERT INTO "habibur_rahman" VALUES(108,2,'108:2','এতেকে তুমি তোমাৰ প্ৰতিপালকৰ উদ্দেশ্যে ছালাত আদায় কৰা আৰু কোৰবানী কৰা।');
INSERT INTO "habibur_rahman" VALUES(108,3,'108:3','নিশ্চয় তোমাৰ প্ৰতি বিদ্বেষ পোষণকাৰীয়েই নিৰ্বংশ।');
INSERT INTO "habibur_rahman" VALUES(109,1,'109:1','কোৱা, ‘হে কাফিৰসকল !');
INSERT INTO "habibur_rahman" VALUES(109,2,'109:2','তোমালোকে যাৰ ইবাদত কৰা, মই তাৰ ইবাদত নকৰোঁ,');
INSERT INTO "habibur_rahman" VALUES(109,3,'109:3','আৰু মই যাৰ ইবাদত কৰোঁ, তোমালোকে তেওঁৰ ইবাদতকাৰী নহয়,');
INSERT INTO "habibur_rahman" VALUES(109,4,'109:4','আৰু মই তাৰ ইবাদতকাৰী নহয় যাৰ ইবাদত তোমালোকে কৰা।');
INSERT INTO "habibur_rahman" VALUES(109,5,'109:5','আৰু মই যাৰ ইবাদত কৰোঁ, তোমালোকে তেওঁৰ ইবাদতকাৰী নহয়,');
INSERT INTO "habibur_rahman" VALUES(109,6,'109:6','তোমালোকৰ দ্বীন তোমালোকৰ বাবে, আৰু মোৰ দ্বীন মোৰ বাবে।');
INSERT INTO "habibur_rahman" VALUES(110,1,'110:1','যেতিয়া আল্লাহৰ সহায় আৰু বিজয় আহিব,');
INSERT INTO "habibur_rahman" VALUES(110,2,'110:2','আৰু তুমি মানুহক দলে দলে আল্লাহৰ দ্বীনত প্ৰৱেশ কৰোতে দেখিবা,');
INSERT INTO "habibur_rahman" VALUES(110,3,'110:3','তেতিয়া তুমি তোমাৰ প্ৰতিপালকৰ প্ৰশংসাসহ তেওঁৰ পৱিত্ৰতা আৰু মহিমা ঘোষণা কৰিবা আৰু তেওঁৰ ওচৰত ক্ষমা প্ৰাৰ্থনা কৰিবা, নিশ্চয় তেওঁ তাওবা গ্ৰহণকাৰী।');
INSERT INTO "habibur_rahman" VALUES(111,1,'111:1','ধ্বংস হওক আবু লাহাবৰ দুয়ো হাত আৰু সি নিজেও ধ্বংস হৈছে।');
INSERT INTO "habibur_rahman" VALUES(111,2,'111:2','তাৰ ধন-সম্পদ আৰু তাৰ উপাৰ্জন তাৰ কোনো কামত নাহিল।');
INSERT INTO "habibur_rahman" VALUES(111,3,'111:3','অতিশীঘ্ৰেই সি লেলিহান জুইত পতিত হ’ব।');
INSERT INTO "habibur_rahman" VALUES(111,4,'111:4','আৰু তাৰ স্ত্ৰীও- যিজনীয়ে ইন্ধন বহন কৰে,');
INSERT INTO "habibur_rahman" VALUES(111,5,'111:5','তাইৰ ডিঙিত থাকিব পকোৱা টান ৰছি।');
INSERT INTO "habibur_rahman" VALUES(112,1,'112:1','কোৱা, ‘তেওঁ আল্লাহ, এক-অদ্বিতীয়,');
INSERT INTO "habibur_rahman" VALUES(112,2,'112:2','আল্লাহ হৈছে ছামাদ; (তেওঁ কাৰো মুখাপেক্ষী নহয়, সকলো তেওঁৰেই মুখাপেক্ষী)');
INSERT INTO "habibur_rahman" VALUES(112,3,'112:3','তেওঁ কাকো জন্ম দিয়া নাই আৰু তেৱোঁ জন্ম লোৱা নাই,');
INSERT INTO "habibur_rahman" VALUES(112,4,'112:4','আৰু তেওঁৰ সমতুল্য কোনো নাই’।');
INSERT INTO "habibur_rahman" VALUES(113,1,'113:1','কোৱা, মই আশ্ৰয় বিচাৰো উষাৰ প্ৰতিপালকৰ।');
INSERT INTO "habibur_rahman" VALUES(113,2,'113:2','তেওঁ যি সৃষ্টি কৰিছে তাৰ অনিষ্টৰ পৰা,');
INSERT INTO "habibur_rahman" VALUES(113,3,'113:3','আৰু ৰাতিৰ অন্ধকাৰৰ অনিষ্টৰ পৰা, যেতিয়া ই গভীৰ হয়,');
INSERT INTO "habibur_rahman" VALUES(113,4,'113:4','আৰু সেইসকল নাৰীৰ অনিষ্টৰ পৰা যিসকলে গাঁঠিত ফুঁ দিয়ে,');
INSERT INTO "habibur_rahman" VALUES(113,5,'113:5','আৰু হিংসুকৰ অনিষ্টৰ পৰা, যেতিয়া সি হিংসা কৰে।');
INSERT INTO "habibur_rahman" VALUES(114,1,'114:1','কোৱা- মই মানুহৰ প্ৰতিপালকৰ আশ্ৰয় প্ৰাৰ্থনা কৰিছোঁ');
INSERT INTO "habibur_rahman" VALUES(114,2,'114:2','মানুহৰ অধিপতিৰ,');
INSERT INTO "habibur_rahman" VALUES(114,3,'114:3','মানুহৰ ইলাহৰ ওচৰত,');
INSERT INTO "habibur_rahman" VALUES(114,4,'114:4','আত্মগোপনকাৰী কুমন্ত্ৰণাদাতাৰ অনিষ্টৰ পৰা,');
INSERT INTO "habibur_rahman" VALUES(114,5,'114:5','যিয়ে মানুহৰ মনত কুমন্ত্ৰণা দিয়ে,');
INSERT INTO "habibur_rahman" VALUES(114,6,'114:6','জিনৰ মাজৰ পৰা আৰু মানুহৰ মাজৰ পৰা।');
