INSERT INTO "hasan_abdulkarim" VALUES(1,1,'1:1','Nhân danh Allah(, Đấng Rất Mực Độ Lượng, Đấng Rất Mực Khoan Dung(2)');
INSERT INTO "hasan_abdulkarim" VALUES(1,2,'1:2','Mọi lời ca tụng đều dâng lên Allah, Thượng Đế( của vũ trụ và muôn loài(4).');
INSERT INTO "hasan_abdulkarim" VALUES(1,3,'1:3','Đấng Rất Mực Độ Lượng, Đấng Rất Mực Khoan Dung.');
INSERT INTO "hasan_abdulkarim" VALUES(1,4,'1:4','Đức Vua( của Ngày Phán xử(6) (Cuối cùng).');
INSERT INTO "hasan_abdulkarim" VALUES(1,5,'1:5','(Ôi Allah) duy chỉ Ngài bầy tôi thờ phụng và chỉ với riêng Ngài bầy tôi cầu xin được giúp đỡ(.');
INSERT INTO "hasan_abdulkarim" VALUES(1,6,'1:6','Xin Ngài hướng dẫn bầy tôi (đi) theo con đường ngay chính.');
INSERT INTO "hasan_abdulkarim" VALUES(1,7,'1:7','Con đường của những người đã được Ngài ban ân(, không phải là (con đường của) những kẻ mà Ngài đã giận dữ và (cũng không phải là con đường của) những ai lầm đường lạc lối.');
INSERT INTO "hasan_abdulkarim" VALUES(2,1,'2:1','Alif. La-m. Mi-m(');
INSERT INTO "hasan_abdulkarim" VALUES(2,2,'2:2','Đây là Kinh Sách, không có gì phải ngờ vực cả, (dùng làm) Chỉ Đạo( cho những người ngay chính sợ Allah(11).');
INSERT INTO "hasan_abdulkarim" VALUES(2,3,'2:3','Những ai tin điều vô hình(, chu đáo dâng lễ Salah(12) và chi dùng những vật mà TA (Allah) đã cung cấp.');
INSERT INTO "hasan_abdulkarim" VALUES(2,4,'2:4','Và những ai tin tưởng nơi những điều (mặc khải) đã được ban xuống cho Ngươi (Muhammad) và những điều đã được ban xuống vào thời trước Ngươi; và họ tin chắc chắn nơi đời sau.');
INSERT INTO "hasan_abdulkarim" VALUES(2,5,'2:5','Họ là những người theo đúng sự Dẫn dắt do Thượng Đế (Allah) của họ (ban xuống) và là những người sẽ thành đạt');
INSERT INTO "hasan_abdulkarim" VALUES(2,6,'2:6','Quả thật, đối với những kẻ không có đức tin(, dù Ngươi có cảnh cáo hay không cảnh cáo chúng, điều đó bằng thừa đối với chúng bởi vì chúng nhất định không tin gì cả.');
INSERT INTO "hasan_abdulkarim" VALUES(2,7,'2:7','Allah đã niêm kín tấm lòng của chúng và lấy tấm màn bịt tai và mắt của chúng lại; và chúng sẽ chịu một sự trừng phạt hết sức to lớn.');
INSERT INTO "hasan_abdulkarim" VALUES(2,8,'2:8','Và trong nhân loại có những người nói: “Chúng tôi tin tưởng nơi Allah và Ngày (Phán xử) Cuối cùng” nhưng thật ra chúng không tin gì cả.');
INSERT INTO "hasan_abdulkarim" VALUES(2,9,'2:9','Chúng dối Allah và những người tin tưởng nhưng thực sự chúng chỉ dối bản thân mình nhưng chúng không nhận thấy (điều đố).');
INSERT INTO "hasan_abdulkarim" VALUES(2,10,'2:10','Trong lòng của chúng có một chứng bệnh (giả dối). Allah làm cho bệnh tình của chúng thêm trầm trọng và chúng sẽ bị trừng phạt đau đớn vì tội nói dối.');
INSERT INTO "hasan_abdulkarim" VALUES(2,11,'2:11','Và khi có lời bảo chúng: “Chớ làm điều ác đức trên trái đất” thì chúng đáp: “Chúng tôi chỉ là những người cải thiện cho tốt.”');
INSERT INTO "hasan_abdulkarim" VALUES(2,12,'2:12','Này, chắc chắn chúng là những kẻ ác đức, nhưng chúng không nhận thấy (điều đó).');
INSERT INTO "hasan_abdulkarim" VALUES(2,13,'2:13','Và khi có lời bảo chúng: “Các ngươi hãy tin tưởng như mọi người tin tưởng” thì chúng đáp: “Há chúng tôi tin giống như những kẻ đần độn tin hay sao?” Này, chắc chắn chúng là những kẻ đần độn, nhưng chúng không biết (đó thôi).');
INSERT INTO "hasan_abdulkarim" VALUES(2,14,'2:14','Và khi gặp những ai có đức tin, chúng bảo: “Chúng tôi tin tưởng.” Nhưng khi ở riêng với những tên Shaytan( của bọn chúng, chúng bảo: “Chúng tôi theo quí ngài. Chúng tôi chỉ chế giễu họ.”');
INSERT INTO "hasan_abdulkarim" VALUES(2,15,'2:15','Allah chế giễu lại chúng và buông cho chúng lang thang vơ vẩn trong sự thái quá.');
INSERT INTO "hasan_abdulkarim" VALUES(2,16,'2:16','Chúng là những kẻ đã mua lấy sự lầm lạc thay vì theo Chỉ Đạo, nhưng cuộc đổi chác của chúng chẳng có lợi gì và chúng cũng không được hướng dẫn đi đúng đường.');
INSERT INTO "hasan_abdulkarim" VALUES(2,17,'2:17','Thí dụ của chúng giống hình ảnh của một người nhúm lên một ngọn lửa, và khi ngọn lửa soi sáng mọi vật xung quanh người (nhúm lửa), thì Allah lấy mất ánh sáng của chúng, bỏ chúng ở lại trong tăm tối, khiến chúng không nhìn thấy gì cả.');
INSERT INTO "hasan_abdulkarim" VALUES(2,18,'2:18','(Giống kẻ) điếc, câm và mù, chúng sẽ không quay về (với Chính Đạo).');
INSERT INTO "hasan_abdulkarim" VALUES(2,19,'2:19','Hoặc giống như một lớp mây sắp cho mưa từ trên trời, trong đó vừa là âm u, vừa là sấm sét và tia chớp. Chúng lấy ngón tay bịt tai lại để đừng nghe tiếng sấm sét, điếng cả người vì sợ chết. Và Allah bao vây những kẻ không có đức tin.');
INSERT INTO "hasan_abdulkarim" VALUES(2,20,'2:20','Tia chớp làm cho chúng lóe mắt không nhìn thấy gì, giống như cảnh khi ánh sáng lóe lên chúng lần mò bước đi trong đó; và khi trời tối đen như mực thì chúng đứng im một chỗ. Và nếu Allah muốn, Ngài sẽ lấy đi thính giác và thị giác của chúng bởi vì Allah có quyền chi phối trên tất cả vạn vật.');
INSERT INTO "hasan_abdulkarim" VALUES(2,21,'2:21','Hỡi nhân loại! Hãy thờ phụng Thượng Đế (Allah) của các ngươi, Đấng đã tạo các ngươi và những ai trước các ngươi để các ngươi trở thành những người ngay chính sợ Allah.');
INSERT INTO "hasan_abdulkarim" VALUES(2,22,'2:22','Đấng đã tạo trái đất như một tấm thảm và bầu trời như một chiếc lọng che cho các ngươi và ban nước mưa từ trên trời xuống mà Ngài dùng để làm mọc ra cây trái thành thực phẩm cho các ngươi (dùng). Bởi thế, chớ dựng những đối tác ngang vai( với Allah trong lúc các ngươi biết (điều đó).');
INSERT INTO "hasan_abdulkarim" VALUES(2,23,'2:23','Và nếu các ngươi nghi ngờ về những điều (mặc khải) mà TA (Allah) đã ban xuống cho người bề tôi (Muhammad) của TA thì hãy mang đến một Chương (Surah)( tương tự như cái của Nó (Qur''an) và hãy gọi những nhân chứng của các ngươi ngoài Allah đến xác nhận nếu các ngươi nói thật.');
INSERT INTO "hasan_abdulkarim" VALUES(2,24,'2:24','Nhưng nếu các ngươi không thể làm được và chắc chắn các ngươi sẽ không bao giờ làm nổi thì hãy sợ ngọn Lửa mà chất đốt của nó là con người và đá được chuẩn bị sẵn cho những kẻ phủ nhận đức tin.');
INSERT INTO "hasan_abdulkarim" VALUES(2,25,'2:25','Và hãy báo cho những ai có đức tin và làm việc thiện về việc họ sẽ được (Allah) ban thưởng những ngôi Vườn bên dưới có các dòng sông chảy. Mỗi lần họ được cung cấp trái cây của chúng làm thực phẩm, họ nói: “Đây là những món mà chúng tôi đã được cung cấp trước đây.” Và họ sẽ được ban cho những món ăn tương tự, và trong đó họ sẽ có những người vợ trong sạch; và sẽ ở trong đó đời đời.');
INSERT INTO "hasan_abdulkarim" VALUES(2,26,'2:26','Quả thật, Allah chẳng có gì e thẹn khi Ngài dùng những thí dụ để so sánh cho dù đó là một con muỗi hay một con vật to lớn hơn nó nữa. Bởi vì đối với những ai có đức tin thì biết đó là sự thật từ Thượng Đế của họ; ngược lại, đối với những ai không có đức tin thì (lấy cớ đó để) nói: “Allah ngụ ý muốn gì khi Ngài đưa ra thí dụ so sánh này?” Do nó, Ngài làm cho nhiều người đi lạc và cũng bởi nó, Ngài dắt nhiều người đi đúng đường; nhưng Ngài không dùng nó để đưa ai đi lạc cả ngoại trừ những kẻ bất tuân vượt quá mức giới hạn:');
INSERT INTO "hasan_abdulkarim" VALUES(2,27,'2:27','Những ai vi phạm Giao Ước( của Allah sau khi đã được phê chuẩn, cắt đứt những gì mà Allah đã ra lệnh phải duy trì và gieo rắc điều thối nát trên trái đất. Họ là những kẻ mất mát.');
INSERT INTO "hasan_abdulkarim" VALUES(2,28,'2:28','Làm sao các ngươi có thể phủ nhận Allah( trong lúc thấy các ngươi đã chết, Ngài đã làm cho các ngươi sống, rồi Ngài sẽ làm cho các ngươi chết, rồi sẽ dựng các ngươi sống lại, rồi các ngươi sẽ trở về gặp Ngài (để chịu sự xét xử)?');
INSERT INTO "hasan_abdulkarim" VALUES(2,29,'2:29','Ngài là Đấng đã tạo cho các ngươi tất cả mọi vật dưới đất rồi hướng về bầu trời Ngài hoàn chỉnh chúng thành bảy tầng trời; và Ngài biết hết mọi vật.');
INSERT INTO "hasan_abdulkarim" VALUES(2,30,'2:30','Và khi Thượng Đế (Allah) của Ngươi phán bảo các Thiên Thần: ‘TA sẽ đặt một Đại diện của TA( trên trái đất.” (Các Thiên Thần) thưa: “Phải chăng Ngài đặt một nhân vật sẽ hành động thối nát và làm đổ máu nơi đó, trong lúc bầy tôi tán dương ca tụng và thánh hóa Ngài hay sao?” (Allah) phán: "TA biết điều mà các ngươi không biết."');
INSERT INTO "hasan_abdulkarim" VALUES(2,31,'2:31','Và (Allah) dạy Adam tên gọi (của) tất cả (vạn vật). Rồi Ngài đặt (mọi vật) ra trước mặt các Thiên thần, và phán: “Các ngươi hãy cho TA biết tên của những vật này nếu các ngươi chân thật.”');
INSERT INTO "hasan_abdulkarim" VALUES(2,32,'2:32','(Các Thiên Thần) thưa: “Quang vinh thay Ngài! Bầy tôi không biết điều gì ngoài điều mà Ngài đã dạy bầy tôi. Quả thật, duy chỉ Ngài là Đấng Biết hết, Đấng Rất Mực Sáng Suốt.”');
INSERT INTO "hasan_abdulkarim" VALUES(2,33,'2:33','(Allah) phán: “Này Adam! Nhà ngươi cho (Thiên Thần) biết tên gọi của chúng (vạn vật).” Bởi thế, khi (Adam) cho (Thiên Thần) biết tên gọi của chúng (vạn vật), (Allah) phán: “Há TA đã không báo cho các ngươi rõ TA biết điều vô hình của các tầng trời và trái đất và TA biết điều các ngươi tiết lộ và giấu giếm hay sao?”');
INSERT INTO "hasan_abdulkarim" VALUES(2,34,'2:34','Và hãy nhớ lại khi TA (Allah) phán cho các Thiên Thần: “Hãy phủ phục( trước Adam!” Bởi thế, chúng phủ phục ngoại trừ Iblis(21) đã từ chối và ngạo mạn. Và nó trở thành một tên phản nghịch.');
INSERT INTO "hasan_abdulkarim" VALUES(2,35,'2:35','Và TA (Allah) phán (cho Adam): “Này Adam! Ngươi và vợ của Ngươi hãy ở trong thiên đàng và tha hồ ăn (hoa quả) dồi dào của nó bất cứ nơi nào tùy ý hai ngươi muốn, nhưng chớ đến gần cái ''Cây'' này bởi vì (nếu ăn trái của nó) hai ngươi sẽ trở thành những kẻ làm điều sai quấy.”');
INSERT INTO "hasan_abdulkarim" VALUES(2,36,'2:36','Nhưng Shaytan đã làm cho hai (vợ chồng Adam) rơi xuống khỏi đó (thiên đàng) và làm cho hai người rời khỏi (tình trạng sung sướng) mà họ đã từng hưởng trong đó. Và TA phán: “Hãy đi xuống (tất cả vợ chồng Adam và Iblis)! Đứa này sẽ là kẻ thù của đứa kia. Và trên trái đất các ngươi sẽ có một nơi ở (tạm) và một cuộc sống (tạm) suốt một thời gian.”');
INSERT INTO "hasan_abdulkarim" VALUES(2,37,'2:37','Sau đó, Adam học được từ Thượng Đế của Người Lời (Mặc khải) bởi vì Ngài xót thương đoái nhìn Người trở lại. Quả thật, Ngài là Đấng Hằng Quay Lại Tha thứ, Đấng Rất Mực Khoan Dung.');
INSERT INTO "hasan_abdulkarim" VALUES(2,38,'2:38','TA (Allah) phán: ‘Tất cả hãy đi xuống( khỏi đó (thiên đàng). Nhưng nếu có Chỉ Đạo(23) từ TA đến cho các ngươi; ai tuân theo Chỉ Đạo của TA thì sẽ không lo sợ cũng sẽ không buồn phiền.');
INSERT INTO "hasan_abdulkarim" VALUES(2,39,'2:39','Ngược lại, ai phủ nhận (Chỉ Đạo của TA) và cho những Lời Mặc Khải cua TA là điều giả tạo thì sẽ làm bạn với Lửa (của hỏa ngục) và sẽ ở trong đó đời đời.');
INSERT INTO "hasan_abdulkarim" VALUES(2,40,'2:40','Hỡi con cháu của Israel(! Hãy nhớ Ân huệ mà TA (Allah) đã ban cho các ngươi và hãy làm tròn Lời Giao ước của các ngươi với TA và TA sẽ làm tròn Lời Giao Ước của TA với các ngươi; và các ngươi hãy chỉ sợ riêng TA thôi.');
INSERT INTO "hasan_abdulkarim" VALUES(2,41,'2:41','Và hãy tin tưởng nơi những điều TA ban xuống nhằm xác nhận lại những điều đang ở (trong tay) của các ngươi và chớ là người đầu tiên phủ nhận (Thông điệp của TA) và chớ bán các Lời Mặc Khải của TA với một giá nhỏ nhoi; và các ngươi hãy chỉ sợ riêng TA thôi.');
INSERT INTO "hasan_abdulkarim" VALUES(2,42,'2:42','Và chớ trộn lẫn sự thật với sự giả dối và giấu giếm sự thật trong lúc các ngươi biết (điều đó).');
INSERT INTO "hasan_abdulkarim" VALUES(2,43,'2:43','Và hãy dâng lễ Salah một cách chu đáo và đóng Zakah (thuế cho người nghèo) và cúi đầu cùng với những người cúi đầu (thần phục ngài trong Salah).');
INSERT INTO "hasan_abdulkarim" VALUES(2,44,'2:44','Phải chăng các ngươi bảo mọi người cư xử đạo đức nhưng tự mình quên làm nó trong lúc các ngươi đọc rõ Kinh Sách (hơn ai)? Thế phải chăng các ngươi không hiểu?');
INSERT INTO "hasan_abdulkarim" VALUES(2,45,'2:45','Và hãy cầu xin được giúp đỡ trong niềm kiên nhẫn và với việc lễ nguyện Salah; và quả thật điều này rất khó nhưng không mấy khó đối với những người hạ mình khiêm tốn (trước Allah):');
INSERT INTO "hasan_abdulkarim" VALUES(2,46,'2:46','Những ai nghĩ rằng họ sẽ gặp Thượng Đế của họ và sẽ trở về gặp Ngài (ở đời sau)');
INSERT INTO "hasan_abdulkarim" VALUES(2,47,'2:47','Hỡi con cháu của Israel! Hãy nhớ Ân huệ mà TA đã ban cho các ngươi và (việc) TA ưu đãi các ngươi hơn thiên hạ.');
INSERT INTO "hasan_abdulkarim" VALUES(2,48,'2:48','Và hãy sợ một Ngày mà không một linh hồn nào có thể giúp ích được cho một linh hồn nào khác; và sẽ không có một sự can thiệp nào được chấp nhận giùm cho nó và sẽ không có một sự bồi thường nào được chấp nhận từ nó và họ sẽ không được (ai) giúp đỡ.');
INSERT INTO "hasan_abdulkarim" VALUES(2,49,'2:49','Và hãy nhớ lại khi TA (Allah) đã cứu các ngươi thoát khỏi đám thuộc hạ của Fir-''awn(; chúng áp bức các ngươi bằng cực hình tàn ác: chúng giết con trai của các ngươi và tha mạng cho các phụ nữ của các ngươi; và có một sự thử thách lớn lao từ Thượng Đế của các ngươi trong sự việc đó.');
INSERT INTO "hasan_abdulkarim" VALUES(2,50,'2:50','Và hãy nhớ lại khi TA đã rẽ biển (Hồng hải) ra làm hai (để) cho các ngươi (chạy băng qua bờ phía bên kia) và TA đã cứu sống các ngươi và đã nhận đám thuộc hạ của Fir-''awn chết chìm (dưới biển) trong lúc các ngươi đứng nhìn rõ (cảnh tượng);');
INSERT INTO "hasan_abdulkarim" VALUES(2,51,'2:51','Và hãy nhớ lại khi TA đã hẹn gặp Musa trong (thời gian) bốn mươi đêm rồi trong thời gian Y vắng mặt, các ngươi đã mang con bò con (đúc) ra thờ và các ngươi đã làm một điều hết sức sai quấy.');
INSERT INTO "hasan_abdulkarim" VALUES(2,52,'2:52','Rồi sau việc làm (sai quấy) đó, TA đã lượng thứ cho các ngưoi để cho các ngươi có dịp tỏ lòng biết ơn (TA).');
INSERT INTO "hasan_abdulkarim" VALUES(2,53,'2:53','Và hãy nhớ lại khi TA đã ban cho Musa Kinh Sách và Chuẩn mực (dùng phân biệt Phúc và Tội) để cho các ngươi có cơ hội được hướng dẫn (theo Chính Đạo).');
INSERT INTO "hasan_abdulkarim" VALUES(2,54,'2:54','Và hãy nhớ lại khi Musa đã nói với đám dân của Y: “Này hỡi dân ta! Quả thật, các người đã tự làm hại bản thân mình qua việc các người đã thờ con bò con; bởi thế hãy quay về sám hối với Thượng Đế của các người. Do đó, hãy tự giết (những tên tội lỗi của) các người. Điều đó tốt cho các người hơn dưới cái nhìn của Thượng Đế của các người.” Tiếp đó Ngài quay lại tha thứ cho các ngươi bởi vì quả thật Ngài là Đấng Hằng Tha Thứ, Đấng Rất Mực Khoan Dung.');
INSERT INTO "hasan_abdulkarim" VALUES(2,55,'2:55','Và hãy nhớ lại khi các ngươi đã nói với Musa: “Chúng tôi sẽ không bao giờ tin tưởng thầy trừ phi chúng tôi thấy Allah công khai.” Vì thế, lưỡi tầm sét đã đánh các ngươi trong lúc các ngươi mục kích rõ (cảnh tượng).');
INSERT INTO "hasan_abdulkarim" VALUES(2,56,'2:56','Rồi TA làm cho các ngươi sống lại sau cái chết của các ngươi để cho các ngươi còn có dịp tạ ơn (Allah).');
INSERT INTO "hasan_abdulkarim" VALUES(2,57,'2:57','Và TA đã đưa lùm mây đến che mát cho các ngươi (nơi sa mạc) và TA đã ban lộc và Chim cút xuống cho các ngươi (và phán): “Hãy ăn những món (thực phẩm) tốt và sạch mà TA cung cấp cho các người.” (Nhưhg họ không hài lòng với các món đó). Và (việc bất mãn của) họ không làm thiệt hại TA ; ngược lại, họ tự làm hại bản thân mình mà thôi.');
INSERT INTO "hasan_abdulkarim" VALUES(2,58,'2:58','Và hãy nhớ lại khi TA đã phán: “Hãy vào thị trấn (Jerusalem) này và ăn tùy thích thực phẩm dồi dào của nó ở bất cứ nơi nào và hãy bước vào cửa với dáng điệu phủ phục và thưa: ‘Xin Ngài tha thứ cho bầy tôi’, TA sẽ tha thứ những lỗi lầm của các ngươi và sẽ ân thưởng thêm cho những người làm tốt.');
INSERT INTO "hasan_abdulkarim" VALUES(2,59,'2:59','Nhưng những kẻ làm điều sai quấy đã thay đổi Lời (Mặc khải) sai với lời đã được truyền xuống cho chúng. Bởi thế, TA đã ban tai họa từ trên trời xuống trừng phạt những kẻ làm điều sai quấy vì tội chúng đã bất tuân và vượt quá mức giới hạn.');
INSERT INTO "hasan_abdulkarim" VALUES(2,60,'2:60','Và hãy nhớ lại khi Musa cầu xin nước uống cho người dân của Người. TA (Allah) phán: “Hãy dùng Chiếc gậy của Ngươi đánh lên tảng đá. ” Thế là từ đó phun ra mười hai mạch nước suối (cho mười hai bộ lạc của Israel)(. Mỗi bộ lạc đều biết điểm nước của họ. Hãy ăn (thực phẩm) và uống (nước) do Allah ban cho và chớ làm điều ác đức và thối nát trên trái đất.');
INSERT INTO "hasan_abdulkarim" VALUES(2,61,'2:61','Và hãy nhớ lại khi các ngươi đã bảo Musa: “Hỡi Musa! Chúng tôi không thể tiếp tục cam chịu với một loại thức ăn (này mãi). Bởi thế, xin thầy hãy cầu xin Thượng Đế (Allah) của thầy giùm chúng tôi để Ngài làm mọc ra từ đất trồng những loại thực phẩm như: rau tươi, dưa chuột, tỏi, đậu lăng-ti và hành tây của nó.” (Musa) đáp: “Phải chăng các người muốn đổi cái tốt lấy cái tệ hay sao? Hãy đi đến bất cứ thị trấn nào (của Ai Cập), các người sẽ có được những món mà các người đòi hỏi.” Và họ đã bị hạ nhục và nghèo khó và tự rước vào thân sự giận dữ của Allah. Sở dĩ như thế là vì họ đã từng phủ nhận các Lời Mặc Khải của Allah và đã giết các Nabi( của Allah không có lý do chính đáng. Như thế là vì họ bất tuân Allah và hằng vượt quá mức giới hạn (qui định bởi Allah)');
INSERT INTO "hasan_abdulkarim" VALUES(2,62,'2:62','Quả thật, những ai tin tưởng (nơi Qur''an) và những ai là người Do thái và tín đồ của Ki-Tô giáo và nhữhg người Sabian(, ai tin tưởng nơi Allah và Ngày (Phán xử) Cuối cùng và làm việc thiện thì sẽ có phần thưởng của họ nơi Thượng Đế của họ; và họ sẽ không lo sợ cũng sẽ không buồn phiền.');
INSERT INTO "hasan_abdulkarim" VALUES(2,63,'2:63','Và hãy nhớ lại khi TA (Allah) đã nhận lời Giao ước của các ngươi và nhấc ngọn núi (tại Sinai) cao khỏi đầu của các ngươi (và phán): “Hãy nắm vững những điều TA đã ban cho các ngươi (trong Taurah) và hãy nhớ cho kỹ những điều ghi trong đó để may ra các ngươi sẽ trở thành những người ngay chính sợ Allah.”');
INSERT INTO "hasan_abdulkarim" VALUES(2,64,'2:64','Rồi sau đó các ngươi quay lưng làm ngơ. Nếu Allah không đặc ân và khoan dung thì chắc chắn các ngươi đã trở thành những kẻ mất mát rồi.');
INSERT INTO "hasan_abdulkarim" VALUES(2,65,'2:65','Và các ngươi dư biết ai trong các ngươi là những kẻ vi phạm (giới cấm của) Ngày Thứ bảy(. Bởi thế, TA (Allah) đã phán cho chúng: “Hãy thành loài khỉ đáng khinh!”');
INSERT INTO "hasan_abdulkarim" VALUES(2,66,'2:66','Bởi thế, TA (Allah) đã dùng nó (hình phạt) để làm ngã lòng những kẻ sống vào thời đại của chúng và những ai sống sau chúng và làm một bài học cho những người ngay chính sợ Allah.');
INSERT INTO "hasan_abdulkarim" VALUES(2,67,'2:67','Và hãy nhớ lại khi Musa nói với người dân của Y: “Quả thật, Allah ra lệnh cho các người tế một con bò cái tơ.” Họ bảo: “Phải chăng thầy mang chúng tôi ra làm trò cười hay sao?” (Musa) đáp: ‘Ta xin Allah che chở, chớ để ta thành một người ngu muội.”');
INSERT INTO "hasan_abdulkarim" VALUES(2,68,'2:68','Họ bảo: “Thầy hãy cầu xin Thượng Đế của Thầy giùm chúng tôi, cho chúng tôi biết rõ nó là gì?” (Musa) đáp: “Ngài (Allah) phán: “Nó là một con bò cái không già lắm cũng không non lắm, cỡ tuổi trung bình. Nào, hãy làm theo điều Ngài ra lệnh.”');
INSERT INTO "hasan_abdulkarim" VALUES(2,69,'2:69','Họ bảo: “Thầỵ hãy cầu xin Thượng Đế của Thầy giùm chúng tôi, cho chúng tôi biết rõ màu da của nó là gì.” (Musa) đáp: “Ngài (Allah) phán: “Nó là một con bò cái màu da vàng hung, làm cho ngươi xem thích thú.”');
INSERT INTO "hasan_abdulkarim" VALUES(2,70,'2:70','Họ bảo: “Thầy hãy cầu xin Thượng Đế của Thầy giùm chúng tôi, cho chúng tôi biết rõ nó là gì bởi vì đối với chúng tôi các con bò cái đều giống nhau, dễ nhầm lẫn. Lần này nếu Allah muốn, chúng tôi sẽ được chỉ dẫn đúng đắn.”');
INSERT INTO "hasan_abdulkarim" VALUES(2,71,'2:71','(Musa) đáp: “Ngài (Allah) phán: “Nó là một con bò cái không bị bắt cày đất cũng không dùng để kéo nước vào ruộng, khỏe mạnh và nguyên lành (không có gì khiếm khuyết cả).” Họ bảo: “Bây giờ Thầy trình bày rõ sự thật.” Và họ đã tế nó nhưng hầu như không muốn làm.');
INSERT INTO "hasan_abdulkarim" VALUES(2,72,'2:72','Và hãy nhớ lại khi các ngươi đã giết một sinh mạng nhưng cãi nhau về nó (vụ án mạng). Và Allah đưa ra (ánh sáng) điều (bí ẩn) mà các ngươi đã giấu giếm.');
INSERT INTO "hasan_abdulkarim" VALUES(2,73,'2:73','Bởi thế, TA (Allah) đã phán: “Hãy đánh nó (người chết) với một bộ phận của nó (con bò cái đã tế).” Bằng cách đó, Allah làm cho người chết sông lại (và khai tên của thủ phạm trong vụ án mạng) và làm cho các ngươi thấy Dấu lạ của Ngài (Allah) để may ra các ngươi hiểu rõ (sự tình của vụ sát nhân).');
INSERT INTO "hasan_abdulkarim" VALUES(2,74,'2:74','Rồi kể từ sau đó, quả tim (tấm lòng) của các ngươi chai cứng lại như đá hoặc cứng dữ hơn. Bởi vì, có loại đá từ đó nước suối (sông) phun ra và cũng có loại đá nứt làm hai để nước trong đó chảy ra; và cũng có những loại đá vì sợ Allah mà rơi xuống. Và Allah không làm ngơ trước những điều các ngươi làm.');
INSERT INTO "hasan_abdulkarim" VALUES(2,75,'2:75','(Hỡi những ai có đức tin!) Phải chăng các ngươi nuôi hy vọng rằng họ (những người Do thái) sẽ tin tưởng các ngươi hay sao? Và chắc chắn một thành phần của họ (Thầy tu Do thái) đã nghe Lời phán của Allah rồi cố tình làm hỏng ý nghĩa của nó sau khi đã hiểu rõ nó trong lúc họ biết (điều họ làm).');
INSERT INTO "hasan_abdulkarim" VALUES(2,76,'2:76','Và khi gặp những ai có đức tin, họ bảo: “Chúng tôi tin tưởng,” Nhưng khi gặp riêng nhau, họ bảo: “Quí vị có cho (những người Muslim) biết những điều mà Allah đã tiết lộ cho quí vị (trong Taurah) hay không để họ (người Muslim) có dịp tranh luận với quí vị về điều đó trước mặt Thượng Đế của quí vị?” Thế phải chăng các người không hiểu?');
INSERT INTO "hasan_abdulkarim" VALUES(2,77,'2:77','Và há họ (người Do thái) không biết rằng Allah biết điều họ giấu giếm và điều họ tiết lộ?');
INSERT INTO "hasan_abdulkarim" VALUES(2,78,'2:78','Và trong bọn họ có những tên mù chữ không biết Kinh Sách là gì ngoại trừ điều mong ước hão huyền và chỉ phỏng đoán.');
INSERT INTO "hasan_abdulkarim" VALUES(2,79,'2:79','Bởi thế, khốn nạn cho những ai đã tự tay mình viết Kinh Sách rồi bảo: “Đây là Kinh Sách do Allah (ban xuống),” hầu mang nó đi bán với một giá nhỏ nhoi. Bởi thế, khốn nạn cho họ về những điều mà bàn tay của họ đã viết ra và khến nạn cho họ về cái (lợi lộc) mà họ đã kiếm được (từ đó).');
INSERT INTO "hasan_abdulkarim" VALUES(2,80,'2:80','Và họ (người Do thái) bảo: “Lửa (của hỏa ngục) chỉ chạm phải chúng tôi trong một số ngày nhất định.” Hãy bảo họ (hỡi Muhammad!): “Phải chăng các người đã nhận từ Allah một lời Giao ước bởi vì Allah không bao giờ vi phạm Lời Giao ước của Ngài? Hoặc phải chăng các người đã đổ thừa cho Allah điều mà các người không biết?”');
INSERT INTO "hasan_abdulkarim" VALUES(2,81,'2:81','Vâng, ai chuốc tội và tội quấn lấy thân thì chắc chắn sẽ là những người bạn của Lửa; họ sẽ ở trong đó đời đời.');
INSERT INTO "hasan_abdulkarim" VALUES(2,82,'2:82','Ngược lại, những ai có đức tin và làm việc thiện thì sẽ là những người Bạn của thiên đàng, trong đó họ sẽ vào ở đời đời.');
INSERT INTO "hasan_abdulkarim" VALUES(2,83,'2:83','Và hãy nhớ lại khi TA (Allah) đã nhận Lời Giao ước từ con cháu của Israel (phán bảo): ”Các ngươi không được tôn thờ ai khác mà chỉ (tôn thờ) riêng Allah thôi và ăn ở tử tế với cha mẹ và bà con ruột thịt và các trẻ mồ côi và người thiếu thốn và ăn nói lễ độ với mọi người và chu đáo dâng lễ Salah và đóng Zakah rồi các ngươi quay lưng làm ngơ trừ một thiểu số trong các ngươi và các ngươi là những kẻ sa ngã (cho đến nay).”');
INSERT INTO "hasan_abdulkarim" VALUES(2,84,'2:84','Và hãy nhớ lại khi TA (Allah) đã nhận Lời Giao ước của các ngươi (phán bảo): “Chớ làm đổ máu của các ngươi và chớ xua đuổi nhau ra khỏi nhà cửa của các ngươi rồi các ngươi long trọng xác nhận (Lời Giao ước) và các ngươi là nhân chứng (của nó).”');
INSERT INTO "hasan_abdulkarim" VALUES(2,85,'2:85','Rồi cũng chính các ngươi lại giết hại lẫn nhau và xua đuổi một thành phần của các ngươi ra khỏi nhà của họ, tiếp tay (cho kẻ thù) chống lại họ trong tội lỗi và hận thù; và nếu họ rơi vào tay của các ngươi như tù binh thi các ngươi đòi họ chuộc mạng mặc dù các ngươi không được phép trục xuất họ. Phải chăng các ngươi chỉ tin một phần Kinh Sách và phủ nhận phần còn lại? Bởi thế, phần phạt dành cho ai trong các ngươi làm điều đó không gì khác hơn là sự nhục nhã ở đời này; và vào Ngày phục sinh, họ sẽ nhận một sự trừng phạt khủng khiếp hơn bởi vì Allah không làm ngơ trước những điều các ngươi làm.');
INSERT INTO "hasan_abdulkarim" VALUES(2,86,'2:86','Họ là những kẻ đã mua đời này với giá của đời sau. Bởi thế, việc trừng phạt họ sẽ không được giảm nhẹ và họ sẽ không được ai cứu giúp.');
INSERT INTO "hasan_abdulkarim" VALUES(2,87,'2:87','Và chắc chắn TA (Allah) đã ban cho Musa Kinh Sách và đã cử một loạt các Sứ Giả sau Y. TA đã ban cho Ysa con trai của Maryam những bằng chứng rõ rệt và hỗ trợ Y với Thiên Thần Jibril. Phải chăng mỗi lần một Sứ Giả (của TA) đến gặp các ngươi mang theo điều mà bản thân của các ngươi không thích, các ngươi tỏ ra ngạo mạn? Rồi các ngươi cho một số (Sứ Giả của TA) nói dốì còn một số khác thì các ngươi đã giết đi?');
INSERT INTO "hasan_abdulkarim" VALUES(2,88,'2:88','Và họ nói: “Quả tim của chúng tôi là những cái bọc kín (chứa Lời truyền của Allah, chúng tôi không cần thêm điều nào khác).” Không, Allah nguyền rủa họ vì tội không tin của họ. Bởi thế, điều mà họ tin tưởng thật là ít.');
INSERT INTO "hasan_abdulkarim" VALUES(2,89,'2:89','Và khi một Kinh Sách từ Allah đến với họ, xác nhận điều họ đang giữ (trong Kinh Tawrah và Injĩl) và mặc dù trước đó họ đã hằng cầu nguyện cho được thắng những ai không có đức tin (với sự xuất hiện của một vị Nabi) nhưng khi điều mà họ công nhận đúng đã đến với họ thì họ lại phủ nhận. Bởi thế, Allah nguyền rủa những kẻ không có đức tin.');
INSERT INTO "hasan_abdulkarim" VALUES(2,90,'2:90','Và tồi tệ thay điều vì nó mà họ đã bán rẻ linh hồn của họ, rằng vì lòng đố kỵ mà họ không tin nơi điều (Mặc khải) do Allah ban xuống. (Họ viện cớ nói) rằng đáng lý ra Allah với lòng ưu ái của Ngài nên ban sự Mặc khải xuống cho ai mà Ngài muốn trong số bầy tôi của Ngài (thay vì cho Muhammad). Bởi thế, họ tự chuốc lấy sự Giận dữ này đến sự Giận dữ khác (của Allah). Và những kẻ không có đức tin sẽ nhận một sự trừng phạt nhục nhã.');
INSERT INTO "hasan_abdulkarim" VALUES(2,91,'2:91','Và khi có lời bảo họ: “Hãy tin tưởng nơi điều Ạllah ban xuống (cho Muhammad),” họ đáp: “Chúng tôi tin nơi điều đã được ban xuống cho chúng tôi (trong Tawrah)” và họ không tin nơi điều nào khác sau Nó (Tawrah), trong lúc Nó (Qur''an) là sự Thật xác nhận lại điều họ đang giữ. Hãy bảo họ: “Thế tại sao các ngươi đã giết các Nabi của Allah trước đây nếu các người thực sự có đức tin?”');
INSERT INTO "hasan_abdulkarim" VALUES(2,92,'2:92','Và quả thật Musa đã đến gặp các ngươi với những bằng chứng rõ rệt rồi sau (khi) Y (đi vắng), các ngươi đã mang con bò con (đúc) ra thờ và các ngươi là những kẻ làm điều sai quấy.');
INSERT INTO "hasan_abdulkarim" VALUES(2,93,'2:93','Và hãy nhớ lại khi TA (Allah) đã nhận Lời Giao ước của các ngươi và đã nhấc ngọn núi (ở Sinai) cao khỏi đầu của các ngươi (và phán); “Hãy nắm thật vững điều (Mặc khải) mà TA đã ban cho các ngươi và hãy nghe theo (Lời phán). Họ đáp: “Chúng tôi nghe nhưng chúng tôi không tuân theo.” Và vì không có đức tin, nên (hình ảnh của) con bò con (đúc) đã thấm sâu vào quả tim (tấm lòng) của họ. Hãy bảo họ: “Tồi tệ thay điều mà đức tin của các ngươi đã sai bảo các ngươi (làm) nếu các ngươi tin tưởng (nơi bất cứ điều gì).”');
INSERT INTO "hasan_abdulkarim" VALUES(2,94,'2:94','Hãy bảo họ: “Nếu nhà cửa ở đời sau nơi Allah chỉ đặc biệt dành riêng cho các ngươi chứ không cho ai khác trong nhân loại, thì hãy mong cho được chết sớm đi nếu các ngươi nói thật.”');
INSERT INTO "hasan_abdulkarim" VALUES(2,95,'2:95','Và nhất định họ sẽ không bao giờ mong được chết sớm bởi vì (họ sợ) những điều (tội lỗi) mà bàn tay của họ đã gởi đi trước và Allah biết rõ những kẻ làm điều sai quấy.');
INSERT INTO "hasan_abdulkarim" VALUES(2,96,'2:96','Và chắc chắn Ngươi sẽ thấy trong loài người, họ là những kẻ tham sống nhất, (tham sống) hơn cả người dân đa thần. Mỗi một người của họ đều ao ước được sống thọ bằng một ngàn năm nhưng tuổi thọ đó chẳng cứu được họ thoát khỏi sự trừng phạt của Allah bởi vì Allah là Đấng hằng thấy những điều (tội lỗi) mà họ đã làm.');
INSERT INTO "hasan_abdulkarim" VALUES(2,97,'2:97','Hãy bảo họ (hỡi Muhammad!): “Ai là kẻ thù của (Thiên Thần) Jibril bởi vì Y (Jibril) mang Nó (Qur''an) xuống đặt vào tấm lòng của Ngươi theo phép của Allah? Nhằm xác nhận những điều đã được ban xuống trước, vừa là một Chỉ Đạo vừa là một Tin mừng cho những người có đức tin.');
INSERT INTO "hasan_abdulkarim" VALUES(2,98,'2:98','Ai là kẻ thù của Allah và của các Thiên Thần và của các Sứ Giả (của Allah) và của (Thiên Thần) Jibril và của (Thiên Thần) Mika-il, thì Allah là kẻ thù của những kẻ không có đức tin đó.”');
INSERT INTO "hasan_abdulkarim" VALUES(2,99,'2:99','Và chắc chắn TA (Allah) đã ban xuống cho Ngươi (Muhammad) các Câu kinh rõ rệt nhưng chỉ những kẻ dấy loạn, bất tuân mới không tin.');
INSERT INTO "hasan_abdulkarim" VALUES(2,100,'2:100','Phải chăng mỗi lần họ giao ước một lời cam kết nào thì một thành phần của họ quẳng nó sang một bên? Không, đa số bọn họ không có đức tin.');
INSERT INTO "hasan_abdulkarim" VALUES(2,101,'2:101','Và mỗi lần một Sứ Giả của Allah đến gặp họ để xác nhận lại điều họ đang giữ thì một thành phần của những kẻ đã được ban cho Kinh Sách (người Do Thái và tín đồ Thiên Chúa giáo) đã quẳng Kinh Sách của Allah ra sau lưng, làm như họ không hay biết gì cả!');
INSERT INTO "hasan_abdulkarim" VALUES(2,102,'2:102','Ngược lại, họ nghe theo những điều mà những tên Shaytan đã đọc (lệch lạc) về quyền lực của Sulayman. Sulayman đã không phủ nhận đức tin; ngược lại, chỉ những tên Shaytan mới không tin tưởng: Chúng dạy loài người pháp thuật đã được ban xuống cho hai Thiên Thần Harut và Marut tại Babil (Ba-bi-lôn). Nhưng hai (Thiên Thần) này không dạy (pháp thuật) cho một ai mà không bảo trước: “Chúng tôi đây chỉ là một sự cám dỗ (để thử thách quí vị); do đó chớ phủ nhận đức tin.” Tuy nhiên, họ vẫn học từ hai vị Thiên Thần đó những điều (pháp thuật) mà họ dùng để làm chia ly cặp vợ chồng. Và họ không hại được một ai trừ phi có phép của Allah. Và họ học hỏi điều làm thiệt thân họ chứ không làm lợi gì cho họ. Bởi vì họ biết chắc rằng ai mua bán pháp thuật thì sẽ không được hưởng một phần (tốt) nào ở đời sau. Và tồi tệ thay giá cả (tiền bạc) vì nó mà họ đã bán rẻ linh hồn của họ. Phải chi họ biết điều đó!');
INSERT INTO "hasan_abdulkarim" VALUES(2,103,'2:103','Và nếu họ có đức tin và sợ Allah thì chắc chắn phần thưởng từ Allah sẽ tốt hơn nhiều. Phải chi họ biết điều đó!');
INSERT INTO "hasan_abdulkarim" VALUES(2,104,'2:104','Hỡi những ai có đức tin! Chớ dùng tiếng Rã''inã( (mà xưng hô với Sứ Giả của Allah) mà nên dùng tiếng Unzurnã (để xưng hô với Người) và hãy chú ý nghe Người (dạy). Và những kẻ không có đức tin sẽ bị trừng phạt đau đớn (vì tội phạm thượng đối với Sứ Giả của Allah).');
INSERT INTO "hasan_abdulkarim" VALUES(2,105,'2:105','Những kẻ không có đức tin, dù đó là thành phần của Người dân Kinh Sách( hay là Người dân đa thần đều không muốn một điều tốt nào từ Thượng Đế (Allah) của các ngươi được ban xuống cho các ngươi. Nhưng do lòng Khoan dung của Ngài, Allah chọn người nào Ngài muốn (để phục vụ Ngài) bởi vì Allah là Chủ nhân của Thiên ân vĩ đại.');
INSERT INTO "hasan_abdulkarim" VALUES(2,106,'2:106','Bất cứ một Câu kinh nào TA (Allah) hủy bỏ hoặc làm cho quên đi thì sẽ được TA thay thế bằng một Câu kinh tốt hơn hoặc tương đương với nó. Há ngươi (hỡi người!) không biết rằng Allah có quyền trên tất cả mọi việc hay sao?');
INSERT INTO "hasan_abdulkarim" VALUES(2,107,'2:107','Há ngươi (hỡi người!) không biết rằng Allah cai trị các tầng trời và trái đất hay sao? Và ngoài Ngài ra, các ngươi không có một vị Bảo hộ hay một vị Cứu tinh nào khác.');
INSERT INTO "hasan_abdulkarim" VALUES(2,108,'2:108','Phải chăng các ngươi muốn vặn hỏi Sứ Giả (Muhammad) của các ngươi giống như (Sứ Giả) Mũsa đã bị (các tín đồ của Y) vặn hỏi trước đây hay sao? Và ai thay đổi từ chỗ có đức tin sang chỗ không tin thì chắc chắn sẽ đi lạc khỏi Chính Đạo (Islam) êm ả.');
INSERT INTO "hasan_abdulkarim" VALUES(2,109,'2:109','Đa số trong đám Người dân Kinh Sách, do lòng ganh tị, muốn làm cho các ngươi trở lại (tình trạng) mất đức tin sau khi các ngươi đã có đức tin và sau khi họ đã chứng kiến rõ sự thật. Bởi thế, hãy lượng thứ và bỏ qua cho họ cho đến khi nào Allah ban hành Mệnh lệnh của Ngài xuống. Và quả thật, Allah có toàn quyền định đoạt trên tất cả mọi vấn đề.');
INSERT INTO "hasan_abdulkarim" VALUES(2,110,'2:110','Và hãy chu đáo dâng lễ Salah và đóng zakãh và bất cứ điều (tốt) nào các ngươi đã gởi đi trước cho bản thân của các ngươi (ở đời sau), các ngươi sẽ tìm thấy nó lại nơi Allah bởi vì chắc chắn Allah thấy hết những điều tốt mà các ngươi đã làm.');
INSERT INTO "hasan_abdulkarim" VALUES(2,111,'2:111','Và họ bảo: “Chỉ người Do thái và người theo Thiên Chúa giáo mới được vào thiên đàng.” Đấy chẳng qua là điều mơ ước (ảo huyền) của họ. Hãy bảo (họ): “Hãy trưng các bằng chứng của các ngươi ra xem nếu các ngươi nói thật.”');
INSERT INTO "hasan_abdulkarim" VALUES(2,112,'2:112','Vâng, bất cứ ai nạp trọn sắc diện của mình cho Allah đồng thời là một người làm tốt thì sẽ được phần thưởng nơi Thượng Đế của y. Họ sẽ không lo sợ cũng sẽ không buồn phiền.');
INSERT INTO "hasan_abdulkarim" VALUES(2,113,'2:113','Và những người Do thái bảo: “Các tín đồ Thiên Chúa giáo chẳng dựa vào cái gì (để làm cơ sở); và những tín đồ Thiên Chúa giáo bảo: “Những người Do thái chẳng dựa vào cái gì (để làm cơ sở) trong lúc họ đọc chung một Kinh Sách. Tương tự như thế, những kẻ không biết gì cũng nói ra lời giống như lời của họ. Nhưng vào Ngày phục sinh, Allah sẽ xét xử giữa bọn họ về những điều họ đã từng tranh chấp.');
INSERT INTO "hasan_abdulkarim" VALUES(2,114,'2:114','Và còn ai sai quấy hơn kẻ đã ngăn cản (nơi) Thánh đường của Allah không cho tên của Ngài được tụng niệm trong đó và còn tích cực tìm cách đập phá chúng (những thánh đường)? Đối với những kẻ như thế thì chỉ có cách làm cho chúng sợ khi chúng muốn bước vào thánh đường. Chúng sẽ bị hạ nhục ở đời này và sẽ bị trừng phạt nặng nề ở đời sau.');
INSERT INTO "hasan_abdulkarim" VALUES(2,115,'2:115','Và hướng đông và hướng tây là của Allah. Bỏi thế, khi các ngươi quay mặt về hướng nào thì Allah hiện diện nơi đó. Bởi vì quả thật, Allah Bao la, Biết hết (mọi việc).');
INSERT INTO "hasan_abdulkarim" VALUES(2,116,'2:116','Và họ bảo: “Allah có một đứa con trai.” Quang vinh và trong sạch thay Ngài! Không, mọi vật trong các tầng trời và trái đất đều là (những tạo vật) của Ngài cả. Mọi vật đều thần phục Ngài.');
INSERT INTO "hasan_abdulkarim" VALUES(2,117,'2:117','(Allah là) Đấng sáng tạo các tầng trời và trái đất; và khi Ngài quyết định một việc (vật), Ngài chỉ phán bảo: “Hãy thành” thì nó sẽ thành (theo Ý Ngài muốn).');
INSERT INTO "hasan_abdulkarim" VALUES(2,118,'2:118','Và những kẻ không biết gì nói: ‘Tại sao Allah không trực tiếp nói chuyện với bọn ta? hoặc tại sao không có một Lời Mặc Khải nào được mang đến cho bọn ta?” Tương tự như thế, những kẻ (không có đức tin) trước họ cũng đã nói ra những lời giống như lời của họ. Tấm lòng của họ giống nhau. Chắc chắn TA (Allah) đã trình bày rõ những Lời Mặc Khải cho đám người có đức tin vững chắc.');
INSERT INTO "hasan_abdulkarim" VALUES(2,119,'2:119','Và chắc chắn TA (Allah) cử Ngươi (Muhammad) đến làm một người mang Tin mừng và Cảnh cáo bằng sự thật. Và Ngươi sẽ không bị chất vấn về (tội lỗi của) những người bạn của hỏa ngục.');
INSERT INTO "hasan_abdulkarim" VALUES(2,120,'2:120','Và những người Do thái lẫn tín đồ Thiên Chúa giáo sẽ không bao giờ hài lòng với Ngươi trừ phi Ngươi theo tín ngưỡng của họ. Hãy bảo họ: “Quả thật, Chỉ Đạo của Allah mới là sự Hướng dẫn (duy nhất và đúng đắn.)” Và nếu Ngươi làm theo điều mong muốn của họ sau khi Ngươi đã tiếp thu sự hiểu biết (về Chỉ Đạo của Allah) thì Ngươi sẽ không được ai che chở hoặc giúp đỡ để tránh khỏi (sự trừng phạt của) Allah.');
INSERT INTO "hasan_abdulkarim" VALUES(2,121,'2:121','Những ai đã được TA (Allah) ban cho Kinh Sách nên đọc Nó (Kinh Sách) theo lẽ mà Nó phải được đọc, họ sẽ tin nơi Nó. Ngược lại, ai không tin nơi Nó thì sẽ là những kẻ mất mát.');
INSERT INTO "hasan_abdulkarim" VALUES(2,122,'2:122','Hỡi con cháu của Israel! Hãy nhớ những Ân huệ mà TA (Allah) đã ban cho các ngươi và việc TA đã ưu đãi các ngươi hơn thiên hạ.');
INSERT INTO "hasan_abdulkarim" VALUES(2,123,'2:123','Và hãy sợ một Ngày mà không một linh hồn nào sẽ đền bồi cho một linh hồn nào khác và sẽ không có một sự bồi thường nào được chấp nhận từ nó và sẽ không có ai được can thiệp giùm cho ai và cũng sẽ không có ai giúp đỡ được ai.');
INSERT INTO "hasan_abdulkarim" VALUES(2,124,'2:124','Và hãy nhớ lại khi Ibrahim đã được Thượng Đế của Y thử thách bằng những Mệnh-lệnh mà Y đã hoàn tất mỹ mãn. (Bởi thế, Allah đã) phán: “TA sẽ cử Ngươi làm một nhà lãnh đạo cho nhân loại.” (Ibrahim kèo nài) thưa: “Và từ con cháu của bề tôi nữa.” (Allah) đáp: “Lời hứa của TA không can hệ gì đến những kẻ làm điều sai quấy.”');
INSERT INTO "hasan_abdulkarim" VALUES(2,125,'2:125','Và hãy nhớ lại khi TA (Allah) đã chỉ định Ngôi Đền (Ka’bah) làm một nơi an toàn cho thiên hạ tới lui thăm viếng và (phán cho các khách viếng): “Hãy lấy chỗ đứng của Ibrãhĩm làm địa điểm dâng lễ cầu nguyện” và TA đã bắt Ibrahim và Isma''il (con trai của Người) cam kết phải giữ Ngôi Đền của TA cho trong sạch (để khách thập phương đến thăm và) đi vòng quanh Nó và cho những người lánh trần và cho những người (đến) cúi đầu (thần phục) và cho những người đến phủ phục (lễ nguyện).”');
INSERT INTO "hasan_abdulkarim" VALUES(2,126,'2:126','Và hãy nhớ lại khi Ibrahim (cầu nguyện) thưa: “Lạy Thượng Đế của bề tôi! Xin Ngài làm cho thành phố (Makkah) này an toàn và nuôi sống dân cư của nó với hoa quả, (nuôi) người nào trong bọn chúng tin tưởng nơi Allah và Ngày (Phán xử) Cuối cùng.” (Allah) phán: “Và ai không có đức tin thì TA sẽ cho (y) hưởng thụ ngắn ngủi rồi TA sẽ lôi đầu y đến chỗ trừng phạt của hỏa ngục; và đó là một nơi đến cuối cùng tồi tệ nhất.”');
INSERT INTO "hasan_abdulkarim" VALUES(2,127,'2:127','Và hãy nhớ lại khi Ibrahim và Ịsma''il xây móng của Ngôi Đền (và cầu nguyện): “Lạy Thượng Đế, bầy tôi! Xin Ngài chấp nhận (việc làm này) từ bầy tôi, bởi vì Ngài là Đấng Hằng nghe và Hằng biết (hết mọi việc).”');
INSERT INTO "hasan_abdulkarim" VALUES(2,128,'2:128','“Lạy Thượng Đế của bầy tôi! Xin Ngài làm cho bầy tôi thành hai người Muslim (thần phục) Ngài và tạo một Ummah (Cộng đồng) Muslim của Ngài từ con cháu của bầy tôi và chỉ cho bầy tôi những nghi lễ bắt buộc và lượng thứ cho bầy tôi bởi vì chắc chắn Ngài là Đấng Hằng Lượng thứ, Rất Mực Khoan Dung.');
INSERT INTO "hasan_abdulkarim" VALUES(2,129,'2:129','“Lạy Thượng Đế của bầy tôi! Xin Ngài dựng lên trong chúng (con cháu của bầy tôi) một Sứ Giả (xuất thân) từ chúng. Y sẽ đọc các Lời Mặc Khải của Ngài cho chúng và sẽ dạy chúng Kinh Sách và Lẽ Đúng đắn Khôn ngoan; và Y sẽ thanh sạch hóa chúng. Quả thật, Ngài là Đấng Toàn năng, Đấng Rất Mực Sáng Suốt.”');
INSERT INTO "hasan_abdulkarim" VALUES(2,130,'2:130','Và chỉ ai tự lừa dối bản thân (linh hồn) mình mới bỏ tín ngưỡng của Ibrahim bởi vì TA (Allah) đã chọn Y (làm một nhà lãnh đạo) ở trần gian này; và ở đời sau chắc chắn Y sẽ ở trong hàng ngũ của những người ngoan đạo.');
INSERT INTO "hasan_abdulkarim" VALUES(2,131,'2:131','Và hãy nhớ lại khi Thượng Đế của Y (Ibrahim) đã phán bảo Y: “Hãy thần phục (TA!)”. (Ibrahim) thưa: “Bề tôi xin nạp mình thần phục Thượng Đế (Allah) của vũ trụ và muôn loài.”');
INSERT INTO "hasan_abdulkarim" VALUES(2,132,'2:132','Và Ibrahim đã truyền bảo điều đó cho các con của Y; và Ya''qub cũng làm y như thế, dặn các con: “Này các con! Quả thật, Allah đã chọn tôn giáo (Islam) cho các con; bởi thế các con hãy đừng chết trừ phi các con ở trong tình trạng là những người Muslim (thần phục Allah).');
INSERT INTO "hasan_abdulkarim" VALUES(2,133,'2:133','Phải chăng các ngươi (hỡi con cháu của Israel!) đã chứng kiến cảnh Ya''qub sắp từ trần khi Y bảo các con của Y: “Các con sẽ thờ phụng ai (cái gì) sau (khi) ta (mất?)”. (Các con) thưa: “Chúng con sẽ thờ phụng Thượng Đế của cha và Thượng Đế của tổ tiên của cha: Ibrahim, Isma''il và Is-haq, một Thượng Đế Duy-nhất; và chúng con là những người Muslim (thần phục Allah).”');
INSERT INTO "hasan_abdulkarim" VALUES(2,134,'2:134','Đó là một Ummah (Cộng đồng) đã vĩnh viễn ra đi. Họ sẽ hưởng thành quả mà họ đã làm ra còn các ngươi thì sẽ hưởng theo phần công lao của các ngươi. Các ngươi sẽ không bị chất vấn về những công việc mà họ đã làm.');
INSERT INTO "hasan_abdulkarim" VALUES(2,135,'2:135','Và họ nói: “Hãy trở thành người Do Thái hay thành tín đồ Thiên Chúa giáo nếu muốn được hướng dẫn.” Hãy bảo họ (hỡi Muhammad!): “Không, Ta theo tín ngưỡng của Ibrahim, một tín ngưỡng thuần khiết (chỉ tôn thờ riêng Allah) và Người không phải là một người thờ đa thần.”');
INSERT INTO "hasan_abdulkarim" VALUES(2,136,'2:136','Hãy bảo họ (hỡi người Muslim!): “Chúng tôi tin tưởng nơi Allah và những điều (Mặc khải) đã được ban xuống cho chúng tôi và những điều đã được ban xuống cho Ibrahim, Isma''il, Is-haq, Ya''qub và các Bộ lạc (của Israel); và những điều (Mặc khải) đã được ban xuống cho Musa và ''Isa cũng như những điều đã được ban xuống cho các vị Nabi từ Thượng Đế của Họ; chúng tôi (người Muslim) không phân biệt kỳ thị một Vị (Nabi) nào trong Họ (các Nabi của Allah) và chúng tôi là những người Muslim (thần phục Allah).');
INSERT INTO "hasan_abdulkarim" VALUES(2,137,'2:137','Bởi vậy, nếu họ tin tưởng nơi những điều giống với những điều đã được ban cho các ngươi (hỡi Muslim!) thì chắc chắn họ đi đúng đường. Ngược lại, nếu họ quay lưng làm ngơ, thì chính họ mới là những kẻ đã chia rẽ tôn giáo. Và riêng Allah thôi đủ (giúp) Ngươi (hỡi Muhammad!) đương đầu với họ, bởi vì Ngài là Đấng Hằng nghe và Hằng biết (hết mọi việc).');
INSERT INTO "hasan_abdulkarim" VALUES(2,138,'2:138','Mầu sắc (tôn giáo) của Allah (là Islam). Và ai trội hơn Allah trong việc nhuộm mầu (các tín đồ của Ngài?) Và chúng tôi là những người thờ phụng Ngài.');
INSERT INTO "hasan_abdulkarim" VALUES(2,139,'2:139','Hãy bảo họ: “Phải chăng quí vị muốn tranh luận với chúng tôi về Allah trong lúc Ngài là Thượng Đế của chúng tôi và là Thượng Đế của quí vị? Và phần việc của chúng tôi là của chúng tôi còn phần việc của quí vị là của quí vị. Và chúng tôi thành tâm với Ngài (Allah).');
INSERT INTO "hasan_abdulkarim" VALUES(2,140,'2:140','Hoặc phải chăng quí vị bảo Ibrahim, Isma’il, Is-haq, Ya''qub và các Bộ lạc (của Israel) là những người Do thái hay tín đồ Thiên Chúa giáo cả hay sao? Hãy bảo họ: “Phải chăng quí vị biết hơn Allah?” Còn ai sai quấy hơn kẻ đã giấu giếm Lời xác nhận của Allah (về Sứ Giả Muhammad) trong (Kinh Sách) mà họ đang giữ? Và Allah không làm ngơ trước những điều các ngươi làm.');
INSERT INTO "hasan_abdulkarim" VALUES(2,141,'2:141','Đó là một Ummah (Cộng đồng) đã qua đời. Họ gặt hái thành quả về những điều (thiện) mà họ đã làm ra còn của quí vị thì là điều mà quí vị đã làm ra. Quí vị sẽ không bị chất vấn về những điều mà họ đã làm.');
INSERT INTO "hasan_abdulkarim" VALUES(2,142,'2:142','Những kẻ điên rồ trong thiên hạ sẽ nói: “Điều gì đã làm (cho những người Muslim này) thay đổi hướng Qiblah( mà họ thường quay về đó (để dâng lễ?)” Hãy bảo họ: “Hướng đông và Hướng tây là của Allah. Ngài hướng dẫn người nào Ngài muốn đến con đường ngay chính.”');
INSERT INTO "hasan_abdulkarim" VALUES(2,143,'2:143','Và TA (Allah) đã làm cho các ngươi thành một cộng đồng dung hòa( để các ngươi trở thành các nhân chứng cho nhân loại và Sứ Giả (Muhammad) là một nhân chứng cho các ngươi; và TA (Allah) đã chỉ định hướng Qiblah mà Ngươi thường quay về đó (để dâng lễ) là chỉ để cho TA (Allah) biết ai là người tuân theo Sứ Giả (của Allah) với ai là người quay gót chân theo hướng khác. Và quả thật đó là một thay đổi rất lớn, nhưng không mấy trọng đại đối với những ai đã được Allah hướng dẫn (theo Chính Đạo). Và Allah không làm cho đức tin của các ngươi thành vô nghĩa bởi vì Allah rất mực Độ lượng, Rất Mực Khoan Dung.');
INSERT INTO "hasan_abdulkarim" VALUES(2,144,'2:144','Chắc chắn TA (Allah) thấy Ngươi (Muhammad) ngước mặt lên trời (cầu xin Chỉ Đạo). Bởi thế, TA (Allah) hướng Ngươi về phía Qiblah làm cho Ngươi hài lòng. Do đó, hãy quay mặt của Ngươi hướng về Al-Masjid al-Haram (Thánh đường Linh thiêng tại Makkah). Và ở bất cứ nơi nào, các ngươi hãy quay mặt về phía đó (để dâng lễ). Và chắc chắn những ai đã được (Allah) ban cho Kinh Sách đều biết rằng đó là sự Thật do Thượng Đế của họ ban xuống. Và Allah không làm ngơ trước những điều họ làm.');
INSERT INTO "hasan_abdulkarim" VALUES(2,145,'2:145','Và chắc chắn rằng nếu Ngươi có mang tất cả các Phép lạ đến cho những ai đã được ban cho Kinh Sách thấy (hầu thuyết phục họ về sự thật) đó, thì nhất định họ sẽ không theo hướng Qiblah của Ngươi; và Ngươi cũng sẽ không theo hướng Qiblah của họ; và đôi bên không ai theo hướng Qiblah của ai cả. Và sau khi đã rõ sự Thật, nếu Ngươi tiếp tục làm theo ý muốn của họ thì rõ ràng Ngươi cố tình làm điều sai quấy.');
INSERT INTO "hasan_abdulkarim" VALUES(2,146,'2:146','Những ai mà TA (Allah) đã ban cho Kinh Sách đều nhìn nhận Người (Muhammad là Sứ Giả của Allah) giống như việc họ nhìn nhận con cái của họ vậy, nhưng một số trong bọn họ giấu giếm sự thật trong lúc họ biết (điều đó).');
INSERT INTO "hasan_abdulkarim" VALUES(2,147,'2:147','Sự Thật là từ Thượng Đế của Ngươi. Bởi thế, chớ sinh lòng nghi ngờ (về Nó).');
INSERT INTO "hasan_abdulkarim" VALUES(2,148,'2:148','Mỗi cá nhân (hay cộng đồng) đều có một mục tiêu mà y (hay họ) hướng về (để dâng lễ). Do đó, hãy thi đua nhau làm điều lành. Bất cứ nơi nào các ngươi ở, Allah sẽ đưa các ngươi đến gặp nhau. Bởi vì Allah có quyền chi phối trên tất cả mọi việc.');
INSERT INTO "hasan_abdulkarim" VALUES(2,149,'2:149','Và từ bất cứ nơi nào Ngươi khởi hành, hãy quay mặt của Ngươi về hướng Masjid Al-Haram (để dâng lễ). Và chắc chắn đó là sự Thật từ Thượng Đế của Ngươi. Và Allah không làm ngơ trước những điều các người làm.');
INSERT INTO "hasan_abdulkarim" VALUES(2,150,'2:150','Và từ bất cứ nơi nào Ngươi khởi hành, hãy quay mặt của Ngươi về hướng Masịid-al-Haram (để dâng lễ); và ở bất cứ nơi nào, hãy quay mặt của các người hướng về đó (để dâng lễ) để cho thiên hạ không còn lý do để tranh chấp với các ngươi (về Qiblah) ngoại trừ những ai làm điều sai quấy trong bọn họ. Bởi thế, chớ sợ họ mà hãy sợ TA. Và để cho TA hoàn tất ân huệ của TA cho các ngươi và để cho các ngươi được hướng dẫn đứng Chính Đạo;');
INSERT INTO "hasan_abdulkarim" VALUES(2,151,'2:151','Tương tự như việc TA (Allah) đã cử một Sứ Giả xuất thân từ các ngươi đến để đọc cho các ngươi các Lời Mặc Khải của TA và thanh sạch hóa các ngươi và dạy các ngươi Kinh Sách (Qur''an) và Lẽ đúng đắn khôn ngoan( và dạy các ngươi những điều mà các ngươi chưa từng biết.');
INSERT INTO "hasan_abdulkarim" VALUES(2,152,'2:152','Bởi thế, hãy nhớ TA; TA sẽ nhớ các ngươi trở lại. Và hãy biết ơn TA và chớ phụ ân TA.');
INSERT INTO "hasan_abdulkarim" VALUES(2,153,'2:153','Hỡi những ai có đức tin! Hãy cầu xin sự giúp đỡ (của Allah) bằng sự kiên nhẫn và lễ nguyện Salah. Bởi quả thật, Allah luôn ở cùng với những người kiên nhẫn.');
INSERT INTO "hasan_abdulkarim" VALUES(2,154,'2:154','Và chớ nói về những ai đã hy sinh tính mạng cho Chính Nghĩa của Allah (rằng họ đã) chết rồi. Không, (họ vẫn còn) sống nhưng các ngươi không nhận thấy (điều đó).');
INSERT INTO "hasan_abdulkarim" VALUES(2,155,'2:155','Và chắc chắn TA (Allah) sẽ thử thách các người với những điều sợ hãi, và đói khát, và việc mất mát tài sản và (thiệt hại) sinh mạng và hoa quả (mùa màng), nhưng hãy báo tin mừng cho những người kiên nhẫn:');
INSERT INTO "hasan_abdulkarim" VALUES(2,156,'2:156','Những ai khi gặp phải điều xấu, nói: “Quả thật, chúng tôi là của Allah và chúng tôi sẽ trở về với Ngài”:-');
INSERT INTO "hasan_abdulkarim" VALUES(2,157,'2:157','Họ là những người sẽ nhận Phúc lành và sự Khoan dung từ Thượng Đế của họ và họ là những người được hướng dẫn (đúng Chính Đạo).');
INSERT INTO "hasan_abdulkarim" VALUES(2,158,'2:158','Quả thật, (hai ngọn đồi) Safa và Marwah (tại Makkah) là những biểu hiệu của Allah. Do đó, ai làm Hajj hoặc ''Umrah tại Ngôi Đền (của Allah) thì sẽ không có tội khi đi vòng hai địa điểm đó. Và ai tự nguyện làm điều lành thì quả thật Allah Hằng ghi ơn và Hằng biết (việc làm tốt của họ).');
INSERT INTO "hasan_abdulkarim" VALUES(2,159,'2:159','Quả thật, những ai giấu giếm những bằng chứng rõ rệt mà TA (Allah) đã ban xuống và (giấu giếm) Chỉ Đạo mà TA đã trình bày rõ cho nhân loại trong Kinh Sách thì sẽ bị Allah nguyền rủa và bị cả những Thiên Thần và những người có đức tin nguyền rủa.');
INSERT INTO "hasan_abdulkarim" VALUES(2,160,'2:160','Ngoại trừ những ai biết sám hối, phục thiện và công bố (sự Thật) thì sẽ được TA đoái thương tha thứ bởi vì TA là Đấng Hằng quay lại Tha thứ, Rất Mực Khoan Dung.');
INSERT INTO "hasan_abdulkarim" VALUES(2,161,'2:161','Bởi vì quả thật những ai phủ nhận đức tin và chết trong tình trạng không có đức tin thì sẽ bị cả Allah và Thiên Thần và nhân loại nguyền rủa;');
INSERT INTO "hasan_abdulkarim" VALUES(2,162,'2:162','Chúng sẽ ở trong (tình trạng bị nguyền rủa) đó đời đời. Hình phạt dành cho chúng sẽ không được giảm nhẹ và chúng sẽ không được buông tha.');
INSERT INTO "hasan_abdulkarim" VALUES(2,163,'2:163','Và Thượng Đế của các ngươi là một Thượng Đế Duy nhất. Không có Thượng Đế nào khác, duy chỉ Ngài (Allah), Đấng rất mực Nhân từ, Rất Mực Khoan Dung.');
INSERT INTO "hasan_abdulkarim" VALUES(2,164,'2:164','Quả thật, trong việc tạo hóa các tầng trời và trái đất; và (việc) luân chuyển ban đêm và ban ngày; và (việc) các chiếc tàu (chở hàng hóa) vượt trùng dương làm lợi cho nhân loại; và (việc) Allah ban nước mưa từ trên trời xuống nhờ đó Ngài làm cho mảnh đất chết khô sống lại; và (việc) Ngài rải tung đủ loại sinh vật khắp mặt đất; và (việc) chuyển hướng các luồng gió và luồng mây mà Ngài chế ngự giữa trời và đất; (tất cả các việc đó) là những dấu hiệu cho những người biết suy nghĩ.');
INSERT INTO "hasan_abdulkarim" VALUES(2,165,'2:165','Nhưng trong nhân loại có những kẻ đã dựng những đối thủ ngang vai với Allah. Họ yêu thương chúng giống với tình thương mà họ dành cho Allah. Ngược lại, những ai có đức tin thì yêu thương Allah mạnh hơn. Và nếu những ai làm điều sai quấy có thể nhìn thấy được (sự thật) thì chúng sẽ không thấy gì ngoài sự trừng phạt bởi lẽ tất cả quyền lực đều nằm trong tay của Allah và Allah rất nghiêm khắc trong việc trừng trị.');
INSERT INTO "hasan_abdulkarim" VALUES(2,166,'2:166','(Bởi vì) khi đối diện với hình phạt, những kẻ được tuân theo sẽ (tuyên bố) vô can đối với những kẻ phục tùng chúng và mọi quan hệ giữa đôi bên sẽ bị cắt đứt hết.');
INSERT INTO "hasan_abdulkarim" VALUES(2,167,'2:167','Và những kẻ phục tùng sẽ bảo: “Nếu chúng tôi có cơ hội trở lại trần gian, chúng tôi sẽ tuyên bố vô can đối với bọn chúng giống như việc bọn chúng đã tuyên bố vô can đối với chúng tôi ngày naỵ.” Allah sẽ cho chúng thấy kết quả của việc làm của chúng chỉ gồm những ân hận và nuối tiếc giống như thế. Và chúng sẽ không có cách nào thoát ra khỏi Lửa (của hỏa ngục).');
INSERT INTO "hasan_abdulkarim" VALUES(2,168,'2:168','Hỡi nhân loại! Hãy ăn những thức ăn được cho phép và tốt sạch và chớ giẫm theo dấu chân của Shaytan; quả thật nó là kẻ thù công khai của các ngươi.');
INSERT INTO "hasan_abdulkarim" VALUES(2,169,'2:169','(Shaytan) chỉ ra lệnh cho các ngươi làm điều tội lỗi và điều khả ố và bắt các ngươi nói về Allah những điều các ngươi không biết.');
INSERT INTO "hasan_abdulkarim" VALUES(2,170,'2:170','Và khi có lời bảo họ: “Hãy tuân theo điều (Mặc khải) do Allah ban xuống,” thì họ đáp: “Không, chúng tôi sẽ theo điều mà chúng tôi thấy cha mẹ của chúng tôi đang theo.” Sao! Ngay cả việc cha mẹ của họ không biết gì hoặc không được ai hướng dẫn hay sao?');
INSERT INTO "hasan_abdulkarim" VALUES(2,171,'2:171','Và hình ảnh của những kẻ phủ nhận đức tin có thể so sánh với (hình ảnh của) một người (chăn trừu) thét to cho những con vật không nghe được tiếng gì khác ngoài tiếng gọi và tiếng thét. (Chúng là những sinh vật) điếc, câm và mù cho nên chúng không hiểu gì.');
INSERT INTO "hasan_abdulkarim" VALUES(2,172,'2:172','Hỡi những ai có đức tin! Hãy ăn những thức ăn tốt sạch mà TA (Allah) đã cung cấp cho các ngươi; và hãy biết ơn Allah nếu các ngươi chỉ thờ phụng riêng Ngài.');
INSERT INTO "hasan_abdulkarim" VALUES(2,173,'2:173','(Allah) chỉ cấm các ngươi ăn (thịt của) xác chết, và máu (huyết), và thịt của con heo, và những món mà tên của các thần linh khác không phải (tên của) Allah đã được đọc nhắc đến (khi cắt cổ chúng). Nhưng ai vì nhu cầu bắt buộc mà phải dùng những món (cấm) đó ngoài ý muốn (của mình) và không quá độ, thì sẽ không bị tội. Bởi vì quả thật Allah Hằng Tha Thứ, Rất Mực Khoan Dung.');
INSERT INTO "hasan_abdulkarim" VALUES(2,174,'2:174','Quả thật, những ai giấu giếm những điều (mặc khải) của Kinh Sách mà Allah đã ban xuống và dùng Nó để mua bán với một giá nhỏ nhoi thì là những kẻ chỉ nuốt Lửa vào bụng. Allah sẽ không nói đến chúng vào Ngày phục sinh, cũng sẽ không thanh sạch chúng; và chúng sẽ bị trừng phạt đau đớn.');
INSERT INTO "hasan_abdulkarim" VALUES(2,175,'2:175','Chúng là những kẻ đã mua lấy sự lầm lạc thay vì theo Chỉ Đạo đúng đắn và mua lấy hình phạt thay vì sự tha thứ. Thật chúng gan dạ lắm mới dám đương đầu với Lửa (của hỏa ngục!)');
INSERT INTO "hasan_abdulkarim" VALUES(2,176,'2:176','(Sở dĩ) như thế là vì Allah ban Kinh Sách xuống bằng sự thật và quả thật những ai bất đồng ý kiến về Kinh Sách (của Allah) thì chắc chắn sẽ rơi vào tình trạng rời xa khỏi đạo giáo.');
INSERT INTO "hasan_abdulkarim" VALUES(2,177,'2:177','Đạo đức không phải là các ngươi quay mặt về hướng đông hay hướng tây; mà đạo đức là việc ai tin tưởng nơi Allah và Ngày Phán Xử Cuối Cùng, và các Thiên Thần, và Kinh Sách (của Allah), và các Nabi (của Allah), và vì yêu thương Ngài mà bố thí của cải cho bà con ruột thịt, và cho những trẻ mồ côi, và những người thiếu thốn, và những người lỡ đường, và những người ăn xin, và để chuộc và giải phóng những người nô-lệ, và chu đáo dâng lễ Salah và đóng Zakah và hoàn tất lời hứa khi đã hứa, và kiên trì chịu đựng trong nghịch cảnh (đói khổ) và thiên tai (bệnh hoạn) và trong thời gian xảy ra chiến tranh (bạo động). Họ là những người chân thật và là những người ngay chính sợ Allah.');
INSERT INTO "hasan_abdulkarim" VALUES(2,178,'2:178','Hỡi những ai có đức tin! Luật trả đũa (Qisas) về việc giết người được qui định như sau: (sinh mạng của một) người tự do đổi lấy (sinh mạng của một) người tự do; (sinh mạng của) một người nô lệ đổi lấy (sinh mạng của) một người nô lệ; một người phụ nữ đổi lấy một người phụ nữ; nhưng nếu phạm nhân nào được anh (hay em) của nạn nhân lượng thứ cho phần nào, thì hãy làm theo yêu cầu hợp lý của y và bồi thường cho nạn nhân một cách tốt đẹp. Đây là sự giảm nhẹ và là sự khoan dung từ Thượng Đế của các ngươi. Bởi thế, sau các qui định nêu trên, ai vượt quá mức giới hạn thì sẽ bị trừng trị đích đáng.');
INSERT INTO "hasan_abdulkarim" VALUES(2,179,'2:179','Và trong luật Qisas có việc (cứu) sinh mạng đối với các ngươi, hỡi những người hiểu biết! để cho các ngươi trở thành những người ngay chính sợ Allah.');
INSERT INTO "hasan_abdulkarim" VALUES(2,180,'2:180','Lệnh truyền cho các ngươi (như sau:) Khi một ai trong các ngươi sắp từ trần, nếu y để lại tài sản thì phải lập di chúc cho cha mẹ và bà con ruột thịt một cách hợp lý. Đây là bổn phận của người ngay chính sợ Allah.');
INSERT INTO "hasan_abdulkarim" VALUES(2,181,'2:181','Bởi thế, ai sửa đổi lời di chúc sau khi đã tuyên bố thì chỉ riêng những người sửa đổi đó mới chịu tội. Bởi vì Allah Hằng nghe và Hằng biết hết mọi việc.');
INSERT INTO "hasan_abdulkarim" VALUES(2,182,'2:182','Nhưng người nào sợ có sự thiên vị hoặc tội lỗi từ người để lại di chúc rồi đứng ra giàn xếp giữa những người liên hệ thì sẽ không mang tội. Quả thật, Allah Hằng Tha Thứ, Rất Mực Khoan Dung.');
INSERT INTO "hasan_abdulkarim" VALUES(2,183,'2:183','Hỡi những ai có đức tin! Nhịn chay( là lệnh được truyền xuống cho các ngươi như đã được truyền xuống cho những người trước các ngươi để các ngươi (rèn luyện) sự khắc kỷ và trở thành người ngay chính sợ Allah.');
INSERT INTO "hasan_abdulkarim" VALUES(2,184,'2:184','(Nhịn chay) trong một số ngày ấn định. Nhưng ai trong các ngươi mắc bệnh hoặc đi xa nhà thì sẽ nhịn bù lại cho đủ số ngày đã thiếu (vào tháng khác). Nhưng đối với những ai có khả năng nhịn chay (nhưng rất chật vật trong việc nhịn chay vì già yếu) thì có thể chuộc tội bằng cách nuôi ăn (từng ngày) một người thiếu thốn. Nhưng người nào tự nguyện (bố thí) làm tốt thì điều đó tốt cho y hơn. Tuy nhiên, thực hiện việc nhịn chay tốt cho các ngươi hơn nếu các ngươi biết (giá trị của nó).');
INSERT INTO "hasan_abdulkarim" VALUES(2,185,'2:185','Tháng Ramadan là tháng trong đó (Kinh) Qur’an được ban xuống làm Chỉ Đạo cho nhân loại và mang bằng chứng rõ rệt về Chỉ Đạo và Tiêu chuẩn (phân biệt Phúc và Tội). Bởi thế, ai trong các ngươi chứng kiến (hoặc có mặt tại nhà) tháng đó thì phải nhịn chay trọn tháng; và ai bị bệnh hoặc đi xa nhà thì phải nhịn bù lại số ngày đã thiếu (vào tháng khác). Allah muốn điều dễ dàng cho các ngươi chứ Ngài không muốn gây khó khăn cho các ngươi. (Ngài muốn các ngươi) hoàn tất số ngày (nhịn chay) ấnn định và muốn cho các ngươi tán dương sự Vĩ đại của Allah về việc Ngài đã hướng dẫn các ngươi và để cho các ngươi có dịp tạ ơn Ngài.');
INSERT INTO "hasan_abdulkarim" VALUES(2,186,'2:186','Và khi bầy tôi của TA hỏi Ngươi (Muhammad) về TA, (hãy bảo họ) TA rất gần. TA đáp lại lời cầu xin của người nguyện cầu khi y cầu xin TA. Ngược lại, họ cũng phải đáp lại (Lời gọi của) TA và tin tưởng nơi TA để may ra họ được hướng dẫn đúng đường.');
INSERT INTO "hasan_abdulkarim" VALUES(2,187,'2:187','Cho phép các ngươi được ăn nằm với vợ ban đêm suốt thời gian nhịn chay. Họ là y phục của các ngươi và các ngươi là y phục của họ. Allah biết điều các ngươi thường lén lút với nhau. Do đó, Ngài đoái thương quay lại tha thứ cho các ngươi. Bởi thế, bây giờ các ngươi được phép chung chạ với họ, nhưng hãy cố gắng thực hiện điều Allah đã ra lệnh cho các ngươi, và hãy ăn và uống (ban đêm) cho đến khi các ngươi thấy rõ sợi chỉ trắng của buổi rạng đông tách khỏi sợi chỉ đen của nó, rồi hoàn tất việc nhịn chay cho đến lúc đêm xuống. Và không được ăn nằm với vợ trong thời gian các ngươi ẩn mình E''tikaaf trong Masjid. Đó là những giới cấm quy định bởi Allah. Do đó, chớ vi phạm chúng. Allah trình bày những Lời Mặc Khải của Ngài cho nhân loại đúng như thế để may ra họ trở thành người ngay chính sợ Allah.');
INSERT INTO "hasan_abdulkarim" VALUES(2,188,'2:188','Và chớ gian lận ăn nuốt tài sản của các ngươi lẫn nhau, và chớ dùng nó để hối lộ các quan toà hầu các ngươi có thể ăn không một phần tài sản của nhân loại một cách tội lỗi trong lúc các ngươi biết điều đó.');
INSERT INTO "hasan_abdulkarim" VALUES(2,189,'2:189','Họ hỏi Ngươi về những vầng trăng lưỡi liềm. Hãy bảo họ: “Chúng là những thời điểm cho nhân loại (ghi đếm thời gian) và (cho) việc làm Hajj. Đạo đức không phải là đi vào nhà bằng cửa sau( mà đạo đức là ai sợ Allah. Và hãy đi vào nhà bằng những cửa chính (của nó), và hãy sợ Allah để cho các ngươi có thể thành công.');
INSERT INTO "hasan_abdulkarim" VALUES(2,190,'2:190','Và vì Chính Nghĩa của Allah hãy đánh trả những kẻ đã đánh các ngươi (trước) nhưng chớ vượt quá mức giới hạn bởi vì quả thật Allah không thương những kẻ phạm giới.');
INSERT INTO "hasan_abdulkarim" VALUES(2,191,'2:191','Và hãy giết chúng nơi nào các ngươi bắt được chúng và đánh (đuổi) chúng ra khỏi nơi mà chúng đã đuổi các ngươi ra đi bởi vì quấy nhiễu nghiêm trọng hơn giết chóc. Nhưng chớ đánh chúng trong Thánh Đường Haram (tại Makkah) trừ phi chúng đánh các ngươi trong đó. Và nếu chúng đánh các ngươi thì hãy giết chúng lại. Đó là quả báo dành cho những kẻ không có đức tin.');
INSERT INTO "hasan_abdulkarim" VALUES(2,192,'2:192','Nhưng nếu chúng ngưng chiến thì (nên biết rằng) Allah Hằng Tha Thứ, Rất Mực Khoan Dung.');
INSERT INTO "hasan_abdulkarim" VALUES(2,193,'2:193','Và hãy tiếp tục đánh chúng cho đến khi nào chấm dứt việc quấy nhiễu. Và chúng sẽ hoàn toàn thần phục Allah. Do đó, nếu chúng ngưng chiến thì sẽ không còn mối hiềm thù nào đối với chúng nữa ngoại trừ những kẻ làm điều sai quấy.');
INSERT INTO "hasan_abdulkarim" VALUES(2,194,'2:194','Tháng cấm kỵ là tháng cấm kỵ (cho cả đôi bên Muslim và Kafir) và có Luật trả đũa (Qisas) về những điều cấm kỵ. Bởi thế, ai vi phạm (những giới cấm) nhằm lấn át các ngươi thì hãy lấn át trở lại như thế. Và hãy sợ Allah, và hãy biết rằng Allah ở cùng với những người sợ Allah.');
INSERT INTO "hasan_abdulkarim" VALUES(2,195,'2:195','Và hãy chi dùng (tài sản của các ngươi) cho Chính Nghĩa của Allah, và chớ để cho bàn tay (keo kiệt) của các ngươi xô đẩy các ngươi đến chỗ tự hủy và hãy làm tốt bởi vì quả thật Allah yêu thương những người làm tốt.');
INSERT INTO "hasan_abdulkarim" VALUES(2,196,'2:196','Và hãy hoàn tất việc làm Hajj và ''Umrah dâng lên Allah. Nhưng nếu các ngươi bị cản trở (trong việc này) thì hãy gởi một con vật tế đến tế (nếu có khả năng), và các ngươi không được cạo đầu cho đến khi con vật tế đã đến chỗ tế. Nhưng nếu ai trong các ngươi bị bệnh hoặc ngứa da đầu (cần phải cạo) thì phải chịu phạt bằng cách nhịn chay hoặc bố thí (cho người nghèo) hoặc dâng một con vật tế. Bởi thế, khi các ngươi bình thường trở lại, ai muốn tiếp tục làm ''Umrah nhập chung với Hajj (tức Hajj Tamattua'' và Qiran) thì phải dâng một con vật tế nếu có khả năng. Nhưng ai không có khả năng (dâng một con vật tế) thì phải nhịn chay ba ( ngày trong thời gian làm Hajj và bảy (07) ngày sau khi trở về nhà; như vậy là đủ mười (10) ngày tất cả. Đấy là điều kiện dành cho người nào mà gia đình không ở trong vùng đất Masjid Haram tại Makkah). Và hãy sợ Allah, và hãy biết rằng Allah trừng phạt rất nghiêm khắc.');
INSERT INTO "hasan_abdulkarim" VALUES(2,197,'2:197','(Việc làm) Hajj diễn ra trong những tháng( được biết rõ. Bởi thế, ai thực hiện việc làm Hajj trong những tháng đó thì không được dâm dục, không được hung ác, không được cãi vã trong thời gian làm Hajj. Allah biết điều tốt các ngươi làm. Và hãy mang theo thức ăn trong thời gian xa nhà, Nhưng lương thực tốt nhất là lòng thành kính sợ Allah. Và hãy sợ TA, hỡi những người hiểu biết!');
INSERT INTO "hasan_abdulkarim" VALUES(2,198,'2:198','Các ngươi không mắc tội nếu các ngươi tìm kiếm thiên lộc của Thượng Đế (Allah) của các ngươi (qua việc mua bán đổi chác trong thời gian làm Hajj). Nhưng khi các ngươi vội vã rời (thung lũng) ''Arafat (để đi Muzdalifah), hãy tán dương Allah tại Mash''ar Haram (ở Muzdalifah). Và hãy tán dương Allah đúng theo (cách) Ngài đã chỉ dẫn cho các ngươi bởi vì quả thật trước đây các ngươi là những kẻ lầm đường lạc lối.');
INSERT INTO "hasan_abdulkarim" VALUES(2,199,'2:199','Rồi hãy vội vã rời khỏi (Muzdalifah) nơi mà mọi người vội vã bỏ đi và hãy cầu xin Allah tha thứ. Quả thật, Allah Hằng Tha Thứ, Rất Mực Khoan Dung.');
INSERT INTO "hasan_abdulkarim" VALUES(2,200,'2:200','Do đó, khi các ngươi đã hoàn tất các nghi lễ (của Hajj), thì hãy tán dương Allah đúng như cách các ngươi đã tán dương cha mẹ của các ngươi (trước đây) hoặc tán dương (Allah) mạnh hơn. Nhưng trong nhân loại có người cầu nguyện thưa: “Lạy Thượng Đế chúng tôi! Xin (Ngài) ban cho chúng tôi (Ân sủng) ở đời này!” Và họ sẽ không được phần nào cả ở đời sau.');
INSERT INTO "hasan_abdulkarim" VALUES(2,201,'2:201','Và trong họ, cũng có người cầu nguyện: “Lạy Thượng Đế chúng tôi! Xin Ngài ban cho chúng tôi phần tốt ở đời này và phần tốt ở đời sau và cứu vớt chúng tôi khỏi hình phạt của Lửa (hỏa ngục!)”.');
INSERT INTO "hasan_abdulkarim" VALUES(2,202,'2:202','Họ là những người sẽ được (Allah) ban phần thưởng về những việc tốt mà họ đã làm ra. Và Allah rất nhanh trong việc thanh toán (thưởng phạt).');
INSERT INTO "hasan_abdulkarim" VALUES(2,203,'2:203','Và hãy tụng niệm Allah vào những Ngày ấn định (ngày mồng 12, 13 tháng Zdul Hijjah được gọi là những này Tashriq). Nhưng ai vội vã ra về trong vòng hai ngày thì không phải tội và ai nhất định ở lại (cho hết những Ngày Tashriq) thì cũng không có tội nhất là đối với người nào sợ Allah. Và hãy sợ Allah, và hãy biết rằng các ngươi sẽ được tập trung đưa về gặp Ngài trở lại (để chịu xét xử ở đời sau).');
INSERT INTO "hasan_abdulkarim" VALUES(2,204,'2:204','Và trong nhân loại có hạng người mà lời nói về (đời sống) trần gian này làm cho Ngươi trầm trồ, và y còn mang Allah ra làm chứng cho những điều nằm trong lòng của y trong lúc y lại là một tên tranh chấp hung hãn nhất.');
INSERT INTO "hasan_abdulkarim" VALUES(2,205,'2:205','Nhưng khi bỏ đi, y vội vã đi làm chuyện ác đức trên trái đất, phá hoại mùa màng và giết hại gia súc. Và chắc chắn Allah không chấp nhận việc ác đức.');
INSERT INTO "hasan_abdulkarim" VALUES(2,206,'2:206','Và khi có lời bảo y ''Hãy sợ Allah'' thì tính ngạo mạn của y làm y phạm tội. Bởi thế, y xứng đáng đi vào hỏa ngục, và chắc chắn hỏa ngục là nơi nghỉ xấu xa nhất.');
INSERT INTO "hasan_abdulkarim" VALUES(2,207,'2:207','Ngược lại, trong nhân loại có hạng người chịu bán mình để làm hài lồng Allah. Và Allah Rất mực Độ lượng với bầy tôi của Ngài.');
INSERT INTO "hasan_abdulkarim" VALUES(2,208,'2:208','Hỡi những ai có đức tin! Hãy gia nhập Islam một cách trọn vẹn( và chớ giẫm theo bước chân của Shaytan bởi vì nó là kẻ thù công khai của các ngươi.');
INSERT INTO "hasan_abdulkarim" VALUES(2,209,'2:209','Nhưng nếu các ngươi sa ngã sau khi đã chứng kiến những bằng chứng rõ rệt thì nên biết rằng Allah Toàn năng, Rất Mực Sáng Suốt.');
INSERT INTO "hasan_abdulkarim" VALUES(2,210,'2:210','Phải chăng họ chỉ còn đợi Allah được Thiên Thần tháp tùng xuống gặp chúng trong bóng mây và vấn đề (do đó) sẽ được giải quyết? Bởi vì mọi vấn đề đều được trình lên cho Allah quyết định.');
INSERT INTO "hasan_abdulkarim" VALUES(2,211,'2:211','Hãy hỏi con cháu của Israel xem có bao nhiêu dấu hiệu rõ rệt mà TA (Allah) đã ban cho họ? Và ai thay đổi Ân huệ của Allah sau khi đã tiếp thu nó thì nên biết Allah rất nghiêm khắc trong việc trừng trị.');
INSERT INTO "hasan_abdulkarim" VALUES(2,212,'2:212','Đời sống trần tục này đẹp đẽ (và hấp dẫn) đối với những kẻ không có đức tin. Chúng chê cười những người có đức tin nhưng (chúng không biết việc) những người ngay chính sợ Allah sẽ ở bên trên chúng vào Ngày phục sinh. Và Allah sẽ cung cấp vô số thiên lộc cho người nào mà Ngài muốn.');
INSERT INTO "hasan_abdulkarim" VALUES(2,213,'2:213','(Ban đầu) nhân loại chỉ là một Cộng đồng (Ummah). Sau đó (vì họ lầm lạc nên) Allah dựng lên các Nabi (xuất thân từ họ) làm những vị vừa mang tin mừng vừa báo tin dữ, và (Allah) gởi cùng với Họ những Kinh Sách bằng sự thật để Họ dựa vào đó mà xét xử nhân loại về những vấn đề (nhân loại) thường tranh chấp nhau. Nhưng không ai tranh chấp nhau trong đó ngoại trừ những kẻ đã được (Allah) ban cho Kinh Sách (bởi vì) sau khi tiếp thu (những Lời giáo huấn) rõ rệt, do lòng đố kỵ và muốn áp bức kẻ khác (nên chúng đâm ra tranh chấp đó thôi). Bởi thế, Allah, với sự chấp thuận của Ngài, đã dẫn dắt những ai có đức tin đến với Chân Lý về những điều chúng thường tranh chấp nhau. Và Allah hướng dẫn người nào Ngài muốn đến Con đường ngay chính (của Islam).');
INSERT INTO "hasan_abdulkarim" VALUES(2,214,'2:214','Hoặc phải chăng các ngươi nghĩ rằng các ngươi sẽ vào thiên đàng trong lúc các ngươi chưa hề từng trải những thử thách tương tự (những thử thách) mà những người đã qua đời trước các ngươi đã nếm? Họ đã từng trải qua nghịch cảnh và hoạn nạn và họ dao động (tinh thần) đến nỗi Sứ Giả (Muhammad) và những tín đồ sát cánh với Người đều phải than: “Chừng nào sự cứu giúp của Allah mới đến?” Này! Sự cứu giúp của Allah chắc chắn ở sát bên (các ngươi đó!).');
INSERT INTO "hasan_abdulkarim" VALUES(2,215,'2:215','Họ hỏi Ngươi, vật gì họ phải chi dùng vào việc bố thí? Hãy bảo họ: “Bất cứ vật gì các ngươi chi ra từ tài sản của các ngươi thì là cho cha mẹ và bà con ruột thịt và trẻ mồ côi và người thiếu thốn và người lỡ đường. Và bất cứ vật nào các ngươi tiêu ra từ tài sản của các ngươi thì quả thật Allah biết rõ hết.');
INSERT INTO "hasan_abdulkarim" VALUES(2,216,'2:216','Lệnh chiến đấu đã được truyền xuống cho các ngươi và đó là điều mà các ngươi không thích. Có lẽ các ngươi ghét điều mà chính nó lại tốt cho các ngươi và có lẽ các ngươi thích điều mà chính nó lại xấu cho các ngươi. Allah biết (giá trị của nó) trong lúc các ngươi không biết.');
INSERT INTO "hasan_abdulkarim" VALUES(2,217,'2:217','Họ hỏi Ngươi (Muhammad) về việc chiến đấu trong những tháng cấm kỵ. Hãy bảo họ: “Chiến đấu trong tháng cấm kỵ là một (vi phạm) trọng đại. Nhưng dưới cái nhìn của Allah thì việc cản trở thiên hạ không cho theo Chính Đạo của Allah, việc phủ nhận Ngài, và việc (cấm thiên hạ đến thờ phụng tại) Thánh đường Linh thiêng (ở Makkah) cũng như việc trục xuất dân cư của nó ra khỏi (thánh địa), (tất cả điều đó) còn trọng đại hơn (việc chiến đấu trong tháng cấm kỵ). Bởi vì (dùng bạo lực để) quấy nhiễu nghiêm trọng hơn việc giết chóc. Và chúng sẽ không ngưng đánh các ngươi cho đến khi nào chúng làm cho các ngươi bỏ đạo của các ngươi nếu có khả năng. Và ai trong các ngươi bỏ đạo và chết trong tình trạng không có đức tin thì là những người mà việc làm sẽ trở thành vô nghĩa ở đời này lẫn đời sau. Và họ sẽ là những người Bạn của Lửa (hỏa ngục) nơi mà họ sẽ vào đó ở đời đời.”');
INSERT INTO "hasan_abdulkarim" VALUES(2,218,'2:218','Quả thật, những ai có đức tin và những ai di cư và chiến đấu cho Chính Nghĩa của Allah là những người hy vọng sẽ được Allah khoan dung bởi vì Allah Hằng Tha Thứ, Rất Mực Khoan Dung.');
INSERT INTO "hasan_abdulkarim" VALUES(2,219,'2:219','Họ hỏi Ngươi về việc uống rượu và cờ bạc. Hãy bảo họ: ‘Trong hai điều đó vừa có một tội lớn vừa có một mối lợi cho nhân loại, nhưng tội của hai thứ đó lớn hơn mối lợi của chúng.” Họ lại hỏi Ngươi phải chi dùng cái gì để (làm việc thiện). Hãy bảo họ: “Vật còn lại (sau khi thỏa mãn những nhu cầu cần thiết.)” Allah trình bày cho các ngươi những Lời Mặc Khải của Ngài đúng như thế để cho các ngươi suy ngẫm;');
INSERT INTO "hasan_abdulkarim" VALUES(2,220,'2:220','(Về ảnh hưởng của chúng) ở đời này và đời sau. Họ hỏi Ngươi về tình trạng của các đứa trẻ mồ côi. Hãy bảo họ: “Cải thiện (đời sống) cho chúng là điều tốt nhất, và nếu các ngươi hợp tác làm ăn với chúng thì (hãy xem) chúng như là anh em của các ngươi bởi vì Allah biết ai là kẻ thối nát so với người ngoan đạo . Và nếu Allah muốn thì Ngài thừa sức làm cho các ngươi gặp hoạn nạn khó khăn. Bởi vì quả thật, Allah Toàn Năng, Rất Mực Sáng Suốt.');
INSERT INTO "hasan_abdulkarim" VALUES(2,221,'2:221','Và chớ kết hôn với các phụ nữ thờ đa thần cho đến khi nào họ có đức tin (nơi Allah) bởi vì một phụ nữ nô lệ có đức tin tốt hơn một người phụ nữ thờ đa thần dẫu rằng họ quyến rũ các ngươi. Và chớ kết hôn (con gái của các ngươi) cho những người đàn ông thờ đa thần cho đến khi nào chúng có đức tin (nơi Allah) bởi vì một người nô lệ nam có đức tin tốt hơn một người đàn ông thờ đa thần dẫu rằng chúng quyến rũ các ngươi. Những người thờ đa thần này mời gọi các ngươi đến với Lửa (của hỏa ngục) trong lúc Allah kêu gọi các ngươi đến thiên đàng và sự Tha thứ theo phép của Ngài. Và (Allah) trình bày rõ các Lời Mặc Khải của Ngài cho nhân loại để họ có thể ghi nhớ (việc hôn nhân).');
INSERT INTO "hasan_abdulkarim" VALUES(2,222,'2:222','Họ hỏi Ngươi về kinh kỳ của phụ nữ. Hãy bảo họ: “Nó là một sự ô nhiễm. Bởi thế, hãy xa phụ nữ (người vợ) trong thời gian có kinh kỳ và chỉ đến gần họ khi nào họ sạch sẽ. Do đó, khi họ (tắm gội) sạch sẽ rồi, các ngươi có thể đến với họ lúc nào, nơi nào như Allah đã chỉ thị cho các ngươi. Quả thật, Allah yêu thương những người năng sám hối và những người giữ mình sạch sẽ.');
INSERT INTO "hasan_abdulkarim" VALUES(2,223,'2:223','Vợ của các ngươi là một miếng đất trồng( cho các ngươi. Do đó, hãy đến gần mảnh đất trồng của các ngươi tùy lúc và theo cách mà các ngươi muốn, nhưng hãy chuẩn bị tư tưởng trước cho bản thân của các ngươi (trong việc tuân lệnh Allah và tránh điều Haram khi đến với họ). Và hãy sợ Allah, và biết rằng các ngươi sẽ gặp Ngài trở lại (ở đời sau), và hãy báo tin lành cho nhữỉig người có đức tin.');
INSERT INTO "hasan_abdulkarim" VALUES(2,224,'2:224','Và chớ mang (tên của) Allah ra làm bình phong chống chế cho những lời thề (trống rỗng) của các ngươi để các ngươi không làm việc đạo đức, không sợ Allah và không hòa giải mọi người, bởi vì Allah Hằng Nghe và Hằng Biết (hết mọi việc các ngươi làm).');
INSERT INTO "hasan_abdulkarim" VALUES(2,225,'2:225','Allah sẽ không gọi các ngươi đến hỏi về những điều vô nghĩa trong lời thề của các ngươi mà Ngài sẽ gọi các ngươi đến hỏi về những điều mà quả tim (tấm lòng) của các ngươi đã cố ý làm ra; và Allah Hằng Tha Thứ và Hằng Chịu đựng (với bầy tôi của Ngài).');
INSERT INTO "hasan_abdulkarim" VALUES(2,226,'2:226','Đối với những ai thề thốt sẽ không ăn nằm với vợ nữa, thời gian chờ đợi (cho họ suy nghĩ và quyết định) là bốn tháng. Nhưng nếu họ quyết định trở lại (với vợ) thì chắc chắn Allah Tha thứ và Khoan dung (cho họ);');
INSERT INTO "hasan_abdulkarim" VALUES(2,227,'2:227','Và nếu họ nhất định ly dị (vợ) thì quả thật Allah Hằng Nghe và Hằng Biết (điều đó).');
INSERT INTO "hasan_abdulkarim" VALUES(2,228,'2:228','Và những người vợ lỵ dị, vì quyền lợi của bản thân, nên ở vậy trong ba (tháng) kinh kỳ. Và họ không được giấu (bào thai) mà Allah đã tạo trong bụng của họ nếu họ tin tưởng nơi Allah và Ngày sau. Và người chồng của họ có quyền ưu tiên lấy họ trở lại trong thời gian đó nếu hai người chịu hòa với nhau. Và theo lẽ công bằng, các bà vợ có quyền (đòi hỏi người chồng chu cấp...) tương đương với quyền (mà người chồng đã dùng để) đối xử với họ (như trung thành và kính trọng chồng...); tuy nhiên người đàn ông (có trách nhiệm) trội hơn người đàn bà một bậc; bởi vì Allah Toàn Năng, Rất Mực Sáng Suốt (khi qui định quyền hạn giữa nam và nữ).');
INSERT INTO "hasan_abdulkarim" VALUES(2,229,'2:229','Việc ly dị chỉ được cho phép (tuyên bố) hai lần. Sau đó, (chồng) giữ (vợ) lại một cách tử tế hoặc trả tự do (cho vợ) một cách tốt đẹp. Và các ngươi không được phép lấy lại bất cứ tiền cưới nào mà các ngươi đã tặng cho vợ, ngoại trừ trường hợp đôi bên sợ không thể giữ được những giới hạn (qui định) bởi Allah. Nhưng nếu các ngươi sợ đôi bên không thể giữ được những giới hạn của Allah thì đôi bên không mắc phải tội về phần tặng vật mà bà vợ dùng để chuộc sự tự do của mình. Đây là những giới hạn qui định bởi Allah. Do đó, chớ vi phạm chúng. Và ai vi phạm những giới hạn của Allah thì là những kẻ làm điều sai quấy.');
INSERT INTO "hasan_abdulkarim" VALUES(2,230,'2:230','Do đó, nếu người chồng đã ly dị vợ thì bà vợ sẽ không còn hợp pháp cho ông chồng cũ nữa trừ phi người vợ kết hôn với một người khác; sau này nếu ông chồng mới (này) ly dị bà ta thì hai vợ chồng (cũ) sẽ không mắc tội nếu họ thuận tái hợp với nhau và nếu họ nghĩ rằng họ có thể tôn trọng những giới hạn của Allah. Đó là những giới hạn mà Allah trình bày cho những người hiểu biết.');
INSERT INTO "hasan_abdulkarim" VALUES(2,231,'2:231','Và khi các ngươi ly dị vợ và họ sắp hoàn tất thời hạn ở vậy (''Iddah) thì hoặc giữ họ lại một cách tử tế hoặc trả tự do cho họ một cách tốt đẹp; và chớ giữ họ lại để làm khổ họ, làm thế các ngươi sẽ phạm tội. Và ai làm thế thì thật sự sẽ làm hại bản thân (linh hồn) mình; và chớ mang các Lời Mặc Khải của Allah ra làm điều bỡn cợt, và hãy nhớ Ân huệ của Allah đã ban cho các ngươi trong Kinh Sách và điều đúng đắn khôn ngoan mà Ngài dùng để khuyên bảo các ngươi; và hãy thành kính sợ Allah, và nên nhớ rằng chắc chắn Allah biết hết mọi việc.');
INSERT INTO "hasan_abdulkarim" VALUES(2,232,'2:232','Khi các ngươi ly dị vợ và họ hoàn tất thời hạn ở vậy (''Iddah) của họ thì chớ cản họ tái hôn với người chồng cũ nếu đôi bên thuận tái hợp một cách tốt đẹp. Đó là điều dùng để khuyên bảo những ai trong các ngươi tin tưởng nơi Allah và Ngày sau. Điều đó trong sạch và sạch sẽ cho các ngươi bởi vì Allah biết (Phúc và Tội) trong lúc các ngươi không biết.');
INSERT INTO "hasan_abdulkarim" VALUES(2,233,'2:233','Những bà mẹ ly dị sẽ cho con bú sữa (mẹ) suốt hai năm tròn đối với người (cha) nào muốn cho con họ bú vú mẹ đủ thời hạn, nhưng người cha (của đứa con) phải chịu mọi phí tổn về ăn uống và may mặc cho các bà mẹ một cách tử tế. Mỗi linh hồn chỉ bị bắt làm việc tuỳ theo khả năng của nó; không bà mẹ nào bị làm khổ vì đứa con của bà, cũng không người cha nào bị làm khổ vì đứa con của ông ta. Và đối với người thừa kế, trách nhiệm cũng sẽ như thế. Nhưng nếu đôi bên (vợ chồng ly dị) tham khảo ý kiến lẫn nhau và thỏa thuận thôi cho con bú sữa mẹ thì hai người không mắc tội nếu các ngươi tìm được một bà vú nuôi cho đứa con của các ngươi; và các ngươi không mắc tội nếu các ngươi tặng bà vú nuôi (tiền thù lao) mà các ngươi đã đồng ý tặng họ một cách lịch thiệp. Và hãy sợ Allah, và hãy nhớ rằng Allah thấy rõ những điều các ngươi làm.');
INSERT INTO "hasan_abdulkarim" VALUES(2,234,'2:234','Và những ai trong các ngươi chết bỏ vợ lại, các goá phụ này vì quyền lợi của bản thân sẽ phải ở vậy (''Iddah) bốn tháng và mười ngày. Do đó, khi họ hoàn tất thời hạn ở vậy, các ngươi không có tội về việc họ tự giải quyết bản thân của họ một cách biết điều. Và Allah là Đấng Am Tường những điều các ngươi làm.');
INSERT INTO "hasan_abdulkarim" VALUES(2,235,'2:235','Và các ngươi không có tội nếu các ngươi gián tiếp ngỏ lời đính hôn với các phụ nữ hoặc giữ kín trong lòng các ngươi. Allah biết rằng các ngươi sẽ tưởng nhớ các nàng ấy, nhưng chớ lén lút hứa hẹn với họ trừ phi các ngươi nói ra lời lẽ phong nhã lịch thiệp và chớ nhất quyết ràng buộc việc thành hôn cho đến khi nào mãn hạn qui định của nó; và hãy biết rằng Allah biết điều nằm trong tâm hồn của các ngươi. Do đó, hãy thận trọng giữ mình đối với Ngài, và hãy biết rằng Allah Hằng Tha Thứ, Hằng Chịu đựng (với các ngươi).');
INSERT INTO "hasan_abdulkarim" VALUES(2,236,'2:236','Các ngươi không có tội nếu ly dị vợ trước khi động phòng hoặc chưa định cho họ một phần tặng vật nào; và hãy tặng các bà một món quà tặng (Mahr) thích hợp, người giàu tùy theo phương tiện của mình, người nghèo tùy theo phương tiện của mình, một món quà tặng có tính bắt buộc đối với những người làm tốt.');
INSERT INTO "hasan_abdulkarim" VALUES(2,237,'2:237','Và nếu các ngươi ly dị vợ trước khi động phòng nhưng sau khi đã định cho họ một phần quà tặng thì phân nửa của tặng vật mà các ngươi đã định cho họ không còn là của các ngươi nữa trừ phi các bà từ khước nhận nó hoặc từ khước bởi người đàn ông mà sự ràng buộc hôn nhân nằm trong tay của họ (người giám hộ). Và hãy nhớ rằng từ khước (phân nửa của tặng vật) gần với việc sợ Allah. Và chớ quên đối xử độ lượng giữa các ngươi với nhau. Bởi vì Allah thấy rõ những điều các ngươi làm.');
INSERT INTO "hasan_abdulkarim" VALUES(2,238,'2:238','Hãy chăm sóc và giữ vững các lễ nguyện Salah, nhất là cuộc lễ nguyện Salah chính giữa (''Asr) và hãy đứng (dâng lễ) trước Allah với tinh thần sùng kính hoàn toàn;');
INSERT INTO "hasan_abdulkarim" VALUES(2,239,'2:239','Nếu các ngươi sợ (địch tấn công) thì hãy dâng lễ trong tư thế đi bộ hoặc trong tư thế cưỡi trên lưng con vật; nhưng khi các ngươi được bình yên trở lại, hãy (dâng lễ) tưởng nhớ Allah theo cách mà Ngài đã dạy các ngươi, điều mà các ngươi không biết trước đó.');
INSERT INTO "hasan_abdulkarim" VALUES(2,240,'2:240','Và những ai trong các ngươi chết bỏ vợ ở lại (trần gian) thì nên lập di chúc cho các quả phụ bằng một năm cấp dưỡng và không được trục xuất họ ra khỏi nhà; nhưng nếu họ bỏ đi thì các ngươi không có tội về điều họ tự giải quyết bản thân của họ một cách biết điều. Và Allah Toàn Năng, Rất Mực Sáng Suốt (trong việc quy định đó).');
INSERT INTO "hasan_abdulkarim" VALUES(2,241,'2:241','Và các bà vợ ly dị được hưởng một số tiền cấp dưỡng có tính bắt buộc đối với những người ngay chính sợ Allah.');
INSERT INTO "hasan_abdulkarim" VALUES(2,242,'2:242','Allah trình bày rõ các Lời Mặc Khải của Ngài đúng như thế để cho các ngươi có thể hiểu được (những giới hạn do Ngài qui định).');
INSERT INTO "hasan_abdulkarim" VALUES(2,243,'2:243','Há Ngươi không để ý đến hàng ngàn người vì sợ chết mà bỏ nhà cửa của họ ra đi hay sao? Allah đã phán với họ: “Hãy quyết tử!” rồi Ngài làm cho họ sống trở lại; Quả thật, Allah rất mực Nhân từ với loài người, nhưng đa số nhân loại phụ ơn (Ngài).');
INSERT INTO "hasan_abdulkarim" VALUES(2,244,'2:244','Và hãy chiến đấu cho Chính Nghĩa của Allah, và nên biết rằng quả thật Allah Hằng Nghe và Hằng Biết (mọi việc).');
INSERT INTO "hasan_abdulkarim" VALUES(2,245,'2:245','Ai là người sẽ cho Allah mượn một món vật (mượn) tốt thì Ngài sẽ trả lại gấp đôi và nhiều hơn nữa? Bởi vì Allah (là Đấng) ban ít hay ban nhiều (bổng lộc) và các ngươi sẽ được đưa về gặp Ngài trở lại (ở đời sau).');
INSERT INTO "hasan_abdulkarim" VALUES(2,246,'2:246','Há Ngươi không để ý đến vấn đề của những vị cầm đầu trong hàng ngũ của con cháu của Israel sau thời đại của Musa hay sao khi họ nói với một vị Nabi (tên Samuel) của họ như sau: “Hãy chỉ định một vị vua cho chúng tôi để (lãnh đạo) chúng tôi chiến đấu cho Chính Nghĩa của Allah?” (Nabi ấy) đáp: “Biết đâu các ngươi sẽ không chiến đấu khi lệnh (chiến đấu) được truyền xuống cho các ngươi!” (Họ) bảo: ‘Tại sao chúng tôi không chiến đấu trong lúc chúng tôi lẫn con cháu của chúng tôi đã bị trục xuất ra khỏi nhà cửa của chúng tôi?” Nhưng khi lệnh chiến đấu được truyền xuống cho họ thì họ đào nhiệm ngoại trừ một số ít. Và Allah biết rõ những kẻ sai phạm.');
INSERT INTO "hasan_abdulkarim" VALUES(2,247,'2:247','Và Nabi (Samuel) của họ đã bảo họ: “Allah đã chỉ định Talut làm vua của các ngươi.” Họ đáp: “Làm sao ông ta có thể làm vua cai trị chúng tôi trong lúc chúng tôi xứng đáng hơn ông ta. Hơn nữa, ông ta cũng không được giàu sang ghú quí mà?” (Nabi của họ) đáp: “Quả thật, Allah đã chọn ông ta đề lãnh đạo các ngươi và ban cho ông ta kiến thức dồi dào và thể lực hơn người. Và Allah ban quyền hành cho người nào Ngài muốn bởi vì Allah Bao quát (hết mọi việc) và Hiểu biết (hết mọi vấn đề).');
INSERT INTO "hasan_abdulkarim" VALUES(2,248,'2:248','Và Vị Nabi của họ bảo tiếp: “(Sẽ có) một dấu hiệu (xác nhận) vương quyền của ông ta là các ngươi sẽ nhận được một cái rương chứa thánh vật tượng trưng cho sự thanh bình do Thượng Đế (Allah) của các ngươi ban xuống và cả những thánh vật mà gia đình của Musa và gia đình của Harun đã để lại do những Thiên Thần mang đến. Quả thật, trong sự việc đó, có một dấu hiệu cho các ngươi nếu các ngươi có đức tin.');
INSERT INTO "hasan_abdulkarim" VALUES(2,249,'2:249','Bởi thế, khi Talut ra quân đi chiến đấu, (Talut) bảo (binh tướng): “Chắc chắn Allah sẽ thử thách các ngươi với (nước của) một con sông. Ai uống nước sông đó thì sẽ không là người của ta; và ai không uống thì sẽ là người của ta trừ phi người nào uống từng hớp từ lòng bàn tay của mình.” Nhưng họ (không nghe lời và) đã uống nước sông đó ngoại trừ một số rất ít. Bởi thế, khi họ băng qua sông, (Talut) với những ai có đức tin cùng theo Người lên tiếng: “Ngày nay chúng ta không đủ sức đương đầu với Jalut và đoàn quân của y. Tuy nhiên, những ai nghĩ rằng bề nào họ cũng phải gặp Allah trở lại, đã lên tiếng: “Đã có không biết bao nhiêu lần một lực lượng nhỏ có thể đánh bại một lực lượng lớn với sự cho phép của Allah? Bởi vì Allah ở cùng với những người kiên cường bất khuất".');
INSERT INTO "hasan_abdulkarim" VALUES(2,250,'2:250','Và khi họ tiến quân lên đương đầu với Jalut và đoàn quân của y, họ (cầu nguyện) thưa: “Lạy Thượng Đế chúng tôi! Xin Ngài xối lên chúng tôi (tinh thần) kiên trì nhẫn nại và làm cho bàn chân của chúng tôi vững chắc và giúp chúng tôi đánh thắng những kẻ ngoại giáo không tin.”');
INSERT INTO "hasan_abdulkarim" VALUES(2,251,'2:251','Bởi thế, với sự chấp thuận của Allah, họ đánh tan (quân địch) và Dawood đã giết chết Jalut và Allah đã ban cho Y (Dawood) quyền bính và lẽ đúng đắn khôn ngoan và dạy Người điều mà Ngài muốn. Và nếu Allah không dùng một nhóm người này để chặn đứng một nhóm người kia thì trái đất sẽ rối loạn và thối nát. Nhưng chắc chắn Allah Rất mực Nhân từ Độ lượng với thiên hạ.');
INSERT INTO "hasan_abdulkarim" VALUES(2,252,'2:252','Đấy là những Lời Mặc Khải của Allah. TA đọc chúng cho Ngươi (Muhammad) bằng sự thật. Và thật sự Ngươi là một trong các Sứ Giả (của Allah).');
INSERT INTO "hasan_abdulkarim" VALUES(2,253,'2:253','Đó là các Sứ Giả (của Allah); TA đã ưu đãi một số người này hơn một số người nọ. Trong họ, có người Allah nói chuyện trực tiếp, và nâng cao cấp bậc danh dự; và TA đã ban cho Ysa, con trai của Maryam, những bằng chứng rõ rệt và hỗ trợ Y với Ruh al-Qudus (Đại Thiên Thần Jibril); và nếu Allah muốn thì những người sống sau (thời đại của các Thiên sứ) đã không chiến tranh lẫn nhau sau khi đã chứng kiến những bằng chứng rõ rệt (của Allah) nhưng họ bất đồng ý kiến với nhau, bởi thế trong họ có một số người tin tưởng và có một số không tin. Và nếu Allah muốn thì họ đã không chiến tranh lẫn nhau, nhưng Allah đã làm bất cứ điều gì Ngài muốn.');
INSERT INTO "hasan_abdulkarim" VALUES(2,254,'2:254','Hỡi những ai có đức tin! Hãy chi dùng tài sản mà TA (Allah) đã cung cấp cho các ngươi (để làm việc thiện) trước khi xảy ra Ngày mà sẽ không có việc mua bán đổi chác, sẽ không có tình bạn hữu (bao che cho nhau) và cũng sẽ không có sự can thiệp nào (được chấp thuận). Và những kẻ không có đức tin là những kẻ làm điều sai quấy.');
INSERT INTO "hasan_abdulkarim" VALUES(2,255,'2:255','Allah! Không có Thượng Đế nào khác ngoài Ngài (Allah), Đấng Hằng Sống, Đấng Tự Hữu và Nuôi Dưỡng vạn vật. Ngài không buồn ngủ, cũng không ngủ. Mọi vật trong các tầng trời và trái đất đều là (những tạo vật) của Ngài. Ai là người có thể can thiệp được với Ngài nếu không có phép của Ngài? Ngài biết điều xảy ra trước họ và điều xảy ra sau họ. Họ không thể bao quát được sự hiểu biết của Ngài về bất cứ điều gì ngoại trừ điều nào Ngài muốn cho họ biết. Ngai vàng (Kursi) của Ngài bao trùm cả các tầng trời và trái đất; và việc quản lý trời đất không làm cho Ngài mỏi mệt bởi vì Ngài là Đang Tối Cao, Đấng Chí Đại.');
INSERT INTO "hasan_abdulkarim" VALUES(2,256,'2:256','Không có việc cưỡng bách (tín ngưỡng) trong lãnh vực tôn giáo. Chắc chắn Chân Lý và Lẽ phải bao giờ cũng khác biệt với điều ngụy tạo và sự sai lạc. Bởi thế, ai phủ nhận Tà thần và tin tưởng nơi Allah thì chắc chắn sẽ nắm vững chiếc cán (hay sợi dây cứu rỗi) không bao giờ đứt. Bởi vì Allah Hằng Nghe và Hằng Biết (mọi việc).');
INSERT INTO "hasan_abdulkarim" VALUES(2,257,'2:257','Allah là Đấng Bảo hộ của những người có đức tin. Ngài đưa họ từ tăm tối ra ánh sáng. Ngược lại, các chủ nhân của những kẻ không có đức tin là những tên Tà thần. Chúng dắt họ từ chỗ ánh sáng xuống nơi tối tăm. Những người này sẽ làm bạn với Lửa (của hỏa ngục). Trong đó, họ sẽ ở đời đời.');
INSERT INTO "hasan_abdulkarim" VALUES(2,258,'2:258','Hẳn Ngươi (Muhammad) không suy ngẫm về bạo chúa (Nimrud) đã tranh luận với Ibrahim về Thượng Đế của Y bởi vì Allah đã ban cho hắn quyền bính hay sao? Khi Ibrahim bảo hắn: “Thượng Đế của tôi là Đấng ban sự sống và gây sự chết.” (Hắn) đáp: “Ta cũng (có quyền) ban sự sống và làm cho chết vậy.” Ibrahim đáp: “Nhưng Allah (Thượng Đế của tôi) làm cho mặt trời mọc từ hướng đông, thế ngài làm cho nó mọc từ hướng tây đi (nếu ngài có khả năng?)” Bởi thế, kẻ không có đức tin đó đâm ra lúng túng (vì đuối lý). Và Allah không hướng dẫn đám người làm điều sai quấy.');
INSERT INTO "hasan_abdulkarim" VALUES(2,259,'2:259','Hoặc như ai đó( đã đi ngang qua một thị trấn hoang tàn, nhà cửa sụp đổ. Y bảo: “Làm sao Allah phục sinh lại thị trấn này sau khi nó đã chết (thế này?)" Bởi thế, Allah làm cho y chết một trăm năm rồi dựng y sống lại. (Allah hỏi y): “Nhà ngươi ở lại đó bao lâu?” Y thưa: “Bề tôi ở lại đó một ngày hay một buổi gì đó.” (Allah) bảo y: “Không, nhà ngươi đã ở lại đó một trăm năm rồi. Thế hãy nhìn thức ăn và thức uống của nhà ngươi, chúng chẳng tỏ dấu hiệu gì mục rã cả; và hãy nhìn con lừa của nhà ngươi, và TA lấy ngươi làm một dấu hiệu cho nhân loại; và nhà ngươi hãy nhìn những khúc xương xem TA đã dựng và sắp xếp chúng, rồi TA lấy thịt bao chúng trở lại như thế nào? Bởi thế khi y nhận thức được thực tại, y vội thưa: “Bề tôi biết quả thật Allah có quyền trên tất cả mọi vật.”');
INSERT INTO "hasan_abdulkarim" VALUES(2,260,'2:260','Và hãy nhớ lại khi Ibrahim thưa (với Allah): “Lạy Thượng Đế của bề tôi! Xin Ngài cho bề tôi thấy việc Ngài làm cho người chết sống lại như thế nào?” (Allah) phán bảo (Ibrahim): “Hẳn nhà ngươi không tin (việc phục sinh) hay sao?” (Ibrahim) thưa: “Bẩm không phải thế, nhưng để cho bề tôi được yên tâm (về việc đó.)” (Allah) phán: “Hãy bắt bốn con chim, tập luyện cho chúng xu hướng về ngươi (cột chúng lại và bầm chúng thành từng mảnh), rồi đặt thịt bầm của chúng tại mỗi ngọn đồi, rồi gọi chúng, chúng sẽ bay nhanh đến với ngươi. Và hãy biết rằng Allah Toàn Năng, Toàn Tri (đủ khả năng và sự hiểu biết để làm mọi việc).');
INSERT INTO "hasan_abdulkarim" VALUES(2,261,'2:261','Hình ảnh của những ai chi dùng của cải của họ cho Chính Nghĩa của Allah giống như một hạt lúa trổ ra bảy bông lúa, mỗi bông lúa trổ ra một trăm hạt lúa. Allah tăng nó nhiều cho người nào Ngài muốn bởi vì Allah Rộng rãi Bao la và Biết (hết mọi việc).');
INSERT INTO "hasan_abdulkarim" VALUES(2,262,'2:262','Những ai chi dùng của cải của họ cho Chính Nghĩa của Allah rồi không kèm theo lời nhắc khéo về sự rộng rãi của mình cũng không làm tổn thương danh dự của người khác thì sẽ nhận phần thưởng của họ nơi Thượng Đế của họ. Họ sẽ không lo sợ cũng sẽ không buồn phiền.');
INSERT INTO "hasan_abdulkarim" VALUES(2,263,'2:263','Lời nói tử tế và sự tha thứ tốt hơn của bố thí kèm theo việc làm tổn thương danh dự của người khác bởi vì Allah rất mực Giàu Có (không thiếu thôn chi), hằng Chịu Đựng (vì bầy tôi của Ngài).');
INSERT INTO "hasan_abdulkarim" VALUES(2,264,'2:264','Hỡi những ai có đức tin! Chớ làm cho việc bố thí của các ngươi thành vô nghĩa bằng cách nhắc khéo về lòng rộng rãi của mình và với lời lẽ làm tổn thương danh dự của người khác giống như (việc làm của) những kẻ chi dùng của cải của mình phô trương cho thiên hạ thấy chứ không tin tưởng nơi Allah và Ngày sau. Bởi vì hình ảnh của một người như thế giống hình ảnh của một tảng đá trơn láng có một lớp bụi phủ bên trên; khi mưa rào đổ lên nó, (nước mưa) quét sạch lớp bụi ấy đi bỏ nó nằm trơ trọi một mình. Họ sẽ không định đọat được gì về những vật mà họ đã thu hoạch. Và Allah không hướng dẫn một dân tộc không có đức tin.');
INSERT INTO "hasan_abdulkarim" VALUES(2,265,'2:265','Và hình ảnh của những ai chi dùng của cải của họ nhằm làm hài lòng Allah và để củng cố tâm hồn của họ thêm vững chắc giống như một ngôi vườn được tọa lạc trên một gò đất cao và phì nhiêu; khi mưa rào tuôn lên nó, nước mưa làm tăng vụ mùa gấp đôi; nhưng nếu không có mưa rào thì mưa rươi cũng đủ làm cho nó tăng trưởng. Và Allah thấy rõ điều các ngươi làm.');
INSERT INTO "hasan_abdulkarim" VALUES(2,266,'2:266','Trong các ngươi, há ai muốn có một ngôi vườn chà là và vườn nho có mạch nước chảy bên dưới với đủ loại trái cây bên trong được dành sẵn cho y, nhưng khi y đến tuổi già, con cái của y hãy còn thơ dại, rồi một trận gió nóng như lửa ào đến đốt cháy ngôi vườn và tàn phá nó đi hay không? Allah trình bày rõ những Lời Mặc Khải của Ngài đúng như thế để may ra các ngươi ngẫm nghĩ lại (về việc bố thí của các ngươi).');
INSERT INTO "hasan_abdulkarim" VALUES(2,267,'2:267','Hỡi những ai có đức tin! Hãy chi dùng những món vật tốt mà các ngươi đã thu hoạch được (để bố thí) và những vật mà TA đã sản xuất cho các ngươi từ đất đai; và chớ tìm vật nào xấu của nó mà tiêu ra (cho người khác), vật mà chính các ngươi cũng không muốn nhận trừ phi các ngươi nhắm mắt làm ngơ; và hãy biết rằng Allah rất mực giàu Có (không thiếu chi), Rất đáng Ca tụng.');
INSERT INTO "hasan_abdulkarim" VALUES(2,268,'2:268','Shaytan hăm dọa (làm cho) các ngươi trở thành nghèo khó và bắt các ngươi làm điều thô bỉ trong lúc Allah hứa tha thứ và làm cho các ngươi phát đạt với thiên lộc của Ngài. Bởi vì Allah Rộng rãi Bao la, Biết Hết (mọi việc).');
INSERT INTO "hasan_abdulkarim" VALUES(2,269,'2:269','(Allah) ban sự thông thái khôn ngoan cho người nào Ngài muốn. Và ai được Ngài ban cho sự thông thái khôn ngoan thì chắc chắn là được ban cho muôn vàn cái tốt. Và không ai tiếp thu được sự giác ngộ ngoại trừ những người hiểu biết.');
INSERT INTO "hasan_abdulkarim" VALUES(2,270,'2:270','Và quả thật Allah biết (không sót) một vật gì mà các ngươi chi ra (để bố thí) hoặc một lời thề nào các ngươi đã thề thốt. Và những kẻ làm điều bất công sẽ không được ai giúp đỡ.');
INSERT INTO "hasan_abdulkarim" VALUES(2,271,'2:271','Nếu các ngươi công bố vật bố thí của các ngươi cho thiên hạ biết, điều đó cũng tốt thôi. Ngược lại, nếu các ngươi giấu kín và trao nó đến tận tay của người nghèo, điều đó tốt cho các ngươi hơn; (việc từ thiện) sẽ xóa đi khỏi các ngươi một số tội lỗi. Và Allah Rất Am Tường về những điều (tốt) các ngươi đã làm.');
INSERT INTO "hasan_abdulkarim" VALUES(2,272,'2:272','(Hỡi Muhammad!) Không phải Ngươi hướng dẫn họ theo Chính Đạo mà Allah (là Đấng) hướng dẫn người nào mà Ngài muốn, theo Chính Đạo. Và bất cứ món (vật bố thí) tốt nào các ngươi tiêu ra thì chính bản thân (linh hồn) của các ngươi sẽ hưởng lợi (về nó); và các ngươi tiêu ra như thế là chỉ để làm cho Allah hài lòng mà thôi. Và bất cứ vật tốt nào các ngươi tiêu ra, nó sẽ được trả lại cho các ngươi đầy đủ và các ngươi sẽ không bị đối xử thiệt thòi một mảy may nào.');
INSERT INTO "hasan_abdulkarim" VALUES(2,273,'2:273','(Của bố thí là dành) cho người nghèo, những ai vì Chính Nghĩa của Allah đã bị hạn chế trong việc đi đây đó trên mặt đất (để tìm kế sinh nhai); những kẻ kém hiểu biết thấy tư cách khiêm tốn của họ tưởng rằng họ đầy đủ không cần gì khác; Ngươi (hỡi Sứ Giả!) có thể nhận biết hoàn cảnh của họ qua (những nét đặc biệt) trên gương mặt của họ; họ không trơ trẽn xin xỏ và quấy rầy thiên hạ. Bởi thế, bất cứ vật tốt nào các ngươi tiêu ra cho họ, chắc chắn Allah biết rõ nó.');
INSERT INTO "hasan_abdulkarim" VALUES(2,274,'2:274','Những ai chi dùng tài sản của họ đêm và ngày (vào việc từ thiện) một cách kín đáo hay công khai, sẽ nhận phần thưởng của họ nơi Thượng Đế (Allah) của họ; họ sẽ không lo sợ cũng sẽ không buồn phiền.');
INSERT INTO "hasan_abdulkarim" VALUES(2,275,'2:275','Những ai ăn (lấy) tiền lời cho vay (Riba) sẽ không đứng vững trừ phi đứng như một kẻ đã bị Shaytan sờ mó và làm cho điên cuồng. Sở dĩ như thế là vì họ nói: “Thương mại có khác gì với việc cho vay lấy lãi đâu.” Nhưng Allah cho phép buôn bán và cấm cho vay lấy lãi. Bởi thế, ai đã nhận được lệnh cảnh cáo của Allah và ngưng (cho vay lấy lãi) thì sẽ được (Allah) tha thứ về quá khứ của y; và công việc của y sẽ được trình lên cho Allah quyết định. Ngược lại, ai tái phạm thì sẽ làm Bạn của Lửa (hỏa ngục); trong đó, họ sẽ vào ở đờiđời.');
INSERT INTO "hasan_abdulkarim" VALUES(2,276,'2:276','Allah xóa bỏ (định chế) ''Riba'' (cho vay lấy lãi) và làm phát đạt việc bố thí (Sadaqah); và Allah không thương những kẻ phụ ơn và tội lỗi.');
INSERT INTO "hasan_abdulkarim" VALUES(2,277,'2:277','Quả thật, những ai có đức tin và làm việc thiện, và chu đáo dâng lễ Salah và đóng Zakah thì sẽ nhận phần thưởng của họ ở nơi Thượng Đế của họ; họ sẽ không lo sợ cũng sẽ không buồn phiền.');
INSERT INTO "hasan_abdulkarim" VALUES(2,278,'2:278','Hỡi những ai có đức tin! Nếu các ngươi là những người có đức tin thật sự thì hãy sợ Allah và bỏ phần còn lại của tiền lời cho vay.');
INSERT INTO "hasan_abdulkarim" VALUES(2,279,'2:279','Nhưng nếu các ngươi không thi hành thì hãy coi chừng chiến tranh từ Allah và Sứ Giả của Ngài; nếu các ngươi hối cải thì sẽ được tiền vốn của các ngươi trở lại. Chớ đối xử bất công với người khác và các ngươi sẽ không bị đối xử bất công trở lại.');
INSERT INTO "hasan_abdulkarim" VALUES(2,280,'2:280','Và nếu người thiếu nợ lâm vào hoàn cảnh khó khăn, hãy gia hạn cho y cho đến khi y cảm thấy nhẹ gánh lo (trong việc trả nợ); nhưng nếu các ngươi bố thí (tiền nợ) cho họ thì điều đó tốt nhất cho các ngươi, nếu các ngươi biết.');
INSERT INTO "hasan_abdulkarim" VALUES(2,281,'2:281','Và hãy sợ Ngày mà các ngươi sẽ được đưa về gặp Allah trở lại (để chịu xét xử). Rồi mỗi linh hồn sẽ được trả lại đầy đủ về những điều (tội lỗi) mà nó đã phạm và họ sẽ không bị đối xử bất công.');
INSERT INTO "hasan_abdulkarim" VALUES(2,282,'2:282','Hỡi những ai có đức tin! Khi các ngươi giao dịch mượn tiền bạc của nhau trong một thời hạn ấn định, hãy viết nó xuống thành giấy tờ và hãy gọi một người biên chép đến ghi chép những điều kết ước giữa đôi bên một cách vô tư và công bằng; người biên chép không được từ chối viết theo điều mà Allah đã dạy y. Bởi thế, hãy để cho y viết. Và người mắc nợ đọc ra số nợ; y phải sợ Allah, Thượng Đế của y; và y không được bớt một tí nào số nợ của y; nếu người mắc nợ không được sáng suốt (về tinh thần), hoặc yếu ớt hoặc không có khả năng đọc nó thì người giám hộ của y đọc số nợ ấy một cách vô tư và công bằng; và hãy gọi hai người đàn ông của các ngươi đến làm chứng; nhưng nếu không có hai người đàn ông thì chọn một người đàn ông và hai người đàn bà của các ngươi đến làm chứng; mục đích là nếu một trong hai nữ nhân chứng đó nhầm lẫn, người nọ sẽ nhắc lại người kia; và những nhân chứng không được từ chối khi được mời. Và chớ ái ngại viết nó thành văn dù số nợ nhỏ hay lớn đến kỳ hạn của nó. Đối vđi Allah, điều đó công bằng hơn, chính xác hơn khi đưa ra bằng chứng và thích hợp hơn trong việc chặn đứng điều nghi kỵ lẫn nhau; ngoại trừ trường hợp mua bán tại chỗ giữa các ngươi với nhau thì các ngươi không phạm tội nếu không viết thành văn bản và hãy gọi nhân chứng đến làm chứng khỉ các ngươi thương lượng buôn bán. Và chớ hại người biên chép lẫn nhân chứng; và nếu các ngươi hại họ thì quả thật đó là một hành động hung ác nơi các ngươi. Và hãy sợ Allah bởi vì Allah dạy các ngươi (làm tốt) và Allah Hằng Biết mọi việc.');
INSERT INTO "hasan_abdulkarim" VALUES(2,283,'2:283','Và nếu các ngươi đi xa (nhà) và không tìm ra một người biên chép thì một lời hứa danh dự với tín vật (có thể làm bảo chứng thay cho tờ giao kèo). Nhưng nếu một trong các ngươi tín nhiệm lẫn nhau mà giao cho người kia giữ tín vật thì người thụ ủy phải làm tròn trách nhiệm của anh ta (bằng cách giao hoàn tín vật lại cho chủ nhân của nó) và hãy để cho y sợ Allah, Thượng Đế của y; và y không được giấu giếm bằng chứng. Và ai giấu giếm bằng chứng thì quả thật tấm lòng của y vướng tội, bởi vì Allah Biết rõ điều các ngươi làm.');
INSERT INTO "hasan_abdulkarim" VALUES(2,284,'2:284','Mọi vật trong các tầng trời và trái đất đều là (tạo vật) của Allah cả. Và nếu các ngươi bộc lộ hay giấu kín điều nằm trong lòng của các ngươi thì Allah sẽ gọi các ngươi đến hỏi về điều đó. Bởi thế, Ngài sẽ tha thứ cho người nào Ngài muốn và trừng phạt kẻ nào Ngài muốn bởi vì Allah có quyền định đọat trên tất cả mọi vật.');
INSERT INTO "hasan_abdulkarim" VALUES(2,285,'2:285','Sứ Giả (Muhammad) tin tưởng nơi điều (mặc khải) đã được ban xuống cho Y từ Thượng Đế của Y và những người có đức tin (cũng tin tưởng như Y). Tất cả đều tin tưởng nơi Allah, nơi các Thiên Thần của Ngài, các Kinh Sách của Ngài, và các Sứ Giả của Ngài. (Họ nói): “Chúng tôi không phân biệt và kỳ thị một vị nào trong các Sứ Giả của Ngài.” Và họ thưa: “Bầy tôi nghe và vâng lời. Xin Ngài tha thứ cho bầy tôi, lạy Thượng Đế của bầy tôi, bởi vì bề nào bầy tôi cũng phải trở về gặp lại Ngài (ở đời sau).”');
INSERT INTO "hasan_abdulkarim" VALUES(2,286,'2:286','Allah không bắt một linh hồn vác gánh nặng (trách nhiệm) quá khả năng của nó. Nó hưởng (phúc) về điều tốt mà nó đã làm ra và chịu phạt về tội mà nó đã gây ra. (Lời cầu nguyện của những tín đồ sẽ là): “Lạy Thượng Đế của bầy tôi! Xin Ngài đừng bắt tội bầy tôi nếu bầy tôi quên hay nhầm lẫn. Lạy Thượng Đế của bầy tôi xin Ngài đừng bắt bầy tôi vác gánh nặng giống (gánh nặng) mà Ngài đã đặt lên những người trước bầy tôi. Lạy Thượng Đế của bầy tôi! Xin Ngài đừng bắt bầy tôi vác gánh nặng vượt quá khả năng của bầy tôi. Xin Ngài lượng thứ và khoan hồng bầy tôi. Ngài là Đấng Bảo hộ che chở bầy tôi, bởi thế xin Ngài yểm trợ bầy tôi thắng những kẻ không có đức tin.”');
INSERT INTO "hasan_abdulkarim" VALUES(3,1,'3:1','Alif. Lãm. Mĩm.');
INSERT INTO "hasan_abdulkarim" VALUES(3,2,'3:2','Allah! Không có Thượng Đế nào khác, duy chỉ Ngài (là Thượng Đế), Đấng Hằng Sống, Đấng Tự hữu và Nuôi Dưỡng vạn vật.');
INSERT INTO "hasan_abdulkarim" VALUES(3,3,'3:3','Ngài ban Kinh Sách (Qur''an) xuống cho Ngươi (Muhammad!) để xác nhận lại điều (mặc khải) đã được ban xuống trước Nó (Qur''an); và Ngài đã ban Kinh Tawrah (cho Musa) và Kinh Injil (cho Ysa).');
INSERT INTO "hasan_abdulkarim" VALUES(3,4,'3:4','Để làm một Chỉ Đạo cho nhân loại trước đó và Ngài đã ban xuống Furqan (Chuẩn mực phân biệt phúc và tội). Quả thật, những ai phủ nhận các Lời Mặc Khải của Allah thì sẽ nhận một sự trừng phạt khủng khiếp; và Allah là Đấng Toàn Năng, nắm việc trừng phạt.');
INSERT INTO "hasan_abdulkarim" VALUES(3,5,'3:5','Quả thật, Allah là Đấng mà bất cứ vật gì ở dưới đất hay trên trời đều không thể giấu giếm khỏi Ngài đặng.');
INSERT INTO "hasan_abdulkarim" VALUES(3,6,'3:6','Ngài là Đấng đã nắn tạo các ngươi trong dạ con (của các bà mẹ) như thế nào tùy theo Ngài muốn. Không có Thượng Đế nào khác duy chỉ Ngài, Đấng Toàn năng, Đấng Rất Mực Sáng Suốt.');
INSERT INTO "hasan_abdulkarim" VALUES(3,7,'3:7','Ngài là Đấng đã ban Kinh Sách (Qur’an) xuống cho Ngươi (Muhammad!) trong đó có những câu cụ thể rõ ràng. Chúng là nền tảng của Kinh Sách trong lúc những câu khác thì đề cập tổng quát các vấn đề. Bởi thế, những ai trong lòng có ý tưởng lệch lạc thì tuân theo những câu nói tổng quát của nó mà tìm cách gây chia rẽ và giải thích ý nghĩa của nó lệch đi. Và chỉ riêng Allah thôi biết rõ ý nghĩa thật sự của nó. Và những người có trình độ hiểu biết vững chắc đều thưa: “Bầy tôi tin nơi Nó; toàn bộ đều đến từ Thượng Đế của bầy tôi.” Và chỉ những người hiểu biết mới nắm được lời nhắc nhở (của Qur''an).');
INSERT INTO "hasan_abdulkarim" VALUES(3,8,'3:8','(Họ thưa): “Lạy Thượng Đế của bầy tôi! Xin đừng làm cho tấm lòng của bầy tôi nghiêng ngả và lạc hướng sau khi Ngài đã hướng dẫn bầy tôi và xin ban cho bầy tôi Hồng Ân từ Ngài bởi vì quả thật Ngài là Đấng Hằng Ban bố.”');
INSERT INTO "hasan_abdulkarim" VALUES(3,9,'3:9','“Lạy Thượng Đế của bầy tôi, không còn gì phải nghi ngờ việc rằng Ngài sẽ cho tập trung nhân loại vào một Ngày (Phán xử). Quả thật, Allah không bao giờ thất hứa cả.”');
INSERT INTO "hasan_abdulkarim" VALUES(3,10,'3:10','Quả thật, những ai không có đức tin (là những kẻ mà) tài sản cũng như con cái đông đảo của chúng sẽ không giúp ích gì được cho chúng thoát khỏi (sự trừng phạt của) Allah. Và chúng sẽ thành chất đốt cho Lửa (của hỏa ngục).');
INSERT INTO "hasan_abdulkarim" VALUES(3,11,'3:11','(Chúng) giống như đám thuộc hạ của Fir-''awn và của những kẻ trước chúng. Chúng phủ nhận các Lời Mặc Khải của TA (Allah), bởi thế Allah bắt phạt chúng về tội lỗi của chúng. Và Allah trừng phạt (chúng) rất nghiêm khắc.');
INSERT INTO "hasan_abdulkarim" VALUES(3,12,'3:12','Hãy bảo những kẻ không có đức tin: “Các người sẽ sớm bị đánh bại và sẽ bị tập trung lùa vào hỏa ngục. Và đó là một nơi nằm nghỉ xấu xa nhất.”');
INSERT INTO "hasan_abdulkarim" VALUES(3,13,'3:13','Chắc chắn các ngươi (hỡi người Do thái!) đã có một dấu hiệu trong việc hai đoàn quân giao chiến (tại Badr)(: Một đoàn quân chiến đấu cho Chính Nghĩa của Allah và đoàn quân kia không có đức tin (chống lại Allah). Chính mắt của chúng thấy rõ quân số của chúng đông hơn gấp đôi. Nhưng Allah trợ lực người nào Ngài muốn với sự cứu trợ của Ngài. Quả thật, trong sự việc đó là một bài học cho những người có cái nhìn thấu suốt.');
INSERT INTO "hasan_abdulkarim" VALUES(3,14,'3:14','Nhân loại thường đam mê những thú vui từ phụ nữ, con cái, kho tàng vàng bạc chất đóng, giống ngựa tốt, gia súc, và đất đai trồng trọt tốt. Đó là sự hưởng thụ của đời sống trần tục này trong lúc Allah có nơi Ngài một nơi trở về tốt đẹp hơn (những thứ đó).');
INSERT INTO "hasan_abdulkarim" VALUES(3,15,'3:15','Hãy bảo họ: “Há ta sẽ cho các ngươi biết về phần thưởng tốt hơn những lạc thú đó hay chăng? (rằng) những ai sợ Allah thì sẽ có ở nơi Thượng Đế của họ những Ngôi vườn (thiên đàng) bên dưới có các dòng sông chảy, nơi mà họ sẽ vào ở đời đời với các bà vợ trong sạch và sẽ được Allah hài lòng. Và Allah thấy rõ các bầy tôi (của Ngài).”');
INSERT INTO "hasan_abdulkarim" VALUES(3,16,'3:16','Những ai (cầu nguyện) thưa: “Lạy Thượng Đế của bầy tôi! Bầy tôi thực sự tin tưởng. Xin Ngài tha thứ tội lỗi của bầy tôi cho bầy tôi và giải cứu bầy tôi thoát khỏi sự trừng phạt của Lửa (hỏa ngục).”');
INSERT INTO "hasan_abdulkarim" VALUES(3,17,'3:17','Họ là những người kiên nhẫn chịu đựng (mọi bất trắc), những người chân thật (trong lời nói và việc làm), những người thành tâm sùng kính (Allah), những ai chi dùng (tài sản cho Chính Nghĩa của Allah), những người cầu xin (Allah) tha thứ (tội lỗi) vào lúc tinh sương.');
INSERT INTO "hasan_abdulkarim" VALUES(3,18,'3:18','Allah xác nhận và các Thiên Thần và những người hiểu biết đều làm chứng rằng không có Thượng Đế nào khác cả duy chỉ có Ngài, Đấng duy trì nền công lý. Không có Thượng Đế nào khác cả duy chỉ có Ngài, Đấng Toàn năng, Đấng Rất Mực Sáng Suốt.');
INSERT INTO "hasan_abdulkarim" VALUES(3,19,'3:19','Quả thật, Chính Đạo đối với Allah là Islam. Và những ai đã được ban cho Kinh Sách cũng không tranh chấp nhau về nó; ngược lại, chỉ từ sau khi tiếp thu được sự hiểu biết, họ mới đâm ra ganh tị lẫn nhau. Và ai phủ nhận các Lời Mặc Khải của Allah thì quả thật Allah rất nhanh trong việc thanh toán (xử phạt).');
INSERT INTO "hasan_abdulkarim" VALUES(3,20,'3:20','Bởi thế, nếu những kẻ không có đức tin tranh luận với Ngươi (Muhammad!), hãy bảo họ: “Ta đã tự dâng hiến mình cho Allah (trong Islam) và những ai theo Ta (cũng làm thế)". Và hãy bảo những ai đã được ban cho Kinh Sách và những kẻ thất học: “Hãy theo Islam.” Do đó, nếu họ theo Islam thì chắc chắn họ được hướng dẫn; ngược lại, nếu họ quay bỏ đi thì nhiệm vụ của Ngươi chỉ là truyền đạt (thông điệp của Allah). Và Allah thấy rõ bầy tôi của Ngài.');
INSERT INTO "hasan_abdulkarim" VALUES(3,21,'3:21','Quả thật, những ai phủ nhận các Lời Mặc Khải của Allah và đã giết các Nabi bất phân đạo lý và giết những ai trong nhân loại ra lệnh việc đối xử công bằng thì hãy báo cho họ biết về một sự trừng phạt đau đớn.');
INSERT INTO "hasan_abdulkarim" VALUES(3,22,'3:22','Họ là những kẻ mà việc làm (thiện tốt) của họ trên thế gian này sẽ tan biến mất và ở đời sau họ sẽ không được ai cứu giúp.');
INSERT INTO "hasan_abdulkarim" VALUES(3,23,'3:23','Há Ngươi (Muhammad) không nhìn thấy những kẻ đã được ban cho một phần của Kinh Sách hay sao? Họ được mời đến với Kinh Sách (Qur''an) của Allah để được giải quyết mối tranh chấp giữa họ với nhau, rồi một thành phần của họ bỏ đi, không buồn lưu ý đến (việc giải hoà).');
INSERT INTO "hasan_abdulkarim" VALUES(3,24,'3:24','Sở dĩ như thế là vì họ nói: “Lửa (của hỏa ngục) sẽ chạm phải thân mình chúng tôi chỉ trong một số ngày ấn định.” Và những điều mà họ đã bịa đặt ra trong tôn giáo của họ đã đánh lừa họ.');
INSERT INTO "hasan_abdulkarim" VALUES(3,25,'3:25','Thế họ sẽ xử sự ra sao khi TA (Allah) sẽ cho tập trung họ lại vào một Ngày nhất định sẽ xảy ra, không có gì phải ngờ vực cả. Và mỗi người (linh hồn) sẽ được trả lại đầy đủ về điều (phúc và tội) mà y đã thu hoạch? Và họ sẽ không bị đối xử thiệt thòi.');
INSERT INTO "hasan_abdulkarim" VALUES(3,26,'3:26','Hãy bảo: “Lạy Allah, Đức Vua nắm mọi quyền hành! Ngài ban quyền hành cho người nào Ngài muốn và thu hồi quyền hành từ kẻ nào Ngài muốn; Ngài ban vinh dự cho người nào Ngài muốn và hạ nhục kẻ nào Ngài muốn. Mọi điều tốt đều do Tay của Ngài cả. Quả thật, Ngài có quyền trên tất cả mọi vật.');
INSERT INTO "hasan_abdulkarim" VALUES(3,27,'3:27','“Ngài làm cho ban đêm nhập vào ban ngày và làm cho ban ngày nhập vào ban đêm; Ngài đưa cái sống ra từ cái chết và đưa cái chết ra khỏi cái sống. Và Ngài cung dưỡng một cách vô kể cho người nào Ngài muốn.”');
INSERT INTO "hasan_abdulkarim" VALUES(3,28,'3:28','Những người có đức tin chớ nên nhận những kẻ không có đức tin làm người bảo hộ của mình thay vì những người có đức tin, và ai làm thế thì sẽ không còn liên hệ gì với Allah nữa ngoại trừ trường hợp các ngươi sợ hiểm họa do chúng gây ra. Và Allah khuyên các ngươi nên thận trọng với chính Ngài (Allah) bởi vì mục tiêu trở về cuối cùng là Allah.');
INSERT INTO "hasan_abdulkarim" VALUES(3,29,'3:29','Hãy bảo họ: “Dù các người có giấu giếm hay tiết lộ điều gì nằm trong lòng của các người, Allah biết rõ cả, bởi vì Ngài biết hết mọi vật trong các tầng trời và mọi vật dưới đất. Và Allah có quyền chi phối trên tất cả mọi vật.”');
INSERT INTO "hasan_abdulkarim" VALUES(3,30,'3:30','Vào Ngày mà mỗi người sẽ đối diện với điều phúc mà nó đã làm và với điều tội mà nó đã phạm, thì người (linh hồn) đó sẽ ao ước phải chi y được xa tội lỗi đó một khoảng cách thật xa. Và Allah cảnh cáo các ngươi về Ngài. Và Allah Rất mực Nhân từ với bầy tôi của Ngài.');
INSERT INTO "hasan_abdulkarim" VALUES(3,31,'3:31','Hãy bảo (họ, hỡi Muhammad!): “Nếu các người thực sự yêu thương Allah thì hãy theo Ta, Allah sẽ yêu thương các người (trở lại) và tha tội cho các người bởi vì Allah Hằng Tha Thứ, Rất Mực Khoan Dung.');
INSERT INTO "hasan_abdulkarim" VALUES(3,32,'3:32','Hãy bảo tiếp: “Hãy vâng lệnh Allah và Sứ Giả (của Allah).” Nhưng nếu họ quay bỏ đi thì quả thật Allah không yêu thương những kẻ không có đức tin (nơi Ngài).');
INSERT INTO "hasan_abdulkarim" VALUES(3,33,'3:33','Quả thật, Allah đã chọn Adam và Nuh và dòng dõi của Ibrahim và dòng dõi của ''Imran hơn thiên hạ.');
INSERT INTO "hasan_abdulkarim" VALUES(3,34,'3:34','Con cháu lẫn nhau (trong các dòng họ đó). Và Allah Hằng nghe và biết (mọi việc).');
INSERT INTO "hasan_abdulkarim" VALUES(3,35,'3:35','Khi bà vợ của ''Imran (cầu nguyện) thưa: “Lạy Thượng Đế của bề tôi! Quả thật, bề tôi xin hiến (đứa con) trong bụng của bề tôi cho Ngài để đặc biệt phụng sự Ngài; xin Ngài nhận (sự hiến dâng này) của bề tôi bởi vì Ngài là Đấng Hằng Nghe và Biết mọi việc.”');
INSERT INTO "hasan_abdulkarim" VALUES(3,36,'3:36','Bởi thế, sau khi hạ sanh, bà ta thưa: “Lạy Thượng Đế của bề tôi! Bề tôi đã hạ sanh một đứa con gái - và Allah biết rõ đứa con mà bà đã hạ sanh - và con trai không như con gái, và bề tôi đặt tên cho Nữ là Maryam và bề tôi giao Nữ và con cái của Nữ cho Ngài để Ngài bảo vệ (chúng) tránh xa Shaytan phản nghịch, đáng bị đánh đuổi đi nơi khác.”');
INSERT INTO "hasan_abdulkarim" VALUES(3,37,'3:37','Do đó, Thượng Đế của Nữ (Maryam) đã chấp nhận Nữ một cách tốt đẹp và làm cho Nữ trưởng thành xinh đẹp và giao Nữ cho Zakariya (Zacharias) chăm sóc. Mỗi lần Zakariya đi vào phòng tu của Nữ (để chăm sóc Nữ), Y thấy thực phẩm bên cạnh Nữ. (Zakariya) hỏi: “Hỡi Maryam, Nữ có thức ăn này từ đâu?” (Maryam) đáp: “Thức ăn này do Allah ban cho. Quả thật, Allah cung dưỡng vô kể cho người nào Ngài muốn.”');
INSERT INTO "hasan_abdulkarim" VALUES(3,38,'3:38','Tức thời Zakariya cầu nguyện Thượng Đế của Y, thưa: “Lạy Thượng Đế của bề tôi! Xin Ngài rủ lòng thương ban cho bề tôi một đứa con tốt (và có hiếu) để nối dòng. Quả thật, Ngài là Đấng Hằng Nghe lời cầu xin.”');
INSERT INTO "hasan_abdulkarim" VALUES(3,39,'3:39','Bởi thế, Thiên Thần gọi Y (Zakariya) trong lúc Y đứng dâng lễ (và cầu nguyện) trong phòng tu, bảo: “Quả thật, Allah báo cho Ngươi tin mừng về (việc Ngài ban cho Ngươi một đứa con trai) tên Yahya, để xác nhận một Lời phán từ Allah, (đứa con đó) vừa là một nhà lãnh đạo quí phái vừa là một người trong sạch và vừa là một vị Nabi của những người ngoan đạo.”');
INSERT INTO "hasan_abdulkarim" VALUES(3,40,'3:40','(Zakariya) thưa: “Lạy Thượng Đế của bề tôi! Làm sao bề tôi sẽ có con trai trong lúc bề tôi tuổi đã già và vợ thì lại hiếm hoi nữa?” (Allah) đáp: “Allah làm điều gì Ngài muốn đúng như thế.”');
INSERT INTO "hasan_abdulkarim" VALUES(3,41,'3:41','(Zakariya) thưa: “Lạy Thượng Đế của bề tôi! Xin Ngài ban cho bề tôi một dấu hiệu.” (Allah) phán: “Dấu hiệu của Ngươi sẽ là (việc) Ngươi sẽ không nói chuyện được với mọi người trong ba ngày mà chỉ ra dấu thôi. Và hãy tụng niệm Thượng Đế của Ngươi cho thật nhiều và tán dương Ngài vào buổi tối và vào buổi sáng.”');
INSERT INTO "hasan_abdulkarim" VALUES(3,42,'3:42','Và (hãy nhớ) khi các Thiên Thần bảo (Maryam): “Hỡi Maryam! Quả thật, Allah đã chọn Nàng và tẩy sạch Nàng và chọn Nàng (để phụng sự Ngài) hơn những phụ nữ khác trong thiên hạ.”');
INSERT INTO "hasan_abdulkarim" VALUES(3,43,'3:43','Hỡi Maryam “Hãy dốc lòng thờ phụng Thượng Đế của Nàng, hãy phủ phục và cúi đầu thần phục (trước Allah) cùng với những người cúi đầu thần phục.”');
INSERT INTO "hasan_abdulkarim" VALUES(3,44,'3:44','Đây là phần của các tin lành thuộc cõi vô hình mà TA (Allah) đã mặc khải cho Ngươi (hỡi Muhammad!) Ngươi không có mặt với họ khi họ dùng quẻ xin xăm để chọn ai là người của họ sẽ đứng ra chăm sóc Maryam(, Ngươi cũng không có mặt với họ khi họ tranh luận với nhau về (vấn đề này).');
INSERT INTO "hasan_abdulkarim" VALUES(3,45,'3:45','(Hãy nhớ) khi các Thiên Thần bảo (Nàng Maryam): “Hỡi Maryam Quả thật, Allah báo cho Nàng tin mừng về một Lời phán từ Ngài: Tên của Y là Masih( Ysa (Giê su), con trai của Maryam, (Y) sẽ được vinh danh ở đời này và đời sau và sẽ thuộc thành phần của những người ở kế cận Allah.”');
INSERT INTO "hasan_abdulkarim" VALUES(3,46,'3:46','“Y sẽ nói với dân chúng lúc còn ấu thơ( và lúc trưởng thành và sẽ là một người ngoan đạo .”');
INSERT INTO "hasan_abdulkarim" VALUES(3,47,'3:47','(Maryam) thưa: “Lạy Thượng Đế của bề tôi! Làm sao bề tôi sẽ có con trong lúc không một người đàn ông nào chạm đến mình của bề tôi?” (Allah) phán: “(Sự việc sẽ xảy ra) đúng như thế! Allah tạo hóa bất cứ vật gì Ngài muốn. Khi Ngài quyết định một việc, Ngài chỉ phán: ''Hãy thành!’ thì nó sẽ thành như thế.”');
INSERT INTO "hasan_abdulkarim" VALUES(3,48,'3:48','Và Ngài (Allah) sẽ dạy Y (Ysa) Kinh Sách, và Lẽ đứng đắn khôn ngoan và Kinh Tawrah (Cựu Ước) và Kinh Injil (Tân Ước).');
INSERT INTO "hasan_abdulkarim" VALUES(3,49,'3:49','Và (bổ nhiệm Y làm) một Sứ Giả cho con cháu của Israel (với Bức Thông Điệp): “Ta đến với các ngươi với một dấu hiệu từ Thượng Đế của các ngươi. Ta sẽ lấy đất sét nắn thành hình một con chim cho các ngươi, tiếp đó Ta sẽ hà hơi vào nó, thế là nó sẽ trở thành một con chim (thật sự) theo Phép của Allah. Và Ta sẽ chữa lành những người mù bẩm sinh và chữa lành những người mang bệnh cùi và Ta làm cho người chết sống lại theo Phép của Allah và Ta sẽ cho các ngươi biết món (thực phẩm) nào các ngươi có thể dùng và món nào các ngươi có thể dự trữ trong nhà. Quả thật, trong sự việc đó là một dấu hiệu cho các ngươi nếu các ngươi có đức tin;');
INSERT INTO "hasan_abdulkarim" VALUES(3,50,'3:50','Và để xác nhận lại những điều (mặc khải) của Kinh Tawrah đã được ban xuống trước Ta và giải tỏa một số những giới cấm đã ràng buộc các ngươi trước đây. Và Ta đến gặp các ngươi với một Bằng chứng từ Thượng Đế của các ngươi, bởi thế hãy thành tâm sợ Allah và tuân theo Ta.');
INSERT INTO "hasan_abdulkarim" VALUES(3,51,'3:51','Quả thật, Allah là Thượng Đế của Ta và là Thượng Đế của các ngươi. Bởi thế, hãy tôn thờ Ngài. Đây là Chính Đạo.”');
INSERT INTO "hasan_abdulkarim" VALUES(3,52,'3:52','Bởi thế, khi Ysa nhận thấy sự bất tín của họ, Y lên tiếng hỏi: “Ai là người sẽ giúp Ta phục vụ Allah?” Các Tông đồ thưa: “Chúng tôi là những người hậu thuẫn cho Allah. Chúng tôi tin tưởng nơi Allah và tự xác nhận chúng tôi là những người Muslim (thần phục Allah).”');
INSERT INTO "hasan_abdulkarim" VALUES(3,53,'3:53','“Lạy Thượng Đế của bầy tôi! Bầy tôi đã tin tưởng điều mà Ngài đã ban xuống và bầy tôi tuân theo Sứ Giả Ysa). Bởi thế, xin Ngài ghi (tên của) bầy tôi cùng với các chứng nhân của sự Thật.”');
INSERT INTO "hasan_abdulkarim" VALUES(3,54,'3:54','Và chúng (những kẻ không tin) mưu định (hãm hại Ysa) và Allah cũng hoạch định. Và Allah là Đấng Hoạch định Siêu việt.');
INSERT INTO "hasan_abdulkarim" VALUES(3,55,'3:55','(Hãy nhớ) khi Allah phán: “Hỡi Ysa TA sẽ làm cho Ngươi qua đời( và sẽ mang Ngươi lên cùng với TA và TA sẽ tẩy sạch Ngươi khỏi (lời vu cáo của) những kẻ không có đức tin và làm cho những ai theo Ngươi cao hơn những kẻ không có đức tin cho đến Ngày phục sinh. Rồi các ngươi sẽ được đưa về gặp TA trở lại và TA sẽ phân xử giữa các ngươi về những điều mà các ngươi thường tranh chấp nhau.”');
INSERT INTO "hasan_abdulkarim" VALUES(3,56,'3:56','“Còn đối với những ai không tin thì TA sẽ trừng phạt chúng bằng một sự trừng phạt khủng khiếp ở đời này lẫn đời sau và chúng sẽ không được ai giúp đỡ cả.”');
INSERT INTO "hasan_abdulkarim" VALUES(3,57,'3:57','Và đối với những ai có đức tin và làm việc thiện, (Allah) sẽ trả cho họ đầy đủ phần thưởng của họ. Và Allah không yêu thương những kẻ làm điều sai quấy.');
INSERT INTO "hasan_abdulkarim" VALUES(3,58,'3:58','Đó là những Lời Mặc Khải mà TA (Allah) đọc cho Ngươi (hỡi Muhammad!) với lời nhắc nhở sáng suốt.');
INSERT INTO "hasan_abdulkarim" VALUES(3,59,'3:59','Đối với Allah, trường hợp của Ysa (Giê su) giống trường hợp của Adam. Ngài (Allah) tạo Y từ đất bụi rồi phán cho Y: ''Hãy thành!'' và Y thành đúng như thế.');
INSERT INTO "hasan_abdulkarim" VALUES(3,60,'3:60','(Đấy là) sự Thật (đến) từ Thượng Đế của Ngươi (hỡi Muhammad!). Bởi thế, Ngươi chớ sinh lòng nghi ngờ.');
INSERT INTO "hasan_abdulkarim" VALUES(3,61,'3:61','Bởi thế, nếu có ai tranh luận với Ngươi về điều này sau khi Ngươi đã tiếp thu được mọi sự hiểu biết liên quan thì hãy bảo họ: “Hãy mời đến đây. Hãy gọi tập họp con trai của chúng tôi và con trai của quí vị và phụ nữ của chúng tôi lẫn phụ nữ của quí vị, bản thân của chúng tôi và bản thân của quí vị rồi để chúng ta thật lòng cầu nguyện - rồi xin Allah giáng lời nguyền rủa của Ngài lên mình những kẻ nói dối.”(');
INSERT INTO "hasan_abdulkarim" VALUES(3,62,'3:62','Câu chuyện này hoàn toàn thật và chỉ do một Thượng Đế Duy Nhất (Allah) ban xuống. Và quả thật, Allah là Đấng Toàn Năng, Đấng Rất Mực Sáng Suốt.');
INSERT INTO "hasan_abdulkarim" VALUES(3,63,'3:63','Nhưng nếu họ quay bỏ đi thì quả thật Allah là Đấng biết rõ (ý đồ của) những kẻ ác đức.');
INSERT INTO "hasan_abdulkarim" VALUES(3,64,'3:64','Hãy bảo họ: “Hỡi Người dân Kinh Sách! Hãy đến cùng với một Lời phán giống nhau giữa chúng tôi và quí vị rằng chúng ta chỉ thờ phụng riêng Allah thôi, và chúng ta không tổ hợp bất cứ ai (cái gì) với Ngài (Allah), và không ai giữa chúng ta sẽ thờ các vị chủ tể nào khác ngoài Allah cả.” Nhưng nếu họ quay bỏ đi thì hãy bảo họ: “Hãy chứng thực rằng chúng tôi là những người Muslim (thần phục Allah.)”');
INSERT INTO "hasan_abdulkarim" VALUES(3,65,'3:65','Hỡi người dân Kinh Sách! Tại sao các ngươi tranh luận về Ibrahim (nói Ibrahim là người Do thái hay là người theo Thiên Chúa giáo) trong lúc (các ngươi biết) Kinh Tawrah và Kinh Injil chỉ được ban xuống sau Y mà thôi. Thế các ngươi không hiểu hay sao?');
INSERT INTO "hasan_abdulkarim" VALUES(3,66,'3:66','À phải rồi! Các ngươi là những kẻ quen thói tranh luận về những điều mà các ngươi không có một chút hiểu biết nào. Thế tại sao các ngươi tranh luận về điều mà các ngươi không biết một tí gì? Và Allah biết (sự thật) trong lúc các ngươi không biết.');
INSERT INTO "hasan_abdulkarim" VALUES(3,67,'3:67','Ibrahim không là một người Do thái cũng không là một tín đồ Thiên Chúa giáo. Y là một người Muslim thuần túy (chỉ tôn thờ Allah) và không là một người thờ đa thần.');
INSERT INTO "hasan_abdulkarim" VALUES(3,68,'3:68','Quả thật, trong nhân loại, người gần Ibrahim nhất là những ai tuân theo Y và Nabi (Muhammad) này cùng những ai có đức tin. Và Allah là Đấng Bảo Hộ của những người có đức tin.');
INSERT INTO "hasan_abdulkarim" VALUES(3,69,'3:69','Một phần tử của người dân Kinh Sách mong làm cho các ngươi lạc đạo nhưng chúng tự làm cho mình lạc đạo trong lúc chúng không nhận thấy (điều đó).');
INSERT INTO "hasan_abdulkarim" VALUES(3,70,'3:70','Hỡi người dân Kinh Sách! Tại sao các ngươi phủ nhận các Lời Mặc Khải của Allah trong lúc các ngươi xác nhận (chúng)?');
INSERT INTO "hasan_abdulkarim" VALUES(3,71,'3:71','Hỡi người dân Kinh Sách! Tại sao các ngươi lấy điều gian dối để che đậy sự Thật và giấu giếm sự Thật trong lúc các ngươi biết (điều đó)?');
INSERT INTO "hasan_abdulkarim" VALUES(3,72,'3:72','Một thành phần của người dân Kinh Sách bảo (nhau): “Hãy tin nơi điều (mặc khải) được ban xuống cho những ai có đức tin vào đầu ngày và phủ nhận nó vào cuối ngày, (làm thế) may ra họ (người Muslim) có thể trở lại (việc không tin tưởng).');
INSERT INTO "hasan_abdulkarim" VALUES(3,73,'3:73','Và chỉ tin ai là người theo đạo của quí vị.” Hãy bảo họ (hỡi Muhammad!): “Quả thật, Chỉ Đạo đúng đắn nhất là Chỉ Đạo của Allah. (Phải chăng các người sợ) rằng một người nào đó sẽ được (Allah) ban cho điều giống như điều đã được ban cho các người hoặc (sợ) họ sẽ tranh luận với các người trước mặt Thượng Đế của các người? Hãy bảo họ: “Quả thật, mọi Thiên ân đều do bàn Tay của Allah cả. Ngài ban nó cho người nào Ngài muốn bởi vì Allah Rộng rãi Bao la, Biết hết mọi việc.”');
INSERT INTO "hasan_abdulkarim" VALUES(3,74,'3:74','Do lòng Khoan Dung của Ngài, (Allah) chọn người nào Ngài muốn (để phục vụ Ngài) bởi vì Allah là Chủ nhân của Thiên ân Chí đại.');
INSERT INTO "hasan_abdulkarim" VALUES(3,75,'3:75','Và trong đám người dân Kinh Sách, có người nếu Ngươi giao cho y giữ một đóng vàng thì y sẽ sẵn sàng trả lại cho Ngươi, và trong họ có kẻ nếu Ngươi giao cho y giữ chỉ một đồng tiền vàng thôi thì y cũng sẽ không trả lại cho Ngươi trừ phi Ngươi đứng đó đòi mãi. Sở dĩ như thế là vì họ bảo: “Chúng tôi không chịu trách nhiệm về cách đối xử của những kẻ thất học.” Và họ nói dối rồi đổ thừa cho Allah trong lúc họ biết.');
INSERT INTO "hasan_abdulkarim" VALUES(3,76,'3:76','Vâng! Ai giữ đúng lời hứa của mình và sợ Allah thì quả thật Allah yêu thương những người ngay chính sợ Allah.');
INSERT INTO "hasan_abdulkarim" VALUES(3,77,'3:77','Quả thật, những ai bán Lời Giao Ước của Allah và Lời Thề của họ với một giá nhỏ nhoi thì sẽ không được hưởng một phần nào ở đời sau và Allah sẽ không buồn nói đến họ cũng sẽ không nhìn mặt họ vào Ngày phục sinh, và Ngài sẽ không tẩy sạch họ, và họ sẽ chịu một sự trừng phạt đau đớn.');
INSERT INTO "hasan_abdulkarim" VALUES(3,78,'3:78','Và quả thật, một thành phần của họ đã bóp méo Kinh Sách với miệng lưỡi của họ (khi đọc Kinh) làm cho các ngươi tưởng lầm đó là điều nằm trong Kinh Sách trong lúc đó không phải là điều nằm trong Kinh Sách, và họ bảo đó là điều do Allah ban xuống nhưng thực sự đó không phải là điều do Allah ban xuống và họ nói dối rồi đổ thừa cho Allah trong lúc họ biết.');
INSERT INTO "hasan_abdulkarim" VALUES(3,79,'3:79','Không một người nào được Allah ban cho Kinh Sách, Luật Đạo và Sứ Mạng Nabi rồi bảo nhân loại: “Hãy trở thành bầy tôi của ta thay vì của Allah". Ngược lại, (y phải nói:) “Hãy là bầy tôi ngoan đạo (của Allah)” bởi vì các ngươi đã từng dạy Kinh Sách và các ngươi đã trau dồi học hỏi nó.”');
INSERT INTO "hasan_abdulkarim" VALUES(3,80,'3:80','Và y cũng không được buộc các ngươi tôn các Thiên Thần và các Nabi lên làm chúa tể. Phải chăng y bắt các ngươi phủ nhận đức tin sau khi các ngươi đã trở thành người Muslim (thần phục Allah)?');
INSERT INTO "hasan_abdulkarim" VALUES(3,81,'3:81','Và (hãy nhớ) khi Allah nhận lời Giao Ước của các Nabi, Ngài phán: “TA (Allah) ban cho các ngươi Kinh Sách và lẽ sáng suốt Khôn ngoan, rồi một Sứ Giả (của TA) đến gặp các ngươi để xác nhận lại điều các ngươi đang giữ; các ngươi phải tin nơi Y và giúp đỡ Y”. (Allah) phán (hỏi): "Các ngươi có đồng ý và nghiêm túc nhận điều này làm một Giao Ước của TA với các ngươi hay không?". (Các Nabi) thưa: “Bầy tôi đồng ý”. (Allah) phán: “Thế các ngươi hãy xác nhận đi và TA cùng xác nhận với các ngươi.”');
INSERT INTO "hasan_abdulkarim" VALUES(3,82,'3:82','Bởi thế, ai quay bỏ đi sau đó thì là những kẻ dấy loạn, bất tuân.');
INSERT INTO "hasan_abdulkarim" VALUES(3,83,'3:83','Phải chăng họ muốn tìm một cái gì khác hơn tôn giáo của Allah trong lúc bất cứ ai trong các tầng trời và trái đất, dù muốn hay không, đều phải thần phục Ngài và họ sẽ được đưa về gặp Ngài trở lại?');
INSERT INTO "hasan_abdulkarim" VALUES(3,84,'3:84','Hãy bảo họ, (hỡi Muhammad!): “Chúng tôi tin tưởng nơi Allah và nơi điều (mặc khải) đã được ban xuống cho chúng tôi và điều đã được ban xuống cho Ibrahim, Isma''il, Is-haq, Ya''qub, các bộ lạc (của Israel) và điều (mặc khải) đã được ban xuống cho Musa, Ysa và các Nabi (của Allah) từ Thượng Đế của họ. Chúng tôi không kỳ thị phân biệt một ai trong số họ và chúng tôi là những người Muslim thần phục Ngài".');
INSERT INTO "hasan_abdulkarim" VALUES(3,85,'3:85','Và ai tìm kiếm một tôn giáo nào khác ngoài Islam thì y sẽ không bao giờ được (Allah) chấp thuận, và ở đời sau y sẽ là một trong những kẻ bị thua thiệt.');
INSERT INTO "hasan_abdulkarim" VALUES(3,86,'3:86','Làm sao Allah có thể hướng dẫn một đám người phủ nhận đức tin sau khi họ đã tin tưởng và sau khi đã xác nhận rằng Sứ Giả (Muhammad) là sự thật và sau khi đã chứng kiến những bằng chứng rõ rệt? Và Allah không hướng dẫn một đám người làm điều sai quấy.');
INSERT INTO "hasan_abdulkarim" VALUES(3,87,'3:87','Họ là những kẻ mà phần thưởng sẽ gồm lời nguyền rủa của Allah, của giới Thiên Thần và của toàn thể nhân loại.');
INSERT INTO "hasan_abdulkarim" VALUES(3,88,'3:88','Họ sẽ ở trong (sự nguyền rủa) đó đời đời, hình phạt dành cho họ sẽ không được giảm nhẹ và họ sẽ không được dung tha.');
INSERT INTO "hasan_abdulkarim" VALUES(3,89,'3:89','Ngoại trừ những ai biết hối cải và sửa mình sau đó thì quả thật Allah Hằng Tha Thứ, Rất Mực Khoan Dung.');
INSERT INTO "hasan_abdulkarim" VALUES(3,90,'3:90','Quả thật, những ai chối bỏ đức tin sau khi đã tin tưởng rồi càng ngoan cố không tin thì sự ăn năn hối cải của họ sẽ không bao giờ được chấp nhận. Và họ là những kẻ hoàn toàn lạc đạo.');
INSERT INTO "hasan_abdulkarim" VALUES(3,91,'3:91','Quả thật, những ai chối bỏ đức tin và chết trong tình trạng không có đức tin, cho dù có mang cả một trái đất đầy vàng đến để chuộc tội cho một ai đó trong số họ thì sự chuộc tội đó sẽ không bao giờ được (Allah) chấp nhận. Họ là những kẻ sẽ chịu một sự trừng phạt đau đớn và sẽ không được ai giúp đỡ cả.');
INSERT INTO "hasan_abdulkarim" VALUES(3,92,'3:92','Các ngươi sẽ không bao giờ đạt đến mức đạo đức trừ phi các ngươi chi dùng (cho Chính Nghĩa của Allah) những vật mà các người yêu thích nhất; và bất cứ vật gì các ngươi tiêu ra thì Allah đều biết rõ cả.');
INSERT INTO "hasan_abdulkarim" VALUES(3,93,'3:93','Tất cả các loại thực phẩm đều được phép cho con cháu của Israel ngoại trừ thực phẩm nào mà Israel tự cấm mình trước khi Kinh Tawrah được ban xuống. Hãy bảo (con cháu của Israel): “Các ngươi hãy mang Kinh Tawrah ra đây và hãy đọc nó lên nếu các ngươi nói thật.”');
INSERT INTO "hasan_abdulkarim" VALUES(3,94,'3:94','Bởi thế, kể từ sau đó, ai bịa đặt điều nói dối rồi đổ thừa cho Allah thì là những kẻ làm điều sai quấy.');
INSERT INTO "hasan_abdulkarim" VALUES(3,95,'3:95','Hãy bảo họ: “Allah nói sự thật. Bởi thế, hãy theo tín ngưỡng của Ibrahim thuần túy (tôn thờ chỉ riêng Allah) và Y không phải là một người thờ đa thần.');
INSERT INTO "hasan_abdulkarim" VALUES(3,96,'3:96','Quả thật, Ngôi Đền đầu tiên được chỉ định cho nhân loại (để thờ phụng Allah) là cái tại Bakkah (Makkah) đầy phúc đức và là chỉ hướng cho thiên hạ.');
INSERT INTO "hasan_abdulkarim" VALUES(3,97,'3:97','Nơi đó có những dấu hiệu rõ ràng: Maqam (chỗ đứng) của Ibrahim; và ai đi vào đó (Makkah) sẽ được an toàn. Và việc đi làm Hajj dâng lên Allah tại Ngôi Đền (Ka''bah) là một bổn phận đối với người nào trong nhân loại có đủ khả năng tìm đường đi đến đó; và ai không có đức tin thì (nên biết) quả thật Allah Rất Mực Giàu Có, Độc Lập với vũ trụ và muôn loài.');
INSERT INTO "hasan_abdulkarim" VALUES(3,98,'3:98','Hãy bảo họ: “Hỡi người dân Kinh Sách! Tại sao các ngươi phủ nhận các Lời Mặc Khải của Allah trong lúc Allah chứng kiến mọi việc các người làm?”');
INSERT INTO "hasan_abdulkarim" VALUES(3,99,'3:99','Hãy bảo họ: “Hỡi người dân Kinh Sách! Tại sao các ngươi cản trở ai có đức tin không cho theo Chính Đạo của Allah, và tìm cách làm cho nó cong queo trong lúc các ngươi là nhân chứng (xác nhận sự thật)? Nhưng Allah không làm ngơ trước những điều các người làm.');
INSERT INTO "hasan_abdulkarim" VALUES(3,100,'3:100','Hỡi những ai có đức tin! Nếu các ngươi nghe theo một thành phần của những ai đã được ban cho Kinh Sách thì họ sẽ làm cho các ngươi không còn tin tưởng nữa sau khi đã có đức tin.');
INSERT INTO "hasan_abdulkarim" VALUES(3,101,'3:101','Và làm sao các ngươi có thể chối bỏ đức tin trong lúc các Lời Mặc Khải của Allah được đọc ra để nhắc các ngươi và vị Sứ Giả (của Allah) đang sống cùng với các ngươi? Và ai nắm vững (Thông điệp của) Allah thì chắc chắn sẽ được hướng dẫn đúng Chính Đạo.');
INSERT INTO "hasan_abdulkarim" VALUES(3,102,'3:102','Hỡi những ai có đức tin! Hãy sợ Allah theo lẽ mà Ngài phải được khiếp sợ và chỉ chết trong tình trạng các ngươi là những người Muslim (thần phục Allah).');
INSERT INTO "hasan_abdulkarim" VALUES(3,103,'3:103','Và hãy cùng nhau nắm vững sợi dây (Islam) mà Allah đã giăng ra cho các ngươi và đừng chia rẽ nhau, và hãy nhớ Ân huệ của Allah đã ban cho các ngươi khi các ngươi hãy còn là kẻ thù của nhau, nhưng Ngài đã kết hợp trái tim (tấm lòng) của các ngươi trở lại. Bởi thế, do Ân huệ của Ngài, các ngươi đã trở thành anh em với nhau, và (trước đó) các ngươi đang ở trên bờ hố lửa (chiến tranh) nhưng Ngài đã cứu các ngươi ra khỏi (tình trạng) đó. Allah trình bày rõ các Lời Mặc Khải của Ngài cho các ngươi như thế để may ra các ngươi được hướng dẫn (đúng đường).');
INSERT INTO "hasan_abdulkarim" VALUES(3,104,'3:104','Và hãy để cho một tập thể từ các ngươi vùng lên đi mời gọi thiên hạ đến với điều tốt và bảo họ làm điều thiện và cản họ làm điều ác. Và đó là những người sẽ thành đạt.');
INSERT INTO "hasan_abdulkarim" VALUES(3,105,'3:105','Và đừng trở thành như những ai đã chia rẽ (tôn giáo) và tranh chấp lẫn nhau sau khi đã tiếp thu các bằng chứng rõ rệt; và họ là những kẻ sẽ nhận một sự trừng phạt vô cùng to lớn.');
INSERT INTO "hasan_abdulkarim" VALUES(3,106,'3:106','Vào một Ngày khi một số gương mặt sẽ sáng rỡ và một số gương mặt sẽ tối sầm. Bởi thế, đối với những ai có gương mặt tối sầm, (sẽ có lời này): “Có phải các người đã chối bỏ đức tin sau khi đã tin tưởng? Thôi hãy nếm sự trừng phạt về tội các người đã chối bỏ đức tin.”');
INSERT INTO "hasan_abdulkarim" VALUES(3,107,'3:107','Và đối với những ai có gương mặt sáng rỡ thì sẽ được Allah khoan dung và họ sẽ ở trong đó (thiên đàng) đời đời.');
INSERT INTO "hasan_abdulkarim" VALUES(3,108,'3:108','Đó là các Lời Mặc Khải của Allah mà TA (Allah) đã đọc ra cho Ngươi (Muhammad) bằng sự thật. Và Allah không muốn cho thiên hạ gặp điều bất công nào.');
INSERT INTO "hasan_abdulkarim" VALUES(3,109,'3:109','Và mọi vật trong các tầng trời và mọi vật dưới đất đều là của Allah cả. Và mọi việc sẽ được trình lên cho Allah (quyết định).');
INSERT INTO "hasan_abdulkarim" VALUES(3,110,'3:110','Các ngươi (hỡi Muslim!) là một cộng đồng được gầy dựng (để làm gương) cho nhân loại. Các ngươi bảo ban làm điều thiện tốt và ngăn cản làm điều xấu và các ngươi tin tưởng nơi Allah. Và nếu Người dân Kinh Sách tin tưởng thì điều đó tốt cho họ hơn. Trong họ có một số tin tưởng nhưng đa số là những kẻ dấy loạn, bất tuân.');
INSERT INTO "hasan_abdulkarim" VALUES(3,111,'3:111','Họ sẽ không bao giờ hãm hại được các ngươi, mà chỉ quấy rầy chút ít thôi. Và nếu chúng có đánh các ngươi thì chúng cũng sẽ quay lưng bỏ chạy, rồi chẳng được ai cứu giúp.');
INSERT INTO "hasan_abdulkarim" VALUES(3,112,'3:112','Niềm sỉ nhục sẽ đuổi theo họ bất cứ nơi nào họ cư ngụ trừ phi nhận được sự cam kết từ Allah và từ nhân loại. Họ tự chuốc lên mình sự giận dữ của Allah và tự đón lấy sự khốn nạn. Sở dĩ như thế là vì họ đã phủ nhận các Lời Mặc Khải của Allah và đã giết các Nabi bất phân phải trái, sở dĩ như thế là vì họ đã bất tuân và đã vượt quá mức giới hạn.');
INSERT INTO "hasan_abdulkarim" VALUES(3,113,'3:113','Không phải họ đều giống nhau cả. Trong số người dân Kinh Sách, có một nhóm đứng đọc các Lời Mặc Khải của Allah thâu đêm và phủ phục Allah.');
INSERT INTO "hasan_abdulkarim" VALUES(3,114,'3:114','Họ tin tưởng nơi Allah và Ngày (Phán xử) Cuối Cùng, họ bảo ban làm điều thiện tốt và ngăn cản làm điều xấu và thi đua làm điều lành; và họ là những người ngoan đạo .');
INSERT INTO "hasan_abdulkarim" VALUES(3,115,'3:115','Và bất cứ điều tốt nào mà họ làm sẽ không bao giờ bị (Allah) bác bỏ bởi vì Allah là Đấng Hằng biết những người ngay chính sợ Allah.');
INSERT INTO "hasan_abdulkarim" VALUES(3,116,'3:116','Quả thật, những ai không có đức tin thì tài sản cũng như con cái (đông đảo) của họ sẽ chẳng giúp được họ tránh khỏi (sự trừng phạt của) Allah. Và họ sẽ là những người bạn của hỏa ngục, nơi mà họ sẽ vào đó ở đời đời.');
INSERT INTO "hasan_abdulkarim" VALUES(3,117,'3:117','Hình ảnh về những vật mà họ chi dùng ở trần gian này có thể ví như một luồng gió cực kỳ giá buốt bay đến tàn phá mùa màng của một đám người tự hủy hoại linh hồn của mình cho nên họ đã tàn phá nó. Không phải Allah hãm hại họ mà chính họ đã tự làm hại bản thân mình.');
INSERT INTO "hasan_abdulkarim" VALUES(3,118,'3:118','Hỡi những ai có đức tin! Chớ kết thân với ai ngoài các ngươi ra, chúng (kẻ thù) không bỏ lỡ cơ hội làm cho các ngươi hư hỏng. Chúng chỉ muốn cho các ngươi gặp đau khổ. Chắc chắn điều ác độc đã xuất hiện trên cửa miệng của chúng, nhưng điều hiểm độc nằm trong lòng của chúng còn dữ tợn hơn. TA (Allah) trình bày rõ các Lời Mặc Khải cho các ngươi (để các ngươi đề phòng) nếu các ngươi chịu suy ngẫm.');
INSERT INTO "hasan_abdulkarim" VALUES(3,119,'3:119','Ô này! Các ngươi là những người thương xót chúng nhưng chúng không thương xót các ngươi, và các ngươi tin tưởng toàn bộ Kinh Sách (Qur''an). Và khi gặp các ngươi, chúng bảo: “Chúng tôi tin tưởng.” Nhưng khi ở riêng ra thì chúng phát giận các ngươi đến nỗi đã hậm hực cắn những đầu ngón tay của chúng. Hãy bảo họ (hỡi Muhammad!): “Tức lắm thì hãy chết đi!”. Quả thật, Allah biết điều (bí ẩn) nằm trong lòng (của chúng).');
INSERT INTO "hasan_abdulkarim" VALUES(3,120,'3:120','Nếu các ngươi gặp điều lành thì chúng tức tối; và nếu các ngươi gặp hoạn nạn thì chúng vui cười hớn hở về tai nạn đó. Và nếu các ngươi kiên nhẫn và sợ Allah thì mưu kế hiểm độc của chúng chẳng hại gì được các ngươi, bởi vì quả thật Allah tóm thâu hết mọi việc chúng làm.');
INSERT INTO "hasan_abdulkarim" VALUES(3,121,'3:121','Và (hãy nhớ vào một buổi nọ) khi Ngươi (hỡi Sứ Giả!) ra khỏi nhà từ sáng sớm để đi bố trí tín đồ vào vị trí chiến đấu (tại Uhud)(. Và Allah Hằng nghe và Hằng thấy (hết mọi việc).');
INSERT INTO "hasan_abdulkarim" VALUES(3,122,'3:122','(Hãy nhớ) khi hai đội quân (của bộ lạc Banu Salma Khazraj và bộ lạc Banu Harithah) thuộc lực lượng của các ngươi có tư tưởng hèn nhát (muốn bỏ ngũ) trong lúc Allah là Đấng Bảo vệ cho hai đội quân đó. Và những người có đức tin nên tin cậy mà phó thác cho Allah.');
INSERT INTO "hasan_abdulkarim" VALUES(3,123,'3:123','Và chắc chắn Allah đã giúp các ngươi (chiến thắng) tại Badr trong lúc các ngươi chỉ gồm một đội quân vô vọng. Bởi thế, hãy sợ Allah để may ra các ngươi có dịp đáp ân (Allah).');
INSERT INTO "hasan_abdulkarim" VALUES(3,124,'3:124','(Hãy nhớ) khi Ngươi (Muhammad) bảo các tín đồ: “Không đủ cho các người hay sao việc Thượng Đế của các người gởi ba ngàn Thiên Thần xuống để tăng cường cho các người?”');
INSERT INTO "hasan_abdulkarim" VALUES(3,125,'3:125','Vâng, nếu các ngươi kiên trì và sợ Allah và nếu chúng (địch quân) bất thần tấn công các ngươi thì Thượng Đế của các ngươi sẽ tăng cường cho các ngươi năm ngàn Thiên Thần mang dấu hiệu đặc biệt.');
INSERT INTO "hasan_abdulkarim" VALUES(3,126,'3:126','Allah làm điều đó chỉ để báo tin mừng cho các ngươi và làm cho các ngươi yên tâm với nó. Và mọi thắng lợi đều do Allah, Đấng Toàn Năng, Đấng Sáng Suốt (quyết định tất cả).');
INSERT INTO "hasan_abdulkarim" VALUES(3,127,'3:127','(Làm thế) để Ngài tiêu diệt một thành phần của những ai không có đức tin hoặc hạ nhục chúng, bởi thế chúng trở về thất bại.');
INSERT INTO "hasan_abdulkarim" VALUES(3,128,'3:128','Hoàn toàn không phải là công việc của Ngươi (mà là của Allah), hoặc Ngài (Allah) tha thứ cho chúng hoặc Ngài trừng phạt chúng bởi lẽ chúng là những kẻ làm điều sai quấy.');
INSERT INTO "hasan_abdulkarim" VALUES(3,129,'3:129','Bởi vì mọi vật trong các tầng trời và mọi vật dưới đất đều là của Allah cả. Ngài tha thứ cho người nào Ngài muốn và trừng phạt kẻ nào Ngài muốn. Và Allah Hằng Tha Thứ, Rất Mực Khoan Dung.');
INSERT INTO "hasan_abdulkarim" VALUES(3,130,'3:130','Hỡi những ai có đức tin! Chớ cho vay lấy lãi (Riba) gấp đôi hoặc nhiều hơn; ngược lại hãy sợ Allah để may ra các ngươi được thịnh vượng.');
INSERT INTO "hasan_abdulkarim" VALUES(3,131,'3:131','Và hãy sợ Lửa (của hỏa ngục) được chuẩn bị sẵn cho những kẻ không có đức tin.');
INSERT INTO "hasan_abdulkarim" VALUES(3,132,'3:132','Ngược lại, hãy vâng lệnh Allah và Sứ Giả (của Ngài) để may ra các ngươi được khoan dung.');
INSERT INTO "hasan_abdulkarim" VALUES(3,133,'3:133','Và hãy nhanh chóng cầu xin sự Tha thứ từ Thượng Đế của các ngươi và (cầu xin) thiên đàng nơi mà khoảng rộng bằng các tầng trời và trái đất gộp lại, được chuẩn bị cho những người ngay chính sợ Allah:');
INSERT INTO "hasan_abdulkarim" VALUES(3,134,'3:134','Những ai chi dùng (cho Chính Nghĩa của Allah) lúc giàu cũng như lúc nghèo, đồng thời kiềm hãm được cơn giận và lượng thứ cho người, bởi vì Allah yêu thương những người làm tốt;');
INSERT INTO "hasan_abdulkarim" VALUES(3,135,'3:135','Và những ai khi đã phạm một điều thô bỉ hoặc làm hại bản thân mình liền tưởng nhớ Allah và cầu xin Ngài tha tội; và ai có thể tha tội nếu không phải là Allah? Và họ không tái phạm điều sai quấy mà họ đã làm.');
INSERT INTO "hasan_abdulkarim" VALUES(3,136,'3:136','Phần thưởng dành cho những người như thế sẽ gồm sự Tha thứ từ nơi Thượng Đế của họ và những Ngôi vườn thiên đàng bên dưới có các dòng sông chảy, nơi mà họ sẽ vào đó ở đời đời. Và tuyệt hảo thay phần thưởng dành cho những người làm việc (tốt).');
INSERT INTO "hasan_abdulkarim" VALUES(3,137,'3:137','Chắc chắn những lề lối xử thế (Sunan) tương tự đã ra đi trước các ngươi. Bởi thế, hãy đi du lịch trên trái đất để xét xem kết cuộc của những kẻ phủ nhận sự thật đã xẩy ra như thế nào.');
INSERT INTO "hasan_abdulkarim" VALUES(3,138,'3:138','(Qur''an) này là lời tuyên bố công khai cho nhân loại, một Chỉ Đạo và Lời giáo huấn cho những người ngay chính sợ Allah.');
INSERT INTO "hasan_abdulkarim" VALUES(3,139,'3:139','Và chớ yếu lòng cũng chớ buồn phiền, bởi vì các ngươi sẽ chiến thấng nếu các ngươi thực sự có đức tin.');
INSERT INTO "hasan_abdulkarim" VALUES(3,140,'3:140','Nếu các ngươi bị tổn thất thì chắc chắn đám người (ngoại đạo) kia cũng bị tổn thất tương tự như thế. Đó là những ngày (thử thách) mà TA (Allah) cho xoay vần giữa nhân loại. Và để cho Allah biết ai trong các ngươi là những người thật sự có đức tin và để Ngài thu nhận những người tử đạo trong các ngươi, bởi vì Allah không yêu thương những kẻ làm điều sai quấy.');
INSERT INTO "hasan_abdulkarim" VALUES(3,141,'3:141','Và để Allah thanh lọc những ai thực sự có đức tin và tiêu diệt những kẻ không cố đức tin.');
INSERT INTO "hasan_abdulkarim" VALUES(3,142,'3:142','Há các ngươi nghĩ rằng các ngươi sẽ vào thiên đàng trong lúc Allah chưa (thử thách để) biết ai trong các ngươi là người đã chiến đấu hăng say (cho Chính Nghĩa của Ngài) và kiên cường nhẫn nại ư?');
INSERT INTO "hasan_abdulkarim" VALUES(3,143,'3:143','Và chắc chắn các ngươi mong được chết (tử đạo) trước khi gặp nó (cái chết); nay, các ngươi thấy rõ nó trong lúc các ngươi đứng nhìn (trận chiến).');
INSERT INTO "hasan_abdulkarim" VALUES(3,144,'3:144','Và Muhammad chỉ là một Sứ Giả (của Allah). Nhiều Sứ Giả đã qua đời trước Y. Nếu Y chết hoặc bị giết thì các ngươi sẽ quay gót bỏ chạy hay sao? Và ai quay gót bỏ chạy thì y sẽ không bao giờ làm cho Allah thiệt thòi cả. Ngược lại, Allah sẽ trọng thưởng những người biết ơn.');
INSERT INTO "hasan_abdulkarim" VALUES(3,145,'3:145','Và không một người nào tự nhiên chết nếu không có phép của Allah theo tuổi thọ đã được ghi sẵn. Và ai muốn phần thưởng ở trần gian, TA (Allah) sẽ ban nó cho y. Và ai muốn phần thưởng ở đời sau, TA sẽ ban nó cho y. Và TA sẽ thưởng những người biết ơn.');
INSERT INTO "hasan_abdulkarim" VALUES(3,146,'3:146','Và có bao nhiêu Nabi cùng với nhiều tín đồ ngoan đạo đã chiến đấu (cho Chính Nghĩa của Allah?) Nhưng họ không sờn lòng nhụt chí, không tỏ ra yếu đuối và buông xuôi khi gặp hoạn nạn trên con đường chiến đấu cho Chính Nghĩa của Allah. Và Allah yêu thương những người kiên cường nhẫn nại.');
INSERT INTO "hasan_abdulkarim" VALUES(3,147,'3:147','Và họ đã không thốt ra lời nào khác ngoài lời này: “Lạy Thượng Đế của bầy tôi! Xin Ngài tha tội cho bầy tôi và (bỏ qua) những điều quá đáng (mà bầy tôi đã phạm) trong việc làm của bầy tôi và xin Ngài củng cố bàn chân của bầy tôi thêm vững chắc và giúp bầy tôi thắng được những kẻ không có đức tin.”');
INSERT INTO "hasan_abdulkarim" VALUES(3,148,'3:148','Bởi thế, Allah đã ban cho họ phần thưởng ở đời này và phần thưởng ưu việt ở đời sau bởi vì Allah yêu thương những người làm tốt.');
INSERT INTO "hasan_abdulkarim" VALUES(3,149,'3:149','Hỡi những ai có đức tin! Nếu các ngươi nghe theo những kẻ không có đức tin thì chúng sẽ làm cho các ngươi quay gót tháo lui, và vì thế các ngươi sẽ bỏ về thua thiệt.');
INSERT INTO "hasan_abdulkarim" VALUES(3,150,'3:150','Không! Allah là Đấng Bảo Hộ của các ngươi và là Đấng Cứu Giúp Ưu Việt.');
INSERT INTO "hasan_abdulkarim" VALUES(3,151,'3:151','TA sẽ làm cho những kẻ không có đức tin kinh hãi trong lòng bởi vì chúng tổ hợp với Allah những kẻ (thần linh) mà Ngài đã không ban cho một chút thẩm quyền nào; nhà ở của chúng (người thờ đa thần) sẽ là Lửa (của hỏa ngục); và nhà ở của những kẻ làm điều sai quấy thật là xấu xa.');
INSERT INTO "hasan_abdulkarim" VALUES(3,152,'3:152','Và chắc chắn Allah đã thực hiện đúng lời hứa của Ngài với các ngươi khi các ngươi gần đánh bại chúng (tại Uhud) với sự chấp thuận của Ngài cho đến lúc các ngươi không còn tự chủ nữa và đâm cãi nhau về mệnh lệnh (của Sứ Giả của Allah) và không chấp hành nó sau khi Ngài (Allah) trưng bày cho các ngươi thấy các món (chiến lợi phẩm) mà các ngươi thèm muốn. Trong các ngươi, có người ham muốn đời sống trần tục này nhưng cũng có người mong muốn đời sau; rồi Ngài (Allah) đưa đẩy các ngươi xa chúng (kẻ thù) hầu thử thách các ngươi. Và chắc chắn Ngài (Allah) đã lượng thứ cho các ngươi bởi vì Allah Rất mực Nhân từ đối với những người có đức tin.');
INSERT INTO "hasan_abdulkarim" VALUES(3,153,'3:153','(Hãy nhớ) khi các ngươi (hoảng hốt) cắm đầu chạy lên đồi cao và không quay nhìn đến một ai bên cạnh và Sứ Giả (của Allah) từ đằng sau lớn tiếng gọi các ngươi trở lại vị trí (chiến đấu). Bởi thế, Ngài (Allah) bắt các ngươi nếm từ mùi đau khổ này đến mùi đau khổ khác để cho các ngươi không còn luyến tiếc về món (chiến lợi phẩm) đã thoát khỏi tay của các ngươi và để cho các ngươi quên đi (tai họa) đã rơi nhằm phải các ngươi bởi vì Allah Hằng Am tường những điều các ngươi đã làm.');
INSERT INTO "hasan_abdulkarim" VALUES(3,154,'3:154','Rồi sau cơn hoạn nạn đó Ngài (Allah) đã ban an bình cho các ngươi. Bởi thế, một nhóm của các ngươi ngủ thiếp đi và một nhóm khác hãy còn tư lự về thân phận của họ rồi đâm ra nghĩ không đúng về Allah, lối suy nghĩ (thái quá) của Thời kỳ Ngu muội (trước Islam). Họ nói: “Há chúng tôi đã không có ý kiến gì trong công việc này hay sao?” Hãy bảo họ: “Công việc này hoàn toàn thuộc về Allah.” Họ giấu nơi mình điều (bất mãn) mà họ không dám tiết lộ ra cho Ngươi biết. Họ bảo nhau: “Nếu chúng mình có ý kiến trong công việc này thì chúng mình đâu đến nỗi bị giết nơi đây.” Hãy bảo họ (hỡi Sứ Giả!): “Dẫu cho các ngươi có ở tại nhà thì những ai đã tới số chết, nhất định sẽ bước ra ngoài để đi đến chỗ chết.” (Làm thế) để Allah thử thách cái (đức tin) nằm trong lòng của các ngươi và để tẩy sạch cái (cặn bã tội lỗi nằm) trong trái tim của các ngươi bởi vì Allah Hằng biết (điều bí mật) nằm trong lòng (của các ngươi).');
INSERT INTO "hasan_abdulkarim" VALUES(3,155,'3:155','Quả thật, những ai trong các ngươi quay lưng (đào tẩu) vào ngày hai đoàn quân (Muslim và Kafir) chạm trán (tại Uhud) thì chỉ Shaytan làm cho họ sa ngã vì những điều (tội lỗi) mà họ đã phạm nhưng chắc chắn Allah Hằng Lượng thứ cho họ. Quả thật, Allah Hằng Tha Thứ, Hằng Chịu đựng (với bầy tôi của Ngài).');
INSERT INTO "hasan_abdulkarim" VALUES(3,156,'3:156','Hỡi những ai có đức tin! Chớ (cư xử) như những ai không có đức tin và nói về các anh em (Muslim) khi họ ra đi (truyền giáo) xa trên mặt đất hoặc ra đi tham chiến như sau: “Giá họ ở lại với chúng mình thì đâu đến nỗi bị chết hoặc bị giết.” Chắc chắn Allah biến điều đó thành một nguồn ân hận trong lòng của họ bởi vì chính Allah (là Đấng) ban sự sống và gây cho chết (chứ không phải tự họ muốn). Và Allah thấy rõ những điều các ngươi đã làm.');
INSERT INTO "hasan_abdulkarim" VALUES(3,157,'3:157','Và nếu các ngươi bị giết hoặc chết cho Chính Nghĩa của Allah thì chắc chắn sự Tha thứ và Khoan Dung của Allah sẽ tốt hơn những món mà chúng góp nhặt.');
INSERT INTO "hasan_abdulkarim" VALUES(3,158,'3:158','Và nếu các ngươi chết hoặc bị giết thì bề nào các ngươi cũng sẽ được đưa về gặp lại Allah.');
INSERT INTO "hasan_abdulkarim" VALUES(3,159,'3:159','Bởi thế, với lòng Khoan dung của Allah, Ngươi (hỡi Sứ Giả!) nên đối xử hiền hòa với họ. Và nếu Ngươi khắt khe và cứng rắn với họ thì họ sẽ bỏ Ngươi đi tản mác khắp nơi. Bởi thế, hãy lượng thứ cho họ và cầu xin Allah tha thứ cho họ và tham khảo ý kiến với họ trong công việc (chung). Bởi thế, khi đã lấy quyết định rồi thì hãy phó thác cho Allah bởi vì quả thật Allah yêu thương những người tin cậy và phó thác (cho Ngài).');
INSERT INTO "hasan_abdulkarim" VALUES(3,160,'3:160','Nếu Allah giúp các ngươi thì không ai có thể thắng các ngươi nổi nhưng nếu Ngài bỏ rơi các ngươi thì sau Ngài ai là vị có thể giúp các ngươi (chiến thắng)? Bởi thế, những người có đức tin nên tin cậy mà phó thác cho Allah.');
INSERT INTO "hasan_abdulkarim" VALUES(3,161,'3:161','Và không một Nabi nào được phép giấu trộm chiến lợi phẩm; và ai giấu trộm một vật gì thì y sẽ phải mang vật giấu đó ra trình (trước Allah) vào Ngày phục sinh. Rồi mỗi người sẽ được trả lại đầy đủ về điều mà y đã thu hoạch và họ sẽ không bị đối xử bất công.');
INSERT INTO "hasan_abdulkarim" VALUES(3,162,'3:162','Há một người tuân theo sự hài lòng của Allah giống với một người tự chuốc lấy sự Giận dữ của Allah và nhà ở của y là hỏa ngục hay sao? Và đó là một nơi đến cuối cùng xấu xa nhất.');
INSERT INTO "hasan_abdulkarim" VALUES(3,163,'3:163','Đối với Allah, họ có cấp bậc khác nhau, bởi vì Allah thấy rõ những điều họ làm.');
INSERT INTO "hasan_abdulkarim" VALUES(3,164,'3:164','Chắc chắn Allah gia ân nhiều cho những người có đức tin khi Ngài dựng lên trong họ một Sứ Giả xuất thân từ họ để đọc cho họ các Lời Mặc Khải của Ngài và tẩy sạch họ và dạy họ Kinh Sách và Lẽ đúng đắn khôn ngoan trong lúc trước đây họ là những người hoàn toàn lầm lạc.');
INSERT INTO "hasan_abdulkarim" VALUES(3,165,'3:165','Phải chăng khi các ngươi bị thiệt hại nặng (tại Uhud) mặc dầu trước đó (tại Badr) các ngươi đã đập (kẻ thù) bằng một đòn mạnh gấp hai (đòn các ngươi đã bị tại Uhud), các ngươi than thở, nói: “Tai họa này từ đâu đến?” Hãy bảo họ: "Tai họa này do bản thân của các người gây ra". Bởi vì thật ra Allah có quyền chi phối trên tất cả mọi việc.');
INSERT INTO "hasan_abdulkarim" VALUES(3,166,'3:166','Và tai họa rơi nhằm phải các ngươi vào ngày hai đoàn quân (Muslim và Kafir) đụng độ nhau (tại Uhud)( là do sự chấp thuận của Allah để cho Ngài biết rõ những người có đức tin.');
INSERT INTO "hasan_abdulkarim" VALUES(3,167,'3:167','Và để Ngài biết ai chơi trò giả tạo đức tin, và có lời bảo chúng: “Hãy đến chiến đấu cho Chính Nghĩa của Allah hoặc phòng thủ (thành phố Madinah) thì chúng đáp: “Giá chúng tôi biết chắc có một trận giao tranh (giữa ta và địch) thì chúng tôi đã tòng quân với quí vị rồi.” Ngày đó chúng tiếp cận với sự không tin hơn là sự tin tưởng, bởi vì miệng của chúng nói ra điều không có trong lòng của chúng. Và Allah biết rõ những điều (giả dối) mà chúng giấu giếm.');
INSERT INTO "hasan_abdulkarim" VALUES(3,168,'3:168','Những ai nói về các anh em (Muslim) của mình trong lúc chúng ngồi yên tại nhà: “Phải chỉ họ nghe lời của chúng mình thì đâu đến nỗi phải bị giết.” Hãy bảo chúng (hỡi Sứ Giả!): “Nếu các ngươi nói đúng sự thật thì hãy xoay cái chết đi khỏi mình của các ngươi thử xem?”');
INSERT INTO "hasan_abdulkarim" VALUES(3,169,'3:169','Và chớ nghĩ rằng những ai hy sinh vì Chính Nghĩa của Allah đã chết. Không, họ vẫn còn sống ở nơi Thượng Đế của họ và được cung dưỡng (đầy đủ).');
INSERT INTO "hasan_abdulkarim" VALUES(3,170,'3:170','Họ vui hưởng thiên lộc mà Allah đã ban cấp. Họ được tin vui về những ai còn lại sau nhưng chưa nhập đoàn chung với họ rằng họ (những người tử đạo) sẽ không lo sợ cũng sẽ không buồn phiền.');
INSERT INTO "hasan_abdulkarim" VALUES(3,171,'3:171','Họ hoan hỉ với tin vui về ân huệ và thiên lộc của Allah và rằng quả thật Allah không làm mất phần thưởng của những người có đức tin:');
INSERT INTO "hasan_abdulkarim" VALUES(3,172,'3:172','Những ai đã đáp lại lời gọi của Allah và của Sứ Giả (của Allah) sau khi mang thương tích (tại Uhud); dành cho những ai làm tốt và sợ Allah sẽ là một phần thưởng to lớn.');
INSERT INTO "hasan_abdulkarim" VALUES(3,173,'3:173','Những ai mà người ta đã nói với họ: “Quả thật, những người (ngoại đạo) tập trung đến tấn công qúi vị. Bởi thế, hãy sợ chúng.” Nhưng điều đó chỉ làm tăng thêm đức tin của họ và họ nói: “Allah đủ giúp chúng tôi (chống lại kẻ thù) bởi vì Ngài là Đấng Bảo Trợ Ưu Việt.”');
INSERT INTO "hasan_abdulkarim" VALUES(3,174,'3:174','Và họ đã trở về nhà với ân huệ và thiên lộc của Allah. Họ đã không gặp điều rủi ro nào; và họ làm theo sự hài lòng của Allah. Và Allah có vô vàn thiên lộc vĩ đại.');
INSERT INTO "hasan_abdulkarim" VALUES(3,175,'3:175','Chỉ Shaytan mới xúi giục các ngươi sợ bạn bè của nó; bởi thế chớ sợ chúng mà hãy sợ TA (Allah) nếu các ngươi là những người có đức tin.');
INSERT INTO "hasan_abdulkarim" VALUES(3,176,'3:176','Và chớ để cho những ai lao đầu vào vòng không tin tưởng làm cho Ngươi (Muhammad) buồn rầu. Tuyệt đối chúng không bao giờ hại được Allah; ngược lại Allah không muốn cho chúng hưởng một phần lộc nào ở đời sau. Và chúng sẽ phải chịu một sự trừng phạt to lớn.');
INSERT INTO "hasan_abdulkarim" VALUES(3,177,'3:177','Quả thật, những ai dùng đức tin để mua lấy sự không tin thì tuyệt đối sẽ không bao giờ hại được Allah (với việc làm của mình). Ngược lại, chúng sẽ phải chịu một sự trừng phạt đau đớn.');
INSERT INTO "hasan_abdulkarim" VALUES(3,178,'3:178','Và những ai không có đức tin chớ nghĩ rằng việc TA gia hạn cho chúng là tốt cho bản thân của chúng. Việc TA gia hạn cho chúng là chỉ để cho chúng chồng chất thêm tội lỗi; và chúng sẽ phải chịu một sự trừng phạt nhục nhã.');
INSERT INTO "hasan_abdulkarim" VALUES(3,179,'3:179','Allah sẽ không để cho những người có đức tin ở mãi trong hoàn cảnh mà các ngươi hiện đang sống cho đến khi nào Ngài tách biệt điều ô uế ra khỏi điều tốt sạch. Và Allah không là Đấng tiết lộ cho các ngươi biết những bí mật của cõi vô hình; ngược lại, Allah chọn người nào Ngài muốn trong số các Sứ Giả của Ngài (để tiếp thu điều bí mật đó). Bởi thế, hãy tin tưởng nơi Allah và các Sứ Giả của Ngài. Do đó, nếu các ngươi tin tưởng và sợ Allah thì các người sẽ được một phần thưởng rất lớn.');
INSERT INTO "hasan_abdulkarim" VALUES(3,180,'3:180','Và những ai keo kiệt ôm lấy phần thiên lộc mà Allah đã ban cấp chớ nên nghĩ đó là điều tốt cho họ. Không, việc đó chỉ xấu cho họ thôi. Tài sản mà họ ích kỷ ôm giữ sẽ sớm được mang ra treo lủng lẳng nơi cổ của họ vào Ngày phục sinh. Bởi vì di sản của các tầng trời và trái đất đều là của Allah cả; và Allah Rất Am Tường về những điều các ngươi làm.');
INSERT INTO "hasan_abdulkarim" VALUES(3,181,'3:181','Chắc chắn Allah đã nghe lời tuyên bố của những kẻ đã nói: “Quả thật, Allah nghèo và chúng tôi giàu”. Chắc chắn TA sẽ cho ghi lời chúng đã nói và việc chúng đã giết các vị Nabi không có lý do chính đáng và TA sẽ phán: “Hãy nếm sự trừng phạt của sự thiêu đốt.”');
INSERT INTO "hasan_abdulkarim" VALUES(3,182,'3:182','Sở dĩ như thế là vì điều (tội lỗi) mà bàn tay của các ngươi đã gởi đi trước và bởi vì Allah không bao giờ đối xử bất công đối với bầy tôi (của Ngài).');
INSERT INTO "hasan_abdulkarim" VALUES(3,183,'3:183','Những ai đã nói: “Quả thật, Allah đã giao ước với chúng tôi bảo chớ nên tin tưởng một Sứ Giả nào trừ phi người Sứ Giả đó mang đến cho chúng tôi một vật tế mà lửa sẽ cháy bén.”( (Hỡi Sứ Giả!) hãy bảo chúng: “Chắc chắn trước ta, đã có những vị Sứ Giả được phái đến gặp các người với những bằng chứng rõ rệt và với điều mà các người đang nói đến, nhưng tại sao các người đã giết Họ nếu các người chân thật?”.');
INSERT INTO "hasan_abdulkarim" VALUES(3,184,'3:184','Bởi thế, nếu chúng cho rằng Ngươi (Muhammad) nói dối thì chắc chắn chúng cũng đã phủ nhận những vị Sứ Giả trước Ngươi đã đến gặp chúng với những bằng chứng rõ rệt và với Kinh Zubur và Kinh Sách soi sáng (vấn đề).');
INSERT INTO "hasan_abdulkarim" VALUES(3,185,'3:185','Mỗi một linh hồn đều phải nếm cái chết. Và chỉ vào Ngày phục sinh các ngươi mới được đền bù lại trọn vẹn phần công lao của các ngươi. Bởi thế, ai được bốc đi xa khỏi Lửa (của hỏa ngục) và được thu nhận vào thiên đàng thì chắc chắn sẽ thành đạt. Và đời sống trần gian này chỉ là một sự hưởng thụ đầy dối trá.');
INSERT INTO "hasan_abdulkarim" VALUES(3,186,'3:186','Chắc chắn các ngươi sẽ bị thử thách về tài sản lẫn sinh mạng của các ngươi. Và chắc chắn các ngươi sẽ nghe lắm điều phiền não từ những kẻ đã được ban cho Kinh Sách trước các ngươi và từ những kẻ thờ đa thần. Tuy nhiên, nếu các ngươi kiên nhẫn và sợ Allah thì quả thật đó là một quyết định rất lớn lao.');
INSERT INTO "hasan_abdulkarim" VALUES(3,187,'3:187','Và (hãy nhớ) khi Allah đã nhận Lời Giao ước của những kẻ đã được ban cấp Kinh Sách (với lời phán): “Các ngươi hãy công bố nó (điều nói về Sứ Giả Muhammad) cho nhân loại và không được giấu giếm nó,” thì chúng (không ngại) quẳng nó ra sau lưng và bán nó với một giá nhỏ nhoi; và thật xấu xa thay điều mà chúng đã bán ra.');
INSERT INTO "hasan_abdulkarim" VALUES(3,188,'3:188','Ngươi chớ nghĩ rằng những ai hí-hửng với lợi lộc mà chúng mang về và yêu thích việc thiên hạ ca ngợi mình về những điều mà chúng đã không làm,- Ngươi chớ nghĩ rằng chúng sẽ an toàn thoát khỏi hình phạt (của Allah); ngược lại chúng sẽ bị trừng phạt đau đớn.');
INSERT INTO "hasan_abdulkarim" VALUES(3,189,'3:189','Bởi vì Allah nắm quyền thống trị các tầng trời và trái đất; và Allah có quyền chi phối trên tất cả mọi việc.');
INSERT INTO "hasan_abdulkarim" VALUES(3,190,'3:190','Quả thật, trong việc tạo hóa các tầng trời và trái đất và trong việc luân chuyển của ban đêm và ban ngày là những dấu hiệu cho những người hiểu biết:');
INSERT INTO "hasan_abdulkarim" VALUES(3,191,'3:191','Những ai tưởng nhớ Allah lúc đứng, lúc ngồi và lúc nằm nghiêng một bên và nghiền ngẫm (về sự mầu nhiệm) trong việc tạo hóa các tầng trời và trái đất (đến nỗi phải thốt lên): “Lạy Thượng Đế của bầy tôi! Ngài đã không tạo hóa (mọi thứ) này vô mục đích. Quang vinh và trong sạch thay Ngài! Xin Ngài giải cứu bầy tôi thoát khỏi sự trừng phạt của hỏa ngục.');
INSERT INTO "hasan_abdulkarim" VALUES(3,192,'3:192','“Lạy Thượng Đế của bầy tôi! Rõ thật Ngài! Ai mà bị Ngài cho vào hỏa ngục thì quả thật y bị Ngài hạ nhục. Và những kẻ làm điều sai quấy sẽ không được ai giúp đỡ.');
INSERT INTO "hasan_abdulkarim" VALUES(3,193,'3:193','“Lạy Thượng Đế của bầy tôi! Bầy tôi đã nghe lời gọi của một vị mời (bầy tôi) đến với Đức tin, bảo (bầy tôi): ''Các ngươi hãy tin tưởng nơi Thượng Đế của các ngươi,'' bởi thế bầy tôi đã tin tưởng. Lạy Thượng Đế của bầy tôi! Xin Ngài tha tội cho bầy tôi và xóa bỏ những việc làm xấu xa của bầy tôi cho bầy tôi và bắt hồn bầy tôi (ra đi) cùng với những người đạo đức.');
INSERT INTO "hasan_abdulkarim" VALUES(3,194,'3:194','“Lạy Thượng Đế của bầy tôi! Xin Ngài ban cho bầy tôi điều mà Ngài đã hứa với bầy tôi qua (trung gian của) các vị Sứ Giả của Ngài và đừng hạ nhục bầy tôi vào Ngày phục sinh. Bởi vì, Ngài không bao giờ thất hứa trong lời hứa của Ngài.”');
INSERT INTO "hasan_abdulkarim" VALUES(3,195,'3:195','Do đó, Thượng Đế của họ đã đáp lời cầu nguyện của họ, phán bảo: “TA sẽ không làm phí mất công lao của bất cứ người làm việc nào trong các ngươi, dù là nam hay nữ, người này và người kia. Bởi thế, những ai đã di cư và bị xua đuổi ra khỏi nhà cửa của họ và chịu hoạn nạn đau khổ vì Chính Nghĩa của TA, đã anh dũng chiến đâu và bị giết thì chắc chắn TA sẽ xóa bỏ tội lỗi của họ cho họ và thu nhận họ vào những Ngôi vườn nơi thiên đàng bên dưới có các dòng sông chảy, một phần thưởng xứng đáng từ Allah; và Allah, có nơi Ngài những phần thưởng tốt nhất.”');
INSERT INTO "hasan_abdulkarim" VALUES(3,196,'3:196','Ngươi chớ bị mắc lừa bởi việc đi lại hí hửng trên mặt đất của những kẻ không có đức tin.');
INSERT INTO "hasan_abdulkarim" VALUES(3,197,'3:197','(Đó chỉ là) một sự hưởng thụ tạm bợ rồi nhà ở của chúng sẽ là hỏa ngục, một nơi nằm nghỉ thật vô cùng tồi tệ.');
INSERT INTO "hasan_abdulkarim" VALUES(3,198,'3:198','Ngược lại, những ai sợ Thượng Đế của họ thì sẽ được hưởng những Ngôi vườn nơi thiên đàng bên dưới có các dòng sông chảy, nơi mà họ sẽ vào đó ở đời đời, một sự chiêu đãi từ Allah và những gì ở nơi Allah sẽ tốt hơn dành cho những người ngoan đạo.');
INSERT INTO "hasan_abdulkarim" VALUES(3,199,'3:199','Và quả thật, trong số Người dân Kinh Sách có người tin tưởng nơi Allah và điều (mặc khải) đã được ban cho các ngươi và điều (mặc khải) đã được ban cho họ, họ hạ mình khiêm tốn trước Allah, họ không bán các Lời Mặc Khải của Allah với một giá nhỏ nhoi; họ là những người mà phần thưởng sẽ ở nơi Thượng Đế của họ. Quả thật, Allah rất nhanh trong việc thanh toán (thưởng phạt).');
INSERT INTO "hasan_abdulkarim" VALUES(3,200,'3:200','Hỡi những ai có đức tin! Hãy tuyệt đối kiên nhẫn và kiên quyết và hãy kính sợ Allah để may ra các ngươi được thành đạt.');
INSERT INTO "hasan_abdulkarim" VALUES(4,1,'4:1','Hỡi nhân loại! Hãy sợ Thượng Đế (Allah) của các ngươi, Đấng đã tạo các ngươi từ một người duy nhất (Adam) và từ Y Ngài đã tạo ra người vợ (Hauwa) của Y và từ hai người họ, (Allah) rải ra vô số đàn ông và đàn bà (trên khắp mặt đất). Và hãy kính sợ Allah, Đấng mà các ngươi đòi hỏi (quyền hạn) lẫn nhau và hãy (kính trọng) những dạ con( bởi vì quả thật Allah Hằng Theo Dõi các ngươi.');
INSERT INTO "hasan_abdulkarim" VALUES(4,2,'4:2','Và hãy trả lại cho các trẻ mồ côi tài sản của chúng (khi chúng đến tuổi trưởng thành). Và chớ tráo vật dụng xấu (của các ngươi) đổi lấy vật dụng tốt (của chúng). Và chớ ăn bớt tài sản của chúng (bằng cách nhập chung lại) với tài sản của các ngươi. Bởi vì đó là một trọng tội.');
INSERT INTO "hasan_abdulkarim" VALUES(4,3,'4:3','Và nếu các ngươi sợ không thể đối xử công bằng với các con (gái) mồ côi, hãy cưới những người phụ nữ (khác) mà các ngươi vừa ý hoặc hai, hoặc ba, hoặc bốn. Nhưng nếu các ngươi sợ không thể (ăn ở) công bằng với họ (vợ) thì hãy cưới một bà thôi hoặc người (phụ nữ) nào ở dưới quyền của các ngươi(. Điều đó thích hợp cho các ngươi hơn để may ra (vì thế) các ngươi tránh được bất công.');
INSERT INTO "hasan_abdulkarim" VALUES(4,4,'4:4','Và hãỵ tặng cho các người vợ tiền cưới bắt buộc (Mahr) của họ. Nhưng nếu họ vui lòng tặng lại một phần nào cho các ngươi thì các ngươi hãy hoan hỉ hưởng nó một cách bổ ích.');
INSERT INTO "hasan_abdulkarim" VALUES(4,5,'4:5','Và chớ giao cho các (đứa trẻ mồ côi) kém hiểu biết tài sản của các ngươi mà Allah đã giao quyền quản lý cho các người để làm phương tiện cấp dưỡng (chúng); ngược lại hãy dùng nó để cung dưỡng và may mặc cho chúng và dùng lời lẽ nhã nhặn mà ăn nói với chúng.');
INSERT INTO "hasan_abdulkarim" VALUES(4,6,'4:6','Và hãy trắc nghiệm các đứa trẻ mồ côi cho đến lúc chúng đến tuổi kết hôn. Nếu các ngươi nhận thấy chúng đã trưởng thành vững chắc, hãy giao tài sản của chúng lại cho chúng và chớ tiêu phí nó nhanh chóng sợ rằng chúng mau lớn khôn (mà đòi lại), và người (giám hộ) nào giàu, nên hãm mình (tránh tiêu xài nó); ngược lại người (giám hộ) nào nghèo, hãy tiêu dùng nó một cách vừa phải. Bởi thế, khi các ngươi giao tài sản của chúng lại cho chúng, hãy mời người khác đến làm chứng cho chúng; và Allah đủ đảm trách việc ghi sổ.');
INSERT INTO "hasan_abdulkarim" VALUES(4,7,'4:7','Người đàn ông được hưởng một phần của gia tài do cha mẹ và bà con gần để lại; và người đàn bà được hưởng một phần của gia tài do cha mẹ và bà con gần để lại; dù gia tài đó ít hay nhiều, việc chia phần đều có tính nhất định.');
INSERT INTO "hasan_abdulkarim" VALUES(4,8,'4:8','Và lúc chia gia tài, nếu có mặt của bà con gần và trẻ mồ cồi và người nghèo tại đó thì hãy cung cấp cho họ một phần nào của (gia tài), và nói năng với họ lời lẽ nhã nhặn.');
INSERT INTO "hasan_abdulkarim" VALUES(4,9,'4:9','Và những ai (đứng ra chia gia tài) nên sợ trong lòng giống như sợ cho chính bản thân mình rằng mai hậu cũng sẽ để lại con cái thơ dại không ai chăm sóc giống như thế. Bởi vậy, họ hãy kính sợ Allah và hãy nói lời lẽ ngay chính.');
INSERT INTO "hasan_abdulkarim" VALUES(4,10,'4:10','Bởi vì quả thật, những ai ăn bớt tài sản của trẻ mồ côi một cách bất chính thì chẳng khác nào nuốt lửa vào bụng. Và họ sẽ sớm bị nướng trong lửa ngọn (của hỏa ngục).');
INSERT INTO "hasan_abdulkarim" VALUES(4,11,'4:11','Allah ra lệnh cho các ngươi về việc con cái của các ngươi hưởng gia tài (như sau): phần của con trai bằng hai phần của con gái. Nhưng nếu chỉ có con gái và số con gái nhiều hơn hai thì phần của tất cả các con gái là hai phần ba (3) của gia tài để lại; và nếu chỉ có một đứa con gái thì phần của nữ là phân nửa (1/2) gia tài để lại. Nếu người chết có con thì cả cha lẫn mẹ mỗi người được hưởng một phần sáu (1/6) của gia tài để lại. Nhưng nếu (người chết) không có con và chỉ có cha mẹ là những người thừa kế thì người mẹ được hưởng một phần ba (1/3) của gia tài để lại. Nếu người chết có đông anh chị em thì người mẹ được hưởng một phần sáu (1/6) của gia tài để lại. Việc phân chia gia tài này sẽ được thực hiện sau khi đã thi hành xong những điều ghi trong di chúc và sau khi trả hết nợ nần nếu có. Các ngươi không biết giữa cha mẹ và con cái ai là người gần nhất trong việc hưởng lộc, cho nên Allah định phần như thế (cho các ngươi) bởi vì quả thật Allah Rất mực Hiểu biết và Rất Mực Sáng Suốt.');
INSERT INTO "hasan_abdulkarim" VALUES(4,12,'4:12','Và các ngươi được hưởng phân nửa (2) gia tài của các bà vợ để lại nếu không có con. Nhưng nếu có một đứa con thì các ngươi được hưởng một phần tư (1/4) của gia tài để lại sau khi thực hiện xong những điều ghi trong di chúc và trả hết nợ. Và các người vợ sẽ được hưởng một phần tư (1/4) của gia tài để lại nếu các ngươi không có con; nhưng nếu các ngươi có con thì họ sẽ hưởng một phần tám (1/8) của gia tài mà các ngươi để lại sau khi thực hiện xong những điều ghi trong di chúc và trả hết nợ. Và nếu người chết, dù là nam hay nữ, để lại gia tài nhưng không có con cái hay cha mẹ để thừa hưởng mà chỉ có một người anh (hay em) trai hoặc một người chị (hay em) gái thì người anh (hay em) trai hoặc người chị (hay em) gái còn sống đó, mỗi người (trong hai) sẽ hưởng một phần sáu (1/6) của gia tài; nhưng nếu số lượng anh chị em đông hơn một người thì phần của họ là một phần ba (1/3) của gia tài sau khi thực hiện xong những điều ghi trong di chúc hoặc trả hết nợ nần miễn sao những người thừa kế không bị thiệt thòi. Đó là điều lệnh do Allah ban xuống. Và Allah Hằng Biết, Hằng Chịu Đựng (với bầy tôi của Ngài).');
INSERT INTO "hasan_abdulkarim" VALUES(4,13,'4:13','Đó là những giới hạn qui định bởi Allah. Và ai vâng lệnh Allah và Sứ Giả của Ngài thì sẽ được chấp nhận vào thiên đàng bên dưới có các dòng sông chảy, họ sẽ sống trong đó đời đời; và đó là một thành tựu vĩ đại.');
INSERT INTO "hasan_abdulkarim" VALUES(4,14,'4:14','Ngược lại, ai không vâng lệnh Allah và Sứ Giả của Ngài và vi phạm các giới hạn của Allah thì sẽ sa vào hỏa ngục, họ sẽ phải ở trong đó đời đời và sẽ phải hứng chịu một sự trừng phạt nhục nhã.');
INSERT INTO "hasan_abdulkarim" VALUES(4,15,'4:15','Bất cứ người vợ nào của các ngươi phạm tội thông gian hãy đưa ra bốn người của các ngươi đến làm chứng để buộc tội họ. Nếu họ thú tội, hãy nhốt họ trong nhà cho đến khi cái chết túm lấy họ hoặc Allah sẽ mở ra cho họ một lối thoát khác.');
INSERT INTO "hasan_abdulkarim" VALUES(4,16,'4:16','Và nếu hai người (đàn ông và đàn bà) trong các ngươi phạm tội thông gian thì hãy phạt cả hai thật nặng. Nhưng nếu họ sám hối và sửa mình thì hãy bỏ mặc họ bởi vì quả thật Allah Hằng Tha Thứ, Rất Mực Khoan Dung.');
INSERT INTO "hasan_abdulkarim" VALUES(4,17,'4:17','(Nên nhớ rằng) Allah chỉ chấp nhận việc hối cải của những ai vì ngu muội mà phạm tội rồi nhanh chóng sám hối sau đó. Allah sẽ tha thứ cho những người như thế bởi vì Allah là Đấng Hằng Biết, Rất Mực Sáng Suốt.');
INSERT INTO "hasan_abdulkarim" VALUES(4,18,'4:18','Việc hối cải không có hiệu quả đối với những ai tiếp tục phạm tội cho đến khi họ sắp chết thì họ mới nói: “Bây giờ tôi đã hối cải.” (Việc hối cải) cũng không có kết quả đối với những ai chết trong tình trạng không có đức tin. Đối với những người này, TA (Allah) đã chuẩn bị sẵn cho họ một sự trừng phạt đau đớn.');
INSERT INTO "hasan_abdulkarim" VALUES(4,19,'4:19','Hỡi những ai có đức tin! Các ngươi không được phép cưỡng bức vợ để thừa hưởng gia tài của họ, cũng không được giam (hành hạ) họ để lấy lại một phần nào tiền cưới (Mahr) mà các người đã tặng họ ngoại trừ trường hợp họ công khai phạm tội thông gian; ngược lại, hãy sống chung với họ một cách tử tế bởi vì nếu các ngươi ghét họ thì có lẽ các ngươi ghét một (sinh) vật mà Allah đã ban nhiều cái tốt lành.');
INSERT INTO "hasan_abdulkarim" VALUES(4,20,'4:20','Và nếu các ngươi muốn lấy người vợ sau thay cho người vợ trước và các ngươi đã tặng mỗi bà một đóng vàng (làm quà cưới) thì các ngươi không được phép lấy lại một tí nào cả. Phải chăng các ngươi muốn lấy lại của cải bằng cách vu oan hoặc làm nên tội công khai?');
INSERT INTO "hasan_abdulkarim" VALUES(4,21,'4:21','Làm sao các ngươi có thể (nhắm mắt) lấy lại của cải đó trong lúc các ngươi đã ăn nằm với nhau và họ đã nhận từ các ngươi một lời giao ước long trọng (khi thành hôn)?');
INSERT INTO "hasan_abdulkarim" VALUES(4,22,'4:22','Và chớ cưới phụ nữ nào mà người cha của các ngươi đã cưới làm vợ ngoại trừ trường hợp đã lỡ xảy ra (trong quá khứ) bởi vì đó là một điều ô nhục và là một tập tục đáng ghê tởm.');
INSERT INTO "hasan_abdulkarim" VALUES(4,23,'4:23','Cấm các ngươi (lấy làm vợ các phụ nữ sau đây:) mẹ, con gái, chị (em) ruột, cô, dì, cháu (gọi bằng bác hay chú, hay ông), mẹ nuôi, chị (em) nuôi cùng bú một vú, mẹ vợ, con gái riêng của người vợ mà các ngươi đã ăn nằm đang dưới quyền giám hộ của các ngươi; nhưng nếu người con gái riêng của người vợ mà các ngươi chưa ăn nằm thì các ngươi không có tội nếu lấy chúng; con dâu (vợ của con ruột); (cấm lấy) hai chị em ruột cùng một lúc ngoại trừ trường hợp đã lỡ xảy ra (trong quá khứ). Quả thật, Allah Hằng Tha Thứ, Rất Mực Khoan Dung.');
INSERT INTO "hasan_abdulkarim" VALUES(4,24,'4:24','Và (cấm các người lấy) các phụ nữ đã có chồng ngoại trừ (những nữ tù binh) nằm trong tay của các ngươi. Đó là lệnh (cấm) của Allah đối với các ngươi. Ngoài những người phụ nữ vừa nêu, các phụ nữ khác đều hợp pháp cho các ngươi (để lấy làm vợ) miễn sao các ngươi kiếm vợ bằng cách dùng tài sản của các ngươi để cưới hỏi đàng hoàng chứ không được gian dâm (hay ngoại tình). Bởi việc các ngươi hưởng lạc từ họ, hãy tặng họ phần tiền cưới (Mahr) bắt buộc của họ. Nhưng nếu sau khi lễ vật (Mahr) đã được qui định, các ngươi thỏa thuận (thay đổi nó) thì các ngươi sẽ không có tội. Quả thật, Allah là Đấng Hằng Biết, Đấng Sáng suốt.');
INSERT INTO "hasan_abdulkarim" VALUES(4,25,'4:25','Và ai trong các ngươi không có phương tiện cưới hỏi các nữ tín đồ tự do (không phải là nữ nô lệ) thì hãy cưới các cô gái nô lệ có đức tin nằm trong tay của các ngươi, bởi vì Allah biết rõ đức tin của các ngươi: các ngươi thuộc chung một cộng đồng (Muslim). Do đó, hãy cưới các cô ấy với sự chấp thuận của chủ nhân của họ và tặng họ phần lễ vật (Mahr) của họ một cách biết điều xem họ như phụ nữ được cưới hỏi đàng hoàng chứ không như đàn bà ngoại tình cũng không như tình nhân vụng trộm; bởi thế khi họ đã thành vợ (chồng), nếu họ phạm tội thông gian thì hình phạt dùng xử lý họ chỉ bằng phân nửa hình phạt dùng xử lý các phụ nữ tự do. Điều kiện (cho phép) này là dành cho ai trong các ngươi sợ phạm tội (ngoại tình nếu không lập gia đình). Và (hãy biết rằng) việc hãm mình khắc kỷ sẽ tốt hơn cho các ngươi. Và Allah Hằng Tha Thứ, Rất Mực Khoan Dung.');
INSERT INTO "hasan_abdulkarim" VALUES(4,26,'4:26','Allah muốn trình bày rõ cho các ngươi lề lối xử thế của những người (đạo đức) trước các ngươi hầu hướng dẫn và tha thứ cho các ngươi bởi vì Allah Rất mực Hiểu Biết, Rất Mực Sáng Suốt.');
INSERT INTO "hasan_abdulkarim" VALUES(4,27,'4:27','Và Allah muốn tha thứ cho các ngươi nhưng những kẻ làm theo dục vọng muốn làm cho các ngươi sa ngã dữ dội.');
INSERT INTO "hasan_abdulkarim" VALUES(4,28,'4:28','Allah muốn làm nhẹ gánh nặng (tội lỗi) cho các ngươi bởi vì con người được tạo ra vốn yếu đuối (dễ sa vào vòng tình ái vụng trộm).');
INSERT INTO "hasan_abdulkarim" VALUES(4,29,'4:29','Hỡi những ai có dức tin! Chớ ăn tài sản lẫn nhau giữa các người một cách bất chính trừ phi bằng thỏa thuận mua bán giữa các ngươi với nhau; và chớ tự giết hại bản thân. Quả thật, Allah Hằng Thương Xótđối với các ngươi.');
INSERT INTO "hasan_abdulkarim" VALUES(4,30,'4:30','Và ai làm điều đó với hận thù và bất công thì sẽ sớm bị TA (Allah) nướng trong hỏa ngục, và việc (trừng phạt) đó rất dễ dàng đối với Allah.');
INSERT INTO "hasan_abdulkarim" VALUES(4,31,'4:31','Nếu các ngươi tránh được những (tội) lớn mà các ngươi bị cấm, TA sẽ xoá đi khỏi các ngươi những điều tội lỗi (nhỏ) và thu nhận các ngươi vào (thiên đàng) bằng lối vào vô cùng vinh dự.');
INSERT INTO "hasan_abdulkarim" VALUES(4,32,'4:32','Và chớ ham muốn những thứ mà Allah dùng để ưu đãi người này hơn người nọ. Đàn ông hưởng phần (kết quả) mà họ đã kiếm được và đàn bà hưởng phần (kết quả) mà họ đã kiếm được; và hãy cầu xin Allah về thiên lộc của Ngài bởi vì Allah Hằng Biết hết mọi việc.');
INSERT INTO "hasan_abdulkarim" VALUES(4,33,'4:33','Và TA đã chỉ định cho từng người ai là người thừa kế hưởng gia tài của cha mẹ hay thân thuộc để lại. Và đối với những người (di cư từ Makkah) mà các ngươi đã nắm tay thề thốt với nhau, hãy cho họ phần qui định của họ bởi vì Allah làm chứng cho tất cả mọi việc.');
INSERT INTO "hasan_abdulkarim" VALUES(4,34,'4:34','Người đàn ông là trụ cột (của gia đình) trên đàn bà bởi vì Allah ban cho người này sức lực hơn người kia và bởi vì họ chi dùng tài sản của họ vào việc cấp dưỡng gia đình. Do đó, người đàn bà đức hạnh nên phục tùng chồng và trông coi (nhà cửa) trong lúc chồng vắng mặt với sự giúp đỡ và trông chừng của Allah; và đối với các bà (vợ) mà các ngươi sợ họ thất tiết và bướng bỉnh, (trước hết) hãy cảnh cáo họ, (kế đó) từ chối ăn nằm với họ, và (cuối cùng) đánh họ (nhẹ tay); bởi thế, nếu họ chịu nghe theo các ngươi thì chớ kiếm chuyện (rầy rà) với họ bởi vì Allah Rất Tối Cao, Rất Vĩ Đại.');
INSERT INTO "hasan_abdulkarim" VALUES(4,35,'4:35','Và nếu các ngươi sợ (vợ chồng) thôi nhau, hãy mời một vị trọng tài của gia đình bên chồng và một vị trọng tài của gia đình bên vợ đến hòa giải. Nếu hai đằng muốn hòa thuận thì Allah sẽ giải hòa hai người trở lại bởi vì Allah Hằng Biết, Am Tường mọi sự việc.');
INSERT INTO "hasan_abdulkarim" VALUES(4,36,'4:36','Hãy thờ phụng Allah và tuyệt đối chớ tổ hợp bất cứ cái gì (ai) với Ngài, và hãy ăn ở tử tế với cha mẹ, bà con ruột thịt, trẻ mồ côi, người thiếu thốn; hãy (đối xử tử tế) với xóm giềng gần xa, với bạn bè bên cạnh, với người lỡ đường xa, và với (tù binh) nằm trong tay của các ngươi bởi vì quả thật Allah không yêu thương những kẻ tự phụ, khoe khoang:');
INSERT INTO "hasan_abdulkarim" VALUES(4,37,'4:37','Những ai hà tiện và xúi giục thiên hạ hà tiện (như mình) và giấu giếm thiên lộc mà Allah đã ban cho họ; và TA đã chuẩn bị cho những kẻ không có đức tin một sự trừng phạt nhục nhã.');
INSERT INTO "hasan_abdulkarim" VALUES(4,38,'4:38','Và những ai tiêu dùng tài sản của họ phô trương cho thiên hạ thấy chứ không tin tưởng nơi Allah và Ngày (Phán xử) Cuối cùng; và ai kết bạn với Shaytan thì đó là một người bạn xấu nhất!');
INSERT INTO "hasan_abdulkarim" VALUES(4,39,'4:39','Và họ có điều chi thiệt thòi nếu họ tin tưởng nơi Allah và tin nơi Ngày (Phán xử) Cuối cùng và tiêu dùng (thiên lộc) mà Allah đã ban cho họ (để làm việc thiện)? Và Allah biết họ rất rõ.');
INSERT INTO "hasan_abdulkarim" VALUES(4,40,'4:40','Quả thật, Allah không hề làm một điều bất công nào dù có nhỏ bằng hạt cải đi nữa. Và nếu có một điều phúc lành nào, Ngài sẽ nhân nó lên gấp đôi (cho họ) và ban cho từ Ngài một phần thưởng rất lớn.');
INSERT INTO "hasan_abdulkarim" VALUES(4,41,'4:41','Thế sự việc sẽ ra sao khi TA đưa ra từ mỗi cộng đồng một nhân chứng và TA sẽ đưa Ngươi (hỡi Muhammad!) ra làm một Nhân chứng đối chất với đám người (không có đức tin) này?');
INSERT INTO "hasan_abdulkarim" VALUES(4,42,'4:42','Vào Ngày (Phán xử) đó những ai không có đức tin và bất tuân Sứ Giả (của Allah) sẽ ao ước cho mặt đất được lấp bằng bên trên họ bởi vì họ sẽ không thể giấu giếm khỏi Allah một câu chuyện (gian dối) nào.');
INSERT INTO "hasan_abdulkarim" VALUES(4,43,'4:43','Hỡi những ai có đức tin! Chớ đến gần việc lễ nguyện Salah khi các ngươi bị choáng váng (say rượu) trừ phi (tỉnh táo trở lại và) biết điều các ngươi đang nói ra; cũng chớ (dâng lễ) trong tình trạng dơ bẩn ngoại trừ trường hợp các ngươi đang trên đường đi xa (ngang qua một thánh đường), trừ phi các ngươi tắm rửa sạch sẽ; và nếu các ngươi bị bệnh hoặc đang trên đường đi xa hoặc ai trong các ngươi từ nhà vệ sinh đi ra hoặc các ngươi đã chung đụng (ăn nằm) với vợ nhưng không tìm ra nước (để hoàn tất nghi thức tẩy sạch) thì hãy đến chỗ đất (hay cát) sạch, dùng nó lau mặt và hai tay của các ngươi (theo thủ tục tẩy sạch ''Tayammum'') bởi vì quả thật Allah Độ- lượng tha thứ (cho các ngươi).');
INSERT INTO "hasan_abdulkarim" VALUES(4,44,'4:44','Há Ngươi (Muhammad!) không lưu ý đến những kẻ đã được ban cho một phần của Kinh Sách, chúng đã mua lấy sự lầm lạc và muốn các ngươi lạc đạo (như chúng)?');
INSERT INTO "hasan_abdulkarim" VALUES(4,45,'4:45','Nhưng Allah biết rõ kẻ thù của các ngươi và Allah đủ sức làm một Vị Che chở và Allah đủ sức làm một Đấng Giúp đỡ (các ngươi chống lại chúng).');
INSERT INTO "hasan_abdulkarim" VALUES(4,46,'4:46','Trong số những ai là người Do thái, có một số đã cố ý làm sai lệch ý nghĩa của những lời nói. Chúng nói: “Same''na wa ''asayna" - "Chúng tôi nghe nhưng không vâng lời", và nói: “Isma'' ghayra musma''in” - "Hãy nghe nhưng nghe khác đi" và nói: "Ra''ina" bằng chiếc lưỡi của chúng để nhạo báng tôn giáo (Islam); nhưng nếu chúng nói: “Sami''na wa ata''na" và “Isma wa Unzurna" thì tốt cho chúng hơn và thẳng thắn hơn. Nhưng Allah nguyền rủa chúng vì tội xúc phạm của chúng. Bởi thế, chỉ một số ít bọn chúng tin tưởng mà thôi.');
INSERT INTO "hasan_abdulkarim" VALUES(4,47,'4:47','Hỡi những ai đã được ban cho Kinh Sách! Hãy tin tưởng nơi điều (mặc khải) mà TA đã ban xuống (cho Muhammad) xác nhận lại điều (mặc khải) đang nằm (trong tay của) các ngươi trước khi TA thay đổi bộ mặt của các ngươi và quay chúng (bộ mặt) ra sau lưng và nguyền rủa chúng giống như việc TA đã nguyền rủa những kẻ đã vi phạm (giới cấm của) Ngày Thứ bảy bởi vì Mệnh lệnh của Allah phải được thi hành.');
INSERT INTO "hasan_abdulkarim" VALUES(4,48,'4:48','Quả thật, Allah không tha thứ tội tổ hợp những thần linh cùng với Ngài nhưng Ngài tha thứ (tội) nào khác với (tội) đó cho người nào Ngài muốn; và ai tổ hợp những thần linh với Allah thì chắc chắn đã tự chuốc lấy một tội rất lớn.');
INSERT INTO "hasan_abdulkarim" VALUES(4,49,'4:49','Há Ngươi (Muhammad!) không lưu ý đến những kẻ đã tự cho mình trong sạch hơn người hay sao? Không, chỉ Allah mới tẩy sạch được người nào Ngài muốn và chúng sẽ không bị xử lý bất công một tí nào.');
INSERT INTO "hasan_abdulkarim" VALUES(4,50,'4:50','Hãy xem! Chúng đã đặt điều nói dối rồi đổ thừa cho Allah như thế nào! Bởi thế, việc đó đủ là một tội công khai rồi.');
INSERT INTO "hasan_abdulkarim" VALUES(4,51,'4:51','Há Ngươi (Sứ Giả!) không lưu ý đến những kẻ đã được ban cho một phần Kinh Sách? Chúng tin tưởng nơi pháp thuật và Tà thần (Taghut) và chúng nói với những kẻ không có đức tin rằng họ là những người được hướng dẫn đúng đường hơn là những người (Muslim) có đức tin.');
INSERT INTO "hasan_abdulkarim" VALUES(4,52,'4:52','Họ là những kẻ đã bị Allah nguyền rủa; và ai đã bị Allah nguyền rủa thì Ngươi (Muhammad) sẽ không bao giờ tìm được cho y một người giúp đỡ nào.');
INSERT INTO "hasan_abdulkarim" VALUES(4,53,'4:53','Phải chăng chúng có một phần vương quyền? Nếu quả thật như thế thì chúng sẽ không bao giờ cho người ta một tí nào cả.');
INSERT INTO "hasan_abdulkarim" VALUES(4,54,'4:54','Hoặc phải chăng chúng ganh tị với người ta về Đặc ân mà Allah đã ban cho họ? Nhưng chắc chắn TA đã ban cho dòng dõi của Ibrahim Kinh Sách và Lẽ đúng đắn khôn ngoan và ban cho họ một vương quốc rộng lớn.');
INSERT INTO "hasan_abdulkarim" VALUES(4,55,'4:55','Nhưng trong chúng (người Do thái và tín đồ Thiên Chúa giáo), có người tin tưởng nơi Y (Muhammad) và có người quay mặt lánh xa Y; và hỏa ngục có đủ lửa ngọn (để đốt chúng).');
INSERT INTO "hasan_abdulkarim" VALUES(4,56,'4:56','Quả thật, những ai phủ nhận các Lời Mặc Khải của TA sẽ sớm bị TA ném vào Lửa (của hỏa ngục); mỗi lần lớp da của chúng bị nướng chín, TA cho thay lớp da mới để chúng tiếp tục nếm hình phạt (của Lửa). Quả thật, Allah Toàn Năng, Rất Mực Sáng Suốt.');
INSERT INTO "hasan_abdulkarim" VALUES(4,57,'4:57','Ngược lại, những ai có đức tin và làm việc thiện thì sẽ sớm được TA cho vào những Ngôi vườn (thiên đàng) bên dưới có các dòng sông chảy, họ sẽ sống trong đó đờ đời; trong đó họ sẽ có những người bạn đời trong sạch; và TA sẽ đưa họ đến nghỉ ngơi dưới tàn cây mát rượi.');
INSERT INTO "hasan_abdulkarim" VALUES(4,58,'4:58','Quả thật, Allah ra lệnh cho các ngươi phải giao hoàn tín vật (amanah) về lại cho chủ nhân của nó và khi các ngươi xét xử người ta, hãy xét xử với sự công bằng. Quả thật, tuyệt hảo thay điều mà Allah dùng để khuyên dạy các ngươi. Quả thật, Allah là Đấng Hằng Nghe và là Đấng Hằng Thấy (mọi việc).');
INSERT INTO "hasan_abdulkarim" VALUES(4,59,'4:59','Hỡi những ai có đức tin! Hãy tuân mệnh Allah và tuân lệnh Sứ Giả (của Allah) và (tuân theo) những (viên chức) có thẩm quyền trong các ngươi. Nhưng nếu các ngươi bất đồng ý kiến về điều gì, hãy quay về tham khảo Allah (qua Qur''an) và Sứ Giả (của Allah - qua Sunnah của Y) nếu các ngươi tin tưởng nơi Allah và nơi Ngày (Phán xử) Cuối Cùng. Đó là lối giải thích tốt nhất và đúng đắn nhất.');
INSERT INTO "hasan_abdulkarim" VALUES(4,60,'4:60','Há Ngươi (Muhammad!) không lưu ý đến những kẻ đã xác nhận việc chúng tin nơi điều (mặc khải) đã được ban xuống cho Ngươi và nơi điều đã được ban xuống (vào thời) trước Ngươi hay sao? Chúng muốn nhờ Tà thần (Taghut) xét xử công việc của chúng trong lúc chúng được lệnh phải tẩy chay (Tà thần). Và Shaytan muốn dắt chúng lạc đạo rất xa.');
INSERT INTO "hasan_abdulkarim" VALUES(4,61,'4:61','Và khi có lời nhắc chúng: “Hãy đến (nghe) điều Mặc khải do Allah ban xuống và đến gặp Sứ Giả (của Allah), Ngươi thấy những tên giả tạo đức tin quay mặt lánh xa Ngươi, dang ra thật xa.');
INSERT INTO "hasan_abdulkarim" VALUES(4,62,'4:62','Thế thì sự việc sẽ ra sao khi chúng gặp phải tai họa do hậu quả của những việc (tội lỗi) mà bàn tay của chúng đã gởi đi trước cho chúng? Rồi chúng đến gặp Ngươi thề thốt nhân danh Allah nói: “Quả thật, chúng tôi chỉ muốn điều lành và sự hòa thuận mà thôi.”');
INSERT INTO "hasan_abdulkarim" VALUES(4,63,'4:63','Chúng là những kẻ mà Allah biết rõ những điều (thối nát) nằm trong lòng của chúng. Bởi thế, hãy lánh xa chúng, nhưng hãy cảnh cáo chúng và nói với chúng lời lẽ thấm thía vào tim gan của chúng.');
INSERT INTO "hasan_abdulkarim" VALUES(4,64,'4:64','Và TA cử một Sứ Giả đến chỉ là để cho (dân chúng) tuân theo với sự chấp thuận của Allah. Nhưng nếu trường hợp chúng làm hại tâm hồn của chúng rồi đến gặp Ngươi để nhờ Ngươi xin Allah tha thứ và Sứ Giả (của Allah) xin Allah tha thứ cho chúng thì chúng sẽ thấy Allah Hằng Quay lại Tha thứ, Rất Mực Khoan Dung (đối với bầy tôi của Ngài).');
INSERT INTO "hasan_abdulkarim" VALUES(4,65,'4:65','Nhưng không, nhân danh Thượng Đế của Ngươi, chúng chỉ thật lòng tin tưởng khi nào chúng đến yêu cầu Ngươi đứng ra phân xử về điều chúng đang tranh chấp rồi chúng cảm thấy không uất ức trong lòng về quyết định mà Ngươi đã đưa ra và chúng hoàn toàn qui phục.');
INSERT INTO "hasan_abdulkarim" VALUES(4,66,'4:66','Và nếu TA ra lệnh cho chúng “Hãy hy sinh tính mạng hoặc bỏ nhà cửa ra đi (chinh chiến)” thì chỉ một số ít trong bọn chúng làm theo; và nếu chúng làm theo điều yêu cầu thì chắc chắn điều đó tốt và vững chắc hơn (về đức tin) cho chúng.');
INSERT INTO "hasan_abdulkarim" VALUES(4,67,'4:67','Và trong trường hợp đó TA sẽ tự tay ban cho chúng một phần thưởng rất lớn.');
INSERT INTO "hasan_abdulkarim" VALUES(4,68,'4:68','Và chắc chắn TA sẽ hướng dẫn chúng theo đúng Chính Đạo.');
INSERT INTO "hasan_abdulkarim" VALUES(4,69,'4:69','Và ai vâng lệnh Allah và Sứ Giả (của Allah) thì sẽ là cùng hội cùng thuyền với những người đã được Allah ban cho Ân huệ trong hàng ngũ của các Nabi và của những người yêu chuộng sự Thật, của những anh hùng tử đạo và của những người ngoan đạo. Thật tuyệt hảo thay hội đoàn gồm những người đó!');
INSERT INTO "hasan_abdulkarim" VALUES(4,70,'4:70','Đó là Thiên ân của Allah và Allah biết đủ hết mọi việc.');
INSERT INTO "hasan_abdulkarim" VALUES(4,71,'4:71','Hỡi những ai có đức tin! Hãy đề cao cảnh giác và chuẩn bị cẩn thận khi các ngươi xuất quân ra trận từng nhóm hay toàn đội.');
INSERT INTO "hasan_abdulkarim" VALUES(4,72,'4:72','Và quả thật, trong các ngươi, có người chậm trễ đằng sau (khi xuất quân). Bởi thế, nếu chẳng may các ngươi gặp hoạn nạn thì hắn nói ngay: “Chắc chắn Allah đã đặc ân cho tôi khi tôi không có mặt với họ.”');
INSERT INTO "hasan_abdulkarim" VALUES(4,73,'4:73','Ngược lại, nếu các ngươi gặp điều may mắn do Allah ban cấp thì chắc chắn hắn sẽ nói ra điều làm như giữa các ngươi và hắn chẳng có một chút quan hệ tình cảm nào cả. Hắn nói: “Thật đáng tiếc! Phải chi tôi có mặt với họ thì tôi sẽ chiến thắng vẻ vang.”');
INSERT INTO "hasan_abdulkarim" VALUES(4,74,'4:74','Bởi thế, hãy để cho những ai bán đời sống trần tục này đổi lấy đời sau, chiến đấu cho Chính Nghĩa của Allah. Và ai chiến đấu cho Chính Nghĩa của Allah, dù bị giết hay chiến thắng, thì sẽ sớm được TA ban cho một phần thưởng rất lớn.');
INSERT INTO "hasan_abdulkarim" VALUES(4,75,'4:75','Và điều gì cản các ngươi không đi chiến đấu cho Chính Nghĩa của Allah để bênh vực cho những người đàn ông, đàn bà và trẻ thơ cô thế bị áp bức, cho những người đã cầu nguyện, thưa: “Lạy Thượng Đế của bầy tôi! Xin Ngài cứu bầy tôi ra khỏi thị trấn này, dân cư của nó là những kẻ áp bức bất công và xin Ngài dựng lên một Người bảo hộ để bảo vệ và một Vị cứu tinh để giúp đỡ bầy tôi!”');
INSERT INTO "hasan_abdulkarim" VALUES(4,76,'4:76','Những ai có đức tin thì chiến đấu cho Chính Nghĩa của Allah; ngược lại, những ai phủ nhận đức tin thì chiến đấu cho Tà thần (Taghut); bởi thế, hãy chiến đấu chống bạn bè của Shaytan bởi vì quả thật mưu kế của Shaytan yếu kém.');
INSERT INTO "hasan_abdulkarim" VALUES(4,77,'4:77','Há Ngươi không lưu ý đến những kẻ đã được bảo hãy ngưng tay chiến đấu và dâng lễ nguyện Salah một cách chu đáo và đóng Zakah! Nhưng sau đó khi lệnh chiến đấu được truyền xuống cho chúng thì này! Một thành phần của bọn chúng sợ người ta như sợ Allah hoặc sợ dữ hơn nữa. Chúng than: “Lạy Thượng Đế của bầy tôi! Tại sao Ngài bắt bầy tôi đi chiến đấu? Sao Ngài không buông tha cho bầy tôi sống đến lúc mãn hạn sắp tới?” Hãy bảo chúng: “Đời sống trần gian này ngắn ngủi trong lúc đời sống Ngày sau tốt hơn cho người ngay chính sợ Allah và các ngươi sẽ không bị đối xử bất công một tí nào.');
INSERT INTO "hasan_abdulkarim" VALUES(4,78,'4:78','“Bất cứ nơi nào các ngươi ở, cái chết sẽ đến bắt các ngươi dù có ở trong các thành lũy kiên cố đi nữa!” Và khi gặp một điều lành, chúng nói: “Điều (lành) này do Allah ban cho.” Ngược lại, khi gặp điều chẳng lành, chúng nói: “Điều (dữ) này do Ngươi (Muhammad) gây ra.” Hãy bảo chúng: “Mọi chuyện đều do Allah (quyết định) cả,” thế đám người này gặp điều gì vậy? Hầu như chúng không hiểu bất cứ câu chuyện gì!');
INSERT INTO "hasan_abdulkarim" VALUES(4,79,'4:79','(Này hỡi người!) Nếu ngươi gặp điều lành thì đó là do Allah ban cho. Ngược lại, nếu ngươi gặp điều dữ thì đó là tự mình làm ra. Và TA chỉ cử Ngươi (Muhammad!) làm một Sứ Giả (để mang Thông điệp) đến cho nhân loại. Và Allah đủ làm một Nhân chứng.');
INSERT INTO "hasan_abdulkarim" VALUES(4,80,'4:80','Ai vâng lời Sứ Giả (của Allah) là tuân mệnh Allah. Và ai (bất tuân) quay bỏ đi thì TA không phái Ngươi (Muhammad) đến để canh gác chúng.');
INSERT INTO "hasan_abdulkarim" VALUES(4,81,'4:81','Và chúng nói: “Vâng lời” nhưng khi đi khuất mặt Ngươi, một thành phần trong bọn chúng ban đêm bàn tính làm điều khác với điều mà Ngươi đã nói với chúng. Allah cho ghi chép điều chúng âm mưu ban đêm. Bởi thế, hãy lánh xa chúng và hãy phó thác cho Allah và Allah đủ làm một Đấng Thọ Lãnh (sắp đặt công việc được giao phó).');
INSERT INTO "hasan_abdulkarim" VALUES(4,82,'4:82','Tại sao chúng không chịu nghiền ngẫm về Qur’an. Nếu là từ một Đấng nào khác Allah thì chắc chắn chúng sẽ tìm thấy trong đó nhiều mâu thuẫn.');
INSERT INTO "hasan_abdulkarim" VALUES(4,83,'4:83','Và khi thấy có tin tức liên hệ đến nền an ninh công cộng hoặc có điều gì sợ sệt thì chúng phổ biến nó ra ngoài. Nếu chúng báo cáo nó lên Sứ Giả (của Allah) hoặc lên giới chức có thẩm quyền thì những người có khả năng nghiên cứu đều biết tin tức đó từ đâu đến. Và nếu Allah không nhân từ và khoan dung thì tất cả các ngươi ngoại trừ một số ít đã theo Shaytan rồi.');
INSERT INTO "hasan_abdulkarim" VALUES(4,84,'4:84','Bởi thế, hãy chiến đấu cho Chính Nghĩa của Allah. Ngươi chỉ chịu trách nhiệm cho bản thân của Ngươi. Nhưng hãy động viên tinh thần của những người có đức tin. Có lẽ Allah sẽ chặn đứng sức mạnh của những kẻ ngoại đạo bởi vì Allah mạnh hơn tất cả về sức lực và về sự trừng phạt.');
INSERT INTO "hasan_abdulkarim" VALUES(4,85,'4:85','Ai can thiệp bằng một sự can thiệp tốt thì sẽ có phần trong đó; ngược lại ai can thiệp bằng một sự can thiệp tội lỗi thì sẽ chịu trách nhiệm trong đó. Bởi vì Allah là Đấng Giám Sát tất cả mọi việc.');
INSERT INTO "hasan_abdulkarim" VALUES(4,86,'4:86','Và khi các ngươi được chào hỏi lịch thiệp, hãy đáp lại bằng lời chào tốt hơn hoặc ít ra cũng với lời chào tương tự. Bởi vì quả thật, Allah là Đấng tính sổ tất cả các vấn đề.');
INSERT INTO "hasan_abdulkarim" VALUES(4,87,'4:87','Allah! Không có Thượng Đế nào khác duy chỉ Ngài (là Thượng Đế). Ngài sẽ tập trung các ngươi trở lại vào Ngày phục sinh mà không có gì phải ngờ vực cả. Và ai thật hơn Allah về Lời tuyên bố?');
INSERT INTO "hasan_abdulkarim" VALUES(4,88,'4:88','Tại sao các ngươi chia rẽ nhau thành hai nhóm chỉ vì (vấn đề của) những tên giả tạo đức tin trong lúc Allah lật đổ chúng vì những điều (gian ác) mà chúng đã gây ra? Phải chăng các ngươi muốn hướng dẫn những kẻ mà Allah đánh lạc hướng hay sao? Và ai mà Allah đánh lạc hướng thì Ngươi sẽ không bao giờ tìm được cho y một con đường (ngay chính).');
INSERT INTO "hasan_abdulkarim" VALUES(4,89,'4:89','Chúng chỉ mong cho các ngươi phủ nhận đức tin giống như việc chúng phủ nhận đức tin để cho đôi bên bằng nhau. Bởi thế, chớ nhận ai trong bọn chúng làm người đỡ đầu ngoại trừ trường hợp vì Chính Nghĩa của Allah mà chúng di cư (đến Madinah với các ngươi). Nhưng nếu chúng bội giáo, hãy bắt và giết chúng nơi nào các ngươi tìm ra chúng. Và chớ nhận ai trong bọn chúng làm người đỡ đầu hay giúp đỡ.');
INSERT INTO "hasan_abdulkarim" VALUES(4,90,'4:90','Ngọai trừ những ai gia nhập nhóm (đồng minh) mà các ngươi đã ký một hiệp ước (hoà bình) với họ hoặc những ai đến gặp các ngươi với thiện tâm muốn cầu hoà hoặc không chiến tranh với người dân ngoại đạo của chúng. Và nếu Allah muốn, Ngài sẽ thừa sức làm cho chúng có ưu thế hơn các ngươi. Nhưng nếu chúng rút lui xa các ngươi, không đánh các ngườơi và đề nghị hoà bình với các ngươi thì Allah sẽ không cho các người lý do nào để (chiến tranh) chống lại chúng.');
INSERT INTO "hasan_abdulkarim" VALUES(4,91,'4:91','Các ngươi sẽ thấy có những người khác (bọn chúng) muốn được sống yên ổn với các ngươi và với người dân ngoại đạo của họ nữa. Nhưng mỗi lần bị lôi cuốn vào vòng thù nghịch, họ liền bội ước. Bởi thế, nếu họ không chịu rút lui xa các ngươi, không đưa ra đề nghị hòa bình cũng không chịu ngưng chiến thì hãy bắt và giết họ nơi nào các ngươi tìm ra họ. Trường hợp của những người này, TA ban cho các ngươi thẩm quyền trừng phạt họ.');
INSERT INTO "hasan_abdulkarim" VALUES(4,92,'4:92','Một người có đức tin không được giết hại một người có đức tin khác trừ phi vì lầm lỗi. Và người nào vì lỗi lầm đã giết một người có đức tin thì phải phóng thích một người nô lệ có đức tin và bồi thường nhân mạng cho gia đình người chết trừ phi vì nhân đạo họ từ chối nhận tiền bồi thường đó. Nhưng nếu người chết thuộc đám dân đang thù nghịch với các ngươi và y là một người có đức tin thì việc phóng thích một người nô lệ có đức tin là đủ. Nhưng nếu nạn nhân là người thuộc đám dân mà các ngươi đã ký một hiệp ước (hòa bình) với họ thì phải trả cho gia đình của nạn nhân tiền bồi thường nhân mạng cùng với việc phóng thích một người nô lệ có đức tin. Đối với phạm nhân nào không có phương tiện để bồi thường thì phải nhịn chay hai tháng liên tục như là một phương cách tạ tội do Allah truyền xuống bởi vì Allah Hằng Biết, Rất Mực Sáng Suốt.');
INSERT INTO "hasan_abdulkarim" VALUES(4,93,'4:93','Và ai cố tình giết một người có đức tin thì phần thưởng cho y là hỏa ngục nơi mà hắn sẽ vào ở đời đời trong đó. Và Allah sẽ giận dữ và nguyền rủa hắn và chuẩn bị cho hắn một sự trừng phạt khủng khiếp.');
INSERT INTO "hasan_abdulkarim" VALUES(4,94,'4:94','Hỡi những ai có đức tin! Khi các ngươi ra đi chiến đấu cho Chính Nghĩa của Allah, hãy cân nhắc sự việc cho kỹ và chớ đáp lại bất cứ ai chào các ngươi bằng lời này: “Anh không là một người có đức tin”; chỉ mong tìm lợi lộc của đời sống trần gian này. Ngược lại, Allah có vô số chiến lợi phẩm nơi Ngài cho các ngươi. (Nên nhớ rằng) trước đây các ngươi lâm vào hoàn cảnh đúng như thế, rồi Allah đã đặc ân cho các ngươi; bởi thế, hãy cân nhắc cho kỹ (trước khi ra quân) bởi vì Allah Hằng Am Tường mọi việc các người làm.');
INSERT INTO "hasan_abdulkarim" VALUES(4,95,'4:95','Trong số những người có đức tin, ngoại trừ các phế binh, những người ở lại nhà không ngang bằng với những người ra đi chiến đấu cho Chính Nghĩa của Allah vì họ hy sinh cả tài sản lẫn sinh mạng của họ. Allah đặc ân cấp bậc cao cho những người hy sinh tài sản lẫn sinh mạng của họ để chiến đấu (cho Allah) hơn những người ở lại nhà dẫu rằng Allah hứa ban điều tốt cho tất cả (những ai có đức tin). Và Allah ban đặc ân cho những ai tòng quân chiến đấu hơn những người ở lại nhà bằng một phần thưởng rất lớn.');
INSERT INTO "hasan_abdulkarim" VALUES(4,96,'4:96','(Họ sẽ được ban cho ) cấp bậc cao từ nơi Ngài cùng với sự Tha thứ và sự Khoan Dung (của Ngài) bởi Allah là Đấng Hằng Tha Thứ, Rất Mực Khoan Dung.');
INSERT INTO "hasan_abdulkarim" VALUES(4,97,'4:97','Quả thật, những ai mà Thiên Thần bắt hồn cho chết trong lúc họ tự làm hại bản thân mình thì (Thiên Thần) sẽ bảo: “Đâu là điều kiện của các ngươi?” Họ đáp: “Chúng tôi cô thế và bị áp bức trên mặt đất.” (Thiên Thần) bảo: “Thế đất đai của Allah không đủ rộng để cho các ngươi di cư (đi lánh nạn) hay sao?” Đối với những người như thế, nhà ở của họ là hỏa ngục, một nơi đến cuối cùng rất xấu xa!');
INSERT INTO "hasan_abdulkarim" VALUES(4,98,'4:98','Ngọai trừ đàn ông, đàn bà và trẻ thơ thật sự yếu đuối và cô thế không có phương tiện (để di cư) cũng không được ai dẫn đường đi lánh nạn.');
INSERT INTO "hasan_abdulkarim" VALUES(4,99,'4:99','Đối với những người này, may ra Allah sẽ tha thứ cho họ bởi vì Allah Hằng Lượng Thứ, Rất Mực Khoan Dung.');
INSERT INTO "hasan_abdulkarim" VALUES(4,100,'4:100','Và ai di cư vì Chính Nghĩa của Allah thì sẽ tìm thấy trên trái đất có nhiều chỗ tị nạn, bao la. Và ai vì Allah và Sứ Giả của Ngài mà bỏ nhà cửa ra đi tị nạn rồi chết nửa đường thì chắc chắn Allah sẽ chịu trách nhiệm về việc tưởng thưởng họ bởi vì Allah Hằng Tha Thứ, Rất Mực Khoan Dung.');
INSERT INTO "hasan_abdulkarim" VALUES(4,101,'4:101','Và khi di chuyển xa trên trái đất, các ngươi không mắc tội nếu rút ngắn việc dâng lễ Salah vì sợ những kẻ không có đức tin có thể tấn công các ngươi và quả thật những kẻ không có đức tin là kẻ thù công khai của các ngươi.');
INSERT INTO "hasan_abdulkarim" VALUES(4,102,'4:102','Và khi Ngươi (Muhammad) ở cùng với họ (Muslim), Ngươi hãy đứng chủ lễ để hướng dẫn họ, và để cho một thành phần của họ cùng đứng dâng lễ với Ngươi mang theo vũ khí của họ. Bởi thế, khi họ đã lễ xong, để họ rút lui về đằng sau (giữ an ninh) và để cho số người còn lại chưa lễ bước đến cùng dâng lễ với Ngươi; và dặn họ nên đề phòng và mang theo vũ khí cùng với họ. Những kẻ ngoại đạo chỉ mong cho các ngươi lơ là về vũ khí và quân trang hầu thừa cơ đánh úp các ngươi một trận. Các ngươi không có tội nếu các ngươi để vũ khí sang một bên (để đi dâng lễ) vì nước mưa làm cho bất tiện hay vì đau ốm. Tuy nhiên, nên đề phòng cẩn thận. Quả thật, Allah đã chuẩn bị cho những kẻ không có đức tin một sự trừng phạt nhục nhã.');
INSERT INTO "hasan_abdulkarim" VALUES(4,103,'4:103','Và khi các ngươi đã hoàn tất cuộc lễ Salah, hãy tưởng nhớ Allah (ở các tư thế) đứng, ngồi và nằm nghiêng một bên. Bởi thế, khi các ngươi được an toàn (không còn sợ nguy hiểm nữa) hãy lễ nguyện bình thường trở lại bởi vì lễ nguyện Salah được truyền xuống cho những người có đức tin (phải thi hành) vào những giờ giấc qui định.');
INSERT INTO "hasan_abdulkarim" VALUES(4,104,'4:104','Và chớ trì trệ trong việc đuổi bắt địch. Nếu các ngươi đau đớn thì chúng cũng bị đau đớn như các ngươi. Nhưng các ngươi đặt hy vọng nơi Allah điều mà chúng không có niềm hy vọng nào. Và Allah Hằng Biết, Rất Mực Sáng Suốt.');
INSERT INTO "hasan_abdulkarim" VALUES(4,105,'4:105','Quả thật, TA (Allah) đã ban cho Ngươi (Muhammad) Kinh Sách (Qur''an) bằng sự thật để Ngươi dùng xét xử giữa nhân loại về điều mà Allah đã chỉ cho Ngươi thấy. Và chớ bênh vực cho những kẻ bội phản.');
INSERT INTO "hasan_abdulkarim" VALUES(4,106,'4:106','Và Ngươi hãy cầu xin Allah tha thứ. Quả thật, Allah Hằng Tha Thứ, Rất Mực Khoan Dung.');
INSERT INTO "hasan_abdulkarim" VALUES(4,107,'4:107','Và chớ bênh vực thay cho những kẻ đã phản bội bản thân (linh hồn) mình bởi vì quả thật Allah không yêu thương những kẻ phản bội, tội lỗi.');
INSERT INTO "hasan_abdulkarim" VALUES(4,108,'4:108','Chúng tìm cách trốn khỏi thiên hạ nhưng chúng không thể trốn thoát khỏi Allah đặng bỏi vì Ngài có mặt với chúng khi chúng mưu định kế hoạch ban đêm bằng những lời nói mà Ngài không thể chấp nhận. Và Allah là Đấng Tóm Thâu tất cả mọi điều chúng làm.');
INSERT INTO "hasan_abdulkarim" VALUES(4,109,'4:109','À này! Đây là những kẻ mà các ngươi có thể bênh vực giùm cho ở đời này nhưng ai có thể bênh vực giùm cho chúng trước Allah vào Ngày phục sinh và ai là kẻ sẽ thụ lãnh việc làm (tội lỗi) của chúng?');
INSERT INTO "hasan_abdulkarim" VALUES(4,110,'4:110','Và ai làm điều tội lỗi hoặc làm hại bản thân mình rồi cầu xin Allah tha thứ thì sẽ thấy Allah Hằng Tha Thứ, Rất Mực Khoan Dung.');
INSERT INTO "hasan_abdulkarim" VALUES(4,111,'4:111','Và ai phạm tội thì chỉ bản thân y lãnh tội. Bởi vì Allah Hằng Biết, Rất Mực Sáng Suốt.');
INSERT INTO "hasan_abdulkarim" VALUES(4,112,'4:112','Và ai phạm lỗi hoặc phạm tội rồi đổ thừa cho một người vô tội thì chắc chắn sẽ mang vào mình tội vu cáo và tội lỗi hiển nhiên.');
INSERT INTO "hasan_abdulkarim" VALUES(4,113,'4:113','Và nếu Allah không đặc ân và khoan dung cho Ngươi (hỡi Sứ Giả!) thì chắc chắn một nhóm của bọn chúng đã quyết định làm cho Ngươi lạc hướng nhưng chúng tự làm cho mình lạc hướng mà thôi. Và chúng không thể hại được Ngươi một tí nào cả bởi vì Allah đã ban cho Ngươi Kinh Sách và lẽ sáng suốt khôn ngoan và dạy Ngươi điều mà Ngươi đã không từng biết trước đây. Và Đặc ân Allah ban cho Ngươi thật là vĩ đại.');
INSERT INTO "hasan_abdulkarim" VALUES(4,114,'4:114','Đa số những cuộc mật đàm của chúng không có điều gì tốt cả trừ phi ai đó truyền lệnh thực thi việc bố thí hoặc làm điều lành hoặc hòa giải giữa nhân loại (thì cuộc mật đàm ấy được cho phép). Và ai làm các điều đó chỉ mong làm cho Allah hài lòng thì chắc chắn sẽ được TA (Allah) ban cho một phần thưởng rất lớn.');
INSERT INTO "hasan_abdulkarim" VALUES(4,115,'4:115','Và ai còn tranh cãi và chống đối Sứ Giả (của Allah) sau khi Chỉ Đạo hướng dẫn đã được trình bày rõ cho y và đi theo (con đường) khác với con đường của những người có đức tin thì TA (Allah) sẽ bỏ mặc y đi theo cái mà y đã quay về và TA sẽ nướng y trong hỏa ngục, một nơi đến cuối cùng rất xấu xa.');
INSERT INTO "hasan_abdulkarim" VALUES(4,116,'4:116','Quả thật, Allah không tha thứ việc tổ hợp (ai, vật) cùng với Ngài (Allah) nhưng Ngài tha thứ điều gì khác hơn điều đó cho người nào Ngài muốn. Và ai tổ hợp (ai, vật) cùng với Allah thì quả thật y đã lạc đạo quá xa.');
INSERT INTO "hasan_abdulkarim" VALUES(4,117,'4:117','Chúng (người ngoại đạo tại Makkah) chỉ cầu nguyện những nữ thần thay vì Ngài (Allah). Chúng không cầu nguyện ai khác ngoài tên Shaytan phản nghịch!');
INSERT INTO "hasan_abdulkarim" VALUES(4,118,'4:118','Allah nguyền rủa nó (Shaytan) bởi vì nó đã nói: “Tôi sẽ bắt đi từ bầy tôi của Ngài một phần qui định;');
INSERT INTO "hasan_abdulkarim" VALUES(4,119,'4:119','Và tôi sẽ dắt chúng đi lạc đạo, xúi giục chúng theo đuổi những thèm muốn sai lầm và ra lệnh cho chúng; bởi thế, chúng cắt tai gia súc; và tôi sẽ truyền lệnh cho chúng, bởi thế chúng thay đổi sự tạo hóa của Allah.” Và ai nhận Shaytan làm kẻ đỡ đầu thay vì Allah thì chắc chắn sẽ hoàn toàn thua thiệt công khai.');
INSERT INTO "hasan_abdulkarim" VALUES(4,120,'4:120','Nó (Shaytan) hứa với chúng và xúi giục chúng thèm muốn điều sai trái và Shaytan chỉ hứa với chúng điều gian dối.');
INSERT INTO "hasan_abdulkarim" VALUES(4,121,'4:121','Chúng là những kẻ mà nhà ở sẽ là hỏa ngục và sẽ không tìm được một lối thoát nào để thoát ra khỏi đó.');
INSERT INTO "hasan_abdulkarim" VALUES(4,122,'4:122','Ngược lại, những ai có đức tin và làm việc thiện tốt, TA (Allah) sẽ sớm chấp nhận họ vào các Ngôi vườn (thiên đàng) bên dưới có các dòng sông chảy, họ sẽ sống trong đó đời đời bởi vì Lời hứa của Allah là sự Thật. Và ai thật hơn Allah trong Lời hứa?!');
INSERT INTO "hasan_abdulkarim" VALUES(4,123,'4:123','Không phải theo ước muốn của các ngươi cũng không phải theo mong muốn của người dân Kinh Sách (mà lời hứa của Allah sẽ thể hiện); ai làm nên tội thì sẽ lãnh đủ về tội đó; và ngoài Allah ra, y sẽ không tìm được một người đỡ đầu hay một vị cứu tinh nào giúp đỡ y được cả.');
INSERT INTO "hasan_abdulkarim" VALUES(4,124,'4:124','Và bất cứ ai làm điều thiện tốt, dù là nam hay nữ, và là một người có đức tin thì sẽ vào thiên đàng và sẽ không bị đối xử bất công một tí nào.');
INSERT INTO "hasan_abdulkarim" VALUES(4,125,'4:125','về tôn giáo, ai tốt hơn người dâng trọn bản thân mình cho Allah đồng thời là một người làm tốt và theo tín ngưỡng của Ibrahim thuần túy (chỉ thờ phượng một mình Allah)? Và Allah nhận Ibrahim làm một nô lệ cận kề.');
INSERT INTO "hasan_abdulkarim" VALUES(4,126,'4:126','Nhưng mọi vật trong các tầng trời và mọi vật dưới đất đều là (tạo vật) cua Allah cả. Và Allah là Đấng Tóm Thâu hết tất cả mọi vật.');
INSERT INTO "hasan_abdulkarim" VALUES(4,127,'4:127','Họ hỏi Ngươi (Muhammad) về phụ nữ. Hãy bảo (họ): “Allah đã nghị định cho các ngươi về họ (phụ nữ) và các điều lệnh đã được đọc ra cho các ngươi trong Kinh Sách liên quan đến các phụ nữ mồ côi mà các ngươi không chịu tặng phần (tiền cưới) bắt buộc đã được qui định cho họ nhưng lại muốn kết hôn với họ; và về các đứa bé cô thế (bị áp bức) thì các ngươi vì lẽ công bằng phải đứng ra bênh vực các đứa bé mồ côi; và bất cứ việc tốt nào các ngươi làm Allah đều biết rõ cả.');
INSERT INTO "hasan_abdulkarim" VALUES(4,128,'4:128','Và nếu người vợ sợ người chồng đối xử tàn tệ hoặc bỏ rơi thì hai đằng không có tội nếu chịu hòa giải với nhau; và hoà giải luôn luôn là (một giải pháp) tốt. Nhưng lòng tham đã có sẵn nơi bản thân của mỗi người. Tuy nhiên, nếu các ngươi làm tốt và sợ Allah thì quả thật Allah Rất Am Tường về những điều các ngươi đã từng làm.');
INSERT INTO "hasan_abdulkarim" VALUES(4,129,'4:129','Và không bao giờ các ngươi có thể đối xử công bằng với các bà vợ mặc dầu các ngươi rất muốn. Bởi thế, chớ hoàn toàn nghiêng hẳn (về một bà và bỏ rơi bà khác) như là phạt treo bà ta. Bởi thế, nếu các ngươi giải hòa và sợ Allah thì (các người sẽ thấy) quả thật Allah Hằng Tha Thứ, Rất Mực Khoan Dung.');
INSERT INTO "hasan_abdulkarim" VALUES(4,130,'4:130','Và nếu hai vợ chồng nhất định thôi nhau thì Allah sẽ làm cho đôi bên giàu có với thiên lộc của Ngài. Và Allah Bao La, Rất Mực Sáng Suốt.');
INSERT INTO "hasan_abdulkarim" VALUES(4,131,'4:131','Và mọi vật trong các tầng trời và mọi vật dưới đất đều là của Allah cả. Và quả thật TA (Allah) đã ra lệnh cho những ai đã được ban cho Kinh Sách trước các ngươi và cả các ngươi nữa (hỡi Muslim!). Các ngươi hãy sợ Allah (trong mọi giao dịch). Nhưng nếu các ngươi phủ nhận thì quả thật mọi vật trong các tầng trời và mọi vật dưới đất đều là của Allah cả. Và Allah Rất Mực Giàu Có, Rất Đáng Ca Tụng.');
INSERT INTO "hasan_abdulkarim" VALUES(4,132,'4:132','Và mọi vật trong các tầng trời và mọi vật dưới đất đều là của Allah cả. Và chỉ một mình Allah đã đủ chi phối tất cả mọi sự việc.');
INSERT INTO "hasan_abdulkarim" VALUES(4,133,'4:133','Hỡi nhân loại! Nếu (Allah) muốn, Ngài thừa sức tiêu diệt các ngươi và mang một loại (tạo vật) khác đến thay thế các ngươi. Bởi vì Allah là Đấng Toàn Năng trên sự việc đó.');
INSERT INTO "hasan_abdulkarim" VALUES(4,134,'4:134','Ai muốn phần thưởng của đời này thì ở nơi Allah có cả phần thưởng của đời này lẫn đời sau. Và Allah là Đấng Hằng Nghe, Đấng Hằng Thấy.');
INSERT INTO "hasan_abdulkarim" VALUES(4,135,'4:135','Hỡi những ai có đức tin! Hãy hiên ngang bênh vực nền công lý như là nhân chứng cho Allah dẫu điều đó có nghịch với bản thân của các ngươi, nghịch với cha mẹ và bà con của các ngươi; và dẫu cho y giàu hay nghèo thì Allah vẫn kế cận hai người đó (hơn các ngươi). Bởi thế, chớ làm theo điều ham muốn của các ngươi sợ rằng các ngươi có thể lệch lạc. Nếu các ngươi quay đi hoặc xa lánh (sự Thật) thì nên biết rằng Allah Rất Am Tường điều mà các ngươi đã làm.');
INSERT INTO "hasan_abdulkarim" VALUES(4,136,'4:136','Hỡi những ai có đức tin! Hãy tin tưởng nơi Allah và Sứ Giả của Ngài và Kinh Sách (Qur''an) mà Ngài đã ban xuống cho Sứ Giả của Ngài và Kinh Sách mà Ngài đã ban xuống trước đây. Và ai phủ nhận Allah, các Thiên Thần của Ngài, các Kinh Sách của Ngài, các Sứ Giả của Ngài và Ngày Phán Xử Cuối Cùng thì quả thật y đã lạc đạo quá xa.');
INSERT INTO "hasan_abdulkarim" VALUES(4,137,'4:137','Quả thật, những ai có đức tin rồi chối bỏ đức tin, rồi lại tin, sau đó lại chối bỏ đức tin và càng ngoan cố không tin thì chắc chắn Allah sẽ không tha thứ cho họ cũng như không hướng dẫn họ theo Chính Đạo (Islam).');
INSERT INTO "hasan_abdulkarim" VALUES(4,138,'4:138','Hãy báo tin cho những người giả tạo đức tin (Munafiq) về một sự trừng phạt đau đớn đang chờ đợi chúng:');
INSERT INTO "hasan_abdulkarim" VALUES(4,139,'4:139','Những ai nhận những kẻ ngoại đạo làm người bảo hộ thay vì những người có đức tin. Phải chăng họ muốn tìm quyền vinh nơi chúng? Bởi vì quả thật, mọi thứ quyền vinh đều là của Allah cả.');
INSERT INTO "hasan_abdulkarim" VALUES(4,140,'4:140','Và chắc chắn đã có lệnh truyền cho các ngươi (hỡi những người Muslim) trong Kinh Sách rằng khi các ngươi nghe các Lời Mặc Khải của Allah bị phủ nhận hay bị chế giễu thì chớ ngồi chung với bọn chúng trừ phi chúng chuyển sang câu chuyện khác bởi trong trường hợp đó, các ngươi sẽ như chúng. Quả thật, Allah sẽ tập trung tất cả những tên giả tạo đức tin và những kẻ không có đức tin vào trong hỏa ngục.');
INSERT INTO "hasan_abdulkarim" VALUES(4,141,'4:141','Những kẻ đang dòm ngó các ngươi. Bởi thế, khi Allah ban cho các ngươi một thắng lợi nào, chúng bảo (các ngươi): “Chúng tôi không về phe của quí vị hay sao?” Và nếu những kẻ không có đức tin đạt một phần (thắng lợi) nào thì chúng bảo: “Há chúng tôi không nắm ưu thế hơn quí vị và che chở quí vị khỏi tay của những người tin tưởng hay sao?” Nhưng Allah sẽ xét xử giữa các ngươi vào Ngày phục sinh (để biết ai là người chân thật và ai là kẻ gian dối). Và không bao giờ Allah mở đường cho những kẻ không có đức tin thắng những người có đức tin.');
INSERT INTO "hasan_abdulkarim" VALUES(4,142,'4:142','Quả thật, những tên giả tạo đức tin tìm cách lừa dối Allah nhưng chính Ngài đã đánh lừa chúng (vào cạm bẫy của chúng). Và khi chúng đứng dậy để dâng lễ, chúng đứng lên một cách uể oải, chúng chỉ muốn phô trương cho thiên hạ thấy, và chỉ tưởng nhớ Allah chút ít thôi.');
INSERT INTO "hasan_abdulkarim" VALUES(4,143,'4:143','Chúng do dự giữa bên này và bên nọ, không dứt khoát theo phía bên nào. Và ai mà Allah đánh lạc hướng thì Ngươi sẽ không bao giờ tìm được cho y một con đường.');
INSERT INTO "hasan_abdulkarim" VALUES(4,144,'4:144','Hỡi những ai có đức tin! Chớ nhận những kẻ không có đức tin làm người bảo hộ thay vì những người có đức tin. Phải chăng các ngươi muốn giao cho Allah một thẩm quyền để trừng phạt các ngươi hay sao?');
INSERT INTO "hasan_abdulkarim" VALUES(4,145,'4:145','Quả thật, những tên giả tạo đức tin sẽ ở tận đáy cùng của hỏa ngục và Ngươi sẽ không tìm được cho chúng một vị cứu tinh nào.');
INSERT INTO "hasan_abdulkarim" VALUES(4,146,'4:146','Ngoại trừ những ai biết ăn năn hối cải và sửa mình và bám chắc vào Allah và dâng tôn giáo của họ hoàn toàn cho Allah thì sẽ được liệt vào hàng ngũ của những người có đức tin. Và Allah sẽ sớm ban cho những người có đức tin một phần thưởng vĩ đại.');
INSERT INTO "hasan_abdulkarim" VALUES(4,147,'4:147','Allah sẽ trừng phạt các ngươi nếu các ngươi biết ơn và có đức tin bởi Allah là Đấng Hằng Ghi Công, Đấng Hằng Biết.');
INSERT INTO "hasan_abdulkarim" VALUES(4,148,'4:148','Allah không thích việc mang lời lẽ xấu xa (tội lỗi) ra la lối giữa công chúng ngoại trừ trường hợp một người bị áp bức. Và Allah là Đấng Hằng Nghe, Đấng Hằng Biết.');
INSERT INTO "hasan_abdulkarim" VALUES(4,149,'4:149','Nếu các ngươi phô trương hay giấu giếm một việc làm tốt hoặc lượng thứ cho việc xấu thì quả thật Allah là Đấng Hằng Tha Thứ, Đấng Toàn Năng.');
INSERT INTO "hasan_abdulkarim" VALUES(4,150,'4:150','Quả thật, những ai phủ nhận Allah và các vị Sứ Giả của Ngài và muốn phân cách Allah với các vị Sứ Giả của Ngài và nói: “Chúng tôi tin một số (Sứ Giả) và phủ nhận một số khác,” và muốn chọn một lối đi chính giữa cái đó.');
INSERT INTO "hasan_abdulkarim" VALUES(4,151,'4:151','Chúng là những kẻ không có đức tin nơi sự Thật và TA (Allah) đã chuẩn bị cho những kẻ không có đức tin một sự trừng phạt nhục nhã.');
INSERT INTO "hasan_abdulkarim" VALUES(4,152,'4:152','Ngược lại, những ai có đức tin nơi Allah và các Sứ Giả của Ngài và không kỳ thị phân biệt một vị (Sứ Giả) nào của (Allah) thì TA (Allah) sẽ sớm ban cho họ phần thưởng của họ. Và Allah là Đấng Hằng Tha Thứ, Rất Mực Khoan Dung.');
INSERT INTO "hasan_abdulkarim" VALUES(4,153,'4:153','Người Dân Kinh Sách yêu cầu Ngươi (Muhammad) mang từ trên trời xuống một Kinh Sách cho họ. Nhưng quả thật họ đã yêu cầu Musa đưa ra một điều (Phép lạ) lớn hơn cái đó nữa, bởi lẽ họ đã bảo (Musa): “Hãy mang Allah đến cho chúng tôi thấy công khai” thì liền đó lưỡi tầm sét (của Allah) đã đánh họ gục xuống bất tỉnh vì điều sai quấy của họ; rồi sau đó họ còn mang con bò con (được đúc) ra thờ sau khi đã chứng kiến những bằng chứng rõ rệt. Mặc dù thế TA (Allah) vẫn lượng thứ cho điều đó. Và TA đã ban cho Musa một thẩm quyền rõ rệt (của một Sứ Giả của Allah).');
INSERT INTO "hasan_abdulkarim" VALUES(4,154,'4:154','Do lời giao ước của họ, TA (Allah) đã dựng ngọn núi (Tur) cao khỏi đầu họ. Và (vào một dịp khác) TA (Allah) đã phán cho họ: “Hãy bước vào cửa mọp đầu thần phục” và (một lần nữa) TA (Allah) đã phán bảo họ: "Chớ vi phạm lệnh cấm vào Ngày Thứ Bảy" và TA (Allah) đã nhận từ họ một sự giao ước long trọng.');
INSERT INTO "hasan_abdulkarim" VALUES(4,155,'4:155','Vì họ bội ước qua việc họ phủ nhận các dấu hiệu của Allah, qua việc họ đã giết hại các Nabi (của Allah) không có lý do chính đáng, qua việc họ nói: “Quả tim của chúng tôi được bọc kín” nên Allah đã niêm kín tấm lòng của họ về tội không tin. Bởi thế, đức tin của họ rất mỏng manh.');
INSERT INTO "hasan_abdulkarim" VALUES(4,156,'4:156','Và vì họ phủ nhận đức tin và dùng lời lẽ nặng nề để vu oan cho Maryam.');
INSERT INTO "hasan_abdulkarim" VALUES(4,157,'4:157','Và vì họ đã nói: “Chúng tôi đã giết chết Masih Ysa, con trai của Maryam, tức Sứ Giả của Allah;” và thực ra họ đã không giết được Y (Ysa) cũng không đóng đinh Y trên cây thánh giá mà là một sự hoán đổi tương tự đã được trình bày qua cho họ. Và quả thật, những ai bất đồng ý kiến về việc (sát hại Ysa) hoàn toàn nghi ngờ về sự kiện đó; họ không hiểu biết chắc chắn việc đó, ngược lại chỉ nhắm mắt phỏng chừng mà thôi. Và chắc chắn họ đã không giết chết Người (Ysa).');
INSERT INTO "hasan_abdulkarim" VALUES(4,158,'4:158','Không, Allah đã đưa Người (Ysa) lên cùng với Ngài. Bởi vì Allah là Đấng Toàn Năng, Rất Mực Sáng Suốt.');
INSERT INTO "hasan_abdulkarim" VALUES(4,159,'4:159','Và chỉ Người dân Kinh Sách tin tưởng nơi Y trước khi xảy ra cái chết của Y (Ysa). Và vào Ngày phục sinh, Y sẽ là nhân chứng đối chất lại với họ (trước Allah).');
INSERT INTO "hasan_abdulkarim" VALUES(4,160,'4:160','Và vì những việc làm sai quấy của người Do thái nên TA (Allah) đã cấm họ dùng một số thực phẩm tốt và sạch đã được chấp thuận cho họ; và vì việc họ đã cản trở đa số nhân loại theo Con đường của Allah;');
INSERT INTO "hasan_abdulkarim" VALUES(4,161,'4:161','Và vì họ lấy tiền lãi cho vay (Riba) mà họ đã bị cấm thu trước đó; và vì họ đã ăn chặn tài sản của thiên hạ một cách bất chính; cho nên TA (Allah) đã chuẩn bị sẵn cho những kẻ không có đức tin trong bọn họ một sự trừng phạt đau đớn.');
INSERT INTO "hasan_abdulkarim" VALUES(4,162,'4:162','Tuy nhiên, những người nào của họ có một cơ sở hiểu biết vững chắc và có đức tin thì đều tin tưởng nơi điều (Mặc khải) đã được ban xuống cho Ngươi (Muhammad) và điều đã được ban xuống vào thời trước Ngươi và (đặc biệt) những người dâng lễ nguyện Salah một cách chu đáo và đóng Zakah và tin tưởng nơi Allah và Ngày Phán Xử Cuối Cùng thì là những người mà TA (Allah) sẽ ban cho một phần thưởng rất to lớn.');
INSERT INTO "hasan_abdulkarim" VALUES(4,163,'4:163','Quả thật, TA (Allah) đã mặc khải cho Ngươi (Muhammad!) giống như việc TA đã mặc khải cho Nuh và cho các Nabi sau Người; và TA đã mặc khải cho Ibrãhĩm và Ismã''ĩl và Ishãq và Ya''qũb và các Bộ lạc (của Israel); và cho ''Isa và Ayyũb và Yũnus và Hãrũn và cho Sulaymãn; và TA đã ban cho Dawood Zabũr (kinh Thi thiên).');
INSERT INTO "hasan_abdulkarim" VALUES(4,164,'4:164','Và trước đây, một số Sứ Giả, TA (Allah) đã kể lại câu chuyện của Họ cho Ngươi; và một số khác TA đã không kể lại cho Ngươi biết câu chuyện của Họ. Và Allah phán bảo trực tiếp với Mũsa.');
INSERT INTO "hasan_abdulkarim" VALUES(4,165,'4:165','Các vị Sứ Giả vừa làm người báo tin lành vừa là người cảnh cáo để cho nhân loại không còn dựa vào lý do nào khác để khiếu nại với Allah sau khi các vị Sứ Giả đó (đã được phái đến với họ). Và Allah Toàn Năng, Rất Mực Sáng Suốt.');
INSERT INTO "hasan_abdulkarim" VALUES(4,166,'4:166','Và Allah xác nhận điều (Mặc khải) mà Ngài đã ban xuống cho Ngươi (Muhammad); Ngài ban Nó (Qur‘an) xuống từ Kiến thức của Ngài; và các Thiên Thần cũng xác nhận như thế; nhưng riêng Allah thôi cũng đủ làm chứng (cho việc này.)');
INSERT INTO "hasan_abdulkarim" VALUES(4,167,'4:167','Quả thật, những ai không có đức tin và cản trở (thiên hạ) theo Con đường của Allah thì quả thật họ đã lạc đạo quá xa.');
INSERT INTO "hasan_abdulkarim" VALUES(4,168,'4:168','Quả thật, những ai không có đức tin và làm điều sai quấy thì chắc chắn sẽ không được Allah tha thứ cũng không được Ngài hướng dẫn bất cứ đường nào đúng đắn nào.');
INSERT INTO "hasan_abdulkarim" VALUES(4,169,'4:169','Ngoại trừ con đường dẫn họ đến hỏa ngục, nơi mà họ sẽ vào ở đời đời trong đó; và (việc) đó chẳng mấy gì khó khăn đối với Allah.');
INSERT INTO "hasan_abdulkarim" VALUES(4,170,'4:170','Hỡi nhân loại! Quả thật, Sứ Giả (Muhammad) đến với các ngươi mang theo sự Thật từ Thượng Đế (Allah) của các ngươi; bởi thế, việc tin tưởng nơi Y là một điều tốt cho các ngươi. Và nếu các ngươi phủ nhận thì quả thật mọi vật trong các tầng trời và trái đất đều là của Allah cả. Và Allah là Đấng Hằng Biết, Đấng Rất Mực Sáng Suốt.');
INSERT INTO "hasan_abdulkarim" VALUES(4,171,'4:171','Hỡi Người dân Kinh Sách! Chớ tỏ ra thái quá trong tôn giáo của các ngươi và chỉ nói sự thật về Allah. Masih Ysa con trai của Maryam chỉ là một Sứ Giả của Allah và là Lời phán (Hãy Thành) mà Ngài truyền xuống cho Maryam và là một Tinh thần (Ruh) từ Ngài (Allah). Bởi thế, hãy tin tưởng nơi Allah và các Sứ Giả của Ngài. Và chớ nói ''Ba'' (ngôi). Từ bỏ (tư tưởng đó) tốt cho các ngươi hơn bởi Allah là Thượng Đế Duy nhất, Ngài là Đấng Siêu Việt vượt trên sự việc có một đứa con trai bởi lẽ mọi vật trong các tầng trời và mọi vật dưới đất đều là (tạo vật) của Ngài. Và chỉ một mình Allah đủ chi phối và điều hành tất cả mọi sự việc.');
INSERT INTO "hasan_abdulkarim" VALUES(4,172,'4:172','Masih (Ysa) không có gì phải ái ngại khi làm một người bề tôi của Allah; các Thiên Thần kế cận (Allah) nhất cũng không ái ngại làm điều đó. Và ai tỏ ra ái ngại không muốn phụng sự Ngài và tỏ thái độ ngạo mạn thì sẽ bị Ngài tập trung đưa về chầu Ngài.');
INSERT INTO "hasan_abdulkarim" VALUES(4,173,'4:173','Còn đối với những ai có đức tin và làm việc thiện thì Ngài (Allah) sẽ sớm trả lại cho họ đầy đủ phần công lao của họ và còn ban thêm thiên lộc của Ngài nữa. Ngược lại, đối với những ai miệt thị và tỏ ra ngạo mạn thì sẽ bị Ngài phạt bằng một hình phạt đau đớn và ngoài Allah ra họ sẽ không tìm được một Đấng Bảo Hộ hay một Vị Cứu Tinh nào (để giúp đỡ họ).');
INSERT INTO "hasan_abdulkarim" VALUES(4,174,'4:174','Hỡi nhân loại! Một Bằng chứng xác thực từ Thượng Đế (Allah) của các ngươi đã đến với các ngươi bởi vì TA đã ban xuống cho các ngươi một ánh sáng rõ rệt.');
INSERT INTO "hasan_abdulkarim" VALUES(4,175,'4:175','Bởi thế, đối với những ai tin tưởng nơi Allah và bám chắc vào Ngài thì sẽ sớm được Ngài khoan dung và ưu ái và sẽ được hướng dẫn theo con đường ngay chính để trở về với Ngài.');
INSERT INTO "hasan_abdulkarim" VALUES(4,176,'4:176','Họ hỏi Ngươi (Muhammad) về một quyết định pháp lý. Hãy bảo họ: “Allah đã chỉ dẫn cho các ngươi về việc hưởng gia tài của một người không có cha mẹ và con cái như sau: nếu một người qua đời không có con mà chỉ có một người chị (hay em gái) thì người này được hưởng phân nửa (2) gia tài do y để lại. Nhưng nếu người chị (hay em gái) chết đi nhưng không có con thì anh (hay em) trai sẽ là người thừa kế của chị (hay em gái) đó; còn nếu người chết có hai chị (em) gái còn sống thì hai người này sẽ được hưởng hai phần ba (2/3) của gia tài mà y để lại; và nếu có anh (em) trai lẫn chị (em) gái còn sống thì phần của nam bằng hai phần của nữ. Allah giải thích rõ điều này cho các ngươi sợ rằng các người sai lạc. Và Allah Hằng Biết hết mọi việc.”');
INSERT INTO "hasan_abdulkarim" VALUES(5,1,'5:1','Hỡi những ai có đức tin! Hãy làm tròn (tất cả) các nghĩa vụ(. Các ngươi được phép dùng (thịt) của tất cả các gia súc bốn chân ngoại trừ những con nào đã được công bố cho các ngươi nhưng không được phép dùng thịt của các con thú săn trong suốt thời gian hãm mình làm Hajj (trong vùng Thánh điạ của Makkah). Quả thật, Allah phán quyết điều gì Ngài muốn.');
INSERT INTO "hasan_abdulkarim" VALUES(5,2,'5:2','Hỡi những ai có đức tin! Chớ xúc phạm các biểu hiệu của Allah, chớ vi phạm những tháng cấm kỵ, chớ phạm đến những con vật tế (cho nghi thức Hajj), và chớ phạm đến những con vật có mang vòng đeo cổ (để làm dấu chuẩn bị làm vật tế trong nghi thức Hajj) và cũng chớ quấy rầy các du khách tới lui thăm viếng Ngôi Đền thiêng (Ka''bah) mong kiếm thiên lộc và sự hài lòng của Thượng Đế (Allah) của họ. Và khi chấm dứt tình trạng hãm mình (của việc làm Hajj) các ngươi được phép đi săn (thú); và chớ để cho việc thù ghét một đám người đã (có lần) cản trở các ngươi không cho vào Thánh đường Linh thiêng (tại Makkah) thúc đẩy các ngươi đến chỗ phạm giới. Ngược lại, hãy giúp đỡ nhau trong đạo đức và sợ Allah và chớ tiếp tay cho nhau gây tội ác và hận thù. Hãy sợ Allah. Quả thật, Allah rất nghiêm khắc trong việc trừng phạt.');
INSERT INTO "hasan_abdulkarim" VALUES(5,3,'5:3','(Allah) cấm các ngươi dùng (thịt của) xác chết, máu, thịt của con heo, món vật cúng cho ai khác không phải là Allah, (thịt của) những con vật bị vặn (hay thắt) cổ chết, những con vật bị đập chết, những con vật bị rơi trên cao xuống chết, những con vật bị húc chết (bằng sừng), những con vật bị các con thú dữ ăn một phần trừ phi các người làm cho sạch (bằng cách cắt cổ chúng theo đúng nghi thức), và những món vật cúng trên bàn thờ (hay trên đá); và cấm các ngươi chia phần bằng cách xin xăm. Tất cả các thứ đó đều ô uế. Ngày nay, những kẻ không có đức tin tuyệt vọng về (việc phá hoại) tôn giáo của các ngươi. Bởi thế, chớ Sợ chúng mà hãy sợ TA (Allah). Ngày nay, TA đã hoàn chỉnh tôn giáo của các ngươi cho các ngươi và hoàn tất Ân huệ của TA cho các ngươi và đã chọn Islam làm tôn giáo của các ngươi. Nhưng ai vì quá đói (bắt buộc phải ăn các món cấm đó) chứ không cố tình phạm giới thì quả thật Allah Hằng Tha Thứ và Khoan Dung.');
INSERT INTO "hasan_abdulkarim" VALUES(5,4,'5:4','Họ hỏi Ngươi (Muhammad) đâu là những món (thực phẩm) họ được phép dùng. Hãy bảo họ: “Các ngươi được phép dùng các món (ăn) tốt và sạch. Và những con thú (hay chim) săn mà các con thú đi săn do các ngươi huấn luyện theo phương cách Allah đã dạy cho các ngươi đã bắt được. Bởi thế, hãy ăn thịt của con vật mà chúng đã bắt được cho các ngươi nhưng hãy nhắc tên của Allah lên nó. Và hãy sợ Allah bởi quả thật, Allah rất nhanh trong việc xét xử.');
INSERT INTO "hasan_abdulkarim" VALUES(5,5,'5:5','Ngày nay, các ngươi được phép dùng các món (thực phẩm) tốt và sạch. Thực phẩm của những người đã được ban cho Kinh Sách được chấp thuận (Halal) cho các ngươi (dùng) và thực phẩm của các ngươi được chấp thuận (Halal) cho họ (dùng). Các ngươi được phép cưới các trinh nữ tự do có đức tin (không phải là nữ nô lệ) và cả các trinh nữ tự do trong số người dân đã được ban cho Kinh Sách vào thời kỳ trước các ngươi với điều kiện các ngươi phải tặng họ phần tiền cưới bắt buộc (Mahr) với mục đích nhận họ làm vợ chứ không vì ham muốn tình dục cũng không nhận họ làm tình nhân vụng trộm. Và ai phủ nhận đức tin thì việc làm của y sẽ không có kết quả; và ở đời sau y sẽ là một kẻ thua thiệt.');
INSERT INTO "hasan_abdulkarim" VALUES(5,6,'5:6','Hỡi những ai có đức tin! Khi các ngươi đứng dậy để đi dâng lễ Salah hãy rửa mặt và (hai) tay của các ngươi đến cùi chỏ và lau vuốt đầu của các ngươi (với nước) và rửa hai bàn chân đến tận mắt cá. Và nếu các ngươi không được sạch (do việc chăn gối) thì phải tẩy sạch toàn thân (bằng cách tắm toàn thân, và nếu các ngươi bị bệnh hoặc trên đường đi xa hoặc sau khi bước ra khỏi nhà vệ sinh hoặc sau khi chung đụng( với vợ nhưng không tìm ra nước (để tẩy sạch) thì hãy đến chỗ đất (hay cát) sạch, dùng nó lau mặt và tay của các ngươi (đến cùi chỏ) theo hình thức Tayyammum; Allah không muốn gây khó khăn cho các ngươi; ngược lại Ngài muốn tẩy sạch các ngươi và hoàn tất Ân huệ của Ngài cho các ngươi để cho các ngươi có thể tạ ơn.');
INSERT INTO "hasan_abdulkarim" VALUES(5,7,'5:7','Và hãy nhớ Ân huệ của Allah đã ban cho các ngươi và (nhớ) lời giao ước mà các ngươi đã giao ước với Ngài khi các ngươi thưa: “Bầy tôi xin nghe và tuân lệnh". Và hãy sợ Allah bởi quả thật Allah Hằng Biết mọi điều thầm kín trong lòng (của các ngươi).');
INSERT INTO "hasan_abdulkarim" VALUES(5,8,'5:8','Hỡi những ai có đức tin! Hãy vì công lý mà hiên ngang đứng ra làm nhân chứng cho Allah. Và chớ để cho lòng hận thù đối với một đám người thôi thúc các ngươi hành động bất công. Hãy công bằng: điều đó gần với lòng sùng kính Allah. Hãy sợ Allah bởi quả thật, Allah là Đấng Am Tường mọi điều các người làm.');
INSERT INTO "hasan_abdulkarim" VALUES(5,9,'5:9','Allah hứa tha thứ và ban một phần thưởng vĩ đại cho những ai có đức tin và làm việc thiện tốt.');
INSERT INTO "hasan_abdulkarim" VALUES(5,10,'5:10','Còn những ai không có đức tin và phủ nhận các Lời Mặc Khải của TA (Allah) thì sẽ trở thành người bạn của hỏa ngục.');
INSERT INTO "hasan_abdulkarim" VALUES(5,11,'5:11','Hỡi những ai có đức tin! Hãy nhớ Ân huệ của Allah đã ban cho các ngươi khi một đám người có ý định giăng tay của chúng đến hãm hại các ngươi nhưng Ngài (Allah) đã giữ tay của chúng khỏi các ngươi. Bởi thế, hãy sợ Allah; và những người có đức tin nên tin tưởng mà phó thác cho Allah.');
INSERT INTO "hasan_abdulkarim" VALUES(5,12,'5:12','Và chắc chắn Allah đã nhận lời giao ước của con cháu của Israel và đã bổ nhiệm mười hai vị lãnh đạo của họ(; xong Allah phán: “TA ở cùng với các ngươi nếu các ngươi thường xuyên dâng lễ Salah và đóng Zakah và tin tưởng nơi các Sứ Giả của TA và trợ lực Họ và cho Allah mượn một món mượn tốt thì chắc chắn TA sẽ xóa tội cho các ngươi và đưa các ngươi vào những Ngôi vườn thiên đàng bên dưới có các dòng sông chảy; nhưng sau đó nếu ai trong các ngươi phủ nhận đức tin thì chắc chắn sẽ lạc khỏi con đường bằng phẳng (của Islam).');
INSERT INTO "hasan_abdulkarim" VALUES(5,13,'5:13','Nhưng vì họ đã bội ước nên TA (Allah) đã nguyền rủa họ và làm chai cứng tấm lòng của họ. Họ đã đổi vị trí của Lời phán (của Allah trong Kinh Sách); Họ đã bỏ bớt một phần của Bức Thông Điệp (của Allah); bởi thế, Ngươi sẽ thấy họ không ngưng gian lận ngoại trừ một số ít; nhưng hãy lượng thứ và bỏ qua cho họ. Quả thật, Allah yêu thương những người làm tốt.');
INSERT INTO "hasan_abdulkarim" VALUES(5,14,'5:14','Và trong số những người đã tự gọi mình là tín đồ Thiên Chúa giáo, TA (Allah) đã nhận lời giao ước của họ, nhưng họ đã bỏ quên một phần của Bức Thông Điệp đã được ban cho họ nên TA (Allah) đã khơi dậy ác cảm và hận thù giữa họ với nhau cho đến Ngày phục sinh và Allah sẽ sớm báo cho họ biết điều (sai quấy) mà họ đã thực hiện.');
INSERT INTO "hasan_abdulkarim" VALUES(5,15,'5:15','Hỡi Người dân Kinh Sách! Quả thật, Sứ Giả (Muhammad) của TA đã đến gặp các ngươi trình bày rõ cho các ngươi nhiều điều mà các ngươi đã từng giấu giếm trong Kinh Sách (của các ngươi) và lướt qua nhiều điều (không cần đề cập nữa). Chắc chắn, một ánh sáng và một Kinh Sách quang minh từ Allah đã được gởi đến cho các ngươi.');
INSERT INTO "hasan_abdulkarim" VALUES(5,16,'5:16','Allah dùng Nó (Qur''an) để hướng dẫn tất cả những ai tuân theo sự hài lòng của Ngài hầu đi theo con đường an bình (của Islam) và đưa họ rời khỏi tăm tối để bước ra ánh sáng theo sự chấp thuận của Ngài và hướng dẫn họ theo con đường ngay chính.');
INSERT INTO "hasan_abdulkarim" VALUES(5,17,'5:17','Quả thật là không có đức tin đối với những ai nói rằng Allah là Masih (Ysa), con trai của Maryam. Hãy bảo họ: “Thế thì ai có quyền cản trở Allah nếu Ngài muốn tiêu diệt Masih (Ysa) lẫn người mẹ của Y và bất cứ ai trên trái đất này?” Bởi vì Allah nắm quyền thống trị các tầng trời và trái đất và mọi vật giữa trời đất. Ngài tạo hóa bất cứ vật gì Ngài muốn. Và Allah có quyền chi phối trên tất cả mọi vật.');
INSERT INTO "hasan_abdulkarim" VALUES(5,18,'5:18','Và người Do thái lẫn tín đồ Thiên Chúa giáo đều nói: “Chúng tôi là các đứa con đáng yêu của Allah.” Hãy bảo họ: “Nếu thế thì tại sao Ngài (Allah) bắt phạt các ngươi vì tội lỗi của các ngươi?” Không, các ngươi là những người phàm trong loài người mà Ngài đã tạo; Ngài tha thứ cho người nào Ngài muốn và trừng phạt kẻ nào Ngài muốn bởi vì Allah nắm quyền thống trị các tầng trời và trái đất và vạn vật giữa trời đất. Và Ngài là mục tiêu trở về cuối cùng (của tất cả).');
INSERT INTO "hasan_abdulkarim" VALUES(5,19,'5:19','Hỡi Người dân Kinh Sách! Quả thật, Sứ Giả (Muhammad) của TA (Allah) đã đến gặp các ngươi để giải thích rõ sự việc sau một thời gian tạm ngưng phái các Sứ Giả (của TA) bởi vì các ngươi có thể nói: “Không có một người mang tin lành và báo tin dữ nào đến gặp chúng tôi cả.” Vì thế một người vừa mang tin lành vừa làm việc cảnh cáo đến gặp các ngươi. Bởi vì Allah có quyền trên tất cả mọi việc.');
INSERT INTO "hasan_abdulkarim" VALUES(5,20,'5:20','Và (hãy nhớ) khi Musa bảo người dân của Y: “Hỡi dân ta! Hãy nhớ Ân huệ của Allah đã ban cho các ngươi khi Ngài đã bổ nhiệm trong các ngươi các Nabi và làm cho các ngươi thành vua (chúa) và ban cho các ngươi (Ân phúc) mà Ngài đã không hề ban cho một ai khác trong thiên hạ.”');
INSERT INTO "hasan_abdulkarim" VALUES(5,21,'5:21','“Hỡi dân ta! Hãy vào Thánh địa mà Allah đã chỉ định cho các ngươi và chớ quay trở lui bởi vì làm thế các ngươi sẽ trở thành những kẻ thua thiệt.”');
INSERT INTO "hasan_abdulkarim" VALUES(5,22,'5:22','Họ đáp: “Hỡi Musa! Quả thật, nơi đó có một dân tộc sức mạnh phi thường; và thực sự chúng tôi sẽ không bao giờ tiến vào (thánh địa) được trừ phi chúng rời khỏi nơi đó. Bởi thế, nếu chúng ra đi thì chúng tôi sẽ vào.”');
INSERT INTO "hasan_abdulkarim" VALUES(5,23,'5:23','(Nhưng) trong số những người sợ Allah có hai người đã được Allah ban ân (tên Yusha'' và Kalab) vội lên tiếng: “Nào, hãy tấn công chúng vào cổng (chính); bởi thế khi nào quí vị vào cổng được thì chắc chắn quí vị chiến thắng, và hãy phó thác cho Allah nếu quí vị có đức tin thực sự.”');
INSERT INTO "hasan_abdulkarim" VALUES(5,24,'5:24','Và họ nói tiếp: “Hỡi Musa! Chúng tôi sẽ không bao giờ vào trong ấy đặng nếu chúng còn ở trong đó. Bởi thế, thầy và Thượng Đế của thầy hãy vào tấn công chúng trước, chúng tôi ngồi đợi ở đây.”');
INSERT INTO "hasan_abdulkarim" VALUES(5,25,'5:25','(Musa) thưa: “Lạy Thượng Đế của bề tôi! Bề tôi chỉ kiểm soát được bản thân của bề tôi và người anh của bề tôi thôi! Xin Ngài tách rời hai anh em của bề tôi ra khỏi đám loạn thần này!”');
INSERT INTO "hasan_abdulkarim" VALUES(5,26,'5:26','(Allah) phán: “Thế thì nó (Thánh địa) sẽ bị cấm đối với họ trong vòng bốn mươi ( năm. (Suốt thời gian này) họ sẽ đi lang thang khắp nơi trên trái đất. Bởi thế, Ngươi chớ than van cho đám loạn thần này.”');
INSERT INTO "hasan_abdulkarim" VALUES(5,27,'5:27','(Hỡi Muhammad!) Hãy đọc cho họ nghe đúng sự thật câu chuyện về hai đứa con trai( (Qobil và Habil) của Adam khi chúng dâng lễ vật (lên Allah); nhưng Ngài chỉ chấp nhận từ một đứa và không chấp nhận từ đứa kia. (Qobil) bảo (đứa em): “Chắc chắn tao sẽ giết mày.” (Habil) đáp: “Allah chỉ chấp nhận từnhững người ngay chính sợ Allah.”');
INSERT INTO "hasan_abdulkarim" VALUES(5,28,'5:28','“Nếu anh nhất định giăng tay ra giết tôi thì tôi sẽ không giăng tay ra giết anh trở lại bởi vì tôi sợ Allah, Thượng Đế của vũ trụ và muôn loài.”');
INSERT INTO "hasan_abdulkarim" VALUES(5,29,'5:29','“Thực ra tôi muốn anh lãnh cả tội của tôi lẫn tội của anh. Vì thế anh sẽ làm bạn với hỏa ngục và đây là phần thưởng dành cho những ai làm điều sai quấy”.');
INSERT INTO "hasan_abdulkarim" VALUES(5,30,'5:30','Bởi thế, ý nghĩ vị kỹ muốn giết em của hắn làm cho hắn cảm thấy thích thú. Do đó, hắn đã giết đứa em của hắn. Vì thế hắn đã trở thành một người thua thiệt.');
INSERT INTO "hasan_abdulkarim" VALUES(5,31,'5:31','Sau đó, Allah gởi một con quạ đến cào đất để chỉ cho hắn (Qobil) thấy cách chôn xác của đứa em của hắn. (Thấy thế) hắn than: ‘Ta thật khốn nạn! Há ta không bằng con quạ này hay sao đến nỗi ta không biết cách chôn xác của em ta? Thế rồi hắn đâm ra hối hận.');
INSERT INTO "hasan_abdulkarim" VALUES(5,32,'5:32','Bởi lý do đó, nên TA (Allah) đã quyết định cho con cháu của Israel như sau: ai giết một người (vô tội) trừ phi (giết) một tên sát nhân hay một kẻ phá hoại trên trái đất thì như là hắn đã giết toàn thể nhân loại; và ai cứu sống một sinh mạng thì như là hắn đã cứu sống toàn thể nhân loại. Và chắc chắn các Sứ Giả của TA (Allah) đã đến gặp họ với những bằng chứng rõ rệt rồi sau đó đa số bọn họ vẫn hành động thái quá trên mặt đất!');
INSERT INTO "hasan_abdulkarim" VALUES(5,33,'5:33','Hình phạt dùng trừng trị những kẻ tạo chiến tranh chống lại Allah và Sứ Giả của Ngài và nỗ lực gây phá họai trên trái đất chỉ có việc: hoặc xử tử, hoặc đóng đinh trên thập tự giá hoặc chặt tay này và chân kia hoặc trục xuất ra khỏi lãnh thổ. Đó là hình phạt nhục nhã dành cho chúng ở thế gian này; và ở đời sau chúng sẽ chịu một sự trừng phạt vĩ đại;');
INSERT INTO "hasan_abdulkarim" VALUES(5,34,'5:34','Ngoại trừ những ai biết ăn năn sám hối trước khi các ngươi (Muslim) chinh phục chúng. Nhưng hãy biết rằng Allah Hằng Tha Thứ, Rất Mực Khoan Dung.');
INSERT INTO "hasan_abdulkarim" VALUES(5,35,'5:35','Hỡi những ai có đức tin! Hãy sợ Allah và tìm phương cách hướng về Ngài và chiến đấu cho Chính Nghĩa của Ngài để may ra các ngươi được thành đạt.');
INSERT INTO "hasan_abdulkarim" VALUES(5,36,'5:36','Quả thật, những kẻ không có đức tin dẫu cho họ có tất cả mọi thứ trên trái đất và có gấp đôi thứ đó để chuộc tội hầu khỏi bị trừng phạt vào Ngày phục sinh thì nó sẽ không được chấp nhận từ họ và họ sẽ chịu một sự trừng phạt đau đớn.');
INSERT INTO "hasan_abdulkarim" VALUES(5,37,'5:37','Họ mong muốn được thoát ra khỏi hỏa ngục nhưng họ sẽ không thoát ra khỏi đó đặng và họ sẽ chịu một sự trừng phạt triền miên.');
INSERT INTO "hasan_abdulkarim" VALUES(5,38,'5:38','(Đối với) người nam hay nữ trộm cắp, hãy chặt tay của họ như là một sự phạt đền tội mà họ đã phạm, một biện pháp từ Allah nhằm làm ngã lòng (kẻ trộm cắp). Và Allah Toàn Năng, Rất Mực Sáng Suốt.');
INSERT INTO "hasan_abdulkarim" VALUES(5,39,'5:39','Nhưng ai biết ăn năn hối cải và sửa mình sau khi làm điều sai quấy thì quả thật Allah quay lại tha thứ cho y. Quả thật, Allah Hằng Tha Thứ, Rất Mực Khoan Dung.');
INSERT INTO "hasan_abdulkarim" VALUES(5,40,'5:40','Há Ngươi không biết rằng quyền thống trị các tầng trời và trái đất thuộc về Allah hay sao? Ngài trừng phạt kẻ nào Ngài muốn và tha thứ cho người nào Ngài muốn bởi vì Allah Toàn Năng trên mọi vạn vật.');
INSERT INTO "hasan_abdulkarim" VALUES(5,41,'5:41','Hỡi Sứ Giả (Muhammad)! Chớ đâm lo buồn cho những ai thi đua nhau chạy vào vòng không tin tưởng trong số những kẻ đã nói: “Chúng tôi tin tưởng” trên đầu môi chót lưỡi nhưng tấm lòng của chúng không có đức tin. Và những ai là người Do thái, chúng nghe theo điều gian dối, chúng nghe theo đám người khác chẳng hề đến tiếp xúc với Ngươi; chúng thay đổi lời nói khỏi vị trí của nó; chúng nói: “Nếu quí vị được ban cho điều này thì hãy tiếp thu nó, ngược lại nếu quí vị không được ban cho thì hãy cảnh giác!”. Và người nào mà Allah muốn thử thách thì Ngươi sẽ hoàn toàn bất lực trong việc cứu hắn thoát khỏi hình phạt của Allah. Đó là những kẻ mà Allah không muốn tẩy sạch tấm lòng của chúng. Chúng sẽ bị hạ nhục trên trần thế, nhưng ở đời sau chúng sẽ bị trừng phạt nặng nề hơn.');
INSERT INTO "hasan_abdulkarim" VALUES(5,42,'5:42','(Chúng thích) nghe theo điều gian dối, (thích) ăn các món bị cấm. Bởi thế, nếu chúng đến yết kiến Ngươi thì hoặc phân xử giữa bọn chúng hoặc từ chối tiếp chúng. Và nếu Ngươi từ chối tiếp chúng thì chúng chẳng hại gì được Ngươi. Và nếu Ngươi phân xử bọn chúng thì hãy xét xử một cách công bằng. Quả thật, Allah yêu thương người (xét xử) công bằng.');
INSERT INTO "hasan_abdulkarim" VALUES(5,43,'5:43','Nhưng tại sao chúng đến nhờ Ngươi phân xử trong lúc chúng đã có Kinh Tawrãh trước mặt? (Bởi vì) trong Kinh (Tawrãh) đã có sẵn Mệnh lệnh của Allah; rồi sau việc (phân xử) đó, chúng lật lọng bởi vì chúng là những kẻ tuyệt đối không tin gì cả.');
INSERT INTO "hasan_abdulkarim" VALUES(5,44,'5:44','Quả thật, TA (Allah) đã ban Kinh Tawrah xuống (cho Musa) trong đó có sẵn Chỉ Đạo và ánh sáng (cho con cháu của Israel) mà các Nabi tức các vị thần phục Allah (trong Islam), các thầy tu Do thái, các tiến sĩ luật Do thái đã dùng (Kinh Tawrah) để xét xử những ai là người Do thái bởi vì các vị đó được giao cho nhiệm vụ bảo quản Kinh Sách của Allah đồng thời làm chứng cho nó. Bởi thế, chớ sợ người mà hãy sợ TA (Allah) và chớ bán các Lời Mặc Khải của TA với một giá nhỏ nhoi; và ai không xét xử theo điều Allah đã ban xuống thì đó là những kẻ không có đức tin.');
INSERT INTO "hasan_abdulkarim" VALUES(5,45,'5:45','TA (Allah) đã ra lệnh cho họ trong (Tawrah): “Mạng đền mạng, mắt đền mắt, mũi đền mũi, tai đền tai, răng đền răng và thương tích đền bằng thương tích theo luật công bằng.” Nhưng ai vì nhân đạo bỏ qua việc trả thù thì đó là cách xóa tội cho hắn; và ai không xét xử theo điều Allah đã ban xuống thì đó là những kẻ làm điều sai quấy.');
INSERT INTO "hasan_abdulkarim" VALUES(5,46,'5:46','Và TA (Allah) đã cử Ysa, con trai của Maryam nối gót Họ (các Nabi tiền bối) đến để xác nhận lại các điều (mặc khải) trong Kinh Tawrãh đã được ban xuống trước thời kỳ của Người và TA đã ban cho Người Kinh Injĩl trong đó có Chỉ Đạo và ánh sáng để xác nhận lại điều đã có trước nó trong Kinh Tawrãh. (Kinh Injĩl) vừa là một Chỉ Đạo vừa là Lời cảnh cáo cho những người sợ Allah.');
INSERT INTO "hasan_abdulkarim" VALUES(5,47,'5:47','Và hãy để người dân của Kinh Injil xét xử theo điều (mặc khải) mà Allah đã ban xuống. Và ai không xét xử theo điều mà Allah đã ban xuống thì là những kẻ dấy loạn.');
INSERT INTO "hasan_abdulkarim" VALUES(5,48,'5:48','Và TA (Allah) đã ban cho Ngươi (hỡi Muhammad!) Kinh Sách (Qur’an) bằng sự Thật, xác nhận lại các điều (mặc khải) trong Kinh Sách trước Nó (Qur''an) và giữ gìn nó( (Kinh Sách cũ) cho được nguyên vẹn. Bởi thế, Ngươi hãy xét xử theo điều Allah đã ban xuống và chớ làm theo điều ham muốn của họ nghịch với Chân Lý mà Ngươi đã tiếp thu. TA (Allah) đã qui định cho từng (Sứ Giả) trong các ngươi một hệ thống luật pháp (Shari''ah) và một lề lối. Và nếu Allah muốn, Ngài thừa sức làm cho các ngươi thành một Cộng đồng (Ummah) duy nhất nhưng (Ngài không làm thế) là vì Ngài muốn thử thách các ngươi với điều mà Ngài đã ban cho các ngươi. Bởi thế, hãy thi đua làm điều thiện. Tất cả các ngươi sẽ được đưa về gặp Allah trở lại rồi Ngài sẽ cho các ngươi biết kết quả về các vấn đề mà các ngươi đã từng tranh chấp.');
INSERT INTO "hasan_abdulkarim" VALUES(5,49,'5:49','Và Ngươi hãy xét xử họ theo điều Allah đã ban xuống và chớ làm theo ý muốn của họ và hãy coi chừng họ e rằng họ sẽ quyến dụ Ngươi làm theo điều nghịch với một phần trong các điều lệnh mà Allah đã ban xuống cho Ngươi; nếu họ từ chối thì nên biết rằng Allah chỉ muốn trừng phạt họ về một số tội lỗi của họ. Và quả thật, đa số nhân loại là những kẻ dấy loạn.');
INSERT INTO "hasan_abdulkarim" VALUES(5,50,'5:50','Phải chăng họ mong được xét xử theo luật lệ của Thời kỳ ngu muội? Và ai (là Đấng) xét xử tốt hơn Allah cho đám người có đức tin?');
INSERT INTO "hasan_abdulkarim" VALUES(5,51,'5:51','Hỡi những ai có đức tin! Chớ kết thân với người Do thái và tín đồ Thiên Chúa giáo. Họ là đồng minh bảo vệ lẫn nhau. Và ai trong các người (Muslim) quay về kết thân với họ thì là người của họ. Quả thật, Allah không hướng dẫn đám người làm điều sai quấy.');
INSERT INTO "hasan_abdulkarim" VALUES(5,52,'5:52','Bởi thế, Ngươi thấy những kẻ mang trong lòng một chứng bệnh (giả dối) lăng xăng tới lui giữa bọn họ, nói: “Chúng tôi sợ gặp phải điều chẳng lành.” Do đó, giả sử Allah ban cho một thắng lợi hoặc một quyết định nào khác từ Ngài thì họ sẽ vô cùng hối tiếc về những điều mà họ đã giấu giếm trong lòng.');
INSERT INTO "hasan_abdulkarim" VALUES(5,53,'5:53','Và những người có đức tin sẽ nói: “Phải chăng đây là những kẻ đã thề thốt bằng lời thề mạnh bạo nhất nhân danh Allah rằng họ sẽ luôn luôn sát cánh với các anh?” Việc làm của họ sẽ vô ích bởi thế họ sẽ trở thành những kẻ thua thiệt.');
INSERT INTO "hasan_abdulkarim" VALUES(5,54,'5:54','Hỡi những ai có đức tin! Nếu ai trong các ngươi bỏ đạo của mình thì Allah sẽ đưa một đám người mà Ngài sẽ yêu thương và họ sẽ yêu thương Ngài đến; họ sẽ hạ mình khiêm tốn trước những người có đức tin nhưng lại khắt khe với những kẻ không tin; họ chiến đấu cho Chính Nghĩa của Allah và không bao giờ sợ lời trách móc của những kẻ hay chỉ trích. Đó là Thiên ân mà Allah ban cho người nào Ngài muốn. Và Allah Rộng Rãi Bao La, Đấng Hằng Biết.');
INSERT INTO "hasan_abdulkarim" VALUES(5,55,'5:55','Bạn hữu đỡ đầu thật sự của các ngươi chỉ là Allah và Sứ Giả của Ngài và những người có đức tin cùng với những ai chu đáo dâng lễ Salah, và đóng Zakah và cúi đầu thần phục (Allah);');
INSERT INTO "hasan_abdulkarim" VALUES(5,56,'5:56','Và ai quay về kết bạn với Allah và Sứ Giả của Ngài và những ai có đức tin thì y đã nhập đảng của Allah; họ sẽ đắc thắng vẻ vang.');
INSERT INTO "hasan_abdulkarim" VALUES(5,57,'5:57','Hỡi những ai có đức tin! Chớ nhận những ai trong số những kẻ đã được ban cho Kinh Sách trước các ngươi và những kẻ không có đức tin đã mang đạo giáo của các ngươi ra chế giễu và bỡn cợt làm người bảo hộ của các ngươi. Và hãy sợ Allah nếu các ngươi thực sự có đức tin.');
INSERT INTO "hasan_abdulkarim" VALUES(5,58,'5:58','Bởi vì khi các ngươi lớn tiếng gọi nhau đến dâng lễ, chúng mang điều đó ra chế giễu và bỡn cợt; sở dĩ như thế là vì chúng là một đám người không hiểu gì cả.');
INSERT INTO "hasan_abdulkarim" VALUES(5,59,'5:59','Hãy bảo họ: “Hỡi Người dân Kinh Sách! Phải chăng các ngươi phá rối bọn ta chỉ vì bọn ta tin tưởng nơi Allah và điều (mặc khải) đã được ban xuống cho bọn ta và điều đã được ban xuống trước đây và đa số các ngươi làm loạn và bất tuân?”');
INSERT INTO "hasan_abdulkarim" VALUES(5,60,'5:60','Hãy bảo họ: “Há ta có nên báo cho các ngươi biết về một điều còn tàn tệ hơn cái đó liên quan đến việc trừng phạt từ Allah hay chăng?” Những kẻ mà Allah đã nguyền rủa, đã giận dữ, những kẻ trong bọn chúng mà Allah đã biến thành khỉ và heo và là bầy tôi của Tà thần (Taghut). Những kẻ này rất tồi tệ về chỗ ở và lạc xa con đường bằng phẳng (của Islam).');
INSERT INTO "hasan_abdulkarim" VALUES(5,61,'5:61','Và khi đến tiếp xúc với các ngươi (Muslim) họ nói: “Chúng tôi tin tưởng.” Và chắc chắn họ nhập đạo bằng sự không tin và sẽ ra khỏi đạo cũng bằng lối đó. Nhưng Allah biết rõ những điều họ giấu giếm.');
INSERT INTO "hasan_abdulkarim" VALUES(5,62,'5:62','Và Ngươi thấy đa số bọn họ lao mình vào vòng tội lỗi và hiềm thù và ăn các món bị cấm. Những điều mà họ làm thật là xấu xa.');
INSERT INTO "hasan_abdulkarim" VALUES(5,63,'5:63','Tại sao các thầy tu Do thái và các vị tiến sĩ luật của họ không cản bọn họ thốt ra điều tội lỗi và cấm họ ăn các thứ bị cấm? Điều mà họ thực hiện thật hết sức xấu xa!');
INSERT INTO "hasan_abdulkarim" VALUES(5,64,'5:64','Người Do thái nói: “Bàn Tay của Allah bị xích chặt lại.” Tay của bọn họ mới bị xích lại và họ bị nguyền rủa vì lời phạm thượng của họ. Không, hai Tay của Ngài mở rộng. Ngài chi dùng thiên lộc của Ngài như thế nào tùy ý Ngài muốn. Và chắc chắn điều mà Thượng Đế của Ngươi ban xuống cho Ngươi càng làm cho bọn họ thêm thái quá và chống đối mãnh liệt. Và TA (Allah) đã khiến cho bọn họ ác cảm và hận thù lẫn nhau cho đến Ngày phục sinh. Mỗi lần họ khơi ngọn lửa chiến tranh lên thì Allah liền dập tắt nó. Và họ tận lực phá hoại cho thối nát trên trái đất, nhưng Allah không yêu thương những kẻ thối nát hay phá hoại.');
INSERT INTO "hasan_abdulkarim" VALUES(5,65,'5:65','Nếu Người dân Kinh Sách tín tưởng và sợ Allah thì chắc chắn TA (Allah) sẽ xoá bỏ tội lỗi của họ và chấp nhận họ vào các ngôi vườn thiên đàng hạnh phúc.');
INSERT INTO "hasan_abdulkarim" VALUES(5,66,'5:66','Nếu họ tuân thủ thực hiện (điều lệnh trong) Tawrah và Injil và thi hành tất cả các điều lệnh từ Thượng Đế của họ ban xuống thì chắc chắn họ sẽ hưởng (vô số) thiên lộc từ bên trên và từ bên dưới (chân của) họ. Trong họ, có một tập thể tuân theo Chính Đạo, còn đa số thì làm điều thối nát (tội lỗi).');
INSERT INTO "hasan_abdulkarim" VALUES(5,67,'5:67','Hỡi Sứ Giả (Muhammad!) Hãy truyền đạt những điều mà Thượng Đế của Ngươi đã ban xuống cho Ngươi. Nếu Ngươi không thi hành thì là Ngươi không truyền đạt Thông điệp của Ngài. Và Allah sẽ bảo vệ Ngươi khỏi bị nhân loại (ám hại). Bởi vì quả thật, Allah không dẫn dắt đám người không tin tưởng.');
INSERT INTO "hasan_abdulkarim" VALUES(5,68,'5:68','Hãy bảo họ: “Hỡi Người dân Kinh Sách! Các người chẳng có gì để làm cơ sở trừ phi các người dựa vào Kinh Tawrãh và Injĩl và tuân hành tất cả các điều (mặc khải) mà Thượng Đế của các người đã ban xuống.” Và chắc chắn tất cả các điều (mặc khải) mà Thượng Đế của Ngươi (Muhammad) đã ban xuống cho Ngươi càng làm cho bọn họ thêm thái quá và chống đối mãnh liệt. Bởi thế, Ngươi chớ than van giùm cho đám người không có đức tín.');
INSERT INTO "hasan_abdulkarim" VALUES(5,69,'5:69','Quả thật, những ai có đức tin và những ai là người Do thái và người Sabian và tín đồ Thiên Chúa giáo đã tin nơi Allah và Ngày Phán Xử Cuối Cùng và làm việc thiện thì sẽ không lo sợ cũng sẽ không buồn phiền.');
INSERT INTO "hasan_abdulkarim" VALUES(5,70,'5:70','Chắc chắn TA (Allah) đã nhận lời giao ước của con cháu của Israel và phái các Sứ Giả (của TA) đến với họ. Mỗi lần một Sứ Giả (của TA) đến gặp họ với điều (lệnh) trái nghịch với ý muốn của họ thì một số (Sứ Giả) đã bị họ cho là giả mạo, và một số khác thì bị họ giết chết.');
INSERT INTO "hasan_abdulkarim" VALUES(5,71,'5:71','Và họ nghĩ sẽ không có sự trừng phạt nào cả, cho nên, họ giả mù và giả điếc; rồi Allah quay lại tha thứ cho họ; rồi họ lại tiếp tục giả mù, giả điếc; nhưng Allah thấy rõ điều họ làm.');
INSERT INTO "hasan_abdulkarim" VALUES(5,72,'5:72','Chắc chắn là không có đức tin những ai nói: “Masih (Ysa), con trai của Maryam, là Allah.” Ngược lại, chính Masih (Ysa) đã nói: “Hỡi con cháu của Israel! Hãy tôn thờ Allah, Thượng Đế của Ta và là Thượng Đế của các ngươi.” Quả thật, ai tổ hợp thần linh cùng với Allah thì chắc chắn sẽ bị cấm vào thiên đàng và chỗ ở của y sẽ là hỏa ngục. Và những kẻ làm điều sai quấy sẽ không được ai giúp đỡ cả.');
INSERT INTO "hasan_abdulkarim" VALUES(5,73,'5:73','Chắc chắn là không có đức tin những ai nói: “Quả thật, Allah là vị thứ ba trong Ba (ngôi).” Bởi vì về Thượng Đế thì chỉ có một Thượng Đế duy nhất. Và nếu họ không chịu ngưng nói điều (phạm thượng) đó thì chắc chắn những kẻ không có đức tin trong bọn họ sẽ gặp phải một sự trừng phạt đau đớn.');
INSERT INTO "hasan_abdulkarim" VALUES(5,74,'5:74','Thế tại sao họ không chịu tạ tội với Allah và xin Ngài tha thứ? Bởi vì Allah Hằng Tha Thứ, Rất Mực Khoan Dung.');
INSERT INTO "hasan_abdulkarim" VALUES(5,75,'5:75','Masih (Ysa), con trai của Maryam, chỉ là một Sứ Giả (của Allah); nhiều Sứ Giả (của Allah) đã qua đời trước Y. Mẹ của Y là một phụ nữ chân thật, cả hai (mẹ và con) đều ăn thực phẩm (như mọi người). Hãy xem, TA (Allah) đã giải thích rõ các Lời Mặc Khải của TA (Allah) cho họ như thế nào! Rồi hãy xem, họ tránh né sự thật như thế nào!');
INSERT INTO "hasan_abdulkarim" VALUES(5,76,'5:76','Hãy bảo họ: “Phải chăng các ngươi tôn thờ những kẻ không có quyền hãm hại cũng chẳng làm lợi gì cho các ngươi? Và Allah là Đấng Hằng Nghe, Đấng Hằng Biết.');
INSERT INTO "hasan_abdulkarim" VALUES(5,77,'5:77','Hãy bảo họ: “Hỡi Người dân Kinh Sách! Chớ tỏ ra phi lý và thái quá trong tôn giáo của các ngươi một cachs không đúng với sự Thật và cũng chớ nghe theo đám người lạc đạo trước đây bởi vì chúng đã dắt nhiều người đi lạc và tự chúng cũng đã lạc khỏi con đường bằng phẳng (của Islam).');
INSERT INTO "hasan_abdulkarim" VALUES(5,78,'5:78','Những kẻ không có đức tin trong số con cháu của Israel đã bị Dawood và Ysa con trai của Maryam, nguyền rủa từ cửa miệng của hai người. Như thế là vì họ bất tuân và tiếp tục phạm giới.');
INSERT INTO "hasan_abdulkarim" VALUES(5,79,'5:79','Họ không cấm nhau làm điều ác đức mà họ đã từng làm. Và những điều mà họ làm thật là tội lỗi.');
INSERT INTO "hasan_abdulkarim" VALUES(5,80,'5:80','Ngươi thấy đa số bọn họ kết bạn với những kẻ không có đức tin (để chống lại người Muslim). Tồi tệ thay điều mà tâm hồn của họ đã xúi giục và gởi đi trước cho họ khiến Allah giận dữ họ và họ sẽ bị ở trong sự trừng phạt đó mãi mãi.');
INSERT INTO "hasan_abdulkarim" VALUES(5,81,'5:81','Và nếu họ tin tưởng nơi Allah và Nabi (Muhammad) và những điều đã được ban xuống cho Y thì họ sẽ không kết bạn với những kẻ không có đức tin, nhưng đa số bọn họ là những kẻ dấy loạn, bất tuân.');
INSERT INTO "hasan_abdulkarim" VALUES(5,82,'5:82','Ngươi sẽ thấy người Do thái và những người thờ đa thần là những người thù hằn những người có đức tin dữ dằn nhất; và chắc chắn Ngươi sẽ thấy có quan hệ tình cảm gần gủi nhất với những người có đức tin là những ai nói: “Chúng tôi là tín đồ Thiên Chúa giáo.” Sở dĩ như thế là vì trong họ có các bậc thức giả và các bậc chân tu và họ không kiêu căng.');
INSERT INTO "hasan_abdulkarim" VALUES(5,83,'5:83','Và khi họ nghe điều (mặc khải) đã được ban xuống cho Sứ Giả (Muhammad), Ngươi thấy mắt của họ nhòa lệ vì sự Thật mà họ công nhận. Họ thưa: “Lạy Thượng Đế của bầy tôi! Bầy tôi tin tưởng, xin Ngài ghi tên của bầy tôi cùng với các chứng nhân (của sự Thật)".');
INSERT INTO "hasan_abdulkarim" VALUES(5,84,'5:84','“Và tại sao chúng tôi lại không tin tưởng nơi Allah và nơi sự Thật đã đến với chúng tôi? Bởi vì chúng tôi hy vọng Thượng Đế của chúng tôi sẽ chấp nhận chúng tôi gia nhập hội đoàn của những người ngoan đạo.”');
INSERT INTO "hasan_abdulkarim" VALUES(5,85,'5:85','Và vì lời cầu nguyện của họ mà Allah đã thưởng họ thiên đàng, bên dưới có các dòng sông chảy để làm nơi cư ngụ vĩnh viễn. Và đó là phần thưởng dành cho những người làm tốt.');
INSERT INTO "hasan_abdulkarim" VALUES(5,86,'5:86','Ngược lại, những ai không có đức tin và cho các Lời Mặc Khải của TA (Allah) gian dối thì sẽ trở thành cư dân của hỏa ngục.');
INSERT INTO "hasan_abdulkarim" VALUES(5,87,'5:87','Hỡi những ai có đức tin! Chớ cấm dùng các món sạch và tốt lành mà Allah đã cho phép các ngươi dùng nhưng chớ quá độ bởi vì quả thật Allah không yêu thương những người quá độ.');
INSERT INTO "hasan_abdulkarim" VALUES(5,88,'5:88','Và hãy ăn các món được phép dùng và tốt sạch mà Allah đã cung cấp cho các ngươi. Nhưng hãy sợ Allah, Đấng mà các ngươi tin tưởng.');
INSERT INTO "hasan_abdulkarim" VALUES(5,89,'5:89','Allah sẽ không bắt phạt các ngươi về những lời thề trống rỗng ngược lại Ngài bắt phạt các người về các lời thề nghiêm trọng. Để chuộc tội, phải nuôi ăn mười ( người nghèo theo cân lượng trung bình mà các ngươi dùng để nuôi gia đình hoặc phải may mặc cho họ hoặc giải phóng một người nô lệ. Nhưng ai không có phương tiện thì phải nhịn chay ba ngày liên tục. Đó là cách chuộc tội cho các lời thề nghiêm trọng mà các người đã phạm. Hãy coi chừng lời thề của các ngươi. Allah giải thích rõ các Lời Mặc Khải của Ngài để may ra các ngươi biết tạ ơn.');
INSERT INTO "hasan_abdulkarim" VALUES(5,90,'5:90','Hỡi những ai có đức tin! Việc uống rượu, cờ bạc, thờ cúng trên bàn thờ bằng đá và dùng tên bắn để làm quẻ xin xăm là điều khả ố và là việc làm của Shaytan. Hãy từ bỏ nó (việc làm khả ố đó) để may ra các ngươi được phát đạt.');
INSERT INTO "hasan_abdulkarim" VALUES(5,91,'5:91','Shaytan chỉ muốn tạo ác cảm và hận thù giữa các ngươi qua việc uống rượu và bài bạc và cản trở các ngươi tưởng nhớ Allah và việc dâng lễ Salah. Thế các ngươi không chịu ngưng hay sao?');
INSERT INTO "hasan_abdulkarim" VALUES(5,92,'5:92','Hãy tuân lệnh Allah và vâng lời Sứ Giả (Muhammad). Và hãy thận trọng (để tránh phạm tội). Bởi thế, nếu các ngươi làm ngơ thì nên biết rằng nhiệm vụ của Sứ Giả của TA chỉ là truyền đạt (Thông điệp của Allah) một cách rõ ràng.');
INSERT INTO "hasan_abdulkarim" VALUES(5,93,'5:93','Những ai có đức tin và làm việc thiện không mắc tội về món thực phẩm mà họ đã dùng (trong quá khứ) khi họ giữ mình khỏi phạm tội và họ tin tưởng và làm việc thiện; rồi họ giữ mình khỏi phạm tội và tin tưởng rồi giữ mình khỏi phạm tội và làm tốt bởi vì Allah yêu thương những người làm tốt.');
INSERT INTO "hasan_abdulkarim" VALUES(5,94,'5:94','Hỡi những ai có đức tin! Chắc chắn Allah sẽ thử thách các ngươi bằng con thú săn nằm trong tằm tay và chiếc lao săn bắn của các người để Allah biết ai là người sợ Ngài, Đấng Vô Hình. Bởi thế, ai phạm giới sau đó thì sẽ bị trừng phạt đau đớn.');
INSERT INTO "hasan_abdulkarim" VALUES(5,95,'5:95','Hỡi những ai có đức tin! Chớ giết thú săn trong lúc các ngươi đang trong tình trạng hãm mình (hành hương Hajj và U''mrah); và ai trong các người cố tình giết nó thì phải bị phạt đền bằng một con thú nuôi tương đương với con thú (săn) đã bị giết, chọn trong đàn gia súc, dưới sự giám sát của hai ngươi công bằng trong các ngươi và được dắt đến (Ngôi Đền) Ka''bah để làm vật tế; hoặc có thể chuộc tội bằng cách nuôi ăn một người nghèo hoặc bằng cách nhịn chay tương đương với việc nuôi ăn đó mục đích để cho hắn nếm mùi phạt đền; Allah lượng thứ về việc đã qua; nhưng ai tái phạm thì sẽ bị Allah trừng phạt bởi vì Allah Toàn Năng, nắm trọn việc phạt đền.');
INSERT INTO "hasan_abdulkarim" VALUES(5,96,'5:96','Các ngươi được phép bắt các loại hải sản dùng làm thực phẩm: đó là nguồn lương thực cho các ngươi và cho khách bộ hành của sa mạc nhưng các ngươi không được phép giết thú săn trên bộ trong lúc các ngươi đang trong tình trạng hãm mình (hành hương); và hãy sợ Allah, Đấng mà các người sẽ được tập trung trở lại (để chịu sự xét xử).');
INSERT INTO "hasan_abdulkarim" VALUES(5,97,'5:97','Allah làm cho Ka''bah, Ngôi Đền thiêng, thành một chỗ đứng (dâng lễ) cho nhân loại, và các tháng thiêng liêng (để cúng tế) và các con vật tế và các con vật đeo vòng trên cổ để làm dấu (phân biệt). Như thế để cho các ngươi biết rằng Allah biết mọi việc trong các tầng trời và mọi việc dưới đất; và rằng Allah biết hết mọi việc.');
INSERT INTO "hasan_abdulkarim" VALUES(5,98,'5:98','Nên biết rằng Allah rất nghiêm khắc trong việc trừng phạt và rằng Allah Rất Mực Tha thứ, Rất Mực Khoan Dung.');
INSERT INTO "hasan_abdulkarim" VALUES(5,99,'5:99','Nhiệm vụ của một Sứ Giả (của Allah) chỉ là truyền đạt (Thông điệp của Ngài). Nhưng Allah biết rõ điều các ngươi tiết lộ và điều các ngươi giấu giếm.');
INSERT INTO "hasan_abdulkarim" VALUES(5,100,'5:100','Hãy bảo họ: “Vật bẩn thỉu và vật tốt sạch không có giá trị ngang nhau, dẫu rằng vô số vật bẩn thỉu làm cho Ngươi trầm trồ; bởi thế hãy sợ Allah, hỡi những ai thông hiểu để các ngươi có thể phát đạt.”');
INSERT INTO "hasan_abdulkarim" VALUES(5,101,'5:101','Hỡi những ai có đức tin! Chớ hỏi vặn những điều mà nếu được trình bày rõ cho các người thì sẽ làm cho các ngươi thêm thắc mắc. Nhưng nếu các ngươi hỏi về các vấn đề trong thời gian Kinh Qur''an đang được ban xuống thì chúng (các vấn đề) sẽ được trình bày rõ cho các ngươii. Allah luợng thứ cho các ngươi về vấn đề đó. Và Allah Hằng Tha Thứ, Hằng Chịu Đựng.');
INSERT INTO "hasan_abdulkarim" VALUES(5,102,'5:102','Quả thật, trước các ngươi đã có người đặt những câu hỏi tương tự và cũng vì thế họ đã mất niềm tin.');
INSERT INTO "hasan_abdulkarim" VALUES(5,103,'5:103','Allah đã không đặt ra các điều (mê tín liên quan đến con lạc đà) như Bahirah(; hoặc Sa-ibah hoặc Wasilah hoặc Ham. Chính những kẻ không có đức tin mới đặt điều nói dối rồi đổ thừa cho Allah; và đa số bọn chúng không hiểu gì.');
INSERT INTO "hasan_abdulkarim" VALUES(5,104,'5:104','Và khi được nhắc: “Hãy đến tiếp thu điều mặc khải do Allah ban xuống và hãy đến gặp Sứ Giả (Muhammad,)” thì họ đáp: “Phong tục mà chúng tôi thấy cha mẹ của chúng tôi đang theo đủ cho chúng tôi rồi.” Sao! Ngay cả việc cha mẹ của họ không biết gì cũng không được hướng dẫn hay sao?');
INSERT INTO "hasan_abdulkarim" VALUES(5,105,'5:105','Hỡi những ai có đức tin! Các người chịu trách nhiệm về bản thân của các người. Dẫu cho ai đó có lạc đạo đi nữa thì việc đó chẳng hại gì đến các người miễn sao các người đi đúng đường là đủ. Tất cả các người sẽ về trình diện Allah trở lại, xong Ngài sẽ cho các người biết về mọi điều các người đã từng làm.');
INSERT INTO "hasan_abdulkarim" VALUES(5,106,'5:106','Hỡi những ai có đức tin! Khi một người (Muslim) của cộng đồng các ngươi sắp từ trần trong lúc y muốn lập di chúc thì hai người công minh của các ngươi hoặc người khác (không thuộc cộng đồng của các ngươi) sẽ đứng ra làm chứng nếu các ngươi đang đi xa trên trái đất và các ngươi không thể tránh khỏi cái chết. Nếu các ngươi nghi ngờ (lòng thành thật của họ) thì hãy giữ hai người làm chứng lại sau cuộc lễ Salah và yêu cầu họ thề nhân danh Allah nói như sau: “Chúng tôi không bán lời khai của chúng tôi bằng một giá tiền nào dẫu cho người thụ hưởng là người bà con gần của chúng tôi và chúng tôi sẽ không giấu giếm bằng chứng mà chúng tôi đã cam kết với Allah bởi vì trong trường hợp đó chúng tôi sẽ là những kẻ tội lỗi.”');
INSERT INTO "hasan_abdulkarim" VALUES(5,107,'5:107','Nhưng nếu khám phá ra việc hai người làm chứng đó phạm tội (khai man) thì hai người khác chọn trong số bà con gần nhất có quyền đòi hỏi việc hưởng gia tài của người chết, đứng ra thế chỗ của hai người phạm tội trên, và yêu cầu họ thề nhân danh Allah, như sau: “Chúng tôi xác nhận lời khai của chúng tôi thật hơn lời khai của hai người trước và chúng tôi không vi phạm sự thật bởi vì trong trường hợp đó chúng tôi sẽ là những kẻ làm điều sai quấy.”');
INSERT INTO "hasan_abdulkarim" VALUES(5,108,'5:108','Hy vọng rằng cách đó sẽ làm cho lời khai của họ xác thực hoặc ít ra làm cho họ sợ việc có thể lời khai sau này sẽ được chấp nhận và bác bỏ lời khai trước của họ. Nhưng hãy sợ Allah và chịu nghe (lệnh của Ngài) bởi vì Allah sẽ không dẫn dắt đám người bất tuân hay chống đối.');
INSERT INTO "hasan_abdulkarim" VALUES(5,109,'5:109','Vào một Ngày Allah sẽ tập họp tất cả các Sứ Giả (của Ngài) xong Ngài hỏi Họ: “Đâu là điều mà các ngươi đã được trả lời?” Họ đồng thưa: “Chúng tôi không biết gì. Quả thật, Ngài, chỉ riêng Ngài biết điều vô hình (bí mật).”');
INSERT INTO "hasan_abdulkarim" VALUES(5,110,'5:110','Lúc đó Allah sẽ phán: “Hỡi Ysa, con trai của Maryaml Hãy nhớ Ân huệ của TA đã ban cho Ngươi và Mẹ của Ngươi khi TA đã hỗ trợ Ngươi với Ruh al-Qudus (Thiên Thần Jibril) để Ngươi có thể nói chuyện được với thiên hạ lúc ấu thơ và lúc trưởng thành; và việc TA đã dạy Ngươi Kinh Sách và Lẽ thông thái khôn ngoan và Kinh Tawrah và Kinh Injil và việc Ngươi đã lấy đất sét nắn thành hình một con chim theo phép của TA, xong Ngươi hà hơi vào nó khiến nó thành một con chim thật sự với sự chấp thuận của TA; và việc Ngươi chữa lành người mù bẩm sinh và người mang bệnh cùi theo sự chấp thuận của TA; và việc Ngươi làm cho người chết sống lại với sự chấp thuận của TA; và việc TA cản con cháu của Israel không cho sát hại Ngươi khi Ngươi trưng các bằng chứng rõ rệt cho họ thấy bởi vì những kẻ không có lòng tin trong bọn họ lên tiếng: “Đây chỉ là trò ảo thuật hiển hiện!”');
INSERT INTO "hasan_abdulkarim" VALUES(5,111,'5:111','Và lúc TA mặc khải cho các tông đồ (của Ysa) phán bảo: “Hãy tin tưởng nơi TA và các Sứ Giả của TA,” họ đồng cầu nguyện: “Bầy tôi đã tin tưởng. Xin Ngài chứng thực giùm bầy tôi là những người Muslim.”');
INSERT INTO "hasan_abdulkarim" VALUES(5,112,'5:112','(Hãy nhớ) lúc các tông đồ lên tiếng, thưa: “Hỡi Ysa, con trai của Maryarn, có thể nào Thượng Đế (Allah) của ngài gởi từ trên trời xuống cho chúng tôi một chiếc bàn đầy thực phẩm?” (Ysa) bảo: “Hãy sợ Allah nếu các ngươi là những người có đức tin.”');
INSERT INTO "hasan_abdulkarim" VALUES(5,113,'5:113','(Các tông đồ) thưa: “Chúng tôi chỉ ao ước được ăn thực phẩm của (chiếc bàn ấy) để cho thỏa lòng khao khát của chúng tôi và để cho chúng tôi biết rằng ngài đã nói sự thật với chúng tôi và để cho chúng tôi trở thành nhân chứng cho nó.”');
INSERT INTO "hasan_abdulkarim" VALUES(5,114,'5:114','Ysa, con trai của Maryam liền cầu nguyện: “Lạy Allah, Thượng Đế chúng tôi! Xin Ngài gởi từ trên trời xuống cho chúng tôi một chiếc bàn đầy thực phẩm để làm một bàn tiệc mừng cho bầy tôi - cho người đầu tiên và cho người cuối cùng của chúng tôi - và là một Phép mầu từ Ngài; và xin Ngài cung dưỡng chứng tôi bởi vì Ngài là Đấng Cung Dưỡng Ưu việt.”');
INSERT INTO "hasan_abdulkarim" VALUES(5,115,'5:115','Allah phán: “TA sẽ gởi nó xuống cho các ngươi. Nhưng nếu ai trong các ngươi không tin tưởng sau đó thì sẽ bị TA phạt bằng một hình phạt mà TA chưa hề dùng để phạt một người nào khác trong thiên hạ.”');
INSERT INTO "hasan_abdulkarim" VALUES(5,116,'5:116','Và (hãy nhớ) lúc Allah sẽ bảo: Hỡi Ysa, con trai của Maryaml Có phải Ngươi đã nói với nhân loại: “Hãy nhận Ta và Mẹ của Ta làm hai Thượng Đế bên cạnh Allah? (Ysa) sẽ thưa: “Ngài thật quang vinh và trong sạch! Bề tôi không bao giờ có thể nói được điều mà bề tôi không có quyền nói. Nếu bề tôi có nói ra điều đó thì chắc chắn Ngài đã biết nó. Ngài biết hết mọi điều trong tâm tư của bề tôi trong lúc bề tôi không biết một tí gì nơi Ngài. Quả thật, Ngài, chỉ riêng Ngài thôi mới biết rõ điều vô hình.');
INSERT INTO "hasan_abdulkarim" VALUES(5,117,'5:117','“Bề tôi chỉ nói với họ điều nào mà Ngài đã ra lệnh cho bề tôi (đó là:) ''Hãy thờ phụng Allah, Thượng Đế (Đấng Chủ Tể) của Ta và là Thượng Đế của các ngươi.'' Và bề tôi là một nhân chứng đối với họ trong suốt thời gian bề tôi còn sống với họ; bởi thế khi Ngài đưa bề tôi lên cùng với Ngài, thì Ngài là Đấng Trông chừng họ và Ngài làm chứng cho tất cả mọi việc.');
INSERT INTO "hasan_abdulkarim" VALUES(5,118,'5:118','“Nếu Ngài trừng phạt họ thì bề nào họ vẫn là bầy tôi của Ngài; và nếu Ngài tha thứ cho họ thì quả thật Ngài vẫn là Đấng Toàn Năng, Đấng Sáng suốt.”');
INSERT INTO "hasan_abdulkarim" VALUES(5,119,'5:119','Allah sẽ phán: “Đây là Ngày mà người chân thật sẽ hưởng kết quả về sự trung thực của mình: họ sẽ được hưởng các Ngôi vườn (thiên đàng) bên dưới có các dòng sông chảy mà họ sẽ vào đó sống đời đời. Allah sẽ hài lòng với họ và họ sẽ toại nguyện với Ngài. Đó là một thắng lợi vĩ đại.');
INSERT INTO "hasan_abdulkarim" VALUES(5,120,'5:120','Allah nắm quyền thống trị các tầng trời và trái đất và vạn vật giữa trời đất. Và Ngài là Đấng Toàn Năng trên mọi thứ.');
INSERT INTO "hasan_abdulkarim" VALUES(6,1,'6:1','Mọi lời ca tụng đều dâng lên Allah, Đấng đã tạo các tầng trời và trái đất và đã làm ra cái u tối và ánh sáng, thế mà những kẻ phủ nhận đức tin lại dựng những đối tượng ngang vai với (Allah) của họ.');
INSERT INTO "hasan_abdulkarim" VALUES(6,2,'6:2','Ngài là Đấng đã tạo các ngươi từ đất sét rồi qui định (cho mỗi người) một thời hạn (tuổi thọ). Nhưng Ngài còn giữ nơi Ngài một hạn kỳ khác. Rồi các ngươi sinh lòng nghi ngờ (về sự phục sinh).');
INSERT INTO "hasan_abdulkarim" VALUES(6,3,'6:3','Và Ngài là Allah (đang an vị) trong các tầng trời và trái đất. Ngài biết điều bí mật và điều công khai của các ngươi. Và Ngài biết điều (tốt và xấu) mà các ngươi đã làm ra.');
INSERT INTO "hasan_abdulkarim" VALUES(6,4,'6:4','Và không một dấu hiệu nào trong các dấu hiệu của Thượng Đế của họ đến với họ mà họ lại không quay mặt làm ngơ.');
INSERT INTO "hasan_abdulkarim" VALUES(6,5,'6:5','Bởi thế, chắc chắn họ phủ nhận Sự Thật khi nó đến với họ. Nhưng họ sẽ sớm biết tin tức về những điều mà họ thường chế giễu.');
INSERT INTO "hasan_abdulkarim" VALUES(6,6,'6:6','Há họ không thấy việc TA (Allah) đã hủy diệt trước họ bao nhiêu thế hệ mà TA đã định cư trên trái đất hay sao? TA đã định cư họ trên trái đất không như TA đã định cư các ngươi. TA đã ban nước mưa xối xả từ trên trời xuống cho họ và tạo các dòng sông chảy bên dưới họ, nhưng TA đã tiêu diệt họ vì những tội lỗi của họ và đã cho sinh sản những thế hệ khác (để thay thế họ).');
INSERT INTO "hasan_abdulkarim" VALUES(6,7,'6:7','Nếu TA (Allah) có ban xuống cho Ngươi một Kinh Sách (viết) trên các miếng da thuộc mà họ có thể sờ với bàn tay của họ thì chắc chắn những kẻ không có lòng tin sẽ nói: “Đây chỉ là một trò ảo thuật hiển hiện.”');
INSERT INTO "hasan_abdulkarim" VALUES(6,8,'6:8','Và họ nói: “Tại sao không phái một Thiên Thần xuống cho Y (Muhammad)?” Và nếu TA phái một Thiên Thần xuống thì vấn đề đã được giải quyết xong rồi đâu có còn việc gia hạn cho họ.');
INSERT INTO "hasan_abdulkarim" VALUES(6,9,'6:9','Và nếu TA có làm cho Y (Sứ Giả) thành một Thiên Thần thì TA vẫn phải làm cho Y thành một người phàm. Và TA làm cho họ lúng túng thêm về điều đang làm cho họ lúng túng.');
INSERT INTO "hasan_abdulkarim" VALUES(6,10,'6:10','Và chắc chắn các Sứ Giả trước Ngươi (Muhammad) đã bị chế giễu nhưng chung cuộc điều mà họ thường chế giễu sẽ bao vây những ai đã chế giễu lại trong đó.');
INSERT INTO "hasan_abdulkarim" VALUES(6,11,'6:11','Hãy bảo họ: “Hãy đi du lịch trên trái đất rồi xét xem kết cuộc của những kẻ phủ nhận Chân Lý đã xảy ra như thế nào.”');
INSERT INTO "hasan_abdulkarim" VALUES(6,12,'6:12','- Hãy hỏi họ: “Mọi vật trong các tầng trời và trái đất là của ai?” (Nếu họ không trả lời thì) hãy bảo họ: “Của Allah” Ngài tự qui định cho mình lòng Khoan dung. Mục đích để Ngài sẽ tập trung các ngươi trở lại vào Ngày phục sinh, (điều đó) không có gì phải ngờ vực cả. Chỉ những ai đánh mất linh hồn mới không tin tưởng.');
INSERT INTO "hasan_abdulkarim" VALUES(6,13,'6:13','Mọi vật ẩn mình vào ban đêm và ban ngày đều là của Allah cả. Và Ngài là Đấng Hằng Nghe và Hằng Biết (mọi vật).');
INSERT INTO "hasan_abdulkarim" VALUES(6,14,'6:14','Hãy bảo họ: “Há Ta sẽ phải nhận ai khác làm Đấng Bảo Hộ ngoài Allah, Đấng đã sáng tạo các tầng trời và trái đất hay sao? Và Ngài là Đấng Nuôi Dưỡng (vạn vật) chứ không được ai nuôi. Hãy bảo họ: “Ta (Muhammad) được lệnh phải là người đầu tiên thần phục Allah (trong lslam) và không được là một người thờ đa thần”.');
INSERT INTO "hasan_abdulkarim" VALUES(6,15,'6:15','Hãy bảo họ: "Ta sợ hình phạt vào một Ngày (Xét xử) Vĩ Đại nếu Ta bất tuân Thượng Đế (Allah) của Ta".');
INSERT INTO "hasan_abdulkarim" VALUES(6,16,'6:16','Vào Ngày đó, ai không bị phạt thì chắc chắn là được (Allah) khoan dung. Và đó là một sự thành tựu rạng rỡ.');
INSERT INTO "hasan_abdulkarim" VALUES(6,17,'6:17','Và nếu Allah giáng hình phạt lên ngươi (hỡi người) thì ngoài Ngài không ai có thể thu nó lại được; và nếu Ngài ban cho ngươi điều lành thì Ngài là Đấng Toàn Năng trên tất cả mọi thứ.');
INSERT INTO "hasan_abdulkarim" VALUES(6,18,'6:18','Và Ngài là Đấng Tối Thượng ở bên trên các bầy tôi của Ngài; và Ngài là Đấng Rất Mực Sáng Suốt, Đấng Am tường mọi việc (vật).');
INSERT INTO "hasan_abdulkarim" VALUES(6,19,'6:19','Hãy bảo họ: “Cái gì (có giá trị) lớn nhất trong bằng chứng?” Hãy bảo họ: “(Việc) Allah làm chứng giữa Ta và các người (là lớn nhất.) Và Qur’an này đã được mặc khải cho Ta hầu dùng cảnh cáo các người và bất cứ ai mà nó có thể tiếp xúc. Phải chăng các người đã chứng nhận có những thần linh bên cạnh Allah hay sao?” Hãy bảo họ: “Ta không thể xác nhận như thế được.” Hãy bảo họ: “Chỉ Ngài (Allah) là Thượng Đế Duy nhất. Và Ta vô can về những điều mà các người đã tổ hợp với Ngài.”');
INSERT INTO "hasan_abdulkarim" VALUES(6,20,'6:20','Những ai đã được TA (Allah) ban cho Kinh Sách đều nhìn nhận Y (là Sứ Giả của Allah) giống như việc họ nhìn nhận con cái của họ vậy; những ai đánh mất linh hồn mới không tin ấy thôi.');
INSERT INTO "hasan_abdulkarim" VALUES(6,21,'6:21','Còn ai sai quấy hơn kẻ đã đặt điều nói dối rồi đổ thừa cho Allah hoặc phủ nhận các Lời Mặc Khải của Ngài? Và thật sự những kẻ làm điều sai quấy sẽ không bao giờ phát đạt.');
INSERT INTO "hasan_abdulkarim" VALUES(6,22,'6:22','Và vào một Ngày TA (Allah) sẽ tập trung tất cả bọn chúng trở lại rồi TA sẽ hỏi những ai đã tổ hợp thần linh (cùng với TA): “Đâu là các thần linh của các ngươi mà các ngươi đã xác nhận?”');
INSERT INTO "hasan_abdulkarim" VALUES(6,23,'6:23','Rồi chúng không nại được lý do nào để chạy tội ngoài lời xin xỏ này: “Xin thề với Allah, Thượng Đế của chúng tôi, chúng tôi không phải là những kẻ thờ đa thần.”');
INSERT INTO "hasan_abdulkarim" VALUES(6,24,'6:24','Thấy không! Chúng đã tự dối lòng mình như thế nào! Và điều gian dối mà chúng đã bịa đặt ra đã bỏ chúng đi biệt dạng.');
INSERT INTO "hasan_abdulkarim" VALUES(6,25,'6:25','Và trong bọn chúng có một số (giả vờ) nghe Ngươi đọc. Nhưng TA (Allah) lấy tấm màn bao kín quả tim của chúng lại đến nỗi chúng không hiểu Nó (Qur''an); và chúng cảm thấy nặng tai; và nếu chúng có thấy từng dấu hiệu nào, chúng cũng sẽ không tin tưởng. (Tệ) đến mức khi đến tiếp xúc với Ngươi, chúng chỉ muốn gây sự với Ngươi; những kẻ không có đức tin thường nói: “(Qur''an) này chẳng qua là chuyện cổ tích của người cổ xưa!”');
INSERT INTO "hasan_abdulkarim" VALUES(6,26,'6:26','Và chúng cấm người khác nghe Nó (Qur''an) còn chúng thì dang ra xa. Và làm thế, chúng chỉ tự hủy hoại bản thân (linh hồn) của chúng nhưng chúng không nhận thấy điều đó.');
INSERT INTO "hasan_abdulkarim" VALUES(6,27,'6:27','Và nếu Ngươi có thể thấy được cảnh chúng sẽ bị bắt đứng trên Lửa (của hỏa ngục), chúng sẽ than: “Thật khổ thân chúng tôi! Nếu chúng tôi được trở về trần gian thì (lần này) chúng tôi sẽ không phủ nhận các dấu hiệu của Thượng Đế của chúng tôi và chúng tôi sẽ trở thành những người tin tưởng.”');
INSERT INTO "hasan_abdulkarim" VALUES(6,28,'6:28','Không, những điều mà chúng đã từng giấu giếm trước đây sẽ hiện rõ cho chúng thấy. Và nếu chúng được đưa trở về trần gian thì chắc chắn chúng cũng sẽ tái phạm điều đã từng bị cấm, bởi vì quả thật chúng là những kẻ nói dối.');
INSERT INTO "hasan_abdulkarim" VALUES(6,29,'6:29','Và (đôi lúc) chúng nói: “Quả thật, không có đời sống nào khác ngoài đời sống trần gian này và chúng tôi sẽ không được phục sinh trở lại.”');
INSERT INTO "hasan_abdulkarim" VALUES(6,30,'6:30','Và nếu Ngươi có thể thấy được cảnh chúng sẽ bị bắt đứng trước mặt Thượng Đế (Allah) của chúng, Ngài sẽ phán: “Há điều này không thực ư?” Chúng sẽ thưa: “Đúng là sự thật, lạy Thượng Đế của bầy tôi!” Ngài sẽ phán: “Thế thì hãy nếm sự trừng phạt vì tội các ngươi đã phủ nhận đức tin.”');
INSERT INTO "hasan_abdulkarim" VALUES(6,31,'6:31','Chắc chắn sẽ thua thiệt đối với những ai phủ nhận việc gặp gỡ Allah (ở đời sau) mãi cho đến lúc Giờ (tận thế) đột ngột xảy đến cho họ thì họ mới than: “Thật khổ thân chúng tôi về điều chúng tôi thường lơ đễnh,” và họ mang trên lưng gánh nặng (tội lỗi) của họ. Há gánh nặng (tội lỗi) không tồi tệ hay sao?');
INSERT INTO "hasan_abdulkarim" VALUES(6,32,'6:32','Và cuộc sống trần tục này chỉ là trò chơi và giải trí. Và chắc chắn nhà ở nơi cõi đời sau sẽ tốt nhất cho những ai sợ Allah. Thế các ngươi không hiểu điều đó sao?');
INSERT INTO "hasan_abdulkarim" VALUES(6,33,'6:33','TA (Allah) biết lời họ nói ra làm cho Ngươi đau buồn. Bởi vì thật ra họ không cho Ngươi nói dối; ngược lại, những kẻ làm điều sai quấy cãi lại các Lời Mặc Khải của Allah thôi.');
INSERT INTO "hasan_abdulkarim" VALUES(6,34,'6:34','Và chắc chắn các Sứ Siả trước Ngươi đã từng bị phủ nhận. Nhưng Họ đã kiên nhẫn chịu đựng về những điều đã bị bài bác và sự ngược đãi cho đến khi sự cứu trợ của TA đã đến với Họ. Và không ai có thể làm thay đổi được Lời phán (và Quyết định) của Allah. Và chắc chắn Ngươi đã tiếp thu được tin tức về các Sứ Giả (tiền nhiệm trước Ngươi).');
INSERT INTO "hasan_abdulkarim" VALUES(6,35,'6:35','Và nếu Ngươi cảm thấy khó khăn trước sự cự tuyệt của họ thì với khả năng của Ngươi, hãy tìm một cái lỗ chui xuống dưới đất hoặc bắt một cái thang đi lên trời để mang xuống cho họ một dấu lạ (để thuyết phục họ.) Và nếu Allah muốn, Ngài sẽ thừa sức tập trung họ lại và hướng dẫn họ theo Chính Đạo. Bởi thế, Ngươi chớ cư xử như một người ngu muội (đâm ra thối chí và mất kiên nhẫn).');
INSERT INTO "hasan_abdulkarim" VALUES(6,36,'6:36','Chỉ những ai thành tâm nghe (Qur''an) mới đáp lại (Lời gọi của Allah). Còn đối với người chết, Allah sẽ dựng họ sống lại rồi họ sẽ được đưa trở về trình diện Ngài.');
INSERT INTO "hasan_abdulkarim" VALUES(6,37,'6:37','Và họ nói như sau: “Tại sao không có một Phép lạ nào từ Thượng Đế của Y (Muhammad) được ban xuống cho Y?” Hãy bảo họ: “Thật ra Allah thừa sức ban xuống một Phép lạ nhưng đa số bọn họ không biết (Phép lạ đó ngụ ý gì.)”');
INSERT INTO "hasan_abdulkarim" VALUES(6,38,'6:38','Và không có một loại thú vật nào (sống) trên trái đất cũng không có một loại chim chóc nào bay được bằng hai cánh mà không sống (kết đoàn) thành cộng đồng giống như các ngươi. TA (Allah) đã không bỏ sót một điều nào trong quyển sổ (Định Luật) rồi cuối cùng chúng sẽ được đưa về gặp Thượng Đế (Allah) của chúng trở lại.');
INSERT INTO "hasan_abdulkarim" VALUES(6,39,'6:39','Và những ai phủ nhận các dấu hiệu của TA (Allah) thì (chẳng khác nào) như kẻ điếc và câm, và chìm sâu trong tăm tối. Allah đánh lạc hướng kẻ nào Ngài muốn và dắt đi đúng đường người nào Ngài muốn.');
INSERT INTO "hasan_abdulkarim" VALUES(6,40,'6:40','Hãy hỏi họ: “Các người hãy suy nghĩ cho kỹ và trả lời cho thật rằng nếu hình phạt của Allah hoặc Giờ (Tận thế) bất ngờ xảy đến cho các người thì ngoài Allah, ai là Đấng mà các người sẽ gọi cầu cứu?”');
INSERT INTO "hasan_abdulkarim" VALUES(6,41,'6:41','Không, chỉ một mình Ngài các ngươi van cầu; và nếu Ngài muốn, Ngài sẽ bốc đi khỏi các ngươi điều (nguy khốn) mà các ngươi van xin Ngài và các ngươi sẽ quên mất hết điều (hay thần linh) mà các ngươi đã tổ hợp với Ngài.');
INSERT INTO "hasan_abdulkarim" VALUES(6,42,'6:42','Và chắc chắn trước Ngươi, TA (Allah) đã cử phái các Sứ Giả đến với các cộng đồng và TA đã phạt các cộng đồng đó với nỗi khó khăn và buồn phiền mục đích để cho họ hạ mình khiêm tốn.');
INSERT INTO "hasan_abdulkarim" VALUES(6,43,'6:43','Nhưng tại sao họ không chịu hạ mình thần phục khi họ gặp nỗi buồn phiền mà TA giáng lên họ; ngược lại, trái tim của họ chai đi và Shaytan đã làm cho hành động (tội lỗi) của họ tỏ ra hoa mỹ đối với họ.');
INSERT INTO "hasan_abdulkarim" VALUES(6,44,'6:44','Bởi thế, khi họ quên bẳng lời cảnh cáo mà họ đã nhận, TA (Allah) mở toang cho họ mọi cánh cửa (phồn vinh) về mọi vấn đề cho đến lúc họ chỉ biết khoe khoang về các tiện nghi mà họ đã được ban cho thì lúc đó TA bắt phạt họ một cách bất ngờ khiến họ lặng câm tuyệt vọng.');
INSERT INTO "hasan_abdulkarim" VALUES(6,45,'6:45','Gốc rễ của những kẻ làm điều sai quấy vì thế bị cắt đứt hết. Và mọi ca tụng đều thuộc về Allah, Thượng Đế của vũ trụ và muôn loài.');
INSERT INTO "hasan_abdulkarim" VALUES(6,46,'6:46','Hãy bảo họ: “Các người hãy nghĩ kỹ xem, nếu Allah lấy mất đi thính giác (cái nghe) và thị giác (cái thấy) của các người và niêm kín quả tim (tấm lòng) của các người lại thì ai là Thượng Đế ngoài Allah có thể phục hồi chúng cho các người? Xem đó, TA đã giải thích rõ ràng các Lời Mặc Khải cho họ như thế nào, vậy mà họ vẫn quay bỏ đi.');
INSERT INTO "hasan_abdulkarim" VALUES(6,47,'6:47','Hãy bảo họ: “Các người hãy nghĩ kỹ xem, nếu hình phạt của Allah hoặc bất ngờ hoặc công khai xảy đến cho các người thì chỉ đám người làm điều sai quấy mới bị tiêu diệt thôi hay sao?');
INSERT INTO "hasan_abdulkarim" VALUES(6,48,'6:48','TA (Allah) đã cử các Sứ Giả đến chỉ để làm công tác báo trước (tin lành) và cảnh cáo (về tin dữ); bởi thế ai tin tưởng và sửa mình thì sẽ không lo sợ và cũng sẽ không buồn phiền.');
INSERT INTO "hasan_abdulkarim" VALUES(6,49,'6:49','Ngược lại, những ai phủ nhận các dấu hiệu của TA thì sẽ bị trừng phạt vì tội bất tuân và đã vượt quá mức giới hạn.');
INSERT INTO "hasan_abdulkarim" VALUES(6,50,'6:50','Hãy bảo họ: “Ta đã không nói với các ngươi rằng Ta giữ các kho tàng của Allah, và Ta cũng không biết điều vô hình và Ta cũng không nói với các ngươi Ta đây là một Thiên Thần. (Ta đã nói), ‘Ta chỉ tuân theo điều (lệnh) đã được mặc khải cho Ta". Hãy hỏi họ: “Há người mù và người sáng mắt bằng nhau hay sao? Thế tại sao các người không chịu suy ngẫm (về điều đó).”');
INSERT INTO "hasan_abdulkarim" VALUES(6,51,'6:51','Và hãy dùng Nó (Qur''an) để cảnh cáo những ai sợ việc sẽ bị tập trung trước mặt Thượng Đế (Allah) của họ, (rằng) sẽ không có một người bảo vệ hay một người can thiệp nào ngoài Ngài có thể giúp đỡ họ được. Mục đích để cho họ sợ Allah mà giữ mình khỏi phạm tội.');
INSERT INTO "hasan_abdulkarim" VALUES(6,52,'6:52','Và không được xua đuổi những ai cầu nguyện Thượng Đế của họ vào buổi sáng và buổi tối, mong gặp mặt Ngài. Ngươi không chịu tội giùm cho họ về bất cứ điều gì và họ cũng không chịu tội giùm cho Ngươi về bất cứ điều gì; bởi thế nếu Ngươi xua đuổi họ thì Ngươi sẽ trở thành một người làm điều sai quấy.');
INSERT INTO "hasan_abdulkarim" VALUES(6,53,'6:53','Và TA (Allah) đã dùng một số người này để thử thách một số người kia để họ có dịp nói: “Phải chăng những người này đã được Allah đặc ân trong bọn mình?” Há Allah không biết ai là người biết ơn hay sao?');
INSERT INTO "hasan_abdulkarim" VALUES(6,54,'6:54','Và khi những ai tin tưởng nơi các dấu hiệu của TA (Allah) đến gặp Ngươi thì hãy chào họ, bảo: “Mong các người được bằng an! Thượng Đế (Allah) của các người đã tự qui định cho mình lòng khoan dung tha thứ: rằng quả thật ai vì ngu muội mà lỡ lầm phạm tội rồi ăn năn sám hối và sửa mình sau đó thì quả thật Ngài (Allah) Hằng Tha Thứ, Rất Mực Khoan Dung.');
INSERT INTO "hasan_abdulkarim" VALUES(6,55,'6:55','Và TA (Allah) đã trình bày các Lời Mặc Khải đúng như thế để cho con đường của những kẻ tội lỗi được phơi bày ra công khai.');
INSERT INTO "hasan_abdulkarim" VALUES(6,56,'6:56','Hãy bảo họ: ‘Ta bị cấm tôn thờ những kẻ mà các người cầu nguyện ngoài Allah.” Hãy bảo tiếp: "Ta sẽ không làm theo ý muốn riêng tư của các người. Nếu Ta nghe theo các người thì chắc chắn Ta sẽ lạc đạo và Ta sẽ không còn là một người được hướng dẫn nữa.”');
INSERT INTO "hasan_abdulkarim" VALUES(6,57,'6:57','Hãy bảo họ: “Ta dựa vào một bằng chứng rõ rệt từ Thượng Đế (Allah) của Ta nhưng các người lại phủ nhận nó. Cái (hình phạt) mà các người muốn giục nó xảy ra tức khắc không nằm nơi Ta. Mọi phán quyết đều thuộc về Allah cả. Ngài tuyên bố sự thật. Và Ngài là Đấng Quyết Định Ưu Việt.”');
INSERT INTO "hasan_abdulkarim" VALUES(6,58,'6:58','Hãy bảo họ: “Nếu cái (hình phạt) mà các người thúc giục cho mau xảy đến nằm nơi Ta thì việc tranh chấp (phải trái) giữa Ta với các người đã được giải quyết xong rồi. Nhưng Allah biết rõ những kẻ làm điều sai quấy.”');
INSERT INTO "hasan_abdulkarim" VALUES(6,59,'6:59','Mọi chìa khóa (hay kho tàng) của cõi vô hình đều nằm nơi Ngài (Allah), chỉ riêng Ngài biết rõ chúng. Bởi vì Ngài biết rõ mọi vật trên đất liền và dưới biển cả. Và không một chiếc lá nào rụng mà Ngài không biết cũng không một hạt giống nào chôn sâu dưới cái u tối của đất đai mà Ngài không biết; cũng không có cái xanh tươi hay cái khô héo nào mà lại không được ghi sẵn trong một quyển sổ (định mệnh) rõ ràng.');
INSERT INTO "hasan_abdulkarim" VALUES(6,60,'6:60','Và Ngài (Allah) là Đấng cho bắt hồn của các ngươi vào ban đêm và Ngài biết rõ điều (tốt và xấu) mà các ngươi đã làm ban ngày. Rồi vào ban ngày Ngài đánh thức các ngươi dậy trở lại để cho các ngươi sống hết tuổi thọ ấn định; rồi cuối cùng các ngươi sẽ được đưa về giao lại cho Ngài rồi Ngài sẽ báo cho các ngươi biết về mọi điều các ngươi đã làm.');
INSERT INTO "hasan_abdulkarim" VALUES(6,61,'6:61','Và Ngài (Allah) là Đấng Tối Thượng chi phối bầy tôi của Ngài. Và Ngài cử các vị Canh gác (Thiên Thần) theo trông chừng các ngươi cho đến khi một trong các ngươi đối diện với cái chết thì các Thiên Thần Sứ Giả của TA sẽ bắt hồn y và chúng sẽ không chểnh mảng trong công tác (bắt hồn).');
INSERT INTO "hasan_abdulkarim" VALUES(6,62,'6:62','Rồi (tất cả các linh hồn) sẽ được đưa trở về trình diện Allah, Đấng Chủ Nhân thật sự của họ. Chắc chắn Ngài nắm mọi phán quyết và rất nhanh trong việc xét xử.');
INSERT INTO "hasan_abdulkarim" VALUES(6,63,'6:63','Hãy bảo họ: “Ai đã cứu các người ra khỏi chỗ u tối nguy hiểm của đất liền và biển cả khi các người khiêm tốn và kín đáo van vái Ngài cứu mạng. (Các người đã nguyện trong lòng:) “Nếu Ngài cứu chúng tôi được thoát hiểm thì chắc chắn chúng tồi sẽ là những người biết ơn?”');
INSERT INTO "hasan_abdulkarim" VALUES(6,64,'6:64','Hãy bảo họ: “Chính Allah đã cứu các người ra khỏi các chỗ nguy hiểm đó và khỏi mọi họan nạn khác rồi các ngươi lại tổ hợp kẻ khác cùng với Ngài (trong việc thờ phụng).');
INSERT INTO "hasan_abdulkarim" VALUES(6,65,'6:65','Hãy bảo họ: Ngài (Allah) có toàn quyền gởi thiên tai từ trên cao xuống hoặc từ dưới chân lên cho các người và bao phủ các người với nạn xung đột bè phái và làm cho các người nếm mùi tân khổ của sự sát phạt lẫn nhau.” Hãy xem, TA đã giải thích các Lời Mặc Khải bằng nhiều cách để may ra các người có thể hiểu được (Thông điệp).');
INSERT INTO "hasan_abdulkarim" VALUES(6,66,'6:66','Và người dân của Y phủ nhận Nó (Qur’an) trong lúc Nó là Chân Lý. Hãy bảo họ: "Ta không phải là người thọ lãnh (trách nhiệm) cho các người".');
INSERT INTO "hasan_abdulkarim" VALUES(6,67,'6:67','Mỗi lời cáo thị đều cần một thời gian ấn định (để cho nó thể hiện). Rồi đây các ngươi sẽ sớm biết (sự thật).');
INSERT INTO "hasan_abdulkarim" VALUES(6,68,'6:68','Và khi Ngươi thấy những kẻ đâm đầu cãi nhau một cách vô ích về các Lời Mặc Khải của TA, hãy lánh xa họ cho đến khi họ đổi sang câu chuyện khác. Nếu Shaytan làm cho Ngươi quên việc đó thì sau khi nhớ lại, chớ ngồi chung với những kẻ làm điều sai quấy.');
INSERT INTO "hasan_abdulkarim" VALUES(6,69,'6:69','Và mặc dầu những ai sợ Allah không chịu trách nhiệm về hành động của họ nhưng hãy nhắc nhở họ để may ra họ sẽ trở thành người ngay chính sợ Allah.');
INSERT INTO "hasan_abdulkarim" VALUES(6,70,'6:70','Và chớ bận tâm về những kẻ đã lợi dụng tôn giáo của họ để làm một trò chơi và giải trí bởi vì cuộc sống trần tục này đã lừa dối họ. Nhưng hãy dùng Nó (Qur''an) mà nhắc nhở họ bởi vì một linh hồn có thể tự hủy về những tội lỗi mà nó đã phạm. Nó sẽ thấy rằng chẳng có một người bảo hộ hay một người can thiệp nào ngoài Allah có thể giúp đỡ nó nổi. Và nếu nó có dâng mọi thứ tương đương (với tội) để xin chuộc mạng thì cũng sẽ không được chấp nhận từ nó. Những người này là những kẻ đã tự hủy hoại bản thân mình vì những tội lỗi mà họ đã làm. Họ sẽ được cho uống một loại nước sôi nóng bỏng và bị trừng phạt đau đớn vì tội tiếp tục không tin tưởng.');
INSERT INTO "hasan_abdulkarim" VALUES(6,71,'6:71','Hãy bảo họ: “Há chúng tôi sẽ phải cầu nguyện ngoài Allah những vật không làm lợi cũng chẳng hãm hại được chúng tôi và chúng tôi sẽ phải quay gót bỏ đạo sau khỉ Allah đã hướng dẫn chúng tôi hay sao? Giống như kẻ đã bị các tên Shaytàn lừa gạt và dắt đi lang thang ngơ ngác trên trái đất, bạn bè của hắn gọi hắn đến với Chỉ Đạo, nói: “Hãy đến với chúng tôi.” Hãy bảo họ: “Chỉ Đạo của Allah là Chỉ Đạo đúng đắn nhất. Và chúng tôi được lệnh phải nạp mình thần phục Đấng Chủ Tể (Thượng Đế) của vu trụ và muôn loài;');
INSERT INTO "hasan_abdulkarim" VALUES(6,72,'6:72','“Và phải dâng lễ nguyện Salah một cách chu đáo và sợ Ngài,” bởi vì Ngài là Đấng mà các người sẽ được tập trung đưa về trình diện.');
INSERT INTO "hasan_abdulkarim" VALUES(6,73,'6:73','Và Ngài (Allah) là Đấng đã vì Chân Lý mà tạo ra các tầng trời và trái đất. Và vào Ngày mà Ngài sẽ phán: ''Hãy thành'' thì nó sẽ thành (theo Lời Ngài Phán). Lời Phán của Ngài là Sự Thật. Và Ngài nắm mọi quyền hành vào Ngày mà tiếng còi (phục sinh) sẽ được thổi lên. Ngài biết điều bí mật (vô hình) và điều công khai (hữu hình) bởi vì Ngài là Đấng Rất Mực Sáng Suốt, Đấng Am Tường.');
INSERT INTO "hasan_abdulkarim" VALUES(6,74,'6:74','Và (hãy nhớ) khi Ibrahim thưa với Azar, phụ thân của Y như sau: “Phải chăng cha đã nhận các hình tượng này làm những thần linh? Con thấy cha và người dân của cha lầm lạc rõ ràng.”');
INSERT INTO "hasan_abdulkarim" VALUES(6,75,'6:75','Và TA (Allah) đã chỉ cho Ibrahim thấy việc cai quản các tầng trời và trái đất để cho Y trở thành một người vững lòng tin.');
INSERT INTO "hasan_abdulkarim" VALUES(6,76,'6:76','Bởi thế, khi màn đêm bao phủ lấy Y, Y thấy một vì sao. Y thốt lên: “Đây là Thượng Đế (Đấng Chủ Tể) của ta.” Nhưng khi thấy sao lặn, Người tự bảo: “Ta không thích các vật hay lặn.”');
INSERT INTO "hasan_abdulkarim" VALUES(6,77,'6:77','Nhưng khi thấy mặt trăng ló lên sáng ngời, Y tự bảo: “Đây là Thượng Đế của ta.” Nhưng khi thấy vầng trăng lặn mất, Y tự bảo: “Nếu Thượng Đế của ta không hướng dẫn thì chắc chắn ta sẽ bị lầm lạc.”');
INSERT INTO "hasan_abdulkarim" VALUES(6,78,'6:78','Nhưng khi thấy mặt trời mọc lên rạng rỡ, Y tự bảo: “Đây là Thượng Đế của ta, vị này lớn nhất.” Nhưng khi thấy mặt trời lặn, Y lớn tiếng bảo: "Này hỡi dân ta! Ta tẩy chay những kẻ mà các người đã tôn thờ cùng chung với Allah".');
INSERT INTO "hasan_abdulkarim" VALUES(6,79,'6:79','“Ta thẳng thắn và thành thật hướng mặt của ta về Đấng đã sáng tạo các tầng trời và trái đất và Ta sẽ không là một người thờ đa thần”.');
INSERT INTO "hasan_abdulkarim" VALUES(6,80,'6:80','Và người dân của Y tranh luận với Y. Y bảo họ: “Phải chăng các người tranh luận với Ta về Allah bởi vì Ngài đã hướng dẫn Ta hay sao? Ta không sợ những kẻ mà các người tổ hợp với Ngài, trừ phi Thượng Đế (Allah) của Ta muốn một điều gì khác. Sự Hiểu biết của Thượng Đế của Ta bao la, bao quát hết mọi vật (việc.) Thế các ngươi chưa thức tỉnh hay sao?');
INSERT INTO "hasan_abdulkarim" VALUES(6,81,'6:81','"Và tại sao Ta phải sợ những kẻ (thần linh) mà các người tổ hợp với Allah trong lúc các người không sợ việc tổ hợp (thần linh) với Allah, một điều mà Allah không bao giờ chấp thuận cho các người làm. Thế giữa hai chúng ta, ai được bảo đảm an toàn nhất nếu các người biết?"');
INSERT INTO "hasan_abdulkarim" VALUES(6,82,'6:82','Những ai có đức tin và không trộn lẫn đức tin thuần túy của họ với điều sai trái (việc tôn thờ đa thần) thì là những người sẽ được an toàn nhất bởi vì họ được hướng dẫn đúng theo Chính Đạo.');
INSERT INTO "hasan_abdulkarim" VALUES(6,83,'6:83','Và đó là lập luận mà TA (Allah) đã ban cho Ibrahim để Y dùng đối đáp với người dân của Y. TA nâng cấp bậc cho người nào TA muốn bởi vì Thượng Đế của Ngươi (Muhammad) Rất Mực Sáng Suốt, Rất Mực Hiểu Biết.');
INSERT INTO "hasan_abdulkarim" VALUES(6,84,'6:84','Và TA (Allah) đã ban cho Y (Ibrahim đứa con trai) Is-haaq và (đứa cháu nội) Ya''qub. Tất cả đều được TA hướng dẫn. Và TA đã hướng dẫn Nuh trước đó, và (đã hướng dẫn) trong dòng dõi của Y (Ibrahim) Dawood, Sulayman, Ayyub, Yusuf, Musa và Harun. Và TA đã ân thưởng những người làm tốt đúng như thế.');
INSERT INTO "hasan_abdulkarim" VALUES(6,85,'6:85','Và (đã hướng dẫn) Zakariya và Yahya và Ysa và Ilyas. Tất cả đều là những người ngoan đạo.');
INSERT INTO "hasan_abdulkarim" VALUES(6,86,'6:86','Và (đã hướng dẫn) Isma''il và Al-Yasa'' và Yunus và Lut. Tất cả đã được TA đặc ân hơn thiên hạ.');
INSERT INTO "hasan_abdulkarim" VALUES(6,87,'6:87','Và trong số cha mẹ của họ, con cái của họ và anh em của họ, TA đã chọn và hướng dẫn họ theo Chính Đạo.');
INSERT INTO "hasan_abdulkarim" VALUES(6,88,'6:88','Đó là Chỉ Đạo của Allah mà Ngài đã dùng để hướng dẫn người nào Ngài muốn trong số bầy tôi của Ngài. Nhưng nếu họ tổ hợp (tôn thờ) những thần linh khác cùng với Allah (trong việc thờ phụng) thì tất cả việc làm của họ sẽ hoài công.');
INSERT INTO "hasan_abdulkarim" VALUES(6,89,'6:89','Họ là những người đã được TA (Allah) ban cho Kinh Sách, sự thông hiểu luật đạo và sứ mạng truyền giáo; nhưng nếu những người (hậu bối) này (của họ) phủ nhận sứ mạng đó thì chắc chắn TA sẽ giao nó cho một dân tộc khác không phủ nhận nó.');
INSERT INTO "hasan_abdulkarim" VALUES(6,90,'6:90','Họ là những người đã được Allah hướng dẫn. Bởi thế, Ngươi hãy tuân theo Chỉ Đạo của họ. Hãy bảo người dân: ‘Ta không đòi hỏi các người phần thưởng về Nó (Qur’an). Nó chỉ là một thông điệp nhắc nhở cho thiên hạ.”');
INSERT INTO "hasan_abdulkarim" VALUES(6,91,'6:91','Họ lượng giá Allah không đúng khi họ nói: “Allah đã không ban điều nào xuống cho người phàm cả.” Hãy bảo họ: "Thế ai ban Kinh Sách mà Musa đã mang đến như một ánh sáng và như một Chỉ Đạo cho nhân loại? (Kinh Sách) mà các người trình bày một phần trên các miếng da thuộc (để phô trương) trong lúc nhiều phần khác các người giấu đi. Và các người đã được dạy cho nhiều điều mà cả các người lẫn cha mẹ của các người đã không từng biết trước đó". Hãy bảo họ: “Allah (đã ban nó xuống).” Rồi để mặc họ vui đùa trong các câu chuyện tầm phào, vô bổ.');
INSERT INTO "hasan_abdulkarim" VALUES(6,92,'6:92','Và đây là một Kinh Sách mà TA (Allah) đã ban xuống mang nhiều phúc lành và xác nhận lại những điều (mặc khải) đã được ban xuống trước Nó và để cho Ngươi (Muhammad) dùng cảnh báo (người dân của) thành phố mẹ (Makkah) và người dân xung quanh nó. Và những ai tin nơi đời sau sẽ tin Nó (Qur''an) và họ giữ gìn lễ nguyện Salah của họ.');
INSERT INTO "hasan_abdulkarim" VALUES(6,93,'6:93','Và còn ai sai quấy hơn kẻ đã đặt điều nói dối rồi đổ thừa cho Allah hoặc y đã nói: “Tôi đã nhận được sự mặc khải” trong lúc y đã không nhận được điều mặc khải nào; và y đã nói: "Ta sẽ ban xuống điều giống như điều Allah đã ban xuống". Nếu Ngươi có thể nhìn thấy được tình cảnh của những kẻ sai quấy giẫy giụa đau đớn vào lúc sắp chết khi các Thiên Thần giăng tay đến và phán: “Hãy giao hồn của các ngươi cho ta. Ngày nay các ngươi sẽ nhận lấy hình phạt nhục nhã vì tội các ngươi đã từng nói cho Allah những điều không đúng với sự Thật và các ngươi đã khinh thường và phủ nhận các dấu hiệu của Ngài.”');
INSERT INTO "hasan_abdulkarim" VALUES(6,94,'6:94','Và chắc chắn các ngươi sẽ đến trình diện TA (Allah) đơn chiếc giống như cảnh TA đã tạo các ngươi lần đầu tiên và các ngươi bỏ lại sau lưng mọi thứ (tiện nghi) mà TA đã ban cho các ngươi; và TA không thấy đến cùng với các ngươi những kẻ can thiệp nào mà các ngươi đã xác nhận chúng là những thần linh của các ngươi. Chắc chắn mọi quan hệ giữa các ngươi sẻ bị cắt đứt hết và những điều huyễn hoặc mà các ngươi đã từng xác nhận trước đây đã bỏ các ngươi đi biệt dạng.');
INSERT INTO "hasan_abdulkarim" VALUES(6,95,'6:95','Chính Allah là Đấng đã chẻ hạt giống và hạt chà là ra làm hai và làm nó nẩy mầm. Ngài cho xuất cái sống ra từ cái chết và cho xuất cái chết ra khỏi cái sống. Allah của các ngươi là như thế. Thế các ngươi lệch hướng về đâu?');
INSERT INTO "hasan_abdulkarim" VALUES(6,96,'6:96','Ngài chẻ đôi cảnh bình minh. Ngài làm ra ban đêm để nghỉ ngơi, mặt trời và mặt trăng để ghi đếm thời gian. Đó là sự đo lường sắp xếp của Đấng Toàn Năng, Đấng Toàn Tri.');
INSERT INTO "hasan_abdulkarim" VALUES(6,97,'6:97','Và Ngài là Đấng đã làm ra những ngôi sao (trên trời) cho các ngươi, nhờ chúng các ngươi có thể định được hướng đi giữa chốn u tối của đất liền va biển cả. Quả thật, TA đã giải thích các dấu hiệu cho đám người có sự hiểu biết.');
INSERT INTO "hasan_abdulkarim" VALUES(6,98,'6:98','Và Ngài là Đấng đã cho sinh sản các ngươi từ một người duy nhất (Adam). Xong có một chỗ ngụ và một kho chứa (cho các ngươi). TA giải thích các dấu hiệu cho đám người thông hiểu.');
INSERT INTO "hasan_abdulkarim" VALUES(6,99,'6:99','Và Ngài là Đấng cho mưa từ trên trời xuống mà TA dùng làm mọc ra đủ loại thảo mộc, xong từ nó TA làm mọc ra cộng xanh tươi mà TA cho ra trái từng chùm; và từ thân và cành cây chà là, TA làm trổ ra từng chùm trái lủng lẳng, thấp và gần; và có cả vườn nho, vườn trái ô liu và vườn lựu, giống nhau (về trái hạt) nhưng khác nhau (về phẩm chất.) Hãy nhìn ngấm trái của chúng khi chúng ra trái và khi trái chín. Quả thật, trong sự việc đó là các dấu hiệu cho đám người có đức tin.');
INSERT INTO "hasan_abdulkarim" VALUES(6,100,'6:100','Thế mà họ lại tôn loài Jinn làm kẻ đối tác của Allah mặc dầu Ngài đã tạo ra chúng. Và không một chút hiểu biết, họ đã gán con trai và con gái cho Ngài. Thật quang vinh và trong sạch thay Ngài! Ngài vượt lên trên những điều mà họ đã qui cho Ngài.');
INSERT INTO "hasan_abdulkarim" VALUES(6,101,'6:101','Đấng sáng tạo các tầng trời và trái đất. Làm sao Ngài có thể có được một đứa con trai trong lúc Ngài không có vợ? Và Ngài đã tạo hóa mọi vật. Và Ngài biết hết mọi vật.');
INSERT INTO "hasan_abdulkarim" VALUES(6,102,'6:102','Allah, Thượng Đế của các ngươi là như thế. Không có Thượng Đế nào khác cả duy chỉ Ngài (là Thượng Đế), Đấng Tạo Hóa vạn vật. Bởi thế, hãy thờ phụng Ngài. Và Ngài là Đấng Thọ lãnh về tất cả mọi việc.');
INSERT INTO "hasan_abdulkarim" VALUES(6,103,'6:103','Không cái nhìn nào bắt kịp được Ngài trong lúc Ngài bắt kịp tất cả mọi cái nhìn; bởi vì Ngài là Đấng Tinh tế, Đấng Am tường.');
INSERT INTO "hasan_abdulkarim" VALUES(6,104,'6:104','Quả thật, ánh sáng quang minh từ Thượng Đế của các người đã đến soi rọi các ngươi. Bởi thế, ai nhìn thấy thì được lợi cho bản thân (linh hồn) mình còn ai mù lòa thì là bất lợi cho bản thân mình. Và Ta (Muhammad) không là vị Giám Thị theo canh gác các ngươi.');
INSERT INTO "hasan_abdulkarim" VALUES(6,105,'6:105','Và TA (Allah) đã giải thích rõ các Lời Mặc Khải đúng như thế và để cho chúng (những kẻ chống đối) lấy cớ đó để nói: “Ngươi đã học điều này (từ một người khác)” và để TA trình bày cho một đám người hiểu biết.');
INSERT INTO "hasan_abdulkarim" VALUES(6,106,'6:106','Hãy tuân theo những điều mà Thượng Đế (Allah) của Ngươi đã mặc khải cho Ngươi. Không có Thượng Đế nào khác cả duy chỉ Ngài (là Thượng Đế). Và hãy lánh xa những người thờ đa thần.');
INSERT INTO "hasan_abdulkarim" VALUES(6,107,'6:107','Và nếu Allah muốn, họ đã không tôn thờ các thần linh (cùng với Allah) và TA đã không cử Ngươi làm giám thị theo canh gác họ và Ngươi cũng không phải là một người thọ lãnh (công việc) giùm cho họ.');
INSERT INTO "hasan_abdulkarim" VALUES(6,108,'6:108','Nhưng chớ mắng nhiếc những kẻ (thần linh) mà họ van vái ngoài Allah bởi vì do thiếu hiểu biết họ có thể mắng nhiếc Allah trở lại để trả thù; đối với mỗi cộng đồng, TA đã trang hoàng các việc làm của họ rồi họ sẽ được đưa về gặp Thượng Đế (Allah) của họ trở lại, xong Ngài sẽ cho họ biết mọi điều họ đã từng làm.');
INSERT INTO "hasan_abdulkarim" VALUES(6,109,'6:109','Và họ mang Allah ra thề thốt bằng những lời thề hết sức nghiêm trọng (nói) rằng nếu có một dấu lạ nào đến cho họ thì chắc chắn họ sẽ tin tưởng hoàn toàn. Hãy bảo họ: “Mọi dấu lạ đều nằm nơi Allah cả.” Và điều gì có thể làm cho các ngươi (hỡi Muslim!) nhìn nhận rằng dẫu cho dấu lạ đó có thật sự xảy đến với họ thì nhất định họ vẫn không tin.');
INSERT INTO "hasan_abdulkarim" VALUES(6,110,'6:110','Và TA (Allah) sẽ chuyển tấm lòng và cái nhìn của họ sang phía khác đúng như việc họ đã không tin nơi Nó (Qur''an) lần đầu và TA sẽ bỏ mặc họ lang thang trong sự thái quá của họ.');
INSERT INTO "hasan_abdulkarim" VALUES(6,111,'6:111','Và nếu TA (Allah) có phái Thiên Thần xuống gặp họ và làm cho người chết nói chuyện được với họ hoặc dẫu cho TA có tập trung mọi thứ mang đặt trước mặt họ để cho họ nhìn thấy tận mắt thì họ vẫn không tin trừ phi Allah muốn khác đi, bởi vì đa số bọn họ là những kẻ ngu muội.');
INSERT INTO "hasan_abdulkarim" VALUES(6,112,'6:112','Và tương tự như thế TA (Allah) đã đặt Shaytan và Jinn làm kẻ thù cho mỗi vị Nabi, chúng gợi ý cho nhau bằng những lời lẽ hoa mỹ để mong lừa dối thiên hạ. Và nếu Thượng Đế (Allah) của Ngươi muốn khác đi thì chúng sẽ không làm được. Do đó, hãy bỏ mặc chúng với những điều chúng bịa đặt.');
INSERT INTO "hasan_abdulkarim" VALUES(6,113,'6:113','Và hãy để cho tấm lòng của những kẻ không tin nơi đời sau nghiêng về lời dụ dỗ đường mật đó và để cho họ vui sướng với nó và để cho họ kiếm chác những gì họ muốn kiếm chác được.');
INSERT INTO "hasan_abdulkarim" VALUES(6,114,'6:114','Hãy bảo họ: “Há ta phải đi tìm một Đấng Xét Xử nào khác ngoài Allah hay sao trong lúc Ngài là Đấng đã ban Kinh Sách với lời giải thích cặn kẽ xuống cho các người?” Và những kẻ mà TA (Allah) đã ban cho Kinh Sách đều biết rõ rằng Nó (Qur’an) đã được Thượng Đế của Ngươi ban xuống bằng sự thật. Bởi thế, Ngươi chớ là một người ngờ vực (về sự việc đó).');
INSERT INTO "hasan_abdulkarim" VALUES(6,115,'6:115','Lời phán của Thượng Đế (Allah) của Ngươi hoàn hảo về Chân Lý và công lý. Không ai có thể thay đổi được Lời phán của Ngài bởi vì Ngài là Đấng Hằng Nghe, Đấng Hằng Biết hết (mọi việc).');
INSERT INTO "hasan_abdulkarim" VALUES(6,116,'6:116','Và nếu Ngươi tuân theo trào lưu của đa số người trên trái đất thì chúng sẽ dắt Ngươi đi lạc khỏi Chính Đạo của Allah bởi vì họ chỉ làm theo sở thích và chỉ đoán chừng mà thôi.');
INSERT INTO "hasan_abdulkarim" VALUES(6,117,'6:117','Quả thật, Thượng Đế (Allah) của Ngươi biết rõ ai là kẻ lạc khỏi Chính Đạo của Ngài và ai là người được hướng dẫn.');
INSERT INTO "hasan_abdulkarim" VALUES(6,118,'6:118','Do đó, hãy ăn thịt (của con vật) mà tên của Allah được đọc lên cho nó nếu các ngươi tin tưởng nơi các Lời Mặc Khải của Ngài.');
INSERT INTO "hasan_abdulkarim" VALUES(6,119,'6:119','Và tại sao các ngươi không ăn thịt (của con vật) mà tên của Allah đã được đọc nhắc lên nó và quả thật Ngài đã giải thích cho các ngươi thực phẩm nào mà các ngươi không được dùng ngoại trừ vì nhu cầu bắt buộc phải dùng (để duy trì sự sống)? Và quả thật, nhiều người dắt thiên hạ đi lạc là do sự ham muốn riêng tư của họ chứ không do hiểu biết. Quả thật, Thượng Đế của Ngươi biết rõ những kẻ phạm giới.');
INSERT INTO "hasan_abdulkarim" VALUES(6,120,'6:120','Hãy tránh mọi thứ tội, công khai hay kín đáo. Bởi vì ai làm tội thì sẽ đền tội mà họ đã gây ra.');
INSERT INTO "hasan_abdulkarim" VALUES(6,121,'6:121','Và chớ ăn thịt (của con vật) mà tên của Allah không được đọc nhắc lên nó. Bởi vì đó là một sự phạm giới. Và quả thật Shaytan xúi giục bạn bè của nó tranh luận với các ngươi. Và nếu các ngươi nghe theo chúng thì sẽ trở thành những người thờ đa thần.');
INSERT INTO "hasan_abdulkarim" VALUES(6,122,'6:122','Có thể nào một người đã chết (ngu muội trong tín ngưỡng) được TA (Allah) ban cho sự sống và ánh sáng nhờ đó y có thể bước đi thong dong giữa thiên hạ lại ngang bằng với một người đắm chìm trong u mê mà hắn không bao giờ thoát ra nổi? Việc làm của những kẻ không có đức tin tỏ ra hấp dẫn đối với chúng giống như thế.');
INSERT INTO "hasan_abdulkarim" VALUES(6,123,'6:123','Và TA (Allah) đã khiến cho các tên tội lỗi của mỗi thị trấn lên làm lãnh tụ để chúng có cơ hội âm mưu đặt kế hoạch trong đó. Nhưng chúng chỉ âm mưu chống lại bản thân mình nhưng chúng không nhận thây điều đó.');
INSERT INTO "hasan_abdulkarim" VALUES(6,124,'6:124','Và khi có một dấu hiệu đến với chúng, chúng bảo: “Chúng tôi sẽ không bao giờ tin tưởng trừ phi chúng tôi nhận được điều giống như điều đã được ban cho các Sứ Giả của Allah.” Allah biết rõ đâu là chỗ mà Ngài phải đặt Thông điệp của Ngài. Rồi đây những tên tội lỗi sẽ sớm bị Allah hạ nhục và sẽ chịu một sự trừng phạt khắc nghiệt về những điều chúng đã từng âm mưu.');
INSERT INTO "hasan_abdulkarim" VALUES(6,125,'6:125','Bởi thế, ai là người mà Allah muốn hướng dẫn thì sẽ được Ngài mở rộng tấm lòng để đến với Islam. Và ai là người mà Allah đánh lạc hướng thì Ngài sẽ thu hẹp tấm lòng của y chật chội đến mức y có cảm giác như đang đi lên trời. Allah đặt sự ô uế lên những kẻ không tin tưởng đúng như thế.');
INSERT INTO "hasan_abdulkarim" VALUES(6,126,'6:126','Và đây là con đường (đạo lý) chính trực của Thượng Đế (Allah) của Ngươi. Quả thật, TA (Allah) đã giải thích các Lời Mặc Khải rõ ràng cho đám người ghi nhớ.');
INSERT INTO "hasan_abdulkarim" VALUES(6,127,'6:127','Họ sẽ được hưởng ngôi nhà bằng an với Thượng Đế của họ. Và Ngài sẽ là Đấng Che chở của họ do những điều (thiện) mà họ đã làm.');
INSERT INTO "hasan_abdulkarim" VALUES(6,128,'6:128','Và vào một Ngày, Ngài sẽ tập hợp tất cả trở lại, và phán: “Hỡi tập đoàn Jinn, quả thật các ngươi đã kết nạp nhiều (vong hồn) của loài người.” Bạn bè của chúng trong loài người sẽ thưa: “Lạy Thượng Đế của bầy tôi! Bầy tôi đã vui vầy vơi nhau nhưng (cuối cùng) bầy tôi đã mãn hạn kỳ mà Ngài đã ấn định cho bầy tôi.” (Allah) sẽ phán: hỏa ngục sẽ là chỗ cư ngụ mà các ngươi sẽ vào sống muôn đời trong đó trừ phi Allah muốn khác đi. Và quả thật Thượng Đế của Ngươi là Đấng Thông Suốt, Đấng Hằng Biết.');
INSERT INTO "hasan_abdulkarim" VALUES(6,129,'6:129','TA (Allah) đã khiến cho những tên sai quấy quay về kết thân với nhau vì những điều (tội lỗi) mà chúng đã phạm.');
INSERT INTO "hasan_abdulkarim" VALUES(6,130,'6:130','Hỡi tập đoàn Jinn và loài người! Há đã không có các Sứ Giả xuất thân từ các ngươi đến thuật lại cho các ngươi về các dấu hiệu của TA và báo cho các ngươi biết về Ngày hội ngộ này của các ngươi hay chăng?” Chúng sẽ thưa: “Bầy tôi xác nhận (điều đó) có thật nơi bản thân của bầy tôi!” Nhưng đời sống trần tục này đã dối gạt chúng cho nên chúng đã tự xác nhận rằng mình là những kẻ không tin tưởng.');
INSERT INTO "hasan_abdulkarim" VALUES(6,131,'6:131','Sở đĩ như thế là vì Thượng Đế (Allah) của Ngươi không muốn tiêu diệt các thị trấn một cách bất công trong khi dân cư của chúng chưa được báo trước.');
INSERT INTO "hasan_abdulkarim" VALUES(6,132,'6:132','Và tất cả sẽ được ban cấp bậc tương xứng với việc làm của họ bởi vì Thượng Đế của Ngươi không làm ngơ về những điều họ đã làm.');
INSERT INTO "hasan_abdulkarim" VALUES(6,133,'6:133','Và Thượng Đế của Ngươi Rất mực giàu có và đầy lòng Khoan dung. Nếu muốn, Ngài có thể tiêu diệt các ngươi bất cứ lúc nào và đưa ai mà Ngài muốn đến kế tục các ngươi giống như việc Ngài đã cho sinh sản các ngươi từ con cháu của đám người khác.');
INSERT INTO "hasan_abdulkarim" VALUES(6,134,'6:134','Bởi vì quả thật (hình phạt) đã được hứa với các ngươi chắc chắn sẽ xảy ra. Và các ngươi sẽ không tài nào trốn thoát được.');
INSERT INTO "hasan_abdulkarim" VALUES(6,135,'6:135','Hãy bảo họ: “Hỡi dân ta! Các người hãy làm theo khả năng của các người. Ta sẽ làm phần việc của ta; rồi đây các người sẽ sớm biết kết quả tốt đẹp về ngôi nhà (ở cõi đời sau) sẽ thuộc về ai. Bởi vì quả thật, những kẻ làm điều sai quấy sẽ không phát đạt.”');
INSERT INTO "hasan_abdulkarim" VALUES(6,136,'6:136','Và họ cúng cho Allah một phần đất trồng trọt và một số thú nuôi mà Ngài đã tạo ra cho họ và họ nói theo sở thích: “Đây là phần cúng cho Allah và đây là phần cúng cho các thần linh của chúng tôi.” Nhưng phần đất và thú nuôi cúng cho các thần linh của họ không đến tay Allah trong lúc phần cúng cho Allah lại đến tay của các thần linh của họ. Thật bỉ ổi thay điều mà họ suy xét.');
INSERT INTO "hasan_abdulkarim" VALUES(6,137,'6:137','Và các thần linh của họ đã làm cho việc giết con cái của họ tỏ ra hấp dẫn đối với đa số người thờ đa thần với mục đích đưa họ đến chỗ hủy diệt và xáo trộn trong tôn giáo của họ; và nếu Allah muốn khác đi thì họ đã không làm điều đó. Thôi hãy bỏ mặc họ với điều mà họ bịa đặt.');
INSERT INTO "hasan_abdulkarim" VALUES(6,138,'6:138','Và họ nói (một cách huyễn hoặc) như sau: “Thú nuôi và hoa mầu này bị cấm (dùng). Không ai được phép ăn (thịt của) chúng ngoại trừ người nào chúng tôi cho phép.” Ngoài ra, còn có loại thú họ cấm không cho chở (hoặc cưỡi) trên lưng và có loại thú nuôi (khi cắt cổ làm thịt) không được nhắc tên của Allah lên chúng; họ bịa đặt điều nói dối rồi đổ thừa cho Ngài. Ngài sẽ sớm bắt phạt họ về điều (tội lỗi) mà họ đã bịa đặt.');
INSERT INTO "hasan_abdulkarim" VALUES(6,139,'6:139','Và họ nói: “Con trong bụng của con thú này chỉ dành riêng cho đàn ông chúng tôi và các bà không được dùng. Nhưng nếu con vật đẻ ra bị chết thì mọi người đều được hưởng phần trong đó.” Ngài (Allah) sẽ sớm bắt phạt chúng về điều mê tín chúng đã bịa đặt rồi đổ thừa cho Ngài. Quả thật, Ngài Rất mực Thông suốt và Biết hết (mọi việc).');
INSERT INTO "hasan_abdulkarim" VALUES(6,140,'6:140','Chắc chắn là thua thiệt những ai do điên rồ và ngu muội đã giết con cái của họ và cấm dùng thực phẩm mà Allah đã cung cấp cho họ, và bịa đặt điều nói dối rồi đổ thừa cho Allah. chắc chắn họ đã lầm lạc và không được ai hướng dẫn.');
INSERT INTO "hasan_abdulkarim" VALUES(6,141,'6:141','Và Ngài (Allah) là Đấng đã cho sản xuất các ngôi vườn có hàng dậu và không có hàng dậu, và chà là, và rẫy trồng đỏ loại hoa mầu, và trái ô liu và trái lựu giống nhau (về mặt trái hạt) nhưtỉg khác nhau (về mặt phẩm chất). Hãy ăn trái của chúng khi trái chín nhưng hãy trả phần thuế (hoa lợi) vào ngày gặt hái và chớ phung phí bởi vì Allah không yêu thương những kẻ phung phí.');
INSERT INTO "hasan_abdulkarim" VALUES(6,142,'6:142','Và trong loài thú nuôi, có con dùng để chuyên chở và có con dùng để ăn thịt. Hãy ăn thịt con nào Allah cung cấp cho các ngươi nhưng chớ dẫm theo bước chân của Shaytan bởi vì nó là kẻ thù công khai của các người.');
INSERT INTO "hasan_abdulkarim" VALUES(6,143,'6:143','Bắt tám con vật, chia theo cặp; cừu một cặp và dê một cặp. Xong hỏi họ: “Có phải Ngài (Allah) cấm (ăn thịt) hai con đực hoặc hai con cái hoặc con nào nằm trong bụng của hai con cái đó hay không? Hãy cho Ta biết (sự thật) từ sự hiểu biết của các ngươi nếu các ngươi chân thật.”');
INSERT INTO "hasan_abdulkarim" VALUES(6,144,'6:144','Lạc đà một cặp và bò một cặp, rồi hỏi họ: “Có phải Ngài (Allah) cấm (dùng thịt của) hai con đực hoặc hai con cái hoặc con nào nằm trong bụng của hai con cái đó hay không? Phải chăng các ngươi có mặt tại đó khi Allah ban hành lệnh cấm? Thế còn ai sai quấy hơn kẻ đã đặt điều nói dối rồi đổ thừa cho Allah mục đích làm cho loài người lạc đạo do thiếu hiểu biết của họ. Quả thật, Allah không hướng dẫn đám người làm điều sai quấy.”');
INSERT INTO "hasan_abdulkarim" VALUES(6,145,'6:145','Hãy bảo họ: ‘Ta không thấy nơi điều đã được mặc khải cho Ta có khoản nào cấm một người không được dùng món nào đó trừ phi là thịt của xác chết hoặc máu tuôn ra hoặc thịt của con heo bởi vì nó ô uế, hoặc gớm ghiếc khi nó được cúng cho kẻ (thần linh) nào khác không phải là Allah. Nhưng ai vì nhu cầu bắt buộc đành phải ăn chứ không cố ý hay quá độ thì quả thật Thượng Đế (Allah) của Ngươi Hằng Tha Thứ, Rất Mực Khoan Dung.');
INSERT INTO "hasan_abdulkarim" VALUES(6,146,'6:146','Còn đối với những ai là người Do thái, TA (Allah) đã cấm họ dùng (thịt của) tất cả loài vật có móng chân liền (không chẻ làm hai hoặc nhọn), và TA đã cấm họ dùng mỡ bò và mỡ dê ngoại trừ loại mỡ dính vào lưng hoặc dính vào ruột và xương của chúng. TA bắt phạt họ như thế là vì tội chống đối của họ; và quả thật TA (Allah) nói sự thật.');
INSERT INTO "hasan_abdulkarim" VALUES(6,147,'6:147','Nhưng nếu họ cho Ngươi nói dối thì hãy bảo họ: “Thượng Đế (Allah) của các ngươi vô cùng Khoan dung nhưng đám người tội lỗi sẽ không tránh khỏi sự giận dữ của Ngài.”');
INSERT INTO "hasan_abdulkarim" VALUES(6,148,'6:148','Những ai tôn thờ đa thần sẽ nói như sau: “Nếu Allah muốn khác thì chúng tôi lẫn cha mẹ của chúng tôi đâu có thờ thần linh (cùng với Ngài) và chúng tôi cũng không cấm đoán điều gì.” Những kẻ trước họ cũng đã nói dối giống như thế cho đến khi họ nếm hình phạt của TA. Hãy hỏi họ: “Há các ngươi có một sự hiểu biết chắc chắn (về điều các ngươi đã nói) hay chăng? Nếu có thì hãy mang nó đến trình bày trước bọn ta. Các ngươi chỉ làm theo sở thích và các ngươi chỉ phỏng đoán.”');
INSERT INTO "hasan_abdulkarim" VALUES(6,149,'6:149','Hãy bảo họ: “Chỉ Allah mới có các lập luận tối hậu. Bởi vì nếu quả thật Allah muốn thì chắc chắn Ngài đã hướng dẫn tất cả các người.”');
INSERT INTO "hasan_abdulkarim" VALUES(6,150,'6:150','Hãy bảo họ: “Hãy đưa các nhân chứng của các người đến để xác nhận Allah đã cấm điều này.” Nhưng nếu họ xác nhận thì Ngươi chớ nên xác nhận cùng với họ. Và chớ làm theo điều ham muốn của những kẻ đã phủ nhận các dấu hiệu của TA (Allah) và những kẻ không tin nơi đời sau bởi vì họ dựng những đối tượng ngang vai với Thượng Đế (Allah) của họ.');
INSERT INTO "hasan_abdulkarim" VALUES(6,151,'6:151','Hãy bảo họ: “Đến đây, để Ta đọc cho các ngươi điều lệnh mà Thượng Đế của các người đã cấm các ngươi: chớ bao giờ tổ hợp bất cứ cái gì với Ngài và hãy ăn ở tử tế với cha mẹ của các ngươi; và chớ vì sợ nghèo mà giết con cái của các ngươi. TA cung dưỡng các ngươi và cả chúng nữa. Và chớ đến gần những điều thô bỉ (Zina) dù công khai hay kín đáo; và chớ giết sinh mạng (của con người) mà Allah đã làm cho linh thiêng trừ phi vì lý do chính đáng (của công lý và luật pháp). Đó là điều Ngài chỉ thị cho các ngươi để cho các ngươi hiểu.');
INSERT INTO "hasan_abdulkarim" VALUES(6,152,'6:152','“Và chớ đến gần tài sản của trẻ mồ côi trừ phi với kế hoạch cải thiện nó cho đến khi trẻ đến tuổi trưởng thành (để có thể quản lý nó); và hãy đo cho đúng và cân cho đủ; TA chỉ bắt mỗi linh hồn gánh vác (trách nhiệm) tùy theo khả năng của nó; và khi các ngươi nói năng thì hãy công bằng trong lời nói dẫu rằng nó có nghịch với bà con ruột thịt đi nữa; và hãy làm tròn Lời Giao Ước của Allah. Đó là điều Ngài chỉ thị cho các ngươi để cho các ngươi ghi nhớ.');
INSERT INTO "hasan_abdulkarim" VALUES(6,153,'6:153','“Và (hãy biết) rằng đây là con đường ngay chính của Ta. Hãy theo nó và chớ theo con đường nào (khác) sợ rằng nó sẽ đưa các ngươi lệch khỏi con đường của Ngài. Ngài chỉ thị cho các ngươi như thế để cho các ngươi trở thành người ngay chính sợ Allah.”');
INSERT INTO "hasan_abdulkarim" VALUES(6,154,'6:154','Rồi TA (Allah) ban cho Musa Kinh Sách để TA hoàn tất (ân huệ của TA) cho những ai làm tốt và giải thích rõ từng vấn đề và đó là một Chỉ Đạo và một Hồng Ân (cho con cháu của Israel) để cho họ tin tưởng nơi việc gặp gỡ Thượng Đế của họ lại (ở đời sau).');
INSERT INTO "hasan_abdulkarim" VALUES(6,155,'6:155','Và đây là một Kinh Sách mà TA (Allah) đã ban xuống như một phúc lành. Do đó, hãy theo Nó (Qur’an) và sợ Allah để cho các ngươi có thể được khoan dung.');
INSERT INTO "hasan_abdulkarim" VALUES(6,156,'6:156','E rằng các ngươi có thể nói: “Kinh Sách (của Allah) chỉ được truyền xuống cho hai giáo phái (Do thái và Thiên Chúa giáo) trước chúng tôi, và chúng tôi không hề biết gì về việc học hỏi (Kinh Sách) của họ.”');
INSERT INTO "hasan_abdulkarim" VALUES(6,157,'6:157','Hoặc các ngươi cũng có thể nói: “Nếu Kinh Sách đã được ban xuống cho chúng tôi thì chúng tôi đã được hướng dẫn đúng hơn họ.” Bởi thế, giờ đây một Bằng Chứng và một Chỉ Đạo và một Hồng Ân từ Thượng Đế (Allah) của các ngươi đã đến với các ngươi. Cho nên còn ai sai quấy hơn kẻ phủ nhận các Lời Mặc Khải của Allah và lánh xa chúng? TA (Allah) sẽ sớm trừng phạt những ai lánh xa các Lời Mặc Khải của TA bằng một hình phạt rất mực xấu xa vì tội chúng đã từng quay lánh bỏ đi.');
INSERT INTO "hasan_abdulkarim" VALUES(6,158,'6:158','Phải chăng họ đang chờ các Thiên Thần xuống gặp họ hoặc Thượng Đế của Ngươi đến gặp họ hoặc một vài dấu hiệu của Thượng Đế của Ngươi hiện ra? Vào một Ngày mà một vài dấu hiệu của Thượng Đế của Ngươi hiện ra, đức tin của một người sẽ chẳng giúp ích gì được cho linh hồn của người đã từng không tin tưởng trước đó và đã không thu hoạch được một điều tốt nào trong đức tin của họ. Hãy bảo họ: “Hãy chờ đợi đi bởi vì chúng tôi cũng đang chờ đợi (như quí vị).”');
INSERT INTO "hasan_abdulkarim" VALUES(6,159,'6:159','Còn đối với những ai đã phân chia tôn giáo của họ và lập thành giáo phái, Ngươi chẳng có gì quan hệ với họ. Công việc của họ chỉ quan hệ đến Allah. Rồi Ngài sẽ cho họ biết về những điều họ đã từng làm.');
INSERT INTO "hasan_abdulkarim" VALUES(6,160,'6:160','Ai đến (trình diện Allah) với một điều phúc lành thì sẽ được hưởng mười điều lành tương tự; ngược lại, ai đến (gặp Allah) với một điều dữ thì sẽ bị phạt bằng một điều dữ tương tự. Và họ sẽ không bị (Allah) đối xử bất công.');
INSERT INTO "hasan_abdulkarim" VALUES(6,161,'6:161','Hãy bảo họ: “Quả thật, Thượng Đế (Allah) của Ta đã hướng dẫn Ta đến con đường ngay chính, tôn giáo đúng đắn, tín ngưỡng của Ibrahim thuần túy (chỉ tôn thờ riêng Allah). Và Ta không là một người thờ đa thần.”');
INSERT INTO "hasan_abdulkarim" VALUES(6,162,'6:162','Hãy bảo họ: “Quả thật, cuộc lễ nguyện Salah của Ta, việc tế lễ của Ta, cuộc sống và cái chết của Ta đều hiến trọn cho Allah, Thượng Đế (Đấng Chủ Tể) của vũ trụ.');
INSERT INTO "hasan_abdulkarim" VALUES(6,163,'6:163','“Ngài không có đối tác ngang vai. Và Ta đã được chỉ thị như thế và Ta là người Muslim đầu tiên thần phục Ngài.”');
INSERT INTO "hasan_abdulkarim" VALUES(6,164,'6:164','Hãy bảo họ: “Ta sẽ phải tìm một Thượng Đế (Đấng Chủ Tể) nào khác Allah hay sao trong lúc Ngài là Thượng Đế của vạn vật? Và mỗi linh hồn chỉ chịu trách nhiệm cho chính bản thân mình; và không một người khuân vác nào sẽ vác giùm gánh nặng (trách nhiệm) của người khác. Rồi chung cuộc các người sẽ quay về trình diện Thượng Đế của các người trở lại. Xong Ngài sẽ cho các người biết (sự thật) về những điều các người đã từng tranh chấp.”');
INSERT INTO "hasan_abdulkarim" VALUES(6,165,'6:165','Và Ngài là Đấng đã làm cho các người nối nghiệp trên mặt đất. Và Ngài đã nâng cấp bậc của người này trội hơn người kia để Ngài có thể thử thách các người với món quà mà Ngài đã ban cho các người. Quả thật, Thượng Đế của Ngươi rất nhanh trong việc trừng phạt nhưng quả thật Ngài cũng Hằng Tha Thứ, Rất Mực Khoan Dung.');
INSERT INTO "hasan_abdulkarim" VALUES(7,1,'7:1','Alif. Lam. Mim. Sad.');
INSERT INTO "hasan_abdulkarim" VALUES(7,2,'7:2','(Đây là) một Kinh Sách được ban xuống cho Ngươi (Muhammad!) nhưng chớ vì Nó mà tấm lòng của Ngươi sẽ thắt lại (bởi vì Nó được ban xuống) là để cho Ngươi dùng cảnh báo (những ai lầm đường lạc lối) và nhắc nhở những người có đức tin.');
INSERT INTO "hasan_abdulkarim" VALUES(7,3,'7:3','Hãy tuân theo điều (mặc khải) đã được ban xuống cho các ngươi từ Thượng Đế (Allah) của các ngươi (hỡi nhân loại!) và chớ nghe theo người bảo hộ (hay chủ nhân) nào khác ngoài Ngài. Ít thay việc các ngươi ghi nhớ lời khuyên bảo này!');
INSERT INTO "hasan_abdulkarim" VALUES(7,4,'7:4','Và có bao nhiêu thị trấn đã bị TA (Allah) tiêu diệt vì tội lỗi (của dân cư) của chúng? hình phạt của TA xảy đến cho chúng bất ngờ vào ban đêm hoặc vào lúc chúng đang nghỉ trưa.');
INSERT INTO "hasan_abdulkarim" VALUES(7,5,'7:5','Bởi thế, khi hình phạt của TA giáng xuống chúng, chúng chỉ biết than thân (nói): “Quả thật, chúng tôi là những kẻ làm điều sai quấy.”');
INSERT INTO "hasan_abdulkarim" VALUES(7,6,'7:6','Rồi TA (Allah) sẽ hỏi những người tiếp thu Thông Điệp và những Sứ Giả (của TA).');
INSERT INTO "hasan_abdulkarim" VALUES(7,7,'7:7','Bởi thế, do hiểu biết, TA (Allah) sẽ kể lại cho chúng toàn bộ sự tích bởi vì TA (Allah) không hề vắng mặt (bất cứ lúc nào và nơi nào).');
INSERT INTO "hasan_abdulkarim" VALUES(7,8,'7:8','Và việc cân đo (Phúc và Tội) vào Ngày (Xét Xử) đó xảy ra thật. Bởi thế, những ai mà bàn cân (chứa việc thiện) nặng thì sẽ là những người thành đạt.');
INSERT INTO "hasan_abdulkarim" VALUES(7,9,'7:9','Còn những ai mà bàn cân (chứa việc thiện) nhẹ thì sẽ là những người tự hủy hoại bản thân mình vì tội đã từng xuyên tạc các dấu hiệu của TA.');
INSERT INTO "hasan_abdulkarim" VALUES(7,10,'7:10','Và quả thật, TA (Allah) đã định cư các ngươi trên trái đất và tạo nơi đó làm phương tiện sinh sống cho các ngươi. Ít thay việc các ngươi tỏ lòng biết ơn.');
INSERT INTO "hasan_abdulkarim" VALUES(7,11,'7:11','Và quả thật, TA (Allah) đã tạo hóa các ngươi rồi ban cho các ngươi hình thể rồi TA bảo các Thiên Thần quỳ xuống chào Adam và chúng quỳ xuống ngoại trừ Iblis; nó không chịu phủ phục.');
INSERT INTO "hasan_abdulkarim" VALUES(7,12,'7:12','(Allah) phán: “Điều gì cản nhà ngươi không chịu phủ phục khi TA ra lệnh cho ngươi (hỡi Iblis)? (Iblis) thưa: “Bề tôi tốt hơn hắn (Adam) bởi vì Ngài đã tạo bề tôi từ lửa còn Ngài tạo hắn từ đất sét.”');
INSERT INTO "hasan_abdulkarim" VALUES(7,13,'7:13','(Allah) phán: “Nhà ngươi hãy đi xuống khỏi đó (thiên đàng). Nhà ngươi không được ngạo mạn trong đó. Hãy đi ra! Quả thật, nhà ngươi là một tên khốn nạn nhất.”');
INSERT INTO "hasan_abdulkarim" VALUES(7,14,'7:14','(Iblis) tâu: “Xin Ngài tạm tha cho bề tôi (được sống) cho đến ngày chúng (con cháu của Adam) được dựng sống lại.”');
INSERT INTO "hasan_abdulkarim" VALUES(7,15,'7:15','(Allah) phán: “Tạm tha cho nhà ngươi.”');
INSERT INTO "hasan_abdulkarim" VALUES(7,16,'7:16','(Iblis) kèo nài: “Bởi vì Ngài đã xua đuổi bề tôi, bề tôi sẽ nằm chờ (dụ dỗ) chúng (con cháu của Adam) trên con đường ngay chính của Ngài.”');
INSERT INTO "hasan_abdulkarim" VALUES(7,17,'7:17','“Rồi bề tôi sẽ tấn công chúng từ đằng trước, đằng sau, bên phải và bên trái. Và Ngài sẽ thấy đa số bọn chúng là những kẻ phụ ơn.”');
INSERT INTO "hasan_abdulkarim" VALUES(7,18,'7:18','(Allah) phán: “Hãy đi ra khỏi đó, thứ đáng khinh và đáng tống cổ đi nơi khác. Ai trong bọn chúng theo nhà ngươi thì tất cả sẽ bị TA bắt nhốt vào đầy hỏa ngục.”');
INSERT INTO "hasan_abdulkarim" VALUES(7,19,'7:19','(Và Allah phán cho Adam): “Hỡi Adam! Ngươi và vợ của ngươi hãy ở trong thiên đàng. Hai ngươi hãy ăn tùy thích (món ngon vật lạ) mà hai ngươi tìm thấy bất cứ nơi nào trong đó, nhưng chớ đến gần cái ''Cây'' này sợ rằng hai ngươi sẽ trở thành những kẻ phạm giới.”');
INSERT INTO "hasan_abdulkarim" VALUES(7,20,'7:20','Nhưng Shaytan (Iblis) đã thì thào với hai người (lời đường mật) để làm lộ ra cho hai người thấy điều xấu hổ đã từng được giấu kín khỏi hai người (từ trước); và nó nói: “Hai anh chị có biết tại sao Thượng Đế của anh chị cấm (anh chị) đến cái ''Cây'' này không? Chỉ vì lý do sợ anh chị trở thành Thiên Thần hoặc thành người sống bất tử ấy thôi!”');
INSERT INTO "hasan_abdulkarim" VALUES(7,21,'7:21','Và nó thề với hai người: “Tôi là một người cố vấn tốt nhất cho hai anh chị.”');
INSERT INTO "hasan_abdulkarim" VALUES(7,22,'7:22','Bởi thế nó đã quỷ quyệt làm cho hai (vợ chồng của Adam) rơi xuống (khỏi thiên đàng), khi hai người nếm (trái của) Cây (đã bị cấm) thì phần xấu hổ của họ liền lộ ra cho họ thấy và hai người bắt đầu khâu lá cây trong thiên đàng để che kín thân thể của họ. Và Thượng Đế của hai người (vợ chồng Adam) gọi hai người, và phán: “Há TA đã không cấm hai ngươi đến gần cái Cây đó và bảo hai ngươi rằng Shaytan là kẻ thù không đội trời chung với hai ngươi hay sao?”');
INSERT INTO "hasan_abdulkarim" VALUES(7,23,'7:23','Hai người cầu nguyện, thưa: “Lạy Thượng Đế của bầy tôi! Bầy tôi đã tự làm hại bản thân mình. Nếu Ngài không tha thứ và khoan dung thì chắc chắn bầy tôi sẽ trở thành những kẻ thua thiệt.”');
INSERT INTO "hasan_abdulkarim" VALUES(7,24,'7:24','(Allah) phán: “Hãy đi xuống, đứa này sẽ là kẻ thù của đứa kia. Các ngươi sẽ có trên trái đất một nơi nghỉ tạm và phương tiện sinh sống tạm suốt một thời gian ngắn.”');
INSERT INTO "hasan_abdulkarim" VALUES(7,25,'7:25','(Allah) phán tiếp: “Nơi đó các ngươi sẽ sống, và nơi đó các ngươi sẽ chết và từ đó (cuối cùng) các ngươi sẽ được đưa ra trở lại.”');
INSERT INTO "hasan_abdulkarim" VALUES(7,26,'7:26','Hỡi con cháu của Adam! TA (Allah) đã ban cho các ngươi y phục để che kín phần xấu hổ của các ngươi và để trang sức; tuy nhiên, y phục tốt nhất là việc sợ Allah. Đó là một trong các dấu hiệu của Allah để may ra họ ghi nhớ.');
INSERT INTO "hasan_abdulkarim" VALUES(7,27,'7:27','Hỡi con cháu của Adam! Chớ để cho Shaytan lừa gạt các ngươi như nó đã dụ dỗ cha mẹ của các ngươi ra khỏi thiên đàng bằng cách lột trần y phục của hai người họ để phơi bày cho cả hai thấy sự xấu hổ của mình bởi vì nó (Jinn) và bộ lạc của nó nhìn thấy các ngươi từ một chỗ mà các ngươi không thể nhìn thấy chúng được. Quả thật, TA khiến cho các tên Shaytan làm kẻ bảo hộ của những ai không có đức tin.');
INSERT INTO "hasan_abdulkarim" VALUES(7,28,'7:28','Và khi chúng phạm một điều thô bỉ, chúng nói: “Bọn ta thấy cha mẹ của bọn ta làm thế và Allah đã ra lệnh cho bọn ta làm điều đó.” Hãy bảo chúng: “Không, quả thật Allah không hề ra lệnh bắt (các ngươi) làm điều thô bỉ. Phải chăng các ngươi đã đổ thừa cho Allah điều mà các ngươi không biết?”');
INSERT INTO "hasan_abdulkarim" VALUES(7,29,'7:29','Hãy bảo chúng: “Thượng Đế của Ta chỉ thị (cho các người) việc (ăn mặc) đúng đắn nghiêm trang và hướng mặt các người đúng (về Allah) tại mỗi nơi thờ phụng và cầu nguyện Ngài với lòng chân thành chỉ dành riêng cho Ngài. Các ngươi sẽ trở về với Ngài giống như việc Ngài đã khởi sự tạo hóa các ngươi lúc ban đầu.');
INSERT INTO "hasan_abdulkarim" VALUES(7,30,'7:30','Một số (người) đã được Ngài (Allah) hướng dẫn còn một số khác thì lạc hướng. Điều đó chính đáng bởi vì chúng nhận Shaytan làm kẻ bảo hộ thay vì Allah và chúng còn nghĩ rằng chúng đã được hướng dẫn đúng đường.');
INSERT INTO "hasan_abdulkarim" VALUES(7,31,'7:31','Hỡi con cháu của Adam! Hãy ăn mặc y phục trang nhã và chỉnh tề tại mỗi Masjid; và hãy ăn và uống nhưng chớ quá độ bởi vì Ngài (Allah) không yêu thương những kẻ quá độ.');
INSERT INTO "hasan_abdulkarim" VALUES(7,32,'7:32','Hãy bảo chúng: “Ai cấm dùng các món trang sức xinh đẹp mà Allah đã làm ra cho các bầy tôi của Ngài (sử dụng) và cấm dùng các món thực phẩm tốt và sạch?” Hãy bảo chúng: “Các thứ đó dành cho những ai có đức tin sống ở đời này (và) dành riêng cho họ vào Ngày phục sinh.” TA (Allah) đã giải thích rõ các Lời Mặc Khải đúng như thế cho đám người hiểu biết.');
INSERT INTO "hasan_abdulkarim" VALUES(7,33,'7:33','Hãy bảo chúng: “Thượng Đế của Ta chỉ cấm (làm) các điều thô bỉ, dù công khai hay kín đáo, và điều tội lỗi và việc áp bức (kẻ khác) bất chấp lẽ phải và sự thật, và việc tổ hợp (thần linh) với Allah, điều mà Ngài không bao giờ chấp thuận, và việc nói bậy cho Allah những điều mà các ngươi không biết.”');
INSERT INTO "hasan_abdulkarim" VALUES(7,34,'7:34','Mỗi một cộng đồng có một thời hạn ấn định; bởi thế khi thời hạn đã mãn, họ sẽ không thể trì hoãn lại được một thời khắc nào và cũng không thể ra đi sớm hơn (một thời khắc nào).');
INSERT INTO "hasan_abdulkarim" VALUES(7,35,'7:35','Hỡi con cháu của Adam! Nếu có các Sứ Giả xuất thân từ các ngươi đến gặp các ngươi kể lại các Lời Mặc Khải của TA cho các ngươi thì những ai sợ Allah và sửa mình sẽ không lo sợ cũng sẽ không buồn phiền.');
INSERT INTO "hasan_abdulkarim" VALUES(7,36,'7:36','Còn những ai phủ nhận các Lời Mặc Khải của TA và tỏ ra khinh thường chúng thì sẽ trở thành bạn của hỏa ngục; chúng sẽ vào ở trong đó đời đời.');
INSERT INTO "hasan_abdulkarim" VALUES(7,37,'7:37','Thế còn ai sai quấy hơn những kẻ bịa đặt ra điều nói dối rồi đổ thừa cho Allah hoặc phủ nhận các Lời Mặc Khải của Ngài? Chúng là những kẻ tiếp tục nhận phần (lộc) đã được ghi trong sổ (định mệnh) của chúng cho đến khi các Thiên sứ (Thiên Thần) của TA đến bắt hồn chúng. (Thần Chết) sẽ bảo: “Đâu là những kẻ mà các ngươi đã từng van vái ngoài Allah?” (Các vong hồn) sẽ đáp: “Chúng đã bỏ chúng tôi đi mất rồi.” Và chúng thú tội nghịch với bản thân mình rằng chúng là những kẻ không có đức tin.');
INSERT INTO "hasan_abdulkarim" VALUES(7,38,'7:38','(Allah) sẽ phán: “Các ngươi hãy nhập vào tập đoàn của loài người và Jinn đã qua đời trước các ngươi mà vào trong hỏa ngục. Mỗi lần một tập đoàn mới đi vào, nó nguyền rủa tập đoàn chị của nó trong đó. Mãi cho đến khi tất cả đều vào trong đó (hỏa ngục), đoàn người cuối cùng sẽ tố cáo đoàn người đầu tiên như sau: “Lạy Thượng Đế của bầy tôi, những người này đã dắt bầy tôi đi lạc. Xin Ngài phạt họ gấp đôi trong hỏa ngục.” (Allah) sẽ phán: “Mỗi người sẽ bị phạt gấp đôi, nhưng các ngươi không biết đó thôi”.');
INSERT INTO "hasan_abdulkarim" VALUES(7,39,'7:39','Và tập đoàn đầu tiên sẽ nói với tập đoàn cuối cùng: “Các anh chẳng hưởng đặc ân nào hơn bọn tôi, thôi hãy nếm hình phạt về tội các anh đã làm.”');
INSERT INTO "hasan_abdulkarim" VALUES(7,40,'7:40','Quả thật, những ai đã phủ nhận các Lời Mặc Khải của TA (Allah) và khinh thường chúng thì sẽ không có một cánh cửa nào của bầu trời được mở ra cho họ và họ sẽ không được vào thiên đàng trừ phi con lạc đà chui vào được cái lỗ của chiếc kim may. Và TA trừng phạt những kẻ tội lỗi như thế.');
INSERT INTO "hasan_abdulkarim" VALUES(7,41,'7:41','Họ sẽ có một cái giường trong hỏa ngục với các tấm phủ bên trên. TA trừng phạt những kẻ làm điều sai quấy đúng như thế.');
INSERT INTO "hasan_abdulkarim" VALUES(7,42,'7:42','Ngược lại, những ai có đức tin và làm việc thiện - TA không bắt mỗi linh hồn gánh (trách nhiệm) quá sức của nó - thì sẽ là những người bạn của thiên đàng, trong đó họ sẽ vào ở đời đời.');
INSERT INTO "hasan_abdulkarim" VALUES(7,43,'7:43','Và TA (Allah) sẽ xóa đi khỏi lòng họ nỗi oán thù; sẽ có các dòng sông chảy bên dưới họ và họ sẽ thưa: “Mọi lời ca tụng đều dâng lên Allah, Đấng đã hướng dẫn bầy tôi đến chỗ này; nếu Allah không hướng dẫn thì bầy tôi sẽ không bao giờ tìm thấy Chỉ Đạo. Quả thật, các Sứ Giả của Thượng Đế chúng tôi đã mang Chân Lý đến”. Và có tiếng hô lớn: “Đấy là thiên đàng của quí vị; quí vị thừa hưởng nó vì điều (thiện) mà quí vị đã từng làm.”');
INSERT INTO "hasan_abdulkarim" VALUES(7,44,'7:44','Và những người bạn của thiên đàng sẽ gọi những người bạn của hỏa ngục bảo: “Chúng tôi thấy điều mà Thượng Đế chúng tôi đã hứa với chúng tôi xảy ra thật. Thế các anh có thấy điều mà Thượng Đế các anh đã hứa có thật chăng? (Họ) sẽ đáp: “Vâng, thật”. Nhưng có một người hô lớn giữa bọn họ: “Allah nguyền rủa những kẻ làm điều sai quấy:');
INSERT INTO "hasan_abdulkarim" VALUES(7,45,'7:45','Những ai đã cản trở (người khác) theo con đường của Allah và còn ủm cách bẻ cong nó và phủ nhận đời sau.”');
INSERT INTO "hasan_abdulkarim" VALUES(7,46,'7:46','Và giữa hai (loại người đó) có một bức màn ngăn cách. Và trên các cao điểm có những người biết mặt từng người của họ qua các dấu vết của họ. Và họ lớn tiếng gọi những người bạn của thiên đàng, chúc: “Mong quí vị được bằng an!” Họ chưa vào đó (thiên đàng), nhưng hy vọng sẽ được vào.');
INSERT INTO "hasan_abdulkarim" VALUES(7,47,'7:47','Và khi cặp mắt của họ quay nhìn về phía những người bạn của hỏa ngục, họ cầu nguyện thưa: “Lạy Thượng Đế của bầy tôi! Xin Ngài đừng đặt bầy tôi cùng với những kẻ làm điều sai quấy.”');
INSERT INTO "hasan_abdulkarim" VALUES(7,48,'7:48','Và những người bạn nơi các cao điểm lớn tiếng gọi những người (trong hỏa ngục) mà họ biết mặt qua nét đặc trưng của chúng, và nói với chúng: “Số (người) đông đảo và của cải dồi dào mà các người đã từng tự hào có giúp ích gì được cho các người chăng?.”');
INSERT INTO "hasan_abdulkarim" VALUES(7,49,'7:49','“Phải chăng đây là những người mà các người đã mạnh miệng thề rằng Allah sẽ không bao giờ khoan dung họ?” (Có lời bảo những người đã được Allah khoan dung), “Quí vị hãy bước vào thiên đàng, quí vị sẽ không lo sợ cũng sẽ không buồn phiền”.');
INSERT INTO "hasan_abdulkarim" VALUES(7,50,'7:50','Và những người bạn của hỏa ngục lớn tiếng gọi những người bạn của thiên đàng, nói: “Quí vị hãy rót xuống cho chúng tôi nước uống hoặc bất cứ món gì mà Allah đã ban cấp cho quí vị.” Họ đáp: “Quả thật, Allah cấm cả hai thứ đó cho những người không tin tưởng:');
INSERT INTO "hasan_abdulkarim" VALUES(7,51,'7:51','“Những ai đã dùng tôn giáo của mình làm phương tiện giải trí và mua vui và bị đời sống trần tục này dối gạt thì vào Ngày (Xét xử) đó, TA (Allah) sẽ quên bẵng chúng giống như việc chúng quên bẵng Ngày hội ngộ này của chúng (với TA) và việc chúng thường xuyên tạc các dấu hiệu của TA.');
INSERT INTO "hasan_abdulkarim" VALUES(7,52,'7:52','Và quả thật, TA (Allah) đã mang đến cho chúng một Kinh Sách (Qur''an) đã được TA giải thích rõ ràng căn cứ vào sự hiểu biết (của TA). (Kinh Sách này) là một Chỉ Đạo và là một Hồng Ân cho đám người có đức tin.');
INSERT INTO "hasan_abdulkarim" VALUES(7,53,'7:53','Phải chăng chúng chỉ chờ xem kết quả của nó (Lời phán trong Kinh Sách)? Vào Ngày (mà lời cảnh cáo của) nó được thể hiện, những ai đã quên bẵng nó sẽ nói: “Quả thật, các Sứ Giả của Thượng Đế bầy tôi đã mang Chân Lý đến. Thế có người can thiệp nào đứng ra can thiệp giùm chúng tôi hoặc có thể nào chúng tôi được trả về trần gian trở lại để chúng tôi có dịp làm khác với việc mà chúng tôi đã từng làm trong quá khứ hay chăng?” Chắc chắn chúng đã đánh mất linh hồn của chúng và những điều mà chúng đã từng bịa đặt ra sẽ bỏ chúng đi biệt dạng.');
INSERT INTO "hasan_abdulkarim" VALUES(7,54,'7:54','Quả thật, Thượng Đế (Đấng Chủ Tể) của các ngươi là Allah, Đấng đã tạo các tầng trời và trái đất trong sáu ngày( rồi Ngài an vị trên chiếc Ngai vương. Ngài lấy ban đêm phủ ban ngày, đêm và ngày đuổi theo bắt nhau một cách nhanh chóng; và mặt trời, mặt trăng và các tinh tú đều phụng mệnh Ngài. Tuyệt diệu thay, Ngài tạo hóa và chỉ huy tất cả! Thật phúc thay Allah, Thượng Đế (Đấng Chủ Tể) của vũ trụ và muôn loài!');
INSERT INTO "hasan_abdulkarim" VALUES(7,55,'7:55','Hãy cầu nguyện Thượng Đế của các ngươi trong sự hạ mình và kín đáo bởi vì Ngài không yêu thương những kẻ vượt quá mức giới hạn.');
INSERT INTO "hasan_abdulkarim" VALUES(7,56,'7:56','Và chớ gây rối trên trái đất sau khi nó đã được ổn định trật tự và các ngươi hãy cầu nguyện Ngài với nỗi sợ hãi cùng với niềm hy vọng (trong lòng) bởi vì quả thật Hồng Ân của Allah luôn luôn tiếp cận với những người làm tốt.');
INSERT INTO "hasan_abdulkarim" VALUES(7,57,'7:57','Và Ngài là Đấng đưa những luồng gió báo trước tin mừng về Hồng Ân của Ngài; mãi cho đến khi chúng tập trung các lớp mây chứa nước mưa lại, TA chuyển chúng đến một mảnh đất chết cứng rồi TA cho đổ mưa lên đó và làm mọc ra đủ loại hoa mầu. TA sẽ dựng người chết (sống lại) đúng như thế để cho các ngươi ghi nhớ (việc phục sinh).');
INSERT INTO "hasan_abdulkarim" VALUES(7,58,'7:58','Và đất tốt (sạch) cho mọc ra hoa mầu phì nhiêu theo sự chấp thuận của Thượng Đế của nó; và đất xấu chỉ cho đâm ra hoa mầu lưa thưa. TA giải thích các dấu hiệu đúng như thế cho đám người biết tri ân.');
INSERT INTO "hasan_abdulkarim" VALUES(7,59,'7:59','Quả thật, TA (Allah) đã cử Nuh (Nô-ê) đến cho người dân của Y. Y bảo (họ): “Này hỡi dần ta! Hãy thờ phụng Allah, các người không có Thượng Đế nào khác ngoài Ngài. Ta sợ giùm cho các người về sự trừng phạt của một Ngày vĩ đại!”');
INSERT INTO "hasan_abdulkarim" VALUES(7,60,'7:60','Các lãnh tụ trong người dân của Y đáp: “Quả thật, bọn ta thấy ngươi lầm lạc rõ ràng.”');
INSERT INTO "hasan_abdulkarim" VALUES(7,61,'7:61','(Nuh) bảo: “Hỡi dân ta! Ta chẳng có gì lầm lạc cả. Ta là một Sứ Giả của Thượng Đế của vũ trụ và muôn loài.');
INSERT INTO "hasan_abdulkarim" VALUES(7,62,'7:62','"Ta truyền đạt cho các người Bức Thông Điệp của Thượng Đế của ta và ta là một người cố vấn thành thật. Và ta biết từ Allah điều mà các người không biết".');
INSERT INTO "hasan_abdulkarim" VALUES(7,63,'7:63','“Há các người ngạc nhiên về Bức Thông Điệp của Thượng Đế của các người do một người phàm xuất thân từ các người mang đến để nhắc nhở các người hầu các người sợ Allah và được khoan dung hay chăng?”');
INSERT INTO "hasan_abdulkarim" VALUES(7,64,'7:64','Nhưng họ đã cho rằng Y nói dối, cho nên TA đã cứu Y và những người theo Y trên một chiếc tàu, và TA đã nhận chìm (dưới nước lụt) những ai đã phủ nhận các dấu hiệu của TA. Quả thật, chúng là một đám người mù quáng.');
INSERT INTO "hasan_abdulkarim" VALUES(7,65,'7:65','Và về dân tộc ''Ad, TA đã cử Hud, một người anh em của họ đến với họ. (Hud) bảo (họ): “Hỡi dân ta! Hãy thờ phụng Allah, các người không có một Thượng Đế nào khác ngoài Ngài. Thế các người không sợ Allah hay sao?”');
INSERT INTO "hasan_abdulkarim" VALUES(7,66,'7:66','Các lãnh tụ của những kẻ không có đức tin trong người dân của Y lên tiếng: “Thật sự bọn ta thấy ngươi điên rồ và bọn ta nghĩ rằng ngươi là một tên nói dối.”');
INSERT INTO "hasan_abdulkarim" VALUES(7,67,'7:67','(Hud) bảo: “Ta không ngu xuẩn, ngược lại ta là một Sứ Giả của Thượng Đế của vũ trụ và muôn loài".');
INSERT INTO "hasan_abdulkarim" VALUES(7,68,'7:68','“Ta truyền đạt cho các người những Bức Thông Điệp của Thượng Đế của Ta và Ta là một người cố vấn rất tín nhiệm cho các người".');
INSERT INTO "hasan_abdulkarim" VALUES(7,69,'7:69','“Há các người ngạc nhiên về một thông điệp nhắc nhở từ Thượng Đế của các người được gởi đến cho các người qua trung gian của một người phàm xuất thân từ các người được dùng để cảnh cáo các người hay sao? Và hãy nhớ lại khi Ngài (Allah) đã chỉ định các người nối nghiệp người dân của Nuh và tạo các người có vóc dáng to lớn. Do đó, hãy nhớ các đặc ân của Allah đã ban cho (các người) để may ra các người được phát đạt.”');
INSERT INTO "hasan_abdulkarim" VALUES(7,70,'7:70','Họ đáp: “Có phải ngươi đến yêu cầu bọn ta tôn thờ riêng Allah thôi và từ bỏ những vị mà cha mẹ của bọn ta đã từng thờ phụng hay sao? Nếu ngươi là một người nói thật thì hãy mang cái (hình phạt) mà ngươi thường nói để hăm dọa bọn ta đến trừng phạt bọn ta đi.”');
INSERT INTO "hasan_abdulkarim" VALUES(7,71,'7:71','(Hud) bảo: “Quả thật hình phạt và sự giận dữ của Thượng Đế của các người đã rơi nhằm phải các người rồi. Phải chăng các người tranh luận với ta về những tên gọi mà các người lẫn cha mẹ của các người đã bịa đặt, điều mà Allah đã không ban cho một thẩm quyền nào? Thế các người hãy đợi xem, ta sẽ cùng đợi với các người.”');
INSERT INTO "hasan_abdulkarim" VALUES(7,72,'7:72','Bởi thế, TA lấy lòng Khoan dung mà giải cứu Hud với những ai theo Y và chặt đứt gốc rễ của những kẻ đã phủ nhận các dấu hiệu của TA bởi vì chúng là những kẻ không có đức tin.');
INSERT INTO "hasan_abdulkarim" VALUES(7,73,'7:73','Và về người dân Thamud, TA đã cử Saleh, một người anh em của họ đến với họ. (Saleh) bảo (họ): “Hỡi dân ta! Hãy thờ phụng Allah, các người không có Thượng Đế nào khác ngoài Ngài cả. Quả thật, một dấu hiệu rõ rệt từ Thượng Đế của các người đã đến với các người: con lạc đà cái này của Allah là một dấu hiệu cho các người. Bởi thế, hãy để mặc nó ăn cỏ trên đất đai của Allah, và chớ mó tay hãm hại nó; nếu không nghe, các người sẽ bị trừng phạt đau đớn.');
INSERT INTO "hasan_abdulkarim" VALUES(7,74,'7:74','Và (hãy nhớ) khi Ngài đã chỉ định các ngươi nối nghiệp (người dân) ''Ad và ban cho các người chỗ ngụ trên trái đất, các ngươi đã xây cất dinh thự nơi đồng bằng và đục núi làm nhà trong đó. Bởi thế, hãy nhớ các ân huệ của Allah và chớ là những kẻ phá rối trị an trên trái đất.');
INSERT INTO "hasan_abdulkarim" VALUES(7,75,'7:75','Các lãnh tụ của những kẻ ngạo mạn trong đám người dân của Y (Saleh) nói với những người yếu thế, và những ai có đức tin trong họ, bảo: “Các người có biết chắc Saleh là một Sứ Giả của Thượng Đế của Y hay không? Họ đáp: “Chúng tôi tin tưởng nơi điều (mặc khải) mà Người mang đến.”');
INSERT INTO "hasan_abdulkarim" VALUES(7,76,'7:76','Những kẻ ngạo mạn đáp: “Bọn ta phủ nhận điều mà các người hằng tin tưởng.”');
INSERT INTO "hasan_abdulkarim" VALUES(7,77,'7:77','Xong chúng (những kẻ không tin) cắt nhượng con lạc đà cái và xấc xược thách đố mệnh lệnh của Thượng Đế của chúng bởi vì chúng nói: “Hỡi Saleh! Nếu ngươi là một Sứ Giả của Allah thì hãy mang cái (hình phạt) mà ngươi dọa dẫm bọn ta đến trừng phạt bọn ta đi.”');
INSERT INTO "hasan_abdulkarim" VALUES(7,78,'7:78','Do đó, một trận động đất đã chụp bắt chúng vào buổi sáng làm cho chúng chết chúi đầu trong nhà.');
INSERT INTO "hasan_abdulkarim" VALUES(7,79,'7:79','Tiếp đó, Saleh rời bỏ chúng, và bảo: “Hỡi dân ta! Quả thật, ta đã truyền đạt cho các người Thông Điệp của Thượng Đế của ta và khuyên bảo các người điều tốt lành nhưng các người không thích những người cố vấn tốt.”');
INSERT INTO "hasan_abdulkarim" VALUES(7,80,'7:80','Và (hãy nhớ) Lut khi Y bảo người dân của Y (như sau): “Phải chăng các người đã làm một điều thô bỉ nhất mà không một người nào trong thiên hạ đã làm như các người?".');
INSERT INTO "hasan_abdulkarim" VALUES(7,81,'7:81','“Các người đã làm tình với đàn ông thay vì phụ nữ. Không, các người là một đám người vượt quá mức giới hạn.”');
INSERT INTO "hasan_abdulkarim" VALUES(7,82,'7:82','Và người dân của Y không biết nói gì để trả lời ngoài điều này: “Hãy tống cổ họ ra khỏi thành phố của các người! Bởi vì họ là những người muốn được trong sạch.”');
INSERT INTO "hasan_abdulkarim" VALUES(7,83,'7:83','Nhưng TA đã cứu Y (Lut) và gia đình của Y ngoại trừ người vợ của Y; bà ta là một người trì trệ đằng sau.');
INSERT INTO "hasan_abdulkarim" VALUES(7,84,'7:84','Và TA đã trút lên họ một trận mưa đá (diêm sinh). Bởi thế, hãy xem kết cuộc của những kẻ tội lỗi đã xảy ra như thế nào!');
INSERT INTO "hasan_abdulkarim" VALUES(7,85,'7:85','Về người dân Madyan (Madian), TA đã cử Shu''aib, một người anh em của họ đến với họ. Shu''aib bảo (họ): “Hỡi dân ta! Hãy thờ phụng Allah, các người không có Thượng Đế nào khác ngoài Ngài cả. Quả thật, đã có một bằng chứng rõ rệt từ Thượng Đế của các người đến với các người. Bởi thế, hãy đo cho đúng và cân cho đủ và chớ lấy bớt hàng hóa của người và chớ gây phá rối trên trái đất sau khi nó đã được ổn định trật tự. Điều đó tốt nhất cho các người nếu các người có đức tin".');
INSERT INTO "hasan_abdulkarim" VALUES(7,86,'7:86','“Và chớ ngồi chờ tại mỗi ngã đường dọa dẫm và cản trở những ai tin nơi Ngài (Allah) xa lánh con đường của Ngài và tìm cách bẻ cong nó. Và hãy nhớ lại khi các người chỉ gồm một thiểu số rồi Ngài gia tăng nhân số của các người thêm đông; và hãy xét xem kết cuộc của những kẻ phá họai xảy ra như thế nào".');
INSERT INTO "hasan_abdulkarim" VALUES(7,87,'7:87','“Và nếu trong các người có một số người tin tưởng nơi điều (mặc khải) mà ta mang đến và có một số khác không tin tưởng thì hãy kiên nhẫn chịu đựng cho đến khi Allah xét xử giữa chúng ta bởi vì Ngài là Đấng Xét Xử Ưu Việt.”');
INSERT INTO "hasan_abdulkarim" VALUES(7,88,'7:88','Các lãnh tụ của những kẻ kiêu căng trong đám dân của Y lên tiếng: “Hỡi Shu''aib, hoặc bọn ta sẽ tống cổ ngươi và các tín đồ theo ngươi ra khỏi thị trấn của bọn ta hoặc các ngươi sẽ phải trở lại với tín ngưỡng của bọn ta.” (Shu''aib) đáp: "Sao? Dẫu chúng tôi cả ghét nó nữa hay sao!"');
INSERT INTO "hasan_abdulkarim" VALUES(7,89,'7:89','“Nếu trở lại với tín ngưỡng của quí vị thì chắc chắn chúng tôi bắt buộc phải bịa đặt điều nói dối với Allah sau khi Ngài đã giải thoát chúng tôi ra khỏi (tín ngưỡng) đó. Chúng tôi không có cách nào trở về với nó được trừ phi Allah, Thượng Đế của chúng tôi muốn khác đi. Sự hiểu biết của Thượng Đế của chúng tôi bao quát tất cả mọi vấn đề. Chúng tôi trọn tin và phó thác cho Allah. (Họ cầu nguyện thưa:) “Lạy Thượng Đế của bầy tôi! Xin Ngài dùng sự thật mà quyết định giữa bầy tôi với người dân của bầy tôi bởi vì Ngài là Đấng Quyết Định Ưu Việt.”');
INSERT INTO "hasan_abdulkarim" VALUES(7,90,'7:90','Các lãnh tụ của những kẻ không có đức tin trong đám người dân của Y lên tiếng: “Nếu các người tuân theo Shu''aib thì chắc chắn các người sẽ thua thiệt!”');
INSERT INTO "hasan_abdulkarim" VALUES(7,91,'7:91','Nhưng một trận động đất bất thần chụp bắt họ vào buổi sáng khiến họ chết chúi đầu trong nhà.');
INSERT INTO "hasan_abdulkarim" VALUES(7,92,'7:92','Những ai cho Shu''aib nói dối đã bị tiêu diệt toàn bộ làm như họ đã không từng sống nơi đó bao giờ. Những ai đã bảo Shu''aib nói dối mới là những kẻ thua thiệt.');
INSERT INTO "hasan_abdulkarim" VALUES(7,93,'7:93','Thế rồi Shu''aib từ giả (họ) ra đi và nói với họ: “Hỡi dân ta! Quả thật, ta đã truyền đạt cho các người những Bức Thông Điệp của Thượng Đế của ta và khuyên bảo các người điều tốt lành. Nhưng làm sao ta có thể thương tiếc cho một đám người không tin tưởng.”');
INSERT INTO "hasan_abdulkarim" VALUES(7,94,'7:94','Và TA đã không cử một vị Nabi nào đến một thị trấn mà lại không bắt dân cư của nó chịu cam khổ và hoạn nạn để cho họ hạ mình thần phục.');
INSERT INTO "hasan_abdulkarim" VALUES(7,95,'7:95','Rồi TA đổi điều xấu (khốn khổ) sang điều tốt (trù phú) cho đến khi họ phát đạt dồi dào và họ nói: “ Cha mẹ của chúng tôi cũng đã từng trải cảnh nghèo và cảnh giàu”. Thế rồi TA bất thần túm bắt họ trong lúc họ không ngờ đến.');
INSERT INTO "hasan_abdulkarim" VALUES(7,96,'7:96','Và nếu dân cư của các thị trấn tin tưởng và sợ Allah thì chắc chắn TA sẽ mở ra cho họ bao thứ ân phúc từ trên trời xuống dưới đất nhưng họ phủ nhận (Allah và các Sứ Giả) cho nên TA đã bắt phạt họ về các điều (tội lỗi) mà họ đã làm.');
INSERT INTO "hasan_abdulkarim" VALUES(7,97,'7:97','Dân cư của các thị trấn cảm thấy an toàn chăng khi cơn thịnh nộ của TA đến chụp bắt họ vào ban đêm trong lúc họ đang yên giấc?');
INSERT INTO "hasan_abdulkarim" VALUES(7,98,'7:98','Hoặc họ cảm thấy an toàn chăng khi cơn thịnh nộ của TA đến với họ vào lúc mặt trời lên cao khi họ đang vui đùa?');
INSERT INTO "hasan_abdulkarim" VALUES(7,99,'7:99','Hoặc họ cảm thấy an toàn chăng trước kế hoạch bí mật của Allah? Nhưng không ai cảm thấy an toàn trước kế hoạch của Allah ngoại trừ đám người thua thiệt.');
INSERT INTO "hasan_abdulkarim" VALUES(7,100,'7:100','Há không là một Chỉ Đạo (bài học) cho những ai thừa kế trái đất sau khi dân cư của nó (đã bị tiêu diệt)? rằng nếu muốn, TA sẽ thừa sức tiêu diệt họ vì tội lỗi của họ và niêm kín tấm lòng của họ khiến họ không nghe biết gì hay sao?');
INSERT INTO "hasan_abdulkarim" VALUES(7,101,'7:101','Đó là câu chuyện về các thị trấn mà TA kể lại cho Ngươi (Muhammad); và quả thật đã có những Sứ Giả xuất thân từ họ đến gặp họ với những bằng chứng rõ rệt nhưng họ không tin tưởng nơi điều (mặc khải) mà họ đã từng phủ nhận trước đó. Allah niêm kín tấm lòng của những kẻ không tin đúng như thế.');
INSERT INTO "hasan_abdulkarim" VALUES(7,102,'7:102','Và TA thấy đa số bọn họ không giữ đúng lời hứa và quả thật TA thấy bọn họ chống đối và bất tuân.');
INSERT INTO "hasan_abdulkarim" VALUES(7,103,'7:103','Rồi sau họ, TA đã cử Musa mang những dấu hiệu của TA đến gặp Fir-''awn (Pharaon) và các vị tù trưởng của y nhưng chúng phủ nhận các dấu hiệu đó; bởi thế, hãy nhìn xem kết cuộc của những kẻ thối nát đã xảy ra như thế nào.');
INSERT INTO "hasan_abdulkarim" VALUES(7,104,'7:104','Và Musa bảo: “Hỡi Fir-''awn, Ta đây là một Sứ Giả do Thượng Đế của vũ trụ và muôn loài phái đến".');
INSERT INTO "hasan_abdulkarim" VALUES(7,105,'7:105','“Ta chỉ được quyền nói sự thật về Allah. Nay Ta đến gặp các người với một bằng chứng rõ rệt từ Thượng Đế của các người; do đó, hãy để cho con cháu của Israel ra đi cùng với Ta.”');
INSERT INTO "hasan_abdulkarim" VALUES(7,106,'7:106','(Fir-''awn) đáp: “Nếu đến với một dấu hiệu thì ngươi hãy trưng bày nó ra xem nếu ngươi nói thật.”');
INSERT INTO "hasan_abdulkarim" VALUES(7,107,'7:107','Nghe bảo (Musa) liền ném chiếc gậy của Y xuống tức thời nó biến thành một con rắn rõ rệt.');
INSERT INTO "hasan_abdulkarim" VALUES(7,108,'7:108','Và (Musa) đưa bàn tay của Y ra tức thời nó chói sáng trước mắt những người đứng nhìn.');
INSERT INTO "hasan_abdulkarim" VALUES(7,109,'7:109','Các lãnh tụ của đám người dân của Fir-''awn (kinh hãi) nói: “Quả thật đây là một nhà ảo thuật tài giỏi".');
INSERT INTO "hasan_abdulkarim" VALUES(7,110,'7:110','“Hắn muốn đuổi quí vị ra khỏi lãnh thổ của quí vị. Thế quí vị có ý kiến gì?”');
INSERT INTO "hasan_abdulkarim" VALUES(7,111,'7:111','Họ tâu (với Fir-''awn:) “Giữ hắn và anh của hắn lại (trong một thời gian) và cử người đi khắp các thị trấn cáo thị (và tập trung)".');
INSERT INTO "hasan_abdulkarim" VALUES(7,112,'7:112','“Đưa về trình diện bệ hạ tất cả các vị phù thủy tài giỏi (của chúng ta).”');
INSERT INTO "hasan_abdulkarim" VALUES(7,113,'7:113','Và các vị phù thủy đến trình diện Fir-''awn. Họ tâu: “(Thưa bệ hạ!) Đương nhiên chúng tôi sẽ được ân thưởng nếu chúng tôi thắng cuộc.”');
INSERT INTO "hasan_abdulkarim" VALUES(7,114,'7:114','(Fir-''awn) bảo: “Vâng, trong trường hợp đó, các ngươi sẽ là những cận thần của ta.”');
INSERT INTO "hasan_abdulkarim" VALUES(7,115,'7:115','(Các vị phù thủy) lên tiếng: “Hỡi Musa! Hoặc ngươi ném (bửu bối của ngươi) xuống trước hoặc bọn ta sẽ ném trước?”');
INSERT INTO "hasan_abdulkarim" VALUES(7,116,'7:116','(Musa) đáp: “Quí vị ném xuống trước.” Do đó, họ ném bửu bối của họ xuống. Họ làm hoa mắt người dân và khiến họ khiếp đãm trước một trò ảo thuật lớn lao.');
INSERT INTO "hasan_abdulkarim" VALUES(7,117,'7:117','Nhưng TA (Allah) đã mặc khải cho Musa như sau: “Hãy ném chiếc gậy của Ngươi xuống.” Nó tức khắc nuốt trọn các vật mà họ đã làm giả.');
INSERT INTO "hasan_abdulkarim" VALUES(7,118,'7:118','Bởi thế, sự Thật đã toàn thắng và những món vật giả mà họ đã làm trở thành vô hiệu.');
INSERT INTO "hasan_abdulkarim" VALUES(7,119,'7:119','Cho nên họ thua cuộc và bị khinh miệt.');
INSERT INTO "hasan_abdulkarim" VALUES(7,120,'7:120','Và các tên phù thủy sụp xuống quỳ lạy (khuất phục)');
INSERT INTO "hasan_abdulkarim" VALUES(7,121,'7:121','Họ thưa: “Chúng tôi tin tưởng nơi Thượng Đế của vũ trụ và muôn loài".');
INSERT INTO "hasan_abdulkarim" VALUES(7,122,'7:122','“Thượng Đế của Musa và Harun"');
INSERT INTO "hasan_abdulkarim" VALUES(7,123,'7:123','Fir-''awn bảo: “Các ngươi bạo gan tin tưởng nơi y (Musa) trước khi ta cho phép các ngươi hay sao? Quả thật, đây là một mưu kế mà các ngươi đã thảo hoạch với nhau trong thành phố hầu trục xuất dân cư của nó đi nơi khác. Rồi đây các ngươi sẽ sớm biết (hậu quả của việc làm của các ngươi).');
INSERT INTO "hasan_abdulkarim" VALUES(7,124,'7:124','“Chắc chắn ta sẽ cho chặt tay và chân của các ngươi mỗi bên một cái rồi chắc chắn ta sẽ cho đóng đinh tất cả bọn ngươi trên thập tự giá.”');
INSERT INTO "hasan_abdulkarim" VALUES(7,125,'7:125','Họ nói: “Bề nào chúng tôi cũng sẽ trở về với Thượng Đế (Allah) của chúng tôi".');
INSERT INTO "hasan_abdulkarim" VALUES(7,126,'7:126','“Còn bệ hạ, bệ hạ nhất định trả thù chúng tôi chỉ vì chúng tôi tin tưởng nơi các dấu hiệu của Thượng Đế chúng tôi khi chúng đến với chúng tôi hay sao? (Họ cầu nguyện): ''Lạy Thượng Đế của bầy tôi! Xin Ngài xối lên bầy tôi sự kiên trì nhẫn nại và bắt hồn bầy tôi chết như là người Muslim (thần phục Ngài).”');
INSERT INTO "hasan_abdulkarim" VALUES(7,127,'7:127','Các lãnh tụ trong đám thuộc hạ của Fir’awn tâu: “Ngài sẽ để cho Musa và người dân của nó làm loạn trong xứ, bỏ rơi bệ hạ và các thần linh của bệ hạ hay sao? (Fir’awn) đáp: “Ta sẽ cho giết con trai của chúng và tha mạng các phụ nữ của chúng và áp chế bọn chúng.”');
INSERT INTO "hasan_abdulkarim" VALUES(7,128,'7:128','Musa bảo người dân của Y: “Hãy cầu xin Allah giúp đỡ và hãy kiên nhẫn bởi vì quả thật đất đai là của Allah. Nếu Ngài muốn, Ngài sẽ cho người nào trong số bầy tôi của Ngài thừa hưởng nó. Và kết quả cuối cùng sẽ thuộc về những người sợ Allah.”');
INSERT INTO "hasan_abdulkarim" VALUES(7,129,'7:129','Họ thưa lại: “Chúng tôi gặp phải hoạn nạn trước và sau khi Thầy đến với chúng tôi.” (Musa) đáp: “Có lẽ vì thế mà Thượng Đế của các người sẽ tiêu diệt kẻ thù của các người và sẽ cho các người nối nghiệp họ trên trái đất và xem các người hành động ra sao?”');
INSERT INTO "hasan_abdulkarim" VALUES(7,130,'7:130','Và quả thật TA (Allah) đã trừng phạt các thuộc hạ của Fir’awn với những năm hạn hán và thất mùa để cho chúng có thể tỉnh ngộ.');
INSERT INTO "hasan_abdulkarim" VALUES(7,131,'7:131','Và khi gặp điều phúc lành, chúng nói: “Cái này là của chúng tôi.” Nhưng khi gặp điều bất hạnh, chúng đổ tội cho Musa và những ai theo Y đã mang điều xui xẻo đến. Nhưng đối với Allah điều xui xẻo chỉ là của chúng nhưng đa số bọn chúng không biết.');
INSERT INTO "hasan_abdulkarim" VALUES(7,132,'7:132','Chúng bảo (Musa:) “Bất cứ dấu lạ nào mà ngươi mang đến để mê hoặc bọn ta, bọn ta nhất định sẽ không tin nơi ngươi.”');
INSERT INTO "hasan_abdulkarim" VALUES(7,133,'7:133','Và TA (Allah) đã giáng lên chúng thiên tai (như chết chóc, lụt lội, cuồng phong) và nạn châu chấu, nạn chí rận, nạn cóc nhái và nạn máu tươi: các dấu hiệu với các chi tiết rõ rệt nhưng chúng vẫn tỏ ra ngạo mạn và trở thành đám người vô cùng tội lỗi.');
INSERT INTO "hasan_abdulkarim" VALUES(7,134,'7:134','Và mỗi lần thiên tai rơi nhằm chúng, chúng nài nỉ với Musa, bảo: “Hỡi Musa! Hãy cầu xin Thượng Đế của ngươi giùm bọn ta qua lời hứa của Ngài với ngươi (rằng) nếu ngươi giải tỏa được thiên tai khỏi bọn ta thì chắc chắn bọn ta sẽ tin tưởng nơi ngươi và bọn ta sẽ để cho con cháu của Israel ra đi cùng với ngươi.”');
INSERT INTO "hasan_abdulkarim" VALUES(7,135,'7:135','Nhưng khi TA (Allah) giải tỏa thiên tai đi khỏi chúng, theo hạn định của chúng thì tức khắc chúng nuốt lời hứa!');
INSERT INTO "hasan_abdulkarim" VALUES(7,136,'7:136','Bởi thế, TA (Allah) đã trả thù chúng và nhận chìm chúng dưới biển (Hồng hải) bởi lẽ chúng đã phủ nhận các dấu hiệu của TA mà chúng đã lơ là.');
INSERT INTO "hasan_abdulkarim" VALUES(7,137,'7:137','Và TA (Allah) đã chỉ định đám người bị xem như yếu thế (người dân Israel) làm những người thừa kế phần đất phía đông và phía tây mà TA đã ban phúc. Và Lời phán tốt đẹp của Thượng Đế của Ngươi dành cho con cháu của Israel đã được hoàn tất bởi vì họ đã chịu đựng gian khổ và TA đã san bằng bao nhiêu công trình do Fir-''awn và đám người của hắn đã xây cất và gầy dựng nên.');
INSERT INTO "hasan_abdulkarim" VALUES(7,138,'7:138','Và TA (Allah) đã đưa con cháu của (Israel) băng qua biển (Hồng hải) an toàn. (Trên đường đi đến miền đất hứa) họ gặp một đám người đang sùng bái một số tượng vật. Thấy thế (người dân Israel) van nài Musa, thưa: “Hỡi Musa! Xin Thầy làm ra cho chúng tôi một thần linh giống như các thần linh của họ.” Musa đáp: “Các ngươi đúng là một đám người ngu muội.”');
INSERT INTO "hasan_abdulkarim" VALUES(7,139,'7:139','Quả thật, đám người này sắp bị tiêu diệt về điều chúng đang làm và việc làm của chúng sẽ trở thành vô ích.');
INSERT INTO "hasan_abdulkarim" VALUES(7,140,'7:140','(Musa) tiếp: “Há ta sẽ phải đi tìm cho các ngươi một Thượng Đế khác Allah hay sao trong lúc Ngài đã ban đặc ân cho các ngươi hơn thiên hạ?”');
INSERT INTO "hasan_abdulkarim" VALUES(7,141,'7:141','Và hãy nhớ lại việc TA (Allah) giải cứu các ngươi thoát khỏi đồng bọn Fir’awn, chúng áp bức các ngươi với bao nhiêu cực hình tàn ác: chúng đã giết con trai của các ngươi và tha mạng các phụ nữ của các ngươi; và trong sự kiện đó là một sự thử thách vô cùng to lớn từ Thượng Đế (Allah) của các ngươi.');
INSERT INTO "hasan_abdulkarim" VALUES(7,142,'7:142','Và TA (Allah) đã hẹn gặp Musa ba mươi đêm (tại núi Sinai) và đã hoàn tất cuộc gặp gỡ đó thêm mười đêm, như thế thời gian gặp gỡ với Thượng Đế của Y là bốn mươi đêm. Nhưng (trước khi ra đi) Musa có dặn Harun, người em của Y: “Em đại diện cho Ta, trông coi người dân Ta và làm cho đúng và chớ theo đường lối của những kẻ thối nát.”');
INSERT INTO "hasan_abdulkarim" VALUES(7,143,'7:143','Và khi Musa đến chỗ hẹn của TA và Thượng Đế của Y tiếp chuyện với Y. (Musa) thưa: “Lạy Thượng Đế của bề tôi! Xin Ngài hiện ra trước mặt để cho bề tôi nhìn thấy Ngài.” (Allah) phán: “Ngươi không bao giờ nhìn thấy TA (trực tiếp) nhưng hãy nhìn về phía ngọn núi kia, nếu nó còn đứng nguyên tại chỗ thì ngươi sẽ thấy TA.” Và khi Thượng Đế của Y biểu dương Hào quang của Ngài lên ngọn núi, nó (hào quang) làm cho nó (ngọn núi) vỡ tan thành bụi. Và Musa té xuống bất tỉnh. Sau khi hồi tỉnh, Y thưa: “Quang vinh thay Ngài! Bề tôi xin sám hối với Ngài và là người đầu tiên tin tưởng (nơi Ngài.)"');
INSERT INTO "hasan_abdulkarim" VALUES(7,144,'7:144','(Allah) phán: “Hỡi Musa! Quả thật, TA đã chọn Ngươi hơn người (khác) qua các Thông Điệp của TA và qua Lời phán trực tiếp của TA. Bởi thế, hãy nhận lấy các điều (mặc khải) mà TA đã ban cho Ngươi và hãy là một người biết ơn.”');
INSERT INTO "hasan_abdulkarim" VALUES(7,145,'7:145','Và TA (Allah) đã ghi cho Y trên các Bia ký lời răn và chi tiết về đủ mọi vấn đề (và dặn): “Hãy giữ kỹ các Bia ký này và chỉ thị cho người dân của Y nắm vững các lời khuyên tốt nhất của chúng. TA sẽ chỉ cho Ngươi thấy nhà ở của những kẻ chống đối và bất tuân.”');
INSERT INTO "hasan_abdulkarim" VALUES(7,146,'7:146','TA sẽ làm cho những kẻ ngạo mạn và khinh thường phải trái trên trái đất xa lánh các dấu hiệu của TA; và nếu thấy từng dấu hiệu thì chúng sẽ không tin tưởng và nếu thấy con đường đúng đắn thì chúng sẽ không nhận đó là Chính Đạo. Và nếu thấy con đường lầm lạc thì chúng sẽ cho đó là Chính Đạo; như thế là bởi vì chúng thường phủ nhận các dấu hiệu của TA và tỏ ra lơ là.');
INSERT INTO "hasan_abdulkarim" VALUES(7,147,'7:147','Và những ai phủ nhận các dấu hiệu của TA cùng với việc gặp gỡ (TA ) ở đời sau thì việc làm của chúng sẽ vô nghĩa. Há chúng chỉ mong được tưởng thưởng (hay trừng phạt) về điều chúng đã làm hay sao?');
INSERT INTO "hasan_abdulkarim" VALUES(7,148,'7:148','Và người dân của Musa, sau khi Y đi khỏi, đã dùng nữ trang của họ đúc thành một con bò con, thân của nó (phát âm giống tiếng) rống (của con bò). Há họ không thấy rằng con bò đúc ấy không nói được cũng không dẫn đường được cho họ hay sao? Họ tôn thờ nó và trở thành những kẻ làm điều sai quấy.');
INSERT INTO "hasan_abdulkarim" VALUES(7,149,'7:149','Và khi hối tiếc về việc đã qua và nhận thấy mình đã lầm lạc, họ cầu nguyện, thưa: “Nếu Thượng Đế (Allah) của bầy tôi không khoan dung và tha thứ thì chắc chắn bầy tôi sẽ là những kẻ thua thiệt.”');
INSERT INTO "hasan_abdulkarim" VALUES(7,150,'7:150','Và khi trở về gặp lại người dân của mình, Musa vừa giận vừa buồn. Y lên tiếng: “Thật là tội lỗi điều mà các ngươi đã làm sau khi ta đi vắng. Phải chăng các ngươi hấp tấp qua mặt chỉ thị của Thượng Đế của các ngươi hay sao? Và Y ném các tấm Bia ký xuống đất, nắm lấy chùm tóc của người em và kéo về phía mình. (Harun) lên tiếng: “Hỡi anh của em! Dân chúng xem thường em và còn toan giết ta nữa; do đó, anh chớ để cho kẻ thù vui thích trên sự đau khổ của em và chớ liệt em vào thành phần của đám người làm điều sai quấy.');
INSERT INTO "hasan_abdulkarim" VALUES(7,151,'7:151','Musa cầu nguyện, thưa: “Lạy Thượng Đế của bề tôi! Xin Ngài tha thứ cho bề tôi và người em của bề tôi và khoan dung với bầy tôi bởi vì Ngài là Đấng Rất Mực Khoan Dung.”');
INSERT INTO "hasan_abdulkarim" VALUES(7,152,'7:152','Quả thật, những ai thờ con bò con sẽ bị Thượng Đế (Allah) của họ giận dữ và bị hạ nhục ở đời này. TA trừng phạt những kẻ bịa đặt điều gian dối đúng như thế.');
INSERT INTO "hasan_abdulkarim" VALUES(7,153,'7:153','Ngược lại, những ai đã làm điều tội lỗi rồi sau đó ăn năn hối cải và tin tưởng (thật sự) (thì sẽ thấy) Thượng Đế của Ngươi sau việc đó rất mực Tha thứ và Khoan dung (đối với họ).');
INSERT INTO "hasan_abdulkarim" VALUES(7,154,'7:154','Và khi nguôi giận, Musa lượm các tấm Bia ký lên và trong bản văn có ghi Chỉ Đạo và Hồng Ân cho những ai sợ Thượng Đế của họ.');
INSERT INTO "hasan_abdulkarim" VALUES(7,155,'7:155','Và Musa đã chọn trong đám người dân của mình bảy mươi người để đưa đến chỗ hẹn của TA (Allah). Nhưng khi họ bị lay chuyển bởi trận động đất dữ dội, (Musa) cầu nguyện, thưa: “Lạy Thượng Đế của bề tôi! Nếu Ngài muốn thì Ngài đã giết cả họ lẫn bề tôi từ trước (cuộc gặp gỡ này). Phải chăng Ngài muốn tiêu diệt bầy tôi vì tội lỗi do một số người điên rồ trong bầy tôi đã làm? Đó chẳng qua là sự thử thách mà Ngài dùng để đánh lạc hướng kẻ nào Ngài muốn và hướng dẫn người nào Ngài muốn. Ngài là Đấng Bảo Hộ của bầy tôi, do đó xin Ngài tha thứ và khoan dung với bầy tôi. Bởi vì Ngài là Đấng Tha Thứ Ưu Việt.');
INSERT INTO "hasan_abdulkarim" VALUES(7,156,'7:156','“Và xin Ngài qui định cho bầy tôi điều tốt ở đời này và đời sau. Quả thật, bầy tôi quay về hối cải với Ngài.” (Allah) phán: TA sẽ dùng hình phạt của TA mà trừng trị kẻ nào TA muốn nhưng lòng Khoan dung của TA bao quát lên tất cả mọi thứ. TA sẽ qui định (sự Khoan dung của TA) cho những ai ngay chính sợ Allah và đóng Zakah và những ai là người tin tưởng nơi các dấu hiệu của TA:');
INSERT INTO "hasan_abdulkarim" VALUES(7,157,'7:157','Những ai tuân theo Sứ Giả (Muhammad), một Nabi không biết chữ được nhắc qua trong (các Kinh Sách của) họ; Kinh Tawrah và Injil( - Y ra lệnh cho họ làm điều lành và cấm cản làm điều dữ; Y cho phép họ dùng thực phẩm tốt và sạch và cấm họ dùng thực phẩm dơ bẩn; và Y tháo bớt gánh nặng của họ cũng như giải phóng họ khỏi các gông cùm đang đè nặng lên họ. Bởi thế, những ai tin tưởng nơi Y và ủng hộ Y và giúp đỡ Y và tuân theo ánh sáng đã được gởi xuống cùng với Y thì là những người sẽ thành đạt.');
INSERT INTO "hasan_abdulkarim" VALUES(7,158,'7:158','Hãy bảo (họ): “Hỡi nhân loại! Ta là Sứ Giả của Allah được cử phái đến cho tất cả các người; (Allah) Đấng đang thống trị các tầng trời và trái đất; không có Thượng Đế nào khác cả, duy chỉ Ngài (là Thượng Đế); Ngài ban sự sống và gây cho chết. Bởi thế, hãy tin tưởng nơi Ngài và Sứ Giả của Ngài, một Nabi không biết chữ. Y cũng tin tưởng nơi Allah và các Lời phán của Ngài. Hãy tuân theo Y để cho các ngươi được hướng dẫn đúng đường.');
INSERT INTO "hasan_abdulkarim" VALUES(7,159,'7:159','Và trong đám người dân của Musa, có một tập thể dựa vào Chân Lý mà hướng dẫn và xét xử công bằng.');
INSERT INTO "hasan_abdulkarim" VALUES(7,160,'7:160','Và TA (Allah) đã phân chia họ thành mười hai bộ lạc. Và TA đã mặc khải cho Musa khi người dân của Y đòi cung cấp nước uống (như sau): “Hãy dùng chiếc gậy của Ngươi mà đánh lên tảng đá.” Tức thời từ đó phụt ra mười hai ngọn suối. Mỗi bộ lạc biết điểm nước của họ. Và TA đã dùng các lùm mây để che mát họ; và đã ban mật ong và chim cút xuống cho họ và phán: “Hãy dùng các món thực phẩm tốt và sạch mà TA cung cấp cho các ngươi.” (Nhưng họ không bằng lòng). Và họ chẳng làm thiệt hại gì TA, ngược lại họ chỉ làm thiệt thân họ mà thôi.');
INSERT INTO "hasan_abdulkarim" VALUES(7,161,'7:161','Và khi họ được khuyên: “Hãy ở trong thị trấn (Jerusalem) này và ăn (thực phẩm) bất cứ nơi nào tùy thích nhưng hãy nói: Xin Ngài tha thứ và bước vào cửa với dáng điệu phủ phục. TA sẽ tha thứ lỗi lầm cho các ngươi và TA sẽ tăng thêm phần thưởng cho những người làm tốt.”');
INSERT INTO "hasan_abdulkarim" VALUES(7,162,'7:162','Nhưng những kẻ làm điều sai quấy trong họ đã thay đổi Lời phán đã được truyền, cho nên TA đã gởi tai ương từ trên trời xuống phạt chúng do những điều sai quấy của chúng.');
INSERT INTO "hasan_abdulkarim" VALUES(7,163,'7:163','Và hãy hỏi họ về một thị trấn nằm sát bờ biển, một nơi mà họ chịu hình phạt vì đã vi phạm (giới cấm của) ngày Thứ Bảy khi cá của họ lội vào bờ (nhô đầu lên khỏi mặt nước) để gặp họ công khai. Nhưng vào ngày nào không phải là Thứ Bảy thì cá không rủ nhau vào bờ. TA đã thử thách họ đúng như thế bởi vì họ là những kẻ nổi loạn và bất tuân.');
INSERT INTO "hasan_abdulkarim" VALUES(7,164,'7:164','Và một tập thể của họ đã lên tiếng hỏi: “ích lợi gì việc quí vị khuyên lơn một đám người mà Allah muốn tiêu diệt hoặc sẽ bị Ngài trừng phạt bằng một hình phạt khủng khiếp? (Các nhà truyền giáo) đáp: “Để giải nhiệm cho chúng tôi trước Thượng Đế của quí vị và để may ra họ sợ Allah.');
INSERT INTO "hasan_abdulkarim" VALUES(7,165,'7:165','Bởi thế, khi họ quên mất các giới (cấm) đã được nhắc nhở, TA giải cứu những ai đã từng cấm cản (người khác) làm điều tội lỗi và bắt phạt những ai đã làm điều sai quấy bằng một hình phạt vô cùng đau đớn vì tội chúng đã từng nổi lọan và bất tuân.');
INSERT INTO "hasan_abdulkarim" VALUES(7,166,'7:166','Bởi thế, khi chúng vi phạm các giới cấm, TA phán cho chúng: “Hãy thành loài khỉ đáng khinh!”');
INSERT INTO "hasan_abdulkarim" VALUES(7,167,'7:167','Và (hãy nhớ) khi Thượng Đế của Ngươi tuyên bố rằng Ngài sẽ cử những người nào đó đến trừng trị chúng và trừng phạt chúng đau đớn cho đến Ngày phục sinh bởi vì Thượng Đế của Ngươi rất nhanh trong việc trả thù nhưng cũng rất mực Tha thứ, Rất Mực Khoan Dung.');
INSERT INTO "hasan_abdulkarim" VALUES(7,168,'7:168','Và TA (Allah) đã chia cắt họ thành nhiều cộng đồng sống rải rác trên khắp mặt đất. Trong họ có người ngoan đạo nhưng cũng có người không như thế. TA đã thử thách họ với điều lành và điều dữ để may ra họ quay về (với Chính Đạo).');
INSERT INTO "hasan_abdulkarim" VALUES(7,169,'7:169','Rồi sau họ, có một thế hệ (xấu) nối nghiệp; thế hệ mới này thừa hưởng Kinh Sách (của Allah nhưng thay vì tuân theo Kinh Sách) đã chọn lấy các món hàng tạm bợ gần gủi (của trần gian) và nói ra điều này để cáo lỗi: “Chúng tôi hy vọng sẽ được tha thứ.” Và nếu họ gặp các món phù hoa tương tự thì họ cũng sẽ chụp lấy chúng. Phải chăng họ đã không tôn trọng Lời Giao Ước đã ràng buộc họ trong Kinh Sách rằng họ chỉ được phép nói sự thật về Allah thôi hay sao? Và họ đã học các điều ghi trong đó và nhà ở nơi đời sau là tốt nhất cho những ai ngay chính sợ Allah. Thế các ngươi không hiểu hay sao?');
INSERT INTO "hasan_abdulkarim" VALUES(7,170,'7:170','Và những ai nắm vững Kinh Sách và chu đáo dâng lễ ''Salah'' thì TA sẽ không làm mất phần thưởng của những người làm việc thiện.');
INSERT INTO "hasan_abdulkarim" VALUES(7,171,'7:171','Và (hãy nhớ) khi TA nhấc ngọn núi (Sinai) lên khỏi đầu của họ giống như một cái tán che bên trên và họ ngỡ rằng nó sắp sụp xuống đè họ; (TA phán): “Hãy nắm thật vững các điều mà TA đã ban cho các ngươi và hãy nhớ kỹ điều ghi trong đó để may ra các ngươi trở nên ngay chính sợ Allah.”');
INSERT INTO "hasan_abdulkarim" VALUES(7,172,'7:172','Và (hãy nhớ) khi Thượng Đế của Ngươi bắt con cháu của Adam, hậu duệ của họ sinh ra từ trái cật của họ, xác nhận đối với bản thân mình (trước câu hỏi:) “Phải chăng TA Allah là Thượng Đế của các ngươi?” Họ đáp: “Dạ, thưa vâng! Bầy tôi xin xác nhận.” (TA làm thế là vì e) rằng các ngươi có thể viện lý do để nói vào Ngày phục sinh: “Chúng tôi không hề biết đến điều này.”');
INSERT INTO "hasan_abdulkarim" VALUES(7,173,'7:173','Hoặc các ngươi có thể nói: “Chính cha mẹ của chúng tôi chủ trương việc thờ nhiều thần linh trước đây, chúng tôi chỉ là hậu duệ của họ, (chúng tôi không hay biết gì). Phải chăng Ngài sẽ tiêu diệt chúng tôi vì điều (tội lỗi) mà những kẻ theo ngụy giáo đã làm?”');
INSERT INTO "hasan_abdulkarim" VALUES(7,174,'7:174','Và TA (Allah) đã giải thích các dấu hiệu rõ ràng như thế để may ra họ còn có dịp quay về (với Chính Đạo).');
INSERT INTO "hasan_abdulkarim" VALUES(7,175,'7:175','Hãy đọc kể lại cho họ câu chuyện của một người( mà TA (Allah) đã ban cho các dấu hiệu của TA, nhưng y quẳng chúng sang một bên, cho nên Shaytan đã đuổi theo kịp y, và y đã trở thành một kẻ lầm lạc.');
INSERT INTO "hasan_abdulkarim" VALUES(7,176,'7:176','Và nếu muốn, TA đã nâng y lên cao với chúng (các dấu hiệu) nhưng y bám chặt vào trái đất và theo đuổi những ham muốn trần tục. Hình ảnh của y chẳng khác nào hình ảnh của một con chó: nếu Ngươi đánh đuổi nó thì nó sẽ le lưỡi của nó ra và nếu Ngươi không đánh đuổi nó thì nó cũng le luỡi ra. Đó là hình ảnh của đám người đã phủ nhận các dấu hiệu của TA. Bởi thế, Ngươi hãy kể lại câu chuyện trên để may ra họ ngẫm nghĩ lại (về bản thân của họ).');
INSERT INTO "hasan_abdulkarim" VALUES(7,177,'7:177','Hình ảnh của đám người đã từng phủ nhận các dấu hiệu của TA và làm hỏng linh hồn của mình thật rất tồi tệ.');
INSERT INTO "hasan_abdulkarim" VALUES(7,178,'7:178','Ai mà Allah hướng dẫn thì sẽ đi đúng đường, và ai mà Allah đánh lạc hướng thì sẽ là những kẻ thua thiệt.');
INSERT INTO "hasan_abdulkarim" VALUES(7,179,'7:179','Và chắc chắn TA (Allah) sẽ đưa vào hỏa ngục đa số Jinn và người. (Bởi vì) chúng có trái tim (tấm lòng) nhưng không chịu vận dụng nó để hiểu, chúng có mắt nhưng không chịu vận dụng nó để quan sát, chúng có tai nhưng không chịu vận dụng nó để nghe; những kẻ như thế chẳng khác nào thú vật, không, chúng còn tệ hơn nữa. Chúng là những kẻ thờ ơ, khinh suất.');
INSERT INTO "hasan_abdulkarim" VALUES(7,180,'7:180','Allah có các tên gọi tốt đẹp nhất; do đó hãy cầu xin Ngài bằng các tên gọi đó. Và hãy lánh xa những kẻ bóp méo tên gọi của Ngài. Họ sẽ lãnh đủ về tội của họ.');
INSERT INTO "hasan_abdulkarim" VALUES(7,181,'7:181','Và trong số những người mà TA (Allah) đã tạo, có một cộng đồng (tập thể) hướng dẫn (người khác) bằng sự thật và dựa vào đó mà đối xử công bằng (với người khác).');
INSERT INTO "hasan_abdulkarim" VALUES(7,182,'7:182','Và những ai phủ nhận các dấu hiệu của TA thì sẽ dần dần bị TA đưa đến chỗ hủy diệt từ những chỗ mà họ không biết.');
INSERT INTO "hasan_abdulkarim" VALUES(7,183,'7:183','Và TA tạm tha cho họ. Bởi vì kế hoạch của TA rất vững mạnh.');
INSERT INTO "hasan_abdulkarim" VALUES(7,184,'7:184','Và há họ không chịu ngẫm nghĩ ư? Người Bạn (Muhammad) của họ không mất trí. Y chỉ là một người báo trước công khai.');
INSERT INTO "hasan_abdulkarim" VALUES(7,185,'7:185','Há họ không nhìn thấy gì trong vương quốc gồm các tầng trời và trái đất và mọi vật mà Allah đã tạo hay sao? Rằng có lẽ tuổi thọ của họ sắp mãn đến nơi hay sao? Thế đâu là thông điệp mà họ sẽ tin tưởng sau cái (Qur''an) này?');
INSERT INTO "hasan_abdulkarim" VALUES(7,186,'7:186','Ai mà Allah đánh lạc hướng thì sẽ không gặp một người dẫn đường nào; bởi vì Ngài bỏ mặc họ lang thang vơ vẩn trong sự thái quá của họ.');
INSERT INTO "hasan_abdulkarim" VALUES(7,187,'7:187','Họ hỏi Ngươi về Giờ (tận thế hay phục sinh) chừng nào sẽ xảy ra. Hãy bảo họ: “Duy chỉ Thượng Đế (Allah) của ta biết rõ nó. Chỉ riêng Ngài tiết lộ thời điểm của nó. Nó sẽ nặng nề (đối với mọi vật) trong các tầng trời và trái đất. Nó sẽ xảy đến cho các người một cách bất ngờ". Họ hỏi Ngươi làm như Ngươi quen thuộc với nó lắm. Hãy bảo họ: “Chỉ riêng Allah biết rõ nó, ngược lại đa số nhân loại không biết gì cả.”');
INSERT INTO "hasan_abdulkarim" VALUES(7,188,'7:188','Hãy bảo họ: “Ta không có quyền quyết định điều lợi hoặc điều hại cho chính bản thân ta trừ phi điều nào Allah muốn. Và nếu ta biết điều vô hình thì chắc chắn ta sẽ tom góp nhiều điều tốt cho ta và sẽ không gặp một điều bất hạnh nào. Thật sự ta chỉ là một người báo trước và là một người mang tin lành cho những người có đức tin.”');
INSERT INTO "hasan_abdulkarim" VALUES(7,189,'7:189','Ngài là Đấng đã tạo các ngươi từ một người duy nhất (Adam), và từ Y Ngài tạo ra người vợ của Y để cho Y sống yên bình với nàng. Do đó khi chồng ôm phủ vợ, vợ thụ thai và mang theo một gánh nhẹ mỗi lần di chuyển. Đến khi bà phát triển nặng nề, hai vợ chồng cùng cầu nguyện Allah, Thượng Đế của hai người, thưa: “Nếu Ngài ban cho bầy tôi một đứa con tốt lành (về đủ mọi phương diện) thì chắc chắn bầy tôi sẽ vô cùng biết ơn.”');
INSERT INTO "hasan_abdulkarim" VALUES(7,190,'7:190','Nhưng khi Ngài ban cho hai vợ chồng một đứa con tốt lành thì hai người bắt đầu tổ hợp những thần linh cùng với Ngài về (đứa con) mà Ngài đã ban cho họ. Nhưng Allah ở bên trên điều (hay kẻ) mà họ đã tổ hợp với Ngài.');
INSERT INTO "hasan_abdulkarim" VALUES(7,191,'7:191','Phải chăng họ tổ hợp với Allah những vật không tạo được cái gì mà chính chúng lại được tạo ra?');
INSERT INTO "hasan_abdulkarim" VALUES(7,192,'7:192','Chúng không thể giúp họ cũng không thể tự giúp mình được.');
INSERT INTO "hasan_abdulkarim" VALUES(7,193,'7:193','Và nếu các ngươi có gọi chúng đến với Chỉ Đạo thì chúng sẽ không theo các ngươi. Điều đó bằng thừa đối với các ngươi, dẫu các ngươi có gọi chúng hay im lặng;');
INSERT INTO "hasan_abdulkarim" VALUES(7,194,'7:194','Quả thật, những kẻ (hay vật) mà các ngươi gọi (cầu nguyện) ngoài Allah chỉ là bầy tôi như các người vậy. Hãy gọi (cầu nguyện) chúng đi và hãy để chúng đáp lại các người nếu các người nói thật!');
INSERT INTO "hasan_abdulkarim" VALUES(7,195,'7:195','Há chúng có chân để đi, có tay để nắm, có mắt để nhìn, có tai để nghe chăng? Hãy bảo họ: “Các ngươi hãy gọi các kẻ thần linh của các ngươi, rồi để chúng đặt kế hoạch chống lại Ta, và chớ để cho Ta nghỉ xả hơi!"');
INSERT INTO "hasan_abdulkarim" VALUES(7,196,'7:196','“Quả thật, Đấng Bảo Hộ của ta là Allah, Đấng đã ban Kinh Sách xuống; và Ngài che chở những người ngoan đạo" .');
INSERT INTO "hasan_abdulkarim" VALUES(7,197,'7:197','‘Trong lúc những kẻ mà các người cầu nguyện ngoài Ngài (Allah) không có khả năng giúp đỡ các người cũng không tự giúp mình được.”');
INSERT INTO "hasan_abdulkarim" VALUES(7,198,'7:198','Và nếu Ngươi gọi chúng đến với Chỉ Đạo thì chúng sẽ không nghe; và Ngươi thấy chúng nhìn Ngươi nhưng chúng không nhìn thấy gì cả.');
INSERT INTO "hasan_abdulkarim" VALUES(7,199,'7:199','Hãy lượng thứ và chỉ thị (cho họ) làm điều nào đúng nhưng hãy lánh xa những kẻ ngu dốt.');
INSERT INTO "hasan_abdulkarim" VALUES(7,200,'7:200','Và nếu có một đề nghị của Shaytan xâm nhập đầu óc của Ngươi, hãy cầu xin Allah che chở bởi vì Ngài Hằng Nghe và Hằng Biết (mọi việc).');
INSERT INTO "hasan_abdulkarim" VALUES(7,201,'7:201','Quả thật, những ai sợ Allah, một khi bị Shaytan xúi giục, liền tưởng nhớ (Allah) thì sẽ thấy đầu óc mình bừng sáng trở lại.');
INSERT INTO "hasan_abdulkarim" VALUES(7,202,'7:202','Nhưng các anh em (xấu) của họ chỉ muốn thúc đẩy họ lao mình vào lầm lạc rồi sẽ không bỏ ngang ý đồ đó.');
INSERT INTO "hasan_abdulkarim" VALUES(7,203,'7:203','Và khi Ngươi không mang một dấu lạ nào đến cho họ, họ nói: "Tại sao không mang nó đến?” Hãy bảo họ: "Ta chỉ làm theo điều được mặc khải cho Ta từ Thượng Đế của Ta. (Qur''an) này là một sự soi sáng từ Thượng Đế của các người, một Chỉ Đạo và một Hồng Ân cho đám người có đức tin.”');
INSERT INTO "hasan_abdulkarim" VALUES(7,204,'7:204','Và khi Qur''an được xướng đọc, hãy lắng nghe và giữ im lặng để may ra các ngươi nhận được Hồng Ân (của Allah).');
INSERT INTO "hasan_abdulkarim" VALUES(7,205,'7:205','Và hãy tưởng nhớ Thượng Đế của ngươi trong tâm khảm (hỡi Muslim!), vừa khiêm tốn vừa kính trọng nhưng không được lớn tiếng (trong lúc tụng niệm) vào các buổi sáng và buổi tối và chớ là một người vô ý tứ.');
INSERT INTO "hasan_abdulkarim" VALUES(7,206,'7:206','Quả thật, những ai ở gần Thượng Đế của Ngươi không quá tự hào trong việc thờ phụng Ngài và họ tán dương Ngài và phủ phục (trước Ngài).');
INSERT INTO "hasan_abdulkarim" VALUES(8,1,'8:1','Họ hỏi Ngươi về chiến lợi phẩm (thu tại chiến trường). Hãy bảo họ: “Chiến lợi phẩm đó thuộc về Allah và Sứ Giả (của Allah). Bởi thế, hãy sợ Allah và giải quyết ổn thỏa (việc tranh chấp) giữa các người với nhau; và nếu các người là những người có đức tin thì hãy tuân lệnh của Allah và Sứ Giả của Ngài."');
INSERT INTO "hasan_abdulkarim" VALUES(8,2,'8:2','Những người có đức tin chỉ là những ai mà quả tim rúng động sợ hãi khi nghe nhắc đến Allah và khi nghe đọc các Lời Mặc Khải của Ngài, đức tin của họ vững mạnh thêm và họ trọn tin và phó thác cho Thượng Đế (Allah) của họ;');
INSERT INTO "hasan_abdulkarim" VALUES(8,3,'8:3','Những ai năng dâng lễ ''Salah'' một cách chu đáo và chi dùng những vật mà TA (Allah) đã ban cấp.');
INSERT INTO "hasan_abdulkarim" VALUES(8,4,'8:4','Họ là những người có đức tin thực sự và được Thượng Đế của họ ban cho cấp bậc vinh dự cùng với sự tha thứ và bổng lộc rộng rãi.');
INSERT INTO "hasan_abdulkarim" VALUES(8,5,'8:5','Giống như việc Thượng Đế của Ngươi ra lệnh cho Ngươi ra khỏi nhà (để đi chiến đấu) với lý do chính đáng nhưng có một số tín đồ không thích (việc đi chiến đâu của Ngươi).');
INSERT INTO "hasan_abdulkarim" VALUES(8,6,'8:6','(Họ) tranh cãi với Ngươi về lý do (đi chiến đấu) sau khi nó đã được trình bày (cho họ), bởi vì (họ có cảm tưởng) như bị đưa đến chố chết trong khi họ nhìn thấy (cảnh chết).');
INSERT INTO "hasan_abdulkarim" VALUES(8,7,'8:7','Và (hãy nhớ) khi Allah hứa với các ngươi, trong hai đoàn (người) của địch, đoàn nào là đoàn mà các ngươi sẽ phải đương đầu trong lúc các ngươi lại mong được chạm trán với đoàn (thương buôn) không trang bị vũ khí gì cả; trong lúc Allah lại muốn chứng minh sự thật về Lời phán của Ngài và muốn chặt đứt gốc rễ của những kẻ không tin.');
INSERT INTO "hasan_abdulkarim" VALUES(8,8,'8:8','Mục đích để Ngài chứng minh (với các người) rằng sự thật vẫn là sự thật và sự giả dối vẫn là sự giả dối dẫu rằng những kẻ tội lỗi không thích điều đó');
INSERT INTO "hasan_abdulkarim" VALUES(8,9,'8:9','Và (hãy nhớ) khi các ngươi cầu xin Thượng Đế của các ngươi (cứu giúp) và Ngài đã đáp lời cầu xin của các ngươi (qua lời hứa): “TA sẽ trợ giúp các ngươi với một ngàn Thiên Thần xếp thành hàng ngũ.”');
INSERT INTO "hasan_abdulkarim" VALUES(8,10,'8:10','Và Allah làm điều đó là chỉ để cho các ngươi phấn khởi và yên tâm chứ thật ra sự cứu giúp chỉ đến từ Allah thôi bởi vì Allah Toàn Năng, Rất Mực Sáng Suốt.');
INSERT INTO "hasan_abdulkarim" VALUES(8,11,'8:11','Và (hãy nhớ) khi Ngài (Allah) làm cho các ngươi bình tâm bằng cách làm cho các ngươi thiếp đi một giấc và Ngài cho mưa từ trên trời xuống để tẩy sạch các ngươi và phủi sạch sự ô nhiễm của Shaytan và củng cố tấm lòng của các ngươi thêm vững tin và làm cho bàn chân của các người đứng vững chắc.');
INSERT INTO "hasan_abdulkarim" VALUES(8,12,'8:12','(Hãy nhớ) khi Thượng Đế (Allah) của Ngươi đã mặc khải cho các Thiên Thần: TA ở cùng với các ngươi, hãy củng cố (tấm lòng của) những ai có đức tin; TA sẽ gieo nỗi kinh hãi vào lòng của những kẻ không có đức tin, bởi thế hãy đập vào cổ và từng đầu ngón tay của chúng.”');
INSERT INTO "hasan_abdulkarim" VALUES(8,13,'8:13','Sở dĩ như thế là vì chúng chống đối Allah và Sứ Giả của Ngài. Và ai chống đối Allah và Sứ Giả của Ngài thì sẽ bị Allah trừng trị đích đáng.');
INSERT INTO "hasan_abdulkarim" VALUES(8,14,'8:14','Như thế. Thôi, hãy nếm nó (hình phạt,) và (nên biết) rằng những kẻ không có đức tin sẽ chịu hình phạt của hỏa ngục.');
INSERT INTO "hasan_abdulkarim" VALUES(8,15,'8:15','Hỡi những ai có đức tin! Khi các ngươi đối diện với những kẻ không tin đang dàn trận để tấn công các ngươi, chớ quay lưng về phía chúng.');
INSERT INTO "hasan_abdulkarim" VALUES(8,16,'8:16','Và ai quay lưng về phía địch vào Ngày (đụng độ)( đó trừ phi quay lưng lại để dụ địch rồi đánh ngược trở lại hoặc rút về nhập với đoàn quân của mình thì chắc chắn là tự rước sự giận dữ của Allah vào mình và chỗ ngụ của người đó sẽ là hỏa ngục, một trạm đến cuối cùng rất tệ hại!');
INSERT INTO "hasan_abdulkarim" VALUES(8,17,'8:17','Bởi thế, không phải các ngươi đã giết chúng mà chính Allah đã giết; và không phải Ngươi đã ném chúng khi Ngươi ném (với một nấm đất tại Badr) mà chính Allah đã ném, mục đích để Ngài (Allah) thử thách họ (những người có đức tin) bằng một sự thử thách tốt từ Ngài. Bởi vì Allah Hằng Nghe và Hằng Biết (mọi việc).');
INSERT INTO "hasan_abdulkarim" VALUES(8,18,'8:18','Như thế! Và (nên biết) rằng chính Allah đã làm suy yếu mưu đồ của những kẻ không tin.');
INSERT INTO "hasan_abdulkarim" VALUES(8,19,'8:19','(Hỡi những kẻ không tin!) Nếu các ngươi cầu xin một sự xét xử thì sự xét xử đã đến với các ngươi. Và nếu các ngươi từ bỏ mưu đồ (chiến tranh) thì điều đó tốt cho các ngươi hơn; ngược lại nếu các ngươi tái diễn trò tương tự thì TA (Allah) cũng sẽ tái diễn hình phạt giống như thế. Và lực lượng của các ngươi chẳng giúp ích gì được cho các ngươi dẫu cho quân số có đông đến mấy đi nữa. Và nên biết rằng Allah ở cùng với những người có đức tin.');
INSERT INTO "hasan_abdulkarim" VALUES(8,20,'8:20','Hỡi những ai có đức tin! Hãy tuân lệnh của Allah và Sứ Giả của Ngài và chớ quay bỏ đi trong lúc các ngươi đang nghe (Người truyền lệnh).');
INSERT INTO "hasan_abdulkarim" VALUES(8,21,'8:21','Và chớ cư xử như những ai đã từng nói: “Chúng tôi nghe” nhưng họ đã thực sự không nghe.');
INSERT INTO "hasan_abdulkarim" VALUES(8,22,'8:22','Bởi vì đối với Allah loài thú xấu nhất là kẻ điếc và người câm, những kẻ không hiểu gì.');
INSERT INTO "hasan_abdulkarim" VALUES(8,23,'8:23','Và nếu Allah biết nơi họ có điều tốt nào thì chắc chắn Ngài đã làm cho họ biết nghe. Và nếu Allah có làm cho họ nghe được đi nữa thì họ cũng quay bỏ đi bởi vì họ đã có dã tâm đào nhiệm.');
INSERT INTO "hasan_abdulkarim" VALUES(8,24,'8:24','Hỡi những ai có đức tin! Hãy đáp lời kêu gọi của Allah và của Sứ Giả (Muhammad) khi Ngài kêu gọi các ngươi đến với điều làm cho các ngươi sống và hãy biết rằng Allah xen vào giữa một người và quả tim (tấm lòng) của y và chính Ngài là Đấng mà tất cả các ngươi sẽ được tập trung đưa về (trình diện).');
INSERT INTO "hasan_abdulkarim" VALUES(8,25,'8:25','Và hãy sợ tai họa mà không riêng gì những kẻ sai phạm sẽ phải chịu (mà là tất cả); và nên biết rằng Allah trừng phạt rất nghiêm khắc.');
INSERT INTO "hasan_abdulkarim" VALUES(8,26,'8:26','Và hãy nhớ khi các ngươi chỉ gồm một thiểu số yếu ớt trong xứ, các ngươi sợ bị bắt cóc đem đi thủ tiêu, bởi thế Ngài (Allah) ban cho các ngươi một nơi tị nạn( và tăng cường sức mạnh cho các ngươi với sự trợ giúp của Ngài và cung dưỡng các ngươi với lộc ăn tốt và sạch để cho các ngươi tỏ lòng biết ơn.');
INSERT INTO "hasan_abdulkarim" VALUES(8,27,'8:27','Hỡi những ai có đức tin! Chớ phản bội Allah và Sứ Giả (Muhammad) và chớ cố ý gian lận tín vật đã được ký thác nơi các ngươi trong lúc các ngươi biết.');
INSERT INTO "hasan_abdulkarim" VALUES(8,28,'8:28','Và hãy biết rằng tài sản và con cái của các ngươi chỉ là một sự thử thách và sẽ có một phần thưởng to lớn hơn nơi Allah.');
INSERT INTO "hasan_abdulkarim" VALUES(8,29,'8:29','Hỡi những ai có đức tin! Nếu các ngươi sợ Allah, Ngài sẽ ban cho các ngươi Tiêu Chuẩn (dùng phân biệt điều phải và lẽ quấy) và giải tỏa các ngươi khỏi mọi việc làm tội lỗi và tha thứ cho các ngươi, bởi vì Allah làm chủ vô vàn thiên ân vĩ đại.');
INSERT INTO "hasan_abdulkarim" VALUES(8,30,'8:30','Và hãy nhớ khi những kẻ không có đức tin mưu định chống lại Ngươi (Muhammad) nhằm giam cầm Ngươi hoặc sát hại Ngươi hoặc trục xuất Ngươi đi nơi khác. Chúng âm mưu và đặt kế hoạch và Allah cũng đặt kế hoạch; và Allah là Đấng hoạch định giỏi nhất (so với các nhà hoạch định).');
INSERT INTO "hasan_abdulkarim" VALUES(8,31,'8:31','Và khi các Lời Mặc Khải của TA (Allah) được đọc lên (để nhắc chúng), chúng nói: “Chúng tôi đã từng nghe điều này. Nếu muốn, chúng tôi cũng sẽ nói ra được lời tương tự bởi vì đây chẳng qua là chuyện kể của người cổ xưa mà thôi.”');
INSERT INTO "hasan_abdulkarim" VALUES(8,32,'8:32','Và hãy nhớ khi chúng thưa: “Ôi Allah! Nếu (Qur''an) này là Chân Lý do Ngài ban xuống thì xin Ngài hãy làm cho mưa đá đổ từ trên trời xuống đè lên chúng tôi hoặc mang một hình phạt đau đớn nào đến trừng phạt chúng tôi!”');
INSERT INTO "hasan_abdulkarim" VALUES(8,33,'8:33','Và Allah sẽ không trừng phạt chúng trong lúc Ngươi đang sống giữa bọn chúng và Ngài sẽ không trừng phạt chúng trong lúc chúng đang cầu xin được tha thứ.');
INSERT INTO "hasan_abdulkarim" VALUES(8,34,'8:34','Và tại sao Allah lại không trừng phạt chúng trong lúc chúng ngang ngược cản thiên hạ không cho vào Thánh đường linh thiêng (tại Makkah) và chúng cũng không phải là những người bảo quản nó (Thánh đường)? Quả thật, chỉ những người ngay chính sợ Allah mới là những người bảo quản nó, nhưng đa số bọn chúng không biết điều đó.');
INSERT INTO "hasan_abdulkarim" VALUES(8,35,'8:35','(Hơn nữa), lễ nguyện (Salah) của chúng tại Ngôi Đền (Ka’bah của Allah) chỉ gồm có việc huýt sáo và vỗ tay (thì làm sao chúng có thể là người bảo quản Thánh đường?). Thôi, hãy nếm hình phạt về những điều mà các ngươi thường phủ nhận (hỡi những kẻ không tin!)');
INSERT INTO "hasan_abdulkarim" VALUES(8,36,'8:36','Quả thật, những kẻ không tin chi dùng tài sản của chúng vào việc cản trở (thiên hạ) không cho theo con đường của Allah, bởi thế chúng sẽ tiếp tục chi dùng nó. Rồi nó sẽ làm cho chúng đau khổ, rồi cuối cùng chúng sẽ thua cuộc. Và những kẻ không tin sẽ được tập trung để đưa vào hỏa ngục.');
INSERT INTO "hasan_abdulkarim" VALUES(8,37,'8:37','Mục đích để Allah có thể tách biệt điều ô uế khỏi điều tốt sạch( và chồng chất điều ô uế (tội lỗi) lên nhau, gộp tất cả lại thành một đống mà Ngài quẳng vào hỏa ngục. Chúng sẽ là những kẻ thua thiệt.');
INSERT INTO "hasan_abdulkarim" VALUES(8,38,'8:38','Hãy bảo những kẻ không tin, nếu chúng ngưng (chiến tranh) thì việc làm quá khứ của chúng sẽ được tha thứ; ngược lại, nếu chúng tái diễn thì hình phạt áp dụng cho những kẻ làm ác trước chúng (là một bài học cảnh cáo).');
INSERT INTO "hasan_abdulkarim" VALUES(8,39,'8:39','Và hãy tiếp tục đánh chúng cho đến khi nào không còn sự ngược đãi nữa; và sự thần phục sẽ hoàn toàn dành cho Allah. Nhưng nếu chúng ngưng (chiến) thì quả thật Allah thấy rõ mọi điều chúng làm.');
INSERT INTO "hasan_abdulkarim" VALUES(8,40,'8:40','Và nếu chúng từ chối thì nên biết rằng Allah là Đấng Bảo Hộ của các ngươi, Đáng Bảo Hộ Ưu Việt, Đấng Cứu Giúp Ưu Việt.');
INSERT INTO "hasan_abdulkarim" VALUES(8,41,'8:41','Và hãy nhận biết trong tất cả các chiến lợi phẩm mà các ngươi tịch thu (trong chiến tranh), một phần năm (5) là của Allah và của Sứ Giả (Muhammad) và của bà con gần, và của các trẻ mồ côi, và của người thiếu thốn và của người lỡ đường xa, nếu các ngươi thực sự tin tưởng nơi Allah và nơi điều mặc khải đã được ban xuống cho người tôi trung (Muhammad) của TA (Allah) vào ngày phân biệt (giữa Chính-giáo và Tà-giáo) tức là ngày hai lực lượng (Muslim và Kafir) đụng độ nhau (tại Badr.) Bởi vì Allah có toàn quyền định đoạt tất cả mọi việc.');
INSERT INTO "hasan_abdulkarim" VALUES(8,42,'8:42','Hãy nhớ lại khi các ngươi ở phía bên này (của thung lũng Badr) và địch quân cấm trại xa phía bên kia và đoàn khách thương của chúng ở phía dưới các ngươi (đang di chuyển về hướng biển). Và nếu các ngươi hẹn giao tranh thì chắc chắn các ngươi sẽ thất bại trong việc hẹn gặp đó. Nhưng (việc giao chiến đã xảy ra) là vì Allah muốn hoàn tất mệnh lệnh mà Ngài đã ban hành: rằng ai tới số chết thì sẽ chết theo bằng chứng rõ rệt và ai sống sót thì sẽ sống theo bằng chứng rõ rệt bởi vì quả thật Allah Hằng Nghe và Hằng Biết (mọi việc).');
INSERT INTO "hasan_abdulkarim" VALUES(8,43,'8:43','Hãy nhớ lại khi Allah đã cho Ngươi nằm mộng thấy (địch) chỉ gồm một thiểu số. Và nếu Ngài cho Ngươi thấy quân số của chúng đồng đảo, thì chắc chắn các ngươi sẽ yếu lòng và sẽ cãi nhau về quyết định giao chiến. Nhưng Allah đã cứu các ngươi bởi vì chắc chắn Ngài biết điều nằm trong lòng của mọi người.');
INSERT INTO "hasan_abdulkarim" VALUES(8,44,'8:44','Và hãy nhớ lại khi Ngài (Allah) đã cho các người thấy chúng chỉ gồm một thiểu số dưới cặp mắt của các ngươi khi hai bên giáp chiến và Ngài làm cho chứng khỉnh thường các ngươi dưới cặp mắt của chúng mục đích để cho Allah hoàn tất công việc mà Ngài đã quyết định bởi vì tất cả mọi việc đều phải trình lại cho Allah (để tham định).');
INSERT INTO "hasan_abdulkarim" VALUES(8,45,'8:45','Hỡi những ai có đức tin! Khi các ngươi chạm địch hãy kiên quyết (chớ lùi bước) và tưởng nhớ Allah cho thật nhiều để các ngươi có thể chiến thắng.');
INSERT INTO "hasan_abdulkarim" VALUES(8,46,'8:46','Và hãy tuân lệnh Allah và Sứ Giả của Ngài và chớ cãi nhau e rằng các ngươi sẽ thối chí và mất ưu thế. Và hãy kiên nhẫn bởi vì quả thật Allah ở cùng với những người kiên nhẫn.');
INSERT INTO "hasan_abdulkarim" VALUES(8,47,'8:47','Và chớ giống như những kẻ đã xuất quân từ nhà với dáng điệu diễu võ dương oai cho thiên hạ thấy và mưu định cản trở thiên hạ không cho theo con đường của Allah nhưng Allah bao quát hết mọi việc chúng làm.');
INSERT INTO "hasan_abdulkarim" VALUES(8,48,'8:48','Và hãy nhớ lại khi Shaytan làm cho việc làm (sai trái) của chúng hấp dẫn đối với chúng bằng cách nói (với chúng): “Ngày này không một ai trong nhân loại có thể chiến thắng được các người bởi vì ta sát cánh với các người.” Nhưng khi hai đoàn quân (Muslim và Kafir) đối mặt nhau thì (Shaytan) quay bỏ đi và nói lại (với chúng): "Ta giả biệt các người. Ta thấy điều( (thất bại) mà các người không thấy. Ta sợ Allah bởi vì Allah rất nghiêm khắc trong việc trừng phạt".');
INSERT INTO "hasan_abdulkarim" VALUES(8,49,'8:49','Và hãy nhớ lại khi các tên giả tạo đức tin và những ai mang một chứng bệnh (giả dối) trong lòng lên tiếng (về những người Muslim): “Những người này đã bị tôn giáo của họ dối gạt.” Nhưng ai tin cậy và phó thác cho Allah thì sẽ thấy quả thật Allah Toàn Năng, Rất Mực Sáng Suốt.');
INSERT INTO "hasan_abdulkarim" VALUES(8,50,'8:50','Và nếu ngươi có thể chứng kiến được cảnh huống khi Thiên Thần bắt hồn những kẻ không tin (lúc chết) bằng cách đập vào mặt và lưug của chúng (và bảo:) “Hãy nếm hình phạt của lửa đốt.”');
INSERT INTO "hasan_abdulkarim" VALUES(8,51,'8:51','Sở dĩ như thế là vì những điều (tội lỗi) mà bàn tay của các ngươi đã gây ra và rằng Allah không hề bất công đối với bầy tôi của Ngài.');
INSERT INTO "hasan_abdulkarim" VALUES(8,52,'8:52','Giống như cung cách của đám thuộc hạ của Fir’awn (Phê-rô) và của những kẻ sống trước chúng: chúng đã phủ nhận các dấu hiệu của Allah, bởi thế, Allah đã bắt phạt chúng vì tội lỗi của chúng. Quả thật, Allah rất mạnh và nghiêm khắc trong việc trừng phạt.');
INSERT INTO "hasan_abdulkarim" VALUES(8,53,'8:53','Sở đĩ như thế là vì Allah không thay đổi ân huệ mà Ngài đã ban cho một đám người (hay dân tộc) trừ phi họ thay đổi điều nằm nơi bản thản của họ. Và quả thật, Allah Hằng Nghe và Hằng Biết (hết mọi việc).');
INSERT INTO "hasan_abdulkarim" VALUES(8,54,'8:54','Giống lề thói của đám thuộc hạ của Fir-''awn và những kẻ sống trước chúng: chúng phủ nhận các dấu hiệu của Thượng Đế (Allah) của chúng. Bởi thế, TA (Allah) đã tiêu diệt chúng vì tội lỗi của chúng; và TA đã nhận đám thuộc hạ của Fir-''awn chết chìm bởi vì tất cả bọn chúng đều là những kẻ làm điều sai quấy.');
INSERT INTO "hasan_abdulkarim" VALUES(8,55,'8:55','Quả thật, sinh vật xấu nhất đối với Allah là những ai phản bội bởi vì chúng không có đức tin:');
INSERT INTO "hasan_abdulkarim" VALUES(8,56,'8:56','Những ai trong bọn chúng đã giao ước với Ngươi rồi sau đó bội ước bất cứ lúc nào và không sợ Allah;');
INSERT INTO "hasan_abdulkarim" VALUES(8,57,'8:57','Bởi thế, nếu Ngươi chinh phục được chúng thì hãy phân tán chúng và những kẻ theo chúng mục đích làm cho chúng nhớ lại (hình phạt cảnh cáo).');
INSERT INTO "hasan_abdulkarim" VALUES(8,58,'8:58','Và nếu Ngươi sợ đám người nào bội ước thì hãy ném trả lại chúng (giao ước của chúng) để được ngang bằng nhau bởi lẽ Allah không yêu thương những kẻ phản bội.');
INSERT INTO "hasan_abdulkarim" VALUES(8,59,'8:59','Và chớ để cho những kẻ không có đức tin nghĩ rằng chúng đã trốn thoát khỏi (Allah) bởi vì chúng sẽ không bao giờ phá hỏng được (kế hoạch của Allah).');
INSERT INTO "hasan_abdulkarim" VALUES(8,60,'8:60','Và hãy dồn tất cả sức mạnh của các ngươi để chuẩn bị lực lượng sẵn sàng chống lại chúng kể cả các con chiến mã hầu làm cho kẻ thù của Allah và của các ngươi kinh hồn bạt vía và cả các kẻ thù khác nữa mà các ngươi không biết nhưng Allah biết rõ chúng. Và bất cứ cái gì các ngươi chi dùng cho Chính Nghĩa của Allah thì sẽ được trả lại đầy đủ cho các ngươi và các ngươi sẽ không bị đối xử bất công.');
INSERT INTO "hasan_abdulkarim" VALUES(8,61,'8:61','Nhưng nếu (kẻ thù) chịu hoà thì hãy giải hòa với chúng và phó thác cho Allah bởi vì Ngài (Allah) là Đấng Hằng Nghe và Hằng Biết (mọi việc).');
INSERT INTO "hasan_abdulkarim" VALUES(8,62,'8:62','Và nếu chúng có ý định muốn lừa gạt Ngươi thì quả thật Allah đủ giúp Ngươi; Ngài là Đấng sẽ ủng hộ Ngươi với sự trợ giúp của Ngài và với (sự tham gia của) những người có đức tin.');
INSERT INTO "hasan_abdulkarim" VALUES(8,63,'8:63','Và (hơn nữa) Ngài đã đoàn kết tấm lòng của họ (các tín đồ). Bởi vì nếu Ngươi có chi dùng tất cả những gì trên trái đất (để đoàn kết họ) thì sẽ không thể đoàn kết tấm lòng của họ nổi; ngược lại, chỉ Allah đoàn kết họ được bởi vì Ngài Toàn Năng, Rất Mực Sáng Suốt.');
INSERT INTO "hasan_abdulkarim" VALUES(8,64,'8:64','Hỡi Nabi (Muhammad)! Allah đủ giúp Ngươi và những người có đức tin theo Ngươi.');
INSERT INTO "hasan_abdulkarim" VALUES(8,65,'8:65','Hỡi Nabi (Muhammad)! Hãy động viên những người tin tưởng đi chiến đấu. Nếu trong các ngươi có hai mươi người kiên nhẫn thì họ sẽ đánh bại hai trăm kẻ địch; nếu có một trăm (tín đồ kiên nhẫn) thì họ sẽ đánh bại một ngàn người không đức tin bởi vì chúng là đám người không hiểu biết gì.');
INSERT INTO "hasan_abdulkarim" VALUES(8,66,'8:66','Bây giờ Allah giảm bớt (trách nhiệm) cho các ngươi bởi vì Ngài biết các ngươi còn có một điểm yếu. Bởi thế, nếu trong các ngươi có một trăm tín đồ kiên nhẫn thì họ sẽ đánh bại hai trăm người của địch và nếu có một ngàn tín đồ kiên nhẫn thì họ sẽ đánh bại hai ngàn quân địch với sự chấp thuận của Allah bởi vì Allah sát cánh với những người kiên nhẫn.');
INSERT INTO "hasan_abdulkarim" VALUES(8,67,'8:67','Một Nabi không được phép giữ các tù binh chiến tranh cho mình trừ phi Người bình định xong lãnh thổ. Các người mong muốn các mối lợi tạm bợ của trần gian nhưng Allah lại mong nhìn về đời sau. Và Allah Toàn năng, Rất Mực Sáng Suốt.');
INSERT INTO "hasan_abdulkarim" VALUES(8,68,'8:68','Nếu không vì một sắc chỉ của Allah đã được ban hành thì chắc chắn các ngươi sẽ phải chịu một hình phạt to lớn về các (khoản tiền chuộc mạng) mà các ngươi đã lấy.');
INSERT INTO "hasan_abdulkarim" VALUES(8,69,'8:69','Nhưng bây giờ hãy vui vẻ hưởng các món hợp pháp và tốt sạch mà các ngươi đã tịch thu được trong chiến tranh nhưng hãy sợ Allah. Quả thật, Allah Hằng Tha Thứ, Rất Mực Khoan Dung.');
INSERT INTO "hasan_abdulkarim" VALUES(8,70,'8:70','Hỡi Nabi (Muhammad)! Hãy bảo các tù binh nằm trong tay kiểm soát của các ngươi: “Nếu Allah biết có điều nào tốt trong lòng của các ngươi, Ngài sẽ ban cho các ngươi món vật tốt hơn vật của các ngươi đã bị tịch thu và sẽ tha thứ cho các ngươi bởi vì Allah Hằng Tha Thứ, Rất Mực Khoan Dung.”');
INSERT INTO "hasan_abdulkarim" VALUES(8,71,'8:71','Và nếu chúng (những người ngoại đạo tại Makkah) có ý lừa đảo Ngươi (hỡi Nabi) thì chúng cũng đã lừa đảo Allah trước rồi, bởi thế Ngài đã ban cho (Ngươi) sức mạnh để thắng chúng bởi vì Allah Biết hết, Rất Mực Sáng Suốt.');
INSERT INTO "hasan_abdulkarim" VALUES(8,72,'8:72','Quả thật, những ai có đức tin (và vì tín ngưỡng của họ) đã di cư đi tị nạn và hy sinh cả tài sản lẫn sinh mạng của họ cho Chính Nghĩa của Allah và những ai đã cho họ (những người di cư) chỗ tị nạn và giúp đỡ họ là đồng minh bảo vệ lẫn nhau; còn những ai có đức tin nhưng không di cư thì các ngươi không có bổn phận phải bảo vệ họ về bất cứ điều gì trừ phi họ di cư đi tị nạn. Và nếu vì lý do tôn giáo, họ yêu cầu các ngươi bảo vệ họ thì các ngươi có bổn phận giúp đỡ họ ngoại trừ đối với đám người mà giữa họ và các người đã ký một thoả hiệp tương trợ; và nên nhớ rằng Allah biết hết mọi điều các ngươi làm.');
INSERT INTO "hasan_abdulkarim" VALUES(8,73,'8:73','Ngược lại, những ai không có đức tin thì là đồng minh lẫn nhau trừ phi các ngươi thực hiện điều này: (Hãy bảo vệ lẫn nhau), nếu không thế thì áp bức và loạn lạc sẽ đầy dẫy (khắp nơi) trên trái đất và sẽ có sự thối nát lớn lao.');
INSERT INTO "hasan_abdulkarim" VALUES(8,74,'8:74','Và những ai có đức tin, di cư và chiến đấu cho Chính Nghĩa của Allah cùng với những ai chứa chấp và giúp đỡ họ là những người thực sự có đức tin. Họ sẽ được (Allah) tha thứ và sẽ được bổng lộc dồi dào.');
INSERT INTO "hasan_abdulkarim" VALUES(8,75,'8:75','Và những ai tin tưởng sau đó và di cư tị nạn và chiến đấu cùng với các ngươi (cho Chính Nghĩa của Allah) thì là những người của các ngươi, tuy nhiên, trong Kinh Sách của Allah thì bà con ruột thịt gần nhau (trong việc hưởng gia tài) hơn (là những người anh em trong đạo). Và Allah biết hết mọi việc.');
INSERT INTO "hasan_abdulkarim" VALUES(9,1,'9:1','Allah và Sứ Giả của Ngài tuyên bố hủy bỏ thỏa hiệp( mà các ngươi đã ký kết với những người đa thần.');
INSERT INTO "hasan_abdulkarim" VALUES(9,2,'9:2','Do đó, trong thời hạn bốn tháng(, các ngươi được tự do đi lại trong xứ nhưng nên biết rằng các ngươi không thể trốn thoát khỏi Allah (qua mưu định gian dối của các ngươi); và nên biết rằng Allah sẽ hạ nhục những kẻ phủ nhận Ngài.');
INSERT INTO "hasan_abdulkarim" VALUES(9,3,'9:3','Và một bản Tuyên Cáo( từ Allah và Sứ Giả của Ngài đã thông báo cho dân chúng tập trung vào ngày đại lễ Hajj biết Allah và Sứ Giả của Ngài hết can hệ với những người thờ đa thần. Bởi thế, nếu các ngươi (hỡi những người thờ đa thần!) hối cải thì điều đó tốt nhất cho các ngươi. Và nếu các ngươi từ chối thì nên biết rằng các ngươi không thể trốn thoát khỏi (bàn tay) của Allah. Và hãy báo cho những kẻ phủ nhận Allah về một sự trừng phạt đau đớn.');
INSERT INTO "hasan_abdulkarim" VALUES(9,4,'9:4','Ngoại trừ những người dân đa thần nào mà các ngươi đã ký một thỏa hiệp với họ, rồi sau đó đã không lừa gạt các ngươi về bất cứ điều gì, cũng không tiếp tay cho một ai để chống lại các ngươi; bởi thế, hãy thi hành hiệp ước với họ cho đến khi chấm dứt thời hạn của họ. Bởi vì Allah yêu thương những người ngay chính sợ Allah.');
INSERT INTO "hasan_abdulkarim" VALUES(9,5,'9:5','Do đó, khi những tháng cấm đã chấm dứt, hãy đánh và giết những kẻ thờ đa thần ở nơi nào các ngươi tìm thấy chúng; các ngươi hãy bắt chúng, vây chúng và mai phục đánh chúng; nhưng nếu chúng hối cải và dâng lễ Salah và đóng Zakah thì hãy mở đường cho chúng (đi). Bởi vì Allah là Đấng Hằng Tha Thứ, Rất Mực Khoan Dung.');
INSERT INTO "hasan_abdulkarim" VALUES(9,6,'9:6','Và nếu có một người dân đa thần nào đến xin Ngươi chỗ tị nạn thì hãy che chở giúp y mãi cho đến khi y thấm nhuần lời răn của Allah rồi hộ tống y đến một nơi an toàn. Sở dĩ như thế là vì họ là một đám người không biết gì cả.');
INSERT INTO "hasan_abdulkarim" VALUES(9,7,'9:7','Làm sao Allah và Sứ Giả của Ngài có thể thỏa hiệp với những người dân đa thần được, ngoại trừ những ai mà các ngươi đã ký một thoả hiệp tại (Hudaibiyah) gần Thánh đường Linh thiêng (tại Makkah)? Chừng nào chúng ngay thẳng với các ngươi thì hãy ngay thẳng lại với chúng bởi vì Allah yêu thương những người ngay chính sợ Ngài.');
INSERT INTO "hasan_abdulkarim" VALUES(9,8,'9:8','Làm sao được? Bởi vì nếu chúng nắm ưu thế trên các ngươi thì chúng sẽ không tôn trọng nơi các ngươi mối quan hệ thân thuộc lẫn thỏa hiệp đã ký kết. Miệng của chúng nói ra lời ngọt ngào để làm vừa lòng các ngươi nhưng lòng dạ của chúng lại không muốn. Và đa số bọn chúng là những kẻ dấy loạn và bất tuân.');
INSERT INTO "hasan_abdulkarim" VALUES(9,9,'9:9','Chúng đã bán rẽ các Lời Mặc Khải của Allah cho nên chúng đã cản (nhiều người) xa khỏi con đường của Ngài. Thật tồi tệ thay điều chúng đã từng làm.');
INSERT INTO "hasan_abdulkarim" VALUES(9,10,'9:10','Chúng không tôn trọng nơi một người có đức tin mối quan hệ thân thuộc lẫn hiệp ước đã ký kết. Chính chúng mới là những kẻ vi phạm các ràng buộc.');
INSERT INTO "hasan_abdulkarim" VALUES(9,11,'9:11','Nhưng nếu chúng hối cải và năng dâng lễ Salah và đóng Zakah thì chúng là anh em với các ngươi trong đạo. Và TA (Allah) đã giải thích các Lời Mặc Khải cho một đám người có hiểu biết.');
INSERT INTO "hasan_abdulkarim" VALUES(9,12,'9:12','Và nếu chúng vi phạm lời thề sau khi chúng giao ước và thóa mạ tôn giáo của các ngươi thì hãy đánh các lãnh tụ phản trắc đó bởi vì lời thề của chúng chẳng có giá trị gì đối với chúng cả. (Làm thế) may ra chúng mới chịu ngưng (quấy nhiễu).');
INSERT INTO "hasan_abdulkarim" VALUES(9,13,'9:13','Phải chăng các ngươi không muốn đánh một đám người đã từng nuốt lời thề, đã âm mưu trục xuất Sứ Giả (của Allah) và đã khởi sự tấn công các ngươi trước? Các ngươi sợ chúng hay sao? Không, Allah mới đáng cho các ngươi phải sợ nếu các ngươi thực sự có đức tin.');
INSERT INTO "hasan_abdulkarim" VALUES(9,14,'9:14','Hãy đánh chúng! Allah sẽ trừng phạt chúng với bàn tay của các ngươi, và sẽ hạ nhục chúng và sẽ giúp các ngươi đánh thắng chúng và chữa lành lòng ngực của một đám người có đức tin.');
INSERT INTO "hasan_abdulkarim" VALUES(9,15,'9:15','Và Ngài (Allah) sẽ xóa đi cơn giận trong lòng của họ và Allah quay lại (khoan dung) cho người nào Ngài muốn bởi vì Allah Toàn Tri, Rất Mực Sáng Suốt.');
INSERT INTO "hasan_abdulkarim" VALUES(9,16,'9:16','Phải chăng các ngươi nghĩ rằng các ngươi sẽ được yên thân trong lúc Allah chưa (thử thách để) biết ai là người sẽ tận lực chiến đấu (cho Chính Nghĩa của Ngài) và sẽ không nhận ai ngoài Allah và Sứ Giả của Ngài và những người có đức tin làm các vị bảo hộ của họ hay sao? Bởi vì Allah là Đấng Am Tường những điều các ngươi làm.');
INSERT INTO "hasan_abdulkarim" VALUES(9,17,'9:17','Nhũng người dân đa thần không xứng đáng làm công việc bảo quản các Thánh đường của Allah bởi vì chúng tự xác nhận mình không có đức tin. Đó là những kẻ mà việc làm sẽ không có kết quả và chúng sẽ vào ở muôn đời trong hỏa ngục.');
INSERT INTO "hasan_abdulkarim" VALUES(9,18,'9:18','Chỉ ai tin nơi Allah và Ngày (Phán Xử) Cuối Cùng và năng dâng lễ Salah và đóng Zakah và không sợ ai mà chỉ sợ riêng Allah thôi mới là những người chăm sóc và bảo quản các Thánh đường của Allah. May ra họ là những người sẽ được hướng dẫn.');
INSERT INTO "hasan_abdulkarim" VALUES(9,19,'9:19','Phải chăng các ngươi cho rằng việc cung cấp nước uống cho những người đến làm Hajj và việc trông coi bảo quản Thánh đường Linh thiêng (tại Makkah) lại ngang bằng với một người có đức tin nơi Allah và Ngày (Phán Xử) Cuối Cùng và chiến đấu anh dũng cho Chính Nghĩa của Allah hay sao? Đối với Allah, họ không ngang bằng nhau. Và Allah không hướng dẫn đám người làm điều sai quấy.');
INSERT INTO "hasan_abdulkarim" VALUES(9,20,'9:20','Đối với Allah, những ai có đức tin và đã di cư (tị nạn) và đã tận lực chiến đấu cho Chính Nghĩa của Allah, vừa hy sinh cả tài sản lẫn sinh mạng của họ, sẽ có cấp bậc cao. Và họ là những người sẽ thành đạt.');
INSERT INTO "hasan_abdulkarim" VALUES(9,21,'9:21','Thượng Đế (Allah) của họ sẽ báo cho họ tin vui về sự Khoan Dung và sự Hài Lòng của Ngài và về các Ngôi vườn (thiên đàng) dành ân thưởng họ; trong đó họ sẽ hưởng niềm hạnh phúc vĩnh cửu.');
INSERT INTO "hasan_abdulkarim" VALUES(9,22,'9:22','Họ sẽ ở trong đó đời đời. Quả thật, Allah có một phần thưởng to lớn nơi Ngài.');
INSERT INTO "hasan_abdulkarim" VALUES(9,23,'9:23','Hỡi những ai có đức tin! Nếu cha mẹ và anh em của các ngươi yêu sự không tin hơn đức tin (nơi Allah) thì chớ nhận họ làm người bảo hộ của các ngươi. Và ai trong các ngươi quay về kết thân với họ thì là những kẻ làm điều sai quấy.');
INSERT INTO "hasan_abdulkarim" VALUES(9,24,'9:24','Hãy bảo họ: “Nếu cha mẹ, con cái, anh em, các bà vợ, dòng họ của các ngươi và tài sản mà các ngươi tậu được, và việc mua bán mà các ngươi sợ thất bại hoặc biệt thự xinh đẹp mà các ngươi thích thú, (tất cả các thứ đó) quí giá đối với các ngươi hơn Allah và Sứ Giả của Ngài và hơn việc chiến đấu cho Chính Nghĩa của Ngài thì hãy đợi cho đến khi Allah sẽ ban hành quyết định của Ngài xuống bởi vì Allah không hướng dẫn dắt một đám người dấy loạn và bất tuân’’');
INSERT INTO "hasan_abdulkarim" VALUES(9,25,'9:25','Chắc chắn Allah đã trợ giúp các ngươi tại nhiều chiến trường và vào ngày các ngươi chạm địch tại Hunain(. Này, quân số đông đảo của các ngươi làm cho các ngươi tự mãn nhưng chúng chẳng giúp ích gì được cho các ngươi. (Lúc đó) đất đai tuy rộng thênh thang bổng nhiên trở nên chật hẹp đối với các ngươi rồi các ngươi buộc phải đánh tháo lui.');
INSERT INTO "hasan_abdulkarim" VALUES(9,26,'9:26','Rồi Allah ban sự trầm tĩnh xuống cho Sứ Giả của Ngài và cho các tín đồ và gởi các viện binh (Thiên Thần) mà các ngươi không nhìn thấy được (đến trợ lực các ngươi), và Ngài trừng phạt những kẻ không có đức tin. Và đó là phần phạt của những kẻ phủ nhận Allah.');
INSERT INTO "hasan_abdulkarim" VALUES(9,27,'9:27','Rồi sau sự việc (đáng tiếc) đó Allah quay lại tha thứ cho người nào Ngài muốn bởi vì Allah Hằng Tha Thứ, Rất Mực Khoan Dung.');
INSERT INTO "hasan_abdulkarim" VALUES(9,28,'9:28','Hỡi những ai có đức tin! Quả thật, những người dân đa thần không được trong sạch. Bởi thế, sau năm (tế lễ) này của chúng, chớ để cho chúng đến gần Thánh đường Linh thiêng (tại Makkah). Nếu các ngươi sợ nghèo thì Allah sẽ làm cho các ngươi giàu có từ nguồn thiên lộc của Ngài nếu Ngài muốn; bởi vì Allah là Đấng Hằng Biết, Đấng Thông Suốt và Chí Minh.');
INSERT INTO "hasan_abdulkarim" VALUES(9,29,'9:29','Hãy đánh những ai không tin tưởng nơi Allah và Ngày (Phán Xử) Cuối Cùng, và không tôn trọng các giới cấm mà Allah và Sứ Giả của Ngài đã cấm cũng không chấp nhận tôn giáo chân lý trong số những ai đã được ban cho Kinh Sách cho đến khi nào chúng chịu thần phục và tự tay chịu trả thuế(.');
INSERT INTO "hasan_abdulkarim" VALUES(9,30,'9:30','Những người Do thái gọi Uzair (Ezra) là con trai của Allah và các tín đồ Thiên Chúa giáo gọi Masih (Ysa) là con trai của Allah. Đó là lời nói phát ra từ cửa miệng của họ, bắt chước lời nói của những kẻ đã không có đức tin trước đây. Allah nguyền rủa họ. Sao mà họ lầm lạc đến thế!');
INSERT INTO "hasan_abdulkarim" VALUES(9,31,'9:31','Họ đã nhận các thầy tu Do thái và các thầy tu Công giáo làm chúa của họ thay vì Allah và (nhận cả) Masih (Ysa) con trai của Maryam (làm Chúa của họ nữa) trong lúc họ nhận lệnh chỉ được thờ phụng một Thượng Đế Duy nhất. Không có Thượng Đế nào khác cả duy chỉ Ngài (Allah là Thượng Đế). Ngài quang vinh và trong sạch về những điều (kẻ) mà họ đã tổ hợp với Ngài.');
INSERT INTO "hasan_abdulkarim" VALUES(9,32,'9:32','Họ muốn dập tắt Ánh Sáng của Allah bằng miệng lưỡi của họ nhưng Allah không chấp thuận (điều đó), ngược lại, Ngài muốn hoàn chỉnh Ánh Sáng của Ngài mặc dù những kẻ không có đức tin ghét điều đó.');
INSERT INTO "hasan_abdulkarim" VALUES(9,33,'9:33','Ngài là Đấng đã cử Sứ Giả của Ngài mang Chỉ Đạo và tôn giáo chân lý (Islam) đến để làm cho nó thắng mọi tôn giáo mặc dầu những người dân đa thần không thích.');
INSERT INTO "hasan_abdulkarim" VALUES(9,34,'9:34','Hỡi những ai có đức tin! Quả thật, đa số các thầy tu Do thái và các thầy tu Công giáo đã ăn chặn tài sản của thiên hạ bằng sự giả dối của họ và cản trở người khác theo Chính Đạo của Allah; và có những kẻ dự trữ vàng và bạc nhưng không chi dùng nó cho Chính Nghĩa của Allah; hãy báo cho họ biết về một sự trừng phạt đau đớn.');
INSERT INTO "hasan_abdulkarim" VALUES(9,35,'9:35','Vào ngày mà nó (vàng bạc tích trữ) sẽ được nung đỏ trong lửa và sẽ được mang đến đóng vào trán, hông và lưng của họ (kèm theo lời phán:) “Đây là vật mà các ngươi tích trữ cho bản thân của các ngươi. Thôi, hãy nếm món (vật) mà các ngươi đã tích trữ.”');
INSERT INTO "hasan_abdulkarim" VALUES(9,36,'9:36','Quả thật, số tháng (trong một năm) mà Allah đã qui định trong sổ sách của Ngài kể từ ngày tạo thiên lập địa là mười hai ( tháng, trong đó có bốn tháng linh(69). Đó là tôn giáo đúng đắn. Bởi thế, chớ làm thiệt hại bản thân mình (bằng cách vi phạm các điều cấm kỵ) trong các tháng linh đó. Và hãy đánh toàn bộ những người dân đa thần giống như chúng đã đánh các ngươi toàn bộ. Và nên biết rằng Allah ở cùng với những người ngay chính sợ Ngài.');
INSERT INTO "hasan_abdulkarim" VALUES(9,37,'9:37','Tục lệ dời tháng linh là một minh chứng thêm về sự không tin của chúng: vì tục lệ đó mà những kẻ không tin bị lầm lạc. Có năm chúng cho phép dời lại một tháng linh, nhưng có năm chúng cấm dời. Làm thế để chúng có thể bù lại cho đủ số tháng linh qui định bởi Allah. Bằng cách đó chúng cho phép làm điều mà Allah đã cấm cản. Việc làm điên rồ của chúng lại làm đẹp lòng chúng. Nhưng Allah đã không hướng dẫn một đám người không có niềm tin.');
INSERT INTO "hasan_abdulkarim" VALUES(9,38,'9:38','Hỡi những ai có đức tin! Tại sao khi được yêu cầu ra đi chiến đấu( cho Chính Nghĩa của Allah, các ngươi ngồi lì dưới đất? Phải chăng các ngươi thích đời sống trần tục này hơn đời sau? Nhưng sự hưởng nhàn ở đời này thật là nhỏ nhoi so với đời sau.');
INSERT INTO "hasan_abdulkarim" VALUES(9,39,'9:39','Trừ phi các ngươi ra đi chiến đấu, nếu không thì Ngài (Allah) sẽ trừng phạt các ngươi đau đớn và sẽ đưa một đám người khác đến thế chỗ của các ngươi và các ngươi chẳng làm thiệt hại được Ngài, bởi vì Allah là Đấng Toàn Năng trên tất cả mọi thứ.');
INSERT INTO "hasan_abdulkarim" VALUES(9,40,'9:40','Nếu các ngươi không giúp Y (Nabi Muhammad cũng không sao). Bởi vì Allah đã giúp Y khi những kẻ không có đức tin nhất định trục xuất Y đi nơi khác. (Nabi) là người thứ hai trong số hai người trốn trong hang núi. (Nabi) nói với người bạn đồng hành (Abu Bakr): “Chớ sợ, bởi vì Allah đang ở cùng với chúng ta.” Rồi Allah ban sự trầm tĩnh từ Ngài xuống cho Y và tăng cường cho Y với thiên binh mà (mắt trần của) các ngươi không nhìn thấy đặng và Ngài làm lịm đi lời nói (huênh hoang) của những kẻ không tin và làm lẫy lừng Lời nói của Allah bởi vì Allah Toàn Năng, Rất Mực Sáng Suốt.');
INSERT INTO "hasan_abdulkarim" VALUES(9,41,'9:41','Hãy lên đường đi chiến đấu (dù được trang bị với phương tiện) nhẹ hay nặng; và hãy tận lực chiến đấu cho Chính Nghĩa của Allah với cả tài sản và sinh mạng của các ngươi. Điều đó tốt nhất cho các ngươi nếu các ngươi biết (giá trị của nó).');
INSERT INTO "hasan_abdulkarim" VALUES(9,42,'9:42','Nếu họ thấy có một mối lợi gần kề trước mắt và một chuyến đi dễ dàng thì chắc chắn họ sẽ theo Ngươi, nhưng quảng đường viễn chinh (đến Tabuk) quá xa (và nặng nhọc) đối với họ. Và họ đã thề nhân danh Allah (nói:) “Nếu có khả năng, chắc chắn chúng tôi sẽ đi chiến đấu với quí vị.” Họ đã tự hủy hoại linh hồn của họ bởi vì Allah biết rõ họ là những kẻ nói dối.');
INSERT INTO "hasan_abdulkarim" VALUES(9,43,'9:43','Allah lượng thứ cho Ngươi đó! Tại sao Ngươi cho phép chúng (ở lại nhà) khi chưa hiện rõ cho Ngươi thấy ai nói thật và chưa biết ai nói dối?');
INSERT INTO "hasan_abdulkarim" VALUES(9,44,'9:44','Những ai có đức tin nơi Allah và Ngày (Phán Xử) Cuối Cùng đã không hỏi xin Ngươi miễn đi chiến đấu với cả tài sản và sinh mạng của họ bởi vì Allah biết rõ những người ngay chính sợ Allah.');
INSERT INTO "hasan_abdulkarim" VALUES(9,45,'9:45','Chỉ những ai không có đức tin nơi Allah và Ngày Phán Xử Cuối Cùng và ngờ vực trong lòng mới xin phép Ngươi (được ở lại nhà). Bởi vì do ngờ vực mà chúng đâm ra lưỡng lự.');
INSERT INTO "hasan_abdulkarim" VALUES(9,46,'9:46','Và nếu chúng có ý định đi chiến đấu thì chúng đã chuẩn bị (cho cuộc viễn chinh) rồi. Nhưng Allah lại không thích việc chúng đi tham chiến; bởi thế Ngài giữ chúng lại và phán: “Hãy ở lại nhà cùng với những người ở lại.”');
INSERT INTO "hasan_abdulkarim" VALUES(9,47,'9:47','Nếu chúng có đi tham chiến thì chúng cũng chẳng giúp gì thêm cho các ngươi, ngược lại, chúng chỉ làm rối loạn hàng ngũ bằng cách lăng xăng chạy qua chạy lại giữa các ngươi để xúi giục kẻ khác làm loạn và trong các ngươi có một số tín đồ nghe theo chúng; nhưng Allah biết rõ những người làm điều sai quấy.');
INSERT INTO "hasan_abdulkarim" VALUES(9,48,'9:48','Chắc chắn chúng đã tìm cách xúi giục cuộc nổi loạn trước đây và làm đảo lộn công việc, gây thêm khó khăn cho Ngươi cho đến khi xảy ra sự thật; và Mệnh lệnh của Allah tỏ rõ cho họ thấy thì họ càng căm tức thêm.');
INSERT INTO "hasan_abdulkarim" VALUES(9,49,'9:49','Trong chúng có người nói: “Xin miễn cho tôi (đi chiến đấu) và chớ thử thách tôi.” Há chúng đã không rơi vào vòng thử thách ư? Thật vậy, Hoả Ngục đang bao vây những kẻ không có đức tin.');
INSERT INTO "hasan_abdulkarim" VALUES(9,50,'9:50','Nếu Ngươi gặp điều phúc lành thì chúng trở nên buồn bực nhưng nếu Ngươi gặp hoạn nạn thì chúng lên tiếng ngay: “Quả thật, chúng tôi cần phải lo liệu công việc của chúng tôi trước". Và chúng thích thú quay bỏ đi.');
INSERT INTO "hasan_abdulkarim" VALUES(9,51,'9:51','Hãy bảo chúng: “Chẳng có điều gì (tốt hay xấu) xảy đến cho chúng tôi cả mà chỉ có điều Allah đã định sẵn cho chúng tôi. Ngài là Đấng Bảo Hộ của chúng tôi". Và những người có đức tin hãy trọn tin mà phó thác cho Allah.');
INSERT INTO "hasan_abdulkarim" VALUES(9,52,'9:52','Hãy bảo chúng: “Phải chăng các người đang mong cho bọn ta gặp phải một trong hai điều tốt nhất? (Thắng lợi hoặc tử đạo?) Và bọn ta đang mong việc Allah sẽ tự Ngài giáng hình phạt lên các người hoặc Ngài sẽ dùng bàn tay của bọn ta để trừng trị các người. Bởi thế, hãy trông chờ đi, bọn ta sẽ cùng chờ đợi với các người.');
INSERT INTO "hasan_abdulkarim" VALUES(9,53,'9:53','Hãy bảo chúng: “Chi dùng (cho Chính Nghĩa của Allah) tự nguyện hay không, là do các người. Nhưng nó sẽ không được chấp nhận từ các người bởi lẽ các người là một đám người dấy loạn và bất tuân.');
INSERT INTO "hasan_abdulkarim" VALUES(9,54,'9:54','Và lý do tại sao việc đóng góp của chúng không được chấp nhận là vì chúng đã phủ nhận Allah và Sứ Giả của Ngài và chúng đến dâng lễ Salah một cách uể oải và chúng miễn cưỡng chi dùng (tài sản của chúng cho Chính Nghĩa của Allah).');
INSERT INTO "hasan_abdulkarim" VALUES(9,55,'9:55','Bởi vậy chớ để cho tài sản và con cái của chúng làm Ngươi trầm trồ. Thật ra, Allah chỉ muốn dùng những thứ đó để trừng phạt chúng trên thế gian này và để cho linh hồn của chúng ra đi vĩnh viễn trong tình trạng chúng là những kẻ phủ nhận đức tin.');
INSERT INTO "hasan_abdulkarim" VALUES(9,56,'9:56','Và chúng thề nhân danh Allah rằng chúng thực sự là thành phần của các ngươi (Muslim) nhưng chúng không phải là người của các ngươi; ngược lại, chúng là một đám người sợ (các ngươi).');
INSERT INTO "hasan_abdulkarim" VALUES(9,57,'9:57','Nếu chúng có thể tìm được một chỗ để trốn, dù đó là một cái hang hoặc là một nơi ẩn náu thì chúng sẽ quay mình cắm đầu chạy đến đó.');
INSERT INTO "hasan_abdulkarim" VALUES(9,58,'9:58','Và trong bọn chúng có kẻ đã xuyên tạc Ngươi về việc chia của bố thí. Nếu chúng được chia phần thì chúng hả dạ; ngược lại, nếu không được chia phần thì chúng đùng đùng nổi giận.');
INSERT INTO "hasan_abdulkarim" VALUES(9,59,'9:59','Và nếu chúng bằng lòng với phần chia mà Allah và Sứ Giả của Ngài ban cấp và nói: “Allah đủ cho chúng tôi! Allah và Sứ Giả của Ngài sẽ sớm ban thiên lộc của Ngài cho chúng tôi. Chúng tôi đặt hy vọng nơi Allah.”');
INSERT INTO "hasan_abdulkarim" VALUES(9,60,'9:60','Thật ra, của bố thí chỉ dành cho người nghèo, người thiếu thốn, người thu và quản lý của bố thí, những người mà tấm lòng của họ vừa mới hài hoà (với Chân Lý của Islam), những người bị giam cầm (nô lệ hay tù binh chiến tranh), những người mắc nợ, dùng để phục vụ Chính Nghĩa của Allah, và những người lỡ đường xa. Đó là Mệnh lệnh của Allah bởi vì Allah Biết hết, Rất Mực Sáng Suốt.');
INSERT INTO "hasan_abdulkarim" VALUES(9,61,'9:61','Và trong bọn chúng có kẻ đã xúc phạm Nabi (Muhammad) bởi vì chúng nói: “Người thật lắm tai.” Hãy bảo chúng: “Người nghe điều gì tốt cho các ngươi. Người tin tưởng nơi Allah, tin cậy các tín đồ và là một Hồng Ân cho những ai có đức tin.” Và những ai xúc phạm Sứ Giả của Allah thì sẽ nhận một sự trừng phạt đau đớn.');
INSERT INTO "hasan_abdulkarim" VALUES(9,62,'9:62','Chúng mang Allah ra thề thốt với các ngươi để làm hài lòng các ngươi nhưng đúng ra, chúng phải làm hài lòng Allah và Sứ Giả của Ngài nếu chúng là những người có đức tin.');
INSERT INTO "hasan_abdulkarim" VALUES(9,63,'9:63','Há chúng không biết rằng ai chống đối Allah và Sứ Giả của Ngài sẽ chịu (hình phạt của hỏa ngục) hay sao, nơi mà chúng sẽ vào ở muôn đời? Đó là một sự nhục nhã vô cùng to lớn.');
INSERT INTO "hasan_abdulkarim" VALUES(9,64,'9:64','Những người giả tạo đức tin lo sợ về việc có một chương kinh nói về chúng sẽ được ban xuống, vạch cho chúng biết điều (tội lỗi) nằm trong lòng của chúng. Hãy bảo chúng: “Các người hãy chế nhạo đi!” Quả thật, Allah sẽ mang ra ánh sáng tất cả các điều mà các ngươi đang sợ (thiên hạ biết).');
INSERT INTO "hasan_abdulkarim" VALUES(9,65,'9:65','Và nếu Ngươi có hỏi chúng thì chắc chắn chúng sẽ đáp: “Chúng tôi chỉ nói đùa cho vui.” Hãy bảo chúng: “Có phải các ngươi đang chế giễu Allah và các Lời Mặc Khải của Ngài cũng như Sứ Giả của Ngài?');
INSERT INTO "hasan_abdulkarim" VALUES(9,66,'9:66','Chớ tìm cách biện bạch. Chắc chắn các ngươi chối bỏ đức tin sau khi các ngươi đã tuyên bố tin tưởng. Nếu TA (Allah) lượng thứ cho một thành phần của các ngươi thì TA cũng sẽ trừng phạt một thành phần khác bởi vì chúng là những kẻ tội lỗi.');
INSERT INTO "hasan_abdulkarim" VALUES(9,67,'9:67','Những người giả tạo đức tin, nam và nữ, đều như nhau. Chúng truyền lệnh làm điều ác (tội lỗi) và cấm cản làm điều lành (đạo đức) và bụm chặt tay lại (keo kiệt không muốn chi dùng). Chúng quên bẵng Allah nên Allah bỏ quên chúng trở lại. Quả thật, những tên giả tạo đức tin là những kẻ dấy loạn và bất tuân.');
INSERT INTO "hasan_abdulkarim" VALUES(9,68,'9:68','Và Allah hứa với những người giả tạo đức tin, nam và nữ, và với những kẻ không có đức tin, Lửa của hỏa ngục, trong đó chúng sẽ vào ở đời đời. hỏa ngục đủ cho chúng. Và Allah nguyền rủa chúng và chúng sẽ bị trừng phạt vĩnh viễn.');
INSERT INTO "hasan_abdulkarim" VALUES(9,69,'9:69','Giống như những kẻ trước các ngươi, quyền lực của họ mạnh hơn các ngươi, tài sản và con cái của họ nhiều và đông hơn các ngươi. Họ hưởng thụ phần của họ một thời gian, các người hưởng thụ phần của các ngươi một thời gian giống như những kẻ trước các ngươi đã từng hưởng thụ; và các ngươi đã tán gẫu giống như những kẻ trước các ngươi đã tán gẫu. Đó là những kẻ mà việc làm của chúng chẳng được kết quả nào ở đời này và đời sau. Và chúng sẽ là những kẻ thua thiệt.');
INSERT INTO "hasan_abdulkarim" VALUES(9,70,'9:70','Há chúng chưa hề nghe câu chuyện của những kẻ sống trước chúng hay sao? Đám người của Nuh, của ''Ad, và Thamud, và đám người của Ibrahim và của Madyan và các thị trấn bị lật ngược (do các trận động đất)? Các Sứ Giả của họ đã đến gặp họ với những bằng chứng rõ rệt (để khuyên họ, nhưng họ không nghe,) bởi thế không phải Allah bất công với họ mà chính họ tự hại bản thân của mình mà thôi.');
INSERT INTO "hasan_abdulkarim" VALUES(9,71,'9:71','Và những người tin tưởng, nam và nữ, là đồng minh bảo vệ lẫn nhau. Họ ra lệnh làm điều lành và cấm cản nhau làm điều dữ và năng dâng lễ Salah và đóng Zakah và tuân lệnh của Allah và Sứ Giả của Ngài. Đó là những người mà Allah khoan dung bởi vì Allah Toàn Năng, Thấu Suốt (tất cả).');
INSERT INTO "hasan_abdulkarim" VALUES(9,72,'9:72','Allah hứa với những người có đức tin, nam và nữ về các Ngôi vườn (thiên đàng) bên dưới có các dòng sông chảy, họ sẽ ở trong đó đời đời và mãi mãi, với các biệt thự cao sang trong các thiên đàng vĩnh cửu; nhưng hạnh phúc tột bậc là sự hài lòng của Allah; và đó là một thắng lợi vĩ đại.');
INSERT INTO "hasan_abdulkarim" VALUES(9,73,'9:73','Hỡi Nabi (Muhammad)! Hãy chiến đấu quyết liệt chống những kẻ không tin và những tên giả tạo đức tin và hãy cứng rắn đối với chúng, bởi vì nhà ở của chúng là hỏa ngục, một nơi cư ngụ xấu xa nhất.');
INSERT INTO "hasan_abdulkarim" VALUES(9,74,'9:74','Chúng thề nhân danh Allah rằng chúng đã không nói điều gì (xấu) cả, nhưng chắc chắn chúng đã thốt ra lời lẽ phạm thượng và không tin sau khi đã gia nhập Islam và chúng đã mưu định một ý đồ mà chúng không thể thực hiện được. Việc trả thù này của chúng là để đền lại thiên lộc mà Allah và Sứ Giả của Ngài đã làm cho chúng thành giàu có. Nếu chúng ăn năn hối cải thì tốt cho chúng; ngược lại, nếu chúng trở lại (con đường thù hận cũ) thì Allah sẽ trừng phạt chúng đau đớn ở đời này và đời sau. Và chúng sẽ không có một ai trên trái đất để bênh vực và giúp đỡ chúng.');
INSERT INTO "hasan_abdulkarim" VALUES(9,75,'9:75','Và trong bọn chúng có người giao ước với Allah (nguyện rằng): "Nếu Ngài ban cho chúng tôi thiên lộc của Ngài thì chúng tôi sẽ bố thí (rộng rãi) và sẽ là những người đạo đức".');
INSERT INTO "hasan_abdulkarim" VALUES(9,76,'9:76','Nhưng khi Ngài ban cho chúng thiên lộc, chúng đâm ra keo kiệt và lật lọng và ghét việc thực hiện lời giao ước.');
INSERT INTO "hasan_abdulkarim" VALUES(9,77,'9:77','Bởi vì chúng đã bội ước và dối gạt Allah nên Ngài đã trả miếng lại bằng cách đặt sự giả dối vào sâu trong lòng của chúng (kéo dài) mãi cho đến ngày chúng sẽ gặp lại Ngài.');
INSERT INTO "hasan_abdulkarim" VALUES(9,78,'9:78','Há chúng không biết rằng Allah biết rõ tư tưởng bí mật và những lời mật nghị của chúng ư? Và (nên biết) rằng Allah biết rõ mọi điều vô hình.');
INSERT INTO "hasan_abdulkarim" VALUES(9,79,'9:79','Những ai nói xấu tín đồ có hảo tâm bố thí rộng rãi và chỉ trích những người bố thí tùy khả năng của họ và chê cười họ thì sẽ bị Allah ném trả lại giọng điệu chê cười của chúng và sẽ bị trừng phạt đau đớn.');
INSERT INTO "hasan_abdulkarim" VALUES(9,80,'9:80','Dẫu Ngươi có xin hoặc không xin (Allah) tha thứ cho chúng (thì tội của chúng sẽ không được dung tha); dẫu Ngươi có cầu xin sự tha thứ cho chúng bảy mươi lần đi nữa thì Allah vẫn không bao giờ tha thứ cho chúng. Sở dĩ như thế là vì chúng phủ nhận Allah và Sứ Giả của Ngài. Và Allah không hướng dẫn một đám người dấy loạn và bất tuân.');
INSERT INTO "hasan_abdulkarim" VALUES(9,81,'9:81','Những kẻ được cho phép ở lại nhà (trong chiến dịch Tabuk) vui mừng về việc chúng không đi chiến đấu với Sứ Giả của Allah bởi vì chúng ghét việc tham chiến với cả tài sản lẫn sinh mạng của chúng cho Chính Nghĩa của Allah; và chúng nói: “Chớ đi chiến đấu dưới ánh nắng cháy da.” Hãy bảo chúng: “Lửa của hỏa ngục nóng còn dữ dội hơn sức nóng của mặt trời.” Phải chi chúng hiểu điều đó.');
INSERT INTO "hasan_abdulkarim" VALUES(9,82,'9:82','Bởi thế, chúng nên cười ít và khóc nhiều về những điều mà chúng đã gây ra.');
INSERT INTO "hasan_abdulkarim" VALUES(9,83,'9:83','Bởi thế, nếu Allah đưa Ngươi trở về gặp lại một phần tử của bọn chúng và chúng sẽ đến xin phép Ngươi cho chúng đi (tham chiến) với Ngươi thì hãy bảo thẳng chúng: “Các người chớ bao giờ đi chiến đấu với ta cũng chớ bao giờ đi đánh giặc với ta bởi vì ngay từ lúc đầu các người đã chọn việc ở lại nhà thì (bầy giờ) cứ tiếp tục ở nhà cùng với những người ở lại.”');
INSERT INTO "hasan_abdulkarim" VALUES(9,84,'9:84','Và chớ bao giờ cầu lễ cho một người nào của bọn chúng từ trần và chớ đứng gần ngôi mộ của y bởi vì chúng phủ nhận Allah và Sứ Giả của Ngài và chết trong tình trạng phản nghịch.');
INSERT INTO "hasan_abdulkarim" VALUES(9,85,'9:85','Chớ để cho tài sản lẫn con cái của chúng làm Ngươi trầm trồ bởi vì Allah chỉ muốn dùng các thứ đó để trừng phạt chúng nơi trần gian này và muốn cho chúng vĩnh viễn ra đi trong tình trạng chúng là những kẻ không tin.');
INSERT INTO "hasan_abdulkarim" VALUES(9,86,'9:86','Và khi một Chương Kinh (của Qur''an) được ban xuống (truyền) rằng hãy tin tưởng nơi Allah và đi chiến đấu với Sứ Giả của Ngài thì những kẻ giàu có trong bọn chúng đến xin phép Ngươi được miễn đi chiến đấu và thưa: “Hãy cho phép chúng tôi ở lại hậu phương cùng với những người ở lại.”');
INSERT INTO "hasan_abdulkarim" VALUES(9,87,'9:87','Chúng rất hài lòng được ở lại hậu phương cùng với phụ nữ ở lại (giữ) nhà. Quả tim của chúng bị niêm kín cho nên chúng không hiểu gì cả.');
INSERT INTO "hasan_abdulkarim" VALUES(9,88,'9:88','Ngược lại, Sứ Giả (Muhammad) cùng với các tín đồ theo Y đã đi chiến đấu với cả tài sản lẫn sinh mạng của họ: đó là những người sẽ được hưởng mọi điều tốt và là những người sẽ có hạnh phúc.');
INSERT INTO "hasan_abdulkarim" VALUES(9,89,'9:89','Allah đã chuẩn bị sẵn cho họ các Ngôi Vườn (thiên đàng) bên dưới có các dòng sông chảy để cho họ vào ở vĩnh viễn. Đó là một thắng lợi rất to lớn.');
INSERT INTO "hasan_abdulkarim" VALUES(9,90,'9:90','Trong số người dân Ả Rập của vùng sa mạc và những ai nói dối Allah và Sứ Giả của Ngài để được ở lại nhà, có những kẻ đã đến cáo lỗi với Ngươi để được miễn đi chiến đấu. Những kẻ không có đức tin trong bọn chúng sẽ bị trừng phạt đau đớn.');
INSERT INTO "hasan_abdulkarim" VALUES(9,91,'9:91','(Tuy nhiên,) những người yếu đuối, hoặc bệnh tật hoặc nghèo khó không tìm ra phương tiện để đi tham chiến thì sẽ không bị khiển trách (về việc ở lại nhà) miễn sao họ thành thật với Allah và với Sứ Giả của Ngài. Bởi vì không có lý do nào để khiển trách những người làm tốt. Và Allah Hằng Tha Thứ, Rất Mực Khoan Dung.');
INSERT INTO "hasan_abdulkarim" VALUES(9,92,'9:92','Cũng không bị khiển trách, những ai đã đến gặp Ngươi để nhờ cấp cho phương tiện di chuyển, nhưng Ngươi bảo họ: “Ta không tìm ra phương tiện chuyên chở cho các ngươi” thì họ trở về buồn bã, nước mắt đầm đìa vì đã không tìm ra phương tiện để đóng góp (vào việc tham chiến).');
INSERT INTO "hasan_abdulkarim" VALUES(9,93,'9:93','Lý do khiển trách chỉ áp dụng cho những ai có đầy đủ phương tiện nhưng lại đến xin phép Ngươi cho miễn đi tham chiến: chúng hài lòng về việc được ở lại hậu phương với phụ nữ ở lại (giữ) nhà. Allah niêm kín quả tim của chúng nên chúng không biết gì cả.');
INSERT INTO "hasan_abdulkarim" VALUES(9,94,'9:94','Chúng sẽ đến cáo lỗi với các ngươi khi các ngươi trở về gặp lại chúng. Hãy bảo chúng: “Quí vị chớ cáo lỗi. Chúng tôi sẽ không bao giờ tin tưởng quí vị. Allah đã báo cho chúng tôi biết sự thật về quí vị. Allah và Sứ Giả của Ngài sẽ quan sát hành động của quí vị rồi cuối cùng quí vị sẽ được đưa về gặp lại Đấng biết điều bí mật và điều công khai (của quí vị), xong Ngài sẽ báo cho quí vị rõ về các việc (tội lỗi) mà quí vị đã từng làm.”');
INSERT INTO "hasan_abdulkarim" VALUES(9,95,'9:95','Khi các ngươi trở về gặp lại chúng, chúng sẽ mang Allah ra thề thốt với các ngươi mục đích để cho các ngươi lánh xa chúng. Bởi thế, hãy để mặc chúng bởi vì chúng bẩn thỉu và nhà ở của chúng là hỏa ngục. Đó là phần (phạt) về tội mà chúng đã làm ra.');
INSERT INTO "hasan_abdulkarim" VALUES(9,96,'9:96','Chúng sẽ thề với các ngươi để cho các ngươi hoà thuận lại với chúng. Nhưng nếu các ngươi hài lòng với chúng thì Allah vẫn không hài lòng với những kẻ làm loạn và bất tuân.');
INSERT INTO "hasan_abdulkarim" VALUES(9,97,'9:97','Người Ả Rập du mục là những kẻ rất không tin và giả tạo đức tin và hầu như hoàn toàn không biết gì đến Mệnh lệnh của Allah đã được truyền xuống cho Sứ Giả của Ngài. Bởi vì Allah Hằng Biết và Thông Suốt (hết lòng dạ và hành động của chúng).');
INSERT INTO "hasan_abdulkarim" VALUES(9,98,'9:98','Và trong số người dân Ả Rập du mục có kẻ đã xem của cải mà họ chi dùng (cho Chính Nghĩa của Allah) là một thứ tiền phạt và chúng chờ xem điều bất hạnh xảy đến cho các ngươi. Nhưng chính chúng là nạn nhân của tai ương đó bởi vì Allah Hằng Nghe và Hằng Biết (hết mọi việc).');
INSERT INTO "hasan_abdulkarim" VALUES(9,99,'9:99','Nhưng trong số người dân Ả Rập du mục có người tin tưởng nơi Allah và Ngày (Phán Xử) Cuối Cùng và xem của cải mà họ chi dùng (cho Chính Nghĩa của Allah) là một phương tiện để đưa họ đến gần Allah và để được lời chúc phúc của Sứ Giả (Muhammad). Vâng, những thứ đó sẽ đưa họ đến gần (Allah). Allah sẽ sớm khoan dung họ bởi vì Allah Hằng Tha Thứ, Rất Mực Khoan Dung.');
INSERT INTO "hasan_abdulkarim" VALUES(9,100,'9:100','Và những người tiên phong (của Islam) trong số các tín đồ di cư và trong số các tín đồ trợ giúp và những ai theo họ trong mọi việc làm tốt thì sẽ được Allah hài lòng với họ cũng như việc họ sẽ được hài lòng với Ngài. Ngài đã chuẩn bị cho họ các Ngôi vườn (thiên đàng) bên dưới có các dòng sông chảy để họ vào ở trong đó đời đời. Đó là một sự thành tựu vĩ đại.');
INSERT INTO "hasan_abdulkarim" VALUES(9,101,'9:101','Một số người dân Ả Rập du mục sống xung quanh các người cũng như một số thị dân của thành phố Madinah là những tên giả tạo đức tin. Chúng quen thói giả dối. Ngươi không biết chúng nhưng TA (Allah) biết rõ chúng. TA sẽ trừng phạt chúng hai lần. Rồi chúng sẽ được đưa về chịu một sự trừng phạt khủng khiếp.');
INSERT INTO "hasan_abdulkarim" VALUES(9,102,'9:102','Còn một số người khác đã nhận tội của họ. Họ đã trộn lẫn việc tốt chung với việc xấu. Có lẽ Allah đoái thương mà tha thứ cho họ bởi vì Allah Hằng Tha Thứ, Rất Mực Khoan Dung.');
INSERT INTO "hasan_abdulkarim" VALUES(9,103,'9:103','(Hỡi Sứ Giả)! Hãy nhận của bố thí từ tài sản của họ và dùng nó để tẩy sạch và làm trong sạch họ và hãy cầu nguyện giùm cho họ. Quả thật, lời cầu nguyện của Ngươi là một sự bảo đảm cho họ. Và Allah Hằng Nghe và Biết (hết mọi việc).');
INSERT INTO "hasan_abdulkarim" VALUES(9,104,'9:104','Há họ không biết rằng Allah chấp nhận sự hối cải và của bố thí của bầy tôi của Ngài và rằng Allah Hằng Quay lại Tha thứ, Rất Mực Khoan Dung hay sao?');
INSERT INTO "hasan_abdulkarim" VALUES(9,105,'9:105','Hãy bảo họ: “Hãy làm (việc thiện). Allah và Sứ Giả của Ngài và những người tin tưởng sẽ quan sát việc làm của các người. Và các người sẽ được đưa về gặp lại Đấng biết điều bí mật và điều công khai. Xong Ngài sẽ cho các người biết về các việc mà các ngươi đã làm.”');
INSERT INTO "hasan_abdulkarim" VALUES(9,106,'9:106','Còn một số khác bị phạt treo và phải chờ quyết định của Allah hoặc Ngài sẽ trừng phạt hay tha thứ cho họ bởi vì Allah Biết hết và Thông Suốt (hoàn cảnh của họ).');
INSERT INTO "hasan_abdulkarim" VALUES(9,107,'9:107','Và những kẻ đã xây một thánh đường với ý đồ làm tổn thương và chống đối (Islam) và làm chia rẽ hàng ngũ của những người tin tưởng và dùng nó làm một nơi ẩn núp cho kẻ( đã từng gây chiến chống Allah và Sứ Giả của Ngài trước đây. Và chúng thề thốt, nói: “Chúng tôi chỉ muốn làm điều phúc lành” nhưng Allah xác nhận chúng là những kẻ nói dối.');
INSERT INTO "hasan_abdulkarim" VALUES(9,108,'9:108','Ngươi chớ bao giờ đứng (dâng lễ) trong đó. Chắc chắn Thánh đường đã được xây dựng vào ngày đầu tiên (tại Quba’) với lòng ngay chính sợ Allah mới là nơi xứng đáng cho Ngươi đứng dâng lễ trong đó. Trong đó, có người yêu thích việc thanh sạch bản thân. Bởi vì Allah yêu thương những người thanh khiết.');
INSERT INTO "hasan_abdulkarim" VALUES(9,109,'9:109','Vậy ai là người tốt nhất? Một người xây dựng thánh đường với lòng ngay chính sợ Allah và muốn làm hài lòng Ngài và một người xây dựng thánh đường trên một bờ đê rạn nứt chỉ chờ sụp xuống hỏa ngục cùng với y? Bởi vì Allah không hướng dẫn một đám người làm điều sai quấy.');
INSERT INTO "hasan_abdulkarim" VALUES(9,110,'9:110','Ngôi thánh đường mà chúng xây dựng sẽ không bao giờ chấm dứt nỗi ngờ vực trong lòng của chúng trừ phi trái tim của chúng bị cắt ra làm hai (thì mới hết). Bởi vì Allah Hằng Biết và Thông Suốt hết (nỗi lòng của chúng).');
INSERT INTO "hasan_abdulkarim" VALUES(9,111,'9:111','Quả thật, Allah đã mua từ những người tin tưởng sinh mạng và tài sản của họ để đổi lại cho họ thiên đàng. Họ đã chiến đấu cho Chính Nghĩa của Allah, bởi thế họ đã giết (địch) và bị (địch) giết. (Đó là) một Lời Hứa đã ràng buộc Ngài (Allah) trong (Kinh) Tawrah, lnjil và Qur''an. Và ai giữ trọn lời hứa hơn Allah? Bởi thế, hãy hoan hỉ với sự đổi chác mà các ngươi đã mặc cả với Ngài. Và đó là một thành quả vô cùng to lớn.');
INSERT INTO "hasan_abdulkarim" VALUES(9,112,'9:112','Những người ăn năn hối cải (với Allah), những người thờ phụng Ngài, những người đi đây đó để phụng sự Ngài, những người cúi đầu kính cẩn, và quỳ mọp phủ phục, những người truyền lệnh làm điều thiện và ngăn cấm làm điều ác và tôn trọng các giới hạn qui định bởi Allah (sẽ hoan hỉ). Và hãy báo tin vui cho những người tin tưởng.');
INSERT INTO "hasan_abdulkarim" VALUES(9,113,'9:113','Nabi và những người có đức tin không được phép xin (Allah) tha thứ cho những người dân đa thần dù đó là bà con ruột thịt của họ đi nữa sau khi họ đã nhận thấy mình là những người bạn của hỏa ngục.');
INSERT INTO "hasan_abdulkarim" VALUES(9,114,'9:114','Và Ibrahim cầu xin (Allah) tha thứ cho người cha của mình là do lời hứa mà Y đã hứa với người cha. Nhưng khi thấy rõ người cha của mình là một kẻ thù của Allah thì Người đoạn tuyệt với ông. Quả thật, Ibrahim là một người con có hiếu, hằng chịu đựng.');
INSERT INTO "hasan_abdulkarim" VALUES(9,115,'9:115','Allah không phải là Đấng đã làm cho một đám người lạc hướng sau khi đã hướng dẫn họ trừ phi Ngài làm cho họ thấy rõ điều mà họ cần phải tránh. Bởi vì Allah biết hết mọi việc.');
INSERT INTO "hasan_abdulkarim" VALUES(9,116,'9:116','Quả thật, Allah là Đấng nắm quyền thông trị các tầng trời và trái đất. Ngài ban sự sống và gây cho chết. Và ngoài Allah, các ngươi không có một Vị Bảo hộ hay một Vị Cứu tinh nào cả.');
INSERT INTO "hasan_abdulkarim" VALUES(9,117,'9:117','Chắc chắn Allah quay lại tha thứ cho Nabi và các tín đồ Di cư (Muhajir) và các tín đồ Trợ giúp (Ansar) là những người đã từng theo Y(Nabi) trong những giờ phút khó khăn sau khi tấm lòng của một số người của họ gần muốn chao đảo; rồi Ngài quay lại tha thứ cho họ bởi vì Ngài Hằng Lượng Thứ và Khoan Dung.');
INSERT INTO "hasan_abdulkarim" VALUES(9,118,'9:118','Và cuối cùng Ngài (Allah) tha thứ cho ba người (Ka''ab, Murarah và Hilal) đã bị án treo. (Họ cảm thấy nhục) đến mức trái đất tuy rộng thênh thang bổng nhiên trở thành chật hẹp đối với linh hồn của họ; và họ nhận thấy không còn chỗ nào để chạy trốn khỏi Allah trừ phi chạy đến tạ tội với Ngài. Rồi Ngài quay lại tha thứ để cho họ có dịp ăn năn hối cải. Bởi vì Allah là Đấng Hằng Đoái Thương, Hằng Tha Thứ, Rất Mực Khoan Dung.');
INSERT INTO "hasan_abdulkarim" VALUES(9,119,'9:119','Hỡi những ai có đức tin! Hãy sợ Allah và hãy nhập đoàn với những người chân thật.');
INSERT INTO "hasan_abdulkarim" VALUES(9,120,'9:120','Thị dân của thành phố Madinah và những người Ả Rập du mục sống lân cận không được bỏ mặc Sứ Giả của Allah (đi chiến đấu một mình) cũng không được quí mạng sống của mình hơn mạng sống của Nabi. Như thế là vì mọi khó khăn mà họ gặp phải trên đường đi chiến đấu sẽ được Allah ghi công cho họ chẳng hạn như chịu khát, chịu mệt, chịu đói vì Chính Nghĩa của Allah hoặc như mỗi bước chân của họ bước đi sẽ làm cho những kẻ không tin nổi giận hoặc như mọi thắng lợi mà họ đã đạt được từ kẻ thù: tất cả đều được xem là việc thiện của họ. Bởi vì Allah sẽ không làm mất phần thưởng của những người làm tốt.');
INSERT INTO "hasan_abdulkarim" VALUES(9,121,'9:121','Cũng như món tiền, dù nhỏ hay lớn (ít hay nhiều) mà họ chi dùng (cho Chính Nghĩa của Allah) hoặc như việc băng qua các thung lũng, tất cả đều được ghi công cho họ để Allah dựa vào đó mà tưởng thưởng họ về những việc làm tốt của họ.');
INSERT INTO "hasan_abdulkarim" VALUES(9,122,'9:122','Và những người có đức tin không bắt buộc phải đi chiến đấu tất cả. Nếu mỗi thành phần cử một nhóm người của họ đi chinh chiến để học hỏi (các vấn đề) tôn giáo và khi trở về để giáo dục lại dân chúng thì cách đó sẽ giúp họ tránh phạm tội.');
INSERT INTO "hasan_abdulkarim" VALUES(9,123,'9:123','Hỡi những ai có đức tin! Hãy đánh những kẻ không tin ở gần các ngươi và tỏ cho chúng thấy các ngươi cứng rắn; và hãy biết rằng Allah sát cánh với những người sợ Ngài.');
INSERT INTO "hasan_abdulkarim" VALUES(9,124,'9:124','Và khi có một chương Kinh được ban xuống thì có người trong bọn chúng lên tiếng: “Ai trong quí vị sẽ tăng thêm đức tin với nó?” Bởi thế, đối với những ai có đức tin thì nó sẽ gia tăng đức tin cho họ và họ hoan hỉ.');
INSERT INTO "hasan_abdulkarim" VALUES(9,125,'9:125','Còn đối với những ai mang một chứng bệnh (ngờ vực) trong lòng thì nó sẽ làm cho bệnh tình của họ ô nhiễm thêm, cái này chồng lên cái kia và chúng sẽ chết trong tình trạng mất đức tin.');
INSERT INTO "hasan_abdulkarim" VALUES(9,126,'9:126','Há chúng không nhận thấy mỗi năm chúng đã gặp tai hoạ một hay hai lần hay sao? Rồi chúng không chịu ăn năn hối cải và không chịu tỉnh ngộ.');
INSERT INTO "hasan_abdulkarim" VALUES(9,127,'9:127','Và khi có một chương Kinh nào được ban xuống, chúng nhìn nhau (có ý hỏi): “Có người nào nhìn thấy quí vị hay chăng?” Allah xoay tấm lòng của chúng đi nơi khác bởi vì chúng là một đám người không hiểu gì cả.');
INSERT INTO "hasan_abdulkarim" VALUES(9,128,'9:128','(Hỡi người dân!) Quả thật, một Sứ Giả xuất thân từ các người đã đến gặp các ngươi, Y buồn rầu khi thấy các ngươi đau khổ, và hết sức lo lắng cho các ngươi. Y đại lượng và khoan dung đối với những người tin tưởng.');
INSERT INTO "hasan_abdulkarim" VALUES(9,129,'9:129','Nhưng nếu chúng từ chối thì hãy bảo chúng: “Allah đủ cho ta. Không có Thượng Đế nào khác duy chỉ Ngài (là Thượng Đế). Ta phó thác cho Ngài bởi vì Ngài là Thượng Đế (Đấng Chủ Tể) của Chiếc Ngai vương Chí đại.”');
INSERT INTO "hasan_abdulkarim" VALUES(10,1,'10:1','Alif. Lam. Ra. Đây là những Câu của một Kinh Sách rất mực thông suốt.');
INSERT INTO "hasan_abdulkarim" VALUES(10,2,'10:2','Phải chăng thiên hạ ngạc nhiên về việc TA (Allah) đã mặc khải Kinh Sách cho một người phàm xuất thân từ họ? (phán bảo) “Ngươi hãy cảnh cáo nhân loại (về nguy cơ của họ ở ngày sau) và hãy báo tin mừng cho những người tin tưởng rằng họ sẽ nhận niềm vinh dự với Thượng Đế (Allah) của họ về sự thành thật mà họ đã gởi đi trước cho họ?” Nhưng những kẻ không có đức tin lại nói rằng: “Người này (Muhammad) chẳng qua chỉ là một nhà phù thủy tài giỏi!”');
INSERT INTO "hasan_abdulkarim" VALUES(10,3,'10:3','Quả thật, Thượng Đế của các ngươi là Allah, Đấng đã tạo các tầng trời và trái đất trong sáu Ngày rồi lên an vị trên Ngai vương để điều hành và quản lý công việc. Không ai được phép can thiệp (vào công việc của Ngài) trừ phi được Ngài cho phép. Allah, Thượng Đế của các ngươi là như thế. Do đó, hãy thờ phụng Ngài. Các ngươi có ghi nhớ điều đó chăng?');
INSERT INTO "hasan_abdulkarim" VALUES(10,4,'10:4','Tất cả các ngươi sẽ được đưa về gặp Ngài trở lại. Lời Hứa (này) của Allah là sự Thật. Ngài bắt đầu sự tạo hóa rồi tái lập nó để Ngài tưởng thưởng một cách công bằng những ai tin tưởng và làm việc thiện. Ngược lại, những ai không có đức tin thì sẽ uống một loại nước thật sôi và chịu một hình phạt rất đau đớn bởi vì họ đã từng phủ nhận Allah.');
INSERT INTO "hasan_abdulkarim" VALUES(10,5,'10:5','Ngài là Đấng đã làm ra mặt trời sáng chói và mặt trăng chiếu sáng (tươi mát) và qui định cho nó những giai đoạn tròn khuyết chính xác nhờ đó các người biết được con số của những niên kỷ và cách ghi đếm thời gian. Allah không tạo những cái đó một cách phù phiếm mà vì Chân Lý. Ngài giải thích những dấu hiệu của Ngài cho một đám người hiểu biết.');
INSERT INTO "hasan_abdulkarim" VALUES(10,6,'10:6','Quả thật, trong sự luân chuyển của đêm và ngày cũng như của tất cả vạn vật mà Allah đã tạo hóa trong các tầng trời và trái đất là những dấu hiệu cho một đám người sợ Allah.');
INSERT INTO "hasan_abdulkarim" VALUES(10,7,'10:7','Quả thật, những ai không màng đến việc gặp gỡ lại TA (ở Ngày Sau) và họ hài lòng và thỏa mãn với đời sống trần tục này cùng với những kẻ không lưu ý đến những dấu hiệu của TA,');
INSERT INTO "hasan_abdulkarim" VALUES(10,8,'10:8','Họ là những kẻ mà nhà ở sẽ là hỏa ngục do hậu quả của những điều mà họ đã từng thu hoạch.');
INSERT INTO "hasan_abdulkarim" VALUES(10,9,'10:9','Quả thật, những ai tin tưởng và làm việc thiện thì chắc chắn do đức tin của họ mà sẽ được Thượng Đế của họ hướng dẫn đến những Ngôi vườn (thiên đàng) hạnh phúc bên dưới có các dòng sông chảy.');
INSERT INTO "hasan_abdulkarim" VALUES(10,10,'10:10','Lời cầu nguyện của họ trong đó (sẽ là:) “Quang vinh thay Ngài, Ôi Allah!” và lời chào họ trong đó sẽ là: “Sự Bằng An (cho quí vị) và lời cầu nguyện cuối cùng của họ sẽ là: “Mọi lời ca tụng đều dâng lên Allah, Thượng Đế (Đấng Chủ Tể) của vũ trụ và muôn loài.”');
INSERT INTO "hasan_abdulkarim" VALUES(10,11,'10:11','Và nếu Allah thúc đẩy điều xấu đến nhanh cho loài người (theo yêu cầu của họ) cũng như việc họ thúc giục điều tốt mau đến cho họ thì tuổi thọ đã được ban cho họ (để sống và hoạt động) đã chấm dứt từ lâu. (Đường lối của TA lại khác); TA để mặc cho những ai không màng đến việc gặp lại TA (ở đời sau) lang thang mù quáng trong sự thái quá của họ.');
INSERT INTO "hasan_abdulkarim" VALUES(10,12,'10:12','Và (thái độ lạ lùng của) con người (là) khi gặp hoạn nạn, họ van vái TA giải cứu họ (ở đủ mọi tư thế) lúc nằm, lúc ngồi hoặc lúc đứng. Nhưng sau khi TA đã giải cứu họ thoát khỏi cơn nguy khốn, họ bước đi dửng dưng làm như họ chẳng hề van vái TA cứu nguy khi gặp hoạn nạn. Những điều (vô ơn) mà những kẻ phạm tội đã từng làm lại tỏ ra đẹp mắt đối với chúng.');
INSERT INTO "hasan_abdulkarim" VALUES(10,13,'10:13','(Hỡi nhân loại!) Thật sự TA (Allah) đã tiêu diệt những thế hệ trước các ngươi khi chúng làm điều sai quấy. Những Sứ Giả của chúng đã đến gặp chúng với những bằng chứng rõ rệt (để răn chúng) nhưng chúng không tin. TA đã phạt những kẻ tội lỗi đúng như thế.');
INSERT INTO "hasan_abdulkarim" VALUES(10,14,'10:14','Rồi sau chúng, TA đã làm cho các ngươi thành những người nối nghiệp chúng trên trái đất để xem các ngươi cư xử ra sao.');
INSERT INTO "hasan_abdulkarim" VALUES(10,15,'10:15','Và khi những Lời Mặc Khải rõ ràng của TA được đọc ra cho họ, những kẻ không màng đến việc gặp gỡ lại TA ở đời sau đã nói: “Hãy mang đến cho bọn ta một Qur''an khác quyển này hoặc đổi quyển này đi.” Hãy bảo họ (hỡi Muhammad!): "Ta không thể tự ý thay đổi Nó (Qur''an) được. Ta chỉ tuân theo điều đã được mặc khải cho Ta. Ta sợ hình phạt vào Ngày vĩ đại sắp đến nếu Ta bất tuân Thượng Đế (Allah) của Ta".');
INSERT INTO "hasan_abdulkarim" VALUES(10,16,'10:16','Hãy bảo họ: “Nếu Allah muốn, Ta đã không đọc Qur''an này cho các ngươi làm gì, Ngài cũng không cho các ngươi biết về nó làm gì. Rõ ràng Ta đã sống trọn cuộc đời của Ta với các ngươi trước (khi) Nó (được ban xuống). Thế các ngươi không hiểu hay sao?');
INSERT INTO "hasan_abdulkarim" VALUES(10,17,'10:17','Thế còn ai gian ác hơn kẻ đặt điều nói dối rồi đổ thừa cho Allah hoặc phủ nhận những Lời Mặc Khải của Ngài? chắc chắn những kẻ tội lỗi sẽ không thành đạt.');
INSERT INTO "hasan_abdulkarim" VALUES(10,18,'10:18','Và họ thờ phụng ngoài Allah những kẻ đã không làm hại cũng chẳng làm lợi gì cho họ và họ nói: “Những vị này là những vị can thiệp giùm cho chúng tôi với Allah.” Hãy bảo họ (hỡi Muhammad!): “Phải chăng các người muốn báo cho Allah biết điều mà Ngài không biết trong các tầng trời và trái đất hay sao? Quang vinh và trong sạch thay Ngài! Ngài tối cao, vượt hẳn những điều mà họ đã tổ hợp với Ngài!');
INSERT INTO "hasan_abdulkarim" VALUES(10,19,'10:19','Và nhân loại chỉ là một cộng đồng(; về sau họ bất đồng ý kiến với nhau. Nếu không vì một Lời phán mà Thượng Đế của Ngươi đã tuyên bố trước đây thì những điều mà họ bất đồng đã được giải quyết rồi.');
INSERT INTO "hasan_abdulkarim" VALUES(10,20,'10:20','Và chúng nói: “Tại sao không có một Phép Lạ nào từ Thượng Đế của y được ban xuống cho y? Hãy bảo chúng: “Chắc chắn điều vô hình là của Allah. Thôi, các người hãy chờ đợi, Ta sẽ cùng chờ đợi với các người.”');
INSERT INTO "hasan_abdulkarim" VALUES(10,21,'10:21','Và khi TA (Allah) cho loài người nếm mùi khoan dung sau cơn hoạn nạn thì họ bày mưu chống lại các dấu hiệu của TA. Hãy bảo họ: “Allah mưu tính còn nhanh hơn các người". Quả thật, các Thiên Sứ (Thiên Thần) của TA đã ghi chép tất cả các kế hoạch mà các ngươi đang mưu định.');
INSERT INTO "hasan_abdulkarim" VALUES(10,22,'10:22','Ngài là Đấng đã giúp các ngươi du lịch trên đất liền và biển cả cho đến lúc các ngươi lên tàu (nó) đưa họ vượt trùng dương thuận buồm xuôi gió. Họ vui thích với chuyến đi. Đột nhiên một trận bão nổi lên và những làn sóng thần bao vây họ tứ phía. Họ nghĩ rằng họ sắp bị nhận chìm trong đó. Họ van vái Allah cứu mạng, chân thành với Ngài trong tôn giáo, thưa rằng: “Nếu Ngài giải cứu bầy tôi thoát khỏi cơn nguy khốn này, bầy tôi sẽ là những người biết ơn.”');
INSERT INTO "hasan_abdulkarim" VALUES(10,23,'10:23','Nhưng sau khi Ngài đã giải cứu họ thoát nạn thì chính những người đó lại bắt đầu làm loạn trên trái đất, bất chấp công lý và sự thật. Hỡi nhân loại! Việc làm loạn của các ngươi chỉ gây thiệt hại cho bản thân của các ngươi thôi. (Các ngươi có thể hưởng thụ) những lạc thú tạm bợ của đời sống trần tục này. Nhưng cuối cùng các ngươi sẽ được đưa về gặp TA trở lại rồi TA sẽ cho các ngươi biết về những điều các ngươi đã từng làm trên thế gian.');
INSERT INTO "hasan_abdulkarim" VALUES(10,24,'10:24','(Các ngươi yêu thích đời sống trần tục, nhưng) thật sự đời sống trần tục này chẳng khác nào hình ảnh của một cơn mưa mà TA đã gởi từ trên trời xuống; nó dung hợp với đất đai để làm mọc ra đủ loại hoa mầu cung cấp thực phẩm cho loài người và loài thú cho đến lúc mặt đất khoác lên mình những món trang sức xinh đẹp thì những người chủ của hoa mầu nghĩ rằng họ sẽ được toàn quyền định đoạt về tài sản của họ. Đột nhiên Mệnh Lệnh của TA được ban xuống tới tấp đêm và ngày cho các hoa mầu. TA tàn phá chúng toàn bộ làm như chúng đã không mọc xum xuê vào ngày hôm trước. TA đã giải thích những Lời Mặc Khải của TA như thế cho một đám người biết ngẫm nghĩ.');
INSERT INTO "hasan_abdulkarim" VALUES(10,25,'10:25','(Các ngươi bị mê hoặc bởi đời sống trần tục) trong lúc Allah kêu gọi các ngươi đến ngôi nhà Bằng An (thiên đàng) và hướng dẫn người nào Ngài muốn đến con đường Ngay Chính (của Islam).');
INSERT INTO "hasan_abdulkarim" VALUES(10,26,'10:26','Những ai làm tốt sẽ nhận phần thưởng tốt về việc họ làm, và có lẽ nhiều hơn (công lao của họ) nữa. Gương mặt của họ sẽ không sa sầm cũng không nhục nhã. Họ là những người Bạn của thiên đàng trong đó họ sẽ vào ở đời đời.');
INSERT INTO "hasan_abdulkarim" VALUES(10,27,'10:27','Còn những ai phạm tội thì sẽ chịu hình phạt ngang với tội mà họ đã phạm, điều nhục nhã sẽ bao trùm gương mặt của họ; họ sẽ không có ai bênh vực để thoát khỏi (hình phạt của) Allah. Gương mặt của họ nám đen giống như có một lớp màn u tối bao phủ. Họ sẽ là những người Bạn của hỏa ngục mà trong đó họ sẽ vào ở đời đời.');
INSERT INTO "hasan_abdulkarim" VALUES(10,28,'10:28','Vào một Ngày TA sẽ cho tập trung tất cả bọn chúng lại, rồi TA sẽ bảo những người dân đa thần: “Các ngươi và thần linh của các ngươi hiện ở đâu nay hãy ở đó.” Rồi TA sẽ tách biệt chúng ra. Và các thần linh của chúng sẽ bảo chúng: “Các ngươi đâu có thờ phụng bọn ta.”');
INSERT INTO "hasan_abdulkarim" VALUES(10,29,'10:29','“Một mình Allah đủ làm chứng giữa bọn ta và các ngươi bởi vì nếu các ngươi có thật sự thờ phụng bọn ta thì bọn ta vẫn không hay biết gì đến việc thờ phụng của các ngươi cả.”');
INSERT INTO "hasan_abdulkarim" VALUES(10,30,'10:30','(Vào ngày đó) mỗi linh hồn sẽ chứng kiến hậu quả của những điều mà nó đã gởi đi trước; họ sẽ được đưa về gặp lại Allah, Thượng Đế (Đấng Chủ Tể) thật sự của họ; và những điều mà họ đã từng bịa đặt ra trước đó sẽ bỏ họ đi biệt dạng.');
INSERT INTO "hasan_abdulkarim" VALUES(10,31,'10:31','Hãy hỏi họ: “Ai cấp dưỡng các người từ trên trời xuống dưới đất? Ai nắm quyền kiểm soát thính giác và thị giác của các người? Và ai đưa cái sống ra từ cái chết và ai đưa cái chết ra khỏi cái sống? Ai quản lý định đọat công việc (của vũ trụ)? Họ sẽ trả lời: “Allah!” Vậy hãy bảo họ: ‘Thế các người không sợ Ngài hay sao?”');
INSERT INTO "hasan_abdulkarim" VALUES(10,32,'10:32','Allah, Thượng Đế thực sự của các ngươi là như thế. Thế, phải chăng điều gì khác với sự thật là sự lầm lạc? Thế các ngươi lạc hướng đi đâu?');
INSERT INTO "hasan_abdulkarim" VALUES(10,33,'10:33','Lời nói của Thượng Đế của Ngươi về những kẻ dấy loạn và bất tuân đúng như thế. Rằng chúng sẽ không tin tưởng.');
INSERT INTO "hasan_abdulkarim" VALUES(10,34,'10:34','Hãy hỏi họ: “Trong số các thần linh của các người, ai có khả năng khởi sự tạo hóa rồi phục hồi nó“? Hãy bảo họ: “Allah khởi sự tạo hóa rồi phục hồi nó. Thế sao các ngươi lại lánh xa (sự thật)?”');
INSERT INTO "hasan_abdulkarim" VALUES(10,35,'10:35','Hãy hỏi họ: “Trong số các thần linh của các ngươi ai có khả năng hướng dẫn đến Chân Lý? Hãy bảo họ: “Allah là Đấng hướng dẫn đến Chân Lý.” Phải chăng Đấng hướng dẫn đến Chân Lý đáng được tuân theo hay một kẻ không hướng dẫn được ai trừ phi được hướng dẫn (đáng được tuân theo?) Các người có chuyện gì vậy? Các người xử lý sao vậy?');
INSERT INTO "hasan_abdulkarim" VALUES(10,36,'10:36','Và đa số bọn họ chỉ phỏng đoán. Chắc chắn sự phỏng đoán không bao giờ giúp họ đạt được Chân Lý về bất cứ điều gì. Quả thật, Allah biết rõ những điều họ làm.');
INSERT INTO "hasan_abdulkarim" VALUES(10,37,'10:37','Và (Kinh) Qur''an này không thể do ai khác ngoài Allah làm ra, nhưng Nó (Qur''an) xác nhận lại các điều (đã được mặc khải) trước nó và giải thích đầy đủ Kinh Sách do Thượng Đế của vũ trụ và muôn loài (ban xuống), không có gì phải ngờ vực cả.');
INSERT INTO "hasan_abdulkarim" VALUES(10,38,'10:38','Hoặc phải chăng họ nói Y (Muhammad) đã bịa đặt ra Nó (Qur''an)? Hãy bảo họ: “Nếu điều các người nói ra đúng sự thật thì hãy làm thử một chương Kinh giống như thế và gọi bất cứ ai ngoài Allah đến giúp?"');
INSERT INTO "hasan_abdulkarim" VALUES(10,39,'10:39','Không, họ phủ nhận những điều mà sự hiểu biết của họ không thể quán triệt nổi và họ chưa đạt được lời giải thích của Nó. Những kẻ trước họ đã phủ nhận sự thật giống như thế nhưng hãy xem kết cuộc của những kẻ làm điều sai quấy đã xảy ra thế nào.');
INSERT INTO "hasan_abdulkarim" VALUES(10,40,'10:40','Và trong bọn họ có người tin tưởng nơi Nó và có kẻ không tin nơi Nó. Và Thượng Đế của Ngươi (hỡi Sứ Giả!) biết rõ ai là những kẻ thối nát.');
INSERT INTO "hasan_abdulkarim" VALUES(10,41,'10:41','Và nếu họ cho rằng Ngươi (Muhammad) nói dối,thì hãy bảo họ: "Ta chịu trách nhiệm về công việc của Ta và các người chịu trách nhiệm về công việc của các người. Các người vô can với công việc của Ta và Ta vô can với công việc của các người.”');
INSERT INTO "hasan_abdulkarim" VALUES(10,42,'10:42','Và trong bọn họ, có kẻ (giả vờ) nghe Ngươi nói, nhưng có thể nào Ngươi làm cho người điếc nghe được hay chăng trong lúc họ là những kẻ không hiểu gì?');
INSERT INTO "hasan_abdulkarim" VALUES(10,43,'10:43','Và trong bọn họ, có kẻ (chăm chú) nhìn Ngươi, nhưng có thể nào Ngươi hướng dẫn một người mù đi đúng đường được chăng trong lúc họ là những kẻ không nhìn thấy gì?');
INSERT INTO "hasan_abdulkarim" VALUES(10,44,'10:44','Chắc chắn Allah sẽ không bất công đối với con người bất cứ điều gì; ngược lại con người tự bất công với chính bản thân mình.');
INSERT INTO "hasan_abdulkarim" VALUES(10,45,'10:45','(Bây giờ họ mê say với đời sống trần tục) nhưng vào Ngày mà Ngài sẽ tập trung họ lại, họ sẽ có cảm tưởng như họ đã ở trên trần gian khoảng một tiếng đồng hồ trong ngày. Họ sẽ nhận diện lẫn nhau. Chắc chắn sẽ thua thiệt đối với những ai đã từng phủ nhận việc gặp gỡ Allah ở đời sau bởi vì họ là những kẻ đã không được hướng dẫn.');
INSERT INTO "hasan_abdulkarim" VALUES(10,46,'10:46','Hoặc giả TA sẽ cho Ngươi thấy thực hiện một vài điều mà TA đã hứa với họ (trong thời gian Ngươi còn sống) hoặc giả TA sẽ bắt hồn của Ngươi ra đi (trước khi xảy ra điều đã hứa) thì trong trường hợp nào đi nữa họ vẫn phải trở lại gặp TA sau này. Rồi Allah sẽ là nhân chứng về những điều họ đã từng làm.');
INSERT INTO "hasan_abdulkarim" VALUES(10,47,'10:47','Và mỗi một cộng đồng đều có một Sứ Giả (được Allah phái đến) với họ; bởi thế khi Sứ Giả của họ đến gặp họ thì vấn đề (tranh chấp) giữa họ sẽ được giải quyết một cách công bằng và họ sẽ không bị đối xử bất công.');
INSERT INTO "hasan_abdulkarim" VALUES(10,48,'10:48','Và họ nói: “Chừng nào lời hứa (về sự trừng phạt) này sẽ xảy ra nếu quí vị (Sứ Giả) nói sự thật?”');
INSERT INTO "hasan_abdulkarim" VALUES(10,49,'10:49','Hãy bảo họ: "Ta không kiểm soát được điều hại và điều lợi ngay cho chính bản thân Ta ngoại trừ điều nào Allah muốn. Mỗi một cộng đồng đều có một thời hạn ấn định; khi thời hạn đã mãn thì họ sẽ không thể ở nán lại được một giờ khắc nào và cũng không thể giục nó đi sớm hơn được một giờ khắc nào cả.”');
INSERT INTO "hasan_abdulkarim" VALUES(10,50,'10:50','Hãy bảo họ: “Các người hãy nghĩ xem, nếu Ngài (Allah) giáng hình phạt của Ngài lên các người ban đêm hoặc ban ngày thì hình phạt nào sẽ là cái mà những kẻ tội lỗi sẽ mong cho chóng đến?”');
INSERT INTO "hasan_abdulkarim" VALUES(10,51,'10:51','Thế nếu hình phạt thực sự xảy đến cho các ngươi, các ngươi có chịu tin tưởng chay chăng? (Các ngươi sẽ hỏi lại) ''Bây giờ ư?'' trong lúc trước đây các ngươi đã giục nó cho chóng đến.');
INSERT INTO "hasan_abdulkarim" VALUES(10,52,'10:52','Rồi sẽ có lời bảo những kẻ làm điều sai quấy: “Hãy nếm hình phạt triền miên này!” Phải chăng các ngươi chỉ chịu hình phạt về những điều (tội lỗi) mà các ngươi đã phạm?');
INSERT INTO "hasan_abdulkarim" VALUES(10,53,'10:53','Và họ hỏi Ngươi để biết nó (sự trừng phạt) thực sự xảy ra hay không? Hãy bảo họ: “Vâng! Nhân danh Thượng Đế (Allah) của Ta, chắc chắn đó là sự thật. Và các người sẽ không thể thoát khỏi đặng!”');
INSERT INTO "hasan_abdulkarim" VALUES(10,54,'10:54','Và giá mỗi người (linh hồn) làm điều sai quấy đều có được tất cả những gì trên mặt đất dùng để chuộc tội (thì sẽ không được chấp thuận); và khi đối diện với hình phạt, họ sẽ ôm niềm hối hận (về tội đã làm); và vấn đề sẽ được giải quyết giữa họ một cách công bằng và họ sẽ không bị đối xử bất công.');
INSERT INTO "hasan_abdulkarim" VALUES(10,55,'10:55','Há tất cả những gì trong các tầng trời và trái đất không phải là của Allah hay sao? Há lời hứa của Allah không thật hay sao? Nhưng đa số bọn họ không biết (điều đó).');
INSERT INTO "hasan_abdulkarim" VALUES(10,56,'10:56','Ngài ban sự sống và gây cho chết và các ngươi sẽ được đưa về gặp Ngài trở lại.');
INSERT INTO "hasan_abdulkarim" VALUES(10,57,'10:57','Hỡi nhân loại! Một lời khuyên bảo tốt (Qur’an) từ Thượng Đế (Allah) của các ngươi đã đến với các ngươi; và là một phương thức chữa lành (những căn bệnh) trong lòng của các ngươi; vừa là một Chỉ Đạo và một Hồng Ân cho những người có đức tin.');
INSERT INTO "hasan_abdulkarim" VALUES(10,58,'10:58','Hãy bảo họ: “Vậy hãy để cho họ (những người có đức tin) vui hưởng thiên lộc của Allah và sự Khoan dung của Ngài như thế” Điều đó tốt hơn tài sản mà họ tích lũy.');
INSERT INTO "hasan_abdulkarim" VALUES(10,59,'10:59','Hãy bảo họ: “Các người đã xét lại hay chưa việc các người đã cấm và cho phép dùng các món vật mà Allah đã ban xuống cho các người làm thực phẩm?’’ Hãy hỏi họ: “Phải chăng Allah cho phép các người làm thế hay tự ý các người bịa đặt ra rồi đổ thừa cho Allah?”');
INSERT INTO "hasan_abdulkarim" VALUES(10,60,'10:60','Và những kẻ bịa đặt điều gian dối rồi đổ thừa cho Allah sẽ nghĩ gì về số phận của mình vào Ngày phục sinh? Quả thật, Allah Rất Mực Nhân Từ với loài người nhưng đa số thiên hạ không biết ơn.');
INSERT INTO "hasan_abdulkarim" VALUES(10,61,'10:61','Và không một hoạt động doanh thương nào mà Ngươi làm và không một đoạn Kinh Qur''an nào mà Ngươi xướng đọc cũng như không một hành động nào mà các ngươi (nhân loại) làm mà lại không nằm dưới sự chứng kiến của TA khi các ngươi miệt mài trong đó, bởi vì không có gì có thể giấu giếm khỏi được Thượng Đế (Allah) của Ngươi (Muhammad) dù cho đó là sức nặng của một hạt nguyên tử nằm dưới đất hay ở trên trời đi nữa và không có cái gì nhỏ hoặc lớn hơn cái đó mà lại không được ghi chép trong một quyển sổ (Định Mệnh) rõ ràng.');
INSERT INTO "hasan_abdulkarim" VALUES(10,62,'10:62','Ô này! Chắc chắn những người Wali( của Allah sẽ không lo sợ cũng sẽ không buồn phiền:');
INSERT INTO "hasan_abdulkarim" VALUES(10,63,'10:63','Họ là những ai tin tưởng nơi Ngài và hằng sợ Ngài.');
INSERT INTO "hasan_abdulkarim" VALUES(10,64,'10:64','Họ sẽ nhận tin mừng ở đời này và ở đời sau. (Bởi vì) chẳng có gì thay đổi trong các Lời Phán của Allah. Đó là một sự thành tựu vĩ đại.');
INSERT INTO "hasan_abdulkarim" VALUES(10,65,'10:65','(Hỡi Muhammad!) Chớ để lời nói của họ làm Ngươi buồn; bởi vì chắc chắn tất cả quyền hành và vinh dự đều thuộc về Allah cả. Ngài là Đấng Hằng Nghe và Hằng Biết.');
INSERT INTO "hasan_abdulkarim" VALUES(10,66,'10:66','Ô này! Quả thật, bất cứ ai ở trên trời và ai ở dưới đất đều là (tạo vật) của Allah cả. Và những kẻ cầu nguyện các thần linh ngoài Allah tuân theo gì vậy? Quả thật, họ chỉ tuân theo điều phỏng chừng và chỉ bịa đặt điều gian dối.');
INSERT INTO "hasan_abdulkarim" VALUES(10,67,'10:67','Ngài (Allah) là Đấng làm ra ban đêm cho các ngươi để nghỉ ngơi và ban ngày để nhìn thấy. Quả thật, trong đó là các dấu hiệu cho một đám người biết nghe (lời Ngài).');
INSERT INTO "hasan_abdulkarim" VALUES(10,68,'10:68','Họ nói: “Allah đã có một đứa con trai.” Thật quang vinh và trong sạch thay Ngài! Ngài giàu Có (Tự Đầy đủ). Mọi vật trong các tầng trời và dưới đất đều là (tạo vật) của Ngài. Các ngươi chẳng có gì bảo đảm cho điều (bịa đặt) này. Phải chăng các ngươi đã nói (bậy) cho Allah điều mà các ngươi không biết?');
INSERT INTO "hasan_abdulkarim" VALUES(10,69,'10:69','Hãy bảo họ: “Chắc chắn những ai bịa đặt điều nói dối rồi đổ thừa cho Allah sẽ không bao giờ thành đạt.”');
INSERT INTO "hasan_abdulkarim" VALUES(10,70,'10:70','(Mà chỉ là) một sự hưởng thụ ngắn ngủi nơi trần gian, rồi họ sẽ được đưa trở về gặp lại TA rồi TA sẽ cho họ nếm hình phạt khủng khiếp về tội họ đã từng phủ nhận (Allah và các dấu hiệu của Ngài).');
INSERT INTO "hasan_abdulkarim" VALUES(10,71,'10:71','Hãy đọc cho họ (nghe) câu chuyện của Nuh khi Y bảo người dân của Y: "Này hỡi dân ta! Nếu các người cảm thấy khó chịu về địa vị của ta cũng như việc ta nhắc nhở các người về những dấu hiệu của Allah thì ta hoàn toàn phó thác cho Allah. Thế thì các người hãy cùng hợp tác với các thần linh của các người mà sắp đặt công việc của các người rồi chớ để cho kế hoạch của các người có điều nào sơ sót rồi hãy (dùng nó mà) xử lý ta và chớ để cho ta rảnh tay".');
INSERT INTO "hasan_abdulkarim" VALUES(10,72,'10:72','"Nhưng nếu các người quay bỏ đi thì (nên biết rằng) Ta đâu có đòi hỏi các người tưởng thưởng Ta; bởi vì phần thưởng của Ta ở nơi Allah và Ta nhận lệnh phải là một người Muslim (thần phục Ngài).”');
INSERT INTO "hasan_abdulkarim" VALUES(10,73,'10:73','Nhưng họ cho rằng Người (Nuh) nói dối. Sau đó TA đã giải cứu Y và tín đồ theo Y trên một chiếc tàu và làm cho họ thành những người nối nghiệp (trên trái đất) và TA nhận chìm những kẻ phủ nhận những dấu hiệu của TA. Bởi thế, hãy nhìn xem kết cuộc của những kẻ đã được cảnh cáo (nhưng không chịu hối cải) như thế nào.');
INSERT INTO "hasan_abdulkarim" VALUES(10,74,'10:74','Rồi sau Người (Nuh) TA đã cử nhiều Sứ Giả đến cho người dân của họ mang theo những bằng chứng rõ rệt nhưng họ không tin tưởng nơi những điều mà họ đã từng phủ nhận trước đó. TA đã niêm kín tấm lòng của những kẻ phạm tội như thế.');
INSERT INTO "hasan_abdulkarim" VALUES(10,75,'10:75','Rồi sau họ (các Sứ Giả tiền nhiệm), TA đã cử Musa và Harun đến gặp Fir-''awn và những tên tù trưởng của y mang theo những bằng chứng rõ rệt của TA. Nhưng chúng tỏ ra ngạo mạn và là một đám người tội lỗi.');
INSERT INTO "hasan_abdulkarim" VALUES(10,76,'10:76','Nhưng khi sự Thật từ TA đến với chúng, chúng nói: “Chắc chắn đây là một trò ảo thuật hiển hiện.”');
INSERT INTO "hasan_abdulkarim" VALUES(10,77,'10:77','Musa bảo: “Các người đã nói về sự Thật như thế hay sao khi nó đến với các người? Đây là một trò ảo thuật ư? Bởi vì những nhà ảo thuật không hề thành đạt."');
INSERT INTO "hasan_abdulkarim" VALUES(10,78,'10:78','Chúng bảo (Musa): “Phải chăng nhà ngươi đến gặp bọn ta để làm bọn ta từ bỏ những điều (đường lối) mà bọn ta thấy ông bà cha mẹ của bọn ta đang theo hầu hai anh em nhà ngươi có thể làm bá chủ trong lãnh thổ ? Và bọn ta sẽ không tin tưởng nơi hai ngươi!”');
INSERT INTO "hasan_abdulkarim" VALUES(10,79,'10:79','Và Fir-''awn nói (với các chư thần): “Hãy triệu đến cho ta tất cả các nhà phù thủy tài giỏi.”');
INSERT INTO "hasan_abdulkarim" VALUES(10,80,'10:80','Bởi thế, khi các nhà phù thủy đến trình diện, Musa bảo họ: “Quí vị hãy ném xuống đất bất cứ bửu bối nào mà quí vị muốn ném.”');
INSERT INTO "hasan_abdulkarim" VALUES(10,81,'10:81','Tiếp đó, khi họ ném xuống, Musa bảo: “Cái mà quí vị mang đến là trò ảo thuật. Chắc chắn Allah sẽ làm cho nó thành vô dụng và chắc chắn Allah không để cho việc làm của những kẻ thối nát được thành công.”');
INSERT INTO "hasan_abdulkarim" VALUES(10,82,'10:82','Và Allah chứng minh và thiết lập Chân Lý bằng Lời Phán của Ngài mặc dầu những kẻ tội lỗi ghét điều đó.');
INSERT INTO "hasan_abdulkarim" VALUES(10,83,'10:83','Nhưng vì lý do sợ Fir-''awn và những vị tù trưởng của y sẽ hành hạ họ nên không ai tin tưởng nơi Musa ngoại trừ một số con cái của người dân của Y. Và quả thật, Fir-''awn rất quyền thế trong lãnh thổ và là một kẻ vượt quá mức tội ác.');
INSERT INTO "hasan_abdulkarim" VALUES(10,84,'10:84','Và Musa bảo: “Này hỡi dân ta! Nếu các người thực sự tin tưởng nơi Allah thì hãy phó thác cho Ngài nếu các người là những người Muslim (thần phục Ngài).');
INSERT INTO "hasan_abdulkarim" VALUES(10,85,'10:85','Bởi thế, họ thưa: “Chúng tôi phó thác cho Allah. Lạy Thượng Đế của bầy tôi! Xin Ngài đừng biến chúng tôi thành một phương tiện thử thách cho những kẻ sai quấy.”');
INSERT INTO "hasan_abdulkarim" VALUES(10,86,'10:86','“Và xin Ngài hãy lấy lòng Khoan Dung mà giải thoát bầy tôi ra khỏi đám người không tin này.”');
INSERT INTO "hasan_abdulkarim" VALUES(10,87,'10:87','Và TA đã mặc khải cho Musa và (Harun) người em của Y rằng: Hãy dựng nhà cho người dân của hai ngươi tại Ai Cập và hãy dùng nhà của các ngươi làm Qiblah (nơi thờ phượng) và hãy dâng lễ nguyện Salah và báo tin mừng cho những người tin tưởng.');
INSERT INTO "hasan_abdulkarim" VALUES(10,88,'10:88','Và Musa (cầu nguyện) thưa: “Lạy Thượng Đế của bầy tôi! Ngài đã ban cho Fir-''awn và các tù trưởng của y sự huy hoàng và sự giàu sang ở đời này, bởi thế y đã dắt thiên hạ đi lạc khỏi con đường của Ngài. Lạy Thượng Đế của bầy tôi! Xin Ngài hãy tàn phá tài sản của chúng và làm cho tấm lòng của chúng chai cứng lại. Bởi thế, chúng sẽ tiếp tục không tin tưởng cho đến lúc chúng sẽ chứng kiến hình phạt đau đớn (được dành cho chúng).”');
INSERT INTO "hasan_abdulkarim" VALUES(10,89,'10:89','(Allah) phán: Lời cầu xin của hai (anh em) nhà ngươi được (TA) chấp nhận. Do đó, hãy chính trực và chớ theo con đường của những kẻ không biết gì.');
INSERT INTO "hasan_abdulkarim" VALUES(10,90,'10:90','Và TA đã đưa con cháu của Israel vượt qua biển (Hồng hải). Fir-''awn và quân lính của y đuổi theo bắt họ đầy căm phẫn và hận thù cho đến khi y bị nhận chìm dưới nước thì y mới nói: "Tôi tin tưởng quả thật là Ngài; Không có Thượng Đế nào ngoài Đấng mà con cháu của Israel tin tưởng. Và tôi là một người Muslim thần phục Ngài."');
INSERT INTO "hasan_abdulkarim" VALUES(10,91,'10:91','(Allah phán): À bây giờ nhà ngươi mới tin. Trước đây không lâu nhà ngươi hãy còn làm loạn và nhà ngươi là một kẻ thối nát.');
INSERT INTO "hasan_abdulkarim" VALUES(10,92,'10:92','Ngày này TA giữ thân xác của ngươi lại để làm một dấu hiệu cho những ai nối nghiệp nhà ngươi. Và quả thật đa số thiên hạ vẫn còn lơ là trước những dấu hiệu của TA.');
INSERT INTO "hasan_abdulkarim" VALUES(10,93,'10:93','Và TA đã định cư con cháu của Israel tại một nơi tốt đẹp và an toàn và cung cấp cho họ lương thực tốt, bởi thế họ không bất đồng ý kiến với nhau (về điều gì); cho đến khi họ tiếp thu được sự hiểu biết (họ mới đâm ra chia rẽ nhau.) Chắc chắn Allah sẽ giải quyết mối bất hòa giữa họ vào Ngày phục sinh về những điều mà họ thường tranh chấp.');
INSERT INTO "hasan_abdulkarim" VALUES(10,94,'10:94','(Hỡi Muhammad!) Nếu Ngươi có ý nghi ngờ về điều (mặc khải) mà TA đã ban xuống cho Ngươi, thì hãy hỏi điều này nơi những kẻ đã đọc Kinh Sách trước Ngươi. Chắc chắn đó là sự Thật từ Thượng Đế của Ngươi đã được mang đến cho Ngươi. Vậy chớ là một kẻ sinh lòng nghi ngờ.');
INSERT INTO "hasan_abdulkarim" VALUES(10,95,'10:95','Và cũng chớ là một trong những kẻ phủ nhận những Lời Mặc Khải của Allah bởi vì làm thế Ngươi sẽ trở thành một kẻ thu thiệt.');
INSERT INTO "hasan_abdulkarim" VALUES(10,96,'10:96','Quả thật, những ai mà Lời Phán của Thượng Đế của Ngươi (hỡi Muhammad) đã xác nhận đúng, sẽ nhất định không tin tưởng,');
INSERT INTO "hasan_abdulkarim" VALUES(10,97,'10:97','Cho dù từng Phép lạ được mang đến trình bày cho họ thấy (họ vẫn không tin) mãi cho đến khi họ đối diện với một hình phạt đau đớn.');
INSERT INTO "hasan_abdulkarim" VALUES(10,98,'10:98','Bởi thế, giá có một thị trấn nào đó tin tưởng thì đức tin của nó sẽ giúp ích cho nó biết mấy chẳng hạn như người dân của Yunus. Khi họ tin tưởng, TA đã xóa hình phạt nhục nhã đi khỏi họ ở đời sống trần tục này và cho họ tiếp tục hưởng thụ cuộc đời đến một thời hạn ấn định.');
INSERT INTO "hasan_abdulkarim" VALUES(10,99,'10:99','Và giá Thượng Đế của Ngươi (Muhammad) muốn thì từng người một sống trên trái đất này, tất cả, sẽ có đức tin. Thế phải chăng Ngươi cưỡng ép người ta ngược với ý muốn của họ cho đến khi họ tin tưởng hay sao?');
INSERT INTO "hasan_abdulkarim" VALUES(10,100,'10:100','Và không một người nào có đức tin trừ phi Allah chấp thuận cho y. Và Ngài sẽ đặt sự ô uế lên những kẻ không hiểu gì.');
INSERT INTO "hasan_abdulkarim" VALUES(10,101,'10:101','Hãy bảo (họ): “Hãy quan sát những vật trong các tầng trời và dưới đất.” Và không một dấu hiệu cũng không một người Báo trước nào có thể giúp ích được cho một đám người không có đức tin.');
INSERT INTO "hasan_abdulkarim" VALUES(10,102,'10:102','Thế phải chăng họ mong đợi (những ngày) giống như những ngày (xấu xa) của những kẻ đã qua đời trước họ? Hãy bảo (họ): “Thế các người hãy chờ đợi, ta sẽ cùng chờ đợi với các người.”');
INSERT INTO "hasan_abdulkarim" VALUES(10,103,'10:103','Rồi TA giải cứu các Sứ Giả của TA cùng với những ai có đức tin. TA có bổn phận phải giải cứu những người có đức tin đúng như thế.');
INSERT INTO "hasan_abdulkarim" VALUES(10,104,'10:104','Hãy bảo người dân: “Hỡi nhân loại! Nếu các người nghi ngờ về tôn giáo của Ta thì (nên biết rằng) ngoài Allah, Ta không thờ phụng những kẻ mà các người thờ phụng. Ngược lại, Ta thờ phụng Allah, Đấng sẽ làm cho các người chết và ta nhận lệnh phải trở thành một người tin tưởng."');
INSERT INTO "hasan_abdulkarim" VALUES(10,105,'10:105','Và (Ta nhận được lệnh): “Hãy hướng mặt Ngươi về tôn giáo chính thống (chỉ tôn thờ riêng Allah) và chớ trở thành một người thờ đa thần".');
INSERT INTO "hasan_abdulkarim" VALUES(10,106,'10:106','“Và chớ cầu nguyện ngoài Allah những kẻ không mang lợi cũng chẳng gây hại được Ngươi; nếu làm thế thì chắc chắn Ngươi sẽ là một kẻ sai phạm".');
INSERT INTO "hasan_abdulkarim" VALUES(10,107,'10:107','Và nếu Allah làm cho Ngươi đau ốm thì duy chỉ Ngài chữa lành nó đặng; và nếu Ngài ban điều tốt nào cho Ngươi thì không ai có thể giữ thiên lộc của Ngài lại đặng. Ngài ban nó cho người nào Ngài muốn trong số bầy tôi của Ngài. Và Ngài Hằng Tha Thứ, Rất Mực Khoan Dung.');
INSERT INTO "hasan_abdulkarim" VALUES(10,108,'10:108','Hãy bảo (người dân): “Hỡi nhân loại! Bây giờ Chân Lý từ Thượng Đế của các người đã đến với các người. Do đó, ai theo Chính Đạo thì việc theo đó chỉ có lợi cho bản thân mình; còn ai đi lạc thì việc lạc đó chỉ bất lợi cho bản thân mình. Ta không là người thọ lãnh (trách nhiệm) cho các người.”');
INSERT INTO "hasan_abdulkarim" VALUES(10,109,'10:109','Và hãy tuân theo những điều đã được mặc khải cho Ngươi và hãy kiên nhẫn chịu đựng cho đến khi Allah phán xử. Bởi vì Ngài là Đấng Xét Xử Ưu Việt.');
INSERT INTO "hasan_abdulkarim" VALUES(11,1,'11:1','Alif. Lam. Ra. (Đây là) một Kinh Sách gồm những Câu cơ bản có ý nghĩa rõ ràng tiếp đó được giải thích chi tiết bởi Đấng Sáng Suốt, Đấng Am Tường.');
INSERT INTO "hasan_abdulkarim" VALUES(11,2,'11:2','(Kinh Sách này dạy) các ngươi không được thờ phụng ai ngoài Allah cả. Quả thật, Ta (Muhammad) được Ngài (Allah) cử phái đến với các ngươi như một người Báo Trước và người mang Tin Mừng.');
INSERT INTO "hasan_abdulkarim" VALUES(11,3,'11:3','Và hãy cầu xin Thượng Đế (Allah) của các ngươi tha thứ và quay về sám hối với Ngài để may ra Ngài cho các ngươi hưởng thụ một đời sống vui tươi cho đến hết tuổi thọ đã được qui định cho các ngươi và Ngài sẽ ban thiên lộc của Ngài cho tất cả các ngươi hưởng thiên lộc. Nhưng nếu các ngươi quay bỏ đi thì chắc chắn Ta sợ giùm cho các ngươi về sự trừng phạt vào Ngày (Phán Xử) Vĩ Đại.');
INSERT INTO "hasan_abdulkarim" VALUES(11,4,'11:4','(Bởi vì) các ngươi sẽ trở về gặp Ngài trở lại và Ngài sẽ toàn quyền định đoạt về tất cả mọi việc.');
INSERT INTO "hasan_abdulkarim" VALUES(11,5,'11:5','Nhưng này! Họ khép kín cõi lòng của họ để mong lẫn trốn khỏi Ngài. Nhưng không! Dẫu họ có lấy áo quần che kín thân mình của họ lại thì Ngài vẫn biết điều họ giấu giếm và điều họ phơi bày. Chắc chắn Ngài biết hết những điều thầm kín nhất trong lòng họ.');
INSERT INTO "hasan_abdulkarim" VALUES(11,6,'11:6','Và không một sinh vật nào sống trên trái đất mà nguồn lương thực lại không do Allah cung cấp. Ngài biết nơi sống và nơi tạm ngụ của nó. Tất cả đều được ghi chép trong một quyển sổ (Định mệnh) rõ ràng.');
INSERT INTO "hasan_abdulkarim" VALUES(11,7,'11:7','Và Ngài (Allah) là Đấng đã tạo hóa các tầng trời và trái đất trong sáu Ngày và Ngai Vương (''Arsh) của Ngài nằm bên trên (mặt) nước để Ngài có thể thử thách các ngươi xem ai trong các ngươi là người tốt nhất trong việc làm (của mình). Và nếu Ngươi (Muhammad) có bảo họ: “Chắc chắn các ngươi sẽ được phục sinh trở lại sau khi chết” thì chắc chắn những kẻ không có đức tin sẽ đáp: “Điều này rõ ràng chỉ là một trò ảo thuật công khai (chứ làm sao có đặng!)”');
INSERT INTO "hasan_abdulkarim" VALUES(11,8,'11:8','Và nếu TA cho dời việc trừng phạt họ đến một thời hạn qui định thì chắc chắn họ sẽ nói, “Lý do gì giữ nó lại?” Ô này! Ngày mà hình phạt sẽ xảy đến cho họ, nhất định họ sẽ không thoát khỏi tai họa và họ sẽ bị bao vây bởi những điều mà họ đã từng chế giễu!');
INSERT INTO "hasan_abdulkarim" VALUES(11,9,'11:9','Và nếu TA đích thân cho con người nếm mùi Khoan dung rồi tước mất nó khỏi y thì chắc chắn y sẽ thất vọng và phụ ơn.');
INSERT INTO "hasan_abdulkarim" VALUES(11,10,'11:10','Và nếu TA cho y nếm (hưởng) ân huệ sau khi đã chịu tai họa thì y sẽ nói: “Mọi điều xui xẻo đã đi mất rồi.” Chắc chắn y sẽ vui mừng và tự đắc.');
INSERT INTO "hasan_abdulkarim" VALUES(11,11,'11:11','Ngược lại, những ai tỏ lòng kiên nhẫn và làm việc thiện thì sẽ không thế. Họ là những người sẽ được tha thứ và sẽ được ân thưởng rất lớn.');
INSERT INTO "hasan_abdulkarim" VALUES(11,12,'11:12','Có thể Ngươi (Nabi) xuôi lòng mà bỏ mất một phần của những điều đã được mặc khải cho Ngươi và lòng của Ngươi se lại vì lời chúng nói: “Tại sao không có một kho tàng nào được ban xuống cho y và tại sao không có một Thiên Thần nào được cử xuống cùng với y?”. Thật ra, Ngươi chỉ là một người Báo Trước trong lúc Allah là Đấng Thọ Lãnh sắp đặt tất cả mọi việc.');
INSERT INTO "hasan_abdulkarim" VALUES(11,13,'11:13','Hoặc chúng cũng có thể nói: “Y đã bịa đặt ra Nó (Qur''an)”. Hãy bảo (chúng): “Vậy các ngươi hãy mang đến mười chương giống y như Nó xem, và nếu được, hãy gọi bất cứ ai ngoài Allah đến giúp nếu các ngươi nói đúng sự thật!”.');
INSERT INTO "hasan_abdulkarim" VALUES(11,14,'11:14','Bởi thế, nếu chúng (các thần linh) không đáp ứng lời cầu khẩn của các ngươi thì nên biết Nó (Qur’an) được ban xuống (đầy đủ) từ Kiến thức của Allah và rằng không có Thượng Đế nào khác duy chỉ Ngài. Thế tại sao các ngươi không chịu thần phục (Allah và thành người Muslim?).');
INSERT INTO "hasan_abdulkarim" VALUES(11,15,'11:15','Ai muốn đời sống trần tục này với vẽ hào nhoáng của nó thì TA sẽ trả đầy đủ phần công lao của họ nơi đó và họ sẽ không bị cắt giảm một tí nào nơi đó.');
INSERT INTO "hasan_abdulkarim" VALUES(11,16,'11:16','Họ là những kẻ sẽ không hưởng được gì ở đời sau ngoài hỏa ngục); (lúc đó họ sẽ nhận thấy) công trình của họ nơi (trần gian) sẽ tiêu tan và công việc mà họ đã từng làm nơi đó sẽ trở thành vô nghĩa.');
INSERT INTO "hasan_abdulkarim" VALUES(11,17,'11:17','Thế ai giống với người đã từng dựa vào một bằng chứng rõ rệt từ Thượng Đế (Allah) của y và được một nhân chứng do Ngài cử đến đọc Nó (Qur’an cho y) và trước y đã có Kinh Sách của Musa làm một Chỉ Hướng và một Hồng Ân? Họ (Muslim) là những người tin nơi nó. Còn người nào thuộc các giáo phái không tin nơi nó thì hỏa ngục sẽ là nơi hứa hẹn của y. Bởi thế, Ngươi (Nabi) chớ sinh nghi về Nó (Qur’an) bởi vì quả thật Nó là Chân Lý do Thượng Đế của Ngươi ban xuống nhưng đa số nhân loại không tin.');
INSERT INTO "hasan_abdulkarim" VALUES(11,18,'11:18','Và còn ai sai phạm hơn kẻ đã đặt điều nói dối rồi đổ thừa cho Allah! Họ sẽ được mời đến trình diện trước Thượng Đế của họ và các nhân chứng sẽ bảo: “Đây là những kẻ đã nói dối rồi đổ thừa cho Allah". Quả thật, Allah nguyền rủa những kẻ làm điều sai quấy.');
INSERT INTO "hasan_abdulkarim" VALUES(11,19,'11:19','Những ai đã cản trở (người khác) không cho theo con đường của Allah và còn tìm cách bẻ cong nó và phủ nhận đời sau.');
INSERT INTO "hasan_abdulkarim" VALUES(11,20,'11:20','Họ sẽ không chạy trốn được bất cứ nơi nào trên trái đất và sẽ không được ai che chở hầu thoát khỏi (hình phạt của) Allah. Họ sẽ bị trừng phạt gấp đôi. Họ sẽ không thể nghe cũng sẽ không thấy được (Chân Lý).');
INSERT INTO "hasan_abdulkarim" VALUES(11,21,'11:21','Họ là những kẻ đã đánh mất linh hồn của mình và những điều mà họ đã từng bịa đặt sẽ bỏ đi biệt dạng.');
INSERT INTO "hasan_abdulkarim" VALUES(11,22,'11:22','Không một chút nghi ngờ nào, họ là những kẻ sẽ mất mát nhất ở đời sau.');
INSERT INTO "hasan_abdulkarim" VALUES(11,23,'11:23','Quả thật, những ai có đức tin và làm việc thiện và hạ mình khiêm tốn trước Allah thì sẽ là những người Bạn của thiên đàng nơi mà họ sẽ vào đó ở đời đời.');
INSERT INTO "hasan_abdulkarim" VALUES(11,24,'11:24','Hình ảnh về hai loại người này có thể so sánh với một bên gồm người mù và người điếc và bên kia gồm người thấy và người nghe được. Họ bằng nhau chăng khi mang ra so sánh? Thế các ngươi không lưu ý (về điều đó hay sao?).');
INSERT INTO "hasan_abdulkarim" VALUES(11,25,'11:25','Và quả thật TA đã cử Nuh (Noah) đến cho người dân của Y. (Y bảo họ): “Ta là một người Báo Trước (được phái) đến với các ngươi.');
INSERT INTO "hasan_abdulkarim" VALUES(11,26,'11:26','(Ta báo cho biết): các ngươi chỉ nên thờ phụng riêng Allah thôi bởi vì Ta sợ giùm cho các ngươi về sự Trừng Phạt vào một Ngày đau đớn.');
INSERT INTO "hasan_abdulkarim" VALUES(11,27,'11:27','Nhưng các vị cầm đầu trong đám người dân của Y đáp: “Bọn ta thấy Ngươi chỉ là một người phàm như bọn ta và bọn ta cũng nhận thấy chỉ những kẻ tồi tệ nhất trong bọn ta mới mù quáng theo Ngươi và bọn ngươi chẳng có gì trội hơn bọn ta cả. Không, bọn ta nghĩ, bọn ngươi là những kẻ nói dối.”');
INSERT INTO "hasan_abdulkarim" VALUES(11,28,'11:28','(Nuh) bảo: “Hỡi dân ta! Há các người có nhận thấy rằng nếu Ta đã dựa vào một bằng chứng rõ rệt từ Thượng Đế của Ta và đựơc Ngài ban cho Hồng Ân nhưng các người không nhìn thấy nó (Hồng Ân) thì có thể nào bọn ta bắt ép các người châp nhận nó trong lúc các người không thích?".');
INSERT INTO "hasan_abdulkarim" VALUES(11,29,'11:29','“Và hỡi dân Ta! Ta không đòi hỏi tài sản của các người về việc (truyền giáo) này. Phần thưởng của Ta chỉ do Allah ban cấp và ta không thể xua đuổi những người có đức tin đi nơi khác bởi vì quả thật họ sẽ trở về gặp Thượng Đế của họ lại. Ngược lại, Ta thấy các người mới thực sự là một đám người ngu muội!".');
INSERT INTO "hasan_abdulkarim" VALUES(11,30,'11:30','“Và hỡi dân Ta! Ai sẽ giúp Ta tránh được (hình phạt của) Allah nếu Ta xua đuổi họ? Thế các ngươi không lưu ý đến điều đó hay sao?"');
INSERT INTO "hasan_abdulkarim" VALUES(11,31,'11:31','“Và Ta đã không bảo các người rằng Ta có được các kho tàng của Allah; và Ta cũng không biết được điều vô hình; và Ta đã không bảo mình là một Thiên Thần; và Ta đã không nói về những ai mà cặp mắt các người đã khinh rẽ rằng Allah sẽ không bao giờ ban một điều tốt nào cho họ. Allah biết rõ nhất điều nằm trong lòng của họ. Nếu Ta đã thực sự tuyên bố các điều đó thì quả thật Ta là một kẻ làm điều sai quấy.”');
INSERT INTO "hasan_abdulkarim" VALUES(11,32,'11:32','(Người dân) đáp: “Này hỡi Nuh! Ngươi đã tranh luận với bọn ta khá nhiều. Nào, hãy mang điều (hình phạt) mà ngươi dùng để hăm dọa bọn ta đến cho bọn ta thấy nếu ngươi là một người nói thật.”');
INSERT INTO "hasan_abdulkarim" VALUES(11,33,'11:33','(Nuh) bảo: "Chỉ Allah mang nó (hình phạt) đến cho các người thôi, nếu Ngài muốn và (lúc đó) các người sẽ không tài nào chạy thoát được!"');
INSERT INTO "hasan_abdulkarim" VALUES(11,34,'11:34','“Và nếu Ta muốn khuyên bảo các người điều tốt lành thì nó chẳng giúp ích gì cho các người một khi Allah muốn bỏ mặc các người lạc lối. Ngài là Thượng Đế của các người và các người sẽ được đưa về gặp Ngài trở lại (ở đời sau).”');
INSERT INTO "hasan_abdulkarim" VALUES(11,35,'11:35','Hoặc họ (người Quraish) cũng có thể nói: “Y (Muhammad) đã bịa đặt ra Nó (Qur’an).” Hãy bảo họ: “Nếu Ta đã bịa đặt nó thì Ta sẽ chịu tội về việc đó và Ta vô can về những điều tội lỗi mà các ngươi đã phạm.”');
INSERT INTO "hasan_abdulkarim" VALUES(11,36,'11:36','Và Nuh nhận được Lời Mặc Khải (của Allah): "Không ai trong đám người dân của Ngươi sẽ tin tưởng ngoại trừ những người đã có đức tin chắc chắn. Vậy chớ buồn rầu về những điều chúng đã từng làm."');
INSERT INTO "hasan_abdulkarim" VALUES(11,37,'11:37','“Và hãy đóng một chiếc tàu dưới mắt quan sát của TA và tuân theo điều mặc khải của TA và chớ khiếu nại với TA giùm cho những kẻ làm điều sai quấy. Chắc chắn chúng sẽ bị nhận chết dưới nước (lụt).”');
INSERT INTO "hasan_abdulkarim" VALUES(11,38,'11:38','Và (Nuh) bắt đầu đóng chiếc tàu. Và mỗi lần đi ngang qua chỗ của Y, các vị cầm đầu trong đám người dân của Y, cười chế nhạo Y. (Nuh) bảo: “Nếu các ngươi chế nhạo bọn ta thì bọn ta sẽ chế nhạo các ngươi lại giống như thế.”');
INSERT INTO "hasan_abdulkarim" VALUES(11,39,'11:39','“Rồi đây các ngươi sẽ sớm biết ai là kẻ sẽ nhận hình phạt nhục nhã bao trùm lấy y và sẽ hứng chịu một sự trừng phạt dai dẳng.”');
INSERT INTO "hasan_abdulkarim" VALUES(11,40,'11:40','(Ngày tháng trôi qua) cho đến khi Mệnh Lệnh của TA được ban hành. Tức thời nước mạch trào lên. TA phán: “Hãy đưa lên tàu từng cặp (thú vật) đủ loại và cả gia đình của ngươi và những ai có đức tin ngoại trừ kẻ mà lệnh xử lý đã được ban hành." Nhưng chỉ một số ít có đức tin cùng với Y.');
INSERT INTO "hasan_abdulkarim" VALUES(11,41,'11:41','Và (Nuh) bảo: “Hãy lên tàu, nhân danh Allah, dù nó trôi hay nó đậu. Chắc chắn, Đức Thượng Đế của ta Hằng Tha Thứ, Rất Mực Khoan Dung.”');
INSERT INTO "hasan_abdulkarim" VALUES(11,42,'11:42','Và chiếc tàu chở họ trôi đi giữa các làn sóng to như những quả núi; và Nuh lớn tiếng gọi đứa con trai của mình đứng ở đằng xa, bảo: “Này con trai! Hãy lên tàu cùng với cha. Chớ nhập chung với những kẻ không tin tưởng.”');
INSERT INTO "hasan_abdulkarim" VALUES(11,43,'11:43','(Đứa con trai) đáp: “Con sẽ chạy lên núi, nó sẽ cứu con thoát khỏi nước lụt.” (Nuh) bảo: “Ngày nay không có gì có thể cứu giải con khỏi Mệnh Lệnh của Allah ngoại trừ người nào được Ngài khoan dung.” Và những ngọn sóng ùa đến chia lìa hai cha con họ. Do đó, đứa con đã bị chết chìm (dưới nước lụt).');
INSERT INTO "hasan_abdulkarim" VALUES(11,44,'11:44','Và (Allah) phán: “Hỡi đất! Hãy rút hết nước của ngươi. Hỡi bầu trời! Hãy ngưng mưa của ngươi.” Và nước lụt hạ xuống. Và Mệnh Lệnh của Allah đã được thi hành hoàn tất. Và nó (chiếc tàu) đậu trên ngọn núi Judi. Và có lời phán: “Đi đời lũ sai quấy!”');
INSERT INTO "hasan_abdulkarim" VALUES(11,45,'11:45','Và Nuh cầu nguyện Thượng Đế của Y, thưa: “Lạy Thượng Đế của bề tôi! Quả thật, đứa con trai của bề tôi là người của gia đình bề tôi và chắc chắn Lời hứa của Ngài là sự Thật và Ngài là Đấng Xét Xử Công Minh nhất của những vị xét xử.”');
INSERT INTO "hasan_abdulkarim" VALUES(11,46,'11:46','(Allah) phán: “Hỡi Nuh! Quả thật, nó không thuộc gia đình nhà ngươi bởi vì việc làm của nó không tốt. Bởi thế, chớ cầu xin TA về điều mà ngươi không biết. TA khuyên bảo ngươi như thế kẻo ngươi có thể trở thành một người ngu muội.”');
INSERT INTO "hasan_abdulkarim" VALUES(11,47,'11:47','(Nuh) thưa: “Lạy Thượng Đế của bề tôi! Xin Ngài giữ bề tôi tránh xa việc đòi hỏi Ngài về những điều mà bề tôi không biết. Nếu Ngài không tha thứ và khoan dung thì chắc chắn bề tôi sẽ trở thành một người thua thiệt.”');
INSERT INTO "hasan_abdulkarim" VALUES(11,48,'11:48','(Allah) phán: “Hỡi Nuh! Hãy xuống tàu với sự Bằng An và Ân Phúc do TA ban cho ngươi và cho các cộng đồng xuất thân từ những ai theo ngươi; và có những cộng đồng đã được TA cho hưởng lạc (nơi trần gian) rồi TA sẽ trừng phạt chúng bằng một hình phạt đau đớn.”');
INSERT INTO "hasan_abdulkarim" VALUES(11,49,'11:49','Đấy là câu chuyện vô hình mà TA đã mặc khải cho Ngươi (Muhammad, một câu chuyện mà) trước đây Ngươi cũng như người dân của Ngươi chưa hề biết đến. Do đó, hãy kiên nhẫn chịu đựng (như Nuh). Chắc chắn kết cuộc tốt đẹp sẽ về tay của những người ngay chính sợ Allah.');
INSERT INTO "hasan_abdulkarim" VALUES(11,50,'11:50','Và {TA đã cử) đến với dân tộc ''Ad, (Nabi) Hud, một người anh em của họ. (Hud) bảo: "Hỡi dân Ta! Hãy thờ phụng Allah, các người không có Thượng Đế nào khác ngoài Ngài. Các người chỉ là những kẻ bịa đặt (điều huyễn hoặc)!"');
INSERT INTO "hasan_abdulkarim" VALUES(11,51,'11:51','"Hỡi dân Ta! Ta không yêu cầu các người tưởng thưởng Ta về (thông điệp) này. Quả thật phần thưởng của Ta ở nơi Đấng đã sáng tạo Ta. Thế các người không hiểu ư?"');
INSERT INTO "hasan_abdulkarim" VALUES(11,52,'11:52','“Và hỡi dân Ta! Hãy cầu xin Thượng Đế (Allah) của các người tha thứ và hối cải với Ngài; Ngài sẽ ban nước mưa xối xả từ trên trời xuống cho các người và sẽ gia tăng sức mạnh sẵn có của các người; và chớ quay bỏ đi thành những kẻ tội lỗi.”');
INSERT INTO "hasan_abdulkarim" VALUES(11,53,'11:53','(Họ) đáp: "Hỡi Hud! Ngươi chẳng đưa một bằng chứng nào đến cho bọn ta thấy cả và bọn ta không vì lời nói của ngươi mà bỏ các thần linh của bọn ta và bọn ta cũng sẽ không tin tưởng nơi ngươi."');
INSERT INTO "hasan_abdulkarim" VALUES(11,54,'11:54','“Bọn ta chỉ nói rằng có lẽ một số thần linh của bọn ta sẽ hãm hại ngươi.” (Hud) bảo: "Ta cầu xin Allah làm chứng và yêu cầu bọn ngươi làm chứng rằng ta hoàn toàn vô can về việc các ngươi đã tổ hợp nhiều thần linh (cùng với Ngài)."');
INSERT INTO "hasan_abdulkarim" VALUES(11,55,'11:55','"Ngoài Ngài, tất cả bọn ngươi hãy cộng tác bày mưu hãm hại Ta và chớ để cho Ta nghỉ xả hơi xem nào."');
INSERT INTO "hasan_abdulkarim" VALUES(11,56,'11:56','"Còn Ta, Ta tin cậy và phó thác cho Allah, Thượng Đế của Ta và Thượng Đế của các ngươi. (Ta cho các ngươi biết) không một sinh vật nào mà Ngài không nắm bằng chùm tóc nơi trán của nó. Quả thật, Thượng Đế của Ta đang ở trên Chính Đạo (Chân Lý)."');
INSERT INTO "hasan_abdulkarim" VALUES(11,57,'11:57','“Bởi thế, nếu các ngươi quay bỏ đi thì ít nhất Ta cũng đã truyền được cho các ngươi thông điệp mà Ta đã được cử mang đến cho các ngươi. Và Thượng Đế của Ta sẽ bổ nhiệm một đám người khác đến nối nghiệp các ngươi và các người sẽ không hãm hại được Ngài một tí nào; quả thật Thượng Đế của Ta kiểm soát tất cả mọi việc.”');
INSERT INTO "hasan_abdulkarim" VALUES(11,58,'11:58','Và khi Mệnh lệnh của TA được ban hành, TA nhủ lòng Khoan dung mà cứu Hud cùng với những ai theo Y và TA đã cứu họ thoát khỏi một hình phạt khắc nghiệt.');
INSERT INTO "hasan_abdulkarim" VALUES(11,59,'11:59','Và đó là số phận của (người dân) ''Ad. Họ đã phủ nhận các dấu hiệu của Thượng Đế (Allah) của họ và bất tuân các Sứ Giả của Ngài (Allah) và tuân lệnh từng tên bạo ngược ương ngạnh.');
INSERT INTO "hasan_abdulkarim" VALUES(11,60,'11:60','Và lời nguyền rủa đuổi theo họ nơi trần gian này và Ngày phục sinh. Ô này! Quả thật (người dân) ''Ad đã phủ nhận Thượng Đế (Allah) của họ. Thật đáng đời ''Ad, người dân của Hud!.');
INSERT INTO "hasan_abdulkarim" VALUES(11,61,'11:61','Và (TA đã cử) đến với người dân Thamud, Saleh một người anh em của họ. (Saleh) bảo: "Hỡi dân Ta! Hãy thờ phụng Allah; các người không có Thượng Đế nào khác ngoài Ngài cả. Ngài cho sinh sản các người từ trái đất và định cư các người nơi đó. Bởi thế, hãy cầu xin Ngài tha thứ và hối cải với Ngài. Quả thật, Thượng Đế của Ta luôn luôn kế cận và Hằng đáp ứng (lời cầu xin của các người)."');
INSERT INTO "hasan_abdulkarim" VALUES(11,62,'11:62','(Họ) đáp: “Hỡi Saleh! Mãi cho đến nay ngươi đã sống với bọn ta và là nguồn hy vọng của bọn ta. Phải chăng ngươi cấm bọn ta tôn thờ những đấng mà cha mẹ của bọn ta đã thờ phụng? Và quả thật bọn ta rất đỗi nghi ngờ về điều mà ngươi mời bọn ta chấp nhận.”');
INSERT INTO "hasan_abdulkarim" VALUES(11,63,'11:63','(Saleh) bảo: “Hỡi dân Ta! Các người có nhận thấy chăng, nếu Ta dựa vào một bằng chứng rõ rệt từ Thượng Đế của Ta và được Ngài ban cho Hồng Ân thì ai là kẻ sẽ giúp Ta tránh khỏi (hình phạt của) Ngài nếu Ta bất tuân Ngài. Bởi thế, các người chỉ làm cho Ta thêm mất mát.”');
INSERT INTO "hasan_abdulkarim" VALUES(11,64,'11:64','(Saleh) bảo: “Con lạc đà cái( này của Allah là một dấu hiệu ban cho các người. Bởi thế, hãy để mặc nó ăn cỏ nơi miếng đất trồng của Allah và chớ mó tay hãm hại nó; e rằng các người sẽ bị trừng phạt nhanh chóng.”');
INSERT INTO "hasan_abdulkarim" VALUES(11,65,'11:65','Nhưng họ đã cắt nhượng của nó. Thấy thế (Saleh) bảo: “Các người còn được ba ngày nữa để vui vầy trong nhà. (Sau đó sẽ là ngày tàn của các người). Đó là một lời hứa không ngoa.”');
INSERT INTO "hasan_abdulkarim" VALUES(11,66,'11:66','Bởi thế, khi Mệnh Lệnh của TA được ban hành, TA nhủ lòng khoan dung mà cứu Saleh với những ai tin tưởng cùng theo Y khỏi sự ô nhục của ngày đó. Quả thật, Thượng Đế của Ngươi (hỡi Muhammad!) là Đấng Toàn Lực, Toàn Năng.');
INSERT INTO "hasan_abdulkarim" VALUES(11,67,'11:67','Tiếng gầm dữ dội của trận động đất đã chụp bắt những kẻ làm điều sai quấy, khiến họ chết chúi đầu la liệt trong nhà trước buổi sáng,');
INSERT INTO "hasan_abdulkarim" VALUES(11,68,'11:68','Làm như họ chẳng hề sống thịnh vượng nơi đó bao giờ. Quả thật người dân Thamud đã phủ nhận Thượng Đế (Allah) của họ. Thật đáng đời Thamud!');
INSERT INTO "hasan_abdulkarim" VALUES(11,69,'11:69','Quả thật, các Thiên Sứ (Thiên Thần) của TA mang tin mừng đến cho Ibrahim. Họ chào Y, nói: ''Salam'' (Bằng an). Y đáp: "Salam". Xong, Y vội mang thịt bò (con) nướng ra đãi khách.');
INSERT INTO "hasan_abdulkarim" VALUES(11,70,'11:70','Nhưng khi thấy bàn tay của họ không chạm đến thức ăn, Y sinh nghi và đâm sợ. Họ bảo: “Đừng sợ. Chúng tôi là các Thiên Sứ được cử đến với người dân của Lut.”');
INSERT INTO "hasan_abdulkarim" VALUES(11,71,'11:71','Và bà vợ của Y đứng gần đó bật cười. Thế là, TA báo cho nữ ta tin mừng về (đứa con trai) Is-haq và sau Is-haq là (đứa cháu nội) Ya''qub.');
INSERT INTO "hasan_abdulkarim" VALUES(11,72,'11:72','(Vợ của Ibrahim) lên tiếng: “Thật khổ cho thân tôi! Làm sao tôi sẽ có con trong khi tôi đã là một bà lão và chồng tôi cũng là một ông lão? Quả thật đây là một điều kỳ lạ!”');
INSERT INTO "hasan_abdulkarim" VALUES(11,73,'11:73','(Các Thiên sứ) bảo: “Nhà ngươi ngạc nhiên về Mệnh Lệnh của Allah ư? Hồng Ân và Ân Phúc của Allah được ban cho các ngươi, hỡi người nhà (của Ibrahim.) Quả thật, Ngài (Allah) Rất đáng Ca tụng, Rất Mực Quang Vinh."');
INSERT INTO "hasan_abdulkarim" VALUES(11,74,'11:74','Bởi thế, khi Ibrahim hết sợ trong lòng và nhận được tin mừng. Y bắt đầu khiếu nại với TA giùm cho đám dân của Lut.');
INSERT INTO "hasan_abdulkarim" VALUES(11,75,'11:75','Quả thật, Ibrahim là một người hằng chịu đựng, xót thương người và năng hối cải.');
INSERT INTO "hasan_abdulkarim" VALUES(11,76,'11:76','(Allah phán): “Hỡi Ibrahim! Hãy bỏ qua chuyện (kèo nài) này. Quả thật, Mệnh Lệnh của Thượng Đế của Ngươi đã được ban hành. Quả thật, hình phạt sẽ đến với họ, không thể thu hồi đặng nữa.”');
INSERT INTO "hasan_abdulkarim" VALUES(11,77,'11:77','Khi đến gặp Lut, các Thiên sứ của TA thấy Y đang buồn rầu giùm cho người dân của mình và cảm thấy bất lực (trong việc che chở họ.) Y tự bảo: “Đây là một ngày thật buồn bã.”');
INSERT INTO "hasan_abdulkarim" VALUES(11,78,'11:78','Và người dân của Y chạy đến gặp Y bởi vì từ trước họ đã từng quen thói làm điều thô bỉ (khi thấy khách lạ).Lut bảo họ: “Hỡi dân ta! Đây, các đứa con gái của ta. Chúng trong sạch cho các người hơn (trong việc chăn gối.) Nào, hãy sợ Allah và chớ hạ nhục ta trước mặt khách. Trong các người, chẳng có một người nào biết điều hay sao?”');
INSERT INTO "hasan_abdulkarim" VALUES(11,79,'11:79','Họ đáp: “Ông đã biết rõ chúng tôi không cần đến các đứa con gái của ông, và biết quá rõ điều chúng tôi muốn.”');
INSERT INTO "hasan_abdulkarim" VALUES(11,80,'11:80','(Lut) đáp: “Phải chi ta có đủ quyền lực để trừng trị bọn ngươi hoặc đích thân sẽ nhờ một thế lực khác ủng hộ.”');
INSERT INTO "hasan_abdulkarim" VALUES(11,81,'11:81','(Các Thiên Sứ) bảo: “Hỡi Lut! Chúng tôi là các Thiên Sứ của Thượng Đế của Ngươi. Bằng mọi giá chúng không thể chạm đến mình Ngươi được. Nào, Ngươi và gia đình hãy ra đi vào phần cuối của ban đêm và đừng để cho một người nào của Ngươi ngoảnh nhìn lại phía sau ngoại trừ người vợ của Ngươi. Quả thật, điều gì xảy đến cho chúng sẽ xảy đến cho nữ ta như thế. Quả thật, giờ hẹn của chúng là buổi sáng. Phải chăng buổi sáng sắp gần kề?”');
INSERT INTO "hasan_abdulkarim" VALUES(11,82,'11:82','Bởi thế, khi Mệnh Lệnh của TA được ban hành, TA đã lật ngược chúng (các thị trấn)( và cho đổ lên chúng những trận mưa đá diêm sinh cứng như đất sét nung, lớp này chồng lên lớp khác;');
INSERT INTO "hasan_abdulkarim" VALUES(11,83,'11:83','Đã được đánh dấu từ Thượng Đế của Ngươi (Muhammad). Chúng không xa những kẻ làm điều sai quấy là bao!');
INSERT INTO "hasan_abdulkarim" VALUES(11,84,'11:84','Và (TA đã cử) đến với người dân Madyan, Shu''aib một người anh em của họ. (Shu''aib) bảo: "Hỡi dân Ta! Hãy thờ phụng Allah, các người không có Thượng Đế nào khác ngoài Ngài cả, và chớ cân và đo thiếu (cho người). Hiện nay Ta thấy các người làm ăn phát đạt nhưng Ta sợ giùm cho các người về hình phạt vào một ngày sẽ túm bắt tất cả."');
INSERT INTO "hasan_abdulkarim" VALUES(11,85,'11:85','“Hỡi dân Ta! Hãy cân đủ và đo đúng và chớ gian lận giữ lại hàng hoá của người; và chớ có ác ý gây điều thối nát trên trái đất.');
INSERT INTO "hasan_abdulkarim" VALUES(11,86,'11:86','“Phần mà Allah để lại cho các người (sau khi đóng Zakah) sẽ tốt cho các người hơn nếu các người là những người có đức tin. Và Ta không phải là giám thị theo trông chừng các người.”');
INSERT INTO "hasan_abdulkarim" VALUES(11,87,'11:87','Họ đáp: “Hỡi Shu''aib! Phải chăng việc lễ nguyện Salah của ngươi đã chỉ thị cho ngươi bắt bọn ta từ bỏ những thần linh mà cha mẹ (ông bà) của bọn ta đã tôn thờ? Hoặc (bắt bọn ta) ngưng làm điều mà bọn ta muốn bằng tài sản của bọn ta hay sao? Bởi vì quả thật, ngươi là một người hằng chịu đựng, biết điều.”');
INSERT INTO "hasan_abdulkarim" VALUES(11,88,'11:88','(Shu’ayb) bảo: “Hỡi dân Ta! Há các người nhận thấy nếu ta có một bằng chứng rõ rệt từ Thượng Đế (Allah) của Ta và được Ngài ban cấp bổng lộc tốt (để thi hành sứ mạng của Ngài thì Ta có ngưng được hay chăng?) Ta sẽ không mong, vì mục đích chống lại các người, làm điều mà Ta đã cấm cản các người. Quả thật, Ta chỉ muốn cải thiện các người theo khả năng của Ta. Và sự thành công của Ta chỉ tùy thuộc vào (sự giúp đỡ của) Allah. Ta hoàn toàn phó thác cho Allah và quay về sám hối với Ngài.');
INSERT INTO "hasan_abdulkarim" VALUES(11,89,'11:89','“Và hỡi dân ta! Đừng để cho sự bất đồng giữa Ta với các người thúc đẩy các người phạm tội, e rằng các người sẽ nhận một hình phạt giống như hình phạt đã rơi nhằm phải người dân của Nuh, người dân của Hud, người dân của Saleh. Và người dân của Lut không xa các người là bao!');
INSERT INTO "hasan_abdulkarim" VALUES(11,90,'11:90','“Và hãy cầu xin Thượng Đế (Allah) của các người tha thứ và sám hối với Ngài. Quả thật, Thượng Đế của Ta Rất Mực Khoan Dung, Rất mực Xót thương (loài người).”');
INSERT INTO "hasan_abdulkarim" VALUES(11,91,'11:91','Họ đáp: “Hỡi Shu''aib! Bọn ta không hiểu đặng nhiều điều mà ngươi đã nói. Và bọn ta nhận thấy ngươi là một người quá yếu trong bọn ta. Nếu không vì gia đình (dòng họ) của ngươi thì bọn ta đã ném đá giết ngươi bởi vì ngươi không có uy thế hơn bọn ta.”');
INSERT INTO "hasan_abdulkarim" VALUES(11,92,'11:92','(Shu''aib) bảo: “Hỡi dân Ta! Phải chăng các người cho rằng gia đình (dòng họ) của Ta mạnh hơn Allah hay sao? Và các người đã quẳng Ngài ra sau lưng của các người. Quả thật, Thượng Đế của Ta tóm thâu hết tất cả các việc các ngươi làm.');
INSERT INTO "hasan_abdulkarim" VALUES(11,93,'11:93','“Và hỡi dân Ta! Hãy làm điều mà các người đang theo, Ta làm công việc của Ta. Rồi đây các người sẽ sớm biết ai sẽ bị trừng phạt nhục nhã và ai là kẻ nói dối. Và hãy trông chờ, Ta sẽ cùng trông chờ với các người.”');
INSERT INTO "hasan_abdulkarim" VALUES(11,94,'11:94','Và khi Mệnh Lệnh của TA được ban hành, TA nhủ lòng khoan dung mà cứu thoát Shu''aib với những ai đã tin tưởng theo Y. Và tiếng gầm dữ dội (của trận động đất) đã chụp bắt những kẻ làm điều sai quấy, khiến họ chết chúi đầu la liệt trong nhà vào buổi sáng.');
INSERT INTO "hasan_abdulkarim" VALUES(11,95,'11:95','Tựa như họ chẳng hề sống thịnh vượng nơi đó bao giờ. Quả thật, Madyan bị trừng phạt giống như Thamud vậy!');
INSERT INTO "hasan_abdulkarim" VALUES(11,96,'11:96','Và quả thật TA đã cử Musa mang những dấu hiệu của TA với một thẩm quyền rõ rệt,');
INSERT INTO "hasan_abdulkarim" VALUES(11,97,'11:97','Đến gặp Fir-''awn và các vị cầm đầu của hắn. Nhưng chúng thực thi mệnh lệnh của Fir-''awn trong lúc mệnh lệnh của Fir-''awn không đúng Chân Lý.');
INSERT INTO "hasan_abdulkarim" VALUES(11,98,'11:98','Hắn sẽ dẫn đầu đám dân của hắn vào Ngày phục sinh và dẫn chúng vào hỏa ngục. Thật xấu xa thay chỗ mà chúng bị dẫn đi vào.');
INSERT INTO "hasan_abdulkarim" VALUES(11,99,'11:99','Và lời nguyền rủa sẽ theo đuổi chúng suốt đời này và Ngày phục sinh. Và xấu xa thay món quà mà chúng sẽ được ban tặng.');
INSERT INTO "hasan_abdulkarim" VALUES(11,100,'11:100','Đó là một số câu chuyện của các thị trấn mà TA kể lại cho Ngươi (Muhammad), một số hãy còn lưu truyền còn một số thì đã đi vào quên lãng.');
INSERT INTO "hasan_abdulkarim" VALUES(11,101,'11:101','Không phải TA đã làm thiệt hại chúng mà chính chúng tự làm hại bản thân (linh hồn) mình bởi vì các thần linh mà chúng cầu nguyện ngoài Allah chẳng giúp ích gì được cho chúng khi Mệnh Lệnh của Thượng Đế của Ngươi (Muhammad) đã xảy đến cho chúng; ngược lại, chúng chỉ làm cho chúng thêm mất mát.');
INSERT INTO "hasan_abdulkarim" VALUES(11,102,'11:102','Sự túm bắt của Thượng Đế của Ngươi (Muhammad) đúng như thế khi Ngài trừng phạt (dân cư của) các thị trấn trong lúc chúng đang làm điều sai quấy. Quả thật, sự túm bắt của Ngài rất đau đớn, rất khủng khiếp.');
INSERT INTO "hasan_abdulkarim" VALUES(11,103,'11:103','Quả thật, trong sự việc đó có một dấu hiệu (bài học) cho ai là người sợ sự trừng phạt ở đời sau: đó là một ngày mà nhân loại sẽ được tập trung trở lại; đó là một Ngày mà tất cả đều hiện diện để làm chứng.');
INSERT INTO "hasan_abdulkarim" VALUES(11,104,'11:104','Và TA chỉ dời nó (Ngày đó) đến một thời hạn ấn định.');
INSERT INTO "hasan_abdulkarim" VALUES(11,105,'11:105','Khi Ngày đó đến, không một người (linh hồn) nào được lên tiếng trừ phi có phép của Ngài (Allah). Do đó trong họ có kẻ bị họa và có người được phúc.');
INSERT INTO "hasan_abdulkarim" VALUES(11,106,'11:106','Bởi thế, đối với những ai bị họa thì sẽ ở trong hỏa ngục, họ sẽ than vắn thở dài và tức tưởi trong đó.');
INSERT INTO "hasan_abdulkarim" VALUES(11,107,'11:107','Họ sẽ ở trong đó lâu bằng thời gian mà trời và đất hãy còn tồn tại trừ phi Thượng Đế của Ngươi (Muhammad) muốn khác đi bởi vì quả thật Thượng Đế của Ngươi làm bất cứ điều gì Ngài muốn.');
INSERT INTO "hasan_abdulkarim" VALUES(11,108,'11:108','Còn đối với những ai được phúc thì sẽ ở trong thiên đàng lâu bằng thời gian mà bầu trời và trái đất còn tồn tại trừ phi Thượng Đế của Ngươi muốn (khác đi), một quà tặng vĩnh cửu.');
INSERT INTO "hasan_abdulkarim" VALUES(11,109,'11:109','Do đó, Ngươi chớ sinh nghi về những kẻ mà những người (ngoại đạo) này thờ phụng. Họ chỉ thờ phụng giống như việc cha mẹ của họ đã thờ phụng từ trước. Và quả thật, TA sẽ trả lại đầy đủ phần việc của họ, không giảm bớt tí nào.');
INSERT INTO "hasan_abdulkarim" VALUES(11,110,'11:110','Và quả thật, TA đã ban cho Musa Kinh Sách, nhưng đã có những bất đồng trong đó. Nếu không vì một Lời Phán của Thượng Đế của Ngươi (Muhammad) đã được công bố từ trước thì vấn đề đã được quyết định xong giữa họ với nhau. Và quả thật, họ còn vương vấn, nghi hoặc về nó.');
INSERT INTO "hasan_abdulkarim" VALUES(11,111,'11:111','Và chắc chắn Thượng Đế của Ngươi sẽ trả lại đầy đủ cho từng người của họ về công trình của họ bỏi vì Ngài là Đấng Am Tường về việc họ làm.');
INSERT INTO "hasan_abdulkarim" VALUES(11,112,'11:112','Bởi thế, hãy (kiên quyết) đứng vững (cho Chính Đạo) như đã được truyền, Ngươi và người nào sám hối (với Allah) cùng với Ngươi và chớ phạm giới bởi vì Ngài thấy rõ điều các ngươi làm.');
INSERT INTO "hasan_abdulkarim" VALUES(11,113,'11:113','Và chớ thiên vị (giúp) những ai làm điều sai quấy e rằng hỏa ngục sẽ chạm đến các ngươi bởi vì ngoài Allah ra không ai có thể che chở và cứu giứp các ngươi.');
INSERT INTO "hasan_abdulkarim" VALUES(11,114,'11:114','Và hãy dâng lễ nguyện Salah vào hai đầu mút của ban ngày và vào một số giấc ban đêm bởi vì điều thiện sẽ xóa tan điều xấu (tội lỗi). Đó là điều nhắc nhở cho những người lưu ý.');
INSERT INTO "hasan_abdulkarim" VALUES(11,115,'11:115','Và hãy kiên nhẫn chịu đựng. Bởi vì quả thật, Allah sẽ không làm mất phần thưởng của những người làm tốt.');
INSERT INTO "hasan_abdulkarim" VALUES(11,116,'11:116','Giá nơi các thế hệ trước các ngươi có được những người sáng suốt ngăn cản thiên hạ làm điều thối nát trên trái đất, ngoại trừ một thiểu số đã được TA cứu sống? Ngược lại, những kẻ làm điều sai quấy vẫn theo đuổi những lạc thú trần gian và trở thành những kẻ tội lỗi.');
INSERT INTO "hasan_abdulkarim" VALUES(11,117,'11:117','Và Thượng Đế của Ngươi không phải là Đấng đã tiêu diệt các thị trấn một cách bất công trong lúc dân cư của chúng là những người làm điều thiện.');
INSERT INTO "hasan_abdulkarim" VALUES(11,118,'11:118','Và nếu muốn, Thượng Đế của Ngươi đã làm cho nhân loại thành một cộng đồng duy nhất nhưng họ vẫn không ngừng bất đồng.');
INSERT INTO "hasan_abdulkarim" VALUES(11,119,'11:119','Ngoại trừ những ai mà Thượng Đế của Ngươi khoan dung; và vì thế mà Ngài đã tạo ra họ. Và Lời Phán của Thượng Đế của Ngươi sẽ được thực hiện: TA (Allah) sẽ nhốt Jinn và người, tất cả vào đầy hỏa ngục.');
INSERT INTO "hasan_abdulkarim" VALUES(11,120,'11:120','Và tất cả các câu chuyện của các Sứ Giả mà TA đã kể lại cho Ngươi đều nhằm củng cố tấm lòng của Ngươi thêm vững chắc. Và trong (chương) này Ngươi tiếp thu được Chân Lý với lời động viên và nhắc nhở cho những người có đức tin.');
INSERT INTO "hasan_abdulkarim" VALUES(11,121,'11:121','Và hãy bảo những kẻ không tin tưởng: "Hãy làm bất cứ điều gì theo khả năng của các người, bọn Ta làm phần việc của bọn Ta."');
INSERT INTO "hasan_abdulkarim" VALUES(11,122,'11:122','“Và hãy đợi xem, bọn Ta cũng sẽ chờ đợi (như các người).”');
INSERT INTO "hasan_abdulkarim" VALUES(11,123,'11:123','Và điều vô hình của các tầng trời và trái đất là của Allah; mọi việc đều được trình về cho Ngài quyết định. Bởi thế, Hãy thờ phụng Ngài và phó thác cho Ngài (hỡi Muhammad!). Và Thượng Đế của Ngươi không làm ngơ về mọi điều các ngươi làm.');
INSERT INTO "hasan_abdulkarim" VALUES(12,1,'12:1','Alif. Lam. Ra. Đây là những Câu của một Kinh Sách minh bạch.');
INSERT INTO "hasan_abdulkarim" VALUES(12,2,'12:2','Quả thật, TA đã ban Nó (những Lời Mặc Khải) xuống (dưới hình thức của) Qur''an (dùng để đọc xướng) bằng tiếng Ả Rập để các ngươi có thể đọc hiểu dễ dàng.');
INSERT INTO "hasan_abdulkarim" VALUES(12,3,'12:3','Qua những điều mà TA đã mặc khải cho Ngươi (Muhammad) trong Qur''an này, TA kể lại cho Ngươi một câu chuyện tốt đẹp nhất mà trước đây Ngươi chưa hề biết đến.');
INSERT INTO "hasan_abdulkarim" VALUES(12,4,'12:4','(Câu chuyện xảy ra) khi Yusuf( thưa với người cha của Y: “Thưa cha, con nằm mộng thấy mười một vì sao và cả mặt trời lẫn mặt trăng; con thấy chúng đều phủ phục trước mặt con.”');
INSERT INTO "hasan_abdulkarim" VALUES(12,5,'12:5','(Người cha) bảo: "Này con yêu dấu, chớ kể lại điều con nằm mộng thấy cho các anh con nghe, cha sợ rằng chúng sẽ bày mưu hãm hại con. Chắc chắn Shaytan là kẻ thù công khai của loài người."');
INSERT INTO "hasan_abdulkarim" VALUES(12,6,'12:6','"Và Thượng Đế (Allah) của con đã chọn con như thế và Ngài đã dạy con cách giải thích những câu chuyện khúc mắc và muốn hoàn tất Ân Huệ của Ngài cho con và cho hậu duệ của Ya''qub giống như việc Ngài đã hoàn tất Ân Huệ cho các tổ tiên của con, Ibrahim và Is-haq. Chắc chắn Thượng Đế của con Rất Mực Hiểu Biết, Rất Mực Sáng Suốt."');
INSERT INTO "hasan_abdulkarim" VALUES(12,7,'12:7','Chắc chắn trong (câu chuyện của) Yusuf và những người anh của Y là những bằng chứng cho những người hỏi tìm (sự thật).');
INSERT INTO "hasan_abdulkarim" VALUES(12,8,'12:8','Khi (những người anh cùng cha khác mẹ của Yusuf) bàn với nhau: "Rõ ràng cha tụi mình yêu Yusuf và em của nó hơn tụi mình mặc dù tụi mình đông và mạnh hơn. Chắc chắn cha tụi mình sai lầm rõ ràng."');
INSERT INTO "hasan_abdulkarim" VALUES(12,9,'12:9','"Hãy giết Yusuf hoặc bắt nó quẳng đi nơi khác, làm thế ân sủng của cha mới dồn về hết cho các người và sau đó các người mới trở thành một đám người tốt (nơi cha)."');
INSERT INTO "hasan_abdulkarim" VALUES(12,10,'12:10','Một người trong bọn chúng đáp: “Chớ giết Yusuf. Nhưng nếu mọi người nhất định làm một điều gì để loại trừ nó thì hãy ném nó xuống giếng, có thể một đoàn khách thương (đi ngang qua chỗ đó) sẽ vớt nó mang đi nơi khác.”');
INSERT INTO "hasan_abdulkarim" VALUES(12,11,'12:11','Rồi chúng thưa (với người cha): "Thưa cha! Tại sao cha không tin tụi con mà giao Yusuf cho tụi con (trông coi) bởi vì chắc chắn chúng con là những người anh luôn luôn cầu mong điều lành cho đứa em của mình."');
INSERT INTO "hasan_abdulkarim" VALUES(12,12,'12:12','“Ngày mai cha hãy để em đi ra ngoài chơi với tụi con và tụi con sẽ trông coi em cẩn thận.”');
INSERT INTO "hasan_abdulkarim" VALUES(12,13,'12:13','(Người cha) bảo: “Chắc chắn việc các con đưa nó đi xa làm cha lo lắng. Cha sợ sói sẽ ăn thịt nó trong lúc các con ham chơi không ngó ngàng đến nó.”');
INSERT INTO "hasan_abdulkarim" VALUES(12,14,'12:14','Chúng thưa: “Nếu sói ăn thịt em thì tụi con đông như thế này há bó tay chịu thua hay sao?”');
INSERT INTO "hasan_abdulkarim" VALUES(12,15,'12:15','Do đó, khi chúng đưa Yusuf đi xa, chúng đồng lòng ném Y xuống giếng. Và TA đã mặc khải cho Y (bảo): “Một ngày nào đó, nhà ngươi sẽ nói cho bọn chúng biết việc làm này của chúng và chúng sẽ không nhận biết (ngươi).”');
INSERT INTO "hasan_abdulkarim" VALUES(12,16,'12:16','Và vào đầu hôm, chúng khóc lóc đến gặp người cha.');
INSERT INTO "hasan_abdulkarim" VALUES(12,17,'12:17','Chúng thưa: “Thưa cha, chúng con rủ nhau chạy đua và để Yusuf ở gần hành lý của chúng con, do đó một con sói đã ăn thịt em mất rồi. Và chắc cha sẽ không tin tụi con mặc dầu tụi con nói sự thật.”');
INSERT INTO "hasan_abdulkarim" VALUES(12,18,'12:18','Và chúng mang chiếc áo bôi máu giả đến trình (với người cha). Người cha bảo: “Không, tâm trí của tụi bây đã bày vẽ sự việc. (Sự việc đã như thế) thì kiên nhẫn là (giải pháp) tốt đẹp nhất. Và chỉ Allah là Đấng ta cầu xin được giúp đỡ về điều mà tụi bây đã xác nhận.”');
INSERT INTO "hasan_abdulkarim" VALUES(12,19,'12:19','Và một đoàn khách thương đến (dừng chân tại chỗ giếng nước). Họ phái một người múc nước (đi kéo nước giếng). Người đó thả chiếc gầu xuống giếng rồi sau đó hô lớn: “Có tin mừng! Đây (kéo được) một cậu bé.” Và họ giấu Y (Yusuf) để làm một món hàng (đổi chác). Và Allah biết rõ điều họ làm.');
INSERT INTO "hasan_abdulkarim" VALUES(12,20,'12:20','Và họ (đoàn khách thương) đã bán Y (Yusuf) với một giá rẻ mạt, chỉ một vài đồng tiền bạc. Họ là những người đã xem Y chẳng ra gì.');
INSERT INTO "hasan_abdulkarim" VALUES(12,21,'12:21','Và vị khách đã mua Y nơi Ai Cập bảo vợ: "Hãy quí trọng cậu ta trong nhà mình. Biết đâu cậu ta sẽ mang lợi về cho mình hoặc mình sẽ nhận cậu ta làm con nuôi." Và TA đã định cư Yusuf trong lãnh thổ như thế hầu TA dạy Y cách giải thích những câu chuyện khúc mắc; và Allah toàn quyền kiểm soát công việc của Ngài nhưng đa số nhân loại không biết.');
INSERT INTO "hasan_abdulkarim" VALUES(12,22,'12:22','Và khi Y (Yusuf) trưởng thành, TA ban cho Y trí suy xét và kiến thức. Và TA đãi ngộ những người làm tốt như thế.');
INSERT INTO "hasan_abdulkarim" VALUES(12,23,'12:23','Nhưng bà chủ của ngôi nhà mà Y đang tá túc tìm cách quyến rũ Y. Và nữ ta đóng chặt các cửa phòng và mời mọc: “Hỡi chàng, hãy đến đây!” Y đáp: “Cầu xin Allah che chở khỏi điều này, quả thật ngài quan lớn là chủ nhân của tôi, ngài ban cho tôi một chỗ tá túc tốt đẹp. Quả thật những kẻ làm điều sai quấy sẽ không thành đạt.”');
INSERT INTO "hasan_abdulkarim" VALUES(12,24,'12:24','Và quả thật nữ ta muốn (thân xác của) Y và nếu không thấy rõ minh chứng của Thượng Đế của Y thì Y cũng đã động lòng trước sự quyến rũ đó của nữ ta. Như thế là để TA đưa những điều xấu xa và thô bỉ xa khỏi Y. Bởi vì trong số bầy tôi của TA, Y là một người bề tôi thành tâm và trung thực.');
INSERT INTO "hasan_abdulkarim" VALUES(12,25,'12:25','Và hai người đua nhau chạy nhanh đến cửa phòng và nữ ta (đuổi theo) giật rách vạt áo phía sau của Y; và hai người gặp ông (chồng) của nữ tại cửa phòng. Nữ ta lên tiếng: “Đâu là hình phạt xứng đáng dùng xử lý kẻ đã có dã tâm dụ dỗ người vợ của ngài? Phải chăng bỏ tù hay trừng phạt nó đau đớn?”');
INSERT INTO "hasan_abdulkarim" VALUES(12,26,'12:26','(Yusuf) thưa: "Chính bà ấy quyến rũ tôi.” Và một nhân chứng trong gia đình của nữ ta chịu đứng ra làm chứng, nói: “Nếu áo của y bị rách phía trước thì bà ấy nói sự thật còn hắn là kẻ nói dối."');
INSERT INTO "hasan_abdulkarim" VALUES(12,27,'12:27','“Ngược lại, nếu áo của y bị rách từ phía sau thì bà ấy nói dối còn y là người nói thật.”');
INSERT INTO "hasan_abdulkarim" VALUES(12,28,'12:28','Bởi thế, khi ông (chồng) nhìn thấy áo của Y (Yusuf) bị rách từ phía sau, ông bảo ngay: “Chắc chắn đây là âm mưu của các nàng. Quả thật, âm mưu của các nàng thật dữ dội.”');
INSERT INTO "hasan_abdulkarim" VALUES(12,29,'12:29','“Hỡi Yusuf, hãy bỏ qua chuyện này. Còn nàng, hãy xin tha thứ tội lỗi của nang. Quả thật, nàng mới là người sai (trong chuyện này).”');
INSERT INTO "hasan_abdulkarim" VALUES(12,30,'12:30','Và các phụ nữ trong thành phố to nhỏ bảo nhau: "Bà vợ của một vị đại thần đã cố ý dụ dỗ người tớ trai của bà. Nó đã làm cho bà mê tít. Quả thật, chúng mình thấy bà ấy lầm lạc rõ ràng."');
INSERT INTO "hasan_abdulkarim" VALUES(12,31,'12:31','Do đó, khi nghe lời đàm tiếu, nữ ta cho người đi mời họ và chuẩn bị cho họ một buổi tiệc sang trọng; và nữ ta trao cho mỗi bà một con dao (để cắt trái cây); nữ ta bảo (Yusuf): “Hãy bước ra trình diện các bà.” Do đó, khi nhìn thấy Y (với diện mạo khôi ngô) thì họ đều khen Y vĩ đại và (trong lúc sửng sờ trước diện mạo của Y) họ đã cắt tay của họ (lúc nào không biết); họ cùng lên tiếng: “Allah mầu nhiệm! Đây không phải là một người phàm. Chấc chắn đây phải là một Thiên Thần.”');
INSERT INTO "hasan_abdulkarim" VALUES(12,32,'12:32','Nữ ta bảo: “Đây là người (tớ) vì nó mà quí bà đã chỉ trích tôi. Quả thật, tôi đã dụ dỗ nó nhưng nó đã tự kềm chế lấy mình nó được. Và bây giờ nếu nó không làm theo chỉ thị của tôi thì chắc chắn nó sẽ bị bắt giam vào tù và sẽ trở thành một người bị hạ nhục.”');
INSERT INTO "hasan_abdulkarim" VALUES(12,33,'12:33','(Yusuf cầu nguyện) thưa: “Lạy Thượng Đế của bề tôi! Lao tù quí cho bề tôi hơn là những điều mà họ mời bề tôi chấp nhận. Và nếu Ngài không đưa âm mưu của các bà ra xa bề tôi thì chắc chắn bề tôi sẽ xuôi lòng nghe theo các bà và trở thành một người ngu muội.”');
INSERT INTO "hasan_abdulkarim" VALUES(12,34,'12:34','Do đó, Thượng Đế của Y đáp lại (lời cầu nguyện của) Y và đưa âm mưu của các nữ ra xa khỏi Y. Quả thật, Ngài là Đấng Hằng Nghe và Hằng Biết mọi việc.');
INSERT INTO "hasan_abdulkarim" VALUES(12,35,'12:35','Rồi sau khi chứng kiến những bằng chứng về sự vô tội của Y, họ nghĩ (cách tốt nhất là) nên nhốt Y vào tù trong một thời gian.');
INSERT INTO "hasan_abdulkarim" VALUES(12,36,'12:36','Và có hai thanh niên cùng vào tù với Y. Một trong hai người bảo: “Tôi (nằm mộng) thấy đang (vắt nho) làm rượu. Và người kia bảo: "Tôi (cũng nằm mộng) thấy mình đội trên đầu bánh mì và chim đang mổ ăn. Xin ông giải mộng đó giùm chúng tôi. Chúng tôi thấy ông quả là một người thiện tốt.”');
INSERT INTO "hasan_abdulkarim" VALUES(12,37,'12:37','Y (Yusuf) nói: "Trước khi thức ăn đến cho hai bạn dùng tôi sẽ cho hai bạn biết ý nghĩa thực sự của giấc mộng. Đây là điều mà Thượng Đế của tôi đã dạy tôi. Quả thật, tôi đã từ bỏ tín ngưỡng của một đám người không tin tưởng nơi Allah và không tin nơi đời sau."');
INSERT INTO "hasan_abdulkarim" VALUES(12,38,'12:38','“Và hiện nay tôi theo tín ngưỡng của cha ông tôi: Ibrahim, Is-haq, và Ya''qub và chúng tôi không được phép tổ hợp bất cứ cái gì với Allah. Đó là thiên ân của Allah ban cho chúng tôi và nhân loại. Nhưng đa số nhân loại không biết ơn."');
INSERT INTO "hasan_abdulkarim" VALUES(12,39,'12:39','“Hỡi hai người bạn tù của tôi! Phải chăng nhiều Thượng Đế khác biệt tốt hơn hay là một Allah Duy Nhất Tối thượng tốt hơn?"');
INSERT INTO "hasan_abdulkarim" VALUES(12,40,'12:40','"Những vật (hay thần linh) mà các bạn đang thờ phụng chỉ là những tên gọi mà các bạn và cha mẹ của các bạn đã đặt cho chúng, chứ Allah đã không ban cấp cho một chút thẩm quyền nào. Chắc chắn duy chỉ Allah thôi nắm quyền xét xử. Ngài ra lệnh cho các bạn không được thờ phụng ai khác mà chỉ thờ phụng riêng Ngài. Đó là tôn giáo chính trực nhưng đa số nhân loại không biết."');
INSERT INTO "hasan_abdulkarim" VALUES(12,41,'12:41','“Hỡi hai người bạn tù của tôi, trong hai bạn, một người sẽ rót rượu cho chúa của y uống, và người kia thì sẽ bị đống đinh trên thập tự giá và chim sẽ mổ đầu của y ăn. vấn đề mà hai bạn muốn biết đã được quyết định như thế.”');
INSERT INTO "hasan_abdulkarim" VALUES(12,42,'12:42','Và Y (Yusuf) nói với một trong hai người bạn tù mà Y nghĩ y sẽ được cứu sống, như sau: “Bạn nhớ nhắc tên của tôi với chúa của bạn nhé!” Nhưng Shaytan làm cho người đó quên mất việc nhắc nhở( với chúa của y. Bởi thế, Y (Yusuf) phải ở lại trong tù thêm vài năm nữa.');
INSERT INTO "hasan_abdulkarim" VALUES(12,43,'12:43','Vị vua (của Ai Cập) nói (với quân thần trong triều): "Trẫm (nằm mộng) thấy bảy con bò mập bị bảy con bò ốm ăn thịt và bảy bông lúa xanh tươi và bảy bông lúa vàng héo. Hỡi các chư khanh! Hãy giải mộng cho trẫm điều trẫm đã thấy nếu các khanh biết giải mộng.”');
INSERT INTO "hasan_abdulkarim" VALUES(12,44,'12:44','Họ thưa: “Một giấc mộng lộn xộn và các hạ thần không biết giải đoán các giấc mộng.”');
INSERT INTO "hasan_abdulkarim" VALUES(12,45,'12:45','Và một trong hai người bạn tù đã được tha bổng sực nhớ lại (lời dặn của Yusuf) sau một thời gian dài quên lững, lên tiếng: “Bề tôi sẽ trình bày với quí ngài ý nghĩa của nó; xin quí ngài hãy cử bề tôi đi gặp (Yusuf).”');
INSERT INTO "hasan_abdulkarim" VALUES(12,46,'12:46','(Y nói): “Hỡi Yusuf! Hỡi người chân thật! Hãy giải thích cho chúng tôi (giấc mộng) về bảy con bò mập bị bảy con bò ốm ăn nuốt mất và bảy bông lúa xanh tươi với bảy bông lúa vàng héo để tôi có thể trở về gặp lại người dân báo cáo cho họ biết (ý nghĩa của nó).”');
INSERT INTO "hasan_abdulkarim" VALUES(12,47,'12:47','(Yusuf) bảo: "Các người cứ siêng năng trồng trọt suốt bảy năm liền như thường lệ, rồi cất giữ nguyên hạt mùa màng đã gặt ngoại trừ một số ít dùng để ăn."');
INSERT INTO "hasan_abdulkarim" VALUES(12,48,'12:48','“Rồi sau thời gian đó sẽ xảy ra bảy năm (hạn hán) cơ cực ăn tiêu hết lúa thóc mà các người đã dự trữ trong các năm trước ngoại trừ một số ít các người đã cất giữ cẩn thận."');
INSERT INTO "hasan_abdulkarim" VALUES(12,49,'12:49','“Rồi sau thời gian đó sẽ đến một năm mà người dân sẽ được nước mưa dồi dào và trong năm đó người dân sẽ ép (nho và dầu).”');
INSERT INTO "hasan_abdulkarim" VALUES(12,50,'12:50','Và nhà vua phán: “Hãy đưa y đến gặp trẫm”. Do đó, khi sứ thần đến gặp Y (Yusuf), Y bảo: “Hãy về tâu lại với chúa của ngài và hỏi đức vua sẽ xử lý thế nào về việc các quí bà đã cắt tay của họ trước đây? Quả thật, Thượng Đế của Ta Hằng Biết âm mưu của các bà.”');
INSERT INTO "hasan_abdulkarim" VALUES(12,51,'12:51','Vua phán: “Sự tình của các bà như thế nào khi các bà đã tìm cách quyến rũ Yusuf?” Các bà cùng lên tiếng: “Allah mầu nhiệm! Chúng thiếp hoàn toàn không biết một điều xấu nào.” (Thấy thế) Bà vợ của vị đại thần thưa: “Sự thật đã rõ ràng, chính tiện thiếp là người đã tìm cách dụ dỗ cậu ta. Và quả thật, cậu ta là một người chân thật.”');
INSERT INTO "hasan_abdulkarim" VALUES(12,52,'12:52','"(Ta yêu cầu) điều đó (Yusuf tiếp) là để cho ngài (quan chủ của Ta) biết rằng Ta không bí mật phản bội ngài (khi ngài đi vắng) và quả thật Allah không chỉ dẫn mưu kế của những kẻ bội phản."');
INSERT INTO "hasan_abdulkarim" VALUES(12,53,'12:53','“Và tôi đã không tự cho mình hoàn toàn vô tội bởi vì chắc chắn dục vọng (của con người) hay xúi giục làm điều tội lỗi trừ phi Thượng Đế của Ta khoan dung. Quả thật, Thượng Đế của Ta Hằng Tha Thứ, Rất Mực Khoan Dung.”');
INSERT INTO "hasan_abdulkarim" VALUES(12,54,'12:54','Và vua phán: “Hãy đưa y đến gặp trẫm. Trẫm sẽ chọn y (làm việc) riêng cho trẫm.” Do đó, khi Y (Yusuf) tâu với vua tự sự, vua phán: “Hãy yên tâm. Ngày nay nhà ngươi có địa vị cao, được trẫm tín nhiệm.”');
INSERT INTO "hasan_abdulkarim" VALUES(12,55,'12:55','Y (Yusuf) xin vua: “Xin bệ hạ cho hạ thần quản lý kho tài nguyên trong nước. Hạ thần là một người bảo quản tốt, hiểu biết công việc.”');
INSERT INTO "hasan_abdulkarim" VALUES(12,56,'12:56','Và bằng cách đó TA (Allah) đã định cư Yusuf trong xứ (Ai Cập) để Y có thể nắm quyền bất cứ nơi nào mà Y muốn trong xứ. TA ban Hồng Ân của TA cho người nào TA muốn và TA không làm mất phần thưởng của những người làm tốt.');
INSERT INTO "hasan_abdulkarim" VALUES(12,57,'12:57','Và chắc chắn phần thưởng của đời sau sẽ tốt nhất cho những ai tin tưởng và sợ Allah.');
INSERT INTO "hasan_abdulkarim" VALUES(12,58,'12:58','Và các người anh (cùng cha khác mẹ) của Yusuf đến (Ai Cập). Và họ vào trình diện Y. Y nhận ra họ nhưng họ không nhận ra Y.');
INSERT INTO "hasan_abdulkarim" VALUES(12,59,'12:59','Và khi Y cung cấp phần lương thực cho họ, Y bảo: "Hãy đưa một đứa em của các người cùng một cha( với các người đến gặp Ta. Há các người đã không thấy việc Ta đong đủ lượng thóc (hay ngô) cho các người và Ta là một người rất trọng khách?"');
INSERT INTO "hasan_abdulkarim" VALUES(12,60,'12:60','“Ngược lại, nếu không đưa nó đến gặp Ta thì các người sẽ không được một tí lượng thóc (hay ngô) nào từ Ta và các người cũng sẽ không được đến gần Ta nữa.”');
INSERT INTO "hasan_abdulkarim" VALUES(12,61,'12:61','Họ thưa: “Chúng tôi sẽ cố gắng van nài cha của nó cho phép nó cùng đến với chúng tôi và chắc chắn chúng tôi sẽ làm được việc này.”');
INSERT INTO "hasan_abdulkarim" VALUES(12,62,'12:62','Và Y (Yusuf) bảo những người giúp việc mang số tiền mua thóc của họ để vào trong túi da đựng thóc của họ lại để cho họ nhận ra nó khi trở về gặp gia đình và để cho họ có thể trở lại (Ai Cập).');
INSERT INTO "hasan_abdulkarim" VALUES(12,63,'12:63','Do đó, khi trở về gặp người cha, họ thưa: “Thưa cha! Họ sẽ từ chối cân lượng thóc cho chúng con (nếu không dẫn đứa em của chúng con theo). Bởi thế, xin cha hãy cho em cùng đi với chúng con hầu chúng con sẽ được lượng thóc qui định. Và chắc chắn chúng con sẽ trông nom em nó cẩn thận.”');
INSERT INTO "hasan_abdulkarim" VALUES(12,64,'12:64','(Người cha) bảo: “Há ta tin tưởng tụi bây mà giao nó (cho tụi bây) giống như ta đã tin tưởng mà giao anh của nó (cho tụi bây) trước đây hay sao? Nhưng Allah là Đấng Trông nom tốt nhất. Và Ngài là Đấng Khoan dung nhất của những người tỏ lòng khoan dung.”');
INSERT INTO "hasan_abdulkarim" VALUES(12,65,'12:65','Và khi mở túi đựng thóc, họ thấy số tiền mua thóc của họ đã được trả lại cho họ. Họ thưa: “Thưa cha! Chúng ta còn mong muốn gì hơn! Đây tiền mua thóc của chúng ta đã được trả lại cho chúng ta, chúng ta sẽ được phần lượng thóc cho gia đình của chúng ta và chúng con sẽ trông nom đứa em của chúng con cẩn thận; và chúng ta sẽ có thêm phần lượng thóc bằng sức chở của một con lạc đà. Đấy là một số lượng dễ dãi (đối với quan Tổng Quản Khố)."');
INSERT INTO "hasan_abdulkarim" VALUES(12,66,'12:66','(Người cha) bảo: "Ta sẽ không bao giờ cho nó đi cùng với tụi bây trừ phi tụi bây thề với Cha bằng một lời thề long trọng nhân danh Allah rằng tụi bây sẽ đưa nó trở lại cho Cha ngoại trừ trường hợp bị vây hãm (và bất lực không làm gì được). Và khi họ thề thốt bằng lời thề long trọng, người cha bảo: “Allah là Đấng làm chứng và thọ lãnh mọi điều chúng ta tuyên bố.”');
INSERT INTO "hasan_abdulkarim" VALUES(12,67,'12:67','(Nguôi giận, người cha) dặn: "Này các con! Tất cả chớ vào (thủ đô của Ai Cập) bằng một cửa duy nhất mà hãy đi vào bằng nhiều cửa khác nhau. Và cha không thể giúp các con tránh khỏi (hình phạt của) Allah về bất cứ điều gì. Bởi vì việc xét xử là của Allah. Ta hoàn toàn phó thác cho Ngài. Vậy hãy để cho những người phó thác nên phó thác cho Ngài."');
INSERT INTO "hasan_abdulkarim" VALUES(12,68,'12:68','Và khi họ đi vào (thủ đô của Ai Cập) theo phương cách mà người cha đã khuyên bảo thì lời dặn dò đó chẳng giúp họ tránh khỏi (kế hoạch của) Allah về bất cứ điều gì. Ngược lại, nó chỉ làm thỏa mãn ước vọng trong lòng của Ya''qub thôi. Và quả thật, Y có kiến thức về những điều mà TA (Allah) đã dạy bảo Y, nhưng đa số nhân loại không biết.');
INSERT INTO "hasan_abdulkarim" VALUES(12,69,'12:69','Và khi họ vào trình diện Yusuf, Y tiếp riêng đứa em ruột và giữ nó lại với Y. Y bảo đứa em: “Quả thật, anh là anh ruột của em đây. Thôi em chớ buồn về những điều mà các anh ấy đã làm.”');
INSERT INTO "hasan_abdulkarim" VALUES(12,70,'12:70','Do đó, khi cung cấp cho họ phần lương thực của họ, Y (Yusuf) đặt một chiếc cốc uống vào trong chiếc túi đựng thóc của đứa em ruột rồi cho một người tri hô lớn, bảo: “Này, hỡi đoàn khách thương! Các người là những kẻ trộm.”');
INSERT INTO "hasan_abdulkarim" VALUES(12,71,'12:71','Và quay về phía họ, (đoàn khách thương) hỏi: “Quí ngài mất cái gì vậy?”');
INSERT INTO "hasan_abdulkarim" VALUES(12,72,'12:72','Họ đáp: “Chúng tôi mất một chiếc cốc uống của nhà vua. Người nào mang nó trả lại sẽ được phần thưởng (lượng thóc) bằng sức chở của một con lạc đà. Và Ta bảo đảm cho việc đó.”');
INSERT INTO "hasan_abdulkarim" VALUES(12,73,'12:73','(Những người anh của Yusuf) lên tiếng: “Xin thề với Allah, quí ngài biết rõ chúng tôi đến đây không phải để làm điều tệ bạc tại địa phương, và chúng tôi không phải là những kẻ trộm.”');
INSERT INTO "hasan_abdulkarim" VALUES(12,74,'12:74','(Những người làm của Yusuf) bảo: “Thế thì hình phạt dùng xử lý kẻ trộm là gì nếu các người (bị bắt quả tang) đã nói dối.”');
INSERT INTO "hasan_abdulkarim" VALUES(12,75,'12:75','Họ (các người anh của Yusuf) thưa: “hình phạt xử lý y sẽ (như sau:) “Tang vật tìm thấy trong túi đựng thóc của người nào thì người đó sẽ bị cam tù để chuộc tội. Chúng tôi trừng phạt những kẻ làm bậy như thế.”');
INSERT INTO "hasan_abdulkarim" VALUES(12,76,'12:76','Thế là Y (Yusuf) bắt đầu lục lọi những chiếc túi của họ (các người anh) trước khi đến chiếc túi của đứa em ruột. Rồi Y móc nó (chiếc cốc) từ chiếc túi của đứa em ra. TA (Allah) sắp đặt kế hoạch cho Yusuf như thế. Y không thể bắt giữ đứa em ruột ở lại được theo luật lệ của nhà vua trừ phi Allah muốn khác đi. TA nâng cấp bậc (về kiến thức) cho người nào TA muốn. Nhưng cao hơn tất cả những ai đã được ban cho kiến thức thì chỉ có Đấng Toàn Tri (Allah).');
INSERT INTO "hasan_abdulkarim" VALUES(12,77,'12:77','(Các người anh) thưa: “Nếu nó ăn cắp thì anh của nó cũng đã ăn cắp trước đây.” Nhưng Yusuf giấu kín điều đó trong lòng và không tiết lộ cho họ biết. Y nói (thầm trong bụng): “Các người đang khốn đốn. Và Allah biết rõ những điều các người đã tuyên bố.”');
INSERT INTO "hasan_abdulkarim" VALUES(12,78,'12:78','(Các người anh) thưa: “Bẩm quan đại thần! Em nó có một người cha rất già. (Người sẽ buồn rầu cho số phận của nó). Do đó, xin ngài hãy bắt giữ một người của chúng tôi thay cho nó. Quả thật, chúng tôi thấy ngài là một người làm tốt."');
INSERT INTO "hasan_abdulkarim" VALUES(12,79,'12:79','Y (Yusuf) bảo: “Allah cấm làm thế! Chúng tôi chỉ bắt giữ ai là người mà chúng tôi tìm thấy bảo vật nơi y. Nếu làm ngược lại thì chắc chắn chúng tôi là những kẻ làm điều sai quấy.”');
INSERT INTO "hasan_abdulkarim" VALUES(12,80,'12:80','Do đó, khi hết hy vọng nơi Y (Yusuf), họ hợp nhau bàn kín. Người lớn nhất trong bọn lên tiếng: “Há các em không biết rằng phụ thân của các em đã nhận lời thề từ Allah đối với các em và trước đây các em đã không làm tròn nhiệm vụ của các em đối với Yusuf hay sao? Do đó, anh sẽ không bao giờ rời địa hạt này trừ phi phụ thân của anh cho phép hoặc Allah sẽ xét xử (việc này) giùm anh. Và Ngài (Allah) là Đấng Xét Xử Ưu Việt."');
INSERT INTO "hasan_abdulkarim" VALUES(12,81,'12:81','“Hãy trở về gặp phụ thân.” Và chúng thưa (với nguời cha): "Thưa cha! Quả thật đứa con trai của cha đã phạm tội ăn cắp. (Nó bị bắt giữ lại). Và chúng con chỉ xác nhận điều nào chúng con biết và chúng con không thể quản lý nổi điều vô hình."');
INSERT INTO "hasan_abdulkarim" VALUES(12,82,'12:82','“Và cha có thể hỏi thị dân nơi chúng con đã tá túc và đoàn khách thương cùng trở về với chúng con thì (sẽ biết) chúng con nói thật.”');
INSERT INTO "hasan_abdulkarim" VALUES(12,83,'12:83','(Người cha) bảo: “Không, tâm hồn tụi bây khéo bày chuyện. Do đó, kiên nhẫn là giải pháp tốt đẹp nhất. Biết đâu, Allah sẽ đưa tất cả chúng nó về lại cho cha. Quả thật, Ngài là Đấng Hằng Biết, Đấng Vô cùng Sáng suốt.”');
INSERT INTO "hasan_abdulkarim" VALUES(12,84,'12:84','Và Y (Ya''qub) quay mặt lánh xa chúng và lên tiếng: “Ôi Ta thật buồn rầu cho Yusuf!" Và do bởi dồn nén nỗi ưu phiền, nên cặp mắt của Y nhòa đi.');
INSERT INTO "hasan_abdulkarim" VALUES(12,85,'12:85','(Các con của Ya''qub) thưa: “Allah mầu nhiệm! Cha không ngưng thương nhớ Yusuf như thế này thì chắc chắn cha sẽ ngã bệnh nặng hoặc sẽ chết sớm.”');
INSERT INTO "hasan_abdulkarim" VALUES(12,86,'12:86','(Người cha) bảo: “Cha chỉ biết than thở nỗi âu sầu và buồn phiền của cha với Allah thôi và Allah cho cha biết điều mà các con không biết."');
INSERT INTO "hasan_abdulkarim" VALUES(12,87,'12:87','“Này các con! Các con hãy đi dọ hỏi tin tức của Yusuf và đứa em của nó và chớ bao giờ tuyệt vọng nơi lòng Khoan dung của Allah bởi vì quả thật chỉ đám người vô đức tin mới tuyệt vọng nơi lòng Khoan dung của Allah.”');
INSERT INTO "hasan_abdulkarim" VALUES(12,88,'12:88','Tiếp đó, khi vào trình diện Yusuf, họ thưa: “Bẩm quan đại thần! Chúng tôi và gia đình chúng tôi gặp hoạn nạn. Chúng tôi chỉ mang theo một chút ít vốn liếng, xin ngài đong đủ và bố thí cho chúng tôi bởi vì quả thật Allah sẽ tưởng thưởng những người bố thí.”');
INSERT INTO "hasan_abdulkarim" VALUES(12,89,'12:89','Y bảo: “Há các anh đã biết điều mà các anh đã đối xử với Yusuf và đứa em của nó chăng khi các anh ngu muội?”');
INSERT INTO "hasan_abdulkarim" VALUES(12,90,'12:90','Họ lên tiếng: “Có thật chăng ngài là Yusuf?” Người đáp: “Vâng, tôi là Yusuf đây. Và đây là đứa em của tôi. Chắc chắn, Allah đã nhân từ với chúng ta. Quả thật, ai sợ Allah và nhẫn nại, thì thật sự Allah sẽ không bao giờ làm mất phần thưởng của những người làm tốt.”');
INSERT INTO "hasan_abdulkarim" VALUES(12,91,'12:91','Họ lên tiếng: “Allah mầu nhiệm! Quả thật Allah đã ưu đãi em hơn các anh và quả thật các anh là những kẻ sai quấy.”');
INSERT INTO "hasan_abdulkarim" VALUES(12,92,'12:92','Y (Yusuf) bảo: "Ngày nay không có gì phải khiển trách các anh cả. Allah sẽ tha thứ cho các anh. Bởi vì Ngài là Đấng Khoan Dung nhất của những người tỏ lòng khoan dung."');
INSERT INTO "hasan_abdulkarim" VALUES(12,93,'12:93','“Hãy mang chiếc áo này của em về ném vào mặt của phụ thân, (cách này sẽ làm cho cặp mắt của) cha sáng trở lại, rồi đưa tất cả gia đình về đây với em.”');
INSERT INTO "hasan_abdulkarim" VALUES(12,94,'12:94','Và khi đoàn khách thương lên đường (rời Ai Cập), người cha lên tiếng: “Quả thật, Cha ngửi thấy mùi thơm của Yusuf. Chớ nghĩ rằng cha là một ông già lẫm cẫm.”');
INSERT INTO "hasan_abdulkarim" VALUES(12,95,'12:95','Họ đáp: “Allah mầu nhiệm! Quả thật cha vẫn bị ám ảnh như xưa.”');
INSERT INTO "hasan_abdulkarim" VALUES(12,96,'12:96','Tiếp đó, khi người mang tin lành đến nơi, y ném (chiếc áo của Yusuf) vào mặt của Y (Ya''qub), và y sáng mắt trở lại. Y bảo: “Há cha đã không bảo, Allah cho cha biết điều mà các con không biết hay sao?”');
INSERT INTO "hasan_abdulkarim" VALUES(12,97,'12:97','(Các con của Ya''qub) thưa: “Thưa cha! Xin cha cầu xin (Allah) tha thứ cho chúng con về tội lỗi của chúng con, quả thật chúng con là những kẻ sai quấy.”');
INSERT INTO "hasan_abdulkarim" VALUES(12,98,'12:98','Y (Ya''qub) bảo: “Cha sẽ sớm xin Thượng Đế của Cha tha thứ cho các con. Bởi vì Ngài là Đấng Hằng Tha Thứ, Rất Mực Khoan Dung.”');
INSERT INTO "hasan_abdulkarim" VALUES(12,99,'12:99','Tiếp đó, khi họ (cả gia đình) vào trình diện Yusuf, Y đưa cha mẹ đến ở chung với mình và thưa: “Hãy vào Ai Cập an toàn nếu Allah muốn.”');
INSERT INTO "hasan_abdulkarim" VALUES(12,100,'12:100','Và Yusuf nâng cha mẹ lên ngồi trên chiếc ngai vàng và họ đồng sụp lạy trước mặt người. Và (Yusuf) thưa: “Thưa cha! Đây là ý nghĩa của giấc mộng mà con đã thấy trước đây; Thượng Đế của con đã biến nó thành hiện thực; Ngài đã tốt với con khi Ngài đưa con ra khỏi ngục tù và đã đưa tất cả người (trong gia đình) từ sa mạc đến đoàn tụ với con sau khi Shaytan đã gây hiềm thù giữa con và các anh con. Quả thật, Thượng Đế của con rất mực Tế nhị đối với người nào Ngài muốn. Bởi vì Ngài là Đấng Toàn Tri, Đấng Vô cùng Sáng suốt.');
INSERT INTO "hasan_abdulkarim" VALUES(12,101,'12:101','“Lạy Thượng Đế của bề tồi! Ngài đã ban cho bề tôi quyền hành cai trị và đã dạy bề tôi cách giải thích câu chuyện (trong mộng). Đấng đã sáng tạo các tầng trời và trái đất, Ngài là Đấng Bảo Hộ của bề tôi ở đời này và đời sau. Xin Ngài làm cho bề tôi chết như một người Muslim (thần phục Ngài) và cho bề tôi gia nhập hội đoàn của những người ngoan đạo.”');
INSERT INTO "hasan_abdulkarim" VALUES(12,102,'12:102','Đó là câu chuyện vô hình mà TA đã mặc khải cho Ngươi (Muhammad). Ngươi không có mặt với họ khi họ họp nhau bàn thảo âm mưu lập kế hoạch.');
INSERT INTO "hasan_abdulkarim" VALUES(12,103,'12:103','Và đa số nhân loại sẽ không có đức tin dẫu rằng Ngươi rất muốn (điều tốt cho họ).');
INSERT INTO "hasan_abdulkarim" VALUES(12,104,'12:104','Và Ngươi cũng không đòi hỏi họ tưởng thưởng Ngươi về việc (phổ biến Qur’an) này, ngược lại, đó là một thông điệp nhắc nhở cho muôn loài (Jinn và người).');
INSERT INTO "hasan_abdulkarim" VALUES(12,105,'12:105','Và có nhiều dấu hiệu trong các tầng trời và trái đất mà họ đã bỏ qua khi quay mặt lánh xa chúng.');
INSERT INTO "hasan_abdulkarim" VALUES(12,106,'12:106','Và đa số bọn họ không tin tưởng nơi Allah trong thời gian tôn thờ đa thần.');
INSERT INTO "hasan_abdulkarim" VALUES(12,107,'12:107','Thế họ cảm thấy an toàn chăng nếu tấm phủ của hình phạt của Allah đến chụp bắt họ hoặc Giờ Tận Thế có thể bất ngờ xảy đến cho họ trong lúc họ không nhận thấy?');
INSERT INTO "hasan_abdulkarim" VALUES(12,108,'12:108','Hãy bảo (họ): “Đây là con đường của Ta (Muhammad). Ta mời các người đến với Allah. Ta và những người theo Ta thấy nó rất rõ. Quang vinh và trong sạch thay Allah! Và Ta không phải là một người thờ đa thần."');
INSERT INTO "hasan_abdulkarim" VALUES(12,109,'12:109','Và trước Ngươi (Muhammad), TA chỉ cử phái những người phàm (đến làm Sứ Giả) mà TA đã mặc khải cho (Kinh Sách). (Họ xuất thân) từ trong dân chúng của những thị trấn. Phải chăng chúng (những kẻ chỉ trích Ngươi) đã không du lịch trên trái đất để xét xem kết cuộc của những kẻ (không tin tưởng) trước chúng như thế nào? Và nhà ở nơi đời sau sẽ tốt nhất cho những ai sợ Allah. Há các ngươi không hiểu hay sao?');
INSERT INTO "hasan_abdulkarim" VALUES(12,110,'12:110','(Allah tạm hoãn cho những kẻ không tin tưởng) cho đến khi những Sứ Giả (của Ngài) mất hết hy vọng và nghĩ rằng mình đã bị phủ nhận thì lúc đó sự cứu giúp của TA sẽ đến với Họ. Và TA sẽ cứu thoát người nào TA muốn nhưng sự trừng phạt của TA chắc chắn sẽ không tránh khỏi đám người tội lỗi.');
INSERT INTO "hasan_abdulkarim" VALUES(12,111,'12:111','Chắc chắn nơi những câu chuyện của Họ (các Sứ Giả) có một bài học cho những người thông hiểu. Nó (Qur’an) không phải là một lời bịa đặt mà là một sự xác nhận những điều đã có trước Nó và là một sự trình bày chi tiết tất cả những sự việc. Và (Nó) là một Chỉ Đạo và một Hồng Ân cho đám người có đức tin.');
INSERT INTO "hasan_abdulkarim" VALUES(13,1,'13:1','Alif. Lam. Mim. Ra. Đây là những Câu của một Kinh Sách đã được ban xuống cho Ngươi (Muhammad) từ Thượng Đế của Ngươi, đó là sự thật nhưng đa số nhân loại không tin.');
INSERT INTO "hasan_abdulkarim" VALUES(13,2,'13:2','Allah là Đấng đã dựng cao các tầng trời không cần trụ chống mà các ngươi có thể nhìn thấy (bằng mắt thường) rồi Ngài lên ngôi, an vị trên chiếc Ngai Vương (''Arsh); và Ngài chế ngự mặt trời và mặt trăng (theo định luật của Ngài), mỗi cái chạy (theo quỹ đạo của nó) suốt hạn kỳ đã được qui định (cho chúng.) Ngài điều hành tất cả các công việc. Ngài giải thích chi tiết các câu Kinh để các ngươi có thể khẳng định chắc chắn về việc gặp lại Thượng Đế (Allah) của các ngươi (ở đời sau).');
INSERT INTO "hasan_abdulkarim" VALUES(13,3,'13:3','Và Ngài là Đấng đã trải rộng mặt đất và đặt nơi đó những quả núi vững chắc và Ngài làm ra sông ngòi và từ mỗi loại trái cây Ngài đã làm mọc ra nơi đó từng cặp, hai và hai; Ngài lấy ban ngày bao phủ ban đêm. Quả thật, trong sự việc đó là những dấu hiệu cho đám người biết ngẫm nghĩ.');
INSERT INTO "hasan_abdulkarim" VALUES(13,4,'13:4','Và trên trái đất có những vùng đất rộng tiếp giáp nhau, có những mảnh vườn nho, những cánh đồng bắp (lúa) và những cây chà là mọc từ một gốc hoặc không cùng một gốc được tưới bằng một loại nước duy nhất nhưng TA làm cho loại trái này ăn ngon hơn loại trái kia khi dùng. Chắc chắn nơi những hiện tượng đó là những dấu hiệu cho đám người hiểu biết.');
INSERT INTO "hasan_abdulkarim" VALUES(13,5,'13:5','Và nếu Ngươi ngạc nhiên (về sự không tin của họ) thì lời họ nói ra càng làm cho (Ngươi thêm) sửng sốt. Họ bảo: “Khi chúng tôi đã trở thành cát bụi, phải chăng chúng tôi sẽ thật sự được dựng sống lại trong một sự tạo hóa mới?” Họ là những kẻ đã phủ nhận Thượng Đế (Allah) của họ và là những kẻ sẽ mang gông cùm xung quanh cổ; và họ sẽ là bạn của hỏa ngục, một nơi họ sẽ sống đời đời.');
INSERT INTO "hasan_abdulkarim" VALUES(13,6,'13:6','Và họ giục Ngươi mang tai họa đến trước thay vì điều lành, mặc dầu trước họ chắc chắn đã xảy ra những thí dụ điển hình (về sự trừng phạt đó). Và chắc chắn Thượng Đế của Ngươi Hằng Tha Thứ cho loài người về những điều sai phạm của họ nhưng Thượng Đế của Ngươi trừng phạt cũng rất nghiêm khắc.');
INSERT INTO "hasan_abdulkarim" VALUES(13,7,'13:7','Và những kẻ không tin tưởng đã nói: "Tại sao không có một dấu lạ nào từ Thượng Đế của Y được ban xuống cho Y?" Quả thật, Ngươi chỉ là một người báo trước; và mỗi dân tộc đều có một người dẫn đường.');
INSERT INTO "hasan_abdulkarim" VALUES(13,8,'13:8','Allah biết bào thai mà mỗi phụ nữ mang (nơi mình) và những bào thai thiếu hoặc dư (tháng) nằm trong các dạ con. Và đối với Ngài mỗi vật đều được tính toán và định lượng đúng mức.');
INSERT INTO "hasan_abdulkarim" VALUES(13,9,'13:9','Ngài biết điều vô hình (bí mật) và điều bộc lộ (công khai). (Allah là) Đấng Vĩ Đại, Đấng Tối Cao.');
INSERT INTO "hasan_abdulkarim" VALUES(13,10,'13:10','Ai trong các ngươi giấu giếm lời nói (của mình) hoặc ai nói toạc ra ngoài cũng như ai ẩn mình trong đêm tối với ai bước đi ngang nhiên giữa bán ngày, (đối với Ngài) tất cả đều như nhau cả.');
INSERT INTO "hasan_abdulkarim" VALUES(13,11,'13:11','Bởi vì đằng trước và đằng sau y đều có những vị (Thiên Thần) nối tiếp nhau theo canh chừng y theo Mệnh lệnh của Allah. Quả thật, Allah không thay đổi điều gì nơi một dân tộc trừ phỉ họ tự thay đổi điều nằm nơi họ. Và một khi Allah đã quyết định xử phạt một dân tộc thì không ai có thể đảo ngược lệnh phạt đó đặng và ngoài Ngài ra, không ai là vị bảo hộ có thể che chở họ được.');
INSERT INTO "hasan_abdulkarim" VALUES(13,12,'13:12','Ngài là Đấng phơi bày cho các ngươi làn chớp sáng mà các ngươi vừa sợ vừa mừng (hy vọng); và Ngài đưa những đám mây nặng trĩu hạt mưa lên cao.');
INSERT INTO "hasan_abdulkarim" VALUES(13,13,'13:13','Và sấm sét tán dương lời ca tụng Ngài(. Và các Thiên Thần vì khiếp sợ Ngài cũng tán tụng như thế. Ngài ném những lưỡi tầm sét nghe chát chúa. Ngài dùng chúng để đánh kẻ nào Ngài muốn; và họ tranh cãi nhau về Allah. Nhưng Ngài Rất Khắc Nghiệt trong kế hoạch của Ngài.');
INSERT INTO "hasan_abdulkarim" VALUES(13,14,'13:14','Đúng ra các lời cầu nguyện chỉ đệ đạt lên riêng Ngài thôi bởi vì những kẻ mà họ cầu nguyện ngoài Ngài không đáp lại họ được tí nào cả; nó chẳng khác nào hình ảnh của một người đưa hai bàn tay ra hứng nước để đưa vào miệng nhưng nước không đến miệng của y đặng; bởi vì lời cầu nguyện của những kẻ không có đức tin chỉ đi vào lầm lạc (vô hiệu).');
INSERT INTO "hasan_abdulkarim" VALUES(13,15,'13:15','Và bất cứ ai trong các tầng trời và trái đất, dù muốn hay không, đều phải phủ phục lạy Allah; và bóng của họ cũng làm thế vào buổi sáng và buổi chiều.');
INSERT INTO "hasan_abdulkarim" VALUES(13,16,'13:16','Hãy bảo họ: “Ai là Thượng Đế (Đấng Chủ Tể) của các tầng trời và trái đất?” Hãy bảo họ: “Allah!” Hãy bảo họ: “Phải chăng các ngươi đã tôn thờ ngoài Ngài những vị bảo hộ không có khả năng tự quyết định điều lợi và điều hại cho mình?” Hãy bảo họ: “Người mù và người sáng mắt có ngang bằng nhau không? hoặc bóng tối và ánh sáng có như nhau không? hoặc phải chăng họ đã tổ hợp với Allah những kẻ ''hợp tác'' đã tạo hóa được cái giống như tạo vật của Ngài, cho nên đối với họ, việc tạo hóa đều giống nhau?” Hãy bảo họ: “Allah là Đấng Tạo Hóa tất cả vạn vật. Và Ngài là Đấng Duy nhất, Đấng Tối Thượng.”');
INSERT INTO "hasan_abdulkarim" VALUES(13,17,'13:17','Ngài cho mưa từ trên trời xuống. Do đó, những thung lũng dồn nước chảy xuống theo định lượng của chúng; tiếp đó, có những dòng thác cuốn bọt nước nổi trên mặt trôi đi nơi khác. Và từ những khoáng chất được nấu trong lò lửa để tìm kim khí hầu dùng làm đồ nữ trang và dụng cụ nấu nướng cũng có một lớp bọt tương tự. Bằng cách đó, Allah trình bày Chân Lý và sự ngụy tạo bởi vì bọt nước sẽ cuốn đi như rác rến, còn điều gì có lợi cho nhân loại thì sẽ tồn tại trên mặt đất. Allah đưa ra những ngụ ngôn như thế để răn đời.');
INSERT INTO "hasan_abdulkarim" VALUES(13,18,'13:18','(Phần thưởng) dành cho những ai đáp lại lời gọi của Thượng Đế của họ là mọi điều tốt lành. Ngược lại, những kẻ không đáp lại lời gọi của Ngài thì dẫu cho họ có tất cả những gì trên trái đất cùng với những thứ tương tự mà họ đang có để chuộc tội của họ (thì cũng vô ích thôi.) Họ là những kẻ sẽ nhận một sự phán quyết rất xấu và nhà ở của họ sẽ là hỏa ngục; và đó là một cái giường nghỉ rất tồi tệ.');
INSERT INTO "hasan_abdulkarim" VALUES(13,19,'13:19','Thế thì một người biết chắc những điều mà Thượng Đế của Ngươi đã ban xuống cho Ngươi là sự Thật có giống với một kẻ mù tịt hay không? Quả thật chỉ những người trí thức mới tiếp thu được thông điệp này:');
INSERT INTO "hasan_abdulkarim" VALUES(13,20,'13:20','Những ai làm tròn Lời Giao Ước của Allah và không nuốt lời thề;');
INSERT INTO "hasan_abdulkarim" VALUES(13,21,'13:21','Và những ai nối lại những điều mà Allah đã ra lệnh cho kết hợp lại và sợ Thượng Đế của họ và sợ một sự phán quyết xấu (về họ).');
INSERT INTO "hasan_abdulkarim" VALUES(13,22,'13:22','Và những ai kiên nhẫn tìm cách làm hài lòng Thượng Đế của họ và dâng lễ nguyện (Salah) và chi dùng một cách kín đáo hay công khai từ những vật mà TA đã ban cấp và dùng điều tốt để xóa bỏ điều xấu. Họ là những người sẽ đi đến nhà ở (tốt đẹp) của họ (ở đời sau);');
INSERT INTO "hasan_abdulkarim" VALUES(13,23,'13:23','Những Ngôi Vườn thiên đàng Vĩnh Cửu; họ sẽ đi vào đó với ai là người ngoan đạo trong số cha mẹ, vợ chồng và con cái của họ; và các Thiên Thần vào chào họ từ mỗi cánh cửa, (bảo):');
INSERT INTO "hasan_abdulkarim" VALUES(13,24,'13:24','“Sự Bằng An được ban cho quí vị về những điều mà quí vị đã nhẫn nại chịu đựng. Bởi thế, Ngôi nhà cuối cùng (của quí vị) thật tuyệt hảo!”');
INSERT INTO "hasan_abdulkarim" VALUES(13,25,'13:25','Ngược lại, những ai vi phạm Lời Giao Ước của Allah sau khi đã tuyên thệ trung thành với nó và xé làm hai những điều mà Allah đã ra lệnh cho kết hợp lại và hành động thối nát trên trái đất thì đó là những kẻ sẽ bị nguyền rủa và sẽ có một chỗ ở rất xấu (ở đời sau).');
INSERT INTO "hasan_abdulkarim" VALUES(13,26,'13:26','Allah gia tăng hay thu hẹp bổng lộc cho người nào Ngài muốn. Và họ vui thú với đời sống trần tục này nhưng đời sống trần tục này so với đời sau chỉ là một sự hưởng thụ ngắn ngủi.');
INSERT INTO "hasan_abdulkarim" VALUES(13,27,'13:27','Và những ai không có niềm tin lên tiếng: "Tại sao không có một dấu lạ nào từ Thượng Đế của Y được ban xuống cho Y?" Hãy bảo họ (hỡi Muhammad!): “Chắc chắn Allah đánh lạc hướng kẻ nào Ngài muốn và hướng dẫn về với Ngài người nào biết hối cải:');
INSERT INTO "hasan_abdulkarim" VALUES(13,28,'13:28','Những ai có đức tin và thỏa lòng với sự tưởng nhớ Allah và chắc chắn chỉ với sự tưởng nhớ Allah mà họ được thỏa lòng;');
INSERT INTO "hasan_abdulkarim" VALUES(13,29,'13:29','Những ai có đức tin và làm việc thiện thì sẽ được hạnh phúc và sẽ có một nơi trở về tốt đẹp.');
INSERT INTO "hasan_abdulkarim" VALUES(13,30,'13:30','Đúng như thế, TA đã cử Ngươi (Muhammad) đến với một cộng đồng mà trước nó đã có những cộng đồng khác lui vào dĩ vãng, mục đích để Ngươi đọc nhắc họ những điều mà TA đã mặc khải cho Ngươi bởi vì họ phủ nhận Đấng (Allah) Rất mực Độ lượng. Hãy bảo họ: “Ngài (Allah) là Thượng Đế của Ta; không có Thượng Đế nào khác duy chỉ có Ngài. Ta phó thác cho Ngài và Ta quay về hối cải với Ngài.”');
INSERT INTO "hasan_abdulkarim" VALUES(13,31,'13:31','Và giả sử có một quyển Qur''an có thể dời được núi, hoặc có thể chẻ đất ra làm hai hoặc có thể làm cho người chết nói được (thì các ngươi vẫn không tin.) Không, mọi công việc (hay mệnh lệnh) đều là của Allah cả. Há những ai có đức tin không biết rằng, nếu Allah muốn thì Ngài đã thừa khả năng hướng dẫn toàn thể nhân loại theo Chính Đạo hay sao? Nhưng những kẻ không có niềm tin không ngừng gặp tai họa về những điều mà họ đã từng làm hoặc tai họa sẽ giáng xuống gần nhà của họ nằm chờ cho đến khi lời hứa của Allah thể hiện. Chắc chắn, Allah không hề thất hứa trong Lời hứa của Ngài.');
INSERT INTO "hasan_abdulkarim" VALUES(13,32,'13:32','Và chắc chắn những Sứ Giả trước Ngươi (Muhammad) đã từng bị chế giễu nhưng TA tạm tha cho những kẻ không tin, rồi cuối cùng TA đã bắt phạt chúng. Do đó, (nhìn xem) sự trừng phạt của TA khủng khiếp dường nào!');
INSERT INTO "hasan_abdulkarim" VALUES(13,33,'13:33','Thế thì ai là Đấng Hằng ngự trên từng linh hồn về những điều mà họ đã làm ra? Nhưng họ đã qui cho Allah có những kẻ ''hợp tác''. Hãy bảo họ: “Hãy cho biết tên của chúng (nếu các ngươi nói thật). Há các ngươi muốn chỉ bảo Ngài (Allah) về những điều mà Ngài không biết trên trái đất hay sao? hoặc phải chăng đó chỉ là một lối phô trương lời nói (của các ngươi?)” Không, kế hoạch của những kẻ không tin tưởng tỏ vẻ hấp dẫn đốí với họ, nhưng họ bị cản trở xa khỏi Chính Đạo. Và ai mà Allah đánh lạc hướng thì sẽ không có người dẫn đường cho y.');
INSERT INTO "hasan_abdulkarim" VALUES(13,34,'13:34','Họ sẽ bị phạt ở đời này nhưng chắc chắn sự trừng phạt ở đời sau sẽ khắc nghiệt hơn. Và họ sẽ không có ai che chở để thoát khỏi (sự trừng phạt của) Allah.');
INSERT INTO "hasan_abdulkarim" VALUES(13,35,'13:35','Hình ảnh về thiên đàng đã được hứa ban cho những người sợ Allah giống như (Ngôi vườn) bên dưới có các dòng sông chảy; trái cây và bóng mát của nó sẽ vĩnh cửu. Đó là kết cuộc của những ai sợ Allah; ngược lại, kết cuộc của những kẻ không có niềm tin là hỏa ngục.');
INSERT INTO "hasan_abdulkarim" VALUES(13,36,'13:36','Và những ai được TA ban cho Kinh Sách (Qur''an) vui mừng về những điều đã được mặc khải cho Ngươi (Muhammad) nhưng trong các giáo phái có kẻ phủ nhận một phần của Nó. Hãy bảo họ: “Ta (Muhammad) nhận lệnh chỉ được thờ phụng riêng Allah thôi và không được phép tổ hợp những kẻ ''hợp tác'' cùng với Ngài. Ngài là Đấng Ta cầu nguyện và là Đấng Ta sẽ trở về gặp lại.”');
INSERT INTO "hasan_abdulkarim" VALUES(13,37,'13:37','Và đúng như thế, TA ban Nó (Qur''an) xuống cho Ngươi như một Bộ Luật xét xử bằng tiếng Ả Rập. Và nếu Ngươi làm theo những điều mong muốn của họ sau khi Ngươi đã tiếp thu mọi hiểu biết thì Ngươi sẽ không có một người bảo hộ hoặc một người che chở nào có thể giúp Ngươi thoát khỏi (hình phạt của) Allah.');
INSERT INTO "hasan_abdulkarim" VALUES(13,38,'13:38','Và quả thật, TA đã cử các Sứ Giả đến trước Ngươi và TA đã làm cho họ có vợ và con cái. Và không một Sứ Giả nào được phép mang đến một câu nào nếu không có sự chấp thuận của Allah. Mỗi thời kỳ đều có một Kinh Sách (hay Chỉ Dụ).');
INSERT INTO "hasan_abdulkarim" VALUES(13,39,'13:39','Allah xóa đi hoặc giữ lại điều nào Ngài muốn trong Kinh Sách (của Ngài). Và Ngài giữ Quyển Kinh Mẹ (Al-Lauh Al-Mahfuz) ở nơi Ngài.');
INSERT INTO "hasan_abdulkarim" VALUES(13,40,'13:40','Và nếu TA cho Ngươi thấy một phần của những điều mà TA đã hứa với họ (trong thời gian Ngươi còn sống) hoặc TA bắt hồn của Ngươi đi (trước khi chúng được thể hiện) thì nhiệm vụ của Ngươi chỉ là truyền đạt (Thông điệp) và nhiệm vụ của TA là xét xử.');
INSERT INTO "hasan_abdulkarim" VALUES(13,41,'13:41','Há họ không thấy rằng TA đang thu hẹp dần dần lãnh thổ của nó (xứ Ả Rập) từ vùng ngoại biên hay sao? Và khi Allah đã xét xử thì không ai có thể thu hồi phán quyết của Ngài đặng. Và Ngài Rất nhanh trong việc thanh toán.');
INSERT INTO "hasan_abdulkarim" VALUES(13,42,'13:42','Và quả thật, trước họ cũng đã có những kẻ mưu đồ, nhưng tất cả các kế hoạch đều do Allah sắp đặt (bởi vì) Ngài biết hết điều mà từng linh hồn đã làm ra. Và những kẻ không có đức tin sẽ sớm biết kết cuộc tốt đẹp sẽ thuộc về ai.');
INSERT INTO "hasan_abdulkarim" VALUES(13,43,'13:43','Và những kẻ không có đức tín bảo: “Ngươi (Muhammad) không phải là một Sứ Giả.” Hãy bảo họ: "Allah đủ làm chứng giữa Ta với các người và những ai có hiểu biết về Kinh Sách."');
INSERT INTO "hasan_abdulkarim" VALUES(14,1,'14:1','Alif. Lam. Ra. (Đây là) một Kinh Sách mà TA (Allah) ban cho Ngươi (Muhammad) để Ngươi dùng Nó đưa nhân loại từ tăm tối ra ánh sáng với sự chấp thuận của Thượng Đế (Allah) của họ, hầu đi đến con đường của Đấng Toàn Năng, Đấng Rất đáng Ca ngợi.');
INSERT INTO "hasan_abdulkarim" VALUES(14,2,'14:2','Allah là Đấng mà mọi vật trong các tầng trời và mọi vật dưới đất đều thuộc về Ngài. Và những kẻ không có đức tin (nơi Ngài) sẽ rất thống khổ về một sự trừng phạt khủng khiếp (đang chờ họ):');
INSERT INTO "hasan_abdulkarim" VALUES(14,3,'14:3','Những ai yêu đời sống trần tục này hơn đời sau và cản trở người khác không cho theo con đường của Allah và còn tìm cách bẻ cong nó. Họ là những người lầm lạc rất xa.');
INSERT INTO "hasan_abdulkarim" VALUES(14,4,'14:4','Và không một Sứ Giả nào đã được TA phái đến mà lại không nói thứ tiếng của người dân của y, mục đích để y trình bày rõ (Thông Điệp của Allah) cho họ. Sau đó, Allah đánh lạc hướng kẻ nào Ngài muốn và hướng dẫn người nào Ngài muốn, bởi vì Ngài là Đấng Toàn Năng, Đấng Rất Mực Sáng Suốt.');
INSERT INTO "hasan_abdulkarim" VALUES(14,5,'14:5','Và chắc chắn TA đã cử Musa (Môi-se) mang theo những dấu lạ của TA đến (làm theo Lệnh Phán:) “ Hãy đưa người dân của Ngươi từ tăm tối ra ánh sáng và nhắc nhở họ về những ngày( của Allah. Chắc chắn trong sự việc đó là nhứng dấu hiệu cho mỗi người kiên nhẫn và biết ơn.');
INSERT INTO "hasan_abdulkarim" VALUES(14,6,'14:6','Và (hãy nhớ) khi Musa bảo người dân của Y: “Hãy nhớ Ân huệ mà Allah đã ban cho các người khi Ngài cứu các người thoát khỏi đồng bọn của Fir-''awn (Phê-rô); chúng hành hạ các người bằng hình phạt tai ác, chúng giết con trai của các người và chỉ tha mạng phụ nữ của các người; và trong sự việc đó, là một sự thử thách ghê gớm từ Thượng Đế của các người.”');
INSERT INTO "hasan_abdulkarim" VALUES(14,7,'14:7','Và (hãy nhớ) khi Thượng Đế (Allah) của các ngươi đã công bố: Nếu các ngươi biết ơn, TA sẽ ban thêm (Ân huệ của TA) cho các ngươi; nhưng nếu các ngươi phụ ơn thì chắc chắn hình phạt của TA (đối với kẻ bội ơn) sẽ rất khủng khiếp.');
INSERT INTO "hasan_abdulkarim" VALUES(14,8,'14:8','Và Musa bảo: “Nếu các người phụ ơn, các ngươi và toàn thể nhân loại trên trái đất, thì (các ngươi nên biết rằng) chắc chắn Allah Tự Đầy Đủ, Rất Đáng Ca tụng (không cần đến ơn nghĩa của các ngươi).');
INSERT INTO "hasan_abdulkarim" VALUES(14,9,'14:9','Há các ngươi đã nghe câu chuyện của những người (đã qua đời) trước các ngươi hay chưa? Đám dân của Nuh, của ''Ad và của Thamud và của những người (đến) sau họ? Không ai biết rõ họ hơn Allah. Những Sứ Giả của họ mang theo những bằng chứng rõ rệt đã đến gặp họ, nhưng họ đưa tay vào miệng (ra vẽ căm tức) và nói: “Chúng tôi không tin tưởng nơi Thông điệp mà quí vị được cử mang đến. Và chúng tôi rất đỗi ngờ vực về điều mà quí vị mời chúng tôi chấp nhận.');
INSERT INTO "hasan_abdulkarim" VALUES(14,10,'14:10','Những vị Sứ Giả của họ bảo: “Phải chăng các người ngờ vực về Allah, Đấng đã sáng tạo các tầng trời và trái đất? Ngài kêu gọi các người (tin tưởng) để Ngài có thể tha thứ tội lỗi của các người cho các người và gia hạn cho các người sống đến một thời kỳ ấn định.” Họ đáp: “Quí vị chỉ là những người phàm như chúng tôi. Quí vị có ý định làm cho chúng tôi bỏ những vị mà cha mẹ của chúng tôi đã từng thờ phụng. Thế hãy trưng cho chúng tôi thấy một vài bằng chứng về thẩm quyền rõ rệt (của quí vị).”');
INSERT INTO "hasan_abdulkarim" VALUES(14,11,'14:11','Những vị Sứ Giả của họ bảo: “Quả thật, chúng tôi chỉ là những người phàm như các người, nhưng Allah ban Ân Huệ cho người nào Ngài muốn trong số bề tôi của Ngài. Và chúng tôi chỉ có thể trưng bày cho các người một thẩm quyền (dấu hiệu) khi nào Allah chấp thuận. Và những người có đức tin nên phó thác cho Allah.');
INSERT INTO "hasan_abdulkarim" VALUES(14,12,'14:12','“Và tại sao chúng tôi lại không tin cậy mà phó thác cho Allah bởi vì chắc chắn Ngài đã chỉ dẫn các đường lối của chúng tôi cho chúng tôi và chắc chắn chúng tôi sẽ kiên nhẫn chịu đựng sự hành hạ mà các người đã đối xử với chúng tôi. Và những người tin cậy nên phó thác cho Allah."');
INSERT INTO "hasan_abdulkarim" VALUES(14,13,'14:13','Và những kẻ không có đức tin nói với những Sứ Giả của họ: “Chắc chắn chúng tôi sẽ phải đuổi quí vị ra khỏi xứ hoặc quí vị phải trở lại với tín ngưỡng của chúng tôi.” Bởi thế, Thượng Đế của Họ đã mặc khải cho Họ: Chắc chắn TA (Allah) sẽ tiêu diệt những kẻ làm điều sai quấy.');
INSERT INTO "hasan_abdulkarim" VALUES(14,14,'14:14','Và chắc chắn TA sẽ định cư các ngươi trên trái đất sau chúng. Đó là (phần thưởng) dành cho ai là người sợ việc đứng trước mặt TA (vào Ngày Phán Xử Cuối Cùng) và sợ lời hăm dọa (trừng phạt) của TA.');
INSERT INTO "hasan_abdulkarim" VALUES(14,15,'14:15','Và chúng( cầu xin cho được thành công nhưng mỗi tên bạo ngược đều bị xử thất bại.');
INSERT INTO "hasan_abdulkarim" VALUES(14,16,'14:16','Trước mặt y sẽ là hỏa ngục và y sẽ bị bắt uống một loại nước sôi hôi thối.');
INSERT INTO "hasan_abdulkarim" VALUES(14,17,'14:17','Y sẽ uống nó từng ngụm nhưng khó nuốt xuống cổ họng. Và cái chết bao vây y tứ phía, nhưng y sẽ không chết ngay. Và trước mặt y sẽ là một sự trừng phạt khắc nghiệt.');
INSERT INTO "hasan_abdulkarim" VALUES(14,18,'14:18','Hình ảnh của những kẻ phủ nhận Thượng Đế (Allah) của họ (như sau): việc làm của họ là các đống tro tàn mà gió sẽ thổi bay đi dữ dội vào một ngày bão tố, họ sẽ không thể định đoạt được gì về những vật mà họ đã thu hoạch. Việc lạc đạo sẽ xa tít như thế.');
INSERT INTO "hasan_abdulkarim" VALUES(14,19,'14:19','Há Ngươi không nhận thấy vì Chân Lý mà Allah đã tạo hóa các tầng trời và trái đất hay sao? Nếu muốn, Ngài sẽ tiêu diệt các ngươi và làm ra một tạo hóa mới (để thay thế các ngươi).');
INSERT INTO "hasan_abdulkarim" VALUES(14,20,'14:20','Và đối với Allah, việc làm đó chẳng có gì to tát (khó khăn).');
INSERT INTO "hasan_abdulkarim" VALUES(14,21,'14:21','Và tất cả sẽ đến trình diện Allah. Rồi những người cô thế sẽ nói với những vị lãnh tụ ngạo ngược như sau: “Quả thật, chúng tôi là đệ tử của quí ngài, há quí ngài giúp chúng tôi thoát khỏi sự trừng phạt của Allah được tí nào hay chăng?” Chúng sẽ đáp: “Giá Allah đã hướng dẫn chúng tôi thì chúng tôi đã hướng dẫn quí ông lại rồi. Giờ đây dẫu chúng ta cố than van kêu gào hay ráng chịu đi nữa thì điều đó cũng bằng thừa. Bởi vì chúng ta không còn lối thoát nào khác.”');
INSERT INTO "hasan_abdulkarim" VALUES(14,22,'14:22','Và khi công việc (xét xử) đã được quyết định, Shaytan sẽ nói: “Quả thật, Allah đã giữ đúng Lời Hứa vơi các ngươi và ta cũng đã hứa nhưng thất hứa vơi các ngươi. Và ta chẳng có quyền gì đối với các ngươi ngoại trừ việc kêu gọi để dắt các ngươi (đi lạc), rồi các ngươi đã nghe theo ta. Bởi thế, chớ nên trách ta mà hãy tự trách mình. Giờ đây ta chẳng cứu được các ngươi và các ngươi cũng chẳng cứu được ta. Quả thật, ta phủ nhận việc các ngươi đã cho ta là một kẻ ''hợp tác'' với Allah trước đây. Quả thật, những kẻ sai phạm sẽ bị trừng phạt đau đớn.”');
INSERT INTO "hasan_abdulkarim" VALUES(14,23,'14:23','Còn những ai có đức tin và làm việc thiện thì sẽ được đưa vào các Ngôi Vườn (thiên đàng) bên dưới có các dòng sông chảy để vào ở trong đó đời đời theo sự chấp nhận của Thượng Đế của họ. Lời chào đón họ trong đó sẽ là ''Salam'' (Bằng an!).');
INSERT INTO "hasan_abdulkarim" VALUES(14,24,'14:24','Há Ngươi (Muhammad) đã không thấy việc Allah đưa ra một hình ảnh về một Lời nói tốt( hay sao? Nó như một loại cây tốt, rễ của nó mọc vững chắc và cành của nó vươn cao lên trời.');
INSERT INTO "hasan_abdulkarim" VALUES(14,25,'14:25','Nó cho ra trái vào bất cứ mùa nào theo Phép của Thượng Đế của nó. Allah đưa ra thí dụ so sánh cho nhân loại để may ra họ biết ghi nhớ.');
INSERT INTO "hasan_abdulkarim" VALUES(14,26,'14:26','Còn hình ảnh về một Lời nói xấu thì như là một loại cây xấu, rễ của nó nhô lên khỏi mặt đất và không đứng vững.');
INSERT INTO "hasan_abdulkarim" VALUES(14,27,'14:27','Allah với Lời nói vững chắc của Ngài sẽ củng cố vững chắc những ai tin tưởng trên trần thế này và ở đời sau. Và Allah sẽ bỏ mặc những kẻ làm điều sai quấy đi lạc bởi vì Allah làm bất cứ điều gì Ngài muốn.');
INSERT INTO "hasan_abdulkarim" VALUES(14,28,'14:28','Há Ngươi không thấy những kẻ đã đổi Ân Huệ của Allah lấy sự không tin và đưa người dân của chúng đi vào ngôi nhà tiêu diệt ư?');
INSERT INTO "hasan_abdulkarim" VALUES(14,29,'14:29','hỏa ngục mà chúng sẽ bị nướng trong đó là một nơi nghỉ rất xấu.');
INSERT INTO "hasan_abdulkarim" VALUES(14,30,'14:30','Và chúng dựng những đôi thủ ngang vai với Allah hầu mong dắt thiên hạ đi lạc khỏi con đường của Ngài. Hãy bảo chúng: “Hãy hưởng thụ đi. Rồi chắc chắn trạm đến cuối cùng của các người là hỏa ngục.”');
INSERT INTO "hasan_abdulkarim" VALUES(14,31,'14:31','(Hỡi Muhammad!) Hãy bảo các bề tôi của TA, những ai có đức tin, rằng họ phải chu đáo dâng lễ nguyện Salah và chi dùng một cách kín đáo hay công khai (để làm việc thiện) phần bổng lộc mà TA đã ban cấp trước khi xảy ra Ngày sẽ không có sự đổi chác cũng sẽ không có tình bạn hữu (giúp đỡ) nào trong Ngày đó.');
INSERT INTO "hasan_abdulkarim" VALUES(14,32,'14:32','Allah là Đấng đã tạo hóa các tầng trời và trái đất và cho mưa từ trên trời xuống mà Ngài dùng để làm mọc ra cây trái làm thực phẩm cho các ngươi và Ngài đã chế ngự tàu bè cho các ngươi để các ngươi có thể giương buồm vượt trùng dương theo sự chỉ huy của Ngài; và Ngài cũng đã chế ngự sông ngòi cho các ngươi (sử dụng).');
INSERT INTO "hasan_abdulkarim" VALUES(14,33,'14:33','Và Ngài đã chế ngự mặt trời và mặt trăng, (làm cho) cả hai di chuyển (theo qũy đạo của chúng) và Ngài đã chế ngự ban ngày và ban đêm cho các ngươi.');
INSERT INTO "hasan_abdulkarim" VALUES(14,34,'14:34','Và Ngài đã ban cho các ngươi tất cả những gì mà các ngươi yêu cầu. Và nếu đếm các Ân Huệ của Allah thì các ngươi không đếm xiết. Quả thật, con người là một kẻ làm điều sai quấy, bội ơn.');
INSERT INTO "hasan_abdulkarim" VALUES(14,35,'14:35','Và (hãy nhớ) khi Ibrahim( cầu nguyện, thưa: "Lạy Thượng Đế của bề tôi! Xin Ngài làm cho thành phố (Makkah) này thanh bình và yên ổn và giữ bề tôi và con cháu của bề tôi xa khỏi việc thờ cúng các thần tượng."');
INSERT INTO "hasan_abdulkarim" VALUES(14,36,'14:36','"Lạy Thượng Đế của bề tôi! Quả thật, chúng (những thần tượng) đã dắt nhiều người đi lạc. Bởi thế, ai tuân theo bề tôi thì sẽ lá người của bề tôi và ai bất tuân thì Ngài vẫn là Đấng Hằng Tha Thứ, Rất Mực Khoan Dung."');
INSERT INTO "hasan_abdulkarim" VALUES(14,37,'14:37','"Lạy Thượng Đế của bầy tôi! Bề tôi đã định cư con cháu( của bề tôi nơi thung lũng (Makkah) không có đất đai trồng trọt, gần Ngôi Đền thiêng của Ngài (Ka’bah) để cho chúng có thể dâng lễ nguyện Salah, thưa Thượng Đế bầy tôi. Bởi thế, xin Ngài làm cho tấm lòng của một số nhân loại đoái thương mà nuôi chúng với hoa quả (của họ) để chúng có thể tạ ân Ngài."');
INSERT INTO "hasan_abdulkarim" VALUES(14,38,'14:38','"Lạy Thượng Đế của bầy tôi! Chắc chắn Ngài biết rõ điều bầy tôi giấu giếm và điều bầy tôi bộc lộ. Bởi vì không một điều (vật) gì dưới đất và trên trời có thể giấu giếm khỏi Allah đặng."');
INSERT INTO "hasan_abdulkarim" VALUES(14,39,'14:39','"Mọi lời ca tụng và tri ân đều dâng lên Allah, Đấng đã ban cho bề tôi, dù tuổi đã già, (hai đứa con) Isma''il và Is-haaq(. Chắc chắn Thượng Đế của bề tôi là Đấng Hằng Nghe lời cầu nguyện (của các bề tôi của Ngài)."');
INSERT INTO "hasan_abdulkarim" VALUES(14,40,'14:40','"Lạy Thượng Đế của bề tôi! Xin Ngài làm cho bề tôi thành một người giữ vững lễ nguyện Salah và từ con cháu của bề tôi nữa. Lạy Thượng Đế của bầy tôi, xin Ngài chấp nhận lời cầu nguyện."');
INSERT INTO "hasan_abdulkarim" VALUES(14,41,'14:41','"Lạy Thượng Đế của bầy tôi! Xin Ngài tha thứ cho bề tôi và cha mẹ của bề tôi và những người có đức tin vào Ngày mà việc xét xử sẽ được thiết lập."');
INSERT INTO "hasan_abdulkarim" VALUES(14,42,'14:42','Và chớ nghĩ rằng Allah làm ngơ về các điều mà những kẻ sai quấy đã làm. Ngài chỉ triển hạn cho họ sống đến Ngày mà cặp mắt của họ sẽ nhìn trân tráo.');
INSERT INTO "hasan_abdulkarim" VALUES(14,43,'14:43','Kinh hãi chạy đến trước, ngẩng cổ và ngóc đầu nhìn đăm đăm, cặp mắt không quay về nhìn mình, và tấm lòng trống trải.');
INSERT INTO "hasan_abdulkarim" VALUES(14,44,'14:44','Do đó, (hỡi Muhammad!) hãy báo trước cho nhân loại biết về một Ngày mà họ sẽ chịu hình phạt. Lúc đó, những kẻ làm điều sai quấy sẽ (lạy lục Allah) thưa: “Lạy Thượng Đế của bầy tôi! Xin Ngài tạm hoãn cho bầy tôi đến một thời kỳ gần kề, bầy tôi hứa sẽ đáp lại lời gọi của Ngài và sẽ tuân theo các Sứ Giả (của Ngài)!” Sao! Há các người đã từng thề thốt trước đây rằng các người sẽ không suy tàn (chết) hay sao?');
INSERT INTO "hasan_abdulkarim" VALUES(14,45,'14:45','Và các ngươi đã tá túc trong nhà của những kẻ đã từng làm hại bản thân mình và các ngươi đã thấy TA đã đối xử với chúng như thế nào; và TA đưa ra những thí dụ để cho các ngươi (suy ngẫm).');
INSERT INTO "hasan_abdulkarim" VALUES(14,46,'14:46','Và quả thật họ đã âm mưu đặt kế hoạch nhưng kế hoạch của họ nằm nơi Allah dẫu rằng kế hoạch dữ dội đến mức có thể lay chuyển núi non.');
INSERT INTO "hasan_abdulkarim" VALUES(14,47,'14:47','Bởi thế, chớ nghĩ rằng Allah sẽ thất hứa với các Sứ Giả của Ngài. Chắc chắn Allah là Đấng Toàn Năng, nắm việc trừng phạt.');
INSERT INTO "hasan_abdulkarim" VALUES(14,48,'14:48','(Hãy cảnh cáo họ về) một Ngày mà trái đất này sẽ biến thành một trái đất khác và các tầng trời cũng thế, và (nhân loại) sẽ đến trình diện Allah, Đấng Duy Nhất, Đấng An Vị.');
INSERT INTO "hasan_abdulkarim" VALUES(14,49,'14:49','Vào Ngày đó, Ngươi sẽ thấy những kẻ tội lỗi sẽ bị trói xích với nhau.');
INSERT INTO "hasan_abdulkarim" VALUES(14,50,'14:50','Áo quần của chúng sẽ bằng hắc ín và mặt của chúng sẽ bị bao phủ bằng một lớp lửa.');
INSERT INTO "hasan_abdulkarim" VALUES(14,51,'14:51','(Làm thế) để Allah tưởng thưởng (hoặc trừng phạt) mỗi linh hồn về những điều mà họ đã thu hoạch. Chắc chắn, Allah Rất nhanh trong việc thanh toán (xét xử).');
INSERT INTO "hasan_abdulkarim" VALUES(14,52,'14:52','(Qur’an) này là Thông Điệp được ban cho nhân loại dùng để nhắc nhở và để biết rằng quả thật Ngài (Allah) là Thượng Đế Duy Nhất và để cho những người hiểu biết lưu ý cảnh giác.');
INSERT INTO "hasan_abdulkarim" VALUES(15,1,'15:1','Alif. Lam. Ra. Đây là những Câu của một Kinh Sách và là quyển Qur''an minh bạch.');
INSERT INTO "hasan_abdulkarim" VALUES(15,2,'15:2','Lắm lúc những kẻ không tin tưởng mong ước phải chi họ trở thành người Muslim.');
INSERT INTO "hasan_abdulkarim" VALUES(15,3,'15:3','Ngươi (Muhammad) hãy để mặc họ ăn uống và hưởng thụ. Và hãy để cho niềm hy vọng (hảo huyền) đánh lừa họ; rồi đây họ sẽ sớm biết.');
INSERT INTO "hasan_abdulkarim" VALUES(15,4,'15:4','Và không bao giờ TA tiêu diệt một thị trấn mà lại không (báo trước) Sắc Chỉ qui định.');
INSERT INTO "hasan_abdulkarim" VALUES(15,5,'15:5','Không một cộng đồng nào có thể ra đi sớm hơn hạn định của nó và cũng không được ở lại trễ.');
INSERT INTO "hasan_abdulkarim" VALUES(15,6,'15:6','Và họ lên tiếng bảo: "Này hỡi người nhận Thông Điệp, chắc chắn ngươi là một tên điên."');
INSERT INTO "hasan_abdulkarim" VALUES(15,7,'15:7','"Tại sao ngươi không đưa những Thiên Thần xuống gặp bọn ta, nếu ngươi là một người nói thật?"');
INSERT INTO "hasan_abdulkarim" VALUES(15,8,'15:8','TA (Allah) phái các Thiên Thần xuống chỉ với lý do chính đáng. Và lúc đó chúng sẽ không còn được triển hạn nữa.');
INSERT INTO "hasan_abdulkarim" VALUES(15,9,'15:9','Quả thật, TA đã ban Thông Điệp nhắc nhở (Qur''an) xuống và chính TA sẽ bảo quản Nó.');
INSERT INTO "hasan_abdulkarim" VALUES(15,10,'15:10','Và quả thật, trước Ngươi (Muhammad) TA đã cử các Sứ Giả đến cho những giáo phái của thời xưa.');
INSERT INTO "hasan_abdulkarim" VALUES(15,11,'15:11','Và không một Sứ Giả nào được cử đến với họ mà lại không bị chế giễu.');
INSERT INTO "hasan_abdulkarim" VALUES(15,12,'15:12','Nhưng TA làm cho nó len lỏi vào tấm lòng của những kẻ tội lỗi đúng như thế.');
INSERT INTO "hasan_abdulkarim" VALUES(15,13,'15:13','Họ không tin nơi Nó ( Qur’an) và âu đó là lề lối của những kẻ đã qua (đời) trước họ.');
INSERT INTO "hasan_abdulkarim" VALUES(15,14,'15:14','Và dù cho TA có mở cho họ một cánh cửa của bầu trời để họ tiếp tục lên đó suốt ngày,');
INSERT INTO "hasan_abdulkarim" VALUES(15,15,'15:15','Thì họ vẫn lên tiếng bảo: “Bọn ta bị quáng mắt. Không, bọn ta là đám người bị tà thuật mê hoặc.”');
INSERT INTO "hasan_abdulkarim" VALUES(15,16,'15:16','Và quả thật TA đã làm ra các chùm sao trên trời để tô điểm nó xinh đẹp cho những người ngắm nhìn.');
INSERT INTO "hasan_abdulkarim" VALUES(15,17,'15:17','Và TA bảo vệ nó khỏi bị từng tên Shaytan đáng bị tống cổ (len lỏi đột nhập).');
INSERT INTO "hasan_abdulkarim" VALUES(15,18,'15:18','Nếu có tên nào lén nghe trộm (tin tức) thì sẽ bị một chùm lửa đỏ rực rượt đánh.');
INSERT INTO "hasan_abdulkarim" VALUES(15,19,'15:19','Và TA đã trải rộng mặt đất (như một tấm thảm) mà TA đặt lên đó những quả núi vững chắc và cho mọc đủ loại cây trái theo cân lượng quân bình.');
INSERT INTO "hasan_abdulkarim" VALUES(15,20,'15:20','Và nơi đó, TA đã làm ra phương tiện sinh sống cho các ngươi và cho cả ai (sinh vật) mà các ngươi không có nhiệm vụ cung dưỡng.');
INSERT INTO "hasan_abdulkarim" VALUES(15,21,'15:21','Và không một vật gì mà kho tài nguyên lại không nằm nơi TA nhưng TA chỉ ban nó (thiên lộc) xuống theo định lượng rõ rệt.');
INSERT INTO "hasan_abdulkarim" VALUES(15,22,'15:22','Và TA gởi những luồng gió gieo tinh và cho mưa từ trên trời xuống. Rồi TA ban nó cho các ngươi dùng để uống nhưng các ngươi không phải là chủ nhân giữ kho chứa của nó.');
INSERT INTO "hasan_abdulkarim" VALUES(15,23,'15:23','Và quả thật, chính TA làm cho sống và gây cho chết và chính TA là Đấng Thừa kế.');
INSERT INTO "hasan_abdulkarim" VALUES(15,24,'15:24','Và chắc chắn, TA biết trong các ngươi ai là những người đã qua đời và ai là những người còn sống sót.');
INSERT INTO "hasan_abdulkarim" VALUES(15,25,'15:25','Và quả thật, Thượng Đế (Allah) của Ngươi sẽ cho tập trung họ lại (để phán xét). Quả thật, Ngài Thông suốt, Biết (hết mọi vấn đề).');
INSERT INTO "hasan_abdulkarim" VALUES(15,26,'15:26','Và quả thật, TA đã tạo hóa con người bằng đất sét khô, lấy từ loại đất sét nhào nặn được.');
INSERT INTO "hasan_abdulkarim" VALUES(15,27,'15:27','Và Jaann (tổ tiên của loài Jinn), TA đã tạo nó trước đây bằng hơi lửa.');
INSERT INTO "hasan_abdulkarim" VALUES(15,28,'15:28','Và (hãy nhớ lại) khi Thượng Đế (Allah) của Ngươi (Muhammad) đã bảo các Thiên Thần: “TA sắp tạo một người phàm làm bằng đất sét khô, lấy từ đất sét nhào nặn được.');
INSERT INTO "hasan_abdulkarim" VALUES(15,29,'15:29','Rồi khi TA uốn nắn Y (Adam) thành hình thể và thổi vào Y Ruh (Tinh thần) của TA, các ngươi hãy sụp lạy Y.');
INSERT INTO "hasan_abdulkarim" VALUES(15,30,'15:30','Do đó, tất cả các Thiên Thần đồng quỳ lạy.');
INSERT INTO "hasan_abdulkarim" VALUES(15,31,'15:31','Ngoại trừ Iblis. Nó từ chối, không chịu nhập chung với những vị phủ phục.');
INSERT INTO "hasan_abdulkarim" VALUES(15,32,'15:32','(Allah) bảo: “Hỡi Iblis nhà ngươi có chuyện gì, tại sao nhà ngươi không chịu nhập chung với những vị phủ phục?”');
INSERT INTO "hasan_abdulkarim" VALUES(15,33,'15:33','(Iblis đáp) thưa: “Bề tôi không phải là kẻ quỳ lạy một người phàm mà Ngài đã tạo bằng đất sét khô lấy từ đất sét nhào nặn được.”');
INSERT INTO "hasan_abdulkarim" VALUES(15,34,'15:34','(Allah) phán: Hãy đi ra khỏi đó (Vườn Trời); nhà ngươi là một tên đáng bị trục xuất.');
INSERT INTO "hasan_abdulkarim" VALUES(15,35,'15:35','Và chắc chắn nhà ngươi sẽ bị nguyền rủa cho đến Ngày Phán xử.');
INSERT INTO "hasan_abdulkarim" VALUES(15,36,'15:36','(Iblis) thưa: “Lạy Thượng Đế (Allah) của bề tôi! Xin Ngài triển hạn cho bề tôi tận đến Ngày (người chết) sẽ được dựng sống lại.”');
INSERT INTO "hasan_abdulkarim" VALUES(15,37,'15:37','(Allah) phán: Vậy thì nhà ngươi là một tên được triển hạn.');
INSERT INTO "hasan_abdulkarim" VALUES(15,38,'15:38','Cho đến Ngày của thời gian qui định.');
INSERT INTO "hasan_abdulkarim" VALUES(15,39,'15:39','(Iblis) thưa: “Lạy Thượng Đế (Allah) của bề tôi! Bởi vì Ngài xua đuổi bề tôi nên bề tôi sẽ tô điểm nó (tội lỗi) thành xinh đẹp đối với chúng (nhân loại) trên trái đất và bề tôi sẽ làm cho toàn thể bọn chúng lầm lạc."');
INSERT INTO "hasan_abdulkarim" VALUES(15,40,'15:40','“Ngoại trừ ai trong bọn chúng là bề tôi trung trinh của Ngài.”');
INSERT INTO "hasan_abdulkarim" VALUES(15,41,'15:41','(Allah) phán: Đây là con đường ngay thẳng dẫn đến TA.');
INSERT INTO "hasan_abdulkarim" VALUES(15,42,'15:42','Quả thật, nhà ngươi không có quỵền đối với các bề tôi của TA ngoại trừ ai trong bọn họ là những kẻ lầm lạc tuân theo ngươi.');
INSERT INTO "hasan_abdulkarim" VALUES(15,43,'15:43','Và quả thật, hỏa ngục là điểm hẹn cho toàn thể bọn chúng.');
INSERT INTO "hasan_abdulkarim" VALUES(15,44,'15:44','Nó có bảy cửa đi vào. Đi vào mỗi cửa sẽ là một loại (tội nhân) nhất định của bọn chúng.');
INSERT INTO "hasan_abdulkarim" VALUES(15,45,'15:45','Quả thật, những người sợ Allah sẽ ở trong các Ngôi Vườn (thiên đàng) có suối nước trong.');
INSERT INTO "hasan_abdulkarim" VALUES(15,46,'15:46','(Lời nghinh đón họ sẽ là:) “Quí vị hãy bước vào (thiên đàng) với sự Bằng an Yên ổn.”');
INSERT INTO "hasan_abdulkarim" VALUES(15,47,'15:47','Và TA sẽ tẩy xóa nỗi oán thù khỏi lòng họ; họ sẽ là anh em với nhau, nằm nghỉ trên những chiếc tràng kỷ, mặt nhìn mặt (vui vầy).');
INSERT INTO "hasan_abdulkarim" VALUES(15,48,'15:48','Trong đó, họ sẽ không cồn cảm giác mệt mỏi và không bị mời ra.');
INSERT INTO "hasan_abdulkarim" VALUES(15,49,'15:49','(Hỡi Muhammad) hãy báo cho bề tôi của TA biết (rằng) TA đây là Đấng Hằng Tha Thứ, Rất Mực Khoan Dung.');
INSERT INTO "hasan_abdulkarim" VALUES(15,50,'15:50','Nhưng sự trừng phạt của TA cũng sẽ rất đau đớn.');
INSERT INTO "hasan_abdulkarim" VALUES(15,51,'15:51','Và hãy báo cho họ (mọi người) biết về những vị khách của Ibrahim.');
INSERT INTO "hasan_abdulkarim" VALUES(15,52,'15:52','Khi vào gặp Người, họ lên tiếng chào: “Bằng an” Y đáp: Và hãy báo cho họ (mọi người) biết về những vị khách của Ibrahim. "Chúng tôi sợ quí vị."');
INSERT INTO "hasan_abdulkarim" VALUES(15,53,'15:53','Họ bảo: "Đừng sợ! Chúng tôi mang tin mừng đến cho ông về một đứa con trai được ban cho kiến thức."');
INSERT INTO "hasan_abdulkarim" VALUES(15,54,'15:54','(Ibrahim) đáp: “Phải chăng quí vị mang tin mừng đến cho tôi khi tôi đã về già? Vậy tin mừng về việc gì vậy?”');
INSERT INTO "hasan_abdulkarim" VALUES(15,55,'15:55','Họ bảo: “Chúng tôi mang tin mừng đến cho ông thật đấy. Bởi thế, chớ nên tuyệt vọng.”');
INSERT INTO "hasan_abdulkarim" VALUES(15,56,'15:56','(Ibrahim) đáp: “Và những ai tuyệt vọng nơi lòng Khoan Dung của Thượng Đế của mình thì đó là những kẻ lầm lạc?”');
INSERT INTO "hasan_abdulkarim" VALUES(15,57,'15:57','(Ibrahim) tiếp: “Vậy quí vị đến gặp tôi có chuyện gì, hỡi các vị Sứ (của Allah?)”');
INSERT INTO "hasan_abdulkarim" VALUES(15,58,'15:58','Họ bảo: "Quả thật, chúng tôi được phái cử đến với một đám người tội lỗi."');
INSERT INTO "hasan_abdulkarim" VALUES(15,59,'15:59','"Ngoại trừ gia đình của Lut mà chúng tôi có nhiệm vụ phải giải cứu tất cả."');
INSERT INTO "hasan_abdulkarim" VALUES(15,60,'15:60','“Trừ người vợ của Y (Lut) mà chúng tôi đã quyết định sẽ nằm trong số người tri trệ đằng sau.”');
INSERT INTO "hasan_abdulkarim" VALUES(15,61,'15:61','Bởi thế, khi những vị Sứ (của Allah) vào gặp gia đình của Lut,');
INSERT INTO "hasan_abdulkarim" VALUES(15,62,'15:62','Y bảo: “Quả thật quí vị là những người lạ mặt.”');
INSERT INTO "hasan_abdulkarim" VALUES(15,63,'15:63','Họ đáp: “Không, chúng tôi đến gặp ông để giải quyết điều (trừng phạt) mà những kẻ không tin hãy còn nghi ngờ."');
INSERT INTO "hasan_abdulkarim" VALUES(15,64,'15:64','“Và chúng tôi mang sự thật (về sự trừng phạt) đến cho ông. Và chúng tôi là những người nói thật."');
INSERT INTO "hasan_abdulkarim" VALUES(15,65,'15:65','“Do đó, Ông và gia đình hãy ra đi vào phần cuối của ban đêm và đi đằng sau họ và chớ để cho bất cứ ai trong gia đình của ông ngoái nhìn lại phía sau và tiếp tục đi đến nơi mà các ông đã được truyền.”');
INSERT INTO "hasan_abdulkarim" VALUES(15,66,'15:66','Và TA cho Y biết quyết định đó rằng số người (tội lỗi) còn ở lại sau sẽ bị tiêu diệt trọn bộ vào buổi sáng.');
INSERT INTO "hasan_abdulkarim" VALUES(15,67,'15:67','Và người dân trong thành phố vui mừng chạy ùa đến (nhà của Lut).');
INSERT INTO "hasan_abdulkarim" VALUES(15,68,'15:68','(Lut) bảo (chúng): "Đây là những vị khách của ta, bởi thế chớ làm cho ta mất mặt (với họ)."');
INSERT INTO "hasan_abdulkarim" VALUES(15,69,'15:69','“Hãy sợ Allah và chớ hạ nhục ta!”');
INSERT INTO "hasan_abdulkarim" VALUES(15,70,'15:70','(Người dân của Lut) đáp: “Há chúng tôi đã không cấm ông tiếp xúc với mọi người ư?”');
INSERT INTO "hasan_abdulkarim" VALUES(15,71,'15:71','(Lut) bảo: “Đây, các đứa con gái của ta, nếu các người muốn hành động (theo ý của các người.)');
INSERT INTO "hasan_abdulkarim" VALUES(15,72,'15:72','(TA thề) bằng sinh mạng của Ngươi (hỡi Muhammad!) vì cuồng dâm mà chúng quanh quấn (như thế).');
INSERT INTO "hasan_abdulkarim" VALUES(15,73,'15:73','Do đó, tiếng gầm dữ dội (của trận động đất) đã chụp bắt chúng vào lúc rạng đông.');
INSERT INTO "hasan_abdulkarim" VALUES(15,74,'15:74','Tiếp đó, TA lật ngược chúng ngổn ngang và đổ lên chúng những trận mưa đá diêm sinh cứng như đất sét nung.');
INSERT INTO "hasan_abdulkarim" VALUES(15,75,'15:75','Chắc chắn trong biến cố đó, có những dấu hiệu cho những ai sáng suốt.');
INSERT INTO "hasan_abdulkarim" VALUES(15,76,'15:76','Và quả thật, chúng (những thị trấn sụp đổ) hãy còn nằm trên các con đường qua lại (giữa Makkah và Syria).');
INSERT INTO "hasan_abdulkarim" VALUES(15,77,'15:77','Trong biến cố đó là một dấu hiệu cho những người có đức tin.');
INSERT INTO "hasan_abdulkarim" VALUES(15,78,'15:78','Và quả thật, đám người dân của khu rừng Aykah cũng là những kẻ làm điều sai quấy.');
INSERT INTO "hasan_abdulkarim" VALUES(15,79,'15:79','Do đó, TA đã trừng phạt chúng. Và chắc chắn hai (thị trấn của) chúng (còn để lại di tích) trên một xa lộ thấy rõ.');
INSERT INTO "hasan_abdulkarim" VALUES(15,80,'15:80','Và chắc chắn người dân của vùng núi Hijr đã phủ nhận những vị Sứ đã được cử đến (với họ).');
INSERT INTO "hasan_abdulkarim" VALUES(15,81,'15:81','Và TA đã trình bày cho họ những dấu hiệu của TA nhưng họ quay mặt cự tuyệt.');
INSERT INTO "hasan_abdulkarim" VALUES(15,82,'15:82','Và họ đã đục núi làm nhà (và cảm thấy) an toàn.');
INSERT INTO "hasan_abdulkarim" VALUES(15,83,'15:83','Nhưng tiếng gầm dữ dội đã chụp bắt họ vào buổi sáng.');
INSERT INTO "hasan_abdulkarim" VALUES(15,84,'15:84','Và những công trình mà họ đã làm ra chẳng giúp họ được gì.');
INSERT INTO "hasan_abdulkarim" VALUES(15,85,'15:85','Và TA đã tạo hóa các tầng trời và trái đất và mọi thứ giữa trời đất chỉ vì Chân Lý. Và Giờ (Phán xử) chắc chắn sẽ xảy đến. Do đó, hãy bỏ qua (lỗi lầm của họ) một cách lịch sự.');
INSERT INTO "hasan_abdulkarim" VALUES(15,86,'15:86','Quả thật, Thượng Đế của Ngươi là Đấng Tạo Hóa, Đấng Toàn Tri.');
INSERT INTO "hasan_abdulkarim" VALUES(15,87,'15:87','Và TA đã ban cho Ngươi (Muhammad) bảy Câu Kinh nhắc đi nhắc lại và Quyển Qur''an vĩ đại.');
INSERT INTO "hasan_abdulkarim" VALUES(15,88,'15:88','Nhưng chớ trố mắt nhìn những món vật mà TA đã ban cho những tầng lớp của họ hưởng thụ, cũng chớ buồn rầu giùm cho họ mà hãy rủ lòng thương của Ngươi xuống cho những người có đức tin.');
INSERT INTO "hasan_abdulkarim" VALUES(15,89,'15:89','Và hãy bảo họ: “Quả thật Ta đây là một ngươi Báo trước công khai.”');
INSERT INTO "hasan_abdulkarim" VALUES(15,90,'15:90','Giống như cái (hình phạt) mà TA đã giáng lên những kẻ chia rẽ:');
INSERT INTO "hasan_abdulkarim" VALUES(15,91,'15:91','Những ai đã chia mảnh quyển Qur''an.');
INSERT INTO "hasan_abdulkarim" VALUES(15,92,'15:92','Bởi thế, nhân danh Thượng Đế của Ngươi, Ngài sẽ hạch hỏi tất cả bọn chúng.');
INSERT INTO "hasan_abdulkarim" VALUES(15,93,'15:93','Về công việc mà chúng đã từng làm.');
INSERT INTO "hasan_abdulkarim" VALUES(15,94,'15:94','Do đó, hãy công bố những điều mà Ngươi đã được chỉ thị và hãy lánh xa những kẻ tôn thờ đa thần.');
INSERT INTO "hasan_abdulkarim" VALUES(15,95,'15:95','Quả thật, TA đủ giúp Ngươi chống lại những kẻ nhạo báng:');
INSERT INTO "hasan_abdulkarim" VALUES(15,96,'15:96','Những ai dựng một thần linh cùng với Allah; và rồi đây chúng sẽ sớm biết.');
INSERT INTO "hasan_abdulkarim" VALUES(15,97,'15:97','Và quả thật, TA biết lòng Ngươi se lại vì những điều chúng đã nói;');
INSERT INTO "hasan_abdulkarim" VALUES(15,98,'15:98','Nhưng hãy tán dương lời ca tụng Thượng Đế của Ngươi và trở thành một người phủ phục (Allah).');
INSERT INTO "hasan_abdulkarim" VALUES(15,99,'15:99','Và hãy thờ phụng Thượng Đế của Ngươi cho đến khi điều kiên định (cái chết) xảy đến cho Ngươi.');
INSERT INTO "hasan_abdulkarim" VALUES(16,1,'16:1','Allah sắp ban hành mệnh lệnh. Bởi thế, các ngươi chớ giục cho mau đến. Quang vinh và trong sạch thay Ngài! Ngài Tối Cao, vượt khỏi những điều chúng tổ hợp (với Ngài).');
INSERT INTO "hasan_abdulkarim" VALUES(16,2,'16:2','Ngài phái các Thiên Thần xuống (trần) theo Mệnh Lệnh của Ngài mang sự Mặc Khải đến cho người nào mà Ngài muốn trong số bầy tôi củaa Ngài (và bảo:) “Hãy cảnh cáo (loài người) rằng không có Thượng Đế nào khác cả duy chỉ có TA, bởi thế hãy sợ TA.”');
INSERT INTO "hasan_abdulkarim" VALUES(16,3,'16:3','Vì Chân Lý, Ngài đã tạo các tầng trời và trái đất. Ngài Tối Cao, vượt khỏi những điều chúng tổ hợp (với Ngài).');
INSERT INTO "hasan_abdulkarim" VALUES(16,4,'16:4','Ngài đã tạo con người từ một giọt tinh dịch nhưng y lại là một đối thủ công khai (chống lại Ngài).');
INSERT INTO "hasan_abdulkarim" VALUES(16,5,'16:5','Và gia súc. Ngài đã tạo chúng cho các ngươi (sử dụng). Nơi chúng có các vật dụng phủ ấm và nhiều nguồn lợi khác. Chẳng hạn thịt của chúng các ngươi (dùng để) ăn.');
INSERT INTO "hasan_abdulkarim" VALUES(16,6,'16:6','Và nơi chúng có một vẽ đẹp cho các ngươi khi các ngươi lùa chúng về chuồng vào buổi chiều và đánh chúng ra đồng ăn cỏ vào buổi sáng.');
INSERT INTO "hasan_abdulkarim" VALUES(16,7,'16:7','Và chúng chuyên chở hàng nặng cho các ngươi đến các thành phố (xa xôi), những nơi mà các ngươi phải vất vả lắm mới có thể đến nơi đặng. Chắc chắn Thượng Đế của các người là Đấng Rất Mực Nhân Từ, Rất Mực Khoan Dung.');
INSERT INTO "hasan_abdulkarim" VALUES(16,8,'16:8','Và loài ngựa, la và lừa mà (Ngài đã tạo) cho các ngươi để cưỡi và trang trí. Và Ngài đã tạo những vật mà các ngươi không biết.');
INSERT INTO "hasan_abdulkarim" VALUES(16,9,'16:9','Và Allah có nhiệm vụ chỉ con đường ngay chính (cho mọi người) nhưng có những con đường lệch khỏi Chính Đạo; và nếu muốn, Ngài thừa sức hướng dẫn tất cả đi đúng đường.');
INSERT INTO "hasan_abdulkarim" VALUES(16,10,'16:10','Ngài là Đấng đã ban nước mưa từ trên trời xuống. Từ đó các ngươi có nước uống, và nhờ đó cây cỏ mọc ra nơi cánh đồng cho gia súc ăn.');
INSERT INTO "hasan_abdulkarim" VALUES(16,11,'16:11','Ngài dùng nó để làm mọc ra hoa mầu, và trái ô liu, và trái chà là, và trái nho, và đủ loại trái cây cho các ngươi dùng. Chắc chắn trong sự việc đó là một dấu hiệu cho một đám người biết ngẫm nghĩ.');
INSERT INTO "hasan_abdulkarim" VALUES(16,12,'16:12','Và Ngài đã chế ngự ban đêm và ban ngày, mặt trời và mặt trăng và các ngôi sao cho các ngươi theo Mệnh Lệnh của Ngài; chắc chắn trong sự việc đó là những dấu hiệu cho một đám người thấu hiểu.');
INSERT INTO "hasan_abdulkarim" VALUES(16,13,'16:13','Và những vật trên đất liền mà Ngài đã làm ra dồi dào cho các ngươi (sử dụng) mang nhiều mầu sắc (và phẩm chất) khác biệt. Chắc chắn trong sự việc này là một dấu hiệu cho một đám người ghi nhớ.');
INSERT INTO "hasan_abdulkarim" VALUES(16,14,'16:14','Và Ngài đã chế ngự biển cả mà các ngươi có các món thịt tươi để ăn và các món trang sức để đeo. Và ngươi (hỡi người!) thấy tàu bè cày sóng (vượt trùng dương) giúp các ngươi đi tìm thiên lộc của Ngài và để cho các ngươi tạ ơn.');
INSERT INTO "hasan_abdulkarim" VALUES(16,15,'16:15','Và Ngài đã đặt những quả núi vững chắc lên trên mặt đất (giữ nó thăng bằng) sợ rằng nó rung chuyển (chồng chành) với các ngươi; và (Ngài đã tạo) sông ngòi và đường sá để các ngươi đi đúng hướng.');
INSERT INTO "hasan_abdulkarim" VALUES(16,16,'16:16','Và (Ngài đã tạo) những cảnh giới (để làm dấu). Và nhờ các ngôi sao (trên trời) mà họ (nhân loại) định được hướng đi (đúng mục tiêu).');
INSERT INTO "hasan_abdulkarim" VALUES(16,17,'16:17','Thế phải chăng Đấng Tạo Hóa bằng với kẻ không tạo hay sao? Thế các ngươi không chịu tỉnh ngộ hay sao?');
INSERT INTO "hasan_abdulkarim" VALUES(16,18,'16:18','Và nếu có đếm các Ân Huệ của Allah thì các ngươi không thể đếm xiết. Quả thật, Allah Hằng Tha Thứ, Rất Mực Khoan Dung.');
INSERT INTO "hasan_abdulkarim" VALUES(16,19,'16:19','Và Allah biết điều các ngươi giấu giếm và tiết lộ.');
INSERT INTO "hasan_abdulkarim" VALUES(16,20,'16:20','Và những kẻ (thần linh) mà họ cầu nguyện ngoài Allah đã không tạo hóa được cái gì trong lúc chính chúng lại được tạo ra.');
INSERT INTO "hasan_abdulkarim" VALUES(16,21,'16:21','(Đó là những vật) chết chứ không sống. Chúng cũng không biết khi nào chúng sẽ được dựng sống lại.');
INSERT INTO "hasan_abdulkarim" VALUES(16,22,'16:22','Thượng Đế của các ngươi là một Thượng Đế Duy nhất. Bởi thế, những ai không tin tưởng nơi đời sau thì lòng họ ương ngạnh và kiêu căng.');
INSERT INTO "hasan_abdulkarim" VALUES(16,23,'16:23','Không một chút nghi ngờ nào, chắc chắn Allah biết rõ điều họ giấu giếm và bộc lộ. Quả thật, Ngài không yêu thương những kẻ tự phụ, kiêu căng.');
INSERT INTO "hasan_abdulkarim" VALUES(16,24,'16:24','Và khi có lời bảo họ: “Điều mà Thượng Đế của các ngươi đã ban xuống (cho Muhammad) là gì vậy?” Họ (mỉa mai) đáp: “Chuyện ngụ ngôn của người cổ xưa!”');
INSERT INTO "hasan_abdulkarim" VALUES(16,25,'16:25','(Kết quả) là họ sẽ mang trọn gánh nặng (tội lỗi) của họ vào Ngày phục sinh và luôn cả gánh nặng của những ai vì thiếu hiểu biết mà đã bị họ dắt đi lạc. Thật xấu xa thay gánh nặng họ sẽ phải mang.');
INSERT INTO "hasan_abdulkarim" VALUES(16,26,'16:26','Chắc chắn trước họ đã có những kẻ âm mưu (phá hoại Chính Đạo của Allah) nhưng Allah đến phá vỡ nền móng kiến trúc của chúng làm cho mái nhà từ bên trên đổ sụp xuống đè lên chúng và hình phạt đến bắt chúng từ những nơi mà chúng không hề nhận ra.');
INSERT INTO "hasan_abdulkarim" VALUES(16,27,'16:27','Rồi vào Ngày phục sinh, Ngài sẽ hạ nhục chúng và bảo: Đâu là những kẻ ''hợp tác'' của TA, những kẻ vì chúng mà các ngươi thường tranh chấp (với các tín đồ của TA)?” Những ai được ban cho kiến thức sẽ lên tiếng: “Ngày nay những kẻ không có đức tin chắc chắn sẽ bị hạ nhục và chịu họa."');
INSERT INTO "hasan_abdulkarim" VALUES(16,28,'16:28','Những ai mà các Thiên Thần bắt hồn trong lúc chúng tự làm hại bản thân (linh hồn) mình. Rồi chúng (giả vờ) chịu thần phục, (thưa): “Thật tình chúng tôi đã không làm điều gì tội lỗi.” (Thiên Thần sẽ bảo:) “Vâng, quả thật Allah biết rõ điều các ngươi đã từng làm."');
INSERT INTO "hasan_abdulkarim" VALUES(16,29,'16:29','“Thôi, hãy bước vào các cửa của hỏa ngục để vào ở trong đó. Bởi thế, chỗ ngụ của những kẻ kiêu căng thật là xấu xa.”');
INSERT INTO "hasan_abdulkarim" VALUES(16,30,'16:30','Và có lời bảo những ai sợ Allah (như sau:) “Điều mà Thượng Đế (Allah) của các người đã ban xuống là gì?” Họ đáp: “Điều tốt.” Dành cho những ai làm điều lành ở đời này thì sẽ gặp lành. Và nhà ở (của họ) ở đời sau sẽ tốt hơn nữa. Và chắc chắn nhà ở của những người Sợ Allah sẽ tuyệt hảo!');
INSERT INTO "hasan_abdulkarim" VALUES(16,31,'16:31','Những Ngôi vườn thiên đàng Vĩnh Cửu mà họ sẽ vào, bên dưới có các dòng sông chảy . Trong đó họ sẽ được tất cả các thứ mong ước. Allah sẽ tưởng thưởng những ai ngay chính sợ Allah đúng như thế.');
INSERT INTO "hasan_abdulkarim" VALUES(16,32,'16:32','Những ai mà các Thiên Thần bắt hồn trong tình trạng tốt sạch, (Thiên Thần) chúc họ: “Mong quí vị được bằng an. Xin mời quí vị vào thiên đàng do những điều (tốt) mà quí vị đã từng làm (trên thế gian).”');
INSERT INTO "hasan_abdulkarim" VALUES(16,33,'16:33','Phải chăng chúng (những kẻ không tin) chờ các Thiên Thần đến (bắt hồn) hay chờ Lệnh phán của Thượng Đế (Allah) của Ngươi (Muhammad) đến (xử lý) chúng? Những kẻ trước chúng cũng đã xử sự đúng như thế. Và Allah đã không làm hại chúng mà chính chúng tự làm hại bẳn thân (linh hồn) mình mà thôi.');
INSERT INTO "hasan_abdulkarim" VALUES(16,34,'16:34','Bỏi thế, những điều tội lỗi mà chúng đã từng làm sẽ đến chụp bắt chúng và những điều mà chúng đã từng chế giễu sẽ vây hãm chúng trở');
INSERT INTO "hasan_abdulkarim" VALUES(16,35,'16:35','Và những kẻ thờ đa thần lên tiếng: “Nếu Allah muốn, chúng tôi lẫn cha mẹ của chúng tôi đâu có tôn thờ bất cứ thứ gì ngoài Ngài, và chúng tôi đã không cấm cản bất cứ điều gì ngoài điều mà Ngài đã cấm.” Những kẻ trước chúng cũng đã làm đúng như thế. Vậy chẳng phải nhiệm vụ của những Sứ Giả (của Allah) chỉ là công khai truyền đạt (Thông Điệp của Allah) sao?');
INSERT INTO "hasan_abdulkarim" VALUES(16,36,'16:36','Và quả thật, TA đã cử một Sứ Giả đến cho mỗi cộng đồng (với Mệnh Lệnh): “Hãy thờ phụng Allah và tránh xa Tà thần. Bởi thế, trong họ có kẻ được Allah hướng dẫn và có kẻ không tránh khỏi đi lạc. Do đó, hãy đi du lịch trên trái đất để quan sát kết cuộc của những kẻ đã phủ nhận Sự Thật xẩy ra như thế nào.');
INSERT INTO "hasan_abdulkarim" VALUES(16,37,'16:37','Nếu Ngươi (Muhammad) muốn hướng dẫn họ thì quả thật Allah không hướng dẫn ai mà Ngài đánh lạc hướng. Và họ sẽ không được ai giúp đỡ.');
INSERT INTO "hasan_abdulkarim" VALUES(16,38,'16:38','Và họ thề nhân danh Allah bằng những lời thề nghiêm trọng nhất, rằng Allah sẽ không phục sinh ai đã chết. Vâng, (Allah sẽ phục sinh y), một lời hứa mà Ngài bắt buộc phải thi hành, nhưng đa số nhân loại không biết.');
INSERT INTO "hasan_abdulkarim" VALUES(16,39,'16:39','(Allah sẽ phục sinh người chết) để Ngài chứng minh cho họ thấy sự thật về điều mà họ đã từng bất đồng ý kiến và để cho những kẻ không tin tưởng biết chính họ mới là những kẻ nói dối.');
INSERT INTO "hasan_abdulkarim" VALUES(16,40,'16:40','Quả thật, Lời Phán của TA đối với một vật mà TA muốn nó thành thì TA chỉ bảo với nó: “Hãy Thành!” thì nó sẽ thành.');
INSERT INTO "hasan_abdulkarim" VALUES(16,41,'16:41','Và những ai đã vì Allah mà di cư đi tị nạn sau khi bị đối xử bất công thì chắc chắn sẽ được TA cho định cư tại một nơi tốt đẹp trên thế gian và phần thưởng dành cho họ ở đời sau sẽ lớn hơn nữa nếu họ biết (điều đó):');
INSERT INTO "hasan_abdulkarim" VALUES(16,42,'16:42','Những ai kiên nhẫn và phó thác (công việc của họ) cho Thượng Đế (Allah) của họ.');
INSERT INTO "hasan_abdulkarim" VALUES(16,43,'16:43','Và trước Ngươi (Muhammad), TA chỉ cử những người phàm mà TA đã mặc khải (Kinh Sách) đến làm Sứ Giả. Nếu các ngươi không biết (điều gì về Islam) thì hãy hỏi những người hiểu biết (các học giả Islam).');
INSERT INTO "hasan_abdulkarim" VALUES(16,44,'16:44','(Các Sứ Giả đến) với những bằng chứng rõ rệt và với Kinh Sách. Và TA cũng đã ban cho Ngươi Lời Nhắc Nhở (Kinh Qur''an) để Ngươi giải thích rõ cho mọi người biết về những điều mà TA đã ban xuống cho họ và để họ ngẫm nghĩ.');
INSERT INTO "hasan_abdulkarim" VALUES(16,45,'16:45','Há những kẻ âm mưu điều tội lỗi cảm thấy an toàn chăng nếu Allah sẽ làm cho đất nuốt mất họ đi hoặc hình phạt sẽ đến với họ từ đâu đó mà họ không nhận ra?');
INSERT INTO "hasan_abdulkarim" VALUES(16,46,'16:46','Hoặc Ngài sẽ trừng phạt họ trong lúc họ đang di chuyển qua lại đến nỗi họ sẽ không tránh khỏi đặng?');
INSERT INTO "hasan_abdulkarim" VALUES(16,47,'16:47','Hoặc Ngài sẽ túm bắt họ bằng cách làm cho họ suy giảm từ từ? Nhưng quả thật, Thượng Đế (Allah) của Ngươi Rất Mực Nhân Từ, Rất Mực Khoan Dung.');
INSERT INTO "hasan_abdulkarim" VALUES(16,48,'16:48','Há họ không quan sát vạn vật mà Allah đã tạo, xem bóng của mỗi vật tự động quay sang phải và trái, và quỳ lạy Allah trong lúc chúng hạ mình khiêm tốn như thế nào ư?');
INSERT INTO "hasan_abdulkarim" VALUES(16,49,'16:49','Và bất cứ sinh vật nào trong các tầng trời và dưới đất và cả Thiên Thần nữa đều phủ phục Allah và không hề tỏ vẻ kiêu căng.');
INSERT INTO "hasan_abdulkarim" VALUES(16,50,'16:50','Chúng sợ Thượng Đế (Allah) ở bên trên chúng và làm bất cứ điều gì mà Ngài phán truyền.');
INSERT INTO "hasan_abdulkarim" VALUES(16,51,'16:51','Và Allah đã phán: “Chớ tôn thờ hai thần linh. Quả thật, Ngài (Allah) chỉ là một Thượng Đế Duy nhất. Do đó, hãy sợ TA! (riêng TA thôi, hỡi nhân loại!)”');
INSERT INTO "hasan_abdulkarim" VALUES(16,52,'16:52','Và bất cứ cái gì trong các tầng trời và dưới đất đều thuộc về Ngài cả; và phụng mệnh Ngài trong tôn giáo là điều bắt buộc. Thế các ngươi sợ sệt kẻ khác thay vì Allah ư?');
INSERT INTO "hasan_abdulkarim" VALUES(16,53,'16:53','Và bất cứ ân huệ nào mà các ngươi hưởng thụ đều do Allah ban cho cả. Rồi khi các ngươi gặp hoạn nạn, các ngươi kêu cứu với Ngài.');
INSERT INTO "hasan_abdulkarim" VALUES(16,54,'16:54','Rồi, khi Ngài giải cứu các ngươi thoát nạn thì một phần tử trong các ngươi lại tổ hợp những kẻ (hợp tác) khác cùng với Thượng Đế của họ.');
INSERT INTO "hasan_abdulkarim" VALUES(16,55,'16:55','(Mục đích) để họ có thể phủ nhận những Ân huệ mà TA đã ban cho. Thế, hãy hưởng thụ đi. Rồi đây, các ngươi sẽ sớm biết (hậu quả).');
INSERT INTO "hasan_abdulkarim" VALUES(16,56,'16:56','Và họ trích ra một phần bổng lộc mà TA đã ban cấp để dâng cho những kẻ mà họ không biết. Này, nhân danh Allah, chắc chắn các ngươi sẽ bị chất vấn về những điều mà các ngươi đã bịa đặt.');
INSERT INTO "hasan_abdulkarim" VALUES(16,57,'16:57','Và họ đặt chuyện (nói) Allah có con gái. Quang vinh và trong sạch thay Ngài! Và những điều mà họ mong muốn chỉ là cho họ mà thôi.');
INSERT INTO "hasan_abdulkarim" VALUES(16,58,'16:58','Và khi một người trong bọn nhận được tin tức về một đứa bé gái (vừa mới chào đời), gương mặt của y sa sầm và nỗi lòng buồn rầu vô hạn.');
INSERT INTO "hasan_abdulkarim" VALUES(16,59,'16:59','Y nhục nhã lẫn trốn thiên hạ vì hung tin vừa mới nhận. Y phải giữ nó (đứa bé gái) lại trong tủi nhục hay phải chôn sống nó dưới đất? Ôi xót xa thay điều mà y quyết định!');
INSERT INTO "hasan_abdulkarim" VALUES(16,60,'16:60','Đối với những ai không tin tưởng nơi đời sau thì như là một hình ảnh so sánh xấu xa; ngược lại, Allah là một hình ảnh so sánh cao quí. Bởi vì Ngài là Đấng Toàn Năng, Đấng Rất Mực Sáng Suốt.');
INSERT INTO "hasan_abdulkarim" VALUES(16,61,'16:61','Và nếu Allah muốn bắt phạt loài người về tội ác của họ thì Ngài sẽ không để cho một sinh vật nào tồn tại trên trái đất. Nhưng Ngài triển hạn cho họ sống đến một thời kỳ ấn định. Do đó, khi thời hạn đã mãn thì nhất định họ sẽ không thể nấn ná thêm được một giờ khắc nào, và cũng không thể ra đi sớm hơn giờ khắc ấn định.');
INSERT INTO "hasan_abdulkarim" VALUES(16,62,'16:62','Và họ qui cho Allah điều mà chính họ lại không thích và lưỡi của họ thốt ra lời lẽ dối trá, nói rằng cái gì tốt là của họ. Không một chút nghi ngờ nào, chắc chắn hỏa ngục là phần dành cho họ và chắc chắn họ sẽ là những kẻ đầu tiên chạy nhanh đến đó trước.');
INSERT INTO "hasan_abdulkarim" VALUES(16,63,'16:63','Nhân danh Allah, TA đã cử phái (các Sứ Giả) đến với những cộng đồng trước thời của Ngươi (Muhammad) nhưng Shaytan tô điểm việc làm của họ thành hấp dẫn đối với họ; do đó, ngày nay nó là kẻ đỡ đầu của họ và họ sẽ chịu một sự trừng phạt đau đớn.');
INSERT INTO "hasan_abdulkarim" VALUES(16,64,'16:64','Và TA đã ban Kinh Sách (Qur’an) xuống cho Ngươi để Ngươi dùng Nó mà giải thích cho họ rõ những điều họ bất đồng trong đó; và (Kinh Qur''an) vừa là một Chỉ Đạo vừa là một Hồng Ân cho một số người có đức tin.');
INSERT INTO "hasan_abdulkarim" VALUES(16,65,'16:65','Và Allah đã ban nước mưa từ trên trời xuống mà Ngài dùng để làm hồi sinh đất đai đã chết khô. Chắc chắn trong sự việc đó là một dấu hiệu cho một số người biết nghe lời (Allah).');
INSERT INTO "hasan_abdulkarim" VALUES(16,66,'16:66','Và quả thật nơi gia súc cũng có một bài học cho các ngươi. TA ban cho các ngươi loại thức uống từ chất nằm trong bụng của chúng, giữa phân và máu: sữa tươi tinh khiết và có hương vị làm cho người uống thích thú.');
INSERT INTO "hasan_abdulkarim" VALUES(16,67,'16:67','Và (một bài học) từ trái chà là và trái nho mà các ngươi ép ra chất rượu và có được một loại lương thực tốt. Quả thật, trong sự việc đó là một dấu hiệu cho một đám người thông hiểu.');
INSERT INTO "hasan_abdulkarim" VALUES(16,68,'16:68','Và Thượng Đế (Allah) của Ngươi (Muhammad) đã mặc khải cho ong mật rằng: Hãy xây tổ (làm nhà) trên núi, và trên cây và trên những vật mà họ (nhân loại) đã dựng lên.');
INSERT INTO "hasan_abdulkarim" VALUES(16,69,'16:69','“Và hãy ăn (hút mật) từ mỗi loại trái cây, rồi hãy đi theo các con đường thành thuộc của Thượng Đế của ngươi”; từ trong bụng của chúng tiết ra một loại chất uống có nhiều màu sắc khác biệt; trong đó chứa một dược liệu chữa bệnh cho nhân loại. Chắc chắn, trong sự việc đó là một dấu hiệu cho một đám người biết ngẫm nghĩ.');
INSERT INTO "hasan_abdulkarim" VALUES(16,70,'16:70','Và Allah đã tạo hóa các người rồi bắt hồn các người. Trong các người, có một số được trả về sống tiếp cho đến hết tuổi già yếu, và trở thành lẩm cẩm đến nỗi không còn biết gì sau khi đã biết nhiều. Quả thật, Allah Toàn Tri, Toàn Lực.');
INSERT INTO "hasan_abdulkarim" VALUES(16,71,'16:71','Và Allah đã ưu đãi một số người này hơn một số người khác về bổng lộc. Và những ai được ưu đãi không giao bổng lộc của mình cho những kẻ (nô lệ) nằm dưới tay quản lý của mình hầu làm cho chúng bình đẳng với mình về phương diện này. Phải chăng họ đã phủ nhận những ân huệ của Allah?');
INSERT INTO "hasan_abdulkarim" VALUES(16,72,'16:72','Và Allah đã tạo từ bản thân của các ngươi những người vợ cho các ngươi và từ các bà vợ, đã tạo con cái và cháu chắt cho các ngươi và cung cấp lương thực tốt cho các ngươi. Phải chăng họ tin tưởng nơi điều giả dối và phủ nhận các ân huệ của Allah?');
INSERT INTO "hasan_abdulkarim" VALUES(16,73,'16:73','Và họ thờ phụng ngoài Allah những kẻ không có một chút quyền hành và cũng không có khả năng cung cấp cho họ một tí bổng lộc nào từ các tầng trời và dưới đất.');
INSERT INTO "hasan_abdulkarim" VALUES(16,74,'16:74','Bởi thế, chớ đưa ra những hình ảnh so sánh với Allah(. Quả thật, Allah biết hết (mọi việc) trong lúc các ngươi không biết.');
INSERT INTO "hasan_abdulkarim" VALUES(16,75,'16:75','Allah đưa ra thí dụ so sánh (về hai người): một người nô lệ bị quản thúc, không có một chút quyền hành nào; và một người được TA cung cấp bổng lộc tốt mà y chi dùng kín đáo và công khai (vào việc từ thiện). Vậy hai người đó có ngang bằng nhau không? Mọi lời ca tụng đều thuộc về Allah. Không! Đa số bọn họ không biết.');
INSERT INTO "hasan_abdulkarim" VALUES(16,76,'16:76','Và Allah đưa ra một thí dụ so sánh (khác) về hai người; một trong hai người thì câm, không có một chút quyền nào và là một gánh nặng cho người chủ. Bất cứ nơi nào người chủ sai đi, y không hề mang về một điều tốt nào. Y có bằng với một người ra lệnh thi hành nền công lý và đi theo con Đường ngay chính hay không?');
INSERT INTO "hasan_abdulkarim" VALUES(16,77,'16:77','Và Allah nắm điều vô hình của các tầng trời và trái đất. Và vấn đề của Giờ (Phán Xử) sẽ xảy ra chỉ như cái nháy mắt hoặc nhanh hơn nữa. Quả thật, Allah toàn năng trên tất cả mọi sự việc.');
INSERT INTO "hasan_abdulkarim" VALUES(16,78,'16:78','Và Allah đã mang các ngươi ra khỏi bụng mẹ (trong lúc) các ngươi không biết gì cả và Ngài đã ban cho các ngươi thính giác, thị giác và trái tim để các ngươi có thể tạ ơn (Allah).');
INSERT INTO "hasan_abdulkarim" VALUES(16,79,'16:79','Há họ không thấy những con chim bị chế ngự thăng bằng giữa không trung hay sao? Không ai giữ chúng thăng bằng được ngoại trừ Allah. Chắc chắn trong sự việc đó là những dấu hiệu cho một đám người có đức tin.');
INSERT INTO "hasan_abdulkarim" VALUES(16,80,'16:80','Và Allah đã làm nhà cửa cho các ngươi yên nghỉ và đã dùng da thuộc của gia súc làm thành căn lều mà các ngươi cảm thấy nhẹ vào ngày di chuyển cũng như vào ngày dừng chân nghỉ tạm; và từ lông cứng và lông mịn cũng như lông thường của chúng (các ngươi) có được vật trang trí và vật hưởng dụng trong một thời gian.');
INSERT INTO "hasan_abdulkarim" VALUES(16,81,'16:81','Và Allah đã dùng những vật mà Ngài đã tạo để che mát cho các ngươi và dựng núi làm nơi trú ẩn cho các ngươi và làm ra những chiếc áo choàng để bảo vệ các ngươi khỏi bị nóng và làm những chiếc áo giáp bảo vệ các ngươi trước sức tấn công (của địch). Bằng cách đó Ngài đã hoàn tất Ân Huệ của Ngài cho các ngươi để may ra các ngươi thần phục Ngài (trong Islam).');
INSERT INTO "hasan_abdulkarim" VALUES(16,82,'16:82','Nhưng nếu họ quay bỏ đi thì nhiệm vụ của Ngươi chỉ là công khai truyền đạt (Thông điệp của Allah).');
INSERT INTO "hasan_abdulkarim" VALUES(16,83,'16:83','Họ đều công nhận Ân Huệ của Allah nhưng phủ nhận nó, và đa số bọn họ là những kẻ phụ ơn.');
INSERT INTO "hasan_abdulkarim" VALUES(16,84,'16:84','Và (hãy nhớ) Ngày mà TA sẽ dựng lên từ mỗi cộng đồng một nhân chứng(, lúc đó, những kẻ không có đức tin sẽ không được phép (cáo lỗi) và cũng không được phép tạ tội sửa mình.');
INSERT INTO "hasan_abdulkarim" VALUES(16,85,'16:85','Và khi những kẻ làm điều sai quấy thấy sự trừng phạt thì sẽ không có sự giảm nhẹ cho họ và họ sẽ không được nghỉ xã hơi.');
INSERT INTO "hasan_abdulkarim" VALUES(16,86,'16:86','Và khi thấy những thần linh của họ, những kẻ tôn thờ đa thần sẽ thưa: “Lạy Thượng Đế của bầy tôi! Đây là những thần linh mà bầy tôi thường cầu nguyện ngoài Ngài.” Nhưng chúng sẽ mắng trả lại, bảo: “Thật sự các người chỉ là những tên nói dối.”');
INSERT INTO "hasan_abdulkarim" VALUES(16,87,'16:87','Vào Ngày đó họ sẽ công khai thần phục Allah và những kẻ (thần linh) mà họ đã từng bịa đặt sẽ biến đi mất dạng, bỏ họ ở lại trong lúng túng.');
INSERT INTO "hasan_abdulkarim" VALUES(16,88,'16:88','Những ai không tin tưởng và cản trở người khác xa lánh con đường của Allah thì sẽ bị TA gia tăng hình phạt chồng chất vì tội đã từng gieo rắc điều thối nát.');
INSERT INTO "hasan_abdulkarim" VALUES(16,89,'16:89','Và (hãy nhớ) Ngày mà TA sẽ dựng sống lại từ mỗi cộng đồng một nhân chứng của họ đứng ra đối chất với họ và TA sẽ đưa Ngươi (Muhammad) ra làm một nhân chứng đối chất với những người (Quraish) này. Và TA đã ban cho Ngươi Kinh Sách (Qur’an) trình bày rõ tất cả mọi điều; và Nó là một Chỉ Đạo, một Hồng Ân và một Tin mừng cho những người Muslim.');
INSERT INTO "hasan_abdulkarim" VALUES(16,90,'16:90','Allah truyền lệnh thi hành nền công lý và làm điều thiện và bố thí (giúp đỡ) người ruột thịt( và cấm làm điều ác đức(89), điều tội lỗi(90) và áp bức bất công(91). Ngài dạy bảo các ngươi để may ra các ngươi lưu ý (đến Phúc và Tội).');
INSERT INTO "hasan_abdulkarim" VALUES(16,91,'16:91','Và hãy làm tròn Lời Giao Ước của Allah khi các ngươi đã giao ước và chớ vi phạm lời thề sau khi đã thề xác nhận và chắc chắn các ngươi đã nhận Allah làm một Bảo chứng của các ngươi. Quả thật, Allah Hằng biết điều các người làm.');
INSERT INTO "hasan_abdulkarim" VALUES(16,92,'16:92','Và chớ xử sự như một phụ nữ tháo tung cuộn chỉ của bà sau khi đã được se chặt. Các ngươi dùng lời thề để lừa dối lẫn nhau sợ rằng một cộng đồng này mai hậu sẽ có đông người hơn một cộng đồng kia. Quả thật, Allah chỉ dùng nó để thử thách các ngươi. Và vào Ngày phục sinh chắc chắn Ngài sẽ cho các ngươi thấy rõ điều mà cắc ngươi đã từng tranh chấp nhau.');
INSERT INTO "hasan_abdulkarim" VALUES(16,93,'16:93','Và nếu muốn, Allah đã làm cho các ngươi thành một cộng đồng duy nhất nhưng Ngài đánh lạc hướng kẻ nào Ngài muốn và hướng dẫn người nào Ngài muốn. Và chắc chắn, các ngươi sẽ bị tra hỏi về những điều các ngươi đã từng làm.');
INSERT INTO "hasan_abdulkarim" VALUES(16,94,'16:94','Và chớ dùng lời thề của các ngươi làm một phương tiện lừa phỉnh lẫn nhau sợ rằng một bàn chân sẽ bị trượt ngã sau khi đã đứng vững và các ngươi sẽ nếm điều xấu xa do việc các ngươi đã cản trở người khác xa lánh con đường của Allah và các ngươi sẽ nhận một sự trừng phạt to lớn.');
INSERT INTO "hasan_abdulkarim" VALUES(16,95,'16:95','Và chớ bán Lời Giao Ước của Allah với một giá nhỏ nhoi. Quả thật, món vật gì với Allah sẽ tốt cho các người hơn nếu các người biết (giá trị của nó).');
INSERT INTO "hasan_abdulkarim" VALUES(16,96,'16:96','Những gì trong tay các ngươi sẽ cạn kiệt còn những gì trong tay Allah sẽ tồn tại vĩnh viễn. Và chắc chắn TA sẽ ban thưởng cho những ai kiên nhẫn phần thưởng (của họ) tùy theo việc tốt nhất mà họ đã làm.');
INSERT INTO "hasan_abdulkarim" VALUES(16,97,'16:97','Ai làm việc thiện bất luận nam hay nữ và là một người có đức tin thì chắc chắn TA sẽ cho y sống một đời sống lành mạnh tốt đẹp; và chắc chắn TA sẽ ban cho họ phần thưởng của họ tùy theo điều tốt nhất mà họ đã làm.');
INSERT INTO "hasan_abdulkarim" VALUES(16,98,'16:98','Vậy khi đọc Qur''an, Ngươi (hỡi Muhammad!) hãy cầu xin Allah che chở tránh xa Shaytan đáng bị trục xuất.');
INSERT INTO "hasan_abdulkarim" VALUES(16,99,'16:99','Quả thật, nó không có quyền đối với những ai có đức tin và phó thác (công việc) cho Thượng Đế (Allah) của họ.');
INSERT INTO "hasan_abdulkarim" VALUES(16,100,'16:100','Nó chỉ có quyền đối với những ai nhận nó làm kẻ bảo hộ và những ai tổ hợp thần linh cùng với Ngài (Allah).');
INSERT INTO "hasan_abdulkarim" VALUES(16,101,'16:101','Và khi TA đổi một Câu (của Qur’an) thay thế một Câu khác, và Allah biết rõ điều Ngài đã ban xuống thì họ nói Ngươi (Muhammad) chỉ là một kẻ bịa đặt. Không, đa số bọn họ không biết.');
INSERT INTO "hasan_abdulkarim" VALUES(16,102,'16:102','Hãy bảo họ Ruh Al-Qudus (Thiên Thần Jibril) mang nó (Câu) xuống từ Thượng Đế (Allah) của Ngươi bằng sự Thật để củng cố (đức tin của) những người tin tưởng và làm một Chỉ Đạo cũng như Tin Mừng cho những người Muslim (thần phục Allah).');
INSERT INTO "hasan_abdulkarim" VALUES(16,103,'16:103','Và chắc chắn TA biết rõ chính họ đã nói: “Quả thật có một người phàm đã dạy y (Muhammad).” Rõ ràng ngôn ngữ của kẻ mà họ ám chỉ hoàn toàn khác lạ trong lúc đây là tiếng Ả Rập trong sáng.');
INSERT INTO "hasan_abdulkarim" VALUES(16,104,'16:104','Quả thật, những ai không tin tưởng nơi những dấu hiệu của Allah thì sẽ không được Allah hướng dẫn và sẽ chịu một sự trừng phạt đau đớn.');
INSERT INTO "hasan_abdulkarim" VALUES(16,105,'16:105','Chỉ những ai không tin tưởng nơi những dấu hiệu của Allah mới bịa đặt điều nói dối và thực sự là những kẻ nói dối.');
INSERT INTO "hasan_abdulkarim" VALUES(16,106,'16:106','Ai phủ nhận Allah sau khi đã tin tưởng ngoại trừ ai là người bị ép buộc (bỏ đạo) trong lúc tấm lòng của y hãy còn yên bình với đức tin, còn kẻ nào vỗ ngực tuyên bố không tin tưởng thì sẽ đón nhận sự Giận Dữ của Allah và sẽ chịu một sự trừng phạt rất lớn.');
INSERT INTO "hasan_abdulkarim" VALUES(16,107,'16:107','Sở dĩ như thế là vì họ yêu đời sống trần tục này hơn đời sau. Và quả thật, Allah không hướng dẫn một đám người không tin tưởng.');
INSERT INTO "hasan_abdulkarim" VALUES(16,108,'16:108','Họ là những kẻ mà Allah khóa chặt trái tim, thính giác và thị giác của họ, cho nên, họ là những kẻ khinh suất.');
INSERT INTO "hasan_abdulkarim" VALUES(16,109,'16:109','Không một chút nghi ngờ nào, quả thật họ là những kẻ sẽ thua thiệt nhất ở đời sau.');
INSERT INTO "hasan_abdulkarim" VALUES(16,110,'16:110','Rồi đối với những ai di cư đi tị nạn sau khi đã bị sách nhiễu rồi sau đó chiến đấu anh dũng và kiên nhẫn chịu đựng (mọi thử thách) thì quả thật Thượng Đế (Allah) của Ngươi (Muhammad), sau những biến cố đó, sẽ thật sự tha thứ và khoan dung (cho họ).');
INSERT INTO "hasan_abdulkarim" VALUES(16,111,'16:111','(Hãy nhớ) Ngày mà mỗi linh hồn sẽ đến khiếu nại cho bản thân mình và mỗi linh hồn sẽ được trả lại đầy đủ về điều mà nó đã làm và họ sẽ không bị đối xử bất công.');
INSERT INTO "hasan_abdulkarim" VALUES(16,112,'16:112','Và Allah đưa ra một thí dụ so sánh: Có một thị trấn an ninh và yên bình (Makka), được tiếp liệu lương thực dồi dào từ khắp nơi, nhưng nó phủ nhận những An Huệ của Allah cho nên Allah bắt nó nếm mùi tận khổ của sự đói khát và sợ sệt do điều (tội lỗi) mà (dân cư của) nó đã từng làm ra.');
INSERT INTO "hasan_abdulkarim" VALUES(16,113,'16:113','Và chắc chắn đã có một Sứ Giả xuất thân từ họ đến gặp họ (trình bày Thông Điệp của Allah) nhưng họ đã cho rằng Y nói dối, cho nên, hình phạt đến chụp bắt họ trong lúc họ là những kẻ làm điều sai quấy.');
INSERT INTO "hasan_abdulkarim" VALUES(16,114,'16:114','Do đó hãy dùng những thực phẩm Halal (được phép) và tốt sạch mà Allah đã cung cấp cho các ngươi và hãy biết ơn Allah về những Ân Huệ của Ngài nếu duy chỉ Ngài là Đấng mà các ngươi thờ phụng.');
INSERT INTO "hasan_abdulkarim" VALUES(16,115,'16:115','Quả thật, Ngài (Allah) chỉ cấm các ngươi (dùng thịt của) xác chết, và máu (huyết) và thịt heo và những món vật (cúng) mà tên khác với Allah đã được nhắc lên nó; nhưng ai vì nhu cầu bắt buộc (mà dùng) chứ không ham muốn cũng không quá độ thì quả thật Allah Hằng Tha Thứ, Rất Mực Khoan Dung (đối với họ).');
INSERT INTO "hasan_abdulkarim" VALUES(16,116,'16:116','Và chớ nói về một món vật mà lưỡi của các ngươi có thể nói dối: "món này đượ phép và món này bị nghiêm cấm", bịa đặt điều nói dối rồi đổ thừa cho Allah. Chắc chắn, những kẻ bịa đặt điều nói dối rồi đổ thừa cho Allah sẽ không bao giờ phát đạt.');
INSERT INTO "hasan_abdulkarim" VALUES(16,117,'16:117','(Đó chỉ là) một sự hưởng thụ tạm bợ nhỏ nhoi nhưng rồi họ sẽ chịu một hình phạt đau đớn.');
INSERT INTO "hasan_abdulkarim" VALUES(16,118,'16:118','Và đối với những ai là người Do thái, TA đã cấm họ (dùng) những món (thực phẩm) mà TA đã kể cho Ngươi (Muhammad) trước đây. TA đã không làm hại chúng mà chúng tự làm hại bản thân mình.');
INSERT INTO "hasan_abdulkarim" VALUES(16,119,'16:119','Rồi đối với những ai vì ngu dốt đã làm điều tội lỗi rồi sau đó hối cải và sửa mình thì quả thật Thượng Đế (Allah) của Ngươi sau những điều sai phạm đó sẽ tha thứ và khoan dung (cho họ).');
INSERT INTO "hasan_abdulkarim" VALUES(16,120,'16:120','Quả thật, Ibrahim là một nhà lãnh đạo có nhiều đức tính tốt, hết lòng sùng bái Allah, chỉ tôn thờ Allah và không là một người tôn thờ đa thần.');
INSERT INTO "hasan_abdulkarim" VALUES(16,121,'16:121','Người biết ơn (Allah) về những Ân Huệ của Ngài. Ngài đã chọn Y và hướng dẫn Y theo Chính Đạo.');
INSERT INTO "hasan_abdulkarim" VALUES(16,122,'16:122','Và TA đã ban cho Y điều tốt ở đời này. Và ở đời sau, quả thật, Y sẽ nằm trong thành phần của người lương thiện (đạo đức).');
INSERT INTO "hasan_abdulkarim" VALUES(16,123,'16:123','Rồi TA đã mặc khải cho Ngươi (Muhammad, phán): “Hãy theo tín ngưỡng của Ibrahim: chỉ tôn thờ Allah và không là một người thờ đa thần."');
INSERT INTO "hasan_abdulkarim" VALUES(16,124,'16:124','(Vấn đề Ngày Thứ Bảy) chỉ được truyền xuống cho những ai đã bất đồng ý kiến về nó. Nhưng Thượng Đế (Allah) của Ngươi xét xử họ (người Do thái) vào Ngày phục sinh về những điều mà họ đã từng tranh chấp nhau.');
INSERT INTO "hasan_abdulkarim" VALUES(16,125,'16:125','Hãy gọi mời (tất cả) đến với con đường của Thượng Đế của Ngươi (hỡi Muhammad!) một cách khôn ngoan và với lời khuyến khích tốt đẹp; và hãy tranh luận với họ bằng phương cách tốt đẹp nhất. Quả thật, Thượng Đế của Ngươi biết rõ ai là kẻ đi lạc khỏi con đường của Ngài; và ai là người được hướng dẫn.');
INSERT INTO "hasan_abdulkarim" VALUES(16,126,'16:126','Và nếu các ngươi trả miếng thì hãy trả đủa ngang bằng với miếng mà các ngươi đã bị đòn nhưng nếu các ngươi kiên nhẫn chịu đựng thì chắc chắn đó là điều tốt nhất cho những ai kiên nhẫn.');
INSERT INTO "hasan_abdulkarim" VALUES(16,127,'16:127','Và hãy kiên nhẫn chịu đựng bởi vì Ngươi kiên nhẫn (hỡi Muhammad!) chỉ vì Allah mà thôi. Và chớ buồn rầu cho chúng cũng chớ se lòng về những điều chúng đang âm mưu.');
INSERT INTO "hasan_abdulkarim" VALUES(16,128,'16:128','Quả thật, Allah ở cùng với những ai sợ Ngài và với những ai là người làm tốt.');
INSERT INTO "hasan_abdulkarim" VALUES(17,1,'17:1','Quang vinh và trong sạch thay Ngài, Đấng đã đưa người bề tôi( của Ngài đi xa, vào một đêm, từ Thánh đường Al-Haram (tại Makkah) đến Thánh đường Al-Aqsa (tại Jerusalem) mà vòng đai đã được TA ban phúc, để TA chỉ cho Y thấy những dấu hiệu của TA. Rõ thật, Ngài là Đấng Hằng Nghe, Đấng Hằng Thấy.');
INSERT INTO "hasan_abdulkarim" VALUES(17,2,'17:2','Và TA đã ban cho Musa (Môi-se) Kinh Sách mà TA dùng làm một Chỉ Đạo cho con cháu của Israel (truyền rằng): “Chớ nhận ai khác TA làm Đấng Trông Cậy và Phó Thác.”');
INSERT INTO "hasan_abdulkarim" VALUES(17,3,'17:3','Hỡi con cháu của những người mà TA đã chở cùng với Nuh (Nô- ê trên chiếc tàu)! Quả thật, Y là một người bề tôi biết ơn.');
INSERT INTO "hasan_abdulkarim" VALUES(17,4,'17:4','Và TA đã quyết định cho con cháu của Israel trong Kinh Sách rằng chắc chắn các ngươi sẽ hai lần làm nên tội ác trên trái đất và các ngươi chắc chắn sẽ hống hách, vô cùng hống hách!');
INSERT INTO "hasan_abdulkarim" VALUES(17,5,'17:5','Bởi thế, khi lời hứa đầu tiên trong hai (lời hứa đó) thể hiện, TA dựng các bầy tôi của TA nổi lên chống lại các ngươi, chúng mở trận tấn công dữ dội, tràn vào tàn phá nhà cửa của các ngươi(. Và đó là một lời hứa đã được hoàn tất.');
INSERT INTO "hasan_abdulkarim" VALUES(17,6,'17:6','Rồi TA làm cho các ngươi thắng chúng trở lại và TA hỗ trợ các ngươi với tài sản và con cái và làm cho các ngươi đông đảo hơn.');
INSERT INTO "hasan_abdulkarim" VALUES(17,7,'17:7','(Và TA phán:) Nếu các ngươi làm điều lành thì bản thân sẽ hưởng lành; và nếu các ngươi làm điều xấu thì sẽ chuốc dữ vào thân.” Bởi thế, khỉ lời hứa lần sau được thể hiện, (TA dựng một đám bầy tôi khác đến) bôi nhọ thể diện của các ngươi và chúng vào tận nơi thờ phụng của các ngươi giống như chúng đã đi vào đó lần đầu và chúng đã tiêu diệt bất cứ vật gì lọt vào vòng kiểm soát của chúng.');
INSERT INTO "hasan_abdulkarim" VALUES(17,8,'17:8','Có lẽ Thượng Đế của các ngươi khoan dung các ngươi; nhưng nếu các ngươi tái phạm, TA sẽ cho tái diễn (hình phạt tương tự). Và TA sẽ dùng hỏa ngục làm một thứ ngục thất cho những kẻ không có đức tin.');
INSERT INTO "hasan_abdulkarim" VALUES(17,9,'17:9','Quả thật, Qur’an này hướng dẫn (nhân loại) đến con đường ngay chính đúng đắn hơn và mang tin mừng cho những người tin tưởng, những ai làm điều thiện, rằng họ sẽ nhận một phần ân thưỏng rất lớn.');
INSERT INTO "hasan_abdulkarim" VALUES(17,10,'17:10','Và báo cho những ai không tin tưởng nơi đời sau rằng TA đã chuẩn bị sẵn cho chúng một sự trừng phạt đau đớn.');
INSERT INTO "hasan_abdulkarim" VALUES(17,11,'17:11','Và con người cầu nguyện (Allah) cho điều xấu (mau đến) giống như y cầu nguyện cho điều tốt (mau đến); bởi vì con người vốn nôn nóng và vội vàng.');
INSERT INTO "hasan_abdulkarim" VALUES(17,12,'17:12','Và TA làm cho ban đêm và ban ngày thành hai dấu hiệu: TA làm mờ tối dấu hiệu ban đêm và làm sáng tỏ dấu hiệu ban ngày để cho các ngươi có thể đi tìm thiên lộc của Thượng Đế của các ngươi và giúp các ngươi biết được con số của niên kỷ và cách tính toán (thời gian). Và mỗi vật, TA giải thích rõ từng chi tiết.');
INSERT INTO "hasan_abdulkarim" VALUES(17,13,'17:13','Và TA cột vào cổ của mỗi người hành động của y; vào Ngày phục sinh TA sẽ mang một quyển sổ (hành động) mở sẵn đến cho y.');
INSERT INTO "hasan_abdulkarim" VALUES(17,14,'17:14','(TA phán bảo y:) “Hãy đọc quyển sổ của nhà ngươi! Ngày nay linh hồn của nhà ngươi đủ thanh toán nhà ngươi.”');
INSERT INTO "hasan_abdulkarim" VALUES(17,15,'17:15','Ai theo Chỉ Đạo thì việc theo đó chỉ có lợi cho bản thân mình; còn ai lạc đạo thì là bất lợi cho bản thân mình. Và không người khuân vác nào vác giùm gánh nặng (tội lỗi) của người khác. Và TA không trừng phạt trừ phi TA dựng lên một Sứ Giả.');
INSERT INTO "hasan_abdulkarim" VALUES(17,16,'17:16','Khi muốn tiêu diệt một thị trấn, TA truyền lệnh cho thị dân nào của nó sống sung túc (bảo: hãy thần phục TA và ăn ở đạo đức); nhưng chúng ngoan cố và làm điều thối nát; vì thế, khi Lời (trừng phạt của TA) đã được xác định đối với nó thì lúc đó TA sẽ tiêu diệt nó hoàn toàn.');
INSERT INTO "hasan_abdulkarim" VALUES(17,17,'17:17','Và có bao nhiêu thế hệ đã bị TA tiêu diệt sau Nuh Và Thượng Đế của Ngươi đủ biết, đủ thấy tội lỗi của đám bầy tôi của Ngài.');
INSERT INTO "hasan_abdulkarim" VALUES(17,18,'17:18','Ai muốn kiếp phù sinh (của đời này) TA sẽ giục điều mà TA sẵn sàng ban cấp đến cho kẻ nào TA muốn. Rồi TA làm sẵn hỏa ngục cho y; trong đó, y sẽ bị nướng, bị hạ nhục và bị bỏ xó.');
INSERT INTO "hasan_abdulkarim" VALUES(17,19,'17:19','Và ai mong muốn đời sau và tích cực phấn đấu cho nó theo tiêu chuẩn qui định đồng thời là một người có đức tin thì sẽ được (Allah) gia ân xứng đáng về nỗ lực của họ.');
INSERT INTO "hasan_abdulkarim" VALUES(17,20,'17:20','Cho từng người - những người này cũng như những người nọ TA đều ban ân sủng của Thượng Đế của Ngươi. Và ân sủng của Thượng Đế của Ngươi không bao giờ bị hạn chế.');
INSERT INTO "hasan_abdulkarim" VALUES(17,21,'17:21','Hãy xem! TA đã ưu đãi người này hơn người nọ (ở đời này) như thế nào. Nhưng chắc chắn đời sau sẽ có nhiều cấp bậc và ưu đãi to lớn hơn.');
INSERT INTO "hasan_abdulkarim" VALUES(17,22,'17:22','(Bởi thế) chớ dựng một thần linh nào khác cùng với Allah sợ rằng ngươi (hỡi người!) sẽ ngồi (trong hỏa ngục) bị khinh miệt và bị bỏ quên.');
INSERT INTO "hasan_abdulkarim" VALUES(17,23,'17:23','Và Thượng Đế của Ngươi quyết định rằng các ngươi chỉ thờ phụng riêng Ngài, và ăn ở tử tế với cha mẹ. Nếu một trong hai người (cha mẹ) hoặc cả hai người (cha mẹ) sống với ngươi đến tuổi già, chớ nói tiếng vô lễ với hai người (cha mẹ), và chớ xua đuổi hai người (cha mẹ), mà phải ăn nói với hai người (cha mẹ) lời lẽ tôn kính.');
INSERT INTO "hasan_abdulkarim" VALUES(17,24,'17:24','Và hãy nhân từ đối xử khiêm nhường với cha mẹ và (cầu nguyện) thưa: “Lạy Thượng Đế của bề tôi! Xin Ngài rủ lòng thương cha mẹ của bề tôi giống như hai người đã thương yêu, chăm sóc bề tôi lúc hãy còn bé.”');
INSERT INTO "hasan_abdulkarim" VALUES(17,25,'17:25','Thượng Đế của các ngươi biết rõ điều nằm trong lòng của các ngươi. Nếu các ngươi ăn ở đạo hạnh thì chắc chắn Ngài sẽ tha thứ cho những người hằng quay về ăn năn hối cải (với Ngài).');
INSERT INTO "hasan_abdulkarim" VALUES(17,26,'17:26','Và hãy tặng cho họ hàng ruột thịt phần bắt buộc của y, cho người thiếu thốn và khách lỡ đường nhưng chớ hoang phí quá mức.');
INSERT INTO "hasan_abdulkarim" VALUES(17,27,'17:27','Quả thật, những kẻ phí phạm là anh em của Shaytan. Và Shaytan lúc nào cũng bội ơn Thượng Đế của nó.');
INSERT INTO "hasan_abdulkarim" VALUES(17,28,'17:28','Và nếu Ngươi từ chối tặng họ do bởi Ngươi cũng đang hy vọng chờ Hồng Ân của Thượng Đế của Ngươi thì hãy ăn nói với họ lời lẽ nhã nhặn.');
INSERT INTO "hasan_abdulkarim" VALUES(17,29,'17:29','Và chớ trói chặt bàn tay của ngươi vào cổ (như kẻ hà tiện) và cũng chớ giăng nó ra quá xa tầm với (như kẻ hoang phí) sợ rằng ngươi ngồi xuống bị chê bai và nghèo khổ.');
INSERT INTO "hasan_abdulkarim" VALUES(17,30,'17:30','Quả thật, Thượng Đế của Ngươi nới rộng bổng lộc cho người nào Ngài muốn và hạn chế nó lại (theo Ý Ngài muốn). Quả thật, Ngài Hằng Biết, Hằng Thấy bầy tôi của Ngài.');
INSERT INTO "hasan_abdulkarim" VALUES(17,31,'17:31','Và chớ vì sợ nghèo mà giết con cái của các ngươi. TA cung dưỡng chúng và cả các ngươi. chắc chắn, việc giết chúng (con cái) là một trọng tội.');
INSERT INTO "hasan_abdulkarim" VALUES(17,32,'17:32','Và chớ đến gần việc ngoại tình. Chắc chắn nó là một tội lớn và là một con đường tội lỗi.');
INSERT INTO "hasan_abdulkarim" VALUES(17,33,'17:33','Và chớ giết hại một sinh mạng (người) mà Allah đã làm cho linh thiêng trừ phi với lý do chính đáng. Và ai bị giết oan (một cách bất công), TA sẽ ban thẩm quyền (đồi thế mạng theo luật Qisas hoặc tha thứ hoặc lấy tiền Diyah thế mạng) cho người thừa kế của y, nhưng không được vượt quá mức giới hạn trong việc giết chóc(. Bởi vì y sẽ được (luật pháp của Islam) giúp đỡ.');
INSERT INTO "hasan_abdulkarim" VALUES(17,34,'17:34','Và chớ đến gần tài sản của trẻ mồ côi trừ phi với điều gì tốt nhất (để cải thiện nó) cho đến khi đứa trẻ trưởng thành. Và hãy làm tròn lời giao ước. Chắc chắn, lời giao ước sẽ bị gặn hỏi.');
INSERT INTO "hasan_abdulkarim" VALUES(17,35,'17:35','Và hãy đo cho đủ khi các ngươi đo ra và hãy cân với một bàn cân ngay thẳng. Điều đó tốt và công bằng nhất về cuối.');
INSERT INTO "hasan_abdulkarim" VALUES(17,36,'17:36','Và chớ (làm chứng) về điều mà ngươi (hỡi người) không biết một tí nào. Chắc chắn thính giác, thị giác và trái tim, tất cả những giác quan đó sẽ bị hạch hỏi.');
INSERT INTO "hasan_abdulkarim" VALUES(17,37,'17:37','Và chớ bước đi trên mặt đất với điệu bộ kiêu căng. Chắc chắn ngươi sẽ không bao giờ chẻ được trái đất ra làm hai và cũng không bao giờ đứng cao bằng núi.');
INSERT INTO "hasan_abdulkarim" VALUES(17,38,'17:38','Tất cả các điều đó đều xấu xa (tội lỗi), rất đáng ghét đối với Thượng Đế của Ngươi.');
INSERT INTO "hasan_abdulkarim" VALUES(17,39,'17:39','Đó là những điều khôn ngoan mà Thượng Đế của Ngươi đã mặc khải cho Ngươi. Và chớ dựng một thần linh nào khác cùng với Allah e rằng ngươi (hỡi người!) sẽ bị ném vào Hoả ngục, bị sỉ vả, bỏ xó.');
INSERT INTO "hasan_abdulkarim" VALUES(17,40,'17:40','Thế phải chăng Thượng Đế của các ngươi (hỡi người Quraish không tin!) đã biệt đãi các ngươi với con trai và nhận Thiên Thần làm con gái của Ngài hay sao? Quả thật, các ngươi đã nói ra điều xúc phạm tày trời.');
INSERT INTO "hasan_abdulkarim" VALUES(17,41,'17:41','Và chắc chắn TA đã giải thích sự việc trong Qur''an này bằng nhiều phương cách với mục đích làm cho họ lưu ý đến lời nhắc nhở nhưng Nó chỉ làm cho họ căm ghét thêm.');
INSERT INTO "hasan_abdulkarim" VALUES(17,42,'17:42','Hãy bảo họ: “Nếu quả thật có những thần linh khác cùng với Ngài (Allah) đúng theo lời họ nói thì chắc chắn chúng sẽ lặn lội tìm đường đến gặp Chủ nhân của chiếc Ngai Vương.');
INSERT INTO "hasan_abdulkarim" VALUES(17,43,'17:43','Quang vinh và trong sạch thay Ngài! Ngài Tối Cao, Độc Lập vượt hẳn mọi điều dối trá tày trời mà họ nói.');
INSERT INTO "hasan_abdulkarim" VALUES(17,44,'17:44','Bảy tầng trời và trái đất và tất cả mọi vật giữa trời đất đều tán dương Ngài. Không vật nào mà không tán dương Ngài, tuy nhiên, các ngươi không hiểu lời tán dương của chúng ấy thôi. Quả thật, Ngài Hằng Chịu đựng, Hằng Tha Thứ.');
INSERT INTO "hasan_abdulkarim" VALUES(17,45,'17:45','Và khi Ngươi đọc Qur''an, TA đặt giữa Ngươi và những kẻ không tin nơi đời sau một bức màn vô hình.');
INSERT INTO "hasan_abdulkarim" VALUES(17,46,'17:46','Và TA lấy những tấm phủ bao quả tim của chúng lại e rằng chúng sẽ hiểu Nó (Qur’an) và làm cho tai của chúng nặng (như điếc); và khi Ngươi chỉ nhắc đến riêng Thượng Đế của Ngươi trong Qur''an thì chúng quay lưng bỏ đi nơi khác, đầy căm ghét.');
INSERT INTO "hasan_abdulkarim" VALUES(17,47,'17:47','TA biết rõ lý do gì thúc đẩy chúng nghe khi chúng lắng nghe Ngươi (đọc Qur''an) và khi chúng họp nhau bàn kín thì những kẻ làm điều sai quấy lên tiếng: “Các anh chỉ nghe theo lời của một kẻ bị mê hoặc.”');
INSERT INTO "hasan_abdulkarim" VALUES(17,48,'17:48','Thấy không! Chúng đã so sánh Ngươi như thế nào; nhưng chúng đã lạc lầm. Bỏi thế, chúng không thể tìm ra một con đường (ngay chính).');
INSERT INTO "hasan_abdulkarim" VALUES(17,49,'17:49','Chúng nói: “Phải chăng khi chúng tôi đã trở thành xương khô và mảnh vụn, chúng tôi sẽ được dựng sống lại thành một tạo hóa mới?”');
INSERT INTO "hasan_abdulkarim" VALUES(17,50,'17:50','Hãy bảo (chúng): “Dẫu cho các người là đá hay sắt."');
INSERT INTO "hasan_abdulkarim" VALUES(17,51,'17:51','“Hoặc bất cứ tạo vật nào mà tâm trí của các ngươi cho là cứng nhất, (chúng cũng sẽ được dựng sống lại).” Nhưng chúng sẽ hỏi: “Ai sẽ phục sinh chúng tôi trở lại?” Hãy bảo chúng: “Đấng đã sáng tạo các ngươi lúc ban đầu.” Chúng sẽ nhìn Ngươi lắc đầu và nói: “Chừng nào nó xảy ra?” Hãy bảo: "Có lẽ sẽ gần thôi."');
INSERT INTO "hasan_abdulkarim" VALUES(17,52,'17:52','“Vào Ngày mà Ngài sẽ gọi các người, rồi các người sẽ đáp lại bằng lời ca tụng Ngài và các người sẽ tưởng tượng rằng các người đã ở lại (trần gian) chỉ trong chốc lát.”');
INSERT INTO "hasan_abdulkarim" VALUES(17,53,'17:53','Và hãy bảo bầy tôi của TA chỉ nên nói điều nào tốt nhất. Quả thật, Shaytan tìm cách làm cho họ chia rẽ nhau. Bởi vì Shaytan là kẻ thù công khai của loài người.');
INSERT INTO "hasan_abdulkarim" VALUES(17,54,'17:54','Thượng Đế của các ngươi biết rõ các ngươi nhất. Nếu muốn, Ngài sẽ khoan dung các người hoặc nếu muốn, Ngài sẽ trừng phạt các người. Và TA đã không cử Ngươi (Muhammad) đến làm một người thọ lãnh cho họ.');
INSERT INTO "hasan_abdulkarim" VALUES(17,55,'17:55','Và Thượng Đế của các ngươi biết rõ ai ở trong các tầng trời và trái đất. Và TA đã làm cho một số Nabi này trội hơn một số (Nabi) khác và TA đã ban (Kinh) Zabur cho Dawood.');
INSERT INTO "hasan_abdulkarim" VALUES(17,56,'17:56','Hãy bảo họ: “Các người hãy cầu nguyện những kẻ mà các người đã xác nhận (chúng là thần linh của các người) ngoài Ngài (Allah, các người sẽ thấy) chúng sẽ không có khả năng bốc hoạn nạn đi khỏi các người, và cũng không thể chuyển nó sang cho người khác đặng.”');
INSERT INTO "hasan_abdulkarim" VALUES(17,57,'17:57','Những kẻ (thần linh) mà chúng cầu nguyện cũng sẽ tìm phương cách đến gặp Thượng Đế của họ, (và sẽ thi đua xem) ai trong họ là người gần (Thượng Đế) nhất và hy vọng nơi lòng Khoan dung của Ngài và sợ sự trừng phạt của Ngài. Quả thật, sự trừng phạt của Thượng Đế (Allah) của Ngươi là điều phải đáng sợ.');
INSERT INTO "hasan_abdulkarim" VALUES(17,58,'17:58','Và không một thị trấn nào mà sẽ không bị TA tiêu diệt trước Ngày phục sinh hoặc bị trừng phạt dữ dội. Đó là điều đã được ghi chép trong sổ sách (Định mệnh).');
INSERT INTO "hasan_abdulkarim" VALUES(17,59,'17:59','Và TA ngưng gởi những Dấu lạ xuống chỉ vì lý do những người của thế hệ trước đã cho đó là những điều giả tạo. Và TA đã ban cho (người dân) Thamud con lạc đà cái như một minh chứng, nhưng chúng sát hại nó. Và TA chỉ gởi những Dấu lạ xuống để làm cho (người dân) sợ.');
INSERT INTO "hasan_abdulkarim" VALUES(17,60,'17:60','Và khi TA phán cho Ngươi bảo: “Quả thật, Thượng Đế của Ngươi tóm thâu nhân loại (trong bàn Tay của Ngài). Và sự mục kích mà TA cho Ngươi thây chẳng qua là một sự thử thách cho nhân loại; và Cây Zaqqũm đáng bị nguyền rủa (ghi) trong Qur''an (cũng thế). Và TA cảnh cáo cho chúng sợ nhưng chẳng thay đổi được gì, ngược lại chỉ làm cho chúng thêm thái quá.');
INSERT INTO "hasan_abdulkarim" VALUES(17,61,'17:61','Và khi TA phán cho các Thiên Thần: “Hãy quỳ lạy Adam!” Chúng đồng quỳ lạy ngoại trừ Iblis. Nó thưa: “Bề tôi phải quỳ lạy kẻ mà Ngài đã tạo bằng đất sét hay sao?”');
INSERT INTO "hasan_abdulkarim" VALUES(17,62,'17:62','Nói tiếp: “Ngài xét lại xem, đây là kẻ mà Ngài làm cho vinh dự hơn bề tôi ư? Nếu Ngài gia hạn cho bề tôi sống đến Ngày phục sinh, chắc chắn bề tôi sẽ túm bắt con cháu của nó ngoại trừ một số ít.”');
INSERT INTO "hasan_abdulkarim" VALUES(17,63,'17:63','(Allah) phán: Hãy cút đi! Đứa nào trong bọn chúng nghe theo ngươi thì chắc chắn hỏa ngục sẽ là phần thưởng dành cho tất cả bọn bây, một phần thưởng lớn vô kể.');
INSERT INTO "hasan_abdulkarim" VALUES(17,64,'17:64','Và tùy khả năng của ngươi, hãy xúi giục kẻ nào trong bọn chúng bằng cách dùng âm thanh (quyến rũ) của ngươi và vận dụng cả kỵ binh lẫn bộ binh của ngươi tấn công chúng và chia sẻ tài sản và con cái với chúng và hứa hẹn với chúng. Nhưng Shaytan chỉ hứa hão với chúng.');
INSERT INTO "hasan_abdulkarim" VALUES(17,65,'17:65','“Quả thật, nhà ngươi không có quyền đối với các bầy tôi (có đức tin) của TA;” và Thượng Đế của Ngươi đủ làm một Đấng Giám Hộ.');
INSERT INTO "hasan_abdulkarim" VALUES(17,66,'17:66','Thượng Đế của các ngươi là Đấng làm cho tàu bè chạy nhanh trên biển cả để các ngươi đi tìm thiên lộc của Ngài. Quả thật, Ngài Hằng Khoan Dung với các ngươi.');
INSERT INTO "hasan_abdulkarim" VALUES(17,67,'17:67','Và khi các ngươi gặp hoạn nạn trên biển cả, những kẻ mà các ngươi thường cầu nguyện đã bỏ (các ngươi) đi mất biệt, ngoại trừ Ngài (với các ngươi). Nhưng khi Ngài cứu các ngươi lên bờ an toàn, các ngươi quay lánh xa (Ngài). Và con người vốn vong ân bội nghĩa.');
INSERT INTO "hasan_abdulkarim" VALUES(17,68,'17:68','Thế phải chăng các người cảm thấy an toàn nghĩ rằng Ngài sẽ không làm cho một sườn đất sụp xuống nuốt mất các người hoặc sẽ không gởi một trận bão cát đến phạt các người và các người sẽ không tìm ra một Đâng Bảo Hộ để giúp đỡ các người hay sao?');
INSERT INTO "hasan_abdulkarim" VALUES(17,69,'17:69','Hoặc các ngươi cảm thấy an toàn chăng nghĩ rằng Ngài sẽ không gởi các ngươi trở lại nơi đó một lần khác và sẽ gởi một trận cuồng phong đến nhận chìm các ngươi về việc các ngươi đã bội ơn; rồi các ngươi sẽ không tìm ra một kẻ nào trả thù lại TA được.');
INSERT INTO "hasan_abdulkarim" VALUES(17,70,'17:70','Và chắc chắn TA đã làm vinh danh con cháu của Adam (nhân loại). Và TA đã chuyên chở họ trên đất liền và biển cả và cung cấp lộc ăn tốt và sạch cho họ và đặc biệt ưu đãi họ vinh dự hơn đa số tạo vật khác của TA.');
INSERT INTO "hasan_abdulkarim" VALUES(17,71,'17:71','(Hãy ghi nhớ) Ngày mà TA sẽ gọi tập trung tất cả nhân loại cùng với những vị lãnh đạo của chúng. Rồi ai được trao cho quyển sổ (hành động) của mình nơi tay phải thì sẽ (vui thích) đọc quyển sổ bộ của họ và sẽ không bị đối xử bất công một tí nào.');
INSERT INTO "hasan_abdulkarim" VALUES(17,72,'17:72','Và ai mù lòa ở đời này (vì không có đức tin) sẽ mù lòa ở đời sau và lạc xa khỏi Chính Đạo.');
INSERT INTO "hasan_abdulkarim" VALUES(17,73,'17:73','Và quả thật, chúng đã âm mưu quyến dụ Ngươi (Muhammad) bỏ những điều mà TA đã mặc khải cho Ngươi và xúi giục Ngươi bịa đặt ra một điều khác để chống lại TA và trong trường hợp đó, chúng sẽ nhận Ngươi làm một người bạn thân tình (của chúng).');
INSERT INTO "hasan_abdulkarim" VALUES(17,74,'17:74','Và nếu TA không củng cố Ngươi vững chãi thì Ngươi suýt đã xiêu lòng nghe theo bọn chúng.');
INSERT INTO "hasan_abdulkarim" VALUES(17,75,'17:75','Trong trường hợp đó, TA chắc sẽ phải cho Ngươi nếm (sự trừng phạt) gấp đôi ở đời này và gấp đôi (hình phạt) sau khi chết và Ngươi sẽ không tìm ra ai để giúp đỡ Ngươi chống lại TA.');
INSERT INTO "hasan_abdulkarim" VALUES(17,76,'17:76','Và quả thật, chúng đã âm mưu làm cho Ngươi sợ hãi mà bỏ quê hương ra đi với mục đích trục xuất Ngươi ra khỏi đó. Nhưng trong trường hợp đó, bọn chúng cũng sẽ ở lại nơi đó (Makkah) chỉ một thời gian ngắn sau (khi) Ngươi (bỏ đi).');
INSERT INTO "hasan_abdulkarim" VALUES(17,77,'17:77','(Đó là) Đường lối (của TA) cho Sứ Giả nào trong số các Sứ Giả của TA đã được cử phái đến trước Ngươi và Ngươi sẽ không tìm thấy một sự thay đổi nào trong đường lối của TA.');
INSERT INTO "hasan_abdulkarim" VALUES(17,78,'17:78','Hãy chu đáo dâng lễ nguyện Salah từ lức mặt trời nghiêng bóng cho đến lúc màn đêm tối đặc(. Và hãy đọc Qur''an vào lúc hừng đông. Quả thật, đọc Qur''an vào lúc hừng đông được chứng giám (bởi Thiên Thần).');
INSERT INTO "hasan_abdulkarim" VALUES(17,79,'17:79','Và hãy thức một phần của ban đêm để dâng lễ nguyện Salah Tahajjud như một lễ phụ cho Ngươi; có lẽ Thượng Đế (Allah) của Ngươi sẽ nhấc Ngươi lên một địa vị vinh quang đáng ca ngợi.');
INSERT INTO "hasan_abdulkarim" VALUES(17,80,'17:80','Và hãy cầu nguyện, thưa: “Lạy Thượng Đế của bề tôi! Xin Ngài làm cho bề tôi đi vào (Madinah) bằng lối vào chân chính và làm cho bề tôi đi ra (khỏi Makkah) bằng lối ra chân chính và ban cho bề tôi từ Ngài một thẩm quyền (để yểm trợ bề tôi.)"');
INSERT INTO "hasan_abdulkarim" VALUES(17,81,'17:81','Và hãy tuyên bố: "Chân Lý đã đến, sự ngụy tạo phải tan biến. Quả thật, mọi sự ngụy tạo đều tiêu tan."');
INSERT INTO "hasan_abdulkarim" VALUES(17,82,'17:82','Và TA ban xuống trong Qur''an điều chữa lành (bệnh) và một Hồng Ân cho những người có đức tin; ngược lại, chỉ làm cho những kẻ sai quấy thêm thua thiệt.');
INSERT INTO "hasan_abdulkarim" VALUES(17,83,'17:83','Và khi TA ban ân huệ cho con người (vô đức tin), y quay bỏ đi và lánh xa (thay vì đến với TA) và khi gặp hoạn nạn, y buông xuôi một cách thất vọng.');
INSERT INTO "hasan_abdulkarim" VALUES(17,84,'17:84','Hãy bảo họ: “Mỗi người hành động theo đường lối, tín ngưỡng riêng. Do đó, Thượng Đế của các người biết rõ ai là người được hướng dẫn theo Chính Đạo.”');
INSERT INTO "hasan_abdulkarim" VALUES(17,85,'17:85','Họ hỏi Ngươi về Ruh (Thiên Thần Jibril). Hãy bảo họ: “Ruh là do Chỉ thị của Thượng Đế của Ta. Sự hiểu biết chỉ được ban cho các người chút ít.”');
INSERT INTO "hasan_abdulkarim" VALUES(17,86,'17:86','Và nếu muốn, TA sẽ lấy đi những điều mà TA đã mặc khải cho Ngươi; rồi Ngươi sẽ không thể tìm ra một vị bảo hộ nào giúp Ngươi chống lại TA (về việc này).');
INSERT INTO "hasan_abdulkarim" VALUES(17,87,'17:87','Trừ phi Thượng Đế của Ngươi khoan dung. Quả thật, Thiên An của Ngài ban cho Ngươi thật lớn.');
INSERT INTO "hasan_abdulkarim" VALUES(17,88,'17:88','Hãy bảo họ: “Nếu loài người và Jinn họp nhau lại để làm một (Kinh Sách) giống như Qur''an này thì chúng sẽ không làm được một quyển giống như nó, mặc dầu chúng cố gắng hỗ trợ lẫn nhau.”');
INSERT INTO "hasan_abdulkarim" VALUES(17,89,'17:89','Và chắc chắn, TA đã giải thích cho nhân loại trong Qur''an này đủ loại ngụ ngôn, nhưng đa số nhân loại từ chối (sự thật và) không tin tưởng.');
INSERT INTO "hasan_abdulkarim" VALUES(17,90,'17:90','Và họ nói: "Bọn ta không bao giờ tin tưởng nơi Ngươi (Muhammad) trừ phi Ngươi làm cho suối nước phụt từ dưới đất lên cho bọn ta."');
INSERT INTO "hasan_abdulkarim" VALUES(17,91,'17:91','"Hoặc Ngươi có một ngôi vườn chà là và vườn nho mà Ngươi làm cho những suối nước phụt lên ở chính giữa chúng."');
INSERT INTO "hasan_abdulkarim" VALUES(17,92,'17:92','"Hoặc Ngươi làm cho trời sụp xuống đè nát bọn ta thành từng mảnh đúng như lời Ngươi đã xác nhận hoặc Ngươi mang Allah và Thiên Thần xuống đối diện (với bọn ta)."');
INSERT INTO "hasan_abdulkarim" VALUES(17,93,'17:93','“Hoặc Ngươi có một ngôi nhà bằng vàng hay Ngươi đi lên trời; và bọn ta không bao giờ tin tưởng nơi việc thăng thiên của Ngươi trừ phi Ngươi mang xuống cho bọn ta một Kinh Sách mà bọn ta đọc được.” Hãy bảo họ: “Quang vinh và trong sạch thay Thượng Đế của Ta! Phải chăng ta chỉ là một người phàm được cử làm một Sứ Giả (của Allah)?”');
INSERT INTO "hasan_abdulkarim" VALUES(17,94,'17:94','Và không có gì cản trở nhân loại tin tưởng khi Chỉ Đạo đã đến với họ ngoại trừ điều mà họ nói: “Há Allah đã dựng một người phàm làm một Sứ Giả (của Ngài)?”');
INSERT INTO "hasan_abdulkarim" VALUES(17,95,'17:95','Hãy bảo họ: “Nếu trên trái đất chỉ có các Thiên Thần đi lại yên ổn thì chắc chắn TA sẽ phái một Thiên Thần từ trên trời xuống làm một Sứ Giả.”');
INSERT INTO "hasan_abdulkarim" VALUES(17,96,'17:96','Hãy bảo họ: “Allah đủ làm một nhân chứng giữa Ta và các người. Quả thật, Allah là Đấng Hằng Am tường, và Hằng Thấy các bầy tôi của Ngài.”');
INSERT INTO "hasan_abdulkarim" VALUES(17,97,'17:97','Và ai được Allah hướng dẫn thì sẽ đi đúng đường; và ai mà Ngài đánh lạc hướng thì ngoài Ngài, Ngươi sẽ không bao giờ tìm được cho chúng những kẻ bảo hộ nào. Và vào Ngày phục sinh, TA sẽ tập trung chúng lại, quỳ úp mặt xuống, mù, câm và điếc; chỗ ngụ của chúng sẽ là hỏa ngục. Mỗi lần ngọn lửa giảm xuống, TA sẽ gia tăng lửa ngọn thêm cho chúng.');
INSERT INTO "hasan_abdulkarim" VALUES(17,98,'17:98','Đó là phần thưởng dành cho chúng bởi vì thật sự chúng đã phủ nhận các dấu hiệu của TA và nói: “Phải chăng khi chúng tôi đã thành xương khô và mảnh vụn, chúng tôi sẽ được dựng sống lại thành một sự tạo hóa mới?”');
INSERT INTO "hasan_abdulkarim" VALUES(17,99,'17:99','Há họ không nhìn nhận rằng Allah, Đấng đã tạo hóa các tầng trời và trái đất, sẽ thừa khả năng tái tạo được cái mới giống như chúng hay sao? Ngài đã ấn định cho chúng một thời hạn mà không có gì phải nghi ngờ cả. Nhưng những kẻ làm điều sai quấy từ chối (sự thật), ngược lại chỉ (chấp nhận) sự không tin.');
INSERT INTO "hasan_abdulkarim" VALUES(17,100,'17:100','Hãy bảo họ: “Nếu các người làm chủ những kho tàng ''Hồng Ân'' của Thượng Đế của Ta thì lúc đó chắc chắn các người sẽ giữ chúng lại vì sợ tiêu pha hết. Bởi vì con người vốn keo kiệt.”');
INSERT INTO "hasan_abdulkarim" VALUES(17,101,'17:101','Và quả thật, TA đã ban cho Musa chín Phép Lạ rõ rệt. Ngươi hãy hỏi con cháu của Israel (sẽ rõ). Khi Y (Musa) đến gặp chúng, Fir-''awn bảo Y: “Hỡi Musa, ta nghĩ rằng ngươi là nhà ảo thuật."');
INSERT INTO "hasan_abdulkarim" VALUES(17,102,'17:102','(Musa) đáp: “Chắc chắn, ngài biết rõ không ai có khả năng ban những thứ này xuống làm bằng chứng trước mắt mọi người ngoại trừ Thượng Đế của các tầng trời và trái đất và quả thật, tôi nghĩ rằng ngài, ôi Fir-''awn, sắp bị tiêu diệt.”');
INSERT INTO "hasan_abdulkarim" VALUES(17,103,'17:103','Do đó, khi Fir-''awn nhất định loại trừ họ (con cháu Israel) khỏi lãnh thổ (Ai Cập), TA đã nhận chìm hắn và tất cả thuộc hạ của hắn (dưới biển Hồng hải).');
INSERT INTO "hasan_abdulkarim" VALUES(17,104,'17:104','Và sau hắn, TA đã phán cho con cháu của Israel như sau: Hãy định cư thanh bình trên đất hứa”. Và khi Lời Hứa sau cùng xảy đến, TA sẽ tập trung các ngươi lại, trộn lẫn với nhau.');
INSERT INTO "hasan_abdulkarim" VALUES(17,105,'17:105','Và TA đã ban Nó xuống bằng sự thật và Nó đã xuống bằng sự thật. Và TA chỉ phái Ngươi đến làm một người mang tin mừng và báo trước.');
INSERT INTO "hasan_abdulkarim" VALUES(17,106,'17:106','Và Qur''an, TA chia nó thành từng phần để Ngươi có thể đọc cho nhân loại theo từng giai đoạn và TA ban Nó xuống theo từng thời kỳ.');
INSERT INTO "hasan_abdulkarim" VALUES(17,107,'17:107','Hãy bảo họ: “Các người tin hay không tin nơi Nó, bởi vì những ai được ban cho sự hiểu biết trước (khi) Nó (được ban xuống), liền quỳ úp mặt xuống đất, phủ phục khi Nó được đọc ra cho họ.”');
INSERT INTO "hasan_abdulkarim" VALUES(17,108,'17:108','Và họ nói: “Quang vinh và trong sạch thay chúng tôi! Quả thật, Lời Hứa của Thượng Đế chúng tôi đa hoàn tất.”');
INSERT INTO "hasan_abdulkarim" VALUES(17,109,'17:109','Và họ quỳ úp mặt xuống đất khóc ra nước mắt và Nó làm cho họ khiêm tốn thêm.');
INSERT INTO "hasan_abdulkarim" VALUES(17,110,'17:110','Hãy bảo họ: “Cầu nguyện Allah hay cầu nguyện Al-Rahman, cầu nguyện Ngài với bất cứ danh xưng nào (đều tốt cả) bởi vì Ngài có các danh xưng tốt đẹp nhất. Và trong cuộc dâng lễ nguyện Salah của Ngươi chớ đọc kinh quá lớn tiếng cũng chớ quá nhỏ, mà hãy tìm lấy con đường chính giữa."');
INSERT INTO "hasan_abdulkarim" VALUES(17,111,'17:111','Và nói: “Mọi lời ca tụng đều dâng lên Allah, Đấng đã không nhận ai làm con trai (của Ngài) và không có một kẻ hợp tác nào chia sẻ quyền bính cai trị của Ngài và Ngài cũng không quá yếu đuối mà cần đến một người Bảo Hộ để giúp đỡ Ngài. Và hãy lam rạng rỡ sự Vĩ đại của Ngài.”');
INSERT INTO "hasan_abdulkarim" VALUES(18,1,'18:1','Mọi lời ca tụng đều dâng lên Allah, Đấng đã ban Kinh Sách (Qur''an) cho Người tôi trung của Ngài và không làm cho Nó quanh co.');
INSERT INTO "hasan_abdulkarim" VALUES(18,2,'18:2','(Một Kinh Sách) ngay thẳng (và rõ ràng) dùng để cảnh cáo (những người vô đức tin) về một sự trừng phạt nghiêm khắc từ Ngài và vừa mang tin mừng cho những người tin tưởng, những ai làm việc thiện, rằng họ sẽ nhận một phần thưởng tốt (thiên đàng).');
INSERT INTO "hasan_abdulkarim" VALUES(18,3,'18:3','Họ sẽ ở trong đó đời đời.');
INSERT INTO "hasan_abdulkarim" VALUES(18,4,'18:4','Và để cảnh báo những ai nói Allah có một đứa con trai.');
INSERT INTO "hasan_abdulkarim" VALUES(18,5,'18:5','Họ và cha mẹ của họ không có một chút hiểu biết nào về việc đó. Trọng đại thay lời lẽ đã thốt ra từ cửa miệng của họ! Quả thật, họ chỉ nói dối.');
INSERT INTO "hasan_abdulkarim" VALUES(18,6,'18:6','Có lẽ Ngươi đau buồn đến nỗi tổn hại đến bản thân trong việc bám theo dấu chân( của họ nếu họ không tin tưởng nơi câu chuyện này (Kinh Qur''an).');
INSERT INTO "hasan_abdulkarim" VALUES(18,7,'18:7','Sự thật là mọi vật mà TA đã làm ra trên quả đất chỉ nhằm trang hoàng cho đẹp, mục đích để TA (dùng chúng) thử thách ai trong nhân loại là người tốt nhất trong việc làm của họ.');
INSERT INTO "hasan_abdulkarim" VALUES(18,8,'18:8','Và sự thật là TA sẽ làm cho mọi vật trên đó (trái đất) thành một loại đất xấu (không sản xuất).');
INSERT INTO "hasan_abdulkarim" VALUES(18,9,'18:9','Hoặc phải chăng Ngươi nghĩ rằng những người Bạn của Hang núi và của Bia ký( là một kỳ quan trong những Dấu Lạ của TA hay sao?');
INSERT INTO "hasan_abdulkarim" VALUES(18,10,'18:10','(Hãy nhớ lại) khi đám thanh niên chạy vào Hang núi trốn và cầu nguyện thưa: “Lạy Thượng Đế bầy tôi! Xin Ngài khoan dung bầy tôi và sắp đặt công việc của bầy tôi được đúng đắn”.');
INSERT INTO "hasan_abdulkarim" VALUES(18,11,'18:11','Do đó, TA lấy tấm màn bịt tai chúng lại trong Hang núi trong một số năm (để chúng ngủ và không còn nghe thấy gì).');
INSERT INTO "hasan_abdulkarim" VALUES(18,12,'18:12','Rồi TA đánh thức chúng dậy để TA thử thách xem nhóm nào trong hai nhóm sẽ giỏi nhất trong việc tính toán thời gian mà chúng đã lưu lại nơi đó.');
INSERT INTO "hasan_abdulkarim" VALUES(18,13,'18:13','TA kể lại cho Ngươi (Muhammad) câu chuyện của chúng bằng sự thật. Quả thật, chúng là một đám thanh niên tin tưởng nơi Thượng Đế của chúng và TA gia tăng Chỉ Đạo cho chúng.');
INSERT INTO "hasan_abdulkarim" VALUES(18,14,'18:14','Và TA củng cố tấm lòng của chúng khi chúng đứng dậy thưa: “Thượng Đế của bầy tôi là Thượng Đế của các tầng trời và trái đất. Bầy tôi sẽ không bao giờ cầu nguyện một thần linh nào ngoài Ngài. Nếu làm nghịch lại thì quả thật bầy tôi đã nói ra lời lẽ xúc phạm tày trời."');
INSERT INTO "hasan_abdulkarim" VALUES(18,15,'18:15','"Những người (ngoại đạo) này trong đám người dân của bầy tôi đã tôn thờ những thần linh ngoài Ngài. Tại sao họ không tự đưa ra một thẩm quyền rõ rệt (để chứng minh cho việc làm của họ?) Bởi thế, còn ai sai quấy hơn kẻ đã đặt điều nói dối rồi đổ thừa cho Allah?"');
INSERT INTO "hasan_abdulkarim" VALUES(18,16,'18:16','(Đám thanh niên bảo nhau): “Và khi các anh lánh xa họ và những vật mà họ tôn thờ ngoài Allah, hãy chạy vào trong hang núi trốn, Thượng Đế của các anh sẽ tưới Hồng Ân của Ngài lên các anh và sắp đặt công việc được dễ dàng cho các anh.”');
INSERT INTO "hasan_abdulkarim" VALUES(18,17,'18:17','Và Ngươi (Muhammad) có thể nhìn thấy mặt trời mọc lệch về phía bên phải của cái hang của chúng và khi lặn, nó đi ngang qua chúng về phía bên trái trong lúc chúng nằm ngủ ngay tại một khoảng trống chính giữa cái hang. Đoa là một trong những Dấu Lạ của Allah. Ai mà Allah hướng dẫn thì sẽ tìm ra Chỉ Đạo. Và ai mà Allah đánh lạc hướng thì Ngươi sẽ không bao giờ tìm được cho y một người bảo hộ nào để dắt y đi đúng đường.');
INSERT INTO "hasan_abdulkarim" VALUES(18,18,'18:18','Và Ngươi đã nghĩ rằng chúng hãy còn thức nhưng kỳ thật chúng đang ngủ. Và TA đã lật trở thân người của chúng sang phải và trái. Và con chó của chúng duỗi thẳng hai chân trước ra trên bậc thềm của cái hang. Nếu nhìn chúng, chắc chắn Ngươi sẽ kinh hãi mà bỏ chạy và sẽ rất khiếp đãm chúng (trong lòng).');
INSERT INTO "hasan_abdulkarim" VALUES(18,19,'18:19','Và TA đã đánh thức chúng dậy (trong tình trạng đó) để chúng có dịp hỏi nhau; một người trong bọn nói: “Các anh ở lại đây bao lâu?” Chúng đáp: “Chúng mình ở đây một ngày hay một buổi gì đó.” Chúng nói: “Thượng Đế của các anh biết rõ các anh đã ở lại đây bao lâu. Thôi hãy cử một người của các anh cầm số tiền này của các anh xuống phố, nhờ tìm mua thức ăn tốt sạch nhất mang về cho các anh dùng và dặn anh ấy dòm ngố cẩn thận, chớ để cho một ai biết tung tích của các anh.');
INSERT INTO "hasan_abdulkarim" VALUES(18,20,'18:20','“Quả thật nếu họ (dân chứng) khám phá ra (tung tích của) các anh, họ sẽ ném đá giết các anh và bắt các anh trỏ lại con đường thờ phụng của họ và trong trường hợp đó các anh sẽ không bao giờ thành công.”');
INSERT INTO "hasan_abdulkarim" VALUES(18,21,'18:21','Và bằng cách đó, TA đã làm lộ tung tích của chúng cho (thiên hạ) biết để họ biết rằng Lời Lứa của Allah là sự thật và Giờ Xét Xử (cũng thật) không có gì phải nghi ngờ cả. (Hãy hồi tưởng lại) khi họ (dân cư trong thành phố) tranh luận với nhau về công việc của chúng. (Một số dân) nói: “Xây một tòa nhà lên (chỗ của) chúng. Thượng Đế của chúng biết rõ chúng.” Nhưng những người thắng thế trong việc bàn thảo đó lại nói: “Chắc chắn chúng ta sẽ xây cất một thánh đường lên (chỗ của) chúng.”');
INSERT INTO "hasan_abdulkarim" VALUES(18,22,'18:22','(Nay có một số người) nói: “Đám thanh niên đó gồm ba người, con chó của chúng nữa là bốn; (số người khác) nói: “Chúng gồm năm người, con chó của chúng nữa là sáu, (toàn là lời) đoán mò điều vô hình. Và (một số người khác nữa lại) nói: “Chúng gồm bảy người, con chó nữa là tám.” Hãy bảo họ (hỡi Muhammad!): “Thượng Đế của Ta biết rõ nhất số người của chúng. Chỉ một số ít biết chuyện đó. Bởi thế chớ tranh luận với ai về việc của chúng trừ phi biết rõ vấn đề và cũng chớ hỏi bất cứ ai về vấn đề của chúng.”');
INSERT INTO "hasan_abdulkarim" VALUES(18,23,'18:23','Và chớ bao giờ nói về một điều gì như sau: “Chắc chắn tôi sẽ làm điều đó ngày mai”.');
INSERT INTO "hasan_abdulkarim" VALUES(18,24,'18:24','Trừ phi (dè dặt nói:) ''Nếu Allah muốn như thế''. Và hãy niệm nhớ Thượng Đế của Ngươi khi Ngươi quên và nói: “Có lẽ Thượng Đế của Ta sẽ hướng dẫn tôi đến gần với Chân Lý hơn điều này.”');
INSERT INTO "hasan_abdulkarim" VALUES(18,25,'18:25','Và đám thanh niên ở lại trong Hang núi của chúng ba trăm năm và thêm chín năm nữa (theo âm lịch).');
INSERT INTO "hasan_abdulkarim" VALUES(18,26,'18:26','Hãy bảo: “Allah biết rõ nhất chúng đã ở lại đó bao lâu (bởi vì) Ngài nắm mọi bí mật của các tầng trời và trái đất. Ngài Thấy và Nghe rất rõ (mọi việc). Và chúng không có một Đấng Bảo Hộ nào ngoài Ngài, và Ngài cũng không có một kẻ hợp tác nào chia sẻ Quyết Định và Luật Lệ của Ngài.');
INSERT INTO "hasan_abdulkarim" VALUES(18,27,'18:27','Và hãy đọc (cho người dân) từ Kinh Sách (Qur’an) của Thượng Đế của Ngươi những điều đã được mặc khải cho Ngươi. Không ai ccs thể thay đổi được Lời Phán của Ngài(. Và Ngươi sẽ không bao giờ tìm được một chỗ nương tựa nào khác ngoài Ngài.');
INSERT INTO "hasan_abdulkarim" VALUES(18,28,'18:28','Và hãy kiên nhẫn sống với những người cầu nguyện Thượng Đế của họ sáng và chiều, chỉ mong tìm sắc diện của Ngài và chớ vì lý do thèm muốn vẽ hào nhoáng của đời sống trần tục mà Ngươi không lấy mắt ngó ngàng đến họ; Ngươi cũng chớ nghe theo lời của kẻ nào mà TA (Allah) đã làm cho lòng y lơ là việc tưởng nhớ TA và chỉ biết theo đuổi dục vọng của y; và công việc của y đã trở thành thái quá.');
INSERT INTO "hasan_abdulkarim" VALUES(18,29,'18:29','Và hãy bảo: “Chân Lý là từ Thượng Đế của các người.” Bởi thế, hãy để cho người nào muốn, được tự do tin tưởng; và để cho người nào muốn, được tự do không tin tưởng. Quả thật, TA đã chuẩn bị cho những kẻ làm điều sai quấy Lửa ngọn mà mái vòm sẽ bao nhốt chúng lại bên trong; và nếu chúng kêu la xin giảm bớt hình phạt thì chúng sẽ được giảm cho bằng một loại nước sôi giống như loại dầu sôi; nó sẽ lột hết da mặt của chúng. Thật khiếp đảm thay loại nước uống đó và xấu xa thay chỗ nghỉ (của chúng)!');
INSERT INTO "hasan_abdulkarim" VALUES(18,30,'18:30','Quả thật, đối với những ai có đức tin và làm việc thiện thì chắc chắn TA (Allah) sẽ không làm mất phần thưởng của bất cứ ai làm việc tốt.');
INSERT INTO "hasan_abdulkarim" VALUES(18,31,'18:31','Họ là những người sẽ được vào những Ngôi Vườn Vĩnh Cửu (thiên đàng), bên dưới có các dòng sông chảy. Trong đó, họ sẽ được trang điểm bằng vòng vàng và sẽ được phục sức bằng áo lụa thật mịn màu xanh lục có thêu trổ xinh đẹp. Họ sẽ tựa mình nằm nghỉ trên những chiếc tràng kỷ cao. Tuyệt diệu thay phần thưởng của họ và tốt đẹp thay giường nghỉ của họ!');
INSERT INTO "hasan_abdulkarim" VALUES(18,32,'18:32','Và hãy đưa ra thí dụ về hai người: một trong hai người đã được TA ban cho hai ngôi vườn nho rào bằng cây chà là. TA đặt giữa hai ngôi vườn một cái rẫy.');
INSERT INTO "hasan_abdulkarim" VALUES(18,33,'18:33','Cả hai ngôi vườn đều cho ra trái xum xuê và không có trái lép. Và TA làm cho một mạch nước phụt lên chính giữa hai ngôi vườn.');
INSERT INTO "hasan_abdulkarim" VALUES(18,34,'18:34','Và y (người chủ vườn) được mùa trái cây. Y nói với người bạn của y trong lúc đang trò chuyện với nhau: "Tôi được nhiều của và mạnh hơn anh vì đông người hơn."');
INSERT INTO "hasan_abdulkarim" VALUES(18,35,'18:35','Và y đi vào ngôi vườn và tự làm cho mình sai quấy, y nói: "Tôi nghĩ rằng ngôi vườn này sẽ không bao giờ tiêu tan."');
INSERT INTO "hasan_abdulkarim" VALUES(18,36,'18:36','"Tôi cũng nghĩ Giờ (Xét Xử) sẽ không xảy ra và nếu tôi thực sự được đưa về gặp Thượng Đế của tôi trở lại thì chắc chắn tôi sẽ tìm được huê lợi khá hơn ngôi vườn này."');
INSERT INTO "hasan_abdulkarim" VALUES(18,37,'18:37','Người bạn đáp lại trong lúc nói chuyện với y: "Phải chăng anh không tin tưởng nơi Đấng đã tạo anh ra từ cát bụi, rồi từ một giọt tinh dịch rồi uốn nắn anh thành một người bình thường?"');
INSERT INTO "hasan_abdulkarim" VALUES(18,38,'18:38','“Đối với tôi, Ngài là Allah, Thượng Đế của tôi và tôi không gán ghép một ai làm vị hợp tác cùng Thượng Đế của tôi cả."');
INSERT INTO "hasan_abdulkarim" VALUES(18,39,'18:39','"Thật ra khi bước vào vườn, anh phải nên nói: ''Đây là những gì Allah muốn, không có sức mạnh nào ngoài sức mạnh của Allah.'' Nếu anh thấy tôi kém hơn anh về tài sản và con cái."');
INSERT INTO "hasan_abdulkarim" VALUES(18,40,'18:40','“Có lẽ Thượng Đế của tôi sẽ ban cấp cho tôi điều tốt hơn ngôi vườn của anh và Ngài sẽ gởi những lưỡi tầm sét từ trên trời xuống tàn phá ngôi vườn của anh và biến nó thành một bãi đất trơn."');
INSERT INTO "hasan_abdulkarim" VALUES(18,41,'18:41','“Hoặc mạch nước của nó sẽ chảy mất tiêu xuống dưới lòng đất khiến anh sẽ không bao giờ tìm thấy mạch nước trở lại.”');
INSERT INTO "hasan_abdulkarim" VALUES(18,42,'18:42','Và mùa màng trái cây của y bị tiêu diệt toàn bộ. Do đó, y bắt đầu xoa hai lòng bàn tay (tỏ vẻ hối tiếc) về công lao và tiền bạc mà y đã chi tiêu vào việc khai khẩn và trồng trọt vườn nho. Và trái nho lẫn giàn nho đều ngã rạp xuống đất nằm ngổn ngang; và y nói: “Thật khổ cho thân tôi! Phải chi tôi không tổ hợp một ai với Thượng Đế của mình (thì đâu xảy ra cớ sự này!)”');
INSERT INTO "hasan_abdulkarim" VALUES(18,43,'18:43','Và ngoài Allah, y không có một nhóm đảng nào có thể giúp y (tránh khỏi sự trừng phạt) và cũng không tự cứu mình được.');
INSERT INTO "hasan_abdulkarim" VALUES(18,44,'18:44','Đấy, (vào Ngày Phán Xét Cuối Cùng) Quyền hành che chở chỉ thuộc riêng về Allah, Thượng Đế Đích Thực. Ngài là Đấng Tốt nhất trong việc tưởng thưởng, và là Đấng Tốt nhất ban cho kết quả cuối cùng.');
INSERT INTO "hasan_abdulkarim" VALUES(18,45,'18:45','Hãy đưa ra thí dụ về đời sống trần tục này cho họ (nghiệm). Nó giống như nước mưa mà TA ban từ trên trời xuống; cây cỏ thảo mộc nhờ hút lấy nó mà trở thành xanh tươi; rồi cây cỏ bắt đầu dần dần héo khô thành cộng rạ vỡ vụn mà gió thổi tung đi khắp nơi; và Allah là Đấng quyết định hết mọi việc.');
INSERT INTO "hasan_abdulkarim" VALUES(18,46,'18:46','Của cải và con cái là những món trang hoàng của đời sống trần tục này trong lúc chỉ có việc làm phúc đức mới tồn tại. Đối với Allah việc làm phúc đức là tốt nhất về phương diện tưởng thưởng và hy vọng.');
INSERT INTO "hasan_abdulkarim" VALUES(18,47,'18:47','Và Ngày mà TA sẽ dỡ mất các ngọn núi và Ngươi sẽ thấy mặt đất sẽ trống trải bằng phẳng và TA sẽ tập trung chúng lại và sẽ không bỏ sót một người nào.');
INSERT INTO "hasan_abdulkarim" VALUES(18,48,'18:48','Và chúng sẽ được dẫn đến trước mặt Thượng Đế của Ngươi sắp thành hàng ngũ chỉnh tề. Chắc chắn các ngươi đến gặp TA giống như tình trạng TA đã tạo các ngươi lần đầu. Phải chăng các ngươi đã nghĩ TA sẽ không bao giờ định đoạt việc hội ngộ này với các ngươi hay sao?');
INSERT INTO "hasan_abdulkarim" VALUES(18,49,'18:49','Và quyển sổ (của chúng) sẽ được mang đặt trước mặt. Rồi Ngươi sẽ thấy những kẻ tội lỗi kinh hãi về những điều ghi trong đó. Và chúng sẽ than: “Ôi, thật khổ thân chúng tôi! Một quyển sổ gì như thế này! Nó ghi không sót một điều nào dù nhỏ hay lớn.” Và chúng sẽ thấy xuất hiện trước mặt chúng tất cả những điều mà chúng đã làm. Và Thượng Đế của Ngươi sẽ không đối xử bất công với một ai cả.');
INSERT INTO "hasan_abdulkarim" VALUES(18,50,'18:50','Và khi TA phán cho các Thiên Thần: Hãy phủ phục trước Adam. Chúng phủ phục ngoại trừ Iblis. Nó thuộc loài Jinn(. Nó bất tuân Mệnh Lệnh của Thượng Đế của nó. Phải chăng các ngươi chấp nhận nó và con cháu của nó làm kẻ bảo hộ của các ngươi thay vì TA trong lúc chúng là kẻ thù của các ngươi hay sao? Việc đổi chác này của những kẻ làm điều sai quấy thật là xấu xa.');
INSERT INTO "hasan_abdulkarim" VALUES(18,51,'18:51','TA đâu có gọi chúng (Jinn) đến chứng kiến việc tạo hóa các tầng trời và trái đất và ngay cả việc tạo hóa bản thân của chúng; và TA cũng đã không nhận những kẻ dắt thiên hạ đi lạc làm người ủng hộ.');
INSERT INTO "hasan_abdulkarim" VALUES(18,52,'18:52','Và Ngày mà Ngài sẽ bảo (những kẻ tôn thờ Jinn:) “Các ngươi hãy cầu nguyện những kẻ mà các ngươi tưởng tượng là những vị ''hợp tác'' của TA!” Bởi thế, họ cầu nguyện chúng, nhưng chúng không đáp lời họ. Và TA sẽ đặt một bức chắn phân cách giữa bọn chúng.');
INSERT INTO "hasan_abdulkarim" VALUES(18,53,'18:53','Và những kẻ tội lỗi sẽ nhìn thấy hỏa ngục. Rồi chúng sẽ nghĩ rằng chúng sẽ sa vào đó và sẽ không tìm thấy một lối thoát nào ra khỏi đó.');
INSERT INTO "hasan_abdulkarim" VALUES(18,54,'18:54','Và quả thật TA đã trình bày trong Qur''an đủ loại hình ảnh thí dụ có lợi cho nhân loại; nhưng con người hay tranh luận nhất về nhiều vấn đề.');
INSERT INTO "hasan_abdulkarim" VALUES(18,55,'18:55','Và không có gì cản trở loài người tin tưởng khi Chỉ Đạo đã đến với họ và cầu xin Thượng Đế của họ tha thứ ngoại trừ việc mong chờ cho đường lối của những người xưa sẽ xảy đến với họ hoặc hình phạt (của Allah) sẽ đến trực diện họ.');
INSERT INTO "hasan_abdulkarim" VALUES(18,56,'18:56','Và TA đã cử phái các Sứ Giả đến chỉ để mang tin mừng và cảnh cáo (nhân loại); nhưng những kẻ không tin tưởng chỉ muốn dùng luận điệu giả dối của mình để cãi vã và đánh đổ Chân Lý. Và chúng mang những dấu hiệu và lời cảnh cáo của TA ra làm trò đùa để bỡn cợt.');
INSERT INTO "hasan_abdulkarim" VALUES(18,57,'18:57','Và còn ai sai quấy hơn kẻ đã được nhắc nhở bằng những Lời Mặc Khải của Thượng Đế của y nhưng quay mặt lánh xa chúng và quên mất những điều (tội lỗi) mà bàn tay của y đã gởi đi trước? Quả thật, TA (Allah) đã lấy những tấm màn phủ kín trái tim (tấm lòng) của chúng lại sợ rằng chúng hiểu được Nó (Qur''an) và làm cho tai của chúng điếc; và nếu Ngươi có gọi chúng đến với Chỉ Đạo thì trong trường hợp đó, chúng sẽ vĩnh viễn không bao giờ chấp nhận Chỉ Đạo.');
INSERT INTO "hasan_abdulkarim" VALUES(18,58,'18:58','Và Thượng Đế của Ngươi Hằng Tha Thứ, Rất Mực Khoan Dung. Nếu Ngài muốn gọi chúng đến xét xử về những điều (tội lỗi) mà chúng đã phạm thì chắc chắn Ngài sẽ thúc giục hình phạt chóng đến với chúng. Không, chúng còn có một dịp hẹn (gặp Ngài vào Ngày phục sinh.) Lúc đó, chúng sẽ không bao giờ tìm được một chỗ thoát thân.');
INSERT INTO "hasan_abdulkarim" VALUES(18,59,'18:59','(Người dân của) những thị trấn đó (của ''Ad, Thamud) mà TA đã tiêu diệt khi chúng làm quấy. Và TA đã định giờ hẹn để tiêu diệt chúng.');
INSERT INTO "hasan_abdulkarim" VALUES(18,60,'18:60','Và (hãy nhớ lại) khi Musa nói với tiểu đồng của Y: "Ta sẽ không bỏ cuộc mãi cho đến lúc Ta đến tận nơi tiếp giáp của hai vùng biển hoặc Ta sẽ tiếp tục cuộc hành trình vô hạn định."');
INSERT INTO "hasan_abdulkarim" VALUES(18,61,'18:61','Nhưng khi hai thầy trò đến chỗ tiếp giáp của hai biển, hai người quên lửng con cá( của họ. Nó đã tìm đường nhảy xuống biển lội đi tự do như đi trong đường hầm.');
INSERT INTO "hasan_abdulkarim" VALUES(18,62,'18:62','Do đó, khi hai thầy trò đã đi qua khỏi chỗ hẹn (mà không biết), (Musa) bảo tiểu đồng: “Hãy dọn bữa ăn sáng của chúng mình ra; chắc chắn chúng mình đã thấm mệt vì chuyến đi quá lâu này.”');
INSERT INTO "hasan_abdulkarim" VALUES(18,63,'18:63','(Tiểu đồng) thưa: “Thầy có thấy (điều gì hay không) khi chúng ta đang tiến đến mũi đá? Bởi vì tôi đã quên lửng (chuyện của) con Cá và chỉ có Shaytan đã làm cho tôi quên thưa (chuyện của nó) với Thầy; và nó đã tìm đường nhảy xuống biển đi mất dạng một cách kỳ diệu.”');
INSERT INTO "hasan_abdulkarim" VALUES(18,64,'18:64','Musa bảo: “Chỗ đó là địa điểm mà chúng ta đang tìm.” Do đó, hai người nương theo dấu vết cũ mà quay trở lại chỗ hẹn.');
INSERT INTO "hasan_abdulkarim" VALUES(18,65,'18:65','Thế là họ gặp được một người bề tôi của TA (Allah) mà TA đã ban cho Hồng Ân và đã được TA dạy cho Kiến thức của TA.');
INSERT INTO "hasan_abdulkarim" VALUES(18,66,'18:66','Musa thưa với Người lạ (Al-Khidir): "Tôi theo thầy được chăng để thầy dạy tôi Chân Lý cao thâm mà (Allah) đã chỉ dạy thầy?"');
INSERT INTO "hasan_abdulkarim" VALUES(18,67,'18:67','Người lạ đáp: “Quả thật ngươi sẽ không nhẫn nại được với ta.”');
INSERT INTO "hasan_abdulkarim" VALUES(18,68,'18:68','“Và làm sao ngươi có thể kiên nhẫn được với điều vượt quá sự hiểu biết của ngươi.”');
INSERT INTO "hasan_abdulkarim" VALUES(18,69,'18:69','(Musa) thưa: “Thầy sẽ thấy, nếu Allah muốn, tôi sẽ rất kiên nhẫn; và tôi sẽ không cãi thầy về bất cứ điều gì.”');
INSERT INTO "hasan_abdulkarim" VALUES(18,70,'18:70','Người lạ đáp: “Vậy nếu ngươi nhất định theo ta thì chớ hỏi ta về bất cứ điều gì trừ phi tự ta nói ra cho ngươi biết.”');
INSERT INTO "hasan_abdulkarim" VALUES(18,71,'18:71','Thế là hai người ra đi cho đến khi lên một chiếc thuyền, Người lạ (Khidir) bỗng đục lỗ chiếc thuyền. (Musa) lên tiếng: “Phải chăng thầy đục lỗ nó để nhận chìm những người (hành khách) trên thuyền này hay sao? Chắc chắn thầy đã làm một điều hết sức tồi tệ.”');
INSERT INTO "hasan_abdulkarim" VALUES(18,72,'18:72','Người lạ bảo: “Há ta đã không bảo chắc chắn ngươi sẽ không thể kiên nhẫn cùng với ta hay sao?”');
INSERT INTO "hasan_abdulkarim" VALUES(18,73,'18:73','(Musa) thưa: “Xin thầy chớ rầy tôi về điều tôi đã trót quên và làm khó dễ tôi trong việc (học hỏi) của tôi.”');
INSERT INTO "hasan_abdulkarim" VALUES(18,74,'18:74','Thế rồi hai người tiếp tục đi mãi cho đến khi gặp một đứa bé trai thì Người lạ giết chết nó. (Musa) lên tiếng: "Thầy đã giết một người vô tội như thế sao? Quả thật, thầy đã phạm một điều hết sức tội lỗi."');
INSERT INTO "hasan_abdulkarim" VALUES(18,75,'18:75','Người lạ đáp: “Há ta đã không bảo ngươi rằng ngươi sẽ không thể kiên nhẫn cùng với ta hay sao?”');
INSERT INTO "hasan_abdulkarim" VALUES(18,76,'18:76','(Musa) thưa: “Nếu sau này tôi còn hỏi thầy điều gì thì chớ để cho tôi theo thầy nữa. Chắc chắn thầy sẽ có đủ lý do để từ chối (lời khẩn khoản của) tôi.');
INSERT INTO "hasan_abdulkarim" VALUES(18,77,'18:77','Rồi hai người tiếp tục đi mãi cho đến khi gặp dân cư của một thị trấn. Hai người hỏi xin thức ăn của họ, nhưng bị từ chối. Sau đó, hai người thấy trong thị trấn có một bức tường gần muốn ngã, nhưng Người lạ dựng nó đứng thẳng trở lại. (Musa) lên tiếng: “Nếu thầy muốn, thầy có thể đòi thị dân trả công thầy về việc này!”');
INSERT INTO "hasan_abdulkarim" VALUES(18,78,'18:78','Người lạ bảo: “Đây là lúc ta và ngươi chia tay. Bây giờ ta cho ngươi biết ý nghĩa về những việc mà ngươi không có đủ kiên nhẫn để học hỏi.');
INSERT INTO "hasan_abdulkarim" VALUES(18,79,'18:79','“Về chiếc thuyền, nó là tài sản của những người nghèo làm ăn trên biển. Nhưng ta muốn làm cho nó hư đi bởi vì đằng sau họ có một ông vua hay dùng cường quyền mà tịch thu tất cả những chiếc thuyền (của người khác).');
INSERT INTO "hasan_abdulkarim" VALUES(18,80,'18:80','"Và về đứa trẻ, bởi vì cha mẹ của nó là những người có đức tin nên ta sợ rằng do hành động thái quá và vô ơn của nó (đối với Allah), nó sẽ làm cho cha mẹ của nó trở nên vô đức tin."');
INSERT INTO "hasan_abdulkarim" VALUES(18,81,'18:81','"Do đó, ta mong rằng Thượng Đế (Allah) của cha mẹ (của nó) sẽ ban lại cho hai người họ một đứa con trai khác tính tình trong sạch và gần với sự khoan dung hơn."');
INSERT INTO "hasan_abdulkarim" VALUES(18,82,'18:82','“Và về bức tường, bởi vì nó là tài sản của hai đứa trẻ mồ côi trong thị trấn và phía dưới có một kho tàng để dành cho chúng và người cha của chúng là một người ngoan đạo nên Thượng Đế (Allah) của ngươi (Musa) muốn rằng khi hai đứa trẻ ấy trưởng thành chúng sẽ lấy kho tàng của chúng ra (sử dụng), như là một Hồng Ân của Thượng Đế của ngươi. Và ta đã không tự ý làm ra chuyện đó(. Đó là lời giải thích về những điều mà ngươi không thể nhẫn nại được.”');
INSERT INTO "hasan_abdulkarim" VALUES(18,83,'18:83','Và họ hỏi Ngươi (Muhammad!) về Zdul Qarnain(. Hãy bảo họ: "Ta sẽ đọc cho các ngươi hồi ký của Người.”');
INSERT INTO "hasan_abdulkarim" VALUES(18,84,'18:84','Quả thật, TA (Allah) đã định cư Y (Zdul Qarnain) trên trái đất và đã ban cho Y phương tiện về mọi mặt.');
INSERT INTO "hasan_abdulkarim" VALUES(18,85,'18:85','Do đó, Y đi theo một con đường (để thi hành điều công lý).');
INSERT INTO "hasan_abdulkarim" VALUES(18,86,'18:86','Cho đến khi Y đến một nơi (của phía) mặt trời lặn, Y thấy nó lặn xuống một dòng suối nước đục ngầu và thấy một đám dân sống gần đó. Họ bảo: “Hỡi Zdul Qarnain, hoặc ngài trừng phạt chúng hoặc ngài đối xử nhân đạo với chúng.”');
INSERT INTO "hasan_abdulkarim" VALUES(18,87,'18:87','Y (Zdul Qarnain) nói: "Đối với ai làm điều sai quấy thì ta sẽ trừng trị y rồi y sẽ được đưa về trình diện Thượng Đế của y, rồi Ngài sẽ trừng phạt y vô cùng khủng khiếp."');
INSERT INTO "hasan_abdulkarim" VALUES(18,88,'18:88','“Nhưng đối với ai có đức tin và làm việc thiện thì y sẽ được một phần thưởng tốt và ta sẽ nói với y lời lẽ dịu dàng về mệnh lệnh của ta.”');
INSERT INTO "hasan_abdulkarim" VALUES(18,89,'18:89','Rồi Y (Zdul Qarnain) đi theo một con đường (khác).');
INSERT INTO "hasan_abdulkarim" VALUES(18,90,'18:90','Cho đến khi Y đến một nơi (về phía) mặt trời mọc và thấy nó mọc trên một đám người mà TA đã không ban một tấm phủ nào để che (nắng) mặt trời.');
INSERT INTO "hasan_abdulkarim" VALUES(18,91,'18:91','(Y để mặc họ sống) như thế. Chắc chắn TA (Allah) quán triệt mọi điều mà Y có.');
INSERT INTO "hasan_abdulkarim" VALUES(18,92,'18:92','Rồi Y đi theo một con đường (khác).');
INSERT INTO "hasan_abdulkarim" VALUES(18,93,'18:93','Cho đến khi Y đến một chỗ giữa hai ngọn núi và thấy gần hai ngọn núi có một số dân hầu như không hiểu được một lời lẽ nào cả.');
INSERT INTO "hasan_abdulkarim" VALUES(18,94,'18:94','Họ nói: “Hỡi Zdul Qamain, quả thật, Yajuj và Majuj phá hoại trong xứ. Chúng tôi có phải dâng lễ vật cho ngài để ngài có thể dựng một (bức thành) ngăn cản chúng khỏi chúng tôi được chăng?”');
INSERT INTO "hasan_abdulkarim" VALUES(18,95,'18:95','Y bảo: “Cái (quyền lực) mà Thượng Đế của Ta ban cho ta tốt hơn (cống vật của các người). Do đó, các người chỉ giúp ta sức lực, ta sẽ dựng một bức thành ngăn cách các người khỏi bọn chúng."');
INSERT INTO "hasan_abdulkarim" VALUES(18,96,'18:96','“Hãy mang đến cho ta những thỏi sắt.” Cuối cùng khi lắp bằng chỗ trũng của hai sườn núi, Y bảo: “Hãy (dùng bễ thổi của các người mà) thổi.” Cho đến khi Y làm cho nó (đỏ) như lửa, Y bảo: “Hãy mang đến cho ta loại chì nấu chảy để ta đổ lên đó.”');
INSERT INTO "hasan_abdulkarim" VALUES(18,97,'18:97','Bởi thế chúng (Yajuj và Majuj) không thể trèo qua tường và cũng không đào thủng nó được.');
INSERT INTO "hasan_abdulkarim" VALUES(18,98,'18:98','Y bảo: “Đây là một Hồng Ân từ Thượng Đế của Ta. Nhưng khi Lời Hứa của Thượng Đế của Ta thể hiện, Ngài sẽ làm cho nó vH tan tành. Và Lời hứa của Thượng Đế của Ta luôn luôn thật.”');
INSERT INTO "hasan_abdulkarim" VALUES(18,99,'18:99','Và vào Ngày đó, TA sẽ để cho chúng (ồ ạt) nổi lên như làn sóng chồng lên nhau, và tiếng còi hụ sẽ được thổi lên và TA sẽ tập trung chúng lại, toàn bộ.');
INSERT INTO "hasan_abdulkarim" VALUES(18,100,'18:100','Và vào Ngày đó, TA sẽ trưng bày hỏa ngục ra trước mặt những kẻ vô đức tin rõ ràng cho chúng thấy.');
INSERT INTO "hasan_abdulkarim" VALUES(18,101,'18:101','Những ai mà cặp mắt bị che lấp bởi một lớp màng khiến chúng quên lời Nhắc nhở của TA (Qur''an), và cũng không thể nghe được.');
INSERT INTO "hasan_abdulkarim" VALUES(18,102,'18:102','Thế phải chăng những kẻ không có đức tin nghĩ rằng chúng có thể nhận các bầy tôi của TA làm chủ nhân thay vì TA hay sao? Quả thật, TA đã chuẩn bị sẵn hỏa ngục cho những kẻ không tin làm một nơi cư ngụ.');
INSERT INTO "hasan_abdulkarim" VALUES(18,103,'18:103','Hãy bảo (họ): “Chúng tôi có phải báo cho các ông biết những kẻ mất mát nhất về phương diện việc làm (là ai chăng?)');
INSERT INTO "hasan_abdulkarim" VALUES(18,104,'18:104','“Những ai đã phí mất công lao nơi trần gian trong lúc họ nghĩ rằng họ đang được điều tốt trong việc làm của họ.”');
INSERT INTO "hasan_abdulkarim" VALUES(18,105,'18:105','Họ là những kẻ đã phủ nhận các dấu hiệu của Thượng Đế của họ và việc gặp gỡ lại Ngài (ở đời sau). Do đo, việc làm của họ sẽ trở thành vô nghĩa. Bởi thế vào Ngày phục sinh TA (Allah) sẽ không trả cho họ một cân (giá trị) nào cả.');
INSERT INTO "hasan_abdulkarim" VALUES(18,106,'18:106','Như thế; phần quả báo của họ là hỏa ngục bởi vì họ không tin tưởng và đã chế nhạo các Lời Mặc Khải và các Sứ Giả của TA.');
INSERT INTO "hasan_abdulkarim" VALUES(18,107,'18:107','Quả thật! Những ai có đức tin và làm việc thiện thì sẽ được ân thưởng các Ngôi Vườn Firdaws (nơi thiên đàng) làm nơi cư ngụ.');
INSERT INTO "hasan_abdulkarim" VALUES(18,108,'18:108','Trong đó, họ sẽ sống đời đời và sẽ không mong đi nơi khác.');
INSERT INTO "hasan_abdulkarim" VALUES(18,109,'18:109','Hãy bảo: “Nếu đại dương dùng làm mực (để viết) những Lời phán của Thượng Đế của Ta thì chắc chắn đại dương sẽ ráo cạn trước khi dứt Lời Phán của Thượng Đế của Ta dẫu chúng tôi cố mang một đại dương khác tương tự đến châm thêm cho nó.”');
INSERT INTO "hasan_abdulkarim" VALUES(18,110,'18:110','Hãy bảo: “Quả thật, Ta (Muhammad) là một người phàm như các người nhưng ta được mặc khải cho biết chắc chắn Thượng Đế của các người là một Thượng Đế Duy nhất. Do đó ai kỳ vọng được gặp lại Thượng Đế của y thì nên làm việc thiện và trong việc thờ phụng Thượng Đế (Allah) của y, chớ nên dựng một ai làm vị hợp tác của Ngài.');
INSERT INTO "hasan_abdulkarim" VALUES(19,1,'19:1','Kaf. Ha. Ya.''Ain. Sad.');
INSERT INTO "hasan_abdulkarim" VALUES(19,2,'19:2','(Đây là) một câu chuyện nhắc lại Hồng Ân của Thượng Đế của Ngươi đã được ban cấp cho người tôi trung của Ngài, Zakariya.');
INSERT INTO "hasan_abdulkarim" VALUES(19,3,'19:3','Khi Y âm thầm cầu xin Thượng Đế của Người.');
INSERT INTO "hasan_abdulkarim" VALUES(19,4,'19:4','Y thưa: "Lạy Thượng Đế của bề tôi! Thật sự xương cốt của bề tôi đã yếu ớt và mái đầu (của bề tôi) óng ánh hoa râm, nhưng lạy Thượng Đế, bề tôi không hề bất mãn trong việc van cầu Ngài."');
INSERT INTO "hasan_abdulkarim" VALUES(19,5,'19:5','"Và quả thật, bề tôi lo lắng cho thân tộc sau (khi) bề tôi (vĩnh viễn ra đi) trong lúc vợ của bề tôi lại hiếm hoi. Do đó xin Ngài ban cho bề tôi từ Ngài một đứa con để nối dõi."');
INSERT INTO "hasan_abdulkarim" VALUES(19,6,'19:6','"Để nó thật sự thừa kế bề tôi và thừa kế dòng dõi của Ya''qub và lạy Thượng Đế của bề tôi, xin Ngài chấp nhận nó (làm bề tôi của Ngài)."');
INSERT INTO "hasan_abdulkarim" VALUES(19,7,'19:7','Hỡi Zakariya, TA báo cho Ngươi tin mừng về một đứa con trai; tên của nó là Yahya (Giăng). Từ trước đến nay TA chưa hề đặt tên cho ai giống như thế.');
INSERT INTO "hasan_abdulkarim" VALUES(19,8,'19:8','Y thưa: “Lạy Thượng Đế của bề tôi! Làm sao bề tôi có thể có con trong lúc vợ của bề tôi hiếm hoi, vả lại bề tôi đã già yếu”.');
INSERT INTO "hasan_abdulkarim" VALUES(19,9,'19:9','(Allah) phán: “Đúng như thế! Thượng Đế của Ngươi phán: ‘Chuyện đó rất dễ đối với TA. Chắc chắn TA đã tạo ra Ngươi trước đây trong lúc Ngươi không là gì cả.”');
INSERT INTO "hasan_abdulkarim" VALUES(19,10,'19:10','(Zakariya) thưa: “Lạy Thượng Đế của bề tôi! Xin Ngài chỉ định cho bề tôi một dấu hiệu.” (Allah) phán: “dấu hiệu của Ngươi sẽ như sau: Ngươi sẽ không nói chuyện với mọi người trong ba đêm liên tiếp.”');
INSERT INTO "hasan_abdulkarim" VALUES(19,11,'19:11','Thế là (Zakariya) bước ra khỏi phòng tu kín đến gặp người dân của Y và ra dấu bảo họ tán dương (Allah) vào buổi sáng và buổi tối.');
INSERT INTO "hasan_abdulkarim" VALUES(19,12,'19:12','(Allah phán cho Yahya:) “Hỡi Yahya, hãy nắm vững Kinh Sách (Tawrah) bằng tất cả nghị lực của Ngươi!” Và TA ban cho Ngươi sự khôn ngoan dẫu rằng Ngươi hãy còn ít tuổi.');
INSERT INTO "hasan_abdulkarim" VALUES(19,13,'19:13','Và TA làm cho Y có lòng thương người và trong sạch; và sùng bái (Allah).');
INSERT INTO "hasan_abdulkarim" VALUES(19,14,'19:14','Và hiếu thảo với cha mẹ; và không hách dịch cũng không phản nghịch;');
INSERT INTO "hasan_abdulkarim" VALUES(19,15,'19:15','Và Bằng an được ban cho Y vào ngày Y chào đời, vào ngày Y lìa đời và vào ngày Y được phục sinh trở lại!');
INSERT INTO "hasan_abdulkarim" VALUES(19,16,'19:16','Và hãy nhắc lại trong Kinh Sách (Qur''an) về Maryam khi Nữ rời gia đình đi lánh mặt tại một nơi về phía Đông;');
INSERT INTO "hasan_abdulkarim" VALUES(19,17,'19:17','Nữ giăng một bức màn để lánh mặt họ. Rồi TA cử Thiên Thần Jibril của TA đến gặp Nữ. (Thiên Thần) hiện ra trước mặt Nữ như một người đàn ông toàn diện;');
INSERT INTO "hasan_abdulkarim" VALUES(19,18,'19:18','Nữ lên tiếng: "Tôi cầu xin Đấng Rất Mực Độ Lượng bảo vệ tôi tránh xa ông. (Chớ đến gần tôi) nếu ông là ngươi sợ Allah.”');
INSERT INTO "hasan_abdulkarim" VALUES(19,19,'19:19','(Thiên Thần) bảo: "Ta chỉ là một vị Sứ của Thượng Đế của Ngươi (đến báo cho biết việc Ngài) sẽ ban cho Ngươi một đứa con trai trong sạch.”');
INSERT INTO "hasan_abdulkarim" VALUES(19,20,'19:20','Nữ thưa (với Thiên sứ): “Làm sao tôi có thể có được một đứa con trai trong lúc không có một người phàm nào chạm đến mình tôi. Vả lại, tôi không phải là một người phụ nữ hư đốn.”');
INSERT INTO "hasan_abdulkarim" VALUES(19,21,'19:21','(Thiên Thần) bảo: “(Sự việc sẽ) đúng như thế! Thượng Đế (Allah) của Ngươi đã phán: ‘Việc (tạo hóa) đó rất dễ đối với TA’. Và chắc chắn TA sẽ làm cho Y (Ysa) thành một Dấu lạ cho nhân gian và một Hồng Ân từ TA. Và đó là một việc đã được (Allah) quyết định.’”');
INSERT INTO "hasan_abdulkarim" VALUES(19,22,'19:22','Sau đó Nữ (Maryam) thụ thai Y (Ysa) và cưu mang Y về tạm lánh mặt ở một nơi xa.');
INSERT INTO "hasan_abdulkarim" VALUES(19,23,'19:23','Rồi những cơn đau bụng hạ sanh đưa đẩy Nữ đến một thân cây chà là. Nữ (than thở:) “Ôi, khổ cho thân tôi! Phải chi tôi chết sớm trước việc này và bị quên bẵng đi (có tốt hơn không?)”');
INSERT INTO "hasan_abdulkarim" VALUES(19,24,'19:24','Nhưng (có tiếng) gọi Nữ từ bên dưới bảo: "Nàng chớ buồn phiền. Chắc chắn Thượng Đế của Nữ sẽ tạo một mạch nước bên dưới Nữ."');
INSERT INTO "hasan_abdulkarim" VALUES(19,25,'19:25','"Và hãy lắc thân cây chà là về phía Nữ, trái chín tươi sẽ rụng xuống mình Nữ."');
INSERT INTO "hasan_abdulkarim" VALUES(19,26,'19:26','“Do đó, hãy ăn và uống và vui vẻ. Nhưng nếu có thấy một người phàm nào thì hãy bảo: ‘Quả thật tôi đã nguyện nhịn chay với Đấng Rất Mực Độ Lượng. Bởi thế ngày nay tôi nhất định sẽ không nói chuyện với ai.’”');
INSERT INTO "hasan_abdulkarim" VALUES(19,27,'19:27','Rồi (sau khi hạ sanh) Nữ ẳm đứa con về nhà gặp lại người dân của Nữ. Thấy thế, người dân (của Nữ) bảo: "Hỡi Maryam, chắc chắn Nữ mang về một điều hết sức quái đản chưa từng thấy."');
INSERT INTO "hasan_abdulkarim" VALUES(19,28,'19:28','“Hỡi em gái của Harun(! Cha của Nữ không phải là một người ác đức và mẹ của Nữ cũng không phải là một phụ nữ lăng loàn.”');
INSERT INTO "hasan_abdulkarim" VALUES(19,29,'19:29','Nhưng Nữ lấy tay chỉ vào đứa bé. (Người dân) bảo: “Làm sao bọn ta có thể nói chuyện được với một đứa bé nằm trong nôi?”');
INSERT INTO "hasan_abdulkarim" VALUES(19,30,'19:30','(Đứa bé Ysa tức Giê Su) lên tiếng: "Ta là một người bề tôi của Allah. Ngài ban cho Ta Kinh Sách và chỉ định Ta làm một Nabi."');
INSERT INTO "hasan_abdulkarim" VALUES(19,31,'19:31','"Và Ngài ban phúc cho Ta bất cứ nơi nào Ta ở và truyền cho Ta dâng lễ nguyện Salah và đóng Zakah suốt thời gian Ta còn sống."');
INSERT INTO "hasan_abdulkarim" VALUES(19,32,'19:32','"Và hiếu thảo với mẹ Ta; và sẽ không hách dịch và vô phúc."');
INSERT INTO "hasan_abdulkarim" VALUES(19,33,'19:33','“Và Salam (Bằng An) được ban cho Ta vào ngày Ta chào đời, vào ngày Ta lìa đời và vào ngày Ta sẽ được phục sinh trở lại.”');
INSERT INTO "hasan_abdulkarim" VALUES(19,34,'19:34','Đó là (câu chuyện của) Ysa con trai của Maryam: (và là) lời xác thực mà (những kẻ không tin) hãy còn nghi ngờ.');
INSERT INTO "hasan_abdulkarim" VALUES(19,35,'19:35','Việc cho rằng Allah tự nhận (cho mình) một đứa con trai không thích hợp (với Quyền Năng Siêu Phàm của Ngài.) Thật quang vinh và trong sạch thay Ngài! Khi nào Ngài quyết định một việc, Ngài chỉ phán với nó “Hãy thành!” thì nó sẽ thành như thế.');
INSERT INTO "hasan_abdulkarim" VALUES(19,36,'19:36','Và (Ysa bảo): “Quả thật Allah là Thượng Đế của Ta và là Thượng Đế của các người. Do đó hãy thờ phụng Ngài. Đây là Chính Đạo.”');
INSERT INTO "hasan_abdulkarim" VALUES(19,37,'19:37','Nhưng những giáo phái bất đồng ý kiến với nhau (về sự thật này). Bởi thế, khốn khổ cho những ai phủ nhận việc sắp xảy ra Ngày (Xét Xử) Vĩ đại!');
INSERT INTO "hasan_abdulkarim" VALUES(19,38,'19:38','Chúng sẽ nghe và thấy rõ về Ngày (Xét xử) mà chúng sẽ đến trình diện TA. Nhưng ngày nay những kẻ làm điều sai quấy lầm lạc công khai.');
INSERT INTO "hasan_abdulkarim" VALUES(19,39,'19:39','Và hãy cảnh cáo chúng về Ngày (chúng sẽ) thở dài khi công việc đã được quyết định bởi vì chúng vẫn còn lơ là và không tin tưởng.');
INSERT INTO "hasan_abdulkarim" VALUES(19,40,'19:40','Chính TA sẽ thừa hưởng trái đất kể cả những ai sống trên đó và chúng sẽ được đưa trở về gặp lại TA (để chịu sự xét xử).');
INSERT INTO "hasan_abdulkarim" VALUES(19,41,'19:41','Và hãy nhắc lại trong Kinh Sách (Qur''an) về Ibrahim. Quả thật, Y là một người chân thật, một Nabi.');
INSERT INTO "hasan_abdulkarim" VALUES(19,42,'19:42','Khi (xưa) Y thưa với phụ thân: "Thưa cha! Tại sao cha tôn thờ những vật không nghe cũng không thấy được và không mang lợi gì về cho cha?"');
INSERT INTO "hasan_abdulkarim" VALUES(19,43,'19:43','"Thưa cha! Chắc chắn con đã tiếp thu được kiến thức mà cha đã không lĩnh hội được; bởi thế, xin cha hãy nghe con; con sẽ đưa cha dến một con đường bằng phẳng."');
INSERT INTO "hasan_abdulkarim" VALUES(19,44,'19:44','"Thưa cha! Cha chớ tôn thờ Shaytan (bởi vì) quả thật, Shaytan là một tên phản nghịch đã từng chống đối Đấng (Allah) Rất Mực Độ Lượng."');
INSERT INTO "hasan_abdulkarim" VALUES(19,45,'19:45','“Thưa cha! Con sợ Đấng (Allah) Rất Mực Độ Lượng sẽ trừng phạt cha bằng một hình phạt khiến cha trở thành một người bạn của Shaytan (trong hỏa ngục).”');
INSERT INTO "hasan_abdulkarim" VALUES(19,46,'19:46','(Người cha) đáp: “Hẳn con ghét những thần linh của cha lắm hay sao, hỡi con Ibrahim! Nếu con không từ bỏ thái độ này, cha sẽ ném đá giết con; và hãy đi đâu cho khuất mắt cha trong một thời gian lâu.”');
INSERT INTO "hasan_abdulkarim" VALUES(19,47,'19:47','(Ibrahim) thưa: "Con mong cha gặp mọi sự bằng an. Con sẽ cầu xin Thượng Đế của con tha thứ cho cha. Quả thật Ngài Hằng Rộng lượng với con."');
INSERT INTO "hasan_abdulkarim" VALUES(19,48,'19:48','“Và tôi sẽ từ biệt các ông và những kẻ mà các ông cầu nguyện ngoài Allah; và tôi sẽ cầu nguyện Thượng Đế của Ta; tôi hy vọng sẽ không đến nỗi vô phúc với lời cầu xin (chân thành) với Thượng Đế của Ta.”');
INSERT INTO "hasan_abdulkarim" VALUES(19,49,'19:49','Do đó, khi Ibrahim từ biệt họ và những vật mà họ tôn thờ ngoài Allah, TA đã ban cho Y (đứa con) Is-haaq và (đứa cháu nội) Yaqub; và mỗi đứa đã được TA cử làm Nabi.');
INSERT INTO "hasan_abdulkarim" VALUES(19,50,'19:50','Và TA đã ban Hồng Ân cho con cháu của Ibrahim và làm vinh danh Họ qua tiếng nói trung thực (của Họ).');
INSERT INTO "hasan_abdulkarim" VALUES(19,51,'19:51','Và hãy nhắc lại trong Kinh Sách (Qur''an) về Musa. Quả thật, Y là một người chất phác; và là một Sứ Giả, một Nabi.');
INSERT INTO "hasan_abdulkarim" VALUES(19,52,'19:52','Và TA đã gọi Y từ phía bên phải của ngọn núi (Sinai) và đưa Y xích lại gần để (TA) nói nhỏ;');
INSERT INTO "hasan_abdulkarim" VALUES(19,53,'19:53','Và TA lấy lòng Khoan dung mà gia ân cho Y (bằng cách cử) người anh của Y, Harun, làm một Nabi (để phụ tá).');
INSERT INTO "hasan_abdulkarim" VALUES(19,54,'19:54','Và hãy nhắc lại trong Kinh Sách (Qur''an) về Isma''il. Quả thật, Y là một người giữ đúng lời hứa. Và là một Sứ Giả, một Nabi.');
INSERT INTO "hasan_abdulkarim" VALUES(19,55,'19:55','Và Y đã từng ra lệnh cho người nhà dâng lễ nguyện Salah và đóng Zakah, và Thượng Đế của Y hài lòng với Y.');
INSERT INTO "hasan_abdulkarim" VALUES(19,56,'19:56','Và hãy nhắc lại trong Kinh Sách (Qur''an) về Idris (Enoch). Quả thật, Y là một người chân thật, một Nabi.');
INSERT INTO "hasan_abdulkarim" VALUES(19,57,'19:57','Và TA đã nhấc Y lên một địa vị cao nhã.');
INSERT INTO "hasan_abdulkarim" VALUES(19,58,'19:58','Họ là những người mà Allah đã gia ân trong số các Nabi xuất thân từ con cháu của Adam và từ những người mà TA đã chuyên chở trên chiếc tàu cùng với Nuh và từ con cháu của Ibrahim và của Israel và từ những người mà TA đã hướng dẫn và chọn lựa. Mỗi lần những Lời Mặc Khải của Đấng Rất Mực Độ Lượng được đọc ra cho họ, họ sụp xuống quỳ lạy và khóc.');
INSERT INTO "hasan_abdulkarim" VALUES(19,59,'19:59','Nhưng tiếp theo họ là một hậu thế bỏ bê lễ nguyện Salah và theo đuổi những dục vọng thấp hèn. Bởi thế họ sẽ sớm bị hủy diệt.');
INSERT INTO "hasan_abdulkarim" VALUES(19,60,'19:60','Ngoại trừ những ai biết hối cải và tin tưởng và làm việc thiện. Bởi thế, họ là những người sẽ vào thiên đàng và sẽ không bị đối xử thiệt thòi một tí nào:');
INSERT INTO "hasan_abdulkarim" VALUES(19,61,'19:61','Các Ngôi Vườn Vĩnh Cửu (thiên đàng) mà Đấng (Allah) Rất Mực Độ Lượng đã hứa với các bề tôi của Ngài, dẫu rằng vô hình. Quả thật, Lời Hứa của Ngài là điều nhất định sẽ phải xảy ra.');
INSERT INTO "hasan_abdulkarim" VALUES(19,62,'19:62','Trong đó, họ sẽ không nghe thấy một lời tầm phào nào mà chỉ nghe lời chúc tụng ''Bằng an''. Trong đó họ sẽ được cung phụng đầy đủ vào buổi sáng và buổi tối.');
INSERT INTO "hasan_abdulkarim" VALUES(19,63,'19:63','Đó là thiên đàng mà TA sẽ ban cấp cho người ngay chính sợ Allah trong số bề tôi cua TA để thừa hưởng.');
INSERT INTO "hasan_abdulkarim" VALUES(19,64,'19:64','Và chúng tôi (Thiên Thần) chỉ xuống trần theo Mệnh Lệnh của Thượng Đế (Allah) của Ngươi (hỡi Muhammad!). Điều gì (xảy ra) trước chúng tôi và điều gì (xảy ra) sau chúng tôi và điều gì (xảy ra) giữa hai điều đó đều là của Ngài cả. Và Thượng Đế của Ngươi là Đấng không hề quên sót (một điều gì).');
INSERT INTO "hasan_abdulkarim" VALUES(19,65,'19:65','(Ngài là) Thượng Đế (Đấng Chủ Tể) của các tầng trời và trái đất và của những gì giữa trời đất. Do đó, hãy thờ phụng Ngài; và hãy kiên nhẫn trong việc thờ phụng Ngài. Ngươi có biết có ai giống Ngài hay không?');
INSERT INTO "hasan_abdulkarim" VALUES(19,66,'19:66','Và con người (không có đức tin) lên tiếng: “Sao? khi chết rồi tôi sẽ được dựng sống lại hay sao?”');
INSERT INTO "hasan_abdulkarim" VALUES(19,67,'19:67','Và hẳn con người không nhớ rằng chính TA đã tạo hóa ra y trước đây trong lúc y không là gì cả hay sao?');
INSERT INTO "hasan_abdulkarim" VALUES(19,68,'19:68','Bởi thế, nhân danh Thượng Đế của Ngươi (hỡi Muhammad!) chắc chắn TA sẽ tập trung chúng lại và cả bè lũ Shaytan nữa; rồi TA sẽ dẫn chúng đến quỳ gối xung quanh hỏa ngục.');
INSERT INTO "hasan_abdulkarim" VALUES(19,69,'19:69','Rồi từ mỗi giáo phái TA sẽ lôi cổ từng tên ương ngạnh nhất đã chống đối Đấng (Allah) Rất Mực Độ Lượng ra (trình diện).');
INSERT INTO "hasan_abdulkarim" VALUES(19,70,'19:70','Rồi TA biết chắc trong bọn chúng ai là kẻ đáng bị đốt nướng nhất trong đó (hỏa ngục).');
INSERT INTO "hasan_abdulkarim" VALUES(19,71,'19:71','Không một ai trong các người lại khỏi sa xuống (hỏa ngục). Và đối với Thượng Đế của Ngươi (Muhammad!) đó là một quyết định chắc chắn sẽ phải thể hiện.');
INSERT INTO "hasan_abdulkarim" VALUES(19,72,'19:72','Rồi TA sẽ giải cứu những ai sợ và thần phục Allah và TA sẽ để cho những kẻ làm điều sai quấy quỳ gối trong đó.');
INSERT INTO "hasan_abdulkarim" VALUES(19,73,'19:73','Và khi những Lời Mặc Khải rõ rệt của TA được đọc ra để nhắc chúng, những kẻ vô đức tin nói với những người tin tưởng như sau: “Giữa hai chúng ta, nhóm nào là nhóm có địa vị tốt và đáng kính trọng hơn cả trong Hội đồng (của thành phố Makkah)?”');
INSERT INTO "hasan_abdulkarim" VALUES(19,74,'19:74','Nhưng trước chúng đã có bao nhiêu thế hệ giàu có và hào nhoáng hơn chúng đã bị TA tiêu diệt?');
INSERT INTO "hasan_abdulkarim" VALUES(19,75,'19:75','Hãy bảo chúng: “Ai sống trong lầm lạc thì Đấng (Allah) Rất Mực Độ Lượng sẽ nới lỏng sợi dây cho y sống thêm một thời gian cho đến khi chúng thấy điều đã được hứa với chúng thể hiện trước mắt - hoặc là hình phạt hoặc là Giờ (Tận thế) - thì lúc đó chúng sẽ biết ai là kẻ có địa vị tồi tệ nhất và có lực lượng yếu ớt nhất.');
INSERT INTO "hasan_abdulkarim" VALUES(19,76,'19:76','Và Allah sẽ gia tăng Chỉ Đạo cho những ai đi đúng đường. Và những việc làm từ thiện đối với Thượng Đế của Ngươi sẽ tồn tại lâu dài, và sẽ tốt nhất về phương diện tưởng thưởng và kết cuộc.');
INSERT INTO "hasan_abdulkarim" VALUES(19,77,'19:77','Thế Ngươi có thấy (loại) người đã phủ nhận những Lời Mặc Khải của TA nhưng lại nói: “Chắc chắn tôi sẽ được ban cấp của cải và con cái.”');
INSERT INTO "hasan_abdulkarim" VALUES(19,78,'19:78','Phải chăng y đã nhìn thấu điều vô hình hoặc đã nhận một Lời Cam Kết nào với Đấng (Allah) Rất Mực Độ Lượng?');
INSERT INTO "hasan_abdulkarim" VALUES(19,79,'19:79','Nhất định không! TA sẽ cho ghi sổ điều y đã nói và sẽ gia tăng hình phạt cho y (trong hỏa ngục).');
INSERT INTO "hasan_abdulkarim" VALUES(19,80,'19:80','Và TA sẽ thừa kế y về những thứ mà y đã nói bởi vì y đến gặp lại TA (tay không) trơ trọi một mình (sau khi chết).');
INSERT INTO "hasan_abdulkarim" VALUES(19,81,'19:81','Và họ chấp nhận những thần linh khác ngoài Allah mục đích nhờ chúng giúp họ thêm quyền thế và uy danh.');
INSERT INTO "hasan_abdulkarim" VALUES(19,82,'19:82','Nhất định không! Những thần linh đó sẽ phủ nhận công trình thờ phụng của họ và sẽ trở thành đối thủ chống lại họ.');
INSERT INTO "hasan_abdulkarim" VALUES(19,83,'19:83','Há Ngươi không thấy việc TA đã cử những tên Shaytan đến với những kẻ vô đức tin hầu xúi giục chúng nổi trận lôi đình?');
INSERT INTO "hasan_abdulkarim" VALUES(19,84,'19:84','Bởi thế chớ vội chống báng chúng. Quả thật, TA chỉ gia hạn cho chúng một số (ngày gia hạn) nhất định.');
INSERT INTO "hasan_abdulkarim" VALUES(19,85,'19:85','Vào một Ngày TA sẽ tập trung những người ngay chính sợ Allah đến trình diện Đấng Rất Mực Độ Lượng như một phái đoàn .');
INSERT INTO "hasan_abdulkarim" VALUES(19,86,'19:86','Và TA sẽ lùa những tên tội lỗi xuống hỏa ngục như một đàn thú.');
INSERT INTO "hasan_abdulkarim" VALUES(19,87,'19:87','Không ai có quyền can thiệp ngoại trừ người nào đã nhận từ Đấng (Allah) Rất Mực Độ Lượng một Lời Cam Kết.');
INSERT INTO "hasan_abdulkarim" VALUES(19,88,'19:88','Và chúng nói: “Đấng Rất Mực Độ Lượng đã có một đứa con trai.”');
INSERT INTO "hasan_abdulkarim" VALUES(19,89,'19:89','Chắc chắn các ngươi đã dựng đúng một điều tội lỗi vô cùng tày trời;');
INSERT INTO "hasan_abdulkarim" VALUES(19,90,'19:90','Vì thế mà các tầng trời gần như muốn vỡ tung, trái đất gần như muốn bể ra làm hai và những quả núi gần như muốn sụp đổ tan tành.');
INSERT INTO "hasan_abdulkarim" VALUES(19,91,'19:91','Bởi chúng bịa đặt điều cho rằng Đấng Rất Mực Độ Lượng có con trai;');
INSERT INTO "hasan_abdulkarim" VALUES(19,92,'19:92','Và thật hết sức phi lý cho rằng Đấng Rất Mực Độ Lượng có một đứa con trai;');
INSERT INTO "hasan_abdulkarim" VALUES(19,93,'19:93','Bởi thật tất cả những ai trong các tầng trời và trái đất đều đến trình diện Đấng Rất Mực Độ Lượng như một người bề tôi.');
INSERT INTO "hasan_abdulkarim" VALUES(19,94,'19:94','Quả thật, Ngài đã kiểm kê và đánh số họ một cách chính xác.');
INSERT INTO "hasan_abdulkarim" VALUES(19,95,'19:95','Và vào Ngày phục sinh, từng người một sẽ đơn độc đến trình diện Ngài.');
INSERT INTO "hasan_abdulkarim" VALUES(19,96,'19:96','Quả thật, những ai có đức tin và làm việc thiện sẽ được Đấng Rất Mực Độ Lượng thương yêu.');
INSERT INTO "hasan_abdulkarim" VALUES(19,97,'19:97','Bởi thế TA làm cho Nó (Qur''an) thành dễ dàng với chiếc lưỡi của Ngươi mà Ngươi dùng để báo tin mừng cho những người ngay chính sợ Allah và để cảnh cáo đám người hay cãi vã.');
INSERT INTO "hasan_abdulkarim" VALUES(19,98,'19:98','Và có bao nhiêu thế hệ trước họ đã bị TA tiêu diệt? Há Ngươi có tìm thấy một người nào của bọn chúng hoặc nghe một lời xầm xì nào của chúng hay không?');
INSERT INTO "hasan_abdulkarim" VALUES(20,1,'20:1','Ta. Ha.');
INSERT INTO "hasan_abdulkarim" VALUES(20,2,'20:2','TA ban Qur''an xuống cho Ngươi (Muhammad) không phải để làm Ngươi buồn khổ.');
INSERT INTO "hasan_abdulkarim" VALUES(20,3,'20:3','Mà đó là một Lời Nhắc Nhở dành cho những người có lòng kính sợ (Allah).');
INSERT INTO "hasan_abdulkarim" VALUES(20,4,'20:4','Một sự Mặc Khải từ Đấng đã tạo hóa trái đất và các tầng trời cao.');
INSERT INTO "hasan_abdulkarim" VALUES(20,5,'20:5','Đấng Rất Mực Độ Lượng an vị trên chiếc Ngai Vương.');
INSERT INTO "hasan_abdulkarim" VALUES(20,6,'20:6','Mọi vật trong các tầng trời và trái đất cũng như những gì nằm giữa chúng và những gì nằm sâu dưới lòng đất đều là của Ngài (Allah).');
INSERT INTO "hasan_abdulkarim" VALUES(20,7,'20:7','Và nếu Ngươi có nói lớn tiếng thì quả thật Ngài vẫn biết rõ những điều bí mật và thầm kín.');
INSERT INTO "hasan_abdulkarim" VALUES(20,8,'20:8','Allah! Không có Thượng Đế nào khác cả duy chỉ có Ngài. Ngài mang những tên gọi tốt đẹp nhất.');
INSERT INTO "hasan_abdulkarim" VALUES(20,9,'20:9','Và Ngươi đã biết câu chuyện của Musa hay chưa?');
INSERT INTO "hasan_abdulkarim" VALUES(20,10,'20:10','Khi thấy một ngọn lửa, Y (Musa) bảo gia đình của Y: “Mọi người hãy tạm dừng chân ở đây. Ta sẽ đến chỗ ngọn lửa biết đâu có thể mang về cho mọi người một cục than hồng hoặc biết đâu có thể tìm thấy nơi đống lửa đó một Chỉ Đạo.”');
INSERT INTO "hasan_abdulkarim" VALUES(20,11,'20:11','Nhưng khi Y tiến đến đống lửa thì bổng nhiên có tiếng gọi bảo: "Hỡi Musa!"');
INSERT INTO "hasan_abdulkarim" VALUES(20,12,'20:12','"Quả thật TA là Thượng Đế của Ngươi. Ngươi hãy cởi giày của Ngươi ra bởi đích thực Ngươi đang ở nơi thung lũng Tuwa linh thiêng."');
INSERT INTO "hasan_abdulkarim" VALUES(20,13,'20:13','"Và TA đã chọn Ngươi. Bởi thế, hãy lắng nghe điều mặc khải."');
INSERT INTO "hasan_abdulkarim" VALUES(20,14,'20:14','"Quả thật, TA là Allah. Không có Thượng Đế nào khác ngoài TA. Do đó, hãy thờ phụng TA và hãy dâng lễ nguyện Salah để tưởng nhớ TA."');
INSERT INTO "hasan_abdulkarim" VALUES(20,15,'20:15','"Thật sự Giờ Tận Thế đang tiến đến nhưng TA muốn giữ kín nó để cho mỗi linh hồn nuôi hy vọng sẽ được ân thưởng tùy theo sự cố gắng của nó."');
INSERT INTO "hasan_abdulkarim" VALUES(20,16,'20:16','“Do đó, chớ để cho ai không có đức tin nơi nó (Giờ Tận Thế) và chạy theo những dục vọng của bản thân làm cho Ngươi xao lãng nó e rằng Ngươi sẽ bị tiêu diệt."');
INSERT INTO "hasan_abdulkarim" VALUES(20,17,'20:17','"Và vật gì nơi tay phải của Ngươi vậy, hỡi Musa?"');
INSERT INTO "hasan_abdulkarim" VALUES(20,18,'20:18','Y thưa: “Đó là chiếc gậy mà bề tôi dùng để tựa mình và đập rụng lá cây cho đàn cừu của bề tôi và nhiều công dụng khác nữa.”');
INSERT INTO "hasan_abdulkarim" VALUES(20,19,'20:19','(Allah) phán: “Hãy quẳng nó xuống, hỡi Musa!”');
INSERT INTO "hasan_abdulkarim" VALUES(20,20,'20:20','Thế là Y ném nó xuống. Nó liền biến thành một con rắn biết bò.');
INSERT INTO "hasan_abdulkarim" VALUES(20,21,'20:21','(Allah) phán: "Hãy cầm lấy nó và chớ sợ. TA sẽ làm cho nó trở lại nguyên trạng ban đầu."');
INSERT INTO "hasan_abdulkarim" VALUES(20,22,'20:22','"Và hãy áp bàn tay (phải) của Ngươi sát vào ba sườn của Ngươi, nó sẽ hiện ra trăng toát, không có gì đau đớn cả. Một Phép lạ khác."');
INSERT INTO "hasan_abdulkarim" VALUES(20,23,'20:23','"Để TA trưng cho Ngươi thấy một số Dấu Lạ lớn nhất của TA."');
INSERT INTO "hasan_abdulkarim" VALUES(20,24,'20:24','“Ngươi hãy đi gặp Fir-''awn (Phê-rô). Quả thật, nó đã tàn ác quá mức.”');
INSERT INTO "hasan_abdulkarim" VALUES(20,25,'20:25','(Musa) thưa: "Lạy Thượng Đế, xin Ngài nới rộng lòng ngực của bề tôi."');
INSERT INTO "hasan_abdulkarim" VALUES(20,26,'20:26','"Và xin Ngài làm cho công tác của bề tôi được dễ dàng"');
INSERT INTO "hasan_abdulkarim" VALUES(20,27,'20:27','"Và xin Ngài tháo cái gút (ngọng) ra khỏi lưỡi của bề tôi."');
INSERT INTO "hasan_abdulkarim" VALUES(20,28,'20:28','"Để chúng (đối phương) có thể hiểu được lời nói của bề tôi."');
INSERT INTO "hasan_abdulkarim" VALUES(20,29,'20:29','“Và xin Ngài cử một người phụ tá cho bề tôi từ gia đình của bề tôi."');
INSERT INTO "hasan_abdulkarim" VALUES(20,30,'20:30','"Harun, người em của bề tôi."');
INSERT INTO "hasan_abdulkarim" VALUES(20,31,'20:31','"Xin Ngài gia tăng sức mạnh cho bề tôi bởi nó (Harun)."');
INSERT INTO "hasan_abdulkarim" VALUES(20,32,'20:32','"Và xin Ngài hãy để nó chia sẻ công việc của bề tôi."');
INSERT INTO "hasan_abdulkarim" VALUES(20,33,'20:33','"Để cả hai bề tôi có thể tán dương Ngài nhiều."');
INSERT INTO "hasan_abdulkarim" VALUES(20,34,'20:34','"Và tưởng nhớ Ngài thật nhiều."');
INSERT INTO "hasan_abdulkarim" VALUES(20,35,'20:35','“Bởi vì quả thật Ngài Hằng thấy bầy tôi.”');
INSERT INTO "hasan_abdulkarim" VALUES(20,36,'20:36','(Allah) phán: "Chắc chắn những điều Ngươi yêu cầu được chấp thuận, hỡi Musa!"');
INSERT INTO "hasan_abdulkarim" VALUES(20,37,'20:37','"Và quả thật, TA đã gia ân cho ngươi vào một dịp khác."');
INSERT INTO "hasan_abdulkarim" VALUES(20,38,'20:38','"Khi TA đã mặc khải cho mẹ của Ngươi điều mặc khải."');
INSERT INTO "hasan_abdulkarim" VALUES(20,39,'20:39','"Rằng hãy đặt nó (Musa) vào trong chiếc rương và ném nó xuống sông; dòng sông sẽ đẩy giạt nó vào bờ; kẻ thù của TA và cũng là kẻ thù của nó sẽ vớt nó lên bờ.” Và TA đã ban cho Ngươi tình thương từ TA (hỡi Musa) để Ngươi được nuôi dưỡng dưới Mắt (săn sóc) của TA.');
INSERT INTO "hasan_abdulkarim" VALUES(20,40,'20:40','"Khi người chị của Ngươi la cà đi theo và nói: Quí ngài có muốn tôi chỉ cho quí ngài một người để chăm sóc đứa bé hay không? Bằng (cách) đó TA mang Ngươi trả lại cho mẹ của Ngươi để cho cặp mắt của nữ dịu lại và hết lo buồn. Và Ngươi đã (lỡ tay) giết chết một người (dân Ai Cập) nhưng TA đã cứu Ngươi ra khỏi cảnh nguy khốn và đã thử thách Ngươi với một sự thử thách lớn rồi Ngươi lưu vong nhiều năm với người dân Madyan. Sau đó, Ngươi đến đây như đã định, hỡi Musa!"');
INSERT INTO "hasan_abdulkarim" VALUES(20,41,'20:41','"Và TA đã rèn luyện Ngươi (để làm việc) cho TA."');
INSERT INTO "hasan_abdulkarim" VALUES(20,42,'20:42','"Hãy lên đường, ngươi và em của ngươi, với Phép Lạ của TA, và chớ xao lãng việc tưởng nhớ TA."');
INSERT INTO "hasan_abdulkarim" VALUES(20,43,'20:43','“Hai ngươi hãy đi gặp Fir-''awn. Quả thật, hắn đã tàn ác quá mức."');
INSERT INTO "hasan_abdulkarim" VALUES(20,44,'20:44','"Tuy nhiên hãy ăn nói với hắn lời lẽ nhã nhặn may ra vì thế hắn sẽ lưu ý đến Lời cảnh tỉnh hoặc sẽ biết kính sợ."');
INSERT INTO "hasan_abdulkarim" VALUES(20,45,'20:45','Hai người (Musa và Harun) thưa: “Lạy Thượng Đế của bầy tôi! Quả thật, bầy tôi sợ rằng y (Fir-''awn) sẽ có hành động quá quắt hoặc tàn bạo với bầy tôi.”');
INSERT INTO "hasan_abdulkarim" VALUES(20,46,'20:46','(Allah) phán: "Chớ sợ! Chắc chắn TA luôn ở cùng với hai ngươi. TA nghe và thấy hết mọi việc."');
INSERT INTO "hasan_abdulkarim" VALUES(20,47,'20:47','"Do đó, hai ngươi hãy lên đường đi gặp hắn và bảo: Quả thật chúng tôi là Sứ Giả của Thượng Đế của ngài. Xin ngài hãy để cho con cháu của Israel ra đi với chúng tôi và chớ trừng phạt họ. Thật sự, chúng tôi đến gặp ngài với một Phép Lạ từ Thượng Đế của ngài. Và sự bằng an đến với ai tuân theo Chỉ Đạo."');
INSERT INTO "hasan_abdulkarim" VALUES(20,48,'20:48','"Quả thật chúng tôi được (Thượng Đế chúng tôi) mặc khải (cho biết) sự trừng phạt (đang chờ) ai là kẻ phủ nhận (Thông điệp của Ngài) và quay bỏ đi."');
INSERT INTO "hasan_abdulkarim" VALUES(20,49,'20:49','(Fir-''awn) bảo: "Thế ai là Thượng Đế của hai ngươi, hỡi Musa?"');
INSERT INTO "hasan_abdulkarim" VALUES(20,50,'20:50','(Musa) đáp: “Thượng Đế chúng tôi là Đấng đã ban cho mọi vật hình thể và bản chất rồi hướng dẫn (chúng).”');
INSERT INTO "hasan_abdulkarim" VALUES(20,51,'20:51','(Fir-''awn) bảo: “Thế đối với những người thuộc thế hệ trước thì thế nào?”');
INSERT INTO "hasan_abdulkarim" VALUES(20,52,'20:52','(Musa) đáp: "Sự việc đó thuộc về kiến thức ở nơi Thượng Đế của tôi được ghi chép trong Kinh Sách ở nơi Ngài; Thượng Đế của tôi không nhầm lẫn cũng không quên sót (điều gì)."');
INSERT INTO "hasan_abdulkarim" VALUES(20,53,'20:53','Đấng đã làm trái đất thành một giường ngủ cho các ngươi và mở ra nơi đó những con đường thông thương qua lại và ban nước mưa từ trên trời xuống cho các ngươi (chính là TA - Allah); TA dùng nó để làm mọc ra đủ loại cây, từng cặp khác nhau.');
INSERT INTO "hasan_abdulkarim" VALUES(20,54,'20:54','Các ngươi hãy ăn (rau củ, trái quả từ chúng ) và nuôi gia súc của các ngươi. Quả thật, trong sự việc đó là các dấu hiệu cho những người thông hiểu (để nghiệm ra điều chân lý).');
INSERT INTO "hasan_abdulkarim" VALUES(20,55,'20:55','Từ nó (trái đất) TA đã tạo hóa các ngươi và sẽ hoàn các người về lại với nó và từ nó TA sẽ đưa các người trở ra một lần nữa.');
INSERT INTO "hasan_abdulkarim" VALUES(20,56,'20:56','Và quả thật TA đã trưng cho hắn (Fir-''awn) thấy tất cả Phép Lạ và Bằng Chứng của TA, nhưng hắn đã phủ nhận và chối bỏ.');
INSERT INTO "hasan_abdulkarim" VALUES(20,57,'20:57','Hắn bảo: "Phải chăng ngươi đến gặp bọn ta với dụng ý dùng các pháp thuật của ngươi đuổi bọn ta ra khỏi lãnh thổ của bọn ta hay sao, hỡi Musa!"');
INSERT INTO "hasan_abdulkarim" VALUES(20,58,'20:58','"Nhưng bọn ta cũng có thể đưa ra pháp thuật tương tự để đối chọi với ngươi. Do đó giữa bọn ta và ngươi hãy hẹn nhau tại một nơi (thách đấu), bọn ta và ngươi sẽ không lỡ hẹn!.”');
INSERT INTO "hasan_abdulkarim" VALUES(20,59,'20:59','(Musa) đáp: “Cuộc hẹn của quí ngài sẽ nhằm vào Ngày đại hội và hãy cho dân chúng tụ tập vào lúc mặt trời lên cao khoảng một sào.”');
INSERT INTO "hasan_abdulkarim" VALUES(20,60,'20:60','Do đó, Fir-''awn lui về, bàn thảo kế hoạch rồi đến nơi hẹn.');
INSERT INTO "hasan_abdulkarim" VALUES(20,61,'20:61','Musa bảo họ: “Quí ngài sắp mắc họa đến nơi rồi! Chớ đặt điều nói dối rồi đổ thừa cho Allah e rằng Ngài sẽ tiêu diệt quí ngài bằng một hình phạt. Và kẻ ngụy tạo chắc chắn sẽ thất bại.”');
INSERT INTO "hasan_abdulkarim" VALUES(20,62,'20:62','Thế rồi, chúng bàn cãi với nhau về công việc của chúng và giấu kín cuộc mật nghị.');
INSERT INTO "hasan_abdulkarim" VALUES(20,63,'20:63','Chúng bảo nhau: "Hai tên này quả thật là hai tên phù thủy, chúng muốn dùng pháp thuật để đuổi quí ngài ra khỏi lãnh thổ và xóa bỏ lối sống của quí ngài."');
INSERT INTO "hasan_abdulkarim" VALUES(20,64,'20:64','“Do đó, hãy hợp sức trong kế hoạch rồi đến nơi hẹn, và đi theo đội ngũ chỉnh tề. Ngày nay, ai cao tay ấn chắc chắn sẽ thắng cuộc.”');
INSERT INTO "hasan_abdulkarim" VALUES(20,65,'20:65','Chúng bảo: “Hỡi Musa hoặc ngươi ném (bửu bối) xuống (trước) hoặc bọn ta là người đầu tiên ném xuống?”');
INSERT INTO "hasan_abdulkarim" VALUES(20,66,'20:66','(Musa) đáp: “Không, quí vị ném trước.” Thế là bằng pháp thuật của chúng, chúng đã gây ảo giác trước Y (Musa) để Y nhìn thấy những sợi dây cũng như những chiếc gậy của chúng đang cử động.');
INSERT INTO "hasan_abdulkarim" VALUES(20,67,'20:67','Thấy thế, Musa đâm sợ trong lòng.');
INSERT INTO "hasan_abdulkarim" VALUES(20,68,'20:68','TA phán "Chớ sợ! Chắc chắn nhà ngươi cao tay ấn hơn."');
INSERT INTO "hasan_abdulkarim" VALUES(20,69,'20:69','“Và hãy ném bảo vật nơi tay phải của ngươi xuống, nó sẽ nuốt hết những vật mà chúng đã làm ra. Quả thật, vật mà chúng đã làm chỉ là xảo thuật của một nhà ảo thuật. Và nhà ảo thuật sẽ không thành công dù từ đâu đến.”');
INSERT INTO "hasan_abdulkarim" VALUES(20,70,'20:70','Bởi thế, những tên phù thủy buông mình xuống phủ phục (khi chứng kiến phép màu đích thực của Musa). Chúng thưa: “Chúng tôi tin tưởng nơi Thượng Đế của Harun và Musa."');
INSERT INTO "hasan_abdulkarim" VALUES(20,71,'20:71','(Fir-''awn) bảo: “Các ngươi đã táo gan tin tưởng nơi hắn trước khi ta cho phép các ngươi ư? Chính hắn là tên cầm đầu của các ngươi nên hắn đã dạy các ngươi pháp thuật! Bởi thế chắc chắn ta sẽ cho chặt tay và chân của các ngươi mỗi bên một cái; và chắc chắn ta sẽ cho đóng đinh các ngươi trên thân cây chà là để cho các ngươi biết giữa hai bên ai sẽ trừng phạt dữ dội và lâu hơn.”');
INSERT INTO "hasan_abdulkarim" VALUES(20,72,'20:72','(Các tên phù thủy) đáp: "Chúng tôi sẽ không bao giờ xem ngài quí hơn những bằng chứng rõ rệt đã đến với chúng tôi và quí hơn Đấng đã sáng tạo ra chúng tôi. Cho nên, ngài cứ quyết định làm điều gì ngài muốn; thật sự, ngài chỉ có thể định đoạt điều gì liên quan đến đời sống trần tục này mà thôi!"');
INSERT INTO "hasan_abdulkarim" VALUES(20,73,'20:73','“Thật sự, chúng tôi đã tin tưởng nơi Thượng Đế (Allah) của chúng tôi. Chúng tôi hy vọng Ngài sẽ tha thứ cho chúng tôi về tội lỗi của chúng tôi và (về tội đã làm) những phù phép mà ngài đã bắt ép chúng tôi làm. Bởi vì Allah Ưu Việt và Vĩnh Cửu.”');
INSERT INTO "hasan_abdulkarim" VALUES(20,74,'20:74','Quả thật ai đến trình diện Thượng Đế của y (vào Ngày Xét Xử) như một kẻ tội lỗi thì chắc chắn sẽ sa vào hỏa ngục, một nơi y sẽ không chết và cũng sẽ không sống.');
INSERT INTO "hasan_abdulkarim" VALUES(20,75,'20:75','Và ai đến trình diện Ngài như một người có đức tin và đã làm việc thiện thì là những người sẽ được ân thưởng cấp bậc ưu hạng:');
INSERT INTO "hasan_abdulkarim" VALUES(20,76,'20:76','Những Ngôi Vườn Vĩnh Cửu (thiên đàng), bên dưới có các dòng sông chảy. Họ sẽ vào ở trong đó đời đời. Và đó là phần thưởng của những người đã tẩy sạch bản thân mình (khỏi tội).');
INSERT INTO "hasan_abdulkarim" VALUES(20,77,'20:77','Và quả thật, TA đã mặc khải cho Musa: “Hãy dẫn các bầy tôi của TA ra đi ban đêm và (dùng chiếc gậy) đánh xuống (biển) hầu dọn đường ráo khô cho họ băng qua biển (Hồng Hải). Chớ sợ (đám Fir-''awn) đuổi theo bắt cũng chớ lo sợ (bị chết chìm dưới nước).”');
INSERT INTO "hasan_abdulkarim" VALUES(20,78,'20:78','Thế là, Fir-''awn cùng với quân lính của hắn đuổi theo bắt họ nhưng sóng biển đã bao phủ và nhận úp chúng toàn bộ.');
INSERT INTO "hasan_abdulkarim" VALUES(20,79,'20:79','Và Fir-''awn đã dẫn người dân của y đi lạc (khỏi Chính Đạo) và đã không hướng dẫn họ.');
INSERT INTO "hasan_abdulkarim" VALUES(20,80,'20:80','Hỡi con cháu của Israel! TA đã cứu thoát các ngươi khỏi kẻ thù của các ngươi và đã kết ước với các ngươi ở phía bên phải của ngọn núi (Sinai) và đã ban ân phúc và chim cút xuống cho các ngươi.');
INSERT INTO "hasan_abdulkarim" VALUES(20,81,'20:81','(Và TA phán:) "Các ngươi hãy dùng các món thực phẩm tốt và sạch mà TA đã cung cấp cho các ngươi nhưng chớ thái quá e rằng cơn Thịnh Nộ của TA sẽ giáng lên các ngươi; và ai mà cơn Thịnh Nộ của TA giáng nhằm phải thì chắc chắn sẽ bị tiêu diệt."');
INSERT INTO "hasan_abdulkarim" VALUES(20,82,'20:82','“Nhưng thật sự TA cũng Hằng Tha Thứ cho những ai biết hối cải và có đức tin và làm việc thiện rồi theo đúng Chỉ Đạo (cho đến chết).”');
INSERT INTO "hasan_abdulkarim" VALUES(20,83,'20:83','Và (khi Musa lên đến ngọn núi Sinai, Allah phán:) "Điều chi giục Ngươi bỏ người dân của Ngươi đến đây trước vậy, hỡi Musa?"');
INSERT INTO "hasan_abdulkarim" VALUES(20,84,'20:84','(Musa) thưa: "Họ bám sát theo dấu chân của bề tôi; bề tôi vội đến trình diện Ngài, lạy Thượng Đế, để mong Ngài hài lòng."');
INSERT INTO "hasan_abdulkarim" VALUES(20,85,'20:85','(Allah) phán: “Quả thật TA đã thử thách đám dân của Ngươi sau (khi) Ngươi (ra đi) và tên Samiri( đã dẫn họ lầm lạc.”');
INSERT INTO "hasan_abdulkarim" VALUES(20,86,'20:86','Bởi thế, Musa quay về gặp đám dân của Y vừa giận vừa buồn. Y bảo: "Này hỡi dân ta! Thượng Đế (Allah) của các người đã không hứa với các người một lời hứa tốt đẹp hay sao? Phải chăng lời hứa của Ngài quá lâu đối với các người hay là các người muốn cơn Thịnh Nộ của Thượng Đế của các người sẽ giáng xuống các người để rồi các người đã nuốt lời hứa với ta?"');
INSERT INTO "hasan_abdulkarim" VALUES(20,87,'20:87','Họ đáp: "Chúng tôi không tự quyền bội ước với thầy nhưng vì chúng tôi bị bắt vác nặng nữ trang của toàn dân (Ai Cập) cho nên chúng tôi quẳng chúng (vào lửa) theo đề xuất của Samiri."');
INSERT INTO "hasan_abdulkarim" VALUES(20,88,'20:88','Rồi y (Samiri) đúc ra cho họ một con bò con, thân hình của nó (phát ra tiếng giống tiếng) rống (của con bò). Rồi họ bảo: "Đây là thần linh của các người và là thần linh của Musa nhưng (Musa) đã quên."');
INSERT INTO "hasan_abdulkarim" VALUES(20,89,'20:89','Thế, họ đã không nhận thấy nó (bò đúc) không trả lời họ được tiếng nào và không thể hãm hại cũng không làm lợi gì được cho họ hay sao?');
INSERT INTO "hasan_abdulkarim" VALUES(20,90,'20:90','Và quả thật Harun cũng đã bảo họ trước đó: “Hỡi dân ta! Các người chỉ bị thử thách bởi nó (con bò đúc) và quả thật Thượng Đế của các người là Đấng Rất Mực Độ Lượng. Do đó hãy nghe theo ta và tuân lệnh ta.”');
INSERT INTO "hasan_abdulkarim" VALUES(20,91,'20:91','Họ đáp: “Chúng tôi sẽ không bao giờ ngưng thờ nó cho đến khi nào Musa (Môi-se) trở về gặp chúng tôi.”');
INSERT INTO "hasan_abdulkarim" VALUES(20,92,'20:92','(Musa) bảo: "Hỡi Harun! Điều gì cản em (ngăn họ) khi thấy họ lầm lạc?"');
INSERT INTO "hasan_abdulkarim" VALUES(20,93,'20:93','“Há em không tuân theo anh? Em lại trái lệnh anh thế ư?”');
INSERT INTO "hasan_abdulkarim" VALUES(20,94,'20:94','(Harun) đáp: "Hỡi anh trai của em! Chớ nắm râu và đầu của em! Quả thật em đã sợ việc anh sẽ nói: ''em đã gây chia rẽ giữa con cháu của Israel và đã không thi hành lời dặn của anh''."');
INSERT INTO "hasan_abdulkarim" VALUES(20,95,'20:95','(Musa) bảo: “Hỡi Samiri! Mục tiêu của ngươi là gì (khi ngươi đúc ra con bò này?”');
INSERT INTO "hasan_abdulkarim" VALUES(20,96,'20:96','Y đáp: "Tôi thấy điều mà họ không thấy. Bởi thế tôi đã hốt một nắm (đất) nơi dấu chân (ngựa) của Thiên sứ (Jibril) và ném nó (lên con bò đúc). Tâm trí của tôi đã xúi giục tôi như thế.”');
INSERT INTO "hasan_abdulkarim" VALUES(20,97,'20:97','(Musa) bảo y: “Hãy cút đi! Suốt đời này (hình phạt) dành cho ngươi sẽ như sau: ngươi sẽ nói (với người ta:) “Chớ chạm đến mình tôi! và (trong tương lai) ngươi sẽ gặp một lời hứa (trừng phạt) mà ngươi sẽ không bao giờ tránh khỏi. Và bây giờ ngươi hãy giương mắt ra nhìn thần linh của ngươi, vật mà ngươi đã dốc lòng sùng bái; chắc chắn bọn ta sẽ đốt nó (ra tro) rồi sẽ vãi tung nó xuống biển.”');
INSERT INTO "hasan_abdulkarim" VALUES(20,98,'20:98','Thượng Đế của các ngươi chỉ là Allah, Đấng mà ngoài Ngài, không có một Thượng Đế nào khác. Và sự Hiểu Biết của Ngài bao la, tóm thâu hết mọi vật.');
INSERT INTO "hasan_abdulkarim" VALUES(20,99,'20:99','TA kể lại cho Ngươi (Muhammad) một vài tin tức về các diễn biến trước đây như thế. Và quả thật, chính TA đã ban cho Ngươi Thông điệp nhắc nhở này (Qur''an).');
INSERT INTO "hasan_abdulkarim" VALUES(20,100,'20:100','Ai lánh xa Nó (Qur''an) thì sẽ vác gánh nặng (tội lỗi) vào Ngày phục sinh.');
INSERT INTO "hasan_abdulkarim" VALUES(20,101,'20:101','Họ sẽ ở mãi trong (tình trạng) đó. Và gánh nặng (tội lỗi) của họ vào Ngày phục sinh thật là xấu xa.');
INSERT INTO "hasan_abdulkarim" VALUES(20,102,'20:102','Ngày mà tiếng Còi sẽ được thổi vang; và ngày đó TA sẽ cho tập trung những kẻ tội lỗi, xanh mặt (vì sợ hãi).');
INSERT INTO "hasan_abdulkarim" VALUES(20,103,'20:103','Chúng xầm xì bàn tán với nhau: “Các anh chỉ ở lại (đây) lâu khoảng chừng mười ngày.”');
INSERT INTO "hasan_abdulkarim" VALUES(20,104,'20:104','TA biết rõ những điều chúng sẽ nói khi một người xét đoán giỏi nhất trong bọn lên tiếng: “Chắc chắn các anh sẽ ở lại đây lâu chỉ khoảng một ngày.”');
INSERT INTO "hasan_abdulkarim" VALUES(20,105,'20:105','Và chúng hỏi Ngươi về những quả núi, hãy bảo chúng: "Thượng Đế (Allah) của ta sẽ bứng chúng và vãi đi tứ tán."');
INSERT INTO "hasan_abdulkarim" VALUES(20,106,'20:106','Rồi Ngài (Allah) sẽ để cho nó (trái đất) thành bình địa.');
INSERT INTO "hasan_abdulkarim" VALUES(20,107,'20:107','Nơi đó, Ngươi sẽ thấy không có gì quanh co và lồi lõm.');
INSERT INTO "hasan_abdulkarim" VALUES(20,108,'20:108','Vào Ngày đó, (nhân loại) sẽ đi theo vị Gọi (Thiên Thần). Y chẳng có ý gì lừa dối; và những tiếng nói sẽ tắt lịm đi trước mặt Đấng Rất Mực Độ Lượng cho nên Ngươi sẽ không còn nghe thấy gì ngoài tiếng xầm xì.');
INSERT INTO "hasan_abdulkarim" VALUES(20,109,'20:109','Vào Ngày đó, việc can thiệp sẽ vô giá trị ngoại trừ của ai mà Đấng Rất Mực Độ Lượng cho phép và lời yêu cầu của y được chấp thuận.');
INSERT INTO "hasan_abdulkarim" VALUES(20,110,'20:110','Ngài (Allah) biết điều gì trước họ (nhân loại) và điều gì sau họ và họ không biết một tí gì về nó theo sự hiểu biết (của họ).');
INSERT INTO "hasan_abdulkarim" VALUES(20,111,'20:111','(Tất cả) những gương mặt sẽ gục xuống trước Đấng Hằng Sống, Đấng Tự Hữu và Nuôi Dưỡng (vạn vật); và ai gánh vác tội lỗi trên lưng thì chắc chắn sẽ thất bại.');
INSERT INTO "hasan_abdulkarim" VALUES(20,112,'20:112','Và ai làm việc thiện và có đức tin thì sẽ không sợ bị thiệt thòi và mất phần.');
INSERT INTO "hasan_abdulkarim" VALUES(20,113,'20:113','Và TA đã ban Nó (Qur''an) xuống bằng tiếng Ả Rập đúng như thế và đã giải thích cặn kẽ trong đó có những lời cảnh báo (về sự trừng phạt) để may ra chúng sợ hoặc may ra nó sẽ làm cho chúng tưởng nhớ (đến Allah).');
INSERT INTO "hasan_abdulkarim" VALUES(20,114,'20:114','Bởi thế, cao cả nhất là Allah, Đức Vua thật sự. Và chớ giục giã với Qur''an trước khi việc mặc khải Nó sẽ hoàn tất cho Ngươi. Và hãy cầu nguyện, thưa: “Lạy Thượng Đế của bề tôi! Xin Ngài gia tăng kiến thức cho bề tôi.”');
INSERT INTO "hasan_abdulkarim" VALUES(20,115,'20:115','Và quả thật, TA đã kết ước với Adam trước đây nhưng Y đã quên; và TA thấy Y không cương quyết.');
INSERT INTO "hasan_abdulkarim" VALUES(20,116,'20:116','Và khi TA phán cho các Thiên Thần "Hãy phủ phục trước Adam" thì chúng phủ phục ngoại trừ Iblis, nó đã từ chối.');
INSERT INTO "hasan_abdulkarim" VALUES(20,117,'20:117','Sau đó TA phán: "Hỡi Adam! Quả thật đây (Iblis) là kẻ thù của ngươi và vợ của ngươi. Bởi thế chớ để cho nó đưa hai (vợ chồng) ngươi ra khỏi Vườn trời e rằng ngươi sẽ đau khổ."');
INSERT INTO "hasan_abdulkarim" VALUES(20,118,'20:118','"Quả thật, đó là chỗ hứa của ngươi; trong đó (thiên đàng) ngươi sẽ không bị đói cũng không bị trần truồng."');
INSERT INTO "hasan_abdulkarim" VALUES(20,119,'20:119','“Quả thật trong đó ngươi sẽ không bị khát cũng không bị nóng nực.”');
INSERT INTO "hasan_abdulkarim" VALUES(20,120,'20:120','Nhưng Shaytan đã thì thào với Y (Adam). Nó nói: “Hỡi Adam! Ngươi có muốn ta đưa ngươi đến Cây Vĩnh Cửu và một vương quốc sẽ không bao giờ điêu tàn không?”');
INSERT INTO "hasan_abdulkarim" VALUES(20,121,'20:121','Rồi hai người họ (vợ chồng Adam) ăn (trái) của cây đó. Bởi thế phần kín đáo của hai người họ lộ ra trước mắt. Thế nên hai người khâu lá cây của Vườn trời để che kín thân mình lại. Bởi vì Adam đã bất tuân Thượng Đế của Y, cho nên Y đã lầm lạc.');
INSERT INTO "hasan_abdulkarim" VALUES(20,122,'20:122','Rồi Thượng Đế của Y chọn Y. Ngài đã quay lại tha thứ và hướng dẫn Y.');
INSERT INTO "hasan_abdulkarim" VALUES(20,123,'20:123','(Allah) phán: "Cả hai hãy đi xuống khỏi đó (Vườn trời), thù hằn lẫn nhau. Nhưng nếu có Chỉ Đạo từ TA đến cho các ngươi, ai theo Chỉ Đạo của TA thì sẽ không lầm lạc cũng không bất hạnh."');
INSERT INTO "hasan_abdulkarim" VALUES(20,124,'20:124','"Ngược lại, ai lánh xa Thông Điệp Nhắc Nhở của TA thì chắc chắn sẽ sống một cuộc đời eo hẹp (khổ sở) và vào Ngày phục sinh TA sẽ phục sinh y trở lại mù lòa."');
INSERT INTO "hasan_abdulkarim" VALUES(20,125,'20:125','Y sẽ thưa: “Lạy Thượng Đế (Allah!) Tại sao Ngài dựng bề tôi sống lại mù lòa trong lúc trước đây bề tôi nhìn thấy.”');
INSERT INTO "hasan_abdulkarim" VALUES(20,126,'20:126','(Allah sẽ) phán: “Như thế đấy! Các Lời Mặc Khải của TA đã đến với ngươi nhưng ngươi không quan tâm; và Ngày nay (TA) bỏ quên ngươi trở lại giống như thế.”');
INSERT INTO "hasan_abdulkarim" VALUES(20,127,'20:127','Và TA ban thưởng xứng đáng cho ai vượt quá mức giới hạn và không tin tưởng nơi các Lời Mặc Khải của Thượng Đế của y đúng như thế. Và chắc chắn sự trừng phạt ở đời sau sẽ khắc nghiệt và dai dẳng hơn.');
INSERT INTO "hasan_abdulkarim" VALUES(20,128,'20:128','Hẳn không là một Chỉ Đạo cho họ hay sao việc TA đã tiêu diệt bao nhiêu thế hệ trước họ, chủ nhân của các căn nhà mà chúng đang bước đi? Quả thật nơi sự việc đó là những dấu hiệu cho những người thông hiểu.');
INSERT INTO "hasan_abdulkarim" VALUES(20,129,'20:129','Và nếu không vì một Lời phán của Thượng Đế của Ngươi (hỡi Muhammad!) đã được tuyên bố và một thời hạn đã được ấn định thì chắc chắn (hình phạt) đã xảy ra (cho chúng) rồi.');
INSERT INTO "hasan_abdulkarim" VALUES(20,130,'20:130','Bởi thế, hãy ráng chịu đựng với những lời (mỉa mai) của chúng và hãy tán dương ca tụng Thượng Đế của Ngươi trước khi mặt trời mọc và trước khi mặt trời lặn; và hãy tán dương Ngài vào những giờ giấc của ban đêm và giờ giấc đầu và cuối của ban ngày để may ra Ngươi sẽ hài lòng (với phần thưởng mà Ngài sẽ ban cho).');
INSERT INTO "hasan_abdulkarim" VALUES(20,131,'20:131','Và chớ trố mắt nhìn về những thứ mà TA đã ban cấp cho những cặp (bè nhóm) của chúng hưởng thụ, sự lộng lẫy của đời sổng trần tục này, (những thứ) mà TA dùng để thử thách chúng; Ngược lại thiên lộc của Thượng Đế của Ngươi là ưu tốt và lâu dài hơn.');
INSERT INTO "hasan_abdulkarim" VALUES(20,132,'20:132','Và hãy ra lệnh cho người nhà của Ngươi dâng lễ nguyện Salah'' và hãy kiên nhẫn. TA không đòi hỏi Ngươi bổng lộc; Chính TA cung cấp nó cho Ngươi. Và kết cuộc tốt đẹp dành cho ai sùng bái và sợ Allah.');
INSERT INTO "hasan_abdulkarim" VALUES(20,133,'20:133','(Những kẻ vô đức tin) nói: "Tại sao Y (Muhammad) không mang đến cho chúng ta một bằng chứng của Thượng Đế của Y?” Há những điều ghi trong các Tờ Kinh cũ (như kinh Tawrah và Injil) đã không là một bằng chứng rõ rệt cho chúng hay sao?');
INSERT INTO "hasan_abdulkarim" VALUES(20,134,'20:134','Và nếu TA tiêu diệt chúng bằng một hình phạt trước khi (Qur''an được mặc khải) thì chắc chắn chúng sẽ thưa: “Lạy Thượng Đế của bầy tôi! Tại sao Ngài không cử một Sứ Giả đến với chúng tôi để chúng tôi có dịp tuân theo các Lời Mặc Khải của Ngài trước khi chúng tôi bị thất sủng và bị hạ nhục?”');
INSERT INTO "hasan_abdulkarim" VALUES(20,135,'20:135','Hãy bảo chúng: “Mỗi người (trong chúng ta) đều mong đợi (sự Xét Xử của Allah); do đó các người hãy chờ đợi đi. Rồi các người sẽ sớm biết ai đi theo con đường bằng phẳng và ai được hướng dẫn.”');
INSERT INTO "hasan_abdulkarim" VALUES(21,1,'21:1','Việc xét xử nhân loại ngày càng đến gần nhưng họ vẫn dửng dưng quay bỏ đi.');
INSERT INTO "hasan_abdulkarim" VALUES(21,2,'21:2','Họ nghe không sót từng lời cảnh cáo mới của Thượng Đế của họ nhưng họ chỉ nghe với tinh thần bông đùa.');
INSERT INTO "hasan_abdulkarim" VALUES(21,3,'21:3','Lòng họ không mấy thiết tha. Những kẻ làm điều sai quấy giấu kín cuộc mật đàm của họ. (Họ bảo:) “Phải chăng (người) này chỉ là một người phàm như quí vị? Phải chăng quí vị bị mê hoặc bởi pháp thuật (của y) trong lúc quí vị nhìn thấy rõ (sự việc)?”');
INSERT INTO "hasan_abdulkarim" VALUES(21,4,'21:4','Hãy bảo họ (hỡi Muhammad:) “Thượng Đế của ta biết rõ (từng) lời nói (thốt ra) trong các tầng trời và trái đất bởi vì Ngài là Đấng Hằng Nghe và Hằng Biết (mọi việc).');
INSERT INTO "hasan_abdulkarim" VALUES(21,5,'21:5','“Không! họ nói: (Những lời y nói ra) chỉ là những giấc mộng lộn xộn. Không! Y bịa đặt ra Nó (Qur''an). Không! Y là một nhà thơ. Thế, hãy bảo y mang đến cho bọn mình một Phép Lạ (bằng chứng) giống như những cái đã được ban cấp cho các Sứ Giả của thời xưa.”');
INSERT INTO "hasan_abdulkarim" VALUES(21,6,'21:6','Trước họ, không có một thị trấn nào trong số các thị trấn đã bị TA tiêu diệt lại có đức tin cả. Thế, họ (những kẻ ngoại giáo của thị trấn Makkah này) sẽ tin tưởng hay sao?');
INSERT INTO "hasan_abdulkarim" VALUES(21,7,'21:7','Và trước Ngươi (hỡi Muhammad, các Sứ Giả) do TA cử đến chỉ là những người phàm mà TA đã mặc khải cho (Kinh Sách). Bởi thế, nếu các ngươi không biết thì hãy hỏi những người đang giữ Thông Điệp (như Kinh Tawrah và Injil thì sẽ rõ).');
INSERT INTO "hasan_abdulkarim" VALUES(21,8,'21:8','Và TA đã không làm ra họ (các Sứ Giả) với cơ thể mà lại không ăn uống; vả lại, họ cũng không phải là những người bất tử.');
INSERT INTO "hasan_abdulkarim" VALUES(21,9,'21:9','Rồi TA đã hoàn tất Lời hứa (của TA) cho họ. Bởi thế TA đã cứu sống họ và người nào mà TA muốn. Và TA đã tiêu diệt những kẻ tội lỗi.');
INSERT INTO "hasan_abdulkarim" VALUES(21,10,'21:10','Quả thật, TA đã ban xuống cho các ngươi một Kinh Sách, trong đó, là Thông Điệp nhắc nhở các ngươi. Thế các ngươi không hiểu hay sao?');
INSERT INTO "hasan_abdulkarim" VALUES(21,11,'21:11','Và có bao nhiêu thị trấn đã bị TA tiêu diệt vì (dân cư của chúng) đã làm điều sai quấy và sau chúng, TA đã tạo ra một đám người khác (để thay thế)!');
INSERT INTO "hasan_abdulkarim" VALUES(21,12,'21:12','Bởi thế, khi thấy hình phạt của TA, chúng hớt hãi chạy trốn.');
INSERT INTO "hasan_abdulkarim" VALUES(21,13,'21:13','Chớ bỏ chạy, (hỡi người!) hãy trở lại với những cái (tiện nghi) mà các ngươi đã sống xa hoa nơi đó và mái nhà (xưa) để các ngươi có thể bị chất vấn.');
INSERT INTO "hasan_abdulkarim" VALUES(21,14,'21:14','Chúng than thở: “Ôi thật khổ thân chúng tôi! Quả thật, chúng tôi là những kẻ làm điều sai quấy.”');
INSERT INTO "hasan_abdulkarim" VALUES(21,15,'21:15','Và tiếng kêu than của chúng kéo dài triền miên, mãi cho đến lúc TA làm tắt lịm đi như san bằng một cánh đồng sau mùa gặt.');
INSERT INTO "hasan_abdulkarim" VALUES(21,16,'21:16','Và TA đã không tạo hóa các tầng trời và trái đất cũng như mọi vật giữa chúng để vui chơi.');
INSERT INTO "hasan_abdulkarim" VALUES(21,17,'21:17','Nếu muốn có một thú tiêu khiển thì TA đã lấy nó từ TA, nếu TA có ý làm điều đó.');
INSERT INTO "hasan_abdulkarim" VALUES(21,18,'21:18','Không! TA lấy Chân Lý (Qur''an) ném vào sự giả dối, bởi thế, nó đánh vỡ đầu sự giả dối. Hãy xem! sự giả dối chỉ có tiêu tan. Và khốn khổ cho các ngươi về những điều (giả tạo) mà các người đã qui cho (TA)!');
INSERT INTO "hasan_abdulkarim" VALUES(21,19,'21:19','Bất cứ ai trong các tầng trời và trái đất đều là (tạo vật) của Ngài (Allah) cả. Và bất cứ ai (Thiên Thần) ở với Ngài đều không quá tự hào và mệt mỏi trong việc thờ phụng Ngài.');
INSERT INTO "hasan_abdulkarim" VALUES(21,20,'21:20','Họ không ngưng tán dương ca tụng Ngài cả đêm lẫn ngày.');
INSERT INTO "hasan_abdulkarim" VALUES(21,21,'21:21','Phải chăng chúng đã tôn thờ những thần linh của trái đất có khả năng phục sinh được (người chết) hay sao?');
INSERT INTO "hasan_abdulkarim" VALUES(21,22,'21:22','Nếu trong trời đất có những thần linh khác ngoài Allah thì chắc chắn cả hai (trời và đất) sẽ đi đến chỗ sụp đổ. Bởi thế thật quang vinh và trong sạch thay Allah, Thượng Đế của Ngai vương, về những điều chúng đã qui cho (Ngài).');
INSERT INTO "hasan_abdulkarim" VALUES(21,23,'21:23','Ngài (Allah) sẽ không bị ai chất vấn về việc Ngài làm, ngược lại, chúng sẽ bị (Ngài) chất vấn (về điều chúng đã qui cho Ngài).');
INSERT INTO "hasan_abdulkarim" VALUES(21,24,'21:24','Phải chăng chúng đã tôn thờ những thần linh khác ngoài Ngài? Hãy bảo chúng: "Hãy trưng bằng chứng của các người ra xem! (Qur''an) này la Thông Điệp nhắc nhở cho người nào theo Ta (Muhammad) và là điều nhắc nhở cho những người trước Ta." Không! Đa số bọn chúng không biết sự thật. Bởi thế chúng đã quay bỏ đi.');
INSERT INTO "hasan_abdulkarim" VALUES(21,25,'21:25','Và không một Sứ Giả nào đã được cử phái đến trước Ngươi mà TA đã không mặc khải cho y bảo nói: “Không có Thượng Đế nào khác chỉ có TA (Allah), bởi thế hãy thờ phụng riêng TA.”');
INSERT INTO "hasan_abdulkarim" VALUES(21,26,'21:26','Và chúng (những kẻ ngoại giáo) đã nói: “Đấng Rất Mực Độ Lượng có một đứa con trai.” Quang vinh và trong sạch thay Ngài! Không! Họ (những ai mà chúng cho là con cái của Allah) đều là bề tôi vinh dự (của Allah).');
INSERT INTO "hasan_abdulkarim" VALUES(21,27,'21:27','Họ (các Sứ Giả của Allah) không nói trước (qua mặt) Ngài (Allah) và họ chỉ làm theo mệnh lệnh của Ngài.');
INSERT INTO "hasan_abdulkarim" VALUES(21,28,'21:28','Ngài biết điều gì (có) trước họ và điều gì (xảy ra) sau họ và họ không can thiệp giùm cho ai được ngoại trừ cho người nào mà Ngài (Allah) hài lòng. Và họ khiếp sợ Ngài.');
INSERT INTO "hasan_abdulkarim" VALUES(21,29,'21:29','Và ai trong họ tự xưng "Ta là thần linh ngoài Ngài (Allah)” thì người đó sẽ bị TA phạt Hoả Ngục. TA trừng phạt những kẻ làm điều sai quấy đúng như thế.');
INSERT INTO "hasan_abdulkarim" VALUES(21,30,'21:30','Há những kẻ không có lòng tin không nhận thấy rằng các tầng trời và trái đất quyện lại với nhau (thành một khối) trước khi TA chẻ chúng ra làm hai hay sao? Và TA đã tạo mọi sinh vật từ nước. Thế, chúng không tin hay sao?');
INSERT INTO "hasan_abdulkarim" VALUES(21,31,'21:31','Và TA đã đặt trên trái đất các quả núi vững chắc sợ rằng nó (trái đất) rung chuyển với chúng và đã làm ra nơi đó các con đường thông thương (băng qua những ngọn núi) để may ra chúng được hướng dẫn.');
INSERT INTO "hasan_abdulkarim" VALUES(21,32,'21:32','Và TA đã làm bầu trời thành cái lọng che được canh giữ kỹ càng. Nhưng chúng quay bỏ đi trước những dấu hiệu của nó (bầu trời).');
INSERT INTO "hasan_abdulkarim" VALUES(21,33,'21:33','Và Ngài (Allah) là Đấng đã tạo hóa ban đêm và ban ngày, mặt trời và mặt trăng, mỗi vật bơi đi trong quỹ đạo (của nó).');
INSERT INTO "hasan_abdulkarim" VALUES(21,34,'21:34','Và trước Ngươi (Muhammad), TA đã không làm cho một người phàm nào sống bất tử cả. Thế phải chăng nếu Ngươi chết thì chúng sẽ sống bất tử hay sao?');
INSERT INTO "hasan_abdulkarim" VALUES(21,35,'21:35','Mỗi một linh hồn đều phải nếm cái chết; TA sẽ thử thách các ngươi với điều xấu và điều tốt. Và các ngươi sẽ được đưa trở về gặp lại TA (để chịu xét xử).');
INSERT INTO "hasan_abdulkarim" VALUES(21,36,'21:36','Và khi những kẻ không có đức tin thấy Ngươi, chúng chỉ muốn chế giễu Ngươi. (Chúng nói:) “Đây phải chăng là kẻ đã miệt thị những thần linh của quí vị?” và chúng đã phạm thượng khi nghe nhắc đến Đấng Rất Mực Độ Lượng.');
INSERT INTO "hasan_abdulkarim" VALUES(21,37,'21:37','Con người được tạo ra (bẩm tính) nôn nóng. TA (Allah) sẽ sớm cho các ngươi thấy những bằng chứng của TA. Do đó chớ giục TA.');
INSERT INTO "hasan_abdulkarim" VALUES(21,38,'21:38','Và chúng nói: “Chừng nào lời hứa (trừng phạt) này sẽ thể hiện nếu quí vị (Muslim) nói sự thật?”');
INSERT INTO "hasan_abdulkarim" VALUES(21,39,'21:39','Giá những kẻ không tin biết được giờ phút mà chúng sẽ không thể tránh khỏi Lửa (của hỏa ngục) táp vào mặt và lưng của chúng và sẽ không được ai giúp đỡ (thì chúng đâu có giục TA).');
INSERT INTO "hasan_abdulkarim" VALUES(21,40,'21:40','Không! (hình phạt) sẽ bất ngờ xảy đến cho chúng khiến chúng ngỡ ngàng. Rồi chúng sẽ không thể đẩy nó ra xa và cũng không được gia hạn thêm nữa.');
INSERT INTO "hasan_abdulkarim" VALUES(21,41,'21:41','Và quả thật, nhiều Sứ Giả trước Ngươi đã từng bị chế giễu nhưng chính những điều mà những kẻ nhạo báng thường chế giễu sẽ vây hãm chúng lại trong đó.');
INSERT INTO "hasan_abdulkarim" VALUES(21,42,'21:42','Hãy bảo chúng: “Ai canh giữ các người an toàn ban đêm và ban ngày khỏi (cơn Thịnh Nộ) của Đấng Rất Mực Độ Lượng?” Không, chúng quay mặt đi làm ngơ trước Lời nhắc nhở của Thượng Đế của chúng.');
INSERT INTO "hasan_abdulkarim" VALUES(21,43,'21:43','Hoặc phải chăng chúng được những thần linh (khác) bảo vệ tránh khỏi TA? Chúng sẽ không thể tự cứu mình và cũng không tự bảo vệ tránh khỏi (hình phạt của) TA.');
INSERT INTO "hasan_abdulkarim" VALUES(21,44,'21:44','Không! TA cho những người (Quraish) này và cha mẹ của chúng hưởng lạc cho đến khi tuổi thọ của chúng kéo dài thêm. Thế chúng đã không thấy việc TA đã đến tận đất đai của chúng, và thu hẹp dần dần biên giới( của nó lại hay sao? Thế phải chăng chúng sẽ chiến thắng?');
INSERT INTO "hasan_abdulkarim" VALUES(21,45,'21:45','Hãy bảo chúng: “Ta (Muhammad) chỉ cảnh cáo các người theo Lời Mặc Khải (đã được truyền xuống).” Nhưng người điếc không nghe được tiếng gọi dù y có được cảnh cáo đi nữa.');
INSERT INTO "hasan_abdulkarim" VALUES(21,46,'21:46','Và nếu có một làn hơi trừng phạt của Thượng Đế của Ngươi chạm phải chúng thì chắc chắn chúng sẽ than: “Thật khổ thân chúng tôi! Rõ thật chúng tôi là những kẻ làm điều sai quấy.');
INSERT INTO "hasan_abdulkarim" VALUES(21,47,'21:47','Và TA sẽ thiết lập các bàn cân công lý vào Ngày phục sinh. Bởi thế sẽ không có một người (linh hồn) nào bị đối xử bất công về bất cứ điều gì. Và nếu (chúng có làm một việc dù nhỏ) bằng sức nặng của một hạt cải đi nữa thì TA cũng sẽ mang nó ra (cân đo). Và một mình TA đủ làm công việc thanh toán sổ sách.');
INSERT INTO "hasan_abdulkarim" VALUES(21,48,'21:48','Và quả thật, TA đã ban cho Musa và Harun Tiêu chuẩn xét xử và một Ánh Sáng quang minh (Kinh Tawrah) và một sự Nhắc Nhở cho những người ngay chính sợ Allah.');
INSERT INTO "hasan_abdulkarim" VALUES(21,49,'21:49','Những ai âm thầm sợ Thượng Đế nơi cõi vô hình và khiếp sợ Giờ (Xét xử).');
INSERT INTO "hasan_abdulkarim" VALUES(21,50,'21:50','Và đây là một Thông điệp nhắc nhở (Qur''an) đầy phúc đức mà TA đã ban xuống (cho các ngươi). Thế các ngươi, (hỡi Muslim!) sẽ phủ nhận Nó hay sao?');
INSERT INTO "hasan_abdulkarim" VALUES(21,51,'21:51','Và chắc chắn trước đây TA đã ban cho Ibrahim lẽ phải của Người; và TA biết rõ về Y.');
INSERT INTO "hasan_abdulkarim" VALUES(21,52,'21:52','(Hãy nhớ) khi Y thưa với phụ thân và người dân của mình bảo: “Những hình tượng này mà quí vị sùng bái là gì vậy?”');
INSERT INTO "hasan_abdulkarim" VALUES(21,53,'21:53','Họ đáp: “Bọn ta thấy cha mẹ của bọn ta tôn thờ chúng.”');
INSERT INTO "hasan_abdulkarim" VALUES(21,54,'21:54','Y bảo: “Chắc chắn quí vị và cha mẹ của quí vị, tất cả, đều đã lầm lạc một cách rõ ràng.”');
INSERT INTO "hasan_abdulkarim" VALUES(21,55,'21:55','Họ đáp: “Ngươi nói thật hay bông đùa với bọn ta?”');
INSERT INTO "hasan_abdulkarim" VALUES(21,56,'21:56','Y bảo: “Không! Thượng Đế của quí vị là Thượng Đế (Đấng Chủ Tể) của các tầng trời và trái đất, Đấng đã sáng tạo chúng (từ cái không). Và tôi là một trong những người làm chứng cho việc đó."');
INSERT INTO "hasan_abdulkarim" VALUES(21,57,'21:57','Và Người thề: “Nhân danh Allah! Chắc chắn tôi sẽ đặt kế hoạch đập phá những bức tượng của các ông sau khi các ông bỏ đi.”');
INSERT INTO "hasan_abdulkarim" VALUES(21,58,'21:58','Bởi thế, Y đã đập phá chúng (các bức tượng) và chỉ chừa lại bức tượng lớn nhất để họ có thể trở lại (tiếp chuyện) với nó.');
INSERT INTO "hasan_abdulkarim" VALUES(21,59,'21:59','(Họ bảo nhau): "Tên nào đã hành động thế này đối với những thần linh của bọn ta? Nó quả thật là một tên làm điều sai quấy."');
INSERT INTO "hasan_abdulkarim" VALUES(21,60,'21:60','(Dân chúng) lên tiếng: “Chúng tôi có nghe một thanh niên tên Ibrahim đã nói chuyện ra vẻ khinh miệt chúng (những bức tượng).”');
INSERT INTO "hasan_abdulkarim" VALUES(21,61,'21:61','Họ bảo: “Thế bắt nó đưa ra trước công chúng để người dân chứng kiến (tội ác của nó).”');
INSERT INTO "hasan_abdulkarim" VALUES(21,62,'21:62','Họ bảo: “Hỡi Ibrahim! Phải chăng ngươi đã làm điều này đối với các thần linh của bọn ta?”');
INSERT INTO "hasan_abdulkarim" VALUES(21,63,'21:63','Y đáp: “Không! Bức tượng lớn nhất này của chúng đã làm điều đó. Thế hãy hỏi chúng xem nếu chúng biết nói.”');
INSERT INTO "hasan_abdulkarim" VALUES(21,64,'21:64','Thế rồi họ quay nhìn nhau (hổ thẹn) rồi nói: “Rõ chính quí vị mới thực sự là những kẻ làm điều sai quấy.”');
INSERT INTO "hasan_abdulkarim" VALUES(21,65,'21:65','Rồi (vì xấu hổ) họ gục đầu, (nói thầm): “Chắc chắn ngươi đã biết rõ những bức tượng này đâu biết nói!”');
INSERT INTO "hasan_abdulkarim" VALUES(21,66,'21:66','(Ibrahim) bảo: “Thế phải chăng quí vị thờ phụng ngoài Allah những vật chẳng làm lợi cũng chẳng hãm hại được quí vị hay sao?”');
INSERT INTO "hasan_abdulkarim" VALUES(21,67,'21:67','“Thật xấu hổ thay cho quí vị và cho những vật mà quí vị tôn thờ ngoài Allah! Thế quí vị chưa hiểu ư?”');
INSERT INTO "hasan_abdulkarim" VALUES(21,68,'21:68','Họ nói: “Bắt nó thiêu sống và giúp những thần linh của quí vị nếu quí vị muốn hành động.”');
INSERT INTO "hasan_abdulkarim" VALUES(21,69,'21:69','TA (Allah) phán. "Hỡi Lửa! Hãy nguội mát và thành một nguồn bằng an cho Ibrahim"');
INSERT INTO "hasan_abdulkarim" VALUES(21,70,'21:70','Và chúng mưu toan hại Y nhưng TA đã làm cho chúng thua lớn.');
INSERT INTO "hasan_abdulkarim" VALUES(21,71,'21:71','Và TA đã giải cứu Y và Lut và đưa họ đến một vùng đất mà TA đã ban phúc cho muôn loài.');
INSERT INTO "hasan_abdulkarim" VALUES(21,72,'21:72','Và TA đã ban cho Y (Ibrahim đứa con trai) Is-haaq và (đứa cháu nội) Ya''qub như một món quà; và mỗi đứa TA đã làm thành người ngoan đạo.');
INSERT INTO "hasan_abdulkarim" VALUES(21,73,'21:73','Và TA đã làm cho họ (con cháu của Ibrahim) thành lãnh tụ để hướng dẫn (nhân loại) theo Mệnh Lệnh của TA và đã mặc khải bảo họ làm việc thiện và dâng lễ nguyện Salah và đóng Zakah và thờ phụng riêng TA.');
INSERT INTO "hasan_abdulkarim" VALUES(21,74,'21:74','Và (hãy nhớ) Lut: TA đã ban cho Y Lẽ Phán xét đúng đắn và sự hiểu biết; và đã giải cứu Y ra khỏi thị trấn mà dân cư đã từng làm những điều khả ố. Quả thật, chúng là một đám dân bất tuân, chống lại Allah.');
INSERT INTO "hasan_abdulkarim" VALUES(21,75,'21:75','Và TA đã khoan dung Y. Quả thật, Y là một người ngoan đạo.');
INSERT INTO "hasan_abdulkarim" VALUES(21,76,'21:76','Và (hãy nhớ) Nuh (Nô-ê) khi Y cầu xin (với TA) trước đây. Bởi thế TA đã đáp lời cầu xin của Y. Rồi TA cứu Y và gia đình của Y thoát khỏi cơn đại nạn.');
INSERT INTO "hasan_abdulkarim" VALUES(21,77,'21:77','Và TA đã cứu Y thoát khỏi đám dân đã từng phủ nhận các Bằng chứng của TA. Quả thật chúng là một đám dân tội lỗi. Bởi thế TA đã nhận chúng chết chìm cả đám (dưới nước lụt).');
INSERT INTO "hasan_abdulkarim" VALUES(21,78,'21:78','Và (hãy nhớ) Dawood và Sulayman khi hai người xét xử vụ một đám rẫy đã bị đàn cừu của một đám dân đột nhập vào ăn hoa mầu ban đêm. Và TA đã chứng kiến việc xét xử của họ.');
INSERT INTO "hasan_abdulkarim" VALUES(21,79,'21:79','Nhưng TA đã làm cho Sulayman thấu triệt vấn đề hơn trong lúc mỗi người đều được TA ban cho lý suy luận khôn ngoan và kiến thức. Và TA đã bắt núi non lẫn chim chóc hòa âm với Dawood mà tán dương TA. Và TA là Đấng đã làm ra (những việc đó).');
INSERT INTO "hasan_abdulkarim" VALUES(21,80,'21:80','Và TA đã dạy Y (Dawood) cách làm áo giáp cho các ngươi để tự bảo vệ trong những cuộc giao tranh. Thế các ngươi biết ơn chăng?');
INSERT INTO "hasan_abdulkarim" VALUES(21,81,'21:81','Và TA đã bắt những cơn gió mạnh phục tùng Sulayman bay êm ả theo lệnh của Y đến vùng đất mà TA đã ban phúc. Và TA là Đấng Toàn Tri.');
INSERT INTO "hasan_abdulkarim" VALUES(21,82,'21:82','Và trong đám Shaytan (loài Jinn), có những tên đã lặn xuống nước (đễ phục dịch) Y (Sulayman) và làm các công tác khác ngoài việc (lặn) đó. Và TA là Đấng Trông chừng chúng.');
INSERT INTO "hasan_abdulkarim" VALUES(21,83,'21:83','Và (hãy nhớ) Ayyub (Job) khi Y cầu xin Thượng Đế của Y, thưa: “Bề tôi đã gặp nạn. Và Ngài là Đấng Khoan Dung nhất của những người tỏ lòng khoan dung.”');
INSERT INTO "hasan_abdulkarim" VALUES(21,84,'21:84','Bởi thế, TA đã đáp lời cầu xin của Y. Rồi TA đã làm tan nỗi khổ mà từ lâu Y đã cam chịu, và TA đã phục hồi cho Y gia đình của Y và ban cho họ những cái tương tự: một sự Khoan dung từ TA và là điều Nhắc nhở cho những người thờ phụng (TA).');
INSERT INTO "hasan_abdulkarim" VALUES(21,85,'21:85','Và Isma''il và Idris và Zdul-Kifli. Tất cả đều là những người kiên nhẫn.');
INSERT INTO "hasan_abdulkarim" VALUES(21,86,'21:86','Và TA đã khoan dung họ. Quả thật, họ là những người ngoan đạo.');
INSERT INTO "hasan_abdulkarim" VALUES(21,87,'21:87','Và (hãy nhớ) Zdan-nun (Yunus - Jonah) khi Y nổi giận bỏ đi và đã nghĩ TA không có quyền làm gì Y. Nhưng Y đã cầu xin (với TA) thưa: “Không có Thượng Đế nào khác cả duy chỉ Ngài; Quang vinh thay Ngài! Quả thật bề tôi là một người sai quấy.”');
INSERT INTO "hasan_abdulkarim" VALUES(21,88,'21:88','Bởi thế TA đã đáp lời cầu xin của Y và cứu khổ Y; và TA đã giải cứu những người tin tưởng đúng như thế.');
INSERT INTO "hasan_abdulkarim" VALUES(21,89,'21:89','Và (hãy nhớ) Zakariya khi Y cầu xin Thượng Đế của Y, thưa: “Lạy Thượng Đế của bề tôi! Xin Ngài đừng để cho bề tôi tuyệt tự. Bởi vì Ngài là Đấng Thừa Tự Ưu việt.”');
INSERT INTO "hasan_abdulkarim" VALUES(21,90,'21:90','Bởi thế, TA đã đáp lời cầu xin của Y và ban cho Y (đứa con trai) Yahya (Giăng). Và TA đã chữa lành (bệnh hiếm hoi của) người vợ của Y cho Y. Quả thật họ là những người hay thi đua nhau làm phúc. Họ thường cầu nguyện TA vừa hy vọng vừa sợ hãi. Và họ là những người hạ mình khiêm tốn trước TA.');
INSERT INTO "hasan_abdulkarim" VALUES(21,91,'21:91','Và (hãy nhớ) Nữ (Maryam) là người đã giữ trọn tiết trinh. Rồi TA cho thổi (Linh hồn)( vào Nữ từ Ruh (Jibril) của TA và đã làm cho Nữ và đứa con trai của Nữ thành một Dấu Lạ cho nhân gian.');
INSERT INTO "hasan_abdulkarim" VALUES(21,92,'21:92','Quả thật, Cộng đồng( này của các ngươi (các Nabi) là một Cộng đồng duy nhất. Và TA là Thượng Đế của các ngươi. Bởi thế hãy thờ phụng TA');
INSERT INTO "hasan_abdulkarim" VALUES(21,93,'21:93','Nhưng họ (những giáo phái) đã chia rẽ công việc (tôn giáo) của họ với nhau. (Họ đâu biết cuối cùng) tất cả sẽ trở lại gặp TA.');
INSERT INTO "hasan_abdulkarim" VALUES(21,94,'21:94','Bởi thế ai làm điều lành và có đức tin thì công lao của y sẽ không bị phủ nhận và chính TA sẽ ghi công cho y.');
INSERT INTO "hasan_abdulkarim" VALUES(21,95,'21:95','Và một lệnh cấm đã được ban hành cho (người dân của) mỗi thị trấn mà TA đã tiêu diệt rằng chúng sẽ không trở lại (chốn xưa).');
INSERT INTO "hasan_abdulkarim" VALUES(21,96,'21:96','Mãi cho đến khi Yajuj (Gog) và Majuj (Magog) được buông thả và sẽ tràn xuống (tấn công) từ mỗi ngọn đồi.');
INSERT INTO "hasan_abdulkarim" VALUES(21,97,'21:97','Và Lời Hứa thật sự này dần dần sẽ thể hiện. Bởi thế, khi nó đến, cặp mắt của những kẻ vô đức tin sẽ mở to trân tráo (và nói:) “Ôi khổ thân chúng tôi! Quả thật, chúng tôi đã lơ là về (Lời cảnh cáo) này. Không! Chúng tôi là những kẻ làm điều sai quấy.”');
INSERT INTO "hasan_abdulkarim" VALUES(21,98,'21:98','Chính các ngươi và những vật mà các ngươi tôn thờ ngoài Allah sẽ là chất đôtt của hỏa ngục, nơi mà các ngươi sẽ sa xuống.');
INSERT INTO "hasan_abdulkarim" VALUES(21,99,'21:99','Nếu những vật (bục tượng) này là những thần linh thực sự thì chúng sẽ không sa xuống (hỏa ngục), nhưng tất cả sẽ đi vào ở trong đó.');
INSERT INTO "hasan_abdulkarim" VALUES(21,100,'21:100','Trong đó, chúng sẽ khóc tức tưởi và sẽ không nghe thấy gì.');
INSERT INTO "hasan_abdulkarim" VALUES(21,101,'21:101','Bởi vì quả thật, những ai mà cái tốt của TA đã (được gởi) đi trước (cho) họ thì sẽ được đưa ra khỏi (hỏa ngục);');
INSERT INTO "hasan_abdulkarim" VALUES(21,102,'21:102','Họ sẽ không nghe thấy một tiếng xì xào nào của (hỏa ngục) và sẽ ở đời đời trong (tình trạng) mà tâm hồn của họ đã từng ao ước.');
INSERT INTO "hasan_abdulkarim" VALUES(21,103,'21:103','Họ sẽ không còn sợ hãi; và các Thiên Thần sẽ nghênh đón họ (chúc:) “Đây là Ngày (vui) mà qúi vị đã được hứa.”');
INSERT INTO "hasan_abdulkarim" VALUES(21,104,'21:104','(Hãy nhớ) Ngày mà TA sẽ cuốn tròn các tầng trời giống như các tờ kinh được cuốn tròn cho những quyển Kinh giống việc TA đã khởi nguyên việc tạo hóa lần đầu tiên, TA sẽ tái lập nó. (Đó là) một Lời Hứa ràng buộc TA phải thực hiện. Chính TA sẽ làm điều này.');
INSERT INTO "hasan_abdulkarim" VALUES(21,105,'21:105','Và quả thật TA đã ghi trong Thi Thiên (đã ban cho Dawood) sau Bức Thông Điệp (đã được ban cho Musa) rằng trái đất sẽ do những bề tôi ngoan đạo của TA thừa hưởng.');
INSERT INTO "hasan_abdulkarim" VALUES(21,106,'21:106','Quả thật, trong (Qur''an) này là một Thông Điệp gởi cho đám người thật sự thờ phụng (Allah).');
INSERT INTO "hasan_abdulkarim" VALUES(21,107,'21:107','Và TA đã cử phái Ngươi (Muhammad) đến như một Hồng Ân cho muôn loài.');
INSERT INTO "hasan_abdulkarim" VALUES(21,108,'21:108','(Bởi thế) hãy bảo họ: “Quả thật Ta được mặc khải cho biết Thượng Đế của các người là một Thượng Đế duy nhất. Thế các người sẽ không là người Muslim phục mệnh Ngài hay sao?”');
INSERT INTO "hasan_abdulkarim" VALUES(21,109,'21:109','Nhưng nếu họ quay bỏ đi thì hãy bảo: "Ta đã công bố (Thông Điệp của Allah) đồng đều cho các người; và Ta không biết điều mà các người đã được hứa hiện nay ở gần hay ở xa."');
INSERT INTO "hasan_abdulkarim" VALUES(21,110,'21:110','"Chính Ngài (Allah) biết lời lẽ mà các người đã hô hoán và biết điều mà các người đã giấu giếm."');
INSERT INTO "hasan_abdulkarim" VALUES(21,111,'21:111','"Và Ta (Muhammad) không biết: có lẽ đó là một sự thử thách hay là một sự hưởng thụ trong một thời gian ngắn cho các người."');
INSERT INTO "hasan_abdulkarim" VALUES(21,112,'21:112','Y (Muhammad) thưa: “Lạy Thượng Đế của bề tôi! Xin Ngài lấy sự Thật mà phân xử. Và Thượng Đế Rất Mực Độ Lượng sẽ giúp bầy tôi đối phó với những điều mà các người đã qui cho (Allah, Ta và Qur''an).”');
INSERT INTO "hasan_abdulkarim" VALUES(22,1,'22:1','Hỡi nhân loại! Hãy sợ Thượng Đế của các ngươi bởi vì cơn chấn động của Giờ Tận Thế sẽ là một hiện tượng vô cùng khủng khiếp.');
INSERT INTO "hasan_abdulkarim" VALUES(22,2,'22:2','Ngày mà các ngươi sẽ thấy: mỗi bà mẹ sẽ quên bẵng đứa con đang bú và mỗi người phụ nữ mang thai sẽ trút bỏ gánh nặng của mình và Ngươi sẽ thấy thiên hạ như say rượu trong lúc họ không uống say. Tuy nhiên sự trừng phạt của Allah sẽ rất khủng khiếp.');
INSERT INTO "hasan_abdulkarim" VALUES(22,3,'22:3','Và trong nhân loại đã có kẻ tranh luận về Allah không một chút hiểu biết nào cả và nghe theo từng tên Shaytan ương ngạnh.');
INSERT INTO "hasan_abdulkarim" VALUES(22,4,'22:4','Điều đã được qui định cho nó (Shaytan) là ai quay về kết bạn với nó thì sẽ bị nó dắt đi lạc và sẽ bị nó dắt đi đến hình phạt của hỏa ngục.');
INSERT INTO "hasan_abdulkarim" VALUES(22,5,'22:5','Hỡi nhân loại! Nếu các ngươi nghi ngờ về việc phục sinh thì quả thật TA đã tạo hóa các ngươi từ (đất) bụi rồi từ tinh dịch rồi từ một hòn máu đặc rồi từ một miếng thịt thành hình đầy đủ và không thành hình (sẩy thai) để TA trưng bày cho các ngươi (Quyền Năng của TA). Và TA đặt trong các dạ con người nào TA muốn đến một thời hạn ấn định; rồi TA mang các ngươi ra đời thành những đứa bé; rồi nuôi các ngươi đến tuổi trưởng thành; và trong các ngươi có người bị bắt hồn chết sớm và trong các ngươi có người được trả (hồn) về (sống) cho đến tuổi già yếu đến nỗi y không còn biết gì cả sau khi đã biết nhiều. Và ngươi thấy đất đai khô nứt nhưng khi TA tưới nước mưa lên đó, nó cựa mình sống lại và phồng lên và cho mọc lên đủ loại cây cối từng cặp.');
INSERT INTO "hasan_abdulkarim" VALUES(22,6,'22:6','(Việc phục sinh sẽ) như thế; bởi vì Allah, Ngài là Chân Lý tuyệt đối và Ngài làm cho người chết sống lại và rằng Ngài là Đấng Toàn Năng trên tất cả mọi thứ.');
INSERT INTO "hasan_abdulkarim" VALUES(22,7,'22:7','Và rằng Giờ Tận Thế đang tiến đến dần, không có gì phải nghi ngờ cả và Allah sẽ phục sinh bất cứ ai đang nằm dưới mộ.');
INSERT INTO "hasan_abdulkarim" VALUES(22,8,'22:8','Và trong nhân loại có kẻ tranh luận về Allah không một chút hiểu biết nào cả và không có một Chỉ Đạo và cũng không dựa vào một Kinh Sách minh bạch nào (để làm bằng chứng) cả.');
INSERT INTO "hasan_abdulkarim" VALUES(22,9,'22:9','Y quay người sang một bên( hầu làm cho (thiên hạ) rời xa con đường của Allah. Y sẽ bị hạ nhục ở đời này; và vào Ngày phục sinh, y sẽ bị TA cho nếm hình phạt của Lửa cháy bùng.');
INSERT INTO "hasan_abdulkarim" VALUES(22,10,'22:10','Như thế là vì những điều (tội lỗi) mà bàn tay của ngươi đã gởi đi trước. Bởi vì quả thật, Allah không hề đối xử bất công với bầy tôi của Ngài.');
INSERT INTO "hasan_abdulkarim" VALUES(22,11,'22:11','Và trong nhân loại có kẻ thờ phụng Allah đứng trên bờ lề (của đức tin). Bởi thế, nếu gặp điều phúc lành thì y sẽ hài lòng với nó. Ngược lại, nếu gặp hoạn nạn thì y quay mặt sang phía khác.Thế là y mất cả đời này lẫn đời sau. Đấy quả thật là một sự mất mát rõ ràng.');
INSERT INTO "hasan_abdulkarim" VALUES(22,12,'22:12','Y cầu nguyện ngoài Allah những kẻ không hãm hại được y và cũng không làm lợi gì cho y. Như thế là y lầm lạc rất xa.');
INSERT INTO "hasan_abdulkarim" VALUES(22,13,'22:13','Y cầu nguyện kẻ hãm hại y chứ không mang lợi gì cho y. Chắc chắn đó là một người chủ rất xấu và chắc chắn đó là một người bạn đồng hành rất xấu.');
INSERT INTO "hasan_abdulkarim" VALUES(22,14,'22:14','Quả thật, Allah sẽ thu nhận những ai tin tưởng và làm việc thiện vào những Ngôi Vườn (thiên đàng) bên dưới có các dòng sông chảy. Quả thật, Allah làm bất cứ điều gì Ngài muốn.');
INSERT INTO "hasan_abdulkarim" VALUES(22,15,'22:15','Ai nghĩ rằng Allah sẽ không bao giờ giúp đỡ Y (Nabi Muhammad) ở đời này và đời sau thì cứ nối một sợi dây cột vào trần nhà rồi tự thắt cổ, rồi để cho y xem phải chăng kế hoạch của y sẽ làm tiêu tan điều làm y tức giận?');
INSERT INTO "hasan_abdulkarim" VALUES(22,16,'22:16','Và đúng như thế, TA đã ban Nó (Qur''an) xuống bằng những câu kinh minh bạch và rằng Allah sẽ hướng dẫn người nào Ngài muốn.');
INSERT INTO "hasan_abdulkarim" VALUES(22,17,'22:17','Quả thật, những ai có đức tin (Muslim) và những người Do thái và những người Sobian và những người Thiên Chúa và những người thờ lửa và những ai thờ đa thần thì chắc chắn sẽ được Allah xét xử giữa họ vào Ngày phục sinh bởi vì chắc chắn Allah làm chứng cho tất cả mọi việc.');
INSERT INTO "hasan_abdulkarim" VALUES(22,18,'22:18','Há Ngươi (Muhammad) không nhận thấy Allah là Đấng mà bất cứ ai trong các tầng trời và bất cứ ai dưới đất cùng với mặt trời, mặt trăng, các tinh tú, núi non, cây cối, thú vật và đa số nhân loại đều phải phủ phục quỳ lạy hay sao? Nhưng đa số (người) cũng đáng bị Ngài trừng phạt. Và ai mà bị Allah hạ nhục thì sẽ không có ai làm cho vinh danh lại nổi. Quả thật, Allah làm bất cứ điều gì Ngài muốn.');
INSERT INTO "hasan_abdulkarim" VALUES(22,19,'22:19','Hai nhóm đối lập (Muslim và Kafir) này tranh luận nhau về Thượng Đế của họ. Bởi thế những kẻ phủ nhận (Allah) sẽ mặc áo may bằng các mảnh Lửa, đầu sẽ hứng chịu một loại nước thật sôi xối lên trên.');
INSERT INTO "hasan_abdulkarim" VALUES(22,20,'22:20','(Nước sôi này) sẽ làm chảy tan bất cứ vật gì trong bụng và làn da của chúng.');
INSERT INTO "hasan_abdulkarim" VALUES(22,21,'22:21','Và chúng sẽ bị đánh bằng những cây roi sắt.');
INSERT INTO "hasan_abdulkarim" VALUES(22,22,'22:22','Mỗi lần chúng muốn lẩn trốn khỏi hình phạt đau đớn của (hỏa ngục) thì sẽ bị bắt đưa vào trở lại (với lời phán:) “Hãy nếm hình phạt của sự cháy phỏng!”');
INSERT INTO "hasan_abdulkarim" VALUES(22,23,'22:23','Quả thật, Allah sẽ thu nhận những ai có đức tin và làm việc thiện vào những Ngôi Vườn (thiên đàng) bên dưới có các dòng sông chảy; trong đó, họ sẽ được trang điểm bằng vòng vàng và ngọc trai; và sẽ phục sức bằng lụa là.');
INSERT INTO "hasan_abdulkarim" VALUES(22,24,'22:24','Bởi vì (trên thế gian) họ đã được hướng dẫn đến (nghe) những lời nói tốt đẹp và họ đã được hướng dẫn đến con đường của Đấng (Allah) Rất Đáng Ca tụng.');
INSERT INTO "hasan_abdulkarim" VALUES(22,25,'22:25','Quả thật (đối với) những kẻ không tin tưởng và cản người khác không cho vào Al-Masjid Al-Haram (tại Makkah) nơi mà TA đã làm ra bình đẳng cho mọi người, cho dân cư của nó cũng như cho du khách thập phương (đến thờ phụng). Và ai có ý định làm điều xúc phạm một cách sai quấy trong đó, thì TA sẽ cho nếm một hình phạt đau đớn.');
INSERT INTO "hasan_abdulkarim" VALUES(22,26,'22:26','Và (hãy nhớ) khi TA (Allah) chỉ định cho Ibrahim địa điểm xây dựng Ngôi Đền (Ka''bah) phán truyền: "Chớ tổ hợp với TA bất cứ cái gì và hãy giữ Ngôi Đền của TA trong sạch cho những người đi vòng quanh nó, cho những người đứng dâng lễ, và cho những người cúi đầu và phủ phục (lễ bái TA)."');
INSERT INTO "hasan_abdulkarim" VALUES(22,27,'22:27','"Và hãy tuyên bố với nhân loại việc thi hành Hajj(. Họ sẽ đi bộ hoặc cưỡi từng con lạc đà gầy ốm băng qua từng hẻm núi sâu thẳm để đến cùng (dâng lễ) với Ngươi."');
INSERT INTO "hasan_abdulkarim" VALUES(22,28,'22:28','"Để cho họ chứng kiến những mối lợi được ban cấp cho họ và để họ tụng niệm tên của Allah trong một số ngày ấn định( và (nhắc tên của Allah) trên những con thú nuôi mà Ngài đã cung cấp cho họ (để làm vật tế)(110). Do đó, hãy ăn thịt của chúng (sau khi tế) và phân phối (thịt của chúng) cho những người nghèo đói."');
INSERT INTO "hasan_abdulkarim" VALUES(22,29,'22:29','“Rồi để cho họ kết thúc tình trạng xốc xếch bù xù( của họ và để cho họ hoàn tất lời thề của họ và đi vòng quanh Ngôi Đền(112) lâu đời.”');
INSERT INTO "hasan_abdulkarim" VALUES(22,30,'22:30','Đó (là việc thi hành Hajj). Và ai tôn trọng những giới cấm của Allah thì là điều tốt cho y đối với Thượng Đế của y. Và (Allah) cho phép các ngươi (dùng thịt của) những con thú nuôi (trong lúc thi hành Hajj) ngoại trừ những con thú nào đã được đọc ra cho các ngươi. Do đó, hãy tránh sự ô nhiễm của (việc thờ cúng) những tượng thần và tránh lời nói giả dối.');
INSERT INTO "hasan_abdulkarim" VALUES(22,31,'22:31','Chính trực với Allah trong đức tin, không tổ hợp những đối tác ngang vai cùng với Ngài. Và ai tổ hợp những đối tác ngang vai cùng với Allah thì chẳng khác nào như bị rơi từ trên trời xuống rồi bị chim chóc chụp bắt đưa đi xa hoặc như bị một trận gió ào đến quét mang đi đến một nơi xa xôi.');
INSERT INTO "hasan_abdulkarim" VALUES(22,32,'22:32','(Việc làm Hajj) như thế. Và ai tôn trọng các Biểu Hiệu của Allah thì là có lòng sợ Allah.');
INSERT INTO "hasan_abdulkarim" VALUES(22,33,'22:33','Nơi chúng (những con vật tế), các ngươi có được những mối lợi trong một thời hạn ấn định. Rồi (chúng sẽ được dắt đến) địa điểm tế nhắm về Ngôi Đền cổ.');
INSERT INTO "hasan_abdulkarim" VALUES(22,34,'22:34','Và TA ban cho mỗi cộng đồng một số nghi lễ cúng tế để họ có thể niệm tên của Allah trên những con thú nuôi mà Ngài đã ban cấp cho họ dùng làm thực phẩm. Nhưng Thượng Đế của các ngươi chỉ là một Thượng Đế duy nhất. Do đó, hãy thần phục Ngài trong Islam. Và hãy báo tin mừng cho những người hạ mình khiêm tốn:');
INSERT INTO "hasan_abdulkarim" VALUES(22,35,'22:35','Những ai khi nghe nhắc đến tên của Allah lòng họ rún động vì khiếp sợ Ngài và kiên nhẫn chịu đựng những điều bất hạnh rơi nhằm phải họ và dâng lễ nguyện Salah và chi dùng những bổng lộc mà TA đã cung cấp.');
INSERT INTO "hasan_abdulkarim" VALUES(22,36,'22:36','Và những con Budn (lạc đà, bò, cừu, dê v.v...) mà TA đã ban cấp cho các ngươi (làm vật tế) được kể như là các Biểu Hiệu của Allah ban cho các ngươi. Nơi chúng các ngươi có được các món tốt. Do đó, khi chúng đứng thành hàng (để tế) hãy đọc tên của Allah lên chúng (và cắt cổ chúng). Khi chúng đã ngã xuống nằm một bên hông, hãy dùng thịt của chúng và bố thí cho những người nghèo sống bằng lòng với số phận và cho những người ăn xin. TA đã chế phục chúng cho các ngươi như thế để các ngươi có dịp tạ ơn Allah.');
INSERT INTO "hasan_abdulkarim" VALUES(22,37,'22:37','Không phải thịt cũng không phải máu của chúng đến tận Allah mà chính lòng ngay chính sợ Allah của các ngươi lên giáp Ngài. Ngài chế phục chúng cho các ngươi như thế là để cho các ngươi có dịp tán dương sự Vĩ đại của Allah về việc Ngài đã hướng dẫn các ngươi Và hãy báo tin mừng cho những người làm tốt.');
INSERT INTO "hasan_abdulkarim" VALUES(22,38,'22:38','Quả thật, Allah sẽ bảo vệ những ai có đức tin; và quả thật, Allah không yêu thương từng tên phản trắc, vô ơn (theo Shaytan).');
INSERT INTO "hasan_abdulkarim" VALUES(22,39,'22:39','(Allah) chấp thuận cho những ai bị tấn công được phép (cầm vũ khí) chiến đấu (tự vệ) bởi vì họ bị áp bức. Quả thật, Allah là Đấng Toàn Năng trong việc trợ giúp họ:');
INSERT INTO "hasan_abdulkarim" VALUES(22,40,'22:40','Những ai bị trục xuất ra khỏi nhà cửa của họ không ngoài lý do chính đáng nào khác hơn điều họ nói: “Thượng Đế (Đấng Chủ Tể) của chúng tôi là Allah!” Và nếu Allah không dùng một số người này để chặn đứng một số người kia thì những tu viện và nhà thờ (của Thiên Chúa giáo), và giáo đường (của Do Thái giáo) và thánh đường (của Islam), những nơi mà tên của Allah đã được tụng niệm nhiều, chắc chắn sẽ bị phá sụp. Và chắc chắn Allah giúp đỡ người nào giúp đỡ (Chính nghĩa của) Ngài. Quả thật, Allah Rất Mạnh, Rất Quyền Năng.');
INSERT INTO "hasan_abdulkarim" VALUES(22,41,'22:41','Những ai, nếu TA thiết lập họ trên trái đất, sẽ dâng lễ Salah và đóng Zakah và ra lệnh làm điều lành (do Allah truyền) và cấm làm điều ác (do Allah cấm). Và Allah là Đấng nắm quyền chung quyết về mọi vấn đề.');
INSERT INTO "hasan_abdulkarim" VALUES(22,42,'22:42','Và nếu chúng (những người Quraish tại Makkah) cho Ngươi nói dối thì chắc chắn trước chúng đám dân của Nuh và của ''Ad và của Thamud cũng đã cho (các Nabi của họ) nói dối (giống như thế).');
INSERT INTO "hasan_abdulkarim" VALUES(22,43,'22:43','Và đám dân của Ibrahim và đám dân của Lut (cũng thế);');
INSERT INTO "hasan_abdulkarim" VALUES(22,44,'22:44','Và người dân của Madyan (cũng vậy); và Musa cũng đã bị cáo buộc đã nói dối. Bởi thế, TA (Allah) đã thả lỏng cho những kẻ không tin, rồi (sau đó) TA đã bắt phạt chúng. Bởi thế, (hãy xem) sự trừng phạt của TA đã xảy ra khủng khiếp như thế nào!');
INSERT INTO "hasan_abdulkarim" VALUES(22,45,'22:45','Vậy có nhiều thị trấn đã bị TA tiêu diệt trong lúc chúng phạm điều sai quấy đến nỗi mái nhà của chúng nằm lật ngược ngổn ngang; và nhiều giếng nước và lâu đài kiên cố bị bỏ hoang.');
INSERT INTO "hasan_abdulkarim" VALUES(22,46,'22:46','Thế phải chăng chúng đã không đi du lịch trên trái đất để cho tâm trí của chúng hiểu được (Thực tại) và tai của chúng có dịp nghe câu chuyện về (sự Trừng Phạt)? Bởi thế, không phải cặp mắt của chúng đã mù mà chính tâm trí của chúng đã mù lòa vậy.');
INSERT INTO "hasan_abdulkarim" VALUES(22,47,'22:47','Và chúng thúc giục Ngươi mang hình phạt đến trong lúc Allah không bao giờ thất hứa trong Lời Hứa của Ngài bởi vì quả thật một Ngày đối với Thượng Đế của Ngươi là bằng một ngàn năm theo niên kỷ do các ngươi ghi đếm.');
INSERT INTO "hasan_abdulkarim" VALUES(22,48,'22:48','Và đã có nhiều thị trấn đã được TA gia hạn trong lúc chúng đã làm điều sai quấy. Rồi TA bắt phạt chúng; và tất cả sẽ trở về với TA.');
INSERT INTO "hasan_abdulkarim" VALUES(22,49,'22:49','Hãy bảo (hỡi Muhammad): “Hỡi nhân loại! Ta chỉ là một người báo trước công khai (được cử đến) với các người.”');
INSERT INTO "hasan_abdulkarim" VALUES(22,50,'22:50','Bởi thế, những ai có đức tin và làm việc thiện thì sẽ được (Allah) tha thứ (tội lỗi) và được (Ngài) ban cấp bổng lộc dồi dào.');
INSERT INTO "hasan_abdulkarim" VALUES(22,51,'22:51','Ngược lại, những ai tìm mọi cách để phá hoại những Lời Mặc Khải của TA thì sẽ là cư dân của hỏa ngục.');
INSERT INTO "hasan_abdulkarim" VALUES(22,52,'22:52','Và không một Sứ Giả hay một Nabi nào mà TA đã cử đến trước Ngươi (lại không vấp điều này): khi Y mong muốn một điều tốt nào thì Shaytan thừa cơ nhảy vào đưa đề nghị sửa đổi điều Y muốn. Nhưng Allah hủy bỏ điều mà Shaytan đã xúi giục, rồi Allah tiếp tục xác nhận những Lời Mặc Khải của Ngài bởi vì Allah Toàn Tri, Rất Mực Sáng Suốt.');
INSERT INTO "hasan_abdulkarim" VALUES(22,53,'22:53','(Làm thế) để Ngài dừng điều mà Shaytan đã xen vào làm một sự thử thách đối với những ai mang trong lòng một căn bệnh (nghi hoặc và giả dối) và với những ai mà tấm lòng đã chai cứng. Và chắc chắn những kẻ làm điều sai quấy sẽ ở trong sự phân hóa cách biệt xa (Chân Lý).');
INSERT INTO "hasan_abdulkarim" VALUES(22,54,'22:54','Và để cho những ai có kiến thức biết Nó (Qur''an) là Chân Lý do Thượng Đế của Ngươi đã ban xuống, và để họ tin tưởng nơi Nó và để tấm lòng của họ khiêm tốn trước Nó. Quả thật, Allah là Đấng Hướng Dẫn những ai có đức tin đến với Chính Đạo.');
INSERT INTO "hasan_abdulkarim" VALUES(22,55,'22:55','Còn những ai không có đức tin thì sẽ không ngừng nghi ngờ Nó cho đến lúc Giờ Tận Thế đột nhiên xảy đến cho chúng hoặc hình phạt của một Ngày Xấu Xa sẽ (bất ngờ) xảy đến cho chúng.');
INSERT INTO "hasan_abdulkarim" VALUES(22,56,'22:56','Vào Ngày đó, mọi quyền hành sẽ nằm trong tay của Allah. Ngài sẽ xét xử giữa bọn chúng. Bởi thế, những ai có đức tin và làm việc thiện thì sẽ ở trong những Ngôi Vườn hạnh phúc (thiên đàng).');
INSERT INTO "hasan_abdulkarim" VALUES(22,57,'22:57','Ngược lại, những ai không có đức tin và cho những Lời Mặc Khải của TA giả dối thì sẽ chịu một sự trừng phạt nhục nhã.');
INSERT INTO "hasan_abdulkarim" VALUES(22,58,'22:58','Và những ai vì Chính Nghĩa của Allah đã dời cư rồi bị giết hoặc chết thì chắc chắn sẽ được Allah cung cấp cho một nguồn bổng lộc tốt. Bởi quả thật Allah là Đấng Ban Phát Ưu việt.');
INSERT INTO "hasan_abdulkarim" VALUES(22,59,'22:59','Chắc chắn Ngài sẽ đưa họ vào một nơi mà họ sẽ rất hài lòng. Và quả thật, Allah là Đấng Hằng Biết, Hằng Chịu Đựng.');
INSERT INTO "hasan_abdulkarim" VALUES(22,60,'22:60','(Sự việc sẽ) như thế. Và ai đánh trả lại ngang bằng với thương tích mà y đã chịu rồi lại bị áp bức nữa thì chắc chắn Allah sẽ giúp đỡ y bởi vì quả thật Allah Hằng Lượng Thứ, Hằng Tha Thứ.');
INSERT INTO "hasan_abdulkarim" VALUES(22,61,'22:61','(Sự việc sẽ) như thế là vì Allah nhập ban đêm vào ban ngày và nhập ban ngày vào ban đêm và rằng Allah là Đấng Hằng Nghe, Hằng Thấy (mọi việc).');
INSERT INTO "hasan_abdulkarim" VALUES(22,62,'22:62','(Sự việc sẽ) như thế là vì Allah là Chân Lý Tuyệt Đối. Và bởi vì những kẻ mà chúng cầu nguyện ngoài Ngài chỉ là ngụy tạo và bởi vì Allah là Đấng Tối Cao, Đấng Vĩ Đại.');
INSERT INTO "hasan_abdulkarim" VALUES(22,63,'22:63','Há Ngươi không thấy việc Allah ban nước mưa từ trên trời xuống nhờ đó đất đai trở thành xanh tươi? Quả thật, Allah Rất Mực Tinh Tế, Rất Mực Am Tường.');
INSERT INTO "hasan_abdulkarim" VALUES(22,64,'22:64','Bất cứ cái gì trong các tầng trời và bất cứ cái gì dưới đất đều là (tạo vật) của Ngài cả. Và quả thật, Allah Rất Mực Giàu Có, Rất Đáng Ca Tụng.');
INSERT INTO "hasan_abdulkarim" VALUES(22,65,'22:65','Há Ngươi không thấy việc Allah đã chế ngự mọi vật trên đất liền cho các ngươi (sử dụng) kể cả những chiếc tàu chạy băng qua đại dương theo sự Chỉ Huy của Ngài? Và việc Ngài giữ bầu trời không cho rơi xuống đất trừ phi Ngài cho phép. Quả thật, Allah Rất Mực Nhân từ, Rất Mực Khoan Dung.');
INSERT INTO "hasan_abdulkarim" VALUES(22,66,'22:66','Và Ngài là Đấng làm cho các ngươi sống rồi làm cho các ngươi chết rồi làm cho các ngươi sống lại (vào Ngày phục sinh). Quả thật, con người là một kẻ vong ân.');
INSERT INTO "hasan_abdulkarim" VALUES(22,67,'22:67','TA đã qui định cho mỗi cộng đồng một nghi lễ thờ phụng( mà họ phải tuân theo. Vậy chớ để cho chúng (người ngoại đạo) tranh luận với Ngươi về vấn đề (ăn thịt các con vật được cắt cổ và không được cắt cổ), tuy nhiên, hãy gọi mời chúng đến với Thượng Đế của Ngươi. Quả thật, Ngươi đang ở trên Chính Đạo.');
INSERT INTO "hasan_abdulkarim" VALUES(22,68,'22:68','Và nếu chúng tranh luận với Ngươi (về việc tế vật) thì hãy bảo (chúng): “Chắc chắn Allah biết rõ điều các người đang làm.”');
INSERT INTO "hasan_abdulkarim" VALUES(22,69,'22:69','“Allah sẽ phân xử các người vào Ngày phục sinh về những vấn đề mà các người đang tranh chấp.”');
INSERT INTO "hasan_abdulkarim" VALUES(22,70,'22:70','Há Ngươi không biết Allah biết mọi điều trên trời và dưới đất hay sao? Bởi vì quả thật, mọi điều đó đều nằm trong một Quyển Kinh (Al-Lauh Al-Mahfuzh). Và quả thật, điều đó rất dễ đối với Allah.');
INSERT INTO "hasan_abdulkarim" VALUES(22,71,'22:71','Nhưng chúng lại tôn thờ ngoài Allah những kẻ (vật) mà Ngài đã không ban một chút thẩm quyền nào và cũng không biết một tí gì về chúng; và những kẻ làm điều sai quấy sẽ không được ai giúp đỡ cả.');
INSERT INTO "hasan_abdulkarim" VALUES(22,72,'22:72','Và khi những Lời Mặc Khải của TA được đọc lên nhắc nhở chúng, Ngươi sẽ thấy hiện ra trên gương mặt của những kẻ không có đức tin nét phản đối. Chúng gần muốn nhảy xổ đến tấn công những ai đọc những Lời Mặc Khải của TA cho chúng. Hãy bảo chúng: “Vậy các người có muốn ta báo cho biết về một điều còn tệ hơn cái đó nữa hay không? (Đó là) Lửa mà Allah đã hứa ban cho những kẻ không có đức tin; và đó là một nơi đến cuối cùng rất xấu xa.”');
INSERT INTO "hasan_abdulkarim" VALUES(22,73,'22:73','Hỡi nhân loại! Đây là một hình ảnh thí dụ được trình bày cho các ngươi. Hãy lắng nghe cho kỹ! Quả thật, những kẻ mà các ngươi cầu nguyện ngoài Allah sẽ không bao giờ tạo ra được một con ruồi dẫu chúng có hợp lực với nhau để làm chuyện đó; và nếu con ruồi đó có giật tha đi món gì của chúng thì chung cũng sẽ không thể lấy nó lại được. Kẻ van cầu cũng như kẻ được cầu đều bất lực cả.');
INSERT INTO "hasan_abdulkarim" VALUES(22,74,'22:74','Họ không thể đánh giá Allah đúng với giá trị đích thực của Ngài. Quả thật Allah Toàn Lực, Toàn Năng.');
INSERT INTO "hasan_abdulkarim" VALUES(22,75,'22:75','Allah chọn các vị Sứ (của Ngài) trong hàng ngũ của Thiên Thần và nhân loại. Quả thật, Allah Hằng Nghe và Hằng Thấy.');
INSERT INTO "hasan_abdulkarim" VALUES(22,76,'22:76','Ngài biết điều gì trước họ và điều gì sau họ. Và mọi việc đều được đưa trở về cho Allah (quyết định).');
INSERT INTO "hasan_abdulkarim" VALUES(22,77,'22:77','Hỡi những ai có đức tin! Hãy cúi đầu và phủ phục và thờ phụng Thượng Đế (Allah) của các ngươi và hãy làm điều lành để may ra các ngươi được thành đạt.');
INSERT INTO "hasan_abdulkarim" VALUES(22,78,'22:78','Và hãy đấu tranh cho Allah như một nghĩa vụ đối với Ngài. Ngài đã chọn các ngươi (để làm việc đó); và Ngài đã không đặt gánh nặng lên các ngươi trong tôn giáo(. (Tôn giáo này là) tín ngưỡng của tổ phụ Ibrahim của các ngươi. Ngài đã đặt tên cho các ngươi là ''Muslim'' từ trước và trong hiện tại. (Mục đích) để cho Sứ Giả (Muhammad) trở thành một nhân chứng cho các ngươi và để (sau đó) các ngươi trở thành nhân chứng cho nhân loại(115). Bởi thế, các ngươi hãy dâng lễ nguyện Salah và đóng Zakah và hãy bám chắc vào Allah. Ngài là Đấng Giám Hộ của các ngươi; Ngài là Đấng Giám Hộ, Đấng Cứu Giúp Ưu việt.');
INSERT INTO "hasan_abdulkarim" VALUES(23,1,'23:1','Những người có đức tin chắc chắn thành đạt.');
INSERT INTO "hasan_abdulkarim" VALUES(23,2,'23:2','Họ là những ai hạ mình khiêm tốn trong việc dâng lễ nguyện Salah.');
INSERT INTO "hasan_abdulkarim" VALUES(23,3,'23:3','Và là những ai tránh xa chuyện tầm phào vô bổ.');
INSERT INTO "hasan_abdulkarim" VALUES(23,4,'23:4','Và là những ai tích cực đóng Zakah.');
INSERT INTO "hasan_abdulkarim" VALUES(23,5,'23:5','Và là những ai che giữ phần kín đáo.');
INSERT INTO "hasan_abdulkarim" VALUES(23,6,'23:6','Ngoại trừ với vợ hoặc với những (nữ tù binh) nằm dưới tay phải của họ thì không bị khiển trách (về việc đó).');
INSERT INTO "hasan_abdulkarim" VALUES(23,7,'23:7','Nhưng nếu ai tìm cách vượt quá mức giới hạn thì là những kẻ phạm tội.');
INSERT INTO "hasan_abdulkarim" VALUES(23,8,'23:8','Và là những ai thực hiện đúng lời ủy thác và lời hứa.');
INSERT INTO "hasan_abdulkarim" VALUES(23,9,'23:9','Và là những ai cẩn thận giữ gìn các lễ nguyện Salah của họ.');
INSERT INTO "hasan_abdulkarim" VALUES(23,10,'23:10','Họ thật sự là những người thừa kế.');
INSERT INTO "hasan_abdulkarim" VALUES(23,11,'23:11','Họ sẽ thừa hưởng thiên đàng Hạnh Phúc, nơi mà họ sẽ vào sống đời đời.');
INSERT INTO "hasan_abdulkarim" VALUES(23,12,'23:12','Và quả thật, TA đã tạo con người từ một loại đất sét tinh chất.');
INSERT INTO "hasan_abdulkarim" VALUES(23,13,'23:13','Và TA đã làm cho y (con cháu của Adam) thành một tinh dịch được lưu trữ tại một nơi nghỉ an toàn (tử cung, dạ con).');
INSERT INTO "hasan_abdulkarim" VALUES(23,14,'23:14','Rồi TA tạo hóa tinh dịch thành một hòn máu đặc, sau đó TA tạo hóa hòn máu đặc thành một miếng thịt, tiếp đó TA tạo hóa miếng thịt thành xương, rồi TA lấy thịt bao xương lại rồi TA làm cho nó thành một tạo vật khác. Do đó, phúc thay Allah, Đấng Tạo Hóa Ưu Việt!');
INSERT INTO "hasan_abdulkarim" VALUES(23,15,'23:15','Rồi sau đó, chắc chắn các ngươi sẽ chết.');
INSERT INTO "hasan_abdulkarim" VALUES(23,16,'23:16','Rồi vào Ngày phục sinh, chắc chắn các ngươi sẽ được dựng sống lại.');
INSERT INTO "hasan_abdulkarim" VALUES(23,17,'23:17','Và quả thật, TA đã tạo hóa bên trên các ngươi bảy quỹ đạo (tầng trời) và TA không bao giờ vô tâm và bỏ mặc (không kiểm soát) sự tạo hóa (của TA).');
INSERT INTO "hasan_abdulkarim" VALUES(23,18,'23:18','Và TA cho mưa từ trên trời xuống theo mức lượng ấn định rồi làm cho nó thắm vào lòng đất; và TA có toàn quyền tháo bỏ nó đi (bất cứ lúc nào).');
INSERT INTO "hasan_abdulkarim" VALUES(23,19,'23:19','Rồi TA dùng nó (nước mưa) để làm (mọc) ra cho các ngươi những mảnh vườn chà là và vườn nho mà các ngươi sẽ thu hoạch được nhiều trái quả để dùng.');
INSERT INTO "hasan_abdulkarim" VALUES(23,20,'23:20','Và có một loại cây mọc tại núi Sinai cho ra dầu và dùng làm nước tương cho người dùng.');
INSERT INTO "hasan_abdulkarim" VALUES(23,21,'23:21','Quả thật, các ngươi rút được một bài học nơi gia súc. TA cho các ngươi uống một loại thức uống (sữa) vắt từ bụng của chúng; chúng còn cung cấp cho các ngươi nhiều nguồn lợi khác; và thịt của chúng các ngươi dùng để ăn.');
INSERT INTO "hasan_abdulkarim" VALUES(23,22,'23:22','Và các ngươi được chuyên chở trên (lưng của) chúng và được chở trên (boong) tàu.');
INSERT INTO "hasan_abdulkarim" VALUES(23,23,'23:23','Và quả thật, TA đã cử Nuh đến cho người dân của Y và Y bảo họ: “Này hỡi dân ta! Hãy thờ phụng Allah! Các người không có Thượng Đế nào khác ngoài Ngài. Thế các người không sợ Ngài hay sao?”');
INSERT INTO "hasan_abdulkarim" VALUES(23,24,'23:24','Nhưng các lãnh tụ của những kẻ không tin trong người dân của Y đã nói: “Người này chỉ là một người phàm như quí vị. Y muốn làm bá chủ trên quí vị. Và nếu Allah muốn (cử một Sứ Giả đến với quí vị) thì Ngài đã cử những Thiên Thần (chứ không phải người phàm như Y). Chúng ta chưa hề nghe điều (mà Y nói) này nơi tổ tiên của chúng ta.”');
INSERT INTO "hasan_abdulkarim" VALUES(23,25,'23:25','“Thật sự Y chỉ là một tên bị quỷ nhập. Vậy hãy rán chịu đựng với Y trong một thời gian (xem sao?”)');
INSERT INTO "hasan_abdulkarim" VALUES(23,26,'23:26','(Nuh cầu nguyện) thưa: “Lạy Thượng Đế của bề tôi! Xin Ngài giúp đỡ bề tôi bởi vì chúng cho bề tôi nói dối.”');
INSERT INTO "hasan_abdulkarim" VALUES(23,27,'23:27','Bởi thế, TA đã mặc khải cho Y bảo: "Nhà ngươi hãy đóng một chiếc tàu dưới mắt quan sát của TA và theo Lời Mặc Khải của TA và khi Mệnh lệnh của TA đến (cho nhà ngươi qua việc) nước phụt lên từ bếp lò, (lúc đó) hãy mang lên tàu mỗi cặp thú (đực và cái) và gia đình của nhà ngươi ngoại trừ ai trong bọn chúng mà Lời (trừng phạt của TA) đã được tuyên bố trước cho y; và chớ kêu nài với TA giùm cho những ai làm điều sai quấy. Chắc chắn chúng nó sẽ bị nhận chết chìm (dưới nước lụt)."');
INSERT INTO "hasan_abdulkarim" VALUES(23,28,'23:28','"Và khi nhà ngươi lên tàu và người nào theo Ngươi hãy tán dương thưa: Mọi lời ca tụng đều dâng lên Allah, Đấng đã cứu vớt bầy tôi khỏi đám người làm điều sai quấy."');
INSERT INTO "hasan_abdulkarim" VALUES(23,29,'23:29','"Và hãy thưa: Lạy Thượng Đế của bề tôi! Xin Ngài làm cho bề tôi cập bờ tại một bãi đậu mà Ngài ban phúc bởi vì Ngài là Đấng giúp đưa lên bờ ưu việt hơn hết."');
INSERT INTO "hasan_abdulkarim" VALUES(23,30,'23:30','Quả thật, nơi sự việc đó là những dấu hiệu cho những người thông hiểu. Và quả thật, TA thử thách (loài người).');
INSERT INTO "hasan_abdulkarim" VALUES(23,31,'23:31','Rồi sau họ, TA đã tạo ra một thế hệ khác.');
INSERT INTO "hasan_abdulkarim" VALUES(23,32,'23:32','Rồi TA đã cử đến với họ một Sứ Giả xuất thân từ họ, (bảo Y) tuyên bố: “Hãy thờ phụng Allah, các người không có Thượng Đế nào khác ngoài Ngài. Thế các người sẽ không sợ Ngài hay sao?”');
INSERT INTO "hasan_abdulkarim" VALUES(23,33,'23:33','Nhưng các lãnh tụ trong người dân của Y, những ai không tin tưởng và phủ nhận việc gặp gỡ (Allah) ở đời sau và những ai đã được TA ban cho một đời sống xa hoa ở trần thế này, đã nói: "Người này chỉ là một người phàm như quí vị. Y ăn thực phẩm mà quí vị ăn và uống loại nước mà quí vị uống."');
INSERT INTO "hasan_abdulkarim" VALUES(23,34,'23:34','"Và nếu quí vị nghe theo một người phàm giống quí vị thì lúc đó chắc chắn quí vị sẽ là những người thua thiệt."');
INSERT INTO "hasan_abdulkarim" VALUES(23,35,'23:35','"Phải chăng Y đã hứa với quí vị rằng khi quí vị đã chết và thành cát bụi và xương tàn, quí vị sẽ được đưa ra (khỏi mộ và) sống trở lại?"');
INSERT INTO "hasan_abdulkarim" VALUES(23,36,'23:36','"Thật xa vời, xa vời thay điều mà quí vị đã được hứa!"');
INSERT INTO "hasan_abdulkarim" VALUES(23,37,'23:37','"Quả thật (không có) đời sống (nào khác cả) mà chỉ có đời sống của chúng ta ở thế gian này. Chúng ta chết và chúng ta sống. Và chúng ta sẽ không được phục sinh trở lại."');
INSERT INTO "hasan_abdulkarim" VALUES(23,38,'23:38','"Quả thật, Y chỉ là một người phàm. Y đã bịa đặt điều nói dối rồi đổ thừa cho Allah và chúng ta sẽ không tin tưởng nơi Y."');
INSERT INTO "hasan_abdulkarim" VALUES(23,39,'23:39','Y thưa: “Lạy Thượng Đế của bề tôi! Xin Ngài giúp đỡ bề tôi bởi vì chúng cho bề tôi nói dối.”');
INSERT INTO "hasan_abdulkarim" VALUES(23,40,'23:40','(Allah) phán: “Chỉ trong một thời gian ngắn nữa, chắc chắn chúng sẽ ân hận.”');
INSERT INTO "hasan_abdulkarim" VALUES(23,41,'23:41','Rồi (hình phạt) chụp bắt chúng đúng với sự thật. Và TA đã làm cho chúng thành một đóng rác. Bởi thế đi đời lũ người làm điều sai quấy!');
INSERT INTO "hasan_abdulkarim" VALUES(23,42,'23:42','Rồi sau họ, TA đã tạo ra một thế hệ khác.');
INSERT INTO "hasan_abdulkarim" VALUES(23,43,'23:43','Không một cộng đồng nào có thể thúc giục thời hạn của nó sớm chấm dứt và cũng không thể dời nó về sau.');
INSERT INTO "hasan_abdulkarim" VALUES(23,44,'23:44','Rồi TA lần lượt cử các Sứ Giả của TA đến. Mỗi lần Sứ Giả của họ đến, họ cho Y nói dối. Bởi thế, TA đã bắt phạt cộng đồng này đến cộng đồng kia. Và TA làm cho chúng trở thành những câu chuyện răn đời. Cho nên, đi đời lũ người không tin tưởng!');
INSERT INTO "hasan_abdulkarim" VALUES(23,45,'23:45','Rồi TA cử Musa và người em của Y Harun, mang theo các Phép Lạ của TA với một thẩm quyền rõ rệt;');
INSERT INTO "hasan_abdulkarim" VALUES(23,46,'23:46','Đến gặp Fir-''awn và các vị tù trưởng của hắn. Nhưng chúng hống hách tự cao và chúng là một bè lũ tự phụ.');
INSERT INTO "hasan_abdulkarim" VALUES(23,47,'23:47','Rồi chúng nói: “Há chúng mình sẽ phải tin tưởng nơi hai người phàm giống như chúng mình trong lúc người dân của hai đứa nó lại là tôi tớ của chúng mình hay sao?”');
INSERT INTO "hasan_abdulkarim" VALUES(23,48,'23:48','Bởi thế, chúng đã phủ nhận hai người và trở thành những kẻ bị tiêu diệt.');
INSERT INTO "hasan_abdulkarim" VALUES(23,49,'23:49','Và quả thật, TA đã ban cho Musa Kinh Sách để họ được hướng dẫn.');
INSERT INTO "hasan_abdulkarim" VALUES(23,50,'23:50','Và TA đã làm cho đứa con trai của Maryam (Ysa) và người mẹ của Y (Maryam) thành một Dấu Lạ và đã định cư hai người ở một trên cao, một chỗ nghỉ yên lành với dòng suối chảy.');
INSERT INTO "hasan_abdulkarim" VALUES(23,51,'23:51','Hỡi các Sứ Giả! Hãy dùng thức ăn tốt và sạch và làm việc thiện. Quả thật, TA Hằng Biết điều các ngươi làm.');
INSERT INTO "hasan_abdulkarim" VALUES(23,52,'23:52','Quả thật, cộng đồng của các ngươi là một cộng đồng duy nhất. Và TA là Thượng Đế của các ngươi. Do đó hãy sợ TA.');
INSERT INTO "hasan_abdulkarim" VALUES(23,53,'23:53','Nhưng họ đã chia cắt công việc (thống nhất đạo) giữa họ với nhau thành giáo phái; mỗi giáo phái vui vầy với điều họ đang có.');
INSERT INTO "hasan_abdulkarim" VALUES(23,54,'23:54','Vậy hãy để mặc họ ở trong cảnh hỗn loạn đến một kỳ hạn (ấn định).');
INSERT INTO "hasan_abdulkarim" VALUES(23,55,'23:55','Phải chăng họ nghĩ việc TA gia tăng của cải và con cái thêm cho họ,');
INSERT INTO "hasan_abdulkarim" VALUES(23,56,'23:56','(Là) TA thúc đẩy điều tốt đến cho họ hay sao? Không! Họ không nhận thấy (Sự thật).');
INSERT INTO "hasan_abdulkarim" VALUES(23,57,'23:57','Quả thật, những ai có lòng Kính sợ mới thực sự là những người sợ Thượng Đế của họ,');
INSERT INTO "hasan_abdulkarim" VALUES(23,58,'23:58','Và những ai tin tưởng nơi những Lời Mặc Khải của Thượng Đế của họ,');
INSERT INTO "hasan_abdulkarim" VALUES(23,59,'23:59','Và những ai (trong việc thờ phụng) không tổ hợp những đối tác ngang vai cùng với Thượng Đế của họ,');
INSERT INTO "hasan_abdulkarim" VALUES(23,60,'23:60','Và những ai bố thí tặng vật cho người khác nhưng tấm lòng của họ luôn luôn lo sợ bởi vì họ sẽ trở về gặp lại Thượng Đế của họ,');
INSERT INTO "hasan_abdulkarim" VALUES(23,61,'23:61','Những người đó mới thực sự là những người thi đua nhau làm việc thiện và là những người tiên phong (trong việc thiện tốt).');
INSERT INTO "hasan_abdulkarim" VALUES(23,62,'23:62','TA không bắt mỗi người chịu đựng quá khả năng của y và TA đang giữ một tập hồ sơ luôn luôn nói sự Thật và họ sẽ không bị đối xử bất công.');
INSERT INTO "hasan_abdulkarim" VALUES(23,63,'23:63','Không, tấm lòng của chúng đang hoang mang về (Qur''an) này và ngoài điều đó chúng còn (hoang mang về) những công việc (tội lỗi) khác mà chúng đang làm.');
INSERT INTO "hasan_abdulkarim" VALUES(23,64,'23:64','Cho đến khi TA bắt phạt những kẻ sống xa hoa trong bọn chúng thì chúng van xin tha mạng.');
INSERT INTO "hasan_abdulkarim" VALUES(23,65,'23:65','Ngày nay, chớ khóc lóc xin xỏ. Quả thật, các ngươi sẽ không được TA cứu giúp.');
INSERT INTO "hasan_abdulkarim" VALUES(23,66,'23:66','Quả thật, những Lời Mặc Khải của TA đã được đọc nhắc các ngươi, nhưng các ngươi đã quay gót bỏ đi.');
INSERT INTO "hasan_abdulkarim" VALUES(23,67,'23:67','Các ngươi ngạo nghễ xem thường Nó (Qur''an). Các ngươi thức thâu đêm tán gẫu chuyện không đâu.');
INSERT INTO "hasan_abdulkarim" VALUES(23,68,'23:68','Thế, chúng không ngẫm nghĩ về Lời Phán hay sao? Hoặc đã xảy đến cho chúng đỉều đã không xảy đến cho cha mẹ của chúng hay sao?');
INSERT INTO "hasan_abdulkarim" VALUES(23,69,'23:69','Hay là chúng không công nhận vị Sứ Giả (Muhammad) của chúng nên chúng từ chối Y?');
INSERT INTO "hasan_abdulkarim" VALUES(23,70,'23:70','Hay là chúng nói về Y như sau: “Y bị quỷ nhập.” Nhất định không! Y mang Chân Lý đến cho chúng nhưng đa số bọn chúng ghét sự thật.');
INSERT INTO "hasan_abdulkarim" VALUES(23,71,'23:71','Và nếu Chân Lý a tòng với những điều ham muốn của chúng thì các tầng trời và trái đất và vạn vật giữa trời đất chắc chắn sẽ đi đến chỗ sụp đổ. Không! TA ban cho chúng Thông Điệp (Nhắc nhở) nhưng chúng quay lánh xa điều Nhắc nhở của chúng.');
INSERT INTO "hasan_abdulkarim" VALUES(23,72,'23:72','Hay phải chăng Ngươi (hỡi Muhammad!) đã đòi chúng tiền cấp dưỡng? Nhưng sự cấp dưỡng của Thượng Đế của Ngươi là tốt nhất. Và Ngài là Đấng Cung Dưỡng Ưu Việt.');
INSERT INTO "hasan_abdulkarim" VALUES(23,73,'23:73','Và quả thật, Ngươi đã kêu gọi chúng đến với Chính Đạo.');
INSERT INTO "hasan_abdulkarim" VALUES(23,74,'23:74','Và quả thật, những ai không tin tưởng nơi đời sau chắc chắn sẽ lạc xa Chính Đạo.');
INSERT INTO "hasan_abdulkarim" VALUES(23,75,'23:75','Và nếu TA có khoan dung và làm cho chúng hết khổ sở thì chắc chắn chúng cũng lại tiếp tục vượt quá mức giới hạn, và lang thang vơ vẩn.');
INSERT INTO "hasan_abdulkarim" VALUES(23,76,'23:76','Và quả thật, TA đã bắt phạt chúng, nhưng chúng không hạ mình khiêm tốn trước Thượng Đế của chúng và cũng không chịu cầu khẩn van xin.');
INSERT INTO "hasan_abdulkarim" VALUES(23,77,'23:77','Cho đến khi TA mở cửa (hỏa ngục để) trừng phạt chúng nghiêm khắc thì chúng mới tuyệt vọng trong đó.');
INSERT INTO "hasan_abdulkarim" VALUES(23,78,'23:78','Và Ngài là Đấng đã tạo cho các ngươi hhích giác, thị giác và trái tim. Các ngươi đáp ân Ngài ít thay!');
INSERT INTO "hasan_abdulkarim" VALUES(23,79,'23:79','Và Ngài là Đấng gia tăng (nhân số của) các ngươi đông đảo trên mặt đất và các ngươi sẽ được tập trung đưa về trình diện Ngài trở lại (vào Ngày phục sinh).');
INSERT INTO "hasan_abdulkarim" VALUES(23,80,'23:80','Và Ngài là Đấng làm cho sống và gây cho chết và Ngài làm cho ban đêm và ban ngày nối đuôi nhau luân chuyển. Thế các ngươi không hiểu hay sao?');
INSERT INTO "hasan_abdulkarim" VALUES(23,81,'23:81','Không! Chúng nói ra điều giống như điều mà những người đời xưa đã nói.');
INSERT INTO "hasan_abdulkarim" VALUES(23,82,'23:82','Chúng nói: "Phải chăng khi chúng tôi chết và thành cát bụi và xương tàn, chắc chắn chúng tôi sẽ được dựng sống lại hay sao?"');
INSERT INTO "hasan_abdulkarim" VALUES(23,83,'23:83','“Quả thật, chúng tôi và cha mẹ của chúng tôi cũng đã được hứa điều (giống như) điều này trước đây. Nhưng (chúng tôi nghĩ) đó chỉ là chuyện ngụ ngôn của người cổ xưa mà thôi!"');
INSERT INTO "hasan_abdulkarim" VALUES(23,84,'23:84','(Hỡi Muhammad!) hãy bảo chúng: "Trái đất và những ai sống nơi đó thuộc về ai, nếu các người biết?”');
INSERT INTO "hasan_abdulkarim" VALUES(23,85,'23:85','Chúng sẽ đáp: “Của Allah!” Hãy bảo chúng: “Thế các người không lưu ý hay sao?”');
INSERT INTO "hasan_abdulkarim" VALUES(23,86,'23:86','Hãy bảo chúng: “Ai là Thượng Đế (Chủ Nhân) của bảy tầng trời và là Thượng Đế của Chiếc Ngai Vương Chí Đại?”');
INSERT INTO "hasan_abdulkarim" VALUES(23,87,'23:87','Chắc chắn chúng sẽ đáp: “Allah!” Hãy bảo chúng: “Thế các người không sợ Ngài hay sao?”');
INSERT INTO "hasan_abdulkarim" VALUES(23,88,'23:88','Hãy hỏi chúng: “Ai là Đấng nắm quyền thống trị mọi vật trong Tay Ngài và là Đấng bảo vệ (tất cả)? Nhưng (chúng) không được ai bảo vệ thoát khỏi (hình phạt cỏa) Ngài nếu các người biết.”');
INSERT INTO "hasan_abdulkarim" VALUES(23,89,'23:89','Chắc chắn chúng sẽ đáp: “Allah!” Hãy bảo chúng: “Thế tại sao các người còn mê muội?”');
INSERT INTO "hasan_abdulkarim" VALUES(23,90,'23:90','Không! TA đã ban cho chúng Chân Lý nhưng chúng mới thực sự là những kẻ nói dối.');
INSERT INTO "hasan_abdulkarim" VALUES(23,91,'23:91','Allah không nhận ai làm con trai của Ngài và cũng không có một thần linh nào ở cùng với Ngài bởi lẽ mỗi vị thần sẽ lấy mất đi vật mà vị ấy đã tạo và vị này sẽ cai trị vị kia. Quang vinh thay Allah! Ngài hoàn toàn vô can về những điều chúng đã qui cho Ngài.');
INSERT INTO "hasan_abdulkarim" VALUES(23,92,'23:92','Đấng Toàn Tri biết điều kín đáo và điều công khai. Bởi thế, Ngài Tối Cao, vượt hẳn những kẻ mà chúng đã tổ hợp với Ngài.');
INSERT INTO "hasan_abdulkarim" VALUES(23,93,'23:93','(Hỡi Muhammad!) hãy thưa: “Lạy Thượng Đế của bề tôi! Nếu Ngài muốn cho bề tôi thấy (hình phạt) đã được hứa với chúng,');
INSERT INTO "hasan_abdulkarim" VALUES(23,94,'23:94','thì Lạy Thượng Đế của bề tôi! Xin chớ để cho bề tôi ở cùng với đám người làm điều sai quấy này.”');
INSERT INTO "hasan_abdulkarim" VALUES(23,95,'23:95','Và chắc chắn TA thừa sức làm cho Ngươi thấy điều mà TA đã dọa phạt chúng.');
INSERT INTO "hasan_abdulkarim" VALUES(23,96,'23:96','Ngươi hãy lấy điều lành mà xóa tan điều dữ. TA biết rõ điều chúng đã thốt ra.');
INSERT INTO "hasan_abdulkarim" VALUES(23,97,'23:97','Và hãy thưa: "Lạy Thượng Đế của bề tôi! Xin Ngài che chở bề tôi tránh khỏi lời xúi giục của những tên Shaytan."');
INSERT INTO "hasan_abdulkarim" VALUES(23,98,'23:98','“Và Lạy Thượng Đế của bề tôi!, Xin Ngài che chở bề tôi sợ rằng chúng sẽ đến gần bề tôi.”');
INSERT INTO "hasan_abdulkarim" VALUES(23,99,'23:99','Cho đến khi một người trong bọn chúng đối diện với cái chết thì y sẽ lạy lục, thưa: “Lạy Thượng Đế xin Ngài trả bề tôi về (trần thế),');
INSERT INTO "hasan_abdulkarim" VALUES(23,100,'23:100','Để bề tôi có thể làm việc thiện mà bề tôi đã bỏ qua!” Không bao giờ! Đấy chỉ là lời nói mà y đã thốt ra. Và trước mặt chúng là một bức màn vô hình ngăn cách chúng với trần thế cho đến Ngày chúng sẽ được phục sinh trở lại.');
INSERT INTO "hasan_abdulkarim" VALUES(23,101,'23:101','Rồi, khi Tiếng còi (Tận thế) được thổi lên, vào Ngày đó sẽ không có tình máu mủ ruột thịt giữa bọn chúng và cũng sẽ không có ai hỏi thăm ai cả.');
INSERT INTO "hasan_abdulkarim" VALUES(23,102,'23:102','(Hoàn cảnh Hôm đó chỉ có việc:) ai mà bàn cân (chứa việc thiện) của y nặng thì sẽ là những người thành đạt;');
INSERT INTO "hasan_abdulkarim" VALUES(23,103,'23:103','Còn ai mà bàn cân (chứa việc thiện) của y nhẹ thì sẽ là những kẻ đã làm thiệt hại bản thân mình. Chúng sẽ ở trong hỏa ngục.');
INSERT INTO "hasan_abdulkarim" VALUES(23,104,'23:104','Lửa (của hỏa ngục) sẽ thiêu đốt bộ mặt của chúng và trong đó chúng sẽ nhe răng như cười.');
INSERT INTO "hasan_abdulkarim" VALUES(23,105,'23:105','Há những Lời Mặc Khải của TA đã không được đọc nhắc các ngươi rồi các ngươi cho là giả dối đó sao?');
INSERT INTO "hasan_abdulkarim" VALUES(23,106,'23:106','Chúng sẽ thưa: “Lạy Thượng Đế của bầy tôi! Bầy tôi quá đỗi cùng cực cho nên đã trở thành một đám người lầm lạc.”');
INSERT INTO "hasan_abdulkarim" VALUES(23,107,'23:107','(Chúng thưa): “Lạy Thượng Đế của bầy tôi! Xin Ngài đưa bầy tôi ra khỏi (hỏa ngục) này. Sau này nếu bầy tôi tái phạm thì quả thật bầy tôi sẽ là những kẻ làm điều sai quấy.”');
INSERT INTO "hasan_abdulkarim" VALUES(23,108,'23:108','(Allah sẽ) phán: “Hảy cút vào trong đó và chớ thưa bẩm gì với TA!”');
INSERT INTO "hasan_abdulkarim" VALUES(23,109,'23:109','Quả thật, trong số bề tôi của TA đã có một phần tử cầu nguyện thưa: “Lạy Thượng Đế của bầy tôi! Bầy tôi tin tưởng. Xin Ngài tha thứ và khoan dung bầy tôi bởi vì Ngài là Đấng Rất Mực Khoan Dung.”');
INSERT INTO "hasan_abdulkarim" VALUES(23,110,'23:110','Nhưng các ngươi đã chế giễu họ đến nỗi việc nhạo báng đó đã làm cho các ngươi quên mất Thông Điệp Nhắc Nhở của TA trong lúc các ngươi mải mê nhạo báng họ.');
INSERT INTO "hasan_abdulkarim" VALUES(23,111,'23:111','(Bởi thế) Ngày này TA ân thưởng họ về việc họ đã kiên nhẫn chịu đựng (với các ngươi). Quả thật, họ là những người thành tựu.');
INSERT INTO "hasan_abdulkarim" VALUES(23,112,'23:112','(Allah sẽ) phán: “Các ngươi đã ở trên trái đất bao nhiêu năm?”');
INSERT INTO "hasan_abdulkarim" VALUES(23,113,'23:113','(Chúng sẽ) thưa: “Bầy tôi ở lại một ngày hay nửa ngày gì đó. Xin hỏi những vị ghi sổ.”');
INSERT INTO "hasan_abdulkarim" VALUES(23,114,'23:114','(Allah) sẽ phán: "Các ngươi ở lại đó chỉ một khoảnh khắc. Phải chi các ngươi biết điều đó (lúc còn sống?)"');
INSERT INTO "hasan_abdulkarim" VALUES(23,115,'23:115','“Phải chăng các ngươi đã nghĩ rằng TA đã tạo hóa các ngươi để giải trí và rằng các ngươi sẽ không trở lại gặp TA?”');
INSERT INTO "hasan_abdulkarim" VALUES(23,116,'23:116','Bởi thế, Tối Cao thay Allah, Đức Vua Thật Sự! Không có Thượng Đế nào khác duy chỉ Ngài, Thượng Đế của Ngai Vương Vinh Dự!');
INSERT INTO "hasan_abdulkarim" VALUES(23,117,'23:117','Và ai cầu nguyện một thần linh khác cùng với Allah mà không có một bằng chứng nào về việc (thờ phụng) đó thì việc thanh toán y chỉ nằm nơi Thượng Đế (Allah) của y. Quả thật, những kẻ không có đức tin sẽ không thành đạt.');
INSERT INTO "hasan_abdulkarim" VALUES(23,118,'23:118','Và (Hỡi Muhammad!) hãy cầu nguyện, thưa: “Lạy Thượng Đế của bề tôi! Xin Ngài tha thứ và khoan dung bởi vì Ngài là Đấng Khoan Dung Ưu Việt.');
INSERT INTO "hasan_abdulkarim" VALUES(24,1,'24:1','(Đây là) một Chương mà TA ban xuống và qui định thành luật; và trong đó có những câu TA ban xuống được trình bày rõ ràng hầu các ngươi có thể ghi nhớ.');
INSERT INTO "hasan_abdulkarim" VALUES(24,2,'24:2','Về người phụ nữ và ngươi đàn ông phạm tội gian dâm, hãy đánh cả hai một trăm roi và chớ động lòng thương hại họ trong việc chấp hành Lệnh phạt của Allah nếu các ngươi có đức tin nơi Allah và Ngày Cuối Cùng. Và hãy mời một số người có đức tin đến chứng kiến việc trừng phạt.');
INSERT INTO "hasan_abdulkarim" VALUES(24,3,'24:3','Người đàn ông phạm tội gian dâm không được kết hôn với ai khác ngoài người đàn bà phạm tội thông dâm hoặc người đàn bà thờ đa thần. Và người đàn bà phạm tội gian dâm không được kết hôn với ai khác ngoài người đàn ông phạm tội thông dâm hoặc người đàn ông thờ đa thần. Và điều đó bị cấm đối với những người có đức tin.');
INSERT INTO "hasan_abdulkarim" VALUES(24,4,'24:4','Và (đối với) những ai buộc tội những người phụ nữ trinh thục nhưng không đưa ra đủ bốn người làm chứng, hãy đánh chúng tám mươi roi và sau đó chớ bao giờ nhận bằng chứng từ chúng nữa bởi vì chúng là những người nói dối, bất tuân Allah.');
INSERT INTO "hasan_abdulkarim" VALUES(24,5,'24:5','Ngoại trừ những ai sau đó biết hối cải và sửa mình thì quả thật, Allah Hằng Tha Thứ, Rất Mực Khoan Dung.');
INSERT INTO "hasan_abdulkarim" VALUES(24,6,'24:6','Và đối với những ai buộc tội vợ (ngoại tình) nhưng ngoài họ ra, không có ai làm chứng thì để cho mỗi người của họ thề bốn lần nhân danh Allah rằng mình là một người nói thật.');
INSERT INTO "hasan_abdulkarim" VALUES(24,7,'24:7','Và (trong lời thề) lần thứ năm, để y xin Allah nguyền rủa y nếu y là một người nói dối.');
INSERT INTO "hasan_abdulkarim" VALUES(24,8,'24:8','Và bà (vợ) sẽ khỏi bị phạt (ném đá cho chết) nếu bà ta thề xác nhận bốn lần nhân danh Allah rằng ông ta là một người nói dối.');
INSERT INTO "hasan_abdulkarim" VALUES(24,9,'24:9','Và (trong lời thề) lần thứ năm, để bà xin Allah giáng sự Giận Dữ (của Ngài) lên bà nếu ông (chồng của bà) là một người nói thật.');
INSERT INTO "hasan_abdulkarim" VALUES(24,10,'24:10','Và nếu Allah không gia ân và khoan hồng cho các ngươi (thì các ngươi đã bị Ngài phạt mất rồi) nhưng quả thật, Allah Rất Mực Độ Lượng và Vô cùng Sáng Suốt.');
INSERT INTO "hasan_abdulkarim" VALUES(24,11,'24:11','Quả thật những kẻ vu khống (''A-ishah, người vợ của Nabi Muhammad) chỉ gồm một phần tử nhỏ trong các ngươi. Chớ nghĩ rằng biến cố đó là một điều xấu cho các ngươi. Không, đó là một điều tốt cho các ngươi. Mỗi người trong bọn chúng sẽ chịu hình phạt về tội mà y đã gây ra, nhưng riêng kẻ chủ mưu thì sẽ chịu một hình phạt lớn hơn.');
INSERT INTO "hasan_abdulkarim" VALUES(24,12,'24:12','Tại sao những người có đức tin, nam và nữ, khi nghe lời (vu khống) đó lại không nghĩ tốt cho tín hữu của mình và bảo (đám người vu khống): “Đây rõ ràng là một lời vu khống!”');
INSERT INTO "hasan_abdulkarim" VALUES(24,13,'24:13','Tại sao chúng không đưa ra bốn nhân chứng cho việc đó? Bởi vì khi chứng không đưa ra đủ số nhân chứng thì đối với Allah, chúng là những tên nói dối.');
INSERT INTO "hasan_abdulkarim" VALUES(24,14,'24:14','Và nếu Allah không gia ân và khoan dung cho các ngươi ở đời này và đời sau thì chắc chắn các ngươi sẽ phải chịu một sự trừng phạt lớn lao vì tội các ngươi đã nhanh nhẩu (loan tin đồn thất thiệt).');
INSERT INTO "hasan_abdulkarim" VALUES(24,15,'24:15','Khi các ngươi phổ biến nó với lời lẽ của các ngươi và nói ra từ miệng của các ngươi điều mà các ngươi không có một chút hiểu biết nào và các ngươi nghĩ rằng đó là một chuyện cỏn con (không đáng giá) thì ngược lại, đối với Allah đó là một điều hết sức trọng đại.');
INSERT INTO "hasan_abdulkarim" VALUES(24,16,'24:16','Và tại sao khi nghe nó (tin đồn) các ngươi không đáp lại: “Không phải việc của chúng tôi đi bàn tán chuyện này. Quang Vinh và trong sạch thay Allah! Ngài vô can (về việc này)! Đây là một lời vu khống hết sức trọng đại (của chúng).”');
INSERT INTO "hasan_abdulkarim" VALUES(24,17,'24:17','Allah cảnh cáo các ngươi chớ bao giờ tái phạm điều tương tự nếu các ngươi là những người có đức tin thật sự.');
INSERT INTO "hasan_abdulkarim" VALUES(24,18,'24:18','Và Allah trình bày rõ cho các ngươi những Lời Mặc Khải của Ngài. Bởi vì Allah là Đấng Toàn Tri, Đấng Sáng Suốt.');
INSERT INTO "hasan_abdulkarim" VALUES(24,19,'24:19','Và những ai trong hàng ngũ của những người có đức tin thích cho lời gièm pha này được phổ biến rộng rãi thì sẽ chịu một sự trừng phạt đau đớn ở đời này lẫn đời sau bởi vì Allah biết rõ (sự thật) trong lúc các ngươi không biết.');
INSERT INTO "hasan_abdulkarim" VALUES(24,20,'24:20','Và nếu Allah không gia ân và khoan dung cho các ngươi (thì các ngươi đã bị Ngài phạt mất rồi) nhưng quả thật, Allah Rất Mực Nhân từ, Rất Mực Khoan Dung.');
INSERT INTO "hasan_abdulkarim" VALUES(24,21,'24:21','Hỡi những ai có đức tin! Chớ dẫm theo dấu chân của Shaytan bởi vì ai bước theo dấu chân của Shaytan thì sẽ bị nó sai khiến làm điều khả ố và điều tội lỗi. Và nếu Allah không gia ân và khoan dung cho các ngươi thì trong tương lai không một ai trong các ngươi sẽ được tẩy sạch (tội lỗi). Tuy nhiên, Allah sẽ tẩy sạch người nào Ngài muốn bởi vì Allah là Đấng Hằng Nghe và Hằng Biết (hết mọi việc).');
INSERT INTO "hasan_abdulkarim" VALUES(24,22,'24:22','Và những người giàu có và dồi dào phương tiện trong các ngươi chớ nên thề dứt khoát rằng họ sẽ không bố thí cho bà con gần của họ(, cho những người túng thiếu, và cho những người di cư vì Chính Nghĩa của Allah (đã liên can trong vụ phao tin đồn thất thiệt). Ngược lại, họ nên độ lượng và bỏ qua (việc đó). Phải chăng các ngươi không thích việc Allah tha thứ cho các ngươi? Bởi vì quả thật, Allah là Đấng Hằng Tha Thứ, Rất Mực Khoan Dung.');
INSERT INTO "hasan_abdulkarim" VALUES(24,23,'24:23','Quả thật, những ai vu khống những người đàn bà trinh thục, ngây thơ và thiếu dè dặt, nhưng có đức tin thì sẽ bị nguyền rủa ở đời này và đời sau. Và chúng sẽ nhận một sự trừng phạt to lớn.');
INSERT INTO "hasan_abdulkarim" VALUES(24,24,'24:24','Vào Ngày mà chiếc lưỡi cũng như bàn tay và bàn chân của chúng sẽ làm chứng chống lại chúng về những tội lỗi mà chúng đã phạm.');
INSERT INTO "hasan_abdulkarim" VALUES(24,25,'24:25','Vào Ngày đó, Allah sẽ báo đền đầy đủ về hành vi của chúng và chúng sẽ biết Allah là Chân Lý hiển hiện.');
INSERT INTO "hasan_abdulkarim" VALUES(24,26,'24:26','Gian phụ xứng đôi với gian phu và gian phu xứng đôi với gian phụ; và phụ nữ đoan chính xứng đôi với đàn ông đứng đắn và đàn ông đứng đắn xứng đôi với phụ nữ đoan chính. Những người này vô tội về những điều vu khống mà chúng (những kẻ vu khống) đã nói ra. Họ sẽ được tha thứ và được ban cho bổng lộc dồi dào (nơi thiên đàng).');
INSERT INTO "hasan_abdulkarim" VALUES(24,27,'24:27','Hỡi những ai có đức tin! Chớ bước vào nhà của ai khác ngoài nhà của mình trừ phi các ngươi đã xin phép chủ nhà (mà các ngươi muốn vào) và hãy chào Salam cho người trong nhà. Điều đó tốt cho các ngươi để may ra các ngươi ghi nhớ (phép lịch sự).');
INSERT INTO "hasan_abdulkarim" VALUES(24,28,'24:28','Nhưng nếu không thấy ai trong nhà thì chớ bước vào và (đợi) cho đến khi các ngươi được cho phép. Và nếu được yêu cầu ‘Hãy lui ra’ thì hãy trở lui. Điều đó trong sạch cho các ngươi hơn bởi vì Allah biết rõ những điều các ngươi làm.');
INSERT INTO "hasan_abdulkarim" VALUES(24,29,'24:29','Các ngươi không có tội nếu bước vào những ngôi nhà không có người ở bên trong nhưng có các món (vật dụng) cho các ngươi dùng, bởi vì Allah biết rõ điếu các ngươi tiết lộ và điều các người giấu giếm.');
INSERT INTO "hasan_abdulkarim" VALUES(24,30,'24:30','Hãy bảo những người đàn ông có đức tin nên hạ cái nhìn (của họ) xuống và che phủ phần kín đáo (của cơ thể) của họ. Điều đó tốt cho họ hơn bởi vì Allah là Đấng Am Tường những điều họ làm.');
INSERT INTO "hasan_abdulkarim" VALUES(24,31,'24:31','Hãy bảo những người phụ nữ có đức tin nên hạ thấp cái nhìn (của họ) xuống và che phủ phần kín đáo (của cơ thể) của họ và chớ phô bày nhan sắc ra ngoài ngoại trừ bộ phận nào lộ ra ngoài tự nhiên (như hai bàn tay, gương mặt); và họ nên kéo khăn choàng phủ lên ngực; và chớ phô bày nhan sắc ngoại trừ đối với chồng, hoặc đối với cha ruột, hoặc cha chồng, hoặc con trai ruột, hoặc con trai của chồng, hoặc con trai của anh em ruột, hoặc con trai của chị em ruột, hoặc những người đàn bà (giúp việc), hoặc những người nô lệ thuộc quyền sở hữu của họ, hoặc những người đàn ông phục dịch nhưng đã hết tình dục, hoặc những đứa bé chưa có cảm giác với các phần kín đáo của đàn bà; và (bảo họ) chớ nện (gót) chân mạnh (xuống đất) để cho người ta biết mình đang giấu nữ trang (trong người). Và hãy quay về sám hối với Allah, tất cả các ngươi, hỡi những người có đức tin, để may ra các ngươi được thành đạt.');
INSERT INTO "hasan_abdulkarim" VALUES(24,32,'24:32','Và hãy kết hôn những người độc thân trong các ngươi và những người đức hạnh trong số những người nam và nữ giúp việc. Nếu họ nghèo, Allah sẽ làm cho họ giàu với thiên lộc của Ngài bởi vì Allah Rộng Rãi Bao La, Hằng Biết (mọi việc).');
INSERT INTO "hasan_abdulkarim" VALUES(24,33,'24:33','Và đối với những ai không tìm ra phương tiện để kết hôn thì hãy kiềm chế (và giữ mình trong sạch) cho đến khi Allah sẽ làm cho họ giàu với thiên lộc của Ngài. Và những ai trong số những người (nô lệ) thuộc quyền sở hữu của các ngươi đòi hỏi một chứng thư (trả tự do) thì hãy viết (nó) xuống cho họ nếu các ngươi biết họ là người tốt và hãy ban cấp cho họ (tặng vật) từ tài sản mà Allah đã ban cho các ngươi. Và chớ vì mục đích kiếm được món lợi trần gian mà ép buộc những phụ nữ nô lệ của các ngươi hành nghề mãi dâm nếu họ muốn giữ tiết hạnh. Và ai đã lỡ ép họ làm điều đó thì sau việc cưỡng bách (họ hành nghề) thì (nên biết rằng) Allah tỏ lòng tha thứ và khoan dung (đối với nạn nhân của việc cưỡng bách).');
INSERT INTO "hasan_abdulkarim" VALUES(24,34,'24:34','Và quả thật, TA đã ban những Lời Mặc Khải xuống để giải thích rõ sự việc và đó là một thí dụ rút từ (câu chuyện của) những người đã qua đời trước các ngươi và là một lời cảnh cáo cho những người ngay chính.');
INSERT INTO "hasan_abdulkarim" VALUES(24,35,'24:35','Allah là Ánh Sáng của các tầng trời và trái đất. Ánh Sáng của Ngài có thể ví như một cái chụp (khuôn) mà bên trong là ngọn đèn. Ngọn đèn nằm trong một lồng kiến (kính); lồng kiến như một ngôi sao sáng lấp lánh. Ngọn đèn được thắp từ một loại cây được ban phúc, cây ô liu, không (mọc) ở hướng Đông cũng không (mọc) ở hướng Tây; dầu của nó rất gần, tỏa sáng mặc dầu chưa chạm phải lửa. Ánh sáng trên ánh sáng. Allah hướng dẫn đến Ánh Sáng của Ngài người nào Ngài muốn. Allah đưa ra những thí dụ so sánh cho nhân loại bởi vì Allah Hằng Biết mọi việc.');
INSERT INTO "hasan_abdulkarim" VALUES(24,36,'24:36','(Ánh Sáng của Allah được tỏa) trong các ngôi nhà (thánh đường) mà Allah cho phép được dựng lên, được giữ sạch và được tôn trọng và trong đó tên của Ngài được tụng niệm. Họ (những người được soi sáng) tán dương Ngài trong đó vào buổi sáng và buổi tối:');
INSERT INTO "hasan_abdulkarim" VALUES(24,37,'24:37','Những người mà việc mua bán đổi chác cũng như việc làm ăn kinh doanh không làm xao lãng việc tưởng nhớ Allah, xao lãng việc dâng lễ nguyện Salah và trả Zakah. Họ lo sợ Ngày mà trái tim và cặp mắt của họ sẽ đảo lộn (về việc Allah sẽ phán xét họ).');
INSERT INTO "hasan_abdulkarim" VALUES(24,38,'24:38','(Với hy vọng) Allah sẽ ân thưởng họ phần thưởng tốt nhất về những việc thiện mà họ đã làm và sẽ gia ân thêm cho họ bởi vì Allah cung dưỡng một cách vô kể người nào Ngài muốn với Thiên lộc của Ngài.');
INSERT INTO "hasan_abdulkarim" VALUES(24,39,'24:39','Ngược lại, những ai không có đức tin thì việc làm của họ chẳng khác nào ảo ảnh nơi bãi sa mạc mà một người chết khát cho là nước (uống), cho đến khi y đến nơi, y chẳng thấy gì cả mà chỉ thấy Allah với y; và Ngài trả đầy đủ phần trương mục của y cho y; bởi vì, Allah Rất Nhanh trong việc thanh toán.');
INSERT INTO "hasan_abdulkarim" VALUES(24,40,'24:40','Hoặc (tình trạng của y) giống như cái u tối dưới biển sâu không đáy. Y bị bao phủ bên trên bởi một lớp sóng, bên trên lớp sóng là một đám mây (đen), lớp u tối này chồng lên lớp u tối khác. Khi đưa tay ra, y không thấy nó được. Và ai mà Allah không ban cho ánh sáng thì không thể tiếp thu được ánh sáng.');
INSERT INTO "hasan_abdulkarim" VALUES(24,41,'24:41','Há Ngươi không nhận thấy việc mọi vật trong các tầng trời và trái đất đều tán dương Allah kể cả những con chim xòe đôi cánh của chúng ra hay sao? Mỗi vật đều biết cách dâng lễ nguyện Salah và cách tán dương của nó. Và Allah biết rõ những điều chúng làm.');
INSERT INTO "hasan_abdulkarim" VALUES(24,42,'24:42','Bởi vì Allah nắm quyền thống trị các tầng trời và trái đất. Và mọi vật đều phải trở về với Allah.');
INSERT INTO "hasan_abdulkarim" VALUES(24,43,'24:43','Há Ngươi không nhận thấy việc Allah di chuyển các luồng mây rồi kết hợp chúng lại thành một khối lơn; sau đó Ngươi thấy nước (mưa) từ giữa đó rơi xuống hay sao? Và Ngài ban từ bầu trời xuống (mây to như) những quả núi bên trong chứa mưa đá mà Ngài dùng để đánh kẻ nào Ngài muốn và đưa nó dang ra khỏi người nào Ngài muốn. Tia chớp của nó gần làm cho hoa mắt (mọi người).');
INSERT INTO "hasan_abdulkarim" VALUES(24,44,'24:44','Allah lật trở ban đêm và ban ngày nối tiếp theo nhau. Quả thật, trong sự việc đó là một bài học cho những người có cái nhìn thấu suốt.');
INSERT INTO "hasan_abdulkarim" VALUES(24,45,'24:45','Và Allah tạo mọi sinh vật từ nước. Trong số những sinh vật đó, có con trườn bằng bụng; và trong chúng, có con đi bằng hai chân; và trong chúng, có con đi bằng bốn chân. Allah tạo hóa vật gì Ngài muốn; bởi vì Allah là Đấng Toàn Năng trên tất cả mọi thứ.');
INSERT INTO "hasan_abdulkarim" VALUES(24,46,'24:46','Quả thật, TA đã ban những Lời Mặc Khải xuống để trình bày rõ sự việc. Và Allah hướng dẫn đến Chính Đạo người nào Ngài muốn.');
INSERT INTO "hasan_abdulkarim" VALUES(24,47,'24:47','Và chúng (những tên giả tạo đức tin) nói: “Chúng tôi tin tưởng nơi Allah và Sứ Giả (Muhammad) và chúng tôi vâng lời.” Nhưng sau đó, một phần tử của chúng quay bỏ đi. Và chúng là những kẻ không có đức tin.');
INSERT INTO "hasan_abdulkarim" VALUES(24,48,'24:48','Và khi chúng được gọi đến với Allah và Sứ Giả của Ngài để Y phân xử giữa bọn chúng thì một phần tử lánh mặt bỏ đi.');
INSERT INTO "hasan_abdulkarim" VALUES(24,49,'24:49','Nhưng nếu chúng nắm được phần phải thì chúng sẵn sàng đến gặp Sứ Giả (Muhammad) ngay.');
INSERT INTO "hasan_abdulkarim" VALUES(24,50,'24:50','Phải chăng trong trái tim của chúng có một căn bệnh? Hay là chúng nghi hoặc? Hay là chúng sợ Allah và Sứ Giả của Ngài xử ép chúng? Không, những người này là những kẻ làm điều sai quấy.');
INSERT INTO "hasan_abdulkarim" VALUES(24,51,'24:51','Lời tuyên bố của những người thật sự có đức tin khi được mời đến với Allah và Sứ Giả của Ngài để Y phân xử giữa họ, chỉ gồm có câu: “Chúng tôi nghe và chúng tôi vâng lời.” Những người này là những người sẽ thắng lợi.');
INSERT INTO "hasan_abdulkarim" VALUES(24,52,'24:52','Và ai vâng lời Allah và Sứ Giả của Ngài và sợ Allah và ngay chính với Ngài thì là những người sẽ thành đạt.');
INSERT INTO "hasan_abdulkarim" VALUES(24,53,'24:53','(Những kẻ giả dối) mang Allah ra thề bằng lời thề nghiêm trọng (nói) nếu Ngươi (Muhammad) ra lệnh cho chúng thì chắc chắn chúng sẽ ra đi (chiến đấu). Hãy bảo chúng: “Chớ thề thốt làm gì. Sự vâng lời đó đều được nhìn nhận (là dối trá).” Bởi vì quả thật, Allah Rất Am tường mọi điều các ngươi làm.');
INSERT INTO "hasan_abdulkarim" VALUES(24,54,'24:54','Hãy bảo chúng: "Hãy vâng lệnh Allah và vâng lệnh Sứ Giả (Muhammad). Nhưng nếu các ngươi quay bỏ đi thì (nên biết rằng) Y chỉ chịu trách nhiệm về việc làm của Y, còn các ngươi thì chịu trách nhiệm về việc làm của các ngươi. Và nếu các ngươi vâng lệnh Y thì sẽ được hướng dẫn đúng đường bởi vì nhiệm vụ của một Sứ Giả chỉ là công khai truyền đạt (Thông Điệp của Allah)."');
INSERT INTO "hasan_abdulkarim" VALUES(24,55,'24:55','Allah đã hứa với những ai có đức tin và làm việc thiện trong các người rằng chắc chắn Ngài sẽ làm cho họ trở thành những người nối nghiệp của Ngài trên trái đất giống như việc Ngài đã làm cho những ai trước họ trở thành những người nối nghiệp như thế và chắc chắn Ngài sẽ thiết lập cho họ tôn giáo của họ mà Ngài đã chấp thuận và chắc chắn Ngài sẽ đổi lại cho họ tình trạng an ninh và thanh bình sau cảnh phập phòng lo sợ của họ (với điều kiện) họ chỉ thờ phụng riêng TA (Allah) thôi, và không tổ hợp bất cứ cái gì với TA; và sau đó, ai không tin tưởng thì sẽ là những kẻ nổi loạn và bất tuân Allah.');
INSERT INTO "hasan_abdulkarim" VALUES(24,56,'24:56','Và hãy dâng lễ nguyện Salah và đóng Zakah và vâng lệnh Sứ Giả để may ra các ngươi được (Allah) khoan dung.');
INSERT INTO "hasan_abdulkarim" VALUES(24,57,'24:57','Chớ nghĩ rằng những kẻ không có đức tin sẽ thoát khỏi (Allah) trên mặt đất bởi vì chỗ ngụ của chúng sẽ là hỏa ngục, một nơi trở về rất xấu xa.');
INSERT INTO "hasan_abdulkarim" VALUES(24,58,'24:58','Hỡi những ai có đức tin! Hãy để cho những ai thuộc quyền sở hữu của các ngươi và những ai chưa đến tuổi dậy thì hỏi xin phép các ngươi (trước khi vào phòng riêng của các ngươi) vào ba lúc: vào lúc trước cuộc dâng lễ Salah buổi sáng, và vào lúc các ngươi cởi trần để (nằm nghỉ) buổi trưa và vào lúc sau cuộc dâng lễ buổi tối - đó là ba thời điểm mà các ngươi ở trần để nghỉ ngơi - Các ngươi, cũng như họ, không có tội ngoài ba lúc đó nếu các ngươi đi lại giao tế với nhau. Allah trình bày rõ những Lời Mặc Khải của Ngài cho các ngươi đúng như thế bởi vì Allah Toàn Tri, Rất Mực Sáng Suốt.');
INSERT INTO "hasan_abdulkarim" VALUES(24,59,'24:59','Và khi con cái của các ngươi đến tuổi dậy thì, hãy bắt chúng hỏi xin phép (trước khi vào phòng riêng của các ngươi) giống như những ai trước chúng đã hỏi xin phép. Allah trình bày rõ những Lời Mặc Khải của Ngài cho các ngươi đúng như thế bởi vì Allah Toàn Tri, Vô cùng Sáng suốt.');
INSERT INTO "hasan_abdulkarim" VALUES(24,60,'24:60','Và những người đàn bà quá tuổi sinh con và đã bỏ ý định lập gia đình sẽ không có tội nếu họ cởi lớp áo ngoài của họ ra miễn là không lả lơi phơi bày nhan sắc. Và nếu họ tự chế được thì sẽ tốt cho họ hơn. Bởi vì Allah Toàn Thính và Toàn Tri.');
INSERT INTO "hasan_abdulkarim" VALUES(24,61,'24:61','Người mù và người què và người mắc bệnh cũng như chính bản thân của các ngươi không có tội nếu các ngươi dùng bữa tại nhà của các ngươi, hoặc tại nhà của cha, hoặc của mẹ, hoặc của anh em ruột, hoặc của chị em ruột, hoặc nhà của chú bác, hoặc nhà của cô, hoặc nhà của cậu, hoặc nhà của dì, hoặc tại nhà mà các người giữ chìa khóa, hoặc tại nhà của một người bạn. Các ngươi không có tội nếu các ngươi ăn chung hoặc ăn riêng từng nhóm. Nhưng khi các ngươi bước vào nhà, hãy chào hỏi lẫn nhau bằng lời chào đầy phúc đức và tốt đẹp do Allah (chỉ dạy). Allah trình bày rõ những Lời Mặc Khải của Ngài cho các ngươi đúng như thế để may ra các ngươi hiểu được.');
INSERT INTO "hasan_abdulkarim" VALUES(24,62,'24:62','Chỉ được gọi là người có đức tin những ai tin nơi Allah và Sứ Giả của Ngài và khi ở cùng với Y để bàn công việc chung, họ không ngang nhiên bỏ đi trừ phi xin phép rời (buổi họp) bởi vì những ai hỏi xin phép Y thì đó là những người thực sự tin tưởng nơi Allah và Sứ Giả của Ngài. Bởi thế, nếu họ xin phép Ngươi để đi lo công việc (làm ăn) của họ thì hãy chấp thuận cho người nào của họ mà Ngươi muốn và hãy xin Allah tha thứ cho họ bởi vì quả thật, Allah Hằng Tha Thứ, Rất Mực Khoan Dung.');
INSERT INTO "hasan_abdulkarim" VALUES(24,63,'24:63','Chớ nghĩ việc Sứ Giả (Muhammad) gọi các ngươi giống như việc các ngươi gọi nhau. Allah biết rõ những ai trong các ngươi đã lén lút bỏ đi. Bởi thế, hãy cảnh cáo những ai chống đối mệnh lệnh của Y (Sứ Giả Muhammad) nên biết, nếu làm thế, chúng sẽ gặp phải hoạn nạn hay gặp phải một hình phạt đau đớn.');
INSERT INTO "hasan_abdulkarim" VALUES(24,64,'24:64','Chắc chắn, mọi vật trong các tầng trời và trái đất đều là (tạo vật) của Allah. Chắc chắn, Ngài biết điều mà các ngươi định làm; và vào Ngày mà họ sẽ được đưa về trình diện Ngài trở lại, Ngài sẽ báo cho họ biết điều mà họ đã từng làm bởi vì Allah Hằng Biết (mọi việc).');
INSERT INTO "hasan_abdulkarim" VALUES(25,1,'25:1','Phúc thay (Allah!) Đấng đã ban Tiêu chuẩn (phân biệt phúc và tội)( xuống cho người tôi trung (Muhammad) của Ngài để Y trở thành một vị Cảnh báo cho muôn loài (người và jinn).');
INSERT INTO "hasan_abdulkarim" VALUES(25,2,'25:2','Đấng nắm quyền thống trị các tầng trời và trái đất; Ngài đã không nhận (ai) làm con trai của Ngài và cũng không có một đối tác nào trong việc an vị của Ngài; Và Ngài đã tạo hóa tất cả mọi vật và đo lường (mỗi vật) theo đúng mức lượng của nó.');
INSERT INTO "hasan_abdulkarim" VALUES(25,3,'25:3','Nhưng ngoài Ngài, họ tôn thờ những thần linh không có khả năng tạo được vật gì trong lúc chính chúng lại được tạo ra và chúng không làm hại cũng không làm lợi gì được cho chính mình và cũng không có khả năng làm cho chết và làm cho sống và cũng không thể phục sinh được (người chết).');
INSERT INTO "hasan_abdulkarim" VALUES(25,4,'25:4','Và những ai không có đức tin nói: “Đây chẳng qua là một điều dối trá mà Y (Muhammad) đã bịa đặt và có một đám người khác đã giúp Y làm Nó (Qur''an). Nhưng quả thật chúng (mới là những kẻ) đã đưa ra lời sai quấy và điều giả dối.');
INSERT INTO "hasan_abdulkarim" VALUES(25,5,'25:5','Và chúng nói: "Toàn là chuyện cổ tích của người đời xưa mà Y đã cho viết lại và đã được đọc ra cho Y sáng và chiều."');
INSERT INTO "hasan_abdulkarim" VALUES(25,6,'25:6','Hãy bảo chúng: “Nó (Qur''an) do Đấng Hằng Biết điều bí mật trong các tầng trời và trái đất ban xuống. Quả thật, Ngài là Đấng Hằng Tha Thứ, Rất Mực Khoan Dung.');
INSERT INTO "hasan_abdulkarim" VALUES(25,7,'25:7','Và chúng nói: "Sứ Giả gì như thế này? Y ăn thức ăn và đi rong giữa chợ! Tại sao không có một Thiên Thần được cử xuống với Y để làm một vị cảnh cáo cùng với Y?"');
INSERT INTO "hasan_abdulkarim" VALUES(25,8,'25:8','“Hoặc tại sao (Allah) không ban cho Y một kho tàng hoặc tại sao Y không làm chủ một ngôi vườn mà Y sẽ ăn (trái) của nó?” Và những kẻ làm điều sai quấy nói: “Các ông chỉ nghe theo một người bị mê hoặc.”');
INSERT INTO "hasan_abdulkarim" VALUES(25,9,'25:9','Thấy không! Chúng đã đưa ra một thí dụ so sánh cho Ngươi như thế. Bởi vậy chúng đã lạc đường. Rồi chúng sẽ không thể tìm thấy Chính Đạo.');
INSERT INTO "hasan_abdulkarim" VALUES(25,10,'25:10','Phúc thay (Allah!) Nếu Ngài muốn, Ngài sẽ làm ra những thứ còn tốt hơn những thứ đó cho Ngươi: những ngôi vườn bên dưới có các dòng sông chảy và các biệt thự cho Ngươi.');
INSERT INTO "hasan_abdulkarim" VALUES(25,11,'25:11','Không! Chúng phủ nhận Giờ Tận Thế và TA đã chuẩn bị một Lửa ngọn cho những ai phủ nhận Giờ Tận Thế.');
INSERT INTO "hasan_abdulkarim" VALUES(25,12,'25:12','Khi nó (Lửa) thấy chúng từ xa, chúng sẽ nghe nó gào thét gầm gừ;');
INSERT INTO "hasan_abdulkarim" VALUES(25,13,'25:13','Và khi bị xích và bị ném vào một xó chật hẹp của nó (hỏa ngục), chúng sẽ kêu gào cho được chết phứt trong đó;');
INSERT INTO "hasan_abdulkarim" VALUES(25,14,'25:14','Ngày nay, chớ kêu la cho được chết phứt mà hãy kêu gào cho được chết nhiều lần.');
INSERT INTO "hasan_abdulkarim" VALUES(25,15,'25:15','Hãy bảo chúng: “Phải chăng (hỏa ngục) tốt hay thiên đàng Vĩnh Cửu được hứa ban cấp cho những người ngay chính sợ Allah tốt hơn? thiên đàng là phần thưởng và là nơi đến cuối cùng của họ.');
INSERT INTO "hasan_abdulkarim" VALUES(25,16,'25:16','Trong đó, họ sẽ được mọi thứ mà họ mong ước và sẽ ở vĩnh viễn. Đó là một lời hứa mà Thượng Đế của Ngươi bắt buộc phải thi hành.');
INSERT INTO "hasan_abdulkarim" VALUES(25,17,'25:17','Vào một Ngày mà Ngài (Allah) sẽ tập trung chúng với những kẻ mà chúng thờ phụng ngoài Allah, rồi Ngài sẽ hỏi: “Phải chăng các ngươi đã dắt các bầy tôi này của TA đi lạc hay tự chúng đi lạc?”');
INSERT INTO "hasan_abdulkarim" VALUES(25,18,'25:18','Chúng sẽ thưa: “Quang vinh và trong sạch thay Ngài! Bảo chúng tôi đã tôn thờ các thần phù hộ khác thay vì Ngài thì là một điều không đúng đối với chúng tôi. Nhưng Ngài đã ban cho chúng và cha mẹ của chúng mọi thứ tiện nghi (trên trần thế) đến đỗi chúng quên mất Lời cảnh cáo (của Ngài) và đã trở thành đám người suy đồi.”');
INSERT INTO "hasan_abdulkarim" VALUES(25,19,'25:19','“Bây giờ chúng (thần linh) xác nhận các ngươi đã nói dối về những điều mà các ngươi đã từng nói; bởi thế các ngươi không thể gỡ gạc cũng không được giúp đỡ.” Và ai trong các ngươi làm điều sai quấy thì TA sẽ cho y nếm một sự trừng phạt lớn lao.');
INSERT INTO "hasan_abdulkarim" VALUES(25,20,'25:20','Và không một Sứ Giả nào trong số các Sứ Giả mà TA đã cử đến trước Ngươi (hỡi Muhammad!) lại không ăn thức ăn và không đi rong giữa chợ; và TA đã dùng một số người này để thử thách một số người kia hầu xem các ngươi có kiên nhẫn hay không? Và Thượng Đế (Allah) của Ngươi là Đấng Hằng Thấy (mọi việc).');
INSERT INTO "hasan_abdulkarim" VALUES(25,21,'25:21','Và những ai không mong gặp lại TA (ở đời sau) lên tiếng: "Tại sao không có những Thiên Thần được cử xuống cho bọn ta hoặc tại sao bọn ta không thấy Thượng Đế (Đấng Chủ Tể) cua bọn ta?" Chắc chắn, chúng đã tỏ lòng tự cao và tự phụ quá mức.');
INSERT INTO "hasan_abdulkarim" VALUES(25,22,'25:22','Vào Ngày mà chúng sẽ thấy Thiên Thần, ngày đó những kẻ tội lỗi sẽ không được một tin vui nào cả và (Thiên Thần) sẽ bảo (chúng): “Dang ra! Hãy dang ra xa!”(');
INSERT INTO "hasan_abdulkarim" VALUES(25,23,'25:23','Và TA sẽ xét lại công trình nào mà chúng đã làm ra và sẽ làm cho nó thành tro bay tứ tán.');
INSERT INTO "hasan_abdulkarim" VALUES(25,24,'25:24','Những cư dân của thiên đàng vào Ngày đó sẽ có một nơi ngụ tốt và một chỗ nghỉ tốt đẹp.');
INSERT INTO "hasan_abdulkarim" VALUES(25,25,'25:25','Và vào một Ngày mà bầu trời sẽ chẻ đôi với những áng mây và các Thiên Thần sẽ đườc cử xuống rầm rộ.');
INSERT INTO "hasan_abdulkarim" VALUES(25,26,'25:26','Quyền thống trị vào Ngày đó đúng là của Đấng Rất Mực Độ Lượng và là một Ngày thật gian nan cho những kẻ không có đức tin.');
INSERT INTO "hasan_abdulkarim" VALUES(25,27,'25:27','Và vào Ngày mà tên làm điều sai quấy sẽ vừa cắn đầu ngón tay vừa than thở: “Ôi khổ cho thân tôi! Phải chi tôi theo con đường cùng với Sứ Giả (Muhammad thì đâu đến nỗi này!)"');
INSERT INTO "hasan_abdulkarim" VALUES(25,28,'25:28','“Ôi khổ cho thân tôi! Phải chi tôi đừng làm bạn với tên đó!"');
INSERT INTO "hasan_abdulkarim" VALUES(25,29,'25:29','“Chắc chắn, nó đã dắt tôi lạc xa Thông điệp Nhắc nhở (Qur''an) sau khi tôi đã tiếp thu Nó. Quả thật, Shaytan là một tên phản bội loài người (vào lúc nguy khốn).”');
INSERT INTO "hasan_abdulkarim" VALUES(25,30,'25:30','Và Sứ Giả (Muhammad) thưa: “Lạy Thượng Đế của bề tôi! Quả thật, người dân của bề tôi xao lãng Qur''an này!”');
INSERT INTO "hasan_abdulkarim" VALUES(25,31,'25:31','Và TA đã dựng trong số những kẻ tội lỗi một kẻ thù chống đối mỗi vị Nabi. Nhưng Thượng Đế của Ngươi đủ làm một Vị Hướng Dẫn và một Vị Cứu Tinh (cho Ngươi).');
INSERT INTO "hasan_abdulkarim" VALUES(25,32,'25:32','Và những kẻ không có đức tin lên tiếng: "Tại sao trọn bộ Qur''an không được mặc khải cho Y một lần một?" (TA mặc khải Nó) đúng như thế là vì TA muốn dùng Nó để củng cố tấm lòng của Ngươi; và TA đọc Nó dần dần theo từng giai đoạn.(');
INSERT INTO "hasan_abdulkarim" VALUES(25,33,'25:33','Và không một thí dụ (so sánh) nào do chúng mang đến hỏi Ngươi mà TA lại không mang sự thật và lời giải thích tốt đẹp nhất đến cho Ngươi (để đối đáp với chúng).');
INSERT INTO "hasan_abdulkarim" VALUES(25,34,'25:34','Những ai sẽ bị tập trung úp mặt xuống để đưa vào hỏa ngục thì sẽ rất bất hạnh về chỗ ở và lạc rất xa khỏi Chính Đạo.');
INSERT INTO "hasan_abdulkarim" VALUES(25,35,'25:35','Và quả thật, TA đã ban Kinh Sách cho Musa và đã chỉ định người em của Y, Harun, làm một phụ tá cho Y.');
INSERT INTO "hasan_abdulkarim" VALUES(25,36,'25:36','Rồi TA phán: “Hai ngươi hãy ra đi gặp đám người đã phủ nhận những Phép Lạ của TA.” Bởi thế, TA đã tiêu diệt chúng toàn bộ.');
INSERT INTO "hasan_abdulkarim" VALUES(25,37,'25:37','Và người dân của Nuh cũng thế. Khi chúng cho những Sứ Giả nói dối, TA đã nhận chúng chết chìm dưới nước và đã dùng chúng làm một bài học cảnh cáo cho nhân loại. Và TA đã chuẩn bị cho những kẻ làm điều sai quấy một sự trừng phạt đau đớn.');
INSERT INTO "hasan_abdulkarim" VALUES(25,38,'25:38','Và (người dân) của ''Ad và của Thamud và Dân cư của Rass( và nhiều thế hệ giữa chúng (cũng đã chịu số phận tương tự).');
INSERT INTO "hasan_abdulkarim" VALUES(25,39,'25:39','Và TA đã trình bày cho mỗi thế hệ những hình ảnh thí dụ và đã tiêu diệt từng thế hệ một cách khủng khiếp (vì tội lỗi của chúng).');
INSERT INTO "hasan_abdulkarim" VALUES(25,40,'25:40','Và chắc chắn, chúng (những người Quraish ngoại giáo) đã đi ngang qua một thị trấn( đã bị tàn phá bởi một trận mưa tai ác (vào thời của Lut). Thế, chúng đã không từng nhìn thấy nó hay sao? Không! Chúng không mong (được) phục sinh (trở lại).');
INSERT INTO "hasan_abdulkarim" VALUES(25,41,'25:41','Và khi thấy Ngươi, chúng chỉ muốn chế nhạo Ngươi (bảo): "Phải chăng đây là kẻ mà Allah cử đến làm Sứ Giả (cho bọn mình?)"');
INSERT INTO "hasan_abdulkarim" VALUES(25,42,'25:42','“Quả thật, chỉ còn tí nữa là Y đã làm cho bọn mình từ bỏ những thần linh của bọn mình nếu bọn mình không kiên trì với các đấng ấy.” Rồi đây, chúng sề sớm biết, khi chúng đối diện với hình phạt, ai là kẻ đã lạc xa con đường (của Allah).');
INSERT INTO "hasan_abdulkarim" VALUES(25,43,'25:43','Ngươi có thấy kẻ đã nhận dục vọng của mình làm thần linh của y hay không? Thế, phải chăng Ngươi sẽ là người thọ lãnh công việc cho y?');
INSERT INTO "hasan_abdulkarim" VALUES(25,44,'25:44','Phải chăng Ngươi đã nghĩ đa số bọn chúng nghe hoặc hiểu nó? Chúng chẳng khác nào thú vật; không, chúng lạc đường rất xa.');
INSERT INTO "hasan_abdulkarim" VALUES(25,45,'25:45','Há Ngươi đã không nhìn thấy việc Thượng Đế của Ngươi đã kéo dài cái bóng như thế nào ư? Và nếu muốn, Ngài thừa sức làm cho nó đứng yên tại chỗ. Và TA đã dùng mặt trời làm một chỉ điểm cho nó.');
INSERT INTO "hasan_abdulkarim" VALUES(25,46,'25:46','Rồi TA kéo thu nó về hướng của TA một cách dễ dàng.');
INSERT INTO "hasan_abdulkarim" VALUES(25,47,'25:47','Và Ngài là Đấng đã tạo ban đêm thành một tấm phủ cho các ngươi và tạo giấc ngủ thành một sự nghỉ ngơi và tạo ban ngày như một sự phục sinh.');
INSERT INTO "hasan_abdulkarim" VALUES(25,48,'25:48','Và Ngài là Đấng đã phái những luồng gió làm người loan tin báo trước Hồng Ân (trận mưa) sắp đến của Ngài. Và TA ban nước mưa tinh khiết từ trên trời xuống,');
INSERT INTO "hasan_abdulkarim" VALUES(25,49,'25:49','Mà TA dùng để làm hồi sinh lại lớp đất chết khô và dùng để giải khát những sinh vật trong loài thú và loài người mà TA đã tạo ra nhiều.');
INSERT INTO "hasan_abdulkarim" VALUES(25,50,'25:50','Và TA phân phối nó (nước mưa) giữa họ để may ra họ tưởng nhớ (Thiên lộc của Allah), nhưng đa số nhân loại từ chối mọi thứ ngoại trừ việc phụ ơn (của họ).');
INSERT INTO "hasan_abdulkarim" VALUES(25,51,'25:51','Và nếu muốn, TA có thể bổ nhiệm tại mỗi thị trấn một người Cảnh cáo.');
INSERT INTO "hasan_abdulkarim" VALUES(25,52,'25:52','Do đó, Ngươi chớ nghe theo những kẻ không tin nhưng hãy dùng Nó (Qur''an) mà đấu tranh( chống lại chúng bằng một cuộc đấu tranh vĩ đại.');
INSERT INTO "hasan_abdulkarim" VALUES(25,53,'25:53','Và Ngài là Đấng đã cho hai biển nước tự do chảy: một loại với nước ngọt, dễ uống và loại kia với nước mặn, chát. Và Ngài dựng một bức chắn tách biệt hai dòng nước, không cho trộn lẫn với nhau.(');
INSERT INTO "hasan_abdulkarim" VALUES(25,54,'25:54','Và Ngài là Đấng đã tạo hóa con người từ nước. Và Ngài thiết lập cho y mối quan hệ ruột thịt và quan hệ hôn nhân. Và Thượng Đế (Allah) của Ngươi là Đấng Toàn Năng.');
INSERT INTO "hasan_abdulkarim" VALUES(25,55,'25:55','Nhưng chúng lại tôn thờ ngoài Allah những vật không làm lợi cũng không hãm hại được chúng. Và kẻ không tin lúc nào cũng là kẻ tán trợ (Shaytan) chống lại Thượng Đế của nó.');
INSERT INTO "hasan_abdulkarim" VALUES(25,56,'25:56','Và TA chỉ cử Ngươi (Muhammad) làm một người vừa mang tin mừng vừa cảnh cáo.');
INSERT INTO "hasan_abdulkarim" VALUES(25,57,'25:57','Hãy bảo (chúng, hỡi Muhammad!): "Ta không đòi hỏi các người trả thù lao cho ta về việc (truyền bá) này ngoại trừ việc này: hãy để cho người nào muốn, được tự do chọn lấy con đường đi đến với Thượng Đế của y."');
INSERT INTO "hasan_abdulkarim" VALUES(25,58,'25:58','Và hãy phó thác cho Đấng Hằng Sống, Đấng không bao giờ chết và hãy tán dương lời ca tụng Ngài. Và Ngài đủ biết tội lỗi của đám bầy tôi của Ngài.');
INSERT INTO "hasan_abdulkarim" VALUES(25,59,'25:59','Đấng đã tạo các tầng trời và trái đất và vạn vật giữa trời đất trong sáu Ngày rồi an vị trên Ngai Vương của Ngài, Đấng Rất Mực Nhân Từ! Hãy hỏi Ngài, Đấng Am Tường mọi việc (nếu muốn biết về các Đức Tính, về sự tạo hóa, về cách an vi của Ngài v.v...).');
INSERT INTO "hasan_abdulkarim" VALUES(25,60,'25:60','Và khi có lời bảo chúng: “Hãy phủ phục Đấng Al-Rahman (Allah)!” Chúng đáp: “Nhưng Đấng Al-Rahman (Rất mực Độ lượng) là gì? Bọn ta sẽ phải quỳ lạy Đấng mà Ngươi (Muhammad) truyền lệnh cho bọn ta hay sao?” Và điều đó càng làm cho chúng thêm thù nghịch.');
INSERT INTO "hasan_abdulkarim" VALUES(25,61,'25:61','Phúc thay (Allah!), Đấng đã làm ra các chùm sao trên trời và đặt trong đó một chiếc đèn (mặt trời) và một mặt trăng chiếu sáng.');
INSERT INTO "hasan_abdulkarim" VALUES(25,62,'25:62','Và Ngài là Đấng đã làm ra ban đêm và ban ngày nối đuôi nhau luân chuyến để cho ai muốn tụng niệm tưởng nhớ hoặc muốn tạ ơn (Ngài).');
INSERT INTO "hasan_abdulkarim" VALUES(25,63,'25:63','Và bầy tôi của Đấng Al-Rahman (Allah) là những ai đi lại trên mặt đất dáng điệu khiêm tốn và khi người ngu dốt gây gổ với họ, chỉ trả lời: “(Xin cho được) Bằng an!”');
INSERT INTO "hasan_abdulkarim" VALUES(25,64,'25:64','Và những ai thức đêm quỳ lạy và đứng nghiêm trước mặt Thượng Đế của họ.');
INSERT INTO "hasan_abdulkarim" VALUES(25,65,'25:65','Và những ai cầu nguyện, thưa: “Lạy Thượng Đế của bầy tôi! Xin Ngài giữ chúng tôi tránh khỏi hình phạt của hỏa ngục. Quả thật, đó là một sự trừng phạt dai dẳng.”');
INSERT INTO "hasan_abdulkarim" VALUES(25,66,'25:66','Quả thật, hỏa ngục là một nơi ngụ và là một nơi nghỉ rất xấu xa.');
INSERT INTO "hasan_abdulkarim" VALUES(25,67,'25:67','Và những ai khi tiêu dùng không phung phí cũng không keo kiệt mà giữ mức trung bình giữa hai thái cực đó.');
INSERT INTO "hasan_abdulkarim" VALUES(25,68,'25:68','Và những ai không cầu nguyện một thần linh nào khác cùng với Allah cũng không giết một linh hồn mà Allah đã làm cho linh thiêng trừ phi với lý do chính đáng; và không gian dâm; và ai vi phạm những điều đó thì là rước tội vào thân.');
INSERT INTO "hasan_abdulkarim" VALUES(25,69,'25:69','Y sẽ bị trừng phạt gấp đôi vào Ngày phục sinh và sẽ ở trong đó (hỏa ngục) đời đời một cách nhục nhã.');
INSERT INTO "hasan_abdulkarim" VALUES(25,70,'25:70','Ngoại trừ ai biết hối cải và tin tưởng và làm việc thiện thì là những người mà Allah sẽ đổi điều xấu xa (tội lỗi) của họ thành điều tốt lành (phúc đức) bởi vì Allah là Đấng Hằng Tha Thứ, Rất Mực Khoan Dung.');
INSERT INTO "hasan_abdulkarim" VALUES(25,71,'25:71','Và ai biết hối cải và làm việc thiện thì quả thật đã chân thành hối cải với Allah.');
INSERT INTO "hasan_abdulkarim" VALUES(25,72,'25:72','Và những ai không làm chứng cho điều giả dối và khi gác bỏ ngoài tai chuyện tầm phào thì bỏ qua một cách lịch sự.');
INSERT INTO "hasan_abdulkarim" VALUES(25,73,'25:73','Và những ai khi được nhắc về những Lời Mặc Khải của Thượng Đế của họ sẽ không gục đầu ra vẻ tai điếc mắt đui khi nghe chúng.');
INSERT INTO "hasan_abdulkarim" VALUES(25,74,'25:74','Và những ai cầu nguyện, thưa: "Lạy Thượng Đế của bầy tôi! Xin Ngài làm cho vợ và con cái của bầy tôi thành một nguồn vui cho cặp mắt của bầy tôi và biến bầy tôi thành người lãnh đạo cho những người ngay chính."');
INSERT INTO "hasan_abdulkarim" VALUES(25,75,'25:75','Họ sẽ được (Allah) ban thưởng địa vị cao sang vì đã từng kiên nhẫn chịu đựng. Nơi đó (thiên đàng), họ sẽ được đón chào tốt đẹp với lời chúc ''Salam'' (Bằng an).');
INSERT INTO "hasan_abdulkarim" VALUES(25,76,'25:76','Họ sẽ ở trong đó (thiên đàng) đời đời. Một nơi ngụ và một nơi nghỉ hết sức tốt đẹp!');
INSERT INTO "hasan_abdulkarim" VALUES(25,77,'25:77','(Hỡi Muhammad!) Hãy bảo họ: “Nếu không vì lời cầu nguyện của các người thì Thượng Đế (Allah) của Ta đã không mấy quan tâm đến các người bởi vì các người đã từng phủ nhận (Ngài). Rồi đây, các người sẽ không thể tránh khỏi (sự trừng phạt của Ngài).”');
INSERT INTO "hasan_abdulkarim" VALUES(26,1,'26:1','Ta. Sin. Mim.');
INSERT INTO "hasan_abdulkarim" VALUES(26,2,'26:2','Đây là những Câu của một Kinh Sách minh bạch.');
INSERT INTO "hasan_abdulkarim" VALUES(26,3,'26:3','Có lẽ Ngươi tự huỷ mình vì buồn rầu về việc chúng không tin tưởng.');
INSERT INTO "hasan_abdulkarim" VALUES(26,4,'26:4','Nếu muốn, TA có thể gởi từ trên trời xuống một phép lạ mà chúng sẽ cúi cổ xuống thần phục.');
INSERT INTO "hasan_abdulkarim" VALUES(26,5,'26:5','Và không một điều nhắc nhở mới nào của Đấng Rất Mực Độ Lượng được ban xuống cho chúng mà chúng lại không quay mặt lánh xa.');
INSERT INTO "hasan_abdulkarim" VALUES(26,6,'26:6','Bởi thế, chắc chắn chúng đã phủ nhận Chân Lý; rồi đây chúng sẽ nhận tin tức về những điều mà chúng đã từng chế giễu.');
INSERT INTO "hasan_abdulkarim" VALUES(26,7,'26:7','Và há chúng đã không quan sát trái đất, nơi mà TA đã làm mọc ra bao nhiêu cặp cây cối quí hóa hay sao?');
INSERT INTO "hasan_abdulkarim" VALUES(26,8,'26:8','trong đó là một dấu hiệu; nhưng đa số bọn chúng không tin.');
INSERT INTO "hasan_abdulkarim" VALUES(26,9,'26:9','Và Thượng Đế của Ngươi là Đấng Toàn Năng, Đấng Rất Mực Khoan Dung.');
INSERT INTO "hasan_abdulkarim" VALUES(26,10,'26:10','Và (hãy nhớ) khi Thượng Đế của Ngươi gọi Musa (phán bảo): "Nhà ngươi hãy đi gặp một đám người làm điều sai quấy"');
INSERT INTO "hasan_abdulkarim" VALUES(26,11,'26:11','“Đám người của Fir-''awn há chúng không sợ Allah hay sao?”');
INSERT INTO "hasan_abdulkarim" VALUES(26,12,'26:12','(Musa) thưa: "Lạy Thượng Đế! Bề tôi sợ chúng sẽ cho bề tôi nói dối."');
INSERT INTO "hasan_abdulkarim" VALUES(26,13,'26:13','“Và lòng ngực của bề tôi chật hẹp và chiếc lưỡi của bề tôi cử động không nhanh chống. Xin Ngài cho gọi Harun đến (giúp bề tôi)."');
INSERT INTO "hasan_abdulkarim" VALUES(26,14,'26:14','“Và chúng buộc tội bề tôi đã phạm một trọng tội; do đó bề tôi sợ chúng sẽ giết bề tôi.”');
INSERT INTO "hasan_abdulkarim" VALUES(26,15,'26:15','(Allah) phán: "Không sao cả! Hai ngươi cứ lên đường với những Phép Lạ của TA. TA sẽ ở cùng với các ngươi, lắng nghe."');
INSERT INTO "hasan_abdulkarim" VALUES(26,16,'26:16','"Bởi thế, hai ngươi cứ đi gặp Fir-''awn bảo (y): Chúng tôi là Sứ Giả của Thượng Đế của vũ trụ và muôn loài."');
INSERT INTO "hasan_abdulkarim" VALUES(26,17,'26:17','“Xin ngài cho phép con cháu của Israel ra đi với chúng tôi.”');
INSERT INTO "hasan_abdulkarim" VALUES(26,18,'26:18','(Fir-''awn) bảo (Musa): "Há bọn ta đã không thương yêu và nuôi nấng ngươi như con cái trong (nhà của) bọn ta và ngươi đã ở với bọn ta nhiều năm trong đời ngươi hay sao?"');
INSERT INTO "hasan_abdulkarim" VALUES(26,19,'26:19','“Thế mà nhà ngươi đã nhẫn tâm làm một việc (sát nhân) mà ngươi đã làm. Và nhà ngươi là một tên phụ ơn.”');
INSERT INTO "hasan_abdulkarim" VALUES(26,20,'26:20','(Musa) đáp: "Tôi làm điều đó vào lúc tôi nhầm lẫn."');
INSERT INTO "hasan_abdulkarim" VALUES(26,21,'26:21','"Và vì sợ quí ngài nên tôi đã phải bỏ trốn đi xa và Thượng Đế của tôi đã ban cho tôi lý giải khôn ngoan và cử tôi làm một Sứ Giả của Ngài."');
INSERT INTO "hasan_abdulkarim" VALUES(26,22,'26:22','“Đấy là ân xưa mà ngài đã nại ra để khiển trách tôi; và ngài đã bắt con cháu của Israel làm nô lệ.”');
INSERT INTO "hasan_abdulkarim" VALUES(26,23,'26:23','Fir-''awn lên tiếng: “Và Thượng Đế của vũ trụ và muôn loài là gì chứ?”');
INSERT INTO "hasan_abdulkarim" VALUES(26,24,'26:24','Musa đáp: “Thượng Đế của các tầng trời và trái đất và vạn vật giữa trời đất nếu quí ngài muốn có một niềm tin vững chắc (về Ngài).”');
INSERT INTO "hasan_abdulkarim" VALUES(26,25,'26:25','(Fir-''awn) nói với cận thần: “Há các khanh không nghe rõ?”');
INSERT INTO "hasan_abdulkarim" VALUES(26,26,'26:26','(Musa) tiếp: “Thượng Đế của quí ngài và của tổ tiên của quí ngài.”');
INSERT INTO "hasan_abdulkarim" VALUES(26,27,'26:27','(Fir-''awn) bảo: “Quả thật, Sứ Giả của các ngươi là một thằng điên!”');
INSERT INTO "hasan_abdulkarim" VALUES(26,28,'26:28','(Musa) tiếp: “Thượng Đế của phương đông và phương tây và vạn vật nằm giữa hai phương đó, nếu quí vị thông hiểu được.”');
INSERT INTO "hasan_abdulkarim" VALUES(26,29,'26:29','(Fir-''awn) bảo: “Nếu nhà ngươi quả quyết tôn thờ một Thượng Đế khác thay vì ta thì chắc chắn ta se bỏ tù nhà ngươi.”');
INSERT INTO "hasan_abdulkarim" VALUES(26,30,'26:30','(Musa) đáp: “Sao! Dẫu cho tôi có trưng bày cho ngài thấy một điều minh bạch.”');
INSERT INTO "hasan_abdulkarim" VALUES(26,31,'26:31','(Fir-''awn) bảo: “Thế hãy đưa nó (phép lạ) ra xem nếu nhà ngươi là một người nói thật.”');
INSERT INTO "hasan_abdulkarim" VALUES(26,32,'26:32','Bởi thế, Musa ném chiếc gậy của Y xuống thì nó biến thành một con rắn rõ rệt.');
INSERT INTO "hasan_abdulkarim" VALUES(26,33,'26:33','Và khi đưa bàn tay của Y ra, nó hiện ra trắng toát trước mặt những người đứng nhìn.');
INSERT INTO "hasan_abdulkarim" VALUES(26,34,'26:34','Fir-''awn quay bảo các cận thần: "Tên này quả thật là một tên phù thủy tài giỏi."');
INSERT INTO "hasan_abdulkarim" VALUES(26,35,'26:35','“Y định dùng pháp thuật để đánh đuổi các khanh ra khỏi lãnh thổ của các khanh. Thế các khanh định làm gì y?”');
INSERT INTO "hasan_abdulkarim" VALUES(26,36,'26:36','(Chư thần) thưa: "Hãy tạm tha cho y và người em của y (trong một thời gian) và cử những người loan tin đi khắp các thị trấn."');
INSERT INTO "hasan_abdulkarim" VALUES(26,37,'26:37','“Nhờ triệu về cho ngài tất cả những nhà phù thủy tài giỏi.”');
INSERT INTO "hasan_abdulkarim" VALUES(26,38,'26:38','Do đó, những nhà phù thủy được triệu tập tại một nơi hẹn vào một ngày đã được công bố rõ.');
INSERT INTO "hasan_abdulkarim" VALUES(26,39,'26:39','Và có lời bảo người dân: "Há quí vị không đi dự đại hội hay sao?"');
INSERT INTO "hasan_abdulkarim" VALUES(26,40,'26:40','“Có lẽ chúng tôi sẽ theo các nhà phù thủy nếu họ thắng cuộc.”');
INSERT INTO "hasan_abdulkarim" VALUES(26,41,'26:41','Bởi thế, khi đến nơi, những nhà phù thủy thưa với Fir-''awn: “Chúng tôi sẽ được ân thưởng chăng nếu chúng tối thắng cuộc?”');
INSERT INTO "hasan_abdulkarim" VALUES(26,42,'26:42','(Fir-''awn) đáp: “Vâng, có chứ! Chắc chắn lúc đó các ngươi sẽ là cận thần của ta.”');
INSERT INTO "hasan_abdulkarim" VALUES(26,43,'26:43','Musa bảo họ: “Hãy ném bảo vật của quí vị xuống.”');
INSERT INTO "hasan_abdulkarim" VALUES(26,44,'26:44','Do đó, họ ném những sợi dây và chiếc gậy của họ xuống và nói: “Nhờ quyền lực của Fir-''awn, chắc chắn chúng tôi sẽ thắng cuộc.”');
INSERT INTO "hasan_abdulkarim" VALUES(26,45,'26:45','Nhưng khi Musa ném chiếc gậy của Y xuống, tức thời nó nuốt mất những món vật mà họ đã làm giả.');
INSERT INTO "hasan_abdulkarim" VALUES(26,46,'26:46','Bởi thế, những tên phù thủy (khiếp sợ) sụp xuống quỳ lạy.');
INSERT INTO "hasan_abdulkarim" VALUES(26,47,'26:47','Và lên tiếng: "Chúng tôi tin tưởng nơi Thượng Đế của vũ trụ và muôn loài."');
INSERT INTO "hasan_abdulkarim" VALUES(26,48,'26:48','“Thượng Đế của Musa và của Harun.”');
INSERT INTO "hasan_abdulkarim" VALUES(26,49,'26:49','(Fir-''awn) bảo: “Các ngươi cả tin nơi y trước khi ta cho phép các ngươi hay sao? Y quả là tên đầu não của các ngươi; Y đã dạy các ngươi pháp thuật; rồi đây các ngươi sẽ sớm biết hậu quả. Chắc chắn, ta sẽ cho chặt tay và chân của các ngươi mỗi bên một cánh và sẽ cho đóng đinh các ngươi trên thập tự giá, tất cả, không sót một tên nào.”');
INSERT INTO "hasan_abdulkarim" VALUES(26,50,'26:50','Họ đồng thanh đáp: “Chẳng có gì thiệt cả. (Bởi vì) chúng tôi sẽ trở về gặp Thượng Đế của chúng tôi trở lại');
INSERT INTO "hasan_abdulkarim" VALUES(26,51,'26:51','“Chúng tôi mong Thượng Đế chúng tôi sẽ tha thứ cho chúng tôi những lỗi lầm của chúng tôi bởi vì chúng tôi là những người tin tưởng đầu tiên.”');
INSERT INTO "hasan_abdulkarim" VALUES(26,52,'26:52','Và TA đã mặc khải cho Musa phán: “Nhà ngươi hãy dẫn bầy tôi của TA ra đi ban đêm. Thật sự, các ngươi sẽ bị truy nã.”');
INSERT INTO "hasan_abdulkarim" VALUES(26,53,'26:53','Bởi thế, Fir-''awn phái những người loan tin đi khắp các thị trấn.');
INSERT INTO "hasan_abdulkarim" VALUES(26,54,'26:54','“Quả thật, những người (Israel) này chỉ là một nhóm nhỏ."');
INSERT INTO "hasan_abdulkarim" VALUES(26,55,'26:55','"Quả thật, chúng đã phạm tội khi quân chọc giận bọn ta."');
INSERT INTO "hasan_abdulkarim" VALUES(26,56,'26:56','“Nhưng tất cả bọn ta đã cảnh giác trước”');
INSERT INTO "hasan_abdulkarim" VALUES(26,57,'26:57','Bởi thế, TA đã đưa bọn chúng ra khỏi hoa viên và suối nước,');
INSERT INTO "hasan_abdulkarim" VALUES(26,58,'26:58','Và rời khỏi những kho tàng châu báu và địa vị cao sang.');
INSERT INTO "hasan_abdulkarim" VALUES(26,59,'26:59','Đúng như thế. Và TA đã cho con cháu của Israel thừa kế chúng.');
INSERT INTO "hasan_abdulkarim" VALUES(26,60,'26:60','Rồi chúng đuổi theo bắt họ vào lúc hừng đông.');
INSERT INTO "hasan_abdulkarim" VALUES(26,61,'26:61','Bởi thế, khi hai đoàn người đối diện nhau, các tông đồ của Musa nói: “Chắc chắn chúng ta sắp bị truy sát rồi.”');
INSERT INTO "hasan_abdulkarim" VALUES(26,62,'26:62','(Musa) bảo: “Không hề gì! Thượng Đế của ta ở với ta, và sẽ hướng dẫn ta.”');
INSERT INTO "hasan_abdulkarim" VALUES(26,63,'26:63','Liền đó, TA đã mặc khải cho Musa, phán: “Hãy dùng chiếc gậy của ngươi mà đánh xuống mặt biển!” Tức thời, biền rẽ ra làm hai, biến thành bức thành đứng sừng sững như một quả núi vĩ đại.');
INSERT INTO "hasan_abdulkarim" VALUES(26,64,'26:64','Và TA đưa đám (người bị truy nã) kia đến gần nơi đó.');
INSERT INTO "hasan_abdulkarim" VALUES(26,65,'26:65','Và TA đã cứu nguy Musa và những ai theo Y, tất cả.');
INSERT INTO "hasan_abdulkarim" VALUES(26,66,'26:66','Rồi TA đã nhận đám kia chết chìm.');
INSERT INTO "hasan_abdulkarim" VALUES(26,67,'26:67','Quả thật, trong biến cố đó là một bài học; nhưng đa số bọn chúng không là những người tin tưởng.');
INSERT INTO "hasan_abdulkarim" VALUES(26,68,'26:68','Và quả thật Thượng Đế (Allah) của Ngươi là Đấng Toàn Năng, Đấng Rất Mực Khoan Dung.');
INSERT INTO "hasan_abdulkarim" VALUES(26,69,'26:69','Và hãy đọc cho chúng (người Quraish) câu chuyện của Ibrahim.');
INSERT INTO "hasan_abdulkarim" VALUES(26,70,'26:70','Khi Y thưa với phụ thân và người dân của Y: “Vật gì quí vị đang thờ phụng vậy?”');
INSERT INTO "hasan_abdulkarim" VALUES(26,71,'26:71','Họ đáp: “Bọn ta đang thờ các tượng thần và rất đỗi sùng kính họ.”');
INSERT INTO "hasan_abdulkarim" VALUES(26,72,'26:72','(Ibrahim) bảo: "Chúng có nghe được quí vị chăng khi quí vị gọi chúng?"');
INSERT INTO "hasan_abdulkarim" VALUES(26,73,'26:73','“Hoặc chúng có giúp ích hay hãm hại được quí vị chăng?”');
INSERT INTO "hasan_abdulkarim" VALUES(26,74,'26:74','Họ đáp: “Không, nhưng bọn ta thấy cha mẹ của bọn ta đã làm thế.”');
INSERT INTO "hasan_abdulkarim" VALUES(26,75,'26:75','(Ibrahim) bảo: “Thế quí vị có quan sát đồ vật mà quí vị đang tôn thờ chăng?"');
INSERT INTO "hasan_abdulkarim" VALUES(26,76,'26:76','“Quí vị lẫn tổ tiên xa xưa của quí vị?"');
INSERT INTO "hasan_abdulkarim" VALUES(26,77,'26:77','“Nhưng, chúng là kẻ thù của tôi ngoại trừ Thượng Đế của vũ trụ và muôn loài."');
INSERT INTO "hasan_abdulkarim" VALUES(26,78,'26:78','“Đấng đã tạo ra tôi, rồi hướng dẫn tôi."');
INSERT INTO "hasan_abdulkarim" VALUES(26,79,'26:79','“Và là Đấng đã cho tôi ăn và uống."');
INSERT INTO "hasan_abdulkarim" VALUES(26,80,'26:80','“Và là Đấng đã chữa lành tôi khi tôi mắc bệnh."');
INSERT INTO "hasan_abdulkarim" VALUES(26,81,'26:81','“Và là Đấng sẽ gây cho tôi chết rồi phục sinh tôi trở lại."');
INSERT INTO "hasan_abdulkarim" VALUES(26,82,'26:82','“Và là Đấng mà tôi hy vọng sẽ tha thứ cho tôi về những lỗi lầm của tôi vào Ngày Xét xử."');
INSERT INTO "hasan_abdulkarim" VALUES(26,83,'26:83','“Lạy Thượng Đế của bề tôi! Xin Ngài ban cho bề tôi thông thái khôn ngoan và dung nạp bề tôi chung với những người đức hạnh."');
INSERT INTO "hasan_abdulkarim" VALUES(26,84,'26:84','“Và vinh danh bề tôi nơi những thế hệ tương lai."');
INSERT INTO "hasan_abdulkarim" VALUES(26,85,'26:85','“Và biến bề tôi thành một người thừa kế thiên đàng Hạnh phúc."');
INSERT INTO "hasan_abdulkarim" VALUES(26,86,'26:86','“Và tha thứ cho phụ thân của bề tồi bởi vì người là một người lầm lạc."');
INSERT INTO "hasan_abdulkarim" VALUES(26,87,'26:87','“Và chớ hạ nhục bề tôi vào Ngày mà nhân loại sẽ được phục sinh."');
INSERT INTO "hasan_abdulkarim" VALUES(26,88,'26:88','“Ngày mà của cải cũng như con cái sẽ không giúp ích được ai."');
INSERT INTO "hasan_abdulkarim" VALUES(26,89,'26:89','Ngoại trừ ai mang đến cho Allah một tấm lòng trong sạch.');
INSERT INTO "hasan_abdulkarim" VALUES(26,90,'26:90','Và thiên đàng sẽ được mang đến gần những người sợ Allah.');
INSERT INTO "hasan_abdulkarim" VALUES(26,91,'26:91','Và hỏa ngục sẽ được mang đến trước mặt những kẻ lạc lối.');
INSERT INTO "hasan_abdulkarim" VALUES(26,92,'26:92','Và có lời bảo chúng: “Đâu là những vật mà các người đã tôn thờ,');
INSERT INTO "hasan_abdulkarim" VALUES(26,93,'26:93','“Khác Allah? Há chúng có thể giải cứu các ngươi hoặc tự cứu?”');
INSERT INTO "hasan_abdulkarim" VALUES(26,94,'26:94','Bởi thế, chúng sẽ bị lùa vào hỏa ngục, chúng và đám quỉ quái.');
INSERT INTO "hasan_abdulkarim" VALUES(26,95,'26:95','Và cả binh tướng của Iblis nữa, tất cả.');
INSERT INTO "hasan_abdulkarim" VALUES(26,96,'26:96','Và trong lúc cãi vã nhau trong đó, chúng sẽ nói:');
INSERT INTO "hasan_abdulkarim" VALUES(26,97,'26:97','“Thề với Allah, chúng tôi là những kẻ đã công khai lầm lạc."');
INSERT INTO "hasan_abdulkarim" VALUES(26,98,'26:98','“Khi chúng tôi suy tôn quí vị ngang vai với Thượng Đế của vũ trụ và muôn loài."');
INSERT INTO "hasan_abdulkarim" VALUES(26,99,'26:99','"Và chỉ những tên kẻ tội lỗi đã dắt chúng tôi đi lạc."');
INSERT INTO "hasan_abdulkarim" VALUES(26,100,'26:100','“Bởi thế, chúng tôi không có ai can thiệp."');
INSERT INTO "hasan_abdulkarim" VALUES(26,101,'26:101','“Cũng không có một người bạn chân tình nào (để giúp đỡ)."');
INSERT INTO "hasan_abdulkarim" VALUES(26,102,'26:102','“Giá có dịp trở lại (trần gian) thì chúng tôi sẽ tin tưởng.”');
INSERT INTO "hasan_abdulkarim" VALUES(26,103,'26:103','Trong đó là một dấu hiệu, nhưng đa số bọn chúng không tin.');
INSERT INTO "hasan_abdulkarim" VALUES(26,104,'26:104','Và Thượng Đế của Ngươi là Đấng Toàn Năng, Đấng Rất Mực Khoan Dung.');
INSERT INTO "hasan_abdulkarim" VALUES(26,105,'26:105','Người dần của Nuh đã cho những Sứ Giả (của Allah) nói dối,');
INSERT INTO "hasan_abdulkarim" VALUES(26,106,'26:106','Khi người anh em của họ, Nuh, bảo: “Các người không sợ Allah ư?"');
INSERT INTO "hasan_abdulkarim" VALUES(26,107,'26:107','“Quả thật, Ta là một Sứ Giả đáng tin cậy đến với các người.');
INSERT INTO "hasan_abdulkarim" VALUES(26,108,'26:108','“Bởi thế, hãy sợ Allah và nghe theo Ta."');
INSERT INTO "hasan_abdulkarim" VALUES(26,109,'26:109','“Và Ta không đòi hỏi các người tưởng thưởng Ta về nó. Chỉ Thượng Đế của vũ trụ và muôn loài sẽ ân thưởng Ta."');
INSERT INTO "hasan_abdulkarim" VALUES(26,110,'26:110','“Do đó, hãy sợ Allah và nghe theo Ta.”');
INSERT INTO "hasan_abdulkarim" VALUES(26,111,'26:111','Họ đáp: “Bọn ta phải tin tưởng ngươi hay sao trong lúc chỉ có những kẻ bần cùng đi theo ngươi?”');
INSERT INTO "hasan_abdulkarim" VALUES(26,112,'26:112','(Nuh) bảo: “Ta đâu biết điều họ đã từng làm."');
INSERT INTO "hasan_abdulkarim" VALUES(26,113,'26:113','“Chỉ Thượng Đế của Ta nắm hồ sơ của họ, nếu các người nhận thấy."');
INSERT INTO "hasan_abdulkarim" VALUES(26,114,'26:114','“Và Ta không phải là người xua đuổi những người có đức tin."');
INSERT INTO "hasan_abdulkarim" VALUES(26,115,'26:115','“Mà chỉ là một người cảnh cáo công khai.”');
INSERT INTO "hasan_abdulkarim" VALUES(26,116,'26:116','Họ nói: “Nếu không chịu ngưng, hỡi Nuh!, bọn ta sẽ ném đá giết ngươi.”');
INSERT INTO "hasan_abdulkarim" VALUES(26,117,'26:117','(Nuh), thưa: “Lạy Thượng Đế! Người dân của bề tôi phủ nhận bề tôi."');
INSERT INTO "hasan_abdulkarim" VALUES(26,118,'26:118','“Bởi thế, xin Ngài công minh phân xử giữa bề tôi và họ và xin giải cứu bề tôi và ai là người tin tưởng tuân theo bề tôi.”');
INSERT INTO "hasan_abdulkarim" VALUES(26,119,'26:119','Do đó, TA đã giải cứu Y và những người theo Y trên một chiếc tàu chở đầy (người và thú vật).');
INSERT INTO "hasan_abdulkarim" VALUES(26,120,'26:120','Rồi, TA nhận đám người còn lại chết chìm dưới nước (lụt).');
INSERT INTO "hasan_abdulkarim" VALUES(26,121,'26:121','Trong đó là một dấu hiệu nhưng đa số bọn chúng không tin.');
INSERT INTO "hasan_abdulkarim" VALUES(26,122,'26:122','Và Thượng Đế của Ngươi là Đấng Toàn Năng, Đấng Rất Mực Khoan Dung.');
INSERT INTO "hasan_abdulkarim" VALUES(26,123,'26:123','(Người dân) ''Ad đã cho những Sứ Giả của Allah nói dối.');
INSERT INTO "hasan_abdulkarim" VALUES(26,124,'26:124','Khi người anh em của họ, Hud, bảo: “Các người không sợ Allah ư?"');
INSERT INTO "hasan_abdulkarim" VALUES(26,125,'26:125','"Ta thực sự là một Sứ Giả đáng tin cậy (đến) với các người."');
INSERT INTO "hasan_abdulkarim" VALUES(26,126,'26:126','“Do đó, hãy sợ Allah và nghe theo Ta."');
INSERT INTO "hasan_abdulkarim" VALUES(26,127,'26:127','"Ta không đòi hỏi các người tưởng thưởng ta về nó (Thông Điệp của Allah). Thượng Đế của vũ trụ và muôn loài sẽ ân thưởng Ta."');
INSERT INTO "hasan_abdulkarim" VALUES(26,128,'26:128','“Phải chăng các người đã xây cất trên mỗi ngọn đồi cao (biệt thự làm) một cảnh giới để khoe khoang?"');
INSERT INTO "hasan_abdulkarim" VALUES(26,129,'26:129','“Phải chăng các người đã xây cất những lâu đài kiên cố để mong ở trong đó vĩnh viễn?"');
INSERT INTO "hasan_abdulkarim" VALUES(26,130,'26:130','“Và khi các người tước đoạt, các người bóc lột như những bạo chúa."');
INSERT INTO "hasan_abdulkarim" VALUES(26,131,'26:131','“Bởi thế, hãy sợ Allah và nghe theo Ta."');
INSERT INTO "hasan_abdulkarim" VALUES(26,132,'26:132','“Và hãy sợ Đấng đã trợ giúp các người với những vật mà các người đã từng biết."');
INSERT INTO "hasan_abdulkarim" VALUES(26,133,'26:133','“Ngài đã giúp đỡ các người với gia súc và con cái."');
INSERT INTO "hasan_abdulkarim" VALUES(26,134,'26:134','“Và hoa viên và suối nước."');
INSERT INTO "hasan_abdulkarim" VALUES(26,135,'26:135','"Ta sợ giùm cho các người về sự trừng phạt của một Ngày vĩ đại.”');
INSERT INTO "hasan_abdulkarim" VALUES(26,136,'26:136','Họ đáp: “Ông có cảnh cáo hay không cảnh cáo đi nữa, điều đó bằng thừa đối với chúng tôi."');
INSERT INTO "hasan_abdulkarim" VALUES(26,137,'26:137','“Công việc này của ông chỉ là một phong cách của người xưa."');
INSERT INTO "hasan_abdulkarim" VALUES(26,138,'26:138','“Và chúng tôi sẽ không bị trừng phạt đau đớn.”');
INSERT INTO "hasan_abdulkarim" VALUES(26,139,'26:139','Bởi thế, chúng đã cho Y nói dối. Cho nên, TA đã tiêu diệt chúng. Trong đó là một dấu hiệu. Nhưng đa số bọn chúng không tin.');
INSERT INTO "hasan_abdulkarim" VALUES(26,140,'26:140','Và Thượng Đế của Ngươi là Đấng Toàn Năng, Đấng Rất Mực Khoan Dung.');
INSERT INTO "hasan_abdulkarim" VALUES(26,141,'26:141','Và (người dân) Thamud đã cho những Sứ Giả (của Allah) nói dối.');
INSERT INTO "hasan_abdulkarim" VALUES(26,142,'26:142','Khi người anh em của họ, Saleh, bảo: “Các người không sợ Allah ư?"');
INSERT INTO "hasan_abdulkarim" VALUES(26,143,'26:143','“Ta là một Sứ Giả đáng tin cậy (đến) với các người."');
INSERT INTO "hasan_abdulkarim" VALUES(26,144,'26:144','“Do đó, hãy sợ Allah và nghe theo Ta."');
INSERT INTO "hasan_abdulkarim" VALUES(26,145,'26:145','“Ta không đòi hỏi các người tưởng thưởng Ta về nó (Thông Điệp của Allah). Thượng Đế của vũ trụ và muôn loài sẽ ân thưởng Ta."');
INSERT INTO "hasan_abdulkarim" VALUES(26,146,'26:146','“Phải chăng các người sẽ được an toàn hưởng thụ những tiện nghi hiện có nơi đây hay sao?"');
INSERT INTO "hasan_abdulkarim" VALUES(26,147,'26:147','“Nơi những hoa viên và suối nước?"');
INSERT INTO "hasan_abdulkarim" VALUES(26,148,'26:148','“Với những cánh đồng bắp lúa và vườn cây chà là hoa lá mịn màng?"');
INSERT INTO "hasan_abdulkarim" VALUES(26,149,'26:149','“Và các người đã khéo tay đục núi xây nhà trong đó."');
INSERT INTO "hasan_abdulkarim" VALUES(26,150,'26:150','“Do đó, hãy sợ Allah và nghe theo Ta."');
INSERT INTO "hasan_abdulkarim" VALUES(26,151,'26:151','“Và chớ nghe theo mệnh lệnh của những kẻ thái quá."');
INSERT INTO "hasan_abdulkarim" VALUES(26,152,'26:152','“Những ai đã làm những điều thối nát trong xứ nhưng không sửa mình.”');
INSERT INTO "hasan_abdulkarim" VALUES(26,153,'26:153','Họ đáp: “Ông chỉ là một người bị mê hoặc."');
INSERT INTO "hasan_abdulkarim" VALUES(26,154,'26:154','“Ông chỉ là một người phàm như chúng tôi. Thế, hãy mang một Phép Lạ đến cho chúng tôi thấy nếu ông là một người nói thật.”');
INSERT INTO "hasan_abdulkarim" VALUES(26,155,'26:155','(Saleh) đáp: “Đây là con lạc đà cái. Nó uống phần nước của nó và các người uống phần nước của các người vào một ngày đã được qui định."');
INSERT INTO "hasan_abdulkarim" VALUES(26,156,'26:156','“Và chớ giết nó sợ lằng các người sẽ bị trừng phạt vào một Ngày vĩ đại.”');
INSERT INTO "hasan_abdulkarim" VALUES(26,157,'26:157','Nhưng chúng đã cắt nhượng nó và sau đó đâm ra hối hận.');
INSERT INTO "hasan_abdulkarim" VALUES(26,158,'26:158','Do đó, chúng đã bị trừng phạt. Trong đó là một dấu hiệu. Nhưng đa số bọn chung không tin tưởng.');
INSERT INTO "hasan_abdulkarim" VALUES(26,159,'26:159','Và Thượng Đế của Ngươi là Đấng Toàn Năng, Đấng Rất Mực Khoan Dung.');
INSERT INTO "hasan_abdulkarim" VALUES(26,160,'26:160','Người dân của Lut cũng đã cho những Sứ Giả nói dối.');
INSERT INTO "hasan_abdulkarim" VALUES(26,161,'26:161','Khi người anh em của họ, Lut, bảo: “Các người không sợ Allah ư?"');
INSERT INTO "hasan_abdulkarim" VALUES(26,162,'26:162','"Ta thực sự là một Sứ Giả đáng tin cậy (đến) với các người."');
INSERT INTO "hasan_abdulkarim" VALUES(26,163,'26:163','“Do đó, hãy sợ Allah và nghe theo Ta."');
INSERT INTO "hasan_abdulkarim" VALUES(26,164,'26:164','"Ta không đòi hỏi các người tưởng thưởng Ta về nó (Thông Điệp của Allah). Chỉ Thượng Đế của vũ trụ và muôn loài sẽ ân thưởng Ta."');
INSERT INTO "hasan_abdulkarim" VALUES(26,165,'26:165','“Phải chăng trong vũ trụ, duy chỉ các người là những kẻ đã đến (làm tình) với đàn ông."');
INSERT INTO "hasan_abdulkarim" VALUES(26,166,'26:166','“Và bỏ rơi những người mà Allah đã tạo ra cho các người để làm vợ? Không, các người là những kẻ đã làm điều thái quá.”');
INSERT INTO "hasan_abdulkarim" VALUES(26,167,'26:167','Họ đáp: “Nếu không chịu ngưng, hỡi Lut, thì ông sẽ bị trục xuất.”');
INSERT INTO "hasan_abdulkarim" VALUES(26,168,'26:168','(Lut) bảo: "Ta là một người thù ghét việc làm của các người."');
INSERT INTO "hasan_abdulkarim" VALUES(26,169,'26:169','“Lạy Thượng Đế của bề tôi! Xin Ngài giải cứu bề tôi và gia đình của bề tôi khỏi điều (tội lỗi) mà chúng đã làm.”');
INSERT INTO "hasan_abdulkarim" VALUES(26,170,'26:170','Bởi thế, TA đã giải cứu Lut và gia đình của Y, tất cả;');
INSERT INTO "hasan_abdulkarim" VALUES(26,171,'26:171','Ngoại trừ một bà lão (vợ Lut) trong số những người ở lại đằng sau.');
INSERT INTO "hasan_abdulkarim" VALUES(26,172,'26:172','Rồi, TA đã tiêu diệt đám người còn lại;');
INSERT INTO "hasan_abdulkarim" VALUES(26,173,'26:173','Và TA đã xối lên chúng một trận mưa đích đáng. Và tai ác thay trận mưa (đá) xối lên đầu những kẻ đã được cảnh cáo trước!');
INSERT INTO "hasan_abdulkarim" VALUES(26,174,'26:174','Trong đó là một dấu hiệu. Nhưng đa số bọn chúng không tin.');
INSERT INTO "hasan_abdulkarim" VALUES(26,175,'26:175','Thượng Đế của Ngươi là Đấng Toàn Năng, Đấng Rất Mực Khoan Dung.');
INSERT INTO "hasan_abdulkarim" VALUES(26,176,'26:176','Dân cư tại Khu rừng gần Madyan đã cho những Sứ Giả nói dối,');
INSERT INTO "hasan_abdulkarim" VALUES(26,177,'26:177','Khi Shu’aib bảo họ: “Các người không sợ Allah hay sao?"');
INSERT INTO "hasan_abdulkarim" VALUES(26,178,'26:178','"Ta thực sự là một Sứ Giả đáng tin cậy (đến) với các người."');
INSERT INTO "hasan_abdulkarim" VALUES(26,179,'26:179','“Do đó, hãy sợ Allah và nghe theo Ta."');
INSERT INTO "hasan_abdulkarim" VALUES(26,180,'26:180','“Và Ta không đòi hỏi các người tưởng thưởng Ta về nó. Thật sự Thượng Đế của vũ trụ và muôn loài se ân thưởng Ta."');
INSERT INTO "hasan_abdulkarim" VALUES(26,181,'26:181','“Hãy đo cho đủ và chớ làm cho người khác mất mát."');
INSERT INTO "hasan_abdulkarim" VALUES(26,182,'26:182','"Và hãy cần với một bàn cân thẳng đứng."');
INSERT INTO "hasan_abdulkarim" VALUES(26,183,'26:183','“Và chớ lấy bớt vật dụng của người và chớ hành động thối nát trên mặt đất, gieo rắc tội ác."');
INSERT INTO "hasan_abdulkarim" VALUES(26,184,'26:184','“Và hãy sợ Allah, Đấng đã tạo hóa các người và những thế hệ trước.”');
INSERT INTO "hasan_abdulkarim" VALUES(26,185,'26:185','Họ đáp: “Ông chỉ là một người bị mê hoặc."');
INSERT INTO "hasan_abdulkarim" VALUES(26,186,'26:186','“Và ông là một người phàm như chúng tôi và chúng tôi nghĩ ông là một người nói dối."');
INSERT INTO "hasan_abdulkarim" VALUES(26,187,'26:187','"Thế, ông hãy làm cho một phần trời sụp xuống đè chúng tôi đi nếu ông là một người nói thật.”');
INSERT INTO "hasan_abdulkarim" VALUES(26,188,'26:188','Y bảo: “Thượng Đế của Ta là Đấng biết rõ điều mà các người đã làm.”');
INSERT INTO "hasan_abdulkarim" VALUES(26,189,'26:189','Nhưng họ đã cho Y nói dối. Bởi thế, họ đã gánh chịu Hình phạt của một Ngày mịt trời; đó là sự trừhg phạt của một Ngày vĩ đại.');
INSERT INTO "hasan_abdulkarim" VALUES(26,190,'26:190','Quả thật, trong đó là một dấu hiệu; Nhưng đa số bọn chúng là những kẻ không tin tưởng.');
INSERT INTO "hasan_abdulkarim" VALUES(26,191,'26:191','Thượng Đế của Ngươi là Đấng Toàn Năng, Đấng Rất Mực Khoan Dung.');
INSERT INTO "hasan_abdulkarim" VALUES(26,192,'26:192','Quả thật, Nó (Qur''an) do Thượng Đế của vũ trụ ban xuống.');
INSERT INTO "hasan_abdulkarim" VALUES(26,193,'26:193','Thiên Thần Jibril đã mang Nó xuống;');
INSERT INTO "hasan_abdulkarim" VALUES(26,194,'26:194','Đặt vào quả tim của Ngươi (Muhammad) để Ngươi trở thành một Người báo trước;');
INSERT INTO "hasan_abdulkarim" VALUES(26,195,'26:195','Bằng tiếng Ả Rập trong sáng;');
INSERT INTO "hasan_abdulkarim" VALUES(26,196,'26:196','Quả thật, Nó (đã được nhắc đến) trong những Kinh Sách cũ.');
INSERT INTO "hasan_abdulkarim" VALUES(26,197,'26:197','Và hẳn Nó không là một dấu hiệu cho họ hay sao bởi vì những người có học trong số con cháu của Israel đã công nhận Nó?');
INSERT INTO "hasan_abdulkarim" VALUES(26,198,'26:198','Và nếu TA có ban Nó xuống cho một người không phải gốc gác Ả Rập,');
INSERT INTO "hasan_abdulkarim" VALUES(26,199,'26:199','Để y đọc lại cho họ thì họ vẫn không tin tưởng nơi Nó.');
INSERT INTO "hasan_abdulkarim" VALUES(26,200,'26:200','Bằng cách đó, TA đã làm cho nó (việc phủ nhận Qur''an) thấm vào lòng của những kẻ tội lỗi;');
INSERT INTO "hasan_abdulkarim" VALUES(26,201,'26:201','Họ sẽ không tin tưởng nơi Nó (Qur''an) cho đến khi đối diện với sự trừng phạt đau đớn;');
INSERT INTO "hasan_abdulkarim" VALUES(26,202,'26:202','Bởi Vì nó sẽ đến chụp bắt họ bất ngờ trong lúc họ không nhận thấy.');
INSERT INTO "hasan_abdulkarim" VALUES(26,203,'26:203','Sau đó, họ sẽ than: “Chúng tôi có được gia hạn hay chăng?”');
INSERT INTO "hasan_abdulkarim" VALUES(26,204,'26:204','Thế, họ còn hối thúc hình phạt cho mau đến nữa hay chăng?');
INSERT INTO "hasan_abdulkarim" VALUES(26,205,'26:205','Há Ngươi nhận thấy, nếu TA cho họ hưởng lạc thêm nhiều năm nữa,');
INSERT INTO "hasan_abdulkarim" VALUES(26,206,'26:206','Rồi sau đó, hình phạt đã được hứa sẽ đến chụp bắt họ,');
INSERT INTO "hasan_abdulkarim" VALUES(26,207,'26:207','Thì việc hưởng thụ đó của họ chẳng giúp ích gì được cho họ cả;');
INSERT INTO "hasan_abdulkarim" VALUES(26,208,'26:208','Và quả thật, TA chỉ tiêu diệt một thị trấn sau khi đã có những người báo trước được cử phái đến với nó;');
INSERT INTO "hasan_abdulkarim" VALUES(26,209,'26:209','Để làm công việc nhắc nhở. Bởi vì TA không hề bất công.');
INSERT INTO "hasan_abdulkarim" VALUES(26,210,'26:210','Và không phải những tên Shaytan mang Nó (Qur''an) xuống.');
INSERT INTO "hasan_abdulkarim" VALUES(26,211,'26:211','Bởi vì việc làm đó không thích hợp với chúng và chúng cũng không có khả năng (làm được việc đó).');
INSERT INTO "hasan_abdulkarim" VALUES(26,212,'26:212','Thật sự, chúng bị đuổi ra xa không cho nghe (Qur''an).');
INSERT INTO "hasan_abdulkarim" VALUES(26,213,'26:213','Bởi thế, chớ cầu nguyện một thần linh nào khác cùng với Allah bởi vì làm thế, Ngươi sẽ là một trong những kẻ bị trừng phạt.');
INSERT INTO "hasan_abdulkarim" VALUES(26,214,'26:214','Và hãy cảnh cáo thân tộc gần gũi của Ngươi (Muhammad).');
INSERT INTO "hasan_abdulkarim" VALUES(26,215,'26:215','Hãy đối xử từ tốn với những người có đức tin theo Ngươi.');
INSERT INTO "hasan_abdulkarim" VALUES(26,216,'26:216','Nhưng nếu họ bất tuân Ngươi thì hãy bảo (họ): "Ta vô can về những điều các người làm."');
INSERT INTO "hasan_abdulkarim" VALUES(26,217,'26:217','Và hãy phó thác cho Đấng Toàn Năng, Đấng Rất Mực Khoan Dung.');
INSERT INTO "hasan_abdulkarim" VALUES(26,218,'26:218','Ngài thấy Ngươi khi Ngươi đứng (dâng lễ Tahajjud ban đêm).');
INSERT INTO "hasan_abdulkarim" VALUES(26,219,'26:219','Và cả động tác của Ngươi trong số những người sụp lạy.');
INSERT INTO "hasan_abdulkarim" VALUES(26,220,'26:220','Bởi vì quả thật, Ngài là Đấng Hằng Nghe, Đấng Hằng Biết.');
INSERT INTO "hasan_abdulkarim" VALUES(26,221,'26:221','Há Ta (Muhammad) sẽ phải báo cho các người biết ai là kẻ mà những tên Shaytan thường xuống gặp?');
INSERT INTO "hasan_abdulkarim" VALUES(26,222,'26:222','Chúng hay xuống nhập với từng tên nói dối và tội lỗi.');
INSERT INTO "hasan_abdulkarim" VALUES(26,223,'26:223','Chúng lắng nghe (những tên Shaytan) và đã số bọn chúng là những tên nói dối.');
INSERT INTO "hasan_abdulkarim" VALUES(26,224,'26:224','Và các thi sĩ, chỉ những kẻ lầm lạc mới nghe theo chúng.');
INSERT INTO "hasan_abdulkarim" VALUES(26,225,'26:225','Há Ngươi không thấy chúng lang thang vơ vẩn nơi mỗi thung lũng?');
INSERT INTO "hasan_abdulkarim" VALUES(26,226,'26:226','Và chúng nói ra điều mà chúng không làm hay sao?');
INSERT INTO "hasan_abdulkarim" VALUES(26,227,'26:227','Ngoại trừ những ai có đức tin, làm việc thiện, tụng niệm Allah nhiều và giành thắng lợi (từ những kẻ đa thần) sau khi đã bị đối xử bất công. Và những kẻ làm điều sai quấy sẽ sớm biết đâu là cuộc đổi đời mà họ sẽ phải gánh chịu sau này.');
INSERT INTO "hasan_abdulkarim" VALUES(27,1,'27:1','Ta. Sin. Đây là những Câu của Qur''an và một Kinh Sách minh bạch.');
INSERT INTO "hasan_abdulkarim" VALUES(27,2,'27:2','Một nguồn Chỉ Đạo và tin mừng cho những người có đức tin.');
INSERT INTO "hasan_abdulkarim" VALUES(27,3,'27:3','Những ai dâng lễ nguyện Salah và đóng Zakah và có đức tin nơi đời sau.');
INSERT INTO "hasan_abdulkarim" VALUES(27,4,'27:4','Quả thật, đối với những ai không tin có đời sau, TA đã khiến cho việc làm của họ tỏ ra đẹp mắt đối với họ; bởi thế, họ lang thang vơ vẩn.');
INSERT INTO "hasan_abdulkarim" VALUES(27,5,'27:5','Họ là những kẻ sẽ chịu một sự trừng phạt xấu xa và sẽ là những người thua thiệt lớn nhất ở đời sau.');
INSERT INTO "hasan_abdulkarim" VALUES(27,6,'27:6','Và chắc chắn, Ngươi (Muhammad!) đã tiếp thu Qur''an từ Đấng Vô Cùng Sáng Suốt và Toàn Tri.');
INSERT INTO "hasan_abdulkarim" VALUES(27,7,'27:7','(Hãy nhớ lại) khi Musa bảo người nhà: “Quả thật, ta thấy một ngọn lửa ở đằng xa, ta sẽ đến chỗ ngọn lửa mang về cho các người tin tức từ chỗ đó hoặc sẽ mang về cho các người một khúc củi cháy để các người sưởi ấm.”');
INSERT INTO "hasan_abdulkarim" VALUES(27,8,'27:8','Nhưng khi đến chỗ của ngọn lửa, Y nghe tiếng gọi, bảo: “Phúc cho ai ở trong lửa và cho ai ở xung quanh nó! Quang vinh và trong sạch thay Allah! Thượng Đế (Đấng Chủ Tể) của muôn loài!');
INSERT INTO "hasan_abdulkarim" VALUES(27,9,'27:9','“Hỡi Musa! Thực sự, TA là Allah, Đấng Toàn Năng, Đấng Chí Minh.');
INSERT INTO "hasan_abdulkarim" VALUES(27,10,'27:10','“Hãy ném chiếc gậy của ngươi xuống!” Nhưng khi thấy nó động đậy giống như một con rắn, Y liền quay lưng bỏ chạy và không ngoái nhìn lại phía sau. “Hỡi Musa! Chớ hoảng sợ. Quả thật! Các Sứ Giả chớ nên hoảng sợ trước mặt TA."');
INSERT INTO "hasan_abdulkarim" VALUES(27,11,'27:11','“Ngoại trừ ai làm điều sai quấy rồi sau đó làm điều thiện để chuộc điều xấu thì thực sự TA Hằng Tha Thứ, Hằng Khoan dung."');
INSERT INTO "hasan_abdulkarim" VALUES(27,12,'27:12','“Và hãy áp bàn tay của Ngươi vào lòng ngực của Ngươi, khi rút ra, nó sẽ trở thành trắng toát nhưng không có gì đau đớn cả; đó là một trong số chín Phép Lạ mang đến trình bày cho Fir-''awn và đám người của hắn. Quả thật chúng là một đám người ương ngạnh, bất tuân."');
INSERT INTO "hasan_abdulkarim" VALUES(27,13,'27:13','Nhưng khi những Phép Lạ của TA được mang đến cho chúng nhìn tận mắt, chúng nói: “Đây rõ ràng là một trò ảo thuật!”');
INSERT INTO "hasan_abdulkarim" VALUES(27,14,'27:14','Nhưng vì sai quấy và kiêu ngạo, chúng đã phủ nhận (những Phép Lạ đó) mặc dầu trong thâm tâm, chúng nhìn nhận (đó là sự thật). Bởi thế, hãy nhìn xem kết cuộc của những kẻ thối nát đã xảy ra như thế nào.');
INSERT INTO "hasan_abdulkarim" VALUES(27,15,'27:15','Và quả thật, TA đã ban kiến thức cho Dawood và Sulayman. Và cả hai đều tán dương thưa: “Mọi ca tụng đều dâng lên Allah, Đấng đã ưu đãi bầy tôi hơn nhiều bề tôi tin tưởng khác của Ngài.”');
INSERT INTO "hasan_abdulkarim" VALUES(27,16,'27:16','Và Sulayman thừa kế Dawood. Và Y bảo: “Hỡi nhân loại! Chúng tôi đã được (Allah) dạy cho (hiểu được) tiếng nói của loài chim và đã được (Allah) ban cho đủ mọi thứ và đây là một Thiên ân rõ rệt.”');
INSERT INTO "hasan_abdulkarim" VALUES(27,17,'27:17','Và đoàn binh tướng của Sulayman gồm loài Jinn, loài người và chim chóc đã được triệu tập trước mặt Y theo đội ngũ chỉnh tề.');
INSERT INTO "hasan_abdulkarim" VALUES(27,18,'27:18','(Đoàn quân của Sulayman di chuyển) mãi cho đến lúc chúng đến một thung lũng của loài kiến; một con kiến lên tiếng: “Hỡi đoàn kiến chúng ta! Hãy lui vào chỗ ở của các người kẻo Sulayman và đoàn quân của Người sẽ (vô tình) dẫm nát các anh trong lúc họ không hề hay biết gì cả.”');
INSERT INTO "hasan_abdulkarim" VALUES(27,19,'27:19','Lấy làm vui thích trước lời nói của nó, Sulayman mỉm cười và cầu nguyện, thưa: “Lạy Thượng Đế của bề tôi! Xin Ngài ban cho bề tôi quyền lực hầu bề tôi đa tạ Ngài về những Ân Huệ mà Ngài đã ban cho bề tôi và cho cha mẹ của bề tôi và để bề tôi làm việc thiện hầu làm hài lòng Ngài và xin lấy Đức Khoan Dung của Ngài mà thâu nhận bề tôi vào đám bầy tôi ngay chính của Ngài.”');
INSERT INTO "hasan_abdulkarim" VALUES(27,20,'27:20','Và (Sulayman) kiểm tra đoàn chim, rồi bảo: “Sao ta không thấy con Hud-hud (Chim đầu rìu)? Phải chăng nó vắng mặt?”');
INSERT INTO "hasan_abdulkarim" VALUES(27,21,'27:21','"Chắc chắn, ta sẽ phạt nó nghiêm khắc; hoặc ta sẽ giết nó hoặc nó sẽ phải cho ta biết lý do rõ ràng (về việc nó vắng mặt)."');
INSERT INTO "hasan_abdulkarim" VALUES(27,22,'27:22','Nhưng không dám chậm trễ, con Hud-hud liền vội vàng (bay về) thưa: “Hạ thần bay đi tham quan nơi địa điểm mà bệ hạ chưa hề đặt chân đến và hạ thần mang tín tức chắc chắn từ Saba'' (Sheba) về tâu với bệ hạ."');
INSERT INTO "hasan_abdulkarim" VALUES(27,23,'27:23','“Hạ thần đã khám phá thấy có một nữ vương cai trị (dân) chúng và nữ vương đó đã được cung phụng đầy đủ mọi thứ và làm chủ một ngai vương vĩ đại."');
INSERT INTO "hasan_abdulkarim" VALUES(27,24,'27:24','“Hạ thần thấy vị nữ vương đó và dân chúng của bà ta thờ mặt trời thay vì Allah, và Shaytan đã làm cho họ thích thú với việc làm của họ và ngăn cản họ xa lánh con đường (của Allah) bởi vì họ không được ai hướng dẫn cả."');
INSERT INTO "hasan_abdulkarim" VALUES(27,25,'27:25','Chẳng lẽ họ không quỳ lạy Allah, Đấng trưng bày những điều bí mật trong các tầng trời và trái đất và biết rõ những điều các ngươi giấu kín cũng như những điều các ngươi phơi bày và công khai hay sao?!');
INSERT INTO "hasan_abdulkarim" VALUES(27,26,'27:26','Allah! Không có Thượng Đế nào khác cả duy chỉ Ngài (là Thượng Đế). Thượng Đế (Đấng Chủ Tể) của chiếc Ngai vương vĩ đại.');
INSERT INTO "hasan_abdulkarim" VALUES(27,27,'27:27','Sulayman bảo (con chim Hud-hud): "Ta sẽ xem nhà ngươi nói thật hay nhà ngươi là một tên nói dối.”');
INSERT INTO "hasan_abdulkarim" VALUES(27,28,'27:28','“Nhà ngươi hãy mang phong thư này của ta giao cho họ rồi từ giả họ lui về, sau đó đợi xem họ phúc đáp điều gì.”');
INSERT INTO "hasan_abdulkarim" VALUES(27,29,'27:29','(Nữ vương) bảo: “Hỡi các tướng lĩnh! Trẫm vừa nhận một phong thư khả kính."');
INSERT INTO "hasan_abdulkarim" VALUES(27,30,'27:30','“Phong thư này đến từ Sulayman và nó bắt đầu bằng câu: ''Nhân danh Allah, Đấng Rất Mực Độ Lượng, Đấng Rất Mực Khoan Dung."');
INSERT INTO "hasan_abdulkarim" VALUES(27,31,'27:31','“(Nội dung bức thư): ''Chớ cao ngạo đối với ta và hãy đến gặp ta như một người Muslim (thần phục Allah)''.”');
INSERT INTO "hasan_abdulkarim" VALUES(27,32,'27:32','(Nữ vương) nói (tiếp): “Hỡi các chư khanh! Các chư khanh hãy cố vấn trẫm trong việc này. Trẫm không quyết định một công việc gì mà không có mặt của các khanh cả.”');
INSERT INTO "hasan_abdulkarim" VALUES(27,33,'27:33','(Quần thần của vị nữ vương) tâu: “Chúng ta hùng mạnh và dày dạn kinh nghiệm chinh chiến dữ dội và quyết định là ở nơi nữ vương. Bởi thế, nữ vương hãy suy xét mà ra lệnh.”');
INSERT INTO "hasan_abdulkarim" VALUES(27,34,'27:34','Nữ vương bảo: “Các vua chúa khi tiến vào một thị trấn nào thì họ sẽ tàn phá nó và bắt những người quyền thế nhất của nó xuống làm thứ dân thấp hèn nhất; và họ sẽ làm như thế (đối với chúng ta)."');
INSERT INTO "hasan_abdulkarim" VALUES(27,35,'27:35','“Nhưng trẫm sẽ sai sứ thần mạng lễ vật đi triều cống họ rồi đợi xem các sứ thần sẽ mang điều (phúc đáp) gì trở về.”');
INSERT INTO "hasan_abdulkarim" VALUES(27,36,'27:36','Bởi thế, khi các sứ thần (của vị nữ vương kia) đến yết kiến Sulayman, Y bảo họ: “Các người định làm cho ta giàu thêm hay sao? Quả thật, những thứ mà Allah ban cho ta tốt hơn nhiều só với những thứ mà Ngài ban cho các người. Không, các người vui thích với những cống vật của các người."');
INSERT INTO "hasan_abdulkarim" VALUES(27,37,'27:37','“Các người hãy lui về (tâu lại) với họ, ta sẽ đem một đoàn quân đến hỏi tội họ và chắc chắn họ sẽ không bao giờ địch nổi, ta sẽ đuổi họ ra khỏi nơi đó một cách nhục nhã và họ sẽ phải qui hàng.”');
INSERT INTO "hasan_abdulkarim" VALUES(27,38,'27:38','(Sulayman) bảo (các chư tướng): “Hỡi các tướng lãnh! Ai trong các ngươi là kẻ mang ngai vàng của nữ vương kia đến nạp cho ta trước khi chúng đến qui hàng ta?”');
INSERT INTO "hasan_abdulkarim" VALUES(27,39,'27:39','(Lập tức) một tên ''Ifrit trong hàng ngũ của loài Jinn tâu: “Hạ thần sẽ mang nó đến nạp cho bệ hạ trước khi bệ hạ rời khỏi chỗ ngồi của bệ hạ. Quả thật, hạ thần có đủ sức mạnh và đáng tin cậy để làm việc đó.”');
INSERT INTO "hasan_abdulkarim" VALUES(27,40,'27:40','(Vừa dứt lời) một tên (Jinn) làu thông Kinh Sách liền tâu: “Hạ thần sẽ mang nó đến nạp cho bệ hạ trong nháy mắt.” Bởi thế, khi thấy nó được mang đến đặt trước mặt, Y (Sulayman) bảo: “Đây là một trong những Thiên Ân mà Thượng Đế của Ta dùng để thử thách (xem) ta có phải là một người biết ơn hay là một kẻ bội ơn. Bởi vì, ai biết tạ ơn thì việc tạ ơn đó chỉ có lợi cho bản thân mình; ngược lại, ai bội ơn thì Thượng Đế của Ta vẫn là Đấng Giàu Có và Quảng Đại”.');
INSERT INTO "hasan_abdulkarim" VALUES(27,41,'27:41','(Sulayman) bảo: “Hãy cải trang ngai vàng của nữ để xem nữ có được hướng dẫn (để nhìn ra ngai vàng của mình) hay sẽ là một người không được hướng dẫn.”');
INSERT INTO "hasan_abdulkarim" VALUES(27,42,'27:42','Bởi thế, khi nữ ta đến yết kiến, (Sulayman) hỏi: “Ngai vàng của nàng có giống cái này chăng?” Nữ ta đáp: “Dường như nó giống như thế.” Và (Sulayman bảo): “Ta đã được ban cho kiến thức trước nàng và là người Muslim (thần phục Allah).”');
INSERT INTO "hasan_abdulkarim" VALUES(27,43,'27:43','"Và những thứ mà nàng đã từng thờ phụng ngoài Allah đã cản trở nàng, bởi vì nàng thuộc về một dân tộc vô đức tin (nơi Ngài)."');
INSERT INTO "hasan_abdulkarim" VALUES(27,44,'27:44','Nữ được mời: “Xin mời nữ vương bước vào dinh” Bởi thế, khi nữ thấy nó, nữ cứ ngỡ đó là một hồ nước cho nên nữ đã vén váy của mình lên khiến ống chân của nữ lộ ra ngoài. Sulayman bảo: “Đây là một cung điện được lót bằng những miếng pha lê.” (Nghe nói), nữ thốt lên: “Lạy Thượng Đế của bề tôi! Bề tôi đã làm hỏng tâm hồn của bề tôi. Và bề tôi xin cùng với Sulayman nạp mình thần phục Allah, Thượng Đế của vũ trụ và muôn loài.”');
INSERT INTO "hasan_abdulkarim" VALUES(27,45,'27:45','Và quả thật trước đây, TA đã cử đến với người dân Thamud người anh em của họ Saleh, Y bảo họ: “Các người hãy thờ phụng Allah.” Nhưng lúc đó họ chia thành hai nhóm tranh chấp nhau.');
INSERT INTO "hasan_abdulkarim" VALUES(27,46,'27:46','Saleh bảo: “Hỡi dân ta! Tại sao các người chỉ muốn hối thúc điều dữ đến trước điều lành? Tại sao các người không cầu xin Allah tha thứ để may ra các người được khoan dung?”');
INSERT INTO "hasan_abdulkarim" VALUES(27,47,'27:47','Họ đáp: “Chúng tôi đoán ông là một người mang điềm xui xẻo đến cho chúng tôi.” Saleh bảo: “Điềm xui của các người ở nơi Allah; không, các người là một đám dân đang bị thử thách.”');
INSERT INTO "hasan_abdulkarim" VALUES(27,48,'27:48','Trong thành phố, có chín người hay làm điều thối nát trong xứ nhưng không chịu sửa mình.');
INSERT INTO "hasan_abdulkarim" VALUES(27,49,'27:49','Họ bảo (nhau): “Hãy cùng nhau thề nhân danh Allah rằng bọn mình nhất định sẽ tấn công giết y và gia đình của y vào ban đêm rồi bọn mình sẽ cho người thừa kế của y biết bọn mình không hề biết gì đến việc gia đình của y bị thủ tiêu và quả thật bọn mình nói thật.”');
INSERT INTO "hasan_abdulkarim" VALUES(27,50,'27:50','Và họ mưu đồ sắp đặt một kế hoạch và TA (Allah) cũng sắp đặt một kế hoạch trong lúc họ không nhận thấy điều đó.');
INSERT INTO "hasan_abdulkarim" VALUES(27,51,'27:51','Bởi thế, hãy xem kết quả của kế hoạch của họ như thế nào. Quả thật, TA đã tiêu diệt họ và toàn bộ đám dân của họ.');
INSERT INTO "hasan_abdulkarim" VALUES(27,52,'27:52','Vì vậy, nhà cửa của họ đã bị tàn phá ngổn ngang vì tội họ đã hành động sai quấy. Thật sự, trong (câu chuyện) đó là một bài học cho đám người hiểu biết.');
INSERT INTO "hasan_abdulkarim" VALUES(27,53,'27:53','Và TA đã cứu sống những người tin tưởng và những người ngay chính sợ Allah.');
INSERT INTO "hasan_abdulkarim" VALUES(27,54,'27:54','Và (hãy nhớ lại) Lut khi Y bảo người dân của Y: “Phải chăng các người đã làm điều khả ố trong lúc các người thấy rõ."');
INSERT INTO "hasan_abdulkarim" VALUES(27,55,'27:55','Phải chăng vì dâm dục các người đã giao hợp với đàn ông thay vì phụ nữ? Không, các người là một đám dân đã quen thói làm điều ngu dốt.');
INSERT INTO "hasan_abdulkarim" VALUES(27,56,'27:56','Câu trả lời của đám dân của Y chẳng có gì khác hơn điều này: “Hãy trục xuất gia đình của Lut ra khỏi thành phố của quí vị! Chúng là những người muốn giữ mình trong sạch!”');
INSERT INTO "hasan_abdulkarim" VALUES(27,57,'27:57','Bởi thế, TA đã giải cứu Y và gia đình của Y ngoại trừ bà vợ của Y. TA đã sắp đặt cho bà ta ở lại trong đám người trì trệ đằng sau.');
INSERT INTO "hasan_abdulkarim" VALUES(27,58,'27:58','Và TA đã trừng phạt chúng (đám người tội lỗi) bằng một trận mưa (đá) khủng khiếp. Và đó là một trận mưa diệt vong đối với những kẻ đã từng được cảnh cáo trước.');
INSERT INTO "hasan_abdulkarim" VALUES(27,59,'27:59','(Hỡi Muhammad!) Hãy thưa: “Mọi ca tụng đều dâng lên Allah và lời chúc ''Bằng an'' cho những bề tôi của Ngài mà Ngài đã chọn (để mang Thông điệp). (Thử hỏi chúng) Ai là Đấng Ưu Việt? Allah hay là những kẻ mà chúng cho là những vị hợp tác của Ngài?”');
INSERT INTO "hasan_abdulkarim" VALUES(27,60,'27:60','Há (không ưu việt ư?) Đấng đã tạo các tầng trời và trái đất và cho mưa từ trên trời xuống cho các ngươi nhờ đó, TA làm mọc ra những mảnh vườn xanh tươi đẹp đẽ mà các ngươi không thể làm mọc ra cây trái của chúng đặng. Có chăng một thần linh khác cùng với Allah? Không, chúng là một đám người thường bịa đặt những đối thủ ngang vai (với Allah).');
INSERT INTO "hasan_abdulkarim" VALUES(27,61,'27:61','Há (không ưu việt ư?) Đấng đã làm trái đất thành một nơi ở vững chắc và đặt giữa nó các sông ngòi và đặt giữa hai biển (nước mặn và nước ngọt) một bức chắn (vô hình tách biệt chúng). Có chăng một thần linh cùng (chia sẻ quyền năng) với Allah? Không, đa số bọn chúng không biết gì.');
INSERT INTO "hasan_abdulkarim" VALUES(27,62,'27:62','Há (không ưu việt ư?) Đấng đã đáp lại (lời cầu cứu của) người đau khổ khi y van xin Ngài và cứu y thoát nạn và làm cho các người (nhân loại) thành những người kế thừa trên trái đất. Có chăng một thần linh cùng (chia sẻ quyền năng) với Allah? ít thay điều các ngươi ghi nhớ!');
INSERT INTO "hasan_abdulkarim" VALUES(27,63,'27:63','Há (không ưu việt ư?) Đấng đã hướng dẫn các ngươi qua những lớp tăm tối của đất liền và biển cả và gởi những luồng gió làm người loan tin đến báo trước Hồng Ân (mưa) sắp đến của Ngài. Có chăng một thần linh cùng (chia sẻ quyền năng) với Allah? Allah Tối Thượng, Ngài ở bên trên mọi điều chúng qui cho Ngài.');
INSERT INTO "hasan_abdulkarim" VALUES(27,64,'27:64','Há (không ưu việt ư?) Đấng đã khởi sự việc tạo hóa rồi tái diễn nó lại và đã cung dưỡng cho các ngươi từ trên trời xuống dưới đất. Có chăng một thần linh cùng (chia sẻ quyền năng) với Allah? Hãy bảo chúng: “Hãy trưng các bằng chứng của các người ra xem nếu các người nói thật?”');
INSERT INTO "hasan_abdulkarim" VALUES(27,65,'27:65','Hãy bảo chúng: “Không ai biết rõ điều vô hình trong các tầng trời và trái đất ngoại trừ Allah; và chúng không biết được giờ phút nào chúng sẽ được phục sinh trở lại.”');
INSERT INTO "hasan_abdulkarim" VALUES(27,66,'27:66','Không, chúng không thể biết đặng đời sau. Không, chúng nghi ngờ về nó (đời sau). Không, chúng mù tịt về nó.');
INSERT INTO "hasan_abdulkarim" VALUES(27,67,'27:67','Và những kẻ không tín tưởng nói: “Phải chăng khi chúng tôi và cha mẹ của chúng tôi đã trở thành cát bụi, thật sự chúng tôi sẽ được dựng sống lại?”');
INSERT INTO "hasan_abdulkarim" VALUES(27,68,'27:68','“Quả thật, trước đây chúng tôi lẫn cha mẹ của chúng tôi đã được hứa điều này. Nhưng điều hứa này (theo chúng tôi nghĩ) chỉ là chuyện cổ tích của người cổ xưa.”');
INSERT INTO "hasan_abdulkarim" VALUES(27,69,'27:69','Hãy bảo chúng: “Các người hãy đi du lịch trên trái đất để nhìn xem kết cuộc của những kẻ tội lỗi như thế nào.”');
INSERT INTO "hasan_abdulkarim" VALUES(27,70,'27:70','Và Ngươi (Muhammad!) chớ buồn phiền về điều chúng nói và cũng chớ khổ tâm về những điều chúng âm mưu.');
INSERT INTO "hasan_abdulkarim" VALUES(27,71,'27:71','Và chúng nói: “Chừng nào lời hứa (phục sinh) này sẽ xảy ra nếu quí vị (Muslim) nói thật?”');
INSERT INTO "hasan_abdulkarim" VALUES(27,72,'27:72','Hãy bảo chúng: “Điều mà các người thúc giục cho mau đến có lẽ ở sát sau lưng của các người.”');
INSERT INTO "hasan_abdulkarim" VALUES(27,73,'27:73','Và quả thật, Thượng Đế (Allah) của Ngươi (Muhammad!) đã đặc ân đối với loài người, nhưng đa số bọn chúng không biết ơn.');
INSERT INTO "hasan_abdulkarim" VALUES(27,74,'27:74','Và quả thật, Thượng Đế (Allah) của Ngươi biết rõ điều chúng giấu giếm trong lòng và điều chúng bộc lộ ra ngoài.');
INSERT INTO "hasan_abdulkarim" VALUES(27,75,'27:75','Và không có một điều bí mật (vô hình) nào trên trời và dưới đất mà lại không được ghi trong một Quyển Sổ (định mệnh) rõ ràng.');
INSERT INTO "hasan_abdulkarim" VALUES(27,76,'27:76','Rõ thật, Kinh Qur''an này kể lại cho con cháu của Israel nhiều điều mà chúng thường tranh chấp nhau trong đó.');
INSERT INTO "hasan_abdulkarim" VALUES(27,77,'27:77','Và rõ thật, Nó (Qur''an) là một Chỉ Đạo và một Hồng Ân (ban) cho những người tin tưởng.');
INSERT INTO "hasan_abdulkarim" VALUES(27,78,'27:78','Rõ thật, Thượng Đế của Ngươi sẽ quyết định giữa chúng bằng việc Xét Xử của Ngài bởi vì Ngài là Đấng Toàn Năng, Đấng Toàn Tri.');
INSERT INTO "hasan_abdulkarim" VALUES(27,79,'27:79','Bởi thế, Ngươi hãy giao phó (công việc) cho Allah bởi vì Ngươi đang ở trên Chân Lý quang minh.');
INSERT INTO "hasan_abdulkarim" VALUES(27,80,'27:80','Rõ thật, Ngươi không thể làm cho người chết nghe được và cũng không thể làm cho người điếc nghe được tiếng gọi (nhất là) khi chúng quay lưng bỏ đi.');
INSERT INTO "hasan_abdulkarim" VALUES(27,81,'27:81','Và Ngươi cũng không thể hướng dẫn người mù ra khỏi tình trạng lầm lạc của họ được. Ngươi chỉ có thể làm cho ai tin tưởng nơi những Lời Mặc Khải của TA nghe được bởi vì họ là những người Muslim.');
INSERT INTO "hasan_abdulkarim" VALUES(27,82,'27:82','Và khi Lời (Xét Xử) đối với chúng sắp thể hiện, TA sẽ đưa ra từ trái đất một loại thú vật nói chuyện với chúng, bởi vì nhân loại không có một xác tín nào về những Lời Mặc Khải của TA.');
INSERT INTO "hasan_abdulkarim" VALUES(27,83,'27:83','Và hãy nhắc chúng về Ngày mà TA sẽ cho tập trung từ mỗi cộng đồng những thành phần đã phủ nhận những Lời Mặc Khải của TA thành một đội ngũ; rồi chúng sẽ bị bắt đứng thành hàng nghiêm chỉnh.');
INSERT INTO "hasan_abdulkarim" VALUES(27,84,'27:84','Cho đến lúc chúng đến tập hợp hết, rồi Ngài sẽ hỏi chúng: “Phải chăng các ngươi đã phủ nhận những Lời Mặc Khải của TA trong lúc các ngươi không thấu triệt chúng nổi với kiến thức của các ngươi hoặc phải chăng điều mà các ngươi đã làm chỉ có bấy nhiêu thôi?”');
INSERT INTO "hasan_abdulkarim" VALUES(27,85,'27:85','Và Lệnh (xử phạt) sẽ được thi hành đối với chúng bởi vì chúng đã làm điều sai quấy. Vì thế, chúng không thể nói được gì.');
INSERT INTO "hasan_abdulkarim" VALUES(27,86,'27:86','Phải chăng chúng đã không suy xét việc TA đã làm ra ban đêm cho chúng nghỉ ngơi và ban ngày để nhìn thấy? Quả thật, trong việc (tạo hóa) đó là những dấu hiệu cho đám người có đức tin.');
INSERT INTO "hasan_abdulkarim" VALUES(27,87,'27:87','Và (hãy nhắc chúng) về Ngày mà Tiếng Còi (Tận thế) sẽ được thổi lên, ai ở trong các tầng trời và ai ở dưới đất đều phải kinh hồn bạt vía ngoại trừ ai là người mà Allah muốn dung tha. Và tất cả đều phải hạ mình khiêm tốn đến trình diện trước mặt Ngài.');
INSERT INTO "hasan_abdulkarim" VALUES(27,88,'27:88','Và Ngươi sẽ thấy những quả núi mà Ngươi tưởng chúng cố định vững chắc, sẽ tan vỡ như những đám mây tan. Công trình nghệ thuật của Allah, Đấng đã hoàn chỉnh mọi vật. Quả thật, Ngài Rất Mực Am Tường mọi điều các ngươi làm.');
INSERT INTO "hasan_abdulkarim" VALUES(27,89,'27:89','Ai mang theo điều tốt thì sẽ được tưởng thưởng tốt hơn điều (tốt) đó và sẽ an toàn thoát khỏi cảnh hãi hùng của Ngày đó.');
INSERT INTO "hasan_abdulkarim" VALUES(27,90,'27:90','Và ai mang theo điều tội lỗi, mặt của chúng sẽ bị quẳng úp vào Lửa (của hỏa ngục kèm theo lời phán): “Phải chăng các người chỉ bị phạt tương xứng với điều mà các ngươi đã làm?”');
INSERT INTO "hasan_abdulkarim" VALUES(27,91,'27:91','Ta (Muhammad) nhận lệnh chỉ được thờ phụng Thượng Đế (Đấng Chủ Tể) của thành phố (Makkah) mà Ngài đã làm cho linh thiêng này, và mọi vật đều là của Ngài cả. Và Ta nhận được lệnh phải là một trong những người Muslim (thần phục) Ngài.');
INSERT INTO "hasan_abdulkarim" VALUES(27,92,'27:92','Và (nhận lệnh) phải đọc Qur''an. Bởi thế ai nhận được Chỉ Đạo thì việc hướng dẫn đó chỉ có lợi cho bản thân y; ngược lại, ai lầm lạc thì hãy bảo y: "Ta chỉ là một người cảnh báo thôi!”');
INSERT INTO "hasan_abdulkarim" VALUES(27,93,'27:93','Và hãy bảo (hỡi Muhammad!): “Mọi ca tụng đều dâng lên Allah! Ngài sẽ sớm cho các người thấy những dấu hiệu của Ngài và các người sẽ nhận ra chúng." Và Thượng Đế (Allah) của Ngươi không làm ngơ về những điều các người làm.');
INSERT INTO "hasan_abdulkarim" VALUES(28,1,'28:1','Ta.Sin. Mim.');
INSERT INTO "hasan_abdulkarim" VALUES(28,2,'28:2','Đây là những Câu của một Kinh Sách minh bạch.');
INSERT INTO "hasan_abdulkarim" VALUES(28,3,'28:3','TA (Allah) đọc cho Ngươi (hỡi Muhammad!) câu chuyện của Musa và Fir-''awn bằng sự thật (để Ngươi đọc lại) cho nhóm người có đức tin.');
INSERT INTO "hasan_abdulkarim" VALUES(28,4,'28:4','Quả thật, Fir-''awn tự suy tôn (là Thượng Đế) tối cao trong xứ và chia dân chúng thàng nhiều giáo phái; hắn áp bức một nhóm dân (Israel) bằng cách giết chết con trai của họ và tha sống phụ nữ của họ. Quả thật, hắn là một tên tàn bạo quá mức.');
INSERT INTO "hasan_abdulkarim" VALUES(28,5,'28:5','Và TA muốn ban ân cho những người cô thế trong xứ, và làm cho họ thành những nhà lãnh đạo (tôn giáo) và thành những người kế thừa (trái đất).');
INSERT INTO "hasan_abdulkarim" VALUES(28,6,'28:6','Và TA ban cho họ một chỗ định cư trong xứ và làm cho Fir-''awn và Haman cùng binh tướng của hai đứa chúng thấy (dân Israel) có những điều làm cho chúng cả sợ.');
INSERT INTO "hasan_abdulkarim" VALUES(28,7,'28:7','Và TA đã mặc khải cho mẹ của Musa, phán: “Ngươi hãy cho đứa con trai của ngươi bú vú (của ngươi) nhưng khi ngươi lo sợ cho (sinh mạng của) nó, hãy ném nó xuống sông và chớ sợ sệt cũng chớ lo buồn cho (số phận của nó). Rõ thật, TA sẽ mang nó trả lại cho ngươi và cử nó làm một Sứ Giả (của TA)."');
INSERT INTO "hasan_abdulkarim" VALUES(28,8,'28:8','Bởi thế, người nhà của Fir-''awn lượm Y (Musa) mang về nuôi để cho Y trở thành một kẻ thù và thành một nguồn lo âu cho chúng. Rõ thật, Fir-''awn và Haman và binh tướng của hai đứa chúng là những kẻ tội lỗi.');
INSERT INTO "hasan_abdulkarim" VALUES(28,9,'28:9','Và bà vợ của Fir-''awn thưa (với chồng): “(Đứa bé này) là một nguồn vui cho cặp mắt của thiếp và bệ hạ. Bệ hạ chớ giết nó. Biết đâu nó sẽ giúp ích chúng mình hoặc chúng mình sẽ nhận nó làm con của chúng mình." Và chúng không nhận thấy (hậu quả của việc chúng đang làm).');
INSERT INTO "hasan_abdulkarim" VALUES(28,10,'28:10','Và tấm lòng của người mẹ của Musa trở nên trống trải (vì thương nhớ con), nữ ta gần như muốn tiết lộ (câu chuyện) về đứa con trai của mình nếu TA không củng cố tấm lòng của nữ để cho nữ vẫn còn là một người có đức tin.');
INSERT INTO "hasan_abdulkarim" VALUES(28,11,'28:11','Và nữ đã bảo người chị của Musa: “Con hãy theo (dọ hỏi tung tích) đứa em của con!” Thế là, người chị đứng nhìn đứa em từ đằng xa trong lúc (kẻ thù) không để ý.');
INSERT INTO "hasan_abdulkarim" VALUES(28,12,'28:12','Và từ trước, TA đã khiến cho Y (Musa) không bú được sữa của những bà vú em, (mãi cho đến lúc người chị của Musa) đến gặp (người nhà của Fir-''awn và) thưa: “Quí ngài có muốn tiện nữ chỉ cho quí ngài một gia đình đứng ra chăm sóc (đứa bé) giùm cho quí ngài hay không?" Và họ là những người chỉ cầu mong cho đứa bé được mạnh giỏi.');
INSERT INTO "hasan_abdulkarim" VALUES(28,13,'28:13','Bằng cách đó, TA đã mang Y (Musa) trả lại cho mẹ của Y để cho cặp mắt của nữ ta dịu lại và làm cho nữ ta hết buồn và để cho nữ ta biết rằng Lời Hứa của Allah là sự thật; nhưng đa số bọn chúng không biết.');
INSERT INTO "hasan_abdulkarim" VALUES(28,14,'28:14','Và khi Musa (Môi-se) đến tuổi trưởng thành vững chắc, TA đã ban cho Y sự lý giải khôn ngoan và kiến thức. TA đãi ngộ những người làm tốt như thế.');
INSERT INTO "hasan_abdulkarim" VALUES(28,15,'28:15','Và khi đi vào thành phố lúc thị dân không để ý đến (người lạ mặt), Y thấy hai người đàn ông ẩu đả nhau nơi đó; một người thuộc phe của Y và người kia thuộc phe của kẻ thù của Y. Và người thuộc phe của Y gọi Y tiếp tay để đánh lại kẻ địch. Vì thế, Musa cung tay đánh kẻ thù và kết liễu sinh mạng của y. (Nhưng Musa sực tỉnh) vội nói: “Đây là hành động của Shaytan; và rõ thật, Shaytan là kẻ thù công khai chuyên dụ dỗ (người ta) đi lạc.”');
INSERT INTO "hasan_abdulkarim" VALUES(28,16,'28:16','(Musa) thưa: “Lạy Thượng Đế của bề tôi! Bề tôi đã tự hại mình, xin Ngài tha thứ cho bề tôi.” Do đó, (Allah) đã tha thứ cho Y. Rõ thật, Ngài là Đấng Hằng Tha Thứ, Đấng Rất Mực Khoan Dung.');
INSERT INTO "hasan_abdulkarim" VALUES(28,17,'28:17','(Musa) thưa: “Lạy Thượng Đế của bề tôi! Vì Ngài đã ban ân cho bề tôi cho nên bề tôi sẽ không bao giờ tiếp tay cho những kẻ tội lỗi nữa.”');
INSERT INTO "hasan_abdulkarim" VALUES(28,18,'28:18','Nhưng vào một buổi sáng nọ, khi Y đang ở trong thành phố vừa lo sợ vừa ngó chừng (binh lính của Fir-''awn, sợ chúng đến lùng bắt) thì kẻ đã kêu cứu Y trợ lực ngày hôm qua bây giờ lại thét kêu Y tiếp tay cho y nữa. Musa bảo y: “Rõ thật, mày là một tên chuyên dắt (người ta) đi lạc!”');
INSERT INTO "hasan_abdulkarim" VALUES(28,19,'28:19','Và khi Người định dùng sức túm lấy kẻ thù của hai người thì hắn vội lên tiếng: “Hỡi Mũsa! Mày định giết tao như mày đã giết một người ngày hôm qua hay sao? Rõ thật mày chỉ muốn trở thành một tên tàn bạo trong xứ chớ không muốn trở thành một người làm điều thiện.”');
INSERT INTO "hasan_abdulkarim" VALUES(28,20,'28:20','Và từ cuối phố, có một người chạy đến (báo với Musa), bảo: “Hỡi Musa! Các vị tù trưởng đang họp nhau bàn chuyện của anh để giết anh. Vậy, hãy chạy trốn đi. Quả thật, tôi chỉ muốn cho anh gặp điều phúc lành.”');
INSERT INTO "hasan_abdulkarim" VALUES(28,21,'28:21','Tức thời, Y bỏ chỗ đó trốn đi nơi khác vừa lo sợ vừa ngó chừng đề phòng. Y cầu nguyện: “Lạy Thượng Đế của bề tôi! Xin Ngài giải cứu bề tôi khỏi đám người làm điều sai quấy!”');
INSERT INTO "hasan_abdulkarim" VALUES(28,22,'28:22','Và khi chạy trốn về phía địa phận của Madyan, Y nói: “Biết đâu Thượng Đế của ta sẽ hướng dẫn ta đi theo con đường bằng phẳng (đúng đắn).”');
INSERT INTO "hasan_abdulkarim" VALUES(28,23,'28:23','Và khi đến những địa điểm có nước của địa phận Madyan, Y thấy nơi đó có một đám người đang múc nước (cho đàn cừu của họ uống) và ngoài đám đó ra, Y thấy hai phụ nữ đang giữ (đàn cừu của họ) lại phía sau. Y đến hỏi hai nữ: “Hai cô có chuyện gì vậy?” Hai nữ trả lời: “Chúng tôi không thể múc nước cho đàn cừu của chúng tôi uống được trừ phi những người chăn cừu dắt đàn cừu của họ rời khỏi chỗ đó. Và cha của chúng tôi là một người rất già (không giúp chúng tôi đặng).”');
INSERT INTO "hasan_abdulkarim" VALUES(28,24,'28:24','Do đó, Musa ra tay múc nước (giùm cho) hai cô gái rồi lui về nghỉ mát dưới bóng cây. Tiếp đó, Y cầu nguyện: “Lạy Thượng Đế của bề tôi! Bề tôi đang cần điều lành mà Ngài sắp ban cho.”');
INSERT INTO "hasan_abdulkarim" VALUES(28,25,'28:25','Sau đó, một trong hai cô gái bẽn lẽn đến gặp Y, thưa: “Cha tôi mời anh đến gặp người để người hậu đãi anh về công ơn anh đã múc nước giùm chúng tôi.” Thế là, Y đến gặp ông ta và kể lại cho ông ta câu chuyện của mình. Ông ta bảo: “Chớ sợ! Cậu đã thoát khỏi đám người gian ác rồi.”');
INSERT INTO "hasan_abdulkarim" VALUES(28,26,'28:26','Một trong hai cô gái thưa: “Thưa cha! Xin cha mướn anh ấy giúp việc. Thật sự, người tốt nhất mà cha có thể mướn để giúp việc là một người khỏe mạnh và trung tín.”');
INSERT INTO "hasan_abdulkarim" VALUES(28,27,'28:27','Ông già bảo: “Bác có ý gả một trong hai đứa con gái của bác cho cháu với điều kiện cháu phải làm thuê cho bác trong vòng tám năm; nhưng nếu cháu muốn làm thuê đủ mười năm thì tùy ý cháu; và bác không muốn đối xử khắc khe với cháu; nếu Allah muốn, cháu sẽ thấy bác là một người ngoan đạo.”');
INSERT INTO "hasan_abdulkarim" VALUES(28,28,'28:28','(Musa) thưa: “Thôi, hai bác cháu mình thỏa thuận như thế đi! Cháu có thể hoàn tất bất cứ thời hạn nào trong hai thời hạn vừa nói, nhưng bác không được bất công với cháu, bởi vì Allah là Đấng Thọ lãnh những điều chúng ta vừa nói.”');
INSERT INTO "hasan_abdulkarim" VALUES(28,29,'28:29','Do đó, khi Musa đã mãn hạn giao kèo và cùng với gia đình của mình ra đi, Y thấy một ngọn lửa trên sườn núi Tur. Y bảo người nhà: “Hãy dừng lại! Ta thấy lửa ở đằng xa. Hy vọng ta sẽ mang về cho các người tin tức của nó hoặc mang về cho các người một khúc củi cháy để các người có thể sưởi ấm.”');
INSERT INTO "hasan_abdulkarim" VALUES(28,30,'28:30','Tiếp đó, khi đến chỗ của ngọn lửa thì có tiếng gọi Y từ bờ phía bên phải của thung lũng tại một vùng đất đã được ban phúc của loại cây thiêng (Cây ô liu), phán: “Hỡi Musa! Rõ thật, TA đây là Allah, Thượng Đế (Đấng Chủ Tể) của vũ trụ và muôn loài .”');
INSERT INTO "hasan_abdulkarim" VALUES(28,31,'28:31','Và phán: “Hãy ném chiếc gậy của ngươi xuống.” Sau đó, khi thấy nó động đậy giống như một con rắn, Y quay mình bỏ chạy và không dám nhìn lại phía sau. “Hỡi Musa! Hãy bước đến gần và chớ sợ. Quả thật, nhà ngươi được an toàn (không hề gì).”');
INSERT INTO "hasan_abdulkarim" VALUES(28,32,'28:32','“Hãy áp bàn tay của ngươi vào lòng ngực của ngươi, khi rút ra, nó sẽ trở thành trắng toát không có gì đau đớn cả và hãy khép cánh tay của ngươi vào cạnh sườn của ngươi lại để khỏi sợ. Đấy là hai bằng chứng của Thượng Đế của ngươi được mang đến trình bày cho Fir-''awn và các vị tù trưởng của hắn. Quả thật, chúng là một đám người làm loạn chống lại Allah."');
INSERT INTO "hasan_abdulkarim" VALUES(28,33,'28:33','(Musa) thưa: “Lạy Thượng Đế của bề tôi! Quả thật, bề tôi đã giết một người của chúng. Do đó, bề tôi sợ chúng sẽ giết bề tôi trở lại.”');
INSERT INTO "hasan_abdulkarim" VALUES(28,34,'28:34','“Và người em của bề tôi, Harun, nó hoạt bát hơn bề tôi, do đó, xin Ngài cử no làm một người phụ tá cùng đi với bề tôi để xác nhận vai trò của bề tôi. Quả thật, bề tôi sợ chúng sẽ buộc tội bề tôi nói dối.”');
INSERT INTO "hasan_abdulkarim" VALUES(28,35,'28:35','(Allah) phán: “TA sẽ tăng cường cánh tay của ngươi với người em của ngươi và sẽ ban cho hai anh em nhà ngươi quyền uy khiến chúng không hại được hai ngươi. Với những Phép Lạ của TA, hai ngươi và những người theo hai ngươi chắc chắn sẽ thắng lợi."');
INSERT INTO "hasan_abdulkarim" VALUES(28,36,'28:36','Nhưng khi Musa đến gặp chúng-Fir-''awn và bè lũ của hắn với những Lời Mặc Khải rõ rệt của TA, chúng nói: “Đây chẳng qua là trò ảo thuật đã được bịa đặt và chúng ta chưa hề nghe chuyện này nơi tổ tiên của chúng ta.”');
INSERT INTO "hasan_abdulkarim" VALUES(28,37,'28:37','Và Musa bảo: “Thượng Đế của Ta biết rõ ai là người mang Chỉ Đạo từ Ngài đến và ai là người sẽ hưởng kết cuộc tốt đẹp của ngôi nhà (thiên đàng). Rõ thật, những kẻ làm điều sai quấy sẽ không thành đạt.”');
INSERT INTO "hasan_abdulkarim" VALUES(28,38,'28:38','Và Fir-''awn bảo: “Hỡi quân thần! Ta không biết có chúa nào khác của các ngươi ngoài ta ra. Này Haman, hãy đốt lò nung gạch cho trẫm và xây cho trẫm một cái tháp để trẫm đi lên (trời) gặp Thượng Đế của Musa và trẫm nghĩ rằng nó chỉ là một tên nói dối.”');
INSERT INTO "hasan_abdulkarim" VALUES(28,39,'28:39','Hắn (Fir-''awn) và bè lũ của hắn tỏ ra kiêu căng trong xứ không lý do chính đáng và nghĩ rằng chúng sẽ không bị đưa trở về gặp lại TA (để chịu sự Xét xử).');
INSERT INTO "hasan_abdulkarim" VALUES(28,40,'28:40','Bởi thế, TA đã túm bắt hắn và bè lũ của hắn rồi quẳng chúng xuống biển. Do đó, Ngươi hãy nhìn xem kết cuộc của những kẻ làm điều sai quấy như thế nào.');
INSERT INTO "hasan_abdulkarim" VALUES(28,41,'28:41','Và TA đã biến chúng thành những tên đầu đàn chuyên gọi (người ta) đi vào hỏa ngục. Và vào Ngày phục sinh, chúng sẽ không được (ai) giúp đỡ.');
INSERT INTO "hasan_abdulkarim" VALUES(28,42,'28:42','Và TA đã đính kèm những lời nguyền rủa theo bọn chúng suốt đời trên thế gian này; và vào Ngày phục sinh, chúng sẽ là những kẻ đáng ghê tởm.');
INSERT INTO "hasan_abdulkarim" VALUES(28,43,'28:43','Và sau khi tiêu diệt những thế hệ cũ, quả thật TA đã ban cho Musa Kinh Sách (Tawrah) dùng để soi sáng tâm linh cho nhân loại. (Nó) vừa là một Chỉ Đạo vừa là một Hồng Ân cho họ để may ra nhờ đó mà họ ghi nhớ.');
INSERT INTO "hasan_abdulkarim" VALUES(28,44,'28:44','Và Ngươi (hỡi Muhammad!) đã không ở phía tây (của ngọn núi Tur) khi TA quyết định công việc cho Musa và cũng không là một nhân chứng (cho biến cố đó).');
INSERT INTO "hasan_abdulkarim" VALUES(28,45,'28:45','Và TA đã cho sản sinh những thế hệ khác (sau đó) và cho họ sống thọ hơn; và Ngươi không phải là một người sống chung với người dân của Madyan và đọc nhắc họ về những Lời Mặc Khải của TA, nhưng chính TA là Đấng đã cử phái các Sứ Giả.');
INSERT INTO "hasan_abdulkarim" VALUES(28,46,'28:46','Và Ngươi cũng đã không ở bên sườn núi Tur khi TA gọi (Musa) nhưng Ngươi (được cử đến như) là một Hồng Ân từ Thượng Đế của Ngươi để Ngươi có thể cảnh báo một dân tộc mà trước Ngươi đã không có một vị Báo trước nào được cử đến với họ mục đích để cho họ ghi nhớ (hay thức tỉnh).');
INSERT INTO "hasan_abdulkarim" VALUES(28,47,'28:47','Và nếu (TA) không (gởi Ngươi đến với dân tộc Quraish này) thì e rằng khi chúng gặp tai họa do những điều (tội lỗi) mà bàn tay của chúng đã gây ra, chúng sẽ có lý do để trách (TA) rằng “Lạy Thượng Đế của bầy tôi! Tại sao Ngài không cử một Sứ Giả đến với bầy tôi? Bởi vì bầy tôi sẽ tuân theo những Lời Mặc Khải của Ngài và bầy tôi sẽ trở thành những người tin tưởng.”');
INSERT INTO "hasan_abdulkarim" VALUES(28,48,'28:48','Nhưng khi (Ngươi mang) sự Thật đến với chúng, chúng lại nói: “Tại sao Y (Muhammad) không được ban cho điều (Phép lạ) giống như điều (Phép lạ) đã được ban cho Musa?” Há chúng đã không tin tưởng nơi những điều đã được ban cho Musa trước đây hay sao? Chúng nói: “Hai loại phù phép này (Kinh Tawrah và Kinh Qur''an) hỗ trợ lẫn nhau!” Và chúng nói tiếp: “Chúng tôi không tin tất cả (các thứ đó).”');
INSERT INTO "hasan_abdulkarim" VALUES(28,49,'28:49','Hãy bảo chúng: “Thế các người hãy mang một Kinh Sách từ Allah đến làm Chỉ Đạo tốt hơn hai quyển (Tawrah và Qur''an) này và Ta (Muhammad) sẽ theo nó, nếu các người nói thật.”');
INSERT INTO "hasan_abdulkarim" VALUES(28,50,'28:50','Nhưng nếu chúng không trả lời Ngươi thì nên biết chúng chỉ tuân theo những điều mơ ước của chúng mà thôi. Và còn ai lầm lạc hơn kẻ chỉ tuân theo dục vọng của mình thay vì (tuân theo) Chỉ Đạo của Allah. Quả thật, Allah không hướng dẫn đám người làm điều sai quấy.');
INSERT INTO "hasan_abdulkarim" VALUES(28,51,'28:51','Và chắc chắn TA làm cho Lời nói (Qur''an) thấu đến chúng để may ra chúng ghi nhớ.');
INSERT INTO "hasan_abdulkarim" VALUES(28,52,'28:52','Những ai đã được TA ban cho Kinh Sách trước Nó (Qur''an) sẽ tin tưởng nơi Nó.');
INSERT INTO "hasan_abdulkarim" VALUES(28,53,'28:53','Và khi Nó được đọc ra cho họ thì họ nói ngay: “Chúng tôi tin tưởng nơi Nó.” Rõ ràng Nó là Chân Lý (đã được ban xuống) từ Thượng Đế của chúng tôi. Thật sự, chúng tôi là những người thần phục Allah (Muslim ) trước Nó (Qur''an).”');
INSERT INTO "hasan_abdulkarim" VALUES(28,54,'28:54','Họ sẽ được (Allah) ban thưởng hai lần bởi vì họ đã kiên nhẫn chịu đựng (sự thử thách) và họ dùng điều thiện tốt để xóa bỏ điều xấu và chi dùng của cải mà TA đã cung cấp cho họ (để làm việc thiện).');
INSERT INTO "hasan_abdulkarim" VALUES(28,55,'28:55','Và khi nghe chuyện tầm phào, họ cáo lui bỏ đi và nói: “Chúng tôi có công việc của chúng tôi và quí vị công việc của quí vị. Chúc quí vị được ''Bằng an''. Chúng tôi không tìm kiếm kẻ ngu dốt (để tranh chấp về Qur''an)”');
INSERT INTO "hasan_abdulkarim" VALUES(28,56,'28:56','Rõ thật, Ngươi (Muhammad) không hướng dẫn được ai mà Ngươi yêu thương, ngược lại Allah là Đấng hướng dẫn người nào Ngài muốn. Và Ngài biết rõ nhất những người theo Chính Đạo.');
INSERT INTO "hasan_abdulkarim" VALUES(28,57,'28:57','Và chúng (người Quraish) nói: “Nếu theo Chỉ Đạo cùng với Ngươi (Muhammad) thì bọn ta sẽ bị túm bắt đi mất khỏi xứ của bọn ta.” Há TA (Allah) đã không thiết lập cho chúng một thánh địa an toàn, mà đủ loại hoa quả được mang đến cho nó như là bổng lộc của TA hay sao? Nhưng đa số bọn chúng không biết gì.');
INSERT INTO "hasan_abdulkarim" VALUES(28,58,'28:58','Và có bao nhiêu thị trấn đã bị TA tiêu diệt vì (dân cư của) nó vui đùa hưởng lạc trong cuộc sống của chúng? Bởi thế, nhà cửa của chúng điêu tàn hoang phế sau khi đã bị tiêu diệt, chỉ trừ một số ít. Và chính TA là Đấng Thừa kế di sản của chúng.');
INSERT INTO "hasan_abdulkarim" VALUES(28,59,'28:59','Và Thượng Đế của Ngươi sẽ không tiêu diệt những thị trấn nào trừ phi Ngài dựng lên tại mỗi thủ phủ của chúng một Sứ Giả để đọc nhắc chúng về những Lời Mặc Khải của TA. Và TA cũng không tiêu diệt những thị trấn trừ phỉ dân cư của chúng là những kẻ làm điều sai quấy.');
INSERT INTO "hasan_abdulkarim" VALUES(28,60,'28:60','Và những vật đã được ban cho các ngươi chỉ là những vật hưởng tạm và món trang sức của đời sống trần tục này. Và thứ gì với Allah (ở đời sau) sẽ tốt nhất và vĩnh viễn. Thế các người không hiểu ư?');
INSERT INTO "hasan_abdulkarim" VALUES(28,61,'28:61','Thế thì một người mà TA đã hứa bằng một Lời Hứa tốt đẹp (thiên đàng) mà y sẽ tiếp nhận (ở đời sau) có giống với kẻ mà TA đã cho hưởng lạc tạm bợ ở trần gian này, rồi vào Ngày phục sinh y sẽ được dựng sống lại để chịu tội (trong hỏa ngục)?');
INSERT INTO "hasan_abdulkarim" VALUES(28,62,'28:62','Và vào Ngày mà Ngài (Allah) sẽ gọi chúng đến chất vấn, rồi Ngài sẽ phán: “Đâu là những đối tác ngang vai của TA mà các ngươi đã từng xác nhận trước đây?”');
INSERT INTO "hasan_abdulkarim" VALUES(28,63,'28:63','Những kẻ mà Lời (kết tội) đã chứng minh đúng sẽ lên tiếng thưa: “Lạy Thượng Đế của bầy tôi! Đây là những kẻ mà bầy tôi đã dắt đi lạc. Bầy tôi dắt chúng đi giống như việc tự bản thân chúng tôi đi lạc; bầy tôi vô can với chúng trước mặt Ngài; không phải chúng đã thờ phụng bầy tôi."');
INSERT INTO "hasan_abdulkarim" VALUES(28,64,'28:64','Và có lời phán bảo: “Hãy gọi những thần linh của các người đi!” Và chúng sẽ gọi chúng; nhưng không ai trả lời chúng và chúng sẽ thấy hình phạt trước mặt chúng. Phải chi chúng tuân theo Chỉ Đạo (thì đâu đến nỗi bị phạt hỏa ngục!)');
INSERT INTO "hasan_abdulkarim" VALUES(28,65,'28:65','Và vào Ngày (Allah) sẽ gọi chúng đến chất vấn, rồi phán: “Đâu là điều mà các ngươi đã trả lời các Sứ Giả (của TA)"');
INSERT INTO "hasan_abdulkarim" VALUES(28,66,'28:66','Nhưng vào Ngày đó, lý lẽ biện bạch của chúng sẽ mờ mịt. Bởi thế, chúng không thể hỏi han vấn kế nhau được.');
INSERT INTO "hasan_abdulkarim" VALUES(28,67,'28:67','Nhưng đối với ai biết hối cải, và tin tưởng và làm việc thiện (ở đời này) thì may ra sẽ là một trong những người thành đạt.');
INSERT INTO "hasan_abdulkarim" VALUES(28,68,'28:68','Và Thượng Đế (Allah) của Ngươi (Muhammad) tạo hóa và chọn lựa bất cứ vật gì Ngài muốn. Chúng không có một sự chọn lựa nào (trong quyết định của Ngài). Quang vinh và trong sạch thay Allah! Và Ngài Tối Cao, vượt hẳn những kẻ mà chúng đã tổ hợp với Ngài.');
INSERT INTO "hasan_abdulkarim" VALUES(28,69,'28:69','Và Thượng Đế của Ngươi biết rõ điều chúng giấu giếm trong lòng và điều chúng bộc lộ ra ngoài.');
INSERT INTO "hasan_abdulkarim" VALUES(28,70,'28:70','Và Ngài là Allah. Không có Thượng Đế nào khác cả duy chỉ Ngài (là Thượng Đế). Mọi lời ca tụng vào lúc đầu (đời này) và vào lúc cuối (đời sau) đều thuộc về Ngài cả. Ngài nắm mọi quyết định Xét Xử; và các ngươi sẽ được đưa trở về gặp Ngài.');
INSERT INTO "hasan_abdulkarim" VALUES(28,71,'28:71','Hãy bảo (chúng): “Các người hãy nhận xét, nếu Allah làm cho ban đêm kéo dài vĩnh viễn cho các người cho đến Ngày phục sinh thì Ai là Thượng Đế ngoài Allah sẽ mang ánh sáng lại cho các người? Thế các người có nghe không?”');
INSERT INTO "hasan_abdulkarim" VALUES(28,72,'28:72','Hãy bảo (chúng): “Các người hãy nhận xét! Nếu Allah làm cho ban ngày kéo dài vĩnh viễn cho các người cho đến Ngày phục sinh thì Ai là Thượng Đế ngoài Allah sẽ mang bóng tối đến cho các người để các người có thể nghỉ ngơi trong đó? Các người có thấy không?”');
INSERT INTO "hasan_abdulkarim" VALUES(28,73,'28:73','"Và chính do Đức Khoan Dung của Ngài mà Ngài đã làm ra ban đêm và ban ngày cho các người để các người có thể nghỉ ngơi (vào ban đêm) và tìm kế sinh nhai (vào ban ngày); và để cho các người tạ ơn Ngài."');
INSERT INTO "hasan_abdulkarim" VALUES(28,74,'28:74','Và vào Ngày mà Ngài sẽ gọi chúng đến chất vấn, rồi phán: “Đâu là những đối tác ngang vai của TA mà các ngươi đã từng xác nhận?”');
INSERT INTO "hasan_abdulkarim" VALUES(28,75,'28:75','(Cũng vào Ngày đó) TA sẽ đưa ra từ mỗi cộng đồng một nhân chứng rồi TA sẽ phán: “Hãy trưng bằng chứng của các ngươi ra đây” Thì lúc đó chúng sẽ biết rằng Allah nắm hết mọi sự thật và những điều (giả tạo) mà chúng đã từng bịa đặt sẽ bỏ chúng đi mất dạng.');
INSERT INTO "hasan_abdulkarim" VALUES(28,76,'28:76','Quả thật, Qorun xuất thân từ đám dân của Musa nhưng y đối xử ngạo mạn với họ. Và TA đã ban cho y kho tàng châu báu (nhiều đến mức) cần phải nhờ hàng trung đội người vạm vỡ mới vác nổi những chìa khóa kho. (Hãy nhớ lại) khi người dân của y bảo y: "Chớ cuống quýt mừng rỡ. Thật sự, Allah không yêu thương những kẻ mừng cuống quýt (với sự giàu có của mình)."');
INSERT INTO "hasan_abdulkarim" VALUES(28,77,'28:77','“Mà hãy dùng của cải mà Allah đã ban cho ngươi để tìm kiếm một Ngôi nhà ở đời sau và chớ quên phần (việc) của ngươi ở đời này và hãy làm tốt (với người khác) giống như việc Allah đối xử tốt với ngươi và chớ tìm cách làm điều thối nát trong xứ. Quả thật, Allah không yêu thương những kẻ làm điều tàn ác thối nát.');
INSERT INTO "hasan_abdulkarim" VALUES(28,78,'28:78','Y (Qorun) đáp: "Ta được giàu có là do sự hiểu biết của ta.” Phải chăng y đã không biết việc Allah đã tiêu diệt ai là người đã có nhiều quyền lực và giàu có hơn y nơi những thế hệ trước y hay sao? Và những kẻ tội lỗi sẽ không bị hỏi (ngay) về những tội lỗi của chúng.');
INSERT INTO "hasan_abdulkarim" VALUES(28,79,'28:79','Rồi y ra ngoài gặp dân chúng phô trương vẻ lộng lẫy của mình. Những kẻ ham muốn đời sống trần tục này (thấy vẻ lộng lẫy đó) vội lên tiếng: “Ôi, ước chi chúng tôi được ban cho của cải giống như tài sản đã được ban cho Qorun. Quả thật, y đã làm chủ một kho tàng vĩ đại.”');
INSERT INTO "hasan_abdulkarim" VALUES(28,80,'28:80','Nhưng những người được (Allah) ban cho kiến thức lại nói: “Chỉ khổ thân quí vị thôi. Bởi vì phần thưởng của Allah (ở đời sau) tốt hơn cho ai có đức tin và làm việc thiện và chỉ những người kiên nhẫn mới đạt được phần thưởng đó.”');
INSERT INTO "hasan_abdulkarim" VALUES(28,81,'28:81','Bởi thế, TA đã làm cho đất sụp xuống nuốt mất y lẫn nhà cửa của y. Và y không có được một bè nhóm nào giải cứu y thoát khỏi hình phạt của Allah và y cũng không thể tự cứu mình được.');
INSERT INTO "hasan_abdulkarim" VALUES(28,82,'28:82','Và những kẻ đã mơ ước một địa vị giống như địa vị của y ngày hôm qua nay bắt đầu nói: “Ôi! Quả thật Allah nới rộng và thu hẹp bổng lộc của Ngài cho người nào Ngài muốn trong số bầy tôi của Ngài; nếu Allah không nhân từ đối với bầy tôi thì Ngài đã làm cho đất sụp xuống nuốt mất bầy tôi rồi. Ôi! Những kẻ không có đức tin chắc chắn sẽ không thành đạt.”');
INSERT INTO "hasan_abdulkarim" VALUES(28,83,'28:83','Đó là Ngôi nhà (hạnh phúc) ở đời sau mà TA đã làm ra cho những ai không muốn tự suy tôn mình cao cả và cũng không làm những điều thối nát trên trái đất. Và kết cuộc tốt đẹp sẽ về tay của những người ngay chính.');
INSERT INTO "hasan_abdulkarim" VALUES(28,84,'28:84','Ai mang theo điều thiện tốt thì sẽ được phần thưởng tốt hơn cái đó; và ai mang theo điều xấu thì những ai làm điều xấu chỉ sẽ bị phạt ngang bằng với điều xấu mà họ đã làm.');
INSERT INTO "hasan_abdulkarim" VALUES(28,85,'28:85','Quả thật, Đấng đã ban cho Ngươi Qur''an sẽ đưa Ngươi trở lại cố hương (hoặc là Makkah hoặc là thiên đàng). Hãy bảo: “ Thượng Đế (Allah) của ta biết rõ ai là người mang Chỉ Đạo (của Ngài) đến và ai là kẻ lầm đường lạc lối.”');
INSERT INTO "hasan_abdulkarim" VALUES(28,86,'28:86','Và Ngươi đã không mơ ước việc Kinh Sách (Qur''an) được ban xuống cho Ngươi nhưng đó là một Hồng Ân từ Thượng Đế (Allah) của Ngươi; bởi thế, chớ bao giờ tán trợ những kẻ không có đức tin (phủ nhận Allah).');
INSERT INTO "hasan_abdulkarim" VALUES(28,87,'28:87','Và chớ để chúng (kẻ không tin) làm cho Ngươi từ bỏ những Lời Mặc Khải của Allah sau khi chúng đã được ban xuống cho Ngươi mà hãy kêu gọi (nhân loại) đến với Thượng Đế (Allah) của Ngươi và chớ trở thành một người tôn thờ đa thần .');
INSERT INTO "hasan_abdulkarim" VALUES(28,88,'28:88','Và chớ bao giờ cầu nguyện một thần linh nào khác cùng với Allah (bởi vì) không có Thượng Đế nào khác cả duy chỉ Ngài (là Thượng Đế). Mọi vật đều tiêu tan chỉ trừ sắc Diện của Ngài. Ngài nắm quyền Xét Xử và tất cả các ngươi sẽ được đưa về gặp Ngài trở lại (để chịu sự Phán Xử).');
INSERT INTO "hasan_abdulkarim" VALUES(29,1,'29:1','Alif. Lam. Mim.');
INSERT INTO "hasan_abdulkarim" VALUES(29,2,'29:2','Phải chăng thiên hạ nghĩ rằng mình sẽ được yên thân khi nói: “Chúng tôi tin tưởng” và sẽ không bị thử thách hay sao?');
INSERT INTO "hasan_abdulkarim" VALUES(29,3,'29:3','Và quả thật, TA đã thử thách những người trước họ để Allah làm cho thấy rõ ai là người chân thật và ai là kẻ gian dối.');
INSERT INTO "hasan_abdulkarim" VALUES(29,4,'29:4','Hoặc phải chăng những ai làm điều tội lỗi nghĩ rằng chúng có thể qua mặt được TA hay sao? Điều mà họ xét đoán thật vô cùng tệ hại.');
INSERT INTO "hasan_abdulkarim" VALUES(29,5,'29:5','Ai hy vọng sẽ trở về gặp lại Allah thì thời hạn của Allah sẽ đến bởi vì Ngài là Đấng Hằng Nghe, Đấng Hằng Biết.');
INSERT INTO "hasan_abdulkarim" VALUES(29,6,'29:6','Và ai phấn đấu thì y chỉ phấn đấu cho bản thân (linh hồn) mình. Rõ thật, Allah Tự Đầy Đủ, không nhờ vả đến thế gian.');
INSERT INTO "hasan_abdulkarim" VALUES(29,7,'29:7','Và những ai tin tưởng và làm việc thiện thì sẽ được TA xóa bỏ những điều xấu xa (tội lỗi) và sẽ được TA tưởng thưởng tùy theo những điều tốt nhất mà họ đã từng làm.');
INSERT INTO "hasan_abdulkarim" VALUES(29,8,'29:8','Và TA đã truyền lệnh cho con người hãy ăn ở tử tế với cha mẹ. Nhưng nếu cha mẹ đấu tranh với ngươi để buộc ngươi tổ hợp với TA những kẻ mà ngươi không hề biết một tí gì thì chớ nghe lời của hai người (cha mẹ). Và các ngươi sẽ trở lại gặp TA, rồi TA sẽ cho các ngươi biết về những điều các ngươi đã từng làm.');
INSERT INTO "hasan_abdulkarim" VALUES(29,9,'29:9','Và những ai tin tưởng và làm việc thiện thì chắc chắn sẽ được TA thu nhận vào đoàn thể của những người ngoan đạo.');
INSERT INTO "hasan_abdulkarim" VALUES(29,10,'29:10','Và trong nhân loại đã có những kẻ nói: “Chúng tôi tin tưởng nơi Allah.” nhưng khi vì Allah mà chúng chịu khổ thì chúng lại cho cảnh người áp bức người là một hình phạt của Allah. Và nếu có sự trợ giúp đến từ Thượng Đế của Ngươi thì chúng lại nói: “Quả thật chúng tôi luôn luôn sát cánh với anh em.” Há Allah không biết rõ những điều nằm trong lòng của thiên hạ hay sao?');
INSERT INTO "hasan_abdulkarim" VALUES(29,11,'29:11','Và chắc chắn, Allah biết ai là những người tin tưởng và biết chắc chắn ai là những tên giả tạo đức tin.');
INSERT INTO "hasan_abdulkarim" VALUES(29,12,'29:12','Và những kẻ không có đức tin nói với những người có đức tin như sau: “Hãy theo con đường của chúng tôi và chúng tôi sẽ gánh chịu tội lỗi của quí vị.” Nhưng chúng sẽ không gánh một chút tội nào của họ cả. Chắc chắn, chúng là những kẻ nói dối.');
INSERT INTO "hasan_abdulkarim" VALUES(29,13,'29:13','Và chắc chắn, chúng sẽ vác gánh nặng (tội lỗi) của chúng và những gánh nặng ngoài gánh nặng của chúng. Và chắc chắn, chúng sẽ bị (Allah) hỏi (tội) vào Ngày phục sinh về những điều mà chúng đã từng bịa đặt.');
INSERT INTO "hasan_abdulkarim" VALUES(29,14,'29:14','Và chắc chắn, TA đã cử Nuh đến với người dân của Y. Và Y sống với họ thiếu năm mươi năm nữa là đủ một ngàn năm. Rồi, trận Đại Hồng Thủy túm bắt chúng trong lúc chúng là những kẻ làm điều sai quấy.');
INSERT INTO "hasan_abdulkarim" VALUES(29,15,'29:15','Rồi, TA đã giải cứu Y và những tín đồ theo Y trên một chiếc tàu và lấy đó làm một bài học cho thế gian.');
INSERT INTO "hasan_abdulkarim" VALUES(29,16,'29:16','Và (hãy nhớ) Ibrahim, khi Y bảo người dân của Y như sau: “Hãy thờ phụng Allah và hãy sợ Ngài. Điều đó tốt nhất cho các ngươi nếu các người biết."');
INSERT INTO "hasan_abdulkarim" VALUES(29,17,'29:17','“Các người chỉ thờ phụng những hình tượng thay vì Allah và các người chỉ bịa đặt chuyện hoang đường. Thật sự, những kẻ mà các người thờ phụng không có khả năng ban bổng lộc cho các người. Do đó, hãy tìm bổng lộc nơi Allah và thờ phụng Ngài và tạ ân Ngài; và các người sẽ được đưa về gặp Ngài trở lại."');
INSERT INTO "hasan_abdulkarim" VALUES(29,18,'29:18','“Và nếu các người cho (ta) nói dối thì chắc chắn những cộng đồng trước các người cũng đã cho (các Sứ Giả của họ) nói dối như thế; và nhiệm vụ của một Sứ Giả chỉ là làm công tác truyền đạt một cách công khai.”');
INSERT INTO "hasan_abdulkarim" VALUES(29,19,'29:19','Há họ đã không nhận xét việc Allah bắt đầu việc tạo hóa rồi phục hồi nó như thế nào ư? Điều đó quả thật dễ dàng đối với Allah.');
INSERT INTO "hasan_abdulkarim" VALUES(29,20,'29:20','Hãy bảo (chúng): “Hãy đi du lịch trên trái đất để quan sát (Allah) khởi sự việc tạo hóa rồi Allah phục sinh đời sau như thế nào. Quả thật, Allah là Đấng Toàn Năng trên mọi thứ.”');
INSERT INTO "hasan_abdulkarim" VALUES(29,21,'29:21','Ngài trừng phạt kẻ nào Ngài muốn và khoan dung người nào Ngài muốn. Và các ngươi sẽ được đưa về gặp Ngài trở lại.');
INSERT INTO "hasan_abdulkarim" VALUES(29,22,'29:22','Và dù ở trên trời hay dưới đất các ngươi sẽ không thể trốn thoát khỏi (Allah) đặng và ngoài Allah (các ngươi) sẽ không có một vị bảo hộ hay một vị cứu tinh nào.');
INSERT INTO "hasan_abdulkarim" VALUES(29,23,'29:23','Và những ai phủ nhận những dấu hiệu của Allah và việc gặp lại Ngài (ở đời sau) thì là những người sẽ tuyệt vọng nơi Đức Khoan Dung của TA và sẽ nhận một sự trừng phạt đau đớn.');
INSERT INTO "hasan_abdulkarim" VALUES(29,24,'29:24','Và người dân của Y (Ibrahim) không trả lời điều nào khác ngoài điều chúng nói: “Hãy giết y hoặc thiêu sống y!” Nhưng Allah đã cứu sống Y ra khỏi lửa. Quả thật, trong (câu chuyện) đó (về Ibrahim) là những dấu hiệu cho đám người tin tưởng.');
INSERT INTO "hasan_abdulkarim" VALUES(29,25,'29:25','Và (Ibrahim) bảo: “Các người đã tôn thờ những hình tượng thay vì Allah là vì các người yêu thương lẫn nhau ở đời này; rồi vào Ngày phục sinh, các người sẽ phủ nhận và nguyền rủa lẫn nhau và nhà ở cuối cùng của các người sẽ là hỏa ngục và các người sẽ không được ai giúp đỡ.”');
INSERT INTO "hasan_abdulkarim" VALUES(29,26,'29:26','Riêng Lut thì tin nơi Y (Ibrahim). Và Lut bảo: "Tôi sẽ dời cư về với Thượng Đế của tôi. Bởi vì quả thật, Ngài là Đấng Toàn Năng, Đấng Chí Minh."');
INSERT INTO "hasan_abdulkarim" VALUES(29,27,'29:27','Và TA đã ban cho Y (Ibrahim đứa con trai) Is-haaq và (đứa cháu nội) Ya''qub và làm cho con cháu của Y tiếp thu điều mặc khải vài Kinh Sách và ban cho Y phần thưởng ở trần thế; và ở đời sau, Y sẽ thật sự gia nhập đoàn người hiền lương.');
INSERT INTO "hasan_abdulkarim" VALUES(29,28,'29:28','Và (hãy nhớ) Lut, khi Y bảo người dân của Y: "Các người đã làm một điều hết sức khả ố mà chưa ai trong thiên hạ đã từng làm trước các người."');
INSERT INTO "hasan_abdulkarim" VALUES(29,29,'29:29','“Phải chăng các người đã giao hợp với đàn ông và chuyên đánh cướp xa lộ và làm những điều sàm sỡ trong những cuộc hội họp?” Nhưng người dân của Y không trả lời điều nào khác ngoài điều họ nói: “Ông hãy mang hình phạt của Allah đến (trừng phạt) chúng tôi đi nếu ông là một người nói thật.”');
INSERT INTO "hasan_abdulkarim" VALUES(29,30,'29:30','Người (Lut) thưa: “Lạy Thượng Đế của bề tôi! Xin Ngài trợ giúp bề tôi thắng đám người thối nát này.”');
INSERT INTO "hasan_abdulkarim" VALUES(29,31,'29:31','Và khi các Thiên Sứ (Thiên Thần) của TA đến gặp Ibrahim với tin mừng, bảo: “Chúng tôi sắp tiêu diệt dân cư của thị trấn (Sô-đôm của Palestine) này (của Lut.) Quả thật, dân cư của nó là những kẻ làm điều sai quấy.”');
INSERT INTO "hasan_abdulkarim" VALUES(29,32,'29:32','Ibrahin đáp: “Nhưng Lut ở trong đó.” Họ đáp: “Chúng tôi biết rõ ai ở trong đó. Chắc chắn, chúng tôi sẽ giải cứu Y và gia đình của Y ngoại trừ bà vợ của Y. Bà ta là một trong những kẻ trì trệ đằng sau.”');
INSERT INTO "hasan_abdulkarim" VALUES(29,33,'29:33','Và khi các Thiên Sứ của TA đến gặp Lut, Y lo âu giùm cho người dân của Y và cảm thấy bất lực. Và họ bảo: “Ngươi chớ lo sợ và chớ buồn phiền. Thật sự chúng tôi sẽ giải cứu Ngươi và gia đình Ngươi ngoại trừ bà vợ của Ngươi. Bà ấy là một người trì trệ đằng sau.”');
INSERT INTO "hasan_abdulkarim" VALUES(29,34,'29:34','Thật sự, chúng tôi sắp mang một hình phạt từ trên trời xuống trừng phạt dân cư của thị trấn này bởi vì chúng là những kẻ dấy loạn bất tuân Allah.');
INSERT INTO "hasan_abdulkarim" VALUES(29,35,'29:35','Và chắc chắn, TA đã để lại một dấu tích công khai( của việc trừng phạt đó cho những người biết suy nghĩ.');
INSERT INTO "hasan_abdulkarim" VALUES(29,36,'29:36','Và với người dân Madyan, TA đã cử người anh em Shu''aib của họ đến với họ. Shu''aib bảo: "Hỡi dân ta! Hãy thờ phụng Allah và sợ Ngày Cuối Cùng; và chớ gây tội ác trên trái đất như những kẻ thối nát."');
INSERT INTO "hasan_abdulkarim" VALUES(29,37,'29:37','Nhưng họ cho Y (Shu’aib) nói dối cho nên một trận động đất dữ dội đã chụp bắt họ chết la liệt trong nhà vào buổi sáng.');
INSERT INTO "hasan_abdulkarim" VALUES(29,38,'29:38','Và người dân ''Ad và Thamud (cũng thế). Nhà cửa đổ nát của họ hãy còn là một dấu tích rành rành cho các ngươi về họ. Shaytan đã biến việc làm của họ thành hấp dẫn đối với họ cho nên nó đã giữ họ xa con đường (của Allah) trong lúc họ là những người nhìn thấu đáo.');
INSERT INTO "hasan_abdulkarim" VALUES(29,39,'29:39','Và Qãrũn và Fir-a’wn và Hãmãn (cũng thế). Và chắc chắn Mũsa đã đến gặp chúng với những bằng chứng, nhưng chúng tỏ thái độ ngạo mạn trên trái đất. Nhưng chúng vẫn không thoát khỏi TA.');
INSERT INTO "hasan_abdulkarim" VALUES(29,40,'29:40','TA đã bắt phạt tất cả bọn chúng tùy theo tội của từng đứa. Trong bọn chúng, có kẻ đã bị TA trừng phạt bằng một trận cuồng phong có mưa đá (như đối với người dân của Lut); có kẻ đã bị TA trừng phạt bằng một tiếng thét dữ dội (như đối với người dân Thamud); có kẻ đã bị TA trừng phạt bằng cách làm cho đất sụp xuống (như Qorun); và có kẻ đã bị TA trừng phạt bằng cách nhận chìm dưới nước (như Fir-''awn và Haman). Và Allah không phải là Đấng đã hãm hại chúng mà chính chúng đã tự làm hại bản thân mình.');
INSERT INTO "hasan_abdulkarim" VALUES(29,41,'29:41','Hình ảnh của những ai tôn thờ những chủ nhân khác thay vì Allah chẳng khác nào hình ảnh của con nhện tự giăng tơ làm nhà. Quả thật, mỏng manh nhất trong các loại nhà chỉ có nhà của con nhện, nếu chúng biết điều đó.');
INSERT INTO "hasan_abdulkarim" VALUES(29,42,'29:42','Quả thật, Allah biết rõ bất cứ cái gì chúng cầu nguyện thay vì Ngài. Bởi vì Ngài là Đấng Toàn Năng, Đấng Chí Minh.');
INSERT INTO "hasan_abdulkarim" VALUES(29,43,'29:43','Và đó là những hình ảnh thí dụ mà TA trình bày cho nhân loại. Và chỉ những người có kiến thức mới thấu hiểu được (ý nghĩa của) chúng.');
INSERT INTO "hasan_abdulkarim" VALUES(29,44,'29:44','Allah vì Chân Lý đã tạo hóa các tầng trời và trái đất. Quả thật, trong (việc tạo hóa) đó là một dấu hiệu cho những người có đức tin.');
INSERT INTO "hasan_abdulkarim" VALUES(29,45,'29:45','Hãy đọc những điều đã được mặc khải cho Ngươi trong Kinh Sách (Qur''an) và hãy dâng lễ Salah một cách chu đáo. Quả thật, lễ nguyện Salah ngăn cản một người tránh xa trọng tội và điều tội lỗi. Và việc tưởng nhớ Allah là điều lớn nhất. Và Allah biết rõ những điều các ngươi làm.');
INSERT INTO "hasan_abdulkarim" VALUES(29,46,'29:46','Và chớ tranh luận với người dân Kinh Sách trừ phi với thái độ nhã nhặn và chỉ với những người của họ làm điều sai quấy và bảo họ: “Chúng tôi tin tưởng nơi điều đã được ban xuống cho chúng tôi và nơi điều đã được ban xuống cho quí vị. Và Thượng Đế của chúng tôi lẫn Thượng Đế của quí vị chỉ là Một (Thượng Đế). Và chúng tôi là những người Muslim thần phục Ngài.');
INSERT INTO "hasan_abdulkarim" VALUES(29,47,'29:47','Và TA đã ban Kinh Sách (Qur''an) xuống cho Ngươi đúng như thế. Bởi thế, những ai mà TA đã ban cho Kinh Sách tin tưởng nơi Nó (Qur''an) và một số người (Ả Rập) này cũng tin tưởng nơi Nó. Và chỉ những kẻ không tin mới cãi lại những Lời Mặc Khải của TA.');
INSERT INTO "hasan_abdulkarim" VALUES(29,48,'29:48','Và trước Nó (Qur''an), Ngươi đã không hề đọc một Kinh Sách nào và cũng không viết được một Kinh Sách nào với tay phải của Ngươi; bởi vì trong trường hợp đó, những kẻ theo ngụy giáo sẽ đâm ra nghi ngờ (về Nó).');
INSERT INTO "hasan_abdulkarim" VALUES(29,49,'29:49','Không! Nó (Qur''an) gồm những Lời Mặc Khải rõ ràng ở trong lòng của những ai đã được (Allah) ban cho kiến thức. Và chỉ những kẻ làm điều sai quấy mới cãi lại những Lời Mặc Khải của TA.');
INSERT INTO "hasan_abdulkarim" VALUES(29,50,'29:50','Và chúng nói: ‘Tại sao Thượng Đế của Y (Muhammad) đã không ban cho Y những Phép Lạ?” Hãy đáp: “Chỉ Allah mới có những Phép Lạ. Ta chỉ là một người Báo trước công khai."');
INSERT INTO "hasan_abdulkarim" VALUES(29,51,'29:51','Há không đủ cho chúng hay sao việc TA đã ban Kinh Sách xuống cho Ngươi để (Nó) được đọc lại cho chúng? Quả thật, trong đó là Hồng Ân và điều cảnh báo cho đám người tin tưởng.');
INSERT INTO "hasan_abdulkarim" VALUES(29,52,'29:52','Hãy bảo chúng: “Allah đủ làm chứng giữa ta (Muhammad) và các người. Ngài biết mọi điều trong các tầng trời và trái đất. Và những ai tin tưởng nơi điều gian dối, và không tin tưởng nơi Allah mới là những kẻ sẽ thua thiệt."');
INSERT INTO "hasan_abdulkarim" VALUES(29,53,'29:53','Và chúng hối thúc Ngươi mang hình phạt đến cho chúng. Và nếu không vì một thời hạn nhất định đã được qui định cho chúng thì hình phạt chắc chắn đã đến với chúng rồi. Và chắc chắn, nó sẽ bất ngờ xảy đến cho chúng trong lúc chúng không nhận thấy.');
INSERT INTO "hasan_abdulkarim" VALUES(29,54,'29:54','Chúng hối thúc Ngươi mang hình phạt đến. Và rõ thật hỏa ngục sắp bao vây những kẻ không tin.');
INSERT INTO "hasan_abdulkarim" VALUES(29,55,'29:55','Và vào Ngày mà hình phạt sẽ bao vây chúng từ bên trên xuống và từ dưới chân lên với Lời phán: “Hãy nếm (hậu quả của) những điều (tội lỗi) mà các ngươi đã từng làm.”');
INSERT INTO "hasan_abdulkarim" VALUES(29,56,'29:56','“Hỡi các bầy tôi có đức tin của TA. Trái đất của TA rộng bao la. Bởi thế, hãy thờ phụng chỉ riêng TA thôi!”');
INSERT INTO "hasan_abdulkarim" VALUES(29,57,'29:57','Mỗi linh hồn sẽ phải nếm cái chết. Rồi các ngươi sẽ được đưa về gặp TA trở lại.');
INSERT INTO "hasan_abdulkarim" VALUES(29,58,'29:58','Và những ai có đức tin và làm việc thiện thì chắc chắn sẽ được TA ban thưởng những ngôi nhà cao sang trong thiên đàng bên dưới có các dòng sông chảy, để vào ở trong đó đời đời. Ân phúc thay phần thưởng dành cho những người làm việc (tốt).');
INSERT INTO "hasan_abdulkarim" VALUES(29,59,'29:59','Những ai kiên trì và phó thác cho Thượng Đế của họ.');
INSERT INTO "hasan_abdulkarim" VALUES(29,60,'29:60','Và có nhiều sinh vật đã không tự vác lấy lương thực của mình. Allah cung dưỡng chúng và cung dưỡng các ngươi bởi vì Ngài là Đấng Hằng Nghe, Đấng Hằng Biết.');
INSERT INTO "hasan_abdulkarim" VALUES(29,61,'29:61','Và nếu Ngươi có hỏi chúng ai đã tạo hóa các tầng trời và trái đất và đã chế ngự mặt trời và mặt trăng thì chắc chắn chúng sẽ đáp: “Allah!” Thế tại sao chúng lánh xa Ngài?');
INSERT INTO "hasan_abdulkarim" VALUES(29,62,'29:62','Allah nới rộng và thu hẹp bổng lộc cho người nào Ngài muốn trong số bầy tôi của Ngài; bởi vì quả thật, Allah biết hết mọi việc.');
INSERT INTO "hasan_abdulkarim" VALUES(29,63,'29:63','Và nếu Ngươi có hỏi chúng ai đã ban nước mưa từ trên trời xuống mà Ngài dùng để làm sống lại lớp đất chết khô thì chắc chắn chúng sẽ đáp: “Allah!” Hãy bảo chúng: “Mọi lời ca tụng đều dâng lên Allah!” Không! Đa số bọn chúng không hiểu.');
INSERT INTO "hasan_abdulkarim" VALUES(29,64,'29:64','Và đời sống trần tục này chỉ là một thú tiêu khiển và trò chơi. Và nhà ở ở đời sau mới là đời sống thực sự, nếu chúng biết điều đó.');
INSERT INTO "hasan_abdulkarim" VALUES(29,65,'29:65','Bởi thế, khi chúng lên tàu (ra khơi), chúng cầu nguyện Allah, dâng trọn đức tin cho Ngài. Nhưng khi Ngài cứu chúng lên bờ an toàn thì chúng dựng những đối tác ngang vai cùng với Ngài.');
INSERT INTO "hasan_abdulkarim" VALUES(29,66,'29:66','(Làm thế) để chúng có thể phụ ân mà TA đã ban cho chúng và để chúng tự do hưởng thụ (cuộc đời). Nhưng rồi chúng sẽ sớm biết.');
INSERT INTO "hasan_abdulkarim" VALUES(29,67,'29:67','Há chúng không thấy việc TA đã làm cho (Makkah) thành một thánh địa an ninh bất khả xâm phạm trong lúc thiên hạ xung quanh chúng bị bắt cóc đi mất tích? Thế, phải chăng chúng tin tưởng nơi điều gian dối và phủ nhận những ân huệ của Allah hay sao?');
INSERT INTO "hasan_abdulkarim" VALUES(29,68,'29:68','Và còn ai sai quấy hơn kẻ đã đặt điều nói dối rồi đổ thừa cho Allah và phủ nhận Chân Lý khi nó đến với y? Phải chăng trong hỏa ngục sẽ có một chỗ ở cho những kẻ vô đức tin?');
INSERT INTO "hasan_abdulkarim" VALUES(29,69,'29:69','Ngược lại, những ai chiến đấu cho (Chính Nghĩa của) TA thì chắc chắn sẽ được hướng dẫn đến những con đường của TA. Và chắc chắn, Allah đang sát cánh với những người làm tốt.');
INSERT INTO "hasan_abdulkarim" VALUES(30,1,'30:1','Alif. Lam. Mim.');
INSERT INTO "hasan_abdulkarim" VALUES(30,2,'30:2','Những người La Mã( đã bị đánh bại.');
INSERT INTO "hasan_abdulkarim" VALUES(30,3,'30:3','Tại một lãnh thổ gần kề(; nhưng sau cuộc chiến bại, họ sẽ chiến thắng trở lại.');
INSERT INTO "hasan_abdulkarim" VALUES(30,4,'30:4','Trong một thời gian ngắn. Đó là công việc của Allah trước và sau. Và vào ngày đó, những người tin tưởng sẽ vui mừng,');
INSERT INTO "hasan_abdulkarim" VALUES(30,5,'30:5','Về sự trợ giúp của Allah. Ngài giúp đỡ người nào Ngài muốn (để cho họ chiến thắng) bởi vì Ngài là Đấng Toàn Năng, Đấng Bao Dung.');
INSERT INTO "hasan_abdulkarim" VALUES(30,6,'30:6','(Đó là) Lời Hứa của Allah và Ngài không hề thất hứa trong Lời Hứa của Ngài, nhưng đa số thiên hạ không biết.');
INSERT INTO "hasan_abdulkarim" VALUES(30,7,'30:7','Họ chỉ biết hình thức bề ngoài của đời sống trần tục và lơ là về đời sau.');
INSERT INTO "hasan_abdulkarim" VALUES(30,8,'30:8','Há họ không chịu suy nghĩ trong tâm trí của họ về việc Allah đã tạo hóa các tầng trời và trái đất và vạn vật giữa trời đất chỉ vì Chân Lý và trong một thời hạn nhất định hay sao? Và quả thật, đa số nhân loại không tin việc gặp gỡ Thượng Đế của họ (vào Ngày phục sinh).');
INSERT INTO "hasan_abdulkarim" VALUES(30,9,'30:9','Há họ không chịu đi du lịch trên trái đất để nhìn xem kết cuộc của những ai sống trước họ đã xảy ra như thế nào ư? (Những người đó) mạnh hơn họ về quyền lực và khai khẩn đất đai và sinh sống nơi đó đông đảo và giàu có hơn những thứ mà những người này (Quraish) đã làm; và đã có những Sứ Giả của họ đến gặp họ với những bằng chứng rõ rệt. Bởi thế, Allah đã không hãm hại họ mà chính họ đã tự làm hại bản thân mình.');
INSERT INTO "hasan_abdulkarim" VALUES(30,10,'30:10','Rồi cuối cùng, những ai làm điều xấu sẽ lại gặp điều xấu bởi vì họ đã phủ nhận các dấu hiệu của Allah và đã mang chúng ra làm trò cười.');
INSERT INTO "hasan_abdulkarim" VALUES(30,11,'30:11','Allah khởi sự việc tạo hóa rồi phục hồi nó, rồi các ngươi sẽ được đưa về gặp Ngài trở lại.');
INSERT INTO "hasan_abdulkarim" VALUES(30,12,'30:12','Và vào Ngày mà Giờ Tận Thế sẽ được thiết lập, những kẻ tội lỗi sẽ lặng câm, tuyệt vọng.');
INSERT INTO "hasan_abdulkarim" VALUES(30,13,'30:13','Và trong số những thần linh của chúng, không ai đứng ra bênh vực chúng và chúng sẽ phủ nhận những thần linh đó của chúng.');
INSERT INTO "hasan_abdulkarim" VALUES(30,14,'30:14','Và vào Ngày mà Giờ Tận Thế sẽ được thiết lập, tức ngày mà tất cả mọi người sẽ được tách biệt (giữa người có đức tin và kẻ vô đức tin).');
INSERT INTO "hasan_abdulkarim" VALUES(30,15,'30:15','Đối với những ai có đức tin và làm việc thiện thì sẽ được sống sung sướng nơi ngôi vườn xanh tươi (của thiên đàng).');
INSERT INTO "hasan_abdulkarim" VALUES(30,16,'30:16','Còn đối với những ai không tin tưởng và phủ nhận các dấu hiệu của TA và việc gặp gỡ (TA ở) đời sau thì sẽ là những kẻ bị đưa đến chỗ trừng phạt (hỏa ngục).');
INSERT INTO "hasan_abdulkarim" VALUES(30,17,'30:17','Bởi thế, hãy tán dương Allah khi các ngươi bước vào buổi tối (cuộc lễ nguyện Salah Magrib và ''I-sha'') và bước vào buổi sáng (cuộc lễ nguyện Salah Fajr).');
INSERT INTO "hasan_abdulkarim" VALUES(30,18,'30:18','Và mọi lời ca tụng trong các tầng trời và trái đất đều thuộc về Ngài cả và (hãy ca tụng Ngài vào cuộc lễ nguyện Salah ''Asr) lúc xế chiều và (vào cuộc lễ nguyện Salah Zhuhr) lúc ngày bắt đầu chếch bóng.(');
INSERT INTO "hasan_abdulkarim" VALUES(30,19,'30:19','Ngài đưa cái sống ra từ cái chết và Ngài đưa cái chết ra khỏi cái sống; và Ngài làm hồi sinh lại miếng đất đã chết khô và các ngươi sẽ được đưa ra (sống lại) giống như thế.');
INSERT INTO "hasan_abdulkarim" VALUES(30,20,'30:20','Và trong các dấu hiệu của Ngài có điều này: Ngài đã tạo các ngươi từ đất bụi, rồi các ngươi trở thành những người phàm sống rải rác khắp nơi.');
INSERT INTO "hasan_abdulkarim" VALUES(30,21,'30:21','Và trong các dấu hiệu của Ngài có điều này: Ngài đã tạo từ bản thân của các ngươi những người vợ cho các ngươi để các ngươi sống yên lành với họ và Ngài đã đặt giữa các ngươi tình yêu thương và lòng bao dung. Quả thật, nơi sự việc đó là những dấu hiệu cho một số người biết ngẫm nghĩ.');
INSERT INTO "hasan_abdulkarim" VALUES(30,22,'30:22','Và trong các dấu hiệu của Ngài là việc tạo hóa các tầng trời và trái đất và sự khác biệt về ngôn ngữ và mầu da của các người. Quả thật, nơi sự việc đó là những dấu hiệu cho những người hiểu biết.');
INSERT INTO "hasan_abdulkarim" VALUES(30,23,'30:23','Và trong các dấu hiệu của Ngài là giấc ngủ của các ngươi ban đêm và ban ngày và việc tìm kiếm bổng lộc của Ngài. Quả thật, trong sự việc đó là những dấu hiệu cho một số người biết lắng nghe.');
INSERT INTO "hasan_abdulkarim" VALUES(30,24,'30:24','Và trong các dấu hiệu của Ngài là việc Ngài làm cho các ngươi thấy tia chớp, (khiến các ngươi) vừa lo vừa mừng; và Ngài ban nước mưa từ trên trời xuống mà Ngài dùng làm hồi sinh lại miếng đất đã chết khô. Quả thật, trong sự việc đó là những dấu hiệu cho những người thông hiểu.');
INSERT INTO "hasan_abdulkarim" VALUES(30,25,'30:25','Và trong các dấu hiệu của Ngài là bầu trời và trái đất đứng vững theo lệnh của Ngài; rồi khi Ngài gọi các ngươi bằng một tiếng Gọi duy nhất thì lúc đó, các ngươi sẽ từ trong đất bước ra.');
INSERT INTO "hasan_abdulkarim" VALUES(30,26,'30:26','Bất cứ ai ở trong các tầng trời và trái đất đều là (tạo vật) của Ngài cả. Tất cả đều tuân phục Ngài.');
INSERT INTO "hasan_abdulkarim" VALUES(30,27,'30:27','Và Ngài là Đấng khởi sự việc tạo hóa rồi tái lập nó và điều đó rất dễ đối với Ngài. Và những hình ảnh so sánh cao cả trong các tầng trời và trái đất đều thuộc về Ngài. Bởi vì Ngài là Đấng Toàn Năng, Đấng Chí Minh.');
INSERT INTO "hasan_abdulkarim" VALUES(30,28,'30:28','Ngài đưa ra một thí dụ so sánh cho các ngươi từ bản thân của các ngươi; các ngươi có chăng trong số (tôi tớ) nằm dưới tay kiểm soát của các ngươi những đối tác cùng san sẻ tài sản mà TA đã ban cấp cho các ngươi? Và vì sự bình đẳng đó mà các ngươi sợ chúng giống như việc các ngươi sợ nhau? Đấy, TA giải thích rõ các dấu hiệu cho những người thông hiểu.');
INSERT INTO "hasan_abdulkarim" VALUES(30,29,'30:29','Không, những kẻ làm điều sai quấy, vì thiếu hiểu biết, đã tuân theo những dục vọng của chúng. Thế, ai là người sẽ hướng dẫn kẻ mà Allah đánh lạc hướng? Và chúng sẽ không có ai giúp đỡ.');
INSERT INTO "hasan_abdulkarim" VALUES(30,30,'30:30','Do đó, hãy định diện của Ngươi (hỡi Muhammad!) hướng về tôn giáo Hanif (chỉ tôn thờ Allah), Fitrah( (tôn giáo tự nhiên) của Allah theo đó Ngài đã tạo hóa con người. Không có một sự thay đổi nào trong tôn giáo của Allah. Đó là tôn giáo đứng đắn nhưng đa số thiên hạ không biết.');
INSERT INTO "hasan_abdulkarim" VALUES(30,31,'30:31','Hãy (luôn luôn) quay về sám hối với Ngài và sợ Ngài và hãy dâng lễ nguyện Salah và chớ là những người tôn thờ đa thần.');
INSERT INTO "hasan_abdulkarim" VALUES(30,32,'30:32','Trong số những kẻ đã phân chia tôn giáo của họ và kết thành những giáo phái; mỗi giáo phái vui vầy với điều mà họ đang giữ.');
INSERT INTO "hasan_abdulkarim" VALUES(30,33,'30:33','Và khi loài người gặp hoạn nạn, họ van xin Thượng Đế của họ, tỏ lòng sám hối với Ngài rồi khi Ngài cho họ nếm mùi khoan dung từ Ngài thì một thành phần của họ lại tổ hợp những đối tác ngang vai cùng với Thượng Đế của họ.');
INSERT INTO "hasan_abdulkarim" VALUES(30,34,'30:34','(Làm thế) là để họ phủ nhận ân huệ mà TA đã ban bố. Thế thì hãy hưởng thụ đi! Rồi đây các người sẽ sớm biết.');
INSERT INTO "hasan_abdulkarim" VALUES(30,35,'30:35','Hoặc phải chăng TA đã ban cho họ một thẩm quyền (hay Kinh Sách) đã nói về việc tổ hợp những đối tác ngang vai cùng với Ngài (Allah)?');
INSERT INTO "hasan_abdulkarim" VALUES(30,36,'30:36','Và khi TA cho loài người nếm mùi khoan dung, họ lấy làm vui mừng; nhưng khi gặp điều bất hạnh do chính bàn tay của họ đã gây ra trước kia thì họ đâm ra tuyệt vọng.');
INSERT INTO "hasan_abdulkarim" VALUES(30,37,'30:37','Há họ không thấy việc Allah nới rộng bổng lộc (của Ngài) cho người nào Ngài muốn, và thu hẹp nó (từ kẻ nào Ngài muốn). Quả thật, trong sự việc đó là những dấu hiệu cho đám người tin tưởng.');
INSERT INTO "hasan_abdulkarim" VALUES(30,38,'30:38','Do đó, hãy trao cho người bà con ruột thịt phần của họ và của người thiếu thốn (ăn xin) và của người lỡ đường. Điều đó tốt nhất cho những ai muốn tìm sắc Diện (Hài lòng) của Allah. Và họ là những người sẽ thành đạt.');
INSERT INTO "hasan_abdulkarim" VALUES(30,39,'30:39','Và bất cứ vật gì các ngươi tặng (cho người khác) từ Riba( với mục đích làm tăng phần (của mình) trong tài sản của thiên hạ thì sẽ không gia tăng dưới cái nhìn của Allah. Ngược lại, bất cứ vật gì các ngươi tặng ra từ Zakah mong muốn tìm sắc Diện (Hài lòng) của Allah thì đó mới là những người sẽ được gia tăng nhiều hơn.');
INSERT INTO "hasan_abdulkarim" VALUES(30,40,'30:40','Allah là Đấng đã tạo hóa các người, rồi cung dưỡng các người rồi làm cho các người chết rồi làm cho các người sống trở lại (vào Ngày phục sinh). Thế, trong số những thần thánh của các người, ai là kẻ có khả năng làm được ít nhất một điều trong những điều vừa kể? Quang vinh thay Ngài! Bởi vì Ngài tối cao vượt lên trên những kẻ mà chúng đã tổ hợp với Ngài.');
INSERT INTO "hasan_abdulkarim" VALUES(30,41,'30:41','Sự hư hại xuất hiện trên đất liền và biển cả là vì những hành vi thối nát do bàn tay của con người đã làm ra, cho nên (Allah) cho họ nếm (hậu quả) của những điều mà họ đã làm để may ra họ còn có dịp (tỉnh ngộ và) quay về (với nẻo chánh).');
INSERT INTO "hasan_abdulkarim" VALUES(30,42,'30:42','Hãy bảo (họ): “Hãy đi du lịch trên trái đất để xét xem kết cuộc của những kẻ đã sống trước đây. Đa số bọn chúng là những kẻ tôn thờ đa thần.');
INSERT INTO "hasan_abdulkarim" VALUES(30,43,'30:43','Bởi thế, hãy định diện của Ngươi (Muhammad) hướng về tôn giáo đúng đắn trước khi xảy ra Ngày không thể tránh khỏi từ Allah. Vào Ngày đó, (nhân loại) sẽ bị tách làm hai: (một nhóm lên thiên đàng, một nhóm xuống hỏa ngục);');
INSERT INTO "hasan_abdulkarim" VALUES(30,44,'30:44','Ai không có đức tin thì sẽ lãnh đủ về sự không tin của mình; còn ai làm việc thiện thì là tự chuẩn bị cho mình một chỗ tốt (nơi thiên đàng).');
INSERT INTO "hasan_abdulkarim" VALUES(30,45,'30:45','Mục đích để Ngài dùng thiên lộc của Ngài mà trọng thưởng những ai có đức tin và làm việc thiện. Quả thật, Ngài không thương yêu những kẻ không có đức tin.');
INSERT INTO "hasan_abdulkarim" VALUES(30,46,'30:46','Và trong các dấu hiệu của Ngài có điểm này: Ngài gởi những luồng gió bay đi như những người mang tin mừng (về trận mưa sắp đến) và để cho các ngươi nếm mùi Khoan Dung của Ngài và để cho những chiếc tàu buồm lướt sóng (vượt trùng dương) theo lệnh của Ngài và để cho các ngươi tìm kiếm thiên lộc của Ngài và để cho các ngươi tạ ơn Ngài.');
INSERT INTO "hasan_abdulkarim" VALUES(30,47,'30:47','Và chắc chắn, TA đã cử phái các Sứ Giả trước Ngươi (Muhammad) đến với người dân của họ; bởi thế, họ đến gặp (người dân của) họ với những bằng chứng rõ rệt. Sau đó, TA đã trừng phạt những kẻ phạm giới. Và TA có trách nhiệm phải giúp đỡ những người có đức tin.');
INSERT INTO "hasan_abdulkarim" VALUES(30,48,'30:48','Allah là Đấng gởi những luồng gió bay đi, thổi đưa các đám mây lên cao rồi trải nó rộng trên nền trời theo ý Ngài muốn và đập nó tan vụn ra; rồi Ngươi thấy nước mưa từ giữa đó rơi xuống; rồi khi Ngài làm cho nước mưa rơi nhằm phải ai trong số bầy tôi của Ngài mà Ngài muốn thì họ vui mừng thỏa thích.');
INSERT INTO "hasan_abdulkarim" VALUES(30,49,'30:49','Và trước đó, trước khi có nước mưa rơi xuống cho họ, họ đã hết sức tuyệt vọng (trông chờ nước mưa)!');
INSERT INTO "hasan_abdulkarim" VALUES(30,50,'30:50','Này, hãy nhìn xem kết quả của Đức Khoan Dung của Allah về việc Ngài làm hồi sinh lại lớp đất khô cằn như thế nào. Rõ thật, Đấng (Allah) ấy sẽ phục sinh lại người chết như thế bởi vì Ngài thừa khả năng làm được mọi việc.');
INSERT INTO "hasan_abdulkarim" VALUES(30,51,'30:51','Và nếu TA gởi một luồng gió đến và họ thấy (ruộng rẫy của họ) trổ vàng, thì sau đó, họ sẽ vong ơn (và hết tin tưởng).');
INSERT INTO "hasan_abdulkarim" VALUES(30,52,'30:52','Như vậy, quả thật Ngươi (Muhammad) không thể làm cho người chết nghe được. Ngươi cũng không thể làm cho người điếc nghe được lời mời (gọi) khi họ quay lưng bỏ đi.');
INSERT INTO "hasan_abdulkarim" VALUES(30,53,'30:53','Và Ngươi cũng không thể hướng dẫn được người mù khỏi lạc đường. Ngươi chỉ có thể làm cho ai tin nơi các Lời Mặc Khải của TA nghe hiểu mà thôi, bởi vì họ là người ''Muslim'' (tuân mệnh Allah).');
INSERT INTO "hasan_abdulkarim" VALUES(30,54,'30:54','Allah là Đấng đã tạo hóa các ngươi yếu đuối, rồi từ chỗ yếu đuối làm cho các ngươi khỏe mạnh, rồi sau khi khỏe mạnh làm cho các ngươi yếu đuối trở lại với mái tóc hoa râm. Ngài tạo hóa vật gì Ngài muốn bởi vì Ngài là Đấng Toàn Tri, Đấng Toàn Năng.');
INSERT INTO "hasan_abdulkarim" VALUES(30,55,'30:55','Và vào Ngày mà Giờ Tận Thế sẽ được thiết lập, những kẻ tội lỗi sẽ thề thốt rằng mình đã ở lại (trần gian) chỉ một tiếng đồng hồ mà thôi: chúng đã từng bị lừa dối như thế.');
INSERT INTO "hasan_abdulkarim" VALUES(30,56,'30:56','Còn những ai đã được ban cho sự hiểu biết và có đức tin thì sẽ nói: “Chắc chắn các người đã ở lại cho đến Ngày phục sinh chiếu theo Quyết định của Allah. Thì đây là Ngày phục sinh, nhưng các người không biết đó thôi.”');
INSERT INTO "hasan_abdulkarim" VALUES(30,57,'30:57','Vào Ngày đó, những lý lẽ chạy tội của những kẻ làm điều sai quấy sẽ không giúp ích gì được cho chúng và chúng cũng sẽ không được (Allah) cho phép hối cải.');
INSERT INTO "hasan_abdulkarim" VALUES(30,58,'30:58','Và chắc chắn, TA đã trình bày cho nhân loại trong Qur''an này đủ loại hình ảnh thí dụ; nhưng nếu Ngươi (Muhammad) có mang đến cho họ bất cứ dấu hiệu nào thì chắc chắn những ai không có đức tin sẽ bảo: “Rõ thật, các ông chỉ nói chuyện huyền hoặc.”');
INSERT INTO "hasan_abdulkarim" VALUES(30,59,'30:59','Allah niêm kín tấm lòng của những ai thiếu hiểu biết giống như thế.');
INSERT INTO "hasan_abdulkarim" VALUES(30,60,'30:60','Bởi thế, hãy kiên nhẫn chịu đựng (hỡi Muhammad!). Chắc chắn, Lời hứa của Allah là thật. Và chớ để cho những ai không có đức tin vững chắc làm cho Ngươi ngã lòng.');
INSERT INTO "hasan_abdulkarim" VALUES(31,1,'31:1','Alif.Lam. Mim.');
INSERT INTO "hasan_abdulkarim" VALUES(31,2,'31:2','Đầy là những Câu của Kinh Sách (Qur''an) thông suốt,');
INSERT INTO "hasan_abdulkarim" VALUES(31,3,'31:3','Một Chỉ Đạo và một Hồng Ân ban cho những người làm tốt:');
INSERT INTO "hasan_abdulkarim" VALUES(31,4,'31:4','Những ai dâng lễ nguyện Salah một cách chu đáo và đóng Zakah và có một đức tin vững chắc nơi đời sau;');
INSERT INTO "hasan_abdulkarim" VALUES(31,5,'31:5','Họ là những người đang theo đúng Chỉ Đạo của Thượng Đế (Allah) của họ và là những người sẽ thành đạt.');
INSERT INTO "hasan_abdulkarim" VALUES(31,6,'31:6','Và trong thiên hạ có kẻ( đã mua tập truyện giải trí hầu lôi cuốn những người thiếu hiểu biết lạc khỏi con đường của Allah và nhạo báng nó. Họ là những người sẽ chịu một sự trừng phạt rất nhục nhã.');
INSERT INTO "hasan_abdulkarim" VALUES(31,7,'31:7','Và khi những Lời Mặc Khải của TA được đọc ra cho y, y quay bỏ đi với dáng điệu tự phụ làm như y chẳng hề nghe đến chúng bao giờ, ra tuồng như hai tai của y điếc nặng. (Hỡi Muhammad!) Hãy báo cho y biết về một sự trừng phạt đau đớn.');
INSERT INTO "hasan_abdulkarim" VALUES(31,8,'31:8','Quả thật, những ai có đức tin và làm việc thiện thì sẽ được hưởng thiên đàng hạnh phúc;');
INSERT INTO "hasan_abdulkarim" VALUES(31,9,'31:9','Họ sẽ vào ở trong đó đời đời. Một Lời Hứa thật sự của Allah; và Ngài là Đấng Toàn Năng, Đấng Rất Mực Sáng Suốt.');
INSERT INTO "hasan_abdulkarim" VALUES(31,10,'31:10','Ngài đã tạo các tầng trời không cần trụ chống mà các ngươi có thể nhìn thấy (với mắt thường) và đã dựng lên trên mặt đất những quả núi vững chắc, sợ rằng nó (đất) sẽ rung chuyển với các ngươi và Ngài đã rải ra nơi đó đủ loại thú vật. Và TA ban nước mưa từ trên trời xuống mà TA dùng để làm mọc ra nơi đó đủ cặp (thảo mộc) quí giá.');
INSERT INTO "hasan_abdulkarim" VALUES(31,11,'31:11','Đây là sự tạo hóa của Allah. Thế, hãy chỉ cho TA xem đâu là vật mà những ai khác Ngài đã tạo? Không, những kẻ làm điều sai quấy đang lầm lạc rõ ràng.');
INSERT INTO "hasan_abdulkarim" VALUES(31,12,'31:12','Và chắc chắn, TA đã ban cho Luqman( trí khôn ngoan, phán bảo: “Hãy biết ơn Allah,” ai biết ơn (Allah) thì việc biết ơn đó chỉ có lợi cho bản thân (linh hồn) mình; ngược lại, ai phụ ơn (Allah) thì quả thật Allah Rất Mực Giàu có, Rất Đáng Ca tụng (không cần đến ơn nghĩa của ai).');
INSERT INTO "hasan_abdulkarim" VALUES(31,13,'31:13','Và hãy nhớ lại khi Luqman bảo đứa con trai của mình khi Y khuyên con, bảo: “Hỡi con yêu dấu! Chớ tổ hợp (điều gì, vật gì) cùng với Allah (trong việc thờ phụng Ngài). Quả thật, tổ hợp những thần thánh cùng với Allah là một điều sai quấy to lớn.');
INSERT INTO "hasan_abdulkarim" VALUES(31,14,'31:14','Và TA đã truyền lệnh cho con người về (việc hiếu thảo với) cha mẹ. Mẹ của y đã cưu mang y từ đau yếu (gian khổ) này chồng lên đau yếu (gian khổ) khác; và cho y bú và dứt sữa y trong vòng hai năm; bởi thế, hãy tạ ân TA (Allah) và biết ơn cha mẹ của ngươi; cuối cùng, nhà ngươi sẽ trở về gặp lại TA.');
INSERT INTO "hasan_abdulkarim" VALUES(31,15,'31:15','Và nếu cha mẹ (của ngươi) đấu tranh bắt ngươi tổ hợp với TA những kẻ (vật, điều) mà ngươi không hề biết đến bao giờ thì chớ nghe lời hai người, nhưng hãy ăn ở tử tế với hai người ở đời này và hãy theo con đường của những ai trở về sám hối với TA. Rồi, các ngươi sẽ trở lại gặp TA (sau khi chết). Sau đó, TA sẽ cho các ngươi biết về những điều mà các ngươi đã từng làm (trên thế gian).');
INSERT INTO "hasan_abdulkarim" VALUES(31,16,'31:16','“Này con yêu dấu! Dẫu cho có một vật gì nhẹ bằng sức nặng của một hạt cải và dẫu cho nó có nằm trong đá hoặc trong các tầng trời hoặc dưới đất đi nữa thì Allah thừa khả năng mang nó ra ngoài (cho thiên hạ thấy). Bởi vì quả thật, Allah Rất Mực Tinh tế, Rất Mực Am Tường.');
INSERT INTO "hasan_abdulkarim" VALUES(31,17,'31:17','“Này con yêu dấu! Hãy dâng lễ nguyện Salah một cách chu đáo và làm điều thiện tốt và cấm cản điều xấu và kiên trì chịu đựng điều rủi ro xảy đến cho con, bởi vì quả thật đó là một sự kiên định trong mục đích thi hành công việc."');
INSERT INTO "hasan_abdulkarim" VALUES(31,18,'31:18','“Và chớ phùng má (tự phụ) tránh xa người và chớ bước đi trên mặt đất với dáng điệu lấc xấc bởi vì quả thật, Allah không yêu thương tất cả những kẻ khoác lác kiêu căng."');
INSERT INTO "hasan_abdulkarim" VALUES(31,19,'31:19','“Mà hãy đi đứng khiêm nhường và dịu giọng trong ăn nói bởi vì quả thật, âm thanh khó chịu và đáng ghét nhất là tiếng hí của con lừa”');
INSERT INTO "hasan_abdulkarim" VALUES(31,20,'31:20','Há các ngươi không nhận thấy việc Allah đã chế ngự mọi vật trong các tầng trời và mọi vật dưới đất (cho các ngươi sử dụng) và đã công khai hay kín đáo hoàn tất ân huệ của Ngài cho các ngươi? Nhưng trong thiên hạ có kẻ thích tranh luận về Allah nhưng tuyệt đối không biết một tí gì (về Ngài), cũng không căn cứ vào một Chỉ nào và không dựa vào một Kinh Sách minh bạch nào (để làm bằng).');
INSERT INTO "hasan_abdulkarim" VALUES(31,21,'31:21','Và khi có lời bảo chúng: “Hãy tuân theo điều mà Allah đã ban xuống” thì chúng đáp: “Không, chúng tôi làm theo điều mà chúng tôi thấy cha mẹ của chúng tôi đang theo.” Sao? ngay cả việc Shaytan kêu gọi chúng đi chịu hình phạt của Lửa cháy (trong hỏa ngục hay sao?).');
INSERT INTO "hasan_abdulkarim" VALUES(31,22,'31:22','Và ai đệ nạp diện mạo của mình cho Allah đồng thời là một người làm tốt thì chắc chắn sẽ nắm được chiếc cán (cứu rỗi) vững chắc nhất bởi vì kết cuộc của mọi việc đều phải trình về cho Allah (quyết định).');
INSERT INTO "hasan_abdulkarim" VALUES(31,23,'31:23','Và ai không tin tưởng thì chớ để cho sự không tin của y làm cho Ngươi buồn. (Bởi vì) chúng sẽ trở lại gặp TA, lúc đó TA sẽ cho chúng biết về những việc mà chúng đã làm. Quả thật, Allah Hằng Biết điều thầm kín trong lòng (của mỗi người).');
INSERT INTO "hasan_abdulkarim" VALUES(31,24,'31:24','TA để cho chúng hưởng lạc trong một thời gian ngắn rồi TA sẽ lôi chúng đến chỗ trừng phạt khắc nghiệt.');
INSERT INTO "hasan_abdulkarim" VALUES(31,25,'31:25','Và giả sử Ngươi (Muhammad) hỏi chúng: “Ai đã tạo các tầng trời và trái đất?” chắc chắn chúng sẽ đáp “Allah,” thì hãy bảo ngay: “Mọi lời ca tụng đều thuộc về Allah!” (Đấng Sáng Tạo). Không, đa số bọn chúng không biết (rằng)');
INSERT INTO "hasan_abdulkarim" VALUES(31,26,'31:26','Mọi vật trong các tầng trời và trái đất đều thuộc về Allah cả. Quả thật, Allah là Đấng Rất Mực Giàu Có, Rất đáng Ca tụng.');
INSERT INTO "hasan_abdulkarim" VALUES(31,27,'31:27','Và nếu cây cối trên trái đất được dùng làm bút viết và biển cả làm (lọ) mực với nước (mực) của bảy biển châm thêm cho nó (dùng để viết Lời phán của Allah) thì Lời phán của Allah sẽ không bao giờ cạn kiệt. Bởi vì quả thật, Allah Toàn Năng, Rất Mực Sáng Suốt.');
INSERT INTO "hasan_abdulkarim" VALUES(31,28,'31:28','Việc tạo hóa và việc phục sinh tất cả các ngươi chẳng khác nào (việc tạo hóa và phục sinh của) một linh hồn duy nhất. Quả thật, Allah Hằng Nghe, Hằng Thấy');
INSERT INTO "hasan_abdulkarim" VALUES(31,29,'31:29','Há Ngươi (Muhammad) không nhận thấy việc Allah nhập ban đêm vào ban ngày và ban ngày vào ban đêm và đã chế ngự mặt trời và mặt trăng, mỗi vật di chuyển theo quỹ đạo của nó đến một thời hạn ấn định rằng Allah Hằng Am Tường về mọi việc mà các ngươi làm hay sao?');
INSERT INTO "hasan_abdulkarim" VALUES(31,30,'31:30','Sở dĩ như thế là vì Allah là Chân Lý tuyệt đối, và rằng vật (hay kẻ) mà chúng cầu nguyện không phải là Ngài chỉ là vật giả tạo ấy thôi; và rằng Allah, Ngài là Đấng Tối Cao, Đấng Vĩ Đại.');
INSERT INTO "hasan_abdulkarim" VALUES(31,31,'31:31','Há Ngươi không nhận thấy việc những chiếc tàu (buồm) lướt sóng băng đại dương được là do Ân Huệ của Allah để Ngài chỉ cho các ngươi thấy các dấu hiệu của Ngài hay sao? Quả thật, trong sự việc đó là các dấu hiệu cho từng người kiên nhẫn và biết ơn.');
INSERT INTO "hasan_abdulkarim" VALUES(31,32,'31:32','Và khi một làn sóng to như tấm phủ ùa đến bao phủ chúng, chúng van xin Allah, thành khẩn chỉ với riêng Ngài. Nhưng sau khi Ngài đã cứu chúng lên bờ an toàn thì có một số người trong bọn chúng đứng ở chính giữa (đức tin và sự không tin). Và chỉ những tên bội phản, vô ơn mới phủ nhận các dấu hiệu của TA.');
INSERT INTO "hasan_abdulkarim" VALUES(31,33,'31:33','Hỡi nhân loại! Hãy sợ Thượng Đế (Allah) của các ngươi và sợ Ngày mà tuyệt đối cha sẽ không giải cứu được con và con cũng sẽ không giải cứu được cha. Quả thật, Lời Hứa của Allah sẽ thật sự. Bởi thế, chớ để cho đời sống trần tục này đánh lừa các ngươi và chớ để cho tên trùm lừa gạt (Shaytan) đánh lừa các ngươi về Allah.');
INSERT INTO "hasan_abdulkarim" VALUES(31,34,'31:34','Quả thật, chỉ riêng Allah biết rõ khi nào Giờ (xét xử) sẽ xảy ra. Và Ngài cho mưa xuống và biết cái (bào thai) nằm trong dạ con (của các bà mẹ). Và không một người (linh hồn) nào biết rõ vật gì y sẽ kiếm được ngày mai; và không một linh hồn nào biết được đâu là mảnh đất mà y sẽ chết (và được chôn). Quả thật, Allah Rất mực Hiểu biết, Rất mực Am tường.');
INSERT INTO "hasan_abdulkarim" VALUES(32,1,'32:1','Alif. Lam. Mim.');
INSERT INTO "hasan_abdulkarim" VALUES(32,2,'32:2','Kinh Sách (Qur''an) do Thượng Đế (Đấng Chủ Tể) của vũ trụ và muôn loài ban xuống, không có chi phải ngờ vực cả.');
INSERT INTO "hasan_abdulkarim" VALUES(32,3,'32:3','Thế mà chúng (những kẻ không tin) bảo: “Y đã bịa đặt ra Nó (Qur''an)?” Không, đó là Chân Lý do Thượng Đế của Ngươi (ban xuống) để Ngươi dùng cảnh cáo một đám dân mà trước Ngươi đã không có một người báo trước nào đến với họ, để may ra họ được hướng dẫn.');
INSERT INTO "hasan_abdulkarim" VALUES(32,4,'32:4','Allah là Đấng đã tạo hóa các tầng trời và trái đất và vạn vật giữa ười đất trong sáu Ngày rồi lên an vin trên chiếc Ngai vương của Ngài. Ngoài Ngài, các ngươi không có một đấng Bảo hộ nào và cũng không có một Vị can thiệp nào cả. Thế các ngươi không chịu nhớ hay sao?');
INSERT INTO "hasan_abdulkarim" VALUES(32,5,'32:5','Ngài sắp đặt mọi công việc từ trên trời xuống dưới đất rồi công việc sẽ được trình lên cho Ngài trong một ngày, mà khoảng thời gian là bằng một ngàn năm theo cách tính của các người.');
INSERT INTO "hasan_abdulkarim" VALUES(32,6,'32:6','(Allah) là thế, Đấng Hằng Biết điều vô hình và điều hữu hình, Đấng Toàn Năng, Đấng Rất Mực Khoan Dung.');
INSERT INTO "hasan_abdulkarim" VALUES(32,7,'32:7','Đấng hoàn thiện mọi vật mà Ngài đã tạo; và khởi sự việc tạo hóa con người từ đất sét.');
INSERT INTO "hasan_abdulkarim" VALUES(32,8,'32:8','Rồi, Ngài làm ra dòng dõi của Y từ chất tinh dịch của một loại dung dịch vô giá trị.');
INSERT INTO "hasan_abdulkarim" VALUES(32,9,'32:9','Rồi, Ngài uốn nắn y thành hình thể và hà vào cơ thể của y linh hồn của Ngài; và Ngài làm ra cho các ngươi thính giác (tai) và thị giác (mắt) và tấm lòng (trái tim). Ít thay ơn mà các ngươi tạ Ngài!');
INSERT INTO "hasan_abdulkarim" VALUES(32,10,'32:10','Và chúng bảo: “Có thật chăng khi chúng tôi (chết) tiêu tan dưới đất, chúng tôi sẽ được tạo hóa mới trở lại?” Không, chúng phủ nhận việc gặp gỡ Thượng Đế của chúng (vào Ngày phục sinh).');
INSERT INTO "hasan_abdulkarim" VALUES(32,11,'32:11','Hãy bảo : “Thần Chết, vị phụ trách các người, sẽ bắt hồn của các người rồi các người sẽ được đưa về gặp Thượng Đế của các người trở lại.”');
INSERT INTO "hasan_abdulkarim" VALUES(32,12,'32:12','Và giá Ngươi có thể nhìn thấy tình cảnh của những kẻ tội lỗi gục đầu trước mặt Thượng Đế (Allah) của chúng van xin: “Lạy Thượng Đế của bầy tôi! Chúng tôi đã thấy và nghe (sự thật). Xin Ngài cho chúng tôi trở lại trần gian, chúng tôi sẽ làm điều thiện. Rõ thật! Bây giờ chúng tôi tin tưởng chắc chắn.”');
INSERT INTO "hasan_abdulkarim" VALUES(32,13,'32:13','Và nếu muốn, chắc chắn TA đã ban cho mỗi người (linh hồn) Chỉ Đạo của y, nhưng Lời Phán từ TA (về những kẻ tội lỗi) sẽ thể hiện đúng sự thật, (rằng): “TA sẽ giam chung loài jinn và loài người vào đầy hỏa ngục.”');
INSERT INTO "hasan_abdulkarim" VALUES(32,14,'32:14','Bởi thế, hãy nếm (hình phạt) về việc các ngươi đã lãng quên việc gặp gỡ Ngày này của các ngươi. Quả thật, TA cũng bỏ quên các ngươi trở lại (như thế). Thôi, hãy nếm hình phạt bất tận về những điều (tội lỗi) mà các ngươi đã từng làm.');
INSERT INTO "hasan_abdulkarim" VALUES(32,15,'32:15','Chỉ thực lòng tin tưởng nơi các Lời Mặc Khải của TA, những ai khi được nhắc về chúng liền quỳ xuống phủ phục và tán dương lời ca tụng Thượng Đế của họ và không tỏ vẻ tự cao;');
INSERT INTO "hasan_abdulkarim" VALUES(32,16,'32:16','Họ rời giường ngủ (để) cầu nguyện Thượng Đế của họ vừa lo sợ vừa hy vọng (nơi lòng Khoan Dung của Ngài) và chi dùng tài sản mà TA đã cung cấp cho họ (để làm việc thiện).');
INSERT INTO "hasan_abdulkarim" VALUES(32,17,'32:17','Bởi vì không một người (linh hồn) nào biết được niềm vui sướng đã được giấu kín( dùng làm phần thưởng của họ về những việc thiện mà họ đã từng làm (trên thế gian).');
INSERT INTO "hasan_abdulkarim" VALUES(32,18,'32:18','Thế, một người tin tưởng có giống với một kẻ bất tuân hay không? (Chắc chắn) họ không giống nhau.');
INSERT INTO "hasan_abdulkarim" VALUES(32,19,'32:19','Đối với những ai có đức tin và làm việc thiện thì sẽ được ban cấp những vườn trời (nơi thiên đàng) làm chỗ ngụ: một sự đãi ngộ về những điều tốt mà họ đã từng làm.');
INSERT INTO "hasan_abdulkarim" VALUES(32,20,'32:20','Còn đối với những ai bất tuân và phạm giới thì nhà ở của chúng sẽ là hỏa ngục. Mỗi lần muốn thoát ra khỏi đó, chúng sẽ bị lồi vào trong trở lại với lời phán: “Hãy nếm hình phạt của Lửa mà các ngươi đã từng phủ nhận.”');
INSERT INTO "hasan_abdulkarim" VALUES(32,21,'32:21','Và chắc chắn, TA sẽ bắt chúng nếm hình phạt gần kề (ở trần gian) đến hình phạt lớn (ở đời sau) để chúng (có dịp hối cải mà) quay về (với Islam).');
INSERT INTO "hasan_abdulkarim" VALUES(32,22,'32:22','Và còn ai sai quấy hơn kẻ đã được nhắc các Lời Mặc Khải của Thượng Đế của y nhưng quay lánh xa? Quả thật, TA sẽ trừng trị những kẻ tội lỗi.');
INSERT INTO "hasan_abdulkarim" VALUES(32,23,'32:23','Và chắc chắn TA đã ban cho Musa Kinh Sách. Bởi thế, Ngươi (Muhammad) chớ nghi ngờ về việc tiếp xúc với Y (nhân chuyến dạ hành và thăng thiên). Và TA đã làm cho Nó (Kinh Tawrah) thành một Chỉ Đạo cho con cháu của Israel.');
INSERT INTO "hasan_abdulkarim" VALUES(32,24,'32:24','Và TA đã bổ nhiệm trong họ các nhà lãnh đạo để hướng dẫn (thiên hạ) theo Mệnh Lệnh của TA khi họ nhẫn nại và trở thành những người tin tưởng chắc chắn nơi các Lời Mặc Khải của TA.');
INSERT INTO "hasan_abdulkarim" VALUES(32,25,'32:25','Quả thật, Thượng Đế của Ngươi (Muhammad) sẽ xét xử giữa họ về những điều mà họ thường tranh chấp trong (Kinh Sách) vào Ngày phục sinh.');
INSERT INTO "hasan_abdulkarim" VALUES(32,26,'32:26','Há không là một Chỉ Đạo cho họ ư việc TA đã tiêu diệt bao nhiêu thế hệ trước mà chỗ ở hoang phế của họ đang dẫm đạp? Quả thật, nơi sự việc đó là các dấu hiệu. Thế, họ không nghe hay sao?');
INSERT INTO "hasan_abdulkarim" VALUES(32,27,'32:27','Há họ đã không nhìn thấy việc TA đã dẫn nước (mưa) đến một vùng đất khô ráo mà TA dùng để làm mọc ra hoa mầu làm thực phẩm nuôi gia súc của họ và cả họ nữa hay sao? Thế, họ không thấy hay sao?');
INSERT INTO "hasan_abdulkarim" VALUES(32,28,'32:28','Và họ bảo: “Thế, chừng nào xảy ra Quyết Định (phân xử giữa chúng tôi với quí vị) nếu quí vị (Muslim) nói thật?”');
INSERT INTO "hasan_abdulkarim" VALUES(32,29,'32:29','Hãy bảo (họ): “Vào ngày mà Quyết định (phân xử) thể hiện thì niềm tin của những ai không có đức tin sẽ không giúp ích gì được cho họ cả và họ cũng sẽ không được dung tha.”');
INSERT INTO "hasan_abdulkarim" VALUES(32,30,'32:30','Do đó, hãy lánh xa họ và đợi (xem kết quả); họ cũng đang đợi (như các ngươi).');
INSERT INTO "hasan_abdulkarim" VALUES(33,1,'33:1','Hỡi Nabi (Muhammad!) Hãy sợ Allah và làm tròn bổn phận đôi với Ngài và chớ nghe theo những kẻ không có đức tin và những tên giả tạo đức tin. Quả thật, Allah Toàn Tri và Chí Minh.');
INSERT INTO "hasan_abdulkarim" VALUES(33,2,'33:2','Và hãy tuân theo điều đã được mặc khải cho Ngươi từ Thượng Đế (Allah) của Ngươi bởi vì quả thật, Allah Rất Mực Am Tường về những điều các ngươi làm.');
INSERT INTO "hasan_abdulkarim" VALUES(33,3,'33:3','Và hãy ủy thác cho Allah bởi vì Allah đủ làm một Đấng Bảo Hộ.');
INSERT INTO "hasan_abdulkarim" VALUES(33,4,'33:4','Allah không đặt hai quả tim trong lòng của một người đàn ông. Ngài cũng không biến những người vợ mà các ngươi cho là giống cái lưng( của mẹ của các ngươi thành mẹ ruột của các ngươi. Ngài cũng không làm cho con nuôi của các ngươi thành con ruột. Đấy chỉ là lời nói thốt ra từ cửa miệng của các ngươi trong lúc Allah nói sự thật. Và Ngài chỉ dẫn con đường (chân chính).');
INSERT INTO "hasan_abdulkarim" VALUES(33,5,'33:5','Hãy gọi chúng (con nuôi) theo tên của người cha ruột của chúng. Cách gọi đó vô tư hơn đối với Allah. Nhưng nếu các ngươi không biết tên thật của người cha của chúng thì chúng là anh em với các ngươi trong đạo và là người nô lệ được giải phóng của các ngươi; và các ngươi không có tội nếu các ngươi nhầm lẫn trong việc xưng hô này. Ngược lại, điều đáng tội là khi các ngươi có ý xấu trong lòng. Và Allah Hằng Tha thự, Hằng Khoan Dung.');
INSERT INTO "hasan_abdulkarim" VALUES(33,6,'33:6','Đối với những người có đức tin thì Nabi thân thiết (và quí) hơn bản thân của họ; và các bà vợ của Người là các bà mẹ (đạo) của họ. Tuy nhiên, theo Kinh Sách (Sắc Chỉ) của Allah thì người bà con ruột thịt gần kề nhau (về việc hưởng di sản) hơn là những người có đức tin (trong đạo) và hơn những người di cư (từ Makkah); ngoại trừ trường hợp các ngươi làm một hành động tốt đối với bạn đạo thân thiết nhất của các ngươi. Đó là Sắc Chỉ ghi trong Kinh Sách.');
INSERT INTO "hasan_abdulkarim" VALUES(33,7,'33:7','Và (hãy nhớ) khi TA (Allah) đã nhận Lời giao ước từ các Nabi và từ Ngươi (Muhammad) và từ Nuh và Ibrahim và Musa và Ysa con trai của Maryam; và nhận từ họ(. Lời giao ước long trọng;');
INSERT INTO "hasan_abdulkarim" VALUES(33,8,'33:8','Để Ngài có thể hỏi những người chân thật (các Nabi) về sự đích thật của họ (về việc truyền đạt Thông Điệp của Allah) và chuẩn bị cho những kẻ không có đức tin một sự trừng phạt đau đớn.');
INSERT INTO "hasan_abdulkarim" VALUES(33,9,'33:9','Hỡi những ai có đức tin! Hãy nhớ Ân huệ của Allah đã ban cho các ngươi khi đoàn quân địch kéo đến tấn công các ngươi(. Nhưng TA đã gởi một trận cuồng phong và đoàn thiên binh (Thiên Thần) mà các ngươi không thể nhìn thấy (bằng mắt thường) đến giúp chống lại chúng; và Allah Hằng Thấy những điều các ngươi làm.');
INSERT INTO "hasan_abdulkarim" VALUES(33,10,'33:10','Khi chúng đến tấn công các ngươi từ phía trên và phía dưới của các ngươi và cặp mắt của các ngươi đảo lộn sang một bên và quả tim nhảy lên đến tận cổ và các ngươi đâm ra nghĩ bậy về Allah.');
INSERT INTO "hasan_abdulkarim" VALUES(33,11,'33:11','Vào lúc đó, các tín đồ bị thử thách tột độ và dao động tinh thần dữ dội.');
INSERT INTO "hasan_abdulkarim" VALUES(33,12,'33:12','Và khi những tên giả tạo đức tin và những kẻ mang một chứng bệnh (nghi ngờ) trong lòng lên tiếng: “Allah và Sứ Giả của Ngài chỉ hứa hão với chúng tôi.”');
INSERT INTO "hasan_abdulkarim" VALUES(33,13,'33:13','Và khi một phần tử trong bọn chúng lớn tiếng bảo: “Này hỡi người dân của Yathrib (Al-Madinah)! Các ngươi không thể cầm cự nổi (trước sức tấn công của địch). Bởi thế hãy tháo lui.” Và một nhóm người của bọn chúng đến xin phép Nabi thưa: “Nhà cửa của chúng tôi bỏ ngõ”; trong lúc thật ra nhà cửa của chúng không bỏ ngõ nhưng chúng chỉ muốn đào tẩu ấy thôi.');
INSERT INTO "hasan_abdulkarim" VALUES(33,14,'33:14','Và nếu địch quân tấn công chúng và đột nhập vào thành phố từ khấp mọi phía và chúng được yêu cầu đầu hàng và chống lại người Muslim thì chắc chắn chúng sẽ không ngần ngại làm điều đó và chúng chỉ ngập ngừng một chốc lát.');
INSERT INTO "hasan_abdulkarim" VALUES(33,15,'33:15','Và chắc chắn trước đó chúng đã cam kết với Allah rằng chúng sẽ không quay lưng bỏ chạy; và lời cam kết với Allah chắc chắn sẽ bị chất vấn (vào Ngày phục sinh).');
INSERT INTO "hasan_abdulkarim" VALUES(33,16,'33:16','Hãy bảo (chúng): “Bỏ chạy chẳng lợi gì cho các ngươi nếu muốn trốn thoát cái chết hay cảnh giết chóc, bởi vì các ngươi chỉ hưởng lạc được trong một chốc lát mà thôi.”');
INSERT INTO "hasan_abdulkarim" VALUES(33,17,'33:17','Hãy bảo (chúng): “Ai là kẻ có thể che chở các ngươi khỏi Allah nếu Ngài muốn hãm hại hoặc khoan dung các ngươi?” Và chúng sẽ không tìm ra một Vị che chở, và một Vị cứu tinh nào ngoài Allah cả.');
INSERT INTO "hasan_abdulkarim" VALUES(33,18,'33:18','Chắc chắn, Allah thừa biết ai trong các ngươi đã cản trở (người khác đi tham chiến) và nói với những người anh em (trong đạo) của họ: “Hãy đến cùng với bọn tôi!” trong lúc chúng chỉ đi tham chiến chút ít.');
INSERT INTO "hasan_abdulkarim" VALUES(33,19,'33:19','Chúng đê tiện đối với các ngươi. Bởi vì khi xảy ra cảnh hãi hùng, Ngươi sẽ thấy chúng dáo dác nhìn Ngươi, cặp mắt đảo quanh giống như cặp mắt của một người thất thần sắp chết. Nhưng khi cơn nguy khốn đã qua khỏi thì chúng nhiếc mắng các người bằng lời lẽ thậm tệ, chỉ muốn bồn rút vật dụng (chiến lợi phẩm). Những người này không có một chút đức tin nào cả. Bởi thế, Allah sẽ biến việc làm của chúng thành vô nghĩa. Và việc đó quá dễ đối với Allah.');
INSERT INTO "hasan_abdulkarim" VALUES(33,20,'33:20','Chúng nghĩ rằng Liên quân (của người ngoại đạo) chưa rút đi. Và nếu Liên quân (địch) có trở lại lần nữa thì chúng sẽ mong ước đi (lang thang) trong vùng sa mạc, trà trộn với người Ả Rập du mục để dọ thám tình hình của các ngươi. Và nếu chúng ở cùng với các ngươi đi nữa thì chúng cũng chỉ tham chiến chút ít ấy thôi.');
INSERT INTO "hasan_abdulkarim" VALUES(33,21,'33:21','Chắc chắn nơi Sứ Giả của Allah các ngươi có được một gương mẫu tốt đẹp nhất đối với những ai hy vọng về (việc gặp gỡ) Allah và Ngày (Phán Xử) cuối cùng và tưởng nhớ Allah nhiều nhất.');
INSERT INTO "hasan_abdulkarim" VALUES(33,22,'33:22','Và khi những người có đức tin thấy liên quân (địch), họ nói: “Đây là điều mà Allah và Sứ Giả của Ngài đã hứa với chúng tôi bởi vì Allah và Sứ Giả của Ngài nói sự thật.” Và điều đó chỉ làm tăng thêm đức tin và sự tuân phục của họ (đối với Allah).');
INSERT INTO "hasan_abdulkarim" VALUES(33,23,'33:23','Và trong số người tin tưởng, có những người giữ đúng lời giao ước của mình với Allah. Bởi thế trong họ, có người đã hoàn tất lời thề (bằng sự tử đạo) và có người còn chờ (đến phiên của họ) và họ đã không thay đổi (lời giao ước) một chút nào.');
INSERT INTO "hasan_abdulkarim" VALUES(33,24,'33:24','(Mục đích) để cho Allah trọng thưởng những người chân thật về lòng thành thật của họ và trừng phạt những kẻ giả tạo đức tin hoặc quay lại tha thứ cho chúng tùy Ngài muốn. Quả thật, Allah Hằng Tha Thứ, Rất Mực Khoan Dung.');
INSERT INTO "hasan_abdulkarim" VALUES(33,25,'33:25','Và Allah đẩy lùi những kẻ vô đức tin ra đi trong giận dữ. Chúng không thu được một lợi lộc nào. Và Allah đủ giúp những người tin tưởng chiến đấu. Bởi vì quả thật, Allah Toàn Lực, Toàn Năng.');
INSERT INTO "hasan_abdulkarim" VALUES(33,26,'33:26','Và trong số người dân Kinh Sách, Ngài đã đuổi những kẻ đã tiếp tay cho địch xuống khỏi thành lũy của chúng và gieo khiếp đảm trong lòng của chúng: một số người (của chúng), các người đã giết và một số khác, các người đã bắt làm tù binh.');
INSERT INTO "hasan_abdulkarim" VALUES(33,27,'33:27','Và Ngài cho các ngươi thừa hưởng đất đai, và nhà cửa và tài sản của chúng và một phần đất đai (tại Khaibar) mà các ngươi chưa đặt chân đến. Và Allah là Đấng Toàn Năng trên tất cả mọi thứ.');
INSERT INTO "hasan_abdulkarim" VALUES(33,28,'33:28','Hỡi Nabi (Muhammad!) Hãy bảo các bà vợ của Ngươi: “Nếu các nàng muốn đời sống trần tục này và vẽ hào nhoáng của nó, thì các nàng hãy đến đây, Ta sẽ cho các nàng hưởng lạc và trả tự do cho các nàng một cách tốt đẹp."');
INSERT INTO "hasan_abdulkarim" VALUES(33,29,'33:29','“Ngược lại, nếu các nữ yêu quí Allah và Sứ Giả của Ngài và muốn một mái nhà (xinh đẹp) ở đời sau thì Allah sẽ thực sự chuẩn bị cho các phụ nữ làm điều tốt trong các nữ một phần thưởng rất lớn.”');
INSERT INTO "hasan_abdulkarim" VALUES(33,30,'33:30','Hỡi các bà vợ của Nabi (Muhammad!) Ai trong quí bà là người đã công khai phạm điều bất nhã thì sẽ bị phạt gấp đôi; và chuyện đó quá dễ đôi với Allah.');
INSERT INTO "hasan_abdulkarim" VALUES(33,31,'33:31','Và ai trong các nữ là người cung kính Allah và Sứ Giả của Ngài và làm điều thiện thì TA sẽ thưởng nữ ấy gấp đôi và chuẩn bị cho nữ ấy bổng lộc rộng rãi nơi thiên đàng.');
INSERT INTO "hasan_abdulkarim" VALUES(33,32,'33:32','Hỡi các bà vợ của Nabi (Muhammad!) Các nữ không giống các phụ nữ khác. Nếu các nữ sợ Allah thì chớ ăn nói lả lơi, e rằng những ai mang trong lòng một chứng bệnh (dâm dục) sẽ động lòng tà dâm; ngược lại, hãy ăn nói đoan trang lịch thiệp.');
INSERT INTO "hasan_abdulkarim" VALUES(33,33,'33:33','Và các nữ hãy ở trong nhà và chớ phô trương vẻ đẹp theo lối chưng diện của các phụ nữ vào thời kỳ ngu muội; và các nữ hãy dâng lễ nguyện Salah một cách chu đáo và đóng Zakah và vâng lệnh Allah và Sứ Giả của Ngài. Allah chỉ muốn xóa điều nhơ nhuốc đi khỏi các ngươi, hỡi người nhà của gia đình Nabi, và tẩy sạch các ngươi thành những người triệt để trong sạch.');
INSERT INTO "hasan_abdulkarim" VALUES(33,34,'33:34','Và các nữ hãy ghi nhớ những điều nằm trong các Lời Mặc Khải đã được đọc ra trong nhà của các nữ và điều khôn ngoan đúng đắn. Quả thật, Allah Rất Mực Tinh Tế, Rất Mực Am Tường.');
INSERT INTO "hasan_abdulkarim" VALUES(33,35,'33:35','Quả thật, đối với những người Muslim, nam và nữ; những người có đức tin, nam và nữ; những người cung kính, nam và nữ; những người chân thật, nam và nữ; những người kiên nhẫn, nam và nữ; những người khiêm nhường, nam và nữ; những người bố thí, nam và nữ; những người kiêng cử, nam và nữ; những người giữ lòng trinh bạch, nam và nữ; những người tưởng nhớ Allah nhiều, nam và nữ; Allah sẽ tha thứ và ban thưởng to lớn cho họ.');
INSERT INTO "hasan_abdulkarim" VALUES(33,36,'33:36','Và không thích hợp đối với một người có đức tin, nam và nữ, rằng khi Allah và Sứ Giả của Ngài đã quyết định một công việc gì rồi lại còn đòi quyền được chọn lựa trong công việc của họ nữa; và ai bất tuân Allah và Sứ Giả của Ngài thì chắc chắn đã công khai lầm lạc.');
INSERT INTO "hasan_abdulkarim" VALUES(33,37,'33:37','Và hãy nhớ khi Ngươi (Nabi!) nói với người mà Allah đã ban ân và Ngươi đã ban ân như sau: “Hãy giữ vợ( của ngươi lại, và hãy sợ Allah.” Và Ngươi giữ kín trong lòng điều(138) mà Allah sắp công bố". Và Ngươi (Nabi) sợ thiên hạ (đàm tiếu) trong lúc Allah là Đấng mà đúng ra Ngươi phải sợ nhất. Bởi thế, khi Zaid (Ibnu Harithah)(139) đã dứt khoát với vợ, TA đã gả nàng cho Ngươi mục đích để cho những người có đức tin không gặp trở ngại trong việc kết hôn với các bà vợ của các đứa con nuôi của mình khi chúng đã dứt khoát với vợ của chúng. Và Lệnh của Allah phải được thi hành.');
INSERT INTO "hasan_abdulkarim" VALUES(33,38,'33:38','Nabi (Muhammad) không phạm tội trong việc mà Allah ra lệnh cho Y thi hành. Đó là đường lối của Allah áp dụng cho các ngươi đã qua đời trước đây(. Và Mệnh Lệnh của Allah là một Chỉ Dụ đã được quyết định.');
INSERT INTO "hasan_abdulkarim" VALUES(33,39,'33:39','Những ai truyền bá Thông điệp của Allah và sợ Ngài; và không Sợ ai duy chỉ sợ Allah thôi. Bởi vì chỉ riêng Allah thôi đủ xét xử.');
INSERT INTO "hasan_abdulkarim" VALUES(33,40,'33:40','(Nabi) Muhammad không phải là người cha của một ai trong số đàn ông của các ngươi. Ngược lại, Y là Sứ Giả của Allah và là Ân Tín( của các Nabi. Và Allah Hằng Biết hết mọi việc.');
INSERT INTO "hasan_abdulkarim" VALUES(33,41,'33:41','Hỡi những ai có đức tin! Hãy tưởng nhớ Allah thật nhiều.');
INSERT INTO "hasan_abdulkarim" VALUES(33,42,'33:42','Và hãy tán dương Ngài vào (cuộc lễ nguyện Salah Fajr) buổi sáng và vào (cuộc lễ nguyện Salah ''Asr) buổi chiều.');
INSERT INTO "hasan_abdulkarim" VALUES(33,43,'33:43','Ngài là Đấng ban phúc lành xuống cho các ngươi và các Thiên Thần của Ngài cũng làm thế (cầu xin Allah ban phúc lành và tha thứ cho các ngươi), để Ngài đưa các ngươi từ chỗ tăm tối ra chốn ánh sáng. Và Ngài hằng khoan dung đối với những người có đức tin.');
INSERT INTO "hasan_abdulkarim" VALUES(33,44,'33:44','Lời kính chào của họ vào ngày họ trình diện Ngài (Allah) sẽ là: ''Salam'' (Bằng an); và Ngài đã chuẩn bị cho họ một phần thưởng rộng rãi.');
INSERT INTO "hasan_abdulkarim" VALUES(33,45,'33:45','Hỡi Nabi (Muhammad!) Thật sự, TA đã cử phái Ngươi làm một nhân chứng, một người mang tin mừng và một người báo trước,');
INSERT INTO "hasan_abdulkarim" VALUES(33,46,'33:46','Và làm một người gọi mời (nhân loại) đến với Allah theo sự chấp thuận của Ngài; và như một ngọn đuốc (chiếc đèn) phát ra ánh sáng (soi đường);');
INSERT INTO "hasan_abdulkarim" VALUES(33,47,'33:47','Và hãy báo tin mừng cho những người có đức tin rằng họ sẽ tiếp nhận một bổng lộc rất lớn từ Allah.');
INSERT INTO "hasan_abdulkarim" VALUES(33,48,'33:48','Và chớ nghe lời những kẻ không có đức tin và những kẻ giả tạo đức tin và hãy bỏ qua thái độ xấc xược của chúng và hãy phó thác (công việc) cho Allah bởi vì Allah đủ làm một Đấng Thọ Thác.');
INSERT INTO "hasan_abdulkarim" VALUES(33,49,'33:49','Hỡi những ai có đức tin! Khi các ngươi kết hôn với những phụ nữ có đức tin rồi ly dị họ trước khi chạm đến mình họ, các ngươi sẽ không áp dụng thời gian ở vậy (''Iddah)( mà các ngươi tính đếm đối với họ. Nhưng hãy tặng họ một món quà và trả tự do cho họ một cách tốt đẹp.');
INSERT INTO "hasan_abdulkarim" VALUES(33,50,'33:50','Hỡi Nabi (Muhammad!) Ngài (Allah) làm Halal (hợp pháp) cho Ngươi các bà vợ mà Ngươi đã trả tiền cưới (Mahr), và những người mà tay phải của Ngươi làm chủ trong số các (nữ tù binh) mà Allah đã ban cho Ngươi; và con gái của chú bác của Ngươi; và con gái của người cô của Ngươi; và con gái của người cậu của Ngươi; và con gái của người dì của Ngươi đã cùng di cư (đến Madinah) với Ngươi; và (cưới) phụ nữ có đức tin nào tự hiến mình làm vợ của Nabi nếu Nabi muốn kết hôn với họ; điều khoản này đặc biệt dành cho Ngươi chứ không áp dụng cho những người có đức tin khác. TA biết đặc ân mà TA đã ban cấp cho họ liên hệ đến các bà vợ của họ và những nữ tù binh nằm trong tay của họ để Ngươi không gặp khó khăn (trong việc cưới vợ). Và Allah Hằng Tha Thứ, Rất Mực Khoan Dung.');
INSERT INTO "hasan_abdulkarim" VALUES(33,51,'33:51','Ngươi có thể dời lại (việc đi thăm) người vợ nào của Ngươi tùy Ngươi muốn và có thể tiếp bất cứ người (vợ) nào tùy Ngươi muốn; và Ngươi không bị khiển trách nếu muốn tiếp lại người (vợ) nào mà Ngươi đã dời (phiên của) họ (trước đó); như thế để làm dịu mắt họ (những người vợ) và để cho họ khỏi buồn và hài lòng với phần mà Ngươi đã ban cấp cho tất cả những người vợ của Ngươi. Và Allah biết điều nằm trong lòng của các ngươi. Và Allah Hằng Biết, Hằng Chịu đựng (với bầy tôi của Ngài).');
INSERT INTO "hasan_abdulkarim" VALUES(33,52,'33:52','Sau điều khoản này, Ngươi không được phép cưới thêm vợ nữa; và cũng không được thay đổi họ để lấy vợ khác dẫu rằng sắc đẹp của họ lôi cuốn Ngươi ngoại trừ phụ nữ (nữ tù binh) nào nằm trong tay phải của Ngươi. Và Allah là Đấng Giám Sát mọi việc.');
INSERT INTO "hasan_abdulkarim" VALUES(33,53,'33:53','Hỡi những ai có đức tin! Chớ bước vào nhà của Nabi trừ phi được mời đến dùng bữa và chớ đến (sớm hơn) khiến phải ngồi đợi cho đến khi thức ăn được nấu xong; ngược lại, hãy bước vào nhà (của Nabi) khi được mời; và sau khi các ngươi đã dùng bữa, hãy giải tán ra về; và chớ nấn ná ngồi lại tán gẫu. Quả thật, cách xử sự đó làm phiền Nabi và Y cảm thấy e ngại cho việc yêu cầu các ngươi ra về. Ngược lại, Allah không e ngại mà nói cho các ngươi biết sự thật. Và khi các ngươi muốn hỏi chuyện những người (vợ của Nabi) thì các ngươi hãy hỏi họ từ sau một bức màn. Cách đó trong sạch cho tấm lòng của các ngươi và cho tấm lòng của họ hơn. Các ngươi không được phép quấy rầy Vị Sứ Giả của Allah, cũng không bao giờ được phép kết hôn với vợ của Y sau (khi) Y (qua đời). Quả thật, điều đó dưới cái nhìn của Allah là một trọng tội.');
INSERT INTO "hasan_abdulkarim" VALUES(33,54,'33:54','Dẫu cho các người có tiết lộ hoặc giấu giếm một điều gì thì Allah vẫn là Đấng hằng biết hết mọi điều.');
INSERT INTO "hasan_abdulkarim" VALUES(33,55,'33:55','Các bà (vợ của Nabi) không mắc tội khi để lộ diện (không che mặt) trước mặt người cha, anh em trai, con trai của anh chị em ruột, trước mặt các bà (giúp việc), trước những người (nô lệ) nằm dưới tay phải của họ và hãy sợ Allah (hỡi các bà!) Quả thật, Allah là Đấng chứng giám mọi việc.');
INSERT INTO "hasan_abdulkarim" VALUES(33,56,'33:56','Quả thật, Allah và các Thiên Thần của Ngài đều Solawat( cho Nabi (Muhammad). Hỡi những ai có đức tin! Hãy chúc phúc cho Y (Muhammad) và chào Y bằng lời chào tốt lành.(144)');
INSERT INTO "hasan_abdulkarim" VALUES(33,57,'33:57','Quả thật, những ai quấy rầy Allah và Sứ Giả của Ngài thì sẽ bị Allah nguyền rủa ở đời này và đời sau; và Ngài đã chuẩn bị cho họ một sự trừng phạt nhục nhã.');
INSERT INTO "hasan_abdulkarim" VALUES(33,58,'33:58','Còn những ai quấy rầy những người tin tưởng nam và nữ một cách không thỏa đáng thì sẽ mang vào mình một tội vu cáo và một tội lỗi hiển nhiên.');
INSERT INTO "hasan_abdulkarim" VALUES(33,59,'33:59','Hỡi Nabi (Muhammad!) Hãy bảo những người vợ của Ngươi và các đứa con gái của Ngươi cũng những người vợ của những người có đức tin dùng áo choàng phủ kín thân mình của họ. Làm thế để dễ nhận biết họ và để họ không bị xúc phạm. Và Allah Hằng Tha Thứ, Rất Mực Khoan Dung.');
INSERT INTO "hasan_abdulkarim" VALUES(33,60,'33:60','Nếu những tên giả tạo đức tin và những kẻ mang trong lòng một căn bệnh (dâm dục) và những kẻ gây rối (phao tin thất thiệt) tại thành phố Madinah không chịu ngưng tay thì chắc chắn TA sẽ để cho Ngươi trị tội chúng rồi chúng sẽ sống nơi đó với Ngươi với tư cách người láng giềng nhưng chỉ trong một thời gian ngắn.');
INSERT INTO "hasan_abdulkarim" VALUES(33,61,'33:61','Chúng sẽ bị nguyền rủa ở nơi nào chúng bị phát giác và sẽ bị bắt và bị giết không một chút thương xót.');
INSERT INTO "hasan_abdulkarim" VALUES(33,62,'33:62','(Đó là) đường lối của Allah đối với những kẻ đã qua đời trước kia và Ngươi sẽ thấy không có môt sự thay đổi nào trong đường lối của Allah (đối với những kẻ bội phản).');
INSERT INTO "hasan_abdulkarim" VALUES(33,63,'33:63','Thiên hạ hỏi Ngươi về Giờ Tận Thế. Hãy bảo (họ): “Chỉ Allah biết nó thôi. Và điều gì làm cho ngươi biết được sự việc đó, biết đâu Giờ Tận Thế đang gần kề.”');
INSERT INTO "hasan_abdulkarim" VALUES(33,64,'33:64','Quả thật, Allah nguyền rủa những kẻ không có đức tin và Ngài đã chuẩn bị cho chúng Lửa ngọn (nơi hỏa ngục).');
INSERT INTO "hasan_abdulkarim" VALUES(33,65,'33:65','Chúng sẽ ở trong đó đời đời; và sẽ không tìm ra một Vị che chở hoặc một Vị cứu tinh nào cả.');
INSERT INTO "hasan_abdulkarim" VALUES(33,66,'33:66','Vào Ngày mà bộ mặt của chúng bị lật úp vào hỏa ngục, chúng sẽ than: “Thật khổ thân của chúng tôi! Phải chi chúng tôi vâng lệnh Allah và vâng lời Sứ Giả (Muhammad)!”');
INSERT INTO "hasan_abdulkarim" VALUES(33,67,'33:67','Và chúng sẽ thưa: “Lạy Thượng Đế của bầy tôi! Bầy tôi đã nghe theo các vị cầm đầu và các ông lớn của bầy tôi; bởi thế họ đã dắt bầy tôi đi lạc đường."');
INSERT INTO "hasan_abdulkarim" VALUES(33,68,'33:68','“Lạy Thượng Đế của bầy tôi! Xin phạt họ gấp đôi và rủa họ bằng lời nguyền rủa thậm tệ nhất.”');
INSERT INTO "hasan_abdulkarim" VALUES(33,69,'33:69','Hỡi những ai có đức tin! Chớ như những ai đã quấy rầy Musa bởi vì Allah đã giải oan cho Y khỏi những điều (vu khống) mà chúng đã nói; và Y là (một người bề tôi) đáng kính.');
INSERT INTO "hasan_abdulkarim" VALUES(33,70,'33:70','Hỡi những ai có đức tin! Hãy sợ Allah và nói năng thật thà.');
INSERT INTO "hasan_abdulkarim" VALUES(33,71,'33:71','Ngài sẽ cải thiện việc làm của các ngươi cho các ngươi và tha tội cho các ngươi; và ai vâng lệnh Allah và Sứ Giả của Ngài thì chắc chắn sẽ thành đạt vẽ vang.');
INSERT INTO "hasan_abdulkarim" VALUES(33,72,'33:72','Quả thật, TA đã trao việc ủy thác trọng trách và sứ mạng tinh thần cho các tầng trời và trái đất và núi non nhưng chúng từ chối và vì cả sợ nên đã tránh xa nó; Nhưng con người lãnh nó; rõ ràng y hết sức sai quấy, ngu muội.');
INSERT INTO "hasan_abdulkarim" VALUES(33,73,'33:73','(Làm thế) để Allah trừng phạt những tên giả tạo đức tin, nam và nữ; và (trừng phạt) những kẻ thờ đa thần nam và nữ; và để Ngài lượng thứ cho những người có đức tin, nam và nữ. Và Allah Hằng Tha Thứ, Rất Mực Khoan Dung.');
INSERT INTO "hasan_abdulkarim" VALUES(34,1,'34:1','Mọi lời ca tụng kính dâng Allah, Đấng mà mọi vật trong các tầng trời và mọi vật dưới đất đều là của Ngài và thuộc về Ngài mọi lời ca tụng ở đời sau; và Ngài là Đấng Rất Mực Sáng Suốt Đấng Am tường (mọi việc).');
INSERT INTO "hasan_abdulkarim" VALUES(34,2,'34:2','Ngài biết mọi vật chui vào lòng đất và mọi vật nhô lên khỏi mặt đất và mọi vật từ trên trời rơi xuống và mọi vật bay lên trời; và Ngài là Đấng Hằng Khoan Dung, Hằng Tha Thứ.');
INSERT INTO "hasan_abdulkarim" VALUES(34,3,'34:3','Và những kẻ không có đức tin bảo: “Giờ Tận Thế sẽ không xảy đến cho chúng tôi.” Hãy bảo họ: “Vâng, thề bởi (Allah) của ta, Đấng biết điều vô hình, chắc chắn Giờ Tận Thế sẽ xảy đến cho các người.” Không một vật gì trong các tầng trời và dưới đất có thể thoát khỏi (sự kiểm soát của) Ngài dù đó là sức nặng của một hạt nguyên tử (hay hạt cải) đi nữa; và không một vật gì dù nhỏ hay lớn hơn cái đó mà lại không được ghi sẵn trong một Quyển Sổ rõ ràng.');
INSERT INTO "hasan_abdulkarim" VALUES(34,4,'34:4','(Mục đích) để Ngài tưởng thưởng những ai có đức tin và làm việc thiện. Họ là những người sẽ được (Allah) tha thứ và sẽ được ban cấp bổng lộc rộng rãi.');
INSERT INTO "hasan_abdulkarim" VALUES(34,5,'34:5','Còn những ai phấn đấu để phá hỏng các Lời Mặc Khải của TA (Allah) thì chắc chắn sẽ chịu một sự trừng phạt đau đớn.');
INSERT INTO "hasan_abdulkarim" VALUES(34,6,'34:6','Và những ai đã được ban cho kiến thức thì thấy rằng điều (Mặc khải) mà Thượng Đế của Ngươi đã ban xuống cho Ngươi là Chân Lý; và hướng dẫn (nhân loại) đến con đường của Đấng Toàn Năng, Đấng Rất Đáng Ca Tụng.');
INSERT INTO "hasan_abdulkarim" VALUES(34,7,'34:7','Những kẻ không có đức tin bảo: “Há quí vị muốn chúng tôi chỉ cho quí vị một người (Muhammad), y sẽ cho quí vị biết khi quí vị đã hoàn toàn mục rã (dưới đất) quí vị sẽ được tạo hóa mới trở lại?”');
INSERT INTO "hasan_abdulkarim" VALUES(34,8,'34:8','Phải chăng y đã nói dối rồi đổ thừa cho Allah hay là y bị quỷ ám? Không, những ai không tin tưởng nơi đời sau thì sẽ bị trừng phạt và lầm lạc rất xa.');
INSERT INTO "hasan_abdulkarim" VALUES(34,9,'34:9','Há họ không nhìn thấy gì trước mắt và sau lưng họ từ trên trời xuống dưới đất ư? Nếu muốn, TA (Allah) thừa sức làm cho đất sụp xuống nuốt tiêu mất họ hoặc làm cho một mảnh trời rơi xuống đè chết họ. Quả thật, nơi sự việc đó là một dấu hiệu cho từng bề tôi quay về hối cải (với Allah).');
INSERT INTO "hasan_abdulkarim" VALUES(34,10,'34:10','Và chắc chắn TA đã ban cho Dawood thiên ân của TA, (phán bảo): "Hỡi núi non! Hãy cùng với y tán dương (Allah); và hỡi chim chóc (cũng thế), và TA đã hóa sắt thành mềm cho y.”');
INSERT INTO "hasan_abdulkarim" VALUES(34,11,'34:11','(Và bảo y): “Hãy chế áo giáp và ráp các vòng sắt thật cân bằng và hãy làm việc thiện (hỡi Dawood và con cháu của Ngươi!) Quả thật, TA (Allah) hằng thấy điều các ngươi làm.”');
INSERT INTO "hasan_abdulkarim" VALUES(34,12,'34:12','Và (TA) bắt gió phục tùng Sulayman; bởi thế (tốc độ của) gió lướt đi một buổi sáng bằng (thời gian di chuyển của) một tháng và một buổi chiều bằng (thời gian di chuyển của) một tháng. Và TA đã hóa một ngọn suối bằng thau (nấu chảy) chảy ra cho y; và trong loài Jinn có những tên làm việc đằng trước y theo Phép của Thượng Đế của y; và nếu có tên (Jinn) nào cãi lệnh của TA thì TA sẽ cho nó nếm hình phạt của Lửa ngọn cháy bùng.');
INSERT INTO "hasan_abdulkarim" VALUES(34,13,'34:13','Chúng (Jinn) làm ra cho y (Sulayman) những vật mà y muốn: những cung điện, những hình tượng, những chén bát lớn như những bể nước và những chảo nấu cố định một chỗ. “Hỡi gia đình (dòng dõi của) Dawood. Hãy làm việc với lòng tri ân (Allah)!” Nhưng chỉ một số ít bầy tôi của TA tỏ lòng biết ơn.');
INSERT INTO "hasan_abdulkarim" VALUES(34,14,'34:14','Nhưng khi TA quyết định cho y (Sulayman) chết, chẳng có gì làm cho chúng (Jinn) thấy cái chết của y ngoại trừ một loại sâu đất đã gặm mòn chiếc gậy của y. Bởi thế, khi y ngã xuống, loài Jinn mới vỡ lẽ rằng nếu biết được điều vô hình thì chúng đâu có phải ở lại lâu trong sự trừng phạt nhục nhã đó.');
INSERT INTO "hasan_abdulkarim" VALUES(34,15,'34:15','Chắc chắn có một dấu hiệu cho người dân Saba'' (Sê-ba) ngay tại chỗ của họ ở: hai ngôi vườn nằm bên phải và bên trái. Hãy ăn bổng lộc của Thượng Đế của các ngươi và biết ơn Ngài; một thành phố tốt đẹp (với) một Thượng Đế Hằng Tha Thứ.');
INSERT INTO "hasan_abdulkarim" VALUES(34,16,'34:16','Nhưng họ quay lánh xa. Bởi thế, TA đã gởi một trận thủy tai từ đập nước đến phạt họ và biến hai ngôi vườn của họ thành hai ngôi vườn cho mọc ra loại trái đắng và loại cây tân hoa liễu và một số ít cây kè.');
INSERT INTO "hasan_abdulkarim" VALUES(34,17,'34:17','TA bắt phạt họ như thế vì tội phụ ơn của họ. Và phải chăng TA chỉ bắt phạt những kẻ vong ân?');
INSERT INTO "hasan_abdulkarim" VALUES(34,18,'34:18','Giữa họ (người dân của thị trấn Saba'') và những thị trấn mà TA đã ban phúc, TA đã đặt những thị trấn (khác) dễ nhìn thấy từ xa và sắp đặt nơi đó những chặng đường du lịch dễ đi; do đó, hãy đi du lịch an toàn ban đêm và ban ngày (qua những địa điểm đó).');
INSERT INTO "hasan_abdulkarim" VALUES(34,19,'34:19','Nhưng họ thưa: “Lạy Thượng Đế chúng tôi! Xin Ngài làm cho những chặng đường du lịch của chúng tôi dài xa thêm.” Nhưng (mong như thế), họ tự làm hại bản thân mình. Bởi thế, TA đã lấy họ làm thành những câu chuyện và đã phân tán họ toàn bộ, tản mác khắp nơi. Quả thật, nơi sự việc đó là những dấu hiệu cho từng người kiên nhẫn, biết ơn.');
INSERT INTO "hasan_abdulkarim" VALUES(34,20,'34:20','Và chắc chắn Iblis đã chứng minh điều mà nó phỏng đoán về họ đúng với sự thật. Bởi thế, họ nghe theo nó ngoại trừ một phần tử nhỏ trong số những người có đức tin.');
INSERT INTO "hasan_abdulkarim" VALUES(34,21,'34:21','Và thật ra, Iblis không có một chút quyền hành nào đối với họ mà đó chỉ là để cho TA biết rõ ai là người tin tưởng nơi cõi đời sau so với ai trong họ là người hãy còn nghi ngờ về cõi đó; và Thượng Đế (Allah) của Ngươi là Đấng Giám Sát tất cả mọi điều.');
INSERT INTO "hasan_abdulkarim" VALUES(34,22,'34:22','Hãy bảo họ (hỡi Muhammad!): “Hãy cầu nguyện ngoài Allah những kẻ mà các người xác nhận (là thần linh của các người). Chúng chẳng kiểm soát được một vật gì trong các tầng trời và trái đất cho dù đó là sức nặng của một hạt nguyên tử (hay hạt cải) đi nữa; và chúng cũng không có một phần chia nào trong trời đất; và trong bọn chúng không có một tên nào là vị ủng hộ của Ngài cả."');
INSERT INTO "hasan_abdulkarim" VALUES(34,23,'34:23','Và việc can thiệp với Ngài (Allah) chẳng có hiệu lực gì trừ phi với ai mà Ngài cho phép. Mãi đến khi lòng của họ( hết sợ, lúc đó họ mới lên tiếng hỏi: “Thượng Đế của quí vị phán gì vậy?” Họ (các tín đồ) đáp: “Chân Lý.” Bởi vì Ngài là Đấng Tối Cao, Đấng Vĩ Đại.');
INSERT INTO "hasan_abdulkarim" VALUES(34,24,'34:24','Hãy bảo họ “Ai cung cấp bổng lộc cho các người từ các tầng trời xuống dưới đất?” Hãy bảo họ: “Allah!” "(Giữa chúng ta), chúng tôi hay quí vị sẽ là người công khai đi đúng đường hay lạc đường.”');
INSERT INTO "hasan_abdulkarim" VALUES(34,25,'34:25','Hãy bảo họ: “Quí vị sẽ không bị tra hỏi về tội của chúng tôi và chúng tôi cũng sẽ không bị tra hỏi về điều quí vị đã làm.”');
INSERT INTO "hasan_abdulkarim" VALUES(34,26,'34:26','Hãy bảo tiếp: “Thượng Đế của chúng ta sẽ tập trung chúng ta trở lại rồi Ngài sẽ xét xử giữa chúng ta bằng sự thật bởi vì Ngài là Đại Thẩm Phán Toàn Tri ”');
INSERT INTO "hasan_abdulkarim" VALUES(34,27,'34:27','Hãy bảo họ: “Hãy chỉ cho ta đâu là những kẻ mà các người đã cho là những vị đối tác ngang vai của Ngài (Allah). Nhất định không có ai. Không, Ngài là Allah, Đấng Toàn Năng, Đấng Chí Minh."');
INSERT INTO "hasan_abdulkarim" VALUES(34,28,'34:28','Và TA (Allah) chỉ cử phái Ngươi (Muhammad) làm một Người mang tin mừng và một Người cảnh báo cho (tất cả) nhân loại nhưng đa số người không biết.');
INSERT INTO "hasan_abdulkarim" VALUES(34,29,'34:29','Và họ bảo: "Thế chừng nào Lời Hứa (về việc xét xử) này sẽ xảy ra nếu quí vị (Muslim) nói thật?”');
INSERT INTO "hasan_abdulkarim" VALUES(34,30,'34:30','Hãy bảo họ: “Cuộc hẹn cho quí vị sẽ là ngày mà quí vị không thể dời lại cũng không thể giục đến sớm hơn một giờ khắc nào.”');
INSERT INTO "hasan_abdulkarim" VALUES(34,31,'34:31','Và những kẻ không có đức tin bảo: “Chúng tôi sẽ không bao giờ tin nơi Qur''an này và cũng không tin nơi điều (Kinh Sách) nào trước nó.” Giá Ngươi (Muhammad) có thể nhìn thấy được tình cảnh của những kẻ làm điều sai quấy bị bắt đứng trước mặt Thượng Đế của chúng, chúng lời qua tiếng lại trách móc lẫn nhau. Những kẻ tự cho mình yếu thế sẽ bảo những tên cường bạo: “Nếu không vì nể nang quí ngài thì chúng tôi đã trở thành người tin tưởng rồi!”');
INSERT INTO "hasan_abdulkarim" VALUES(34,32,'34:32','Những tên cường bạo sẽ đáp lời những kẻ tự cho mình yếu thế, bảo: “Phải chăng bọn ta đã ngăn cản không cho các ngươi theo Chỉ Đạo khi nó đến với các ngươi?” Không, chính các ngươi mới là những kẻ tội lỗi.');
INSERT INTO "hasan_abdulkarim" VALUES(34,33,'34:33','Và những kẻ tự cho mình yếu thế sẽ đáp lời những tên cường bạo: “Không, quí ngài đã cùng nhau bày mưu cả đêm lẫn ngày khi quí ngài ra lệnh cho chúng tôi bảo phải phủ nhận Allah và dựng những đối thủ ngang vai với Ngài.” Chúng sẽ hối hận khi nhìn thấy hình phạt. TA sẽ đóng những chiếc gông vào cổ của những kẻ phủ nhận đức tin. Phải chăng chúng bị phạt chỉ vì tội mà chúng đã làm?');
INSERT INTO "hasan_abdulkarim" VALUES(34,34,'34:34','Và không một người cảnh báo nào được TA cử đến một thị trân mà không bị những người giàu có của nó tuyên bố: “Chúng tôi không tin nơi thông điệp mà quí vị mang đến cho chúng tôi.”');
INSERT INTO "hasan_abdulkarim" VALUES(34,35,'34:35','Và họ bảo: “Chúng tôi có nhiều của cải và đông con và chúng tôi sẽ không bị trừng phạt.”');
INSERT INTO "hasan_abdulkarim" VALUES(34,36,'34:36','Hãy bảo họ: “Quả thật, Thượng Đế của Ta thu hẹp hay nới rộng bổng lộc của Ngài cho ai mà Ngài muốn, nhưng đa số nhân loại không biết.”');
INSERT INTO "hasan_abdulkarim" VALUES(34,37,'34:37','Và không phải của cải cũng không phải con cái của các ngươi là những thứ sẽ đưa các ngươi đến gần TA theo cấp bậc, mà ngược lại chỉ ai có đức tin và làm việc thiện, bởi vì họ là những người sẽ được tưởng thưởng gấp đôi do những điều (tốt) họ đã làm và sẽ ở trong những ngôi nhà an toàn trên cao.');
INSERT INTO "hasan_abdulkarim" VALUES(34,38,'34:38','Còn những ai tích cực hoạt động để phá hỏng các dấu hiệu của TA thì sẽ bị đưa đến chỗ trừng phạt.');
INSERT INTO "hasan_abdulkarim" VALUES(34,39,'34:39','Hãy bảo họ: “Thượng Đế của Ta nới rộng hay thu hẹp bổng lộc của Ngài cho ai mà Ngài muốn trong số bầy tôi của Ngài. Và bất cứ của cải nào mà các người chi dùng (cho Chính Nghĩa của Ngài) thì sẽ được Ngài hoàn lại bởi vì Ngài là Đấng Cung Dưỡng Tốt Nhất.”');
INSERT INTO "hasan_abdulkarim" VALUES(34,40,'34:40','Vào Ngày mà Ngài sẽ tập trung tất cả bọn chúng lại rồi phán bảo các Thiên Thần: “Có phải các ngươi là những kẻ mà bọn người này đã từng tôn thờ?”');
INSERT INTO "hasan_abdulkarim" VALUES(34,41,'34:41','Các Thiên Thần sẽ thưa: “Quang vinh thay Ngài! Ngài là Chủ Nhân của bầy tôi chứ đâu phải chúng. Không, chúng tôn thờ Jinn. Đa số bọn chúng tin tưởng nơi chúng (Jinn).”');
INSERT INTO "hasan_abdulkarim" VALUES(34,42,'34:42','Ngày nay (Ngày phục sinh), không ai được quyền trục lợi và hãm hại lẫn nhau. Và TA sẽ bảo những ai làm điều sai quấy: “Hãy nếm hình phạt của hỏa ngục mà các ngươi đã từng cho là giả tạo.”');
INSERT INTO "hasan_abdulkarim" VALUES(34,43,'34:43','Và khi các Lời Mặc Khải minh bạch của TA được đọc ra cho chúng thì chúng bảo: “(Muhammad) này chỉ là một người phàm. Y muốn cản trở quí vị tôn thờ những vật mà cha mẹ của quí vị đã từng tôn thờ.” Chúng bảo: “Đây chỉ là điều giả dối khéo bịa đặt.” Và những kẻ phủ nhận đức tin nói về Chân Lý khi nó đến với chúng như sau: “Đây chỉ là điều mê hoặc hiển hiện.”');
INSERT INTO "hasan_abdulkarim" VALUES(34,44,'34:44','Nhưng TA đã không ban cho chúng (người Quraish) các Kinh Sách mà chúng có thể học hỏi; và cũng không cử trước Ngươi một người cảnh báo nào đến với chúng.');
INSERT INTO "hasan_abdulkarim" VALUES(34,45,'34:45','Và những kẻ trước chúng cũng đã phủ nhận (Chân Lý). Những người (Quraish) này đã không nhận được một phần mười ân huệ mà TA đã ban cho các tiền nhân của chúng, thế mà chúng đã cho các Sứ Giả của TA nói dối. Bởi thế, sự trừng phạt của TA sẽ khủng khiếp ra sao.');
INSERT INTO "hasan_abdulkarim" VALUES(34,46,'34:46','Hãy bảo chúng (hỡi Muhammad!) "Ta chỉ khuyên các người một điều: hãy vì Allah các người đứng lên từng cặp hay từng người rồi tự suy nghĩ; (thì sẽ thấy) người bạn (Muhammad) của các người đã không bị quỷ ám (điên) mà chỉ là một người báo trước về một sự trừng phạt khủng khiếp sắp đến.”');
INSERT INTO "hasan_abdulkarim" VALUES(34,47,'34:47','Hãy bảo: “Phần thưởng mà ta có thể hỏi từ các người là của các người; còn phần thưởng của ta thì do Allah (ban cấp) bởi vì Ngài chứng giám tất cả mọi việc."');
INSERT INTO "hasan_abdulkarim" VALUES(34,48,'34:48','Hãy bảo: “Quả thật, Thượng Đế của Ta quẳng ban Chân Lý xuống; Ngài thấu rõ điều vô hình.”');
INSERT INTO "hasan_abdulkarim" VALUES(34,49,'34:49','Hãy bảo: “Chân Lý (Qur’an) đã đến thì sự Ngụy Tạo không khởi sự cũng không phục hồi được gì.”');
INSERT INTO "hasan_abdulkarim" VALUES(34,50,'34:50','Hãy bảo: “Nếu ta lầm lạc thì chỉ bản thân ta lầm lạc; và nếu ta được hướng dẫn thì là do điều mặc khải mà Allah đã ban cấp cho ta bởi vì Ngài Hằng Nghe và Rất Gần Kề."');
INSERT INTO "hasan_abdulkarim" VALUES(34,51,'34:51','Và nếu Ngươi (Muhammad) có thể nhìn thấy được tình trạng mà chúng bị hãi hùng bởi vì chúng sẽ không trốn thoát được và sẽ bị túm bắt từ một nơi rất gần.');
INSERT INTO "hasan_abdulkarim" VALUES(34,52,'34:52','Và chúng sẽ bảo: “(Bây giờ) chúng tôi tin nơi Nó (Qur’an);” nhưng làm sao chúng có thể nhận được đức tin từ một nơi quá xa!');
INSERT INTO "hasan_abdulkarim" VALUES(34,53,'34:53','Và chắc chắn, chúng đã không tin nơi Chân Lý từ trước và đã từng phỏng chừng về điều vô hình từ một nơi xa.');
INSERT INTO "hasan_abdulkarim" VALUES(34,54,'34:54','Và giữa chúng với những điều mà chúng mong ước (Chân Lý) có một bức chắn ngăn cách, tương tự trường hợp đã xảy ra cho đồng bọn của chúng trong quá khứ. Quả thật, chúng là đám người ở trong tình trạng ngờ vực trầm trọng.');
INSERT INTO "hasan_abdulkarim" VALUES(35,1,'35:1','Mọi lời ca tụng đều thuộc về Allah, Đấng đã sáng tạo các tầng trời và trái đất; Ngài đã chỉ định các Thiên Thần làm thiên sứ có cánh: hai, hoặc ba, hoặc bốn (đôi). Ngài thêm vào sự tạo hóa bất cứ cái gì Ngài muốn bởi vì Ngài thừa khả năng làm được tất cả mọi vật (việc).');
INSERT INTO "hasan_abdulkarim" VALUES(35,2,'35:2','Bất cứ Hồng Ân nào mà Allah ban cho nhân loại, không ai có quyền giữ lại và cái nào mà Ngài giữ lại thì không ai ngoài Ngài có quyền ban phát nó sau đó. Bởi vì Ngài là Đấng Toàn Năng, Đấng Chí Minh.');
INSERT INTO "hasan_abdulkarim" VALUES(35,3,'35:3','Hỡi nhân loại! Hãy nhớ Ân huệ của Allah đã ban cho các ngươi. Có chăng một Đấng Tạo Hóa nào khác Allah đã cung phụng cho các ngươi từ trên trời xuống dưới đất? Không có Thượng Đế nào cả duy chỉ Ngài. Thế sao các ngươi quay lánh xa?');
INSERT INTO "hasan_abdulkarim" VALUES(35,4,'35:4','Và nếu chúng phủ nhận Ngươi (Muhammad) thì chắc chắn các Sứ Giả trước Ngươi cũng đã bị chúng phủ nhận, nhưng mọi vấn đề sẽ được trình về cho Allah quyết định.');
INSERT INTO "hasan_abdulkarim" VALUES(35,5,'35:5','Hỡi nhân loại! Chắc chắn Lời Hứa của Allah là sự thật. Bởi thế chớ để cho đời sống trần tục này đánh lừa các ngươi. Và chớ để cho tên đại bịp (Shaytan) lừa gạt các ngươi về Allah.');
INSERT INTO "hasan_abdulkarim" VALUES(35,6,'35:6','Quả thật, Shaytan là kẻ thù của các ngươi. Vậy hãy đối xử với nó như một kẻ thù. Nó chuyên dụ dỗ đồ đệ của nó để làm cho chúng trở thành những người bạn của Lửa ngọn.');
INSERT INTO "hasan_abdulkarim" VALUES(35,7,'35:7','Những ai không có đức tin thì sẽ bị trừng phạt nghiêm khắc; ngược lại, những ai có đức tin và làm việc thiện thì sẽ được tha thứ và sẽ được ban thưởng rất lớn.');
INSERT INTO "hasan_abdulkarim" VALUES(35,8,'35:8','Thế hẳn một người xem việc làm bất lương của y đẹp mắt rồi tự cho đó là điều tốt (có bằng với một người ngoan đạo hay không?) Bởi vì thật ra Allah đánh lạc hướng kẻ nào mà Ngài muốn và hướng dẫn người nào Ngài muốn; Bởi thế chớ vì thương xót giùm cho chúng mà Ngươi (Muhammad) tự làm hao mòn thân xác. Quả thật, Allah hằng biết về những điều chúng làm.');
INSERT INTO "hasan_abdulkarim" VALUES(35,9,'35:9','Và Allah là Đấng đã gởi các luồng gió bay đi; vì thế chúng thổi đưa đám mây lên cao và TA đưa nó đến một vùng đất khô cằn; rồi dùng nó để làm sống lại mảnh đất đã chết khô. Và việc phục sinh (người chết) sẽ giống như thế.');
INSERT INTO "hasan_abdulkarim" VALUES(35,10,'35:10','Ai muốn vinh quang thì tất cả mọi vinh quang đều là của Allah cả. Mọi lời nói tốt đẹp sẽ bốc lên tận Ngài và mọi việc làm phúc thiện sẽ tâng bốc nó trong lúc những ai âm mưu điều bất lương thì sẽ bị trừng phạt nghiêm khắc. Và mưu đồ của những kẻ đó sẽ hoàn toàn bị thất bại.');
INSERT INTO "hasan_abdulkarim" VALUES(35,11,'35:11','Và Allah đã tạo hóa các người từ đất bụi; rồi từ một giọt tinh dịch; rồi Ngài làm cho các người thành cặp (vợ chồng). Và không một phụ nữ nào mang thai hoặc hạ sanh (con) mà Ngài không biết. Và không một người nào sống thọ hay giảm thọ mà lại không được ghi sẵn trong một Quyển sổ (Định mệnh) bởi vì quả thật, điều đó rất dễ đối với Allah.');
INSERT INTO "hasan_abdulkarim" VALUES(35,12,'35:12','Và hai biển nước không giống nhau. Một loại thì ngọt, dễ uống và một loại thì mặn, chát. Nhưng các ngươi được ăn thịt tươi từ mỗi loại biển cả và từ đó (mò) lấy đồ nữ trang để đeo. Và ngươi thấy những chiếc tàu (buồm) cày sóng đưa các ngươi đi tìm thiên lộc của Ngài và để cho các ngươi tri ân Ngài.');
INSERT INTO "hasan_abdulkarim" VALUES(35,13,'35:13','Ngài nhập ban đêm vào ban ngày và nhập ban ngày vào ban đêm; và Ngài chế ngự mặt trời và mặt trăng (theo định luật của Ngài); mỗi cái di chuyển (theo quỹ đạo của nó) đến một thời hạn ấn định. Allah, Thượng Đế của các người là thế. Ngài nắm quyền thống trị (vạn vật). Ngược lại, những kẻ mà các người cầu nguyện không phải là Ngài không kiểm soát được một lớp vỏ mỏng.');
INSERT INTO "hasan_abdulkarim" VALUES(35,14,'35:14','Nếu các người cầu nguyện chúng thì chúng sẽ không nghe được lời cầu của các người và nếu chúng có nghe đặng thì chúng vẫn không đáp lại lời cầu của các người; và vào Ngày phục sinh, chúng sẽ phủ nhận việc các người ''tôn thờ'' chúng. Và không ai có thể báo cho Ngươi (Muhammad!) biết (sự thật) giống như Đấng Am Tường (Allah).');
INSERT INTO "hasan_abdulkarim" VALUES(35,15,'35:15','Hỡi nhân loại! Các ngươi mới là những kẻ nghèo hèn nhờ vả Allah; ngược lại, Allah Rất mực Giàu Có, Rất Đáng Ca Tụng.');
INSERT INTO "hasan_abdulkarim" VALUES(35,16,'35:16','Nếu muốn, Ngài sẽ xóa bỏ các ngươi và lập nên một sự tạo hóa mới (thay thế các ngươi).');
INSERT INTO "hasan_abdulkarim" VALUES(35,17,'35:17','Và việc đó chẳng có gì nặng nhọc đối với Allah cả.');
INSERT INTO "hasan_abdulkarim" VALUES(35,18,'35:18','Và không một người khuân vác nào sẽ vác giùm gánh nặng (tội lỗi) của người khác cả; và nếu một người nào đó vác quá sức của mình cầu cứu một người khác vác giùm gánh nặng của y thì không một tí gánh nặng (tội lỗi) nào của y sẽ được (người khác) vác giùm dẫu đó là bà con ruột thịt của y đi nữa. Ngươi (Muhammad) chỉ có thể cảnh báo những ai sợ Đấng Thượng Đế vô hình của họ và dâng lễ nguyện Salah một cách chu đáo; và ai tự tẩy sạch (tội lỗi) thì việc tẩy sạch đó chỉ có lợi cho bản thân mình.''Và cuối cùng (tất cả) đều trở về gặp Allah.');
INSERT INTO "hasan_abdulkarim" VALUES(35,19,'35:19','Bởi vì người mù và người sáng mắt không như nhau;');
INSERT INTO "hasan_abdulkarim" VALUES(35,20,'35:20','Bóng tối và ánh sáng cũng thế;');
INSERT INTO "hasan_abdulkarim" VALUES(35,21,'35:21','Bóng mát và sức nóng của mặt trời cũng thế;');
INSERT INTO "hasan_abdulkarim" VALUES(35,22,'35:22','Người sống và người chết cũng không giống nhau. Quả thật, Allah có thể làm cho người nào mà Ngài muốn nghe được; nhưng Ngươi (Muhammad) không thể làm cho người nào nằm dưới mộ nghe được.');
INSERT INTO "hasan_abdulkarim" VALUES(35,23,'35:23','Bởi vì quả thật, Ngươi chỉ là một Người báo trước.');
INSERT INTO "hasan_abdulkarim" VALUES(35,24,'35:24','Quả thật! TA (Allah) cử phái Ngươi (Muhammad) mang Chân Lý đến, vừa làm một Người báo tin lành, báo tin dữ; và không một Cộng đồng nào mà không có một Người cảnh báo đến với họ.');
INSERT INTO "hasan_abdulkarim" VALUES(35,25,'35:25','Và nếu chúng phủ nhận Ngươi thì chắc chắn những kẻ trước chúng cũng đã phủ nhận như thế. Các Sứ Giả của chúng đã đến gặp chúng với những bằng chứng rõ rệt và với những quyển Kinh (Zubur) và với một Kinh Sách soi sáng.');
INSERT INTO "hasan_abdulkarim" VALUES(35,26,'35:26','Rồi, TA bắt phạt những kẻ phủ nhận đức tin. Bởi thế, hãy xem sự trừng phạt của TA khủng khiếp thế nào!');
INSERT INTO "hasan_abdulkarim" VALUES(35,27,'35:27','Há Ngươi không nhận thấy việc Allah ban nước mưa từ trên trời xuống? TA (Allah) dùng nó để làm mọc ra hoa quả mang nhiều màu sắc khác biệt; và trên núi có những đường rạch trắng và đỏ, và những màu sắc khác biệt. Và (cũng có những vật) màu đen sậm.');
INSERT INTO "hasan_abdulkarim" VALUES(35,28,'35:28','Và loài người và dã thú và gia súc cũng mang những màu sắc khác biệt như thế. Trong số bầy tôi của Ngài (Allah), chỉ những ai hiểu biết mới sợ Allah. Quả thật, Allah Toàn Năng, Hằng Tha Thứ.');
INSERT INTO "hasan_abdulkarim" VALUES(35,29,'35:29','Quả thật, những ai đọc Kinh Sách của Allah và dâng lễ nguyện Salah một cách chu đáo và chi dùng kín đáo hay công khai những vật (tài sản) mà TA đã cung cấp (để làm việc thiện) thì sẽ hy vọng nơi một sự đổi chác không bao giờ thất bại.''');
INSERT INTO "hasan_abdulkarim" VALUES(35,30,'35:30','Để Ngài trả lại đầy đủ công lao của họ và tưởng thưởng họ thêm với thiên lộc của Ngài bởi vì quả thật Ngài Hằng Tha Thứ, Hằng ghi công (cho họ).');
INSERT INTO "hasan_abdulkarim" VALUES(35,31,'35:31','Và những điều mà TA đã mặc khải cho Ngươi (Muhammad) trong Kinh Sách (Qur’an) là Chân Lý, xác nhận lại những điều đã ban xuống trước Nó. Quả thật, Allah Rất Mực Am Tường và Hằng Thấy rõ các bầy tôi của Ngài.');
INSERT INTO "hasan_abdulkarim" VALUES(35,32,'35:32','Rồi TA cho những ai trong số bầy tôi của TA mà TA đã chọn lựa thừa hưởng Kinh Sách (Qur’an). Nhưng trong họ, có người tự làm hại bản thân mình, và có người thì theo con đường chính giữa và có người, với sự chấp thuận của Allah thì tiên phong làm những việc tốt. Đó mới thật là thiên lộc lớn nhất:');
INSERT INTO "hasan_abdulkarim" VALUES(35,33,'35:33','Những Ngôi Vườn Vĩnh Cửu (thiên đàng ) mà họ sẽ đi vào; trong đó, họ sẽ đeo vòng vàng và ngọc trai và sẽ mặc áo lụa là.');
INSERT INTO "hasan_abdulkarim" VALUES(35,34,'35:34','Và họ sẽ thưa: “Mọi lời ca tụng đều dâng lên Allah, Đấng đã làm cho chúng tôi hết ưu phiền. Quả thật, Thượng Đế của chúng tôi Hằng Tha Thứ và Hằng ghi công (cho chúng tôi)!');
INSERT INTO "hasan_abdulkarim" VALUES(35,35,'35:35','Đấng đã dùng thiên lộc của Ngài mà định cư chúng tôi nơi mái nhà vĩnh cửu, một nơi mà chúng tôi khỏi phải vất vả cực nhọc và lao tâm (như trước).');
INSERT INTO "hasan_abdulkarim" VALUES(35,36,'35:36','Còn đối với những ai không có đức tin thì sẽ bị phạt bằng Lửa của hỏa ngục. Chúng sẽ không được quyết định cho chết phứt; và cũng không được giảm nhẹ hình phạt của Lửa (hỏa ngục). Đấy, TA bắt phạt tất cả những kẻ không có đức tin như thế.');
INSERT INTO "hasan_abdulkarim" VALUES(35,37,'35:37','Và chúng sẽ la hét trong đó, thưa : “Lạy Thượng Đế của bầy tôi! Xin thả chúng tôi ra. Chúng tôi hứa sẽ làm điều thiện khác với điều tội lỗi mà chúng tôi đã từng làm.” (Sẽ có lời bảo chúng): “Há TA đã không cho các ngươi sống đủ lâu để cho ai được nhắc nhở sẽ nhận thông điệp nơi đó hay sao? Và (hơn nữa) đã có Người báo trước đến gặp các ngươi. Thôi, hãy nếm (Lửa). Bởi vì không có vị cứu tinh nào đến giúp đỡ những kẻ làm điều sai quấy cả.');
INSERT INTO "hasan_abdulkarim" VALUES(35,38,'35:38','Quả thật, Allah là Đấng Biết rõ điều vô hình của các tầng trời và trái đất. Rõ thật Ngài biết điều (bí mật) nằm trong lòng con người.');
INSERT INTO "hasan_abdulkarim" VALUES(35,39,'35:39','Ngài là Đấng đã làm cho các ngươi thành những người kế nghiệp trên trái đất. Bởi thế, ai không có đức tin thì sẽ lãnh đủ về sự không tin của mình. Và sự không tin của những kẻ vô đức tin chỉ làm cho chúng thêm đáng ghét dưới cái nhìn của Thượng Đế của chúng. Và sự không tin của những kẻ không có đức tin chỉ làm cho chúng thêm mất mát.');
INSERT INTO "hasan_abdulkarim" VALUES(35,40,'35:40','Hãy bảo chúng: “Các người có nhìn thấy những thần linh của các người mà các người cầu nguyện ngoài Allah hay không? Hãy chỉ cho ta thấy cái (vật) mà chúng đã tạo dưới đất? Hoặc chúng có phần hùn hạp nào trên các tầng trời hay không? Hoặc TA có ban cho chúng Kinh Sách nhờ đó chúng có được bằng chứng rõ rệt hay không? Không. Những kẻ làm điều sai quấy chỉ hứa hão với nhau.');
INSERT INTO "hasan_abdulkarim" VALUES(35,41,'35:41','Quả thật, Allah nắm vững các tầng trời và trái đất e rằng chúng ngưng (hoạt động). Và nếu chúng thật sự ngưng (hoạt động) thì sau Ngài chẳng có ai có đủ khả năng nắm giữ (lèo lái) chúng lại được. Quả thật, Ngài (Allah) Hằng Chịu Đựng, Hằng Tha Thứ.');
INSERT INTO "hasan_abdulkarim" VALUES(35,42,'35:42','Và chúng mang Allah ra thề thốt bằng lời thề nghiêm trọng nhất (nói rằng) nếu có một người báo trước nào đến gặp chúng thì chắc chắn chúng sẽ được hướng dẫn hơn bất cứ cộng đồng nào khác. Nhưng khi một Vị báo trước (Muhammad) đến gặp chúng thì việc đó chỉ làm cho chúng chạy xa thêm.');
INSERT INTO "hasan_abdulkarim" VALUES(35,43,'35:43','Chúng ngạo mạn trong xứ và âm mưu làm điều tội lỗi. Nhưng mưu đồ độc ác đó không vây hãm ai khác ngoài kẻ chủ mưu. Phải chăng chúng chỉ mong đợi đường lối của các tiền nhân? Bởi thế, Ngươi sẽ không thấy có một sự thay đổi nào trong đường lối (xử lý) của Allah, và sẽ thấy không có sự chuyển hướng nào trong đường lối xử lý của Allah.');
INSERT INTO "hasan_abdulkarim" VALUES(35,44,'35:44','Há chúng đã không đi xa trên trái đất để chứng kiến kết cuộc của những kẻ trước chúng dẫu rằng họ mạnh hơn chúng về quyền lực? Và Allah là Đấng mà không,vật gì trong các tầng trời và trái đất có thể làm hỏng (kế hoạch của) Ngài đặng. Bởi vì Ngài Toàn Tri, Toàn Lực.');
INSERT INTO "hasan_abdulkarim" VALUES(35,45,'35:45','Và nếu Allah bắt phạt loài người vì tội mà họ đã phạm thì Ngài sẽ không để trên mặt đất một loại sinh vật nào sống đặng, nhưng Ngài gia hạn cho họ sống đến một thời kỳ ấn định. Bởi thế, khi thời hạn của họ đã mãn thì chắc chắn Allah sẽ thấy lại tất cả bầy tôi của Ngài.');
INSERT INTO "hasan_abdulkarim" VALUES(36,1,'36:1','Ya-Sin');
INSERT INTO "hasan_abdulkarim" VALUES(36,2,'36:2','Thề bởi Qur''an Cao Minh.');
INSERT INTO "hasan_abdulkarim" VALUES(36,3,'36:3','Chắc chắn Ngươi (Muhammad) đích thực là Vị Sứ Giả (của Allah).');
INSERT INTO "hasan_abdulkarim" VALUES(36,4,'36:4','Đang ở trên con đường Ngay Chính (Islam).');
INSERT INTO "hasan_abdulkarim" VALUES(36,5,'36:5','(Qur''an này) do Đấng Toàn Năng, Đấng Khoan Dung ban xuống.');
INSERT INTO "hasan_abdulkarim" VALUES(36,6,'36:6','Để Ngươi dùng cảnh báo đến một dân tộc( mà tổ tiên của họ trước đó đã không được cảnh báo để rồi họ khinh suất.');
INSERT INTO "hasan_abdulkarim" VALUES(36,7,'36:7','Chắc chắn, Lời Phán là điều Chân Lý đối với đa số bọn họ nhưng họ không có đức tin.');
INSERT INTO "hasan_abdulkarim" VALUES(36,8,'36:8','TA đã thật sự đóng những chiếc gông vào cổ của họ cao đến tận cằm cho nên họ ngẩng (cứng) cổ.');
INSERT INTO "hasan_abdulkarim" VALUES(36,9,'36:9','Và TA đã đặt một bức chắn (vô hình) trước mặt và một bức chắn sau (lưng) họ, nên họ không nhìn thấy.');
INSERT INTO "hasan_abdulkarim" VALUES(36,10,'36:10','Dầu Ngươi có cảnh báo hay không cảnh báo họ thì điều đó bằng thừa đối với họ, họ nhất định sẽ không có đức tin.');
INSERT INTO "hasan_abdulkarim" VALUES(36,11,'36:11','Ngươi chỉ có thể cảnh báo người nào tuân theo Lời Nhắc nhở (Qur''an) và sợ Đấng Al-Rahman( Vô hình. Do đó, hãy báo tin mừng cho y về sự tha thứ và phần thưởng rộng rãi (nơi thiên đàng).');
INSERT INTO "hasan_abdulkarim" VALUES(36,12,'36:12','TA sẽ thực sự làm cho người chết sống lại và ghi chép những điều mà họ đã gởi đi trước và những dấu tích mà họ đã lưu lại. TA cho ghi tất cả mọi điều trong một quyển sổ minh bạch.');
INSERT INTO "hasan_abdulkarim" VALUES(36,13,'36:13','Ngươi hãy trình bày cho họ một hình ảnh thí dụ: câu chuyện về dân cư của một thị trấn khi những Sứ Giả đến gặp họ.');
INSERT INTO "hasan_abdulkarim" VALUES(36,14,'36:14','Khi TA phái hai Sứ Giả đến gặp họ, họ cho hai người nói dối. Bởi thế, TA tăng cường thêm vị thứ ba để họ đồng thanh bảo: “Chúng tôi thực sự là những Sứ Giả được cử đến với quí vị.”');
INSERT INTO "hasan_abdulkarim" VALUES(36,15,'36:15','Họ (dân cư) đáp: “Các ông chỉ là những người phàm giống chúng tôi và Đấng Al-Rahman đã không ban điều (mặc khải) nào xuống cả. Rõ thật, các ông chỉ nói dối.”');
INSERT INTO "hasan_abdulkarim" VALUES(36,16,'36:16','(Các Sứ Giả) đáp: "Thượng Đế của chúng tôi biết rõ chúng tôi là những Sứ Giả được cử đến với quí vị."');
INSERT INTO "hasan_abdulkarim" VALUES(36,17,'36:17','"Và nhiệm vụ của chúng tôi chỉ là truyền đạt công khai."');
INSERT INTO "hasan_abdulkarim" VALUES(36,18,'36:18','Họ bảo: “Chúng tôi thấy có điềm xui nơi các ông. Nếu các ông không chịu ngưng thì chắc chắn chúng tôi sẽ ném đá giết các ông và chắc chắn chúng tôi sẽ áp dụng hình phạt đau đớn đối với các ông.”');
INSERT INTO "hasan_abdulkarim" VALUES(36,19,'36:19','(Các Sứ Giả) đáp: “Điềm xui của quí vị do nơi quí vị. Sao? (Quí vị cho là điều xui ư) khi được nhắc nhở. Không. Quí vị đúng là một đám người phạm mọi thứ tội."');
INSERT INTO "hasan_abdulkarim" VALUES(36,20,'36:20','Và có một người đàn ông từ cuối phố chạy đến, bảo: “Này hỡi người dân của ta! Hãy tuân theo các vị Sứ Giả."');
INSERT INTO "hasan_abdulkarim" VALUES(36,21,'36:21','“Hãy tuân theo những người không đòi quí vị tiền công và là những người được hướng dẫn."');
INSERT INTO "hasan_abdulkarim" VALUES(36,22,'36:22','“Và tại sao tôi không thờ phụng Đấng đã sáng tạo ra tôi và là Đấng mà quí vị sẽ được đưa trở về gặp lại."');
INSERT INTO "hasan_abdulkarim" VALUES(36,23,'36:23','“Há tôi sẽ phải tôn thờ những thần linh khác thay vì Ngài ư? Nếu Đấng Al-Rahman muốn hãm hại tôi thì chúng chẳng giúp ích gì được cho tôi, và cũng không cứu vớt được tôi."');
INSERT INTO "hasan_abdulkarim" VALUES(36,24,'36:24','“Lúc đó, tôi sẽ lầm lạc rõ ràng."');
INSERT INTO "hasan_abdulkarim" VALUES(36,25,'36:25','“Quả thật, bây giờ tôi tin tưởng nơi Thượng Đế của (tất cả) quí vị. Bởi thế, quí vị hãy nghe lời tôi.”');
INSERT INTO "hasan_abdulkarim" VALUES(36,26,'36:26','Có Lời phán bảo: “Nhà ngươi hãy bước vào thiên đàng.” (Y) than: ‘Thật khổ thay! Phải chi người dân của tôi biết được,"');
INSERT INTO "hasan_abdulkarim" VALUES(36,27,'36:27','"Việc Thượng Đế của tôi đã tha thứ và ban vinh dự cho tôi."');
INSERT INTO "hasan_abdulkarim" VALUES(36,28,'36:28','Và sau y, TA đã không gởi một đoàn thiên binh nào xuống trừng phạt người dân của y và cũng không cần cho xuống như thế.');
INSERT INTO "hasan_abdulkarim" VALUES(36,29,'36:29','Mà TA chỉ cần cho một tiếng gầm duy nhất thì chúng sẽ chết im lìm.');
INSERT INTO "hasan_abdulkarim" VALUES(36,30,'36:30','Thật xót xa cho con người, không một Sứ Giả nào đã được (TA) cử đến với họ mà không bị họ chế giễu.');
INSERT INTO "hasan_abdulkarim" VALUES(36,31,'36:31','Há họ không thấy việc TA đã tiêu diệt bao nhiêu thế hệ trước họ hay sao? Các tiền nhân đó sẽ không bao giờ trở lại gặp họ.');
INSERT INTO "hasan_abdulkarim" VALUES(36,32,'36:32','Và thực sự, từng người trong bọn họ sẽ được đưa đến trước mặt TA.');
INSERT INTO "hasan_abdulkarim" VALUES(36,33,'36:33','Và có một dấu hiệu cho họ là mảnh đất khô cằn mà TA làm cho sống lại; và từ đó TA làm mọc ra trái hạt mà họ dùng làm thực phẩm.');
INSERT INTO "hasan_abdulkarim" VALUES(36,34,'36:34','Và TA đã làm ra nơi đó những ngôi vườn chà là và vườn nho; và đã làm những mạch nước chảy phụt ra trong đó.');
INSERT INTO "hasan_abdulkarim" VALUES(36,35,'36:35','Để cho họ ăn trái của nó; và bàn tay của họ đã không làm được việc đó. Thế họ không tri ân (TA) hay sao?');
INSERT INTO "hasan_abdulkarim" VALUES(36,36,'36:36','Quang vinh thay Ngài! Đấng đã tạo thành từng cặp (âm dương) tất cả những vật mà đất đai cho mọc ra và từ chính bản thân của họ và từ những vật khác mà họ không biết.');
INSERT INTO "hasan_abdulkarim" VALUES(36,37,'36:37','Và có một dấu hiệu cho họ là ban đêm; TA rút ban ngày ra khỏi ban đêm thì họ ở trong bóng tối.');
INSERT INTO "hasan_abdulkarim" VALUES(36,38,'36:38','Và mặt trời di chuyển theo quỹ đạo của nó đến một thời hạn ấn định. Đó là quyết định sắp xếp của Đấng Toàn Năng, Đấng Toàn Tri.');
INSERT INTO "hasan_abdulkarim" VALUES(36,39,'36:39','Và mặt trăng, TA đã qui định cho nó những giai đoạn cho đến khi nó trở lại tình trạng cũ giống như ''Urjũn (lưỡi liềm).');
INSERT INTO "hasan_abdulkarim" VALUES(36,40,'36:40','Mặt trời không được phép bắt kịp mặt trăng và ban đêm không được phép qua mặt ban ngày; mỗi cái bơi đi theo quỹ đạo (của nó).');
INSERT INTO "hasan_abdulkarim" VALUES(36,41,'36:41','Và có một dấu hiệu nữa cho họ là việc TA đã chuyên chở con cháu của họ trên một chiếc tàu chất đầy (vào thời đại của Nuh).');
INSERT INTO "hasan_abdulkarim" VALUES(36,42,'36:42','Và TA đã tạo cho họ một chiếc tàu tương tự để chở họ đi.');
INSERT INTO "hasan_abdulkarim" VALUES(36,43,'36:43','Và nếu muốn, TA sẽ nhận họ chết chìm và họ sẽ không kêu la cầu cứu với ai được và cũng sẽ không được ai cứu sống.');
INSERT INTO "hasan_abdulkarim" VALUES(36,44,'36:44','Trừ phi được TA khoan dung và cho hưởng lạc trong một thời hạn ấn định.');
INSERT INTO "hasan_abdulkarim" VALUES(36,45,'36:45','Và khi có lời bảo họ: “Hãy sợ điều trước các người (trên trần thế) và điều sau các người (ở đời sau) để may ra các người được (Allah) khoan dung.”');
INSERT INTO "hasan_abdulkarim" VALUES(36,46,'36:46','Và không một dấu hiệu nào trong số các dấu hiệu của Thượng Đế của họ được trưng bày cho họ mà họ không quay lưng làm ngơ.');
INSERT INTO "hasan_abdulkarim" VALUES(36,47,'36:47','Và khi có lời bảo họ: “Hãy chi dùng của cải mà Allah đã ban cấp cho quí vị” thì những kẻ không đức tin bảo những người có đức tin: “Há chúng tôi phải nuôi ăn người (nghèo) mà nếu Allah muốn, chính Ngài phải nuôi hay sao? Rõ thật quí ông lầm lạc.”');
INSERT INTO "hasan_abdulkarim" VALUES(36,48,'36:48','Và họ bảo: “Thế chừng nào Lời Hứa (về sự Trừng Phạt) sẽ xảy ra nếu quí ông (Muslim) nói thật?”');
INSERT INTO "hasan_abdulkarim" VALUES(36,49,'36:49','Họ chỉ đợi một tiếng Gầm duy nhất. Nó sẽ bất ngờ chụp bắt họ trong lúc họ đang cãi nhau!');
INSERT INTO "hasan_abdulkarim" VALUES(36,50,'36:50','Lúc đó, họ sẽ không kịp trối trăng và cũng sẽ không kịp trở về gặp gia đình của mình đặng.');
INSERT INTO "hasan_abdulkarim" VALUES(36,51,'36:51','G');
INSERT INTO "hasan_abdulkarim" VALUES(36,52,'36:52','Họ sẽ lên tiếng: “Thật khổ thân chúng tôi! Ai đã đánh thức chúng tôi dậy từ những chỗ yên nghỉ của chúng tôi?”; “Đây là điều mà Đấng Al-Rahman đã hứa và các vị Sứ Giả đã xác nhận.”');
INSERT INTO "hasan_abdulkarim" VALUES(36,53,'36:53','Chỉ cần một tiếng gầm duy nhất thì tất cả bọn họ sẽ được đưa đến trình diện trước mặt TA.');
INSERT INTO "hasan_abdulkarim" VALUES(36,54,'36:54','Vào Ngày đó, không một người nào sẽ bị xét xử thiệt thòi bất cứ điều gì; và các ngươi sẽ được trả lại đầy đủ về điều mà các ngươi đã làm;');
INSERT INTO "hasan_abdulkarim" VALUES(36,55,'36:55','Những cư dân của thiên đàng vào Ngày đó sẽ vui mừng về những điều mình đã làm.');
INSERT INTO "hasan_abdulkarim" VALUES(36,56,'36:56','Và cùng với các bà vợ, họ sẽ nằm nghỉ trên những chiếc tràng kỷ cao, dưới bóng mát.');
INSERT INTO "hasan_abdulkarim" VALUES(36,57,'36:57','Sẽ hưởng thụ đủ loại hoa quả và sẽ có đủ mọi thứ mà họ đòi.');
INSERT INTO "hasan_abdulkarim" VALUES(36,58,'36:58','Lời chao Salam sẽ là lời (chúc mừng) của Thượng Đế Rất Mực Khoan Dung.');
INSERT INTO "hasan_abdulkarim" VALUES(36,59,'36:59','Hỡi những kẻ tội lỗi Ngày nay, các ngươi hãy dang ra xa.');
INSERT INTO "hasan_abdulkarim" VALUES(36,60,'36:60','Há TA đã không bảo các ngươi, hỡi con cháu của Adam, chớ tôn thờ Shaytan bởi vì nó là kẻ thù công khai của các ngươi hay sao?');
INSERT INTO "hasan_abdulkarim" VALUES(36,61,'36:61','Và hãy thờ phụng TA. (Bởi vì) đây là Chính Đạo.');
INSERT INTO "hasan_abdulkarim" VALUES(36,62,'36:62','Và quả thật, nó (Shaytan) đã dắt đa số các ngươi đi lạc. Thế tại sao các ngươi không chịu hiểu?');
INSERT INTO "hasan_abdulkarim" VALUES(36,63,'36:63','Đây là hỏa ngục mà các ngươi đã được hứa (trước đây).');
INSERT INTO "hasan_abdulkarim" VALUES(36,64,'36:64','Ngày nay, hãy ôm lấy nó (Lửa) vì tội các ngươi đã không tin.');
INSERT INTO "hasan_abdulkarim" VALUES(36,65,'36:65','Ngày đó, TA sẽ khóa chặt miệng của họ, và bàn tay của họ sẽ nói chuyện với TA và chân của họ sẽ làm chứng về những điều mà họ đã làm.');
INSERT INTO "hasan_abdulkarim" VALUES(36,66,'36:66','Và nếu muốn, TA sẽ xóa tiêu cặp mắt của họ vì thế họ phấn đấu chạy đi tìm đường, nhưng làm sao họ có thể nhìn thấy?');
INSERT INTO "hasan_abdulkarim" VALUES(36,67,'36:67','Và nếu muốn, TA sẽ biến dạng họ ngay tại chỗ; do đó, họ không thể bước tới cũng không thể bước lui.');
INSERT INTO "hasan_abdulkarim" VALUES(36,68,'36:68','Và người nào được TA cho sống thọ, TA sẽ đảo ngược y (yếu đuối) trở lại trong việc tạo hóa. Thế họ không hiểu hay sao?');
INSERT INTO "hasan_abdulkarim" VALUES(36,69,'36:69','Và TA đã không dạy Y (Muhammad) thơ phú; và điều đó không thích hợp với Y. Thật ra, nó chỉ là Lời Nhắc nhở và là một Qur''an minh bạch.');
INSERT INTO "hasan_abdulkarim" VALUES(36,70,'36:70','Mục đích để Y dùng cảnh báo những ai đang sống và để chứng minh Lời (xử phạt) đối với những kẻ không tin đúng sự thật.');
INSERT INTO "hasan_abdulkarim" VALUES(36,71,'36:71','Há họ đã không thấy việc TA đã tạo hóa cho họ, trong số loài vật do bàn tay của TA đã làm ra, gia súc mà họ làm chủ hay sao?');
INSERT INTO "hasan_abdulkarim" VALUES(36,72,'36:72','Và TA đã bắt chúng (thú vật) phục tùng họ; bởi thế, một số vật họ dùng để cưỡi và một số khác họ ăn thịt.');
INSERT INTO "hasan_abdulkarim" VALUES(36,73,'36:73','Và nơi chúng có nhiều mối lợi khác cho họ và có chất (sữa tươi) để uống. Thế, họ không tri ân (TA) hay sao?');
INSERT INTO "hasan_abdulkarim" VALUES(36,74,'36:74','Và họ đã tôn thờ những thần linh khác thay vì Allah với hy vọng sẽ được giúp đỡ;');
INSERT INTO "hasan_abdulkarim" VALUES(36,75,'36:75','Nhưng chúng không thể giúp đỡ họ mà chỉ là một lũ sẽ được đưa đến đối diện với họ;');
INSERT INTO "hasan_abdulkarim" VALUES(36,76,'36:76','Bởi thế, chớ để cho lời lẽ (chế giễu) của họ làm Ngươi buồn. Quả thật, TA biết rõ điều họ giấu giếm và điều họ tiết lộ.');
INSERT INTO "hasan_abdulkarim" VALUES(36,77,'36:77','Há con người không nhận thấy việc TA đã tạo hóa y từ một giọt tinh dịch hay sao? Nhưng y lại là một đối thủ công khai (của TA).');
INSERT INTO "hasan_abdulkarim" VALUES(36,78,'36:78','Và y đưa ra một điều so sánh về TA nhưng lại quên bẵng việc tạo hóa của mình. Y bảo: “Ai có khả năng phục sinh lại được những khúc xương đã rã mục?”');
INSERT INTO "hasan_abdulkarim" VALUES(36,79,'36:79','Hãy bảo y: (hỡi Muhammad!) “Đấng đã tạo hóa chúng lúc ban đầu sẽ phục sinh chúng lại. Bởi vì, Ngài biết tất cả việc tạo hóa;');
INSERT INTO "hasan_abdulkarim" VALUES(36,80,'36:80','“Đấng đã làm ra lửa cho các người từ một loại cây tươi mà các người dùng để nhúm lửa.”');
INSERT INTO "hasan_abdulkarim" VALUES(36,81,'36:81','Há Đấng đã tạo hóa các tầng trời và trái đất không có khả năng tạo hóa được những cái giống như họ hay sao? Vâng, thật vậy, Ngài là Đấng Tạo Hóa Toàn Tri');
INSERT INTO "hasan_abdulkarim" VALUES(36,82,'36:82','Chỉ thị của Ngài khi Ngài muốn tạo một vật gì, chỉ gồm có Lời (phán) với nó “Hãy Thành!” thì nó,sẽ thành.');
INSERT INTO "hasan_abdulkarim" VALUES(36,83,'36:83','Do đó, quang vinh thay Ngài! Đấng mà quyền thống trị vạn vật nằm trong Tay Ngài và các ngươi sẽ được đưa về gặp Ngài trở lại.');
INSERT INTO "hasan_abdulkarim" VALUES(37,1,'37:1','(Thề) bởi những (Thiên Thần) đứng sắp hàng chỉnh tề.');
INSERT INTO "hasan_abdulkarim" VALUES(37,2,'37:2','Những (Thiên Thần kéo đưa (mây) đi (một cách khéo léo).');
INSERT INTO "hasan_abdulkarim" VALUES(37,3,'37:3','Những (Thiên Thần) tuyên đọc Lời Nhắc nhở (Qur''an).');
INSERT INTO "hasan_abdulkarim" VALUES(37,4,'37:4','Thật sự Thượng Đế của các ngươi là Một (Duy nhất).');
INSERT INTO "hasan_abdulkarim" VALUES(37,5,'37:5','Thượng Đế của các tầng trời và trái đất và của vạn vật giữa trời đất và là Thượng Đế của những điểm của mặt trời mọc (ở phía Đông).');
INSERT INTO "hasan_abdulkarim" VALUES(37,6,'37:6','Quả thật! TA đã trang hoàng tầng trời hạ giới với các vì sao.');
INSERT INTO "hasan_abdulkarim" VALUES(37,7,'37:7','Và canh giữ nó xa khỏi từng tên Shaytan phản nghịch.');
INSERT INTO "hasan_abdulkarim" VALUES(37,8,'37:8','Chúng không nghe trộm được hội nghị trên cao và bị đánh đuổi từ khắp nơi.');
INSERT INTO "hasan_abdulkarim" VALUES(37,9,'37:9','Chúng bị tống đi xa. Và chúng sẽ vĩnh viễn bị trừng phạt.');
INSERT INTO "hasan_abdulkarim" VALUES(37,10,'37:10','Ngoại trừ tên nào lén giật (tin tức) và bị đánh bằng một lửa ngọn đỏ.');
INSERT INTO "hasan_abdulkarim" VALUES(37,11,'37:11','Hãy hỏi họ phải chăng họ khó tạo hay là ai khác mà TA đã tạo (khó hơn)? Quả thật, TA đã tạo họ từ một loại đất sét nhão, dính.');
INSERT INTO "hasan_abdulkarim" VALUES(37,12,'37:12','Không, Ngươi ngạc nhiên về việc họ chế giễu.');
INSERT INTO "hasan_abdulkarim" VALUES(37,13,'37:13','Và khi được nhắc nhở, họ không lưu tâm;');
INSERT INTO "hasan_abdulkarim" VALUES(37,14,'37:14','Và khi thấy một dấu hiệu họ mang nó ra bỡn cợt;');
INSERT INTO "hasan_abdulkarim" VALUES(37,15,'37:15','Và bảo: “(Qur''an) này chỉ là trò ảo thuật hiển hiện!"');
INSERT INTO "hasan_abdulkarim" VALUES(37,16,'37:16','"Khi chúng tôi chết và thành đất bụi và xương khô, chúng tôi sẽ được phục sinh trở lại hay sao?"');
INSERT INTO "hasan_abdulkarim" VALUES(37,17,'37:17','“Kể cả tổ tiên xa xaa của chúng tôi nữa ư?”');
INSERT INTO "hasan_abdulkarim" VALUES(37,18,'37:18','Hãy bảo họ: “Vâng, đúng thế. Và các ngươi sẽ bị hạ nhục.”');
INSERT INTO "hasan_abdulkarim" VALUES(37,19,'37:19','Do đó, chỉ cần một tiếng gầm duy nhất thì lúc đó họ sẽ thấy.');
INSERT INTO "hasan_abdulkarim" VALUES(37,20,'37:20','Và sẽ than: “Thật khổ thân chúng tôi! Đây là Ngày Xét xử!”');
INSERT INTO "hasan_abdulkarim" VALUES(37,21,'37:21','“Đây là Ngày phân loại mà các ngươi đã từng cho là giả tạo.”');
INSERT INTO "hasan_abdulkarim" VALUES(37,22,'37:22','(Có lệnh phán): “Hãy tập trung những ai làm điều sai quấy và bạn đồng hành của chúng và những vật mà chúng đã tôn thờ,');
INSERT INTO "hasan_abdulkarim" VALUES(37,23,'37:23','không phải Allah, rồi đưa chúng đến con đường dẫn vào hỏa ngục"');
INSERT INTO "hasan_abdulkarim" VALUES(37,24,'37:24','“Nhưng hãy bắt chúng dừng lại! Bởi vì chúng phải bị tra hỏi."');
INSERT INTO "hasan_abdulkarim" VALUES(37,25,'37:25','“Các ngươi có chuyện gì? Tại sao các ngươi không giúp đỡ lẫn nhau?"');
INSERT INTO "hasan_abdulkarim" VALUES(37,26,'37:26','Không, Ngày đó chúng sẽ tự nạp mình (chịu phạt).');
INSERT INTO "hasan_abdulkarim" VALUES(37,27,'37:27','Chúng sẽ quay lại hỏi nhau.');
INSERT INTO "hasan_abdulkarim" VALUES(37,28,'37:28','Chúng bảo: “Rõ các anh là những người đã đến gặp chúng tôi bên phải.”');
INSERT INTO "hasan_abdulkarim" VALUES(37,29,'37:29','“Không, các anh mới là những người không có đức tin."');
INSERT INTO "hasan_abdulkarim" VALUES(37,30,'37:30','“Chúng tôi đâu có quyền bắt các anh nghe theo. Không, các anh mới là đám người bất tuân."');
INSERT INTO "hasan_abdulkarim" VALUES(37,31,'37:31','“Bởi thế, Lời (phán) của Thượng Đế của chúng tôi xác nhận đúng đối với chúng tôi. Chúng tôi buộc phải nếm (hình phạt thôi)."');
INSERT INTO "hasan_abdulkarim" VALUES(37,32,'37:32','“Do đó, chúng tôi đã dắt các anh đi lạc; và chúng tôi cũng lầm lạc.”');
INSERT INTO "hasan_abdulkarim" VALUES(37,33,'37:33','Bởi thế, vào Ngày đó chúng sẽ cùng nhau san sẻ hình phạt.');
INSERT INTO "hasan_abdulkarim" VALUES(37,34,'37:34','Quả thật, TA sẽ xử lý những kẻ tội lỗi đúng như thế.');
INSERT INTO "hasan_abdulkarim" VALUES(37,35,'37:35','Quả thật chúng là những kẻ, khi được nhắc: ‘Không có Thượng Đế nào khác duy chỉ Allah (là Thượng Đế)'' thì đã tỏ ra ngạo mạn.');
INSERT INTO "hasan_abdulkarim" VALUES(37,36,'37:36','Và chúng bảo: “Sao! chúng tôi phải bỏ các thần linh của chúng tôi chỉ vì một tên thi sĩ mất trí (Muhammad) hay sao?”');
INSERT INTO "hasan_abdulkarim" VALUES(37,37,'37:37','Không! Y (Muhammad) mang Chân Lý đến để xác nhận lại các Sứ Giả.');
INSERT INTO "hasan_abdulkarim" VALUES(37,38,'37:38','Rõ thật, các ngươi sẽ phải nếm hình phạt đau đớn;');
INSERT INTO "hasan_abdulkarim" VALUES(37,39,'37:39','Và sẽ bị trừng phạt chỉ bởi những điều mà các ngươi đã làm;');
INSERT INTO "hasan_abdulkarim" VALUES(37,40,'37:40','Ngoại trừ các bầy tôi chân thành (trong sạch) của Allah.');
INSERT INTO "hasan_abdulkarim" VALUES(37,41,'37:41','Họ là những người sẽ hưởng bổng lộc được biết rõ:');
INSERT INTO "hasan_abdulkarim" VALUES(37,42,'37:42','Trái cây (hoa quả); và họ sẽ được vinh dự,');
INSERT INTO "hasan_abdulkarim" VALUES(37,43,'37:43','Trong những Ngôi vườn hạnh phúc (của thiên đàng),');
INSERT INTO "hasan_abdulkarim" VALUES(37,44,'37:44','(Nằm nghỉ) đối diện trên những chiếc tràng kỷ cao,');
INSERT INTO "hasan_abdulkarim" VALUES(37,45,'37:45','Một cốc (rượu) sẽ được qhuyển vòng đi cho từng người;');
INSERT INTO "hasan_abdulkarim" VALUES(37,46,'37:46','Trong vắt, ngọt lịm, làm cho người uống thích thú,');
INSERT INTO "hasan_abdulkarim" VALUES(37,47,'37:47','Không làm cho họ nhức đầu, đau bụng... và choáng váng.');
INSERT INTO "hasan_abdulkarim" VALUES(37,48,'37:48','Bên cạnh họ sẽ là những trinh nữ với mắt to xinh đẹp, e-lệ nhìn;');
INSERT INTO "hasan_abdulkarim" VALUES(37,49,'37:49','(Làn da mịn) như những quả trứng non được giữ kỹ.');
INSERT INTO "hasan_abdulkarim" VALUES(37,50,'37:50','Rồi họ quay sang hỏi nhau;');
INSERT INTO "hasan_abdulkarim" VALUES(37,51,'37:51','Một người của họ lên tiếng bảo: "Tôi có một người bạn thân,');
INSERT INTO "hasan_abdulkarim" VALUES(37,52,'37:52','“Anh ta thường bảo: ''Phải chăng anh là một người có đức tin;');
INSERT INTO "hasan_abdulkarim" VALUES(37,53,'37:53','“Phải chăng sau khi chết và thành cát bụi và xương khô, chúng ta sẽ được (dựng sống lại) để chịu xét xử?”');
INSERT INTO "hasan_abdulkarim" VALUES(37,54,'37:54','(Một người của họ) bảo: “Há các anh thử nhìn xuống?”');
INSERT INTO "hasan_abdulkarim" VALUES(37,55,'37:55','Anh ta nhìn xuống và thấy mình đang ở trong hỏa ngục.');
INSERT INTO "hasan_abdulkarim" VALUES(37,56,'37:56','Anh ta bảo: “Thề bởi Allah! Chút xíu nữa là anh đã hại tôi!');
INSERT INTO "hasan_abdulkarim" VALUES(37,57,'37:57','“Nếu không do Ân Huệ của Thượng Đế của tôi thì chắc chắn tôi đã là một trong những người bị đưa đến (hỏa ngục)!"');
INSERT INTO "hasan_abdulkarim" VALUES(37,58,'37:58','“Phải chăng chúng ta sẽ không chết nữa?"');
INSERT INTO "hasan_abdulkarim" VALUES(37,59,'37:59','“Ngoại trừ cái chết đầu tiên của chúng ta và chúng ta sẽ không bị trừng phạt?”');
INSERT INTO "hasan_abdulkarim" VALUES(37,60,'37:60','Chắc chắn, đó là một sự thành tựu lớn lao!');
INSERT INTO "hasan_abdulkarim" VALUES(37,61,'37:61','Vì hình ảnh này, những người làm việc hãy cố gắng làm (tốt).');
INSERT INTO "hasan_abdulkarim" VALUES(37,62,'37:62','Phải chăng điều (thành tựu) đó tốt hay cây Zaqqum tốt hơn?');
INSERT INTO "hasan_abdulkarim" VALUES(37,63,'37:63','Quả thật, TA làm ra nó để trừng phạt những tên làm điều sai quấy.');
INSERT INTO "hasan_abdulkarim" VALUES(37,64,'37:64','Đó (Zaqqum) là một loại cây mọc từ dưới đáy của hỏa ngục.');
INSERT INTO "hasan_abdulkarim" VALUES(37,65,'37:65','Chồi non của nó giống như đầu của bọn Shaytan.');
INSERT INTO "hasan_abdulkarim" VALUES(37,66,'37:66','Thật sự, chúng sẽ ăn nhét nó vào đầy bụng.');
INSERT INTO "hasan_abdulkarim" VALUES(37,67,'37:67','Và sẽ được cho uống với một lọai nước sôi.');
INSERT INTO "hasan_abdulkarim" VALUES(37,68,'37:68','Rồi chúng sẽ được đưa vào hỏa ngục trở lại.');
INSERT INTO "hasan_abdulkarim" VALUES(37,69,'37:69','Quả thật, chúng thấy cha mẹ của chúng lầm lạc;');
INSERT INTO "hasan_abdulkarim" VALUES(37,70,'37:70','Nhưng chúng lại ùn ùn chạy theo bước chân của họ!');
INSERT INTO "hasan_abdulkarim" VALUES(37,71,'37:71','Và chắc chắn, đa số những người xưa trước chúng cũng đã lầm lạc;');
INSERT INTO "hasan_abdulkarim" VALUES(37,72,'37:72','Và quả thật, TA đã cử những người báo trước đến với họ.');
INSERT INTO "hasan_abdulkarim" VALUES(37,73,'37:73','Bởi thế, hãy nhìn xem kết cuộc của những kẻ đã được cảnh báo (nhưng làm ngơ) đã xảy ra như thế nào.');
INSERT INTO "hasan_abdulkarim" VALUES(37,74,'37:74','Ngoại trừ các bầy tôi chân thành (trong sạch) với Allah.');
INSERT INTO "hasan_abdulkarim" VALUES(37,75,'37:75','Quả thật, (khi xưa) Nuh đã cầu nguyện TA. Bởi thế, TA là Đấng Ưu Việt đáp lại lời cầu xin (của Y).');
INSERT INTO "hasan_abdulkarim" VALUES(37,76,'37:76','Và TA đã giải cứu Y và gia đình của Y thoát khỏi đại họa');
INSERT INTO "hasan_abdulkarim" VALUES(37,77,'37:77','Và TA đã làm cho dòng dõi của Y sống sót.');
INSERT INTO "hasan_abdulkarim" VALUES(37,78,'37:78','Và TA đã để lại cho Y nơi hậu thế (lời chúc phúc):');
INSERT INTO "hasan_abdulkarim" VALUES(37,79,'37:79','Salam (Sự Bằng an) cho Nuh trong thế gian.');
INSERT INTO "hasan_abdulkarim" VALUES(37,80,'37:80','Quả thật, TA đã trọng thưởng những người làm tốt như thế.');
INSERT INTO "hasan_abdulkarim" VALUES(37,81,'37:81','Bởi vì rõ thật, Y là một người bề tôi có đức tin của TA.');
INSERT INTO "hasan_abdulkarim" VALUES(37,82,'37:82','Rồi, TA nhận chết chìm các tên (tội lỗi) khác.');
INSERT INTO "hasan_abdulkarim" VALUES(37,83,'37:83','Và Ibrahim đã thực sự nhập phái (tôn giáo) của Người (Nuh).');
INSERT INTO "hasan_abdulkarim" VALUES(37,84,'37:84','Khi Y đến với Thượng Đế của Y với một tấm lòng trong sạch.');
INSERT INTO "hasan_abdulkarim" VALUES(37,85,'37:85','Khi Y nói với phụ thân và người dân: “Quí vị tôn thờ gì vậy?"');
INSERT INTO "hasan_abdulkarim" VALUES(37,86,'37:86','“Phải chăng là một điều giả tạo việc quí vị muốn (tôn thờ) các thần linh khác thay vì Allah?"');
INSERT INTO "hasan_abdulkarim" VALUES(37,87,'37:87','“Thế quí vị nghĩ sao về Đấng Thượng Đế của vũ trụ và muôn loài?”');
INSERT INTO "hasan_abdulkarim" VALUES(37,88,'37:88','Rồi Y (Ibrahim) liếc nhìn các vì sao (trên trời).');
INSERT INTO "hasan_abdulkarim" VALUES(37,89,'37:89','Rồi bảo: “Người tôi muốn bệnh"');
INSERT INTO "hasan_abdulkarim" VALUES(37,90,'37:90','Bởi thế, họ (người dân) ra đi, bỏ Y ở lại.');
INSERT INTO "hasan_abdulkarim" VALUES(37,91,'37:91','Rồi Y lén đi gặp các tượng thần của họ. Y hỏi chúng “Các ngươi không ăn (các món cúng trước mặt) hay sao?"');
INSERT INTO "hasan_abdulkarim" VALUES(37,92,'37:92','“Các ngươi có chuyện gì vậy? Sao các ngươi không chịu nói?”');
INSERT INTO "hasan_abdulkarim" VALUES(37,93,'37:93','Rồi Y nhảy xổ đến đưa tay phải ra đập bể chúng.');
INSERT INTO "hasan_abdulkarim" VALUES(37,94,'37:94','Sau đó, họ (người dân) vội vã chạy đến gặp Y.');
INSERT INTO "hasan_abdulkarim" VALUES(37,95,'37:95','Y bảo họ: “Quí ông tôn thờ những bức tượng do quí ông tạc ư?"');
INSERT INTO "hasan_abdulkarim" VALUES(37,96,'37:96','"Trong lúc Allah tạo ra quí ông và những vật mà quí ông làm.”');
INSERT INTO "hasan_abdulkarim" VALUES(37,97,'37:97','Họ bảo : “Hãy xây một tòa nhà cho nó rồi ném vào lửa cháy!”');
INSERT INTO "hasan_abdulkarim" VALUES(37,98,'37:98','Họ đã bày mưu hãm hại Y, nhưng TA đã hạ nhục họ.');
INSERT INTO "hasan_abdulkarim" VALUES(37,99,'37:99','Và Y bảo: "Ta sẽ đi gặp Thượng Đế của Ta. Ngài sẽ hướng dẫn ta.”');
INSERT INTO "hasan_abdulkarim" VALUES(37,100,'37:100','“Lạy Thượng Đế của bề tôi! Xin Ngài ban cho bề tôi một đứa (con) đức hạnh.”');
INSERT INTO "hasan_abdulkarim" VALUES(37,101,'37:101','Bởi thế, TA ban cho Y tin mừng về một đứa con trai chịu đựng.');
INSERT INTO "hasan_abdulkarim" VALUES(37,102,'37:102','Đến khi đứa trẻ đến tuổi biết chạy cùng với Y, Y bảo đứa con: “Này con yêu dấu! Cha thấy trong mộng việc cha sẽ tế con. Thế con nghĩ sao? (Isma''il) đáp: “Thưa cha! Cha cứ làm theo lệnh truyền. Cha sẽ thấy, nếu Allah muốn, con sẽ là một đứa con kiên nhẫn.”');
INSERT INTO "hasan_abdulkarim" VALUES(37,103,'37:103','Thế nên, khi hai cha con cùng nạp mình thần phục (Allah), người cha bắt đứa con quỳ gục đầu xuống đất, (để tế);');
INSERT INTO "hasan_abdulkarim" VALUES(37,104,'37:104','Thì TA gọi Y, (phán bảo): “Này hỡi Ibrahim!"');
INSERT INTO "hasan_abdulkarim" VALUES(37,105,'37:105','“Nhà ngươi đã hoàn tất điều (thấy) trong mộng.” Quả thật! TA ân thưởng những người làm tốt như thế.');
INSERT INTO "hasan_abdulkarim" VALUES(37,106,'37:106','Quả thật, đây là một sự thử thách công khai.');
INSERT INTO "hasan_abdulkarim" VALUES(37,107,'37:107','Và TA đã chuộc mạng đứa trẻ (Isma''il) bằng một con vật tế lớn.');
INSERT INTO "hasan_abdulkarim" VALUES(37,108,'37:108','Và TA đã để lại cho Y (Ibrahim) nơi hậu thế (Lời chúc phúc):');
INSERT INTO "hasan_abdulkarim" VALUES(37,109,'37:109','“Salam (Sự Bằng an) cho Ibrahim.”');
INSERT INTO "hasan_abdulkarim" VALUES(37,110,'37:110','TA ân thưởng những người làm tốt như thế.');
INSERT INTO "hasan_abdulkarim" VALUES(37,111,'37:111','Quả thật, Y là một người bề tôi có đức tin của TA.');
INSERT INTO "hasan_abdulkarim" VALUES(37,112,'37:112','Và TA ban cho Y tin mừng về Is-haaq (sau này trở thành) một Nabi trong số những người đức hạnh.');
INSERT INTO "hasan_abdulkarim" VALUES(37,113,'37:113','Và TA đã ban phúc cho Y và Is-haaq nhưng trong con cháu của hai người (Ibrahim và Is-haaq) có một số làm tốt và có một số đã công khai tự hại bản thân mình.');
INSERT INTO "hasan_abdulkarim" VALUES(37,114,'37:114','Và quả thật, TA đã ban ân cho Musa và Harun.');
INSERT INTO "hasan_abdulkarim" VALUES(37,115,'37:115','Và đã giải cứu hai người và người dân của hai người thoát khỏi đại họa;');
INSERT INTO "hasan_abdulkarim" VALUES(37,116,'37:116','Và đã trợ giúp hai người chiến thắng;');
INSERT INTO "hasan_abdulkarim" VALUES(37,117,'37:117','Và đã ban cho hai người một Kinh Sách sáng tỏ;');
INSERT INTO "hasan_abdulkarim" VALUES(37,118,'37:118','Và đã hướng dẫn hai người theo Chính Đạo;');
INSERT INTO "hasan_abdulkarim" VALUES(37,119,'37:119','Và TA đã để lại cho hai người nơi hậu thế (Lời chúc phúc):');
INSERT INTO "hasan_abdulkarim" VALUES(37,120,'37:120','Salam (Bằng an) cho Musa và Harun!”');
INSERT INTO "hasan_abdulkarim" VALUES(37,121,'37:121','Quả thật, TA ân thưởng những người làm tốt như thế.');
INSERT INTO "hasan_abdulkarim" VALUES(37,122,'37:122','Quả thật, hai người là bề tôi có đức tin của TA.');
INSERT INTO "hasan_abdulkarim" VALUES(37,123,'37:123','Và quả thật, Ilyas (Elias) là một trong các Sứ Giả (của Allah).');
INSERT INTO "hasan_abdulkarim" VALUES(37,124,'37:124','Khi Y bảo người dân của Y: “Các người không sợ Allah ư?"');
INSERT INTO "hasan_abdulkarim" VALUES(37,125,'37:125','“Phải chăng các người cầu nguyện Ba''l và bỏ rơi Đấng Tạo Hóa Ưu việt?"');
INSERT INTO "hasan_abdulkarim" VALUES(37,126,'37:126','“Allah, Thượng Đế của các người và của tổ tiên xa xưa của các người ư?”');
INSERT INTO "hasan_abdulkarim" VALUES(37,127,'37:127','Nhưng họ phủ nhận Y. Bởi thế, họ sẽ bị dẫn đến (Lửa)."');
INSERT INTO "hasan_abdulkarim" VALUES(37,128,'37:128','Ngoại trừ bầy tôi thành tâm (trong sạch) của Allah.');
INSERT INTO "hasan_abdulkarim" VALUES(37,129,'37:129','Và TA đã để lại cho Y nơi hậu thế (Lời chúc phúc):');
INSERT INTO "hasan_abdulkarim" VALUES(37,130,'37:130','Salam (Sự Bằng an) cho Ilyas (Elias)!”');
INSERT INTO "hasan_abdulkarim" VALUES(37,131,'37:131','Quả thật, TA ân thưởng những người làm tốt như thế.');
INSERT INTO "hasan_abdulkarim" VALUES(37,132,'37:132','Quả thật, Y là một người bề tôi có đức tin của TA.');
INSERT INTO "hasan_abdulkarim" VALUES(37,133,'37:133','Và quả thật, Lut (Lốt) là một trong các Sứ Giả (của Allah).');
INSERT INTO "hasan_abdulkarim" VALUES(37,134,'37:134','Khi TA giải cứu Y và gia đình của Y, tất cả.');
INSERT INTO "hasan_abdulkarim" VALUES(37,135,'37:135','Ngoại trừ một bà lão (vợ của Lut) trong số người ở lại đằng sau.');
INSERT INTO "hasan_abdulkarim" VALUES(37,136,'37:136','Rồi TA đã tiêu diệt số người còn lại.');
INSERT INTO "hasan_abdulkarim" VALUES(37,137,'37:137','Và quả thật, các ngươi đi ngang qua (chỗ của) họ vào buổi sáng;');
INSERT INTO "hasan_abdulkarim" VALUES(37,138,'37:138','Và ban đêm. Thế các ngươi không hiểu hay sao?');
INSERT INTO "hasan_abdulkarim" VALUES(37,139,'37:139','Và quả thật, Yunus là một trong các Sứ Giả (của Allah).');
INSERT INTO "hasan_abdulkarim" VALUES(37,140,'37:140','Khi Y bỏ chạy đến một chiếc tàu chở đầy người.');
INSERT INTO "hasan_abdulkarim" VALUES(37,141,'37:141','Y chịu rút thăm và Y thua cuộc.');
INSERT INTO "hasan_abdulkarim" VALUES(37,142,'37:142','Do đó, một con cá lớn đã nuốt Y vào bụng bởi vì Y đã phạm một điều đáng trách.');
INSERT INTO "hasan_abdulkarim" VALUES(37,143,'37:143','Nếu không là người (biết ăn năn sám hối và) tán dương Allah,');
INSERT INTO "hasan_abdulkarim" VALUES(37,144,'37:144','Thì Y đã phải ở trong bụng cá cho đến Ngày phục sinh.');
INSERT INTO "hasan_abdulkarim" VALUES(37,145,'37:145','Nhưng TA đã quẳng Y lên một bờ biển trống trải trong lúc Y đang bệnh;');
INSERT INTO "hasan_abdulkarim" VALUES(37,146,'37:146','Và TA đã khiến cho cây bầu mọc phủ mình Y.');
INSERT INTO "hasan_abdulkarim" VALUES(37,147,'37:147','Và TA cử Y cho một trăm ngàn người dân hoặc nhiều hơn.');
INSERT INTO "hasan_abdulkarim" VALUES(37,148,'37:148','Do đó, họ có đức tin. Và TA đã cho họ hưởng lạc một thời gian.');
INSERT INTO "hasan_abdulkarim" VALUES(37,149,'37:149','Hãy hỏi họ (những người Quraish thờ đa thần): “Phải chăng con gái thuộc về Thượng Đế của Ngươi, còn con trai là của họ?”');
INSERT INTO "hasan_abdulkarim" VALUES(37,150,'37:150','“Hoặc phải chăng họ đã chứng kiến việc TA đã tạo hóa các Thiên Thần thành nữ giới?');
INSERT INTO "hasan_abdulkarim" VALUES(37,151,'37:151','Ô này! Rõ thật, họ đã nói dối khi họ nói:');
INSERT INTO "hasan_abdulkarim" VALUES(37,152,'37:152','“Allah đã sanh con.” Và quả thật, họ là những kẻ nói dối.');
INSERT INTO "hasan_abdulkarim" VALUES(37,153,'37:153','Ngài (Allah) thích con gái hơn con trai ư?');
INSERT INTO "hasan_abdulkarim" VALUES(37,154,'37:154','Các ngươi có chuyện gì vậy? Các ngươi xử trí ra sao?');
INSERT INTO "hasan_abdulkarim" VALUES(37,155,'37:155','Thế các ngươi không nhớ hay sao?');
INSERT INTO "hasan_abdulkarim" VALUES(37,156,'37:156','Hoặc phải chăng các ngươi có đủ thẩm quyền rõ ràng.');
INSERT INTO "hasan_abdulkarim" VALUES(37,157,'37:157','Thế, hãy mang Kinh Sách của các ngươi đến nếu các ngươi nói thật!');
INSERT INTO "hasan_abdulkarim" VALUES(37,158,'37:158','Và họ bịa đặt mối quan hệ thân thuộc giữa Ngài và loài Jinn trong lúc loài Jinn biết việc chúng sẽ phải trình diện (Allah).');
INSERT INTO "hasan_abdulkarim" VALUES(37,159,'37:159','Quang vinh và trong sạch thay Allah về những điều họ đã qui cho Ngài!');
INSERT INTO "hasan_abdulkarim" VALUES(37,160,'37:160','Ngoại trừ các bầy tôi thành tâm (trong sạch) của Allah.');
INSERT INTO "hasan_abdulkarim" VALUES(37,161,'37:161','Và các ngươi và những kẻ (thần linh) mà các ngươi tôn thờ.');
INSERT INTO "hasan_abdulkarim" VALUES(37,162,'37:162','Các ngươi không thể quyến dụ được ai (có đức tin) chống lại Ngài,');
INSERT INTO "hasan_abdulkarim" VALUES(37,163,'37:163','Ngoại trừ ai muốn đi vào Lửa cháy (của hỏa ngục).');
INSERT INTO "hasan_abdulkarim" VALUES(37,164,'37:164','(Các Thiên Thần nói:) Không ai trong chúng tôi lại không có một vị trí ấn định;');
INSERT INTO "hasan_abdulkarim" VALUES(37,165,'37:165','Quả thật, chúng tôi đứng thành hàng.');
INSERT INTO "hasan_abdulkarim" VALUES(37,166,'37:166','Và quả thật, chúng tôi tán dương Ngài.');
INSERT INTO "hasan_abdulkarim" VALUES(37,167,'37:167','Và họ (những người Ả Rập ngoại đạo) thường nói:');
INSERT INTO "hasan_abdulkarim" VALUES(37,168,'37:168','“Giá có được một Lời Nhắc nhở từ tổ tiên của chúng tôi,');
INSERT INTO "hasan_abdulkarim" VALUES(37,169,'37:169','"Thì chắc chắn chúng tôi đã trở thành các bầy tôi chân thành của Allah.”');
INSERT INTO "hasan_abdulkarim" VALUES(37,170,'37:170','(Nay đã có Qur''an), nhưng họ phủ nhận nó; rồi họ sẽ sớm biết!');
INSERT INTO "hasan_abdulkarim" VALUES(37,171,'37:171','Và Lời Phán của TA đã được phán cho các bầy tôi của TA, các Sứ Giả,');
INSERT INTO "hasan_abdulkarim" VALUES(37,172,'37:172','Rằng chắc chắn họ sẽ được trợ giúp,');
INSERT INTO "hasan_abdulkarim" VALUES(37,173,'37:173','Bằng đoàn thiên binh (Thiên Thần) của TA, và sẽ chiến thắng.');
INSERT INTO "hasan_abdulkarim" VALUES(37,174,'37:174','Bởi thế, hãy tạm lánh xa một thời gian.');
INSERT INTO "hasan_abdulkarim" VALUES(37,175,'37:175','Và xem họ (xử sự ra sao) và họ sẽ sớm thấy (kết quả)!');
INSERT INTO "hasan_abdulkarim" VALUES(37,176,'37:176','Thế, phải chăng họ giục việc trừng phạt của TA chóng đến?');
INSERT INTO "hasan_abdulkarim" VALUES(37,177,'37:177','Nhưng khi nó (hình phạt) đáp xuống sân nhà của họ thì buổi sáng sẽ là điều thê thảm cho những người đã được báo trước.');
INSERT INTO "hasan_abdulkarim" VALUES(37,178,'37:178','Bởi thế, hãy lánh xa họ một thời gian ngắn,');
INSERT INTO "hasan_abdulkarim" VALUES(37,179,'37:179','Và xem họ (xử sự ra sao); rồi họ sẽ sớm thấy (kết quả)!');
INSERT INTO "hasan_abdulkarim" VALUES(37,180,'37:180','Quang vinh thay Thượng Đế của Ngươi, Thượng Đế của Danh dự và Quyền lực! Ngài vô tội về những điều mà họ đã qui cho Ngài.');
INSERT INTO "hasan_abdulkarim" VALUES(37,181,'37:181','Và Salam (Sự Bằng an) cho các vị Sứ Giả!');
INSERT INTO "hasan_abdulkarim" VALUES(37,182,'37:182','Và mọi lời ca tụng đều dâng lên Allah, Thượng Đế của vũ trụ và muôn loài.');
INSERT INTO "hasan_abdulkarim" VALUES(38,1,'38:1','Sad. Thề bởi (Kinh) Qur''an đầy Lời Nhắc Nhở.');
INSERT INTO "hasan_abdulkarim" VALUES(38,2,'38:2','Không, những kẻ không tin lúc nào cũng tỏ vẻ cường bạo và chống đối;');
INSERT INTO "hasan_abdulkarim" VALUES(38,3,'38:3','Có bao nhiêu thế hệ trước họ đã bị TA tiêu diệt? Nhưng cuối cùng họ khóc lóc van xin (khoan hồng) khi đã hết thời hạn cứu chữa.');
INSERT INTO "hasan_abdulkarim" VALUES(38,4,'38:4','Và họ ngạc nhiên về việc một Người báo trước (Muhammad) xuất thân từ họ đến cảnh cáo họ. Những người không có niềm tin bảo: “Đây là một tên phù thủy, một tên nói dối.”');
INSERT INTO "hasan_abdulkarim" VALUES(38,5,'38:5','Phải chăng Y (Muhammad) nhập (tất cả) các thần linh lại thành một Thượng Đế? Đây thật là một điều hết sức quái dị!');
INSERT INTO "hasan_abdulkarim" VALUES(38,6,'38:6','Những kẻ cầm đầu trong bọn họ đi rong: “Các anh tiếp tục nhẫn nại, bám chắc vào các thần linh của các anh, bởi vì đây là một kế hoạch."');
INSERT INTO "hasan_abdulkarim" VALUES(38,7,'38:7','“Chúng ta chưa hề nghe đến điều này nơi tín ngưỡng sau cùng (của tổ tiên chúng ta). Thật ra, đây chỉ là một điều bịa đặt!');
INSERT INTO "hasan_abdulkarim" VALUES(38,8,'38:8','“Trong chúng ta há Lời nhắc nhở (Qur’an) chỉ được ban xuống riêng cho Y thôi hay sao?” Không, họ nghi ngờ về Lời nhắc nhỡ (Qur’an) của TA. Không, họ chưa nếm mùi trừng phạt của TA!');
INSERT INTO "hasan_abdulkarim" VALUES(38,9,'38:9','Hoặc phải chăng họ giữ kho tàng Hồng Ân của Thượng Đế của Ngươi (Muhammad!), Đấng Toàn Năng, Đấng Ban bố?');
INSERT INTO "hasan_abdulkarim" VALUES(38,10,'38:10','Hoặc phải chăng quyền thống trị các tầng trời và trái đất và vạn vật giữa trời đất là của họ? Nếu thế, hãy để cho họ đi lên đó!');
INSERT INTO "hasan_abdulkarim" VALUES(38,11,'38:11','Họ là một đội quân sẽ bị đánh bại trong liên quân.');
INSERT INTO "hasan_abdulkarim" VALUES(38,12,'38:12','Trước họ (đã có nhiều kẻ) phủ nhận (các Sứ Giả của Allah) như đám dân của Nuh, ''Ad và Fir-''awn, chủ nhân của các cột trụ.');
INSERT INTO "hasan_abdulkarim" VALUES(38,13,'38:13','Và (đám dân của) Thamud và người dân của Lut và dân cư của khu rừng Aykah. Họ là thành phần của liên quân (người ngoại đạo).');
INSERT INTO "hasan_abdulkarim" VALUES(38,14,'38:14','Tất cả đều phủ nhận các Sứ Giả, cho nên sự trừng phạt của TA ắt xảy ra.');
INSERT INTO "hasan_abdulkarim" VALUES(38,15,'38:15','Và những người này chỉ còn chờ một tiếng gầm duy nhất. Khi đó, chắc chắn sẽ không còn sự triễn hạn nữa.');
INSERT INTO "hasan_abdulkarim" VALUES(38,16,'38:16','Và họ thưa: “Lạy Thượng Đế của bầy tôi! Xin Ngài nhanh chóng cho chúng tôi thấy phần số của chúng tôi trước khi xảy ra Ngày Thanh Toán.”');
INSERT INTO "hasan_abdulkarim" VALUES(38,17,'38:17','Ngươi hãy ráng chịu đựng về những điều họ nói và hãy nhớ đến người bề tôi Dawood của TA, một con người cang cường. Quả thật, Y hằng quay về (sám hối với Allah).');
INSERT INTO "hasan_abdulkarim" VALUES(38,18,'38:18','Chính TA đã chế ngự núi non, (phán bảo) hãy cùng với Y tán dương TA đêm và ngày.');
INSERT INTO "hasan_abdulkarim" VALUES(38,19,'38:19','Và chim chóc tụ tập thành đàn; tất cả cùng hợp đoàn với Y quay về (tán dương Allah).');
INSERT INTO "hasan_abdulkarim" VALUES(38,20,'38:20','Và TA đã củng cố quyền bính của Y và ban cho Y trí sáng suốt trong việc phán xét và ăn nói quả quyết.');
INSERT INTO "hasan_abdulkarim" VALUES(38,21,'38:21','Ngươi (Muhammad) có biết câu chuyện về hai người kiện nhau đã trèo tường đột nhập vào phòng tu (của Dawood) hay chưa?');
INSERT INTO "hasan_abdulkarim" VALUES(38,22,'38:22','Khi họ vào gặp Dawood, Y đâm hoảng sợ. Họ thưa: “Xin ngài chớ sợ! Chúng tôi là hai người kiện nhau, người này muốn chèn ép người kia; do đó, xin ngài phân xử giữa chúng tôi bằng sự thật và chớ bất công và hướng dẫn chúng tôi đến con đường êm xuôi."');
INSERT INTO "hasan_abdulkarim" VALUES(38,23,'38:23','“Thực sự, đây là người anh em của tôi (trong đạo). Anh ấy có chín mươi chín con cừu cái, và tôi chỉ có một con cừu cái duy nhất. Thế mà anh ấy còn bảo tôi ''hãy giao nó cho ta.'' Và anh ấy đã tranh luận thắng tôi.”');
INSERT INTO "hasan_abdulkarim" VALUES(38,24,'38:24','(Dawood) vội bảo (chưa kịp hỏi đối phương): “Chắc chắn, y đã bức hiếp ngươi bằng cách đòi nhập con cừu của ngươi vào bầy cừu của y. Quả thật (trong việc hùn hạp làm ăn), nhiều người hợp tác hay bức hiếp lẫn nhau ngoại trừ những ai có đức tin và làm điều thiện; nhưng ít có người như thế.” Và Dawood sực thấy có lẽ TA đã thử thách Y, cho nên Y đã cầu xin Thượng Đế của Y tha thứ và sụp xuống cúi mình và quay về (sám hối với Allah).');
INSERT INTO "hasan_abdulkarim" VALUES(38,25,'38:25','Do đó, TA đã tha thứ cho Y về điều đó; và quả thật, nơi TA Y có được một chỗ gần gũi và một nơi trở về tốt đẹp.');
INSERT INTO "hasan_abdulkarim" VALUES(38,26,'38:26','Này hỡi Dawood! TA đã chỉ định Ngươi làm một Đại diện trên trái đất. Bởi thế, hãy xét xử nhân loại theo sự thật (và công bằng) và chớ làm theo ý muốn (của Ngươi), bởi vì nó sẽ đưa Ngươi lạc khỏi con đường của Allah. Quả thật, những ai lạc khỏi đạo của Allah thì sẽ bị trừng phạt khủng khiếp về tội đã quên bẵng Ngày Thanh toán.');
INSERT INTO "hasan_abdulkarim" VALUES(38,27,'38:27','Và TA đã không tạo ra trời và đất và muôn loài giữa ười đất vô mục đích. Đâv chỉ là lối suy nghĩ của những ai không có niềm tin ấy thôi. Và khốn kho cho những ai không có đức tin không tránh khỏi Lửa.');
INSERT INTO "hasan_abdulkarim" VALUES(38,28,'38:28','Há TA sẽ đối xử với những ai có đức tin và làm điều thiện giống như đối với những kẻ thối nát trên trái đất hay sao? Hoặc TA sẽ đối xử với người sợ Allah như đối với kẻ bất lương hay sao?');
INSERT INTO "hasan_abdulkarim" VALUES(38,29,'38:29','Đây là một Kinh Sách (Qur''an) đầy phúc đức mà TA đã ban xuống cho Ngươi để giúp họ nghiền ngẫm về những Câu của Nó và để cho những người thông hiểu ghi nhớ.');
INSERT INTO "hasan_abdulkarim" VALUES(38,30,'38:30','Và TA đã ban cho Dawood (đứa con trai) Sulayman, một người bề tôi ưu hạng (của TA) bởi vì Y hằng quay về (sám hối với TA).');
INSERT INTO "hasan_abdulkarim" VALUES(38,31,'38:31','Vào một buổi xế chiều, có một số ngựa tốt, vó câu lẹ làng được mang đến trình Y (Sulayman).');
INSERT INTO "hasan_abdulkarim" VALUES(38,32,'38:32','Y bảo: “Ta đã thật sự yêu thích món vật tốt hơn việc tưởng nhớ Thượng Đế của Ta” cho đến lúc (mặt trời) khuất bóng sau màn đêm.');
INSERT INTO "hasan_abdulkarim" VALUES(38,33,'38:33','(Và bảo): “Hãy dắt chúng (ngựa) trở lại cho ta.” Và Y lấy tay sờ (vuốt) chân và cổ của chúng.');
INSERT INTO "hasan_abdulkarim" VALUES(38,34,'38:34','Và TA đã thử thách Sulayman. TA đã đặt lên ngai vàng của Y một con quỷ.( Vì thế Y đã mất ngôi trong một thời gian). Rồi, Y quay về sám hối (với TA và được phục ngôi trở lại).');
INSERT INTO "hasan_abdulkarim" VALUES(38,35,'38:35','(Sulayman) thưa: “Lạy Thượng Đế của bề tôi! Xin Ngài tha thứ cho bề tôi và ban cho bề tôi một quyền bính (triều đại) mà Ngài sẽ không ban cho một ai khác sau bề tôi bởi vì Ngài là Đấng Hằng Ban Bố.”');
INSERT INTO "hasan_abdulkarim" VALUES(38,36,'38:36','Và TA đã chế ngự gió cho Y, và bắt nó bay lướt nhẹ đến nơi nào tùy theo lệnh của Y.');
INSERT INTO "hasan_abdulkarim" VALUES(38,37,'38:37','Và bắt Shaytan phục tùng Y, tất cả (gồm) thợ xây nhà và thợ lặn.');
INSERT INTO "hasan_abdulkarim" VALUES(38,38,'38:38','Còn những tên khác thì bị trói xích lại với nhau.');
INSERT INTO "hasan_abdulkarim" VALUES(38,39,'38:39','(Allah phán cho Sulayman): “Đây là quà tặng của TA ban cho Ngươi. Do đó, sử dụng (tặng) hay giữ lại khống cần phải tính sổ.”');
INSERT INTO "hasan_abdulkarim" VALUES(38,40,'38:40','Và thực sự, Y có nơi TA một chỗ gần gũi và một nơi trở về tốt đẹp.');
INSERT INTO "hasan_abdulkarim" VALUES(38,41,'38:41','Và hãy nhớ đến người bề tôi Ayyub của TA khi Y khóc than cầu cứu với Thượng Đế của Y, thưa: “Quả thật, Shaytan đã chạm phải (thân mình của) bề tôi làm cho bề tôi khổ tâm và đau đớn (thể xác).”');
INSERT INTO "hasan_abdulkarim" VALUES(38,42,'38:42','(Allah phán): “Hãy dùng chân của ngươi dậm lên mặt đất. Nơi đây, sẽ có nước mát (chảy ra) cho ngươi tắm rửa, và giải khát.”');
INSERT INTO "hasan_abdulkarim" VALUES(38,43,'38:43','Và TA đã trả gia đình lại cho Y và ban cùng với họ Hồng Ân tương tự từ TA và là một sự Nhắc Nhở cho những người thông hiểu.');
INSERT INTO "hasan_abdulkarim" VALUES(38,44,'38:44','“Và hãy nắm trong tay một chùm cỏ, mà Ngươi dùng để đánh và chớ bỏ lời thề.” Quả thật, TA đã thấy Y hằng chịu đựng, kiên nhẫn. Y thật là một người bề tôi ưu hạng bởi vì Y hằng quay về (sám hối).');
INSERT INTO "hasan_abdulkarim" VALUES(38,45,'38:45','Và hãy nhớ đến những người bề tôi Ibrahim, và Is-haaq và Ya''qub của TA. Họ là những người cang cường và nhìn thấu sự việc.');
INSERT INTO "hasan_abdulkarim" VALUES(38,46,'38:46','Quả thật, TA đã chọn họ đặc biệt tưởng nhớ Ngôi nhà.');
INSERT INTO "hasan_abdulkarim" VALUES(38,47,'38:47','Và quả thật, đối với TA chắc chắn họ là thành phần ưu tú được tuyển chọn!');
INSERT INTO "hasan_abdulkarim" VALUES(38,48,'38:48','Và hãy nhớ đến Isma''il và Al-Yasa'' (Elisha) và Zul-kifl. Và tất cả đều là thành phần ưu tú.');
INSERT INTO "hasan_abdulkarim" VALUES(38,49,'38:49','(Qur''an) này là Lời Nhắc Nhở. Và quả thật những người ngay chính sợ Allah sẽ có một nơi trở về tốt đẹp.');
INSERT INTO "hasan_abdulkarim" VALUES(38,50,'38:50','Những Ngôi Vườn Vĩnh Cửu (của thiên đàng) với những cánh cửa luôn luôn được mở rộng cho họ.');
INSERT INTO "hasan_abdulkarim" VALUES(38,51,'38:51','Trong đó, họ sẽ tựa mình nằm nghỉ thoải mái; và sẽ gọi tùy thích đủ loại trái cây để dùng và nước giải khát để uống;');
INSERT INTO "hasan_abdulkarim" VALUES(38,52,'38:52','Bên cạnh họ là những trinh nữ đồng lứa tuổi với cái nhìn thẹn thùng.');
INSERT INTO "hasan_abdulkarim" VALUES(38,53,'38:53','Đây là những món mà các ngươi đã được hứa vào Ngày Thanh Toán.');
INSERT INTO "hasan_abdulkarim" VALUES(38,54,'38:54','Đây là bổng lộc không hề dứt cạn của TA.');
INSERT INTO "hasan_abdulkarim" VALUES(38,55,'38:55','Đây (là phần của người sợ Allah). Còn đối với những kẻ thái quá thì sẽ có một nơi trở về rất xấu xa:');
INSERT INTO "hasan_abdulkarim" VALUES(38,56,'38:56','hỏa ngục, nơi mà chúng sẽ bị đốt (nướng); một nơi nghỉ rất xấu.');
INSERT INTO "hasan_abdulkarim" VALUES(38,57,'38:57','Đây (là phần của kẻ thái quá). Bởi thế, hãy để cho chúng nếm nó: một lọai nước cực sôi và một loại chất mủ hôi tanh.');
INSERT INTO "hasan_abdulkarim" VALUES(38,58,'38:58','Và các hình phạt tương xứng khác.');
INSERT INTO "hasan_abdulkarim" VALUES(38,59,'38:59','Đây là một đoàn người cắm đầu chạy chung với các ngươi, không được ai nghinh đón cả! Chắc chắn, chúng sẽ bị nướng trong Lửa.');
INSERT INTO "hasan_abdulkarim" VALUES(38,60,'38:60','(Đám thuộc hạ) sẽ nói (với các lãnh tụ của chúng): “Không, Quí ngài (cũng thế!) Không ai chào đón quí ngài cả. Chính quí ngài đã mang điều này đến cho chúng tôi. Bởi thế, đây là chỗ ngụ xấu xa."');
INSERT INTO "hasan_abdulkarim" VALUES(38,61,'38:61','Và chúng thưa: “Lạy Thượng Đế của bầy tôi! Kẻ nào làm cho bầy tôi mang tội này thì xin phạt y gấp đôi trong hỏa ngục.”');
INSERT INTO "hasan_abdulkarim" VALUES(38,62,'38:62','Và chúng sẽ bảo: “Chuyện gì xảy đến cho chúng tôi? Tại sao chúng tôi không thấy những người mà chúng tôi đã liệt vào đám người xấu?"');
INSERT INTO "hasan_abdulkarim" VALUES(38,63,'38:63','“Phải chăng chúng tôi đã chế giễu họ hay chúng tôi bị quáng mắt không nhìn thấy họ?”');
INSERT INTO "hasan_abdulkarim" VALUES(38,64,'38:64','Đấy là Sự Thật về việc tranh cãi của các cư dân nơi hỏa ngục.');
INSERT INTO "hasan_abdulkarim" VALUES(38,65,'38:65','Hãy bảo (họ): "Ta chỉ là một người báo trước. Không có Thượng Đế nào cả duy chỉ có Allah, Đấng Duy Nhất, Đấng Tối Thượng."');
INSERT INTO "hasan_abdulkarim" VALUES(38,66,'38:66','“Thượng Đế của các tầng trời và trái đất và vạn vật giữa trời đất, Đấng Toàn Năng, Đấng Hằng Tha Thứ”');
INSERT INTO "hasan_abdulkarim" VALUES(38,67,'38:67','Hãy bảo họ: “Nó (Qur''an) là một nguồn tin vĩ đại."');
INSERT INTO "hasan_abdulkarim" VALUES(38,68,'38:68','“Cái mà các người thường lánh xa."');
INSERT INTO "hasan_abdulkarim" VALUES(38,69,'38:69','"Ta không biết gì về các lãnh tụ trên cao khi họ tranh luận với nhau."');
INSERT INTO "hasan_abdulkarim" VALUES(38,70,'38:70','"Ta chỉ được mặc khải cho biết ta là một người báo trước công khai.”');
INSERT INTO "hasan_abdulkarim" VALUES(38,71,'38:71','(Hãy nhớ lại ) khi xưa Thượng Đế của Ngươi (Muhammad) đã phán bảo các Thiên Thần: "TA sắp tạo một con người phàm bằng đất sét"');
INSERT INTO "hasan_abdulkarim" VALUES(38,72,'38:72','Bởi thế, khi TA uốn nắn Y (Adam) thành hình thể và hà vào Y linh hồn của TA, (TA phán bảo các Thiên Thần:) "Các ngươi hãy sụp xuống phủ phục Y.”');
INSERT INTO "hasan_abdulkarim" VALUES(38,73,'38:73','Do đó, tất cả Thiên Thần đồng quỳ lạy;');
INSERT INTO "hasan_abdulkarim" VALUES(38,74,'38:74','Ngoại trừ Iblis. Nó tự cao và là một tên phản nghịch.');
INSERT INTO "hasan_abdulkarim" VALUES(38,75,'38:75','Allah phán: “Này hỡi Iblis! Điều gì cản nhà ngươi quỳ lạy một nhân vật mà TA đã tạo từ hai Bàn Tay của TA? Phải chăng nhà ngươi ngạo mạn hay trịch thượng?”');
INSERT INTO "hasan_abdulkarim" VALUES(38,76,'38:76','(Iblis) thưa: “Bề tôi tốt hơn Y (Adam) bởi vì Ngài đã tạo bề tôi từ lửa và còn Ngài tạo Y từ đất sét.”');
INSERT INTO "hasan_abdulkarim" VALUES(38,77,'38:77','(Allah) phán: “Vậy thì nhà ngươi hãy bước ra khỏi đó (vườn trời). Nhà ngươi đáng bị tống cổ ra ngoài."');
INSERT INTO "hasan_abdulkarim" VALUES(38,78,'38:78','“Nhà ngươi sẽ bị TA nguyền rủa cho đến Ngày Phán Xử Cuối Cùng.”');
INSERT INTO "hasan_abdulkarim" VALUES(38,79,'38:79','(Iblis) thưa: “Lạy Thượng Đế của bề tôi! Xin Ngài gia hạn cho bề tôi đến Ngày mà (người chết) sẽ được phục sinh trở lại.”');
INSERT INTO "hasan_abdulkarim" VALUES(38,80,'38:80','(Allah) phán: "Thế thì nhà ngươi là một kẻ được gia hạn"');
INSERT INTO "hasan_abdulkarim" VALUES(38,81,'38:81','“Cho đến Ngày của thời hạn đã được ấn định.”');
INSERT INTO "hasan_abdulkarim" VALUES(38,82,'38:82','(Iblis) thưa: “Do Quyền Năng của Ngài, bề tôi sẽ hướng dẫn tất cả bọn chúng (con cháu của Adam) lầm lạc."');
INSERT INTO "hasan_abdulkarim" VALUES(38,83,'38:83','“Ngoại trừ những bề tôi chân thành của Ngài trong bọn chúng.”');
INSERT INTO "hasan_abdulkarim" VALUES(38,84,'38:84','(Allah) phán: “Sự Thật (là thế), và TA luôn luôn nói sự Thật."');
INSERT INTO "hasan_abdulkarim" VALUES(38,85,'38:85','“Rằng chắc chắn TA sẽ giam nhà ngươi và kẻ nào trong chúng (nhân loại) theo ngươi vào hỏa ngục, tất cả."');
INSERT INTO "hasan_abdulkarim" VALUES(38,86,'38:86','Hãy bảo (chúng, Hỡi Muhammad!): “Ta không đòi hỏi các người tưởng thưởng Ta về (Qur''an) này và Ta cũng không phải là một tên giả vờ nói ra điều không có."');
INSERT INTO "hasan_abdulkarim" VALUES(38,87,'38:87','“Nó (Qur''an) chỉ là một sự Nhắc Nhở cho muôn loài (người, và tất cả những gì hiện có)."');
INSERT INTO "hasan_abdulkarim" VALUES(38,88,'38:88','“Và chắc chắn các người sẽ biết Thông Tin của Nó sau một thời gian.”');
INSERT INTO "hasan_abdulkarim" VALUES(39,1,'39:1','Kinh Sách (Qur''an) do Allah, Đấng Toàn Năng, Đấng Chí Minh ban xuống.');
INSERT INTO "hasan_abdulkarim" VALUES(39,2,'39:2','Chắc chắn TA ban Kinh Sách xuống cho Ngươi (Muhammad) bằng sự thật. Bởi thế, hãy thờ phụng Allah, thành tâm thần phục riêng Ngài.');
INSERT INTO "hasan_abdulkarim" VALUES(39,3,'39:3','Há việc sùng bái chân thành không phải chỉ dành riêng cho Allah ư? Thế mà những kẻ đã chấp nhận những vị bảo hộ ngoài Ngài đã nói: “Chúng tôi tôn thờ họ chỉ vì nhờ họ đưa chúng tôi đến gần Allah.” Chắc chắn Allah sẽ xét xử giữa bọn chúng về điều chúng thường tranh chấp. Chắc chắn Allah không hướng dẫn ai là người nói dối và không có đức tin.');
INSERT INTO "hasan_abdulkarim" VALUES(39,4,'39:4','Nếu Allah muốn nhận cho Mình một đứa con thì Ngài đã chọn trong số nhân vật mà Ngài đã tạo một người vừa ý Ngài. Nhưng Ngài quang vinh và trong sạch! Allah là Đấng Duy Nhất, Đấng Tối Thượng.');
INSERT INTO "hasan_abdulkarim" VALUES(39,5,'39:5','Ngài vì Chân Lý đã tạo các tầng trời và trái đất. Ngài cuốn ban đêm bao phủ ban ngày và cuốn ban ngày bao phủ ban đêm. Ngài chế ngự mặt trời và mặt trăng. Mỗi vật di chuyển đến một thời hạn ấn định. Há Ngài không là Đấng Toàn Năng, Đấng Hằng Tha Thứ hay sao?');
INSERT INTO "hasan_abdulkarim" VALUES(39,6,'39:6','Ngài đã tạo hóa các ngươi (con người) từ một Người duy nhất (Adam). Rồi từ Y, tạo ra người vợ của Y (Hauwa). Rồi Ngài ban tám con gia súc (cặp trừu, cặp dê, cặp bò, cặp lạc đà, đực và cái) xuống cho các ngươi. Ngài tạo các ngươi trong dạ con của các bà mẹ của các ngươi theo từng giai đoạn, cái này sau cái kia, trong ba lớp màng tối. Allah, Thượng Đế của các ngươi là thế. Ngài nắm quyền thống trị. Không có Thượng Đế nào cả duy chỉ Ngài (là Thượng Đế). Thế tại sao các người lánh xa Ngài?');
INSERT INTO "hasan_abdulkarim" VALUES(39,7,'39:7','Nếu các ngươi phủ nhận (Allah) thì chắc chắn Allah không cần đến các ngươi nhưng Ngài không hài lòng với thái độ vong ân của đám bầy tôi của Ngài. Ngược lại, nếu các ngươi tri ân thì Ngài sẽ hài lòng với các ngươi về việc đó. Bởi vì không một người khuân vác nào sẽ vác giùm gánh nặng (tội lỗi) của người khác. Rồi các ngươi sẽ trở về gặp Thượng Đế của các ngươi trở lại; tiếp đó, Ngài sẽ cho các ngươi biết về những điều mà các ngươi đã từng làm. Chắc chắn Ngài là Đấng Hằng Biết những điều nằm trong lòng của mỗi người.');
INSERT INTO "hasan_abdulkarim" VALUES(39,8,'39:8','Và khi gặp hoạn nạn, con người kêu than cầu cứu với Thượng Đế của y, quay về sám hối với Ngài. Rồi khi Ngài ban cho y ân huệ từ Ngài, y quên bẵng điều mà y đã từng cầu xin Ngài trước kia và hơn nữa còn bày đặt những đối thủ ngang vai với Allah hầu mong dắt thiên hạ lạc khỏi con đường của Ngài. Hãy bảo y: "Hãy hưởng thụ sự vô tín của ngươi trong một thời gian ngắn. Chắc chắn ngươi sẽ là một người bạn của hỏa ngục."');
INSERT INTO "hasan_abdulkarim" VALUES(39,9,'39:9','Phải chăng một người thức đêm dốc lòng cung kính (Allah), lúc thì phủ phục, lúc thì đứng thẳng, luôn luôn chú ý đến đời sau và hy vọng nơi Đức Khoan Dung của Thượng Đế của y (giống với một kẻ không có đức tin hay sao) Hãy bảo y: “Phải chăng một người (hiểu) biết và một người thiếu hiểu biết ngang bằng nhau?” Chắc chắn, chỉ những người thông hiểu mới ghi nhớ (lời khuyến cáo).');
INSERT INTO "hasan_abdulkarim" VALUES(39,10,'39:10','Hãy bảo (họ): “Hỡi các bầy tôi có đức tin của TA! Hãy sợ Thượng Đế của các ngươi và làm tròn bổn phận đối với Ngài. Ai làm điều thiện tốt nơi trần thế thì sẽ gặp điều tốt trở lại. Và đất đai của Allah rộng bao la. Chỉ những người kiên nhẫn mới hưởng trọn phần thưởng của mình, không cần phải tính sổ.”');
INSERT INTO "hasan_abdulkarim" VALUES(39,11,'39:11','Hãy bảo họ: “Quả thật, Ta (Muhammad) nhận lệnh phải thờ phụng Allah, thành tâm thần phục riêng Ngài."');
INSERT INTO "hasan_abdulkarim" VALUES(39,12,'39:12','“Và được lệnh phải trở thành một trong những người Muslim đầu tiên.”');
INSERT INTO "hasan_abdulkarim" VALUES(39,13,'39:13','Hãy bảo họ: "Ta sợ, nếu bất tuân Thượng Đế của Ta, sẽ bị trừng phạt vào một Ngày (Xét Xử) vĩ đại.”');
INSERT INTO "hasan_abdulkarim" VALUES(39,14,'39:14','Hãy bảo họ: “Ta thờ phụng Allah, thành tâm thần phục riêng Ngài.”');
INSERT INTO "hasan_abdulkarim" VALUES(39,15,'39:15','Thế, các ngươi hãy tôn thờ kẻ nào mà các ngươi thích ngoài Ngài. Hãy bảo họ: “Những kẻ mất mát là những ai sẽ đánh mất bản thân (linh hồn) và gia đình vào Ngày phục sinh. Đó thực sự là một sự mất mát rõ ràng!”');
INSERT INTO "hasan_abdulkarim" VALUES(39,16,'39:16','Chúng sẽ bị các tấm màn bằng lửa bao phủ phía trên và phía dưới. Allah dùng nó (lửa) để (cảnh báo và) làm run sợ bầy tôi của Ngài như thế. “Hỡi bầy tôi của TA! Hãy sợ TA.”');
INSERT INTO "hasan_abdulkarim" VALUES(39,17,'39:17','Còn những ai tránh xa các tà thần, e sợ mắc phải (tội) tôn thờ chúng và quay về sám hối với Allah thì sẽ được tin mừng. (Hỡi Muhammad!) hãy báo tin mừng đó cho bầy tôi của TA:');
INSERT INTO "hasan_abdulkarim" VALUES(39,18,'39:18','Những ai nghe Lời Phán và tuân theo điều tốt nhất của nó. Họ là những người đã được Allah hướng dẫn; và là những người thông hiểu.');
INSERT INTO "hasan_abdulkarim" VALUES(39,19,'39:19','Thế phải chăng một người mà Lời xử phạt đã được xác nhận đúng (bằng với một người vô tội)? Thế Ngươi (Muhammad) muốn cứu vớt một người ở trong hỏa ngục hay sao?');
INSERT INTO "hasan_abdulkarim" VALUES(39,20,'39:20','Ngược lại, những ai sợ Thượng Đế (Allah) của họ thì sẽ có các tầng lầu xây cất chồng lên nhau dành riêng cho họ, bên dưới có các dòng sông chảy. (Đó là) Lời Hứa của Allah. Và Allah không hề thất hứa.');
INSERT INTO "hasan_abdulkarim" VALUES(39,21,'39:21','Há Ngươi (Muhammad) không thấy việc Allah cho mưa từ trên trời xuống, rồi làm cho nó thấm xuống lòng đất chảy thành mạch nước; rồi dùng nó làm mọc ra cây trái đủ màu sắc; rồi sau đó, mùa màng bắt đầu tàn úa. Ngươi thấy nó héo vàng; rồi Ngài (Allah) làm cho nó khô héo vỡ vụn. Quả thật, trong đó là sự nhắc nhở cho những người thông hiểu.');
INSERT INTO "hasan_abdulkarim" VALUES(39,22,'39:22','Thế, phải chăng một người được Allah mở rộng tấm lòng để tiếp thu Islam, rồi bước theo ánh sáng của Thượng Đế của y (giống với một người không có đức tin)? Bởi thế, khốn khổ cho những ai mà tấm lòng đã trở thành chai cứng trước Lời Nhắc Nhở của Allah! Rõ ràng, họ đang lầm lạc.');
INSERT INTO "hasan_abdulkarim" VALUES(39,23,'39:23','Allah đã truyền xuống những Lời tuyên bố tốt đẹp nhất: một Kinh Sách mang đại ý giống nhau và lặp đi lặp lại. Khi nghe nó, làn da của những ai sợ Thượng Đế (Allah) của họ nổi lên vì run sợ. Rồi lớp da và tấm lòng của họ mềm đi khi nhớ đến Allah. Đó là Chỉ Đạo của Allah. Ngài dùng Nó để hướng dẫn người nào Ngài muốn; còn ai mà Allah đánh lạc hướng thì sẽ không có người hướng đạo.');
INSERT INTO "hasan_abdulkarim" VALUES(39,24,'39:24','Thế thì một người dùng bộ mặt của mình để che đỡ hình phạt xấu xa của Ngày phục sinh (có bằng với người được vào thiên đàng hay không)? Và có lời bảo những tên làm điều sai quấy: “Hãy nếm những điều mà các ngươi đã làm ra.”');
INSERT INTO "hasan_abdulkarim" VALUES(39,25,'39:25','Những ai trước họ đã phủ nhận (các Sứ Giả của Allah); cho nên sự trừng phạt đã đến với họ từ các hướng mà họ không nhận ra.');
INSERT INTO "hasan_abdulkarim" VALUES(39,26,'39:26','Bởi thế, Allah bắt họ nếm mùi ô nhục ở đời này nhưng sự trừng phạt dành cho họ ở đời sau chắc chắn sẽ to lớn hơn, nếu họ biết điều đó!');
INSERT INTO "hasan_abdulkarim" VALUES(39,27,'39:27','Chắc chắn, TA đã trình bày cho nhân loại trong Qur''an này tất cả các hình ảnh thí dụ để giúp họ ghi nhớ (Lời Cảnh Báo).');
INSERT INTO "hasan_abdulkarim" VALUES(39,28,'39:28','Một Qur''an bằng tiếng Ả Rập, không có gì quanh co, để may ra họ sợ Allah.');
INSERT INTO "hasan_abdulkarim" VALUES(39,29,'39:29','Allah đưa ra thí dụ so sánh về một người (nô lệ) phục vụ nhiều chủ nhân gồm những kẻ hợp tác nhưng hay tranh chấp nhau và một người phục vụ chỉ một ông chủ duy nhất, thế hai người đó, nếu mang ra so sánh, có ngang bằng nhau không? Mọi lời ca tụng đều thuộc về Allah! Không, đa số bọn họ không biết gì.');
INSERT INTO "hasan_abdulkarim" VALUES(39,30,'39:30','Thật sự, Ngươi (Muhammad!) sẽ chết và họ (cũng) sẽ chết.');
INSERT INTO "hasan_abdulkarim" VALUES(39,31,'39:31','Rồi vào Ngày Xét xử cuối cùng, các người sẽ đối chất với nhau trước mặt Thượng Đế của các người.');
INSERT INTO "hasan_abdulkarim" VALUES(39,32,'39:32','Thế còn ai sai quấy hơn kẻ đã đặt điều nói dối rồi đổ thừa cho Allah và phủ nhận Chân Lý khi nó đến với y? Há trong hỏa ngục không có một chỗ ngụ dành cho những kẻ không có đức tin hay sao?');
INSERT INTO "hasan_abdulkarim" VALUES(39,33,'39:33','Và những ai mang Chân Lý đến và tin nơi nó thì là những người ngay chính sợ Allah.');
INSERT INTO "hasan_abdulkarim" VALUES(39,34,'39:34','Họ sẽ có được mọi điều mà họ ước muốn nơi Thượng Đế của họ. Đó là phần thưởng của những người làm tốt.');
INSERT INTO "hasan_abdulkarim" VALUES(39,35,'39:35','Mục đích để Allah xóa tội mà họ đã phạm và ân thưởng họ về những điều tốt nhất mà họ đã từng làm.');
INSERT INTO "hasan_abdulkarim" VALUES(39,36,'39:36','Há Allah không đủ cho bầy tôi của Ngài ư? Và chúng muốn làm cho Ngươi sợ với những ai khác không phải là Ngài. Và ai mà Allah không hướng dẫn thì sẽ không có người hướng đạo.');
INSERT INTO "hasan_abdulkarim" VALUES(39,37,'39:37','Và người nào mà Allah hướng dẫn thì sẽ không bị ai dắt đi lạc. Há Allah không Toàn Năng, nắm mọi việc báo đền hay sao?');
INSERT INTO "hasan_abdulkarim" VALUES(39,38,'39:38','Và chắc chắn nếu Ngươi hỏi họ: “Ai đã tạo các tầng trời và trái đất” thì chắc chắn họ sẽ đáp ''Allah'' Hãy bảo họ: “Các người hãy nghĩ xem, nếu Allah muốn hãm hại ta thì những kẻ mà các người khấn vái ngoài Allah có thể bốc đi khỏi ta nỗi đau đớn ấy hay không? hoặc nếu Allah muốn khoan dung ta thì chúng cố thể giữ nó lại được hay không?” Hãy bảo họ: “Allah đủ cho ta thôi.” Người tin cậy (Allah) nên phó thác cho Ngài.');
INSERT INTO "hasan_abdulkarim" VALUES(39,39,'39:39','Hãy bảo họ: “Hỡi dân ta! Các người hãy làm theo khả năng của các người, và ta theo khả năng của ta, rồi đây các người sẽ sớm biết:');
INSERT INTO "hasan_abdulkarim" VALUES(39,40,'39:40','Ai sẽ bị trừng phạt nhục nhã và sẽ bị phạt vĩnh viễn.”');
INSERT INTO "hasan_abdulkarim" VALUES(39,41,'39:41','Quả thật, TA đã ban Kinh Sách (Qur''an) xuống cho Ngươi (dùng làm Chỉ Đạo) cho nhân loại bằng sự thật. Bởi thế, ai tiếp thu Chỉ Đạo thì được lợi cho bản thân y; còn ai lạc hướng thì là bất lợi cho bản thân y. Và Ngươi (Muhammad!) không là người Thọ lãnh (đảm trách) giùm cho họ.');
INSERT INTO "hasan_abdulkarim" VALUES(39,42,'39:42','Allah cho bắt hồn của người chết khi mệnh chung và bắt hồn của người chưa chết khi nằm ngủ. Hồn của người nào mà Ngài quyết định cho chết thì sẽ bị giữ lại; và các hồn khác thì được gởi trở về (cơ thể) đến một kỳ hạn ấn định. Quả thật, trong sự việc đó là các dấu hiệu cho đám người biết ngẫm nghĩ.');
INSERT INTO "hasan_abdulkarim" VALUES(39,43,'39:43','Há họ đã nhận những kẻ không phải là Allah làm những vị can thiệp giùm họ? Hãy bảo họ: “Dẫu cho chúng không có quyền hành gì cũng không có lý trí nữa hay sao?”');
INSERT INTO "hasan_abdulkarim" VALUES(39,44,'39:44','Hãy bảo họ: “Mọi việc can thiệp đều thuộc về Allah cả. Ngài nắm quyền thống trị các tầng trời và trái đất. Rồi các người sẽ được đưa về gặp Ngài trở lại.”');
INSERT INTO "hasan_abdulkarim" VALUES(39,45,'39:45','Và khi nghe nhắc đến riêng Allah thôi, tấm lòng của những ai không tin tưởng nơi đời sau tràn đầy oán ghét; và khi những kẻ không phải là Ngài (Allah) được nhắc đến thì họ vui thích ra mặt!');
INSERT INTO "hasan_abdulkarim" VALUES(39,46,'39:46','Hãy (cầu nguyện) thưa: “Lạy Allah, Đấng sáng tạo các tầng trời và trái đất! Đấng Biết hết mọi điều vô hình (bí mật) và điều bộc lộ công khai, xin hãy xét xử giữa bầy tôi của Ngài về những điều mà họ thường tranh chấp!”');
INSERT INTO "hasan_abdulkarim" VALUES(39,47,'39:47','Và giá những kẻ làm điều sai quấy có được tất cả mọi vật trên trái đất và nhiều hơn thế nữa thì chắc chắn họ sẽ dùng nó để chuộc nỗi đau đớn của việc trừng phạt vào Ngày Phán Xử Cuối Cùng (nhưng cũng vô ích thôi). Và họ sẽ đón nhận từ Allah điều (hình phạt) mà họ không ngờ đến.');
INSERT INTO "hasan_abdulkarim" VALUES(39,48,'39:48','Và những điều tội lỗi mà họ đã làm sẽ chạm trán họ và những điều mà họ thường chế giễu sẽ bao vây họ.');
INSERT INTO "hasan_abdulkarim" VALUES(39,49,'39:49','Bởi thế, khi gặp hoạn nạn, con người cầu xin TA cứu nạn; rồi khi TA ban ân huệ từ TA cho y,y nói: “Tôi được nó (ân huệ) là do hiểu biết của tôi.” Không, đấy là một sự thử thách nhưng đa số bọn họ không biết.');
INSERT INTO "hasan_abdulkarim" VALUES(39,50,'39:50','Chắc chắn những kẻ sống trước họ cũng đã nói ra lời đó. Nhưng những thứ mà họ đã tậu được chẳng giúp ích gì được cho họ cả.');
INSERT INTO "hasan_abdulkarim" VALUES(39,51,'39:51','Bởi thế, những điều xấu xa mà họ đã làm sẽ rơi nhằm phải họ trở lại. Và những ai làm điều sai quấy trong số những người (đang sống) này sẽ lãnh đủ về những điều tội lỗi mà họ đã gây ra. Và họ sẽ không bao giờ thoát khỏi (Allah).');
INSERT INTO "hasan_abdulkarim" VALUES(39,52,'39:52','Há họ không biết việc Allah nới rộng hay thu hẹp bổng lộc của Ngài cho người mà Ngài muốn hay sao? Quả thật, trong đó là các dấu hiệu cho đám người có đức tin!');
INSERT INTO "hasan_abdulkarim" VALUES(39,53,'39:53','Hãy bảo họ: “Hỡi các bầy tôi của TA! những ai đã làm cho tâm hồn mình vướng tội thì chớ nên tuyệt vọng về Đức Khoan Dung của Allah. Chắc chắn Allah sẽ tha thứ mọi thứ tội. Quả thật, Ngài là Đấng Hằng Tha Thứ, Đấng Rất Mực Khoan Dung.');
INSERT INTO "hasan_abdulkarim" VALUES(39,54,'39:54','“Hãy quay về hối cải với Thượng Đế của các người và thần phục Ngài (trong Islam) trước khi đối diện với hình phạt; rồi các người sẽ không được ai giúp đỡ nữa."');
INSERT INTO "hasan_abdulkarim" VALUES(39,55,'39:55','“Và hãy làm theo điều tốt nhất mà Thượng Đế của các người đã ban xuống cho các người (trong Qur''an) trước khi hình phạt mà các người không nhận thấy sẽ bất ngờ xảy đến cho các người.”');
INSERT INTO "hasan_abdulkarim" VALUES(39,56,'39:56','E rằng một linh hồn sẽ than: “Ôi thật khổ thần tôi! Tôi đã lơ là đối với Allah và là một trong những người hay nhạo báng (Thông Điệp của Allah).”');
INSERT INTO "hasan_abdulkarim" VALUES(39,57,'39:57','Hoặc (y) sẽ nói: “Giá Allah hướng dẫn tôi thì chắc tôi đã trở thành một người sợ Allah rồi!”');
INSERT INTO "hasan_abdulkarim" VALUES(39,58,'39:58','Hoặc y sẽ nói khi thấy hình phạt: “Giá được dịp trở lại (trần gian) thì tôi sẽ trở thành một trong những người làm tốt.”');
INSERT INTO "hasan_abdulkarim" VALUES(39,59,'39:59','Vâng! Chắc chắn các dấu hiệu của TA đã đến cho ngươi (hỡi người!) nhưng ngươi phủ nhận chúng và tỏ ra ngạo mạn và trở thành một kẻ không tin tưởng.');
INSERT INTO "hasan_abdulkarim" VALUES(39,60,'39:60','Và vào Ngày phục sinh, ngươi sẽ thấy gương mặt của những kẻ nói dối rồi đổ thừa cho Allah sẽ nám đen. Há trong hỏa ngục không có một chỗ ngụ cho những kẻ ngạo mạn hay sao?');
INSERT INTO "hasan_abdulkarim" VALUES(39,61,'39:61','Allah sẽ cứu vớt những ai ngay chính, sợ Ngài và làm tròn bổn phận đối với Ngài và đưa họ đến chỗ thành tựu (thiên đàng) của họ. Không một điều xấu xa nào chạm đến mình họ, và họ cũng sẽ không buồn phiền.');
INSERT INTO "hasan_abdulkarim" VALUES(39,62,'39:62','Allah là Đấng Tạo Hóa tất cả vạn vật và là Đấng Bảo Hộ.');
INSERT INTO "hasan_abdulkarim" VALUES(39,63,'39:63','Ngài nắm tất cả các chìa khóa của các tầng trời và trái đất; và những ai phủ nhận các dấu hiệu của Allah là những kẻ sẽ mất mát.');
INSERT INTO "hasan_abdulkarim" VALUES(39,64,'39:64','Hãy bảo (họ): “Phải chăng các người ra lệnh bảo Ta thờ phụng ai đó không phải là Allah hay sao, hỡi những kẻ ngu dốt?”');
INSERT INTO "hasan_abdulkarim" VALUES(39,65,'39:65','Và chắc chắn, Ngươi (Muhammad) và những vị (Sứ Giả) trước Ngươi đã được mặc khải cho biết: “Nếu Ngươi tôn thờ những kẻ hợp tác (thần linh) cùng với Allah thì việc làm của Ngươi sẽ trở thành vô nghĩa và chắc chắn Ngươi sẽ trở thành một kẻ mất mát.”');
INSERT INTO "hasan_abdulkarim" VALUES(39,66,'39:66','Không, hãy thờ phụng Allah và hãy là một người biết ơn (Ngài).');
INSERT INTO "hasan_abdulkarim" VALUES(39,67,'39:67','Và họ đánh giá Allah không đúng với giá trị của Ngài. Vào Ngày phục sinh toàn thể trái đất sẽ bị nắm trọn trong Tay của Ngài và tất cả các tầng trời sẽ được cuộn lại trong Tay Phải của Ngài. Thật quang vinh và trong sạch Ngài! Ngài Tối Cao, vượt hẳn điều mà họ đã qui rằng Ngài có những kẻ hợp tác.');
INSERT INTO "hasan_abdulkarim" VALUES(39,68,'39:68','Và khi Tiếng Còi (Tận thế) được thổi lên, ai ở trong các tầng trời và dưới đất đều phải ngất xỉu, ngoại trừ ai mà Allah muốn buông tha. Rồi Tiếng Còi thứ hai được thổi lên thì này họ sẽ đứng thẳng và ngóng nhìn.');
INSERT INTO "hasan_abdulkarim" VALUES(39,69,'39:69','Và trái đất sẽ chói lòa hào quang của Thượng Đế của nó và quyển sổ (hành động của mỗi người) sẽ được mang ra mở trước mặt; các vị Nabi và những người làm chứng sẽ được triệu đến trước mặt và họ sẽ được phán xử một cách công bằng và sẽ không bị đối xử thiệt thòi một tí nào.');
INSERT INTO "hasan_abdulkarim" VALUES(39,70,'39:70','Và mỗi người (linh hồn) sẽ được thanh toán đầy đủ về việc y đã làm bởi vì Ngài (Allah) là Đấng Hằng Biết những điều họ đã làm.');
INSERT INTO "hasan_abdulkarim" VALUES(39,71,'39:71','Và những ai không tin tưởng sẽ bị lùa đến hỏa ngục từng nhóm mãi cho đến khi họ đến nơi thì các cánh cửa của hỏa ngục sẽ được mở toang và các Vị cai ngục sẽ bảo họ: “Há các vị Sứ Giả xuất thân từ các ngươi đã không đến gặp các ngươi để đọc cho các ngươi các Lời Mặc Khải của Thượng Đế của các ngươi và cảnh báo các ngươi về Ngày gặp gỡ này của các ngươi hay sao?” Họ sẽ đáp: “Vâng, có.” Nhưng Lời xử phạt đã xác nhận đúng đối với những kẻ không có đức tin.');
INSERT INTO "hasan_abdulkarim" VALUES(39,72,'39:72','Sẽ có lời bảo họ: “Hãy bước vào các cửa của hỏa ngục để ở trong đó.” Bởi thế, thật xấu xa thay chỗ ngụ của những kẻ ngạo mạn!');
INSERT INTO "hasan_abdulkarim" VALUES(39,73,'39:73','Ngược lại, những ai ngay chính sợ Allah và làm tròn bổn phận đối với Ngài sẽ được đưa đến thiên đàng từng nhóm, mãi cho đến lúc họ đến nơi thì các cánh cửa của thiên đàng sẽ được mở rộng cho họ và những Vị gác cửa (thiên đàng) sẽ chào họ (nói): Bằng an cho quí vị! Quí vị đã thành tựu, hãy đi vào ở trong đó.”');
INSERT INTO "hasan_abdulkarim" VALUES(39,74,'39:74','Và họ sẽ nói: “Mọi lời ca tụng đều dâng lên Allah, Đấng đã giữ đúng Lời Hứa của Ngài với bầy tôi và cho bầy tôi thừa hưởng đất đai (này). Bầy tôi có thể ở trong thiên đàng bất cứ nơi nào bầy tôi muốn. Bởi thế, phần thưởng ban cho những người làm việc (tốt) thật là ưu việt!”');
INSERT INTO "hasan_abdulkarim" VALUES(39,75,'39:75','Và Ngươi sẽ thấy các Thiên Thần bao quanh Ngai Vương của Allah khắp mọi phía tán dương lời ca tụng Thượng Đế của họ. Và họ (tất cả tạo vật) sẽ được phân xử đúng với sự thật (và công bằng) và có lời hoan hô: “Mọi lời ca tụng đều dâng lên Allah, Thượng Đế của vũ trụ và muôn loài.”');
INSERT INTO "hasan_abdulkarim" VALUES(40,1,'40:1','Ha. Mim.');
INSERT INTO "hasan_abdulkarim" VALUES(40,2,'40:2','Kinh Sách (Qur''an) do Allah, Đấng Toàn Năng, Đấng Toàn Tri ban xuống,');
INSERT INTO "hasan_abdulkarim" VALUES(40,3,'40:3','Đấng tha thứ tội lỗi, chấp nhận việc sám hối, nhưng nghiêm khắc trong việc trừng phạt, Đấng ban bố (ân huệ). Không có Thượng Đế nào cả duy chỉ Ngài (là Thượng Đế). Tất cả đều trở về gặp Ngài.');
INSERT INTO "hasan_abdulkarim" VALUES(40,4,'40:4','Không ai tranh cãi về các dấu hiệu của Allah ngoài những kẻ không có đức tin. Bởi thế, chớ để cho sự tung hoành của chúng trong xứ làm Ngươi (Muhammad) bị mắc lừa.');
INSERT INTO "hasan_abdulkarim" VALUES(40,5,'40:5','Trước chúng, người dân của Nuh và sau chúng, các liên minh (tòng phạm) đã phủ nhận (các Sứ Giả của họ); mỗi một cộng đồng đều âm mưu chống lại Sứ Giả của họ để mong triệt hạ Y và dùng lời lẽ ngụy biện để tranh luận hầu dập tắt Chân Lý; nhưng TA đã bắt phạt họ. Bởi thế, (hãy xem) việc trừng phạt của TA khủng khiếp như thế nào.');
INSERT INTO "hasan_abdulkarim" VALUES(40,6,'40:6','Lời của Thượng Đế của Ngươi (Muhammad) đối với những kẻ không có đức tin đều là thật rằng chúng sẽ là những người bạn của hỏa ngục.');
INSERT INTO "hasan_abdulkarim" VALUES(40,7,'40:7','Những vị (Thiên Thần) mang Ngai Vương của Allah và những vị (Thiên Thần) đứng hầu xung quanh nó đều tán dương Lời ca tụng Thượng Đế của họ và tin tưởng nơi Ngài; và cầu xin sự tha thứ cho những ai có đức tin, thưa: “Lạy Thượng Đế của bầy tôi! Xin Ngài lấy Đức Khoan dung và sự Hiểu biết (của Ngài) mà bao trùm tất cả mọi vật. Xin Ngài tha thứ cho những ai quay về sám hối (với Ngài) và tuân theo con đường của Ngài và cứu vớt họ khỏi hình phạt của hỏa ngục."');
INSERT INTO "hasan_abdulkarim" VALUES(40,8,'40:8','“Lạy Thượng Đế của bầy tôi! Xin Ngài cho họ được vào những ngôi Vườn Vĩnh Cửu (thiên đàng) mà Ngài đã hứa với họ và người đạo đức trong số cha mẹ, vợ chồng và con cái của họ bởi vì Ngài là Đấng Toàn Năng, Đấng Chí Minh."');
INSERT INTO "hasan_abdulkarim" VALUES(40,9,'40:9','“Và bảo vệ khỏi điều xấu xa (tội lỗi). Và ai được Ngài cứu vớt khỏi điều xấu xa vào Ngày (Phán Xử) đó thì chắc chắn sẽ được Ngài khoan dung. Và đó là một thắng lợi vẻ vang nhất.”');
INSERT INTO "hasan_abdulkarim" VALUES(40,10,'40:10','Quả thật, những ai không có đức tin sẽ bị mắng: “Chắc chắn Allah ghét bỏ các ngươi còn hơn việc các ngươi ruồng ghét lẫn nhau khi các ngươi được mời chấp nhận đức tin nhưng các ngươi đã từ chối.”');
INSERT INTO "hasan_abdulkarim" VALUES(40,11,'40:11','Chúng sẽ thưa: “Lạy Thượng Đế của bầy tôi! Ngài đã làm cho bầy tôi chết hai lần( và sống hai lần. Bây giờ bầy tôi thú nhận tội của bầy tôi. Thế có lối thoát nào ra khỏi (hỏa ngục) hay chăng?”');
INSERT INTO "hasan_abdulkarim" VALUES(40,12,'40:12','(Sẽ có lời bảo chúng:) “Như thế là vì khi Allah, Đấng Duy Nhất được khấn vái (trong lời cầu nguyện) thì các người phủ nhận; và nếu có các thần linh nào được cầu nguyện cùng với Ngài (Allah) thì các người tin tưởng. Bởi thế, việc phán xử là của Allah, Đấng Tối Cao, Đấng Vĩ Đại.”');
INSERT INTO "hasan_abdulkarim" VALUES(40,13,'40:13','Ngài là Đấng trưng bày cho các ngươi thấy các dấu hiệu của Ngài và ban bổng lộc (nước mưa) từ trên trời xuống cho các ngươi và chỉ ai biết sám hối mới ghi nhớ.');
INSERT INTO "hasan_abdulkarim" VALUES(40,14,'40:14','Do đó, hãy thành tâm cầu nguyện Allah, triệt để thần phục chỉ riêng Ngài dẫu rằng những kẻ vô đức tin ghét điều đó.');
INSERT INTO "hasan_abdulkarim" VALUES(40,15,'40:15','(Allah) tốii cao về cấp bậc, Chủ nhân của Ngai Vương. Ngài phái Ruh (Jibril) mang Mặc Khải theo Mệnh Lệnh của Ngài xuống gặp người nào mà Ngài muốn trong số bầy tôi của Ngài để người đó cảnh báo nhân loại về Ngày Gặp Gỡ (phục sinh).');
INSERT INTO "hasan_abdulkarim" VALUES(40,16,'40:16','Ngày mà tất cả sẽ phải đến trình diện. Không một điều (tội lỗi) nào của họ có thể giấu giếm khỏi Allah. Quyền hành (Phán Xử) vào Ngày đó sẽ thuộc về ai? Thuộc về Allah, Đấng Duy Nhất, Đấng Tối Thượng.');
INSERT INTO "hasan_abdulkarim" VALUES(40,17,'40:17','Vào Ngày đó, mỗi người (linh hồn) sẽ được tưởng thưởng về công trình của mình. Sẽ không có việc (xét xử) bất công vào Ngày đó. Quả thật, Allah rất nhanh trong việc thanh toán (việc tưởng thưởng và trừng phạt).');
INSERT INTO "hasan_abdulkarim" VALUES(40,18,'40:18','Và hãy cảnh báo họ về Ngày (phục sinh) đang tiến đến gần, Ngày mà những quả tim sẽ nhảy thót lên cổ họng, làm nghẹt thở. Những kẻ làm điều sai quấy sẽ không có bạn bè hoặc người can thiệp nào để nhờ cậy.');
INSERT INTO "hasan_abdulkarim" VALUES(40,19,'40:19','(Allah) biết rõ điều gian xảo nơi cặp mắt của họ và điều (dối trá) giấu kín trong lòng (của họ).');
INSERT INTO "hasan_abdulkarim" VALUES(40,20,'40:20','Và Allah sẽ phán quyết bằng sự Thật, trong lúc những kẻ mà họ khấn vái ngoài Ngài sẽ không quyết định được gì cả. Rõ thật Allah Ngài là Đấng Hằng Nghe, Đấng Hằng Thấy.');
INSERT INTO "hasan_abdulkarim" VALUES(40,21,'40:21','Và há họ không đi du lịch trên trái đất để xét xem kết cuộc của những ai đã sống trước họ ra sao? Chúng mạnh hơn họ về quyền lực và di tích mà chúng còn để lại trên trái đất. Nhưng Allah bắt phạt chúng vì tội lỗi của chúng. Và không có ai che chở chúng khỏi (sự trừng phạt của) Allah.');
INSERT INTO "hasan_abdulkarim" VALUES(40,22,'40:22','Như thế là vì các Sứ Giả của chúng đã đến gặp chúng với những bằng chứng rõ rệt, nhưng chúng không tin tưởng, cho nên Allah bắt phạt chúng. Quả thật, Ngài Đấng Hùng Mạnh và trừng phạt rất nghiêm khắc.');
INSERT INTO "hasan_abdulkarim" VALUES(40,23,'40:23','Và chắc chắn TA đã cử Musa mang các Phép Lạ của TA với một thẩm quyền rõ rệt,');
INSERT INTO "hasan_abdulkarim" VALUES(40,24,'40:24','Đến gặp Fir-''awn, Haman( và Qorun nhưng chúng bảo (Musa) là một tên phù thủy, một tên nói dối.');
INSERT INTO "hasan_abdulkarim" VALUES(40,25,'40:25','Và khi Y (Musa) mang Chân Lý từ TA đến cho chúng, chúng bảo: “Hãy giết các đứa con trai của những ai tin tưởng theo Y (Musa) và tha sống phụ nữ của chúng.” Và mưu đồ của những kẻ không có đức tin chỉ rơi vào lầm lạc.');
INSERT INTO "hasan_abdulkarim" VALUES(40,26,'40:26','Fir-''awn bảo: “Để ta giết Musa, và để nó gọi Thượng Đế của nó (đến cứu nó). Ta sợ nó sẽ thay đổi tôn giáo của các ngươi hoặc sẽ dấy loạn trong xứ.”');
INSERT INTO "hasan_abdulkarim" VALUES(40,27,'40:27','Và Musa bảo: "Tôi cầu xin Thượng Đế của tôi và của quí vị che chở tôi khỏi (tay của) từng tên ngạo mạn không tin tưởng nơi Ngày Thanh Toán (để thưởng và phạt)."');
INSERT INTO "hasan_abdulkarim" VALUES(40,28,'40:28','Và một người đàn ông có đức tin trong đám thuộc hạ của Fir-''awn đã từng giấu kín đức tin của mình vội lên tiếng thưa: “Ngài sẽ giết một người chỉ vì lý do y nói: ''Thượng Đế của tôi là Allah và chắc chắn đã mang những bằng chứng rõ rệt từ Thượng Đế của quí ngài đến cho quí ngài hay sao? Và nếu là một tên nói dối thì y sẽ chịu tội về lời gian dối của y. Ngược lại, nếu y là một người nói sự thật thì điều mà y hăm dọa quí ngài chắc chắn sẽ nhằm phải quí ngài. Thật sự, Allah không hướng dẫn một người phạm tội (giết người, áp bức...), một tên nói dối."');
INSERT INTO "hasan_abdulkarim" VALUES(40,29,'40:29','"Thưa quí ngài! Ngày nay quyền hành nằm trong tay của quí ngài. Quí ngài thắng thế trong xứ. Nhưng ai sẽ cứu chúng ta khỏi hình phạt của Allah nếu nó nhằm phải chúng ta?” Fir-''awn đáp: "Ta chỉ trình bày cho các người thấy điều mà ta thấy (đúng). Và ta chỉ hướng dẫn các người theo con đường chân chính.”');
INSERT INTO "hasan_abdulkarim" VALUES(40,30,'40:30','Và người có đức tin đó thưa: “Thưa quí ngài! Thật sự, tôi sợ giùm cho quí ngài về (đại họa) giống như (cái đã xảy ra vào) Ngày của Liên quân (tòng phạm)!"');
INSERT INTO "hasan_abdulkarim" VALUES(40,31,'40:31','“Giống như số phận của người dân của Nuh, của ''Ad và của Thamud và của những kẻ sống sau chúng bởi vì Allah không muốn bầy tôi của Ngài chịu thiệt thòi.');
INSERT INTO "hasan_abdulkarim" VALUES(40,32,'40:32','“Và thưa quí ngài! Tôi sợ giùm cho quí ngài về Ngày với gọi nhau (đau đớn).');
INSERT INTO "hasan_abdulkarim" VALUES(40,33,'40:33','“Ngày mà quí ngài sẽ quay lưng tháo chạy. Quí vị sẽ không có một vị che chở nào từ Allah. Và ai mà Allah đánh lạc hướng thì sẽ không được một người hướng đạo nào cả."');
INSERT INTO "hasan_abdulkarim" VALUES(40,34,'40:34','“Và chắc chắn khi xưa Yusuf (Joseph) mang các bằng chứng rõ rệt đến gặp quí ngài nhưng quí ngài không ngừng nghi ngờ về thông điệp mà Y mang đến; mãi cho đến khi Y qua đời thì quí ngài bảo: “Allah sẽ không bao giờ dựng một Sứ Giả nào sau Y nữa.” Allah đánh lạc hướng kẻ nào là một người áp bức, tội lỗi và hay nghi ngờ giống như thế.');
INSERT INTO "hasan_abdulkarim" VALUES(40,35,'40:35','“Những ai hay tranh cãi về các dấu hiệu của Allah nhưng không có một thẩm quyền (hay bằng chứng) nào thì thật đáng ghét đối với Allah và đối với những người có đức tin. Allah niêm kín tấm lòng của từng tên ngạo mạn, bạo ngược giống như thế.”');
INSERT INTO "hasan_abdulkarim" VALUES(40,36,'40:36','Và Fir-''awn bảo: “Hỡi Haman! Hãy xây cho ta một cái tháp cao để ta có thể đến giáp những con đường."');
INSERT INTO "hasan_abdulkarim" VALUES(40,37,'40:37','“Những con đường của các tầng trời đưa ta lên diện kiến Thượng Đế của Musa bởi vì ta nghĩ nó là một tên nói dối.” Và đối với Fir-''awn, hành động tội lỗi của y đã tỏ ra hấp dẫn đối với y giống như thế và y bị cản trở khỏi con đường (của Allah). Và mưu đồ của Fir-''awn chỉ đưa đến thất bại.');
INSERT INTO "hasan_abdulkarim" VALUES(40,38,'40:38','Người tin tưởng (đó) tiếp: “Hỡi người dân của tôi! Hãy nghe lời tôi. Tôi sẽ hướng dẫn quí vị theo con đường chân chính.');
INSERT INTO "hasan_abdulkarim" VALUES(40,39,'40:39','“Hỡi người dân của tôi! Đời sống trần tục này chỉ là một sự hưởng thụ ngắn ngủi và chắc chắn đời sau mới là một ngôi nhà vĩnh cửu.');
INSERT INTO "hasan_abdulkarim" VALUES(40,40,'40:40','“Ai làm điều xấu thì chỉ bị phạt ngang bằng với tội đã phạm. Ngược lại, ai làm điều thiện, bất luận nam hay nữ, và có đức tin thì sẽ vào thiên đàng, nơi mà họ sẽ hưởng vô vàn bổng lộc.');
INSERT INTO "hasan_abdulkarim" VALUES(40,41,'40:41','“Hỡi người dân của tôi! Tại sao lại có chuyện (ngược ngạo) như thế trong lúc tôi kêu gọi quí vị đến chỗ Cứu Rỗi thì quí vị lại kêu gọi tôi đi vào hỏa ngục?"');
INSERT INTO "hasan_abdulkarim" VALUES(40,42,'40:42','“Quí vị kêu gọi tôi phủ nhận Allah và tổ hợp thần linh cùng với Ngài điều mà tôi không biết một tí gì trong lúc tôi kêu gọi quí vị đến với Đấng Toàn Năng, Đấng Hằng Tha Thứ.');
INSERT INTO "hasan_abdulkarim" VALUES(40,43,'40:43','“Không có gì phải nghi ngờ cả, quí vị kêu gọi tôi đến với một kẻ không có quyền đòi thiên hạ phải cầu nguyện mình ỡ đời này và ở đời sau; và quả thật, mục tiêu trở về của tất cả chúng ta là Allah; và quả thật những kẻ tội lỗi sẽ là những người bạn của hỏa ngục.');
INSERT INTO "hasan_abdulkarim" VALUES(40,44,'40:44','“Rồi sau này quí vị sẽ nhớ những điều tôi nói. Tôi giao công việc của tôi cho Allah bởi vì Allah là Đấng Hằng Thấy bầy tôi (của Ngài).”');
INSERT INTO "hasan_abdulkarim" VALUES(40,45,'40:45','Do đó, Allah giải cứu y (người có đức tin) khỏi những điều xấu xa mà chúng âm mưu hại y; và tai họa bao vây người nhà (thuộc hạ) của Fir-''awn tứ phía.');
INSERT INTO "hasan_abdulkarim" VALUES(40,46,'40:46','hỏa ngục mà chúng sẽ bị dắt ra chạm trán cả buổi sáng lẫn buổi chiều. Và vào Ngày mà Giờ (Xét Xử) sẽ được thiết lập, sẽ có lời (bảo Thiên Thần): “Hãy đưa thuộc hạ của Fir-''awn vào chỗ trừng phạt khắc nghiệt nhất.”');
INSERT INTO "hasan_abdulkarim" VALUES(40,47,'40:47','Và khi chúng sẽ cãi nhau trong hỏa ngục; người cô thế sẽ bảo kẻ ngạo mạn như sau: “Quả thật, chúng tôi là người theo quí ngài. Có thể nào quí ngài bốc một phần Lửa đốt khỏi chúng tôi được hay không?”');
INSERT INTO "hasan_abdulkarim" VALUES(40,48,'40:48','Những kẻ ngạo mạn sẽ đáp: “Tất cả chúng ta đều ở trong dó (hỏa ngục) cả! Chắc chắn, Allah sẽ phân xử giữa bầy tôi của Ngài.”');
INSERT INTO "hasan_abdulkarim" VALUES(40,49,'40:49','Và những kẻ ở trong hỏa ngục sẽ nói với những vị Cai ngục (Thiên Thần): “Hãy xin Thượng Đế của quí ngài giảm bớt hình phạt giùm chúng tôi một ngày được hay không?”');
INSERT INTO "hasan_abdulkarim" VALUES(40,50,'40:50','Họ sẽ đáp: “Há các Sứ Giả của các người đã không đến gặp các ngươi với những bằng chứng rõ rệt ư?” Chúng sẽ thưa: “Dạ, có!” Họ bảo chúng: “Thế hãy cầu nguyện (theo ý muốn của các ngươi).” Nhưng lời cầu nguyện của những kẻ không có đức tin chỉ quanh quẩn trong lầm lạc.');
INSERT INTO "hasan_abdulkarim" VALUES(40,51,'40:51','Chắc chắn, TA sẽ giúp các Sứ Giả của TA và những ai có đức tin ở đời này chiến thắng; và vào Ngày mà các nhân chúng sẽ đứng ra (làm chứng);');
INSERT INTO "hasan_abdulkarim" VALUES(40,52,'40:52','Ngày mà lời lẽ phân trần của những kẻ làm điều sai quấy sẽ không giúp ích gì được cho chúng. Ngược lại, chúng sẽ nhận lời nguyền rủa và một chỗ ngụ xấu xa.');
INSERT INTO "hasan_abdulkarim" VALUES(40,53,'40:53','Và quả thật, TA đã ban Chỉ Đạo cho Musa và làm cho con cháu của Israel thừa hưởng Kinh Sách (Tawrah).');
INSERT INTO "hasan_abdulkarim" VALUES(40,54,'40:54','Một Chỉ Đạo và một sự Nhắc Nhở cho những người thông hiểu.');
INSERT INTO "hasan_abdulkarim" VALUES(40,55,'40:55','Bởi thế, hãy kiên nhẫn (hỡi Muhammad!) bởi vì Lời Hứa của Allah sẽ đích thực. Và hãy xin Thượng Đế của Ngươi tha thứ lỗi lầm của Ngươi và hãy tán dương ca tụng Thượng Đế của Ngươi đêm và ngày.');
INSERT INTO "hasan_abdulkarim" VALUES(40,56,'40:56','Quả thật, những ai tranh cãi về các dấu hiệu của Allah nhưng không có một chút thẩm quyền nào được ban cho thì chỉ muốn sự tự cao tự đại trong lòng mình, nhưng chúng sẽ không bao giờ đạt nó được. Bởi thế, hãy xin Allah che chơ bởi vì Ngài là Đấng Hằng Nghe và Hằng Thấy (mọi việc).');
INSERT INTO "hasan_abdulkarim" VALUES(40,57,'40:57','Chắc chắn việc tạo hóa các tầng trời và trái đất lớn hơn việc tạo hóa loài người, nhưng đa số nhân loại không biết.');
INSERT INTO "hasan_abdulkarim" VALUES(40,58,'40:58','Người mù mắt và người sáng mắt không ngang bằng nhau; và những ai có đức tin và làm việc thiện không ngang bằng với người làm điều xấu và tội lỗi. Các người ít nhớ thay!');
INSERT INTO "hasan_abdulkarim" VALUES(40,59,'40:59','Quả thật, Giờ Tận Thế chắc chắn sẽ đến, không có chi phải nghi ngờ cả; nhưng đa số thiên hạ không tin tưởng.');
INSERT INTO "hasan_abdulkarim" VALUES(40,60,'40:60','Và Thượng Đế của các ngươi phán: “Hãy cầu nguyện TA, TA sẽ đáp lại các ngươi. Thật sự, những ai kiêu ngạo xem thường việc thờ phụng TA thì sẽ đi vào hỏa ngục một cách nhục nhã.”');
INSERT INTO "hasan_abdulkarim" VALUES(40,61,'40:61','Allah là Đấng đã làm ra ban đêm cho các ngươi để các ngươi có thể nghỉ ngơi và ban ngày để nhìn thấy. Và quả thật, Allah có đầy đủ thiên lộc cho loài người nhưng đa số thiên hạ không biết ơn.');
INSERT INTO "hasan_abdulkarim" VALUES(40,62,'40:62','Allah, Thượng Đế của các ngươi là thế; Đấng Tạo Hóa vạn vật. Không có Thượng Đế nào cả, duy chỉ Ngài (là Thượng Đế). Thế tại sao các ngươi lánh xa (Ngài)?');
INSERT INTO "hasan_abdulkarim" VALUES(40,63,'40:63','Những ai phủ nhận các dấu hiệu của Allah thường tránh né như thế.');
INSERT INTO "hasan_abdulkarim" VALUES(40,64,'40:64','Allah là Đấng làm trái đất thành một nơi cư ngụ cho các ngươi và bầu trời thành cái lọng (mái) che và ban cho các ngươi hình thể và tô điểm hình thể của các ngươi thành xinh đẹp và cung dưỡng các ngươi với thực phẩm tốt và sạch. Allah, Thượng Đế của các ngươi là thế. Bởi thế, Phúc đức thay Allah! Thượng Đế của vũ trụ và muôn loài.');
INSERT INTO "hasan_abdulkarim" VALUES(40,65,'40:65','Ngài là Đấng Hằng sống. Không có Thượng Đế nào khác cả, duy chỉ Ngài (là Thượng Đế). Do đó, hãy cầu nguyện Ngài, chân thành tôn thờ chỉ riêng Ngài. Mọi lời ca tụng đều thuộc về Allah, Thượng Đế (Đấng Chủ Tể) của vũ trụ và muôn loài.');
INSERT INTO "hasan_abdulkarim" VALUES(40,66,'40:66','Hãy bảo (chúng): “(Allah) cấm Ta không được tôn thờ những ai mà các người cầu nguyện ngoài Ngài khi Ta đã nhận được những bằng chứng rõ rệt từ Thượng Đế của Ta. Và Ta được lệnh phải thần phục Thượng Đế (Đấng Chủ Tể) của vũ trụ và muôn loài.');
INSERT INTO "hasan_abdulkarim" VALUES(40,67,'40:67','Ngài là Đấng đã tạo hóa các ngươi từ đất bụi, rồi từ giọt tinh dịch, rồi từ một hòn máu đặc, rồi mang các ngươi ra đời thành các đứa bé, rồi làm cho các ngươi lớn mạnh đầy đủ sức lực, rồi làm cho các ngươi trở thành già yếu. Và trong các ngươi, có người chết trước (chết non); và (có người) được để cho sống đến thời hạn qui định và để cho các ngươi thông hiểu.');
INSERT INTO "hasan_abdulkarim" VALUES(40,68,'40:68','Ngài (Allah) là Đấng làm cho sống và làm cho chết. Bởi thế, khi Ngài quyết định một vật (việc), Ngài chỉ phán với nó “Hãy Thành!” thì nó sẽ thành (như Ý Ngài muốn).');
INSERT INTO "hasan_abdulkarim" VALUES(40,69,'40:69','Há Ngươi (Muhammad) không nhận thấy những kẻ đã tranh cãi về các dấu hiệu của Allah rằng chúng xa lánh (Chân Lý) như thế nào ư?');
INSERT INTO "hasan_abdulkarim" VALUES(40,70,'40:70','Những ai đã phủ nhận Kinh Sách (Qur''an) và những điều (mặc khải) mà TA đã ban xuống cùng với các Sứ Giả của TA. Rồi đây, chúng sẽ sớm biết (hậu quả của việc làm của chúng).');
INSERT INTO "hasan_abdulkarim" VALUES(40,71,'40:71','Khi những chiếc gông sẽ được đóng vào cổ của chúng và những sợi xích mà chúng sẽ bị kéo lôi đi.');
INSERT INTO "hasan_abdulkarim" VALUES(40,72,'40:72','Vào trong loại nước sôi hôi thúi; rồi vào trong Lửa, nơi mà chúng sẽ bị đốt.');
INSERT INTO "hasan_abdulkarim" VALUES(40,73,'40:73','Rồi sẽ có lời bảo chúng: “Đâu là những thần linh mà các ngươi tôn thờ');
INSERT INTO "hasan_abdulkarim" VALUES(40,74,'40:74','Ngoài Allah?” Chúng sẽ thưa: “Họ đã bỏ chúng tôi đi mất biệt. Trước đây chúng tôi đã không cầu nguyện và tôn thờ ai (vật gì).” Allah đã đánh lạc hướng những kẻ không có đức tin như thế.');
INSERT INTO "hasan_abdulkarim" VALUES(40,75,'40:75','Như thế là vì các ngươi đã thỏa thích với những điều không đúng với sự Thật và các ngươi thường hí hửng trên trái đất.');
INSERT INTO "hasan_abdulkarim" VALUES(40,76,'40:76','Hãy bước vào các cánh cửa hỏa ngục để ở trong đó. Thật xấu xa thay chỗ ngụ của những kệ ngạo mạn!');
INSERT INTO "hasan_abdulkarim" VALUES(40,77,'40:77','Bởi thế hãy kiên nhẫn (hỡi Muhammad!). Quả thật, Lời Hứa của Allah sẽ là sự thật. Bởi thế, dẫu TA có cho Ngươi thấy một phần của những điều mà TA đã hứa với chúng hoặc TA có bắt hồn của Ngươi (về cõi Ân Phúc) trước thì bề nào chúng vẫn phải trở về gặp TA (để chịu xét xử).');
INSERT INTO "hasan_abdulkarim" VALUES(40,78,'40:78','Và quả thật, TA đã cử các Sứ Giả đến (với nhân loại) trước Ngươi. Trong số đó có người TA đã kể cho Ngươi câu chuyện của họ và có người TA không kể câu chuyện của họ cho Ngươi. Và không một Sứ Giả nào được quyền mang một dấu hiệu đến nếu không có Phép của Allah. Và khi Mệnh Lệnh của Allah đã được ban hành, vấn đề sẽ được quyết định đúng với sự thật. Và những kẻ tuân theo điều giả dối lúc đó sẽ hoàn toàn bị thất bại.');
INSERT INTO "hasan_abdulkarim" VALUES(40,79,'40:79','Allah là Đấng làm ra gia súc cho các ngươi. Một số các ngươi dùng để cưỡi và một số các người dùng để ăn thịt.');
INSERT INTO "hasan_abdulkarim" VALUES(40,80,'40:80','Và nơi chúng các ngươi còn được một số lợi ích khác. Và nhờ chúng các ngươi có thể đạt được một ước vọng trong lòng. Và các ngươi được chuyên chở trên (lưng) của chúng và trên (boong) tàu.');
INSERT INTO "hasan_abdulkarim" VALUES(40,81,'40:81','Và Ngài trưng cho các ngươi thấy các dấu hiệu của Ngài. Thế đâu là các dấu hiệu của Allah mà các ngươi phủ nhận?');
INSERT INTO "hasan_abdulkarim" VALUES(40,82,'40:82','Há họ không đi du lịch trên trái đất để nhận xét kết cuộc của những ai đã sống trước họ ra sao ư? Chúng đông người và mạnh hơn họ và những di tích (lịch sử) mà chúng đã để lại trên trái đất. Tuy nhiên, những công trình mà họ đã làm ra chẳng giúp ích gì được cho họ cả.');
INSERT INTO "hasan_abdulkarim" VALUES(40,83,'40:83','Bởi vì khi những Sứ Giả của họ đến gặp họ với những bằng chứng rõ rệt (để răn họ), thì họ ỷ lại và tự hào về kiến thức (và tài năng) họ đang có, và những điều mà họ thường chế giễu sẽ bao vây họ trong đó.');
INSERT INTO "hasan_abdulkarim" VALUES(40,84,'40:84','Bởi thế, khi thấy sự trừng phạt của TA, họ thưa: “Chúng tôi tin tưởng nơi Đấng Allah Duy Nhất; và phủ nhận những đối tác mà chúng tôi đã tổ hợp với Ngài.”');
INSERT INTO "hasan_abdulkarim" VALUES(40,85,'40:85','Nhưng đức tin của họ vào lúc thấy hình phạt của TA chẳng giúp ích gì được cho họ cả. Đó là Sunnah (Đường lối) của Allah mà Ngài dùng để đối xử với bầy tôi của Ngài (từ thuở nào). Và những kẻ không có đức tin cuối cùng nhất định sẽ mất mát.');
INSERT INTO "hasan_abdulkarim" VALUES(41,1,'41:1','Ha. Mim.');
INSERT INTO "hasan_abdulkarim" VALUES(41,2,'41:2','Việc ban (Qur''an) xuống là do Đấng Rất Mực Độ Lượng, Đấng Rất Mực Khoan Dung.');
INSERT INTO "hasan_abdulkarim" VALUES(41,3,'41:3','Một Kinh Sách với các câu được giả thích chi tiết; một Qur''an bằng tiếng Ả Rập ban cho những người hiểu biết.');
INSERT INTO "hasan_abdulkarim" VALUES(41,4,'41:4','(Mang) Tin mừng và Lời cảnh báo; nhưng đa số bọn họ quay bỏ đi, cho nên họ không nghe được (điều lành và điều dữ).');
INSERT INTO "hasan_abdulkarim" VALUES(41,5,'41:5','Họ bảo: “Quả tim của bọn ta được bọc kín tránh khỏi những điều mà Ngươi mời bọn ta chấp nhận, tai của bọn ta điếc và giữa bọn ta và Ngươi có một bức màn ngăn cách. Bởi thế, hãy làm (theo đường lối của Ngươi); quả thật, bọn ta làm (theo đường lối của bọn ta)."');
INSERT INTO "hasan_abdulkarim" VALUES(41,6,'41:6','Hãy bảo họ: “Ta (Muhammad) là một người phàm như các người, nhưng ta được mặc khải cho biết Thượng Đế của các người chỉ có một Thượng Đế. Do đó, hãy theo con đường ngay chính dẫn đến Ngài và xin Ngài tha thứ; và khốn khổ cho những người thờ đa thần."');
INSERT INTO "hasan_abdulkarim" VALUES(41,7,'41:7','"Những ai không đóng Zakah và không tin có đời sau."');
INSERT INTO "hasan_abdulkarim" VALUES(41,8,'41:8','"Quả thật, những ai có đức tin và làm việc thiện thì sẽ được ban thưởng không dứt."');
INSERT INTO "hasan_abdulkarim" VALUES(41,9,'41:9','Hãy bảo họ (hỡi Muhammad): “Phải chăng các người phủ nhận Đấng đã tạo hóa trái đất trong hai Ngày và các người dựng những đối thủ ngang vai với Ngài? Đấy là Thượng Đế của vũ trụ và muôn loài."');
INSERT INTO "hasan_abdulkarim" VALUES(41,10,'41:10','Và Ngài (Allah) đã đặt nơi đó những quả núi vững chắc cao hơn mặt đất và ban phúc nơi đó và tính toán lương thực (cho dân cư) của nó nơi đó trong bốn Ngày, đồng đều cho những người đòi hỏi.');
INSERT INTO "hasan_abdulkarim" VALUES(41,11,'41:11','Rồi Ngài hướng về bầu trời lúc đó là một lớp khói mờ. Tiếp đó, Ngài phán cho nó và cho trái đất: “Hai ngươi hãy đến cùng nhau, thuận hay không thuận?” cả hai cùng thưa: “Chúng tôi thuận đến với nhau.”');
INSERT INTO "hasan_abdulkarim" VALUES(41,12,'41:12','Rồi Ngài hoàn tất chúng thành bẩy tầng trời trong hai Ngày và Ngài mặc khải cho mỗi tầng trời công việc của nó. Và TA đã trang hoàng tầng trời của thế gian (tầng trời hạ giới) với những ngọn đèn (tinh tú) và (cử Thiên Thần) canh giữ nó. Đó là Quyết định của Đấng Toàn Năng, Đấng Toàn Tri.');
INSERT INTO "hasan_abdulkarim" VALUES(41,13,'41:13','Nhưng nếu họ quay bỏ đi thì hãy bảo (họ): “Ta cảnh báo các người về lưỡi tầm sét giống như lưỡi tầm sét đã đánh người dân ''Ad và Thamud."');
INSERT INTO "hasan_abdulkarim" VALUES(41,14,'41:14','Khi các Sứ Giả (của Allah) đã đến gặp chúng từ phía trước lẫn phía sau, (bảo:) “Hãy thờ phụng chỉ riêng Allah thôi.” Chúng đáp: “Nếu muốn, Thượng Đế chúng tôi sẽ cử Thiên Thần xuống (làm Sứ Giả). Bởi thế, chúng tôi không tin nơi điều mà quí vị mang đến .”');
INSERT INTO "hasan_abdulkarim" VALUES(41,15,'41:15','Về (người dân) ''Ad, chúng đã cư xử ngạo mạn trên trái đất không lý do chính đáng. Chúng bảo: “Ai mạnh hơn bọn ta về sức mạnh?” Há chúng không nhận thấy rằng Allah, Đấng đã tạo hóa chúng, đã mạnh hơn chúng về sức mạnh hay sao? Và chúng thường phủ nhận các dấu hiệu của TA.');
INSERT INTO "hasan_abdulkarim" VALUES(41,16,'41:16','Bởi thế, TA đã gởi một trận cuồng phong đến phạt chúng suốt những ngày bất hạnh để TA cho chúng nếm sự trừng phạt nhục nhã ở đời này; nhưng sự trừng phạt ở đời sau còn nhục nhã hơn; và chúng sẽ không được ai cứu giúp.');
INSERT INTO "hasan_abdulkarim" VALUES(41,17,'41:17','Còn về (người dân) Thamud. TA đã hướng dẫn chúng nhưng chúng thích sự mù quáng (của con tim) hơn là Chỉ Đạo sáng sủa. Bởi thế, lưỡi tầm sét của sự trừng phạt nhục nhã đã đánh chúng vì những điều (tội lỗi) mà chúng đã làm ra.');
INSERT INTO "hasan_abdulkarim" VALUES(41,18,'41:18','Và TA đã cứu sống những ai có đức tin và sợ Allah.');
INSERT INTO "hasan_abdulkarim" VALUES(41,19,'41:19','Và vào Ngày mà những kẻ thù của Allah sẽ được tập trung lùa vào hỏa ngục; bởi thế, chúng sẽ được tập hợp theo từng nhóm.');
INSERT INTO "hasan_abdulkarim" VALUES(41,20,'41:20','Mãi cho đến khi chúng đến đó (hỏa ngục), đôi tai, cặp mắt và các làn da của chúng sẽ làm chứng chống lại chúng về những điều (tội lỗi) mà chúng đã từng làm.');
INSERT INTO "hasan_abdulkarim" VALUES(41,21,'41:21','Và chúng sẽ bảo các lớp da của chúng: “Tại sao tụi bây làm chứng chống lại bọn ta?” (Các lớp da sẽ) đáp: “Allah khiến chúng tôi nói giống như Ngài khiến cho mọi vật đều nói và Ngài đã tạo ra quí vị lần đầu và quí vị sẽ được đưa về gặp Ngài trở lại."');
INSERT INTO "hasan_abdulkarim" VALUES(41,22,'41:22','“Quí vị không chịu tìm cách che giấu mình của quí vị bởi vì đôi tai, cặp mất và các làn da của quí vị có thể làm chứng chống lại quí vị; nhưng quí vị nghĩ rằng Allah không mấy biết về những điều mà quí vị đã từng làm."');
INSERT INTO "hasan_abdulkarim" VALUES(41,23,'41:23','“Và ý nghĩ đó của quí vị mà quí vị đã nghĩ về Thượng Đế của quí vị đã tiêu diệt quí vị; bởi thế, ngày đó, quí vị sẽ trở thành những kẻ hoàn toàn mất mát.”');
INSERT INTO "hasan_abdulkarim" VALUES(41,24,'41:24','Nhưng nếu chúng có kiên nhẫn chịu đựng thì hỏa ngục vẫn là chỗ ngụ của chúng. Và nếu chúng có xin được tha thứ thì lời cầu xin của chúng cũng sẽ không được chấp thuận.');
INSERT INTO "hasan_abdulkarim" VALUES(41,25,'41:25','Và TA đã chỉ định cho chúng những người bạn đường (tương đắc) có nhiệm vụ làm cho chúng say đắm với những điều (tội lỗi) đằng trước chúng và những điều (sai trái) đằng sau chúng. Và Lời (xử) phạt bọn chúng đều xác minh đúng nơi các cộng đồng của loài Jinn và loài người đã qua đời trước bọn chúng. Quả thật, chúng là những kẻ mất mát.');
INSERT INTO "hasan_abdulkarim" VALUES(41,26,'41:26','Và những kẻ không có tin bảo: “Chớ nphe Qur''an này và gây náo động ồn ào (khi nó được xướng lên) để may ra các ngươi nắm ưu thế.”');
INSERT INTO "hasan_abdulkarim" VALUES(41,27,'41:27','Nhưng chắc chắn TA sẽ cho những kẻ không có đức tin nếm sự trừng phạt khắc nghiệt và sẽ trừng phạt chúng vô cùng tàn tệ về những điều (tội lỗi) chúng đã từng làm.');
INSERT INTO "hasan_abdulkarim" VALUES(41,28,'41:28','Đấy là quả báo dành cho những kẻ thù của Allah: trong đó (hỏa ngục) chúng sẽ có một mái nhà vĩnh viễn, một quả báo cho tội chúng đã từng phủ nhận các dấu hiệu của TA.');
INSERT INTO "hasan_abdulkarim" VALUES(41,29,'41:29','Và những kẻ không có đức tin sẽ thưa: “Lạy Thượng Đế bầy tôi! Xin Ngài vạch mặt những tên Jinn và lủ người đã dắt bầy tôi đi lạc để bầy tôi chà đạp hai chúng nó dưới bàn chân của bầy tôi khiến cho chúng thành những tên hèn hạ nhất.”');
INSERT INTO "hasan_abdulkarim" VALUES(41,30,'41:30','Quả thật, những ai nói: “Thượng Đế của chúng tôi là Allah.” rồi đứng vững như bàn thạch, các Thiên Thần sẽ xuống động viên họ, bảo: “Chớ lo sợ cũng chớ buồn phiền mà hãy vui lên với tin mừng về thiên đàng (hạnh phúc) mà quí vị đã được hứa!"');
INSERT INTO "hasan_abdulkarim" VALUES(41,31,'41:31','“Chúng tôi là bạn của quí vị ở đời này và ở đời sau. Và nơi đó, quí vị sẽ được bất cứ món gì mà bản thân của quí vị ước muốn và nơi đó quí vị sẽ được ban cấp bất cứ món gì mà quí vị yêu cầu."');
INSERT INTO "hasan_abdulkarim" VALUES(41,32,'41:32','“Một sự khoan đãi từ Đấng Hằng Tha Thứ, Đấng Rất Mực Khoan Dung.”');
INSERT INTO "hasan_abdulkarim" VALUES(41,33,'41:33','Và còn ai tử tế và tốt về lời nói hơn người mời gọi (nhân loại) đến với Allah và làm việc thiện và bảo: tôi đây là một người Muslim.”');
INSERT INTO "hasan_abdulkarim" VALUES(41,34,'41:34','Và điều thiện tốt và điều xấu, tội lỗi không thể ngang bằng nhau. Hãy dùng điều thiện tốt mà đẩy lùi điều xấu và tội lỗi. Rồi (Ngươi sẽ thấy) kẻ ôm lòng hận thù Ngươi sẽ trở thành người bạn thân tình của Ngươi.');
INSERT INTO "hasan_abdulkarim" VALUES(41,35,'41:35','Và không ai được phú cho cái đó (lòng tốt) ngoại trừ những người thật sự kiên nhẫn và không ai được phú cho cái đó, ngoại trừ người có phần số lớn (được vào thiên đàng).');
INSERT INTO "hasan_abdulkarim" VALUES(41,36,'41:36','Và nếu có lời từ Shaytan xúi giục Ngươi (làm điều sai trái) thì hãy xin Allah che chở bởi vì quả thật Ngài Hằng Nghe, Hằng Biết mọi việc.');
INSERT INTO "hasan_abdulkarim" VALUES(41,37,'41:37','Và ban đêm và ban ngày, và mặt trời và mặt trăng là những dấu hiệu chỉ dẫn của Ngài (Allah). Chớ lạy mặt trời và mặt trăng mà hãy quỳ lạy Allah, Đấng đã tạo ra chúng nếu các ngươi tôn thờ chỉ riêng Ngài thôi.');
INSERT INTO "hasan_abdulkarim" VALUES(41,38,'41:38','Nhưng nếu chúng ngạo mạn thì những ai (Thiên Thần) ở cùng với Thượng Đế của Ngươi hằng tán dương Ngài cả đêm lẫn ngày và không bao giờ mỏi mệt.');
INSERT INTO "hasan_abdulkarim" VALUES(41,39,'41:39','Và trong các dấu hiệu của Ngài (có cái này): rằng đất đai hạ mình khiêm tốn (khô nứt). Nhưng khi TA tưới nước mưa lên nó, nó cựa mình (sống lại) và cho mọc ra (hoa màu). Quả thật, Đấng làm cho nó sống lại chắc chắn sẽ làm cho người chết sống lại (vào Ngày phục sinh). Quả thật, Ngài thừa khả năng làm được tất cả mọi vật.');
INSERT INTO "hasan_abdulkarim" VALUES(41,40,'41:40','Quả thật, những ai báng bổ các dấu hiệu của TA thì sẽ không trốn khỏi TA đặng. Thế ai tốt hơn? Kẻ bị ném vào hỏa ngục hay là người bước ra an toàn vào Ngày phục sinh? (Hỡi những kẻ không có đức tin!) hãy làm những điều mà các ngươi muốn bởi vì chắc chắn Ngài (Allah) Hằng Thấy những điều các người làm.');
INSERT INTO "hasan_abdulkarim" VALUES(41,41,'41:41','Quả thật, những ai phủ nhận Lời Nhắc Nhở (Qur''an) khi Nó đến với họ (thì sẽ bị trừng phạt). Và quả thật, Nó là một Kinh Sách rất đỗi quyền năng.');
INSERT INTO "hasan_abdulkarim" VALUES(41,42,'41:42','Không một điều giả dối nào có thể xâm nhập Nó (Qur''an) từ đằng trước hay đằng sau. Bởi vì Nó do Đấng (Allah) Rất Mực Sáng Suốt, Rất Đáng Ca Tụng ban xuống.');
INSERT INTO "hasan_abdulkarim" VALUES(41,43,'41:43','Những điều mà chúng nói cho Ngươi chẳng khác với điều đã được nói cho các Sứ Giả trước Ngươi. Quả thật, Thượng Đế của Ngươi nắm trọn sự tha thứ và sự trừng phạt đau đớn.');
INSERT INTO "hasan_abdulkarim" VALUES(41,44,'41:44','Nếu TA làm Qur''an bằng một thứ tiếng không phải ngôn ngữ Ả Rập thì (nhũng người Ả Rập ngoại đạo) sẽ bảo: “Tại sao các câu kinh của nó không được giải thích chi tiết (theo ngôn ngữ của chúng tôi?)” Sao! một (Kinh Sách bằng) ngôn ngữ khác (tiếng Ả Rập) và một (Sứ Giả) người Ả Rập ư? Hãy bảo chúng: “Nó là một Chỉ Đạo và là một phương thuốc chữa lành bệnh cho những ai có đức tin. Ngược lại, những ai không có đức tin thì tai của chúng điếc và Nó (Qur''an) là một sự mù lòa đối với chúng. Chúng là những người được gọi từ một chỗ rất xa.');
INSERT INTO "hasan_abdulkarim" VALUES(41,45,'41:45','Và quả thật TA đã ban cho Musa Kinh Sách nhưng lại có sự tranh cãi trong đó. Nếu không vì một Lời Phán từ Thượng Đế của Ngươi đã được phán ra trước đây thì việc tranh cãi giữa bọn chúng đã được giải quyết xong. Nhưng chúng lại rất đỗi nghi ngờ về Nó.');
INSERT INTO "hasan_abdulkarim" VALUES(41,46,'41:46','Ai làm điều thiện thì được lợi cho bản thần mình. Còn ai làm điều xấu và tội lỗi thì bất lợi cho bản thân y. Bởi vì quả thật Thượng Đế của Ngươi không bao giờ bất công với bầy tôi (của Ngài).');
INSERT INTO "hasan_abdulkarim" VALUES(41,47,'41:47','Sự hiểu biết về Giờ Tận Thế chỉ được qui riêng cho Ngài (Allah) thôi. Và không một trái cây nào lú ra khỏi vỏ của nó, cũng không một người phụ nữ mang thai và hạ sanh (đứa con) nào mà Ngài không biết. Và vào Ngày mà Ngài (Allah) sẽ gọi chúng đến hỏi: “Những thần linh (mà các ngươi cho là những đối tác) của TA hiện ở đâu?” Chúng sẽ thưa: “Bầy tôi xin bảo đảm với Ngài không một ai trong bầy tôi làm chứng (cho việc Ngài có những đối tác ngang vai).”');
INSERT INTO "hasan_abdulkarim" VALUES(41,48,'41:48','Và những kẻ (thần linh) mà chúng đã từng khấn vái trước kia sẽ bỏ chúng đi biệt dạng và chúng sẽ thấy mình không thể trốn thoát khỏi (sự trừng phạt của Allah).');
INSERT INTO "hasan_abdulkarim" VALUES(41,49,'41:49','Con người (vô đức tin) không cảm thấy mỏi mệt trong việc cầu nguyện điều tốt lành (cho bản thân mình). Nhưng nếu gặp phải điều xấu xa (hoạn nạn) thì y đâm buông xuôi, tuyệt vọng.');
INSERT INTO "hasan_abdulkarim" VALUES(41,50,'41:50','Và chắc chắn nếu TA cho y nếm mùi Khoan Dung từ TA sau khi y gặp hoạn nạn thì chắc chắn y sẽ nói: “Điều này do bởi tôi (làm ra). Và tôi nghĩ sẽ không có Giờ Tận Thế gì cả; và nếu tôi có được đưa trở về gặp Thượng Đế của tôi lại thì chắc chắn tôi sẽ có được vô vàn cái tốt (phú quí giàu sang) nơi Ngài.” Nhưng chắc chắn TA sẽ cho những kẻ không có đức tin biết về những điều (tội lỗi) mà chúng đã làm và sẽ cho chúng nếm sự trừng phạt to lớn.');
INSERT INTO "hasan_abdulkarim" VALUES(41,51,'41:51','Và khi TA ban ân huệ cho con người, y trở mặt và dang ra xa (thay vì đến gần TA) nhưng khi gặp hoạn nạn thì y sẽ cầu nguyện (TA) rất lâu.');
INSERT INTO "hasan_abdulkarim" VALUES(41,52,'41:52','Hãy bảo chúng: “Các người có suy xét chăng nếu Nó (Qurn) thật sự do Allah ban xuống, rồi các người phủ nhận Nó thì ai là người lầm lạc hơn kẻ chống đối Nó từ xa?”');
INSERT INTO "hasan_abdulkarim" VALUES(41,53,'41:53','TA sẽ sớm cho chúng thấy các dấu hiệu của TA trong vũ trụ và nơi bản thân của chúng; cho đến khi tỏ rõ cho chúng thấy Nó (Qur''an) là Chân Lý. Há không đủ cho Thượng Đế của Ngươi hay sao việc Ngài là Nhân Chứng cho tất cả mọi việc?');
INSERT INTO "hasan_abdulkarim" VALUES(41,54,'41:54','Ô này! Quả thật chúng còn nghi ngờ về việc gặp gỡ Thượng Đế của chúng! Ô này! Quả thật Ngài (Allah) bao quát hết tất cả mọi vật.');
INSERT INTO "hasan_abdulkarim" VALUES(42,1,'42:1','Ha. Mim.');
INSERT INTO "hasan_abdulkarim" VALUES(42,2,'42:2','Ain. Sin. Qaf.');
INSERT INTO "hasan_abdulkarim" VALUES(42,3,'42:3','Allah, Đấng Toàn Năng, Đấng Chí Minh, mặc khải (Kinh Sách) cho Ngươi và cho các vị (Sứ Giả) trước Ngươi đúng như thế.');
INSERT INTO "hasan_abdulkarim" VALUES(42,4,'42:4','Mọi vật trong các tầng trời và mọi vật dưới đất đều là của Ngài. Và Ngài la Đấng Tối Cao, Đấng Vĩ Đại.');
INSERT INTO "hasan_abdulkarim" VALUES(42,5,'42:5','(Do Quyền Uy của Ngài) các tầng trời gần chẻ ra làm hai phía bên trên họ và các Thiên Thần tán dương lời ca tụng Thượng Đế của họ và cầu xin tha thứ cho mọi người trên trái đất. Quả thật Allah là Đấng Hằng Tha Thứ, Đấng Rất Mực Khoan Dung.');
INSERT INTO "hasan_abdulkarim" VALUES(42,6,'42:6','Và những ai nhận kẻ khác ngoài Ngài làm người bảo hộ thì (nên biết) Allah là Đấng Trông Chừng họ và Ngươi không là một người thọ lãnh giùm cho họ.');
INSERT INTO "hasan_abdulkarim" VALUES(42,7,'42:7','Và TA đã mặc khải cho Ngươi (Muhammad) Qur''an bằng tiếng Ả Rập đúng như thế để Ngươi dùng cảnh báo thị trấn mẹ (Makkah) và tất cả ai ở xung quanh nó và báo trước về Ngày Đại Hội mà không có chi phải nghi ngờ. (Sau cuộc Phán Xử), một nhóm sẽ đi vào thiên đàng và một mhoms sẽ đi vào hỏa ngục.');
INSERT INTO "hasan_abdulkarim" VALUES(42,8,'42:8','Và nếu muốn, Allah thừa sức làm cho họ trở thành một cộng đồng duy nhất, nhưng Ngài thu nhận người nào Ngài muốn vào vòng Khoan Dung của Ngài. Và những kẻ làm điều sai quấy sẽ không có được một Vị che chở lẫn một cứu nhân nào cả.');
INSERT INTO "hasan_abdulkarim" VALUES(42,9,'42:9','Hoặc phải chăng họ đã nhận những kẻ khác ngoài Ngài (Allah) làm người bảo vệ ư? Nhưng Allah mới thực sự là Đấng Bảo vệ và Ngài làm cho người chết sống lại và Ngài là Đấng Toàn Năng trên mọi thứ.');
INSERT INTO "hasan_abdulkarim" VALUES(42,10,'42:10','Và bất cứ điều gì mà các ngươi tranh chấp đều được trình lên cho Allah quyết định. Đó là Allah, Thượng Đế của Ta (Muhammad), Đấng mà Ta phó thác công việc và quay về sám hối;');
INSERT INTO "hasan_abdulkarim" VALUES(42,11,'42:11','Đấng sáng tạo các tầng trời và trái đất. Ngài tạo từ bản thân của các ngươi các người vợ cho các ngươi và (tạo) từ các loại gia súc các con cái của chúng. Bằng cách đó, Ngài gia tăng các ngươi thêm đông đảo. Không có cái gì giống Ngài cả. Và Ngài là Đấng Hằng Nghe, Đấng Hằng Thấy.');
INSERT INTO "hasan_abdulkarim" VALUES(42,12,'42:12','Ngài nắm các chìa khóa (kho tài nguyên) của các tầng trời và trái đất. Ngài nới rộng hoặc thu hẹp bổng lộc (của Ngài) cho người nào Ngài muốn. Quả thật, Ngài là Đấng Hằng Biết tất cả mọi việc.');
INSERT INTO "hasan_abdulkarim" VALUES(42,13,'42:13','Ngài đã qui định cho các ngươi tôn giáo (Islam) mà Ngài đã truyền xuống cho Nuh và là cái (tôn giáo) mà TA đã mặc khải cho Ngươi và cũng là cái mà TA đã truyền cho Ibrahim và cho Musa và cho Ysa, phán bảo: ''Hãy thành lập tôn giáo và chớ phân chia (thành giáo phái) trong đó''. Đối với những người thờ đa thần, điều mà Ngươi mời họ chấp nhận thật là khó khăn. Allah chọn cho Ngài người nào Ngài muốn và hướng dẫn về với Ngài người nào hằng quay về sám hối.');
INSERT INTO "hasan_abdulkarim" VALUES(42,14,'42:14','Và từ sau lúc tiếp thu được sự hiểu biết (về tôn giáo), do bởi muốn lấn át nhau nên họ đã chia rẽ nhau. Nếu không vì một Lời Phán từ Thượng Đế của Ngươi tuyên bố (dời Ngày Phán Xử) đến mọt thời hạn ấn định thì vấn đề tranh chấp giữa họ đã được giải quyết xong. Và quả thật, những ai thừa hưởng Kinh Sách (Tawrah và Injil) sau họ còn rất đỗi nghi ngờ về nó (Islam).');
INSERT INTO "hasan_abdulkarim" VALUES(42,15,'42:15','Vì lý do đó, hãy mời gọi (nhân loại theo Islam); và hãy đứng vững như Ngươi đã được truyền và chớ nghe theo điều ham muốn của họ và bảo (họ): “Ta tin nơi điều (mặc khải) của Kinh Sách mà Allah đã ban xuống và được lệnh thi hành nền công lý giữa các người. Allah là Thượng Đế của bọn ta và là Thượng Đế của các người. Việc làm của bọn ta là của bọn ta và việc làm của các người là của các người. Không có việc tranh cãi giữa bọn ta và các người. Allah sẽ tập trung (tất cả) chúng ta trở lại và đưa về gặp Ngài (để chịu xét xử).”');
INSERT INTO "hasan_abdulkarim" VALUES(42,16,'42:16','Và những ai tranh cãi về (tôn giáo của) Allah sau khi nó đã được công nhận thì việc tranh cãi của họ thật là vô-ích dưới cái nhìn của Thượng Đế của họ; và họ sẽ đón nhận sự Giận Dữ (của Allah) và sẽ hứng chịu một sự trừng phạt khủng khiếp.');
INSERT INTO "hasan_abdulkarim" VALUES(42,17,'42:17','Allah là Đấng đã ban Kinh Sách (Qur''an) xuống bằng sự thật và chiếc Cân (Công lý). Và điều gì sẽ làm cho Ngươi (Muhammad) biết có lẽ Giờ Tận Thế sắp gần kề?');
INSERT INTO "hasan_abdulkarim" VALUES(42,18,'42:18','Những ai không tin nơi nó (Giờ Tận Thế) giục nó cho mau đến. Còn những ai có đức tin thì sợ nó bởi vì họ biết đó là sự Thật. Quả thật, những ai tranh cãi về Giờ Tận Thế chắc chắn đi lạc rất xa.');
INSERT INTO "hasan_abdulkarim" VALUES(42,19,'42:19','Allah Rất Mực Nhân Từ với bầy tôi của Ngài. Ngài cung dưỡng bổng lộc cho người nào Ngài muốn. Và Ngài là Đấng Toàn Lực, Đấng Toàn Năng.');
INSERT INTO "hasan_abdulkarim" VALUES(42,20,'42:20','Ai mong muốn mảnh đất trồng ở đời sau, TA sẽ gia tăng cho y nơi mảnh đất của y; và ai mong muốn mảnh đất trồng ở đời này, TA sẽ ban nó cho y nhưng y sẽ không có một phần nào cả ở đời sau.');
INSERT INTO "hasan_abdulkarim" VALUES(42,21,'42:21','Hoặc phải chăng họ có những đối tác (thần linh) đã thiết lập cho họ một tôn giáo mà Allah không chấp thuận? Nếu không vì một Lời Phán đã được công bố thì vấn đề (tranh chấp) giữa họ đã được giải quyết xong. Và quả thật, những kẻ làm điều sai quấy sẽ chịu một sự trừng phạt đau đớn.');
INSERT INTO "hasan_abdulkarim" VALUES(42,22,'42:22','Ngươi (Muhammad) sẽ thấy những kẻ làm điều sai quấy lo sợ về những điều mà chúng đã phạm và chắc chắn nó (sự trừng phạt) sẽ rơi nhằm phải họ trong lúc những ai có đức tin và làm việc thiện sẽ ở giữa những cánh đồng xanh tươi của thiên đàng. Họ sẽ được những thứ mà họ mong ước từ Thượng Đế của họ. Đó là một thiên lộc rất lớn.');
INSERT INTO "hasan_abdulkarim" VALUES(42,23,'42:23','Đó là điều mà Allah đã báo tin vui cho bầy tôi của Ngài, những ai có đức tin và làm việc thiện. Hãy bảo họ: ‘Ta không đòi hỏi các người phần thưởng về nó (việc truyền giáo) mà chỉ muốn tình thương dành cho một người ruột thịt (như ta Muhammad).” Và ai làm được một điều tốt thì TA sẽ tăng thêm cái tốt cho y trong đó. Quả thật, Allah Hằng Tha Thứ, Hằng Ghi Công.');
INSERT INTO "hasan_abdulkarim" VALUES(42,24,'42:24','Nhưng họ bảo: “Y (Muhammad) đã bày đặt nói dối rồi đổ cho Allah.” Nhưng nếu Allah muốn thì Ngài sẽ niêm kín quả tim (tấm lòng) của Ngươi lại. Và Allah xóa bỏ điều giả dối và thiết lập sự Thật (Islam) bằng Lời Phán của Ngài (Qur''an). Quả thật, Ngài biết rõ điều (thầm kín) trong lòng (của mỗi người).');
INSERT INTO "hasan_abdulkarim" VALUES(42,25,'42:25','Và Ngài là Đấng chấp nhận sự hối cải của bầy tôi của Ngài và lượng thứ điều xấu xa (tội lỗi) bởi vì Ngài biết rõ điều các người làm.');
INSERT INTO "hasan_abdulkarim" VALUES(42,26,'42:26','Và Ngài đáp lại (lời cầu xin của) những ai có đức tin và làm việc thiện và tăng thêm Thiên lộc cho họ. Ngược lại, những ai không có đức tin thì sẽ bị trừng phạt nghiêm khắc.');
INSERT INTO "hasan_abdulkarim" VALUES(42,27,'42:27','Và nếu Allah nới rộng bổng lộc cho các bầy tôi của Ngài thì chắc chắn họ sẽ làm điều bất công trên trái đất nhưng Ngài ban (nó) xuống theo định lượng mà Ngài muốn. Quả thật, Ngài là Đấng Am Tường, Đấng Hằng Thấy các bầy tôi của Ngài.');
INSERT INTO "hasan_abdulkarim" VALUES(42,28,'42:28','Và Ngài là Đấng ban (nước) mưa xuống sau khi (nhân gian) tuyệt vọng và Ngài rải tung Hồng Ân của Ngài. Và Ngài là Đấng Bảo hộ Rất Đáng Ca Tụng.');
INSERT INTO "hasan_abdulkarim" VALUES(42,29,'42:29','Và trong các dấu hiệu của Ngài là việc tạo hóa các tầng trời và trái đất và tất cả các sinh vật mà Ngài rải tung giữa trời và đất; và Ngài có toàn quyền tập trung chúng lại khi Ngài muốn.');
INSERT INTO "hasan_abdulkarim" VALUES(42,30,'42:30','Và bất cứ điều bất hạnh nào rơi nhằm phải các ngươi, đó là do bàn tay của các ngươi đã làm ra và Ngài đã lượng thứ cho rất nhiều.');
INSERT INTO "hasan_abdulkarim" VALUES(42,31,'42:31','Và các ngươi không thể trốn thoát khỏi (Allah) trên trái đất và ngoài Allah, các ngươi không có được một Đấng Bảo Hộ lẫn một Cứu Nhân nào cả.');
INSERT INTO "hasan_abdulkarim" VALUES(42,32,'42:32','Và trong các dấu hiệu của Ngài là các chiếc tàu (buồm) giữa biển khơi, giống như những quả núi.');
INSERT INTO "hasan_abdulkarim" VALUES(42,33,'42:33','Nếu muốn, Ngài sẽ cho ngưng gió lại thì chúng sẽ bất động trên lưng (của biển cả). Quả thật, trong đó là các dấu hiệu (bài học) cho từng người kiên nhẫn và biết ơn.');
INSERT INTO "hasan_abdulkarim" VALUES(42,34,'42:34','Hoặc Ngài có thể tiêu diệt chúng (các con tàu) vì điều mà họ (những người trên tàu) đã làm ra, nhưng Ngài đã lượng thứ cho rất nhiều.');
INSERT INTO "hasan_abdulkarim" VALUES(42,35,'42:35','Và những ai thường tranh cãi về các dấu hiệu của TA thì nên biết họ sẽ không có một chỗ tị nạn.');
INSERT INTO "hasan_abdulkarim" VALUES(42,36,'42:36','Bởi thế, bất cứ cái gì mà các ngươi được ban cấp thì đó là một sự hưởng thụ của đời sống trần tục này; và cái gì với Allah sẽ tốt và vĩnh viễn hơn cho những ai có đức tin và phó thác cho Đức Thượng Đế của họ.');
INSERT INTO "hasan_abdulkarim" VALUES(42,37,'42:37','Và những ai tránh xa các tội lớn (bất hiếu với cha mẹ, và dâm loạn, ngoại tình) và khi nổi giận, họ (đè nén cơn giận và) tha thứ.');
INSERT INTO "hasan_abdulkarim" VALUES(42,38,'42:38','Và những ai đáp lại (Lời gọi của) Thượng Đế của họ và dâng lễ nguyện Salah một cách chu đáo và làm công việc của họ theo lối tham khảo ý kiến và chi dùng những vật mà TA đã ban cấp cho họ.');
INSERT INTO "hasan_abdulkarim" VALUES(42,39,'42:39','Và những ai khi bị áp bức một cách bất công sẽ giúp đỡ nhau tự vệ.');
INSERT INTO "hasan_abdulkarim" VALUES(42,40,'42:40','Trả oán bằng cái oán tương tự. Nhưng ai lượng thứ và giải hòa thì phần thưởng của y ở nơi Allah bởi vì quả thật, Ngài không yêu thương những kẻ làm điều bất công, sai trái.');
INSERT INTO "hasan_abdulkarim" VALUES(42,41,'42:41','Và chắc chắn, ai tự vệ sau khi bị áp bức thì sẽ không có lý do nào để khiển trách họ.');
INSERT INTO "hasan_abdulkarim" VALUES(42,42,'42:42','Lý do khiển trách chỉ áp dụng với những ai áp bức con người một cách sai quấy và dấy loạn trên trái đất không lý do chính đáng. Họ là những người sẽ chịu một sự trừng phạt đau đớn.');
INSERT INTO "hasan_abdulkarim" VALUES(42,43,'42:43','Và chắc chắn, ai kiên nhẫn chịu đựng và tha thứ thì đó là một sự đòi hỏi quyết tâm cao.');
INSERT INTO "hasan_abdulkarim" VALUES(42,44,'42:44','Và ai mà Allah đánh lạc hướng thì sẽ không có một Đấng Che Chở thay thế Ngài. Và Ngươi (Muhammad) sẽ thấy những kẻ làm điều sai quấy khi chạm trán với hình phạt sẽ nói: “Còn có con đường nào để trở về chăng?”');
INSERT INTO "hasan_abdulkarim" VALUES(42,45,'42:45','Và Ngươi sẽ thấy chúng bị dắt đưa đến đó (hỏa ngục) hạ mình khiêm tốn vì nhục nhã và lắm lét nhìn trộm. Và những ai có đức tin sẽ bảo: “Những người mất mát thực sự là những ai đã đánh mất bản thân (linh hồn) và gia đình mình vào Ngày phục sinh. Quả thật, những kẻ làm điều sai quấy sẽ bị trừng phạt vĩnh viễn.”');
INSERT INTO "hasan_abdulkarim" VALUES(42,46,'42:46','Và ngoài Allah, chúng sẽ không có các vị che chở nào để giúp đỡ chúng. Và ai mà Allah đánh lạc hướng thì sẽ không có con đường nào cả.');
INSERT INTO "hasan_abdulkarim" VALUES(42,47,'42:47','Hãy đáp lại (Lời gọi của) Thượng Đế của các ngươi (bằng cách theo Islam) trước khi xảy ra Ngày mà theo Lệnh của Allah sẽ không thu hồi lại kịp . Vào Ngày đó, các ngươi sẽ không có một nơi lánh nạn cũng không có một lý do để chạy tội.');
INSERT INTO "hasan_abdulkarim" VALUES(42,48,'42:48','Nhưng nếu họ quay bỏ đi thì TA đã không cử Ngươi đến làm một người trông nom họ. Bổn phận của Ngươi chỉ là truyền đạt (Thông điệp). Quả thật, khi TA cho con người nếm mùi Khoan Dung từ TA thì y vui mừng; ngược lại, nếu gặp tai họa do bàn tay của y đã làm ra trước đó thì quả thật, lúc đó con người sẽ phụ ơn.');
INSERT INTO "hasan_abdulkarim" VALUES(42,49,'42:49','Allah nắm quyền thống trị các tầng trời và trái đất. Ngài tạo hóa vật gì Ngài muốn. Ngài ban con gái cho người nào Ngài muốn và con trai cho người nào Ngài muốn.');
INSERT INTO "hasan_abdulkarim" VALUES(42,50,'42:50','Hoặc Ngài ban cả hai, nam và nữ, và làm cho hiếm hoi người nào Ngài muốn. Quả thật, Ngài Toàn Tri và Toàn Năng.');
INSERT INTO "hasan_abdulkarim" VALUES(42,51,'42:51','Phương cách thích hợp đối với một người phàm khi Allah muốn phán bảo với y chỉ là Thiên khải (Mặc khải) hoặc từ sau một bức màn (vô hình) hoặc cử phái một Thiên Thần (Thiên Sứ) đến mặc khải điều mà Ngài muốn, theo Phép của Ngài. Quả thật, Ngài Tối Cao và Chí Minh.');
INSERT INTO "hasan_abdulkarim" VALUES(42,52,'42:52','Và đúng như thế, TA đã mặc khải cho Ngươi Tinh Thần theo Mệnh Lệnh của TA. (Trước đó), Ngươi không hề biết Kinh Sách và đức tin là gì. Nhưng TA đã làm cho Nó (Qur''an) thành một nguồn sáng mà TA dùng để hướng dẫn người nào TA muốn trong số bầy tôi của TA. Và quả thật, Ngươi hướng dẫn (nhân loại) đến con đường ngay chính:');
INSERT INTO "hasan_abdulkarim" VALUES(42,53,'42:53','Con đường của Allah, Đấng mà tất cả những gì trong các tầng trời và tất cả những gì dưới đất đều thuộc về Ngài. Quả thật, tất cả mọi việc đều được trình lên cho Allah (quyết định) cả.');
INSERT INTO "hasan_abdulkarim" VALUES(43,1,'43:1','Ha. Mim.');
INSERT INTO "hasan_abdulkarim" VALUES(43,2,'43:2','Thề bởi Kinh Sách quang minh (này).');
INSERT INTO "hasan_abdulkarim" VALUES(43,3,'43:3','Quả thật, TA đã làm Nó thành một Qur''an bằng tiếng Ả Rập để cho các ngươi đọc hiểu.');
INSERT INTO "hasan_abdulkarim" VALUES(43,4,'43:4','Quả thật, Nó (Qur''an) nằm trong Tập Kinh Mẹ được cất giữ nơi TA, rất cao cả và quán thông.');
INSERT INTO "hasan_abdulkarim" VALUES(43,5,'43:5','Há TA sẽ lấy lại Lời Nhắc Nhở (Qur''an) khỏi các ngươi bởi vì các ngươi là một đám người phạm tội quá đáng?');
INSERT INTO "hasan_abdulkarim" VALUES(43,6,'43:6','Và có bao nhiêu Nabi đã được TA phái đi cho các dân tộc xa xưa?');
INSERT INTO "hasan_abdulkarim" VALUES(43,7,'43:7','Và không một Nabi nào đến gặp họ mà không bị chế giễu.');
INSERT INTO "hasan_abdulkarim" VALUES(43,8,'43:8','Bởi thế, TA đã tiêu diệt những người mạnh hơn họ (người Ả Rập Quraish) về quyền lực. Và hình ảnh của người xưa đã lui vào dĩ vãng.');
INSERT INTO "hasan_abdulkarim" VALUES(43,9,'43:9','Và nếu Ngươi (Muhammad) hỏi họ: “Ai đã tạo các tầng trời và trái đất?” thì chắc chắn họ sẽ đáp: “Đấng Toàn Năng, Đấng Toàn Tri đã tạo hóa chúng.”');
INSERT INTO "hasan_abdulkarim" VALUES(43,10,'43:10','Đấng đã làm trái đất như một chiếc giường ngủ cho các ngươi và đã làm nơi đó những con đường cho các ngươi để các ngươi tìm đường đi (đến mục tiêu).');
INSERT INTO "hasan_abdulkarim" VALUES(43,11,'43:11','Và là Đấng ban nước mưa từ trên trời xuống theo định lượng mà TA dùng để làm sống lại mảnh đất đã chết cứng. Các ngươi sẽ được dựng sống lại giống như thế.');
INSERT INTO "hasan_abdulkarim" VALUES(43,12,'43:12','Và là Đấng đã tạo tất cả mọi vật theo cặp (nam nữ, trống mái, âm dương) và đã tạo cho các ngươi các con tàu và gia súc để các ngươi dùng cưỡi;');
INSERT INTO "hasan_abdulkarim" VALUES(43,13,'43:13','Để cho các ngươi ngồi vững trên lưng của chúng và khi ngồi yên trên đó các ngươi nhớ ơn của Thượng Đế của các ngươi và thưa: “Quang vinh thay Đấng đã chế ngự con (vật) này cho bầy tôi sử dụng bởi vì bầy tôi không đủ khả năng (chế ngự) nó."');
INSERT INTO "hasan_abdulkarim" VALUES(43,14,'43:14','“Và chắc chắn bầy tôi sẽ trở về gặp Thượng Đế của bầy tôi!”');
INSERT INTO "hasan_abdulkarim" VALUES(43,15,'43:15','Và họ (người Ả Rập Quraish) đã làm cho một số bầy tôi của Ngài có một phần chia với Ngài. Rõ ràng con người quả thật vong ân.');
INSERT INTO "hasan_abdulkarim" VALUES(43,16,'43:16','Sao! Há Ngài (Allah) đã nhận những vật mà Ngài đã tạo làm con gái và chọn cho các ngươi con trai ư?');
INSERT INTO "hasan_abdulkarim" VALUES(43,17,'43:17','Và khi một người trong bọn họ nhận được tin tức về thí dụ (nói về con gái) mà họ đã bịa đặt cho Đấng Al-Rahman, gương mặt của y sa sầm, ngập tràn đau thương.');
INSERT INTO "hasan_abdulkarim" VALUES(43,18,'43:18','Và há một người (phụ nữ)( được nuôi nấng bằng vòng vàng và không ăn nói rành rỗi khi tranh luận (lại là kẻ phụ tá của Allah) hay sao?');
INSERT INTO "hasan_abdulkarim" VALUES(43,19,'43:19','Và họ đã qui cho các Thiên Thần vốn là bầy tôi của Đấng Al-Rahman là nữ giới. Phải chăng họ đã chứng kiến việc tạo hóa các Thiên Thần? Lời xác nhận của họ sẽ được ghi chép và họ sẽ bị gặn hỏi (về việc đó).');
INSERT INTO "hasan_abdulkarim" VALUES(43,20,'43:20','Và họ nói: “Nếu Đấng Al-Rahman muốn thì chúng tôi đâu có tôn thờ các (nữ thần) làm gì?" Họ không có một chút hiểu biết nào về điều đó. Họ chỉ phỏng đoán mà thôi.');
INSERT INTO "hasan_abdulkarim" VALUES(43,21,'43:21','Hoặc há TA (Allah) đã ban cho họ một Kinh Sách trước (quyển Qur''an) này cho nên họ bám chắc theo đó?');
INSERT INTO "hasan_abdulkarim" VALUES(43,22,'43:22','Không! Họ nói: “Chúng tôi thấy cha mẹ của chúng tôi theo một tôn giáo nào đó nên chúng tôi theo dấu chân của họ.”');
INSERT INTO "hasan_abdulkarim" VALUES(43,23,'43:23','Và giống như thế, không một người báo trước nào trước Ngươi đã được TA cử phái đến cho một thị trấn mà những người giàu có nơi đó lại không nói: “Chúng tôi thấy cha mẹ của chúng tôi theo một tôn giáo nào đó và chắc chắn chúng tôi theo dấu chân của họ.”');
INSERT INTO "hasan_abdulkarim" VALUES(43,24,'43:24','(Người Báo trước bảo họ): “Sao? Dẫu ta mang đến cho các người một Chỉ Đạo tốt hơn cái mà các người thấy cha mẹ của các người đang theo ư?” Họ đáp: “Chúng tôi không tin nơi điều mà quí vị mang đến.”');
INSERT INTO "hasan_abdulkarim" VALUES(43,25,'43:25','Bởi thế, TA đã trừng phạt họ. Hãy xem kết cuộc của những kẻ đã phủ nhận (Thông điệp của Allah) xảy ra như thế nào?');
INSERT INTO "hasan_abdulkarim" VALUES(43,26,'43:26','Và khi Ibrahim thưa với phụ thân và người dân của Y: "Tôi vô can về những kẻ (thần linh) mà qui vị tôn thờ."');
INSERT INTO "hasan_abdulkarim" VALUES(43,27,'43:27','“Ngoại trừ Đấng đã sáng tạo ra tôi; và quả thật, Ngài sẽ hướng dẫn tôi.”');
INSERT INTO "hasan_abdulkarim" VALUES(43,28,'43:28','Và Y đã làm cho Nó( thành một lời trường cửu cho hậu thế, nhờ đó, may ra họ có thể quay trở lại (với Allah).');
INSERT INTO "hasan_abdulkarim" VALUES(43,29,'43:29','Không! TA đã để cho những người (Quraish) này và cha mẹ của họ hưởng thụ (cuộc đời) cho đến khi Chân Lý (Qur''an) và một Sứ Giả (Muhammad) minh bạch đến với họ.');
INSERT INTO "hasan_abdulkarim" VALUES(43,30,'43:30','Nhưng khi Chân Lý đến với họ, họ bảo: “Đây là trò ảo thuật, và chúng tôi không tin nơi Nó (Qur''an).');
INSERT INTO "hasan_abdulkarim" VALUES(43,31,'43:31','Và họ bảo: "Tại sao Qur''an này không được ban xuống cho những người (lãnh đạo) của hai thị trấn lớn (Makkah và Ta-if?)”');
INSERT INTO "hasan_abdulkarim" VALUES(43,32,'43:32','Phải chăng họ muốn phân chia Hồng Ân của Thượng Đế của Ngươi hay sao? Chính TA phân chia sinh kế của họ giữa họ với nhau trong cuộc sống trần tục này và TA nâng một số người này trội hơn một số người khác về cấp bậc để cho người này bắt người kia phục dịch. Nhưng Hồng Ân (thiên đàng) của Thượng Đế của Ngươi tốt hơn của cải mà họ tích lũy.');
INSERT INTO "hasan_abdulkarim" VALUES(43,33,'43:33','Và nếu không vì lý do muốn cho nhân loại trở thành một cộng đồng duy nhất thì TA đã làm cho ai phủ nhận Đấng Al-Rahman có được những mái nhà bằng bạc và cầu thang (bằng bạc) để đi lên.');
INSERT INTO "hasan_abdulkarim" VALUES(43,34,'43:34','Và những cửa nhà (bằng bạc) và những chiếc tràng kỷ (bằng bạc) để nằm nghỉ.');
INSERT INTO "hasan_abdulkarim" VALUES(43,35,'43:35','Và những món trang sức bằng vàng. Nhưng tất cả những thứ đó chỉ là sự hưởng thụ (ngắn ngủi) của đời sống trần tục này. Và đời sau đói với Thượng Đế của Ngươi mới là (sự hưởng thụ) dành cho những người ngay chính sợ Allah.');
INSERT INTO "hasan_abdulkarim" VALUES(43,36,'43:36','Ai nhắm mắt (làm ngơ) trước Lời Nhắc nhở của Đấng Al-Rahman, TA sẽ chỉ định một tên Shaytan làm một bạn đường với y.');
INSERT INTO "hasan_abdulkarim" VALUES(43,37,'43:37','Và quả thật, chúng (những tên Shaytan) tìm cách cản trở họ rời xa con đường (của Allah) trong lúc họ nghĩ họ được hướng dẫn.');
INSERT INTO "hasan_abdulkarim" VALUES(43,38,'43:38','Mãi cho đến khi đến trình diện TA, y bảo (người bạn Shaytan): “Phải chi tôi với bạn cách xa nhau bằng khoảng cách của hai phương đông (thì tốt biết mấy). Thật tồi tệ thay một người bạn đường như thế!"');
INSERT INTO "hasan_abdulkarim" VALUES(43,39,'43:39','Vào Ngày đó, nó (lời trách móc) chẳng giúp ích gì được cho các ngươi khi các ngươi đã làm điều sai quấy; quả thật các ngươi cùng san sẻ sự trừng phạt.');
INSERT INTO "hasan_abdulkarim" VALUES(43,40,'43:40','Có thể nào Ngươi (Muhammad) làm cho người điếc nghe được hoặc hướng dẫn một người mù hoặc ai đó đang công khai lầm lạc?');
INSERT INTO "hasan_abdulkarim" VALUES(43,41,'43:41','Bởi thế, nếu TA có đưa Ngươi đi khỏi (Makkah) thì chắc chắn TA sẽ trừng phạt họ.');
INSERT INTO "hasan_abdulkarim" VALUES(43,42,'43:42','Hoặc TA cho Ngươi thấy điều (trừng phạt) mà TA đã hứa với họ; quả thật, TA có quyền đối với họ.');
INSERT INTO "hasan_abdulkarim" VALUES(43,43,'43:43','Bởi thế, hãy bám chắc vào điều đã được mặc khải cho Ngươi. Quả thật, Ngươi đang theo đúng Chính Đạo.');
INSERT INTO "hasan_abdulkarim" VALUES(43,44,'43:44','Và Nó (Qur''an) là Lời Nhắc Nhở được ban cho Ngươi, và người dân của Ngươi, và các người sẽ bị chất vấn (về nó).');
INSERT INTO "hasan_abdulkarim" VALUES(43,45,'43:45','Và hãy hỏi những Sứ Giả của TA mà TA đã cử đến trước Ngươi xem TA có chỉ định những thần linh nào khác ngoài Đấng Al-Rahman để cho chúng tôn thờ hay không?');
INSERT INTO "hasan_abdulkarim" VALUES(43,46,'43:46','Và quả thật, TA đã cử Musa mang những Phép Lạ của TA đến gặp Fir-''awn và các tên đầu lĩnh của y; do đó, Y bảo (chúng): “Ta là Sứ Giả của Thượng Đế của vũ trụ và muôn loài.”');
INSERT INTO "hasan_abdulkarim" VALUES(43,47,'43:47','Nhưng khi Y đến gặp chúng với những Phép Lạ của TA thì chúng cười nhạo cho những Phép Lạ đó.');
INSERT INTO "hasan_abdulkarim" VALUES(43,48,'43:48','Và TA cho chúng thấy từ Phép Lạ này đến Phép Lạ kia, cái sau lớn hơn cái trước và TA đã bắt phạt chúng để may ra chúng thức tính mà quay về (với Chính Đạo).');
INSERT INTO "hasan_abdulkarim" VALUES(43,49,'43:49','Và chúng bảo (Musa): “Hỡi tên phù thủy kia! Hãy cầu xin Thượng Đế của Ngươi hộ bọn ta theo điều mà Ngài đã giao ước với Ngươi bởi vì bọn ta muốn được hướng dẫn."');
INSERT INTO "hasan_abdulkarim" VALUES(43,50,'43:50','Nhưng khi TA bốc hình phạt đi khỏi chúng thì chúng nuốt lời hứa.');
INSERT INTO "hasan_abdulkarim" VALUES(43,51,'43:51','Và Fir-''awn đã tuyên bố với người dân của hắn, bảo: “Hỡi dân ta! Há ta không làm bá chủ xứ Ai Cập và những dòng sông này đang chảy bên dưới ta hay sao? Thế các ngươi không nhìn thấy hay sao?"');
INSERT INTO "hasan_abdulkarim" VALUES(43,52,'43:52','“Há ta không trội hơn tên (Musa) này hay sao, nó đáng khỉnh và ăn nói không được lưu loát?"');
INSERT INTO "hasan_abdulkarim" VALUES(43,53,'43:53','“Thế tại sao nó không được ban cho vòng vàng hoặc không được Thiên Thần tháp tùng?”');
INSERT INTO "hasan_abdulkarim" VALUES(43,54,'43:54','Bởi thế, hắn đã xúi giục người dân của hắn khiến họ theo hắn. Quả thật, chúng là một đám người bất tuân và nổi loạn');
INSERT INTO "hasan_abdulkarim" VALUES(43,55,'43:55','Bởi thế, khi chúng khiêu khích TA, TA bắt phạt và nhận tất cả bọn chúng chết chìm (dưới biển sâu).');
INSERT INTO "hasan_abdulkarim" VALUES(43,56,'43:56','Và TA đã lấy chúng làm một tiền sự và một thí dụ (bài học) cho hậu thế.');
INSERT INTO "hasan_abdulkarim" VALUES(43,57,'43:57','Và khi đứa con trai của Maryam( được đưa ra làm một thí dụ (trong Qur''an) thì người dân của Y la lối về điều đó.');
INSERT INTO "hasan_abdulkarim" VALUES(43,58,'43:58','Và chúng bảo: “Há những thần linh của bọn ta tốt hay Y (Ysa) (tốt hơn?)" Chúng đưa vấn đề đó ra cho Ngươi chỉ vì muốn tranh cãi. Không! Chúng là một đám người thích cãi vã.');
INSERT INTO "hasan_abdulkarim" VALUES(43,59,'43:59','Y (Ysa) chỉ là một người bề tôi (của TA - Allah). TA ban ân cho Y và lấy Y làm một cái gương cho con cháu của Israel.');
INSERT INTO "hasan_abdulkarim" VALUES(43,60,'43:60','Và nếu muốn, TA đã làm ra Thiên Thần từ các ngươi để kế tục nhau trên trái đất.');
INSERT INTO "hasan_abdulkarim" VALUES(43,61,'43:61','Và quả thật Y (Ysa) là một dấu hiệu cho biết Giờ Tận Thế sắp xảy ra, bởi thế chớ nghi ngờ về điều đó, nhưng hãy theo TA (Allah); Đây là Chính Đạo.');
INSERT INTO "hasan_abdulkarim" VALUES(43,62,'43:62','Và chớ để Shaytan cản trở các ngươi (theo Chính Đạo). Quả thật, nó là kẻ thù công khai của các ngươi.');
INSERT INTO "hasan_abdulkarim" VALUES(43,63,'43:63','Và khi Ysa đến với những bằng chứng rõ rệt, Y bảo: “Chắc chắn Ta đến gặp các người với điều sáng suốt cao minh và để làm sáng tỏ một vài điểm mà các người bất đồng ý kiến trong đó. Bởi thế, hãy sợ Allah và nghe theo Ta."');
INSERT INTO "hasan_abdulkarim" VALUES(43,64,'43:64','"Quả thật Allah, Ngài là Thượng Đế của Ta và là Thượng Đế của các người. Bởi thế, hãy thờ phụng Ngài. Đây là Chính Đạo."');
INSERT INTO "hasan_abdulkarim" VALUES(43,65,'43:65','Nhưng các nhóm (giáo phái) giữa bọn chúng bất đồng ý kiến với nhau. Bởi thế, khốn khổ cho những ai làm điều sai quấy (đặt điều nói quấy cho Ysa) về sự trừng phạt của Ngày Đau khổ (của việc Phán Xử).');
INSERT INTO "hasan_abdulkarim" VALUES(43,66,'43:66','Phải chăng chúng chỉ chờ đợi Giờ Tận Thế, e rằng nó sẽ bất ngờ xảy đến cho chúng trong lúc chúng không nhận thấy?');
INSERT INTO "hasan_abdulkarim" VALUES(43,67,'43:67','Vào Ngày đó những người bạn thân tình sẽ trở thành kẻ thù của nhau ngoại trừ những người ngay chính sợ Allah.');
INSERT INTO "hasan_abdulkarim" VALUES(43,68,'43:68','“Hỡi bầy tôi của TA! Ngày nay, các ngươi sẽ không lo sợ cũng sẽ không buồn phiền:');
INSERT INTO "hasan_abdulkarim" VALUES(43,69,'43:69','Những ai tin tưởng nơi các dấu hiệu của TA và là người Muslim (thần phục Allah).');
INSERT INTO "hasan_abdulkarim" VALUES(43,70,'43:70','Hãy vui vầy bước vào thiên đàng, các ngươi cùng với vợ của các ngươi.');
INSERT INTO "hasan_abdulkarim" VALUES(43,71,'43:71','Đĩa ăn và tách uống bằng vàng sẽ được chuyền đi vòng đến họ. Trong đó có tất cả những món vật mà bản thân (linh hồn) mong ước và tất cả những món vật làm đẹp mắt. Và các ngươi sẽ ở trong đó đời đời.');
INSERT INTO "hasan_abdulkarim" VALUES(43,72,'43:72','Đấy là thiên đàng mà các ngươi sẽ thừa hưởng vì những điều tốt mà các ngươi đã từng làm (trên thế gian).');
INSERT INTO "hasan_abdulkarim" VALUES(43,73,'43:73','Trong đó sẽ có rất nhiều hoa quả để cho các ngươi dùng.');
INSERT INTO "hasan_abdulkarim" VALUES(43,74,'43:74','Quả thật, những kẻ tội lỗi sẽ bị phạt hỏa ngục mà chúng sẽ vào ở đời đời.');
INSERT INTO "hasan_abdulkarim" VALUES(43,75,'43:75','Trong đó, chúng sẽ không được giảm nhẹ hình phạt và sẽ vô cùng tuyệt vọng.');
INSERT INTO "hasan_abdulkarim" VALUES(43,76,'43:76','Và TA (Allah) đã không hãm hại chúng nhưng chúng là những kẻ làm điều sai quấy.');
INSERT INTO "hasan_abdulkarim" VALUES(43,77,'43:77','Và chúng sẽ kêu la: “Hỡi vị cai ngục. Hãy xin Thượng Đế của ngài giết phứt chúng tôi đi! (Vị cai ngục) sẽ đáp: “Chắc các ngươi sẽ phải ở đó đời đời.”');
INSERT INTO "hasan_abdulkarim" VALUES(43,78,'43:78','Quả thật, TA đã mang Chân Lý đến cho các ngươi nhưng đa số các ngươi ghét Chân Lý.');
INSERT INTO "hasan_abdulkarim" VALUES(43,79,'43:79','Phải chăng chúng sắp đặt công việc nhưng chính TA mới sắp đặt.');
INSERT INTO "hasan_abdulkarim" VALUES(43,80,'43:80','Phải chăng chúng nghĩ rằng TA không nghe đặng những điều bí mật và lời mật nghị của chúng? Vâng, các thiên sứ (Thiên Thần) của TA ở cạnh chúng ghi chép lại (điều chúng âm mưu).');
INSERT INTO "hasan_abdulkarim" VALUES(43,81,'43:81','Hãy bảo chúng: “Nếu quả thật Đấng Al-Rahman có một đứa con trai thì Ta sẽ là một người thờ phụng đau tiên.”');
INSERT INTO "hasan_abdulkarim" VALUES(43,82,'43:82','Quang vinh và trong sạch thay Thượng Đế của các tầng trời và trái đất, Thượng Đế của Ngai Vương, Ngài vượt hẳn những điều chúng đã qui cho Ngài.');
INSERT INTO "hasan_abdulkarim" VALUES(43,83,'43:83','Bởi thế, hãy để mặc chúng lảm nhảm và vui đùa cho đến khi chúng đối diện với Ngày (tàn) của chúng mà chúng đã được hứa.');
INSERT INTO "hasan_abdulkarim" VALUES(43,84,'43:84','Và Ngài là Thượng Đế trên trời và là Thượng Đế dưới đất; và Ngài là Đấng Chí Minh, Đấng Toàn Tri.');
INSERT INTO "hasan_abdulkarim" VALUES(43,85,'43:85','Và phúc thay Đấng nắm quyền thống trị các tầng trời và trái đất và vạn vật giữa trời đất; và Ngài biết rõ Giờ Tận Thế và (tất cả) các ngươi sẽ được đưa về gặp Ngài.');
INSERT INTO "hasan_abdulkarim" VALUES(43,86,'43:86','Và những kẻ mà chúng khấn vái ngoài Ngài (Allah) không có quyền can thiệp ngoại trừ những ai làm chứng cho sự Thật (xác nhận Chân Lý) trong lúc chúng biết;');
INSERT INTO "hasan_abdulkarim" VALUES(43,87,'43:87','Và nếu Ngươi có hỏi chúng: “Ai đã tạo hóa các người?” thì chắc chắn chúng sẽ đáp: ''Allah!'' thế sao chúng lại lánh xa (Allah)!');
INSERT INTO "hasan_abdulkarim" VALUES(43,88,'43:88','Và (Allah biết rõ) lời (than của Muhammad) nói: ‘Thưa Thượng Đế của bề tôi! Những người (Quraish) này là một đám người không tin tưởng.”');
INSERT INTO "hasan_abdulkarim" VALUES(43,89,'43:89','Bởi thế, hãy lánh xa chúng và nói: Salam (Bằng an)! Rồi đây chúng sẽ sớm biết (sự thật).');
INSERT INTO "hasan_abdulkarim" VALUES(44,1,'44:1','Ha. Mim.');
INSERT INTO "hasan_abdulkarim" VALUES(44,2,'44:2','Thề bởi Kinh Sách (Qur''an) Quang Minh;');
INSERT INTO "hasan_abdulkarim" VALUES(44,3,'44:3','Quả thật TA ban Nó (Qur''an) xuống vào một đêm đầy ân phúc (của tháng Ramadan). Quả thật, TA là Đấng Hằng Cảnh Báo.');
INSERT INTO "hasan_abdulkarim" VALUES(44,4,'44:4','Trong đêm đó, mọi công việc công minh đều được quyết định.');
INSERT INTO "hasan_abdulkarim" VALUES(44,5,'44:5','Theo Chỉ thị của TA. Quả thật, TA là Đấng Hằng Cử Phái (các Sứ Giả).');
INSERT INTO "hasan_abdulkarim" VALUES(44,6,'44:6','Như một Hồng Ân từ Thượng Đế của Ngươi (Muhammad). Quả thật, Ngài là Đấng Hằng Nghe, Đấng Hằng Biết (mọi việc),');
INSERT INTO "hasan_abdulkarim" VALUES(44,7,'44:7','Thượng Đế (Đấng Chủ Tể) của các tầng trời và trái đất và vạn vật giữa trời đất nếu các ngươi khẳng định đức tin một cách vững chắc.');
INSERT INTO "hasan_abdulkarim" VALUES(44,8,'44:8','Không có Thượng Đế nào cả duy chỉ Ngài (là Thượng Đế), Đấng làm cho sống và gây cho chết. Thượng Đế của các ngươi và là Thượng Đế của ông bà tổ tiên của các ngươi.');
INSERT INTO "hasan_abdulkarim" VALUES(44,9,'44:9','Không, họ đùa cợt trong hoài nghi.');
INSERT INTO "hasan_abdulkarim" VALUES(44,10,'44:10','Bởi thế, Ngươi (Muhammad) hãy đợi xem Ngày mà bầu trời sẽ tỏa ra một loại khói mờ rõ rệt.');
INSERT INTO "hasan_abdulkarim" VALUES(44,11,'44:11','Phủ mờ cả nhân loại. Đây là một sự trừng phạt đau đớn.');
INSERT INTO "hasan_abdulkarim" VALUES(44,12,'44:12','(Họ sẽ thưa:) “Lạy Thượng Đế của bầy tôi! Xin Ngài giải tỏa sự trừng phạt này đi khỏi bầy tôi, thật sự bầy tôi là sẽ là những người có đức tin.”');
INSERT INTO "hasan_abdulkarim" VALUES(44,13,'44:13','Làm sao họ có thể tiếp thu được Lời Nhắc Nhở trong lúc một Sứ Giả công khai đã đến gặp họ,');
INSERT INTO "hasan_abdulkarim" VALUES(44,14,'44:14','Rồi họ quay lánh xa Y và nói: “Đồ thứ học mót, đồ thằng điên.”');
INSERT INTO "hasan_abdulkarim" VALUES(44,15,'44:15','Và chắc chắn, TA sẽ giải tỏa sự trừng phạt đi (khỏi các ngươi) trong một thời gian ngắn; nhưng chắc chắn các người sẽ trở lại (tật cũ).');
INSERT INTO "hasan_abdulkarim" VALUES(44,16,'44:16','Vào Ngày mà TA sẽ túm bắt họ dữ dằn. Quả thật, TA sẽ trừng phạt họ.');
INSERT INTO "hasan_abdulkarim" VALUES(44,17,'44:17','Và trước họ, quả thật TA đã xử lý đám người của Fir-''awn và đã phái một Sứ Giả thật vinh dự (Musa) đến gặp chúng.');
INSERT INTO "hasan_abdulkarim" VALUES(44,18,'44:18','(Y bảo:) “Hãy giao hoàn cho tôi bầy tôi của Allah (con cháu của Israel). Tôi là một Sứ Giả tín nhiệm (được cử đến) với quí vị."');
INSERT INTO "hasan_abdulkarim" VALUES(44,19,'44:19','“Và chớ tự đề cao mình hơn Allah. Tôi đến gặp quí vị với một thẩm quyền rõ rệt."');
INSERT INTO "hasan_abdulkarim" VALUES(44,20,'44:20','“Và tôi cầu xin Thượng Đế của tôi và cũng là Thượng Đế của quí vị che chở để khỏi bị quí vị ném đá (giết chết)."');
INSERT INTO "hasan_abdulkarim" VALUES(44,21,'44:21','“Và nếu không tin tưởng nơi tôi thì quí vị hãy để cho tôi yên.”');
INSERT INTO "hasan_abdulkarim" VALUES(44,22,'44:22','Bởi thế, Y (Musa) cầu nguyện Thượng Đế của Y, thưa: “Những người này là một đám người tội lỗi.”');
INSERT INTO "hasan_abdulkarim" VALUES(44,23,'44:23','(Allah phán bảo Musa): “Ngươi hãy đưa bầy tôi của TA ra đi ban đêm. Các ngươi sẽ bị truy sát."');
INSERT INTO "hasan_abdulkarim" VALUES(44,24,'44:24','“Và hãy rời biển (Hồng hải) rẽ đôi (để cho chúng băng qua). Quả thật, chúng là một đoàn quân sẽ bị nhận chìm dưới nước.”');
INSERT INTO "hasan_abdulkarim" VALUES(44,25,'44:25','Có bao nhiêu vườn tược và dòng suối chúng đã bỏ lại?');
INSERT INTO "hasan_abdulkarim" VALUES(44,26,'44:26','Và ruộng đồng và biệt thự cao sang?');
INSERT INTO "hasan_abdulkarim" VALUES(44,27,'44:27','Và lạc thú (trần gian) mà chúng đã từng hưởng thụ?');
INSERT INTO "hasan_abdulkarim" VALUES(44,28,'44:28','Đúng như thế. Và TA đã làm cho đám người khác thừa hưởng chúng.');
INSERT INTO "hasan_abdulkarim" VALUES(44,29,'44:29','Bởi thế, trời và đất chẳng ai nhỏ lệ (khóc thương) giùm chúng. Chúng cũng không được tạm tha.');
INSERT INTO "hasan_abdulkarim" VALUES(44,30,'44:30','Và quả thật TA đã giải cứu con cháu của Israel thoát khỏi một sự trừng phạt nhục nhã,');
INSERT INTO "hasan_abdulkarim" VALUES(44,31,'44:31','Khỏi Fir-''awn, bởi vì quả thật hắn ngạo mạn trong số những kẻ bạo ngược, tàn ác quá mức.');
INSERT INTO "hasan_abdulkarim" VALUES(44,32,'44:32','Và quả thật TA đã chọn họ trội hơn thiên hạ về kiến thức.');
INSERT INTO "hasan_abdulkarim" VALUES(44,33,'44:33','Và đã ban cho họ những đặc ân mà trong đó là một sự thử thách rõ rệt.');
INSERT INTO "hasan_abdulkarim" VALUES(44,34,'44:34','Rõ thật những người (Quraish) này tuyên bố:');
INSERT INTO "hasan_abdulkarim" VALUES(44,35,'44:35','“Quả thật, nó (cái chết) chỉ là cái chết đầu tiên của chúng tôi và chúng tôi sẽ không được dựng sống lại.”');
INSERT INTO "hasan_abdulkarim" VALUES(44,36,'44:36','"Thế hãy đưa tổ tiên của chúng tôi trở lại xem nếu quí vị nói sự thật."');
INSERT INTO "hasan_abdulkarim" VALUES(44,37,'44:37','Phải chăng họ tài ba hơn đám dân của Tubba''( và những ai sống trước họ hay sao? TA đã tiêu diệt chúng bởi vì chúng là những kẻ tội lỗi.');
INSERT INTO "hasan_abdulkarim" VALUES(44,38,'44:38','Và TA đã không tạo các tầng trời và trái đất và vạn vật giữa trời đất để vui chơi.');
INSERT INTO "hasan_abdulkarim" VALUES(44,39,'44:39','Chỉ vì Chân Lý TA đã tạo hai cái đó. Nhưng đa số bọn chúng không biết.');
INSERT INTO "hasan_abdulkarim" VALUES(44,40,'44:40','Quả thật, Ngày Phân Loại (giữa người có đức tin và kẽ vô đức tin) là thời điểm đã được ấn định cho tất cả bọn chúng.');
INSERT INTO "hasan_abdulkarim" VALUES(44,41,'44:41','Ngày mà không một thân nhân gần nào có thể giúp đỡ một thân nhân gần khác về bất cứ điều gì và họ sẽ không được ai giúp đỡ.');
INSERT INTO "hasan_abdulkarim" VALUES(44,42,'44:42','Ngoại trừ người nào được Allah khoan dung bởi vì Ngài là Đấng Toàn Năng, Đấng Hằng Khoan dung.');
INSERT INTO "hasan_abdulkarim" VALUES(44,43,'44:43','Quả thật, cây Zaqqum,');
INSERT INTO "hasan_abdulkarim" VALUES(44,44,'44:44','Sẽ là thực phẩm của những kẻ tội lỗi,');
INSERT INTO "hasan_abdulkarim" VALUES(44,45,'44:45','Giống như loại dầu nấu sôi, sôi sùng sục trong bụng của chúng,');
INSERT INTO "hasan_abdulkarim" VALUES(44,46,'44:46','Như loại nước sôi hừng hực.');
INSERT INTO "hasan_abdulkarim" VALUES(44,47,'44:47','(Sẽ có tiếng vọng lớn:) “Hãy túm bắt nó lôi vào giữa đám lửa cháy bùng,"');
INSERT INTO "hasan_abdulkarim" VALUES(44,48,'44:48','“Rồi đổ lên đầu nó hình phạt của nước sôi."');
INSERT INTO "hasan_abdulkarim" VALUES(44,49,'44:49','“(Và mắng): ‘Hãy nếm đi! (Khi xưa) mày quyền thế và vinh dự!"');
INSERT INTO "hasan_abdulkarim" VALUES(44,50,'44:50','“Đây thật sự là điều mà các ngươi đã hoài nghi.”');
INSERT INTO "hasan_abdulkarim" VALUES(44,51,'44:51','Quả thật! Những người ngay chính sợ Allah sẽ được ở một nơi an toàn (thiên đàng).');
INSERT INTO "hasan_abdulkarim" VALUES(44,52,'44:52','Giữa các hoa viên và suối nước;');
INSERT INTO "hasan_abdulkarim" VALUES(44,53,'44:53','Phục sức bằng áo lụa mịn và gấm thêu, (nằm tựa mình) đối diện nhau,');
INSERT INTO "hasan_abdulkarim" VALUES(44,54,'44:54','Đúng như thế; và TA sẽ kết hôn họ với các tiên nữ có đôi mắt to trữ tình.');
INSERT INTO "hasan_abdulkarim" VALUES(44,55,'44:55','Trong đó (thiên đàng), họ sẽ gọi đủ loại hoa quả an toàn để dùng;');
INSERT INTO "hasan_abdulkarim" VALUES(44,56,'44:56','Trong đó, họ sẽ không nếm qua cái chết nào khác ngoài cái chết lần đầu (ở trần gian); và Ngài (Allah) sẽ bảo vệ họ khỏi sự trừng phạt của hỏa ngục.');
INSERT INTO "hasan_abdulkarim" VALUES(44,57,'44:57','Như một Thiên An từ Thượng Đế của Ngươi (Muhammad)! Đấy mới là một sự thành tựu vĩ đại.');
INSERT INTO "hasan_abdulkarim" VALUES(44,58,'44:58','Quả thật, TA đã làm cho Nó (Qur’an) dễ (hiểu) bằng chiếc lưỡi (lời nói) của Ngươi để may ra họ ghi nhớ.');
INSERT INTO "hasan_abdulkarim" VALUES(44,59,'44:59','Thôi hãy chờ đợi và họ cũng chờ đợi (như Ngươi).');
INSERT INTO "hasan_abdulkarim" VALUES(45,1,'45:1','Ha. Mim.');
INSERT INTO "hasan_abdulkarim" VALUES(45,2,'45:2','Kinh Sách do Allah, Đấng Toàn Năng, Đấng Toàn Tri ban xuống.');
INSERT INTO "hasan_abdulkarim" VALUES(45,3,'45:3','Quả thật, trong các tầng trời và trái đất là các dấu hiệu cho những người có đức tin.');
INSERT INTO "hasan_abdulkarim" VALUES(45,4,'45:4','Và trong việc tạo hóa các ngươi cũng như việc Ngài rải các thú vật sống khắp nới là các dấu hiệu cho những người có một đức tin vững chắc.');
INSERT INTO "hasan_abdulkarim" VALUES(45,5,'45:5','Và trong việc ban đêm và ban ngày chạy nối đuôi nhau, và trong việc Allah gởi bổng lộc (mưa) từ trên trời xuống mà Ngài dùng để làm sống lại lớp đất đã chết khô và trong việc quay chiều gió, là các dấu hiệu cho những người thông hiểu.');
INSERT INTO "hasan_abdulkarim" VALUES(45,6,'45:6','Đó là các câu Kinh của Allah mà TA đã đọc cho Ngươi bằng sự thật. Thế, chúng sẽ tin nơi lời trình bày nào sau Allah và các câu Kinh của Ngài?');
INSERT INTO "hasan_abdulkarim" VALUES(45,7,'45:7','Khốn khổ thay cho từng tên láo khoét tội lỗi,');
INSERT INTO "hasan_abdulkarim" VALUES(45,8,'45:8','Nghe rõ các Lời Mặc Khải của Allah đã được đọc cho y rồi cứ tiếp tục tỏ thái độ ngạo mạn làm như y chẳng hề nghe qua nó bao giờ. Thế, hãy báo cho y biết về một sự trừng phạt đau đớn (nơi Cõi đời sau).');
INSERT INTO "hasan_abdulkarim" VALUES(45,9,'45:9','Và khi học được điều gì từ các Lời Mặc Khải của TA, y mang chúng ra làm trò cười. Những kẻ đó sẽ nhận một sự trừng phạt nhục nhã.');
INSERT INTO "hasan_abdulkarim" VALUES(45,10,'45:10','Trước mặt chúng là hỏa ngục; và bất cứ điều gì mà chúng đã kiếm được sẽ không giúp ích gì được cho chúng và những kẻ mà chúng nhận làm vị bảo hộ ngoài Allah cũng sẽ không giúp ích gì được cho chúng. Và chúng sẽ chịu một sự trừng phạt vô cùng khủng khiếp.');
INSERT INTO "hasan_abdulkarim" VALUES(45,11,'45:11','(Kinh Qur''an) này là một Chỉ Đạo. Và những ai không có đức tin nơi các Lời Mặc Khải của Thượng Đế của họ thì sẽ nhận một sự trừng phạt đau đớn từ một hình phạt vô cùng ghê rợn.');
INSERT INTO "hasan_abdulkarim" VALUES(45,12,'45:12','Allah là Đấng đã chế ngự biển cả cho các ngươi để cho tàu bè lướt sóng chạy trên đó theo sự Chỉ huỵ của Ngai và để giúp các ngươi tìm kiếm thiên lộc của Ngài và đe cho các ngươi có dịp tạ ơn.');
INSERT INTO "hasan_abdulkarim" VALUES(45,13,'45:13','Và Ngài đã chế ngự cho các ngươi (sử dụng) bất cứ vật gì trong các tầng trời và bất cứ vật gì dưới đất, toàn bộ đều từ Ngài cả. Quả thật, trong đó là các dấu hiệu cho những người biết ngẫm nghĩ.');
INSERT INTO "hasan_abdulkarim" VALUES(45,14,'45:14','Hãy bảo những ai có đức tin nên tha thứ cho những người tuyệt vọng về những Ngày (Phán Xử) của Allah, rằng Ngài sẽ đáp đền (ân oán) cho từng nhóm người chiếu theo điều mà họ đã từng làm ra.');
INSERT INTO "hasan_abdulkarim" VALUES(45,15,'45:15','Ai làm điều thiện thì bản thân y sẽ được phúc; và ai làm điều ác thì bản thân y sẽ chịu họa. Rồi các ngươi sẽ được đưa trở về gặp Ngài (để chịu sự Phán Xử).');
INSERT INTO "hasan_abdulkarim" VALUES(45,16,'45:16','Và quả thật TA đã ban cho con cháu của Israel Kinh Sách và luật lệ và việc tiếp thu thiên khải và cung dưỡng họ với lương thực tốt và sạch và đã ưu đãi họ hơn thiên hạ.');
INSERT INTO "hasan_abdulkarim" VALUES(45,17,'45:17','Và TA đã ban cho họ những bằng chứng rõ rệt về mọi công việc (của tôn giáo). Nhưng chỉ từ sau lúc tiếp thu được sự hiểu biết, họ mới đâm ra ganh tị và chia rẽ nhau (thành giáo phái). Quả thật, Allah sẽ giải quyết trường hợp của họ vào Ngày phục sinh về những điều mà họ đã từng tranh chấp.');
INSERT INTO "hasan_abdulkarim" VALUES(45,18,'45:18','Rồi TA đặt Ngươi (Muhammad) trên con đường (luật pháp) của công việc (tôn giáo). Bởi thế, hãy tuân theo nó và chớ tuân theo những điều ước muốn của những kẻ không biết gì.');
INSERT INTO "hasan_abdulkarim" VALUES(45,19,'45:19','Quả thật, chúng chẳng giúp đỡ được Ngươi thoát khỏi (sự trừng phạt của) Allah. Và những kẻ làm điều sai quấy là bạn bè tán trợ lẫn nhau. Ngược lại, Allah là Đấng Bảo Vệ của những người ngay chính sợ Allah.');
INSERT INTO "hasan_abdulkarim" VALUES(45,20,'45:20','(Qur''an) này là một Soi Rọi cho nhân loại và một Chỉ Đạo (hướng dẫn) và một Hồng Ân cho những người có đức tin vững chãi.');
INSERT INTO "hasan_abdulkarim" VALUES(45,21,'45:21','Hoặc phải chăng những kẻ làm điều tội lỗi nghĩ rằng TA sẽ làm cho họ ngang bằng với những người có đức tin và làm việc thiện về việc sống và chết của họ hay sao? Thật tệ thay điều chúng xét đoán!');
INSERT INTO "hasan_abdulkarim" VALUES(45,22,'45:22','Và vì Chân Lý Allah đã tạo hóa các tầng trời và trái đất, (mục đích) để cho mỗi linh hồn sẽ được đền trả (xứng đáng) về những điều (tốt và xấu) mà họ đã làm ra và sẽ không bị đối xử bất công.');
INSERT INTO "hasan_abdulkarim" VALUES(45,23,'45:23','Thế Ngươi có thấy kẻ đã lấy lòng ham muốn của y làm thần linh của y hay không? Biết thế, nên Allah để mặc cho y lạc hướng và Ngài niêm kín cái nghe (thính giác) và quả tim (tấm lòng) của y và lấy tấm màn che mắt (thị giác) của y lại. Thế ai sẽ hướng dẫn y sau (khi) Allah (từ chối y?) Thế, các ngươi không ghi nhớ hay sao?');
INSERT INTO "hasan_abdulkarim" VALUES(45,24,'45:24','Và chúng bảo: “Chẳng có đời sống nào khác hơn đời sốing trần tục này. Chúng tôi chết và chúng tôi sống. Chỉ có thời gian giết chết chúng tôi thôi.” Chúng không có một chút hiểu biết nào về điều đó. Chúng chỉ phỏng đoán.');
INSERT INTO "hasan_abdulkarim" VALUES(45,25,'45:25','Và khi các Lời Mặc Khải rõ rệt của TA được đọc ra cho chúng (về sự phục sinh), luận điệu chống chế của chúng chỉ gồm có lời này: “Nếu quí vị (Sứ Giả) nói sự thật thì hãy đưa ông bà tổ tiên của chúng tôi trở lại (trần gian) xem!”');
INSERT INTO "hasan_abdulkarim" VALUES(45,26,'45:26','Hãy bảo chúng: “Allah làm cho các người sống, rồi làm cho các người chết, rồi sẽ tập trung các người vào Ngày phục sinh điều mà không có chi phải nghi ngờ cả.” Nhưng đa số nhân loại không biết.');
INSERT INTO "hasan_abdulkarim" VALUES(45,27,'45:27','Allah nắm quyền thống trị các tầng trời và trái đất, và vào Ngày mà Giờ Tận Thế sẽ được thiết lập, Ngày đó những kẻ làm điều gian dối sẽ mất mát.');
INSERT INTO "hasan_abdulkarim" VALUES(45,28,'45:28','Và Ngươi sẽ thấy mỗi cộng đồng sẽ quỳ gối xuống; mỗi cộng đồng sẽ được gọi đến xem quyển sổ (hành động) của mình: "Ngày này các người sẽ được đền trả xứng đáng về những điều mà các người đã từng làm."');
INSERT INTO "hasan_abdulkarim" VALUES(45,29,'45:29','“Quyển sổ này của TA sẽ nói sự thật về các ngươi. Quả thật, TA đã cho ghi chép lại những điều các ngươi đã từng làm.”');
INSERT INTO "hasan_abdulkarim" VALUES(45,30,'45:30','Bởi thế, đối với những ai có đức tin và làm việc thiện, Thượng Đế của họ sẽ khoan dung họ. Đó là một thắng lợi rạng rỡ.');
INSERT INTO "hasan_abdulkarim" VALUES(45,31,'45:31','Ngược lại, đối với những ai phủ nhận đức tin, (thì sẽ có lời bảo chúng): “Há các Lời Mặc Khải của TA đã không được đọc nhắc các ngươi hay sao? nhưng các ngươi đã tỏ ra ngạo mạn và các ngươi là một đám người tội lỗi."');
INSERT INTO "hasan_abdulkarim" VALUES(45,32,'45:32','Và khi có lời bảo (chúng): “Quả thật Lời Hứa của Allah là sự Thật và Giờ Tận Thế (cũng thật), không có chi phải nghi ngờ cả thì các người thường bảo: ''Chúng tôi không biết Giờ Tận Thế là gì. Chúng tôi nghĩ đó chỉ là điều tưởng tượng. Vì thế, chúng tôi không có một đức tin vững chắc về nó.''”');
INSERT INTO "hasan_abdulkarim" VALUES(45,33,'45:33','Và tội lỗi của những điều mà chúng đã từng làm sẽ lộ ra cho chúng thấy và những điều mà chúng đã từng chế giễu sẽ vây hãm chúng lại.');
INSERT INTO "hasan_abdulkarim" VALUES(45,34,'45:34','Và sẽ có lời bảo (chúng): “Ngày nay, sẽ bỏ quên các ngươi giống như việc các ngươi đã từng lãng quên việc gặp gỡ Ngày này của các ngươi. Và chỗ ở của các ngươi sẽ là hỏa ngục và các ngươi sẽ không có ai giúp đỡ.”');
INSERT INTO "hasan_abdulkarim" VALUES(45,35,'45:35','Sở dĩ như thế là vì các ngươi thường chế giễu các Lời Mặc Khải của Allah và đời sống trần tục này đã đánh lừa các ngươi.” Bởi thế, vào Ngày đó chúng sẽ không được đưa ra khỏi nơi đó (hỏa ngục) và chúng sẽ không còn có dịp trở lại trần gian để sám hối và sửa mình.');
INSERT INTO "hasan_abdulkarim" VALUES(45,36,'45:36','Do đó, mọi lời ca tụng đều dâng lên Allah, Thượng Đế (Đấng Chủ Tể) của các tầng trời và Thượng Đế của trái đất, Thượng Đế của vũ trụ và muôn loài.');
INSERT INTO "hasan_abdulkarim" VALUES(45,37,'45:37','Và Ngài nắm Uy quyền trong các tầng trời và trái đất; và Ngài là Đấng Toàn Năng, Đấng Chí Minh.');
INSERT INTO "hasan_abdulkarim" VALUES(46,1,'46:1','Ha. Mim.');
INSERT INTO "hasan_abdulkarim" VALUES(46,2,'46:2','Kinh Sách (Qur''an) do Allah, Đấng Toàn Năng, Đấng Chí Minh ban xuống.');
INSERT INTO "hasan_abdulkarim" VALUES(46,3,'46:3','Và TA đã tạo các tầng trời và trái đất và vạn vật giữa trời đất chỉ vì Chân Lý và trong một thời gian nhất định. Và những kẻ không có đức tin đã lánh xa điều mà họ đã được cảnh cáo.');
INSERT INTO "hasan_abdulkarim" VALUES(46,4,'46:4','Hãy bảo chúng: “Các người có thấy những kẻ mà các người khấn vái ngoài Allah chăng? Hãy chỉ cho ta vật gì của trái đất mà chúng đã tạo? hoặc chúng có một phần chia nào trong các tầng trời hay không? hãy mang đến cho ta một Kinh Sách trước quyển này; hoặc một vài di tích hiểu biết mà các người đã thừa hưởng nếu các người nói thật?');
INSERT INTO "hasan_abdulkarim" VALUES(46,5,'46:5','Và còn ai lầm lạc hơn kẻ khấn vái ngoài Allah những kẻ (thần linh) sẽ không trả lời y được cho đến Ngày phục sinh và chính chúng cũng không biết việc người ta đang khấn vái chúng?');
INSERT INTO "hasan_abdulkarim" VALUES(46,6,'46:6','Và khi nhân loại sẽ được tập trung lại (vào Ngày phục sinh), chúng (các thần linh) sẽ trở thành kẻ thù của họ và sẽ phủ nhận việc họ tôn thờ chúng.');
INSERT INTO "hasan_abdulkarim" VALUES(46,7,'46:7','Và khi các Lời Mặc Khải rõ rệt của TA đã được đọc ra cho chúng, những kẻ vô đức tin nói về Sự Thật (Qur''an) sau khi nó đến tai chúng như sau: “Đây là trò phù thủy rõ rệt!”');
INSERT INTO "hasan_abdulkarim" VALUES(46,8,'46:8','Hoặc chúng nói: “Y (Muhammad) đã bịa đặt Nó (Qur''an)”? Hãy bảo chúng: “Nếu ta đã bịa đặt Nó thì các người sẽ không tài nào giúp ta thoát khỏi (hình phạt của) Allah. Ngài biết rõ điều mà các người nói về Nó. Riêng Ngài thôi đủ làm một nhân chứng giữa ta và các người. Và Ngài là Đấng Hằng Tha Thứ, Rất Mực Khoan Dung.”');
INSERT INTO "hasan_abdulkarim" VALUES(46,9,'46:9','Hãy bảo chúng: “Ta không phải là một người bịa đặt điều mới trong số các Sứ Giả (của Allah), ta cũng không biết điều gì sẽ xảy đến cho ta và cho các người. Thật sự, ta chỉ tuân theo điều đã được mặc khải cho ta và ta chỉ là một Người báo trước công khai.”');
INSERT INTO "hasan_abdulkarim" VALUES(46,10,'46:10','Hãy bảo chúng: “Các người có nhận thấy chăng, nếu (Qur''an này) do Allah ban xuống và các người phủ nhận Nó, trong lúc một nhân chứng trong con cháu của Israel (như Abdullah bin Salam) xác nhận điều tương tự (ghi trong Kinh Sách của họ - Kinh Tawrah) cho nên y tin tưởng (và theo Islam), trong lúc các người lại ngạo mạn. Chắc chắn, Allah không hướng dẫn đám người làm điều sai quấy.”');
INSERT INTO "hasan_abdulkarim" VALUES(46,11,'46:11','Và những kẻ vô đức tin nói về những người có đức tin như sau: “Nếu nó (Islam) là một điều tốt thì những người (Muslim nghèo khó) này đã không gia nhập nó trước bọn ta.” và khi chúng không muốn để cho Nó (Qur''an) hướng dẫn chúng, chúng bảo: “Đây là một điều láo khoét từ xa xưa.”');
INSERT INTO "hasan_abdulkarim" VALUES(46,12,'46:12','Và trước Nó (Qur''an) là Kinh Sách của Musa (Kinh Tawrah) được xem như một Chỉ Đạo và một Hồng Ân. Và (Qur’an) này là một Kinh Sách bằng tiến Ả Rập xác nhận lại (sự Thật nơi Kinh Tawrãh của Mũsa) dùng đe cảnh cáo những ai làm điều sai quấy, và báo tin mừng cho những người làm tốt.');
INSERT INTO "hasan_abdulkarim" VALUES(46,13,'46:13','Quả thật, những ai nói: “Thượng Đế chúng tôi là Allah,” rồi kiên quyết đứng vững thì sẽ không lo sợ cũng sẽ không buồn phiền.');
INSERT INTO "hasan_abdulkarim" VALUES(46,14,'46:14','Họ sẽ là những người Bạn của thiên đàng, nơi mà họ sẽ vào ở trong đó (đời đời), một phần thưởng về những điều (thiện) mà họ đã từng làm (trên thế gian).');
INSERT INTO "hasan_abdulkarim" VALUES(46,15,'46:15','Và TA đã truyền cho con người nên hiếu thảo và ân cần với cha mẹ của mình. Mẹ của y đã cưu mang y nặng nhọc và hạ sanh y trong đau đớn; thời gian cưu mang và dứt sữa y là ba mươi tháng. Mãi cho đến khi y trưởng thành và được bốn mươi tuổi, y cầu nguyện, thưa: “Lạy Thượng Đế của bề tôi! Xin Ngài ban cho bề tôi khả năng và sức mạnh để bề tôi có thể đền đáp về các Ân huệ mà Ngài đã ban cho bề tôi và cho cha mẹ của bề tôi và để cho bề tôi có thể làm điều thiện được Ngài chấp nhận. Và xin Ngài nhân từ với bề tôi về con cháu của bề tôi. Quả thật, bề tôi quay về sám hối với Ngài và là một người Muslim (thần phục Ngài)."');
INSERT INTO "hasan_abdulkarim" VALUES(46,16,'46:16','Họ sẽ là những người nằm trong cư dân của thiên đàng mà TA sẽ thu nhận về công đức tốt nhất mà họ đã làm và bỏ qua những việc làm xấu xa của họ. Đây là Lời Hứa chân thật đã được hứa với họ (trên thế gian).');
INSERT INTO "hasan_abdulkarim" VALUES(46,17,'46:17','Và có kẻ nói tiếng "Uf" (âm thanh tỏ vẻ bực mình) với cha mẹ của mình, bảo: “Có phải hai ông bà đã (dọa) tôi hứa rằng tôi sẽ được đưa ra (khỏi mộ) trở lại trong lúc những thế hệ đã qua đời trước tôi (đâu có ai sống lại)?” Và hai ông bà cầu xin Allah giúp đỡ (và rầy con:) “Khổ thân con đấy! Con phải tin tưởng! Quả thật, Lời Hứa của Allah là sự Thật.” Nhưng y nói: “Điều này chẳng qua chỉ là chuyện cổ tích mà thôi.”');
INSERT INTO "hasan_abdulkarim" VALUES(46,18,'46:18','Đó là những kẻ mà lời (trừng phạt) đối với chúng đã được xác minh đúng sự thật nơi những cộng đồng của loài Jinn và loài người đã qua đời trước chúng. Chúng quả thật là những kẻ sẽ mất mát nhất (ở đời sau).');
INSERT INTO "hasan_abdulkarim" VALUES(46,19,'46:19','Và tất cả sẽ được ban cho cấp bậc tùy theo công việc mà họ đã làm và để Ngài (Allah) đền trả lại họ đầy đủ về việc làm của họ; và họ sẽ không bị đối xử bất công.');
INSERT INTO "hasan_abdulkarim" VALUES(46,20,'46:20','Và vào Ngày mà những kẻ vô đức tin sẽ được đưa đến trước hỏa ngục và (có lời bảo chúng): “Các người đã tiêu phí các món vật tốt trong đời của các người nơi thế gian và các người đã vui hưởng chúng. Bởi thế, Ngày nay các người sẽ được đền bù lại bằng hình phạt nhục nhã vì lý do các người đã tỏ ra ngạo mạn trên mặt đất, bất chấp lẽ phải và sự thật và bởi vì các người đã bất tuân và dấy loạn.”');
INSERT INTO "hasan_abdulkarim" VALUES(46,21,'46:21','Và hãy nhớ (Hud), người anh em của bộ lạc ''Ad khi Y cảnh báo người dân của Y sống nơi vùng cồn cát (ở phía Nam của bán đảo Ả Rập); và trước và sau (thời kỳ của) Y đã có những Người báo trước đến bảo họ: “Hãy thờ phụng riêng Allah thôi. Thật sự, ta sợ giùm cho các người về sự trừng phạt của một Ngày Vĩ Đại.”');
INSERT INTO "hasan_abdulkarim" VALUES(46,22,'46:22','Họ đáp: “Có phải ông đến gặp chúng tôi để bắt chúng tôi bỏ các thần linh của chúng tôi? Thế hãy mang điều (hình phạt) mà ông đã hăm dọa chúng tôi đến cho chúng tôi thấy đi nếu ông nói thật?”');
INSERT INTO "hasan_abdulkarim" VALUES(46,23,'46:23','(Hud) bảo: “Chỉ riêng Allah biết rõ (Giờ khắc mà) tai họa (sẽ xảy đến cho các người). Và ta chỉ truyền đạt cho các người điều (trừng phạt) mà ta được cử mang đến. Nhưng ta thấy các người là một đám người thật ngu xuẩn.”');
INSERT INTO "hasan_abdulkarim" VALUES(46,24,'46:24','Nhưng khi thấy nó (hình phạt) đang tiến đến thung lũng của họ như một đám mây dày đặc, họ bảo: “Đám mây này sẽ ban cho chúng ta nước mưa.” (Hud bảo:) “Không, đó là (hình phạt) mà các người thúc giục cho mau đến! - một trận gió mang một sự trừng phạt đau đớn bay đến.');
INSERT INTO "hasan_abdulkarim" VALUES(46,25,'46:25','Tàn phá mọi vật theo Mệnh Lệnh của Thượng Đế của nó. Bởi thế, sáng hôm sau chẳng còn thấy gì ngoài những căn nhà (đổ nát) của họ. TA báo oán đám người tội lỗi đúng như thế.');
INSERT INTO "hasan_abdulkarim" VALUES(46,26,'46:26','Và chắc chắn TA đã định cư họ trong sự (phồn thịnh) mà TA đã không dùng để định cư các người (hỡi đám người Quraish!) và TA đã ban cho họ thính giác, thị giác và tấm lòng (cùng trí tuệ), nhưng thính giác, thị giác và tấm lòng của họ chẳng giúp ích gì được cho họ khi họ tiếp tục bài bác các Lời Mặc Khải của Allah và họ sẽ bị vây hãm kín mít bởi các điều mà họ đã từng chế giễu.');
INSERT INTO "hasan_abdulkarim" VALUES(46,27,'46:27','Và quả thật, TA đã tàn phá những thị trấn xung quanh các người và đã trình bày các Dấu hiệu bằng nhiều cách để các người có dịp trở lại (nẻo chánh).');
INSERT INTO "hasan_abdulkarim" VALUES(46,28,'46:28','Thế tại sao những kẻ mà chúng nhận làm những thần linh ngoài Allah, một hình thức đưa họ đến gần Ngài không đến giúp họ đi? Không, chúng đã bỏ họ đi mất dạng. Và đó chỉ là lời nói dối của họ và là một điều mà họ đã bịa đặt.');
INSERT INTO "hasan_abdulkarim" VALUES(46,29,'46:29','Và khi TA (Allah) quay một nhóm (ba đến mười con) Jinn hướng về phía Ngươi (Muhammad) và lắng nghe (Ngươi) đọc Qur''an. Và khi chúng hiện diện nơi đó, chúng bảo nhau: “Hãy im lặng nghe (Qur''an)!” Bởi thế, khi cuộc xướng đọc chấm dứt, chúng trở về cảnh báo người dân của chúng.');
INSERT INTO "hasan_abdulkarim" VALUES(46,30,'46:30','Chúng bảo: “Hỡi người dân chúng tôi! Chúng tôi đã nghe đọc một Kinh Sách đã được ban xuống sau Musa, xác nhận lại điều đã có trước Nó, hướng dẫn đến Chân Lý và Chính Đạo (Islam)."');
INSERT INTO "hasan_abdulkarim" VALUES(46,31,'46:31','"Hỡi người dân chúng tôi! Hãy đáp lại Người mời gọi của Allah (Nabi Muhammad) và tin tưởng nơi Người; Ngài (Allah) sẽ tha thứ tội lỗi của quí vị cho quí vị và giải cứu quí vị khỏi sự trừng phạt đau đớn.');
INSERT INTO "hasan_abdulkarim" VALUES(46,32,'46:32','Và ai không đáp lại Người mời gọi của Allah thì sẽ không trốn thoát khỏi (Allah) được trên trái đất cũng sẽ không có các vị bảo hộ nào ngoài Allah cả. Họ đang lầm lạc rõ rệt.”');
INSERT INTO "hasan_abdulkarim" VALUES(46,33,'46:33','Há họ không nhận thấy rằng Allah, Đấng đã tạo hóa các tầng trời và trái đất và không bao giờ mỏi mệt bởi việc tạo hóa chúng, sẽ thừa khả năng làm cho người chết sống lại hay sao? Vâng, quả thật, Ngài thừa quyền năng làm được tất cả mọi vật.');
INSERT INTO "hasan_abdulkarim" VALUES(46,34,'46:34','Và vào Ngày mà những kẻ vô đức tin sẽ được dắt đến trước hỏa ngục với câu hỏi: “Há cái này không thật hay sao?” Chúng sẽ thưa: “Lạy Thượng Đế của bầy tôi, Vâng ạ!” (Allah) phán: “Thế hãy nếm sự trừng phạt vì tội các ngươi đã từng phủ nhận đức tin.”');
INSERT INTO "hasan_abdulkarim" VALUES(46,35,'46:35','Do đó, hãy kiên nhẫn như các Sứ Giả đầy cương nghị (trước Ngươi) đã từng chịu đựng và chớ nóng lòng về chúng (những kẻ vô đức tin). Bởi vì vào Ngày mà chúng nhìn thấy điều (trừng phạt) đã được hứa với chúng, chúng sẽ có cảm tưởng như đã ở lại trên trái đất chỉ một giờ trong một ngày. (Qur''an là) một Thông điệp. Thế phải chăng chỉ đám người bất tuân mới bị tiêu diệt hay sao?');
INSERT INTO "hasan_abdulkarim" VALUES(47,1,'47:1','Những ai không tin tưởng và cản trở (người khác) không cho theo con đường của Allah là những kẻ mà việc làm sẽ trở thành vô nghĩa.');
INSERT INTO "hasan_abdulkarim" VALUES(47,2,'47:2','Còn những ai có đức tin và làm việc thiện và tin tưởng nơi những điều (mặc khải) đã được ban xuống cho Muhammad bởi lẽ đó là Chân Lý từ Thượng Đế (Allah) của họ thì sẽ được Ngài (Allah) xóa bỏ mọi điều xấu xa (tội lỗi) và cải thiện điều kiện của họ.');
INSERT INTO "hasan_abdulkarim" VALUES(47,3,'47:3','Sở dĩ như thế là vì những kẻ vô đức tin tuân theo điều giả dối, và những người có đức tin tuân theo Chân Lý từ Thượng Đế của họ. Và Allah trình bày những hình ảnh của họ làm thí dụ cho nhân loại đúng như thế.');
INSERT INTO "hasan_abdulkarim" VALUES(47,4,'47:4','Do đó, khi các ngươi giao chiến với những kẻ không tin, hãy đập vào cổ của chúng cho đến khi các ngươi thắng được chúng, rồi trói chặt chúng lại; sau đó, hoặc trả tự do hoặc bắt chúng chuộc mạng cho đến khi chiến tranh trút bỏ gánh nặng của nó. (Allah truyền lệnh cho các ngươi) như thế. Và nếu Allah muốn thì đích thân Ngài sẽ trừng phạt chúng. Nhưng Ngài muốn dùng người này để thử thách người kia. Và những ai bị giết vì con đường của Allah, thì chắc chắn Ngài sẽ không bao giờ làm phí mất công lao của họ.');
INSERT INTO "hasan_abdulkarim" VALUES(47,5,'47:5','Ngài sẽ sớm hướng dẫn và cải thiện điều kiện của họ;');
INSERT INTO "hasan_abdulkarim" VALUES(47,6,'47:6','Và Ngài sẽ thu nhận họ vào thiên đàng mà Ngài sẽ cho họ biết.');
INSERT INTO "hasan_abdulkarim" VALUES(47,7,'47:7','Hỡi những ai có đức tin! Nếu các ngươi giúp (Chính Nghĩa của) Allah thì Ngài sẽ giúp các người trở lại và làm cho bàn chân của các ngươi đứng vững chắc.');
INSERT INTO "hasan_abdulkarim" VALUES(47,8,'47:8','Và những kẻ không tin sẽ bị tiêu diệt và (Allah) sẽ triệt tiêu việc làm của chúng.');
INSERT INTO "hasan_abdulkarim" VALUES(47,9,'47:9','Như thế là vì chúng ghét những điều (mặc khải) mà Allah đã ban xuống. Cho nên Ngài đã biến việc làm của chúng thành vô nghĩa.');
INSERT INTO "hasan_abdulkarim" VALUES(47,10,'47:10','Há chúng không đi xa trên trái đất để xét xem kết cuộc (tàn khốc) của những kẻ sống trước chúng như thế nào ư? Allah đã tiêu diệt chúng. Và những kẻ không có đức tin sẽ không tránh khỏi số phận tương tự.');
INSERT INTO "hasan_abdulkarim" VALUES(47,11,'47:11','Như thế là vì Allah là Đấng Che Chở của những người có đức tin trong lúc những kẻ không có đức tin không có Đấng Che Chở nào cả.');
INSERT INTO "hasan_abdulkarim" VALUES(47,12,'47:12','Quả thật, Allah sẽ thu nhận những người có đức tin và làm việc thiện vào những thiên đàng phía dưới có các dòng sông chảy; còn những kẻ không có đức tin thì huởng lạc (ở đời này); và chúng ăn uống như thú vật; và chỗ ở của chúng sẽ là hỏa ngục.');
INSERT INTO "hasan_abdulkarim" VALUES(47,13,'47:13','Có bao nhiêu thị trấn phú cường hơn thị trấn (Makkah) của Ngươi (Muhammad), quê hương đã trục xuất Ngươi đi nơi khác, đã bị TA tiêu diệt? Bởi thế, không có ai cứu chúng cả.');
INSERT INTO "hasan_abdulkarim" VALUES(47,14,'47:14','Thế một người có được một bằng chứng rõ rệt từ Thượng Đế của y có giống với một kẻ mà việc làm tội lỗi của hắn tỏ ra hấp dẫn đối với hắn và tuân theo dục vọng của hắn hay không?');
INSERT INTO "hasan_abdulkarim" VALUES(47,15,'47:15','Hình ảnh của thiên đàng đã được hứa với những người ngay chính sợ Allah sẽ là nơi có những dòng sông nước trong vắt không hôi tanh, những dòng sông sữa tươi mà mùi vị không thay đổi, những dòng sông rượu mà người uống cảm thấy sảng khoái, và những dòng sông mật ong tinh khiết; có đủ loại trái cây ngon ngọt và sự Tha thứ từ Thượng Đế của họ. (Người ở trong thiên đàng) có giống với một kẻ đời đời ở trong hỏa ngục và uống một loại nước cực sôi cắt đứt ruột của y thành từng đoạn hay không?');
INSERT INTO "hasan_abdulkarim" VALUES(47,16,'47:16','Trong bọn chúng có người đến nghe Ngươi (Muhammad) (truyền giảng); cuối cùng khi chúng rời khỏi Ngươi, chúng nói với những ai đã được ban cho sự hiểu biết (như sau:) “Điều mà Y vừa nói là gì vậy?” Chúng là những kẻ mà Allah đã niêm phong quả tim (tấm lòng) và chỉ biết theo dục vọng của mình.');
INSERT INTO "hasan_abdulkarim" VALUES(47,17,'47:17','Ngược lại, những ai được hướng dẫn thì sẽ được Allah gia tăng thêm Chỉ Đạo và được ban cho lòng sùng kính và sợ Allah.');
INSERT INTO "hasan_abdulkarim" VALUES(47,18,'47:18','Thế phải chăng chúng chỉ chờ đợi Giờ Tận Thế? E rằng nó sẽ xảy đến cho chúng bất ngờ. Nhưng quả thật đã xảy ra những điềm báo trước của nó. Và khi nó thực sự xảy ra thì sự tưởng nhớ của chúng có mang (lợi) gì đến cho chúng?');
INSERT INTO "hasan_abdulkarim" VALUES(47,19,'47:19','Bởi thế, nên biết rằng quả thật, không có Thượng Đế nào khác duy chỉ Allah thôi. Và hãy xin (Ngài) tha thứ tội của Ngươi (Muhammad) và tha thứ cho các tín đồ nam và nữ. Và Allah biết rõ việc các ngươi di chuyển và nơi chốn nghỉ ngơi của các ngươi.');
INSERT INTO "hasan_abdulkarim" VALUES(47,20,'47:20','Và những ai có đức tín bảo: “Tại sao không có một chương kinh nào được ban xuống cho chúng tôi?” Nhưng khi có một chương đề cập cụ thể vấn đề được ban xuống trong đó có nói đến việc chiến đấu thì Ngươi sẽ thấy những kẻ mang sẵn trong lòng một chứng bệnh (giả tạo đức tin) sẽ đảo mắt nhìn Ngươi giống như cái nhìn của một người thất thần sắp chết; nhưng đáng đời cho chúng.');
INSERT INTO "hasan_abdulkarim" VALUES(47,21,'47:21','Vâng lệnh (Allah) và lời nói lịch sự (sẽ tốt cho chúng hơn!) Bởi thế, khi vấn đề (chuẩn bị chinh chiến) đã được giải quyết, sau đó nếu chúng chân thật với Allah thì điều đó tốt cho chúng.');
INSERT INTO "hasan_abdulkarim" VALUES(47,22,'47:22','Thế phải chăng, nếu các ngươi quay lưng (phản đạo) thì có lẽ các ngươi sẽ làm điều thối nát trên trái đất và cắt đứt tình máu mủ ruột thịt?');
INSERT INTO "hasan_abdulkarim" VALUES(47,23,'47:23','Chúng là những kẻ mà Allah đã nguyền rủa; cho nên Ngài đã làm cho chúng điếc và mù.');
INSERT INTO "hasan_abdulkarim" VALUES(47,24,'47:24','Há chúng không chịu nghiền ngẫm Qur''an hay là quả tim (tấm lòng) của chúng đã bị khóa chặt (niêm kín)?');
INSERT INTO "hasan_abdulkarim" VALUES(47,25,'47:25','Quả thật, những ai quay lưng phản đạo sau khi đã nhận Chỉ Đạo (của Allah) thì là những kẻ đã bị Shaytan xúi giục và bị nó hứa hão.');
INSERT INTO "hasan_abdulkarim" VALUES(47,26,'47:26','Như thế là vì chúng nói với những kẻ ghét điều (mặc khải) do Allah đã ban xuống (như sau): “Chúng tôi sẽ tuân theo quí vị về một phần công việc.” Nhưng Allah biết điều bí mật của chúng.');
INSERT INTO "hasan_abdulkarim" VALUES(47,27,'47:27','Thế (tình trạng) sẽ ra sao khi các Thiên Thần đến bắt hồn của chúng lúc chết bằng cách đập vào mặt và lưng của chúng?');
INSERT INTO "hasan_abdulkarim" VALUES(47,28,'47:28','Như thế là vì chúng tuân theo điều làm phật lòng Allah và ghét điều làm hài lòng Ngài. Bởi thế, Ngài triệt tiêu việc làm của chúng.');
INSERT INTO "hasan_abdulkarim" VALUES(47,29,'47:29','Phải chăng những ai mang trong lòng một chứng bệnh (giả tạo đức tin) nghĩ rằng Allah sẽ không đưa ra ánh sáng nỗi oán ghét của chúng hay sao?');
INSERT INTO "hasan_abdulkarim" VALUES(47,30,'47:30','Và nếu muốn, TA (Allah) có thể vạch mặt chúng cho Ngươi (Muhammad) thấy; nhưng Ngươi có thể nhận ra chúng qua những nét đặc trưng của chúng. Và chắc chắn, Ngươi nhận ra chúng qua giọng nói của chúng; và Allah biết rõ những điều các ngươi làm.');
INSERT INTO "hasan_abdulkarim" VALUES(47,31,'47:31','Và chắc chắn TA (Allah) sẽ thử thách các ngươi cho đến khi TA biết rõ ai là người trong các ngươi đã chiến đấu anh dũng và kiên trì, và TA sẽ thử thách tình trạng của các ngươi.');
INSERT INTO "hasan_abdulkarim" VALUES(47,32,'47:32','Quả thật, những ai không có đức tin và cản trở (người khác) theo con đường của Allah và chống đối Sứ Giả (của Allah) sau khi đã được hướng dẫn thì sẽ không làm tổn thương Allah một cứ điều gì; ngược lại, Ngài sẽ biến việc làm của chúng thành vô nghĩa.');
INSERT INTO "hasan_abdulkarim" VALUES(47,33,'47:33','Hỡi những ai có đức tin! Hãy tuân lệnh Allah và tuân lệnh Sứ Giả (của Allah) và chớ làm phí mất công lao của các ngươi.');
INSERT INTO "hasan_abdulkarim" VALUES(47,34,'47:34','Quả thật, những ai không có đức tin và cản trở (thiên hạ) xa khỏi con đường của Allah rồi chết như những kẻ vô đức tin thì chắc chắn Allah sẽ không bao giờ tha thứ cho họ.');
INSERT INTO "hasan_abdulkarim" VALUES(47,35,'47:35','Bởi thế, chớ nản lòng và chớ nhút nhát kêu gọi hòa bình trong lúc các ngươi đang nắm ưu thế; và Allah đang ở cùng với các ngươi. Và không bao giờ Allah sẽ làm phí mất công sức của các người.');
INSERT INTO "hasan_abdulkarim" VALUES(47,36,'47:36','Cuộc sống trần tục này chỉ là một cuộc vui chơi và tiêu khiển. Và nếu các ngươi có đức tin và sợ Allah thì Ngài sẽ đãi ngộ các ngươi xứng đáng và không đòi hỏi gì từ tài sản của các ngươi.');
INSERT INTO "hasan_abdulkarim" VALUES(47,37,'47:37','Nếu Ngài hỏi nó (tài sản) từ các ngươi và làm áp lực đối với các ngươi thì các ngươi sẽ keo kiệt ôm giữ nó và Ngài sẽ đưa ra ánh sáng nỗi oán ghét (được giấu kín trong lòng của các ngươi).');
INSERT INTO "hasan_abdulkarim" VALUES(47,38,'47:38','Này các ngươi! Các ngươi là những người được kêu gọi chi dùng (tài sản) cho Chính Nghĩa của Allah nhưng trong các ngươi có một số keo kiệt (ôm giữ của). Và ai keo kiệt thì chỉ keo kiệt bất lợi cho bản thân mình thôi bởi vì Allah Giàu có và Đầy đủ trong lúc các ngươi là những kẻ nghèo khó. Và nếu các ngươi quay lưng (bỏ Islam và không tuân lệnh của Allah) thì Ngài sẽ đưa một đám người khác đến thay thế các ngươi rồi họ sẽ không giống như các ngươi.');
INSERT INTO "hasan_abdulkarim" VALUES(48,1,'48:1','Quả thật, TA (Allah) đã ban cho Ngươi (Muhammad) một thắng lợi rõ rệt.');
INSERT INTO "hasan_abdulkarim" VALUES(48,2,'48:2','Để Allah tha thứ cho Ngươi tội lỗi của Ngươi trong quá khứ và trong tương lai; và hoàn tất Ân huệ của Ngài cho Ngươi và hướng dẫn Ngươi theo Chính Đạo;');
INSERT INTO "hasan_abdulkarim" VALUES(48,3,'48:3','Và Allah đã tận tình giúp đỡ Ngươi bằng một sự giúp đỡ mãnh liệt.');
INSERT INTO "hasan_abdulkarim" VALUES(48,4,'48:4','Ngài là Đấng ban sự bình lặng xuống tấm lòng của những người có đức tin để cho đức tin của họ ngày càng gia tăng; và các lực lượng (Thiên Thần) trên trời và (lực lượng của tín đồ) dưới đất đều là của Allah cả. Và Allah là Đấng Hằng Biết hết, Rất Mực Sáng Suốt.');
INSERT INTO "hasan_abdulkarim" VALUES(48,5,'48:5','Để Ngài có thể thu nhận các nam và nữ tín đồ vào những thiên đàng bên dưới có các dòng sông chảy để vào ở trong đó đời đời; và Ngài sẽ xóa bỏ những điều xấu xa của họ; và điều đó dưới nhãn quan của Allah là một thắng lợi vẻ vang nhất.');
INSERT INTO "hasan_abdulkarim" VALUES(48,6,'48:6','Và để Ngài có thể trừng phạt những tên giả tạo đức tin, nam và nữ, và những người thờ thần tượng, nam và nữ, đã nghĩ xấu về Allah, đến lượt chúng sẽ đền tội; và chúng đón nhận cơn thịnh nộ của Allah, và Ngài sẽ nguyền rủa chúng và chuẩn bị sẵn cho chúng hỏa ngục, một mức đến cuối cùng rất xấu.');
INSERT INTO "hasan_abdulkarim" VALUES(48,7,'48:7','Và các lực lượng trên trời và dưới đất đều là của Allah cả. Và Allah là Đấng Hằng Biết hết tất cả, Rất Mực Cao Minh.');
INSERT INTO "hasan_abdulkarim" VALUES(48,8,'48:8','Quả thật, TA (Allah) đã phái Ngươi (Muhammad) đến làm một Nhân chứng, vừa báo tin mừng vừa cảnh cáo.');
INSERT INTO "hasan_abdulkarim" VALUES(48,9,'48:9','Để các ngươi (hỡi các tín đồ!) có thể tin tưởng nơi Allah và Sứ Giả của Ngài và ủng hộ và tôn vinh Y, và tán dương Ngài (Allah) sáng và chiều.');
INSERT INTO "hasan_abdulkarim" VALUES(48,10,'48:10','Thật sự, những ai tuyên thệ trung thành với Ngươi (tại Hudaybiyah) thì chỉ là tuyên thệ trung thành với Allah ấy thôi. Tay của Allah đặt lên trên tay của họ; bởi thế, ai nuốt lời thề thì là bội ước với chính mình. Ngược lại, ai giữ đúng lời thề với Allah thì sẽ sớm được Ngài ban cho một phần thưởng rất lớn.');
INSERT INTO "hasan_abdulkarim" VALUES(48,11,'48:11','Những người Ả Rập vùng sa mạc trì trệ (trong việc tham chiến) sẽ nói với Ngươi: “Chúng tôi bận lo cho tài sản và gia đình của chúng tôi; hãy cầu xin (Allah) tha thứ cho chúng tôi.” Miệng của chúng nói ra điều chẳng có trong lòng. Hãy bảo chúng: “Thế ai có khả năng can thiệp giúp các người với Allah về bất cứ điều gì nếu Ngài muốn trừng phạt hay tưởng thưởng các người? Không, Allah rất đỗi am tường những điều các người làm."');
INSERT INTO "hasan_abdulkarim" VALUES(48,12,'48:12','“Không, các người nghĩ rằng Sứ Giả (của Allah) và những người có đức tin sẽ không bao giờ trở về gặp gia đình, và điều đó làm cho các người cảm thấy khoan khoái trong lòng và các người đã có tư tưởng xấu; và các người là một đám người sắp bị suy vong.”');
INSERT INTO "hasan_abdulkarim" VALUES(48,13,'48:13','Và ai không tin tưởng nơi Allah và Sứ Giả của Ngài thì quả thật TA đã chuẩn bị cho những kẻ không có đức tin một ngọn Lửa cháy bùng.');
INSERT INTO "hasan_abdulkarim" VALUES(48,14,'48:14','Và Allah nắm quyền thống trị các tầng trời và trái đất. Ngài tha thứ cho người nào Ngài muốn và trừng phạt kẻ nào Ngài muốn. Và Allah Hằng Tha Thứ, Rất Mực Khoan Dung.');
INSERT INTO "hasan_abdulkarim" VALUES(48,15,'48:15','Những người trì trệ (trong việc tham chiến) sẽ nói (với các ngươi) khi thấy các ngươi ra đi thu nhặt chiến lợi phẩm: “Hãy cho chúng tôi theo quí vị.” Chúng muốn thay đổi Lời Phán của Allah. Hãy bảo chúng: “Chớ bao giờ theo chúng tôi! Allah đã phán như thế cho các người từ trước.” Nhưng chúng lại bảo: “Không, quí vị ganh tị với chúng tôi.” Không, chúng chỉ hiểu được chút ít.');
INSERT INTO "hasan_abdulkarim" VALUES(48,16,'48:16','Hãy bảo những người Ả Rập vùng sa mạc trì trệ (trong việc tham chiến): “Các người được kêu gọi đi tham chiến chống một đám người đã từng quen thuộc với chiến tranh dữ dội, hoặc các người chiến thắng chúng hoặc các người đầu hàng. Bởi thế, nếu các người tuân lệnh thì Allah sẽ trọng thưởng các người; và nếu các người quay bỏ đi (đào ngũ) như trường hợp các người đã từng làm trước đây thì Ngài sẽ trừng phạt các người bằng một sự trừng phạt đau đớn.”');
INSERT INTO "hasan_abdulkarim" VALUES(48,17,'48:17','Người mù, người què, và người bệnh không bị khiển trách (nếu không tham chiến). Và ai tuân lệnh Allah và Sứ Giả của Ngài thì sẽ được thu nhận vào thiên đàng bên dưới có các dòng sông chảy; ngược lại, ai quay lưng bỏ đi (đào ngũ) thì sẽ bị phạt bằng một sự trừng phạt đau đớn.');
INSERT INTO "hasan_abdulkarim" VALUES(48,18,'48:18','Quả thật, Allah đã hài lòng với những người có đức tin khi họ tuyên thệ trung thành với Ngươi dưới tàn cây (tại Hudaibiyah) bởi vì Ngài biết điều nằm trong lòng của họ; bởi thế, Ngài đã ban sự bình lặng xuống cho họ; và Ngài ân thưởng họ bằng một cuộc thắng lợi gần kề.');
INSERT INTO "hasan_abdulkarim" VALUES(48,19,'48:19','Và họ sẽ thu nhặt rất nhiều chiến lợi phẩm. Và Allah Toàn Năng, Rất Mực Sáng Suốt.');
INSERT INTO "hasan_abdulkarim" VALUES(48,20,'48:20','Và Allah đã hứa với các ngươi việc các ngươi sẽ tiếp thu rất nhiều chiến lợi phẩm và Ngài đã giục điều này mau đến cho các ngươi, và đã giữ tay của địch xa các ngươi, và để cho nó trở thành một Dấu hiệu cho những người có đức tin, và để Ngài hướng dẫn các ngươi theo Chính Đạo.');
INSERT INTO "hasan_abdulkarim" VALUES(48,21,'48:21','Và kể cả những (thắng lợi và chiến lợi phẩm) khác (mà Allah đã hứa nhưng) chưa thuộc quyền của các ngươi nhưng Allah đã tóm thâu chúng bởi vì Allah có toàn quyền trên tất cả mọi vật.');
INSERT INTO "hasan_abdulkarim" VALUES(48,22,'48:22','Và nếu những kẻ không có đức tin đến tấn công các ngươi thì chắc chắn chúng sẽ quay tháo lui rồi chúng sẽ không tìm được một Đấng Che chở và một Cứu nhân nào để giúp đỡ.');
INSERT INTO "hasan_abdulkarim" VALUES(48,23,'48:23','Đường lối xử lý của Allah đã xảy ra từ trước. Và Ngươi sẽ không bao giờ thấy có một sự thay đổi trong Đường lối của Allah.');
INSERT INTO "hasan_abdulkarim" VALUES(48,24,'48:24','Và Ngài (Allah) là Đấng đã giữ tay của chúng lại không cho tấn công các ngươi và giữ tay của các ngươi không cho tấn công chúng tại thung lũng Makkah sau khi Ngài đã làm cho các ngươi thắng chúng. Và Allah Hằng Thấy những điều các người làm.');
INSERT INTO "hasan_abdulkarim" VALUES(48,25,'48:25','Chúng là những kẻ không có đức tin và cản trở các ngươi không cho vào AL-Masịid Al-Haram (tại Makkah) và giữ những con vật tế lại không cho dắt đến chỗ tế. Và nếu không vì những người có đức tin, nam và nữ, mà các ngươi không nhận ra (sống trà trộn với chúng nơi đó), và rằng các ngươi có thể giết lầm họ và vì thế vô tình các ngươi có thể phạm tội (sát nhân) đối với họ (thì Allah đã cho phép các ngươi tấn công vào Makkah rồi; Nhưng Ngài đã giữ tay của các ngươi lại) để Allah có thể khoan dung người nào Ngài muốn. Nếu họ sống tách biệt thì chắc chắn TA sẽ trừng phạt những kẻ không có đức tin trong họ bằng một sự trừng phạt đau đớn.');
INSERT INTO "hasan_abdulkarim" VALUES(48,26,'48:26','Khi những kẻ không có đức tin bộc lộ trong lòng niềm tự hào và tự cao của Thời kỳ Ngu muội (tại Hudaibiyah) thì ngược lại Allah ban sự trầm tĩnh của Ngài xuống cho Sứ Giả của Ngài và cho những người có đức tin và bắt họ tôn trọng Lời Phán về việc sùng kính và sợ Allah và họ xứng danh và xứng đáng với nó. Và Allah Biết hết tất cả mọi việc.');
INSERT INTO "hasan_abdulkarim" VALUES(48,27,'48:27','Chắc chắn Allah sẽ xác nhận sự thật về sự mục kích( của Sứ Giả của Ngài: chắc chắn các ngươi sẽ vào Al-Masjid al-Haram (tại Makkah) an toàn, đầu cạo trọc và tóc cắt ngắn (theo nghi thức làm Hajj hay ''Umrah), không sợ hãi, nếu Allah muốn. Bởi vì Ngài biết điều mà các ngươi không biết. Và ngoài cái đó, Ngài sẽ ban cho một thắng lợi gần kề.');
INSERT INTO "hasan_abdulkarim" VALUES(48,28,'48:28','Ngài là Đấng đã cử Sứ Giả (Muhammad) của Ngài, mang Chỉ Đạo và Tôn giáo của Chân Lý (Islam) đến để thắng tất cả mọi tôn giáo. Và Allah đủ làm một Nhân Chứng (cho sự thật này).');
INSERT INTO "hasan_abdulkarim" VALUES(48,29,'48:29','Muhammad là Sứ Giả của Allah. Và những ai theo Y thì nghiêm khắc với những kẻ không có đức tin nhưng rất thương xót lẫn nhau. Ngươi sẽ thấy họ cúi mình, quỳ mọp (bái phục), tìm kiếm thiên ân từ Allah và sự hài lòng của Ngài. Dấu vết của họ nổi trên gương mặt của họ qua tì vết của việc phủ phục. Đó là hình ảnh của họ (được mô tả) trong (Kinh) Tawrah) và hình ảnh của họ trong (Kinh) thì như là một hạt giống đâm ra chồi và trở thành cứng, và dày đặc và đứng thẳng trên thân cây của nó, làm đẹp mắt và mát lòng người gieo. Kết quả là nó làm cho những kẻ không có đức tin đâm thù hận họ. Và Allah hứa tha thứ và ban phần thưỏng vĩ đại cho những ai trong họ có đức tin và làm việc thiện.');
INSERT INTO "hasan_abdulkarim" VALUES(49,1,'49:1','Hỡi những ai có đức tin! Chớ qua mặt Allah và Sứ Giả của Ngài. Và hãy sợ Allah. Quả thật, Allah Hằng Nghe và Hằng Biết (mọi việc).');
INSERT INTO "hasan_abdulkarim" VALUES(49,2,'49:2','Hỡi những ai có đức tin! Chớ cất giọng của các ngươi cao hơn giọng nói của Nabi (Muhammad) và chớ nói lớn tiếng với Y khi nói chuyện, giống như việc các ngươi thường ăn nói lớn tiếng với nhau, e rằng việc làm của các ngươi sẽ trở thành vô nghĩa trong lúc các ngươi không nhận thấy điều đó.');
INSERT INTO "hasan_abdulkarim" VALUES(49,3,'49:3','Quả thật, những ai hạ giọng của mình trước mặt Sứ Giả của Allah thì là những người mà tấm lòng đã được Allah rèn luyện cho ngay chính. Họ sẽ được tha thứ và được một phần thưởng rất lớn.');
INSERT INTO "hasan_abdulkarim" VALUES(49,4,'49:4','Quả thật, những ai lớn tiếng gọi Ngươi (Muhammad) từ bên ngoài nội phòng thì đa số đều là những người thiếu suy nghĩ.');
INSERT INTO "hasan_abdulkarim" VALUES(49,5,'49:5','Phải chi họ chịu khó chờ chốc lát cho đến khi Ngươi bước ra ngoài phòng để tiếp họ thì tốt cho họ hơn. Và Allah Hằng Tha Thứ, Rất Mực Khoan Dung.');
INSERT INTO "hasan_abdulkarim" VALUES(49,6,'49:6','Hỡi những ai có đức tin! Nếu có một kẻ gây rối mang tin tức đến báo với các ngươi, hãy kiểm tra nó cho thật kỹ e rằng vì sơ suất, các ngươi có thể làm hại đến người (khác) rồi sau đó đâm ra hối hận về điều các ngươi đã làm.');
INSERT INTO "hasan_abdulkarim" VALUES(49,7,'49:7','Và nên biết rằng Sứ Giả của Allah đang sống với các ngươi. Nếu Y làm theo sở thích của các ngươi về nhiều vấn đề thì chắc chắn các ngươi sẽ gặp nhiều điều phiền phức. Nhưng Allah làm cho đức tin thắm thiết vào tấm lòng của các ngươi và tô điểm nó xinh đẹp trong lòng của các ngươi và làm cho việc vô đức tin, gây rối và bất tuân trở nên đáng ghét đối với các ngươi. Đó là những người được hướng dẫn.');
INSERT INTO "hasan_abdulkarim" VALUES(49,8,'49:8','(Đó là) một thiên sủng và một thiên ân từ Allah. Quả thật, Allah Biết Hết, Rất Cao Minh.');
INSERT INTO "hasan_abdulkarim" VALUES(49,9,'49:9','Và nếu có hai nhóm tín đồ giao chiến, hãy giải hòa giữa đôi bên. Nhưng nếu nhóm này áp bức và lấn át nhóm kia thì hãy đánh nhóm áp bức cho đến khi họ trở về phục tùng Mệnh Lệnh của Allah. Bởi thế, nếu họ chiêu hồi thì hãy giải hòa giữa hai nhóm một cách công bằng và vô tư. Quả thật, Allah yêu thương những người công bằng, vô tư.');
INSERT INTO "hasan_abdulkarim" VALUES(49,10,'49:10','Chỉ những người có đức tin mới là anh em với nhau (trong đạo). Do đó, hãy giải hòa giữa hai anh em của các ngươi. Hãy sợ Allah để may ra các ngươi được khoan dung.');
INSERT INTO "hasan_abdulkarim" VALUES(49,11,'49:11','Hỡi những ai có đức tin! Một đám người này chớ nên cười chê một đám người nọ. Biết đâu đám người này tốt hơn đám người nọ (và ngược lại). Và một số phụ nữ này không nên cười chê một số phụ nữ nọ. Biết đâu nhóm phụ nữ này tốt hơn nhóm phụ nữ nọ (và ngược lại). Chớ nói xấu lẫn nhau và chớ mắng nhiếc nhau bằng cách bêu tên tục (của nhau ra trước công chúng). Bêu tên xấu của một người sau khi y đã tin tưởng là một việc xấu xa. Và ai không chừa bỏ (thói xấu đó) thì là những người làm điều sai quấy.');
INSERT INTO "hasan_abdulkarim" VALUES(49,12,'49:12','Hỡi những ai có đức tin! Hãy tránh nghi kỵ càng nhiều càng tốt. Quả thật, nghi kỵ trong một vai trường hợp là tội. Chớ dọ thám cũng chớ nói xấu lẫn nhau. Phải chăng một trong các ngươi thích ăn thịt của người anh em của mình đã chết hay sao? Bởi thế, nên gớm ghiếc việc đó. Và hãy sợ Allah. Quả thật, Allah Hằng quay lại (Tha thứ), Rất Mực Khoan Dung.');
INSERT INTO "hasan_abdulkarim" VALUES(49,13,'49:13','Hỡi nhân loại! TA đã tạo hóa các ngươi từ một người nam (Adam) và một người nữ (Hauwa) và làm cho các ngươi thành quốc gia và bộ lạc để các ngươi nhận biết lẫn nhau (như anh em). Quả thật, dưới cái Nhìn của Allah, người vinh dự nhất là người ngay chính và sợ Allah nhất trong các ngươi. Quả thật, Allah Biết hết, Rất Am tường (mọi việc).');
INSERT INTO "hasan_abdulkarim" VALUES(49,14,'49:14','Những người Ả Rập (của vùng sa mạc) nói: “Chúng tôi tin tưởng.” Hãy bảo chúng: “Các người chưa có đức tin. Ngược lại hãy nói: "Chúng tôi thần phục (trong Islam), bởi vì đức tin chưa thấm sâu vào tấm lòng của các người. Và nếu các người vâng lệnh Allah và Sứ Giả của Ngài thì Ngài sẽ không giảm bớt điều gì trong việc làm của các ngươi. Quả thật, Allah Hằng Tha Thứ, Rất Mực Khoan Dung."');
INSERT INTO "hasan_abdulkarim" VALUES(49,15,'49:15','Chỉ những ai tin tưởng nơi Allah và Sứ Giả của Ngài rồi không nghi ngờ điều gì và chiến đấu bằng cả tài sản lẫn sinh mạng của họ cho Chính Đạo của Allah mới là những người có đức tin. Họ là những người chân thật.');
INSERT INTO "hasan_abdulkarim" VALUES(49,16,'49:16','Hãy bảo chúng (những kẻ không có đức tin): “Phải chăng các người muốn dạy Allah về tôn giáo của các người trong lúc Allah biết hết mọi vật trong các tầng trời và mọi vật dưới đất hay sao? Và Allah biết hết mọi việc.”');
INSERT INTO "hasan_abdulkarim" VALUES(49,17,'49:17','Chúng xem việc theo Islam của chúng như một ân huệ ban cho Ngươi (Muhammad). Hãy bảo chúng: “Các người chớ xem (việc theo) Islam của các người là một ân huệ dành cho Ta. Không, Allah đã ban ân cho các người trong việc Ngài đã hướng dẫn các người đến với đức tin nếu các người chần thật."');
INSERT INTO "hasan_abdulkarim" VALUES(49,18,'49:18','“Quả thật, Allah biết điều vô hình của các tầng trời và trái đất. Và Allah thấy hết tất cả những điều các người làm.”');
INSERT INTO "hasan_abdulkarim" VALUES(50,1,'50:1','Qaf. Thề bởi (Kinh) Qur''an Quang Vinh.');
INSERT INTO "hasan_abdulkarim" VALUES(50,2,'50:2','Không, chúng ngạc nhiên về việc có một Người báo trước xuất thân từ chúng đến gặp chúng. Bởi thế, những kẻ không cố đức tin lên tiếng: “Đây là một điều lạ lùng!"');
INSERT INTO "hasan_abdulkarim" VALUES(50,3,'50:3','“Có thật chăng, khi chúng tôi chết và thành cát bụi (chúng tôi sẽ sống trở lại ? Nếu thật như thế thì) đó là một sự trở về xa xôi.”');
INSERT INTO "hasan_abdulkarim" VALUES(50,4,'50:4','Chắc chắn TA (Allah) thừa biết việc đất đai ăn tiêu (thân xác của) chúng; nhưng có một quyển sổ bộ còn lưu trữ nơi TA.');
INSERT INTO "hasan_abdulkarim" VALUES(50,5,'50:5','Không, chúng phủ nhận Chân Lý (Qur''an) khi Nó đến với chúng cho nên chúng đâm ra lúng túng (không biết thật hay giả).');
INSERT INTO "hasan_abdulkarim" VALUES(50,6,'50:6','Há chúng không nhìn lên bầu trời bên trên chúng xem TA đã dựng và trang hoàng nó như thế nào và không có một kẽ hở nào trong đó ư?');
INSERT INTO "hasan_abdulkarim" VALUES(50,7,'50:7','Và trái đất mà TA đã trải rộng và dựng trên đó những quả núi vững chắc và làm mọc ra đủ cặp thảo mộc xinh đẹp.');
INSERT INTO "hasan_abdulkarim" VALUES(50,8,'50:8','Một sự quan sát và một sự nhắc nhở cho từng người tôi quay về.');
INSERT INTO "hasan_abdulkarim" VALUES(50,9,'50:9','Và TA ban nước mưa đầy ân phúc từ trên trời xuống mà TA dùng làm mọc ra vườn tược và trái hạt vào mùa gặt.');
INSERT INTO "hasan_abdulkarim" VALUES(50,10,'50:10','Và cây chà là cao lớn đâm ra trái từng chùm lủng lẳng, chồng lên nhau;');
INSERT INTO "hasan_abdulkarim" VALUES(50,11,'50:11','Làm lương thực cho bầy tôi (của Allah). Và TA dùng nó để làm sống lại một mảnh đất đã chết khô. Và việc phục sinh sẽ giống như thế.');
INSERT INTO "hasan_abdulkarim" VALUES(50,12,'50:12','Trước chúng, người dân của Nuh, và Dân cư của Rass, người dân Thamud đã phủ nhận (Chân Lý).');
INSERT INTO "hasan_abdulkarim" VALUES(50,13,'50:13','Và ''Ad, Fir-''awn, và anh em của Lut,');
INSERT INTO "hasan_abdulkarim" VALUES(50,14,'50:14','Dân cư của khu rừng ( Aykah) và người dân của Tubba'', mỗi dân tộc đều cho các Sứ Giả nói dối cho nên lời đe dọa của TA thể hiện.');
INSERT INTO "hasan_abdulkarim" VALUES(50,15,'50:15','Phải chăng TA đã mỏi mệt với việc tạo hóa đầu tiên? Không, chúng nghi ngờ về sự tạo hóa mới (việc phục sinh).');
INSERT INTO "hasan_abdulkarim" VALUES(50,16,'50:16','Và quả thật, TA đã tạo hóa con người và biết điều mà bản thân y thì thào (xúi giục) y bởi vì TA gần kề y hơn tĩnh mạch nơi cổ của y.');
INSERT INTO "hasan_abdulkarim" VALUES(50,17,'50:17','Khi hai vị Ghi Chép (Thiên Thân ghi chép hành động), một vị ngồi bên phải và một vị ngồi bên trái (để sẵn ghi chép).');
INSERT INTO "hasan_abdulkarim" VALUES(50,18,'50:18','Không một lời nào y thốt ra mà vị Theo Dõi (Thiên Thần) bên cạnh lại không lập tức ghi nhận.');
INSERT INTO "hasan_abdulkarim" VALUES(50,19,'50:19','Và khi tình trạng đau đớn của cái chết thực sự xảy ra, (sẽ có lời bảo y): “Đấy là cái mà nhà ngươi cố tránh!”');
INSERT INTO "hasan_abdulkarim" VALUES(50,20,'50:20','Và tiếng Còi sẽ được hụ lên: Đấy là Ngày của đã được hứa.');
INSERT INTO "hasan_abdulkarim" VALUES(50,21,'50:21','Và mỗi người (linh hồn) sẽ bước đến (trình diện). Đi theo y là một (vị Thiên Thần) dẫn đường và một vị (Thiên Thần) làm chứng.');
INSERT INTO "hasan_abdulkarim" VALUES(50,22,'50:22','(Sẽ có lời bảo y): “Nhà ngươi đã lơ là về việc này. Bây giờ TA tháo bỏ tấm màn của ngươi và cặp mắt của ngươi ngày nay sáng tỏ.”');
INSERT INTO "hasan_abdulkarim" VALUES(50,23,'50:23','Và người bạn đồng hành của y thưa: “Đây (hồ sơ) của y có sẵn nơi bề tôi!”');
INSERT INTO "hasan_abdulkarim" VALUES(50,24,'50:24','“Hai ngươi hãy ném từng tên vô đức tin ương ngạnh vào hỏa ngục."');
INSERT INTO "hasan_abdulkarim" VALUES(50,25,'50:25','“Kẻ ngăn cản điều tốt, phạm pháp và nghi ngờ"');
INSERT INTO "hasan_abdulkarim" VALUES(50,26,'50:26','“Kẻ đã dựng một thần linh cùng với Allah. Hãy ném nó vào chỗ trừng phạt khắc nghiệt nhất.”');
INSERT INTO "hasan_abdulkarim" VALUES(50,27,'50:27','Người bạn (Shaytan) của y sẽ thưa: “Lạy Thượng Đế của bầy tôi! (Bề) tôi đã không làm chó y phạm giới. Nhưng tự y lạc hướng xa ấy thôi.”');
INSERT INTO "hasan_abdulkarim" VALUES(50,28,'50:28','(Allah sẽ) phán: “Các ngươi không được cãi vã trước mặt TA. TA đã gởi cho các ngươi Lời Cảnh báo."');
INSERT INTO "hasan_abdulkarim" VALUES(50,29,'50:29','“Lời tuyên án nơi TA không thay đổi; và TA không đối xử bất công với các bầy tôi của TA.”');
INSERT INTO "hasan_abdulkarim" VALUES(50,30,'50:30','Vào Ngày mà TA sẽ phán cho hỏa ngục: “Nhà ngươi đầy hết chỗ hay chưa?” Nó sẽ thưa: “(Bẩm Allah!) Còn nữa không?”');
INSERT INTO "hasan_abdulkarim" VALUES(50,31,'50:31','Và thiên đàng sẽ được mang đến trước mặt những người ngay chính và sợ Allah, không xa.');
INSERT INTO "hasan_abdulkarim" VALUES(50,32,'50:32','“Đây là món đã được hứa với mỗi người biết hối cải và giữ gìn (bản thân)."');
INSERT INTO "hasan_abdulkarim" VALUES(50,33,'50:33','“Người sợ Đấng Al-Rahman vô hình và đến với một tấm lòng phục thiện."');
INSERT INTO "hasan_abdulkarim" VALUES(50,34,'50:34','“Các ngươi hãy bước vào đó (thiên đàng) với sự Bằng an; đó là Ngày của một đời sống vĩnh cửu.”');
INSERT INTO "hasan_abdulkarim" VALUES(50,35,'50:35','Trong đó, họ sẽ có vô vàn món vật mà họ ao ước và còn nhiều món khác nữa nơi TA.');
INSERT INTO "hasan_abdulkarim" VALUES(50,36,'50:36','Và có bao nhiêu thế hệ hùng cường hơn chúng về quyền lực đã bị TA tiêu diệt trước chúng và đã trôi giạt đi khắp nơi trên mặt đất? Có còn chăng một chỗ lánh nạn cho chúng?');
INSERT INTO "hasan_abdulkarim" VALUES(50,37,'50:37','Chắc chắn, trong đó (Qur''an) có một sự nhắc nhở cho ai có một quả tim (biết kính sợ), hoặc lắng tai nghe và là một nhân chứng.');
INSERT INTO "hasan_abdulkarim" VALUES(50,38,'50:38','Và quả thật, TA đã tạo hóa các tầng trời và trái đất và vạn vật giữa hai chúng trong sáu Ngày và TA không hề mỏi mệt.');
INSERT INTO "hasan_abdulkarim" VALUES(50,39,'50:39','Bởi thế, hãy cố gắng chịu đựng với những điều chúng nói và hãy tán dương lời ca tụng Thượng Đế của Ngươi trước khi mặt trời mọc và trước khi nó lặn.');
INSERT INTO "hasan_abdulkarim" VALUES(50,40,'50:40','Và hãy tán dương Ngài suốt một phần của ban đêm (trong các cuộc lễ nguyện Salah Magrib và ''I-sha'') và sau những lần phủ phục.');
INSERT INTO "hasan_abdulkarim" VALUES(50,41,'50:41','Và hãy chú ý nghe vào Ngày mà Người Gọi sẽ lớn tiếng gọi từ một chỗ rất gần.');
INSERT INTO "hasan_abdulkarim" VALUES(50,42,'50:42','Ngày mà chúng sẽ thực sự nghe một Tiếng Gầm (dữ dội). Đó sẽ là Ngày (mà mọi người) sẽ bước ra (khỏi mộ).');
INSERT INTO "hasan_abdulkarim" VALUES(50,43,'50:43','Quả thật, TA là Đấng làm cho sống và gây chết. Và tất cả sẽ trở về gặp TA,');
INSERT INTO "hasan_abdulkarim" VALUES(50,44,'50:44','Ngày mà trái đất sẽ chẻ làm hai, nhả chúng ra để chúng vội chạy đến (tập hợp). Đó là một cuộc tập hợp sẽ thật sự (xảy ra) dễ dàng đối với TA.');
INSERT INTO "hasan_abdulkarim" VALUES(50,45,'50:45','TA biết rõ điều chúng nói; và Ngươi (Muhammad) không là một người cưỡng bách chúng (theo đạo). Bởi thế, hãy dùng Kinh Qur''an mà cảnh báo ai là người sợ Lời Đe dọa của TA.');
INSERT INTO "hasan_abdulkarim" VALUES(51,1,'51:1','Thề bởi những (trận gió) phân tán;');
INSERT INTO "hasan_abdulkarim" VALUES(51,2,'51:2','Và những (đám mây) mang nặng (hạt mưa);');
INSERT INTO "hasan_abdulkarim" VALUES(51,3,'51:3','Và những (chiếc tàu) lướt đi nhẹ nhàng êm ái;');
INSERT INTO "hasan_abdulkarim" VALUES(51,4,'51:4','Và những vị phân phối (thiên lộc và thiên ân) theo Mệnh Lệnh;-');
INSERT INTO "hasan_abdulkarim" VALUES(51,5,'51:5','Điều mà các ngươi đã được hứa chắc chắn sẽ (đến) thật.');
INSERT INTO "hasan_abdulkarim" VALUES(51,6,'51:6','Việc Phán Xử chắc chắn sẽ xảy ra.');
INSERT INTO "hasan_abdulkarim" VALUES(51,7,'51:7','Thề bởi bầu trời đầy các con đường (quỹ đạo),');
INSERT INTO "hasan_abdulkarim" VALUES(51,8,'51:8','Quả thật, các ngươi có nhiều ý kiến khác nhau.');
INSERT INTO "hasan_abdulkarim" VALUES(51,9,'51:9','Lánh xa Nó (Qur''an) là người lánh xa.');
INSERT INTO "hasan_abdulkarim" VALUES(51,10,'51:10','Những kẻ phỏng đoán (và nói dối) bị nguyền rủa:');
INSERT INTO "hasan_abdulkarim" VALUES(51,11,'51:11','Những ai vô tâm, lơ là.');
INSERT INTO "hasan_abdulkarim" VALUES(51,12,'51:12','Chúng hỏi: “Ngày Phán Xử khi nào xảy ra?”');
INSERT INTO "hasan_abdulkarim" VALUES(51,13,'51:13','(Đó là) Ngày mà chúng sẽ bị xử (đốt) trên Lửa.');
INSERT INTO "hasan_abdulkarim" VALUES(51,14,'51:14','Hãy nếm sự trừng phạt này của các ngươi. Đây là cái (hình phạt) ma các ngươi thường hối thúc cho mau đến.');
INSERT INTO "hasan_abdulkarim" VALUES(51,15,'51:15','Quả thật, những người sợ Allah sẽ ở trong các hoa viên và suối nước.');
INSERT INTO "hasan_abdulkarim" VALUES(51,16,'51:16','Hưởng mọi thứ mà Thượng Đế (Allah) của họ ban cho. Quả thật, trước đó họ là những người làm việc nghĩa.');
INSERT INTO "hasan_abdulkarim" VALUES(51,17,'51:17','Ban đêm họ thường ít ngủ.');
INSERT INTO "hasan_abdulkarim" VALUES(51,18,'51:18','Và thường cầu xin (Allah) tha thứ trước hừng đông.');
INSERT INTO "hasan_abdulkarim" VALUES(51,19,'51:19','Và trong tài sản của họ, có phần qui định dành cho người ăn xin và người túng thiếu nhưng không xin xỏ.');
INSERT INTO "hasan_abdulkarim" VALUES(51,20,'51:20','Và trên trái đất có những dấu hiệu cho những người có đức tin vững chãi.');
INSERT INTO "hasan_abdulkarim" VALUES(51,21,'51:21','Và nơi bản thân của các ngươi. Thế các ngươi không nhìn thấy ư?');
INSERT INTO "hasan_abdulkarim" VALUES(51,22,'51:22','Và trên trời có bổng lộc và những thứ mà các ngươi đã được hứa.');
INSERT INTO "hasan_abdulkarim" VALUES(51,23,'51:23','Bởi thế, thề bởi Thượng Đế (Đấng Chủ Tể) của các tầng trời và trái đất, đó là Sự Thật giống như cái (Thực tại) mà các ngươi đang nói.');
INSERT INTO "hasan_abdulkarim" VALUES(51,24,'51:24','Há Ngươi (Muhammad) đã biết câu chuyện về các vị khách vinh dự của Ibrahim hay chưa?');
INSERT INTO "hasan_abdulkarim" VALUES(51,25,'51:25','Khi đến gặp Y, họ chào: “Bằng an” (Ibrahim) đáp: “Bằng an”. Họ là những người lạ mặt.');
INSERT INTO "hasan_abdulkarim" VALUES(51,26,'51:26','Rồi Y quay vào gặp người nhà; sau đó, mang thịt bò con béo ra.');
INSERT INTO "hasan_abdulkarim" VALUES(51,27,'51:27','Yđặt nó trước mặt khách và thưa: “Quí vị không dùng hay sao?”');
INSERT INTO "hasan_abdulkarim" VALUES(51,28,'51:28','(Khi thấy họ không ăn), (Ibrahim) đâm sợ họ. Họ bảo: “Chớ sợ!” và báo cho Y tin mừng về một đứa con trai hiểu biết.');
INSERT INTO "hasan_abdulkarim" VALUES(51,29,'51:29','Nhưng bà vợ của Y bước đến trước, lớn tiếng than. Nữ vừa lấy tay đập vào trán của mình vừa nói: “Một bà lão hiếm hoi”');
INSERT INTO "hasan_abdulkarim" VALUES(51,30,'51:30','Họ đáp: “Thượng Đế (Đấng Chủ Tể) của nữ đã truyền đúng như thế. Bởi vì Ngài là Đấng Rất Cao Minh, Toàn Tri, Biết (hết tất cả)."');
INSERT INTO "hasan_abdulkarim" VALUES(51,31,'51:31','(Ibrahim) bảo: “Thế công tác của quí vị là gì, thưa các vị sứ?”');
INSERT INTO "hasan_abdulkarim" VALUES(51,32,'51:32','Họ đáp: “Chúng tôi được phái đến gặp một đám người tội lỗi"');
INSERT INTO "hasan_abdulkarim" VALUES(51,33,'51:33','“Để trút lên chúng (mưa) đá (làm bằng) gạch nung."');
INSERT INTO "hasan_abdulkarim" VALUES(51,34,'51:34','“Đã được Thượng Đế của Ngươi (Ibrahim) đánh dấu để trừng phạt những kẻ tội lỗi thái quá.”');
INSERT INTO "hasan_abdulkarim" VALUES(51,35,'51:35','"Bởi thế, chúng tôi đưa ai là người có đức tin ra khỏi nơi đó."');
INSERT INTO "hasan_abdulkarim" VALUES(51,36,'51:36','"Nhưng chúng tôi không thấy ai ngoài một gia đình Muslim."');
INSERT INTO "hasan_abdulkarim" VALUES(51,37,'51:37','"Và chúng tôi đã để lại nơi đó một dấu hiệu (làm bài học) cho những ai sợ sự trừng phạt đau đớn."');
INSERT INTO "hasan_abdulkarim" VALUES(51,38,'51:38','Và về Musa khi TA phái Y đến gặp Fir-''awn với một thẩm quyền rõ rệt.');
INSERT INTO "hasan_abdulkarim" VALUES(51,39,'51:39','Nhưng ỷ vào quyền hành, hắn (Fir-''awn) quay lưng làm ngơ và bảo: “(Đây là) một tên phù thủy hay một thằng điên.”');
INSERT INTO "hasan_abdulkarim" VALUES(51,40,'51:40','Bởi thế, TA đã bắt phạt hắn và lực lượng của hắn. Và TA đã quẳng chúng xuống biển; và hắn (Fir-''awn) là một tên đáng trách.');
INSERT INTO "hasan_abdulkarim" VALUES(51,41,'51:41','Và về bộ tộc ''Ad (là một dấu hiệu) khi TA gởi một trận cuồng phong tàn phá đến trừng phạt chúng.');
INSERT INTO "hasan_abdulkarim" VALUES(51,42,'51:42','Nó không bỏ sót một vật gì khi ùa đến, ngoại trừ việc để lại những đống vụn rã rời.');
INSERT INTO "hasan_abdulkarim" VALUES(51,43,'51:43','Và về bộ tộc Thamud (là một Dấu Hiệu) khi có lời bảo chúng: “Các người hãy tạm hưởng lạc trong một thời gian ngắn.”');
INSERT INTO "hasan_abdulkarim" VALUES(51,44,'51:44','Nhưng chúng xấc xược thách thức Mệnh Lệnh của Thượng Đế của chúng. Do đó, lưỡi tầm sét đã đánh chúng trong lúc chúng đang ngóng nhìn.');
INSERT INTO "hasan_abdulkarim" VALUES(51,45,'51:45','Bởi thế, chúng không đứng dậy cũng không tự cứu mình được.');
INSERT INTO "hasan_abdulkarim" VALUES(51,46,'51:46','Trước đây, người dân của Nuh cũng đã bị tiêu diệt như thế. Quả thật, chúng là một đám người bất tuân, nổi loạn.');
INSERT INTO "hasan_abdulkarim" VALUES(51,47,'51:47','Và bầu trời, TA đã dựng nó bằng sức mạnh và TA làm cho nó bao la.');
INSERT INTO "hasan_abdulkarim" VALUES(51,48,'51:48','Và trái đất, TA đã trải nó ra rộng; và Đấng trải ấy thật là Ưu Việt.');
INSERT INTO "hasan_abdulkarim" VALUES(51,49,'51:49','Và TA đã tạo hóa mọi vật theo cặp để cho các ngươi ghi nhớ.');
INSERT INTO "hasan_abdulkarim" VALUES(51,50,'51:50','Bởi thế, hãy chạy đến với Allah, quả thật, Ta (Muhammad) là một người báo trước rõ rệt do Ngài cử đến với các người.');
INSERT INTO "hasan_abdulkarim" VALUES(51,51,'51:51','Và chớ tôn thờ một thần linh nào cùng với Allah. Quả thật, Ta là một người báo trước công khai do Ngài cử đến với các người.');
INSERT INTO "hasan_abdulkarim" VALUES(51,52,'51:52','Đúng như thế; không một Sứ Giả nào đến gặp những ai sống trước chúng mà không bị xuyên tạc là ''một tên phù thủy hay một thằng điên''.');
INSERT INTO "hasan_abdulkarim" VALUES(51,53,'51:53','Phải chăng đây là lời di chúc mà chúng truyền cho nhau? Không, chúng là một dân tộc thái quá.');
INSERT INTO "hasan_abdulkarim" VALUES(51,54,'51:54','Bởi thế, hãy quay lánh xa chúng. Ngươi không bị khiển trách.');
INSERT INTO "hasan_abdulkarim" VALUES(51,55,'51:55','Và hãy nhắc nhở. Và sự nhắc nhở có lợi cho những người có đức tin.');
INSERT INTO "hasan_abdulkarim" VALUES(51,56,'51:56','Và TA đã tạo ra loài Jinn và loài người chỉ để thờ phụng TA.');
INSERT INTO "hasan_abdulkarim" VALUES(51,57,'51:57','TA không đòi hỏi bổng lộc từ chúng và cũng không đòi chúng nuôi TA.');
INSERT INTO "hasan_abdulkarim" VALUES(51,58,'51:58','Chính Allah là Đấng Cung cấp bổng lộc (cho tất cả), Chủ Nhân của Quyền Lực, rất Hùng Mạnh.');
INSERT INTO "hasan_abdulkarim" VALUES(51,59,'51:59','Bởi thế, phần của những kẻ làm điều sai quấy giống với phần của những người bạn của chúng. Bởi thế, chúng không cần phải vội vàng.');
INSERT INTO "hasan_abdulkarim" VALUES(51,60,'51:60','Khốn khổ cho những kẻ vô đức tin về Ngày mà chúng đã được hứa.');
INSERT INTO "hasan_abdulkarim" VALUES(52,1,'52:1','Thề bởi ngọn Núi (nơi đã xảy ra việc mặc khải);');
INSERT INTO "hasan_abdulkarim" VALUES(52,2,'52:2','Và bởi Kinh Sách đã được ghi khắc;');
INSERT INTO "hasan_abdulkarim" VALUES(52,3,'52:3','Nơi miếng da thuộc được mở ra;');
INSERT INTO "hasan_abdulkarim" VALUES(52,4,'52:4','Và bởi Ngôi Đền ở bên trên bẩy tầng trời;');
INSERT INTO "hasan_abdulkarim" VALUES(52,5,'52:5','Và bởi mái vòm (bầu trời) được dựng cao;');
INSERT INTO "hasan_abdulkarim" VALUES(52,6,'52:6','Và bởi đại dương (nổi sóng) dâng trào;');
INSERT INTO "hasan_abdulkarim" VALUES(52,7,'52:7','Quả thật, sự trừng phạt của Thượng Đế của Ngươi chắc chắn sẽ xảy ra,');
INSERT INTO "hasan_abdulkarim" VALUES(52,8,'52:8','Không ai có thể tránh khỏi đặng; -');
INSERT INTO "hasan_abdulkarim" VALUES(52,9,'52:9','Ngày mà bầu trời sẽ rung chuyển dữ dội;');
INSERT INTO "hasan_abdulkarim" VALUES(52,10,'52:10','Và những ngọn núi sẽ dời đi một cách khủng khiếp.');
INSERT INTO "hasan_abdulkarim" VALUES(52,11,'52:11','Bởi thế, vào Ngày đó những kẻ phủ nhận sự thật sẽ rất thống khổ;');
INSERT INTO "hasan_abdulkarim" VALUES(52,12,'52:12','Những ai bông đùa nói chuyện vô bổ.');
INSERT INTO "hasan_abdulkarim" VALUES(52,13,'52:13','Vào Ngày mà chúng sẽ bị xô xuống Lửa của hỏa ngục bằng một cuộc xô mạnh bạo.');
INSERT INTO "hasan_abdulkarim" VALUES(52,14,'52:14','“Đây là Lửa mà các người thương phủ nhận.”');
INSERT INTO "hasan_abdulkarim" VALUES(52,15,'52:15','Thế phải chăng đây là trò ảo thuật? hay là các ngươi không nhìn thấy?');
INSERT INTO "hasan_abdulkarim" VALUES(52,16,'52:16','Hãy chịu đốt trong đó. Dù các ngươi rán chịu hay không chịu nổi thì điều đó bằng thừa đối với các ngươi. Bởi vì các ngươi chỉ nhận lấy quả báo về những việc làm của các ngươi mà thôi.');
INSERT INTO "hasan_abdulkarim" VALUES(52,17,'52:17','Quả thật, những người ngay chính sợ Allah sẽ thực sự ở trong thiên đàng và sẽ sung sướng;');
INSERT INTO "hasan_abdulkarim" VALUES(52,18,'52:18','Hưởng hạnh phúc mà Thượng Đế của họ ban cấp và Thượng Đế của họ sẽ giải cứu họ khỏi sự trừng phạt của hỏa ngục.');
INSERT INTO "hasan_abdulkarim" VALUES(52,19,'52:19','(Họ được bảo:) “Hãy ăn uống thỏa thích do công trình mà các người đã làm.”');
INSERT INTO "hasan_abdulkarim" VALUES(52,20,'52:20','Họ sẽ tựa mình nằm nghỉ trên những chiếc tràng kỹ được xếp thành hàng và TA sẽ kết hôn họ với những tiên nữ với đôi mắt to, đẹp và trữ tình.');
INSERT INTO "hasan_abdulkarim" VALUES(52,21,'52:21','Và những ai có đức tin và con cái của họ cùng theo họ trong đức tin, TA sẽ cho con cái của họ đoàn tụ với họ (nơi thiên đàng). Và TA sẽ ân thưởng họ không thiếu một thứ gì về những việc làm (tốt) của họ. Mỗi người là một bảo chứng cho những điều mà họ đã làm ra.');
INSERT INTO "hasan_abdulkarim" VALUES(52,22,'52:22','Và TA sẽ gia tăng cho họ trái cây và thịt ngon tùy món mà họ thích.');
INSERT INTO "hasan_abdulkarim" VALUES(52,23,'52:23','Trong đó (thiên đàng), họ sẽ trao cho nhau một cái tách không chứa lời tam phào và không tội lỗi.');
INSERT INTO "hasan_abdulkarim" VALUES(52,24,'52:24','Và được hầu hạ bởi những thiếu niên xinh đẹp như những viên ngọc trai được giữ kỹ.');
INSERT INTO "hasan_abdulkarim" VALUES(52,25,'52:25','Họ sẽ bước đến gặp nhau hỏi han.');
INSERT INTO "hasan_abdulkarim" VALUES(52,26,'52:26','Nói: "Trước đây chúng tôi và gia đình thường lo lắng''');
INSERT INTO "hasan_abdulkarim" VALUES(52,27,'52:27','“Nhưng Allah đã ban ân cho chúng tôi và cứu chúng tôi khỏi hình phạt của Lửa đốt cháy."');
INSERT INTO "hasan_abdulkarim" VALUES(52,28,'52:28','“Quả thật, trước đây chúng tôi đã từng cầu nguyện Ngài. Rõ thật, Ngài là Đấng Rất Mực Nhân Từ, Rất Mực Khoan Dung.”');
INSERT INTO "hasan_abdulkarim" VALUES(52,29,'52:29','Bởi thế, hãy nhắc nhở. Do Ân huệ của Thượng Đế của Ngươi, Ngươi không phải là một tên thầy bói cũng không phải là một tên điên.');
INSERT INTO "hasan_abdulkarim" VALUES(52,30,'52:30','Hoặc có phải chúng nói: “(Y là) một nhà thơ. Bọn ta đợi xem, sớm muộn gì y cũng gặp tai nạn.”');
INSERT INTO "hasan_abdulkarim" VALUES(52,31,'52:31','Hãy bảo chúng: “Các người hãy đợi, Ta cùng chờ đợi với các người"');
INSERT INTO "hasan_abdulkarim" VALUES(52,32,'52:32','Phải chăng đầu óc của chúng bảo nói ra điều này? Hay là chúng thái quá?');
INSERT INTO "hasan_abdulkarim" VALUES(52,33,'52:33','Phải chăng chúng nói: “Y đã bịa đặt Nó.” Không, chúng không tin tưởng.');
INSERT INTO "hasan_abdulkarim" VALUES(52,34,'52:34','Thế hãy để chúng mang một Lời tường trình giống như Nó (Qur''an) đến nếu chúng nói thật.');
INSERT INTO "hasan_abdulkarim" VALUES(52,35,'52:35','Phải chăng chúng đã được tạo hóa từ cái không là gì cả? Hay chúng là những đấng tạo hóa?');
INSERT INTO "hasan_abdulkarim" VALUES(52,36,'52:36','Phải chăng chúng đã tạo hóa các tầng trời và trái đất? Không, chúng không có một niềm tin vững chắc.');
INSERT INTO "hasan_abdulkarim" VALUES(52,37,'52:37','Phải chăng chúng giữ các kho tàng của Thượng Đế của Ngươi? hoặc chúng có toàn quyền quản lý (các kho tàng)?');
INSERT INTO "hasan_abdulkarim" VALUES(52,38,'52:38','Hoặc phải chăng chúng có một cái thang dùng đi lên trời để nghe (những điều bí mật của Thiên Thần?) Bởi thế, hãy để cho những kẻ nghe lỏm đưa ra một bằng chứng rõ rệt thử xem?');
INSERT INTO "hasan_abdulkarim" VALUES(52,39,'52:39','Hoặc phải chăng Ngài chỉ có con gái còn con trai thì của các người?');
INSERT INTO "hasan_abdulkarim" VALUES(52,40,'52:40','Phải chăng Ngươi đã đòi chúng tiền thù lao cho nên chúng nợ nần chồng chất?');
INSERT INTO "hasan_abdulkarim" VALUES(52,41,'52:41','Hoặc phải chăng chúng nắm diều Vô hình cho nên chúng viết nó xuống?');
INSERT INTO "hasan_abdulkarim" VALUES(52,42,'52:42','Hoặc phải chăng chúng mưu đồ (hại Ngươi?) Nhưng chính những kẻ vô niềm tin lại rơi vào mưu đồ của chúng.');
INSERT INTO "hasan_abdulkarim" VALUES(52,43,'52:43','Phải chăng chúng có một Thượng Đế khác hơn Allah? Thật quang vinh thay Allah! Ngài vượt hẳn những điều mà chúng đã qui cho Ngài.');
INSERT INTO "hasan_abdulkarim" VALUES(52,44,'52:44','Và dẫu chúng có thấy một mảnh trời rơi xuống thì chúng vẫn nói: “Đó là những đám mây kết lại thành đóng lớn!”');
INSERT INTO "hasan_abdulkarim" VALUES(52,45,'52:45','Bởi thế, hãy để mặc chúng cho đến lúc chúng sẽ gặp Ngày (tàn) của chúng mà chúng sẽ bất tỉnh.');
INSERT INTO "hasan_abdulkarim" VALUES(52,46,'52:46','Ngày mà âm mưu của chúng sẽ không giúp ích gì được cho chúng và chúng sẽ không được ai cứu giúp.');
INSERT INTO "hasan_abdulkarim" VALUES(52,47,'52:47','Và chắc chắn những kẻ làm điều sai quấy sẽ nhận một sự trừng phạt ngoài cái đó nhưng đa số bọn chúng không biết.');
INSERT INTO "hasan_abdulkarim" VALUES(52,48,'52:48','Và hãy kiên nhẫn đợi Quyết Định của Thượng Đế của Ngươi bởi vì quả thật Ngươi nằm dưới Mắt của TA (Allah); và hãy tán dương lời ca tụng Thượng Đế của Ngươi khi Ngươi thức giấc (hay đứng dâng lễ).');
INSERT INTO "hasan_abdulkarim" VALUES(52,49,'52:49','Do đó, hãy tán dương Ngài vào ban đêm và vào lúc các vì sao lặn.');
INSERT INTO "hasan_abdulkarim" VALUES(53,1,'53:1','Thề bởi ngôi sao khi nó đi xuống (lặn);');
INSERT INTO "hasan_abdulkarim" VALUES(53,2,'53:2','Người Bạn của các ngươi không lạc hướng cũng không nhầm lẫn.');
INSERT INTO "hasan_abdulkarim" VALUES(53,3,'53:3','Và Y không nói theo sở thích.');
INSERT INTO "hasan_abdulkarim" VALUES(53,4,'53:4','Quả thật, Nó (Qur''an) chỉ là điều mặc khải đã được mặc khải.');
INSERT INTO "hasan_abdulkarim" VALUES(53,5,'53:5','Y được một vị (Thiên Thần Jibril) rất quyền lực dạy cho.');
INSERT INTO "hasan_abdulkarim" VALUES(53,6,'53:6','(Một Vị) toàn vẹn về mọi mặt; rồi Y đi lên.');
INSERT INTO "hasan_abdulkarim" VALUES(53,7,'53:7','Và Y (Jibril) xuất hiện trên phần cao nhất của chân trời;');
INSERT INTO "hasan_abdulkarim" VALUES(53,8,'53:8','Rồi Y tiến đến gần;');
INSERT INTO "hasan_abdulkarim" VALUES(53,9,'53:9','Xa bằng khoảng cách của hai cây cung hoặc gần hơn;');
INSERT INTO "hasan_abdulkarim" VALUES(53,10,'53:10','Bằng cách đó, Ngài đã mặc khải cho người bề tôi điều mà Ngài muốn.');
INSERT INTO "hasan_abdulkarim" VALUES(53,11,'53:11','Tấm lòng (của Muhammad) không dối trá về điều mà Y đã thấy.');
INSERT INTO "hasan_abdulkarim" VALUES(53,12,'53:12','Thế các người muốn tranh cãi với Y về điều mà Y đã thấy ư?');
INSERT INTO "hasan_abdulkarim" VALUES(53,13,'53:13','Và quả thật Y đã thấy (Jibril) trong lần giáng trần thứ hai.');
INSERT INTO "hasan_abdulkarim" VALUES(53,14,'53:14','Gần Cây Bách Hương của biên giới xa thẳm;');
INSERT INTO "hasan_abdulkarim" VALUES(53,15,'53:15','Gần nó (Cây Bách Hương) là thiên đàng Cư ngụ;');
INSERT INTO "hasan_abdulkarim" VALUES(53,16,'53:16','Khi Cây Bách Hương được bao phủ bởi cái vốn bao phủ (nó);');
INSERT INTO "hasan_abdulkarim" VALUES(53,17,'53:17','Mắt nhìn không quay sang cũng không vượt quá tầm mức (giới hạn);');
INSERT INTO "hasan_abdulkarim" VALUES(53,18,'53:18','Quả thật, Y đã thấy những Dấu Hiệu lớn nhất của Thượng Đế của Y.');
INSERT INTO "hasan_abdulkarim" VALUES(53,19,'53:19','Há các ngươi đã thấy (thần) Al-Lat và (thần) Al-''Uzza,');
INSERT INTO "hasan_abdulkarim" VALUES(53,20,'53:20','Và Manãt, (nữ thần) thứ ba hay sao?');
INSERT INTO "hasan_abdulkarim" VALUES(53,21,'53:21','Phải chăng nam giới thuộc về các ngươi còn nữ giới thì thuộc về Ngài?');
INSERT INTO "hasan_abdulkarim" VALUES(53,22,'53:22','Một sự phân chia như thế thật chẳng công bằng!');
INSERT INTO "hasan_abdulkarim" VALUES(53,23,'53:23','Đó chẳng qua là những tên gọi mà các ngươi và cha mẹ của các ngươi đã qui cho chúng chứ Allah đã không ban xuống một thẩm quyền nào. Chúng chỉ tưởng tượng và theo điều mà bản thân của chúng mong muốn trong lúc một Chỉ Đạo từ Thượng Đế của chúng đã đến với chúng.');
INSERT INTO "hasan_abdulkarim" VALUES(53,24,'53:24','Hoặc phải chăng con người sẽ được điều mà y mong ước?');
INSERT INTO "hasan_abdulkarim" VALUES(53,25,'53:25','Nhưng cái sau cùng và cái trước tiên (đời này) đều là của Allah cả.');
INSERT INTO "hasan_abdulkarim" VALUES(53,26,'53:26','Và có bao nhiêu Thiên Thần trong các tầng trời mà sự can thiệp chẳng có kết quả gì trừ phi Allah chấp thuận cho ai mà Ngài muốn và hài lòng?');
INSERT INTO "hasan_abdulkarim" VALUES(53,27,'53:27','Quả thật, những ai không có đức tin nơi đời sau đã dùng tên của phái nữ mà đặt cho Thiên Thần.');
INSERT INTO "hasan_abdulkarim" VALUES(53,28,'53:28','Trong lúc chúng chẳng có một chút hiểu biết gì về việc đó. Quả thật, chúng chỉ làm theo tưởng tượng. Và điều tưởng tượng chẳng thay thế được Sự Thật.');
INSERT INTO "hasan_abdulkarim" VALUES(53,29,'53:29','Do đó, hãy lánh xa kẻ nào làm ngơ trước Lời Nhắc Nhở (Qur''an) của TA và chỉ ham muốn đời sống trần tục này.');
INSERT INTO "hasan_abdulkarim" VALUES(53,30,'53:30','Đó là mức hiểu biết mà chúng đã đạt đến. Quả thật, Thượng Đế (Đấng Chủ Tể) của Ngươi là Đấng biết rõ ai đi lạc khỏi con đường của Ngài và ai là người đã được hướng dẫn.');
INSERT INTO "hasan_abdulkarim" VALUES(53,31,'53:31','Và mọi vật trong các tầng trời và mọi vật dưới đất đều là của Ngài cả. Mục đích để Ngài trừng phạt những ai phạm tội về những điều mà chúng đã làm và để tưởng thưởng những người làm tốt với cái tốt nhất (thiên đàng).');
INSERT INTO "hasan_abdulkarim" VALUES(53,32,'53:32','Những ai tránh được những tội lớn và các tội Al-Fawahish (như thông gian, ngoại tình) ngoại trừ những điều xúc phạm nhỏ nhặt. Quả thật, Thượng Đế của Ngươi với lòng Tha thứ bao la. Ngài biết rõ cac ngươi khi Ngài tạo ra các ngươi từ đất (bụi) và khi các ngươi hãy còn là thai nhi nằm trong bụng mẹ của các ngươi. Bởi thế, chớ tự cho mình trong sạch. Ngài biết rõ ai là người sợ Allah nhất.');
INSERT INTO "hasan_abdulkarim" VALUES(53,33,'53:33','Há Ngươi (Muhammad) đã thấy một người quay lưng (bỏ đi),');
INSERT INTO "hasan_abdulkarim" VALUES(53,34,'53:34','Hắn cho một ít rồi ngưng tay?');
INSERT INTO "hasan_abdulkarim" VALUES(53,35,'53:35','Phải chăng hắn hiểu biết điều vô hình cho nên hắn thấy (tương lai)?');
INSERT INTO "hasan_abdulkarim" VALUES(53,36,'53:36','Há hắn đã không được thông báo về những điều ghi trong các tờ Kinh của Musa hay sao?');
INSERT INTO "hasan_abdulkarim" VALUES(53,37,'53:37','Và (nơi các tờ Kinh của) Ibrahim, vốn đã hoàn tất nhiệm vụ?');
INSERT INTO "hasan_abdulkarim" VALUES(53,38,'53:38','(Các tờ Kinh đó ghi) rằng: không một người chất đầy (tội lỗi) nào lại vác giùm gánh nặng (tội lỗi) của người khác.');
INSERT INTO "hasan_abdulkarim" VALUES(53,39,'53:39','Và rằng: con người chỉ lãnh phần mà hắn cố gắng làm ra;');
INSERT INTO "hasan_abdulkarim" VALUES(53,40,'53:40','Và rằng: nỗ lực của hắn sẽ sớm được thấy;');
INSERT INTO "hasan_abdulkarim" VALUES(53,41,'53:41','Rồi hắn sẽ được tưởng thưởng bằng một phần thưởng trọn vẹn;');
INSERT INTO "hasan_abdulkarim" VALUES(53,42,'53:42','Và cuối cùng là sự trở về gặp Thượng Đế của Ngươi (Muhammad).');
INSERT INTO "hasan_abdulkarim" VALUES(53,43,'53:43','Và chính Ngài là Đấng làm cho cười và làm cho khóc;');
INSERT INTO "hasan_abdulkarim" VALUES(53,44,'53:44','Và chính Ngài là Đấng làm cho sống và gây cho chết.');
INSERT INTO "hasan_abdulkarim" VALUES(53,45,'53:45','Và chính Ngài là Đấng tạo ra cặp (đôi), nam và nữ;');
INSERT INTO "hasan_abdulkarim" VALUES(53,46,'53:46','Từ tinh dịch khi nó xuất ra;');
INSERT INTO "hasan_abdulkarim" VALUES(53,47,'53:47','Và rằng Ngài phục sinh một lần thứ hai;');
INSERT INTO "hasan_abdulkarim" VALUES(53,48,'53:48','Và chính Ngài là Đấng ban cho sự giàu sang và làm cho mãn nguyện;');
INSERT INTO "hasan_abdulkarim" VALUES(53,49,'53:49','Và chính Ngài là Thượng Đế (Đấng Chủ Tể) của Sao Thiên Lang.');
INSERT INTO "hasan_abdulkarim" VALUES(53,50,'53:50','Và chính Ngài là Đấng đã tiêu diệt bộ lạc ''Ad thuở xưa;');
INSERT INTO "hasan_abdulkarim" VALUES(53,51,'53:51','Và không bỏ sót (bộ tộc) Thamud.');
INSERT INTO "hasan_abdulkarim" VALUES(53,52,'53:52','Và cả người dân của Nuh trước đây bởi vì chúng đã từng bất công và phạm giới quá mức.');
INSERT INTO "hasan_abdulkarim" VALUES(53,53,'53:53','Và (Ngài đã tiêu diệt) những thị trấn đổ phế.');
INSERT INTO "hasan_abdulkarim" VALUES(53,54,'53:54','Bởi thế, cái (hình phạt) từng bao phủ đã bao phủ chúng.');
INSERT INTO "hasan_abdulkarim" VALUES(53,55,'53:55','Thế Ân Huệ nào của Thượng Đế của ngươi mà ngươi hãy còn nghi ngờ?');
INSERT INTO "hasan_abdulkarim" VALUES(53,56,'53:56','Đây là một Người báo trước trong loạt người báo trước đã có từ xưa.');
INSERT INTO "hasan_abdulkarim" VALUES(53,57,'53:57','(Báo trước) việc sắp đến (Giờ Tận Thế) sẽ đến;');
INSERT INTO "hasan_abdulkarim" VALUES(53,58,'53:58','Ngoài Allah ra, không ai có thể chuyển hướng nó được;');
INSERT INTO "hasan_abdulkarim" VALUES(53,59,'53:59','Thế các ngươi ngạc nhiên về lời trình bày (Qur''an) này chăng?');
INSERT INTO "hasan_abdulkarim" VALUES(53,60,'53:60','Và nhẹ dạ cười đùa chứ không khóc lóc;');
INSERT INTO "hasan_abdulkarim" VALUES(53,61,'53:61','Và phí mất thời gian (ham vui);');
INSERT INTO "hasan_abdulkarim" VALUES(53,62,'53:62','Thôi hãy quỳ mọp xuống phủ phục Allah và thờ phụng Ngài.');
INSERT INTO "hasan_abdulkarim" VALUES(54,1,'54:1','Giờ Tận Thế đã gần kề và mặt Trăng chẻ làm đôi;');
INSERT INTO "hasan_abdulkarim" VALUES(54,2,'54:2','Và nếu chúng có thấy một Phép Lạ chúng vẫn quay bỏ đi và bảo: “Đây là một trò ảo thuật liên hồi (và dữ dội).”');
INSERT INTO "hasan_abdulkarim" VALUES(54,3,'54:3','Và chúng phủ nhận và làm theo sỡ thích; và mỗi vấn đề đều được kết thúc.');
INSERT INTO "hasan_abdulkarim" VALUES(54,4,'54:4','Và chúng đã tiếp thu tin tức mà nội dung hăm dọa khiến chúng cả Sợ;');
INSERT INTO "hasan_abdulkarim" VALUES(54,5,'54:5','(Qur''an chứa) điều khôn ngoan vô cùng thâm thúy; nhưng những Lời Cảnh Báo chẳng thấm tháp gì chúng cả.');
INSERT INTO "hasan_abdulkarim" VALUES(54,6,'54:6','Bởi thế, hãy lánh xa chúng (hỡi Muhammad!) và chờ đến Ngày mà Người gọi sẽ gọi chúng đến đối diện với một điều hết sức khủng khiếp.');
INSERT INTO "hasan_abdulkarim" VALUES(54,7,'54:7','Chúng sẽ bước ra khỏi mộ, nhìn gục xuống, (hãi hùng) giống như các con châu chấu bay tán loạn;');
INSERT INTO "hasan_abdulkarim" VALUES(54,8,'54:8','Vội vã hướng nhanh về phía người gọi. Những kẻ không có đức tin sẽ bảo: “Đây là một Ngày khó khăn vất vả!”');
INSERT INTO "hasan_abdulkarim" VALUES(54,9,'54:9','Người dân của Nuh trước chúng đã phủ nhận (Sứ Giả của họ). Bởi thế, họ đã phủ nhận người bề tôi của TA. Và bảo: “Một thằng điên.” Và Y bị nhiếc mắng và hăm dọa.');
INSERT INTO "hasan_abdulkarim" VALUES(54,10,'54:10','Y cầu cứu với Thượng Đế của Y, thưa: “Bề tôi đã thua cuộc, xin Ngài giúp đỡ.”');
INSERT INTO "hasan_abdulkarim" VALUES(54,11,'54:11','Bởi thế, TA đã mở những cánh cửa của bầu trời cho nước mưa đổ xuống;');
INSERT INTO "hasan_abdulkarim" VALUES(54,12,'54:12','Và TA đã làm cho đất phun nước lên. Và hai loại nước gặp nhau cho một vấn đề đã được quyết định.');
INSERT INTO "hasan_abdulkarim" VALUES(54,13,'54:13','Và TA đã chở Y (Nuh) trên một chiếc (tàu) làm bằng ván và bằng sợi dây xe kết bằng thân cây dừa nước.');
INSERT INTO "hasan_abdulkarim" VALUES(54,14,'54:14','Chiếc tàu trôi dưới Mắt (trông chừng) của TA. Một phần thưởng ban cấp cho người đã từng bị phủ nhận (và khinh rẻ).');
INSERT INTO "hasan_abdulkarim" VALUES(54,15,'54:15','Và chắc chắn TA đã để nó lại như một Dấu Hiệu. Thế có ai nhớ chăng?');
INSERT INTO "hasan_abdulkarim" VALUES(54,16,'54:16','Bởi thế, sự trừng phạt và lời cảnh báo của TA (đáng sợ) dường nào?');
INSERT INTO "hasan_abdulkarim" VALUES(54,17,'54:17','Và TA đã làm cho Qur''an dễ hiểu và dễ nhớ. Thế có ai nhớ chăng?');
INSERT INTO "hasan_abdulkarim" VALUES(54,18,'54:18','(Người dân) ''Ad cũng đã phủ nhận (Sứ Giả Hud của họ). Bởi thế, sự trừng phạt và lời cảnh báo của TA (đáng sợ) dường nào?');
INSERT INTO "hasan_abdulkarim" VALUES(54,19,'54:19','Quả thật, TA (Allah) đã gởi một trận gió gào thét dữ dội đến trừng phạt chúng vào một Ngày của đại họa liên hồi.');
INSERT INTO "hasan_abdulkarim" VALUES(54,20,'54:20','Càn quét con người đi mất biệt giống như cây chà là bị thổi bứng gốc.');
INSERT INTO "hasan_abdulkarim" VALUES(54,21,'54:21','Hãy xem sự trừng phạt và lời cảnh cáo của TA như thế nào?');
INSERT INTO "hasan_abdulkarim" VALUES(54,22,'54:22','Và chắc chắn TA đã làm Qur''an dễ hiểu và dễ nhớ. Thế có ai nhớ chăng?');
INSERT INTO "hasan_abdulkarim" VALUES(54,23,'54:23','(Người dân) Thamud phủ nhận những lời cảnh cáo;');
INSERT INTO "hasan_abdulkarim" VALUES(54,24,'54:24','Bởi vì chúng đã bảo: “Một người phàm đơn lẽ giữa chúng ta! Há chúng ta nghe theo y hay sao? Trong trường hợp đó, chúng ta thật sự lầm lạc và điên rồ!”');
INSERT INTO "hasan_abdulkarim" VALUES(54,25,'54:25','“Phải chăng giữa chúng ta, Y (Nabi Saleh) là người đã nhận thông điệp Nhắc Nhở? Không, Y là mọt tên láo toét hỗn xược!”');
INSERT INTO "hasan_abdulkarim" VALUES(54,26,'54:26','Mai đây chúng sẽ biết ai là một tên láo khoét, một tên hỗn xược.');
INSERT INTO "hasan_abdulkarim" VALUES(54,27,'54:27','Quả thật, TA đã gởi một con lạc đà cái đến thử thách chúng. Do đó, hãy quan sát chúng (hỡi Saleh!) và kiên nhẫn.');
INSERT INTO "hasan_abdulkarim" VALUES(54,28,'54:28','Và hãy cho chúng biết nước (hồ) sẽ được chia sẻ giữa chúng (và con lạc đà cái). Mỗi bên thay phiên nhau uống nước (hồ).');
INSERT INTO "hasan_abdulkarim" VALUES(54,29,'54:29','Nhưng chúng gọi bạn bè của chúng đến, cầm dao cắt nhượng con lạc đà.');
INSERT INTO "hasan_abdulkarim" VALUES(54,30,'54:30','Hãy xem sự trừng phạt và lời cảnh cáo của TA như thế nào?');
INSERT INTO "hasan_abdulkarim" VALUES(54,31,'54:31','Và TA đã gởi tiếng gầm thét duy nhất đến phạt chúng và chúng chết như cây khô dùng rào chuồng thú.');
INSERT INTO "hasan_abdulkarim" VALUES(54,32,'54:32','Và TA đã làm cho Qur''an dễ hiểu và dễ nhớ. Thế có ai nhớ chăng?');
INSERT INTO "hasan_abdulkarim" VALUES(54,33,'54:33','Người dân của Lut (Lốt) phủ nhận lời cảnh báo.');
INSERT INTO "hasan_abdulkarim" VALUES(54,34,'54:34','Quả thật, TA (Allah) đã gởi một trận bão đá đến tiêu diệt chúng chỉ trừ gia đình của Lut; TA đã giải cứu họ vào lúc sáng sớm.');
INSERT INTO "hasan_abdulkarim" VALUES(54,35,'54:35','Như là một Ân Huệ từ TA. TA đãi ngộ người biết ơn đúng như thế.');
INSERT INTO "hasan_abdulkarim" VALUES(54,36,'54:36','Và quả thật Lut đã cảnh báo chúng về việc TA sẽ túm bắt chúng, nhưng chúng nghi ngờ lời cảnh báo.');
INSERT INTO "hasan_abdulkarim" VALUES(54,37,'54:37','Và quả thật chúng đã đòi Lut trao những vị khách lạ cho chúng. Nhưng TA làm cho mắt của chúng thành mù. (Và phán): “Thôi hãy nếm mùi trừng phạt và lời cảnh báo của TA.”');
INSERT INTO "hasan_abdulkarim" VALUES(54,38,'54:38','Và một sự trừng phạt dữ dội đã chụp bắt chúng vào buổi sáng.');
INSERT INTO "hasan_abdulkarim" VALUES(54,39,'54:39','Bởi thế, hãy nếm mùi trừng phạt và lời cảnh báo của TA.');
INSERT INTO "hasan_abdulkarim" VALUES(54,40,'54:40','Và TA đã làm cho Qur''an dễ hiểu và dễ nhớ. Thế có ai nhớ chăng?');
INSERT INTO "hasan_abdulkarim" VALUES(54,41,'54:41','Và chắc chắn những lời cảnh báo đã đến cho người dân của Fir-''awn.');
INSERT INTO "hasan_abdulkarim" VALUES(54,42,'54:42','Nhưng chúng phủ nhận tất cả các Phép Lạ của TA. Bởi thế, TA đã túm bắt chúng bằng một cuộc túm bắt của Đấng Toàn Năng, Đấng Quyền Lực Vô Song.');
INSERT INTO "hasan_abdulkarim" VALUES(54,43,'54:43','Phải chăng những kẻ không tin của cắc người (hỡi đám Quraish!) giỏi hơn những người dẫn đó hay sao? Hoặc phải chăng trong Kinh Sách có ghi điều cho các người được miễn phạt?');
INSERT INTO "hasan_abdulkarim" VALUES(54,44,'54:44','Hoặc phải chăng chúng bảo: “Chúng tôi liên minh giúp đỡ lẫn nhau.”');
INSERT INTO "hasan_abdulkarim" VALUES(54,45,'54:45','Nhưng liên quân của chúng sẽ bị đánh bại và quay lưng tháo chạy.');
INSERT INTO "hasan_abdulkarim" VALUES(54,46,'54:46','Không, Giờ Tận Thế là thời điểm ấn định của chúng và Giờ Tận Thế sẽ đau đớn và cay đắng hơn.');
INSERT INTO "hasan_abdulkarim" VALUES(54,47,'54:47','Quả thật, những kẻ tội lỗi đi lạc hướng và điên rồ.');
INSERT INTO "hasan_abdulkarim" VALUES(54,48,'54:48','Vào Ngày mà chúng sẽ bị lôi úp mặt trong Lửa: “Hãy nếm Lửa chạm!”');
INSERT INTO "hasan_abdulkarim" VALUES(54,49,'54:49','Quả thật, TA (Allah) tạo hóa tất cả mọi vật theo Tiền Định.');
INSERT INTO "hasan_abdulkarim" VALUES(54,50,'54:50','Và Mệnh Lệnh của TA duy nhất, như một cái nháy mắt.');
INSERT INTO "hasan_abdulkarim" VALUES(54,51,'54:51','Và chắc chắn (khi xưa), TA đã tiêu diệt những bè phái của các ngươi; thế có ai nhớ (điều răn) chăng?');
INSERT INTO "hasan_abdulkarim" VALUES(54,52,'54:52','Và mỗi một điều mà chúng đã làm đều được ghi vào quyển sổ.');
INSERT INTO "hasan_abdulkarim" VALUES(54,53,'54:53','Và bất cứ điều gì nhỏ hay lớn đều được ghi chép.');
INSERT INTO "hasan_abdulkarim" VALUES(54,54,'54:54','Quả thật, những người ngay chính sợ Allah sẽ ở trong những Ngôi Vườn và dòng sông (của thiên đàng).');
INSERT INTO "hasan_abdulkarim" VALUES(54,55,'54:55','Tại một chỗ ngồi thực sự kế cận Đức Vua Quyền Năng.');
INSERT INTO "hasan_abdulkarim" VALUES(55,1,'55:1','Al-Rahman (Đấng Rất Mực Độ Lượng)');
INSERT INTO "hasan_abdulkarim" VALUES(55,2,'55:2','Đã dạy (Kinh) Qur''an.');
INSERT INTO "hasan_abdulkarim" VALUES(55,3,'55:3','Đã tạo hóa con người.');
INSERT INTO "hasan_abdulkarim" VALUES(55,4,'55:4','Đã dạy y (con người) ăn nói rành rõi, thông suốt.');
INSERT INTO "hasan_abdulkarim" VALUES(55,5,'55:5','Mặt trời và mặt trăng (đã được) tính toán (sắp xếp);');
INSERT INTO "hasan_abdulkarim" VALUES(55,6,'55:6','Thảo mộc (hay tinh tú) và cây cối (cả hai) đều quỳ lạy Ngài.');
INSERT INTO "hasan_abdulkarim" VALUES(55,7,'55:7','Và bầu trời mà Ngài đã dựng cao và Ngài đã thiết lập chiếc Cân.');
INSERT INTO "hasan_abdulkarim" VALUES(55,8,'55:8','Để các ngươi không được vuợt quá mức cân bằng.');
INSERT INTO "hasan_abdulkarim" VALUES(55,9,'55:9','Và hãy cân cho đúng và chớ cân thiếu.');
INSERT INTO "hasan_abdulkarim" VALUES(55,10,'55:10','Và trái đất mà Ngài đã trải rộng cho gia súc.');
INSERT INTO "hasan_abdulkarim" VALUES(55,11,'55:11','Nơi đó mọc ra hoa quả và cây chà là nặng trĩu hạt.');
INSERT INTO "hasan_abdulkarim" VALUES(55,12,'55:12','Và lúa thóc với cộng rạ và hương thơm (của cây cỏ).');
INSERT INTO "hasan_abdulkarim" VALUES(55,13,'55:13','Thế Ân Huệ nào của Thượng Đế của hai ngươi (Jinn và con người) mà hai ngươi phủ nhận?');
INSERT INTO "hasan_abdulkarim" VALUES(55,14,'55:14','Ngài đã tạo con người bằng đất sét kêu như loại làm đồ gốm.');
INSERT INTO "hasan_abdulkarim" VALUES(55,15,'55:15','Và Ngài đã tạo loài Jinn bằng lửa ngọn không có khói.');
INSERT INTO "hasan_abdulkarim" VALUES(55,16,'55:16','Thế Ân Huệ nào của Thượng Đế của hai ngươi mà hai ngươi phủ nhận?');
INSERT INTO "hasan_abdulkarim" VALUES(55,17,'55:17','Ngài là Thượng Đế của hai phương Đông và hai phương Tây.');
INSERT INTO "hasan_abdulkarim" VALUES(55,18,'55:18','Thế Ân Huệ nào của Thượng Đế của hai ngươi mà hai ngươi phủ nhận?');
INSERT INTO "hasan_abdulkarim" VALUES(55,19,'55:19','Và Ngài để cho hai biển nước (mặn và ngọt) tự do giao lưu.');
INSERT INTO "hasan_abdulkarim" VALUES(55,20,'55:20','Giữa hai chúng có một tấm chắn mà chúng không thể vượt qua được.');
INSERT INTO "hasan_abdulkarim" VALUES(55,21,'55:21','Thế Ân Huệ nào của Thượng Đế của hai ngươi mà hai ngươi phủ nhận?');
INSERT INTO "hasan_abdulkarim" VALUES(55,22,'55:22','Ngọc trai và san hô được mò lấy từ hai chúng (để làm vật trang sức).');
INSERT INTO "hasan_abdulkarim" VALUES(55,23,'55:23','Thế Ân Huệ nào của Thượng Đế của hai ngươi mà hai ngươi phủ nhận?');
INSERT INTO "hasan_abdulkarim" VALUES(55,24,'55:24','Và tàu bè to như những quả núi lướt sóng trên biển là vật của Ngài');
INSERT INTO "hasan_abdulkarim" VALUES(55,25,'55:25','Thế Ân Huệ nào của Thượng Đế của hai ngươi mà hai ngươi phủ nhận?');
INSERT INTO "hasan_abdulkarim" VALUES(55,26,'55:26','Bất cứ ai trên trái đất đều phải tiêu vong (chết)');
INSERT INTO "hasan_abdulkarim" VALUES(55,27,'55:27','Nhưng Sắc Diện của Thượng Đế của Ngươi (Muhammad), Đấng Quyền Uy, Tối Thượng và Vang Vinh sẽ còn mãi.');
INSERT INTO "hasan_abdulkarim" VALUES(55,28,'55:28','Thế Ân Huệ nào của Thượng Đế của hai ngươi mà hai ngươi phủ nhận?');
INSERT INTO "hasan_abdulkarim" VALUES(55,29,'55:29','Bất cứ ai trong các tầng trời và trái đất đều cầu xin Ngài. Ngài biểu thị công việc hằng ngày.');
INSERT INTO "hasan_abdulkarim" VALUES(55,30,'55:30','Thế Ân Huệ nào của Thượng Đế của hai ngươi mà hai ngươi phủ nhận?');
INSERT INTO "hasan_abdulkarim" VALUES(55,31,'55:31','TA (Allah) sẽ lo (giải quyết) cho các ngươi, hỡi hai loại tạo vật');
INSERT INTO "hasan_abdulkarim" VALUES(55,32,'55:32','Thế Ân Huệ nào của Thượng Đế của hai ngươi mà hai ngươi phủ nhận?');
INSERT INTO "hasan_abdulkarim" VALUES(55,33,'55:33','Này hỡi tập đoàn Jinn và loài người! Nếu các ngươi có khả năng vượt qua được phạm vi của các tầng trời và trái đất thì hãy vượt đi! Nhưng các ngươi chỉ có thể vượt qua được với quyền hạn được ban cấp.');
INSERT INTO "hasan_abdulkarim" VALUES(55,34,'55:34','Thế Ân Huệ nào của Thượng Đế của hai ngươi mà hai ngươi phủ nhận?');
INSERT INTO "hasan_abdulkarim" VALUES(55,35,'55:35','Lửa ngọn và đồng thau (nấu chảy) sẽ được gởi đến trừng phạt hai ngươi và hai ngươi sẽ không được ai giúp đỡ.');
INSERT INTO "hasan_abdulkarim" VALUES(55,36,'55:36','Thế Ân Huệ nào của Thượng Đế của hai ngươi mà hai ngươi phủ nhận?');
INSERT INTO "hasan_abdulkarim" VALUES(55,37,'55:37','Và khi bầu trời bị chẻ làm đôi và sẽ đỏ rực như da thú.');
INSERT INTO "hasan_abdulkarim" VALUES(55,38,'55:38','Thế Ân Huệ nào của Thượng Đế của hai ngươi mà hai ngươi phủ nhận?');
INSERT INTO "hasan_abdulkarim" VALUES(55,39,'55:39','Vào Ngày đó, không một người nào hoặc tên Jinn nào sẽ bị hỏi tội của nó.');
INSERT INTO "hasan_abdulkarim" VALUES(55,40,'55:40','Thế Ân Huệ nào của Thượng Đế của hai ngươi mà hai ngươi phủ nhận?');
INSERT INTO "hasan_abdulkarim" VALUES(55,41,'55:41','Những kẻ tội lỗi sẽ được nhận diện qua những dấu vết (nám đen) của chúng và sẽ bị túm phần tóc trước trán và túm bàn chân.');
INSERT INTO "hasan_abdulkarim" VALUES(55,42,'55:42','Thế Ân Huệ nào của Thượng Đế của hai ngươi mà hai ngươi phủ nhận?');
INSERT INTO "hasan_abdulkarim" VALUES(55,43,'55:43','Đây là hỏa ngục mà những kẻ tội lỗi đã từng phủ nhận;');
INSERT INTO "hasan_abdulkarim" VALUES(55,44,'55:44','Chúng sẽ bước đi giữa nó (Lửa) và giữa nước sôi.');
INSERT INTO "hasan_abdulkarim" VALUES(55,45,'55:45','Thế Ân Huệ nào của Thượng Đế của hai ngươi mà hai ngươi phủ nhận?');
INSERT INTO "hasan_abdulkarim" VALUES(55,46,'55:46','Và cho ai sợ đứng trước mặt Thượng Đế của y thì sẽ có hai Ngôi vườn.');
INSERT INTO "hasan_abdulkarim" VALUES(55,47,'55:47','Thế Ân Huệ nào của Thượng Đế của hai ngươi mà hai ngươi phủ nhận?');
INSERT INTO "hasan_abdulkarim" VALUES(55,48,'55:48','(Ngôi vườn) có những tàn cây tỏa rộng.');
INSERT INTO "hasan_abdulkarim" VALUES(55,49,'55:49','Thế Ân Huệ nào của Thượng Đế của hai ngươi mà hai ngươi phủ nhận?');
INSERT INTO "hasan_abdulkarim" VALUES(55,50,'55:50','Trong hai cái (ngôi vườn) có hai dòng nước suối chảy.');
INSERT INTO "hasan_abdulkarim" VALUES(55,51,'55:51','Thế Ân Huệ nào của Thượng Đế của hai ngươi mà hai ngươi phủ nhận?');
INSERT INTO "hasan_abdulkarim" VALUES(55,52,'55:52','Trong hai cái (ngôi vườn) có từng cặp trái cây, đủ loại.');
INSERT INTO "hasan_abdulkarim" VALUES(55,53,'55:53','Thế Ân Huệ nào của Thượng Đế của hai ngươi mà hai ngươi phủ nhận?');
INSERT INTO "hasan_abdulkarim" VALUES(55,54,'55:54','(Họ) tựa mình nằm nghỉ trên những tấm thảm được thêu trổ xinh đẹp. Và trái cây trong hai ngôi vườn gần tầm tay hái.');
INSERT INTO "hasan_abdulkarim" VALUES(55,55,'55:55','Thế Ân Huệ nào của Thượng Đế của hai ngươi mà hai ngươi phủ nhận?');
INSERT INTO "hasan_abdulkarim" VALUES(55,56,'55:56','Trong chúng sẽ có các tiên nữ trinh trắng với cái nhìn thẹn thùng mà chưa một người hay tên Jinn nào trước họ đã chạm đến thân mình.');
INSERT INTO "hasan_abdulkarim" VALUES(55,57,'55:57','Thế Ân Huệ nào của Thượng Đế của hai ngươi mà hai ngươi phủ nhận?');
INSERT INTO "hasan_abdulkarim" VALUES(55,58,'55:58','Họ (đẹp) như hồng ngọc và ngọc trai.');
INSERT INTO "hasan_abdulkarim" VALUES(55,59,'55:59','Thế Ân Huệ nào của Thượng Đế của hai ngươi mà hai ngươi phủ nhận?');
INSERT INTO "hasan_abdulkarim" VALUES(55,60,'55:60','Há phần thưởng dành cho cái tốt không phải là cái tốt ư?');
INSERT INTO "hasan_abdulkarim" VALUES(55,61,'55:61','Thế Ân Huệ nào của Thượng Đế của hai ngươi mà hai ngươi phủ nhận?');
INSERT INTO "hasan_abdulkarim" VALUES(55,62,'55:62','Và ngoài hai cái (ngôi vườn) đó, có hai ngôi vườn khác.');
INSERT INTO "hasan_abdulkarim" VALUES(55,63,'55:63','Thế Ân Huệ nào của Thượng Đế của hai ngươi mà hai ngươi phủ nhận?');
INSERT INTO "hasan_abdulkarim" VALUES(55,64,'55:64','(Ngôi vườn) mầu xanh đậm.');
INSERT INTO "hasan_abdulkarim" VALUES(55,65,'55:65','Thế Ân Huệ nào của Thượng Đế của hai ngươi mà hai ngươi phủ nhận?');
INSERT INTO "hasan_abdulkarim" VALUES(55,66,'55:66','Trong hai cái (ngôi vườn) có hai dòng nước mạch phụt lên dồi dào.');
INSERT INTO "hasan_abdulkarim" VALUES(55,67,'55:67','Thế Ân Huệ nào của Thượng Đế của hai ngươi mà hai ngươi phủ nhận?');
INSERT INTO "hasan_abdulkarim" VALUES(55,68,'55:68','Trong hai cái (ngôi vườn) có trái cây, trái chà là và trái lựu.');
INSERT INTO "hasan_abdulkarim" VALUES(55,69,'55:69','Thế Ân Huệ nào của Thượng Đế của hai ngươi mà hai ngươi phủ nhận?');
INSERT INTO "hasan_abdulkarim" VALUES(55,70,'55:70','Trong chúng (thiên đàng) có các (tiên nữ) tươi tốt và xinh đẹp.');
INSERT INTO "hasan_abdulkarim" VALUES(55,71,'55:71','Thế Ân Huệ nào của Thượng Đế của hai ngươi mà hai ngươi phủ nhận?');
INSERT INTO "hasan_abdulkarim" VALUES(55,72,'55:72','Các tiên nữ ở trong đài các;');
INSERT INTO "hasan_abdulkarim" VALUES(55,73,'55:73','Thế Ân Huệ nào của Thượng Đế của hai ngươi mà hai ngươi phủ nhận?');
INSERT INTO "hasan_abdulkarim" VALUES(55,74,'55:74','Trước họ chưa có một người hay tên Jinn nào chạm đến thân mình.');
INSERT INTO "hasan_abdulkarim" VALUES(55,75,'55:75','Thế Ân Huệ nào của Thượng Đế của hai ngươi mà hai ngươi phủ nhận?');
INSERT INTO "hasan_abdulkarim" VALUES(55,76,'55:76','(Ho) tựa mình nằm nghỉ trên những chiếc gối mầu xanh và trên những tấm thảm đẹp đẽ.');
INSERT INTO "hasan_abdulkarim" VALUES(55,77,'55:77','Thế Ân Huệ nào của Thượng Đế của hai ngươi mà hai ngươi phủ nhận?');
INSERT INTO "hasan_abdulkarim" VALUES(55,78,'55:78','Phúc thay Đại Danh của Thượng Đế của Ngươi (Muhammad!), Đấng Quyền Uy, Tối Thượng và Quang Vinh.');
INSERT INTO "hasan_abdulkarim" VALUES(56,1,'56:1','Khi "Biến Cố Ắt Xảy Ra" (Giờ Tận Thế) thể hiện.');
INSERT INTO "hasan_abdulkarim" VALUES(56,2,'56:2','"Biến Cố Ắt Xảy Ra" đó không phải là điều giả dối.');
INSERT INTO "hasan_abdulkarim" VALUES(56,3,'56:3','Nó sẽ hạ thấp (người này) và sẽ nâng cao (người kia).');
INSERT INTO "hasan_abdulkarim" VALUES(56,4,'56:4','Khi trái đất bị rung chuyển bằng một sự rung chuyển dữ dội.');
INSERT INTO "hasan_abdulkarim" VALUES(56,5,'56:5','Và khi những quả núi bị vỡ nát tan tành,');
INSERT INTO "hasan_abdulkarim" VALUES(56,6,'56:6','Thành bụi bặm rải rác khắp nơi.');
INSERT INTO "hasan_abdulkarim" VALUES(56,7,'56:7','Và các ngươi sẽ được phân thành ba loại:');
INSERT INTO "hasan_abdulkarim" VALUES(56,8,'56:8','Những người Bạn bên Tay Phải; ai là những người Bạn bên Tay Phải?');
INSERT INTO "hasan_abdulkarim" VALUES(56,9,'56:9','Những người Bạn bên Tay Trái; ai là nhũng người Bạn bên Tay Trái?');
INSERT INTO "hasan_abdulkarim" VALUES(56,10,'56:10','Những Người tiên phong sẽ (vào thiên đàng) trước tiên.');
INSERT INTO "hasan_abdulkarim" VALUES(56,11,'56:11','Họ là những người sẽ gần Allah nhất.');
INSERT INTO "hasan_abdulkarim" VALUES(56,12,'56:12','Trong những ngôi Vườn Hạnh phúc (thiên đàng).');
INSERT INTO "hasan_abdulkarim" VALUES(56,13,'56:13','Đa số thuộc các thế hệ trước,');
INSERT INTO "hasan_abdulkarim" VALUES(56,14,'56:14','Thiểu số thuộc các thế hệ sau.');
INSERT INTO "hasan_abdulkarim" VALUES(56,15,'56:15','(Họ sẽ nằm nghỉ) trên những tràng kỷ lót (vàng và cẩm thạch),');
INSERT INTO "hasan_abdulkarim" VALUES(56,16,'56:16','Tựa mình trên đó, mặt đối mặt.');
INSERT INTO "hasan_abdulkarim" VALUES(56,17,'56:17','Được phục dịch bởi các thiếu niên trẻ trung đi vòng quanh.');
INSERT INTO "hasan_abdulkarim" VALUES(56,18,'56:18','Mang những cốc uống, bình chứa và tách uống từ những suối nước trong.');
INSERT INTO "hasan_abdulkarim" VALUES(56,19,'56:19','Họ sẽ không bị nhức đầu do (uống) nó và sẽ không choáng váng.');
INSERT INTO "hasan_abdulkarim" VALUES(56,20,'56:20','Và có đủ loại trái cây mà họ sẽ tự do lựa chọn.');
INSERT INTO "hasan_abdulkarim" VALUES(56,21,'56:21','Và đủ loại thịt chim mà họ ưa thích.');
INSERT INTO "hasan_abdulkarim" VALUES(56,22,'56:22','Và có những tiên nữ với đôi mắt to (trữ tình),');
INSERT INTO "hasan_abdulkarim" VALUES(56,23,'56:23','Giống như những viên ngọc trai được giữ kỹ;');
INSERT INTO "hasan_abdulkarim" VALUES(56,24,'56:24','Một phần thưởng về những việc (thiện) mà họ đã từng làm.');
INSERT INTO "hasan_abdulkarim" VALUES(56,25,'56:25','Họ sẽ không nghe thấy trong đó những câu chuyện tầm phào và tội lỗi.');
INSERT INTO "hasan_abdulkarim" VALUES(56,26,'56:26','Mà chỉ nghe lời chào "Salam, Salam" - (Bằng an, Bằng an!)');
INSERT INTO "hasan_abdulkarim" VALUES(56,27,'56:27','Những người Bạn bên tay phải. Ai là những người Bạn bên tay phải?');
INSERT INTO "hasan_abdulkarim" VALUES(56,28,'56:28','(Họ sẽ ở) giữa những cây Bách Hương không gai góc,');
INSERT INTO "hasan_abdulkarim" VALUES(56,29,'56:29','Giữa những cầy chuối ra trái chồng lên nhau,');
INSERT INTO "hasan_abdulkarim" VALUES(56,30,'56:30','Và bóng mát tỏa rộng');
INSERT INTO "hasan_abdulkarim" VALUES(56,31,'56:31','Và nước chảy tràn trề,');
INSERT INTO "hasan_abdulkarim" VALUES(56,32,'56:32','Và trái cây dồi dào');
INSERT INTO "hasan_abdulkarim" VALUES(56,33,'56:33','Không dứt mùa cũng không thiếu hụt,');
INSERT INTO "hasan_abdulkarim" VALUES(56,34,'56:34','Và những tràng kỷ cao.');
INSERT INTO "hasan_abdulkarim" VALUES(56,35,'56:35','Quả thật, TA đã tạo sinh họ bằng một sự tạo sinh đặc biệt.');
INSERT INTO "hasan_abdulkarim" VALUES(56,36,'56:36','TA (Allah) đã tạo họ (tiên nữ) hoàn toàn trinh tiết;');
INSERT INTO "hasan_abdulkarim" VALUES(56,37,'56:37','Chỉ biết yêu thương (chồng) cùng lứa tuổi;');
INSERT INTO "hasan_abdulkarim" VALUES(56,38,'56:38','Dành cho những người Bạn của bên Tay Phải.');
INSERT INTO "hasan_abdulkarim" VALUES(56,39,'56:39','Một số (người Bạn bên tay phải) thuộc thế hệ trước;');
INSERT INTO "hasan_abdulkarim" VALUES(56,40,'56:40','Và một số thuộc thế hệ sau.');
INSERT INTO "hasan_abdulkarim" VALUES(56,41,'56:41','Và những người Bạn bên tay trái. Ai là những người Bạn bên tay trái?');
INSERT INTO "hasan_abdulkarim" VALUES(56,42,'56:42','(Chúng sẽ ở) giữa ngọn gió nóng cháy và nước sôi,');
INSERT INTO "hasan_abdulkarim" VALUES(56,43,'56:43','Giữa đám khói mù nghi ngút;');
INSERT INTO "hasan_abdulkarim" VALUES(56,44,'56:44','Không mát cũng không dịu,');
INSERT INTO "hasan_abdulkarim" VALUES(56,45,'56:45','Quả thật, trước đó chúng là những kẻ sống xa hoa lộng lẫy.');
INSERT INTO "hasan_abdulkarim" VALUES(56,46,'56:46','Và ngoan cố phạm những tội lớn.');
INSERT INTO "hasan_abdulkarim" VALUES(56,47,'56:47','Và chúng thường nói: “Phải chăng khi chúng tôi chết và trở thành đất bụi và xương khô, chúng tôi sẽ được phục sinh trở lại?');
INSERT INTO "hasan_abdulkarim" VALUES(56,48,'56:48','“Kể cả tổ tiên xa xưa của chúng tôi nữa ư?”');
INSERT INTO "hasan_abdulkarim" VALUES(56,49,'56:49','Hãy bảo chúng: “Vâng, thật vậy, cả người đời trước lẫn người đời sau."');
INSERT INTO "hasan_abdulkarim" VALUES(56,50,'56:50','"Tất cả sẽ được tập trung đi dự đại hội vào một ngày được qui định."');
INSERT INTO "hasan_abdulkarim" VALUES(56,51,'56:51','Rồi cả các ngươi nữa, hỡi những kẻ lạc lối, những kẻ phủ nhận.');
INSERT INTO "hasan_abdulkarim" VALUES(56,52,'56:52','Các ngươi sẽ ăn trái cây Zaqqum.');
INSERT INTO "hasan_abdulkarim" VALUES(56,53,'56:53','Mà các ngươi sẽ nhét vào đầy bụng.');
INSERT INTO "hasan_abdulkarim" VALUES(56,54,'56:54','Sau đó các ngươi sẽ uống nước sôi, chồng lên thêm.');
INSERT INTO "hasan_abdulkarim" VALUES(56,55,'56:55','Uống nó như những con lạc đà chết khát uống nước vậy.');
INSERT INTO "hasan_abdulkarim" VALUES(56,56,'56:56','Đây là sự chiêu đãi của chúng vào Ngày Phán Xử.');
INSERT INTO "hasan_abdulkarim" VALUES(56,57,'56:57','TA (Allah) đã tạo hóa các ngươi, nhưng tại sao các ngươi không chấp nhận Sự Thật?');
INSERT INTO "hasan_abdulkarim" VALUES(56,58,'56:58','Thế, há các ngươi có thấy cái (tinh dịch) mà các ngươi đã cho xuất ra chăng?');
INSERT INTO "hasan_abdulkarim" VALUES(56,59,'56:59','Phải chăng các ngươi là vị đã tạo ra nó hay TA là Đấng Tạo Hóa?');
INSERT INTO "hasan_abdulkarim" VALUES(56,60,'56:60','TA là Đấng đã định cái chết giữa các ngươi, và TA sẽ không bất lực.');
INSERT INTO "hasan_abdulkarim" VALUES(56,61,'56:61','Trong việc thay hình đổi dạng của các ngươi và tạo các ngươi (trở lại) theo cái (hình dạng) mà các ngươi không thể biết được.');
INSERT INTO "hasan_abdulkarim" VALUES(56,62,'56:62','Và quả thật, các ngươi đã thừa biết việc tạo sinh lần đầu (Adam), thế tại sao các ngươi không chịu nhớ (lưu ý)?');
INSERT INTO "hasan_abdulkarim" VALUES(56,63,'56:63','Thế, há các ngươi có thấy cái mà các ngươi gieo xuống đất chăng?');
INSERT INTO "hasan_abdulkarim" VALUES(56,64,'56:64','Phải chăng các ngươi đã làm mọc nó hay TA là Đấng đã làm mọc ra?');
INSERT INTO "hasan_abdulkarim" VALUES(56,65,'56:65','Nếu muốn, TA sẽ làm cho nó nát bấy và các ngươi sẽ đâm ra ngạc nhiên.');
INSERT INTO "hasan_abdulkarim" VALUES(56,66,'56:66','“Quả thật, chúng tôi (là những kẻ) bị thất mùa."');
INSERT INTO "hasan_abdulkarim" VALUES(56,67,'56:67','“Không, chúng tôi bị tước mất hết.”');
INSERT INTO "hasan_abdulkarim" VALUES(56,68,'56:68','Há các ngươi có thấy nước mà các ngươi uống chăng?');
INSERT INTO "hasan_abdulkarim" VALUES(56,69,'56:69','Phải chăng các ngươi làm cho nó xuống từ những đám mây hay TA là Đấng ban nó xuống?');
INSERT INTO "hasan_abdulkarim" VALUES(56,70,'56:70','Nếu muốn, TA sẽ làm cho nó hóa mặn, tại sao các ngươi không tri ân?');
INSERT INTO "hasan_abdulkarim" VALUES(56,71,'56:71','Há các ngươi có thấy lửa mà các ngươi nhúm chăng?');
INSERT INTO "hasan_abdulkarim" VALUES(56,72,'56:72','Phải chăng các ngươi đã tạo loại cây cho ra lửa hay TA là Đấng đã tạo?');
INSERT INTO "hasan_abdulkarim" VALUES(56,73,'56:73','TA dùng nó (lửa) làm một vật nhắc nhở (về hỏa ngục) và làm một vật dụng cho những người sống tại những vùng sa mạc.');
INSERT INTO "hasan_abdulkarim" VALUES(56,74,'56:74','Do đó, hãy tán dương Đại Danh của Thượng Đế của Ngươi, Đấng Chí Đại.');
INSERT INTO "hasan_abdulkarim" VALUES(56,75,'56:75','Bởi thế, TA (Allah) thề bởi hiện tượng lặn khuất của các vì sao;');
INSERT INTO "hasan_abdulkarim" VALUES(56,76,'56:76','Và đó là một lời thề trọng đại, nếu các ngươi biết.');
INSERT INTO "hasan_abdulkarim" VALUES(56,77,'56:77','Và đó là một sự xướng đọc (Qur''an) rất vinh dự.');
INSERT INTO "hasan_abdulkarim" VALUES(56,78,'56:78','Trong một Kinh Sách được giữ kỹ (trên cao)');
INSERT INTO "hasan_abdulkarim" VALUES(56,79,'56:79','Mà không ai được phép sờ đến ngoại trừ những người trong sạch.');
INSERT INTO "hasan_abdulkarim" VALUES(56,80,'56:80','Một sự Mặc Khải do Thượng Đế của vũ trụ ban xuống.');
INSERT INTO "hasan_abdulkarim" VALUES(56,81,'56:81','Thế, phải chăng đây là lời tường trình mà các ngươi xem nhẹ?');
INSERT INTO "hasan_abdulkarim" VALUES(56,82,'56:82','Và các ngươi đã dùng (Qur''an) trong việc tìm kiếm bổng lộc; (thay vì tri ân Allah) các ngươi đã phủ nhận (Nó)?');
INSERT INTO "hasan_abdulkarim" VALUES(56,83,'56:83','Thế tại sao các ngươi không (can thiệp) khi nó lên tận cổ (của y)?');
INSERT INTO "hasan_abdulkarim" VALUES(56,84,'56:84','Và lúc đó các ngươi chỉ lấy mắt nhìn.');
INSERT INTO "hasan_abdulkarim" VALUES(56,85,'56:85','Và TA ở gần y hơn các ngươi nhưng các ngươi không thấy.');
INSERT INTO "hasan_abdulkarim" VALUES(56,86,'56:86','Nếu tự cho mình không bị phán xử thì tại sao các ngươi không');
INSERT INTO "hasan_abdulkarim" VALUES(56,87,'56:87','Đưa nó (hồn) trở lại (cho thân xác), nếu các ngươi nói thật?');
INSERT INTO "hasan_abdulkarim" VALUES(56,88,'56:88','Bởi thế, nếu y là một trong những người gần Allah nhất,');
INSERT INTO "hasan_abdulkarim" VALUES(56,89,'56:89','Thì sẽ được an nghỉ với hương thơm và thiên đàng Hạnh Phúc.');
INSERT INTO "hasan_abdulkarim" VALUES(56,90,'56:90','Và nếu y là một trong những người Bạn của Tay Phải,');
INSERT INTO "hasan_abdulkarim" VALUES(56,91,'56:91','Thì (sẽ được chào) “Salam!” từ những người Bạn bên Tay Phải.');
INSERT INTO "hasan_abdulkarim" VALUES(56,92,'56:92','Và nếu y là một trong những kẻ phủ nhận (Chân Lý) và lạc lối,');
INSERT INTO "hasan_abdulkarim" VALUES(56,93,'56:93','Thì sẽ được chiêu đãi bằng nước sôi,');
INSERT INTO "hasan_abdulkarim" VALUES(56,94,'56:94','Và chịu đốt (nướng) trong hỏa ngục.');
INSERT INTO "hasan_abdulkarim" VALUES(56,95,'56:95','Quả thật, đây là sự Thật rất chắc chắn;');
INSERT INTO "hasan_abdulkarim" VALUES(56,96,'56:96','Bởi thế, hãy tán dương đại danh của Thượng Đế của Ngươi, Đấng Chí Đại.');
INSERT INTO "hasan_abdulkarim" VALUES(57,1,'57:1','Mọi vật trong các tầng trời và trái đất dâng lời tán dương lên Allah bởi vì Ngài là Đấng Toàn Năng, Đấng Chí Minh.');
INSERT INTO "hasan_abdulkarim" VALUES(57,2,'57:2','Ngài nắm quyền thống trị các tầng trời và trái đất. Ngài làm cho sống và gây cho chết. Và Ngài là Đấng Toàn Năng trên tất cả mọi thứ.');
INSERT INTO "hasan_abdulkarim" VALUES(57,3,'57:3','Ngài là Đấng Đầu Tiên và là Đấng Cuối Cùng; và là Đấng Hiển Hiện và là Đấng Ẩn Tàng; và Ngài là Đấng Toàn Tri, Biết hết mọi vật.');
INSERT INTO "hasan_abdulkarim" VALUES(57,4,'57:4','Ngài là Đấng đã tạo hóa các tầng trời và trái đất trong sáu Ngày rồi ngự lên Ngai Vương. Ngài Biết rõ vật gì chui xuống dưới đất và vật gì từ dưới đất ngoi lên và vật gì từ trên trời rơi xuống và vật gì thăng lên trời. Và Ngài ở cùng với các ngươi bất cứ nơi nào các ngươi có mặt.( Và Allah thấy rõ điều các ngươi làm.');
INSERT INTO "hasan_abdulkarim" VALUES(57,5,'57:5','Ngài nắm quỵền thống trị các tầng trời và trái đất. Và mọi việc đều được trình về cho Ngài (quyết định).');
INSERT INTO "hasan_abdulkarim" VALUES(57,6,'57:6','Ngài nhập ban đêm vào ban ngày và nhập ban ngày vào ban đêm(. Và Ngài biết điều nằm trong lòng (của mọi ngươi).');
INSERT INTO "hasan_abdulkarim" VALUES(57,7,'57:7','Hãy tin tưởng nơi Allah và Sứ Giả của Ngài. Và hãy chi dùng (cho Chính Nghĩa của Allah) tài sản mà Ngài đã ban cho các ngươi thừa hưởng. Bởi thế, những ai trong các ngươi tin tưởng và chi dùng (theo con đường của Allah) thì sẽ nhận một phần thưởng rất lớn.');
INSERT INTO "hasan_abdulkarim" VALUES(57,8,'57:8','Và tại sao các ngươi không tin tưởng nơi Allah trong lúc Sứ Giả (Muhammad) đã kêu gọi các ngươi hãy tin tưởng nơi Thượng Đế của các ngươi và chắc chắn Ngài (Allah) đã nhận lời giao ước của các ngươi, nếu các ngươi là những người thực sự có đức tin.');
INSERT INTO "hasan_abdulkarim" VALUES(57,9,'57:9','Ngài là Đấng đã ban cho Người bề tôi (Muhammad) của Ngài các Lời Mặc Khải rõ rệt để Ngài có thể đưa các ngươi từ tăm tối ra ánh sáng. Quả thật, Allah Đại Lượng và Khoan Dung đối với các ngươi.');
INSERT INTO "hasan_abdulkarim" VALUES(57,10,'57:10','Và tại sao các ngươi không chi dùng cho Chính Đạo của Allah trong lúc quyền thừa kế các tầng trời và trái đất thuộc về Allah? Không ngang bằng nhau giữa các ngươi, ai là người đã chỉ dùng (tài sản của mình) và chiến đấu (cho Chính Đạo của Allah) trước khi xảy ra cuộc đại thắng (vào Makkah, với người chi dùng và chiến đấu về sau). Những người này trội hơn những người chi dùng và chiến đấu về sau về cấp bậc. Tuy nhiên, Allah hứa ban phần thưởng tốt cho tất cả. Và Ngài Am Tường những điều các ngươi làm.');
INSERT INTO "hasan_abdulkarim" VALUES(57,11,'57:11','Ai là người sẽ cho Allah mượn một phần mượn tốt? Bởi vì Ngài sẽ gia tăng nó lên nhiều cho y. Và y sẽ được trọng thưởng dồi dào.');
INSERT INTO "hasan_abdulkarim" VALUES(57,12,'57:12','Vào Ngày mà Ngươi sẽ thấy những người có đức tin, nam và nữ: Ánh sáng của họ sẽ chạy trước mặt họ và bên phải của họ. “Ngày nay quí vị được tin vui: Những Ngôi vườn bên dưới có các dòng sông chảy (thiên đàng) để vào ở trong đó đời đời. Đó là một thắng lợi vĩ đại.”');
INSERT INTO "hasan_abdulkarim" VALUES(57,13,'57:13','(Cũng là) ngày mà những kẻ giả tạo đức tin, nam và nữ, sẽ nói với những người có đức tin như sau: “Hãy đợi chúng tôi với! Hãy cho chúng tôi mượn một chút ánh sáng của quí vị.” Sẽ có lời đáp lại: “Hãy trở lui về phía sau các anh và tìm ánh sáng đâu đó!” Thế rồi một bức tường cao có cửa sẽ được dựng lên để phân cách đôi bên; phía bên trong nó (cửa) sẽ là sự Khoan Dung (của Allah) và phía bên ngoài của nó sẽ là mục tiêu của sự trừng phạt (hỏa ngục).”');
INSERT INTO "hasan_abdulkarim" VALUES(57,14,'57:14','(Những kẻ giả tạo đức tin) sẽ gọi họ (những người có đức tin), bảo: “Há chúng tôi không được ở cùng với quí vị hay sao?” Họ đáp: “Vâng, nhưng các anh đã tự để cho mình bị cám dỗ, các anh chờ đợi (sự thất bại của chúng tôi); các anh nghi ngờ (Lời Hứa của Allah); và những ước muốn (hão huyền) của các anh đã lừa dối các anh cho đến khi Mệnh Lệnh của Allah thể hiện. Và tên đại bịp (Shaytan) đã lừa gạt các anh về Allah.”');
INSERT INTO "hasan_abdulkarim" VALUES(57,15,'57:15','“Cho nên ngày nay, việc xin chuộc mạng từ các anh và từ những kẻ không có đức tin sẽ không được (Allah) chấp nhận. Chỗ ở của các anh sẽ là hỏa ngục, nó xứng đáng với các anh và là một nơi đến cuối cùng rất tồi tệ.”');
INSERT INTO "hasan_abdulkarim" VALUES(57,16,'57:16','Há chưa phải là lúc để cho những ai có đức tin (ý thức) rằng tấm lòng của họ nên khiêm tốn trước Lời Nhắc Nhở của Allah và trước Chân Lý đã được ban xuống hay sao và không nên như là những kẻ đã được ban cho Kinh Sách trước đây, do bởi thời hạn đã kéo dài cho nên tấm lòng của họ chai cứng lại (và không tin tưởng)? Và đa số bọn họ là những kẻ bất tuân, dấy loạn.');
INSERT INTO "hasan_abdulkarim" VALUES(57,17,'57:17','Nên biết rằng Allah làm cho đất sống lại sau khi nó đã chết (khô). Chắc chắn Ngài trình bày rõ các Lời Mặc Khải cho các ngươi để may ra các ngươi hiểu được.');
INSERT INTO "hasan_abdulkarim" VALUES(57,18,'57:18','Quả thật, những người bố thí (Sadaqah), nam và nữ, và cho Allah mượn một phần mượn tốt thì (Allah) sẽ tăng nó lên nhiều cho họ, và sẽ ban cho họ một phần thưởng rộng rãi.');
INSERT INTO "hasan_abdulkarim" VALUES(57,19,'57:19','Và những ai tin tưởng nơi Allah và các Sứ Giả của Ngài thì đều là những người chân thật và là những chứng nhân của sự Thật (người tử đạo) dưới cái nhìn của Thượng Đế của họ. Họ sẽ được phần thưởng và ánh sáng của họ. Ngược lại, những ai không có đức tin và phủ nhận các Lời Mặc Khải của TA (Allah) thì sẽ là cư dân nơi hỏa ngục.');
INSERT INTO "hasan_abdulkarim" VALUES(57,20,'57:20','Nên biết rằng cuộc sống trần tục này chỉ là trò vui chơi, thú tiêu khiển, một sự tô điểm và khoe khoang tâng bốc lẫn nhau và tranh đua làm giàu và sinh con đẻ cái. Hình ảnh của nó giống như hoa màu (đâm trổ xanh tươi) sau cơn mưa, làm mát dạ người trồng; rồi nó bắt đầu héo hon, ngươi (hỡi người!) thấy nó vàng úa rồi khô khan và vỡ vụn. Nhưng ở đời sau, sẽ có một sự trừng phạt khủng khiếp (đối với những kẻ tội lỗi) và sự Tha thứ và Hài lòng của Allah (dành cho những người có đức tin và làm việc thiện). Và đời sống trần tục này chỉ là một sự hưởng thụ tạm bợ lừa dối (con người).');
INSERT INTO "hasan_abdulkarim" VALUES(57,21,'57:21','Hãy thi đua nhau chạy đến xin Thượng Đế của các ngươi tha thứ và (ban cho) thiên đàng nơi mà khoảng rộng bằng khoảng rộng của trời và đất, được chuẩn bị sẵn cho những ai có đức tin nơi Allah và các Sứ Giả của Ngài. Đó là Thiên lộc mà Allah sẽ ban cho người nào Ngài muốn bởi vì Allah là Chủ Nhân của Thiên lộc vĩ đại.');
INSERT INTO "hasan_abdulkarim" VALUES(57,22,'57:22','Không một tai họa nào rơi xuống trái đất hoặc nhằm vào bản thân của các ngươi mà lại không được ghi chép trong Quyển Sổ Định Mệnh trước khi TA (Allah) thể hiện nó. Quả thật, việc đó rất dễ đối với Allah.');
INSERT INTO "hasan_abdulkarim" VALUES(57,23,'57:23','Để cho các ngươi chớ buồn về vật gì đã mất và chớ mừng cuống quýt về vật gì đã được ban cho các ngươi. Và Allah không yêu thương bất cứ người khoác lác khoe khoang nào:');
INSERT INTO "hasan_abdulkarim" VALUES(57,24,'57:24','Những ai keo kiệt và xúi giục người khác keo kiệt. Và kẻ nào quay lưng (không chi dùng cho Chính Nghĩa của Allah) thì quả thật Allah Rất Mực Giàu Có và Rất Đáng Ca Tụng (chẳng cần đến của bố thí của các ngươi).');
INSERT INTO "hasan_abdulkarim" VALUES(57,25,'57:25','Quả thật, TA (Allah) đã cử các Sứ Giả của TA đến với những Bằng chứng rõ rệt và đã ban xuống cùng với Họ Kinh Sách và chiếc Cân (Công lý) để cho nhân loại dựa theo đó mà duy trì nền công lý; và TA đã ban sắt xuống trong đó là một sức tấn công mãnh liệt (chiến tranh) và nhiều nguồn lợi khác cho nhân loại, mục đích để Allah biết ai là người sẽ ủng hộ (Chính Nghĩa của) Ngài và Sứ Giả của Ngài. Quả thật, Allah Rất Quyền Lực, Rất Quyền Năng.');
INSERT INTO "hasan_abdulkarim" VALUES(57,26,'57:26','Và chắc chắn TA (Allah) đã phái Nuh và Ibrahim đến (làm Sứ Giả) và đã chỉ định hậu duệ của hai người lãnh Sứ Mạng tiếp thu mặc khải và tiếp nhận Kinh Sách. Bởi thế, trong họ, có người đi đúng đường, nhưng đa số là những kẻ dấy loạn, bất tuân.');
INSERT INTO "hasan_abdulkarim" VALUES(57,27,'57:27','Rồi TA phái các Sứ Giả (khác) của TA (Allah) nối gót chân Họ. Và TA đã phái Ysa, con trai của Maryam tiếp theo và ban cho Y Kinh Injil và TA đã đặt trong lòng của những ai tuân theo Y (Ysa) lòng xót thương và sự khoan dung nhưng chế độ tu hành (không lập gia đình) là tự họ bày ra chứ TA đã không qui định cho họ bao giờ, ngoại trừ việc tìm cách làm hài lòng Allah; nhưng họ không chăm lo điều mà lẽ ra họ phải chăm lo. Tuy nhiên, TA vẫn ban cho những ai trong họ có đức tin phần thưởng của họ. Nhưng đa số bọn họ là những người dấy loạn, bất tuân.');
INSERT INTO "hasan_abdulkarim" VALUES(57,28,'57:28','Hỡi những ai có đức tin! Hãy sợ Allah và tin tưởng nơi Sứ Giả (Muhammad) của Ngài. Ngài (Allah) sẽ lấy lòng Khoan dung của Ngài mà ban cho các ngươi gấp đôi (phần thưởng). Ngài sẽ ban cho các ngươi ánh sáng nhờ đó các ngươi sẽ bước đi (đúng theo Chính Đạo) và sẽ tha thứ cho các ngươi bởi vì Allah Hằng Tha Thứ, Rất Mực Khoan Dung.');
INSERT INTO "hasan_abdulkarim" VALUES(57,29,'57:29','Để cho những người dân Kinh Sách biết rằng họ không quyết định được điều gì về Thiên Ân của Allah. Và rằng Thiên Ân hoàn toàn nằm trong Tay của Allah. Ngài ban nó cho người nào mà Ngài muốn bởi vì Allah là Chủ Nhân của Thiên Ân vĩ đại.');
INSERT INTO "hasan_abdulkarim" VALUES(58,1,'58:1','Quả thật, Allah đã nghe lời nói của người phụ nữ (tên Khaulah bint Tha''laba) đã khiếu nại với Ngươi (Muhammad) về việc người chồng của nữ (tên Aus bin As-Samit) và than thở với Allah; và Allah nghe lời đối thoại giữa hai ngươi (Muhammad và nữ). Quả thật, Allah Hằng Nghe và Hằng Thấy (mọi việc).');
INSERT INTO "hasan_abdulkarim" VALUES(58,2,'58:2','Ai trong các ngươi (mắng vợ): “Mày đối với tao như cái lưng của mẹ tao” để thôi vợ bằng lối Al-Zihar( (thì nên biết) họ (các bà vợ) không thể là người mẹ của họ được; bởi vì mẹ của họ chỉ là những ai đã hạ sanh họ. Và chắc chắn họ đã thốt ra lời lẽ sai quấy và chua ngoa (để đạt cho được mục đích của họ). Và quả thật, Allah Hằng Độ Lượng và Hằng Tha Thứ.');
INSERT INTO "hasan_abdulkarim" VALUES(58,3,'58:3','Và những ai thôi vợ theo lối Al-Zihar rồi muốn rút lại lời mình tuyên bố thì phải (chịu phạt bằng cách) giải phóng một người nô lệ trước khi hai người trở lại ăn nằm với nhau. Đó là lời cảnh báo về việc đó. Và Allah rất mực am tường về những điều các người làm.');
INSERT INTO "hasan_abdulkarim" VALUES(58,4,'58:4','Nhưng ai không tìm ra (phương tiện để làm việc đó) thì phải nhịn chay suốt hai tháng liền trước khi hai người trở lại ăn nằm với nhau; nhưng nếu ai không có khả năng (nhịn chay) thì phải nuôi ăn sáu mươi người nghèo. Như thế là để chứng tỏ các ngươi tin tưởng nơi Allah và Sứ Giả của Ngài. Và đó là những giới hạn qui định bởi Allah. Và những người cãi lệnh sẽ bị trừng phạt đau đớn.');
INSERT INTO "hasan_abdulkarim" VALUES(58,5,'58:5','Quả thật, những ai chống đối (Mệnh Lệnh của) Allah và Sứ Giả của Ngài thì sẽ bị hạ nhục giống như những ai trước họ đã bị hạ nhục; và quả thật TA (Allah) đã ban các Lời Mặc Khải xuống rõ ràng. Và những người cãi lệnh sẽ bị trừng phạt nhục nhã.');
INSERT INTO "hasan_abdulkarim" VALUES(58,6,'58:6','Vào Ngày mà Allah sẽ phục sinh tất cả bọn chúng, lúc đó Ngài sẽ báo cho chúng biết về những điều (tội lỗi) mà chúng đã làm. Allah đã cho ghi chép nó (vào sổ) nhưng chúng lại quên mất nó. Và Allah là Nhân Chứng cho tất cả mọi việc.');
INSERT INTO "hasan_abdulkarim" VALUES(58,7,'58:7','Há Ngươi (Muhammad!) không nhận thấy rằng Allah biết hết mọi vật trong các tầng trời và mọi vật dưới đất. Không có một cuộc mật nghị nào giữa ba người mà Ngài không là (người) thứ tư tham dự với họ; và không (một cuộc mật nghị nào) giữa năm người mà Ngài không là (người) thứ sáu tham dự với họ; hoặc ít hay đông hơn số đó mà Ngài không tham dự cùng với họ bất cứ nơi nào họ có mặt. Rồi vào Ngày phục sinh, Ngài sẽ cho họ biết về những điều họ đã từng làm. Bởi vì, quả thật, Allah biết rõ hết mọi điều.');
INSERT INTO "hasan_abdulkarim" VALUES(58,8,'58:8','Há Ngươi không nhận thấy những kẻ đã bị cấm không được mật nghị, rồi sau đó, tái phạm điều mà chúng đã bị cấm? Và chúng mật nghị với nhau bàn tính chuyện tội lỗi, chuyện phạm giới và chuyện bất tuân Sứ Giả. Và khi chúng đến gặp Ngươi, chúng không chào Ngươi bằng lời chào mà Allah đã dùng để chào Ngươi và chúng bảo nhau: "Tại sao Allah không phạt bọn ta vì điều mà bọn ta nói?” hỏa ngục thôi cũng đủ trừng phạt chúng; chúng sẽ bị đốt trong đó, một nơi đến cuối cùng thật xấu!');
INSERT INTO "hasan_abdulkarim" VALUES(58,9,'58:9','Hỡi những ai có đức tin! Khi mật nghị với nhau, các ngươi chớ nên bàn tính chuyện tội lỗi, chuyện phạm giới và chuyện bất tuân Sứ Giả, mà hãy bàn chuyện đạo đức và chuyện kính sợ Allah. Và hãy sợ Allah, Đấng mà các ngươi sẽ được đưa trở về trinh diện (để chịu việc Phán Xử).');
INSERT INTO "hasan_abdulkarim" VALUES(58,10,'58:10','Chuyện mật nghị (bàn điều tội lỗi) chỉ do Shaytan xúi giục với mục đích gây rối cho những người có đức tin. Nhưng nó (Shaytan) chẳng hãm hại họ được tí nào trừ phi Allah cho phép. Và những người có đức tin nên tin cậy mà phó thác cho Allah.');
INSERT INTO "hasan_abdulkarim" VALUES(58,11,'58:11','Hỡi những ai có đức tin! Khi các ngươi được yêu cầu nới rộng chỗ ngồi cho người khác trong cuộc họp, hãy nới ra. Allah (với lòng Khoan dung của Ngài) sẽ nới rộng chỗ ngồi cho các ngươi. Và khi các ngươi được yêu cầu đứng dậy, hãy đứng dậy. Allah sẽ nâng những ai tin tưởng trong các ngươi và những ai được ban sự hiểu biết lên điạ vị và cấp bậc cao. Và Allah Rất Mực Am Tường về những điều các người làm.');
INSERT INTO "hasan_abdulkarim" VALUES(58,12,'58:12','Hỡi những ai có đức tin! Khi các ngươi đến gặp Sứ Giả (Muhammad) để tham khảo ý kiến về chuyện riêng tư, hãy bố thí (cho người nghèo) trước khi các ngươi tham khảo ý kiến. Điều đó tốt và trong sạch hơn cho các ngươi. Nhưng nếu các ngươi không tìm được phương tiện (để bố thí) thì quả thật, Allah Hằng Tha Thứ và Khoan Dung (các ngươi).');
INSERT INTO "hasan_abdulkarim" VALUES(58,13,'58:13','Phải chăng các ngươi sợ mất tiền vào việc bố thí trước khi đến tham khảo việc riêng (với Sứ Giả) hay sao? Nhưng khi các ngươi không làm được và Allah quay lại tha thứ cho các ngươi thì (ít ra) các ngươi phải dâng lễ nguyện Salah một cách chu đáo và đóng Zakah và vâng lệnh Allah và Sứ Giả của Ngài. Và Allah Rất Mực Am Tường về những điều các ngươi làm.');
INSERT INTO "hasan_abdulkarim" VALUES(58,14,'58:14','Há Ngươi không thấy những kẻ (giả tạo đức tin) quay lại (kết bạn) với đám người (Do thái) mà Allah giận dữ ư? Họ không là thành phần của các ngươi (Muslim) và cũng không là thành phần của chúng (người Do thái), và họ mang điều giả dối ra thề thốt trong lúc họ biết điều đó.');
INSERT INTO "hasan_abdulkarim" VALUES(58,15,'58:15','Allah đã chuẩn bị cho họ một sự trừng phạt nghiêm khắc. Những điều mà họ đã từng làm thật là tội lỗi.');
INSERT INTO "hasan_abdulkarim" VALUES(58,16,'58:16','Họ đã dùng lời thề để làm bình phong (che đậy việc làm tội lỗi của họ). Bởi thế, họ đã cản trở (người khác) theo Chính Đạo của Allah. Vì thế, họ sẽ chịu một sự trừng phạt nhục nhã.');
INSERT INTO "hasan_abdulkarim" VALUES(58,17,'58:17','Của cải và con cái của họ sẽ không bao giờ giúp họ thoát khỏi (sự trừng phạt của) Allah. Họ sẽ là những người bạn của hỏa ngục, nơi mà họ sẽ vào ở trong đó (đời đời).');
INSERT INTO "hasan_abdulkarim" VALUES(58,18,'58:18','Vào Ngày mà Allah sẽ phục sinh tất cả bọn họ (để chịu việc Phán Xử), họ sẽ thề thốt với Ngài như họ đã từng thề thốt với các ngươi (hỡi Muslim). Và họ nghĩ rằng họ sẽ ỷ vào một cái gì đó (để biện minh với Ngài). Không, thật sự, họ chỉ là những tên nói dối.');
INSERT INTO "hasan_abdulkarim" VALUES(58,19,'58:19','Shaytan đã làm chủ được họ và làm cho họ quên mất việc tưởng nhớ Allah. Họ thuộc bè đảng của Shaytan. Này, quả thật, bè đảng của Shaytan nhất định sẽ thua.');
INSERT INTO "hasan_abdulkarim" VALUES(58,20,'58:20','Quả thật, những ai chống đối (Mệnh Lệnh của) Allah và Sứ Giả của Ngài thì sẽ là những kẻ bị hạ nhục.');
INSERT INTO "hasan_abdulkarim" VALUES(58,21,'58:21','Allah đã ban hành chỉ dụ: “TA và các Sứ Giả của TA nhất định sẽ thắng.” Bởi vì Allah Toàn Lực và Toàn Năng.');
INSERT INTO "hasan_abdulkarim" VALUES(58,22,'58:22','Ngươi (Nabi) sẽ không tìm thấy một đám người nào đã có đức tin nơi Allah và Ngày (Phán Xử) Cuối Cùng lại kết thân với ai là người chống đối Allah và Sứ Giả của Ngài dẫu rằng đó là cha mẹ, con cái, anh em hay bà con ruột thịt của họ đi nữa. Bởi vì họ là những người mà Đức tin đã được khắc ghi vào trong quả tim (tâm khảm) của họ và (Allah) đã tăng cường họ với Tinh thần (Ruh) từ Ngài. Và Ngài sẽ thu nhận họ vào những Ngôi Vườn (thiên đàng) có các dòng sông chảy bên dưới để họ vào ở trong đó đời đời. Allah sẽ hài lòng với họ và họ sẽ hài lòng với Ngài. Họ nhập đảng của Allah. Thật sự chỉ có đảng của Allah mới thắng lợi vẻ vang thôi.');
INSERT INTO "hasan_abdulkarim" VALUES(59,1,'59:1','Mọi vật trong các tầng trời và mọi vật dưới đất đều tán dương Allah bởi vì Ngài là Đấng Toàn Năng, Đấng Chí Minh.');
INSERT INTO "hasan_abdulkarim" VALUES(59,2,'59:2','Ngài là Đấng đã trục xuất những kẻ phản nghịch trong nhóm Người dân Kinh Sách (nhóm Do thái Banu An-Nadhir) ra khỏi nhà cửa của chúng nhân cuộc tập hợp (lực lượng để trừng phạt chúng) lần đầu. Các ngươi không nghĩ rằng chúng sẽ bỏ đi và chính chúng cũng nghĩ thành lũy kiên cố của chúng có thể bảo vệ chúng tránh khỏi (sự trừng phạt của) Allah. Nhưng (lực lượng của) Allah đến tấn công chúng từ một nơi mà chúng không ngờ tới. Và Ngài ném sự kinh hãi vào lòng của chúng khiến chúng tự đập phá nhà cửa với bàn tay của chúng và với bàn tay của những người tin tưởng. Bởi thế, hãy tiếp thu (bài học) cảnh báo, hỡi những kẻ có đôi mắt biết nhìn!');
INSERT INTO "hasan_abdulkarim" VALUES(59,3,'59:3','Và nếu Allah không quyết định trục xuất chúng (ra khỏi thành phố Madinah) thì chắc chắn Ngài sẽ trừng phạt chúng ở trần gian này. Nhưng chắc chắn chúng sẽ chịu sự trừng phạt của hỏa ngục ở đời sau.');
INSERT INTO "hasan_abdulkarim" VALUES(59,4,'59:4','Như thế là vì chúng đã chống đối Allah và Sứ Giả của Ngài. Và ai chống đối Allah thì (nên biết) Allah rất nghiêm khắc trong việc trừng phạt.');
INSERT INTO "hasan_abdulkarim" VALUES(59,5,'59:5','Bất cứ cây chà là nào mà các ngươi (hỡi Muslim!) đốn ngã hoặc cây nào mà các ngươi chừa lại để cho đứng thẳng trên rễ của nó thì (mọi việc làm đó) đều do phép của Allah cả và để cho Ngài hạ nhục những kẻ dấy loạn, bất tuân Allah.');
INSERT INTO "hasan_abdulkarim" VALUES(59,6,'59:6','Và chiến lợi phẩm nào mà Allah tịch thu từ chúng để ban cho Sứ Giả của Ngài thì các ngươi không cần cưỡi ngựa hay lạc đà đến chinh phục. Ngược lại, chính Allah ban thẩm quyền cho các Sứ Giả của Ngài kiểm soát người nào mà Ngài muốn bởi vì Allah là Đấng Toàn Năng trên tất cả mọi thứ.');
INSERT INTO "hasan_abdulkarim" VALUES(59,7,'59:7','Chiến lợi phẩm nào mà Allah tịch thu từ dân cư của những thị trấn để ban cho Sứ Giả của Ngài thì là của Allah, và của Sứ Giả (Muhammad), và của bà con ruột thịt (của Muhammad), và của các trẻ mồ côi, và của người thiếu thốn, và của người lỡ đường nhằm mục đích để cho nó không nằm trong vòng (luân lưu) của người giàu có trong các ngươi. Và vật gì mà Sứ Giả ban cho các ngươi thì hãy nhận nó và vật gì mà Sứ Gia cấm thì hãy từ khước nó và hãy sợ Allah bởi vì, quả thật, Allah rất nghiêm khắc trong việc trừng phạt.');
INSERT INTO "hasan_abdulkarim" VALUES(59,8,'59:8','(Trong số chiến lợi phẩm đó cũng có phần) dành cho những người Muhajir (di cư từ Makkah) nghèo túng vốn đã bị trục xuất khỏi nhà cửa của họ (tại Makkah) và bị tước đoạt tài sản, ra đi tìm thiên lộc và sự hài lòng của Allah và đã ủng hộ (Chính Nghĩa của) Allah và Sứ Giả của Ngài. Họ là những người chân thật.');
INSERT INTO "hasan_abdulkarim" VALUES(59,9,'59:9','Và những ai đã có nhà cửa (tại Madinah) và có đức tin (tức người Ansar) trước họ (người Muhajir) yêu thương người (Muhajir) nào đã di cư (từ Makkah) đến sống với họ và trong lòng họ không có một chút thèm muốn những vật đã được ban cho (những người Muhajir); ngược lại, họ đã dành cho (những người Muhajir) quyền ưu tiên hơn mình mặc dầu họ cũng là những người thiếu thốn. Và ai giữ được lòng mình khỏi tham lam thì là những người chắc chắn sẽ thành công.');
INSERT INTO "hasan_abdulkarim" VALUES(59,10,'59:10','Và những ai đến sau họ cầu nguyện: “Lạy Thượng Đế của bầy tôi! Xin Ngài tha thứ cho bầy tôi và cho những người anh em đã tin tưởng trước chúng tôi và xin Ngài chớ đặt trong lòng bầy tôi nỗi oán thù đối với những người đã tin tưởng bởi vì quả thật Ngài Rất Mực Nhân Từ, Rất Mực Khoan Dung.”');
INSERT INTO "hasan_abdulkarim" VALUES(59,11,'59:11','Há Ngươi (Muhammad) đã không nhìn về những kẻ giả tạo đức tin? Chúng nói với những người anh em của chúng gồm những kẻ không có đức tin trong nhóm Người dân Kinh Sách như sau: “Nếu các anh bị trục xuất thì chúng tôi sẽ ra đi cùng với các anh và chúng tôi sẽ không bao giờ nghe theo một ai về vấn đề của các anh; và nếu các anh bị tấn công thì chúng tôi sẽ trợ chiến các anh.” Nhưng Allah làm chứng, chúng là những tên nói dối.');
INSERT INTO "hasan_abdulkarim" VALUES(59,12,'59:12','Khẳng định rằng nếu họ (những người Do thái Banu Nadhir tại Madinah) bị trục xuất thì chúng (những tên giả tạo đức tin) sẽ không ra đi cùng với họ; và nếu họ bị tấn công thì chúng sẽ không trợ giúp họ; và nếu chúng có giúp họ đi nữa thì chúng cũng sẽ quay lưng (phản bội). Và họ sẽ không được ai cứu giúp.');
INSERT INTO "hasan_abdulkarim" VALUES(59,13,'59:13','Chắc chắn trong lòng của chúng, các ngươi đáng sợ hơn Allah. Sở dĩ như thế là vì chúng là một đám người không thông hiểu.');
INSERT INTO "hasan_abdulkarim" VALUES(59,14,'59:14','Chúng sẽ không hợp lực đánh các ngươi trừ phi cố thủ trong những thị xã có thành lũy kiên cố hoặc chiến đấu đằng sau những bức tường. Nỗi thù nghịch giữa bọn chúng với nhau rất kịch liệt. Ngươi nghĩ rằng chúng đoàn kết nhưng kỳ thật tấm lòng của chúng chia rẽ. Sở dĩ như thế là vì chúng là một đám người không hiểu biết.');
INSERT INTO "hasan_abdulkarim" VALUES(59,15,'59:15','Giống trường hợp của những kẻ (Do thái Banu Qaynuqa’) vừa bị trừng phạt trước chúng. Chúng đã nếm hậu quả tai hại về lề lối xử sự của chúng; và chúng sẽ chịu sự trừng phạt đau đớn (ở đời sau).');
INSERT INTO "hasan_abdulkarim" VALUES(59,16,'59:16','(Đồng minh của chúng đã lừa gạt chúng) giống trường hợp của Shaytan đã lừa gạt con người khi nó bảo y: “Anh chớ tin (Allah)!” Bởi thế, khi y (con người) hết tin (Allah), nó lại bảo: “Tôi không dính dấp gì đến anh cả. Tôi sợ Allah, Thượng Đế của vũ trụ và muôn loài.”');
INSERT INTO "hasan_abdulkarim" VALUES(59,17,'59:17','Bởi thế, kết cuộc của hai đứa chúng là cả hai đều sa vào hỏa ngục và ở trong đó đời đời. Và đó là phần thưởng dành cho những kẻ làm điều sai quấy.');
INSERT INTO "hasan_abdulkarim" VALUES(59,18,'59:18','Hỡi những ai có đức tin! Hãy sợ Allah và mỗi người (linh hồn) nên xét lại những điều mà y đã gởi đi trước cho y vào ngày mai. Và hãy sự Allah! Quả thật, Allah Rất Mực Am Tường về những điều các ngươi làm.');
INSERT INTO "hasan_abdulkarim" VALUES(59,19,'59:19','Và chớ đối xử như những ai đã quên lãng Allah cho nên Ngài đã làm cho chúng lãng quên bản thân của chúng trở lại. Chúng là những kẻ dấy loạn, bất tuân Allah.');
INSERT INTO "hasan_abdulkarim" VALUES(59,20,'59:20','Những người Bạn của hỏa ngục và những người Bạn của thiên đàng không ngang bằng nhau. Những người Bạn của thiên đàng mới là những người thắng lợi.');
INSERT INTO "hasan_abdulkarim" VALUES(59,21,'59:21','Nếu TA (Allah) truyền giáng Qur''an này lên một quả núi, Ngươi (Muhammad) sẽ thấy Nó khiêm tốn và chẻ làm đôi vì khiếp sợ Allah. Đó là hình ảnh thí dụ mà TA trình bày cho nhân loại để may ra họ biết ngẫm nghĩ.');
INSERT INTO "hasan_abdulkarim" VALUES(59,22,'59:22','Ngài là Allah, Đấng mà ngoài Ngài không có một Thượng Đế nào khác (đáng được tôn thờ); Đấng biết hết điều vô hình và điều hữu hình. Ngài là Đấng Rất Mực Độ Lượng, Đấng Rất Mực Khoan Dung.');
INSERT INTO "hasan_abdulkarim" VALUES(59,23,'59:23','Ngài là Allah, Đấng mà ngoài Ngài không có một Đấng Thượng Đế nào khác (xứng đáng được tôn thờ), Đức Vua, Đấng Linh Thiêng, Đấng Ban sự Bằng An, Đấng Ban Đức Tin, Đấng Bảo Vệ An Ninh, Đấng Toàn Năng, Đấng Không Cưỡng Lại được, Đấng Tự Hào. Quang vinh và trong sạch thay Allah! Ngài vượt hẳn những điều hay những đối tác ngang vai mà chúng (những kẻ thờ đa thần) đã qui cho Ngài.');
INSERT INTO "hasan_abdulkarim" VALUES(59,24,'59:24','Ngài là Allah, Đấng Tạo Hóa, Đấng Khởi Sự, Đấng Ban hình thể. Ngài mang những Tên gọi tốt đẹp nhất. Mọi vật trong các tầng trời và trái đất đều tán dương Ngài bởi vì Ngài là Đấng Toàn Năng, Đấng Chí Minh.');
INSERT INTO "hasan_abdulkarim" VALUES(60,1,'60:1','Hỡi những ai có đức tin! Chớ kết bạn với kẻ thù của TA và kẻ thù của các ngươi bằng cách bày tỏ thiện cảm của các ngươi với chúng trong lúc (các ngươi biết chắc) chúng phủ nhận sự Thật đã đến với các ngươi: chúng đã trục xuất Sứ Giả (Muhammad) và kể cả các ngươi (đi chỗ khác) bởi vì các ngươi tin tưởng nơi Allah, Thượng Đế của các ngươi; nếu các ngươi ra đi chiến đấu cho Chính Đạo của TA và tìm sự hài lòng của TA (thì chớ kết bạn với chúng). Các ngươi đã kín dáo bày tỏ thiện cảm đối với chúng và TA biết rõ điều các ngươi giấu giếm và điều các ngươi bộc lộ. Và ai trong các ngươi làm điều đó thì chắc chắn đã đi lạc rất xa khỏi Chính Đạo.');
INSERT INTO "hasan_abdulkarim" VALUES(60,2,'60:2','Nếu thắng được các ngươi thì chúng sẽ là kẻ thù của các ngươi và sẽ giăng cả bàn tay lẫn chiếc lưỡi của chúng ra hãm hại các ngươi và muốn cho các ngươi phủ nhận (sự Thật như chúng).');
INSERT INTO "hasan_abdulkarim" VALUES(60,3,'60:3','Anh em ruột thịt cũng như con cái của các ngươi chẳng giúp ích gì được cho các ngươi vào Ngày phục sinh. Ngài (Allah) sẽ quyết định giữa các ngươi bởi vì Allah thấy rõ những điều các ngươi làm.');
INSERT INTO "hasan_abdulkarim" VALUES(60,4,'60:4','Chắc chắn có một gương tốt nơi Ibrahim và những ai theo Y để cho các ngươi (bắt chước) khi họ bảo người dân của họ: “Chúng tôi vô can đối với quí vị và những tượng vật mà quí vị tôn thờ ngoài Allah và chúng tôi phủ nhận quí vị; giữa chúng tôi và quí vị có một mối hiềm thù kéo dài cho đến lúc quí vị tin tưởng nơi Đấng Allah Duy Nhất,” ngoại trừ lời nói mà Ibrahim đã thưa với phụ thân của Người: “Chắc chắn con sẽ xin (Allah) tha thứ cho cha nhưng con không có quyền đòi Allah ban cho cha bất cứ điều gì (cha muốn). (Rồi họ cầu nguyện, thưa:) “Lạy Thượng Đế của bầy tôi! Bầy tôi phó thác cho Ngài và quay về sám hối với Ngài và sẽ trở về gặp Ngài.”');
INSERT INTO "hasan_abdulkarim" VALUES(60,5,'60:5','“Lạy Thượng Đế của bầy tôi! Xin Ngài đừng dùng chúng tôi làm một mục tiêu thử thách cho những kẻ không có đức tin, Lạy Thượng Đế của bầy tôi, xin Ngài tha thứ cho bầy tôi. Quả thật, Ngài là Đấng Toàn Năng, Đấng Rất Mực Cao Minh.');
INSERT INTO "hasan_abdulkarim" VALUES(60,6,'60:6','Chắc chắn họ là một gương tốt cho các ngươi noi theo, cho ai là người đặt hy vọng nơi Allah và Ngày (Phán Xử) Cuối Cùng. Và ai quay làm ngơ thì quả thật Allah Rất Mực Giàu Có (không thiếu thốn chi) và Rất Đáng Ca Tụng.');
INSERT INTO "hasan_abdulkarim" VALUES(60,7,'60:7','Biết đâu (trong tương lai) Allah sẽ đặt tình thương giữa các ngươi với những kẻ mà các ngươi hiện đang xem như kẻ thù bởi vì Allah có quyền (trên tất cả). Và Allah Hằng Tha Thứ, Rất Mực Khoan Dung.');
INSERT INTO "hasan_abdulkarim" VALUES(60,8,'60:8','Allah không cấm các ngươi đối xử tử tế và công bằng với những ai không giao chiến với các ngươi và trục xuất các ngươi ra khỏi nhà cửa của các ngươi vì vấn đễ tôn giáo bởi vì quả thật Allah yêu thương những người công bằng.');
INSERT INTO "hasan_abdulkarim" VALUES(60,9,'60:9','Allah chỉ cấm các ngươi kết bạn và giao hảo với những ai đã chiến đấu chống các ngươi vì vấn đề tôn giáo và trục xuất các ngươi ra khỏi nhà cửa của các ngươi và tiếp tay trong việc trục xuất các ngươi, sợ rằng các ngươi có thể quay lại (kết thân) với chúng. Và ai quay về kết bạn với chúng thì đó là những người làm điều sai quấy.');
INSERT INTO "hasan_abdulkarim" VALUES(60,10,'60:10','Hỡi những ai có đức tin! Khi những người phụ nữ tin tưởng chạy đến tị nạn với các ngươi (tại Madinah) hãy kiểm tra (sát hạch) họ; Allah biết đức tin của họ. Và khi các ngươi biết chắc họ là những người (nữ) tin tưởng thật sự, chớ giao trả họ về với những kẻ không tin (tại Makkah). Họ không là những (người vợ) hợp pháp cho chúng và chúng cũng không là những (người chồng) hợp pháp cho họ. Nhưng hãy trả lại cho những người chồng ngoại đạo (tiền cưới Mahr) mà chúng đã chi ra (cho người vợ của chúng). Các ngươi không có tội nếu cưới họ làm vợ với điều kiện các ngươi chi cho họ tiền cưới bắt buộc (Mahr) của họ. Và chớ ràng buộc những người (vợ) không có đức tin (tại Makkah) thủ tiết với các ngươi: và đòi họ (vợ ngoại đạo) trả tiền cưới mà các ngươi đã chi ra cho họ và hãy để cho chúng (những người chồng ngoại đạo) đòi lại tiền cưới mà chúng đã chi ra (cho những người vợ Muslim đã chạy đến tị nạn với các ngươi). Đó là Chỉ Dụ của Allah. Ngài xét xử (công bằng) giữa các ngươi bởi vì Allah Toàn Tri, Rất Mực Cao Minh.');
INSERT INTO "hasan_abdulkarim" VALUES(60,11,'60:11','Nếu người vợ nào của các người bỏ các ngươi để đến với những kẻ không tin và các ngươi có được một cơ hội tương xứng (qua việc những người đàn bà tin tưởng bỏ chúng chạy về phía của các ngươi), thì hãy trả lại cho những kẻ (không tin) mà vợ đã bỏ đi một số tiền tương đương với tiền cưới mà chúng đã chi ra (cho vợ). Và hãy sợ Allah, Đấng mà các người tin tưởng.');
INSERT INTO "hasan_abdulkarim" VALUES(60,12,'60:12','Hỡi Nabi (Muhammad!) Khi những người phụ nữ tin tưởng đến gặp Ngươi để xin tuyên thệ với Ngươi, gồm việc họ sẽ không tổ hợp với Allah bất cứ cái gì (trong việc thờ phụng Ngài), và sẽ không ăn cắp, sẽ không ngoại tình (hay thông gian), sẽ không giết con cái của họ, sẽ không nói xấu kẻ khác, không cố tình bịa đặt điều gian dối giữa tay và chần của họ (cho rằng con ngoại tình là đứa con của chồng), và sẽ không bất tuân Ngươi (Muhammad) về bất cứ điều tốt nào thì hãy chấp nhận lời tuyên thệ của họ và hãy xin Allah tha thứ cho họ. Quả thật, Allah Hằng Tha Thứ, Rất Mực Khoan Dung.');
INSERT INTO "hasan_abdulkarim" VALUES(60,13,'60:13','Hỡi những ai có đức tin! Chớ quay lại kết bạn với những kẻ tự chuốc lấy sự giận dữ của Allah (người Do thái). Chắc chắn chúng đã tuyệt vọng về đời sau giống như những kẻ không có đức tin đang tuyệt vọng về những bầu bạn (của chúng) đang nằm dưới mộ.');
INSERT INTO "hasan_abdulkarim" VALUES(61,1,'61:1','Mọi vật trong các tầng trời và mọi vật dưới đất đều tán dương Allah bởi vì Ngài là Đấng Toàn Năng, Đấng Chí Minh.');
INSERT INTO "hasan_abdulkarim" VALUES(61,2,'61:2','Hỡi những ai có đức tin! Tại sao các ngươi nói ra những điều mà các ngươi không làm?');
INSERT INTO "hasan_abdulkarim" VALUES(61,3,'61:3','Điều đáng ghét nhất đối với Allah là các ngươi nói ra những điều mà các ngươi không làm.');
INSERT INTO "hasan_abdulkarim" VALUES(61,4,'61:4','Quả thật, Allah yêu thương những ai chiến đấu cho con đường (Chính Nghĩa) của Ngài theo đội ngũ, làm như họ là (bộ phận) của một kiến trúc rắn chắc.');
INSERT INTO "hasan_abdulkarim" VALUES(61,5,'61:5','Và (hãy nhớ) khi Musa bảo người dân của Y: “Này hỡi dân ta! Tại sao các ngươi làm tổn thương Ta trong lúc các ngươi biết chắc Ta là Sứ Giả của Allah được phái đến với các ngươi? Bởi thế, khi chúng rẽ lối, Allah làm cho tấm lòng của chúng lệch lạc; và Allah không hướng dẫn đám người dấy loạn, bất tuân.');
INSERT INTO "hasan_abdulkarim" VALUES(61,6,'61:6','Và (hãy nhớ) khi Ysa, con trai của Maryam, bảo: “Hỡi con cháu của Israel! Ta là Sứ Giả của Allah được phái đến với các người, xác nhận lại những điều trong Kinh Tawrah (đã được mặc khải) trước Ta và báo tin mừng về một Sứ Giả (của Allah), tên Ahmad (tức Muhammad) sẽ đến sau Ta.” Nhưng khi Y (Muhammad) đến gặp chúng với những Bằng Chứng rõ rệt thì chúng bảo: “Đây là một trò phù thủy hiển hiện.”');
INSERT INTO "hasan_abdulkarim" VALUES(61,7,'61:7','Và còn sai quấy hơn kẻ đặt điều nói dối rồi đổ thừa cho Allah trong lúc y được mời gia nhập Islam Và Allah không hướng dẫn đám người làm điều sai quấy.');
INSERT INTO "hasan_abdulkarim" VALUES(61,8,'61:8','Chúng muốn dập tất Ánh Sáng của Allah bằng miệng lưỡi của chúng, nhưng Allah sẽ hoàn thiện Ánh Sáng của Ngài dẫu rằng những kẻ không tin ghét điều đó.');
INSERT INTO "hasan_abdulkarim" VALUES(61,9,'61:9','Và Ngài là Đấng đã phái Sứ Giả của Ngài mang Chỉ Đạo và tôn giáo của Sự Thật đến để làm cho Nó (Islam) thắng tất cả mọi tôn giáo mặc dầu những người tôn thờ đa thần ghét điều đó.');
INSERT INTO "hasan_abdulkarim" VALUES(61,10,'61:10','Hỡi những ai có đức tin! Há TA hướng dẫn các ngươi đến với một cuộc đổi chác sẽ giải cứu các ngươi khỏi sự trừng phạt đau đớn?');
INSERT INTO "hasan_abdulkarim" VALUES(61,11,'61:11','(Đó là việc) các ngươi tin tưởng nơi Allah và Sứ Giả của Ngài; và chiến đấu cho Chính Nghĩa của Allah bằng cả tài sản lẫn sinh mạng của các ngươi. Điều đó tốt nhất cho các ngươi nếu các ngươi biết.');
INSERT INTO "hasan_abdulkarim" VALUES(61,12,'61:12','Ngài sẽ tha thứ tội lỗi của các ngươi cho các ngươi và thu nhận các ngươi vào những ngôi Vườn có các dòng sông chảy bên dưới, và những chỗ ở tốt đẹp nơi những Ngôi Vườn Vĩnh Cửu (thiên đàng). Đó là một cuộc thành tựu vĩ đại.');
INSERT INTO "hasan_abdulkarim" VALUES(61,13,'61:13','Và (ban cấp) một (ân sủng) khác mà các ngươi yêu thích: Sự giúp đỡ của Allah và một cuộc thắng lợi gần kề. Và hãy báo tin mừng này cho những người tin tưởng.');
INSERT INTO "hasan_abdulkarim" VALUES(61,14,'61:14','Hỡi những ai có đức tin! Hãy là những người giúp đỡ (Chính Nghĩa) của Allah, giống như điều mà Ysa, con trai của Maryam đã bảo các Tông đồ (của Y): “Ai là người ủng hộ Ta về (Chính Nghĩa của) Allah?” Các Tông đồ thưa: “Chúng tôi là những người trợ giúp (Chính Nghĩa) của Allah.” Nhưng một thành phần của con cháu của Israel đã tin tưởng và một thành phần (khác) không có đức tin. Nhưng TA đã tăng cường sức mạnh cho những ai có đức tin chống lại kẻ thù của họ cho nên họ đã chiến thắng.');
INSERT INTO "hasan_abdulkarim" VALUES(62,1,'62:1','Mọi vật trong các tầng trời và mọi vật dưới đất đều tán dương Allah, Đấng Chúa Tể, Đấng Hiển Linh, Đấng Toàn Năng, Đấng Chí Minh.');
INSERT INTO "hasan_abdulkarim" VALUES(62,2,'62:2','Ngài là Đấng đã dựng trong đám người dân (Ả Rập) thất học (tại Makkah) một Sứ Giả xuất thân từ họ để đọc các Lời Mặc Khải của Ngài cho họ và tẩy sạch họ (khỏi sự ô uế của việc tôn thờ đa thần) và dạy họ Kinh Sách và đường lối đúng đắn khôn ngoan. Và quả thật, trước đó họ đã lầm lạc rõ rệt.');
INSERT INTO "hasan_abdulkarim" VALUES(62,3,'62:3','Và (phái Y đến) với những người khác thuộc thành phần của họ nhưng chưa nhập (đạo) với họ. Và Ngài là Đấng Toàn Năng, Đấng Rất Mực Sáng Suốt.');
INSERT INTO "hasan_abdulkarim" VALUES(62,4,'62:4','Đó là Thiên Ân mà Allah ban cho người nào Ngài muốn. Và Allah là Chủ Nhân của Thiên Ân Vĩ Đại.');
INSERT INTO "hasan_abdulkarim" VALUES(62,5,'62:5','Hình ảnh của những ai gánh (trách nhiệm thi hành) Kinh Tawrah rồi không gánh nó (trọn nhiệm vụ) sẽ giống như hình ảnh của một con lừa chở trên lưng hằng đống sách (nhưng không hiểu sách đó nói gì). Thật xấu xa thay hình ảnh của những ai phủ nhận các Lời Mặc Khải) của Allah (giống như hình ảnh của con lừa chở sách). Và Allah không hướng dẫn đám người làm điều sai quấy.');
INSERT INTO "hasan_abdulkarim" VALUES(62,6,'62:6','Hãy bảo (chúng): “Hỡi những ai là người Do thái! Nếu các ngươi tự cho mình là những vị Wali của Allah, khác với người ta thì hãy cầu mong cho được chết đi nếu các người nói thật.”');
INSERT INTO "hasan_abdulkarim" VALUES(62,7,'62:7','Nhưng chúng sẽ không bao giờ muốn nó (cái chết) bởi vì (chúng sợ hậu quả của) những điều (tội lỗi) mà bàn tay của chúng đã gởi đi trước. Và Allah biết rõ những kẻ làm điều sai quấy.');
INSERT INTO "hasan_abdulkarim" VALUES(62,8,'62:8','Hãy bảo (chúng): “Quả thật cái chết mà các người muốn chạy trốn sẽ giáp mặt các người. Rồi các người sẽ được đưa trở về (trình diện) Đấng biết điều vô hình và điều hữu hình. Rồi Ngài sẽ cho các ngươi biết những điều các người đã làm.”');
INSERT INTO "hasan_abdulkarim" VALUES(62,9,'62:9','Hỡi những ai có đức tin! Khi tiếng Azdan dõng dạc gọi các ngươi đến dâng lễ nguyện Salah vào Ngày Thứ Sáu( (Jumu-''ah), hãy vội vã đến (Thánh đường) để tưởng nhớ Allah và tạm gác việc mua bán sang một bên. Điều đó tốt cho các ngươi nhất nếu các ngươi biết.');
INSERT INTO "hasan_abdulkarim" VALUES(62,10,'62:10','Rồi khi cuộc lễ nguyện Salah chấm dứt, hãy tản mác (khắp nơi) trên mặt đất đi tìm thiên lộc của Allah và hãy nhớ Allah cho thật nhiều để may ra các ngươi được thành công.');
INSERT INTO "hasan_abdulkarim" VALUES(62,11,'62:11','Và khi thấy việc mua bán đổi chác hoặc trò chơi giải trí (như đánh trống, hát hò...), họ tản mác đi về phía đó bỏ Ngươi (Muhammad) đứng (thuyết giảng - Khutbah). Hãy bảo họ: “Điều gì liên hệ đến Allah tốt hơn trò giải trí và việc mua bán”. Và Allah là Đấng Cung dưỡng ưu việt.');
INSERT INTO "hasan_abdulkarim" VALUES(63,1,'63:1','Khi đến gặp Ngươi (Muhammad) những người giả tạo đức tin bảo: “Chúng tôi xác nhận người là Sứ Giả của Allah” trong lúc Allah đã biết Ngươi thực sự là Sứ Giả của Ngài. Và Allah cũng xác nhận những người giả tạo đức tin chắc chắn là những tên nói dối.');
INSERT INTO "hasan_abdulkarim" VALUES(63,2,'63:2','Chúng dùng lời thề của chúng làm bình phong (che đậy việc làm tội lỗi của chúng). Bởi thế, chúng cản trở thiên hạ theo con đường của Allah. Những điều chúng từng làm quả thật là xấu xa, tội lỗi.');
INSERT INTO "hasan_abdulkarim" VALUES(63,3,'63:3','Sở dĩ như thế là vì chúng có niềm tin rồi chúng lại phủ nhận đức tin; bởi thế, quả tim của chúng bị niêm kín lại cho nên chúng không hiểu gì.');
INSERT INTO "hasan_abdulkarim" VALUES(63,4,'63:4','Và khi nhìn chúng, Ngươi (Muhammad) trâm trồ dáng dấp bề ngoài của chúng; và khi chúng nói, Ngươi lắng nghe lời lẽ của chúng. Chúng giống như những khúc gỗ (bọng) dùng để chống đỡ. Chúng nghĩ rằng mỗii tiếng thét đều nhằm chỉ trích chúng. Chúng là kẻ thù. Bởi the, hãy cảnh giác chúng. Allah nguyền rủa chúng. Chúng quay hướng về đâu?');
INSERT INTO "hasan_abdulkarim" VALUES(63,5,'63:5','Khi có lời bảo chúng: “Hãy đến đây. Sứ Giả của Allah sẽ xin (Allah) tha thứ cho các anh.” Nhưng chúng quay đầu sang phía khác; và Ngươi thấy chúng bỏ đi dáng điệu ngạo mạn.');
INSERT INTO "hasan_abdulkarim" VALUES(63,6,'63:6','Dẫu Ngươi có cầu xin (Allah) tha thứ hay không cầu xin sự tha thứ cho chúng, điều đó bằng thừa đối với chúng. Allah sẽ không bao giờ tha thứ cho chúng. Quả thật, Allah không hướng dẫn đám người dấy loạn, bất tuân.');
INSERT INTO "hasan_abdulkarim" VALUES(63,7,'63:7','Chúng là những kẻ nói: “Chớ chi tiêu (bố thí) cho những người theo Sứ Giả của Allah cho đến lúc họ bỏ đi (khỏi Madĩnah).” Nhưng những người giả tạo đức tin không hiểu thấu được kho (lương thực) của các tầng trời và trái đất đều là của Allah cả.');
INSERT INTO "hasan_abdulkarim" VALUES(63,8,'63:8','Và chúng tuyên bố: “Nếu chúng tôi trở về Madinah, người quyền thế nhất (ám chỉ Abdullah bin Ubai) sẽ đuổi thằng tồi nhất (ám chỉ Sứ Giả của Allah) ra khỏi đó.” Nhưng những người giả tạo đức tin không biết rằng danh dự và quyền thế đều thuộc về Allah và Sứ Giả của Ngài và những người tin tưởng.');
INSERT INTO "hasan_abdulkarim" VALUES(63,9,'63:9','Hỡi những ai có đức tin! Chớ để cho tài sản và con cái của các ngươi làm cho các ngươi xao lãng việc tưởng nhớ Allah. Và ai làm thế thì sẽ là những người thua thiệt.');
INSERT INTO "hasan_abdulkarim" VALUES(63,10,'63:10','Và hãy chi dùng (vào việc thiện) phần tài sản mà TA đã cung cấp cho các ngươi trước khi (cái) chết đến cho mỗi một người trong các ngươi. Bởi vì lúc đó y sẽ (hối tiếc) thưa: “Lạy Thượng Đế của bề tôi! Nếu Ngài gia hạn cho bề tôi sống thêm một thời gian ngắn nữa thì bề tôi sẽ bố thí (rộng rãi) và trở thành một người đức hạnh.”');
INSERT INTO "hasan_abdulkarim" VALUES(63,11,'63:11','Và Allah sẽ không bao giờ gia hạn cho một người (linh hồn) nào cả khi đã mãn hạn. Và Allah rất am tường về những điều các ngươi làm.');
INSERT INTO "hasan_abdulkarim" VALUES(64,1,'64:1','Mọi vật trong các tầng trời và mọi vật dưới đất đều tán dương Allah. Quyền hành thống trị là của Ngài; và Lời ca tụng cũng thuộc về Ngài. Và Ngài là Đấng Toàn Năng trên tất cả mọi thứ.');
INSERT INTO "hasan_abdulkarim" VALUES(64,2,'64:2','Ngài là Đấng đã tạo hóa các ngươi, nhưng trong các ngươi có kẻ không tin tưởng và trong các ngươi có người tin tưởng. Và Allah Hằng Thấy điều các ngươi làm.');
INSERT INTO "hasan_abdulkarim" VALUES(64,3,'64:3','Ngài vì Chân Lý đã tạo hóa các tầng trời và trái đất; và Ngài đã tạo hình thể các ngươi và đã hoàn thiện hình thể của các ngươi. Và các ngươi sẽ trở về gặp Ngài.');
INSERT INTO "hasan_abdulkarim" VALUES(64,4,'64:4','Ngài biết mọi vật trong các tầng trời và dưới đất; và biết điều các ngươi giấu giếm và điều các ngươi bộc lộ, bởi vì Allah biết rõ điều nằm trong lòng (của mọi người).');
INSERT INTO "hasan_abdulkarim" VALUES(64,5,'64:5','Há các ngươi chưa biết tin tức về những kẻ đã không có đức tin trước đây ư? Bởi thế, chúng đã nếm hậu quả tai hại của những công việc của chúng; và chúng đã chịu một sự trừng phạt đau đớn.');
INSERT INTO "hasan_abdulkarim" VALUES(64,6,'64:6','Sở dĩ như thế là vì những Sứ Giả của chúng đã đến gặp chúng với những bằng chứng rõ rệt nhưng chúng bảo: “Há người phàm lại hướng dẫn chúng tôi ư?” Bởi thế, chúng không tin tưởng và quay bỏ đi. Và Allah không cần đến chúng bởi vì Allah Rất Mực Giàu Có, Rất Đáng Ca Tụng.');
INSERT INTO "hasan_abdulkarim" VALUES(64,7,'64:7','Những kẻ không có niềm tin nghĩ rằng chúng sẽ không được phục sinh trở lại (để chịu sự Phán xử). Hãy bảo chúng (hỡi Muhammad!): “Vâng, thề bởi Thượng Đế của Ta, chắc chắn các người sẽ được dựng sông lại rồi sẽ được báo cho biết về những điều mà các ngtrời đã lam. Và điều đó rất dễ đối với Allah.');
INSERT INTO "hasan_abdulkarim" VALUES(64,8,'64:8','Bởi thế, hãy tin tưởng Allah và Sứ Giả của Ngài và Ánh Sáng (Qur''an) mà TA (Allah) đã ban xuống. Và Allah Rất Am Tường về những điều các người làm.');
INSERT INTO "hasan_abdulkarim" VALUES(64,9,'64:9','(Hãy nhớ) Ngày mà Ngài (Allah) sẽ tập trung tất cả các ngươi vào Ngày Đại hội (Phán Xử). Đó là Ngày của (người) thắng và (kẻ) bại. Và ai tin tưởng nơi Allah và làm việc thiện thì Ngài (Allah) sẽ xóa bỏ mọi điều xấu xa (tội lỗi) khỏi y và sẽ thu nhận y vào những Ngôi Vườn bên dưới có các dòng sông chảy (thiên đàng) để vào ở trong đó đời đời. Đó là một thắng lợi Vĩ đại.');
INSERT INTO "hasan_abdulkarim" VALUES(64,10,'64:10','Ngược lại, những ai không có đức tin và phủ nhận các Lời Mặc Khải của TA thì sẽ là những người bạn của hỏa ngục, nơi mà chúng sẽ vào ở trong đó đời đời. Và (là) một trạm đến cuối cùng rất xấu.');
INSERT INTO "hasan_abdulkarim" VALUES(64,11,'64:11','Không một tai họa nào xảy ra mà không do phép của Allah cả. Và ai tin tưởng nơi Allah thì Ngài sẽ hướng dẫn tấm lòng của y (theo Chính Đạo) bởi vì Allah biết rõ hết mọi việc.');
INSERT INTO "hasan_abdulkarim" VALUES(64,12,'64:12','Và hãy tuân lệnh Allah và tuân lệnh Sứ Giả (Muhammad). Nhưng nếu các ngươi quay bỏ đi thì nhiệm vụ của Sứ Giả của TA chỉ là truyền đạt (Thông Điệp) một cách công khai.');
INSERT INTO "hasan_abdulkarim" VALUES(64,13,'64:13','Allah (Duy Nhất)! Không có Thượng Đế nào khác duy chỉ Ngài (là Thượng Đế). Bởi thế, những người có đức tin nên tin cậy mà phó thác cho Allah.');
INSERT INTO "hasan_abdulkarim" VALUES(64,14,'64:14','Hỡi những ai có đức tin! Quả thật, trong số người vợ và con cái của các ngươi, có người thù nghịch (cản trở) các ngươi (trong việc tuân lệnh Allah và Sứ Giả của Ngài). Bởi thế, hãy cảnh giác họ. Nhưng nếu các ngươi lượng thứ bỏ qua và tha thứ cho họ thì quả thật Allah Hằng Tha Thứ, Rất Mực Khoan Dung.');
INSERT INTO "hasan_abdulkarim" VALUES(64,15,'64:15','Tài sản và con cái của các ngươi chỉ là một sự thử thách (đối với các ngươi). Ngược lại, Allah có nơi Ngài một phần thưởng rất lớn (thiên đàng để dành cho các ngươi).');
INSERT INTO "hasan_abdulkarim" VALUES(64,16,'64:16','Do đó, hãy sợ Allah (và làm tròn bổn phận đối với Ngài) theo khả năng của các ngươi. Và hãy nghe và vâng lệnh. Và hãy chi tiêu (bố thí) có lợi cho bản thân (linh hồn) của các ngươi. Và ai giữ bản thân của mình khỏi tham lam (ích kỷ) thì là những người sẽ thành đạt.');
INSERT INTO "hasan_abdulkarim" VALUES(64,17,'64:17','Nếu các ngươi cho Allah mượn một phần mượn tốt (để đóng góp vào con đường Chính Nghĩa của Allah) thì Ngài sẽ gia tăng nó gấp đôi (vào trương mục tín dụng) cho các ngươi; và sẽ tha thứ (tội lỗi) cho các ngươi bởi vì Allah Hằng Ghi Công và Hằng Chịu Đựng.');
INSERT INTO "hasan_abdulkarim" VALUES(64,18,'64:18','Đấng Biết hết điều ẩn tàng không thấy và điều hiển hiện, Đấng Toàn Năng, Đấng Chí Minh.');
INSERT INTO "hasan_abdulkarim" VALUES(65,1,'65:1','Hỡi Nabi (Muhammad!) Khi các ngươi ly dị các bà (vợ), hãy ly dị họ vào ''Iddah( (thời hạn ấn định) của họ và hãy đếm cho đúng ''Iddah. Và hãy sợ Allah, Thượng Đế của các ngươi. Chớ đuổi họ ra khỏi nhà cũng chớ để cho họ bỏ đi, ngoại trừ trường hợp họ công khai phạm điều (gian dâm) thô bỉ. Và đó là những giới hạn (qui định) bởi Allah. Và ai vi phạm giới hạn của Allah thì chắc chắn tự làm hại bản thân mình. Ngươi đâu biết sau này Allah có thể tạo một điều kiện mới nào đó (để hòa giải đôi bên).');
INSERT INTO "hasan_abdulkarim" VALUES(65,2,'65:2','Bởi thế, khi chấm dứt thời hạn ấn định (''Iddah) của họ, hoặc giữ họ (vợ) lại một cách tử tế hoặc ly hôn họ một cách tử tế; và hãy mời hai người công bằng trong các ngươi đến làm chứng và hãy vì Allah mà thiết lập chứng cớ rõ ràng. Đó là điều khuyến cáo cho người nào tin nơi Allah và Ngày (Phán Xử) Cuối Cùng. Và ai sợ Allah thì sẽ được Ngài mở cho một lối thoát.');
INSERT INTO "hasan_abdulkarim" VALUES(65,3,'65:3','Và Ngài sẽ cung dưỡng y từ nơi mà y không thể ngờ tới. Và ai phó thác cho Allah thì Ngài sẽ làm cho y đầy đủ. Bởi vì, chắc chắn Allah sẽ thực hiện mục đích của Ngài. Chắc chắn, Allah qui định mức lượng cho tất cả mọi vật.');
INSERT INTO "hasan_abdulkarim" VALUES(65,4,'65:4','Và ai trong số họ (những người vợ) của các ngươi đã quá tuổi có kinh, nếu các ngươi còn nghi ngờ (kinh kỳ của họ) thì thời hạn ấn định ''Iddah (chờ ly hôn) là ba tháng và ai trong số họ không có kinh (thì thời hạn cũng thế). Và những người vợ nào mang thai thì thời hạn ấn định (chờ ly hôn) của họ kéo dài cho đến khi hạ sanh. Và ai sợ Allah thì Ngài sẽ làm mọi việc được dễ dàng cho y.');
INSERT INTO "hasan_abdulkarim" VALUES(65,5,'65:5','Đó là Mệnh Lệnh của Allah mà Ngài đã ban xuống cho các ngươi. Và ai sợ Allah thì Ngài sẽ xóa bỏ mọi điều xấu xa (tội lỗi) cho y và sẽ nới rộng phần thưởng cho y.');
INSERT INTO "hasan_abdulkarim" VALUES(65,6,'65:6','Hãy để cho những người vợ ly dị trú ngụ nơi nào mà các ngươi đang ở tùy theo phương tiện của các ngươi nhưng chớ đày đọa họ để áp bức họ bỏ đi. Và nếu họ mang thai thì hãy cấp dưỡng cho họ đến khi hạ sanh. Và nếu họ cho con bú thì hãy trang trải cho họ; và hãy tham khảo ý kiến lẫn nhau một cách biết điều. Nếu các ngươi gặp khó khăn (trong việc cho con bú) thì người đàn bà khác sẽ cho đứa con bú giùm cho y (với sở phí của y).');
INSERT INTO "hasan_abdulkarim" VALUES(65,7,'65:7','Người giàu phương tiện sẽ cấp dưỡng theo phương tiện của mình. Và người eo hẹp phương tiện sẽ cấp dưỡng theo phương tiện mà Allah đã ban cấp. Allah chỉ bắt mỗi người chịu gánh nặng (phí tổn) tùy theo phương tiện mà Ngài đã ban cho. Allah sẽ làm nhẹ gánh lo sau nỗi khó khăn (túng quẫn).');
INSERT INTO "hasan_abdulkarim" VALUES(65,8,'65:8','Và có bao nhiêu thị trấn đã chống đối Mệnh Lệnh của Thượng Đế của chúng và (chống đối) các Sứ Giả của Ngài? Bởi thế, TA đã thanh toán chúng bằng một cuộc thanh toán dữ dội. Và TA đã trừng phạt chúng bằng một cuộc trừng phạt ghê rợn.');
INSERT INTO "hasan_abdulkarim" VALUES(65,9,'65:9','Bởi thế, chúng đã nếm hậu quả tai hại về công việc của chúng và kết cuộc của công việc của chúng là mất mát thua thiệt.');
INSERT INTO "hasan_abdulkarim" VALUES(65,10,'65:10','Allah đã chuẩn bị cho chúng một sự trừng phạt nghiêm khắc (ở đời sau). Bởi thế, hãy sợ Allah hỡi những người hiểu biết, những ai có đức tin. Quả thật, Allah đã ban xuống cho các ngươi Lời Nhắc Nhở (Qur''an).');
INSERT INTO "hasan_abdulkarim" VALUES(65,11,'65:11','Một Sứ Giả (của Allah) đọc cho các ngươi các Lời Mặc Khải của Allah, giải thích rõ sự việc, để Ngài có thể đưa những ai có đức tin và làm việc thiện bước từ tăm tối ra ánh sáng; và ai tin tưởng nơi Allah và làm việc thiện thì sẽ được Ngài đưa vào những Ngôi Vườn bên dưới có các dòng sông chảy để ở trong đó đời đời. Chắc chắn, Allah sẽ cung cấp cho y lương thực tốt nhất.');
INSERT INTO "hasan_abdulkarim" VALUES(65,12,'65:12','Allah là Đấng đã tạo hóa bảy tầng trời và cái giống như chúng của trái đất. Mệnh Lệnh (của Allah) đi xuống giữa chúng (các tầng trời và tầng đất) để cho các ngươi biết Allah là Đấng Toàn Năng trên tất cả mọi thứ và rằng Allah bao trùm (quán xuyến) hết tất cả mọi vật (việc) với sự Hiểu Biết của Ngài.');
INSERT INTO "hasan_abdulkarim" VALUES(66,1,'66:1','Hỡi Nabi (Muhammad!) Tại sao Ngươi tự cấm (dùng) món (thực phẩm) mà Allah cho phép Ngươi (dùng) chỉ vì muốn làm vừa lòng các bà vợ của Ngươi? Và Allah Hằng Tha Thứ, Rất Mực Khoan Dung.');
INSERT INTO "hasan_abdulkarim" VALUES(66,2,'66:2','Quả thật, trước đây, Allah đã qui định cho các ngươi (Muslim) phương cách xóa bỏ lời thề (trong một số trường hợp). Và Allah là Chủ Nhân của các ngươi; và Ngài là Đấng Toàn Tri, Đấng Rất Mực Sáng Suốt.');
INSERT INTO "hasan_abdulkarim" VALUES(66,3,'66:3','Và khi Nabi thổ lộ chuyện tâm tình cho một bà vợ của Y (bà Hafsah) sau đó bà tiết lộ nó (cho một người (vợ) khác, bà ''A-ishah). Và Allah cho Y biết việc đó. Y đã xác nhận một phần (câu chuyện) và bỏ qua một phần (khác). Bởi thế, khi Y cho bà (.Hafsah) biết sự việc thì bà bảo: “Ai đã cho mình biết điều này?" (Nabi) đáp: “(Allah), Đấng Toàn Tri, Đấng Am Tường (mọi việc), đã cho Ta biết (sự Thật).”');
INSERT INTO "hasan_abdulkarim" VALUES(66,4,'66:4','Nếu hai ngươi (hỡi ''A-ishah và Hafsah) quay về sám hối với Allah thì tấm lòng của hai ngươi chịu thuận. Ngược lại, nếu hai ngươi tiếp tay nhau chống lại Y thì Allah, Đấng Bảo Hộ của Y, va (Thiên Thần) Jibril và những tín đồ đức hạnh và các Thiên Thần sẽ ủng hộ Y sau đó.');
INSERT INTO "hasan_abdulkarim" VALUES(66,5,'66:5','Nếu Y (Muhammad) ly dị (tất cả) các ngươi, có lẽ Thượng Đế (Allah) của Y sẽ đổi lại cho Y các bà vợ tốt hơn các ngươi: các nữ Muslim có đức tin, sùng kính, năng sám hối (với Allah), thờ phụng (Allah), nhịn chay, đã có một đời chồng hay còn trinh.');
INSERT INTO "hasan_abdulkarim" VALUES(66,6,'66:6','Hỡi những ai có đức tin! Hãy giữ gìn bản thân và gia đình của các ngươi tránh khỏi hỏa ngục nơi mà chất đốt là con người (không có đức tin) và đá (bục tượng), do những Thiên Thần lạnh lùng và nghiêm nghị quản lý, không bất tuân Allah về điều gì mà Ngài ra lệnh cho họ (Thiên Thần) và thi hành đúng những điều họ được truyền.');
INSERT INTO "hasan_abdulkarim" VALUES(66,7,'66:7','Hỡi những kẻ không có đức tin! Ngày nay các ngươi chớ tìm cách chạy tội. Các ngươi chỉ được quả báo về những điều mà các ngươi đã từng làm.');
INSERT INTO "hasan_abdulkarim" VALUES(66,8,'66:8','Hỡi những ai có đức tin! Hãy chân thành quay về sám hối với Allah, may ra Thượng Đế của các ngươi sẽ xóa bỏ tội lỗi cho các ngươi và thu nhận các ngươi vào những Ngôi Vườn bên dưới có các dòng sông chảy (thiên đàng) vào Ngày mà Allah sẽ không hạ nhục Nabi (Muhammad) và những ai có đức tin cùng theo Y. Ánh sáng của họ sẽ chạy đằng trước và phía bên tay phải của họ; họ (cầu nguyện) thưa: “Lạy Thượng Đế của bầy tôi! Xin Ngài hoàn thiện Anh Sáng của bầy tôi cho bầy tôi và tha thứ cho bầy tôi bởi vì quả thật, Ngài là Đấng Toàn Năng trên tất cả mọi thứ.”');
INSERT INTO "hasan_abdulkarim" VALUES(66,9,'66:9','Hỡi Nabi (Muhammad!) Hãy chiến đấu chống những kẻ không có đức tin và những kẻ giả tạo đức tin và hãy nghiêm khắc với chúng; bởi vì nhà ở của chúng là hỏa ngục và là một trạm đến cuối cùng tồi tệ nhất.');
INSERT INTO "hasan_abdulkarim" VALUES(66,10,'66:10','Allah trình bày cho những kẻ không có đức tin thí dụ điển hình về người vợ của Nuh và người vợ của Lut: Hai ả ở dưới (quyền của) hai người bề tôi đức hạnh của TA (Allah) nhưng hai ả đã phản bội chồng. Nhưng họ (các ông chồng đức hạnh) chẳng giúp gì được cho hai người vợ của mình tránh khỏi (Hình phạt của) Allah; và có lời phán bảo: “Hai ngươi hãy đi vào hỏa ngục cùng với những kẻ đi vào.”');
INSERT INTO "hasan_abdulkarim" VALUES(66,11,'66:11','Và Allah trình bày cho những người có đức tin một thí dụ điển hình về bà (Asiyah) vợ của Fir-''awn khi bà cầu nguyện thưa: “Lạy Thượng Đế của bề tôi! Xin Ngài xây cho bề tôi một mái nhà gần Ngài nơi thiên đàng và cứu bề tôi thoát khỏi (tay của) Fir-''awn cùng việc làm (tội lỗi) của y và cứu bề tôi thoát khỏi đám người làm điều sai quấy.');
INSERT INTO "hasan_abdulkarim" VALUES(66,12,'66:12','Và (thí dụ về) Maryam, đứa con gái của ''Imran: Nữ đã giữ mình trinh tiết. Bởi thế, TA (Allah) đã cho Ruh (Thiên Thần Jibril) của TA hà vào (cơ thể của) Nữ; và Nữ xác nhận sự Thật về Lời phán (Kun - ''Hãy Thành’) của Thượng Đế (Allah) của Nữ và (xác nhận sự thật về) Kinh Sách của Ngài (Allah) và Nữ là một nữ tín đồ sùng tín.');
INSERT INTO "hasan_abdulkarim" VALUES(67,1,'67:1','Phúc thay Đấng đang nắm quyền thống trị trong Tay Ngài và toàn quyền định đoạt trên tất cả mọi việc.');
INSERT INTO "hasan_abdulkarim" VALUES(67,2,'67:2','Đấng đã tạo sự chết và sự sống để thử thách ai trong các ngươi là người tốt nhất trong việc làm của mình; và Ngài là Đấng Toàn Năng, Đấng Hằng Tha Thứ.');
INSERT INTO "hasan_abdulkarim" VALUES(67,3,'67:3','Đấng đã tạo hóa bảy tầng trời, tầng này chồng lên tầng kia; ngươi không thấy có một khuyết điểm nào trong việc tạo hóa của Đấng Đố Lượng. Bởi thế, hãy quay lại nhìn nữa xem, ngươi có thấy một kẽ hở nào không?');
INSERT INTO "hasan_abdulkarim" VALUES(67,4,'67:4','Rồi quay nhìn hai lần nữa xem, cái nhìn của ngươi sẽ trở lại với ngươi mờ nhạt và rũ rượi.');
INSERT INTO "hasan_abdulkarim" VALUES(67,5,'67:5','Và quả thật, TA đã trang hoàng tầng trời hạ giới với những chiếc đèn và làm cho chúng thành những chiếc hỏa tiễn dùng đánh đuổi những Shaytan và TA đã chuẩn bị cho chúng một sự trừng phạt của Lửa ngọn;');
INSERT INTO "hasan_abdulkarim" VALUES(67,6,'67:6','Và (chuẩn bị) cho những ai phủ nhận Thượng Đế của họ một sự trừng phạt của hỏa ngục; và đó là một nơi đến cuối cùng tồi tệ nhất.');
INSERT INTO "hasan_abdulkarim" VALUES(67,7,'67:7','Khi bị quẳng vào đó, chúng sẽ nghe tiếng nó rít (lửa) vào giống như nó hà (lửa) ra.');
INSERT INTO "hasan_abdulkarim" VALUES(67,8,'67:8','Nó gần như muốn phát nổ vì giận dữ: Mỗi lần một nhóm người bị quẳng vào đó, những vị cai ngục lên tiếng hỏi: “Há không có một người báo trước nào đến gặp cac ngươi hay sao?”');
INSERT INTO "hasan_abdulkarim" VALUES(67,9,'67:9','Chúng sẽ đáp: “Vâng, có. Quả thật, đã có một người báo trước đến gặp chúng tôi, nhưng chúng tôi phủ nhận và nói: "Allah đã không ban một điều (mặc khải) nào xuống cả. Quí vị đã lầm lạc quá to''.”');
INSERT INTO "hasan_abdulkarim" VALUES(67,10,'67:10','Và chúng sẽ nói: “Giá chúng tôi biết nghe hoặc chịu suy nghĩ thì đâu đến noi trở thành những người bạn của Lửa ngọn.”');
INSERT INTO "hasan_abdulkarim" VALUES(67,11,'67:11','Bởi thế, chúng sẽ thú tội của mình. Nhưng những người bạn của Lửa ngọn ở quá xa!');
INSERT INTO "hasan_abdulkarim" VALUES(67,12,'67:12','Quả thật, những ai sợ Đấng Thượng Đế (Allah) Vô hình của họ thì sẽ được tha thứ và được một phần thưởng rất lớn.');
INSERT INTO "hasan_abdulkarim" VALUES(67,13,'67:13','Và dẫu các ngươi có giấu giếm hoặc công bố lời nói của các ngươi (cũng không sao) bởi vì quả thật, Ngài biết rõ những điều nằm trong lòng (của các ngươi).');
INSERT INTO "hasan_abdulkarim" VALUES(67,14,'67:14','Há Đấng Tạo Hóa không biết ư? Và Ngài là Đấng Rất Mực Tinh tế, Đấng Rất Mực Am Tường.');
INSERT INTO "hasan_abdulkarim" VALUES(67,15,'67:15','Ngài là Đấng đã làm ra trái đất cho các ngươi sử dụng. Do đó, hãy băng qua các nẻo đường của nó và dùng thực phẩm của Ngài (Allah). Và các ngươi sẽ trở về với Ngài.');
INSERT INTO "hasan_abdulkarim" VALUES(67,16,'67:16','Phải chăng các ngươi cảm thấy an toàn thoát khỏi Đấng ở trên trời (nghĩ) rằng Ngài sẽ không làm cho đất sụp xuống nuốt mất các ngươi khi nó rung động dữ dội hay sao?');
INSERT INTO "hasan_abdulkarim" VALUES(67,17,'67:17','Hoặc phải chăng các ngươi cảm thấy an toàn thoát khỏi Đấng ở trên trời, (nghĩ) rằng Ngài sẽ không gởi một cơn trốt dữ dội nào đến phạt các ngươi hay sao? Rồi các ngươi sẽ biết Lời cảnh báo của TA (khủng khiếp) thế nào?');
INSERT INTO "hasan_abdulkarim" VALUES(67,18,'67:18','Vàquả thật những kẻ trước chúng đã phủ nhận (Lời cảnh báo của TA). Rồi hãy xem sự trừng phạt của TA khủng khiếp thế nào?');
INSERT INTO "hasan_abdulkarim" VALUES(67,19,'67:19','Há chúng không quan sát những con chim xòe cánh và xếp cánh bên trên chúng hay sao? Ngoài Đấng Độ Lượng (Allah), không ai có thể giữ chúng lơ lửng trên không trung được. Quả thật, Ngài nhìn thấy hết mọi vật.');
INSERT INTO "hasan_abdulkarim" VALUES(67,20,'67:20','Đấng nào ngoài Đấng Độ Lượng (Allah), dù đó là một đạo quân (được phái đến) cho các ngươi, có thể giúp các ngươi? Chắc chắn, những kẻ không có đức tin chỉ sống trong ảo vọng.');
INSERT INTO "hasan_abdulkarim" VALUES(67,21,'67:21','Hoặc Đấng nào là Đấng có thể cung dưỡng cho các ngươi nếu Ngài (Allah) muốn giữ nguồn lương thực của Ngài lại? Không, chúng tiếp tục ương ngạnh và chạy tron (sự thật).');
INSERT INTO "hasan_abdulkarim" VALUES(67,22,'67:22','Thế, một người gục mặt bước đi với dáng điệu đáng khinh được hướng dẫn đúng đường hay một người bước đi bình thản trên Chính Đạo?');
INSERT INTO "hasan_abdulkarim" VALUES(67,23,'67:23','Hãy bảo chúng: “Ngài là Đấng đã tạo hóa các ngươi và phú cho các ngươi thính giác, thị giác và trái tim. Các ngươi tạ ơn Ngài ít thay!"');
INSERT INTO "hasan_abdulkarim" VALUES(67,24,'67:24','Hãy bảo chúng: “Ngài là Đấng đã rải rắc các ngươi khắp nơi trên mặt đất và các ngươi sẽ được tập trung đưa về trinh diện Ngài trở lại."');
INSERT INTO "hasan_abdulkarim" VALUES(67,25,'67:25','Chúng nói: “Chừng nào lời hứa này sẽ xảy ra nếu quí vị nói thật?”');
INSERT INTO "hasan_abdulkarim" VALUES(67,26,'67:26','Hãy bảo chúng: “Chỉ riêng Allah có đủ hiểu biết (về giờ giấc đó). Ta -Muhammad- chỉ là một người báo trước công khai.”');
INSERT INTO "hasan_abdulkarim" VALUES(67,27,'67:27','Bởi thế, khi thấy nó đến gần, gương mặt của những kẻ không có đức tin tỏ vẻ buồn rầu và có lời bảo: “Đây là cái mà các người thường cầu nguyện”.');
INSERT INTO "hasan_abdulkarim" VALUES(67,28,'67:28','Hãy bảo chúng: “Các người có xét thấy chăng, nếu Allah muốn tiêu diệt ta và người nào theo ta hoặc muốn khoan dung bọn ta thì ai có thể cứu những kẻ không có đức tin thoát khỏi sự trừng phạt đau đớn?”');
INSERT INTO "hasan_abdulkarim" VALUES(67,29,'67:29','Hãy bảo chúng: “Ngài là Đấng Độ Lượng, bọn ta tin tưởng Ngài và phó thác chó Ngài. Rồi đây các người sẽ sớm biết ai là kẻ lạc hướng rõ rệt.”');
INSERT INTO "hasan_abdulkarim" VALUES(67,30,'67:30','Hãy bảo chúng: “Các người có xét thấy chăng? Nếu vào một buổi sáng nào đó mạch nước của các người rút mất tiêu (xuống lòng đất) thì ai sẽ mang nước mạch trong vắt đến tiếp liệu cho các người?”');
INSERT INTO "hasan_abdulkarim" VALUES(68,1,'68:1','Nun. Thề bởi cây viết và điều mà họ (Thiên Thần) ghi chép.');
INSERT INTO "hasan_abdulkarim" VALUES(68,2,'68:2','Do Ân Huệ của Thượng Đế (Allah) của Ngươi (Muhammad!), Ngươi không phải là một kẻ điên.');
INSERT INTO "hasan_abdulkarim" VALUES(68,3,'68:3','Và quả thật, Ngươi sẽ được một phần thưởng không hề dứt.');
INSERT INTO "hasan_abdulkarim" VALUES(68,4,'68:4','Và quả thật, Ngươi được phú cho những đức tính vĩ đại');
INSERT INTO "hasan_abdulkarim" VALUES(68,5,'68:5','Bởi thế, Ngươi sẽ sớm thấy và chúng cũng sẽ sớm thấy,');
INSERT INTO "hasan_abdulkarim" VALUES(68,6,'68:6','Ai trong các ngươi là kẻ mắc bệnh điên.');
INSERT INTO "hasan_abdulkarim" VALUES(68,7,'68:7','Quả thật, Thượng Đế của Ngươi biết rõ ai lạc khỏi con đường của Ngài và biết ai đi đúng đường.');
INSERT INTO "hasan_abdulkarim" VALUES(68,8,'68:8','Bởi thế, chớ nghe theo những kẻ phủ nhận (Thông Điệp của Allah).');
INSERT INTO "hasan_abdulkarim" VALUES(68,9,'68:9','Chúng mong rằng nếu Ngươi nhượng bộ thì chúng cũng sẽ nhượng bộ.');
INSERT INTO "hasan_abdulkarim" VALUES(68,10,'68:10','Và chớ nghe theo từng tên thề thốt đê tiện,');
INSERT INTO "hasan_abdulkarim" VALUES(68,11,'68:11','Một kẻ vu khống chuyên tới lui nói xấu người khác,');
INSERT INTO "hasan_abdulkarim" VALUES(68,12,'68:12','Ngăn cản điều thiện, thái quá và tội lỗi,');
INSERT INTO "hasan_abdulkarim" VALUES(68,13,'68:13','Hung bạo, và ngoài ra, hạ cấp,');
INSERT INTO "hasan_abdulkarim" VALUES(68,14,'68:14','Như thế là vì y giàu có và đông con.');
INSERT INTO "hasan_abdulkarim" VALUES(68,15,'68:15','Khi các Câu Kinh của TA (Allah) được đọc nhắc y, y bảo: “Chuyện của cổ nhân.”');
INSERT INTO "hasan_abdulkarim" VALUES(68,16,'68:16','TA (Allah) sẽ đóng dấu (sỉ nhục) trên mõm (miệng) của y!');
INSERT INTO "hasan_abdulkarim" VALUES(68,17,'68:17','Quả thật, TA thử thách chúng như việc TA đã thử thách những người bạn của một ngôi vườn khi chúng thề sẽ hái hết trái cây vào một buổi sáng.');
INSERT INTO "hasan_abdulkarim" VALUES(68,18,'68:18','Nhưng đã không nói ''Insha-Allah'' (nếu Allah muốn).');
INSERT INTO "hasan_abdulkarim" VALUES(68,19,'68:19','Do đó, một tai ương từ Thượng Đế của Ngươi đã đến tàn phá nó trong lúc chúng ngủ;');
INSERT INTO "hasan_abdulkarim" VALUES(68,20,'68:20','Cho nên, sáng hôm sau nó (vườn trái cây) đen thui như bị cháy rụi.');
INSERT INTO "hasan_abdulkarim" VALUES(68,21,'68:21','Và khi trời hừng sáng, chúng gọi nhau (bảo);');
INSERT INTO "hasan_abdulkarim" VALUES(68,22,'68:22','“Nếu các anh muốn hái trái thì hãy ra vườn sớm!”');
INSERT INTO "hasan_abdulkarim" VALUES(68,23,'68:23','"Rồi chúng vừa đi vừa hạ thấp giọng, thì thào với nhau:”');
INSERT INTO "hasan_abdulkarim" VALUES(68,24,'68:24','“Chớ để cho một người nghèo nào vào vườn hái trái cây trước các anh.”');
INSERT INTO "hasan_abdulkarim" VALUES(68,25,'68:25','Và chúng ra đi thật sớm, nhất quyết trong ý đồ ngăn chặn đó.');
INSERT INTO "hasan_abdulkarim" VALUES(68,26,'68:26','Nhưng khi thấy nó (ngôi vườn), chúng bảo nhau: “Quả thật, chúng ta đi lạc.”');
INSERT INTO "hasan_abdulkarim" VALUES(68,27,'68:27','“Không, chúng ta bị tước mất hoa màu.”');
INSERT INTO "hasan_abdulkarim" VALUES(68,28,'68:28','Một người ôn hòa nhất trong bọn lên tiếng: “Há tôi đã không nói với các anh tại sao các anh không tán dương (Allah)?”');
INSERT INTO "hasan_abdulkarim" VALUES(68,29,'68:29','Chúng liền thốt: “Quang vinh thay Thượng Đế bầy tôi! Bầy tôi đã sai quấy.”');
INSERT INTO "hasan_abdulkarim" VALUES(68,30,'68:30','Rồi chúng quay lại trách móc nhau.');
INSERT INTO "hasan_abdulkarim" VALUES(68,31,'68:31','Chúng than: “Thật khổ thân chúng ta! Chúng ta là những kẻ phạm tội.”');
INSERT INTO "hasan_abdulkarim" VALUES(68,32,'68:32','Có lẽ Thượng Đế chúng ta sẽ đổi lại cho chúng ta một ngôi vườn khác tốt hơn nó. Chúng ta là những người cầu khẩn Thượng Đế chúng ta.');
INSERT INTO "hasan_abdulkarim" VALUES(68,33,'68:33','Sự trừng phạt (ở đời này) đúng như thế. Nhưng sự trừng phạt ở đời sau chắc chắn sẽ to lớn hơn nếu họ biết.');
INSERT INTO "hasan_abdulkarim" VALUES(68,34,'68:34','Những người sợ Allah sẽ hưởng thiên đàng Hạnh phúc nơi Thượng Đế của họ.');
INSERT INTO "hasan_abdulkarim" VALUES(68,35,'68:35','Há TA sẽ đối xử với những người Muslim như những kẻ tội lỗi hay sao?');
INSERT INTO "hasan_abdulkarim" VALUES(68,36,'68:36','Các ngươi gặp chuyện gì vậy? Các ngươi xử lý ra sao?');
INSERT INTO "hasan_abdulkarim" VALUES(68,37,'68:37','Hoặc phải chăng các ngươi có Kinh Sách mà các ngươi học hỏi.');
INSERT INTO "hasan_abdulkarim" VALUES(68,38,'68:38','Rằng các ngươi có được trong đó mọi điều mà các ngươi chọn?');
INSERT INTO "hasan_abdulkarim" VALUES(68,39,'68:39','Há các ngươi đã nhận từ TA một lời thề có hiệu lực cho đến Ngày phục sinh (rằng) các ngươi sẽ có được bất cứ điều gì mà các ngươi muốn?');
INSERT INTO "hasan_abdulkarim" VALUES(68,40,'68:40','Hãy hỏi chúng, ai là người của chúng sẽ đứng ra bảo đảm điều đó!');
INSERT INTO "hasan_abdulkarim" VALUES(68,41,'68:41','Hoặc phải chăng chúng có những kẻ hợp tác (đứng ra bảo đảm)? Hãy đưa những ''kẻ hợp tác'' của chúng đến nếu chúng nói thật.');
INSERT INTO "hasan_abdulkarim" VALUES(68,42,'68:42','Vào Ngày mà ống Chân sẽ được để lộ ra và chúng sẽ được gọi đến để phủ phục trước (Allah) Nhưng chúng sẽ không thể quỳ được-');
INSERT INTO "hasan_abdulkarim" VALUES(68,43,'68:43','Cặp mắt của chúng nhìn gục xuống, đầy xấu hổ. Và quả thật trước kia chúng đã được mời đến để phủ phục trong lúc hãy còn khỏe mạnh.');
INSERT INTO "hasan_abdulkarim" VALUES(68,44,'68:44','Thôi, hãy để mặc TA với kẻ phủ nhận lời tường trình này (Qur''an). TA sẽ lôi chúng dần dần từ nơi mà chúng không nhận thấy.');
INSERT INTO "hasan_abdulkarim" VALUES(68,45,'68:45','Và TA tạm tha cho chúng. Chắc chắn, kế hoạch của TA rất mãnh liệt.');
INSERT INTO "hasan_abdulkarim" VALUES(68,46,'68:46','Hoặc phải chăng Ngươi đã đòi chúng tiền thù lao cho nên chúng nợ nần chồng chất?');
INSERT INTO "hasan_abdulkarim" VALUES(68,47,'68:47','Hoặc phải chăng chúng nắm điều vô hình nên chúng viết nó xuống?');
INSERT INTO "hasan_abdulkarim" VALUES(68,48,'68:48','Bởi thế, hãy kiên nhẫn chờ đợi Lệnh Phán của cảa Ngươi và chớ như Người Bạn của con Cá( (Yunus) khi Y cầu cứu trong lúc đau khổ tuyệt vọng.');
INSERT INTO "hasan_abdulkarim" VALUES(68,49,'68:49','Nếu không do một Ân Huệ từ Thượng Đế của Y ban cho; và Y đã được quẳng lên bãi biển hoang vu, và đáng bị khiển trách.');
INSERT INTO "hasan_abdulkarim" VALUES(68,50,'68:50','Rồi Thượng Đế của Y đã chọn Y và làm cho Y trở thành một người đức hạnh.');
INSERT INTO "hasan_abdulkarim" VALUES(68,51,'68:51','Và những kẻ không có đức tin muốn làm cho Ngươi sa chân với cái nhìn của chúng khi chúng nghe Lời Nhắc Nhở (của Qur''an); và chúng lên tiếng bảo: “Rõ thật, Y (Muhammad) là một thằng điên.”');
INSERT INTO "hasan_abdulkarim" VALUES(68,52,'68:52','Và Nó (Qur''an) chỉ là Lời Nhắc Nhở (của Allah) ban cho muôn loài.');
INSERT INTO "hasan_abdulkarim" VALUES(69,1,'69:1','Thực tại!');
INSERT INTO "hasan_abdulkarim" VALUES(69,2,'69:2','Thực tại là gì?');
INSERT INTO "hasan_abdulkarim" VALUES(69,3,'69:3','Và điều gì cho Ngươi (Muhammad) biết Thực tại là gì?');
INSERT INTO "hasan_abdulkarim" VALUES(69,4,'69:4','(Bộ tộc) Thamud và dân tộc ''Ad phủ nhận Qari''ah (Ngày Tận Thế)!');
INSERT INTO "hasan_abdulkarim" VALUES(69,5,'69:5','Bởi thế, đối với bộ tộc Thamud. chúng đã bị tiêu diệt bởi một trận bùng nổ dữ dội.');
INSERT INTO "hasan_abdulkarim" VALUES(69,6,'69:6','Và đối với dân tộc ''Ad, chúng đã bị tiêu diệt bởi một trận cuồng phong hết sức khủng khiếp;');
INSERT INTO "hasan_abdulkarim" VALUES(69,7,'69:7','Mà Ngài (Allah) đã cho thổi dữ dội liên tiếp bảy đêm và tám ngày đến nỗi Ngươi có thể thấy người dân nơi đó (chết) nằm la liệt như cây chà là bị bứng gốc và ngã rạp xuống đất.');
INSERT INTO "hasan_abdulkarim" VALUES(69,8,'69:8','Bởi thế, Ngươi có thấy một đứa nào của bọn chúng sống sót hay chăng?');
INSERT INTO "hasan_abdulkarim" VALUES(69,9,'69:9','Và đến lượt Fir-''awn, và những kẻ trước y và những thị trấn (Sodom và Gomorrah) đã bị lật ngược vì quá tội lỗi.');
INSERT INTO "hasan_abdulkarim" VALUES(69,10,'69:10','Bởi vì chúng bất tuân vị Sứ Giả của Thượng Đế (Allah) của chúng cho nên Ngài bắt phạt chúng bằng một sự túm bắt cường bạo.');
INSERT INTO "hasan_abdulkarim" VALUES(69,11,'69:11','Chính TA đã chuyên chở các ngươi trôi nổi khi nước dâng quá mức.');
INSERT INTO "hasan_abdulkarim" VALUES(69,12,'69:12','Mà TA đã làm để nhắc nhở các ngươi và những ai (có) tai, ghi nhớ nó.');
INSERT INTO "hasan_abdulkarim" VALUES(69,13,'69:13','Bởi thế, khi Còi Hụ hụ lên tiếng đầu tiên;');
INSERT INTO "hasan_abdulkarim" VALUES(69,14,'69:14','Và khi đất đai với núi non bị dời đi và bị đập nát;');
INSERT INTO "hasan_abdulkarim" VALUES(69,15,'69:15','Vào Ngày đó, Biến cố trọng đại sẽ xảy ra.');
INSERT INTO "hasan_abdulkarim" VALUES(69,16,'69:16','Và bầu trời sẽ chẻ ra làm đôi bởi vì vào Ngày đó nó sẽ mỏng manh,');
INSERT INTO "hasan_abdulkarim" VALUES(69,17,'69:17','Và các Thiên Thần sẽ ở trên ranh giới của nó; và vào Ngày đó tám Thiên Thần sẽ khiêng Ngai Vương của Thượng Đế của Ngươi bên trên chúng.');
INSERT INTO "hasan_abdulkarim" VALUES(69,18,'69:18','Vào Ngày đó, các ngươi sẽ được triệu tập để xét xử, không một điều bí mật nào của các ngươi sẽ được giấu nhẹm nữa,');
INSERT INTO "hasan_abdulkarim" VALUES(69,19,'69:19','Bởi thế, đối với ai được trao cho sổ bộ của mình nơi tay phải thì sẽ nói: “Đây, hãy đọc sổ bộ của tôi.”');
INSERT INTO "hasan_abdulkarim" VALUES(69,20,'69:20','"Tôi nghĩ tôi sẽ phải đối mặt với sự thanh toán.”');
INSERT INTO "hasan_abdulkarim" VALUES(69,21,'69:21','Bởi thế, y sẽ sống một đời sống hạnh phúc,');
INSERT INTO "hasan_abdulkarim" VALUES(69,22,'69:22','Nơi một ngôi Vườn (thiên đàng) trên cao,');
INSERT INTO "hasan_abdulkarim" VALUES(69,23,'69:23','Cho ra trái từng chùm (thấp và gần) vừa tầm tay hái.');
INSERT INTO "hasan_abdulkarim" VALUES(69,24,'69:24','Có lời bảo: "Hãy ăn và uống thoải mái vì điều (thiện) mà quí vị đã gởi đi trước cho mình vào những ngày đã qua.”');
INSERT INTO "hasan_abdulkarim" VALUES(69,25,'69:25','Ngược lại, đối với ai được trao cho sổ bộ của mình nơi tay trái thì sẽ than: “Thật khổ thân tôi! Thà đừng giao cho tôi sổ bộ này của tôi!');
INSERT INTO "hasan_abdulkarim" VALUES(69,26,'69:26','“Và thà đừng hay biết gì về việc thanh toán (này) của tôi!');
INSERT INTO "hasan_abdulkarim" VALUES(69,27,'69:27','“Ôi, thà chết phứt đi cho rồi!');
INSERT INTO "hasan_abdulkarim" VALUES(69,28,'69:28','“Của cải của tôi chẳng giúp gì được cho tôi,');
INSERT INTO "hasan_abdulkarim" VALUES(69,29,'69:29','“Quyền hành của tôi đã rời bỏ tôi!”');
INSERT INTO "hasan_abdulkarim" VALUES(69,30,'69:30','(Có lệnh phán:) “Hãy túm bắt và trói hắn lại,');
INSERT INTO "hasan_abdulkarim" VALUES(69,31,'69:31','“Hãy nướng hắn trong Lửa ngọn (của hỏa ngục).');
INSERT INTO "hasan_abdulkarim" VALUES(69,32,'69:32','“Rồi xiềng hắn bằng sợi dây xích dài bằng bảy mươi tấc.');
INSERT INTO "hasan_abdulkarim" VALUES(69,33,'69:33','“Rõ thật, hắn thường không tin tưởng nơi Allah Chí Đại,');
INSERT INTO "hasan_abdulkarim" VALUES(69,34,'69:34','“Và không khuyến khích (người khác) nuôi ăn người túng thiếu;');
INSERT INTO "hasan_abdulkarim" VALUES(69,35,'69:35','“Bởi thế, ngày nay hắn không có bạn tại đây.');
INSERT INTO "hasan_abdulkarim" VALUES(69,36,'69:36','“Và không có thức ăn nào khác ngoài chất mủ hôi tanh từ vết thương,');
INSERT INTO "hasan_abdulkarim" VALUES(69,37,'69:37','“Chỉ để dành cho những kẻ phạm nhân ăn mà thôi.”');
INSERT INTO "hasan_abdulkarim" VALUES(69,38,'69:38','Bởi thế, TA (Allah) thề bởi điều mà các ngươi nhìn thấy,');
INSERT INTO "hasan_abdulkarim" VALUES(69,39,'69:39','Và bởi điều mà các ngươi không thấy,');
INSERT INTO "hasan_abdulkarim" VALUES(69,40,'69:40','Rằng Nó (Qur''an) là lời đọc của một vị Sứ Giả rất vinh dự.');
INSERT INTO "hasan_abdulkarim" VALUES(69,41,'69:41','Và Nó không phải là lời của một thi sĩ. Nhưng các ngươi chẳng mấy tin.');
INSERT INTO "hasan_abdulkarim" VALUES(69,42,'69:42','Và Nó cũng không phải là lời của một thầy bói. Các ngươi không lưu ý.');
INSERT INTO "hasan_abdulkarim" VALUES(69,43,'69:43','(Nó là) Lời Mặc Khải do Thượng Đế của vũ trụ và muôn loài ban xuống.');
INSERT INTO "hasan_abdulkarim" VALUES(69,44,'69:44','Và nếu Y (Muhammad) bịa đặt những lời lẽ về (Allah),');
INSERT INTO "hasan_abdulkarim" VALUES(69,45,'69:45','Thì chắc chắn TA sẽ túm bắt Y bằng tay phải,');
INSERT INTO "hasan_abdulkarim" VALUES(69,46,'69:46','Rồi TA sẽ cắt đứt động mạch (nơi quả tim) của Y,');
INSERT INTO "hasan_abdulkarim" VALUES(69,47,'69:47','Bởi thế, không một ai trong các ngươi có thể bảo vệ Y (khỏi TA).');
INSERT INTO "hasan_abdulkarim" VALUES(69,48,'69:48','Và quả thật, Nó là Lời Nhắc Nhở ban cho những người sợ Allah.');
INSERT INTO "hasan_abdulkarim" VALUES(69,49,'69:49','Và TA biết chắc trong các ngươi có người phủ nhận (Nó).');
INSERT INTO "hasan_abdulkarim" VALUES(69,50,'69:50','Và quả thật, Nó là nguồn lo âu cho những kẻ không có đức tin.');
INSERT INTO "hasan_abdulkarim" VALUES(69,51,'69:51','Nhưng Nó là Chân Lý tuyệt đối được khẳng định chắc chắn.');
INSERT INTO "hasan_abdulkarim" VALUES(69,52,'69:52','Bởi thế, hãy tán dương Đại Danh của Thượng Đế Chí Đại của Ngươi.');
INSERT INTO "hasan_abdulkarim" VALUES(70,1,'70:1','Có người hỏi về sự Trừng phạt sắp xảy ra');
INSERT INTO "hasan_abdulkarim" VALUES(70,2,'70:2','Cho những kẻ không có đức tin mà không ai có thể tránh được,');
INSERT INTO "hasan_abdulkarim" VALUES(70,3,'70:3','Từ Allah, Chủ Nhân của những con đường thăng lên trời,');
INSERT INTO "hasan_abdulkarim" VALUES(70,4,'70:4','Các Thiên Thần và Ruh (Jibril) đi lên chầu Ngài trong một Ngày tương đương với năm mươi ngàn năm (theo niên kỷ của con người).');
INSERT INTO "hasan_abdulkarim" VALUES(70,5,'70:5','Bởi thế, hãy kiên nhẫn chịu đựng một cách vui tươi,');
INSERT INTO "hasan_abdulkarim" VALUES(70,6,'70:6','Quả thật, chúng (những kẻ không có đức tin) thấy nó xa vời,');
INSERT INTO "hasan_abdulkarim" VALUES(70,7,'70:7','Nhưng TA (Allah) thấy nó gần kề;');
INSERT INTO "hasan_abdulkarim" VALUES(70,8,'70:8','Ngày mà bầu trời sẽ (tan rã) như đồng thau được nấu chảy;');
INSERT INTO "hasan_abdulkarim" VALUES(70,9,'70:9','Và những quả núi sẽ (nhẹ) như lông trừu;');
INSERT INTO "hasan_abdulkarim" VALUES(70,10,'70:10','Không một người bạn nào sẽ hỏi thăm người bạn mình;');
INSERT INTO "hasan_abdulkarim" VALUES(70,11,'70:11','Mặc dầu họ nhìn thấy nhau. Kẻ tội lỗi mong muốn nếu y có thể dâng các đứa con để thế mạng hầu khỏi bị trừng phạt vào ngay đó;');
INSERT INTO "hasan_abdulkarim" VALUES(70,12,'70:12','(Dâng) cả vợ và anh em của y,');
INSERT INTO "hasan_abdulkarim" VALUES(70,13,'70:13','(Dâng) bà con ruột thịt đã chứa chấp y,');
INSERT INTO "hasan_abdulkarim" VALUES(70,14,'70:14','Và tất cả những gì trên quả đất; rồi để nó (sự dâng cúng) sẽ giải cứu y.');
INSERT INTO "hasan_abdulkarim" VALUES(70,15,'70:15','Nhưng vô phương. Bởi vì chỉ có Lửa ngọn');
INSERT INTO "hasan_abdulkarim" VALUES(70,16,'70:16','Đốt tróc da đầu.');
INSERT INTO "hasan_abdulkarim" VALUES(70,17,'70:17','Nó (Lửa) sẽ gọi ai quay lưng và quay mặt bỏ đi,');
INSERT INTO "hasan_abdulkarim" VALUES(70,18,'70:18','Và tom góp của cải và giấu kỹ.');
INSERT INTO "hasan_abdulkarim" VALUES(70,19,'70:19','Quả thật, con người được tạo ra vốn nôn nóng,');
INSERT INTO "hasan_abdulkarim" VALUES(70,20,'70:20','Hay than vãn khi gặp điều dữ;');
INSERT INTO "hasan_abdulkarim" VALUES(70,21,'70:21','Và keo kiệt khi gặp điều lành (may mắn, giàu có).');
INSERT INTO "hasan_abdulkarim" VALUES(70,22,'70:22','Ngoại trừ những người dâng lễ nguyện Salah,');
INSERT INTO "hasan_abdulkarim" VALUES(70,23,'70:23','Những ai kiên tri trong việc dâng lễ nguyện Salah của họ;');
INSERT INTO "hasan_abdulkarim" VALUES(70,24,'70:24','Và những ai trích xuất từ tài sản của họ một phần qui định bắt buộc,');
INSERT INTO "hasan_abdulkarim" VALUES(70,25,'70:25','(Để bố thí) cho người ăn xin và người thiếu hụt;');
INSERT INTO "hasan_abdulkarim" VALUES(70,26,'70:26','Và những ai xác nhận có Ngày Phán Xử (Cuối Cùng),');
INSERT INTO "hasan_abdulkarim" VALUES(70,27,'70:27','Và những ai sợ sự trừng phạt của Thượng Đế (Allah) của họ;');
INSERT INTO "hasan_abdulkarim" VALUES(70,28,'70:28','Sự trừng phạt của Thượng Đế của họ là điều mà không ai cảm thấy an toàn.');
INSERT INTO "hasan_abdulkarim" VALUES(70,29,'70:29','Và những ai giữ gìn phần kín đáo của cơ thể (tiết chế tình dục),');
INSERT INTO "hasan_abdulkarim" VALUES(70,30,'70:30','Ngoại trừ với vợ của mình và với ai nằm trong tay phải của mình (các nữ tù binh); bởi vì họ không bị khiển trách (đối với họ).');
INSERT INTO "hasan_abdulkarim" VALUES(70,31,'70:31','Nhưng ai tìm cách vượt quá (giới hạn) đó thì sẽ là những kẻ phạm tội.');
INSERT INTO "hasan_abdulkarim" VALUES(70,32,'70:32','Và những ai tôn trọng sự ký thác và lời giao ước;');
INSERT INTO "hasan_abdulkarim" VALUES(70,33,'70:33','Và những ai đứng vững trong việc làm chứng của họ;');
INSERT INTO "hasan_abdulkarim" VALUES(70,34,'70:34','Và những ai chăm nom việc dâng lễ nguyện Salah của họ.');
INSERT INTO "hasan_abdulkarim" VALUES(70,35,'70:35','Những người đó sẽ vinh dự sống trong những Ngôi Vườn (thiên đàng).');
INSERT INTO "hasan_abdulkarim" VALUES(70,36,'70:36','Chuyện gì xảy ra cho những kẻ không tin đổ xô chạy đến trước mặt Ngươi.');
INSERT INTO "hasan_abdulkarim" VALUES(70,37,'70:37','Từng nhóm một, từ phía bên phải và bên trái?');
INSERT INTO "hasan_abdulkarim" VALUES(70,38,'70:38','Há từng tên của bọn chúng muốn được vào thiên đàng Hạnh phúc?');
INSERT INTO "hasan_abdulkarim" VALUES(70,39,'70:39','Vô phương! Quả thật, TA đã tạo chúng từ chất mà chúng biết.');
INSERT INTO "hasan_abdulkarim" VALUES(70,40,'70:40','Bởi thế, TA thề nhân danh Thượng Đế của các điểm của hướng Đông và của hướng Tây rằng TA có quyền định đoạt');
INSERT INTO "hasan_abdulkarim" VALUES(70,41,'70:41','Việc thay thế chúng bằng những người khác tốt hơn chúng. Và TA sẽ không thất bại (trong Kế hoạch của TA).');
INSERT INTO "hasan_abdulkarim" VALUES(70,42,'70:42','Bởi thế, hãy để mặc chúng vùi đầu tán gẫu và vui đùa cho đến khi giáp Ngày (Phán Xử) mà chúng đã được hứa;');
INSERT INTO "hasan_abdulkarim" VALUES(70,43,'70:43','Ngày mà chúng sẽ vội vã đi ra khỏi mộ giống như cảnh chúng đâm đầu chạy đến mục tiêu (đã định sẵn cho chúng),');
INSERT INTO "hasan_abdulkarim" VALUES(70,44,'70:44','Cặp mắt nhìn xuống, tràn đầy nhục nhã. Đó là Ngày mà chúng đã được hứa!');
INSERT INTO "hasan_abdulkarim" VALUES(71,1,'71:1','Quả thật, TA (Allah) đã cử Nuh (Nô-ê) đến cho người dân của Y (với Mệnh Lệnh): “Hãy cảnh cáo người dân của Ngươi trước khi một sự trừng phạt đau đớn sẽ xảy đến cho họ.”');
INSERT INTO "hasan_abdulkarim" VALUES(71,2,'71:2','Người bảo: “Này hỡi dân ta! Ta là một người báo trước được công khai cử đến với các người."');
INSERT INTO "hasan_abdulkarim" VALUES(71,3,'71:3','Rằng “Hãy thờ phụng Allah và sợ Ngài và vâng lời ta."');
INSERT INTO "hasan_abdulkarim" VALUES(71,4,'71:4','“Ngài sẽ tha thứ tội lỗi cho các người và gia hạn cho các người (sống) đến một thời hạn ấn định. Quả thật, khi thời hạn do Allah ấn định đã mãn thì sẽ không còn việc gia hạn nữa, nếu các người biết.”');
INSERT INTO "hasan_abdulkarim" VALUES(71,5,'71:5','(Nuh) thưa: “Lạy Thượng Đế của bề tôi! Bề tôi đã gọi mời người dân của bề tôi ban đêm lẫn ban ngày"');
INSERT INTO "hasan_abdulkarim" VALUES(71,6,'71:6','“Nhưng lời gọi mời của bề tôi chẳng thêm thắt được gì ngược lại chỉ làm cho họ chạy xa hơn."');
INSERT INTO "hasan_abdulkarim" VALUES(71,7,'71:7','“Và mỗi lần bề tôi gọi mời họ (theo Islam) để Ngài tha thứ cho họ thì họ lấy ngón tay bịt tai lại và lấy áo choàng phủ lên mình và tiếp tục ngoan cố và vô cùng ngạo mạn."');
INSERT INTO "hasan_abdulkarim" VALUES(71,8,'71:8','“Rồi, bề tôi gọi mời họ lớn tiếng."');
INSERT INTO "hasan_abdulkarim" VALUES(71,9,'71:9','“Rồi, bề tôi nói với họ có lúc công khai có lúc kín đáo."');
INSERT INTO "hasan_abdulkarim" VALUES(71,10,'71:10','“Bề tôi bảo họ: ''Hãy cầu xin Thượng Đế của các người tha thứ. Quả thật, Ngài Hằng Tha Thứ"');
INSERT INTO "hasan_abdulkarim" VALUES(71,11,'71:11','Ngài sẽ cho mưa xuống dồi dào cho các ngươi.');
INSERT INTO "hasan_abdulkarim" VALUES(71,12,'71:12','Và gia tăng của cải và con cái cho các ngươi và tạo vườn tược và sông ngòi cho các ngươi.');
INSERT INTO "hasan_abdulkarim" VALUES(71,13,'71:13','Các ngươi có chuyện gì vậy? Tại sao các ngươi không hy vọng nơi Quyền Uy (ban cấp) của Allah?');
INSERT INTO "hasan_abdulkarim" VALUES(71,14,'71:14','Và chắc chắn Ngài đã tạo các ngươi theo từng giai đoạn.');
INSERT INTO "hasan_abdulkarim" VALUES(71,15,'71:15','Há các ngươi không thấy việc Allah đã tạo bảy tầng trời chồng lên nhau như thế nào ư?');
INSERT INTO "hasan_abdulkarim" VALUES(71,16,'71:16','Và đã làm ra trong chúng mặt trăng như một ánh sáng và mặt trời như một chiếc đèn.');
INSERT INTO "hasan_abdulkarim" VALUES(71,17,'71:17','Và Allah đã tạo sinh và tăng trưởng các ngươi từ đất đai.');
INSERT INTO "hasan_abdulkarim" VALUES(71,18,'71:18','Rồi, Ngài sẽ hoàn các ngươi vào trong đó lại và đưa các ngươi đi ra bằng một cuộc đi ra toàn bộ.');
INSERT INTO "hasan_abdulkarim" VALUES(71,19,'71:19','Và Allah làm ra trái đất cho các ngươi như một tấm thảm được trải rộng.');
INSERT INTO "hasan_abdulkarim" VALUES(71,20,'71:20','Để cho các ngươi đi lại trên những con đường thênh thang của nó.');
INSERT INTO "hasan_abdulkarim" VALUES(71,21,'71:21','Nuh thưa: “Lạy Thượng Đế của bề tôi! Họ bất tuân bề tôi và nghe theo người mà của cải và con cái chẳng thêm được gì cho y ngoại trừ sự mất mát.”');
INSERT INTO "hasan_abdulkarim" VALUES(71,22,'71:22','Và họ mưu định một kế hoạch to lớn.');
INSERT INTO "hasan_abdulkarim" VALUES(71,23,'71:23','Và họ bảo: “Chớ từ bỏ các thần linh của các người và chớ bỏ (các thần): Wadd, Suwa'', Yaguth, Ya''uq và Nasr."');
INSERT INTO "hasan_abdulkarim" VALUES(71,24,'71:24','"Và chắc chắn, họ đã dắt nhiều người đi lạc; và xin Ngài đừng gia tăng gì cho những kẻ làm điều sai quấy ngoại trừ sự lầm lạc.”');
INSERT INTO "hasan_abdulkarim" VALUES(71,25,'71:25','Do những hành vi tội lỗi của họ mà họ bị nhận chết chìm sau đó bị đưa vào Lửa. Bởi thế, ngoài Allah, họ đã không tìm được một ai để giúp đỡ.');
INSERT INTO "hasan_abdulkarim" VALUES(71,26,'71:26','Và Nuh thưa: “Lạy Thượng Đế của bề tôi! Xin Ngài đừng để cho một tên phản nghịch nào sống sót trên trái đất."');
INSERT INTO "hasan_abdulkarim" VALUES(71,27,'71:27','“Bởi vì nếu Ngài để chừa chúng lại thì chúng sẽ dắt các bầy tôi của Ngài đi lạc và sẽ sanh con đẻ cháu gồm toàn là những tên phản nghịch, vong ơn."');
INSERT INTO "hasan_abdulkarim" VALUES(71,28,'71:28','“Lạy Thượng Đế của bề tôi! Xin Ngài tha thứ cho bề tôi và cho cha mẹ của bề tôi và cho ai vào nhà của bề tôi như một người có đức tin và cho những người nam và nữ tín đồ và chớ gia tăng gì thêm cho những kẻ làm điều sai quấy ngoại trừ sự diệt vong!”');
INSERT INTO "hasan_abdulkarim" VALUES(72,1,'72:1','Hãy bảo: “Ta (Muhammad) đã được mặc khải cho biết có một đám Jinn đã nghe (đọc Qur''an). Chúng bảo: “Chúng tôi đã nghe một cuộc xướng đọc (Qur''an) hết sức tuyệt diệu!"');
INSERT INTO "hasan_abdulkarim" VALUES(72,2,'72:2','“Nó hướng dẫn đến Chân Lý. Bởi thế, chúng tôi tin nơi Nó. Và chúng tôi không tổ một kẻ nào cùng với Thượng Đế của chúng tôi."');
INSERT INTO "hasan_abdulkarim" VALUES(72,3,'72:3','"Quả thật, Quyền Uy của Thượng Đế của chúng tôi hết sức tối cao, Ngài không có vợ và không có con."');
INSERT INTO "hasan_abdulkarim" VALUES(72,4,'72:4','"Và quả thật, trong chúng tôi có kẻ điên rồ thường nói những điều quá đáng cho Allah."');
INSERT INTO "hasan_abdulkarim" VALUES(72,5,'72:5','"Và chúng tôi nghĩ loài người và Jinn chớ nên nói những điều gian dối cho Allah."');
INSERT INTO "hasan_abdulkarim" VALUES(72,6,'72:6','"Và quả thật, có những đàn ông trong loài người đã nhờ những đàn ông trong loài Jinn che chở nhưng chúng chỉ làm cho họ mắc thêm tội."');
INSERT INTO "hasan_abdulkarim" VALUES(72,7,'72:7','"Và quả thật họ (những kẻ không có đức tin) đã nghĩ như các ngươi đang nghĩ rằng Allah sẽ không bao giờ phục sinh một ai cả."');
INSERT INTO "hasan_abdulkarim" VALUES(72,8,'72:8','"Và chúng tôi đã đi lên đụng trời nhưng thấy đầy những cảnh vệ binh hung tợn và lửa ngọn sáng rực trong đó."');
INSERT INTO "hasan_abdulkarim" VALUES(72,9,'72:9','"Và quả thật, chúng tôi thường ngồi tại những chỗ ngồi của nó (trên trời) để nghe lén tin tức nhưng bây giờ ai nghe trộm thì sẽ thấy một ngọn lửa đang mai phục ngó y."');
INSERT INTO "hasan_abdulkarim" VALUES(72,10,'72:10','"Và chúng tôi không biết có phải Thượng Đế của họ muốn giáng tai họa xuống cho những ai sống trên trái đất hay là muốn hướng dẫn họ đến Chân Lý."');
INSERT INTO "hasan_abdulkarim" VALUES(72,11,'72:11','"Và quả thật, trong chúng tôi có người đức hạnh và trong chúng tôi cũng có kẻ khác đi. Chúng tôi theo những con đường khác biệt."');
INSERT INTO "hasan_abdulkarim" VALUES(72,12,'72:12','"Và quả thật, chúng tôi nghĩ chúng tôi không có cách chi làm cho Allah thất bại trên trái đất và chúng tôi vô phương chạy trốn khỏi Ngài."');
INSERT INTO "hasan_abdulkarim" VALUES(72,13,'72:13','"Và quả thật khi chúng tôi nghe Chỉ Đạo (Qur''an) chúng tôi tin nơi Nó. Và ai tin tưởng nơi Thượng Đế của y thì sẽ không sợ bị giảm (phần thưởng) cũng không sợ bị nặng (hình phạt)."');
INSERT INTO "hasan_abdulkarim" VALUES(72,14,'72:14','"Và quả thật, trong chúng tôi có một số chịu thần phục Allah (thành Muslim) nhưng trong chúng tôi cũng có một số lệch lạc. Bởi thế, ai theo Islam (thần phục Allah) thì là tìm được Chân Lý."');
INSERT INTO "hasan_abdulkarim" VALUES(72,15,'72:15','Ngược lại, đối với những kẻ đi sái đường, chúng sẽ thành chất đốt của hỏa ngục.');
INSERT INTO "hasan_abdulkarim" VALUES(72,16,'72:16','Và nếu họ (người Quraish) tin tưởng nơi Allah và giữ đúng đạo thì chắc chắn TA (Allah) sẽ ban cho họ nước mưa dồi dào;');
INSERT INTO "hasan_abdulkarim" VALUES(72,17,'72:17','Để TA có thể thử thách họ trong đó. Và ai quay bỏ việc tưởng nhớ Thượng Đế của y thì Ngài sẽ đưa đẩy y vào sự trừng phạt gia tăng mãi.');
INSERT INTO "hasan_abdulkarim" VALUES(72,18,'72:18','Và những Thánh đường là của Allah. Bởi vậy, chớ cầu nguyện một ai cùng với Allah (trong đó).');
INSERT INTO "hasan_abdulkarim" VALUES(72,19,'72:19','Và quả thật, khi người bề tôi của Allah đứng cầu nguyện Ngài, chúng (những kẻ không tin) xúm nhau đến bao quanh Người đông đảo.');
INSERT INTO "hasan_abdulkarim" VALUES(72,20,'72:20','Hãy bảo chúng: "Ta chỉ cầu nguyện Thượng Đế của Ta và không tổ hợp một ai cùng với Ngài (trong việc thờ phụng).”');
INSERT INTO "hasan_abdulkarim" VALUES(72,21,'72:21','Hãy bảo chúng: "Ta không có quyền hãm hại các người cũng không buộc các người theo Chân Lý.”');
INSERT INTO "hasan_abdulkarim" VALUES(72,22,'72:22','Hãy bảo chúng: “Nhất định không một ai có thể cứu ta khỏi (sự Trừng phạt của) Allah (nếu ta bất tuân Ngài) và ngoài Ngài, ta không bao giờ tìm được một chỗ nương thân nào khác."');
INSERT INTO "hasan_abdulkarim" VALUES(72,23,'72:23','Trừ phi ta truyền đạt (Chân Lý nhận) từ Ngài và Thông điệp của Ngài. Và ai bất tuân Allah và Sứ Giả của Ngài thì chắc chắn sẽ tiếp nhận Lửa của hỏa ngục mà y sẽ vào ở trong đó đời đời.');
INSERT INTO "hasan_abdulkarim" VALUES(72,24,'72:24','Mãi cho đến khi chúng chứng kiến điều (trừng phạt) mà chúng đã được hứa thì chúng sẽ biết ai là kẻ yếu thế về phương diện người giúp đỡ và thiểu số hơn.');
INSERT INTO "hasan_abdulkarim" VALUES(72,25,'72:25','Hãy bảo chúng: "Ta không biết điều (trừng phạt) mà các người được hứa đã gần kề hay Thượng Đế của Ta định nó vào một thời gian xa hơn.”');
INSERT INTO "hasan_abdulkarim" VALUES(72,26,'72:26','(Chỉ riêng) Ngài biết điều vô hình. Bởi thế, Ngài không tiết lộ điều bí mật của Ngài cho một ai.');
INSERT INTO "hasan_abdulkarim" VALUES(72,27,'72:27','Ngoại trừ một Sứ Giả mà Ngài đã chọn. Bởi thế, quả thật, Ngài phái cảnh vệ binh (Thiên Thần) tháp tùng Người đằng trước và đằng sau');
INSERT INTO "hasan_abdulkarim" VALUES(72,28,'72:28','Để Ngài có thể biết họ (các Sứ Giả) đã truyền đạt Thông điệp của Thượng Đế của họ hay chưa và Ngài bao quát (và kiểm soát) mọi điều nơi họ và đếm từng điều (mặc khải) đã được đánh số.');
INSERT INTO "hasan_abdulkarim" VALUES(73,1,'73:1','Hỡi người cuốn mình trong áo (Nabi Muhammad)!');
INSERT INTO "hasan_abdulkarim" VALUES(73,2,'73:2','Hãy đứng (dâng lễ nguyện Salah) ban đêm nhưng không trọn đêm;');
INSERT INTO "hasan_abdulkarim" VALUES(73,3,'73:3','Phân nửa đêm hay ngắn hơn một chút,');
INSERT INTO "hasan_abdulkarim" VALUES(73,4,'73:4','Hoặc lâu hơn. Và hãy thư thả đọc Qur''an với âm điệu nhịp nhàng.');
INSERT INTO "hasan_abdulkarim" VALUES(73,5,'73:5','Quả thật, TA sẽ sớm truyền xuống cho Ngươi Lời Phán nghiêm trọng.');
INSERT INTO "hasan_abdulkarim" VALUES(73,6,'73:6','Thật vậy, thức dậy ban đêm (để dâng lễ nguyện Salah Tahajjud) là một điều nặng nhọc nhưng tư tưởng sáng suốt và lời nói nghiêm túc.');
INSERT INTO "hasan_abdulkarim" VALUES(73,7,'73:7','Quả thật, ban ngày Ngươi bận bịu với công việc lê thê hằng ngày.');
INSERT INTO "hasan_abdulkarim" VALUES(73,8,'73:8','Nhưng hãy nhắc Đại Danh của Thượng Đế của Ngươi và dốc lòng phụng sự Ngài.');
INSERT INTO "hasan_abdulkarim" VALUES(73,9,'73:9','Thượng Đế của phương Đông và phương Tây, không có Thượng Đế nào khác cả duy chỉ Ngài. Do đó, hãy tôn Ngài làm Đấng Bảo Hộ của Ngươi.');
INSERT INTO "hasan_abdulkarim" VALUES(73,10,'73:10','Và hãy chịu đựng với những điều chúng nói và lánh xa chúng một cách đẹp đẽ.');
INSERT INTO "hasan_abdulkarim" VALUES(73,11,'73:11','Và để mặc TA đối phó với những tên phủ nhận sự thật nhưng giàu tiện nghi (lạc thú); và tạm tha chúng một thời gian ngắn.');
INSERT INTO "hasan_abdulkarim" VALUES(73,12,'73:12','Quả thật, TA có sẵn những sợi dây xích và Lửa ngọn;');
INSERT INTO "hasan_abdulkarim" VALUES(73,13,'73:13','Và loại thức ăn làm nghẹn họng và một sự Trừng phạt đau đớn.');
INSERT INTO "hasan_abdulkarim" VALUES(73,14,'73:14','Vào Ngày mà trái đất và những quả núi bị chấn động dữ dội và những quả núi sẽ giống những đồi cát đổ tuôn xuống.');
INSERT INTO "hasan_abdulkarim" VALUES(73,15,'73:15','Quả thật, TA đã phái một Sứ Giá đến với các ngươi làm một nhân chứng đối với các ngươi giống như TA đã cử một Sứ Giả đến với Fir-''awn.');
INSERT INTO "hasan_abdulkarim" VALUES(73,16,'73:16','Nhưng Fir-''awn đã bất tuân Sứ Giả nên TA đã bắt phạt y hết sức gắt gao.');
INSERT INTO "hasan_abdulkarim" VALUES(73,17,'73:17','Thế các ngươi sẽ giữ mình ra sao để khỏi bị họa vào một Ngày mà các đứa trẻ sẽ bạc đầu nếu các ngươi không có đức tin.');
INSERT INTO "hasan_abdulkarim" VALUES(73,18,'73:18','(Ngày) bầu trời sẽ chẻ đôi. Lời Hứa của Ngài chắc chắn sẽ thể hiện.');
INSERT INTO "hasan_abdulkarim" VALUES(73,19,'73:19','Chắc chắn đây là một Lời cảnh cáo. Bởi thế, ai thích thì hãy tự chọn lấy con đường đi đến với Thượng Đế của y.');
INSERT INTO "hasan_abdulkarim" VALUES(73,20,'73:20','Quả thật Thượng Đế của Ngươi biết việc Ngươi đứng dâng lễ nguyện Salah gần hai phần ba đêm, hoặc phân nửa đêm, hoặc một phần ba đêm với một thành phần của những ai theo Ngươi. Nhưng Ngài đã ấn định (thời gian của) ban dêm và ban ngày. Ngài biết các ngươi không thể nhớ đúng (thời gian) nên Ngài đoái thương mà quay lại tha thứ cho các ngươi. Do đó, hãy đọc Qur''an càng nhiều càng tốt, phần nào mà các ngươi nhận thấy dễ cho các ngươi. Ngài biết trong các ngươi có một số đau yếu, một số khác đi tìm kiếm thiên lộc của Allah xa trên trái đất; và có một số khác đi chiến đấu cho Chính Nghĩa của Allah. Do đó, hãy đọc Qur''an phần nào mà các ngươi thấy dễ cho các ngươi. Và hãy chu đáo dâng lễ nguyện Salah và đóng Zakah và cho Allah mượn một món mượn tốt. Và điều thiện nào mà các ngươi gởi đi trước cho bản thân của các ngươi thì sẽ tìm thấy lại nơi Allah. Vâng, nó sẽ trở thành một phần thưởng tốt và lớn hơn. Và hãy cầu xin Allah tha thứ. Và quả thật, Allah Hằng Tha Thứ, Rất Mực Khoan Dung.');
INSERT INTO "hasan_abdulkarim" VALUES(74,1,'74:1','Hỡi người đắp chăn (Nabi Muhammad)!');
INSERT INTO "hasan_abdulkarim" VALUES(74,2,'74:2','Hãy đứng lên và cảnh báo (dân chúng)!');
INSERT INTO "hasan_abdulkarim" VALUES(74,3,'74:3','Và hãy vĩ đại hóa Thượng Đế (Allah) của Ngươi.');
INSERT INTO "hasan_abdulkarim" VALUES(74,4,'74:4','Và hãy giữ y-phục của Ngươi sạch sẽ!');
INSERT INTO "hasan_abdulkarim" VALUES(74,5,'74:5','Hãy tránh xa tượng thần;');
INSERT INTO "hasan_abdulkarim" VALUES(74,6,'74:6','Và khi làm ơn (cho người) chớ mong (người) trả ơn;');
INSERT INTO "hasan_abdulkarim" VALUES(74,7,'74:7','Và vì Thượng Đế của Ngươi hãy kiên nhẫn;');
INSERT INTO "hasan_abdulkarim" VALUES(74,8,'74:8','Bởi thế, khi Còi Hụ hụ lên (lần thứ hai);');
INSERT INTO "hasan_abdulkarim" VALUES(74,9,'74:9','Thì Ngày đó sẽ là Ngày vất vả (cực nhọc).');
INSERT INTO "hasan_abdulkarim" VALUES(74,10,'74:10','Không mấy thoải mái cho những kẻ không có đức tin;');
INSERT INTO "hasan_abdulkarim" VALUES(74,11,'74:11','Hãy để mặc TA với kẻ mà TA đã tạo ra cô đơn;');
INSERT INTO "hasan_abdulkarim" VALUES(74,12,'74:12','Và TA đã làm cho y có được của cải dồi dào (giàu có).');
INSERT INTO "hasan_abdulkarim" VALUES(74,13,'74:13','Và con cái đầy đàn bên cạnh.');
INSERT INTO "hasan_abdulkarim" VALUES(74,14,'74:14','Và suôn sẻ mọi điều;');
INSERT INTO "hasan_abdulkarim" VALUES(74,15,'74:15','Rồi y còn tham lam muốn TA ban thêm;');
INSERT INTO "hasan_abdulkarim" VALUES(74,16,'74:16','Nhất định không! Quả thật, y là kẻ chống đối các Dấu Hiệu của TA.');
INSERT INTO "hasan_abdulkarim" VALUES(74,17,'74:17','TA sẽ làm cho y khốn đốn như trèo núi dốc đứng.');
INSERT INTO "hasan_abdulkarim" VALUES(74,18,'74:18','Quả thật, y suy ngẫm và mưu đồ,');
INSERT INTO "hasan_abdulkarim" VALUES(74,19,'74:19','Bởi thế, y chỉ thiệt thân! Y mưu đồ làm sao!');
INSERT INTO "hasan_abdulkarim" VALUES(74,20,'74:20','Rồi y chỉ thiệt thân! Y mưu đồ làm sao!');
INSERT INTO "hasan_abdulkarim" VALUES(74,21,'74:21','Rồi y nhìn (ngẫm nghĩ);');
INSERT INTO "hasan_abdulkarim" VALUES(74,22,'74:22','Rồi cau mày và nhăn nhó;');
INSERT INTO "hasan_abdulkarim" VALUES(74,23,'74:23','Rồi quay lưng và tỏ vẻ ngạo mạn;');
INSERT INTO "hasan_abdulkarim" VALUES(74,24,'74:24','Thế rồi y bảo: “Điều này chẳng qua là trò ảo thuật có từ xưa.”');
INSERT INTO "hasan_abdulkarim" VALUES(74,25,'74:25','“Rõ thật (Qur''an) này chỉ là Lời nói của một người phàm.”');
INSERT INTO "hasan_abdulkarim" VALUES(74,26,'74:26','TA sẽ sớm quẳng y vào Lửa của hỏa ngục.');
INSERT INTO "hasan_abdulkarim" VALUES(74,27,'74:27','Và điều gì sẽ cho Ngươi biết Lửa của hỏa ngục là gì?');
INSERT INTO "hasan_abdulkarim" VALUES(74,28,'74:28','Nó không dung tha cũng không bỏ sót (một tên sai quấy và tội lỗi nào).');
INSERT INTO "hasan_abdulkarim" VALUES(74,29,'74:29','Nó đốt cháy và lột hết các lớp da.');
INSERT INTO "hasan_abdulkarim" VALUES(74,30,'74:30','Trên nó có mười chín (vị Thiên Thần giữ ngục).');
INSERT INTO "hasan_abdulkarim" VALUES(74,31,'74:31','Và TA đã không chỉ định ai ngoài Thiên Thần làm những người canh gác hỏa ngục. Và TA ấn định con số ( của chúng như một sự thử thách cho những ai không có đức tin và để thuyết phục những ai đã được ban cấp Kinh Sách và cũng để tăng cường đức tin cho những người tin tưởng; và để cho những người được ban cho Kinh Sách cũng như những người tín tưởng không còn có gì để ngờ vực cả; và để cho những kẻ mang trong lòng một chứng bệnh và những kẻ không có đức tin có dịp nói: “Allah định nói gì qua thí dụ so sánh này.” Allah đánh lạc hướng kẻ nào Ngài muốn và hướng dẫn người nào Ngài muốn như thế; và không ai biết rõ lực lượng của Thượng Đế của Ngươi ngoại trừ Ngài. Và Nó là Lời Nhắc Nhở cho người phàm.');
INSERT INTO "hasan_abdulkarim" VALUES(74,32,'74:32','Không, thề bởi mặt trăng;');
INSERT INTO "hasan_abdulkarim" VALUES(74,33,'74:33','Và bởi đêm tối khi nó rút đi,');
INSERT INTO "hasan_abdulkarim" VALUES(74,34,'74:34','Và bởi hừng đông khi nó chói rạng;');
INSERT INTO "hasan_abdulkarim" VALUES(74,35,'74:35','Quả thật, Nó là một đại họa;');
INSERT INTO "hasan_abdulkarim" VALUES(74,36,'74:36','Một sự cảnh báo cho người phàm;');
INSERT INTO "hasan_abdulkarim" VALUES(74,37,'74:37','Cho ai trong các người muốn tiên phong đi trước hoặc muốn ở lại sau.');
INSERT INTO "hasan_abdulkarim" VALUES(74,38,'74:38','Mỗi người (linh hồn) sẽ là một cam kết cho những điều mà nó đã làm.');
INSERT INTO "hasan_abdulkarim" VALUES(74,39,'74:39','Ngoại trừ những người Bạn của phía Tay Phải');
INSERT INTO "hasan_abdulkarim" VALUES(74,40,'74:40','Trong thiên đàng. Họ sẽ hỏi nhau;');
INSERT INTO "hasan_abdulkarim" VALUES(74,41,'74:41','Về những kẻ tội lỗi.');
INSERT INTO "hasan_abdulkarim" VALUES(74,42,'74:42','“Điều gì đã đưa các anh vào Lửa của hỏa ngục vậy?”');
INSERT INTO "hasan_abdulkarim" VALUES(74,43,'74:43','Chúng sẽ đáp: “Chúng tôi là những ngươi không năng dâng lễ nguyện Salah."');
INSERT INTO "hasan_abdulkarim" VALUES(74,44,'74:44','"Và chúng tôi là những người thường không nuôi ăn người nghèo."');
INSERT INTO "hasan_abdulkarim" VALUES(74,45,'74:45','"Và chúng tôi thường nói chuyện vô bổ với những người hay chuyện vãn tầm phào."');
INSERT INTO "hasan_abdulkarim" VALUES(74,46,'74:46','"Và chúng tôi thường phủ nhận Ngày Phán xử."');
INSERT INTO "hasan_abdulkarim" VALUES(74,47,'74:47','"Cho tới khi chúng tôi gặp điều kiên định (cái chết)”.');
INSERT INTO "hasan_abdulkarim" VALUES(74,48,'74:48','Bởi thế, không một sự can thiệp nào của những người can thiệp có thể giúp ích chúng được.');
INSERT INTO "hasan_abdulkarim" VALUES(74,49,'74:49','Bởi thế, lý do gì đã khiến chúng quay lánh xa Lời cảnh báo?');
INSERT INTO "hasan_abdulkarim" VALUES(74,50,'74:50','Chúng giống như con lừa khiếp đảm;');
INSERT INTO "hasan_abdulkarim" VALUES(74,51,'74:51','Tháo chạy trước con sư tử.');
INSERT INTO "hasan_abdulkarim" VALUES(74,52,'74:52','Không, mỗi người trong bọn chúng muốn được trao cho những tờ kinh được trải ra');
INSERT INTO "hasan_abdulkarim" VALUES(74,53,'74:53','Vô phương! Không, chúng không sợ đời sau.');
INSERT INTO "hasan_abdulkarim" VALUES(74,54,'74:54','Vô phương! Quả thật Nó (Qur''an) là Lời cảnh báo;');
INSERT INTO "hasan_abdulkarim" VALUES(74,55,'74:55','Bởi thế, người nào muốn thì hãy (đọc và) cảnh giác;');
INSERT INTO "hasan_abdulkarim" VALUES(74,56,'74:56','Nhưng không ai cảnh giác đặng trừ phi Allah muốn bởi vì Ngài là Chủ Nhân đáng phải sợ và là Chủ Nhân ban sự tha thứ.');
INSERT INTO "hasan_abdulkarim" VALUES(75,1,'75:1','TA thề bởi Ngày phục sinh;');
INSERT INTO "hasan_abdulkarim" VALUES(75,2,'75:2','Và TA thề bởi linh hồn tự trách.');
INSERT INTO "hasan_abdulkarim" VALUES(75,3,'75:3','Há con người nghĩ rằng TA sẽ không tập hợp xương cốt của y hay sao?');
INSERT INTO "hasan_abdulkarim" VALUES(75,4,'75:4','Vâng, TA thừa sức ráp lại toàn hảo từng đầu ngón tay của y.');
INSERT INTO "hasan_abdulkarim" VALUES(75,5,'75:5','Không! Con người muốn tiếp tục phạm tội;');
INSERT INTO "hasan_abdulkarim" VALUES(75,6,'75:6','Y hỏi: “Lúc nào sẽ xảy ra Ngày phục sinh?”');
INSERT INTO "hasan_abdulkarim" VALUES(75,7,'75:7','Bởi thế, khi cặp mắt bị chói lòa;');
INSERT INTO "hasan_abdulkarim" VALUES(75,8,'75:8','Và mặt trăng bị che khuất,');
INSERT INTO "hasan_abdulkarim" VALUES(75,9,'75:9','Và mặt trời và mặt trăng giao nhau.');
INSERT INTO "hasan_abdulkarim" VALUES(75,10,'75:10','Vào Ngày đó con người sẽ bảo: “Đâu là chỗ để thoát thân?”');
INSERT INTO "hasan_abdulkarim" VALUES(75,11,'75:11','Vô phương! Không có chỗ để nương thân.');
INSERT INTO "hasan_abdulkarim" VALUES(75,12,'75:12','Ngày đó, chỗ nương thân duy nhất là (chạy) về với Thượng Đế của Ngươi.');
INSERT INTO "hasan_abdulkarim" VALUES(75,13,'75:13','Vào Ngàỵ đó, con người sẽ được báo cho biết điều mà y đã gởi đi trước và điều mà y còn để lại sau.');
INSERT INTO "hasan_abdulkarim" VALUES(75,14,'75:14','Không! Con người nhìn thấy rõ bản thân của mình nhất;');
INSERT INTO "hasan_abdulkarim" VALUES(75,15,'75:15','Mặc dầu y viện đủ lý do để chạy tội.');
INSERT INTO "hasan_abdulkarim" VALUES(75,16,'75:16','Chớ uốn lưỡi của Ngươi về Nó (Qur''an) hầu giục Nó đến nhanh;');
INSERT INTO "hasan_abdulkarim" VALUES(75,17,'75:17','Quả thật, TA có nhiệm vụ thu nhặt và đọc Nó (cho Ngươi);');
INSERT INTO "hasan_abdulkarim" VALUES(75,18,'75:18','Bởi thế, khi TA đọc Nó (Qur''an), hãy đọc theo Nó.');
INSERT INTO "hasan_abdulkarim" VALUES(75,19,'75:19','Rồi chính TA có nhiệm vụ giải thích Nó minh bạch.');
INSERT INTO "hasan_abdulkarim" VALUES(75,20,'75:20','Không! Không, các ngươi yêu đời sống hiện tại;');
INSERT INTO "hasan_abdulkarim" VALUES(75,21,'75:21','Và quên lãng đời sau.');
INSERT INTO "hasan_abdulkarim" VALUES(75,22,'75:22','Vào Ngày đó, gương mặt của một số người sáng rỡ.');
INSERT INTO "hasan_abdulkarim" VALUES(75,23,'75:23','Ngắm nhìn Thượng Đế của họ;');
INSERT INTO "hasan_abdulkarim" VALUES(75,24,'75:24','Và vào Ngày đó, gương mặt của một số người buồn hiu, nhăn nhó.');
INSERT INTO "hasan_abdulkarim" VALUES(75,25,'75:25','Lo nghĩ về một tai họa đập nát lưng sẽ giáng lên họ;');
INSERT INTO "hasan_abdulkarim" VALUES(75,26,'75:26','Không! Khi (hồn của người chết) lên đến xương quai xanh;');
INSERT INTO "hasan_abdulkarim" VALUES(75,27,'75:27','Và có tiếng hỏi: “Ai là thầy thuốc (có thể cứu y sống)?”');
INSERT INTO "hasan_abdulkarim" VALUES(75,28,'75:28','Và y nghĩ (đã đến lúc) phải biệt ly;');
INSERT INTO "hasan_abdulkarim" VALUES(75,29,'75:29','Và ống chân này nhập vào ống chân nọ;');
INSERT INTO "hasan_abdulkarim" VALUES(75,30,'75:30','Đưa (y) về với Thượng Đế của Ngươi vào Ngày đó.');
INSERT INTO "hasan_abdulkarim" VALUES(75,31,'75:31','Bởi thế, y không tin cũng không dâng lễ nguyện Salah.');
INSERT INTO "hasan_abdulkarim" VALUES(75,32,'75:32','Ngược lại, phủ nhận sự thật và quay bỏ đi;');
INSERT INTO "hasan_abdulkarim" VALUES(75,33,'75:33','Rồi y về nhà với dáng điệu ngạo mạn;');
INSERT INTO "hasan_abdulkarim" VALUES(75,34,'75:34','Chỉ thiệt thân ngươi thôi (hỡi người!), chỉ thiệt thân!');
INSERT INTO "hasan_abdulkarim" VALUES(75,35,'75:35','Rồi, chỉ thiệt thân ngươi thôi (hỡi người!), chỉ thiệt thân.');
INSERT INTO "hasan_abdulkarim" VALUES(75,36,'75:36','Há con người nghĩ rằng y được tự do tác oai tác quái?');
INSERT INTO "hasan_abdulkarim" VALUES(75,37,'75:37','Há y không là một giọt tinh dịch được xuất ra hay sao?');
INSERT INTO "hasan_abdulkarim" VALUES(75,38,'75:38','Rồi trở thành một hòn máu đặc, tiếp đó thành hình thể;');
INSERT INTO "hasan_abdulkarim" VALUES(75,39,'75:39','Rồi từ y, (Allah) làm ra hai giới (tính), nam và nữ.');
INSERT INTO "hasan_abdulkarim" VALUES(75,40,'75:40','Há Đấng đó không đủ Quyền Năng phục sinh được người chết hay sao?');
INSERT INTO "hasan_abdulkarim" VALUES(76,1,'76:1','Phải chăng con người đã trải qua một thời kỳ mà y không là một cái gì đáng nói đến hay sao?');
INSERT INTO "hasan_abdulkarim" VALUES(76,2,'76:2','TA đã tạo hóa con người từ một dung dịch để TA có thể thử thách y. Bởi thế, TA đã làm cho y nghe được và thấy được');
INSERT INTO "hasan_abdulkarim" VALUES(76,3,'76:3','TA đã chỉ dẫn cho y con đường xem y biết ơn hay bội ơn.');
INSERT INTO "hasan_abdulkarim" VALUES(76,4,'76:4','TA đã chuẩn bị cho những kẻ bội ơn sợi dây xích, gông cùm và lửa ngọn.');
INSERT INTO "hasan_abdulkarim" VALUES(76,5,'76:5','Người đức hạnh sẽ uống từ một tách nước chứa dung hợp thơm tho.');
INSERT INTO "hasan_abdulkarim" VALUES(76,6,'76:6','(Múc từ) một ngọn suối mà những người bề tôi của Allah sẽ uống, vì thế nó sẽ phun ra dồi dào.');
INSERT INTO "hasan_abdulkarim" VALUES(76,7,'76:7','Họ đã hoàn tất lời thề và lo sợ Ngày mà sự xấu xa sẽ lan tràn khắp nơi.');
INSERT INTO "hasan_abdulkarim" VALUES(76,8,'76:8','Và vì thương yêu Ngài (Allah), họ đã chu cấp thực phẩm cho người nghèo, trẻ mồ côi, và tù binh.');
INSERT INTO "hasan_abdulkarim" VALUES(76,9,'76:9','(Và bảo:) “Chúng tôi chu cấp quí vị là vì sắc Diện (Hài Lòng) của Allah thôi. Chúng tôi không mong quí vị tưởng thưởng hoặc đền ơn."');
INSERT INTO "hasan_abdulkarim" VALUES(76,10,'76:10','“Chúng tôi sợ một Ngày cau mặt nhăn nhó và buồn thảm từ Thượng Đế của chúng tôi.”');
INSERT INTO "hasan_abdulkarim" VALUES(76,11,'76:11','Bởi thế, Allah sẽ giải cứu họ khỏi sự xấu xa của Ngày đó và làm cho họ sáng rỡ và vui sướng.');
INSERT INTO "hasan_abdulkarim" VALUES(76,12,'76:12','Và vì đã kiên nhẫn chịu đựng nên Ngài sẽ ban thưởng họ thiên đàng và lụa là,');
INSERT INTO "hasan_abdulkarim" VALUES(76,13,'76:13','Nằm nghỉ trên những chiếc tràng kỷ trong đó (thiên đàng), không cảm thấy (sức nóng bức của) mặt trời và cảnh giá lạnh.');
INSERT INTO "hasan_abdulkarim" VALUES(76,14,'76:14','Và bóng mát của nó (thiên đàng) che họ sát bên trên và trái cây từng chùm của nó lủng lẳng sa xuống gần tầm tay hái;');
INSERT INTO "hasan_abdulkarim" VALUES(76,15,'76:15','Và những bình chứa bằng bạc và những chiếc ly cao chân bằng thủy tinh sẽ được chuyền đi quanh giữa họ.');
INSERT INTO "hasan_abdulkarim" VALUES(76,16,'76:16','(Bình chứa) bằng bạc trong như pha lê; họ sẽ định mức lượng của chúng.');
INSERT INTO "hasan_abdulkarim" VALUES(76,17,'76:17','Và trong đó, họ sẽ được cho uống từ một cái cốc chứa dung hợp gừng,');
INSERT INTO "hasan_abdulkarim" VALUES(76,18,'76:18','Một ngọn suối trong đó được gọi là Salsabil.');
INSERT INTO "hasan_abdulkarim" VALUES(76,19,'76:19','Và có những thiếu niên vĩnh viễn tươi trẻ đi vòng quanh họ (để hầu hạ). Khi thấy chúng, Ngươi ngỡ đó là những hạt trân châu rải rác.');
INSERT INTO "hasan_abdulkarim" VALUES(76,20,'76:20','Và khi nhìn, Ngươi sẽ thấy nơi đó niềm hạnh phúc và một vương quốc vĩ đại.');
INSERT INTO "hasan_abdulkarim" VALUES(76,21,'76:21','Họ sẽ mặc áo lụa mịn màu xanh, có thêu trổ xinh đẹp. Họ sẽ đeo vòng tay bằng bạc. Và Thượng Đế của họ sẽ cho họ uống một loại nước tinh khiết.');
INSERT INTO "hasan_abdulkarim" VALUES(76,22,'76:22','Quả thật, đây là quà tặng ban cho các ngươi như một phần thưởng; và sự cố gắng của các ngươi đã được chấp nhận.');
INSERT INTO "hasan_abdulkarim" VALUES(76,23,'76:23','TA là Đấng đã ban Qur''an xuống cho Ngươi theo từng giai đoạn.');
INSERT INTO "hasan_abdulkarim" VALUES(76,24,'76:24','Do đó, hãy kiên nhẫn chịu đựng với Mệnh Lệnh của Thượng Đế của Ngươi và chớ nghe theo tên tội lỗi hay kẻ không tin nào của bọn chúng.');
INSERT INTO "hasan_abdulkarim" VALUES(76,25,'76:25','Và hãy tụng niệm Tên của Thượng Đế của Ngươi vào buổi sáng và buổi tối.');
INSERT INTO "hasan_abdulkarim" VALUES(76,26,'76:26','Và hãy phủ phục trước Ngài ban đêm và tán dương Ngài thâu đêm.');
INSERT INTO "hasan_abdulkarim" VALUES(76,27,'76:27','Quả thật, những người (Quraish) này yêu đời sống hiện tại và bỏ quên sau lưng chúng một Ngày sẽ rất cực nhọc.');
INSERT INTO "hasan_abdulkarim" VALUES(76,28,'76:28','TA đã tạo hóa chúng và làm cho cơ thể của chúng cường tráng nhưng khi muốn, TA có thể đưa những người tương tự đến thay thế chúng toàn bộ.');
INSERT INTO "hasan_abdulkarim" VALUES(76,29,'76:29','Quả thật, Qur''an này là một Lời Nhắc Nhở. Bởi thế, để cho người nào muốn tự chọn lấy con đường đi đến với Thượng Đế của y.');
INSERT INTO "hasan_abdulkarim" VALUES(76,30,'76:30','Nhưng các ngươi không muốn đặng trừ phi Allah muốn (hướng dẫn). Quả thật, Allah Toàn Tri và Rất Mực Cao Minh.');
INSERT INTO "hasan_abdulkarim" VALUES(76,31,'76:31','Ngài (Allah) sẽ khoan dung người nào Ngài muốn. Và Ngài đã chuẩn bị cho những kẻ làm điều sai quấy một sự trừng phạt đau đớn');
INSERT INTO "hasan_abdulkarim" VALUES(77,1,'77:1','Thề bởi (các luồng gió) được phái đi, luồng này tiếp theo luồng kia;');
INSERT INTO "hasan_abdulkarim" VALUES(77,2,'77:2','Bởi thế, chúng thổi mạnh theo luồng,');
INSERT INTO "hasan_abdulkarim" VALUES(77,3,'77:3','Và chúng rải tung (mọi vật) đi khắp nơi;');
INSERT INTO "hasan_abdulkarim" VALUES(77,4,'77:4','Và tách biệt (cái tốt) khỏi (cái xấu);');
INSERT INTO "hasan_abdulkarim" VALUES(77,5,'77:5','Thề bởi (các vị Sứ) phổ biến Lời Nhắc Nhở đi rộng khắp.');
INSERT INTO "hasan_abdulkarim" VALUES(77,6,'77:6','Hoặc để chứng minh hoặc để cảnh báo;');
INSERT INTO "hasan_abdulkarim" VALUES(77,7,'77:7','Quả thật, điều mà các ngươi đã được hứa chắc chắn sẽ xảy ra.');
INSERT INTO "hasan_abdulkarim" VALUES(77,8,'77:8','Bởi thế, khi những vì sao lu mờ dần;');
INSERT INTO "hasan_abdulkarim" VALUES(77,9,'77:9','Và khi bầu trời bị chẻ làm hai;');
INSERT INTO "hasan_abdulkarim" VALUES(77,10,'77:10','Và khi những quả núi bị thổi rắc đi như bụi;');
INSERT INTO "hasan_abdulkarim" VALUES(77,11,'77:11','Và khi các Sứ Giả được gọi tập hợp vào một thời điểm;');
INSERT INTO "hasan_abdulkarim" VALUES(77,12,'77:12','Vào Ngày nào (những dấu hiệu đó) được dời lại?');
INSERT INTO "hasan_abdulkarim" VALUES(77,13,'77:13','Vào Ngày Phân loại (giữa những có đức tin và những người không có đức tin).');
INSERT INTO "hasan_abdulkarim" VALUES(77,14,'77:14','Và điều gì giải thích cho Ngươi biết Ngày Phân loại là gì?');
INSERT INTO "hasan_abdulkarim" VALUES(77,15,'77:15','Thống khổ thay Ngày đó cho những kẻ phủ nhận sự thật!');
INSERT INTO "hasan_abdulkarim" VALUES(77,16,'77:16','Há TA đã không tiêu diệt những người xưa (làm điều tội lỗi và sai quấy) hay sao?');
INSERT INTO "hasan_abdulkarim" VALUES(77,17,'77:17','Rồi, TA làm cho những thế hệ sau này theo chúng.');
INSERT INTO "hasan_abdulkarim" VALUES(77,18,'77:18','TA đối xử với những kẻ tội lỗi đúng như thế.');
INSERT INTO "hasan_abdulkarim" VALUES(77,19,'77:19','Thống khổ thay Ngày đó cho những kẻ phủ nhận sự thật!');
INSERT INTO "hasan_abdulkarim" VALUES(77,20,'77:20','Há TA đã không tạo các ngươi từ một chất lỏng đáng khinh ư?');
INSERT INTO "hasan_abdulkarim" VALUES(77,21,'77:21','Mà TA đã đặt an toàn tại một nơi chứa cố định.');
INSERT INTO "hasan_abdulkarim" VALUES(77,22,'77:22','Đến một kỳ hạn ấn định.');
INSERT INTO "hasan_abdulkarim" VALUES(77,23,'77:23','Do đó, TA quyết định bởi vì TA là Đấng Quyết Định Siêu Việt.');
INSERT INTO "hasan_abdulkarim" VALUES(77,24,'77:24','Thống khổ thay Ngày đó cho những kẻ phủ nhận sự thật!');
INSERT INTO "hasan_abdulkarim" VALUES(77,25,'77:25','Há TA đã không làm trái đất thành một nơi đón nhận');
INSERT INTO "hasan_abdulkarim" VALUES(77,26,'77:26','Người sống và kẻ chết?');
INSERT INTO "hasan_abdulkarim" VALUES(77,27,'77:27','Và đã đặt nơi đó những quả núi cao sừng sững vững chắc và cung cấp cho các ngươi nước uống mát rượi hay sao?');
INSERT INTO "hasan_abdulkarim" VALUES(77,28,'77:28','Thống khổ thay Ngày đó cho những kẻ phủ nhận sự thật!');
INSERT INTO "hasan_abdulkarim" VALUES(77,29,'77:29','“Hãy đi đến cái (hỏa ngục) mà các ngươi đã từng phủ nhận.”');
INSERT INTO "hasan_abdulkarim" VALUES(77,30,'77:30','“Hãy đi đến chỗ bóng tối của ba cột trụ (khói đen bay lên),');
INSERT INTO "hasan_abdulkarim" VALUES(77,31,'77:31','“Không che mát cũng không che chở các ngươi khỏi Lửa ngọn.”');
INSERT INTO "hasan_abdulkarim" VALUES(77,32,'77:32','Quả thật, Nó hà ra những ngọn lửa to như những pháo đài.');
INSERT INTO "hasan_abdulkarim" VALUES(77,33,'77:33','Giống những con lạc đà màu vàng.');
INSERT INTO "hasan_abdulkarim" VALUES(77,34,'77:34','Thống khổ thay Ngày đó cho những kẻ phủ nhận sự thật!');
INSERT INTO "hasan_abdulkarim" VALUES(77,35,'77:35','Đây là Ngày mà chúng sẽ không nói được,');
INSERT INTO "hasan_abdulkarim" VALUES(77,36,'77:36','Cũng sẽ không được khiếu nại điều gì.');
INSERT INTO "hasan_abdulkarim" VALUES(77,37,'77:37','Thống khổ thay Ngày đó cho những kẻ phủ nhận sự thật!');
INSERT INTO "hasan_abdulkarim" VALUES(77,38,'77:38','Đây là Ngày Phân loại. TA sẽ tập trung các ngươi và những người xưa.');
INSERT INTO "hasan_abdulkarim" VALUES(77,39,'77:39','Nếu các ngươi có mưu định gì thì hãy dùng nó mà phá hỏng TA thử xem!');
INSERT INTO "hasan_abdulkarim" VALUES(77,40,'77:40','Thống khổ thay Ngày đó cho những kẻ phủ nhận sự thật!');
INSERT INTO "hasan_abdulkarim" VALUES(77,41,'77:41','Quả thật, những người sợ Allah sẽ ở dưới bóng mát và gần suối nước trong;');
INSERT INTO "hasan_abdulkarim" VALUES(77,42,'77:42','(Hưởng) loại trái cây nào mà họ thích.');
INSERT INTO "hasan_abdulkarim" VALUES(77,43,'77:43','“Hãy ăn và uống cho mãn nguyện tấm lòng do những việc (thiện) ma quí vị đã từng làm.”');
INSERT INTO "hasan_abdulkarim" VALUES(77,44,'77:44','Chắc chắn, TA sẽ trọng thưởng những người làm tốt đúng như thế.');
INSERT INTO "hasan_abdulkarim" VALUES(77,45,'77:45','Thống khổ thay Ngày đó cho những kẻ phủ nhận sự thật!');
INSERT INTO "hasan_abdulkarim" VALUES(77,46,'77:46','(Hỡi những kẻ không tin!) Cứ ăn uống và hưởng thụ cuộc đời ngắn ngủi này. Chắc chắn, các ngươi là những kẻ tội lỗi.');
INSERT INTO "hasan_abdulkarim" VALUES(77,47,'77:47','Thống khổ thay Ngày đó cho những kẻ phủ nhận sự thật!');
INSERT INTO "hasan_abdulkarim" VALUES(77,48,'77:48','Và khi có lời bảo chúng: “Hãy cúi đầu, chúng không cúi đầu.”');
INSERT INTO "hasan_abdulkarim" VALUES(77,49,'77:49','Thống khổ thay Ngày đó cho những kẻ phủ nhận sự thật!');
INSERT INTO "hasan_abdulkarim" VALUES(77,50,'77:50','Thế, chúng sẽ tin tưởng vào Lời trình bày nào sau Nó (Qur''an)?');
INSERT INTO "hasan_abdulkarim" VALUES(78,1,'78:1','Chúng đang hỏi nhau điều gì vậy?');
INSERT INTO "hasan_abdulkarim" VALUES(78,2,'78:2','Về các nguồn tin vĩ đại,');
INSERT INTO "hasan_abdulkarim" VALUES(78,3,'78:3','Điều mà chúng không đồng ý với nhau;');
INSERT INTO "hasan_abdulkarim" VALUES(78,4,'78:4','Không, chúng sẽ sớm biết!');
INSERT INTO "hasan_abdulkarim" VALUES(78,5,'78:5','Rồi, nhất định không! Chúng sẽ sớm biết!');
INSERT INTO "hasan_abdulkarim" VALUES(78,6,'78:6','Há TA đã không làm trái đất như một chiếc giường,');
INSERT INTO "hasan_abdulkarim" VALUES(78,7,'78:7','Và những quả núi như những cột trụ?');
INSERT INTO "hasan_abdulkarim" VALUES(78,8,'78:8','Và TA đã tạo các ngươi thành cặp đôi.');
INSERT INTO "hasan_abdulkarim" VALUES(78,9,'78:9','Và làm giấc ngủ của các ngươi thành một sự nghỉ ngơi.');
INSERT INTO "hasan_abdulkarim" VALUES(78,10,'78:10','Và làm màn đêm thành một tấm phủ,');
INSERT INTO "hasan_abdulkarim" VALUES(78,11,'78:11','Và làm ban ngày để tìm kế sinh nhai.');
INSERT INTO "hasan_abdulkarim" VALUES(78,12,'78:12','Và TA đã dựng bên trên các ngươi bảy cái (tầng trời) vững chắc,');
INSERT INTO "hasan_abdulkarim" VALUES(78,13,'78:13','Và làm một chiếc đèn (mặt trời) chói sáng.');
INSERT INTO "hasan_abdulkarim" VALUES(78,14,'78:14','Và ban nước xuống dồi dào từ các đám mây chứa nước mưa.');
INSERT INTO "hasan_abdulkarim" VALUES(78,15,'78:15','Mà TA dùng để làm mọc ra trái hạt và cây cối.');
INSERT INTO "hasan_abdulkarim" VALUES(78,16,'78:16','Và vườn tược cây lá xum xuê.');
INSERT INTO "hasan_abdulkarim" VALUES(78,17,'78:17','Quả thật, Ngày Quyết Định là một cuộc hẹn ấn định,');
INSERT INTO "hasan_abdulkarim" VALUES(78,18,'78:18','Ngày mà Còi Hụ sẽ hụ vang rền và các ngươi sẽ đến từng đoàn;');
INSERT INTO "hasan_abdulkarim" VALUES(78,19,'78:19','Và bầu trời sẽ được mở toang như cửa mở,');
INSERT INTO "hasan_abdulkarim" VALUES(78,20,'78:20','Và những quả núi sẽ bị dời đi như ảo ảnh.');
INSERT INTO "hasan_abdulkarim" VALUES(78,21,'78:21','Quả thật, hỏa ngục là một chỗ mai phục,');
INSERT INTO "hasan_abdulkarim" VALUES(78,22,'78:22','Một chỗ ngụ cho những kẻ vượt quá mức tội lỗi,');
INSERT INTO "hasan_abdulkarim" VALUES(78,23,'78:23','Chúng sẽ ở trong đó đời đời,');
INSERT INTO "hasan_abdulkarim" VALUES(78,24,'78:24','Trong đó, không có gì mát để nếm cũng chẳng có gì để uống.');
INSERT INTO "hasan_abdulkarim" VALUES(78,25,'78:25','Ngoại trừ một loại nước cực sôi và chất mủ hôi tanh.');
INSERT INTO "hasan_abdulkarim" VALUES(78,26,'78:26','Một phần đền xứng đáng.');
INSERT INTO "hasan_abdulkarim" VALUES(78,27,'78:27','Quả thật, chúng không màng đến việc phán xử.');
INSERT INTO "hasan_abdulkarim" VALUES(78,28,'78:28','Và chúng triệt để phủ nhận các Lời Mặc Khải của TA.');
INSERT INTO "hasan_abdulkarim" VALUES(78,29,'78:29','Và TA đã cho ghi mọi điều vào một quyển sổ.');
INSERT INTO "hasan_abdulkarim" VALUES(78,30,'78:30','Bởi thế, hãy nếm (Lửa); do đó, TA sẽ không thêm gì cả cho các ngươi ngoài hình phạt.');
INSERT INTO "hasan_abdulkarim" VALUES(78,31,'78:31','Quả thật những người ngay chính sợ Allah sẽ thành đạt;');
INSERT INTO "hasan_abdulkarim" VALUES(78,32,'78:32','Những ngôi vườn tươi tốt và những vườn nho;');
INSERT INTO "hasan_abdulkarim" VALUES(78,33,'78:33','Những trinh nữ với bộ ngực căng tròn cùng lứa tuổi;');
INSERT INTO "hasan_abdulkarim" VALUES(78,34,'78:34','Và một cốc đầy (rượu).');
INSERT INTO "hasan_abdulkarim" VALUES(78,35,'78:35','Không nghe một lời sàm sỡ và gian dối nào trong đó;');
INSERT INTO "hasan_abdulkarim" VALUES(78,36,'78:36','Một phần thưởng từ Thượng Đế của Ngươi, một món quà đã được tính toán');
INSERT INTO "hasan_abdulkarim" VALUES(78,37,'78:37','(Bởi) Thượng Đế của các tầng trời và trái đất và của mọi vật giữa hai (trời đất), Đấng Rất Mực Độ Lượng mà không ai được quyền đôi co.');
INSERT INTO "hasan_abdulkarim" VALUES(78,38,'78:38','Ngày mà Thiên Thần libril và các Thiên Thần đứng thành hàng. Không ai được nói ngoại trừ người nào mà Đấng Rất Mực Độ Lượng cho phép; và y sẽ nói điều nào đúng đắn.');
INSERT INTO "hasan_abdulkarim" VALUES(78,39,'78:39','Đó là Ngày của sự Thật. Bởi thế, người nào muốn, hãy chọn lấy một chỗ ngụ với Thượng Đế của y.');
INSERT INTO "hasan_abdulkarim" VALUES(78,40,'78:40','Quả thật, TA đã cảnh cáo các ngươi về một sự trừng phạt gần kề, Ngày mà con người sẽ thấy cái mà bàn tay của y đã gởi đi trước và người vô đức tin sẽ than: “Thiệt thân tôi mất! Ước gì tôi là cát bụi!”');
INSERT INTO "hasan_abdulkarim" VALUES(79,1,'79:1','Thề bởi các vị (Thiên Thần) giật mạnh (hồn của kẻ sai quấy và tội lỗi);');
INSERT INTO "hasan_abdulkarim" VALUES(79,2,'79:2','Thề bởi các vị rút nhẹ (linh hồn của người ngoan đạo);');
INSERT INTO "hasan_abdulkarim" VALUES(79,3,'79:3','Thề bởi các vị lướt đi như bơi.');
INSERT INTO "hasan_abdulkarim" VALUES(79,4,'79:4','Rồi vội vã tiến nhanh đến trước.');
INSERT INTO "hasan_abdulkarim" VALUES(79,5,'79:5','Để thi hành công việc (đã được truyền).');
INSERT INTO "hasan_abdulkarim" VALUES(79,6,'79:6','Vào Ngày mà trận động đất làm chấn động dữ dội.');
INSERT INTO "hasan_abdulkarim" VALUES(79,7,'79:7','Tiếp theo là trận (động đất) liên hồi.');
INSERT INTO "hasan_abdulkarim" VALUES(79,8,'79:8','Ngày đó, quả tim sẽ đập mạnh;');
INSERT INTO "hasan_abdulkarim" VALUES(79,9,'79:9','Cặp mắt của chúng gục xuống.');
INSERT INTO "hasan_abdulkarim" VALUES(79,10,'79:10','Chúng bảo: “Há chúng ta sẽ trở lại tình trạng của thuở sinh tiền?');
INSERT INTO "hasan_abdulkarim" VALUES(79,11,'79:11','Ngay sau khi chúng ta đã trở thành xương khô rả mục rồi ư?”');
INSERT INTO "hasan_abdulkarim" VALUES(79,12,'79:12','Chúng bảo: “Lúc đó sẽ là một sự trở lại thua thiệt!”');
INSERT INTO "hasan_abdulkarim" VALUES(79,13,'79:13','Quả thật, nó (Lệnh tập trung) chỉ gồm một tiếng Thét kinh hoàng,');
INSERT INTO "hasan_abdulkarim" VALUES(79,14,'79:14','Thì lúc đó chúng sẽ nổi lên trên mặt (đất).');
INSERT INTO "hasan_abdulkarim" VALUES(79,15,'79:15','Há Ngươi đã nghe câu chuyện của Musa hay chưa?');
INSERT INTO "hasan_abdulkarim" VALUES(79,16,'79:16','Khi Thượng Đế của Y gọi Y nơi thung lũng Tuwa'' linh thiêng');
INSERT INTO "hasan_abdulkarim" VALUES(79,17,'79:17','Hãy đi gặp Fir-''awn; hắn đã thực sự vượt quá mức tội ác.');
INSERT INTO "hasan_abdulkarim" VALUES(79,18,'79:18','Hãy bảo y: “Há ngài muốn được tẩy sạch (tội lỗi) chăng?"');
INSERT INTO "hasan_abdulkarim" VALUES(79,19,'79:19','“Và tôi sẽ hướng dẫn ngài đến với Thượng Đế của ngài hầu có lòng sợ (Allah?)”');
INSERT INTO "hasan_abdulkarim" VALUES(79,20,'79:20','Bởi thế, (Musa) trưng cho y (Fir-''awn) thấy Phép Lạ lớn.');
INSERT INTO "hasan_abdulkarim" VALUES(79,21,'79:21','Nhưng hắn phủ nhận và bất tuân;');
INSERT INTO "hasan_abdulkarim" VALUES(79,22,'79:22','Rồi quay lưng bỏ đi, cố gắng tìm cách chống lại (Allah).');
INSERT INTO "hasan_abdulkarim" VALUES(79,23,'79:23','Bởi thế, hắn tập trung dân chúng và tuyên bố,');
INSERT INTO "hasan_abdulkarim" VALUES(79,24,'79:24','Rồi bảo: "Ta là Thượng Đế tối cao của các ngươi.”');
INSERT INTO "hasan_abdulkarim" VALUES(79,25,'79:25','Thế là Allah bắt phạt hắn, bằng hình phạt của đời sau và đời này.');
INSERT INTO "hasan_abdulkarim" VALUES(79,26,'79:26','Quả thật, trong đó là một bài học (cảnh báo) cho ai sợ (Allah).');
INSERT INTO "hasan_abdulkarim" VALUES(79,27,'79:27','Há các ngươi khó tạo hay bầu trời mà Ngài đã dựng lên khó hơn?');
INSERT INTO "hasan_abdulkarim" VALUES(79,28,'79:28','Ngài đã nhấc chiều cao của nó lên cao và sắp xếp nó đều đặn theo thứ tự.');
INSERT INTO "hasan_abdulkarim" VALUES(79,29,'79:29','Và làm ban đêm của nó tối mịt và đưa ban mai của nó ra sáng tỏ.');
INSERT INTO "hasan_abdulkarim" VALUES(79,30,'79:30','Và sau đó, Ngài trải rộng mặt đất;');
INSERT INTO "hasan_abdulkarim" VALUES(79,31,'79:31','Từ đó, Ngài làm cho nước của nó phun ra và (làm ra) đồng cỏ,');
INSERT INTO "hasan_abdulkarim" VALUES(79,32,'79:32','Và những quả núi mà Ngài dựng đứng vững chắc;');
INSERT INTO "hasan_abdulkarim" VALUES(79,33,'79:33','(Thành) một nguồn lương thực cho các ngươi và gia súc của các ngươi.');
INSERT INTO "hasan_abdulkarim" VALUES(79,34,'79:34','Bởi thế khi đại họa (của Ngày phục sinh) xảy đến,');
INSERT INTO "hasan_abdulkarim" VALUES(79,35,'79:35','Ngày mà con người hồi tưởng lại những điều y đã cố gắng;');
INSERT INTO "hasan_abdulkarim" VALUES(79,36,'79:36','hỏa ngục sẽ được phô bày cho người nào nhìn thấy,');
INSERT INTO "hasan_abdulkarim" VALUES(79,37,'79:37','Nhưng đối với ai vượt quá mức giới hạn và bất tuân (Allah),');
INSERT INTO "hasan_abdulkarim" VALUES(79,38,'79:38','Và thích đời sống trần tục này (hơn đời sau),');
INSERT INTO "hasan_abdulkarim" VALUES(79,39,'79:39','Thì quả thật, nhà ở của y sẽ là hỏa ngục;');
INSERT INTO "hasan_abdulkarim" VALUES(79,40,'79:40','Còn đối với ai sợ việc đứng trước mặt Thượng Đế của y và kiềm hãm mình tránh những dục vọng thấp hèn,');
INSERT INTO "hasan_abdulkarim" VALUES(79,41,'79:41','Thì quả thật, nhà ở của y sẽ là thiên đàng.');
INSERT INTO "hasan_abdulkarim" VALUES(79,42,'79:42','Chúng hỏi Ngươi về Giờ (phục sinh) chừng nào sẽ xảy ra?');
INSERT INTO "hasan_abdulkarim" VALUES(79,43,'79:43','Hẳn điều hiểu biết nào Ngươi có thể công bố cho biết?');
INSERT INTO "hasan_abdulkarim" VALUES(79,44,'79:44','Chỉ Thượng Đế của Ngươi biết thời hạn của nó.');
INSERT INTO "hasan_abdulkarim" VALUES(79,45,'79:45','Ngươi chỉ là một Người báo trước cho ai sợ nó.');
INSERT INTO "hasan_abdulkarim" VALUES(79,46,'79:46','Vào Ngày mà chúng thấy nó, chúng sẽ có cảm tưởng như đã ở lại trần gian chỉ một buổi chiều hay một buổi sáng gì đó!');
INSERT INTO "hasan_abdulkarim" VALUES(80,1,'80:1','Y (Nabi) cau mày và quay sang phía khác,');
INSERT INTO "hasan_abdulkarim" VALUES(80,2,'80:2','Vì một người mù đến gặp Y.');
INSERT INTO "hasan_abdulkarim" VALUES(80,3,'80:3','Và điều gì cho Ngươi biết, may ra y được rửa sạch tội?');
INSERT INTO "hasan_abdulkarim" VALUES(80,4,'80:4','Hoặc được nhắc nhở bởi vì sự nhắc nhở có lợi cho y?');
INSERT INTO "hasan_abdulkarim" VALUES(80,5,'80:5','Còn đối với kẻ tự cho mình đầy đủ giàu có,');
INSERT INTO "hasan_abdulkarim" VALUES(80,6,'80:6','Thì Ngươi lại ân cần với hắn;');
INSERT INTO "hasan_abdulkarim" VALUES(80,7,'80:7','Chuyện gì xẩy đến cho Ngươi nếu hắn không được tẩy sạch?');
INSERT INTO "hasan_abdulkarim" VALUES(80,8,'80:8','Còn ai chạy đến với Ngươi,');
INSERT INTO "hasan_abdulkarim" VALUES(80,9,'80:9','Vì cả sợ (Allah),');
INSERT INTO "hasan_abdulkarim" VALUES(80,10,'80:10','Thì Ngươi lại lơ là với y.');
INSERT INTO "hasan_abdulkarim" VALUES(80,11,'80:11','Không, quả thật Nó là Lời Cảnh Tỉnh.');
INSERT INTO "hasan_abdulkarim" VALUES(80,12,'80:12','Bởi thế, người nào muốn thì hãy lưu ý');
INSERT INTO "hasan_abdulkarim" VALUES(80,13,'80:13','(Các điều) trong các tờ Kinh rất cao quí,');
INSERT INTO "hasan_abdulkarim" VALUES(80,14,'80:14','Cao thâm, trong sạch,');
INSERT INTO "hasan_abdulkarim" VALUES(80,15,'80:15','Do bàn tay của những người biên chép');
INSERT INTO "hasan_abdulkarim" VALUES(80,16,'80:16','Vinh dự và đạo đức (ghi lại).');
INSERT INTO "hasan_abdulkarim" VALUES(80,17,'80:17','Con người thật đáng chết! Cái gì làm cho y phủ nhận Ngài (Allah)?');
INSERT INTO "hasan_abdulkarim" VALUES(80,18,'80:18','Ngài đã tạo hóa y từ cái gì?');
INSERT INTO "hasan_abdulkarim" VALUES(80,19,'80:19','Ngài tạo hóa y từ tinh dịch và định mạng (cho y);');
INSERT INTO "hasan_abdulkarim" VALUES(80,20,'80:20','Rồi làm con đường cho y được dễ dàng;');
INSERT INTO "hasan_abdulkarim" VALUES(80,21,'80:21','Rồi làm cho y chết; rồi chôn y xuống mộ;');
INSERT INTO "hasan_abdulkarim" VALUES(80,22,'80:22','Rồi khi muốn, Ngài sẽ dựng y sống lại.');
INSERT INTO "hasan_abdulkarim" VALUES(80,23,'80:23','Không, khi Ngài truyền lệnh, y không tuân hành.');
INSERT INTO "hasan_abdulkarim" VALUES(80,24,'80:24','Thế con người hãy quan sát thực phẩm của y:');
INSERT INTO "hasan_abdulkarim" VALUES(80,25,'80:25','Rằng TA xối nước (mưa) xuống dồi dào,');
INSERT INTO "hasan_abdulkarim" VALUES(80,26,'80:26','Rồi, TA chẻ đất nứt ra thành mảnh,');
INSERT INTO "hasan_abdulkarim" VALUES(80,27,'80:27','Bởi thế, TA làm ra trái giống mọc ra trong đó,');
INSERT INTO "hasan_abdulkarim" VALUES(80,28,'80:28','Và trái nho và rau cải xanh tươi,');
INSERT INTO "hasan_abdulkarim" VALUES(80,29,'80:29','Và trái ô liu và trái chà là,');
INSERT INTO "hasan_abdulkarim" VALUES(80,30,'80:30','Và vườn trái cây rậm rạp,');
INSERT INTO "hasan_abdulkarim" VALUES(80,31,'80:31','Và trái cây và cây cỏ,');
INSERT INTO "hasan_abdulkarim" VALUES(80,32,'80:32','Làm lương thực cho các ngươi và cho gia súc.');
INSERT INTO "hasan_abdulkarim" VALUES(80,33,'80:33','Nhưng khi tiếng Thét chát chúa xảy ra,');
INSERT INTO "hasan_abdulkarim" VALUES(80,34,'80:34','Ngày mà mỗi người sẽ chạy bỏ anh em của mình,');
INSERT INTO "hasan_abdulkarim" VALUES(80,35,'80:35','Và bỏ cha mẹ,');
INSERT INTO "hasan_abdulkarim" VALUES(80,36,'80:36','Và bỏ vợ, và con cái.');
INSERT INTO "hasan_abdulkarim" VALUES(80,37,'80:37','Ngày đó, mỗi người lo âu cho bản thân đủ làm y quên bẵng người khác.');
INSERT INTO "hasan_abdulkarim" VALUES(80,38,'80:38','Ngày đó, có những gương mặt (người tốt) sẽ sáng rỡ');
INSERT INTO "hasan_abdulkarim" VALUES(80,39,'80:39','Cười nói, thỏa thích với tin vui.');
INSERT INTO "hasan_abdulkarim" VALUES(80,40,'80:40','Ngày đó, có những gương mặt sầm tối');
INSERT INTO "hasan_abdulkarim" VALUES(80,41,'80:41','Dính toàn bụi bặm:');
INSERT INTO "hasan_abdulkarim" VALUES(80,42,'80:42','Đó là những kẻ phủ nhận và tội lỗi.');
INSERT INTO "hasan_abdulkarim" VALUES(81,1,'81:1','Khi mặt trời cuốn xếp lại và biến mất;');
INSERT INTO "hasan_abdulkarim" VALUES(81,2,'81:2','Và khi các vì sao rơi rụng;');
INSERT INTO "hasan_abdulkarim" VALUES(81,3,'81:3','Và khi các quả núi bị di chuyển mất biệt;');
INSERT INTO "hasan_abdulkarim" VALUES(81,4,'81:4','Và khi các con lạc đà cái mang thai bị bỏ bê;');
INSERT INTO "hasan_abdulkarim" VALUES(81,5,'81:5','Và khi các con dã thú sẽ được lùa tập trung lại;');
INSERT INTO "hasan_abdulkarim" VALUES(81,6,'81:6','Và khi các đại dương dâng trào;');
INSERT INTO "hasan_abdulkarim" VALUES(81,7,'81:7','Và khi các linh hồn sẽ được kết đôi với thân xác;');
INSERT INTO "hasan_abdulkarim" VALUES(81,8,'81:8','Và khi các đứa bé gái bị chôn sống sẽ được tra hỏi-');
INSERT INTO "hasan_abdulkarim" VALUES(81,9,'81:9','Vì tội gì mà bé đã bị giết?');
INSERT INTO "hasan_abdulkarim" VALUES(81,10,'81:10','Và khi các tờ giấy (ghi hành động) sẽ được mở ra;');
INSERT INTO "hasan_abdulkarim" VALUES(81,11,'81:11','Và khi bầu trời sẽ bị lột trần;');
INSERT INTO "hasan_abdulkarim" VALUES(81,12,'81:12','Và khi hỏa ngục sẽ được nhúm thành lửa ngọn;');
INSERT INTO "hasan_abdulkarim" VALUES(81,13,'81:13','Và khi thiên đàng sẽ được mang đến gần,');
INSERT INTO "hasan_abdulkarim" VALUES(81,14,'81:14','Mỗi người sẽ biết điều mà y trình bày.');
INSERT INTO "hasan_abdulkarim" VALUES(81,15,'81:15','Bởi thế, TA thề bởi các hành tinh lặn khuất,');
INSERT INTO "hasan_abdulkarim" VALUES(81,16,'81:16','Di chuyển nhanh biến mất,');
INSERT INTO "hasan_abdulkarim" VALUES(81,17,'81:17','Và bởi ban đêm khi nó ra đi;');
INSERT INTO "hasan_abdulkarim" VALUES(81,18,'81:18','Và bởi ban mai khi nó thổi (màn đêm đi mất) và sáng tỏ;');
INSERT INTO "hasan_abdulkarim" VALUES(81,19,'81:19','Quả thật, Nó (Qur''an) là Lời phán của (Allah do) một Vị Thiên sứ (Jibril) rất vinh dự (đã mang đến),');
INSERT INTO "hasan_abdulkarim" VALUES(81,20,'81:20','Được Chủ Nhân của Ngai Vương ban cho quyền lực và danh dự,');
INSERT INTO "hasan_abdulkarim" VALUES(81,21,'81:21','Được (các Thiên Thần) tuân theo nơi đó và rất đáng tin cậy.');
INSERT INTO "hasan_abdulkarim" VALUES(81,22,'81:22','Và người Bạn (Muhammad) của các ngươi không phải là một kẻ điên.');
INSERT INTO "hasan_abdulkarim" VALUES(81,23,'81:23','Và quả thật, Y đã thấy vị (Jibril) tại chân trời sáng tỏ.');
INSERT INTO "hasan_abdulkarim" VALUES(81,24,'81:24','Và Y không giữ cho riêng mình điều huyền bí (vô hình).');
INSERT INTO "hasan_abdulkarim" VALUES(81,25,'81:25','Và Nó (Qur''an) không là lời nói của Shaytan đáng tống xuất.');
INSERT INTO "hasan_abdulkarim" VALUES(81,26,'81:26','Thế các ngươi đi đâu?');
INSERT INTO "hasan_abdulkarim" VALUES(81,27,'81:27','Quả thật, Nó (Qur''an) chỉ là Lời Cảnh Tỉnh nhắc nhỡ muôn loài.');
INSERT INTO "hasan_abdulkarim" VALUES(81,28,'81:28','Nhắc nhở ai trong các ngươi muốn đi đúng đường,');
INSERT INTO "hasan_abdulkarim" VALUES(81,29,'81:29','Nhưng điều mà các ngươi muốn sẽ không thành tựu trừ phi Allah, Thượng Đế của vũ trụ và muôn loài muốn (hay chấp thuận).');
INSERT INTO "hasan_abdulkarim" VALUES(82,1,'82:1','Khi bầu trời bị chẻ đôi.');
INSERT INTO "hasan_abdulkarim" VALUES(82,2,'82:2','Và khi các vì sao rơi rụng tản mác;');
INSERT INTO "hasan_abdulkarim" VALUES(82,3,'82:3','Và khi các đại dương dâng trào;');
INSERT INTO "hasan_abdulkarim" VALUES(82,4,'82:4','Và khi các ngôi mộ bị quật ngược;');
INSERT INTO "hasan_abdulkarim" VALUES(82,5,'82:5','Con người sẽ biết điều mà y đã gởi đi trước và để lại sau;');
INSERT INTO "hasan_abdulkarim" VALUES(82,6,'82:6','Này hỡi người! Điều gì đã đánh lừa ngươi về Thượng Đế Rất Mực Quảng Đại của ngươi?');
INSERT INTO "hasan_abdulkarim" VALUES(82,7,'82:7','Đấng đã tạo hóa ngươi, ban hình thể, rồi làm cho ngươi quân bình;');
INSERT INTO "hasan_abdulkarim" VALUES(82,8,'82:8','Ngài sẽ cấu tạo ngươi theo hình thể nào mà Ngài muốn;');
INSERT INTO "hasan_abdulkarim" VALUES(82,9,'82:9','Nhưng không! Các ngươi phủ nhận việc Phán Xử.');
INSERT INTO "hasan_abdulkarim" VALUES(82,10,'82:10','Và quả thật, có các vị (Thiên Thần) theo trông chừng các ngươi,');
INSERT INTO "hasan_abdulkarim" VALUES(82,11,'82:11','Các vị biên chép đáng tôn kính,');
INSERT INTO "hasan_abdulkarim" VALUES(82,12,'82:12','Họ biết rõ mọi điều các ngươi làm.');
INSERT INTO "hasan_abdulkarim" VALUES(82,13,'82:13','Quả thật, người đức hạnh chắc chắn sẽ ở trong thiên đàng Hạnh phúc;');
INSERT INTO "hasan_abdulkarim" VALUES(82,14,'82:14','Và quả thật, kẻ tội lỗi và sai quấy chắc chắn sẽ ở trong hỏa ngục,');
INSERT INTO "hasan_abdulkarim" VALUES(82,15,'82:15','Nơi mà chúng sẽ sa xuống vào Ngày Phán Xử,');
INSERT INTO "hasan_abdulkarim" VALUES(82,16,'82:16','Và là nơi mà chúng sẽ không được vắng mặt.');
INSERT INTO "hasan_abdulkarim" VALUES(82,17,'82:17','Và điều gì cho ngươi biết Ngày Phán Xử là gì?');
INSERT INTO "hasan_abdulkarim" VALUES(82,18,'82:18','Rồi điều gì cho ngươi biết Ngày Phán Xử là gì?');
INSERT INTO "hasan_abdulkarim" VALUES(82,19,'82:19','Ngày mà không một người nào có thể làm được một điều gì để giúp người khác bởi vì mọi Mệnh Lệnh vào Ngày đó sẽ hoàn toàn thuộc về Allah.');
INSERT INTO "hasan_abdulkarim" VALUES(83,1,'83:1','Khốn khổ cho những kẻ tham lam bòn rút:');
INSERT INTO "hasan_abdulkarim" VALUES(83,2,'83:2','Những ai khi nhận của người thì đòi cho đủ,');
INSERT INTO "hasan_abdulkarim" VALUES(83,3,'83:3','Nhưng khi đo (cân) ra cho người thì đưa thiếu;');
INSERT INTO "hasan_abdulkarim" VALUES(83,4,'83:4','Há những người này không nghĩ rằng họ sẽ được phục sinh,');
INSERT INTO "hasan_abdulkarim" VALUES(83,5,'83:5','Vào một Ngày Vĩ đại?');
INSERT INTO "hasan_abdulkarim" VALUES(83,6,'83:6','Ngày mà nhân loại sẽ đứng trước mặt Thượng Đế của vũ trụ và muôn loài.');
INSERT INTO "hasan_abdulkarim" VALUES(83,7,'83:7','Không! Quả thật, hồ sơ của kẻ tội lỗi được cất giữ trong Sijjin.');
INSERT INTO "hasan_abdulkarim" VALUES(83,8,'83:8','Và điều gì cho Ngươi (Nabi) biết Sijjin là gì?');
INSERT INTO "hasan_abdulkarim" VALUES(83,9,'83:9','Một quyển sổ được ghi khắc.');
INSERT INTO "hasan_abdulkarim" VALUES(83,10,'83:10','Và Ngày đó khốn khổ cho những kẻ phủ nhận sự thật;');
INSERT INTO "hasan_abdulkarim" VALUES(83,11,'83:11','Những ai phủ nhận Ngày Phán Xử.');
INSERT INTO "hasan_abdulkarim" VALUES(83,12,'83:12','Và không ai phủ nhận nó ngoại trừ từng kẻ tội lỗi vượt quá mức giới hạn.');
INSERT INTO "hasan_abdulkarim" VALUES(83,13,'83:13','Và khi các Lời Mặc Khải của TA được đọc nhắc y thì y bảo: “Chuyện của cổ nhân.”');
INSERT INTO "hasan_abdulkarim" VALUES(83,14,'83:14','Nhưng không, vết nhơ dính vào quả tim của chúng do những điều (tội lỗi) mà chúng đã phạm.');
INSERT INTO "hasan_abdulkarim" VALUES(83,15,'83:15','Nhất định không! Ngày đó, chúng sẽ bị che khuất khỏi Thượng Đế của chúng.');
INSERT INTO "hasan_abdulkarim" VALUES(83,16,'83:16','Và quả thật, chúng sẽ sa hỏa ngục;');
INSERT INTO "hasan_abdulkarim" VALUES(83,17,'83:17','Có lời bảo chúng: “Đây là cái mà các ngươi thường phủ nhận!”');
INSERT INTO "hasan_abdulkarim" VALUES(83,18,'83:18','Không! quả thật, hồ sơ của người đức hạnh sẽ được giữ trong ''Illiyun.');
INSERT INTO "hasan_abdulkarim" VALUES(83,19,'83:19','Và điều gì cho Ngươi biết ''Illiyun là gì?');
INSERT INTO "hasan_abdulkarim" VALUES(83,20,'83:20','Một quyển sổ được khắc ghi');
INSERT INTO "hasan_abdulkarim" VALUES(83,21,'83:21','Mà những vị (Thiên Thần) cận kề (Allah) sẽ xác nhận.');
INSERT INTO "hasan_abdulkarim" VALUES(83,22,'83:22','Quả thật, người đức hạnh sẽ ở trong (thiên đàng) Hạnh phúc.');
INSERT INTO "hasan_abdulkarim" VALUES(83,23,'83:23','Thưởng ngoạn trên các tràng kỷ.');
INSERT INTO "hasan_abdulkarim" VALUES(83,24,'83:24','Ngươi sẽ thấy ánh hạnh phúc phản chiếu trên gương mặt của họ.');
INSERT INTO "hasan_abdulkarim" VALUES(83,25,'83:25','Họ sẽ được cho uống một loại rượu tinh khiết được khằn kín.');
INSERT INTO "hasan_abdulkarim" VALUES(83,26,'83:26','Khằn của nó bằng xạ hương. Và về việc (ân thưởng) đó, hãy để cho những người nỗ lực nên cố gắng (thi đua).');
INSERT INTO "hasan_abdulkarim" VALUES(83,27,'83:27','Và chất hòa với nó (rượu) sẽ là (chất) Tasnim.');
INSERT INTO "hasan_abdulkarim" VALUES(83,28,'83:28','Múc từ một ngọn suối mà những người gần (Allah) nhất sẽ uống.');
INSERT INTO "hasan_abdulkarim" VALUES(83,29,'83:29','Quả thật, những ai phạm tội thường chê cười những ai có đức tin.');
INSERT INTO "hasan_abdulkarim" VALUES(83,30,'83:30','Và mỗi khi chúng đi ngang qua chỗ của họ, chúng nháy mắt với nhau;');
INSERT INTO "hasan_abdulkarim" VALUES(83,31,'83:31','Và khi trở về gặp gia đình, chúng mang điều giễu cợt trở về;');
INSERT INTO "hasan_abdulkarim" VALUES(83,32,'83:32','Và khi thấy họ, chúng bảo nhau: “Quả thật đây là những kẻ lầm lạc!”');
INSERT INTO "hasan_abdulkarim" VALUES(83,33,'83:33','Nhưng chúng không được cử phái đi trông chừng họ.');
INSERT INTO "hasan_abdulkarim" VALUES(83,34,'83:34','Bởi thế, Ngày đó những ai có đức tin sẽ cười trả những kẻ vô đức tin;');
INSERT INTO "hasan_abdulkarim" VALUES(83,35,'83:35','Thưởng ngoạn trên các tràng kỷ.');
INSERT INTO "hasan_abdulkarim" VALUES(83,36,'83:36','Phải chăng những kẻ vô đức tin sẽ được đền trả đầy đủ về những điều (tội lỗi) mà chúng đã từng làm?');
INSERT INTO "hasan_abdulkarim" VALUES(84,1,'84:1','Khi bầu trời nứt ra,');
INSERT INTO "hasan_abdulkarim" VALUES(84,2,'84:2','Và tuân lệnh của Thượng Đế của nó và thi hành nhiệm vụ;');
INSERT INTO "hasan_abdulkarim" VALUES(84,3,'84:3','Và khi trái đất được trải bằng,');
INSERT INTO "hasan_abdulkarim" VALUES(84,4,'84:4','Và quẳng mọi vật nằm trong nó ra ngoài và trở thành trống rỗng,');
INSERT INTO "hasan_abdulkarim" VALUES(84,5,'84:5','Và tuân lệnh của Thượng Đế của nó và thi hành nhiệm vụ;');
INSERT INTO "hasan_abdulkarim" VALUES(84,6,'84:6','Này hỡi con người! Quả thật, ngươi làm lụng hết sức vất vả để trở về (gặp) Thượng Đế của ngươi, bởi thế ngươi sẽ gặp Ngài (Allah).');
INSERT INTO "hasan_abdulkarim" VALUES(84,7,'84:7','Do đó, đối với ai được trao sổ bộ của mình nơi tay phải,');
INSERT INTO "hasan_abdulkarim" VALUES(84,8,'84:8','Thì sẽ được thanh toán bằng một cuộc thanh toán dễ dàng,');
INSERT INTO "hasan_abdulkarim" VALUES(84,9,'84:9','Và được trở về với gia đình vui vẻ.');
INSERT INTO "hasan_abdulkarim" VALUES(84,10,'84:10','Ngược lại, đối với ai được trao sổ bộ của mình từ sau lưng,');
INSERT INTO "hasan_abdulkarim" VALUES(84,11,'84:11','Thì sẽ kêu gào cho được chết phứt;');
INSERT INTO "hasan_abdulkarim" VALUES(84,12,'84:12','Và bước vào Lửa ngọn cháy bùng');
INSERT INTO "hasan_abdulkarim" VALUES(84,13,'84:13','Quả thật (trên trần thế), y đã sống thích thú với gia đình!');
INSERT INTO "hasan_abdulkarim" VALUES(84,14,'84:14','Quả thật, y đã nghĩ sẽ không bao giờ trở lại (gặp TA)');
INSERT INTO "hasan_abdulkarim" VALUES(84,15,'84:15','Vâng! Quả thật, Thượng Đế của y hằng thấy y;');
INSERT INTO "hasan_abdulkarim" VALUES(84,16,'84:16','Bởi thế, TA thề bởi ánh hoàng hôn đỏ rực;');
INSERT INTO "hasan_abdulkarim" VALUES(84,17,'84:17','Và bởi ban đêm và mọi vật mà nó tập trung;');
INSERT INTO "hasan_abdulkarim" VALUES(84,18,'84:18','Và bởi mặt trăng tròn;');
INSERT INTO "hasan_abdulkarim" VALUES(84,19,'84:19','Chắc chắn, các ngươi sẽ bước từ chặng này đến chặng khác.');
INSERT INTO "hasan_abdulkarim" VALUES(84,20,'84:20','Nhưng trắc trở chuyện gì mà họ không chịu tin?');
INSERT INTO "hasan_abdulkarim" VALUES(84,21,'84:21','Bởi vì khi nghe đọc Qur''an, họ không chịu phủ phục;');
INSERT INTO "hasan_abdulkarim" VALUES(84,22,'84:22','Không, những kẻ không có đức tin phủ nhận sự thật;');
INSERT INTO "hasan_abdulkarim" VALUES(84,23,'84:23','Và Allah biết rõ những điều mà họ giấu giếm.');
INSERT INTO "hasan_abdulkarim" VALUES(84,24,'84:24','Bởi thế, hãy báo cho họ về một sự trừng phạt đau đớn;');
INSERT INTO "hasan_abdulkarim" VALUES(84,25,'84:25','Ngoại trừ những ai có đức tin và làm việc thiện thì sẽ nhận một phần thưởng không hề dứt.');
INSERT INTO "hasan_abdulkarim" VALUES(85,1,'85:1','Thề bởi bầu trời, chứa đựng các chòm sao.');
INSERT INTO "hasan_abdulkarim" VALUES(85,2,'85:2','Thề bởi Ngày (Phán Xử) đã được hứa;');
INSERT INTO "hasan_abdulkarim" VALUES(85,3,'85:3','Thề bởi Ngày chứng giám (thứ Sáu), và ngày (tập trung tại thung lũng ''Arafat làm Hajj) được nhìn nhận;');
INSERT INTO "hasan_abdulkarim" VALUES(85,4,'85:4','Đám người Ukhdud( bị sát hại;');
INSERT INTO "hasan_abdulkarim" VALUES(85,5,'85:5','Lửa (nung trong hào) bằng củi đốt,');
INSERT INTO "hasan_abdulkarim" VALUES(85,6,'85:6','Khi chúng ngồi bên cạnh nó;');
INSERT INTO "hasan_abdulkarim" VALUES(85,7,'85:7','Và chứng kiến cảnh mà chúng đang làm đối với những người có đức tin.');
INSERT INTO "hasan_abdulkarim" VALUES(85,8,'85:8','Và chúng hành xác họ (các tín đồ) chỉ vì lý do họ đặt niềm tin nơi Allah, Đấng Toàn Năng, Đấng Rất Đáng Ca tụng!');
INSERT INTO "hasan_abdulkarim" VALUES(85,9,'85:9','Đấng nắm quyền thống trị các tầng trời và trái đất! Và Allah là Nhân Chứng cho mọi việc.');
INSERT INTO "hasan_abdulkarim" VALUES(85,10,'85:10','Quả thật, những ai hành xác những nam và nữ tín đồ rồi không hối cải thì sẽ chịu sự trừng phạt của hỏa ngục và hình phạt của Lửa đốt.');
INSERT INTO "hasan_abdulkarim" VALUES(85,11,'85:11','Quả thật, những ai có đức tin và làm việc thiện thì sẽ được hưởng những ngôi vườn bên dưới có các dòng sông chảy (thiên đàng). Đó là một sự thành tựu vĩ đại.');
INSERT INTO "hasan_abdulkarim" VALUES(85,12,'85:12','Quả thật, sự túm bắt của Thượng Đế của Ngươi rất mãnh liệt.');
INSERT INTO "hasan_abdulkarim" VALUES(85,13,'85:13','Quả thật, Ngài (Allah) khởi tạo và tái tạo (phục sinh).');
INSERT INTO "hasan_abdulkarim" VALUES(85,14,'85:14','Và Ngài là Đấng Hằng Tha Thứ, Đấng Hằng Yêu Thương.');
INSERT INTO "hasan_abdulkarim" VALUES(85,15,'85:15','Chủ nhân của Ngai Vương, Đấng Quang Vinh');
INSERT INTO "hasan_abdulkarim" VALUES(85,16,'85:16','Đấng làm (tạo) bất cứ điều (vật) gì mà Ngài muốn.');
INSERT INTO "hasan_abdulkarim" VALUES(85,17,'85:17','Ngươi có nghe câu chuyện về Các lực lượng');
INSERT INTO "hasan_abdulkarim" VALUES(85,18,'85:18','Của Fir-''awn và Thamud chưa?');
INSERT INTO "hasan_abdulkarim" VALUES(85,19,'85:19','Không! Những ai không có đức tin thì tiếp tục phủ nhận sự thật.');
INSERT INTO "hasan_abdulkarim" VALUES(85,20,'85:20','Nhưng Allah vây bắt chúng từ đằng sau.');
INSERT INTO "hasan_abdulkarim" VALUES(85,21,'85:21','Không! Nó là Qur''an quang vinh.');
INSERT INTO "hasan_abdulkarim" VALUES(85,22,'85:22','(Ghi khắc) trong Bản Văn Lưu trữ trên cao.');
INSERT INTO "hasan_abdulkarim" VALUES(86,1,'86:1','Thề bởi bầu trời và khách viếng đêm (Sao mai);');
INSERT INTO "hasan_abdulkarim" VALUES(86,2,'86:2','Và điều gì cho Ngươi biết khách viếng đêm là gì?');
INSERT INTO "hasan_abdulkarim" VALUES(86,3,'86:3','(Đó là) một vì sao chiếu sáng;');
INSERT INTO "hasan_abdulkarim" VALUES(86,4,'86:4','Quả thật, mỗi một người đều có một vị (Thiên Thần) theo trông coi.');
INSERT INTO "hasan_abdulkarim" VALUES(86,5,'86:5','Con người hãy xét xem y được tạo hóa từ cái gì?');
INSERT INTO "hasan_abdulkarim" VALUES(86,6,'86:6','Y được tạo từ một giọt nước xuất ra,');
INSERT INTO "hasan_abdulkarim" VALUES(86,7,'86:7','Từ xương lưng và xương sườn;');
INSERT INTO "hasan_abdulkarim" VALUES(86,8,'86:8','Quả thật, Ngài (Allah) thừa khả năng làm y (sống) trở lại!');
INSERT INTO "hasan_abdulkarim" VALUES(86,9,'86:9','Vào một Ngày mà mọi bí mật đều được kiểm tra,');
INSERT INTO "hasan_abdulkarim" VALUES(86,10,'86:10','Thì y sẽ bất lực và không được ai giúp đỡ.');
INSERT INTO "hasan_abdulkarim" VALUES(86,11,'86:11','Thề bởi bầu trời (cho mưa) trở lại.');
INSERT INTO "hasan_abdulkarim" VALUES(86,12,'86:12','Và bởi đất đai nứt ra.');
INSERT INTO "hasan_abdulkarim" VALUES(86,13,'86:13','Quả thật! Nó (Qur''an) là một Lời phân biệt (phúc và tội).');
INSERT INTO "hasan_abdulkarim" VALUES(86,14,'86:14','Và nó không phải là lời dùng để bỡn cợt.');
INSERT INTO "hasan_abdulkarim" VALUES(86,15,'86:15','Quả thật, chúng đang mưu định một kế hoạch (chống đối)');
INSERT INTO "hasan_abdulkarim" VALUES(86,16,'86:16','Và TA (Allah) cũng đang hoạch định một kế hoạch.');
INSERT INTO "hasan_abdulkarim" VALUES(86,17,'86:17','Thôi, hãy tạm tha (hỡi Muhammad!) cho những kẻ vô đức tin. Hãy nhã nhặn tạm tha cho chúng.');
INSERT INTO "hasan_abdulkarim" VALUES(87,1,'87:1','Hãy tán dương đại danh của Thượng Đế của Ngươi, Đấng Tối Cao,');
INSERT INTO "hasan_abdulkarim" VALUES(87,2,'87:2','Đấng đã tạo hóa (tất cả) và ban hình thể;');
INSERT INTO "hasan_abdulkarim" VALUES(87,3,'87:3','Và đã đo lường và hướng dẫn;');
INSERT INTO "hasan_abdulkarim" VALUES(87,4,'87:4','Và đã làm mọc ra đồng cỏ,');
INSERT INTO "hasan_abdulkarim" VALUES(87,5,'87:5','Sau đó, làm cho nó thành cộng rạ khô.');
INSERT INTO "hasan_abdulkarim" VALUES(87,6,'87:6','TA sẽ bắt Ngươi đọc thuộc (Qur''an), vì thế Ngươi sẽ không quên,');
INSERT INTO "hasan_abdulkarim" VALUES(87,7,'87:7','Ngoại trừ điều nào mà Allah muốn. Quả thật, Ngài biết rõ điều bộc lộ và điều giấu giếm.');
INSERT INTO "hasan_abdulkarim" VALUES(87,8,'87:8','Và TA sẽ làm dễ dàng điều dễ dàng cho Ngươi (theo).');
INSERT INTO "hasan_abdulkarim" VALUES(87,9,'87:9','Bởi thế, hãy nhắc nhở (nhân loại) nếu lời nhắc nhở có lợi.');
INSERT INTO "hasan_abdulkarim" VALUES(87,10,'87:10','Ai sợ (Allah) thì sẽ tỉnh ngộ,');
INSERT INTO "hasan_abdulkarim" VALUES(87,11,'87:11','Nhưng những kẻ bất hạnh thì tránh xa Nó (Qur''an),');
INSERT INTO "hasan_abdulkarim" VALUES(87,12,'87:12','Kẻ đi vào Lửa lớn (của hỏa ngục),');
INSERT INTO "hasan_abdulkarim" VALUES(87,13,'87:13','Rồi trong đó, y sẽ không chết cũng không sống.');
INSERT INTO "hasan_abdulkarim" VALUES(87,14,'87:14','Chắc chắn sẽ thành đạt đối với ai là người rửa sạch (tội),');
INSERT INTO "hasan_abdulkarim" VALUES(87,15,'87:15','Và tưởng nhớ đại danh của Thượng Đế của y, và dâng lễ nguyện Salah.');
INSERT INTO "hasan_abdulkarim" VALUES(87,16,'87:16','Không, các ngươi thích đời sống trần tục này;');
INSERT INTO "hasan_abdulkarim" VALUES(87,17,'87:17','Trong lúc đời sau tốt và vĩnh viễn hơn.');
INSERT INTO "hasan_abdulkarim" VALUES(87,18,'87:18','Quả thật, điều này có (ghi) trong các Tờ Kinh cũ,');
INSERT INTO "hasan_abdulkarim" VALUES(87,19,'87:19','Các Tờ Kinh của Ibrahim và Musa.');
INSERT INTO "hasan_abdulkarim" VALUES(88,1,'88:1','Ngươi có (nghe) câu chuyện về hiện tượng bao trùm hay chưa?');
INSERT INTO "hasan_abdulkarim" VALUES(88,2,'88:2','Vào Ngày đó, gương mặt (của một số người) sẽ sa sầm.');
INSERT INTO "hasan_abdulkarim" VALUES(88,3,'88:3','Cực nhọc, rũ rượi.');
INSERT INTO "hasan_abdulkarim" VALUES(88,4,'88:4','Sẽ đi vào trong Lửa cháy bùng,');
INSERT INTO "hasan_abdulkarim" VALUES(88,5,'88:5','Sẽ được cho uống nước (sôi) múc từ một ngọn suối nóng bỏng,');
INSERT INTO "hasan_abdulkarim" VALUES(88,6,'88:6','Không có thức ăn nào cho họ dùng ngoài loại cây đắng gai góc');
INSERT INTO "hasan_abdulkarim" VALUES(88,7,'88:7','Không dinh dưỡng cũng không làm cho hết đói.');
INSERT INTO "hasan_abdulkarim" VALUES(88,8,'88:8','Vào Ngày đó, gương mặt (của một số người) sẽ hân hoan,');
INSERT INTO "hasan_abdulkarim" VALUES(88,9,'88:9','Mãn nguyện với nỗ lực của mình.');
INSERT INTO "hasan_abdulkarim" VALUES(88,10,'88:10','Trong một ngôi vườn cao đẹp.');
INSERT INTO "hasan_abdulkarim" VALUES(88,11,'88:11','Mà họ sẽ không nghe một lời nhảm nhí nào,');
INSERT INTO "hasan_abdulkarim" VALUES(88,12,'88:12','Bên trong có suối nước chảy,');
INSERT INTO "hasan_abdulkarim" VALUES(88,13,'88:13','Có bệ ngồi cao,');
INSERT INTO "hasan_abdulkarim" VALUES(88,14,'88:14','Có cốc nước giải khát dọn sẵn.');
INSERT INTO "hasan_abdulkarim" VALUES(88,15,'88:15','Có gối tựa sắp thành hàng,');
INSERT INTO "hasan_abdulkarim" VALUES(88,16,'88:16','Có thảm trải xinh đẹp.');
INSERT INTO "hasan_abdulkarim" VALUES(88,17,'88:17','Thế họ không quan sát các con lạc đà, xem chúng đã được tạo hóa như thế nào ư?');
INSERT INTO "hasan_abdulkarim" VALUES(88,18,'88:18','Và bầu trời đã được nhấc cao như thế nào ư?');
INSERT INTO "hasan_abdulkarim" VALUES(88,19,'88:19','Và các quả núi đã được dựng đứng như thế nào ư?');
INSERT INTO "hasan_abdulkarim" VALUES(88,20,'88:20','Và trái đất đã được trải rộng như thế nào ư?');
INSERT INTO "hasan_abdulkarim" VALUES(88,21,'88:21','Hãy nhắc nhở (họ); Ngươi chỉ là một Người Nhắc Nhở;');
INSERT INTO "hasan_abdulkarim" VALUES(88,22,'88:22','Chứ không phải là một vị giám đốc (độc tài) đối với họ.');
INSERT INTO "hasan_abdulkarim" VALUES(88,23,'88:23','Trừ phi ai quay bỏ đi và không tin tưởng');
INSERT INTO "hasan_abdulkarim" VALUES(88,24,'88:24','Thì Allah sẽ trừng phạt y bằng một hình phạt to lớn.');
INSERT INTO "hasan_abdulkarim" VALUES(88,25,'88:25','Quả thật, họ sẽ trở về gặp TA');
INSERT INTO "hasan_abdulkarim" VALUES(88,26,'88:26','Rồi, TA có nhiệm vụ thanh toán họ.');
INSERT INTO "hasan_abdulkarim" VALUES(89,1,'89:1','Thề bởi hừng đông;');
INSERT INTO "hasan_abdulkarim" VALUES(89,2,'89:2','Thề bởi mười đêm (đầu tiên của tháng Zul Hijjah)');
INSERT INTO "hasan_abdulkarim" VALUES(89,3,'89:3','Thề bởi (đêm) chẵn( và lẽ (của mười đêm đó).');
INSERT INTO "hasan_abdulkarim" VALUES(89,4,'89:4','Thề bởi ban đêm khi nó ra đi.');
INSERT INTO "hasan_abdulkarim" VALUES(89,5,'89:5','Há chẳng là một bằng chứng cho những người hiểu biết trong sự việc đó ư?');
INSERT INTO "hasan_abdulkarim" VALUES(89,6,'89:6','Há Ngươi không thấy Thượng Đế của Ngươi đã đối xử với ''Ad như thế nào ư?');
INSERT INTO "hasan_abdulkarim" VALUES(89,7,'89:7','(Người dân) của (thị trấn) Iram có nhiều cột trụ cao');
INSERT INTO "hasan_abdulkarim" VALUES(89,8,'89:8','Mà không cái nào trong xứ được tạo giống như chúng cả.');
INSERT INTO "hasan_abdulkarim" VALUES(89,9,'89:9','Và (người dân) Thamud đã đục đá (làm nhà) trong thung lũng như thế nào ư?');
INSERT INTO "hasan_abdulkarim" VALUES(89,10,'89:10','Và (với) Fir-''awn, chủ nhân của các cột trụ;');
INSERT INTO "hasan_abdulkarim" VALUES(89,11,'89:11','Đã hành động thái quá trong xứ;');
INSERT INTO "hasan_abdulkarim" VALUES(89,12,'89:12','Chồng chất tội ác.');
INSERT INTO "hasan_abdulkarim" VALUES(89,13,'89:13','Bởi thế, Thượng Đế của Ngươi đã giáng những đòn trừng phạt chúng;');
INSERT INTO "hasan_abdulkarim" VALUES(89,14,'89:14','Quả thật, Thượng Đế của Ngươi hằng theo dõi (chúng);');
INSERT INTO "hasan_abdulkarim" VALUES(89,15,'89:15','Bởi thế, về vấn đề của con người, khi Thượng Đế của y thử thách y với danh dự và ân huệ thì y bảo: Thượng Đế của tôi đã ban cho tôi niềm vinh dự.''');
INSERT INTO "hasan_abdulkarim" VALUES(89,16,'89:16','Nhưng khi Thượng Đế của y thử thách y với việc thu hẹp bổng lộc thì y bảo: ''Thượng Đế của tôi đã hạ nhục tôi!’');
INSERT INTO "hasan_abdulkarim" VALUES(89,17,'89:17','Nhưng không! Các ngươi không quí trọng các trẻ mồ côi!');
INSERT INTO "hasan_abdulkarim" VALUES(89,18,'89:18','Và cũng không khuyến khích nhau nuôi ăn người thiếu thốn!');
INSERT INTO "hasan_abdulkarim" VALUES(89,19,'89:19','Và tham lam ăn nuốt vô độ di sản (của kẻ khác),');
INSERT INTO "hasan_abdulkarim" VALUES(89,20,'89:20','Và mê muội yêu của cải giàu sang quá đáng!');
INSERT INTO "hasan_abdulkarim" VALUES(89,21,'89:21','Nhất định không! Khi trái đất bị nghiền nát thành bụi,');
INSERT INTO "hasan_abdulkarim" VALUES(89,22,'89:22','Và khi Thượng Đế của Ngươi ngự ra với Thiên Thần hàng hàng lớp lớp,');
INSERT INTO "hasan_abdulkarim" VALUES(89,23,'89:23','Và vào Ngày đó, hỏa ngục sẽ được đưa đến gần. Vào Ngày đó, con người sẽ sực nhớ, nhưng sự tưởng nhớ có ích lợi gì cho y?');
INSERT INTO "hasan_abdulkarim" VALUES(89,24,'89:24','Y sẽ than: Thiệt thân tôi mất! Phải chi tôi đã gởi đi trước (các việc thiện) cho đời sống này của tôi!''');
INSERT INTO "hasan_abdulkarim" VALUES(89,25,'89:25','Bởi thế, vào Ngày đó, sẽ không một ai trừng phạt giống như Ngài trừng phạt cả.');
INSERT INTO "hasan_abdulkarim" VALUES(89,26,'89:26','Và không một ai sẽ trói gô giống như Ngài trói cả.');
INSERT INTO "hasan_abdulkarim" VALUES(89,27,'89:27','(Và có lời bảo người đức hạnh:) “Hỡi người an nghỉ và toại nguyện kia!');
INSERT INTO "hasan_abdulkarim" VALUES(89,28,'89:28','“Hãy trở về với Thượng Đế của ngươi, hài lòng với mình và làm hài lòng (Ngài)!');
INSERT INTO "hasan_abdulkarim" VALUES(89,29,'89:29','“Hãy nhập vào bầy tôi (đức hạnh) của TA (Allah),');
INSERT INTO "hasan_abdulkarim" VALUES(89,30,'89:30','“Và bước vào Ngôi Vườn của TA (Allah!)”');
INSERT INTO "hasan_abdulkarim" VALUES(90,1,'90:1','TA (Allah) thề bởi thị trấn (Makkah) này;');
INSERT INTO "hasan_abdulkarim" VALUES(90,2,'90:2','Và Ngươi là một cư dân (tự do) của thị trấn này;');
INSERT INTO "hasan_abdulkarim" VALUES(90,3,'90:3','Và thề bởi đấng sinh thành (Adam) và con cháu mà T đã sinh ra;');
INSERT INTO "hasan_abdulkarim" VALUES(90,4,'90:4','Chắc chắn, TA đã tạo hóa con người để làm lụng cực nhọc.');
INSERT INTO "hasan_abdulkarim" VALUES(90,5,'90:5','Phải chăng y nghĩ không có ai thắng được y hay sao?');
INSERT INTO "hasan_abdulkarim" VALUES(90,6,'90:6','Y bảo: ‘Tôi đã tiêu phí vô số tài sản.”');
INSERT INTO "hasan_abdulkarim" VALUES(90,7,'90:7','Phải chăng y nghĩ không có ai thấy được y?');
INSERT INTO "hasan_abdulkarim" VALUES(90,8,'90:8','Há TA đã không làm cho y có cặp mắt?');
INSERT INTO "hasan_abdulkarim" VALUES(90,9,'90:9','Và chiếc lưỡi và đôi môi?');
INSERT INTO "hasan_abdulkarim" VALUES(90,10,'90:10','Và chỉ cho y hai con đường (chính và tà)?');
INSERT INTO "hasan_abdulkarim" VALUES(90,11,'90:11','Nhưng y không mạo hiểm trên con đường dốc đứng.');
INSERT INTO "hasan_abdulkarim" VALUES(90,12,'90:12','Và Ngươi có biết con đường dốc đứng là gì chăng?');
INSERT INTO "hasan_abdulkarim" VALUES(90,13,'90:13','(Đó là) việc giải phóng một vòng cổ (nô lệ);');
INSERT INTO "hasan_abdulkarim" VALUES(90,14,'90:14','Và nuôi ăn vào một ngày đói lả.');
INSERT INTO "hasan_abdulkarim" VALUES(90,15,'90:15','Một đứa trẻ mồ côi thân thuộc;');
INSERT INTO "hasan_abdulkarim" VALUES(90,16,'90:16','Hoặc một người túng thiếu dính bụi đường.');
INSERT INTO "hasan_abdulkarim" VALUES(90,17,'90:17','Rồi trở thành một người có đức tin và khuyến khích nhau kiên nhẫn và khuyến khích nhau độ lượng.');
INSERT INTO "hasan_abdulkarim" VALUES(90,18,'90:18','Họ là những người bạn phía tay phải (sẽ vào thiên đàng).');
INSERT INTO "hasan_abdulkarim" VALUES(90,19,'90:19','Còn những ai phủ nhận các Dấu Hiệu của TA thì sẽ là những người bạn phía tay trái;');
INSERT INTO "hasan_abdulkarim" VALUES(90,20,'90:20','Lửa sẽ bao phủ lấy chúng.');
INSERT INTO "hasan_abdulkarim" VALUES(91,1,'91:1','Thề bởi mặt Trời và sức chói sáng của nó;');
INSERT INTO "hasan_abdulkarim" VALUES(91,2,'91:2','Thề bởi mặt Trăng khi đi theo nó;');
INSERT INTO "hasan_abdulkarim" VALUES(91,3,'91:3','Thề bởi ban Ngày khi nó phô bày sự rạng rỡ;');
INSERT INTO "hasan_abdulkarim" VALUES(91,4,'91:4','Thề bởi ban Đêm khi che lắp nó;');
INSERT INTO "hasan_abdulkarim" VALUES(91,5,'91:5','Thề bởi bầu trời và Đấng đã dựng nó;');
INSERT INTO "hasan_abdulkarim" VALUES(91,6,'91:6','Thề bởi trái đất và Đấng đã trải rộng nó;');
INSERT INTO "hasan_abdulkarim" VALUES(91,7,'91:7','Thề bởi linh hồn và Đấng đã hoàn chỉnh nó;');
INSERT INTO "hasan_abdulkarim" VALUES(91,8,'91:8','Rồi Ngài làm cho nó linh cảm, bởi thế, nó ý thức được điều xấu và thiện;');
INSERT INTO "hasan_abdulkarim" VALUES(91,9,'91:9','Người nào tẩy sạch nó thì chắc chắn sẽ thành đạt.');
INSERT INTO "hasan_abdulkarim" VALUES(91,10,'91:10','Người nào làm nó thối nát thì chắc chắn sẽ thất bại.');
INSERT INTO "hasan_abdulkarim" VALUES(91,11,'91:11','(Bộ tộc) Thamud, do lòng kiêu căng tự phụ đã phủ nhận sự thật.');
INSERT INTO "hasan_abdulkarim" VALUES(91,12,'91:12','Khi quân khốn nạn nhất trong bọn chúng đứng dậy.');
INSERT INTO "hasan_abdulkarim" VALUES(91,13,'91:13','Thấy thế, Sứ Giả của Allah bảo chúng: “Đây là con lạc đà cái của Allah, hãy để cho nó uống nước.”');
INSERT INTO "hasan_abdulkarim" VALUES(91,14,'91:14','Nhưng chúng cho Y nói dối và cắt nhượng nó. Bởi thế Thượng Đế của chúng đã giận dữ, trừng phạt và san bằng chúng vì tội ác của chúng.');
INSERT INTO "hasan_abdulkarim" VALUES(91,15,'91:15','Và Ngài (Allah) không sợ hậu quả (của việc trừng phạt) đó.');
INSERT INTO "hasan_abdulkarim" VALUES(92,1,'92:1','Thề bởi ban đêm khi nó bao phủ;');
INSERT INTO "hasan_abdulkarim" VALUES(92,2,'92:2','Thề bởi ban ngày khi nó sáng tỏ;');
INSERT INTO "hasan_abdulkarim" VALUES(92,3,'92:3','Thề bởi Đấng đã tạo hóa nam và nữ giới;');
INSERT INTO "hasan_abdulkarim" VALUES(92,4,'92:4','Quả thật, nỗ lực của các ngươi rõ khác biệt;');
INSERT INTO "hasan_abdulkarim" VALUES(92,5,'92:5','Bởi thế, đối với ai bố thí và sợ Allah;');
INSERT INTO "hasan_abdulkarim" VALUES(92,6,'92:6','Và tin nơi cái Tốt;');
INSERT INTO "hasan_abdulkarim" VALUES(92,7,'92:7','Thì TA sẽ làm con đường đi đến thoải mái được dễ dàng cho y;');
INSERT INTO "hasan_abdulkarim" VALUES(92,8,'92:8','Ngược lại, đối với ai keo kiệt và tự cho mình giàu có đầy đủ.');
INSERT INTO "hasan_abdulkarim" VALUES(92,9,'92:9','Và phủ nhận cái Tốt');
INSERT INTO "hasan_abdulkarim" VALUES(92,10,'92:10','Thì TA sẽ làm con đường đưa đến cực nhọc được dễ dàng cho y;');
INSERT INTO "hasan_abdulkarim" VALUES(92,11,'92:11','Và của cải chẳng giúp ích gì được y khi y bị tiêu hủy;');
INSERT INTO "hasan_abdulkarim" VALUES(92,12,'92:12','Quả thật, TA (Allah) có nhiệm vụ ban Chỉ Đạo hướng dẫn.');
INSERT INTO "hasan_abdulkarim" VALUES(92,13,'92:13','Và quả thật, đời sau và đời này là của TA.');
INSERT INTO "hasan_abdulkarim" VALUES(92,14,'92:14','Bởi thế, TA cảnh cáo các ngươi về ngọn Lửa cháy dữ dội.');
INSERT INTO "hasan_abdulkarim" VALUES(92,15,'92:15','Không ai bị nướng (đốt) trong đó ngoài những kẻ bất hạnh nhất,');
INSERT INTO "hasan_abdulkarim" VALUES(92,16,'92:16','Tên phủ nhận sự thật và quay bỏ đi.');
INSERT INTO "hasan_abdulkarim" VALUES(92,17,'92:17','Và người ngay chính sợ Allah sẽ được giữ xa khỏi nó.');
INSERT INTO "hasan_abdulkarim" VALUES(92,18,'92:18','Người cho (bố thí) của cải của mình nhằm tẩy sạch (bản thân),');
INSERT INTO "hasan_abdulkarim" VALUES(92,19,'92:19','Và không đòi hỏi một ai trả ơn mình về ân huệ đã ban cho người,');
INSERT INTO "hasan_abdulkarim" VALUES(92,20,'92:20','Mà chỉ mong tìm sắc diện của Thượng Đế của mình, Đấng Tối Cao;');
INSERT INTO "hasan_abdulkarim" VALUES(92,21,'92:21','Và chắc chắn y sẽ sớm được hài lòng (vào thiên đàng).');
INSERT INTO "hasan_abdulkarim" VALUES(93,1,'93:1','Thề bởi ban mai;');
INSERT INTO "hasan_abdulkarim" VALUES(93,2,'93:2','Và bởi ban đêm khi nó tĩnh mịch;');
INSERT INTO "hasan_abdulkarim" VALUES(93,3,'93:3','Thượng Đế của Ngươi không bỏ rơi cũng không ghét Ngươi.');
INSERT INTO "hasan_abdulkarim" VALUES(93,4,'93:4','Và chắc chắn, đời sau tốt cho Ngươi hơn đời sống (hiện tại).');
INSERT INTO "hasan_abdulkarim" VALUES(93,5,'93:5','Và chắc chắn, Thượng Đế của Ngươi sẽ ban cho Ngươi điều làm Ngươi hài lòng;');
INSERT INTO "hasan_abdulkarim" VALUES(93,6,'93:6','Há Ngài đã không thấy Ngươi mồ côi nên đã ban cho một chỗ nương tựa?');
INSERT INTO "hasan_abdulkarim" VALUES(93,7,'93:7','Và thấy Ngươi lang thang (tìm Chân Lý) nên đã ban cho Chỉ Đạo?');
INSERT INTO "hasan_abdulkarim" VALUES(93,8,'93:8','Và thấy Ngươi nghèo khó nên đã ban cho sự giàu có?');
INSERT INTO "hasan_abdulkarim" VALUES(93,9,'93:9','Bởi thế, đối với trẻ mồ côi chớ nên bạc đãi (chúng);');
INSERT INTO "hasan_abdulkarim" VALUES(93,10,'93:10','Và đối với người ăn xin chớ nên xua đuổi (họ);');
INSERT INTO "hasan_abdulkarim" VALUES(93,11,'93:11','Và hãy tuyên bố về Ân Huệ của Thượng Đế của Ngươi.');
INSERT INTO "hasan_abdulkarim" VALUES(94,1,'94:1','Há TA đã không mở rộng lòng ngực cho Ngươi?');
INSERT INTO "hasan_abdulkarim" VALUES(94,2,'94:2','Và trút bỏ khỏi Ngươi gánh nặng');
INSERT INTO "hasan_abdulkarim" VALUES(94,3,'94:3','Đã đè nặng lên lưng của Ngươi?');
INSERT INTO "hasan_abdulkarim" VALUES(94,4,'94:4','Và TA đã nâng cao danh tiếng của Ngươi?');
INSERT INTO "hasan_abdulkarim" VALUES(94,5,'94:5','Quả thật, sự dễ dàng đi đôi với sự khó khăn;');
INSERT INTO "hasan_abdulkarim" VALUES(94,6,'94:6','Quả thật, sự dễ dàng đi đôi với sự khó khăn.');
INSERT INTO "hasan_abdulkarim" VALUES(94,7,'94:7','Bởi thế, khi thư thả, Ngươi hãy đứng dậy (dâng lễ nguyện Salah).');
INSERT INTO "hasan_abdulkarim" VALUES(94,8,'94:8','Và Ngươi hãy hướng về Thượng Đế của Ngươi (mà cầu nguyện và hy vọng)');
INSERT INTO "hasan_abdulkarim" VALUES(95,1,'95:1','Thề bởi cây Sung, và cây Ồ liu,');
INSERT INTO "hasan_abdulkarim" VALUES(95,2,'95:2','Thề bởi ngọn Núi Sina');
INSERT INTO "hasan_abdulkarim" VALUES(95,3,'95:3','Thề bởi thành phố (Makkah) an ninh này,');
INSERT INTO "hasan_abdulkarim" VALUES(95,4,'95:4','Chắc chắn, TA đã tạo hóa con người theo một hình thể tốt đẹp nhất,');
INSERT INTO "hasan_abdulkarim" VALUES(95,5,'95:5','Rồi TA lật y xuống thấp nhất trong cái thấp (vì phạm tội),');
INSERT INTO "hasan_abdulkarim" VALUES(95,6,'95:6','Ngoại trừ nhũng ai có đức tin và làm việc thiện thì sẽ được phần thưởng vô tận.');
INSERT INTO "hasan_abdulkarim" VALUES(95,7,'95:7','Nhưng cái gì làm cho ngươi phủ nhận việc Phán Xử?');
INSERT INTO "hasan_abdulkarim" VALUES(95,8,'95:8','Há Allah không là Đấng Thẩm Phán vô cùng Cao minh ư?');
INSERT INTO "hasan_abdulkarim" VALUES(96,1,'96:1','Hãy đọc! Nhân danh Thượng Đế của Ngươi, Đấng đã tạo,');
INSERT INTO "hasan_abdulkarim" VALUES(96,2,'96:2','Đã tạo con người từ một hòn máu đặc.');
INSERT INTO "hasan_abdulkarim" VALUES(96,3,'96:3','Hãy đọc! Và Thượng Đế của Ngươi Rất Mực Quảng Đại,');
INSERT INTO "hasan_abdulkarim" VALUES(96,4,'96:4','Đấng đã dạy (kiến thức) bằng cây viết;');
INSERT INTO "hasan_abdulkarim" VALUES(96,5,'96:5','Đã dạy con người điều mà y không biết;');
INSERT INTO "hasan_abdulkarim" VALUES(96,6,'96:6','Nhưng không! Quả thật, con người thái quá,');
INSERT INTO "hasan_abdulkarim" VALUES(96,7,'96:7','Bởi vì y tự thấy mình đầy đủ.');
INSERT INTO "hasan_abdulkarim" VALUES(96,8,'96:8','Quả thật, y sẽ trở lại gặp Thượng Đế của Ngươi (ở đời sau).');
INSERT INTO "hasan_abdulkarim" VALUES(96,9,'96:9','Há Ngươi có thấy một kẻ (Abu Jahl) đã ngăn cản');
INSERT INTO "hasan_abdulkarim" VALUES(96,10,'96:10','Một người bề tôi (Muhammad) khi Y dâng lễ nguyện Salah?');
INSERT INTO "hasan_abdulkarim" VALUES(96,11,'96:11','Há Ngươi xét thấy y được hướng dẫn hay chăng?');
INSERT INTO "hasan_abdulkarim" VALUES(96,12,'96:12','Hoặc truyền bảo (người khác) nên sợ Allah hay chăng?');
INSERT INTO "hasan_abdulkarim" VALUES(96,13,'96:13','Há Ngươi xét thấy y phủ nhận sự thật và quay bỏ đi?');
INSERT INTO "hasan_abdulkarim" VALUES(96,14,'96:14','Há y không biết rằng Allah Hằng thấy (tất cả).');
INSERT INTO "hasan_abdulkarim" VALUES(96,15,'96:15','Nhưng không! Nếu y không chịu ngưng (phá rối) thì chắc chắn TA sẽ nắm chùm tóc trán (của y),');
INSERT INTO "hasan_abdulkarim" VALUES(96,16,'96:16','Một chùm tóc láo khoét, tội lỗi.');
INSERT INTO "hasan_abdulkarim" VALUES(96,17,'96:17','Hãy để y gọi bè nhóm của y (đến giúp);');
INSERT INTO "hasan_abdulkarim" VALUES(96,18,'96:18','TA sẽ gọi các cảnh vệ của hỏa ngục (đến trừng trị)!');
INSERT INTO "hasan_abdulkarim" VALUES(96,19,'96:19','Nhưng không! Chớ nghe theo y. Và hãy phủ phục và đến gần (Ngài)');
INSERT INTO "hasan_abdulkarim" VALUES(97,1,'97:1','Quả thật, TA đã ban Nó (Qur''an) xuống vào một Đêm Định Mệnh.');
INSERT INTO "hasan_abdulkarim" VALUES(97,2,'97:2','Và điều gì cho Ngươi biết Đêm Định Mệnh là gì?');
INSERT INTO "hasan_abdulkarim" VALUES(97,3,'97:3','Đêm Định Mệnh tốt hơn một ngàn tháng bình thường;');
INSERT INTO "hasan_abdulkarim" VALUES(97,4,'97:4','Trong đêm đó, các Thiên Thần và Ruh (Jibril) mang Quyết Định của Allah (về tất cả mọi vấn đề) xuống trần theo Phép của Thượng Đế của họ;');
INSERT INTO "hasan_abdulkarim" VALUES(97,5,'97:5','Sự Bằng an. Nó kéo dài cho đến hừng đông.');
INSERT INTO "hasan_abdulkarim" VALUES(98,1,'98:1','Những ai không có đức tin trong số Người dân Kinh Sách và những người thờ đa thần không chịu từ bỏ (tín ngưỡng của họ) cho đến khi họ gặp minh chứng (Nabi Muhammad);');
INSERT INTO "hasan_abdulkarim" VALUES(98,2,'98:2','Một Sứ Giả do Allah phái đến đọc các Tờ Kinh trong sạch (Qur''an).');
INSERT INTO "hasan_abdulkarim" VALUES(98,3,'98:3','Trong đó, gồm các sắc chỉ chính trực.');
INSERT INTO "hasan_abdulkarim" VALUES(98,4,'98:4','Và những ai đã được ban Kinh Sách chỉ chia rẽ nhau sau khi họ đã gặp minh chứng (Nabi Muhammad).');
INSERT INTO "hasan_abdulkarim" VALUES(98,5,'98:5','Và họ được lệnh chỉ phải thờ phụng riêng Allah, triệt để thầnphục Ngài một cách chính trực và dâng lễ nguyện Salah một cách chu đáo và đóng Zakah và đó là tôn giáo đúng đắn, ngay chính.');
INSERT INTO "hasan_abdulkarim" VALUES(98,6,'98:6','Quả thật, những ai không có tin trong số Người dân Kinh Sách và những người thờ đa thần sẽ vào ở trong hỏa ngục đời đời. Họ là những sinh vật xấu xa nhất.');
INSERT INTO "hasan_abdulkarim" VALUES(98,7,'98:7','Quả thật, những ai có đức tin (nơi Allah và Sứ Giả Muhammad) và làm việc thiện thì sẽ là những sinh vật tốt đẹp nhất.');
INSERT INTO "hasan_abdulkarim" VALUES(98,8,'98:8','Phần thưởng của họ nơi Thượng Đế của họ sẽ là những Ngôi Vườn Vĩnh Cửu (thiên đàng) bên dưới có các dòng sông chảy mà họ sẽ vào ở trong đó đời đời; Allah sẽ hài lòng với họ và họ sẽ toại nguyện với Ngài. Phần ân thưởng đó dành cho ai là người sợ Thượng Đế của mình.');
INSERT INTO "hasan_abdulkarim" VALUES(99,1,'99:1','Khi trái đất chấn động qua một cuộc động đất (cuối cùng);');
INSERT INTO "hasan_abdulkarim" VALUES(99,2,'99:2','Và trút gánh nặng của nó ra ngoài,');
INSERT INTO "hasan_abdulkarim" VALUES(99,3,'99:3','Và con người sẽ bảo: “Chuyện gì xảy đến cho nó vậy?”');
INSERT INTO "hasan_abdulkarim" VALUES(99,4,'99:4','Vào Ngày đó, nó sẽ kể câu chuyện của nó.');
INSERT INTO "hasan_abdulkarim" VALUES(99,5,'99:5','Bởi vì Thượng Đế (Allah) của Ngươi mặc khải cho nó.');
INSERT INTO "hasan_abdulkarim" VALUES(99,6,'99:6','Vào Ngày đó, nhân loại sẽ đi rời rạc từng đoàn đến chứng kiến việc làm của họ.');
INSERT INTO "hasan_abdulkarim" VALUES(99,7,'99:7','Bởi thế, ai làm việc thiện tốt và ngoan đạo dù nhẹ bằng hạt cải cũng sẽ thấy nó;');
INSERT INTO "hasan_abdulkarim" VALUES(99,8,'99:8','Và ai làm việc xấu và tội lỗi dù nhẹ bằng hạt cải cũng sẽ thấy nó.');
INSERT INTO "hasan_abdulkarim" VALUES(100,1,'100:1','Thề bởi các con chiến mã chạy hùng hậu xông trận (tấn công địch);');
INSERT INTO "hasan_abdulkarim" VALUES(100,2,'100:2','Bởi thế (móng chân của chúng) làm bắn ra tia lửa;');
INSERT INTO "hasan_abdulkarim" VALUES(100,3,'100:3','Rồi tấn công vào buổi hừng sáng;');
INSERT INTO "hasan_abdulkarim" VALUES(100,4,'100:4','Rồi làm tung bụi mịt mù;');
INSERT INTO "hasan_abdulkarim" VALUES(100,5,'100:5','Và chúng xông thẳng vào lòng (quân địch);');
INSERT INTO "hasan_abdulkarim" VALUES(100,6,'100:6','Quả thật! Con người vong ân đối với Thượng Đế (Allah) của y;');
INSERT INTO "hasan_abdulkarim" VALUES(100,7,'100:7','Và chính y là nhân chứng cho việc đó;');
INSERT INTO "hasan_abdulkarim" VALUES(100,8,'100:8','Và quả thật, y quá yêu thích sự giàu có.');
INSERT INTO "hasan_abdulkarim" VALUES(100,9,'100:9','Há y không biết, khi mọi vật nằm dưới mộ đều bị quật lên?');
INSERT INTO "hasan_abdulkarim" VALUES(100,10,'100:10','Và mọi điều (bí mật) trong lòng sẽ được phơi bày.');
INSERT INTO "hasan_abdulkarim" VALUES(100,11,'100:11','Quả thật, vào Ngày đó, Thượng Đế của họ sẽ rất mực Am Tường.');
INSERT INTO "hasan_abdulkarim" VALUES(101,1,'101:1','Al-Qari''ah.');
INSERT INTO "hasan_abdulkarim" VALUES(101,2,'101:2','Al-Qari''ah là gì?');
INSERT INTO "hasan_abdulkarim" VALUES(101,3,'101:3','Và điều gì cho Ngươi (Muhammad) biết Al-Qari''ah là gì?');
INSERT INTO "hasan_abdulkarim" VALUES(101,4,'101:4','Đó là Ngày mà nhân loại sẽ như bươm bướm bay tán loạn;');
INSERT INTO "hasan_abdulkarim" VALUES(101,5,'101:5','Và những quả núi sẽ (bắn tung) như cụm lông cừu được chải ra;');
INSERT INTO "hasan_abdulkarim" VALUES(101,6,'101:6','Bởi thế, đối với ai mà bàn cân (chứa việc thiện tốt và ngoan đạo) nặng,');
INSERT INTO "hasan_abdulkarim" VALUES(101,7,'101:7','Thì sẽ hưởng một đời sống hài lòng và thỏa nguyện.');
INSERT INTO "hasan_abdulkarim" VALUES(101,8,'101:8','Và đối với ai mà bàn cân (chứa việc thiện tốt và ngoan đạo) nhẹ,');
INSERT INTO "hasan_abdulkarim" VALUES(101,9,'101:9','Thì nhà ở của y sẽ là Hawiyah.');
INSERT INTO "hasan_abdulkarim" VALUES(101,10,'101:10','Và điều gì cho Ngươi biết Hawiyah là gì?');
INSERT INTO "hasan_abdulkarim" VALUES(101,11,'101:11','(Đó là) Lửa ngọn cháy dữ dội!');
INSERT INTO "hasan_abdulkarim" VALUES(102,1,'102:1','Việc tom góp của cải (làm giàu) làm cho các ngươi bận tâm,');
INSERT INTO "hasan_abdulkarim" VALUES(102,2,'102:2','Cho đến khi các ngươi đi thăm mộ.');
INSERT INTO "hasan_abdulkarim" VALUES(102,3,'102:3','Nhất định không! Các ngươi sẽ sớm biết!');
INSERT INTO "hasan_abdulkarim" VALUES(102,4,'102:4','Rồi nhất định không! Các ngươi sẽ sớm biết!');
INSERT INTO "hasan_abdulkarim" VALUES(102,5,'102:5','Nhất định không! Nếu các ngươi biết với một sự hiểu biết chắc chắn');
INSERT INTO "hasan_abdulkarim" VALUES(102,6,'102:6','Thì chắc chắn sẽ thấy hỏa ngục!');
INSERT INTO "hasan_abdulkarim" VALUES(102,7,'102:7','Rồi chắc chắn các ngươi sẽ thấy nó với cặp mắt kiên định!');
INSERT INTO "hasan_abdulkarim" VALUES(102,8,'102:8','Rồi chắc chắn vào Ngày đó các ngươi sẽ bị tra hỏi về lạc thú.');
INSERT INTO "hasan_abdulkarim" VALUES(103,1,'103:1','Thề bởi thời gian.');
INSERT INTO "hasan_abdulkarim" VALUES(103,2,'103:2','Quả thật, con người sẽ đi vào thua thiệt,');
INSERT INTO "hasan_abdulkarim" VALUES(103,3,'103:3','Ngoại trừ những ai có đức tin và làm việc thiện và khuyên bảo nhau tôn trọng sự thật và khuyên bảo nhau kiên nhẫn.');
INSERT INTO "hasan_abdulkarim" VALUES(104,1,'104:1','Thiệt thân cho từng kẻ vu cáo và nói xấu người khác.');
INSERT INTO "hasan_abdulkarim" VALUES(104,2,'104:2','Những ai vơ vét tiền bạc và luôn tay đếm nó,');
INSERT INTO "hasan_abdulkarim" VALUES(104,3,'104:3','Nghĩ rằng của cải sẽ làm cho y sống đời đời!');
INSERT INTO "hasan_abdulkarim" VALUES(104,4,'104:4','Nhất định không! Chắc chắn, y sẽ bị quẳng vào Hutamah;');
INSERT INTO "hasan_abdulkarim" VALUES(104,5,'104:5','Và điều gì cho Ngươi (Nabi) biết Hutamah là gì?');
INSERT INTO "hasan_abdulkarim" VALUES(104,6,'104:6','Đó là Lửa của Allah, được nhúm lên (thành ngọn),');
INSERT INTO "hasan_abdulkarim" VALUES(104,7,'104:7','Sẽ bốc cao lên tận quả tim;');
INSERT INTO "hasan_abdulkarim" VALUES(104,8,'104:8','Quả thật, nó (Lửa) sẽ vây kín chúng lại;');
INSERT INTO "hasan_abdulkarim" VALUES(104,9,'104:9','Bên trong các cột lửa cháy tỏa rộng.');
INSERT INTO "hasan_abdulkarim" VALUES(105,1,'105:1','Há Ngươi không thấy Thượng Đế của Ngươi đã đối phó với đoàn quân cưỡi voi (của Abrahah Al-Ashram định tiêu diệt ngôi đền Ka''bah) như thế nào ư?');
INSERT INTO "hasan_abdulkarim" VALUES(105,2,'105:2','Há Ngài đã không phá hỏng kế hoạch của bọn chúng hay sao?');
INSERT INTO "hasan_abdulkarim" VALUES(105,3,'105:3','Và Ngài đã phái chim bay từng đàn đến tiêu diệt chúng?');
INSERT INTO "hasan_abdulkarim" VALUES(105,4,'105:4','Đánh chúng bằng đá Sijjil (đất sét nung).');
INSERT INTO "hasan_abdulkarim" VALUES(105,5,'105:5','Bởi thế, làm cho chúng (chết) như cọng rạ khô (sau mùa gặt).');
INSERT INTO "hasan_abdulkarim" VALUES(106,1,'106:1','Nhằm bảo vệ bộ tộc Quraish,');
INSERT INTO "hasan_abdulkarim" VALUES(106,2,'106:2','Bảo vệ họ đi buôn xa (về phía Nam) vào mùa đông và (về phía Bắc) vào mùa hạ.');
INSERT INTO "hasan_abdulkarim" VALUES(106,3,'106:3','Bởi vậy, họ phải thờ phụng Thượng Đế của Ngôi Đền (Ka''bah) này (tại Makkah).');
INSERT INTO "hasan_abdulkarim" VALUES(106,4,'106:4','Đấng đã nuôi họ khỏi đói và cho họ được an toàn khỏi sợ.');
INSERT INTO "hasan_abdulkarim" VALUES(107,1,'107:1','Há Ngươi có thấy kẻ phủ nhận việc Phán Xử (để thưởng và phạt)?');
INSERT INTO "hasan_abdulkarim" VALUES(107,2,'107:2','Bởi lẽ kẻ ấy đã xua đuổi trẻ mồ côi;');
INSERT INTO "hasan_abdulkarim" VALUES(107,3,'107:3','Và không khuyến khích việc nuôi ăn những người thiếu thốn;');
INSERT INTO "hasan_abdulkarim" VALUES(107,4,'107:4','Bởi thế, thiệt thân cho những người dâng lễ nguyện Salah.');
INSERT INTO "hasan_abdulkarim" VALUES(107,5,'107:5','Những ai lơ là trong việc dâng lễ nguyện Salah của họ,');
INSERT INTO "hasan_abdulkarim" VALUES(107,6,'107:6','Những ai chỉ muốn phô trương cho (người khác) thấy,');
INSERT INTO "hasan_abdulkarim" VALUES(107,7,'107:7','Và từ chối giúp đỡ (láng giềng) về những vật dụng cần thiết.');
INSERT INTO "hasan_abdulkarim" VALUES(108,1,'108:1','Quả thật, TA đã ban cho Ngươi (Muhammad) Al-Kauthar (một con sông nơi thiên đàng).');
INSERT INTO "hasan_abdulkarim" VALUES(108,2,'108:2','Bởi thế, hãy dâng lễ nguyện Salah và tế lễ (dâng lên Allah).');
INSERT INTO "hasan_abdulkarim" VALUES(108,3,'108:3','Quả thật, kẻ xúc phạm Ngươi mới thực sự bị cắt đứt (mọi hy vọng).');
INSERT INTO "hasan_abdulkarim" VALUES(109,1,'109:1','Hãy bảo (chúng): “Này hỡi những kẻ phủ nhận Allah!"');
INSERT INTO "hasan_abdulkarim" VALUES(109,2,'109:2','"Ta không tôn thờ những kẻ (thần linh) mà các ngươi tôn thờ."');
INSERT INTO "hasan_abdulkarim" VALUES(109,3,'109:3','“Các ngươi cũng sẽ không tôn thờ Đấng (Allah) mà Ta thờ phụng."');
INSERT INTO "hasan_abdulkarim" VALUES(109,4,'109:4','“Và Ta sẽ không là một tín đồ của kẻ mà các ngươi đang tôn thờ."');
INSERT INTO "hasan_abdulkarim" VALUES(109,5,'109:5','“Và các ngươi cũng sẽ không tôn thờ Đấng mà Ta đang thờ phụng.''');
INSERT INTO "hasan_abdulkarim" VALUES(109,6,'109:6','“Các ngươi có tôn giáo của các ngươi và Ta có tôn giáo của Ta.”');
INSERT INTO "hasan_abdulkarim" VALUES(110,1,'110:1','Khi sự giúp đỡ của Allah đến (với Ngươi Muhammad) và sự thắng lợi (khi chinh phục Makkah),');
INSERT INTO "hasan_abdulkarim" VALUES(110,2,'110:2','Và Ngươi thấy nhân loại gia nhập tôn giáo của Allah (Islam) từng đoàn;');
INSERT INTO "hasan_abdulkarim" VALUES(110,3,'110:3','Bởi thế, hãy tán dương lời ca tụng Thượng Đế (Allah) của Ngươi và cầu xin Ngài tha thứ. Quả thật, Ngài Hằng quay lại tha thứ.');
INSERT INTO "hasan_abdulkarim" VALUES(111,1,'111:1','Hai tay của Abu Lahab bị hủy diệt và y bị hủy diệt!');
INSERT INTO "hasan_abdulkarim" VALUES(111,2,'111:2','Của cải và tất cả những gì mà y tậu tác sẽ chẳng giúp y được gì.');
INSERT INTO "hasan_abdulkarim" VALUES(111,3,'111:3','Y sẽ bị đốt trong Lửa ngùn ngụt cháy!');
INSERT INTO "hasan_abdulkarim" VALUES(111,4,'111:4','Và vợ của y, người đã từng vác củi khô (cũng sẽ bị như thế).');
INSERT INTO "hasan_abdulkarim" VALUES(111,5,'111:5','Cổ của ả ta sẽ bị thắt bởi một sợi dây thốt nốt.');
INSERT INTO "hasan_abdulkarim" VALUES(112,1,'112:1','Hãy nói: “Ngài, Allah, là Một (Duy nhất).');
INSERT INTO "hasan_abdulkarim" VALUES(112,2,'112:2','“Allah là Đấng Tự Hữu, Độc Lập mà tất cả phải nhờ vả.');
INSERT INTO "hasan_abdulkarim" VALUES(112,3,'112:3','“Ngài không sinh (đẻ) ai, cũng không do ai sinh ra.');
INSERT INTO "hasan_abdulkarim" VALUES(112,4,'112:4','“Và không một ai (cái gì) có thể so sánh với Ngài đặng.”');
INSERT INTO "hasan_abdulkarim" VALUES(113,1,'113:1','Hãy nói: “Bề tôi cầu xin Thượng Đế của buổi rạng đông che chở,"');
INSERT INTO "hasan_abdulkarim" VALUES(113,2,'113:2','"Tránh khỏi sự tác hại của những vật mà Ngài đã tạo,"');
INSERT INTO "hasan_abdulkarim" VALUES(113,3,'113:3','“Và khỏi sự tác hại của màn đêm khi nó bao phủ,"');
INSERT INTO "hasan_abdulkarim" VALUES(113,4,'113:4','“Và khỏi sự tác hại của những kẻ thổi (phù phép) vào những chiếc gút thắt,"');
INSERT INTO "hasan_abdulkarim" VALUES(113,5,'113:5','“Và khỏi sự hãm hại của những kẻ đố kỵ khi họ ganh tị.”');
INSERT INTO "hasan_abdulkarim" VALUES(114,1,'114:1','Hãy nói: "Bề tôi cầu xin Thượng Đế của nhân loại,"');
INSERT INTO "hasan_abdulkarim" VALUES(114,2,'114:2','“Đức Vua của nhân loại,"');
INSERT INTO "hasan_abdulkarim" VALUES(114,3,'114:3','“Đấng Thượng Đế của nhân loại,"');
INSERT INTO "hasan_abdulkarim" VALUES(114,4,'114:4','“(Che chở) tránh khỏi sự hãm hại của kẻ thì thào (lời xúi giục, bùa phép) rồi lẫn mất,"');
INSERT INTO "hasan_abdulkarim" VALUES(114,5,'114:5','“Kẻ đã thì thào (những điều tác hại) vào lòng người,"');
INSERT INTO "hasan_abdulkarim" VALUES(114,6,'114:6','“Từ loài Jinn và loài người.”');
