INSERT INTO "hasan_efendi" VALUES(1,1,'1:1','Me emrin e Allahut, të Gjithëmëshirshmit, Mëshirëplotit! ');
INSERT INTO "hasan_efendi" VALUES(1,2,'1:2','Çdo lavdërim i përket Allahut, Zotit të botëve,');
INSERT INTO "hasan_efendi" VALUES(1,3,'1:3','të Gjithëmëshirshmit, Mëshirëplotit,');
INSERT INTO "hasan_efendi" VALUES(1,4,'1:4','Sunduesit të Ditës së Gjykimit.');
INSERT INTO "hasan_efendi" VALUES(1,5,'1:5','Vetëm Ty të adhurojmë dhe vetëm prej Teje ndihmë kërkojmë. ');
INSERT INTO "hasan_efendi" VALUES(1,6,'1:6','Udhëzona në rrugën e drejtë! ');
INSERT INTO "hasan_efendi" VALUES(1,7,'1:7','Në rrugën e atyre që u ke dhuruar mirësi e jo në të atyre që kanë shkaktuar zemërimin Tënd, as në të atyre që janë të humbur! ');
INSERT INTO "hasan_efendi" VALUES(2,1,'2:1','Elif, Lâm, Mîm. ');
INSERT INTO "hasan_efendi" VALUES(2,2,'2:2','Ky është Libri në të cilin nuk ka dyshim . Ai është udhërrëfyes për të devotshmit, ');
INSERT INTO "hasan_efendi" VALUES(2,3,'2:3','të cilët besojnë në të fshehtën, falin namazin dhe japin nga ajo që u kemi dhënë Ne;');
INSERT INTO "hasan_efendi" VALUES(2,4,'2:4','dhe të cilët besojnë në atë që të është shpallur ty (o Muhamed), në atë që është shpallur përpara teje dhe që me bindje i besojnë jetës tjetër.');
INSERT INTO "hasan_efendi" VALUES(2,5,'2:5','Ata janë udhëzuar në rrugë të drejtë nga Zoti i tyre dhe pikërisht ata janë të shpëtuarit. ');
INSERT INTO "hasan_efendi" VALUES(2,6,'2:6','Sa për ata që mohojnë, njësoj është, i paralajmërove apo nuk i paralajmërove - ata nuk besojnë.');
INSERT INTO "hasan_efendi" VALUES(2,7,'2:7','Allahu i ka vulosur zemrat e tyre, ndërsa në veshët dhe sytë e tyre kanë mbulesë; ata i pret një dënim i madh.');
INSERT INTO "hasan_efendi" VALUES(2,8,'2:8','Ka disa njerëz që thonë: “Besojmë në Allahun dhe në Ditën e Gjykimit!”, por në të vërtetë ata nuk janë besimtarë.');
INSERT INTO "hasan_efendi" VALUES(2,9,'2:9','Ata përpiqen të mashtrojnë Allahun dhe besimtarët, por në të vërtetë mashtrojnë vetëm vetveten, megjithëse nuk e ndiejnë.');
INSERT INTO "hasan_efendi" VALUES(2,10,'2:10','Në zemrat e tyre ka një sëmundje, të cilën Allahu ua shton. Ata i pret një dënim i dhembshëm, për shkak të hipokrizisë së tyre.');
INSERT INTO "hasan_efendi" VALUES(2,11,'2:11','Kur u thuhet: “Mos bëni çrregullime në tokë!” - ata thonë: “Ne bëjmë vetëm përmirësime në tokë!”');
INSERT INTO "hasan_efendi" VALUES(2,12,'2:12','Por jo! Në të vërtetë, pikërisht ata janë shkaktarët e çrregullimeve, ndonëse nuk e ndiejnë.');
INSERT INTO "hasan_efendi" VALUES(2,13,'2:13','Kur atyre u thuhet: “Besoni edhe ju ashtu siç kanë besuar njerëzit e tjerë!” - ata thonë: “Vallë, a të besojmë edhe ne ashtu siç besojnë mendjelehtët?!” Por jo! Në të vërtetë, pikërisht ata janë mendjelehtët, por nuk e dinë këtë.');
INSERT INTO "hasan_efendi" VALUES(2,14,'2:14','Kur takohen me besimtarët, ata thonë “Besojmë” – por, kur mbesin vetëm me djajtë e tyre, thonë: “Në të vërtetë, ne jemi me ju; ne vetëm po talleshim”.');
INSERT INTO "hasan_efendi" VALUES(2,15,'2:15','Allahu do të tallet me ata dhe do t’i lërë një kohë të gjatë që të bredhin verbërisht në mendjemadhësinë e tyre.');
INSERT INTO "hasan_efendi" VALUES(2,16,'2:16','Pikërisht këta janë ata që kanë këmbyer udhëzimin me humbjen, kështu që tregtia atyre nuk u solli kurrfarë dobie dhe as nuk ishin të udhëzuar në rrugën e drejtë.');
INSERT INTO "hasan_efendi" VALUES(2,17,'2:17','Rasti i tyre shëmbëllen me rastin e atyre që ndezin një zjarr dhe, kur ai ua ndriçon ç’kanë rreth vetes, Allahu ua merr dritën, i lë në errësirë dhe ata nuk shohin asgjë!');
INSERT INTO "hasan_efendi" VALUES(2,18,'2:18','Ata janë të shurdhër, memecë e të verbër, prandaj nuk kthehen në rrugë të drejtë.');
INSERT INTO "hasan_efendi" VALUES(2,19,'2:19','Ose shëmbëllen me ata që, gjatë shtrëngatës nga qielli, shoqëruar me errësirë, bubullimë dhe shkreptimë, i mbyllin veshët me gishtat e tyre, për të mos dëgjuar ushtimën nga frika e vdekjes. Kështu, Allahu i ka nën kontroll mohuesit.');
INSERT INTO "hasan_efendi" VALUES(2,20,'2:20','Shkreptima, pothuaj, ua merr shikimin. Sa herë që ajo ua ndriçon rrugën, ata ecin dhe, kur i kaplon errësira, ndalen. Sikur të donte Allahu, do t’ua merrte dëgjimin dhe shikimin. Pa dyshim, Allahu është i Fuqishëm për çdo gjë. ');
INSERT INTO "hasan_efendi" VALUES(2,21,'2:21','O njerëz! Adhuroni Zotin Tuaj, i Cili ju ka krijuar ju dhe ata që ishin para jush, që të mund të ruheni (nga të këqijat);');
INSERT INTO "hasan_efendi" VALUES(2,22,'2:22','i Cili e ka bërë për ju tokën shtresë dhe qiellin ndërtesë; i Cili prej së larti zbret ujë dhe nëpërmjet tij bën që të rriten fruta si ushqim për ju. Prandaj mos sajoni me vetëdije zota të barabartë me Allahun.');
INSERT INTO "hasan_efendi" VALUES(2,23,'2:23','Nëse ju dyshoni në atë që ia kemi zbritur robit tonë (Muhamedit a.s.), atëherë hartoni një sure të ngjashme me atë që ia kemi shpallur atij! Thirrni në ndihmë edhe dëshmitarët tuaj (që i adhuroni) në vend të Allahut, nëse jeni të sinqertë.');
INSERT INTO "hasan_efendi" VALUES(2,24,'2:24','Nëse nuk mundeni, dhe kurrsesi nuk do të mundeni, atëherë ruajuni zjarrit, lëndë djegëse e të cilit janë njerëzit dhe gurët dhe që është përgatitur për të ndëshkuar mohuesit.');
INSERT INTO "hasan_efendi" VALUES(2,25,'2:25','Ata që besojnë dhe bëjnë vepra të mira, përgëzoji me kopshte të Xhenetit, nëpër të cilat rrjedhin lumenj. Sa herë që atyre t’u jepet si ushqim ndonjë frutë e kopshteve, ata do të thonë: “Këtë e kemi ngrënë edhe përpara”. Por atyre do t’u jepen vetëm fruta të ngjashme me ato të Tokës. Në Xhenet ata do të kenë bashkëshorte të pastra dhe atje do të jetojnë përgjithmonë.');
INSERT INTO "hasan_efendi" VALUES(2,26,'2:26','Allahu nuk ngurron të japë shembull mushkonjën ose diçka më të madhe se ajo. Ata që besojnë, e dinë se kjo është e vërteta nga Zoti i tyre, kurse ata që nuk besojnë, thonë: “Çfarë kërkon të tregojë Allahu me këtë shembull?” Nëpërmjet tij, Ai shumëkënd e lë në humbje e shumëkujt ia ndriçon rrugën e drejtë. Ai i lë në humbje vetëm gjynahqarët e mëdhenj,');
INSERT INTO "hasan_efendi" VALUES(2,27,'2:27','të cilët shkelin besën e Allahut, pasi e patën dhënë, ndërpresin atë që ka urdhëruar Allahu për ta mbajtur lidhur dhe bëjnë ngatërresa në Tokë; ata janë njëmend të shkatërruar.');
INSERT INTO "hasan_efendi" VALUES(2,28,'2:28','Si mund ta mohoni Allahun, ndërkohë që keni qenë të pajetë e Ai ju dha jetën?! Pastaj Ai do t’ju bëjë të vdisni, më pas do t’ju ringjallë e, në fund, do të ktheheni tek Ai?!');
INSERT INTO "hasan_efendi" VALUES(2,29,'2:29','Është Ai që për ju ka krijuar çdo gjë që ka në Tokë. Pastaj, i është drejtuar qiellit dhe e ka rregulluar, duke e bërë atë shtatë qiej. Ai është i Dijshëm për çdo gjë.');
INSERT INTO "hasan_efendi" VALUES(2,30,'2:30','Kur Zoti yt u tha engjëjve: “Unë do të krijoj një mëkëmbës (që do të zbatojë ligjet e Zotit) në tokë”, ata thanë: “A do të vësh atje dikë që do të bëjë çrregullime e do të derdhë gjak në të, ndërkohë që Ne të madhërojmë, të lavdërojmë dhe të lartësojmë ashtu si të takon Ty?!” Ai tha: “Unë di atë që ju nuk e dini”.');
INSERT INTO "hasan_efendi" VALUES(2,31,'2:31','Allahu ia mësoi Ademit emrat e çdo gjëje, pastaj ua paraqiti engjëjve dhe u tha: “Më tregoni emrat e tyre, nëse ajo që thoni është e vërtetë!”');
INSERT INTO "hasan_efendi" VALUES(2,32,'2:32','“Qofsh i lavdëruar! - thanë ata - Ne dimë vetëm atë që na ke mësuar Ti. Në të vërtetë, Ti je i Gjithëdituri, i Urti.”');
INSERT INTO "hasan_efendi" VALUES(2,33,'2:33','“O Adem, - tha Ai - ua trego atyre emrat e gjërave!” Kur ai ua tregoi emrat e tyre, Allahu tha: “A nuk ju kam thënë se vetëm Unë i di fshehtësitë e qiellit e të Tokës dhe vetëm Unë e di çfarë tregoni haptazi dhe çfarë fshihni?!”');
INSERT INTO "hasan_efendi" VALUES(2,34,'2:34','Kur u thamë engjëjve: “Përuluni në sexhde para Ademit”, ata u përulën, përveç Iblisit, i cili nuk deshi, u tregua mendjemadh dhe u bë jobesimtar.');
INSERT INTO "hasan_efendi" VALUES(2,35,'2:35','Ne thamë: “O Adem, jeto ti dhe gruaja jote në Xhenet dhe aty hani sa të doni e kur të doni, por mos iu afroni kësaj peme, se ndryshe do të bëheni të padrejtë”.');
INSERT INTO "hasan_efendi" VALUES(2,36,'2:36','Shejtani i shtyu të gabojnë e i nxori nga lumturia ku ishin. “Zbritni - u thamë Ne - do të jeni armiq të njëri-tjetrit. Në Tokë do të qëndroni, do të keni furnizim e do të jetoni deri në një kohë të caktuar”.');
INSERT INTO "hasan_efendi" VALUES(2,37,'2:37','Ademi mësoi disa fjalë (se si të kërkonte falje) nga Zoti i tij, kështu që Ai ia pranoi pendimin. Sigurisht, Ai i pranon shumë pendimet, Ai është Mëshirëploti.');
INSERT INTO "hasan_efendi" VALUES(2,38,'2:38','Ne u thamë: “Zbritni të gjithë prej Xhenetit! Kur t’ju vijë udhërrëfimi Ynë, ata që do t’i përmbahen atij, nuk do të kenë frikë dhe nuk do të dëshpërohen.');
INSERT INTO "hasan_efendi" VALUES(2,39,'2:39','Kurse ata që i mohojnë dhe i përgënjeshtrojnë shpalljet Tona, do të jenë banorë të Zjarrit, ku do të mbesin gjithmonë.”');
INSERT INTO "hasan_efendi" VALUES(2,40,'2:40','O bijtë e Israilit, kujtoni të mirat që ju kam falur! Përmbushni premtimin që Më keni dhënë, që edhe Unë të përmbush premtimin që ju kam bërë (për t’ju futur në Xhenet). Vetëm prej Meje duhet të frikësoheni.');
INSERT INTO "hasan_efendi" VALUES(2,41,'2:41','Besoni në atë (Kuran) që kam zbritur si vërtetues të Shkrimeve tuaja, e mos u bëni mohuesit e parë të tij! Mos i këmbeni shpalljet e Mia me një vlerë të vogël dhe ruhuni vetëm prej Meje!');
INSERT INTO "hasan_efendi" VALUES(2,42,'2:42','Mos e përzieni të vërtetën me të pavërtetën, dhe të vërtetën mos e fshihni me vetëdije.');
INSERT INTO "hasan_efendi" VALUES(2,43,'2:43','Faleni namazin, jepeni zekatin dhe përkuluni në ruku bashkë me ata që përkulen (në namaz).');
INSERT INTO "hasan_efendi" VALUES(2,44,'2:44','Vallë, a i urdhëroni njerëzit që të bëjnë të mira e veten po e harroni, ju që lexoni Shkrimet?! A nuk arsyetoni?');
INSERT INTO "hasan_efendi" VALUES(2,45,'2:45','Kërkoni ndihmën e Allahut me durim e me namaz! Kjo është njëmend e vështirë, përveçse për të devotshmit,');
INSERT INTO "hasan_efendi" VALUES(2,46,'2:46','që janë të bindur se do të takojnë Zotin e tyre dhe se do të kthehen tek Ai.');
INSERT INTO "hasan_efendi" VALUES(2,47,'2:47','O bijtë e Israilit! Kujtoni të mirat që ju kam dhënë dhe që ju kam ngritur mbi njerëzit e tjerë (të asaj kohe).');
INSERT INTO "hasan_efendi" VALUES(2,48,'2:48','Frikësojuni Ditës kur askush nuk do të mund të bëjë asgjë për askënd dhe, kur prej askujt nuk do të pranohet as ndërmjetësim, as shpërblesë e as që do të ndihmoheni.');
INSERT INTO "hasan_efendi" VALUES(2,49,'2:49','Kujtoni se si ju shpëtuam nga populli i Faraonit, i cili ju shtypte mizorisht: jua therte djemtë e jua linte të gjalla femrat. Kjo ka qenë një sprovë e madhe për ju - nga Zoti juaj.');
INSERT INTO "hasan_efendi" VALUES(2,50,'2:50','Kujtoni se si e çamë detin e ju shpëtuam, ndërsa në praninë tuaj i fundosëm njerëzit e Faraonit.');
INSERT INTO "hasan_efendi" VALUES(2,51,'2:51','Kujtoni kur i caktuam Musait dyzet net (për takimin me të), e pastaj ju adhuruat viçin, duke vepruar si keqbërës!');
INSERT INTO "hasan_efendi" VALUES(2,52,'2:52','Pastaj Ne ju falëm, që të bëheni mirënjohës.');
INSERT INTO "hasan_efendi" VALUES(2,53,'2:53','Kujtoni kur Musait i dhamë Librin dhe Dalluesin , që të udhëzoheni (në rrugën e drejtë)');
INSERT INTO "hasan_efendi" VALUES(2,54,'2:54','dhe, kur ai i tha popullit të vet: “O populli im, ju vetëm vetes i bëtë keq, kur e adhuruat viçin si zot! Prandaj kthehuni të penduar te Krijuesi juaj dhe vritni njëri-tjetrin! Kjo është më mirë për ju në sy të Krijuesit tuaj. Ai jua fali gabimin, sepse Ai i pranon shumë pendimet dhe është Mëshirëploti”. ');
INSERT INTO "hasan_efendi" VALUES(2,55,'2:55','Kujtoni kur i thatë Musait: “O Musa! Ne nuk do të të besojmë ty, derisa të shohim Allahun haptazi!”. Atëherë një rrufe ju goditi, ndërsa ishit duke parë.');
INSERT INTO "hasan_efendi" VALUES(2,56,'2:56','Pas vdekjes suaj, Ne ju ringjallëm, që të bëheni falënderues.');
INSERT INTO "hasan_efendi" VALUES(2,57,'2:57','Pastaj bëmë që retë t’ju bënin hije dhe ju zbritëm manën dhe thëllëza, duke thënë: “Hani nga ushqimet e mira, që ju kemi dhënë!” (Ata nuk ishin mirënjohës dhe kështu) nuk na bënë keq Neve, por i bënë keq vetes.');
INSERT INTO "hasan_efendi" VALUES(2,58,'2:58','Kujtoni kur thamë: “Hyni në këtë qytet dhe hani ç’të doni dhe sa të dëshironi, por, duke hyrë në qytet, përuluni në sexhde dhe thoni: “Falna!” që Ne t’jua falim gabimet. Atyre që bëjnë vepra të mira, Ne ua shtojmë shpërblimin”');
INSERT INTO "hasan_efendi" VALUES(2,59,'2:59','Por keqbërësit e ndryshuan fjalën që iu tha me një fjalë tjetër, prandaj Ne u sollëm atyre që bënë (këtë) padrejtësi një dënim nga qielli për mosbindjen e tyre.');
INSERT INTO "hasan_efendi" VALUES(2,60,'2:60','Kur Musai kërkoi ujë për popullin e tij, Ne i thamë: “Bjeri gurit me shkopin tënd!” Atëherë, nga ai shpërthyen dymbëdhjetë burime, dhe çdo fis e dinte burimin e vet. Ne thamë: “Hani e pini prej begative të Allahut dhe mos bëni të këqija e mbrapshti në Tokë!”.');
INSERT INTO "hasan_efendi" VALUES(2,61,'2:61','Kujtoni kur i thatë: “O Musa! Ne nuk mund të kënaqemi vetëm me një lloj ushqimi, prandaj lutju për ne Zotit tënd të na dhurojë nga ato që jep toka: perime, tranguj, thjerrëza, qepë dhe hudhra!” Ai ju tha: “Vërtet doni të ndërroni atë që është më e mirë me atë që është më e dobët?! Shkoni në qytet, atje do të gjeni atë që kërkoni!” Ata i kaploi poshtërimi dhe përulja dhe merituan zemërimin e Allahut. Kjo ndodhi, sepse ata i mohonin shpalljet e Allahut dhe i vrisnin të dërguarit pa asnjë të drejtë. Kjo ndodhi, sepse ata kundërshtonin dhe kalonin çdo kufi.');
INSERT INTO "hasan_efendi" VALUES(2,62,'2:62','Ata që besojnë (në Kuran), hebrenjtë, të krishterët dhe sabi’inët - kushdo prej tyre që beson Allahun dhe jetën tjetër (sipas Kuranit), dhe bën vepra të mira (sipas Islamit), do të shpërblehen nga Zoti i tyre; ata nuk do të kenë pse të frikësohen apo të pikëllohen!');
INSERT INTO "hasan_efendi" VALUES(2,63,'2:63','Kujtoni (o hebrenj) kur morëm besën tuaj dhe ngritëm mbi ju malin Tur, duke thënë: “Merreni seriozisht atë që ju dhamë dhe mbani në mendje rregullat e saj, në mënyrë që të ruheni nga gjynahet!”');
INSERT INTO "hasan_efendi" VALUES(2,64,'2:64','Por më pas, ju i kthyet shpinën (besëlidhjes) dhe, sikur të mos ishte mirësia e Allahut ndaj jush dhe mëshira e Tij, do të ishit të humbur.');
INSERT INTO "hasan_efendi" VALUES(2,65,'2:65','Sigurisht që ju keni dëgjuar për ata prej jush që shkelën Sabatin dhe të cilëve Ne u thamë: “Bëhuni majmunë të përçmuar e të urryer!”.');
INSERT INTO "hasan_efendi" VALUES(2,66,'2:66','Këtë dënim e bëmë paralajmërim për bashkëkohësit dhe për brezat e tyre pasardhës, si dhe këshillë për ata që i frikësohen Allahut.');
INSERT INTO "hasan_efendi" VALUES(2,67,'2:67','Kujto kur Musai i tha kombit të vet: “Allahu ju urdhëron të therni një lopë!” Ata thanë: “A do të tallesh me ne?” - “Zoti më ruajttë!” - tha Musai - “të bëhem unë nga të paditurit!”.');
INSERT INTO "hasan_efendi" VALUES(2,68,'2:68','Ata thanë: “Lutju Zotit tënd, që të na shpjegojë si duhet të jetë ajo!” Musai tha: “Ajo të jetë një lopë as plakë, as e re, por e mesme; pra, bëni atë që ju është urdhëruar!”');
INSERT INTO "hasan_efendi" VALUES(2,69,'2:69','Ata thanë: “Lutju Zotit tënd që të na shpjegojë çfarë ngjyre të ketë ajo!” Musai tha: “Ai thotë se ajo duhet të jetë lopë me ngjyrë të verdhë të ndezur, të cilën e pëlqejnë shikuesit”.');
INSERT INTO "hasan_efendi" VALUES(2,70,'2:70','Ata thanë: “Lutju Zotit tënd, që të na shpjegojë çfarë lloji të jetë, sepse lopët të gjitha na duken njësoj e ne, në dashtë Zoti, do të udhëzohemi në rrugën e drejtë”.');
INSERT INTO "hasan_efendi" VALUES(2,71,'2:71','Musai tha: “Ai thotë se lopa s’duhet të jetë e lodhur nga lërimi apo nga ujitja e tokës, por të jetë e shëndoshë dhe pa asnjë të metë”. Ata thanë: “Tani na the të vërtetën”. Kështu, ata e therën lopën, por desh e lanë pa flijuar.');
INSERT INTO "hasan_efendi" VALUES(2,72,'2:72','Dhe, pasi vratë një njeri e u grindët me njëri-tjetrin në lidhje me të, Allahu e nxori në shesh atë që fshihnit ju.');
INSERT INTO "hasan_efendi" VALUES(2,73,'2:73','Ne ju thamë: “Prekeni të vrarin me një copë të lopës së flijuar!” Dhe kështu, Allahu do t’i ngjallë të vdekurit dhe do t’ju tregojë mrekullitë e veta, që ju të kuptoni.');
INSERT INTO "hasan_efendi" VALUES(2,74,'2:74','Por, pas kësaj, zemrat tuaja u forcuan e u bënë si guri, madje edhe më të forta. Në të vërtetë, ka gurë nga të cilët burojnë lumenj, ka të atillë që çahen e nga të cilët rrjedh uji, madje ka edhe syresh që rrëzohen nga frika e Allahut. Allahu nuk është i pavëmendshëm ndaj veprimeve tuaja.');
INSERT INTO "hasan_efendi" VALUES(2,75,'2:75','Vërtet shpresoni ju (besimtarë) se ata (hebrenjtë) do t’ju besonin juve, ndërkohë që disa prej tyre i dëgjonin fjalët e Allahut dhe, pasi i kuptonin, me vetëdije i shtrembëronin ato?!');
INSERT INTO "hasan_efendi" VALUES(2,76,'2:76','Kur takohen me besimtarët, ata thonë: “Ne besojmë”, e kur ngelin vetëm me njëri-tjetrin, ata thonë: “A u tregoni atyre, atë që jua ka shpallur Allahu (në Teurat), për ta përdorur si argument kundër jush te Zoti juaj? A nuk po mendoni?”');
INSERT INTO "hasan_efendi" VALUES(2,77,'2:77','A nuk e dinë ata se Allahu di çfarë fshehin dhe çfarë shpallin?!');
INSERT INTO "hasan_efendi" VALUES(2,78,'2:78','Disa prej atyre janë analfabetë, nuk e njohin Librin, përveç disa trillimeve dhe flasin vetëm me hamendje.');
INSERT INTO "hasan_efendi" VALUES(2,79,'2:79','Mjerë ata që me duart e tyre shkruajnë Librin, e pastaj thonë: “Kjo është prej Allahut” që për këtë të kenë ndonjë dobi të vogël (materiale). Mjerë ata për çfarë kanë shkruar me duart e tyre dhe mjerë ata për çfarë kanë fituar!');
INSERT INTO "hasan_efendi" VALUES(2,80,'2:80','Ata thonë: “Zjarri do të na prekë vetëm ca ditë të numëruara”. Thuaju (o Muhamed): “A keni marrë për këtë ndonjë zotim prej Allahut, kështu që Ai nuk do ta shkelë kurrë zotimin e vet (dhe do t’ju dënojë pak) apo po shpifni kundër Allahut gjëra që nuk i dini?”');
INSERT INTO "hasan_efendi" VALUES(2,81,'2:81','Nuk është kështu si thoni ju! Ata që bëjnë vepra të këqija dhe mbulohen nga fajet e veta, do të jenë banorë të Zjarrit (të Xhehenemit), ku do të qëndrojnë përgjithmonë.');
INSERT INTO "hasan_efendi" VALUES(2,82,'2:82','Kurse ata që besojnë e bëjnë vepra të mira, do të jenë banorë të Xhenetit, ku do të qëndrojnë përgjithmonë. ');
INSERT INTO "hasan_efendi" VALUES(2,83,'2:83','Kujtoni kur Ne morëm besën tuaj, o bijtë e Israilit, se do të adhuroni vetëm Allahun, do të silleni mirë me prindërit, të afërmit, jetimët dhe të varfrit, do t’u thoni fjalë të mira njerëzve, do të falni namazin e do të jepni zekatin. Mirëpo, më pas, ju ia kthyet shpinën (besëlidhjes), përveç një pakice prej jush, duke iu shmangur (edhe tani).');
INSERT INTO "hasan_efendi" VALUES(2,84,'2:84','Dhe kujtoni kur nga ju morëm besën që të mos derdhni gjakun e njëri-tjetrit dhe që të mos e dëboni njëri-tjetrin nga banesat tuaja! Ju e pranuat këtë dhe dolët dëshmitarë.');
INSERT INTO "hasan_efendi" VALUES(2,85,'2:85','Mirëpo ja tek jeni, duke vrarë farefisin tuaj, duke i dëbuar disa syresh nga shtëpitë e veta dhe duke ndihmuar njëri-tjetrin kundër tyre, fajësisht dhe armiqësisht. Dhe, kur ju vijnë të robëruar, ju i shpëtoni nga robëria me të holla, ndërkohë që me marrëveshje e keni të ndaluar t’i dëboni. Mos vallë, një pjesë të Librit (Teuratit) e besoni, kurse pjesën tjetër e mohoni?! Cilido prej jush që vepron kështu, do të ndëshkohet me poshtërim në këtë jetë, ndërsa në Ditën e Kiametit do të dërgohet në dënimin më të ashpër. Allahu nuk është i pavëmendshëm ndaj veprimeve tuaja.');
INSERT INTO "hasan_efendi" VALUES(2,86,'2:86','Këta janë ata që jetën e kësaj bote e kanë blerë me jetën e ardhshme. Atyre nuk do t’u lehtësohet dënimi dhe askush nuk do t’u vijë në ndihmë.');
INSERT INTO "hasan_efendi" VALUES(2,87,'2:87','Ne i dhamë Musait Librin dhe pas tij çuam të dërguarit njërin pas tjetrit. Ne i dhamë Isait, të birit të Merjemes, mrekulli të qarta dhe e mbështetëm atë me Shpirtin e Shenjtërisë (Xhebrailin a.s.). Sa herë që ndonjë i dërguar ju sillte atë që nuk ju pëlqente, ju kapardiseshit, prandaj disa i quajtët gënjeshtarë e disa i vratë.');
INSERT INTO "hasan_efendi" VALUES(2,88,'2:88','Ata thonë: “Zemrat tona janë të vulosura”. Jo, s’është ashtu, por ata i ka mallkuar Allahu për shkak të mohimit të tyre, prandaj shumë pak prej tyre besojnë.');
INSERT INTO "hasan_efendi" VALUES(2,89,'2:89','Dhe, kur një Libër që vërtetonte Shkrimet e tyre, u erdhi atyre nga Allahu, ndonëse përpara luteshin për ndihmë kundër jobesimtarëve, pra, kur u erdhi ajo që e dinin se është e vërtetë, ata e mohuan atë! Prandaj mallkimi i Allahut qoftë mbi mohuesit!');
INSERT INTO "hasan_efendi" VALUES(2,90,'2:90','Ata e shitën veten keq, kur nuk besuan në atë që shpalli Allahu, nga zilia se Allahu ia zbret shpalljen kujt të dëshirojë nga robtë e Vet. Dhe kështu tërhoqën mbi vete zemërim përmbi zemërim! Sigurisht që mohuesit i pret një dënim poshtërues.');
INSERT INTO "hasan_efendi" VALUES(2,91,'2:91','Kur atyre u thuhet: “Besoni në atë që ka shpallur Allahu!” ata përgjigjen: “Ne besojmë në atë që na është shpallur neve”, duke mos besuar në atë që është zbritur më pas, ndonëse ajo është e Vërteta që pohon Shkrimet e tyre. Thuaju atyre: “Nëse ishit besimtarë, përse i vratë profetët e Allahut, atëherë kur ju erdhën?!”');
INSERT INTO "hasan_efendi" VALUES(2,92,'2:92','Musai ju solli shenja të qarta, por gjatë mungesës së tij, ju adhuruat viçin si zot, prandaj u bëtë fajtorë të mëdhenj.');
INSERT INTO "hasan_efendi" VALUES(2,93,'2:93','Kur morëm besën tuaj dhe ngritëm mbi ju malin e Turit, thamë: “Merreni seriozisht atë që ju kemi dhënë dhe dëgjoni!” Ju thatë: “Ne dëgjuam, por kundërshtojmë!” Për shkak të mohimit, atyre u ishte rrënjosur në zemra dashuria (dhe adhurimi) për viçin. Thuaju: “Nëse jeni vërtet besimtarë, sa gjë të keqe po ju urdhëruaka besimi juaj!”');
INSERT INTO "hasan_efendi" VALUES(2,94,'2:94','Thuaju: “Nëse Shtëpia e jetës së përtejme (Xheneti) tek Allahu është vetëm për ju e jo për njerëzit e tjerë, atëherë kërkoni vdekjen, nëse ajo që thoni është e vërtetë”.');
INSERT INTO "hasan_efendi" VALUES(2,95,'2:95','Por ata kurrsesi nuk e dëshirojnë vdekjen për shkak të të këqijave që kanë bërë! Allahu i njeh mirë keqbërësit.');
INSERT INTO "hasan_efendi" VALUES(2,96,'2:96','Dhe, vërtet, do t’i gjesh ata më lakmues për të jetuar sesa njerëzit e tjerë, madje, edhe se paganët. Secili nga ata dëshiron të jetojë një mijë vjet; por edhe nëse jeton aq, nuk do të mund t’i ikë dënimit! Allahu i sheh punët e tyre të këqija.');
INSERT INTO "hasan_efendi" VALUES(2,97,'2:97','Thuaju (o Muhamed) atyre që janë armiq të Xhebrailit, se ai e ka zbritur Kuranin në zemrën tënde me lejen e Allahut, si vërtetues të librave të shenjtë të mëparshëm dhe si udhërrëfyes e lajm të mirë për besimtarët.');
INSERT INTO "hasan_efendi" VALUES(2,98,'2:98','Kush është armik i Allahut, i engjëjve të Tij, i të dërguarve të Tij, i Xhebrailit dhe Mikailit, ta dijë se Allahu është armik i jobesimtarëve.');
INSERT INTO "hasan_efendi" VALUES(2,99,'2:99','Ne të kemi zbritur ty shpallje e shenja të qarta, të cilave vetëm njerëzit e këqij nuk duan t’u besojnë.');
INSERT INTO "hasan_efendi" VALUES(2,100,'2:100','A thua çdoherë që japin ndonjë premtim, disa prej tyre e flakin tej atë? Jo, por shumica e tyre nuk besojnë.');
INSERT INTO "hasan_efendi" VALUES(2,101,'2:101','Tani që atyre u erdhi një i Dërguar nga Allahu, vërtetues i Shkrimeve që kanë me vete, një grup prej atyre, të cilëve iu dhanë Shkrimet, e hodhën pas shpine Librin e Allahut, gjoja se nuk dinin gjë për atë,');
INSERT INTO "hasan_efendi" VALUES(2,102,'2:102','dhe shkuan pas shpifjeve që bënin djajtë kundër mbretërisë së Sulejmanit. E Sulejmani nuk ishte mohues, por djajtë ishin mohues. Ata u mësonin njerëzve magjinë dhe dijeninë që u ishte dërguar dy engjëjve në Babiloni - Harutit e Marutit. Këta engjëj nuk i mësonin ndokujt magji, pa i thënë: “Ne jemi dërguar për t’ju vënë në provë, andaj ti mos u bëj mohues (duke mësuar apo ushtruar magjinë)!” Dhe njerëzit mësuan nga këta të dy se si t’i ndajnë bashkëshortët. Por magjistarët nuk mund t’i bëjnë dëm askujt pa lejen e Allahut. E megjithatë, njerëzit mësojnë ato gjëra që i dëmtojnë e nuk u bëjnë dobi. Në të vërtetë, ata e dinin se ai që e përvetësonte këtë mjeshtëri, nuk do të kishte kurrfarë të mire në jetën tjetër. Eh, sa e keqe është ajo për të cilën shitën shpirtrat e veta! Ah, sikur ta dinin ata!');
INSERT INTO "hasan_efendi" VALUES(2,103,'2:103','Sikur ata të besonin dhe të ruheshin (nga gjynahet), shpërblimi i Allahut do të ishte më i mirë. Ah, sikur ta dinin ata!');
INSERT INTO "hasan_efendi" VALUES(2,104,'2:104','O besimtarë! Mos thoni: “Rá‘iná” , por thoni: “Undhurná! - “Na shiko!” dhe dëgjoni! Për mohuesit ka dënim pikëllues.');
INSERT INTO "hasan_efendi" VALUES(2,105,'2:105','Ata që mohojnë, si ithtarët e Librit , ashtu edhe paganët, nuk dëshirojnë që t’ju zbresë ndonjë e mirë nga Zoti juaj. Por Allahu ia dhuron mëshirën e Vet kujt të dojë. Allahu zotëron mirësi të madhe.');
INSERT INTO "hasan_efendi" VALUES(2,106,'2:106','Për çdo varg (Kurani) që Ne e shfuqizojmë apo që e bëjmë të harrohet, Ne sjellim ndonjë tjetër më të mirë ose të ngjashëm me të. A nuk e di ti se Allahu është i Fuqishëm për (të bërë) çdo gjë?!');
INSERT INTO "hasan_efendi" VALUES(2,107,'2:107','A nuk e di ti se vetëm Allahut i përket sundimi i qiejve dhe i Tokës? Përveç Allahut ju nuk keni tjetër mbrojtës e as ndihmës.');
INSERT INTO "hasan_efendi" VALUES(2,108,'2:108','A mos doni të pyesni të Dërguarin tuaj, ashtu siç u pyet Musai më parë? Cilido që e ndërron besimin me mohim, pa dyshim që e ka humbur rrugën e drejtë.');
INSERT INTO "hasan_efendi" VALUES(2,109,'2:109','Shumë nga ithtarët e Librit, prej zilisë që kanë në vete, dëshirojnë që t’ju kthejnë nga besimi në mosbesim, tani që e vërteta është e qartë për ata. Por ju falini ata dhe lërini, derisa të arrijë urdhri i Allahut. Vërtet, Allahu është i Fuqishëm për çdo gjë.');
INSERT INTO "hasan_efendi" VALUES(2,110,'2:110','Faleni namazin dhe jepeni zekatin! Çfarëdo të mire që të bëni për shpirtin tuaj, do ta gjeni tek Allahu. Në të vërtetë, Ai sheh mirë gjithçka që bëni.');
INSERT INTO "hasan_efendi" VALUES(2,111,'2:111','Ata thonë: “Askush nuk do të hyjë në Xhenet, përveç hebrenjve ose të krishterëve”. Këto janë dëshirat e tyre boshe! Thuaju (o Muhamed): “Nëse është e vërtetë ajo që thoni, atëherë sillni provën tuaj!”');
INSERT INTO "hasan_efendi" VALUES(2,112,'2:112','Vërtet, ata që i përulen Allahut dhe bëjnë vepra të mira, do ta kenë shpërblimin te Zoti i tyre, nuk do të frikësohen e as do të pikëllohen.');
INSERT INTO "hasan_efendi" VALUES(2,113,'2:113','Hebrenjtë thonë: “Të krishterët nuk kanë kurrfarë udhërrëfimi ku të mbështeten!” Edhe të krishterët thonë: “Hebrenjtë nuk kanë kurrfarë urdhërrëfimi ku të mbështeten!” Ndërkohë, të dyja palët lexojnë të njëjtin Libër. Po kështu, edhe ata që nuk dinë, thonë të njëjtat fjalë. Allahu do t’i gjykojë kundërshtitë e tyre në Ditën e Kiametit.');
INSERT INTO "hasan_efendi" VALUES(2,114,'2:114','Kush është më fajtor se ai që pengon të përmendet nëpër xhamitë e Allahut Emri i Tij dhe përpiqet për shkatërrimin e tyre?! Ata nuk duhet të hyjnë në to, përveçse të frikësuar. Për ata, në këtë jetë ka poshtërim, kurse në jetën tjetër dënim të madh.');
INSERT INTO "hasan_efendi" VALUES(2,115,'2:115','Të Allahut janë Lindja dhe Perëndimi, pra, ngado që të ktheheni është Fytyra (ana) e Allahut. Vërtet, Allahu është Gjithëpërfshirës dhe i Gjithëdijshëm. ');
INSERT INTO "hasan_efendi" VALUES(2,116,'2:116','Ata thonë: “Allahu ka bërë një fëmijë”. I Lavdishëm dhe i Lartësuar qoftë Ai! Përkundrazi, Atij i përket çdo gjë që gjendet në qiej e në Tokë dhe Atij i nënshtrohet gjithçka.');
INSERT INTO "hasan_efendi" VALUES(2,117,'2:117','Ai është Krijuesi i qiejve dhe i Tokës, pa shembull të mëparshëm dhe, kur vendos për diçka, vetëm thotë për të: “Bëhu!” - dhe ajo bëhet.');
INSERT INTO "hasan_efendi" VALUES(2,118,'2:118','Kurse të paditurit thonë: “Përse Allahu nuk flet me ne ose së paku të na sillte ndonjë shenjë!” Njësoj thonin edhe ata para tyre; zemrat e tyre i përngjajnë njëra-tjetrës! Ndërkohë, Ne ua kemi treguar qartë shenjat Tona atyre që besojnë bindshëm.');
INSERT INTO "hasan_efendi" VALUES(2,119,'2:119','Njëmend, Ne të kemi dërguar ty (Muhamed) si sjellës të lajmit të mirë dhe paralajmërues; ti nuk do të pyetesh për banorët e Zjarrit flakërues.');
INSERT INTO "hasan_efendi" VALUES(2,120,'2:120','As hebrenjtë, as të krishterët nuk do të jenë të kënaqur me ty, derisa të pranosh besimin e tyre. Thuaju: “Vetëm udhërrëfimi i Allahut është i drejtë!” E, në qoftë se pas diturisë që të ka zbritur, ti ndjek dëshirën e atyre, atëherë askush nuk do të të ndihmonte apo mbronte nga dënimi i Allahut.');
INSERT INTO "hasan_efendi" VALUES(2,121,'2:121','Ata që u kemi dhënë Librin dhe e lexojnë ashtu siç është shpallur, ata besojnë vërtet në të. Kurse ata që nuk besojnë në të, pa dyshim, janë të humbur.');
INSERT INTO "hasan_efendi" VALUES(2,122,'2:122','O bijtë e Israilit, kujtoni të mirat që ju dhashë dhe që ju ngrita mbi gjithë njerëzit e tjerë (të asaj kohe)!');
INSERT INTO "hasan_efendi" VALUES(2,123,'2:123','Frikësojuni Ditës kur askush nuk do të mund të bëjë asgjë për askënd dhe, kur prej askujt, nuk do të pranohet as ndërmjetësim, as shpërblesë dhe as që do të ndihmoheni.');
INSERT INTO "hasan_efendi" VALUES(2,124,'2:124','Kur Allahu e provoi Ibrahimin me disa urdhërime, të cilat ai i plotësoi, Allahu i tha: “Unë do të të bëj ty imam (prijës) të njerëzve!” - “Po pasardhësit e mi?” - pyeti ai. Allahu tha: “Premtimi Im nuk i përfshin të padrejtët.”');
INSERT INTO "hasan_efendi" VALUES(2,125,'2:125','Kujtoni kur Ne e bëmë Qabenë vend grumbullimi dhe sigurie për njerëzit, duke thënë “Bëjeni vendin e Ibrahimit si vend faljeje!” Ne i urdhëruam Ibrahimin dhe Ismailin: “Pastrojeni Shtëpinë Time (Qabenë) për ata që e vizitojnë, që rrinë aty për adhurim dhe që falen duke u përkulur dhe përulur”.');
INSERT INTO "hasan_efendi" VALUES(2,126,'2:126','Kur Ibrahimi u lut: “Zoti Im, bëje këtë vend qytet të sigurt dhe furnizoji me prodhime banorët e tij, ata që besojnë në Allahun dhe në jetën tjetër!” - Allahu tha: “Atë që nuk beson, për pak kohë do ta bëj që të kënaqet e pastaj do ta hedh në dënimin e zjarrit.” Eh, sa vendbanim i keq që është ai!');
INSERT INTO "hasan_efendi" VALUES(2,127,'2:127','Kur Ibrahimi dhe Ismaili ngritën themelet e Qabes, u lutën: “O Zoti ynë! Pranoje prej nesh (këtë vepër)! Me të vërtetë, Ti je Ai që dëgjon dhe di gjithçka!');
INSERT INTO "hasan_efendi" VALUES(2,128,'2:128','O Zoti ynë! Na bëj të përulur ndaj Teje! Bëj që edhe pasardhësit tanë të jenë të përulur ndaj Teje! Tregona ritet e Haxhit dhe pranoje pendimin tonë, se, pa dyshim, Ti je Pranuesi i pendimit, Mëshirëploti!');
INSERT INTO "hasan_efendi" VALUES(2,129,'2:129','O Zoti ynë! Dërgo tek ata një të dërguar nga gjiri i tyre, që t’u lexojë shpalljet e Tua, t’ua mësojë Librin dhe Urtësinë (rregullat e fesë) e t’i pastrojë (nga gjynahet)! Vërtet, Ti je i Plotfuqishmi dhe i Urti!”');
INSERT INTO "hasan_efendi" VALUES(2,130,'2:130','Fesë së Ibrahimit i shmanget vetëm ai që e nënçmon vetveten. Ne e zgjodhëm atë në këtë botë, kurse në botën tjetër ai do të jetë ndër të mirët.');
INSERT INTO "hasan_efendi" VALUES(2,131,'2:131','Kur Zoti i tij i tha: “Përulu!” - ai u përgjigj: “Iu përula Zotit të gjithësisë!”');
INSERT INTO "hasan_efendi" VALUES(2,132,'2:132','Ibrahimi ua dha këtë porosi fëmijëve të tij; po ashtu edhe Jakubi: “O bijtë e mi! Pa dyshim, Allahu ka zgjedhur për ju fenë e vërtetë, prandaj mos vdisni ndryshe veçse duke qenë myslimanë të nënshtruar ndaj Atij!”');
INSERT INTO "hasan_efendi" VALUES(2,133,'2:133','A ishit ju të pranishëm kur Jakubit iu afrua vdekja dhe i pyeti të bijtë: “Çfarë do të adhuroni pas meje?” - Ata u përgjigjën: “Ne do të adhurojmë Zotin tënd, që është edhe Zoti i etërve të tu: Ibrahimit, Ismailit dhe Is’hakut, një Zot të vetëm. Ne Atij i nënshtrohemi!”');
INSERT INTO "hasan_efendi" VALUES(2,134,'2:134','Ata njerëz tashmë kanë kaluar. Ata do të kenë çfarë kanë merituar dhe ju do të keni atë që meritoni e nuk do të pyeteni për veprat e tyre.');
INSERT INTO "hasan_efendi" VALUES(2,135,'2:135','Ata thonë: “Bëhuni hebrenj ose të krishterë, që të jeni në rrugë të drejtë!” Thuaju: “Në asnjë mënyrë. Ne jemi të fesë së pastër të Ibrahimit, i cili nuk ka qenë idhujtar”.');
INSERT INTO "hasan_efendi" VALUES(2,136,'2:136','Thoni: “Ne besojmë në Allahun, në atë që na është shpallur neve (Kuranin), në atë që i është shpallur Ibrahimit, Ismailit, Is’hakut, Jakubit dhe Esbatëve (bijve të Jakubit), në atë që i është dhënë Musait e Isait dhe në atë që u është dhënë profetëve nga Zoti i tyre. Ne nuk bëjmë kurrfarë dallimi midis tyre dhe vetëm Allahut i përulemi”.');
INSERT INTO "hasan_efendi" VALUES(2,137,'2:137','Nëse ata besojnë në atë që besoni edhe ju, atëherë janë në rrugën e drejtë. E, nëse ia kthejnë shpinën asaj, atëherë sigurisht që do të përçahen. Por Allahu të mjafton ty (Muhamed si mbrojtës) kundër atyre. Ai i dëgjon dhe i di të gjitha.');
INSERT INTO "hasan_efendi" VALUES(2,138,'2:138','Thoni: “Kjo fe është “ngjyra” që na ka dhënë Allahu! S’ka “ngjyrë” më të bukur se ajo që jep Allahu! Ne vetëm Atë adhurojmë!”');
INSERT INTO "hasan_efendi" VALUES(2,139,'2:139','Thuaj: “A mos doni të haheni me ne për Allahun, kur Ai është Zoti ynë dhe Zoti juaj?! Ne jemi përgjegjës për veprat tona, kurse ju për tuajat. Ne vetëm Atij i përkushtohemi.”');
INSERT INTO "hasan_efendi" VALUES(2,140,'2:140','A po thoni se Ibrahimi, Ismaili, Is’haku, Jakubi dhe Esbatët kanë qenë çifutë (me fe), ose të krishterë? Thuaju: “A e dini më mirë ju apo Allahu? Kush është më keqbërës se ai që fsheh të vërtetën e ardhur prej Allahut? Por Allahu nuk është i pavëmendshëm ndaj veprave që punoni ju”.');
INSERT INTO "hasan_efendi" VALUES(2,141,'2:141','Ata njerëz tashmë kanë kaluar. Ata do të kenë çfarë kanë merituar dhe ju do të keni atë që meritoni e nuk do të pyeteni për veprat e tyre.');
INSERT INTO "hasan_efendi" VALUES(2,142,'2:142','Disa mendjelehtë (nga ithtarët e Librit) do të thonë: “Ç’i ktheu ata prej kiblës së tyre?” Thuaju: “Të Allahut janë Lindja dhe Perëndimi. Ai udhëzon kë të dojë në rrugën e drejtë”.');
INSERT INTO "hasan_efendi" VALUES(2,143,'2:143','Kështu, Ne ju kemi bërë një bashkësi të drejtë, që të bëheni dëshmitarë përmbi njerëzit dhe që i Dërguari të jetë dëshmitar përmbi ju. Ne nuk e kemi bërë kthimin tënd nga Qabeja (o Muhamed) për tjetër arsye, por vetëm për t’i dalluar ata që i shkojnë pas të Dërguarit, nga ata që kthehen nga thembrat (që tërhiqen nga besimi tek ai). Kjo provë ka qenë e rëndë (për njerëzit), por jo për ata që Allahu i ka udhëzuar. Dijeni se Allahu nuk ua humb besimin tuaj (d.m.th. namazin që keni falur nga Kudsi). Në të vërtetë, Allahu është Përdëllimtar dhe i Mëshirshëm me njerëzit.');
INSERT INTO "hasan_efendi" VALUES(2,144,'2:144','Ne të pamë ty (Muhamed) që vazhdimisht drejtoje shikimin nga qielli. Mirë pra, Ne do të të kthejmë ty nga kibla që të kënaq ty. Prandaj, ktheje fytyrën tënde nga Xhamia e Shenjtë (Qabeja)! Dhe kudo që të gjendeni, kthejeni fytyrën tuaj nga ajo anë! Pa dyshim, ata që u është dhënë Libri (Teurati), e dinë se drejtimi nga Qabeja është e vërteta prej Zotit të tyre. Allahu nuk është i pakujdesshëm ndaj punëve që ata bëjnë.');
INSERT INTO "hasan_efendi" VALUES(2,145,'2:145','Edhe sikur t’u sillje të gjitha provat atyre që u është dhënë Libri, ata prapëseprapë nuk do ta pranonin kiblën tënde, por edhe ti nuk do ta pranosh kiblën e tyre dhe askush nga ata nuk do të pranonte kiblën e njëri-tjetrit. E, nëse ti, pasi të është zbritur dituria, do të ndiqje dëshirat e tyre, atëherë pa dyshim që do të ishe një nga keqbërësit.');
INSERT INTO "hasan_efendi" VALUES(2,146,'2:146','Ata që u kemi dhënë Librin, e njohin atë (Profetin a.s.), ashtu siç i njohin bijtë e tyre, por disa prej tyre e fshehin të vërtetën, duke e ditur mirë.');
INSERT INTO "hasan_efendi" VALUES(2,147,'2:147','E vërteta është prej Zotit Tënd; pra, kurrsesi mos u bëj nga ata që dyshojnë.');
INSERT INTO "hasan_efendi" VALUES(2,148,'2:148','Çdokush ka një drejtim nga i cili kthehet. Pra, bëni garë me njëri-tjetrin për punë të mira. Kudo që të gjendeni, Allahu do t’ju mbledhë të gjithëve; Ai është i Fuqishëm për çdo gjë.');
INSERT INTO "hasan_efendi" VALUES(2,149,'2:149','Nga cilido vend që të dalësh, ktheje fytyrën nga Xhamia e Shenjtë. Kjo është e vërteta që vjen nga Zoti yt. Allahu nuk është i pakujdesshëm ndaj veprave që ju bëni.');
INSERT INTO "hasan_efendi" VALUES(2,150,'2:150','Nga cilido vend që të dalësh, ktheje fytyrën nga Xhamia e Shenjtë. Kudo që të ndodheni, ktheni fytyrat tuaja nga ajo anë, që njerëzia të mos ketë ç’të thotë kundër jush, përveç keqbërësve - por atyre mos iu frikësoni, frikësohuni vetëm prej Meje -, që të plotësoj mirësinë Time ndaj jush dhe që ju të shkoni në udhë të drejtë.');
INSERT INTO "hasan_efendi" VALUES(2,151,'2:151','Po kështu, (për të plotësuar mirësinë Time ndaj jush) ju kam sjellë një të Dërguar, nga mesi juaj, i cili ju lexon shpalljet Tona, ju pastron (nga gjynahet), ju mëson Librin (Kuranin) e Urtësinë (rregullat e fesë) dhe ju mëson ato gjëra që nuk i keni ditur.');
INSERT INTO "hasan_efendi" VALUES(2,152,'2:152','Prandaj, më kujtoni Mua (me namaz e lutje) që Unë t’ju kujtoj (me shpërblime) dhe falënderomëni Mua (duke m’u bindur) e mos i mohoni dhuntitë e Mia (me mosbindje)!');
INSERT INTO "hasan_efendi" VALUES(2,153,'2:153','O besimtarë! Kërkoni ndihmë për veten nëpërmjet durimit dhe namazit! Në të vërtetë, Allahu është me të duruarit.');
INSERT INTO "hasan_efendi" VALUES(2,154,'2:154','Dhe mos thoni për ata që vriten në rrugën e Allahut se janë të vdekur! Jo! Ata janë të gjallë, ndonëse ju nuk e ndieni!');
INSERT INTO "hasan_efendi" VALUES(2,155,'2:155','Sigurisht që Ne do t’ju provojmë me frikë dhe uri, me dëmtim të pasurisë, të njerëzve dhe të të lashtave! Prandaj, përgëzoji të duruarit,');
INSERT INTO "hasan_efendi" VALUES(2,156,'2:156','të cilët, kur i godet ndonjë fatkeqësi thonë: “Të Allahut jemi dhe vetëm tek Ai do të kthehemi!”');
INSERT INTO "hasan_efendi" VALUES(2,157,'2:157','Ata do të shpërblehen me bekim dhe mëshirë nga Zoti i tyre; ata janë në rrugën e drejtë!');
INSERT INTO "hasan_efendi" VALUES(2,158,'2:158','Kodrinat Safa dhe Merva (në Mekë) janë pjesë e Simboleve të Allahut, prandaj ai që viziton Qaben për haxh ose për umre , s’ka kurrfarë gjynahu nëse sillet mes tyre. Kush bën vepra të mira vullnetare, ta dijë se Allahu është njëmend Falënderues dhe i Gjithëdijshëm.');
INSERT INTO "hasan_efendi" VALUES(2,159,'2:159','Me të vërtetë, ata që fshehin shpalljet Tona të qarta, pasi ua kemi shpjeguar njerëzve në Libër (Teurat), do të mallkohen nga Allahu dhe do të mallkohen nga ata (engjëj e njerëz) që janë ngarkuar të mallkojnë.');
INSERT INTO "hasan_efendi" VALUES(2,160,'2:160','Mirëpo atyre që pendohen, përmirësohen dhe tregojnë atë që kishin fshehur, Unë ua pranoj pendimin, sepse Unë jam Pranuesi i pendimeve dhe Mëshirëploti.');
INSERT INTO "hasan_efendi" VALUES(2,161,'2:161','Ata që nuk besojnë dhe vdesin duke qenë jobesimtarë, do të mallkohen nga Allahu, engjëjt dhe të gjithë njerëzit.');
INSERT INTO "hasan_efendi" VALUES(2,162,'2:162','Ata do të jenë gjithmonë të mallkuar, nuk do t’u lehtësohet dënimi dhe nuk do t’u jepet afat.');
INSERT INTO "hasan_efendi" VALUES(2,163,'2:163','Zoti juaj është një Zot i vetëm! S’ka zot tjetër (të denjë për adhurim), përveç Atij, të Gjithëmëshirshmit, Mëshirëplotit!');
INSERT INTO "hasan_efendi" VALUES(2,164,'2:164','Me të vërtetë, në krijimin e qiejve dhe të Tokës, në këmbimin e natës me ditën, në anijet që lundrojnë nëpër det për t’u sjellë dobi njerëzve, në ujin që Allahu zbret prej qiellit, duke ngjallur përmes tij tokën e vdekur dhe duke shpërndarë gjithfarë gjallesash, në lëvizjen e erërave dhe në retë që qëndrojnë midis qiellit e Tokës, pra, në të gjitha këto, sigurisht që ka shenja për njerëzit me intelekt.');
INSERT INTO "hasan_efendi" VALUES(2,165,'2:165','E megjithatë, disa njerëz zgjedhin (për të adhuruar) në vend të Allahut (zota) të tjerë, (duke i konsideruar) si të barabartë me Atë dhe duke i dashur siç duhet Allahu. Por ata që besojnë, e duan shumë më tepër Allahun (se sa ç’i duan idhujtarët idhujt e tyre). Sikur keqbërësit të shihnin që tani çastin kur të përballen me ndëshkimin, do ta kuptonin se e tërë fuqia i përket Allahut dhe se dënimi i Tij është fort i ashpër.');
INSERT INTO "hasan_efendi" VALUES(2,166,'2:166','Ah, sikur ta shihnin çastin kur të adhuruarit do t’i braktisin adhuruesit e tyre, kur të gjithë do të shohin dënimin e midis tyre do të këputet çdo lidhje.');
INSERT INTO "hasan_efendi" VALUES(2,167,'2:167','Atëherë adhuruesit do të thonë: “Ah, sikur të ktheheshim edhe një herë (në jetën e Tokës) e t’i braktisnim ata , ashtu siç na braktisën ne!” Kështu, Allahu do t’ua tregojë veprat e tyre si burim hidhërimi për ta dhe ata s’kanë për të dalë kurrë nga zjarri.');
INSERT INTO "hasan_efendi" VALUES(2,168,'2:168','O njerëz! Ushqehuni me të gjitha ato gjëra të lejuara e të mira që gjenden në Tokë dhe mos ndiqni gjurmët e djallit, sepse ai është njëmend armiku juaj i hapur.');
INSERT INTO "hasan_efendi" VALUES(2,169,'2:169','Ai ju nxit vetëm në të këqija, në vepra të turpshme dhe të flisni për Allahun gjëra që nuk i dini.');
INSERT INTO "hasan_efendi" VALUES(2,170,'2:170','Kur atyre u thuhet: “Ndiqni atë që ka shpallur Allahu!” ata përgjigjen: “Jo! Ne do të ndjekim atë udhë në të cilën i kemi gjetur të parët tanë” - Vallë, edhe atëherë kur të parët e tyre nuk kuptonin gjë e nuk ishin të udhëzuar?!');
INSERT INTO "hasan_efendi" VALUES(2,171,'2:171','Mohuesit i shëmbëllejnë kafshëve, të cilat, kur i thërret dikush, dëgjojnë vetëm thirrje e zë, por s’kuptojnë asgjë. Ata janë të shurdhër, memecë dhe të verbër e nuk kuptojnë asgjë.');
INSERT INTO "hasan_efendi" VALUES(2,172,'2:172','O besimtarë! Hani nga ushqimet e lejuara që ju kemi dhënë dhe, nëse jeni adhurues të Allahut, falënderojeni Atë!');
INSERT INTO "hasan_efendi" VALUES(2,173,'2:173','Me të vërtetë, Allahu ju ka ndaluar të hani kafshën e ngordhur, gjakun e derdhur, mishin e derrit dhe mishin që nuk është therur në emër të Allahut. Por kushdo që detyrohet nga nevoja të hajë ndonjë gjë prej këtyre, pa dashur të bëjë gjynah dhe pa e kaluar kufirin, nuk do të fajësohet. Vërtet, Allahu është Falës i madh dhe Mëshirëplotë.');
INSERT INTO "hasan_efendi" VALUES(2,174,'2:174','Pa dyshim, ata që fshehin çfarë ka shpallur Allahu në Libër dhe e këmbejnë atë me diçka tjetër të pavlerë, do të gëlltisin në barkun e tyre vetëm zjarr. Në ditën e Kiametit, Allahu nuk do t’u flasë atyre, nuk do t’i pastrojë (nga gjynahet) dhe do t’u japë dënim të dhembshëm.');
INSERT INTO "hasan_efendi" VALUES(2,175,'2:175','Ata e kanë këmbyer rrugën e drejtë me humbjen dhe faljen e Zotit me dënimin e Tij. Po sa të durueshëm do të jenë në zjarr?!');
INSERT INTO "hasan_efendi" VALUES(2,176,'2:176','Kjo është kështu, sepse Allahu e ka zbritur Librin me të vërtetën. Sigurisht, ata që nuk pajtohen rreth tij, janë të përçarë dhe larg së vërtetës.');
INSERT INTO "hasan_efendi" VALUES(2,177,'2:177','Mirësia nuk është të kthyerit e fytyrës suaj nga lindja dhe perëndimi, por mirësia është (cilësi) e atij që beson Allahun, Ditën e Fundit, engjëjt, Librin dhe profetët; e atij që me vullnet jep nga pasuria e vet për të afërmit, jetimët, të varfrit, udhëtarët e mbetur rrugës, lypësit dhe për lirimin e të robëruarve; e atij që fal namazin dhe e jep zekatin; dhe e atyre që i plotësojnë premtimet, kur marrin përsipër diçka; e atyre që durojnë në kohë skamjeje, sëmundjeje dhe lufte. Këta janë besimtarët e vërtetë dhe këta janë ata që e kanë frikë Allahun.');
INSERT INTO "hasan_efendi" VALUES(2,178,'2:178','O besimtarë! Është caktuar për ju kisasi për vrasje: I liri për të lirin, skllavi për skllavin dhe femra për femrën. Ai (vrasës) që falet nga farefisi i të vrarit, duhet të sillet njerëzishëm dhe le të zbatojë dëmshpërblimin me të mirë. Kjo (falje) është një lehtësi dhe mëshirë nga ana e Zotit. E kushdo që pas kësaj (faljes), e kapërcen kufirin, do të marrë një dënim të dhembshëm.');
INSERT INTO "hasan_efendi" VALUES(2,179,'2:179','Në ligjin e barazisë në ndëshkim ka siguri për jetën tuaj, o mendarë, në mënyrë që ju të përmbaheni!');
INSERT INTO "hasan_efendi" VALUES(2,180,'2:180','Ju është urdhëruar që, nëse ndonjërit prej jush i afrohet vdekja dhe ka pasuri për të lënë pas, atëherë të lërë testamentin në mënyrë të drejtë për prindërit dhe farefisin; ky është detyrim për besimtarët e devotshëm.');
INSERT INTO "hasan_efendi" VALUES(2,181,'2:181','Kush e ndryshon testamentin pasi e ka dëgjuar, ta dijë se gjynahu është i rëndë për ata që e ndryshojnë! Sigurisht që Allahu i dëgjon dhe i di të gjitha.');
INSERT INTO "hasan_efendi" VALUES(2,182,'2:182','Kush i trembet ndonjë gabimi apo padrejtësie nga ana e lënësit të testamentit dhe e ndreq atë, ta dijë se nuk bën gjynah. Vërtet, Allahu është Falës i madh dhe Mëshirëplotë.');
INSERT INTO "hasan_efendi" VALUES(2,183,'2:183','O besimtarë! Ju është urdhëruar agjërimi, ashtu si u ishte urdhëruar atyre para jush, që të mund të ruheni nga të këqijat.');
INSERT INTO "hasan_efendi" VALUES(2,184,'2:184','Agjërimi zgjat disa ditë, por, nëse ndonjëri prej jush është i sëmurë ose duke udhëtuar, le të agjërojë aq ditë sa i ka prishur në ditët e tjera pas. Ata që kanë vështirësi për të agjëruar, duhet të ushqejnë si shpagim nga një të varfër për çdo ditë agjërimi. E, nëse ndonjëri jep më shumë se kaq, kjo është edhe më mirë për të. Por, agjërimi juaj është më i dobishëm, në qoftë se e dini.');
INSERT INTO "hasan_efendi" VALUES(2,185,'2:185','Muaji i Ramazanit është ai, në të cilin ka zbritur Kurani, që është udhërrëfyes për njerëzit, plot me shenja të qarta për rrugën e drejtë dhe dallues (i së mirës nga e keqja). Pra, kushdo nga ju që dëshmon këtë muaj, le të agjërojë! Sa i përket atij që është i sëmurë ose gjendet në udhëtim e sipër (le të agjërojë më vonë) aq ditë sa nuk i ka agjëruar. Allahu dëshiron që t’jua lehtësojë dhe jo që t’jua vështirësojë. Ai dëshiron që të plotësoni numrin e ditëve të agjërimit dhe që ta madhëroni Allahun (në fund të agjërimit), për shkak se ju ka drejtuar në rrugë të drejtë e që ta falënderoni Atë.');
INSERT INTO "hasan_efendi" VALUES(2,186,'2:186','Kur robërit e Mi (besimtarë) të pyesin për Mua, (thuaju se) Unë jam afër, i përgjigjem lutjeve të lutësit, kur ai më lutet Mua. Prandaj, le t’i përgjigjen thirrjes Sime dhe le të më besojnë Mua, për të qenë në rrugë të drejtë.');
INSERT INTO "hasan_efendi" VALUES(2,187,'2:187','Tani është e lejuar për ju që, gjatë netëve të agjërimit, të shkoni me bashkëshortet tuaja. Ato janë petk për ju e ju jeni petk për ato. Allahu e di mirë se ju e keni mashtruar veten (duke shkuar me gratë para se të zbriste lejimi), por Allahu e ka pranuar pendimin tuaj dhe jua ka falur gabimin. Prandaj, ju lejohet bashkimi me bashkëshortet tuaja dhe tani kërkoni atë që ka caktuar Allahu për ju. Dhe hani e pini derisa të dallohet fija e bardhë (bardhësia) e agimit nga fija e zezë (errësira e natës), pastaj plotësojeni agjërimin derisa të bjerë mbrëmja. Mos iu afroni atyre (bashkëshorteve) gjatë kohës kur mbylleni për adhurim në xhami! Këta janë kufijtë e Allahut, prandaj mos iu afroni atyre! Kështu, pra, Allahu ua shpjegon njerëzve urdhëresat e veta, që ata të ruhen nga të këqijat.');
INSERT INTO "hasan_efendi" VALUES(2,188,'2:188','Mos ia merrni pasurinë njëri-tjetrit padrejtësisht! Dhe mos u bëni mitëdhënës te gjykatësit për t’ua përvetësuar në mënyrë të paligjshme dhe me vetëdije njerëzve një pjesë të mallit.');
INSERT INTO "hasan_efendi" VALUES(2,189,'2:189','Të pyesin ty (o Muhamed) për hënat e reja; thuaju: “Ato janë për njerëzit shenja përcaktuese të kohës dhe të haxhillëkut”. Nuk është mirësi të hysh në shtëpi nga dera e pasme (siç bënin paganët para Islamit), por mirësia qëndron në të ruajturit nga të këqijat. Hyni në shtëpi nga dyert e tyre dhe kijeni frikë Allahun, në mënyrë që të shpëtoni!');
INSERT INTO "hasan_efendi" VALUES(2,190,'2:190','Luftoni në rrugën e Allahut ndaj atyre që luftojnë kundër jush, por mos e kaloni kufirin, se Allahu nuk i do ata që e kalojnë kufirin.');
INSERT INTO "hasan_efendi" VALUES(2,191,'2:191','Vritini ata kudo që t’i gjeni dhe dëbojini prej vendeve, ashtu siç ju dëbuan ata ju. Mosbesimi është më i rëndë se vrasja. Mos luftoni me ata pranë Xhamisë së Shenjtë (Qabesë), përveç rastit kur ata ju sulmojnë aty brenda! E, nëse ata luftojnë me ju aty, atëherë vritini ata! I këtillë duhet të jetë ndëshkimi për jobesimtarët.');
INSERT INTO "hasan_efendi" VALUES(2,192,'2:192','Por, në qoftë se ata heqin dorë, atëherë Allahu është vërtet Falës dhe Mëshirëplotë.');
INSERT INTO "hasan_efendi" VALUES(2,193,'2:193','Luftojini ata derisa të zhduket mosbesimi dhe derisa të mbretërojë besimi vetëm për Allahun! Por, në qoftë se ata heqin dorë nga lufta kundër jush, atëherë s’ka armiqësi, përveçse kundër keqbërësve.');
INSERT INTO "hasan_efendi" VALUES(2,194,'2:194','Shkelja e muajit të shenjtë do të shpaguhet me shkelje të muajit të shenjtë, por dhe shkelja e të shenjtave do të ndëshkohet në të njëjtën mënyrë. Pra, kushdo që ju sulmon, edhe ju keni të drejtë ta sulmoni atë, ashtu siç ju sulmoi ju! Frikësojuni Allahut dhe dijeni se Allahu është me ata që ruhen nga të këqijat.');
INSERT INTO "hasan_efendi" VALUES(2,195,'2:195','Shpenzojeni pasurinë në rrugën e Allahut dhe mos e çoni veten tuaj në shkatërrim, por bëni të mira; Allahu me të vërtetë i do bamirësit.');
INSERT INTO "hasan_efendi" VALUES(2,196,'2:196','Kryeni haxhin dhe umren për hir të Allahut! Nëse në këtë rrugë pengoheni, atëherë therni kurban, sipas mundësisë, dhe mos i rruani kokat tuaja, derisa të arrijë kurbani në vend! Cilido prej jush që është i sëmurë ose që ka ndonjë plagë në kokë (dhe si i tillë, nuk mund ta rruajë kokën), le ta zëvendësojë me agjërim, lëmoshë ose kurban. Kur të jeni të sigurt (nga armiqtë), ai që prej jush kryen umren deri në haxh, le të therë kurban, sipas mundësisë. Ndërsa ai që nuk ka mundësi, është i detyruar të agjërojë tri ditë në haxh dhe shtatë ditë pas kthimit nga haxhi; këto janë dhjetë ditë të plota. Kjo është për ata që nuk banojnë në Mekë. Ta keni frikë Allahun dhe dijeni se Ai dënon ashpër.');
INSERT INTO "hasan_efendi" VALUES(2,197,'2:197','Koha e haxhillëkut është në muaj të caktuar. Kush vendos të kryejë haxhillëkun, le të largohet nga marrëdhëniet (me gruan), grindjet dhe sharjet. Ndërkaq, çdo vepër të mirë që bëni, Allahu e di. Pajisuni me gjërat që ju nevojiten për rrugë dhe dijeni se pajisja më e mirë është devotshmëria. Prandaj, kini frikë prej Meje, o njerëz të mençur!');
INSERT INTO "hasan_efendi" VALUES(2,198,'2:198','Nuk është gjynah të kërkoni nga Zoti juaj ndonjë të mirë. E, kur të ktheheni nga Arafati, përmendni Allahun në vendet e shenjta! Përmendni Atë që ju ka udhëzuar në rrugën e drejtë, sepse ju më parë ishit të humbur!');
INSERT INTO "hasan_efendi" VALUES(2,199,'2:199','Mandej, kthehuni, siç kthehen të gjithë të tjerët dhe kërkoni ndjesë prej Allahut! Se Allahu është Falës i madh dhe Mëshirëplotë.');
INSERT INTO "hasan_efendi" VALUES(2,200,'2:200','Pasi t’i kryeni ritet e haxhillëkut, përmendni Allahun ashtu siç i kujtoni prindërit tuaj, madje edhe më tepër! Disa njerëz thonë: “Zoti ynë, jepna të mira në këtë botë!” Por, për këta, s’ka asnjë të mirë në botën tjetër.');
INSERT INTO "hasan_efendi" VALUES(2,201,'2:201','Por ka dhe të tjerë që thonë: “Zoti ynë, na jep të mira në këtë botë, na jep të mira në botën tjetër dhe na ruaj nga dënimi i Zjarrit (të Xhehenemit)!”');
INSERT INTO "hasan_efendi" VALUES(2,202,'2:202','Këtyre do t’u jepet shpërblim për atë që kanë bërë. Allahu është i shpejtë në llogari.');
INSERT INTO "hasan_efendi" VALUES(2,203,'2:203','Dhe madhëroni Allahun në (tri) ditët e caktuara (të Teshrikut). Por, edhe ata që nguten dhe rrinë vetëm dy ditë (në Mina), nuk bëjnë gjynah; nuk bën gjynah as ai që rri më shumë, nëse i frikësohet vërtet Allahut. Ta keni frikë Allahun e ta dini mirë se vetëm tek Ai do të tuboheni!');
INSERT INTO "hasan_efendi" VALUES(2,204,'2:204','Ka ndonjë njeri (hipokrit), fjalët e të cilit në jetën e kësaj bote ta bëjnë qejfin dhe që betohet në Allahun për atë që mban në zemër, por që, në të vërtetë, është kundërshtari (yt) më i betuar.');
INSERT INTO "hasan_efendi" VALUES(2,205,'2:205','E, kur largohet, përpiqet të bëjë të këqija në tokë, duke shkatërruar të mbjellat dhe bagëtinë. Por Allahu nuk e do shkatërrimin.');
INSERT INTO "hasan_efendi" VALUES(2,206,'2:206','Kur atij i thuhet “Kije frikë Allahun!”, ai mburret dhe bën më shumë të këqija. Atij i mjafton Xhehenemi. Eh, sa vendbanim i keq është ai vend!');
INSERT INTO "hasan_efendi" VALUES(2,207,'2:207','Ka njerëz që e flijojnë veten, për të fituar pëlqimin e Allahut. Allahu është shumë i mëshirshëm me robtë e Vet.');
INSERT INTO "hasan_efendi" VALUES(2,208,'2:208','O besimtarë! Pranojeni Islamin me të gjitha rregullat e tij dhe mos shkoni gjurmëve të djallit, se ai është armiku juaj i përbetuar.');
INSERT INTO "hasan_efendi" VALUES(2,209,'2:209','E, nëse i shmangeni së drejtës, pasi ju kanë ardhur prova të qarta, atëherë dijeni se Allahu është i Plotfuqishëm dhe i Urtë.');
INSERT INTO "hasan_efendi" VALUES(2,210,'2:210','E ç’presin tjetër ata, që t’u vijë Allahu përmes hijeve të reve bashkë me engjëjt?! Atëherë merr fund puna! Tek Allahu kthehen të gjitha punët.');
INSERT INTO "hasan_efendi" VALUES(2,211,'2:211','Pyeti bijtë e Israilit: sa shenja të qarta u kemi dërguar Ne atyre? Ata që mohojnë të mirat e Allahut, pasi u janë dërguar, do të dënohen. Vërtet, Allahu është i rreptë në ndëshkim.');
INSERT INTO "hasan_efendi" VALUES(2,212,'2:212','Ata që nuk besojnë, janë të joshur nga jeta e kësaj bote, prandaj tallen me besimtarët. Por njerëzit që i frikësohen Allahut, do të jenë mbi ata - në Ditën e Kiametit. Allahu begaton pa kufi kë të dojë.');
INSERT INTO "hasan_efendi" VALUES(2,213,'2:213','Dikur, të gjithë njerëzit përbënin një bashkësi të vetme. Pastaj Allahu u dërgoi profetët me lajme të mira dhe paralajmërime. Bashkë me ata, Ai zbriti edhe Librin me të vërtetën, për t’i gjykuar njerëzit në çështjet për të cilat nuk pajtoheshin. Mirëpo, njerëzit u grindën midis tyre, pasi u erdhën shenjat e qarta, nga zilia që kishin për njëri-tjetrin. Atëherë, me vullnetin e Vet, Allahu i udhëzoi besimtarët që ta kuptonin të vërtetën, lidhur me çështjet për të cilat të tjerët nuk pajtoheshin. Allahu shpie në udhë të drejtë kë të dojë.');
INSERT INTO "hasan_efendi" VALUES(2,214,'2:214','Vërtet mendoni të hyni në Xhenet, pa provuar atë që kanë provuar të tjerët para jush? Ata i goditi mjerimi dhe sëmundjet dhe, aq shumë u tronditën, saqë çdo i dërguar dhe ndjekësit e tij, thirrën: “Kur do të arrijë ndihma e Allahut?!” Ja, ndihma e Allahut është afër!');
INSERT INTO "hasan_efendi" VALUES(2,215,'2:215','Të pyesin ty çfarë të japin si lëmoshë. Thuaju: “Çfarëdo bamirësie që bëni, ajo le t’u takojë prindërve, farefisit, jetimëve, të varfërve dhe udhëtarëve. Çfarëdo të mire që bëni, Allahu e di atë”.');
INSERT INTO "hasan_efendi" VALUES(2,216,'2:216','Lufta është bërë detyrim për ju, ndonëse e urreni. Por mund ta urreni një gjë, ndërkohë që ajo është e mirë për ju e mund ta doni një gjë, ndërkohë që ajo është e dëmshme për ju. Allahu di, kurse ju nuk dini.');
INSERT INTO "hasan_efendi" VALUES(2,217,'2:217','Të pyesin ty për muajin e shenjtë, për luftën në të. Thuaju: “Luftimi në atë kohë është gjynah i madh, por pengimi i njerëzve prej udhës së Allahut, mohimi i Tij, pengimi i njerëzve nga vizita e Xhamisë së Shenjtë dhe dëbimi i banorëve të saj nga ajo, janë gjynah më i madh tek Allahu. Kurse adhurimi i idhujve është gjynah më i madh se vrasja.” Ata vazhdimisht do të luftojnë kundër jush, që të mund t’ju kthejnë nga besimi juaj. Ata që e mohojnë fenë dhe vdesin si mohues, - veprat e tyre (të mira) do të humbasin në këtë botë dhe në tjetrën. Ata do të jenë banorë të Zjarrit, ku do të qëndrojnë përgjithmonë.');
INSERT INTO "hasan_efendi" VALUES(2,218,'2:218','Vërtet, ata që besuan dhe ata që mërguan e luftuan në rrugë të Allahut, pikërisht ata shpresojnë në mëshirën e Allahut. Allahu është Falës i madh dhe Mëshirëplotë.');
INSERT INTO "hasan_efendi" VALUES(2,219,'2:219','Të pyesin ty për verën dhe kumarin. Thuaju: “Ato sjellin dëme të mëdha (gjynahe), por edhe dobi për njerëzit. Megjithatë, dëmi i tyre është më i madh se dobia”. Të pyesin se çfarë duhet të japin si lëmoshë. Thuaju: “Tepricën!” Ja, kështu jua shpjegon Allahu shpalljet e Tij, që të mund të mendoni');
INSERT INTO "hasan_efendi" VALUES(2,220,'2:220','për këtë jetë dhe për jetën tjetër. Të pyesin ty për jetimët, thuaju: “T’u bëni mirë atyre është vepër e bukur. Nëse jetoni bashkë me ata, dijeni se janë vëllezërit tuaj. Allahu di ta dallojë keqbërësin prej mirëbërësit. Sikur të donte Allahu, do t’ju detyronte me punë të vështira. Vërtet, Allahu është i Plotfuqishëm dhe i Urtë.');
INSERT INTO "hasan_efendi" VALUES(2,221,'2:221','Mos u martoni me paganet, gjersa të bëhen besimtare. Skllavja besimtare është më e mirë se pagania, edhe nëse kjo e fundit mund t’ju pëlqejë. Mos i martoni myslimanet me paganë, gjersa ata të bëhen besimtarë. Skllavi besimtar është më i mirë se pagani, edhe nëse ky i fundit mund t’ju pëlqejë. Ata ju thërrasin në zjarr, kurse Allahu, me mirësinë e Tij, ju thërret në Xhenet e falje (të gjynaheve). Ai ua shpjegon njerëzve shpalljet e Veta, që të mendojnë ata.');
INSERT INTO "hasan_efendi" VALUES(2,222,'2:222','Të pyesin ty (Muhamed) për të përmuajshmet e femrave. Thuaju: “Ajo është gjendje e dëmshme.” Prandaj, gjatë kësaj kohe, mos iu afroni atyre (për marrëdhënie) derisa të pastrohen. Pasi të pastrohen, atëherë afrohuni tek ato ashtu si ju ka urdhëruar Allahu. Pa dyshim, Allahu i do ata që pendohen fort (për gjynahet) dhe ata që pastrohen.');
INSERT INTO "hasan_efendi" VALUES(2,223,'2:223','Gratë tuaja janë “ara” për ju, prandaj iu afroni “arave” tuaja si të doni. Por bëni diçka (të mirë) për veten tuaj (duke përmendur emrin e Allahut para kontaktit). Kijeni frikë Allahun dhe dijeni se do të takoheni me Atë. Jepu lajm të mirë besimtarëve (se do fitojnë Xhenetin).');
INSERT INTO "hasan_efendi" VALUES(2,224,'2:224','Mos e bëni pengesë betimin në Allahun për kryerjen e veprave të mira, largimin prej të ligave dhe për pajtimin e njerëzve! Allahu i dëgjon dhe i di të gjitha.');
INSERT INTO "hasan_efendi" VALUES(2,225,'2:225','Allahu nuk ju dënon nëse betoheni pa qëllim, por ju dënon për atë që vendosni në zemrat tuaja. Allahu është Falës i madh e i Butë.');
INSERT INTO "hasan_efendi" VALUES(2,226,'2:226','Ata që betohen se nuk do t’u afrohen grave të veta, duhet të presin katër muaj; nëse pendohen nga betimi, ta dinë se Allahu është Falës i madh dhe Mëshirëplotë.');
INSERT INTO "hasan_efendi" VALUES(2,227,'2:227','E, nëse vendosin të ndahen, vërtet, Allahu dëgjon dhe di çdo gjë.');
INSERT INTO "hasan_efendi" VALUES(2,228,'2:228','Gratë e ndara duhet të presin tri cikle mujore dhe nuk është e lejueshme ta fshehin atë që ka krijuar Allahu në mitrën e tyre, nëse besojnë Allahun dhe Ditën e Kiametit. Burrat e tyre, nëse dëshirojnë mirësi dhe qetësi, kanë të drejtë t’i kthejnë gjatë këtij afati. Gratë kanë aq të drejta sa kanë edhe detyra, sipas arsyes së shëndoshë, ndonëse burrat kanë një shkallë (më shumë përgjegjësie) mbi ato. Allahu është i Plotfuqishëm dhe i Urtë.');
INSERT INTO "hasan_efendi" VALUES(2,229,'2:229','Ndarja mund të bëhet dy herë. Më pas, gruaja ose të mbahet me të mirë, ose të lejohet të ikë me të mirë. Burrave nuk u lejohet të marrin gjë nga ajo që iu kanë dhënë atyre, vetëm nëse të dyja palët frikësohen se nuk do t’i zbatojnë kufijtë e Allahut. Pra, nëse të dy frikësohen se nuk do të mund t’u përmbahen kufijve të Allahut, atëherë nuk është gjynah për asnjërin, nëse gruaja i jep diçka burrit (për t’u ndarë nga ai). Këta janë kufijtë e Allahut, andaj mos i shkelni ata! Ata që shkelin kufijtë e Allahut, pikërisht ata janë keqbërësit.');
INSERT INTO "hasan_efendi" VALUES(2,230,'2:230','Më pas, nëse burri e ndan sërish gruan (për të tretën herë), nuk mund të martohet më me të, pa u martuar ajo me një burrë tjetër. Por, në qoftë se gruaja ndahet rregullisht nga burri i dytë, atëherë nuk kanë gjynah as ajo e as burri i parë që të martohen sërish me njëri-tjetrin, nëse mendojnë se do t’i respektojnë kufijtë e Allahut; këto janë urdhrat e Allahut, që Ai ua shpjegon njerëzve që kuptojnë.');
INSERT INTO "hasan_efendi" VALUES(2,231,'2:231','Kur t’i ndani gratë dhe atyre t’u afrohet fundi i periudhës së pritjes, ose mbajini me të mirë, ose lërini të ikin me të mirë. Dhe mos i mbani ato me qëllim lëndimi, se në këtë mënyrë i bëni padrejtësi vetes. Mos luani me shpalljet e Allahut! Kujtoni begatitë që ju ka dhënë Ai, si dhe Librin (Kuranin) dhe diturinë e hollësishme, që jua ka zbritur për t’ju këshilluar. Kini frikë Allahun dhe ta dini se Allahu është i Dijshëm për çdo gjë!');
INSERT INTO "hasan_efendi" VALUES(2,232,'2:232','Kur t’i lini gratë të lira dhe ato të kenë plotësuar kohën e pritjes, mos i pengoni ato që të martohen përsëri me burrat e tyre, nëse merren vesh me të mirë. Kështu këshillohet çdokush prej jush që beson Allahun dhe Ditën e Kiametit. Kjo është më e ndershme dhe më e virtytshme. Allahu di, kurse ju nuk dini.');
INSERT INTO "hasan_efendi" VALUES(2,233,'2:233','Nënat le t’u japin gji fëmijëve të tyre dy vjet, për ata që duan t’ia plotësojnë dhënien e gjirit. Babai i fëmijës ka për detyrë, sipas mundësisë së tij, që t’i sigurojë si duhet ushqim dhe veshmbathje. Nuk ngarkohet askush përtej mundësisë që ka: nëna le të mos dëmtohet për shkak të fëmijës së saj e as babai të mos dëmtohet për shkak të tij. Edhe trashëgimtari ka të njëjtat detyrime për të gjitha këto. Por, nëse pas këshillimit, ata vendosin me pëlqim të ndërsjellë që ta ndajnë fëmijën nga gjiri, kjo nuk është gjynah për ata. Po ashtu, nuk është gjynah për ju nëse doni t’i gjeni fëmijës suaj ndonjë mëndeshë, me kusht që t’ia jepni atë që e keni menduar, sipas rregullave. Ta keni frikë Allahun dhe dijeni se Allahu i sheh mirë të gjitha punët që bëni ju.');
INSERT INTO "hasan_efendi" VALUES(2,234,'2:234','Gratë të cilave iu vdes burri, duhet të presin katër muaj e dhjetë ditë (pas vdekjes së tij). Pas kalimit të këtij afati, ju nuk jeni përgjegjës për çfarë bëjnë ato me jetën e tyre sipas rregullave. Allahu e njeh mirë çdo vepër që ju bëni.');
INSERT INTO "hasan_efendi" VALUES(2,235,'2:235','Nuk është gjynah për ju nëse grave të tilla u jepni shenjë se do t’i kërkoni apo nëse këtë qëllim e fshihni në shpirtin tuaj. Allahu e di se do të mendoni për ato, por mos u premtoni atyre fshehurazi, vetëm nëse bisedoni me to ndershmërisht. Dhe mos vendosni për martesë para se të kalojë afati i paraparë i pritjes. Ta dini se Allahu e di ç’ka në zemrat tuaja, andaj ruajuni prej Tij. Dhe ta dini se Allahu është Falës i madh dhe i Butë.');
INSERT INTO "hasan_efendi" VALUES(2,236,'2:236','Nuk është gjynah për ju nëse i ndani gratë para se të keni marrëdhënie me to ose para se të caktoni shumën e dhuratës martesore (që do t’u jepni). Por u jepni dhuratë të pëlqyeshme: i pasuri sipas gjendjes së tij dhe i vobekti sipas gjendjes së vet. Kjo është detyrë e atyre që dëshirojnë të bëjnë vepra të mira.');
INSERT INTO "hasan_efendi" VALUES(2,237,'2:237','Në qoftë se i ndani gratë para se të keni marrëdhënie me to, por ia keni caktuar shumën e dhuratës martesore, atëherë atyre u përket gjysma e dhuratës, veç nëse ua falin ato ose ua falin ata që kanë në dorë lidhjen e martesës . Por që të falni është më pranë devotshmërisë. Mos harroni që të bëheni bamirës midis jush! Vërtet, Allahu e sheh mirë se çfarë bëni.');
INSERT INTO "hasan_efendi" VALUES(2,238,'2:238','Faleni rregullisht namazin, sidomos atë të mesmin dhe qëndroni me devotshmëri para Allahut, duke iu lutur Atij. ');
INSERT INTO "hasan_efendi" VALUES(2,239,'2:239','Nëse keni frikë, faluni duke qenë në këmbë ose hipur (mbi kalë)! Kur të jeni të sigurt, atëherë t’i kryeni faljet për Allahun, ashtu si ju ka mësuar Ai atë që nuk e keni ditur!');
INSERT INTO "hasan_efendi" VALUES(2,240,'2:240','Ata prej jush, të cilëve iu është afruar vdekja dhe kanë gra për të lënë pas, duhet t’u lënë atyre në testament mjetet për të jetuar një vit, pa i bërë që të dalin nga shtëpia. Por, nëse ato vetë e braktisin shtëpinë, ju nuk jeni përgjegjës për çfarë bëjnë me jetën e tyre sipas rregullave. Allahu është i Plotfuqishëm e i Urtë.');
INSERT INTO "hasan_efendi" VALUES(2,241,'2:241','Grave të ndara u takon sigurimi i caktuar i shpenzimeve të jetesës. Kjo është detyrë e besimtarëve.');
INSERT INTO "hasan_efendi" VALUES(2,242,'2:242','Kështu jua shpjegon Allahu shpalljet e Veta, në mënyrë që të kuptoni.');
INSERT INTO "hasan_efendi" VALUES(2,243,'2:243','Mendo (o Muhamed) për ata që me mijëra ikën nga qytetet e tyre nga frika e vdekjes. Allahu u tha atyre “Vdisni!” pastaj, i ringjalli ata. Vërtet, Allahu zotëron mirësi të mëdha për njerëzit, por shumica e tyre nuk e falënderojnë.');
INSERT INTO "hasan_efendi" VALUES(2,244,'2:244','Luftoni në rrugën e Allahut dhe ta dini se Allahu i dëgjon të gjitha dhe di çdo gjë.');
INSERT INTO "hasan_efendi" VALUES(2,245,'2:245','Kush do t’i japë Allahut një hua të bukur, që Ai t’ia kthejë shpërblimin shumëfish? Allahu e shtrëngon (riskun) dhe e liron; tek Ai do të ktheheni.');
INSERT INTO "hasan_efendi" VALUES(2,246,'2:246','A nuk e ke dëgjuar ti (o Muhamed) se çfarë i tha paria e bijve të Israilit, pas Musait, një profeti të tyre? I tha: “Na dërgo një mbret, që të luftojmë në rrugën e Allahut!” - Profeti u tha: “Po sikur të mos luftoni kur të shpallet lufta?” Ata thanë: “E përse të mos luftojmë në rrugën e Allahut, ne që jemi dëbuar nga atdheu ynë dhe jemi ndarë nga fëmijët tanë?!” Mirëpo, kur u urdhëruan të luftojnë, ata nuk luftuan, me përjashtim të një pakice. Allahu i njeh mirë keqbërësit.');
INSERT INTO "hasan_efendi" VALUES(2,247,'2:247','Profeti i tyre u tha: “Allahu ka dërguar Talutin si mbretin tuaj.” Ata thanë: “Si mund të bëhet ai mbreti ynë, kur ne kemi më tepër merita se ai për të sunduar?! Përveç kësaj, ai nuk ka ndonjë pasuri të madhe.” Ai u përgjigj: “Allahu e ka zgjedhur atë për mbretin tuaj dhe e ka pajisur me dijeni të gjerë dhe me fuqi trupore. Allahu ia jep pushtetin e Vet kujt të dojë; Allahu është Mirëbërës i madh dhe i Gjithëdijshëm.”');
INSERT INTO "hasan_efendi" VALUES(2,248,'2:248','Profeti u tha atyre: “Shenjë e sundimit të tij është ardhja e Arkës. Aty ka qetësi për ju nga Zoti juaj dhe relikte të lëna nga familja e Musait dhe Harunit. Atë e bartin engjëjt. Sigurisht që kjo është një shenjë për ju, nëse jeni besimtarë!”');
INSERT INTO "hasan_efendi" VALUES(2,249,'2:249','Kur Taluti u nis për luftë bashkë me ushtrinë, u tha atyre: “Allahu do t’ju provojë me një lumë. Kush pi ujë në të, nuk është ushtari im. Ndërsa kush nuk pi ujë në të ose kënaqet vetëm me një grusht ujë, është ushtari im” . Por të gjithë pinë, përveç një pakice. Pasi ai me ata (ushtarë) që ishin besimtarë kaluan lumin, shumë prej tyre thanë: “Ne sot nuk kemi fuqi të luftojmë me Xhalutin dhe ushtrinë e tij”. Por ata që ishin të bindur se do të takoheshin me Allahun, thanë: “Sa herë, me ndihmën e Allahut, një ushtri e vogël ka ngadhënjyer mbi një ushtri të madhe! Allahu është me të durueshmit”.');
INSERT INTO "hasan_efendi" VALUES(2,250,'2:250','Dhe kur u përballën me Xhalutin dhe ushtrinë e tij, thanë: “Zoti ynë! Na pajis me durim, na forco këmbët tona dhe na ndihmo kundër këtij populli jobesimtar!”');
INSERT INTO "hasan_efendi" VALUES(2,251,'2:251','Me ndihmën e Allahut, ata i thyen armiqtë dhe Davudi e vrau Xhalutin. Allahu i dha atij pushtetin dhe Profecinë dhe i mësoi dituritë që deshi. Sikur Allahu të mos i ndihmonte njerëzit e mirë kundër të këqijve, bota do të ishte shkatërruar. Por Allahu është shumë Bujar me krijesat e Veta.');
INSERT INTO "hasan_efendi" VALUES(2,252,'2:252','Këto janë shpalljet e Allahut. Ne t’i lexojmë ty (Muhamed) ato me të vërtetën, sepse, pa dyshim, ti je një prej të dërguarve Tanë.');
INSERT INTO "hasan_efendi" VALUES(2,253,'2:253','Nga këta të dërguar, disa syresh i ngritëm mbi të tjerët. Disave Allahu u foli drejtpërdrejt, kurse të tjerë i ngriti në shkallë të larta. Ne i dhamë Isait, birit të Merjemes, prova (mrekulli) të qarta dhe e ndihmuam me Shpirtin e Shenjtërisë (Xhebrailin a.s.). Sikur të donte Allahu, njerëzit që erdhën pas të dërguarve nuk do të vriteshin me njëri-tjetrin pas ardhjes së provave të qarta, por ata u përçanë: disa besuan, kurse të tjerët mohuan. Sikur të donte Allahu, ata nuk do të vriteshin me njëri-tjetrin, por Allahu bën si të dojë Vetë.');
INSERT INTO "hasan_efendi" VALUES(2,254,'2:254','O besimtarë! Jepni pa u kursyer nga të mirat që ju kemi dhënë Ne, para se të vijë Dita (e Gjykimit) në të cilën nuk ka shit-blerje, as miqësi, as ndërmjetësi! Jobesimtarët janë keqbërësit e vërtetë.');
INSERT INTO "hasan_efendi" VALUES(2,255,'2:255','Allahu! Nuk ka zot tjetër (që meriton adhurimin) përveç Tij, të Gjallit, të Përjetshmit, Mbajtësit të gjithçkaje! Atë nuk e kaplon as dremitja, as gjumi! Atij i përket gjithçka që gjendet në qiej dhe gjithçka që gjendet në Tokë. Kush mund të ndërhyjë tek Ai për ndokënd pa lejen e Tij? Ai di çdo gjë që ka ndodhur përpara dhe çdo gjë që do të ndodhë pas njerëzve, kurse ata nuk mund të përvetësojnë asgjë nga Dituria e Tij, përveçse aq sa Ai dëshiron. Kursi-u i Tij shtrihet mbi qiejt dhe Tokën dhe Ai nuk e ka të rëndë t’i ruajë ato. Ai është i Larti, Madhështori!');
INSERT INTO "hasan_efendi" VALUES(2,256,'2:256','S’ka detyrim në fe, sepse tashmë është dalluar e drejta nga e shtrembra! Ai që mohon idhujt (dhe gjithçka që adhurohet në vend të Allahut) dhe beson në Allahun, ka siguruar lidhjen më të fortë, e cila nuk këputet kurrë. Allahu dëgjon dhe di gjithçka.');
INSERT INTO "hasan_efendi" VALUES(2,257,'2:257','Allahu është Mbrojtësi i atyre që besojnë. Ai i nxjerr ata nga errësira në dritë. Sa për ata që nuk besojnë, mbrojtësi i tyre është djalli, i cili i nxjerr ata nga drita dhe i shpie në errësirë. Këta janë banorët e Zjarrit (të Xhehenemit), ku do të qëndrojnë përgjithmonë.');
INSERT INTO "hasan_efendi" VALUES(2,258,'2:258','A nuk ke dëgjuar ti (Muhamed) për atë që bëri fjalë me Ibrahimin për Zotin e tij, sepse Allahu i kishte dhënë pushtet? Kur Ibrahimi i tha: “Zoti im është Ai që jep jetë dhe shkakton vdekje” - ai u përgjigj: “Edhe unë mund të jap jetë e të shkaktoj vdekje”. Ibrahimi pastaj i tha: “Allahu e sjell Diellin nga lindja. Sille ti nga perëndimi”! Atëherë, ai (mohuesi) mbeti me gojën hapur. Allahu nuk i udhëzon ata që janë keqbërës.');
INSERT INTO "hasan_efendi" VALUES(2,259,'2:259','Po për atë i cili, kur kaloi pranë një qyteti të rrënuar përtokë, tha: “Si mund ta kthejë në jetë Allahu këtë qytet tani që ai është i vdekur?!” Dhe Allahu e vdiq atë për njëqind vjet e pastaj e ringjalli dhe e pyeti: “Sa kohë ke ndenjur këtu?” - Ai u përgjigj: “Një ditë ose gjysmë dite”. - “Jo” - i tha Allahu, “por ke ndenjur njëqind vjet. Shiko ushqimin dhe pijen tënde që nuk janë prishur! Shikoje gomarin tënd (si do ta ringjallim) që të të bëjmë ty dëshmi mrekullie për njerëzit; vështroji eshtrat se si i bashkojmë e pastaj i veshim me mish!” Kur atij iu bë e qartë, ai tha: “Tashmë e di se Allahu është i Fuqishëm për çdo gjë!”');
INSERT INTO "hasan_efendi" VALUES(2,260,'2:260','Kur Ibrahimi tha: “O Zoti im! Tregomë si i ngjall të vdekurit!” – Zoti i tha: “A nuk beson?” - “Besoj” - iu përgjigj Ibrahimi, “por dëshiroj të më qetësohet zemra.” Allahu i tha: “Merr katër zogj dhe copëtoji. Shpërndaj në çdo kodër nga një copë prej tyre pastaj thirri! Ata do të të vijnë me të shpejtë. Dhe ta dish se Allahu është i Plotfuqishëm dhe i Urtë.”');
INSERT INTO "hasan_efendi" VALUES(2,261,'2:261','Ata që shpenzojnë pasurinë e vet në rrugë të Allahut, i shëmbëllejnë atij që mbjell një kokërr nga e cila dalin shtatë kallinj, ku secili kalli ka nga njëqind kokrra. Allahu ia shton (shpërblimin) edhe më tepër kujt të dëshirojë; Allahu është Mirëbërës i madh dhe i Gjithëdijshëm.');
INSERT INTO "hasan_efendi" VALUES(2,262,'2:262','Ata që shpenzojnë pasurinë e vet në rrugë të Allahut, pa e përcjellë atë me përgojim apo fyerje, do të kenë shpërblim te Zoti i tyre dhe nuk do të kenë pse të frikësohen apo pikëllohen.');
INSERT INTO "hasan_efendi" VALUES(2,263,'2:263','Fjala e mirë dhe falja e gabimit janë më të vlefshme se lëmosha që përcillet me fyerje. Allahu është i Vetëmjaftueshëm dhe i Butë (nuk nxiton në dënime).');
INSERT INTO "hasan_efendi" VALUES(2,264,'2:264','O besimtarë! Mos i çoni dëm lëmoshat tuaja duke ua kujtuar ato (atyre që ua keni dhënë) dhe duke fyer, siç vepron ai që e shpenzon pasurinë e vet për sy e faqe të botës dhe nuk beson në Allahun dhe Ditën e Fundit. Ai shëmbëllen me një gur të lëmuar të mbuluar me dhé, mbi të cilin bie shiu me rrebesh, duke e lënë të zhveshur. Ata nuk kanë kurrfarë dobie nga veprat që kanë bërë, se Allahu nuk e udhëzon në rrugë të drejtë popullin mohues.');
INSERT INTO "hasan_efendi" VALUES(2,265,'2:265','Ata që shpenzojnë pasurinë e vet, për të kërkuar kënaqësinë e Allahut dhe që e forcojnë veten me punë të mira, shëmbëllejnë me një kopsht të mbjellë në rrafshnaltë, në të cilin bie shi i bollshëm, andaj jep fruta dyfish. Nëse nuk i bie shi i bollshëm, i bie shi i imët që i mjafton. Allahu i sheh mirë punët që bëni ju.');
INSERT INTO "hasan_efendi" VALUES(2,266,'2:266','A dëshiron ndonjëri nga ju që, duke qenë i moshuar e me fëmijë të mitur e të pambrojtur, kopshti i tij plot palma hurmash, vreshta e gjithfarë lloje frutash dhe i ujitur nga lumenj rrjedhës, të digjet nga një stuhi zjarri?! Ja, kështu Allahu jua shpjegon shpalljet e Tij, me qëllim që të mendoni.');
INSERT INTO "hasan_efendi" VALUES(2,267,'2:267','O besimtarë! Jepni pa u kursyer nga të mirat që keni fituar dhe nga ato që jua kemi nxjerrë Ne nga frytet e tokës. Mos dhuroni nga ato gjëra të pavlefshme të pasurisë suaj e të cilat ju nuk do t’i pranonit ndryshe veçse symbyllur. Ta dini se Allahu është i Vetëmjaftueshëm dhe i Denjë për çdo lëvdatë.');
INSERT INTO "hasan_efendi" VALUES(2,268,'2:268','Djalli ju frikëson me skamje e varfëri dhe ju urdhëron të bëni vepra të pamoralshme. Kurse Allahu ju premton faljen dhe mirësinë e Tij. Allahu është Mirëbërës i madh dhe i Gjithëdijshëm.');
INSERT INTO "hasan_efendi" VALUES(2,269,'2:269','Ai ia jep urtësinë (të kuptuarit e Kuranit) kujt të dojë. Cilitdo që i është dhënë urtësia, vërtet që i është dhënë një mirësi e madhe. Por këtë nuk e kupton tjetërkush, përveç njerëzve të mençur.');
INSERT INTO "hasan_efendi" VALUES(2,270,'2:270','Çfarëdo gjëje që të jepni dhe çdo betim që të bëni, me siguri që Allahu ka dijeni për atë! Keqbërësit nuk do të kenë askënd që t’i ndihmojë.');
INSERT INTO "hasan_efendi" VALUES(2,271,'2:271','T’i jepni lëmoshat haptazi, është gjë e mirë, por t’ua jepni ato të varfërve fshehurazi, është edhe më mirë dhe ju shlyen disa nga gjynahet tuaja. Allahu e njeh mirë çdo vepër që ju bëni.');
INSERT INTO "hasan_efendi" VALUES(2,272,'2:272','Ti (Muhamed) nuk e ke për detyrë t’i udhëzosh ata në rrugë të drejtë, por është Allahu Ai që udhëzon kë të dojë në rrugën e drejtë. Çfarëdo të mire që të jepni, është në dobinë tuaj, nëse ju këtë e bëni vetëm për hir të Allahut! Çfarëdo që të jepni nga të mirat, do t’ju rikthehet e plotë dhe nuk do t’ju bëhet padrejtësi.');
INSERT INTO "hasan_efendi" VALUES(2,273,'2:273','(Lëmosha) u takon të varfërve, të cilët kanë hyrë në rrugën e Zotit e nuk janë në gjendje të udhëtojnë (për të fituar). Kush nuk i njeh ata, kujton se janë të kamur, për shkak se ata nuk lypin. Do t’i njohësh nga pamja e tyre. Ata nuk i mërzisin njerëzit duke lypur. E çdo gjë që shpenzoni prej të mirave, Allahu i di ato.');
INSERT INTO "hasan_efendi" VALUES(2,274,'2:274','Ata që japin nga pasuria e tyre natën dhe ditën, fshehurazi dhe haptazi, do të kenë shpërblim te Zoti i tyre; ata nuk do të kenë pse të frikësohen apo pikëllohen.');
INSERT INTO "hasan_efendi" VALUES(2,275,'2:275','Ata që marrin fajde, do të ringjallen (në Ditën e Gjykimit) si ai që djalli e ka çmendur me prekjen e vet, sepse ata thonë se tregtia është njëlloj si kamataria. Porse Allahu e ka lejuar tregtinë, kurse kamatarinë e ka ndaluar. Ai të cilit i vjen këshilla nga Zoti i tij dhe i shmanget kamatës, le ta mbajë atë që tashmë ka fituar dhe fati i tij është në duart e Allahut. Ndërsa ata që i kthehen kamatës sërish, do të jenë banorë të Zjarrit, ku do të qëndrojnë përgjithmonë.');
INSERT INTO "hasan_efendi" VALUES(2,276,'2:276','Allahu ia heq çdo përfitim kamatës, kurse e rrit përfitimin e bamirësisë. Allahu nuk i do ata që janë mohues - gjynahqarë.');
INSERT INTO "hasan_efendi" VALUES(2,277,'2:277','Ata që besojnë dhe kryejnë vepra të mira, falin namazin dhe japin zekatin, do të kenë shpërblim te Zoti i tyre dhe nuk do të kenë pse të frikësohen apo pikëllohen.');
INSERT INTO "hasan_efendi" VALUES(2,278,'2:278','O ju që keni besuar! Ta keni frikë Allahun dhe hiqni dorë nga kamata, nëse jeni besimtarë të vërtetë.');
INSERT INTO "hasan_efendi" VALUES(2,279,'2:279','Nëse nuk e bëni këtë, atëherë iu është shpallur luftë nga Allahu dhe i Dërguari i Tij. Nëse pendoheni, atëherë ju takon kapitali fillestar. Në këtë mënyrë, nuk dëmtoni askënd e as nuk dëmtoheni vetë.');
INSERT INTO "hasan_efendi" VALUES(2,280,'2:280','Nëse e dini se borxhliu është ngushtë, shtyjani afatin (e shlyerjes së borxhit) derisa ta përmirësojë gjendjen. Por është më mirë për ju që t’ia falni borxhin, veç sikur ta dinit.');
INSERT INTO "hasan_efendi" VALUES(2,281,'2:281','Dhe ruajuni asaj Dite që do të ktheheni tek Allahu, kur çdokujt do t’i jepet ajo që ka fituar dhe askujt nuk do t’i bëhet padrejtësi.');
INSERT INTO "hasan_efendi" VALUES(2,282,'2:282','O besimtarë! Shkruajeni huan që ia jepni njëri-tjetrit për një afat të caktuar. Le ta shënojë atë me drejtësi një shkrues ndërmjet jush; asnjë shkrues të mos ngurrojë që ta shënojë ashtu siç e ka mësuar Allahu. Ai le të shënojë, kurse borxhliu le të diktojë dhe le t’i frikësohet Allahut, Zotit të vet, duke mos lënë mangët asgjë nga borxhi! Në qoftë se borxhliu është mendjelehtë ose i mitur, ose i tillë që nuk është në gjendje të diktojë, atëherë le të diktojë saktësisht kujdestari i tij. Thirrni dy dëshmitarë prej njerëzve tuaj e, nëse nuk gjeni dy dëshmitarë burra, atëherë thirrni për dëshmitarë një burrë dhe dy gra, nga ata që i quani të përshtatshëm; në mënyrë që, nëse njëra grua harron, t’ia kujtojë tjetra. Dëshmitarët nuk duhet të kundërshtojnë kurdo që të thirren. Mos e lini pa e shënuar borxhin, qoftë i vogël apo i madh, duke shënuar edhe afatin e pagimit. Kjo mënyrë është më e drejtë para Allahut, më e saktë për dëshmi dhe më e përshtatshme për të mos dyshuar. Por, nëse është fjala për mallin që qarkullon nga dora në dorë, atëherë nuk do të keni gjynah nëse nuk e shënoni atë. Thirrni dëshmitarë edhe kur të lidhni kontrata për shitblerje dhe të mos dëmtohet as shkruesi, as dëshmitari. Nëse bëni të kundërtën, atëherë keni bërë gjynah, prandaj ruajuni Allahut! Allahu ju mëson ju; Allahu është i Dijshëm për çdo gjë. ');
INSERT INTO "hasan_efendi" VALUES(2,283,'2:283','Nëse jeni në udhëtim e nuk gjeni shkrues, atëherë merrni peng. Dhe, nëse njëri prej jush ka besim tek tjetri, atëherë borxhliu le ta kthejë atë që i është besuar dhe le t’i frikësohet Allahut, Zotit të vet. Mos e fshihni dëshminë, se, kush e fsheh atë, zemra e tij është gjynahqare. Allahu di çdo gjë që punoni ju.');
INSERT INTO "hasan_efendi" VALUES(2,284,'2:284','Allahut i përket gjithçka që gjendet në qiej dhe gjithçka që gjendet në Tokë. Edhe nëse e tregoni apo e fshihni çfarë keni në shpirtin tuaj, Allahu do t’ju kërkojë llogari për të. Kështu, Ai do të falë kë të dojë dhe do të dënojë kë të dojë. Allahu është i Fuqishëm për çdo gjë.');
INSERT INTO "hasan_efendi" VALUES(2,285,'2:285','I Dërguari beson në atë (Kuranin) që i është shpallur nga Zoti i tij e po ashtu dhe besimtarët: të gjithë besojnë në Allahun, engjëjt e Tij, librat e Tij dhe të dërguarit e Tij (duke thënë): “Ne nuk bëjmë dallim mes asnjërit prej të dërguarve të Tij”. Ata thonë: “Ne dëgjojmë dhe bindemi. Faljen Tënde kërkojmë, o Zoti Ynë dhe te Ti do të kthehemi!”');
INSERT INTO "hasan_efendi" VALUES(2,286,'2:286','Allahu nuk e ngarkon askënd përtej fuqisë që ka: në dobi të tij është e mira që bën, kurse në dëm të tij është e keqja që punon. Zoti ynë, mos na ndëshko për atë që harrojmë ose veprojmë pa qëllim! Zoti ynë, mos na ngarko barrë të rëndë, ashtu si i ngarkove ata para nesh! Zoti ynë, mos na ngarko me diçka që nuk mund ta bartim! Na i shlyej gjynahet tona, na i fal ato dhe na mëshiro! Ti je Zoti Ynë! Prandaj na jep fitore kundër atyre që nuk besojnë!”');
INSERT INTO "hasan_efendi" VALUES(3,1,'3:1','Elif Lâm Mîm.');
INSERT INTO "hasan_efendi" VALUES(3,2,'3:2','Allahu! Nuk ka zot tjetër (që meriton adhurimin) përveç Tij, të Gjallit, të Përjetshmit, Mbajtësit të gjithçkaje.');
INSERT INTO "hasan_efendi" VALUES(3,3,'3:3','Ai të ka shpallur ty Librin, me të vërtetën e saktë, duke vërtetuar shpalljet e mëparshme. Ai i ka zbritur Teuratin dhe Ungjillin');
INSERT INTO "hasan_efendi" VALUES(3,4,'3:4','më parë si udhërrëfyes për njerëzit dhe ka zbritur edhe Dalluesin (e së vërtetës nga e pavërteta). Ata që nuk besojnë shpalljet e Allahut, i pret dënim i ashpër; Allahu është i Plotfuqishëm dhe i Zoti për shpagim.');
INSERT INTO "hasan_efendi" VALUES(3,5,'3:5','Pa dyshim, Allahut nuk i fshihet asgjë në Tokë dhe as në qiell!');
INSERT INTO "hasan_efendi" VALUES(3,6,'3:6','Ai ju formon në mitër, ashtu siç dëshiron Vetë; s’ka zot tjetër që meriton të adhurohet përveç Tij, të Plotfuqishmit, të Urtit!');
INSERT INTO "hasan_efendi" VALUES(3,7,'3:7','Është Ai që të ka shpallur ty (Muhamed) Librin, disa vargje të të cilit janë të qarta e me kuptim të drejtpërdrejtë. Ato janë themelet e Librit. Kurse disa (vargje) të tjera janë jo krejtësisht të qarta (me kuptime alegorike). Ata, zemrat e të cilëve priren nga e pavërteta, ndjekin vargjet më pak të qarta, duke kërkuar të krijojnë pështjellim dhe duke kërkuar t’i komentojnë sipas dëshirës së vet. Por, kuptimin e tyre të vërtetë e di vetëm Allahu. Ndërsa ata që janë thelluar në dijeni, thonë: “Ne i besojmë (Kuranit). Të gjitha këto (vargje të qarta e alegorike) janë nga Zoti ynë!” Këtë e kuptojnë vetëm mendarët.');
INSERT INTO "hasan_efendi" VALUES(3,8,'3:8','(Ata thonë:) “O Zoti ynë! Mos lejo që zemrat tona të shmangen (nga e vërteta), pasi na ke udhëzuar në rrugën e drejtë, dhe jepna mëshirë prej Teje; vërtet, Ti je Dhuruesi i Madh!');
INSERT INTO "hasan_efendi" VALUES(3,9,'3:9','O Zoti ynë! Ti do t’i mbledhësh njerëzit në një Ditë për të cilën nuk ka dyshim. Me të vërtetë, Allahu nuk e shkel premtimin”.');
INSERT INTO "hasan_efendi" VALUES(3,10,'3:10','Në të vërtetë, atyre që nuk besojnë, nuk do t’u vlejnë aspak (kur të dalin) para Allahut, - as pasuria dhe as fëmijët e tyre. Ata do të jenë lëndë djegëse e zjarrit,');
INSERT INTO "hasan_efendi" VALUES(3,11,'3:11','ashtu siç ishin ithtarët e Faraonit dhe ata që ishin para tyre. Ata i quajtën gënjeshtra Shenjat Tona. Prandaj, Allahu i përlau ata për gjynahet që bënë. Allahu është i rreptë në ndëshkim.');
INSERT INTO "hasan_efendi" VALUES(3,12,'3:12','Thuaju (o Muhamed) atyre që mohuan: “Ju do të mposhteni dhe do të tuboheni në Xhehenem. Eh, sa shtrat i keq është ai vend!”');
INSERT INTO "hasan_efendi" VALUES(3,13,'3:13','Sigurisht që kishte shenjë për ju në dy ushtritë që u ndeshën: njëra që luftonte në rrugën e Allahut dhe tjetra - e mohuesve, së cilës i dukej se para vetes kishte dy herë më shumë kundërshtarë. Allahu me ndihmën e Vet forcon kë të dëshirojë. Kjo, me të vërtetë, është mësim për largpamësit.');
INSERT INTO "hasan_efendi" VALUES(3,14,'3:14','Burrave u është hijeshuar dashuria për gjërat e dëshiruara: për gratë, fëmijët, arin dhe argjendin e grumbulluar, kuajt e bukur, bagëtitë dhe arat e lëruara. Këto janë kënaqësitë e kësaj jete, por shumë më i mirë është kthimi tek Allahu.');
INSERT INTO "hasan_efendi" VALUES(3,15,'3:15','Thuaj (o Muhamed!): “A doni t’ju tregoj për gjëra më të mira se ato? Për ata që janë të devotshëm, te Zoti i tyre do të ketë kopshte, nëpër të cilat rrjedhin lumenj dhe ku do të banojnë përgjithmonë. Aty do të kenë bashkëshorte të pastra dhe kënaqësi nga Allahu”. Allahu i vrojton robërit e Vet');
INSERT INTO "hasan_efendi" VALUES(3,16,'3:16','që thonë: “Zoti ynë! Me të vërtetë kemi besuar, andaj na i fal gjynahet tona dhe na ruaj nga dënimi i zjarrit!”;');
INSERT INTO "hasan_efendi" VALUES(3,17,'3:17','që janë të durueshëm, të sinqertë, të devotshëm në adhurimin e Allahut; që japin lëmoshë dhe që kërkojnë faljen e gjynaheve para agimit.');
INSERT INTO "hasan_efendi" VALUES(3,18,'3:18','Allahu dëshmon, e po ashtu edhe engjëjt, edhe njerëzit e dijes, se nuk ka zot tjetër që meriton të adhurohet përveç Tij, duke mbajtur drejtësinë. S’ka zot tjetër përveç Atij, të Plotfuqishmit, të Urtit.');
INSERT INTO "hasan_efendi" VALUES(3,19,'3:19','Feja e vërtetë tek Allahu është vetëm Islami. Ata që u është dhënë Libri , patën mospajtime nga zilia që kishin midis tyre, vetëm pasi u erdhi dituria. Por kushdo që i mohon udhëzimet e Allahut, ta dijë se Allahu është i shpejtë në llogari.');
INSERT INTO "hasan_efendi" VALUES(3,20,'3:20','Nëse ata të kundërshtojnë ty (o Muhamed) thuaju: “Unë i përulem vetëm Allahut, e po ashtu edhe ata që më pasojnë mua”. Dhe thuaju atyre që u është dhënë Libri si dhe analfabetëve : “A i përuleni Allahut?” Nëse e pranojnë Islamin, atëherë kanë gjetur udhën e drejtë. Por, nëse refuzojnë, detyra jote është vetëm që t’ua përcjellësh dijen. Allahu i vrojton robërit e Vet.');
INSERT INTO "hasan_efendi" VALUES(3,21,'3:21','Lajmëroi ata që mohojnë shpalljet e Allahut, vrasin profetët padrejtësisht dhe vrasin edhe ata që urdhërojnë të veprohet me drejtësi, për dënimin e dhembshëm që i pret.');
INSERT INTO "hasan_efendi" VALUES(3,22,'3:22','Këta janë ata, veprat e të cilëve nuk do të kenë vlerë në këtë botë dhe në tjetrën. Për ata nuk do të ketë asnjë ndihmues.');
INSERT INTO "hasan_efendi" VALUES(3,23,'3:23','A nuk i sheh ata (hebrenj) që u është dhënë një pjesë e Librit?! Atyre iu bëhet thirrje të vijnë te Libri i Allahut për t’i gjykuar, por një pjesë e tyre kthejnë shpinën, duke mos ia vënë veshin.');
INSERT INTO "hasan_efendi" VALUES(3,24,'3:24','Kjo, sepse ata thonë: “Ne nuk do të na prekë zjarri i Xhehenemit, përveçse disa ditë”. Shpifjet që bënin i kanë mashtruar ata në besimin e vet.');
INSERT INTO "hasan_efendi" VALUES(3,25,'3:25','Çfarë do të ndodhë me ata, kur t’i tubojmë në Ditën (e Gjykimit), për të cilën s’ka pikë dyshimi, kur çdokujt do t’i jepet shpërblimi i veprave të veta e askujt nuk do t’i bëhet padrejtësi?');
INSERT INTO "hasan_efendi" VALUES(3,26,'3:26','Thuaj (o Muhamed): “O Allah, Zotëruesi i gjithë Pushtetit! Ti ia jep pushtetin kujt të duash dhe ia heq kujt të duash; Ti lartëson kë të duash dhe poshtëron kë të duash. Çdo e mirë është në Dorën Tënde! Ti je vërtet i Plotfuqishëm për çdo gjë.');
INSERT INTO "hasan_efendi" VALUES(3,27,'3:27','Ti bën që nata të hyjë tek dita dhe që dita të hyjë tek nata. Ti e nxjerr të gjallën prej së vdekurës dhe të vdekurën e nxjerr prej së gjallës. Ti i jep kujt të duash pa kufij.”');
INSERT INTO "hasan_efendi" VALUES(3,28,'3:28','Besimtarët të mos i marrin për miq e mbrojtës mohuesit, në vend të besimtarëve. Ata që veprojnë kështu, nuk kanë çfarë të shpresojnë tek Allahu, vetëm nëse këtë e bëjnë (me gojë e jo me zemër) për t’u ruajtur nga ata. Allahu ju paralajmëron që t’i ruheni Vetes së Tij, sepse të gjithë do të ktheheni tek Allahu.');
INSERT INTO "hasan_efendi" VALUES(3,29,'3:29','Thuaju: “Edhe nëse e fshihni atë që keni në zemrat tuaja apo e shpallni haptazi, Allahu e di atë. Ai di çfarë gjendet në qiej dhe çfarë gjendet në Tokë. Allahu është i Fuqishëm për çdo gjë.”');
INSERT INTO "hasan_efendi" VALUES(3,30,'3:30','(Kini frikë) ditën, kur çdokush do ta gjejë përpara atë që ka punuar mirë dhe atë që ka punuar keq, duke dëshiruar që midis tij dhe së keqes së tij, të ketë largësi të madhe. Allahu ju paralajmëron që t’i ruheni Vetes së Tij. Allahu është i Butë e i Mëshirshëm me robërit e Vet.');
INSERT INTO "hasan_efendi" VALUES(3,31,'3:31','Thuaju (o Muhamed): “Nëse ju e doni Allahun, atëherë më ndiqni mua, që Allahu t’ju dojë dhe t’jua falë gjynahet! Allahu është Falës i Madh dhe Mëshirëplotë”.');
INSERT INTO "hasan_efendi" VALUES(3,32,'3:32','Thuaju: “Bindjuni Allahut dhe të Dërguarit!” Në qoftë se kthejnë shpinën, (dijeni se) Allahu, me të vërtetë, nuk i do jobesimtarët.');
INSERT INTO "hasan_efendi" VALUES(3,33,'3:33','Allahu i zgjodhi Ademin, Nuhun, familjen e Ibrahimit dhe familjen e Imranit mbi të gjithë njerëzit (e kohës së tyre).');
INSERT INTO "hasan_efendi" VALUES(3,34,'3:34','(Ata ishin) pasardhës të njëri - tjetrit. Allahu dëgjon dhe di çdo gjë.');
INSERT INTO "hasan_efendi" VALUES(3,35,'3:35','Kujto kur gruaja e Imranit tha: “Zoti im, unë Ty ta kam kushtuar atë që është në barkun tim, që të të shërbejë vetëm Ty; andaj, pranoje prej meje! Se Ti, me të vërtetë, dëgjon dhe di çdo gjë.”');
INSERT INTO "hasan_efendi" VALUES(3,36,'3:36','E kur ajo lindi, tha: “Zoti im! Unë linda femër, - Allahu e di më mirë se çfarë lindi ajo, - e mashkulli nuk është si femra. Unë ia vura emrin Merjeme dhe i kam lënë atë me pasardhësit e saj nën mbrojtjen Tënde nga djalli i mallkuar”.');
INSERT INTO "hasan_efendi" VALUES(3,37,'3:37','Zoti e pranoi atë (Merjemen) me ëndje, e bëri që të rritet mirë dhe e la nën kujdestarinë e Zekerias. Sa herë që hynte Zekeria në dhomën e saj, gjente pranë saj ushqim dhe e pyeste: “O Merjeme! Nga të vjen ky ushqim?” Ajo përgjigjej: “Ky është nga Allahu, se Allahu e furnizon kë të dojë, pa kufi”.');
INSERT INTO "hasan_efendi" VALUES(3,38,'3:38','Atëherë Zekerija iu lut Zotit të vet e tha: “Zoti im! Dhuromë nga ana Jote një pasardhës të mirë! Se Ti, me të vërtetë, je Dëgjues i lutjes!”');
INSERT INTO "hasan_efendi" VALUES(3,39,'3:39','E ndërsa lutej në këmbë, në faltore, engjëjt e thirrën (Zekerijan): “Allahu të jep lajmin e mirë për Jahjain, i cili do të vërtetojë Fjalën e Allahut (Isain a.s.), do të jetë një fisnik, i dëlirë dhe profet, një prej të mirëve”.');
INSERT INTO "hasan_efendi" VALUES(3,40,'3:40','(Zekerija) tha: “Zoti im! Qysh do të kem djalë, kur mua më ka zënë pleqëria, e gruaja ime është shterpë?! (Engjëlli i) tha: “Kështu! Allahu bën çfarë të dojë”.');
INSERT INTO "hasan_efendi" VALUES(3,41,'3:41','Ai tha: “O Zot! Më jep një shenjë!” (Zoti) i tha: “Shenjë për ty është se tri ditë nuk do t’u flasësh njerëzve, veçse me gjeste. Por, Zotin tënd përmende shumë dhe adhuroje Atë në mbrëmje dhe në mëngjes”.');
INSERT INTO "hasan_efendi" VALUES(3,42,'3:42','(Kujto) kur engjëjt thanë: “O Merjeme! Allahu të ka zgjedhur, të ka pastruar dhe të ka parapëlqyer ty mbi të gjitha gratë e botës.');
INSERT INTO "hasan_efendi" VALUES(3,43,'3:43','O Merjeme! Bindju Zotit tënd! Bëj sexhde dhe përkulu në ruku bashkë me ata që përkulen (duke adhuruar Allahun)!”');
INSERT INTO "hasan_efendi" VALUES(3,44,'3:44','Këto janë lajme të fshehta, që po t’i shpallim ty (o Muhamed). Ti nuk ishe i pranishëm kur ata hodhën short se kush prej tyre do të merrte nën kujdes Merjemen dhe nuk ishe i pranishëm kur ata diskutonin.');
INSERT INTO "hasan_efendi" VALUES(3,45,'3:45','(Kujto) kur engjëjt thanë: “O Merjeme! Allahu të jep lajmin e mirë për një (djalë që do të lindë me një) Fjalë prej Tij: emrin do ta ketë Mesih - Isa i biri i Merjemes, do të jetë i nderuar në këtë botë dhe në tjetrën dhe një nga të afërmit e Allahut.');
INSERT INTO "hasan_efendi" VALUES(3,46,'3:46','Ai do t’u flasë njerëzve qysh i vogël në djep edhe si njeri i rritur dhe do të jetë nga më të mirët”.');
INSERT INTO "hasan_efendi" VALUES(3,47,'3:47','Ajo tha: “Zoti im! Qysh do të kem unë fëmijë, kur nuk më ka prekur askush?!” – “Kështu – tha (engjëlli) - Allahu krijon ç’të dojë Vetë. Kur vendos diçka, Ai vetëm thotë për të: “Bëhu!” dhe ajo bëhet.”');
INSERT INTO "hasan_efendi" VALUES(3,48,'3:48','(Allahu) do t’ia mësojë atij shkrimin, diturinë, Teuratin dhe Ungjillin');
INSERT INTO "hasan_efendi" VALUES(3,49,'3:49','dhe do ta caktojë atë të Dërguar te bijtë e Israilit (të cilëve do t’u thotë): “Ju kam sjellë prova nga Zoti juaj (që vërtetojnë Profecinë time): bëj për ju diçka prej balte në formë shpendi, fryj në të e me urdhrin e Allahut ajo bëhet shpend i gjallë; shëroj të verbrit e lindur dhe të lebrosurit dhe i ngjall të vdekurit me lejen e Allahut; ju tregoj çfarë hani dhe çfarë ruani në shtëpitë tuaja. Sigurisht që te të gjitha këto ka prova për ju, nëse jeni besimtarë të vërtetë.');
INSERT INTO "hasan_efendi" VALUES(3,50,'3:50','Dhe (kam ardhur te ju) për t’ju vërtetuar Teuratin, që është para meje dhe që t’ju lejoj disa gjëra që i keni pasur të ndaluara. Ju kam sjellë një provë nga Zoti juaj, prandaj kijeni frikë Allahun dhe bindmuni!');
INSERT INTO "hasan_efendi" VALUES(3,51,'3:51','Me të vërtetë, Allahu është Zoti im dhe Zoti juaj, andaj adhurojeni Atë. Kjo është rruga e drejtë!”');
INSERT INTO "hasan_efendi" VALUES(3,52,'3:52','Kur Isai vuri re mosbesimin e tyre, tha: “Kush janë ndihmuesit e mi në rrugën e Allahut?” nxënësit (e tij) u përgjigjën: “Ne jemi ndihmuesit e (fesë së) Allahut. Ne e besojmë Allahun e ti (Isa) dëshmo se ne (i) jemi nënshtruar (Atij).');
INSERT INTO "hasan_efendi" VALUES(3,53,'3:53','Zoti ynë! Ne besojmë atë që na ke shpallur dhe pasojmë të Dërguarin (Isain). Na shkruaj pra, bashkë me dëshmitarët!”');
INSERT INTO "hasan_efendi" VALUES(3,54,'3:54','Dhe ata i përgatitën një kurth, por edhe Allahu u përgatiti kurth (dënim), se Allahu është strategu më i mirë.');
INSERT INTO "hasan_efendi" VALUES(3,55,'3:55','(Kujtoje, o Muhamed) kur Allahu tha: “O Isa! Unë do të të marr, do të të ngre pranë Meje dhe do të të shpëtoj ty nga ata që nuk besojnë, si dhe do t’i vendos ata që të ndjekin ty, përmbi ata që të mohojnë, deri në Ditën e Kiametit. Pastaj, të gjithë do të ktheheni tek Unë dhe do të gjykoj midis jush për çështjet që jeni grindur.');
INSERT INTO "hasan_efendi" VALUES(3,56,'3:56','Sa për ata që nuk besojnë, do t’i dënoj me dënim të ashpër në këtë botë dhe në tjetrën. Dhe nuk do të kenë askënd që t’u vijë në ndihmë.');
INSERT INTO "hasan_efendi" VALUES(3,57,'3:57','Ndërsa atyre që besojnë dhe punojnë vepra të mira, Zoti do t’u japë shpërblimin e merituar. Allahu nuk i do punëmbrapshtët.”');
INSERT INTO "hasan_efendi" VALUES(3,58,'3:58','Këto që po t’i tregojmë ty (o Muhamed) janë vargje dhe këshilla të larta (nga Kurani).');
INSERT INTO "hasan_efendi" VALUES(3,59,'3:59','Rasti i Isait për Allahun është si rasti i Ademit që e krijoi prej baltës e pastaj i tha: “Bëhu!” - dhe ai u bë.');
INSERT INTO "hasan_efendi" VALUES(3,60,'3:60','E vërteta është nga Zoti yt, andaj mos u bëj nga ata që dyshojnë!');
INSERT INTO "hasan_efendi" VALUES(3,61,'3:61','Atyre që të kundërshtojnë ty për çështjen e Isait, pasi të ka ardhur njohuria, thuaju: “Ejani t’i thërrasim bijtë tanë dhe bijtë tuaj, gratë tona dhe gratë tuaja, veten tonë dhe ju vetë, pastaj, të lutemi që mallkimi i Allahut t’i godasë gënjeshtarët!”');
INSERT INTO "hasan_efendi" VALUES(3,62,'3:62','Pa dyshim, të gjitha këto janë rrëfime të vërteta. S’ka zot tjetër (që meriton të adhurohet) përveç Allahut. Vërtet, Allahu është i Plotfuqishmi, i Urti.');
INSERT INTO "hasan_efendi" VALUES(3,63,'3:63','Në qoftë se ata ia kthejnë shpinën të drejtës, (dije se) Allahu i njeh mirë ngatërrestarët.');
INSERT INTO "hasan_efendi" VALUES(3,64,'3:64','Thuaj: “O ithtarët e Librit, ejani të biem në një fjalë të përbashkët mes nesh dhe jush: se do të adhurojmë vetëm Allahun, se nuk do t’i shoqërojmë Atij asgjë (në adhurim) dhe se nuk do ta mbajmë për zot njëri-tjetrin, në vend të Allahut!” Nëse ata nuk pranojnë, atëherë thuaju: “Dëshmoni se ne i jemi nënshtruar Allahut!”');
INSERT INTO "hasan_efendi" VALUES(3,65,'3:65','O ithtarët e Librit! Pse polemizoni për Ibrahimin, kur Teurati dhe Ungjilli janë zbritur vetëm pas tij? A nuk kuptoni?');
INSERT INTO "hasan_efendi" VALUES(3,66,'3:66','Mirë ju polemizoni për gjëra që keni njëfarë dijenie (për çështje që lidhen me Musain a.s. dhe Isain a.s.), por, pse polemizoni për atë, për të cilën s’dini asgjë (për Ibrahimin a.s.)? Allahu e di hollësisht, ndërsa ju nuk dini asgjë për këtë.');
INSERT INTO "hasan_efendi" VALUES(3,67,'3:67','Ibrahimi nuk ka qenë as hebre, as i krishterë, por ka qenë besimtar i vërtetë (monoteist) i përulur ndaj Allahut dhe nuk ka qenë nga politeistët.');
INSERT INTO "hasan_efendi" VALUES(3,68,'3:68','Pa dyshim, njerëzit më të afërt me Ibrahimin janë ata që e pasuan atë dhe ky Profet me besimtarët (e tij). Allahu është mbrojtës i besimtarëve.');
INSERT INTO "hasan_efendi" VALUES(3,69,'3:69','Një grup ithtarësh të Librit dëshirojnë që t’ju shpien në rrugë të gabuar. Në të vërtetë ata çojnë në humbje vetëm vetveten, por këtë nuk e ndiejnë.');
INSERT INTO "hasan_efendi" VALUES(3,70,'3:70','O ithtarë të Librit! Përse i mohoni shpalljet e Allahut (Kuranin), edhe pse jeni dëshmitarë se ato janë të vërteta?');
INSERT INTO "hasan_efendi" VALUES(3,71,'3:71','O ithtarët e Librit! Përse e pështjelloni të vërtetën me të pavërtetën dhe përse e fshihni të vërtetën me qëllim?');
INSERT INTO "hasan_efendi" VALUES(3,72,'3:72','Një grup ithtarësh të Librit thanë: “Besojeni në mëngjes atë që u është zbritur besimtarëve, por në mbrëmje mohojeni atë, se ndoshta edhe ata do ta braktisin');
INSERT INTO "hasan_efendi" VALUES(3,73,'3:73','dhe besojuni vetëm atyre që ndjekin fenë tuaj!” Thuaj (o Muhamed): “Udhëzimi i Allahut është udhëzimi i vërtetë”. (Hebrenjtë i thanë njëri-tjetrit:) “Mos besoni se mund t’i jepet ndokujt ajo që ju është dhënë juve (vetëm nëse ndjekin fenë tuaj), përndryshe ata do të polemizojnë kundër jush te Zoti juaj.” Thuaj (o Muhamed): “Hiri është në Dorë të Allahut, Ai ia jep atë kujt të dojë. Allahu është Mirëbërës i madh dhe i Gjithëdijshëm.”');
INSERT INTO "hasan_efendi" VALUES(3,74,'3:74','Mëshirën e Vet, Ai ia dhuron kujt të dojë. Allahu është zotërues i mirësisë së pakufishme.');
INSERT INTO "hasan_efendi" VALUES(3,75,'3:75','Ka nga ata ithtarë të Librit, që sikur t’u besosh një barrë të tërë ari, do t’jua kthejnë, por ka edhe të atillë që, nëse u jep në besim, qoftë edhe një dinar, nuk ta kthejnë, veçse nëse nuk u ndahesh. Kjo është kështu, ngase ata thonë: “Ne nuk kemi ndonjë detyrim ndaj analfabetëve (arabë)”. Ata flasin gënjeshtra për Allahun me vetëdije.');
INSERT INTO "hasan_efendi" VALUES(3,76,'3:76','Jo! Por kushdo që plotëson premtimin e tij dhe ruhet nga të këqijat, (ta dijë se) Allahu, me të vërtetë, i do ata që janë të devotshëm.');
INSERT INTO "hasan_efendi" VALUES(3,77,'3:77','Vërtet, ata që e shkëmbejnë premtimin dhe betimin e tyre ndaj Allahut me një vlerë të vogël, nuk do të kenë asnjë të mirë në jetën e ardhshme. Allahu nuk do t’u flasë atyre, as nuk do t’i shikojë në Ditën e Kiametit dhe as nuk do t’i pastrojë prej gjynaheve e për ta do të ketë dënim të dhembshëm.');
INSERT INTO "hasan_efendi" VALUES(3,78,'3:78','Me të vërtetë, një grup ithtarësh të Librit e shtrembërojnë Librin me gjuhët e tyre (ndërsa e lexojnë atë), që ju të mendoni se ai lloj leximi është prej Librit (që ka zbritur Allahu), kurse në të vërtetë, nuk është prej Librit. Ata thonë: “Kjo që lexojmë është nga Allahu”, por ajo nuk është nga Allahu. Ata flasin gënjeshtra për Allahun, duke qenë të vetëdijshëm për këtë.');
INSERT INTO "hasan_efendi" VALUES(3,79,'3:79','Nuk është (e mundur për) një njeri të cilit Allahu i ka dhënë Librin, dijeninë e gjerë dhe Profecinë, që t’u thotë njerëzve: “Bëhuni robërit e mi dhe jo të Allahut”. Përkundrazi, ai thotë: “Bëhuni robër të ditur të Zotit (që i zbatojnë porositë e Tij), sepse ua keni mësuar Librin të tjerëve dhe e mësoni edhe vetë!”');
INSERT INTO "hasan_efendi" VALUES(3,80,'3:80','Ai njeri nuk do t’ju urdhërojë që engjëjt dhe profetët t’i merrni për zota. Vallë, si mund t’ju urdhërojë ai të bëheni mohues, pasi i jeni nënshtruar Allahut?!');
INSERT INTO "hasan_efendi" VALUES(3,81,'3:81','Kujto kur Allahu mori besëlidhjen nga profetët (dhe u tha): “Çfarëdo që t’ju jap prej Librit dhe urtësisë, kur t’ju vijë një i Dërguar, vërtetues i asaj që ju është dhënë, pa dyshim që duhet ta besoni dhe ta ndihmoni atë! A e pranoni këtë dhe barrën që po ju ngarkoj?” Ata u përgjigjën: “E pranojmë”. (Allahu) u tha: “Bëhuni dëshmitarë, se edhe Unë bashkë me ju jam dëshmitar.');
INSERT INTO "hasan_efendi" VALUES(3,82,'3:82','Kurse ata që i shmangen detyrës pas kësaj, me të vërtetë janë të këqij.”');
INSERT INTO "hasan_efendi" VALUES(3,83,'3:83','A mos kërkojnë ata diçka tjetër përveç fesë së Allahut, ndërkohë që Atij i përulen të gjithë ata që gjenden në qiej dhe në Tokë, me hir a me pahir dhe tek Ai do të kthehen të gjithë?!');
INSERT INTO "hasan_efendi" VALUES(3,84,'3:84','Thuaj (o Muhamed): “Ne besojmë Allahun, atë që na është shpallur neve, atë që i është shpallur Ibrahimit, Ismailit, Is’hakut, Jakubit, Esbatëve (bijve të tij) dhe në atë që i është dhënë Musait, Isait dhe profetëve nga Zoti i tyre. Ne nuk bëjmë asnjë dallim midis tyre dhe Ne vetëm Atij (Allahut) i përulemi.”');
INSERT INTO "hasan_efendi" VALUES(3,85,'3:85','Kush kërkon tjetër fe përveç Islamit, nuk do t’i pranohet dhe ai në botën tjetër do të jetë i humbur. ');
INSERT INTO "hasan_efendi" VALUES(3,86,'3:86','E si t’i udhëzojë Allahu në rrugë të drejtë njerëzit që u bënë mohues, pasi më parë patën besuar e dëshmuar se i Dërguari është i vërtetë dhe u kishin ardhur provat e qarta?! Allahu nuk i udhëzon në rrugë të drejtë ata që janë keqbërës.');
INSERT INTO "hasan_efendi" VALUES(3,87,'3:87','Mallkimi i Allahut, i engjëjve dhe i mbarë njerëzve është shpërblimi i tyre.');
INSERT INTO "hasan_efendi" VALUES(3,88,'3:88','Ata do të jenë përherë nën peshën e (dënimit e të mallkimit), vuajtjet nuk do t’u lehtësohen dhe nuk do t’u jepet afat (pushim);');
INSERT INTO "hasan_efendi" VALUES(3,89,'3:89','përveç atyre që pas kësaj pendohen e përmirësohen, se Allahu është Falës i madh, Mëshirëplotë.');
INSERT INTO "hasan_efendi" VALUES(3,90,'3:90','Vërtet, atyre që mohojnë pasi patën besuar dhe pastaj e shtojnë mohimin e tyre, nuk do t’u pranohet pendimi (kur janë me shpirtin në grykë). Pikërisht këta janë ata që kanë humbur.');
INSERT INTO "hasan_efendi" VALUES(3,91,'3:91','Nuk ka dyshim se atyre që e mohojnë besimin dhe vdesin mosbesimtarë, nuk do t’u pranohet asnjë shpërblesë, edhe sikur ajo të ishte sa e tërë toka e mbuluar me ar. Ata i pret një dënim i dhembshëm dhe askush nuk do t’i ndihmojë.');
INSERT INTO "hasan_efendi" VALUES(3,92,'3:92','Nuk keni për ta arritur përkushtimin e vërtetë, derisa të ndani (lëmoshë) nga ajo (pasuri) që e doni. Çfarëdo që të ndani, Allahu e di mirë atë.');
INSERT INTO "hasan_efendi" VALUES(3,93,'3:93','Të gjitha ushqimet kanë qenë të lejuara për bijtë e Israilit, përveç asaj që Israili ia ndaloi vetes para shpalljes së Teuratit. Thuaj (o Muhamed): “Silleni Teuratin dhe lexojeni atë, nëse thoni të vërtetën!”');
INSERT INTO "hasan_efendi" VALUES(3,94,'3:94','E kushdo që shpif gënjeshtra për Allahun, pas kësaj ai ka kaluar nga e drejta në të shtrembrën.');
INSERT INTO "hasan_efendi" VALUES(3,95,'3:95','Thuaj (o Muhamed!): “Allahu thotë të vërtetën, prandaj ndiqeni fenë e pastër të Ibrahimit , i cili nuk ishte idhujtar”.');
INSERT INTO "hasan_efendi" VALUES(3,96,'3:96','Me të vërtetë, Faltorja e parë e ngritur për njerëzit, është ajo në Bekkë (Mekë), e bekuar dhe udhërrëfyese për popujt.');
INSERT INTO "hasan_efendi" VALUES(3,97,'3:97','Në të ka shenja të qarta (që janë): vendi i Ibrahimit (mekami Ibrahim); (fakti që) kush hyn në të është i sigurt; dhe vizita (për haxh) e kësaj Faltoreje (e cila është detyrë) për këdo që ka mundësi ta përballojë rrugën. E kush nuk beson, Allahu nuk ka nevojë për askënd.');
INSERT INTO "hasan_efendi" VALUES(3,98,'3:98','Thuaj (o Muhamed): “O ithtarët e Librit! Pse mohoni shpalljet e Allahut, ndërkohë që Allahu është dëshmitar i çdo gjëje që bëni?!”');
INSERT INTO "hasan_efendi" VALUES(3,99,'3:99','Thuaj: “O ithtarët e Librit! Pse e pengoni nga rruga e Allahut atë që beson e përpiqeni ta shtrembëroni (atë rrugë), ndërkohë që jeni dëshmitarë (se kjo është rruga e vërtetë)? (Dijeni se) Allahu nuk është i pavëmendshëm ndaj veprave që ju bëni!”');
INSERT INTO "hasan_efendi" VALUES(3,100,'3:100','O besimtarë! Nëse i bindeni një grupi nga ata që u është dhënë Libri, ata do t’ju kthejnë në jobesimtarë, pasi patët besuar.');
INSERT INTO "hasan_efendi" VALUES(3,101,'3:101','E si mund të mos besoni kur juve ju lexohen shpalljet e Allahut dhe midis jush ndodhet i Dërguari i Tij?! Kush mbahet fort pas (fesë së) Allahut, sigurisht që është i drejtuar në udhë të mbarë.');
INSERT INTO "hasan_efendi" VALUES(3,102,'3:102','O besimtarë! Kijeni frikë Allahun ashtu si i takon Atij dhe vdisni vetëm duke qenë myslimanë!');
INSERT INTO "hasan_efendi" VALUES(3,103,'3:103','Të gjithë mbahuni fort për litarin e Allahut (Kuranin) dhe mos u përçani! Kujtoni dhuntinë e Allahut për ju, sepse, kur ishit në armiqësi, Ai i pajtoi zemrat tuaja e, në saje të dhuntisë së Tij, u bëtë vëllezër. Dhe ju ishit buzë greminës së zjarrit, ndërsa Ai ju shpëtoi prej saj. Kështu, Allahu jua shpjegon shpalljet e Veta, që ju të drejtoheni në udhën e drejtë.');
INSERT INTO "hasan_efendi" VALUES(3,104,'3:104','Le të dalë prej jush një grup që të thërrasë për në mirësi , të urdhërojë për vepra të mira e të ndalojë prej veprave të shëmtuara! Këta njerëz do të jenë të shpëtuarit.');
INSERT INTO "hasan_efendi" VALUES(3,105,'3:105','Mos u bëni si ata që, pasi u erdhën provat e qarta, u përçanë dhe u grindën. Për këta do të ketë dënim të madh');
INSERT INTO "hasan_efendi" VALUES(3,106,'3:106','ditën (e Kiametit) kur disa fytyra do të zbardhen e disa të tjera do të nxihen. Atyre që do t’u nxihet fytyra, do t’u thuhet: “Vallë, ju mohuat pas besimit?! Atëherë shijoni dënimin për shkak se nuk besuat!”');
INSERT INTO "hasan_efendi" VALUES(3,107,'3:107','Kurse ato fytyra që do të zbardhen e do të shkëlqejnë, do të jenë në mëshirën e Allahut (në Xhenet), ku do të qëndrojnë përgjithmonë.');
INSERT INTO "hasan_efendi" VALUES(3,108,'3:108','Këto janë vargjet e Allahut, që t’i lexojmë ty me të vërtetën dhe Allahu nuk i dëshiron askujt padrejtësi.');
INSERT INTO "hasan_efendi" VALUES(3,109,'3:109','Allahut i përket gjithçka që gjendet në qiej dhe gjithçka që gjendet në Tokë dhe tek Allahu do të kthehen të gjitha çështjet.');
INSERT INTO "hasan_efendi" VALUES(3,110,'3:110','Ju jeni populli më i mirë i dalë për njerëzimin: (sepse) ju urdhëroni që të bëhen vepra të mira, i ndaloni të këqijat dhe besoni Allahun. Sikur ithtarët e Librit të besonin, do të ishte më mirë për ata; ka prej tyre besimtarë të vërtetë, por shumica e tyre janë larg udhës së drejtë.');
INSERT INTO "hasan_efendi" VALUES(3,111,'3:111','Ata, përveç lëndimit, nuk mund t’ju dëmtojnë ju. Nëse ju luftojnë, ata do të kthejnë thembrat (të mundur) dhe pastaj nuk do të ndihmohen.');
INSERT INTO "hasan_efendi" VALUES(3,112,'3:112','Kudo që gjenden, ata janë të kapluar nga poshtërimi, vetëm nëse kanë ndonjë mbrojtje prej Allahut apo prej njerëzve. Ata merituan zemërimin e Allahut dhe do t’i kaplojë mjerimi. Kjo ishte për shkak se mohuan shpalljet e Tij dhe pa kurrfarë të drejte, i vrisnin profetët. Kjo ishte për shkak se ata nuk u bindën dhe i kaluan kufijtë (në të këqija).');
INSERT INTO "hasan_efendi" VALUES(3,113,'3:113','(Megjithatë) ata nuk janë të gjithë njëlloj. Disa nga ithtarët e Librit janë në të drejtën: në orët e natës lexojnë vargjet e Librit të Allahut dhe falin namaz.');
INSERT INTO "hasan_efendi" VALUES(3,114,'3:114','Ata besojnë Allahun dhe Ditën e Kiametit, e urdhërojnë të mirën, e ndalojnë të keqen dhe nxitojnë për të bërë punë të mira. Këta janë ndër të drejtët.');
INSERT INTO "hasan_efendi" VALUES(3,115,'3:115','Çfarëdo të mire që të bëjnë, nuk do t’iu mohohet shpërblimi. Allahu i di mirë të devotshmit.');
INSERT INTO "hasan_efendi" VALUES(3,116,'3:116','Ata që nuk besojnë, nuk do t’i mbrojë asgjë nga dënimi i Allahut, as pasuria e tyre e as fëmijët e tyre. Ata do të jenë banorët e zjarrit (të Xhehenemit), ku do të qëndrojnë përgjithmonë.');
INSERT INTO "hasan_efendi" VALUES(3,117,'3:117','Gjithçka që jobesimtarët shpenzojnë në jetën e kësaj bote, i përngjan erës së akullt, e cila godet të lashtat e ndonjë populli që i ka bërë dëm vetes dhe ua zhduk të lashtat. Allahu nuk u ka bërë padrejtësi atyre, por ata i kanë bërë padrejtësi vetvetes.');
INSERT INTO "hasan_efendi" VALUES(3,118,'3:118','O besimtarë! Mos zgjidhni për miq dhe këshilltarë të afërt njerëz të tjerë jashtë gjirit tuaj; ata nuk ngurrojnë që t’ju bëjnë dëm. Ata dëshirojnë që të vuani. Urrejtja shfaqet në gjithçka që del prej gojës së tyre, kurse ajo që fshehin në zemrën e tyre, është edhe më e madhe. Ne jua kemi shpjeguar vargjet (e Kuranit), nëse jeni njerëz që kuptoni.');
INSERT INTO "hasan_efendi" VALUES(3,119,'3:119','Ja, ju i doni ata, por ata nuk ju duan ju dhe ju i besoni të gjithë librave (ndërsa ata jo). Kur ata ju takojnë, thonë: “Edhe ne besojmë” e, kur mbeten vetëm, nga mllefi ndaj jush, ata i kafshojnë majat e gishtave me dhëmbë. Thuaju: “Plasni me mllefin tuaj”. Vërtet, Allahu e di mirë ç’kanë në gjokset e tyre.');
INSERT INTO "hasan_efendi" VALUES(3,120,'3:120','Nëse ju vjen ndonjë e mirë, ata i brengos e, nëse ju gjen ndonjë e keqe, ata gëzohen, por nëse ju duroni dhe ruheni nga gjynahet, dinakëritë e tyre nuk mund t’ju dëmtojnë aspak. Vërtet Allahu ka dijeni për çdo gjë që punojnë ata.');
INSERT INTO "hasan_efendi" VALUES(3,121,'3:121','Atë mëngjes, kur ti (o Muhamed) dole nga shtëpia jote për t’u caktuar besimtarëve vendet e luftës - e Allahu dëgjon dhe di çdo gjë -,');
INSERT INTO "hasan_efendi" VALUES(3,122,'3:122','dy grupe nga ju menduan të ktheheshin prej frikës, ndonëse Allahu ishte ndihmues i tyre. Andaj besimtarët le të mbështeten vetëm te Allahu!');
INSERT INTO "hasan_efendi" VALUES(3,123,'3:123','Allahu ju ndihmoi në Bedër, kur qetë të pafuqishëm, andaj frikësojuni Allahut, për të qenë falënderues.');
INSERT INTO "hasan_efendi" VALUES(3,124,'3:124','Atëherë ti (Muhamed) u thoshe besimtarëve: “A nuk ju mjafton ndihma e Zotit tuaj me tre mijë engjëj të dërguar?”');
INSERT INTO "hasan_efendi" VALUES(3,125,'3:125','Po! Por, nëse ata ju sulmojnë, bëhuni të durueshëm dhe të dëgjueshëm dhe Zoti juaj menjëherë do t’ju dërgojë në ndihmë pesë mijë engjëj, me shenjë të dalluar.');
INSERT INTO "hasan_efendi" VALUES(3,126,'3:126','Allahu e bëri këtë që t’ju sjellë lajmin e gëzuar dhe që t’jua qetësojë zemrat. Vërtet, fitorja është vetëm nga Allahu, i Plotfuqishmi dhe i Urti.');
INSERT INTO "hasan_efendi" VALUES(3,127,'3:127','(Gjithashtu Allahu e bëri këtë edhe) që të shkatërrojë një pjesë prej atyre që nuk besuan apo t’i turpërojë, e kështu të kthehen të humbur.');
INSERT INTO "hasan_efendi" VALUES(3,128,'3:128','Ti nuk ke asgjë në dorë, nëse do t’ua pranojë Ai pendimin apo do t’i dënojë, se ata janë vërtet keqbërës.');
INSERT INTO "hasan_efendi" VALUES(3,129,'3:129','Allahut i përket gjithçka që gjendet në qiej dhe gjithçka që gjendet në Tokë! Ai fal kë të dojë dhe dënon kë të dojë. Allahu është Falës i madh e Mëshirëplotë.');
INSERT INTO "hasan_efendi" VALUES(3,130,'3:130','O besimtarë! Mos pranoni kamatë të shumëfishtë mbi kamatë! Kijeni frikë Allahun, që të shpëtoni');
INSERT INTO "hasan_efendi" VALUES(3,131,'3:131','dhe ruajuni zjarrit, i cili është përgatitur për jobesimtarët!');
INSERT INTO "hasan_efendi" VALUES(3,132,'3:132','Bindjuni Allahut dhe të Dërguarit, për të shpresuar mëshirën (e Allahut).');
INSERT INTO "hasan_efendi" VALUES(3,133,'3:133','Nxitoni drejt faljes së gjynaheve nga Zoti juaj dhe Xhenetit, hapësira e të cilit është sa qiejt e Toka dhe që është përgatitur për të devotshmit,');
INSERT INTO "hasan_efendi" VALUES(3,134,'3:134','të cilët japin lëmoshë edhe kur janë në mirëqenie, edhe kur janë në vështirësi, e mposhtin zemërimin dhe ua falin fajet njerëzve. Allahu i do bamirësit.');
INSERT INTO "hasan_efendi" VALUES(3,135,'3:135','Për ata që, kur bëjnë vepra të turpshme ose i bëjnë dëm vetes, e kujtojnë Allahun, i kërkojnë falje për gjynahet e tyre - e kush i fal gjynahet përveç Allahut? - dhe nuk ngulmojnë me vetëdije në gabimet që kanë bërë,');
INSERT INTO "hasan_efendi" VALUES(3,136,'3:136','pra, për këta do të ketë si shpërblim falje madhështore (të gjynaheve) nga ana e Zotit të tyre dhe kopshte, nëpër të cilët rrjedhin lumenj dhe ku do të qëndrojnë përherë. Sa shpërblim i bukur është ky për ata që punojnë (vepra të bukura)!');
INSERT INTO "hasan_efendi" VALUES(3,137,'3:137','Para jush kanë kaluar shumë shembuj (popujsh nga ndjekësit e profetëve që iu ndodhi ajo që po ju ndodh edhe juve). Prandaj udhëtoni nëpër botë dhe shihni si ishte fundi i atyre që i përgënjeshtruan profetët.');
INSERT INTO "hasan_efendi" VALUES(3,138,'3:138','Këto janë lajme të qarta dhe udhërrëfyese për të devotshmit.');
INSERT INTO "hasan_efendi" VALUES(3,139,'3:139','Mos u ligështoni dhe mos u pikëlloni, sepse ju, gjithsesi jeni më të lartët, nëse jeni besimtarë të vërtetë.');
INSERT INTO "hasan_efendi" VALUES(3,140,'3:140','Nëse ju ka goditur ndonjë plagë, edhe popullin (armik) e kanë goditur të tilla plagë. Ato ditë (fitoreje dhe humbjeje) Ne i ndërrojmë midis njerëzve, që Allahu t’i dallojë besimtarët dhe për t’i bërë disa nga ju dëshmorë. Allahu nuk i do keqbërësit.');
INSERT INTO "hasan_efendi" VALUES(3,141,'3:141','E kështu, Allahu i dëlir besimtarët e i shuan jobesimtarët.');
INSERT INTO "hasan_efendi" VALUES(3,142,'3:142','A mendoni se do të hyni në Xhenet, pa i provuar Allahu ata që kanë luftuar prej jush dhe pa i provuar ata që kanë duruar?');
INSERT INTO "hasan_efendi" VALUES(3,143,'3:143','Ju e dëshironit vdekjen para se të ballafaqoheshit me të dhe ja pra, ju e patë atë me sytë tuaj.');
INSERT INTO "hasan_efendi" VALUES(3,144,'3:144','Muhamedi është vetëm i Dërguar, para të cilit ka pasur edhe të dërguar të tjerë. Nëse ai vdes ose vritet, a mos do të ktheheni ju prapa? Kushdo që kthehet prapa, nuk i bën kurrfarë dëmi Allahut. Dhe Allahu do t’i shpërblejë falënderuesit.');
INSERT INTO "hasan_efendi" VALUES(3,145,'3:145','Çdo shpirt vdes vetëm me vendimin e Allahut, në kohë të caktuar. Kush dëshiron mirësitë e kësaj bote, Ne do t’i japim atij prej saj, ndërsa kush dëshiron shpërblimin e botës tjetër, Ne do t’i japim atij prej saj. Ne do t’i shpërblejmë mirënjohësit.');
INSERT INTO "hasan_efendi" VALUES(3,146,'3:146','Sa e sa profetë bashkë me shumë besimtarë të vërtetë luftuan e megjithatë nuk u përkulën para goditjeve që morën në rrugën e Allahut, as nuk u dobësuan e as nuk u nënshtruan. Allahu i do të duruarit.');
INSERT INTO "hasan_efendi" VALUES(3,147,'3:147','Ata vetëm thoshin: “O Zoti ynë! Na i fal gjynahet tona dhe teprimin në punën tonë, na i forco këmbët (në istikam) dhe na ndihmo kundër popullit jobesimtar!”');
INSERT INTO "hasan_efendi" VALUES(3,148,'3:148','Dhe Allahu u dhuroi atyre shpërblimin e kësaj bote dhe shpërblimin më të mirë të jetës tjetër. Allahu i do punëmirët.');
INSERT INTO "hasan_efendi" VALUES(3,149,'3:149','O besimtarë! Nëse u bindeni jobesimtarëve, ata do t’ju kthejnë prapa (në të pafe) e ju do të jeni të humbur.');
INSERT INTO "hasan_efendi" VALUES(3,150,'3:150','Por, Allahu është Mbrojtësi juaj dhe Ai është Ndihmuesi më i mirë.');
INSERT INTO "hasan_efendi" VALUES(3,151,'3:151','Ne do të mbjellim frikë në zemrat e jobesimtarëve, sepse ata adhurojnë veç Allahut gjëra, për të cilat Ai nuk ka sjellë kurrfarë prove. Vendbanimi i tyre është zjarri e sa vendstrehim i shëmtuar është ky për keqbërësit!');
INSERT INTO "hasan_efendi" VALUES(3,152,'3:152','Vërtet Allahu e plotësoi premtimin e Vet, kur ju me vullnetin e Tij e thyet armikun. Por, kur ju u frikësuat, u grindët midis jush dhe kundërshtuat (Profetin a.s.), pasi Ai ju tregoi atë që pëlqenit , (Allahu e tërhoqi ndihmën e Vet). Disa nga ju dëshirojnë këtë botë, ndërsa të tjerë nga ju dëshirojnë botën tjetër. Pastaj Ai, për t’ju provuar, bëri që të zmbrapseni para armikut dhe pastaj, sigurisht që jua fali atë gabim. Allahu ka mirësi të madhe për besimtarët.');
INSERT INTO "hasan_efendi" VALUES(3,153,'3:153','Kur ju morët malin dhe nuk i vinit vesh askujt, të Dërguarin e kishit prapa e ju thërriste, Allahu ju dënoi me dëshpërim pas dëshpërimit, që të mos pikëlloheni as për atë që ju doli duarsh, as për atë që ju goditi. Allahu di gjithçka që punoni ju.');
INSERT INTO "hasan_efendi" VALUES(3,154,'3:154','Pastaj, pas dëshpërimit tuaj, (Allahu) ju dërgoi qetësinë - një gjumë, që kaploi një grup prej jush. Kurse grupi tjetër (hipokritët) kujdesej vetëm për veten, duke menduar për Allahun të pavërtetën, ashtu si mendonin paganët. Ata thoshin: “Ku është fitorja që na ishte premtuar?” Thuaju (o Muhamed!): “Të gjitha janë në dorë të Allahut!” Ata fshehin në vete atë që nuk duan të ta shfaqin ty. Ata thoshin: “Sikur të pyeteshim ne për këtë gjë, nuk do të vriteshim këtu”. Thuaju: “Edhe sikur të kishit qenë nëpër shtëpitë tuaja, prapëseprapë atyre që u është shkruar të vriten, do të dilnin në vendin e vrasjes.” Allahu e bëri këtë për të provuar atë që keni në kraharorët tuaj dhe për t’ju pastruar atë që keni në zemrat tuaja. Vërtet, Allahu e di mirë se ç’keni në zemër.');
INSERT INTO "hasan_efendi" VALUES(3,155,'3:155','Ata prej jush që kthyen shpinën ditën kur u ndeshën dy ushtritë, në të vërtetë, djalli i shtyu në gabim, për shkak të disa gjynaheve që kishin bërë. Por, në të vërtetë, Allahu ua fali (gabimin), sepse Allahu është Falës i madh dhe i Butë.');
INSERT INTO "hasan_efendi" VALUES(3,156,'3:156','O besimtarë! Mos bëni si ata jobesimtarë (hipokritët) që u thonin vëllezërve, kur këta niseshin në udhëtim ose në luftim: “Sikur të rrinit me ne, as nuk do të vdisnit, as nuk do të vriteshit” - që këtë Allahu ta kthejë si pikëllim në zemrat e tyre. Allahu është Ai që jep jetën dhe sjell vdekjen; Allahu i sheh të gjitha punët që bëni ju.');
INSERT INTO "hasan_efendi" VALUES(3,157,'3:157','Edhe nëse ju vriteni ose vdisni në udhë të Allahut, falja dhe mëshira e Allahut, me të vërtetë, janë më të mira nga ato që kanë grumbulluar ata.');
INSERT INTO "hasan_efendi" VALUES(3,158,'3:158','Edhe po vdiqët apo u vratë, me siguri do të tuboheni tek Allahu.');
INSERT INTO "hasan_efendi" VALUES(3,159,'3:159','Në saje të mëshirës së Allahut, u solle butësisht me ta (o Muhamed). Sikur të ishe i ashpër dhe i vrazhdë, ata do të largoheshin prej teje. Prandaj falua atyre gabimin, kërkoi falje Allahut për ata dhe këshillohu me ata për çështje të ndryshme. Kur të vendosësh për diçka, mbështetu tek Allahu. Vërtet, Allahu i do ata që mbështeten tek Ai.');
INSERT INTO "hasan_efendi" VALUES(3,160,'3:160','Nëse ju ndihmon Allahu, s’ka kush t’ju mposhtë e, nëse ju lë pa ndihmën e Tij, kush është ai që do t’ju ndihmojë, përveç Tij?! Pra, vetëm tek Allahu le të mbështeten besimtarët.');
INSERT INTO "hasan_efendi" VALUES(3,161,'3:161','Është e pamundur për ndonjë profet që të përvetësojë në mënyrë të pandershme prenë e luftës. Kushdo që bën një gjë të tillë, do të vijë (i ngarkuar me të) në Ditën e Kiametit e pastaj do t’i jepet çdokujt ajo që ka merituar dhe askujt nuk do t’i bëhet padrejtësi.');
INSERT INTO "hasan_efendi" VALUES(3,162,'3:162','Vallë, a mund të krahasohet ai që kërkon kënaqësinë e Allahut me atë që ka merituar zemërimin e Allahut dhe strehimi i tij është Xhehenemi?! Eh, sa strehim i keq është ai!');
INSERT INTO "hasan_efendi" VALUES(3,163,'3:163','Tek Allahu ata kanë shkallë të ndryshme; Allahu i sheh të gjitha veprat që ata bëjnë.');
INSERT INTO "hasan_efendi" VALUES(3,164,'3:164','Allahu u ka dhënë besimtarëve dhuratë të madhe, kur u solli një të dërguar nga gjiri i tyre, i cili ua lexon shpalljet e Tij, i pastron nga gjynahet dhe u mëson Librin (Kuranin) e urtësinë (diturinë e thellë fetare), edhe pse më parë, me të vërtetë, ata ishin në rrugë krejt të gabuar.');
INSERT INTO "hasan_efendi" VALUES(3,165,'3:165','Kur ju goditi fatkeqësia, ndërkohë ju i kishit goditur më parë armiqtë tuaj dyfish, ju thatë: “Prej nga na erdhi neve kjo?!” Thuaj (o Muhamed): “Kjo ndodhi prej jush”. Vërtet, Allahu është i Fuqishëm për çdo gjë.');
INSERT INTO "hasan_efendi" VALUES(3,166,'3:166','Ajo që ju gjeti atë ditë, kur u ndeshën dy ushtritë, ishte me vullnetin e Allahut - për të zbuluar se cilët janë besimtarët e vërtetë,');
INSERT INTO "hasan_efendi" VALUES(3,167,'3:167','dhe për të zbuluar se cilët janë me dy faqe. Atyre iu tha: “Ejani të luftoni në rrugën e Allahut ose të paktën, mbrohuni!” Ata u përgjigjën: “Sikur ta dinim që do të bëhej luftë e vërtetë, me siguri, do t’ju pasonim.” Atë ditë ata ishin më afër mohimit sesa besimit, sepse me gojën e tyre flisnin atë që nuk e kishin në zemrat e tyre; Allahu e di mirë çfarë fshehin ata.');
INSERT INTO "hasan_efendi" VALUES(3,168,'3:168','Atyre që nuk luftuan dhe thanë për vëllezërit e vet: “Sikur të na kishin dëgjuar ne, nuk do të ishin vrarë”, thuaju: “Prapësojeni vdekjen nga vetja, nëse flisni të vërtetën!”');
INSERT INTO "hasan_efendi" VALUES(3,169,'3:169','Kurrsesi mos i quani të vdekur ata që janë vrarë në rrugën e Allahut. Jo, janë të gjallë, duke u ushqyer te Zoti i tyre.');
INSERT INTO "hasan_efendi" VALUES(3,170,'3:170','Janë të gëzuar për çfarë u ka dhënë Allahu nga dhuntitë e Tij dhe gëzohen edhe për ata që ende nuk u janë bashkuar e që kanë ngelur pas tyre (dhe ende nuk kanë rënë martirë), sepse nuk do të frikësohen (për çfarë i pret) dhe nuk do të pikëllohen (për çfarë kanë lënë pas).');
INSERT INTO "hasan_efendi" VALUES(3,171,'3:171','Ata gëzohen me dhuntitë dhe mirësinë e Allahut, sepse Allahu nuk ua humb shpërblimin besimtarëve.');
INSERT INTO "hasan_efendi" VALUES(3,172,'3:172','Për ata që morën plagë të rënda, pasi iu përgjigjën thirrjes së Allahut dhe të të Dërguarit dhe për ata që kanë bërë të mira e që i frikësohen Allahut, ka shpërblim të madh.');
INSERT INTO "hasan_efendi" VALUES(3,173,'3:173','(Shpërblim kanë edhe ata) të cilëve njerëzit (hipokritë) u thanë: “Shumë njerëz po mblidhen kundër jush, prandaj frikësohuni!” e kjo gjë ua shtoi atyre besimin dhe thanë: “Neve na mjafton Allahu, Ai është mbrojtës i mrekullueshëm!”');
INSERT INTO "hasan_efendi" VALUES(3,174,'3:174','Ata u kthyen me dhuntitë dhe mirësitë e Allahut, nuk i gjeti asgjë e keqe dhe fituan kënaqësinë e Allahut. Allahu është Zotërues i mirësive të mëdha.');
INSERT INTO "hasan_efendi" VALUES(3,175,'3:175','Vetëm djalli i frikëson ithtarët e vet. Ju mos iu frikësoni atyre, por frikësohuni prej Meje, nëse jeni besimtarë!');
INSERT INTO "hasan_efendi" VALUES(3,176,'3:176','Ti (o Muhamed) mos u brengos nga ata që nxitojnë në mohim, sepse, në të vërtetë ata nuk mund ta dëmtojnë Allahun aspak. Allahu dëshiron që të mos i shpërblejë me kurrfarë dhurate në botën tjetër; dhe për ata ka ndëshkim të madh.');
INSERT INTO "hasan_efendi" VALUES(3,177,'3:177','Ata që e ndërrojnë besimin e vërtetë me mohim, nuk mund t’i bëjnë kurrfarë dëmi Allahut e për ta ka ndëshkim të dhembshëm.');
INSERT INTO "hasan_efendi" VALUES(3,178,'3:178','Le të mos mendojnë kurrsesi jobesimtarët se për ata është mirë që ua zgjasim jetën. Ne ua zgjasim jetën vetëm për t’i shtuar gjynahet; për ata ka ndëshkim poshtërues.');
INSERT INTO "hasan_efendi" VALUES(3,179,'3:179','Allahu nuk i lë besimtarët në gjendjen që ndodheni ju, por do ta ndajë të keqin prej të mirit. Allahu nuk do t’ua zbulojë të fshehtën (që vetëm Ai e di), por Allahu zgjedh kë të dojë nga të dërguarit e Tij. Besoni në Allahun dhe në të dërguarit e Tij! Nëse besoni dhe i frikësoheni Allahut, ju do të keni shpërblim të madh.');
INSERT INTO "hasan_efendi" VALUES(3,180,'3:180','Ata që bëhen koprracë me të mirat që ua ka dhënë Allahu, kurrsesi të mos mendojnë se kështu është më mirë për ta! Jo, kjo është më keq për ata! Ajo që kanë grumbulluar do t’u ngarkohet atyre në qafë Ditën e Kiametit. E Allahut është trashëgimia e qiejve dhe e Tokës dhe Allahu e di mirë ç’punoni ju.');
INSERT INTO "hasan_efendi" VALUES(3,181,'3:181','Sigurisht që Allahu i ka dëgjuar fjalët e atyre (çifutëve) që thanë: “Allahu është i varfër, kurse ne jemi të pasur”. Ne do ta shënojmë atë që thanë, si dhe vrasjen e profetëve nga ana e tyre pa kurrfarë të drejte dhe do t’u themi atyre: “Shijoni dënimin në zjarr!”');
INSERT INTO "hasan_efendi" VALUES(3,182,'3:182','Ky është ndëshkimi i veprave të duarve tuaja, se Allahu nuk është i padrejtë ndaj robërve të Vet.');
INSERT INTO "hasan_efendi" VALUES(3,183,'3:183','Atyre që thonë: “Allahu na ka urdhëruar që të mos i besojmë asnjë të dërguari, derisa të na sjellë një kurban që ta përpijë zjarri”, thuaju (o Muhamed): “Të dërguarit para meje ju kanë sjellë juve prova të qarta si dhe atë që thoni ju, atëherë, pse i vratë ata, nëse thoni të vërtetën?”');
INSERT INTO "hasan_efendi" VALUES(3,184,'3:184','Nëse ata ty (o Muhamed) të quajnë për gënjeshtar, edhe të dërguar të tjerë që kanë ardhur para teje janë përgënjeshtruar, ndonëse kanë sjellë shenja të qarta, shkrime dhe Librin e ndritur.');
INSERT INTO "hasan_efendi" VALUES(3,185,'3:185','Çdo shpirt do ta shijojë vdekjen. Shpërblimet do t’u plotësohen vetëm në Ditën e Kiametit; kushdo që shpëton nga zjarri i Xhehenemit dhe hyn në Xhenet, ai ka fituar (gjithçka që mund të dëshirohet). Ndërsa jeta e kësaj bote është vetëm kënaqësi mashtruese.');
INSERT INTO "hasan_efendi" VALUES(3,186,'3:186','Ju, me siguri, do të sprovoheni në pasurinë e në jetën tuaj dhe do të dëgjoni shumë fyerje nga popujt që iu është dhënë Libri para jush, si dhe nga paganët. Por, nëse ju duroni dhe ruheni (nga të këqijat), atëherë kjo është vërtet përcaktuese e çështjeve.');
INSERT INTO "hasan_efendi" VALUES(3,187,'3:187','Kujtoje (o Muhamed) kohën kur Allahu mori premtimin nga ata që u është dhënë Libri se do t’ua shpjegojnë atë njerëzve dhe nuk do të fshehin asgjë nga ai. Pastaj ata e hodhën pas shpine (premtimin) dhe e ndërruan atë me një vlerë të vogël. Eh, sa këmbim të shëmtuar që bënë ata!');
INSERT INTO "hasan_efendi" VALUES(3,188,'3:188','Mos mendo se ata që gëzohen për çfarë kanë punuar dhe dëshirojnë të lavdërohen për çfarë nuk kanë punuar, pra, kurrsesi mos mendo se ata do t’i shpëtojnë ndëshkimit; për ata ka ndëshkim të dhembshëm.');
INSERT INTO "hasan_efendi" VALUES(3,189,'3:189','Allahut i përket sundimi i qiejve dhe i Tokës dhe (vetëm) Allahu është i Fuqishëm për çdo gjë.');
INSERT INTO "hasan_efendi" VALUES(3,190,'3:190','Me të vërtetë, në krijimin e qiejve dhe të Tokës dhe në ndërrimin e natës e të ditës, ka shenja për mendarët,');
INSERT INTO "hasan_efendi" VALUES(3,191,'3:191','për ata që e përmendin Allahun duke qëndruar në këmbë, ndenjur ose shtrirë dhe që meditojnë për krijimin e qiejve dhe të Tokës (duke thënë:) “O Zoti Ynë! Ti nuk i ke krijuar kot këto, lartësuar qofsh (nga çdo e metë)! Prandaj na ruaj nga ndëshkimi i zjarrit.');
INSERT INTO "hasan_efendi" VALUES(3,192,'3:192','O Zoti Ynë, cilindo që Ti e fut në zjarr, Ti e ke poshtëruar atë. Dhe për mohuesit nuk do të ketë kurrfarë ndihmuesi.');
INSERT INTO "hasan_efendi" VALUES(3,193,'3:193','O Zoti Ynë! Ne dëgjuam një thirrës që na ftonte në besim: “Besoni Zotin tuaj!” dhe kështu besuam. O Zoti Ynë! Na i fal gjynahet tona, na i shlyej gabimet dhe bëna që të vdesim me të mirët!');
INSERT INTO "hasan_efendi" VALUES(3,194,'3:194','O Zoti Ynë! Na e jep shpërblimin që na ke premtuar nëpërmjet të dërguarve të Tu dhe mos na poshtëro në Ditën e Kiametit! Se Ti, me të vërtetë, nuk e shkel premtimin e dhënë!”');
INSERT INTO "hasan_efendi" VALUES(3,195,'3:195','Dhe Zoti iu përgjigj lutjes së tyre: “Unë nuk do t’ia humb mundin askujt nga ju që ka bërë vepra, qoftë mashkull apo femër. Ju jeni njëlloj (në shpërblim). Atyre që u shpërngulën, u dëbuan nga vatrat e tyre, u munduan në rrugën Time, luftuan dhe u vranë, Unë do t’ua mbuloj veprat e këqija dhe do t’i shpie në kopshte, nëpër të cilat rrjedhin lumenj, si shpërblim nga Allahu. Shpërblimi më i mirë është tek Allahu.”');
INSERT INTO "hasan_efendi" VALUES(3,196,'3:196','Ti (o Muhamed) mos u mashtro nga bredhja e jobesimtarëve nëpër botë!');
INSERT INTO "hasan_efendi" VALUES(3,197,'3:197','Kjo mirëqenie është e shkurtër; pastaj, strehimi i tyre është Xhehenemi. Eh, sa shtrat i keq është ai vend!');
INSERT INTO "hasan_efendi" VALUES(3,198,'3:198','Por, ata që i frikësohen Zotit të tyre do të kenë kopshte nëpër të cilat rrjedhin lumenj dhe ku do të banojnë përjetësisht, si dhuratë prej Allahut. Dhe ajo që është tek Allahu, është dhurata më e mirë për besimtarët e vërtetë.');
INSERT INTO "hasan_efendi" VALUES(3,199,'3:199','Midis ithtarëve të Librit, me siguri ka të atillë që e besojnë Allahun dhe atë që ju është shpallur juve, si dhe atë që u është shpallur atyre, duke qenë të përulur para Allahut dhe pa i këmbyer shpalljet e Tij me ndonjë vlerë të paktë. Ata do të kenë shpërblimin e tyre te Zoti i tyre. Vërtet, Allahu është i shpejtë në llogari!');
INSERT INTO "hasan_efendi" VALUES(3,200,'3:200','O besimtarë! Bëhuni të durueshëm dhe nxiteni njëri-tjetrin të jeni të tillë; bëhuni të vendosur dhe vigjilentë (në vepra të mira dhe në ruajtjen e kufijve) dhe kijeni frikë Allahun, që të shpëtoni!');
INSERT INTO "hasan_efendi" VALUES(4,1,'4:1','O njerëz! Frikësojuni Zotit tuaj, i Cili ju krijoi prej një njeriu, ndërsa prej atij krijoi bashkëshorten e tij, kurse prej këtyre të dyve krijoi shumë meshkuj e femra. Dhe frikësojuni Allahut, me emrin e të Cilit ju kërkoni të drejtat e ndërsjella dhe ruani lidhjet farefisnore. Se vërtet, Allahu është përherë Mbikëqyrës mbi ju.');
INSERT INTO "hasan_efendi" VALUES(4,2,'4:2','Jepuni pasurinë jetimëve, mos ua ndërroni sendet e tyre të mira me tuajat të këqija. Mos e harxhoni pasurinë e tyre duke e përzier me pasurinë tuaj. Se kjo, me të vërtetë, është gjynah i madh.');
INSERT INTO "hasan_efendi" VALUES(4,3,'4:3','Nëse keni frikë se nuk bëni drejtësi ndaj jetimeve, atëherë, martohuni me ato gra që ju pëlqejnë: me dy, me tri apo me katër; e, nëse frikësoheni se nuk do të jeni të drejtë ndaj të gjithave njësoj, atëherë mjaftohuni me një grua ose me atë që e keni në pushtetin tuaj. Në këtë mënyrë do t’i ruheni padrejtësisë më lehtë.');
INSERT INTO "hasan_efendi" VALUES(4,4,'4:4','Dhe jepuni me gjithë zemër grave tuaja dhuratën e kurorëzimit. Në qoftë se ato ju falin me dëshirën e vet diçka prej asaj (dhurate), pranojeni atë pa frikë dhe me kënaqësi!');
INSERT INTO "hasan_efendi" VALUES(4,5,'4:5','Mos e lini në dorë të të paaftëve mendërisht pasurinë tuaj që Allahu jua ka dhënë si mjet jetese për ju; ushqejini dhe vishini ata prej saj dhe u thoni atyre fjalë të mira. ');
INSERT INTO "hasan_efendi" VALUES(4,6,'4:6','Vërini në provë jetimët, derisa të bëhen për martesë! Nëse vëreni se janë pjekur, atëherë jepuani pasurinë e tyre! Mos nxitoni në shpenzimin e pasurisë së tyre në mënyrë të panevojshme dhe para se ata të rriten! Kush është pasanik, le të përmbahet në marrjen e pasurisë së tyre e, në qoftë se është i varfër, le të harxhojë prej saj aq sa është e arsyeshme dhe e drejtë. Kur t’ua dorëzoni pasurinë e tyre, bëjeni këtë në prani të dëshmitarëve. Allahu është i mjaftueshëm si llogaritës.');
INSERT INTO "hasan_efendi" VALUES(4,7,'4:7','Meshkujve u takon pjesë nga ajo që ua kanë lënë prindërit dhe të afërmit, por, edhe femrave u takon pjesë nga ajo që ua kanë lënë prindërit dhe të afërmit qoftë ajo pak apo shumë, si pjesë e caktuar dhe e detyruar.');
INSERT INTO "hasan_efendi" VALUES(4,8,'4:8','E kur në ndarjen e trashëgimisë të jenë të pranishëm farefisi i largët, jetimët dhe të varfrit, u dhuroni edhe atyre diçka dhe thuajuni fjalë të ëmbla.');
INSERT INTO "hasan_efendi" VALUES(4,9,'4:9','Le të druajnë nëse ata lënë pas vetes fëmijë të mitur, duke u brengosur për ta, le të kenë frikë Allahun dhe le të flasin drejt.');
INSERT INTO "hasan_efendi" VALUES(4,10,'4:10','Vërtet, ata që shpenzojnë pasurinë e jetimit pa të drejtë, vetëm mbushin barkun e tyre me zjarr dhe ata do të digjen në zjarrin flakërues.');
INSERT INTO "hasan_efendi" VALUES(4,11,'4:11','Allahu ju urdhëron për trashëgiminë tuaj: mashkullit i takon aq sa pjesa e dy femrave; nëse trashëgimtarë janë dy e më tepër femra, atyre u takojnë dy të tretat nga trashëgimia; nëse është një femër - i takon gjysma e pasurisë; e për çdonjërin prind të të vdekurit - u përket një e gjashta nga pasuria e trashëguar, nëse i vdekuri ka lënë fëmijë; e nëse nuk ka fëmijë, e trashëgojnë prindërit dhe nënës i bie një e treta; nëse i vdekuri ka vëllezër, nënës së tij i bie një e gjashta, pas kryerjes së testamentit të lënë dhe larjes së borxhit. Ju nuk e dini se cili është më i afërt për ju nga dobia: prindërit tuaj apo fëmijët tuaj. Ky caktim është nga ana e Allahut. Pa dyshim Allahu është i Gjithëdijshëm dhe i Urtë.');
INSERT INTO "hasan_efendi" VALUES(4,12,'4:12','Juve ju takon gjysma e pasurisë nga ajo që kanë lënë bashkëshortet tuaja, nëse ato nuk kanë fëmijë. Por, nëse kanë fëmijë, ju takon një e katërta e asaj që kanë lënë ato, pasi të kryhet testamenti e të lahet borxhi. Në qoftë se nuk keni fëmijë, atyre (grave) u takon një e katërta e pasurisë që keni lënë e, nëse keni fëmijë, atyre u takon një e teta nga ajo që keni lënë, pasi të kryhet testamenti e të lahet borxhi. Nëse i vdekuri, qoftë burrë ose grua, është pa prindër dhe fëmijë, por ka vëlla ose motër (prej nënës), atëherë çdonjërit prej tyre i takon një e gjashta; e nëse ka më shumë nga ata, atëherë marrin pjesë bashkërisht në një të tretën e pasurisë, pasi të kryhet testamenti e të lahet borxhi, duke mos e dëmtuar askënd. Kjo është porosia e Allahut! Allahu është i Gjithëdijshëm dhe shumë Durues.');
INSERT INTO "hasan_efendi" VALUES(4,13,'4:13','Këta janë kufijtë e caktuar prej Allahut. Atë që i bindet Allahut dhe të Dërguarit të Tij, Ai e shpie në kopshte, nëpër të cilët rrjedhin lumenj dhe ku do të qëndrojë përherë. Kjo është fitorja e madhe.');
INSERT INTO "hasan_efendi" VALUES(4,14,'4:14','Ndërsa atë që nuk i bindet Allahut dhe të Dërguarit të Tij dhe shkel kufijtë e vënë prej Tij, Allahu e shpie në zjarr, ku do të qëndrojë përherë dhe për të ka ndëshkim poshtërues.');
INSERT INTO "hasan_efendi" VALUES(4,15,'4:15','Për ato gra tuaja që bëjnë kurvëri, kërkoni që të dëshmojnë për këtë gjë katër veta prej jush; nëse dëshmojnë për këtë gjë, i mbani ato në shtëpi derisa t’u vijë vdekja ose derisa Allahu t’u japë atyre rrugëdalje tjetër.');
INSERT INTO "hasan_efendi" VALUES(4,16,'4:16','Dhe ata të dy prej jush që kanë bërë këtë punë të turpshme, i ndëshkoni që të dy; e nëse pendohen dhe përmirësohen, ua hiqni dënimin. Se Allahu e pranon pendimin dhe është Mëshirues.');
INSERT INTO "hasan_efendi" VALUES(4,17,'4:17','Allahu është pranues i pendimit vetëm për ata që bëjnë ndonjë të keqe nga padituria dhe pendohen shpejt. Pra, këtyre Allahu ua pranon pendimin. Allahu është i Gjithëdijshëm dhe i Urtë.');
INSERT INTO "hasan_efendi" VALUES(4,18,'4:18','Nuk pranohet pendimi i atyre që vazhdimisht bëjnë të këqija e, kur u afrohet vdekja, atëherë thonë: “Unë tani me të vërtetë po pendohem”. As për ata që vdesin duke qenë jobesimtarë. Për këta, Ne kemi përgatitur dënim të dhembshëm.');
INSERT INTO "hasan_efendi" VALUES(4,19,'4:19','O besimtarë! - nuk ju lejohet që t''i merrni në trashëgim gratë me forcë. Mos i trajtoni ato ashpër me qëllimin që t’u merrni një pjesë të dhuratës martesore që u keni dhënë. (Një gjë e tillë ju lejohet) vetëm atëherë kur ato bëjnë imoralitet të hapur. Jetoni e silluni mirë me to! Në qoftë se ato nuk ju pëlqejnë (atëherë duroni sepse), është e mundur që juve të mos ju pëlqejë diçka, por që Allahu t’ju sjellë nëpërmjet saj shumë të mira.');
INSERT INTO "hasan_efendi" VALUES(4,20,'4:20','Në qoftë se dëshironi të ndërroni grua, duke e lëshuar njërën për të marrë një tjetër, asaj që i keni dhënë pasuri , mos i merrni nga ajo (pasuri) asgjë. A do ta merrnit atë padrejtësisht, duke bërë gjynah të qartë?!');
INSERT INTO "hasan_efendi" VALUES(4,21,'4:21','Si do t’ia merrnit atë dhuratë, kur ju i jeni dhënë njëri-tjetrit dhe kur ato kanë marrë nga ju besë të fortë?');
INSERT INTO "hasan_efendi" VALUES(4,22,'4:22','Mos i merrni ato gra që kanë qenë gratë e baballarëve tuaj, përveç rasteve që tashmë kanë kaluar. Vërtet, kjo është vepër e keqe dhe rrugë e shëmtuar.');
INSERT INTO "hasan_efendi" VALUES(4,23,'4:23','Ju ndalohet martesa me nënat tuaja, me bijat tuaja, me motrat tuaja, me hallat tuaja, me tezet tuaja, me bijat e vëllezërve dhe bijat e motrave, me nënat (gratë) që ju kanë dhënë gji, me motrat tuaja, që kanë thithur prej një gjiri, me nënat e grave tuaja, me vajzën e gruas suaj që gjendet nën kujdestarinë tuaj, nëse me atë grua keni pasur marrëdhënie bashkëshortore; e, nëse nuk keni pasur marrëdhënie bashkëshortore, atëherë nuk ka pengesa. Ju ndalohet martesa me gratë e bijve tuaj dhe t’i bashkoni dy motra, përveç rasteve që tashmë kanë kaluar. Pa dyshim, Allahu është Falës dhe Mëshirëplotë.');
INSERT INTO "hasan_efendi" VALUES(4,24,'4:24','(Ju ndalohet martesa) edhe me gratë që gjenden nën kurorë, përveç atyre që i zini robëresha. Ky është urdhri i Allahut. Ju janë lejuar të tjerat, me kusht që t’i kërkoni me pasurinë tuaj për martesë e jo për jetë të përkohshme. Grave, me të cilat kaloni jetë bashkëshortore, jepuni dhuratat e detyrueshme (për jetesë). S’ka gjynah për ju, me pëlqimin e të dyja palëve, që ta shtoni apo ta pakësoni sasinë e dhuratës së premtuar. Se, Allahu, me të vërtetë, është i Gjithëdijshëm dhe i Urtë.');
INSERT INTO "hasan_efendi" VALUES(4,25,'4:25','Cilido nga ju që nuk mund të martohet me gra të lira besimtare, për shkaqe të vështira materiale, le të martohet me një vajzë besimtare që gjendet nën pushtetin tuaj (robëreshë) - e Allahu e di më së miri besimin tuaj -, se jeni nga njëri-tjetri. Martohuni me to me lejen e pronarëve të tyre. Jepuni dhuratat si është e zakonshme për gra të ndershme e jo imorale të hapura apo të fshehta, që jetojnë me dashnorë. Nëse ato, pasi të martohen, bëjnë kurvëri, dënimi i tyre është përgjysmë me dënimin e grave të lira (të pamartuara). Kjo është për ata nga ju, që kanë frikë se do të bëjnë gabim (kurvëri). Nëse përmbaheni, kjo është edhe më mirë! Allahu është Falës dhe Mëshirëplotë.');
INSERT INTO "hasan_efendi" VALUES(4,26,'4:26','Allahu dëshiron që t’ju sqarojë, t’ju udhëzojë në rrugën e të dërguarve të mëparshëm dhe t’jua pranojë pendimet tuaja. Allahu është i Gjithëdijshëm dhe i Urtë.');
INSERT INTO "hasan_efendi" VALUES(4,27,'4:27','Allahu dëshiron që t’jua pranojë pendimet tuaja, por ata që jepen pas epsheve, dëshirojnë që të largoheni krejtësisht nga udha e drejtë.');
INSERT INTO "hasan_efendi" VALUES(4,28,'4:28','Allahu dëshiron që t’jua lehtësojë (barrët), pasi njeriu është krijuar si një qenie e pafuqishme.');
INSERT INTO "hasan_efendi" VALUES(4,29,'4:29','O besimtarë, mos e përvetësoni pasurinë e njëri-tjetrit në mënyrë të palejueshme, përveç rastit kur ajo është tregti me pëlqim të dyanshëm dhe mos e vritni veten (dhe njëri-tjetrin)! Vërtet, Allahu është i Mëshirshëm me ju.');
INSERT INTO "hasan_efendi" VALUES(4,30,'4:30','Këdo prej jush që e bën këtë përdhunisht dhe padrejtësisht, Ne, pa dyshim, do ta hedhim në zjarr. Kjo është shumë e lehtë për Allahun.');
INSERT INTO "hasan_efendi" VALUES(4,31,'4:31','Në qoftë se ju u shmangeni gjynaheve të mëdha, që ju janë ndaluar, Ne do t’jua falim gabimet tuaja të vogla dhe do t’ju shpiem në një vend të nderuar. ');
INSERT INTO "hasan_efendi" VALUES(4,32,'4:32','Mos i lakmoni ato gjëra, me anë të të cilave Allahu i ka bërë disa nga ju të dallohen mbi të tjerët: meshkujt do të kenë pjesë prej asaj që kanë punuar e po ashtu edhe femrat do të kenë pjesë prej asaj që kanë punuar. Kërkoni prej Allahut nga mirësitë e Tij. Vërtet, Allahu është i Dijshëm për çdo gjë. ');
INSERT INTO "hasan_efendi" VALUES(4,33,'4:33','Gjithkujt Ne i kemi caktuar trashëgimtarët për çdo gjë që lënë prindërit ose të afërmit, ndërsa atyre me të cilët keni bërë marrëveshje, jepuni pjesën e vet. Sigurisht që Allahu është dëshmitar për çdo gjë.');
INSERT INTO "hasan_efendi" VALUES(4,34,'4:34','Meshkujt kanë autoritet mbi gratë, meqë Allahu u ka dhënë disa cilësi mbi ato dhe, meqë ata shpenzojnë për mbajtjen e tyre. Gra të mira janë ato të dëgjueshmet, që ruajnë fshehtësitë që ka urdhëruar Allahu. Sa i përket grave që ju i druheni mosbindjes dhe sjelljes së keqe nga ana e tyre, këshillojini, (e, nëse kjo s’bën dobi) mos i pranoni në shtrat dhe (në fund) i rrihni ato (lehtas). Por, nëse ato ju binden, atëherë mos i ndëshkoni më. Allahu, me të vërtetë, është i Lartësuar dhe i Madhëruar! ');
INSERT INTO "hasan_efendi" VALUES(4,35,'4:35','Nëse i druheni ndarjes midis burrit e gruas, atëherë caktoni një ndërmjetës nga ana e burrit dhe një nga ana e gruas. Në qoftë se ata dëshirojnë paqësim, Allahu sjell marrëveshje midis tyre. Allahu, me të vërtetë, është i Gjithëdijshëm dhe i di të gjitha qëllimet.');
INSERT INTO "hasan_efendi" VALUES(4,36,'4:36','Adhuroni Allahun dhe mos i shoqëroni Atij asgjë (në adhurim); silluni mirë me prindërit, të afërmit, jetimët, të varfrit, fqinjët e afërt dhe të largët, me atë që keni pranë, me udhëtarin (që ka mbetur ngushtë) dhe me ata që janë robër nën pushtetin tuaj. Në të vërtetë, Allahu nuk i do njerëzit arrogantë e mburravecë;');
INSERT INTO "hasan_efendi" VALUES(4,37,'4:37','(e as) ata që janë koprracë, të cilët kërkojnë që edhe të tjerët të bëhen koprracë dhe fshehin atë që ua ka dhuruar Allahu nga mirësitë e Tij. Ne kemi përgatitur për mohuesit ndëshkim poshtërues.');
INSERT INTO "hasan_efendi" VALUES(4,38,'4:38','(Ai nuk i do) as ata që ndajnë nga pasuria e tyre sa për sy e faqe të botës dhe që nuk e besojnë Allahun e as Ditën e Kiametit. Ai që ka për shok djallin, dihet sa shok i keq është!');
INSERT INTO "hasan_efendi" VALUES(4,39,'4:39','E ç’dëm do të kishin ata sikur të besonin Allahun dhe Ditën e Kiametit dhe të ndanin nga pasuria që u ka dhënë Allahu?! Allahu di çdo gjë për ta.');
INSERT INTO "hasan_efendi" VALUES(4,40,'4:40','Vërtet, Allahu nuk dëmton askënd asnjë grimë, por, nëse (dikush) bën mirësi, Allahu ia shumëfishon atë dhe i jep shpërblim të madh.');
INSERT INTO "hasan_efendi" VALUES(4,41,'4:41','Si do të bëhet kur prej çdo populli të sjellin dëshmitarë dhe të të sjellin ty (o Muhamed!) për dëshmitar mbi të gjithë ata?!');
INSERT INTO "hasan_efendi" VALUES(4,42,'4:42','Atë ditë, ata që nuk kanë besuar dhe që nuk i janë bindur të Dërguarit, do të dëshironin të rrafshoheshin me tokën, por Allahut nuk mund t’i fshehin asnjë fjalë.');
INSERT INTO "hasan_efendi" VALUES(4,43,'4:43','O besimtarë! Mos u falni kur jeni të dehur, prisni të kthjelloheni e të dini se ç’flisni; as kur jeni të papastër (xhunub) - përveçse nëse jeni duke udhëtuar, derisa të laheni. Dhe, nëse jeni të sëmurë ose gjendeni në rrugë ose keni kryer nevojën natyrore, ose keni bërë marrëdhënie me gra dhe nuk gjeni ujë, atëherë fërkoni fytyrën dhe duart tuaja me dhé të pastër. Allahu, me të vërtetë, është Shlyes dhe Falës i gjynaheve.');
INSERT INTO "hasan_efendi" VALUES(4,44,'4:44','A nuk i sheh ti (o Muhamed!) ata që iu është dhënë një pjesë prej Librit? Ata e ndërrojnë të mirën me të keqen dhe dëshirojnë që edhe ju të humbisni rrugën e drejtë.');
INSERT INTO "hasan_efendi" VALUES(4,45,'4:45','Allahu i di më së miri armiqtë tuaj. Allahu mjafton si Mbrojtës për ju. Allahu mjafton si Ndihmës për ju.');
INSERT INTO "hasan_efendi" VALUES(4,46,'4:46','Në mesin e hebrenjve ka nga ata që i nxjerrin fjalët nga konteksti i tyre dhe i thonë Profetit: “Dëgjuam, por nuk të bindemi”! dhe “Dëgjo, mos dëgjofsh!” dhe “Ra’iná!” , duke shtrembëruar (shprehjet) me gjuhën e tyre dhe duke shpifur për fenë. Po sikur ata të kishin thënë: “Dëgjuam dhe u bindëm!” dhe “Dëgjo!” e “Shikona ne!”, kjo do të ishte më e mirë dhe më e drejtë për ata. Allahu i mallkoi ata për shkak të mohimit të tyre, prandaj ata nuk besojnë, përveç një numri të vogël.');
INSERT INTO "hasan_efendi" VALUES(4,47,'4:47','O ju, të cilëve ju është dhënë Libri! Besoni atë që kemi zbritur si vërtetues të asaj që keni në duar para se t’jua fshijmë fytyrat e t’jua kthejmë prapa ose t’ju mallkojmë ashtu siç i mallkuam shkelësit e Sabatit. Urdhri i Allahut do të zbatohet doemos.');
INSERT INTO "hasan_efendi" VALUES(4,48,'4:48','Vërtet, Allahu nuk fal që të adhurohet dikush apo diçka tjetër veç Atij, por gjynahet e tjera më të vogla ia fal kujt të dojë. Kushdo që i bën shok Allahut (në adhurim), ka bërë gjynah të tmerrshëm.');
INSERT INTO "hasan_efendi" VALUES(4,49,'4:49','A nuk i ke parë ata që e kujtojnë veten të dëlirë? Jo, Allahu dëlir kë të dojë dhe askush nuk do të pësojë as padrejtësinë më të vogël.');
INSERT INTO "hasan_efendi" VALUES(4,50,'4:50','Shiko si shpifin ata gënjeshtra kundër Allahut. Mjafton kjo shpifje si gjynah i qartë.');
INSERT INTO "hasan_efendi" VALUES(4,51,'4:51','A nuk i ke parë ata që u është dhënë një pjesë e Librit? Besojnë në idhuj dhe hyjni të rreme dhe thonë për jobesimtarët: “Këta janë në rrugë më të drejtë se ata që besojnë ”.');
INSERT INTO "hasan_efendi" VALUES(4,52,'4:52','Këta janë ata që i ka mallkuar Allahu dhe, atij që e mallkon Allahu, nuk do t’i gjendet ndihmues.');
INSERT INTO "hasan_efendi" VALUES(4,53,'4:53','A do të kenë ata ndonjë pjesë të pushtetit? Nëse është kështu, ata nuk do t’u jepnin njerëzve as sa puçërrza e bërthamës.');
INSERT INTO "hasan_efendi" VALUES(4,54,'4:54','A mos i kanë zili njerëzit për çfarë u ka dhuruar Allahu nga të mirat e Veta? Ne u dhamë pasardhësve të Ibrahimit Librin e urtësinë, si dhe një pushtet të madh.');
INSERT INTO "hasan_efendi" VALUES(4,55,'4:55','Disa prej tyre e besuan atë e disa iu shmangën atij. Për ata mjafton Xhehenemi përcëllues.');
INSERT INTO "hasan_efendi" VALUES(4,56,'4:56','Ata që s’besojnë në shenjat Tona, Ne padyshim, do t’i djegim në zjarr. Sapo t’u digjet lëkura, Ne do t’ua ndërrojmë atë me lëkurë tjetër, për të përjetuar dënimin. Me të vërtetë, Allahu është i Plotfuqishëm dhe i Urtë.');
INSERT INTO "hasan_efendi" VALUES(4,57,'4:57','Sa për ata që besojnë dhe punojnë vepra të mira, Ne do t’i shpiem në kopshte, nëpër të cilat rrjedhin lumenj dhe ku do të banojnë përgjithmonë. Ata do të kenë bashkëshorte të pastra dhe Ne do t’i shpiem në hije të dendura.');
INSERT INTO "hasan_efendi" VALUES(4,58,'4:58','Allahu ju urdhëron që amanetet t’ua ktheni atyre që u përkasin dhe, kur të gjykoni midis njerëzve, të gjykoni drejt. Vërtet, Allahu ju këshillon mrekullueshëm dhe vërtet, Allahu dëgjon çdo gjë dhe vështron të gjitha punët! ');
INSERT INTO "hasan_efendi" VALUES(4,59,'4:59','O besimtarë! Bindjuni Allahut, bindjuni të Dërguarit dhe atyre që drejtojnë punët tuaja. Nëse nuk pajtoheni në ndonjë gjë, drejtojuni Allahut dhe të Dërguarit , nëse besoni Allahun dhe Ditën e Kiametit. Kjo për ju është më e mira dhe shpjegimi më i bukur.');
INSERT INTO "hasan_efendi" VALUES(4,60,'4:60','A nuk i ke parë ti (o Muhamed!) ata (hipokritë) që pohojnë se kanë besuar në atë që të është shpallur ty dhe profetëve të tjerë para teje? Ata kërkojnë gjykimin e zotave të rremë, ndërkohë që janë urdhëruar t’i mohojnë ata. Djalli dëshiron që t’i nxjerrë ata krejtësisht nga udha e drejtë.');
INSERT INTO "hasan_efendi" VALUES(4,61,'4:61','Po kur u thuhet atyre: “Ejani tek ajo që ka shpallur Allahu dhe tek i Dërguari”, i sheh hipokritët se si largohen prej teje me neveri.');
INSERT INTO "hasan_efendi" VALUES(4,62,'4:62','Si do të jetë gjendja e tyre kur t’i godasë fatkeqësia, për shkak të asaj që vetë kanë punuar e të vijnë te ti, duke u betuar në Allahun e duke thënë: “Ne kemi dashur vetëm të bëjmë mirësi dhe pajtim”?!');
INSERT INTO "hasan_efendi" VALUES(4,63,'4:63','Këta janë ata për të cilët Allahu e di se ç’kanë në zemrat e tyre. Largohu prej tyre, këshilloji dhe thuaju fjalë që i prekin në zemër.');
INSERT INTO "hasan_efendi" VALUES(4,64,'4:64','Ne i kemi nisur të dërguarit vetëm me qëllimin që njerëzit, me lejen e Allahut, t’u binden atyre. Dhe në qoftë se ata , kurdo që ta ngarkonin veten (me gjynahe), të vinin te ti për t’i kërkuar falje Allahut dhe i Dërguari t’i lutej Allahut për shfajësimin e tyre, do ta vërenin, se Allahu, me të vërtetë, është Pranues i pendimit dhe Mëshirëplotë.');
INSERT INTO "hasan_efendi" VALUES(4,65,'4:65','Jo, për Zotin tënd, ata nuk do të jenë besimtarë të vërtetë, derisa të të marrin ty për gjyqtar për kundërshtitë mes tyre; e pastaj, të mos ndiejnë kurrfarë dyshimi ndaj gjykimit tënd dhe të të binden ty plotësisht.');
INSERT INTO "hasan_efendi" VALUES(4,66,'4:66','Sikur Ne t’i urdhëronim ata: “Vritni veten!” ose: “Lini shtëpitë tuaja!”, pak prej tyre do ta bënin këtë. E sikur të vepronin ata siç këshilloheshin, kjo për ata do të ishte më mirë dhe do të ishin më të fortë në besim.');
INSERT INTO "hasan_efendi" VALUES(4,67,'4:67','Atëherë, Ne, pa dyshim, do t’u jepnim shpërblim të madh nga ana Jonë');
INSERT INTO "hasan_efendi" VALUES(4,68,'4:68','dhe me siguri do t’i udhëzonim në rrugë të drejtë.');
INSERT INTO "hasan_efendi" VALUES(4,69,'4:69','Kushdo që i bindet Allahut dhe të Dërguarit, do të jetë me ata të cilëve Allahu u ka dhënë shumë dhunti: me profetët, me të sinqertët , me dëshmorët dhe me të drejtët! Eh sa shokë të mrekullueshëm janë këta!');
INSERT INTO "hasan_efendi" VALUES(4,70,'4:70','Kjo dhunti është nga Allahu, e mjafton që Allahu e di çdo gjë.');
INSERT INTO "hasan_efendi" VALUES(4,71,'4:71','O besimtarë! Bëhuni syçelë (kundër armikut) e luftojeni atë, qoftë grupe-grupe ose të gjithë së bashku!');
INSERT INTO "hasan_efendi" VALUES(4,72,'4:72','Në të vërtetë, disa prej jush ngurrojnë për të shkuar në luftë. Nëse ju ndodh ndonjë fatkeqësi, ndonjëri thotë: “Vetë Allahu më ka shpërblyer mua për të mos u gjendur i pranishëm me ta”.');
INSERT INTO "hasan_efendi" VALUES(4,73,'4:73','E nëse ju arrin ndonjë e mirë nga Allahu, ai atëherë, me siguri thotë – a thua se nuk ka midis tij dhe jush dashamirësi -: “Ah, sikur të kisha qenë me ata e të arrija sukses të madh!”');
INSERT INTO "hasan_efendi" VALUES(4,74,'4:74','Le të luftojnë në rrugën e Allahut ata që nuk e kursejnë jetën e kësaj bote për jetën e ardhshme. Atij që lufton në rrugën e Allahut, e vritet ose fiton, Ne, me siguri, do t’i japim shpërblim të madh.');
INSERT INTO "hasan_efendi" VALUES(4,75,'4:75','Ç’keni ju që nuk luftoni në udhë të Allahut dhe për shpëtimin e të shtypurve: për meshkujt e pafuqishëm, gratë dhe fëmijët, të cilët thërrasin: “O Zoti ynë! Nxirrna nga ky qytet, banorët e të cilit janë keqbërës! Na cakto një mbrojtës dhe na dërgo nga ana Jote dikë që të na ndihmojë!”');
INSERT INTO "hasan_efendi" VALUES(4,76,'4:76','Ata që besojnë, luftojnë në rrugën e Allahut, kurse ata që nuk besojnë, luftojnë në rrugën e djallit. Luftoni kundër ithtarëve të djallit, sepse vërtet, dinakëria e djallit është e dobët!');
INSERT INTO "hasan_efendi" VALUES(4,77,'4:77','A nuk i ke parë (o Muhamed) ata që janë porositur: “Tërhiqni duart tuaja nga lufta, falni namazin dhe jepni zekatin!”?! Por kur, pas kësaj, u urdhëruan që të luftojnë, një grup prej tyre u frikësua nga njerëzit, ashtu si i frikësohen Allahut, madje edhe më tepër dhe thirrën: “O Zoti ynë! Pse na caktove të luftojmë? Ah, sikur të na kishe kursyer nga lufta edhe për një kohë të shkurtër!” Thuaju: “Kënaqësitë e kësaj jete janë të vogla. Jeta tjetër është më e mirë për ata që e kanë frikë Allahun e s’bëjnë gjynahe. Juve nuk do t’ju bëhet asnjë fije padrejtësi.');
INSERT INTO "hasan_efendi" VALUES(4,78,'4:78','Kudo që të gjendeni, do t’ju arrijë vdekja, madje, qofshi edhe në kështjellat më të forta!” Sa herë që u vjen ndonjë e mirë, ata thonë: “Kjo është nga ana e Allahut”. Por, nëse i godet ndonjë e ligë, ata thonë: “Kjo është prej teje (o Muhamed)!” Thuaju: “Të gjitha janë prej Allahut”. Ç’është kështu me këtë popull, që nuk kupton asgjë?');
INSERT INTO "hasan_efendi" VALUES(4,79,'4:79','Çdo e mirë që të gjen ty o njeri, prej Allahut është, kurse e keqja që të godet, është prej vetes sate. Ne të kemi sjellë ty (o Muhamed) si të Dërguar për të gjithë njerëzimin. Allahu mjafton për dëshmitar.');
INSERT INTO "hasan_efendi" VALUES(4,80,'4:80','Kush i bindet të Dërguarit, i është bindur Allahut. Sa për ata që kthejnë kokën mënjanë, Ne nuk të kemi dërguar që të jesh rojtar i tyre.');
INSERT INTO "hasan_efendi" VALUES(4,81,'4:81','Dhe ata thonë: “Të jemi bindur”, por, kur largohen prej teje, një grup nga ata natën shtjellojnë diçka tjetër nga ato që u the ti. Por Allahu i shënon shtjellimet e tyre. Prandaj largohu prej tyre dhe mbështetu tek Allahu! Se Allahu të mjafton ty për mbrojtës.');
INSERT INTO "hasan_efendi" VALUES(4,82,'4:82','A nuk përsiatin ata për Kuranin! Sikur ai të mos ishte prej Allahut, do të gjenin në të shumë kundërthënie.');
INSERT INTO "hasan_efendi" VALUES(4,83,'4:83','Kur atyre u vjen ndonjë lajm i rëndësishëm që ka të bëjë me sigurinë ose frikën, ata e përhapin (pa e vërtetuar mirë). Por, në qoftë se këtë lajm do t’ia përcillnin (për shqyrtim) të Dërguarit dhe parisë, hulumtuesit do ta merrnin vesh prej tyre (se çfarë lajmi duhet përhapur e çfarë jo). Sikur të mos ishte mirësia e Allahut dhe mëshira e Tij, ju të gjithë do të ndiqnit djallin, përveç një numri të vogël.');
INSERT INTO "hasan_efendi" VALUES(4,84,'4:84','Prandaj, lufto në rrugën e Allahut! Ti je përgjegjës vetëm për veten tënde. Nxiti edhe besimtarët, se mbase Allahu e pengon fuqinë e jobesimtarëve! Allahu është më i fuqishmi dhe më i ashpri në dënime.');
INSERT INTO "hasan_efendi" VALUES(4,85,'4:85','Kush ndërmjetëson për një çështje të mirë, do të ketë pjesë në shpërblimin e saj, kurse kush ndërmjetëson për një çështje të keqe, do të ketë pjesë në ndëshkimin e saj. Allahu është i Gjithëpushtetshëm.');
INSERT INTO "hasan_efendi" VALUES(4,86,'4:86','Kur ju përshëndet dikush me respekt, kthejani me një përshëndetje më të mirë ose në të njëjtën mënyrë! Pa dyshim, Allahu llogarit çdo gjë. ');
INSERT INTO "hasan_efendi" VALUES(4,87,'4:87','Allahu! Nuk ka zot tjetër (të denjë për adhurim) përveç Atij. Ai do t’ju mbledhë në Ditën e Kiametit, për të cilën nuk ka dyshim aspak! E kush është në fjalë më i vërtetë se Allahu?');
INSERT INTO "hasan_efendi" VALUES(4,88,'4:88','Përse jeni ndarë në dy grupe për çështjen e hipokritëve, kur Allahu i ka flakur ata në mosbesim për shkak të veprave të tyre?! A mos vallë, dëshironi t’i ktheni në rrugë të drejtë ata që Allahu i ka lënë në humbje? Për atë që Allahu e lë në humbje, ti nuk mund të gjesh rrugë të drejtë.');
INSERT INTO "hasan_efendi" VALUES(4,89,'4:89','Ata dëshirojnë që edhe ju të mos besoni, ashtu siç nuk besojnë vetë dhe të bëheni të njëjtë me ata. Kurrsesi mos u bëni shokë me ata, derisa të mërgojnë për hir të Allahut. Nëse ata shmangen, ndiqini e i vrisni, kudo që t’i gjeni! Dhe mos zgjidhni nga ata as miq, as mbështetës,');
INSERT INTO "hasan_efendi" VALUES(4,90,'4:90','përveç atyre që janë strehuar te një popull me të cilin ju keni marrëveshje ose atyre që kanë ardhur te ju, e u vjen rëndë që të luftojnë kundër jush ose të luftojnë kundër popullit të vet. Sikur të kishte dashur Allahu, do t’u kishte dhënë pushtet mbi ju dhe ata do të luftonin kundër jush. Në qoftë se ata tërhiqen prej jush, nuk hyjnë në luftë kundër jush dhe ju ofrojnë paqe, atëherë për ju Allahu nuk lë shteg që të luftoni kundër tyre.');
INSERT INTO "hasan_efendi" VALUES(4,91,'4:91','Ju do të hasni në njerëz të tjerë, të cilët dëshirojnë të sigurohen nga ju dhe nga kombi i vet. Sa herë që thirren në idhujtari, menjëherë bien në të. Nëse nuk shmangen prej jush, nëse nuk ju ofrojnë paqen dhe nëse nuk heqin dorë nga luftimi kundër jush, atëherë i kapni e i vrisni kudo që t’i gjeni! Kundër tyre ju kemi dhënë të drejtë të qartë.');
INSERT INTO "hasan_efendi" VALUES(4,92,'4:92','Nuk bën që besimtari të vrasë besimtarin, përveçse gabimisht. Kush vret një besimtar gabimisht, duhet të lirojë një besimtar të robëruar dhe t’u paguajë gjakun trashëgimtarëve të të vrarit, përpos rastit kur familja e të vrarit ia fal gjakun. Nëse i vrari është nga një popull që e keni armik e njëkohësisht është besimtar, duhet të lirohet një besimtar i robëruar. Nëse i vrari është nga një popull me të cilin ju keni lidhur marrëveshje, i duhet paguar gjaku familjes së tij, duke liruar edhe një besimtar të robëruar. Ai që s’mund të gjejë rob, duhet të agjërojë dy muaj rresht. (Kjo është bërë) në shenjë pendimi para Allahut. Se Allahu është i Gjithëdijshëm dhe i Urtë.');
INSERT INTO "hasan_efendi" VALUES(4,93,'4:93','Kushdo që vret një besimtar me qëllim, ndëshkimi i tij është Xhehenemi, në të cilin do të qëndrojë përgjithmonë. Allahu është zemëruar me atë, e ka mallkuar dhe i ka përgatitur një dënim të madh.');
INSERT INTO "hasan_efendi" VALUES(4,94,'4:94','O besimtarë! Kur të shkoni në luftë, në rrugë të Allahut, shqyrtoni gjithçka me kujdes! Mos i thoni atij që ju përshëndet me selam: “Nuk je besimtar”, me qëllim që të fitoni kënaqësi të kësaj jete, ndërkohë që tek Allahu ka fitime të shumta. Të tillë keni qenë edhe ju më parë e Allahu ju dha mirësi; prandaj gjithmonë sqarojini mirë të gjitha punët! Se Allahu është Njohës i veprave tuaja.');
INSERT INTO "hasan_efendi" VALUES(4,95,'4:95','Nuk janë të njëjtë ata besimtarë që rrinë në shtëpitë e tyre, përveç të paaftëve, me ata që luftojnë në rrugën e Allahut me pasurinë dhe jetën e tyre. Allahu i ngre një shkallë më lart ata që kanë luftuar me pasurinë dhe jetën e tyre kundrejt atyre që kanë ndenjur në shtëpitë e veta. Ai u ka premtuar të gjithëve mirësi, por i ka dalluar ata që luftojnë mbi ata që nuk luftojnë me shpërblim të madh:');
INSERT INTO "hasan_efendi" VALUES(4,96,'4:96','me shkallë të veçanta, me falje dhe mëshirë. Allahu është Falës dhe Mëshirëplotë.');
INSERT INTO "hasan_efendi" VALUES(4,97,'4:97','Kur engjëjt ua marrin shpirtrat atyre që e kanë ngarkuar veten me faje, u thonë: “Ku ishit?” Ata përgjigjen: “Ishim të pafuqishëm në Tokë”. (Engjëjt)u thonë: “A nuk qe Toka e Allahut e gjerë që të mërgonit në të?” Këta janë ata (njerëz), vendbanimi i të cilëve është Xhehenemi; eh, sa i keq është ai vendbanim!');
INSERT INTO "hasan_efendi" VALUES(4,98,'4:98','Me përjashtim të meshkujve të pafuqishëm, grave dhe fëmijëve, të cilët nuk kanë as fuqinë e as mundësinë të drejtohen në rrugë,');
INSERT INTO "hasan_efendi" VALUES(4,99,'4:99','mbase Allahu i fal, se Allahu, me të vërtetë, është Shlyes dhe Falës i gjynaheve.');
INSERT INTO "hasan_efendi" VALUES(4,100,'4:100','Kush mërgon për çështjen e Allahut, ai gjen shumë vendbanime dhe begati në tokë. Kush lë shtëpinë e vet, duke u shpërngulur për tek Allahu dhe i Dërguari i Tij dhe ndodh që t’i vijë vdekja, Allahu me siguri do ta shpërblejë. Se Allahu është Falës dhe Mëshirëplotë.');
INSERT INTO "hasan_efendi" VALUES(4,101,'4:101','Kur të udhëtoni, nuk është gjynah që ta shkurtoni namazin, nëse druheni që jobesimtarët do t’ju bëjnë ndonjë dëm. Me të vërtetë, jobesimtarët janë armiqtë tuaj të hapur.');
INSERT INTO "hasan_efendi" VALUES(4,102,'4:102','Kur të jesh ti (Muhamed) në mes të besimtarëve dhe të drejtosh faljen e namazit (në luftë), atëherë njëri grup prej tyre le të falet me ty dhe le t’i mbajnë pranë edhe armët e veta! Pastaj këta, le të rrinë pas jush dhe le të vijë grupi tjetër, i cili nuk është falur dhe, le të falet me ty! Le të jenë syçelë dhe t’i mbajnë armët e veta! Jobesimtarët dëshirojnë që të jeni të pakujdesshëm ndaj armëve dhe pajimeve tuaja, që t’ju sulmojnë me tërë fuqinë. Nuk ka gjynah për ju nëse hasni në ndonjë shqetësim prej shiut ose sëmundjes e i lini armët, por edhe atëherë, bëhuni syçelë. Allahu, me të vërtetë, ka përgatitur dënim poshtërues për jobesimtarët.');
INSERT INTO "hasan_efendi" VALUES(4,103,'4:103','Pasi të kryeni namazin, përmendeni Allahun duke qëndruar në këmbë, ulur ose të shtrirë. Kur të jeni jashtë rrezikut, atëherë faleni namazin të plotë, sepse namazi është detyrë për besimtarët në kohë të caktuar.');
INSERT INTO "hasan_efendi" VALUES(4,104,'4:104','Mos u tregoni të dobët në përndjekjen e armikut! Nëse ndieni dhembje prej luftës, edhe ata ndiejnë dhimbje ashtu si edhe ju. Mirëpo ju shpresoni prej Allahut atë që ata nuk e shpresojnë. Allahu është i Gjithëdijshëm dhe i Urtë.');
INSERT INTO "hasan_efendi" VALUES(4,105,'4:105','Vërtet, Ne (o Muhamed) ta kemi zbritur Kuranin me të drejtën, në mënyrë që ti të gjykosh ndërmjet njerëzve sipas asaj që të ka shpallur Allahu. Dhe mos u bëj mbrojtës i tradhtarëve! ');
INSERT INTO "hasan_efendi" VALUES(4,106,'4:106','Kërko ndjesë prej Allahut! Se Allahu është Falës e Mëshirëplotë.');
INSERT INTO "hasan_efendi" VALUES(4,107,'4:107','Dhe mos i mbroj ata që mashtrojnë vetveten! Se Allahu nuk i do tradhtarët dhe gjynahqarët.');
INSERT INTO "hasan_efendi" VALUES(4,108,'4:108','Ata fshihen nga njerëzit, por nuk fshihen dot nga Allahu. Ai është pranë tyre edhe natën, kur trillojnë gjëra me të cilat Ai nuk është i kënaqur. Se Allahu përfshin (me dijen e Tij) gjithçka që ata bëjnë.');
INSERT INTO "hasan_efendi" VALUES(4,109,'4:109','Ja, ju i mbrojtët ata në këtë botë, por kush do t’i mbrojë tek Allahu në Ditën e Kiametit ose kush do të bëhet përfaqësues i tyre?');
INSERT INTO "hasan_efendi" VALUES(4,110,'4:110','Kush punon vepër të keqe ose e ngarkon veten me gjynahe, pastaj kërkon falje prej Allahut, do të gjejë se Allahu është Falës e Mëshirëplotë.');
INSERT INTO "hasan_efendi" VALUES(4,111,'4:111','Kush bën gjynah, ai ia ka bërë shpirtit të vet. Allahu është i Gjithëdijshëm dhe i Urtë.');
INSERT INTO "hasan_efendi" VALUES(4,112,'4:112','Kush bën ndonjë gjynah ose të keqe, pastaj ia vesh atë një të pafajshmi, atëherë ai e ka ngarkuar veten me fajin e shpifjes dhe me një gjynah të qartë.');
INSERT INTO "hasan_efendi" VALUES(4,113,'4:113','Sikur të mos e kishe mirësinë e Allahut dhe mëshirën e Tij, një grup i atyre do të përpiqej që të të nxirrte nga udha e drejtë. Ata me këtë e humbin vetëm vetveten, kurse ty nuk të bëjnë kurrfarë dëmi. Allahu të shpalli ty Librin (Kuranin) dhe Urtësinë e plotë, si dhe të mësoi atë që nuk e ke ditur. Dhuntia e Allahut për ty është shumë e madhe.');
INSERT INTO "hasan_efendi" VALUES(4,114,'4:114','S’ka kurrfarë të mire në shumë prej bisedave të tyre të fshehta, përveç rastit kur ndokush kërkon që të jepet sadaka ose të kryhet një vepër e mirë apo paqësim ndërmjet njerëzve. Atij që e bën këtë për të fituar kënaqësinë e Allahut, Ne do t’i japim një shpërblim të madh.');
INSERT INTO "hasan_efendi" VALUES(4,115,'4:115','Këdo që e kundërshton të Dërguarin, pasi i është dhënë udhëzimi dhe ndjek rrugë tjetër nga ajo e besimtarëve, Ne e lëmë të ecë andej nga është nisur dhe do ta përcëllojmë në zjarrin e Xhehenemit; eh, sa vendbanim i keq që është ai!');
INSERT INTO "hasan_efendi" VALUES(4,116,'4:116','Vërtet, Allahu nuk fal që të adhurohet dikush apo diçka tjetër veç Atij, por gjynahet e tjera më të vogla ia fal kujt të dojë. Kushdo që i bën shok Allahut (në adhurim), ai me të vërtetë, ka humbur dhe është larguar shumë prej udhës së drejtë.');
INSERT INTO "hasan_efendi" VALUES(4,117,'4:117','Ata nuk adhurojnë tjetër veçse idhuj femërorë dhe nuk thërrasin tjetër përveç djallit kryengritës.');
INSERT INTO "hasan_efendi" VALUES(4,118,'4:118','Atë e ka mallkuar Allahu, sepse (djalli) ka thënë: “Unë do të marr medoemos një pjesë të caktuar të robërve të Tu.');
INSERT INTO "hasan_efendi" VALUES(4,119,'4:119','Me çdo kusht do t’i shpie ata në rrugë të shtrembër, do t’i josh me shpresa të kota dhe, me siguri, do t’i urdhëroj që t’u presin veshët bagëtive, e pastaj do t’i urdhëroj që të ndryshojnë krijesën e Allahut. “Kush merr djallin për mbrojtës në vend të Allahut, ai ka pësuar humbje të qartë.');
INSERT INTO "hasan_efendi" VALUES(4,120,'4:120','Ai (djalli) u premton atyre dhe i josh me shpresa, por ato që premton djalli, janë vetëm mashtrime.');
INSERT INTO "hasan_efendi" VALUES(4,121,'4:121','Për ata, vendbanim është Xhehenemi dhe nuk mund të gjejnë shpëtim prej tij.');
INSERT INTO "hasan_efendi" VALUES(4,122,'4:122','Ata që besojnë dhe bëjnë vepra të mira, Ne do t’i shpiem në kopshte të Xhenetit, nëpër të cilat rrjedhin lumenj. Aty do të qëndrojnë vazhdimisht e përgjithmonë. Ky është premtimi i vërtetë i Allahut. E kush është më i vërtetë se Allahu në fjalë?!');
INSERT INTO "hasan_efendi" VALUES(4,123,'4:123','(Shpëtimi në jetën tjetër) nuk do të jetë as sipas dëshirës suaj (o myslimanë), as sipas dëshirës së ithtarëve të Librit. Por kushdo që bën një të keqe, do të ndëshkohet për atë dhe nuk do të gjejë as mbrojtës, as mbështetës tjetër në vend të Allahut.');
INSERT INTO "hasan_efendi" VALUES(4,124,'4:124','Dhe kushdo që bën vepra të mira, qoftë ai mashkull ose femër, duke qenë besimtar, do të hyjë në Xhenet dhe nuk do t’i bëhet asnjë padrejtësi.');
INSERT INTO "hasan_efendi" VALUES(4,125,'4:125','Kush është besimtar më i mirë se ai që i dorëzohet sinqerisht Allahut, duke qenë bamirës dhe zbatues i fesë së pastër të Ibrahimit, të cilin Allahu e ka marrë për mik të ngushtë?!');
INSERT INTO "hasan_efendi" VALUES(4,126,'4:126','Allahut i përket gjithçka që gjendet në qiej dhe gjithçka që gjendet në Tokë dhe Allahu i përfshin të gjitha (në dijen e Tij).');
INSERT INTO "hasan_efendi" VALUES(4,127,'4:127','Ata kërkojnë prej teje (o Muhamed) që t’i sqarosh lidhur me çështjet e grave. Thuaju: “Për çështjet e tyre ju sqarojnë Allahu dhe Libri që ju lexohet , si për femrat jetime, të cilave nuk u jepni dhuratën e caktuar për ato edhe pse dëshironi të martoheni me to, ashtu edhe për fëmijët e mitur. Allahu ju udhëzon që të silleni drejt me jetimët. Allahu është në dijeni të çdo të mire që bëni.”');
INSERT INTO "hasan_efendi" VALUES(4,128,'4:128','Nëse gruaja druan nga burri i saj se do të sillet keq ose nuk do ta përfillë, atëherë nuk ka gjynah për ata, nëse pajtohen ndërmjet tyre. Pajtimi është më i mirë. Njerëzit janë të prirë nga koprracia. Por në qoftë se ju silleni mirë dhe ruheni prej të këqijave, Allahu e di mirë çfarë veproni.');
INSERT INTO "hasan_efendi" VALUES(4,129,'4:129','Ju nuk do të mund të silleni drejt midis grave tuaja, edhe sikur të dëshironi. Andaj, mos anoni krejtësisht vetëm nga njëra, duke e lënë tjetrën si të varur. Nëse ju i përmirësoni marrëdhëniet dhe ruheni nga padrejtësia, ta dini se Allahu është vërtet Falës dhe Mëshirëplotë.');
INSERT INTO "hasan_efendi" VALUES(4,130,'4:130','E nëse ata ndahen, Allahu me begatinë e Tij, çdonjërin prej tyre e bën të pavarur. Se Allahu është Dorëgjerë dhe i Urtë.');
INSERT INTO "hasan_efendi" VALUES(4,131,'4:131','E Allahut është gjithçka që gjendet në qiej dhe në Tokë. Ne i kemi porositur ata që u është shpallur Libri para jush dhe ju: “Frikësojuni Allahut! Nëse mohoni, dijeni se Allahut i përket gjithçka që gjendet në qiej dhe në Tokë. Allahu është i Vetëmjaftueshëm dhe i Denjë për çdo lavdërim.”');
INSERT INTO "hasan_efendi" VALUES(4,132,'4:132','Allahut i përket gjithçka që gjendet në qiej dhe në Tokë. Allahu është Mbrojtësi juaj i mjaftueshëm.');
INSERT INTO "hasan_efendi" VALUES(4,133,'4:133','Nëse do, Ai ju dëbon ju o njerëz dhe sjell të tjerë. Allahu ka fuqi ta bëjë këtë.');
INSERT INTO "hasan_efendi" VALUES(4,134,'4:134','Kush dëshiron shpërblimin e kësaj bote, (le ta dijë se) tek Allahu janë shpërblimi i kësaj dhe i botës tjetër. Allahu dëgjon dhe vështron çdo gjë.');
INSERT INTO "hasan_efendi" VALUES(4,135,'4:135','O besimtarë! Bëhuni zbatues të palëkundur të drejtësisë, duke dëshmuar në emër të Allahut, qoftë edhe kundër jush ose kundër prindërve dhe të afërmve tuaj. Qoftë i pasur ose i varfër ai (për të cilin dëshmoni), Allahu është për ata vlerësuesi më i drejtë. Dhe mos shkoni pas epsheve tuaja e të shtrembëroni drejtësinë! Nëse ju ngatërroni dëshminë ose i shmangeni asaj, vërtet që Allahu e di se çfarë bëni ju.');
INSERT INTO "hasan_efendi" VALUES(4,136,'4:136','O besimtarë! Besoni Allahun, të Dërguarin e Tij, Librin, që ia ka zbritur të Dërguarit të Tij dhe Librin që e ka zbritur më parë. Kushdo që mohon Allahun, engjëjt e Tij, Librat e Tij, të Dërguarit e Tij dhe Ditën e Kiametit, ai, me të vërtetë, ka humbur larg prej udhës së drejtë.');
INSERT INTO "hasan_efendi" VALUES(4,137,'4:137','Ata që në fillim besojnë dhe pastaj e mohojnë besimin, më pas sërish besojnë dhe sërish e mohojnë besimin dhe thellohen në mosbesim, Allahu, me të vërtetë, nuk do t’i falë dhe nuk do t’i shpjerë në rrugë të drejtë.');
INSERT INTO "hasan_efendi" VALUES(4,138,'4:138','Paralajmëroji hipokritët (o Muhamed) për dënimin e mjerueshëm që i pret,');
INSERT INTO "hasan_efendi" VALUES(4,139,'4:139','ata që miqësohen me mohuesit e jo me besimtarët! Vallë, tek ata kërkojnë fuqi, kur, në të vërtetë, e gjithë fuqia i përket vetëm Allahut!');
INSERT INTO "hasan_efendi" VALUES(4,140,'4:140','Ai jua ka shpallur në Libër : Kur të dëgjoni se mohohen fjalët e Allahut dhe bëhet tallje me to, atëherë mos rrini me ata (njerëz që veprojnë kështu), derisa të ndërrojnë bisedë, përndryshe do të ishit si ata. Allahu do t’i tubojë në Xhehenem të gjithë hipokritët dhe mohuesit.');
INSERT INTO "hasan_efendi" VALUES(4,141,'4:141','Ata përgjojnë se çfarë do të bëhet me ju. Kështu, nëse ju vjen fitorja nga Allahu, ata thonë: “A nuk ishim vallë ne me ju?” E, nëse mohuesit arrijnë fitore, hipokritët u thonë: “A thua nuk ju ndihmuam deri në fitore dhe ju mbrojtëm prej besimtarëve të vërtetë?” Allahu do të gjykojë ndërmjet jush në Ditën e Kiametit. Allahu nuk do t’iu japë kurrë rast mohuesve që t’i shkatërrojnë besimtarët.');
INSERT INTO "hasan_efendi" VALUES(4,142,'4:142','Në të vërtetë, hipokritët përpiqen të mashtrojnë Allahun, por është Ai që i mashtron ata. Kur ata ngrihen për namaz, ngrihen me përtesë, vetëm sa për t’u dukur para botës dhe Allahun e përmendin fare pak.');
INSERT INTO "hasan_efendi" VALUES(4,143,'4:143','Luhaten në mëdyshje, - nuk janë as me këta, as me ata. Këdo që Allahu shpie në humbje, ti (Muhamed!) nuk do të mund t’ia gjesh rrugën.');
INSERT INTO "hasan_efendi" VALUES(4,144,'4:144','O besimtarë! Mos i zgjidhni mohuesit për miq - në vend të besimtarëve! A mos vallë doni t’i jepni Allahut dëshmi të qartë kundër vetvetes?');
INSERT INTO "hasan_efendi" VALUES(4,145,'4:145','Pa dyshim që hipokritët do të jenë në thellësinë më të madhe të Xhehenemit dhe ti kurrsesi nuk do t’u gjesh atyre ndihmës.');
INSERT INTO "hasan_efendi" VALUES(4,146,'4:146','Por ata që pendohen, përmirësohen, mbështeten fort tek Allahu dhe që besimin e tyre e kanë sinqerisht vetëm për Allahun, do të jenë me besimtarët e vërtetë. Allahu do t’u japë shpërblim të madh besimtarëve.');
INSERT INTO "hasan_efendi" VALUES(4,147,'4:147','Përse t’ju ndëshkojë Allahu, nëse ju e falënderoni dhe e besoni?! Allahu është Mirënjohës dhe i Gjithëdijshëm.');
INSERT INTO "hasan_efendi" VALUES(4,148,'4:148','Allahu nuk do që të thuhen hapur fjalë të këqija, përveçse nga të dëmtuarit. Allahu dëgjon dhe di gjithçka.');
INSERT INTO "hasan_efendi" VALUES(4,149,'4:149','Në qoftë se ju bëni ndonjë vepër të mirë haptazi ose fshehurazi ose falni ndonjë vepër të keqe, Allahu është Falës i madh dhe i Plotfuqishëm.');
INSERT INTO "hasan_efendi" VALUES(4,150,'4:150','Vërtet, ata që mohojnë Allahun dhe të dërguarit e Tij dhe dëshirojnë të ndajnë Allahun nga të dërguarit e Tij, duke thënë: “Ne disa i besojmë e disa nuk i besojmë” dhe duke dashur që kështu të zgjedhin një rrugë të ndërmjetme,');
INSERT INTO "hasan_efendi" VALUES(4,151,'4:151','pikërisht ata janë mohuesit e vërtetë. Ne kemi përgatitur dënim poshtërues për mohuesit.');
INSERT INTO "hasan_efendi" VALUES(4,152,'4:152','Ndërsa atyre që besojnë Allahun dhe të dërguarit e Tij dhe nuk bëjnë dallim mes asnjërit prej tyre, Ai do t’u japë shpërblimin (e premtuar). Se Allahu është Falës dhe Mëshirëplotë.');
INSERT INTO "hasan_efendi" VALUES(4,153,'4:153','Ithtarët e Librit kërkojnë prej teje (o Muhamed) që t’u zbresësh një libër prej qiellit. Në fakt, ata i kërkuan Musait diçka edhe më të madhe (duke i thënë): “Na e trego Allahun haptazi!” Atëherë i shkatërroi rrufeja, për shkak të poshtërsisë që bënë. Pastaj zgjodhën viçin (për adhurim) pas shenjave të qarta që iu patën ardhur. Ne ua falëm atë dhe i dhamë Musait pushtet të dukshëm.');
INSERT INTO "hasan_efendi" VALUES(4,154,'4:154','Ne ngritëm mbi ata malin Tur, sipas besëlidhjes me ta. Pastaj u thamë: “Hyni në portë duke u përulur” dhe “Mos e shkelni sabatin!” dhe morëm nga ata një besë të fortë.');
INSERT INTO "hasan_efendi" VALUES(4,155,'4:155','(Ne i mallkuam ata) për shkak të shkeljes së besëlidhjes nga ana e tyre, për shkak të mohimit që i bënë shpalljeve të Allahut, për shkak të vrasjes së profetëve pa kurrfarë të drejte dhe për thënien e tyre: “Zemrat tona janë të mbyllura në këllëf (prandaj s’i kuptojmë fjalët e profetëve)”. Nuk është kështu, por Allahu ua ka vulosur ato për shkak të mohimit të tyre, prandaj pak prej tyre besojnë.');
INSERT INTO "hasan_efendi" VALUES(4,156,'4:156','(I mallkuam ata) për shkak të mosbesimit të tyre (ndaj Isait a.s.), për shkak të shpifjeve të mëdha që thanë kundër Merjemes');
INSERT INTO "hasan_efendi" VALUES(4,157,'4:157','dhe për fjalët e tyre: “Ne e vramë Mesihun – Isain, të birin e Merjemes, të dërguarin e Allahut”. Por, ata as e vranë, as e kryqëzuan, por ashtu u është dukur. Ata që nuk u pajtuan për çështjen e tij, me siguri që gjenden në dyshim për të. Ata nuk kanë ditur kurrgjë për të, por vetëm kanë hamendësuar. Ata, në të vërtetë, nuk e kanë vrarë,');
INSERT INTO "hasan_efendi" VALUES(4,158,'4:158','por, Allahu e ka ngritur pranë Vetes. Allahu është i Plotfuqishëm dhe i Gjithëdijshëm.');
INSERT INTO "hasan_efendi" VALUES(4,159,'4:159','Të gjithë ithtarët e Librit do t’i besojnë Isait para se ai të vdesë dhe, në Ditën e Kiametit, ai do të jetë dëshmitar kundër tyre.');
INSERT INTO "hasan_efendi" VALUES(4,160,'4:160','Ne ua ndaluam atyre disa ushqime të mira, të cilat ishin të lejuara për ta, për shkak të prapësive që bënë, për shkak se ata pengonin shumë njerëz nga udha e Allahut,');
INSERT INTO "hasan_efendi" VALUES(4,161,'4:161','për shkak se merrnin kamatë, megjithëse kjo ishte e ndaluar për ta dhe për shkak se përvetësonin padrejtësisht pasurinë e njerëzve. Jobesimtarëve midis tyre, Ne u kemi përgatitur një dënim të dhembshëm.');
INSERT INTO "hasan_efendi" VALUES(4,162,'4:162','Mirëpo të thelluarit në dituri prej tyre (hebrenjve) dhe besimtarët besojnë në atë që të është shpallur ty (o Muhamed) dhe në atë që është shpallur para teje. Posaçërisht atyre që falin namazin, atyre që ndajnë zekatin dhe atyre që besojnë në Allahun dhe në Ditën e Kiametit, me siguri do t’u japim shpërblim të madh.');
INSERT INTO "hasan_efendi" VALUES(4,163,'4:163','Ne të frymëzuam ty (o Muhamed) me shpallje, ashtu siç frymëzuam me shpallje Nuhun dhe profetët pas tij; siç frymëzuam me shpallje edhe Ibrahimin, Ismailin, Is’hakun, Jakubin dhe bijtë e tij, Isain, Ejubin, Junusin, Harunin, Sulejmanin dhe Davudin, të cilit i dhamë Zeburin.');
INSERT INTO "hasan_efendi" VALUES(4,164,'4:164','Për disa të dërguar të kemi treguar më parë dhe për disa të tjerë nuk të kemi treguar ty. Sa i takon Musait, Allahu i ka folur atij drejtpërdrejt.');
INSERT INTO "hasan_efendi" VALUES(4,165,'4:165','(Këta janë) të dërguar që kanë sjellë lajme të mira e kanë paralajmëruar, në mënyrë që njerëzit të mos kenë ndonjë justifikim ndaj Allahut, pas ardhjes së të dërguarve. Se, Allahu, është i Plotfuqishëm dhe i Gjithëdijshëm.');
INSERT INTO "hasan_efendi" VALUES(4,166,'4:166','Por Allahu dëshmon për atë që të ka shpallur ty (o Muhamed), se Ai ta ka shpallur atë me dijeninë e Tij; po ashtu edhe engjëjt dëshmojnë për këtë. Allahu është i mjaftueshëm si dëshmitar.');
INSERT INTO "hasan_efendi" VALUES(4,167,'4:167','Me të vërtetë, ata që kanë mohuar dhe kanë larguar njerëzit nga rruga e Allahut, ata kanë humbur larg prej udhës së drejtë.');
INSERT INTO "hasan_efendi" VALUES(4,168,'4:168','Ata që nuk besojnë dhe bëjnë padrejtësi, Allahu nuk i fal, as nuk do t’i udhëzojë në kurrfarë rruge,');
INSERT INTO "hasan_efendi" VALUES(4,169,'4:169','përveçse drejt rrugës së Xhehenemit, ku do të qëndrojnë përgjithmonë. Dhe kjo për Allahun është e lehtë.');
INSERT INTO "hasan_efendi" VALUES(4,170,'4:170','O njerëz! I Dërguari ju ka sjellë të vërtetën nga Zoti juaj, prandaj besoni, se për ju është më mirë! E në qoftë se nuk besoni, ta dini se Allahut i përket gjithçka që gjendet në qiej dhe në Tokë. Allahu është i Gjithëdijshëm dhe i Urtë.');
INSERT INTO "hasan_efendi" VALUES(4,171,'4:171','O ithtarët e Librit! Mos e kaloni kufirin në besimin tuaj dhe për Allahun thoni vetëm të vërtetën! Mesihu - Isai, i biri i Merjemes, është vetëm i Dërguar i Allahut dhe Fjala e Tij, të cilën ia ka dërguar Merjemes, si dhe shpirt (i krijuar) nga Ai. Pra, besojini Allahut dhe të dërguarve të Tij! Dhe mos thoni: “Tre zota”! Hiqni dorë, se është më mirë për ju! Allahu është vetëm një Zot - qoftë lavdëruar Ai! Ai është tepër i lartësuar për të pasur fëmijë. E Tij është gjithçka që gjendet në qiej dhe në Tokë. Allahu mjafton për rregullimin e gjithësisë. ');
INSERT INTO "hasan_efendi" VALUES(4,172,'4:172','Mesihu nuk do ta përbuzte kurrë që të ishte rob i Allahut, as edhe engjëjt më të afërm. Ata që e shpërfillin dhe e nënçmojnë me arrogancë adhurimin ndaj Tij, Allahu do t’i sjellë të gjithë para Vetes.');
INSERT INTO "hasan_efendi" VALUES(4,173,'4:173','Sa për ata që besojnë dhe punojnë vepra të mira, Ai do t’ua plotësojë shpërblimin dhe do t’ua shtojë atë nga mirësitë e Tij. Por, ata që shpërfillin dhe mburren, do të dënohen me ndëshkim pikëllues. Ata nuk do të gjejnë për vete as mbrojtës dhe as ndihmës tjetër në vend të Allahut.');
INSERT INTO "hasan_efendi" VALUES(4,174,'4:174','O njerëz! Me të vërtetë, juve ju ka ardhur një provë bindëse (Muhamedi a.s.) nga Zoti juaj dhe ju është zbritur një Dritë e dukshme (Kurani).');
INSERT INTO "hasan_efendi" VALUES(4,175,'4:175','Kështu, ata që besojnë Allahun dhe mbështeten fort tek Ai, (ta dinë se) Ai do t’i fusë në mëshirën dhe mirësinë e Vet dhe do t’i udhëzojë drejt Vetes përmes rrugës së drejtë.');
INSERT INTO "hasan_efendi" VALUES(4,176,'4:176','Ata kërkojnë nga ti (Muhamed) gjykim (për dikë që vdes pa pasur trashëgimtarë). Thuaju: “Allahu ju udhëzon ju për një njeri të tillë. Nëse një burrë vdes pa pasur fëmijë (dhe prindër), ndërkohë që ka një motër, atëherë asaj i takon gjysma e trashëgimisë. Në qoftë se një grua vdes pa pasur fëmijë (dhe prindër), atëherë vëllai i saj është i vetmi që do ta trashëgojë. Në qoftë se një burrë pa fëmijë (dhe prindër) ka dy motra, atëherë ato do të trashëgojnë dy të tretat e pasurisë; por, nëse ai ka edhe vëllezër edhe motra, atëherë mashkullit i takon trashëgim sa pjesa e dy femrave. Kështu Allahu ju sqaron juve, në mënyrë që të mos humbisni. Allahu është i Dijshëm për çdo gjë. ');
INSERT INTO "hasan_efendi" VALUES(5,1,'5:1','O besimtarë! Plotësoni detyrimet! Ju është lejuar ngrënia e kafshëve, përveç atyre që do t’ju njoftohen. Derisa të gjendeni në ihram gjatë haxhit, nuk ju lejohet gjuetia. Vërtet, Allahu vendos çfarë të dëshirojë.');
INSERT INTO "hasan_efendi" VALUES(5,2,'5:2','O besimtarë! Mos e cenoni shenjtërinë e simboleve të Allahut, as të muajve të mëdhenj, as të kurbanit të Qabesë, as të kurbaneve të shënuara me gjerdan, as (mos i pengoni) ata që janë nisur të vizitojnë Shtëpinë e Shenjtë, duke kërkuar mirësinë dhe kënaqësinë e Zotit të vet. Pas heqjes së Ihramit, gjuani. Dhe, të mos ju nxisë urrejtja ndaj disa njerëzve, që ju penguan të vizitoni Qabenë, pra, kurrsesi të mos ju nxisë që t’i sulmoni! Ndihmojeni njëri-tjetrin në punë të mira dhe në të ruajturit nga të këqijat dhe jo në gjynahe dhe armiqësi! Frikësojuni Allahut, sepse Ai dënon ashpër.');
INSERT INTO "hasan_efendi" VALUES(5,3,'5:3','Është e ndaluar për ju ngrënia e cofëtinës, e gjakut, e mishit të derrit, e mishit të kafshës që është therur jo në emër të Allahut, e mishit të kafshës së mbytur lidhur në fyt, e mishit të kafshës që është rrahur dhe ka ngordhur, e mishit të kafshës që është vrarë nga rrëzimi apo nga brirët e kafshëve të tjera, e mishit të asaj që është kafshuar nga egërsirat - përveç atyre që i therni ju para se të ngordhin. Gjithashtu, është e ndaluar për ju edhe ngrënia e mishit të kafshëve që janë therur për nder të idhujve apo për të kërkuar fatin në fall. Të gjitha këto që u përmendën, janë gjynah. Sot, dëshpërohen ata që nuk besojnë dhe kanë humbur çdo shpresë, që t’ju largojnë prej fesë suaj. Mos u frikësoni prej tyre, por kini frikë prej Meje! Sot jua përsosa fenë tuaj, e plotësova dhuntinë Time ndaj jush dhe zgjodha që Islami të jetë feja juaj. Por kushdo që është i shtrënguar nga uria (dhe ha çfarë është e ndaluar), pa pasur qëllim të bëjë gjynah, do ta shohë se Allahu është Falës e Mëshirëplotë.');
INSERT INTO "hasan_efendi" VALUES(5,4,'5:4','Të pyesin ty ç’është e lejueshme që të hanë. Thuaju (o Muhamed): “Ju lejohet juve çdo gjë që është e dëlirë dhe atë që jua gjuajnë shtazët që i keni stërvitur e i përdorni në gjueti, ashtu siç ju ka mësuar Allahu. Pra, hani atë që jua sjellin, e përmendni emrin e Allahut! Frikësojuni Allahut! Allahu është i shpejtë në llogari.');
INSERT INTO "hasan_efendi" VALUES(5,5,'5:5','Sot ju janë lejuar gjërat e mira e të pastra. Ju janë lejuar edhe ushqimet e ithtarëve të Librit; po ashtu, edhe ushqimet tuaja janë të lejuara për ata. Të lejuara për ju janë femrat e ndershme besimtare dhe femrat e ndershme të atyre që u është dhënë Libri para jush, nëse ju u jepni pajën e kurorëzimit, duke u martuar me ato, e jo duke bërë kurvëri, as duke i marrë për dashnore. Kushdo që mohon besimin, veprat e tij do të zhvlerësohen dhe në jetën tjetër ai do të jetë i dështuar.');
INSERT INTO "hasan_efendi" VALUES(5,6,'5:6','O besimtarë! Kur doni të falni namaz, lani fytyrën dhe duart deri në bërryla, kurse kokën fshijeni me dorë të lagët; lani edhe këmbët deri në nyje. Nëse jeni të papastër (si pasojë e marrëdhënieve bashkëshortore apo e derdhjes së farës), pastrohuni (duke u larë krejtësisht)! Por, në qoftë se jeni të sëmurë (që mund t’ju dëmtojë uji) ose gjendeni në udhëtim, ose ndonjëri nga ju vjen pas kryerjes së nevojës natyrore, ose nëse keni pasur marrëdhënie me bashkëshortet e nuk gjeni ujë, atëherë gjeni dhé të pastër dhe fshini fytyrat e duart me të. Allahu nuk dëshiron t’ju krijojë ndonjë vështirësi, por dëshiron t’ju pastrojë dhe t’i plotësojë dhuntitë e Tij ndaj jush, që të jeni falënderues.');
INSERT INTO "hasan_efendi" VALUES(5,7,'5:7','Kujtoni dhuntinë e Allahut dhe besëlidhjen me të cilën ju ka vënë detyrimet, kur thatë: “Dëgjuam dhe u bindëm!” Frikësojuni Allahut! Vërtet, Allahu di ç’keni në zemrat tuaja.');
INSERT INTO "hasan_efendi" VALUES(5,8,'5:8','O besimtarë! Bëhuni të qëndrueshëm në urdhrat e Allahut dhe bëhuni dëshmues me drejtësi! Le të mos ju nxisë urrejtja ndaj njerëzve për të bërë padrejtësi. Bëhuni të drejtë, se kjo është më afër devotshmërisë dhe frikësojuni Allahut! Pa dyshim, Allahu është i Dijshëm për çdo gjë që punoni ju. ');
INSERT INTO "hasan_efendi" VALUES(5,9,'5:9','Allahu u ka premtuar falje (të gjynaheve) dhe shpërblim të madh atyre që besojnë dhe bëjnë vepra të mira.');
INSERT INTO "hasan_efendi" VALUES(5,10,'5:10','Sa për ata që nuk besojnë dhe mohojnë shpalljet Tona - ata do të jenë banorë të zjarrit.');
INSERT INTO "hasan_efendi" VALUES(5,11,'5:11','O besimtarë! Kujtoni mirësinë që ju dha Allahu ditën kur Ai pengoi duart e një grupi njerëzish që i kishin zgjatur ato për t’ju dëmtuar ju. Frikësojuni Allahut! Besimtarët le të mbështeten vetëm tek Allahu!');
INSERT INTO "hasan_efendi" VALUES(5,12,'5:12','Me të vërtetë, Allahu mori besëlidhjen e bijve të Israilit dhe nga mesi i tyre caktoi dymbëdhjetë mbikëqyrës dhe u tha: “Unë jam me ju! Në qoftë se do të kryeni namazin, do të jepni zekatin, do të besoni të dërguarit e Mi, do t’i ndihmoni ata dhe do t’i jepni vullnetarisht hua Allahut, atëherë, patjetër që Unë do t’jua shlyej gjynahet dhe do t’ju shpie në kopshte, nëpër të cilat rrjedhin lumenj. Por, kushdo nga ju që mohon pas kësaj, me të vërtetë, e ka humbur rrugën e drejtë.');
INSERT INTO "hasan_efendi" VALUES(5,13,'5:13','Për shkak se ata e thyen besëlidhjen e tyre, Ne i mallkuam ata dhe ua bëmë zemrat të ashpra. Ata i kanë shtrembëruar fjalët e Shkrimeve të shenjta dhe kanë harruar një pjesë të mirë nga ato këshilla . Ti (Muhamed) përherë do ta vëresh tradhtinë e tyre, përveç një pakice të tyre; pra, falua fajet dhe mos i qorto! Se, Allahu i do bamirësit.');
INSERT INTO "hasan_efendi" VALUES(5,14,'5:14','Ne kemi marrë besëlidhje edhe prej atyre që thanë: “Jemi të krishterë”, por edhe ata kanë harruar një pjesë të mirë nga ato që janë këshilluar , prandaj ua shtiem armiqësinë dhe urrejtjen midis tyre deri në Ditën e Kiametit. Allahu do t’i lajmërojë ata për veprat që kanë bërë.');
INSERT INTO "hasan_efendi" VALUES(5,15,'5:15','O ithtarët e Librit! Juve ju erdhi i Dërguari Ynë, i cili ju shpjegon gjëra nga ato që i keni fshehur ju prej Librave (Teuratit dhe Ungjillit) dhe ju fal shumë. Juve ju erdhi prej Allahut dritë (i Dërguari) dhe Libër i qartë (Kurani),');
INSERT INTO "hasan_efendi" VALUES(5,16,'5:16','me të cilin Allahu i drejton ata që ndjekin pëlqimin e Tij, në rrugën e shpëtimit, i nxjerr nga errësira në dritë me lejen dhe vullnetin e Vet dhe i shpie në rrugën e drejtë.');
INSERT INTO "hasan_efendi" VALUES(5,17,'5:17','Pa dyshim, janë jobesimtarë ata që thonë: “Allahu është Mesihu, i biri i Merjemes!” Thuaj: “Kush mund ta pengojë Allahun, nëse Ai dëshiron ta zhdukë Mesihun, të birin e Merjemes dhe nënën e tij, si dhe gjithçka që gjendet në Tokë?” Vetëm Allahut i takon sundimi i qiejve, i Tokës dhe i çdo gjëje që gjendet midis tyre. Ai krijon çfarë të dojë dhe është i Plotfuqishëm për çdo gjë.');
INSERT INTO "hasan_efendi" VALUES(5,18,'5:18','Hebrenjtë dhe të krishterët thonë: “Ne jemi bijtë e Allahut dhe të dashurit e Tij”. Thuaj: “Atëherë, përse Ai ju dënon për shkak të gjynaheve tuaja? Jo! Ju jeni njerëz nga ata që Ai ka krijuar. Ai fal kë të dojë dhe dënon kë të dojë. Vetëm Allahut i përket sundimi i qiejve, i Tokës dhe i çdo gjëje që gjendet midis tyre. Dhe vetëm tek Ai kthehen të gjithë.');
INSERT INTO "hasan_efendi" VALUES(5,19,'5:19','O ithtarë të Librit! Ju ka ardhur i Dërguari Ynë, që jua qartëson gjërat pas ndërprerjes së (vargut të) të dërguarve, për të mos thënë ju (kur të dënoheni), se nuk na ka ardhur asnjë që sjell lajme të mira e as ndonjë paralajmërues. Ja, ju erdhi ai që sjell lajme të mira dhe paralajmërime. Allahu është i Plotfuqishëm për çdo gjë.');
INSERT INTO "hasan_efendi" VALUES(5,20,'5:20','Kujto kur Musai i tha popullit të vet: “O populli im! Kujtoni mirësinë e Allahut ndaj jush, kur prej jush bëri profetë, bëri sundimtarë dhe ju dha atë që nuk ia kishte dhënë asnjë populli para jush!');
INSERT INTO "hasan_efendi" VALUES(5,21,'5:21','O populli im! Hyni në Tokën e shenjtë që Allahu e ka caktuar për ju dhe mos u ktheni prapa, se atëherë do të ktheheni të humbur!”');
INSERT INTO "hasan_efendi" VALUES(5,22,'5:22','Ata thanë: “O Musa! Në atë tokë gjendet një popull i fortë dhe ne nuk do të hyjmë pa dalë ata e, nëse ata dalin, ne atëherë, me siguri do të hyjmë në të”.');
INSERT INTO "hasan_efendi" VALUES(5,23,'5:23','Dy burra nga ata që i frikësoheshin Allahut dhe që Ai i kishte pajisur me mirësi, thanë: “Hyni në portën e qytetit! Kur të kaloni nëpër të, me siguri do të jeni fitues. Në qoftë se jeni besimtarë, atëherë mbështetuni tek Allahu!”');
INSERT INTO "hasan_efendi" VALUES(5,24,'5:24','Ata thanë: “O Musa! Kurrsesi nuk do të hyjmë atje, derisa aty të gjenden ata. Kështu që shko ti dhe Zoti yt e luftoni, se ne do të rrimë këtu”.');
INSERT INTO "hasan_efendi" VALUES(5,25,'5:25','Musai tha: “O Zoti im! Unë nuk kam fuqi për askënd tjetër veç vetes dhe vëllait, prandaj na veço ne prej këtij populli të pabindur!”');
INSERT INTO "hasan_efendi" VALUES(5,26,'5:26','Allahu tha: “Ajo (toka e shenjtë) u është ndaluar atyre për dyzet vjet. Ata do të bredhin të çoroditur nëpër tokë, por ty mos të të vijë keq për atë popull të pabindur”.');
INSERT INTO "hasan_efendi" VALUES(5,27,'5:27','Lexoju (o Muhamed) saktësisht ngjarjen e dy bijve të Ademit, kur ata bënë nga një kurban, njërit iu pranua, kurse tjetrit jo. Njëri i tha tjetrit: “Ty gjithsesi do të të vras”. Tjetri tha: “Allahu pranon vetëm prej të devotshmit.');
INSERT INTO "hasan_efendi" VALUES(5,28,'5:28','Nëse ti ngre dorë të më vrasësh mua, unë nuk do ta ngre dorën që të të vras, sepse, në të vërtetë, unë i frikësohem Allahut, Zotit të gjithësisë.');
INSERT INTO "hasan_efendi" VALUES(5,29,'5:29','Unë parapëlqej të marrësh barrën e gjynahut tim dhe të gjynahut tënd e kështu të bëhesh ndër banorët e zjarrit. Ky është ndëshkimi i keqbërësve”.');
INSERT INTO "hasan_efendi" VALUES(5,30,'5:30','Vetja e tij e nxiti në vrasjen e të vëllait dhe e vrau. Kështu, ai u bë nga të humburit.');
INSERT INTO "hasan_efendi" VALUES(5,31,'5:31','Allahu dërgoi një sorrë që rrëmihte në tokë, për t’i treguar se si ta mbulonte kufomën e të vëllait. (Kabili) tha: “I mjeri unë, a nuk qenkam i zoti të bëhem si kjo sorrë e ta mbuloj trupin e tim vëllai?!” E u bë nga të penduarit.');
INSERT INTO "hasan_efendi" VALUES(5,32,'5:32','Prandaj Ne i urdhëruam bijtë e Israilit se kush vret ndokënd, që s’ka vrarë njeri ose që nuk ka bërë çrregullime në Tokë, është sikur të ketë vrarë të gjithë njerëzit. Dhe, nëse dikush shpëton një jetë, është sikur të ketë shpëtuar jetën e krejt njerëzve. Të dërguarit Tanë u sollën shenja të qarta, por shumë nga ata, edhe pas kësaj, i kaluan tërë kufijtë me të këqija në Tokë.');
INSERT INTO "hasan_efendi" VALUES(5,33,'5:33','Pa dyshim se ndëshkimi i atyre që luftojnë kundër Allahut dhe të Dërguarit të Tij dhe që bëjnë shkatërrime në Tokë, është të vriten ose të gozhdohen, ose t’u këputen duart dhe këmbët tërthorazi, ose të dëbohen nga vendi. Ky ndëshkim është poshtërim për ata në këtë jetë, ndërsa në jetën tjetër, për ata do të ketë dënim shumë të madh.');
INSERT INTO "hasan_efendi" VALUES(5,34,'5:34','Përjashtim bëjnë ata që pendohen pa i zënë ju. Ta dini se Allahu është Falës e Mëshirëplotë.');
INSERT INTO "hasan_efendi" VALUES(5,35,'5:35','O besimtarë! Kini frikë Allahun, përpiquni që t’i afroheni Atij me vepra të mira dhe luftoni në rrugën e Tij për të shpëtuar!');
INSERT INTO "hasan_efendi" VALUES(5,36,'5:36','Me të vërtetë, sikur të kenë jobesimtarët tërë pasurinë që gjendet në tokë, madje edhe një herë aq, e ta sakrifikojnë për të shpëtuar nga dënimi i Ditës së Kiametit, nuk do të pranohet prej tyre dhe për ata do të ketë dënim pikëllues.');
INSERT INTO "hasan_efendi" VALUES(5,37,'5:37','Ata do të dëshirojnë të dalin nga zjarri, por s’do të mund të dalin dot. Ata i pret dënimi i përhershëm.');
INSERT INTO "hasan_efendi" VALUES(5,38,'5:38','Vjedhësit dhe vjedhëses ua pritni duart, si ndëshkim për atë që kanë bërë dhe si dënim nga ana e Allahut. Allahu është i Plotfuqishëm dhe i Urtë.');
INSERT INTO "hasan_efendi" VALUES(5,39,'5:39','Ndërsa atij që pendohet pas të këqijave të tij dhe përmirësohet, Allahu ia pranon pendimin, sepse Ai është vërtet Falës dhe Mëshirëplotë.');
INSERT INTO "hasan_efendi" VALUES(5,40,'5:40','A nuk e di ti se vetëm Allahut i takon sundimi i qiejve dhe i Tokës?! Ai dënon kë të dojë dhe fal kë të dojë. Allahu është i Plotfuqishëm për çdo gjë.');
INSERT INTO "hasan_efendi" VALUES(5,41,'5:41','O i Dërguari i Allahut! Mos u brengos për ata që zhyten shpejt në mosbesim, qofshin nga ata që me gojën e tyre thonë: “Besojmë”, ndërkohë që zemrat e tyre nuk besojnë apo prej hebrenjve, të cilët trillimet i dëgjojnë shumë, (madje) dëgjojnë edhe ata që s’kanë ardhur te ti. Ata i ndryshojnë vendet e fjalëve (në tekstet e shenjta) dhe thonë: “Nëse (shpallja) ju është dhënë kështu, pranojeni e, nëse nuk ju është dhënë kështu, atëherë, ruhuni e mos e pranoni”. Kur Allahu dëshiron ta lërë dikë në humbje, ti nuk mund të bësh asgjë për ta penguar Atë. Këta janë të asaj dore të cilët Allahu nuk dëshiron t’ua pastrojë zemrat. Për ata, në këtë jetë ka poshtërim, kurse në jetën tjetër ka ndëshkim të rëndë.');
INSERT INTO "hasan_efendi" VALUES(5,42,'5:42','Ata janë dëgjues të gënjeshtrës dhe e hanë me kënaqësi të madhe atë që është e ndaluar. Nëse vijnë tek ti, gjykoji ata ose shmangu prej tyre! Nëse shmangesh nga ata, nuk mund të të dëmtojnë aspak. E, nëse i gjykon, gjykoji me drejtësi! Vërtet, Allahu i do të drejtët.');
INSERT INTO "hasan_efendi" VALUES(5,43,'5:43','E qysh të drejtohen ata ty për t’i gjykuar, ndërsa e kanë Teuratin, në të cilin është gjykimi i Allahut, e madje edhe pas kësaj shmangen prej teje? Sepse ata nuk janë besimtarë.');
INSERT INTO "hasan_efendi" VALUES(5,44,'5:44','Ne kemi dërguar Teuratin, në të cilin janë udhëzimet dhe drita. Sipas tij, profetët që ia kishin dorëzuar veten Allahut, i gjykonin hebrenjtë; por edhe të diturit dhe rabinët kështu vepronin, sepse atyre iu qe besuar mbrojtja e Librit të Allahut, për të cilin ata dëshmonin. Prandaj, mos kini frikë nga njerëzit, por kini frikë vetëm prej Meje! Dhe mos i ndërroni Fjalët e Mia me ndonjë vlerë të vogël! Kushdo që nuk gjykon sipas asaj që ka shpallur Allahu, ai është mohues i vërtetë.');
INSERT INTO "hasan_efendi" VALUES(5,45,'5:45','Ne u caktuam atyre në Teurat: jeta me jetë, syri me sy, hunda me hundë, veshi me vesh, dhëmbi me dhëmb dhe plaga me plagë (të njëjtë me të). Por kushdo që fal shpagimin si lëmoshë, ka bërë një vepër që është shlyerje për gjynahet e veta. Kushdo që nuk gjykon sipas asaj që ka zbritur Allahu, ai është shkelës i vërtetë.');
INSERT INTO "hasan_efendi" VALUES(5,46,'5:46','Pas atyre profetëve, Ne dërguam Isain, të birin e Merjemes, si vërtetues të Teuratit që kishte ardhur para tij. Dhe i dhamë atij Ungjillin, në të cilin ka udhëzim dhe dritë, si përmbushje të asaj që ishte shpallur para tij në Teurat dhe si udhërrëfim e këshillë për ata që i frikësohen Allahut.');
INSERT INTO "hasan_efendi" VALUES(5,47,'5:47','Le të gjykojnë ithtarët e Ungjillit ashtu siç ka shpallur Allahu në të. Kushdo që nuk gjykon sipas asaj që ka shpallur Allahu, ai është keqbërës i vërtetë.');
INSERT INTO "hasan_efendi" VALUES(5,48,'5:48','Ne të kemi zbritur ty (o Muhamed) Librin me të vërtetën, si përmbushës të shkrimeve të mëparshme dhe mbrojtës të tyre. Prandaj gjykoji ata sipas asaj që të ka zbritur Allahu dhe mos ndiq dëshirat e tyre, duke u shmangur kështu nga e Vërteta që të është shpallur. Ne për secilin nga ju kemi sjellë një ligj dhe një rrugë të qartë. Sikur të donte Allahu, do t’ju kishte bërë një popull të vetëm, por Ai kërkon që t’ju provojë në atë që ju ka dhënë, andaj bëni gara për punë të mira! Të gjithë do të ktheheni te Allahu e Ai do t’ju lajmërojë për çështjet, rreth të cilave kishit kundërshti.');
INSERT INTO "hasan_efendi" VALUES(5,49,'5:49','Prandaj gjykoji sipas asaj që të ka shpallur Allahu e mos ndiq dëshirat e tyre të kota! Ki kujdes që ata mos të të nxisin të largohesh nga një pjesë e asaj që të ka shpallur Allahu! Nëse ata të kthejnë kurrizin e nuk dëgjojnë gjykimin tënd, atëherë ta dish se Allahu do vetëm që t’i dënojë për disa gjynahe të tyre. Në të vërtetë, shumë njerëz janë të pabindur.');
INSERT INTO "hasan_efendi" VALUES(5,50,'5:50','A mos kërkojnë ata gjykimin (me ligjin) e (kohës së) injorancës (paganizmit paraislamik)?! E kush është gjykatës më i mirë se Allahu për njerëzit që besojnë bindshëm?');
INSERT INTO "hasan_efendi" VALUES(5,51,'5:51','O besimtarë! Mos i bëni miq e mbrojtës hebrenjtë dhe të krishterët! Ata janë mbrojtës të njëri-tjetrit. Kushdo prej jush që i bën ata miq, është me ata. Vërtet, Allahu nuk i udhëzon në rrugë të drejtë keqbërësit.');
INSERT INTO "hasan_efendi" VALUES(5,52,'5:52','Ti i sheh ata me zemra të sëmura, sesi vrapojnë për t’u lidhur me ata (hebrenjtë) duke thënë: “Druajmë se mos po na godet ndonjë fatkeqësi”. Mbase Allahu do të sjellë fitore ose diçka tjetër nga ana e Tij, kështu që ata do të pendohen për atë që kanë fshehur në veten e tyre.');
INSERT INTO "hasan_efendi" VALUES(5,53,'5:53','Atëherë besimtarët do të thonë: “Vallë, këta janë ata që betoheshin solemnisht për Allahun, se me të vërtetë ishin me ju?!” Veprat e tyre do të fshihen dhe ata do të jenë humbësit.');
INSERT INTO "hasan_efendi" VALUES(5,54,'5:54','O besimtarë! Nëse ndokush prej jush braktis besimin e vet, dijeni se Allahu do të sjellë njerëz, që i do dhe që e duan; të përulur me besimtarët dhe të ashpër me femohuesit; që luftojnë në rrugën e Allahut dhe nuk i tremben qortimit të asnjë qortuesi. Kjo është mirësia e Allahut, të cilën Ai ia jep kujt të dojë. Allahu është Bujar i madh dhe i Gjithëditur.');
INSERT INTO "hasan_efendi" VALUES(5,55,'5:55','Miqtë dhe mbrojtësit tuaj janë vetëm Allahu, i Dërguari i Tij dhe besimtarët që falin namazin dhe japin zekatin, duke qenë të përulur (në adhurim) ndaj Allahut.');
INSERT INTO "hasan_efendi" VALUES(5,56,'5:56','Ata që marrin për mik e mbrojtës Allahun, të Dërguarin e Tij dhe besimtarët, (ta dinë se) pa dyshim, pala e Allahut do të jetë fituese.');
INSERT INTO "hasan_efendi" VALUES(5,57,'5:57','O besimtarë! Mos i bëni miq ata që fenë tuaj e marrin për tallje dhe lojë, qofshin ata prej atyre që u është dhënë Libri para jush apo prej mohuesve; frikësojuni Allahut, nëse jeni besimtarë!');
INSERT INTO "hasan_efendi" VALUES(5,58,'5:58','Kur thërrisni për namaz, ata tallen dhe luajnë, sepse janë njerëz që nuk kuptojnë.');
INSERT INTO "hasan_efendi" VALUES(5,59,'5:59','Thuaj: “O ithtarët e Librit! A na urreni për ndonjë arsye tjetër, përveç asaj se besojmë në Allahun dhe në shpalljet që na janë zbritur neve dhe se shumica prej jush janë të pabindur ndaj Allahut?”');
INSERT INTO "hasan_efendi" VALUES(5,60,'5:60','Thuaj: “A doni t’ju tregoj kush do të marrë dënimin më të rëndë nga Allahu? Ata që Allahu i ka mallkuar dhe me të cilët është zemëruar, duke i shndërruar në majmunë dhe derra, si dhe ata që adhurojnë djallin. Ata janë në vendin më të mjerueshëm dhe janë më larg rrugës së vërtetë”.');
INSERT INTO "hasan_efendi" VALUES(5,61,'5:61','Kur vijnë te ju, thonë: “Besojmë”. Në të vërtetë, ata jobesimtarë hynë e jobesimtarë dolën! Allahu e di mirë çfarë fshehin ata.');
INSERT INTO "hasan_efendi" VALUES(5,62,'5:62','Ti i sheh shumë nga ata që nxitojnë drejt gjynaheve, armiqësisë dhe ngrënies së fryteve të fitimit të paligjshëm. Njëmend, sa të këqija janë ato vepra!');
INSERT INTO "hasan_efendi" VALUES(5,63,'5:63','Përse rabinët dhe dijetarët nuk i ndalojnë ata të thonë fjalë të ulëta (për Zotin) dhe të hanë fitimet e paligjshme? Sa keq veprojnë ata!');
INSERT INTO "hasan_efendi" VALUES(5,64,'5:64','Hebrenjtë thonë: “Dora e Allahut është e shtrënguar.” Iu lidhshin duart atyre dhe qofshin të mallkuar për atë që thonë! Përkundrazi, të dyja Duart e Tij janë të hapura. Ai dhuron dhe jep si të dojë. Ajo që të është shpallur ty (Muhamed) nga Zoti yt, me siguri që shumicës prej tyre do t’ua shtojë mosbindjen dhe mohimin. Ne kemi futur ndërmjet tyre armiqësi dhe urrejtje deri në Ditën e Kiametit. Kurdo që ata ndezin zjarrin e luftës, Allahu e shuan atë. Ata përpiqen të shkaktojnë ngatërresa në Tokë, por Allahu nuk i do ngatërrestarët.');
INSERT INTO "hasan_efendi" VALUES(5,65,'5:65','Sikur ithtarët e Librit të besonin dhe të ruheshin nga mohimi, Ne do t’ua mbulonim veprat e këqija dhe do t’i shpinim në kopshtet e kënaqësisë.');
INSERT INTO "hasan_efendi" VALUES(5,66,'5:66','Sikur ata t’i përmbaheshin Teuratit e Ungjillit dhe asaj që u është shpallur nga Zoti i tyre, ata do të kënaqeshin me frutat (që rriten) sipër dhe poshtë tyre. Disa prej tyre janë të matur, por shumë prej tyre bëjnë vepra të këqija.');
INSERT INTO "hasan_efendi" VALUES(5,67,'5:67','O i Dërguar! Trego atë që të është shpallur nga Zoti yt! Në qoftë se nuk e bën këtë, atëherë nuk e ke përcjellë Mesazhin e Tij; Allahu të ruan ty prej njerëzve. Natyrisht që Allahu nuk i udhëzon njerëzit që nuk besojnë.');
INSERT INTO "hasan_efendi" VALUES(5,68,'5:68','Thuaj (o Muhamed): “O ithtarët e Librit! Nuk do të keni asgjë në vijë, derisa t’i përmbaheni Teuratit e Ungjillit dhe asaj që ju është shpallur nga Zoti juaj (Kurani). Ajo që të është shpallur ty nga Zoti yt, do t’ua shtojë shumicës së tyre mosbindjen dhe mohimin. Por ti mos u hidhëro për mohuesit.');
INSERT INTO "hasan_efendi" VALUES(5,69,'5:69','Ata që besojnë (në Kuran), hebrenjtë, të krishterët dhe sabinët - kushdo prej tyre që beson Allahun dhe Ditën e Fundit (sipas Kuranit) dhe bën vepra të mira (sipas Islamit), nuk do të frikësohet dhe as që do të dëshpërohet.');
INSERT INTO "hasan_efendi" VALUES(5,70,'5:70','Ne bëmë besëlidhje me bijtë e Israilit dhe u çuam të dërguar. Por, sa herë që u vinte ndonjë i dërguar me porosi që s’ua donte shpirti, ose e mohonin, ose e vrisnin.');
INSERT INTO "hasan_efendi" VALUES(5,71,'5:71','Ata mendonin se nuk do t’i gjente kurrfarë dënimi, prandaj ngelën të verbër dhe të shurdhët. Pastaj, Allahu ua pranoi pendimin, por përsëri shumë nga ata u bënë të verbër dhe të shurdhër (ndaj së vërtetës); Allahu e sheh mirë se çfarë bëjnë ata.');
INSERT INTO "hasan_efendi" VALUES(5,72,'5:72','Pa dyshim, janë jobesimtarë ata që thonë: “Allahu është Mesihu, i biri i Merjemes!”, sepse vetë Mesihu ka thënë: “O bijtë e Israilit, adhuroni Allahun, që është Zoti im dhe Zoti juaj! Sigurisht, atij që adhuron tjetërkënd përveç Allahut, Allahu do t’ia ndalojë Xhenetin dhe strehim i tij do të jetë Zjarri. Për keqbërësit nuk ka ndihmës.');
INSERT INTO "hasan_efendi" VALUES(5,73,'5:73','Pa dyshim, janë jobesimtarë ata që thonë: “Allahu është njëri prej Trinisë!” Nuk ka veçse një Zot të Vetëm të vërtetë! Nëse nuk heqin dorë nga ato që thonë, patjetër që jobesimtarët nga mesi i tyre do t’i godasë një dënim i dhembshëm.');
INSERT INTO "hasan_efendi" VALUES(5,74,'5:74','Përse ata nuk kthehen me pendesë tek Allahu e t’i kërkojnë falje Atij?! Allahu është Falës dhe Mëshirëplotë!');
INSERT INTO "hasan_efendi" VALUES(5,75,'5:75','Mesihu, i biri i Merjemes, është vetëm një i dërguar. Edhe para tij kanë kaluar të dërguar. Nëna e tij përherë ka qenë e drejtë. Që të dy hanin ushqim. Shiko si ua shpjegojmë atyre shpalljet tona dhe shih si largohen nga e Vërteta!');
INSERT INTO "hasan_efendi" VALUES(5,76,'5:76','Thuaju (o Muhamed): “Si mundet vallë, që ju adhuroni në vend të Allahut, atë që nuk është në gjendje t’ju sjellë as dëm e as dobi, ndërkohë që Allahu dëgjon dhe di çdo gjë?!”.');
INSERT INTO "hasan_efendi" VALUES(5,77,'5:77','Thuaj: “O ithtarët e Librit! Mos i shkelni kufijtë e së vërtetës në besimin tuaj dhe mos ndiqni dëshirat e një populli, që gaboi më parë, nxori prej udhës së drejtë shumë njerëz dhe vetë u shmang nga udha e drejtë”.');
INSERT INTO "hasan_efendi" VALUES(5,78,'5:78','Ata që ishin mohues nga bijtë e Israilit, u mallkuan me gjuhën e Davudit dhe të Isait, të birit të Merjemes, për shkak se kundërshtonin, shkelnin çdo kufi');
INSERT INTO "hasan_efendi" VALUES(5,79,'5:79','dhe nuk e ndalonin njëri-tjetrin të bënin vepra të këqija. Eh, sa të shëmtuara ishin veprat që bënin ata!');
INSERT INTO "hasan_efendi" VALUES(5,80,'5:80','Ti e sheh se shumë prej tyre miqësohen me jobesimtarët. Me të vërtetë, e keqe është ajo që i përgatisin ata vetes: që Allahu të zemërohet me ta dhe të mbesin në dënim përjetësisht.');
INSERT INTO "hasan_efendi" VALUES(5,81,'5:81','Sikur ata t’i besonin Allahut, të Dërguarit dhe asaj që i është shpallur atij, nuk do t’i merrnin jobesimtarët për miq. Por, shumica prej tyre janë të pabindur.');
INSERT INTO "hasan_efendi" VALUES(5,82,'5:82','Me siguri do të vëresh se armiqtë më të ashpër të besimtarëve janë hebrenjtë dhe idhujtarët dhe se miqtë më të afërt të tyre janë ata që thonë: “Ne jemi të krishterë”. Kjo për shkak se ndër ta ka priftërinj (të dhënë mbas dijes) e murgj (të përkushtuar ndaj adhurimit) dhe se ata nuk janë kryelartë (ndaj së vërtetës).');
INSERT INTO "hasan_efendi" VALUES(5,83,'5:83','Kur dëgjojnë për atë (Kuranin) që i është zbritur të Dërguarit, i sheh se si u rrjedhin lotët nga sytë, për shkak të së vërtetës që kuptuan. Ata thonë: “O Zoti ynë! Ne besojmë, prandaj na llogarit ndër dëshmitarët e Tu!');
INSERT INTO "hasan_efendi" VALUES(5,84,'5:84','E përse të mos besojmë Allahun dhe të Vërtetën, që na ka ardhur, kur shpresojmë se edhe ne Zoti Ynë do të na çojë me njerëzit e mirë?!”');
INSERT INTO "hasan_efendi" VALUES(5,85,'5:85','Për fjalët e tyre, Allahu do t’i shpërblejë me kopshte, nëpër të cilat rrjedhin lumenj dhe ku ata do të qëndrojnë përgjithmonë. Ky është shpërblimi i punëmirëve.');
INSERT INTO "hasan_efendi" VALUES(5,86,'5:86','Ndërsa ata që mohuan dhe i përgënjeshtruan shpalljet Tona, do të jenë banorë të Zjarrit.');
INSERT INTO "hasan_efendi" VALUES(5,87,'5:87','O besimtarë! Mos ia ndaloni vetes gjërat e mira, të cilat Allahu jua ka lejuar dhe mos e kaloni kufirin! Vërtet, Allahu nuk i do ata që e kapërcejnë kufirin.');
INSERT INTO "hasan_efendi" VALUES(5,88,'5:88','Hani nga ato gjëra të lejuara e të mira që ju ka dhënë Allahu! Frikësojuni Allahut, të Cilit ju i keni besuar!');
INSERT INTO "hasan_efendi" VALUES(5,89,'5:89','Allahu nuk ju ndëshkon për betimin që e bëni pa qëllim, por ju ndëshkon për betimin e bërë me qëllim. Larja e gjynahut për thyerjen e betimit është: ushqyerja e dhjetë të varfërve me mesataren e ushqimit të familjes suaj ose t’i vishni ata, ose të lironi një skllav. Ai që nuk gjen mundësi për këto, le të agjërojë tri ditë. Kjo është shlyerja për thyerjen e betimeve tuaja, nëse betoheni. Mbani betimet tuaja! Kështu jua shpjegon Allahu Fjalët dhe ligjet e Veta, që të jeni mirënjohës.');
INSERT INTO "hasan_efendi" VALUES(5,90,'5:90','O besimtarë! Me të vërtetë, pijet alkoolike, bixhozi, idhujt dhe shigjetat e fallit janë vepra të ndyta nga punët e djallit. Prandaj, largohuni nga këto, me qëllim që të shpëtoni!');
INSERT INTO "hasan_efendi" VALUES(5,91,'5:91','Vërtet, djalli me pije alkoolike dhe me bixhoz kërkon që të fusë midis jush armiqësi e urrejtje dhe t’ju shmangë nga të kujtuarit e Allahut dhe kryerja e namazit. Prandaj, a po hiqni dorë?');
INSERT INTO "hasan_efendi" VALUES(5,92,'5:92','Bindjuni Allahut dhe bindjuni të Dërguarit! Kujdes! Nëse nuk ia vini veshin, ta dini se i Dërguari ka për detyrë vetëm të përçojë shpalljen e qartë!');
INSERT INTO "hasan_efendi" VALUES(5,93,'5:93','Nuk ka gjynah për ata që kanë besuar dhe kanë bërë vepra të mira për atë që kanë ngrënë, për sa kohë që ata ruhen (nga të ndaluarat), besojnë dhe bëjnë vepra të mira, e sërish ruhen dhe besojnë e vazhdimisht ruhen dhe bëjnë vepra të mira. Allahu i do punëmirët.');
INSERT INTO "hasan_efendi" VALUES(5,94,'5:94','O besimtarë! Allahu do t’ju provojë me ndalim nga një gjueti që e arrijnë duart dhe shigjetat tuaja, që Allahu ta njohë atë që i frikësohet Atij, kur askush nuk e sheh. E, kushdo që ta kalojë kufirin, për të ka dënim pikëllues.');
INSERT INTO "hasan_efendi" VALUES(5,95,'5:95','O besimtarë! Mos gjuani, duke qenë në ihram! Kushdo që e vret gjahun me paramendim, për dënim duhet të therë si kurban në Qabe një kafshë shtëpiake të ngjashme me kafshën e gjuajtur, sipas vlerësimit të dy njerëzve të drejtë midis jush; ose të ushqejë disa të varfër për të larë gjynahun; ose të agjërojë në atë masë, që ta ndiejë ndëshkimin për gjynahun e bërë. Allahu e fal atë që ka kaluar (para ndalimit). Ndërsa atë që vepron pas ndalimit, Allahu do ta ndëshkojë. Allahu është i Plotfuqishëm dhe i Zoti për shpagim.');
INSERT INTO "hasan_efendi" VALUES(5,96,'5:96','Ju është lejuar gjuetia e detit dhe çdo ushqim nga deti është për t’u kënaqur ju dhe udhëtarët, por ju është ndaluar gjuetia e tokës, derisa gjendeni në ihram. Kijeni frikë Allahun, para të Cilit do të tuboheni!');
INSERT INTO "hasan_efendi" VALUES(5,97,'5:97','Allahu ka bërë Shtëpinë e Shenjtë të Qabesë, muajin e shenjtë, kurbanin dhe shenjat e tyre vlera të përhershme për njerëzimin, në mënyrë që ju ta dini se Allahu di gjithçka që gjendet në qiej e në Tokë dhe se Ai është i Gjithëdijshëm për çdo gjë.');
INSERT INTO "hasan_efendi" VALUES(5,98,'5:98','Ta dini se Allahu është i Ashpër në ndëshkim, por njëkohësisht është edhe Falës e Mëshirëplotë.');
INSERT INTO "hasan_efendi" VALUES(5,99,'5:99','I Dërguari ka për detyrë vetëm t’ju kumtojë Shpalljen. Por Allahu e di çfarë bëni haptazi e çfarë fshehurazi.');
INSERT INTO "hasan_efendi" VALUES(5,100,'5:100','Thuaju: “Nuk barazohet e keqja me të mirën, edhe sikur të të habisë bollëku i së keqes. Prandaj kijeni frikë Allahun, o njerëz me intelekt, me qëllim që të shpëtoni”.');
INSERT INTO "hasan_efendi" VALUES(5,101,'5:101','O besimtarë! Mos pyesni për gjëra, që, sikur t’ju thuheshin haptazi, nuk do t’ju pëlqenin e, nëse do të pyesnit për to kur ju shpallet Kurani, do t’ju bëheshin të qarta. Allahu jua ka falur kërshërinë. Se Allahu është Falës dhe i Butë.');
INSERT INTO "hasan_efendi" VALUES(5,102,'5:102','Disa njerëz i kanë bërë pyetje të tilla para jush e pastaj u bënë jobesimtarë.');
INSERT INTO "hasan_efendi" VALUES(5,103,'5:103','Allahu nuk e përligj as Behirën, as Saibën, as Vesilën, as Hamin . Por femohuesit trillojnë gënjeshtra për Allahun! Shumica syresh nuk kanë mend.');
INSERT INTO "hasan_efendi" VALUES(5,104,'5:104','Kur u thuhet: “Ejani në atë që ka zbritur Allahu dhe ejani te i Dërguari”! ata thonë: “Neve na mjafton rruga që kemi gjetur nga etërit tanë”. Edhe pse etërit e tyre nuk kanë ditur asgjë e as nuk kanë qenë në udhën e vërtetë?!');
INSERT INTO "hasan_efendi" VALUES(5,105,'5:105','O besimtarë! Ju keni përgjegjësi vetëm për veten tuaj! Ai që është i humbur nuk mund t’ju dëmtojë, nëse jeni në rrugë të drejtë. Tek Allahu do të ktheheni të gjithë e do t’ju lajmërojë për atë që keni punuar.');
INSERT INTO "hasan_efendi" VALUES(5,106,'5:106','O besimtarë! Kur t’ju afrohet vdekja, me rastin e lënies së testamentit, le të dëshmojnë dy të ndershëm prej jush ose dy të tjerë që nuk janë prej jush, nëse fatkeqësia e vdekjes ju zë ndërsa jeni duke udhëtuar nëpër botë. Mbajini ata pas namazit dhe, nëse dyshoni në ndershmërinë e tyre, le të betohen në Allahun (duke thënë): “Ne nuk do ta shesim betimin me kurrfarë çmimi edhe nëse është fjala për çfarëdo të afërmi dhe dëshminë që na urdhëron Allahu nuk do ta fshehim, sepse, atëherë do të ishim vërtet keqbërës”.');
INSERT INTO "hasan_efendi" VALUES(5,107,'5:107','Nëse më pas zbulohet se këta dy dëshmues kanë bërë gjynah, atëherë le të zëvendësohen nga dy të tjerë prej atyre që këta u kanë bërë padrejtësi! Pastaj, dy dëshmitarët e fundit le të betohen në Allahun (duke thënë): “Pa dyshim, dëshmia jonë është më e drejtë se dëshmia e tyre. Ne nuk gënjejmë, sepse përndryshe do të ishim keqbërës”.');
INSERT INTO "hasan_efendi" VALUES(5,108,'5:108','Kështu është më e lehtë që ata të bëjnë dëshminë e tyre të saktë ose të frikësohen nëse betimet e tyre do të dalin në kundërshtim me betime të tjera. Frikësojuni Allahut dhe dëgjojeni! Allahu nuk i udhëzon në rrugë të drejtë njerëzit ngatërrestarë.');
INSERT INTO "hasan_efendi" VALUES(5,109,'5:109','Një ditë Allahu do t’i tubojë të dërguarit e do t’u thotë: “Si ju janë përgjigjur?” Ata do të thonë: “Ne s’kemi dijeni; vetëm Ti i di të gjitha fshehtësitë”.');
INSERT INTO "hasan_efendi" VALUES(5,110,'5:110','Allahu do të thotë: “O Isa, i biri Merjemes, kujtoje mirësinë Time ndaj teje dhe nënës sate: se si të forcova ty me Shpirtin e Shenjtë (Xhebrailin), kështu që ti u predikoje njerëzve, kur ishe foshnjë në djep dhe i rritur; se si të mësova Librin dhe Urtësinë, Teuratin dhe Ungjillin; se si me vullnetin Tim krijoje një figurë shpendi prej balte, pastaj fryje në të dhe ajo bëhej zog me vullnetin Tim; se si me vullnetin Tim i shëroje të lindurit të verbër e të lebrosurit; se si me vullnetin Tim i ngjallje të vdekurit; se si i pengova sulmet e bijve të Israilit kundër teje, atëherë kur iu solle atyre prova të qarta e mohuesit në radhët e tyre thanë: “Kjo është vetëm magji e kulluar!”;');
INSERT INTO "hasan_efendi" VALUES(5,111,'5:111','dhe se si kur i urdhërova dishepujt: “Besomëni Mua dhe të Dërguarit Tim!” - ata u përgjigjën: “Ne besojmë, prandaj dëshmo se Ty të jemi dorëzuar!”');
INSERT INTO "hasan_efendi" VALUES(5,112,'5:112','Kujto kur dishepujt thanë: “O Isa, i biri i Merjemes, a mundet Zoti yt të na zbresë një sofër prej qielli?” Ai tha: “Kini frikë Allahun, nëse jeni besimtarë të vërtetë!”');
INSERT INTO "hasan_efendi" VALUES(5,113,'5:113','“Ne dëshirojmë të hamë nga ajo – thanë ata –, me qëllim që të na qetësohen zemrat dhe ta dimë se na ke thënë të vërtetën dhe për atë të jemi dëshmitarë”.');
INSERT INTO "hasan_efendi" VALUES(5,114,'5:114','“O Allah, o Zoti ynë! – tha Isai, i biri i Merjemes – Zbritna një sofër nga qielli, që të jetë festë për ne, për të parin dhe të fundmin tonë dhe të jetë mrekulli prej Teje dhe na jep mirësi, se Ti je Dhuruesi më i mirë!”');
INSERT INTO "hasan_efendi" VALUES(5,115,'5:115','Allahu u përgjigj: “Unë do t’jua zbres sofrën. Por cilindo nga ju që nuk beson pas kësaj, do ta dënoj ashtu siç nuk kam dënuar askënd në botë”.');
INSERT INTO "hasan_efendi" VALUES(5,116,'5:116','Pastaj Allahu do të thotë (në Ditën e Gjykimit): “O Isa, i biri i Merjemes! Ti ishe ai që u the njerëzve: “Adhuromëni mua dhe nënën time si zota krahas Allahut”?! Isai do të thotë: “Qofsh i lartësuar dhe i lavdëruar! Mua nuk më takon të them diçka, për të cilën nuk kam aspak të drejtë! Po ta kisha thënë ndonjëherë, Ti me siguri do ta dije. Ti e di çfarë ka në shpirtin tim, po unë nuk e di çfarë ka në Veten Tënde. Vetëm Ti i di të gjitha të fshehtat.');
INSERT INTO "hasan_efendi" VALUES(5,117,'5:117','Unë nuk u kam thënë asgjë tjetër, përveç asaj që më ke urdhëruar Ti: “Adhuroni Allahun, Zotin tim dhe Zotin tuaj!” Kam qenë dëshmitar, për sa kohë që kam jetuar midis tyre dhe, qëkurse më more mua, vetëm Ti ishe mbikëqyrës i tyre. Ti je Dëshmitar për çdo gjë.');
INSERT INTO "hasan_efendi" VALUES(5,118,'5:118','Nëse Ti i dënon ata - robërit e Tu janë, e nëse i fal ata, me të vërtetë, vetëm Ti je i Plotfuqishmi dhe i Urti.');
INSERT INTO "hasan_efendi" VALUES(5,119,'5:119','Allahu thotë: “Kjo është Dita në të cilën të sinqertëve u sjell dobi sinqeriteti i tyre. Atyre u takojnë kopshtet, nëpër të cilat rrjedhin lumenjtë e ku do të qëndrojnë përgjithmonë. Allahu është i kënaqur me ata dhe ata janë të kënaqur me Atë. Kjo është fitorja e madhe!”');
INSERT INTO "hasan_efendi" VALUES(5,120,'5:120','Allahut i takon sundimi i qiejve dhe i Tokës dhe gjithçka që ato përmbajnë. Ai është i Fuqishëm për çdo gjë.');
INSERT INTO "hasan_efendi" VALUES(6,1,'6:1','Qoftë lavdëruar Allahu që krijoi qiejt dhe Tokën dhe bëri errësirën dhe dritën. E megjithatë, ata që nuk besojnë, sajojnë zota të tjerë si të barabartë me Zotin e tyre.');
INSERT INTO "hasan_efendi" VALUES(6,2,'6:2','Është Ai që ju krijoi nga balta dhe pastaj caktoi një afat në këtë botë (vdekjen) dhe një tjetër në botën e përtejme (Ditën e Kiametit). Por përsëri ju dyshoni.');
INSERT INTO "hasan_efendi" VALUES(6,3,'6:3','Ai është Allahu (i vetmi Zot i Vërtetë) në qiej dhe në Tokë; Ai di çfarë fshihni dhe çfarë tregoni, si dhe atë që arrini të fitoni.');
INSERT INTO "hasan_efendi" VALUES(6,4,'6:4','Sa herë që atyre (jobesimtarëve) u vjen ndonjë varg nga shpallja e Zotit të tyre, ata ia kthejnë shpinën.');
INSERT INTO "hasan_efendi" VALUES(6,5,'6:5','Kështu, ata e mohojnë të vërtetën, posa u shpallet, por së shpejti do t’u vijnë lajmet e asaj me të cilën talleshin.');
INSERT INTO "hasan_efendi" VALUES(6,6,'6:6','A nuk e shohin ata se sa brezni i kemi zhdukur para tyre?! Ata ishin njerëz që i kishim dhënë më shumë pushtet në tokë sesa ju kemi dhënë juve (mekasve)? Ne u sollëm atyre shira të bollshëm dhe bëmë që të rrjedhin lumenj nëpër viset e tyre; por, për shkak të gjynaheve të veta, i shfarosëm dhe, më pas, krijuam brezni të tjera.');
INSERT INTO "hasan_efendi" VALUES(6,7,'6:7','Sikur Ne të të zbritnim ty një Libër të shkruar në letër e ata ta preknin me duart e tyre, mosbesimtarët me siguri që do të thoshin: “Kjo s’është gjë tjetër, veçse një magji e hapur.”');
INSERT INTO "hasan_efendi" VALUES(6,8,'6:8','Ata thonë: “Sikur t’i kishte zbritur atij (Muhamedit) një engjëll?! Por po të dërgonim Ne engjëllin, puna do të merrte fund dhe nuk do t’u jepej asnjë afat.');
INSERT INTO "hasan_efendi" VALUES(6,9,'6:9','Edhe sikur Ne ta bënim atë (Muhamedin) engjëll, me siguri që do t’i jepnim atij pamje njeriu dhe, kështu, do të bënim që të ngatërroheshin në lidhje me mesazhin, për të cilin tashmë ata janë të ngatërruar.');
INSERT INTO "hasan_efendi" VALUES(6,10,'6:10','Me të vërtetë, janë përqeshur shumë të dërguar para teje, por ata që përqeshnin i përfshiu ajo me të cilën ata talleshin.');
INSERT INTO "hasan_efendi" VALUES(6,11,'6:11','Thuaj: “Udhëtoni nëpër botë e shihni çfarë dënimi kanë marrë ata që përgënjeshtronin (të dërguarit).');
INSERT INTO "hasan_efendi" VALUES(6,12,'6:12','Thuaj: “Kujt i përket gjithçka që gjendet në qiej dhe në Tokë?” Përgjigju: “Allahut!” Ai ia ka bërë detyrim Vetes mëshirën dhe, me siguri, do t’ju tubojë në Ditën e Kiametit, për të cilën nuk ka pikë dyshimi. Ata që kanë humbur shpirtrat e tyre, në të vërtetë nuk do të besojnë asnjëherë.');
INSERT INTO "hasan_efendi" VALUES(6,13,'6:13','E Tij është çdo gjë që pushon natën dhe ditën. Ai dëgjon dhe di çdo gjë.');
INSERT INTO "hasan_efendi" VALUES(6,14,'6:14','Thuaj: “A të marr mbrojtës tjetër, përveç Allahut, Krijuesit të qiejve dhe Tokës? Ai e ushqen (botën), ndërsa Vetë nuk ushqehet”. Thuaj: “Unë jam urdhëruar që të jem i pari që t’i nënshtrohem Atij” dhe kurrsesi mos u bëj idhujtar!');
INSERT INTO "hasan_efendi" VALUES(6,15,'6:15','Thuaj: “Unë kurrë nuk e kundërshtoj Zotin tim, sepse i frikësohem dënimit të një Dite të Madhe”.');
INSERT INTO "hasan_efendi" VALUES(6,16,'6:16','Atë që shpëtohet nga dënimi i kësaj dite, vërtet Zoti e ka mëshiruar. Ky është shpëtimi i vërtetë.');
INSERT INTO "hasan_efendi" VALUES(6,17,'6:17','Nëse nga Allahu të godet ndonjë fatkeqësi, atëherë askush përveç Tij nuk mund të të shpëtojë. E, nëse të dhuron ndonjë mirësi, dije se vetëm Ai është i Fuqishëm për çdo gjë.');
INSERT INTO "hasan_efendi" VALUES(6,18,'6:18','Ai është Sunduesi mbi robërit e Vet; Ai është i Urti dhe ka dijeni për gjithçka.');
INSERT INTO "hasan_efendi" VALUES(6,19,'6:19','Thuaj: “Cila është dëshmia më e madhe (që ti je profet)?” dhe përgjigju: “Allahu është dëshmitar midis meje dhe jush”. Ky Kuran më është shpallur, në mënyrë që nëpërmjet tij t’ju tërheq vërejtjen juve dhe kujtdo që i përcillet. A mos vallë ju dëshmoni se, përveç Allahut ka edhe zota tjerë? Thuaj: “Unë nuk dëshmoj”. Thuaj: “Vërtet, Ai është vetëm Një Zot dhe unë jam larg nga ato që ju ia shoqëroni Allahut (në adhurim)”.');
INSERT INTO "hasan_efendi" VALUES(6,20,'6:20','Ata që ua kemi dhënë Librin , e njohin (Muhamedin), ashtu si i njohin fëmijët e tyre. Por ata që kanë humbur shpirtrat e tyre, nuk do të besojnë kurrë.');
INSERT INTO "hasan_efendi" VALUES(6,21,'6:21','Kush është më keqbërës se ai që trillon gënjeshtra për Allahun ose i përgënjeshtron shpalljet e Tij? Vërtet, keqbërësit nuk do të shpëtojnë.');
INSERT INTO "hasan_efendi" VALUES(6,22,'6:22','Atë ditë kur do t’i tubojmë të gjithë, Ne do t’u themi idhujtarëve: “Ku janë idhujt tuaj, për të cilët thoshit se ishin ortakë të Allahut!”');
INSERT INTO "hasan_efendi" VALUES(6,23,'6:23','Ata nuk do të mund të shfajësohen ndryshe, veçse duke thënë: “Betohemi në Allahun, Zotin tonë, nuk kemi qenë idhujtarë!”');
INSERT INTO "hasan_efendi" VALUES(6,24,'6:24','Shiko se si gënjejnë kundër vetes, por zotat që ata i trilluan, do t’i lënë në baltë.');
INSERT INTO "hasan_efendi" VALUES(6,25,'6:25','Disa prej tyre duket sikur të dëgjojnë, por Ne kemi vënë perde në zemrat e tyre dhe ua kemi rënduar dëgjimin, në mënyrë që ata të mos kuptojnë. Edhe nëse do të shihnin ndonjë prej shenjave Tona, ata sërish nuk do të besonin. Kur vijnë tek ti për të të kundërshtuar, jobesimtarët thonë: “Këto janë vetëm trillime të popujve të lashtë”!');
INSERT INTO "hasan_efendi" VALUES(6,26,'6:26','Ata i ndalojnë të tjerët të besojnë në Kuran, edhe vetë largohen nga ai. Por vetëm veten e dëmtojnë, ndonëse nuk e vënë re.');
INSERT INTO "hasan_efendi" VALUES(6,27,'6:27','Sikur t’i shohësh ata kur të vihen para zjarrit, duke thënë: “Ah! Sikur të ktheheshim pas, nuk do t’i mohonim shpalljet e Zotit tonë dhe do të bëheshim besimtarë!”');
INSERT INTO "hasan_efendi" VALUES(6,28,'6:28','Po, atyre iu doli sheshit ajo që më parë e fshihnin, por edhe sikur të ktheheshin, prapë do të bënin ato që u ishin ndaluar. Vërtet, ata janë gënjeshtarë.');
INSERT INTO "hasan_efendi" VALUES(6,29,'6:29','Ata thoshin: “S’ka tjetër, përveç jetës në këtë botë dhe ne nuk do të ringjallemi”.');
INSERT INTO "hasan_efendi" VALUES(6,30,'6:30','Ah, sikur t’i shohësh ata kur të dalin para Zotit! Ai do t’ju thotë: “A nuk është e vërtetë kjo?” Ata do të përgjigjen: “Po, është e vërtetë, për Zotin tonë!” Allahu do t’u thotë: “E pra, tani shijoni dënimin, sepse nuk besuat!”');
INSERT INTO "hasan_efendi" VALUES(6,31,'6:31','Sigurisht që janë të humbur ata që mohojnë takimin me Allahun! Kur Ora (e Kiametit) t’u vijë papritur, ata do të thërrasin: “Të mjerët ne, sa shumë që e kemi neglizhuar (Kiametin) në jetën e Tokës!” dhe do të bartin gjynahet e veta mbi shpinat e tyre. Eh, sa e shëmtuar është ajo barrë!');
INSERT INTO "hasan_efendi" VALUES(6,32,'6:32','Jeta e kësaj bote është vetëm lojë e argëtim, kurse bota tjetër, pa dyshim që është më e mirë për ata që i frikësohen Allahut. Pra, a nuk po mendoni?');
INSERT INTO "hasan_efendi" VALUES(6,33,'6:33','Ne e dimë se ty të zemëron ajo që thonë ata. Në të vërtetë, nuk je ti ai që po mohojnë ata, por keqbërësit po mohojnë shpalljet e Allahut.');
INSERT INTO "hasan_efendi" VALUES(6,34,'6:34','Edhe para teje (o Muhamed!), shumë të dërguar janë mohuar, por ata e kanë duruar mohimin dhe lëndimin (që kanë pësuar), derisa u ka ardhur ndihma Jonë, sepse askush nuk mund t’i ndryshojë vendimet e Allahut. Ti tashmë ke dëgjuar për ata të dërguar.');
INSERT INTO "hasan_efendi" VALUES(6,35,'6:35','Nëse ty të duket e rëndë për t’u mbajtur përbuzja e tyre, atëherë kërko – nëse mundesh – një zgavër në tokë ose shkallë në qiell, që t’u sjellësh atyre një mrekulli! Sikur Allahu të donte, do t’i kishte mbledhur ata në rrugë të mbarë, prandaj mos u bëj nga ata që nuk e dinë këtë!');
INSERT INTO "hasan_efendi" VALUES(6,36,'6:36','Do të të përgjigjen vetëm ata që dëgjojnë. Sa për të vdekurit, Allahu do t’i ngjallë ata, pastaj të gjithë do të kthehen tek Ai.');
INSERT INTO "hasan_efendi" VALUES(6,37,'6:37','Ata thonë: “Ah! Pse nuk ia zbret atij ndonjë mrekulli Zoti i tij?” Thuaj: “Allahu është i Fuqishëm të dërgojë mrekulli, por shumica e tyre nuk e di”.');
INSERT INTO "hasan_efendi" VALUES(6,38,'6:38','Të gjitha kafshët në Tokë e të gjithë shpendët që fluturojnë me krahët e veta, janë në bashkësi të ngjashme me ju. Ne nuk kemi harruar asgjë në Librin Tonë. Pastaj të gjithë do të tubohen para Zotit të tyre.');
INSERT INTO "hasan_efendi" VALUES(6,39,'6:39','Ata që i mohojnë shpalljet Tona janë shurdhë e memecë, janë në errësirë. Allahu shpie në humbje kë të dojë dhe udhëzon në rrugë të drejtë kë të dojë.');
INSERT INTO "hasan_efendi" VALUES(6,40,'6:40','Thuaj: “Mendoni: nëse ju godet dënimi i Allahut apo ju vjen Ora e Kiametit, a do të thërrisnit për ndihmë dikë tjetër, përveç Allahut?! Më tregoni, nëse thoni të vërtetën!”');
INSERT INTO "hasan_efendi" VALUES(6,41,'6:41','Përkundrazi! Ju do të thërrisnit vetëm Atë! Nëse do të dojë, Ai do t’ju shpëtojë nga ajo për të cilën e thërrisnit dhe ju do t’i harronit idhujt që ia shoqëronit Atij (në adhurim).');
INSERT INTO "hasan_efendi" VALUES(6,42,'6:42','Ne iu kemi dërguar shumë popujve profetë para teje dhe i kemi dënuar ata me skamje dhe fatkeqësi, për t’u bërë të dëgjueshëm.');
INSERT INTO "hasan_efendi" VALUES(6,43,'6:43','Vetëm sikur të përuleshin kur u erdhi ndëshkimi Ynë! Por zemrat e tyre u bënë gur, ndërsa djalli ua paraqiste si të bukura veprat e tyre të këqija.');
INSERT INTO "hasan_efendi" VALUES(6,44,'6:44','Kur ata i harruan paralajmërimet Tona, Ne ua hapëm dyert e çdo mirësie. Por, sapo po gëzoheshin me atë që u dhamë, Ne i dënuam befasisht dhe atëherë ata u zhytën në dëshpërim.');
INSERT INTO "hasan_efendi" VALUES(6,45,'6:45','Kështu humbi gjurma e atij populli keqbërës. Lavdërimi qoftë i Allahut, Zotit të gjithësisë.');
INSERT INTO "hasan_efendi" VALUES(6,46,'6:46','Thuaj: “Më thoni: Sikur Allahu t’jua merrte dëgjimin dhe shikimin e t’jua vuloste zemrat, cili zot, përveç Allahut, do t’jua kthente ato?!” Shih si ua shpjegojmë shpalljet Tona, por ata përsëri largohen nga e vërteta.');
INSERT INTO "hasan_efendi" VALUES(6,47,'6:47','Thuaj: “Më thoni, nëse ju vjen dënimi i Allahut papritmas ose sheshazi, a do të zhdukej kush përveç popullit keqbërës?”');
INSERT INTO "hasan_efendi" VALUES(6,48,'6:48','Ne i çojmë të dërguarit vetëm si sjellës të lajmeve të mira dhe qortues. Pra kushdo që i beson ata dhe bën mirë, nuk do të frikësohet e as nuk do të pikëllohet.');
INSERT INTO "hasan_efendi" VALUES(6,49,'6:49','Kurse ata që mohojnë shpalljet tona, do t’i godasë dënimi për shkak të mohimit.');
INSERT INTO "hasan_efendi" VALUES(6,50,'6:50','Thuaj: “Unë nuk ju them se zotëroj thesaret e Allahut apo se e di të padukshmen dhe as nuk them se jam engjëll. Unë ndjek vetëm atë që më shpallet”. Thuaj: “A barazohet i verbri me atë që sheh? A s’po mendoni?”');
INSERT INTO "hasan_efendi" VALUES(6,51,'6:51','Paralajmëroji (me Kuran) ata që i frikësohen daljes para Zotit të tyre, se atëherë nuk do të kenë as mbrojtës, as ndihmës tjetër përveç Tij, me qëllim që të ruhen nga gjynahet.');
INSERT INTO "hasan_efendi" VALUES(6,52,'6:52','Mos i dëbo ata që i luten Zotit të vet në mëngjes e mbrëmje, duke e dëshiruar Fytyrën (mirësinë) e Tij! Ti nuk je aspak përgjegjës për ata e as ata nuk janë përgjegjës për ty. Nëse ti i dëbon ata, atëherë do të jesh keqbërës.');
INSERT INTO "hasan_efendi" VALUES(6,53,'6:53','Kështu, Ne i kemi bërë disa prej tyre si sprovë për të tjerët, që të thonë: “Vallë, këta janë ata prej nesh, që Allahu u ka dhuruar mirësi?!” - A mos vallë, Allahu nuk i njeh më mirë ata që i janë mirënjohës?');
INSERT INTO "hasan_efendi" VALUES(6,54,'6:54','Kur të vijnë te ti ata që besojnë në shpalljet Tona, thuaj: “Paqja qoftë mbi ju!”. Zoti juaj ia ka bërë detyrim Vetes mëshirën. Nëse dikush nga ju bën ndonjë vepër të keqe pa e ditur, e pastaj pendohet e përmirësohet, ai do ta gjejë Allahun Falës dhe Mëshirues.');
INSERT INTO "hasan_efendi" VALUES(6,55,'6:55','Ne kështu i shpjegojmë hollësisht shpalljet, në mënyrë që të qartësohet rruga e njerëzve të këqij.');
INSERT INTO "hasan_efendi" VALUES(6,56,'6:56','Thuaj: “Mua më është ndaluar t’i adhuroj ata, të cilëve ju u luteni në vend të Allahut “. Thuaj: “Unë nuk i ndjek dëshirat tuaja të kota, sepse atëherë do të isha i humbur dhe nuk do të isha nga ata që janë në rrugë të drejtë”.');
INSERT INTO "hasan_efendi" VALUES(6,57,'6:57','Thuaj: “Unë kam provë të qartë nga Zoti im, por ju e përgënjeshtroni atë. Ajo që ju kërkoni me nxitim, nuk është në pushtetin tim. Vendimi i takon vetëm Allahut. Ai e shpall të vërtetën dhe Ai është Gjykatësi më i mirë”.');
INSERT INTO "hasan_efendi" VALUES(6,58,'6:58','Thuaj: “Sikur ta kisha në dorë atë që kërkoni ju, shpejt e shpejt do të sqarohej puna midis meje dhe jush." Allahu i di më së miri keqbërësit.');
INSERT INTO "hasan_efendi" VALUES(6,59,'6:59','Ai i ka çelësat e së padukshmes dhe vetëm Ai i njeh ato. Ai e di ç’ka në tokë dhe në det; asnjë gjeth nuk bie, pa e ditur Ai dhe nuk ekziston asnjë kokërr në errësirën e tokës, as e njomë, as e thatë, që të mos jetë shënuar në Librin e qartë (Leuhi Mahfudh).');
INSERT INTO "hasan_efendi" VALUES(6,60,'6:60','Ai jua merr shpirtrat (ju vë në gjumë) natën, duke e ditur ç’keni bërë ditën, pastaj ju zgjon që të plotësoni kohën tuaj të caktuar për të jetuar. Në fund, tek Ai do të ktheheni dhe Ai do t’ju tregojë gjithçka që keni bërë.');
INSERT INTO "hasan_efendi" VALUES(6,61,'6:61','Ai është Sunduesi mbi robërit e Vet. Ai ju dërgon rojtarë (engjëj) të cilët, kur ndonjërit nga ju i vjen vdekja, ia marrin shpirtin, duke mos bërë kurrfarë lëshimi.');
INSERT INTO "hasan_efendi" VALUES(6,62,'6:62','Pastaj të gjithë do të kthehen tek Allahu, Zoti tyre i vërtetë. Vetëm Atij i takon të gjykojë dhe Ai është më i shpejtë në llogari.');
INSERT INTO "hasan_efendi" VALUES(6,63,'6:63','Thuaju: “Kush ju shpëton nga errësira e tokës dhe e detit, kur i luteni Atij haptazi dhe fshehurazi (duke thënë): “Nëse Ai na shpëton prej këtyre (rreziqeve), vërtet do të bëhemi mirënjohës”.');
INSERT INTO "hasan_efendi" VALUES(6,64,'6:64','Thuaj: “Allahu ju shpëton nga ato dhe nga çdo e keqe tjetër, por prapëseprapë ju adhuroni të tjerë veç Tij”.');
INSERT INTO "hasan_efendi" VALUES(6,65,'6:65','Thuaj: “Ai ka fuqi t’ju ndëshkojë prej së larti ose nga poshtë këmbëve tuaja, ose t’ju përçajë në grupe e të vuani nga duart e njëri tjetrit." Shih si ua shpjegojmë shpalljet, që ata të mund të kuptojnë!');
INSERT INTO "hasan_efendi" VALUES(6,66,'6:66','Por populli yt e quan të gënjeshtërt atë (Kuranin), ndonëse ai është i vërtetë. Thuaj: “Unë nuk jam rojtari juaj.');
INSERT INTO "hasan_efendi" VALUES(6,67,'6:67','Do të vijë koha, kur çdo profeci do të përmbushet dhe ju do ta merrni vesh.”');
INSERT INTO "hasan_efendi" VALUES(6,68,'6:68','Kur t’i shohësh ata të nënçmojnë shpalljet Tona, largohu prej tyre, derisa të kalojnë në bisedë tjetër! E, nëse djalli të bën të harrosh, sapo të përmendesh, mos rri me keqbërësit!');
INSERT INTO "hasan_efendi" VALUES(6,69,'6:69','Ata që i frikësohen Allahut, s’kanë kurrfarë përgjegjësie për gjynahet e mohuesve, por ua kujtojmë vetëm që të ruhen nga e keqja.');
INSERT INTO "hasan_efendi" VALUES(6,70,'6:70','Largohuni nga ata që fenë e tyre e marrin si lojë dhe dëfrim e që janë mashtruar nga jeta e kësaj bote. Këshilloji ata me Kuranin, me qëllim që të mos i humbasin shpirtrat e tyre për shkak të veprave të veta. Përveç Allahut, ata nuk do të kenë as ndihmës, as ndërmjetës. Edhe sikur të ofrojnë çdo lloj shpagimi, nuk do t’u pranohet. Këta janë ata që do të lihen në humbje për shkak të veprave që kanë bërë. Ata do të pinë ujë të valuar dhe do të kenë dënim të dhembshëm për shkak të mosbesimit të tyre.');
INSERT INTO "hasan_efendi" VALUES(6,71,'6:71','Thuaj: “A të adhurojmë në vend të Allahut çfarë nuk na sjell as dobi, as dëm?! A të kthehemi mbrapsht, pasi Allahu na udhëzoi, duke u bërë si i magjepsuri nga djajtë, që sillet i çoroditur nëpër tokë, ndonëse shokët e thërrasin në rrugë të drejtë: “Eja tek ne”?! Thuaj: “Udhëzimi i Allahut është i vetmi udhëzim i drejtë! Ne jemi të urdhëruar t’i nënshtrohemi Zotit të botëve,');
INSERT INTO "hasan_efendi" VALUES(6,72,'6:72','të falim namazin dhe ta kemi frikë Atë.” Dhe është Ai, para të Cilit do të tuboheni.');
INSERT INTO "hasan_efendi" VALUES(6,73,'6:73','Është Ai që i ka krijuar qiejt dhe Tokën për një qëllim. Ditën kur Ai thotë “Bëhu”, ajo bëhet. Fjala e Tij është e Vërteta dhe Atij i përket pushteti Ditën kur fryhet në Sur . Ai e di të padukshmen dhe të dukshmen dhe Ai është i Urti dhe i Gjithëdijshmi”.');
INSERT INTO "hasan_efendi" VALUES(6,74,'6:74','Trego për Ibrahimin, kur ai i tha të atit, Azerit: “A do t’i adhurosh idhujt si zotat e tu?! Unë vërtet po shoh se ti dhe populli yt jeni në humbje të madhe”.');
INSERT INTO "hasan_efendi" VALUES(6,75,'6:75','Dhe kështu, Ne ia treguam Ibrahimit mbretërinë e qiejve dhe të Tokës, që ai të bëhej nga ata që besojnë vendosmërisht.');
INSERT INTO "hasan_efendi" VALUES(6,76,'6:76','Kur ra nata, ai pa një yll dhe tha: “Ky është Zoti im!” Por, kur perëndoi ylli, ai tha: “Nuk i dua ata që perëndojnë”.');
INSERT INTO "hasan_efendi" VALUES(6,77,'6:77','Kur pa Hënën duke lindur, tha: “Ky është Zoti im!” Por, pasi ajo perëndoi, ai tha: “Nëse Zoti im nuk më udhëzon në rrugën e mbarë, me siguri do të bëhem njëri nga të humburit”.');
INSERT INTO "hasan_efendi" VALUES(6,78,'6:78','Kur vërejti Diellin duke lindur, ai thërriti: “Ky është Zoti im, ky është më i madhi!” Por, pasi dielli perëndoi, ai tha: “O populli im, unë jam larg nga idhujt që ju ia shoqëroni Allahut në adhurim!');
INSERT INTO "hasan_efendi" VALUES(6,79,'6:79','Unë e kthej fytyrën time nga Ai që i ka krijuar qiejt dhe Tokën, duke qenë besimtar i vërtetë, dhe nuk jam nga idhujtarët”!');
INSERT INTO "hasan_efendi" VALUES(6,80,'6:80','Dhe, kur populli i tij polemizoi me të, ai tha: “Doni të polemizoni me mua për Allahun, ndërkohë që Ai më ka udhëzuar në rrugën e drejtë?! Unë nuk i kam frikë idhujt tuaj, sepse vetëm po të dojë Zoti im, ata mund të më lëndojnë. Zoti im, me dijeninë e vet ka përfshirë çdo gjë. A nuk po mendoni?');
INSERT INTO "hasan_efendi" VALUES(6,81,'6:81','E si të frikësohem nga zotat tuaj të rremë, ndërkohë që ju nuk keni frikë t’i shoqëroni Allahut idhuj, për të cilët Ai nuk ju ka zbritur kurrfarë argumenti?! Cili nga ne e meriton të jetë i sigurt? Më thoni, nëse njihni të vërtetën!');
INSERT INTO "hasan_efendi" VALUES(6,82,'6:82','Sa për ata që besojnë dhe nuk e molepsin besimin e tyre me padrejtësi (idhujtari), pikërisht atyre u takon siguria dhe ata janë të udhëzuar.”');
INSERT INTO "hasan_efendi" VALUES(6,83,'6:83','Ky është argumenti Ynë, të cilin Ne ia dhamë Ibrahimit kundër popullit të tij. Ne ngremë kë të duam në shkallë të lartë. Vërtet, Zoti yt është i Urtë e i Gjithëdijshëm.');
INSERT INTO "hasan_efendi" VALUES(6,84,'6:84','Ne i dhuruam atij Is''hakun dhe Jakubin. Që të dy këta i udhëzuam në rrugë të drejtë, ashtu siç patëm udhëzuar më parë Nuhun. Ndër pasardhësit e tij Ne udhëzuam Davudin, Sulejmanin, Ejubin, Jusufin, Musain dhe Harunin. Kështu i shpërblejmë punëmirët.');
INSERT INTO "hasan_efendi" VALUES(6,85,'6:85','Ne udhëzuam edhe Zekerijan, Jahjain, Isain, Iljasin - të gjithë këta kanë qenë nga njerëzit e mirë.');
INSERT INTO "hasan_efendi" VALUES(6,86,'6:86','Po ashtu, udhëzuam edhe Ismailin, Eljesain, Junusin dhe Lutin - secilin prej tyre e ngritëm mbi të gjithë njerëzit.');
INSERT INTO "hasan_efendi" VALUES(6,87,'6:87','Ne zgjodhëm dhe udhëzuam në rrugë të drejtë edhe disa nga paraardhësit, pasardhësit dhe vëllezërit e tyre.');
INSERT INTO "hasan_efendi" VALUES(6,88,'6:88','Ky është udhëzimi i Allahut, të cilin Ai ia jep kujt të dojë nga robërit e Vet. Por, nëse ata adhurojnë zota të tjerë, do t’u humbë çdo gjë nga ajo që kanë bërë.');
INSERT INTO "hasan_efendi" VALUES(6,89,'6:89','Këta janë ata që u kemi dhënë Librin, Urtësinë dhe Profecinë. E, nëse ata (banorët e Mekës) i mohojnë këto, Ne do t’ia besojmë një populli tjetër që nuk do t’i mohojë.');
INSERT INTO "hasan_efendi" VALUES(6,90,'6:90','Këta janë ata, të cilët Allahu i ka udhëzuar në rrugën e drejtë, andaj edhe ti (o Muhamed) ndiq rrugën e tyre. Thuaj: “Unë nuk kërkoj nga ju kurrfarë shpërblimi për atë. Ai është vetëm këshillë për të gjithë botët (njerëzit dhe xhindet)”.');
INSERT INTO "hasan_efendi" VALUES(6,91,'6:91','Ata nuk e vlerësojnë siç duhet Allahun, kur thonë: “Allahu nuk i ka zbritur asgjë njeriut” . Thuaj: “Atëherë, kush e shpalli Librin që solli Musai si dritë dhe udhërrëfyes për njerëzit?! Librin që ju e keni shpërndarë në fletë, ca prej të cilave i tregoni, ndërsa shumicën e fshihni, ndonëse tani keni mësuar diçka që as ju, as etërit tuaj nuk e keni ditur më parë?! Thuaj: “Allahu e ka shpallur (atë Libër)!” Pastaj lëri ata që të argëtohen me gënjeshtrat e tyre.');
INSERT INTO "hasan_efendi" VALUES(6,92,'6:92','Ky (Kuran) është një Libër i bekuar, të cilin ta kemi shpallur ty (o Muhamed), si vërtetues të atij (Teuratit) që gjendet para tij dhe që të paralajmërosh popullin e nënës së qyteteve (Mekës) dhe ata që banojnë rreth saj. Ata që besojnë në jetën e ardhshme, besojnë edhe në të (Kuran) dhe kujdesen për kryerjen e namazit të tyre.');
INSERT INTO "hasan_efendi" VALUES(6,93,'6:93','Kush është keqbërës më i madh se ai që trillon gënjeshtra për Allahun ose që thotë: “Kjo më është shpallur mua”, ndërkohë që asgjë nuk i është shpallur atij? Ose që thotë: “Unë mund të shpall diçka si ajo që ka shpallur Allahu.” Ah sikur t’i shihje keqbërësit gjatë agonisë së vdekjes, kur engjëjt zgjasin duart (duke thënë): “Dorëzoni shpirtrat tuaj! Sot ju do të ndëshkoheni me dënim poshtërues për shkak të të pavërtetave që keni thënë për Allahun dhe që keni shpërfillur shpalljet e Tij”.');
INSERT INTO "hasan_efendi" VALUES(6,94,'6:94','(Allahu do t’u thotë): “Tashmë na vini veç e veç, ashtu siç ju krijuam herën e parë, duke lënë pas vetes ato që ju patëm dhuruar. Ne nuk po i shohim me ju ndërmjetësuesit tuaj, për të cilët pretendonit se ishin ortakët e Allahut. Midis jush janë shkëputur të gjitha lidhjet dhe ajo tek e cila shpresonit, ju ka lënë në baltë”.');
INSERT INTO "hasan_efendi" VALUES(6,95,'6:95','Me të vërtetë, Allahu është Ai që farën dhe bërthamën e bën të mugullojë. Ai e nxjerr të gjallën prej të vdekurës dhe të vdekurën prej të gjallës. Ky është Allahu. Atëherë, përse i largoheni Atij?');
INSERT INTO "hasan_efendi" VALUES(6,96,'6:96','Ai bën të feksë drita e agimit. Ai e ka bërë natën për pushim, ndërsa Diellin dhe Hënën për të njehsuar kohën. Kjo është urdhëresa e Allahut, të Plotfuqishmit dhe të Gjithëdijshmit.');
INSERT INTO "hasan_efendi" VALUES(6,97,'6:97','Është Ai që i ka krijuar yjet, për t’ju rrëfyer rrugën në errësirat e tokës dhe të detit. Ne ua kemi shpjeguar qartë shpalljet Tona njerëzve që dinë.');
INSERT INTO "hasan_efendi" VALUES(6,98,'6:98','Është Ai që ju ka krijuar prej një njeriu të vetëm, që të jetoni dhe të preheni në tokë. Ne ua kemi shpjeguar qartë shpalljet Tona njerëzve që kuptojnë.');
INSERT INTO "hasan_efendi" VALUES(6,99,'6:99','Është Ai që lëshon nga qielli ujë, nëpërmjet të cilit Ai nxjerr sythat e çdo bime. Prej kësaj Ne nxjerrim gjelbërim, kokrra gruri në kallinj, palma hurme të ngarkuara me kalaveshë të ulët, kopshte rrushi, ulliri dhe shege, që ngjajnë, por që janë të ndryshëm nga njëri-tjetri. Vështroni frytet e tyre kur formohen dhe piqen. Sigurisht që në këto ka shenja për njerëzit që besojnë.');
INSERT INTO "hasan_efendi" VALUES(6,100,'6:100','Idhujtarët i shohin xhindet si ortakë të Allahut, ndërkohë që Ai i ka krijuar ata. Me padituri, i kanë veshur Atij djem e vajza. I lavdëruar qoftë Ai dhe i lartësuar mbi gjithçka që ia veshin ata!');
INSERT INTO "hasan_efendi" VALUES(6,101,'6:101','Ai është Krijuesi i qiejve dhe i Tokës! E si mund të ketë Ai fëmijë, ndërkohë që nuk ka bashkëshorte?! Ai ka krijuar çdo gjë dhe është i Dijshëm për çdo gjë.');
INSERT INTO "hasan_efendi" VALUES(6,102,'6:102','Ky është Allahu, Zoti juaj. S’ka zot tjetër të vërtetë përveç Tij, Krijuesit të çdo gjëje, andaj adhuroni vetëm Atë! Ai është Mbikëqyrës i çdo gjëje!');
INSERT INTO "hasan_efendi" VALUES(6,103,'6:103','Shikimet njerëzore nuk mund ta arrijnë Atë, ndërkohë që Ai i arrin shikimet e të gjithëve. Ai është Bamirës i pakufi dhe di çdo gjë.');
INSERT INTO "hasan_efendi" VALUES(6,104,'6:104','(Thuaju o Muhamed): “Tashmë ju kanë ardhur nga Zoti juaj prova të qarta. Ai që i sheh ato, i sjell dobi vetes, ndërsa ai që i mbyll sytë para tyre, i bën dëm vetes. Unë nuk jam rojtari juaj.');
INSERT INTO "hasan_efendi" VALUES(6,105,'6:105','Kështu, Ne i shpjegojmë shpalljet Tona në mënyra të ndryshme, që ata të thonë: “Ti i ke mësuar këto!” dhe që t’ua bëjmë të qarta njerëzve që dinë (të drejtën).');
INSERT INTO "hasan_efendi" VALUES(6,106,'6:106','Prandaj ndiq atë që të ka shpallur Zoti yt. S’ka zot të vërtetë veç Tij. Shmangu prej idhujtarëve.');
INSERT INTO "hasan_efendi" VALUES(6,107,'6:107','Sikur të donte Allahu, ata nuk do të ishin idhujtarë. Ne nuk të kemi bërë ty rojtar të tyre e as që je përgjegjës për ta.');
INSERT INTO "hasan_efendi" VALUES(6,108,'6:108','Mos i shani idhujt që ata i adhurojnë në vend të Allahut, në mënyrë që ata të mos e shajnë Allahun armiqësisht nga padija. Kështu, Ne ia paraqesim të bukura çdo populli veprat e veta. Pastaj, ata do të kthehen te Zoti i tyre, i Cili do t’i lajmërojë për gjithçka që kanë bërë.');
INSERT INTO "hasan_efendi" VALUES(6,109,'6:109','Ata betohen solemnisht në Allahun se, nëse iu vjen një mrekulli, ata me siguri do të besojnë. Thuaj: “Mrekullitë janë vetëm te Allahu!” E kush ju siguron se ata, kur t’u vijë një mrekulli, do të besojnë?');
INSERT INTO "hasan_efendi" VALUES(6,110,'6:110','Ne do t’ua largojmë zemrat dhe shikimet e tyre nga e vërteta, sepse ata nuk e besuan atë (Kuranin) herën e parë. Ne do t’i lëmë ata që të bredhin të verbër në humbje e mosbesim.');
INSERT INTO "hasan_efendi" VALUES(6,111,'6:111','Edhe sikur t’u dërgonim atyre engjëjt, t’i bënim të vdekurit t’u flisnin atyre dhe t’i tubonin të gjitha gjërat para tyre, ata përsëri nuk do të besonin, nëse Allahu nuk do të donte; por shumica e tyre janë të paditur.');
INSERT INTO "hasan_efendi" VALUES(6,112,'6:112','Kështu, çdo profeti Ne i kemi caktuar armiq djajtë e njerëzve dhe të xhindeve, të cilët e frymëzojnë njëri-tjetrin me fjalë boshe të zbukuruara për mashtrim. Por, sikur të donte Zoti yt, ata nuk do të bënin kështu, prandaj lëri ata dhe trillimet e tyre.');
INSERT INTO "hasan_efendi" VALUES(6,113,'6:113','Lëri zemrat e atyre që nuk besojnë në Ditën e Kiametit, që të priren nga mashtrimi dhe të kënaqen me të e le të bëjnë gjynahet që kanë bërë.');
INSERT INTO "hasan_efendi" VALUES(6,114,'6:114','Thuaj: “Vallë, a të kërkoj tjetër gjykatës përveç Allahut, kur është Ai që ju ka zbritur këtë Libër të hollësishëm?!” Ata që Ne u kemi dhënë Shkrimet e shenjta, e dinë se Kurani është me të vërtetë i zbritur nga Zoti yt. Prandaj, kurrsesi mos u bëj nga ata që dyshojnë!');
INSERT INTO "hasan_efendi" VALUES(6,115,'6:115','Të përkryera janë fjalët e Zotit tënd në vërtetësi dhe drejtësi; fjalët e Tij askush nuk mund t’i ndryshojë. Ai dëgjon çdo gjë dhe di çdo gjë.');
INSERT INTO "hasan_efendi" VALUES(6,116,'6:116','Nëse i bindesh shumicës së njerëzve që gjenden në tokë, ata do të të shmangin nga rruga e Allahut. Ata ndjekin vetëm hamendjet. Ata vetëm gënjejnë.');
INSERT INTO "hasan_efendi" VALUES(6,117,'6:117','Në të vërtetë, Zoti yt i njeh më së miri ata që i shmangen rrugës së Tij dhe i di më së miri ata që janë në rrugë të drejtë.');
INSERT INTO "hasan_efendi" VALUES(6,118,'6:118','Hani vetëm atë mish, gjatë therjes së të cilit është përmendur emri i Allahut, nëse vërtet besoni në shpalljet e Tij.');
INSERT INTO "hasan_efendi" VALUES(6,119,'6:119','E përse të mos e hani atë që është therur me emrin e Allahut, kur Ai tashmë jua ka shpjeguar ç’është e ndaluar, përveçse kur jeni në shtrëngesë?! Shumë njerëz i largojnë të tjerët nga rruga e drejtë nga padija dhe nga dëshirat e tyre të kota. Në të vërtetë, Zoti yt i njeh më së miri ata që e kanë kaluar kufirin.');
INSERT INTO "hasan_efendi" VALUES(6,120,'6:120','Largohuni nga gjynahet e hapëta dhe të fshehta, sepse, ata që kanë bërë gjynahe, patjetër që do të ndëshkohen për atë që kanë vepruar.');
INSERT INTO "hasan_efendi" VALUES(6,121,'6:121','Mos hani asnjë mish që nuk është therur me emrin e Allahut, sepse kjo është gjynah! Në të vërtetë, djajtë i nxisin miqtë e vet që t’ju kundërshtojnë ju. E nëse ju u bindeni atyre, me siguri që edhe ju do të ishit idhujtarë.');
INSERT INTO "hasan_efendi" VALUES(6,122,'6:122','A barazohet i vdekuri që Ne e ngjallëm dhe i dhamë dritë me të cilën ecën midis njerëzve, me atë që është në errësirë e nuk mund të dalë prej saj?! Kësisoj, jobesimtarëve u duken të bukura veprat që bëjnë.');
INSERT INTO "hasan_efendi" VALUES(6,123,'6:123','Kështu, Ne kemi bërë në çdo qytet që keqbërësit më të mëdhenj të ngrenë kurthe. Mirëpo, kurthet ia ngrenë vetëm vetvetes e nuk e ndiejnë.');
INSERT INTO "hasan_efendi" VALUES(6,124,'6:124','Kur atyre u vjen ndonjë mrekulli, thonë: “Ne nuk do të besojmë derisa të mos na jepet diçka, si ajo që u është dhënë të dërguarve të Allahut.” Allahu e di më mirë se kujt ia jep mesazhin e Vet. Atyre që kanë bërë gjynahe, do t’u vijë një poshtërim nga ana e Allahut dhe ndëshkim i ashpër për shkak të kurtheve që ngrinin.');
INSERT INTO "hasan_efendi" VALUES(6,125,'6:125','Allahu ia hap zemrën për (të pranuar) Islamin atij që dëshiron ta udhëzojë, ndërsa atij që dëshiron ta lërë në humbje, (ia mbyll zemrën e) ia shtrëngon gjoksin si të jetë duke u ngjitur në qiell. Kështu, Allahu, i ndëshkon ata që nuk besojnë.');
INSERT INTO "hasan_efendi" VALUES(6,126,'6:126','Kjo është rruga e drejtë e Zotit tënd. Ne ua kemi shpjeguar shpalljet Tona njerëzve që ua vënë veshin.');
INSERT INTO "hasan_efendi" VALUES(6,127,'6:127','Ata do të kenë Shtëpinë e shpëtimit te Zoti i tyre. Ai do të jetë Mbrojtësi i tyre, si shpërblim për veprat që kanë bërë.');
INSERT INTO "hasan_efendi" VALUES(6,128,'6:128','Ditën kur Ai do t’i tubojë të gjithë, do të thotë: “O tog i xhindeve! Ju keni mashtruar shumë njerëz!” - Ithtarët e tyre midis njerëzve do të thonë: “O Zoti ynë! Ne u kënaqëm me njëri tjetrin dhe arritëm në afatin tonë, të cilin na e pate caktuar Ti”. Allahu do të thotë: “Zjarri është vendbanimi juaj. Atje do të qëndroni përherë, veç nëse Allahu dëshiron ndryshe. Zoti yt është vërtet i Urtë e i Gjithëdijshëm.');
INSERT INTO "hasan_efendi" VALUES(6,129,'6:129','E kështu, Ne u japim pushtet disa keqbërësve mbi të tjerët, si dënim për veprat e tyre të këqija.');
INSERT INTO "hasan_efendi" VALUES(6,130,'6:130','“O tog i xhindeve dhe i njerëzve, a nuk ju patën ardhur të dërguar nga mesi juaj, për t’ju treguar shpalljet Tona dhe për t’ju paralajmëruar takimin me këtë ditë?” Ata do të thonë: “Ne dëshmojmë kundër vetes sonë”. Vërtet, jeta e kësaj bote i mashtroi ata, kështu që ata do të dëshmojnë kundër vetes se kanë qenë mohues.');
INSERT INTO "hasan_efendi" VALUES(6,131,'6:131','Kjo ndodh sepse Zoti yt nuk e shkatërron asnjë vend për veprat e tyre të gabuara, pa i paralajmëruar banorët e tij.');
INSERT INTO "hasan_efendi" VALUES(6,132,'6:132','Secili do të ketë shkallën e tij, sipas veprave që ka bërë. Zoti yt nuk është i paditur për veprat e tyre.');
INSERT INTO "hasan_efendi" VALUES(6,133,'6:133','Zoti yt është i Vetëmjaftueshmi dhe Zotëruesi i mëshirës. Nëse do, ju zhduk dhe sjell pas jush çfarë të dojë, ashtu si ju ka krijuar nga pasardhësit e popujve të tjerë.');
INSERT INTO "hasan_efendi" VALUES(6,134,'6:134','Ajo që ju është premtuar, me të vërtetë do t’ju arrijë e ju nuk mund t’i ikni asaj.');
INSERT INTO "hasan_efendi" VALUES(6,135,'6:135','Thuaj: “O populli im, punoni në drejtimin tuaj, ndërkohë që edhe unë bëj timen. Së shpejti do ta merrni vesh se për kë do të jetë fundi i mirë. Njëmend, keqbërësit nuk do të shpëtojnë.');
INSERT INTO "hasan_efendi" VALUES(6,136,'6:136','Ata kanë caktuar për Allahun pjesë nga drithi dhe kafshët shtëpiake, të cilat i ka krijuar Ai dhe thonë sipas hamendjes së tyre: “Kjo është për Allahun, ndërsa kjo është për idhujt tanë”. Mirëpo ajo pjesë që është për idhujt e tyre, nuk arrin te Zoti, kurse ajo që ata e kanë caktuar për Allahun - ajo arrin te idhujt e tyre. Sa gjykim të shëmtuar bëjnë ata!');
INSERT INTO "hasan_efendi" VALUES(6,137,'6:137','Shumë paganëve, idhujt e tyre ua kanë bërë t’u duket e bukur vrasja e fëmijëve të tyre, për t’i shkatërruar dhe për t’ua pështjelluar besimin. Sikur të donte Allahu, ata nuk do ta bënin atë. Lërini ata dhe trillimet e tyre.');
INSERT INTO "hasan_efendi" VALUES(6,138,'6:138','Ata thonë sipas hamendjes së tyre: “Këto bagëti shtëpiake dhe këto e këto bimë janë të ndaluara, nuk mund t’i hajë askush, përveç atyre që ua lejojmë ne”. Disa kafshë shtëpiake ata i kanë bërë të ndaluara për ngarkim, ndërsa disa bagëti i therin pa e përmendur emrin e Allahut, duke trilluar gënjeshtra për Të. Ai do t’i ndëshkojë ata për gënjeshtrat e trilluara.');
INSERT INTO "hasan_efendi" VALUES(6,139,'6:139','Ata thonë gjithashtu: “Ajo që gjendet në barkun e këtyre bagëtive, u përket vetëm meshkujve tanë, kurse për gratë tona janë të ndaluara”. Por, nëse lindin të ngordhura, ata të gjithë (burra e gra) janë pjesëmarrës në të”. Allahu do t’i ndëshkojë ata për çfarë i veshin Atij. Ai është vërtet i Urtë dhe i Gjithëdijshëm.');
INSERT INTO "hasan_efendi" VALUES(6,140,'6:140','Të humbur janë ata që i vrasin fëmijët e tyre nga mendjelehtësia dhe padija dhe i bëjnë të ndaluara ato që ua ka dhuruar Allahu, duke trilluar kështu gënjeshtra rreth Allahut. Ata janë të devijuar dhe aspak të udhëzuar.');
INSERT INTO "hasan_efendi" VALUES(6,141,'6:141','Është Ai që ka krijuar kopshte vreshtash të larta dhe të shtruara, hurma dhe bimë, me shije të ndryshme, ullinj e shegë, të ngjashme dhe të ndryshme. Hani frutat e tyre kurdo që të prodhojnë dhe jepni atë që është detyrë në ditën e vjeljes së tyre! Por mos e teproni, se Allahu nuk i do ata që e teprojnë!');
INSERT INTO "hasan_efendi" VALUES(6,142,'6:142','Ai ka krijuar disa bagëti për ngarkim dhe për ushqim. Hani nga ato që jua ka dhuruar Allahu dhe mos ndiqni gjurmët e djallit. Ai është për ju armik i hapur.');
INSERT INTO "hasan_efendi" VALUES(6,143,'6:143','Ai ju ka dhënë tetë lloje bagëtish në çifte. Merr dy llojet për dhentë dhe dy për dhitë. Thuaju : “A i ka ndaluar Allahu dy meshkujt ose dy femrat, ose atë që mbajnë në bark dy femrat?” Lajmëromëni mua me dije të saktë, nëse ajo që thoni është e vërtetë!');
INSERT INTO "hasan_efendi" VALUES(6,144,'6:144','Merr dy llojet për devetë edhe dy për lopët. Thuaju: “A i ka ndaluar Allahu meshkujt apo femrat ose atë që e mbajnë në bark dy femrat? Apo mos keni qenë të pranishëm kur Allahu ju ka urdhëruar për këtë? Atëherë, kush është më keqbërës se ai që shpif gënjeshtra rreth Allahut, për t’i shpënë njerëzit në humbje nga padija? Pa dyshim, Allahu nuk i çon në rrugë të drejtë keqbërësit.');
INSERT INTO "hasan_efendi" VALUES(6,145,'6:145','Thuaj: “Unë nuk gjej në atë që më është shpallur mua asgjë që e ndalon njeriun të hajë çfarë të dojë, përveç kafshëve të ngordhura, gjakut të derdhur, mishit të derrit – se këto janë të ndyta – dhe mishit që është therur në emër të dikujt tjetër në vend të Allahut. Por kushdo që detyrohet nga nevoja të hajë ndonjë gjë prej këtyre, pa dashur të bëjë gjynah dhe pa e kaluar kufirin, nuk do të fajësohet. Vërtet, Allahu është Falës i madh dhe Mëshirëplotë.');
INSERT INTO "hasan_efendi" VALUES(6,146,'6:146','Ne ua ndaluam hebrenjve (të hanin) të gjitha kafshët që kanë kthetra (ose njëthundrakët), ndërsa nga lopët e delet, atyre ua ndaluam dhjamin e brendshëm të tyre, me përjashtim të dhjamit të shpinës ose të zorrëve, ose atë që është i lidhur me eshtra. Me këtë, Ne i ndëshkuam ata për shkak të mosbindjes së tyre. Ne, pa dyshim, themi të vërtetën.');
INSERT INTO "hasan_efendi" VALUES(6,147,'6:147','Prandaj, nëse ata të quajnë gënjeshtar, thuaju: “Zoti juaj është zotërues i mëshirës së gjerë, por ndëshkimi i Tij nuk do të prapësohet nga populli keqbërës”.');
INSERT INTO "hasan_efendi" VALUES(6,148,'6:148','Idhujtarët do të thonë: “Sikur të donte Allahu, nuk do të adhuronim tjetër veç Tij as ne, as baballarët tanë dhe ne nuk do të kishim bërë të ndaluar asgjë”. Kështu mohonin edhe ata para tyre, derisa shijuan dënimin tonë. Thuaju: “A keni ndonjë provë të na paraqitni? Ju mbështeteni vetëm në hamendje dhe jeni vetëm gënjeshtarë”.');
INSERT INTO "hasan_efendi" VALUES(6,149,'6:149','Thuaju: “Allahu ka argumentin më bindës dhe, po të donte Ai, të gjithëve do t’ju përudhte në rrugë të drejtë!”');
INSERT INTO "hasan_efendi" VALUES(6,150,'6:150','Thuaj: “Sillni dëshmitarët tuaj që dëshmojnë se Allahu e ka ndaluar këtë!” E, nëse dëshmojnë (duke gënjyer), ti mos dëshmo me ata. Mos i ndiq dëshirat e kota të atyre që hedhin poshtë shpalljet Tona, nuk besojnë në Botën e përtejme dhe sajojnë zota të tjerë të barabartë me Zotin e tyre!');
INSERT INTO "hasan_efendi" VALUES(6,151,'6:151','Thuaju: “Ejani t’ju lexoj çfarë ju ka urdhëruar Allahu: që të mos i shoqëroni Atij asgjë (në adhurim); që të silleni mirë me prindërit; që të mos vrisni fëmijët tuaj për shkak të varfërisë, sepse Ne ju ushqejmë ju dhe ata; që të mos i afroheni veprave të turpshme, qofshin ato të hapëta apo të fshehta dhe që të mos ia merrni tjetrit jetën, të cilën Allahu e ka shenjtëruar, përveçse kur e kërkon drejtësia (e ligji). Këto ju porosit Ai, me qëllim që të kuptoni.”');
INSERT INTO "hasan_efendi" VALUES(6,152,'6:152','Dhe mos iu qasni pasurisë së jetimit, përveçse për ta rregulluar, derisa të mbërrijë pjekurinë! Kryejeni drejt matjen dhe peshimin; Ne nuk ngarkojmë askënd përtej mundësisë së tij! Kur të flisni, thoni të vërtetën, qoftë edhe për të afërmit tuaj! Plotësojani besën e dhënë Allahut! Këto ju porosit Ai që të mos harroni.');
INSERT INTO "hasan_efendi" VALUES(6,153,'6:153','Kjo është rruga Ime e drejtë; prandaj ndiqeni e mos shkoni rrugëve të tjera që t’ju shmangin nga rruga e Tij. Kjo është ajo që ju porosit Ai për t’u ruajtur nga të këqijat.');
INSERT INTO "hasan_efendi" VALUES(6,154,'6:154','Ne i dhamë Musait Librin si plotësim (të mirësisë Sonë) për atë që vepron mirë, si shpjegim për çdo gjë dhe si udhërrëfyes e mëshirë, që ata (hebrenjtë) të mund të besonin në takimin me Zotin e tyre.');
INSERT INTO "hasan_efendi" VALUES(6,155,'6:155','Ky Libër që kemi shpallur, është i bekuar, prandaj ndiqeni dhe druajuni kundërshtimit, në mënyrë që të mëshiroheni');
INSERT INTO "hasan_efendi" VALUES(6,156,'6:156','dhe që të mos thoni: “Shkrimet e shenjta u janë shpallur vetëm dy sekteve para nesh, ndërsa Ne nuk kemi ditur gjë çfarë mësonin ata”.');
INSERT INTO "hasan_efendi" VALUES(6,157,'6:157','Ose që të mos thoni: “Sikur Shkrimet e shenjta të na ishin shpallur neve, ne do të ishim më të udhëzuar se ata”. Pra, ju ka ardhur nga Zoti juaj provë e qartë, udhërrëfim dhe mëshirë! Atëherë, kush është më keqbërës se ai që i përgënjeshtron shpalljet e Allahut dhe largohet nga ato? Ne, do t’i ndëshkojmë me dënim të rëndë ata që shmangen nga shpalljet Tona, për shkak se u larguan.');
INSERT INTO "hasan_efendi" VALUES(6,158,'6:158','A mos vallë presin ata t’u vijnë engjëjt ose të vijë Zotit yt, ose të vijnë disa shenja të Zotit tënd? Ditën kur të vijnë disa shenja të Zotit tënd, atëherë nuk do t’i bëjë dobi askujt besimi i tij, nëse nuk ka besuar më parë ose nuk ka bërë ndonjë të mirë me besimin e tij! Thuaju: “Pritni, se edhe ne po presim!”.');
INSERT INTO "hasan_efendi" VALUES(6,159,'6:159','Me të vërtetë, ti s’ke të bësh fare me ata që e përçajnë fenë e tyre dhe shndërrohen në sekte. Te Allahu është puna e tyre e pastaj Ai do t’u tregojë çfarë patën punuar.');
INSERT INTO "hasan_efendi" VALUES(6,160,'6:160','Kush bën një vepër të mirë, do të shpërblehet dhjetëfish. Kush bën një vepër të keqe, do të dënohet vetëm sipas gjynahut që ka bërë dhe nuk do t’i bëhet kurrfarë padrejtësie.');
INSERT INTO "hasan_efendi" VALUES(6,161,'6:161','Thuaj: “Me të vërtetë, Zoti im më ka udhëzuar në rrugë të drejtë, në fenë e vërtetë, fenë e pastër të Ibrahimit besimplotë, i cili nuk ka qenë adhurues i idhujve”.');
INSERT INTO "hasan_efendi" VALUES(6,162,'6:162','Thuaj: “Në të vërtetë, namazi im, kurbani im, jeta ime dhe vdekja ime, i përkasin vetëm Allahut, Zotit të botëve.');
INSERT INTO "hasan_efendi" VALUES(6,163,'6:163','Ai nuk ka asnjë ortak. Kështu jam urdhëruar dhe unë jam i pari që i nënshtrohem Atij”.');
INSERT INTO "hasan_efendi" VALUES(6,164,'6:164','Thuaj: “A mos vallë do të kërkoj unë tjetër zot, përveç Allahut, kur Ai është Zoti i gjithçkaje? Kushdo që bën diçka, punon për vete dhe askush nuk ngarkohet me barrën e tjetrit. Pastaj të gjithë do të ktheheni te Zoti juaj dhe Ai do t’ju tregojë për kundërshtitë që kishit mes jush.');
INSERT INTO "hasan_efendi" VALUES(6,165,'6:165','Ai ju ka bërë pasardhës të njëri-tjetrit në Tokë dhe ka ngritur në shkallë disa nga ju mbi të tjerët, për t’ju provuar në atë që ju ka dhënë. Sigurisht që Zoti yt është i shpejtë në dënim, por, në të vërtetë, Ai është edhe Falës e Mëshirëplotë.');
INSERT INTO "hasan_efendi" VALUES(7,1,'7:1','Elif Lâm Mîm Sâd.');
INSERT INTO "hasan_efendi" VALUES(7,2,'7:2','Ky Libër të është shpallur ty; le të mos ketë kurrfarë shtrëngimi në zemrën tënde për shkak të tij! Ai të është shpallur, që nëpërmjet tij të paralajmërosh jobesimtarët dhe të këshillosh besimtarët.');
INSERT INTO "hasan_efendi" VALUES(7,3,'7:3','Ndiqni atë që ju është zbritur nga Zoti juaj e mos ndiqni mbrojtës të tjerë, përveç Tij. Sa pak që i pranoni këshillat!');
INSERT INTO "hasan_efendi" VALUES(7,4,'7:4','Sa shumë qytete kemi rrënuar Ne! Dënimi Ynë u erdhi natën ose ditën kur ata pushonin.');
INSERT INTO "hasan_efendi" VALUES(7,5,'7:5','Kur iu erdhi dënimi, thirrja e tyre e vetme ishte: “Ne kemi qenë vërtet keqbërës!”');
INSERT INTO "hasan_efendi" VALUES(7,6,'7:6','Pa dyshim, Ne do të kërkojmë llogari prej popujve që u kemi çuar të dërguar, e patjetër që do t’i pyesim edhe të dërguarit.');
INSERT INTO "hasan_efendi" VALUES(7,7,'7:7','Dhe do t’jua paraqesim gjithçka që kanë bërë, sepse i dimë plotësisht dhe kemi qenë të pranishëm.');
INSERT INTO "hasan_efendi" VALUES(7,8,'7:8','Peshimi (i veprave) atë ditë do të jetë i drejtë. Ata që u rëndon peshorja me vepra të mira, do të jenë të shpëtuar;');
INSERT INTO "hasan_efendi" VALUES(7,9,'7:9','kurse ata që e kanë të lehtë peshoren me vepra të mira, do të humbin veten e tyre, për shkak se mohuan shpalljet Tona.');
INSERT INTO "hasan_efendi" VALUES(7,10,'7:10','Ne ju kemi vendosur në tokë dhe aty ju kemi dhënë çfarë ju nevojitet për jetesë. Eh, sa pak mirënjohës që jeni!');
INSERT INTO "hasan_efendi" VALUES(7,11,'7:11','Ne ju krijuam e pastaj ju dhamë formën; e u thamë engjëjve: “Përuljuni në sexhde Ademit!” – Të gjithë iu përulën, përveç Iblisit. Ai nuk ishte nga ata që u përulën.');
INSERT INTO "hasan_efendi" VALUES(7,12,'7:12','Allahu tha: “Çfarë të pengoi ty të mos përuleshe, kur të urdhërova?” Ai u përgjigj: “Unë jam më i mirë se ai. Mua më krijove nga zjarri, kurse atë e krijove nga balta”.');
INSERT INTO "hasan_efendi" VALUES(7,13,'7:13','Allahu i tha: “Atëherë, zbrit prej andej (Xhenetit)! Ti nuk ke të drejtë që të krenohesh në të! Shporru, me të vërtetë, ti je prej të poshtëruarve”.');
INSERT INTO "hasan_efendi" VALUES(7,14,'7:14','Iblisi tha: “Më jep afat deri në ditën kur ringjallen njerëzit!”');
INSERT INTO "hasan_efendi" VALUES(7,15,'7:15','Allahu i tha: “Ti je nga ata që u është dhënë afati”.');
INSERT INTO "hasan_efendi" VALUES(7,16,'7:16','Ai tha: “Për shkak se Ti më flake tej, unë do t’u zë pritë njerëzve në rrugën Tënde të drejtë');
INSERT INTO "hasan_efendi" VALUES(7,17,'7:17','e do t’u qasem atyre nga përpara dhe nga mbrapa, nga e djathta dhe nga e majta, e kështu Ti do të vëresh se shumica prej tyre nuk të janë mirënjohës!”');
INSERT INTO "hasan_efendi" VALUES(7,18,'7:18','Allahu i tha: “Dil prej andej i përbuzur dhe i përjashtuar! Pa dyshim, Xhehenemin do ta mbush me ty dhe me të gjithë ata që shkojnë pas teje!”');
INSERT INTO "hasan_efendi" VALUES(7,19,'7:19','Ndërsa Ademit i tha: “O Adem! Bano ti dhe bashkëshortja jote në Xhenet dhe hani ç’të dëshironi, por mos iu afroni kësaj peme, se bëheni keqbërës!”');
INSERT INTO "hasan_efendi" VALUES(7,20,'7:20','Djalli u pëshpëriti, për t’ua zbuluar pjesët e turpshme të mbuluara të trupit të tyre dhe u tha: “Zoti juaj jua ka ndaluar pemën, vetëm që të mos bëheni engjëj ose të pavdekshëm.”');
INSERT INTO "hasan_efendi" VALUES(7,21,'7:21','Dhe iu betua atyre (duke thënë): “Vërtet, Unë jam për ju këshillues i sinqertë!”');
INSERT INTO "hasan_efendi" VALUES(7,22,'7:22','Dhe i mashtroi me dinakërinë e tij. Pasi e shijuan frutën, atyre iu zbuluan vendet e turpshme dhe nisën të mbulohen me gjethet e Xhenetit dhe Zoti i tyre i thirri: “A nuk jua kisha ndaluar atë pemë? A nuk ju pata thënë se djalli është vërtet armiku juaj i hapët?”');
INSERT INTO "hasan_efendi" VALUES(7,23,'7:23','Ata thanë: “O Zoti ynë! Ne e kemi futur veten në gjynah, prandaj, nëse Ti nuk na fal dhe nuk na mëshiron, ne vërtet që do të jemi nga të humburit”.');
INSERT INTO "hasan_efendi" VALUES(7,24,'7:24','Allahu tha: “Zbritni! Ju do të jeni armiq me njëri-tjetrin. Në Tokë do të banoni dhe do të jetoni deri në një kohë të caktuar.');
INSERT INTO "hasan_efendi" VALUES(7,25,'7:25','Aty do të jetoni, aty do të vdisni dhe prej saj do të ringjalleni.”');
INSERT INTO "hasan_efendi" VALUES(7,26,'7:26','O bijtë e Ademit, Ne ju dërguam rroba që të mbuloni vendet e turpshme, si edhe për zbukurim; por, petku i devotshmërisë është më i miri. Këto janë disa shenja të Allahut, që ju të merrni këshillë.');
INSERT INTO "hasan_efendi" VALUES(7,27,'7:27','O bijtë e Ademit, le të mos ju mashtrojë kurrsesi djalli, ashtu si i nxori prindërit tuaj nga Xheneti, duke ua zhveshur rrobat për t’u dukur vendet e turpshme! Ai dhe shpura e tij ju sheh, prej nga ju nuk i shihni ata. Ne i kemi bërë djajtë roje të atyre që nuk besojnë.');
INSERT INTO "hasan_efendi" VALUES(7,28,'7:28','Kur ata bëjnë një vepër të shëmtuar, thonë: “Ne i kemi gjetur prindërit tanë që i bënin, por edhe Allahu na ka urdhëruar kështu”. Thuaj: “Allahu nuk urdhëron të bëhen vepra të pahijshme! Përse thoni për Allahun atë që nuk e dini?”');
INSERT INTO "hasan_efendi" VALUES(7,29,'7:29','Thuaj: “Zoti im urdhëron drejtësi. Drejtohuni vetëm nga Ai në çdo xhami e lutjuni Atij sinqerisht, me besim të pastër e të vërtetë! Siç ju ka krijuar së pari, ashtu do t’ju ringjallë.');
INSERT INTO "hasan_efendi" VALUES(7,30,'7:30','Disa Ai i ka udhëzuar në rrugë të drejtë, kurse disa të tjerë e kanë merituar humbjen. Ata kanë zgjedhur djajtë për mbrojtës në vend të Allahut dhe mendojnë se janë në rrugë të drejtë.');
INSERT INTO "hasan_efendi" VALUES(7,31,'7:31','O bijtë e Ademit, vishuni përshtatshëm kudo dhe kurdo që të faleni! Hani e pini, por mos e teproni, se Ai nuk i do ata që e teprojnë.');
INSERT INTO "hasan_efendi" VALUES(7,32,'7:32','Thuaj: “Kush i ka penguar stolitë dhe ushqimet e këndshme, të cilat Ai i ka krijuar për robërit e Vet?” Thuaj: “Ato janë për besimtarë (dhe të tjerët) në këtë botë, ndërsa në botën tjetër do të jenë vetëm për besimtarët. Kështu ua shpjegojmë shpalljet njerëzve që dinë.');
INSERT INTO "hasan_efendi" VALUES(7,33,'7:33','Thuaj: “Me të vërtetë, Zoti im ka ndaluar vetëm punët e pahijshme, qofshin të hapëta ose të fshehta dhe gjynahet, dhunimin pa të drejtë, t’i shoqërohet Allahut (në adhurim) diçka, për të cilën nuk ju ka dhënë kurrfarë të drejte dhe të thoni për Allahun atë që nuk e dini.');
INSERT INTO "hasan_efendi" VALUES(7,34,'7:34','Çdo popull ka afatin e vet. Kur t’i vijë koha e fundit, ata nuk mund ta shtyjnë, qoftë edhe për një çast dhe as nuk mund ta shpejtojnë.');
INSERT INTO "hasan_efendi" VALUES(7,35,'7:35','O bijtë e Ademit, kur nga gjiri juaj të vijnë të dërguar që ju shpjegojnë shpalljet e Mia, atëherë ata që ruhen nga të këqijat dhe kryejnë vepra të mira, nuk do të frikësohen e as do të pikëllohen.');
INSERT INTO "hasan_efendi" VALUES(7,36,'7:36','Kurse ata që i mohojnë shpalljet e Mia dhe sillen me mendjemadhësi ndaj tyre, do të jenë banorë të zjarrit, ku do të qëndrojnë përgjithmonë.');
INSERT INTO "hasan_efendi" VALUES(7,37,'7:37','Kush është më keqbërës se ai që trillon gënjeshtra kundër Allahut ose që mohon shpalljet e Tij? Atyre do t’u jepet çfarë u është caktuar (në Leuhi Mahfudh). E, kur t’u vijnë të dërguarit Tanë (engjëj), për t’ua marrë shpirtin, do t’u thonë: “Ku janë idhujt që i adhuronit në vend të Allahut?” Ata do të thonë: “Na braktisën.” Kështu ata dëshmojnë kundër vetes që kanë qenë mohues.');
INSERT INTO "hasan_efendi" VALUES(7,38,'7:38','Allahu do t’u thotë: “Hyni në zjarr me popujt - xhindet dhe njerëzit - që kanë qenë para jush”. Kurdo që një popull do të hyjë në zjarr, do të mallkojë të tjerët që kanë hyrë para tij. Dhe, kur të tubohen të gjithë atje, atëherë i fundmi i tyre do t’u thotë të parëve: “O Zoti ynë, këta na kanë sjellë në humbje, prandaj jepu atyre një dënim të dyfishtë zjarri!” Allahu do t’u thotë: “Për secilin (nga ju) ka dënim të dyfishtë, por ju nuk e dini.”');
INSERT INTO "hasan_efendi" VALUES(7,39,'7:39','Të parët do t’u thonë të fundmëve: “Ju nuk keni qenë më të mirë se ne, prandaj shijoni dënimin për atë që keni punuar”.');
INSERT INTO "hasan_efendi" VALUES(7,40,'7:40','Me të vërtetë, atyre që i mohuan shenjat Tona me mendjemadhësi, nuk do t’u hapen dyert e qiellit dhe as që do të hyjnë në Xhenet, derisa të kalojë deveja nëpër vrimë të gjilpërës. Kështu i ndëshkojmë gjynahqarët.');
INSERT INTO "hasan_efendi" VALUES(7,41,'7:41','Për ata janë përgatitur shtretër zjarri dhe sipër tyre do të ketë mbulesa zjarri. Kështu i ndëshkojmë Ne keqbërësit.');
INSERT INTO "hasan_efendi" VALUES(7,42,'7:42','Kurse ata që besojnë dhe kryejnë vepra të mira - Ne nuk ngarkojmë askënd më tepër nga sa ka mundësi - ata janë banorë të Xhenetit, në të cilin do të qëndrojnë përgjithmonë.');
INSERT INTO "hasan_efendi" VALUES(7,43,'7:43','Ne do ta shkulim urrejtjen nga kraharori i tyre. Te këmbët e tyre do të rrjedhin lumenj dhe ata do të thonë: “Qoftë lavdëruar Allahu që na udhëzoi në këtë. Ne nuk do të ishim udhëzuar, sikur të mos na udhëzonte Allahu. Pa dyshim, të dërguarit e Zotit tonë kanë sjellë të vërtetën”. Dhe një zë do t’i thërrasë: “Ky është Xheneti që ju e keni merituar për atë që keni punuar”.');
INSERT INTO "hasan_efendi" VALUES(7,44,'7:44','Dhe banorët e Xhenetit do t’u thonë banorëve të zjarrit: “Ne e gjetëm të vërtetë atë që na premtoi Zoti ynë. Po ju, a e gjetët të vërtetë atë që ju premtoi Zoti juaj?” Ata do të thonë: “Po!” Atëherë, një lajmëtar midis tyre do të thërrasë: “Mallkimi i Allahut është mbi keqbërësit,');
INSERT INTO "hasan_efendi" VALUES(7,45,'7:45','të cilët i kanë penguar të tjerët nga rruga e Allahut, duke dashur ta shtrembërojnë atë dhe nuk kanë besuar në jetën tjetër!”');
INSERT INTO "hasan_efendi" VALUES(7,46,'7:46','Një mur do t’i ndajë ata , ndërsa në lartësitë e tij do të ndodhen njerëz, të cilët i njohin të dyja palët sipas tipareve. Ata do t’u thërrasin banorëve të Xhenetit: “Paqja qoftë mbi ju!” Ata nuk do të kenë hyrë ende në Xhenet, ndonëse shpresojnë.');
INSERT INTO "hasan_efendi" VALUES(7,47,'7:47','E kur t’i kthejnë sytë nga banorët e zjarrit, do të thonë: “O Zoti ynë! Mos na hidh midis këtyre njerëzve të këqij!”');
INSERT INTO "hasan_efendi" VALUES(7,48,'7:48','Pastaj, njerëzit e Murit (A‘râf) do t’u thërrasin disa njerëzve që i njohin nga pamja, duke u thënë: “Nuk ju ndihmoi as grumbullimi i pasurisë suaj, as kryelartësia juaj (ndaj të Vërtetës).');
INSERT INTO "hasan_efendi" VALUES(7,49,'7:49','A janë këta për të cilët u betuat se nuk do t’i arrijë mëshira e Allahut?” Ndërsa atyre do t’u thonë sërish: “Hyni në Xhenet! Për ju s’ka frikë e as pikëllim!”');
INSERT INTO "hasan_efendi" VALUES(7,50,'7:50','Banorët e zjarrit do t’u thërrasin banorëve të Xhenetit: “Derdhni pak ujë te ne ose diçka nga ajo që ju ka dhuruar Allahu!” Ata thonë: “Në të vërtetë, Allahu këto i ka ndaluar për mohuesit,');
INSERT INTO "hasan_efendi" VALUES(7,51,'7:51','të cilët e morën fenë për lojë dhe argëtim dhe u mashtruan nga jeta e kësaj bote”. Ne sot do t’i harrojmë ata, ashtu siç e patën harruar ata takimin e kësaj Dite dhe siç mohuan shpalljet Tona.');
INSERT INTO "hasan_efendi" VALUES(7,52,'7:52','Ne u kemi zbritur atyre një Libër, të cilin e kemi shpjeguar gjerësisht, që të jetë udhërrëfyes dhe mëshirë për njerëzit që besojnë.');
INSERT INTO "hasan_efendi" VALUES(7,53,'7:53','A mos vallë ata presin zbatimin e Tij? Kur t’u vijë dita e zbatimit, do të thonë për atë që e patën mohuar më parë: “Të dërguarit e Zotit tonë na kanë sjellë të vërtetën! A ka ndonjë ndërmjetësues që të ndërhyjë për ne? A mund të kthehemi sërish në jetën e tokës, që të veprojmë ndryshe nga ç’kemi punuar”. Me siguri që ata do t’i humbasin shpirtrat e tyre dhe ajo që trillonin do t’i braktisë.');
INSERT INTO "hasan_efendi" VALUES(7,54,'7:54','Në të vërtetë, Zoti juaj është Allahu, që krijoi qiejt dhe Tokën për gjashtë ditë e pastaj u ngrit mbi Fronin hyjnor (që mbisundon gjithësinë). Ai me errësirën e natës e mbulon ditën, e cila e ndjek atë; Dielli, Hëna dhe yjet i përulen urdhrit të Tij. Vetëm Atij i përket Krijimi dhe Urdhërimi. Qoftë bekuar Allahu, Zoti i botëve!');
INSERT INTO "hasan_efendi" VALUES(7,55,'7:55','Lutjuni Zotit tuaj përulësisht dhe ngadalë! Njëmend, Ai nuk i do ata që e kalojnë kufirin.');
INSERT INTO "hasan_efendi" VALUES(7,56,'7:56','Dhe mos bëni çrregullime në Tokë, pasi është vënë rregulli, ndërsa Atij lutjuni me frikë dhe shpresë. Vërtet, mëshira e Allahut është afër punëmirëve.');
INSERT INTO "hasan_efendi" VALUES(7,57,'7:57','Ai i dërgon erërat si lajmëtare të mëshirës së Tij. Kur erërat lëvizin retë e rënda me shi, Ai i çon ato tek ndonjë vend i vdekur, ku lëshon shi dhe me anë të tij nxjerr lloj-lloj frutash; po kështu do t’i ringjallë edhe të vdekurit. Këta janë shembuj që t’ua vini veshin.');
INSERT INTO "hasan_efendi" VALUES(7,58,'7:58','Toka e mirë i jep frutat me lejen e Zotit të saj, ndërsa toka e keqe, zor se jep gjë. Kështu, në mënyra të ndryshme, Ne ua shpjegojmë shpalljet Tona njerëzve mirënjohës.');
INSERT INTO "hasan_efendi" VALUES(7,59,'7:59','Ne ia dërguam Nuhun popullit të tij e ai u tha: “O populli im! Adhuroni Allahun, ju nuk keni zot tjetër përveç Tij! Unë kam frikë se do të bjerë mbi ju dënimi i një Dite të madhe!”');
INSERT INTO "hasan_efendi" VALUES(7,60,'7:60','Paria e popullit të tij tha: “Ne të shohim ty në humbje të qartë”.');
INSERT INTO "hasan_efendi" VALUES(7,61,'7:61','Ai tha: “O populli im! Unë nuk jam në kurrfarë humbjeje, por jam i dërguar nga ana e Zotit të gjithësisë!');
INSERT INTO "hasan_efendi" VALUES(7,62,'7:62','Unë ju sjell shpalljet e Zotit tim, ju këshilloj dhe di nga Allahu atë që ju nuk e dini.');
INSERT INTO "hasan_efendi" VALUES(7,63,'7:63','A po çuditeni që këshilla e Zotit tuaj t’ju vijë nëpërmjet një njeriu prej jush, i cili ju tërheq vërejtjen, që të kini frikë (Allahun), në mënyrë që të mëshiroheni (prej Tij)?!');
INSERT INTO "hasan_efendi" VALUES(7,64,'7:64','Ata e quajtën gënjeshtar Nuhun. Kështu që Ne shpëtuam me anë të barkës atë dhe të gjithë njerëzit që ishin me të, ndërsa ata që i mohuan shpalljet Tona, i fundosëm. Ata qenë vërtet një popull i verbër.');
INSERT INTO "hasan_efendi" VALUES(7,65,'7:65','Fisit Ad i dërguam vëllanë e tyre - Hudin. Ai tha: “O populli im! Adhuroni Allahun, ju nuk keni zot tjetër përveç Tij. A nuk po i ruheni dënimit?”');
INSERT INTO "hasan_efendi" VALUES(7,66,'7:66','Paria e popullit të tij që nuk besonte, tha: “Ne shohim tek ti mendjelehtësi dhe mendojmë se je gënjeshtar”.');
INSERT INTO "hasan_efendi" VALUES(7,67,'7:67','Ai tha: “O populli im! Unë nuk jam mendjelehtë, por jam i dërguar nga Zoti i botëve.');
INSERT INTO "hasan_efendi" VALUES(7,68,'7:68','Unë ju sjell shpalljet e Zotit tim dhe jam këshilltar besnik.');
INSERT INTO "hasan_efendi" VALUES(7,69,'7:69','A po çuditeni që këshilla e Zotit tuaj t’ju vijë nëpërmjet një njeriu prej jush, i cili ju tërheq vërejtjen? Kujtoni që Ai ju bëri zëvendës pas popullit të Nuhut dhe ju dha trup e fuqi të madhe. Kujtoni dhuntitë e Allahut, në mënyrë që të keni sukses!”');
INSERT INTO "hasan_efendi" VALUES(7,70,'7:70','Ata thanë: “A mos na ke ardhur që të adhurojmë vetëm Allahun e t’i lëmë zotat që kanë adhuruar prindërit tanë?! Atëherë na sill atë me të cilën na kërcënon, nëse je i sinqertë!”');
INSERT INTO "hasan_efendi" VALUES(7,71,'7:71','Ai tha: “Tashmë, ju ka rënë dënimi dhe zemërimi i Zotit tuaj. A më kundërshtoni mua për emrat (e idhujve), të cilët ua keni vënë ju dhe prindërit tuaj e për të cilët Allahu nuk ju ka sjellë kurrfarë diturie? Pra, pritni, se edhe unë po pres me ju!”');
INSERT INTO "hasan_efendi" VALUES(7,72,'7:72','Me mëshirën Tonë, Ne e shpëtuam Hudin dhe të gjithë besimtarët që ishin me të dhe i asgjësuam ata që i mohuan shpalljet Tona. Ata ishin të gjithë jobesimtarë.');
INSERT INTO "hasan_efendi" VALUES(7,73,'7:73','Ndërsa fisit Themud Ne i dërguam vëllanë e tyre, Salihun. Ai tha: “O populli im! Adhuroni Allahun, ju nuk keni zot tjetër përveç Tij. Në të vërtetë, juve ju është dhënë një shenjë e qartë nga Zoti juaj: kjo deve e Allahut për ju është një mrekulli. Lëreni të kullosë në tokën e Allahut dhe mos i bëni ndonjë të keqe, se ju pret ndëshkimi i ashpër!');
INSERT INTO "hasan_efendi" VALUES(7,74,'7:74','Dhe sillni në mend, kur Ai ju bëri zëvendës pas fisit Ad dhe ju dhuroi vendbanime në Tokë; në rrafshinat e saj po ngrini pallate e në kodrina po gdhendni shtëpi. Kujtoni të mirat e Allahut dhe mos bëni çrregullime në tokë!”');
INSERT INTO "hasan_efendi" VALUES(7,75,'7:75','Por paria arrogante e popullit të tij u tha të shtypurve në radhët atyre që kishin besuar: “A e pranoni që Salihu është i dërguar nga Zoti i tij?” Ata thanë: “Sigurisht që ne besojmë në gjithçka që i është shpallur atij”.');
INSERT INTO "hasan_efendi" VALUES(7,76,'7:76','Ata që ishin kryelartë, thanë: “Ne nuk besojmë në atë që besoni ju”.');
INSERT INTO "hasan_efendi" VALUES(7,77,'7:77','Kështu, ata e therën devenë pa e përfillur urdhrin e Zotit të tyre dhe thanë: “O Salih, sillna atë që na ke premtuar, nëse je vërtet i dërguar!”');
INSERT INTO "hasan_efendi" VALUES(7,78,'7:78','Për këtë arsye ata i goditi një tërmet i rëndë në mëngjes dhe mbetën të vdekur në shtëpitë e tyre.');
INSERT INTO "hasan_efendi" VALUES(7,79,'7:79','Ai u largua prej tyre dhe tha: “O populli im! Unë ju solla mesazhin e Zotit tim dhe ju këshillova, por ju nuk i donit ata që këshillojnë”.');
INSERT INTO "hasan_efendi" VALUES(7,80,'7:80','Kujtoni fjalët e Lutit, i cili i tha popullit të vet: “Përse bëni paturpësi të tilla që askush nuk i ka bërë në botë?”.');
INSERT INTO "hasan_efendi" VALUES(7,81,'7:81','Ju u qaseni meshkujve me epsh - në vend të femrave. Me të vërtetë, ju jeni popull që e teproni me të këqija!”');
INSERT INTO "hasan_efendi" VALUES(7,82,'7:82','Përgjigjja e popullit të tij ishte: “Dëbojini ata nga vendbanimi juaj! Ata janë njerëz që duan të mbahen të dëlirë.”');
INSERT INTO "hasan_efendi" VALUES(7,83,'7:83','Kështu, Ne e shpëtuam atë me gjithë familje, përveç gruas së tij. Ajo ishte nga ata që mbetën pas (dhe u ndëshkuan).');
INSERT INTO "hasan_efendi" VALUES(7,84,'7:84','Dhe mbi ta lëshuam rrebesh (gurësh), pra, shiko si përfunduan njerëzit e zvetënuar!');
INSERT INTO "hasan_efendi" VALUES(7,85,'7:85','Popullit të Medjenit Ne i dërguam vëllanë e tyre, Shuajbin, i cili u tha: “O populli im! Adhuroni Allahun! Ju nuk keni zot tjetër përveç Tij. Me të vërtetë, ju ka ardhur provë e qartë nga Zoti juaj, prandaj matni dhe peshoni saktë, mos u hani hakun njerëzve dhe mos bëni ngatërresa në tokë, pasi të jetë vënë rregulli në të. Kjo është më mirë për ju, nëse jeni besimtarë!');
INSERT INTO "hasan_efendi" VALUES(7,86,'7:86','Mos zini pusi në çdo rrugë, duke frikësuar dhe penguar nga rruga e Allahut ata që besojnë tek Ai dhe duke kërkuar shtrembërimin e saj! Kujtoni atëherë kur ishit të paktë në numër e Ai ju shumoi. Shikoni si përfundojnë ngatërrestarët!');
INSERT INTO "hasan_efendi" VALUES(7,87,'7:87','Nëse një grup mes jush beson në mesazhin me të cilin jam dërguar, ndërsa grupi tjetër nuk beson, duroni derisa Allahu të gjykojë midis nesh! Ai është gjykatësi më i mirë”!');
INSERT INTO "hasan_efendi" VALUES(7,88,'7:88','Paria arrogante e popullit të tij tha: “O Shuajb! Kthehuni në fenë tonë ose do t’ju dëbojmë nga qyteti ynë ty me gjithë ndjekësit e tu”. Shuajbi tha: “Edhe pse e urrejmë atë?!');
INSERT INTO "hasan_efendi" VALUES(7,89,'7:89','Nëse do të ktheheshim në fenë tuaj, pasi Allahu na shpëtoi nga ajo, atëherë do të shpifnim gënjeshtra kundër Allahut. Ne nuk mund të kthehemi në të, përveçse po të dojë Allahu, Zoti ynë. Ai përfshin çdo gjë me dijeninë e Vet dhe tek Ai ne mbështetemi. O Zoti ynë, gjyko drejt midis nesh dhe popullit tonë! Ti je Gjykatësi më i mirë”!');
INSERT INTO "hasan_efendi" VALUES(7,90,'7:90','Por paria e pafe i tha popullit të tij: “Nëse shkoni pas Shuajbit, atëherë, me siguri, do të jeni të humbur.”');
INSERT INTO "hasan_efendi" VALUES(7,91,'7:91','E kështu, ata i goditi një tërmet i rëndë dhe u gdhinë të shtrirë përmbys në shtëpitë e tyre.');
INSERT INTO "hasan_efendi" VALUES(7,92,'7:92','Ata që e quajtën Shuajbin gënjeshtar, sikur nuk kishin banuar kurrë në atë vend; ata që e quajtën Shuajbin gënjeshtar, qenë të humbur.');
INSERT INTO "hasan_efendi" VALUES(7,93,'7:93','Shuajbi u largua prej tyre dhe tha: “O populli im, unë ju solla shpalljet e Zotit tim e ju këshillova, prandaj pse të hidhërohem për popullin e pafe?!”');
INSERT INTO "hasan_efendi" VALUES(7,94,'7:94','Sa herë që çonim të dërguar në ndonjë vendbanim, Ne i dënonim banorët e tij me skamje dhe sëmundje, në mënyrë që të përuleshin.');
INSERT INTO "hasan_efendi" VALUES(7,95,'7:95','Pastaj ua shndërronim fatkeqësinë në lulëzim. Por, kur shtoheshin e begatoheshin, ata thoshin: “Edhe etërit tanë i patën goditur hidhërimet e gëzimet”. Kështu që Ne i dënonim papritmas, pa e marrë vesh ata.');
INSERT INTO "hasan_efendi" VALUES(7,96,'7:96','Sikur banorët e atyre qyteteve të besonin dhe të ruheshin prej gjynaheve, Ne do t’u dërgonim bekime nga qielli dhe toka, por ata mohuan, prandaj i dënuam për atë që bënë.');
INSERT INTO "hasan_efendi" VALUES(7,97,'7:97','A janë të sigurt banorët e këtyre qyteteve se nuk do t’iu vijë dënimi Ynë natën, ndërkohë që flenë?!');
INSERT INTO "hasan_efendi" VALUES(7,98,'7:98','A janë të sigurt ata se nuk do t’iu vijë dënimi Ynë ditën, ndërkohë që argëtohen?!');
INSERT INTO "hasan_efendi" VALUES(7,99,'7:99','A janë të sigurt ata nga të papriturat e Allahut? Vetëm ata që janë të humbur nuk u frikësohen të papriturave të Allahut.');
INSERT INTO "hasan_efendi" VALUES(7,100,'7:100','A nuk e është e qartë për ata që trashëguan tokën e banorëve të mëparshëm të saj, se, nëse Ne dëshirojmë, i dënojmë për gjynahet e tyre dhe ua vulosim zemrat, që të mos dëgjojnë?!');
INSERT INTO "hasan_efendi" VALUES(7,101,'7:101','Ne të kemi treguar ty disa nga ngjarjet e atyre popujve. Të dërguarit e tyre u erdhën me prova të qarta, por sigurisht që ata nuk do të besonin diçka që edhe më parë e mohonin. Kështu ua vulos Allahu zemrat mohuesve.');
INSERT INTO "hasan_efendi" VALUES(7,102,'7:102','Ne pamë se shumica e tyre nuk i përmbaheshin besëlidhjes; vërtet, Ne gjetëm se shumica e tyre ishin të pabindur.');
INSERT INTO "hasan_efendi" VALUES(7,103,'7:103','Pas tyre, Ne dërguam Musain me shenjat Tona, te Faraoni dhe paria e tij, por edhe ata i mohuan ato. Pra, shiko si ishte përfundimi i ngatërrestarëve!');
INSERT INTO "hasan_efendi" VALUES(7,104,'7:104','Musai tha: “O Faraon! Në të vërtetë, unë jam i dërguari i Zotit të botëve!');
INSERT INTO "hasan_efendi" VALUES(7,105,'7:105','E kam detyrë që për Allahun të them vetëm të vërtetën dhe tashmë ju kam sjellë provë të qartë nga Zoti juaj; andaj, lëri të vijnë me mua bijtë e Israilit!”');
INSERT INTO "hasan_efendi" VALUES(7,106,'7:106','Faraoni tha: “Nëse ke sjellë ndonjë shenjë, tregoje, nëse ajo që thua është e vërtetë”.');
INSERT INTO "hasan_efendi" VALUES(7,107,'7:107','Musai lëshoi shkopin e vet dhe ai u bë gjarpër i madh!');
INSERT INTO "hasan_efendi" VALUES(7,108,'7:108','Pastaj nxori dorën e tij e ajo iu duk krejt e bardhë të pranishmëve.');
INSERT INTO "hasan_efendi" VALUES(7,109,'7:109','Paria e popullit të Faraonit tha: “Ky qenka vërtet një magjistar i shkathët,');
INSERT INTO "hasan_efendi" VALUES(7,110,'7:110','i cili kërkon t’ju nxjerrë nga toka juaj! Atëherë, çfarë na këshilloni?');
INSERT INTO "hasan_efendi" VALUES(7,111,'7:111','Të tjerët thanë: “Mbani pak mënjanë atë dhe vëllanë e tij dhe dërgoni nëpër qytete lajmëtarë që të tubojnë');
INSERT INTO "hasan_efendi" VALUES(7,112,'7:112','e të të sjellin ty (Faraon) çdo magjistar të shkathët”.');
INSERT INTO "hasan_efendi" VALUES(7,113,'7:113','Dhe kur erdhën magjistarët e Faraonit, thanë: “A do të shpërblehemi, nëse fitojmë?”');
INSERT INTO "hasan_efendi" VALUES(7,114,'7:114','Faraoni tha: “Po, dhe do të jeni nga më të afërmit e mi”.');
INSERT INTO "hasan_efendi" VALUES(7,115,'7:115','Ata thanë: “O Musa! Ose hidh ti, ose të hedhim ne të parët shkopinjtë tanë!”');
INSERT INTO "hasan_efendi" VALUES(7,116,'7:116','“Hidhni ju!” – u përgjigj ai. Kur magjistarët hodhën shkopinjtë e tyre, i magjepsën sytë e njerëzve, i frikësuan ata dhe shfaqën një magji të madhe.');
INSERT INTO "hasan_efendi" VALUES(7,117,'7:117','Atëherë Ne i kumtuam Musait: “Hidhe shkopin tënd!” dhe ai filloi të gëlltiste gjithçka që trilluan ata.');
INSERT INTO "hasan_efendi" VALUES(7,118,'7:118','Në këtë mënyrë, doli në shesh e vërteta dhe dështoi ajo që kishin bërë ata.');
INSERT INTO "hasan_efendi" VALUES(7,119,'7:119','Kështu, Faraoni dhe njerëzit e tij u mundën dhe u kthyen të poshtëruar,');
INSERT INTO "hasan_efendi" VALUES(7,120,'7:120','kurse magjistarët u përulën në sexhde,');
INSERT INTO "hasan_efendi" VALUES(7,121,'7:121','duke thënë: “Ne besojmë në Zotin e botëve –');
INSERT INTO "hasan_efendi" VALUES(7,122,'7:122','Zotin e Musait dhe të Harunit”.');
INSERT INTO "hasan_efendi" VALUES(7,123,'7:123','Faraoni tha: “Ju guxoni ta besoni atë pa lejen time?! Në të vërtetë, ky është një komplot që keni përgatitur, me qëllim që të nxirrni nga qyteti banorët e tij! Keni për ta parë!');
INSERT INTO "hasan_efendi" VALUES(7,124,'7:124','Do t’jua këpus duart dhe këmbët këmbyerazi e pastaj do t’ju kryqëzoj të gjithëve”.');
INSERT INTO "hasan_efendi" VALUES(7,125,'7:125','Ata thanë: “Ne, me siguri, do të kthehemi tek Zoti ynë!');
INSERT INTO "hasan_efendi" VALUES(7,126,'7:126','Ti po hakmerresh ndaj nesh vetëm pse besuam shenjat e Zotit tonë, posa na erdhën. O Zoti ynë, na jep durim të madh dhe bëna të vdesim myslimanë!”');
INSERT INTO "hasan_efendi" VALUES(7,127,'7:127','Paria e popullit të Faraonit tha: “A mos vallë do ta lësh Musain dhe popullin e tij që të bëjnë ngatërresa në tokë dhe që të braktisin ty dhe zotat e tu?” Faraoni u përgjigj: “Meqenëse jemi sundues mbi ata, Ne do t’i vrasim djemtë e tyre dhe do t’i lëmë gjallë gratë e tyre”.');
INSERT INTO "hasan_efendi" VALUES(7,128,'7:128','Musai i tha popullit të vet: “Kërkoni ndihmë nga Allahu dhe duroni, sepse toka është e Allahut. Ai ia jep atë trashëgim cilit të dojë prej robërve të Vet. Fundi i lumtur u takon atyre që i frikësohen Allahut”.');
INSERT INTO "hasan_efendi" VALUES(7,129,'7:129','Ata i thanë: “Ne kemi vuajtur para se të vish ti dhe ende po vuajmë”. Musai tha: “Ndoshta Zoti juaj do ta zhdukë armikun tuaj dhe do t’ju bëjë sundimtarë në Tokë, për t’ju parë se si do të veproni”.');
INSERT INTO "hasan_efendi" VALUES(7,130,'7:130','Dhe Ne e goditëm popullin e Faraonit me thatësi, me vite zie, që të zinin mend.');
INSERT INTO "hasan_efendi" VALUES(7,131,'7:131','Por sa herë që u vinte ndonjë e mirë, ata thonin: “Këtë e kemi merituar!” E kur i godiste ndonjë e keqe, atë merrnin si ogur të zi që vinte prej Musait dhe popullit të tij. Por jo! Allahu ishte Ai që ua ndillte fatin e zi, ndonëse shumica e tyre nuk e dinin!');
INSERT INTO "hasan_efendi" VALUES(7,132,'7:132','Ata i thoshin (Musait): “Çfarëdo prove që të na sjellësh për të na magjepsur me të, ne nuk do të të besojmë.”');
INSERT INTO "hasan_efendi" VALUES(7,133,'7:133','Prandaj, Ne u dërguam atyre përmbytje, karkaleca, rriqra, bretkosa dhe gjak, si shenja të qarta, por ata madhështoheshin dhe ishin popull keqbërës.');
INSERT INTO "hasan_efendi" VALUES(7,134,'7:134','E kur i godiste dënimi, ata thoshin: “O Musa, lutju Zotit tënd për ne, meqë ka besëlidhje me ty! Nëse na liron nga kjo vuajtje, patjetër që do të të besojmë ty dhe do t’i lëmë bijtë e Israilit të ikin me ty”.');
INSERT INTO "hasan_efendi" VALUES(7,135,'7:135','Por, kur i liruam nga vuajtja dhe erdhi afati i caktuar, ata e shkelën menjëherë premtimin.');
INSERT INTO "hasan_efendi" VALUES(7,136,'7:136','Kështu, Ne u hakmorëm dhe i fundosëm në det, ngaqë ata i mohuan shenjat Tona dhe nuk ua vunë veshin.');
INSERT INTO "hasan_efendi" VALUES(7,137,'7:137','Ndërsa popullit të përndjekur Ne i dhamë në zotërim viset lindore dhe perëndimore të tokës që kishim bekuar. Kështu u përmbush premtimi i bukur i Zotit tënd ndaj bijve të Israilit, për shkak të durimit që treguan. Ndërsa ndërtesat dhe kullat e Faraonit dhe të popullit të tij Ne i shkatërruam.');
INSERT INTO "hasan_efendi" VALUES(7,138,'7:138','Ne i kaluam bijtë e Israilit përtej detit, ku ndeshën një popull që adhuronin idhujt e vet. Ata i thanë Musait: “Na bëj edhe neve një zot, ashtu si kanë ata zota”. Musai tha: “Ju qenkeni vërtet një popull i paditur!');
INSERT INTO "hasan_efendi" VALUES(7,139,'7:139','Fenë që ndjekin këta idhujtarë, e pret shkatërrimi dhe ajo që bëjnë është krejt pa vlerë.');
INSERT INTO "hasan_efendi" VALUES(7,140,'7:140','A mos vallë doni të kërkoj për ju zot tjetër përveç Allahut, që ju ka ngritur mbi të tjerët?!”');
INSERT INTO "hasan_efendi" VALUES(7,141,'7:141','Kujtoni kur Ne ju shpëtuam nga populli i Faraonit, i cili ju mundonte pa masë: fëmijët meshkuj jua vriste, kurse femrat tuaja jua linin gjallë. Kjo ka qenë sprovë e madhe për ju nga Zoti juaj.');
INSERT INTO "hasan_efendi" VALUES(7,142,'7:142','Ne i premtuam Musait se do t’i flisnim pas tridhjetë netësh, të cilat ia shtuam edhe me dhjetë të tjera, kështu që takimi me Zotin e tij u bë pas dyzet netësh. Musai i tha vëllait të vet, Harunit: “Më zëvendëso te populli im, mbaj rregull dhe mos ndiq rrugën e ngatërrestarëve!”');
INSERT INTO "hasan_efendi" VALUES(7,143,'7:143','Kur Musai erdhi në kohën e caktuar dhe Zoti foli me të, ai tha: “O Zoti im, shfaqu që të të shoh” Zoti tha: “Ti nuk mund të më shohësh! Por shiko në atë mal: nëse ai mbetet në vendin e vet, atëherë ti do të më shohësh Mua”. E kur Zoti i tij iu shfaq malit, e thërrmoi atë dhe e bëri pluhur, ndërsa Musait i ra të fikët. Kur erdhi në vete, ai tha: “I lavdëruar qofsh Ti o Zot! Kthehem te Ti i penduar. Unë jam i pari i besimtarëve!”');
INSERT INTO "hasan_efendi" VALUES(7,144,'7:144','Zoti tha: “O Musa! Unë të kam zgjedhur ty mbi të tjerët me kumtet e Mia dhe të Folurin Tim (pa ndërmjetës, por të drejtpërdrejtë). Merre atë që ta kam dhënë dhe bëhu mirënjohës!”');
INSERT INTO "hasan_efendi" VALUES(7,145,'7:145','Ne i shkruam në Pllaka udhëzime dhe shpjegime për gjithçka: “Merri këto me bindje dhe urdhëroje popullin tënd që t’i përvetësojë në mënyrën më të mirë. Unë do t’ju tregoj shtëpinë e të pabindurve.');
INSERT INTO "hasan_efendi" VALUES(7,146,'7:146','Do t’i largoj prej shenjave të Mia ata që sillen me arrogancë në Tokë, kështu që edhe kur të shohin çfarëdo shenje, nuk do ta besojnë atë. Kur të shohin rrugën e drejtë, ata nuk do ta ndjekin atë dhe, kur të shohin rrugën e gabuar, do ta marrin atë, sepse ata nuk i besuan shpalljet Tona dhe nuk u kushtonin vëmendje atyre.');
INSERT INTO "hasan_efendi" VALUES(7,147,'7:147','Atyre që mohojnë shpalljet Tona dhe takimin në botën tjetër, do t’u zhvlerësohen veprat. A do të dënohen ndryshe, veçse sipas punëve që kanë bërë?!');
INSERT INTO "hasan_efendi" VALUES(7,148,'7:148','Populli i Musait, pas largimit të tij, filloi të adhurojë një viç të bërë nga stolisjet e tyre të arta. Ai kishte një zë si të lopës. A nuk e shihnin ata se ai as nuk mund t’u fliste, as t’i udhëzonte në rrugë të drejtë?! Ata e zgjodhën këtë viç për adhurim dhe u bënë keqbërës.');
INSERT INTO "hasan_efendi" VALUES(7,149,'7:149','Por, kur u penduan dhe e kuptuan se kishin hyrë në rrugë të gabuar, ata thanë: “Nëse Zoti ynë nuk na mëshiron dhe nuk na fal, me të vërtetë, do të jemi të humbur!”');
INSERT INTO "hasan_efendi" VALUES(7,150,'7:150','Kur Musai u kthye te populli i tij, i zemëruar dhe i pikëlluar, thirri: “Shumë e keqe është ajo që keni bërë pas ikjes sime! A mos vallë donit të shpejtonit ndëshkimin e Zotit tuaj?” Pastaj i hodhi pllakat dhe, duke e kapur vëllanë për koke, e tërhoqi drejt vetes. Haruni i tha: “O djali i nënës sime! Në të vërtetë, këta njerëz më quajtën për të dobët dhe gati më mbytën. Mos më turpëro para armiqve dhe mos më llogarit ndër keqbërësit!”');
INSERT INTO "hasan_efendi" VALUES(7,151,'7:151','Musai u lut: “O Zot! Më fal mua dhe vëllanë tim dhe na prano në mëshirën Tënde! Ti je më i Mëshirshmi i mëshiruesve”.');
INSERT INTO "hasan_efendi" VALUES(7,152,'7:152','Ata që zgjodhën viçin për zot të tyre, do t’i zërë zemërimi i Zotit të tyre dhe poshtërimi në këtë jetë. Kështu i ndëshkojmë Ne ata që shpifin.');
INSERT INTO "hasan_efendi" VALUES(7,153,'7:153','Ndërsa ata që kryejnë vepra të këqija e pastaj pendohen dhe bëhen besimtarë, ta dinë se pas kësaj, Zoti yt është vërtet Falës e Mëshirëplotë.');
INSERT INTO "hasan_efendi" VALUES(7,154,'7:154','Dhe, kur Musait i kaloi zemërimi, i mori Pllakat, në të cilat ishin shënuar udhëzimet për rrugën e drejtë dhe mëshira për ata që i frikësohen Zotit të tyre.');
INSERT INTO "hasan_efendi" VALUES(7,155,'7:155','Më pas Musai zgjodhi nga populli i tij shtatëdhjetë burra, për takimin Tonë. Kur i goditi tërmeti, ai tha: “O Zoti im! Sikur të doje Ti, do t’i zhdukje ata dhe mua që më përpara. Por, a do të na shkatërroje për atë që bënë disa mendjelehtë mes nesh?! Ajo ishte veç një sprovë e urdhëruar prej Teje, për të shpënë në humbje atë që Ti do dhe për të udhëzuar atë që Ti dëshiron. Ti je Mbrojtësi ynë, andaj falna dhe mëshirona! Ti je më i miri i mëshiruesve!');
INSERT INTO "hasan_efendi" VALUES(7,156,'7:156','Na cakto të mira në këtë botë dhe në tjetrën, sepse veç tek Ti ne kthehemi.” Allahu tha: “Unë godas me dënimin Tim kë të dua, ndërsa mëshira Ime përfshin çdo gjë. Atë do t’ua jap atyre që ruhen nga gjynahet e japin zekatin, atyre që u besojnë shpalljeve Tona');
INSERT INTO "hasan_efendi" VALUES(7,157,'7:157','dhe që ndjekin të Dërguarin Tonë, Profetin që nuk di shkrim e lexim, të cilin ata do ta gjejnë të shënuar në shkrimet e tyre, në Teurat dhe Ungjill. Ai do t’i urdhërojë ata të bëjnë vepra të mira dhe do t’i ndalojë nga të këqijat; do t’ua lejojë të mirat e do t’ua ndalojë të këqijat, duke i liruar nga barrët e rënda dhe vështirësitë që kanë pasur. Prandaj, ata që do ta besojnë, përkrahin dhe nderojnë atë, duke ndjekur dritën që është zbritur me të, pikërisht ata janë të fituarit.”');
INSERT INTO "hasan_efendi" VALUES(7,158,'7:158','Thuaj (o Muhamed): “O njerëz, unë jam i Dërguari i Allahut për të gjithë ju, i Atij që i përket sundimi i qiejve dhe i Tokës. Nuk ka zot tjetër të vërtetë veç Atij; Ai jep jetë dhe vdekje, prandaj besoni në Allahun dhe në të Dërguarin e Tij, Profetin që nuk di shkrim e lexim e që beson në Allahun dhe Fjalët e Tij! Shkoni pas tij, që të jeni në rrugën e drejtë!”');
INSERT INTO "hasan_efendi" VALUES(7,159,'7:159','Te populli i Musait ka pasur disa njerëz që udhëhiqnin dhe zbatonin drejtësinë nën dritën e së vërtetës.');
INSERT INTO "hasan_efendi" VALUES(7,160,'7:160','Ne i ndamë hebrenjtë në dymbëdhjetë fise, secili komb më vete. Kur populli i tij i kërkoi ujë, Ne i kumtuam Musait: “Bjeri me shkopin tënd shkëmbit!” dhe prej tij shpërthyen dymbëdhjetë burime. Çdo fis e dinte burimin e vet. Dhe Ne u bëmë hije me retë dhe u zbritëm manën dhe thëllëza, duke iu thënë: “Hani ushqimet e mira që jua kemi dhënë!” Ata nuk na dëmtuan Ne, por i bënë dëm vetvetes.');
INSERT INTO "hasan_efendi" VALUES(7,161,'7:161','Kur atyre iu tha: “Vendosuni në këtë vendbanim dhe hani çfarë të doni; kërkoni falje dhe hyni në portat e tij duke u përulur, që t’jua falim gabimet dhe t’ua shtojmë shpërblimin punëmirëve”,');
INSERT INTO "hasan_efendi" VALUES(7,162,'7:162','keqbërësit nga radhët e tyre, i ndërruan fjalët me të tjera që nuk u ishin thënë. Prandaj Ne u dërguam dënim nga qielli, sepse bënë prapësi.');
INSERT INTO "hasan_efendi" VALUES(7,163,'7:163','Pyeti ata për qytetin që gjendej në bregdet dhe për çfarë i gjeti banorët e tij kur thyen urdhëresën e Sabatit (së shtunës)! Atë ditë u vinin peshqit me shumicë, ndërsa ditëve tjera që nuk i kremtonin, nuk u vinin. Kështu, Ne i vumë në provë, sepse bënin punë të mbrapshta. ');
INSERT INTO "hasan_efendi" VALUES(7,164,'7:164','Kur disa prej tyre thanë : “Përse këshilloni, kur e dini se Allahu do t’na zhdukë ose do të na ndëshkojë ashpër?!” Këshilltarët u përgjigjën: “Për t’u arsyetuar para Zotit tuaj dhe që ju të mund të ruheni nga gjynahet”.');
INSERT INTO "hasan_efendi" VALUES(7,165,'7:165','Por, kur e harruan atë për të cilën ishin paralajmëruar, Ne i shpëtuam ata që këshillonin, kurse i ndëshkuam ashpër keqbërësit, për shkak të mosbindjes së tyre.');
INSERT INTO "hasan_efendi" VALUES(7,166,'7:166','Kur ata ngulmuan me mendjemadhësi të bënin atë që u ishte ndaluar, Ne u thamë: “Bëhuni majmunë të përbuzur!”.');
INSERT INTO "hasan_efendi" VALUES(7,167,'7:167','Pastaj Zoti yt shpalli se do t’u dërgonte atyre të tjerë që do t’i mundonin, në mënyrën më të keqe, deri në Ditën e Kiametit. Me të vërtetë, Zoti yt dënon shpejt, por Ai është edhe Falës e Mëshirëplotë.');
INSERT INTO "hasan_efendi" VALUES(7,168,'7:168','Dhe Ne i shpërndamë ata në Tokë grupe-grupe. Disa prej tyre ishin të mirë e disa jo. Ne i vumë në provë ata me mirësi dhe fatkeqësi, me qëllim që të ktheheshin (në rrugë të drejtë).');
INSERT INTO "hasan_efendi" VALUES(7,169,'7:169','Pas tyre, erdhi një brezni e cila e trashëgoi Librin (Teuratin), por duke marrë gjërat e pavlefshme të kësaj bote dhe duke thënë: “Do të na falet çdo gjë!” Edhe nëse u binin në dorë gjëra të ngjashme, prapë i merrnin ato. Vallë, a nuk e kishin pranuar besëlidhjen në Libër – që për Allahun të thoshin vetëm të vërtetën?! Pikërisht ata, që e kishin studiuar ç’përmbante ai? Bota tjetër është më e mirë për besimtarët që i frikësohen Allahut; akoma nuk e kuptoni këtë?!');
INSERT INTO "hasan_efendi" VALUES(7,170,'7:170','Ata që u përmbahen thënieve të Librit dhe që e kryejnë namazin, ta dinë se Ne, pa dyshim, nuk do t’ua humbim shpërblimin punëmirëve.');
INSERT INTO "hasan_efendi" VALUES(7,171,'7:171','Ne ngritëm sipër tyre malin, që dukej si hije e ata besonin se do të binte mbi ta, dhe u thamë: “Mbajeni fort atë që ju dhamë dhe kujtoni çfarë përmban ajo, që të ruheni nga gjynahet.”');
INSERT INTO "hasan_efendi" VALUES(7,172,'7:172','Kur Zoti yt nxori nga kurrizi i bijve të Ademit pasardhësit e tyre dhe i bëri të dëshmojnë kundër vetes së tyre, u tha: “A nuk jam Unë Zoti juaj?” Ata u përgjigjën: “Po, dëshmojmë se Ti je.” Kështu bëri Ai, në mënyrë që ju (o njerëz) të mos thoni në Ditën e Kiametit: “Këtë nuk e kishim ditur.”');
INSERT INTO "hasan_efendi" VALUES(7,173,'7:173','Ose që të mos thoni: “Me të vërtetë, etërit tanë ishin idhujtarë para nesh, kështu që edhe ne jemi pasardhësit e tyre. A mos vallë do të na shkatërrosh për veprat e atyre që ndoqën mashtrimin?”');
INSERT INTO "hasan_efendi" VALUES(7,174,'7:174','Kështu, Ne i shpjegojmë hollësisht shpalljet, që ata të kthehen (në rrugën e drejtë).');
INSERT INTO "hasan_efendi" VALUES(7,175,'7:175','Tregoju atyre (hebrenjve) për njeriun, të cilit Ne i mësuam shpalljet Tona, por u largua prej tyre. Kështu, djalli e pushtoi atë dhe ai u bë nga të humburit. ');
INSERT INTO "hasan_efendi" VALUES(7,176,'7:176','Sikur të donim, do ta ngrinim atë (me shpalljet Tona), por ai u mbërthye pas jetës tokësore dhe u dha pas dëshirave të veta. Shembulli i tij është si shembulli i qenit: nëse e ndjek, ai të nxjerr gjuhën, nëse largohesh, ai përsëri të nxjerr gjuhën. Ky është shembull i njerëzve, që mohojnë shpalljet Tona. Tregojua atyre këto ngjarje, në mënyrë që ata të mendojnë.');
INSERT INTO "hasan_efendi" VALUES(7,177,'7:177','I shëmtuar është shembulli i njerëzve që i mohojnë shpalljet Tona dhe i bëjnë keq vetvetes.');
INSERT INTO "hasan_efendi" VALUES(7,178,'7:178','Ai të cilin Allahu e udhëzon, është në rrugë të drejtë, ndërsa ai të cilin Allahu e shpie në humbje, është vërtet i humbur.');
INSERT INTO "hasan_efendi" VALUES(7,179,'7:179','Ne kemi paracaktuar për Xhehenemin shumë xhinde dhe njerëz, sepse ata kanë zemra, por nuk kuptojnë, kanë sy, por nuk shohin dhe kanë veshë, por nuk dëgjojnë. Ata janë si bagëtitë, madje edhe më zi. Pikërisht këta janë të shkujdesurit.');
INSERT INTO "hasan_efendi" VALUES(7,180,'7:180','Allahut i përkasin emrat më të bukur, andaj thirreni Atë (lutjuni Atij) me to dhe largohuni nga ata që i shtrembërojnë emrat e Tij. Ata do të ndëshkohen për çfarë kanë vepruar.');
INSERT INTO "hasan_efendi" VALUES(7,181,'7:181','Ndërmjet atyre që kemi krijuar, ka njerëz që i udhëzojnë të tjerët në rrugën e vërtetë dhe gjykojnë drejt.');
INSERT INTO "hasan_efendi" VALUES(7,182,'7:182','Ndërsa ata që i mohojnë shpalljet Tona, dalëngadalë Ne do t’i shpiem drejt shkatërrimit, pa e kuptuar');
INSERT INTO "hasan_efendi" VALUES(7,183,'7:183','e do t’u japim afat. Me të vërtetë, plani Ynë është i fortë.');
INSERT INTO "hasan_efendi" VALUES(7,184,'7:184','A nuk u ka vajtur ndonjëherë në mendje atyre se shoku i tyre nuk ka kurrfarë marrëzie?! Ai është vetëm paralajmërues i hapët.');
INSERT INTO "hasan_efendi" VALUES(7,185,'7:185','A nuk e shohin ata mbretërinë e qiejve e të Tokës dhe gjithçka që ka krijuar Allahu, duke menduar se mund t’u jetë afruar fundi?! Në çfarë Shpallje tjetër do të besojnë pas kësaj?!');
INSERT INTO "hasan_efendi" VALUES(7,186,'7:186','Ata që Allahu i shpie në humbje, askush nuk mund t’i udhëzojë në rrugë të drejtë! Ai i lë ata të enden në mosbesimin dhe humbjen e tyre.');
INSERT INTO "hasan_efendi" VALUES(7,187,'7:187','Ata të pyesin ty (Muhamed) rreth Orës (së Kiametit). Thuaju: “Atë e di vetëm Zoti im. Kohën kur do të ndodhë ajo, do ta tregojë vetëm Ai. Do të jetë e rëndë për qiejt dhe Tokën, do t’u vijë krejtësisht papritmas”. Ata të pyesin, a thua se ti di diçka për të. Thuaj: “Këtë e di vetëm Allahu, ndërsa shumica e njerëzve nuk e dinë (pse kjo gjë është e fshehtë).”');
INSERT INTO "hasan_efendi" VALUES(7,188,'7:188','Thuaj: “Unë nuk mund t’i sjell vetes as dobi, as dëm, përveçse aq sa dëshiron Allahu. Sikur ta dija të padukshmen, do t’i shumoja të mirat dhe nuk do të më prekte asnjë e keqe. Unë jam vetëm paralajmërues dhe sjellës i lajmeve të mira për njerëzit që besojnë”.');
INSERT INTO "hasan_efendi" VALUES(7,189,'7:189','Është Ai që ju krijoi nga një njeri i vetëm (Ademi), prej të cilit krijoi edhe bashkëshorten e tij (Havanë), që të gjente qetësi tek ajo. Kur njeriu bashkohet me gruan e vet, e ngarkon atë me një barrë të lehtë, që ajo e kalon pa e ndier për ca kohë. Dhe, kur ajo rëndohet, ata të dy i luten Allahut, Zotit të tyre: “Nëse na dhuron pasardhës të shëndoshë, do të të jemi vërtet mirënjohës!”');
INSERT INTO "hasan_efendi" VALUES(7,190,'7:190','Por, pasi Ai u dhuron pasardhës të shëndoshë e të mirë, ata të dy i veshin Allahut ortakë në lidhje me atë që u kishte dhënë. Por Allahu është shumë lart nga ato që ia veshin Atij si ortakë!');
INSERT INTO "hasan_efendi" VALUES(7,191,'7:191','A mos vallë ata i veshin Atij si ortakë idhuj që nuk mund të krijojnë asgjë, ndërkohë që vetë janë të krijuar?!');
INSERT INTO "hasan_efendi" VALUES(7,192,'7:192','Idhuj, që nuk mund t’u japin ndonjë ndihmë atyre e as veten nuk mund ta ndihmojnë?!');
INSERT INTO "hasan_efendi" VALUES(7,193,'7:193','Edhe nëse i thirrni në rrugën e drejtë, ata nuk do t’ju ndjekin. Prandaj njësoj është - i thirrët apo nuk i thirrët.');
INSERT INTO "hasan_efendi" VALUES(7,194,'7:194','Në të vërtetë, ata që iu luteni ju, në vend të Allahut, janë robër si ju. Thirrini ata dhe le t’ju përgjigjen, nëse thoni të vërtetën!');
INSERT INTO "hasan_efendi" VALUES(7,195,'7:195','A kanë ata këmbë për të ecur ose duar për të kapur? A kanë sy për të parë ose veshë për të dëgjuar? Thuaj: “Thirrni zotat tuaj të rreme dhe kurdisni kurthe kundër meje, pa më lënë kohë!');
INSERT INTO "hasan_efendi" VALUES(7,196,'7:196','Në të vërtetë, Mbrojtësi im është Allahu, i Cili ka zbritur Librin dhe kujdeset për të mirët.');
INSERT INTO "hasan_efendi" VALUES(7,197,'7:197','Kurse ata që i adhuroni në vend të Tij, nuk mund t’ju ndihmojnë as ju e as veten.');
INSERT INTO "hasan_efendi" VALUES(7,198,'7:198','Edhe sikur t’i thërrasësh në rrugë të drejtë, ata nuk do të dëgjojnë! Ty të duket sikur të vështrojnë, por ata nuk shohin.');
INSERT INTO "hasan_efendi" VALUES(7,199,'7:199','Trego mëshirë, urdhëro vepra të mira dhe shmangu nga të paditurit!');
INSERT INTO "hasan_efendi" VALUES(7,200,'7:200','Nëse djalli përpiqet të të shtyjë drejt së keqes, kërko strehim tek Allahu, se vërtet, Ai dëgjon dhe di gjithçka.');
INSERT INTO "hasan_efendi" VALUES(7,201,'7:201','Ata që i frikësohen Allahut, kur u vjen ndonjë mendim i lig nga djalli, i kujtojnë (urdhrat e Tij), e atëherë e shohin rrugën e drejtë.');
INSERT INTO "hasan_efendi" VALUES(7,202,'7:202','Ndërsa vëllezërit e vet (prej njerëzve), ata i shpien edhe më tej në humbje pa pushuar.');
INSERT INTO "hasan_efendi" VALUES(7,203,'7:203','Kur ti (Muhamed) nuk u lexon atyre ndonjë varg nga Shpallja, ata thonë: “Ah! Sikur ta bëje ti vetë”! Thuaju: “Unë ndjek vetëm atë që më ka shpallur Zoti im. Ky (Kuran) është vetëm dëshmi ndriçuese nga Zoti juaj, udhërrëfim dhe mëshirë për njerëzit që besojnë.”');
INSERT INTO "hasan_efendi" VALUES(7,204,'7:204','Kur të lexohet Kurani, dëgjoni dhe heshtni, në mënyrë që të mëshiroheni.');
INSERT INTO "hasan_efendi" VALUES(7,205,'7:205','Përmende Zotin tënd më vete, i përulur dhe me frikë - jo me zë të lartë, në mëngjes dhe mbrëmje, dhe mos u bëj nga ata që nuk i kushtojnë vëmendje!');
INSERT INTO "hasan_efendi" VALUES(7,206,'7:206','Në të vërtetë, ata që janë afër Zotit tënd, nuk refuzojnë ta adhurojnë Atë, por e lavdërojnë dhe përulen në sexhde para Tij.');
INSERT INTO "hasan_efendi" VALUES(8,1,'8:1','Të pyesin ty (o Muhamed) për plaçkën e luftës. Thuaj: “Plaçka e luftës i përket Allahut dhe të Dërguarit”. Prandaj, frikësojuni Allahut, rregulloni marrëdhëniet midis jush dhe bindjuni Allahut dhe të Dërguarit të Tij, nëse jeni besimtarë të vërtetë”.');
INSERT INTO "hasan_efendi" VALUES(8,2,'8:2','Besimtarë të vërtetë janë vetëm ata, zemrat e të cilëve, kur përmendet Allahu, fërgëllojnë dhe, kur u lexohen shpalljet e Tij, u forcohet besimi dhe vetëm te Zoti i tyre mbështeten,');
INSERT INTO "hasan_efendi" VALUES(8,3,'8:3','ata që falin namazin dhe japin prej asaj që Ne ua kemi dhënë.');
INSERT INTO "hasan_efendi" VALUES(8,4,'8:4','Njëmend, ata janë besimtarë të vërtetë. Ata do të kenë shkallë përnderimi te Zoti i tyre, falje dhe furnizim bujar (në Xhenet).');
INSERT INTO "hasan_efendi" VALUES(8,5,'8:5','Zoti yt të nxori nga shtëpia jote (për të luftuar) për të Vërtetën edhe pse kjo nuk i pëlqeu një grupi prej besimtarëve. ');
INSERT INTO "hasan_efendi" VALUES(8,6,'8:6','Ata polemizojnë me ty për të vërtetën (luftën), e cila u ishte bërë e qartë, sikur të ishin duke u çuar drejt vdekjes së sigurt.');
INSERT INTO "hasan_efendi" VALUES(8,7,'8:7','Allahu ju premtoi fitoren mbi njërin prej dy grupeve, që ai të ishte juaji. Ju dëshironit që grupi i paarmatosur të ishte juaji, ndërsa Allahu dëshironte të përmbushte premtimin e Tij dhe t’ua zhdukte rrënjët mohuesve,');
INSERT INTO "hasan_efendi" VALUES(8,8,'8:8','për ta bërë të Vërtetën (Islamin) ngadhënjyese dhe për ta zhdukur të pavërtetën edhe pse keqbërësit e pafe e urrejnë këtë.');
INSERT INTO "hasan_efendi" VALUES(8,9,'8:9','Ju kërkuat ndihmë nga Zoti juaj e Ai ju përgjigj: “Unë do t’ju ndihmoj me një mijë engjëj të cilët do të vijnë njëri pas tjetrit”.');
INSERT INTO "hasan_efendi" VALUES(8,10,'8:10','Allahu e bëri këtë fitore si lajm të mirë, që të qetësohen zemrat tuaja me të. Fitorja vjen vetëm nga Allahu. Me të vërtetë, Allahu është i Plotfuqishëm dhe i Urtë.');
INSERT INTO "hasan_efendi" VALUES(8,11,'8:11','Ai bëri që ju të koteni në gjumë të lehtë si shenjë sigurie nga ana e Tij dhe lëshoi nga qielli shi, për t’ju pastruar nëpërmjet atij, për të larguar nga ju ndyrësinë e djallit dhe për t’i forcuar zemrat tuaja dhe këmbët.');
INSERT INTO "hasan_efendi" VALUES(8,12,'8:12','(Kujto o Muhamed) kur Zoti yt i frymëzoi engjëjt: “Unë jam me ju, andaj forcojini ata që besojnë. Unë do të shtie frikë në zemrat e mohuesve, andaj goditini në qafë dhe në të gjithë gishtërinjtë”.');
INSERT INTO "hasan_efendi" VALUES(8,13,'8:13','Ne i dënuam kështu, sepse ata dolën kundër Allahut dhe të Dërguarit të Tij. Kushdo që del kundër Allahut dhe të Dërguarit të Tij, ta dijë se Allahu është ndëshkues i ashpër.');
INSERT INTO "hasan_efendi" VALUES(8,14,'8:14','(Ne u thamë atyre:) “Ky është ndëshkimi, provojeni atë. Dënimi i zjarrit i pret mohuesit”.');
INSERT INTO "hasan_efendi" VALUES(8,15,'8:15','O besimtarë, kur të ndeshni ushtritë e jobesimtarëve në betejë, mos ua ktheni shpinën!');
INSERT INTO "hasan_efendi" VALUES(8,16,'8:16','Cilido që ua kthen shpinën atë ditë, përveç atij që tërhiqet me qëllim që të sulmojë përsëri ose për t’u bashkuar me një grup tjetër, do të shkaktojë zemërimin e Allahut dhe do të ketë si strehë Xhehenemin. Eh, sa fund i keq është ky!');
INSERT INTO "hasan_efendi" VALUES(8,17,'8:17','Sigurisht, nuk ishit ju që i vratë ata, por i vrau Allahu! Kur gjuaje, nuk ishe ti (Muhamed) që gjuaje, por ishte Allahu Ai që gjuante, me qëllim që t’u bënte besimtarëve një dhuratë të bukur. Vërtet, Allahu dëgjon dhe di çdo gjë.');
INSERT INTO "hasan_efendi" VALUES(8,18,'8:18','Kështu ka ndodhur; dhe dijeni se Allahu i dobëson dredhitë e mohuesve.');
INSERT INTO "hasan_efendi" VALUES(8,19,'8:19','Nëse ju (jobesimtarë mekas) kërkuat fitore dhe gjykim (prej Allahut), ja tek erdhi fitorja (kundër jush). Por, nëse tërhiqeni (nga mohimi dhe lufta kundër të Dërguarit), kjo do të jetë për ju shumë më e dobishme. Nëse ktheheni (në luftë), edhe Ne do të kthehemi (në ndihmë të besimtarëve). Forcat tuaja nuk do t’ju bëjnë asnjë dobi, sado të jenë të shumta në numër, sepse Allahu është me besimtarët.');
INSERT INTO "hasan_efendi" VALUES(8,20,'8:20','O besimtarë! Bindjuni Allahut dhe të Dërguarit të Tij dhe mos ia ktheni shpinën, ndërkohë që e dëgjoni (të vërtetën)!');
INSERT INTO "hasan_efendi" VALUES(8,21,'8:21','Mos u bëni si ata që thonë “Dëgjojmë”, por nuk ia vënë veshin.');
INSERT INTO "hasan_efendi" VALUES(8,22,'8:22','Krijesat më të këqija para Allahut janë ato që janë shurdhe dhe memece (ndaj së vërtetës) e që nuk hanë arsye.');
INSERT INTO "hasan_efendi" VALUES(8,23,'8:23','Sikur Allahu të dinte ndonjë të mirë tek ata, Ai do t’ua jepte dëgjimin. Por, edhe sikur t’i bënte të dëgjonin, ata do të shmangeshin dhe do të largoheshin nga besimi.');
INSERT INTO "hasan_efendi" VALUES(8,24,'8:24','O besimtarë! Bindjuni Allahut dhe të Dërguarit kur ju thërret drejt asaj që ju jep jetë ! Dhe ta dini se Allahu ndërhyn midis njeriut dhe zemrës së tij dhe se tek Ai do të mblidheni të gjithë!');
INSERT INTO "hasan_efendi" VALUES(8,25,'8:25','Ruajuni ngatërresave, të cilat nuk i godasin vetëm ata që bëjnë keq; dhe ta dini se Allahu dënon ashpër!');
INSERT INTO "hasan_efendi" VALUES(8,26,'8:26','Dhe kujtohuni kur ishit pak në numër, ishit të dobët në tokë e kishit frikë se njerëzit (jobesimtarët) do t’ju gllabëronin! Ai ju strehoi dhe ju forcoi me ndihmën e Tij e ju dha shumë të mira, që ju ta falënderoni.');
INSERT INTO "hasan_efendi" VALUES(8,27,'8:27','O besimtarë! Mos e tradhtoni Allahun dhe të Dërguarin e mos i tradhtoni amanetet me vetëdije.');
INSERT INTO "hasan_efendi" VALUES(8,28,'8:28','Ta dini se pasuria juaj dhe fëmijët tuaj janë vetëm sprovë dhe se vetëm tek Allahu është shpërblimi i madh.');
INSERT INTO "hasan_efendi" VALUES(8,29,'8:29','O besimtarë! Nëse i frikësoheni Allahut, Ai do t’ju japë aftësinë e të gjykuarit drejt, do t’ju lirojë nga gjynahet tuaja dhe do t’ju falë. Allahu zotëron mirësi të madhe.');
INSERT INTO "hasan_efendi" VALUES(8,30,'8:30','Kujto kur mohuesit komplotonin kundër teje: për të të burgosur, vrarë ose dëbuar. Ata bënin kurthe, por edhe Allahu ngrinte kurthe (kundër tyre). Allahu është planifikuesi më i mirë.');
INSERT INTO "hasan_efendi" VALUES(8,31,'8:31','Kur atyre u lexohen shpalljet Tona, ata thonë: “Ne i kemi dëgjuar; po të duam, edhe ne do të thoshim diçka të ngjashme. Këto nuk janë kurrgjë, përveçse përralla të të lashtëve.”');
INSERT INTO "hasan_efendi" VALUES(8,32,'8:32','(Kujtoje, gjithashtu) kur ata thanë: “Zoti ynë, nëse ky (Kuran) është e Vërteta (e zbritur prej Teje), atëherë lësho mbi ne shi gurësh nga qielli ose dërgona një ndëshkim të dhembshëm!”');
INSERT INTO "hasan_efendi" VALUES(8,33,'8:33','Por Allahu nuk do t’i ndëshkonte ata, përderisa ti ishe në mesin e tyre dhe, Allahu nuk do t’i ndëshkojë ata, përderisa ata i kërkojnë falje Atij.');
INSERT INTO "hasan_efendi" VALUES(8,34,'8:34','E përse të mos i dënojë Allahu, kur ata e pengojnë vizitën në Shtëpinë e Shenjtë (Qabe) edhe pse ata nuk janë kujdestarë të saj?! Kujdestarë të saj janë vetëm ata që i frikësohen Allahut, por shumica e tyre nuk e dinë.');
INSERT INTO "hasan_efendi" VALUES(8,35,'8:35','Lutja e tyre në Shtëpinë e Shenjtë ishte vetëm me fërshëllima dhe rrahje duarsh. Prandaj (atyre do t’u thuhet:) “Provojeni ndëshkimin, pasi kurrë nuk besuat”!');
INSERT INTO "hasan_efendi" VALUES(8,36,'8:36','Në të vërtetë, jobesimtarët e shpenzojnë pasurinë e tyre për t’i penguar njerëzit nga rruga e Allahut. Ata do ta shpenzojnë atë, por në fund ajo do jetë vetëm dëshpërim për ta, sepse do të jenë të mundur. Ata që nuk besojnë, në Xhehenem do të tubohen,');
INSERT INTO "hasan_efendi" VALUES(8,37,'8:37','që Allahu të veçojë të keqin nga i miri; të këqijtë t’i palosë njërin mbi tjetrin, e të gjithë t’i hedhë në Xhehenem. Këta, me të vërtetë, janë të humbur.');
INSERT INTO "hasan_efendi" VALUES(8,38,'8:38','Thuaju atyre që nuk besojnë: nëse largohen (nga mosbesimi), do t’u falet e kaluara e, nëse kthehen (në mosbesim), atëherë dënimi i popujve të mëparshëm është paralajmërim për ta.');
INSERT INTO "hasan_efendi" VALUES(8,39,'8:39','Luftoni kundër tyre, derisa të zhduket idhujtaria dhe derisa të mbetet vetëm feja e Allahut. Nëse ata heqin dorë (prej mohimit), Allahu i sheh punët e tyre.');
INSERT INTO "hasan_efendi" VALUES(8,40,'8:40','Por, nëse ata kthejnë shpinën, dijeni se Allahu është mbrojtësi juaj. Ai është mbrojtës dhe mbështetës i mrekullueshëm!');
INSERT INTO "hasan_efendi" VALUES(8,41,'8:41','Ta dini se çfarëdo që të fitoni si plaçkë lufte, një e pesta i takon Allahut, të Dërguarit dhe farefisit të tij, jetimëve, të varfërve dhe udhëtarit (që ka ngelur në rrugë), nëse besoni në Allahun dhe në atë që ia kemi shpallur robit Tonë në ditën e fitores (luftën e Bedrit), atë ditë kur u ndeshën dy ushtritë. Vërtet, Allahu është i Fuqishëm për çdo gjë.');
INSERT INTO "hasan_efendi" VALUES(8,42,'8:42','Kur ju ishit në bregun e afërt të luginës, ata ndodheshin në bregun e largët, ndërsa karvani poshtë jush. Edhe sikur t’i kishit lënë takim njëri-tjetrit, nuk do të ishit pajtuar për kohën e ndeshjes. Por Allahu e përmbushi atë ngjarje që ishte e caktuar, me qëllim që kush duhej të vdiste, të vdiste me prova të qarta dhe, kush duhej të mbetej gjallë, të jetonte me prova të qarta. Vërtet, Allahu i dëgjon të gjitha dhe di çdo gjë.');
INSERT INTO "hasan_efendi" VALUES(8,43,'8:43','(Kujto) kur Allahu të tregoi në ëndërr se ata janë pak. Sikur të të tregonte se ishin shumë, ju do të humbisnit guximin dhe do të grindeshit për atë çështje, por Allahu ju shpëtoi. Ai e di se çfarë keni në zemrat tuaja.');
INSERT INTO "hasan_efendi" VALUES(8,44,'8:44','Kur u përballët (me armikun), Ai jua shfaqi ata në sytë tuaj sikur ishin pak, ndërsa ju ju bëri të dukeshit pak në sytë e atyre, për të përmbushur atë që ishte caktuar. Tek Allahu kthehen të gjitha çështjet.');
INSERT INTO "hasan_efendi" VALUES(8,45,'8:45','O besimtarë, kur të ndesheni me ndonjë forcë armike, qëndroni të patundur dhe përmendni shumë Allahun që të fitoni.');
INSERT INTO "hasan_efendi" VALUES(8,46,'8:46','Bindjuni Allahut dhe të Dërguarit të Tij dhe mos u grindni ndërmjet jush, sepse do të humbni guximin e do t’ju lërë fuqia. Bëhuni të durueshëm, se Allahu, me të vërtetë, është me të durueshmit.');
INSERT INTO "hasan_efendi" VALUES(8,47,'8:47','Mos u bëni si ata (jobesimtarët e Mekës) që dolën nga shtëpitë e tyre me mendjemadhësi e për sy e faqe të botës, duke dashur t’i pengojnë njerëzit nga rruga e Allahut. Allahu e di mirë çfarë punojnë ata.');
INSERT INTO "hasan_efendi" VALUES(8,48,'8:48','Djalli ua zbukuroi atyre veprat e këqija dhe u tha: “Askush në botë nuk do t’ju mundë sot dhe unë jam mbrojtësi juaj”. Mirëpo, kur u ndeshën dy ushtritë (me engjëjt përkrah besimtarëve), ai u kthye nga thembrat e tij dhe u tha (jobesimtarëve): “Unë nuk kam të bëj me ju; unë shoh atë që ju nuk e shihni dhe i frikësohem Allahut, sepse Allahu ndëshkon ashpër”.');
INSERT INTO "hasan_efendi" VALUES(8,49,'8:49','Hipokritët dhe ata që kishin sëmundje (dyshim) në zemrat e veta, thoshin: “Këta i ka mashtruar feja e tyre!” Por ai që mbështetet tek Allahu, me të vërtetë, do ta gjejë Atë të Plotfuqishëm dhe të Urtë.');
INSERT INTO "hasan_efendi" VALUES(8,50,'8:50','Eh,sikur të shihje se si engjëjt ua marrin shpirtrat mohuesve! I godasin ata në fytyrë dhe shpinë (duke u thënë): “Shijoni dënimin e zjarrit!');
INSERT INTO "hasan_efendi" VALUES(8,51,'8:51','Kjo është për shkak të asaj që keni bërë me duart tuaja, sepse Allahu nuk është i padrejtë me robërit e Vet!”');
INSERT INTO "hasan_efendi" VALUES(8,52,'8:52','Kështu u ndodhi edhe ithtarëve të Faraonit dhe atyre që kanë qenë para tyre. Ata i mohuan shpalljet e Allahut dhe Ai i dënoi për shkak të gjynaheve të tyre. Me të vërtetë, Allahu është i Fuqishëm dhe ndëshkon ashpër.');
INSERT INTO "hasan_efendi" VALUES(8,53,'8:53','Kjo ndodh se Allahu nuk ia ndryshon kurrë një mirësi që ia ka dhënë një populli, derisa ata të ndryshojnë atë që kanë në veten e tyre. Vërtet, Allahu dëgjon dhe di gjithçka.');
INSERT INTO "hasan_efendi" VALUES(8,54,'8:54','(Sjelljet e jobesimtarëve të Mekës) ngjajnë me sjelljet e ithtarëve të Faraonit dhe të të tjerëve që jetuan para tyre. Ata i përgënjeshtruan shpalljet e Zotit të tyre dhe kështu Ne i shkatërruam për shkak të gjynaheve të tyre, ndërsa ithtarët e Faraonit i fundosëm. Të gjithë ata ishin keqbërës.');
INSERT INTO "hasan_efendi" VALUES(8,55,'8:55','Krijesat më të këqija tek Allahu janë ata që mohojnë dhe që nuk do të besojnë;');
INSERT INTO "hasan_efendi" VALUES(8,56,'8:56','janë ata me të cilët ke bërë marrëveshje, por që çdo herë e shkelin atë dhe nuk i frikësohen Allahut.');
INSERT INTO "hasan_efendi" VALUES(8,57,'8:57','Nëse ti i ndesh ata në luftë, shpartalloji, që të vënë mend ata që vijnë pas tyre.');
INSERT INTO "hasan_efendi" VALUES(8,58,'8:58','Nëse ke frikë se ndonjë fis e thyen besën, edhe ti prishja marrëveshjen me të njëjtën mënyrë, sepse Allahu nuk i do ata që tradhtojnë.');
INSERT INTO "hasan_efendi" VALUES(8,59,'8:59','Le të mos mendojnë kurrsesi ata që nuk besojnë se do të fitojnë, sepse nuk mund të Na pengojnë.');
INSERT INTO "hasan_efendi" VALUES(8,60,'8:60','Përgatisni kundër tyre kuaj dhe forcë për luftë sa të mundni, që të tmerroni armikun e Allahut dhe armikun tuaj, si dhe të tjerë, përveç tyre, të cilët ju nuk i njihni, por që Allahu i njeh. Çfarëdo që të shpenzoni në rrugën e Allahut, do t’ju shpërblehet dhe nuk do t’ju bëhet padrejtësi.');
INSERT INTO "hasan_efendi" VALUES(8,61,'8:61','Nëse ata shfaqin prirje për paqe, atëherë edhe ti ano nga ajo dhe mbështetu tek Allahu, sepse, vërtet, Ai dëgjon dhe di gjithçka.');
INSERT INTO "hasan_efendi" VALUES(8,62,'8:62','Por, nëse ata synojnë të të mashtrojnë, të mjafton Allahu (si mbrojtës). Ai të ka forcuar me ndihmën e Tij dhe me besimtarët,');
INSERT INTO "hasan_efendi" VALUES(8,63,'8:63','duke i bashkuar zemrat e tyre (si një zemër e vetme). Sikur të shpenzoje tërë pasurinë që gjendet në Tokë, nuk do të mund t’i bashkoje zemrat e tyre, por Allahu i bashkoi ato. Ai është vërtet i Plotfuqishëm dhe i Urtë.');
INSERT INTO "hasan_efendi" VALUES(8,64,'8:64','O Profet! Allahu të mjafton ty dhe besimtarëve që të pasojnë.');
INSERT INTO "hasan_efendi" VALUES(8,65,'8:65','O Profet! Jepu zemër e nxiti besimtarët për luftë! Nëse mes jush gjenden njëzet të qëndrueshëm, ata do të mundin dyqind (mohues) e, nëse nga ju janë njëqind të qëndrueshëm, ata do të mundin një mijë mohues, sepse ata janë njerëz që nuk kuptojnë.');
INSERT INTO "hasan_efendi" VALUES(8,66,'8:66','Tani, Allahu ju ka lehtësuar ju, ngaqë Ai e di se keni dobësi. Kështu, nëse ka prej jush njëqind të qëndrueshëm, ata do të mundin dyqind (mohues) dhe, nëse nga ju janë një mijë (të qëndrueshëm), me vullnetin e Allahut, ata do të mundin dy mijë (mohues). Allahu është me të qëndrueshmit.');
INSERT INTO "hasan_efendi" VALUES(8,67,'8:67','Asnjë profet s’ka të drejtë të ketë robër për vete, derisa të arrijë fitore të plotë në Tokë. Ju dëshironi të mirat kalimtare të kësaj bote, kurse Allahu dëshiron botën tjetër. Allahu është i Plotfuqishëm dhe i Urtë.');
INSERT INTO "hasan_efendi" VALUES(8,68,'8:68','Sikur të mos ishin urdhërimet e mëparshme të Allahut (për lejimin e plaçkës së luftës), do t’ju godiste një dënim i madh për atë që keni marrë.');
INSERT INTO "hasan_efendi" VALUES(8,69,'8:69','Gëzojeni plaçkën e luftës që keni fituar si gjë të lejueshme dhe të mirë dhe frikësojuni Allahut! Vërtet, Allahu është Falës e Mëshirëplotë.');
INSERT INTO "hasan_efendi" VALUES(8,70,'8:70','O Profet! Thuaju robërve që gjenden në duart tuaja: “Nëse Allahu gjen mirësi në zemrat tuaja, do t’ju japë diçka edhe më të mirë nga ajo që ju është marrë dhe do t’ju falë”. Se Allahu është Falës dhe Mëshirëplotë.');
INSERT INTO "hasan_efendi" VALUES(8,71,'8:71','E, nëse ata duan të të tradhtojnë, dije se më parë kanë tradhtuar Allahun dhe prandaj Ai ta mundësoi ty fitoren ndaj tyre. Allahu është i Gjithëdijshëm dhe i Urtë.');
INSERT INTO "hasan_efendi" VALUES(8,72,'8:72','Vërtet, ata që besuan dhe mërguan (për hir të besimit), e më pas luftuan me pasurinë dhe jetën e tyre në rrugën e Allahut dhe ata që u dhanë strehim e i ndihmuan, janë miq e mbrojtës për njëri-tjetrin. Por, ata që besojnë e që nuk kanë mërguar, nuk të kërkohet t’i mbrosh, derisa të mërgojnë. E, nëse ju luten që t’i ndihmoni për çështjen e fesë, atëherë jeni të detyruar t’u dilni në ndihmë, përveçse (kur ju duhet të luftoni) kundër një populli me të cilin keni marrëveshje. Allahu e sheh mirë atë që veproni.');
INSERT INTO "hasan_efendi" VALUES(8,73,'8:73','Kurse jobesimtarët janë miq e mbrojtës të njëri-tjetrit. Nëse nuk veproni ashtu siç jeni urdhëruar , do të bëhet ngatërresë dhe shkatërrim i madh në Tokë.');
INSERT INTO "hasan_efendi" VALUES(8,74,'8:74','Ata që besojnë, mërgojnë e luftojnë për çështjen e Allahut dhe ata që u japin strehim atyre e i ndihmojnë, pa dyshim, janë besimtarë të vërtetë; për ta ka falje dhe shpërblim bujar.');
INSERT INTO "hasan_efendi" VALUES(8,75,'8:75','Edhe ata që u bënë besimtarë më vonë, që mërguan dhe luftuan bashkë me ju, janë vëllezërit tuaj; ndonëse, sipas Librit të Allahut, besimtarët që kanë lidhje farefisnore, janë më të afërt për njëri-tjetrin (në ndarjen e trashëgimisë). Me të vërtetë, Allahu është i Dijshëm për çdo gjë.');
INSERT INTO "hasan_efendi" VALUES(9,1,'9:1','Lirim (i deklaruar) nga ana e Allahut dhe të Dërguarit të Tij prej çdo detyrimi ndaj idhujtarëve me të cilët kishit bërë marrëveshje.');
INSERT INTO "hasan_efendi" VALUES(9,2,'9:2','“Udhëtoni nëpër botë ju (idhujtarë) edhe katër muaj (të sigurt), por ta dini se nuk do të mund t’i shpëtoni Allahut dhe se Allahu do t’i poshtërojë mohuesit”;');
INSERT INTO "hasan_efendi" VALUES(9,3,'9:3','Ja shpallja e Allahut dhe e të Dërguarit të Tij drejtuar njerëzve në ditën e haxhit të madh: “Allahu dhe i Dërguari i Tij shkëpusin çdo detyrim ndaj idhujtarëve”. Nëse ju (idhujtarë) pendoheni, është më mirë për ju; por, nëse largoheni (nga pendimi), ta dini se nuk do të mund t’i shpëtoni Allahut! Njoftoji mohuesit për dënimin e dhembshëm (që i pret).');
INSERT INTO "hasan_efendi" VALUES(9,4,'9:4','Përjashtim bëjnë ata idhujtarë, me të cilët keni lidhur marrëveshje e që nuk ju kanë shkelur asgjë prej saj dhe që nuk kanë ndihmuar askënd kundër jush. Respektojeni marrëveshjen me ta deri në fund. Me të vërtetë, Allahu i do ata që (i) frikësohen (Atij).');
INSERT INTO "hasan_efendi" VALUES(9,5,'9:5','Kur të kalojnë muajt e shenjtë (në të cilët ndalohet lufta), atëherë i vritni idhujtarët kudo që t’i ndeshni, i zini robër, i rrethoni dhe u bëni pusi kudo! Por, nëse pendohen, falin namazin dhe japin zekatin, atëherë i lini të qetë! Vërtet, Allahu është Falës dhe Mëshirëplotë.');
INSERT INTO "hasan_efendi" VALUES(9,6,'9:6','Nëse ndonjë nga idhujtarët kërkon mbrojtje nga ti (Muhamed), mbroje, në mënyrë që të dëgjojë fjalën e Allahut, e pastaj përcille në vendin e tij të sigurt. Kështu duhet, sepse ata janë njerëz që nuk dinë.');
INSERT INTO "hasan_efendi" VALUES(9,7,'9:7','Si mund të kenë idhujtarët besëlidhje me Allahun dhe të Dërguarin e Tij, me përjashtim të atyre me të cilët keni lidhur ju marrëveshje te Xhamia e Shenjtë?! Sa kohë që ata t’i përmbahen marrëveshjes - përmbajuni edhe ju, sepse Allahu i do ata që (i) frikësohen (Atij).');
INSERT INTO "hasan_efendi" VALUES(9,8,'9:8','Si (mund t’u besoni) kur, nëse ju mundin, ata nuk e respektojnë as farefisninë, as marrëveshjen? Ata ju kënaqin me fjalë, ndërsa zemrat e tyre ju kundërshtojnë; shumica e tyre janë të ligj.');
INSERT INTO "hasan_efendi" VALUES(9,9,'9:9','Ata i shesin fjalët e Allahut për një vlerë të vogël dhe i pengojnë njerëzit nga Rruga e Tij. Vërtet të shëmtuara janë veprat e tyre!');
INSERT INTO "hasan_efendi" VALUES(9,10,'9:10','Ndaj besimtarëve, ata nuk respektojnë as farefisni, as besëlidhje; këta janë tejkaluesit e kufirit (në të këqija).');
INSERT INTO "hasan_efendi" VALUES(9,11,'9:11','Megjithatë, nëse ata pendohen, falin namazin dhe japin zekatin, atëherë janë vëllezërit tuaj në fe. Kështu ua shpjegojmë shpalljet njerëzve që dinë.');
INSERT INTO "hasan_efendi" VALUES(9,12,'9:12','Por, nëse ata e prishin betimin pas lidhjes së marrëveshjes dhe sulmojnë fenë tuaj, atëherë luftoni kundër krerëve të mosbesimit - sepse ata nuk kanë besë - që të mund të frenohen (nga të këqijat).');
INSERT INTO "hasan_efendi" VALUES(9,13,'9:13','Vallë, a nuk do të luftoni kundër atyre që e kanë shkelur besëlidhjen, janë përpjekur ta dëbojnë të Dërguarin dhe kanë filluar të parët t’ju sulmojnë?! Vallë, nga ata keni frikë? Allahu e meriton më shumë se ata që t’i frikësoheni, nëse jeni besimtarë!');
INSERT INTO "hasan_efendi" VALUES(9,14,'9:14','Luftoni kundër tyre, që Allahu t’i dënojë ata me duart tuaja, t’i poshtërojë, t’ju ndihmojë juve kundër tyre, t’i shërojë shpirtrat e besimtarëve');
INSERT INTO "hasan_efendi" VALUES(9,15,'9:15','dhe ta largojë hidhërimin nga zemrat e tyre! Allahu ia pranon pendimin kujt të dojë. Allahu është i Gjithëdijshëm dhe i Urtë.');
INSERT INTO "hasan_efendi" VALUES(9,16,'9:16','Vërtet mendoni se do të liheni të qetë para se Allahu t’i ketë njohur ata midis jush që kanë luftuar dhe që nuk kanë kërkuar aleatë të tjerë, përveç Allahut, të Dërguarit të Tij dhe besimtarëve?! Allahu e di mirë se çfarë bëni.');
INSERT INTO "hasan_efendi" VALUES(9,17,'9:17','Nuk u takon idhujtarëve të përkujdesen për faltoret e Allahut, përderisa edhe ata vetë dëshmojnë se janë jobesimtarë. Të kota do të jenë vepra e tyre dhe në zjarrin e Xhehenemit do të qëndrojnë përgjithmonë.');
INSERT INTO "hasan_efendi" VALUES(9,18,'9:18','Faltoret e Allahut i vizitojnë dhe i mirëmbajnë vetëm ata që besojnë Allahun dhe Ditën e Fundit, që falin namazin dhe japin zekatin e që nuk i frikësohen askujt, përveç Allahut. Sigurisht që këta janë në rrugë të drejtë.');
INSERT INTO "hasan_efendi" VALUES(9,19,'9:19','Vërtet mendoni se ai që u jep ujë haxhilerëve dhe mirëmban Xhaminë e Shenjtë është njësoj me atë që beson në Allahun dhe Ditën e Fundit dhe që lufton në rrugën e Allahut?! Këta nuk janë të njëjtë para Allahut. Ai nuk i udhëzon në rrugë të drejtë njerëzit keqbërës.');
INSERT INTO "hasan_efendi" VALUES(9,20,'9:20','Ata që besojnë, mërgojnë dhe luftojnë në rrugën e Allahut me pasurinë e tyre dhe jetën e tyre, janë në shkallë më të lartë tek Allahu. Pikërisht këta janë të fituarit.');
INSERT INTO "hasan_efendi" VALUES(9,21,'9:21','Zoti i tyre u jep lajmin e mirë për mëshirën dhe miratimin e Tij, si dhe për kopshtet ku do të ketë kënaqësi të përhershme për ta');
INSERT INTO "hasan_efendi" VALUES(9,22,'9:22','dhe ku do të qëndrojnë përgjithmonë. Me të vërtetë, vetëm tek Allahu është shpërblimi i madh.');
INSERT INTO "hasan_efendi" VALUES(9,23,'9:23','O besimtarë! Mos i pranoni për mbrojtës baballarët dhe vëllezërit tuaj, nëse ata e duan mosbesimin më shumë sesa besimin. Kushdo prej jush që i merr ata për mbrojtës, është keqbërës.');
INSERT INTO "hasan_efendi" VALUES(9,24,'9:24','Thuaj: “Nëse etërit tuaj, bijtë tuaj, vëllezërit tuaj, gratë tuaja, farefisi juaj, pasuria juaj që e keni fituar, tregtia për të cilën keni frikë se nuk do të shkojë mirë dhe shtëpitë ku e ndieni veten rehat, janë më të dashura për ju sesa Allahu, i Dërguari i Tij dhe lufta në rrugën e Tij, atëherë pritni derisa Allahu të sjellë vendimin (dënimin) e Tij. Allahu nuk e udhëzon në rrugën e drejtë popullin e pabindur.”');
INSERT INTO "hasan_efendi" VALUES(9,25,'9:25','Në të vërtetë, Allahu ju ka ndihmuar në shumë beteja, si dhe në ditën e (luftës së) Hunejnit, kur ju ju mahniti numri juaj i madh. Por ai numër nuk ju solli dobi aspak, sepse toka ju duk juve e ngushtë, edhe pse është e gjerë dhe kthyet shpinën duke ia mbathur.');
INSERT INTO "hasan_efendi" VALUES(9,26,'9:26','Pastaj, Allahu zbriti qetësinë e Tij mbi të Dërguarin e Vet dhe mbi besimtarët, si dhe zbriti luftëtarë (engjëj), të cilët ju nuk i patë dot e i dënoi jobesimtarët. Kështu u “shpërblyen” mohuesit.');
INSERT INTO "hasan_efendi" VALUES(9,27,'9:27','Pas kësaj, Allahu e pranon pendimin për kë të dojë. Allahu është Falës dhe Mëshirëplotë.');
INSERT INTO "hasan_efendi" VALUES(9,28,'9:28','O besimtarë! Në të vërtetë, idhujtarët janë të papastër (për shkak të besimit të kotë), prandaj të mos i afrohen më Xhamisë së Shenjtë pas këtij viti. Dhe mos kini frikë nga varfëria, sepse, po deshi, Allahu ju bën të pasur me dhuntitë e veta. Vërtet, Allahu është i Gjithëdijshëm dhe i Urtë.');
INSERT INTO "hasan_efendi" VALUES(9,29,'9:29','Luftojini ata që nuk besojnë në Allahun e as në Ditën e Kiametit, që nuk e marrin për të ndaluar atë që e ka ndaluar Allahu dhe i Dërguari i Tij, dhe ata, midis atyre që u është dhënë Libri, që nuk e pranojnë fenë e vërtetë, derisa të japin xhizjen (taksën) me dorën e tyre, duke qenë të përulur.');
INSERT INTO "hasan_efendi" VALUES(9,30,'9:30','Hebrenjtë thonë: “Uzejri është i biri i Allahut”, të krishterët thonë: “Mesihu është i biri i Allahut”. Këto janë fjalët e tyre, nga goja e tyre, i imitojnë fjalët e jobesimtarëve të mëparshëm. I vraftë Allahu! Si po shmangen (nga e vërteta)!');
INSERT INTO "hasan_efendi" VALUES(9,31,'9:31','Në vend të Allahut, ata kanë zgjedhur për zot rabinët dhe murgjit e tyre, si dhe Mesinë, të birin e Merjemes, ndërkohë që janë urdhëruar të adhurojnë vetëm një Zot, përveç të Cilit nuk ka tjetër që meriton të adhurohet. I lartësuar qoftë Ai mbi gjithçka që ia shoqërojnë (në adhurim).');
INSERT INTO "hasan_efendi" VALUES(9,32,'9:32','Ata dëshirojnë ta fikin dritën (fenë) e Allahut me gojën e tyre, por Allahu ka vendosur vetëm që ta përsosë dritën e Vet, edhe sikur mohuesit ta urrejnë këtë gjë.');
INSERT INTO "hasan_efendi" VALUES(9,33,'9:33','(Allahu) Është Ai që e ka dërguar të Dërguarin e Tij me udhërrëfimin (Kuranin) dhe fenë e së vërtetës, për ta lartësuar atë mbi të gjitha fetë , edhe sikur idhujtarët ta urrejnë këtë gjë.');
INSERT INTO "hasan_efendi" VALUES(9,34,'9:34','O besimtarë! Në të vërtetë, shumë priftërinj dhe murgj përlajnë pasurinë e njerëzve pa të drejtë dhe i largojnë ata nga rruga e Allahut. Ata që grumbullojnë ar dhe argjend dhe nuk e shpenzojnë për hir të Allahut, paralajmëroji me dënim të dhembshëm.');
INSERT INTO "hasan_efendi" VALUES(9,35,'9:35','Do të vijë dita, kur (thesaret e mbledhura) do të digjen në zjarrin e Xhehenemit dhe me to do të damkoset balli, anët dhe shpina e tyre e do t’u thuhet: “Kjo është ajo që keni grumbulluar për veten; pra, shijoni atë që grumbulluat!”');
INSERT INTO "hasan_efendi" VALUES(9,36,'9:36','Vërtet, numri i muajve tek Allahu është dymbëdhjetë, sipas Librit të Allahut, që prej ditës kur krijoi qiejt dhe Tokën. Katër prej tyre janë të shenjtë, sipas besimit të drejtë. Prandaj, mos i bëni vetes dëm, duke i shkelur këta muaj! Megjithatë, luftoni të gjithë kundër idhujtarëve, ashtu si luftojnë ata të gjithë kundër jush. Ta dini se Allahu është me ata që ruhen nga gjynahet.');
INSERT INTO "hasan_efendi" VALUES(9,37,'9:37','Vërtet, muaji shtesë është vetëm shtesë në mosbesim. Për shkak të tij jobesimtarët shkojnë në humbje: një vit e quajnë të lejueshëm e vitin tjetër, të ndaluar, për të plotësuar numrin e muajve që i ka ndaluar Allahu. Kështu, ata lejojnë atë që ka ndaluar Allahu. Atyre u duken të bukura veprat e tyre të shëmtuara. Allahu nuk i udhëzon në rrugë të drejtë njerëzit jobesimtarë.');
INSERT INTO "hasan_efendi" VALUES(9,38,'9:38','O besimtarë! Çfarë keni që ngurroni e mbërtheheni fort për toke kur ju thuhet: “Dilni në luftë, në rrugën e Allahut?! A jeni të kënaqur me jetën e kësaj bote, në vend të së amshueshmes?! Kënaqësia e kësaj bote, në krahasim me atë të botës tjetër, është shumë e vogël.');
INSERT INTO "hasan_efendi" VALUES(9,39,'9:39','Nëse nuk shkoni në luftë, Ai do t’ju ndëshkojë me dënim të dhembshëm dhe do t’ju zëvendësojë me një popull tjetër; Atij ju nuk do të mund t’i bëni dëm aspak, se Allahu është i Fuqishëm për çdo gjë.');
INSERT INTO "hasan_efendi" VALUES(9,40,'9:40','Nëse ju nuk e ndihmoni atë (Muhamedin a.s.), Allahu tashmë e ka ndihmuar ditën kur atë e dëbuan mohuesit vetë të dytin, kur ishin në shpellë e ai i thoshte shokut të vet (Ebu Bekrit): “Mos u mërzit, se Allahu është vërtet me ne!” Atëherë, Allahu zbriti qetësinë e Vet mbi të, e ndihmoi me një ushtri të padukshme (engjëjsh) dhe bëri që fjala e mosbesimtarëve të jetë më e poshtëruara, kurse Fjala e Allahut të jetë më e lartësuara. Vërtet, Allahu është i Plotfuqishëm dhe i Urtë.');
INSERT INTO "hasan_efendi" VALUES(9,41,'9:41','Dilni në luftë, qofshi të shëndoshë ose të sëmurë dhe luftoni me pasurinë e jetën tuaj në rrugën e Allahut! Kjo është më mirë për ju, veç sikur ta dinit!');
INSERT INTO "hasan_efendi" VALUES(9,42,'9:42','Po të ishte përfitimi afër dhe rruga jo aq e gjatë, ata do të vinin pas teje. Por iu duk rrugë e largët. Dhe ata do të betohen në Allahun: “Sikur të kishim mundur, me siguri do të dilnim bashkë me ju;” dhe kështu ata e shkatërrojnë vetveten; Allahu e di se ata janë gënjeshtarë.');
INSERT INTO "hasan_efendi" VALUES(9,43,'9:43','Allahu të fal ty që u lejove atyre të mungojnë, para se ta kuptoje se kush të flet të vërtetën dhe derisa t’i njihje gënjeshtarët.');
INSERT INTO "hasan_efendi" VALUES(9,44,'9:44','Nuk do të të kërkojnë leje për të mos luftuar me pasurinë dhe jetën e tyre, ata që besojnë në Allahun dhe Ditën e Kiametit. E Allahu i di mirë ata që ruhen nga gjynahet.');
INSERT INTO "hasan_efendi" VALUES(9,45,'9:45','Ty do të kërkojnë leje vetëm ata që nuk besojnë në Allahun dhe Ditën e Kiametit dhe që zemrat e tyre dyshojnë. Ata luhaten në dyshimin e vet.');
INSERT INTO "hasan_efendi" VALUES(9,46,'9:46','Sikur ata të kishin dashur të dilnin me ty, do të ishin përgatitur; por Allahu nuk e pëlqeu vajtjen e tyre, prandaj i pengoi dhe atyre iu tha: “Rrini me ata (të paaftë për luftë) që do të ulen (në shtëpi)!”');
INSERT INTO "hasan_efendi" VALUES(9,47,'9:47','Sikur të kishin dalë me ju, ata do t’ju shtonin juve vetëm të keqe, do të shtinin ngatërresa midis jush dhe do të ngjallnin përçarje, sepse edhe ndër ju ka nga ata që ua vënë veshin. Por Allahu i njeh mirë keqbërësit.');
INSERT INTO "hasan_efendi" VALUES(9,48,'9:48','Ata edhe më parë përpiqeshin të të nxirrnin ngatërresa dhe t’i pështjellonin punët e tua, deri sa erdhi e Vërteta (fitorja) dhe u tregua e qartë feja e Allahut, gjë që ata e urrenin.');
INSERT INTO "hasan_efendi" VALUES(9,49,'9:49','Disa prej atyre (hipokritëve) thonë: “Më lejo (të mungoj në luftë) dhe mos më vër në sprovë!” Ja, ata ranë pikërisht në sprovë. Me të vërtetë, Xhehenemi i përfshin mohuesit.');
INSERT INTO "hasan_efendi" VALUES(9,50,'9:50','Nëse ty të vjen ndonjë e mirë, ata hidhërohen; nëse të godet ndonjë e keqe, ata thonë: “Sa mirë që i morëm masat që më parë” dhe largohen të gëzuar.');
INSERT INTO "hasan_efendi" VALUES(9,51,'9:51','Thuaj: “Do të na godasë vetëm ajo që na ka caktuar Allahu; Ai është Mbrojtësi ynë dhe vetëm tek Allahu le të mbështeten besimtarët!”');
INSERT INTO "hasan_efendi" VALUES(9,52,'9:52','Thuaj: “Çfarë prisni për ne, përveçse njërës prej dy të mirave (fitores ose martirizimit)? Ndërsa ne po presim që Allahu t’ju godasë me dënim nga ana e Tij ose me duart tona. Pra, pritni se edhe ne me ju do të presim”.');
INSERT INTO "hasan_efendi" VALUES(9,53,'9:53','Thuaj: “Shpenzoni me hir a me pahir, nga ju nuk do të pranohet kurrsesi, sepse ju jeni vërtet një popull i mbrapshtë.”');
INSERT INTO "hasan_efendi" VALUES(9,54,'9:54','Ndihmesat e tyre nuk do të pranohen, ngase ata janë njerëz që nuk besojnë në Allahun dhe të Dërguarin e Tij, namazit i afrohen vetëm në mënyrë të ngathët dhe nuk shpenzojnë (nga pasuria), veçse në mënyrë përbuzëse.');
INSERT INTO "hasan_efendi" VALUES(9,55,'9:55','Mos u josh (o Muhamed) nga pasuria dhe fëmijët e tyre, sepse nëpërmjet tyre Allahu kërkon t’i ndëshkojë ata në këtë jetë, kështu që t’u dalë shpirti duke qenë jobesimtarë.');
INSERT INTO "hasan_efendi" VALUES(9,56,'9:56','Ata betohen për Allahun se janë në fenë tuaj, ndërkohë që nuk janë prej jush, por janë njerëz frikacakë (për të treguar si janë në të vërtetë).');
INSERT INTO "hasan_efendi" VALUES(9,57,'9:57','Sikur ata të gjenin ndonjë strehim, shpellë apo vend mbrojtjeje, do të niseshin atje me nxitim.');
INSERT INTO "hasan_efendi" VALUES(9,58,'9:58','Ka edhe të tillë që të qortojnë për shkak të ndarjes së lëmoshës. Nëse atyre u jepet ndonjë pjesë, ata kënaqen, por, nëse nuk u jepet, përnjëherë zemërohen.');
INSERT INTO "hasan_efendi" VALUES(9,59,'9:59','Sikur ata të kënaqeshin me atë që u ka dhënë Allahu dhe i Dërguari i Tij, do të thoshin: “Na mjafton Allahu! Allahu do të na japë nga mirësitë e Veta; edhe i Dërguari i Tij. Ne vetëm tek Allahu i varim shpresat”.');
INSERT INTO "hasan_efendi" VALUES(9,60,'9:60','Lëmosha e zekatit është vetëm për të varfrit, për nevojtarët, për ata që janë ngarkuar me mbledhjen dhe administrimin e tij, për ata të cilëve u duhet fituar zemra (në Islam), për lirimin e skllevërve, për të rënduarit nga borxhet, për (luftëtarët në) rrugën e Allahut dhe për udhëtarët e mbetur rrugëve. Këto janë detyrat e vëna prej Allahut. Vërtet, Allahu është i Gjithëdijshëm dhe i Urtë.');
INSERT INTO "hasan_efendi" VALUES(9,61,'9:61','Ka të atillë që e shqetësojnë të Dërguarin dhe thonë: “Ai dëgjon çdo gjë që i thuhet”. Thuaj (o Muhamed): “Ai dëgjon atë që është e mirë për ju, beson në Allahun dhe u beson besimtarëve. Ai është mëshirë për ata midis jush që besojnë. Kurse ata që e lëndojnë të Dërguarin e Allahut, i pret një ndëshkim i dhembshëm.”');
INSERT INTO "hasan_efendi" VALUES(9,62,'9:62','Ata ju betohen për Allahun për t’ju kënaqur, por është më e drejtë të kënaqin Allahun dhe të Dërguarin e Tij, nëse janë besimtarë.');
INSERT INTO "hasan_efendi" VALUES(9,63,'9:63','Vallë, a nuk e dinë ata, se për atë që e kundërshton Allahun dhe të Dërguarit e Tij, është përgatitur zjarri i Xhehenemit, në të cilin do të qëndrojë përherë? Ky është poshtërimi më i madh!');
INSERT INTO "hasan_efendi" VALUES(9,64,'9:64','Hipokritët druajnë se do të shpallet ndonjë sure e cila do t’i lajmërojë se ç’ka në zemrat e tyre. Thuaj: “Talluni po deshët! Allahu sigurisht që do ta nxjerrë në pah atë së cilës ju i druheni”.');
INSERT INTO "hasan_efendi" VALUES(9,65,'9:65','Nëse i pyet, ata do të të përgjigjen: “Ne vetëm po bënim shaka dhe po luanim”. Thuaj: “A mos vallë me Allahun, shpalljet dhe të Dërguarin e Tij po talleni?”');
INSERT INTO "hasan_efendi" VALUES(9,66,'9:66','Mos u shfajësoni! Ju e mohuat fenë, pasi patët besuar. Nëse Ne i falim disa prej jush (për shkak se u penduan), do t’i ndëshkojmë të tjerët, ngase janë fajtorë (nuk u penduan për talljen që bënë).');
INSERT INTO "hasan_efendi" VALUES(9,67,'9:67','Hipokritët dhe hipokritet janë të ngjashëm me njëri - tjetrin: kërkojnë që të bëhen vepra të këqija e pengojnë të mirat dhe janë dorështrënguar. Ata e kanë harruar Allahun, por edhe Ai i ka harruar ata. Me të vërtetë, hipokritët janë keqbërës.');
INSERT INTO "hasan_efendi" VALUES(9,68,'9:68','Allahu u ka premtuar hipokritëve, hipokriteve dhe jobesimtarëve zjarrin e Xhehenemit, ku do të qëndrojnë përgjithmonë; ai do t’iu mjaftojë atyre! Allahu i ka mallkuar dhe ata do të kenë dënim të vazhdueshëm.');
INSERT INTO "hasan_efendi" VALUES(9,69,'9:69','(Ju hipokritë jeni) si ata që kanë qenë para jush! Ata kanë qenë më të fortë se ju, kanë pasur më shumë pasuri dhe fëmijë. Ata janë kënaqur me pjesën e vet të kësaj bote, por edhe ju po kënaqeni me pjesën tuaj siç janë kënaqur edhe ata; edhe ju jeni dhënë pas të këqijave, ashtu siç ishin dhënë ata. Veprat e tyre do të zhduken në këtë botë dhe në tjetrën; ata janë të shkatërruar.');
INSERT INTO "hasan_efendi" VALUES(9,70,'9:70','A nuk kanë dëgjuar ata për popujt që kanë qenë më përpara: për popullin e Nuhut, për fiset Ad dhe Themud, për popullin e Ibrahimit, për banorët e Medjenit dhe për qytetet e përmbysura (të popullit të Lutit)? Të dërguarit e tyre u sollën shenja të qarta. Allahu nuk u bëri padrejtësi, por ata i bënë padrejtësi vetvetes.');
INSERT INTO "hasan_efendi" VALUES(9,71,'9:71','Besimtarët dhe besimtaret janë miq për njëri-tjetrin. Ata urdhërojnë që të bëhen vepra të mira dhe i ndalojnë të këqijat; falin namazin, japin zekatin dhe i binden Allahut dhe të Dërguarit të Tij. Këta janë ata që do t’i mëshirojë Allahu. Se, Allahu me të vërtetë, është i Plotfuqishëm dhe i Urtë.');
INSERT INTO "hasan_efendi" VALUES(9,72,'9:72','Allahu u ka premtuar besimtarëve dhe besimtareve kopshte, nëpër të cilat rrjedhin lumenj dhe ku do të banojnë përgjithmonë, si dhe pallate të mrekullueshme në Xhenetin e Adnit. Por kënaqësia prej Allahut është shpërblimi më i madh. Kjo është fitorja madhështore.');
INSERT INTO "hasan_efendi" VALUES(9,73,'9:73','O i Dërguar! Lufto kundër mohuesve dhe hipokritëve dhe bëhu i ashpër ndaj tyre! Strehimi i tyre është Xhehenemi; sa vendbanim i shëmtuar.');
INSERT INTO "hasan_efendi" VALUES(9,74,'9:74','Hipokritët betohen për Allahun se nuk thanë asgjë (të keqe). Por, në të vërtetë, ata thanë fjalën e mosbesimit, mohuan pas (pranimit të) Islamit dhe synuan të bënin gjëra të paarritshme. Ata u bënë keqdashës, vetëm sepse Allahu dhe i Dërguari i Tij u kishin dhënë (besimtarëve) nga të mirat e Tij. Nëse pendohen, do të jetë më mirë për ta; por, nëse kthejnë shpinën, Allahu do t’i ndëshkojë me dënim të rëndë në këtë jetë dhe në tjetrën dhe ata nuk do të kenë kurrfarë mbrojtësi apo ndihmësi në Tokë.');
INSERT INTO "hasan_efendi" VALUES(9,75,'9:75','Ka nga ata (hipokritë) që kanë bërë besëlidhjen me Allahun, duke thënë: “Nëse Ai na jep nga dhuntitë e Tij, sigurisht që do të japim lëmoshë dhe sigurisht që do të bëhemi njerëz të mirë”!');
INSERT INTO "hasan_efendi" VALUES(9,76,'9:76','Por kur Ai u dha atyre nga dhuntitë e Veta, ata u bënë koprracë dhe kthyen shpinën, duke u larguar.');
INSERT INTO "hasan_efendi" VALUES(9,77,'9:77','Prandaj, Ai ua lidhi hipokrizinë në zemrat e tyre, deri në ditën kur do të dalin para Tij, sepse e shkelën premtimin dhënë Allahut. Ata e kishin zakon gënjeshtrën.');
INSERT INTO "hasan_efendi" VALUES(9,78,'9:78','Vallë, a nuk e dinë ata se Allahu i di të fshehtat e tyre dhe bisedat e fshehta?! Në të vërtetë, Allahu është Ai që i di të gjitha fshehtësitë.');
INSERT INTO "hasan_efendi" VALUES(9,79,'9:79','Sa për ata (hipokritë) që përqeshin besimtarët që japin lëmoshë me vullnet dhe tallen me ata që nuk janë në gjendje të japin gjë përveç (fryteve të) punës së tyre, Allahu do të tallet me ta. Ata do të kenë dënim të dhembshëm.');
INSERT INTO "hasan_efendi" VALUES(9,80,'9:80','U lute për ta apo nuk u lute (o Muhamed), Allahu nuk do t’i falë, edhe sikur ti të kërkosh falje për ta shtatëdhjetë herë! Kjo sepse ata e mohuan Allahun dhe të Dërguarin e Tij. Allahu nuk i udhëzon në rrugë të mbarë njerëzit e pabindur.');
INSERT INTO "hasan_efendi" VALUES(9,81,'9:81','Ata që mbetën në shtëpitë e tyre, pas (daljes për luftë të) të Dërguarit të Allahut, u gëzuan; nuk u pëlqente të luftonin në rrugën e Allahut me pasurinë dhe jetën e tyre dhe thanë: “Mos shkoni në luftë, nëpër vapë!” Thuaju: “Zjarri i Xhehenemit është edhe më i nxehtë!” Veç sikur të kuptonin!');
INSERT INTO "hasan_efendi" VALUES(9,82,'9:82','Le të qeshin pak (në këtë botë) e të qajnë shumë (në botën tjetër) si dënim për atë që kanë bërë.');
INSERT INTO "hasan_efendi" VALUES(9,83,'9:83','Dhe, nëse të kthen Allahu (pas betejës) te një grup i hipokritëve e ata kërkojnë leje nga ti që të shkojnë me ty në luftë, thuaju (o Muhamed): “Kurrë nuk do të vini me mua në luftë dhe kurrë nuk do të luftoni me mua kundër armikut! Ju ishit të kënaqur kur nuk erdhët herën e parë, prandaj rrini me ata që ngelin pas”.');
INSERT INTO "hasan_efendi" VALUES(9,84,'9:84','Askujt prej hipokritëve, kur të vdesin, mos ia fal xhenazen, as mos rri afër varrit të hipokritit, sepse ata e kanë mohuar Allahun dhe të Dërguarin e Tij dhe qëndruan të pabindur deri në vdekje.');
INSERT INTO "hasan_efendi" VALUES(9,85,'9:85','Mos u josh (o Muhamed) nga pasuria dhe fëmijët e tyre, sepse nëpërmjet tyre Allahu kërkon t’i ndëshkojë ata në këtë jetë, në mënyrë që t’u dalë shpirti duke qenë jobesimtarë.');
INSERT INTO "hasan_efendi" VALUES(9,86,'9:86','Dhe kur shpallet një sure që thotë: “Besoni Allahun dhe luftoni së bashku me të Dërguarin e Tij!” pasanikët e tyre (hipokritëve) kërkojnë leje, duke thënë: “Na lini të rrimë me ata që nuk shkojnë në luftë!”');
INSERT INTO "hasan_efendi" VALUES(9,87,'9:87','Kënaqen të jenë me ata që nuk shkojnë në luftë, zemrat e tyre janë të vulosura e ata nuk kuptojnë!');
INSERT INTO "hasan_efendi" VALUES(9,88,'9:88','Por, i Dërguari dhe ata që besojnë në të, luftojnë me pasurinë e tyre dhe jetën e tyre. Për ata do të ketë çdo të mirë dhe pikërisht ata do të jenë të shpëtuarit.');
INSERT INTO "hasan_efendi" VALUES(9,89,'9:89','Allahu u ka përgatitur atyre kopshtet e Xhenetit, nëpër të cilat rrjedhin lumenj dhe ku do të banojnë përgjithmonë. Kjo është fitorja madhështore.');
INSERT INTO "hasan_efendi" VALUES(9,90,'9:90','Disa beduinë erdhën me shfajësime, duke të të kërkuar leje, që të mos shkonin në luftë. Dhe munguan ata që e gënjyen Allahun dhe të Dërguarin e Tij. Ata prej atyre që mohuan, me të vërtetë, do t’i godasë një dënim i dhembshëm.');
INSERT INTO "hasan_efendi" VALUES(9,91,'9:91','Nuk është gjynah të mos shkojnë në luftë të dobëtit, as të sëmurët, as ata që nuk mund të sigurojnë shpenzimet, nëse janë të sinqertë me Allahun dhe të Dërguarin e Tij. Nuk ka vend për vërejtje ndaj punëmirëve. Allahu është Falës dhe Mëshirëplotë.');
INSERT INTO "hasan_efendi" VALUES(9,92,'9:92','(Nuk ka gjynah) as për ata të cilët, kur vijnë te ti për të kërkuar kafshë shalimi e ti u thua: “Unë nuk mund t’ju siguroj kafshë shalimi”, kthehen me sytë e tyre që u rrjedhin lot, ngaqë nuk mundën të gjenin gjë për të dhënë (në rrugë të Allahut).');
INSERT INTO "hasan_efendi" VALUES(9,93,'9:93','Por ka vend për t’u qortuar ata, që kërkojnë leje nga ti, duke qenë të pasur. Ata kënaqen të rrinë me ata që nuk shkojnë në luftë. Allahu ua ka vulosur atyre zemrat, por ata nuk dinë.');
INSERT INTO "hasan_efendi" VALUES(9,94,'9:94','Kur të ktheheni tek ata, do t’ju vijnë duke u shfajësuar. Thuaju: “Mos kërkoni falje! Sido që të jetë, ne nuk ju besojmë, sepse Allahu na ka lajmëruar për qëllimet tuaja. Me siguri, Allahu dhe i Dërguari i Tij do t’i shohin veprat tuaja; pastaj ju do të ktheheni tek Ai, që i di gjërat e padukshme dhe të dukshme e do t’ju lajmërojë për atë që keni bërë.');
INSERT INTO "hasan_efendi" VALUES(9,95,'9:95','Kur të ktheheni tek ata, do t’ju betohen për Allahun që t’i lini. Largohuni prej tyre! Ata janë të ndyrë dhe strehimi i tyre është Xhehenemi. Ky është dënimi për veprat e tyre.');
INSERT INTO "hasan_efendi" VALUES(9,96,'9:96','Ata do t’ju betohen që të jeni të kënaqur me ata. Nëse ju do të jeni të kënaqur me ata, dijeni se Allahu sigurisht që nuk është i kënaqur me popullin e pabindur.');
INSERT INTO "hasan_efendi" VALUES(9,97,'9:97','Beduinët janë mohuesit dhe hipokritët më të mëdhenj dhe më të prirët për të mos njohur kufijtë që Allahu ia ka shpallur të Dërguarit të Tij. Allahu është i Gjithëdijshëm dhe i Urtë.');
INSERT INTO "hasan_efendi" VALUES(9,98,'9:98','Ndër beduinët, ka të atillë, që shpenzimin në rrugë të Allahut, e quajnë si gjobë dhe presin që ju t’ju godasë ndonjë fatkeqësi. Le t’i godasë ata radha e fatkeqësisë! Allahu dëgjon dhe di gjithçka.');
INSERT INTO "hasan_efendi" VALUES(9,99,'9:99','Po ka nga beduinët të atillë që besojnë Allahun dhe Ditën e Kiametit dhe shpenzimin (në rrugë të Allahut) e quajnë si mjet për t’iu afruar Allahut dhe për të merituar bekimin e të Dërguarit. Dhe vërtet që kjo do t’u sjellë afrimin: Allahu do t’i pranojë ata në mëshirën e Vet. Njëmend, Allahu është Falës i madh dhe Mëshirëplotë.');
INSERT INTO "hasan_efendi" VALUES(9,100,'9:100','Sa u përket besimtarëve të parë, prej muhaxhirëve dhe ensarëve , Allahu është i kënaqur me ata dhe me të gjithë të tjerët që i pasuan me vendosmëri e përkushtim në besim; edhe ata janë të kënaqur me Atë. Allahu ka përgatitur për ata kopshte nëpër të cilat rrjedhin lumenj dhe ku do të jetojnë përgjithmonë. Kjo është fitorja madhështore!');
INSERT INTO "hasan_efendi" VALUES(9,101,'9:101','Ka beduinë në mjedisin tuaj, që janë hipokritë, por ka edhe ndër banorët e Medinës të atillë që janë këmbëngulës në hipokrizi. Ti nuk i di ata. Ne i dimë. Ne do t’i dënojmë dy herë e pastaj, do të kthehen në dënimin e madh (në Xhehenem).');
INSERT INTO "hasan_efendi" VALUES(9,102,'9:102','Ndërkaq, disa të tjerë i pranojnë gjynahet e veta, se i kanë përzier veprat e mira me të këqijat. Ka gjasë që Allahu t’ua pranojë pendimin. Vërtet, Allahu është Falës i madh dhe Mëshirëplotë.');
INSERT INTO "hasan_efendi" VALUES(9,103,'9:103','Merr nga pasuria e tyre lëmoshë me të cilën t’i pastrosh ata dhe t’ua rrisësh veprat e mira! Lutu për ta, sepse lutja jote është vërtet qetësim për ata. Allahu dëgjon dhe di gjithçka.');
INSERT INTO "hasan_efendi" VALUES(9,104,'9:104','A nuk e dinë ata se Allahu është Ai që pranon pendimin dhe bamirësinë nga robërit e Vet dhe se Ai është Pranuesi i pendimeve dhe Mëshirëploti?');
INSERT INTO "hasan_efendi" VALUES(9,105,'9:105','Thuaj: “Punoni, se Allahu, i Dërguari i Tij dhe besimtarët do të shohin punën tuaj. Së shpejti do të ktheheni tek Ai që di të dukshmen dhe të padukshmen e Ai do t’ju njoftojë për atë që keni bërë.”');
INSERT INTO "hasan_efendi" VALUES(9,106,'9:106','Ka edhe të tjerë që janë lënë në pritje të vendimit të Allahut, a do t’i dënojë apo do t’ua pranojë pendimin. Allahu është i Gjithëdijshëm dhe i Urtë.');
INSERT INTO "hasan_efendi" VALUES(9,107,'9:107','(Hipokritë) janë edhe ata që ndërtuan një xhami për t’u shkaktuar dëm (besimtarëve), për ta forcuar mohimin dhe për të krijuar përçarje në mesin e besimtarëve, duke iu shërbyer ajo si pusi atyre që qysh më parë luftonin kundër Allahut dhe të Dërguarit të Tij. Dhe, vërtet ata betohen: “Ne kishim për qëllim që të bënim vetëm një vepër të mirë”! Por Allahu dëshmon se në të vërtetë ata janë gënjeshtarë.');
INSERT INTO "hasan_efendi" VALUES(9,108,'9:108','Ti në atë xhami kurrë mos fal namaz. Ndërsa xhamia e themeluar në përkushtim (ndaj Allahut) që në ditën e parë, pa dyshim që është më e denjë për të falur namaz në të. Aty ka njerëz që duan të pastrohen. Allahu i do ata që pastrohen shpesh.');
INSERT INTO "hasan_efendi" VALUES(9,109,'9:109','Cili është më i mirë, ai që themelet e godinës së vet i vë në përkushtimin ndaj Allahut dhe në kënaqësinë e Tij apo ai që themelet e godinës së tij i vë në bregun e gërryer dhe që rrokulliset me të në zjarrin e Xhehenemit?! Allahu nuk e udhëzon në rrugë të drejtë popullin keqbërës.');
INSERT INTO "hasan_efendi" VALUES(9,110,'9:110','Godina që ata ndërtuan, mbjell gjithnjë dyshim në zemrat e tyre, derisa zemrat e tyre t’u copëtohen. Allahu është i Gjithëdijshëm, i Urtë.');
INSERT INTO "hasan_efendi" VALUES(9,111,'9:111','Në të vërtetë, Allahu ka blerë nga besimtarët jetën dhe pasurinë e tyre, në këmbim të Xhenetit. Ata luftojnë në rrugën e Allahut, vrasin dhe vriten. (Ky është) premtimi i Tij i vërtetë në Teurat, Ungjill dhe Kuran. E kush i përmbahet besëlidhjes më fort se Allahu? Andaj, gëzojuni kësaj shitjeje! Kjo është fitorja madhështore.');
INSERT INTO "hasan_efendi" VALUES(9,112,'9:112','(Këta janë) ata që pendohen, e adhurojnë Allahun, e falënderojnë Atë, agjërojnë, përkulen (në namaz), bien në sexhde (gjatë namazit), urdhërojnë vepra të mira, pengojnë të këqijat dhe u përmbahen kufijve të caktuar nga Allahu. Jepu lajmin e mirë besimtarëve!');
INSERT INTO "hasan_efendi" VALUES(9,113,'9:113','Nuk i takon të Dërguarit dhe besimtarëve që të kërkojnë falje për idhujtarët, qofshin këta edhe të afërmit e tyre, pasi u është bërë e qartë se ata janë banorë të Xhehenemit.');
INSERT INTO "hasan_efendi" VALUES(9,114,'9:114','Ndërsa lutja e Ibrahimit për faljen e babait të tij, ka qenë vetëm për shkak të një premtimi, që i kishte bërë. Por, kur iu bë e qartë se i ati ishte armik i Allahut, ai u nda prej tij. E megjithatë, Ibrahimi ishte njeri i dhembshur dhe i butë.');
INSERT INTO "hasan_efendi" VALUES(9,115,'9:115','Allahu kurrë nuk e çon në humbje një popull që më parë e ka udhëzuar në rrugë të drejtë, pa ia bërë të njohur se prej çfarë duhet të ruhet. Me të vërtetë, Allahu është i Dijshëm për çdo gjë.');
INSERT INTO "hasan_efendi" VALUES(9,116,'9:116','Njëmend, Allahut i takon sundimi i qiejve dhe i Tokës; Ai e jep jetën dhe vdekjen. Përveç Allahut, ju nuk keni tjetër mbrojtës apo përkrahës.');
INSERT INTO "hasan_efendi" VALUES(9,117,'9:117','Pa dyshim, Allahu e pranoi pendimin e të Dërguarit, të muhaxhirëve dhe të ensarëve, të cilët shkuan pas atij në çastin e vështirë, pasi zemrat e disave prej tyre për një fije qenë afër shmangies (nga detyra), pastaj Allahu pranoi pendimin e tyre . Vërtet, Ai është i Butë dhe i Mëshirshëm me ta.');
INSERT INTO "hasan_efendi" VALUES(9,118,'9:118','Ai ua pranoi pendimin edhe atyre të treve , që mbetën prapa atëherë, kur toka, sado e gjerë, iu duk e ngushtë dhe iu shtrënguan shpirtrat, aq sa u bindën se nuk ka strehim tjetër përveç Allahut e pastaj Allahu u dha mëshirë që të pendohen. Me të vërtetë, Allahu pranon pendimin dhe është Mëshirëplotë.');
INSERT INTO "hasan_efendi" VALUES(9,119,'9:119','O besimtarë! Frikësojuni Allahut dhe bëhuni me ata që janë të sinqertë (në fjalë dhe në vepra)!');
INSERT INTO "hasan_efendi" VALUES(9,120,'9:120','Banorëve të Medinës dhe beduinëve që janë rreth saj, nuk u lejohet të mos ndjekin të Dërguarin e Allahut, as të kursejnë jetën e tyre para jetës së tij. Kjo është kështu, sepse ata që janë në rrugën e Allahut, sa herë që t’i godasë etja, lodhja e uria dhe sa herë që të shkelin në ndonjë vend ku do t’i zemërojnë mohuesit apo t’i gjejë gjë nga ana e armikut, atyre do t’iu shkruhet si vepër e mirë. Me të vërtetë, Allahu nuk do t’ua humbë shpërblimin punëmirëve.');
INSERT INTO "hasan_efendi" VALUES(9,121,'9:121','(Gjithashtu) sa herë që ata të shpenzojnë diçka të vogël a të madhe apo të kapërcejnë çfarëdo lugine (në rrugë të Allahut), kjo do t’u shënohet, në mënyrë që Allahu t’i shpërblejë sipas veprave më të mira që kanë bërë!');
INSERT INTO "hasan_efendi" VALUES(9,122,'9:122','Nuk është mirë që besimtarët të shkojnë në luftë të gjithë njëherësh, por prej çdo bashkësie, një grup të mbetet mbrapa, që të thellohet në mësimin e fesë dhe kështu të këshillojë popullin e vet, mbasi të kthehet nga lufta, që t’i frikësohet Allahut.');
INSERT INTO "hasan_efendi" VALUES(9,123,'9:123','O besimtarë! Luftoni kundër mohuesve që gjenden afër jush dhe le ta ndiejnë ata ashpërsinë tuaj. Dhe, ta dini se Allahu është me ata që ruhen nga gjynahet.');
INSERT INTO "hasan_efendi" VALUES(9,124,'9:124','Kur shpallet ndonjë sure, disa prej atyre (që kanë dyshim dhe hipokrizi) thonë: “Kujt prej jush do t’ia shtojë besimin kjo sure?” Për sa u përket besimtarëve, atyre (kjo sure) ua shton besimin dhe ata gëzohen.');
INSERT INTO "hasan_efendi" VALUES(9,125,'9:125','E sa u përket atyre që kanë sëmundje në zemrat e veta, ajo ua shton edhe më dyshimin që kanë dhe ata vdesin si mohues.');
INSERT INTO "hasan_efendi" VALUES(9,126,'9:126','Vallë, a nuk e shohin ata se çdo vit vihen në provë një ose dy herë e prapëseprapë as nuk pendohen, as nuk e mbledhin mendjen?!');
INSERT INTO "hasan_efendi" VALUES(9,127,'9:127','Kur shpallet ndonjë sure (që u tërheq vërejtje), ata shikojnë njëri-tjetrin (duke thënë): “A po ju sheh ndokush?” , pastaj largohen. Allahu i shmang zemrat e tyre, sepse ata janë njerëz që nuk kuptojnë!');
INSERT INTO "hasan_efendi" VALUES(9,128,'9:128','Tashmë ju ka ardhur një i Dërguar nga gjiri juaj. Atij i vjen rëndë për gjynahet që bëni ju, jua dëshiron të mirën me gjithë zemër që ju të shkoni rrugës së drejtë dhe është i butë e i mëshirshëm me besimtarët.');
INSERT INTO "hasan_efendi" VALUES(9,129,'9:129','Nëse ata largohen (nga e vërteta), thuaj: “Më mjafton Allahu, s’ka zot tjetër përveç Tij. Unë mbështetem tek Ai që është Zot i Fronit të Madh!');
INSERT INTO "hasan_efendi" VALUES(10,1,'10:1','Elif Lâm Râ. Këto janë vargjet e Librit plot urtësi.');
INSERT INTO "hasan_efendi" VALUES(10,2,'10:2','Vallë, pse çuditen njerëzit që Ne i shpallëm njërit prej tyre (duke i thënë): “Paralajmëroji njerëzit dhe jepu lajmin e mirë atyre që besojnë se, me të vërtetë, për ata ka shpërblim të mrekullueshëm te Zoti i tyre!” Mohuesit thanë: “Ky është njëmend magjistar i madh!”');
INSERT INTO "hasan_efendi" VALUES(10,3,'10:3','Vërtet që Zoti juaj është Allahu, i Cili i krijoi qiejt dhe Tokën për gjashtë ditë, pastaj u ngrit mbi Fron, duke drejtuar çdo gjë. S’ka ndërmjetës tek Ai, përveçse me lejen e Tij. Ky është Allahu, Zoti juaj, andaj adhurojeni Atë! Vallë, a nuk po mendoni?!');
INSERT INTO "hasan_efendi" VALUES(10,4,'10:4','Tek Ai do të ktheheni të gjithë. Premtimi i Allahut është i vërtetë. Ai e zë fill krijimin e pastaj e përsërit atë, që t’i shpërblejë me të drejtë ata që besojnë dhe bëjnë vepra të mira. Sa i përket atyre që nuk besojnë, ata do të kenë pije nga uji i vluar dhe dënim të madh, për shkak se kanë mohuar.');
INSERT INTO "hasan_efendi" VALUES(10,5,'10:5','Ai e ka bërë Diellin burim drite, kurse Hënën e ka bërë të shkëlqejë, duke i caktuar asaj fazat, që ju të dini numrin e viteve dhe njehsimin e kohës. Allahu i krijoi këto vetëm me të vërtetën. Ai ua shpjegon shenjat e Tij njerëzve që kuptojnë.');
INSERT INTO "hasan_efendi" VALUES(10,6,'10:6','Vërtet, në ndërrimin e natës me ditën dhe në atë që ka krijuar Allahu në qiej dhe në Tokë, ka shenja për njerëzit që i frikësohen Allahut.');
INSERT INTO "hasan_efendi" VALUES(10,7,'10:7','Sigurisht, ata që nuk shpresojnë se do të takohen me Ne, që janë të kënaqur me jetën e kësaj bote, duke gjetur prehjen në të dhe që janë të pavëmendshëm ndaj shenjave Tona,');
INSERT INTO "hasan_efendi" VALUES(10,8,'10:8','do të kenë si vendstrehim zjarrin, për atë që kanë bërë.');
INSERT INTO "hasan_efendi" VALUES(10,9,'10:9','Vërtet, ata që besojnë dhe bëjnë vepra të mira, Zoti i tyre do t’i udhëzojë në rrugë të drejtë, për shkak të besimit të tyre. Pranë tyre do të rrjedhin lumenj në kopshtet e kënaqësisë');
INSERT INTO "hasan_efendi" VALUES(10,10,'10:10','dhe lutja e tyre aty do të jetë “Qofsh i lavdëruar, o Zot!”. Përshëndetja e tyre do të jetë “Selam” dhe lutja e tyre e fundit “Falënderuar qoftë Allahu, Zoti i botëve”!');
INSERT INTO "hasan_efendi" VALUES(10,11,'10:11','Sikur Allahu, t’ua shpejtonte të keqen njerëzve, ashtu siç e dëshirojnë shpejt të mirën, me të vërtetë që ata do të zhdukeshin. Megjithatë, ata që nuk shpresojnë takimin Tonë, Ne i lëmë të bredhin të verbër në humbjen e tyre.');
INSERT INTO "hasan_efendi" VALUES(10,12,'10:12','E, kur njeriun e gjen e keqja, atëherë Na lutet shtrirë ose ulur, ose në këmbë. E, sapo Ne ia largojmë të keqen, vazhdon rrugën, thuajse nuk Na është lutur për fatkeqësinë që e ka goditur. Kështu, plëngprishësve (që shkelin kufijtë e Allahut) iu zbukurohet ajo që punojnë.');
INSERT INTO "hasan_efendi" VALUES(10,13,'10:13','Ne i kemi zhdukur shumë brezni para jush, sepse bënin të këqija. Të dërguarit e tyre u sollën prova të qarta, por ata nuk i besuan. Kështu, Ne i dënojmë njerëzit e këqij.');
INSERT INTO "hasan_efendi" VALUES(10,14,'10:14','Pastaj ju bëmë ju pas tyre zëvendës në Tokë, për të parë se si do të veproni.');
INSERT INTO "hasan_efendi" VALUES(10,15,'10:15','E, kur u lexohen shpalljet Tona të qarta, atëherë ata që nuk shpresojnë se do të dalin para Nesh, thonë: “Na sill ndonjë Kuran tjetër ose ndërroje atë!” Thuaj (o Muhamed): “Është e pamundur ta ndërroj unë atë nga ana ime. Unë shkoj vetëm pas asaj që më është shpallur. Unë druaj, nëse e kundërshtoj Zotin tim, dënimin e Ditës së Madhe.”');
INSERT INTO "hasan_efendi" VALUES(10,16,'10:16','Thuaj: “Sikur të kishte dashur Allahu, unë nuk do t’jua kisha lexuar dhe as që do të bënte Ai që ta mësoni ju atë. Unë kam jetuar një kohë (të gjatë) në mesin tuaj para profetësisë, a nuk po mendoni?”');
INSERT INTO "hasan_efendi" VALUES(10,17,'10:17','Kush është keqbërës më i madh se ai që trillon gënjeshtra për Allahun ose që i përgënjeshtron shpalljet e Tij?! Me të vërtetë, gjynahqarët nuk kanë shpëtim.');
INSERT INTO "hasan_efendi" VALUES(10,18,'10:18','Ata adhurojnë në vend të Allahut atë që as nuk u bën dëm, as nuk u sjell dobi dhe thonë: “Këta janë ndërmjetësit tanë te Allahu”! Thuaj: “A po i tregoni Allahut për diçka që Ai nuk e di në qiej dhe në Tokë?!” Qoftë i lavdëruar Ai dhe i lartësuar mbi gjithçka që ia shoqërojnë Atij (në adhurim)!');
INSERT INTO "hasan_efendi" VALUES(10,19,'10:19','Të gjithë njerëzit kanë qenë të një feje të vetme, pastaj u përçanë. Sikur të mos ishte thënë më parë fjala nga Zoti yt, atëherë do të gjykohej midis tyre, për atë që nuk u pajtuan. ');
INSERT INTO "hasan_efendi" VALUES(10,20,'10:20','Dhe ata thonë: “Sikur t’i ishte zbritur atij ndonjë mrekulli prej Zotit të tij?” Thuaj: “Me të vërtetë, të padukshmet i di vetëm Allahu. E, pritni ju, se edhe unë po pres me ju (gjykimin e Allahut)”.');
INSERT INTO "hasan_efendi" VALUES(10,21,'10:21','Kur Ne, pas të keqes që i godet, u japim njerëzve të shijojnë mëshirën, ata përsëri bëjnë kurthe kundër shpalljeve Tona! Thuaj: “Allahu është më i shpejtë në dënimin e ngatërresave”. Me të vërtetë, të dërguarit Tanë (engjëjt) shënojnë intrigat tuaja.');
INSERT INTO "hasan_efendi" VALUES(10,22,'10:22','Ai ua mundëson (njerëzve) udhëtimin nëpër tokë dhe det. E, kur gjenden në anije që lundron me ta me erën e përshtatshme e ata i gëzohen kësaj, arrin një stuhi dhe i sulmojnë valët nga të gjitha anët e mendojnë se do t’i vënë poshtë, (atëherë) i luten Allahut sinqerisht, duke i premtuar besimin: “Nëse na shpëton nga kjo, me të vërtetë, do të jemi mirënjohës”.');
INSERT INTO "hasan_efendi" VALUES(10,23,'10:23','E kur Allahu i shpëton ata, ata përnjëherë pa kurrfarë të drejte bëjnë turbullira në Tokë. O njerëz! Vërtet, padrejtësia që bëni për t’u kënaqur në jetën e kësaj bote, vetëm ju dëmton. Pastaj ju do të ktheheni te Ne dhe Ne do t’ju njoftojmë për atë që keni punuar.');
INSERT INTO "hasan_efendi" VALUES(10,24,'10:24','Me të vërtetë, jeta e kësaj bote i shëmbëllen shiut që Ne e lëshojmë nga qielli e me të përzihet toka dhe bën bimë, me të cilat ushqehen njerëzit dhe kafshët. E, kur Toka vishet dhe zbukurohet dhe, kur banorët e saj mendojnë se janë zotërues të saj (për ta vjelë a korrur), iu vjen urdhri ynë, natën ose ditën dhe Ne e bëjmë atë si të ishte e korrur, thuajse nuk kishte lulëzuar të djeshmen. Kështu, Ne ua shpjegojmë shpalljet Tona njerëzve që mendojnë.');
INSERT INTO "hasan_efendi" VALUES(10,25,'10:25','Allahu i thërret njerëzit për në Shtëpinë e shpëtimit (Xhenet) dhe udhëzon kë të dojë në rrugën e drejtë.');
INSERT INTO "hasan_efendi" VALUES(10,26,'10:26','Ata që bëjnë vepra të mira, do të kenë shpërblim të mirë, madje edhe më tepër! Fytyrat e tyre nuk do t’i mbulojë as errësira, as poshtërimi. Ata do të jenë banorë të Xhenetit, në të cilin do të banojnë përherë.');
INSERT INTO "hasan_efendi" VALUES(10,27,'10:27','Kurse ata që bëjnë vepra të këqija, do të kenë dënim gjegjës për atë të keqe. Ata do t’i mbulojë poshtërimi, pa pasur askënd që t’i mbrojë nga dënimi i Allahut, me fytyrat e tyre si të ishin të mbuluara me copa të errëta nate. Këta do të jenë banorët e zjarrit dhe aty do të qëndrojnë përgjithmonë.');
INSERT INTO "hasan_efendi" VALUES(10,28,'10:28','Ditën, kur do t’i tubojmë të gjithë, do t’u themi idhujtarëve: “Rrini në vendet tuaja ju dhe idhujt tuaj!” Pastaj do t’i ndajmë ata dhe idhujt e tyre do të thonë: “Ju nuk na keni adhuruar ne.');
INSERT INTO "hasan_efendi" VALUES(10,29,'10:29','Mjafton që Allahu të jetë dëshmitar midis nesh dhe jush. Ne, në të vërtetë, kemi qenë të pavetëdijshëm për adhurimin tuaj.”');
INSERT INTO "hasan_efendi" VALUES(10,30,'10:30','Atëherë, çdo shpirt do të marrë vesh atë që ka punuar më parë. Të gjithë do të kthehen tek Allahu, Zoti i tyre i vërtetë, ndërsa ajo çfarë ata kishin trilluar, do t’i braktisë.');
INSERT INTO "hasan_efendi" VALUES(10,31,'10:31','Thuaj: “Kush ju ushqen nga qielli dhe Toka? Kush mundëson të dëgjuarit dhe të parët? Kush mund të nxjerrë të gjallën nga e vdekura dhe të vdekurën nga e gjalla? Kush i drejton gjërat?” Ata do të thonë “Allahu”. E ti (o Muhamed) thuaj: “Atëherë, përse nuk e keni frikë Atë?”');
INSERT INTO "hasan_efendi" VALUES(10,32,'10:32','Ky është Allahu, Zoti juaj i vërtetë. E çfarë ka pas së vërtetës, përveç humbjes? Atëherë, përse largoheni?');
INSERT INTO "hasan_efendi" VALUES(10,33,'10:33','Kështu përmbushet fjala e Zotit tënd ndaj atyre që bëjnë çrregullime; se, me të vërtetë, ata nuk besojnë.');
INSERT INTO "hasan_efendi" VALUES(10,34,'10:34','Thuaj: “A ka ndër idhujt tuaj ndonjë që mund të fillojë krijimin e pastaj ta përsërisë?” Thuaj: “Allahu e fillon krijimin dhe pastaj e përsërit atë! Prandaj, si largoheni (nga e vërteta drejt së kotës)?');
INSERT INTO "hasan_efendi" VALUES(10,35,'10:35','Thuaj: “A ka ndër idhujt tuaj ndonjë që mund të udhëzojë në rrugë të drejtë?” Thuaj: “Vetëm Allahu udhëzon në rrugë të drejtë! E pra, pas cilit ia vlen të shkosh, pas atij që udhëzon në rrugë të drejtë apo pas atij i cili as vetë nuk është në rrugë të drejtë, veç nëse e shpie dikush? Çfarë keni? Si po gjykoni kështu?!”');
INSERT INTO "hasan_efendi" VALUES(10,36,'10:36','Shumica e tyre veprojnë vetëm sipas hamendjes. Në të vërtetë, hamendja nuk e zëvendëson aspak të vërtetën. Allahu, pa dyshim, e di mirë atë që punojnë ata.');
INSERT INTO "hasan_efendi" VALUES(10,37,'10:37','Ky Kuran nuk është i tillë që të mund të bëhej prej dikujt tjetër veç Allahut. Përkundrazi, ai është vërtetues i shpalljeve të mëparshme dhe shpjegues i Ligjit (për njerëzit) dhe, pa dyshim, është zbritur nga Zoti i botëve!');
INSERT INTO "hasan_efendi" VALUES(10,38,'10:38','Nëse ata thonë: “Ai (Muhamedi) e ka trilluar atë (Kuranin)”, thuaju: “Atëherë, sillni një sure të ngjashme me të dhe thirrni (në ndihmë) kë të mundeni, përveç Allahut, nëse thoni të vërtetën.”');
INSERT INTO "hasan_efendi" VALUES(10,39,'10:39','Por ata e mohojnë atë që nuk e njohin plotësisht, ndërkohë që nuk u ka ardhur ende përmbushja e (premtimeve të) tij. Kështu kanë mohuar edhe popujt që ishin para tyre; prandaj shiko si kanë përfunduar keqbërësit!');
INSERT INTO "hasan_efendi" VALUES(10,40,'10:40','Disa prej tyre besojnë në atë (Kuran) e disa nuk besojnë. Zoti yt i njeh më së miri ngatërrestarët.');
INSERT INTO "hasan_efendi" VALUES(10,41,'10:41','Nëse ata të quajnë gënjeshtar, thuaju: “Unë kam (shpërblim për) punën time, kurse ju keni (shpërblim për) punën tuaj. Ju s’jeni përgjegjës për atë që bëj unë dhe as unë s’jam përgjegjës për atë që bëni ju.”');
INSERT INTO "hasan_efendi" VALUES(10,42,'10:42','Ka prej atyre që të dëgjojnë. Por, a mund të bësh ti që të dëgjojnë të shurdhërit, të cilët s’kanë as mend?');
INSERT INTO "hasan_efendi" VALUES(10,43,'10:43','Ka prej atyre që të shikojnë. Por, a mund të bësh ti që të ecin në rrugë të drejtë të verbërit, të cilët nuk shohin?');
INSERT INTO "hasan_efendi" VALUES(10,44,'10:44','Në të vërtetë, Allahu nuk u bën njerëzve asnjë të keqe, por njerëzit i bëjnë keq vetvetes.');
INSERT INTO "hasan_efendi" VALUES(10,45,'10:45','Ditën kur do t’i tubojë, atyre do t’u duket sikur nuk kanë ndenjur në tokë, veçse një çast të shkurtër dite, aq sa për të njohur njëri - tjetrin. Vërtet, janë të humbur ata që nuk e besojnë takimin me Allahun e nuk janë në rrugë të drejtë.');
INSERT INTO "hasan_efendi" VALUES(10,46,'10:46','Edhe në qoftë se Ne të tregojmë ty diçka nga ai (dënim) që u kemi premtuar atyre, edhe në qoftë se ta marrim shpirtin (para se t’i dënojmë), sërish ata te Ne do të kthehen dhe Allahu është dëshmitar për çfarë bëjnë ata.');
INSERT INTO "hasan_efendi" VALUES(10,47,'10:47','Çdo popull ka pasur të dërguar. Dhe, kur atyre t’u vijë i dërguari (Ditën e Gjykimit), do të gjykohet midis tyre me të drejtë dhe nuk do t’u bëhet asnjë padrejtësi.');
INSERT INTO "hasan_efendi" VALUES(10,48,'10:48','Ata (jobesimtarët) thonë: “Kur do të plotësohet ky premtim, nëse thoni të vërtetën?”');
INSERT INTO "hasan_efendi" VALUES(10,49,'10:49','Thuaj: “Unë nuk kam fuqi t’i bëj vetes as dëm, as dobi, përveç asaj që dëshiron Allahu. Çdo popull ka afatin e vet. Prandaj, kur t’u vijë ai, ata nuk mund ta shtyjnë për asnjë çast e as ta shpejtojnë”.');
INSERT INTO "hasan_efendi" VALUES(10,50,'10:50','Thuaj: “Si mendoni ju, nëse ju vjen dënimi i Tij natën apo ditën, çfarë pjese të tij do të përshpejtojnë keqbërësit?”');
INSERT INTO "hasan_efendi" VALUES(10,51,'10:51','A mos vallë, vetëm atëherë kur të ndodhë do ta besoni? Po më parë, përse kërkonit ardhjen e shpejtë të tij?!');
INSERT INTO "hasan_efendi" VALUES(10,52,'10:52','E pastaj, do t’u thuhet keqbërësve: “Shijoni dënimin e përhershëm! Nuk dënoheni ndryshe, përveçse sipas veprave që keni punuar”.');
INSERT INTO "hasan_efendi" VALUES(10,53,'10:53','Ata kërkojnë shpjegim prej teje: “A është e vërtetë ajo?” Thuaj: “Po, për Zotin tim, pa dyshim që është e vërtetë e ju nuk do të mund ta pengoni atë”!');
INSERT INTO "hasan_efendi" VALUES(10,54,'10:54','Sikur çdo njeri që ka bërë padrejtësi, të kishte tërë pasurinë e botës, do ta jepte atë (Ditën e Gjykimit) si dëmshpërblim (për të shpëtuar). Ata do të fshehin pendimin kur të përballen me dënimin e do të gjykohen me të drejtë dhe nuk do t’u bëhet padrejtësi.');
INSERT INTO "hasan_efendi" VALUES(10,55,'10:55','Vërtet që Allahut i përket çdo gjë që gjendet në qiej dhe në Tokë. Premtimi i Allahut është i vërtetë, por shumica e njerëzve nuk e dinë.');
INSERT INTO "hasan_efendi" VALUES(10,56,'10:56','Ai jep jetën dhe vdekjen dhe tek Ai do të ktheheni.');
INSERT INTO "hasan_efendi" VALUES(10,57,'10:57','O njerëz, ju ka ardhur këshillë (Kurani) nga Zoti juaj, shërim për zemrat tuaja, udhëzim dhe mëshirë për besimtarët.');
INSERT INTO "hasan_efendi" VALUES(10,58,'10:58','Thuaj: “Me dhuntinë e Allahut dhe mëshirën e Tij - vetëm me këto le të gëzohen! Kjo është më e mirë se ajo që grumbullojnë.”');
INSERT INTO "hasan_efendi" VALUES(10,59,'10:59','Thuaj: “Çfarë mendoni ju për ushqimin që jua ka zbritur Allahu, një pjesë të të cilit e bëni të ndaluar e një pjesë tjetër të lejuar?” Thuaj: “A ju ka lejuar Allahu për këtë apo po shpifni gënjeshtra për Allahun?”');
INSERT INTO "hasan_efendi" VALUES(10,60,'10:60','Çfarë do të mendojnë në Ditën e Kiametit, ata që shpifin gënjeshtra për Allahun? Me të vërtetë, Allahu është Bamirës i madh me njerëzit, por, pjesa e tyre dërrmuese nuk e falënderojnë.');
INSERT INTO "hasan_efendi" VALUES(10,61,'10:61','Në çfarëdo pune që të jesh, çfarëdo gjëje që të lexosh nga Kurani dhe çfarëdo pune që të bëni (o njerëz), Ne jemi dëshmitarë, kur ju të thelloheni në të. Zotit tënd nuk i fshihet asnjë grimë peshe, në Tokë e në qiell; as më pak nga ajo e as më shumë nuk mbetet pa u shënuar në Librin e qartë.');
INSERT INTO "hasan_efendi" VALUES(10,62,'10:62','Pa dyshim, miqtë e Allahut nuk do të kenë arsye për t’u frikësuar dhe as për t’u dëshpëruar.');
INSERT INTO "hasan_efendi" VALUES(10,63,'10:63','(Të tillë janë) ata që besojnë dhe që (i) frikësohen (Allahut).');
INSERT INTO "hasan_efendi" VALUES(10,64,'10:64','Ata i pret lajmi i mirë në këtë jetë dhe në tjetrën. S’ka ndryshim në fjalët e Allahut; kjo është fitorja madhështore.');
INSERT INTO "hasan_efendi" VALUES(10,65,'10:65','Mos u dëshpëro nga fjala e tyre. E gjithë fuqia i përket Allahut! Ai dëgjon dhe di çdo gjë.');
INSERT INTO "hasan_efendi" VALUES(10,66,'10:66','Vërtet, Allahut i përket çdokush që gjendet në qiej dhe në Tokë. E çfarë ndjekin ata që adhurojnë në vend të Allahut zota ortakë? Në të vërtetë, ata nuk ndjekin gjë tjetër, përveç hamendjes dhe vetëm gënjejnë.');
INSERT INTO "hasan_efendi" VALUES(10,67,'10:67','Ai jua krijoi natën për të pushuar në të, kurse ditën për të parë dhe këtu, vërtet, ka shenja (argumente) për njerëzit që ua vënë veshin.');
INSERT INTO "hasan_efendi" VALUES(10,68,'10:68','Ata thonë: “Allahu ka një bir”. Qoftë i lartësuar Ai! Ai është i Vetëmjaftueshëm, s’ka nevojë për asgjë. Atij i përket gjithçka që gjendet në qiej dhe në Tokë. Ju nuk keni asnjë provë për atë që thoni. Pse thoni për Allahun atë që nuk e dini?!');
INSERT INTO "hasan_efendi" VALUES(10,69,'10:69','Thuaj: “Sigurisht, atyre që shpifin gënjeshtra për Allahun, nuk do t’u ecë mbarë”.');
INSERT INTO "hasan_efendi" VALUES(10,70,'10:70','Ata do të kenë kënaqësi të shkurtër në këtë botë e pastaj do të kthehen te Ne dhe, Ne, do t’u japim të shijojnë dënimin e ashpër për shkak se nuk besuan.');
INSERT INTO "hasan_efendi" VALUES(10,71,'10:71','Tregoju atyre historinë e Nuhut, kur i tha popullit të vet: “O populli im, nëse për ju është e rëndë prania ime midis jush dhe që t’ju përkujtoj shenjat e Allahut, (dijeni se) unë jam mbështetur vetëm tek Allahu! Prandaj, bashkë me idhujt tuaj, vendosni si do të veproni dhe vendimi juaj le të mos fshihet; zbatojeni kundër meje e mos më jepni kohë!');
INSERT INTO "hasan_efendi" VALUES(10,72,'10:72','Por, nëse ktheni shpinën, (dijeni se) unë prej jush nuk kërkoj kurrfarë shpërblimi. Shpërblimi im është vetëm tek Allahu. Mua më është urdhëruar të jem prej atyre që (i) nënshtrohen (Atij).”');
INSERT INTO "hasan_efendi" VALUES(10,73,'10:73','Por ata e quajtën gënjeshtar, kurse Ne e shpëtuam atë dhe të gjithë besimtarët që ishin në anije me të. Pastaj i bëmë ata zëvendës (të atyre që u zhdukën), kurse i fundosëm ata që përgënjeshtruan shpalljet Tona. Shikoje sesi ishte përfundimi i atyre që ishin paralajmëruar.');
INSERT INTO "hasan_efendi" VALUES(10,74,'10:74','Pastaj, pas tij u çuam të dërguar popujve të tyre e ata u sollën prova atyre, por nuk do të besonin çfarë kishin përgënjeshtruar më parë. Kështu, Ne ua vulosim zemrat atyre që kalojnë kufirin.');
INSERT INTO "hasan_efendi" VALUES(10,75,'10:75','Pastaj, Faraonit dhe parisë së tij pas tyre, u dërguam Musain dhe Harunin me mrekullitë Tona, por ata u sollën me mendjemadhësi, sepse ishin popull keqbërës.');
INSERT INTO "hasan_efendi" VALUES(10,76,'10:76','Kur u erdhi e Vërteta prej Nesh, ata thanë: “Kjo duket që është magji e qartë!”');
INSERT INTO "hasan_efendi" VALUES(10,77,'10:77','Musai tha: “A mos vallë, për të Vërtetën, pasi ju ka ardhur, thoni: Kjo është magji? E pra, magjistarët nuk shpëtojnë!”');
INSERT INTO "hasan_efendi" VALUES(10,78,'10:78','Ndërsa ata thanë: “A mos ke ardhur të na kthesh nga ajo (rrugë), ku i kemi gjetur etërit tanë e, ju të dyve, t’ju takojë pushteti në Tokë? E pra, ne nuk duam t’ju besojmë ju të dyve”!');
INSERT INTO "hasan_efendi" VALUES(10,79,'10:79','Faraoni tha: “M’i sillni të gjithë magjistarët e shkathët!”');
INSERT INTO "hasan_efendi" VALUES(10,80,'10:80','Pasi erdhën magjistarët, Musai u tha: “Hidhni ç’keni për të hedhur”!');
INSERT INTO "hasan_efendi" VALUES(10,81,'10:81','Kur ata i hodhën, Musai tha: “Kjo që keni sjellë ju, është magji dhe vërtet, Allahu do ta shkatërrojë atë. Allahu, me siguri që do ta prishë punën e ngatërrestarëve.');
INSERT INTO "hasan_efendi" VALUES(10,82,'10:82','Allahu, me fjalën e Tij e forcon të Vërtetën, edhe sikur ta urrejnë keqbërësit këtë!”');
INSERT INTO "hasan_efendi" VALUES(10,83,'10:83','Por Musait nuk i besoi askush, përveç një pakice nga populli i tij, nga frika se do t’i keqtrajtonte Faraoni dhe paria e tij. Faraoni ishte vërtet një tiran në tokë dhe në të keqe kalonte çdo kufi.');
INSERT INTO "hasan_efendi" VALUES(10,84,'10:84','Musai tha: “O populli im! Nëse besoni Allahun, mbështetuni tek Ai, nëse jeni myslimanë!”');
INSERT INTO "hasan_efendi" VALUES(10,85,'10:85','Ata thanë: “Ne jemi mbështetur tek Allahu. O Zoti ynë, mos na bëj sprovë për popullin keqbërës (duke i lënë të na shkatërrojnë),');
INSERT INTO "hasan_efendi" VALUES(10,86,'10:86','dhe shpëtona me mëshirën Tënde, nga populli mohues!”');
INSERT INTO "hasan_efendi" VALUES(10,87,'10:87','Ne i shpallëm Musait dhe vëllait të tij: “Ndërtoni shtëpi për popullin tuaj në Egjipt, shndërrojini ato në faltore dhe falni namazin! Dhe sillu lajmin e mirë besimtarëve!”');
INSERT INTO "hasan_efendi" VALUES(10,88,'10:88','Musai tha: “O Zoti ynë! Ti i ke dhënë Faraonit dhe parisë së tij stoli dhe pasuri në jetën e kësaj bote, por ata, o Zoti ynë, na largojnë nga rruga Jote! O Zoti ynë! Shkatërroje pasurinë e tyre dhe ngurtësoi zemrat e tyre, që të mos besojnë, derisa të shijojnë dënimin e dhembshëm!”');
INSERT INTO "hasan_efendi" VALUES(10,89,'10:89','(Allahu) tha: “U pranua lutja e ju të dyve! Qëndroni të dy në rrugën e drejtë dhe kurrsesi mos ndiqni rrugën e të paditurve!”');
INSERT INTO "hasan_efendi" VALUES(10,90,'10:90','Ne i mbartëm bijtë e Israilit përtej detit dhe Faraoni me ushtrinë e tij i ndoqi ata nga ligësia dhe armiqësia. Por, kur po mbytej, (Faraoni) tha: “Besoj se s’ka zot tjetër përveç Atij që i besojnë bijtë e Israilit; dhe unë jam ndër myslimanët.”');
INSERT INTO "hasan_efendi" VALUES(10,91,'10:91','“Vallë, tani (po beson), ndërkohë që më parë ishe i padëgjueshëm dhe ngatërrestar?!');
INSERT INTO "hasan_efendi" VALUES(10,92,'10:92','Sot do të shpëtojmë vetëm trupin tënd, që të bëhesh mësim për ata që vijnë pas teje.” Por, në të vërtetë, shumica e njerëzve nuk ua vënë veshin shenjave Tona.');
INSERT INTO "hasan_efendi" VALUES(10,93,'10:93','Ne i vendosëm bijtë e Israilit në një vend të mirë të dhe u dhamë ushqim të këndshëm. Ata nuk u përçanë në mendime, derisa u erdhi dituria. Me të vërtetë, Zoti yt do t’i gjykojë ata në Ditën e Kiametit në lidhje me përçarjet e tyre.');
INSERT INTO "hasan_efendi" VALUES(10,94,'10:94','Nëse ti (Muhamed) je në mëdyshje për atë që të kemi shpallur ty, pyeti ata që kanë lexuar Shkrimet para teje. Pa dyshim, ty të ka ardhur e Vërteta nga Zoti yt, prandaj mos u bëj kurrsesi nga ata që dyshojnë.');
INSERT INTO "hasan_efendi" VALUES(10,95,'10:95','Dhe mos u bëj nga ata që i përgënjeshtrojnë shenjat Tona, përndryshe do të ishe ndër të humburit.');
INSERT INTO "hasan_efendi" VALUES(10,96,'10:96','Vërtet, ata që merituan Fjalën e Zotit tënd, nuk besojnë,');
INSERT INTO "hasan_efendi" VALUES(10,97,'10:97','edhe sikur t’u vijnë të gjitha shenjat, derisa të shohin dënimin e dhembshëm.');
INSERT INTO "hasan_efendi" VALUES(10,98,'10:98','Përse nuk pati ndonjë qytet, banorët e të cilit të besonin e t’u bënte dobi besimi i tyre, përveç popullit të Junusit? Kur ata besuan, Ne ua larguam dënimin e poshtërimit në jetën e kësaj bote dhe i lamë të kënaqeshin edhe njëfarë kohe.');
INSERT INTO "hasan_efendi" VALUES(10,99,'10:99','Sikur të donte Zoti yt, do të besonin të gjithë ata që gjenden në tokë. Vallë, ti do t’i detyrosh njerëzit të bëhen besimtarë?!');
INSERT INTO "hasan_efendi" VALUES(10,100,'10:100','Askush nuk mund të besojë, përveçse me vullnetin e Allahut. Ai e zbret zemërimin e Tij mbi ata që s’mendojnë .');
INSERT INTO "hasan_efendi" VALUES(10,101,'10:101','Thuaj: “Shikoni ç’ka në qiej dhe në Tokë!” Por shenjat dhe qortimet nuk u bëjnë kurrfarë dobie njerëzve që nuk besojnë.');
INSERT INTO "hasan_efendi" VALUES(10,102,'10:102','Vallë, a presin ata ndonjë gjë tjetër, veç diçkaje të ngjashme me dënimin e atyre që kanë jetuar më parë? Thuaj: “Pritni, se edhe unë po pres me ju!”');
INSERT INTO "hasan_efendi" VALUES(10,103,'10:103','Pastaj, Ne do t’i shpëtojmë të dërguarit Tanë dhe ata që kanë besuar. Kështu, është detyra Jonë që t’i shpëtojmë besimtarët.');
INSERT INTO "hasan_efendi" VALUES(10,104,'10:104','Thuaj: “O njerëz, nëse ju dyshoni në fenë time, unë nuk do t’i adhuroj ato, që ju i adhuroni në vend të Allahut, por do të adhuroj vetëm Allahun, i Cili do t’ju bëjë të vdisni. Unë jam urdhëruar të jem nga ata që besojnë.');
INSERT INTO "hasan_efendi" VALUES(10,105,'10:105','Dhe (më është thënë): ‘Bëhu i sinqertë në fe dhe kurrsesi mos u bëj nga idhujtarët.');
INSERT INTO "hasan_efendi" VALUES(10,106,'10:106','Përveç Allahut, mos iu lut tjetërkujt nga ata që nuk të sjellin as dobi, as dëm. Nëse do ta bëje këtë, atëherë, ti do të ishe vërtet keqbërës.”');
INSERT INTO "hasan_efendi" VALUES(10,107,'10:107','Nëse Allahu vendos që të të godasë ndonjë e keqe, askush nuk mund të ta largojë atë, përveç Tij e, nëse Ai do të të bëjë ndonjë të mirë, s’ka kush që ta pengojë mirësinë e Tij. Ai ia jep atë mirësi kujt të dojë prej robërve të Tij. Ai është Falës dhe Mëshirëplotë.');
INSERT INTO "hasan_efendi" VALUES(10,108,'10:108','Thuaj: “O njerëz, ju erdhi e Vërteta nga Zoti juaj. Kush udhëzohet në rrugën e drejtë, ai është udhëzuar për dobinë e vet dhe kush e humb rrugën e drejtë, ka humbur në dëmin e vet, ndërsa unë nuk jam mbikëqyrësi juaj”.');
INSERT INTO "hasan_efendi" VALUES(10,109,'10:109','Zbatoje atë që të është shpallur ty dhe duro derisa të gjykojë Allahu, se Ai është Gjykatësi më i mirë!');
INSERT INTO "hasan_efendi" VALUES(11,1,'11:1','Elif Lám Râ. Ky është një Libër, vargjet e të cilit janë radhitur mrekullueshëm dhe janë parashtruar me hollësi nga ana e Një të Urti të Gjithëdijshëm.');
INSERT INTO "hasan_efendi" VALUES(11,2,'11:2','Adhuroni vetëm Allahun, sepse unë (Muhamedi) jam për ju një paralajmërues dhe sjellës i lajmit të mirë prej Tij,');
INSERT INTO "hasan_efendi" VALUES(11,3,'11:3','(që ju porosis) të kërkoni falje nga Zoti juaj e të ktheheni tek Ai me pendesë. Ai do t’ju japë të shijoni kënaqësi të bukura deri në afatin e caktuar dhe do t’i japë çdo të miri shpërblimin e merituar. E, nëse ktheni shpinën, unë i frikësohem dënimit tuaj në Ditën e Madhe.');
INSERT INTO "hasan_efendi" VALUES(11,4,'11:4','Tek Allahu do të ktheheni; Ai është i Plotfuqishëm për çdo gjë!');
INSERT INTO "hasan_efendi" VALUES(11,5,'11:5','Ah! Në të vërtetë, ata mbulojnë kraharorin e tyre, që të fshihen nga Ai. Por edhe sikur të mbulohen me petkat e tyre, Ai e di se çfarë fshehin ata dhe çfarë shfaqin. Ai, me të vërtetë, është i Gjithëdijshëm se ç’ka në zemra (të çdokujt).');
INSERT INTO "hasan_efendi" VALUES(11,6,'11:6','S’ka asnjë gjallesë në Tokë që të mos i ketë mjetet e jetesës prej Tij. Ai di vendbanimin e saj dhe vendin e vdekjes. Të gjitha këto janë (të shënuara) në Librin e qartë.');
INSERT INTO "hasan_efendi" VALUES(11,7,'11:7','Është Ai që ka krijuar qiejt dhe Tokën për gjashtë ditë, teksa Froni i Tij ndodhej mbi ujë, për t’ju vënë në provë kush është më i mirë në vepra. Nëse ti u thua: “Ju do të ringjalleni pas vdekjes”, mohuesit, me siguri do të thonë: “Kjo nuk është asgjë tjetër, përveçse magji e kulluar!”');
INSERT INTO "hasan_efendi" VALUES(11,8,'11:8','Nëse Ne ua shtyjmë dënimin për një kohë të shkurtër, ata me siguri do të thoshin: “Kush po e vonon?” Por ditën që do t’u vijë dënimi, asgjë nuk do t’ua largojë atë dhe, do t’i përfshijë ajo që ata e përqeshnin.');
INSERT INTO "hasan_efendi" VALUES(11,9,'11:9','Kur Ne i japim njeriut të shijojë mëshirën Tonë e pastaj ia heqim atë, ai atëherë bëhet shpresëhumbur dhe mohues.');
INSERT INTO "hasan_efendi" VALUES(11,10,'11:10','E, kur pas fatkeqësisë që e ka goditur atë, Ne i japim atij dhunti, ai me siguri do të thotë: “Gjynahet e mia janë larguar nga unë!” atëherë bëhet i gëzuar dhe mendjemadh.');
INSERT INTO "hasan_efendi" VALUES(11,11,'11:11','(Nuk janë të tillë) vetëm ata që durojnë dhe bëjnë vepra të mira; për ata do të ketë falje dhe shpërblim të madh.');
INSERT INTO "hasan_efendi" VALUES(11,12,'11:12','Mund të ndodhë që ti (Muhamed) të lësh mënjanë diçka nga ato që të janë shpallur ty dhe të të ngushtohet kraharori, sepse ata thonë: “Përse nuk i është dërguar ndonjë thesar?” ose, “Përse nuk vjen një engjëll me të?” Ti je vetëm paralajmërues e Allahu kujdeset për çdo gjë.');
INSERT INTO "hasan_efendi" VALUES(11,13,'11:13','Nëse ata thonë: “Ai e ka trilluar atë !” Thuaju (o Muhamed!): “Sillni dhjetë sure të ngjashme (në bukuri dhe elokuencë) të trilluara dhe thirrni në ndihmë kë të mundni, përveç Allahut, nëse thoni të vërtetën!”');
INSERT INTO "hasan_efendi" VALUES(11,14,'11:14','E, nëse nuk ju përgjigjen, atëherë ta dini se ai (Kurani), ka zbritur me dijen e Allahut dhe se s’ka zot tjetër (të denjë për adhurim) përveç Tij. Andaj, a jeni ju (o mohues) myslimanë?');
INSERT INTO "hasan_efendi" VALUES(11,15,'11:15','Atyre që duan jetën e kësaj bote dhe bukuritë e saj, Ne do t’ua shpërblejmë krejtësisht punët e tyre dhe në të nuk do t’u lihet gjë mangët.');
INSERT INTO "hasan_efendi" VALUES(11,16,'11:16','Këta janë ata për të cilët në botën tjetër nuk do të ketë kurrgjë, përveç zjarrit. Do t’u shembet çdo gjë që kanë bërë në këtë jetë dhe do t’u zhvlerësohet ajo që kanë punuar.');
INSERT INTO "hasan_efendi" VALUES(11,17,'11:17','A janë (të barabartë me jobesimtarët), ata, që mbështeten në provë të qartë nga Zoti i tyre , që ndiqet nga një dëshmitar prej Tij (Kurani), para të cilit ishte Libri i Musait (dëshmitar) udhërrëfyes dhe mëshirë? Këta besojnë Kuranin. Dhe për cilindo nga grupet (sektet) që e mohojnë atë, zjarri do të jetë vend i premtuar. Andaj, ti kurrsesi mos dysho në atë; se ai, pa dyshim, është e vërteta prej Zotit tënd, ndonëse shumica e njerëzve nuk besojnë.');
INSERT INTO "hasan_efendi" VALUES(11,18,'11:18','Kush janë më të padrejtë se ata që trillojnë gënjeshtra për Allahun? Ata do të sillen para Zotit të tyre e dëshmitarët do të thonë: “Këta janë ata që kanë gënjyer kundër Zotit të tyre. Mallkimi i Allahut qoftë mbi të padrejtët,');
INSERT INTO "hasan_efendi" VALUES(11,19,'11:19','të cilët pengojnë (të tjerët) nga rruga e Allahut dhe përpiqen ta shtrembërojnë atë, ndërsa vetë nuk besojnë në botën tjetër.”');
INSERT INTO "hasan_efendi" VALUES(11,20,'11:20','Nuk janë ata që kanë pushtetin në Tokë dhe nuk do të kenë ndonjë mbrojtës tjetër, në vend të Allahut. Atyre do t’u shumëfishohet dënimi, sepse as nuk dëgjonin, as shihnin.');
INSERT INTO "hasan_efendi" VALUES(11,21,'11:21','Këta janë ata që dëmtuan vetveten dhe u humbën idhujt e rremë që i trillonin.');
INSERT INTO "hasan_efendi" VALUES(11,22,'11:22','Vërtet, këta do të jenë më të humburit në botën tjetër.');
INSERT INTO "hasan_efendi" VALUES(11,23,'11:23','Pa dyshim, ata që besojnë, bëjnë vepra të mira dhe përulen para Zotit të tyre, do të jenë banorë të Xhenetit, në të cilin do të qëndrojnë përherë.');
INSERT INTO "hasan_efendi" VALUES(11,24,'11:24','Dallimi midis këtyre dy grupeve shëmbëllen me dallimin midis një të verbri të shurdhër dhe një njeriu që sheh e dëgjon. A janë të njëjtë këta (kur të krahasohen)? A nuk po merrni këshillë?!');
INSERT INTO "hasan_efendi" VALUES(11,25,'11:25','Shumë kohë më parë, Ne dërguam Nuhun te popullit i tij. (Ai u tha): “Unë jam për ju paralajmërues i hapët;');
INSERT INTO "hasan_efendi" VALUES(11,26,'11:26','që të adhuroni vetëm Allahun. (Përndryshe), unë kam frikë për ju dënimin e një Dite të dhembshme”.');
INSERT INTO "hasan_efendi" VALUES(11,27,'11:27','Por, paria e popullit të tij, e cila nuk besoi, tha: “Ne shohim se ti je thjesht njeri si ne dhe, shohim, që ty të shkojnë pas vetëm më të pavlerët nga ne, pa menduar fare. Ne nuk shohim se ti ke diçka më të mirë se ne, madje ju quajmë gënjeshtarë”.');
INSERT INTO "hasan_efendi" VALUES(11,28,'11:28','Ai (Nuhu) tha: “O populli im, mendoni pak! Nëse unë mbështetem në një provë të qartë që më ka ardhur nga Zoti im dhe, nëse nga ana e Tij më ka ardhur një mëshirë, e cila është fshehur nga sytë tuaj, a mund t’ju bëjmë ta pranoni me detyrim (provën), kur ju e urreni?');
INSERT INTO "hasan_efendi" VALUES(11,29,'11:29','O populli im! Unë për këtë nuk kërkoj nga ju kurrfarë pasurie. Mua më shpërblen vetëm Allahu. Unë nuk mund t’i dëboj ata që besojnë. Ata do të takohen me Zotin e tyre. Mirëpo, unë shoh se fatkeqësisht, ju jeni popull, që nuk di.');
INSERT INTO "hasan_efendi" VALUES(11,30,'11:30','O populli im, kush do të më mbrojë nga Allahu, nëse unë i dëboj (besimtarët)? A nuk doni të merrni këshillë?');
INSERT INTO "hasan_efendi" VALUES(11,31,'11:31','Unë nuk ju them se kam thesarin e Allahut apo që e di të padukshmen. As nuk them se unë jam engjëll e as do t’u them atyre, të cilët sytë tuaj i nënçmojnë, se Allahu nuk do t’u japë kurrfarë të mire. Allahu e di më mirë se ç’ka në shpirtrat e tyre. Atëherë, sigurisht që do të isha prej të padrejtëve.”');
INSERT INTO "hasan_efendi" VALUES(11,32,'11:32','Ata thanë: “O Nuh, ti na kundërshtove ne, madje e zgjate kundërshtimin. Le të na godasë ajo me çfarë na kërcënon, nëse thua të vërtetën!”');
INSERT INTO "hasan_efendi" VALUES(11,33,'11:33','Tha (Nuhu): “Vetëm Allahu do t’jua sjellë atë, nëse dëshiron; e ju Atij nuk mund t’i ikni.');
INSERT INTO "hasan_efendi" VALUES(11,34,'11:34','Këshilla ime nuk do t’ju bëjë dobi edhe sikur të doja t’ju këshilloja, nëse vullneti i Allahut është që t’ju shkatërrojë. Ai është Zoti juaj dhe ju tek Ai do të ktheheni”.');
INSERT INTO "hasan_efendi" VALUES(11,35,'11:35','Nëse ata thonë: “Ai e ka trilluar atë ”. Thuaj: “E nëse unë e kam trilluar, gjynahu im bie mbi mua e unë jam larg nga gjynahet që bëni ju”.');
INSERT INTO "hasan_efendi" VALUES(11,36,'11:36','Dhe Nuhut iu shpall: “Nga populli yt askush nuk do të besojë, përveç atyre që tashmë kanë besuar, andaj mos u pikëllo për atë që bëjnë ata!');
INSERT INTO "hasan_efendi" VALUES(11,37,'11:37','Ndërto anijen nën Sytë (mbikëqyrjen) dhe udhëzimin Tonë dhe mos m’u drejto më për ata që janë keqbërës! Ata, me siguri, do të fundosen”.');
INSERT INTO "hasan_efendi" VALUES(11,38,'11:38','Ai nisi të ndërtonte anijen. Sa herë që kalonte pranë tij paria e popullit të tij, e përqeshte. Ai u tha: “Nëse ju talleni me ne, edhe ne do të tallemi me ju, ashtu si po talleni ju');
INSERT INTO "hasan_efendi" VALUES(11,39,'11:39','dhe, së shpejti, me siguri, do ta merrni vesh, kë do ta gjejë dënimi poshtërues dhe mbi kë do të bjerë dënimi i përhershëm”.');
INSERT INTO "hasan_efendi" VALUES(11,40,'11:40','Kur arriti urdhri Ynë e uji nisi të gufojë nga sipërfaqja e tokës, Ne i thamë: “Ngarko në anije nga çdo lloj gjallese nga një çift, familjen tënde - përveç atyre kundër të cilëve ka qenë fjala e dënimit - dhe besimtarët!” Por shumë të paktë ishin ata që i kishin besuar atij!');
INSERT INTO "hasan_efendi" VALUES(11,41,'11:41','Ai tha: “Hipni në anije! Me emrin e Allahut ajo lundron dhe ndalet! Me të vërtetë, Zoti im është Falës dhe Mëshirëplotë”.');
INSERT INTO "hasan_efendi" VALUES(11,42,'11:42','Dhe ajo lundronte me ata nëpër valët e mëdha e të larta sa malet, ndërsa Nuhu thërriste birin e vet, i cili ishte ndarë veç: “O biri im, hip me ne e mos u bëj me mohuesit!”');
INSERT INTO "hasan_efendi" VALUES(11,43,'11:43','(I biri) tha: “Unë do të strehohem në një mal i cili do të më ruajë nga uji”. Nuhu iu përgjigj: “Sot askush nuk do të kursehet nga dënimi i Allahut, përveç atij që e mëshiron Ai!” Një valë në mes tyre i ndau dhe ai u fundos.');
INSERT INTO "hasan_efendi" VALUES(11,44,'11:44','Më pas, një zë thirri: “O Tokë, gëlltit ujin tënd dhe ti, o qiell, ndaloje (shiun)!” Dhe uji u tërhoq e u plotësua urdhri (i Allahut). Ndërsa anija u ndal në malin al Xhudij dhe u tha: “Larguar qofshin keqbërësit (nga mëshira e Allahut)!”');
INSERT INTO "hasan_efendi" VALUES(11,45,'11:45','Pastaj Nuhu iu lut Zotit dhe i tha: “O Zoti im, biri im është nga familja ime, premtimi Yt është i vërtetë dhe Ti je Gjykatësi më i drejtë!”');
INSERT INTO "hasan_efendi" VALUES(11,46,'11:46','(Allahu) tha: “O Nuh, ai nuk është nga familja jote, ai ka bërë punë të keqe. Mos më pyet për atë që ti nuk e di. Unë të këshilloj që të mos bëhesh nga të paditurit”.');
INSERT INTO "hasan_efendi" VALUES(11,47,'11:47','(Nuhu) tha: “O Zoti im, mbështetem tek Ti për Të të mos kërkuar gjëra, për të cilat nuk kam dijeni. Nëse Ti nuk më fal dhe nuk më mëshiron, do të jem nga të humburit”.');
INSERT INTO "hasan_efendi" VALUES(11,48,'11:48','Atëherë iu tha: “O Nuh, zbrit me përshëndetje prej Nesh dhe me bekime për ty dhe pasardhësit e atyre që gjenden me ty. Disa popujve (pas teje), Ne do t’u japim kënaqësi e pastaj do t’i godasë nga Ne dënimi i dhembshëm!”');
INSERT INTO "hasan_efendi" VALUES(11,49,'11:49','Këto janë (disa) nga lajmet e fshehta që Ne t’i shpallim ty. Këto nuk i ke ditur as ti, as populli yt para kësaj (shpalljes së Kuranit). Andaj, duro! Se, vërtet, në fund, fitorja është e besimtarëve.');
INSERT INTO "hasan_efendi" VALUES(11,50,'11:50','Fisit Ad (Ne i dërguam) vëllanë e tyre Hudin. Ai tha: “O populli im, adhuroni Allahun! Ju nuk keni Zot tjetër përveç Tij; ju jeni vetëm trillues.');
INSERT INTO "hasan_efendi" VALUES(11,51,'11:51','O populli im! Unë nuk kërkoj nga ju shpërblim për këtë. Mua më shpërblen vetëm Ai që më ka krijuar! A nuk kuptoni?!');
INSERT INTO "hasan_efendi" VALUES(11,52,'11:52','O populli im! Kërkoni falje nga Zoti juaj dhe kthehuni tek Ai të penduar, se Ai do t’ju sjellë shi të begatshëm dhe do t’ju shtojë fuqi mbi fuqinë tuaj. Mos u shmangni prej Atij duke qenë keqbërës!”');
INSERT INTO "hasan_efendi" VALUES(11,53,'11:53','Ata thanë: “O Hud! Ti nuk na ke sjellë kurrfarë prove të dukshme për fjalët e tua, ne nuk do t’i braktisim zotat tanë dhe nuk të besojmë ty!');
INSERT INTO "hasan_efendi" VALUES(11,54,'11:54','Ne themi vetëm se ty të ka goditur ndonjë e keqe nga zotat tanë”. Ai tha: “Unë kam dëshmitar Allahun, por dëshmoni edhe ju, se unë jam larg nga idhujt që ju i adhuroni');
INSERT INTO "hasan_efendi" VALUES(11,55,'11:55','në vend të Tij. Të gjithë ju bëni komplot kundër meje dhe mos më jepni afat aspak!');
INSERT INTO "hasan_efendi" VALUES(11,56,'11:56','Unë mbështetem tek Allahu, Zoti im dhe Zoti juaj. S’ka asnjë gjallesë që të mos jetë nën pushtetin e Tij. Me të vërtetë, Zoti im, është në rrugë të drejtë.');
INSERT INTO "hasan_efendi" VALUES(11,57,'11:57','E, nëse ju shmangeni, unë tashmë jua kam përcjellë atë (mesazhin), për të cilin jam dërguar te ju. Zoti im, në vendin tuaj do të sjellë një popull tjetër, kurse ju nuk mund t’i bëni kurrfarë dëmi Atij. Vërtet, Zoti im është Mbikëqyrës mbi çdo gjë”.');
INSERT INTO "hasan_efendi" VALUES(11,58,'11:58','Kur erdhi urdhri Ynë, me mëshirën Tonë, Ne e shpëtuam Hudin bashkë me besimtarët e tij. I shpëtuam ata nga dënimi i rëndë.');
INSERT INTO "hasan_efendi" VALUES(11,59,'11:59','Ja, ky ishte fisi Ad. Ai i mohonte shenjat e Zotit të tij, nuk i dëgjonte të dërguarit e Tij dhe shkonte pas urdhrit të çdo keqbërësi kokëfortë.');
INSERT INTO "hasan_efendi" VALUES(11,60,'11:60','Ata i ndoqi mallkimi në këtë jetë dhe në Ditën e Kiametit. Me të vërtetë, fisi Ad e mohoi Zotin e tij. Qoftë larguar (nga mëshira e Allahut) fisi Ad, populli i Hudit!');
INSERT INTO "hasan_efendi" VALUES(11,61,'11:61','Fisit Themud (Ne i dërguam) vëllanë e tij, Salihun. “O populli im”, - u tha ai - “adhuroni vetëm Allahun. Ju s’keni zot tjetër përveç Atij! Ai ju krijoi nga toka dhe ju vuri të jetoni në të; andaj, kërkoni falje nga Ai dhe kthehuni me pendim tek Ai! Pa dyshim, Zoti im është afër dhe u përgjigjet (lutjeve).”');
INSERT INTO "hasan_efendi" VALUES(11,62,'11:62','Ata thanë: “O Salih! Ti ke qenë i nderuar mes nesh dhe shpresa jonë para kësaj (fjale). A mos vallë po na ndalon të adhurojmë ato që i kanë adhuruar prindërit tanë? Vërtet, ne jemi në mëdyshje për thirrjen tënde”.');
INSERT INTO "hasan_efendi" VALUES(11,63,'11:63','Ai tha: “O populli im, mendoni pak! Nëse Zoti im më ka dhënë provë të qartë nga Vetja dhe mëshirë, kush do të më mbrojë nga dënimi i Allahut, nëse nuk e dëgjoj?! Ju (me këtë) mua do të më shtonit vetëm humbjen.');
INSERT INTO "hasan_efendi" VALUES(11,64,'11:64','O populli im! Kjo është deveja e Allahut, shenjë e qartë për ju, prandaj lëreni të kullosë në tokën e Allahut dhe mos i bëni ndonjë të keqe, se ju godet ndëshkimi menjëherë”.');
INSERT INTO "hasan_efendi" VALUES(11,65,'11:65','Por ata e therën dhe ai tha: “Jetoni në shtëpitë tuaja edhe tri ditë! Ky është premtimi i vërtetë”.');
INSERT INTO "hasan_efendi" VALUES(11,66,'11:66','E, kur erdhi urdhri Ynë, Ne - me mëshirën Tonë, e shpëtuam Salihun bashkë me besimtarët e tij nga poshtërimi i asaj dite. Me të vërtetë, Zoti yt është i Forti dhe i Plotfuqishmi.');
INSERT INTO "hasan_efendi" VALUES(11,67,'11:67','Kurse ata që bënë të këqija, i kaploi një zë i tmerrshëm dhe në shtëpitë e tyre u gdhinë të vdekur e të palëvizshëm,');
INSERT INTO "hasan_efendi" VALUES(11,68,'11:68','sikur nuk kishin jetuar kurrë në to. Me të vërtetë, fisi Themud mohoi Zotin e tij. Qoftë larguar (nga mëshira e Zotit) fisi Themud!');
INSERT INTO "hasan_efendi" VALUES(11,69,'11:69','Të dërguarit Tanë, i sollën Ibrahimit lajmin e mirë dhe i thanë: “Paqe mbi ty!” Ai u përgjigj: “Paqja qoftë mbi ju!” Dhe me të shpejtë u solli atyre një viç të pjekur.');
INSERT INTO "hasan_efendi" VALUES(11,70,'11:70','Kur pa se ata nuk po i zgjasnin duart tek mishi, nuk i pëlqeu dhe ndjeu frikë prej atyre. (Engjëjt) thanë: “Mos u frikëso, Ne jemi dërguar te populli i Lutit”.');
INSERT INTO "hasan_efendi" VALUES(11,71,'11:71','Gruaja e tij, që rrinte aty, qeshi kur Ne i dhamë lajmin e mirë për Is’hakun e pas Is’hakut - për Jakubin.');
INSERT INTO "hasan_efendi" VALUES(11,72,'11:72','Ajo tha: “Ah! E mjera unë! A mos vallë, kështu plakë do të lind unë, ndërkohë që edhe burri im është plak! Me të vërtetë, kjo është diçka e jashtëzakonshme!”');
INSERT INTO "hasan_efendi" VALUES(11,73,'11:73','Ata (engjëjt), thanë: “A mos vallë, po çuditesh me urdhrin e Allahut? Mëshira e Allahut dhe bekimi i Tij qofshin mbi ju - familjen e të Dërguarit. Me të vërtetë, Ai është i Falënderuar dhe i Lavdëruar!”');
INSERT INTO "hasan_efendi" VALUES(11,74,'11:74','Pasi i kaloi frika Ibrahimit dhe i erdhi lajmi i gëzuar, ai polemizoi me të dërguarit Tanë për popullin e Lutit.');
INSERT INTO "hasan_efendi" VALUES(11,75,'11:75','Vërtet, Ibrahimi ishte i butë, i dhembshur e vazhdimisht i kthyer (tek Allahu).');
INSERT INTO "hasan_efendi" VALUES(11,76,'11:76','“O Ibrahim, shmangu prej kësaj! Me të vërtetë, erdhi urdhri nga Zoti yt dhe ata do t’i gjejë dënimi i pashmangshëm.”');
INSERT INTO "hasan_efendi" VALUES(11,77,'11:77','Kur i erdhën të dërguarit Tanë Lutit, Ai u hidhërua, ndjeu një shtrëngim në shpirtin e tij dhe tha: “Kjo është ditë e rëndë!”');
INSERT INTO "hasan_efendi" VALUES(11,78,'11:78','Populli i tij, që më parë pat punuar vepra të këqija, u vërsul drejt atij. (Luti) tha: “O populli im, këto janë bijat e mia. Ato për ju janë më të pastra, e frikësojuni Allahut dhe mos më turpëroni para mysafirëve të mi. A nuk ka midis jush një njeri me mend?”');
INSERT INTO "hasan_efendi" VALUES(11,79,'11:79','Ata thanë: “Ti e di se ne nuk kemi të drejtë për bijat e tua. Në të vërtetë, ti e di se ç’duam ne”.');
INSERT INTO "hasan_efendi" VALUES(11,80,'11:80','Ai tha: “Sikur unë të kisha fuqi (që t’ju dëboj) ose të mbështetesha në ndonjë krah të fuqishëm!”');
INSERT INTO "hasan_efendi" VALUES(11,81,'11:81','Thanë (mysafirët): “O Lut! Ne jemi të dërguarit e Zotit tënd. Ata nuk mund të të bëjnë ty asgjë e pra, nisu ti me familjen tënde në orët e vona të natës dhe askush prej jush të mos këqyrë prapa, përveç gruas sate. Atë do ta godasë ajo që do t’i gjejë edhe ata. Afati i tyre është agimi i ditës. Vallë, a nuk është afër agimi i ditës?!”');
INSERT INTO "hasan_efendi" VALUES(11,82,'11:82','Kur erdhi urdhri Ynë, Ne e përmbysëm çdo gjë, - ato që ishin lart, i kthyem poshtë dhe mbi ata lëshuam gurë nga balta e pjekur, duke rënë vazhdimisht, si shiu.');
INSERT INTO "hasan_efendi" VALUES(11,83,'11:83','(Ata ishin gurë) të shënuar (për dënim) te Zoti yt. Dënimi nuk është larg nga keqbërësit.');
INSERT INTO "hasan_efendi" VALUES(11,84,'11:84','Popullit të Medjenit Ne i dërguam vëllanë e tyre, Shuajbin. Ai tha: “O populli im, adhuroni Allahun! Ju s’keni tjetër zot përveç Tij. Mos bëni hile as gjatë matjes, as në kandar. Unë po shoh se ju gjendeni në begati dhe kam frikë se do t’ju godasë dënimi i një dite, që ju përfshin të gjithëve.');
INSERT INTO "hasan_efendi" VALUES(11,85,'11:85','O populli im, matni drejt në litër dhe kandar, mos ua hani njerëzve hakun e tyre dhe mos bëni ngatërresa në Tokë!');
INSERT INTO "hasan_efendi" VALUES(11,86,'11:86','Ajo që ju është lënë nga Allahu, është më e mirë për ju, nëse jeni besimtarë; e unë nuk jam ruajtësi juaj”.');
INSERT INTO "hasan_efendi" VALUES(11,87,'11:87','Ata thanë: “O Shuajb, a mos vallë namazi yt urdhëron që ne t’i lëmë ato që i kanë adhuruar të parët tanë ose që të mos bëjmë me pasurinë tonë çfarë të duam? Qenke njëmend i butë dhe i drejtë ti!”');
INSERT INTO "hasan_efendi" VALUES(11,88,'11:88','Ai tha: “O populli im, mendoni pak! Nëse unë kam provë të qartë nga Zoti im, i Cili më ka furnizuar me begati të mira nga ana e Vet, a të mos ju udhëzoj në rrugën e drejtë?! Unë nuk dëshiroj të bëj atë, që jua ndaloj juve. Unë dëshiroj vetëm të përmirësoj atë që mundem. Ndërsa suksesi im varet vetëm nga Allahu. Tek Ai mbështetem dhe Atij i drejtohem.');
INSERT INTO "hasan_efendi" VALUES(11,89,'11:89','O populli im, kundërshtimi ndaj meje le të mos ju çojë kurrsesi që t’ju ndodhë ajo, që i ndodhi popullit të Nuhut ose popullit të Hudit apo popullit të Salihut; mos harroni se edhe populli i Lutit nuk është larg prej jush.');
INSERT INTO "hasan_efendi" VALUES(11,90,'11:90','Kërkoni falje nga Zoti juaj e kthehuni me pendim tek Ai. Me të vërtetë, Zoti im është Mëshirëplotë dhe gjithë dashuri.”');
INSERT INTO "hasan_efendi" VALUES(11,91,'11:91','Ata thanë: “O Shuajb, ne nuk kuptojmë shumë nga ato, që na thua ti dhe të konsiderojmë të dobët midis nesh. Sikur të mos ishte farefisi yt, do të të kishim mbytur me gurë; ti nuk je i respektuar te ne.”');
INSERT INTO "hasan_efendi" VALUES(11,92,'11:92','Ai tha: “O populli im, a mos vallë farefisi im për ju është më i respektuar se Allahu, të cilin po e anashkaloni? Me të vërtetë, Zoti im, ka nën kontroll gjithçka që bëni ju.');
INSERT INTO "hasan_efendi" VALUES(11,93,'11:93','O populli im, bëni sa të mundni, por edhe unë do të bëj. Ju, shpejt do ta mësoni se kë do ta gjejë dënimi që do ta poshtërojë dhe kush është gënjeshtar. Pritni (fundin), se edhe unë do të pres me ju”.');
INSERT INTO "hasan_efendi" VALUES(11,94,'11:94','Kur erdhi urdhri Ynë, Ne e shpëtuam me mëshirën Tonë Shuajbin dhe ata që i besuan atij, kurse mohuesit i kaploi një zë i tmerrshëm dhe në shtëpitë e tyre u gdhinë të vdekur e të palëvizshëm,');
INSERT INTO "hasan_efendi" VALUES(11,95,'11:95','thuajse nuk kishin jetuar kurrë aty. Qoftë larguar (nga mëshira e Zotit) populli i Medjenit, ashtu siç qe larguar edhe fisi Themud!');
INSERT INTO "hasan_efendi" VALUES(11,96,'11:96','Ne e dërguam Musain me mrekullitë Tona dhe me autoritetin e qartë');
INSERT INTO "hasan_efendi" VALUES(11,97,'11:97','te Faraoni dhe paria e tij, por paria shkoi pas urdhrit të Faraonit, ndonëse urdhri i Faraonit nuk ishte i drejtë.');
INSERT INTO "hasan_efendi" VALUES(11,98,'11:98','Ai do ta udhëheqë popullin e tij në Ditën e Kiametit e do ta çojë në zjarr. Eh, sa vend i shëmtuar është ai, ku do të sillen ata!');
INSERT INTO "hasan_efendi" VALUES(11,99,'11:99','Ata i përcjell mallkimi në këtë jetë dhe në jetën tjetër. Eh sa “dhuratë” e shëmtuar është ajo që do t’u jepet!');
INSERT INTO "hasan_efendi" VALUES(11,100,'11:100','Këto janë disa prej historive të qyteteve që po t’i tregojmë ty (o Muhamed). Disa prej atyre ende qëndrojnë, kurse disa janë rrënuar.');
INSERT INTO "hasan_efendi" VALUES(11,101,'11:101','Ne nuk u bëmë atyre padrejtësi, por ata i bënë padrejtësi vetvetes. Hyjnitë, që ata i adhuronin në vend të Allahut, nuk i ndihmuan aspak, kur erdhi urdhri i Zotit tënd dhe nuk iu shtuan atyre kurrgjë tjetër, përveç humbjes.');
INSERT INTO "hasan_efendi" VALUES(11,102,'11:102','I tillë është dënimi i Zotit tënd, kur ndëshkon vendbanimet që bënin gjynahe. Me të vërtetë, dënimi i Tij është i dhembshëm dhe i ashpër.');
INSERT INTO "hasan_efendi" VALUES(11,103,'11:103','Pa dyshim, në këto (ngjarje) ka këshillë për atë që i druhet dënimit të Ditës së Gjykimit. Ajo është Dita në të cilën do të tubohen të gjithë njerëzit; ajo është Ditë e dëshmuar.');
INSERT INTO "hasan_efendi" VALUES(11,104,'11:104','Ne e shtyjmë atë vetëm që të plotësohet afati i caktuar.');
INSERT INTO "hasan_efendi" VALUES(11,105,'11:105','Kur të vijë ajo ditë, njeriu mund të flasë vetëm me lejen e Tij. Disa prej tyre do të jenë të mjerë e disa fatlumë.');
INSERT INTO "hasan_efendi" VALUES(11,106,'11:106','Ata që do të jenë të mjerë, do të hyjnë në zjarr, ku do t’i presin veç ofshamat e vajtimet.');
INSERT INTO "hasan_efendi" VALUES(11,107,'11:107','Aty do të mbeten përherë, derisa të jenë qiejt dhe Toka, veç si të dojë Zoti yt. Me të vërtetë, Zoti yt kryen atë që do.');
INSERT INTO "hasan_efendi" VALUES(11,108,'11:108','Kurse fatlumët, do të hyjnë në Xhenet, ku do të qëndrojnë përherë, derisa të jenë qiejt dhe Toka, veç si të dojë Zoti yt. Kjo është dhuratë e pandërprerë.');
INSERT INTO "hasan_efendi" VALUES(11,109,'11:109','Prandaj, mos u bëj dyshues për atë që këta adhurojnë. Ata adhurojnë vetëm atë që kanë adhuruar dikur të parët e tyre. Dhe Ne do t’ua japim atë që kanë merituar, duke mos ua pakësuar aspak.');
INSERT INTO "hasan_efendi" VALUES(11,110,'11:110','Vërtet, Ne i dhamë Musait Librin, por rreth tij pati kundërshti. Sikur të mos ishte parathënë Fjala e Zotit tënd (për shtyrjen e gjykimit), me siguri që (çështja) do të gjykohej midis tyre (menjëherë). Në fakt, (edhe tani) ata janë në një mëdyshje tepër të madhe në lidhje me të.');
INSERT INTO "hasan_efendi" VALUES(11,111,'11:111','Në të vërtetë, çdonjërit prej tyre Zoti yt do t’ia plotësojë shpagimin për veprat e veta. Se Ai, me siguri, e di mirë atë që punojnë ata.');
INSERT INTO "hasan_efendi" VALUES(11,112,'11:112','Prandaj qëndro i patundur në udhën e drejtë, ashtu siç je i urdhëruar. Ashtu le të veprojnë edhe besimtarët që janë me ty, e mos i kaloni kufijtë, se Ai, me të vërtetë, i sheh mirë ato që punoni ju.');
INSERT INTO "hasan_efendi" VALUES(11,113,'11:113','Dhe mos anoni nga ata që bëjnë të këqija, përndryshe do t’ju përcëllojë Zjarri. Ju nuk do të keni mbrojtës tjetër përveç Allahut dhe nuk do të ndihmoheni.');
INSERT INTO "hasan_efendi" VALUES(11,114,'11:114','Fale namazin në mëngjes e mbrëmje dhe në një kohë të natës, sepse veprat e mira i shlyejnë veprat e këqija. Kjo këshillë vlen për ata që i pranojnë këshillat.');
INSERT INTO "hasan_efendi" VALUES(11,115,'11:115','Dhe duro! Se Allahu, me të vërtetë, nuk e humb shpërblimin për punëmirët.');
INSERT INTO "hasan_efendi" VALUES(11,116,'11:116','A ka pasur njerëz të mençur te popujt para jush, të cilët të pengonin ngatërresat në Tokë, përveç disave prej tyre që i shpëtuam Ne?! Ata që bënin të këqija, jepeshin pas kënaqësive dhe ishin gjynahqarë.');
INSERT INTO "hasan_efendi" VALUES(11,117,'11:117','Zoti yt kurrë nuk do t’i shkatërronte qytetet padrejtësisht, nëse banorët e tyre do të ishin të ndershëm.');
INSERT INTO "hasan_efendi" VALUES(11,118,'11:118','Sikur të kishte dashur Zoti yt, të gjithë njerëzit do t’i bashkonte në një bashkësi të vetme, por ata vazhdojnë të jenë të përçarë,');
INSERT INTO "hasan_efendi" VALUES(11,119,'11:119','përveç atyre, që i ka mëshiruar Zoti yt. Për këtë qëllim Ai i krijoi ata. Do të plotësohet Fjala e Zotit tënd: “Me të vërtetë që do ta mbush Xhehenemin me xhinde dhe njerëz bashkërisht!”');
INSERT INTO "hasan_efendi" VALUES(11,120,'11:120','Ne po t’i tregojmë ty disa nga historitë e të dërguarve, për të ta forcuar zemrën. Në këtë sure të ka ardhur ty e vërteta. Ajo është këshillë dhe kujtesë për besimtarët.');
INSERT INTO "hasan_efendi" VALUES(11,121,'11:121','Dhe thuaju atyre që nuk besojnë: “Ju bëni çfarë të mundni, edhe ne do të bëjmë çfarë të mundim,');
INSERT INTO "hasan_efendi" VALUES(11,122,'11:122','dhe pritni, nëse do të prisni, se dhe ne do të presim!”');
INSERT INTO "hasan_efendi" VALUES(11,123,'11:123','Allahu i di të fshehtat e qiejve dhe të Tokës dhe çdo gjë kthehet tek Ai. Lutju Atij dhe mbështetu tek Ai! Zoti yt nuk është i pavëmendshëm ndaj asaj që bëni ju.');
INSERT INTO "hasan_efendi" VALUES(12,1,'12:1','Elif, Lâm, Râ. Këto janë vargjet e Librit të qartë!');
INSERT INTO "hasan_efendi" VALUES(12,2,'12:2','Ne e zbritëm këtë Kuran në gjuhën arabe, për ta kuptuar ju.');
INSERT INTO "hasan_efendi" VALUES(12,3,'12:3','Ne të tregojmë ty (Muhamed) historinë më të bukur përmes shpalljes së këtij Kurani, ndonëse ti, me të vërtetë, nuk ke qenë në dijeni më parë.');
INSERT INTO "hasan_efendi" VALUES(12,4,'12:4','Kur Jusufi i tha babait të vet: “O babai im! Unë pashë në ëndërr njëmbëdhjetë yje, Diellin dhe Hënën! I pashë të më përuleshin në sexhde”,');
INSERT INTO "hasan_efendi" VALUES(12,5,'12:5','ai i tha: “O biri im! Mos ua trego ëndrrën tënde vëllezërve të tu, që të mos kurdisin ndonjë gjë kundër teje, sepse, vërtet, djalli është armik i hapët i njeriut.');
INSERT INTO "hasan_efendi" VALUES(12,6,'12:6','Dhe kështu, Zoti yt të ka zgjedhur ty dhe të mëson shpjegimin e ëndrrave dhe e plotëson dhuntinë e Vet ndaj teje dhe familjes së Jakubit, ashtu siç e ka plotësuar më parë ndaj të parëve tu, Ibrahimit dhe Is’hakut. Pa dyshim, Zoti yt është i Urtë dhe i Gjithëdijshëm”.');
INSERT INTO "hasan_efendi" VALUES(12,7,'12:7','Për të gjithë ata që pyesin, në historinë e Jusufit dhe të vëllezërve të tij, ka shumë mësime.');
INSERT INTO "hasan_efendi" VALUES(12,8,'12:8','Vëllezërit e tij thanë: “Jusufi dhe vëllai i tij, janë më të dashur se ne për babanë tonë, ndërkohë që ne jemi një grup i tërë. Babai ynë, me të vërtetë, po gabon.');
INSERT INTO "hasan_efendi" VALUES(12,9,'12:9','Vriteni Jusufin ose braktiseni në ndonjë vend të largët, se pastaj babai juaj do të kthehet nga ju dhe, pas kësaj, do të bëheni njerëz të mirë”.');
INSERT INTO "hasan_efendi" VALUES(12,10,'12:10','Njëri prej tyre tha: “Mos e vrisni Jusufin, por hidheni në fund të ndonjë pusi; do ta marrë ndonjë karvan; nëse përnjëmend doni të bëni diçka me atë”.');
INSERT INTO "hasan_efendi" VALUES(12,11,'12:11','Ata thanë: “O babai ynë! Përse për Jusufin nuk ke besim te ne?! Në të vërtetë, Ne i dëshirojmë çdo të mirë atij.');
INSERT INTO "hasan_efendi" VALUES(12,12,'12:12','Dërgoje nesër me ne që të argëtohet dhe të luajë. Të sigurojmë se do ta ruajmë.”');
INSERT INTO "hasan_efendi" VALUES(12,13,'12:13','(Babai) tha: “Unë brengosem po ta merrni dhe kam frikë se mos do ta hajë ujku, ndërkohë që ju s’do ta keni mendjen tek ai.”');
INSERT INTO "hasan_efendi" VALUES(12,14,'12:14','Ata thanë: “Si do ta hajë ujku, kur ne jemi një grup i fortë! Atëherë, me të vërtetë që ne do të ishim të humbur”.');
INSERT INTO "hasan_efendi" VALUES(12,15,'12:15','Pasi e morën, vendosën që ta hidhnin në fund të një pusi dhe Ne i shpallëm Jusufit: “Ti do t’i lajmërosh ata për këtë vepër, pa e vënë re ata (që ti je Jusufi).”');
INSERT INTO "hasan_efendi" VALUES(12,16,'12:16','Dhe në mbrëmje ata shkuan te babai i tyre, duke qarë.');
INSERT INTO "hasan_efendi" VALUES(12,17,'12:17','Thanë: “O babai ynë, ne shkuam të vrapojmë e Jusufin e lamë te rrobat tona, andaj e hëngri ujku. Por ti nuk na beson, edhe pse themi të vërtetën”.');
INSERT INTO "hasan_efendi" VALUES(12,18,'12:18','Dhe sollën këmishën e tij të përgjakur, me gjak të rremë. Ai tha: “Nuk është kështu, por ju keni sajuar diçka (tjetër nga sa thoni). Por, durim! Allahut i kërkoj ndihmë kundër asaj që po tregoni ju”.');
INSERT INTO "hasan_efendi" VALUES(12,19,'12:19','(Ndërkohë në pus) erdhi një karvan. Ata dërguan ujëmbartësin e vet dhe ai lëshoi kovën e tij dhe thirri: “O sa gëzim! Qenka një djalë!” Ata e fshehën atë si plaçkë tregtie, por Allahu e dinte mirë çfarë bënin ata.');
INSERT INTO "hasan_efendi" VALUES(12,20,'12:20','Ata e shitën Jusufin me një çmim të ulët, veç disa grosh, pa pasur fare lakmi (për para).');
INSERT INTO "hasan_efendi" VALUES(12,21,'12:21','Një njeri nga Egjipti që e bleu, i tha gruas së vet: “Bëja të këndshëm qëndrimin këtu. Ndoshta do të na shërbejë ose do ta birësojmë!” Dhe kështu, Ne e vendosëm Jusufin në atë tokë të bukur dhe i mësuam shpjegimin e ëndrrave. Allahu ka kontroll dhe fuqi të plotë mbi çështjet e Tij, por shumica e njerëzve nuk e dinë.');
INSERT INTO "hasan_efendi" VALUES(12,22,'12:22','Kur Jusufi arriti moshën e pjekurisë, Ne i dhamë aftësinë e të gjykuarit dhe dituri. Kështu i shpërblejmë Ne punëmirët.');
INSERT INTO "hasan_efendi" VALUES(12,23,'12:23','Por ajo, në shtëpinë e së cilës gjendej ai, nisi ta joshë në kundërshtim me dëshirën e tij. Ajo ia mbylli të gjitha dyert dhe tha: “Jam e gatshme për ty!” Kurse ai tha: “Allahu më ruajttë! Me të vërtetë, Zoti im ka treguar kujdes për mua dhe, pa dyshim, keqbërësit nuk do të shpëtojnë kurrë”.');
INSERT INTO "hasan_efendi" VALUES(12,24,'12:24','Ajo e dëshiroi atë; por edhe Jusufi do ta kishte dëshiruar atë, sikur të mos kishte parë provën e Zotit të vet. Kështu e larguam nga e keqja dhe vepra e shëmtuar, sepse ai, në të vërtetë, ishte nga robërit Tanë të sinqertë.');
INSERT INTO "hasan_efendi" VALUES(12,25,'12:25','Që të dy vrapuan nga dera dhe ajo ia shqeu këmishën atij nga mbrapa. Te dera, ata hasën burrin e saj. Ajo tha: “Çfarë dënimi meriton ai që kërkon t’i bëjë të keqe gruas sate, përveç burgosjes apo dënimit të dhembshëm?”');
INSERT INTO "hasan_efendi" VALUES(12,26,'12:26','(Jusufi) tha: “Ajo provoi të më joshte”. Një i afërt i saj (që u gjend aty), pohoi: “Nëse këmisha e tij është shqyer përpara, atëherë ajo thotë të vërtetën, kurse ai gënjen.');
INSERT INTO "hasan_efendi" VALUES(12,27,'12:27','Por, nëse këmisha e tij është shqyer prapa, atëherë ajo gënjen, ndërsa ai thotë të vërtetën”.');
INSERT INTO "hasan_efendi" VALUES(12,28,'12:28','Kur burri i saj pa se këmisha e Jusufit ishte shqyer nga mbrapa, tha: “Patjetër që kjo është një nga dredhitë tuaja (të femrave). Në të vërtetë, dredhitë tuaja janë të mëdha!');
INSERT INTO "hasan_efendi" VALUES(12,29,'12:29','Ti, o Jusuf, largohu nga kjo! Kurse ti (gruaja ime), kërko falje për këtë gjynah, sepse, në të vërtetë, ti je fajtore.”');
INSERT INTO "hasan_efendi" VALUES(12,30,'12:30','Disa gra (të parisë) në qytet po thoshin: “Gruaja e Azizit (ministrit) deshi ta joshte shërbëtorin e vet. Asaj i ka rënë vërtet në kokë dashuria për të. Ne besojmë se ajo e ka humbur fare”.');
INSERT INTO "hasan_efendi" VALUES(12,31,'12:31','Kur dëgjoi për thashethemet e tyre, ajo i thirri ato në gosti, çdonjërës i dha nga një thikë dhe (i) tha (Jusufit): “Dil para tyre!” Kur e panë, gratë u mahnitën nga bukuria e tij, prenë duart dhe thanë: “O Zot, o Zot! Ky nuk është njeri, por qenka një engjëll fisnik!”');
INSERT INTO "hasan_efendi" VALUES(12,32,'12:32','Ajo tha: “Ja, ky është ai për të cilin ju më qortuat. Jam përpjekur ta josh, por ai është ruajtur nga gjynahu. Por, nëse ai nuk bën atë që e urdhëroj unë, me siguri që do të burgoset dhe do të poshtërohet”.');
INSERT INTO "hasan_efendi" VALUES(12,33,'12:33','Ai tha: “O Zoti im, më shumë e dua burgun se atë ku më shtyjnë ato. Nëse Ti nuk e largon prej meje dredhinë e tyre, unë do të prirem drejt atyre dhe do të bëhem nga ata që nuk (i) dinë (ligjet e Tua).”');
INSERT INTO "hasan_efendi" VALUES(12,34,'12:34','Dhe Zoti e plotësoi lutjen e tij dhe e shmangu nga dredhitë e tyre. Me të vërtetë, Ai dëgjon dhe di çdo gjë.');
INSERT INTO "hasan_efendi" VALUES(12,35,'12:35','Pastaj u ra ndërmend atyre , pasi i panë shenjat (e pafajësisë së Jusufit) që ta burgosin për një kohë (në mënyrë që ngjarja të mos përhapej në popull).');
INSERT INTO "hasan_efendi" VALUES(12,36,'12:36','Bashkë me Jusufin hynë në burg edhe dy djelmosha. Njëri prej tyre tha: “Unë kam parë (në ëndërr) veten, se si shtrydhja verë”. Tjetri tha: “Unë kam parë në ëndërr, se si mbaja në kokë bukë, të cilën e hanin shpendët. Shpjegona kuptimin e ëndrrave. Ne, me të vërtetë, shohim se ti je njeri i mirë.”');
INSERT INTO "hasan_efendi" VALUES(12,37,'12:37','Jusufi tha: “Sa herë që t’ju vijë ndonjë ushqim (në ëndërr) si risk për ju, unë do t’ju lajmëroj se çfarë kuptimi ka, para se të bëhet realitet. Kjo është prej diturive që m’i ka mësuar Zoti im. Unë e kam flakur fenë e njerëzve që nuk besojnë Allahun e që mohojnë jetën tjetër.');
INSERT INTO "hasan_efendi" VALUES(12,38,'12:38','Unë besoj fenë e të parëve të mi - Ibrahimit, Is’hakut dhe Jakubit. Ne, kurrë nuk mund t’i shoqërojmë Allahut ndonjë gjë (në adhurim). Kjo është dhuntia e Allahut ndaj nesh dhe njerëzve të tjerë, ndonëse shumica e njerëzve nuk janë mirënjohës.');
INSERT INTO "hasan_efendi" VALUES(12,39,'12:39','O shokët e mi të burgut! A janë më mirë një mori zotash të ndryshëm apo Allahu, i Vetmi, Ngadhënjimtari?');
INSERT INTO "hasan_efendi" VALUES(12,40,'12:40','Ata që ju i adhuroni në vend të Allahut, janë vetëm emra, të cilët i keni vënë ju dhe të parët tuaj, pa pasur prej Tij asnjë argument. Vendimi i përket vetëm Allahut: Ai ka urdhëruar që të adhuroni vetëm Atë. Kjo është e vetmja fe e vërtetë, por shumica e njerëzve nuk e di.');
INSERT INTO "hasan_efendi" VALUES(12,41,'12:41','O shokët e mi të burgut! Njëri prej jush do t’i shërbejë verë pronarit të vet, kurse tjetri do të kryqëzohet, e shpendët do të hanë prej kokës së tij. Kështu është caktuar kjo gjë për të cilën pyetët ju!”');
INSERT INTO "hasan_efendi" VALUES(12,42,'12:42','Jusufi i tha njërit prej këtyre të dyve, për të cilin e dinte se do të shpëtonte: “Përmendmë mua te pronari yt!” Por djalli bëri që ai të harronte ta përmendte te pronari i vet dhe kështu, Jusufi mbeti edhe disa vjet në burg.');
INSERT INTO "hasan_efendi" VALUES(12,43,'12:43','Dhe tha mbreti: “Kam parë në ëndërr shtatë lopë të majme, të cilat po i hanin shtatë lopë të dobëta dhe kam parë shtatë kallinj të gjelbër e shtatë të tjerë të tharë. O paria ime, më shpjegoni ëndrrën time, nëse dini t’i interpretoni ëndrrat!”');
INSERT INTO "hasan_efendi" VALUES(12,44,'12:44','Ata thanë: “Këto janë ëndrra të ngatërruara e ne nuk dimë t’i shpjegojmë.”');
INSERT INTO "hasan_efendi" VALUES(12,45,'12:45','Njërit nga ata dy që shpëtoi (nga burgu), iu kujtua pas një kohe e tha: “Unë do t’ju njoftoj për shpjegimin e kësaj (ëndrre), vetëm më dërgoni (te Jusufi)!”');
INSERT INTO "hasan_efendi" VALUES(12,46,'12:46','(Shërbëtori) I tha: “O Jusuf, o njeri i drejtë! Na shpjego (ëndrrën) për shtatë lopë të majme, të cilat hahen prej shtatë lopëve të dobëta dhe për shtatë kallinj të gjelbër e shtatë tjerë të tharë, që të kthehem te njerëzit e ta dinë edhe ata”.');
INSERT INTO "hasan_efendi" VALUES(12,47,'12:47','(Jusufi) tha: “Do të mbillni shtatë vjet rresht. Prodhimin që do të korrni lëreni në kallinj, përveç një pakice që do t’ju shërbejë për të ngrënë,');
INSERT INTO "hasan_efendi" VALUES(12,48,'12:48','sepse pastaj do të vijnë shtatë vjet të thatë, të cilët do t’i harxhojnë ato që i keni ruajtur ju, përveç një pakice që mund të kurseni.');
INSERT INTO "hasan_efendi" VALUES(12,49,'12:49','Pastaj, pas këtyre do të vijë një vit plot shi për njerëzit, vit në të cilin ata do të shtrydhin (rrush etj.).”');
INSERT INTO "hasan_efendi" VALUES(12,50,'12:50','Mbreti tha: “Sillmani atë!” Kur (Jusufit) i erdhi i dërguari, ai i tha: “Kthehu te mbreti yt e pyete: ç’patën ato gra që prenë duart e veta?” Me të vërtetë, Zoti im i di dredhitë e tyre”!');
INSERT INTO "hasan_efendi" VALUES(12,51,'12:51','Mbreti u tha (grave): “Si qe puna juaj kur deshët ta joshnit Jusufin?”. Ato thanë: “Allahu na ruajttë! Ne nuk dimë asgjë të keqe për atë”. Gruaja e Azizit (ministrit) tha: “Tani do të dalë në shesh e vërteta! Unë u përpoqa që ta joshja atë; ai është vërtet njeri i drejtë”.');
INSERT INTO "hasan_efendi" VALUES(12,52,'12:52','(Jusufi) tha: “(E kërkova këtë hetim) që ta dijë ai , se unë nuk e kam tradhtuar gjatë kohës së mungesës së tij. Vërtet, Allahu nuk u jep fund të mirë dredhive të tradhtarëve.');
INSERT INTO "hasan_efendi" VALUES(12,53,'12:53','Unë nuk e shfajësoj veten, sepse shpirti (i njeriut) është fort i prirë për të keqe, përveç atij që e mëshiron Zoti im. Me të vërtetë, Zoti im është Falës dhe Mëshirëplotë”.');
INSERT INTO "hasan_efendi" VALUES(12,54,'12:54','Mbreti tha: “Ma sillni atë, do ta marr pranë vetes.” Pasi bisedoi me të, i tha: “Qysh sot, ti ke pozitë të lartë dhe je i besuar.”');
INSERT INTO "hasan_efendi" VALUES(12,55,'12:55','(Jusufi) i tha: “Më cakto përgjegjës të depove të vendit, sepse unë jam ruajtës dhe njohës (i mirë i këtyre punëve)”.');
INSERT INTO "hasan_efendi" VALUES(12,56,'12:56','Dhe kështu, Ne i dhamë Jusufit pozitë në atë vend dhe ai kishte çfarë të dëshironte. Ne e shpërblejmë me mëshirën Tonë, kë të duam dhe, punëmirëve nuk ua humbim shpërblimin.');
INSERT INTO "hasan_efendi" VALUES(12,57,'12:57','Ndërsa shpërblimi i jetës tjetër është më i mirë për ata që besojnë dhe e kanë frikë Allahun.');
INSERT INTO "hasan_efendi" VALUES(12,58,'12:58','Dhe erdhën vëllezërit e Jusufit e hynë tek ai. Ai i njohu, por ata nuk e njohën.');
INSERT INTO "hasan_efendi" VALUES(12,59,'12:59','Kur i furnizoi ata me ushqim të nevojshëm, u tha: “Më sillni vëllain tuaj prej babait! A nuk e shihni se unë jua jap masën të plotë dhe jam më i miri mikpritës?');
INSERT INTO "hasan_efendi" VALUES(12,60,'12:60','Nëse nuk ma sillni atë, nuk do të ketë më ushqim për ju dhe mos ejani tek unë”!');
INSERT INTO "hasan_efendi" VALUES(12,61,'12:61','Ata thanë: “Ne do të përpiqemi disi, që ta sjellim nga baba i tij dhe do ta bëjmë këtë”.');
INSERT INTO "hasan_efendi" VALUES(12,62,'12:62','Jusufi u tha shërbëtorëve të vet: “Ngarkoni barrët në kafshët e tyre e ua fusni brenda paratë! Ata do t’i gjejnë ato, kur të shkojnë te familja e vet, kështu që do të kthehen përsëri te ne”.');
INSERT INTO "hasan_efendi" VALUES(12,63,'12:63','Kur u kthyen te babai i tyre, ata thanë: “O ati ynë! Nuk do të na japin më ushqim. Prandaj dërgoje me ne vëllain tonë, që të mund të marrim ushqim; të sigurojmë se ne do ta ruajmë atë!”');
INSERT INTO "hasan_efendi" VALUES(12,64,'12:64','Ai u tha: “A mos vallë t’jua besoj juve, ashtu siç jua pata besuar më parë vëllanë e tij? Allahu është Ruajtësi më i mirë dhe Ai është më i Mëshirshmi i mëshiruesve”!');
INSERT INTO "hasan_efendi" VALUES(12,65,'12:65','Kur i çelën barrët e tyre dhe gjetën të hollat e tyre, që ua kishin kthyer, ata thanë: “O ati ynë! Ç’duam më tepër? Këto janë gjërat tona; na janë kthyer. Ne do ta furnizojmë familjen tonë, do ta ruajmë vëllain tonë dhe do ta shtojmë një barrë deveje (ushqim), e kjo është barrë e lehtë.”');
INSERT INTO "hasan_efendi" VALUES(12,66,'12:66','Ai tha: “Unë kurrsesi nuk do ta dërgoj me ju, derisa të betoheni në Allahun, se do të ma ktheni, me përjashtim të rastit kur ju rrezikon ndonjë e keqe e madhe (të gjithëve).” Kur ata u betuan, ai u tha: “Allahu është dorëzënës për atë që flasim ne.');
INSERT INTO "hasan_efendi" VALUES(12,67,'12:67','O djemtë e mi, mos hyni nga e njëjta portë (e qytetit), por nga porta të ndryshme. Unë nuk mund të largoj prej jush asgjë nga (ajo që ju ka caktuar) Allahu! Se pushteti i përket vetëm Allahut. Tek Ai mbështetem unë dhe tek Ai le të mbështeten ata që dëshirojnë të jenë të mbështetur (tek Allahu)”!');
INSERT INTO "hasan_efendi" VALUES(12,68,'12:68','Kur ata hynë (në qytet), ashtu siç i urdhëroi babai i tyre, dëshira e Jakubit u përmbush, por kjo nuk i ndihmoi aspak para caktimit të Allahut. Ai (Jakubi), në të vërtetë, ka qenë dijetar i madh, sepse Ne e kemi mësuar, por shumica e njerëzve nuk e di.');
INSERT INTO "hasan_efendi" VALUES(12,69,'12:69','Kur dolën para Jusufit, ky e afroi vëllanë e vet (Beniaminin) dhe i tha: “Unë në fakt jam vëllai yt dhe mos u hidhëro për atë që më bënë vëllezërit tanë!”');
INSERT INTO "hasan_efendi" VALUES(12,70,'12:70','Pasi i furnizoi me ushqimin e nevojshëm, (Jusufi) shtiu një gotë (të çmueshme) në ngarkesën e vëllait të vet e pastaj një lajmëtar thirri: “O karvan, ju jeni vjedhës të vërtetë!”');
INSERT INTO "hasan_efendi" VALUES(12,71,'12:71','Ata u kthyen drejt tyre (e thanë): “Ç’ju ka humbur”?');
INSERT INTO "hasan_efendi" VALUES(12,72,'12:72','Thanë: “Po kërkojmë gotën e mbretit. Kush e sjell atë, ka shpërblim një barrë deveje ushqim. Unë për këtë jam dorëzënës”.');
INSERT INTO "hasan_efendi" VALUES(12,73,'12:73','(Vëllezërit e Jusufit) thanë: “Betohemi për Allahun, ju e dini se ne nuk kemi ardhur të bëjmë turbullira në këtë vend dhe nuk jemi vjedhës”.');
INSERT INTO "hasan_efendi" VALUES(12,74,'12:74','Ata pyetën: “E cili do të jetë dënimi, nëse ju gënjeni”?');
INSERT INTO "hasan_efendi" VALUES(12,75,'12:75','(Vëllezërit) thanë: “Dënimi për atë, në barrën e të cilit gjendet gota, është që të mbahet peng ai vetë. Kështu i dënojmë ne keqbërësit.”');
INSERT INTO "hasan_efendi" VALUES(12,76,'12:76','Dhe nisi të kontrollojë barrët e tyre para barrës së vëllait të Jusufit. Pastaj nxori gotën nga barra e vëllait të tij. Kështu, Ne e mësuam Jusufin, që ta bënte këtë dredhi. Ai, sipas ligjit të mbretit nuk mund ta merrte (për skllav) vëllanë e vet, përveç se me vullnetin e Allahut. Ne e ngremë lart atë që duam, e mbi çdo të ditur është një i Gjithëdijshëm.');
INSERT INTO "hasan_efendi" VALUES(12,77,'12:77','(Bijtë e Jakubit) thanë: “Nëse ka vjedhur, më parë ka vjedhur edhe vëllai i tij!” Jusufi heshti për këtë dhe nuk shpalli gjë, por mendoi në vetvete: “Ju jeni në pozitë më të keqe. Allahu është më i dijshëm për ato që thoni ju”.');
INSERT INTO "hasan_efendi" VALUES(12,78,'12:78','(Bijtë e Jakubit) thanë: “O ministër, ai e ka babanë shumë plak, andaj merr ndonjërin prej nesh në vend të tij! Ne po shohim se ti je bamirës”.');
INSERT INTO "hasan_efendi" VALUES(12,79,'12:79','(Jusufi) tha: “Allahu na ruajttë, si të marrim atë tjetrin në vend të atij te i cili e kemi gjetur sendin tonë! Atëherë, ne, me të vërtetë, do të ishim të padrejtë!”');
INSERT INTO "hasan_efendi" VALUES(12,80,'12:80','Meqë e humbën shpresën në të (Jusufin), u shpërndanë dhe u këshilluan fshehurazi. Më i madhi nga ata tha: “A nuk e dini që babai juaj ka marrë prej jush betimin në Allahun. Ju edhe më parë e keni shkelur betimin që bëtë për Jusufin. Nuk do të ndahem prej këtij vendi, derisa të më lejojë babai im ose të më gjykojë Allahu, sepse Ai është Gjykatësi më i mirë.');
INSERT INTO "hasan_efendi" VALUES(12,81,'12:81','Kthehuni te babai juaj dhe thoni: “O babai ynë, djali yt ka vjedhur. Ne dëshmojmë vetëm për atë që dimë. Ne nuk mund ta parashikonim të panjohurën .');
INSERT INTO "hasan_efendi" VALUES(12,82,'12:82','Pyet banorët e qytetit në të cilin kemi qenë dhe karvanin me të cilin kemi udhëtuar dhe ki besim se ne jemi të drejtë”!');
INSERT INTO "hasan_efendi" VALUES(12,83,'12:83','Jakubi tha: “Nuk është kështu, por ju keni kurdisur diçka. Unë do të kem durim. Shpresoj se Allahu do të m’i kthejë të gjitha, sepse Ai, në të vërtetë, është i Gjithëdijshëm dhe i Urtë”.');
INSERT INTO "hasan_efendi" VALUES(12,84,'12:84','U largua prej tyre dhe tha: “O i mjeri unë për Jusufin!” Sytë i ishin zbardhur prej pikëllimit dhe ishte plot zemërim.');
INSERT INTO "hasan_efendi" VALUES(12,85,'12:85','Ata thanë: “Për Allahun, ti po e përmend Jusufin pa pushim, saqë do të sëmuresh rëndë ose do të vdesësh!”');
INSERT INTO "hasan_efendi" VALUES(12,86,'12:86','(Jakubi) tha: “Unë dhimbjen time dhe pikëllimin tim ia paraqes vetëm Allahut e nga Allahu di atë që nuk e dini ju”.');
INSERT INTO "hasan_efendi" VALUES(12,87,'12:87','“O bijtë e mi, shkoni e kërkoni lajme për Jusufin dhe vëllain e tij dhe mos e humbni shpresën në mëshirën e Allahut. Shpresën në mëshirën e Allahut nuk e humb askush, përveç jobesimtarëve”.');
INSERT INTO "hasan_efendi" VALUES(12,88,'12:88','Kur ata dolën para Jusufit, thanë: “O ministër, ne dhe familjen tonë e ka goditur një fatkeqësi e madhe dhe kemi sjellë pak gjëra! Sidoqoftë, na e plotëso masën (për ushqimin) tonë dhe na jep lëmoshë, sepse Allahu i shpërblen dhënësit e lëmoshës”.');
INSERT INTO "hasan_efendi" VALUES(12,89,'12:89','Ai tha: “A nuk e mbani mend çfarë keni bërë me Jusufin dhe vëllanë e tij, kur ishit të paditur?”');
INSERT INTO "hasan_efendi" VALUES(12,90,'12:90','Ata thanë: “Vallë, ti je vërtet Jusufi?!” Ai u përgjigj: “Po, unë jam Jusufi e ky është vëllai im. Allahu na mëshiroi. Atë që ruhet (nga gjynahet) dhe bëhet i durueshëm, Allahu e shpërblen, se Ai kurrë nuk ia humb shpërblimin punëmirëve”.');
INSERT INTO "hasan_efendi" VALUES(12,91,'12:91','Ata thanë: “Betohemi për Allahun, vërtet që Allahu të ka ngritur ty mbi ne dhe ne paskemi qenë vërtet të gabuar”.');
INSERT INTO "hasan_efendi" VALUES(12,92,'12:92','Ai u tha: “Sot s’ka qortim për ju; Allahu do t’ju falë. Ai është më i Mëshirshmi i mëshiruesve!');
INSERT INTO "hasan_efendi" VALUES(12,93,'12:93','Shkoni me këtë këmishë timen dhe vëreni në fytyrën e atit tim e ai do të shohë, dhe m’i sillni tërë familjet tuaja!”');
INSERT INTO "hasan_efendi" VALUES(12,94,'12:94','Kur karvani u largua nga vendi (Egjipti), babai i tyre tha: “Unë, me të vërtetë, po ndiej erën e Jusufit, ndonëse ju mund të më quani matuf ”.');
INSERT INTO "hasan_efendi" VALUES(12,95,'12:95','Ata thanë: “Për Allahun, me të vërtetë, ti edhe tani qëndron në gabimin e mëparshëm”.');
INSERT INTO "hasan_efendi" VALUES(12,96,'12:96','Kur erdhi sjellësi i lajmit të mirë dhe ia vuri këmishën në fytyrë, atij iu kthye shikimi. Ai tha: “A nuk ju kam thënë se unë di nga Allahu atë që ju nuk e dini”.');
INSERT INTO "hasan_efendi" VALUES(12,97,'12:97','Ata thanë: “O babai ynë, kërko falje për ne e për gabimet tona! Ne paskemi qenë vërtet gjynahqarë”.');
INSERT INTO "hasan_efendi" VALUES(12,98,'12:98','Ai u përgjigj: “Do ta lus Zotin tim që t’ju falë. Pa dyshim që Ai është Falësi dhe Mëshirëploti”.');
INSERT INTO "hasan_efendi" VALUES(12,99,'12:99','Kur ata hynë te Jusufi, ai i afroi pranë prindërit e vet dhe tha: “Hyni në Egjipt! Me lejen e Allahut, ju do të jeni të sigurt prej çdo të keqeje”.');
INSERT INTO "hasan_efendi" VALUES(12,100,'12:100','Ai i ngriti prindërit e vet në fron dhe ata të gjithë iu përulën atij. Ai tha: “O babai im, ky është shpjegimi i ëndrrës sime të dikurshme. Zoti im e përmbushi atë. Mua më bëri mirësi, kur më nxori nga burgu, kurse ju ju solli nga shkretëtira, pasi djalli pati futur ngatërresë midis meje dhe vëllezërve të mi. Vërtet, Zoti im është i Butë me kë të dojë; Ai është i Gjithëdijshmi dhe i Urti!');
INSERT INTO "hasan_efendi" VALUES(12,101,'12:101','O Zoti im! Ti më ke dhënë pushtet dhe më ke mësuar shpjegimin e ëndrrave! O Krijues i qiejve dhe i Tokës, Ti je Mbrojtësi im edhe në këtë botë, edhe në botën tjetër! Bëj që të vdes si mysliman dhe më shoqëro me punëmirët (në botën tjetër)!”');
INSERT INTO "hasan_efendi" VALUES(12,102,'12:102','Këto janë disa nga historitë e fshehta, të cilat Ne po t’i shpallim ty (o Muhamed). Ti nuk ke qenë me ata, kur vendosnin për punën e tyre dhe kurdisnin dredhira.');
INSERT INTO "hasan_efendi" VALUES(12,103,'12:103','Shumica e njerëzve, sado që të dëshirosh ti, nuk janë besimtarë.');
INSERT INTO "hasan_efendi" VALUES(12,104,'12:104','Ti nuk po kërkon prej tyre kurrfarë shpërblimi për këtë. Ai (Kurani) është vetëm këshillë për të gjithë njerëzit.');
INSERT INTO "hasan_efendi" VALUES(12,105,'12:105','Eh, sa shumë shenja ka në qiej dhe në Tokë, pranë të cilave ata kalojnë pa ua vënë veshin!');
INSERT INTO "hasan_efendi" VALUES(12,106,'12:106','Shumica e tyre nuk besojnë në Allahun, pa i shoqëruar Atij njëkohësisht (idhuj të rremë) në adhurim.');
INSERT INTO "hasan_efendi" VALUES(12,107,'12:107','A janë të sigurt ata se nuk do t’i godasë një dënim gjithëpërfshirës nga Allahu apo që nuk do t’u vijë papritmas Ora, ndërkohë që janë të pavetëdijshëm?');
INSERT INTO "hasan_efendi" VALUES(12,108,'12:108','Thuaj: “Kjo është rruga ime: të ftoj drejt Allahut me dituri të plotë, unë dhe çdokush që më pason mua. I përlëvduar qoftë Allahu; unë nuk jam prej idhujtarëve!”');
INSERT INTO "hasan_efendi" VALUES(12,109,'12:109','Para teje, Ne kemi dërguar vetëm burra nga banorët e qyteteve, të cilët i frymëzuam me shpallje. Vallë! A nuk kanë udhëtuar ata nëpër botë për të parë se si ka qenë fundi i popujve, që kanë jetuar para tyre? Pa dyshim, bota tjetër është më e mirë për ata që (e) kanë frikë (Allahun). Pra, a nuk kuptoni?');
INSERT INTO "hasan_efendi" VALUES(12,110,'12:110','Dhe, kur të dërguarit gati e humbën shpresën dhe menduan se do të shpalleshin gënjeshtarë, atëherë u arriti ndihma Jonë: Ne shpëtojmë atë që duam dhe dënimi Ynë nuk zmbrapset nga populli keqbërës.');
INSERT INTO "hasan_efendi" VALUES(12,111,'12:111','Në tregimet e atyre (të dërguarve) ka këshilla për ata që janë me mend. Ky (Kuran) nuk është tregim i trilluar, por ai është vërtetues i atyre (librave) që janë shpallur para tij, është shpjegues i çdo gjëje dhe udhërrëfyes e mëshirë për njerëzit besimtarë.');
INSERT INTO "hasan_efendi" VALUES(13,1,'13:1','Elif Lâm Mîm Râ’. Këto janë vargjet e Librit (Kuranit)! Ajo që të është shpallur ty (Muhamed) prej Zotit tënd është e Vërteta, por shumica e njerëzve nuk e besojnë.');
INSERT INTO "hasan_efendi" VALUES(13,2,'13:2','Allahu është Ai që qiejt i ka ngritur me shtylla, të cilat nuk mund t’i shihni e më pas është ngritur mbi Fron. Ai ka nënshtruar Diellin dhe Hënën; të gjitha këto rrotullohen për një kohë të caktuar. Ai i drejton të gjitha çështjet dhe i shpjegon në hollësi shpalljet, me qëllim që ju të bindeni se do të takoheni me Zotin tuaj.');
INSERT INTO "hasan_efendi" VALUES(13,3,'13:3','Është Ai që e ka shtrirë tokën, duke krijuar në të male dhe lumenj. Dhe për çdo lloj frute, Ai ka krijuar nga një çift. Ai ka bërë që nata ta mbulojë ditën. Me të vërtetë, në këto ka shenja për njerëzit që mendojnë.');
INSERT INTO "hasan_efendi" VALUES(13,4,'13:4','Në Tokë ka vise që janë të lidhura njëri me tjetrin, ka kopshte të mbjella me hardhi, ka ara dhe palma me një pip ose shumë pipa, të cilat ujiten me të njëjtin ujë, por disa nga frutat i bëjmë më të shijshme nga të tjerat. Pa dyshim, në këto ka shenja për njerëzit që kanë mend.');
INSERT INTO "hasan_efendi" VALUES(13,5,'13:5','Nëse ti (Muhamed) çuditesh nga idhujtarët, janë vërtet të çuditshme fjalët e tyre: “Vallë, pasi të bëhemi dhé, do të krijohemi vërtet përsëri?!” Këta janë ata që nuk besojnë në Zotin e tyre dhe në qafat e të cilëve do të ketë vargonj; këta janë banorët e Xhehenemit, në të cilin do të qëndrojnë përherë.');
INSERT INTO "hasan_efendi" VALUES(13,6,'13:6','Ata të kërkojnë ty me ngut t’u sjellësh të keqen para së mirës, ndonëse para tyre ka pasur shumë dënime shembullore. Zoti yt është Falës për njerëzit, pavarësisht gjynaheve të tyre, por, në të vërtetë, Zoti yt është edhe i ashpër në dënim.');
INSERT INTO "hasan_efendi" VALUES(13,7,'13:7','Jobesimtarët thonë: “Sikur t’i ishte dërguar atij një mrekulli nga Zoti i tij?” Në të vërtetë, ti je vetëm paralajmërues. E, për çdo popull ka udhërrëfyes.');
INSERT INTO "hasan_efendi" VALUES(13,8,'13:8','Allahu e di se ç’mbart çdo femër dhe e di sa shkurtohet e sa zgjatet (koha e lindjes). Çdo gjë tek Ai është me masë.');
INSERT INTO "hasan_efendi" VALUES(13,9,'13:9','Ai i di gjërat e dukshme dhe të padukshme. Ai është i Madhi, i Larti.');
INSERT INTO "hasan_efendi" VALUES(13,10,'13:10','(Për Atë) njëlloj është: si ai prej jush që flet me vete edhe ai që flet me zë të lartë, si ai që fshihet natën edhe ai që ecën ditën.');
INSERT INTO "hasan_efendi" VALUES(13,11,'13:11','Njeriu ka përcjellës (engjëj), para tij dhe pas tij, që e mbikëqyrin atë me urdhrin e Allahut. Me të vërtetë, Allahu nuk e ndryshon gjendjen e një populli, derisa ata të ndryshojnë ç’kanë në vetvete. Kur Ai dëshiron ta ndëshkojë një popull, askush nuk mund ta pengojë Atë; dhe ata, përveç Tij, s’kanë asnjë ndihmës.');
INSERT INTO "hasan_efendi" VALUES(13,12,'13:12','Ai jua tregon juve vetëtimën, duke ju bërë të frikësoheni e të shpresoni; është Ai që krijon re të rënda (me shi).');
INSERT INTO "hasan_efendi" VALUES(13,13,'13:13','Bubullima e madhëron Atë me falënderim, por edhe engjëjt, nga frika ndaj Tij; Ai i dërgon rrufetë dhe godet me to kë të dojë; - dhe ata përsëri diskutojnë për Allahun, ndërkohë që Ai është i ashpër në ndëshkime.');
INSERT INTO "hasan_efendi" VALUES(13,14,'13:14','Vetëm për Atë është lutja e vërtetë! Kurse idhujt, të cilëve jobesimtarët u luten në vend të Allahut, nuk u përgjigjen lutjeve të tyre aspak. Rasti i tyre është si rasti i atij që i zgjat duart tek uji, për t’i ardhur te goja, por ai kurrsesi nuk i vjen. Lutja e jobesimtarëve është e kotë.');
INSERT INTO "hasan_efendi" VALUES(13,15,'13:15','Allahut i përulet në sexhde gjithçka që gjendet në qiej dhe në Tokë, me hir a me pahir, si dhe hijet e tyre i përulen në mëngjes dhe në mbrëmje.');
INSERT INTO "hasan_efendi" VALUES(13,16,'13:16','Thuaj: “Kush është Zoti i qiejve dhe i Tokës?” dhe përgjigju: “Allahu!” Thuaj: “A mos vallë, ju zgjidhni për mbrojtës në vend të Tij, ata që nuk mund t’i sjellin vetes as dobi, as dëm?” Thuaj: “A janë të njëjtë i verbri dhe ai që sheh? Ose a janë njësoj terri dhe drita? Apo ata (idhujtarët) i veshin Allahut ortakë, të cilët krijojnë siç krijon Ai, kështu që krijimi u ngjan i njëjtë atyre (idhujtarëve)?!” Thuaj: “Allahu është Krijuesi i çdo gjëje dhe Ai është i Vetmi, Ngadhënjimtari”.');
INSERT INTO "hasan_efendi" VALUES(13,17,'13:17','Ai e zbret shiun nga qielli, prandaj rrjedhin lumenjtë me masë dhe rrjedha bart mbeturinat mbi sipërfaqen e ujit. Edhe zgjyra, kur e shkrijnë njerëzit në zjarr që të bëjnë stoli ose vegla, është mbeturinë, si ajo. Kështu, Allahu jep shembullin e së vërtetës dhe të së pavërtetës: Mbeturinat hidhen, ndërsa ajo që u shërben njerëzve mbetet në tokë. Kështu, Allahu i shpjegon shembujt.');
INSERT INTO "hasan_efendi" VALUES(13,18,'13:18','Për ata që i përgjigjen Zotit të tyre, është përgatitur e mira e madhe. Ndërsa ata që nuk i përgjigjen Atij - sikur të ishte e tyre gjithçka që gjendet në Tokë, madje edhe një herë aq, ata do ta sakrifikonin atë (për të shpëtuar). Njerëzve të tillë u është përgatitur llogari e rëndë dhe strehimi i tyre është Xhehenemi. Eh, sa shtrat i shëmtuar që është ai!');
INSERT INTO "hasan_efendi" VALUES(13,19,'13:19','Vallë, ai që di se ajo që të është shpallur ty (Muhamed) nga Zoti yt është e vërteta, a është njësoj me atë që është i verbër (ndaj këtij fakti)? Vetëm njerëzit që kuptojnë ia vënë veshin kësaj.');
INSERT INTO "hasan_efendi" VALUES(13,20,'13:20','Këta janë ata që i përmbahen besëlidhjes me Allahun dhe nuk e prishin premtimin;');
INSERT INTO "hasan_efendi" VALUES(13,21,'13:21','dhe ata që mbajnë lidhjet (familjare) që ka urdhëruar Allahu të mbahen, që kanë frikë Zotin e tyre e i frikësohen llogarisë së rëndë;');
INSERT INTO "hasan_efendi" VALUES(13,22,'13:22','dhe ata që durojnë për të fituar dashurinë e Zotit të tyre, që falin namazin, që ndajnë fshehurazi dhe haptazi nga ajo që ua kemi dhënë Ne dhe, që të keqen, e kthejnë me të mirë. Këtyre u takon shpërblimi në Shtëpinë e fundit:');
INSERT INTO "hasan_efendi" VALUES(13,23,'13:23','Xheneti i Adnit, në të cilin do të hyjnë ata dhe besimtarët e ndershëm prej prindërve të tyre, grave dhe pasardhësve të tyre. Dhe engjëjt do të hyjnë tek ata nga çdo derë (e Xhenetit)');
INSERT INTO "hasan_efendi" VALUES(13,24,'13:24','(e do t’u thonë): “Shpëtimi qoftë mbi ju, për durimin që keni treguar (në jetën e kësaj bote)! Sa vendbanim i bukur që është ky!”');
INSERT INTO "hasan_efendi" VALUES(13,25,'13:25','Sa për ata që shkelin besëlidhjen me Allahun, pasi janë zotuar për atë e, që këpusin lidhjet (familjare) që ka urdhëruar Allahu të ruhen dhe bëjnë çrregullime në Tokë - mbi ta rëndon mallkimi dhe përfundimi i keq!');
INSERT INTO "hasan_efendi" VALUES(13,26,'13:26','Allahu ia shton furnizimin kujt të dojë dhe ia pakëson kujt të dojë. Jobesimtarët i gëzohen jetës së kësaj bote, por jeta e kësaj bote në krahasim me botën tjetër është vetëm kënaqësi kalimtare.');
INSERT INTO "hasan_efendi" VALUES(13,27,'13:27','Jobesimtarët thonë: “Pse nuk i dërgohet atij (Muhamedit a.s.) një mrekulli nga Zoti i tij?” Thuaj: “Me të vërtetë, Allahu shpie në humbje ata që Ai do, ndërsa udhëzon në rrugë të drejtë ata që kthehen tek Ai,');
INSERT INTO "hasan_efendi" VALUES(13,28,'13:28','ata që besojnë dhe zemrat e të cilëve, kur përmendet Allahu, qetësohen. Vërtet, zemrat qetësohen me përmendjen e Allahut!”');
INSERT INTO "hasan_efendi" VALUES(13,29,'13:29','Për ata që besojnë dhe bëjnë vepra të mira ka çdo mirësi dhe vendbanim të mrekullueshëm!');
INSERT INTO "hasan_efendi" VALUES(13,30,'13:30','Dhe, kështu Ne të dërguam ty tek një popull, para të cilit kanë kaluar shumë popuj, që ti t’u lexosh atyre shpalljen që të kemi dhënë, sepse ata nuk e besojnë të Gjithëmëshirshmin. Thuaj: “Ai është Zoti im, s’ka zot tjetër përveç Tij; tek Ai mbështetem dhe tek Ai kthehem”!');
INSERT INTO "hasan_efendi" VALUES(13,31,'13:31','(Idhujtarët e Mekës thonë:) Sikur të kishte një Kuran me të cilin të lëviznin malet ose të copëtohej toka, ose të flisnin të vdekurit (që të besojmë Muhamedin)? Jo, e gjithë çështja i takon (vetëm) Allahut. Vallë, a nuk e dinë ata që besojnë se sikur të donte Allahu, do t’i kishte udhëzuar të gjithë njerëzit (në besim)?! Kurse, jobesimtarët (e Mekës), për atë që kanë bërë, vazhdon t’i godasë shkatërrimi apo të bjerë pranë shtëpive të tyre, derisa të kryhet premtimi i Allahut . Nuk ka dyshim se Allahu e kryen premtimin e Tij.');
INSERT INTO "hasan_efendi" VALUES(13,32,'13:32','Edhe para teje janë tallur me të dërguar, andaj Unë u kam dhënë afat atyre që kanë mohuar e pastaj i kam ndëshkuar; eh si ka qenë dënimi Im!');
INSERT INTO "hasan_efendi" VALUES(13,33,'13:33','Vallë, Ai që është mbikëqyrës për çdokënd se çfarë punon, a është njësoj si idhujt që s’dëgjojnë e s’shohin gjë?! E megjithatë ata i shoqërojnë Allahut zota të rremë (në adhurim)? Thuaj: “Më tregoni emrat e atyre (idhujve që ia bëni shok Allahut)!” Apo ju po e njoftoni Atë për ndonjë gjë që Ai nuk e di në tokë apo thoni vetëm fjalë boshe? Jo, por jobesimtarëve dinakëritë e veta u duken të hijshme; ata janë larguar nga rruga e drejtë. Për atë që Allahu e shpie në humbje, s’ka udhërrëfyes.');
INSERT INTO "hasan_efendi" VALUES(13,34,'13:34','Për ata ka dënim në këtë jetë, por dënimi në jetën tjetër, me siguri, është edhe më i ashpër; për ata s’ka mbrojtës nga dënimi i Allahut.');
INSERT INTO "hasan_efendi" VALUES(13,35,'13:35','Xheneti që u është premtuar besimtarëve është i tillë që, nëpër të rrjedhin lumenj; frutat dhe freskia e tij janë të përhershme. Ky është fundi i besimtarëve, kurse fundi i jobesimtarëve është zjarri.');
INSERT INTO "hasan_efendi" VALUES(13,36,'13:36','Ata, të cilëve ua kemi zbritur Librin, gëzohen për atë që të kemi shpallur ty. Dhe ka disa prej grupeve që mohojnë diçka prej tij. Thuaj: “Mua më është urdhëruar që ta adhuroj vetëm Allahun dhe që të mos i shoqëroj Atij asgjë (në adhurim). Vetëm Atij i lutem dhe vetëm tek Ai kthehem”.');
INSERT INTO "hasan_efendi" VALUES(13,37,'13:37','Ja, kështu, Ne e kemi zbritur (Kuranin) në gjuhën arabe (për të qenë) si gjykim (për njerëzit). Por, nëse ti ndjek dëshirat e tyre, pas dijes që të ka ardhur, ti nuk do të kesh as ndihmës, as mbrojtës prej Zotit.');
INSERT INTO "hasan_efendi" VALUES(13,38,'13:38','Vërtet, edhe para teje Ne kemi sjellë të dërguar, të cilëve u kemi dhënë gra dhe pasardhës. Asnjë i dërguar nuk ka sjellë me vete asnjë mrekulli, përveçse me lejen e Allahut. Çdo periudhë e kohës ka librin e vet (ku është shënuar caku i saj).');
INSERT INTO "hasan_efendi" VALUES(13,39,'13:39','Allahu fshin çfarë të dojë e ngulit çfarë të dojë (në këtë libër); dhe tek Ai është Libri amëz.');
INSERT INTO "hasan_efendi" VALUES(13,40,'13:40','Edhe nëse Ne të tregojmë ty nga dënimi që u kemi premtuar atyre apo ta marrim shpirtin më parë, detyra jote është të kumtosh Shpalljen, se llogaria është te Ne.');
INSERT INTO "hasan_efendi" VALUES(13,41,'13:41','A nuk e shohin ata se Ne po ua zvogëlojmë tokën nga skajet e saj? Allahu gjykon, dhe askush nuk mund ta thyejë gjykimin e Tij; Ai është i shpejtë në llogari.');
INSERT INTO "hasan_efendi" VALUES(13,42,'13:42','Dredhi kanë bërë edhe ata që kanë qenë para tyre, por Allahu është zotërues i të gjitha dredhive. Ai e di çfarë fiton çdo njeri dhe mohuesit do ta dinë se për kë do të jetë përfundimi më i mirë.');
INSERT INTO "hasan_efendi" VALUES(13,43,'13:43','Jobesimtarët thonë: “Ti nuk je i dërguar!” Thuaju (o Muhamed!): “Mjafton Allahu për dëshmitar midis meje dhe jush dhe ata që kanë dijeni për Librin (besimtarët çifutë dhe të krishterë të asaj kohe).”');
INSERT INTO "hasan_efendi" VALUES(14,1,'14:1','Elif Lâm Râ. Këtë Libër ta kemi shpallur ty (o Muhamed), për t’i nxjerrë njerëzit, me lejen e Zotit të tyre, nga errësira në dritë, në rrugën e të Plotfuqishmit, të Denjit për çdo lavd,');
INSERT INTO "hasan_efendi" VALUES(14,2,'14:2','Allahut, të Cilit i përket gjithçka që gjendet në qiej dhe në Tokë. Mjerë mohuesit për dënimin e tmerrshëm (që i pret)!');
INSERT INTO "hasan_efendi" VALUES(14,3,'14:3','Mjerë ata që jetën e kësaj bote e duan më shumë se atë të botës tjetër, që largojnë njerëzit nga rruga e Allahut dhe dëshirojnë ta shtrembërojnë atë! Ata janë në humbje të madhe.');
INSERT INTO "hasan_efendi" VALUES(14,4,'14:4','Ne nuk kemi çuar asnjë të dërguar, përveçse në gjuhën e popullit të tij, për t’u shpjeguar atyre (shpalljet e Zotit). Allahu e shpie në humbje kë të dojë dhe e udhëzon në rrugë të drejtë kë të dojë. Ai është i Plotfuqishmi dhe i Urti.');
INSERT INTO "hasan_efendi" VALUES(14,5,'14:5','Ne e dërguam Musain me shenjat Tona (dhe i thamë): “Nxirre popullin tënd nga errësira në dritë dhe përkujtoju atyre ditët e Allahut !” Vërtet, në këtë ka shenja për çdo njeri që është i durueshëm dhe falënderues.');
INSERT INTO "hasan_efendi" VALUES(14,6,'14:6','(Kujto) kur Musai i tha popullit të vet: “Kujtoni dhuntinë që ju dha Allahu, kur ju shpëtoi nga populli i Faraonit, që ju mundonte me torturat më të rënda, duke vrarë djemtë tuaj e duke jua lënë gjallë femrat. Kjo ka qenë një sprovë e madhe për ju nga Zoti juaj.');
INSERT INTO "hasan_efendi" VALUES(14,7,'14:7','(Kujtoje) kur Zoti juaj kumtoi: “Nëse tregoheni mirënjohës, Unë do t’jua shtoj edhe më shumë begatitë e Mia; por, nëse ju mohoni mirësitë e Mia, (atëherë dijeni se) dënimi Im është i rëndë”.');
INSERT INTO "hasan_efendi" VALUES(14,8,'14:8','Dhe Musai tha: “Nëse mohoni ju dhe të gjithë ata që janë në Tokë, (dijeni se) Allahu është vërtet i Vetëmjaftueshëm dhe plot lavdi.”');
INSERT INTO "hasan_efendi" VALUES(14,9,'14:9','A nuk ju ka ardhur lajmi për ata që kanë qenë para jush: për popullin e Nuhut, fiset Ad e Themud dhe për ata pas tyre? Vetëm Allahu i di ata! Të dërguarit e tyre u sollën shenja të qarta, por ata vinin duart në gojë (me tallje) dhe thoshin: “Ne nuk besojmë në mesazhin tuaj. Ne kemi dyshim të madh ndaj asaj që na thërrisni ju”!');
INSERT INTO "hasan_efendi" VALUES(14,10,'14:10','Të dërguarit e tyre u thanë: “Vallë, a mund të dyshohet në Allahun, Krijuesin e qiejve dhe të Tokës?! Ai ju thërret juve për t’ju falur disa gjynahe tuajat dhe që t’ju lërë deri në afatin e caktuar”. Ata thanë: “Ju jeni vetëm njerëz si ne. Ju dëshironi të na shmangni nga ajo që kanë adhuruar të parët tanë. Andaj, na sillni prova të dukshme!”');
INSERT INTO "hasan_efendi" VALUES(14,11,'14:11','Të dërguarit e tyre u thanë: “Ne jemi vetëm njerëz si ju, por Allahu ia dhuron hirin e Tij kujt të dojë prej robërve të Vet, ne nuk mund t’ju sjellim prova pa urdhrin e Allahut. Besimtarët le të mbështeten vetëm tek Allahu.');
INSERT INTO "hasan_efendi" VALUES(14,12,'14:12','E përse të mos mbështetemi tek Allahu, kur Ai tashmë na ka treguar rrugën e drejtë, në të cilën po ecim? Ne do t’i durojmë përndjekjet tuaja e ata që besojnë, le të shpresojnë vetëm tek Allahu!”');
INSERT INTO "hasan_efendi" VALUES(14,13,'14:13','Mohuesit u thanë të dërguarve të tyre: “Ne do t’ju dëbojmë nga vendi ynë ose do të ktheheni në fenë tonë!” Atëherë, Zoti u shpalli (të dërguarve të Vet): “Ne do t’i zhdukim keqbërësit,');
INSERT INTO "hasan_efendi" VALUES(14,14,'14:14','e pas tyre do t’ju vendosim në tokën e tyre. Kjo është për ata që kanë frikë nga qëndrimi para Meje (për gjykim) dhe i tremben premtimit (dënimit) Tim.”');
INSERT INTO "hasan_efendi" VALUES(14,15,'14:15','Dhe (të dërguarit) kërkuan ndihmë (nga Allahu), kurse kokëfortët kryelartë u shkatërruan.');
INSERT INTO "hasan_efendi" VALUES(14,16,'14:16','Pas tyre është Xhehenemi, ku do të pinë ujë të qelbur,');
INSERT INTO "hasan_efendi" VALUES(14,17,'14:17','të cilin do ta gjerbin e nuk do të mund ta gëlltisin. Vdekja do t’u vijë atyre nga të gjitha anët, por nuk do të vdesin. Ata i pret një dënim i rëndë.');
INSERT INTO "hasan_efendi" VALUES(14,18,'14:18','Vepra e atyre, që nuk besojnë Zotin e tyre, i shëmbëllen hirit, të cilin e shpërndan era në një ditë të stuhishme. Ata nuk do të kenë asnjë dobi nga veprat që kanë punuar. Kjo është humbja e madhe.');
INSERT INTO "hasan_efendi" VALUES(14,19,'14:19','Vallë, a nuk sheh ti se si Allahu ka krijuar me urtësi qiejt dhe Tokën? Nëse dëshiron, Ai mund t’ju zhdukë ju dhe të sjellë një popull të ri;');
INSERT INTO "hasan_efendi" VALUES(14,20,'14:20','kjo nuk është punë e rëndë për Allahun Fuqiplotë.');
INSERT INTO "hasan_efendi" VALUES(14,21,'14:21','Të gjithë do të dalin para Allahut. (Atëherë), ata që kanë qenë të përulur, do t’u thonë atyre që kanë qenë kryelartë: “Ne kemi qenë ithtarët tuaj. A do të mund të lehtësoni ju prej nesh diçka nga dënimi i Allahut?” Ata do të thonë: “Sikur të na kishte udhëzuar Allahu në rrugën e drejtë, edhe ne do t’ju kishim udhëzuar ju. Rënkojmë apo durojmë, për ne njësoj është, nuk do të ketë shpëtim.”');
INSERT INTO "hasan_efendi" VALUES(14,22,'14:22','Dhe, kur të marrë fund puna, djalli do të thotë: “Vërtet, Allahu ju bëri një premtim të vërtetë. Edhe unë ju premtova, por ju mashtrova. Unë nuk kam pasur kurrfarë pushteti mbi ju. Unë thjesht ju thirra e ju m’u përgjigjët. Prandaj, mos më qortoni mua, por qortoni veten! Unë nuk jam shpëtimtari juaj e as ju nuk jeni shpëtimtarët e mi. Unë tani e mohoj që më bëtë ortak me Allahun (në adhurim).” Vërtet, keqbërësit do të kenë një dënim të dhembshëm.');
INSERT INTO "hasan_efendi" VALUES(14,23,'14:23','Ndërsa ata që besojnë dhe bëjnë vepra të mira, do të pranohen në kopshte, nëpër të cilat rrjedhin lumenj dhe ku, me lejen e Zotit të tyre, do të banojnë përherë. Atje do të përshëndeten me fjalën: “Selam!”');
INSERT INTO "hasan_efendi" VALUES(14,24,'14:24','Vallë, a nuk e sheh se si Allahu e përqas fjalën e mirë me pemën e mirë, e cila rrënjën e ka të fortë në tokë, kurse degët nga qielli?');
INSERT INTO "hasan_efendi" VALUES(14,25,'14:25','Me lejen e Zotit të saj (pema) jep fruta në çdo kohë. Allahu u tregon shembuj njerëzve, që t’ua vënë veshin.');
INSERT INTO "hasan_efendi" VALUES(14,26,'14:26','Fjala e keqe është si pema e keqe me rrënjë të shkulura, e paqëndrueshme për ekzistencë.');
INSERT INTO "hasan_efendi" VALUES(14,27,'14:27','Allahu i forcon besimtarët me fjalë të qëndrueshme në këtë botë dhe në botën tjetër, kurse keqbërësit i shpie në humbje; Allahu bën ç’të dojë.');
INSERT INTO "hasan_efendi" VALUES(14,28,'14:28','Vallë, a nuk i sheh ata që dhuntinë e Allahut e kanë ndërruar me mohim dhe e kanë sjellë popullin e tyre në Shtëpinë e shkatërrimit?');
INSERT INTO "hasan_efendi" VALUES(14,29,'14:29','Ata do të digjen në Xhehenem. Eh, sa vendbanim i tmerrshëm është ai!');
INSERT INTO "hasan_efendi" VALUES(14,30,'14:30','Ata kanë trilluar zota të rremë si ortakë të Allahut për t’i larguar (njerëzit) nga rruga e Tij. Thuaj: “Kënaquni, se përfundimi juaj është në zjarr!”');
INSERT INTO "hasan_efendi" VALUES(14,31,'14:31','Thuaju robërve të Mi, të cilët besojnë: Le të falin namazin dhe le të japin fshehurazi dhe haptazi nga ajo që ua kemi dhënë Ne, para se të arrijë dita në të cilën s’ka as tregti, as miqësi.');
INSERT INTO "hasan_efendi" VALUES(14,32,'14:32','Allahu është Ai që ka krijuar qiejt dhe Tokën. Ai lëshon ujë nga qielli e nëpërmjet tij nxjerr fruta si ushqim për ju. Ai i ka vënë në shërbimin tuaj anijet, që me lejen e Tij, ju të lundroni me ato nëpër dete. Ai i ka krijuar lumenjtë për dobinë tuaj');
INSERT INTO "hasan_efendi" VALUES(14,33,'14:33','e po ashtu edhe Diellin dhe Hënën, që lëvizin vazhdimisht. Ai ka vënë në shërbimin tuaj natën dhe ditën.');
INSERT INTO "hasan_efendi" VALUES(14,34,'14:34','Ai ju jep gjithçka që i kërkoni. Edhe nëse përpiqeni t’i numëroni dhuntitë e Allahut, nuk do të mund t’i numëroni dot. Njeriu është vërtet i padrejtë dhe mosmirënjohës.');
INSERT INTO "hasan_efendi" VALUES(14,35,'14:35','(Kujtoje) kur Ibrahimi tha: “O Zoti im, bëje të sigurt këtë qytet dhe më ruaj mua dhe pasardhësit e mi nga adhurimi i idhujve!');
INSERT INTO "hasan_efendi" VALUES(14,36,'14:36','O Zoti im, ata kanë shmangur shumë njerëz (nga rruga e drejtë). Ai që më pason mua, është i fesë sime; ndërsa për atë që më kundërshton mua, Ti, me të vërtetë, je Falës dhe Mëshirëplotë.');
INSERT INTO "hasan_efendi" VALUES(14,37,'14:37','O Zoti ynë, unë kam vendosur disa nga pasardhësit e mi, në një luginë që nuk mbillet, afër Shtëpisë Tënde të Shenjtë, që të falin namazin. Prandaj, bëj që zemrat e njerëzve të ndiejnë dashamirësi për ata dhe jepu fruta, me qëllim që ata të të falënderojnë.');
INSERT INTO "hasan_efendi" VALUES(14,38,'14:38','O Zoti ynë, Ti e di vërtet çfarë fshehim e çfarë shfaqim haptazi. Allahut nuk i fshihet asgjë, as në Tokë, as në qiell.');
INSERT INTO "hasan_efendi" VALUES(14,39,'14:39','Qoftë lavdëruar Allahu, i cili në pleqëri më dhuroi Ismailin dhe Is’hakun! Vërtet, Zoti im i dëgjon lutjet!');
INSERT INTO "hasan_efendi" VALUES(14,40,'14:40','O Zoti im, bëj që unë dhe pasardhësit e mi të kryejmë përherë namazin! O Zoti ynë, na i prano lutjet!');
INSERT INTO "hasan_efendi" VALUES(14,41,'14:41','O Zoti ynë, më fal mua, prindërit e mi dhe besimtarët në Ditën, kur do të jepet llogari!”');
INSERT INTO "hasan_efendi" VALUES(14,42,'14:42','Mos mendo kurrsesi që Allahu nuk e vëren atë që bëjnë keqbërësit! Ai vetëm ua shtyn (dënimin) deri në Ditën, kur sytë e tyre do të zgurdullohen (nga tmerret që do të shohin),');
INSERT INTO "hasan_efendi" VALUES(14,43,'14:43','dhe, duke vrapuar me kokë lart, nuk do të mund t’i lëvizin sytë, ndërsa zemrat e tyre do të jenë të zbrazëta (nga tmerri).');
INSERT INTO "hasan_efendi" VALUES(14,44,'14:44','Ti (o Muhamed) paralajmëroji njerëzit për Ditën që atyre do t’u vijë dënimi e keqbërësit do të thonë: “O Zoti ynë, na lër edhe për pak kohë! Ne do t’i përgjigjemi thirrjes Tënde dhe do t’i pasojmë të dërguarit!” Por atyre do t’u thuhet: “Vallë, a nuk betoheshit më parë se s’do të kishte zhdukje për ju?');
INSERT INTO "hasan_efendi" VALUES(14,45,'14:45','Ju banuat në vendbanimet e atyre që e kishin ngarkuar veten me gjynahe dhe e kishit të qartë se si kishim vepruar me ta. Dhe Ne ju kishim treguar shumë shembuj.”');
INSERT INTO "hasan_efendi" VALUES(14,46,'14:46','Ata kurdisin dredhitë e tyre, por Allahu i di dredhitë e tyre. Edhe sikur prej dredhive të tyre të shembeshin malet, ata nuk do t’ia dalin mbanë.');
INSERT INTO "hasan_efendi" VALUES(14,47,'14:47','As mos mendo se Allahu nuk do t’ua mbajë premtimin e Vet të dërguarve. Allahu është vërtet i Plotfuqishëm dhe i Zoti për shpagim.');
INSERT INTO "hasan_efendi" VALUES(14,48,'14:48','Ditën, kur Toka do të ndërrohet me tokë tjetër, por edhe qiejt, të gjithë do të dalin para të Vetmit dhe Ngadhënjyesit Allah.');
INSERT INTO "hasan_efendi" VALUES(14,49,'14:49','Dhe atë ditë do t’i shohësh keqbërësit të lidhur në pranga;');
INSERT INTO "hasan_efendi" VALUES(14,50,'14:50','rrobat do t’i kenë prej katrani e zjarri do t’ua mbështjellë fytyrat,');
INSERT INTO "hasan_efendi" VALUES(14,51,'14:51','(Kështu do të jetë) në mënyrë që Allahu të shpërblejë cilindo për atë që ka punuar. Allahu është vërtet i shpejtë në llogari.');
INSERT INTO "hasan_efendi" VALUES(14,52,'14:52','Ky (Kuran) është shpallje e qartë për njerëzit, me qëllim që ata të marrin paralajmërim nëpërmjet tij, që ta dinë se Allahu është Një Zot i Vetëm dhe që të këshillohen njerëzit e mençur!');
INSERT INTO "hasan_efendi" VALUES(15,1,'15:1','Elif Lâm Râ. Këto janë vargjet e Librit, të Kuranit të qartë!');
INSERT INTO "hasan_efendi" VALUES(15,2,'15:2','Do të vijë dita (e Kiametit), kur mohuesit do të dëshirojnë që të ishin myslimanë.');
INSERT INTO "hasan_efendi" VALUES(15,3,'15:3','Lëri ata që të hanë e të kënaqen dhe le t’i mashtrojë shpresa; ata do ta marrin vesh!');
INSERT INTO "hasan_efendi" VALUES(15,4,'15:4','Ne nuk kemi zhdukur asnjë vend, pa i ardhur koha e caktuar.');
INSERT INTO "hasan_efendi" VALUES(15,5,'15:5','Asnjë popull nuk mund as ta shpejtojë, as ta ngadalësojë afatin (e shkatërrimit të vet).');
INSERT INTO "hasan_efendi" VALUES(15,6,'15:6','Ata (idhujtarët) thonë: “O ti që të është shpallur Këshilla (Kurani), ti je vërtet i marrë!');
INSERT INTO "hasan_efendi" VALUES(15,7,'15:7','Përse nuk na sjell engjëjt, nëse ajo që thua është e vërtetë?”');
INSERT INTO "hasan_efendi" VALUES(15,8,'15:8','Ne i dërgojmë engjëjt vetëm me shpallje dhe dënim e atëherë (të dënuarve) nuk u jepet afat për të pritur.');
INSERT INTO "hasan_efendi" VALUES(15,9,'15:9','Sigurisht, Ne e kemi shpallur Kuranin dhe, sigurisht, Ne do ta ruajmë atë. ');
INSERT INTO "hasan_efendi" VALUES(15,10,'15:10','Ne u kemi çuar të dërguar edhe para teje, sekteve të mëparshme.');
INSERT INTO "hasan_efendi" VALUES(15,11,'15:11','Por ata talleshin me çdo të dërguar që u vinte.');
INSERT INTO "hasan_efendi" VALUES(15,12,'15:12','Ja, kështu Ne e bëjmë mohimin të futet në zemrat e gjynahqarëve.');
INSERT INTO "hasan_efendi" VALUES(15,13,'15:13','Ata nuk e besojnë atë , me gjithë shembujt e popujve të kaluar.');
INSERT INTO "hasan_efendi" VALUES(15,14,'15:14','Edhe sikur Ne të hapnim një derë të qiellit dhe të ngjiteshin nëpër të,');
INSERT INTO "hasan_efendi" VALUES(15,15,'15:15','ata me siguri që do të thoshin: “Sytë tanë kanë qenë të shushatur, madje na kanë magjepsur”!');
INSERT INTO "hasan_efendi" VALUES(15,16,'15:16','Ne kemi krijuar yjësi në qiell dhe e kemi zbukuruar atë për shikuesin.');
INSERT INTO "hasan_efendi" VALUES(15,17,'15:17','Ne e ruajmë atë (qiellin) nga çdo djall i mallkuar ,');
INSERT INTO "hasan_efendi" VALUES(15,18,'15:18','përveç ndonjërit që dëgjon vjedhurazi e të cilin e arrin një yll i këputur me flakë të dukshme.');
INSERT INTO "hasan_efendi" VALUES(15,19,'15:19','Dhe Tokën e kemi shtrirë e nëpër të kemi shpërndarë male të patundura. Ne kemi bërë që në tokë të rritet çdo gjë në përpjesëtim.');
INSERT INTO "hasan_efendi" VALUES(15,20,'15:20','E aty kemi bërë mjetet e jetesës për ju dhe për ata që nuk i mbani ju.');
INSERT INTO "hasan_efendi" VALUES(15,21,'15:21','Nuk ka asnjë send që të mos gjenden tek Ne thesaret e tij; por prej tij Ne japim me masë të caktuar.');
INSERT INTO "hasan_efendi" VALUES(15,22,'15:22','Ne i dërgojmë erërat për pllenim dhe zbresim nga qielli ujë, prej të cilit ju japim të pini e të cilin nuk mund ta depozitoni dot.');
INSERT INTO "hasan_efendi" VALUES(15,23,'15:23','Në të vërtetë, vetëm Ne e japim jetën dhe e sjellim vdekjen dhe vetëm Ne jemi trashëgimtarët (e gjithçkaje).');
INSERT INTO "hasan_efendi" VALUES(15,24,'15:24','Vetëm Ne i dimë ata që kanë qenë para jush dhe vetëm Ne i dimë ata, që do të vijnë pas jush.');
INSERT INTO "hasan_efendi" VALUES(15,25,'15:25','Sigurisht që Zoti yt do t’i tubojë ata të gjithë; Ai është vërtet i Urtë dhe i Gjithëdijshëm.');
INSERT INTO "hasan_efendi" VALUES(15,26,'15:26','Ne e kemi krijuar njeriun prej një balte të thatë të marrë nga balta e zezë pa formë,');
INSERT INTO "hasan_efendi" VALUES(15,27,'15:27','kurse para tij kemi krijuar xhindet nga zjarri pa tym.');
INSERT INTO "hasan_efendi" VALUES(15,28,'15:28','Kur Zoti yt u tha engjëjve: “Unë do të krijoj një njeri prej baltës së thatë të marrë nga balta e zezë pa formë.');
INSERT INTO "hasan_efendi" VALUES(15,29,'15:29','Kur t’i jap formën dhe t’i fryj prej shpirtit Tim , ju t’i përuleni atij në sexhde”,');
INSERT INTO "hasan_efendi" VALUES(15,30,'15:30','Të gjithë engjëjt së bashku iu përulën atij në sexhde,');
INSERT INTO "hasan_efendi" VALUES(15,31,'15:31','përveç Iblisit. Ai nuk pranoi t’u bashkohej atyre që bënë sexhde.');
INSERT INTO "hasan_efendi" VALUES(15,32,'15:32','Allahu i tha: “O Iblis, ç’pate ti që s’u bashkove me ata që u përulën?”');
INSERT INTO "hasan_efendi" VALUES(15,33,'15:33','Iblisi tha: “Unë nuk i përulem një vdekatari që Ti e ke krijuar nga balta e thatë e marrë prej baltës së zezë pa formë”.');
INSERT INTO "hasan_efendi" VALUES(15,34,'15:34','Allahu tha: “Atëherë, dil prej Xhenetit! Ti je vërtet i mallkuar!');
INSERT INTO "hasan_efendi" VALUES(15,35,'15:35','Le të jetë mallkimi mbi ty deri në Ditën e Kiametit”!');
INSERT INTO "hasan_efendi" VALUES(15,36,'15:36','Iblisi tha: “Zoti im, më jep afat deri në ditën kur ata do të ringjallen!”');
INSERT INTO "hasan_efendi" VALUES(15,37,'15:37','Zoti tha: “Sigurisht që do të të jepet afat');
INSERT INTO "hasan_efendi" VALUES(15,38,'15:38','deri në një ditë të caktuar." ');
INSERT INTO "hasan_efendi" VALUES(15,39,'15:39','Iblisi tha: “Zoti im, për shkak se më çove në humbje, unë do t’ju zbukuroj atyre (njerëzve) rrugën e gabuar në Tokë dhe të gjithë do t’i shpie në mashtrim,');
INSERT INTO "hasan_efendi" VALUES(15,40,'15:40','përveç robërve të Tu të sinqertë”.');
INSERT INTO "hasan_efendi" VALUES(15,41,'15:41','(Allahu) tha: “Kjo është rruga Ime e drejtë.');
INSERT INTO "hasan_efendi" VALUES(15,42,'15:42','Në të vërtetë, ti nuk do të kesh pushtet mbi robërit e Mi, përveçse mbi të humburit, që të pasojnë ty.”');
INSERT INTO "hasan_efendi" VALUES(15,43,'15:43','Pa dyshim, Xhehenemi është vendi i premtuar për të gjithë ata.');
INSERT INTO "hasan_efendi" VALUES(15,44,'15:44','Ai ka shtatë dyer e për çdo derë do të ketë një pjesë të caktuar.');
INSERT INTO "hasan_efendi" VALUES(15,45,'15:45','Vërtet, punëmirët do të jenë në kopshtet e Xhenetit pranë burimeve.');
INSERT INTO "hasan_efendi" VALUES(15,46,'15:46','Atyre do t’u thuhet: “Hyni atje me paqe dhe siguri”!');
INSERT INTO "hasan_efendi" VALUES(15,47,'15:47','Ne do ta heqim urrejtjen nga zemrat e tyre; ata do të rrinë ulur si vëllezër përballë njëri-tjetrit në divane.');
INSERT INTO "hasan_efendi" VALUES(15,48,'15:48','Aty nuk do t’i mundojë lodhja e prej aty kurrë nuk do të dëbohen.');
INSERT INTO "hasan_efendi" VALUES(15,49,'15:49','Lajmëroji robërit e Mi (o Muhamed) se, vërtet, Unë jam Falësi i madh, Mëshirëploti,');
INSERT INTO "hasan_efendi" VALUES(15,50,'15:50','dhe se dënimi Im është vërtet dënimi i dhembshëm!');
INSERT INTO "hasan_efendi" VALUES(15,51,'15:51','Tregoju atyre edhe për mysafirët e Ibrahimit!');
INSERT INTO "hasan_efendi" VALUES(15,52,'15:52','Ata shkuan tek ai dhe i thanë “Paqe!”, por ai tha: “Në të vërtetë, Ne kemi frikë nga ju.”');
INSERT INTO "hasan_efendi" VALUES(15,53,'15:53','Ata thanë: “Mos u frikëso, se ne po të sjellim një lajm të mirë! Ti do të kesh një djalë të dijshëm”!');
INSERT INTO "hasan_efendi" VALUES(15,54,'15:54','Ibrahimi tha: “Po ma sillni këtë lajm të mirë, edhe pse më ka goditur pleqëria?! Mbi ç’bazë po ma sillni këtë lajm?”');
INSERT INTO "hasan_efendi" VALUES(15,55,'15:55','Ata u përgjigjën: “Ne ta japim lajmin e mirë me vërtetësi, prandaj mos u bëj shpresëhumbur!”');
INSERT INTO "hasan_efendi" VALUES(15,56,'15:56','Ibrahimi tha: “E kush mund ta humbë shpresën nga mëshira e Zotit të vet, përveç atyre që janë të humbur”?!');
INSERT INTO "hasan_efendi" VALUES(15,57,'15:57','Ai i pyeti: “Cili është qëllimi juaj, o të dërguar?”');
INSERT INTO "hasan_efendi" VALUES(15,58,'15:58','(Ata) thanë: “Ne jemi dërguar te një popull keqbërës (për ta shkatërruar të gjithin),');
INSERT INTO "hasan_efendi" VALUES(15,59,'15:59','përveç familjes së Lutit. Ne do t’i shpëtojmë të gjithë ata,');
INSERT INTO "hasan_efendi" VALUES(15,60,'15:60','përveç gruas së tij; për atë Ne kemi caktuar si dënim që të mbetet pas me të tjerët.”');
INSERT INTO "hasan_efendi" VALUES(15,61,'15:61','Dhe, kur të dërguarit erdhën te Luti,');
INSERT INTO "hasan_efendi" VALUES(15,62,'15:62','ai tha: “Unë nuk ju njoh ju!”');
INSERT INTO "hasan_efendi" VALUES(15,63,'15:63','Ata thanë: “Jo, ne të kemi sjellë atë, në të cilën ata dyshojnë,');
INSERT INTO "hasan_efendi" VALUES(15,64,'15:64','të kemi sjellë të vërtetën, prandaj të jesh i sigurt se ajo që të themi, do të bëhet.');
INSERT INTO "hasan_efendi" VALUES(15,65,'15:65','Nisu me familjen tënde në një kohë të natës, shko pas tyre e askush prej jush të mos shikojë prapa, por vazhdoni në drejtimin e urdhëruar!”');
INSERT INTO "hasan_efendi" VALUES(15,66,'15:66','Dhe Ne ia shpallëm atij atë që do të ngjajë: që i gjithë populli i tij, deri në të mbramin, do të zhdukej në agim.');
INSERT INTO "hasan_efendi" VALUES(15,67,'15:67','Banorët e qytetit erdhën tek ai gjithë gëzim.');
INSERT INTO "hasan_efendi" VALUES(15,68,'15:68','Luti u tha: “Këta janë mysafirët e mi, andaj mos më turpëroni!');
INSERT INTO "hasan_efendi" VALUES(15,69,'15:69','Kijeni frikë Allahun e mos më poshtëroni!”');
INSERT INTO "hasan_efendi" VALUES(15,70,'15:70','Ata thanë: “A nuk ta kemi ndaluar ne që të presësh mysafirë?”');
INSERT INTO "hasan_efendi" VALUES(15,71,'15:71','Ai tha: “Ja vajzat e mia! Nëse doni të bëni çka synoni (martohuni me to)”.');
INSERT INTO "hasan_efendi" VALUES(15,72,'15:72','Betohem për jetën tënde (o Muhamed), se ata ishin vërtet të përhumbur në dehjen e tyre!');
INSERT INTO "hasan_efendi" VALUES(15,73,'15:73','Ata i kaploi zëri i tmerrshëm (i Xhebrailit) kur lindi Dielli, ');
INSERT INTO "hasan_efendi" VALUES(15,74,'15:74','dhe Ne e kthyem qytetin përmbys, duke lëshuar mbi ata shi gurësh prej dheut të pjekur.');
INSERT INTO "hasan_efendi" VALUES(15,75,'15:75','Këto janë njëmend shenja bindëse për ata, që i kuptojnë ato.');
INSERT INTO "hasan_efendi" VALUES(15,76,'15:76','Qyteti i rrënuar është në rrugë, ku gjendet edhe tani.');
INSERT INTO "hasan_efendi" VALUES(15,77,'15:77','Kjo është vërtet treguese për besimtarët.');
INSERT INTO "hasan_efendi" VALUES(15,78,'15:78','Por edhe banorët e Ejkes ishin keqbërës,');
INSERT INTO "hasan_efendi" VALUES(15,79,'15:79','andaj i ndëshkuam dhe që të dy janë në rrugë të dukshme.');
INSERT INTO "hasan_efendi" VALUES(15,80,'15:80','Edhe banorët e Hixhrës (fisi Themud), i quajtën të dërguarit për gënjeshtarë.');
INSERT INTO "hasan_efendi" VALUES(15,81,'15:81','Ne u patëm dhënë shenjat Tona, por ata shmangeshin prej tyre.');
INSERT INTO "hasan_efendi" VALUES(15,82,'15:82','Ata gdhendnin shtëpi në shkëmbinj, duke besuar se nuk do t’i gjente asgjë,');
INSERT INTO "hasan_efendi" VALUES(15,83,'15:83','por edhe ata në agim i goditi zëri i tmerrshëm');
INSERT INTO "hasan_efendi" VALUES(15,84,'15:84','dhe nuk patën aspak dobi nga ajo që patën ndërtuar.');
INSERT INTO "hasan_efendi" VALUES(15,85,'15:85','Ne i kemi krijuar qiejt, Tokën dhe gjithçka që gjendet midis tyre vetëm me urtësi. Ora (e Kiametit) do të vijë me siguri, andaj ti (o Muhamed) sillu me zemërgjerësi.');
INSERT INTO "hasan_efendi" VALUES(15,86,'15:86','Vërtet, Zoti yt është Krijuesi Madhështor, i Gjithëdijshmi.');
INSERT INTO "hasan_efendi" VALUES(15,87,'15:87','Ne të kemi shpallur shtatë vargje që përsëriten dhe Kuranin e madhërishëm.');
INSERT INTO "hasan_efendi" VALUES(15,88,'15:88','Mos i drejto sytë e tu nga kënaqësitë, që Ne ua kemi dhënë disave prej tyre dhe mos u hidhëro për ata! Me besimtarët sillu butë!');
INSERT INTO "hasan_efendi" VALUES(15,89,'15:89','Thuaj: “Në të vërtetë, unë jam vetëm paralajmërues i qartë”.');
INSERT INTO "hasan_efendi" VALUES(15,90,'15:90','Siç është e vërtetë se u dërguam dënim atyre që e ndanë');
INSERT INTO "hasan_efendi" VALUES(15,91,'15:91','dhe e bënë Kuranin në pjesë (duke i besuar ca pjesë dhe duke i mohuar të tjerat),');
INSERT INTO "hasan_efendi" VALUES(15,92,'15:92','ashtu është e vërtetë, për Zotin tënd, se Ne do t’i pyesim ata të gjithë,');
INSERT INTO "hasan_efendi" VALUES(15,93,'15:93','për gjithçka që kanë bërë!');
INSERT INTO "hasan_efendi" VALUES(15,94,'15:94','Ti prediko haptazi atë që të është urdhëruar dhe largohu nga idhujtarët.');
INSERT INTO "hasan_efendi" VALUES(15,95,'15:95','Ne të mjaftojmë ty kundër atyre që të tallin,');
INSERT INTO "hasan_efendi" VALUES(15,96,'15:96','e të cilët, përveç Allahut, adhurojnë edhe zot tjetër. Do ta marrin vesh mirë ata!');
INSERT INTO "hasan_efendi" VALUES(15,97,'15:97','Ne e dimë mirë, që ty të ngushtohet zemra nga fjalët e tyre,');
INSERT INTO "hasan_efendi" VALUES(15,98,'15:98','andaj, lartësoje Zotin tënd duke e lavdëruar dhe bëhu prej atyre që i përulen Atij duke u falur!');
INSERT INTO "hasan_efendi" VALUES(15,99,'15:99','Dhe adhuroje Zotin tënd derisa të të vijë vdekja!');
INSERT INTO "hasan_efendi" VALUES(16,1,'16:1','Urdhri i Allahut do të arrijë patjetër, andaj mos e shpejtoni atë! I lavdëruar qoftë Ai dhe i lartësuar mbi gjithçka, që ia shoqërojnë (në adhurim)!');
INSERT INTO "hasan_efendi" VALUES(16,2,'16:2','Me vullnetin e Tij, Ai ia dërgon engjëjt (Xhibrilin) me Shpallje kujt të dojë prej robërve të Tij (duke u thënë): “Paralajmëroni se s’ka zot tjetër të vërtetë përveç Meje, prandaj të më kini frikë Mua!”');
INSERT INTO "hasan_efendi" VALUES(16,3,'16:3','Ai i ka krijuar qiejt dhe Tokën me urtësi. I lartësuar qoftë Ai mbi gjithçka që ia shoqërojnë (në adhurim)!');
INSERT INTO "hasan_efendi" VALUES(16,4,'16:4','Ai e ka krijuar njeriun prej një pike fare, por ja që ky bëhet kundërshtar i hapët!');
INSERT INTO "hasan_efendi" VALUES(16,5,'16:5','Kafshët Ai i ka krijuar për ju. Nëpërmjet tyre ju siguroni rroba të ngrohta dhe dobi të tjera. Prej tyre ju ushqeheni');
INSERT INTO "hasan_efendi" VALUES(16,6,'16:6','dhe shikoni pamje të bukura, kur i ktheni nga kullota dhe, kur i nxirrni në kullotë.');
INSERT INTO "hasan_efendi" VALUES(16,7,'16:7','Ato mbajnë barrët tuaja deri në vende të largëta, tek të cilat ju do të arrinit me mundim të madh; me të vërtetë, Zoti juaj është i Butë dhe Mëshirëplotë.');
INSERT INTO "hasan_efendi" VALUES(16,8,'16:8','(Ai ka krijuar) edhe kuajt, mushkat dhe gomerët, që ju t’i shaloni dhe t’i mbani për bukuri. Ai krijon edhe gjëra që ju nuk i dini.');
INSERT INTO "hasan_efendi" VALUES(16,9,'16:9','Allahut i takon që t’ju tregojë rrugën e drejtë, kur ka prej tyre edhe të shtrembra; sikur të donte Allahu, do t’i udhëzonte të gjithë.');
INSERT INTO "hasan_efendi" VALUES(16,10,'16:10','Është Ai që zbret nga qielli shi, i cili ju siguron ujë për të pirë dhe bën që të rriten bimët, që shërbejnë si ushqim për kafshët.');
INSERT INTO "hasan_efendi" VALUES(16,11,'16:11','Nëpërmjet tij (ujit) rriten të lashtat, ullinjtë, palmat, rrushi dhe fruta të llojllojshme. Këtu ka vërtet tregues (të fuqisë hyjnore) për njerëzit, që kuptojnë.');
INSERT INTO "hasan_efendi" VALUES(16,12,'16:12','Ai ka bërë që ju ta shfrytëzoni natën dhe ditën, Diellin dhe Hënën. Edhe yjet janë vënë në shërbimin tuaj me urdhrin e Tij. Njëmend, në këto ka shenja për njerëzit që mendojnë.');
INSERT INTO "hasan_efendi" VALUES(16,13,'16:13','Ai ka krijuar në Tokë për ju çdo gjë me ngjyra dhe cilësi të ndryshme. Natyrisht që në këto ka shenja për njerëzit që ua vënë veshin.');
INSERT INTO "hasan_efendi" VALUES(16,14,'16:14','Është Ai që ju ka vënë në shërbim detin, që ju të hani prej tij mish të freskët dhe të nxirrni nga ai stolitë që mbani. Ti sheh se si anijet e çajnë atë, që ju të kërkoni mirësitë e Allahut dhe të jeni mirënjohës.');
INSERT INTO "hasan_efendi" VALUES(16,15,'16:15','Ai ka vendosur male të palëvizshme në Tokë, që ajo të mos tundet bashkë me ju, dhe ka krijuar lumenj e rrugë për të arritur aty ku synoni.');
INSERT INTO "hasan_efendi" VALUES(16,16,'16:16','Ai ka vënë edhe shenja të tjera, si dhe yjet, nëpërmjet të cilave njerëzit orientohen.');
INSERT INTO "hasan_efendi" VALUES(16,17,'16:17','Atëherë, a është njësoj si Ai që krijon, si ai që nuk krijon?! A nuk po merrni vesh?');
INSERT INTO "hasan_efendi" VALUES(16,18,'16:18','Nëse do t’i numëronit mirësitë e Allahut, nuk do të mund t’i llogarisnit ato. Allahu është vërtet Falës dhe Mëshirëplotë.');
INSERT INTO "hasan_efendi" VALUES(16,19,'16:19','Ai di gjithçka që ju fshihni dhe që shfaqni haptazi.');
INSERT INTO "hasan_efendi" VALUES(16,20,'16:20','Ndërsa idhujt që ata i adhurojnë në vend të Allahut, nuk krijojnë asgjë; ata vetë janë të krijuar.');
INSERT INTO "hasan_efendi" VALUES(16,21,'16:21','Ata janë të vdekur, pa kurrfarë gjallërie dhe as që e dinë se kur do të ringjallen.');
INSERT INTO "hasan_efendi" VALUES(16,22,'16:22','Zoti juaj është një Zot i vetëm. Ata që nuk besojnë në jetën tjetër, i kanë zemrat mohuese të së vërtetës dhe janë mendjemëdhenj.');
INSERT INTO "hasan_efendi" VALUES(16,23,'16:23','S’ka dyshim se Allahu di gjithçka që ata fshehin dhe që shfaqin haptazi. Me të drejtë, Ai nuk i do mendjemëdhenjtë.');
INSERT INTO "hasan_efendi" VALUES(16,24,'16:24','Kur atyre u thuhet: “Çfarë ka shpallur Zoti juaj?” Ata thonë: “Përrallat e popujve të lashtë”!');
INSERT INTO "hasan_efendi" VALUES(16,25,'16:25','(Le të thonë kështu, me qëllim) që t’i mbajnë gjynahet e veta të plota në Ditën e Kiametit, si dhe pjesën e gjynaheve të atyre që i çuan në humbje nga padijenia; sa e keqe është barra që do të bartin ata!');
INSERT INTO "hasan_efendi" VALUES(16,26,'16:26','Edhe popujt që ishin para tyre (paganëve të Mekës), kanë kurdisur dredhi. Por Allahu ua rrënoi godinën që nga themelet, kështu që kulmi i saj ra mbi ta dhe dënimi u erdhi andej nga nuk e prisnin.');
INSERT INTO "hasan_efendi" VALUES(16,27,'16:27','Pastaj, në Ditën e Kiametit, Ai do t’i turpërojë e do t’u thotë: “Ku janë “ortakët” e Mi, për shkak të të cilëve grindeshit (me besimtarët)?” Ata të cilëve u është dhënë dituria, do të thonë: “Me të vërtetë, sot poshtërimi dhe dënimi do të bjerë mbi jobesimtarët,');
INSERT INTO "hasan_efendi" VALUES(16,28,'16:28','të cilëve engjëjt ua marrin shpirtin, duke qenë të padrejtë ndaj vetvetes. Ata do të shfaqin përulësi (duke thënë): “Ne nuk kemi bërë asgjë të keqe!” (Dhe do t’u thuhet): “Nuk është kështu! Natyrisht që Allahu i di mirë veprat që keni punuar.');
INSERT INTO "hasan_efendi" VALUES(16,29,'16:29','Hyni në portat e Xhehenemit, ku do të mbeteni përgjithmonë!” Eh, sa vendbanim i shëmtuar është ai për mendjemëdhenjtë!');
INSERT INTO "hasan_efendi" VALUES(16,30,'16:30','Ndërsa atyre që e kanë frikë Allahun, do t’u thuhet: “Çfarë ju ka shpallur Zoti juaj?” - Ata do të thonë: “Mirësi!” Ata që bëjnë vepra të mira në këtë botë, do të kenë mirësi; në botën tjetër do të kenë edhe më të mira. Sa e mrekullueshme do të jetë shtëpia e atyre që i frikësohen Allahut!');
INSERT INTO "hasan_efendi" VALUES(16,31,'16:31','Ata do të hyjnë në Xhenetet e Adnit, ku rrjedhin lumenj dhe ku ka gjithçka, që ata dëshirojnë. Kështu i shpërblen Allahu punëmirët,');
INSERT INTO "hasan_efendi" VALUES(16,32,'16:32','të cilëve engjëjt ua marrin shpirtrat, duke qenë të pastër. U thonë atyre: “Shpëtimi qoftë mbi ju! Hyni në Xhenet, si shpërblim për veprat që keni kryer!”');
INSERT INTO "hasan_efendi" VALUES(16,33,'16:33','A mos presin ata gjë tjetër, veç ardhjes së engjëjve apo të urdhrit të Zotit tënd ?! Kështu bënë edhe ata që qenë para këtyre. Allahu nuk u bëri atyre padrejtësi, por ata i bënë të keqen vetes.');
INSERT INTO "hasan_efendi" VALUES(16,34,'16:34','E kështu, mbi ta ra dënimi për veprat që bënë dhe i kaploi ajo me të cilën talleshin.');
INSERT INTO "hasan_efendi" VALUES(16,35,'16:35','Ata që i shoqërojnë Allahut zota të tjerë, thonë: “Sikur të donte Allahu, as ne, as të parët tanë nuk do të adhuronim gjë tjetër, përveç Tij dhe, nuk do të kishim ndaluar asgjë, pa dëshirën e Tij.” Kështu kanë vepruar edhe popujt para këtyre. Për të dërguarit s’ka tjetër detyrë, veç të shpallin qartë.');
INSERT INTO "hasan_efendi" VALUES(16,36,'16:36','Çdo populli Ne i çuam një të dërguar (që u thoshte): “Adhuroni Allahun dhe shmangni idhujt!” Disa prej tyre Allahu i shpuri në rrugë të drejtë e disa e merituan të mbeten në humbje. Andaj, udhëtoni nëpër botë të shihni se si ishte fundi i përgënjeshtruesve!');
INSERT INTO "hasan_efendi" VALUES(16,37,'16:37','Edhe nëse ti (o Muhamed) lakmon që ata të jenë në rrugë të drejtë, dije se Allahu nuk e çon në rrugë të drejtë atë, që do ta lërë në humbje. Dhe për ata nuk ka kurrfarë ndihmësi.');
INSERT INTO "hasan_efendi" VALUES(16,38,'16:38','Idhujtarët betohen solemnisht për Allahun, se Ai kurrë nuk do t’i ngjallë të vdekurit. Nuk është kështu! Premtimi i Tij është i vërtetë, por shumica e njerëzve nuk e dinë.');
INSERT INTO "hasan_efendi" VALUES(16,39,'16:39','(Ai do t’i ringjallë) që t’u tregojë atë, për të cilën nuk pajtohen dhe që mohuesit ta kuptojnë se kanë qenë gënjeshtarë.');
INSERT INTO "hasan_efendi" VALUES(16,40,'16:40','Nëse dëshirojmë të bëhet diçka, mjafton që të themi: “Bëhu!” dhe ajo bëhet.');
INSERT INTO "hasan_efendi" VALUES(16,41,'16:41','Ata që, pasi vuajtën të shtypur, u shpërngulën për hir të Allahut, Ne do t’i vendosim në vendbanime të mira në këtë botë. Natyrisht, shpërblimi që i pret në botën tjetër (Xheneti), është edhe më i madh. Ah! Sikur ta dinin ata!');
INSERT INTO "hasan_efendi" VALUES(16,42,'16:42','(Xheneti është për) ata që kanë duruar dhe janë mbështetur në Zotin e tyre.');
INSERT INTO "hasan_efendi" VALUES(16,43,'16:43','Ne edhe para teje kemi dërguar vetëm njerëz, të cilëve u kemi dhënë shpallje, andaj pyetni dijetarët e Librit, nëse nuk e dini (këtë).');
INSERT INTO "hasan_efendi" VALUES(16,44,'16:44','(Të dërguarit i çuam) me prova të qarta dhe libra. Ndërsa ty (o Muhamed) të zbritëm Përkujtesën (Kuranin), me qëllim që t’u shpjegosh njerëzve atë që u është shpallur dhe që ata të mendojnë.');
INSERT INTO "hasan_efendi" VALUES(16,45,'16:45','A janë të sigurt ata që bëjnë plane djallëzore se Allahu nuk do t’i fusë në tokë ose që dënimi nuk do t’u vijë andej nga nuk e presin?');
INSERT INTO "hasan_efendi" VALUES(16,46,'16:46','A janë të sigurt ata se Allahu nuk do t’i dënojë gjatë ecejakeve të tyre, pa qenë në gjendje ta pengojnë Atë?');
INSERT INTO "hasan_efendi" VALUES(16,47,'16:47','Ose, se nuk do t’i godasë dënimi pak nga pak? E megjithatë, Zoti juaj është i Butë dhe Mëshirëplotë.');
INSERT INTO "hasan_efendi" VALUES(16,48,'16:48','A nuk e vërejnë ata se si të gjitha ato që i ka krijuar Allahu, hija e tyre anon djathtas e majtas, duke iu falur Allahut dhe duke iu përulur?');
INSERT INTO "hasan_efendi" VALUES(16,49,'16:49','Allahut i përulet në sexhde çdo gjallesë, në qiej dhe në Tokë, edhe engjëjt, të cilët nuk janë kryelartë.');
INSERT INTO "hasan_efendi" VALUES(16,50,'16:50','Ata i frikësohen Zotit të tyre lart mbi ta dhe bëjnë atë, që u urdhërohet.');
INSERT INTO "hasan_efendi" VALUES(16,51,'16:51','Allahu ka thënë: “Mos adhuroni dy zotëra, sepse Ai është një Zot i vetëm, prandaj frikësohuni vetëm prej Meje!”');
INSERT INTO "hasan_efendi" VALUES(16,52,'16:52','Atij i përket çdo gjë që gjendet në qiej dhe në Tokë, Atij i përket bindja e besimi përherë. A mos vallë, do t’i frikësoheni tjetërkujt, përveç Allahut?');
INSERT INTO "hasan_efendi" VALUES(16,53,'16:53','Çdo mirësi që keni është nga Allahu. E, nëse ju godet ndonjë fatkeqësi, prapë nuk keni kujt t’i kërkoni ndihmë përveç Tij.');
INSERT INTO "hasan_efendi" VALUES(16,54,'16:54','E prapëseprapë, kur Ai jua shmang fatkeqësinë, disa nga ju i shoqërojnë Zotit të vet (zota të tjerë në adhurim),');
INSERT INTO "hasan_efendi" VALUES(16,55,'16:55','duke mohuar kështu mirësitë që u dhamë Ne. Epo mirë, kënaquni edhe pak, sepse së shpejti do ta merrni vesh!');
INSERT INTO "hasan_efendi" VALUES(16,56,'16:56','Ata vënë mënjanë për idhujt e tyre, rreth të cilëve nuk dinë gjë, një pjesë të mirësive që ua japim Ne. Betohem për Allahun se ju do të pyeteni me siguri për gjithçka që keni trilluar.');
INSERT INTO "hasan_efendi" VALUES(16,57,'16:57','Ata i veshin Allahut vajza – i lavdëruar dhe i lartësuar qoftë Ai –, ndërsa për vete kanë atë që dëshirojnë (djemtë).');
INSERT INTO "hasan_efendi" VALUES(16,58,'16:58','Dhe, kur ndonjëri prej tyre merr lajmin se i lindi një vajzë, fytyra i nxihet dhe i mbushet me mjerim.');
INSERT INTO "hasan_efendi" VALUES(16,59,'16:59','Ai fshihet nga njerëzit për shkak të lajmit të keq që mori dhe s’di: a ta mbajë këtë fëmijë, pavarësisht poshtërimit apo ta varrosë në dhé. Ah! Sa mbrapsht që gjykojnë ata!');
INSERT INTO "hasan_efendi" VALUES(16,60,'16:60','Atyre që nuk besojnë në jetën tjetër, u takon shembulli i keq (cilësitë e shëmtuara), kurse Allahut i përket shembulli më i lartë (cilësitë më të larta); Ai është i Plotfuqishmi, i Urti.');
INSERT INTO "hasan_efendi" VALUES(16,61,'16:61','Sikur Allahu t’i dënonte njerëzit për shkak të mbrapshtive të tyre, nuk do të linte asgjë të gjallë në Tokë. Por Ai i lë ata deri në një afat të caktuar dhe, kur atyre u vjen afati, ata nuk mund ta shtyjnë atë e as ta shpejtojnë, qoftë edhe për një çast.');
INSERT INTO "hasan_efendi" VALUES(16,62,'16:62','Ata i veshin Allahut atë që për vete nuk e dëshirojnë , kurse gjuhët e tyre flasin gënjeshtra, kur thonë se ata do të kenë shpërblimin më të mirë. Nuk ka dyshim se ata i pret Zjarri, ku do të jenë të parët që do të prijnë.');
INSERT INTO "hasan_efendi" VALUES(16,63,'16:63','Betohem për Allahun se edhe para teje, Ne u kemi çuar të dërguar popujve të tjerë, por djalli ua zbukuroi sjelljet e tyre dhe tani ai është zotëria i tyre; andaj ata i pret dënimi i dhembshëm.');
INSERT INTO "hasan_efendi" VALUES(16,64,'16:64','Ne ta kemi zbritur ty Librin, vetëm që ti t’u sqarosh atyre çështjet, për të cilat ata nuk pajtoheshin, si dhe që të jetë udhërrëfyes e mëshirë për njerëzit që besojnë.');
INSERT INTO "hasan_efendi" VALUES(16,65,'16:65','Allahu zbret nga qielli ujë me të cilin ngjall tokën pasi është tharë! Kjo, në të vërtetë, është shenjë e qartë për njerëzit që duan të dëgjojnë.');
INSERT INTO "hasan_efendi" VALUES(16,66,'16:66','Në fakt, edhe te bagëtitë ju keni shenja për të mësuar prej tyre. Ne ju japim atë që kanë në barkun e tyre prej ushqimit të përpunuar dhe gjakut: qumësht të pastër dhe të shijshëm për ata që e pinë.');
INSERT INTO "hasan_efendi" VALUES(16,67,'16:67','Ndërsa nga frutat e palmave dhe të hardhisë, ju përgatitni pije dhe ushqim të shijshëm. Natyrisht, edhe kjo është shenjë e vërtetë për ata që mendojnë.');
INSERT INTO "hasan_efendi" VALUES(16,68,'16:68','Zoti yt e frymëzoi bletën, (duke thënë): “Ndërto shtëpi në male dhe në drurë dhe në atë (kosheret) që kanë ngritur njerëzit!');
INSERT INTO "hasan_efendi" VALUES(16,69,'16:69','Ushqehu me gjithfarë lloje frutash dhe ec rrugës së Zotit tënd me përulësi!” Nga barku i tyre del lëng ngjyrash të ndryshme, në të cilin ka ilaç e shërim për njerëzit. Vërtet që kjo është shenjë për ata që përsiatin.');
INSERT INTO "hasan_efendi" VALUES(16,70,'16:70','Allahu ju krijon, pastaj jua merr shpirtin. Disa prej jush jetojnë aq gjatë, sa arrijnë një moshë, ku nuk kuptojnë më asgjë nga ajo që kanë ditur. Me të vërtetë, Allahu është i Gjithëdijshëm dhe i Plotfuqishëm.');
INSERT INTO "hasan_efendi" VALUES(16,71,'16:71','Disa prej jush Allahu i ka pajisur më mirë. Por, ata të cilëve u ka dhënë më shumë, nuk u japin furnizim atyre që i kanë nën vete, edhe pse janë të barabartë në nevoja. Si po i mohoni dhuntitë e Allahut?');
INSERT INTO "hasan_efendi" VALUES(16,72,'16:72','Allahu i ka krijuar për ju gratë nga lloji juaj e nga gratë tuaja ju jep djemtë e nipat dhe ju furnizon me ushqime të mira. A mos vallë, ata besojnë në gënjeshtra dhe mohojnë dhuntitë e Allahut,');
INSERT INTO "hasan_efendi" VALUES(16,73,'16:73','duke adhuruar në vend të Allahut, ata që nuk janë në gjendje t’u japin çfarëdo ushqimi, si nga qielli ashtu edhe nga toka dhe të cilët nuk kanë fuqi të bëjnë gjë?!');
INSERT INTO "hasan_efendi" VALUES(16,74,'16:74','Andaj, mos krahasoni asnjë me Allahun! Në të vërtetë, Allahu di, ndërsa ju nuk dini.');
INSERT INTO "hasan_efendi" VALUES(16,75,'16:75','Allahu e krahason shembullin e skllavit, që s’ka mundësi për asgjë dhe që është pronë e të zotit me atë që Ne i kemi dhënë plot begati, prej të cilave jep fshehtazi dhe haptazi. A janë njëlloj? Lavdi i qoftë Allahut! Shumica e tyre nuk dinë.');
INSERT INTO "hasan_efendi" VALUES(16,76,'16:76','Gjithashtu, Allahu krahason shembullin e dy njerëzve, njëri prej të cilëve është shurdh-memec, nuk mund të bëjë asgjë dhe është barrë e pronarit të vet, - kurdo e kudo që ta dërgojë, nuk i sjell asnjë të mirë. A është i barabartë ky me atë që urdhëron për drejtësi dhe ndjek rrugën e drejtë?!');
INSERT INTO "hasan_efendi" VALUES(16,77,'16:77','Allahut i përkasin fshehtësitë e qiejve dhe të Tokës, ndërsa çështja e Ditës së Kiametit është sa çel e mbyll sytë ose edhe më shpejt. Vërtet, Allahu është i Fuqishëm për çdo gjë!');
INSERT INTO "hasan_efendi" VALUES(16,78,'16:78','Allahu ju ka nxjerrë nga barku i nënave tuaja e ju nuk dinit asgjë dhe ju dha të dëgjuarit, të parët dhe zemrat, që të jeni falënderues.');
INSERT INTO "hasan_efendi" VALUES(16,79,'16:79','A nuk shohin ata, se si shpendët fluturojnë pa mundim në hapësirat qiellore? Ato i mban vetëm Allahu. Kjo, në të vërtetë, është shenjë për popullin që beson.');
INSERT INTO "hasan_efendi" VALUES(16,80,'16:80','Allahu ju ka dhënë shtëpi për të banuar dhe lëkurë kafshësh, që t’i përdorni si tenda, në mënyrë që t’i mbartni lehtë, kur të udhëtoni dhe të zini vend. Ai ju ka dhënë edhe leshin, gëzofin e fijet që t’i përdorni për veshje dhe për shtrojë, deri në një kohë.');
INSERT INTO "hasan_efendi" VALUES(16,81,'16:81','Nga ajo që ka krijuar, Ai ka bërë për ju hije; ka bërë për ju streha nëpër male; ka bërë për ju rroba që t’ju mbrojnë nga nxehtësia; ka bërë për ju veshje prej metali për t’ju mbrojtur në luftë. Kështu, Ai i përsos begatitë e Veta mbi ju, me qëllim që ju t’i bindeni vullnetit të Tij.');
INSERT INTO "hasan_efendi" VALUES(16,82,'16:82','E, nëse njerëzit shmangen, ti (o Muhamed) je i detyruar vetëm që të shpallësh qartazi.');
INSERT INTO "hasan_efendi" VALUES(16,83,'16:83','Ata e dinë dhuntinë e Allahut, por pastaj e mohojnë, se shumica e tyre janë jobesimtarë.');
INSERT INTO "hasan_efendi" VALUES(16,84,'16:84','Ndërsa Ditën, kur do të sjellim dëshmitarë nga çdo popull, jobesimtarëve nuk do t’u jepet leje të shfajësohen e as që të kërkojnë falje.');
INSERT INTO "hasan_efendi" VALUES(16,85,'16:85','Kur keqbërësit të shohin dënimin, ai nuk do t’u lehtësohet e as nuk do t’u shtyhet.');
INSERT INTO "hasan_efendi" VALUES(16,86,'16:86','Kur idhujtarët të shohin idhujt që i kanë adhuruar krahas Allahut, do të thonë: “O Zoti ynë, këta janë idhujt tanë, që ne u jemi lutur në vendin Tënd”. Ata (idhujt e tyre) do t’u drejtojnë këto fjalë: “Ju, me të vërtetë, jeni gënjeshtarë!”');
INSERT INTO "hasan_efendi" VALUES(16,87,'16:87','Atë ditë, ata do t’i përulen Allahut dhe do të braktisen nga zotat që kishin sajuar vetë.');
INSERT INTO "hasan_efendi" VALUES(16,88,'16:88','Atyre që mohojnë dhe i pengojnë të tjerët nga rruga e Allahut, Ne do t’u shtojmë dënim mbi dënim, sepse kanë bërë ngatërresa.');
INSERT INTO "hasan_efendi" VALUES(16,89,'16:89','Ditën kur do të sjellim kundër çdo populli dëshmitarë nga gjiri i tyre, Ne do të të sjellim ty (Muhamed) dëshmitar kundër këtyre. Ne ta zbritëm ty Librin, si shpjegim për çdo çështje, si udhërrëfyes, si mëshirë dhe si lajm të mirë për myslimanët.');
INSERT INTO "hasan_efendi" VALUES(16,90,'16:90','Në të vërtetë, Allahu urdhëron drejtësinë, mirësinë dhe ndihmën për të afërmit, si dhe ndalon imoralitetin, veprat e shëmtuara dhe dhunën. Ai ju këshillon, në mënyrë që ju t’ia vini veshin.');
INSERT INTO "hasan_efendi" VALUES(16,91,'16:91','Zbatoni besëlidhjen e Allahut, kur ta merrni mbi vete dhe mos e thyeni betimin, pasi ta keni bërë atë, sepse Allahun e keni bërë dorëzënësin tuaj; vërtet, Allahu e di se çfarë bëni ju.');
INSERT INTO "hasan_efendi" VALUES(16,92,'16:92','Mos u bëni si ajo gruaja që e shprish dhe e bën fije-fije atë që e ka tjerrë fort, duke i përdorur betimet tuaja si mjet mashtrimi të njëri-tjetrit, nga frika se mos një grup të jetë më i madh se tjetri. Allahu, me këtë, vetëm sa u vë në provë e, me siguri, Ai do t’jua tregojë në Ditën e Kiametit, çështjet për të cilat ju nuk pajtoheshit.');
INSERT INTO "hasan_efendi" VALUES(16,93,'16:93','Sikur të kishte dashur Allahu, do t’ju kishte bërë një popull të vetëm, por Ai shpie në humbje kë të dojë dhe udhëzon në rrugë të drejtë kë të dojë; e ju, gjithsesi, do të përgjigjeni për atë që keni bërë.');
INSERT INTO "hasan_efendi" VALUES(16,94,'16:94','Mos i përdorni betimet tuaja për të mashtruar njëri-tjetrin, që të mos rrëshqisni jashtë rrugës së drejtë, pasi qetë mirëorientuar në të, që e keqja të mos ju prekë, meqë penguat nga rruga e Allahut dhe që të mos jetë fati juaj një ndëshkim i madh.');
INSERT INTO "hasan_efendi" VALUES(16,95,'16:95','Mos e këmbeni besëlidhjen e Allahut me ndonjë vlerë të paktë. Ajo që është tek Allahu, është më e mirë për ju, nëse e dini.');
INSERT INTO "hasan_efendi" VALUES(16,96,'16:96','Gjithçka që keni ju, është kalimtare, ndërsa ajo që ndodhet tek Allahu, është e përhershme. Vërtet, Ne do t’i shpërblejmë ata që kanë duruar sipas veprave më të mira që kanë bërë.');
INSERT INTO "hasan_efendi" VALUES(16,97,'16:97','Cilindo mashkull apo femër që kryen vepra të mira, duke qenë besimtar, Ne do ta bëjmë që të kalojë jetë të bukur dhe do ta shpërblejmë sipas veprave më të mira, që ka bërë.');
INSERT INTO "hasan_efendi" VALUES(16,98,'16:98','Kur dëshiron të lexosh Kuranin, kërko mbrojtjen e Allahut kundër djallit të mallkuar,');
INSERT INTO "hasan_efendi" VALUES(16,99,'16:99','sepse, në të vërtetë, djalli nuk ka kurrfarë pushteti mbi ata që besojnë dhe mbështeten tek Zoti i tyre.');
INSERT INTO "hasan_efendi" VALUES(16,100,'16:100','Pushteti i djallit është vetëm mbi ata, që i binden atij dhe adhurojnë zota të tjerë veç Allahut.');
INSERT INTO "hasan_efendi" VALUES(16,101,'16:101','Kur Ne zëvendësojmë një varg me një varg tjetër - e Allahu di më së miri se çfarë shpall –, ata thonë: “Ti je vetëm trillues!” Nuk është ashtu, por shumica e tyre nuk dinë.');
INSERT INTO "hasan_efendi" VALUES(16,102,'16:102','Thuaj: “Atë (Kuranin) e ka zbritur Shpirti i Shenjtërisë nga Zoti yt me të vërtetën, për t’i forcuar ata që besojnë dhe për të qenë udhërrëfyes dhe lajm i mirë për ata që i dorëzohen Allahut”.');
INSERT INTO "hasan_efendi" VALUES(16,103,'16:103','Ne e dimë mirë që ata thonë: “Atë e mëson një njeri”! Por ai për të cilin flasin, flet në gjuhën e të huajve, kurse ky (Kurani) është në gjuhën e qartë arabe. ');
INSERT INTO "hasan_efendi" VALUES(16,104,'16:104','Pa dyshim, ata që nuk u besojnë shpalljeve të Allahut, Allahu nuk i udhëzon dhe për ata ka dënim të dhembshëm.');
INSERT INTO "hasan_efendi" VALUES(16,105,'16:105','Vërtet, gënjeshtra trillojnë vetëm ata, që nuk u besojnë shpalljeve të Allahut; pikërisht ata janë gënjeshtarët e vërtetë.');
INSERT INTO "hasan_efendi" VALUES(16,106,'16:106','Kushdo që mohon Allahun, pas pranimit të besimit, (do të dënohet ashpër), përveç atij që është i detyruar me forcë, ndërkohë që zemra e tij është plot besim. Ndërsa ata që ia hapin zemrën mosbesimit, do të kenë mbi vete zemërimin e Allahut dhe një dënim të madh.');
INSERT INTO "hasan_efendi" VALUES(16,107,'16:107','Kjo, sepse ata e duan më shumë jetën e kësaj bote, sesa jetën e botës tjetër; Allahu nuk i udhëzon në rrugë të drejtë mohuesit.');
INSERT INTO "hasan_efendi" VALUES(16,108,'16:108','Këta janë ata të cilëve Allahu ua ka vulosur zemrat, të dëgjuarit dhe të parët; pikërisht këta janë të shkujdesurit.');
INSERT INTO "hasan_efendi" VALUES(16,109,'16:109','S’ka dyshim se në botën tjetër, ata do të jenë nga të humburit.');
INSERT INTO "hasan_efendi" VALUES(16,110,'16:110','Sigurisht, Zoti yt është Falës dhe Mëshirëplotë ndaj atyre që mërguan, pasi i kishin përndjekur e mandej luftuan dhe duruan në rrugën e Allahut.');
INSERT INTO "hasan_efendi" VALUES(16,111,'16:111','Ditën, kur çdo njeri do të vijë duke mbrojtur veten dhe, kur çdo shpirt do të shpërblehet plotësisht për atë që ka bërë, atyre nuk do t’u bëhet padrejtësi!');
INSERT INTO "hasan_efendi" VALUES(16,112,'16:112','Allahu e ka bërë shembull qytetin (e Mekës), i cili ishte i sigurt dhe i qetë. Atij i arrinin ushqime të bollshme nga të gjitha anët, por pastaj banorët e tij i mohuan dhuntitë e Allahut dhe Ai i bëri të shijojnë urinë dhe frikën, si ndëshkim për atë që bënë.');
INSERT INTO "hasan_efendi" VALUES(16,113,'16:113','Vërtet, atyre u erdhi një i dërguar nga radhët e tyre, por ata e quajtën për gënjeshtar dhe i goditi dënimi, ngaqë ishin të padrejtë.');
INSERT INTO "hasan_efendi" VALUES(16,114,'16:114','Ushqehuni me gjërat e lejuara dhe të mira që jua ka dhënë Allahu dhe falënderoni për mirësitë e Allahut, nëse vërtet e adhuroni Atë.');
INSERT INTO "hasan_efendi" VALUES(16,115,'16:115','Ai ju ka ndaluar vetëm të ngordhurën, gjakun, mishin e derrit dhe atë që është therur në emër tjetër e jo në emër të Allahut. Por ata që shtrëngohen nga uria e madhe, mund t’i hanë ato, sa ta shuajnë urinë e jo të kalojnë kufirin; në të vërtetë, Allahu është Falës dhe Mëshirëplotë.');
INSERT INTO "hasan_efendi" VALUES(16,116,'16:116','Prandaj, mos thoni me gjuhët tuaja të rreme: “Kjo është e lejueshme e kjo e ndaluar”, që kështu të trilloni gënjeshtra për Allahun. Me siguri, ata që trillojnë gënjeshtra ndaj Allahut, nuk do të shpëtojnë.');
INSERT INTO "hasan_efendi" VALUES(16,117,'16:117','Ata do të kenë kënaqësi të shkurtër (në këtë jetë), por i pret një dënim i dhembshëm (në jetën tjetër).');
INSERT INTO "hasan_efendi" VALUES(16,118,'16:118','Ne u ndaluam hebrenjve atë që të treguam ty më parë. Ne asnjëherë nuk u bëmë atyre padrejtësi, por ata i bënin vetvetes padrejtësi.');
INSERT INTO "hasan_efendi" VALUES(16,119,'16:119','Pa dyshim, Zoti yt është Falës dhe Mëshirëplotë ndaj atyre që kanë bërë keq nga padituria, por që pastaj janë penduar dhe përmirësuar.');
INSERT INTO "hasan_efendi" VALUES(16,120,'16:120','Me të vërtetë, Ibrahimi ka qenë prijës shembullor (me të gjitha virtytet e mira), i përulur para Allahut, me besim monoteist dhe nuk ka qenë nga idhujtarët,');
INSERT INTO "hasan_efendi" VALUES(16,121,'16:121','(por ka qenë) mirënjohës ndaj dhuntive të Tij. Ai e zgjodhi atë dhe e udhëzoi në rrugë të drejtë.');
INSERT INTO "hasan_efendi" VALUES(16,122,'16:122','Ne i kemi dhënë atij të mira në këtë botë, ndërsa në botën tjetër, sigurisht që ai do të jetë nga të mirët.');
INSERT INTO "hasan_efendi" VALUES(16,123,'16:123','Pastaj ty të shpallëm: “Paso me sinqeritet fenë e Ibrahimit, që nuk ka qenë prej idhujtarëve”.');
INSERT INTO "hasan_efendi" VALUES(16,124,'16:124','Në të vërtetë, e Shtuna u caktua për ata që patën kundërshti në lidhje me të. Sigurisht që Zoti yt do t’i gjykojë ata në Ditën e Kiametit, për çështjet, që ata nuk pajtoheshin.');
INSERT INTO "hasan_efendi" VALUES(16,125,'16:125','Thirr në rrugën e Zotit tënd me mençuri dhe këshillë të bukur dhe diskuto me ata në mënyrën më të mirë! Me të vërtetë, Zoti yt i di më së miri ata që janë shmangur nga rruga e Tij dhe i di më së miri ata që janë në rrugë të drejtë.');
INSERT INTO "hasan_efendi" VALUES(16,126,'16:126','Nëse ndëshkoni, ndëshkoni aq sa jeni ndëshkuar, por, nëse duroni, kjo është më e mira (rrugë) për të durueshmit.');
INSERT INTO "hasan_efendi" VALUES(16,127,'16:127','Prandaj duro! Durimi yt është vetëm me ndihmën e Allahut. Dhe mos u pikëllo për ta e mos u ngushto për shkak të dredhive të tyre!');
INSERT INTO "hasan_efendi" VALUES(16,128,'16:128','Vërtet, Allahu është me ata që e kanë frikë dhe që janë punëmirë.');
INSERT INTO "hasan_efendi" VALUES(17,1,'17:1','I lavdëruar qoftë Ai që e mbarti robin e Tij (Muhamedin) natën nga Xhamia e Shenjtë (në Mekë) në Xhaminë e Largët (të Jerusalemit), rrethinën e së cilës e kemi bekuar, për t’i treguar atij disa nga mrekullitë Tona. Ai, me të vërtetë, dëgjon çdo gjë dhe sheh çdo gjë.');
INSERT INTO "hasan_efendi" VALUES(17,2,'17:2','Ne i dhamë Musait Librin dhe e bëmë atë udhërrëfyes për bijtë e Israilit (duke u thënë): “Mos merrni mbrojtës tjetër, përveç Meje!');
INSERT INTO "hasan_efendi" VALUES(17,3,'17:3','Ju jeni pasardhës të atyre që Ne i mbartëm me Nuhun! Ai ka qenë vërtet një rob mirënjohës.”');
INSERT INTO "hasan_efendi" VALUES(17,4,'17:4','Ne u shpallëm solemnisht në Libër bijve të Israilit: “Ju do të bëni dy herë ngatërresa në tokë dhe do të tregoni mendjemadhësi të tepërt”.');
INSERT INTO "hasan_efendi" VALUES(17,5,'17:5','Kur erdhi koha e përmbushjes së profecisë për ngatërresën e parë, Ne dërguam kundër jush disa robër Tanët, luftëtarë të fuqishëm, të cilët shkelën në çdo cep të shtëpive tuaja. Kështu u krye premtimi.');
INSERT INTO "hasan_efendi" VALUES(17,6,'17:6','Pastaj jua kthyem juve sërish fitoren kundër tyre, ju ndihmuam me pasuri e djem dhe jua shtuam numrin e ushtarëve.');
INSERT INTO "hasan_efendi" VALUES(17,7,'17:7','Nëse bëni mirësi, e keni bërë për veten tuaj. E, nëse bëni keq, ia keni bërë po vetes. Kur erdhi koha e përmbushjes së profecisë për ngatërresën e dytë, Ne i dërguam (armiqtë tuaj) për t’jua nxirë fytyrat tuaja dhe që të hyjnë në tempull, siç patën hyrë herën e parë dhe të shkatërrojnë plotësisht çdo gjë që pushtonin.');
INSERT INTO "hasan_efendi" VALUES(17,8,'17:8','Ndoshta, Zoti juaj, do t’ju mëshirojë, por, nëse ju ktheheni (në gjynahe), Ne do t’u kthehemi (ndëshkimeve ndaj jush). Dhe Xhehenemin e kemi bërë burg për mohuesit.');
INSERT INTO "hasan_efendi" VALUES(17,9,'17:9','Në të vërtetë, ky Kuran udhëzon drejt asaj që është më e mira dhe u jep lajmin e gëzuar besimtarëve, që bëjnë vepra të mira, se ata do të kenë shpërblim të madh.');
INSERT INTO "hasan_efendi" VALUES(17,10,'17:10','Ndërsa atyre që nuk besojnë në jetën tjetër, Ne u kemi përgatitur dënim të dhembshëm.');
INSERT INTO "hasan_efendi" VALUES(17,11,'17:11','Njeriu mallkon në të keqe, ashtu siç lutet edhe për të mirë; vërtet që njeriu është i ngutur.');
INSERT INTO "hasan_efendi" VALUES(17,12,'17:12','Ne i bëmë natën dhe ditën dy mrekulli. Natën e mbështollëm me errësirë, kurse ditës i dhamë ndriçim, që ju të kërkoni begati nga Zoti juaj e që të dini numrin e viteve dhe njehsimin e kohës. Çdo gjë e kemi shpjeguar hollësisht.');
INSERT INTO "hasan_efendi" VALUES(17,13,'17:13','Çdo njeriu ia kemi varur në qafë llogarinë për punën e tij dhe Ne, do t’ia paraqesim Ditën e Kiametit librin, që do ta gjejë të hapur (e do t’i themi):');
INSERT INTO "hasan_efendi" VALUES(17,14,'17:14','“Lexoje librin tënd! Ti mjafton sot si llogaritar kundër vetvetes!”');
INSERT INTO "hasan_efendi" VALUES(17,15,'17:15','Kush zgjedh rrugën e drejtë, e ka për veten e vet e kush i largohet asaj, i largohet në dëm të vetvetes dhe askush nuk e merr të keqen e tjetërkujt. Ne nuk dënojmë asnjë popull, para se të çojmë të dërguar!');
INSERT INTO "hasan_efendi" VALUES(17,16,'17:16','Kur vendosim ta shkatërrojmë një qytet, Ne i urdhërojmë (për mirë) të pasurit e tij dhe, kur ata bëjnë mbrapshti në të, atëherë përmbushet Vendimi (i dënimit) kundër tyre dhe Ne i shkatërrojmë gjer në themel.');
INSERT INTO "hasan_efendi" VALUES(17,17,'17:17','Sa e sa brezni Ne i kemi shkatërruar pas Nuhut! Mjafton që Zoti yt i di dhe i sheh gjynahet e robërve të Tij.');
INSERT INTO "hasan_efendi" VALUES(17,18,'17:18','Sa për atë që dëshiron këtë botë, Ne shpejtojmë që t’i japim çfarë të dëshirojmë atij që duam. Pastaj ia caktojmë atij Xhehenemin, në të cilin ai do të përvëlohet, duke qenë i turpëruar dhe i dëbuar.');
INSERT INTO "hasan_efendi" VALUES(17,19,'17:19','Sa për atë që dëshiron botën tjetër dhe përpiqet për të me gjithë shpirt e duke qenë besimtar, përpjekja e tij do të shpërblehet.');
INSERT INTO "hasan_efendi" VALUES(17,20,'17:20','Ne i mbështesim të gjithë me dhuratat e Zotit tënd: edhe këta, edhe ata. Dhuratat e Zotit tënd nuk i mohohen askujt.');
INSERT INTO "hasan_efendi" VALUES(17,21,'17:21','Shiko, si Ne i parapëlqejmë disa ndaj të tjerëve. Kurse bota tjetër, në të vërtetë, do të ketë shkallëzime më të mëdha dhe parapëlqime më të theksuara.');
INSERT INTO "hasan_efendi" VALUES(17,22,'17:22','Mos vër zot tjetër me Allahun, se do të mbetesh i poshtëruar dhe i braktisur!');
INSERT INTO "hasan_efendi" VALUES(17,23,'17:23','Zoti yt ka urdhëruar, që të mos adhuroni askënd tjetër përveç Atij dhe, që të silleni mirë me prindërit. Nëse njëri prej tyre ose të dy arrijnë pleqërinë te ti, mos u thuaj atyre as “uh!”, mos i kundërshto, por drejtoju atyre me fjalë respekti.');
INSERT INTO "hasan_efendi" VALUES(17,24,'17:24','Lësho para tyre krahët e përuljes prej mëshirës dhe thuaj: “O Zoti im, mëshiroji ata, ashtu siç më kanë rritur, kur unë isha i vogël!”');
INSERT INTO "hasan_efendi" VALUES(17,25,'17:25','Zoti juaj e di më së miri ç’keni në shpirtrat tuaj. Nëse ju jeni të drejtë, sigurisht që Ai është Falës për ata që pendohen.');
INSERT INTO "hasan_efendi" VALUES(17,26,'17:26','Jepuni të afërmve, të varfrit dhe udhëtarit të drejtën e tyre, por mos bëni shpenzim të tepruar.');
INSERT INTO "hasan_efendi" VALUES(17,27,'17:27','Dorëshpuarit me të vërtetë, janë vëllezërit e djallit dhe djalli është mosmirënjohës ndaj Zotit të tij.');
INSERT INTO "hasan_efendi" VALUES(17,28,'17:28','Por, nëse shmangesh nga ata (kur nuk ke asgjë për të dhënë), duke shpresuar në mëshirën e Zotit tënd, atëherë thuaju atyre ndonjë fjalë të butë.');
INSERT INTO "hasan_efendi" VALUES(17,29,'17:29','Mos e shtrëngo dorën, as mos u bëj dorëshpuar e të mbetesh i qortuar dhe i mjerë.');
INSERT INTO "hasan_efendi" VALUES(17,30,'17:30','Me të vërtetë, Zoti yt i jep me bollëk e ia kufizon (riskun) kujt të dëshirojë; Ai i di dhe i sheh mirë robërit e Vet.');
INSERT INTO "hasan_efendi" VALUES(17,31,'17:31','Mos i vrisni fëmijët tuaj nga frika e varfërisë! Ne u sigurojmë ushqim atyre dhe juve. Vërtet, vrasja e tyre është gjynah i madh.');
INSERT INTO "hasan_efendi" VALUES(17,32,'17:32','Dhe mos iu qasni kurvërisë, se ajo është shfrenim dhe rrugë e shëmtuar!');
INSERT INTO "hasan_efendi" VALUES(17,33,'17:33','Mos ia merrni jetën asnjë njeriu, gjë që Allahu jua ka ndaluar ta bëni, përveçse kur e kërkon e drejta! Nëse dikush vritet padrejtësisht, Ne i kemi dhënë pushtet trashëgimtarit të tij (të kërkojë shpagim). Por edhe ai të mos e teprojë në vrasje, sepse ai është i ndihmuar (nga ligji dhe drejtësia islame).');
INSERT INTO "hasan_efendi" VALUES(17,34,'17:34','Mos iu qasni pasurisë së jetimit, veçse me qëllim të mirë, derisa të arrijë moshën e pjekurisë. Dhe përmbushni zotimet. Me të vërtetë, për zotimet do të jeni përgjegjës.');
INSERT INTO "hasan_efendi" VALUES(17,35,'17:35','Saktësojeni matjen, kur matni dhe peshoni me peshore të rregullt. Kjo është e mira që çon në përfundimin më të bukur.');
INSERT INTO "hasan_efendi" VALUES(17,36,'17:36','Mos shko pas diçkaje për të cilën nuk ke dijeni. Vërtet, dëgjimi, shikimi dhe zemra, të gjithë këta do të merren në përgjegjësi.');
INSERT INTO "hasan_efendi" VALUES(17,37,'17:37','Mos ec nëpër tokë me mendjemadhësi, se ti, në të vërtetë, nuk mund ta çash tokën e as të arrish lartësinë e maleve.');
INSERT INTO "hasan_efendi" VALUES(17,38,'17:38','E keqja e të gjitha këtyre është e urryer nga Zoti yt.');
INSERT INTO "hasan_efendi" VALUES(17,39,'17:39','Kjo është nga Urtësia që të ka shpallur ty, Zoti yt, dhe mos vër zot tjetër me Allahun, se do të hidhesh në Xhehenem, fajtor dhe i dëbuar.');
INSERT INTO "hasan_efendi" VALUES(17,40,'17:40','A mos vallë Allahu ju ka dhuruar djem e (për Vete) ka marrë engjëjt për vajza? Vërtet që ju thoni fjalë të tmerrshme.');
INSERT INTO "hasan_efendi" VALUES(17,41,'17:41','Në të vërtetë, Ne kemi paraqitur me hollësi në këtë Kuran shpalljet Tona, që ata (jobesimtarët) t’ua vënë veshin, por kjo vetëm ua ka shtuar atyre largimin (nga e vërteta).');
INSERT INTO "hasan_efendi" VALUES(17,42,'17:42','Thuaj: “Nëse përveç Atij do të kishte edhe zota të tjerë, siç thonë ata, atëherë ata do të kërkonin rrugë për të arritur te Zoti i Fronit”.');
INSERT INTO "hasan_efendi" VALUES(17,43,'17:43','Qoftë i lavdëruar dhe i lartësuar Ai mbi atë, që thonë ata!');
INSERT INTO "hasan_efendi" VALUES(17,44,'17:44','Atë e madhërojnë shtatë qiejt, Toka dhe gjithçka që gjendet në to. Dhe nuk ka asgjë që nuk e madhëron Atë, duke e lavdëruar, por ju (o njerëz) nuk e kuptoni madhërimin që i bëjnë ata. Ai, me të vërtetë, është i Butë dhe Falës.');
INSERT INTO "hasan_efendi" VALUES(17,45,'17:45','Kur ti (Muhamed) lexon Kuranin, Ne vëmë një perde të fshehtë mes teje dhe atyre që nuk besojnë në jetën e ardhshme.');
INSERT INTO "hasan_efendi" VALUES(17,46,'17:46','Ne kemi vënë mbulesa në zemrat e tyre, që të mos e kuptojnë atë (Kuranin), dhe ua kemi shurdhuar veshët. Kur ti përmend në Kuran vetëm Zotin tënd, ata kthejnë shpinën, duke ikur.');
INSERT INTO "hasan_efendi" VALUES(17,47,'17:47','Ne e dimë mirë se ç’dëgjojnë ata, kur të përgjojnë dhe, kur bisedojnë fshehurazi me njëri-tjetrin e mohuesit thonë: “Ju shkoni veçse pas një njeriu të magjepsur”.');
INSERT INTO "hasan_efendi" VALUES(17,48,'17:48','Shiko me çfarë shëmbëllimesh të krahasojnë! Ata janë të humbur e nuk mund të gjejnë rrugën e drejtë.');
INSERT INTO "hasan_efendi" VALUES(17,49,'17:49','Ata thonë: “Vallë, kur të bëhemi eshtra dhe pluhur, a do të ringjallemi në krijesa të reja?”');
INSERT INTO "hasan_efendi" VALUES(17,50,'17:50','Thuaj: “Bëhuni gurë ose hekur,');
INSERT INTO "hasan_efendi" VALUES(17,51,'17:51','ose çfarëdo krijese tjetër që është më e madhe sipas jush!” Ata do të thonë: “Kush do të na kthejë ne (në jetë?)” Thuaj: “Ai që ju ka krijuar herën e parë”. Ata do t’i tundin kokat nga ti e do të pyesin: “Kur do të bëhet ajo?” Ti thuaju: “Mund të vijë shpejt!');
INSERT INTO "hasan_efendi" VALUES(17,52,'17:52','Ditën, kur Ai do t’ju thërrasë e ju do t’i përgjigjeni Atij duke e falënderuar dhe do të mendoni se në këtë jetë, nuk keni qëndruar veçse pak kohë.”');
INSERT INTO "hasan_efendi" VALUES(17,53,'17:53','Thuaju, robërve të Mi, që të flasin atë që është më e mira. Në të vërtetë, djalli mbjell ngatërresa ndërmjet tyre. Vërtet, djalli është armik i hapët i njeriut.');
INSERT INTO "hasan_efendi" VALUES(17,54,'17:54','Zoti juaj ju njeh më së miri. Nëse dëshiron, Ai do t’ju mëshirojë ose nëse dëshiron, Ai do t’ju dënojë. Ne nuk të kemi dërguar ty (Muhamed) roje dhe rregullues të punëve të tyre.');
INSERT INTO "hasan_efendi" VALUES(17,55,'17:55','Zoti yt i di më së miri ata që gjenden në qiej dhe në Tokë. Ne i kemi ngritur disa profetë mbi të tjerët dhe Davudit i kemi dhënë Zeburin.');
INSERT INTO "hasan_efendi" VALUES(17,56,'17:56','Thuaj: “Thirrini ata që ju i hyjnizoni përveç Tij! Ata nuk mund t’jua largojnë të keqen e as nuk mund ta hedhin atë tjetërkund”.');
INSERT INTO "hasan_efendi" VALUES(17,57,'17:57','Vetë ata që idhujtarët u luten, kërkojnë t’i afrohen Zotit të tyre, madje edhe më të afërtit, duke shpresuar mëshirën e Tij dhe duke iu frikësuar dënimit të Tij. Me të vërtetë, nga dënimi i Zotit tënd duhet të ruhet gjithkush.');
INSERT INTO "hasan_efendi" VALUES(17,58,'17:58','Dhe s’ka asnjë vend që Ne nuk do ta zhdukim para Ditës së Kiametit apo që nuk do ta ndëshkojmë me dënim të ashpër. Kjo gjë është shkruar në Libër.');
INSERT INTO "hasan_efendi" VALUES(17,59,'17:59','Neve nuk na pengon asgjë që të dërgojmë shenja, mirëpo ato i kanë përgënjeshtruar popujt e lashtë. Ne i dhamë fisit Themud devenë si shenjë të qartë, por ai u soll keq me atë. Ne dërgojmë shenja vetëm për t’i frikësuar njerëzit. ');
INSERT INTO "hasan_efendi" VALUES(17,60,'17:60','(Kujto) kur Ne të thamë: “Me të vërtetë, Zoti yt, do të të mbrojë nga njerëzit”! Vizionin që ta treguam, e bëmë vetëm sprovë për njerëzit, ashtu si dhe pemën e mallkuar në Kuran . Dhe Ne i frikësojmë (paralajmërojmë) ata, por kjo vetëm u shton mohimin e madh.');
INSERT INTO "hasan_efendi" VALUES(17,61,'17:61','Kur u thamë engjëjve: “Përuluni në sexhde para Ademit”, ata të gjithë iu përulën në sexhde, përveç Iblisit! Ai tha: “Si?! Unë t’i bëj sexhde atij që Ti e ke krijuar nga balta?!”');
INSERT INTO "hasan_efendi" VALUES(17,62,'17:62','Gjithashtu, tha: “A e sheh këtë që Ti e nderove mbi mua? Nëse Ti më jep kohë deri në Ditën e Kiametit, unë do t’i shfaros me mashtrim pasardhësit e tij, përveç një pakice prej tyre!”');
INSERT INTO "hasan_efendi" VALUES(17,63,'17:63','(Allahu) tha: “Ik! Kushdo prej tyre që të pason ty, do të ketë Xhehenemin si shpërblim – një shpërblim i plotë.');
INSERT INTO "hasan_efendi" VALUES(17,64,'17:64','Bëj për vete, me zërin tënd, kë të mundesh nga ata! Lësho kundër tyre të gjithë kalorësinë e këmbësorinë tënde! Bëj pjesë në pasurinë e fëmijët e tyre dhe jepu premtime! -Premtimet e djallit janë vetëm mashtrime.');
INSERT INTO "hasan_efendi" VALUES(17,65,'17:65','Por në të vërtetë, ti nuk ke kurrfarë pushteti ndaj robërve të Mi! Zoti yt mjafton si mbrojtës (i tyre)!”');
INSERT INTO "hasan_efendi" VALUES(17,66,'17:66','Zoti juaj është ai që e bën anijen të lëvizë nëpër det për të kërkuar nga begatitë e Tij. Ai është vërtet i Mëshirshëm me ju.');
INSERT INTO "hasan_efendi" VALUES(17,67,'17:67','Kur ju ju godet fatkeqësia në det, ata që ju i adhuronit në vend të Atij, zhduken. Por, pasi Ai ju shpëton në tokë, ju i ktheni shpinën. Njeriu është mosmirënjohës.');
INSERT INTO "hasan_efendi" VALUES(17,68,'17:68','Vallë, a jeni të sigurt, që Ai nuk do t’ju fundosë në tokë ose, që nuk do të dërgojë mbi ju stuhi me rërë, pa pasur mundësi që ju të gjeni dikë, që t’ju mbrojë?');
INSERT INTO "hasan_efendi" VALUES(17,69,'17:69','Apo jeni të sigurt, që Ai nuk do t’ju kthejë ju përsëri (në det) e, që nuk do t’ju dërgojë furtunë e t’ju fundosë për shkak të mosbesimit tuaj, pa pasur mundësi që të gjeni dikë, që t’ju mbrojë nga Ne?');
INSERT INTO "hasan_efendi" VALUES(17,70,'17:70','Në të vërtetë, Ne i kemi nderuar bijtë e Ademit: ua kemi bërë të mundur që të udhëtojnë në tokë dhe në det, u kemi dhënë ushqime të mira e të lejuara dhe i kemi ngritur lart mbi shumë prej atyre që kemi krijuar.');
INSERT INTO "hasan_efendi" VALUES(17,71,'17:71','Ditën, kur Ne do t’i thërrasim të gjithë popujt me prijësin (të dërguarin) e vet, ata, që do t’u jepet libri në anën e djathtë, do ta lexojnë librin e vet dhe nuk do të pësojnë as padrejtësinë më të vogël.');
INSERT INTO "hasan_efendi" VALUES(17,72,'17:72','Ai, që në këtë botë ka qenë i verbër, do të jetë i verbër edhe në botën tjetër dhe më i humbur nga rruga e drejtë.');
INSERT INTO "hasan_efendi" VALUES(17,73,'17:73','Ata u përpoqën të të largonin ty (Muhamed) nga ai (Kuran), që të kemi shpallur Ne, me shpresë se ti, duke shpifur, do të Na vishje diçka tjetër përveç tij (Kuranit). Po ta bëje këtë, atëherë, ata do të të merrnin ty për mik.');
INSERT INTO "hasan_efendi" VALUES(17,74,'17:74','Dhe sikur mos të të kishim forcuar Ne, gati do të kishe anuar pak nga ata, ');
INSERT INTO "hasan_efendi" VALUES(17,75,'17:75','dhe atëherë, me të vërtetë, Ne do të bënim që të shijoje dënimin e dyfishtë në këtë botë dhe pas vdekjes; e atëherë, nuk do të gjeje askënd për ndihmës kundër Nesh.');
INSERT INTO "hasan_efendi" VALUES(17,76,'17:76','Në të vërtetë, ata po përpiqen të të trembin që të të dëbojnë ty nga vendi, por atëherë, ata do të qëndronin vetëm pak kohë pas ikjes sate.');
INSERT INTO "hasan_efendi" VALUES(17,77,'17:77','Ky ishte ligji Ynë, për të dërguarit e ardhur para teje, kështu që edhe ti nuk do të gjesh shmangie nga ligji Ynë.');
INSERT INTO "hasan_efendi" VALUES(17,78,'17:78','Kryeje namazin prej kthimit të Diellit (nga gjysma e qiellit) deri në errësirën e natës dhe këndo Kuran në agim; me të vërtetë, falja e mëngjesit është e dëshmuar (nga engjëjt).');
INSERT INTO "hasan_efendi" VALUES(17,79,'17:79','Kaloje një pjesë të natës, si falje shtesë për ty (Muhamed)! Është e vërtetë se Zoti yt do të të vendosë ty në një vend të lavdëruar.');
INSERT INTO "hasan_efendi" VALUES(17,80,'17:80','Thuaj: “O Zoti im, më shpjer në një hyrje të kënaqur, më nxirr nga një dalje të kënaqur dhe më dhuro nga Ti fuqi, që do të më ndihmojë!”');
INSERT INTO "hasan_efendi" VALUES(17,81,'17:81','Thuaj: “E vërteta erdhi, ndërsa e pavërteta u shkatërrua. Sigurisht, e pavërteta është e paracaktuar të zhduket”!');
INSERT INTO "hasan_efendi" VALUES(17,82,'17:82','Ne shpallim nga Kurani atë që është shërim dhe mëshirë për besimtarët e që mohuesve keqbërës u shton vetëm humbje.');
INSERT INTO "hasan_efendi" VALUES(17,83,'17:83','Kur Ne i dhurojmë mirësi njeriut, ai shmanget dhe tërhiqet (nga detyrimet e mirënjohjes ndaj Krijuesit); por, kur e godet e keqja, ai pikëllohet.');
INSERT INTO "hasan_efendi" VALUES(17,84,'17:84','Thuaj: “Çdokush sillet sipas mënyrës së vet, por Zoti juaj e di më së miri, se kush është në rrugën më të drejtë”.');
INSERT INTO "hasan_efendi" VALUES(17,85,'17:85','Do të të pyesin ty për shpirtin. Thuaj: “Shpirti është çështje që i përket vetëm Zotit tim, ndërsa juve nuk ju është dhënë veçse pak dijeni”.');
INSERT INTO "hasan_efendi" VALUES(17,86,'17:86','Me të vërtetë, sikur të donim Ne, do të ta merrnim ty atë shpallje që ta kemi dhënë, dhe ti, pastaj nuk do të gjeje askënd, që do të të ndihmonte për të kundër Nesh.');
INSERT INTO "hasan_efendi" VALUES(17,87,'17:87','Nëse nuk e bëjmë, është vetëm prej mëshirës nga ana e Zotit tënd. Vërtetë që mirësia e Tij ndaj teje është kurdoherë e madhe.');
INSERT INTO "hasan_efendi" VALUES(17,88,'17:88','Thuaj: “Sikur të tuboheshin të gjithë njerëzit dhe xhindet për të hartuar një Kuran të tillë, ata nuk do të mund të hartonin një të ngjashëm me të, madje, edhe sikur ta ndihmonin njëri - tjetrin.”');
INSERT INTO "hasan_efendi" VALUES(17,89,'17:89','Në këtë Kuran, Ne u shpjegojmë njerëzve gjithfarë shembujsh, por shumica e njerëzve, kurrsesi nuk duan të besojnë.');
INSERT INTO "hasan_efendi" VALUES(17,90,'17:90','Ata thonë: “Ne nuk do të të besojmë (o Muhamed), derisa të bësh, që të shpërthejë nga toka një gurrë uji për ne,');
INSERT INTO "hasan_efendi" VALUES(17,91,'17:91','ose, derisa të kesh një kopsht hurmash dhe rrushi, nëpër të cilin të rrjedhin lumenj');
INSERT INTO "hasan_efendi" VALUES(17,92,'17:92','ose, derisa ta bësh qiellin të bjerë copa mbi ne, siç ke thënë ose, derisa të sjellësh Allahun dhe engjëjt para nesh;');
INSERT INTO "hasan_efendi" VALUES(17,93,'17:93','ose, derisa të kesh shtëpi prej ari ose, derisa të ngjitesh në qiell. Ne nuk do të të besojmë, edhe sikur të ngjitesh në qiell, derisa të na sjellësh një libër që ta lexojmë”. Thuaj: “I Përlëvduar qoftë Zoti im! Unë nuk jam tjetër, veçse njeri - i dërguar”!');
INSERT INTO "hasan_efendi" VALUES(17,94,'17:94','Njerëzit nuk i pengoi asgjë të besojnë, kur u erdhi shpallja, përveçse thënies: “A mos vallë, Allahu ka sjellë si të dërguar një njeri?”');
INSERT INTO "hasan_efendi" VALUES(17,95,'17:95','Thuaj: “Sikur në Tokë të kishte engjëj që ecnin qetësisht, Ne do t’u çonim atyre nga qielli një engjëll si të dërguar”.');
INSERT INTO "hasan_efendi" VALUES(17,96,'17:96','Thuaj: “Allahu mjafton për dëshmitar midis meje dhe jush. Vërtet, Ai i njeh dhe i sheh mirë robërit e Vet”.');
INSERT INTO "hasan_efendi" VALUES(17,97,'17:97','Ai, që Allahu e udhëzon në rrugën e drejtë, është i udhëzuari; ndërsa për atë që Ai e çon në humbje, me siguri që nuk do të gjesh mbrojtës tjetër, përveç Atij. Ne do t’i tubojmë ata në Ditën e Kiametit, me fytyrat përtokë, të verbër, shurdhë e memecë. Vendbanimi i tyre është Xhehenemi. Kurdo, që të zbehet zjarri, Ne do t’ia shtojmë flakën.');
INSERT INTO "hasan_efendi" VALUES(17,98,'17:98','Ky është dënimi i tyre, ngase mohuan shenjat Tona dhe thanë: “A mos vallë, kur të bëhemi eshtra dhe pluhur, vërtet do të ngjallemi si krijesa të reja?!”');
INSERT INTO "hasan_efendi" VALUES(17,99,'17:99','A nuk e shohin ata, se Allahu, i Cili krijoi qiejt dhe Tokën, ka mundësi të krijojë të ngjashëm me ata dhe, se ua ka caktuar momentin e ringjalljes, për të cilën nuk ka dyshim? Por, jobesimtarët nuk pranojnë, ata vetëm mohojnë.');
INSERT INTO "hasan_efendi" VALUES(17,100,'17:100','Thuaj: “Sikur ju të zotëronit thesaret e mëshirës së Zotit tim, edhe atëherë ju do të rrudhnit dorën nga droja, se do të shpenzoheni. Njëmend, njeriu rëndom është koprrac”.');
INSERT INTO "hasan_efendi" VALUES(17,101,'17:101','Në të vërtetë, Ne i dhamë Musait nëntë shenja të qarta. Pyeti bijtë e Israilit, kur ai u erdhi të parëve të tyre dhe, kur Faraoni i tha atij: “O Musa, unë besoj se ti je i magjepsur”,');
INSERT INTO "hasan_efendi" VALUES(17,102,'17:102','(Musai) tha: “Tashmë ti e di se këto nuk i ka shpallur askush tjetër, përveç Zotit të qiellit dhe të Tokës, si prova të dukshme dhe Unë jam i bindur se ti je i dënuar në humbje”.');
INSERT INTO "hasan_efendi" VALUES(17,103,'17:103','Ai (Faraoni) deshi t’i dëbonte nga vendi, por Ne e fundosëm bashkë me të gjithë ata, që ishin me të,');
INSERT INTO "hasan_efendi" VALUES(17,104,'17:104','dhe, pas kësaj, u thamë bijve të Israilit: “Vendosuni në këtë tokë. Kur të arrijë premtimi i Ditës së Kiametit, do t’ju sjellim të përzier”.');
INSERT INTO "hasan_efendi" VALUES(17,105,'17:105','Ne e kemi shpallur Kuranin me të vërtetën dhe ai ka zbritur me të vërtetën. Ne të kemi dërguar ty (Muhamed) vetëm si përcjellës të lajmeve të mira dhe paralajmërues.');
INSERT INTO "hasan_efendi" VALUES(17,106,'17:106','Ky është një Kuran, që Ne e kemi ndarë pjesë-pjesë, që ti t’ua mësosh njerëzve pak nga pak, duke e shpallur atë kohë pas kohe.');
INSERT INTO "hasan_efendi" VALUES(17,107,'17:107','Thuaj: “Besoni në të (Kuranin) apo nuk besoni, ata që u është dhënë dijenia para (shpalljes së) tij, bien në sexhde me fytyrat përtokë, kur ai u lexohet,');
INSERT INTO "hasan_efendi" VALUES(17,108,'17:108','dhe thonë: “Qoftë i lavdëruar Zoti ynë! Me të vërtetë, premtimi i Zotit është plotësuar!”');
INSERT INTO "hasan_efendi" VALUES(17,109,'17:109','Ata bien me fytyrë përtokë, duke qarë dhe ai (leximi i Kuranit) ua rrit përuljen.');
INSERT INTO "hasan_efendi" VALUES(17,110,'17:110','Thuaj: “Thirrni Allahun ose thirrni të Gjithëmëshirshmin, me cilindo emër që ta thirrni ju Atë, Ai ka emrat më të bukur. Gjatë namazit, mos lexo (Kuran) me zë të lartë dhe as me zë tepër të ulët, por kërko një rrugë të mesme.”');
INSERT INTO "hasan_efendi" VALUES(17,111,'17:111','Thuaj: “Çdo lavd e falënderim i përket Allahut, i Cili nuk ka bir, nuk ka ortak në pushtet dhe nuk është i pafuqishëm, që të ketë nevojë për ndihmës” dhe madhëroje Atë, duke ngritur lart madhështinë Tij!”');
INSERT INTO "hasan_efendi" VALUES(18,1,'18:1','Çdo lavd dhe falënderim i përket Allahut, i Cili i shpalli robit të Vet (Muhamedit) një Libër të dëlirë nga çdo shtrembërim,');
INSERT INTO "hasan_efendi" VALUES(18,2,'18:2','(një libër) të drejtë, për të paralajmëruar nëpërmjet tij, dënim të ashpër (për jobesimtarët) dhe për t’u dhënë lajmin e mirë besimtarëve, të cilët bëjnë vepra të mira, se për ta do të ketë një shpërblim të bukur (Xheneti),');
INSERT INTO "hasan_efendi" VALUES(18,3,'18:3','në të cilin do të qëndrojnë përherë,');
INSERT INTO "hasan_efendi" VALUES(18,4,'18:4','si dhe për të paralajmëruar ata që thonë: “Allahu ka një bir”.');
INSERT INTO "hasan_efendi" VALUES(18,5,'18:5','Ata nuk kanë kurrfarë dijenie për këtë, as etërit e tyre. Sa fjalë e rëndë është ajo që del nga goja e tyre! Ata flasin vetëm gënjeshtra.');
INSERT INTO "hasan_efendi" VALUES(18,6,'18:6','A mos vallë do të vrasësh veten nga pikëllimi, nëse ata nuk besojnë në këtë Fjalë (Kuran)?');
INSERT INTO "hasan_efendi" VALUES(18,7,'18:7','Çdo gjë që gjendet në Tokë, Ne e kemi bërë stoli për të, për të provuar se kush prej tyre punon më mirë.');
INSERT INTO "hasan_efendi" VALUES(18,8,'18:8','Dhe, Ne do ta bëjmë të shkretë gjithçka, që gjendet në të.');
INSERT INTO "hasan_efendi" VALUES(18,9,'18:9','Vallë, a mendon ti se banorët e Shpellës dhe Rakimit ishin një çudi nga mrekullitë Tona?');
INSERT INTO "hasan_efendi" VALUES(18,10,'18:10','Kur disa djelmosha u strehuan në shpellë dhe thanë: “O Zoti ynë, na jep mëshirë nga ana Jote dhe përgatitna për sjellje të drejtë!”');
INSERT INTO "hasan_efendi" VALUES(18,11,'18:11','Ne ua mbyllëm veshët atyre (duke i lënë të flinin) në shpellë për shumë vjet.');
INSERT INTO "hasan_efendi" VALUES(18,12,'18:12','Pastaj i ngritëm, për të marrë vesh se cili nga të dyja grupet do ta vlerësonte më mirë sa kohë kishin qenë të strehuar.');
INSERT INTO "hasan_efendi" VALUES(18,13,'18:13','Ne po ta tregojmë ty historinë e tyre sipas së vërtetës. Ata ishin djelmosha që besonin në Zotin e tyre dhe Ne i forcuam në rrugën e drejtë.');
INSERT INTO "hasan_efendi" VALUES(18,14,'18:14','Ne i forcuam zemrat e tyre, kur u ngritën e thanë: “Zoti ynë është Zoti i qiejve dhe i Tokës. Ne nuk do t’i lutemi asnjë zoti tjetër, përveç Tij, sepse atëherë do të thoshim një gënjeshtër të madhe.');
INSERT INTO "hasan_efendi" VALUES(18,15,'18:15','Ky populli ynë ka zgjedhur në vend të Tij zota të tjerë, ndonëse ata nuk u sjellin dot asnjë provë të qartë. E kush është keqbërës më i madh, se ai që trillon gënjeshtra për Allahun?');
INSERT INTO "hasan_efendi" VALUES(18,16,'18:16','Kur ju t’i lini ata dhe gjithçka që adhurojnë në vend të Allahut, gjeni strehë në shpellë: Zoti juaj do t’ju mbulojë me mëshirën e Tij dhe do t’ju përgatisë mënyrën nga e cila do të keni dobi”. ');
INSERT INTO "hasan_efendi" VALUES(18,17,'18:17','Ti (o Muhamed) do ta kishe parë Diellin kur lindte, se si anohej nga e djathta e shpellës së tyre dhe, kur perëndonte, ata i linte në anën e majtë, ndërkohë që ata gjendeshin në mesin e saj. Kjo është nga shenjat e Allahut. Sa për atë, që Allahu e udhëzon në rrugë të drejtë, pikërisht ai është i udhëzuari. E sa për atë, që Ai e lë në humbje, ti, me siguri, nuk do të mund t’i gjesh mbrojtës që ta udhëzojë.');
INSERT INTO "hasan_efendi" VALUES(18,18,'18:18','Dhe (po të kishe qenë atje), do të mendoje se ata ishin zgjuar, por ata ishin në gjumë. Ne i rrotullonim ata herë në krah të djathtë e herë në të majtë, kurse qeni i tyre rrinte shtrirë me dy këmbët e para në hyrje të shpellës. Sikur t’i kishe parë, ti do të ikje nga ata dhe do të të kaplonte frika.');
INSERT INTO "hasan_efendi" VALUES(18,19,'18:19','Kështu pra, Ne i zgjuam që të pyesnin njëri-tjetrin. Njëri nga ata tha: “Sa keni ndenjur (në gjumë?)” - U përgjigjën: “Kemi qëndruar një ditë a një pjesë të ditës”. Disa thanë: “Zoti juaj e di më mirë se sa keni ndenjur. Dërgoni njërin prej jush në qytet me këtë monedhë argjendi tuajën e le të shikojë se cili ushqim është më i pastër. Le t’ju sjellë diçka prej tij, le të sillet mirë dhe askush të mos marrë vesh për ju.');
INSERT INTO "hasan_efendi" VALUES(18,20,'18:20','Me të vërtetë, nëse ju zbulojnë, ata do t’ju gjuajnë me gurë ose do t’ju kthejnë (me forcë) në fenë e tyre dhe atëherë nuk do të shpëtoni kurrsesi”! ');
INSERT INTO "hasan_efendi" VALUES(18,21,'18:21','Dhe kështu, Ne bëmë që ata të zbuloheshin, me qëllim që të dihej se premtimi i Allahut është i vërtetë dhe, që të mos ketë asnjë dyshim për Orën (e Kiametit, se ajo do të vijë). Kur ata (banorët e qytetit) po haheshin me njëri-tjetrin për këtë rast, disa thoshin: “Ndërtojini atyre (në hyrje të shpellës) një ndërtesë! Zoti i tyre i njeh më mirë ata”. Ata që e fituan çështjen e tyre, thanë: “Ne do të ndërtojmë një faltore mbi ta!”');
INSERT INTO "hasan_efendi" VALUES(18,22,'18:22','Do të thonë: “Ata ishin tre veta e qeni i tyre ishte i katërti”. Disa do të thonë: “Ata ishin pesë veta e qeni i tyre ishte i gjashti”, duke hamendësuar për atë që është e fshehtë; kurse të tjerë do të thonë: “Ata ishin shtatë veta e qeni i tyre ishte i teti”. Thuaj (o Muhamed): “Zoti im e di më mirë numrin e tyre. Vetëm pak veta dinë diçka rreth tyre”. Dhe mos polemizo rreth tyre, përveçse në mënyrë të përciptë dhe për ata mos pyet askënd!');
INSERT INTO "hasan_efendi" VALUES(18,23,'18:23','Asnjëherë mos thuaj, për çfarëdo gjëje qoftë, se: “Me siguri që nesër do ta bëj këtë”');
INSERT INTO "hasan_efendi" VALUES(18,24,'18:24','pa shtuar “Në dashtë Allahu!” Dhe, kur të harrosh (të thuash “në dashtë Allahu”) - kujtoje Zotin tënd e thuaj: “Shpresoj se Zoti im do të më udhëzojë në atë që është më e mirë dhe më e dobishme nga kjo”.');
INSERT INTO "hasan_efendi" VALUES(18,25,'18:25','Ata qëndruan në shpellën e tyre treqind vjet (diellorë), duke shtuar nëntë (për vitet hënore).');
INSERT INTO "hasan_efendi" VALUES(18,26,'18:26','Thuaj (o Muhamed): “Allahu e di më mirë sa kanë qëndruar. Atij i përket fshehtësia e qiejve dhe e Tokës. Eh, sa mirë sheh Ai çdo gjë dhe sa mirë që dëgjon! Njerëzit nuk kanë tjetër mbrojtës përveç Atij dhe Ai nuk e ndan me askënd pushtetin e Vet.”');
INSERT INTO "hasan_efendi" VALUES(18,27,'18:27','Dhe lexo atë që të është shpallur ty nga Libri i Zotit tënd! Askush nuk mund t’i ndryshojë fjalët e Tij dhe ti nuk ke për të gjetur kurrfarë strehe, përveçse tek Ai.');
INSERT INTO "hasan_efendi" VALUES(18,28,'18:28','Dhe qëndro me ata që i luten Zotit të vet në mëngjes dhe mbrëmje, duke dëshiruar Fytyrën (kënaqësinë) e Tij dhe mos i shmang sytë nga ata (tek të tjerët), duke dëshiruar stolitë e shkëlqimin e kësaj jete. Mos dëgjo atë zemrën e të cilit ia kemi lënë mospërfillëse ndaj përmendjes Sonë e, që shkon pas dëshirave të veta, duke shkelur çdo kufi në veprimet e veta.');
INSERT INTO "hasan_efendi" VALUES(18,29,'18:29','Dhe thuaj: “E vërteta është nga Zoti juaj. Kush të dojë, le të besojë e kush të dojë, le të mohojë”. Ne kemi përgatitur për mohuesit keqbërës një zjarr, që do t’i rrethojë nga të gjitha anët (si çadra). Nëse do të kërkojnë ndihmë, do t’u jepet një ujë si xehja e shkrirë, që do t’ua përcëllojë fytyrat. Sa pije e tmerrshme është ajo e sa strehim i keq është ai (zjarri)!');
INSERT INTO "hasan_efendi" VALUES(18,30,'18:30','Sa për ata që besojnë dhe bëjnë vepra të mira, sigurisht që Ne nuk do t’ua humbim shpërblimin punëmirëve.');
INSERT INTO "hasan_efendi" VALUES(18,31,'18:31','Pikërisht atyre u përket Xheneti i Adnit, nëpër të cilin rrjedhin lumenj. Ata do të stolisen atje me byzylykë ari dhe do të vishen me rroba të gjelbra prej mëndafshi të hollë dhe armaçi, duke ndenjur të mbështetur në shtretër të lartë. Sa shpërblim i mrekullueshëm dhe sa vendbanim i bukur!');
INSERT INTO "hasan_efendi" VALUES(18,32,'18:32','Dhe jepu atyre si shembull ndodhinë e dy njerëzve. Njërit prej tyre Ne i kishim dhënë dy kopshte vreshtash, që i kishim rrethuar me hurma e midis tyre kishim bërë fusha drithi.');
INSERT INTO "hasan_efendi" VALUES(18,33,'18:33','Të dy kopshtet jepnin frutat e veta, pa u munguar asgjë dhe bëmë që midis tyre të rridhte një lumë.');
INSERT INTO "hasan_efendi" VALUES(18,34,'18:34','Ai pati prodhim të bollshëm dhe i tha shokut të vet, duke biseduar me të: “Unë kam më shumë pasuri se ti dhe kam rreth më të fortë!”');
INSERT INTO "hasan_efendi" VALUES(18,35,'18:35','Pastaj hyri në kopshtin e vet dhe, duke qenë i padrejtë me vetveten, tha: “Unë nuk mendoj se kjo do të shkatërrohet ndonjëherë');
INSERT INTO "hasan_efendi" VALUES(18,36,'18:36','dhe nuk mendoj se do të arrijë ndonjëherë Ora (e Kiametit). Por, edhe nëse do të më kthejnë te Zoti im, sigurisht që do të gjej një vend më të mirë se ky (kopsht)”!');
INSERT INTO "hasan_efendi" VALUES(18,37,'18:37','Shoku i tij i tha, duke biseduar me të: “A e mohon ti Atë, i Cili të ka krijuar nga dheu, pastaj nga pika e farës dhe të bëri më në fund njeri të plotë?!');
INSERT INTO "hasan_efendi" VALUES(18,38,'18:38','Sa për mua, unë besoj se Allahu është Zoti im dhe Zotit tim unë nuk i shoqëroj askënd (në adhurim).');
INSERT INTO "hasan_efendi" VALUES(18,39,'18:39','Do të ishte më mirë për ty sikur të kishe thënë, kur hyre në kopshtin tënd: “Kështu ka dashur Allahu! Nuk ka fuqi përveçse nëpërmjet Allahut.” Nëse ti sheh se unë kam më pak pasuri dhe pasardhës,');
INSERT INTO "hasan_efendi" VALUES(18,40,'18:40','dije, Zoti im, mund të më japë diçka më të mirë se kopshti yt, ndërsa në kopshtin tënd mund të dërgojë një shkatërrim nga qielli e të gdhijë rrafsh pa asgjë,');
INSERT INTO "hasan_efendi" VALUES(18,41,'18:41','ose, të bëjë që uji i tij (kopshtit), të humbasë në thellësi e të mos mund ta gjesh kurrë më”.');
INSERT INTO "hasan_efendi" VALUES(18,42,'18:42','Dhe pasuria e tij (e mohuesit) u shkatërrua, kurse ai i rrihte duart për gjithçka që kishte shpenzuar në të (pronën e vet) - e që tashmë ishte shkretuar nga rrënjët -, dhe thërriste: “Ah! Sikur të mos i kisha shoqëruar Zotit tim askënd!”');
INSERT INTO "hasan_efendi" VALUES(18,43,'18:43','Kështu, ai nuk pati kurrfarë rrethi që ta ndihmonte kundër Allahut dhe as që ishte në gjendje të mbronte vetveten.');
INSERT INTO "hasan_efendi" VALUES(18,44,'18:44','Në një rast të tillë, mbrojtja vjen vetëm nga Allahu, i Vërteti. Ai është më i miri për të shpërblyer dhe jep përfundimin më të mirë.');
INSERT INTO "hasan_efendi" VALUES(18,45,'18:45','Jepu atyre shembullin e jetës së kësaj bote: ajo është si uji që Ne e lëshojmë nga qielli; bimësia në tokë përzihet me të, por pastaj bëhet si bar i thatë të cilin e shpërndan era. Allahu është i Fuqishëm për çdo gjë!');
INSERT INTO "hasan_efendi" VALUES(18,46,'18:46','Pasuria dhe djemtë janë stoli e jetës së kësaj bote. Por veprat e mira që mbeten përherë, janë më të mira te Zoti yt sa i përket shpërblimit dhe, janë më të mira sa i përket shpresës (për shpëtim).');
INSERT INTO "hasan_efendi" VALUES(18,47,'18:47','Ditën, kur Ne do t’i shkulim malet, do ta shohësh tokën të zhveshur dhe do t’i tubojmë të gjithë njerëzit, pa lënë askënd prapa.');
INSERT INTO "hasan_efendi" VALUES(18,48,'18:48','Ata do të sillen të radhitur para Zotit tënd (e do t’u thuhet): “Tashmë Na erdhët, ashtu siç ju krijuam herën e parë, ndonëse ju (o jobesimtarë) pretendonit se nuk do t’ju caktonim një takim (ringjalljen)”.');
INSERT INTO "hasan_efendi" VALUES(18,49,'18:49','Dhe Libri (i veprave të tyre) do t’u vihet përpara, e do t’i shohësh gjynahqarët se si do të tmerrohen nga ato që gjenden në të dhe do të thonë: “Të mjerët ne! Çfarë është ky libër që nuk paska lënë asnjë vepër të madhe apo të vogël pa e shënuar”. Aty do të gjejnë të shënuar gjithçka që kanë punuar. Zoti yt nuk i bën padrejtësi askujt.');
INSERT INTO "hasan_efendi" VALUES(18,50,'18:50','Kur u thamë engjëjve: “Përuluni në sexhde para Ademit”, ata u përulën, përveç Iblisit. Ai ishte një nga xhindet dhe nuk iu bind urdhrit të Zotit të vet. Atëherë, a do ta merrnit atë dhe pasardhësit e tij për mbrojtës në vendin Tim, edhe pse ata janë armiqtë tuaj?! Sa këmbim i shëmtuar që është ky për keqbërësit!');
INSERT INTO "hasan_efendi" VALUES(18,51,'18:51','Unë nuk i bëra ata dëshmitarë të krijimit të qiejve dhe të Tokës e as të krijimit të vetë atyre; dhe, sigurisht që Unë nuk mund t’i marr si ndihmës ata që çojnë të tjerët në humbje.');
INSERT INTO "hasan_efendi" VALUES(18,52,'18:52','Ditën, kur Ai do të thotë: “Thirrini ata që ju mendoni se janë shokët e Mi”, ata do t’i thërrasin, por nuk do të marrin përgjigje prej tyre dhe, Ne, do të vendosim ndërmjet tyre barrierë.');
INSERT INTO "hasan_efendi" VALUES(18,53,'18:53','Të ligjtë do të shohin zjarrin dhe do të binden, se me të vërtetë, do të bien në të dhe nuk do të gjejnë largim prej atij.');
INSERT INTO "hasan_efendi" VALUES(18,54,'18:54','Sigurisht, në këtë Kuran, Ne u kemi shpjeguar njerëzve çdo lloj shembulli, por njeriu kundërshton më tepër se çdo krijesë tjetër.');
INSERT INTO "hasan_efendi" VALUES(18,55,'18:55','Çfarë i pengon njerëzit të besojnë, kur u vjen udhëzimi - dhe të kërkojnë falje nga Zoti i tyre?! Apo presin vetëm që të pësojnë fatin e popujve të parë ose që t’u vijë dënimi i hapët?!');
INSERT INTO "hasan_efendi" VALUES(18,56,'18:56','Ne i çojmë të dërguarit vetëm si përcjellës të lajmit të mirë dhe paralajmërues. Por mohuesit (e besimit) polemizojnë me argumente të rreme për ta dobësuar të Vërtetën, ndërsa shenjat dhe paralajmërimet e Mia i marrin për tallje.');
INSERT INTO "hasan_efendi" VALUES(18,57,'18:57','Kush është më i padrejtë se ai që, kur ia kujtojnë shpalljet e Zotit të vet, largohet nga ato dhe harron se ç’kanë bërë duart e tij më parë?! Ne kemi vënë perde në zemrat e tyre, që të mos e kuptojnë atë (Kuranin), dhe ua kemi rënduar veshët. Edhe sikur ti t’i thërrasësh ata në rrugën e drejtë, ata kurrë nuk do të udhëzohen.');
INSERT INTO "hasan_efendi" VALUES(18,58,'18:58','Por Zoti yt është Falësi plot mëshirë. Sikur t’i dënonte ata për çfarë kanë bërë, Ai do t’ua shpejtonte dënimin. Por ata i pret një çast i caktuar, nga i cili kurrë nuk do të shpëtojnë.');
INSERT INTO "hasan_efendi" VALUES(18,59,'18:59','Ato qytete Ne i shkatërruam kur banorët e tyre bënë të këqija dhe, për shkatërrimin e tyre, Ne kishim caktuar një afat.');
INSERT INTO "hasan_efendi" VALUES(18,60,'18:60','(Kujto) kur Musai i tha djaloshit që e shoqëronte: “Nuk do të pushoj së ecuri, derisa të arrij në vendin ku takohen dy detet, edhe nëse do të më duhet të udhëtoj për shumë kohë.”');
INSERT INTO "hasan_efendi" VALUES(18,61,'18:61','Dhe, kur arritën në vendin, ku bashkoheshin dy detet, ata e harruan peshkun e tyre dhe ai rrëshqiti në det, duke e hapur atë si tunel.');
INSERT INTO "hasan_efendi" VALUES(18,62,'18:62','Kur kaluan më tej, Musai i tha shoqëruesit të vet: “Na jep drekën tonë, sepse jemi lodhur nga ky udhëtimi ynë”.');
INSERT INTO "hasan_efendi" VALUES(18,63,'18:63','Ai u përgjigj: “Shiko! Kur ndaluam tek ai shkëmbi, unë e harrova peshkun. Vetëm djalli më bëri ta harroj e nuk ta thashë se peshku mori rrugën nga deti në mënyrë të habitshme!”');
INSERT INTO "hasan_efendi" VALUES(18,64,'18:64','(Musai) tha: “Kjo është ajo që po kërkojmë”. Andaj u kthyen nëpër gjurmët e veta,');
INSERT INTO "hasan_efendi" VALUES(18,65,'18:65','dhe gjetën një prej robërve Tanë, të cilit i patëm dhuruar mëshirë prej Nesh dhe i kishim mësuar nga ana Jonë dije.');
INSERT INTO "hasan_efendi" VALUES(18,66,'18:66','Musai i tha atij: “A mund të të pasoj ty, që të më mësosh edhe mua diçka prej diturisë së drejtë që të është dhënë ty?”');
INSERT INTO "hasan_efendi" VALUES(18,67,'18:67','Ai u përgjigj: “Ti nuk mund të durosh dot me mua!');
INSERT INTO "hasan_efendi" VALUES(18,68,'18:68','E si mund të durosh (pa pyetur) para diçkaje për të cilën nuk di asgjë”?');
INSERT INTO "hasan_efendi" VALUES(18,69,'18:69','(Musai) tha: “Do të më gjesh të durueshëm, në dashtë Allahu e nuk do të të kundërshtoj për asgjë.”');
INSERT INTO "hasan_efendi" VALUES(18,70,'18:70','(Hidri) i tha: “Nëse vjen pas meje, mos më pyet për asgjë, derisa të ta shpjegoj unë!”');
INSERT INTO "hasan_efendi" VALUES(18,71,'18:71','Dhe kështu, u nisën. Kur hipën në një anije, ai (Hidri) hapi një vrimë poshtë. (Musai) tha: “Mos vallë, e hape vrimën për t’i mbytur udhëtarët e saj? Me të vërtetë, ke bërë punë të shumë të keqe”.');
INSERT INTO "hasan_efendi" VALUES(18,72,'18:72','(Hidri) i tha: “A nuk të thashë se ti nuk mund të durosh me mua?”');
INSERT INTO "hasan_efendi" VALUES(18,73,'18:73','Ai u përgjigj: “Mos më qorto për atë që harrova dhe mos më ngarko me vështirësi në punët e mia!”');
INSERT INTO "hasan_efendi" VALUES(18,74,'18:74','Dhe që të dy vazhduan të ecin, derisa takuan një djalosh, të cilin ai (Hidri) e vrau. Tha (Musai): “Përse vrave një njeri të pafajshëm që nuk ka vrarë askënd?! Vërtet që ke bërë një punë të tmerrshme”!');
INSERT INTO "hasan_efendi" VALUES(18,75,'18:75','Ai u përgjigj: “A nuk të thashë se ti nuk mund të durosh me mua?”');
INSERT INTO "hasan_efendi" VALUES(18,76,'18:76','(Musai) tha: “Nëse pas kësaj do të të pyes sërish për çfarëdo gjëje, atëherë mos më mbaj më në shoqërinë tënde! Tashmë ke arsye (të mjaftueshme) nga ana ime (për t’u ndarë prej meje)!”');
INSERT INTO "hasan_efendi" VALUES(18,77,'18:77','Dhe që të dy vazhduan të ecin, derisa arritën në një fshat dhe u kërkuan banorëve të tij, që t’u jepnin për të ngrënë, por ata nuk i pranuan mysafirë. Aty ndeshën një mur që ishte duke u rrëzuar dhe ai (Hidri) e drejtoi. (Musai) tha: “Sikur të kishe dashur ti, mund të kërkoje shpërblim për këtë.”');
INSERT INTO "hasan_efendi" VALUES(18,78,'18:78','Ai tha: “Kjo është ndarja ndërmjet meje dhe teje. Unë do t’i shpjegoj ty ato gjëra për të cilat nuk munde të durosh.');
INSERT INTO "hasan_efendi" VALUES(18,79,'18:79','Sa i përket anijes, ajo ishte pronë e disa të varfërve, që punonin në det. Unë desha ta dëmtoj, sepse prapa tyre gjendej një mbret i cili merrte me dhunë çdo anije të mirë.');
INSERT INTO "hasan_efendi" VALUES(18,80,'18:80','Sa për djaloshin, prindërit e tij ishin besimtarë. Ne e dinim se (dashuria për të) do t’i çonte ata në të keqe dhe mohim,');
INSERT INTO "hasan_efendi" VALUES(18,81,'18:81','dhe dëshiruam që Zoti i tyre, në vend të atij t’u jepte një më të mirë, më të pastër dhe më të mëshirshëm.');
INSERT INTO "hasan_efendi" VALUES(18,82,'18:82','E, sa i përket murit, ai u takonte dy djelmoshave jetimë në qytet dhe, nën të gjendej një thesar i tyre. Babai i tyre kishte qenë njeri i mirë, prandaj Zoti yt dëshiroi që ata të arrinin moshën e pjekurisë e ta nxirrnin thesarin e tyre, si mëshirë nga Zoti yt. Unë këtë nuk e kam bërë sipas gjykimit tim. Ky është shpjegimi i asaj, për të cilën ti s’munde të duroje!');
INSERT INTO "hasan_efendi" VALUES(18,83,'18:83','Ata (hebrenjtë) të pyesin ty (Muhamed) për Dhulkarnejnin, thuaj: “Unë do t’ju tregoj disa lajme për të.”');
INSERT INTO "hasan_efendi" VALUES(18,84,'18:84','Ne i dhamë atij mundësi në Tokë dhe i dhamë mjete e rrugë për të arritur çdo gjë,');
INSERT INTO "hasan_efendi" VALUES(18,85,'18:85','kështu që ai u nis në një rrugë.');
INSERT INTO "hasan_efendi" VALUES(18,86,'18:86','Kur arriti në vendin e perëndimit të diellit, pa se ai po ulej në një burim të zi balte dhe në afërsi të tij gjeti një popull. Ne thamë: “O Dhulkarnejn, mund t’i ndëshkosh ata ose mund t’u bësh mirë atyre.”');
INSERT INTO "hasan_efendi" VALUES(18,87,'18:87','Ai tha: “Sa i përket atij që bën të këqija, Ne do ta ndëshkojmë e pastaj do të kthehet te Zoti i vet, i Cili do ta ndëshkojë me një dënim të rëndë.');
INSERT INTO "hasan_efendi" VALUES(18,88,'18:88','Sa për atë që beson dhe bën vepra të mira, atij i takon shpërblimi më i mirë dhe do t’i premtojmë atij lehtësira.”');
INSERT INTO "hasan_efendi" VALUES(18,89,'18:89','Pastaj ndoqi një rrugë tjetër.');
INSERT INTO "hasan_efendi" VALUES(18,90,'18:90','Kur arriti në vendin e lindjes së diellit, Ai e pa të lindte mbi një popull, të cilit Ne nuk i kishim dhënë gjë, për t’u mbrojtur nga dielli.');
INSERT INTO "hasan_efendi" VALUES(18,91,'18:91','Kështu ndodhi dhe Ne dinim gjithçka për të.');
INSERT INTO "hasan_efendi" VALUES(18,92,'18:92','Pastaj mori një rrugë tjetër.');
INSERT INTO "hasan_efendi" VALUES(18,93,'18:93','Derisa, kur arriti në mes dy malesh, gjeti rrëzë tyre një popull, i cili pothuaj nuk kuptonte asnjë të folme.');
INSERT INTO "hasan_efendi" VALUES(18,94,'18:94','Ata thanë: “O Dhulkarnejn, vërtet, Jexhuxhët dhe Mexhuxhët janë ngatërrestarë në Tokë. A të të japim një pagesë që të ndërtosh një pendë midis nesh dhe atyre?”');
INSERT INTO "hasan_efendi" VALUES(18,95,'18:95','Ai tha: “Ajo të cilën ma ka mundësuar Zoti im, është më e mirë (nga pagesa juaj), por më ndihmoni mua me krahë! Do të ndërtoj një pendë midis jush e atyre.');
INSERT INTO "hasan_efendi" VALUES(18,96,'18:96','Më sillni blloqe hekuri!” Dhe, kur ai i rrafshoi dy anët e thepisura të maleve, tha: “Fryjini zjarrit, derisa hekuri të skuqet si zjarri.” Pastaj tha: “Më sillni bakër të shkrirë që ta hedh në të.”');
INSERT INTO "hasan_efendi" VALUES(18,97,'18:97','Kështu, ata (Jexhuxhët dhe Mexhuxhët) nuk mundën që ta kapërcenin pendën dhe as nuk mundën që të hapnin vrimë në të.');
INSERT INTO "hasan_efendi" VALUES(18,98,'18:98','Ai tha: “Kjo është mëshirë nga Zoti im! Kur të vijë premtimi i Zotit tim, Ai do ta rrafshojë atë (pendën). Premtimi i Zotit tim është (gjithnjë) i vërtetë.');
INSERT INTO "hasan_efendi" VALUES(18,99,'18:99','Atë Ditë, Ne do të bëjmë, që të vërshojnë njëri mbi tjetrin si valët (e detit) dhe do t’i fryhet Surit e do t’i tubojmë, që të gjithë së bashku.');
INSERT INTO "hasan_efendi" VALUES(18,100,'18:100','Atë ditë do t’ua tregojmë haptazi Xhehenemin mohuesve,');
INSERT INTO "hasan_efendi" VALUES(18,101,'18:101','të cilët i kishin sytë të mbuluar para këshillave të Mia dhe të cilët nuk mund të dëgjonin.');
INSERT INTO "hasan_efendi" VALUES(18,102,'18:102','Vallë, a mendojnë mohuesit, se do të mund t’i zgjedhin robërit e Mi si mbrojtës, në vendin Tim?! Ne e kemi përgatitur Xhehenemin si vendbanim për mohuesit.');
INSERT INTO "hasan_efendi" VALUES(18,103,'18:103','Thuaj: “A doni t’ju lajmëroj për ata që, më së shumti, humbasin nga punët e veta,');
INSERT INTO "hasan_efendi" VALUES(18,104,'18:104','për ata, përpjekja e të cilëve ka qenë e kotë në jetën e kësaj bote, ndërkohë që mendonin se po bënin vepra të mira?”');
INSERT INTO "hasan_efendi" VALUES(18,105,'18:105','Këta janë ata, që nuk kanë besuar në shpalljet e Zotit të tyre dhe në takimin me Të. Punët e tyre do të fshihen, andaj Ne nuk do të vëmë kurrfarë peshe për ta (për veprat e tyre), në Ditën e Kiametit.');
INSERT INTO "hasan_efendi" VALUES(18,106,'18:106','Shpërblimi i tyre do të jetë Xhehenemi për shkak të mohimit të tyre dhe për shkak se talleshin me shenjat e Mia dhe të dërguarit e Mi.');
INSERT INTO "hasan_efendi" VALUES(18,107,'18:107','Me të vërtetë, ata që besojnë dhe bëjnë vepra të mira, do të kenë për banesa kopshtet e Firdeusit,');
INSERT INTO "hasan_efendi" VALUES(18,108,'18:108','ku do të qëndrojnë përherë, duke mos dëshiruar asnjë ndryshim.');
INSERT INTO "hasan_efendi" VALUES(18,109,'18:109','Thuaj: “Sikur deti të ishte bojë për të shkruar fjalët e Zotit tim, sigurisht që ai do të shterej, para se të soseshin fjalët e Zotit tim, edhe sikur Ne të sillnim si ndihmë edhe një det tjetër si ai.”');
INSERT INTO "hasan_efendi" VALUES(18,110,'18:110','Thuaj: “Unë jam vetëm një vdekatar si ju, që më është shpallur se Zoti juaj është një Zot i Vetëm. Kështu, kush shpreson takimin me Zotin e vet, le të bëjë vepra të mira dhe të mos i shoqërojë askënd në adhurim Zotit të vet!”');
INSERT INTO "hasan_efendi" VALUES(19,1,'19:1','Kâf, Hâ, Jâ, Ajn, Sád.');
INSERT INTO "hasan_efendi" VALUES(19,2,'19:2','Ky është rrëfim për mëshirën e Zotit tënd ndaj robit të Tij - Zekerijas.');
INSERT INTO "hasan_efendi" VALUES(19,3,'19:3','Kur ai e thirri Zotin e vet me lutje të fshehtë');
INSERT INTO "hasan_efendi" VALUES(19,4,'19:4','(duke thënë): “O Zoti im! Eshtrat tashmë më janë dobësuar, koka më ndriçon nga thinjat dhe asnjëherë nuk kam mbetur i zhgënjyer nga lutja ime për Ty, o Zoti im!');
INSERT INTO "hasan_efendi" VALUES(19,5,'19:5','Unë u frikësohem (sjelljeve të) të afërmve të mi pas meje , ndërsa bashkëshortja ime është beronjë; andaj, më dhuro nga ana Jote një pasardhës,');
INSERT INTO "hasan_efendi" VALUES(19,6,'19:6','që të më trashëgojë mua dhe familjen e Jakubit. O Zoti im, bëje atë të pëlqyeshëm (për Ty)!”');
INSERT INTO "hasan_efendi" VALUES(19,7,'19:7','“O Zekerija, Ne po të japim lajmin e mirë për një djalë që do të quhet Jahja. Askujt nuk ia kemi dhënë këtë emër më parë”.');
INSERT INTO "hasan_efendi" VALUES(19,8,'19:8','Ai tha: “O Zoti im, si mund të kem unë djalë, kur bashkëshortja ime është beronjë dhe unë kam arritur pleqërinë e thellë?!”');
INSERT INTO "hasan_efendi" VALUES(19,9,'19:9','I tha: “Ashtu ka thënë Zoti yt. Kjo për mua është lehtë. Unë të kam krijuar edhe ty më parë, ndërkohë që nuk ekzistoje”.');
INSERT INTO "hasan_efendi" VALUES(19,10,'19:10','(Zekerija) tha: “O Zoti im, më jep ndonjë shenjë!” Ai tha: “Shenja jote do të jetë se nuk do të mund të flasësh me njerëzit për tri net, ndonëse do të jesh i shëndoshë.”');
INSERT INTO "hasan_efendi" VALUES(19,11,'19:11','Ai doli nga faltorja para popullit të vet dhe u bëri me shenjë atyre të lavdëronin Allahun në mëngjes dhe në mbrëmje!');
INSERT INTO "hasan_efendi" VALUES(19,12,'19:12','(Pasi lindi djali, iu tha): “O Jahja, rroke fort Librin (Teuratin).” Dhe Ne, kur ende ishte fëmijë, i dhamë mençuri,');
INSERT INTO "hasan_efendi" VALUES(19,13,'19:13','butësi prej Nesh dhe dëlirësi. Ai ishte besimtar i përkushtuar');
INSERT INTO "hasan_efendi" VALUES(19,14,'19:14','dhe i sjellshëm ndaj prindërve të vet, nuk ishte i ashpër dhe i padëgjueshëm.');
INSERT INTO "hasan_efendi" VALUES(19,15,'19:15','Paqja qoftë mbi atë ditën kur ka lindur, ditën kur të vdesë dhe në ditën kur do të ringjallet.');
INSERT INTO "hasan_efendi" VALUES(19,16,'19:16','Dhe trego (o Muhamed) në Libër për Merjemen, kur u tërhoq nga familja e saj në një vend në lindje,');
INSERT INTO "hasan_efendi" VALUES(19,17,'19:17','duke e fshehur veten nga ata. Ne i dërguam Shpirtin Tonë, i cili iu paraqit asaj si një njeri i plotë.');
INSERT INTO "hasan_efendi" VALUES(19,18,'19:18','Ajo i tha: “Unë kërkoj që të më mbrojë i Gjithëmëshirshmi prej teje, nëse i frikësohesh Allahut.');
INSERT INTO "hasan_efendi" VALUES(19,19,'19:19','Ai tha: “Në të vërtetë, unë jam vetëm një i dërguar i Zotit tënd, që të të dhuroj ty një djalë të pastër.”');
INSERT INTO "hasan_efendi" VALUES(19,20,'19:20','Ajo tha: “Si mund të kem unë djalë, kur asnjë mashkull nuk më ka prekur e nuk jam as e pacipë?!”');
INSERT INTO "hasan_efendi" VALUES(19,21,'19:21','Iu përgjigj: “Kështu është! Zoti yt ka thënë: ‘Kjo për Mua është e lehtë. Ne do ta bëjmë atë shenjë për njerëzit dhe mëshirë nga ana Jonë’. Dhe kjo është punë e kryer.”');
INSERT INTO "hasan_efendi" VALUES(19,22,'19:22','Ajo mbeti me barrë e u tërhoq me të në një vend të largët.');
INSERT INTO "hasan_efendi" VALUES(19,23,'19:23','Dhembjet e lindjes e detyruan atë që të shkonte te trungu i një hurme. Ajo tha: “Ah, sikur të kisha vdekur para kësaj (ngjarjeje) dhe të isha harruar krejt!”');
INSERT INTO "hasan_efendi" VALUES(19,24,'19:24','Një zë e thirri nga poshtë: “Mos u pikëllo, se Zoti yt të ka bërë një burim uji poshtë teje!');
INSERT INTO "hasan_efendi" VALUES(19,25,'19:25','Shkunde trungun e palmës, që të bien mbi ty hurma të freskëta!');
INSERT INTO "hasan_efendi" VALUES(19,26,'19:26','Ha, pi dhe qetësohu. Nëse vëren ndonjë njeri, i thuaj: “Unë jam betuar në të Gjithëmëshirshmin se do të agjëroj, prandaj sot nuk do të flas me askënd.”');
INSERT INTO "hasan_efendi" VALUES(19,27,'19:27','Dhe u kthye te populli i vet, duke mbajtur fëmijën. Ata i thanë: “O Merjeme, ke bërë diçka të turpshme!');
INSERT INTO "hasan_efendi" VALUES(19,28,'19:28','O motra e Harunit! Babai yt nuk ka qenë njeri i keq, as nëna jote nuk ka qenë grua e shthurur.”');
INSERT INTO "hasan_efendi" VALUES(19,29,'19:29','Ajo bëri me shenjë nga i biri. Ata thanë: “Si t’i flasim ne fëmijës që është në djep?”');
INSERT INTO "hasan_efendi" VALUES(19,30,'19:30','Ai (Isai në djep) tha: “Unë jam rob i Allahut. Ai më ka dhënë Librin (Ungjillin), më ka bërë profet');
INSERT INTO "hasan_efendi" VALUES(19,31,'19:31','dhe më ka bërë të bekuar kudo që të jem. Ai më ka porositur që të falem e të jap zekat sa të jem gjallë');
INSERT INTO "hasan_efendi" VALUES(19,32,'19:32','dhe që të jem i mirë ndaj nënës sime dhe nuk më ka bërë të ashpër dhe të padëgjueshëm.');
INSERT INTO "hasan_efendi" VALUES(19,33,'19:33','Le të jetë paqja e shpëtimi mbi mua, në ditën që kam lindur, në ditën që do të vdes dhe në ditën që do të ringjallem!”');
INSERT INTO "hasan_efendi" VALUES(19,34,'19:34','Ky është Isai, i biri i Merjemes. Kjo është fjala e vërtetë, për të cilën ata dyshojnë dhe diskutojnë.');
INSERT INTO "hasan_efendi" VALUES(19,35,'19:35','Allahu nuk ka mbetur të marrë dikë për bir. Qoftë i lavdëruar Ai! Kur vendos diçka, Ai vetëm thotë për të “Bëhu!” dhe ajo bëhet.');
INSERT INTO "hasan_efendi" VALUES(19,36,'19:36','(Isai tha): “Vërtet, Allahu është Zoti im dhe Zoti juaj; andaj adhuroni vetëm Atë! Kjo është udha e drejtë”.');
INSERT INTO "hasan_efendi" VALUES(19,37,'19:37','E megjithatë, sektet u përçanë në lidhje me atë. Mjerë ata që nuk besojnë për takimin e një Dite të madhe (Ditës së Kiametit)!');
INSERT INTO "hasan_efendi" VALUES(19,38,'19:38','E sa mirë do të dëgjojnë e do të shohin atë Ditë, kur do të vijnë te Ne! Por, sot keqbërësit janë në humbje të qartë!');
INSERT INTO "hasan_efendi" VALUES(19,39,'19:39','Paralajmëroji ata (o Muhamed) për Ditën e Pikëllimit, kur do të japin llogari, ndonëse tani ata janë të shkujdesur dhe nuk besojnë.');
INSERT INTO "hasan_efendi" VALUES(19,40,'19:40','Vërtet, jemi Ne që e trashëgojmë Tokën dhe gjithçka që gjendet në të dhe ata te Ne do të kthehen.');
INSERT INTO "hasan_efendi" VALUES(19,41,'19:41','Trego në Libër për Ibrahimin! Ai ishte njeri i së vërtetës dhe profet.');
INSERT INTO "hasan_efendi" VALUES(19,42,'19:42','Ai i tha të atit: “O babai im, përse adhuron atë që as nuk dëgjon, as nuk sheh e as që mund të të sjellë ndonjë dobi?');
INSERT INTO "hasan_efendi" VALUES(19,43,'19:43','O babai im, mua më ka ardhur prej diturisë, çfarë s’të ka ardhur ty; andaj ndiqmë mua dhe unë do të të udhëzoj në rrugën e drejtë!');
INSERT INTO "hasan_efendi" VALUES(19,44,'19:44','O babai im, mos adhuro djallin, sepse ai është gjithmonë i pabindur ndaj të Gjithëmëshirshmit.');
INSERT INTO "hasan_efendi" VALUES(19,45,'19:45','O babai im, unë kam frikë se mos të vjen ndonjë dënim nga i Gjithëmëshirshmi e kështu të bëhesh i afërm i djallit”!');
INSERT INTO "hasan_efendi" VALUES(19,46,'19:46','Ai u përgjigj: “O Ibrahim! Si guxon t’i hedhësh poshtë zotat e mi?! Nëse nuk heq dorë, unë do të të mbys me gurë; andaj, largohu prej meje për një kohë të gjatë”!');
INSERT INTO "hasan_efendi" VALUES(19,47,'19:47','Ibrahimi tha: “Shpëtimi qoftë mbi ty! Do t’i lutem Zotit tim që të të falë. Me të vërtetë, Ai është i mirë me mua.');
INSERT INTO "hasan_efendi" VALUES(19,48,'19:48','Po largohem nga ju dhe nga gjithçka që ju adhuroni në vend të Allahut. Do t’i lutem Zotin tim, sepse jam i sigurt se nuk do të jem fatkeq me lutjet që ia bëj Zotit tim.”');
INSERT INTO "hasan_efendi" VALUES(19,49,'19:49','Dhe, kur u largua nga ata dhe nga idhujt që i adhuronin në vend të Allahut, Ne i dhuruam atij Is’hakun dhe Jakubin dhe që të dy i bëmë profetë.');
INSERT INTO "hasan_efendi" VALUES(19,50,'19:50','Ne u dhuruam atyre mëshirën Tonë dhe bëmë që ata të lavdërohen e të përmenden me nder.');
INSERT INTO "hasan_efendi" VALUES(19,51,'19:51','Trego në Libër edhe për Musain! Njëmend, Ai ishte i përzgjedhur, ishte i dërguar dhe profet.');
INSERT INTO "hasan_efendi" VALUES(19,52,'19:52','Ne e thirrëm atë nga ana e djathtë e malit Tur, duke e afruar në një bisedë të fshehtë');
INSERT INTO "hasan_efendi" VALUES(19,53,'19:53','dhe, nga mëshira Jonë, i dhuruam të vëllanë, Harunin, si profet.');
INSERT INTO "hasan_efendi" VALUES(19,54,'19:54','Trego në Libër për Ismailin! Vërtet, ai ishte i sinqertë në premtimet e veta, ishte i dërguar dhe profet.');
INSERT INTO "hasan_efendi" VALUES(19,55,'19:55','Ai e urdhëronte popullin e vet që të falte namazin e të jepte zekatin dhe ishte i mirëpritur te Zoti i vet.');
INSERT INTO "hasan_efendi" VALUES(19,56,'19:56','Trego në Libër për Idrisin! Ai ishte njeri i së vërtetës dhe profet.');
INSERT INTO "hasan_efendi" VALUES(19,57,'19:57','Ne e ngritëm atë në vend të lartë.');
INSERT INTO "hasan_efendi" VALUES(19,58,'19:58','Këta janë ata, të cilët Allahu i begatoi (me hirin e Tij) nga profetët pasardhës të Ademit, të atyre që Ne i mbartëm me Nuhun, nga pasardhësit e Ibrahimit dhe të Israilit dhe nga ata që Ne i udhëzuam dhe i zgjodhëm. Kur iu lexoheshin vargjet e të Gjithëmëshirshmit, binin duke bërë sexhde dhe qanin.');
INSERT INTO "hasan_efendi" VALUES(19,59,'19:59','Pas atyre erdhën brezni, që e braktisën faljen e namazit dhe ndoqën epshet e veta. Ata do të pësojnë dënimin e madh,');
INSERT INTO "hasan_efendi" VALUES(19,60,'19:60','përveç atyre që pendohen, që besojnë dhe bëjnë punë të mirë. Këta do të hyjnë në Xhenet dhe nuk do t’u bëhet kurrfarë padrejtësie;');
INSERT INTO "hasan_efendi" VALUES(19,61,'19:61','në kopshtet e Adnit, që i Gjithëmëshirshmi ua ka premtuar robërve të Vet, edhe pse ende nuk e kanë parë. Me të vërtetë, premtimi i Tij është i sigurt.');
INSERT INTO "hasan_efendi" VALUES(19,62,'19:62','Aty nuk do të dëgjojnë llomotitje, por vetëm fjalë përshëndetjeje për paqe dhe do të ushqehen në mëngjes dhe në mbrëmje.');
INSERT INTO "hasan_efendi" VALUES(19,63,'19:63','Ky është Xheneti që Ne ua japim në trashëgim robërve Tanë të përkushtuar.');
INSERT INTO "hasan_efendi" VALUES(19,64,'19:64','(Xhebraili tha): “Ne (engjëjt) zbresim vetëm me urdhrin e Zotit tënd. Atij i përket gjithçka që është para nesh, gjithçka që është pas nesh dhe gjithçka që është në mes. Dhe Zoti yt nuk harron kurrë”.');
INSERT INTO "hasan_efendi" VALUES(19,65,'19:65','Ai është Zoti i qiejve, i Tokës dhe i gjithçkaje që është midis tyre, prandaj, vetëm Atë adhuroje dhe bëhu i qëndrueshëm në adhurimin ndaj Atij. A njeh ti ndokënd që ka Emrin e Tij?!');
INSERT INTO "hasan_efendi" VALUES(19,66,'19:66','Njeriu thotë: “Vallë, pasi të vdes, a do të ringjallem përsëri?”');
INSERT INTO "hasan_efendi" VALUES(19,67,'19:67','A nuk e mban mend njeriu se Ne e kemi krijuar atë, kur ende nuk ishte asgjë?!');
INSERT INTO "hasan_efendi" VALUES(19,68,'19:68','Për Zotin tënd! Ne do t’i tubojmë ata dhe djajtë, pastaj do t’i sjellim ata të gjunjëzuar rreth Xhehenemit!');
INSERT INTO "hasan_efendi" VALUES(19,69,'19:69','Pastaj do të ndajmë prej çdo grupi atë që ka qenë më kokëfortë ndaj të Gjithëmëshirshmit.');
INSERT INTO "hasan_efendi" VALUES(19,70,'19:70','Ne pastaj e dimë më së miri se kush prej tyre e ka merituar të digjet në të.');
INSERT INTO "hasan_efendi" VALUES(19,71,'19:71','Dhe nuk ka asnjë prej jush që të mos arrijë tek ai (Xhehenemi). Kjo është vendosur prerazi nga Zoti yt.');
INSERT INTO "hasan_efendi" VALUES(19,72,'19:72','Pastaj do t’i shpëtojmë ata që kanë qenë besimtarë dhe do t’i lëmë keqbërësit të gjunjëzuar në të.');
INSERT INTO "hasan_efendi" VALUES(19,73,'19:73','Kur u lexohen vargjet Tona të qarta, jobesimtarët u thonë atyre që kanë besuar: “Cili grup ka pozitë më të mirë dhe rreth më të mirë?”');
INSERT INTO "hasan_efendi" VALUES(19,74,'19:74','Por sa e sa brezni, që kanë qenë më të begatshëm dhe më të pashëm, Ne i kemi shkatërruar para tyre!');
INSERT INTO "hasan_efendi" VALUES(19,75,'19:75','Thuaj (o Muhamed): “Atyre që janë të humbur, i Gjithëmëshirshmi u dhëntë jetë”, në mënyrë që, kur të shohin atë që u është premtuar: ose dënimin (në këtë jetë) ose Orën (e Kiametit), atëherë ta marrin vesh se kush ka pozitë më të keqe dhe rreth më të dobët.');
INSERT INTO "hasan_efendi" VALUES(19,76,'19:76','Allahu ua shton udhëzimin atyre që janë në rrugë të drejtë. Veprat e mira që mbesin, janë të shpërblyera më mirë te Zoti yt dhe kanë përfundim më të mirë.');
INSERT INTO "hasan_efendi" VALUES(19,77,'19:77','Vallë, a e ke parë atë që nuk beson në shenjat Tona dhe thotë: “Mua do të më jepet (Ditën e Kiametit) edhe pasuria, edhe fëmijët”?!');
INSERT INTO "hasan_efendi" VALUES(19,78,'19:78','A mos ndoshta ai e di të padukshmen apo ka marrë ndonjë premtim nga i Gjithëmëshirshmi?');
INSERT INTO "hasan_efendi" VALUES(19,79,'19:79','Kurrsesi! Ne do ta shënojmë atë që flet dhe do t’ia zgjasim vuajtjen.');
INSERT INTO "hasan_efendi" VALUES(19,80,'19:80','Ne do t’ia marrim atij gjithçka për të cilat flet e ai te Ne do të vijë i vetëm.');
INSERT INTO "hasan_efendi" VALUES(19,81,'19:81','Ata kanë marrë zota, në vend të Allahut, për t’i pasur ndihmës.');
INSERT INTO "hasan_efendi" VALUES(19,82,'19:82','Por jo! Ata do ta mohojnë adhurimin e tyre dhe do të bëhen armiqtë e tyre.');
INSERT INTO "hasan_efendi" VALUES(19,83,'19:83','A nuk e sheh se Ne i kemi dërguar djajtë te mohuesit, për t’i yshtur ata me forcë?');
INSERT INTO "hasan_efendi" VALUES(19,84,'19:84','Andaj, mos kërko shkatërrimin e tyre të shpejtë, se, me të vërtetë, Ne ua numërojmë ditët e caktuara.');
INSERT INTO "hasan_efendi" VALUES(19,85,'19:85','Ditën, kur do t’i tubojmë besimtarët te i Gjithëmëshirshmi, si të ftuar');
INSERT INTO "hasan_efendi" VALUES(19,86,'19:86','e do t’i shtyjmë të ligjtë në Xhehenem, si tufë e etur,');
INSERT INTO "hasan_efendi" VALUES(19,87,'19:87','askush nuk do të mund të ndërhyjë, përveç atij që ka marrë lejen dhe premtimin e të Gjithëmëshirshmit.');
INSERT INTO "hasan_efendi" VALUES(19,88,'19:88','Ata thonë: “I Gjithëmëshirshmi ka një bir”.');
INSERT INTO "hasan_efendi" VALUES(19,89,'19:89','Keni thënë diçka vërtet të tmerrshme!');
INSERT INTO "hasan_efendi" VALUES(19,90,'19:90','Qiejt gati sa nuk copëtohen prej kësaj; Toka gati sa s’çahet e malet sa s’rrëzohen të thërrmuar,');
INSERT INTO "hasan_efendi" VALUES(19,91,'19:91','ngaqë ata i veshin një bir të Gjithëmëshirshmit.');
INSERT INTO "hasan_efendi" VALUES(19,92,'19:92','Kurrë nuk i shkon (madhërisë së) të Gjithëmëshirshmit të ketë një bir.');
INSERT INTO "hasan_efendi" VALUES(19,93,'19:93','S’ka krijesë që gjendet në qiej dhe në Tokë e që të mos vijë si rob para të Gjithëmëshirshmit.');
INSERT INTO "hasan_efendi" VALUES(19,94,'19:94','Sigurisht që Ai e njeh mirë gjithsecilin prej tyre dhe i ka numëruar të gjithë saktësisht.');
INSERT INTO "hasan_efendi" VALUES(19,95,'19:95','Çdonjëri prej tyre, në Ditën e Kiametit, do të dalë fill i vetëm para Tij.');
INSERT INTO "hasan_efendi" VALUES(19,96,'19:96','Në të vërtetë, atyre që besojnë dhe bëjnë vepra të mira, i Gjithëmëshirshmi do t’u dhurojë dashuri.');
INSERT INTO "hasan_efendi" VALUES(19,97,'19:97','Ne e kemi bërë atë (Kuranin) të lehtë, në gjuhën tënde, vetëm që, nëpërmjet tij, ti t’u japësh lajmin e mirë besimtarëve të devotshëm dhe të paralajmërosh njerëzit kokëfortë (në mosbesimin e tyre).');
INSERT INTO "hasan_efendi" VALUES(19,98,'19:98','Sa e sa brezni kemi shkatërruar Ne para tyre! Vallë, a po sheh ndonjërin prej tyre dhe a po dëgjon pëshpëritjen më të vogël të tyre?!');
INSERT INTO "hasan_efendi" VALUES(20,1,'20:1','Tâ, Hâ.');
INSERT INTO "hasan_efendi" VALUES(20,2,'20:2','Ne nuk ta kemi shpallur Kuranin ty (o Muhamed), për të të munduar,');
INSERT INTO "hasan_efendi" VALUES(20,3,'20:3','por që të jetë kujtesë për atë që ka frikë (nga Allahu).');
INSERT INTO "hasan_efendi" VALUES(20,4,'20:4','Ai është zbritur nga Krijuesi i Tokës e i qiejve të lartë.');
INSERT INTO "hasan_efendi" VALUES(20,5,'20:5','I Gjithëmëshirshmi mbi Fron u ngrit (si i takon Madhërisë së Tij).');
INSERT INTO "hasan_efendi" VALUES(20,6,'20:6','Atij i përket ç’ka në qiej e ç’ka në Tokë, si dhe gjithçka që gjendet midis tyre e gjithçka që ndodhet nën tokë.');
INSERT INTO "hasan_efendi" VALUES(20,7,'20:7','Edhe nëse ti flet me zë të lartë, (është e kotë, sepse) Ai, me të vërtetë, i di sekretet edhe më të fshehta.');
INSERT INTO "hasan_efendi" VALUES(20,8,'20:8','Allahu, nuk ka zot tjetër përveç Tij. Ai ka Emrat më të Bukur.');
INSERT INTO "hasan_efendi" VALUES(20,9,'20:9','A e ke dëgjuar historinë e Musait?');
INSERT INTO "hasan_efendi" VALUES(20,10,'20:10','Kur pa një zjarr, ai i tha familjes së vet: “Rrini këtu, sepse pashë një zjarr! Ndoshta mund t’ju sjell një urë të ndezur ose të gjej pranë tij ndonjë udhërrëfyes”.');
INSERT INTO "hasan_efendi" VALUES(20,11,'20:11','Kur arriti te zjarri, një zë e thirri: “O Musa!');
INSERT INTO "hasan_efendi" VALUES(20,12,'20:12','Në të vërtetë, Unë jam Zoti yt! Prandaj, hiqi nallanet, sepse ndodhesh në luginën e shenjtë të Tuvasë.');
INSERT INTO "hasan_efendi" VALUES(20,13,'20:13','Unë të kam zgjedhur ty, prandaj dëgjo atë që po të shpallet!');
INSERT INTO "hasan_efendi" VALUES(20,14,'20:14','Me të vërtetë, Unë jam Allahu, s’ka zot tjetër përveç Meje, prandaj, vetëm Mua më adhuro dhe kryej faljen për të më kujtuar Mua!');
INSERT INTO "hasan_efendi" VALUES(20,15,'20:15','Ora (e Kiametit) do të vijë me siguri, por Unë e mbaj tepër të fshehur, që secili të shpërblehet për atë që ka bërë.');
INSERT INTO "hasan_efendi" VALUES(20,16,'20:16','Prandaj, mos e lër atë që nuk beson në të (Ditën e Kiametit) dhe që jepet pas epsheve të veta të të nxjerrë (nga rruga e drejtë), përndryshe do të humbisje.');
INSERT INTO "hasan_efendi" VALUES(20,17,'20:17','Ç’është ajo që mban në dorën e djathtë o Musa?”');
INSERT INTO "hasan_efendi" VALUES(20,18,'20:18','Ai u përgjigj: “Ky është shkopi im, me të cilin mbahem dhe shkund gjethe për dhentë e mia; më shërben edhe për punë të tjera.”');
INSERT INTO "hasan_efendi" VALUES(20,19,'20:19','(Allahu) i tha: “Hidhe atë, Musa!”');
INSERT INTO "hasan_efendi" VALUES(20,20,'20:20','Ai e hodhi atë dhe, menjëherë, (shkopi) u bë gjarpër që zvarritej.');
INSERT INTO "hasan_efendi" VALUES(20,21,'20:21','(Allahu) i tha: “Merre atë dhe mos u frikëso! Ne do ta kthejmë në gjendjen e tij të mëparshme.');
INSERT INTO "hasan_efendi" VALUES(20,22,'20:22','Fute dorën tënde nën sqetull: ajo do të dalë e bardhë, pa kurrfarë sëmundjeje. Ja, kjo është një shenjë tjetër,');
INSERT INTO "hasan_efendi" VALUES(20,23,'20:23','për të të treguar ty të tjera mrekulli Tonat edhe më të mëdha!');
INSERT INTO "hasan_efendi" VALUES(20,24,'20:24','Shko te Faraoni! Ai, me të vërtetë, i ka shkelur të gjithë kufijtë”.');
INSERT INTO "hasan_efendi" VALUES(20,25,'20:25','(Musai) tha: “O Zoti im, zgjeroma kraharorin tim,');
INSERT INTO "hasan_efendi" VALUES(20,26,'20:26','lehtësoma punën time,');
INSERT INTO "hasan_efendi" VALUES(20,27,'20:27','dhe zgjidhma nyjën e gjuhës sime,');
INSERT INTO "hasan_efendi" VALUES(20,28,'20:28','që ata të kuptojnë fjalën time!');
INSERT INTO "hasan_efendi" VALUES(20,29,'20:29','Më jep një ndihmës nga familja ime,');
INSERT INTO "hasan_efendi" VALUES(20,30,'20:30','Harunin, vëllanë tim!');
INSERT INTO "hasan_efendi" VALUES(20,31,'20:31','Ma rrit fuqinë nëpërmjet atij');
INSERT INTO "hasan_efendi" VALUES(20,32,'20:32','dhe bëma ortak në punën time,');
INSERT INTO "hasan_efendi" VALUES(20,33,'20:33','që të thurim shumë lavde për Ty');
INSERT INTO "hasan_efendi" VALUES(20,34,'20:34','e të të përmendim Ty vazhdimisht!');
INSERT INTO "hasan_efendi" VALUES(20,35,'20:35','Vërtet, Ti do të jesh gjithnjë Mbikëqyrësi ynë”.');
INSERT INTO "hasan_efendi" VALUES(20,36,'20:36','(Allahu) tha: “Kërkesa jote u plotësua, o Musa!');
INSERT INTO "hasan_efendi" VALUES(20,37,'20:37','Edhe më parë, Ne të kemi bërë mirë ty,');
INSERT INTO "hasan_efendi" VALUES(20,38,'20:38','kur i frymëzuam nënës tënde atë që iu frymëzua:');
INSERT INTO "hasan_efendi" VALUES(20,39,'20:39','“Vëre atë (foshnjën) në një sënduk e hidhe në lumë! Lumi do ta nxjerrë në breg e atë do ta marrë një armik Imi dhe armik i tij.” Unë kam hedhur në ty dashurinë Time, që të rritesh nën Syrin (kujdesin) Tim.');
INSERT INTO "hasan_efendi" VALUES(20,40,'20:40','Kur shkoi motra jote e tha: “A doni t’ju tregoj dikë që do të kujdeset për atë?”, Ne të kthyem te nëna jote, që ajo të gëzohej dhe të mos pikëllohej më. Pastaj ti vrave një njeri, por Ne të shpëtuam nga brenga dhe të vumë në sprova të shumta. Ti qëndrove me vite të tëra midis banorëve të Medjenit, pastaj erdhe sipas porosisë së caktuar, o Musa!');
INSERT INTO "hasan_efendi" VALUES(20,41,'20:41','Unë të kam zgjedhur ty për Vete.');
INSERT INTO "hasan_efendi" VALUES(20,42,'20:42','Shkoni ti dhe vëllai yt me mrekullitë e Mia dhe mos reshtni së përmenduri Mua!');
INSERT INTO "hasan_efendi" VALUES(20,43,'20:43','Shkoni te Faraoni! Ai, me të vërtetë, i ka shkelur të gjithë kufijtë!');
INSERT INTO "hasan_efendi" VALUES(20,44,'20:44','Flitini atij fjalë të buta, se ndoshta kujtohet a frikësohet!”');
INSERT INTO "hasan_efendi" VALUES(20,45,'20:45','Ata thanë: “O Zoti ynë! Ne kemi frikë se ai do të na dënojë menjëherë ose do të ndërkrehet më tepër”.');
INSERT INTO "hasan_efendi" VALUES(20,46,'20:46','(Allahu) tha: “Mos u frikësoni! Në të vërtetë, Unë jam me ju, dëgjoj dhe shoh.');
INSERT INTO "hasan_efendi" VALUES(20,47,'20:47','Shkoni tek ai dhe i thoni: “Ne të dy jemi të dërguarit e Zotit tënd, lëshoji të bijtë e Israilit që të vijnë me ne dhe mos i mundo! Ne të kemi sjellë ty një shenjë nga Zoti yt. Paqe mbi atë që ndjek rrugën e drejtë!');
INSERT INTO "hasan_efendi" VALUES(20,48,'20:48','Vërtet, neve na është shpallur se dënimi do të godasë këdo, që (i) mohon (shenjat e Allahut) dhe (i) kthen shpinën (të dërguarve të Tij)”.');
INSERT INTO "hasan_efendi" VALUES(20,49,'20:49','(Faraoni) tha: “E, kush është Zoti juaj, o Musa?”');
INSERT INTO "hasan_efendi" VALUES(20,50,'20:50','(Musai) tha: “Zoti ynë është Ai që çdo gjëje i ka dhënë trajtën e vet e pastaj e ka udhëzuar”.');
INSERT INTO "hasan_efendi" VALUES(20,51,'20:51','(Faraoni) tha: “Si është gjendja e brezave të mëparshëm”?');
INSERT INTO "hasan_efendi" VALUES(20,52,'20:52','(Musai) tha: “Dijenia lidhur me ta gjendet te Zoti im, në Libër. Zoti im nuk gabon dhe nuk harron!”');
INSERT INTO "hasan_efendi" VALUES(20,53,'20:53','Është Ai që Tokën e ka bërë shtrat për ju, që ju ka shtruar rrugët nëpër të dhe që lëshon ujë nga qielli, me qëllim që, nëpërmjet tij, të rriten shumë lloje bimësh të ndryshme.');
INSERT INTO "hasan_efendi" VALUES(20,54,'20:54','Hani dhe kullotni bagëtinë tuaj! Këto, me të vërtetë, janë shenja për ata që janë mendarë.');
INSERT INTO "hasan_efendi" VALUES(20,55,'20:55','Ne ju kemi krijuar prej tokës, aty do t’ju kthejmë dhe nga ajo do t’ju nxjerrim edhe një herë tjetër.');
INSERT INTO "hasan_efendi" VALUES(20,56,'20:56','Ne ia treguam atij (Faraonit) të gjitha shenjat Tona, por ai i mohoi, nuk pranoi (të besonte)');
INSERT INTO "hasan_efendi" VALUES(20,57,'20:57','dhe tha: “O Musa, a ke ardhur që të na nxjerrësh nga toka jonë me magjinë tënde?!');
INSERT INTO "hasan_efendi" VALUES(20,58,'20:58','Ne do të të sjellim ty një magji të ngjashme. Cakto, midis nesh dhe teje, një takim në një vend të sheshtë, ku nuk do të mungojmë as ne, as ti!”');
INSERT INTO "hasan_efendi" VALUES(20,59,'20:59','(Musai) tha: “Takimi me ju është në ditën e kremtes dhe njerëzia le të tubohen paradite”.');
INSERT INTO "hasan_efendi" VALUES(20,60,'20:60','Faraoni u largua, i mblodhi magjistarët e vet e pastaj erdhi.');
INSERT INTO "hasan_efendi" VALUES(20,61,'20:61','Musai u tha atyre: “Të mjerët ju! Mos trilloni gënjeshtra për Allahun, duke bërë që Ai t’ju shkatërrojë me dënim. Do të dështojnë me siguri, ata që trillojnë gënjeshtra!”');
INSERT INTO "hasan_efendi" VALUES(20,62,'20:62','Ata diskutuan me njëri-tjetrin për punën e tyre, duke pëshpëritur fshehurazi.');
INSERT INTO "hasan_efendi" VALUES(20,63,'20:63','Njëri prej tyre tha: “Në të vërtetë, këta (Musai dhe Haruni), janë dy magjistarë, që duan t’ju shporrin nga toka juaj me magjitë e tyre dhe të shkatërrojnë fenë tuaj tejet të mirë.');
INSERT INTO "hasan_efendi" VALUES(20,64,'20:64','Prandaj, bashkojini magjitë tuaja dhe ejani me radhë. Do të fitojë sot ai që ka epërsi!”');
INSERT INTO "hasan_efendi" VALUES(20,65,'20:65','Ata thanë: “O Musa, ose hidh ti, ose të hedhim ne të parët”.');
INSERT INTO "hasan_efendi" VALUES(20,66,'20:66','Ai tha: “Jo, hidhni ju!” Dhe ja tek i dukeshin atij litarët dhe shkopinjtë e tyre se po rendnin vërtet, për shkak të magjisë së tyre.');
INSERT INTO "hasan_efendi" VALUES(20,67,'20:67','Atëherë Musai ndjeu frikë në vetvete.');
INSERT INTO "hasan_efendi" VALUES(20,68,'20:68','Ne i thamë: “Mos u frikëso, se, në të vërtetë, ti do të fitosh!');
INSERT INTO "hasan_efendi" VALUES(20,69,'20:69','Hidhe atë që mban në dorën e djathtë: do të gëlltisë gjithçka që kanë bërë ata, sepse ajo që kanë bërë ata, është vetëm një mashtrim i magjistarëve; e, magjistari nuk do të ketë sukses kudo që të gjendet”.');
INSERT INTO "hasan_efendi" VALUES(20,70,'20:70','Atëherë magjistarët ranë në sexhde dhe thanë: “Ne besojmë në Zotin e Harunit dhe Musait”.');
INSERT INTO "hasan_efendi" VALUES(20,71,'20:71','(Faraoni) tha: “Çfarë?! Ju e besuat atë, para se t’ju jap leje unë?! Në të vërtetë, ai është mjeshtri juaj, që ju ka mësuar magjitë. Unë do t’jua këpus duart dhe këmbët tuaja të këmbyera dhe do t’ju var në trungjet e hurmave. Ju do ta merrni vesh, me siguri, se cili prej nesh është më i ashpër dhe më ngulmues në ndëshkim.”');
INSERT INTO "hasan_efendi" VALUES(20,72,'20:72','(Magjistarët) u përgjigjën: “Ne kurrë nuk do të të japim ty përparësi ndaj provave që na kanë ardhur neve dhe ndaj Atij që na ka krijuar ne. Prandaj, gjyko si të duash; ti gjykon vetëm në jetën e kësaj bote!');
INSERT INTO "hasan_efendi" VALUES(20,73,'20:73','Ne besojmë në Zotin tonë, që të na i falë gabimet dhe magjitë, për të cilat na ke detyruar ti. Allahu është më i Miri dhe i Përjetshmi.');
INSERT INTO "hasan_efendi" VALUES(20,74,'20:74','Atë që i paraqitet Zotit të vet si keqbërës, sigurisht që e pret Xhehenemi, ku as nuk do të vdesë e as nuk do të jetojë.');
INSERT INTO "hasan_efendi" VALUES(20,75,'20:75','Ndërsa kush paraqitet tek Ai si besimtar e që ka bërë vepra të mira, do të ngrihet në shkallë të larta,');
INSERT INTO "hasan_efendi" VALUES(20,76,'20:76','në kopshtet e Adnit, nëpër të cilat rrjedhin lumenj. Ata do të qëndrojnë aty përjetësisht. Ky është shpërblimi për atë që është i dëlirë (nga mosbesimi).”');
INSERT INTO "hasan_efendi" VALUES(20,77,'20:77','Ne i thamë Musait: “Udhëto natën me robërit e Mi, hapu atyre një rrugë të tharë nëpër det dhe mos u frikëso se do të të kapë ndokush e as se do të mbyteni!”');
INSERT INTO "hasan_efendi" VALUES(20,78,'20:78','Faraoni i ndoqi ata (bijtë e Israilit) me ushtrinë e tij, por deti i mbuloi ata me dallgë të mëdha.');
INSERT INTO "hasan_efendi" VALUES(20,79,'20:79','Faraoni i solli humbje popullit të vet dhe nuk e çoi në rrugë të drejtë.');
INSERT INTO "hasan_efendi" VALUES(20,80,'20:80','O bijtë e Israilit, Ne ju shpëtuam nga armiku juaj, bëmë besëlidhje me ju në anën e djathtë të Turit dhe ju zbritëm manë dhe thëllëza.');
INSERT INTO "hasan_efendi" VALUES(20,81,'20:81','Hani nga ushqimet e shijshme e të lejuara me të cilat ju kemi furnizuar dhe mos e kapërceni kufirin, përndryshe do t’ju zërë zemërimi Im. Ai, të cilin e zë zemërimi Im, është i humbur!');
INSERT INTO "hasan_efendi" VALUES(20,82,'20:82','Vërtet, Unë jam Falës për atë që pendohet, beson e bën vepra të mira dhe pastaj qëndron në rrugë të drejtë.');
INSERT INTO "hasan_efendi" VALUES(20,83,'20:83','“Përse nxitove para popullit tënd, o Musa?”');
INSERT INTO "hasan_efendi" VALUES(20,84,'20:84','(Musai) tha: “Ja, ata shkojnë pas gjurmëve të mia e unë nxitova te Ti, o Zoti im, për të fituar kënaqësinë Tënde”.');
INSERT INTO "hasan_efendi" VALUES(20,85,'20:85','(Allahu) tha: “Ne e vumë në provë popullin tënd gjatë mungesës tënde, e Samiriu i nxori ata nga rruga e drejtë”.');
INSERT INTO "hasan_efendi" VALUES(20,86,'20:86','Musai u kthye te populli i vet i zemëruar dhe i pikëlluar. Tha: “O populli im, vallë, a nuk ju dha Zoti juaj një premtim të mirë? A mos ju duk e gjatë koha (e përmbushjes së premtimit) apo dëshironi që t’ju godasë zemërimi i Zotit tuaj?! Prandaj nuk i jeni përmbajtur premtimit që më dhatë?!”');
INSERT INTO "hasan_efendi" VALUES(20,87,'20:87','Ata u përgjigjën: “Ne nuk e kemi thyer premtimin që të patëm dhënë me vullnetin tonë, por qemë të ngarkuar rëndë me stolitë e popullit (të Faraonit), andaj i hodhëm ato (në zjarr për t’u lehtësuar). Po kështu i hodhi edhe Samiriu,');
INSERT INTO "hasan_efendi" VALUES(20,88,'20:88','dhe nga këto ai bëri një trup viçi që pëlliste. Atëherë tha: “Ky është zoti juaj dhe zoti Musait. Ai (Musai) ka harruar (t’ju tregojë për të)!”');
INSERT INTO "hasan_efendi" VALUES(20,89,'20:89','Vallë, a nuk e shihnin ata, se (viçi) nuk u drejtonte atyre asnjë fjalë dhe nuk ishte në gjendje t’u sillte kurrfarë dëmi apo dobie?');
INSERT INTO "hasan_efendi" VALUES(20,90,'20:90','Ndërkohë, Haruni u kishte thënë atyre qysh më parë: “O populli im! Kjo është vetëm një provë për ju! Zoti juaj është i Gjithëmëshirshmi, andaj ndiqmëni mua dhe dëgjoni urdhrin tim!”');
INSERT INTO "hasan_efendi" VALUES(20,91,'20:91','Ata u përgjigjën: “Ne do t’i lutemi atij, derisa të na kthehet Musai”.');
INSERT INTO "hasan_efendi" VALUES(20,92,'20:92','(Musai) tha: “O Harun, ç’të pengoi ty, kur i pe ata se kishin humbur,');
INSERT INTO "hasan_efendi" VALUES(20,93,'20:93','që të vije tek unë?! A mos e kundërshtove urdhrin tim?”');
INSERT INTO "hasan_efendi" VALUES(20,94,'20:94','(Haruni) tha: “O biri i nënës sime, mos më kap për mjekre e as për koke! Unë kisha frikë se do të thoshe: Ke mbjellë përçarje midis bijve të Israilit e nuk i ke mbajtur porositë e mia”.');
INSERT INTO "hasan_efendi" VALUES(20,95,'20:95','(Musai) tha: “Si është puna jote, o Samiri?”');
INSERT INTO "hasan_efendi" VALUES(20,96,'20:96','(Samiriu) u përgjigj: “Unë pashë atë, që ata nuk e panë. Mora një grusht nga gjurmët e të Dërguarit dhe atë e hodha mbi viçin dhe kështu më nxiti vetja (e keqja) ime”. ');
INSERT INTO "hasan_efendi" VALUES(20,97,'20:97','(Musai) tha: “Atëherë ik! Tërë jetën do të të duhet të thuash: ‘Mos më prekni’. Por të është caktuar një premtim, i cili doemos do të zbatohet. Shikoje zotin tënd që ti e ke adhuruar vazhdimisht: Ne do ta djegim e pastaj do ta hedhim në det hirin e tij.');
INSERT INTO "hasan_efendi" VALUES(20,98,'20:98','Në të vërtetë, Zoti juaj është vetëm Allahu; s’ka zot tjetër përveç Tij; dituria e Tij përfshin çdo gjë”.');
INSERT INTO "hasan_efendi" VALUES(20,99,'20:99','Kështu pra, Ne po të të tregojmë ty (o Muhamed) disa histori të së kaluarës. Ky (Kuran) që të kemi dhënë, është një Këshillë nga ana Jonë.');
INSERT INTO "hasan_efendi" VALUES(20,100,'20:100','Kush shmanget nga ai, në Ditën e Kiametit do të mbartë ngarkesë të rëndë,');
INSERT INTO "hasan_efendi" VALUES(20,101,'20:101','që do t’u mbetet përherë. Sa barrë e keqe që do të jetë ajo në Ditën e Kiametit!');
INSERT INTO "hasan_efendi" VALUES(20,102,'20:102','Ditën, kur do të fryhet në Sur, pikërisht atë Ditë do t’i tubojmë të ligjtë me sy të zgurdulluar,');
INSERT INTO "hasan_efendi" VALUES(20,103,'20:103','duke i pëshpëritur njëri-tjetrit: “Ju keni qëndruar (në dynja) vetëm dhjetë ditë.”');
INSERT INTO "hasan_efendi" VALUES(20,104,'20:104','Ne e dimë më mirë se ç’do të thonë ata (në Ditën e Kiametit), kur më i mençuri prej tyre do të thotë: “Ju keni qëndruar vetëm një ditë.”');
INSERT INTO "hasan_efendi" VALUES(20,105,'20:105','Kur të të pyesin ty (o Muhamed) për malet, thuaj: “Ato do t’i bëjë hi e pluhur Zoti im,');
INSERT INTO "hasan_efendi" VALUES(20,106,'20:106','ndërsa toka do të bëhet fushë e shkretë,');
INSERT INTO "hasan_efendi" VALUES(20,107,'20:107','nuk do të shohësh në të as lugina, as brigje.”');
INSERT INTO "hasan_efendi" VALUES(20,108,'20:108','Atë ditë, ata do të shkojnë pas thirrësit, pa iu shmangur dot atij dhe do t’i ulin zërat para të Gjithëmëshirshmit. Ti nuk do të dëgjosh tjetër, veç pëshpëritjes.');
INSERT INTO "hasan_efendi" VALUES(20,109,'20:109','Atë ditë nuk do të bëjë dobi ndërmjetësimi i askujt, përveç ndërmjetësimit të atij, që i jep leje i Gjithëmëshirshmi dhe që i është pranuar fjala.');
INSERT INTO "hasan_efendi" VALUES(20,110,'20:110','Ai e di ç’ka para tyre dhe ç’ka pas tyre, kurse ata nuk mund ta ngërthejnë dijeninë e Allahut.');
INSERT INTO "hasan_efendi" VALUES(20,111,'20:111','Fytyrat do t’i përulen të Gjallit, të Përjetshmit, Mbajtësit të çdo gjëje dhe, sigurisht, do të humbë ai që do të mbartë padrejtësi.');
INSERT INTO "hasan_efendi" VALUES(20,112,'20:112','Ndërsa ai që bën vepra të mira dhe është besimtar, nuk do të druajë nga padrejtësia, as nga pakësimi i asaj që i përket.');
INSERT INTO "hasan_efendi" VALUES(20,113,'20:113','Dhe kështu, Ne e kemi zbritur Kuranin në gjuhën arabe dhe i kemi shpjeguar paralajmërimet, që ata t’ua vënë veshin.');
INSERT INTO "hasan_efendi" VALUES(20,114,'20:114','I Lartësuar qoftë Allahu, Sunduesi i Vërtetë! Mos nxito për ta lexuar Kuranin para se të përfundojë shpallja e tij dhe thuaj: “O Zoti im, shtoma dijeninë!” ');
INSERT INTO "hasan_efendi" VALUES(20,115,'20:115','Ne bëmë besëlidhje edhe me Ademin më parë, por ai harroi, sepse nuk ishte i vendosur.');
INSERT INTO "hasan_efendi" VALUES(20,116,'20:116','Kur u thamë engjëjve: “Përuluni në sexhde para Ademit”, të gjithë u përulën, përveç Iblisit, i cili nuk pranoi.');
INSERT INTO "hasan_efendi" VALUES(20,117,'20:117','Pastaj thamë: “O Adem, me të vërtetë që ky është armiku yt dhe i gruas sate, prandaj mos e lini t’ju nxjerrë kurrsesi nga Xheneti, se pastaj do të vuani në mjerim!');
INSERT INTO "hasan_efendi" VALUES(20,118,'20:118','Këtu nuk do të mbetesh kurrë as i uritur e as i zhveshur,');
INSERT INTO "hasan_efendi" VALUES(20,119,'20:119','dhe nuk do të kesh as etje, as vapë.”');
INSERT INTO "hasan_efendi" VALUES(20,120,'20:120','Por djalli e cyti: “O Adem, a do të të tregoj pemën e përjetësisë dhe të një mbretërimi që nuk zhduket?”');
INSERT INTO "hasan_efendi" VALUES(20,121,'20:121','Kështu, ata të dy, (Ademi dhe Havaja), hëngrën nga ajo pemë e u ndërgjegjësuan për lakuriqësinë e tyre, prandaj nisën të mbulohen me gjethet e Xhenetit. Ademi nuk e zbatoi urdhrin e Zotit të vet, kështu që devijoi (nga rruga e drejtë).');
INSERT INTO "hasan_efendi" VALUES(20,122,'20:122','Pastaj, Zoti i tij e zgjodhi atë, ia pranoi pendimin, e udhëzoi');
INSERT INTO "hasan_efendi" VALUES(20,123,'20:123','dhe tha: “Zbritni prej Xhenetit që të dy (Ademi dhe djalli)! Do të jeni armiq të njëri-tjetrit. Kur t’ju vijë udhëzim nga ana Ime, kush do ta pasojë udhëzimin Tim, as nuk do të humbë, as nuk do të bjerë në mjerim.');
INSERT INTO "hasan_efendi" VALUES(20,124,'20:124','Kushdo që i kthen shpinën Këshillës Sime, do të ketë jetë të mjeruar dhe Ne, në Ditën e Kiametit, do ta ringjallim të verbër.');
INSERT INTO "hasan_efendi" VALUES(20,125,'20:125','Ai do të thotë: “O Zoti im, përse më ringjalle të verbër, kur unë kisha shikuar më parë?”');
INSERT INTO "hasan_efendi" VALUES(20,126,'20:126','(Allahu) do t’i thotë: “Kështu të erdhën shenjat Tona dhe ti i harrove ato e po kështu sot do të jesh i harruar.');
INSERT INTO "hasan_efendi" VALUES(20,127,'20:127','Kështu e ndëshkojmë Ne atë që e tepron (me gjynahe) dhe nuk beson shenjat e Zotit të tij; e dënimi në jetën tjetër, në të vërtetë, është më i ashpër dhe më i qëndrueshëm.');
INSERT INTO "hasan_efendi" VALUES(20,128,'20:128','A nuk e kanë marrë vesh ata se sa e sa brezni Ne i kemi zhdukur para atyre, aty ku ecin nëpër vendet e tyre sot? Me të vërtetë, në këtë ka tregues për njerëzit e mençur.');
INSERT INTO "hasan_efendi" VALUES(20,129,'20:129','Sikur të mos ishte një fjalë e mëparshme nga Zoti yt dhe një afat tashmë i caktuar, dënimi i tyre do të ishte shpejtuar.');
INSERT INTO "hasan_efendi" VALUES(20,130,'20:130','Prandaj duroje (o Muhamed) atë që thonë ata dhe lavdëroje me falënderim Zotin tënd para lindjes së diellit dhe para perëndimit të tij! Lavdëroje Atë edhe në orët e natës, edhe në skajet e ditës, që të mund të kënaqesh. ');
INSERT INTO "hasan_efendi" VALUES(20,131,'20:131','Mos e hidh shikimin në të mirat kalimtare, që Ne ua kemi dhënë disave prej tyre, si stoli të jetës së kësaj bote, për t’i vënë ata në provë! Shpërblimi i Zotit tënd është më i mirë dhe më i qëndrueshëm.');
INSERT INTO "hasan_efendi" VALUES(20,132,'20:132','Urdhëroje familjen tënde, që të falë namazin dhe këmbëngul për këtë! Ne nuk të kërkojmë ty ndonjë ushqim: jemi Ne që të ushqejmë! Përfundimi i lumtur qëndron në frikën ndaj Allahut.');
INSERT INTO "hasan_efendi" VALUES(20,133,'20:133','Ata thonë: “Përse të mos na sjellë ai ndonjë shenjë nga Zoti i tij”? A nuk u erdhi atyre sqarimi për atë që gjendej në shkrimet e lashta?!');
INSERT INTO "hasan_efendi" VALUES(20,134,'20:134','Sikur Ne t’i kishim shkatërruar ata para kësaj, ata, me siguri, do të thoshin: “O Zoti ynë! Sikur të na kishe sjellë një të Dërguar, vërtet që do të kishim ndjekur shpalljet Tuaja, para se të poshtëroheshim dhe turpëroheshim!”');
INSERT INTO "hasan_efendi" VALUES(20,135,'20:135','Thuaju (o Muhamed): “Të gjithë presin, andaj prisni edhe ju, dhe do ta kuptoni se kush janë ithtarët e rrugës së drejtë dhe kush është i udhëzuar drejt.”');
INSERT INTO "hasan_efendi" VALUES(21,1,'21:1','Po u afrohet njerëzve dita për të dhënë llogari, por ata vazhdojnë të jenë të shkujdesur e të shmangur (nga e vërteta).');
INSERT INTO "hasan_efendi" VALUES(21,2,'21:2','Sa herë që atyre u vjen nga Zoti i tyre ndonjë paralajmërim i ri, ata e dëgjojnë, duke u tallur me të.');
INSERT INTO "hasan_efendi" VALUES(21,3,'21:3','Zemrat e tyre janë të hutuara. Ndërsa ata që janë idhujtarë, flasin fshehurazi: “A është ky (Muhamedi) diçka tjetër, përveçse njeri si ju? Vallë, a do të shkoni ju pas magjisë (së tij), kur e shikoni me sytë tuaj?');
INSERT INTO "hasan_efendi" VALUES(21,4,'21:4','(I Dërguari) tha: “Zoti im e di se ç’flitet qoftë në qiell, qoftë në Tokë, Ai dëgjon gjithçka dhe është i Gjithëdijshëm.”');
INSERT INTO "hasan_efendi" VALUES(21,5,'21:5','Ata thonë: “Këto janë vetëm ëndrra të turbullta! Ose ai i ka trilluar ato. Madje ai është poet. Le të na sjellë një mrekulli, ashtu si i kanë sjellë të dërguarit e parë!”');
INSERT INTO "hasan_efendi" VALUES(21,6,'21:6','Asnjë vendbanim nga ata që i kemi shkatërruar, nuk ka besuar e vallë, a do të besojnë këta?');
INSERT INTO "hasan_efendi" VALUES(21,7,'21:7','Edhe para teje kemi dërguar vetëm njerëz, të cilëve, u kemi shpallur; andaj, pyetni njerëzit e shkrimeve (të mëparshme), nëse ju nuk dini!');
INSERT INTO "hasan_efendi" VALUES(21,8,'21:8','Dhe ne nuk i kemi bërë ata (të dërguarit) trupa që nuk hanë ushqim, as nuk kanë qenë të pavdekshëm.');
INSERT INTO "hasan_efendi" VALUES(21,9,'21:9','Pastaj, ua kemi përmbushur atyre premtimin e i kemi shpëtuar së bashku me njerëzit (besimtarë) që kemi dashur; ndërsa i kemi shkatërruar ata që e kanë shkelur kufirin (mohuesit).');
INSERT INTO "hasan_efendi" VALUES(21,10,'21:10','Ne të kemi shpallur Librin, i cili është përkujtesë për ju. Vallë, a nuk po kuptoni?');
INSERT INTO "hasan_efendi" VALUES(21,11,'21:11','Sa e sa vendbanime, banorët e të cilëve kanë qenë mohues, i kemi shkatërruar; e pas tyre kemi ngritur popuj të tjerë!');
INSERT INTO "hasan_efendi" VALUES(21,12,'21:12','Kur e ndienin dënimin Tonë, ata iknin me të shpejtë.');
INSERT INTO "hasan_efendi" VALUES(21,13,'21:13','Mos ikni! Kthehuni atje, në luksin tuaj dhe në pallatet tuaja, se ju do të pyeteni.”');
INSERT INTO "hasan_efendi" VALUES(21,14,'21:14','Ata do të thonë: “Mjerë ne, me të vërtetë, kemi qenë keqbërës!”');
INSERT INTO "hasan_efendi" VALUES(21,15,'21:15','Këto rënkime të tyre nuk pushuan, derisa Ne i palosëm si drithë i korrur.');
INSERT INTO "hasan_efendi" VALUES(21,16,'21:16','Ne nuk e kemi krijuar qiellin dhe Tokën e ç’gjendet në mes tyre, për të luajtur (pa qëllim).');
INSERT INTO "hasan_efendi" VALUES(21,17,'21:17','Sikur të donim argëtim, Ne do ta bënim me gjërat pranë Nesh (si engjëjt, hyritë etj.), nëse do të bënim diçka të tillë.');
INSERT INTO "hasan_efendi" VALUES(21,18,'21:18','Por Ne e godasim të pavërtetën, me të vërtetën, e cila e shkatërron atë dhe ajo zhduket. Dhe, mjerë për ju, për atë që i vishni (Allahut!)');
INSERT INTO "hasan_efendi" VALUES(21,19,'21:19','Atij i përket gjithkush që gjendet në qiej dhe në Tokë. Ata që janë tek Ai (engjëjt), nuk ngurrojnë për të adhuruar Atë e nuk ndiejnë lodhje,');
INSERT INTO "hasan_efendi" VALUES(21,20,'21:20','por e lavdërojnë (Atë) natën dhe ditën, paprerë.');
INSERT INTO "hasan_efendi" VALUES(21,21,'21:21','Vallë, a mos i kanë marrë zotat prej dheu, që të ishin në gjendje t’i ngjallnin të vdekurit?');
INSERT INTO "hasan_efendi" VALUES(21,22,'21:22','Sikur në qiej dhe në Tokë të kishte zota të tjerë, përveç Allahut, si qiejt, ashtu edhe Toka do të shkatërroheshin. Qoftë i lavdëruar Allahu, Zoti i Fronit dhe i lartësuar mbi çka ia veshin Atij!');
INSERT INTO "hasan_efendi" VALUES(21,23,'21:23','Ai nuk mund të pyetet për atë që punon, kurse ata do të pyeten.');
INSERT INTO "hasan_efendi" VALUES(21,24,'21:24','Vallë, a adhurojnë ata të tjerë zota, në vend të Atij?! Thuaj: “Sillni provën tuaj!” Ky është mesazhi për ata që janë me mua dhe i atyre që kanë qenë para meje, por shumica e tyre nuk e di të Vërtetën dhe shmangen nga ajo”.');
INSERT INTO "hasan_efendi" VALUES(21,25,'21:25','Ne nuk kemi nisur asnjë të dërguar para teje, që të mos i kemi shpallur se: “S’ka zot tjetër përveç Meje, andaj më adhuroni (vetëm) Mua!”');
INSERT INTO "hasan_efendi" VALUES(21,26,'21:26','Ata thonë: “I Gjithëmëshirshmi ka për Vete një bir (prej engjëjve)”. Qoftë i lavdëruar Ai dhe i lartësuar! Jo, ata (engjëjt) janë robër të nderuar.');
INSERT INTO "hasan_efendi" VALUES(21,27,'21:27','Ata nuk flasin para (Fjalës së) Tij dhe veprojnë me Urdhrin e Tij.');
INSERT INTO "hasan_efendi" VALUES(21,28,'21:28','Ai e di se ç’ka para tyre (ç’kanë vepruar) dhe ç’ka pas tyre (çfarë do të veprojnë), ndërsa ata ndërmjetësojnë vetëm për atë me të cilin Ai është i kënaqur, e prej frikës së Tij qëndrojnë me nderim dhe frikë (se mos i ndëshkon).');
INSERT INTO "hasan_efendi" VALUES(21,29,'21:29','Këdo prej tyre që thotë: “Unë jam zot, përveç Atij”, Ne e dënojmë me Xhehenem. Kështu i “shpërblejmë” keqbërësit.');
INSERT INTO "hasan_efendi" VALUES(21,30,'21:30','A nuk e dinë mohuesit se qiejt dhe Toka kanë qenë një e tërë? Pastaj Ne e ndamë dhe bëmë nga uji çdo gjë të gjallë! Akoma nuk besojnë?!');
INSERT INTO "hasan_efendi" VALUES(21,31,'21:31','Ne vendosëm nëpër Tokë male të patundura, që ajo të mos lëkundet bashkë me njerëzit dhe bëmë lugina dhe rrugë për të udhëtuar në to.');
INSERT INTO "hasan_efendi" VALUES(21,32,'21:32','Ne e bëmë qiellin mbulesë të mbrojtur, por përsëri ata shmangen nga shenjat që gjenden aty.');
INSERT INTO "hasan_efendi" VALUES(21,33,'21:33','Allahu është Ai, i Cili krijoi natën dhe ditën, Diellin dhe Hënën; e të gjitha këto lundrojnë në gjithësi.');
INSERT INTO "hasan_efendi" VALUES(21,34,'21:34','Para teje, Ne nuk e kemi bërë asnjë të pavdekshëm. Nëse ti vdes, a do të mbesin ata të përjetshëm?!');
INSERT INTO "hasan_efendi" VALUES(21,35,'21:35','Çdo njeri do ta shijojë vdekjen! Ne ju vëmë në provë me të keqe dhe me të mirë dhe te Ne do të ktheheni.');
INSERT INTO "hasan_efendi" VALUES(21,36,'21:36','Kur të shohin jobesimtarët, ata tallen me ty (duke thënë): “Ky është ai që i përmend (për keq) zotat tuaj?” Ndërkohë, ata e mohojnë Këshillën e të Gjithëmëshirshmit.');
INSERT INTO "hasan_efendi" VALUES(21,37,'21:37','Njeriu është krijuar i ngutur. Unë do t’jua tregoj shenjat e Mia, prandaj mos më kërkoni të ngutem.');
INSERT INTO "hasan_efendi" VALUES(21,38,'21:38','Ata thonë: “Kur do të bëhet ky premtim, nëse flisni të vërtetën?”');
INSERT INTO "hasan_efendi" VALUES(21,39,'21:39','Sikur mohuesit ta dinin kohën, kur nuk do të mund ta shmangin zjarrin nga fytyrat dhe shpinat e tyre dhe nuk do të ndihmohen, (nuk do të flisnin kështu)!');
INSERT INTO "hasan_efendi" VALUES(21,40,'21:40','Por atyre (Kiameti) do t’u vijë papritmas e do t’i trullosë, kështu që nuk do të mund ta shmangin atë e as nuk do t’u jepet afat!');
INSERT INTO "hasan_efendi" VALUES(21,41,'21:41','Në të vërtetë, janë përqeshur edhe të dërguar të tjerë para teje, por shpotitësve u erdhi dënimi, me të cilin talleshin.');
INSERT INTO "hasan_efendi" VALUES(21,42,'21:42','Thuaj: “Kush do t’ju ruajë natën dhe ditën (nga dënimi) i të Gjithëmëshirshmit?” Jo, askush! E, megjithatë, ata shmangen nga përmendja e Zotit të tyre.');
INSERT INTO "hasan_efendi" VALUES(21,43,'21:43','Apo ata kanë zota të tjerë, të cilët mund t’i mbrojnë prej Nesh? Këta nuk mund të ndihmojnë as veten dhe askush nuk mund t’i marrë në mbrojtje kundër Nesh.');
INSERT INTO "hasan_efendi" VALUES(21,44,'21:44','Ne u dhamë atyre dhe baballarëve të tyre kënaqësi e ua zgjatëm jetën. A nuk e shohin vallë ata që Ne i qasemi tokës së tyre dhe ia cungojmë skajet? E si mund të jenë ata fitues?!');
INSERT INTO "hasan_efendi" VALUES(21,45,'21:45','Thuaj: “Unë vetëm ju paralajmëroj, me atë që më është shpallur”. Por, shurdhët nuk e dëgjojnë thirrjen kur paralajmërohen.');
INSERT INTO "hasan_efendi" VALUES(21,46,'21:46','Sikur ata t’i prekte një puhi prej dënimit të Zotit tënd, me siguri, do të thoshin: “Mjerë ne që kemi qenë keqbërës!”');
INSERT INTO "hasan_efendi" VALUES(21,47,'21:47','Dhe në Ditën e Kiametit, Ne do të vendosim peshore të sakta e askush nuk do të pësojë asnjë padrejtësi. Ne do të sjellim (për gjykim) çdo vepër, qoftë kjo edhe sa pesha e një kokrre sinapi. Ne jemi të mjaftueshëm për llogaritje.');
INSERT INTO "hasan_efendi" VALUES(21,48,'21:48','Ne i dhamë Musait dhe Harunit Dalluesin , që është dritë dhe këshillë për të devotshmit,');
INSERT INTO "hasan_efendi" VALUES(21,49,'21:49','që i frikësohen Zotit të tyre - edhe kur nuk i sheh askush dhe, që i tremben Orës (së Kiametit).');
INSERT INTO "hasan_efendi" VALUES(21,50,'21:50','Ndërsa ky (Kuran) është Këshillë e bekuar, të cilën Ne e kemi shpallur, prandaj, si mund ta mohoni ju atë?!');
INSERT INTO "hasan_efendi" VALUES(21,51,'21:51','Në të vërtetë, Ne e udhëzuam Ibrahimin në rrugë të drejtë qysh më parë dhe e njihnim atë (se ishte i aftë për këtë gjë).');
INSERT INTO "hasan_efendi" VALUES(21,52,'21:52','Kur ai i tha të atit dhe popullit të tij: “Ç’janë këta idhuj, që ju po i adhuroni kaq shumë?”,');
INSERT INTO "hasan_efendi" VALUES(21,53,'21:53','ata thanë: “Baballarët tanë i adhuronin këta.”');
INSERT INTO "hasan_efendi" VALUES(21,54,'21:54','(Ibrahimi) tha: “Me të vërtetë, ju dhe baballarët tuaj keni qenë në humbje të qartë.”');
INSERT INTO "hasan_efendi" VALUES(21,55,'21:55','Ata thanë: “A po na sjell të Vërtetën apo je nga ata që tallen?”');
INSERT INTO "hasan_efendi" VALUES(21,56,'21:56','Ai tha: “Jo, por Zoti juaj është Zoti i qiejve dhe i Tokës. Është Ai që i ka krijuar ato, ndërsa unë jam njëri prej atyre që e dëshmojnë këtë.');
INSERT INTO "hasan_efendi" VALUES(21,57,'21:57','(Betohem) për Allahun, se do t’i flak idhujt tuaj, posa të largoheni ju!”');
INSERT INTO "hasan_efendi" VALUES(21,58,'21:58','Dhe i bëri ata copa-copa, përveç idhullit të tyre më të madh, kështu që ata t’i drejtoheshin atij.');
INSERT INTO "hasan_efendi" VALUES(21,59,'21:59','Disa thanë: “Kush i bëri kështu zotat tanë? Me të vërtetë, ai është keqbërës!”');
INSERT INTO "hasan_efendi" VALUES(21,60,'21:60','Të tjerët u përgjigjën: “Dëgjuam një djalosh që i përgojonte, quhej Ibrahim.”');
INSERT INTO "hasan_efendi" VALUES(21,61,'21:61','“Atëherë silleni atë para syve të botës, që ata të dëshmojnë” – thanë ata.');
INSERT INTO "hasan_efendi" VALUES(21,62,'21:62','Ata e pyetën: “Ti i bëre kështu zotat tanë, o Ibrahim?”');
INSERT INTO "hasan_efendi" VALUES(21,63,'21:63','Ai tha: “Jo! Këtë e ka bërë ky i madhi i tyre. Pyesni ata, nëse flasin!”');
INSERT INTO "hasan_efendi" VALUES(21,64,'21:64','Atëherë ata i thirrën mendjes dhe i thanë njëri-tjetrit: “Me të vërtetë, ju vetë jeni keqbërësit.”');
INSERT INTO "hasan_efendi" VALUES(21,65,'21:65','Pastaj ulën kokat dhe i thanë: “Sigurisht që ti e di, se këta nuk flasin!”');
INSERT INTO "hasan_efendi" VALUES(21,66,'21:66','Ai u tha: “Mos vallë, ju adhuroni në vend të Allahut gjëra që nuk mund t’ju sjellin kurrfarë dobie e as dëmi?!');
INSERT INTO "hasan_efendi" VALUES(21,67,'21:67','Mjerë ju dhe mjerë ata që ju i adhuroni në vend të Allahut! A nuk arsyetoni?!”');
INSERT INTO "hasan_efendi" VALUES(21,68,'21:68','Ata thirrën: “Digjeni atë dhe ndihmoni zotat tuaj, nëse doni të bëni ndonjë vepër!”');
INSERT INTO "hasan_efendi" VALUES(21,69,'21:69','Ne thamë: “O zjarr, bëhu i ftohtë dhe shpëtim për Ibrahimin!”');
INSERT INTO "hasan_efendi" VALUES(21,70,'21:70','Ata deshën ta lëndonin, por Ne i bëmë të dështonin.');
INSERT INTO "hasan_efendi" VALUES(21,71,'21:71','Ne e shpëtuam atë dhe Lutin (dhe i çuam) në tokën, të cilën e kemi bekuar për njerëzit.');
INSERT INTO "hasan_efendi" VALUES(21,72,'21:72','Ne i dhuruam atij Is’hakun dhe Jakubin si dhunti dhe të gjithë i bëmë të drejtë.');
INSERT INTO "hasan_efendi" VALUES(21,73,'21:73','Ne i bëmë ata udhëheqës që udhëzonin sipas urdhrit Tonë dhe i frymëzuam që të bënin vepra të mira, të kryenin faljet dhe të jepnin zekatin, dhe ata vetëm Ne na adhuruan.');
INSERT INTO "hasan_efendi" VALUES(21,74,'21:74','Lutit i dhamë urtësi dhe dituri, dhe e shpëtuam prej vendbanimit ku bëheshin vepra të liga. Ata ishin vërtet njerëz të këqij e të mbrapshtë.');
INSERT INTO "hasan_efendi" VALUES(21,75,'21:75','Lutin e pranuam në mëshirën Tonë. Me të vërtetë, ai është prej të mirëve.');
INSERT INTO "hasan_efendi" VALUES(21,76,'21:76','(Kujtoje) Nuhun kur ai Na thirri më përpara, dhe Ne iu përgjigjëm atij, duke e shpëtuar atë dhe familjen e tij nga mjerimi i madh (mbytja).');
INSERT INTO "hasan_efendi" VALUES(21,77,'21:77','Dhe e ndihmuam atë kundër popullit që i quante gënjeshtra shenjat Tona. Me të vërtetë, ai ishte popull i keq, prandaj e fundosëm të gjithin.');
INSERT INTO "hasan_efendi" VALUES(21,78,'21:78','(Kujtoje) edhe Davudin dhe Sulejmanin, kur gjykuan mbi të lashtat në të cilat kishin kullotur natën bagëtitë e disa njerëzve. Ne ishim dëshmitarë të gjykimit të tyre.');
INSERT INTO "hasan_efendi" VALUES(21,79,'21:79','Ne e bëmë Sulejmanin ta kuptonte drejtë çështjen dhe, që të dyve u dhamë mençuri e dituri. Ne i nënshtruam malet dhe shpendët që, bashkë me Davudin, të thurnin lavde (për Allahun). Ishim Ne që i bënim këto.');
INSERT INTO "hasan_efendi" VALUES(21,80,'21:80','Ne i mësuam atij (Davudit) punimin e parzmoreve prej hekuri për ju, për t’ju mbrojtur në luftë me armikun, andaj, a jeni mirënjohës?');
INSERT INTO "hasan_efendi" VALUES(21,81,'21:81','Sulejmanit i dhamë erën e fortë, që të fryjë sipas urdhrit të tij, drejt tokës, të cilën Ne e kemi bekuar; Ne jemi të Dijshëm për çdo gjë.');
INSERT INTO "hasan_efendi" VALUES(21,82,'21:82','Dhe (ia nënshtruam atij) disa nga djajtë që zhyteshin në det për të, (duke nxjerrë xhevahire) dhe bënin edhe punë të tjera dhe Ne i ruanim ata (që të mos i bënin dëm).');
INSERT INTO "hasan_efendi" VALUES(21,83,'21:83','(Kujtoje) Ejubin, kur iu lut Zotit të vet: “Mua më ka goditur fatkeqësia e Ti je më mëshiruesi i mëshiruesve!”');
INSERT INTO "hasan_efendi" VALUES(21,84,'21:84','Ne iu përgjigjëm atij, ia larguam mjerimin që kishte dhe ia kthyem familjen të dyfishuar në numër. (Këtë e bëmë) nga mëshira Jonë dhe që të jetë përkujtesë për ata që Na adhurojnë.');
INSERT INTO "hasan_efendi" VALUES(21,85,'21:85','(Kujto) Ismailin, Idrisin dhe Dhulkiflin! Të gjithë këta kanë qenë të durueshëm.');
INSERT INTO "hasan_efendi" VALUES(21,86,'21:86','Ne i pranuam ata me mëshirën Tonë. Me të vërtetë, ata ishin ndër të drejtët.');
INSERT INTO "hasan_efendi" VALUES(21,87,'21:87','(Kujtoje) Dhun-Nunin (Junusin) kur iku i zemëruar e mendoi se Ne nuk do ta dënojmë, andaj thërriti nga errësira: “S’ka zot tjetër, përveç Teje! Qofsh i lavdëruar! Me të vërtetë, unë kam gabuar!”');
INSERT INTO "hasan_efendi" VALUES(21,88,'21:88','Ne iu përgjigjëm atij dhe e shpëtuam nga brengosja. Kështu, Ne i shpëtojmë besimtarët.');
INSERT INTO "hasan_efendi" VALUES(21,89,'21:89','(Kujtoje) Zekerijan, kur iu lut Zotit të vet: “O Zoti im, mos më lër vetëm (pa fëmijë), e Ti je trashëgimtari më i mirë.”');
INSERT INTO "hasan_efendi" VALUES(21,90,'21:90','Ne iu përgjigjëm atij dhe i dhuruam Jahjain, duke ia bërë gruan të aftë (për lindje). Me të vërtetë, ata nxitonin për vepra të mira dhe Na luteshin, me shpresë e frikë dhe ishin të përulur ndaj Nesh.');
INSERT INTO "hasan_efendi" VALUES(21,91,'21:91','(Kujtoje) atë që ruajti virgjërinë e saj ! Ne frymë brenda saj nga shpirti Ynë dhe e bëmë atë dhe birin e saj mrekulli për gjithë njerëzit.');
INSERT INTO "hasan_efendi" VALUES(21,92,'21:92','Vërtet, ky është besimi juaj - besimi i vetëm, e Unë jam Zoti juaj, andaj më adhuroni Mua!');
INSERT INTO "hasan_efendi" VALUES(21,93,'21:93','Njerëzit janë përçarë në çështje të besimit, por të gjithë do të kthehen te Ne (Ditën e Gjykimit).');
INSERT INTO "hasan_efendi" VALUES(21,94,'21:94','Atij që bën vepra të mira dhe është besimtar, përpjekja nuk do t’i mohohet. Dhe këtë Ne ia shënojmë atij.');
INSERT INTO "hasan_efendi" VALUES(21,95,'21:95','Është e pamundur, që banorët e një vendi, të cilët i kemi zhdukur Ne, të kthehen sërish në këtë botë.');
INSERT INTO "hasan_efendi" VALUES(21,96,'21:96','Dhe, kur të dalin Jexhuxhët dhe Mexhuxhët e të shpejtojnë prej çdo lartësie,');
INSERT INTO "hasan_efendi" VALUES(21,97,'21:97','dhe të afrohet premtimi i vërtetë (Kiameti), atëherë sytë e atyre që nuk kanë besuar, do të zgurdullohen, (e thonë): “Mjerë për ne! Ne kemi qenë të pavëmendshëm ndaj kësaj dhe e kemi ngarkuar veten me gjynahe”.');
INSERT INTO "hasan_efendi" VALUES(21,98,'21:98','Me të vërtetë, ju (idhujtarë) dhe idhujt që i adhuroni në vend të Allahut, do të jeni lëndë djegëse për Xhehenemin. Ju do të vini në të.');
INSERT INTO "hasan_efendi" VALUES(21,99,'21:99','Sikur këta të ishin zota (të vërtetë), nuk do të vinin atje dhe të gjithë do të mbeteni përherë aty.');
INSERT INTO "hasan_efendi" VALUES(21,100,'21:100','Ata do të gulçojnë dhe nuk do të dëgjojnë (asgjë).');
INSERT INTO "hasan_efendi" VALUES(21,101,'21:101','Ndërsa ata të cilëve u ka prirë mirësia Jonë qysh më parë, do të jenë larg nga Xhehenemi.');
INSERT INTO "hasan_efendi" VALUES(21,102,'21:102','Ata nuk do të dëgjojnë fërshëllimën e tij dhe do të qëndrojnë përherë në atë që u ka dëshiruar shpirti i tyre.');
INSERT INTO "hasan_efendi" VALUES(21,103,'21:103','Ata nuk do t’i brengosë tmerri më i madh dhe engjëjt do t’i presin (duke u thënë): “Kjo është dita juaj e premtuar.”');
INSERT INTO "hasan_efendi" VALUES(21,104,'21:104','Atë Ditë, Ne do të palosim qiellin, ashtu siç paloset letra e librit. Ashtu siç e filluam krijimin e parë, do ta përsërisim atë. Ky është premtimi Ynë. Ne do ta bëjmë këtë me të vërtetë.');
INSERT INTO "hasan_efendi" VALUES(21,105,'21:105','Ne shënuam në Zebur, pas Teuratit, që tokën (e Xhenetit) do ta trashëgojnë robërit e Mi të mirë.');
INSERT INTO "hasan_efendi" VALUES(21,106,'21:106','Me të vërtetë, në këtë (Kuran) ka mësime të mjaftueshme për ata që (e) adhurojnë (Allahun).');
INSERT INTO "hasan_efendi" VALUES(21,107,'21:107','Dhe Ne nuk të kemi dërguar ty (o Muhamed), veçse si mëshirë për botët.');
INSERT INTO "hasan_efendi" VALUES(21,108,'21:108','Thuaj: “Mua më është shpallur që Zoti juaj është një Zot i vetëm; pra, a jeni myslimanë ?”');
INSERT INTO "hasan_efendi" VALUES(21,109,'21:109','E, nëse ata i shmangen (thirrjes sate), ti thuaju: “Unë ju kam lajmëruar të gjithëve pa dallim dhe nuk di a është afër apo larg ajo, që ju është premtuar.');
INSERT INTO "hasan_efendi" VALUES(21,110,'21:110','Ai i di vërtet fjalët e thëna haptazi apo fshehtazi.');
INSERT INTO "hasan_efendi" VALUES(21,111,'21:111','Ndërsa unë nuk e di: a është ajo sprovë për ju apo kënaqësi e përkohshme.');
INSERT INTO "hasan_efendi" VALUES(21,112,'21:112','O Zoti im, gjyko me drejtësi! Zoti ynë është i Gjithëmëshirshmi, ndihmën e të Cilit kërkojmë kundër atyre që thoni.”');
INSERT INTO "hasan_efendi" VALUES(22,1,'22:1','O njerëz, frikësojuni Zotit tuaj, sepse dridhja e Orës (së Kiametit), është vërtet një ngjarje e madhe!');
INSERT INTO "hasan_efendi" VALUES(22,2,'22:2','Ditën, që do të shihni atë, çdo grua me fëmijë në gji do ta harrojë fëmijën e saj dhe çdo grua shtatzënë do ta hedhë barrën e vet. Njerëzit do t’i shihni si të dehur, ndonëse ata nuk do të jenë të dehur, por dënimi i Allahut është i ashpër.');
INSERT INTO "hasan_efendi" VALUES(22,3,'22:3','Ka disa njerëz që diskutojnë për Allahun, pa kurrfarë dijenie dhe ndjekin rrugën e çdo djalli të ndërkryer,');
INSERT INTO "hasan_efendi" VALUES(22,4,'22:4','e është caktuar për të (djallin), se kush e bën mik, ai (djalli) do ta shmangë (mikun) e do ta sjellë atë në dënimin e zjarrit.');
INSERT INTO "hasan_efendi" VALUES(22,5,'22:5','O njerëz, nëse ju dyshoni në ringjalljen, mendoni si Ne ju kemi krijuar prej dheut, pastaj prej pikës së farës, pastaj prej droçkave të gjakut, pastaj prej një cope mishi me trajtë të plotë ose jo të plotë, për t’jua bërë të qartë! Dhe Ne e mbajmë në mitër atë që duam deri në kohën e caktuar. Pastaj ju nxjerrim si fëmijë e pas kësaj do të arrini moshën e pjekurisë. Disa nga ju vdesin e disa ngelin deri në pleqëri të thellë, që të mos dinë asgjë nga ajo që kanë ditur më parë. Dhe ti e sheh tokën të tharë, por, kur Ne lëshojmë ujë mbi të, ajo trazohet dhe gufon dhe prej saj mbijnë gjithfarë lloj bimësh të mrekullueshme në çift.');
INSERT INTO "hasan_efendi" VALUES(22,6,'22:6','Kjo ndodh (që ta dini) se Allahu është e Vërteta, se Ai i ringjall të vdekurit, se Ai është i Fuqishëm për çdo gjë,');
INSERT INTO "hasan_efendi" VALUES(22,7,'22:7','se Ora (e Kiametit) do të vijë, gjë për të cilën s’ka dyshim dhe se Allahu do t’i ringjallë ata që janë në varre.');
INSERT INTO "hasan_efendi" VALUES(22,8,'22:8','Disa njerëz diskutojnë për Allahun pa kurrfarë diturie, pa udhërrëfyes dhe pa Libër ndriçues.');
INSERT INTO "hasan_efendi" VALUES(22,9,'22:9','Ata e kthejnë mënjanë kokën (nga mendjemadhësia), që të largojnë të tjerët nga rruga e Allahut. Këta njerëz do të poshtërohen në këtë botë, kurse në Ditën e Kiametit Ne do t’i bëjmë, që të shijojnë dënimin e zjarrit të ndezur fort');
INSERT INTO "hasan_efendi" VALUES(22,10,'22:10','(duke iu thënë): “Ky (dënim) është për shkak të veprave tuaja, se Allahu nuk është i padrejtë ndaj robërve të Tij.”');
INSERT INTO "hasan_efendi" VALUES(22,11,'22:11','Disa njerëz të tjerë e adhurojnë Allahun me kusht: Nëse u ecën mbarë, janë të kënaqur, por, nëse i godet fatkeqësia, ata kthehen në mohim e kështu humbasin këtë botë dhe tjetrën. Pikërisht kjo është humbja e qartë.');
INSERT INTO "hasan_efendi" VALUES(22,12,'22:12','Në vend të Allahut, ata u luten edhe atyre që nuk mund të sjellin as ndonjë dëm e as ndonjë dobi. Kjo është vërtet humbja e largët.');
INSERT INTO "hasan_efendi" VALUES(22,13,'22:13','Ata i luten asaj, dëmi i së cilës është më i afërt se dobia: një zotëri i keq dhe një shok i keq!');
INSERT INTO "hasan_efendi" VALUES(22,14,'22:14','Vërtet, ata që besojnë dhe bëjnë vepra të mira, Allahu do t’i fusë në kopshte, nëpër të cilat rrjedhin lumenj. Pa dyshim, Allahu bën çfarë të dëshirojë.');
INSERT INTO "hasan_efendi" VALUES(22,15,'22:15','Kush mendon se Allahu nuk do ta ndihmojë (Muhamedin) në këtë botë dhe në botën tjetër, le të lidhë një litar në tavan e të varet! Pastaj le të shohë nëse do t’ia largojë ky veprim hidhërimin! ');
INSERT INTO "hasan_efendi" VALUES(22,16,'22:16','Dhe kështu, Ne e kemi zbritur Kuranin në vargje të qarta dhe, sigurisht, Allahu udhëzon në rrugë të drejtë kë të dojë.');
INSERT INTO "hasan_efendi" VALUES(22,17,'22:17','Vërtet, si ata që kanë besuar, ashtu dhe hebrenjtë, sabi’inët, të krishterët, mexhusitë e idhujtarët, Allahu do t’i gjykojë drejt të gjithë Ditën e Kiametit. Nuk ka dyshim, se Allahu është dëshmitar për çdo gjë.');
INSERT INTO "hasan_efendi" VALUES(22,18,'22:18','A nuk e sheh ti se, në të vërtetë, Allahut i përulen në sexhde ata që gjenden në qiej dhe në Tokë, edhe Dielli, edhe Hëna, edhe yjet, edhe malet, edhe drurët, edhe kafshët dhe shumë nga njerëzit (myslimanët)? Por ka shumë njerëz (mohues), që meritojnë dënimin. E atë që e poshtëron Allahu, askush nuk mund ta bëjë të nderuar. Allahu, me të vërtetë, bën çfarë të dëshirojë.');
INSERT INTO "hasan_efendi" VALUES(22,19,'22:19','Besimtarët dhe jobesimtarët grinden për Zotin e tyre, por për ata që nuk besojnë janë përgatitur rroba prej zjarri, e mbi kokat e tyre do të hidhet ujë i valuar,');
INSERT INTO "hasan_efendi" VALUES(22,20,'22:20','me të cilin do t’u shkrihen ç’kanë në barqe dhe lëkura.');
INSERT INTO "hasan_efendi" VALUES(22,21,'22:21','Për ata ka kamxhikë prej hekuri.');
INSERT INTO "hasan_efendi" VALUES(22,22,'22:22','Sa herë, që do të përpiqen të dalin nga ankthi i zjarrit, do të rikthehen në të (e do t’u thuhet): “Shijoni dënimin me djegie të tmerrshme!”');
INSERT INTO "hasan_efendi" VALUES(22,23,'22:23','S’ka dyshim se ata që besojnë dhe bëjnë vepra të mira, Allahu i fut në kopshte, nëpër të cilat rrjedhin lumenj. Ata do të stolisen me byzylykë të artë dhe inxhi e rrobat e tyre do të jenë prej mëndafshi.');
INSERT INTO "hasan_efendi" VALUES(22,24,'22:24','Ata qenë udhëzuar drejt fjalës së mirë dhe në rrugën e Atij që është i Denjë për t’u lavdëruar.');
INSERT INTO "hasan_efendi" VALUES(22,25,'22:25','Vërtet, ata që nuk besojnë dhe pengojnë njerëzit nga rruga e Allahut dhe nga Xhamia e Shenjtë, të cilën Ne e kemi bërë të njëjtë për të gjithë njerëzit, si për vendasin ashtu edhe për atë që vjen nga tjetër vend dhe, ata që synojnë ta përdhosin (Xhaminë), me të keqe, Ne do t’i bëjmë të shijojnë dënim të dhembshëm.');
INSERT INTO "hasan_efendi" VALUES(22,26,'22:26','Kujtoje kur Ne vendosëm për Ibrahimin vendin e Faltores (duke e urdhëruar): “Mos më shoqëro asgjë (në adhurim) dhe pastroje Shtëpinë Time (nga idhujtaria), për ata që vijnë rrotull saj (në shenjë adhurimi) dhe për ata që rrinë në këmbë, përkulen dhe bien në sexhde duke u falur.');
INSERT INTO "hasan_efendi" VALUES(22,27,'22:27','Thirri njerëzit për haxhillëk: ata do të vijnë në këmbë dhe me deve prej të gjitha viseve të largëta,');
INSERT INTO "hasan_efendi" VALUES(22,28,'22:28','që të dëshmojnë dobitë e tyre dhe të përmendin Emrin e Allahut në ditët e caktuara mbi kafshët (për kurban), të cilat ua ka dhuruar Ai. Hani nga ato (mishin) dhe ushqeni nevojtarin e varfër!');
INSERT INTO "hasan_efendi" VALUES(22,29,'22:29','Pastaj, haxhinjtë le të pastrojnë trupin e tyre, le t’i kryejnë zotimet e tyre dhe le t’i vijnë rrotull Shtëpisë së lashtë.');
INSERT INTO "hasan_efendi" VALUES(22,30,'22:30','Këto (janë urdhërimet). Për atë që nderon ritet e shenjta të Allahut, do të jetë më mirë te Zoti i tij. Ju janë lejuar bagëtitë, përveç atyre që ju janë thënë (se janë të ndaluara). Andaj, largohuni prej ndotësisë së idhujve dhe largohuni prej fjalëve të rreme.');
INSERT INTO "hasan_efendi" VALUES(22,31,'22:31','Përuluni plotësisht para Allahut me sinqeritet, duke mos i shoqëruar asgjë Atij (në adhurim). Kush i shoqëron Allahut diçka (në adhurim), i ngjan atij që bie nga qielli e që e rrëmben zogu ose i ngjan atij që stuhia e erës e ka hedhur në një vend të largët e të humbur.');
INSERT INTO "hasan_efendi" VALUES(22,32,'22:32','Po, kështu është! Ndërsa kush madhëron shenjat e fesë së Allahut, tregon përkushtimin e zemrës së tij.');
INSERT INTO "hasan_efendi" VALUES(22,33,'22:33','Ju do të përfitoni nga ato (kafshë) deri në një kohë të caktuar; pastaj ato do të flijohen te Faltorja e lashtë.');
INSERT INTO "hasan_efendi" VALUES(22,34,'22:34','Për çdo popull, Ne kemi caktuar një ritual, që ata të përmendin emrin e Allahut gjatë flijimit të kafshëve, që Ai ua ka dhuruar për ushqim. Zoti juaj është një Zot i vetëm, prandaj vetëm Atij përuljuni. Dhe jepu lajme të mira atyre që janë të përulur,');
INSERT INTO "hasan_efendi" VALUES(22,35,'22:35','atyre, zemrat e të cilëve frikësohen, kur përmendet Allahu, që (janë) të durueshëm për gjithçka që i godet, që falin namazet dhe që ndajnë nga ajo, që Ne ua kemi dhënë.');
INSERT INTO "hasan_efendi" VALUES(22,36,'22:36','Devetë dhe gjedhët i kemi bërë për ju si një nga ritet e Allahut për flijim dhe ju keni dobi nga ato. Prandaj, përmendeni emrin e Allahut mbi ato, ndërsa i radhisni në këmbë (për t’i therur). Kur ato të bien në tokë, hani prej (mishit të) tyre dhe ushqeni edhe atë që lyp, edhe atë që nuk lyp. Kështu pra, Ne i kemi vënë ato në shërbimin tuaj, që ju të jeni mirënjohës.');
INSERT INTO "hasan_efendi" VALUES(22,37,'22:37','Tek Allahu nuk arrin as mishi, as gjaku i tyre, por arrin përkushtimi juaj. Kështu, Ai i ka vënë ato në shërbimin tua,j që ju ta madhëroni Allahun sepse ju ka udhëzuar. Dhe, jepu lajmin e mirë, punëdrejtëve!');
INSERT INTO "hasan_efendi" VALUES(22,38,'22:38','Allahu, në të vërtetë, i mbron besimtarët. Ai nuk i do tradhtarët mohues.');
INSERT INTO "hasan_efendi" VALUES(22,39,'22:39','Atyre që janë sulmuar, u lejohet (që të mbrohen), sepse u është bërë padrejtësi dhe, në të vërtetë, Allahu është i Fuqishëm, që t’i ndihmojë');
INSERT INTO "hasan_efendi" VALUES(22,40,'22:40','ata, që janë dëbuar nga vatrat e tyre padrejtësisht, vetëm sepse thanë: “Zoti ynë është Allahu.” Sikur Allahu të mos u kishte dhënë njerëzve mundësinë për t’u mbrojtur nga njëri-tjetri, do të shkatërroheshin manastiret, kishat, sinagogat dhe xhamitë, në të cilat përmendet shumë emri i Allahut. Vërtet, Allahu e ndihmon këdo që e ndihmon Atë. Allahu është vërtet i Fortë dhe i Plotfuqishëm.');
INSERT INTO "hasan_efendi" VALUES(22,41,'22:41','(Allahu i ndihmon edhe) ata, të cilëve, nëse u japim pushtet në tokë, kryejnë faljet, japin zekatin, urdhërojnë kryerjen e veprave të mira dhe pengojnë atë që është e mbrapshtë. Tek Allahu është fundi i të gjitha çështjeve.');
INSERT INTO "hasan_efendi" VALUES(22,42,'22:42','Nëse ata të quajnë ty gënjeshtar, (dije se) para tyre (i) kanë quajtur gënjeshtarë (profetët e vet) edhe populli i Nuhut, fiset Ad e Themud,');
INSERT INTO "hasan_efendi" VALUES(22,43,'22:43','populli i Ibrahimit, populli i Lutit,');
INSERT INTO "hasan_efendi" VALUES(22,44,'22:44','dhe banorët e Medjenit. Edhe Musain e përgënjeshtruan; andaj Unë u dhashë afat mohuesve, pastaj i dënova ata. Eh, si ka qenë dënimi Im!');
INSERT INTO "hasan_efendi" VALUES(22,45,'22:45','Sa e sa qytete i shkatërruam, ngaqë banorët e tyre ishin të këqij dhe tani ato janë kthyer përmbys! Sa e sa puse mbetën të braktisura, sa e sa pallate të larta të shkretuara!');
INSERT INTO "hasan_efendi" VALUES(22,46,'22:46','Vallë, a nuk kanë udhëtuar ata nëpër botë, që zemrat e tyre të mendojnë dhe veshët të dëgjojnë? Në të vërtetë, atyre nuk u janë verbuar sytë (në këto gjëra), por u janë verbuar zemrat e veta në kraharor.');
INSERT INTO "hasan_efendi" VALUES(22,47,'22:47','Ata të kërkojnë që t’u shpejtohet dënimi, por Allahu kurrsesi nuk e thyen premtimin e Vet. Një ditë te Zoti yt është sa një mijë vjet sipas llogarisë suaj.');
INSERT INTO "hasan_efendi" VALUES(22,48,'22:48','Sa e sa vendbanimeve Unë ua shtyva dënimin, edhe pse ata ishin keqbërës, por prapëseprapë i dënova. Tek Unë kthehet çdo gjë.');
INSERT INTO "hasan_efendi" VALUES(22,49,'22:49','Thuaj (o Muhamed!): “O njerëz, unë jam për ju vetëm paralajmërues i qartë”.');
INSERT INTO "hasan_efendi" VALUES(22,50,'22:50','Për ata që besojnë dhe bëjnë vepra të mira, ka falje dhe dhunti fisnike.');
INSERT INTO "hasan_efendi" VALUES(22,51,'22:51','Ndërsa ata që përpiqen t’i luftojnë shpalljet Tona, duke menduar se nuk do t’i dënojmë, (dije se) njerëz të tillë janë banorë të Xhehenemit.');
INSERT INTO "hasan_efendi" VALUES(22,52,'22:52','Ne nuk kemi sjellë para teje (o Muhamed) asnjë të dërguar e asnjë profet që djalli të mos i ndërhynte në leximin e tij (nga shpallja). Por Allahu e zhdukte ndërhyrjen e djallit dhe më pas i forconte shpalljet e Veta. Allahu është i Gjithëdijshëm dhe i Urtë.');
INSERT INTO "hasan_efendi" VALUES(22,53,'22:53','Kjo ndodh që Ai ta bëjë ndërhyrjen e djallit sprovë për njerëzit që i kanë zemrat të sëmura (nga dyshimi) dhe të ngurtësuara prandaj keqbërësit janë në përçarje të madhe');
INSERT INTO "hasan_efendi" VALUES(22,54,'22:54','dhe që ata, të cilëve u është dhënë dituria, ta kuptojnë se ai (Kurani) është e Vërteta nga Zoti yt, ta besojnë atë dhe zemrat e tyre të binden e të qetësohen me të. Nuk ka dyshim se Allahu i udhëzon në rrugën e drejtë ata që besojnë.');
INSERT INTO "hasan_efendi" VALUES(22,55,'22:55','Ata që nuk besojnë do të vazhdojnë të dyshojnë në të (Kuran), derisa t’u vijë Ora (e Kiametit) në mënyrë të papritur ose derisa t’u vijë dënimi i Ditës Shterpë. ');
INSERT INTO "hasan_efendi" VALUES(22,56,'22:56','I tërë pushteti atë ditë i përket Allahut. Ai do t’i gjykojë të gjithë: ata që kanë besuar dhe kanë bërë vepra të mira, do të gjenden në kopshtet e begative,');
INSERT INTO "hasan_efendi" VALUES(22,57,'22:57','ndërsa ata që nuk kanë besuar dhe i kanë përgënjeshtruar shpalljet Tona, do të kenë dënim poshtërues.');
INSERT INTO "hasan_efendi" VALUES(22,58,'22:58','Atyre që kanë mërguar për hir të Allahut e kanë rënë dëshmorë apo kanë vdekur, me siguri, Allahu do t’u japë shpërblim të mirë. Pa dyshim, Allahu është dhuruesi më i mirë,');
INSERT INTO "hasan_efendi" VALUES(22,59,'22:59','Ai do t’i shpjerë në një vend, ku do të jenë vërtet të kënaqur! Allahu është vërtet i Gjithëdijshëm dhe i Butë.');
INSERT INTO "hasan_efendi" VALUES(22,60,'22:60','Po, kështu është! Ai që merr hak për aq sa është dëmtuar e pastaj pëson sërish padrejtësi, me siguri, do të ndihmohet nga Allahu. Allahu është Shlyes i gjynaheve dhe Falës.');
INSERT INTO "hasan_efendi" VALUES(22,61,'22:61','Kjo është kështu, sepse Allahu e ndërfut natën te dita e ditën te nata dhe se Allahu dëgjon dhe sheh gjithçka.');
INSERT INTO "hasan_efendi" VALUES(22,62,'22:62','Kjo është kështu, sepse Allahu është e Vërteta (i vetmi që meriton të adhurohet), kurse gjithçka që adhurohet përveç Tij, është e pavërteta. Dhe, pa dyshim, Allahu është më i Lartësuari dhe i Madhërishmi.');
INSERT INTO "hasan_efendi" VALUES(22,63,'22:63','A nuk e sheh ti se Allahu lëshon shiun nga qielli e toka gjelbërohet?! Me të vërtetë, Allahu i di të gjitha në hollësi dhe njeh mirë çdo gjë.');
INSERT INTO "hasan_efendi" VALUES(22,64,'22:64','Atij i përket ç’ka në qiej e ç’ka në Tokë. Me të vërtetë, Allahu është i Vetëmjaftueshmi dhe i Denji për çdo lavd.');
INSERT INTO "hasan_efendi" VALUES(22,65,'22:65','A nuk e sheh ti, se Allahu ka vënë në shërbimin tuaj gjithçka që është në Tokë dhe anijet që lundrojnë nëpër det me urdhrin e Tij?! Është Ai që e mban qiellin që të mos bjerë në Tokë; kjo mund të ndodhë vetëm me lejen e Tij. Vërtet, Allahu është i Butë dhe i Mëshirshëm me njerëzit.');
INSERT INTO "hasan_efendi" VALUES(22,66,'22:66','Ai ju dha jetë dhe pastaj do t’ju bëjë të vdisni e më vonë do t’ju ringjallë. Me të vërtetë, njeriu është mosmirënjohës.');
INSERT INTO "hasan_efendi" VALUES(22,67,'22:67','Ne kemi caktuar për çdo popull rregulla fetare, sipas të cilave ata shkojnë, prandaj mos lejo kurrsesi (o Muhamed) që të diskutojnë me ty për atë çështje. Dhe thirri te Zoti yt! Sigurisht që ti je në rrugë të drejtë.');
INSERT INTO "hasan_efendi" VALUES(22,68,'22:68','Nëse ata grinden me ty, thuaju: “Allahu e di më së miri atë që bëni ju.');
INSERT INTO "hasan_efendi" VALUES(22,69,'22:69','Ai do të gjykojë në Ditën e Kiametit për kundërshtitë, që keni pasur ndërmjet jush.”');
INSERT INTO "hasan_efendi" VALUES(22,70,'22:70','A nuk e di ti, se Allahu di çdo gjë, që është në qiej e në Tokë? Të gjitha këto janë (shënuar) në Libër . Me të vërtetë, kjo është e lehtë për Allahun.');
INSERT INTO "hasan_efendi" VALUES(22,71,'22:71','Ata adhurojnë në vend të Allahut atë, për të cilën Ai nuk u ka dërguar atyre asnjë provë autoritare dhe, për të cilën, nuk kanë kurrfarë dijenie. Keqbërësit (idhujtarë) nuk do të kenë asnjë, që t’i ndihmojë (Ditën e Gjykimit).');
INSERT INTO "hasan_efendi" VALUES(22,72,'22:72','Kur atyre iu lexohen vargjet Tona të qarta, ti do të vëresh në fytyrat e jobesimtarëve marazin; ata duan t’u vërsulen atyre që iu lexojnë shpalljet Tona. Thuaj: “A doni t’ju tregoj se çfarë është më e keqe se kjo? - Zjarri i Xhehenemit, të cilin Allahu ua ka premtuar atyre që nuk besojnë! Eh, sa vendbanim i shëmtuar që është ai!”');
INSERT INTO "hasan_efendi" VALUES(22,73,'22:73','O njerëz, është sjellë një shembull, andaj dëgjojeni atë: “Idhujt që ju i adhuroni në vend të Allahut, nuk mund të krijojnë as edhe një mizë, edhe nëse bashkohen të gjithë për këtë. E, nëse miza u merr atyre diçka, ata nuk do të mund t’ia marrin. I dobët është përgjëruesi (adhuruesi), por edhe ai të cilit i përgjërohen (idhulli).”');
INSERT INTO "hasan_efendi" VALUES(22,74,'22:74','Ata nuk e çmojnë Allahun me vlerën e Tij të vërtetë. Vërtet, Allahu është i Fortë dhe i Plotfuqishëm.');
INSERT INTO "hasan_efendi" VALUES(22,75,'22:75','Allahu zgjedh të dërguar nga engjëjt dhe njerëzit; me të vërtetë, Allahu dëgjon dhe sheh gjithçka.');
INSERT INTO "hasan_efendi" VALUES(22,76,'22:76','Ai e di ç’ka para tyre dhe ç’ka pas tyre dhe tek Allahu kthehet çdo çështje.');
INSERT INTO "hasan_efendi" VALUES(22,77,'22:77','O besimtarë! Përkuluni dhe përuluni në sexhde (para Allahut)! Adhuroni Zotin tuaj dhe bëni vepra të mira për të arritur shpëtimin!');
INSERT INTO "hasan_efendi" VALUES(22,78,'22:78','Luftoni në rrugën e Allahut, ashtu si duhet luftuar. Ai ju ka zgjedhur dhe nuk ju ka vënë asnjë barrë në fenë tuaj, fenë e babait tuaj, Ibrahimit. Ai ju ka quajtur ju myslimanë qysh më parë (në Shkrimet e Shenjta) dhe në këtë (Kuran), me qëllim që i Dërguari të jetë dëshmitar mbi ju dhe që ju të jeni dëshmitarë mbi njerëzit e tjerë. Andaj, faleni namazin, jepni zekatin dhe mbështetuni tek Allahu! Ai është Mbrojtësi juaj. Sa Mbrojtës dhe Ndihmës i mrekullueshëm është Ai!');
INSERT INTO "hasan_efendi" VALUES(23,1,'23:1','Me të vërtetë, janë të shpëtuar besimtarët,');
INSERT INTO "hasan_efendi" VALUES(23,2,'23:2','të cilët janë të përulur në namazin e tyre,');
INSERT INTO "hasan_efendi" VALUES(23,3,'23:3','të cilët shmangen nga fjalët e kota,');
INSERT INTO "hasan_efendi" VALUES(23,4,'23:4','të cilët japin zekatin,');
INSERT INTO "hasan_efendi" VALUES(23,5,'23:5','të cilët e ruajnë nderin e tyre (nga marrëdhëniet e jashtëligjshme),');
INSERT INTO "hasan_efendi" VALUES(23,6,'23:6','përveçse me gratë e tyre ose me skllavet që kanë në zotërim dhe, për këtë, nuk janë fajtorë,');
INSERT INTO "hasan_efendi" VALUES(23,7,'23:7','ndërsa ata që kërkojnë përtej kësaj, pikërisht ata janë shkelës (të kufijve),');
INSERT INTO "hasan_efendi" VALUES(23,8,'23:8','të cilët u përmbahen amaneteve dhe detyrimeve të marra');
INSERT INTO "hasan_efendi" VALUES(23,9,'23:9','dhe të cilët i kryejnë rregullisht faljet.');
INSERT INTO "hasan_efendi" VALUES(23,10,'23:10','Pikërisht ata do të jenë trashëgimtarët,');
INSERT INTO "hasan_efendi" VALUES(23,11,'23:11','që do ta trashëgojnë Firdeusin, ku do të qëndrojnë përjetësisht.');
INSERT INTO "hasan_efendi" VALUES(23,12,'23:12','Me të vërtetë, Ne e kemi krijuar njeriun prej palcës së baltës,');
INSERT INTO "hasan_efendi" VALUES(23,13,'23:13','e pastaj, e bëmë atë pikë fare dhe e vumë në një vend të sigurt (mitër).');
INSERT INTO "hasan_efendi" VALUES(23,14,'23:14','E mandej, pikën e farës e bëmë droçkë gjaku, e pastaj e bëmë copëz mishi pa formë; mandej, me atë mish bëmë eshtrat, pastaj i veshëm eshtrat me mish dhe pastaj e bëmë atë (njeriun) krijesë tjetër (duke i futur shpirtin). Qoftë lartësuar Allahu, Krijuesi më i përsosur!');
INSERT INTO "hasan_efendi" VALUES(23,15,'23:15','Mandej, pas kësaj ju do të vdisni,');
INSERT INTO "hasan_efendi" VALUES(23,16,'23:16','pastaj, ju me siguri që do të ringjalleni në Ditën e Kiametit.');
INSERT INTO "hasan_efendi" VALUES(23,17,'23:17','Ne kemi krijuar mbi ju shtatë qiej dhe nuk jemi të pakujdesshëm ndaj krijesave Tona.');
INSERT INTO "hasan_efendi" VALUES(23,18,'23:18','Ne zbresim ujë nga qielli me masë të caktuar, pastaj e depozitojmë atë në Tokë. Por, nëse dëshirojmë, Ne jemi në gjendje ta heqim atë tërësisht.');
INSERT INTO "hasan_efendi" VALUES(23,19,'23:19','Nëpërmjet tij (ujit), Ne bëjmë për ju kopshte hurmash dhe rrushi, ku ka shumë fruta për të ngrënë,');
INSERT INTO "hasan_efendi" VALUES(23,20,'23:20','si dhe pemën që rritet në malin e Sinait e që u jep vaj dhe mëlmesë, atyre që e hanë.');
INSERT INTO "hasan_efendi" VALUES(23,21,'23:21','Vërtet, edhe te bagëtitë për ju ka shenja (për të medituar): ju pini atë që gjendet në barkun e tyre, hani mishin e tyre, nxirrni shumë dobi të tjera');
INSERT INTO "hasan_efendi" VALUES(23,22,'23:22','dhe udhëtoni me ato, ashtu siç udhëtoni me anije.');
INSERT INTO "hasan_efendi" VALUES(23,23,'23:23','Ne e dërguam Nuhun te populli i tij dhe ai tha: “O populli im, adhuroni Allahun! Ju nuk keni zot tjetër të vërtetë përveç Tij, vallë, a nuk keni frikë (prej Tij)?”');
INSERT INTO "hasan_efendi" VALUES(23,24,'23:24','Por paria e popullit të tij, që nuk besonte, tha: “Ky është veçse njeri si ju e do vetëm të madhërohet mbi ju. Sikur të kishte dashur Allahu, do të dërgonte engjëj; nuk kemi dëgjuar diçka të tillë nga të parët tanë të lashtë;');
INSERT INTO "hasan_efendi" VALUES(23,25,'23:25','ai është njeri i marrë, andaj lëreni atë njëfarë kohe!”');
INSERT INTO "hasan_efendi" VALUES(23,26,'23:26','(Nuhu) tha: “O Zoti im, më ndihmo, sepse ata më quajnë për gënjeshtar!”');
INSERT INTO "hasan_efendi" VALUES(23,27,'23:27','Dhe Ne i shpallëm atij: “Ndërto anijen nën Sytë (mbikëqyrjen) dhe frymëzimin Tonë e, kur të vijë urdhri Ynë e të vërshojë uji nga furra, ngarko në anije prej çdo lloj gjallese nga një çift dhe familjen tënde, përveç atyre që janë dënuar të humbasin, dhe mos M’u drejto për ata që kanë bërë të këqija se ata, me të vërtetë, do të fundosen.');
INSERT INTO "hasan_efendi" VALUES(23,28,'23:28','Kur të hipësh në barkë ti dhe ata që janë me ty, thuaj: “Qoftë lavdëruar Allahu, i cili na shpëtoi prej popullit keqbërës”');
INSERT INTO "hasan_efendi" VALUES(23,29,'23:29','Dhe thuaj: “Zoti im, më zbarko në vend të bekuar, se Ti je më i miri për të na zbarkuar.”');
INSERT INTO "hasan_efendi" VALUES(23,30,'23:30','Sigurisht që këto ishin shenja. Në të vërtetë, jemi Ne që i vëmë në provë (njerëzit).');
INSERT INTO "hasan_efendi" VALUES(23,31,'23:31','Pas tyre, Ne krijuam një brez tjetër.');
INSERT INTO "hasan_efendi" VALUES(23,32,'23:32','Atyre u çuam një të dërguar nga gjiri i tyre, që u tha: “Adhuroni Allahun! Ju nuk keni zot tjetër, përveç Tij! Vallë a nuk frikësoheni?!”');
INSERT INTO "hasan_efendi" VALUES(23,33,'23:33','Por paria e popullit të tij, e cila nuk besonte dhe e mohonte takimin në botën tjetër dhe, së cilës, i kishim dhënë të mira në këtë botë, tha: “Ky është vetëm njeri si ju, ha nga ato që hani edhe ju dhe pi nga ato që pini edhe ju!');
INSERT INTO "hasan_efendi" VALUES(23,34,'23:34','Nëse ju i bindeni një njeriu si ju, me siguri, do të jeni të humbur!');
INSERT INTO "hasan_efendi" VALUES(23,35,'23:35','A ju premton ai që, kur të vdisni e të bëheni pluhur dhe eshtra, do të ringjalleni vërtet?!');
INSERT INTO "hasan_efendi" VALUES(23,36,'23:36','Sa premtim qesharak që është!');
INSERT INTO "hasan_efendi" VALUES(23,37,'23:37','Nuk ka jetë tjetër përveç jetës së kësaj bote, ne jetojmë e vdesim e nuk do të ringjallemi më.');
INSERT INTO "hasan_efendi" VALUES(23,38,'23:38','Ai është vetëm një njeri që trillon gënjeshtra për Allahun, prandaj nuk i besojmë atij.”');
INSERT INTO "hasan_efendi" VALUES(23,39,'23:39','(I dërguari) tha: “O Zoti im, më ndihmo, sepse ata më akuzojnë se gënjej!”');
INSERT INTO "hasan_efendi" VALUES(23,40,'23:40','(Allahu) tha: “Së shpejti do të pendohen!”');
INSERT INTO "hasan_efendi" VALUES(23,41,'23:41','E, me të drejtë i kaploi ata një zë i tmerrshëm dhe Ne i bëmë si gjethe të fishkura. Larg qoftë (nga mëshira e Allahut) populli keqbërës!');
INSERT INTO "hasan_efendi" VALUES(23,42,'23:42','Pas tyre, Ne krijuam brezni të reja.');
INSERT INTO "hasan_efendi" VALUES(23,43,'23:43','Asnjë popull nuk mund ta shpejtojë apo ta ngadalësojë kohën e caktuar për të.');
INSERT INTO "hasan_efendi" VALUES(23,44,'23:44','Ne i kemi nisur të dërguarit Tanë njëri pas tjetrit. Sa herë që një i dërguar i shkonte ndonjë populli, atë e quanin gënjeshtar! Prandaj Ne i kemi bërë ata (popuj) të ndjekin njëri-tjetrin (në shkatërrim) dhe i kemi bërë histori që rrëfehen. Qoftë larg (nga mëshira e Allahut) populli që nuk beson!');
INSERT INTO "hasan_efendi" VALUES(23,45,'23:45','Pastaj dërguam Musain dhe vëllanë e tij, Harunin, me mrekullitë Tona dhe autoritet të qartë');
INSERT INTO "hasan_efendi" VALUES(23,46,'23:46','te Faraoni dhe paria e tij, por ata ishin njerëz arrogantë dhe nuk i pranuan.');
INSERT INTO "hasan_efendi" VALUES(23,47,'23:47','Ata thanë: “Vallë a t’u besojmë dy njerëzve që janë si ne, ndërkohë që populli i tyre na shërben neve?!”');
INSERT INTO "hasan_efendi" VALUES(23,48,'23:48','Kështu, ata të dy i shpallën për gënjeshtarë, andaj ishin nga të shkatërruarit.');
INSERT INTO "hasan_efendi" VALUES(23,49,'23:49','Në të vërtetë, Ne i dhamë Librin (Teuratin) Musait, që ata të udhëzohen në rrugë të drejtë.');
INSERT INTO "hasan_efendi" VALUES(23,50,'23:50','Ne e bëmë birin e Merjemes dhe nënën e tij mrekulli për njerëzimin dhe i strehuam rrëzë një kodre të qetë me burime uji.');
INSERT INTO "hasan_efendi" VALUES(23,51,'23:51','O të dërguar! Hani nga të lejuarat dhe bëni vepra të mira! Vërtet, Unë e di mirë çfarë bëni ju!');
INSERT INTO "hasan_efendi" VALUES(23,52,'23:52','Vërtet, kjo feja juaj është një fe e vetme, ndërsa Unë jam Zoti juaj, andaj frikësohuni prej Meje!');
INSERT INTO "hasan_efendi" VALUES(23,53,'23:53','Por ata (popuj ku u çuan të dërguarit) u ndanë në grupe në çështje të fesë së tyre. Çdo grup ishte i kënaqur me atë që kishte,');
INSERT INTO "hasan_efendi" VALUES(23,54,'23:54','andaj, lëri ata në padijen e tyre për njëfarë kohe!');
INSERT INTO "hasan_efendi" VALUES(23,55,'23:55','A mos mendojnë ata që Ne, duke u ardhur në ndihmë me pasuri dhe djem,');
INSERT INTO "hasan_efendi" VALUES(23,56,'23:56','po nxitojmë drejt tyre me të mira?! Kurrsesi, por ata nuk janë të vetëdijshëm (për këtë).');
INSERT INTO "hasan_efendi" VALUES(23,57,'23:57','Me të vërtetë, ata që dridhen nga frika e Zotit të tyre,');
INSERT INTO "hasan_efendi" VALUES(23,58,'23:58','që besojnë në shpalljet e Zotit të tyre,');
INSERT INTO "hasan_efendi" VALUES(23,59,'23:59','që nuk i shoqërojnë asgjë (në adhurim) Zotit të tyre,');
INSERT INTO "hasan_efendi" VALUES(23,60,'23:60','dhe që, kur japin atë që japin, zemrat e tyre i kanë plot frikë, ngaqë do të kthehen te Zoti i tyre,');
INSERT INTO "hasan_efendi" VALUES(23,61,'23:61','ata nxitojnë të bëjnë vepra të mira dhe për këto punë janë të parët.');
INSERT INTO "hasan_efendi" VALUES(23,62,'23:62','Ne nuk ngarkojmë askënd përtej mundësive të tij; te Ne është Libri që flet të vërtetën dhe askujt nuk do t’i bëhet padrejtësi.');
INSERT INTO "hasan_efendi" VALUES(23,63,'23:63','Por zemrat e tyre janë mospërfillëse ndaj këtij (Kurani). Ata kanë punë të tjera më të këqija se këto e të cilat po vazhdojnë t’i bëjnë.');
INSERT INTO "hasan_efendi" VALUES(23,64,'23:64','Por kur i dënuam pasanikët e tyre (në betejën e Bedrit), atëherë ata vajtonin.');
INSERT INTO "hasan_efendi" VALUES(23,65,'23:65','(U thuhet): “Mos u lutni sot! Ju nuk do të keni ndihmë prej Nesh.');
INSERT INTO "hasan_efendi" VALUES(23,66,'23:66','Juve ju janë lexuar vargjet e Mia, por ju tërhiqeshit mbrapa,');
INSERT INTO "hasan_efendi" VALUES(23,67,'23:67','me kryelartësi! Ju flisnit kotësi për të (Kuranin), si dikush që rrëfen natën përralla.”');
INSERT INTO "hasan_efendi" VALUES(23,68,'23:68','Vallë, a nuk kanë medituar ata mbi Fjalën (Kuranin) apo atyre u ka ardhur ajo që nuk u kishte ardhur të parëve të tyre?');
INSERT INTO "hasan_efendi" VALUES(23,69,'23:69','Apo ata nuk e kanë njohur të Dërguarin e tyre, prandaj e mohojnë atë?');
INSERT INTO "hasan_efendi" VALUES(23,70,'23:70','Apo thonë: “Ai është i çmendur”? Jo, ai ua sjell të Vërtetën atyre, por shumica prej tyre e urrejnë të Vërtetën.');
INSERT INTO "hasan_efendi" VALUES(23,71,'23:71','Sikur e Vërteta të ishte sipas dëshirave të tyre, atëherë do të shkatërroheshin qiejt dhe Toka dhe gjithçka që gjendet në to. Ne u kemi dërguar atyre Këshillën (Kuranin), por ata shmangen nga ajo.');
INSERT INTO "hasan_efendi" VALUES(23,72,'23:72','A po kërkon shpërblim prej tyre? Shpërblimi i Zotit tënd është më i mirë. Ai është më i miri i furnizuesve.');
INSERT INTO "hasan_efendi" VALUES(23,73,'23:73','Ti i fton ata në rrugën e drejtë,');
INSERT INTO "hasan_efendi" VALUES(23,74,'23:74','por ata që nuk besojnë jetën tjetër, shmangen nga rruga e drejtë.');
INSERT INTO "hasan_efendi" VALUES(23,75,'23:75','Sikur Ne t’i mëshironim ata dhe t’ua largonim fatkeqësitë, sërish ata do të endeshin të hutuar në humbjen dhe mosbesimin e tyre.');
INSERT INTO "hasan_efendi" VALUES(23,76,'23:76','Ne i dënuam ata njëherë, por ata nuk iu përulën Zotit të tyre e as nuk i drejtuan lutje.');
INSERT INTO "hasan_efendi" VALUES(23,77,'23:77','Dhe, kur u hapim atyre derën e një dënimi të madh, atëherë zhyten në dëshpërim.');
INSERT INTO "hasan_efendi" VALUES(23,78,'23:78','Është Ai që jua ka krijuar dëgjimin, shikimin dhe zemrën. Eh, sa pak që e falënderoni ju!');
INSERT INTO "hasan_efendi" VALUES(23,79,'23:79','Është Ai që ju ka sjellë në Tokë dhe para Tij do të mblidheni të gjithë.');
INSERT INTO "hasan_efendi" VALUES(23,80,'23:80','Është Ai që jep jetë dhe vdekje dhe Atij i përket ndërrimi i ditës dhe i natës. Vallë, a nuk kuptoni?');
INSERT INTO "hasan_efendi" VALUES(23,81,'23:81','Por ata thonë, ashtu siç kanë thënë të mëparshmit.');
INSERT INTO "hasan_efendi" VALUES(23,82,'23:82','Ata pyesin: “Vallë, pasi të vdesim e të bëhemi eshtra e dhé, vërtet do të ringjallemi?!');
INSERT INTO "hasan_efendi" VALUES(23,83,'23:83','Kjo na është premtuar neve dhe të parëve tanë, por këto janë vetëm përralla të popujve të lashtë.”');
INSERT INTO "hasan_efendi" VALUES(23,84,'23:84','Thuaj: “E kujt është Toka dhe gjithçka që gjendet në të, nëse e dini?”');
INSERT INTO "hasan_efendi" VALUES(23,85,'23:85','Ata do të thonë: “Të Allahut!” E ti thuaju: “Vallë, a nuk po ia vini veshin?”');
INSERT INTO "hasan_efendi" VALUES(23,86,'23:86','Thuaj: “Kush është Zoti i shtatë qiejve dhe Zoti i Fronit Madhështor?”');
INSERT INTO "hasan_efendi" VALUES(23,87,'23:87','Ata do të thonë: “Allahu!” E ti thuaju: “Vallë, a nuk keni frikë ju?”');
INSERT INTO "hasan_efendi" VALUES(23,88,'23:88','Thuaj: “Kush është Ai që ka në dorë pushtetin mbi çdo gjë, i Cili mbron, por prej Tij nuk mund të mbrohet askush, nëse e dini?”');
INSERT INTO "hasan_efendi" VALUES(23,89,'23:89','Ata thonë: “Allahu.” E ti thuaju: “Atëherë, përse magjepseni”?! ');
INSERT INTO "hasan_efendi" VALUES(23,90,'23:90','Ja! Ne ua kemi zbuluar atyre të Vërtetën, por ata janë gënjeshtarë.');
INSERT INTO "hasan_efendi" VALUES(23,91,'23:91','Allahu nuk ka zgjedhur për Vete ndonjë bir dhe përveç Atij s’ka asnjë zot tjetër, përndryshe çdo zot do të merrte atë që ka krijuar dhe do të ngriheshin njëri mbi tjetrin. Qoftë i lartësuar Ai nga shpifjet e tyre! ');
INSERT INTO "hasan_efendi" VALUES(23,92,'23:92','Ai di të padukshmen dhe atë që shihet. Ai është shumë lart mbi zotat që ia quajnë Atij për shok!');
INSERT INTO "hasan_efendi" VALUES(23,93,'23:93','Thuaj (o Muhamed): “O Zoti im, sikur të ma tregoje dënimin që u ke premtuar atyre,');
INSERT INTO "hasan_efendi" VALUES(23,94,'23:94','atëherë, o Zoti im, mos më lër mua me popullin keqbërës!”');
INSERT INTO "hasan_efendi" VALUES(23,95,'23:95','Dhe, me të vërtetë, Ne jemi në gjendje të të tregojmë ty, çfarë u kemi premtuar atyre.');
INSERT INTO "hasan_efendi" VALUES(23,96,'23:96','Largoje të keqen me atë që është më e mira. Ne i dimë mirë shpifjet e tyre.');
INSERT INTO "hasan_efendi" VALUES(23,97,'23:97','Dhe thuaj: “O Zoti im, kërkoj të më mbrosh nga nxitjet e djajve,');
INSERT INTO "hasan_efendi" VALUES(23,98,'23:98','dhe tek Ti mbështetem, o Zoti im, që të mos i kem pranë meje.”');
INSERT INTO "hasan_efendi" VALUES(23,99,'23:99','Kur i vjen vdekja ndonjërit prej tyre (jobesimtarëve), ai thotë: “O Zoti im, më kthe,');
INSERT INTO "hasan_efendi" VALUES(23,100,'23:100','që të bëj vepra të mira në botën që kam lënë!” Kurrsesi! Me të vërtetë, kjo është një fjalë të cilën ai (kot) e thotë! Prapa (vdekjes së) tyre do të ketë një (kohë) ndarëse , deri në ditën kur do të ringjallen.');
INSERT INTO "hasan_efendi" VALUES(23,101,'23:101','Kur të fryhet në Sur (Ditën e Ringjalljes), atëherë, midis tyre nuk do të ketë lidhje farefisnore dhe ata as që do të pyesin për njëri-tjetrin.');
INSERT INTO "hasan_efendi" VALUES(23,102,'23:102','Ata, që u rëndohet peshorja (e punëve të mira), janë të shpëtuar.');
INSERT INTO "hasan_efendi" VALUES(23,103,'23:103','Sa për ata që do të kenë peshore të lehta, mu ata do të jenë (njerëzit) që kanë humbur vetveten, duke qëndruar përherë në Xhehenem.');
INSERT INTO "hasan_efendi" VALUES(23,104,'23:104','Zjarri do t’ua përcëllojë fytyrat dhe buzët (aq sa do t’u duken dhëmbët).');
INSERT INTO "hasan_efendi" VALUES(23,105,'23:105','(Dhe atyre do t’u thuhet): “Vallë, a nuk ju lexoheshin Shpalljet Tona dhe i quanit ato gënjeshtra?!”');
INSERT INTO "hasan_efendi" VALUES(23,106,'23:106','Ata do të thonë: “O Zoti ynë, ne na mposhti e keqja (tekat dhe dëshira) jonë dhe ishim një popull i humbur.');
INSERT INTO "hasan_efendi" VALUES(23,107,'23:107','O Zoti ynë, nxirrna prej Xhehenemit e, nëse kthehemi prapë tek e keqja, atëherë jemi keqbërës të vërtetë.”');
INSERT INTO "hasan_efendi" VALUES(23,108,'23:108','Ai do t’u thotë: “Rrini aty të përbuzur dhe mos Më flisni asgjë!”');
INSERT INTO "hasan_efendi" VALUES(23,109,'23:109','Mes robërve të Mi, ishte një grup i cili thoshte: “O Zoti ynë, ne kemi besuar, andaj na fal dhe na mëshiro! Ti je më i miri i mëshiruesve!”');
INSERT INTO "hasan_efendi" VALUES(23,110,'23:110','Por ju (o jobesimtarë) i keni talluar ata (aq shumë), sa ju bënë të më harronit Mua së përmenduri, e përherë i përqeshnit ata.');
INSERT INTO "hasan_efendi" VALUES(23,111,'23:111','Unë i kam shpërblyer ata sot për durimin e tyre e ata, me të vërtetë, janë të shpëtuar.');
INSERT INTO "hasan_efendi" VALUES(23,112,'23:112','Ai do t’u thotë (jobesimtarëve): “Sa vjet keni qëndruar në Tokë?”');
INSERT INTO "hasan_efendi" VALUES(23,113,'23:113','Ata do të përgjigjen: “Kemi qëndruar një ditë ose një pjesë dite. Pyesni ata që i kanë numëruar!”');
INSERT INTO "hasan_efendi" VALUES(23,114,'23:114','Ai do të thotë: “Keni qëndruar shumë pak. Eh, sikur ta kishit ditur!');
INSERT INTO "hasan_efendi" VALUES(23,115,'23:115','Mos vallë, keni menduar që Ne ju kemi krijuar kot dhe që nuk do të ktheheshit te Ne (për t’ju gjykuar)?!”');
INSERT INTO "hasan_efendi" VALUES(23,116,'23:116','I lartësuar është Allahu, Sundimtari i Vërtetë! S’ka zot tjetër të vërtetë, përveç Atij, Zotit të Fronit Madhështor!');
INSERT INTO "hasan_efendi" VALUES(23,117,'23:117','Ai që pos Allahut, adhuron zot tjetër, duke mos pasur kurrfarë prove për këtë, me siguri do të japë llogari vetëm te Zoti i tij. Me të vërtetë, nuk ka shpëtim për mohuesit.');
INSERT INTO "hasan_efendi" VALUES(23,118,'23:118','Dhe thuaj (o Muhamed!): “O Zoti im, fal dhe mëshiro, se Ti je më i miri i mëshiruesve!”');
INSERT INTO "hasan_efendi" VALUES(24,1,'24:1','Kjo është një sure, të cilën Ne e kemi zbritur dhe urdhëruar, duke shpallur në të argumente të qarta, që ju t’ua vini veshin.');
INSERT INTO "hasan_efendi" VALUES(24,2,'24:2','Laviren dhe lavirin – ta rrihni çdonjërin prej tyre me nga njëqind goditje. Të mos ju pengojë kurrfarë mëshire për të kryer detyrën e fesë së Allahut, nëse besoni Allahun dhe Ditën e Fundit. Le ta shikojnë dënimin e tyre një grup besimtarësh!');
INSERT INTO "hasan_efendi" VALUES(24,3,'24:3','Laviri mund të martohet vetëm me laviren apo idhujtaren, kurse laviren mund ta marrë vetëm laviri apo idhujtari. Këto janë të ndaluara për besimtarët.');
INSERT INTO "hasan_efendi" VALUES(24,4,'24:4','Ata që i akuzojnë gratë e ndershme (për imoralitet), e pastaj nuk sjellin katër dëshmitarë, t’i rrihni me tetëdhjetë goditje dhe të mos ua pranoni më kurrë dëshminë; këta janë njerëz ngatërrestarë.');
INSERT INTO "hasan_efendi" VALUES(24,5,'24:5','Përjashtim bëjnë ata që, pas kësaj, pendohen dhe përmirësohen. Allahu është vërtet Falës dhe Mëshirëplotë.');
INSERT INTO "hasan_efendi" VALUES(24,6,'24:6','Nëse një burrë e akuzon gruan e tij pa pasur dëshmitarë të tjerë përveç vetes, ai duhet të betohet në Allahun katër herë, se thotë të vërtetën,');
INSERT INTO "hasan_efendi" VALUES(24,7,'24:7','dhe herën e pestë të kërkojë mallkimin e Allahut ndaj vetes, nëse ka gënjyer.');
INSERT INTO "hasan_efendi" VALUES(24,8,'24:8','Ndërsa gruaja do të kursehet nga dënimi, nëse betohet për Allahun katër herë, se i shoqi është gënjeshtar');
INSERT INTO "hasan_efendi" VALUES(24,9,'24:9','dhe, herën e pestë, të kërkojë zemërimin e Allahut ndaj vetes, nëse i shoqi ka thënë të vërtetën.');
INSERT INTO "hasan_efendi" VALUES(24,10,'24:10','Sikur të mos ishin mirësia dhe mëshira e Allahut ndaj jush, si dhe fakti se Ai është Pranues i pendimit e i Urtë (dënimi i Tij do të shpejtonte)!');
INSERT INTO "hasan_efendi" VALUES(24,11,'24:11','Me të vërtetë, ata që shpifën , janë një skotë prej jush. Mos e quani atë si të keqe për ju. Përkundrazi, kjo është mirë për ju. Çdonjëri prej tyre do të dënohet për atë që ka bërë, kurse atë që ka shpifur më shumë, e pret një dënim i madh.');
INSERT INTO "hasan_efendi" VALUES(24,12,'24:12','Kur e dëgjuat këtë përgojim, përse besimtarët dhe besimtaret nuk menduan mirë për njëri-tjetrin dhe të thoshin: “Kjo është shpifje e qartë”?!');
INSERT INTO "hasan_efendi" VALUES(24,13,'24:13','Pse nuk sollën për këtë katër dëshmitarë? Meqë nuk sollën dëshmitarë, ata janë gënjeshtarë para Allahut.');
INSERT INTO "hasan_efendi" VALUES(24,14,'24:14','Sikur të mos ishte mirësia e Allahut ndaj jush dhe mëshira e Tij, në këtë botë dhe në tjetrën, me siguri do t’ju godiste dënim i madh për atë që bëtë.');
INSERT INTO "hasan_efendi" VALUES(24,15,'24:15','Ju përcillnit me gjuhët tuaja dhe flisnit me gojët tuaja diçka, për të cilën nuk kishit dijeni. Ju këtë e merrnit për gjë të lehtë, por para Allahut kjo ishte një shkelje e madhe.');
INSERT INTO "hasan_efendi" VALUES(24,16,'24:16','Përse kur e dëgjuat përgojimin, nuk thatë: “Nuk ka hije për ne të flasim për këtë. Ti qofsh lavdëruar (o Zot)! Kjo është shpifje e madhe!”');
INSERT INTO "hasan_efendi" VALUES(24,17,'24:17','Allahu ju paralajmëron, që të mos përsëritni kurrë më diçka të ngjashme, nëse jeni besimtarë.');
INSERT INTO "hasan_efendi" VALUES(24,18,'24:18','Allahu ju shpjegon shpalljet; se Allahu është i Urtë, i Gjithëdijshëm.');
INSERT INTO "hasan_efendi" VALUES(24,19,'24:19','Ata që duan të përhapen shpifjet e turpshme (ose imoraliteti) ndër besimtarët, i pret një dënim i dhembshëm në këtë botë dhe në tjetrën; Allahu i di të gjitha e ju nuk i dini.');
INSERT INTO "hasan_efendi" VALUES(24,20,'24:20','Sikur të mos ishin mirësia dhe mëshira e Allahut ndaj jush, si dhe fakti se Ai është i Butë e Mëshirues (dënimi i Tij do të shpejtonte)!');
INSERT INTO "hasan_efendi" VALUES(24,21,'24:21','O besimtarë! Mos ndiqni gjurmët e djallit! Kush ndjek gjurmët e djallit, do të nxitet nga ai të bëjë vepra të këqija e të shëmtuara. Sikur të mos ishte mirësia e Allahut ndaj jush dhe mëshira e Tij, askush nga ju nuk do të ishte pastruar kurrë (prej gjynaheve); Allahu pastron atë që dëshiron Vetë. Allahu i dëgjon të gjitha dhe i di të gjitha.');
INSERT INTO "hasan_efendi" VALUES(24,22,'24:22','Njerëzit e ndershëm dhe të pasur ndër ju të mos betohen se nuk do t’u japin të afërmve, të varfërve dhe të mërguarve në rrugën e Allahut; le t’i falin ata dhe të mos ua marrin për keq! Vallë, a nuk doni ju që t’ju falë Allahu? Allahu është Falës e Mëshirëplotë.');
INSERT INTO "hasan_efendi" VALUES(24,23,'24:23','Vërtet, ata që përgojojnë gratë e ndershme, zemërdëlira e besimtare, janë të mallkuar në këtë botë dhe në tjetrën; për ata ka dënim të madh.');
INSERT INTO "hasan_efendi" VALUES(24,24,'24:24','Atë Ditë që do të dëshmojnë kundër tyre gjuhët, duart dhe këmbët e tyre për çfarë kanë bërë,');
INSERT INTO "hasan_efendi" VALUES(24,25,'24:25','Allahu do t’ju shpaguajë dënimin e merituar dhe ata do ta marrin vesh se Allahu është e Vërteta e qartë.');
INSERT INTO "hasan_efendi" VALUES(24,26,'24:26','Gratë e pandershme janë për burrat e pandershëm dhe burrat e pandershëm janë për gratë e pandershme. Gratë e ndershme janë për burra të ndershëm dhe burrat e ndershëm janë për gra të ndershme. Ata janë të pastër nga shpifjet që thuhen (kundër tyre) dhe do të falen e do të kenë një shpërblim bujar (në Xhenet).');
INSERT INTO "hasan_efendi" VALUES(24,27,'24:27','O besimtarë! Mos hyni në shtëpitë e huaja pa marrë leje e pa i përshëndetur banuesit e tyre. Kjo është më e mira për ju. Ndoshta do t’ia vini veshin!');
INSERT INTO "hasan_efendi" VALUES(24,28,'24:28','Nëse atje nuk gjeni askënd, mos hyni derisa t’ju lejohet e, në qoftë se ju thuhet “Kthehuni”, atëherë kthehuni! Kjo është më e mirë për ju. Allahu e di se çfarë bëni ju.');
INSERT INTO "hasan_efendi" VALUES(24,29,'24:29','Nuk është gjynah për ju, nëse hyni në godinat e pabanuara, të cilat ju shërbejnë për diçka. Allahu e di ç’tregoni ju haptazi e çfarë fshihni.');
INSERT INTO "hasan_efendi" VALUES(24,30,'24:30','Thuaju besimtarëve që të ulin shikimet e tyre (nga e ndaluara) dhe ta ruajnë nderin e tyre (nga marrëdhëniet e jashtëligjshme)! Kjo është më mirë për ta! Me të vërtetë, Allahu është i Dijshëm për atë që bëjnë ata.');
INSERT INTO "hasan_efendi" VALUES(24,31,'24:31','Thuaju besimtareve që të ulin shikimet e tyre (nga e ndaluara), ta ruajnë nderin e tyre (nga marrëdhëniet e jashtëligjshme) dhe të mos i shfaqin stolitë e tyre , përveç atyre që janë të dukshme. Le t’i mbulojnë kraharorët me mbulesat e tyre (të kokës) dhe të mos ua shfaqin stolitë e tyre, përveçse bashkëshortëve të tyre ose baballarëve të tyre, ose vjehërrve të tyre, ose bijve të tyre, ose djemve të bashkëshortëve të tyre, ose vëllezërve apo djemve të vëllezërve të tyre, ose djemve të motrave a grave të tyre, ose atyre që i kanë nën zotërim (si skllave), ose shërbëtorëve që nuk kanë epsh ndaj femrave apo fëmijëve që nuk i dinë ngacmimet e femrave. Dhe të mos i rrahin këmbët (për tokë), në mënyrë që të mos duken stolitë e tyre të fshehta. Të gjithë ju, o besimtarë, kthehuni tek Allahu të penduar, që të arrini shpëtimin! ');
INSERT INTO "hasan_efendi" VALUES(24,32,'24:32','I martoni të pamartuarit dhe të pamartuarat ndër ju, si dhe skllevërit dhe skllavet tuaja që janë besimtarë të ndershëm! Nëse janë të varfër, Allahu, do t’i begatojë ata me dhuntinë e Tij; Allahu është i Gjerë në mirësi dhe i Gjithëdijshëm.');
INSERT INTO "hasan_efendi" VALUES(24,33,'24:33','Ata që nuk kanë mundësi për martesë, le të përmbajnë veten, derisa Allahu t’i begatojë me dhuntinë e Tij. Ata skllevër që gjenden në pushtetin tuaj e që kërkojnë të lirohen me shpërblim dhe ju kërkojnë aktlirimin, ua jepni e, nëse e dini që janë të mirë, jepuni diçka nga pasuria juaj, të cilën ju ka dhuruar Allahu. Mos i detyroni skllavet tuaja të bëjnë kurvëri, që të arrini ju dobi të përkohshme të kësaj bote, nëse ato dëshirojnë jetë të ndershme. Nëse ndokush i detyron ato, Allahu do të jetë Falës dhe Mëshirëplotë për to.');
INSERT INTO "hasan_efendi" VALUES(24,34,'24:34','Ne ju kemi dhënë juve shpallje të qarta, shembuj nga jeta e atyre që kanë kaluar para jush dhe këshilla për ata që i frikësohen Allahut.');
INSERT INTO "hasan_efendi" VALUES(24,35,'24:35','Allahu është Drita e qiejve dhe e Tokës. Drita e Tij shëmbëllen me atë të një kamareje, në të cilën ndodhet një llambë. Llamba është në një kristal, kurse kristali është si një yll i shndritshëm. Ajo (llamba) ndizet nga druri i bekuar i ullirit, as lindor, as perëndimor, ndërsa vaji i saj shkëlqen edhe pa e prekur zjarri. Dritë sipër dritës. Allahu udhëzon drejt dritës së Tij kë të dojë dhe paraqet shembujt për njerëzit. Allahu është i Dijshëm për çdo gjë.');
INSERT INTO "hasan_efendi" VALUES(24,36,'24:36','Në xhamitë, që Allahu ka urdhëruar të ngrihen e të përmendet aty emri i Tij, Atë e lavdërojnë në mëngjes dhe mbrëmje,');
INSERT INTO "hasan_efendi" VALUES(24,37,'24:37','njerëz, të cilët shitblerja nuk i pengon për të përmendur Allahut dhe të cilët falin namazin dhe japin zekatin dhe të cilët kanë frikë nga Dita (e Kiametit), kur zemrat dhe shikimet do të jenë të shqetësuara.');
INSERT INTO "hasan_efendi" VALUES(24,38,'24:38','(Këta shpresojnë) që Allahu t’i shpërblejë sipas veprave më të mira që kanë bërë e t’ua shtojë dhuntinë e Tij. Allahu i jep kujt të dojë, pa llogari.');
INSERT INTO "hasan_efendi" VALUES(24,39,'24:39','Sa për ata që nuk besojnë, veprat e tyre janë si mirazhi në shkretëtirë e të cilin i etshmi e kujton për ujë, por, kur arrin tek ai vend, nuk gjen asgjë. Aty do të hasë në dënimin e Allahut, i Cili ia paguan llogarinë e tij të plotë, se Allahu është i shpejtë në llogari.');
INSERT INTO "hasan_efendi" VALUES(24,40,'24:40','Ose janë si errësirat në detin e thellë, të cilat mbulohen nga dallgët që ngrihen njëra mbi tjetrën, duke pasur sipër tyre retë: shtresa errësire njëra mbi tjetrën, aq sa kur e nxjerr dorën e vet, njeriu pothuajse nuk e sheh fare atë. Ai, të cilit Allahu nuk i jep dritë, nuk do të gjejë më dritë.');
INSERT INTO "hasan_efendi" VALUES(24,41,'24:41','A nuk e vëren ti, se Allahun e lavdëron çdo krijesë që ndodhet në qiej dhe në Tokë, madje edhe shpezët krahëhapur?! Çdokush e di se si t’i falet e ta lavdërojë. Allahu e di mirë çfarë bëjnë ata.');
INSERT INTO "hasan_efendi" VALUES(24,42,'24:42','Vetëm Allahut i përket sundimi i qiejve dhe i Tokës dhe tek Ai kthehet çdo gjë.');
INSERT INTO "hasan_efendi" VALUES(24,43,'24:43','A nuk e vëren ti se Allahu i shtyn retë ngadalë e pastaj i bashkon e i grumbullon njërën mbi tjetrën, derisa të shohësh shiun se si del prej tyre dhe bie nga qielli?! Ai, prej reve të mëdha si mali, lëshon breshrin e me të godet kë të dojë, kurse e shmang prej kujt të dojë. Shkëlqimi i vetëtimës së tij gati t’i merr sytë.');
INSERT INTO "hasan_efendi" VALUES(24,44,'24:44','Allahu ndërron ditën dhe natën. Me të vërtetë, në këto (dukuri natyrore) ka mësime për ata që kanë mend.');
INSERT INTO "hasan_efendi" VALUES(24,45,'24:45','Allahu ka krijuar çdo lloj kafshe prej ujit; disa nga ato ecin me barkun e tyre, një pjesë ecin me dy këmbë e disa prej tyre me katër këmbë. Allahu krijon çfarë të dojë. Me të vërtetë, Allahu është i Fuqishëm për çdo gjë.');
INSERT INTO "hasan_efendi" VALUES(24,46,'24:46','Tashmë Ne kemi zbritur shpallje të qarta; Allahu udhëzon kë të dojë në rrugën e drejtë.');
INSERT INTO "hasan_efendi" VALUES(24,47,'24:47','Ata (hipokritët) thonë: “Ne besojmë në Allahun dhe në të Dërguarin dhe (u) bindemi (atyre).” Pastaj disa prej tyre shmangen, sepse nuk janë besimtarë aspak.');
INSERT INTO "hasan_efendi" VALUES(24,48,'24:48','Dhe, kur thirren tek Allahu (Libri i Tij) dhe i Dërguari i Tij, që ai të gjykojë midis tyre, një grup nga ata kthejnë kurrizin.');
INSERT INTO "hasan_efendi" VALUES(24,49,'24:49','Por, nëse e drejta është në anën e tyre, ata vijnë përulësisht.');
INSERT INTO "hasan_efendi" VALUES(24,50,'24:50','A mos kanë sëmundje në zemrat e tyre apo dyshojnë? Apo kanë frikë se mos Allahu dhe i Dërguari i Tij do t’u bëjnë padrejtësi?! Jo, por pikërisht ata janë të padrejtët.');
INSERT INTO "hasan_efendi" VALUES(24,51,'24:51','Ndërsa përgjigjja e vetme e besimtarëve, kur thirren tek Allahu dhe i Dërguari i Tij, që ai të gjykojë midis tyre, është: “Dëgjojmë dhe bindemi”. Pikërisht këta janë të shpëtuarit.');
INSERT INTO "hasan_efendi" VALUES(24,52,'24:52','Ata që i binden Allahut dhe të Dërguarit të Tij, që kanë frikë nga Allahu e i përkushtohen Atij, do të jenë të fituarit e vërtetë.');
INSERT INTO "hasan_efendi" VALUES(24,53,'24:53','Ata (hipokritët) betohen për Allahun me deklarata solemne, se po t’i urdhërosh ata (o Muhamed), që të shkojnë në luftë - ata me siguri do të shkojnë. Thuaj: “Mos u betoni! Ju kërkohet bindja e jo betimi. Vërtet, Allahu e di me hollësi gjithçka që ju bëni.”');
INSERT INTO "hasan_efendi" VALUES(24,54,'24:54','Thuaj: “Bindjuni Allahut dhe bindjuni të Dërguarit!” Nëse shmangeni, (ta dini se) ai është përgjegjës për detyrën e tij, ndërsa ju për atë që jeni ngarkuar. E, nëse i bindeni atij, do të jeni në rrugën e drejtë, kurse i Dërguari është i detyruar vetëm që të përcjellë qartë (Mesazhin Hyjnor).');
INSERT INTO "hasan_efendi" VALUES(24,55,'24:55','Allahu u ka premtuar atyre prej jush, që besojnë dhe që bëjnë vepra të mira, se do t’i bëjë mëkëmbës në Tokë , ashtu si i ka bërë ata para tyre dhe që do t’ua forcojë fenë e tyre, me të cilën Ai është i kënaqur dhe që frikën do t’ua shndërrojë në siguri. Le të më adhurojnë Mua e të mos Më shoqërojnë asgjë (në adhurim). Sa për ata që, pas kësaj, mohojnë (besimin), pikërisht ata janë të mbrapshtët.');
INSERT INTO "hasan_efendi" VALUES(24,56,'24:56','Falni namazin, jepni zekatin dhe bindjuni të Dërguarit, për të fituar mëshirën e Allahut!');
INSERT INTO "hasan_efendi" VALUES(24,57,'24:57','Kurrsesi mos mendo se mohuesit do të shpëtojnë në Tokë! Streha e tyre është zjarri; eh, sa strehë e shëmtuar që është ai!');
INSERT INTO "hasan_efendi" VALUES(24,58,'24:58','O besimtarë! Skllevërit dhe fëmijët tuaj që s’kanë arritur pubertetin, le t’ju kërkojnë leje (për të hyrë) në tri kohë: para namazit të mëngjesit, kur i zhvishni rrobat në mesditë dhe pas namazit të jacisë. Këto janë tri kohë kur hiqni rrobat. Nuk ka gjynah as për ju e as për ata që, pas këtyre rasteve, të vizitoni njëri-tjetrin (pa kërkuar leje për të hyrë). Kështu jua shpjegon Allahu shpalljet juve! Allahu është i Urtë dhe i Gjithëdijshëm.');
INSERT INTO "hasan_efendi" VALUES(24,59,'24:59','Kur fëmijët tuaj të arrijnë moshën e pubertetit, le të kërkojnë leje, ashtu siç kanë kërkuar leje ata para këtyre. Kështu jua shpjegon Allahu juve shpalljet e Tij! Allahu është i Urtë dhe i Gjithëdijshëm.');
INSERT INTO "hasan_efendi" VALUES(24,60,'24:60','Gratë e vjetra, që nuk shpresojnë më të martohen, nuk është gjynah ta heqin mbulesën e jashtme, por jo duke i treguar stolitë e tyre. E, nëse përmbahen, është edhe më mirë për ato. Allahu i dëgjon dhe i di të gjitha.');
INSERT INTO "hasan_efendi" VALUES(24,61,'24:61','Nuk është gjynah për të verbrin, as për të çalin, as për të sëmurin dhe as për ju që të hani në shtëpinë tuaj, ose në shtëpitë e baballarëve tuaj, ose në shtëpitë e nënave tuaja, ose në shtëpitë e vëllezërve tuaj, ose në shtëpitë e motrave tuaja, ose në shtëpitë e xhaxhallarëve tuaj, ose në shtëpitë e hallave tuaja, ose në shtëpitë e dajave tuaj, ose në shtëpitë e tezeve tuaja, ose tek ata, çelësat e të cilëve u janë lënë juve, ose te miqtë. Nuk është gjynah për ju që të hani bashkërisht apo veçmas. Kur të hyni në shtëpi, të përshëndetni njëri-tjetrin me përshëndetje të mirë e të bukur nga Allahu. Kështu jua shpjegon Allahu juve shpalljet që të kuptoni. ');
INSERT INTO "hasan_efendi" VALUES(24,62,'24:62','Besimtarë të vërtetë janë vetëm ata që besojnë Allahun dhe të Dërguarin e Tij dhe të cilët, kur gjenden me atë (të Dërguarin) në një punë të përbashkët, nuk largohen para se të kërkojnë leje prej tij. Me të vërtetë, ata që kërkojnë leje prej teje, pa dyshim, besojnë në Allahun dhe të Dërguarin e Tij. Kur të kërkojnë leje prej teje për ndonjë punë të tyre, jepi leje kujt të duash dhe kërko për ata falje nga Allahu. Me të vërtetë, Allahu është Falës e Mëshirëplotë.');
INSERT INTO "hasan_efendi" VALUES(24,63,'24:63','Mos ia drejtoni thirrjen të Dërguarit, njësoj si thirrjen që ia bëni njëri-tjetrit; Allahu, me siguri, i di ata që shkëputen prej jush tinëzisht. Le të frikësohen ata që kundërshtojnë urdhrin e tij, që të mos i arrijë ndonjë sprovë ose që të mos i godasë një dënim i dhembshëm.');
INSERT INTO "hasan_efendi" VALUES(24,64,'24:64','Pa dyshim, Allahut i përket gjithçka që gjendet në qiej dhe në Tokë. Ai e di se çfarë bëni. Dhe Ditën kur ata do të kthehen tek Ai, do t’i njoftojë për veprat që kanë bërë. Allahu është i Dijshëm për çdo gjë!');
INSERT INTO "hasan_efendi" VALUES(25,1,'25:1','I lartësuar qoftë Ai që i ka zbritur Dalluesin robit të Vet, që të jetë paralajmërues për botët;');
INSERT INTO "hasan_efendi" VALUES(25,2,'25:2','Sunduesi i qiejve dhe i Tokës, i Cili nuk ka bir e nuk ka ortak në pushtet; i Cili ka krijuar çdo gjë dhe e ka rregulluar si duhet!');
INSERT INTO "hasan_efendi" VALUES(25,3,'25:3','E megjithatë ata adhurojnë në vend të Tij zota, të cilët nuk krijojnë asgjë e që vetë janë të krijuar, që nuk zotërojnë për veten as dëm e as dobi e që nuk janë në gjendje të japin as vdekjen, as jetën e as që të ringjallin.');
INSERT INTO "hasan_efendi" VALUES(25,4,'25:4','Jobesimtarët thonë: “Ky (Kuran) është vetëm gënjeshtër, të cilën e ka trilluar ai me ndihmën e njerëzve të tjerë.” Kjo që thonë ata është e padrejtë dhe shpifje.');
INSERT INTO "hasan_efendi" VALUES(25,5,'25:5','Dhe ata thonë: “Këto janë përralla të popujve të lashtë; të cilat i ka vënë t’i shkruhen e t’i lexohen atij në mëngjes e mbrëmje.”');
INSERT INTO "hasan_efendi" VALUES(25,6,'25:6','Thuaj: “Atë e ka shpallur Ai që di fshehtësitë e qiejve dhe të Tokës. Ai fal shumë dhe është Mëshirues”.');
INSERT INTO "hasan_efendi" VALUES(25,7,'25:7','Gjithashtu ata thonë: “Ç’është ky i dërguar që ha ushqim dhe shëtit nëpër tregje?! Përse nuk i është dërguar atij një engjëll e të bëhet me të paralajmërues?!');
INSERT INTO "hasan_efendi" VALUES(25,8,'25:8','Ose, përse nuk i është zbritur një thesar apo të kishte një kopsht, nga i cili të hante?!” Të padrejtët thonë: “Ju ndiqni vetëm një njeri të magjepsur”!');
INSERT INTO "hasan_efendi" VALUES(25,9,'25:9','Shiko çfarë shembujsh të sjellin e ata (me këtë) kanë humbur e nuk mund të gjejnë rrugën e vërtetë.');
INSERT INTO "hasan_efendi" VALUES(25,10,'25:10','Qoftë i lartësuar Ai, i Cili, nëse do, mund të të japë gjëra më të mira se këto, kopshte nëpër të cilat rrjedhin lumenj dhe të bëjë për ty pallate.');
INSERT INTO "hasan_efendi" VALUES(25,11,'25:11','Jo, ata e quajnë gënjeshtër edhe ardhjen e Orës (së Kiametit), ndërsa Ne kemi përgatitur për ata që e mohojnë Orën zjarr flakërues.');
INSERT INTO "hasan_efendi" VALUES(25,12,'25:12','Kur ai t’i vërë re prej së largu, ata do t’ia dëgjojnë vlimin dhe rënkimin.');
INSERT INTO "hasan_efendi" VALUES(25,13,'25:13','E kur të hidhen në një vend të ngushtë të tij, të lidhur duarsh, do të luten për shkatërrimin (e vet).');
INSERT INTO "hasan_efendi" VALUES(25,14,'25:14','“Mos e kërkoni sot shkatërrimin vetëm një herë, por kërkojeni shumë herë!”');
INSERT INTO "hasan_efendi" VALUES(25,15,'25:15','Thuaj: “A është më i mirë ky apo Xheneti i amshuar, i cili u është premtuar të devotshmëve? Ai do të jetë për ta shpërblim dhe vend kthimi.');
INSERT INTO "hasan_efendi" VALUES(25,16,'25:16','Për ata ka aty çfarë të dëshirojnë e do të rrinë në të përherë. Ky është premtimi i Zotit tënd, i kërkuar (prej jush)”.');
INSERT INTO "hasan_efendi" VALUES(25,17,'25:17','Ditën, kur Ai t’i tubojë idhujtarët bashkë me ata që i adhuronin në vend të Allahut, do t’u thotë (të adhuruarve): “A jeni ju që i keni shmangur këta robër të Mi apo ata vetë kanë humbur rrugën e drejtë?”');
INSERT INTO "hasan_efendi" VALUES(25,18,'25:18','Ata do të përgjigjen: “Lavdia qoftë për Ty! Nuk ishte nevoja që ne të marrim mbrojtës tjetër, përveç Teje, por Ti u dhe këtyre dhe etërve të tyre jetë të begatshme, ndërsa ata i harruan paralajmërimet e Tua dhe u bënë popull i shkatërruar”!');
INSERT INTO "hasan_efendi" VALUES(25,19,'25:19','“Këta e përgënjeshtruan atë që thatë ju e nuk do të munden, as të shmangin dënimin, as të ndihmojnë veten. Kush prej jush ka bërë të këqija, Ne atij do t’i japim të shijojë dënim të madh.”');
INSERT INTO "hasan_efendi" VALUES(25,20,'25:20','Ne edhe para teje kemi nisur vetëm të dërguar, të cilët hanin ushqim dhe shëtisnin nëpër tregje. Ne i kemi bërë disa prej jush të jenë sprovë për të tjerët. A do të jeni të durueshëm ju? Zoti yt i sheh të gjitha.');
INSERT INTO "hasan_efendi" VALUES(25,21,'25:21','Ata, që nuk shpresojnë se do të takohen me Ne, thonë: “Pse nuk na dërgohen neve engjëjt ose të shohim Zotin tonë?” Ata, me të vërtetë, janë treguar kryeneçë dhe kanë kaluar çdo kufi në arrogancë dhe padrejtësi.');
INSERT INTO "hasan_efendi" VALUES(25,22,'25:22','Ditën, kur të shohin engjëjt, për keqbërësit nuk do të ketë lajm të mirë dhe do t’u thuhet: “Nuk do të kaloni dot për matanë!”');
INSERT INTO "hasan_efendi" VALUES(25,23,'25:23','Dhe Ne do t’u qasemi veprave që kanë bërë ata dhe do t’i shndërrojmë në pluhur e hi.');
INSERT INTO "hasan_efendi" VALUES(25,24,'25:24','Ndërsa banorët e Xhenetit atë ditë do të kenë vendbanim më të mirë dhe vendpushim më të bukur.');
INSERT INTO "hasan_efendi" VALUES(25,25,'25:25','Ditën, kur qielli me ré do të hapet e engjëjt do të lëshohen në zbritjen e madhe,');
INSERT INTO "hasan_efendi" VALUES(25,26,'25:26','atë ditë pushteti i vërtetë do t’i përkasë vetëm të Gjithëmëshirshmit. Kjo do të jetë ditë e vështirë për jobesimtarët.');
INSERT INTO "hasan_efendi" VALUES(25,27,'25:27','Atë Ditë keqbërësi do të hajë gishtat e duarve të veta, duke thënë: “Ah, sikur ta kisha marrë rrugën (e drejtë) me të Dërguarin!');
INSERT INTO "hasan_efendi" VALUES(25,28,'25:28','Ah, sikur të mos e kisha bërë filanin mik!');
INSERT INTO "hasan_efendi" VALUES(25,29,'25:29','Ai më ka larguar nga Këshilla (Kurani) që më kishte ardhur!” Në të vërtetë, djalli e braktis gjithmonë njeriun në çastin e nevojës.');
INSERT INTO "hasan_efendi" VALUES(25,30,'25:30','Dhe i Dërguari do të thotë: “O Zoti im! Populli im e shpërfilli Kuranin, si (diçka) të urryer”.');
INSERT INTO "hasan_efendi" VALUES(25,31,'25:31','Dhe, kështu Ne, çdo profeti i kemi kundërvënë një armik nga keqbërësit. Por ty të mjafton Zoti yt si udhërrëfyes dhe ndihmës!');
INSERT INTO "hasan_efendi" VALUES(25,32,'25:32','Jobesimtarët thonë: “Përse Kurani nuk i është shpallur i tëri përnjëherë?!” Ne ta kemi shpallur kështu për të forcuar zemrën tënde dhe ta kemi zbuluar qartazi varg pas vargu.');
INSERT INTO "hasan_efendi" VALUES(25,33,'25:33','Sapo ata të të sjellin çfarëdo argumenti, Ne të sjellim të Vërtetën dhe shpjegimin më të mirë.');
INSERT INTO "hasan_efendi" VALUES(25,34,'25:34','Ata, që do të tubohen me fytyrat përmbys në Xhehenem, do të jenë në vendin më të keq dhe më të larguarit prej rrugës së vërtetë.');
INSERT INTO "hasan_efendi" VALUES(25,35,'25:35','Ne i dhamë Musait Librin dhe i bëmë ndihmës vëllanë e tij, Harunin.');
INSERT INTO "hasan_efendi" VALUES(25,36,'25:36','Pastaj (u) thamë (atyre): “Shkoni të dy te populli, i cili i quan të rreme shenjat Tona!” Pastaj e shkatërruam atë popull krejtësisht.');
INSERT INTO "hasan_efendi" VALUES(25,37,'25:37','Popullin e Nuhut, kur i quajti gënjeshtarë të dërguarit, Ne e fundosëm dhe e bëmë mësim për njerëzit. Për keqbërësit kemi përgatitur një dënim të dhembshëm.');
INSERT INTO "hasan_efendi" VALUES(25,38,'25:38','Ne i dënuam fiset Ad dhe Themud, popullin e Ressit, si dhe shumë popujt të tjerë ndërmjet tyre.');
INSERT INTO "hasan_efendi" VALUES(25,39,'25:39','Të gjithëve u sollëm shembuj dhe, më pas, të gjithë i shkatërruam krejtësisht.');
INSERT INTO "hasan_efendi" VALUES(25,40,'25:40','Ata me kohë kanë kaluar pranë vendbanimit që e shkatërroi shiu i tmerrshëm , e vallë, a nuk e kanë parë atë? Jo, ata nuk prisnin të ringjalleshin.');
INSERT INTO "hasan_efendi" VALUES(25,41,'25:41','Sa herë që të shohin ty, të marrin vetëm për tallje: “Vallë, këtë ka sjellë Allahu si të dërguar?!');
INSERT INTO "hasan_efendi" VALUES(25,42,'25:42','Për pak qe duke na shmangur nga hyjnitë tona, po të mos u kishim qëndruar atyre besnikë!” Por, kur të përballen me dënimin, do ta marrin vesh se kush është shmangur më larg rrugës së drejtë.');
INSERT INTO "hasan_efendi" VALUES(25,43,'25:43','A e ke parë ti atë që epshin e vet e ka marrë për zot? A do t’i bëhesh mbrojtës atij?');
INSERT INTO "hasan_efendi" VALUES(25,44,'25:44','A mendon ti se shumica e tyre dëgjojnë dhe kuptojnë? Ata nuk janë tjetër, veçse si bagëti. Madje, ata janë edhe më të shmangur nga rruga e drejtë.');
INSERT INTO "hasan_efendi" VALUES(25,45,'25:45','A nuk e ke parë ti se si Zoti yt e ka zgjatur hijen?! Sikur të kishte dashur, do ta bënte të pandryshueshme? Pastaj Ne e kemi bërë Diellin udhëzues të saj,');
INSERT INTO "hasan_efendi" VALUES(25,46,'25:46','pastaj e tërheqim atë (hijen), dalëngadalë pranë Vetes.');
INSERT INTO "hasan_efendi" VALUES(25,47,'25:47','Dhe është Ai që jua ka bërë natën mbulesë dhe gjumin pushim, kurse ditën jua ka bërë për të lëvizur.');
INSERT INTO "hasan_efendi" VALUES(25,48,'25:48','Është Ai që i dërgon erërat si lajmëtarë të gëzueshëm para mëshirës së Tij, duke lëshuar prej qiellit ujë të pastër,');
INSERT INTO "hasan_efendi" VALUES(25,49,'25:49','që nëpërmjet atij të ngjallim vendin e vdekur dhe t’u japim ujë kafshëve dhe shumë njerëzve që Ne i kemi krijuar.');
INSERT INTO "hasan_efendi" VALUES(25,50,'25:50','Ne e kemi përsëritur (dhuntinë) që të vënë veshin, por shumica e njerëzve kundërshtojnë e janë mosmirënjohës.');
INSERT INTO "hasan_efendi" VALUES(25,51,'25:51','Sikur të donim Ne, do të dërgonim në çdo qytet paralajmërues.');
INSERT INTO "hasan_efendi" VALUES(25,52,'25:52','Mos i dëgjo mohuesit, por luftoji ata me forcë nëpërmjet këtij (Kurani).');
INSERT INTO "hasan_efendi" VALUES(25,53,'25:53','Është Ai që i ka lënë të lirë dy ujëra, - njëri i pijshëm e i ëmbël, kurse tjetri i njelmët e i hidhur, - duke ngritur pengesë e kufi të prerë ndërmjet tyre.');
INSERT INTO "hasan_efendi" VALUES(25,54,'25:54','Është Ai që ka krijuar prej ujit njeriun, e ka vënë në lidhje me fis nga gjaku dhe nga martesa. Zoti yt është i Plotfuqishëm.');
INSERT INTO "hasan_efendi" VALUES(25,55,'25:55','Megjithatë, ata adhurojnë, në vend të Allahut, gjëra që nuk mund t’u sjellin as dobi, as dëm. Jobesimtari është ndihmës (i djallit) kundër Zotit të tij.');
INSERT INTO "hasan_efendi" VALUES(25,56,'25:56','Ne të kemi dërguar ty vetëm si sjellës të lajmit të mirë dhe paralajmërues.');
INSERT INTO "hasan_efendi" VALUES(25,57,'25:57','Thuaj: “Unë nuk kërkoj nga ju kurrfarë shpërblimi, por vetëm që, kush të dëshirojë, le të ndjekë rrugën (që e shpie) drejt Zotit të tij”.');
INSERT INTO "hasan_efendi" VALUES(25,58,'25:58','Mbështetu në të Gjithëjetshmin, i Cili nuk vdes dhe madhëroje Atë me falënderim! Ai i di mjaft mirë gjynahet e robërve të Vet.');
INSERT INTO "hasan_efendi" VALUES(25,59,'25:59','Është Ai, i Cili krijoi qiejt, Tokën dhe gjithçka që gjendet midis tyre për gjashtë ditë e pastaj u ngrit mbi Fron. Ai është i Gjithëmëshirshmi dhe pyet për Atë ata që dinë.');
INSERT INTO "hasan_efendi" VALUES(25,60,'25:60','Kur atyre u thuhet: “Bëjini sexhde të Gjithëmëshirshmit”, ata thonë: “Çfarë është i Gjithëmëshirshmi? Vallë, t’i biem në sexhde atij që na urdhëron ti?” Kjo u shton atyre më shumë largim (prej fesë).');
INSERT INTO "hasan_efendi" VALUES(25,61,'25:61','Qoftë i lartësuar Ai, i Cili e ka stolisur qiellin me yjësi dhe ka vënë në të një Kandil të ndritshëm dhe një Hënë që ndriçon.');
INSERT INTO "hasan_efendi" VALUES(25,62,'25:62','Ai krijoi natën dhe ditën që të vijnë njëra pas tjetrës, si këshillë për ata që duan ta kujtojnë ose duan ta falënderojnë.');
INSERT INTO "hasan_efendi" VALUES(25,63,'25:63','Robërit e të Gjithëmëshirshmit janë ata që ecin thjesht nëpër Tokë dhe, kur të paditurit i sulmojnë me fjalë, ata përgjigjen: “Paqe qoftë!”;');
INSERT INTO "hasan_efendi" VALUES(25,64,'25:64','dhe ata që e kalojnë natën duke iu falur Zotit të tyre në sexhde dhe në këmbë;');
INSERT INTO "hasan_efendi" VALUES(25,65,'25:65','dhe ata që thonë: “O Zoti ynë, largoje prej nesh ndëshkimin e Xhehenemit, sepse, me të vërtetë, dënimi i tij është i përjetshëm');
INSERT INTO "hasan_efendi" VALUES(25,66,'25:66','e ai është vendbanim dhe vendstrehim i keq”;');
INSERT INTO "hasan_efendi" VALUES(25,67,'25:67','dhe ata që, kur shpenzojnë, nuk janë as dorëshpuar, as dorështrënguar, por i përmbahen të mesmes;');
INSERT INTO "hasan_efendi" VALUES(25,68,'25:68','dhe ata që, pos Allahut, nuk adhurojnë zot tjetër, nuk vrasin njeri, gjë që Allahu e ka ndaluar, përveçse me të drejtë dhe që nuk bëjnë kurvëri. E kush i bën këto, do të marrë gjynahe.');
INSERT INTO "hasan_efendi" VALUES(25,69,'25:69','Ndëshkimi do t’i dyfishohet në Ditën e Kiametit dhe ai do të qëndrojë përherë në këtë fatkeqësi i poshtëruar.');
INSERT INTO "hasan_efendi" VALUES(25,70,'25:70','Përjashtim bën ai që pendohet, beson dhe punon vepra të mira. Një njeriu të tillë Allahu ia ndërron veprat e këqija në të mira. Allahu është Falës e Mëshirëplotë.');
INSERT INTO "hasan_efendi" VALUES(25,71,'25:71','Ndërsa, ai që pendohet dhe bën vepra të mira, është i kthyer krejtësisht nga Allahu.');
INSERT INTO "hasan_efendi" VALUES(25,72,'25:72','(Kështu janë) edhe ata që nuk dëshmojnë në mënyrë të gënjeshtërt e, kur kalojnë pranë kotësive, kalojnë me dinjitet;');
INSERT INTO "hasan_efendi" VALUES(25,73,'25:73','edhe ata, që, kur paralajmërohen nga shpalljet e Zotit të tyre, nuk bëhen as të shurdhër, as të verbër;');
INSERT INTO "hasan_efendi" VALUES(25,74,'25:74','edhe ata që thonë: “O Zoti ynë, dhurona nga gratë tona dhe trashëgimtarët tanë, ç’është prehje për sytë tanë dhe bëna shembull për të mirët!”.');
INSERT INTO "hasan_efendi" VALUES(25,75,'25:75','Njerëz të tillë do të shpërblehen me Xhenet të lartë për durimin e tyre dhe do të mirëpriten e do të gjejnë paqe.');
INSERT INTO "hasan_efendi" VALUES(25,76,'25:76','Aty do të qëndrojnë përherë; sa vendbanim dhe vendstrehim i mrekullueshëm që është ai!');
INSERT INTO "hasan_efendi" VALUES(25,77,'25:77','Thuaj: “Zoti im nuk do t’ju përfillte, sikur të mos ishte lutja juaj. Por ju (i) keni përgënjeshtruar (Shpalljet e Tij), andaj dënimi për ju do të jetë i pashmangshëm”.');
INSERT INTO "hasan_efendi" VALUES(26,1,'26:1','Tâ, Sîn, Mîm.');
INSERT INTO "hasan_efendi" VALUES(26,2,'26:2','Këto janë vargjet e Librit të qartë.');
INSERT INTO "hasan_efendi" VALUES(26,3,'26:3','A mos vallë ti (o Muhamed) do ta shkatërrosh veten, ngaqë ata (banorët e Mekës) nuk duan të besojnë?');
INSERT INTO "hasan_efendi" VALUES(26,4,'26:4','Sikur të donim Ne, do të zbritnim nga qielli një shenjë, para së cilës ata do të përulnin kokat.');
INSERT INTO "hasan_efendi" VALUES(26,5,'26:5','Sa herë që atyre u vjen ndonjë Këshillë e re nga i Gjithëmëshirshmi, ata ia kthejnë kurrizin.');
INSERT INTO "hasan_efendi" VALUES(26,6,'26:6','Ata e kanë përgënjeshtruar atë, prandaj do t’u vijë ai (ndëshkim), me të cilin janë tallur.');
INSERT INTO "hasan_efendi" VALUES(26,7,'26:7','Vallë, a nuk shohin ata tokën, prej së cilës kemi bërë të mbijnë sa e sa lloje çifte bimësh fisnike?');
INSERT INTO "hasan_efendi" VALUES(26,8,'26:8','Sigurisht që këtu ka shenja (të njëjësisë së Zotit), por shumica e tyre nuk besojnë.');
INSERT INTO "hasan_efendi" VALUES(26,9,'26:9','Zoti yt është vërtet i Plotfuqishëm dhe Mëshirëplotë.');
INSERT INTO "hasan_efendi" VALUES(26,10,'26:10','Kur Allahu e thërriti Musain: “Shko te populli keqbërës,');
INSERT INTO "hasan_efendi" VALUES(26,11,'26:11','populli i Faraonit! vallë, a nuk po i frikësohen ata Allahut?”');
INSERT INTO "hasan_efendi" VALUES(26,12,'26:12','Ai u përgjigj: “O Zoti im, unë kam frikë se do të më quajnë gënjeshtar,');
INSERT INTO "hasan_efendi" VALUES(26,13,'26:13','e zemra do të më ngushtohet, kurse gjuha mund të më lidhet, andaj, dërgoje edhe Harunin (të më ndihmojë).');
INSERT INTO "hasan_efendi" VALUES(26,14,'26:14','Unë jam edhe përgjegjës ndaj tyre për një krim, andaj kam frikë se do të më vrasin”.');
INSERT INTO "hasan_efendi" VALUES(26,15,'26:15','Allahu tha: “Kurrsesi! Shkoni që të dy me shenjat Tona. Me të vërtetë, Ne jemi me ju dhe dëgjojmë.');
INSERT INTO "hasan_efendi" VALUES(26,16,'26:16','Shkoni te Faraoni dhe i thoni: “Ne jemi të dërguarit e Zotit të botëve,');
INSERT INTO "hasan_efendi" VALUES(26,17,'26:17','që të lejoni bijtë e Israilit të vijnë me ne!”');
INSERT INTO "hasan_efendi" VALUES(26,18,'26:18','Faraoni tha: “Vallë, a nuk të kemi rritur në gjirin tonë si fëmijë?! A nuk ke qëndruar midis nesh shumë vjet të jetës sate?!');
INSERT INTO "hasan_efendi" VALUES(26,19,'26:19','E pastaj bëre veprën që ti e di, duke qenë mosmirënjohës?!”');
INSERT INTO "hasan_efendi" VALUES(26,20,'26:20','Musai tha: “E kam bërë atë, kur nuk kam qenë i udhëzuar.');
INSERT INTO "hasan_efendi" VALUES(26,21,'26:21','Pastaj ika prej jush, kur u frikësova prej jush dhe Zoti im më dhuroi mençuri dhe më bëri një nga të dërguarit.');
INSERT INTO "hasan_efendi" VALUES(26,22,'26:22','A e quan mirësi atë që më bëre mua, ndërkohë që bijtë e Israilit (popullin tim) i bëre skllevër”?');
INSERT INTO "hasan_efendi" VALUES(26,23,'26:23','Faraoni e pyeti: “E ç’është Zoti i botëve?”');
INSERT INTO "hasan_efendi" VALUES(26,24,'26:24','(Musai) u përgjigj: “Është Zoti i qiejve, i Tokës dhe i gjithçkaje që gjendet midis tyre, nëse besoni me bindje”.');
INSERT INTO "hasan_efendi" VALUES(26,25,'26:25','(Faraoni) u tha atyre rreth vetes: “Vallë, a nuk po dëgjoni?!”');
INSERT INTO "hasan_efendi" VALUES(26,26,'26:26','Ai tha: “Zoti juaj dhe Zoti i të parëve tuaj.”');
INSERT INTO "hasan_efendi" VALUES(26,27,'26:27','(Faraoni) tha: “Me të vërtetë, i Dërguari që ju është sjellë, është i çmendur”.');
INSERT INTO "hasan_efendi" VALUES(26,28,'26:28','(Musai) tha: “Zoti i Lindjes dhe i Perëndimit dhe i gjithçkaje që gjendet midis tyre, nëse kuptoni.');
INSERT INTO "hasan_efendi" VALUES(26,29,'26:29','(Faraoni) tha: “Nëse ti merr për zot tjetërkënd, përveç meje, me siguri, do të të fus në burg!”');
INSERT INTO "hasan_efendi" VALUES(26,30,'26:30','(Musai) u përgjigj: “Vallë, edhe pasi të të kem sjellë mrekulli të qartë? “');
INSERT INTO "hasan_efendi" VALUES(26,31,'26:31','(Faraoni) tha: “Tregoje pra, nëse thua të vërtetën!”');
INSERT INTO "hasan_efendi" VALUES(26,32,'26:32','(Musai) lëshoi shkopin e vet e ai u shndërrua në gjarpër të vërtetë!');
INSERT INTO "hasan_efendi" VALUES(26,33,'26:33','Pastaj nxori dorën e vet dhe ajo, menjëherë u duk e bardhë para shikuesve!');
INSERT INTO "hasan_efendi" VALUES(26,34,'26:34','(Faraoni) i tha parisë së tij, që e kishte përreth: “Ky qenka magjistar vërtet i dijshëm!');
INSERT INTO "hasan_efendi" VALUES(26,35,'26:35','Me magjitë e veta, ai dëshiron t’ju dëbojë nga toka juaj, prandaj ç’më këshilloni ju?”');
INSERT INTO "hasan_efendi" VALUES(26,36,'26:36','Ata u përgjigjën: “Lëre këtë dhe vëllanë e tij për pak kohë dhe ço lajmëtarë nëpër qytete,');
INSERT INTO "hasan_efendi" VALUES(26,37,'26:37','që të t’i sjellin ty të gjithë magjistarët e dijshëm”.');
INSERT INTO "hasan_efendi" VALUES(26,38,'26:38','Magjistarët u mblodhën në ditën e caktuar,');
INSERT INTO "hasan_efendi" VALUES(26,39,'26:39','e popullit iu tha: “A jeni mbledhur të gjithë,');
INSERT INTO "hasan_efendi" VALUES(26,40,'26:40','që të shkojmë pas magjistarëve, nëse ata do të jenë fitues?!”');
INSERT INTO "hasan_efendi" VALUES(26,41,'26:41','Kur erdhën magjistarët, i thanë Faraonit: “Vërtet do të shpërblehemi, nëse dalim fitues?”');
INSERT INTO "hasan_efendi" VALUES(26,42,'26:42','Ai u përgjigj: “Po, madje do të jeni vërtet ndër të afërmit e mi”.');
INSERT INTO "hasan_efendi" VALUES(26,43,'26:43','Musai u tha atyre: “Hidheni atë që doni ta hidhni!”');
INSERT INTO "hasan_efendi" VALUES(26,44,'26:44','Ata hodhën litarët dhe shkopinjtë e tyre dhe thanë: “Për madhërinë e Faraonit, me të vërtetë, Ne do të jemi fitues patjetër!”');
INSERT INTO "hasan_efendi" VALUES(26,45,'26:45','Pastaj, Musai hodhi shkopin e tij, që i kapërdiu përnjëherësh sajimet e tyre të rreme!');
INSERT INTO "hasan_efendi" VALUES(26,46,'26:46','Atëherë magjistarët ranë përmbys, duke bërë sexhde,');
INSERT INTO "hasan_efendi" VALUES(26,47,'26:47','dhe thanë: “Ne besojmë në Zotin e botëve,');
INSERT INTO "hasan_efendi" VALUES(26,48,'26:48','Zotin e Musait dhe të Harunit!”');
INSERT INTO "hasan_efendi" VALUES(26,49,'26:49','(Faraoni) tha: “I besuat atij (Musait) para se t’ju lejoja unë! Patjetër që ai është i madhi juaj, i cili ju ka mësuar magjinë. Ta dini se unë, në të vërtetë, do t’jua këpus duart dhe këmbët tuaja tërthorazi dhe do t’ju kryqëzoj të gjithëve!');
INSERT INTO "hasan_efendi" VALUES(26,50,'26:50','Ata thanë: “S’ka gjë! Sigurisht, Ne do të kthehemi te Zoti ynë.');
INSERT INTO "hasan_efendi" VALUES(26,51,'26:51','Ne shpresojmë se Zoti ynë do të na i falë gjynahet, meqë jemi besimtarët e parë.”');
INSERT INTO "hasan_efendi" VALUES(26,52,'26:52','Pastaj, Ne i shpallëm Musait: “Ti udhëto natën me robërit e Mi, por do të jeni të ndjekur”.');
INSERT INTO "hasan_efendi" VALUES(26,53,'26:53','Dhe Faraoni dërgoi nëpër qytete lajmëtarë');
INSERT INTO "hasan_efendi" VALUES(26,54,'26:54','(Për të thënë:) “Këta janë një grup i vogël që');
INSERT INTO "hasan_efendi" VALUES(26,55,'26:55','na kanë provokuar,');
INSERT INTO "hasan_efendi" VALUES(26,56,'26:56','por ne jemi kurdoherë të gatshëm!”');
INSERT INTO "hasan_efendi" VALUES(26,57,'26:57','Kështu, Ne i bëmë ata të lënë kopshtet dhe lumenjtë,');
INSERT INTO "hasan_efendi" VALUES(26,58,'26:58','thesaret e pallatet e mrekullueshme.');
INSERT INTO "hasan_efendi" VALUES(26,59,'26:59','Kështu ndodhi, dhe Ne ia dhamë të gjitha këto bijve të Israilit.');
INSERT INTO "hasan_efendi" VALUES(26,60,'26:60','Në agim, ata (njerëzit e Faraonit) iu afruan atyre.');
INSERT INTO "hasan_efendi" VALUES(26,61,'26:61','Kur dy grupet panë njëri-tjetrin, shokët e Musait thirrën: “Me të vërtetë, na zunë!”');
INSERT INTO "hasan_efendi" VALUES(26,62,'26:62','Ai tha: “Kurrsesi! Në të vërtetë, me mua është Zoti im. Ai do të ma tregojë rrugën”.');
INSERT INTO "hasan_efendi" VALUES(26,63,'26:63','Dhe Ne i shpallëm Musait: “Bjeri me shkopin tënd detit!” Menjëherë deti u nda dhe çdo pjesë e tij u bë si mal i madh.');
INSERT INTO "hasan_efendi" VALUES(26,64,'26:64','Atëherë Ne i afruam aty të tjerët.');
INSERT INTO "hasan_efendi" VALUES(26,65,'26:65','Musain dhe të gjithë ata që ishin me të, i shpëtuam,');
INSERT INTO "hasan_efendi" VALUES(26,66,'26:66','kurse të tjerët i fundosëm.');
INSERT INTO "hasan_efendi" VALUES(26,67,'26:67','Pa dyshim, në këtë ka shenja treguese, por shumica e njerëzve nuk janë besimtarë.');
INSERT INTO "hasan_efendi" VALUES(26,68,'26:68','Në të vërtetë, Zoti yt është i Plotfuqishëm dhe Mëshirëplotë.');
INSERT INTO "hasan_efendi" VALUES(26,69,'26:69','Tregoju atyre historinë e Ibrahimit,');
INSERT INTO "hasan_efendi" VALUES(26,70,'26:70','kur i tha babait të vet dhe popullit të vet: “Çfarë adhuroni ju?');
INSERT INTO "hasan_efendi" VALUES(26,71,'26:71','Ata u përgjigjën: “Adhurojmë idhujt e përherë u jemi përkushtuar atyre”.');
INSERT INTO "hasan_efendi" VALUES(26,72,'26:72','Ai i pyeti: “A ju dëgjojnë ata kur ju luteni?');
INSERT INTO "hasan_efendi" VALUES(26,73,'26:73','A mund t’ju sjellin dobi apo dëm?”');
INSERT INTO "hasan_efendi" VALUES(26,74,'26:74','U përgjigjën: “Jo, por i kemi gjetur të parët tanë që vepronin kështu”.');
INSERT INTO "hasan_efendi" VALUES(26,75,'26:75','Ai tha: “A keni menduar mirë mbi çfarë keni adhuruar,');
INSERT INTO "hasan_efendi" VALUES(26,76,'26:76','si ju, ashtu edhe të parët tuaj të lashtë?');
INSERT INTO "hasan_efendi" VALUES(26,77,'26:77','Në të vërtetë, ata janë armiqtë e mi, përveç Zotit të botëve,');
INSERT INTO "hasan_efendi" VALUES(26,78,'26:78','i Cili më ka krijuar dhe më udhëzon (në rrugë të drejtë);');
INSERT INTO "hasan_efendi" VALUES(26,79,'26:79','i Cili më ushqen dhe më jep të pi;');
INSERT INTO "hasan_efendi" VALUES(26,80,'26:80','i Cili, kur sëmurem, më shëron;');
INSERT INTO "hasan_efendi" VALUES(26,81,'26:81','i Cili do të bëjë që të vdes e pastaj do të më ringjallë;');
INSERT INTO "hasan_efendi" VALUES(26,82,'26:82','dhe i Cili, shpresoj se do të m’i falë gabimet e mia në Ditën e Gjykimit!');
INSERT INTO "hasan_efendi" VALUES(26,83,'26:83','O Zoti im, më dhuro dituri dhe më radhit me të mirët!');
INSERT INTO "hasan_efendi" VALUES(26,84,'26:84','Bëj që të përmendem për të mirë te breznitë e ardhshme');
INSERT INTO "hasan_efendi" VALUES(26,85,'26:85','dhe më bëj një nga trashëgimtarët e Xhenetit të begatë!');
INSERT INTO "hasan_efendi" VALUES(26,86,'26:86','Fale babanë tim, sepse ai është njëri nga të humburit,');
INSERT INTO "hasan_efendi" VALUES(26,87,'26:87','dhe mos më turpëro në Ditën, kur do të ringjallen njerëzit,');
INSERT INTO "hasan_efendi" VALUES(26,88,'26:88','në Ditën, kur askujt nuk do t’i bëjë dobi as pasuria, as fëmijët,');
INSERT INTO "hasan_efendi" VALUES(26,89,'26:89','përveç atij që vjen me zemër të pastër tek Allahu!”');
INSERT INTO "hasan_efendi" VALUES(26,90,'26:90','Atë ditë Xheneti do t’u afrohet besimtarëve të devotshëm,');
INSERT INTO "hasan_efendi" VALUES(26,91,'26:91','ndërsa Xhehenemi do t’u tregohet atyre që kishin humbur.');
INSERT INTO "hasan_efendi" VALUES(26,92,'26:92','Ata do të pyeten: “Ku janë idhujt që ju i adhuronit');
INSERT INTO "hasan_efendi" VALUES(26,93,'26:93','në vend të Allahut? A mund t’ju ndihmojnë ata ose, a mund ta ndihmojnë veten?”');
INSERT INTO "hasan_efendi" VALUES(26,94,'26:94','Kështu, ata do të hidhen në zjarr bashkë me mohuesit,');
INSERT INTO "hasan_efendi" VALUES(26,95,'26:95','si dhe të gjithë ushtarët e Iblisit.');
INSERT INTO "hasan_efendi" VALUES(26,96,'26:96','Ndërsa ndodhen aty brenda, ata do të grinden ndërmjet tyre, duke thënë:');
INSERT INTO "hasan_efendi" VALUES(26,97,'26:97','“Betohemi për Allahun, ne kemi qenë njëmend në humbje të qartë,');
INSERT INTO "hasan_efendi" VALUES(26,98,'26:98','kur ju kemi barazuar me Zotin e botëve.');
INSERT INTO "hasan_efendi" VALUES(26,99,'26:99','Ishin të ligjtë ata që na mashtruan,');
INSERT INTO "hasan_efendi" VALUES(26,100,'26:100','Tashmë ne nuk kemi asnjë ndërmjetësues (tek Allahu)');
INSERT INTO "hasan_efendi" VALUES(26,101,'26:101','e as ndonjë mik të ngushtë!');
INSERT INTO "hasan_efendi" VALUES(26,102,'26:102','Ah, sikur të ktheheshim përsëri (në jetë) e të bëheshim besimtarë!”');
INSERT INTO "hasan_efendi" VALUES(26,103,'26:103','Sigurisht që në këtë ka shenja treguese, por shumica e njerëzve nuk janë besimtarë.');
INSERT INTO "hasan_efendi" VALUES(26,104,'26:104','e Zoti yt, me të vërtetë, është i Plotfuqishëm dhe Mëshirëplotë.');
INSERT INTO "hasan_efendi" VALUES(26,105,'26:105','Populli i Nuhut, i quante gënjeshtarë të dërguarit.');
INSERT INTO "hasan_efendi" VALUES(26,106,'26:106','Kujto kur vëllai i tyre - Nuhu, u tha: “Vallë, a nuk i frikësoheni ju Allahut?');
INSERT INTO "hasan_efendi" VALUES(26,107,'26:107','Në të vërtetë, unë jam një i dërguar i besuar për ju,');
INSERT INTO "hasan_efendi" VALUES(26,108,'26:108','andaj, frikësojuni Allahut dhe bindmuni mua!');
INSERT INTO "hasan_efendi" VALUES(26,109,'26:109','Për këtë, unë nuk kërkoj nga ju kurrfarë shpërblimi; mua do të më shpërblejë vetëm Zoti i botëve.');
INSERT INTO "hasan_efendi" VALUES(26,110,'26:110','Andaj, frikësojuni Allahut dhe bindmuni mua!”');
INSERT INTO "hasan_efendi" VALUES(26,111,'26:111','Ata thanë: “Vallë, a të të besojmë ne ty, ndërkohë që ty po të pasojnë më të ulëtit?”');
INSERT INTO "hasan_efendi" VALUES(26,112,'26:112','Ai tha: “Unë nuk di ç’kanë bërë ata!');
INSERT INTO "hasan_efendi" VALUES(26,113,'26:113','Llogaria e tyre është vetëm te Zoti im, a nuk kuptoni?');
INSERT INTO "hasan_efendi" VALUES(26,114,'26:114','Unë nuk do t’i dëboj besimtarët,');
INSERT INTO "hasan_efendi" VALUES(26,115,'26:115','unë jam vetëm paralajmërues i qartë.”');
INSERT INTO "hasan_efendi" VALUES(26,116,'26:116','Ata thanë: “Nëse nuk tërhiqesh nga këto, o Nuh, me siguri, do të gurëzohesh”.');
INSERT INTO "hasan_efendi" VALUES(26,117,'26:117','Ai tha: “O Zoti im, populli im më mohoi,');
INSERT INTO "hasan_efendi" VALUES(26,118,'26:118','andaj, Ti gjyko mes meje dhe atyre dhe më shpëto mua e besimtarët që janë me mua!”');
INSERT INTO "hasan_efendi" VALUES(26,119,'26:119','Kështu, Ne e shpëtuam atë dhe ata që ishin me të në barkën plot,');
INSERT INTO "hasan_efendi" VALUES(26,120,'26:120','kurse të tjerët që mbetën pas, i fundosëm.');
INSERT INTO "hasan_efendi" VALUES(26,121,'26:121','Njëmend, në këtë ka këshillë, por shumica e njerëzve nuk janë besimtarë.');
INSERT INTO "hasan_efendi" VALUES(26,122,'26:122','Në të vërtetë, Zoti yt është i Plotfuqishëm dhe Mëshirëplotë.');
INSERT INTO "hasan_efendi" VALUES(26,123,'26:123','Fisi Ad i quante gënjeshtarë të dërguarit.');
INSERT INTO "hasan_efendi" VALUES(26,124,'26:124','Kujto kur vëllai i tyre - Hudi, u tha: “Vallë, a nuk i frikësoheni ju Allahut?');
INSERT INTO "hasan_efendi" VALUES(26,125,'26:125','Pa dyshim, unë jam i dërguar i besuar për ju,');
INSERT INTO "hasan_efendi" VALUES(26,126,'26:126','andaj, frikësojuni Allahut dhe bindmuni mua!');
INSERT INTO "hasan_efendi" VALUES(26,127,'26:127','Për këtë, unë nuk kërkoj nga ju kurrfarë shpërblimi, mua do të më shpërblejë vetëm Zoti i botëve.');
INSERT INTO "hasan_efendi" VALUES(26,128,'26:128','A mos vallë në çdo kodrinë po ngrini përmendore, për t’u tallur,');
INSERT INTO "hasan_efendi" VALUES(26,129,'26:129','dhe ngrini pallate, sikur do të jetoni gjithmonë?');
INSERT INTO "hasan_efendi" VALUES(26,130,'26:130','Edhe kur përdorni forcën, e shfrytëzoni atë si tiranë.');
INSERT INTO "hasan_efendi" VALUES(26,131,'26:131','Andaj, frikësojuni Allahut dhe bindmuni mua!');
INSERT INTO "hasan_efendi" VALUES(26,132,'26:132','Frikësojuni Atij, i Cili ju ka dhuruar ato që i dini:');
INSERT INTO "hasan_efendi" VALUES(26,133,'26:133','ju dhuroi juve bagëti dhe fëmijë,');
INSERT INTO "hasan_efendi" VALUES(26,134,'26:134','edhe kopshte, edhe burime.');
INSERT INTO "hasan_efendi" VALUES(26,135,'26:135','Unë, me të vërtetë, i trembem për ju dënimit të një Dite të madhe”.');
INSERT INTO "hasan_efendi" VALUES(26,136,'26:136','Ata thanë: “Për ne është njëlloj: na këshillove ose nuk na këshillove ti.');
INSERT INTO "hasan_efendi" VALUES(26,137,'26:137','Këto janë vetëm doket e popujve të lashtë');
INSERT INTO "hasan_efendi" VALUES(26,138,'26:138','e ne nuk do të jemi të dënuar.”');
INSERT INTO "hasan_efendi" VALUES(26,139,'26:139','Ata e quajtën gënjeshtar atë, andaj Ne i shkatërruam ata. Njëmend, në këtë ka këshillë, por shumica njerëzve nuk janë besimtarë.');
INSERT INTO "hasan_efendi" VALUES(26,140,'26:140','Në të vërtetë, Zoti yt është i Plotfuqishëm dhe Mëshirëplotë.');
INSERT INTO "hasan_efendi" VALUES(26,141,'26:141','Edhe fisi Themud i quante gënjeshtarë të dërguarit.');
INSERT INTO "hasan_efendi" VALUES(26,142,'26:142','Kujto kur vëllai i tyre - Salihu, u tha: “Vallë, a nuk i frikësoheni ju Allahut?');
INSERT INTO "hasan_efendi" VALUES(26,143,'26:143','Pa dyshim, unë jam një i dërguar i besuar për ju,');
INSERT INTO "hasan_efendi" VALUES(26,144,'26:144','andaj, frikësojuni Allahut dhe bindmuni mua!');
INSERT INTO "hasan_efendi" VALUES(26,145,'26:145','Për këtë, unë nuk kërkoj nga ju kurrfarë shpërblimi, mua do të më shpërblejë vetëm Zoti i botëve.');
INSERT INTO "hasan_efendi" VALUES(26,146,'26:146','Vallë, a mendoni ju, se do të mbeteni këtu të sigurt,');
INSERT INTO "hasan_efendi" VALUES(26,147,'26:147','në kopshte e pranë burimeve,');
INSERT INTO "hasan_efendi" VALUES(26,148,'26:148','në ara me të lashta dhe palma hurmash me fruta të shijshme?');
INSERT INTO "hasan_efendi" VALUES(26,149,'26:149','Ju i gdhendni shtëpitë në male me mjeshtri të rrallë,');
INSERT INTO "hasan_efendi" VALUES(26,150,'26:150','andaj, frikësojuni Allahut dhe bindmuni mua,');
INSERT INTO "hasan_efendi" VALUES(26,151,'26:151','dhe mos dëgjoni urdhrat e atyre që e teprojnë në të keqe');
INSERT INTO "hasan_efendi" VALUES(26,152,'26:152','e të cilët bëjnë ngatërresa në Tokë e nuk sjellin përmirësim”.');
INSERT INTO "hasan_efendi" VALUES(26,153,'26:153','Ata thanë: “Ti je vetëm një i magjepsur!');
INSERT INTO "hasan_efendi" VALUES(26,154,'26:154','Ti je vetëm një vdekatar - ashtu si ne, andaj na sill një mrekulli, nëse ajo që thua është e vërtetë!”');
INSERT INTO "hasan_efendi" VALUES(26,155,'26:155','Ai tha: “Ja, kjo është një deve. Ajo do të ketë radhën e saj për të pirë, ashtu si edhe ju, në ditë të caktuara.');
INSERT INTO "hasan_efendi" VALUES(26,156,'26:156','Mos i bëni kurrfarë të keqeje asaj, se do t’ju godasë dënimi i një Dite të madhe!');
INSERT INTO "hasan_efendi" VALUES(26,157,'26:157','Por ata e therën e pastaj u erdhi keq.');
INSERT INTO "hasan_efendi" VALUES(26,158,'26:158','Dhe kështu, ata i goditi dënimi. Njëmend, në këtë ka këshillë, por shumica e njerëzve nuk janë besimtarë.');
INSERT INTO "hasan_efendi" VALUES(26,159,'26:159','Në të vërtetë, Zoti yt është i Plotfuqishëm dhe Mëshirëplotë.');
INSERT INTO "hasan_efendi" VALUES(26,160,'26:160','Populli i Lutit i quante gënjeshtarë të dërguarit.');
INSERT INTO "hasan_efendi" VALUES(26,161,'26:161','Kujto kur vëllai i tyre - Luti, u tha: “Vallë, a nuk i frikësoheni ju Allahut?');
INSERT INTO "hasan_efendi" VALUES(26,162,'26:162','Pa dyshim, unë jam i dërguar i besuar për ju,');
INSERT INTO "hasan_efendi" VALUES(26,163,'26:163','andaj, frikësojuni Allahut dhe bindmuni mua!');
INSERT INTO "hasan_efendi" VALUES(26,164,'26:164','Për këtë, unë nuk kërkoj nga ju kurrfarë shpërblimi, mua do të më shpërblejë vetëm Zoti i botëve.');
INSERT INTO "hasan_efendi" VALUES(26,165,'26:165','Vallë, ju, nga e gjitha bota, iu qaseni meshkujve');
INSERT INTO "hasan_efendi" VALUES(26,166,'26:166','e i lini mënjanë gratë tuaja, të cilat Zoti juaj i ka krijuar bashkëshorte për ju?! Njëmend, ju jeni popull që kaloni çdo kufi të së keqes.”');
INSERT INTO "hasan_efendi" VALUES(26,167,'26:167','Ata thanë: “Nëse nuk tërhiqesh nga këto, o Lut, me siguri do të jesh i dëbuar”.');
INSERT INTO "hasan_efendi" VALUES(26,168,'26:168','Ai tha: “Unë kam vërtet neveri për atë që bëni ju!');
INSERT INTO "hasan_efendi" VALUES(26,169,'26:169','O Zoti im, më shpëto mua dhe familjen time nga ajo që bëjnë ata!”');
INSERT INTO "hasan_efendi" VALUES(26,170,'26:170','Dhe Ne e shpëtuam atë dhe familjen e tij - të gjithë,');
INSERT INTO "hasan_efendi" VALUES(26,171,'26:171','përveç një plake (gruas së Lutit), e cila mbeti me të tjerët.');
INSERT INTO "hasan_efendi" VALUES(26,172,'26:172','Pastaj, të tjerët i shkatërruam.');
INSERT INTO "hasan_efendi" VALUES(26,173,'26:173','Mbi ata lëshuam një breshëri gurësh. Eh, sa i keq që ishte ai shi, për ata që janë paralajmëruar!');
INSERT INTO "hasan_efendi" VALUES(26,174,'26:174','Sigurisht, në këtë ka këshillë, por shumica e tyre nuk janë besimtarë.');
INSERT INTO "hasan_efendi" VALUES(26,175,'26:175','Në të vërtetë, Zoti yt është i Plotfuqishëm dhe Mëshirëplotë.');
INSERT INTO "hasan_efendi" VALUES(26,176,'26:176','Edhe banorët e Ejkës i quajtën gënjeshtarë të dërguarit,');
INSERT INTO "hasan_efendi" VALUES(26,177,'26:177','Kujto kur Shuajbi u tha atyre: “Vallë, a nuk i frikësoheni ju Allahut?');
INSERT INTO "hasan_efendi" VALUES(26,178,'26:178','Pa dyshim, unë jam një i dërguar i besuar për ju,');
INSERT INTO "hasan_efendi" VALUES(26,179,'26:179','andaj, frikësojuni Allahut dhe bindmuni mua!');
INSERT INTO "hasan_efendi" VALUES(26,180,'26:180','Për këtë, unë nuk kërkoj nga ju kurrfarë shpërblimi, mua do të më shpërblejë vetëm Zoti i botëve.');
INSERT INTO "hasan_efendi" VALUES(26,181,'26:181','Bëjeni drejt matjen dhe mos u bëni nga ata që hanë (në matje).');
INSERT INTO "hasan_efendi" VALUES(26,182,'26:182','Peshoni drejtësisht me peshore të saktë,');
INSERT INTO "hasan_efendi" VALUES(26,183,'26:183','mos ua pakësoni njerëzve gjërat e tyre, mos bëni ngatërresa në Tokë');
INSERT INTO "hasan_efendi" VALUES(26,184,'26:184','dhe frikësojuni Atij, që ju ka krijuar ju dhe brezat e mëparshëm!”');
INSERT INTO "hasan_efendi" VALUES(26,185,'26:185','Ata thanë: “Ti je vetëm një i magjepsur.');
INSERT INTO "hasan_efendi" VALUES(26,186,'26:186','Ti je vetëm një vdekatar, si ne. Në të vërtetë, Ne mendojmë se ti je gënjeshtar;');
INSERT INTO "hasan_efendi" VALUES(26,187,'26:187','andaj, lësho mbi ne një copë nga qielli, nëse thua të vërtetën!');
INSERT INTO "hasan_efendi" VALUES(26,188,'26:188','Ai tha: “Zoti im e di më së miri atë që punoni ju.”');
INSERT INTO "hasan_efendi" VALUES(26,189,'26:189','Ata vazhduan të mos e besojnë, prandaj i goditi dënimi Ditës së Errësirës. Njëmend, ky ishte dënimi i një dite të madhe.');
INSERT INTO "hasan_efendi" VALUES(26,190,'26:190','Sigurisht, në këtë ka këshillë, por shumica e tyre nuk janë besimtarë,');
INSERT INTO "hasan_efendi" VALUES(26,191,'26:191','Në të vërtetë, Zoti yt është i Plotfuqishëm dhe Mëshirëplotë.');
INSERT INTO "hasan_efendi" VALUES(26,192,'26:192','Nuk ka dyshim se ky (Kuran) është Shpallja e Zotit të botëve.');
INSERT INTO "hasan_efendi" VALUES(26,193,'26:193','Atë e zbriti Shpirti i besueshëm (Xhebraili)');
INSERT INTO "hasan_efendi" VALUES(26,194,'26:194','në zemrën tënde (o Muhamed) që të jesh nga ata që paralajmërojnë.');
INSERT INTO "hasan_efendi" VALUES(26,195,'26:195','E zbriti në gjuhën e qartë arabe.');
INSERT INTO "hasan_efendi" VALUES(26,196,'26:196','Vërtet, ai është përmendur në Shkrimet e të parëve.');
INSERT INTO "hasan_efendi" VALUES(26,197,'26:197','Vallë, a nuk është provë për ata (mekasit), që atë (Muhamedin) e kanë njohur dijetarët e bijve të Israilit?');
INSERT INTO "hasan_efendi" VALUES(26,198,'26:198','Por edhe sikur t’ia shpallnim Kuranin ndonjë jo-arabi');
INSERT INTO "hasan_efendi" VALUES(26,199,'26:199','e ai t’ua lexonte atyre - ata përsëri nuk do të kishin besuar.');
INSERT INTO "hasan_efendi" VALUES(26,200,'26:200','Ja kështu, Ne ua kemi shtënë (mohimin) në zemër keqbërësve.');
INSERT INTO "hasan_efendi" VALUES(26,201,'26:201','Ata nuk do ta besojnë atë, derisa të shohin dënimin e dhembshëm.');
INSERT INTO "hasan_efendi" VALUES(26,202,'26:202','Por ai do t’u vijë atyre papritmas e ata nuk do ta ndiejnë fare.');
INSERT INTO "hasan_efendi" VALUES(26,203,'26:203','Atëherë ata do thonë: “A do të na jepet afat?”');
INSERT INTO "hasan_efendi" VALUES(26,204,'26:204','A mos vallë, ata duan ta shpejtojnë dënimin Tonë!?');
INSERT INTO "hasan_efendi" VALUES(26,205,'26:205','Mendo, nëse Ne i lëmë që të kënaqen disa vjet');
INSERT INTO "hasan_efendi" VALUES(26,206,'26:206','e pastaj t’i godasë ai (dënim) që u është premtuar,');
INSERT INTO "hasan_efendi" VALUES(26,207,'26:207','çfarë vlere do të kishte ajo me të cilën janë kënaqur?');
INSERT INTO "hasan_efendi" VALUES(26,208,'26:208','Asnjëherë, Ne nuk kemi shkatërruar ndonjë vendbanim, pa i çuar më parë paralajmërues,');
INSERT INTO "hasan_efendi" VALUES(26,209,'26:209','si këshillë për ata. Ne nuk jemi asnjëherë të padrejtë.');
INSERT INTO "hasan_efendi" VALUES(26,210,'26:210','Atë (Kuran) nuk e kanë zbritur djajtë.');
INSERT INTO "hasan_efendi" VALUES(26,211,'26:211','Jo vetëm që kjo s’u takon atyre, por as nuk kanë fuqi të bëjnë një si ai.');
INSERT INTO "hasan_efendi" VALUES(26,212,'26:212','Në të vërtetë, ata mënjanohen nga të dëgjuarit (e Shpalljes).');
INSERT INTO "hasan_efendi" VALUES(26,213,'26:213','Prandaj, mos adhuro tjetër zot, përveç Allahut, që të mos bëhesh nga ata që do të dënohen!');
INSERT INTO "hasan_efendi" VALUES(26,214,'26:214','Paralajmëroje farefisin tënd më të afërm');
INSERT INTO "hasan_efendi" VALUES(26,215,'26:215','dhe bëhu i butë me besimtarët që të pasojnë ty!');
INSERT INTO "hasan_efendi" VALUES(26,216,'26:216','E, nëse nuk të dëgjojnë, thuaju: “Unë nuk jam përgjegjës për ato që bëni ju”.');
INSERT INTO "hasan_efendi" VALUES(26,217,'26:217','Dhe mbështetu në të Plotfuqishmin, Mëshirëplotin,');
INSERT INTO "hasan_efendi" VALUES(26,218,'26:218','i Cili e sheh qëndrimin tënd në këmbë (gjatë namazit)');
INSERT INTO "hasan_efendi" VALUES(26,219,'26:219','dhe lëvizjet e tua midis atyre që përulen në sexhde.');
INSERT INTO "hasan_efendi" VALUES(26,220,'26:220','Vërtet, Ai i dëgjon të gjitha dhe i di të gjitha.');
INSERT INTO "hasan_efendi" VALUES(26,221,'26:221','A doni t’ju tregoj se kujt i vijnë djajtë?');
INSERT INTO "hasan_efendi" VALUES(26,222,'26:222','Ata i vijnë çdo gënjeshtari gjynahqar.');
INSERT INTO "hasan_efendi" VALUES(26,223,'26:223','Ata përgjojnë, por shumica e tyre janë gënjeshtarë.');
INSERT INTO "hasan_efendi" VALUES(26,224,'26:224','Poetët ndiqen nga njerëzit e humbur.');
INSERT INTO "hasan_efendi" VALUES(26,225,'26:225','Vallë, a nuk e sheh ti, që ata bredhin në çdo luginë (të fjalës së kotë),');
INSERT INTO "hasan_efendi" VALUES(26,226,'26:226','duke folur atë që kurrë s’e bëjnë?');
INSERT INTO "hasan_efendi" VALUES(26,227,'26:227','Përveç atyre (poetëve) që besojnë, bëjnë vepra të mira, e përmendin shumë Allahun dhe mbrohen, vetëm pasi u është bërë padrejtësi. Sigurisht, të padrejtët do ta shohin se në çfarë mundimi do të kthehen (pas vdekjes).');
INSERT INTO "hasan_efendi" VALUES(27,1,'27:1','Tâ, Sîn. Këto janë vargjet e Kuranit, të një Libri të qartë,');
INSERT INTO "hasan_efendi" VALUES(27,2,'27:2','një udhërrëfyesi dhe lajmi të mirë për besimtarët,');
INSERT INTO "hasan_efendi" VALUES(27,3,'27:3','të cilët falin namazin, ndajnë zekatin dhe besojnë bindshëm në jetën tjetër.');
INSERT INTO "hasan_efendi" VALUES(27,4,'27:4','Njëmend, atyre që nuk besojnë në jetën tjetër, Ne kemi bërë që t’u duken të bukura veprat e tyre, prandaj ata ecin verbërisht.');
INSERT INTO "hasan_efendi" VALUES(27,5,'27:5','Janë pikërisht ata që i pret dënimi i rëndë dhe në jetën tjetër do të jenë më të humburit.');
INSERT INTO "hasan_efendi" VALUES(27,6,'27:6','Në të vërtetë, ty (o Muhamed) po të shpallet Kurani nga ana e një të Urti të Gjithëdijshëm!');
INSERT INTO "hasan_efendi" VALUES(27,7,'27:7','Kujto kur Musai i tha familjes së tij: “Unë pashë një zjarr. Do të shkoj që t’ju sjell ndonjë lajm prej atje ose t’ju sjell një urë zjarri, për t’u ngrohur”.');
INSERT INTO "hasan_efendi" VALUES(27,8,'27:8','Kur shkoi te zjarri, një zë e thirri: “Bekuar qoftë kush është te zjarri dhe të gjithë (engjëjt) rreth tij! Qoftë i lavdëruar Allahu, Zoti i botëve!');
INSERT INTO "hasan_efendi" VALUES(27,9,'27:9','O Musa! Jam Unë, Allahu, i Plotfuqishmi, i Urti!');
INSERT INTO "hasan_efendi" VALUES(27,10,'27:10','Hidhe shkopin tënd!” Kur e pa shkopin që po lëvizte si gjarpër, ai iku me vrap e nuk u kthye prapa. “O Musa, mos u frikëso! Në të vërtetë, të dërguarit e Mi nuk frikësohen në praninë Time.');
INSERT INTO "hasan_efendi" VALUES(27,11,'27:11','Sa për ata që bëjnë keq, e pastaj të keqen e zëvendësojnë me vepër të mirë, Unë jam vërtet Falës dhe Mëshirëplotë.');
INSERT INTO "hasan_efendi" VALUES(27,12,'27:12','Fute dorën tënde brenda në gji dhe do të dalë e bardhë pa kurrfarë sëmundjeje. Kjo do të jetë njëra prej nëntë mrekullive, që do t’i tregohen Faraonit dhe popullit të tij. Vërtet, ata janë popull i pabindur!”.');
INSERT INTO "hasan_efendi" VALUES(27,13,'27:13','Por kur u erdhën atyre shenjat Tona të dukshme, ata thanë: “Kjo është magji e qartë”!');
INSERT INTO "hasan_efendi" VALUES(27,14,'27:14','Kështu, ata i mohuan ato padrejtësisht dhe me mendjemadhësi, edhe pse shpirti i tyre i besonte për të vërteta. Pa shiko, cili ka qenë fundi i shkatërrimtarëve!');
INSERT INTO "hasan_efendi" VALUES(27,15,'27:15','Ne i dhamë Davudit dhe Sulejmanit dituri dhe ata thanë: “Qoftë lavdëruar Allahu që na ka ngritur mbi shumicën e robërve të Tij besimtarë!');
INSERT INTO "hasan_efendi" VALUES(27,16,'27:16','Sulejmani e trashëgoi Davudin (në misionin e tij profetik) dhe tha: “O populli im! Neve na është mësuar gjuha e shpendëve dhe na është dhënë gjithçka. Kjo është vërtet dhunti e madhe”.');
INSERT INTO "hasan_efendi" VALUES(27,17,'27:17','Para Sulejmanit u tubua ushtria e Tij e përbërë prej xhindeve, njerëzve dhe shpendëve, të gjithë të radhitur në çeta të gatshme.');
INSERT INTO "hasan_efendi" VALUES(27,18,'27:18','Kur arritën në Luginën e Milingonave, një milingonë tha: “O milingona, hyni në banesat tuaja, që të mos ju shkelë Sulejmani dhe ushtria e tij, duke mos ju parë”!');
INSERT INTO "hasan_efendi" VALUES(27,19,'27:19','Ai vuri buzën në gaz, duke qeshur me fjalët e saj dhe tha: “O Zoti im, më frymëzo që të jem mirënjohës për mirësitë që Ti më ke dhënë mua dhe prindërve të mi, dhe që të bëj vepra të mira, me të cilat Ti do të jesh i kënaqur. Më prano, me mëshirën Tënde, në gjirin e robërve të Tu të devotshëm!”');
INSERT INTO "hasan_efendi" VALUES(27,20,'27:20','Ai bëri kontrollin e shpendëve e tha: “Pse nuk e shoh pupëzën këtu, a nuk është e pranishme ajo?');
INSERT INTO "hasan_efendi" VALUES(27,21,'27:21','Nëse nuk do të më sjellë arsyetim bindës, do ta dënoj me dënim të rëndë ose do ta ther!”');
INSERT INTO "hasan_efendi" VALUES(27,22,'27:22','Nuk vonoi shumë e ajo erdhi dhe tha: “Kam marrë vesh diçka që ti nuk e ke ditur dhe të kam sjellë lajme të vërteta prej (qytetit të) Sebës.');
INSERT INTO "hasan_efendi" VALUES(27,23,'27:23','Kam parë një grua që sundonte popullin (e atij qyteti). Ajo zotëronte çdo gjë dhe kishte një fron madhështor.');
INSERT INTO "hasan_efendi" VALUES(27,24,'27:24','Kam parë se ajo dhe populli i saj adhuronin Diellin e jo Allahun. Djalli ua paraqiste të bukura sjelljet e tyre, duke i larguar nga rruga e drejtë, prandaj nuk ishin të udhëzuar.');
INSERT INTO "hasan_efendi" VALUES(27,25,'27:25','Ata nuk i përuleshin në sexhde Allahut, i Cili nxjerr gjithçka që është e fshehur në qiej dhe në Tokë dhe di çfarë fshihni ju dhe çfarë tregoni haptazi.');
INSERT INTO "hasan_efendi" VALUES(27,26,'27:26','Allahu është Një, s’ka zot të vërtetë përveç Tij, Zotit të Arshit të madh!”');
INSERT INTO "hasan_efendi" VALUES(27,27,'27:27','Sulejmani tha: “Do të shohim se a thua të vërtetën apo je gënjeshtar.');
INSERT INTO "hasan_efendi" VALUES(27,28,'27:28','Shko me këtë letër timen e çojua atyre. Pastaj largohu pak nga ata dhe shiko cila do të jetë përgjigjja e tyre!”');
INSERT INTO "hasan_efendi" VALUES(27,29,'27:29','Mbretëresha (e Sebës) tha: “O paria ime! Mua më ka ardhur një letër fisnike.');
INSERT INTO "hasan_efendi" VALUES(27,30,'27:30','Ajo është nga Sulejmani dhe është: “Me emrin e Allahut, të Gjithëmëshirshmit, Mëshirëplotit!');
INSERT INTO "hasan_efendi" VALUES(27,31,'27:31','Mos u madhështoni para meje dhe ejani të përulur si besimtarë”!');
INSERT INTO "hasan_efendi" VALUES(27,32,'27:32','Ajo tha: “O paria ime, më këshilloni se ç’duhet të bëj në këtë çështje! Unë nuk do të vendos asgjë pa ju!”');
INSERT INTO "hasan_efendi" VALUES(27,33,'27:33','Ata thanë: “Ne jemi shumë të fortë dhe luftëtarë të guximshëm, por urdhri të takon ty, andaj mendo mirë se ç’do të urdhërosh!”');
INSERT INTO "hasan_efendi" VALUES(27,34,'27:34','Ajo tha: “Njëmend, kur mbretërit pushtojnë ndonjë vendbanim, e rrënojnë atë dhe e poshtërojnë parinë e tij. Kështu veprojnë ata.');
INSERT INTO "hasan_efendi" VALUES(27,35,'27:35','Tani do t’u dërgoj atyre një dhuratë dhe do të shoh me se do të kthehen të deleguarit.”');
INSERT INTO "hasan_efendi" VALUES(27,36,'27:36','Kur erdhën (të deleguarit), Sulejmani tha: “A mos doni të më ndihmoni me pasuri?! Ajo që më ka dhënë Allahu, është më e mirë nga ajo që ju ka dhënë juve. Por ju gëzoheni me dhuratën tuaj.');
INSERT INTO "hasan_efendi" VALUES(27,37,'27:37','Kthehuni tek ata! Ne do të sjellim ushtri, të cilën nuk do të mund ta përballojnë ata dhe, me siguri, do t’i dëbojmë ata prej Sebës të poshtëruar dhe të përulur”.');
INSERT INTO "hasan_efendi" VALUES(27,38,'27:38','Pastaj ai tha: “O parësi, kush prej jush do të më sjellë fronin e saj, para se ata të vijnë të dorëzuar?”');
INSERT INTO "hasan_efendi" VALUES(27,39,'27:39','Njëri nga xhindet - Ifriti, tha: “Unë do të ta sjell ty para se të ngrihesh prej vendit; unë për këtë jam i fuqishëm dhe i sigurt”.');
INSERT INTO "hasan_efendi" VALUES(27,40,'27:40','Po ai që ishte i pajisur me dijen e Librit, tha: “Unë do të ta sjell ty sa çel e mbyll sytë”. Kur (Sulejmani) e pa të vendosur para tij, tha: “Kjo është nga dhuntitë e Zotit tim, për të më provuar a jam mirënjohës apo mohues. Kush falënderon, e bën për të mirën e vet e, kush mohon, (ta dijë se) Zoti im është i Vetëmjaftueshëm dhe Dhurues.');
INSERT INTO "hasan_efendi" VALUES(27,41,'27:41','Ndryshojeni edhe fronin e saj! Do të shohim se a do ta vërë re këtë apo jo.”');
INSERT INTO "hasan_efendi" VALUES(27,42,'27:42','Kur erdhi ajo, i thanë: “I tillë është froni yt?” Ajo u përgjigj: “Duket sikur të ishte ai”. Sulejmani tha: “Para saj na është dhënë dituria dhe para saj i jemi nënshtruar Allahut”.');
INSERT INTO "hasan_efendi" VALUES(27,43,'27:43','Atë (Belkisën) e kishte penguar (nga Islami) ajo që adhuronte në vend të Allahut, sepse ajo ishte nga populli mohues.');
INSERT INTO "hasan_efendi" VALUES(27,44,'27:44','Asaj i thanë të hynte në pallat. Kur e pa atë, ajo kujtoi se ishte ujë i thellë dhe përvoli fustanin te këmbët. Sulejmani i tha: “Ky pallat është i shtruar me xhama”. Ajo tha: “O Zoti im, me të vërtetë unë e paskam dëmtuar veten dhe tani, bashkë me Sulejmanin i dorëzohem Allahut - Zotit të botëve!”');
INSERT INTO "hasan_efendi" VALUES(27,45,'27:45','Ne i dërguam fisit të Themudit, vëllanë e tij Salihun. Ai tha: “Adhuroni Allahun!” Por ata u ndanë në dy grupe, që grindeshin midis tyre.');
INSERT INTO "hasan_efendi" VALUES(27,46,'27:46','(Salihu) tha: “O populli im, përse ju e dëshironi të keqen para së mirës?! Përse nuk kërkoni nga Allahu falje (të gjynaheve) që të mund të mëshiroheni?!”');
INSERT INTO "hasan_efendi" VALUES(27,47,'27:47','Ata thanë: “Ne kemi ogur të keq prej teje dhe atyre që janë me ty”. Ai u tha: “E mira dhe e keqja është prej Allahut. Në të vërtetë, ju jeni popull i vënë në sprovë”.');
INSERT INTO "hasan_efendi" VALUES(27,48,'27:48','Në qytet kishin qenë nëntë vetë që bënin ngatërresa e nuk vinin rregull.');
INSERT INTO "hasan_efendi" VALUES(27,49,'27:49','Ata i thanë (njëri-tjetrit): “Betohuni në Allahun se natën do ta vrasim atë me gjithë familjen e tij. Pastaj do t’u themi të afërmve të tij se ne nuk kemi qenë të pranishëm në zhdukjen e familjes së tij dhe se po themi të vërtetën.”');
INSERT INTO "hasan_efendi" VALUES(27,50,'27:50','Dhe kurdisën një kurth, por Ne u ngritëm një grackë, pa e ndier ata.');
INSERT INTO "hasan_efendi" VALUES(27,51,'27:51','Tani shiko se si ka qenë fundi i ngatërresave të tyre. Ne i shkatërruam ata të gjithë dhe popullin e tyre.');
INSERT INTO "hasan_efendi" VALUES(27,52,'27:52','Ja shtëpitë e tyre të rrënuara, për shkak të këqijave që bënin. Sigurisht që këtu ka këshillë për njerëzit që dinë.');
INSERT INTO "hasan_efendi" VALUES(27,53,'27:53','Ndërsa, ata që kishin besuar dhe (i) frikësoheshin (Allahut), Ne i shpëtuam.');
INSERT INTO "hasan_efendi" VALUES(27,54,'27:54','Kur Luti i tha popullit të vet: “Përse bëni vepra të mbrapshta e të turpshme, madje në sytë e njëri-tjetrit?');
INSERT INTO "hasan_efendi" VALUES(27,55,'27:55','Vallë, a mos ju u qaseni me epsh meshkujve, në vend të femrave? Në të vërtetë, ju jeni popull i paditur”,');
INSERT INTO "hasan_efendi" VALUES(27,56,'27:56','përgjigjja e vetme e popullit të tij ishte: “Dëbojeni familjen e Lutit prej qytetit tuaj, sepse ata janë njerëz të dëlirë!”');
INSERT INTO "hasan_efendi" VALUES(27,57,'27:57','Dhe Ne i shpëtuam atë dhe familjen e tij, përveç gruas së tij, së cilës i caktuam që të mbetej me të dënuarit.');
INSERT INTO "hasan_efendi" VALUES(27,58,'27:58','Dhe mbi ta lëshuam breshëri (prej gurësh)! Eh, sa e tmerrshme që ishte ajo breshëri për ata që ishin paralajmëruar!');
INSERT INTO "hasan_efendi" VALUES(27,59,'27:59','Thuaj (o Muhamed): “Lëvduar e falënderuar qoftë Allahu dhe shpëtimi qoftë për robërit e Tij të zgjedhur. Kush është më i mirë, Allahu apo ata që ia shoqërojnë Atij (në adhurim)?!');
INSERT INTO "hasan_efendi" VALUES(27,60,'27:60','A ka më të mirë se Ai që ka krijuar qiejt dhe Tokën dhe lëshon për ju nga qielli ujë, me të cilin krijon kopshte të mrekullueshme?! Ju nuk mund t’i bëni pemët të rriten (pa këtë ujë). Vallë, a ka krahas Allahut zot tjetër?! Jo, por ata janë njerëz që i veshin (Allahut) të barabartë me Atë!');
INSERT INTO "hasan_efendi" VALUES(27,61,'27:61','A ka më të mirë se Ai që e ka bërë Tokën të qëndrueshme, ka bërë në të lumenj, ka ngritur në të male dhe ka bërë midis dy deteve pengesë të padukshme?! Vallë, a ka krahas Allahut zot tjetër?! Jo, por shumica e tyre janë të paditur.');
INSERT INTO "hasan_efendi" VALUES(27,62,'27:62','A ka më të mirë se Ai që i vjen në ndihmë nevojtarit të këputur, kur i lutet Atij, që jua largon të keqen dhe ju bën ju trashëgimtarë në tokë?! Vallë, a ka krahas Allahut zot tjetër?! Sa pak që reflektoni!');
INSERT INTO "hasan_efendi" VALUES(27,63,'27:63','A ka më të mirë se Ai që ju rrëfen rrugën në errësirat e tokës dhe të detit dhe ju çon erërat si lajmëtare të mëshirës së Tij? Vallë, a ka krahas Allahut zot tjetër? I Lartësuar qoftë Allahu dhe i Lavdëruar mbi çdo gjë që ia shoqërojnë (në adhurim).');
INSERT INTO "hasan_efendi" VALUES(27,64,'27:64','A ka më të mirë se Ai, që zë fill krijimin dhe pastaj e ripërsërit atë, që ju jep mjete jetese nga qielli dhe toka?! Vallë, a ka krahas Allahut zot tjetër? Thuaj (o Muhamed): “Sillni provat tuaja, nëse ajo që thoni është e vërtetë!”');
INSERT INTO "hasan_efendi" VALUES(27,65,'27:65','Thuaj: “Askush, përveç Allahut, në qiell apo në Tokë, nuk i di të fshehtat dhe as nuk do ta dijë se kur do të ringjallet”.');
INSERT INTO "hasan_efendi" VALUES(27,66,'27:66','Vallë, a dinë ndopak ata për jetën tjetër!? Përkundrazi! Ata dyshojnë në të, ata janë të verbër ndaj saj.');
INSERT INTO "hasan_efendi" VALUES(27,67,'27:67','Dhe mohuesit thonë: “Vallë, a mos do të ringjallemi ne dhe etërit tanë, pasi të jemi bërë dhé?!');
INSERT INTO "hasan_efendi" VALUES(27,68,'27:68','Vërtet, neve dhe etërve tanë na është premtuar kjo qysh më parë, por këto, me gjasë, nuk janë tjetër, veçse legjenda të popujve të lashtë”.');
INSERT INTO "hasan_efendi" VALUES(27,69,'27:69','Thuaj: “Udhëtoni nëpër botë e shikoni si ka qenë fundi i keqbërësve!”');
INSERT INTO "hasan_efendi" VALUES(27,70,'27:70','Mos u pikëllo (o Muhamed) për shkak të tyre dhe mos u ngushto për kurthet e tyre.');
INSERT INTO "hasan_efendi" VALUES(27,71,'27:71','Ata thonë: “Kur do të plotësohet ky premtim, nëse ajo që thoni është e vërtetë?”');
INSERT INTO "hasan_efendi" VALUES(27,72,'27:72','Thuaj: “Mbase mund të jetë pas shpinës suaj ajo, që doni ta shpejtoni”!');
INSERT INTO "hasan_efendi" VALUES(27,73,'27:73','Në të vërtetë, Zoti yt, është Bamirës i madh i njerëzve, por shumica e tyre nuk janë mirënjohës.');
INSERT INTO "hasan_efendi" VALUES(27,74,'27:74','Pa dyshim, Zoti yt di atë që fshehin zemrat e tyre dhe atë që e shfaqin haptazi.');
INSERT INTO "hasan_efendi" VALUES(27,75,'27:75','S’ka asgjë të fshehtë në qiell dhe në Tokë, që të mos jetë e shënuar në Librin e qartë (Leuhi Mahfudh)');
INSERT INTO "hasan_efendi" VALUES(27,76,'27:76','Njëmend, ky Kuran u tregon bijve të Izraelit shumë nga çështjet, për të cilat nuk pajtoheshin,');
INSERT INTO "hasan_efendi" VALUES(27,77,'27:77','dhe, natyrisht, është udhërrëfyes dhe mëshirë për besimtarët e vërtetë.');
INSERT INTO "hasan_efendi" VALUES(27,78,'27:78','Nuk ka dyshim se Zoti yt do t’i gjykojë ata drejtësisht. Ai është i Plotfuqishmi dhe i Gjithëdijshmi.');
INSERT INTO "hasan_efendi" VALUES(27,79,'27:79','Prandaj mbështetu tek Allahu! Vërtet, ti je në të drejtën e qartë!');
INSERT INTO "hasan_efendi" VALUES(27,80,'27:80','Sigurisht që ti (Muhamed), nuk mund t’i bësh të vdekurit të dëgjojnë, as nuk mund t’i bësh të shurdhërit të dëgjojnë thirrjen tënde, kur të kthejnë shpinën!');
INSERT INTO "hasan_efendi" VALUES(27,81,'27:81','Ti nuk mund t’i nxjerrësh as të verbërit nga humbja e tyre! Ty të dëgjojnë vetëm ata që besojnë Shpalljet Tona dhe na nënshtrohen Neve (si myslimanë).');
INSERT INTO "hasan_efendi" VALUES(27,82,'27:82','Dhe, kur të përmbushet Fjala Jonë kundër tyre, Ne do të bëjmë që nga Toka të dalë një kafshë , e cila do t’u flasë atyre, se njerëzit nuk u janë bindur shpalljeve Tona.');
INSERT INTO "hasan_efendi" VALUES(27,83,'27:83','Atë ditë, Ne do të tubojmë prej çdo populli nga një grup prej atyre që mohuan shpalljet Tona. Ata do të vihen në radhë.');
INSERT INTO "hasan_efendi" VALUES(27,84,'27:84','E kur të vijnë, do t’i pyesim: “Ju i mohuat shpalljet e Mia, ndonëse nuk kishit kurrfarë njohurie për to. Ç’është kjo që keni bërë?!”');
INSERT INTO "hasan_efendi" VALUES(27,85,'27:85','Dënimi Ynë do t’i godasë ata, për shkak të të këqijave që bënë e nuk do të mund të nxjerrin asnjë fjalë.');
INSERT INTO "hasan_efendi" VALUES(27,86,'27:86','Vallë, a nuk shohin ata, që Ne e kemi bërë natën për pushim, ndërsa ditën për të parë?! Njëmend, këto janë shenja për besimtarët.');
INSERT INTO "hasan_efendi" VALUES(27,87,'27:87','Dhe Ditën, kur të fryhet në Sur, do të tmerrohet çdokush që gjendet në qiej e në Tokë, përveç atyre që Allahu nuk dëshiron t’i frikësojë. Të gjithë do t’i vijnë Atij të përulur.');
INSERT INTO "hasan_efendi" VALUES(27,88,'27:88','Ti i shikon malet e mendon se janë të palëvizshme, por ato do të lëvizin si retë. E tillë është fuqia e Allahut, i Cili ka përsosur çdo gjë. Ai di mirë çdo gjë që bëni ju.');
INSERT INTO "hasan_efendi" VALUES(27,89,'27:89','Ata që kanë bërë vepra të mira, do të kenë shpërblim më të mirë dhe do të jenë të çliruar nga tmerri i asaj Dite.');
INSERT INTO "hasan_efendi" VALUES(27,90,'27:90','Ndërsa ata që kanë bërë vepra të këqija, do të hidhen me fytyrë përmbys në zjarr. Ju do të shpërbleheni veç për atë që keni bërë!');
INSERT INTO "hasan_efendi" VALUES(27,91,'27:91','(Thuaju, o Muhamed): “Unë jam urdhëruar që të adhuroj vetëm Zotin e këtij Qyteti (Mekës), i Cili e ka bërë atë të shenjtë dhe të Cilit i përket gjithçka. Jam urdhëruar që t’i nënshtrohem Atij');
INSERT INTO "hasan_efendi" VALUES(27,92,'27:92','dhe të këndoj Kuranin.” Kushdo që merr rrugën e drejtë, e bën për dobi të vet. Ndërsa atyre që devijojnë, ti thuaju: “Unë jam vetëm paralajmërues”.');
INSERT INTO "hasan_efendi" VALUES(27,93,'27:93','Dhe thuaj: “Falënderimi qoftë për Allahun, Ai do t’jua tregojë shenjat e Tij e ju do t’i njihni ato!” E Zoti yt, nuk është i pakujdesshëm ndaj veprave që bëni ju.');
INSERT INTO "hasan_efendi" VALUES(28,1,'28:1','Tâ Sîn Mîm.');
INSERT INTO "hasan_efendi" VALUES(28,2,'28:2','Këto janë vargjet e Librit të qartë!');
INSERT INTO "hasan_efendi" VALUES(28,3,'28:3','Ne do të të tregojmë ty pjesë nga historia e Musait me Faraonin, ashtu siç ka qenë me të vërtetë, për njerëzit që besojnë.');
INSERT INTO "hasan_efendi" VALUES(28,4,'28:4','Me të vërtetë që Faraoni madhështohej në Tokë dhe banorët e saj i kishte përçarë në grupe; disa prej tyre i përndiqte, ua vriste fëmijët meshkuj, ndërsa femrat ua linte gjallë. Ai ka qenë vërtet keqbërës.');
INSERT INTO "hasan_efendi" VALUES(28,5,'28:5','Por vullneti Ynë ishte që t’u jepnim mirësi atyre që qenë të shtypur në tokë, t’i bënim prijës e trashëgimtarë,');
INSERT INTO "hasan_efendi" VALUES(28,6,'28:6','t’i forconim ata në Tokë dhe t’u tregonim Faraonit, Hamanit dhe ushtrisë së tyre atë prej së cilës ata frikësoheshin. ');
INSERT INTO "hasan_efendi" VALUES(28,7,'28:7','Kështu, Ne e frymëzuam nënën e Musait: “Jepi gji atij dhe, kur të trembesh për sigurinë e tij, hidhe në lumë, pa pasur frikë dhe as dëshpërim. Me siguri që Ne do të ta kthejmë ty atë dhe do ta bëjmë të dërguar.”');
INSERT INTO "hasan_efendi" VALUES(28,8,'28:8','Por atë e mori nga lumi familja e Faraonit, me qëllim që një ditë ai të bëhej armik dhe t’u sillte mjerim atyre, sepse Faraoni, Hamani dhe ushtria e tyre kanë qenë gjynahqarë.');
INSERT INTO "hasan_efendi" VALUES(28,9,'28:9','Gruaja e Faraonit i tha atij: “Ky (djalë) është gëzim për mua dhe ty, prandaj mos e vrisni, sepse mund të jetë i dobishëm ose mund ta birësojmë”. Por ata nuk e dinin se çfarë po bënin.');
INSERT INTO "hasan_efendi" VALUES(28,10,'28:10','Ndërkohë, zemra e nënës së Musait u bë si të ishte e zbrazët. Edhe pak dhe çdo gjë do të zbulohej, sikur të mos ia kishim forcuar Ne zemrën, që të bëhej besimtare e vërtetë.');
INSERT INTO "hasan_efendi" VALUES(28,11,'28:11','Ajo i tha motrës së Musait: “Shko pas tij!” E motra e vrojtonte atë nga larg, pa u parë nga të tjerët.');
INSERT INTO "hasan_efendi" VALUES(28,12,'28:12','Ne bëmë që ai të mos pranonte asnjë mëndeshë. Kështu, e motra i tha atyre (që kishin birësuar Musain): “A doni t’ju gjej një familje, e cila do të kujdeset për të, duke e edukuar me kujdes?”');
INSERT INTO "hasan_efendi" VALUES(28,13,'28:13','Kështu, Ne e kthyem atë te nëna e vet, që ajo të gëzohej e të mos pikëllohej dhe që ta dinte se premtimi i Allahut është i vërtetë; por, shumica e njerëzve nuk e dinë.');
INSERT INTO "hasan_efendi" VALUES(28,14,'28:14','Dhe, kur ai arriti moshën madhore dhe pjekurinë e duhur, Ne i dhamë atij mençuri dhe dituri. Kështu, i shpërblejmë Ne ata që bëjnë punë të mira.');
INSERT INTO "hasan_efendi" VALUES(28,15,'28:15','Një herë Musai hyri në qytet, pa u vënë re nga banorët e tij. Atje pa dy njerëz që rriheshin. Njëri ishte nga të vetët, kurse tjetri ishte nga kundërshtarët. Ai i grupit të vet i kërkoi ndihmë kundër atij të popullit armik. Dhe Musai i ra me grusht atij, duke e vrarë. Pastaj tha: “Kjo është punë e djallit! Ai është armik i betuar i njeriut dhe ngatërrestar i hapur!');
INSERT INTO "hasan_efendi" VALUES(28,16,'28:16','O Zoti im! Njëmend, Unë i kam bërë keq vetes, andaj më fal!” Dhe Ai e fali. Pa dyshim, Ai është Falësi e Mëshirëploti.');
INSERT INTO "hasan_efendi" VALUES(28,17,'28:17','Musai tha: “O Zoti im! Falë të mirave që më ke dhënë, unë nuk do të bëhem ndihmës i të këqijve!”');
INSERT INTO "hasan_efendi" VALUES(28,18,'28:18','Teksa Musai ecte në mëngjes i frikësuar në qytet, duke pritur se ç’do të ndodhte, kur ai që të djeshmen e kishte thirrur në ndihmë, e thirri sërish në ndihmë. Musai i tha: “Ti qenke tamam një ngatërrestar i hapur!”');
INSERT INTO "hasan_efendi" VALUES(28,19,'28:19','Dhe, kur deshi të sulmonte atë që ishte armik i të dyve, ai i tha: “O Musa, a do të më vrasësh edhe mua, siç vrave atë tjetrin dje?! Ti dëshiron vetëm që të bëhesh dhunues në tokë e nuk dëshiron të bëhesh nga ata që ndreqin e vendosin drejtësi”.');
INSERT INTO "hasan_efendi" VALUES(28,20,'28:20','Aty erdhi një njeri, duke nxituar prej skajit të qytetit, e tha: “O Musa, paria është mbledhur e po bisedon për të të vrarë, prandaj ik, sepse po ta jap një këshillë të sinqertë”.');
INSERT INTO "hasan_efendi" VALUES(28,21,'28:21','Musai doli prej qytetit i frikësuar, duke pritur se ç’do të ndodhte dhe tha: “O Zoti im, më shpëto prej popullit keqbërës!”');
INSERT INTO "hasan_efendi" VALUES(28,22,'28:22','Teksa drejtohej nga Medjeni, ai tha: “Shpresoj se Zoti im do të më tregojë rrugën e drejtë!”');
INSERT INTO "hasan_efendi" VALUES(28,23,'28:23','Kur arriti tek pusi i Medjenit, ndeshi rreth tij shumë njerëz që u jepnin ujë kafshëve. Përveç tyre, ai vuri re dy vasha që po mbanin kafshët e veta. Ai tha: “Si e keni hallin ju?” Ato thanë: “Ne nuk u japim ujë, pa u larguar barinjtë. Babai ynë është shumë plak”.');
INSERT INTO "hasan_efendi" VALUES(28,24,'28:24','Kështu, Musai u dha ujë kafshëve të tyre e pastaj u kthye te një hije dhe tha: “Zoti im, çfarëdo dhuntie që të më japësh, unë jam nevojtar!”');
INSERT INTO "hasan_efendi" VALUES(28,25,'28:25','Më vonë, njëra nga ato dyja, erdhi me ndrojtje dhe i tha: “Babai im të fton për të të shpërblyer, se i dhe ujë (tufës sonë)”. Kur Musai shkoi tek ai dhe i tregoi çfarë kishte përjetuar, ai tha: “Mos u frikëso, sepse ke shpëtuar prej një populli keqbërës!”');
INSERT INTO "hasan_efendi" VALUES(28,26,'28:26','“O babai im, - tha njëra nga ato të dyja - merre këtë njeri në shërbim! Sigurisht që njerëzit e fortë dhe të besueshëm janë më të mirët, që dikush mund të marrë në shërbim”.');
INSERT INTO "hasan_efendi" VALUES(28,27,'28:27','Plaku tha: “Unë dua të të jap për grua njërën prej këtyre dy bijave të mia, me kusht që të më shërbesh mua tetë vjet; por, nëse dëshiron, mund të qëndrosh dhjetë vjet. Unë nuk dëshiroj të t’i rëndoj gjërat. Ti do të shohësh, në dashtë Allahu, se jam një njeri i mirë.”');
INSERT INTO "hasan_efendi" VALUES(28,28,'28:28','Musai tha: “Le të jetë kështu (kjo marrëveshje) midis meje dhe teje. Cilindo prej të dy afateve që të plotësoj, nuk do të jetë padrejtësi për mua. Allahu është Dëshmues për atë që folëm.”');
INSERT INTO "hasan_efendi" VALUES(28,29,'28:29','Kur Musai e plotësoi afatin e caktuar dhe u nis me familjen e vet, vuri re nga larg një zjarr në anën e (malit të) Turit. I tha familjes së vet: “Pritni, sepse kam parë një zjarr. Ndoshta mund t’ju sjell prej tij ndonjë lajm ose një urë zjarri për t’u nxehur”.');
INSERT INTO "hasan_efendi" VALUES(28,30,'28:30','Kur erdhi afër, një zë e thirri nga ana e djathtë e luginës, nga një pemë në një vend të bekuar: “O Musa, në të vërtetë, Unë jam Allahu, Zoti i botëve!');
INSERT INTO "hasan_efendi" VALUES(28,31,'28:31','Hidhe shkopin tënd!” Kur e pa se shkopi po lëvizte shpejt, si të ishte gjarpër, u zmbraps me të shpejtë, pa shikuar pas, ndërsa zëri i tha: “O Musa! Afrohu dhe mos u frikëso, sepse ti je i mbrojtur.');
INSERT INTO "hasan_efendi" VALUES(28,32,'28:32','Fute dorën tënde në gji dhe ajo do të dalë e bardhë, e palënduar. Shtrëngoje krahun pas vetes që të çlirohesh prej frikës! Këto janë dy mrekulli prej Zotit tënd për Faraonin dhe parinë e tij; ata janë njëmend popull i mbrapshtë!”');
INSERT INTO "hasan_efendi" VALUES(28,33,'28:33','Musai tha: “O Zoti im, unë kam vrarë një njeri të tyre, prandaj kam frikë se do të më vrasin.');
INSERT INTO "hasan_efendi" VALUES(28,34,'28:34','Vëllai im, Haruni, është më gojëtar se unë në të folur, andaj dërgoje atë me mua, si ndihmës timin, që të vërtetojë fjalët e mia. Unë vërtet kam frikë se ata do të më quajnë gënjeshtar”.');
INSERT INTO "hasan_efendi" VALUES(28,35,'28:35','Allahu tha: “Ne do të të forcojmë ty me vëllanë tënd dhe që të dyve do t’ju japim fuqi. Kështu, ata nuk do të mund t’ju afrohen juve. Me mrekullitë Tona, ju të dy dhe ata që do t’ju pasojnë, do të jeni ngadhënjimtarë”.');
INSERT INTO "hasan_efendi" VALUES(28,36,'28:36','Dhe kur Musai u solli mrekullitë Tona të qarta, ata thanë: “Kjo është vetëm magji e trilluar; ne nuk e kemi dëgjuar këtë prej stërgjyshërve tanë të lashtë”.');
INSERT INTO "hasan_efendi" VALUES(28,37,'28:37','Musai tha: “Zoti im e di mirë se kush i sjell udhëzimet prej Tij dhe kush do të ketë mbarimin e mirë në jetën tjetër. Sigurisht, keqbërësit nuk do të shpëtojnë.”');
INSERT INTO "hasan_efendi" VALUES(28,38,'28:38','Dhe Faraoni tha: “O pari! Unë nuk di që për ju ka tjetër zot përveç meje! O Haman, më ndez një zjarr për të pjekur qerpiçë dhe më ndërto një kullë të lartë që të ngjitem te Zoti i Musait. Jam i bindur se ai (Musai) gënjen!”');
INSERT INTO "hasan_efendi" VALUES(28,39,'28:39','Faraoni dhe ushtria e tij u sollën me arrogancë dhe padrejtësisht në tokë, duke menduar se nuk do të ktheheshin te Ne.');
INSERT INTO "hasan_efendi" VALUES(28,40,'28:40','Por Ne e kapëm atë dhe ushtrinë e tij dhe i hodhëm në det. Shiko se cili ishte fundi i keqbërësve!');
INSERT INTO "hasan_efendi" VALUES(28,41,'28:41','Ne i bëmë ata udhëheqës, që thërrisnin për në zjarr, ndërsa në Ditën e Kiametit askush nuk do t’i ndihmojë.');
INSERT INTO "hasan_efendi" VALUES(28,42,'28:42','Në këtë botë Ne hodhëm mallkim mbi ta, ndërsa në Ditën e Kiametit ata do të jenë të braktisur e të shkatërruar.');
INSERT INTO "hasan_efendi" VALUES(28,43,'28:43','Pasi i shkatërruam breznitë e mëparshme, Ne i dhamë Musait Librin, si dritë për njerëzit, udhëzim dhe mëshirë, në mënyrë që ata të mund të merrnin këshilla.');
INSERT INTO "hasan_efendi" VALUES(28,44,'28:44','Ti (o Muhamed) nuk ke qenë i pranishëm në anën perëndimore (të Turit), kur Ne ia besuam Musait Misionin (Profecinë) dhe as nuk ke qenë dëshmitar i kësaj ngjarjeje.');
INSERT INTO "hasan_efendi" VALUES(28,45,'28:45','Pastaj, Ne krijuam brezni të tjera që jetuan shumë gjatë. Ti nuk ke banuar midis banorëve të Medjenit, që t’iu lexoje shpalljet Tona, por Ne gjithnjë kemi çuar të dërguar.');
INSERT INTO "hasan_efendi" VALUES(28,46,'28:46','Ti nuk ke qenë as pranë Turit, kur Ne thirrëm Musain. Por ti je dërguar si mëshirë nga Zoti yt për të paralajmëruar njerëzit, të cilëve nuk u kishte ardhur kurrfarë paralajmëruesi para teje, që ata të mund të marrin këshilla');
INSERT INTO "hasan_efendi" VALUES(28,47,'28:47','dhe, që të mos thonë, kur t’i godasë dënimi, për atë që kanë bërë: “O Zoti ynë, të na kishe çuar një të dërguar që t’i pasonim shpalljet Tuaja dhe të bëheshim besimtarë”.');
INSERT INTO "hasan_efendi" VALUES(28,48,'28:48','Pasi u erdhi e Vërteta prej Nesh, ata thanë: “Përse nuk i është dhënë (Muhamedit) diçka e ngjashme me atë që i ishte dhënë Musait!” Por, a nuk e mohojnë ata çfarë i ishte dhënë Musait, duke thënë: “Këta (Teurati dhe Kurani) janë dy magji, që mbështesnin njëra-tjetrën. Ne nuk besojmë asnjërin prej tyre”.');
INSERT INTO "hasan_efendi" VALUES(28,49,'28:49','Thuaju atyre: “Sillni një libër nga Allahu, i cili udhëzon më mirë nga këta të dy, unë do ta pasoj atë, nëse thoni të vërtetën!”');
INSERT INTO "hasan_efendi" VALUES(28,50,'28:50','E nëse ata nuk të përgjigjen ty, dije se ata janë skllevër të dëshirave të veta. E kush është në humbje më të madhe, se ata që udhëhiqen nga dëshirat e veta, pa udhëzim prej Allahut? Sigurisht, Allahu nuk i udhëzon njerëzit keqbërës.');
INSERT INTO "hasan_efendi" VALUES(28,51,'28:51','Ne ua dërgojmë atyre shpalljet njërën pas tjetrës, në mënyrë që t’ua vënë veshin.');
INSERT INTO "hasan_efendi" VALUES(28,52,'28:52','Ata që Ne ua kemi zbritur Librin para tij (Kuranit), i besojnë atij.');
INSERT INTO "hasan_efendi" VALUES(28,53,'28:53','Kur u lexohet atyre (Kurani), ata thonë: “Ne e besojmë atë. Pa dyshim, ai është e vërteta nga Zoti ynë. Vërtet, Ne edhe para tij kemi qenë të nënshtruar ndaj Zotit.”');
INSERT INTO "hasan_efendi" VALUES(28,54,'28:54','Atyre do t’u jepet shpërblim i dyfishtë, për shkak të durimit që kanë treguar. Ata e zmbrapsin të keqen me të mirë, ndajnë lëmoshë nga ajo që u kemi dhënë Ne');
INSERT INTO "hasan_efendi" VALUES(28,55,'28:55','dhe, kur dëgjojnë ndonjë marrëzi, i shmangen asaj, duke thënë: “Ne kemi veprat tona, ndërsa ju keni veprat tuaja! Paqja qoftë me ju! Ne nuk duam shoqëri me të paditurit”.');
INSERT INTO "hasan_efendi" VALUES(28,56,'28:56','Vërtet, ti (Muhamed) nuk mund të udhëzosh kë të duash, por është Allahu Ai që udhëzon kë të dëshirojë. Ai i njeh më mirë ata që janë të udhëzuar.');
INSERT INTO "hasan_efendi" VALUES(28,57,'28:57','Ata thonë: “Nëse ndjekim udhëzimin së bashku me ty, së shpejti do të jemi të dëbuar nga vendlindja”. Vallë, a nuk ua kemi mundësuar, që të jetojnë në një vend të shenjtë e të sigurt, ku sillen fruta të llojllojshme, si ushqim prej Nesh? Por shumica e tyre nuk e dinë.');
INSERT INTO "hasan_efendi" VALUES(28,58,'28:58','Eh, sa e sa fshatra e qytete i kemi zhdukur Ne, sepse banorët e tyre bënin jetë të shfrenuar! Ja tek janë shtëpitë e tyre, të cilat janë banuar pas tyre fare pak ose aspak. Jemi Ne trashëgimtarët e fundit.');
INSERT INTO "hasan_efendi" VALUES(28,59,'28:59','Zoti yt kurrë nuk i ka shkatërruar këto vendbanime, para se t’u çonte në kryeqytetet e tyre të dërguar, që t’u lexonte shpalljet e Tij. Ne kurrë nuk i kemi shkatërruar vendbanimet (pa shkak), por vetëm kur banorët e tyre kanë qenë keqbërës.');
INSERT INTO "hasan_efendi" VALUES(28,60,'28:60','Çdo gjë që ju është dhënë juve, ajo është kënaqësi e kësaj jete dhe stoli e saj, ndërsa ajo që është tek Allahu është më e mirë dhe më e përhershme. Vallë, a nuk e kuptoni?!');
INSERT INTO "hasan_efendi" VALUES(28,61,'28:61','Si mund të jetë i njëjtë ai, që i kemi premtuar shpërblim të mirë, të cilin do ta arrijë dhe ai që i kemi dhënë kënaqësi në jetën e kësaj bote, ndërkohë që në Ditën e Kiametit, do të hidhet në zjarr?!');
INSERT INTO "hasan_efendi" VALUES(28,62,'28:62','Atë Ditë, Allahu do t’i thërrasë ata dhe do t’u thotë: “Ku janë ata për të cilët pretendonit se ishin ortakët e Mi?!”');
INSERT INTO "hasan_efendi" VALUES(28,63,'28:63','Ata që kanë merituar dënimin Tonë, do të thonë: “O Zoti ynë, këta janë ata, që ne i çuam në rrugë të shtrembër. Ne i çuam ata në rrugë të shtrembër, ashtu siç shkuam edhe vetë në atë rrugë. Ne deklarohemi të pafajshëm para Teje; ata nuk na kanë adhuruar ne”.');
INSERT INTO "hasan_efendi" VALUES(28,64,'28:64','Atyre do t’u thuhet: “Thirrni hyjnitë tuaja!” - dhe ata do t’i thërrasin ato, por nuk do të marrin asnjë përgjigje. Ata do të shohin dënimin dhe do të dëshirojnë që të kishin qenë në rrugën e drejtë!');
INSERT INTO "hasan_efendi" VALUES(28,65,'28:65','Atë Ditë, Ai do t’i thërrasë, duke u thënë: “Ç’përgjigje iu dhatë të dërguarve?”');
INSERT INTO "hasan_efendi" VALUES(28,66,'28:66','Atë ditë, argumentet e tyre do t’u ngatërrohen e ata nuk do të mund të pyesin njëri - tjetrin.');
INSERT INTO "hasan_efendi" VALUES(28,67,'28:67','Porse, ai që pendohet, beson dhe bën vepra të mira, do të jetë i shpëtuar.');
INSERT INTO "hasan_efendi" VALUES(28,68,'28:68','Zoti yt krijon ç’të dojë dhe zgjedh lirisht, ndërsa ata (idhujt) nuk mund të zgjedhin. Qoftë i lavdëruar Allahu dhe i lartësuar mbi gjithçka që ia shoqërojnë Atij (në adhurim)!');
INSERT INTO "hasan_efendi" VALUES(28,69,'28:69','Zoti yt e di çfarë fshehin zemrat e tyre dhe çfarë shprehin.');
INSERT INTO "hasan_efendi" VALUES(28,70,'28:70','I tillë është Allahu! S’ka zot tjetër të vërtetë përveç Tij! Atij i takojnë falënderimet dhe lavdërimet në këtë jetë dhe në tjetrën! Vetëm Atij i përket vendimi dhe tek Ai do të ktheheni.');
INSERT INTO "hasan_efendi" VALUES(28,71,'28:71','Thuaju (o Muhamed): “Pa mendoni, nëse Allahu jua bën natën të përhershme deri në Ditën e Kiametit, cili zot, përveç Tij, do t’jua sillte dritën? Vallë, a nuk dëgjoni?”');
INSERT INTO "hasan_efendi" VALUES(28,72,'28:72','Thuaju (o Muhamed): “Pa mendoni, nëse Allahu jua bën ditën të përhershme deri në Ditën e Kiametit, cili zot, përveç Tij, do t’jua sillte natën, në të cilën qetësoheni? Vallë, a nuk shihni?');
INSERT INTO "hasan_efendi" VALUES(28,73,'28:73','Prej mëshirës së Vet, Ai ka bërë për ju natën dhe ditën, që të qetësoheni, të kërkoni nga mirësia e Tij dhe të jeni mirënjohës.”');
INSERT INTO "hasan_efendi" VALUES(28,74,'28:74','Atë Ditë, Ai do t’i thërrasë, duke u thënë: “Ku janë ata që pretendonit, se ishin ortakët e Mi?!”');
INSERT INTO "hasan_efendi" VALUES(28,75,'28:75','Dhe prej çdo populli, Ne do të nxjerrim nga një dëshmitar e do të themi: “Paraqitni provat tuaja!” Atëherë, ata do ta marrin vesh se e vërteta është (vetëm) e Allahut dhe do të braktisen nga zotat që i trilluan vetë.');
INSERT INTO "hasan_efendi" VALUES(28,76,'28:76','Me të vërtetë, Karuni ka qenë nga populli i Musait, por i shtypte ata. Ne i patëm dhënë atij aq thesare, saqë çelësat e tyre vështirë se do të mund t’i mbante një grup njerëzish të fuqishëm. Njerëzit e popullit të tij i thanë: “Mos u ngazëlle me pasurinë që ke, sepse Allahu nuk i do ata që ngazëllehen!');
INSERT INTO "hasan_efendi" VALUES(28,77,'28:77','Por përpiqu me atë që të ka dhënë Allahu të fitosh jetën e botës së ardhshme, duke mos harruar pjesën tënde në këtë botë. Bëju mirë të tjerëve, ashtu si të ka bërë mirë ty Allahu. Mos kërko ngatërresa në Tokë, sepse Allahu nuk i do ngatërrestarët.”');
INSERT INTO "hasan_efendi" VALUES(28,78,'28:78','Ai tha: “Kjo më është dhënë vetëm prej diturisë sime”. Vallë, a nuk e dinte ai, se Allahu kishte zhdukur para tij shumë brezni, që kishin qenë më të fuqishëm dhe që kishin grumbulluar më shumë pasuri se ai? Por keqbërësit nuk do të pyeten për gjynahet e tyre.');
INSERT INTO "hasan_efendi" VALUES(28,79,'28:79','Kur ai dilte para popullit të vet me stolitë e tij, ata që lakmonin jetën e kësaj bote thanë: “Ah! Sikur të na ishte dhënë edhe neve si Karunit! Vërtet që ai është njeri me fat.”');
INSERT INTO "hasan_efendi" VALUES(28,80,'28:80','Ndërsa njerëzit e dijshëm thoshin: “Të mjerët ju! Shpërblimi i Allahut është më i mirë për atë që beson dhe bën vepra të mira. Ai do t’u jepet vetëm të duruarve”.');
INSERT INTO "hasan_efendi" VALUES(28,81,'28:81','Ne bëmë që Karunin dhe shtëpinë e tij t’i gëlltiste toka dhe askush nuk arriti që ta mbronte nga dënimi i Allahut, madje as ai vetë nuk mund ta mbronte veten.');
INSERT INTO "hasan_efendi" VALUES(28,82,'28:82','Të nesërmen, ata që më parë dëshironin të kishin qenë në vend të tij, thoshin: “Është shumë e qartë se Allahu është Ai që i jep bollëk dhe ia pakëson kujt të dojë prej robërve të Tij. Sikur Allahu të mos kishte mëshirë për ne, me të vërtetë që do të na kishte bërë të na gëlltiste toka. Ububu! Është shumë e qartë se mohuesit nuk kanë shpëtim!”');
INSERT INTO "hasan_efendi" VALUES(28,83,'28:83','Sa për shtëpinë e botës tjetër, Ne do ta caktojmë atë për ata që nuk kërkojnë as madhështi e as ngatërresa në Tokë. Përfundimi (i mirë) është për ata që ruhen nga të këqijat.');
INSERT INTO "hasan_efendi" VALUES(28,84,'28:84','Kush bën një të mirë, do të ketë shpërblim më të mirë se ajo. Ndërsa ai që bën një të keqe, do të dënohet vetëm aq sa e meriton vepra e tij.');
INSERT INTO "hasan_efendi" VALUES(28,85,'28:85','Me të vërtetë, Ai që ta ka urdhëruar ty (Muhamed) Kuranin, do të të kthejë në vendin e caktuar (në Mekë). Thuaj: “Zoti im e di mirë se kush sjell udhëzim të drejtë dhe kush është në humbje të qartë.”');
INSERT INTO "hasan_efendi" VALUES(28,86,'28:86','Ti nuk ke shpresuar se do të të shpallej Libri, por e more nga mëshira e Zotit tënd; andaj, kurrsesi mos u bëj ndihmës i mohuesve!');
INSERT INTO "hasan_efendi" VALUES(28,87,'28:87','Kurrsesi mos i lejo ata të të largojnë prej shpalljeve të Allahut, pasi të janë zbritur ty! Lutju Zotit tënd dhe kurrsesi mos u bëj idhujtar!');
INSERT INTO "hasan_efendi" VALUES(28,88,'28:88','Mos adhuro zot tjetër krahas Allahut! S’ka zot tjetër të vërtetë përveç Tij! Çdo gjë do të shkatërrohet, përveç Fytyrës (Qenies) së Tij! Atij i përket vendimi dhe tek Ai do të ktheheni të gjithë!');
INSERT INTO "hasan_efendi" VALUES(29,1,'29:1','Elif, Lâm, Mîm.');
INSERT INTO "hasan_efendi" VALUES(29,2,'29:2','Vërtet mendojnë njerëzit, se do të lihen të thonë “Ne besojmë”, pa u vënë në provë?!');
INSERT INTO "hasan_efendi" VALUES(29,3,'29:3','Ne i kemi sprovuar ata që kanë qenë para tyre, në mënyrë që, Allahu të dallojë ata, që thonë të vërtetën dhe, ata që gënjejnë.');
INSERT INTO "hasan_efendi" VALUES(29,4,'29:4','A mos mendojnë ata që bëjnë vepra të këqija, se do t’i shpëtojnë dënimit Tonë?! Sa keq që gjykojnë ata!');
INSERT INTO "hasan_efendi" VALUES(29,5,'29:5','Kush shpreson takimin e Allahut, ta dijë se Dita e caktuar nga Ai do të arrijë. Ai dëgjon dhe di gjithçka.');
INSERT INTO "hasan_efendi" VALUES(29,6,'29:6','Kush përpiqet, përpiqet vetëm për të mirën e vetvetes, se Allahu, në të vërtetë nuk ka nevojë për asgjë.');
INSERT INTO "hasan_efendi" VALUES(29,7,'29:7','Atyre, që besojnë dhe bëjnë vepra të mira, me siguri që do t’ua shlyejmë të këqijat dhe do t’u japim shpërblim më të mirë se puna që kanë bërë.');
INSERT INTO "hasan_efendi" VALUES(29,8,'29:8','Ne e kemi urdhëruar njeriun, që të sillet mirë me prindërit e vet dhe: “Nëse ata të nxisin ty që të më shoqërosh Mua (në adhurim) atë, për të cilin ti nuk di asgjë, atëherë mos i dëgjo ata.” Tek Unë do të ktheheni të gjithë e do t’ju lajmëroj për gjithçka që keni bërë.');
INSERT INTO "hasan_efendi" VALUES(29,9,'29:9','Ata që besojnë dhe bëjnë vepra të mira, me siguri do t’i radhisim në mesin e të mirëve.');
INSERT INTO "hasan_efendi" VALUES(29,10,'29:10','Ka disa njerëz, që thonë: “Ne besojmë Allahun”. Por, kur vuajnë për çështjen e Allahut, e quajnë përndjekjen prej njeriut njësoj si dënimin e Allahut. Ndërsa, kur u vjen fitorja prej Zotit tënd, ata, me siguri - do të thonë: “Ne kemi qenë me ju”. Vallë! A nuk e di Allahu më së miri se ç’ka në zemrat e çdokujt?');
INSERT INTO "hasan_efendi" VALUES(29,11,'29:11','Sigurisht, Allahu i njeh më mirë ata që kanë besuar dhe hipokritët.');
INSERT INTO "hasan_efendi" VALUES(29,12,'29:12','Mohuesit u thonë besimtarëve: “Ndiqni rrugën tonë e ne atëherë do t’i mbartim gjynahet tuaja!” Por ata nuk do të jenë në gjendje të mbartin nga gjynahet e tyre; në të vërtetë, ata janë gënjeshtarë.');
INSERT INTO "hasan_efendi" VALUES(29,13,'29:13','Ata (mohuesit), do t’i mbartin barrët e veta e bashkë me to edhe barrë të tjera dhe patjetër, që do të pyeten Ditën e Kiametit për gjithçka që trillonin.');
INSERT INTO "hasan_efendi" VALUES(29,14,'29:14','Ne e dërguam Nuhun te populli i tij. Ai qëndroi tek ata një mijë pa pesëdhjetë vjet, e megjithatë, ata i përfshiu përmbytja, duke qenë keqbërës.');
INSERT INTO "hasan_efendi" VALUES(29,15,'29:15','Ne e shpëtuam Nuhun së bashku me ata që qenë në barkë dhe e bëmë shenjë për të gjithë brezat.');
INSERT INTO "hasan_efendi" VALUES(29,16,'29:16','Kujtoje Ibrahimin, kur i tha popullit të vet: “Adhuroni Allahun dhe frikësojuni Atij! Kjo është më e mirë për ju, nëse e dini.');
INSERT INTO "hasan_efendi" VALUES(29,17,'29:17','Vërtet, ata që ju i adhuroni në vend të Allahut, janë vetëm idhuj. Ju po shpifni gënjeshtra. Në të vërtetë, ata që ju i adhuroni në vend të Allahut, nuk kanë pushtet t’ju furnizojnë, andaj, vetëm tek Allahu kërkoni risk dhe adhuroni e falënderoni vetëm Atë. Tek Ai do të ktheheni.');
INSERT INTO "hasan_efendi" VALUES(29,18,'29:18','Nëse ju nuk më besoni, dijeni se edhe popujt e mëparshëm i kanë quajtur gënjeshtarë (të dërguarit) dhe se i Dërguari ka për detyrë vetëm që të shpallë qartazi.”');
INSERT INTO "hasan_efendi" VALUES(29,19,'29:19','Vallë, a nuk e vërejnë ata se si Allahu e zë fill krijimin, pastaj e ripërsërit atë? Natyrisht, kjo është e lehtë për Allahun.');
INSERT INTO "hasan_efendi" VALUES(29,20,'29:20','Thuaj: “Udhëtoni nëpër Tokë dhe shikoni se si Ai e ka zënë fill krijimin. Pastaj do të jetë Allahu Ai që do të bëjë krijimin e dytë (ringjalljen). Me të vërtetë, Allahu është i Fuqishëm për çdo gjë.');
INSERT INTO "hasan_efendi" VALUES(29,21,'29:21','Ai ndëshkon kë të dëshirojë dhe mëshiron kë të dëshirojë; tek Ai do të ktheheni.');
INSERT INTO "hasan_efendi" VALUES(29,22,'29:22','Ndëshkimit të Tij nuk mund t’i shpëtoni as në Tokë e as në qiell dhe, përveç Allahut, nuk keni as mbrojtës, as ndihmës.');
INSERT INTO "hasan_efendi" VALUES(29,23,'29:23','Ata që mohojnë shpalljet e Allahut dhe takimin e Tij, pikërisht ata e kanë humbur shpresën në mëshirën Time dhe për ta do të ketë dënim të dhembshëm.”');
INSERT INTO "hasan_efendi" VALUES(29,24,'29:24','Populli i tij nuk kishte tjetër përgjigje, përveçse tha: “Vriteni atë ose digjeni!” Por Allahu e shpëtoi atë nga zjarri. Vërtet, këtu ka shenja të qarta për njerëzit që besojnë.');
INSERT INTO "hasan_efendi" VALUES(29,25,'29:25','Dhe ai tha: “Ju, në vend të Allahut, keni zgjedhur idhujt, për shkak të dashurisë midis jush në këtë jetë. Por, në Ditën e Kiametit do ta mohoni njëri-tjetrin dhe do ta mallkoni njëri tjetrin. Streha juaj do të jetë zjarri dhe nuk do të ketë për ju kurrfarë ndihmësi.”');
INSERT INTO "hasan_efendi" VALUES(29,26,'29:26','Luti i besoi atij dhe tha: “Unë do të mërgoj te Zoti im; me të vërtetë, Ai është i Plotfuqishmi dhe i Urti.”');
INSERT INTO "hasan_efendi" VALUES(29,27,'29:27','Dhe Ne i dhuruam atij Is’hakun dhe Jakubin dhe pasardhësve të tij u dhamë Profecinë dhe Librin. Ne i dhamë atij shpërblim në këtë botë, ndërsa në jetën tjetër, me siguri, ai do të jetë prej njerëzve të mirë.');
INSERT INTO "hasan_efendi" VALUES(29,28,'29:28','Kur Luti i tha popullit të vet: “Vërtet, ju bëni të tilla lapërdhi, që askush para jush nuk ka bërë në botë:');
INSERT INTO "hasan_efendi" VALUES(29,29,'29:29','bëni marrëdhënie me meshkuj, u zini pusi atyre dhe në tubimet tuaja bëni punët më të ndyra”, populli i tij nuk kishte tjetër përgjigje, përveç se tha: “Na e sill dënimin e Allahut, nëse thua të vërtetën”!');
INSERT INTO "hasan_efendi" VALUES(29,30,'29:30','Ai tha: “O Zoti im, më ndihmo kundër popullit që bën mbrapshti”!');
INSERT INTO "hasan_efendi" VALUES(29,31,'29:31','Dhe, pasi të dërguarit Tanë i sollën lajmin e mirë Ibrahimit, i thanë: “Ne do t’i zhdukim banorët e këtij vendbanimi, sepse banorët e tij janë vërtet keqbërës”.');
INSERT INTO "hasan_efendi" VALUES(29,32,'29:32','Ai (Ibrahimi) tha: “Por atje banon edhe Luti!” Ata thanë: “Ne e dimë më mirë kush është atje. Ne do ta shpëtojmë atë dhe familjen e tij, përveç gruas së tij: ajo do të qëndrojë me ata që do të mbesin (për t’u dënuar)”.');
INSERT INTO "hasan_efendi" VALUES(29,33,'29:33','Kur të dërguarit Tanë (engjëj) shkuan tek Luti, ai u shqetësua dhe u ngushtua (për ata, sepse nuk i mbronte dot). Ata i thanë: “Mos u frikëso dhe mos u brengos! Ne do të të shpëtojmë ty dhe familjen tënde, përveç gruas tënde. Ajo do të mbesë me ata që janë për dënim.');
INSERT INTO "hasan_efendi" VALUES(29,34,'29:34','Në të vërtetë, Ne do të lëshojmë dënim nga qielli, mbi banorët e këtij vendbanimi , ngase ata janë të prishur”.');
INSERT INTO "hasan_efendi" VALUES(29,35,'29:35','Dhe tashmë Ne e kemi lënë atë një shenjë të dukshme për njerëzit që kuptojnë.');
INSERT INTO "hasan_efendi" VALUES(29,36,'29:36','Edhe banorëve të Medjenit (i dërguam) vëllanë e tyre, Shuajbin. Ai u tha: “O populli im! Adhuroni Allahun, shpresoni në Ditën e Fundit dhe mos bëni keq në tokë, si ngatërrestarë!”');
INSERT INTO "hasan_efendi" VALUES(29,37,'29:37','Por, ata e përgënjeshtruan atë, prandaj i goditi një tërmet i tmerrshëm e si pasojë u gdhinë në shtëpitë e tyre të vdekur e të gjunjëzuar.');
INSERT INTO "hasan_efendi" VALUES(29,38,'29:38','E njëjta gjë u ndodhi edhe fiseve Ad dhe Themud, gjë që ju është bërë e qartë prej (gjurmëve të) banimeve të tyre. Atyre djalli ua kishte zbukuruar veprat e veta dhe i kishte shmangur nga rruga e drejtë, edhe pse qenë largpamës.');
INSERT INTO "hasan_efendi" VALUES(29,39,'29:39','Kështu ndodhi edhe me Karunin, Faraonin dhe Hamanin! Atyre u vajti Musai me prova të qarta, por ata treguan mendjemadhësi në Tokë dhe nuk i shpëtuan dënimit.');
INSERT INTO "hasan_efendi" VALUES(29,40,'29:40','Ne të gjithë i dënuam për gjynahun e vet: disave u dërguam furtunë, disa syresh i goditi një zë i tmerrshëm, disa prej tyre i gëlltiti toka dhe të tjerët i përmbyti uji. Allahu nuk u bëri atyre padrejtësi, por ata i bënë padrejtësi vetvetes. ');
INSERT INTO "hasan_efendi" VALUES(29,41,'29:41','Rasti i atyre, që zgjedhin mbrojtës të tjerë në vend të Allahut, është si rasti i merimangës që zgjedh rrjetën e vet për shtëpi. Por shtëpia më e dobët është pikërisht rrjeta e merimangës. Veç sikur ta dinin ata!');
INSERT INTO "hasan_efendi" VALUES(29,42,'29:42','Sigurisht, Allahu e di se çfarë adhurojnë ata në vend të Tij; Ai është i Plotfuqishmi, i Gjithëdijshmi.');
INSERT INTO "hasan_efendi" VALUES(29,43,'29:43','Këta shembuj Ne ua paraqesim njerëzve, por ata nuk i kupton kush, përveç dijetarëve.');
INSERT INTO "hasan_efendi" VALUES(29,44,'29:44','Allahu i ka krijuar qiejt dhe Tokën me qëllim të plotë. Kjo është një shenjë për besimtarët!');
INSERT INTO "hasan_efendi" VALUES(29,45,'29:45','Lexo (o Muhamed) atë që të është zbritur ty prej Librit (Kuranit) dhe fale namazin! Vërtet që namazi të ruan nga shthurja dhe nga çdo vepër e shëmtuar dhe vërtet që përmendja e Allahut është më e madhe! Allahu di gjithçka që ju bëni.');
INSERT INTO "hasan_efendi" VALUES(29,46,'29:46','Diskutoni me ithtarët e Librit vetëm me mënyrën më të mirë, por jo me keqbërësit e tjerë. Dhe thoni: “Ne besojmë në atë që na është zbritur neve dhe në atë që ju është zbritur juve. Zoti ynë dhe Zoti juaj është një - dhe ne Atij i nënshtrohemi si myslimanë”.');
INSERT INTO "hasan_efendi" VALUES(29,47,'29:47','Kështu, Ne të kemi shpallur ty (Muhamed) Librin (Kuranin). Ata që u kemi dhënë Shkrimet, e besojnë atë. Por ka edhe nga ata (mekas) që e besojnë atë. Shpalljet Tona i mohojnë vetëm jobesimtarët.');
INSERT INTO "hasan_efendi" VALUES(29,48,'29:48','Para këtij , ti (Muhamed) nuk kishe lexuar kurrfarë libri e as që kishe shkruar gjë me dorën tënde; përndryshe, ndjekësit e së pavërtetës do të dyshonin. ');
INSERT INTO "hasan_efendi" VALUES(29,49,'29:49','Përkundrazi! Ai (Kurani) është shpallje e qartë në zemrat e atyre, të cilëve u është dhënë dija. Shpalljet Tona i mohojnë vetëm keqbërësit.');
INSERT INTO "hasan_efendi" VALUES(29,50,'29:50','Ata thonë: “Përse nuk i janë zbritur (Muhamedit) mrekulli nga Zoti i tij?” Thuaj (o Muhamed): “Mrekullitë janë vetëm tek Allahu; unë jam vetëm një paralajmërues i qartë”.');
INSERT INTO "hasan_efendi" VALUES(29,51,'29:51','Vallë, a nuk u mjafton atyre që Ne të kemi shpallur ty Librin që u lexohet atyre? Me të vërtetë që në të ka mëshirë dhe këshillë për njerëzit që besojnë.');
INSERT INTO "hasan_efendi" VALUES(29,52,'29:52','Thuaj: “Allahu mjafton si dëshmitar midis meje dhe jush. Ai di çfarë ka në qiej dhe në Tokë.” Ata që besojnë në trillime dhe nuk besojnë në Allahun, pikërisht ata janë të humburit.');
INSERT INTO "hasan_efendi" VALUES(29,53,'29:53','Ata kërkojnë prej teje shpejtimin e dënimit. Sikur të mos ishte afati i caktuar, atyre do t’u kishte ardhur dënimi. Me siguri që atyre do t’u vijë papritmas e pa vënë re.');
INSERT INTO "hasan_efendi" VALUES(29,54,'29:54','Ata kërkojnë prej teje shpejtimin e dënimit, por, në të vërtetë, Xhehenemi do t’i përfshijë të gjithë mohuesit.');
INSERT INTO "hasan_efendi" VALUES(29,55,'29:55','Ditën, kur do t’i kaplojë dënimi prej së larti dhe prej së poshtmi (engjëlli i ngarkuar me ndëshkimin e fajtorëve), do t’u thotë: “Shijoni dënimin për atë që keni bërë!”');
INSERT INTO "hasan_efendi" VALUES(29,56,'29:56','O robërit e Mi, që keni besuar! Toka Ime është vërtet e gjerë, andaj më adhuroni vetëm Mua!');
INSERT INTO "hasan_efendi" VALUES(29,57,'29:57','Çdo shpirt do ta shijojë vdekjen dhe pastaj të gjithë do të ktheheni tek Ne.');
INSERT INTO "hasan_efendi" VALUES(29,58,'29:58','Ata që besojnë dhe bëjnë vepra të mira, Ne do t’i vendosim në pallatet e Xhenetit, ku rrjedhin lumenj; aty do të qëndrojnë përgjithmonë. Sa i bukur është shpërblimi i atyre që punojnë mirë,');
INSERT INTO "hasan_efendi" VALUES(29,59,'29:59','e të cilët kanë duruar dhe janë mbështetur te Zoti i tyre.');
INSERT INTO "hasan_efendi" VALUES(29,60,'29:60','Sa krijesa ka që nuk e mbajnë dot veten me ushqim! Allahu u jep atyre dhe juve! Ai dëgjon dhe di gjithçka.');
INSERT INTO "hasan_efendi" VALUES(29,61,'29:61','Nëse ti (Muhamed) i pyet ata se kush i ka krijuar qiejt dhe Tokën dhe kush i ka nënshtruar (të rrotullohen) Diellin e Hënën, - me siguri që ata do të të thonë “Allahu!” Atëherë, si po largohen (nga rruga e drejtë)?');
INSERT INTO "hasan_efendi" VALUES(29,62,'29:62','Allahu, ia zgjeron furnizimin kujt të dojë nga robërit e Tij, e dikujt ia zvogëlon. Allahu është vërtet i Dijshëm për çdo gjë.');
INSERT INTO "hasan_efendi" VALUES(29,63,'29:63','Nëse ti i pyet ata se kush e zbret ujin nga qielli dhe nëpërmjet tij e gjallëron tokën pas vdekjes së saj, - me siguri që ata do të të thonë “Allahu!” Thuaj: “Qoftë lëvduar Allahu!” Por, shumica e tyre nuk mendojnë.');
INSERT INTO "hasan_efendi" VALUES(29,64,'29:64','Jeta e kësaj bote, nuk është gjë tjetër, veçse argëtim dhe lojë. Ndërsa jeta e botës tjetër, pikërisht ajo është jeta e vërtetë. Veç sikur ta dinin!');
INSERT INTO "hasan_efendi" VALUES(29,65,'29:65','Kur hipin në anije, ata i luten Allahut me devotshmëri të sinqertë, por kur Ai i sjell në tokë shëndoshë e mirë, menjëherë ata i veshin Atij ortakë (në adhurim),');
INSERT INTO "hasan_efendi" VALUES(29,66,'29:66','për të mohuar atë që u kemi dhënë Ne dhe për t’u kënaqur. Por shpejt do ta marrin vesh!');
INSERT INTO "hasan_efendi" VALUES(29,67,'29:67','Vallë, a nuk e shohin ata se si Ne u kemi dhënë atyre një vend të shenjtë e të sigurt (Mekën), ndërkohë që njerëzit rreth tij po grabiten? A mos vallë ata besojnë në gjëra të kota, ndërsa dhuntitë e Allahut i mohojnë?!');
INSERT INTO "hasan_efendi" VALUES(29,68,'29:68','Kush është keqbërës më i madh se ai që trillon gënjeshtra kundër Allahut ose mohon të vërtetën që i ka ardhur?! Vallë, a nuk është Xhehenemi vend për jobesimtarët?!');
INSERT INTO "hasan_efendi" VALUES(29,69,'29:69','Ata që luftojnë për çështjen Tonë, sigurisht, Ne do t’i udhëzojmë drejt udhëve Tona. Vërtet, Allahu është me punëmirët!');
INSERT INTO "hasan_efendi" VALUES(30,1,'30:1','Elif, Lâm, Mîm.');
INSERT INTO "hasan_efendi" VALUES(30,2,'30:2','U mundën bizantinët,');
INSERT INTO "hasan_efendi" VALUES(30,3,'30:3','në afërsi të tokës (arabe), por pas thyerjes së tyre, me siguri do të fitojnë');
INSERT INTO "hasan_efendi" VALUES(30,4,'30:4','brenda disa vjetëve. Me të vërtetë, është vendimi i Allahut, si për të parën dhe për të dytën. Dhe, atë ditë, do të gëzohen besimtarët');
INSERT INTO "hasan_efendi" VALUES(30,5,'30:5','për ndihmën e Allahut; Ai ndihmon kë të dojë dhe është i Plotfuqishëm e Mëshirëplotë.');
INSERT INTO "hasan_efendi" VALUES(30,6,'30:6','(Ky është) premtimi i Allahut, e Allahu nuk e thyen premtimin e vet, por shumica e njerëzve nuk e dinë.');
INSERT INTO "hasan_efendi" VALUES(30,7,'30:7','Ata dinë vetëm anën e dukshme të kësaj jete, ndërsa për jetën tjetër janë të pakujdesshëm.');
INSERT INTO "hasan_efendi" VALUES(30,8,'30:8','Vallë, a nuk mendojnë ata në vetvete? Allahu ka krijuar qiejt dhe Tokën dhe ato që gjenden në mes tyre - vetëm me qëllim dhe afat të caktuar. E, me të vërtetë, shumica e njerëzve nuk besojnë në takimin me Zotin e tyre.');
INSERT INTO "hasan_efendi" VALUES(30,9,'30:9','Vallë, a nuk udhëtojnë nëpër botë e të shohin se si kanë përfunduar popujt e mëparshëm? Ata kanë qenë më të fortë se këta dhe tokën e kanë shfrytëzuar dhe punuar më mirë se sa e punojnë këta, e të dërguarit e tyre u kanë sjellë prova të qarta. Nuk ishte Allahu që u bëri atyre padrejtësi, por ata i bënë vetvetes padrejtësi.');
INSERT INTO "hasan_efendi" VALUES(30,10,'30:10','Pastaj përfundimi i atyre që punuan keq, ka qenë e keqja e madhe (Xhehenemi), ngase i mohonin shpalljet e Allahut dhe talleshin me to.');
INSERT INTO "hasan_efendi" VALUES(30,11,'30:11','Allahu e fillon krijimin e pastaj e përsërit dhe pastaj tek Ai do të ktheheni.');
INSERT INTO "hasan_efendi" VALUES(30,12,'30:12','E, atë ditë kur të arrijë Ora, gjynahqarët do të mbesin pa gojë, shpresëkëputur.');
INSERT INTO "hasan_efendi" VALUES(30,13,'30:13','Ata nuk do të kenë ndërmjetës nga idhujt e vet dhe do t’i mohojnë idhujt e tyre.');
INSERT INTO "hasan_efendi" VALUES(30,14,'30:14','E, atë ditë kur të arrijë Ora, ata (besimtarët dhe jobesimtarët) do të ndahen.');
INSERT INTO "hasan_efendi" VALUES(30,15,'30:15','Ata që kanë besuar dhe kanë bërë vepra të mira, do të gëzojnë në kopshtin e Xhenetit,');
INSERT INTO "hasan_efendi" VALUES(30,16,'30:16','kurse ata që kanë mohuar dhe kanë përgënjeshtruar shenjat Tona dhe takimin në jetën tjetër, do të hidhen në zjarr.');
INSERT INTO "hasan_efendi" VALUES(30,17,'30:17','Prandaj lavdërojeni Allahun, kur bie muzgu dhe, kur gdhin drita!');
INSERT INTO "hasan_efendi" VALUES(30,18,'30:18','Për Atë është falënderimi në qiej dhe në Tokë, në mbrëmje dhe në mesditë.');
INSERT INTO "hasan_efendi" VALUES(30,19,'30:19','Ai krijon të gjallën prej së vdekurës, krijon të vdekurën prej së gjallës dhe e gjallëron tokën pas shkretimit të saj. Në të njëjtën mënyrë do të ktheheni edhe ju në jetë.');
INSERT INTO "hasan_efendi" VALUES(30,20,'30:20','Një prej shenjave të Tij është që Ai ju krijoi prej dheut, pastaj ja, tashmë ju jeni njerëz të shpërndarë në të gjitha anët.');
INSERT INTO "hasan_efendi" VALUES(30,21,'30:21','Dhe një prej shenjave të Tij është që prej jush krijoi për ju bashkëshortet tuaja, që të qetësoheni pranë tyre, duke vënë ndërmjet jush dashuri dhe mëshirë. Me të vërtetë, në këtë ka shenja, për njerëzit që mendojnë.');
INSERT INTO "hasan_efendi" VALUES(30,22,'30:22','Ndërmjet shenjave të Tij janë krijimi i qiejve e i Tokës dhe shumëllojshmëria e gjuhëve dhe e ngjyrave (racave) tuaja. Pa dyshim, në këtë ka shenja për ata që dinë.');
INSERT INTO "hasan_efendi" VALUES(30,23,'30:23','Të tjera shenja të Tij janë gjumi juaj natën e ditën dhe kërkesa e juaj nga dhuntitë e Tij. Vërtet, në këtë ka shenja për njerëzit që dëgjojnë.');
INSERT INTO "hasan_efendi" VALUES(30,24,'30:24','Gjithashtu, prej shenjave të Tij janë paraqitja e vetëtimës si frikë e shpresë dhe dërgimi i shiut prej qiellit, që nëpërmjet tij të gjallërohet toka pas shkretimit të saj. Sigurisht, në këtë ka shenja për njerëzit që mendojnë.');
INSERT INTO "hasan_efendi" VALUES(30,25,'30:25','Dhe një prej shenjave të Tij është që qiejt dhe Toka qëndrojnë sipas urdhrit të Tij, pastaj kur t’ju thërrasë Ai, me një të thirrur, ju përnjëherë do të dilni nga toka (të ringjallur),');
INSERT INTO "hasan_efendi" VALUES(30,26,'30:26','Atij i përket gjithçka që gjendet në qiej dhe në Tokë. Gjithçka i përulet Atij.');
INSERT INTO "hasan_efendi" VALUES(30,27,'30:27','Ai e zë fill krijimin dhe Ai e përsërit atë e për Atë kjo është edhe më e lehtë. Atij i përket shembulli më i lartë në qiej dhe në Tokë. Ai është i Plotfuqishmi dhe i Urti.');
INSERT INTO "hasan_efendi" VALUES(30,28,'30:28','Ai ju ka sjellë një shembull nga vetë jeta juaj: A janë skllevërit tuaj pjesëtarë të barabartë me ju në pasurinë, që Ne ju kemi dhënë juve dhe, a u frikësoheni atyre ashtu, siç i frikësoheni njëri tjetrit? Ja, kështu Ne ua shpjegojmë shenjat Tona njerëzve që mendojnë.');
INSERT INTO "hasan_efendi" VALUES(30,29,'30:29','Por, keqbërësit, nga padituria ndjekin qejfet e veta. Atëherë, kush do ta udhëzojë atë që e ka lënë të humbur Allahu? Për njerëz të tillë nuk ka kurrfarë ndihmësi.');
INSERT INTO "hasan_efendi" VALUES(30,30,'30:30','Drejtohu me përkushtim në fenë e pastër monoteiste, natyrën fillestare, në të cilën Allahu i ka krijuar njerëzit. S’ka ndryshim të krijimit të Allahut. Kjo është feja e drejtë, por shumica e njerëzve nuk e dinë.');
INSERT INTO "hasan_efendi" VALUES(30,31,'30:31','Drejtojuni Atij me pendesë, frikësojuni Atij dhe falni namazin e mos u bëni si ata, që i shoqërojnë Atij diçka tjetër (në adhurim),');
INSERT INTO "hasan_efendi" VALUES(30,32,'30:32','si ata, që fenë e vet e kanë përçarë dhe janë ndarë në grupe, e ku çdo grup është i kënaqur me atë që ka.');
INSERT INTO "hasan_efendi" VALUES(30,33,'30:33','Kur njerëzit i godet ndonjë e keqe, ata i luten Zotit të vet, duke iu kthyer me pendesë, e pastaj, kur Ai u jep të shijojnë mëshirën e Tij, atëherë një pjesë e tyre i shoqërojnë Zotit të vet diçka tjetër (në adhurim),');
INSERT INTO "hasan_efendi" VALUES(30,34,'30:34','për ta mohuar atë që u kemi dhënë Ne. Le të kënaqen, sepse së shpejti do ta marrin vesh!');
INSERT INTO "hasan_efendi" VALUES(30,35,'30:35','A mos vallë Ne u kemi dërguar atyre shpallje, që u flet për idhujt që m’i shoqëronin Mua (në adhurim)?');
INSERT INTO "hasan_efendi" VALUES(30,36,'30:36','Kur Ne u japim njerëzve ta shijojnë dhuntinë, ata gëzohen me të e, kur i godet fatkeqësia për atë që kanë bërë me duart e tyre, atëherë e humbin shpresën.');
INSERT INTO "hasan_efendi" VALUES(30,37,'30:37','Vallë, a nuk dinë ata se Allahu ia zgjeron furnizimin dhe ia ngushton kujt të dojë?! Me të vërtetë, këtu ka një shenjë për njerëzit që besojnë.');
INSERT INTO "hasan_efendi" VALUES(30,38,'30:38','Jepja të drejtën e tij të afërmit, edhe të varfrit, edhe udhëtarit! Kjo është më e mirë për ata që e duan kënaqësinë e Allahut. Pikërisht ata do të jenë të fituarit.');
INSERT INTO "hasan_efendi" VALUES(30,39,'30:39','Ato që i jepni dhuratë për t’jua shtuar (pasurinë tuaj) përmes pasurisë së njerëzve të tjerë (duke jua kthyer dhuratën me një më të mirë), nuk do të shtohen tek Allahu (s’do të ketë shpërblim), kurse kur të jepni si bamirësi, për të arritur kënaqësinë e Allahut, do t’ju shumëfishohen (të mirat).');
INSERT INTO "hasan_efendi" VALUES(30,40,'30:40','Allahu është Ai që ju ka krijuar e ju ka dhënë ushqim; pastaj ju sjell vdekjen e pastaj ju ringjall. A ka ndonjë nga idhujt tuaj që bën diçka të tillë? Ai qoftë i lavdëruar dhe i lartësuar mbi gjithçka, që ia shoqërojnë Atij (në adhurim)!');
INSERT INTO "hasan_efendi" VALUES(30,41,'30:41','Shkatërrimi në tokë dhe në det është shfaqur si pasojë e punëve të këqija të njerëzve, kështu që (Allahu) do t’i bëjë “të shijojnë” pjesë nga ajo që kanë punuar, me qëllim që ata të kthehen (në përmirësim).');
INSERT INTO "hasan_efendi" VALUES(30,42,'30:42','Thuaj (o Muhamed): “Udhëtoni nëpër botë e shikoni si ka qenë përfundimi i atyre para jush”. Shumica e tyre kanë qenë idhujtarë.');
INSERT INTO "hasan_efendi" VALUES(30,43,'30:43','Drejtohu ti me përkushtim në fenë e drejtë, para se të vijë prej Allahut Dita, e cila nuk mund të shtyhet. Atë ditë, njerëzit do të ndahen:');
INSERT INTO "hasan_efendi" VALUES(30,44,'30:44','ata që nuk kanë besuar, do të mbajnë pasojat e mohimit, kurse ata që kanë bërë vepra të mira, i kanë përgatitur vetes (të mira).');
INSERT INTO "hasan_efendi" VALUES(30,45,'30:45','Ata që kanë besuar dhe kanë bërë vepra të mira, Allahu do t’i shpërblejë nga dhuntitë e Veta. Në të vërtetë, Ai nuk i do mohuesit.');
INSERT INTO "hasan_efendi" VALUES(30,46,'30:46','Dhe, një prej shenjave të Tij, është dërgimi i erërave mbartëse të lajmeve të mira (shiut), që t’ju bëjë të shijoni nga mëshira e Tij, që anija të lundrojë me urdhrin e Tij, që të kërkoni nga dhuntia e Tij dhe që të jeni mirënjohës.');
INSERT INTO "hasan_efendi" VALUES(30,47,'30:47','Pa dyshim, Ne kemi nisur të dërguar para teje te popujt e tyre. Ata u paraqitën me prova të qarta, por popujt i mohuan, prandaj Ne i ndëshkuam keqbërësit. Është detyra Jonë që t’i ndihmojmë besimtarët.');
INSERT INTO "hasan_efendi" VALUES(30,48,'30:48','Allahu është ai që i dërgon erërat, që ato t’i lëvizin retë, pastaj i shpërndan ato nëpër qiell si të dojë dhe i copëton ato pjesë-pjesë, e ti sheh shiun, si bie prej tyre. Kur ua çon kujt të dojë nga robërit e Vet, atëherë ata gëzohen përnjëherësh,');
INSERT INTO "hasan_efendi" VALUES(30,49,'30:49','edhe pse, me të vërtetë, para se t’u vinte shiu, ishin të dëshpëruar.');
INSERT INTO "hasan_efendi" VALUES(30,50,'30:50','Andaj, shikoji gjurmët e mëshirës së Allahut, si e gjallëron tokën pas shkretimit të saj. Vërtet, Ai do t’i ringjallë edhe të vdekurit, se Ai është i Fuqishëm për çdo gjë.');
INSERT INTO "hasan_efendi" VALUES(30,51,'30:51','Edhe sikur Ne të dërgonim furtunë, e jobesimtarët të shihnin që çdo gjë të zverdhej, edhe pas kësaj ata do të mbeteshin mohues të dhuntive.');
INSERT INTO "hasan_efendi" VALUES(30,52,'30:52','Me të vërtetë, ti (Muhamed) nuk mund të bësh që “i vdekuri” apo “i shurdhri” të dëgjojnë thirrjen, kur ata ta kthejnë shpinën duke u larguar!');
INSERT INTO "hasan_efendi" VALUES(30,53,'30:53','Ti nuk mund ta udhëzosh të verbrin të dalë nga humbja e tij! Ti mund të bësh që të dëgjojnë vetëm ata që besojnë në shpalljet Tona. Ata janë myslimanë të vërtetë.');
INSERT INTO "hasan_efendi" VALUES(30,54,'30:54','Allahu është ai që ju ka krijuar ju të dobët, pastaj, pas dobësisë, ju ka bërë të fuqishëm e, pas fuqisë, ju bën të dobët dhe të thinjur. Ai krijon çfarë të dojë; Ai është i Gjithëdijshëm dhe i Fuqishëm për çdo gjë.');
INSERT INTO "hasan_efendi" VALUES(30,55,'30:55','Ditën, kur të vijë Ora (e Kiametit), keqbërësit do të betohen se kanë qëndruar (në tokë) veçse për një kohë të shkurtër. Kështu ata shmangen nga e Vërteta.');
INSERT INTO "hasan_efendi" VALUES(30,56,'30:56','Ata, të cilëve u është dhënë dituria dhe besimi, do të thonë: “Ju, me të vërtetë, keni qëndruar aq sa ju ka caktuar Allahu në Libër - deri në Ditën e ringjalljes, - e ja kjo është Dita e ringjalljes, por ju nuk e keni ditur”.');
INSERT INTO "hasan_efendi" VALUES(30,57,'30:57','Atë Ditë, atyre që kanë bërë padrejtësi, nuk do t’u bëjë dobi shfajësimi, as nuk do t’u lejohet që të kërkojnë falje.');
INSERT INTO "hasan_efendi" VALUES(30,58,'30:58','Pa dyshim, në këtë Kuran, Ne u kemi sjellë njerëzve gjithfarë shembujsh. Por, sikur ti (Muhamed) t’u sjellësh atyre çfarëdo mrekullie, jobesimtarët me siguri, do të thonë: “Ju jeni vetëm shpifës”.');
INSERT INTO "hasan_efendi" VALUES(30,59,'30:59','Ja, kështu, Allahu ua vulos zemrat atyre që nuk dinë.');
INSERT INTO "hasan_efendi" VALUES(30,60,'30:60','Duro (o Muhamed), se, me siguri, premtimi i Allahut është i vërtetë dhe s’ke pse të humbasësh durimin kurrsesi nga ata që nuk besojnë!');
INSERT INTO "hasan_efendi" VALUES(31,1,'31:1','Elif, Lâm, Mîm.');
INSERT INTO "hasan_efendi" VALUES(31,2,'31:2','Këto janë vargje të Librit plot Urtësi,');
INSERT INTO "hasan_efendi" VALUES(31,3,'31:3','udhërrëfyes dhe mëshirë për punëmirët,');
INSERT INTO "hasan_efendi" VALUES(31,4,'31:4','që kryejnë namazin, japin zekatin dhe besojnë bindshëm në jetën tjetër.');
INSERT INTO "hasan_efendi" VALUES(31,5,'31:5','Këta janë udhëzuar nga Zoti i tyre dhe vetëm këta janë të shpëtuar.');
INSERT INTO "hasan_efendi" VALUES(31,6,'31:6','Ka disa njerëz që sajojnë tregime të kota, për të shmangur (të tjerët) nga rruga e Allahut me paditurinë e tyre e për t’u tallur me të. Njerëz të tillë i pret dënimi poshtërues.');
INSERT INTO "hasan_efendi" VALUES(31,7,'31:7','Kur ndonjërit prej tyre i lexohen shpalljet Tona, ai e kthen shpinën me mendjemadhësi, sikur nuk i ka dëgjuar, sikur i janë shurdhuar veshët. Andaj, jepu lajmin e dënimit të dhembshëm.');
INSERT INTO "hasan_efendi" VALUES(31,8,'31:8','Me të vërtetë, ata që besojnë dhe bëjnë vepra të mira, do të gjenden në kopshtet e dhuntisë,');
INSERT INTO "hasan_efendi" VALUES(31,9,'31:9','në të cilat do të qëndrojnë përherë. Premtimi i Allahut është i vërtetë; Ai është i Plotfuqishmi dhe i Urti.');
INSERT INTO "hasan_efendi" VALUES(31,10,'31:10','Ai i ka krijuar qiejt pa shtylla të dukshme, ka vënë male të palëvizshme në Tokë, që ju të mos lëkundeni dhe ka shpërndarë në të gjithfarë gjallesash. Ne e zbresim ujin nga qielli dhe, nëpërmjet tij, kemi bërë të mbijë çdo lloj (bime) i mirë.');
INSERT INTO "hasan_efendi" VALUES(31,11,'31:11','Ky është krijimi i Allahut! Më tregoni çfarë kanë krijuar të tjerët pos Tij? Asgjë, por keqbërësit janë vërtet në humbje të plotë.');
INSERT INTO "hasan_efendi" VALUES(31,12,'31:12','Ne i dhamë Lukmanit urtësinë (duke i thënë): “Falënderoje Allahun! Kush është falënderues, e bën për të mirën e vet dhe, kush mohon, ta dijë se vërtet Allahu është i Vetëmjaftueshëm dhe i Denjë për lavdërim”.');
INSERT INTO "hasan_efendi" VALUES(31,13,'31:13','(Kujtoje) kur Lukmani i tha të birit, duke e këshilluar: “O djali im, mos i shoqëro Allahut asgjë (në adhurim)! Pa dyshim, idhujtaria është një padrejtësi shumë e madhe”.');
INSERT INTO "hasan_efendi" VALUES(31,14,'31:14','Ne e kemi urdhëruar njeriun që t’i nderojë prindërit e vet. Nëna e ka mbartur atë, duke duruar mundim pas mundimi, e ndarja e tij (nga gjiri) është brenda dy vjetësh. Ne i thamë atij: “Bëhu falënderues ndaj Meje dhe prindërve të tu! Tek Unë do të ktheheni të gjithë”.');
INSERT INTO "hasan_efendi" VALUES(31,15,'31:15','Nëse ata orvaten, që të më shoqërosh Mua (në adhurim) diçka, për të cilën ti nuk ke dijeni, mos i dëgjo, por sillu mirë me ata në këtë jetë dhe ndiq rrugën e atij që drejtohet tek Unë me devotshmëri; pastaj, tek Unë do të ktheheni dhe Unë do t’ju lajmëroj për atë që keni bërë.');
INSERT INTO "hasan_efendi" VALUES(31,16,'31:16','“O djali im, edhe veprën e rëndë sa kokrra e sinapit që ndodhet në shkëmb, në qiej apo në tokë, Allahu do ta nxjerrë në shesh. Me të vërtetë, Allahu njeh mirë çdo imtësi dhe i di të gjitha fshehtësitë.');
INSERT INTO "hasan_efendi" VALUES(31,17,'31:17','O djali im, kryeje namazin, urdhëroje të mirën, pengoje të keqen dhe bëhu i durueshëm për çfarëdo që të godet! Me të vërtetë, ky është thelbi i patundur i të gjitha punëve!');
INSERT INTO "hasan_efendi" VALUES(31,18,'31:18','Mos i trajto njerëzit me nënçmim dhe mos ec në tokë me mendjemadhësi, se Allahu nuk i do mendjemëdhenjtë e mburracakët!');
INSERT INTO "hasan_efendi" VALUES(31,19,'31:19','Ec me kujdes e i përmbajtur dhe fol me zë të ulët; se zëri më i vrazhdë është zëri i gomarit”!');
INSERT INTO "hasan_efendi" VALUES(31,20,'31:20','A nuk shihni se si Allahu jua ka bërë të mundur t’i shfrytëzoni të gjitha ato që gjenden në qiej dhe në Tokë dhe ju ka mbuluar me dhuntitë e Tij të dukshme e të padukshme? E megjithatë, disa njerëz polemizojnë për Allahun pa kurrfarë dijenie, pa kurrfarë rrëfyesi dhe pa libër ndriçues.');
INSERT INTO "hasan_efendi" VALUES(31,21,'31:21','Kur atyre u thuhet: “Ndiqni atë që ju ka shpallur Allahu!”, ata thonë: “Jo, ne ndjekim fenë e të parëve tanë”. Kështu është, edhe pse djalli i thërret ata në dënimin e Zjarrit!');
INSERT INTO "hasan_efendi" VALUES(31,22,'31:22','Kush i dorëzohet Allahut, me tërë qenien e tij, duke qenë punëmirë, ai është kapur për vegën më të fortë. Tek Allahu do të kthehet çdo gjë.');
INSERT INTO "hasan_efendi" VALUES(31,23,'31:23','Sa për ata që nuk besojnë, ti (o Muhamed), mos u brengos nga mohimi i tyre. Ata do të kthehen te Ne, e Ne do t’i njoftojmë për atë që kanë bërë. Me të vërtetë, Allahu di ç’ka në zemrat e njerëzve.');
INSERT INTO "hasan_efendi" VALUES(31,24,'31:24','Ne ata do t’i kënaqim përkohësisht (në këtë botë) e pastaj do t’i hedhim në dënimin e ashpër (në botën tjetër).');
INSERT INTO "hasan_efendi" VALUES(31,25,'31:25','Nëse ti (o Muhamed) i pyet ata : “Kush i ka krijuar qiejt dhe Tokën?”, me siguri që do të të thonë: “Allahu!”. Atëherë ti thuaju: “Qoftë lavdëruar Allahu!” Por, shumica e tyre nuk dinë.');
INSERT INTO "hasan_efendi" VALUES(31,26,'31:26','Allahut i përket gjithçka, që gjendet në qiej dhe në Tokë. Vërtet, Allahu është i Vetëmjaftueshmi dhe i Denji për lavdërim.');
INSERT INTO "hasan_efendi" VALUES(31,27,'31:27','Sikur të gjithë drurët, që gjenden në Tokë, të ishin pena e deti bojë shkrimi e t’i shtohen atij edhe shtatë dete të tjera, nuk do të shtereshin fjalët e Allahut. Pa dyshim, Allahu është i Plotfuqishmi dhe i Urti.');
INSERT INTO "hasan_efendi" VALUES(31,28,'31:28','Krijimi dhe ringjallja e të gjithë juve është njësoj si krijimi i një njeriu të vetëm. Vërtet, Allahu i dëgjon dhe i sheh të gjitha.');
INSERT INTO "hasan_efendi" VALUES(31,29,'31:29','A nuk shihni se Allahu e kthen natën në ditë dhe ditën në natë, se e ka nënshtruar Diellin dhe Hënën e secili lëviz për një kohë të caktuar dhe se Allahu e di mirë çfarë punoni ju?!');
INSERT INTO "hasan_efendi" VALUES(31,30,'31:30','Kjo është kështu, sepse Allahu është e Vërteta, ndërsa çfarë adhurohet në vend të Tij, është e rreme dhe se Allahu është i Lartësuar dhe i Madhërishëm.');
INSERT INTO "hasan_efendi" VALUES(31,31,'31:31','A nuk e shihni se anijet lundrojnë nëpër det me dhuntinë e Allahut, për t’ju treguar disa nga mrekullitë e Veta? Këto janë vërtet tregues për çdo njeri të duruar dhe mirënjohës.');
INSERT INTO "hasan_efendi" VALUES(31,32,'31:32','Kur ata i mbulon vala si pëlhurë, e thërrasin Allahun me devotshmëri të çiltër. Por sapo Ai i shpëton, duke i kthyer ata në tokë, një pjesë e tyre lëkunden (ndërmjet besimit dhe mosbesimit). Në të vërtetë, shpalljet dhe mrekullitë Tona i mohojnë vetëm mashtruesit dhe mosmirënjohësit.');
INSERT INTO "hasan_efendi" VALUES(31,33,'31:33','O njerëz! Frikësojuni Zotit tuaj dhe frikësojuni Ditës, kur prindi nuk do të mund ta ndihmojë aspak fëmijën e vet, e as fëmija nuk do të mund ta ndihmojë sado pak prindin e vet! Premtimi i Allahut është vërtet i sigurt, prandaj mos e lini jetën e kësaj bote t’ju mashtrojë dhe as djallin mos e lini t’ju mashtrojë për Allahun.');
INSERT INTO "hasan_efendi" VALUES(31,34,'31:34','Pa dyshim, vetëm Allahu e di Orën e caktuar. Ai e zbret shiun dhe e di ç’ka në barkun e shtatzënës. Asnjë njeri nuk di ç’do të fitojë nesër dhe askush nuk di se në ç’vend do të vdesë. Me të vërtetë, Allahu është i Gjithëdijshëm dhe Njohës i çdo gjëje.');
INSERT INTO "hasan_efendi" VALUES(32,1,'32:1','Elif, Lâm, Mîm.');
INSERT INTO "hasan_efendi" VALUES(32,2,'32:2','Shpallja e këtij Libri, pa asnjë dyshim, është nga Zoti i botëve.');
INSERT INTO "hasan_efendi" VALUES(32,3,'32:3','Ata thonë: “Ai e ka trilluar atë! Jo, ajo është e Vërteta nga Zoti yt për të paralajmëruar popullin, të cilit para teje nuk i ka ardhur kurrfarë paralajmëruesi, që të shkojë në rrugë të drejtë.');
INSERT INTO "hasan_efendi" VALUES(32,4,'32:4','Allahu është Ai që ka krijuar qiejt dhe Tokën dhe çdo gjë që gjendet midis tyre, për gjashtë ditë, pastaj është ngritur mbi Fron. Ju nuk keni as ndihmës, as ndërmjetësues tjetër përveç Atij. Vallë, a nuk po vini vesh?');
INSERT INTO "hasan_efendi" VALUES(32,5,'32:5','Ai drejton çdo çështje, prej qiellit deri në Tokë, pastaj ato kthehen tek Ai në ditën, gjatësia e së cilës është një mijë vjet sipas llogaritjes suaj.');
INSERT INTO "hasan_efendi" VALUES(32,6,'32:6','Është Ai që i di gjërat e padukshme dhe të dukshme. Ai është i Plotfuqishmi dhe Mëshirëploti,');
INSERT INTO "hasan_efendi" VALUES(32,7,'32:7','i Cili ka përsosur çdo gjë që ka krijuar. Ai e ka filluar krijimin e njeriut prej baltës.');
INSERT INTO "hasan_efendi" VALUES(32,8,'32:8','Pastaj bëri që pasardhësit e tij të krijohen prej një pike të lëngut të shpërfillur,');
INSERT INTO "hasan_efendi" VALUES(32,9,'32:9','së cilës pastaj i dha trajtë dhe i fryu shpirtin e krijuar prej Tij. Ai ju ka dhënë dëgjimin, shikimin dhe zemrën, por pak prej jush janë mirënjohës.');
INSERT INTO "hasan_efendi" VALUES(32,10,'32:10','Ata thonë: “Vallë, pasi të humbasim nën tokë, përsëri do të ngjallemi?” Vërtet, ata nuk besojnë se do të takohen me Zotin e tyre.');
INSERT INTO "hasan_efendi" VALUES(32,11,'32:11','Thuaju: “Shpirtin do t’jua marrë engjëlli i vdekjes, i cili është caktuar për ju, e pastaj do të ktheheni te Zoti juaj”.');
INSERT INTO "hasan_efendi" VALUES(32,12,'32:12','Sikur t’i shohësh keqbërësit se si para Zotit të tyre kokulur do të thonë: “O Zoti ynë, pamë dhe dëgjuam, andaj, kthena (në atë botë) që të bëjmë vepra të mira! Ne tashmë besojmë me bindje”!');
INSERT INTO "hasan_efendi" VALUES(32,13,'32:13','Sikur Ne të donim, do t’ia jepnim secilit udhëzimin e tij, por do të përmbushet Fjala Ime: “Me të vërtetë, do ta mbush Xhehenemin me xhinde dhe njerëz bashkërisht!”');
INSERT INTO "hasan_efendi" VALUES(32,14,'32:14','(Ne do t’u themi atyre): “Shijoni dënimin, sepse nuk e keni besuar takimin e kësaj dite. Në të vërtetë, Ne ju kemi braktisur, prandaj shijoni dënimin e përhershëm për atë që keni bërë!”');
INSERT INTO "hasan_efendi" VALUES(32,15,'32:15','Shpalljet Tona i besojnë vetëm ata të cilët, kur u përmenden, bien në sexhde, dhe të cilët e madhërojnë Zotin e tyre, e lavdërojnë dhe nuk bëhen mendjemëdhenj.');
INSERT INTO "hasan_efendi" VALUES(32,16,'32:16','Ata ngrihen nga shtrati, i luten Zotit të tyre me frikë e shpresë dhe japin nga ajo që iu kemi dhënë Ne.');
INSERT INTO "hasan_efendi" VALUES(32,17,'32:17','Askush nuk di se çfarë gëzimesh janë fshehur për ata (në jetën tjetër), si shpërblim për punët e mira që kanë bërë.');
INSERT INTO "hasan_efendi" VALUES(32,18,'32:18','Vallë! A është besimtari njësoj si keqbërësi? Jo, ata nuk janë të barabartë.');
INSERT INTO "hasan_efendi" VALUES(32,19,'32:19','Ata që besojnë dhe bëjnë vepra të mira, do të kenë Xhenetin- Strehim, si shpërblim për punët (e mira) që kanë bërë.');
INSERT INTO "hasan_efendi" VALUES(32,20,'32:20','Ndërsa ata që nuk besojnë e që bëjnë keq, vendbanim i tyre do të jetë zjarri. Sa herë që do të duan të dalin nga ai, do të kthehen në të dhe do t’u thuhet: “Shijoni dënimin e zjarrit, të cilin e mohonit!”');
INSERT INTO "hasan_efendi" VALUES(32,21,'32:21','Dhe, Ne do të bëjmë që të shijojnë një dënim më të lehtë në këtë jetë, para dënimit më të madh në jetën e përtejme, me qëllim që të kthehen në rrugën e drejtë.');
INSERT INTO "hasan_efendi" VALUES(32,22,'32:22','Kush është më i padrejtë se ai që, kur i përmenden shpalljet e Zotit të tij, nuk ua vë veshin atyre? Vërtet, Ne do të hakmerremi ndaj keqbërësve!');
INSERT INTO "hasan_efendi" VALUES(32,23,'32:23','Ne i dhamë Musait Librin (Teuratin) - prandaj mos dysho në takimin me atë - dhe e bëmë atë udhërrëfyes për bijtë e Israilit.');
INSERT INTO "hasan_efendi" VALUES(32,24,'32:24','Ne zgjodhëm prej tyre prijës, të cilët udhëzonin me urdhrat Tanë, për sa kohë që ishin të durueshëm dhe besonin bindshëm.');
INSERT INTO "hasan_efendi" VALUES(32,25,'32:25','Me të vërtetë, Zoti yt do të gjykojë midis tyre Ditën e Kiametit, për çështjet në të cilat kishin mosmarrëveshje.');
INSERT INTO "hasan_efendi" VALUES(32,26,'32:26','A nuk e kanë të qartë ata, se sa popuj i kemi shkatërruar Ne para tyre; nëpër vendbanimet e të cilëve ecin? Me të vërtetë, në këto ka shenja paralajmëruese, por ata nuk po vënë vesh.');
INSERT INTO "hasan_efendi" VALUES(32,27,'32:27','A nuk e shohin ata, se Ne e dërgojmë ujin në tokën e shkretuar, e nëpërmjet tij bëjmë që të rriten bimët, me të cilat ushqehen bagëtitë e tyre dhe ata vetë?! A nuk kanë sy për të parë?!');
INSERT INTO "hasan_efendi" VALUES(32,28,'32:28','Ata pyesin: “Kur do të vijë Gjykimi, nëse thoni të vërtetën?”');
INSERT INTO "hasan_efendi" VALUES(32,29,'32:29','Thuaju: “Ditën e gjykimit nuk do t’u bëjë dobi mohuesve besimi i tyre e as që do t’u jepet afat”.');
INSERT INTO "hasan_efendi" VALUES(32,30,'32:30','Andaj, mos ua vër veshin atyre dhe prit, edhe ata, me të vërtetë, po presin!');
INSERT INTO "hasan_efendi" VALUES(33,1,'33:1','O i Dërguar! Kije frikë Allahun dhe mos i dëgjo jobesimtarët dhe hipokritët! Me të vërtetë, Allahu është i Urtë dhe i Gjithëdijshëm.');
INSERT INTO "hasan_efendi" VALUES(33,2,'33:2','Bindju asaj që të është shpallur ty nga Zoti yt, se vërtet, Allahu e di mirë se çfarë bëni!');
INSERT INTO "hasan_efendi" VALUES(33,3,'33:3','Mbështetu tek Allahu; Allahu të mjafton si mbrojtës!');
INSERT INTO "hasan_efendi" VALUES(33,4,'33:4','Allahu nuk i ka dhënë asnjë njeriu dy zemra në kraharor. Ai nuk i konsideron gratë, që ju i ndani si nënat tuaja e as fëmijët tuaj të birësuar nuk i konsideron si bij të vërtetë. Këto janë vetëm fjalë që dalin prej gojës suaj, kurse Allahu thotë të vërtetën dhe udhëzon në rrugë të drejtë.');
INSERT INTO "hasan_efendi" VALUES(33,5,'33:5','Thërritini të birësuarit sipas etërve të tyre! Kjo është më e drejtë në sytë e Allahut. Në qoftë se nuk e dini emrin e etërve të tyre, konsiderojini ata si vëllezërit tuaj të besimit dhe si të mitur nën tutelën tuaj. Nuk është gjynah nëse gaboni, por është gjynah, kur zemrat tuaja bëjnë faj me qëllim. Allahu është Falës dhe Mëshirëplotë.');
INSERT INTO "hasan_efendi" VALUES(33,6,'33:6','I Dërguari është më i afërt për besimtarët, sesa ata për njëri-tjetrin; gratë e tij janë (si) nënat e tyre. Sipas Librit të Allahut, pjesëtarët e farefisit janë më të afërt për njëri-tjetrin , sesa besimtarët e tjerë dhe të mërguarit , përveç rastit kur doni t’u bëni ndonjë të mirë miqve tuaj të afërt (me anë të testamentit). Kjo është shënuar në Libër.');
INSERT INTO "hasan_efendi" VALUES(33,7,'33:7','Ne lidhëm besën me ty (o Muhamed) siç bëmë me profetët e tjerë: me Nuhun, Ibrahimin, Musain dhe Isain, të birin e Merjemes. Ne lidhëm me ata një besë solemne,');
INSERT INTO "hasan_efendi" VALUES(33,8,'33:8','që Allahu të mund t’i pyesë të sinqertët për besnikërinë e tyre. Ndërsa për mohuesit Ai ka përgatitur dënim të dhembshëm.');
INSERT INTO "hasan_efendi" VALUES(33,9,'33:9','O besimtarë! Kujtoni dhuntinë e Allahut kur ju erdhi një ushtri (idhujtarësh), e Ne dërguam mbi ata erë dhe një ushtri (engjëjsh) që ju nuk e shihnit; ndërsa Allahu i sheh të gjitha ato që bëni ju.');
INSERT INTO "hasan_efendi" VALUES(33,10,'33:10','Ata ju sulmuan nga lart dhe nga poshtë, kështu që sytë tuaj u errësuan, zemrat ju erdhën në fyt dhe besimi juaj tek Allahu u lëkund.');
INSERT INTO "hasan_efendi" VALUES(33,11,'33:11','Aty besimtarët u vunë në provë dhe u tronditën si nga një tronditje e fuqishme.');
INSERT INTO "hasan_efendi" VALUES(33,12,'33:12','Por hipokritët dhe ata me zemra të sëmura e të dobëta, thanë: “Allahu dhe i Dërguari i Tij nuk na kanë premtuar asgjë, pos mashtrimit”!');
INSERT INTO "hasan_efendi" VALUES(33,13,'33:13','Dhe një grup prej tyre tha: “O banorët e Jethribit , s’ka qëndrim këtu për ju, andaj kthehuni!” Një grup tjetër nga ata kërkoi leje prej të Dërguarit, duke thënë: “Në të vërtetë, shtëpitë tona janë të pambrojtura”! Por ato nuk ishin të pambrojtura. Ata donin vetëm të iknin.');
INSERT INTO "hasan_efendi" VALUES(33,14,'33:14','Sikur armiku t’i pushtonte ata nga të gjitha anët e t’u kërkonte që të bëheshin idhujtarë, ata do të bëheshin duke ngurruar fare pak.');
INSERT INTO "hasan_efendi" VALUES(33,15,'33:15','Ndërsa më parë i qenë betuar Allahut, se nuk do të zmbrapseshin. Për këtë besëlidhje me Allahun do të përgjigjen!');
INSERT INTO "hasan_efendi" VALUES(33,16,'33:16','Thuaju: “Nuk do t’ju bëjë dobi të ikurit, nëse ikni prej vdekjes apo vrasjes. Edhe atëherë, ju do të kënaqeni vetëm për pak kohë!”');
INSERT INTO "hasan_efendi" VALUES(33,17,'33:17','Thuaju: “Kush është ai, që do t’ju ruajë nga Allahu, nëse Ai dëshiron që t’ju godasë e keqja?! Kush mund ta pengojë Atë, nëse Ai do që t’ju japë mëshirë?!” Ata nuk do të gjejnë as mbrojtës, as ndihmës tjetër përveç Allahut.');
INSERT INTO "hasan_efendi" VALUES(33,18,'33:18','Allahu i njeh mirë ata midis jush, që pengojnë (të tjerët nga vajtja në luftë) dhe u thonë vëllezërve të tyre: “Ejani te ne!” Vetëm pak prej tyre vijnë në luftë,');
INSERT INTO "hasan_efendi" VALUES(33,19,'33:19','por edhe atëherë ngurrojnë që t’ju ndihmojnë. Kur frikësohen, ata të shikojnë ty (o Muhamed) për ndihmë, duke i rrotulluar sytë sikur të ishin në agoni. Por, sapo t’u kalojë frika, ata ju sulmojnë me gjuhë të mprehtë dhe janë koprracë me ju për çfarëdo të mire. Këta janë ata që nuk kanë besuar, andaj Allahu i ka asgjësuar veprat e tyre. E, kjo për Allahun është lehtë.');
INSERT INTO "hasan_efendi" VALUES(33,20,'33:20','Ata mendonin se ende nuk kishte ikur ushtria e aleatëve e, nëse do të vinte ushtria e aleatëve, ata do të dëshironin të ishin në mesin e arabëve të shkretëtirës e të pyesin për ju. Por edhe sikur të mbeteshin me ju, pak do të luftonin.');
INSERT INTO "hasan_efendi" VALUES(33,21,'33:21','Në të Dërguarin e Allahut ka një shembull të mrekullueshëm për atë, që shpreson tek Allahu dhe Dita e Fundit dhe e përmend shumë Allahun.');
INSERT INTO "hasan_efendi" VALUES(33,22,'33:22','Kur besimtarët e vërtetë panë ushtrinë e aleatëve, thanë: “Kjo është ajo që na ka premtuar Allahu dhe i Dërguari i Tij; sigurisht, premtimi i tyre u vërtetua!” Kjo ua forcoi atyre besimin dhe përuljen.');
INSERT INTO "hasan_efendi" VALUES(33,23,'33:23','Midis besimtarëve ka të atillë që e kanë mbajtur besëlidhjen me Allahun. Disa prej tyre kanë vdekur, kurse të tjerët presin pa ndryshuar asgjë nga besnikëria e tyre.');
INSERT INTO "hasan_efendi" VALUES(33,24,'33:24','Nuk ka dyshim se Allahu do t’i shpërblejë të sinqertët për sinqeritetin e tyre e do t’i dënojë hipokritët, nëse do, ose do t’u pranojë pendimin. Vërtet, Allahu është Falës dhe Mëshirëplotë.');
INSERT INTO "hasan_efendi" VALUES(33,25,'33:25','Allahu i zmbrapsi jobesimtarët (e ushtrisë së aleatëve) me hidhërimin e tyre dhe ata dolën duarbosh. Ai i kurseu besimtarët nga lufta. Vërtet, Allahu është i Fortë dhe i Plotfuqishëm.');
INSERT INTO "hasan_efendi" VALUES(33,26,'33:26','Ai i nxori nga kështjellat e tyre ithtarët e Librit që ndihmuan ushtrinë e aleatëve dhe mbolli frikë në zemrat e tyre, kështu që një pjesë të tyre i vratë dhe pjesën tjetër i zutë robër.');
INSERT INTO "hasan_efendi" VALUES(33,27,'33:27','Ai ju bëri trashëgimtarë të tokës, shtëpive dhe pasurive të tyre, si dhe të një toke që nuk e kishit shkelur më parë ; Allahu është i Fuqishëm për çdo gjë.');
INSERT INTO "hasan_efendi" VALUES(33,28,'33:28','O i Dërguar! Thuaju grave tuaja: “Nëse dëshironi jetën e kësaj bote dhe shkëlqimin e saj, ejani, unë do t’ju lë të kënaqeni për ca kohë dhe do t’ju lëshoj si është më mirë.');
INSERT INTO "hasan_efendi" VALUES(33,29,'33:29','Por, nëse dëshironi Allahun, të Dërguarin e Tij dhe botën tjetër, dijeni se Allahu ka përgatitur shpërblim të madh për ato prej jush që bëjnë vepra të mira.”');
INSERT INTO "hasan_efendi" VALUES(33,30,'33:30','O gratë e të Dërguarit! Ato prej jush që bëjnë ndonjë turp të hapur, do të kenë ndëshkim të dyfishtë; kjo për Allahun është e lehtë. ');
INSERT INTO "hasan_efendi" VALUES(33,31,'33:31','Ndërsa atyre prej jush që binden ndaj Allahut e të Dërguarit të Tij dhe bëjnë vepra të mira, Ne do t’u japim shpërblim të dyfishtë e u kemi përgatitur begati të mëdha.');
INSERT INTO "hasan_efendi" VALUES(33,32,'33:32','O gratë e të Dërguarit! Ju nuk jeni si gratë e tjera. Nëse i frikësoheni Allahut, mos flisni me përkëdheli, që të mos ju dëshirojë ai që ka zemër epshore, por flisni drejt dhe në mënyrë të ndershme!');
INSERT INTO "hasan_efendi" VALUES(33,33,'33:33','Qëndroni në shtëpitë tuaja e mos i zbuloni stolitë tuaja, ashtu siç zbuloheshin në kohën e padijes ! Falni namazin, jepni zekatin dhe bindjuni Allahut e të Dërguarit të Tij! O familje e Profetit! Në të vërtetë, Allahu do që ta largojë prej jush papastërtinë dhe t’ju pastrojë plotësisht.');
INSERT INTO "hasan_efendi" VALUES(33,34,'33:34','Mbani në mendje ato që lexohen në shtëpitë tuaja prej Shpalljes së Allahut dhe Urtësisë (profetike)! Me të vërtetë, Allahu është Bamirës i pakufishëm dhe di çdo gjë.');
INSERT INTO "hasan_efendi" VALUES(33,35,'33:35','Me të vërtetë që për myslimanët dhe myslimanet, besimtarët dhe besimtaret, të bindurit ndaj Allahut dhe të bindurat ndaj Allahut, të drejtët (të sinqertët) dhe të drejtat (të sinqertat), duruesit dhe durueset, të përulurit dhe të përulurat, mirëbërësit dhe mirëbërëset, agjëruesit dhe agjërueset, ruajtësit e nderit dhe ruajtëset e nderit, përkujtuesit e Allahut vazhdimisht dhe përkujtueset, pra, për të gjithë këta Allahu ka përgatitur falje dhe shpërblim të madh.');
INSERT INTO "hasan_efendi" VALUES(33,36,'33:36','Nuk i takon asnjë besimtari apo besimtareje të vërtetë që, kur Allahu dhe i Dërguari i Tij vendosin për një çështje, ata të kenë të drejtë të zgjedhin në atë çështje të tyren. Kush e kundërshton Allahun dhe të Dërguarin e Tij, ai me siguri ka humbur.');
INSERT INTO "hasan_efendi" VALUES(33,37,'33:37','Ti (o Muhamed) i the atij, që Allahu dhe ti vetë i keni bërë mirësi: “Mbaje gruan tënde dhe frikësoju Allahut!” Në të vërtetë, ti po fsheh në vetvete atë që Allahu do ta nxjerrë në shesh dhe ti po u frikësohesh njerëzve, kur më e drejtë është t’i frikësohesh Allahut. E, pasi Zejdi bashkëjetoi me gruan e vet dhe u nda prej saj, Ne ta dhamë ty për bashkëshorte, në mënyrë që besimtarët të mos ngurrojnë që të martohen me gratë e të birësuarve të tyre, pasi ata ta kenë plotësuar dëshirën e tyre dhe të jenë ndarë. Urdhri i Allahut do të përmbushet detyrimisht.');
INSERT INTO "hasan_efendi" VALUES(33,38,'33:38','S’ka kurrfarë fajësimi për Profetin, kur bën atë që i ka urdhëruar Allahu. Kështu ka qenë ligji i Allahut edhe për ata (profetë) që kanë qenë më parë, - urdhri i Allahut është vendim i detyruar të ndodhë -,');
INSERT INTO "hasan_efendi" VALUES(33,39,'33:39','e të cilët kumtuan shpalljet e Allahut dhe i frikësoheshin Atij e askujt tjetër përveç Tij. Allahu mjafton si llogaritës.');
INSERT INTO "hasan_efendi" VALUES(33,40,'33:40','Muhamedi nuk është babai i askujt prej burrave tuaj , por është i Dërguari i Allahut dhe vula e profetëve; Allahu është i Gjithëdijshëm për çdo gjë.');
INSERT INTO "hasan_efendi" VALUES(33,41,'33:41','O besimtarë, përmendeni shpesh Allahun');
INSERT INTO "hasan_efendi" VALUES(33,42,'33:42','dhe lavdërojeni Atë në mëngjes dhe në mbrëmje!');
INSERT INTO "hasan_efendi" VALUES(33,43,'33:43','Është Ai që ju bekon, ndërkohë që engjëjt e Tij luten për ju, me qëllim që Ai t’ju nxjerrë nga errësira në dritë. Ai është Mëshirues ndaj besimtarëve.');
INSERT INTO "hasan_efendi" VALUES(33,44,'33:44','Përshëndetja e tyre, kur ta takojnë Atë, është “Paqe”. Ai u ka përgatitur atyre shpërblim bujar.');
INSERT INTO "hasan_efendi" VALUES(33,45,'33:45','O Profet! Ne të kemi dërguar ty si dëshmitar, sjellës të lajmit të mirë dhe paralajmërues;');
INSERT INTO "hasan_efendi" VALUES(33,46,'33:46','njëkohësisht (të kemi çuar) si thirrës, që i fton njerëzit në rrugën e Allahut me urdhrin e Tij dhe si pishtar ndriçues.');
INSERT INTO "hasan_efendi" VALUES(33,47,'33:47','Jepu lajmin e mirë besimtarëve se Allahu ka (përgatitur) për ata dhunti të madhe!');
INSERT INTO "hasan_efendi" VALUES(33,48,'33:48','Mos i dëgjo mohuesit dhe hipokritët! Mos i përfill fyerjet e tyre dhe mbështetu tek Allahu; Allahu mjafton për mbrojtës.');
INSERT INTO "hasan_efendi" VALUES(33,49,'33:49','O besimtarë! Kur martoheni me besimtaret dhe i ndani ato para se t’i prekni, ato nuk janë të detyruara t’ju presin në afatin kohor të caktuar, por jepuni një dhuratë të kënaqshme dhe i ndani njerëzishëm.');
INSERT INTO "hasan_efendi" VALUES(33,50,'33:50','O Profet! Ne t’i kemi lejuar ty (për martesë), gratë që u ke dhënë dhuratat e martesës, skllavet, që t’i ka dhënë ty Allahu (si pre të luftës), vajzat e xhaxhallarëve, hallave, dajave dhe tezeve të cilat mërguan bashkë me ty, si edhe gruan besimtare, që u vetëdha për Ty, nëse Ti dëshiron të martohesh me atë. Kjo të është lejuar vetëm ty e jo besimtarëve të tjerë. Ne e dimë çfarë u kemi caktuar atyre për gratë e tyre dhe për ato që gjenden nën pushtetin e tyre, që për ty të mos ketë fajësim. Allahu është Falës e Mëshirëplotë. ');
INSERT INTO "hasan_efendi" VALUES(33,51,'33:51','Ti mund ta largosh kë të duash prej tyre e të afrosh kë të duash e nuk është gjynah për ty t’i kërkosh (ato) prej të cilave je larguar. Kjo është më e përshtatshme që ato të gëzohen, që të mos pikëllohen dhe që të jenë të kënaqura me atë që u ke dhënë. Allahu e di ç’ka në zemrat tuaja; Allahu është i Gjithëdijshëm dhe i Butë.');
INSERT INTO "hasan_efendi" VALUES(33,52,'33:52','Këtej e tutje nuk të lejohen më gra të tjera, as të marrësh tjetër grua në vend të tyre, edhe sikur të të magjepsë bukuria e tyre, përveç skllaveve. Allahu është mbikëqyrës për çdo gjë.');
INSERT INTO "hasan_efendi" VALUES(33,53,'33:53','O besimtarë, mos hyni në shtëpitë e të Dërguarit, përveç rastit kur ju është dhënë leje për të ngrënë, por jo të pritni përgatitjen e tij (ushqimit)! Hyni vetëm kur t’ju thërrasin dhe, pasi të hani, shpërndahuni! Mos qëndroni pas ushqimit për të biseduar me njëri-tjetrin. Kjo, me të vërtetë, e shqetësonte të Dërguarin e ai turpërohej (që t’jua thoshte këtë), ndërsa Allahu nuk turpërohet që t’jua thotë të vërtetën. Kur të kërkoni prej grave të Profetit diçka, kërkojeni prapa perdes! Kjo është më e pastër për zemrat tuaja dhe të atyre. Juve nuk ju lejohet që ta shqetësoni të Dërguarin e Allahut, as që të martoheni me gratë e tij pas (vdekjes së) tij. Kjo është vërtet gjynah i madh para Allahut.');
INSERT INTO "hasan_efendi" VALUES(33,54,'33:54','Çfarëdo që të shfaqni apo të fshihni, (dijeni se) Allahu është vërtet i Gjithëdijshëm për çdo gjë.');
INSERT INTO "hasan_efendi" VALUES(33,55,'33:55','Nuk është gjynah për ato, nëse dalin të pambuluara para etërve të tyre, djemve të tyre, vëllezërve dhe djemve të vëllezërve të tyre, djemve të motrave të tyre e grave të tyre, si dhe para skllaveve që janë nën pushtetin e tyre. Frikësojuni Allahut, se, me të vërtetë, Allahu është dëshmitar për çdo gjë.');
INSERT INTO "hasan_efendi" VALUES(33,56,'33:56','Vërtet, Allahu e bekon të Dërguarin dhe engjëjt e Tij luten për atë. O besimtarë, lutuni për atë dhe përshëndeteni me “selam”!');
INSERT INTO "hasan_efendi" VALUES(33,57,'33:57','Pa dyshim, ata që fyejnë Allahun dhe të Dërguarin e Tij, i mallkon Allahu në këtë botë dhe në tjetrën. Ai ka përgatitur për ta ndëshkim poshtërues.');
INSERT INTO "hasan_efendi" VALUES(33,58,'33:58','Ata që i lëndojnë besimtarët dhe besimtaret, duke i akuzuar për diçka që nuk e kanë bërë, në të vërtetë, i kanë ngarkuar vetes fajin për shpifje dhe një gjynah të qartë.');
INSERT INTO "hasan_efendi" VALUES(33,59,'33:59','O Profet, thuaju grave dhe vajzave të tua, si dhe grave të besimtarëve, që të lëshojnë mbulesën e tyre (të kokës) përreth trupit. Kjo është mënyra më e përshtatshme që ato të njihen e të mos ngacmohen nga të tjerët. Me të vërtetë, Allahu është Falës dhe Mëshirëplotë.');
INSERT INTO "hasan_efendi" VALUES(33,60,'33:60','Nëse nuk heqin dorë (nga veprimet e tyre) hipokritët, ata që kanë sëmundje (dyshimi) në zemrat e tyre dhe ata që përhapin lajme të gënjeshtërta në Medinë, Ne me siguri, ty do të të japim pushtet mbi ta dhe ditët e tyre në Medinë do të jenë të numëruara.');
INSERT INTO "hasan_efendi" VALUES(33,61,'33:61','Ata janë të mallkuar kudo që të gjenden dhe do të kapen e do të vriten pa mëshirë.');
INSERT INTO "hasan_efendi" VALUES(33,62,'33:62','Kështu ka qenë ligji i Allahut edhe për ata që kanë qenë më parë! Ti kurrsesi nuk do të gjesh ndryshim në ligjet e Allahut.');
INSERT INTO "hasan_efendi" VALUES(33,63,'33:63','Njerëzit të pyesin për Orën (Kiametin), thuaju: “Vetëm Allahu ka dijeni mbi atë”. Kush e di?! Ndoshta Kiameti mund të jetë afër?!');
INSERT INTO "hasan_efendi" VALUES(33,64,'33:64','Vërtet, Allahu i ka mallkuar jobesimtarët dhe ka përgatitur për ata zjarr të flakëruar,');
INSERT INTO "hasan_efendi" VALUES(33,65,'33:65','në të cilin ata do të qëndrojnë përgjithmonë; aty nuk do të gjejnë as mbrojtës, as ndihmës.');
INSERT INTO "hasan_efendi" VALUES(33,66,'33:66','Ditën, kur fytyrat e tyre do të rrotullohen në zjarr, ata do të thonë: “Ah, sikur t’i ishim bindur Allahut dhe të Dërguarit!”');
INSERT INTO "hasan_efendi" VALUES(33,67,'33:67','Dhe do të thonë: “O Zoti ynë, ne iu bindëm parisë tonë dhe eprorëve tanë, por ata na shmangën prej rrugës së drejtë.');
INSERT INTO "hasan_efendi" VALUES(33,68,'33:68','O Zoti ynë, atyre jepu dënim të dyfishtë dhe mallkim të madh!”');
INSERT INTO "hasan_efendi" VALUES(33,69,'33:69','O besimtarë, mos u bëni si ata, që e shqetësuan Musain. Allahu e çliroi atë nga përgojimet e tyre; ai është i nderuar në sytë e Allahut.');
INSERT INTO "hasan_efendi" VALUES(33,70,'33:70','O besimtarë, frikësojuni Allahut dhe thoni vetëm të vërtetën.');
INSERT INTO "hasan_efendi" VALUES(33,71,'33:71','Ai do t’jua bekojë veprat tuaja dhe do t’jua falë gjynahet. Kush i bindet Allahut dhe të Dërguarit të Tij, do të arrijë një fitore madhështore.');
INSERT INTO "hasan_efendi" VALUES(33,72,'33:72','Ne ua ofruam përgjegjësinë qiejve, Tokës dhe maleve, por ato nuk pranuan ta marrin përsipër dhe u frikësuan ta pranojnë atë, kurse njeriu e pranoi. Me të vërtetë, njeriu u tregua i padrejtë me veten dhe i paditur (për pasojat e kësaj përgjegjësie).');
INSERT INTO "hasan_efendi" VALUES(33,73,'33:73','Si pasojë, Allahu do t’i dënojë hipokritët dhe hipokritet, idhujtarët dhe idhujtaret, ndërsa do t’ua pranojë pendimin besimtarëve dhe besimtareve. Vërtet Allahu është Falës dhe Mëshirëplotë.');
INSERT INTO "hasan_efendi" VALUES(34,1,'34:1','I lavdëruar qoftë Allahu, të Cilit i përket gjithçka që ndodhet në qiej dhe në Tokë! I lavdëruar qoftë Ai edhe në jetën tjetër! Ai është i Gjithëdijshëm dhe i Urtë.');
INSERT INTO "hasan_efendi" VALUES(34,2,'34:2','Ai di çfarë hyn në tokë dhe çfarë del prej saj, çfarë zbret nga qielli dhe çfarë ngrihet drejt tij. Ai është Mëshirëploti dhe Falësi.');
INSERT INTO "hasan_efendi" VALUES(34,3,'34:3','Jobesimtarët thonë: “Nuk do të na vijë Ora (e Kiametit)”! Thuaju: “Po, për Zotin tim! Me siguri që ajo do t’ju vijë”! Ai i di të gjitha të fshehtat. Atij nuk mund t’i fshihet asnjë grimë as në qiej, as në Tokë e nuk ka asgjë më të vogël apo më të madhe se ajo, që të mos jetë shënuar në Librin e qartë.');
INSERT INTO "hasan_efendi" VALUES(34,4,'34:4','(Kiameti do t’ju vijë) me qëllim që Ai të shpërblejë ata që besojnë dhe bëjnë vepra të mira. Për njerëz të tillë do të ketë falje dhe begati të mrekullueshme.');
INSERT INTO "hasan_efendi" VALUES(34,5,'34:5','Ndërsa për ata që luftojnë kundër shpalljeve Tona, duke u përpjekur që t’i pengojnë ato, do të ketë dënim të tmerrshëm dhe të dhembshëm.');
INSERT INTO "hasan_efendi" VALUES(34,6,'34:6','Ata, të cilëve u është dhënë dituria, e shohin se ajo, që të është zbritur ty nga Zoti yt, është e vërteta, e cila të udhëzon në rrugën e të Plotfuqishmit dhe të Denjit për çdo lavd.');
INSERT INTO "hasan_efendi" VALUES(34,7,'34:7','Ndërsa jobesimtarët thonë: “A doni t’ju tregojmë një njeri që pretendon se, kur të shpërbëheni plotësisht e të bëheni pluhur, ju, me siguri do të rikrijoheni nga e para?');
INSERT INTO "hasan_efendi" VALUES(34,8,'34:8','A po trillon gënjeshtra për Allahun apo është i çmendur?” (Nuk është kështu), por ata që nuk besojnë në jetën tjetër, do të dënohen dhe janë në humbje të madhe.');
INSERT INTO "hasan_efendi" VALUES(34,9,'34:9','Vallë, a nuk shohin ata çfarë ka para tyre e pas tyre në qiell dhe në Tokë? Sikur të donim, Ne do t’i fundosnim ata në tokë ose do të hidhnim mbi ta copa nga qielli. Me të vërtetë, në këtë ka shenjë për çdo rob që kthehet tek Allahu me pendesë.');
INSERT INTO "hasan_efendi" VALUES(34,10,'34:10','Me të vërtetë, Ne i dhamë Davudit mirësi prej Nesh (dhe thamë): “O male dhe zogj! Lavdërojeni emrin Tim bashkë me Davudin”. Ne ia zbutëm atij hekurin,');
INSERT INTO "hasan_efendi" VALUES(34,11,'34:11','(dhe i thamë): “Puno parzmore të gjera! Thuri ato me maja të barazpeshuara dhe bëj vepra të mira! Pa dyshim, Unë shoh çfarë punoni ju.”');
INSERT INTO "hasan_efendi" VALUES(34,12,'34:12','Sulejmanit Ne ia nënshtruam erën, me të cilën udhëtimin e një muaji ai e bënte brenda një paraditeje ose një pasditeje. Ne i dhamë atij një burim të rrjedhshëm bakri të shkrirë dhe xhinde që i bëmë të punonin para syve të tij, me urdhrin e Zotit të tij. Këdo prej tyre që largohet nga urdhri ynë, do ta bëjmë që të shijojë dënimin e Zjarrit.');
INSERT INTO "hasan_efendi" VALUES(34,13,'34:13','Ata punonin për atë çfarë të dëshironte: faltore dhe statuja, enë të gjera si puse dhe kazanë të mëdhenj të palëvizshëm. “O familje e Davudit! Bëni falënderim (për Allahun)!” Megjithatë, pak nga robërit e Mi, janë falënderues.');
INSERT INTO "hasan_efendi" VALUES(34,14,'34:14','Kur Ne ia caktuam atij vdekjen, atyre nuk u dha shenjë askush për vdekjen e tij, pos krimbit të drurit . Dhe, kur u rrëzua (Sulejmani), u bë e qartë se, sikur ta dinin xhindet të padukshmen, nuk do të vijonin në dënimin e poshtëruar.');
INSERT INTO "hasan_efendi" VALUES(34,15,'34:15','Vendbanimi i Sebës ishte një shenjë e qartë për banorët e tij: dy kopshte në të djathtë dhe në të majtë të lumit. (Ne u thamë atyre): “Hani nga ato që ju ka dhënë Zoti juaj dhe falënderojeni Atë. I mrekullueshëm është vendi juaj dhe Falës i madh është Zoti juaj!”');
INSERT INTO "hasan_efendi" VALUES(34,16,'34:16','Por ata nuk ua vunë veshin (fjalëve Tona), prandaj Ne lëshuam mbi ta ujërat e digës dhe dy kopshtet e tyre ua zëvendësuam me kopshte të tjera, me fruta të hidhëta marine dhe pak lotusë.');
INSERT INTO "hasan_efendi" VALUES(34,17,'34:17','Ne i dënuam kështu si pasojë e mosbesimit dhe e mosmirënjohjes së tyre. Vallë, a dënojmë Ne tjetërkënd, përveç mosmirënjohësve?');
INSERT INTO "hasan_efendi" VALUES(34,18,'34:18','Midis atyre dhe qyteteve që i kemi bekuar, Ne vendosëm vendbanime të dukshme buzë rrugës dhe e bëmë të lehtë udhëtimin (duke u thënë): “Udhëtoni të sigurt nëpër to natë e ditë!”');
INSERT INTO "hasan_efendi" VALUES(34,19,'34:19','Por ata thanë: “O Zoti ynë, bëje më të madhe largësinë e udhëtimeve tona”. kështu ata e ngarkuan veten me gjynahe dhe Ne i bëmë ata rrëfenja dhe i përndamë plotësisht. Sigurisht, në këtë, ka shenja si mësime për çdo të durueshëm dhe falënderues.');
INSERT INTO "hasan_efendi" VALUES(34,20,'34:20','Me të vërtetë, hamendja e Iblisit për ata u vërtetua; ata shkuan pas tij, përveç një grupi besimtarësh të vërtetë.');
INSERT INTO "hasan_efendi" VALUES(34,21,'34:21','Por ai (Iblisi) nuk ka pasur kurrfarë pushteti mbi ta. Ne vetëm kemi dashur të zbulojmë se kush beson në jetën tjetër e kush dyshon në të. Zoti yt është Mbikëqyrës i çdo gjëje.');
INSERT INTO "hasan_efendi" VALUES(34,22,'34:22','Thuaju (o Muhamed): “Thirrini ata që i hyjnizoni krahas Allahut! Ata nuk kanë pushtet as sa pesha e një thërrmije, qoftë në qiej apo në Tokë; për ata nuk ka pjesë as në qiej, as në Tokë dhe Allahu nuk ka asnjë ndihmës nga radhët e tyre.”');
INSERT INTO "hasan_efendi" VALUES(34,23,'34:23','Asnjë ndërmjetësim tek Ai nuk bën dobi, përveçse për atë që Ai jep leje. Kur t’u hiqet frika nga zemrat e tyre, ata do të thonë: “Çfarë tha Zoti juaj?” dhe do të përgjigjen: “Të vërtetën! Ai është i Lartësuar dhe i Madhërishëm.”');
INSERT INTO "hasan_efendi" VALUES(34,24,'34:24','Thuaju: “Kush ju furnizon ju prej qiejve dhe prej Tokës?” - Thuaju: “Allahu! Ne nuk mund të jemi që të dyja palët në rrugën e drejtë, ose ju, ose ne jemi në humbje të qartë!”');
INSERT INTO "hasan_efendi" VALUES(34,25,'34:25','Thuaju: “Ju, nuk do të pyeteni për atë që kemi gabuar ne, as ne nuk do të përgjigjemi për atë që bëni ju.”');
INSERT INTO "hasan_efendi" VALUES(34,26,'34:26','Thuaju: “Ne do të na mbledhë Zoti ynë e do të gjykojë ndërmjet nesh me drejtësi. Ai është Gjykatës i drejtë e i Gjithëdijshëm.”');
INSERT INTO "hasan_efendi" VALUES(34,27,'34:27','Thuaju: “M’i tregoni ata, që ia vishni Atij si ortakë.” Kurrsesi, ata nuk mund të jenë ortakë të Tij. Ai është Allahu (Një i Vetëm), i Plotfuqishmi dhe i Urti.');
INSERT INTO "hasan_efendi" VALUES(34,28,'34:28','Ne të kemi dërguar ty (o Muhamed) vetëm si sjellës të lajmit të mirë dhe paralajmërues për të gjithë njerëzimin, por shumica e njerëzve nuk dinë.');
INSERT INTO "hasan_efendi" VALUES(34,29,'34:29','Ata thonë: “Kur do të vijë ky premtim (Kiameti), nëse ju thoni të vërtetën?”');
INSERT INTO "hasan_efendi" VALUES(34,30,'34:30','Thuaju atyre: “Dita juaj tashmë është caktuar. Ju nuk do të mund ta shtyni atë e as ta shpejtoni, qoftë edhe për një çast.”');
INSERT INTO "hasan_efendi" VALUES(34,31,'34:31','Ata që nuk besojnë, thonë: “Ne nuk do të besojmë kurrsesi në këtë Kuran, as në ato që janë zbritur para tij”. Eh, sikur t’i shihje keqbërësit duke dalë para Zotit të tyre dhe duke shkëmbyer akuza me njëri-tjetrin. Të dobëtit do t’u thonë atyre që madhështoheshin: “Sikur të mos ishit ju, ne do të ishim besimtarë”.');
INSERT INTO "hasan_efendi" VALUES(34,32,'34:32','Ata që madhështoheshin, do t’u thonë atyre që i quanin për të dobët: “Vallë, ne ju penguam nga udhëzimi i Allahut, kur ju erdhi juve?! Jo, por ju jeni keqbërës!”');
INSERT INTO "hasan_efendi" VALUES(34,33,'34:33','Ndërsa të dobëtit do t’u thonë atyre që madhështoheshin: “Nuk është kështu! Ju keni komplotuar natë e ditë, duke na urdhëruar, që ta mohonim Allahun dhe t’i vishnim Atij (zota të tjerë) të barabartë me Atë”. Dhe ata do ta fshehin pikëllimin, kur ta shohin dënimin. Ne do të vëmë vargonj në qafat e atyre që mohuan. A do të ndëshkohen ndryshe, veçse sipas asaj që kanë bërë?!');
INSERT INTO "hasan_efendi" VALUES(34,34,'34:34','Në çdo vendbanim që Ne kemi nisur ndonjë të dërguar, ata që bënin jetë luksoze thoshin: “Me të vërtetë, ne nuk besojmë në mesazhin që keni sjellë!”');
INSERT INTO "hasan_efendi" VALUES(34,35,'34:35','Dhe thanë: “Ne kemi më shumë pasuri dhe fëmijë, prandaj ne nuk do të jemi të dënuar.”');
INSERT INTO "hasan_efendi" VALUES(34,36,'34:36','Thuaju: “Pa dyshim, Zoti im ia shton riskun ose ia pakëson kujt të dojë, por shumica e njerëzve nuk e dinë këtë.”');
INSERT INTO "hasan_efendi" VALUES(34,37,'34:37','As pasuria juaj, as fëmijët tuaj nuk do t’ju afrojnë më shumë te Ne, por vetëm ata që besojnë dhe bëjnë vepra të mira (e meritojnë këtë). Ata do të kenë shpërblim të shumëfishtë për atë që kanë bërë dhe do të jenë të sigurt në banesa të larta (në Xhenet).');
INSERT INTO "hasan_efendi" VALUES(34,38,'34:38','Ndërsa, ata që përpiqen t’i luftojnë shpalljet Tona, ata do të dënohen.');
INSERT INTO "hasan_efendi" VALUES(34,39,'34:39','Thuaj: “Me të vërtetë, Zoti im i jep begati të bollshme kujt të dojë nga robërit e Tij dhe ia pakëson kujt të dojë. Ai ua zëvendëson atë që e ndani ju; Ai është Dhuruesi më i mirë.”');
INSERT INTO "hasan_efendi" VALUES(34,40,'34:40','Ditën, kur do t’i tubojë të gjithë, do t’u thotë engjëjve: “Vallë, këta ju ju adhuronin?”');
INSERT INTO "hasan_efendi" VALUES(34,41,'34:41','Ata do të thonë: “Qofsh i lavdëruar Ti, o Zoti ynë! Ti je Mbrojtësi ynë, jo ata. Ata adhuronin xhindet dhe shumica e mohuesve u besonin atyre.”');
INSERT INTO "hasan_efendi" VALUES(34,42,'34:42','Atë Ditë ata nuk do të mund t’i bëjnë as dobi, as dëm njëri tjetrit. Ne do t’u themi keqbërësve: “Shijoni dënimin e zjarrit të cilin e keni mohuar!”');
INSERT INTO "hasan_efendi" VALUES(34,43,'34:43','Kur iu lexohen atyre shpalljet Tona të qarta, ata (paganët e Mekës) thonë: “Ky (Muhamedi a.s.) është vetëm njeri, që dëshiron t’ju largojë nga ajo që etërit tuaj kanë adhuruar”. Dhe ata thonë: “Ky (Kuran) nuk është asgjë tjetër veçse një gënjeshtër e trilluar!” Dhe, kur u vjen e Vërteta, jobesimtarët thonë: “Kjo është vetëm magji e qartë!”');
INSERT INTO "hasan_efendi" VALUES(34,44,'34:44','Ne nuk u kemi dërguar asnjë Libër, për ta mësuar ata dhe Ne nuk u kemi dërguar atyre para teje asnjë paralajmërues.');
INSERT INTO "hasan_efendi" VALUES(34,45,'34:45','Ata, që kanë qenë para tyre, i mohuan shpalljet Tona. Ata ishin dhjetë herë më të begatë dhe më të fuqishëm (se idhujtarët e Mekës) e, megjithatë, i quajtën gënjeshtarë të dërguarit e Mi. Eh, si ka qenë dënimi Im (ndaj tyre)!');
INSERT INTO "hasan_efendi" VALUES(34,46,'34:46','Thuaj (o Muhamed): “Unë ju këshilloj vetëm një gjë: ngrihuni sinqerisht për Allahun, dy nga dy ose një nga një, e pastaj gjykoni për shokun tuaj (Muhamedin) që nuk është i çmendur. Ai është dërguar vetëm që t’ju paralajmërojë para një dënimi të rëndë.”');
INSERT INTO "hasan_efendi" VALUES(34,47,'34:47','Thuaj: “Çfarëdo shpërblimi, që mund t’ju kem kërkuar, ai qoftë për ju. Shpërblimi im është vetëm tek Allahu; Ai është dëshmitar për çdo gjë”.');
INSERT INTO "hasan_efendi" VALUES(34,48,'34:48','Thuaj: “Pa dyshim, Zoti im shpall të Vërtetën. Ai është i Gjithëdijshëm për të gjitha të fshehtat.”');
INSERT INTO "hasan_efendi" VALUES(34,49,'34:49','Thuaj: “E vërteta erdhi, kurse e pavërteta është fshirë e nuk mund të shfaqet përsëri.”');
INSERT INTO "hasan_efendi" VALUES(34,50,'34:50','Thuaj: “Nëse gaboj, unë gaboj vetëm për dëmin tim e, nëse udhëzohem në rrugën e drejtë, kjo është nga shpallja e Zotit tim. Ai, me të vërtetë, i dëgjon të gjitha dhe është afër”.');
INSERT INTO "hasan_efendi" VALUES(34,51,'34:51','Sikur t’i shihje jobesimtarët, kur të frikësohen (gjatë ringjalljes)! Ata nuk do të kenë nga ikin dhe do të nxirren prej varreve të tyre.');
INSERT INTO "hasan_efendi" VALUES(34,52,'34:52','Pastaj do të thonë: “Ne tashmë besojmë.” Por si do ta përvetësojnë (besimin) nga një vend aq i largët (prej kësaj bote, ku vlen besimi)?!');
INSERT INTO "hasan_efendi" VALUES(34,53,'34:53','Në të vërtetë, ata e mohuan besimin më parë (në këtë botë) dhe e përgënjeshtruan të padukshmen prej së largëti.');
INSERT INTO "hasan_efendi" VALUES(34,54,'34:54','Një pengesë do të vendoset midis tyre dhe asaj që dëshironin, ashtu siç është bërë me të ngjashmit e tyre më parë. Me të vërtetë, ata të gjithë kanë qenë në dyshim të madh.');
INSERT INTO "hasan_efendi" VALUES(35,1,'35:1','Lavdërimi qoftë për Allahun, krijuesin e qiejve dhe Tokës, i Cili i ka bërë engjëjt të dërguar me nga dy, tre dhe katër krahë. Ai shton në krijim çfarë të dojë. Allahu, me të vërtetë, është i pushtetshëm për çdo gjë.');
INSERT INTO "hasan_efendi" VALUES(35,2,'35:2','Begatitë, që Allahu ua jep njerëzve nga mëshira e Tij, nuk mund t’i pengojë askush, ndërsa atë, që Ai e pengon, askush nuk mund ta japë veç Tij; Ai është i Plotfuqishmi dhe i Urti.');
INSERT INTO "hasan_efendi" VALUES(35,3,'35:3','O njerëz! Kujtoni dhuntinë e Allahut për ju! A ka ndonjë krijues, veç Allahut, i cili ju furnizon nga qielli dhe Toka? S’ka zot tjetër të vërtetë përveç Tij. Atëherë, si po largoheni ju?');
INSERT INTO "hasan_efendi" VALUES(35,4,'35:4','Nëse ata të quajnë ty gënjeshtar edhe para teje të dërguar të tjerë i kanë quajtur gënjeshtarë; tek Allahu kthehen të gjitha punët.');
INSERT INTO "hasan_efendi" VALUES(35,5,'35:5','O njerëz! Pa dyshim, premtimi i Allahut (Dita e Gjykimit) është i vërtetë, prandaj të mos ju mashtrojë kurrsesi jeta e kësaj bote dhe të mos ju mashtrojë djalli për Allahun.');
INSERT INTO "hasan_efendi" VALUES(35,6,'35:6','Me të vërtetë, djalli është armik për ju, andaj vështrojeni si armik! Ai vetëm i josh ithtarët e vet, që të bëhen shokë të zjarrit të përflakur.');
INSERT INTO "hasan_efendi" VALUES(35,7,'35:7','Për ata që nuk besojnë, ka dënim të rëndë, kurse për ata që besojnë dhe bëjnë vepra të mira, ka falje dhe shpërblim të madh.');
INSERT INTO "hasan_efendi" VALUES(35,8,'35:8','A është ai, që veprat e veta të këqija i duken të mira (si ai që është i udhëzuar në rrugën e drejtë)? Me të vërtetë, Allahu shpie në humbje kë të dojë dhe udhëzon në rrugë të drejtë kë të dojë. Ti mos e shqetëso veten deri në shkatërrim për humbjen e tyre! Allahu është vërtet i Gjithëdijshëm për atë që bëjnë ata.');
INSERT INTO "hasan_efendi" VALUES(35,9,'35:9','Allahu është ai që i dërgon erërat, të cilat lëvizin retë. Ne i drejtojmë ato në viset e vdekura e, nëpërmjet tyre gjallërojmë tokën, pasi ka qenë e shkretë. Kështu do të jetë edhe ringjallja.');
INSERT INTO "hasan_efendi" VALUES(35,10,'35:10','Kush dëshiron lavdi e pushtet, ta dijë se e gjithë lavdia e pushteti i takojnë Allahut. Tek Ai ngjiten fjalët e bukura, ndërsa veprën e mirë Ai e ngre Vetë. Sa i përket atyre që kurdisin të këqija, ata do të kenë dënim të rëndë dhe dredhitë e tyre do të dështojnë.');
INSERT INTO "hasan_efendi" VALUES(35,11,'35:11','Allahu ju ka krijuar së pari prej dheut (Ademin), pastaj (pasardhësit e tij i ka krijuar) nga pika e farës dhe më në fund ju ka bërë çifte. Dhe asnjë femër nuk mbetet shtatzënë e as nuk lind pa dijeninë e Tij. Jeta e askujt nuk zgjatet e as nuk shkurtohet pa qenë e shënuar në Libër. Me të vërtetë, kjo për Allahun është e lehtë!');
INSERT INTO "hasan_efendi" VALUES(35,12,'35:12','Edhe dy detet (llojet e ujit) nuk janë të njëjta: njëri është i ëmbël e i këndshëm dhe uji i pihet me shije, kurse tjetri është i njelmët dhe i hidhët. Ju ushqeheni me mish të njomë prej të dyve dhe prej tyre nxirrni stolitë që mbani. Shikoni anijet si çajnë ujin, ndërsa ju lundroni me to për të kërkuar nga dhuntitë e Tij. Ndoshta ju bëheni falënderues.');
INSERT INTO "hasan_efendi" VALUES(35,13,'35:13','Ai e bën natën të kalojë në ditë dhe ditën të kalojë në natë. Ai i ka nënshtruar Diellin dhe Hënën, kështu që secili lëviz për një kohë të caktuar. Ky është Allahu, Zoti juaj. Pushteti është i Tij! Kurse ata që ju i adhuroni krahas Tij, nuk zotërojnë as sa cipa e bërthamës së hurmës.');
INSERT INTO "hasan_efendi" VALUES(35,14,'35:14','Nëse ju u luteni atyre, ata nuk e dëgjojnë lutjen tuaj, por edhe sikur ta dëgjonin, nuk do t’ju përgjigjeshin. Në Ditën e Kiametit, ata do ta mohojnë adhurimin që u bënit atyre. Askush nuk mund të të njoftojë ty (o Muhamed) ashtu si Ai që është i Gjithëdijshëm.');
INSERT INTO "hasan_efendi" VALUES(35,15,'35:15','O njerëz! Ju jeni të varfër e nevojtarë për Allahun, kurse Allahu s’ka nevojë për asgjë dhe është i Denjë për çdo lavd.');
INSERT INTO "hasan_efendi" VALUES(35,16,'35:16','Nëse do, Ai ju zhduk dhe sjell krijesa të reja.');
INSERT INTO "hasan_efendi" VALUES(35,17,'35:17','Kjo gjë nuk është e vështirë për Allahun.');
INSERT INTO "hasan_efendi" VALUES(35,18,'35:18','Asnjë shpirt i ngarkuar nuk do të mbartë barrën e tjetrit. Nëse i ngarkuari rëndë thërret dikë që t’ia mbartë, nuk do t’i merret asgjë nga barra, edhe sikur të jetë i afërt. Në të vërtetë, ti paralajmëron vetëm ata që i frikësohen Zotit të tyre në fshehtësi dhe që falin namazin. Kush e pastron veten (nga gjynahet), në të vërtetë e ka për dobinë e vet, ndërsa kthimi është tek Allahu.');
INSERT INTO "hasan_efendi" VALUES(35,19,'35:19','Nuk janë, kurrsesi, njësoj i verbri dhe ai që sheh,');
INSERT INTO "hasan_efendi" VALUES(35,20,'35:20','as errësira dhe drita,');
INSERT INTO "hasan_efendi" VALUES(35,21,'35:21','as freskia (e hijes) dhe vapa.');
INSERT INTO "hasan_efendi" VALUES(35,22,'35:22','Po ashtu, nuk janë kurrë të barabartë të gjallët dhe të vdekurit. Allahu bën të dëgjojë kë të dojë Vetë, kurse ti (Muhamed) nuk mund të bësh të dëgjojnë ata që janë në varre.');
INSERT INTO "hasan_efendi" VALUES(35,23,'35:23','Ti je vetëm paralajmërues.');
INSERT INTO "hasan_efendi" VALUES(35,24,'35:24','Ne të kemi dërguar ty me të Vërtetën, për të sjellë lajme të mira dhe për të paralajmëruar; nuk ka pasur asnjë popull që të mos i ketë ardhur paralajmërues.');
INSERT INTO "hasan_efendi" VALUES(35,25,'35:25','Nëse ata të quajnë ty gënjeshtar, dije se edhe paraardhësit e këtyre i kanë përgënjeshtruar të dërguarit e tyre, që u sollën prova të qarta, shkrime dhe një Libër ndriçues.');
INSERT INTO "hasan_efendi" VALUES(35,26,'35:26','Pastaj Unë i dënova mohuesit; eh, sa i tmerrshëm ka qenë dënimi Im!');
INSERT INTO "hasan_efendi" VALUES(35,27,'35:27','Vallë, a nuk e sheh ti se si Allahu e lëshon ujin prej qiellit dhe nëpërmjet tij krijon fruta llojesh e ngjyrash të ndryshme?! Nëpër male gjenden shtigje të bardha dhe të kuqe, ngjyrash të ndryshme dhe krejtësisht të zeza.');
INSERT INTO "hasan_efendi" VALUES(35,28,'35:28','Njerëzit, kafshët dhe bagëtitë janë llojesh të ndryshme gjithashtu. Në të vërtetë, nga robërit e Tij, Allahut i frikësohen vetëm dijetarët, (që e dinë se) Allahu është vërtet i Plotfuqishëm dhe Falës.');
INSERT INTO "hasan_efendi" VALUES(35,29,'35:29','Pa dyshim, ata që lexojnë Librin e Allahut, falin namazin dhe japin fshehtazi apo haptazi nga ajo që u kemi dhënë Ne, mund të shpresojnë në një fitim që nuk do të humbasë.');
INSERT INTO "hasan_efendi" VALUES(35,30,'35:30','Allahu do t’ua plotësojë atyre shpërblimin dhe do t’ua shtojë nga dhuntitë e Tij. Me të vërtetë, Ai është Falës dhe Falënderues.');
INSERT INTO "hasan_efendi" VALUES(35,31,'35:31','Ajo që ta kemi shpallur ty në Libër, është e vërteta e cila konfirmon atë që është shpallur para tij. Sigurisht që Allahu i njeh dhe i sheh mirë robërit e Vet.');
INSERT INTO "hasan_efendi" VALUES(35,32,'35:32','Pastaj, Ne ua dhamë Librin robërve Tanë të zgjedhur (nga ndjekësit e Muhamedit a.s.). Disa prej tyre i bëjnë dëm vetes (duke bërë vepra të ndaluara), disa janë në rrugë të mesme (duke kryer detyrimet dhe duke shmangur të ndaluarat), ndërsa disa, me lejen e Allahut, shkojnë përpara me të mira (përtej detyrimeve); kjo është dhuntia e madhe.');
INSERT INTO "hasan_efendi" VALUES(35,33,'35:33','Ata do të hyjnë në kopshtet e Adnit të stolisur me byzylykë ari e margaritarë dhe veshja e tyre do të jetë prej mëndafshi.');
INSERT INTO "hasan_efendi" VALUES(35,34,'35:34','Pastaj do të thonë: “Lavdi Allahut, i Cili e ka larguar hidhërimin nga ne. Vërtet, Zoti ynë është Falës dhe Falënderues.');
INSERT INTO "hasan_efendi" VALUES(35,35,'35:35','Është Ai që me mirësinë e Tij na ka pranuar në shtëpinë e banimit të përhershëm, në të cilën nuk do të lodhemi e nuk do të rraskapitemi.”');
INSERT INTO "hasan_efendi" VALUES(35,36,'35:36','Për jobesimtarët është përgatitur zjarri i Xhehenemit, ku ata nuk do të vdesin dhe as që do t’u lehtësohet dënimi. Ja, kështu Ne do t’i dënojmë mosmirënjohësit.');
INSERT INTO "hasan_efendi" VALUES(35,37,'35:37','Atje, ata do të thërrasin: “O Zoti ynë, nxirrna se do të bëjmë vepra të mira e jo nga ato që kemi bërë.” (Atyre do t’u thuhet): “Vallë, a nuk jua zgjatëm jetën aq sa të mund të mendohej ai që donte të kujtonte (Allahun)?! Madje juve ju erdhi edhe paralajmëruesi? Andaj, shijoni (dënimin)! Për keqbërësit nuk ka kurrfarë ndihmësi!”');
INSERT INTO "hasan_efendi" VALUES(35,38,'35:38','Vërtet, Allahu e njeh të fshehtën e qiejve e të Tokës dhe e di mirë ç’ka në zemrat e njerëzve.');
INSERT INTO "hasan_efendi" VALUES(35,39,'35:39','Është Ai, që ju ka bërë trashëgimtarë në Tokë. Kush nuk beson, do të mbajë barrën e mohimit të vet. Mosbesimi u shton jobesimtarëve vetëm urrejtje para Zotit të tyre; mosbesimi u sjell atyre vetëm humbje.');
INSERT INTO "hasan_efendi" VALUES(35,40,'35:40','Thuaju: “Vërini re idhujt që ju i adhuroni krahas Allahut! Më tregoni, çfarë kanë krijuar ata në Tokë?! A kanë marrë gjë pjesë në krijimin e qiejve? Apo Ne u kemi dhënë idhujtarëve një Libër, ku kanë prova (për idhujtarinë që bëjnë)?” Nuk është kështu, por keqbërësit i premtojnë njëri-tjetrit vetëm mashtrime.');
INSERT INTO "hasan_efendi" VALUES(35,41,'35:41','Me të vërtetë, Allahu mban ekuilibrin e qiejve dhe të Tokës, që të mos shkatërrohen. Kur ato nisin të shkatërrohen, askush nuk mund t’i mbrojë, pos Tij. Me të vërtetë, Ai është i Butë dhe Falës (i gjynaheve).');
INSERT INTO "hasan_efendi" VALUES(35,42,'35:42','Ata (paganët e Mekës) janë betuar solemnisht për Allahun që, nëse u vjen ndonjë (profet) paralajmërues, ata do të ndjekin udhëzimin e tij më shpejt se çdo popull tjetër. Por, kur u erdhi paralajmëruesi, kjo vetëm ua shtoi largimin prej rrugës së drejtë.');
INSERT INTO "hasan_efendi" VALUES(35,43,'35:43','Ata u sollën me mendjemadhësi në Tokë dhe me dredhi të shëmtuara, të cilat godasin vetëm atë që i bën. Vallë, a presin ata diçka tjetër veç asaj që ka goditur të mëparshmit? Vërtet, Ti nuk mund të gjesh ndryshim ose shndërrim në ligjet e Allahut.');
INSERT INTO "hasan_efendi" VALUES(35,44,'35:44','Vallë, a nuk kanë udhëtuar ata nëpër botë për të parë si ka qenë përfundimi i të mëparshmëve, të cilët kanë qenë më të fortë se këta? Allahut nuk mund t’i shpëtojë asgjë, as në qiej, as në Tokë. Ai është vërtet i Gjithëdijshëm dhe i Fuqishëm për çdo gjë.');
INSERT INTO "hasan_efendi" VALUES(35,45,'35:45','Sikur Allahu t’i dënonte njerëzit sipas veprave që bëjnë, nuk do të mbetej në sipërfaqen e Tokës asgjë e gjallë, por Ai i shtyn ata deri në afatin e caktuar dhe, kur t’u vijë afati i tyre, ata do ta dinë se Allahu i mbikëqyr mirë robërit e Vet.');
INSERT INTO "hasan_efendi" VALUES(36,1,'36:1','Jâ Sîn.');
INSERT INTO "hasan_efendi" VALUES(36,2,'36:2','Për Kuranin plot urtësi!');
INSERT INTO "hasan_efendi" VALUES(36,3,'36:3','Në të vërtetë, ti (Muhamed) je një nga të dërguarit');
INSERT INTO "hasan_efendi" VALUES(36,4,'36:4','në rrugë të drejtë');
INSERT INTO "hasan_efendi" VALUES(36,5,'36:5','me Shpalljen e të Plotfuqishmit, Mëshirëplotit,');
INSERT INTO "hasan_efendi" VALUES(36,6,'36:6','që të paralajmërosh një popull, paraardhësit e të cilit nuk ishin të paralajmëruar, prandaj këta janë të pakujdesshëm.');
INSERT INTO "hasan_efendi" VALUES(36,7,'36:7','Shumica e tyre meritojnë dënimin Tonë, se ata nuk besojnë.');
INSERT INTO "hasan_efendi" VALUES(36,8,'36:8','Ne do t’u vëmë në qafë vargonj, të cilët do t’u arrijnë deri në mjekër, e kështu do të jenë si të mpirë.');
INSERT INTO "hasan_efendi" VALUES(36,9,'36:9','Ne kemi vënë pengesë para tyre dhe pas tyre e i kemi mbuluar, kështu që ata nuk shohin.');
INSERT INTO "hasan_efendi" VALUES(36,10,'36:10','Për ata është njëlloj, i paralajmërove apo nuk i paralajmërove, ata nuk besojnë.');
INSERT INTO "hasan_efendi" VALUES(36,11,'36:11','Në të vërtetë, paralajmërimi yt i sjell dobi vetëm atij që ndjek Këshillën (Kuranin) dhe i frikësohet të Gjithëmëshirshmit, edhe pa e parë Atë. Këtij jepi lajmin e mirë për falje gjynahesh dhe shpërblim bujar!');
INSERT INTO "hasan_efendi" VALUES(36,12,'36:12','Sigurisht, Ne i ringjallim të vdekurit dhe shënojmë veprat që kanë bërë dhe gjurmët e (punëve të) tyre. Ne e kemi llogaritur çdo gjë në një Libër të qartë.');
INSERT INTO "hasan_efendi" VALUES(36,13,'36:13','Përmendu atyre shembullin e banorëve të qytetit, kur u erdhën të dërguarit.');
INSERT INTO "hasan_efendi" VALUES(36,14,'36:14','Ne u dërguam atyre dy vetë, që ata i quajtën gënjeshtarë. Pastaj i përforcuam të dërguarit me një të tretë e ata thanë: “Ne, me të vërtetë, jemi dërguar te ju!”');
INSERT INTO "hasan_efendi" VALUES(36,15,'36:15','Banorët u thanë: “Ju jeni vetëm njerëz si ne, e i Gjithëmëshirshmi nuk ju ka shpallur asgjë; ju vetëm gënjeni”.');
INSERT INTO "hasan_efendi" VALUES(36,16,'36:16','Ata u përgjigjën: “Zoti ynë e di që ne jemi vërtet të dërguar te ju');
INSERT INTO "hasan_efendi" VALUES(36,17,'36:17','dhe kemi detyrë vetëm që të shpallim qartë”.');
INSERT INTO "hasan_efendi" VALUES(36,18,'36:18','Banorët thanë: “Ne parandiejmë fatkeqësi nga ju. Nëse nuk tërhiqeni, do t’ju gurëzojmë dhe me siguri do t’ju godasë një dënim i dhembshëm prej nesh”.');
INSERT INTO "hasan_efendi" VALUES(36,19,'36:19','Ata u përgjigjën: “Fatkeqësia, që ju ndollët, mund të vijë vetëm nga vetja juaj. Vallë, për shkak se po ju këshillojmë, na kërcënoni?! Ju jeni popull që kaloni çdo lloj kufiri.”');
INSERT INTO "hasan_efendi" VALUES(36,20,'36:20','Dhe nga skajet e qytetit erdhi një njeri, duke nxituar dhe tha: “O populli im, ndiqni të dërguarit!');
INSERT INTO "hasan_efendi" VALUES(36,21,'36:21','Ndiqni ata, që nuk kërkojnë prej jush kurrfarë shpërblimi e që janë në rrugë të drejtë!');
INSERT INTO "hasan_efendi" VALUES(36,22,'36:22','Përse të mos e adhuroj Atë, i Cili më ka krijuar e tek i Cili do të ktheheni të gjithë?!');
INSERT INTO "hasan_efendi" VALUES(36,23,'36:23','Vallë, përse duhet të adhuroj zota të tjerë përveç Tij? Nëse i Gjithëmëshirshmi do të dëshironte të më godiste me ndonjë të keqe, ndërmjetësimi i atyre (zotave) nuk do të më sillte kurrfarë dobie e as që do të mund të më shpëtonin.');
INSERT INTO "hasan_efendi" VALUES(36,24,'36:24','Atëherë, unë do të isha me siguri në humbje të dukshme.');
INSERT INTO "hasan_efendi" VALUES(36,25,'36:25','Unë i kam besuar Zotit tuaj, andaj, më dëgjoni!”');
INSERT INTO "hasan_efendi" VALUES(36,26,'36:26','(Pasi e vrau populli i vet, atij) iu tha: “Hyr në Xhenet!” Ai tha: “Ah, sikur ta dinte populli im,');
INSERT INTO "hasan_efendi" VALUES(36,27,'36:27','se si më fali Zoti im dhe më bëri të nderuar!”');
INSERT INTO "hasan_efendi" VALUES(36,28,'36:28','Por pas (vdekjes së) tij, Ne nuk i dërguam kurrfarë ushtrie nga qielli (si dënim) popullit të tij dhe as që kishte nevojë për të dërguar.');
INSERT INTO "hasan_efendi" VALUES(36,29,'36:29','(Mjaftoi për t’i dënuar ata) vetëm një zë i tmerrshëm, pas të cilit u shuan përnjëherësh.');
INSERT INTO "hasan_efendi" VALUES(36,30,'36:30','Ah, sa mjerim për ata njerëz! Kurdo që u vinte ndonjë i dërguar, ata nuk bënin tjetër, por vetëm talleshin me të.');
INSERT INTO "hasan_efendi" VALUES(36,31,'36:31','Vallë, a nuk e kishin parë ata se sa e sa brezni para tyre që kishim shkatërruar nuk u kthyen më?!');
INSERT INTO "hasan_efendi" VALUES(36,32,'36:32','Të gjithë ata do të paraqiten bashkërisht te Ne.');
INSERT INTO "hasan_efendi" VALUES(36,33,'36:33','Shenjë për ata është toka e vdekur; Ne e ringjallim atë dhe nga ajo nxjerrim drithëra, me të cilat ushqehen.');
INSERT INTO "hasan_efendi" VALUES(36,34,'36:34','Ne kemi krijuar në tokë kopshte hurmash dhe vreshta e kemi bërë të shpërthejnë në të burime uji,');
INSERT INTO "hasan_efendi" VALUES(36,35,'36:35','që ata t’i hanë frutat e tyre. Këto nuk i bënë duart e tyre; ndaj, përse ata nuk duan të jenë mirënjohës?!');
INSERT INTO "hasan_efendi" VALUES(36,36,'36:36','Qoftë lavdëruar Ai që i ka krijuar çift të gjitha llojet: çka mbin prej tokës, ata vetë dhe ato që nuk i dinë!');
INSERT INTO "hasan_efendi" VALUES(36,37,'36:37','Një shenjë tjetër për ata është nata, prej së cilës Ne e tërheqim ditën dhe atëherë ata mbeten në errësirë.');
INSERT INTO "hasan_efendi" VALUES(36,38,'36:38','Dielli sillet në drejtimin e vet të caktuar. Kështu e ka paracaktuar i Plotfuqishmi, i Gjithëdijshmi.');
INSERT INTO "hasan_efendi" VALUES(36,39,'36:39','Hënës i kemi caktuar faza; ajo përditë hahet e bëhet si rremi i përkulur i hurmës.');
INSERT INTO "hasan_efendi" VALUES(36,40,'36:40','As Dielli nuk mund ta arrijë Hënën e as nata nuk mund t’ia kalojë ditës; të gjitha lundrojnë në orbitat e veta.');
INSERT INTO "hasan_efendi" VALUES(36,41,'36:41','Një shenjë tjetër për ata është që paraardhësit e tyre Ne i ngarkuam në anijen e mbushur plot (të Nuhut)');
INSERT INTO "hasan_efendi" VALUES(36,42,'36:42','dhe për ata Ne krijuam mjete të ngjashme me atë anije, në të cilat hipin.');
INSERT INTO "hasan_efendi" VALUES(36,43,'36:43','Po të duam, Ne i fundosim ata e atëherë as nuk do të mund t’i ndihmojë kush, as nuk do të mund të shpëtojnë,');
INSERT INTO "hasan_efendi" VALUES(36,44,'36:44','përveçse me mëshirën Tonë, duke i lënë ata të kënaqen njëfarë kohe.');
INSERT INTO "hasan_efendi" VALUES(36,45,'36:45','Kur atyre u thuhet: “Druajuni asaj që keni para jush dhe asaj që është pas jush, me qëllim që Allahu t’ju mëshirojë” (ata nuk ia vënë veshin).');
INSERT INTO "hasan_efendi" VALUES(36,46,'36:46','Sa herë që atyre u vjen ndonjë nga shenjat e Zotit të tyre, ata i shmangen asaj.');
INSERT INTO "hasan_efendi" VALUES(36,47,'36:47','E kur atyre u thuhet: “Jepni nga ato që ju ka dhënë Allahu”, atëherë, mohuesit u thonë besimtarëve: “Pse, ne do t’i ushqejmë ata që, po të donte Allahu, do t’i ushqente Vetë? Ju jeni vërtet në humbje të qartë!”');
INSERT INTO "hasan_efendi" VALUES(36,48,'36:48','Ata (jobesimtarët) thonë gjithashtu: “Kur do të përmbushet ky premtim, nëse ajo që thoni është e vërtetë?”');
INSERT INTO "hasan_efendi" VALUES(36,49,'36:49','Ata presin vetëm një zë të tmerrshëm, që do t’i kaplojë, ndërsa grinden me njëri-tjetrin.');
INSERT INTO "hasan_efendi" VALUES(36,50,'36:50','Atëherë ata nuk do të mund të lënë testament e as të kthehen te familjet e tyre.');
INSERT INTO "hasan_efendi" VALUES(36,51,'36:51','Dhe, kur të fryhet në Sur, ata do të dalin nga varret dhe do të nxitojnë drejt Zotit të tyre,');
INSERT INTO "hasan_efendi" VALUES(36,52,'36:52','duke thënë: “Mjerë ne! Kush na ngriti prej varreve tona?” (Do t’u thuhet): “Ja, kjo është ajo që ka premtuar i Gjithëmëshirshmi; të dërguarit kanë thënë të vërtetën!”');
INSERT INTO "hasan_efendi" VALUES(36,53,'36:53','Do të jetë ai vetëm një zë i tmerrshëm e ata të gjithë do të paraqiten para Nesh.');
INSERT INTO "hasan_efendi" VALUES(36,54,'36:54','Atë ditë askujt nuk do t’i bëhet ndonjë padrejtësi dhe ju do të shpërbleheni vetëm sipas asaj që keni bërë.');
INSERT INTO "hasan_efendi" VALUES(36,55,'36:55','Sigurisht që atë ditë banorët e Xhenetit nuk do të mendojnë për gjë tjetër, veç lumturisë së tyre.');
INSERT INTO "hasan_efendi" VALUES(36,56,'36:56','Ata dhe gratë e tyre do të jenë në hije, të mbështetur në ndenjëse të lartuara.');
INSERT INTO "hasan_efendi" VALUES(36,57,'36:57','Për ata do të ketë aty fruta dhe çdo gjë që dëshirojnë.');
INSERT INTO "hasan_efendi" VALUES(36,58,'36:58','“Paqe!” do të jetë fjala prej një Zoti Mëshirëplotë.');
INSERT INTO "hasan_efendi" VALUES(36,59,'36:59','(Ndërsa keqbërësve Ai do t’u thotë): “Veçohuni sot ju, o kriminelë!');
INSERT INTO "hasan_efendi" VALUES(36,60,'36:60','Vallë, a nuk ju kam urdhëruar, o bijtë e Ademit, që të mos e adhuroni djallin, se ai është vërtet armik i hapët juaji,');
INSERT INTO "hasan_efendi" VALUES(36,61,'36:61','por të më adhuroni vetëm Mua?! Kjo është rruga e drejtë.');
INSERT INTO "hasan_efendi" VALUES(36,62,'36:62','Sigurisht që ai (djalli) ka shpënë në humbje një mizëri prej jush; vallë a nuk po mendoni?!');
INSERT INTO "hasan_efendi" VALUES(36,63,'36:63','Ky është Xhehenemi, që ju ishte premtuar.');
INSERT INTO "hasan_efendi" VALUES(36,64,'36:64','Digjuni tani në të, sepse kurrë nuk besuat!”');
INSERT INTO "hasan_efendi" VALUES(36,65,'36:65','Sot Ne do t’ua vulosim atyre gojët e do të Na flasin duart e tyre dhe do të dëshmojnë këmbët e tyre për atë që kanë bërë.');
INSERT INTO "hasan_efendi" VALUES(36,66,'36:66','Sikur të donim, Ne do t’ua verbonim atyre sytë, kështu që ata do ta kërkonin më kot rrugën! Vallë, si do të shihnin?');
INSERT INTO "hasan_efendi" VALUES(36,67,'36:67','Sikur të kishim dashur, Ne do t’i kishim shndërruar ata (në krijesa të tjera) në atë vend e ata nuk do të mund as të shkonin e as të ktheheshin.');
INSERT INTO "hasan_efendi" VALUES(36,68,'36:68','Kujtdo, që i japim jetë të gjatë, Ne e bëjmë sërish krijesë të dobët. Vallë, a nuk kuptojnë ata?');
INSERT INTO "hasan_efendi" VALUES(36,69,'36:69','Ne nuk ia kemi mësuar atij (Muhamedit) poezinë e ajo as që është për të. Ky (libër që ka sjellë) është vetëm këshillë dhe një Kuran i qartë,');
INSERT INTO "hasan_efendi" VALUES(36,70,'36:70','për të paralajmëruar këdo që është gjallë dhe për t’u përmbushur fjala e Tij ndaj mohuesve.');
INSERT INTO "hasan_efendi" VALUES(36,71,'36:71','Vallë, a nuk e shohin ata se, ndër gjërat që Duart Tona kanë krijuar për ta, janë bagëtitë që ata zotërojnë?!');
INSERT INTO "hasan_efendi" VALUES(36,72,'36:72','Ne bëmë që ato t’u nënshtrohen atyre, kështu që disa i shalojnë e prej disave ushqehen,');
INSERT INTO "hasan_efendi" VALUES(36,73,'36:73','prej disave pinë dhe nxjerrin dobi të tjera. Vallë, si të mos falënderojnë!');
INSERT INTO "hasan_efendi" VALUES(36,74,'36:74','Por ata kanë zgjedhur zota të tjerë në vend të Allahut, duke shpresuar se do të ndihmohen prej tyre.');
INSERT INTO "hasan_efendi" VALUES(36,75,'36:75','Zotat nuk mund t’i ndihmojnë, e megjithatë idhujtarët janë si një ushtri e pranishme për (t’i mbrojtur) ata.');
INSERT INTO "hasan_efendi" VALUES(36,76,'36:76','Mos u dëshpëro nga fjalët e tyre! Ne e dimë çfarë fshehin dhe çfarë shpallin.');
INSERT INTO "hasan_efendi" VALUES(36,77,'36:77','A nuk sheh njeriu se si Ne e krijuam atë prej pikës së farës, e megjithatë ai bëhet kundërshtar i hapët?!');
INSERT INTO "hasan_efendi" VALUES(36,78,'36:78','Ai na sjell shembull, duke harruar krijimin e vet dhe thotë: “Kush do t’i ngjallë eshtrat kur të kalben?”');
INSERT INTO "hasan_efendi" VALUES(36,79,'36:79','Thuaj: “Do t’i ngjallë Ai që i krijoi ato për herë të parë; Ai ka njohuri të plotë për çdo krijesë;');
INSERT INTO "hasan_efendi" VALUES(36,80,'36:80','Ai që prej drurit të njomë bëri për ju zjarrin, të cilin e përdorni për të ndezur”.');
INSERT INTO "hasan_efendi" VALUES(36,81,'36:81','Vallë, a nuk është i Zoti Ai që krijoi qiejt dhe Tokën, t’i rikrijojë ato? Po! Ai është Krijuesi i çdo gjëje dhe i Gjithëdijshmi!');
INSERT INTO "hasan_efendi" VALUES(36,82,'36:82','Vërtet, urdhri i Tij, kur Ai dëshiron diçka, është që t’i thotë asaj “Bëhu!” - dhe ajo bëhet.');
INSERT INTO "hasan_efendi" VALUES(36,83,'36:83','Prandaj, qoftë lartësuar Ai, në Dorën e të Cilit është pushteti mbi çdo gjë! Tek Ai do të ktheheni!');
INSERT INTO "hasan_efendi" VALUES(37,1,'37:1','Betohem në ata (engjëj) që janë të radhitur');
INSERT INTO "hasan_efendi" VALUES(37,2,'37:2','dhe në ata që pengojnë (të keqen)');
INSERT INTO "hasan_efendi" VALUES(37,3,'37:3','dhe në ata që e lexojnë Kuranin,');
INSERT INTO "hasan_efendi" VALUES(37,4,'37:4','se, në të vërtetë, Zoti juaj është Një,');
INSERT INTO "hasan_efendi" VALUES(37,5,'37:5','Zot i qiejve dhe i Tokës dhe çfarë gjendet mes tyre dhe Zot i lindjeve (të diellit)!');
INSERT INTO "hasan_efendi" VALUES(37,6,'37:6','Ne e kemi stolisur qiellin e kësaj bote me shkëlqimin e yjeve,');
INSERT INTO "hasan_efendi" VALUES(37,7,'37:7','mbrojtje nga djajtë kryeneçë.');
INSERT INTO "hasan_efendi" VALUES(37,8,'37:8','(Kështu që) ata nuk e përgjojnë dot Grupin më të Lartë (engjëjt lart në qiej) dhe qëllohen (me meteorë) nga të gjitha anët.');
INSERT INTO "hasan_efendi" VALUES(37,9,'37:9','Pastaj, ashtu të përzënë do të kenë vuajtje të përhershme (në jetën tjetër).');
INSERT INTO "hasan_efendi" VALUES(37,10,'37:10','(Djajtë nuk përgjojnë dot), veç atij që grabit diçka e që e ndjek një yll verbues.');
INSERT INTO "hasan_efendi" VALUES(37,11,'37:11','Pyeti (o Muhamed) ata (mekas jobesimtarë): A është më i vështirë krijimi i tyre apo i të gjitha gjërave të tjera që i kemi krijuar? Ata i kemi krijuar nga balta ngjitëse.');
INSERT INTO "hasan_efendi" VALUES(37,12,'37:12','Ti çuditesh (pse ata s’të besojnë), ndërsa ata të përqeshin (pse çuditesh)');
INSERT INTO "hasan_efendi" VALUES(37,13,'37:13','e, kur u drejtohen këshilla, ata nuk ua vënë veshin.');
INSERT INTO "hasan_efendi" VALUES(37,14,'37:14','Kur shohin një mrekulli, ata e nxisin njëri-tjetrin për t’u tallur');
INSERT INTO "hasan_efendi" VALUES(37,15,'37:15','dhe thonë: “Kjo s’është gjë tjetër, veçse një magji e kulluar!”');
INSERT INTO "hasan_efendi" VALUES(37,16,'37:16','(Ata thonë): “Vallë, kur të vdesim dhe të bëhemi eshtra e pluhur, a thua njëmend do të ringjallemi?');
INSERT INTO "hasan_efendi" VALUES(37,17,'37:17','Edhe paraardhësit tanë të lashtë?”');
INSERT INTO "hasan_efendi" VALUES(37,18,'37:18','Thuaju: “Po dhe do të jeni të poshtëruar!”');
INSERT INTO "hasan_efendi" VALUES(37,19,'37:19','Ai (zëri i bririt të ringjalljes) do të jetë një zë i vetëm dhe të gjithë (mohuesit e ringjallur) do të shohin (se ç’do të bëhet me ta)');
INSERT INTO "hasan_efendi" VALUES(37,20,'37:20','dhe do të thonë: “Mjerë për ne, kjo është Dita e Gjykimit!”');
INSERT INTO "hasan_efendi" VALUES(37,21,'37:21','Po, kjo është Dita e Gjykimit, të cilën ju e keni përgënjeshtruar!');
INSERT INTO "hasan_efendi" VALUES(37,22,'37:22','(Engjëjve do t’u thuhet): “Mblidhini njerëzit e këqij, shokët e tyre (djaj) dhe idhujt që i kanë adhuruar');
INSERT INTO "hasan_efendi" VALUES(37,23,'37:23','krahas Allahut e tregojuni atyre rrugën e Zjarrit flakërues!');
INSERT INTO "hasan_efendi" VALUES(37,24,'37:24','Ndalini ata, se do të pyeten:');
INSERT INTO "hasan_efendi" VALUES(37,25,'37:25','“Ç’keni që nuk e ndihmoni njëri-tjetrin?”');
INSERT INTO "hasan_efendi" VALUES(37,26,'37:26','Por, atë Ditë ata do të jenë plotësisht të dorëzuar,');
INSERT INTO "hasan_efendi" VALUES(37,27,'37:27','e do t’i afrohen njëri-tjetrit, duke u grindur.');
INSERT INTO "hasan_efendi" VALUES(37,28,'37:28','(Pasuesit) do t’u thonë (prijësve): “Ju na vinit nga ana e djathtë ”.');
INSERT INTO "hasan_efendi" VALUES(37,29,'37:29','Ata do të përgjigjen: “Jo, ju vetë nuk ishit besimtarë!');
INSERT INTO "hasan_efendi" VALUES(37,30,'37:30','Ne nuk kishim kurrfarë pushteti mbi ju, por ju ishit popull i pabindur.');
INSERT INTO "hasan_efendi" VALUES(37,31,'37:31','E kështu u përmbush fjala e Zotit kundër nesh; ne të gjithë do ta shijojmë me siguri dënimin.');
INSERT INTO "hasan_efendi" VALUES(37,32,'37:32','Ne ju mashtruam ju, por edhe vetë ishim të mashtruar.”');
INSERT INTO "hasan_efendi" VALUES(37,33,'37:33','Atë Ditë, të gjithë ata do të jenë së bashku në dënim.');
INSERT INTO "hasan_efendi" VALUES(37,34,'37:34','Ne kështu veprojmë me keqbërësit,');
INSERT INTO "hasan_efendi" VALUES(37,35,'37:35','sepse, kur u thuhej atyre: “Nuk ka zot tjetër (të vërtetë) veç Allahut!” - ata tregoheshin mendjemëdhenj');
INSERT INTO "hasan_efendi" VALUES(37,36,'37:36','dhe thoshin: “Vallë, a t’i braktisim hyjnitë tona për shkak të një poeti të marrë?”');
INSERT INTO "hasan_efendi" VALUES(37,37,'37:37','Nuk është ashtu (siç thonë ata), por ai (Muhamedi a.s.) ka sjellë të vërtetën dhe ka pohuar atë që thonin të dërguarit e mëparshëm.');
INSERT INTO "hasan_efendi" VALUES(37,38,'37:38','Sigurisht, ju do ta provoni dënimin e dhembshëm');
INSERT INTO "hasan_efendi" VALUES(37,39,'37:39','dhe do të shpërbleheni vetëm sipas asaj që keni bërë!');
INSERT INTO "hasan_efendi" VALUES(37,40,'37:40','Kurse robërit e sinqertë të Allahut,');
INSERT INTO "hasan_efendi" VALUES(37,41,'37:41','do të kenë mirësi të caktuara dhe');
INSERT INTO "hasan_efendi" VALUES(37,42,'37:42','fruta të ndryshme e do të jenë të respektuar,');
INSERT INTO "hasan_efendi" VALUES(37,43,'37:43','në kopshtet e lumturisë,');
INSERT INTO "hasan_efendi" VALUES(37,44,'37:44','në kolltukë, përballë njëri-tjetrit.');
INSERT INTO "hasan_efendi" VALUES(37,45,'37:45','Aty do t’u shërbehet një gotë e mbushur nga një burim i rrjedhshëm,');
INSERT INTO "hasan_efendi" VALUES(37,46,'37:46','(me pije) të bardhë e të shijshme për ata që do ta pinë.');
INSERT INTO "hasan_efendi" VALUES(37,47,'37:47','Prej saj as nuk do të trullosen dhe as nuk do të dehen.');
INSERT INTO "hasan_efendi" VALUES(37,48,'37:48','Pranë tyre do të jenë gra me shikim të ulur e sy të bukur');
INSERT INTO "hasan_efendi" VALUES(37,49,'37:49','sikur të ishin vezë të ruajtura mirë.');
INSERT INTO "hasan_efendi" VALUES(37,50,'37:50','Ata do t’i qasen njëri-tjetrit e do të bisedojnë.');
INSERT INTO "hasan_efendi" VALUES(37,51,'37:51','Dhe njëri prej tyre do të thotë: “Unë kam pasur një shok,');
INSERT INTO "hasan_efendi" VALUES(37,52,'37:52','i cili më thoshte: “Vallë, edhe ti je nga ata që besojnë');
INSERT INTO "hasan_efendi" VALUES(37,53,'37:53','se, kur të vdesim e të bëhemi eshtra e pluhur, do të japim llogari vërtet?!”');
INSERT INTO "hasan_efendi" VALUES(37,54,'37:54','- A doni ta shikoni (atë që më qortonte për shkak të besimit tim)?”');
INSERT INTO "hasan_efendi" VALUES(37,55,'37:55','Dhe ai do të shikojë poshtë dhe do ta shohë atë në mes të Zjarrit flakërues');
INSERT INTO "hasan_efendi" VALUES(37,56,'37:56','e do t’i thotë: “Për Allahun! Për pak më shkatërrove edhe mua.');
INSERT INTO "hasan_efendi" VALUES(37,57,'37:57','Po të mos ishte dhuntia e Zotit tim, edhe unë do të isha në zjarr (bashkë me ty)”.');
INSERT INTO "hasan_efendi" VALUES(37,58,'37:58','(Banorët e Xhenetit do të thonë:) “A nuk do të vdesim më,');
INSERT INTO "hasan_efendi" VALUES(37,59,'37:59','përveç vdekjes së parë? Dhe as nuk do të dënohemi?”');
INSERT INTO "hasan_efendi" VALUES(37,60,'37:60','Vërtet, kjo është fitorja më e madhe!');
INSERT INTO "hasan_efendi" VALUES(37,61,'37:61','Për diçka të tillë, le të përpiqen punëtorët!');
INSERT INTO "hasan_efendi" VALUES(37,62,'37:62','A është më e mirë kjo gjendje apo pema e zekumit?');
INSERT INTO "hasan_efendi" VALUES(37,63,'37:63','Ne e kemi bërë atë si mundim për keqbërësit.');
INSERT INTO "hasan_efendi" VALUES(37,64,'37:64','Njëmend, kjo pemë del nga fundi i Zjarrit flakërues');
INSERT INTO "hasan_efendi" VALUES(37,65,'37:65','dhe frutat e saj janë si kokat e djajve.');
INSERT INTO "hasan_efendi" VALUES(37,66,'37:66','Me të vërtetë, ata do ta hanë këtë frutë dhe me të do ta mbushin barkun.');
INSERT INTO "hasan_efendi" VALUES(37,67,'37:67','Pastaj, ata do të kenë një përzierje me ujë të nxehtë (për të pirë)');
INSERT INTO "hasan_efendi" VALUES(37,68,'37:68','e pas kësaj, me siguri, kthimi i tyre do të jetë në Zjarrin flakërues.');
INSERT INTO "hasan_efendi" VALUES(37,69,'37:69','Ata i gjetën etërit e tyre në humbje,');
INSERT INTO "hasan_efendi" VALUES(37,70,'37:70','dhe kështu vazhduan pas gjurmëve të tyre.');
INSERT INTO "hasan_efendi" VALUES(37,71,'37:71','Por, edhe para tyre humbën (rrugën) shumica e popujve të lashtë.');
INSERT INTO "hasan_efendi" VALUES(37,72,'37:72','Në të vërtetë, Ne u dërguam atyre paralajmërues.');
INSERT INTO "hasan_efendi" VALUES(37,73,'37:73','E tani shiko se cili qe fundi i atyre që u paralajmëruan,');
INSERT INTO "hasan_efendi" VALUES(37,74,'37:74','përveç robërve të sinqertë të Allahut.');
INSERT INTO "hasan_efendi" VALUES(37,75,'37:75','Kur Nuhu Na thirri (në ndihmë), Ne iu përgjigjëm bukur:');
INSERT INTO "hasan_efendi" VALUES(37,76,'37:76','e shpëtuam atë dhe familjen e tij prej katastrofës së madhe,');
INSERT INTO "hasan_efendi" VALUES(37,77,'37:77','i lamë në jetë vetëm pasardhësit e tij');
INSERT INTO "hasan_efendi" VALUES(37,78,'37:78','dhe e lamë atë kujtim te brezat e tjerë.');
INSERT INTO "hasan_efendi" VALUES(37,79,'37:79','“Paqja qoftë me Nuhun ndërmjet gjithë krijesave!”');
INSERT INTO "hasan_efendi" VALUES(37,80,'37:80','Ne, kështu i shpërblejmë punëdrejtët.');
INSERT INTO "hasan_efendi" VALUES(37,81,'37:81','Ai është vërtet nga robërit Tanë besimtarë.');
INSERT INTO "hasan_efendi" VALUES(37,82,'37:82','Pastaj i fundosëm të tjerët.');
INSERT INTO "hasan_efendi" VALUES(37,83,'37:83','Pa dyshim që një nga ithtarët e (besimit të) tij (Nuhut) ishte Ibrahimi.');
INSERT INTO "hasan_efendi" VALUES(37,84,'37:84','Ai i erdhi Zotit të vet me zemër të pastër,');
INSERT INTO "hasan_efendi" VALUES(37,85,'37:85','kur i tha babait dhe popullit të vet: “Çfarë adhuroni ju?');
INSERT INTO "hasan_efendi" VALUES(37,86,'37:86','Vallë, a dëshironi hyjni të rreme në vend të Allahut?');
INSERT INTO "hasan_efendi" VALUES(37,87,'37:87','Çfarë mendoni ju për Zotin e gjithësisë?”');
INSERT INTO "hasan_efendi" VALUES(37,88,'37:88','Pastaj u hodhi një shikim yjeve');
INSERT INTO "hasan_efendi" VALUES(37,89,'37:89','e tha: “Unë jam i sëmurë”. ');
INSERT INTO "hasan_efendi" VALUES(37,90,'37:90','Ata u larguan prej tij, duke e lënë pas.');
INSERT INTO "hasan_efendi" VALUES(37,91,'37:91','Pastaj, ai u përvodh te hyjnitë e tyre dhe u tha: “A nuk po hani?');
INSERT INTO "hasan_efendi" VALUES(37,92,'37:92','Ç’keni, përse nuk flisni?”');
INSERT INTO "hasan_efendi" VALUES(37,93,'37:93','Iu afrua atyre kalimthi, duke i goditur me dorën e djathtë.');
INSERT INTO "hasan_efendi" VALUES(37,94,'37:94','(Mbasi u kthyen dhe panë idhujt e thyer) njerëzit nxituan nga ai.');
INSERT INTO "hasan_efendi" VALUES(37,95,'37:95','Ai u tha: “Vallë, ju adhuroni ato që i keni gdhendur vetë,');
INSERT INTO "hasan_efendi" VALUES(37,96,'37:96','ndërkohë që Allahu është Ai që ju ka krijuar ju dhe gjithçka që bëni?!”');
INSERT INTO "hasan_efendi" VALUES(37,97,'37:97','Ata thanë: “Përgatitni për të një furrë dhe hidheni në zjarrin flakërues!”');
INSERT INTO "hasan_efendi" VALUES(37,98,'37:98','Dhe deshën t’i ngrinin një kurth, por Ne i bëmë ata më të poshtëruarit.');
INSERT INTO "hasan_efendi" VALUES(37,99,'37:99','Ai tha: “Unë po shkoj te Zoti im. Ai do të më udhëzojë.”');
INSERT INTO "hasan_efendi" VALUES(37,100,'37:100','“O Zoti im, dhuromë një fëmijë prej të mirëve!”');
INSERT INTO "hasan_efendi" VALUES(37,101,'37:101','Dhe Ne i dhamë lajmin e gëzuar për një djalë të mbarë!');
INSERT INTO "hasan_efendi" VALUES(37,102,'37:102','Kur fëmija u rrit aq sa ta ndihmonte në punë, Ibrahimi i tha: “O djali im, kam parë ëndërr se duhet të të flijoj. Çfarë mendon ti?” – I biri i tha: “O ati im, vepro ashtu siç je urdhëruar! Dashtë Allahu, unë do të jem i durueshëm!”');
INSERT INTO "hasan_efendi" VALUES(37,103,'37:103','Pasi iu nënshtruan që të dy urdhrit, (Ibrahimi) e vuri (Ismailin) me ballë përtokë.');
INSERT INTO "hasan_efendi" VALUES(37,104,'37:104','Ne e thirrëm: “O Ibrahim,');
INSERT INTO "hasan_efendi" VALUES(37,105,'37:105','ti e përmbushe ëndrrën.” Vërtet, Ne kështu i shpërblejmë punëmirët!');
INSERT INTO "hasan_efendi" VALUES(37,106,'37:106','Kjo, me të vërtetë ka qenë një sprovë e qartë!');
INSERT INTO "hasan_efendi" VALUES(37,107,'37:107','Dhe Ne e zëvendësuam atë (Ismailin) me një kurban të madh,');
INSERT INTO "hasan_efendi" VALUES(37,108,'37:108','dhe i lamë atij (Ibrahimit) kujtim të mirë në brezat e mëvonshëm.');
INSERT INTO "hasan_efendi" VALUES(37,109,'37:109','“Paqja qoftë mbi Ibrahimin!”');
INSERT INTO "hasan_efendi" VALUES(37,110,'37:110','Ja, kështu, Ne i shpërblejmë punëmirët.');
INSERT INTO "hasan_efendi" VALUES(37,111,'37:111','Ai ka qenë vërtet një nga robërit Tanë besimtarë.');
INSERT INTO "hasan_efendi" VALUES(37,112,'37:112','Ne i dhamë atij lajmin e mirë për (lindjen e) Is’hakut, profet prej njerëzve të mirë.');
INSERT INTO "hasan_efendi" VALUES(37,113,'37:113','Dhe Ne e bekuam atë dhe Is’hakun, por midis pasardhësve të tyre, përveç punëmirëve, ka edhe të atillë që e kanë ngarkuar dukshëm veten me gjynahe.');
INSERT INTO "hasan_efendi" VALUES(37,114,'37:114','Ne i dhamë dhuntinë Tonë Musait dhe Harunit');
INSERT INTO "hasan_efendi" VALUES(37,115,'37:115','dhe i shpëtuam ata të dy dhe popullin e tyre nga mjerimi i madh.');
INSERT INTO "hasan_efendi" VALUES(37,116,'37:116','Ne i ndihmuam dhe ata ngadhënjyen.');
INSERT INTO "hasan_efendi" VALUES(37,117,'37:117','Ne u dhamë atyre Librin e qartë,');
INSERT INTO "hasan_efendi" VALUES(37,118,'37:118','i udhëzuam në rrugën e drejtë');
INSERT INTO "hasan_efendi" VALUES(37,119,'37:119','dhe u lamë kujtim të mirë për ta brezave të mëvonshëm.');
INSERT INTO "hasan_efendi" VALUES(37,120,'37:120','“Paqja qoftë mbi Musain dhe Harunin!”');
INSERT INTO "hasan_efendi" VALUES(37,121,'37:121','Ja, kështu, Ne i shpërblejmë punëmirët!');
INSERT INTO "hasan_efendi" VALUES(37,122,'37:122','E pa dyshim, ata të dy janë nga robërit Tanë besimtarë.');
INSERT INTO "hasan_efendi" VALUES(37,123,'37:123','Edhe Iljasi ka qenë vërtet një prej të dërguarve.');
INSERT INTO "hasan_efendi" VALUES(37,124,'37:124','Ai i tha popullit të vet: “A nuk po i frikësoheni Allahut?!”');
INSERT INTO "hasan_efendi" VALUES(37,125,'37:125','Vallë, a i luteni ju Ba‘lit , ndërsa braktisni Krijuesin më të mirë,');
INSERT INTO "hasan_efendi" VALUES(37,126,'37:126','Allahun, Zotin tuaj dhe Zotin e të parëve tuaj të lashtë?!”');
INSERT INTO "hasan_efendi" VALUES(37,127,'37:127','Ata e quajtën atë gënjeshtar dhe, për këtë shkak, do të çohen (në dënimin e Zjarrit),');
INSERT INTO "hasan_efendi" VALUES(37,128,'37:128','përveç robërve të sinqertë të Allahut.');
INSERT INTO "hasan_efendi" VALUES(37,129,'37:129','Dhe Ne i lamë atij kujtim të mirë te brezat e mëvonshëm:');
INSERT INTO "hasan_efendi" VALUES(37,130,'37:130','“Paqja qoftë mbi Iljasin!”');
INSERT INTO "hasan_efendi" VALUES(37,131,'37:131','Ja, kështu, Ne i shpërblejmë punëmirët!');
INSERT INTO "hasan_efendi" VALUES(37,132,'37:132','Ai ka qenë vërtet një nga robërit Tanë besimtarë.');
INSERT INTO "hasan_efendi" VALUES(37,133,'37:133','Sigurisht, edhe Luti ka qenë njëri prej të dërguarve.');
INSERT INTO "hasan_efendi" VALUES(37,134,'37:134','Ne e shpëtuam atë dhe tërë familjen e tij,');
INSERT INTO "hasan_efendi" VALUES(37,135,'37:135','përveç plakës që mbeti prapa me të dënuarit,');
INSERT INTO "hasan_efendi" VALUES(37,136,'37:136','pastaj i zhdukëm të tjerët.');
INSERT INTO "hasan_efendi" VALUES(37,137,'37:137','Ju që kaloni pranë rrënojave të tyre në mëngjes');
INSERT INTO "hasan_efendi" VALUES(37,138,'37:138','dhe natën, a nuk po e mblidhni mendjen?');
INSERT INTO "hasan_efendi" VALUES(37,139,'37:139','Junusi ka qenë gjithashtu njëri nga të dërguarit.');
INSERT INTO "hasan_efendi" VALUES(37,140,'37:140','Ai nxitoi drejt një anijeje të mbushur plot e përplot');
INSERT INTO "hasan_efendi" VALUES(37,141,'37:141','dhe hodhi short (me detarët se kush duhej të hidhej) dhe humbi.');
INSERT INTO "hasan_efendi" VALUES(37,142,'37:142','Pastaj atë e gëlltiti një balenë, se ai kishte bërë diçka të gabuar,');
INSERT INTO "hasan_efendi" VALUES(37,143,'37:143','dhe, po të mos ishte nga ata që i luten Allahut,');
INSERT INTO "hasan_efendi" VALUES(37,144,'37:144','me siguri do të mbetej në barkun e saj deri në Ditën e Ringjalljes.');
INSERT INTO "hasan_efendi" VALUES(37,145,'37:145','Por Ne e hodhëm atë në një breg të shkretë, duke qenë i sëmurë.');
INSERT INTO "hasan_efendi" VALUES(37,146,'37:146','Dhe bëmë që sipër tij të mbinte një bimë kungulli (për t’i bërë hije).');
INSERT INTO "hasan_efendi" VALUES(37,147,'37:147','Pastaj e dërguam atë te njëqind mijë e më tepër njerëz,');
INSERT INTO "hasan_efendi" VALUES(37,148,'37:148','dhe ata i besuan, prandaj u dhamë jetë të këndshme për një kohë.');
INSERT INTO "hasan_efendi" VALUES(37,149,'37:149','Pyeti ata (o Muhamed): “Vallë, për Zotin tënd janë vajzat, ndërsa për vete kanë djemtë?!');
INSERT INTO "hasan_efendi" VALUES(37,150,'37:150','Apo Ne i krijuam engjëjt si femra e ata ishin dëshmitarë?!”');
INSERT INTO "hasan_efendi" VALUES(37,151,'37:151','Sigurisht që ata shpifin kur thonë:');
INSERT INTO "hasan_efendi" VALUES(37,152,'37:152','“Allahu ka lindur fëmijë”! - Ata, në të vërtetë, janë gënjeshtarë.');
INSERT INTO "hasan_efendi" VALUES(37,153,'37:153','Vallë, Ai ka parapëlqyer vajzat ndaj djemve?!');
INSERT INTO "hasan_efendi" VALUES(37,154,'37:154','Çfarë keni që gjykoni kështu?!');
INSERT INTO "hasan_efendi" VALUES(37,155,'37:155','A nuk po mendoni?!');
INSERT INTO "hasan_efendi" VALUES(37,156,'37:156','Apo keni ndonjë provë të qartë për atë që thoni?!');
INSERT INTO "hasan_efendi" VALUES(37,157,'37:157','Sillni librin tuaj, nëse thoni të vërtetën!');
INSERT INTO "hasan_efendi" VALUES(37,158,'37:158','Idhujtarët kanë trilluar farefisni ndërmjet Zotit dhe xhindeve, kurse xhindet e dinë se ata do të sillen të gjithë para Tij.');
INSERT INTO "hasan_efendi" VALUES(37,159,'37:159','Lavdi Allahut, qoftë i pastër nga ajo që ata ia veshin Atij!');
INSERT INTO "hasan_efendi" VALUES(37,160,'37:160','Por robërit e sinqertë të Allahut nuk adhurojnë askënd tjetër përveç Tij.');
INSERT INTO "hasan_efendi" VALUES(37,161,'37:161','“Me të vërtetë, as ju, as idhujt tuaj,');
INSERT INTO "hasan_efendi" VALUES(37,162,'37:162','nuk mund të mashtroni askënd kundër (Atij),');
INSERT INTO "hasan_efendi" VALUES(37,163,'37:163','përveç atyre, që do të digjen në Zjarrin flakërues. ');
INSERT INTO "hasan_efendi" VALUES(37,164,'37:164','Çdonjëri prej nesh e ka vendin e caktuar (në qiell, ku adhuron Allahun).');
INSERT INTO "hasan_efendi" VALUES(37,165,'37:165','Ne jemi ata që qëndrojmë në radhë,');
INSERT INTO "hasan_efendi" VALUES(37,166,'37:166','dhe ne jemi ata që e lavdërojmë Atë!”');
INSERT INTO "hasan_efendi" VALUES(37,167,'37:167','Edhe pse ata (idhujtarët mekas) thoshin:');
INSERT INTO "hasan_efendi" VALUES(37,168,'37:168','“Sikur të kishim një Libër nga popujt e mëparshëm,');
INSERT INTO "hasan_efendi" VALUES(37,169,'37:169','ne, me siguri, do të ishim robër të sinqertë të Allahut”,');
INSERT INTO "hasan_efendi" VALUES(37,170,'37:170','ata e mohuan Kuranin, por së shpejti do ta marrin vesh!');
INSERT INTO "hasan_efendi" VALUES(37,171,'37:171','Shumë kohë më parë Ne u premtuam robërve Tanë të dërguar');
INSERT INTO "hasan_efendi" VALUES(37,172,'37:172','se ata do të ndihmohen (prej Nesh)');
INSERT INTO "hasan_efendi" VALUES(37,173,'37:173','dhe se ushtritë tona do të jenë me siguri fitimtare!');
INSERT INTO "hasan_efendi" VALUES(37,174,'37:174','Andaj, largohu prej tyre për njëfarë kohe!');
INSERT INTO "hasan_efendi" VALUES(37,175,'37:175','Vështroji ata (kur t’u zbresë dënimi), se edhe ata do ta shohin (si janë pasojat e mosbesimit të tyre)!');
INSERT INTO "hasan_efendi" VALUES(37,176,'37:176','Vallë, a mos po kërkojnë ata shpejtimin e dënimit Tonë!');
INSERT INTO "hasan_efendi" VALUES(37,177,'37:177','Kur t’u bjerë në mesin e tyre, mëngjesi i atyre që janë paralajmëruar do të jetë shumë i shëmtuar.');
INSERT INTO "hasan_efendi" VALUES(37,178,'37:178','Andaj, largohu prej tyre për njëfarë kohe!');
INSERT INTO "hasan_efendi" VALUES(37,179,'37:179','Vështroji ata, se edhe ata do ta shohin!');
INSERT INTO "hasan_efendi" VALUES(37,180,'37:180','I lartësuar qoftë Zoti yt, Zoti i Madhështisë e i Lavdisë, mbi çfarë i veshin ata!');
INSERT INTO "hasan_efendi" VALUES(37,181,'37:181','Paqja qoftë mbi të dërguarit,');
INSERT INTO "hasan_efendi" VALUES(37,182,'37:182','dhe të gjitha lavdet e falënderimet qofshin për Allahun, Zotin e botëve!');
INSERT INTO "hasan_efendi" VALUES(38,1,'38:1','Sâd! Betohem në Kuranin plot këshilla,');
INSERT INTO "hasan_efendi" VALUES(38,2,'38:2','(se ti Muhamed thua të vërtetën), por jobesimtarët janë mendjemëdhenj dhe kundërshtues.');
INSERT INTO "hasan_efendi" VALUES(38,3,'38:3','Sa e sa breza para tyre, Ne i kemi zhdukur; ata kanë thirrur për mëshirë, por ka qenë vonë!');
INSERT INTO "hasan_efendi" VALUES(38,4,'38:4','Ata (mekasit) çuditen për ardhjen e një paralajmëruesi nga gjiri i tyre dhe jobesimtarët thonë: “Ky është magjistar dhe gënjeshtar.');
INSERT INTO "hasan_efendi" VALUES(38,5,'38:5','A mos kërkon ai, që të gjithë zotat t’i bëjë një Zot të Vetëm? Vërtet që kjo është një gjë e çuditshme!”');
INSERT INTO "hasan_efendi" VALUES(38,6,'38:6','Paria e tyre largohet, duke thënë: “Mos ia vini veshin dhe qëndroni të durueshëm pranë zotave tuaj! Kjo është puna që duhet bërë vërtet.');
INSERT INTO "hasan_efendi" VALUES(38,7,'38:7','Këtë (monoteizëm) nuk e kemi dëgjuar në fenë e fundit (fenë e krishterë). Ky është vetëm një trillim i gënjeshtërt.');
INSERT INTO "hasan_efendi" VALUES(38,8,'38:8','Pse, vetëm atij i është shpallur Fjala e Allahut ndërmjet nesh?!” Po, ata dyshojnë në Këshillën Time, sepse ende nuk e kanë provuar dënimin Tim.');
INSERT INTO "hasan_efendi" VALUES(38,9,'38:9','A mos vallë, ata kanë në dorë thesaret e mëshirës së Zotit tënd, të Plotfuqishmit, Dhuruesit?!');
INSERT INTO "hasan_efendi" VALUES(38,10,'38:10','Apo i tyre është pushteti i qiejve, i Tokës dhe ç’ka midis tyre?! Atëherë le të ngjiten me litar (në qiell).');
INSERT INTO "hasan_efendi" VALUES(38,11,'38:11','Grupi i tyre (mekasve) është vetëm një prej ushtrive, të cilat do të shpartallohen.');
INSERT INTO "hasan_efendi" VALUES(38,12,'38:12','Edhe para tyre ka përgënjeshtruar populli i Nuhut, fisi Ad dhe Faraoni - njeriu i hunjve (ku torturonte njerëzit),');
INSERT INTO "hasan_efendi" VALUES(38,13,'38:13','por edhe fisi Themud, edhe populli i Lutit, edhe banorët e Ejkës; ata ishin vërtet ushtri të fuqishme.');
INSERT INTO "hasan_efendi" VALUES(38,14,'38:14','Të gjithë ata i quajtën të dërguarit gënjeshtarë, andaj e merituan dënimin Tim.');
INSERT INTO "hasan_efendi" VALUES(38,15,'38:15','Kurse këta (mekasit) presin vetëm një britmë (fryrjen e parë të bririt) që nuk do të pushojë .');
INSERT INTO "hasan_efendi" VALUES(38,16,'38:16','Ata thonë: “O Zoti ynë, shpejtona pjesën tonë (të dënimit) para Ditës së Llogarisë!”');
INSERT INTO "hasan_efendi" VALUES(38,17,'38:17','Ti duroje atë që thonë ata dhe kujtoje robin Tonë të fuqishëm, Davudin, që përherë i drejtohej Zotit.');
INSERT INTO "hasan_efendi" VALUES(38,18,'38:18','Ne ia nënshtruam malet që, bashkë me të, t’i luteshin Allahut, në mbrëmje dhe në mëngjes,');
INSERT INTO "hasan_efendi" VALUES(38,19,'38:19','edhe shpendët e tubuar. Të gjithë i drejtoheshin Atij.');
INSERT INTO "hasan_efendi" VALUES(38,20,'38:20','Ne ia forcuam mbretërinë dhe i dhamë mençuri e dijeni për të gjykuar.');
INSERT INTO "hasan_efendi" VALUES(38,21,'38:21','A e ke dëgjuar historinë e dy kundërshtarëve, që hipën mbi murin e faltores së tij?');
INSERT INTO "hasan_efendi" VALUES(38,22,'38:22','Kur hynë te Davudi, ai u frikësua prej tyre. Ata thanë: “Mos u frikëso! Ne jemi dy kundërshtarë, një nga të cilët i ka bërë padrejtësi tjetrit, prandaj gjyko me drejtësi dhe mos u shmang (prej së vërtetës), por na udhëzo në rrugë të drejtë”.');
INSERT INTO "hasan_efendi" VALUES(38,23,'38:23','(Njëri prej tyre tha:) “Ky është vëllai im. Ai ka nëntëdhjetë e nëntë dele, kurse unë kam vetëm një dele, andaj më tha: “Ma jep mua atë!” - dhe më mundi me fjalë.”');
INSERT INTO "hasan_efendi" VALUES(38,24,'38:24','Davudi tha: “Vërtet, ai të ka bërë padrejtësi që të ka kërkuar delen tënde të vetme, për ta bashkuar me delet e veta. Shumë bashkëpronarë i bëjnë padrejtësi njëri-tjetrit, përveç atyre që besojnë dhe bëjnë punë të mira, por ata janë pak”. Davudi e kuptoi, se në të vërtetë Ne e kishim vënë në provë atë, prandaj kërkoi falje nga Zoti i vet, ra në gjunjë dhe u përul, duke u penduar.');
INSERT INTO "hasan_efendi" VALUES(38,25,'38:25','Pastaj, Ne ia falëm këtë e ai te Ne do të jetë i afërt dhe do të ketë strehim të mirë (në jetën tjetër).');
INSERT INTO "hasan_efendi" VALUES(38,26,'38:26','“O Davud, Ne të kemi bërë mëkëmbës në Tokë, prandaj gjyko ndërmjet njerëzve me drejtësi dhe mos shko pas dëshirave dhe tekave, se ato të largojnë nga rruga e Allahut! Me të vërtetë, për ata që largohen nga rruga e Allahut, ka dënim të ashpër, ngaqë e kanë harruar Ditën e Llogarisë.”');
INSERT INTO "hasan_efendi" VALUES(38,27,'38:27','Ne nuk e kemi krijuar kot qiellin, Tokën dhe çfarë ndodhet midis tyre. Kjo është hamendja e jobesimtarëve; prandaj mjerë jobesimtarët kur të hidhen në zjarr!');
INSERT INTO "hasan_efendi" VALUES(38,28,'38:28','A t’i trajtojmë njëlloj ata që besojnë e bëjnë vepra të mira dhe turbulluesit në Tokë?! A t’i trajtojmë njëlloj njerëzit e devotshëm dhe njerëzit e mbrapshtë?!');
INSERT INTO "hasan_efendi" VALUES(38,29,'38:29','Ky (Kuran) është një libër i bekuar, që Ne ta kemi zbritur ty (Muhamed), për të përsiatur mbi vargjet e tij dhe për t’u këshilluar me të mendarët.');
INSERT INTO "hasan_efendi" VALUES(38,30,'38:30','Ne i falëm Davudit Sulejmanin, një rob të mrekullueshëm. Ai i drejtohej gjithnjë me pendesë Zotit!');
INSERT INTO "hasan_efendi" VALUES(38,31,'38:31','Kur një pasdite iu paraqitën kuajt këmbëshpejtë,');
INSERT INTO "hasan_efendi" VALUES(38,32,'38:32','ai tha: “Unë shfaqa dashuri më të madhe për pasurinë (kuajt), sesa për lutjen ndaj Zotit tim, derisa dielli humbi mbas malit.');
INSERT INTO "hasan_efendi" VALUES(38,33,'38:33','M’i ktheni ata (kuajt)” - e pastaj nisi t’i presë me shpatë këmbët dhe qafat e tyre.');
INSERT INTO "hasan_efendi" VALUES(38,34,'38:34','Ne e vumë në provë Sulejmanin dhe vendosëm në fronin e tij një trup, por ai iu drejtua Zotit');
INSERT INTO "hasan_efendi" VALUES(38,35,'38:35','dhe tha: “O Zoti im, më fal dhe më dhuro një pushtet që të mos e ketë askush pas meje! Me të vërtetë, Ti je Dhurues i madh!”');
INSERT INTO "hasan_efendi" VALUES(38,36,'38:36','Pastaj, Ne ia nënshtruam atij erën që të frynte me urdhrin e tij, andej nga të donte ai.');
INSERT INTO "hasan_efendi" VALUES(38,37,'38:37','(Gjithashtu ia nënshtruam) edhe djajtë, ndërmjet të cilëve kishte ndërtues dhe zhytës në ujë,');
INSERT INTO "hasan_efendi" VALUES(38,38,'38:38','si dhe të tjerë të lidhur në pranga.');
INSERT INTO "hasan_efendi" VALUES(38,39,'38:39','(Ne i thamë): “Kjo është dhurata Jonë për ty, prandaj jep lirisht ose mos jep gjë prej saj, sepse nuk do të kesh kurrfarë llogarie për këtë!');
INSERT INTO "hasan_efendi" VALUES(38,40,'38:40','Me të vërtetë, ai te Ne do të ketë vend të afërt dhe strehim të mirë (në jetën tjetër).');
INSERT INTO "hasan_efendi" VALUES(38,41,'38:41','Kujtoje robin Tonë Ejubin, kur iu lut Zotit të vet: “Me të vërtetë, më ka goditur djalli me mundim dhe vuajtje!”');
INSERT INTO "hasan_efendi" VALUES(38,42,'38:42','(Ne iu përgjigjëm): “Bjeri me këmbë tokës dhe aty do të dalë një burim me ujë të ftohtë për larje dhe pirje!”');
INSERT INTO "hasan_efendi" VALUES(38,43,'38:43','Ne ia kthyem atij familjen dhe dy herë më tepër, si mëshirë prej Nesh dhe këshillim për mendarët.');
INSERT INTO "hasan_efendi" VALUES(38,44,'38:44','(Ne i thamë atij): “Merr me dorën tënde një tufë thuprash e bjeri lehtë me to (gruas tënde) dhe mos e shkel betimin tënd! ” Ne e gjetëm atë të durueshëm, rob të mrekullueshëm. Ai kthehej vazhdimisht me pendesë tek Zoti!');
INSERT INTO "hasan_efendi" VALUES(38,45,'38:45','Kujto robërit Tanë: Ibrahimin, Is’hakun dhe Jakubin, të fortë (në besim) dhe largpamës.');
INSERT INTO "hasan_efendi" VALUES(38,46,'38:46','Ne u dhuruam atyre një virtyt të veçantë, që ta kenë gjithmonë në mend botën e ardhshme.');
INSERT INTO "hasan_efendi" VALUES(38,47,'38:47','Njëmend, ata janë tek Ne, nga njerëzit më të mirë e më të zgjedhur.');
INSERT INTO "hasan_efendi" VALUES(38,48,'38:48','Kujtoje edhe Ismailin, Eljesain dhe Dhulkiflin. Të gjithë ata kanë qenë nga besimtarët e zgjedhur e të mirë.');
INSERT INTO "hasan_efendi" VALUES(38,49,'38:49','Ky është një përkujtim. Vërtet, për njerëzit që ruhen nga gjynahet, ka kthim të mirë (në botën tjetër):');
INSERT INTO "hasan_efendi" VALUES(38,50,'38:50','në kopshtet e Adnit, dyert e të cilit janë të hapura për ta.');
INSERT INTO "hasan_efendi" VALUES(38,51,'38:51','Aty do të prehen e do të kënaqen me pemë dhe pije të llojllojshme.');
INSERT INTO "hasan_efendi" VALUES(38,52,'38:52','Pranë tyre do të ketë hyri të dëlira e moshatare që nuk shikojnë anash.');
INSERT INTO "hasan_efendi" VALUES(38,53,'38:53','Kjo ju është premtuar për Ditën e Llogarisë.');
INSERT INTO "hasan_efendi" VALUES(38,54,'38:54','Këto janë, njëmend, begatitë Tona që nuk kanë të sosur!');
INSERT INTO "hasan_efendi" VALUES(38,55,'38:55','Kjo është kështu (për besimtarët). Kurse për njerëzit e pabindur, do të ketë vërtet një kthim të keq');
INSERT INTO "hasan_efendi" VALUES(38,56,'38:56','në Xhehenem, ku do të digjen. Eh, sa vendqëndrim i shëmtuar që është ai!');
INSERT INTO "hasan_efendi" VALUES(38,57,'38:57','Kjo i pret ata! Le ta shijojnë ujin e valuar dhe të qelbët');
INSERT INTO "hasan_efendi" VALUES(38,58,'38:58','dhe të tjera mundime të llojllojshme, të ngjashme me to.');
INSERT INTO "hasan_efendi" VALUES(38,59,'38:59','Ne do t’u themi prijësve të jobesimtarëve: “Ky është një grumbull bashkë me ju. Nuk ka mirëseardhje për ta. Me siguri që ata do të digjen në zjarr!”');
INSERT INTO "hasan_efendi" VALUES(38,60,'38:60','Pasuesit jobesimtarë do t’u thonë prijësve të tyre: “Ah, ju mos paçi çlirim e as qetësi! Ju na keni sjellë në këtë gjendje! Eh, sa strehim i keq është ky!”');
INSERT INTO "hasan_efendi" VALUES(38,61,'38:61','Pastaj ata do të thonë: “O Zoti ynë! Atyre që na e përgatitën këtë neve, dyfishojua dënimin në Zjarr!”');
INSERT INTO "hasan_efendi" VALUES(38,62,'38:62','Do të thonë: “Pse nuk po i shohim njerëzit, që ne i quanim të këqij,');
INSERT INTO "hasan_efendi" VALUES(38,63,'38:63','dhe i përqeshnim apo na kanë humbur sysh?”');
INSERT INTO "hasan_efendi" VALUES(38,64,'38:64','Kjo do të ndodhë vërtet. Banorët e zjarrit (të Xhehenemit) do të grinden ndërmjet tyre.');
INSERT INTO "hasan_efendi" VALUES(38,65,'38:65','Thuaj (o Muhamed): “Në të vërtetë, unë jam vetëm paralajmërues. Nuk ka zot tjetër të vërtetë përveç Allahut, të Vetmit, Ngadhënjimtarit,');
INSERT INTO "hasan_efendi" VALUES(38,66,'38:66','Zotit të qiejve e të Tokës dhe të gjithçkaje që gjendet midis tyre, të Plotfuqishmit, Falësit të Madh”.');
INSERT INTO "hasan_efendi" VALUES(38,67,'38:67','Thuaj: “Ky (Kuran) është një mesazh i madh,');
INSERT INTO "hasan_efendi" VALUES(38,68,'38:68','e megjithatë ju nuk po ia vini veshin atij.');
INSERT INTO "hasan_efendi" VALUES(38,69,'38:69','Unë nuk kam pasur asnjë dijeni për Grupin më të Lartë, kur diskutonin mes tyre (për krijimin e Ademit).');
INSERT INTO "hasan_efendi" VALUES(38,70,'38:70','Mua më është shpallur se jam vetëm paralajmërues i qartë.”');
INSERT INTO "hasan_efendi" VALUES(38,71,'38:71','Kur Zoti yt u tha engjëjve: “Unë do të krijoj një njeri prej balte.');
INSERT INTO "hasan_efendi" VALUES(38,72,'38:72','Kur t’i jap trajtë dhe t’i fryj prej shpirtit Tim, përuljuni në sexhde atij!”,');
INSERT INTO "hasan_efendi" VALUES(38,73,'38:73','të gjithë engjëjt së bashku u përulën në sexhde,');
INSERT INTO "hasan_efendi" VALUES(38,74,'38:74','përveç Iblisit; ai u bë mendjemadh e një nga mohuesit.');
INSERT INTO "hasan_efendi" VALUES(38,75,'38:75','Zoti tha: “O Iblis, ç’të pengoi ty që të mos përuleshe në sexhde para atij që e kam krijuar Vetë me duart e Mia? A mos u bëre mendjemadh apo mendon se je nga të lartët?”');
INSERT INTO "hasan_efendi" VALUES(38,76,'38:76','Iblisi tha: “Unë jam më i mirë se ai. Mua më ke krijuar prej zjarrit, kurse atë prej balte”.');
INSERT INTO "hasan_efendi" VALUES(38,77,'38:77','Allahu tha: “Atëherë dil prej Xhenetit! Me të vërtetë, ti je i mallkuar');
INSERT INTO "hasan_efendi" VALUES(38,78,'38:78','dhe mallkimi Im do të jetë mbi ty deri në Ditën e Gjykimit”.');
INSERT INTO "hasan_efendi" VALUES(38,79,'38:79','Ai tha: “O Zoti im, më jep afat deri në ditën kur do të ringjallen ata!”');
INSERT INTO "hasan_efendi" VALUES(38,80,'38:80','Zoti tha: “Në të vërtetë, ty do të të jepet afat');
INSERT INTO "hasan_efendi" VALUES(38,81,'38:81','deri në një ditë të caktuar (kur i fryhet Surit për herë të parë)”.');
INSERT INTO "hasan_efendi" VALUES(38,82,'38:82','Ai tha: “Betohem në madhërinë Tënde se unë do t’i mashtroj ata të gjithë,');
INSERT INTO "hasan_efendi" VALUES(38,83,'38:83','përveç robërve të Tu të sinqertë!”');
INSERT INTO "hasan_efendi" VALUES(38,84,'38:84','Zoti tha: “E vërteta është - dhe Unë them vetëm të vërtetën');
INSERT INTO "hasan_efendi" VALUES(38,85,'38:85','se, do ta mbush Xhehenemin me ty dhe me të gjithë ata që të pasojnë ty”!');
INSERT INTO "hasan_efendi" VALUES(38,86,'38:86','Thuaj (o Muhamed): “Unë prej jush nuk kërkoj kurrfarë shpërblimi për këtë (Kuran) e as pretendoj të jem diçka që s’jam.');
INSERT INTO "hasan_efendi" VALUES(38,87,'38:87','Në të vërtetë, ky Kuran është këshillë për tërë botën!');
INSERT INTO "hasan_efendi" VALUES(38,88,'38:88','Dhe ju me siguri që do ta merrni vesh të vërtetën shumë shpejt!”');
INSERT INTO "hasan_efendi" VALUES(39,1,'39:1','Ky Libër është shpallur nga Allahu i Plotfuqishëm dhe i Gjithëdijshëm!');
INSERT INTO "hasan_efendi" VALUES(39,2,'39:2','Njëmend, Ne ta kemi zbritur ty Librin me të vërtetën. Andaj adhuro vetëm Allahun me përkushtim të çiltër për Të!');
INSERT INTO "hasan_efendi" VALUES(39,3,'39:3','Vetëm Allahut i takon adhurimi i vërtetë. Sa për ata që marrin mbrojtës të tjerë, përveç Tij, duke thënë: “Ne u lutemi atyre vetëm që të na afrojnë tek Allahu”, Allahu me siguri që do t’i gjykojë për mospajtimet që kanë pasur. Vërtet, Allahu nuk e udhëzon atë që është gënjeshtar e mohues.');
INSERT INTO "hasan_efendi" VALUES(39,4,'39:4','Sikur të donte Allahu të merrte një bir, Ai do të zgjidhte kë të pëlqente prej krijesave të Veta. Qoftë i lartësuar Ai nga kjo gjë! Ai është Allahu, i Vetmi dhe Ngadhënjimtari!');
INSERT INTO "hasan_efendi" VALUES(39,5,'39:5','Ai i ka krijuar qiejt dhe Tokën për të shpallur të Vërtetën. Ai e bën natën të kalojë në ditë dhe ditën në natë, Ai e ka nënshtruar Diellin dhe Hënën. Të gjitha lëvizin për një kohë të caktuar. Vërtet, Ai është i Plotfuqishëm dhe Falës i Madh!');
INSERT INTO "hasan_efendi" VALUES(39,6,'39:6','Ai ju krijoi prej një njeriu të vetëm; pastaj, prej tij krijoi shoqen e tij. Ai ka krijuar për ju tetë çifte bagëtish . Ai ju krijon në barqet e nënave tuaja, fazë pas faze në një errësirë të trefishtë. Ky është Allahu - Zoti juaj; Atij i përket i tërë pushteti; nuk ka zot tjetër të vërtetë përveç Tij. Atëherë, si është e mundur që largoheni prej Tij?!');
INSERT INTO "hasan_efendi" VALUES(39,7,'39:7','Nëse ju e mohoni Atë, dijeni se Allahu është vërtet i pavarur prej jush. Megjithatë, Ai nuk është i kënaqur me mosmirënjohjen e robërve të Tij, por kënaqet me ju, nëse e falënderoni. Asnjë gjynahqar nuk do t’i mbartë gjynahet e tjetrit. Pastaj, do të ktheheni te Zoti juaj e Ai do t’ju njoftojë për atë që keni bërë, sepse Ai e di vërtet se ç’keni në zemrat tuaja.');
INSERT INTO "hasan_efendi" VALUES(39,8,'39:8','Kur njeriun e godet një e keqe, ai i lutet Zotit të vet, duke iu drejtuar me pendesë. E kur atij i vjen një e mirë prej Zotit, ai harron se për çfarë ishte duke u lutur më parë dhe sajon zota të tjerë të barabartë me Allahun, për t’i larguar të tjerët nga rruga e Tij. Thuaj (o Muhamed) : “Kënaqu pak me mosbesimin tënd! Ti, me siguri, do të jesh nga banorët e zjarrit!”');
INSERT INTO "hasan_efendi" VALUES(39,9,'39:9','A mund të krahasohet ai që falet natën, duke bërë sexhde dhe duke qëndruar në këmbë e që ruhet prej jetës tjetër dhe shpreson në mëshirën e Zotit të tij (me jobesimtarin)?! Thuaj: “A janë të barabartë ata që dinë dhe ata që nuk dinë?! Vetëm mendarët i pranojnë këshillat!”');
INSERT INTO "hasan_efendi" VALUES(39,10,'39:10','Thuaj: “O robër besimtarë (të Allahut), frikësojuni Zotit tuaj! Ata që bëjnë mirë në këtë jetë, do të kenë të mira. Toka e Allahut është e gjerë. Vetëm ata që janë të durueshëm do të shpërblehen pa masë”.');
INSERT INTO "hasan_efendi" VALUES(39,11,'39:11','Thuaj: “Unë jam urdhëruar që ta adhuroj Allahun me përkushtim të sinqertë.');
INSERT INTO "hasan_efendi" VALUES(39,12,'39:12','Unë jam urdhëruar të jem i pari nga ata që i nënshtrohen Atij.”');
INSERT INTO "hasan_efendi" VALUES(39,13,'39:13','Thuaj: “Unë i trembem vërtet dënimit të Ditës së madhe, nëse nuk i bindem Zotit tim”.');
INSERT INTO "hasan_efendi" VALUES(39,14,'39:14','Thuaj: “Vetëm Allahun e adhuroj me besimin tim të plotë e të sinqertë,');
INSERT INTO "hasan_efendi" VALUES(39,15,'39:15','kurse ju adhuroni kë të doni në vend të Tij!” Thuaj: “Të humburit e vërtetë janë ata që do të humbin veten dhe familjen e tyre në Ditën e Kiametit. Ja, kjo është humbja e qartë”!');
INSERT INTO "hasan_efendi" VALUES(39,16,'39:16','Ata do të mbulohen me shtresa zjarri nga sipër dhe nga poshtë. Me këtë, Allahu i tremb robërit e Vet. “O robërit e Mi, më kini frikë Mua!”');
INSERT INTO "hasan_efendi" VALUES(39,17,'39:17','Le të gëzohen ata që shmangen nga adhurimi i idhujve e kthehen nga Allahu! Pra, jepu lajme të gëzueshme robërve të Mi,');
INSERT INTO "hasan_efendi" VALUES(39,18,'39:18','të cilët, pasi i dëgjojnë fjalët (që u thuhen), ndjekin atë që është më e mira prej tyre. Këta janë ata që Allahu i ka udhëzuar në rrugë të drejtë; këta janë mendarët.');
INSERT INTO "hasan_efendi" VALUES(39,19,'39:19','Vallë, a mund ta udhëzosh ti (o Muhamed) atë që meriton të dënohet dhe a mund ta shpëtosh atë që gjendet në Zjarr?!');
INSERT INTO "hasan_efendi" VALUES(39,20,'39:20','Ndërkaq, ata që i frikësohen Zotit, do të kenë dhoma të ngritura mbi njëra-tjetrën, pranë të cilave rrjedhin lumenj. Ky është premtimi i Allahut; Allahu nuk e shkel premtimin e Vet.');
INSERT INTO "hasan_efendi" VALUES(39,21,'39:21','A nuk e sheh se si Allahu e zbret ujin nga qielli dhe e bën atë të burojë nga toka? Nëpërmjet tij Ai nxjerr bimë të llojllojshme, ngjyrash të ndryshme, të cilat, më pas thahen e zverdhen dhe pastaj Ai i thërrmon. Me të vërtetë, në të gjitha këto ka këshilla për ata që janë të mençur.');
INSERT INTO "hasan_efendi" VALUES(39,22,'39:22','A është njëlloj si ai që Allahu ia ka hapur kraharorin për të pranuar Islamin, duke qenë kështu në dritën e Zotit të vet, (ashtu edhe ai që e ka zemrën të mbyllur ndaj besimit?!) Prandaj, mjerë ata që e kanë zemrën të pandjeshme, kur përmendet Allahu! Njerëz të tillë janë në humbje të qartë!');
INSERT INTO "hasan_efendi" VALUES(39,23,'39:23','Allahu ka shpallur Fjalën më të bukur (Kuranin) në formën e një libri, pjesët e të cilit i ngjasojnë njëra-tjetrës dhe përsëriten. Prej tij u rrëqethet lëkura atyre që i frikësohen Zotit të tyre e, mandej qetësohen lëkura dhe zemrat e tyre, kur përmendet Allahu. Ky Libër është udhëzim i Allahut. Nëpërmjet tij Allahu udhëzon kë të dojë. Ndërsa atë që Allahu e shpie në humbje, s’mund ta udhëzojë kush.');
INSERT INTO "hasan_efendi" VALUES(39,24,'39:24','A është njësoj si ai që do ta vuajë me fytyrën e vet dënimin e rëndë në Ditën e Kiametit, (ashtu edhe ai që është i shpëtuar)?! Keqbërësve do t’u thuhet: “Shijoni atë që keni fituar!”');
INSERT INTO "hasan_efendi" VALUES(39,25,'39:25','Edhe ata që ishin para tyre i quajtën gënjeshtarë (të dërguarit), prandaj atyre u erdhi dënimi prej nga nuk e prisnin.');
INSERT INTO "hasan_efendi" VALUES(39,26,'39:26','Allahu i poshtëroi ata që në këtë jetë, por dënimi i jetës tjetër do të jetë më i madh! Veç sikur ta dinin!');
INSERT INTO "hasan_efendi" VALUES(39,27,'39:27','Vërtet, Ne u kemi sjellë njerëzve shembuj në këtë Kuran për çdo gjë, për t’ua vënë veshin.');
INSERT INTO "hasan_efendi" VALUES(39,28,'39:28','Ne e kemi shpallur Kuranin në gjuhën arabe dhe pa asnjë kundërthënie, me qëllim që ata ta ruajnë veten nga e keqja.');
INSERT INTO "hasan_efendi" VALUES(39,29,'39:29','Dëgjojeni këtë shembull që ka sjellë Allahu! Janë dy njerëz: njëri ka shumë bashkëpronarë që e kundërshtojnë njëri-tjetrin, ndërsa tjetri ka vetëm një pronar, të cilit i përkushtohet. A janë të njëjtë ata në krahasim?! Lavdëruar qoftë Allahu, por shumica e tyre nuk e dinë këtë!');
INSERT INTO "hasan_efendi" VALUES(39,30,'39:30','Sigurisht që ti (o Muhamed) do të vdesësh një ditë e, sigurisht që edhe ata do të vdesin.');
INSERT INTO "hasan_efendi" VALUES(39,31,'39:31','Pastaj, në Ditën e Gjykimit, ju do të grindeni para Zotit tuaj!');
INSERT INTO "hasan_efendi" VALUES(39,32,'39:32','Kush është më i keq se ai që trillon gënjeshtra për Allahun dhe hedh poshtë të Vërtetën (Kuranin), kur ajo i vjen atij?! A nuk është Xhehenemi strehë për mohuesit?!');
INSERT INTO "hasan_efendi" VALUES(39,33,'39:33','Ata, që e shpallin të Vërtetën dhe që i besojnë asaj, pikërisht ata janë të përkushtuarit që i frikësohen Zotit.');
INSERT INTO "hasan_efendi" VALUES(39,34,'39:34','Ata do të kenë çfarë të duan te Zoti i tyre. Ky është shpërblimi për punëmirët.');
INSERT INTO "hasan_efendi" VALUES(39,35,'39:35','Allahu do t’ua falë atyre veprat më të këqija që kanë bërë dhe do t’i shpërblejë sipas veprave më të mira që kanë bërë.');
INSERT INTO "hasan_efendi" VALUES(39,36,'39:36','A nuk i mjafton Allahu robit të Vet?! Ata të frikësojnë ty (o Muhamed) me zota të tjerë, përveç Tij. Atë që Allahu e shpie në humbje, nuk mund ta udhëzojë kush.');
INSERT INTO "hasan_efendi" VALUES(39,37,'39:37','Ndërsa atë që e udhëzon Allahu, askush nuk mund ta shpjerë në humbje. A nuk është Allahu i Plotfuqishëm dhe i Zoti për t’u hakmarrë?!');
INSERT INTO "hasan_efendi" VALUES(39,38,'39:38','Nëse ti (o Muhamed) i pyet ata (mekasit paganë) se kush i ka krijuar qiejt dhe Tokën, me siguri që do të të thonë: “Allahu”. Thuaju: “Si mendoni, nëse Allahu dëshiron të më shkaktojë ndonjë dëm, a mund ta largojnë atë idhujt që ju i adhuroni krahas Tij?! Po nëse Ai dëshiron të më mëshirojë, a mund ta pengojnë ata mëshirën e Tij?!” Thuaju: “Mua më mjafton Allahu. Tek Ai le të mbështeten ata që kërkojnë mbështetje”.');
INSERT INTO "hasan_efendi" VALUES(39,39,'39:39','Thuaj: “O populli im! Veproni sipas mënyrës suaj, edhe unë do të veproj sipas simes! Shumë shpejt do ta merrni vesh,');
INSERT INTO "hasan_efendi" VALUES(39,40,'39:40','se kush do të marrë një dënim poshtërues dhe ndëshkim të përhershëm!”');
INSERT INTO "hasan_efendi" VALUES(39,41,'39:41','Ne ta kemi zbritur ty Librin me të vërtetën për të udhëzuar njerëzit. Kush shkon në rrugë të drejtë, ai e ka për të mirën e vet, ndërsa kush e humb këtë rrugë, ai humb në dëm të vet. Ti (o Muhamed) nuk je përgjegjës për ata.');
INSERT INTO "hasan_efendi" VALUES(39,42,'39:42','Allahu ua merr shpirtrat atyre që u ka ardhur çasti i vdekjes dhe atyre që nuk kanë vdekur, por që janë në gjumë. Pastaj Ai ndal shpirtin e atij që i është caktuar vdekja, ndërsa të tjerëve ua lë deri në një kohë të caktuar. Me të vërtetë, këto janë shenja për njerëzit që mendojnë.');
INSERT INTO "hasan_efendi" VALUES(39,43,'39:43','A zgjedhin ata, përveç Allahut, ndërmjetësues të tjerë (që gjoja do t’i afrojnë pranë Allahut)?! Thuaj: “Vallë, a i merrni edhe pse nuk kanë asgjë në dorë dhe nuk kuptojnë?!”');
INSERT INTO "hasan_efendi" VALUES(39,44,'39:44','Thuaju: “Ndërmjetësimi është i gjithi në duart e Allahut; Atij i takon pushteti i qiejve dhe i Tokës e pastaj tek Ai do të ktheheni”.');
INSERT INTO "hasan_efendi" VALUES(39,45,'39:45','Kur përmendet vetëm Allahu, zemrat e atyre që nuk besojnë në jetën tjetër neveriten, ndërsa, kur përmenden të tjerët, përveç Allahut, ata menjëherë gëzohen.');
INSERT INTO "hasan_efendi" VALUES(39,46,'39:46','Thuaj: “O Zoti im, Krijues i qiejve dhe i Tokës, Njohës i së dukshmes e i të padukshmes, Ti do të gjykosh mes robërve të Tu për kundërshtitë që kanë pasur”!');
INSERT INTO "hasan_efendi" VALUES(39,47,'39:47','Sikur keqbërësit të kishin çdo gjë që gjendet në Tokë, dhe po kaq më tepër, të gjitha këto do t’i flijonin (për të shpëtuar) prej dënimit të ashpër në Ditën e Kiametit. Atëherë, do të përballen me atë që nuk e kanë llogaritur,');
INSERT INTO "hasan_efendi" VALUES(39,48,'39:48','do t’u shfaqen atyre të këqijat që kanë bërë dhe do t’i kaplojë ajo të cilën e përqeshnin.');
INSERT INTO "hasan_efendi" VALUES(39,49,'39:49','Kur njeriut i bie ndonjë e keqe, ai na lutet Neve; por, kur pas kësaj, i ofrojmë një të mirë prej Nesh, ai thotë: “Kjo më është dhënë vetëm pse kam ditur”. Nuk është kështu! Kjo është vetëm një sprovë, por shumica e tyre nuk e dinë.');
INSERT INTO "hasan_efendi" VALUES(39,50,'39:50','Kështu thoshin edhe ata që ishin para tyre, prandaj nuk patën dobi nga ajo që patën fituar.');
INSERT INTO "hasan_efendi" VALUES(39,51,'39:51','Prandaj ata i goditi dënimi i të këqijave që kishin punuar. Por, edhe keqbërësve prej këtyre do t’u bjerë dënimi për të këqijat, që kanë bërë e nuk do të mund t’i ikin atij.');
INSERT INTO "hasan_efendi" VALUES(39,52,'39:52','A nuk e dinë ata se Allahu ia shton apo ia pakëson riskun kujt të dojë?! Vërtet, në këtë ka shenja për njerëzit që besojnë.');
INSERT INTO "hasan_efendi" VALUES(39,53,'39:53','Thuaj: “O robërit e Mi, që i keni bërë keq vetes me gjynahe, mos e humbni shpresën në mëshirën e Allahut! Allahu, me siguri, i fal të gjitha gjynahet. Vërtet, Ai është Falësi i madh dhe Mëshirëploti.');
INSERT INTO "hasan_efendi" VALUES(39,54,'39:54','Kthehuni tek Zoti juaj dhe nënshtrojuni Atij, para se t’ju vijë dënimi, sepse atëherë nuk do të mund t’ju ndihmojë kush.');
INSERT INTO "hasan_efendi" VALUES(39,55,'39:55','Ndiqni më të mirën e asaj që ju është shpallur nga Zoti juaj, para se t’ju vijë dënimi papritmas, pa e ndier atë,');
INSERT INTO "hasan_efendi" VALUES(39,56,'39:56','që të mos thotë njeriu: “Mjerë për mua që nuk i kam kryer detyrimet ndaj Allahut, madje, edhe jam tallur!”');
INSERT INTO "hasan_efendi" VALUES(39,57,'39:57','Ose të thotë: “Sikur Allahu të më kishte udhëzuar, do të isha bërë njeri i devotshëm”.');
INSERT INTO "hasan_efendi" VALUES(39,58,'39:58','Ose të thotë, kur ta shohë dënimin: “Ah, sikur të ishte e mundur për t’u kthyer edhe një herë e të bëhesha njeri i mirë”!');
INSERT INTO "hasan_efendi" VALUES(39,59,'39:59','(Allahu do t’u thotë): “Me të vërtetë, juve ju erdhi shpallja Ime (Kurani), por nuk besuat dhe u bëtë mendjemëdhenj e jobesimtarë”.');
INSERT INTO "hasan_efendi" VALUES(39,60,'39:60','Në Ditën e Kiametit do t’i shihni ata që kanë trilluar gënjeshtra për Allahun, me fytyra të nxira. Vallë, a nuk është Xhehenemi vend për ata që janë mendjemëdhenj?!');
INSERT INTO "hasan_efendi" VALUES(39,61,'39:61','Por Allahu do t’i shpëtojë ata që janë ruajtur me të mirat e bëra, prandaj ata nuk do t’i prekë e keqja e as nuk do të pikëllohen.');
INSERT INTO "hasan_efendi" VALUES(39,62,'39:62','Allahu është Krijuesi i çdo gjëje dhe Ai është Mbikëqyrës i çdo gjëje.');
INSERT INTO "hasan_efendi" VALUES(39,63,'39:63','Të Tij janë çelësat e qiejve e të Tokës! Sa për ata që i mohojnë shpalljet e Allahut, ata janë të humbur!');
INSERT INTO "hasan_efendi" VALUES(39,64,'39:64','Thuaju atyre: “Vallë, ju po më urdhëroni që të adhuroj dikë tjetër, përveç Allahut, o të paditur?!”');
INSERT INTO "hasan_efendi" VALUES(39,65,'39:65','Me të vërtetë, të është shpallur ty (o Muhamed), si dhe atyre që kanë qenë para teje: “Nëse i shoqëron Allahut diçka tjetër (në adhurim), me siguri që punët e tua do të zhvlerësohen dhe do të jesh ndër të humburit.');
INSERT INTO "hasan_efendi" VALUES(39,66,'39:66','Prandaj, adhuro vetëm Allahun dhe bëhu falënderues!”');
INSERT INTO "hasan_efendi" VALUES(39,67,'39:67','Ata nuk e kanë vlerësuar siç duhet fuqinë e Allahut. Në Ditën e Kiametit, e tërë Toka do të jetë në grushtin e Tij, ndërsa qiejt do të mbështillen nën të djathtën e Tij. Qoftë i lavdëruar Ai dhe i lartësuar mbi idhujt që ia shoqërojnë Atij (në adhurim)!');
INSERT INTO "hasan_efendi" VALUES(39,68,'39:68','Kur do t’i fryhet Surit, do të vdesin të gjithë ata që gjenden në qiej dhe në Tokë, përveç atyre që do Allahu; pastaj do të fryhet për të dytën herë në Sur dhe ata përnjëherë do të ngjallen e do të presin.');
INSERT INTO "hasan_efendi" VALUES(39,69,'39:69','Dhe Toka do të shndrisë me dritën e Zotit të vet dhe do të vendoset Libri (i veprave). Pastaj do të sillen profetët dhe dëshmitarët e ata do të gjykohen me drejtësi, e nuk do t’u bëhet asnjë padrejtësi.');
INSERT INTO "hasan_efendi" VALUES(39,70,'39:70','Secilit do t’i jepet shpërblimi për atë që ka bërë, sepse Ai e di më së miri se çfarë kanë punuar.');
INSERT INTO "hasan_efendi" VALUES(39,71,'39:71','Jobesimtarët do të shtyhen grupe-grupe në Xhehenem dhe, kur të arrijnë atje, do të hapen dyert e tij dhe rojtarët do t’u thonë atyre: “A nuk ju kanë ardhur të dërguar midis jush, të cilët ju lexonin shpalljen e Zotit tuaj e ju tërhiqnin vërejtjen për takimin e kësaj dite?!” Mohuesit do të thonë: “Po, është e vërtetë!” E kështu përmbushet fjala e Allahut për dënimin e mohuesve.');
INSERT INTO "hasan_efendi" VALUES(39,72,'39:72','Atyre u thuhet: “Hyni në portat e Xhehenemit, ku do të qëndroni gjithmonë!” Sa vend i shëmtuar që është ai për mendjemëdhenjtë!');
INSERT INTO "hasan_efendi" VALUES(39,73,'39:73','Ndërsa ata që i janë frikësuar Zotit të tyre, do të çohen grupe-grupe në Xhenet. Kur t’i afrohen atij, dyert e tij do të hapen e rojtarët e tij do t’u thonë atyre: “Shpëtimi qoftë mbi ju! Ju keni qenë njerëz të mirë, andaj hyni në të dhe qëndroni përgjithmonë!”');
INSERT INTO "hasan_efendi" VALUES(39,74,'39:74','Ata do të thonë: “Lavdi Allahut, që përmbushi premtimin e Vet e na dha në trashëgim Xhenetin, ku të vendosemi si të duam!” Sa i mrekullueshëm është shpërblimi i atyre që kanë punuar drejt!');
INSERT INTO "hasan_efendi" VALUES(39,75,'39:75','Do t’i shohësh engjëjt të tubuar rreth Fronit, duke lavdëruar me falënderim Zotin e tyre. Njerëzimi do të gjykohet me drejtësi dhe të gjithë do të thonë: “Falënderimi qoftë për Allahun, Zotin e botëve!”');
INSERT INTO "hasan_efendi" VALUES(40,1,'40:1','Hâ, Mîm.');
INSERT INTO "hasan_efendi" VALUES(40,2,'40:2','Shpallja e këtij Libri është prej Allahut, të Plotfuqishmit dhe të Gjithëdijshmit,');
INSERT INTO "hasan_efendi" VALUES(40,3,'40:3','Falësit të gjynaheve dhe Pranuesit të pendimit, që dënon ashpër, si dhe Zotëruesit të të mirave. Nuk ka zot tjetër të vërtetë përveç Tij dhe tek Ai do të kthehen të gjithë.');
INSERT INTO "hasan_efendi" VALUES(40,4,'40:4','Askush nuk i vë në dyshim shpalljet e Allahut, përveç atyre që nuk besojnë, prandaj mos të mashtrojë ty bredhja e tyre nëpër botë.');
INSERT INTO "hasan_efendi" VALUES(40,5,'40:5','Në të vërtetë, edhe para tyre i mohoi (shpalljet dhe mrekullitë e Allahut) populli i Nuhut. E po ashtu edhe grupet e tjera pas tij. Secili popull u përpoq ta mundonte të dërguarin e vet dhe ta mohonte të vërtetën me kundërshtime të rreme, andaj Unë i dënova ata. Eh, si ka qenë dënimi Im!');
INSERT INTO "hasan_efendi" VALUES(40,6,'40:6','Dhe kështu, Fjala e Zotit tënd do të përmbushet për jobesimtarët: ata do të jenë vërtet banorë të Zjarrit.');
INSERT INTO "hasan_efendi" VALUES(40,7,'40:7','Ata (engjëj), që e mbajnë Fronin dhe ata që janë rreth tij, e madhërojnë Zotin e tyre me falënderim dhe i besojnë Atij. Ata lypin falje për besimtarët, duke thënë: “O Zoti ynë, Ti përfshin çdo gjë me mëshirën dhe dijeninë Tënde, prandaj fali ata që janë penduar e që ndjekin rrugën Tënde dhe ruaji ata prej dënimit të Zjarrit flakërues!');
INSERT INTO "hasan_efendi" VALUES(40,8,'40:8','O Zoti ynë, shpjeri ata në kopshtet e Adnit, të cilat ua ke premtuar, së bashku me të devotshmit prej prindërve të tyre, bashkëshorteve të tyre dhe pasardhësve të tyre! Ti je i Plotfuqishmi dhe i Urti.');
INSERT INTO "hasan_efendi" VALUES(40,9,'40:9','Dhe shpëtoji ata nga e keqja! Atë që Ti e shpëton nga e keqja atë Ditë, Ti e ke mëshiruar; e ky, me të vërtetë, është shpëtimi i madh!”');
INSERT INTO "hasan_efendi" VALUES(40,10,'40:10','Atyre që kanë mohuar, do t’u thuhet: “Urrejtja e Allahut ndaj jush është më e madhe se urrejtja juaj ndaj vetvetes, kur jeni thirrur në besim, por keni mohuar.”');
INSERT INTO "hasan_efendi" VALUES(40,11,'40:11','Ata do të përgjigjen: “O Zoti ynë, na ke bërë të vdesim dy herë dhe na ke ngjallur dy herë. Tani i pranojmë gjynahet tona. Prandaj, a ka ndonjë rrugëdalje (nga Xhehenemi)?”');
INSERT INTO "hasan_efendi" VALUES(40,12,'40:12','Atyre do t’u thuhet: “Ju keni pësuar këtë fat, sepse, kur thirrej Allahu i Vetëm, ju mohonit, por, kur i shoqërohej Atij diçka tjetër (në adhurim), ju besonit. Sot gjykimi i përket vetëm Allahut, të Lartit e të Madhërishmit.');
INSERT INTO "hasan_efendi" VALUES(40,13,'40:13','Është Ai që ju tregon shenjat e Tij dhe ju zbret riskun prej qiellit. Por askush nuk e kujton këtë, përveç atyre që kthehen tek Ai të penduar.');
INSERT INTO "hasan_efendi" VALUES(40,14,'40:14','Andaj, lutjuni Allahut me adhurim të sinqertë për Të, edhe nëse këtë e urrejnë jobesimtarët!');
INSERT INTO "hasan_efendi" VALUES(40,15,'40:15','Ai është zotërues i cilësive më të larta dhe Zoti i Fronit. Ai e dërgon Shpalljen me urdhrin e Tij tek ata, që ka zgjedhur prej robërve të Vet, për t’i paralajmëruar për Ditën e Takimit me Atë;');
INSERT INTO "hasan_efendi" VALUES(40,16,'40:16','për Ditën, kur ata do të paraqiten të zbuluar, kur asgjë e tyre nuk do të jetë e fshehtë për Allahun. Kujt i përket pushteti i asaj dite? Allahut, të Vetmit, Ngadhënjimtarit!');
INSERT INTO "hasan_efendi" VALUES(40,17,'40:17','Atë ditë, çdo njeri do të shpërblehet sipas asaj që ka punuar; atë ditë nuk ka padrejtësi! Allahu, me të vërtetë, është i shpejtë në llogari.');
INSERT INTO "hasan_efendi" VALUES(40,18,'40:18','Paralajmëroji ata për Ditën e afërt të Gjykimit, kur zemrat do t’u vijnë deri në fyt prej pikëllimit. Për njerëzit e këqij nuk ka asnjë mik të afërt dhe as ndërmjetësues të pranueshëm.');
INSERT INTO "hasan_efendi" VALUES(40,19,'40:19','Allahu e di shikimin e përvjedhur të syrit dhe atë që fsheh zemra.');
INSERT INTO "hasan_efendi" VALUES(40,20,'40:20','Ai gjykon me drejtësi, ndërsa idhujt të cilëve u luten, përveç Tij, nuk gjykojnë asgjë. Vërtet, Allahu dëgjon dhe sheh çdo gjë.');
INSERT INTO "hasan_efendi" VALUES(40,21,'40:21','A nuk kanë udhëtuar ata nëpër botë, që të shohin se si ka qenë fundi i atyre që kanë jetuar më parë, kombe shumë herë më të fuqishëm dhe me më shumë gjurmë (përmendore, ndërtime) se këta? Allahu i dënoi për shkak të gjynaheve të tyre dhe ata nuk patën askënd që t’i mbronte prej dënimit të Allahut.');
INSERT INTO "hasan_efendi" VALUES(40,22,'40:22','Kjo ndodhi, sepse atyre u vinin të dërguarit me prova të qarta, por ata nuk u besonin, prandaj Allahu i dënoi. Vërtet që Ai është i Fuqishëm dhe ndëshkon ashpër.');
INSERT INTO "hasan_efendi" VALUES(40,23,'40:23','Më parë Ne e dërguam Musain me mrekullitë Tona dhe me një autoritet të qartë');
INSERT INTO "hasan_efendi" VALUES(40,24,'40:24','te Faraoni, Hamani dhe Karuni, por ata thanë: “Ky është magjistar dhe gënjeshtar!”');
INSERT INTO "hasan_efendi" VALUES(40,25,'40:25','Dhe, kur ai u solli atyre të Vërtetën prej Nesh, ata thanë: “Vritini djemtë e atyre që i kanë besuar atij dhe lërini të gjalla vajzat e tyre!” Por kurthet e mohuesve janë të dënuara të dështojnë.');
INSERT INTO "hasan_efendi" VALUES(40,26,'40:26','Faraoni tha: “Më lini ta vras Musain e ai le ta thërrasë në ndihmë Zotin e vet. Unë vërtet druhem mos ai do t’jua ndryshojë fenë tuaj ose do të shkaktojë trazira në Tokë”.');
INSERT INTO "hasan_efendi" VALUES(40,27,'40:27','Musai tha: “Unë i lutem Zotit Tim dhe Zotit tuaj për të më mbrojtur prej çdo mendjemadhi, që nuk beson në Ditën e Llogarisë”!');
INSERT INTO "hasan_efendi" VALUES(40,28,'40:28','Por një njeri besimtar, nga të afërmit e Faraonit, i cili fshihte besimin e vet, tha: “Vallë, do të vrisni një njeri vetëm se thotë Allahu është Zoti im”, ndërkohë që ai ju ka sjellë shenja të qarta prej Zotit tuaj?! Nëse është gënjeshtar, atij i përket dënimi i gënjeshtrës së vet e, nëse është i drejtë, atëherë ju do t’ju godasë diçka prej asaj me të cilën ju kërcënohet ai. Vërtet, Allahu nuk e udhëzon në rrugë të drejtë atë që e tepron me të këqija dhe është gënjeshtar.');
INSERT INTO "hasan_efendi" VALUES(40,29,'40:29','O populli im, sot juve ju përket pushteti i tokës, por kush do të na mbrojë nga dënimi i Allahut, kur ai të na bjerë neve?” Faraoni tha: “Unë ju tregoj vetëm atë që shoh dhe ju udhëzoj vetëm në rrugën e drejtë”.');
INSERT INTO "hasan_efendi" VALUES(40,30,'40:30','Atëherë, ai që ishte besimtar tha: “O populli im, unë druaj që do t’ju arrijë dënimi si popujve të mëparshëm,');
INSERT INTO "hasan_efendi" VALUES(40,31,'40:31','ashtu siç ka qenë për popullin e Nuhut, për fiset Ad e Themud dhe për breznitë pas tyre. Allahu nuk synon padrejtësi për robërit e Vet.');
INSERT INTO "hasan_efendi" VALUES(40,32,'40:32','O populli im, unë i druhem vërtet Ditës, kur do të thërrisni njëri-tjetrin,');
INSERT INTO "hasan_efendi" VALUES(40,33,'40:33','Ditës kur, nga frika do të ikni prapa, pa pasur askënd që t’ju mbrojë nga dënimi i Allahut. Atë që Allahu e shpie në humbje, nuk mund ta udhëzojë kush.');
INSERT INTO "hasan_efendi" VALUES(40,34,'40:34','Shumë kohë më parë, juve ju pati ardhur Jusufi me prova të qarta, por ju vazhdimisht dyshonit në atë që ju kishte sjellë ai. E, kur ai vdiq, ju thatë: “Allahu nuk do të nisë më të dërguar pas tij!” Ja, kështu Allahu e shpie në humbje çdokënd që e kalon kufirin në të këqija dhe dyshon.');
INSERT INTO "hasan_efendi" VALUES(40,35,'40:35','Ata që i kundërshtojnë shpalljet e Allahut, pa pasur asnjë provë, janë të urryer në sytë e Allahut dhe në sytë e atyre që besojnë. Kështu, Allahu ia vulos zemrën çdo mendjemadhi e dhunuesi”.');
INSERT INTO "hasan_efendi" VALUES(40,36,'40:36','Dhe Faraoni tha: “O Haman, ndërtoma një kullë që t’i arrij rrugët,');
INSERT INTO "hasan_efendi" VALUES(40,37,'40:37','rrugët e qiellit e të shoh Zotin e Musait, sepse jam i bindur se ai (Musai) gënjen”. Kështu, Faraonit iu dukën të bukura veprat e tij të shëmtuara dhe u shmang nga rruga e drejtë, por dredhia e tij e çoi vetëm në rrënim.');
INSERT INTO "hasan_efendi" VALUES(40,38,'40:38','Ai që ishte besimtar tha: “O populli im, më pasoni, që unë t’ju udhëzoj në rrugën e drejtë!');
INSERT INTO "hasan_efendi" VALUES(40,39,'40:39','O populli im, në të vërtetë, jeta e kësaj bote është vetëm kënaqësi e përkohshme, ndërsa jeta tjetër është Shtëpi e përhershme.');
INSERT INTO "hasan_efendi" VALUES(40,40,'40:40','Ata që bëjnë vepra të këqija, do të dënohen vetëm sipas veprave të bëra; ndërsa ata që besojnë dhe bëjnë vepra të mira, qofshin meshkuj apo femra, do të hyjnë në Xhenet. Aty do të begatohen pa kufizim.');
INSERT INTO "hasan_efendi" VALUES(40,41,'40:41','O populli im, ç’është kjo, unë ju thërras në shpëtim, ndërsa ju më thërrisni në zjarr?!');
INSERT INTO "hasan_efendi" VALUES(40,42,'40:42','Ju më thërrisni që të mohoj Allahun dhe t’i shoqëroj Atij (në adhurim) zota të tjerë, për të cilët nuk di asgjë, ndërsa unë ju bëj thirrje të adhuroni të Plotfuqishmin, Falësin e Madh.');
INSERT INTO "hasan_efendi" VALUES(40,43,'40:43','Pa dyshim, zotave drejt të cilëve më ftoni ju, nuk mund t’u lutesh, as në këtë botë, as në tjetrën; kthimi ynë është tek Allahu. Ata që e teprojnë me të këqija, do të jenë banorë të Zjarrit.');
INSERT INTO "hasan_efendi" VALUES(40,44,'40:44','Ju së shpejti do ta kujtoni atë që po ju them! Unë çështjen time ia lë Allahut, sepse Ai i sheh vërtet robërit e Vet”.');
INSERT INTO "hasan_efendi" VALUES(40,45,'40:45','Dhe Allahu e ruajti atë prej të këqijave dhe kurtheve të tyre, ndërsa ithtarët e Faraonit i kaploi dënimi i vrazhdë.');
INSERT INTO "hasan_efendi" VALUES(40,46,'40:46','Ata do të çohen para Zjarrit në mëngjes dhe mbrëmje (në jetën e varrit) e, kur të arrijë Ora (e Kiametit), do të thuhet: “Hidhni ithtarët e Faraonit në dënimin më të rëndë!”');
INSERT INTO "hasan_efendi" VALUES(40,47,'40:47','Dhe, kur të grinden në Zjarr, të dobëtit do t’u thonë eprorëve që madhështoheshin: “Ne ishim ithtarët tuaj, ndaj, a mund të shmangni prej nesh një pjesë të dënimit tonë?”');
INSERT INTO "hasan_efendi" VALUES(40,48,'40:48','Ata që madhështoheshin, do të thonë: “Të gjithë ne jemi në zjarr. Allahu tashmë i ka gjykuar robërit e Tij”.');
INSERT INTO "hasan_efendi" VALUES(40,49,'40:49','Dhe ata që gjenden në zjarr, do t’u thonë rojtarëve të Xhehenemit: “Lutjuni Zotit tuaj që të na lehtësojë dënimin, qoftë vetëm një ditë”!');
INSERT INTO "hasan_efendi" VALUES(40,50,'40:50','Ata do të përgjigjen: “Vallë, a nuk ju kanë sjellë të dërguarit tuaj prova të qarta?” Ata do të thonë: “Po, është vërtetë”. Do t’u thuhet: “Atëherë thërrisni për ndihmë!” Por thirrjet e tyre do të jenë të kota.');
INSERT INTO "hasan_efendi" VALUES(40,51,'40:51','Sigurisht, Ne do t’i ndihmojmë të dërguarit Tanë dhe besimtarët, si në jetën e kësaj bote, ashtu edhe në Ditën, kur do të sillen dëshmitarët.');
INSERT INTO "hasan_efendi" VALUES(40,52,'40:52','Atë Ditë, keqbërësve nuk do t’u bëjë dobi shfajësimi i tyre. Ata i pret mallkimi dhe një vendbanim i shëmtuar.');
INSERT INTO "hasan_efendi" VALUES(40,53,'40:53','Ne i kemi dhënë Musait udhëzimin Tonë dhe u kemi dhënë në trashëgim bijve të Israilit Librin,');
INSERT INTO "hasan_efendi" VALUES(40,54,'40:54','si udhërrëfyes dhe këshillë për mendarët.');
INSERT INTO "hasan_efendi" VALUES(40,55,'40:55','Andaj, bëhu i durueshëm ti (o Muhamed!), sepse, pa dyshim, premtimi i Allahut është i vërtetë. Lutju Zotit për faljen e gjynaheve të tua dhe lartësoje Zotin tënd me falënderim, në mbrëmje dhe mëngjes!');
INSERT INTO "hasan_efendi" VALUES(40,56,'40:56','Ata që polemizojnë për shpalljet e Allahut, pa pasur kurrfarë prove, kanë në zemrat e tyre vetëm mendjemadhësi, e cila nuk do t’i shpjerë në qëllimin e tyre. Prandaj kërko mbrojtje prej Allahut! Me të vërtetë, Ai i dëgjon të gjitha dhe i sheh të gjitha.');
INSERT INTO "hasan_efendi" VALUES(40,57,'40:57','Krijimi i qiejve dhe i Tokës është më i madh se krijimi i njerëzve, por shumica e njerëzve nuk e di.');
INSERT INTO "hasan_efendi" VALUES(40,58,'40:58','Nuk janë njësoj i verbri dhe ai që sheh; as ai që beson e bën vepra të mira nuk është i barabartë me atë që bën keq. Sa pak që mendoni!');
INSERT INTO "hasan_efendi" VALUES(40,59,'40:59','Sigurisht që Ora (e Kiametit) do të vijë! Për këtë s’ka dyshim, por shumica e njerëzve nuk besojnë.');
INSERT INTO "hasan_efendi" VALUES(40,60,'40:60','Zoti juaj ka thënë: “Lutmuni Mua, se do t’ju përgjigjem! Me të vërtetë, ata që tregohen mendjemëdhenj e nuk duan të Më adhurojnë, do të hyjnë në Xhehenem të poshtëruar”!');
INSERT INTO "hasan_efendi" VALUES(40,61,'40:61','Allahu e ka bërë natën që ju të pushoni, ndërsa ditën e ka bërë të ndritshme për të parë. Vërtet, Allahu është Dhurues i madh ndaj njerëzve, por shumica e tyre nuk janë mirënjohës.');
INSERT INTO "hasan_efendi" VALUES(40,62,'40:62','Ja, ky është Allahu, Zoti juaj, Krijues i gjithçkaje. S’ka zot tjetër të vërtetë përveç Tij. Atëherë, si largoheni prej Tij?!');
INSERT INTO "hasan_efendi" VALUES(40,63,'40:63','Kështu largohen ata që i mohojnë shenjat e Allahut.');
INSERT INTO "hasan_efendi" VALUES(40,64,'40:64','Allahu është Ai që jua ka bërë Tokën vendbanim e qiellin kulm, ju ka dhënë formë e jua ka përsosur atë dhe ju ka furnizuar me ushqime të mira. Ja, ky është Allahu, Zoti juaj. Qoftë i lartësuar Allahu, Zoti i botëve!');
INSERT INTO "hasan_efendi" VALUES(40,65,'40:65','Ai është i Përjetshmi, s’ka zot tjetër të vërtetë përveç Tij. Prandaj lutjuni vetëm Atij, duke iu drejtuar me adhurim të sinqertë. Qoftë i lavdëruar Allahu, Zoti i botëve!');
INSERT INTO "hasan_efendi" VALUES(40,66,'40:66','Thuaj (o Muhamed!): “Mua më është ndaluar të adhuroj atë që adhuroni ju, përpos Allahut, pasi më kanë ardhur prova të qarta prej Zotit tim e më është urdhëruar që t’i nënshtrohem Zotit të botëve”.');
INSERT INTO "hasan_efendi" VALUES(40,67,'40:67','Është Ai që ju ka krijuar prej dheut, pastaj prej pikës së farës e pastaj prej droçkës së gjakut. Ai ju bën që të lindni si foshnje, pastaj të arrini moshën e pjekurisë e pastaj të bëheni pleq (ndonëse disa prej jush vdesin më herët), me qëllim që të plotësoni afatin që ju është caktuar të jetoni dhe që të arrini të kuptoni.');
INSERT INTO "hasan_efendi" VALUES(40,68,'40:68','Është Ai që ka në dorë jetën dhe vdekjen. Kur Ai vendos diçka, vetëm thotë për të: “Bëhu!” dhe ajo menjëherë bëhet.');
INSERT INTO "hasan_efendi" VALUES(40,69,'40:69','A i sheh ata që polemizojnë për shpalljet e Allahut, se si largohen nga rruga e drejtë?');
INSERT INTO "hasan_efendi" VALUES(40,70,'40:70','Ata që kanë mohuar Librin dhe mesazhet që i kemi sjellë me të dërguarit Tanë, do ta marrin vesh të vërtetën,');
INSERT INTO "hasan_efendi" VALUES(40,71,'40:71','kur t’u vihen vargoj në qafë dhe të sillen me zinxhirë');
INSERT INTO "hasan_efendi" VALUES(40,72,'40:72','në ujë të valë e pastaj të digjen në zjarr.');
INSERT INTO "hasan_efendi" VALUES(40,73,'40:73','Pastaj atyre do t’u thuhet: “Ku janë ata që i adhuronit,');
INSERT INTO "hasan_efendi" VALUES(40,74,'40:74','në vend Allahut?” Ata do të përgjigjen: “Na kanë braktisur. Vërtet, ne nuk paskemi adhuruar asgjë më parë!” Ja, kështu Allahu i shpie mohuesit në humbje.');
INSERT INTO "hasan_efendi" VALUES(40,75,'40:75','Ky dënim është për shkak se gëzoheshit në tokë, pa kurrfarë të drejte dhe ngaqë keni qenë të ndërkryer.');
INSERT INTO "hasan_efendi" VALUES(40,76,'40:76','Hyni në dyert e Xhehenemit për të qëndruar përherë në të! Sa i shëmtuar që është vendbanimi i mendjemëdhenjve!');
INSERT INTO "hasan_efendi" VALUES(40,77,'40:77','Andaj, bëhu i durueshëm, se premtimi i Allahut, me siguri që do të përmbushet. E, nëse të tregojmë ty (o Muhamed) diçka nga dënimi që u kemi premtuar atyre apo nëse të lëmë të vdesësh para kësaj, ata te Ne do të kthehen gjithsesi.');
INSERT INTO "hasan_efendi" VALUES(40,78,'40:78','Edhe para teje (o Muhamed) Ne kemi nisur të dërguar, për disa prej të cilëve të kemi treguar ty e për disa të tjerë nuk të kemi treguar. Asnjë i dërguar nuk mund të sjellë ndonjë mrekulli, veçse me lejen e Allahut. Dhe kur të vijë urdhri i Allahut, do të gjykohet me drejtësi e atëherë, jobesimtarët kokëfortë do të shkatërrohen.');
INSERT INTO "hasan_efendi" VALUES(40,79,'40:79','Allahu është Ai që ka krijuar për ju kafshët, në mënyrë që disa prej tyre t’i shaloni e disa të tjera t’i përdorni për ushqim.');
INSERT INTO "hasan_efendi" VALUES(40,80,'40:80','Ju përfitoni prej tyre në shumë mënyra; ato ju çojnë ku të keni nevojë, duke ju mbartur nëpër tokë, siç ju mbartin anijet nëpër det.');
INSERT INTO "hasan_efendi" VALUES(40,81,'40:81','Dhe Ai ju tregon shenjat e Tij (që tregojnë se Ai është Një Zot i Vetëm), prandaj cilat shenja të Allahut i mohoni ju?!');
INSERT INTO "hasan_efendi" VALUES(40,82,'40:82','Vallë, a nuk kanë udhëtuar ata nëpër botë, për të parë se si ka qenë fundi i atyre, që kanë jetuar më parë? Ata kanë qenë më të shumtë, më të fortë dhe kanë lënë më shumë gjurmë në Tokë, por nuk u ka bërë aspak dobi ajo që kishin fituar.');
INSERT INTO "hasan_efendi" VALUES(40,83,'40:83','Kur të dërguarit e tyre iu sollën prova të qarta, ata u mburrën me diturinë që zotëronin vetë; por shumë shpejt i goditi ajo, me të cilën talleshin.');
INSERT INTO "hasan_efendi" VALUES(40,84,'40:84','Dhe, kur panë dënimin Tonë, ata thanë: “Tani ne nuk besojmë në askënd tjetër veç Allahut dhe i mohojmë idhujt që ia shoqëronim Atij (në adhurim)”.');
INSERT INTO "hasan_efendi" VALUES(40,85,'40:85','Por besimi i tyre nuk u solli kurrfarë dobie, kur panë dënimin Tonë. I tillë është ligji i Allahut, që ka vepruar gjithmonë midis robërve të Tij. Të humbur kanë qenë jobesimtarët.');
INSERT INTO "hasan_efendi" VALUES(41,1,'41:1','Hâ, Mîm.');
INSERT INTO "hasan_efendi" VALUES(41,2,'41:2','Kjo është Shpallje prej të Gjithëmëshirshmit, Mëshirëplotit.');
INSERT INTO "hasan_efendi" VALUES(41,3,'41:3','Ky është një Libër, vargjet e të cilit janë shtjelluar qartë, një Kuran në gjuhën arabe, për njerëzit me dije.');
INSERT INTO "hasan_efendi" VALUES(41,4,'41:4','Ai sjell lajme të mira dhe paralajmërime, por shumica e njerëzve ia kthejnë shpinën dhe largohen pa e dëgjuar.');
INSERT INTO "hasan_efendi" VALUES(41,5,'41:5','Ata thonë: “Ajo për të cilën ti na thërret, nuk i arrin zemrat tona, sepse ato janë të mbrojtura mirë. Veshët tanë janë të shurdhër e midis nesh e teje ka perde. Prandaj ti vepro si të duash, se edhe ne kështu do të veprojmë”.');
INSERT INTO "hasan_efendi" VALUES(41,6,'41:6','Thuaj (o Muhamed!): “Në të vërtetë, unë jam vetëm një njeri si ju. Mua më është shpallur që Zoti juaj është një Zot i Vetëm, andaj ecni në rrugën e drejtë që të çon tek Ai dhe kërkoni falje prej Tij! Mjerë ata të cilët i shoqërojnë Atij zota të tjerë,');
INSERT INTO "hasan_efendi" VALUES(41,7,'41:7','e të cilët nuk japin zekatin dhe nuk besojnë në jetën tjetër!');
INSERT INTO "hasan_efendi" VALUES(41,8,'41:8','Ndërsa ata që besojnë dhe bëjnë vepra të mira, sigurisht që do të kenë shpërblim të pandërprerë.”');
INSERT INTO "hasan_efendi" VALUES(41,9,'41:9','Thuaj: “Vallë, a me të vërtetë ju e mohoni Atë, i cili e ka krijuar Tokën në dy ditë dhe sajoni zota të barabartë me Atë?!” Ai është Zoti i botëve!');
INSERT INTO "hasan_efendi" VALUES(41,10,'41:10','Ai krijoi në Tokë male të palëvizshme që ngrihen lart, e bekoi atë dhe përcaktoi në të furnizimin (për banorët) e saj me masë për katër ditë; ky është shpjegimi për të gjithë ata që pyesin.');
INSERT INTO "hasan_efendi" VALUES(41,11,'41:11','Pastaj Ai iu kthye qiellit, që ishte në gjendje mjegullie e i tha atij dhe Tokës: “Ejani të dy me hir a me pahir!”. Ata u përgjigjën: “Erdhëm me gjithë dëshirë e të bindur!”');
INSERT INTO "hasan_efendi" VALUES(41,12,'41:12','Ai krijoi shtatë qiej në dy ditë dhe çdo qielli i caktoi detyrat e tij. Qiellin më të afërm Ne e zbukuruam me yje të shkëlqyer dhe i vumë roja. Ky është urdhri i të Plotfuqishmit dhe të Gjithëdijshmit.');
INSERT INTO "hasan_efendi" VALUES(41,13,'41:13','Nëse ata shmangen, ti thuaju: “Ju paralajmëroj me rrufenë që goditi fiset Ad dhe Themud.');
INSERT INTO "hasan_efendi" VALUES(41,14,'41:14','Kur atyre u erdhën të dërguarit nga çdo anë, duke u thënë: “Mos adhuroni tjetër përveç Zotit!”, ata thanë: “Sikur të kishte dashur Zoti ynë, Ai do të kishte dërguar engjëj. Ne nuk besojmë në mesazhin tuaj.”');
INSERT INTO "hasan_efendi" VALUES(41,15,'41:15','Sa i përket fisit Ad, ata u treguan arrogantë në Tokë dhe, pa kurrfarë të drejte, thoshin: “Kush është më i fortë se ne?” Vallë, a nuk e dinin ata se Allahu që i kishte krijuar, është më i fuqishëm se ata?! Kështu, ata i mohuan shpalljet Tona.');
INSERT INTO "hasan_efendi" VALUES(41,16,'41:16','Dhe, Ne u dërguam atyre në ditët e fatkeqësisë erë të acartë, për ta shijuar dënimin poshtërues që në këtë jetë; ndërsa dënimi në jetën tjetër do të jetë edhe më poshtërues dhe askush nuk do t’i ndihmojë.');
INSERT INTO "hasan_efendi" VALUES(41,17,'41:17','Fisit Themud, Ne ia treguam rrugën e drejtë, por ai parapëlqeu verbërimin e jo rrugën e drejtë, andaj e goditi rrufeja e dënimit poshtërues, për atë që kishte bërë.');
INSERT INTO "hasan_efendi" VALUES(41,18,'41:18','E Ne i shpëtuam ata që besuan dhe që u ruajtën (prej të këqijave).');
INSERT INTO "hasan_efendi" VALUES(41,19,'41:19','Paralajmëroji ata për Ditën, kur do të tubohen armiqtë e Allahut për në zjarr e do të shtyhen me radhë.');
INSERT INTO "hasan_efendi" VALUES(41,20,'41:20','Kur të vijnë afër zjarrit, kundër tyre do të dëshmojnë veshët, sytë dhe lëkurët e tyre për atë që kanë bërë.');
INSERT INTO "hasan_efendi" VALUES(41,21,'41:21','Ata do t’u thonë lëkurëve të veta: “Pse dëshmuat kundër nesh?” e ato do të përgjigjen: “Allahu, i Cili ia jep aftësinë e të folurit gjithçkaje, na ka bërë që të flasim. Ai ju ka krijuar për herë të parë dhe tek Ai do të ktheheni”.');
INSERT INTO "hasan_efendi" VALUES(41,22,'41:22','Ju nuk u fshehët, që kundër jush të mos dëshmonin veshët, sytë e lëkura juaj, por ngaqë besonit se Allahu nuk i dinte shumë nga ato që bënit.');
INSERT INTO "hasan_efendi" VALUES(41,23,'41:23','Ky mendim që keni pasur për Zotin tuaj, ju shkatërroi e ju u bëtë humbës.');
INSERT INTO "hasan_efendi" VALUES(41,24,'41:24','Edhe nëse ata durojnë, sërish zjarri është vendstrehim për ta; edhe nëse kërkojnë falje, lutja nuk do t’u merret parasysh.');
INSERT INTO "hasan_efendi" VALUES(41,25,'41:25','Ne u kishim caktuar atyre shokë të këqij, të cilët ua zbukuronin të tashmen dhe të shkuarën e tyre e për ta u përmbush Fjala, ashtu si për popujt që kanë qenë para tyre prej xhindesh dhe njerëzish. Ata kanë qenë vërtet të humbur.');
INSERT INTO "hasan_efendi" VALUES(41,26,'41:26','Jobesimtarët thonë: “Mos e dëgjoni këtë Kuran! Flisni e qeshni me zë të lartë, që të ndërprisni leximin e tij!”');
INSERT INTO "hasan_efendi" VALUES(41,27,'41:27','Por, Ne do të bëjmë që mohuesit të shijojnë një dënim të ashpër dhe do t’i ndëshkojmë ata sipas veprave më të këqija që kanë bërë.');
INSERT INTO "hasan_efendi" VALUES(41,28,'41:28','Ky është “shpërblimi” për armiqtë e Allahut. Xhehenemi është vendbanimi i përhershëm i tyre. Ky dënim është për ata, ngaqë mohonin shpalljet Tona.');
INSERT INTO "hasan_efendi" VALUES(41,29,'41:29','Dhe jobesimtarët do të thonë: “O Zoti ynë, na i trego ata xhinde dhe njerëz, që na kanë shpënë në humbje, që t’i shkelim me këmbët tona e t’i poshtërojmë!”');
INSERT INTO "hasan_efendi" VALUES(41,30,'41:30','Me të vërtetë, atyre që thonë: “Zoti ynë është Allahu”, e pastaj vazhdojnë të vendosur në rrugën e drejtë, do t’u zbresin engjëjt (para vdekjes) e do t’u thonë: “Mos u frikësoni dhe mos u pikëlloni! Dhe gëzojuni Xhenetit që ju është premtuar.');
INSERT INTO "hasan_efendi" VALUES(41,31,'41:31','Ne jemi mbrojtësit tuaj në jetën e kësaj bote dhe në botën tjetër. Atje do të keni çfarë t’ju dëshirojë zemra dhe çfarë të kërkoni,');
INSERT INTO "hasan_efendi" VALUES(41,32,'41:32','dhuratë e pasur prej një Zoti Falës dhe Mëshirëplotë.”');
INSERT INTO "hasan_efendi" VALUES(41,33,'41:33','E kush flet më bukur se ai që i fton njerëzit drejt Allahut, bën vepra të mira dhe thotë: “Unë, me të vërtetë jam mysliman.”');
INSERT INTO "hasan_efendi" VALUES(41,34,'41:34','Nuk barazohet e mira me të keqen! Të keqen ktheje me të mirë e atëherë armiku yt do të të bëhet menjëherë mik i ngushtë.');
INSERT INTO "hasan_efendi" VALUES(41,35,'41:35','Ky virtyt u dhurohet vetëm atyre që durojnë dhe vetëm atyre që kanë një fat të madh. ');
INSERT INTO "hasan_efendi" VALUES(41,36,'41:36','Nëse djalli fillon të të joshë, kërko mbështetje te Allahu. Me të vërtetë, Ai i dëgjon të gjitha dhe i di të gjitha.');
INSERT INTO "hasan_efendi" VALUES(41,37,'41:37','Ndër shenjat e Tij janë nata dhe dita, si dhe Dielli dhe Hëna. Mos bëni sexhde as për Diellin, as për Hënën, por, nëse jeni adhurues të Atij, bëni sexhde për Allahun i Cili i ka krijuar ato!');
INSERT INTO "hasan_efendi" VALUES(41,38,'41:38','E, nëse idhujtarët tregohen kryeneçë, le ta dinë se ata që janë te Zoti yt, e lavdërojnë Atë natën dhe ditën e kurrë nuk ndihen të lodhur.');
INSERT INTO "hasan_efendi" VALUES(41,39,'41:39','Ndër shenjat e Tij është edhe ringjallja e tokës. Ti e sheh tokën të thatë e të shkretë, por, kur zbresim shi mbi të, ajo lëviz dhe gjallërohet. Pa dyshim, Ai që e ngjall atë, i ngjall edhe të vdekurit. Ai është vërtet i pushtetshëm për çdo gjë.');
INSERT INTO "hasan_efendi" VALUES(41,40,'41:40','Ata që i shtrembërojnë shpalljet Tona, nuk mund të Na fshihen. Si mendoni, a është më i mirë ai që do të hidhet në zjarr apo ai që në Ditën e Kiametit do të vijë i sigurt? Bëni ç’të doni, se Ai i sheh të gjitha çfarë punoni ju.');
INSERT INTO "hasan_efendi" VALUES(41,41,'41:41','Ata që e mohojnë Fjalën Tonë përkujtuese , kur u vjen, do të dënohen rëndë. Ai është vërtet një Libër i madhërishëm.');
INSERT INTO "hasan_efendi" VALUES(41,42,'41:42','Atij nuk mund t’i afrohet gënjeshtra nga asnjëra anë. Kjo është shpallje prej një të Urti që meriton të gjitha lavdet.');
INSERT INTO "hasan_efendi" VALUES(41,43,'41:43','Nuk të thuhet ty (o Muhamed) asgjë, përpos asaj që u është thënë të dërguarve para teje. Në të vërtetë, Zoti yt fal shumë, por edhe dënon ashpër.');
INSERT INTO "hasan_efendi" VALUES(41,44,'41:44','Sikur ta kishim shpallur Kuranin në një gjuhë tjetër, ata do të thoshin: “Ah, sikur vargjet e tij të ishin të kuptueshme! Vallë, pse të jetë në një gjuhë të huaj, ndërkohë që ai është arab?!” Thuaj: “Ai është udhërrëfyes dhe shërim për besimtarët”. Por ata që nuk besojnë, janë të shurdhër dhe të verbër, njësoj si të thirreshin prej vendeve të largëta.');
INSERT INTO "hasan_efendi" VALUES(41,45,'41:45','Vërtet, Ne ia dhamë Musait Librin, por rreth tij pati shumë mospajtime. Dhe, sikur të mos ishte Vendimi i Zotit tënd i dhënë më parë (për shtyrjen e gjykimit deri në Ditën e Kiametit), ata (që u përçanë) do të gjykoheshin menjëherë. Me të vërtetë, ata dyshojnë thellë në këtë.');
INSERT INTO "hasan_efendi" VALUES(41,46,'41:46','Kush bën mirë, e bën për dobi të vet, e kush punon keq, punon në dëm të vet. Zoti yt nuk u bën padrejtësi robërve të Tij.');
INSERT INTO "hasan_efendi" VALUES(41,47,'41:47','Vetëm Allahu e di se kur do të vijë Ora. Kurrfarë frute nuk mund të dalë prej kupës së lules dhe asnjë femër nuk mund të mbetet shtatzënë apo të lindë pa dijeninë e Tij. Ditën kur Ai t’i thërrasë njerëzit e t’u thotë: “Ku janë ata zota që i quanit si ortakët e Mi?”, ata do të përgjigjen: “Askush prej nesh nuk dëshmon sot se ata janë ortakët e Tu.”');
INSERT INTO "hasan_efendi" VALUES(41,48,'41:48','Idhujt, që ata i adhuronin më parë, do t’i zhgënjejnë dhe ata do ta dinë se nuk ka më shpëtim.');
INSERT INTO "hasan_efendi" VALUES(41,49,'41:49','Njeriu nuk lodhet kurrë duke kërkuar të mirën, por, nëse e godet e keqja, ai menjëherë bie në dëshpërim dhe humb shpresën.');
INSERT INTO "hasan_efendi" VALUES(41,50,'41:50','Nëse pas fatkeqësisë, Ne i japim atij që të shijojë nga mirësitë Tona, ai me siguri, do të thotë: “Këtë e meritoj! Unë nuk besoj se do të vijë Ora (e Kiametit), por, edhe nëse kthehem te Zoti im, sigurisht që Ai do të më japë shpërblimin më të mirë.” Në të vërtetë, Ne do t’i njoftojmë mohuesit për atë që kanë bërë dhe do t’i bëjmë të shijojnë një dënim të ashpër.');
INSERT INTO "hasan_efendi" VALUES(41,51,'41:51','Kur njeriut i dhurojmë dhuntitë Tona, ai shmanget dhe na kthen kurrizin, mirëpo, kur atë e godet e keqja, ai lutet gjerë e gjatë.');
INSERT INTO "hasan_efendi" VALUES(41,52,'41:52','Thuaju, (o Muhamed): “Më thoni! Nëse ky Kuran është vërtet prej Allahut dhe ju e mohoni atë, atëherë kush është në humbje më të madhe se ai që është në kundërshtim të hapur me Të?!”');
INSERT INTO "hasan_efendi" VALUES(41,53,'41:53','Ne do t’u tregojmë atyre shenjat Tona në hapësirat tokësore e qiellore, si dhe në vetvete, derisa t’u bëhet plotësisht e qartë se ai (Kurani) është e vërteta. Vallë, a nuk të mjafton ty që, Zoti yt është Dëshmues i çdo gjëje?!');
INSERT INTO "hasan_efendi" VALUES(41,54,'41:54','Vërtet, ata dyshojnë në takimin me Zotin e tyre, por le ta dinë se Ai ka nën kontroll çdo gjë.');
INSERT INTO "hasan_efendi" VALUES(42,1,'42:1','Hâ, Mîm.');
INSERT INTO "hasan_efendi" VALUES(42,2,'42:2','Ajn, Sîn, Kâf.');
INSERT INTO "hasan_efendi" VALUES(42,3,'42:3','Kështu Allahu, i Plotfuqishmi, i Urti, të frymëzon ty (o Muhamed), ashtu siç i ka frymëzuar edhe ata para teje.');
INSERT INTO "hasan_efendi" VALUES(42,4,'42:4','Atij i përket gjithçka që gjendet në qiej dhe gjithçka që gjendet në Tokë. Ai është i Larti, i Madhërishmi!');
INSERT INTO "hasan_efendi" VALUES(42,5,'42:5','Qiejt gati sa nuk çahen nga sipër (prej madhështisë së Allahut), ndërkohë që engjëjt lavdërojnë e falënderojnë Zotin e tyre dhe kërkojnë falje për ata që janë në Tokë. Me të vërtetë që Allahu është Falësi dhe Mëshirëploti.');
INSERT INTO "hasan_efendi" VALUES(42,6,'42:6','Allahu është Mbikëqyrës mbi ata që zgjedhin mbrojtës përveç Tij, ndërsa ti (o Muhamed) nuk je kujdestar i tyre.');
INSERT INTO "hasan_efendi" VALUES(42,7,'42:7','Dhe kështu pra, Ne të kemi shpallur ty një Kuran në gjuhën arabe, për të paralajmëruar popullin e Mekës dhe ata rreth saj; për t’i paralajmëruar ata për Ditën, e cila do të vijë me siguri: kur të gjithë njerëzit do të tubohen, disa në Xhenet e disa në Xhehenem.');
INSERT INTO "hasan_efendi" VALUES(42,8,'42:8','Sikur të donte Allahu, do t’i kishte bërë të gjithë të një besimi, por Ai pranon në mëshirën e Vet kë të dojë, kurse mohuesit nuk kanë as mbrojtës, as ndihmës.');
INSERT INTO "hasan_efendi" VALUES(42,9,'42:9','Vallë, a mos zgjedhin ata mbrojtës tjetër, përveç Atij?! Sigurisht, vetëm Allahu është Mbrojtësi. Ai i ngjall të vdekurit dhe është i Fuqishëm për çdo gjë.');
INSERT INTO "hasan_efendi" VALUES(42,10,'42:10','Për çfarëdo gjëje që ju të diskutoni, fjala e fundit i përket Allahut. Ky është Allahu, Zoti im. Unë tek Ai mbështetem dhe tek Ai kthehem i penduar.');
INSERT INTO "hasan_efendi" VALUES(42,11,'42:11','Krijues i qiejve dhe i Tokës, Ai ju ka dhënë bashkëshorte nga vetja juaj për t’ju shumuar, si dhe bagëti në çifte. Asgjë nuk i shëmbëllen Atij. Ai i dëgjon të gjitha dhe i sheh të gjitha.');
INSERT INTO "hasan_efendi" VALUES(42,12,'42:12','Tek Ai janë çelësat e qiejve dhe të Tokës. Ai ia zgjeron mjetet e jetesës kujt të dojë, por edhe ia pakëson kujt të dojë. Vërtet, Ai është i Gjithëdijshëm për çdo gjë.');
INSERT INTO "hasan_efendi" VALUES(42,13,'42:13','Ai ka urdhëruar për ju atë Fe, të cilën ta zbuloi ty (o Muhamed) dhe që pati urdhëruar për Nuhun, Ibrahimin, Musain dhe Isain, duke thënë: “Qëndroni në fenë e drejtë dhe mos u përçani në të!” Por për idhujtarët është e rëndë ajo, në të cilën po i fton ti. Allahu zgjedh për këtë fe kë të dojë dhe udhëzon drejt saj këdo që kthehet tek Ai me pendim.');
INSERT INTO "hasan_efendi" VALUES(42,14,'42:14','Sidoqoftë, ata u përçanë midis tyre prej smirës, vetëm pasi u erdhi dijenia. Dhe, sikur të mos kishte paraprirë fjala e Zotit tënd (për shtyrjen e dënimit) deri në një afat të caktuar, ata do të gjykoheshin menjëherë. Edhe ata që trashëguan Librin më pas, dyshojnë thellë në të.');
INSERT INTO "hasan_efendi" VALUES(42,15,'42:15','Andaj, ti thirru dhe bëhu këmbëngulës, ashtu siç je urdhëruar, mos i ndiq epshet e tyre dhe thuaj: “Unë besoj çdo libër që e ka shpallur Allahu dhe jam urdhëruar që të gjykoj drejt midis jush; Allahu është Zoti ynë dhe Zoti juaj. Veprat tona janë për ne, e veprat tuaja janë për ju. Midis nesh dhe jush nuk ka kurrfarë mosmarrëveshjeje. Allahu do të na tubojë të gjithëve dhe tek Ai do të kthehemi të gjithë”.');
INSERT INTO "hasan_efendi" VALUES(42,16,'42:16','Sa për ata që polemizojnë rreth Allahut, pasi i janë përgjigjur Atij, arsyetimet e tyre do të jenë të pavlefshme te Zoti i tyre, mbi ata do të bjerë zemërimi i Tij e do të dënohen ashpër.');
INSERT INTO "hasan_efendi" VALUES(42,17,'42:17','Allahu është Ai që e ka shpallur Librin me vërtetësi dhe drejtësi. E si do ta dije ti që ndoshta Dita e Kiametit është afër?!');
INSERT INTO "hasan_efendi" VALUES(42,18,'42:18','Kiametin kërkojnë ta shpejtojnë ata që nuk besojnë në të, kurse ata që besojnë, kanë frikë nga ai dhe e dinë se është plotësisht i vërtetë. Pa dyshim, ata që kundërshtojnë për Ditën e Kiametit, janë në një gabim shumë të madh!');
INSERT INTO "hasan_efendi" VALUES(42,19,'42:19','Allahu është i Mirë me robërit e Vet dhe i jep begati kujt të dojë; Ai është i Plotfuqishëm dhe i Pushtetshëm.');
INSERT INTO "hasan_efendi" VALUES(42,20,'42:20','Atij që dëshiron shpërblim në jetën tjetër, Ne do t’ia shtojmë atë. Ndërsa atij që dëshiron shpërblim në këtë jetë, Ne do t’i japim pjesë prej saj, por ai nuk do të ketë pjesë në jetën tjetër.');
INSERT INTO "hasan_efendi" VALUES(42,21,'42:21','A kanë ata idhuj, të cilët u kanë caktuar një fe që nuk e ka lejuar Allahu?! Sikur të mos kishte qenë Fjala e vendimit të mëparshëm, ata do të ishin gjykuar menjëherë. Për keqbërësit është përgatitur një dënim i dhembshëm.');
INSERT INTO "hasan_efendi" VALUES(42,22,'42:22','Atë Ditë ti do t’i shohësh keqbërësit se si tmerrohen për atë që kanë fituar, ndërkohë që ndëshkimi Ynë do t’i godasë me siguri. Ndërsa ata që kanë besuar dhe bërë vepra të mira, do të jenë në kopshtet e mrekullueshme të Xheneteve. Ata do të kenë te Zoti i tyre çfarë të duan; kjo është dhurata e madhe.');
INSERT INTO "hasan_efendi" VALUES(42,23,'42:23','Kështu i gëzon Allahu robërit e Vet, që kanë besuar dhe që kanë bërë vepra të mira. Thuaju: “Për këtë nuk kërkoj kurrfarë shpërblimi tjetër prej jush, përveç respektit (për mua) për hir të farefisnisë (që kemi).” Atij që bën vepër të mirë, Ne do t’ia shtojmë shpërblimin. Allahu, me të vërtetë, është Falës dhe Mirënjohës.');
INSERT INTO "hasan_efendi" VALUES(42,24,'42:24','A thonë ata: “Ai (Muhamedi a.s.) trillon gënjeshtra për Allahun”?! Po të donte Allahu, do ta vuloste zemrën tënde! Allahu e shkatërron të pavërtetën dhe e forcon të vërtetën me fjalët e Veta. Ai e di mirë ç’ka në zemrat e tyre.');
INSERT INTO "hasan_efendi" VALUES(42,25,'42:25','Ai e pranon pendimin e robërve të Vet, i fal gjynahet dhe i di të gjitha çfarë punoni ju.');
INSERT INTO "hasan_efendi" VALUES(42,26,'42:26','Ai u përgjigjet lutjeve të atyre që besojnë dhe bëjnë vepra të mira e u jep edhe më tepër prej dhuntisë së Vet, kurse për mohuesit do të ketë dënim të ashpër.');
INSERT INTO "hasan_efendi" VALUES(42,27,'42:27','Sikur Allahu t’i shtonte mjetet e jetesës për robërit e Vet, ata do ta mbushnin Tokën me mbrapshti. Por Ai u jep me masë, sa dëshiron, sepse Ai i njeh dhe i sheh mirë robërit e Vet.');
INSERT INTO "hasan_efendi" VALUES(42,28,'42:28','Është Ai që e zbret shiun, kur ata kanë humbur çdo shpresë dhe përhap mëshirën e Vet; Ai është Mbrojtësi i vërtetë dhe i Denji për falënderim.');
INSERT INTO "hasan_efendi" VALUES(42,29,'42:29','Një prej shenjave të Tij është krijimi i qiejve dhe i Tokës dhe i qenieve të gjalla, që ka shpërndarë nëpër to; Ai është i Fuqishëm t’i tubojë ata kur të dojë.');
INSERT INTO "hasan_efendi" VALUES(42,30,'42:30','Çfarëdo fatkeqësie që ju godet, është si pasojë e asaj që keni bërë vetë; por Ai fal shumë.');
INSERT INTO "hasan_efendi" VALUES(42,31,'42:31','Në këtë Tokë ju nuk mund t’i shpëtoni Atij, sepse, veç Allahut, nuk ka tjetër mbrojtës apo ndihmës për ju.');
INSERT INTO "hasan_efendi" VALUES(42,32,'42:32','Një prej shenjave të Tij janë anijet e mëdha që lundrojnë në det – si malet.');
INSERT INTO "hasan_efendi" VALUES(42,33,'42:33','Nëse Ai do, e qetëson erën dhe ato mbesin të palëvizshme në sipërfaqen e tij (detit). Këtu ka shenja për çdokënd që është i durueshëm dhe mirënjohës.');
INSERT INTO "hasan_efendi" VALUES(42,34,'42:34','Ose, Ai mund t’i fundosë anijet, për shkak të gjynaheve që kanë bërë ata . Sidoqoftë, Ai fal shumë.');
INSERT INTO "hasan_efendi" VALUES(42,35,'42:35','Le ta dinë ata që vënë në dyshim shenjat Tona, se nuk kanë ku t’i shmangen dënimit Tonë.');
INSERT INTO "hasan_efendi" VALUES(42,36,'42:36','Çdo gjë që ju është dhënë juve, është vetëm kënaqësi në jetën e kësaj bote. Por shpërblimi i Allahut është më i mirë dhe më i përhershëm për ata që besojnë dhe mbështeten te Zoti i tyre;');
INSERT INTO "hasan_efendi" VALUES(42,37,'42:37','për ata që ruhen prej gjynaheve të mëdha e mbrapshtive dhe që, kur i zemëron dikush, falin;');
INSERT INTO "hasan_efendi" VALUES(42,38,'42:38','për ata që i përgjigjen thirrjes së Zotit të tyre, e falin namazin rregullisht dhe këshillohen për punët e veta me njëri-tjetrin; për ata që ndajnë lëmoshë prej asaj që u kemi dhënë Ne;');
INSERT INTO "hasan_efendi" VALUES(42,39,'42:39','dhe për ata që, kur i godet ndonjë padrejtësi e madhe, i bëjnë qëndresë dhe marrin hak.');
INSERT INTO "hasan_efendi" VALUES(42,40,'42:40','Shpërblimi për të keqen është i njëjtë me atë të keqe. Por, atë që fal dhe pajton, e pret shpërblimi i Allahut. Ai, në të vërtetë, nuk i do të padrejtët.');
INSERT INTO "hasan_efendi" VALUES(42,41,'42:41','Për atë që përgjigjet, kur i është bërë një padrejtësi, nuk ka kurrfarë qortimi ndaj tij.');
INSERT INTO "hasan_efendi" VALUES(42,42,'42:42','Qortim ka vetëm kundër atyre që u bëjnë keq njerëzve dhe që pa të drejtë mbjellin ngatërresa në Tokë; për ata ka dënim të dhembshëm.');
INSERT INTO "hasan_efendi" VALUES(42,43,'42:43','Kushdo që duron dhe fal, ta dijë se, në të vërtetë, këto janë nga veprimet më të virtytshme.');
INSERT INTO "hasan_efendi" VALUES(42,44,'42:44','Për atë që Allahu e shpie në humbje, nuk ka mbrojtës tjetër përveç Tij. Ti do të shohësh se si keqbërësit, kur ta shohin dënimin, do të thonë: “A ka ndonjë rrugë për t’u kthyer?”');
INSERT INTO "hasan_efendi" VALUES(42,45,'42:45','Ti do t’i shohësh se si do të ballafaqohen me zjarrin të përulur nga poshtërimi dhe duke parë tinëzisht me bisht të syrit. Ata që kanë besuar do të thonë: “Me të vërtetë që humbësit janë ata që kanë humbur vetveten dhe familjen e tyre në Ditën e Kiametit”. Me të vërtetë, keqbërësit do të vuajnë një dënim të përhershëm!');
INSERT INTO "hasan_efendi" VALUES(42,46,'42:46','Ata nuk do të kenë asnjë mbrojtës që do t’i ndihmojë, në vend Allahut. Për atë që Allahu e shpie në humbje, s’ka kurrfarë rruge (shpëtimi).');
INSERT INTO "hasan_efendi" VALUES(42,47,'42:47','Përgjigjuni thirrjes së Zotit tuaj, para se të vijë Dita, të cilën Allahu nuk e kthen më. Atë Ditë ju nuk do të keni kurrfarë strehimi dhe nuk do të mund të mohoni.');
INSERT INTO "hasan_efendi" VALUES(42,48,'42:48','Nëse ata kthejnë kurrizin, dije se Ne nuk të kemi dërguar ty (o Muhamed), si rojë për ata. Detyra jote është vetëm që të kumtosh. Kur Ne i japim njeriut të shijojë mëshirën Tonë, Ai i gëzohet asaj. Por, nëse atë e godet ndonjë e keqe për shkak të asaj që kanë bërë duart e tij, atëherë, ai bëhet mosmirënjohës i vërtetë.');
INSERT INTO "hasan_efendi" VALUES(42,49,'42:49','I Allahut është pushteti mbi qiejt dhe Tokën. Ai krijon çfarë të dojë. Ai i dhuron vajza kujt të dojë e djem kujt të dojë.');
INSERT INTO "hasan_efendi" VALUES(42,50,'42:50','Disave u dhuron të dyja gjinitë: djem e vajza; e, kë të dojë e lë pa fëmijë. Vërtet, Ai është i Gjithëdijshëm dhe i Fuqishëm.');
INSERT INTO "hasan_efendi" VALUES(42,51,'42:51','Asnjë njeriu nuk i është dhënë t’i flasë Allahu, përveçse me anë të Frymëzimit ose pas perdes, ose me anën e një Kumtuesi (Engjëlli) të dërguar me lejen e Tij, për të shpallur vullnetin e Tij. Ai, me të vërtetë, është i Lartë dhe i Urtë!');
INSERT INTO "hasan_efendi" VALUES(42,52,'42:52','Dhe kështu, Ne të kemi dërguar ty Shpallje me urdhrin dhe vullnetin Tonë. Ti më parë nuk e ke ditur se çfarë është Libri e as ç’është besimi. Por atë (Kuranin), Ne e bëmë dritë, me anë të së cilës udhëzojmë kë të duam nga robërit Tanë. Ti, me të vërtetë, udhëzon në rrugën e drejtë,');
INSERT INTO "hasan_efendi" VALUES(42,53,'42:53','(në) rrugën e Allahut, të Cilit i përket gjithçka që ndodhet në qiej dhe gjithçka që ndodhet në Tokë. Dhe vërtet, tek Allahu kthehen të gjitha çështjet!');
INSERT INTO "hasan_efendi" VALUES(43,1,'43:1','Hâ, Mîm.');
INSERT INTO "hasan_efendi" VALUES(43,2,'43:2','Betohem në Librin e qartë!');
INSERT INTO "hasan_efendi" VALUES(43,3,'43:3','Në të vërtetë, Ne e kemi shpallur Kuranin në gjuhën arabe, për ta kuptuar ju,');
INSERT INTO "hasan_efendi" VALUES(43,4,'43:4','e ai është te Ne, në Librin kryesor (Leuhi-mahfûdh), i lartësuar dhe plot urtësi.');
INSERT INTO "hasan_efendi" VALUES(43,5,'43:5','A ta tërheqim Përkujtimin (Kuranin) prej jush ngaqë jeni popull i shfrenuar?');
INSERT INTO "hasan_efendi" VALUES(43,6,'43:6','Eh, sa profetë u dërguam Ne breznive të mëparshme,');
INSERT INTO "hasan_efendi" VALUES(43,7,'43:7','por, sa herë që u vinte atyre ndonjë profet, ata talleshin me të!');
INSERT INTO "hasan_efendi" VALUES(43,8,'43:8','Prandaj Ne i shkatërruam ata, edhe pse ishin më të fuqishëm se këta (paganët e Mekës). Kështu, pra, është shembulli i popujve të lashtë.');
INSERT INTO "hasan_efendi" VALUES(43,9,'43:9','Nëse ti i pyet ata: “Kush i ka krijuar qiejt dhe Tokën?” Ata, me siguri do të thonë: “Ato i ka krijuar i Plotfuqishmi dhe i Gjithëdijshmi;');
INSERT INTO "hasan_efendi" VALUES(43,10,'43:10','Ai, që jua ka bërë Tokën djep dhe ka krijuar për ju rrugë në të, që të arrini atje ku synoni;');
INSERT INTO "hasan_efendi" VALUES(43,11,'43:11','Ai, që prej qiellit zbret ujë me masë e nëpërmjet tij ngjall tokën e vdekur -kështu do të ngriheni edhe ju (prej varreve)-;');
INSERT INTO "hasan_efendi" VALUES(43,12,'43:12','Ai, që ka krijuar çifte për çdo lloj krijese, që ka bërë anije e kafshë që t’i shaloni');
INSERT INTO "hasan_efendi" VALUES(43,13,'43:13','e të mbaheni fort mbi to, e pastaj t’i kujtoni dhuntitë e Zotit tuaj dhe të thoni: “Qoftë lavdëruar Ai që i nënshtroi këto për ne, se vetë ne këto nuk do të mund t’i kishim nën pushtet');
INSERT INTO "hasan_efendi" VALUES(43,14,'43:14','dhe ne, me siguri, do të kthehemi te Zoti ynë!”');
INSERT INTO "hasan_efendi" VALUES(43,15,'43:15','Ata i kanë quajtur si pjesë të Tij disa prej robërve të Tij! Me të vërtetë, njeriu është mohues i hapët.');
INSERT INTO "hasan_efendi" VALUES(43,16,'43:16','Vallë, prej krijesave të Veta, Ai të zgjedhë vajza e juve t’ju dhurojë djem?!');
INSERT INTO "hasan_efendi" VALUES(43,17,'43:17','Kur ndonjëri nga ata merr lajmin e mirë për lindjen e asaj, që ia veshin të Gjithëmëshirshmit (si pjesë e Tij) , atij i nxihet fytyra dhe pikëllohet.');
INSERT INTO "hasan_efendi" VALUES(43,18,'43:18','Vallë, a është ajo që rritet në stoli dhe e pafuqishme për të kundërshtuar (diçka që mund t’i vishet Allahut si pjesë e Tij)?!');
INSERT INTO "hasan_efendi" VALUES(43,19,'43:19','Engjëjt, të cilët janë robërit e të Gjithëmëshirshmit, ata i bëjnë për femra. A mos vallë ata kanë qenë të pranishëm gjatë krijimit të tyre?! Dëshmia e tyre do të shënohet dhe ata do të përgjigjen (për atë shpifje)!');
INSERT INTO "hasan_efendi" VALUES(43,20,'43:20','Dhe ata thonë: “Sikur të kishte dashur i Gjithëmëshirshmi, ne nuk do t’i kishim adhuruar ata .” Ata nuk dinë asgjë për këtë, ata vetëm gënjejnë.');
INSERT INTO "hasan_efendi" VALUES(43,21,'43:21','Vallë, a u kemi dhënë Ne atyre Libër para tij (Kuranit), të cilit ata i janë përmbajtur?');
INSERT INTO "hasan_efendi" VALUES(43,22,'43:22','Jo, por ata thonë: “Ne i kemi gjetur etërit tanë në këtë fe dhe ne po ndjekim gjurmët e tyre”.');
INSERT INTO "hasan_efendi" VALUES(43,23,'43:23','Po ashtu, në çdo vend që Ne kemi dërguar paralajmërues para teje, pasanikët e atij vendi thonin: “Ne i kemi gjetur etërit tanë në këtë fe dhe po ndjekim gjurmët e tyre”.');
INSERT INTO "hasan_efendi" VALUES(43,24,'43:24','(Çdo i dërguar) thoshte: “Po sikur unë t’ju kem sjellë (një fe) më të ndriçuar se ajo e të parëve tuaj?!” Ata thonin: “Ne e mohojmë mesazhin me të cilin jeni dërguar!”');
INSERT INTO "hasan_efendi" VALUES(43,25,'43:25','Atëherë, Ne hakmerreshim kundër atyre, prandaj shiko si ka qenë përfundimi i femohuesve.');
INSERT INTO "hasan_efendi" VALUES(43,26,'43:26','(Kujto) kur Ibrahimi i tha të atit dhe popullit të tij: “Në të vërtetë, Unë i urrej dhe s’kam të bëj fare me ato që adhuroni ju');
INSERT INTO "hasan_efendi" VALUES(43,27,'43:27','në vend të Atij që më ka krijuar dhe i Cili do të më udhëzojë në rrugën e drejtë”.');
INSERT INTO "hasan_efendi" VALUES(43,28,'43:28','Ai e bëri këtë fjalë të përhershme te pasardhësit e tij, me qëllim që ata të kthehen (vazhdimisht tek Allahu).');
INSERT INTO "hasan_efendi" VALUES(43,29,'43:29','Kurse Unë i lashë të kënaqen këta dhe etërit e tyre, derisa u erdhi e Vërteta (Kurani) dhe i Dërguari, që ua bëri të qarta.');
INSERT INTO "hasan_efendi" VALUES(43,30,'43:30','Por, kur u erdhi e Vërteta, ata thanë: “Kjo është magji dhe ne sigurisht që këtë nuk e besojmë!');
INSERT INTO "hasan_efendi" VALUES(43,31,'43:31','Sikur t’i kishte zbritur ky Kuran ndonjë njeriu të madh prej njërit nga këto dy qytete "!');
INSERT INTO "hasan_efendi" VALUES(43,32,'43:32','Vallë, a mos janë ata që e ndajnë mëshirën e Zotit tënd? Jemi Ne ata që ua ndajmë njerëzve mjetet e jetesës në këtë botë, duke i ngritur në shkallë disa më lart mbi të tjerët, me qëllim që disa t’u shërbejnë të tjerëve. Mëshira e Zotit tënd është më e mirë nga ajo që grumbullojnë ata.');
INSERT INTO "hasan_efendi" VALUES(43,33,'43:33','Dhe, sikur njerëzit të mos ishin një bashkësi mohuesish, Ne do t’i bënim prej argjendi çatitë e shtëpive të atyre që nuk besojnë në të Gjithëmëshirshmin , madje edhe shkallët nëpër të cilat ngjiten,');
INSERT INTO "hasan_efendi" VALUES(43,34,'43:34','edhe dyert e shtëpive të tyre, edhe divanet në të cilat mbështeten,');
INSERT INTO "hasan_efendi" VALUES(43,35,'43:35','dhe (do t’u jepnim) stoli prej ari. Por të gjitha këto janë vetëm kënaqësi të kësaj jete, kurse jeta tjetër te Zoti yt është për punëdrejtët që ruhen (nga gjynahet dhe nga dënimi i Allahut).');
INSERT INTO "hasan_efendi" VALUES(43,36,'43:36','Atij që shmanget nga Këshilla e të Gjithëmëshirshmit (Kurani), Ne do t’ia caktojmë një djall, që do t’i bëhet shok i pandashëm.');
INSERT INTO "hasan_efendi" VALUES(43,37,'43:37','Në të vërtetë, djajtë i shmangin jobesimtarët nga rruga e drejtë, ndonëse ata mendojnë se janë të udhëzuar drejt.');
INSERT INTO "hasan_efendi" VALUES(43,38,'43:38','Dhe, kur të vijë te Ne, (jobesimtari) do t’i thotë (djallit që e kishte shok): “Ah, sikur të kishte qenë largësia midis meje dhe teje sa ajo mes lindjes e perëndimit! Sa shok i keq paske qenë ti!”');
INSERT INTO "hasan_efendi" VALUES(43,39,'43:39','(Atyre do t’u thuhet): “Nuk do t’ju bëjë dobi (pendimi) juve sot , sepse ju u larguat nga e vërteta dhe humbët. Me të vërtetë, ju jeni pjesëtarë në dënim”.');
INSERT INTO "hasan_efendi" VALUES(43,40,'43:40','Vallë, a mos ti do të bësh të dëgjojë i shurdhri dhe të udhëzosh të verbrin dhe atë që është në humbje të qartë?');
INSERT INTO "hasan_efendi" VALUES(43,41,'43:41','Edhe sikur të të marrim ty, Ne, me siguri do të hakmerremi ndaj tyre (në jetën tjetër)');
INSERT INTO "hasan_efendi" VALUES(43,42,'43:42','ose do të të tregojmë atë çka u kemi premtuar atyre. Ne jemi vërtet të Plotfuqishëm mbi ta.');
INSERT INTO "hasan_efendi" VALUES(43,43,'43:43','Andaj, përmbaju fort asaj që të është shpallur ty, sepse ti, në të vërtetë, je në rrugën e drejtë.');
INSERT INTO "hasan_efendi" VALUES(43,44,'43:44','Pa dyshim që Kurani është përkujtesë për ty dhe popullin tënd; e ju do të pyeteni!');
INSERT INTO "hasan_efendi" VALUES(43,45,'43:45','Pyeti të dërguarit Tanë, që i kemi nisur para teje, a mos kemi urdhëruar Ne që të adhurohen zota të tjerë, përveç të Gjithëmëshirshmit?!');
INSERT INTO "hasan_efendi" VALUES(43,46,'43:46','Ne dërguam Musain me shenjat Tona te Faraoni dhe paria e tij, dhe ai tha: “Unë jam i dërguar i Zotit të botëve!”');
INSERT INTO "hasan_efendi" VALUES(43,47,'43:47','Kur ai u solli atyre shenjat Tona, ata filluan t’i përqeshin ato.');
INSERT INTO "hasan_efendi" VALUES(43,48,'43:48','Çdo shenjë që Ne u tregonim atyre, ishte më e madhe se ajo para saj. Prandaj Ne i dënonim ata me ndëshkim, që të ktheheshin (në rrugën e drejtë).');
INSERT INTO "hasan_efendi" VALUES(43,49,'43:49','Ata i thoshin (Musait): “O magjistar! Lutju Zotit tënd për ne, ashtu sipas besëlidhjes tënde me Atë, sepse ne do të kthehemi në rrugë të drejtë!”');
INSERT INTO "hasan_efendi" VALUES(43,50,'43:50','Por, sapo ua largonim dënimin, ata e shkelnin premtimin.');
INSERT INTO "hasan_efendi" VALUES(43,51,'43:51','Dhe Faraoni i thërriste popullit të vet: “O populli im! Vallë, a nuk më përket mua sundimi i Egjiptit dhe i këtyre lumenjve që rrjedhin pranë meje? A nuk shihni?');
INSERT INTO "hasan_efendi" VALUES(43,52,'43:52','Vallë, a nuk jam unë më i mirë se ky i mjerë, i cili mezi mund të shprehet?');
INSERT INTO "hasan_efendi" VALUES(43,53,'43:53','Përse nuk i janë dhënë atij byzylykë ari? Përse nuk kanë ardhur bashkë me të engjëjt?”');
INSERT INTO "hasan_efendi" VALUES(43,54,'43:54','Kështu, ai e mashtroi popullin e vet. Ata iu nënshtruan atij, sepse ishin vërtet një popull i pabindur.');
INSERT INTO "hasan_efendi" VALUES(43,55,'43:55','E, kur Na zemëruan, Ne u hakmorëm ndaj tyre dhe i fundosëm të gjithë.');
INSERT INTO "hasan_efendi" VALUES(43,56,'43:56','Ne i bëmë ata shembull dhe mësim për popujt e mëvonshëm.');
INSERT INTO "hasan_efendi" VALUES(43,57,'43:57','Kur u soll si shembull i biri i Merjemes (Isai a.s.), populli yt (o Muhamed) menjëherë nisi të qeshë');
INSERT INTO "hasan_efendi" VALUES(43,58,'43:58','duke thënë: “A janë më të mirë idhujt tanë apo ai?” Ata ta paraqesin këtë shembull vetëm për shkak se duan të hahen, sepse janë popull grindavec.');
INSERT INTO "hasan_efendi" VALUES(43,59,'43:59','Isai ishte vetëm një rob, të cilit Ne i dhuruam (Profeci) dhe e bëmë shembull për bijtë e Israilit.');
INSERT INTO "hasan_efendi" VALUES(43,60,'43:60','Sikur të duam, Ne (do t’ju shkatërronim dhe) në vendin tuaj do të bënim engjëj, që do t’ju trashëgonin në Tokë.');
INSERT INTO "hasan_efendi" VALUES(43,61,'43:61','Në të vërtetë, Isai është shenjë e Orës (së Kiametit), në ardhjen e së cilës mos dyshoni kurrsesi. Pra, ndiqmëni mua, kjo është rruga e drejtë!');
INSERT INTO "hasan_efendi" VALUES(43,62,'43:62','Le të mos ju shmangë kurrsesi djalli! Ai është vërtet armiku juaj i hapët.');
INSERT INTO "hasan_efendi" VALUES(43,63,'43:63','E, kur Isai solli prova të qarta, tha: “Ju kam sjellë urtësinë (Profecinë) dhe do t’ju shpjegoj disa gjëra, për të cilat nuk po pajtoheni. Andaj, frikësojuni Allahut dhe bindmuni mua!');
INSERT INTO "hasan_efendi" VALUES(43,64,'43:64','Pa dyshim, Allahu është Zoti im dhe Zoti juaj, andaj adhuroni vetëm Atë! Kjo është rruga e drejtë!”');
INSERT INTO "hasan_efendi" VALUES(43,65,'43:65','E megjithatë, sektet e ndryshme patën kundërshti midis tyre. Mjerë ata që kanë bërë padrejtësi – për dënimin e Ditës së dhembshme!');
INSERT INTO "hasan_efendi" VALUES(43,66,'43:66','A mos presin ata diçka tjetër, përveç Orës (së Kiametit), që do t’u vijë papritmas e pa e ndier fare?!');
INSERT INTO "hasan_efendi" VALUES(43,67,'43:67','Atë Ditë miqtë do të bëhen armiq të njëri-tjetrit, përveç të drejtëve, që e kanë pasur frikë Allahun.');
INSERT INTO "hasan_efendi" VALUES(43,68,'43:68','(Allahu do t’u thotë): “O robërit e Mi, për ju sot nuk ka frikë dhe as që do të pikëlloheni.');
INSERT INTO "hasan_efendi" VALUES(43,69,'43:69','Ju që keni besuar shpalljet Tona dhe keni qenë myslimanë, ');
INSERT INTO "hasan_efendi" VALUES(43,70,'43:70','hyni në Xhenet të gëzuar, ju dhe bashkëshortet tuaja,');
INSERT INTO "hasan_efendi" VALUES(43,71,'43:71','ku do t’ju shërbehet me pjata dhe gota prej ari. Aty do të ketë çfarë t’ju dojë shpirti dhe gjithçka që ta kënaq syrin dhe atje do të qëndroni përgjithmonë.');
INSERT INTO "hasan_efendi" VALUES(43,72,'43:72','Ja, ky është Xheneti që ju është dhënë në trashëgim për atë që keni bërë.');
INSERT INTO "hasan_efendi" VALUES(43,73,'43:73','Aty do të keni fruta të shumëllojshme, prej të cilave do të hani.”');
INSERT INTO "hasan_efendi" VALUES(43,74,'43:74','Sa për punëmbrapshtët, pa dyshim që ata do të qëndrojnë përherë në dënimin e Xhehenemit.');
INSERT INTO "hasan_efendi" VALUES(43,75,'43:75','Ai (dënim) nuk do t’u lehtësohet atyre e kurrfarë shprese për shpëtim nuk do të kenë.');
INSERT INTO "hasan_efendi" VALUES(43,76,'43:76','Ne atyre nuk u bëmë padrejtësi, por ata vetë i bënë keq vetvetes.');
INSERT INTO "hasan_efendi" VALUES(43,77,'43:77','Ata do të thërrasin: “O Malik ! (Më mirë) Le të na vdesë Zoti yt!”, ndërsa Ai do të thotë: “Ju do të qëndroni përherë aty!”');
INSERT INTO "hasan_efendi" VALUES(43,78,'43:78','Tashmë, Ne ju kemi sjellë të Vërtetën, por shumica prej jush e urren të Vërtetën.');
INSERT INTO "hasan_efendi" VALUES(43,79,'43:79','Nëse ata kurdisin kurthe, edhe Ne do t’u ngremë kurthe.');
INSERT INTO "hasan_efendi" VALUES(43,80,'43:80','Vallë, a mos kujtojnë ata se Ne nuk i dëgjojmë fjalët e tyre tinëzare dhe bisedat e tyre të fshehta? Pa dyshim, Ne dëgjojmë, ndërsa të dërguarit Tanë (engjëj) që janë pranë tyre, shënojnë.');
INSERT INTO "hasan_efendi" VALUES(43,81,'43:81','Thuaju (o Muhamed): “Sikur i Gjithëmëshirshmi të kishte bir, Unë do të isha i pari që do ta adhuroja atë!');
INSERT INTO "hasan_efendi" VALUES(43,82,'43:82','Qoftë i lartësuar Zoti i qiejve dhe i Tokës, Zoti i Fronit, nga ajo çfarë i veshin Atij!');
INSERT INTO "hasan_efendi" VALUES(43,83,'43:83','Lëri ata të flasin marrëzi dhe le të argëtohen derisa të takojnë Ditën që u është premtuar.');
INSERT INTO "hasan_efendi" VALUES(43,84,'43:84','Ai është i vetmi Zot (i vërtetë) në qiell dhe i vetmi Zot (i vërtetë) në Tokë. Ai është i Urtë dhe i Gjithëdijshëm.');
INSERT INTO "hasan_efendi" VALUES(43,85,'43:85','Qoftë i lartësuar në lavdi Ai, të Cilit i përket pushteti i qiejve dhe i Tokës dhe gjithçka që gjendet mes tyre. Vetëm Ai ka dijeni mbi Orën (e Kiametit) dhe tek Ai do të ktheheni!');
INSERT INTO "hasan_efendi" VALUES(43,86,'43:86','Ata që u adhuruan në vend të Allahut, nuk do të mund të ndërmjetësojnë për askënd, përveç atyre që dëshmuan për të Vërtetën dhe e dinë mirë (se s’ka Zot tjetër të vërtetë veç Allahut).');
INSERT INTO "hasan_efendi" VALUES(43,87,'43:87','Nëse i pyet ata se kush i ka krijuar, me siguri që ata do të thonë: “Allahu!” Atëherë, përse largohen prej Tij?!');
INSERT INTO "hasan_efendi" VALUES(43,88,'43:88','I Dërguari thotë: “O Zoti im, ky është një popull që nuk beson!”');
INSERT INTO "hasan_efendi" VALUES(43,89,'43:89','Largohu prej tyre (o Muhamed) dhe thuaju: “Paqe!” Ata së shpejti do ta marrin vesh.');
INSERT INTO "hasan_efendi" VALUES(44,1,'44:1','Hâ, Mîm.');
INSERT INTO "hasan_efendi" VALUES(44,2,'44:2','Betohem në Librin e qartë!');
INSERT INTO "hasan_efendi" VALUES(44,3,'44:3','Ne e zbritëm Kuranin në një natë të bekuar . Vërtet, Ne gjithmonë kemi qenë paralajmërues.');
INSERT INTO "hasan_efendi" VALUES(44,4,'44:4','Në atë (natë të zbritjes së Kuranit) vendoset çdo punë e urtë,');
INSERT INTO "hasan_efendi" VALUES(44,5,'44:5','me urdhrin Tonë! Ne gjithmonë kemi nisur të dërguar,');
INSERT INTO "hasan_efendi" VALUES(44,6,'44:6','si mëshirë prej Zotit tënd. Me të vërtetë, Ai i dëgjon të gjitha dhe i di të gjitha.');
INSERT INTO "hasan_efendi" VALUES(44,7,'44:7','Ai është Zoti i qiejve, i Tokës dhe i gjithçkaje që gjendet midis tyre, nëse besoni me bindje.');
INSERT INTO "hasan_efendi" VALUES(44,8,'44:8','S’ka zot tjetër të vërtetë përveç Atij. Ai e jep jetën dhe e shkakton vdekjen. Ai është Zoti juaj dhe Zoti i të parëve tuaj të lashtë!');
INSERT INTO "hasan_efendi" VALUES(44,9,'44:9','Por ata argëtohen duke dyshuar.');
INSERT INTO "hasan_efendi" VALUES(44,10,'44:10','Andaj ti prite atë (ditë), kur qielli do të lëshojë tym të qartë,');
INSERT INTO "hasan_efendi" VALUES(44,11,'44:11','që do t’i mbulojë njerëzit, (e do t’u thuhet): “Ky është dënim i dhembshëm!”');
INSERT INTO "hasan_efendi" VALUES(44,12,'44:12','(Mohuesit do të thonë): “O Zoti ynë, lirona prej dënimit! Ne tashmë besojmë!”');
INSERT INTO "hasan_efendi" VALUES(44,13,'44:13','E si mund të përfitojnë ata nga këshilla, ndërkohë që më parë atyre u pat ardhur një i Dërguar me shpjegime të qarta');
INSERT INTO "hasan_efendi" VALUES(44,14,'44:14','e ata i kthyen shpinën, duke thënë: “Ai është një i çmendur i mësuar nga të tjerët”?!');
INSERT INTO "hasan_efendi" VALUES(44,15,'44:15','Edhe sikur t’ju lirojmë nga dënimi pak (kohë), ju me siguri që do të ktheheni (në mosbesim).');
INSERT INTO "hasan_efendi" VALUES(44,16,'44:16','Ditën, kur do t’i mbërthejmë ata me forcën më të madhe, Ne do të hakmerremi.');
INSERT INTO "hasan_efendi" VALUES(44,17,'44:17','Përpara tyre Ne vumë në sprovë popullin e Faraonit, kur u pat ardhur i Dërguari fisnik,');
INSERT INTO "hasan_efendi" VALUES(44,18,'44:18','(i cili u tha): “Më sillni robërit e Allahut, sepse Unë jam për ju një i dërguar i besuar!');
INSERT INTO "hasan_efendi" VALUES(44,19,'44:19','Dhe mos u madhëroni mbi Allahun, sepse unë po ju sjell provë të qartë!');
INSERT INTO "hasan_efendi" VALUES(44,20,'44:20','Unë kërkoj mbështetje në Zotin tim dhe Zotin tuaj, që të mos më vrisni me gurë!');
INSERT INTO "hasan_efendi" VALUES(44,21,'44:21','Nëse nuk më besoni, atëherë largohuni prej meje!”');
INSERT INTO "hasan_efendi" VALUES(44,22,'44:22','Dhe ai iu lut Zotit të vet: “Ky është vërtet një popull keqbërës!”');
INSERT INTO "hasan_efendi" VALUES(44,23,'44:23','(Allahu i tha): “Ec bashkë me robërit e Mi natën, sepse ata do t’ju ndjekin!');
INSERT INTO "hasan_efendi" VALUES(44,24,'44:24','Lëreni pas detin e hapur, se ata, me siguri, do të fundosen si ushtri”!');
INSERT INTO "hasan_efendi" VALUES(44,25,'44:25','Eh, sa e sa kopshte dhe burime i lanë ata,');
INSERT INTO "hasan_efendi" VALUES(44,26,'44:26','sa ara të mbjella dhe ndërtesa fisnike,');
INSERT INTO "hasan_efendi" VALUES(44,27,'44:27','dhe sa begati, në të cilat kënaqeshin!');
INSERT INTO "hasan_efendi" VALUES(44,28,'44:28','Kështu, Ne ua lamë trashëgim ato një populli tjetër.');
INSERT INTO "hasan_efendi" VALUES(44,29,'44:29','Nuk i vajtoi ata as qielli, as Toka, as nuk iu dha afat atyre.');
INSERT INTO "hasan_efendi" VALUES(44,30,'44:30','Ne i shpëtuam bijtë e Israilit nga dënimi poshtërues');
INSERT INTO "hasan_efendi" VALUES(44,31,'44:31','prej Faraonit, që ishte tiran, njeri që kalonte çdo kufi të së keqes.');
INSERT INTO "hasan_efendi" VALUES(44,32,'44:32','Ne i zgjodhëm ata, me qëllim mbi popujt e tjerë (të asaj kohe)');
INSERT INTO "hasan_efendi" VALUES(44,33,'44:33','dhe u dhamë shenja, në të cilat ka sprova të qarta.');
INSERT INTO "hasan_efendi" VALUES(44,34,'44:34','E megjithatë, jobesimtarët thonë:');
INSERT INTO "hasan_efendi" VALUES(44,35,'44:35','“Ekziston vetëm vdekja jonë e parë, ne nuk do të ringjallemi!');
INSERT INTO "hasan_efendi" VALUES(44,36,'44:36','Na i risillni, pra, të parët tanë, nëse thoni të vërtetën!”');
INSERT INTO "hasan_efendi" VALUES(44,37,'44:37','A janë këta më të mirë se populli i Tubbas apo ata para tyre? Ne i shkatërruam ata, sepse ishin vërtet punëmbrapshtë.');
INSERT INTO "hasan_efendi" VALUES(44,38,'44:38','Ne nuk i kemi krijuar qiejt, Tokën dhe çfarë gjendet midis tyre, për t’u zbavitur!');
INSERT INTO "hasan_efendi" VALUES(44,39,'44:39','Ne i kemi krijuar ato me qëllim të plotë, por shumica e tyre nuk e dinë.');
INSERT INTO "hasan_efendi" VALUES(44,40,'44:40','Me të vërtetë, Dita e Gjykimit, është e caktuar për të gjithë ata.');
INSERT INTO "hasan_efendi" VALUES(44,41,'44:41','Atë Ditë, i afërmi nuk mund ta ndihmojë aspak të afërmin e as ata vetë nuk do t’i ndihmojë kush,');
INSERT INTO "hasan_efendi" VALUES(44,42,'44:42','me përjashtim të atyre që i ka mëshiruar Allahu. Ai është vërtet i Plotfuqishëm dhe Mëshirëplotë.');
INSERT INTO "hasan_efendi" VALUES(44,43,'44:43','Pema e zekumit');
INSERT INTO "hasan_efendi" VALUES(44,44,'44:44','do të jetë ushqim i gjynahqarit.');
INSERT INTO "hasan_efendi" VALUES(44,45,'44:45','Si tunxh i shkrirë ajo do të vlojë në barkun e tij,');
INSERT INTO "hasan_efendi" VALUES(44,46,'44:46','ashtu si vlon uji i nxehtë.');
INSERT INTO "hasan_efendi" VALUES(44,47,'44:47','(Engjëjve do t’u thuhet): “Rrëmbejeni atë dhe çojeni në mes të zjarrit flakërues!');
INSERT INTO "hasan_efendi" VALUES(44,48,'44:48','Pastaj, për dënim, hidhni mbi kokën e tij ujë të valuar (duke i thënë):');
INSERT INTO "hasan_efendi" VALUES(44,49,'44:49','“Shijoje këtë, o njeri “i fuqishëm” dhe “fisnik”!');
INSERT INTO "hasan_efendi" VALUES(44,50,'44:50','Ja, ky është dënimi për të cilin ju dyshonit!”');
INSERT INTO "hasan_efendi" VALUES(44,51,'44:51','Punëdrejtët, me të vërtetë, do të jenë në vend të sigurt,');
INSERT INTO "hasan_efendi" VALUES(44,52,'44:52','midis kopshteve dhe burimeve,');
INSERT INTO "hasan_efendi" VALUES(44,53,'44:53','të veshur në petka mëndafshi të hollë dhe armaç, të ulur përballë njëri-tjetrit.');
INSERT INTO "hasan_efendi" VALUES(44,54,'44:54','Po, kështu do të jetë, dhe Ne do t’i martojmë ata me hyri sybukura.');
INSERT INTO "hasan_efendi" VALUES(44,55,'44:55','Aty do të kërkojnë çfarëdo frutash që të dëshirojnë e do të jenë të sigurt.');
INSERT INTO "hasan_efendi" VALUES(44,56,'44:56','Atje nuk do ta provojnë më vdekjen, përveç vdekjes së parë dhe Ai do t’i shpëtojë prej dënimit të zjarrit flakërues.');
INSERT INTO "hasan_efendi" VALUES(44,57,'44:57','Kjo do të jetë dhunti prej Zotit tënd. Kjo është fitorja madhështore!');
INSERT INTO "hasan_efendi" VALUES(44,58,'44:58','Ne e kemi bërë të lehtë Kuranin në gjuhën tënde, që ata të mund të marrin këshilla.');
INSERT INTO "hasan_efendi" VALUES(44,59,'44:59','Andaj ti prit, se edhe ata vetë po presin!');
INSERT INTO "hasan_efendi" VALUES(45,1,'45:1','Hâ, Mîm.');
INSERT INTO "hasan_efendi" VALUES(45,2,'45:2','Ky Libër është shpallur prej Allahut, të Plotfuqishmit dhe të Urtit!');
INSERT INTO "hasan_efendi" VALUES(45,3,'45:3','Me të vërtetë, në qiej dhe në Tokë ka shenja për ata që besojnë.');
INSERT INTO "hasan_efendi" VALUES(45,4,'45:4','Edhe në krijimin tuaj dhe të kafshëve të shpërndara nëpër Tokë, ka shenja për besimtarët e bindur.');
INSERT INTO "hasan_efendi" VALUES(45,5,'45:5','Edhe në ndërrimin e natës dhe të ditës, edhe në ujin që Allahu e zbret nga qielli, duke gjallëruar nëpërmjet tij tokën e shkretuar, edhe në ndërrimin e erërave, ka shenja për njerëzit që mendojnë.');
INSERT INTO "hasan_efendi" VALUES(45,6,'45:6','Këto janë shpalljet e Allahut, të cilat Ne t’i tregojmë ty me tërë të vërtetën. Atëherë, në çfarë fjalësh të tjera do të besojnë ata, nëse mohojnë Allahun dhe shenjat e Tij?!');
INSERT INTO "hasan_efendi" VALUES(45,7,'45:7','Mjerë gjynahqari gënjeshtar!');
INSERT INTO "hasan_efendi" VALUES(45,8,'45:8','Ai i dëgjon shpalljet e Allahut që i lexohen e përsëri ngulmon në mendjemadhësi, sikur nuk i ka dëgjuar ato; jepi lajmin për dënim të dhembshëm!');
INSERT INTO "hasan_efendi" VALUES(45,9,'45:9','E, kur merr vesh diçka nga shpalljet Tona, ai i përqesh. Për njerëz të tillë do të ketë dënim poshtërues.');
INSERT INTO "hasan_efendi" VALUES(45,10,'45:10','Para tyre është Xhehenemi dhe nuk do t’u vijë në ndihmë asgjë nga ato që kanë fituar apo nga ata (idhuj) që i kanë marrë për mbrojtës në vend të Allahut! Ata i pret mundim i madh.');
INSERT INTO "hasan_efendi" VALUES(45,11,'45:11','Ky (Kuran) është udhërrëfyes në rrugën e drejtë. Ata që nuk besojnë shpalljet e Zotit të tyre, do të vuajnë një mundim të madh, të dhembshëm.');
INSERT INTO "hasan_efendi" VALUES(45,12,'45:12','Allahu është Ai që ka nënshtruar për ju detin, që nëpër të, me urdhrin e Tij, të lundrojnë anijet, për të kërkuar dhuntitë e Tij dhe që ju të jeni mirënjohës.');
INSERT INTO "hasan_efendi" VALUES(45,13,'45:13','Ai ka vënë në shërbimin tuaj çka gjendet në qiej dhe në Tokë, të gjitha janë prej Tij. Me të vërtetë, në këto ka shenja për njerëzit që mendojnë.');
INSERT INTO "hasan_efendi" VALUES(45,14,'45:14','Thuaju besimtarëve, që t’i falin ata që nuk shpresojnë në ditët e Allahut , kështu që Ai të shpërblejë çdo popull sipas veprave të veta.');
INSERT INTO "hasan_efendi" VALUES(45,15,'45:15','Kush bën mirë, e ka për vete e kush bën keq, e bën kundër vetes; e në fund do të ktheheni (të gjithë) te Zoti juaj.');
INSERT INTO "hasan_efendi" VALUES(45,16,'45:16','Ne u dhamë bijve të Israilit Librin, pushtetin dhe Profecinë. Ne i begatuam ata me gjëra të mira dhe i ngritëm mbi të gjithë popujt e tjerë (të asaj kohe).');
INSERT INTO "hasan_efendi" VALUES(45,17,'45:17','Gjithashtu, Ne u dhamë udhëzime të qarta mbi besimin, por ata u përçanë, atëherë kur u erdhi dituria, pasi patën smirë në mes tyre. Zoti yt, me siguri, do t’i gjykojë (ata) në Ditën e Kiametit për kundërshtitë që patën.');
INSERT INTO "hasan_efendi" VALUES(45,18,'45:18','Dhe tani, Ne të kemi vendosur ty në rrugën e drejtë të fesë, andaj ndiqe atë dhe mos shko pas dëshirave të atyre që nuk dinë!');
INSERT INTO "hasan_efendi" VALUES(45,19,'45:19','Ata nuk mund të të mbrojnë aspak nga zemërimi i Allahut. Pa dyshim, keqbërësit kanë për mbrojtës njëri-tjetrin, kurse Allahu është Mbrojtësi i atyre që i frikësohen Atij e që ruhen nga gjynahet.');
INSERT INTO "hasan_efendi" VALUES(45,20,'45:20','Ky Kuran është tregues i qartë, udhërrëfyes dhe mëshirë për njerëzit që kanë besim të patundur.');
INSERT INTO "hasan_efendi" VALUES(45,21,'45:21','A mos mendojnë ata që bëjnë vepra të këqija, se Ne do t’i bëjmë të njëjtë me ata që kanë besuar dhe që kanë bërë vepra të mira, se jeta dhe vdekja e tyre do të jetë e njëjtë? Sa keq që gjykojnë ata!');
INSERT INTO "hasan_efendi" VALUES(45,22,'45:22','Allahu i krijoi qiejt dhe Tokën me qëllim të plotë, në mënyrë që çdo njeri të jetë i shpërblyer për atë që ka punuar. E, askujt nuk do t’i bëhet padrejtësi.');
INSERT INTO "hasan_efendi" VALUES(45,23,'45:23','Mendo pak! Kush, përveç Allahut, mund ta udhëzojë atë njeri, që dëshirat e veta i ka bërë zot; atë njeri, të cilin Allahu, duke e njohur për të tillë, e ka shpënë në humbje, duke ia vulosur dëgjimin dhe zemrën e duke i vënë perde para syve të tij?! A nuk po merrni vesh?');
INSERT INTO "hasan_efendi" VALUES(45,24,'45:24','Ata thonë: “Nuk ka tjetër veç jetës së kësaj bote, vdesim e jetojmë dhe vetëm koha na shkatërron”. Ata nuk kanë kurrfarë dijenie për këtë, ata vetëm hamendësojnë ashtu.');
INSERT INTO "hasan_efendi" VALUES(45,25,'45:25','Kur atyre u lexohen shpalljet Tona të qarta, argumenti i tyre i vetëm është: “Ringjallni të parët tanë, nëse thoni të vërtetën!”');
INSERT INTO "hasan_efendi" VALUES(45,26,'45:26','Thuaju: “Allahu jua jep jetën, pastaj ju bën të vdisni e mandej do t’ju tubojë në Ditën e Kiametit e për këtë nuk ka kurrfarë dyshimi; por shumica e njerëzve nuk e dinë”.');
INSERT INTO "hasan_efendi" VALUES(45,27,'45:27','I Allahut është pushteti i qiejve dhe i Tokës! E kur të arrijë Ora (e Kiametit), atë ditë mohuesit do të humbasin.');
INSERT INTO "hasan_efendi" VALUES(45,28,'45:28','Atëherë të gjithë popujt do t’i shohësh të gjunjëzuar. Çdo popull do të thirret në Librin e vet (e do t’i thuhet): “Sot do të shpërbleheni për atë që keni bërë.');
INSERT INTO "hasan_efendi" VALUES(45,29,'45:29','Ky është Libri Ynë, që do t’ju tregojë të vërtetën. Ne i kemi urdhëruar (engjëjt) që të shkruajnë të gjitha ç’keni bërë”.');
INSERT INTO "hasan_efendi" VALUES(45,30,'45:30','Për sa u përket atyre që kanë besuar dhe kanë bërë vepra të mira, ata do t’i pranojë Zoti i tyre në mëshirën e Vet . Kjo është fitorja e lavdishme!');
INSERT INTO "hasan_efendi" VALUES(45,31,'45:31','Ndërsa mohuesve do t’u thuhet: “Vallë, a nuk ju janë lexuar shpalljet e Mia?! Por ju shfaqët mendjemadhësi dhe u bëtë njerëz të këqij.');
INSERT INTO "hasan_efendi" VALUES(45,32,'45:32','Dhe, kur juve ju thuhej: “Premtimi i Allahut është i vërtetë: Ora (e Kiametit) pa dyshim që do të vijë!” - ju thoshit: “Ne nuk e dimë ç’është Ora (e Kiametit). Ne mendojmë se është vetëm një hamendje, prandaj nuk jemi të bindur;”');
INSERT INTO "hasan_efendi" VALUES(45,33,'45:33','Natyrisht, atyre do t’u dalin në shesh të këqijat që kanë bërë e do t’u bjerë kokës ajo me të cilën talleshin.');
INSERT INTO "hasan_efendi" VALUES(45,34,'45:34','Pastaj do t’u thuhet: “Ne sot po ju braktisim në harresë, ashtu siç e keni harruar ju takimin e kësaj dite. Vendqëndrimi juaj është Zjarri dhe për ju nuk ka kurrfarë ndihmësi.');
INSERT INTO "hasan_efendi" VALUES(45,35,'45:35','Ky dënim ju erdhi ngaqë i përqeshnit shpalljet e Allahut dhe u mashtruat nga jeta e kësaj bote.” Atë ditë ata nuk do të nxirren nga zjarri e nuk do të pranohet prej tyre asnjë arsyetim.');
INSERT INTO "hasan_efendi" VALUES(45,36,'45:36','Falënderimi i përket vetëm Allahut, Zotit të qiejve dhe Zotit të Tokës - Zotit të gjithësisë.');
INSERT INTO "hasan_efendi" VALUES(45,37,'45:37','Atij i përket e tërë madhështia në qiej dhe në Tokë. Ai është i Plotfuqishmi dhe i Urti.');
INSERT INTO "hasan_efendi" VALUES(46,1,'46:1','Hâ, Mîm.');
INSERT INTO "hasan_efendi" VALUES(46,2,'46:2','Ky Libër është shpallur prej Allahut, të Plotfuqishmit dhe të Urtit!');
INSERT INTO "hasan_efendi" VALUES(46,3,'46:3','Ne i kemi krijuar qiejt dhe Tokën dhe çfarë gjendet midis tyre me qëllim të plotë dhe me afat të caktuar. E megjithatë, mohuesit shmangen nga ajo që u është paralajmëruar.');
INSERT INTO "hasan_efendi" VALUES(46,4,'46:4','Thuaju: “A i shihni ata që i adhuroni në vend të Allahut? Më tregoni, çfarë pjese të Tokës kanë krijuar ata?! Mos vallë kanë ndonjë pjesë në krijimin e qiejve?! Më sillni një libër të shpallur para këtij (Kuranit) ose ndonjë gjurmë tjetër të dijes (hyjnore), nëse flisni të vërtetën”!');
INSERT INTO "hasan_efendi" VALUES(46,5,'46:5','Kush është më i humbur se ai, i cili, në vend Allahut, u lutet atyre që nuk mund t’u përgjigjen deri në Ditën e Kiametit e që janë krejt të pavetëdijshëm ndaj lutjes së tyre?!');
INSERT INTO "hasan_efendi" VALUES(46,6,'46:6','Kur të tubohen njerëzit (në Ditën e Gjykimit), idhujt do të jenë armiq të tyre dhe do ta mohojnë adhurimin e tyre.');
INSERT INTO "hasan_efendi" VALUES(46,7,'46:7','Kur mohuesve u lexohen shpalljet Tona të qarta, ata thonë për të Vërtetën (Kuranin) që u vjen: “Kjo është magji e qartë!”');
INSERT INTO "hasan_efendi" VALUES(46,8,'46:8','Ose ata thonë: “Ai (Muhamedi) e ka trilluar vetë atë (Kuranin).” Thuaju: “Por, nëse unë e trilloj atë, ju nuk do të mund të më mbroni nga dënimi i Allahut. Ai i di më së miri të gjitha shpifjet tuaja për të (Kuranin). Mjafton Ai dëshmitar ndërmjet meje dhe jush. Ai është Falës dhe Mëshirëplotë”.');
INSERT INTO "hasan_efendi" VALUES(46,9,'46:9','Thuaju: “Unë nuk jam i pari i dërguar dhe nuk e di çfarë do të ndodhë me mua ose me ju. Unë ndjek vetëm atë që më është shpallur dhe kam për detyrë vetëm të jap paralajmërime të qarta”.');
INSERT INTO "hasan_efendi" VALUES(46,10,'46:10','Thuaju: “Më tregoni, se ç’do të bëhet me ju, nëse Kurani është vërtet prej Allahut e ju e mohoni, ndërkohë që një dëshmitar nga bijtë e Israilit dëshmon për vërtetësinë e tij dhe e beson atë, kurse ju tregoheni kryelartë?!” Me të vërtetë, Allahu, nuk i udhëzon (në rrugë të drejtë) njerëzit e padrejtë.');
INSERT INTO "hasan_efendi" VALUES(46,11,'46:11','Mohuesit thonë për besimtarët: “Nëse (Kurani) do të ishte ndonjë gjë e mirë, ata nuk do ta kishin besuar atë para nesh!” E përderisa ata nuk e pranojnë udhëzimin e tij, ata thonë: “Ky është një trillim i lashtë”.');
INSERT INTO "hasan_efendi" VALUES(46,12,'46:12','Para Kuranit është shpallur Libri i Musait, një udhërrëfyes dhe mëshirë për gjithë njerëzimin. Ky Libër (Kurani) e vërteton atë. Ai është shpallur në gjuhën arabe për t’i paralajmëruar ata që bëjnë të këqija dhe për t’u sjellë lajme të mira punëdrejtëve.');
INSERT INTO "hasan_efendi" VALUES(46,13,'46:13','Pa dyshim, ata që thonë: “Zoti ynë është Allahu!” dhe pastaj qëndrojnë në rrugën e drejtë, nuk do të kenë arsye për t’u frikësuar e as për t’u dëshpëruar (kur t’u vijë vdekja).');
INSERT INTO "hasan_efendi" VALUES(46,14,'46:14','Ata do të jenë banorë të Xhenetit, ku do të qëndrojnë përgjithmonë, si shpërblim për atë që kanë punuar.');
INSERT INTO "hasan_efendi" VALUES(46,15,'46:15','Njeriun e kemi porositur të jetë i sjellshëm ndaj prindërve të vet. Nëna e mbart atë me mund dhe me mund e lind. Ai mbartet dhe ushqehet me gji për tridhjetë muaj dhe, kur arrin në moshën e burrërisë e i mbush dyzet vjet, ai thotë: “O Zoti im! Më frymëzo që të të falënderoj për begatitë që Ti më ke dhënë mua dhe prindërve të mi dhe që të bëj vepra të mira, me të cilat Ti do të jesh i kënaqur! Më dhuro pasardhës të mirë! Tek Ti kthehem i penduar dhe Ty të dorëzohem!”.');
INSERT INTO "hasan_efendi" VALUES(46,16,'46:16','Këta janë ata njerëz, të cilëve do t’ua pranojmë veprat e tyre më të mira e nuk do t’ua vëmë re veprat e tyre të këqija. Ata do të jenë banorë të Xhenetit; ky është premtimi i vërtetë që u është premtuar.');
INSERT INTO "hasan_efendi" VALUES(46,17,'46:17','Mirëpo ndonjëri u thotë prindërve të vet: “Uf ju! A po më premtoni se do të ringjallem, ndonëse para meje kanë kaluar shumë brezni?!” Kur ata i drejtohen Allahut për ndihmë, (duke i thënë djalit): “Mjerë për ty! Beso, sepse premtimi i Allahut është i vërtetë!”, ai përgjigjet: “Këto janë vetëm përrallat e popujve të lashtë!”');
INSERT INTO "hasan_efendi" VALUES(46,18,'46:18','Njerëz të tillë do të pësojnë fatin e kombeve të kaluara prej xhindeve dhe njerëzve, ndaj të cilëve u përmbush Fjala (e dënimit). Vërtet, ata janë përherë të humburit.');
INSERT INTO "hasan_efendi" VALUES(46,19,'46:19','Për të gjithë do të ketë shkallë të caktuara, sipas asaj që kanë punuar, në mënyrë që Allahu t’i shpërblejë për veprat e tyre. Askujt nuk do t’i bëhet padrejtësi.');
INSERT INTO "hasan_efendi" VALUES(46,20,'46:20','Ditën, kur ata që kanë mohuar do të vihen para Zjarrit (do t’u thuhet): “Ju keni shfrytëzuar gjithë të mirat në jetën tuaj tokësore dhe jeni kënaqur me to, ndërsa sot do të ndëshkoheni me dënim poshtërues, sepse keni qenë arrogantë në Tokë pa kurrfarë të drejte dhe nuk i jeni bindur Allahut”.');
INSERT INTO "hasan_efendi" VALUES(46,21,'46:21','Kujtoje vëllanë e fisit Ad (Hudin), kur e paralajmëroi popullin e vet në Ahkaf, ndërsa ka pasur paralajmërues edhe para tij, edhe pas tij (kur u tha): “Adhuroni vetëm Allahun! Unë, me të vërtetë, kam frikë se do t’ju godasë dënimi i Ditës së Madhe!”');
INSERT INTO "hasan_efendi" VALUES(46,22,'46:22','Ata u përgjigjën: “Vallë, a ke ardhur të na largosh ne prej hyjnive tona. Sillna atë (dënim) që po na premton dhe të shohim nëse thua të vërtetën!”');
INSERT INTO "hasan_efendi" VALUES(46,23,'46:23','Ai u tha (atyre): “Vetëm Allahu e di (se kur do të vijë dënimi). Unë kam për detyrë vetëm që t’ju përcjell mesazhin për të cilin jam dërguar, por unë po shoh se ju jeni popull i zhytur në padije”.');
INSERT INTO "hasan_efendi" VALUES(46,24,'46:24','Dhe kur panë një ré që u duk se vinte në drejtim të luginave të tyre, ata thanë: “Kjo re po sjell shiun!” “Jo!” – u përgjigj ai – “kjo është ajo që ju e keni shpejtuar: një erë që mbart një dënim të dhembshëm,');
INSERT INTO "hasan_efendi" VALUES(46,25,'46:25','që, me urdhrin e Allahut, shkatërron çdo gjë.” E në mëngjes u panë vetëm shtëpitë e tyre të zbrazëta; ja, kështu e ndëshkojmë Ne popullin keqbërës.');
INSERT INTO "hasan_efendi" VALUES(46,26,'46:26','Ne i kishim bërë ata më të fuqishëm se ju dhe u kishim dhënë atyre dëgjimin, shikimin dhe zemrën, por as dëgjimi i tyre, as shikimi i tyre e as zemra e tyre, nuk u sollën kurrfarë dobie, sepse mohuan shpalljet e Allahut; ata i goditi ajo me të cilën talleshin.');
INSERT INTO "hasan_efendi" VALUES(46,27,'46:27','Edhe më parë Ne kemi shkatërruar vendbanime rreth jush e u kemi shpjeguar atyre shpalljet Tona, që ata të mund të ktheheshin (në rrugën e drejtë).');
INSERT INTO "hasan_efendi" VALUES(46,28,'46:28','Përse nuk u erdhën në ndihmë atyre idhujt, që i adhuruan si zota krahas Allahut, për t’u afruar tek Ai?! Vërtet, idhujt i lanë në baltë ata . Këto ishin vetëm gënjeshtrat dhe shpifjet e tyre (idhujtarëve).');
INSERT INTO "hasan_efendi" VALUES(46,29,'46:29','Kur Ne dërguam te ti një grup xhindesh për të dëgjuar Kuranin, ata, kur erdhën, i thanë njëri-tjetrit: “Heshtni!” Dhe kur përfundoi (leximi), ata u kthyen te populli i tyre si këshillues.');
INSERT INTO "hasan_efendi" VALUES(46,30,'46:30','Ata thanë: “O populli ynë! Ne dëgjuam një Libër të shpallur pas Musait, i cili vërteton shpalljet para tij, udhëzon nga e vërteta dhe tregon rrugën e drejtë.');
INSERT INTO "hasan_efendi" VALUES(46,31,'46:31','O populli ynë, përgjigjjuni thirrësit të Allahut dhe besojini Atij! Ai do t’jua falë gjynahet tuaja dhe do t’ju shpëtojë prej dënimit të dhembshëm.');
INSERT INTO "hasan_efendi" VALUES(46,32,'46:32','Ndërsa ata që nuk i përgjigjen thirrësit të Allahut, nuk do të mund t’i ikin Atij në Tokë; për ata s’ka mbrojtës tjetër, përveç Tij. Ata janë në humbje të madhe.');
INSERT INTO "hasan_efendi" VALUES(46,33,'46:33','A nuk e shohin ata se Allahu, i Cili krijoi qiejt dhe Tokën pa u lodhur aspak në krijimin e tyre, është i Fuqishëm t’i ngjallë të vdekurit? Po, Ai është vërtet i Fuqishëm për çdo gjë.');
INSERT INTO "hasan_efendi" VALUES(46,34,'46:34','Ditën, kur ata që kanë mohuar do të ballafaqohen me zjarrin (do t’u thuhet): “Vallë, a nuk është kjo e vërtetë?” Ata do të thonë: “Po, për Zotin tonë!” Ai do t’u thotë: “Atëherë, shijoni dënimin për mohimin që keni bërë!”');
INSERT INTO "hasan_efendi" VALUES(46,35,'46:35','Prandaj, ti bëhu i durueshëm (o Muhamed), ashtu siç kanë duruar me këmbëngulje të dërguarit e vendosur dhe mos kërko shpejtimin e dënimit për ata ! Ditën, kur të përjetojnë atë që u është premtuar, atyre do t’u duket se kanë qëndruar (në Tokë) vetëm një çast të ditës. Kjo është shpallje e qartë. E kush do të shkatërrohet tjetër, përveç popullit të pabindur!?');
INSERT INTO "hasan_efendi" VALUES(47,1,'47:1','Allahu do t’ua zhvlerësojë veprat atyre që mohojnë dhe i pengojnë njerëzit nga rruga e Tij.');
INSERT INTO "hasan_efendi" VALUES(47,2,'47:2','Ndërsa atyre që besojnë, kryejnë vepra të mira dhe besojnë në atë që i është shpallur Muhamedit - dhe e cila është e Vërteta prej Zotit të tyre, - Ai do t’ua fshijë gjynahet dhe do t’ua përmirësojë gjendjen.');
INSERT INTO "hasan_efendi" VALUES(47,3,'47:3','Kjo, sepse ata që mohojnë, ndjekin të pavërtetën, kurse besimtarët mbështeten në të Vërtetën e ardhur nga Zoti i tyre. Kështu ua sjell Allahu njerëzve shembujt e tyre.');
INSERT INTO "hasan_efendi" VALUES(47,4,'47:4','Kur të ballafaqoheni në luftë me mohuesit, goditini në qafë, derisa t’i rraskapitni e t’i shkatërroni ata dhe pastaj lidhini fort. Mandej ose i lironi me zemërgjerësi, ose me shpërblim, derisa të bëhet armëpushimi. Kështu bëni! Sikur të donte Allahu, do t’i shkatërronte ata, por Ai dëshiron t’ju sprovojë ju me njëri tjetrin. Ai nuk ua humb veprat atyre që vdesin në rrugën e Allahut.');
INSERT INTO "hasan_efendi" VALUES(47,5,'47:5','Ai do t’i udhëzojë ata, do t’ua përmirësojë gjendjen');
INSERT INTO "hasan_efendi" VALUES(47,6,'47:6','dhe do t’i shpjerë në Xhenet, të cilin ua ka bërë të njohur më parë.');
INSERT INTO "hasan_efendi" VALUES(47,7,'47:7','O besimtarë, nëse e ndihmoni Allahun , edhe Ai do t’ju ndihmojë dhe do t’ju bëjë të qëndroni fort në këmbët tuaja.');
INSERT INTO "hasan_efendi" VALUES(47,8,'47:8','Ndërsa ata që nuk besojnë, do të shkatërrohen! Ai do t’ua humbë veprat atyre.');
INSERT INTO "hasan_efendi" VALUES(47,9,'47:9','Kjo për shkak se ata e urrejnë atë që ka shpallur Allahu, andaj Ai i ka zhvlerësuar veprat e tyre.');
INSERT INTO "hasan_efendi" VALUES(47,10,'47:10','Vallë, a nuk kanë udhëtuar ata nëpër Tokë, për të parë përfundimin e atyre që qenë përpara tyre!? Allahu i shfarosi ata plotësisht, e kështu do të përfundojnë edhe mohuesit e tjerë.');
INSERT INTO "hasan_efendi" VALUES(47,11,'47:11','Kjo për shkak se Allahu është Mbrojtës i atyre që besojnë, kurse për mohuesit nuk ka mbrojtës.');
INSERT INTO "hasan_efendi" VALUES(47,12,'47:12','Pa dyshim, ata që besojnë dhe bëjnë vepra të mira, Allahu do t’i shpjerë në Xhenete, nëpër të cilat rrjedhin lumenj. Ndërsa ata që nuk besojnë dhe kënaqen e hanë ashtu siç gëlltisin bagëtia, do të kenë Zjarrin si vendbanim!');
INSERT INTO "hasan_efendi" VALUES(47,13,'47:13','Eh, sa shumë qytete, të cilat kanë qenë më të fuqishme se qyteti yt, që të ka përzënë, Ne i kemi shkatërruar dhe nuk kanë pasur asnjë ndihmës!');
INSERT INTO "hasan_efendi" VALUES(47,14,'47:14','Vallë, a është i njëjtë ai që i përmbahet udhëzimit të Zotit të tij me atë që i zbukurohen punët e veta të shëmtuara dhe lëshohet pas pasioneve të veta?');
INSERT INTO "hasan_efendi" VALUES(47,15,'47:15','Ja përshkrimi i Xhenetit, që u premtohet të përkushtuarve (ndaj Allahut): një vend, ku ka lumenj me ujë të pandotur, lumenj qumështi me shije të pandryshuar, lumenj vere me shije të këndshme për ata që e pinë dhe lumenj prej mjalti të kulluar. Ata do të kenë aty lloj-lloj frutash dhe falje prej Zotit të tyre. A mund të krahasohet ky shembull me Xhehenemin, ku gjynahqarët do të qëndrojnë përherë në zjarr dhe do t’u jepet ujë i valuar, që ua copëton zorrët?!');
INSERT INTO "hasan_efendi" VALUES(47,16,'47:16','Midis tyre ka që bëjnë sikur të dëgjojnë ty, por, posa largohen prej teje, i pyesin ata të cilëve u është dhënë dituria: “Ç’tha ai më parë?!” Këta janë ata, zemrat e të cilëve ua ka vulosur Allahu dhe të cilët lëshohen pas epsheve të veta.');
INSERT INTO "hasan_efendi" VALUES(47,17,'47:17','Sa për ata që ndjekin rrugën e drejtë, Ai ua shton udhëzimin dhe u jep devotshmëri e vetëpërmbajtje.');
INSERT INTO "hasan_efendi" VALUES(47,18,'47:18','A mos vallë mohuesit presin, që t’u vijë befasisht vetëm Ora (e Kiametit)? Ndërkohë, shenjat e saj kanë ardhur. Po për çfarë do t’u shërbejnë atyre këshillat, kur t’iu ketë ardhur Kiameti?!');
INSERT INTO "hasan_efendi" VALUES(47,19,'47:19','Dije se nuk ka zot tjetër të vërtetë përveç Allahut! Kërko falje për gjynahun tënd dhe për besimtarët e besimtaret! Allahu e di se si silleni ju dhe ku qëndroni.');
INSERT INTO "hasan_efendi" VALUES(47,20,'47:20','Besimtarët thonë: “Sikur të ishte shpallur një sure!” Por, kur shpallet një sure e qartë dhe në të përmendet lufta, i sheh ata me zemra të sëmura , si të vështrojnë ty me shikim si të agonisë së vdekjes! Ndërkohë, më e përshtatshme do të ishte për ata');
INSERT INTO "hasan_efendi" VALUES(47,21,'47:21','bindja dhe fjala e mirë! Madje, kur çështja (e luftës) është vendosur, do të ishte më mirë për ata që të tregohen të sinqertë ndaj Allahut.');
INSERT INTO "hasan_efendi" VALUES(47,22,'47:22','Nëse ju (o hipokritë) i ktheni shpinën besimit, atëherë do të bënit ngatërresa në Tokë dhe do t’i shkatërronit lidhjet farefisnore.');
INSERT INTO "hasan_efendi" VALUES(47,23,'47:23','Këta janë ata, të cilët Allahu i ka mallkuar, i ka bërë të shurdhër dhe ua ka verbuar shikimin.');
INSERT INTO "hasan_efendi" VALUES(47,24,'47:24','Vallë, a nuk përsiatin ata për Kuranin apo i kanë zemrat të kyçura?!');
INSERT INTO "hasan_efendi" VALUES(47,25,'47:25','Vërtet, ata që kthehen në mosbesim, pasi u është treguar rruga e drejtë, djalli i ka joshur dhe i ka ushqyer me shpresa të rreme.');
INSERT INTO "hasan_efendi" VALUES(47,26,'47:26','Kjo për shkak se u thoshin atyre që nuk e donin atë, që e ka shpallur Allahu: “Ne do t’ju bindemi juve në disa gjëra” – por Allahu i di mirë fshehtësitë e tyre.');
INSERT INTO "hasan_efendi" VALUES(47,27,'47:27','E çfarë do të bëjnë ata, kur engjëjt do t’ua marrin shpirtin, duke i rrahur në fytyrat dhe shpinat e tyre?!');
INSERT INTO "hasan_efendi" VALUES(47,28,'47:28','Kjo, sepse ata pasuan atë që shkakton zemërimin e Allahut dhe urryen pëlqimin e Tij e kështu, Ai ua zhvlerësoi atyre veprat.');
INSERT INTO "hasan_efendi" VALUES(47,29,'47:29','Vallë, a mendojnë ata që kanë sëmundje në zemrat e tyre , se Allahu nuk do t’ua nxirrte në shesh urrejtjen e tyre (ndaj Islamit)?');
INSERT INTO "hasan_efendi" VALUES(47,30,'47:30','Sikur të donim, Ne do të t’i tregonim ty ata dhe ti, me siguri, do t’i njihje sipas shenjave të tyre. Por, ti do t’i njohësh ata sipas mënyrës së shprehjes së tyre. Allahu i di veprat tuaja.');
INSERT INTO "hasan_efendi" VALUES(47,31,'47:31','Ne do t’ju vëmë në provë ju, derisa t’i njohim ata midis jush, që luftojnë (në rrugën e drejtë) dhe durojnë, e derisa të provojmë ato që thuhen për veprat tuaja.');
INSERT INTO "hasan_efendi" VALUES(47,32,'47:32','Me të vërtetë, ata që nuk besojnë, i pengojnë (të tjerët) nga rruga e Allahut dhe kundërshtojnë të Dërguarin, pasi u është shpjeguar qartë rruga e drejtë, nuk do t’i sjellin Allahut kurrfarë dëmi; Ai do t’i zhvlerësojë veprat e tyre.');
INSERT INTO "hasan_efendi" VALUES(47,33,'47:33','O ju që keni besuar! Bindjuni Allahut dhe bindjuni të Dërguarit dhe mos i çoni dëm veprat tuaja!');
INSERT INTO "hasan_efendi" VALUES(47,34,'47:34','Pa dyshim, ata që nuk besojnë, (i) pengojnë (të tjerët) nga rruga e Allahut dhe vdesin si jobesimtarë, Allahu kurrsesi nuk do t’i falë.');
INSERT INTO "hasan_efendi" VALUES(47,35,'47:35','Dhe mos u bëni të dobët e të kërkoni armëpushim, ndërkohë që jeni më të fuqishmit dhe Allahu është me ju. Ai nuk do t’jua pakësojë (shpërblimin për) veprat tuaja.');
INSERT INTO "hasan_efendi" VALUES(47,36,'47:36','Jeta e kësaj bote nuk është asgjë tjetër, veçse lojë dhe zbavitje. Nëse (i) besoni (Allahut) dhe (i) ruheni (Atij), Ai do t’ju shpërblejë dhe nuk do të kërkojë prej jush pasurinë tuaj.');
INSERT INTO "hasan_efendi" VALUES(47,37,'47:37','Sikur ta kërkonte prej jush tërë pasurinë me këmbëngulje, ju do të bëheshit koprracë dhe, Ai do të nxirrte në shesh urrejtjen tuaj.');
INSERT INTO "hasan_efendi" VALUES(47,38,'47:38','Ja, juve ju bëhet thirrje të shpenzoni në rrugën e Allahut, por disa prej jush janë koprracë. Kush tregohet koprrac, ta dijë se i bën dëm vetes, sepse Allahu është i Pasur (s’ka nevojë për asgjë), ndërsa ju jeni të varfër. E, nëse ju ia ktheni shpinën dhe i largoheni Atij, Ai do t’ju zëvendësojë me një popull tjetër, i cili nuk do ta ndjekë shembullin tuaj.');
INSERT INTO "hasan_efendi" VALUES(48,1,'48:1','Ne të dhamë ty një fitore të qartë, ');
INSERT INTO "hasan_efendi" VALUES(48,2,'48:2','për të t’i falur ty Allahu gabimet e mëparshme dhe të ardhshme, për ta plotësuar dhuntinë e Vet ndaj teje dhe për të të udhëzuar në rrugën e drejtë,');
INSERT INTO "hasan_efendi" VALUES(48,3,'48:3','dhe që Allahu të të japë ndihmë të madhe.');
INSERT INTO "hasan_efendi" VALUES(48,4,'48:4','Është Ai, që zbriti qetësi në zemrat e besimtarëve, për t’ua forcuar besimin, krahas besimit që patën. Të Allahut janë ushtritë e qiejve dhe të Tokës; Allahu është i Gjithëdijshëm dhe i Urtë.');
INSERT INTO "hasan_efendi" VALUES(48,5,'48:5','(Ai e bëri këtë) për t’i çuar besimtarët dhe besimtaret në kopshte nëpër të cilat rrjedhin lumenj e ku do të qëndrojnë përherë, si dhe që t’ua falë gjynahet atyre. Kjo, në sytë e Allahut, është fitorja më e madhe.');
INSERT INTO "hasan_efendi" VALUES(48,6,'48:6','Gjithashtu, (Ai e bëri këtë) për t’i ndëshkuar hipokritët dhe hipokritet, idhujtarët e idhujtaret, që mendojnë keq për Allahun. Radha e fatkeqësisë le t’i godasë ata! Allahu është zemëruar me ata, i ka mallkuar dhe u ka përgatitur Xhehenemin; eh, sa vend i shëmtuar që është ai!');
INSERT INTO "hasan_efendi" VALUES(48,7,'48:7','Të Allahut janë ushtritë e qiejve dhe të Tokës; Allahu është i Plotfuqishëm dhe i Urtë!');
INSERT INTO "hasan_efendi" VALUES(48,8,'48:8','Me të vërtetë, Ne të kemi dërguar ty (Muhamed) dëshmitar, sjellës të lajmeve të mira dhe paralajmërues,');
INSERT INTO "hasan_efendi" VALUES(48,9,'48:9','që t’i besoni Allahut dhe të Dërguarit të Tij, që ta ndihmoni e ta nderoni atë (Muhamedin a.s.) dhe që ta lavdëroni Atë (Allahun xh.sh.) me namaz në mëngjes dhe mbrëmje.');
INSERT INTO "hasan_efendi" VALUES(48,10,'48:10','Ata që të janë betuar për besnikëri, në të vërtetë, i janë betuar për besnikëri Allahut. Dora e Allahut është mbi duart e tyre! Ai që e prish betimin, e prish në dëm të vetes, por, nëse e mban besën ndaj Allahut, Ai do t’i japë atij shpërblim të madh.');
INSERT INTO "hasan_efendi" VALUES(48,11,'48:11','Do të të thonë ty arabët e shkretëtirës, të cilët ngelën pas jush : “Ne ishim të zënë me bagëtitë dhe familjet tona, prandaj lutju Allahut që të na falë ne!” Ata thonë me gjuhën e tyre atë që nuk e kanë në zemrat e tyre. Thuaju: “Kush mund ta pengojë Allahun ndaj jush, nëse Ai kërkon t’ju godasë ose, nëse synon t’ju bëjë ndonjë të mirë?” Allahu e di atë që ju bëni.');
INSERT INTO "hasan_efendi" VALUES(48,12,'48:12','Jo! Ju menduat se i Dërguari dhe besimtarët nuk do të kthehen kurrë te familjet e tyre dhe zemrat tuaja ishin të kënaqura me këtë trill. Ju ushqyet mendime shumë të këqija dhe kështu u bëtë popull i pavlefshëm i nisur drejt shkatërrimit.');
INSERT INTO "hasan_efendi" VALUES(48,13,'48:13','Kush nuk beson në Allahun dhe në të Dërguarin e Tij, ta dijë se Ne kemi përgatitur për mohuesit një Zjarr flakërues!');
INSERT INTO "hasan_efendi" VALUES(48,14,'48:14','I Allahut është pushteti i qiejve dhe i Tokës. Ai fal kë të dojë e dënon kë të dojë. Allahu është Falës i madh dhe Mëshirëplotë.');
INSERT INTO "hasan_efendi" VALUES(48,15,'48:15','Ata që ngelën pas jush, do t’ju thonë, kur të niseni për të marrë plaçkën e luftës: “Na lejoni edhe ne që të vijmë pas jush!” Ata do të donin t’i ndërronin fjalët e Allahut. Thuaju: “Ju kurrsesi nuk do të vini pas nesh, këtë e ka thënë Allahu qysh më parë!” E ata do të thonë: “Nuk është ashtu, por ju na keni zili”. Ah, sa pak që kuptojnë!');
INSERT INTO "hasan_efendi" VALUES(48,16,'48:16','Thuaju atyre arabëve të shkretëtirës, që ngelën pas jush: “Ju do të thirreni për të luftuar kundër një populli shumë luftarak, nëse ata nuk dorëzohen. Nëse bindeni, Allahu do t’ju japë shpërblim të mirë, por, nëse shmangeni, ashtu siç keni bërë edhe më parë, Ai do t’ju ndëshkojë me dënim të dhembshëm.');
INSERT INTO "hasan_efendi" VALUES(48,17,'48:17','Nuk është gjynah për të verbrin, as për të çalin, as të sëmurin (që të mos marrin pjesë në luftë). Kush i bindet Allahut dhe të Dërguarit të Tij, do të shkojë në Xhenete, nëpër të cilat rrjedhin lumenj, kurse ai që shmanget, do të ndëshkohet me dënim të dhembshëm.');
INSERT INTO "hasan_efendi" VALUES(48,18,'48:18','Allahu është i kënaqur me ata besimtarë, kur t’u betuan për besnikëri nën hijen e pemës. Ai e dinte se ç’kishte në zemrat e tyre, prandaj u dha atyre qetësi dhe i shpërbleu me një fitore të afërt ');
INSERT INTO "hasan_efendi" VALUES(48,19,'48:19','e me një plaçkë të madhe që do ta fitojnë. Allahu është i Plotfuqishëm dhe i Urtë.');
INSERT INTO "hasan_efendi" VALUES(48,20,'48:20','Allahu ju premtoi plaçkë të pasur, të cilën do ta fitoni e jua shpejtoi këtë juve. Ai i largoi duart e njerëzve prej jush, me qëllim që (fitorja juaj) të jetë një shenjë për besimtarët e vërtetë dhe që t’ju udhëzojë në rrugën e drejtë.');
INSERT INTO "hasan_efendi" VALUES(48,21,'48:21','Allahu e di edhe për të tjera (fitore e plaçka lufte), të cilat ju ende nuk i keni fituar. Allahu është i Fuqishëm për çdo gjë.');
INSERT INTO "hasan_efendi" VALUES(48,22,'48:22','Nëse mohuesit nisen në luftë kundër jush, me siguri që ata do të ikin dhe pastaj nuk do të gjejnë as mbrojtës, as ndihmës.');
INSERT INTO "hasan_efendi" VALUES(48,23,'48:23','I tillë ka qenë ligji i Allahut edhe më parë, e ti kurrë nuk do të gjesh ndryshime në ligjin e Allahut.');
INSERT INTO "hasan_efendi" VALUES(48,24,'48:24','Është Ai që i ka penguar duart e tyre ndaj jush dhe duart tuaja ndaj tyre brenda në Mekë, pasi ju dha fitoren mbi ata. Allahu i sheh të gjitha çfarë bëni ju.');
INSERT INTO "hasan_efendi" VALUES(48,25,'48:25','Jobesimtarët ju penguan t’i afroheni Xhamisë së Shenjtë dhe (penguan) kafshët për kurban, që të arrinin në vend. Po të mos kishte qenë për disa besimtarë e besimtare (në Mekë) që ju nuk i njihnit e kështu mund t’i merrnit nëpër këmbë, duke bërë gjynah kundrejt tyre pa e ditur, Allahu do t’ju kishte urdhëruar t’i luftonit ata. Sikur ata të ishin veç, Ne do t’i ndëshkonim me dënim të dhembshëm ata (banorë të Mekës), që nuk besuan.');
INSERT INTO "hasan_efendi" VALUES(48,26,'48:26','Dhe, kur në zemrat e mohuesve u shfaq zelltaria pagane, Allahu zbriti qetësinë e Tij në zemrën e të Dërguarit të Tij dhe të besimtarëve e i bëri ata të mbështeten fort në fjalën e përkushtimit (ndaj Allahut) . Ata kishin më shumë të drejtë e qenë më të denjë për të. Allahu është i Gjithëdijshëm për çdo gjë.');
INSERT INTO "hasan_efendi" VALUES(48,27,'48:27','Allahu e përmbushi me të vërtetë vizionin e të Dërguarit të Tij, i cili kishte thënë: “Me siguri, ju do të hyni në Xhaminë e Shenjtë, dashtë Allahu, të sigurt, kokërruar dhe flokëshkurtuar, pa kurrfarë frike.” Ai e dinte atë që nuk e dinit ju dhe, veç kësaj, ju dhuroi një fitore të afërt.');
INSERT INTO "hasan_efendi" VALUES(48,28,'48:28','Është Ai që e ka çuar të Dërguarin e Vet me Udhëzimin dhe fenë e vërtetë, për ta ngritur atë mbi të gjitha fetë. Allahu mjafton si dëshmitar!');
INSERT INTO "hasan_efendi" VALUES(48,29,'48:29','Muhamedi është i Dërguar i Allahut. Ata që janë me të, janë të ashpër ndaj jobesimtarëve e të mëshirshëm midis tyre. I sheh ata si përkulen dhe bien në sexhde, duke kërkuar dhuntinë dhe kënaqësinë e Allahut. Në fytyrat e tyre shihen shenjat e sexhdes. Ky është përshkrimi i tyre në Teurat. Ndërsa në Ungjill, shembulli i tyre përshkruhet si një farë e mbjellë që lëshon filiza e forcohet e pastaj ngrihet e fortë me kërcellin e vet, duke i gëzuar mbjellësit. (Kështu përshkruhen ata) me qëllim që, nëpërmjet tyre, Ai t’i zemërojë mohuesit. Për ata midis tyre që besojnë dhe kryejnë vepra të mira, Allahu ka premtuar falje dhe shpërblim të madh.');
INSERT INTO "hasan_efendi" VALUES(49,1,'49:1','O ju që keni besuar! Mos nxitoni para (urdhrit të) Allahut dhe të Dërguarit të Tij dhe kijeni frikë Allahun! Vërtet, Allahu dëgjon gjithçka dhe është i Gjithëdijshëm.');
INSERT INTO "hasan_efendi" VALUES(49,2,'49:2','O ju që keni besuar! Mos e ngrini zërin tuaj mbi zërin e Profetit dhe mos i flisni atij me zë të lartë, siç bëni me njëri-tjetrin, në mënyrë që të mos ju humbin veprat tuaja pa e ndier ju fare.');
INSERT INTO "hasan_efendi" VALUES(49,3,'49:3','Vërtet, ata që ulin zërin në prani të të Dërguarit të Allahut janë njerëzit, zemrat e të cilëve Allahu i ka kalitur për besim e përkushtim. Për ata ka falje dhe shpërblim të madh.');
INSERT INTO "hasan_efendi" VALUES(49,4,'49:4','Vërtet, ata që të thërrasin ty (o Muhamed) me zë të lartë nga jashtë dhomave të tua, shumica e tyre nuk marrin vesh.');
INSERT INTO "hasan_efendi" VALUES(49,5,'49:5','E sikur të duronin derisa ti të dilje jashtë tek ata, do të ishte më mirë për ata. Por Allahu është Falës dhe Mëshirues.');
INSERT INTO "hasan_efendi" VALUES(49,6,'49:6','O ju që keni besuar! Nëse ndonjë njeri i pandershëm ju sjell ndonjë lajm, sigurohuni mirë (në është i vërtetë), për të mos i bërë dëm ndokujt pa dashje, e të pendoheni pastaj për atë që keni bërë.');
INSERT INTO "hasan_efendi" VALUES(49,7,'49:7','Dhe ta dini se midis jush gjendet i Dërguari i Allahut! Sikur ai t’ju bindej juve në shumë gjëra, ju do ta kishit pësuar. Por Allahu jua ka bërë të dashur besimin dhe jua ka zbukuruar atë në zemrat tuaja, ashtu siç jua ka bërë të urryer mosbesimin, shthurjen dhe mosbindjen. Pikërisht ata janë të udhëzuarit drejt');
INSERT INTO "hasan_efendi" VALUES(49,8,'49:8','me mirësinë dhe hirin e Allahut. Allahu është i Gjithëdijshëm dhe i Urtë.');
INSERT INTO "hasan_efendi" VALUES(49,9,'49:9','Nëse dy palë besimtarësh bëjnë luftë midis tyre, pajtojini ata. Por, nëse njëra palë sulmon tjetrën, atëherë luftoni kundër sulmuesit, derisa të kthehet në urdhrat e Allahut. E, nëse kthehet, atëherë pajtojini ata midis tyre, me drejtësi dhe paanësi, sepse Allahu i do ata që veprojnë me drejtësi.');
INSERT INTO "hasan_efendi" VALUES(49,10,'49:10','Në të vërtetë, besimtarët janë vëllezër, andaj pajtojini vëllezërit tuaj midis tyre dhe kijeni frikë Allahun, në mënyrë që ju të mëshiroheni.');
INSERT INTO "hasan_efendi" VALUES(49,11,'49:11','O ju që keni besuar! Asnjë burrë prej jush të mos tallet me burra të tjerë, se këta mund të jenë më të mirë se ai. Gjithashtu, asnjë grua të mos tallet me gra të tjera, se këto mund të jenë më të mira se ajo. Mos e nënçmoni dhe mos e qesëndisni njëri tjetrin me nofka (të këqija)! Sa e shëmtuar është të thirresh me emër të keq pas (pranimit të) besimit! Ata që nuk pendohen, janë vërtet keqbërës.');
INSERT INTO "hasan_efendi" VALUES(49,12,'49:12','O ju që keni besuar! Mënjanoni shumë dyshime, se, vërtet, disa dyshime janë gjynah. Mos e spiunoni dhe mos e përgojoni njëri-tjetrin! Mos vallë dëshiron ndokush prej jush të hajë mishin e vëllait të vet të vdekur?! Sigurisht që ju do ta urrenit këtë! Kijeni frikë Allahun! Me të vërtetë, Allahu është Pranues i pendimit dhe Mëshirëplotë.');
INSERT INTO "hasan_efendi" VALUES(49,13,'49:13','O njerëz! Në të vërtetë, Ne ju krijuam ju prej një mashkulli dhe një femre dhe ju bëmë popuj e fise, për ta njohur njëri tjetrin. Më i nderuari prej jush tek Allahu është ai që i frikësohet më shumë Atij. Vërtet, Allahu është i Gjithëdijshëm dhe për Atë asgjë nuk është e fshehtë.');
INSERT INTO "hasan_efendi" VALUES(49,14,'49:14','Arabët e shkretëtirës deklarojnë: “Ne besojmë”. Thuaju: “Ju nuk keni besuar! Por më mirë thoni: ‘Ne përulemi’, pasi besimi ende nuk ka hyrë (si duhet) në zemrat tuaja. E, nëse i bindeni Allahut dhe të Dërguarit të Tij, nuk do t’ju pakësohet asgjë prej veprave tuaja. Sigurisht, Allahu është Falës dhe Mëshirëplotë.”');
INSERT INTO "hasan_efendi" VALUES(49,15,'49:15','Besimtarë të vërtetë janë vetëm ata që besojnë Allahun dhe të Dërguarin e Tij dhe pastaj nuk dyshojnë, por luftojnë në rrugën e Allahut me pasurinë dhe jetën e tyre. Këta janë besimtarë të sinqertë. ');
INSERT INTO "hasan_efendi" VALUES(49,16,'49:16','Thuaju: “Vallë, a do ta njoftoni Allahun për fenë tuaj, ndërkohë që Ai di gjithçka që përmbajnë qiejt dhe Toka?! Allahu është i Dijshëm për çdo gjë.”');
INSERT INTO "hasan_efendi" VALUES(49,17,'49:17','Ata e quajnë si favor për ty që kanë pranuar Islamin. Thuaju: “Mos e quani si favor për mua se keni pranuar Islamin; përkundrazi, Allahu ju ka dhuruar mirësi dhe ju ka udhëzuar në besim, nëse ajo që thoni është e vërtetë!”');
INSERT INTO "hasan_efendi" VALUES(49,18,'49:18','Allahu i di fshehtësitë e qiejve dhe të Tokës. Allahu i sheh të gjitha ato që bëni ju.');
INSERT INTO "hasan_efendi" VALUES(50,1,'50:1','Kâf. Betohem në Kuranin e lavdishëm!');
INSERT INTO "hasan_efendi" VALUES(50,2,'50:2','Po! Ata çuditen që nga gjiri i tyre u ka ardhur një paralajmërues, kështu që mohuesit thonë: “Kjo gjë është e çuditshme.');
INSERT INTO "hasan_efendi" VALUES(50,3,'50:3','Vallë, pasi të vdesim e të bëhemi pluhur (do të ringjallemi sërish)? Ai kthim është i largët!”');
INSERT INTO "hasan_efendi" VALUES(50,4,'50:4','Ne e dimë sa prej tyre i merr toka, tek Ne është Libri që i ruan të gjitha.');
INSERT INTO "hasan_efendi" VALUES(50,5,'50:5','Mirëpo, ata e mohojnë të Vërtetën kur u vjen, kështu që ata janë në gjendje të turbulluar.');
INSERT INTO "hasan_efendi" VALUES(50,6,'50:6','A nuk e shohin ata qiellin që qëndron sipër tyre, si e kemi ndërtuar dhe zbukuruar, pa pasur kurrfarë plasaritjeje në të?');
INSERT INTO "hasan_efendi" VALUES(50,7,'50:7','Ndërsa Tokën e kemi shtruar dhe në të kemi shpërndarë male të palëvizshme dhe kemi bërë, që nga ajo, të mbijnë gjithfarëlloj bimësh të bukura,');
INSERT INTO "hasan_efendi" VALUES(50,8,'50:8','si mjet ndriçimi të mendjes dhe përkujtesë për çdo rob që i drejtohet Zotit të vet.');
INSERT INTO "hasan_efendi" VALUES(50,9,'50:9','Ne lëshojmë nga qielli ujë të bekuar dhe nëpërmjet tij krijojmë kopshte, drithëra që korren');
INSERT INTO "hasan_efendi" VALUES(50,10,'50:10','dhe palma hurme të larta me tufa frutash njëra mbi tjetrën,');
INSERT INTO "hasan_efendi" VALUES(50,11,'50:11','si ushqim për robërit Tanë; në këtë mënyrë Ne me shi e ringjallim tokën e vdekur. E kështu do të jetë edhe ringjallja.');
INSERT INTO "hasan_efendi" VALUES(50,12,'50:12','Para tyre ka mohuar populli i Nuhut, banorët e Ressit dhe fisi Themud,');
INSERT INTO "hasan_efendi" VALUES(50,13,'50:13','edhe fisi Ad, Faraoni dhe vëllezërit e Lutit,');
INSERT INTO "hasan_efendi" VALUES(50,14,'50:14','edhe banorët e Ejkës, edhe populli i Tubbas; të gjithë ata i quajtën gënjeshtarë të dërguarit, prandaj kundër tyre u përmbush dënimi Im.');
INSERT INTO "hasan_efendi" VALUES(50,15,'50:15','Vallë, a mos u lodhëm Ne gjatë krijimit të parë? Jo, por ata dyshojnë në krijimin e sërishëm.');
INSERT INTO "hasan_efendi" VALUES(50,16,'50:16','Ne e kemi krijuar njeriun dhe, Ne e dimë ç’i pëshpërit atij shpirti i vet. Ne jemi më pranë tij se damari i qafës së vet.');
INSERT INTO "hasan_efendi" VALUES(50,17,'50:17','Dy engjëj i rrinë atij nga e djathta dhe e majta,');
INSERT INTO "hasan_efendi" VALUES(50,18,'50:18','dhe, për çdo fjalë që ai thotë, ka pranë vetes një mbikëqyrës të gatshëm (për ta shënuar atë).');
INSERT INTO "hasan_efendi" VALUES(50,19,'50:19','Agonia e vdekjes do të sjellë të vërtetën (e jetës tjetër): ja, kjo është ajo së cilës përpiqeshe t’i ikje.');
INSERT INTO "hasan_efendi" VALUES(50,20,'50:20','Dhe do të fryhet në Sur - kjo është Dita e kërcënimit.');
INSERT INTO "hasan_efendi" VALUES(50,21,'50:21','Çdo shpirt do të vijë i shoqëruar me një (engjëll) grahës dhe një (engjëll) dëshmitar.');
INSERT INTO "hasan_efendi" VALUES(50,22,'50:22','(Mohuesit do t’i thuhet): “Ti ke qenë i pavëmendshëm për këtë. Tashti ta kemi ngritur perden (e syve), dhe shikimi yt sot është i mprehtë.”');
INSERT INTO "hasan_efendi" VALUES(50,23,'50:23','E shoqëruesi i tij (engjëll) do të thotë: “Këtu është (shënimi i veprave të tij) që kam gati”.');
INSERT INTO "hasan_efendi" VALUES(50,24,'50:24','(Do t’u thuhet:) “Hidhni në zjarr çdo njeri jobesimtar, kryeneç,');
INSERT INTO "hasan_efendi" VALUES(50,25,'50:25','pengues të të mirave, shkelës dhe dyshues (në fe);');
INSERT INTO "hasan_efendi" VALUES(50,26,'50:26','çdo njeri që ka besuar në zot tjetër, përveç Allahut. Kështu pra, flakeni atë në dënimin më të rëndë!”');
INSERT INTO "hasan_efendi" VALUES(50,27,'50:27','E, shoku i tij (djall) do të thotë: “O Zoti ynë, unë nuk e kam mashtruar atë, por ai vetë ka qenë në humbje të madhe”.');
INSERT INTO "hasan_efendi" VALUES(50,28,'50:28','Atyre (Allahu) do t’u thotë: “Mos u grindni para Meje, Unë ju kam paralajmëruar me kohë.');
INSERT INTO "hasan_efendi" VALUES(50,29,'50:29','Fjala ime nuk ndryshohet dhe Unë nuk jam i padrejtë me robërit e Mi”.');
INSERT INTO "hasan_efendi" VALUES(50,30,'50:30','Atë Ditë Ne do ta pyesim Xhehenemin: “A u mbushe?” e ai do të përgjigjet: “A ka edhe më?”');
INSERT INTO "hasan_efendi" VALUES(50,31,'50:31','Dhe jo larg prej aty, Xheneti do t’u afrohet besimtarëve të devotshëm,');
INSERT INTO "hasan_efendi" VALUES(50,32,'50:32','(që do t’u thuhet:) “Kjo është premtuar për ju, për këdo që është penduar dhe ruajtur (nga gjynahet),');
INSERT INTO "hasan_efendi" VALUES(50,33,'50:33','që e ka pasur frikë të Gjithëmëshirshmin, edhe atëherë kur nuk e shihte kush, e që ka ardhur me zemër të kthyer (nga Allahu).');
INSERT INTO "hasan_efendi" VALUES(50,34,'50:34','Hyni në të (Xhenet) me paqe e siguri. Kjo është Dita e amshimit!”');
INSERT INTO "hasan_efendi" VALUES(50,35,'50:35','Aty do të kenë çfarë të duan - e prej Nesh (do të kenë) edhe më shumë.');
INSERT INTO "hasan_efendi" VALUES(50,36,'50:36','Eh, sa breza shumë më të fuqishëm Ne i kemi shkatërruar para tyre! Ata brodhën nëpër Tokë, po a mund të gjenin strehim?!');
INSERT INTO "hasan_efendi" VALUES(50,37,'50:37','Vërtet, këtu ka këshillë për këdo që ka zemër, dëgjon me vëmendje dhe dëshmon.');
INSERT INTO "hasan_efendi" VALUES(50,38,'50:38','Ne i kemi krijuar qiejt, Tokën dhe çdo gjë që gjendet midis tyre për gjashtë ditë, pa ndierë lodhje aspak.');
INSERT INTO "hasan_efendi" VALUES(50,39,'50:39','Andaj, duroje atë që thonë ata , madhëroje Zotin tënd e falënderoje Atë para lindjes së diellit dhe para perëndimit.');
INSERT INTO "hasan_efendi" VALUES(50,40,'50:40','Madhëroje Atë edhe në një pjesë të natës, edhe pas faljes së namazit.');
INSERT INTO "hasan_efendi" VALUES(50,41,'50:41','Dhe vëri veshin thërritësit që thërret prej një vendi të afërt,');
INSERT INTO "hasan_efendi" VALUES(50,42,'50:42','Ditën, kur ata do ta dëgjojnë thirrjen e vërtetë, ajo do të jetë Dita e Daljes (ringjalljes).');
INSERT INTO "hasan_efendi" VALUES(50,43,'50:43','Jemi Ne ata që japin jetën dhe vdekjen. Te Ne do të kthehet çdo gjë!');
INSERT INTO "hasan_efendi" VALUES(50,44,'50:44','Atë Ditë, toka do të hapet e ata do të dalin me nxitim. Për Ne është e lehtë t’i mbledhim ata të gjithë.');
INSERT INTO "hasan_efendi" VALUES(50,45,'50:45','Ne e dimë mirë çfarë thonë ata, por ti nuk do t’i detyrosh ata me dhunë (që të besojnë). Prandaj këshillo me Kuranin këdo që ka frikë paralajmërimin Tim!');
INSERT INTO "hasan_efendi" VALUES(51,1,'51:1','Betohem në erërat që çojnë (pluhurin)');
INSERT INTO "hasan_efendi" VALUES(51,2,'51:2','dhe në retë e ngarkuara rëndë');
INSERT INTO "hasan_efendi" VALUES(51,3,'51:3','dhe në ato që lundrojnë lehtë');
INSERT INTO "hasan_efendi" VALUES(51,4,'51:4','dhe në engjëjt që shpërndajnë urdhrat');
INSERT INTO "hasan_efendi" VALUES(51,5,'51:5','se, me të vërtetë, është e sigurt ajo që ju premtohet');
INSERT INTO "hasan_efendi" VALUES(51,6,'51:6','dhe çdo shpërblim e dënim do të ndodhë!');
INSERT INTO "hasan_efendi" VALUES(51,7,'51:7','Për qiellin e stolisur me yje!');
INSERT INTO "hasan_efendi" VALUES(51,8,'51:8','Ju keni gjithfarë mendimesh,');
INSERT INTO "hasan_efendi" VALUES(51,9,'51:9','Prej tij (Kuranit) shmanget ai që nuk beson.');
INSERT INTO "hasan_efendi" VALUES(51,10,'51:10','Qofshin mallkuar gënjeshtarët,');
INSERT INTO "hasan_efendi" VALUES(51,11,'51:11','të cilët janë të zhytur në padije dhe të shkujdesur (ndaj Ditës së Gjykimit)!');
INSERT INTO "hasan_efendi" VALUES(51,12,'51:12','Ata pyesin: “Kur do të jetë Dita e Gjykimit?”');
INSERT INTO "hasan_efendi" VALUES(51,13,'51:13','Atë Ditë ata do të përcëllohen në zjarr!');
INSERT INTO "hasan_efendi" VALUES(51,14,'51:14','(Do t’u thuhet): “Shijoni dënimin tuaj! Këtë kërkuat t’ju shpejtohet!”');
INSERT INTO "hasan_efendi" VALUES(51,15,'51:15','Pa dyshim, ata që i frikësohen Allahut do të jenë në kopshte dhe burime,');
INSERT INTO "hasan_efendi" VALUES(51,16,'51:16','duke marrë atë që ua ka dhuruar Zoti i tyre, meqë më parë kanë qenë punëmbarë.');
INSERT INTO "hasan_efendi" VALUES(51,17,'51:17','Ata flinin pak natën,');
INSERT INTO "hasan_efendi" VALUES(51,18,'51:18','ndërsa në agim kërkonin falje (nga Allahu).');
INSERT INTO "hasan_efendi" VALUES(51,19,'51:19','Në pasuritë e tyre ka pasur pjesë për lypësit dhe nevojtarët.');
INSERT INTO "hasan_efendi" VALUES(51,20,'51:20','Në Tokë ka shenja për ata që besojnë me bindje,');
INSERT INTO "hasan_efendi" VALUES(51,21,'51:21','po edhe në vetet tuaja - a nuk e shihni vallë?');
INSERT INTO "hasan_efendi" VALUES(51,22,'51:22','Në qiell është furnizimi juaj dhe gjithçka që ju është premtuar.');
INSERT INTO "hasan_efendi" VALUES(51,23,'51:23','Betohem për Zotin e qiellit dhe të Tokës, se kjo (që ju premtohet) është e vërtetë, ashtu siç është të folurit tuaj.');
INSERT INTO "hasan_efendi" VALUES(51,24,'51:24','A e ke dëgjuar historinë e mysafirëve të nderuar të Ibrahimit?');
INSERT INTO "hasan_efendi" VALUES(51,25,'51:25','Ata hynë tek ai e i thanë: “Paqe!” - dhe ai u tha: “Paqe, o njerëz të panjohur!”');
INSERT INTO "hasan_efendi" VALUES(51,26,'51:26','E pa u vënë re, ai shkoi te njerëzit e shtëpisë dhe solli një viç të majmë.');
INSERT INTO "hasan_efendi" VALUES(51,27,'51:27','Pastaj ua afroi atyre e u tha: “A nuk po hani?”');
INSERT INTO "hasan_efendi" VALUES(51,28,'51:28','Dhe ndjeu frikë prej tyre, por ata i thanë: “Mos u frikëso!” dhe i dhanë lajmin e mirë për një djalë të dijshëm.');
INSERT INTO "hasan_efendi" VALUES(51,29,'51:29','Atëherë erdhi gruaja e tij, që, duke thërritur dhe rrahur fytyrën, tha: “Vallë, a mundem (të lind) unë, plaka shterpë?”');
INSERT INTO "hasan_efendi" VALUES(51,30,'51:30','Ata thanë: “Kështu ka qenë vullneti i Zotit tënd. Ai është vërtet i Urtë dhe i Gjithëdijshëm.”');
INSERT INTO "hasan_efendi" VALUES(51,31,'51:31','Ai i pyeti: “Cili është qëllimi juaj, o të dërguar?”');
INSERT INTO "hasan_efendi" VALUES(51,32,'51:32','Ata thanë: “Ne jemi dërguar te një popull keqbërës,');
INSERT INTO "hasan_efendi" VALUES(51,33,'51:33','për të hedhur mbi ta gurë prej balte të pjekur,');
INSERT INTO "hasan_efendi" VALUES(51,34,'51:34','të shënuar te Zoti yt, për ata që e kalojnë kufirin (në të këqija)”.');
INSERT INTO "hasan_efendi" VALUES(51,35,'51:35','Ne i nxorëm të gjithë besimtarët që ishin në atë qytet');
INSERT INTO "hasan_efendi" VALUES(51,36,'51:36','ndonëse aty gjetëm vetëm një shtëpi myslimane');
INSERT INTO "hasan_efendi" VALUES(51,37,'51:37','dhe në të lamë shenjë për ata që i frikësohen dënimit të dhembshëm.');
INSERT INTO "hasan_efendi" VALUES(51,38,'51:38','Edhe në historinë e Musait (ka mësim), kur ia dërguam Faraonit, me provë të qartë.');
INSERT INTO "hasan_efendi" VALUES(51,39,'51:39','Ai (Faraoni) u shmang (nga besimi), së bashku me komandantët e vet dhe tha: “Musai është magjistar ose i çmendur!”');
INSERT INTO "hasan_efendi" VALUES(51,40,'51:40','Ne e kapëm atë me ushtrinë e tij dhe e hodhëm në det; ai e meritoi.');
INSERT INTO "hasan_efendi" VALUES(51,41,'51:41','(Mësim ka edhe në historinë) e fisit Ad, kur i dërguam erën shkatërruese,');
INSERT INTO "hasan_efendi" VALUES(51,42,'51:42','e cila, ngado që kaloi, nuk kurseu asgjë, duke i kthyer në kalbësira.');
INSERT INTO "hasan_efendi" VALUES(51,43,'51:43','Edhe te fisi Themud (ka mësim), kur iu tha anëtarëve të tij: “Kënaquni njëfarë kohe!”');
INSERT INTO "hasan_efendi" VALUES(51,44,'51:44','Ata u treguan mendjemëdhenj ndaj urdhrave të Allahut, pastaj i goditi rrufeja, ndërsa e shihnin,');
INSERT INTO "hasan_efendi" VALUES(51,45,'51:45','por nuk mundën as të ngriheshin, as të mbroheshin prej dënimit.');
INSERT INTO "hasan_efendi" VALUES(51,46,'51:46','(Mësim ka) edhe te populli i Nuhut, (që e shkatërruam) shumë kohë më parë. Me të vërtetë, ai ishte popull i pabindur!');
INSERT INTO "hasan_efendi" VALUES(51,47,'51:47','Ne e kemi ndërtuar qiellin me fuqinë Tonë dhe Ne e zgjerojmë atë.');
INSERT INTO "hasan_efendi" VALUES(51,48,'51:48','Edhe Tokën e kemi shtruar! Sa shtrues të mrekullueshëm që jemi!');
INSERT INTO "hasan_efendi" VALUES(51,49,'51:49','Dhe nga çdo gjë, Ne kemi krijuar dy palë, në mënyrë që ju të vini veshin!');
INSERT INTO "hasan_efendi" VALUES(51,50,'51:50','“Andaj, ikni (prej dënimit) drejt (shpërblimit të) Allahut, se unë vërtet që jam dërguar te ju prej Tij, si paralajmërues i qartë!');
INSERT INTO "hasan_efendi" VALUES(51,51,'51:51','Dhe mos adhuroni zot tjetër krahas Allahut, se unë vërtet që jam dërguar te ju prej Tij, si paralajmërues i qartë!”');
INSERT INTO "hasan_efendi" VALUES(51,52,'51:52','Kështu, sa herë që një i dërguar vinte te popujt para tyre (mekasve), ata thoshin: “Është magjistar” ose “Është i çmendur!”');
INSERT INTO "hasan_efendi" VALUES(51,53,'51:53','Vallë, a mos ia kanë pasuar këtë njëri-tjetrit? Jo, por ata vetë janë popull që e kanë tepruar! ');
INSERT INTO "hasan_efendi" VALUES(51,54,'51:54','Prandaj shmangu prej atyre; ti nuk do të qortohesh për këtë!');
INSERT INTO "hasan_efendi" VALUES(51,55,'51:55','Por këshillo, se vërtet këshilla u bën dobi besimtarëve.');
INSERT INTO "hasan_efendi" VALUES(51,56,'51:56','Xhindet dhe njerëzit i kam krijuar vetëm që të Më adhurojnë.');
INSERT INTO "hasan_efendi" VALUES(51,57,'51:57','Unë nuk kërkoj prej tyre risk, as nuk dëshiroj që të Më ushqejnë.');
INSERT INTO "hasan_efendi" VALUES(51,58,'51:58','Vërtet, Allahu është Furnizuesi i vetëm, i Fuqishmi, i Plotpushtetshmi.');
INSERT INTO "hasan_efendi" VALUES(51,59,'51:59','Pa dyshim, ata që kanë bërë të këqija, do të kenë pjesën e tyre të dënimit, të ngjashme me pjesën e shokëve të tyre (të mëparshëm) që kanë qenë si ata. Prandaj të mos Më sfidojnë që ta shpejtoj atë.');
INSERT INTO "hasan_efendi" VALUES(51,60,'51:60','Kështu, mjerë mohuesit prej Ditës që u është premtuar!');
INSERT INTO "hasan_efendi" VALUES(52,1,'52:1','Betohem për malin Tur (Sina)');
INSERT INTO "hasan_efendi" VALUES(52,2,'52:2','dhe Librin e shkruar në rreshta,');
INSERT INTO "hasan_efendi" VALUES(52,3,'52:3','në pergamenë të shpalosur');
INSERT INTO "hasan_efendi" VALUES(52,4,'52:4','dhe Shtëpinë plot vizitorë ');
INSERT INTO "hasan_efendi" VALUES(52,5,'52:5','dhe kupën e ngritur (të qiellit)');
INSERT INTO "hasan_efendi" VALUES(52,6,'52:6','dhe detin e mbushur,');
INSERT INTO "hasan_efendi" VALUES(52,7,'52:7','se, me të vërtetë, dënimi i Zotit tënd do të ndodhë');
INSERT INTO "hasan_efendi" VALUES(52,8,'52:8','dhe askush nuk mund ta pengojë.');
INSERT INTO "hasan_efendi" VALUES(52,9,'52:9','Atë Ditë qielli do të lëkundet fuqishëm');
INSERT INTO "hasan_efendi" VALUES(52,10,'52:10','e malet do të lëkunden me të madhe nga vendi!');
INSERT INTO "hasan_efendi" VALUES(52,11,'52:11','Atë Ditë, mjerë për jobesimtarët,');
INSERT INTO "hasan_efendi" VALUES(52,12,'52:12','që, të zhytur në gënjeshtra, argëtoheshin!');
INSERT INTO "hasan_efendi" VALUES(52,13,'52:13','Atë Ditë, ata do të hidhen me forcë në zjarrin e Xhehenemit.');
INSERT INTO "hasan_efendi" VALUES(52,14,'52:14','(Do t’u thuhet): “Ky është zjarri që e keni mohuar!');
INSERT INTO "hasan_efendi" VALUES(52,15,'52:15','A është kjo magji apo nuk shihni?');
INSERT INTO "hasan_efendi" VALUES(52,16,'52:16','Përcëllohuni në të! E duroni apo nuk e duroni, njësoj është për ju. Në të vërtetë, ky është “shpërblimi” i vetëm për atë që keni bërë”.');
INSERT INTO "hasan_efendi" VALUES(52,17,'52:17','Vërtet, besimtarët e devotshëm do të gjenden në kopshtet e Xhenetit dhe në lumturi,');
INSERT INTO "hasan_efendi" VALUES(52,18,'52:18','duke u kënaqur me atë që u ka dhënë Zoti i tyre, sepse Ai i ka ruajtur prej dënimit të Zjarrit flakërues.');
INSERT INTO "hasan_efendi" VALUES(52,19,'52:19','(Do t’u thuhet): “Hani e pini me kënaqësi, si shpërblim për atë që keni bërë!”');
INSERT INTO "hasan_efendi" VALUES(52,20,'52:20','Të mbështetur në divane të renditura, ata do të martohen me hyri sybukura.');
INSERT INTO "hasan_efendi" VALUES(52,21,'52:21','Ne do t’i bashkojmë besimtarët e vërtetë me ata pasardhës të tyre që i ndjekin në besim dhe nuk do t’u pakësojmë asgjë nga shpërblimi për veprat e tyre. Çdo njeri është peng i veprave të veta.');
INSERT INTO "hasan_efendi" VALUES(52,22,'52:22','Dhe do t’ua zgjerojmë dhuntitë atyre me fruta dhe mish, si ua ka ënda.');
INSERT INTO "hasan_efendi" VALUES(52,23,'52:23','Atje do t’ia kalojnë njëri-tjetrit gotat e mbushura e nuk do të ketë biseda të kota e as gjynahe.');
INSERT INTO "hasan_efendi" VALUES(52,24,'52:24','Rreth tyre do të sillen shërbyes, djelmosha si margaritarë të fshehur.');
INSERT INTO "hasan_efendi" VALUES(52,25,'52:25','Ata do të afrohen, duke biseduar me njëri-tjetrin');
INSERT INTO "hasan_efendi" VALUES(52,26,'52:26','e duke thënë: “Më parë, Ne kemi pasur frikë në familjet tona (nga gjykimi i Allahut),');
INSERT INTO "hasan_efendi" VALUES(52,27,'52:27','por, Allahu na ka dhuruar mëshirë dhe na ka ruajtur nga era përcëlluese (e Zjarrit).');
INSERT INTO "hasan_efendi" VALUES(52,28,'52:28','Ne qysh më parë i jemi falur Atij. Ai është vërtet Bamirës dhe Mëshirëplotë”.');
INSERT INTO "hasan_efendi" VALUES(52,29,'52:29','Pra, paralajmëro (o Muhamed), sepse ti, për dhuntinë e Zotit tënd, nuk je as fallxhi, as i çmendur!');
INSERT INTO "hasan_efendi" VALUES(52,30,'52:30','Vërtet thonë ata : “Ai është vjershëtar, të presim derisa ta zhdukë vdekja”?!');
INSERT INTO "hasan_efendi" VALUES(52,31,'52:31','Thuaju: “Prisni, se edhe unë po pres bashkë me ju!”');
INSERT INTO "hasan_efendi" VALUES(52,32,'52:32','A mos u vjen kjo nga mendja e tyre apo ata janë popull i ndërkryer?');
INSERT INTO "hasan_efendi" VALUES(52,33,'52:33','Vërtet thonë ata: “Ai e ka trilluar atë ”?! Nuk është kështu, por ata nuk besojnë.');
INSERT INTO "hasan_efendi" VALUES(52,34,'52:34','Atëherë le të sjellin një libër të ngjashëm me të, nëse ajo që thonë është e vërtetë !');
INSERT INTO "hasan_efendi" VALUES(52,35,'52:35','A mos vallë, ata janë krijuar nga hiçi apo mos janë ata krijues të vetvetes?!');
INSERT INTO "hasan_efendi" VALUES(52,36,'52:36','A mos vallë ata kanë krijuar qiejt dhe Tokën?! Nuk është kështu, por ata nuk janë aspak të bindur.');
INSERT INTO "hasan_efendi" VALUES(52,37,'52:37','A mos zotërojnë thesaret e Zotit tënd?! Apo mos janë ata sunduesit (e gjithçkaje)?!');
INSERT INTO "hasan_efendi" VALUES(52,38,'52:38','A mos kanë ndonjë shkallë (për në qiell) e përgjojnë Atë?! Atëherë, le të sjellë përgjuesi i tyre dëshmi të qartë!');
INSERT INTO "hasan_efendi" VALUES(52,39,'52:39','Vallë, për Atë janë vajzat e për ju djemtë?!');
INSERT INTO "hasan_efendi" VALUES(52,40,'52:40','A mos po kërkon ti shpërblim prej tyre, që të kenë frikë se mos po ngarkohen me detyrim?!');
INSERT INTO "hasan_efendi" VALUES(52,41,'52:41','A mos zotërojnë të padukshmen, kështu që mund ta përshkruajnë?!');
INSERT INTO "hasan_efendi" VALUES(52,42,'52:42','A mos duan të ngrenë kurthe kundër teje? Ta dinë se vetëm mohuesit do të bien në kurthe!');
INSERT INTO "hasan_efendi" VALUES(52,43,'52:43','A mos ata kanë zot tjetër, veç Allahut?! Allahu është i Lartësuar mbi gjithçka që ia veshin Atij!');
INSERT INTO "hasan_efendi" VALUES(52,44,'52:44','Edhe sikur të shihnin se po bie një copë nga qielli, ata do të thoshin: “Është veç një grumbull resh”!');
INSERT INTO "hasan_efendi" VALUES(52,45,'52:45','Prandaj, lëri ata të vazhdojnë kështu, derisa të takohen me Ditën, në të cilën, do të zalisen (nga frika),');
INSERT INTO "hasan_efendi" VALUES(52,46,'52:46','me Ditën, në të cilën, dinakëritë e tyre nuk do t’u bëjnë asnjë dobi dhe kur ata nuk do t’i ndihmojë askush.');
INSERT INTO "hasan_efendi" VALUES(52,47,'52:47','Në të vërtetë, për keqbërësit ka edhe një dënim tjetër para këtij; por shumica e tyre nuk e dinë.');
INSERT INTO "hasan_efendi" VALUES(52,48,'52:48','Prandaj, duro për gjykimin e Zotit tënd se, me të vërtetë, ti je nën Sytë Tanë! Lavdëroje duke e falënderuar Zotin tënd kur të ngrihesh;');
INSERT INTO "hasan_efendi" VALUES(52,49,'52:49','edhe gjatë natës lavdëroje Atë, si dhe pas perëndimit të yjeve!');
INSERT INTO "hasan_efendi" VALUES(53,1,'53:1','Betohem në yllin kur perëndon,');
INSERT INTO "hasan_efendi" VALUES(53,2,'53:2','se shoku juaj (Muhamedi a.s.) nuk ka humbur e nuk ka devijuar!');
INSERT INTO "hasan_efendi" VALUES(53,3,'53:3','Ai nuk flet sipas qejfit të vet,');
INSERT INTO "hasan_efendi" VALUES(53,4,'53:4','por ajo (që thotë) është vetëm shpallje hyjnore, që i vjen atij.');
INSERT INTO "hasan_efendi" VALUES(53,5,'53:5','Atë ia ka mësuar atij një (engjëll) shumë i fuqishëm (Xhebraili),');
INSERT INTO "hasan_efendi" VALUES(53,6,'53:6','mendjelartë, që iu paraqit qartë,');
INSERT INTO "hasan_efendi" VALUES(53,7,'53:7','në horizontin më të lartë.');
INSERT INTO "hasan_efendi" VALUES(53,8,'53:8','Pastaj iu afrua e iu lëshua,');
INSERT INTO "hasan_efendi" VALUES(53,9,'53:9','sa largësia midis dy harqeve ose edhe më afër,');
INSERT INTO "hasan_efendi" VALUES(53,10,'53:10','e i shpalli robit të Allahut, atë që i shpalli.');
INSERT INTO "hasan_efendi" VALUES(53,11,'53:11','Zemra (e Muhamedit) nuk gënjen për atë që ka parë.');
INSERT INTO "hasan_efendi" VALUES(53,12,'53:12','Vallë, a do ta kundërshtonit atë për çfarë ka parë,');
INSERT INTO "hasan_efendi" VALUES(53,13,'53:13','ndërkohë që ai e ka parë atë edhe një herë tjetër,');
INSERT INTO "hasan_efendi" VALUES(53,14,'53:14','te Sidretul Munteha-ja,');
INSERT INTO "hasan_efendi" VALUES(53,15,'53:15','ku ndodhet Xheneti i Strehimit.');
INSERT INTO "hasan_efendi" VALUES(53,16,'53:16','Kur Sidrën çfarë nuk e mbulonte?! ');
INSERT INTO "hasan_efendi" VALUES(53,17,'53:17','Shikimi i tij nuk u shmang dhe as nuk e kaloi masën,');
INSERT INTO "hasan_efendi" VALUES(53,18,'53:18','por ai pa disa nga mrekullitë më madhështore të Zotit të tij!');
INSERT INTO "hasan_efendi" VALUES(53,19,'53:19','A e keni parë Latën dhe Uzzën?');
INSERT INTO "hasan_efendi" VALUES(53,20,'53:20','Po Menatën, të tretën, të fundit?');
INSERT INTO "hasan_efendi" VALUES(53,21,'53:21','Vallë, për ju janë djemtë, ndërsa për Atë vajzat?!');
INSERT INTO "hasan_efendi" VALUES(53,22,'53:22','Kjo ndarje është e padrejtë.');
INSERT INTO "hasan_efendi" VALUES(53,23,'53:23','Këta janë vetëm emra që ua keni ngjitur (idhujve) ju dhe të parët tuaj, kurse Allahu nuk ju ka dërguar për ata asnjë provë. Ata ndjekin vetëm hamendjet dhe atë që ua ka qejfi, ndonëse u ka ardhur prej Zotit të tyre udhëzimi (për rrugën e drejtë).');
INSERT INTO "hasan_efendi" VALUES(53,24,'53:24','A do të ketë njeriu gjithçka që dëshiron?');
INSERT INTO "hasan_efendi" VALUES(53,25,'53:25','Vetëm Allahut i përkasin bota tjetër dhe kjo botë!');
INSERT INTO "hasan_efendi" VALUES(53,26,'53:26','Sa engjëj ka në qiej, ndërmjetësimi i të cilëve nuk bën asnjë dobi, veçse pasi të japë leje Allahu, për kë të dojë e të pëlqejë Vetë!');
INSERT INTO "hasan_efendi" VALUES(53,27,'53:27','Vërtet, ata që nuk besojnë në jetën tjetër, u japin engjëjve emra femërorë.');
INSERT INTO "hasan_efendi" VALUES(53,28,'53:28','Ata nuk kanë asnjë njohuri për këtë, por ndjekin vetëm hamendjen, ndonëse hamendja nuk mund ta zëvendësojë aspak të vërtetën.');
INSERT INTO "hasan_efendi" VALUES(53,29,'53:29','Andaj, ti (o Muhamed) largohu prej atij, që i kthen kurrizin Kuranit dhe, që dëshiron vetëm jetën e kësaj bote!');
INSERT INTO "hasan_efendi" VALUES(53,30,'53:30','Kaq arrin gjithë dija e tyre! Në të vërtetë, Zoti yt i njeh më së miri ata që janë shmangur nga rruga e Tij dhe ata që ndjekin rrugën e drejtë.');
INSERT INTO "hasan_efendi" VALUES(53,31,'53:31','Allahut i përket gjithçka që gjendet në qiej dhe në Tokë, për t’i dënuar ata që bëjnë keq, sipas veprave të veta e, për t’i shpërblyer ata që bëjnë mirë, me shpërblimin më të mirë.');
INSERT INTO "hasan_efendi" VALUES(53,32,'53:32','Për ata që ruhen prej gjynaheve të mëdha dhe veprave imorale, përveç gjynaheve të vogla, me të vërtetë, Zoti yt është i Gjerë në falje. Ai ju njeh mjaft mirë, qysh se ju krijoi nga dheu dhe qysh se keni qenë embrion në barkun e nënave tuaja. Prandaj, mos u lavdëroni me pafajësinë tuaj! Ai i njeh më së miri ata që ruhen nga gjynahet.');
INSERT INTO "hasan_efendi" VALUES(53,33,'53:33','A e ke vënë re atë që shmanget (nga e vërteta)');
INSERT INTO "hasan_efendi" VALUES(53,34,'53:34','dhe jep pak e pa qejf?');
INSERT INTO "hasan_efendi" VALUES(53,35,'53:35','A i di ai vallë gjërat e padukshme, a i sheh ato?');
INSERT INTO "hasan_efendi" VALUES(53,36,'53:36','Vallë, a nuk ka dëgjuar ai për atë që thuhet në Shkrimet e Musait');
INSERT INTO "hasan_efendi" VALUES(53,37,'53:37','dhe të Ibrahimit - i cili i plotësoi detyrimet:');
INSERT INTO "hasan_efendi" VALUES(53,38,'53:38','se askush nuk do t’i mbartë gjynahet e tjetrit');
INSERT INTO "hasan_efendi" VALUES(53,39,'53:39','dhe se njeriu do të ketë vetëm atë për të cilën ka punuar;');
INSERT INTO "hasan_efendi" VALUES(53,40,'53:40','se përpjekjet e tij do të shihen');
INSERT INTO "hasan_efendi" VALUES(53,41,'53:41','dhe se do të shpërblehet me shpërblim të plotë;');
INSERT INTO "hasan_efendi" VALUES(53,42,'53:42','se te Zoti yt do të jetë kthimi;');
INSERT INTO "hasan_efendi" VALUES(53,43,'53:43','se Ai jep gazin dhe vajin');
INSERT INTO "hasan_efendi" VALUES(53,44,'53:44','dhe se Ai jep vdekjen dhe jetën;');
INSERT INTO "hasan_efendi" VALUES(53,45,'53:45','se Ai krijon çiftet, mashkull e femër,');
INSERT INTO "hasan_efendi" VALUES(53,46,'53:46','prej një pike fare kur derdhet,');
INSERT INTO "hasan_efendi" VALUES(53,47,'53:47','dhe se Atij i takon krijimi tjetër;');
INSERT INTO "hasan_efendi" VALUES(53,48,'53:48','se Ai jep pasuri dhe kënaqësi të mjaftueshme,');
INSERT INTO "hasan_efendi" VALUES(53,49,'53:49','Ai, që është Zoti i Siriusit ;');
INSERT INTO "hasan_efendi" VALUES(53,50,'53:50','se Ai i ka shkatërruar fiset e lashta Ad');
INSERT INTO "hasan_efendi" VALUES(53,51,'53:51','e Themud, duke mos lënë askënd prej tyre,');
INSERT INTO "hasan_efendi" VALUES(53,52,'53:52','si dhe popullin e Nuhut më parë, sepse qe më i padrejtë dhe rebel;');
INSERT INTO "hasan_efendi" VALUES(53,53,'53:53','se Ai i asgjësoi edhe qytetet e përmbysura (të popullit të Lutit),');
INSERT INTO "hasan_efendi" VALUES(53,54,'53:54','të cilat i mbuloi ajo që i mbuloi?!');
INSERT INTO "hasan_efendi" VALUES(53,55,'53:55','Atëherë, në çfarë dhuntie të Zotit tënd dyshon ti?');
INSERT INTO "hasan_efendi" VALUES(53,56,'53:56','Ky (Profet) është paralajmërues si ata të mëparshmit.');
INSERT INTO "hasan_efendi" VALUES(53,57,'53:57','Ora e Kiametit po afrohet');
INSERT INTO "hasan_efendi" VALUES(53,58,'53:58','dhe askush, përveç Allahut, nuk mund ta zbulojë atë!');
INSERT INTO "hasan_efendi" VALUES(53,59,'53:59','Vallë, prej kësaj fjale po çuditeni');
INSERT INTO "hasan_efendi" VALUES(53,60,'53:60','dhe po qeshni e nuk po qani?');
INSERT INTO "hasan_efendi" VALUES(53,61,'53:61','Madje, jeni edhe të shkujdesur!');
INSERT INTO "hasan_efendi" VALUES(53,62,'53:62','Më mirë përuluni në sexhde për Allahun dhe adhurojeni Atë!');
INSERT INTO "hasan_efendi" VALUES(54,1,'54:1','U afrua Ora dhe u ça Hëna! ');
INSERT INTO "hasan_efendi" VALUES(54,2,'54:2','E megjithatë, sa herë që shohin ndonjë mrekulli, ata kthejnë kurrizin dhe thonë: “Kjo është në vazhdën e magjive të tij!”');
INSERT INTO "hasan_efendi" VALUES(54,3,'54:3','Ata e mohojnë (të vërtetën) dhe pasojnë dëshirat dhe tekat e tyre, por çdo gjë është e caktuar.');
INSERT INTO "hasan_efendi" VALUES(54,4,'54:4','Sigurisht që atyre u kanë ardhur histori për t’i prapësuar (nga e keqja),');
INSERT INTO "hasan_efendi" VALUES(54,5,'54:5','një urtësi e përsosur, por paralajmërimi nuk u bën dobi.');
INSERT INTO "hasan_efendi" VALUES(54,6,'54:6','Prandaj, largohu nga ata (o Profet), deri Ditën kur do t’i thërrasë Lajmëtari në diçka të tmerrshme.');
INSERT INTO "hasan_efendi" VALUES(54,7,'54:7','Me sy të përulur do të dalin nga varret, sikur të ishin karkaleca të shpërndarë,');
INSERT INTO "hasan_efendi" VALUES(54,8,'54:8','duke shpejtuar drejt Lajmëtarit. Atëherë, mohuesit do të thonë: “Kjo është ditë e vështirë!”');
INSERT INTO "hasan_efendi" VALUES(54,9,'54:9','Para tyre (i) ka përgënjeshtëruar (shenjat Tona) populli i Nuhut. Ata e quanin gënjeshtar robin Tonë (Nuhun), i thoshin “i çmendur”, e pengonin dhe e kërcënonin.');
INSERT INTO "hasan_efendi" VALUES(54,10,'54:10','Kështu, ai iu lut Zotit të vet: “Unë jam i mundur, andaj më ndihmo!”');
INSERT INTO "hasan_efendi" VALUES(54,11,'54:11','Dhe Ne i hapëm dyert e qiellit me ujë të rrëmbyeshëm');
INSERT INTO "hasan_efendi" VALUES(54,12,'54:12','dhe i shpërthyem burimet e tokës, e u takuan ujërat për një qëllim të paracaktuar.');
INSERT INTO "hasan_efendi" VALUES(54,13,'54:13','Ndërsa Nuhun e bartëm në një barkë (të ndërtuar) prej dërrasash dhe gozhdësh,');
INSERT INTO "hasan_efendi" VALUES(54,14,'54:14','e cila lundronte nën Sytë Tanë: si shpërblim për atë (Nuhun a.s.) i cili u mohua.');
INSERT INTO "hasan_efendi" VALUES(54,15,'54:15','Ne e kemi bërë atë si shenjë, por a ka kush që ia vë veshin?');
INSERT INTO "hasan_efendi" VALUES(54,16,'54:16','Eh, sa i tmerrshëm qe dënimi Im pas paralajmërimeve të Mia!');
INSERT INTO "hasan_efendi" VALUES(54,17,'54:17','Vërtet, Ne e kemi bërë Kuranin të lehtë për t’u kuptuar dhe kujtuar, prandaj, a ka ndonjë që të marrë këshillë?!');
INSERT INTO "hasan_efendi" VALUES(54,18,'54:18','Edhe fisi Ad e quajti gënjeshtar (të dërguarin Tonë), por sa i tmerrshëm qe dënimi Im pas paralajmërimeve të Mia!');
INSERT INTO "hasan_efendi" VALUES(54,19,'54:19','Ne u dërguam atyre në një ditë fatkeqe një erë të stuhishme të pandërprerë,');
INSERT INTO "hasan_efendi" VALUES(54,20,'54:20','e cila i nxirrte njerëzit, sikur të ishin trungje të shkulura hurmash.');
INSERT INTO "hasan_efendi" VALUES(54,21,'54:21','Eh, sa i tmerrshëm qe dënimi Im pas paralajmërimeve të Mia!');
INSERT INTO "hasan_efendi" VALUES(54,22,'54:22','Vërtet, Ne e kemi bërë Kuranin të lehtë për t’u kuptuar dhe kujtuar, prandaj, a ka ndonjë që të marrë këshillë?!');
INSERT INTO "hasan_efendi" VALUES(54,23,'54:23','Edhe fisi Themud i përgënjeshtroi paralajmërimet Tona.');
INSERT INTO "hasan_efendi" VALUES(54,24,'54:24','Ata (themudasit) thanë: “Vallë, a të ndjekim një njeri nga mesi ynë? Atëherë do të ishim vërtet në rrugë të humbur dhe të çmendur.');
INSERT INTO "hasan_efendi" VALUES(54,25,'54:25','Vallë, vetëm atij mes nesh t’i dërgohet Shpallja? Jo, ai është gënjeshtar mendjemadh!”');
INSERT INTO "hasan_efendi" VALUES(54,26,'54:26','(Të dërguarit iu tha): “Nesër do ta marrin vesh ata se, kush është gënjeshtari mendjemadh!');
INSERT INTO "hasan_efendi" VALUES(54,27,'54:27','Ne po u dërgojmë devenë për t’i sprovuar, andaj ti (o Salih) priti ata dhe bëhu i durueshëm!');
INSERT INTO "hasan_efendi" VALUES(54,28,'54:28','Dhe lajmëroji ata se uji do të ndahet ndërmjet tyre e devesë dhe çdo vakt do të shihet me rend!”');
INSERT INTO "hasan_efendi" VALUES(54,29,'54:29','Por ata thirrën shokun e tyre, i cili e mori dhe e theri.');
INSERT INTO "hasan_efendi" VALUES(54,30,'54:30','Eh, sa i tmerrshëm qe dënimi Im pas paralajmërimeve të Mia!');
INSERT INTO "hasan_efendi" VALUES(54,31,'54:31','Ne dërguam mbi ta një ulërimë të vetme e ata u bënë si sana e copëtuar në pleme.');
INSERT INTO "hasan_efendi" VALUES(54,32,'54:32','Vërtet, Ne e kemi bërë Kuranin të lehtë për t’u kuptuar dhe kujtuar, prandaj, a ka ndonjë që të marrë këshillë?!');
INSERT INTO "hasan_efendi" VALUES(54,33,'54:33','Edhe populli i Lutit nuk i besoi paralajmërimet.');
INSERT INTO "hasan_efendi" VALUES(54,34,'54:34','Ne dërguam një stuhi gurësh mbi ta, duke hequr familjen e Lutit, që e shpëtuam në agim,');
INSERT INTO "hasan_efendi" VALUES(54,35,'54:35','falë mëshirës Sonë. Ja, kështu Ne i shpërblejmë ata që falënderojnë.');
INSERT INTO "hasan_efendi" VALUES(54,36,'54:36','Në fakt, Luti i paralajmëroi ata për fuqinë e dënimit Tonë, por ata i vunë në dyshim paralajmërimet.');
INSERT INTO "hasan_efendi" VALUES(54,37,'54:37','Ata i kërkuan t’ua dorëzonte mysafirët e tij. Por Ne ua verbuam sytë (e u thamë): “Shijoni dënimin Tim, tani që e dëgjuat paralajmërimin Tim!”');
INSERT INTO "hasan_efendi" VALUES(54,38,'54:38','Dhe të nesërmen në mëngjes, ata i goditi dënimi i përhershëm.');
INSERT INTO "hasan_efendi" VALUES(54,39,'54:39','“Shijoni dënimin Tim, tani që e dëgjuat paralajmërimin Tim!”');
INSERT INTO "hasan_efendi" VALUES(54,40,'54:40','Vërtet, Ne e kemi bërë Kuranin të lehtë për t’u kuptuar dhe kujtuar, prandaj, a ka ndonjë që të marrë këshillë?!');
INSERT INTO "hasan_efendi" VALUES(54,41,'54:41','Edhe njerëzve të Faraonit u patën ardhur paralajmërimet,');
INSERT INTO "hasan_efendi" VALUES(54,42,'54:42','por ata i mohuan të gjitha shenjat Tona, andaj Ne i mbërthyem ata, ashtu siç dënon i Plotfuqishmi dhe i Plotpushtetshmi.');
INSERT INTO "hasan_efendi" VALUES(54,43,'54:43','Vallë, jobesimtarët tuaj janë më të mirë se këta (popuj të shkatërruar më parë), apo ju keni ndonjë paprekshmëri në librat e shpallur?');
INSERT INTO "hasan_efendi" VALUES(54,44,'54:44','Vërtet thonë ata: “Ne jemi grup i pathyeshëm”?!');
INSERT INTO "hasan_efendi" VALUES(54,45,'54:45','Së shpejti do të thyhet grupi dhe (secili prej tyre) do t’ia mbathë.');
INSERT INTO "hasan_efendi" VALUES(54,46,'54:46','Madje, Ora (e Kiametit) do të jetë takimi i tyre dhe Ora do të jetë më e tmerrshme dhe më e hidhët.');
INSERT INTO "hasan_efendi" VALUES(54,47,'54:47','Pa dyshim, keqbërësit janë të humbur (në këtë jetë) dhe do të digjen në zjarr (në jetën tjetër).');
INSERT INTO "hasan_efendi" VALUES(54,48,'54:48','Ditën e Gjykimit ata do të tërhiqen me fytyrat e tyre në zjarr (e do t’u thuhet): “Shijoni zjarrin e Xhehenemit!”');
INSERT INTO "hasan_efendi" VALUES(54,49,'54:49','Vërtet, Ne çdo gjë e kemi krijuar me masë të paracaktuar. ');
INSERT INTO "hasan_efendi" VALUES(54,50,'54:50','Ne urdhërojmë vetëm një herë dhe vullneti Ynë përmbushet sa çel e mbyll sytë.');
INSERT INTO "hasan_efendi" VALUES(54,51,'54:51','Ne tashmë i kemi shkatërruar të ngjashmit me ju (në mosbesim). Prandaj, a ka ndonjë që të marrë këshillë?!');
INSERT INTO "hasan_efendi" VALUES(54,52,'54:52','Çdo bëmë e tyre është shënuar në librat (e engjëjve që regjistrojnë veprat).');
INSERT INTO "hasan_efendi" VALUES(54,53,'54:53','Çdo gjë, e madhe a e vogël qoftë, është shënuar (në Leuhi Mahfudh).');
INSERT INTO "hasan_efendi" VALUES(54,54,'54:54','Vërtet, besimtarët e devotshëm do të jenë në kopshtet e Xhenetit, ku rrjedhin lumenj');
INSERT INTO "hasan_efendi" VALUES(54,55,'54:55','në kuvendin e së vërtetës (në Xhenet), tek një Sundues i Plotfuqishëm.');
INSERT INTO "hasan_efendi" VALUES(55,1,'55:1','I Gjithëmëshirshmi');
INSERT INTO "hasan_efendi" VALUES(55,2,'55:2','ua ka mësuar Kuranin (robërve të Tij),');
INSERT INTO "hasan_efendi" VALUES(55,3,'55:3','ka krijuar njeriun');
INSERT INTO "hasan_efendi" VALUES(55,4,'55:4','dhe i ka mësuar atij të folurit e qartë.');
INSERT INTO "hasan_efendi" VALUES(55,5,'55:5','Dielli dhe Hëna ndjekin rrugën e caktuar.');
INSERT INTO "hasan_efendi" VALUES(55,6,'55:6','Bimët dhe pemët i përulen (Allahut).');
INSERT INTO "hasan_efendi" VALUES(55,7,'55:7','Ai e ka ngritur qiellin lart dhe ka vënë Balancën (për çdo gjë),');
INSERT INTO "hasan_efendi" VALUES(55,8,'55:8','që ju të mos e shkelni atë.');
INSERT INTO "hasan_efendi" VALUES(55,9,'55:9','Prandaj matni drejt e mos hani në peshë!');
INSERT INTO "hasan_efendi" VALUES(55,10,'55:10','Ai e ka bërë Tokën për krijesat.');
INSERT INTO "hasan_efendi" VALUES(55,11,'55:11','Aty ka fruta të llojllojshme dhe palma hurmash me frutat si bistakë,');
INSERT INTO "hasan_efendi" VALUES(55,12,'55:12','ka drithëra me gjethe e kërcej dhe bimë me erë të mirë.');
INSERT INTO "hasan_efendi" VALUES(55,13,'55:13','Pra, cilat dhunti të Zotit tuaj po i mohoni?! ');
INSERT INTO "hasan_efendi" VALUES(55,14,'55:14','Ai e krijoi njeriun prej baltës së thatë, ashtu si vorba,');
INSERT INTO "hasan_efendi" VALUES(55,15,'55:15','ndërsa xhindet i krijoi prej flakës së zjarrit.');
INSERT INTO "hasan_efendi" VALUES(55,16,'55:16','Pra, cilat dhunti të Zotit tuaj po i mohoni?!');
INSERT INTO "hasan_efendi" VALUES(55,17,'55:17','(Ai është) Zoti i dy lindjeve dhe i dy perëndimeve. ');
INSERT INTO "hasan_efendi" VALUES(55,18,'55:18','Pra, cilat dhunti të Zotit tuaj po i mohoni?!');
INSERT INTO "hasan_efendi" VALUES(55,19,'55:19','Ai i ka lëshuar dy dete që të takohen,');
INSERT INTO "hasan_efendi" VALUES(55,20,'55:20','por, midis tyre ka një pengesë që nuk mund ta kapërcejnë.');
INSERT INTO "hasan_efendi" VALUES(55,21,'55:21','Pra, cilat dhunti të Zotit tuaj po i mohoni?!');
INSERT INTO "hasan_efendi" VALUES(55,22,'55:22','Prej tyre nxirren margaritarë dhe korale.');
INSERT INTO "hasan_efendi" VALUES(55,23,'55:23','Pra, cilat dhunti të Zotit tuaj po i mohoni?!');
INSERT INTO "hasan_efendi" VALUES(55,24,'55:24','Atij i përkasin edhe anijet që lundrojnë nëpër dete e që lartohen si male.');
INSERT INTO "hasan_efendi" VALUES(55,25,'55:25','Pra, cilat dhunti të Zotit tuaj po i mohoni?!');
INSERT INTO "hasan_efendi" VALUES(55,26,'55:26','Çdo gjë që është në Tokë, do të zhduket,');
INSERT INTO "hasan_efendi" VALUES(55,27,'55:27','e do të mbetet vetëm Fytyra (Qenia) e Zotit Tënd, plot Madhëri dhe Nderim.');
INSERT INTO "hasan_efendi" VALUES(55,28,'55:28','Pra, cilat dhunti të Zotit tuaj po i mohoni?!');
INSERT INTO "hasan_efendi" VALUES(55,29,'55:29','Atij i lutet gjithkush që ndodhet në qiej dhe në Tokë e në çdo kohë Ai ushtron fuqinë e gjithëmbarshme.');
INSERT INTO "hasan_efendi" VALUES(55,30,'55:30','Pra, cilat dhunti të Zotit tuaj po i mohoni?!');
INSERT INTO "hasan_efendi" VALUES(55,31,'55:31','O ju dy barrët e Tokës (njerëz dhe xhinde), së shpejti do të caktojmë gjykimin tuaj!');
INSERT INTO "hasan_efendi" VALUES(55,32,'55:32','Pra, cilat dhunti të Zotit tuaj po i mohoni?!');
INSERT INTO "hasan_efendi" VALUES(55,33,'55:33','O xhinde dhe njerëz, nëse keni fuqi të përshkoni kufijtë e qiejve dhe të Tokës, atëherë përshkojini ato! Por nuk do të mund ta bëni këtë, përveçse me një fuqi të madhe (nga Allahu)!');
INSERT INTO "hasan_efendi" VALUES(55,34,'55:34','Pra, cilat dhunti të Zotit tuaj po i mohoni?!');
INSERT INTO "hasan_efendi" VALUES(55,35,'55:35','Kundër jush do të dërgohen flakë zjarri dhe tunxh i shkrirë (si dënim për mohimin e dhuntive të Allahut) dhe ju nuk do të mund të mbroheni.');
INSERT INTO "hasan_efendi" VALUES(55,36,'55:36','Pra, cilat dhunti të Zotit tuaj po i mohoni?!');
INSERT INTO "hasan_efendi" VALUES(55,37,'55:37','Veç kur të çahet qielli dhe të bëhet i kuq si trëndafili (në Ditën e Kiametit)!');
INSERT INTO "hasan_efendi" VALUES(55,38,'55:38','Pra, cilat dhunti të Zotit tuaj po i mohoni?!');
INSERT INTO "hasan_efendi" VALUES(55,39,'55:39','Atë Ditë njerëzit dhe xhindet nuk do të pyeten për gjynahet e tyre. ');
INSERT INTO "hasan_efendi" VALUES(55,40,'55:40','Pra, cilat dhunti të Zotit tuaj po i mohoni?!');
INSERT INTO "hasan_efendi" VALUES(55,41,'55:41','Keqbërësit do të njihen sipas shenjave dhe do të kapen për ballukesh dhe për këmbësh.');
INSERT INTO "hasan_efendi" VALUES(55,42,'55:42','Pra, cilat dhunti të Zotit tuaj po i mohoni?!');
INSERT INTO "hasan_efendi" VALUES(55,43,'55:43','Ja, ky është Xhehenemi, të cilin e mohonin keqbërësit!');
INSERT INTO "hasan_efendi" VALUES(55,44,'55:44','Ata do të sillen në mes të zjarrit dhe ujit të valuar në kulm.');
INSERT INTO "hasan_efendi" VALUES(55,45,'55:45','Pra, cilat dhunti të Zotit tuaj po i mohoni?!');
INSERT INTO "hasan_efendi" VALUES(55,46,'55:46','Për ata që i frikësohen paraqitjes para Zotit të vet, do të ketë dy kopshte!');
INSERT INTO "hasan_efendi" VALUES(55,47,'55:47','Pra, cilat dhunti të Zotit tuaj po i mohoni?!');
INSERT INTO "hasan_efendi" VALUES(55,48,'55:48','(Kopshtet do të jenë) të mbjellë me pemë që bëjnë hije.');
INSERT INTO "hasan_efendi" VALUES(55,49,'55:49','Pra, cilat dhunti të Zotit tuaj po i mohoni?!');
INSERT INTO "hasan_efendi" VALUES(55,50,'55:50','Në ta do të ketë dy burime që rrjedhin.');
INSERT INTO "hasan_efendi" VALUES(55,51,'55:51','Pra, cilat dhunti të Zotit tuaj po i mohoni?!');
INSERT INTO "hasan_efendi" VALUES(55,52,'55:52','Dhe prej çdo frute do të ketë nga dy lloje.');
INSERT INTO "hasan_efendi" VALUES(55,53,'55:53','Pra, cilat dhunti të Zotit tuaj po i mohoni?!');
INSERT INTO "hasan_efendi" VALUES(55,54,'55:54','Ata do të jenë të mbështetur në shtroje, anash me armaç mëndafshi e frutat e dy kopshteve do t’i kenë afër sa t’i kapin me dorë.');
INSERT INTO "hasan_efendi" VALUES(55,55,'55:55','Pra, cilat dhunti të Zotit tuaj po i mohoni?!');
INSERT INTO "hasan_efendi" VALUES(55,56,'55:56','Atje do të ketë virgjëresha syulur, të cilat para tyre nuk i kanë prekur as njerëz, as xhinde.');
INSERT INTO "hasan_efendi" VALUES(55,57,'55:57','Pra, cilat dhunti të Zotit tuaj po i mohoni?!');
INSERT INTO "hasan_efendi" VALUES(55,58,'55:58','Ato do të jenë si rubinë dhe korale.');
INSERT INTO "hasan_efendi" VALUES(55,59,'55:59','Pra, cilat dhunti të Zotit tuaj po i mohoni?!');
INSERT INTO "hasan_efendi" VALUES(55,60,'55:60','A ka shpërblim tjetër për të mirën, veç të mirës?!');
INSERT INTO "hasan_efendi" VALUES(55,61,'55:61','Pra, cilat dhunti të Zotit tuaj po i mohoni?!');
INSERT INTO "hasan_efendi" VALUES(55,62,'55:62','Nën këto dy kopshte, do të ketë edhe dy kopshte të tjera.');
INSERT INTO "hasan_efendi" VALUES(55,63,'55:63','Pra, cilat dhunti të Zotit tuaj po i mohoni?!');
INSERT INTO "hasan_efendi" VALUES(55,64,'55:64','Ato do të jenë të stolisura me gjelbërim.');
INSERT INTO "hasan_efendi" VALUES(55,65,'55:65','Pra, cilat dhunti të Zotit tuaj po i mohoni?!');
INSERT INTO "hasan_efendi" VALUES(55,66,'55:66','Aty do të jenë dy burime që gulfojnë.');
INSERT INTO "hasan_efendi" VALUES(55,67,'55:67','Pra, cilat dhunti të Zotit tuaj po i mohoni?!');
INSERT INTO "hasan_efendi" VALUES(55,68,'55:68','Atje do të ketë fruta, palma hurmash dhe shegë.');
INSERT INTO "hasan_efendi" VALUES(55,69,'55:69','Pra, cilat dhunti të Zotit tuaj po i mohoni?!');
INSERT INTO "hasan_efendi" VALUES(55,70,'55:70','Në to do të ketë bukuroshe virtytesh të mrekullueshme.');
INSERT INTO "hasan_efendi" VALUES(55,71,'55:71','Pra, cilat dhunti të Zotit tuaj po i mohoni?!');
INSERT INTO "hasan_efendi" VALUES(55,72,'55:72','(Atje do të ketë) hyri syzeza nëpër tenda.');
INSERT INTO "hasan_efendi" VALUES(55,73,'55:73','Pra, cilat dhunti të Zotit tuaj po i mohoni?!');
INSERT INTO "hasan_efendi" VALUES(55,74,'55:74','Ato hyri nuk i kanë prekur para tyre as njerëz, as xhinde.');
INSERT INTO "hasan_efendi" VALUES(55,75,'55:75','Pra, cilat dhunti të Zotit tuaj po i mohoni?!');
INSERT INTO "hasan_efendi" VALUES(55,76,'55:76','Ata do të rrinë mbështetur në shtroje të gjelbra e qilima të bukur.');
INSERT INTO "hasan_efendi" VALUES(55,77,'55:77','Pra, cilat dhunti të Zotit tuaj po i mohoni?!');
INSERT INTO "hasan_efendi" VALUES(55,78,'55:78','Qoftë lartësuar emri i Zotit tënd, plot Madhëri dhe Nderim!');
INSERT INTO "hasan_efendi" VALUES(56,1,'56:1','Kur të ndodhë Ngjarja (Kiameti),');
INSERT INTO "hasan_efendi" VALUES(56,2,'56:2','ardhjen e saj askush nuk do ta mohojë.');
INSERT INTO "hasan_efendi" VALUES(56,3,'56:3','Disa do t’i ulë e disa do t’i lartësojë.');
INSERT INTO "hasan_efendi" VALUES(56,4,'56:4','Kur Toka të dridhet fuqishëm,');
INSERT INTO "hasan_efendi" VALUES(56,5,'56:5','malet të thërrmohen copë-copë');
INSERT INTO "hasan_efendi" VALUES(56,6,'56:6','e të shndërrohen në pluhur të shpërndarë,');
INSERT INTO "hasan_efendi" VALUES(56,7,'56:7','ju, (o njerëz) do të ndaheni në tri grupe:');
INSERT INTO "hasan_efendi" VALUES(56,8,'56:8','njerëzit e së djathtës - sa të nderuar do të jenë fatlumët!');
INSERT INTO "hasan_efendi" VALUES(56,9,'56:9','njerëzit e së majtës - sa të poshtëruar do të jenë fatkeqët!');
INSERT INTO "hasan_efendi" VALUES(56,10,'56:10','dhe ata që kanë prirë (në besim) e që do të prijnë (edhe në Xhenet).');
INSERT INTO "hasan_efendi" VALUES(56,11,'56:11','Ata janë të afërtit (tek Allahu),');
INSERT INTO "hasan_efendi" VALUES(56,12,'56:12','në kopshtet e kënaqësisë.');
INSERT INTO "hasan_efendi" VALUES(56,13,'56:13','Shumica e tyre janë prej të lashtëve,');
INSERT INTO "hasan_efendi" VALUES(56,14,'56:14','ndërsa një pakicë prej brezave të mëvonshëm.');
INSERT INTO "hasan_efendi" VALUES(56,15,'56:15','Ata do të qëndrojnë në divane të stolisura me ar e gurë të çmuar,');
INSERT INTO "hasan_efendi" VALUES(56,16,'56:16','të mbështetur në to përballë njëri-tjetrit.');
INSERT INTO "hasan_efendi" VALUES(56,17,'56:17','Do t’u sillen rreth (për shërbim) djelmosha përherë të rinj,');
INSERT INTO "hasan_efendi" VALUES(56,18,'56:18','me kupa, ibrikë dhe gota me pije të kulluar, që rrjedh');
INSERT INTO "hasan_efendi" VALUES(56,19,'56:19','e prej së cilës nuk do t’u dhembë koka dhe nuk do t’u humbë mendja,');
INSERT INTO "hasan_efendi" VALUES(56,20,'56:20','e me fruta të cilat do t’i zgjedhin vetë');
INSERT INTO "hasan_efendi" VALUES(56,21,'56:21','dhe mish shpendësh që ua ka ënda.');
INSERT INTO "hasan_efendi" VALUES(56,22,'56:22','Për ata ka edhe hyri symëdha,');
INSERT INTO "hasan_efendi" VALUES(56,23,'56:23','të ngjashme me margaritarët e ruajtur,');
INSERT INTO "hasan_efendi" VALUES(56,24,'56:24','si shpërblim për atë që kanë punuar.');
INSERT INTO "hasan_efendi" VALUES(56,25,'56:25','Aty nuk do të dëgjojnë biseda të kota, as fjalë gjynahesh,');
INSERT INTO "hasan_efendi" VALUES(56,26,'56:26','por vetëm fjalët “Paqe, paqe”!');
INSERT INTO "hasan_efendi" VALUES(56,27,'56:27','E njerëzit e së djathtës – sa të nderuar që janë fatlumët!');
INSERT INTO "hasan_efendi" VALUES(56,28,'56:28','Ata do të qëndrojnë në mes të lotusëve pa gjemba');
INSERT INTO "hasan_efendi" VALUES(56,29,'56:29','dhe bananeve të mbushura me fruta,');
INSERT INTO "hasan_efendi" VALUES(56,30,'56:30','nën hije të gjera,');
INSERT INTO "hasan_efendi" VALUES(56,31,'56:31','pranë një uji rrjedhës,');
INSERT INTO "hasan_efendi" VALUES(56,32,'56:32','dhe mes frutave të llojllojshme,');
INSERT INTO "hasan_efendi" VALUES(56,33,'56:33','të cilat kurrë nuk sosen, as nuk janë të ndaluara;');
INSERT INTO "hasan_efendi" VALUES(56,34,'56:34','dhe në shtroje të ngritura (ku i presin hyritë),');
INSERT INTO "hasan_efendi" VALUES(56,35,'56:35','të cilat Ne i kemi krijuar në një formë të veçantë (jo të lindura),');
INSERT INTO "hasan_efendi" VALUES(56,36,'56:36','dhe i kemi bërë ato virgjëresha,');
INSERT INTO "hasan_efendi" VALUES(56,37,'56:37','të dashura për bashkëshortët e tyre dhe moshatare me ta,');
INSERT INTO "hasan_efendi" VALUES(56,38,'56:38','për njerëzit e së djathtës.');
INSERT INTO "hasan_efendi" VALUES(56,39,'56:39','Janë shumë nga të lashtët');
INSERT INTO "hasan_efendi" VALUES(56,40,'56:40','e shumë edhe nga të mëvonshmit.');
INSERT INTO "hasan_efendi" VALUES(56,41,'56:41','Po njerëzit e së majtës – sa të poshtëruar janë fatkeqët!');
INSERT INTO "hasan_efendi" VALUES(56,42,'56:42','Ata do të jenë në afshin përcëllues të zjarrit dhe ujit të valuar');
INSERT INTO "hasan_efendi" VALUES(56,43,'56:43','dhe nën hijen e një tymi të zi,');
INSERT INTO "hasan_efendi" VALUES(56,44,'56:44','e cila nuk do të jetë as e freskët e as e këndshme.');
INSERT INTO "hasan_efendi" VALUES(56,45,'56:45','Ata, para kësaj, jetonin në luks');
INSERT INTO "hasan_efendi" VALUES(56,46,'56:46','dhe në vazhdimësi bënin gjynahe të rënda ,');
INSERT INTO "hasan_efendi" VALUES(56,47,'56:47','duke thënë: “Vallë, pasi të vdesim e të bëhemi pluhur e eshtra, vërtet do të ringjallemi?!');
INSERT INTO "hasan_efendi" VALUES(56,48,'56:48','Vallë, edhe të parët tanë të lashtë?!”');
INSERT INTO "hasan_efendi" VALUES(56,49,'56:49','Thuaju (o Muhamed): “Me siguri, edhe të parët, edhe të mbramët,');
INSERT INTO "hasan_efendi" VALUES(56,50,'56:50','do të tubohen në një ditë të caktuar.');
INSERT INTO "hasan_efendi" VALUES(56,51,'56:51','Dhe atëherë ju, o jobesimtarë të humbur,');
INSERT INTO "hasan_efendi" VALUES(56,52,'56:52','me siguri që do të hani nga pema e zekumit,');
INSERT INTO "hasan_efendi" VALUES(56,53,'56:53','me të cilën do të mbushni barkun.');
INSERT INTO "hasan_efendi" VALUES(56,54,'56:54','Pastaj do të pini ujë të valuar,');
INSERT INTO "hasan_efendi" VALUES(56,55,'56:55','madje do të pini ashtu siç pi deveja e etshme”.');
INSERT INTO "hasan_efendi" VALUES(56,56,'56:56','Kështu do të jetë gostia e tyre në Ditën e Llogarisë!');
INSERT INTO "hasan_efendi" VALUES(56,57,'56:57','Ne ju krijuam – e pse nuk e pranoni të vërtetën?');
INSERT INTO "hasan_efendi" VALUES(56,58,'56:58','A e keni parë farën që e hidhni?');
INSERT INTO "hasan_efendi" VALUES(56,59,'56:59','A jeni ju që e krijoni atë apo jemi Ne krijuesit e saj?');
INSERT INTO "hasan_efendi" VALUES(56,60,'56:60','Ne e caktojmë vdekjen tuaj dhe askush nuk mund të na pengojë');
INSERT INTO "hasan_efendi" VALUES(56,61,'56:61','që t’jua ndryshojmë trajtën dhe t’ju rikrijojmë ashtu si nuk e dini (në Ditën e Ringjalljes).');
INSERT INTO "hasan_efendi" VALUES(56,62,'56:62','Sigurisht, ju e dini krijimin e parë (prej një pike fare), atëherë përse nuk mendoni?!');
INSERT INTO "hasan_efendi" VALUES(56,63,'56:63','A shihni atë që mbillni?');
INSERT INTO "hasan_efendi" VALUES(56,64,'56:64','Vallë, ju e bëni atë që të mbijë apo jemi Ne rritësit e saj?');
INSERT INTO "hasan_efendi" VALUES(56,65,'56:65','Nëse duam, Ne e lëmë fyçkë dhe ju do të çuditeni');
INSERT INTO "hasan_efendi" VALUES(56,66,'56:66','(e do të thoni): “Ne jemi të dëmtuar,');
INSERT INTO "hasan_efendi" VALUES(56,67,'56:67','madje jemi krejt të privuar (nga furnizimi)!”');
INSERT INTO "hasan_efendi" VALUES(56,68,'56:68','A e shihni ujin që pini?');
INSERT INTO "hasan_efendi" VALUES(56,69,'56:69','Vallë, ju e zbrisni atë prej reve apo Ne e zbresim?');
INSERT INTO "hasan_efendi" VALUES(56,70,'56:70','Nëse duam, Ne e bëjmë atë të njelmët, andaj përse nuk jeni mirënjohës?!');
INSERT INTO "hasan_efendi" VALUES(56,71,'56:71','A e shihni zjarrin që e ndizni?');
INSERT INTO "hasan_efendi" VALUES(56,72,'56:72','Vallë, ju i keni bërë drutë për të apo Ne i kemi bërë?!');
INSERT INTO "hasan_efendi" VALUES(56,73,'56:73','Ne e kemi bërë atë përkujtues (të zjarrit të Xhehenemit) dhe, që t’u sjellë dobi nevojtarëve,');
INSERT INTO "hasan_efendi" VALUES(56,74,'56:74','prandaj lavdëroje emrin e Zotit tënd të Madhërishëm!');
INSERT INTO "hasan_efendi" VALUES(56,75,'56:75','Betohem në çastin e perëndimit të yjeve,');
INSERT INTO "hasan_efendi" VALUES(56,76,'56:76','- e ky është betim i madh, veç sikur ta dinit');
INSERT INTO "hasan_efendi" VALUES(56,77,'56:77','se ky është vërtet një Kuran i nderuar,');
INSERT INTO "hasan_efendi" VALUES(56,78,'56:78','në Librin e ruajtur.');
INSERT INTO "hasan_efendi" VALUES(56,79,'56:79','Atë (Kuranin) e prekin vetëm të pastrit.');
INSERT INTO "hasan_efendi" VALUES(56,80,'56:80','Ai është Shpallje prej Zotit të botëve.');
INSERT INTO "hasan_efendi" VALUES(56,81,'56:81','Vallë, një Fjalë të tillë ju e mohoni');
INSERT INTO "hasan_efendi" VALUES(56,82,'56:82','dhe e bëni detyrën tuaj (të përditshme) ta përgënjeshtroni?!');
INSERT INTO "hasan_efendi" VALUES(56,83,'56:83','Atëherë, përse kur (dikujt) i vjen (shpirti) në grykë');
INSERT INTO "hasan_efendi" VALUES(56,84,'56:84','para syve tuaj,');
INSERT INTO "hasan_efendi" VALUES(56,85,'56:85','- ndërkohë që Ne jemi më afër atij se ju, por ju nuk e vini re');
INSERT INTO "hasan_efendi" VALUES(56,86,'56:86','përse pra, nëse nuk do të gjykoheni (në jetën e përtejme),');
INSERT INTO "hasan_efendi" VALUES(56,87,'56:87','ju nuk ia ktheni shpirtin atij? Përgjigjjuni kësaj, nëse thoni të vërtetën!');
INSERT INTO "hasan_efendi" VALUES(56,88,'56:88','Kështu, nëse ai (në agoni) është nga ata që janë të afërt (me Allahun),');
INSERT INTO "hasan_efendi" VALUES(56,89,'56:89','do të ketë prehje, kënaqësi dhe dhunti të Xhenetit.');
INSERT INTO "hasan_efendi" VALUES(56,90,'56:90','E, nëse është nga njerëzit e së djathtës,');
INSERT INTO "hasan_efendi" VALUES(56,91,'56:91','(ai do të përshëndetet) “Paqe për ty” nga njerëzit e së djathtës.');
INSERT INTO "hasan_efendi" VALUES(56,92,'56:92','Por, nëse (ai në agoni) është nga përgënjeshtruesit e humbur,');
INSERT INTO "hasan_efendi" VALUES(56,93,'56:93','ai do të gostitet me ujë të valuar');
INSERT INTO "hasan_efendi" VALUES(56,94,'56:94','dhe do të përcëllohet në Zjarrin e flakëruar (të Xhehenemit).');
INSERT INTO "hasan_efendi" VALUES(56,95,'56:95','Pa dyshim, kjo është e Vërteta e sigurt.');
INSERT INTO "hasan_efendi" VALUES(56,96,'56:96','Andaj lavdëroje emrin e Zotit tënd të Madhërishëm!');
INSERT INTO "hasan_efendi" VALUES(57,1,'57:1','Allahun e lartëson çdo gjë që është në qiej dhe në Tokë. Ai është i Plotfuqishmi, i Urti.');
INSERT INTO "hasan_efendi" VALUES(57,2,'57:2','Atij i përket sundimi i qiejve dhe i Tokës. Ai jep jetë e vdekje dhe është i fuqishëm për çdo gjë.');
INSERT INTO "hasan_efendi" VALUES(57,3,'57:3','Ai është i Pari dhe i Mbrami, i Dukshmi (me argumente) dhe i Padukshmi (me sy). Ai është i Dijshëm për çdo gjë!');
INSERT INTO "hasan_efendi" VALUES(57,4,'57:4','Ai i krijoi qiejt dhe Tokën për gjashtë ditë, pastaj u ngrit mbi Fron. Ai e di se çfarë hyn në Tokë dhe çfarë del prej saj, çfarë zbret prej qiellit dhe çfarë ngjitet drejt tij. Ai është me ju kudo që të gjendeni; Ai sheh gjithçka që bëni.');
INSERT INTO "hasan_efendi" VALUES(57,5,'57:5','Atij i përket sundimi i qiejve dhe i Tokës; tek Allahu do të kthehet gjithçka!');
INSERT INTO "hasan_efendi" VALUES(57,6,'57:6','Ai e ndërfut natën tek dita dhe ditën tek nata. Ai e di çfarë ka çdokush në kraharor.');
INSERT INTO "hasan_efendi" VALUES(57,7,'57:7','Besoni Allahun dhe të Dërguarin e Tij dhe shpenzoni nga ato që Ai jua ka lënë trashëgim, se për ata midis jush që besojnë dhe shpenzojnë (për bamirësi), do të ketë shpërblim të madh!');
INSERT INTO "hasan_efendi" VALUES(57,8,'57:8','Çfarë keni që nuk besoni në Allahun, ndërkohë që i Dërguari i Tij ju fton që të besoni në Zotin tuaj, i Cili, tashmë, ka marrë besën prej jush?! Atëherë besoni, nëse jeni besimtarë të vërtetë!');
INSERT INTO "hasan_efendi" VALUES(57,9,'57:9','Është Ai që i ka zbritur robit të Vet shpallje të qarta për t’ju nxjerrë nga errësira në dritë. Allahu është vërtet i Butë dhe Mëshirëplotë me ju.');
INSERT INTO "hasan_efendi" VALUES(57,10,'57:10','Atëherë, çfarë keni që nuk shpenzoni në rrugën e Allahut, ndërkohë që vetëm Atij i përket trashëgimia e qiejve dhe e Tokës? Nuk janë të barabartë ata që kanë shpenzuar dhe kanë luftuar para fitores me ata që kanë shpenzuar dhe kanë luftuar më pas. Ata kanë shkallë më të lartë, mirëpo Allahu u premton të gjithëve më të mirën (Xhenetin). Allahu e di mirë atë që bëni ju.');
INSERT INTO "hasan_efendi" VALUES(57,11,'57:11','Kush është ai, që do t’i japë vullnetarisht Allahut një hua të mirë, që Ai pastaj t’ia kthejë shumëfish?! (Veç kësaj), për atë do të ketë shpërblim fisnik,');
INSERT INTO "hasan_efendi" VALUES(57,12,'57:12','ditën që do të shohësh besimtarët dhe besimtaret, me dritën e tyre që shkëlqen para dhe në të djathtë të tyre (e do t’u thuhet): “Lajm i mirë sot për ju: kopshtet e Xhenetit nëpër të cilat rrjedhin lumenjtë e ku do të banoni përgjithmonë. Kjo është fitorja më e madhe!”');
INSERT INTO "hasan_efendi" VALUES(57,13,'57:13','Atë Ditë, hipokritët dhe hipokritet do t’u thonë besimtarëve: “Na prisni edhe ne që të marrim nga drita juaj”! Por atyre do t’u thuhet: “Kthehuni prapa e kërkoni tjetër dritë!” Dhe, ndërmjet tyre, do të vendoset një mur me portë: brenda saj ka mëshirë, kurse jashtë saj ka dënim.');
INSERT INTO "hasan_efendi" VALUES(57,14,'57:14','“A nuk kemi qenë me ju?” - do të thërrasin ata. - “Po, - do t’u përgjigjen, - por ju e çuat veten në tundim, pritët, dyshuat dhe u mashtruat nga vetë dëshirat tuaja të kota, derisa arriti urdhri i Allahut, e djalli ju mashtroi për Allahun!');
INSERT INTO "hasan_efendi" VALUES(57,15,'57:15','Sot nuk pranohet prej jush kurrfarë shpërblese e as prej atyre që nuk kanë besuar: zjarri është vendbanimi juaj, ai është për ju; eh, sa vendbanim i tmerrshëm që është ai!”');
INSERT INTO "hasan_efendi" VALUES(57,16,'57:16','A nuk ka ardhur koha për besimtarët e vërtetë, që zemrat e tyre të përulen para këshillave të Allahut dhe para së Vërtetës që Ai ka shpallur e të mos bëhen si ata, që iu është dhënë Libri më parë?! Me kalimin e një kohe të gjatë, zemrat e tyre u ngurtësuan dhe shumë syresh janë të pabindur.');
INSERT INTO "hasan_efendi" VALUES(57,17,'57:17','Dijeni, se Allahu e ngjall tokën pas vdekjes së saj! Ne ju sqarojmë shpalljet, në mënyrë që ju të kuptoni!');
INSERT INTO "hasan_efendi" VALUES(57,18,'57:18','Ata që japin lëmoshë e që i japin Allahut një hua të mirë, qofshin burra ose gra, do t’u shumëfishohet përfitimi dhe do të kenë një shpërblim fisnik.');
INSERT INTO "hasan_efendi" VALUES(57,19,'57:19','Ata që besojnë Allahun dhe të dërguarit e Tij, pikërisht ata janë njerëzit e sinqertë. Dëshmorët do të kenë te Zoti i vet shpërblimin dhe dritën e tyre. Kurse ata që nuk janë besimtarë dhe i mohojnë shpalljet Tona, do të jenë banorë të zjarrit!');
INSERT INTO "hasan_efendi" VALUES(57,20,'57:20','Ta dini, se jeta në këtë botë, nuk është tjetër veçse lojë, dëfrim e zbukurim, si dhe mburrje ndërmjet jush e rivalitet për më shumë pasuri dhe fëmijë! Ajo i shëmbëllen bimës, e cila pas shiut rritet dhe i gëzon bujqit, por më vonë zverdhet, thahet dhe mbetet pa vlerë. Në botën tjetër ju presin vuajtje të mëdha ose falja e Allahut dhe kënaqësia e Tij; jeta në këtë botë është vetëm kënaqësi e rreme.');
INSERT INTO "hasan_efendi" VALUES(57,21,'57:21','Prandaj rrekuni me njëri-tjetrin, duke kërkuar falje prej Zotit tuaj dhe një kopsht të gjerë sa qielli dhe Toka, që është e përgatitur për ata që i kanë besuar Allahut dhe të dërguarve të Tij. Ajo është mirësia e Allahut, që Ai ia jep kujt të dojë; e Allahu ka mirësi të pafund.');
INSERT INTO "hasan_efendi" VALUES(57,22,'57:22','S’ka fatkeqësi që godet tokën dhe ju, që të mos jetë e shënuar në Libër (Leuhi Mahfûdh) më parë se ta krijojmë atë. Kjo për Allahun është vërtet e lehtë!');
INSERT INTO "hasan_efendi" VALUES(57,23,'57:23','(Këtë paracaktim e kemi bërë) që ju të mos dëshpëroheni për atë që ju ka kaluar, por edhe për të mos u gëzuar së tepërmi për atë që Ai ju ka dhënë. Allahu nuk i do kryelartët mburravecë');
INSERT INTO "hasan_efendi" VALUES(57,24,'57:24','e as ata që, përveçse janë vetë koprracë, i nxisin edhe të tjerët në koprraci. Kushdo që nuk ia vë veshin kësaj, le ta dijë se Allahu është i Vetëmjaftueshmi dhe i Denji për çdo lavd.');
INSERT INTO "hasan_efendi" VALUES(57,25,'57:25','Ne i çuam të dërguarit Tanë me prova të qarta dhe bashkë me ta zbritëm Librat (e shenjtë) dhe peshoren (e drejtësisë): që njerëzit të veprojnë drejt. Ne e zbritëm hekurin, në të cilin ka (material për) luftë të fuqishme dhe dobi (të tjera) për njerëzit, me qëllim që Allahu të njohë ata që e ndihmojnë Atë dhe të dërguarit e Tij, pa e parë Atë. Me të vërtetë, Allahu është i Fortë dhe i Plotfuqishëm.');
INSERT INTO "hasan_efendi" VALUES(57,26,'57:26','Sigurisht, Ne dërguam Nuhun dhe Ibrahimin e u dhuruam trashëgimtarëve të tyre Profecinë dhe Librin. Disa syresh ishin në rrugë të drejtë, kurse shumica prej tyre ishin gjynahqarë të pabindur.');
INSERT INTO "hasan_efendi" VALUES(57,27,'57:27','Pas tyre Ne çuam të dërguar të tjerë, mandej dërguam Isain, të birin e Merjemes. Ne i dhamë atij Ungjillin, kurse në zemrat e pasuesve të tij mbollëm butësi dhe mëshirë. Sa për murgërinë, Ne nuk ua urdhëruam atë, por ata e shpikën vetë për të arritur kënaqësinë e Allahut. E megjithatë, ata nuk u kujdesën si duhet për të. Kështu, Ne u dhamë besimtarëve të vërtetë prej tyre shpërblimin e merituar, sado që shumë prej tyre janë të pabindur.');
INSERT INTO "hasan_efendi" VALUES(57,28,'57:28','O ju (ithtarë të Librit) që besuat (në Musain dhe Isain), ruajuni Allahut dhe besoni në të Dërguarin e Tij (Muhamedin), se Ai do t’ju dhurojë pjesë të dyfishtë nga mëshira e Vet, do t’ju japë dritë, me ndihmën e së cilës do të ecni dhe do t’ju falë; Allahu është Falës dhe Mëshirëplotë.');
INSERT INTO "hasan_efendi" VALUES(57,29,'57:29','Dhe le ta dinë ithtarët e Librit, se ata nuk kanë aspak fuqi mbi çfarëdo mirësie të Allahut dhe se mirësia është vetëm në dorën e Allahut. Ai ia jep kujt të dojë. Allahu zotëron mirësi të pakufishme.');
INSERT INTO "hasan_efendi" VALUES(58,1,'58:1','Allahu i ka dëgjuar fjalët e asaj, që diskutoi me ty për burrin e vet dhe iu ankua Atij. Allahu e dëgjoi bisedën tuaj, sepse Ai dëgjon dhe sheh gjithçka.');
INSERT INTO "hasan_efendi" VALUES(58,2,'58:2','Ata midis jush, që i ndajnë gratë e veta, duke u thënë se iu duken si nënat e veta, duhet ta dinë se ato nuk janë nënat e tyre. Nënat e tyre janë vetëm ato që i kanë lindur ata. Fjalët që ata thanë, janë të pahijshme dhe të pavërteta. Megjithatë, Allahu është Falës dhe Shlyes i gabimeve.');
INSERT INTO "hasan_efendi" VALUES(58,3,'58:3','Ata që i ndajnë gratë e veta në këtë mënyrë e pastaj duan të kthejnë mbrapsht atë që thanë, duhet të lirojnë një skllav (apo skllave), para se të preken përsëri. Kështu ju urdhërohet. Allahu i di të gjitha ato që bëni ju.');
INSERT INTO "hasan_efendi" VALUES(58,4,'58:4','Ai që nuk gjen (ndonjë skllav për të liruar), duhet që të agjërojë dy muaj rresht, para se të preken si bashkëshortë. E, nëse nuk mund ta mbajë agjërimin, atëherë duhet të ushqejë gjashtëdhjetë të varfër. Kjo, që të tregoni besimin në Allahun dhe të Dërguarin e Tij. Këta janë kufijtë e Allahut. Ndërsa për jobesimtarët ka vuajtje të mundimshme.');
INSERT INTO "hasan_efendi" VALUES(58,5,'58:5','Vërtet, ata që i kundërvihen Allahut dhe të dërguarit të Tij, do të poshtërohen, siç janë poshtëruar ata para tyre. Ne kemi zbritur shpallje të qarta, kurse për jobesimtarët do të ketë vuajtje poshtëruese,');
INSERT INTO "hasan_efendi" VALUES(58,6,'58:6','në ditën kur Allahu i ngjall të gjithë e i njofton për veprat që kanë bërë. Allahu të gjitha i ka shënuar, edhe pse ata i kanë harruar. Allahu është dëshmitar i çdo gjëje.');
INSERT INTO "hasan_efendi" VALUES(58,7,'58:7','A nuk e di ti, se Allahu di çdo gjë që është në qiej dhe në Tokë?! Nuk ka bisedë të fshehtë midis tre vetave, që Ai të mos jetë i katërti; as midis pesë vetave e që Ai të mos jetë i gjashti; as kur janë më pak, as kur janë më shumë, që Ai të mos jetë me ata, kudo që të ndodhen; Ai në Ditën e Kiametit do t’i njoftojë ata se çfarë kanë bërë, se, me të vërtetë, Allahu di çdo gjë!');
INSERT INTO "hasan_efendi" VALUES(58,8,'58:8','A nuk i sheh ata të cilët, edhe pse iu është ndaluar të kurdisin fshehtas, kthehen tek ajo që u është ndaluar dhe kurdisin për gjynahe, armiqësi dhe mosbindje ndaj të Dërguarit? Kur vijnë te ti, ata të përshëndesin me fjalë, me të cilat Allahu nuk të përshëndet kurrë dhe thonë me vete: “Pse nuk na dënon Allahu për këtë që po flasim?” Xhehenemi është një dënim i mjaftueshëm për ata. Në flakët e tij do të digjen. Eh, sa vendbanim i tmerrshëm që është ai!');
INSERT INTO "hasan_efendi" VALUES(58,9,'58:9','O besimtarë! Kur të bisedoni fshehtas mes jush, mos flisni për gjynahe, armiqësi dhe mosbindje ndaj të Dërguarit; por bisedoni për mirësi dhe drejtësi. Kijeni frikë Allahun, para të Cilit do të tuboheni.');
INSERT INTO "hasan_efendi" VALUES(58,10,'58:10','Intrigat e fshehta vijnë vetëm nga djalli, për t’i dëshpëruar besimtarët. E megjithatë, ai nuk mund t’u shkaktojë kurrfarë të keqeje, përveç nëse Allahu e lejon atë. Pra, besimtarët le të mbështeten tek Allahu!');
INSERT INTO "hasan_efendi" VALUES(58,11,'58:11','O besimtarë! Kur ju thuhet t’u hapni vend të tjerëve në tubim, atëherë bëni vend, se Allahu do t’ju bëjë vend (në Xhenet)! E kur ju thuhet të ngriheni, atëherë bëni kështu, se Allahu do t’i ngrejë në shkallë të lartë ata midis jush që besojnë dhe që u është dhënë dija. Allahu di çdo gjë që punoni.');
INSERT INTO "hasan_efendi" VALUES(58,12,'58:12','O besimtarë, kur të doni të këshilloheni me të Dërguarin, para këshillimit, jepni lëmoshë, kjo është më mirë dhe më pastër për ju. Po, nëse nuk keni, Allahu me të vërtetë, është Falës dhe Mëshirëplotë.');
INSERT INTO "hasan_efendi" VALUES(58,13,'58:13','A mos po frikësoheni që para këshillimit tuaj të jepni lëmoshë?! E nëse nuk jepni, e Allahu jua ka falur, atëherë kryeni namazin, jepni zekatin dhe dëgjoni Allahun e të Dërguarin e tij! Allahu di çdo gjë që bëni.');
INSERT INTO "hasan_efendi" VALUES(58,14,'58:14','A nuk i sheh ata (hipokritët) që shoqërohen me njerëz, me të cilët Allahu është i zemëruar? Ata nuk ju përkasin as juve (o besimtarë), as atyre. Ata me vetëdije betohen në gënjeshtër (se janë besimtarë).');
INSERT INTO "hasan_efendi" VALUES(58,15,'58:15','Allahu u ka përgatitur atyre dënim të rëndë, sepse është vërtet e mbrapshtë ajo që bëjnë.');
INSERT INTO "hasan_efendi" VALUES(58,16,'58:16','Betimet e tyre i kanë bërë perde (për mbrapshtitë e tyre) dhe i pengojnë të tjerët nga rruga e Allahut; ata i pret dënimi i poshtërues.');
INSERT INTO "hasan_efendi" VALUES(58,17,'58:17','As pasuria, as fëmijët e tyre nuk do t’u vijnë aspak në ndihmë (kur të jenë) para Allahut; ata do të jenë banorë të zjarrit, ku do të qëndrojnë përgjithmonë.');
INSERT INTO "hasan_efendi" VALUES(58,18,'58:18','Ditën, kur Allahu t’i ringjallë të gjithë, ata do t’i betohen Atij, ashtu siç të betohen ty tani, duke menduar se kjo do t’u sjellë dobi. Në të vërtetë, pikërisht ata janë gënjeshtarët!');
INSERT INTO "hasan_efendi" VALUES(58,19,'58:19','Ata i ka vënë poshtë djalli dhe i ka bërë që të harrojnë përmendjen e Allahut. Ata janë pala e djallit; në të vërtetë, pala e djallit janë humbësit.');
INSERT INTO "hasan_efendi" VALUES(58,20,'58:20','Ata që kundërshtojnë Allahun dhe të Dërguarin e Tij, do të jenë në shkallën më të poshtëruar.');
INSERT INTO "hasan_efendi" VALUES(58,21,'58:21','Allahu ka vendosur: “Unë dhe të dërguarit e Mi do të triumfojmë me siguri!” Allahu është vërtet i Fortë dhe i Plotfuqishëm.');
INSERT INTO "hasan_efendi" VALUES(58,22,'58:22','Nuk gjen njerëz, që besojnë në Allahun dhe në Ditën e Fundit, që të ushqejnë dashuri ndaj atyre, të cilët kundërshtojnë Allahun dhe të Dërguarin e tij, edhe në qofshin ata etërit e tyre ose bijtë e tyre, ose vëllezërit e tyre, ose farefisi i tyre. Allahu ka skalitur besimin në zemrat e tyre dhe i ka fuqizuar ata me shpalljen e Vet. Ai do t’i shpjerë ata në kopshte, nëpër të cilat rrjedhin lumenj e ku do të qëndrojnë përgjithmonë. Allahu është i kënaqur me ata, por edhe ata do të jenë të kënaqur me Të. Ata janë Pala e Allahut dhe, vërtet që Pala e Allahut do të jetë fituese.');
INSERT INTO "hasan_efendi" VALUES(59,1,'59:1','Allahun e lartëson çdo gjë në qiej dhe çdo gjë në Tokë. Ai është i Plotfuqishmi dhe i Urti.');
INSERT INTO "hasan_efendi" VALUES(59,2,'59:2','Është Ai, që i nxori nga shtëpitë e tyre ata që nuk besuan midis ithtarëve të Librit në kohën e dëbimit të parë. Ju nuk mendonit se ata do të iknin, kurse ata mendonin se fortesat e tyre do t’i mbronin prej Allahut. Por dënimi i Allahut u erdhi andej nga nuk e pritnin, duke mbjellë në zemrat e tyre frikën, e cila bëri që ata t’i shkatërronin shtëpitë e veta me duart e tyre, si dhe me duart e besimtarëve. Prandaj, mësoni nga kjo, o ju largpamës!');
INSERT INTO "hasan_efendi" VALUES(59,3,'59:3','Sikur të mos e kishte caktuar Allahu dëbimin e tyre, Ai do t’i kishte dënuar me siguri në këtë jetë. Por në jetën tjetër ata i pret dënimi i Zjarrit.');
INSERT INTO "hasan_efendi" VALUES(59,4,'59:4','Kjo, sepse ata iu kundërvunë Allahut dhe të Dërguarit të Tij; kushdo që i kundërvihet Allahut, le ta dijë se Allahu dënon vërtet ashpër.');
INSERT INTO "hasan_efendi" VALUES(59,5,'59:5','Për çfarëdo peme palme (të armikut) që pretë apo latë në këmbë, keni pasur miratimin e Allahut, në mënyrë që Ai t’i poshtërojë të pabindurit.');
INSERT INTO "hasan_efendi" VALUES(59,6,'59:6','Çfarëdo që Allahu i dhuroi të Dërguarit të Tij si plaçkë (të marrë) prej tyre, është prej mirësisë së Tij. Ju nuk hipët mbi kuaj e as mbi deve për ta fituar atë, por Allahu i jep pushtet të dërguarve të Vet mbi ata që Ai dëshiron. Allahu është i Fuqishëm për çdo gjë.');
INSERT INTO "hasan_efendi" VALUES(59,7,'59:7','Çfarëdo që Allahu i dhuroi të Dërguarit të Tij si plaçkë (të marrë) nga popujt e vendbanimeve, i takon: Allahut, të Dërguarit, të afërmve të tij, jetimëve, të varfërve dhe udhëtarëve të mbetur rrugës, në mënyrë që ajo të mos qarkullojë vetëm në duart e të pasurve nga mesi juaj. Çfarëdo që t’ju japë i Dërguari, merreni atë, e çfarëdo që t’ju ndalojë, hiqni dorë prej saj. Frikësojuni Allahut, sepse Allahu dënon vërtet ashpër.');
INSERT INTO "hasan_efendi" VALUES(59,8,'59:8','Një pjesë e plaçkës i takon edhe mërgimtarëve të varfër , të cilët u përzunë nga vendlindja dhe pasuritë e tyre, duke kërkuar mirësinë dhe kënaqësinë e Allahut dhe duke ndihmuar Allahun dhe të Dërguarin e Tij. Pikërisht ata janë besimtarët e sinqertë.');
INSERT INTO "hasan_efendi" VALUES(59,9,'59:9','Ndërsa ata që banojnë në Medinë dhe që e kanë pranuar besimin qysh më parë , i duan mërgimtarët që vijnë në Medinë dhe në zemrat e tyre nuk ndiejnë kurrfarë rëndimi, për atë, që u është dhënë atyre, por duan t’u bëjnë më mirë mërgimtarëve sesa vetes, edhe pse vetë janë nevojtarë. Kushdo që ruhet nga lakmia e vetvetes, me siguri që do të jetë fitues.');
INSERT INTO "hasan_efendi" VALUES(59,10,'59:10','Edhe ata që erdhën pas tyre, thonë: “O Zoti ynë, falna ne dhe vëllezërit tanë, të cilët kanë besuar para nesh dhe mos lejo që në zemrat tona të ketë asnjë të keqe ndaj besimtarëve! O Zoti ynë, Ti je vërtet i Butë dhe Mëshirëplotë!”');
INSERT INTO "hasan_efendi" VALUES(59,11,'59:11','A nuk e ke parë se si hipokritët u thanë shokëve të vet që nuk besuan prej ithtarëve të Librit: “Nëse ju dëbojnë, ne do të dalim me ju dhe kurrkujt nuk do t’i bindemi kundër jush; e, nëse ju sulmoheni, me siguri që do t’ju dalim në ndihmë”? Allahu dëshmon se ata janë vërtet gënjeshtarë.');
INSERT INTO "hasan_efendi" VALUES(59,12,'59:12','Nëse (hebrenjtë) do të dëbohen, hipokritët nuk do të shkojnë me ata. Nëse do të sulmohen, ata nuk do t’u dalin në ndihmë. Por, edhe sikur të nisen për t’i mbështetur, sigurisht që do të kthehen shpejt e do t’i lënë pa ndihmë.');
INSERT INTO "hasan_efendi" VALUES(59,13,'59:13','Në zemrat e tyre ata kanë më shumë frikë nga ju sesa nga Allahu, ngaqë ata janë popull që nuk kuptojnë.');
INSERT INTO "hasan_efendi" VALUES(59,14,'59:14','Ata nuk do të luftojnë kundër jush së bashku, veçse në vende të fortifikuara ose pas mureve. Lufta ndërmjet tyre është e ashpër. Ti mendon se ata janë të bashkuar, mirëpo zemrat e tyre janë të përçara, sepse ata janë njerëz që nuk kuptojnë.');
INSERT INTO "hasan_efendi" VALUES(59,15,'59:15','Këta janë si ata, që kanë qenë pak para tyre, që i shijuan pasojat e sjelljes së tyre (që në këtë botë); (ndërsa në botën tjetër) ata i pret një dënim i dhembshëm!');
INSERT INTO "hasan_efendi" VALUES(59,16,'59:16','Aleatët e tyre hipokritë do t’i lënë në baltë, siç bën djalli, i cili i thotë njeriut: “Mos beso!”. Por, kur ky bëhet mohues, djalli i thotë: “Unë nuk kam të bëj me ty! Në të vërtetë, unë i frikësohem Allahut, Zotit të botëve”!');
INSERT INTO "hasan_efendi" VALUES(59,17,'59:17','Natyrisht që fundi i këtyre të dyve është qëndrimi i përhershëm në Zjarr; ky është dënimi për të gjithë njerëzit e këqij.');
INSERT INTO "hasan_efendi" VALUES(59,18,'59:18','O besimtarë, frikësojuni Allahut dhe çdo njeri le të shikojë se ç’ka përgatitur për të nesërmen! Kijeni frikë Allahun, sepse Ai di çdo gjë që bëni ju!');
INSERT INTO "hasan_efendi" VALUES(59,19,'59:19','Mos u bëni si ata që e harruan Allahun, kështu që Ai i bëri të harronin vetveten! Pikërisht ata janë të pabindurit (ndaj Allahut).');
INSERT INTO "hasan_efendi" VALUES(59,20,'59:20','Nuk janë të barabartë banorët e Xhehenemit dhe banorët e Xhenetit; banorët e Xhenetit do të jenë të fituarit.');
INSERT INTO "hasan_efendi" VALUES(59,21,'59:21','Sikur këtë Kuran t’ia shpallnim ndonjë mali, ti do ta shihje atë (mal) të përulur dhe të copëtuar nga frika e Allahut. Shembuj të tillë Ne u japim njerëzve, që ata të meditojnë.');
INSERT INTO "hasan_efendi" VALUES(59,22,'59:22','Ai është Allahu, përveç të Cilit nuk ka Zot tjetër (të denjë për adhurim), Njohësi i së dukshmes dhe i së padukshmes, Ai është i Gjithëmëshirshmi, Mëshirëploti!');
INSERT INTO "hasan_efendi" VALUES(59,23,'59:23','Ai është Allahu, përveç të Cilit nuk ka Zot tjetër (të denjë për adhurim). Ai është Sunduesi, i Shenjti (i pastër nga çdo e metë), Paqëdhënësi, Dhënësi i sigurisë, Mbikëqyrësi mbi gjithçka, i Plotfuqishmi, Imponuesi, Madhështori. Qoftë i lartësuar Allahu mbi gjithçka që ia shoqërojnë Atij (në adhurim)!');
INSERT INTO "hasan_efendi" VALUES(59,24,'59:24','Ai është Allahu, Krijuesi, Zanafillësi, Ai që çdo gjëje i jep trajtë. Atij i përkasin emrat më të bukur; Atë e përlëvdon gjithçka që ndodhet në qiej dhe në Tokë, Ai është i Plotfuqishmi dhe i Urti.');
INSERT INTO "hasan_efendi" VALUES(60,1,'60:1','O besimtarë, mos i bëni miq armiqtë e Mi dhe tuajt, duke u shprehur atyre dashuri, ndërkohë që ata kanë mohuar të vërtetën që ju ka ardhur dhe kanë dëbuar të Dërguarin dhe ju, vetëm se besonit në Allahun, Zotin tuaj. Nëse keni dalë luftëtarë në rrugën Time dhe për kënaqësinë Time dhe fshehurazi u shfaqni atyre dashuri, atëherë gaboheni. Unë e di çfarë fshihni dhe çfarë bëni haptazi. Secili prej jush që vepron ashtu, është shmangur nga rruga e drejtë.');
INSERT INTO "hasan_efendi" VALUES(60,2,'60:2','Nëse ju mposhtin, ata do të jenë armiqtë tuaj e do t’i zgjasin duart dhe gjuhën e tyre për t’ju lënduar ju, sepse dëshirojnë që ju të bëheni jobesimtarë.');
INSERT INTO "hasan_efendi" VALUES(60,3,'60:3','Në Ditën e Kiametit nuk do t’ju ndihmojnë as fëmijët, as farefisi. Allahu do t’ju ndajë midis jush. Ai e sheh mirë atë që bëni ju.');
INSERT INTO "hasan_efendi" VALUES(60,4,'60:4','Shembull i mrekullueshëm për ju është Ibrahimi dhe ata që kanë qenë me të, kur i thanë popullit të vet: “Ne shkëputemi nga ju dhe nga ato që ju i adhuroni në vend të Allahut; ne ju mohojmë dhe armiqësia e urrejtja midis nesh është e përhershme, derisa të besoni tek Allahu Një dhe i Vetëm”. Përjashtim (nga ky shembull) bën fjala e Ibrahimit për të atin: “Unë do të kërkoj falje për ty, por nuk kam kurrfarë mundësie për të të mbrojtur para Allahut. O Zoti ynë, tek Ti mbështetemi, Ty të drejtohemi dhe tek Ti do të kthehemi.');
INSERT INTO "hasan_efendi" VALUES(60,5,'60:5','O Zoti ynë, mos na bëj sprovë për jobesimtarët dhe falna ne, o Zot. Vërtet, Ti je i Plotfuqishmi dhe i Urti.');
INSERT INTO "hasan_efendi" VALUES(60,6,'60:6','Vërtet, ata njerëz janë një shembull i mirë për ju e për këdo që shpreson në Allahun dhe në Ditën e Fundit. Kushdo që nuk ia vë veshin (këtij shembulli), ta dijë se Allahu është i Vetëmjaftueshmi, i Denji për çdo lavd!');
INSERT INTO "hasan_efendi" VALUES(60,7,'60:7','Ndoshta Allahu do të lidhë miqësi midis jush dhe atyre me të cilët keni qenë armiq. Allahu është i Fuqishëm (për këtë). Ai është Falës dhe Mëshirëplotë.');
INSERT INTO "hasan_efendi" VALUES(60,8,'60:8','Allahu nuk ju ndalon të silleni mirë dhe të jeni të drejtë ndaj atyre që nuk luftojnë kundër jush për shkak të fesë dhe që nuk ju dëbojnë prej shtëpive tuaja. Me të vërtetë, Allahu i do të drejtët.');
INSERT INTO "hasan_efendi" VALUES(60,9,'60:9','Allahu ju ndalon t’i bëni miq ata që luftojnë kundër jush për shkak të fesë dhe ju dëbojnë nga vatrat tuaja, si dhe mbështesin të tjerët që t’ju dëbojnë. Kushdo që i bën miq ata, është punëmbrapshtë.');
INSERT INTO "hasan_efendi" VALUES(60,10,'60:10','O besimtarë, kur ju vijnë besimtaret të mërguara, provoni (besimin) tek ato. Allahu e di më mirë besimin e tyre. Nëse bindeni se ato janë besimtare, mos i ktheni te jobesimtarët. Ato nuk janë (bashkëshorte) të ligjshme për ata dhe as ata nuk janë (bashkëshortë) të ligjshëm për ato. Kthejuni jobesimtarëve pajën, që u kishin dhënë atyre . Nuk ka pengesë që t’i merrni ato për bashkëshorte, pasi t’u jepni pajën e martesës. Mos qëndroni në martesë me idhujtare. Kërkoni atë që keni dhënë (si pajë), por dhe jobesimtarët le të kërkojnë atë që kanë dhënë. Ky është ligji i Allahut, Ai ju gjykon ju; Allahu di çdo gjë dhe është i Gjithëdijshëm e i Urtë.');
INSERT INTO "hasan_efendi" VALUES(60,11,'60:11','Nëse ndonjëra prej grave tuaja kthehet te jobesimtarët e më pas ju fitoni plaçkë lufte prej tyre, atëherë jepuni atyre (besimtarëve që u kanë ikur gratë) shpenzimet e martesës. Kijeni frikë Allahun, të Cilit i besoni!');
INSERT INTO "hasan_efendi" VALUES(60,12,'60:12','O Profet, nëse vijnë tek ti besimtaret e të betohen se nuk do t’i shoqërojnë Allahut asgjë (në adhurim), nuk do të vjedhin, nuk do të bëjnë kurvëri, nuk do t’i vrasin fëmijët e tyre, nuk do të sjellin ndonjë shpifje të sajuar nga ato vetë dhe se nuk do të të kundërshtojnë në çfarë është e drejtë dhe e arsyeshme, atëherë ti pranoje betimin e tyre dhe lutju Allahut t’i falë. Vërtet, Allahu është Falës dhe Mëshirëplotë.');
INSERT INTO "hasan_efendi" VALUES(60,13,'60:13','O besimtarë, mos i bëni miq ata njerëz me të cilët Allahu është i zemëruar. Ata e kanë humbur shpresën për jetën tjetër, ashtu siç e kanë humbur shpresën jobesimtarët për të varrosurit (se do të ringjallen).');
INSERT INTO "hasan_efendi" VALUES(61,1,'61:1','Allahun e përlëvdon gjithçka, që gjendet në qiej dhe në Tokë. Ai është i Plotfuqishmi dhe i Urti!');
INSERT INTO "hasan_efendi" VALUES(61,2,'61:2','O besimtarë, përse thoni atë që nuk e bëni?');
INSERT INTO "hasan_efendi" VALUES(61,3,'61:3','Është shumë e urryer për Allahun të thoni atë që nuk e bëni!');
INSERT INTO "hasan_efendi" VALUES(61,4,'61:4','Vërtet, Allahu i do ata që luftojnë në rrugën e Tij, të radhitur si të ishin një ndërtesë solide.');
INSERT INTO "hasan_efendi" VALUES(61,5,'61:5','Kujto kur Musai i tha popullit të vet: “O populli im, përse po më mundoni, kur e dini mirë se unë jam i dërguari i Allahut te ju?!” Kur ata u larguan (nga e vërteta), Allahu i largoi zemrat e tyre (nga rruga e drejtë); Allahu nuk e shpie në rrugë të drejtë popullin jobesimtar.');
INSERT INTO "hasan_efendi" VALUES(61,6,'61:6','Kujto kur Isai, i biri i Merjemes, tha: “O bijtë e Israilit, unë jam i dërguari i Allahut tek ju, për t’ju vërtetuar Teuratin e shpallur para meje dhe për t’ju sjellë lajmin e gëzueshëm për një të dërguar, emri i të cilit është Ahmed , që do të vijë pas meje”. Por, kur ai u solli atyre shenja të qarta, ata thanë: “Kjo është magji e hapur!”');
INSERT INTO "hasan_efendi" VALUES(61,7,'61:7','Kush është më keqbërës se ai që shpif gënjeshtra kundër Allahut, ndërkohë që i bëhet thirrje për t’iu nënshtruar Atij?! Allahu nuk i udhëzon keqbërësit.');
INSERT INTO "hasan_efendi" VALUES(61,8,'61:8','Ata dëshirojnë të fikin dritën e Allahut me gojën e tyre, por Allahu e përsos dritën e Vet, edhe nëse (këtë gjë) e urrejnë jobesimtarët.');
INSERT INTO "hasan_efendi" VALUES(61,9,'61:9','Allahu është Ai që e ka çuar të Dërguarin e Vet me udhërrëfimin dhe fenë e së Vërtetës, për ta ngritur atë më lart se të gjitha fetë, edhe nëse nuk e pëlqejnë idhujtarët.');
INSERT INTO "hasan_efendi" VALUES(61,10,'61:10','O besimtarë, a doni t’ju drejtoj në një tregti, e cila ju shpëton nga një dënim i dhembshëm?!');
INSERT INTO "hasan_efendi" VALUES(61,11,'61:11','Besoni në Allahun dhe në të Dërguarin e Tij dhe luftoni në rrugën e Allahut me pasurinë dhe trupin tuaj. Kjo, nëse do ta dini, është më mirë për ju.');
INSERT INTO "hasan_efendi" VALUES(61,12,'61:12','Ai do t’jua falë gabimet tuaja dhe do t’ju shpjerë në kopshte, nëpër të cilat rrjedhin lumenj dhe në banesa të mrekullueshme në Xhenetin e Adnit. Kjo është fitorja më e madhe.');
INSERT INTO "hasan_efendi" VALUES(61,13,'61:13','Ai do t’ju falë edhe dhunti të tjera që, i dëshironi: ndihmën e Tij dhe një fitore të afërt! Andaj, gëzoji besimtarët!');
INSERT INTO "hasan_efendi" VALUES(61,14,'61:14','O besimtarë, bëhuni ndihmësit e Allahut, ashtu siç u tha dishepujve Isai, i biri i Merjemes: “Kush janë ndihmësit e mi për çështjen e Allahut?” Dishepujt u përgjigjën: “Ne jemi ndihmësit e Allahut!” Kështu, një pjesë nga bijtë e Israilit e besoi, kurse pjesa tjetër e mohoi. Prandaj Ne i forcuam besimtarët kundër armikut të tyre dhe ata dolën ngadhënjimtarë.');
INSERT INTO "hasan_efendi" VALUES(62,1,'62:1','Allahun e përlëvdon gjithçka që gjendet në qiej dhe në Tokë. Ai është Sunduesi, i Shenjti (i pastër nga çdo e metë), i Plotfuqishmi dhe i Urti.');
INSERT INTO "hasan_efendi" VALUES(62,2,'62:2','Është Ai që u solli analfabetëve një të Dërguar nga gjiri i tyre, për t’u lexuar atyre shpalljet e Tij, për t’i pastruar e për t’u mësuar Librin dhe Urtësinë , ndonëse ata më parë ishin vërtet në humbje të plotë.');
INSERT INTO "hasan_efendi" VALUES(62,3,'62:3','Ai do ta dërgojë atë edhe tek të tjerë (myslimanë) nga ata, të cilët ende nuk u janë bashkuar. Ai është i Plotfuqishmi dhe i Urti.');
INSERT INTO "hasan_efendi" VALUES(62,4,'62:4','Kjo është dhuntia e Allahut; Ai ia jep atë kujt të dojë. Allahu zotëron dhunti të madhe.');
INSERT INTO "hasan_efendi" VALUES(62,5,'62:5','Shembulli i atyre, që iu besua Teurati, por pastaj nuk e zbatuan atë, i shëmbëllen gomarit, që vetëm sa i mbart librat. Sa i keq është shembulli i atyre që mohojnë shpalljet e Allahut! Allahu nuk i udhëzon (në rrugën e drejtë) keqbërësit.');
INSERT INTO "hasan_efendi" VALUES(62,6,'62:6','Thuaju hebrenjve: “Në qoftë se ju pretendoni se jeni të vetmit miq të Allahut nga të gjithë njerëzit, atëherë dëshirojeni vdekjen, po të jeni të sinqertë!”');
INSERT INTO "hasan_efendi" VALUES(62,7,'62:7','Por ata kurrë nuk do ta dëshirojnë vdekjen, për shkak të veprave që kanë bërë duart e tyre. Allahu i njeh mirë keqbërësit.');
INSERT INTO "hasan_efendi" VALUES(62,8,'62:8','Thuaj: “Vdekja prej së cilës po ikni, do t’ju arrijë. Pastaj, ju do të ktheheni tek Ai, i Cili e di të padukshmen dhe të dukshmen dhe Ai do t’ju njoftojë ju për atë që keni bërë.”');
INSERT INTO "hasan_efendi" VALUES(62,9,'62:9','O besimtarë, kur të thirreni për (të falur) namazin (e xhumasë) në ditën e premte, nxitoni për ta përmendur Allahun dhe pezulloni tregtinë! Kjo, që ta dini, është më mirë për ju!');
INSERT INTO "hasan_efendi" VALUES(62,10,'62:10','Dhe, kur të përfundojë namazi, atëherë shpërndahuni nëpër tokë, kërkoni nga mirësitë e Allahut dhe përmendeni shumë Allahun, në mënyrë që të shpëtoni.');
INSERT INTO "hasan_efendi" VALUES(62,11,'62:11','Mirëpo, sa herë që ata shohin ndonjë tregti ose lojë, shkojnë tek ajo e të lënë ty në këmbë. Thuaj: “Ajo që është tek Allahu, është më e mirë nga argëtimi e nga tregtia.” Allahu është Dhuruesi më bujar.');
INSERT INTO "hasan_efendi" VALUES(63,1,'63:1','Kur vijnë tek ti hipokritët, thonë: “Ne dëshmojmë se ti je vërtet i dërguari i Allahut!” Allahu e di se ti je i dërguari i Tij, por Ai dëshmon se hipokritët janë gënjeshtarë!');
INSERT INTO "hasan_efendi" VALUES(63,2,'63:2','Ata i përdorin betimet e tyre si maskë dhe i largojnë (njerëzit) prej rrugës së Allahut. Vërtet, shumë të mbrapshta janë punët e tyre!');
INSERT INTO "hasan_efendi" VALUES(63,3,'63:3','Kjo, sepse ata fillimisht besuan, pastaj mohuan, kështu që zemrat e tyre u vulosën dhe si pasojë ata nuk kuptojnë më.');
INSERT INTO "hasan_efendi" VALUES(63,4,'63:4','Kur i shihni ata, pamja e tyre e bukur ju mahnit e, nëse flasin, ju i dëgjoni fjalët e tyre. E megjithatë, ata janë si trungje të mbështetura . Ata kujtojnë se çdo thirrje që dëgjojnë është kundër tyre. Ata janë armiqtë e vërtetë, andaj ruajuni prej tyre! Allahu i vraftë! Sa shumë që janë larguar (nga e vërteta)?!');
INSERT INTO "hasan_efendi" VALUES(63,5,'63:5','Kur u thuhet atyre: “Ejani, se i Dërguari i Allahut do të kërkojë falje për ju”, ata tundin kokat e tyre dhe i sheh ata të kthehen me mendjemadhësi.');
INSERT INTO "hasan_efendi" VALUES(63,6,'63:6','Njësoj është, u lute për faljen e tyre apo nuk u lute, Allahu kurrsesi nuk do t’i falë. Në të vërtetë, Allahu nuk e udhëzon popullin e pabindur.');
INSERT INTO "hasan_efendi" VALUES(63,7,'63:7','Ata thonë: “Mos u jepni kurrgjë atyre që janë me të Dërguarin e Allahut, me qëllim që të largohen prej tij!” Por Allahut i takojnë thesaret e qiejve dhe të Tokës, ndonëse hipokritët nuk kuptojnë.');
INSERT INTO "hasan_efendi" VALUES(63,8,'63:8','Ata thonë: “Nëse kthehemi në Medinë, pa dyshim që të fuqishmit do t’i dëbojnë të dobëtit nga ajo!” Në të vërtetë, fuqia është e Allahut, e të Dërguarit të Tij dhe e besimtarëve, por hipokritët nuk e dinë.');
INSERT INTO "hasan_efendi" VALUES(63,9,'63:9','O besimtarë, mos lejoni që pasuria dhe fëmijët tuaj t’ju largojnë nga të përmendurit e Allahut. Ata që e bëjnë këtë, do të humbin.');
INSERT INTO "hasan_efendi" VALUES(63,10,'63:10','Prandaj jepni prej atyre mirësive që ju kam dhënë Unë, para se t’i vijë vdekja ndonjërit, e ai të thotë: “O Zoti im, sikur të më kishe lënë edhe një kohë të shkurtër në jetë, do të kisha dhënë lëmoshë dhe do të bëhesha ndër të mirët!”');
INSERT INTO "hasan_efendi" VALUES(63,11,'63:11','Por Allahu nuk ia shtyn afatin asnjë shpirti, që i vjen koha (e vdekjes); Allahu e di mirë gjithçka që bëni ju.');
INSERT INTO "hasan_efendi" VALUES(64,1,'64:1','Allahun e përlëvdon gjithçka, që gjendet në qiej dhe në Tokë. Atij i takon pushteti epror dhe lavdia. Ai është i Fuqishëm për çdo gjë!');
INSERT INTO "hasan_efendi" VALUES(64,2,'64:2','Është Ai që ju krijoi ju, e megjithatë, disa prej jush janë mohues e disa besimtarë. Por Allahu e sheh mirë çdo gjë që bëni.');
INSERT INTO "hasan_efendi" VALUES(64,3,'64:3','Ai i ka krijuar qiejt dhe Tokën me të vërtetën, ju ka dhënë format tuaja dhe jua ka bërë ato të bukura; të gjithë tek Ai do të ktheheni.');
INSERT INTO "hasan_efendi" VALUES(64,4,'64:4','Ai di gjithçka që përmbajnë qiejt dhe Toka. Ai i di të fshehtat tuaja dhe ato që i bëni haptazi. Allahu i di të gjitha ç’keni në zemrat tuaja.');
INSERT INTO "hasan_efendi" VALUES(64,5,'64:5','A nuk keni dëgjuar për ata që kanë mohuar më parë dhe i kanë shijuar pasojat e veprave të tyre? Për ata do të ketë dënim të dhembshëm,');
INSERT INTO "hasan_efendi" VALUES(64,6,'64:6','sepse, kur të dërguarit e tyre sillnin prova të qarta, ata thoshin: “Vallë, vdekatarë si ne do të na udhëheqin?” Dhe kështu mohuan e ia kthyen kurrizin (besimit). Por Allahu nuk ka nevojë (për ata). Allahu është i Vetëmjaftueshëm dhe i Denjë për çdo lavd.');
INSERT INTO "hasan_efendi" VALUES(64,7,'64:7','Mohuesit kujtojnë se nuk do të ringjallen kurrsesi. Thuaju: “Po, për Zotin tim, me siguri që do të ringjalleni e pastaj do të njoftoheni për atë ç’keni bërë!” Dhe kjo është e lehtë për Allahun.');
INSERT INTO "hasan_efendi" VALUES(64,8,'64:8','Prandaj besoni në Allahun, në të Dërguarin e Tij dhe në dritën (Kuranin), që ju kemi zbritur. Allahu e di mirë çdo gjë që bëni ju.');
INSERT INTO "hasan_efendi" VALUES(64,9,'64:9','Dita, kur Ai do t’ju mbledhë, dita, kur të gjithë do të grumbulloheni, do të jetë Dita e humbjes dhe fitimit. Atij që beson në Allahun dhe bën vepra të mira, Allahu do t’ia falë gjynahet dhe do ta shpjerë në kopshte, nëpër të cilat rrjedhin lumenj e ku do të qëndrojë përgjithmonë. Kjo është fitorja e madhe.');
INSERT INTO "hasan_efendi" VALUES(64,10,'64:10','Ndërsa ata që nuk kanë besuar dhe i kanë mohuar shpalljet Tona, do të jenë banorë të Zjarrit, ku do të qëndrojnë përherë. Eh, sa fund i tmerrshëm është ai!');
INSERT INTO "hasan_efendi" VALUES(64,11,'64:11','Çdo fatkeqësi që i bie njeriut, ndodh me vullnetin e Allahut. Ai ia udhëzon zemrën kujtdo që i beson Atij. Allahu di mirë çdo gjë.');
INSERT INTO "hasan_efendi" VALUES(64,12,'64:12','Bindjuni Allahut dhe bindjuni të Dërguarit! Nëse ju i ktheni shpinën të Dërguarit Tonë, dijeni se ai ka për detyrë vetëm që t’ju shpjegojë qartë.');
INSERT INTO "hasan_efendi" VALUES(64,13,'64:13','Allahu është i vetmi Zot i vërtetë, prandaj, vetëm tek Ai le të mbështeten besimtarët.');
INSERT INTO "hasan_efendi" VALUES(64,14,'64:14','O besimtarë! Në të vërtetë, ju keni armiq në radhët e grave tuaja dhe të fëmijëve tuaj, andaj ruajuni prej tyre! Por, nëse nuk i vini re fyerjet e tyre dhe ua falni, ta dini se Allahu është vërtet Falës dhe Mëshirëplotë .');
INSERT INTO "hasan_efendi" VALUES(64,15,'64:15','Pasuria dhe fëmijët tuaj janë vetëm sprovë për ju. Allahu ka shpërblim të madh.');
INSERT INTO "hasan_efendi" VALUES(64,16,'64:16','Prandaj frikësojuni Allahut sa të mundeni, dëgjoni e binduni dhe shpenzoni për bamirësi në të mirën tuaj. Ata që e ruajnë veten nga lakmia e saj, pikërisht ata janë të fituarit.');
INSERT INTO "hasan_efendi" VALUES(64,17,'64:17','Nëse i jepni Allahut një hua të bukur, Ai jua kthen shumëfish atë dhe ju fal. Allahu është Mirënjohës dhe nuk nxiton në dënime.');
INSERT INTO "hasan_efendi" VALUES(64,18,'64:18','Ai është Njohësi i të padukshmes dhe së dukshmes, i Plotfuqishmi dhe i Urti.');
INSERT INTO "hasan_efendi" VALUES(65,1,'65:1','O Profet! Nëse i ndani gratë, ndajini ato para kohës së tyre të pritjes.[371] Llogaritni kohën e tyre të pritjes dhe frikësojuni Allahut, Zotit tuaj. Mos i dëboni ato prej shtëpive të tyre, por edhe ato të mos dalin, me përjashtim të rastit kur shkelin martesën haptazi. Këto janë urdhërimet e Allahut; kushdo që i kapërcen kufijtë e Allahut, i ka bërë dëm vetvetes. Ti nuk e di se pas kësaj Allahu mund të sjellë ndonjë rrethanë tjetër.');
INSERT INTO "hasan_efendi" VALUES(65,2,'65:2','Dhe, kur të mbushet afati i caktuar, i mbani ato si ka hije ose ndahuni me to në mënyrë të pëlqyeshme. Merrni dy dëshmitarë të ndershëm prej jush dhe jepni dëshminë tuaj para Allahut! Kjo është këshillë për ata që i besojnë Allahut dhe Ditës së Fundit. Atij që i frikësohet Allahut, Ai do t’i gjejë rrugëdalje (nga çdo vështirësi)');
INSERT INTO "hasan_efendi" VALUES(65,3,'65:3','dhe do t’i japë risk prej nga nuk e pret. Allahu i mjafton kujtdo që mbështetet tek Ai. Allahu e përmbush me siguri vendimin e Tij dhe për çdo gjë Ai ka caktuar masë.');
INSERT INTO "hasan_efendi" VALUES(65,4,'65:4','Nëse dyshoni për ato gra tuajat, të cilave u janë ndërprerë zakonet, mësoni se koha e tyre e pritjes është tre muaj. Njësoj është edhe për ato që nuk u kanë ardhur ende zakonet. Kurse, për gratë shtatzëna, afati i tyre është deri në lindjen e fëmijës. Atij që i frikësohet Allahut, Ai do t’ia lehtësojë çështjen.');
INSERT INTO "hasan_efendi" VALUES(65,5,'65:5','Ja, këto janë urdhërimet që Allahu i ka zbritur për ju. Atij që i frikësohet Allahut, Ai do t’ia shlyejë veprat e këqija dhe do t’ia rrisë shpërblimin.');
INSERT INTO "hasan_efendi" VALUES(65,6,'65:6','Gratë e ndara në periudhën e pritjes, le të banojnë aty ku banoni edhe ju, sipas mundësive tuaja dhe mos u bini në qafë për t’i detyruar (të largohen). Nëse janë shtatzëna, shpenzoni për ato deri sa të lindin. Nëse fëmija ushqehet me gjirin e nënës, paguajuni atyre shpërblimin dhe merruni vesh me të mirë. Por, nëse keni vështirësi të merreni vesh, atëherë le ta ushqejë me gji një (mëndeshë) tjetër.');
INSERT INTO "hasan_efendi" VALUES(65,7,'65:7','Pasaniku le të shpenzojë sipas pasurisë që ka, ndërsa i varfri le të shpenzojë sipas asaj që i ka dhënë Allahu; Allahu nuk e ngarkon askënd mbi sa i ka dhënë. Pas vështirësisë, Ai sjell lehtësimin.');
INSERT INTO "hasan_efendi" VALUES(65,8,'65:8','Sa shumë qytete u treguan kokëforta para urdhrave të Zotit të vet dhe të dërguarve të Tij! Ne u kërkuam llogari të rreptë dhe i ndëshkuam me dënim të ashpër.');
INSERT INTO "hasan_efendi" VALUES(65,9,'65:9','Ata i provuan pasojat e veprave të veta dhe fundi i tyre qe shkatërrim.');
INSERT INTO "hasan_efendi" VALUES(65,10,'65:10','Allahu ka përgatitur për ata dënim të rëndë, prandaj frikësojuni Allahut, o njerëz të mençur, që keni besuar! Allahu ju ka dërguar udhërrëfyesin:');
INSERT INTO "hasan_efendi" VALUES(65,11,'65:11','një të Dërguar, i cili ju lexon shpalljet e Allahut me shpjegime të qarta, për t’i nxjerrë ata që besojnë dhe bëjnë vepra të mira, nga errësira në dritë. Atë që beson Allahun dhe bën vepra të mira, Ai do ta shpjerë në kopshtet e Xhenetit, nëpër të cilët rrjedhin lumenj dhe ku do të qëndrojë përgjithmonë. Pa dyshim, Allahu ka përgatitur për atë risk të mrekullueshëm.');
INSERT INTO "hasan_efendi" VALUES(65,12,'65:12','Allahu është Ai, që ka krijuar shtatë qiej dhe po aq toka. Urdhrat e Tij zbresin përmes tyre, që ju ta dini se Allahu është i Fuqishëm për çdo gjë dhe se Ai përfshin gjithçka në diturinë e Tij.');
INSERT INTO "hasan_efendi" VALUES(66,1,'66:1','O Profet! Përse po ia ndalon vetes atë që ta ka lejuar Allahu, duke kërkuar t’u bësh qejfin grave të tua?! Allahu është Falës dhe Mëshirëplotë.');
INSERT INTO "hasan_efendi" VALUES(66,2,'66:2','Allahu ka përcaktuar një mënyrë për t’u liruar nga betimet tuaja. Allahu është Mbrojtësi juaj; Ai është i Gjithëdijshmi, i Urti.');
INSERT INTO "hasan_efendi" VALUES(66,3,'66:3','Kur i Dërguari i tregoi një sekret njërës prej grave të tij, ajo e hapi këtë fjalë. Allahu ia zbuloi këtë të Dërguarit, i cili një pjesë të këtij zbulimi ia tha (gruas që ia përhapi sekretin), kurse pjesën tjetër jo. Kur i Dërguari ia tregoi asaj të fshehtën, ajo e pyeti: “E kush të ka treguar ty?” Ai i tha: “Ma tregoi i Gjithëdijshmi, i Mirinformuari”.');
INSERT INTO "hasan_efendi" VALUES(66,4,'66:4','Nëse ju të dyja ktheheni të penduara tek Allahu, sepse vërtet që zemrat tuaja patën devijuar, Allahu do t’ju falë. Nëse ju të dyja bashkoheni kundër tij, (dijeni se) Allahu është Mbrojtësi i tij, Xhebraili dhe besimtarët e mirë. Veç kësaj, edhe engjëjt e mbështesin atë.');
INSERT INTO "hasan_efendi" VALUES(66,5,'66:5','Në qoftë se ai ju ndan, Zoti i tij mund t’ju zëvendësojë me gra më të mira se ju: myslimane, besimtare, të bindura, të kthyera tek Allahu me pendim, adhuruese (të Allahut), agjëruese, të martuara më parë ose të virgjëra.');
INSERT INTO "hasan_efendi" VALUES(66,6,'66:6','O ju, që besoni! Ruani veten dhe familjet tuaja nga zjarri, lënda djegëse e të cilit janë njerëzit dhe gurët. Atë e mbikëqyrin engjëj të fuqishëm e të ashpër, të cilët kurrë nuk i kundërshtojnë urdhrat e Allahut, por i zbatojnë menjëherë ato.');
INSERT INTO "hasan_efendi" VALUES(66,7,'66:7','O ju, që nuk keni besuar! Mos u shfajësoni sot (në Ditën e Gjykimit), sepse, me të vërtetë, dënoheni vetëm për veprat që keni bërë.');
INSERT INTO "hasan_efendi" VALUES(66,8,'66:8','O ju, që keni besuar! Kthehuni tek Allahu me një pendim të sinqertë, që Zoti juaj t’jua shlyejë gabimet e t’ju shpjerë në kopshte, nëpër të cilat rrjedhin lumenj, Ditën, kur Allahu nuk do ta turpërojë Profetin dhe ata që besuan bashkë me atë. Drita e tyre do të shkëlqejë para tyre dhe në të djathtë të tyre. Ata do të thonë: “O Zoti ynë, përsose dritën tonë dhe falna ne! Vërtet Ti je i Fuqishëm për çdo gjë”.');
INSERT INTO "hasan_efendi" VALUES(66,9,'66:9','O Profet! Luftoji të pafetë dhe hipokritët dhe bëhu i ashpër ndaj tyre! Vendbanimi i tyre do të jetë Xhehenemi. Eh, sa i shëmtuar është ai vendbanim!');
INSERT INTO "hasan_efendi" VALUES(66,10,'66:10','Allahu ka dhënë si shembull për ata që nuk besojnë, gruan e Nuhut dhe gruan e Lutit. Ato ishin të martuara me dy nga robtë Tanë të ndershëm dhe i tradhtuan ata. Prandaj, ata nuk mund t’i mbrojnë aspak nga Allahu (Ditën e Gjykimit) e atyre të dyjave do t’u thuhet: “Hyni në zjarr, me ata që po hyjnë!”');
INSERT INTO "hasan_efendi" VALUES(66,11,'66:11','Allahu ka dhënë si shembull për besimtarët gruan e Faraonit, kur ajo tha: “O Zoti im! Ndërtomë një banesë tek Ti, në Xhenet; më shpëto nga Faraoni dhe veprat e tij dhe më shpëto nga populli keqbërës!”');
INSERT INTO "hasan_efendi" VALUES(66,12,'66:12','Edhe Merjemen, të bijën e Imranit, e cila ruajti virgjërinë e saj; Ne frymë brenda saj nga shpirti Ynë (shpirtin e Isait a.s.). Ajo u besoi fjalëve të Zotit të saj dhe librave të Tij e ishte nga të devotshmit!');
INSERT INTO "hasan_efendi" VALUES(67,1,'67:1','Qoftë i lartësuar Ai, në dorën e të Cilit është pushteti suprem dhe që është i Fuqishëm të bëjë çdo gjë!');
INSERT INTO "hasan_efendi" VALUES(67,2,'67:2','Ai i ka krijuar jetën dhe vdekjen, për t’ju provuar se kush prej jush do të veprojë më mirë; Ai është i Plotfuqishmi dhe Falësi i madh.');
INSERT INTO "hasan_efendi" VALUES(67,3,'67:3','Ai ka krijuar shtatë qiej njërin mbi tjetrin. Nuk mund të gjesh në krijimin e të Gjithëmëshirshmit kurrfarë ceni. Hidhe vështrimin: a sheh ndonjë plasaritje?');
INSERT INTO "hasan_efendi" VALUES(67,4,'67:4','Pastaj hidhe sërish vështrimin; sytë e tu do të kthehen të lodhur e të molisur.');
INSERT INTO "hasan_efendi" VALUES(67,5,'67:5','Ne jua kemi zbukuruar qiellin më të afërm me yje të shkëlqyeshme dhe i kemi bërë ato si predha për të larguar djajtë, për të cilët kemi përgatitur dënimin me zjarr.');
INSERT INTO "hasan_efendi" VALUES(67,6,'67:6','Kurse për ata, që e mohojnë Zotin e tyre, dënimi është Xhehenemi; eh, sa i shëmtuar është ai vendbanim!');
INSERT INTO "hasan_efendi" VALUES(67,7,'67:7','Kur të hidhen aty, do të dëgjojnë ulërimën e tij, ndërkohë që ai vlon');
INSERT INTO "hasan_efendi" VALUES(67,8,'67:8','gati për të shpërthyer me furi. Sa herë që aty të hidhet ndonjë grup, rojtarët e tij do t’i pyesin ata: “A nuk ju erdhi ndonjë paralajmërues?!”');
INSERT INTO "hasan_efendi" VALUES(67,9,'67:9','“Po - do të përgjigjen ata - na kanë ardhur vërtet paralajmërues, por ne i quajtëm ata gënjeshtarë dhe thamë: “Allahu nuk ka shpallur asgjë; ju jeni në një humbje të madhe”!');
INSERT INTO "hasan_efendi" VALUES(67,10,'67:10','Ata do të thonë: “Sikur të kishim dëgjuar ose menduar, nuk do të ishim midis banorëve të Zjarrit Flakërues!”');
INSERT INTO "hasan_efendi" VALUES(67,11,'67:11','Kështu, ata do ta pranojnë gjynahun e tyre. Larg qofshin prej mëshirës së Zotit banorët e Zjarrit Flakërues!');
INSERT INTO "hasan_efendi" VALUES(67,12,'67:12','Vërtet, për ata që i frikësohen Zotit të tyre pa e parë Atë, do të ketë falje dhe shpërblim të madh!');
INSERT INTO "hasan_efendi" VALUES(67,13,'67:13','Sido që të flisni ju, fshehtas apo haptas, Ai e di mirë ç’ka në zemrat tuaja.');
INSERT INTO "hasan_efendi" VALUES(67,14,'67:14','E si të mos dijë Ai që ka krijuar gjithçka, kur Ai njeh çdo gjë me imtësi dhe është i Gjithëinformuari?!');
INSERT INTO "hasan_efendi" VALUES(67,15,'67:15','Ai jua ka bërë Tokën të përdorshme, andaj ecni nëpër viset e saj dhe ushqehuni me atë, që ju ka dhënë Ai. Si të ringjalleni, do të ktheheni tek Ai.');
INSERT INTO "hasan_efendi" VALUES(67,16,'67:16','A jeni të sigurt se kush është në qiell nuk do ta bëjë Tokën t’ju kapërdijë, kur të fillojë të dridhet papritmas?');
INSERT INTO "hasan_efendi" VALUES(67,17,'67:17','A jeni të sigurt se kush është në qiell, nuk do t’ju çojë furtunë gurësh? Atëherë do ta dini sa i frikshëm është paralajmërimi Im!');
INSERT INTO "hasan_efendi" VALUES(67,18,'67:18','Edhe ata që ishin para jush i quajtën gënjeshtarë të dërguarit e tyre. Eh, sa i ashpër ka qenë ndëshkimi Im!');
INSERT INTO "hasan_efendi" VALUES(67,19,'67:19','A nuk i shohin shpendët mbi kokat e tyre, se si i hapin dhe i mbledhin krahët? Askush nuk i mban ata, përveç të Gjithëmëshirshmit; Ai, me të vërtetë, sheh çdo gjë.');
INSERT INTO "hasan_efendi" VALUES(67,20,'67:20','Kush është ai, që mund t’ju ndihmojë si një ushtri e tërë, përveç të Gjithëmëshirshmit? Mohuesit janë pre e mashtrimit (të shejtanit).');
INSERT INTO "hasan_efendi" VALUES(67,21,'67:21','Kush janë ata, që do t’ju japin mjete jetese, në qoftë se Ai jua ndalon ato? Por ata janë zhytur në kryeneçësi e mendjemadhësi.');
INSERT INTO "hasan_efendi" VALUES(67,22,'67:22','A është më mirë ai që shkon rrëshqanas e kokulur apo ai që ecën ballëlart në rrugën e drejtë?');
INSERT INTO "hasan_efendi" VALUES(67,23,'67:23','Thuaju: “Është Ai që ju ka krijuar dhe ju ka dhënë dëgjimin, shikimin dhe zemrat, e megjithatë ju pak e falënderoni!”');
INSERT INTO "hasan_efendi" VALUES(67,24,'67:24','Thuaju: “Është Ai që ju ka shpërndarë nëpër Tokë dhe para Tij do të tuboheni”.');
INSERT INTO "hasan_efendi" VALUES(67,25,'67:25','Ata thonë: “Atëherë, kur (do të vijë) ky premtim, nëse thoni të vërtetën?”');
INSERT INTO "hasan_efendi" VALUES(67,26,'67:26','Thuaju: “Dijenia (për këtë) i përket vetëm Allahut, kurse unë jam vetëm një paralajmërues i qartë”.');
INSERT INTO "hasan_efendi" VALUES(67,27,'67:27','Kur të shohin dënimin për së afërmi, fytyrat e atyre që mohuan do të zymtohen dhe, atëherë, do t’u thuhet: “Ja, kjo është ajo që e keni kërkuar të shpejtohej!”');
INSERT INTO "hasan_efendi" VALUES(67,28,'67:28','Thuaj: “Mendoni: në qoftë se Allahu më shkatërron mua dhe ndjekësit e mi apo na mëshiron, kush është ai që do t’i mbrojë jobesimtarët nga dënimi i padurueshëm?!”');
INSERT INTO "hasan_efendi" VALUES(67,29,'67:29','Thuaj: “Ai është i Gjithëmëshirshmi; ne Atë besojmë dhe tek Ai mbështetemi e ju, sigurisht, do ta merrni vesh se kush do të jetë në humbje të plotë”.');
INSERT INTO "hasan_efendi" VALUES(67,30,'67:30','Thuaj: “Mendoni: nëse ujërat tuaja ju shteren, kush (përveç Allahut) do t’ju sjellë ujë rrjedhës?!”');
INSERT INTO "hasan_efendi" VALUES(68,1,'68:1','Nûn. Betohem për penën dhe për atë që shkruajnë');
INSERT INTO "hasan_efendi" VALUES(68,2,'68:2','se ti (o Muhamed), falë mirësisë së Zotit tënd, nuk je i marrë.');
INSERT INTO "hasan_efendi" VALUES(68,3,'68:3','Pa dyshim, ti do të jesh përherë i shpërblyer');
INSERT INTO "hasan_efendi" VALUES(68,4,'68:4','se ti je me virtyte madhore.');
INSERT INTO "hasan_efendi" VALUES(68,5,'68:5','Së shpejti ti do ta shohësh, por edhe ata do ta shohin,');
INSERT INTO "hasan_efendi" VALUES(68,6,'68:6','se cilin prej jush e ka prekur marrëzia.');
INSERT INTO "hasan_efendi" VALUES(68,7,'68:7','Zoti yt e di më së miri, se cili është shmangur nga rruga e Tij dhe, i njeh mirë ata që janë në rrugën e drejtë.');
INSERT INTO "hasan_efendi" VALUES(68,8,'68:8','Andaj, ti mos i dëgjo jobesimtarët!');
INSERT INTO "hasan_efendi" VALUES(68,9,'68:9','Ata dëshirojnë që ti të bësh lëshime, kështu që dhe ata të bëjnë lëshime.');
INSERT INTO "hasan_efendi" VALUES(68,10,'68:10','Por ti mos iu bind çdo betari të përçmuar,');
INSERT INTO "hasan_efendi" VALUES(68,11,'68:11','shpifaraku, që përhap thashetheme,');
INSERT INTO "hasan_efendi" VALUES(68,12,'68:12','penguesi veprash të mira, shkelësi dhe keqbërësi,');
INSERT INTO "hasan_efendi" VALUES(68,13,'68:13','të ashpri që, veç kësaj, është edhe kopil,');
INSERT INTO "hasan_efendi" VALUES(68,14,'68:14','vetëm se është i pasur dhe ka shumë djem.');
INSERT INTO "hasan_efendi" VALUES(68,15,'68:15','Kur i lexohen shpalljet tona, ai thotë: “Këto janë vetëm përralla të të lashtëve!”');
INSERT INTO "hasan_efendi" VALUES(68,16,'68:16','Ne do t’ia damkosim hundën!');
INSERT INTO "hasan_efendi" VALUES(68,17,'68:17','Ne i kemi vënë në provë ata (banorët e Mekës), ashtu siç i sprovuam të zotët e kopshtit, të cilët ishin betuar se në mëngjes do t’i vilnin frutat');
INSERT INTO "hasan_efendi" VALUES(68,18,'68:18','e nuk patën thënë “Në dashtë Zoti”!');
INSERT INTO "hasan_efendi" VALUES(68,19,'68:19','Kështu, kopshtin e goditi një fatkeqësi nga Zoti yt, ndërkohë që ata po flinin');
INSERT INTO "hasan_efendi" VALUES(68,20,'68:20','dhe gdhiu si i kositur.');
INSERT INTO "hasan_efendi" VALUES(68,21,'68:21','Në agim, ata thirrën njëri-tjetrin:');
INSERT INTO "hasan_efendi" VALUES(68,22,'68:22','“Nxitoni herët në kopshtin tuaj, nëse doni t’i vilni frutat!”');
INSERT INTO "hasan_efendi" VALUES(68,23,'68:23','Dhe u nisën duke pëshpëritur:');
INSERT INTO "hasan_efendi" VALUES(68,24,'68:24','“Të mos lejojmë asnjë të vobekët që të hyjë në kopshtin tonë!”');
INSERT INTO "hasan_efendi" VALUES(68,25,'68:25','Ata u nisën herët, të vendosur në atë që thanë.');
INSERT INTO "hasan_efendi" VALUES(68,26,'68:26','Por, kur e panë kopshtin, ata thanë: “Ne qenkemi vërtet të humbur;');
INSERT INTO "hasan_efendi" VALUES(68,27,'68:27','madje, qenkemi të privuar (nga frutat e kopshtit).”');
INSERT INTO "hasan_efendi" VALUES(68,28,'68:28','Më i arsyeshmi ndër ta tha: “A nuk ju thashë unë se duhet ta lartësoni Allahun?!”');
INSERT INTO "hasan_efendi" VALUES(68,29,'68:29','“Lavdëruar qoftë Zoti ynë! - thanë - Ne paskemi qenë vërtet të padrejtë!”');
INSERT INTO "hasan_efendi" VALUES(68,30,'68:30','Atëherë filluan të qortojnë njëri-tjetrin.');
INSERT INTO "hasan_efendi" VALUES(68,31,'68:31','“Ah, të mjerët ne! - thanë ata - Paskemi qenë vërtet arrogantë!');
INSERT INTO "hasan_efendi" VALUES(68,32,'68:32','Zoti ynë mund të na japë një kopsht më të mirë, po të kthehemi të penduar tek Ai!”');
INSERT INTO "hasan_efendi" VALUES(68,33,'68:33','I tillë ka qenë dënimi i tyre. Ndërsa dënimi në jetën tjetër, është më i madh, veç sikur ta dinin!');
INSERT INTO "hasan_efendi" VALUES(68,34,'68:34','Për ata që ruhen (nga gjynahet), tek Allahu do të ketë kopshte të kënaqësive.');
INSERT INTO "hasan_efendi" VALUES(68,35,'68:35','Vallë, a t’i trajtojmë ata që Na nënshtrohen njësoj me keqbërësit?!');
INSERT INTO "hasan_efendi" VALUES(68,36,'68:36','Çfarë keni ju (jobesimtarë)? Si po gjykoni kështu?!');
INSERT INTO "hasan_efendi" VALUES(68,37,'68:37','A keni ndonjë libër, që ju premton,');
INSERT INTO "hasan_efendi" VALUES(68,38,'68:38','se do të keni çfarëdo, që të zgjidhni?!');
INSERT INTO "hasan_efendi" VALUES(68,39,'68:39','Apo kemi bërë me ju ndonjë besëlidhje të vlefshme deri në Ditën e Kiametit dhe, e cila ju siguron, se do ta keni atë që keni urdhëruar?');
INSERT INTO "hasan_efendi" VALUES(68,40,'68:40','Pyeti ata se kush i garanton për këtë.');
INSERT INTO "hasan_efendi" VALUES(68,41,'68:41','Ose, mos kanë ata bashkëpunëtorë? Atëherë le t’i sjellin bashkëpunëtorët e tyre, nëse ajo që thonë është e vërtetë.');
INSERT INTO "hasan_efendi" VALUES(68,42,'68:42','Ditën (e Kiametit), kur të zbulohen shumë ngjarje të frikshme, ata do të thirren që të përulen në sexhde, por nuk do të munden!');
INSERT INTO "hasan_efendi" VALUES(68,43,'68:43','Të ulura do të jenë shikimet e tyre dhe do t’i mbulojë poshtërimi, sepse kanë qenë të ftuar të përuleshin në sexhde, sa qenë shëndoshë (dhe nuk kanë pranuar).');
INSERT INTO "hasan_efendi" VALUES(68,44,'68:44','Prandaj, më lër Mua me ata, që e mohojnë këtë Fjalë (Kuranin). Ne do t’i çojmë ata hap pas hapi drejt rrënimit të tyre, me mënyra që ata nuk i kuptojnë.');
INSERT INTO "hasan_efendi" VALUES(68,45,'68:45','Dhe do t’u japim atyre afat; vërtet që kurthi Im është i fortë!');
INSERT INTO "hasan_efendi" VALUES(68,46,'68:46','A mos po kërkon shpërblim prej tyre, kështu që qenkan të rënduar nga borxhet?');
INSERT INTO "hasan_efendi" VALUES(68,47,'68:47','Apo ata e dinë të padukshmen, kështu që e shkruajnë?');
INSERT INTO "hasan_efendi" VALUES(68,48,'68:48','Prandaj, duro për gjykimin e Zotit tënd dhe mos u bëj si Njeriu i Peshkut (Junusi a.s.), kur iu lut Zotit të vet i dëshpëruar (në barkun e balenës)!');
INSERT INTO "hasan_efendi" VALUES(68,49,'68:49','Sikur të mos i vinte mëshira prej Zotit tënd, ai do të hidhej në një shkretëtirë, i fajësuar!');
INSERT INTO "hasan_efendi" VALUES(68,50,'68:50','Por Zoti i tij e zgjodhi dhe e futi me punëdrejtët.');
INSERT INTO "hasan_efendi" VALUES(68,51,'68:51','Kur dëgjojnë Kuranin, mohuesit sa nuk të hanë me shikimet e tyre dhe thonë: “Ai është vërtet i marrë!”');
INSERT INTO "hasan_efendi" VALUES(68,52,'68:52','Por Kurani është vetëm këshillë për botët.');
INSERT INTO "hasan_efendi" VALUES(69,1,'69:1','E Vërteta e pashmangshme! ');
INSERT INTO "hasan_efendi" VALUES(69,2,'69:2','Ç’është e Vërteta e pashmangshme?');
INSERT INTO "hasan_efendi" VALUES(69,3,'69:3','E kush do të ta shpjegojë ty se ç’është e Vërteta e pashmangshme?');
INSERT INTO "hasan_efendi" VALUES(69,4,'69:4','Fiset Themud dhe Ad e mohuan Gjëmimin e tmerrshëm ,');
INSERT INTO "hasan_efendi" VALUES(69,5,'69:5','prandaj fisi Themud u shkatërrua nga një ulërimë e tmerrshme,');
INSERT INTO "hasan_efendi" VALUES(69,6,'69:6','kurse fisi Ad u shkatërrua nga një furtunë shungulluese të rreptë,');
INSERT INTO "hasan_efendi" VALUES(69,7,'69:7','të cilën Allahu ua dërgoi atyre shtatë net dhe tetë ditë pareshtur. Atëherë mund t’i vëreje njerëzit të përmbysur si trungjet e hurmave të kalbura.');
INSERT INTO "hasan_efendi" VALUES(69,8,'69:8','A sheh ndonjë gjurmë të tyre?');
INSERT INTO "hasan_efendi" VALUES(69,9,'69:9','Faraoni, ata para tij dhe populli i qyteteve të rrënuara kryen gjynahe të mëdha,');
INSERT INTO "hasan_efendi" VALUES(69,10,'69:10','dhe e kundërshtuan të dërguarin e Zotit të tyre, andaj Allahu i zhbiu ata me dënim të ashpër.');
INSERT INTO "hasan_efendi" VALUES(69,11,'69:11','Kur vërshoi uji i madh, Ne ju ngarkuam ju në anije,');
INSERT INTO "hasan_efendi" VALUES(69,12,'69:12','për ta bërë atë një paralajmërim, që ta dëgjojë çdo vesh i vëmendshëm.');
INSERT INTO "hasan_efendi" VALUES(69,13,'69:13','Dhe kur t’i fryhet një herë Surit,');
INSERT INTO "hasan_efendi" VALUES(69,14,'69:14','kur toka dhe malet të ngrihen lart e të thërrmohen me një goditje shkatërrimtare,');
INSERT INTO "hasan_efendi" VALUES(69,15,'69:15','atëherë do të ndodhë Ngjarja e pashmangshme (Kiameti).');
INSERT INTO "hasan_efendi" VALUES(69,16,'69:16','Dhe do të çahet qielli, që atë ditë do të jetë i thyeshëm.');
INSERT INTO "hasan_efendi" VALUES(69,17,'69:17','Engjëjt do të rrinë skajeve të tij, kurse tetë prej tyre do të mbajnë Fronin e Zotit tënd.');
INSERT INTO "hasan_efendi" VALUES(69,18,'69:18','Atë Ditë do të mbeteni zbuluar dhe asnjë fshehtësi juaja nuk do të mbetet pa u shfaqur:');
INSERT INTO "hasan_efendi" VALUES(69,19,'69:19','Ai, të cilit do t’i jepet libri i veprave të tij nga ana e djathtë, do t’u thotë (njerëzve të tij): “Ja, lexoni librin tim!');
INSERT INTO "hasan_efendi" VALUES(69,20,'69:20','Vërtet, unë kam qenë i bindur se do të jepja llogari.”');
INSERT INTO "hasan_efendi" VALUES(69,21,'69:21','Kështu, ai do të ketë jetë të lumtur,');
INSERT INTO "hasan_efendi" VALUES(69,22,'69:22','në një kopsht të lartë,');
INSERT INTO "hasan_efendi" VALUES(69,23,'69:23','frutat e të cilit do t’i kapë lehtë.');
INSERT INTO "hasan_efendi" VALUES(69,24,'69:24','(Njerëzve të tillë u thuhet:) “Ju bëftë mirë, hani e pini për atë, që keni bërë në ditët e kaluara!”');
INSERT INTO "hasan_efendi" VALUES(69,25,'69:25','Por ai, të cilit do t’i jepet libri i veprave të tij nga ana e majtë, do të thotë: “Ah, sikur të mos më ishte dhënë libri im');
INSERT INTO "hasan_efendi" VALUES(69,26,'69:26','dhe të mos e kisha ditur llogarinë time!');
INSERT INTO "hasan_efendi" VALUES(69,27,'69:27','Ah, të kisha qenë i vdekur (e të mos isha ringjallur)!');
INSERT INTO "hasan_efendi" VALUES(69,28,'69:28','Pasuria ime nuk më solli dobi');
INSERT INTO "hasan_efendi" VALUES(69,29,'69:29','dhe më la fuqia ime!”');
INSERT INTO "hasan_efendi" VALUES(69,30,'69:30','(Do të thuhet) “Kapeni dhe lidheni në pranga,');
INSERT INTO "hasan_efendi" VALUES(69,31,'69:31','pastaj digjeni në zjarrin përvëlues,');
INSERT INTO "hasan_efendi" VALUES(69,32,'69:32','mandej lidheni në zinxhirë prej shtatëdhjetë kutesh,');
INSERT INTO "hasan_efendi" VALUES(69,33,'69:33','sepse ai, me të vërtetë, nuk ka besuar në Allahun e Madhërishëm');
INSERT INTO "hasan_efendi" VALUES(69,34,'69:34','dhe nuk është kujdesur për të ushqyer të varfrit e shkretë.');
INSERT INTO "hasan_efendi" VALUES(69,35,'69:35','Prandaj, ai sot këtu s’ka mik të afërt');
INSERT INTO "hasan_efendi" VALUES(69,36,'69:36','e as ushqim tjetër, përveç qelbit,');
INSERT INTO "hasan_efendi" VALUES(69,37,'69:37','të cilin e hanë vetëm gjynahqarët.');
INSERT INTO "hasan_efendi" VALUES(69,38,'69:38','Betohem për gjithçka që ju shihni');
INSERT INTO "hasan_efendi" VALUES(69,39,'69:39','dhe për gjithçka që nuk e shihni,');
INSERT INTO "hasan_efendi" VALUES(69,40,'69:40','se ky (Kuran) është vërtet fjalë e (kumtuar prej) një të Dërguari të nderuar (Muhamedit a.s.)');
INSERT INTO "hasan_efendi" VALUES(69,41,'69:41','e nuk është fjalë poeti; sa pak që besoni!');
INSERT INTO "hasan_efendi" VALUES(69,42,'69:42','Nuk është as fjalë e ndonjë falltari; sa pak që po reflektoni!');
INSERT INTO "hasan_efendi" VALUES(69,43,'69:43','Ai është shpallje e zbritur nga Zoti i botëve!');
INSERT INTO "hasan_efendi" VALUES(69,44,'69:44','Sikur ai (Muhamedi), në mënyrë të rreme, të Na kishte veshur ndonjë fjalë,');
INSERT INTO "hasan_efendi" VALUES(69,45,'69:45','Ne do ta kapnim atë për dorën e djathtë');
INSERT INTO "hasan_efendi" VALUES(69,46,'69:46','dhe me siguri që do t’ia prisnim damarin e qafës');
INSERT INTO "hasan_efendi" VALUES(69,47,'69:47','e askush prej jush nuk do të mund ta mbronte.');
INSERT INTO "hasan_efendi" VALUES(69,48,'69:48','Natyrisht, Kurani është përkujtesë për ata që i frikësohen Zotit!');
INSERT INTO "hasan_efendi" VALUES(69,49,'69:49','Ne e dimë mirë se ka prej jush që e mohojnë atë.');
INSERT INTO "hasan_efendi" VALUES(69,50,'69:50','Ai është keqardhje për mohuesit. ');
INSERT INTO "hasan_efendi" VALUES(69,51,'69:51','Ai është, pa dyshim, e vërtetë e pamohueshme.');
INSERT INTO "hasan_efendi" VALUES(69,52,'69:52','Prandaj, lartësoje emrin e Zotit tënd të Madhërishëm!');
INSERT INTO "hasan_efendi" VALUES(70,1,'70:1','Dikush kërkoi një dënim të shpejtë. ');
INSERT INTO "hasan_efendi" VALUES(70,2,'70:2','Për jobesimtarët askush nuk mund ta ndalojë atë (dënim),');
INSERT INTO "hasan_efendi" VALUES(70,3,'70:3','(që vjen) nga Allahu, Zoti i Rrugëve të Ngjitjes .');
INSERT INTO "hasan_efendi" VALUES(70,4,'70:4','Tek Ai ngjiten engjëjt dhe Shpirti (Xhebraili), në një ditë që zgjat sa pesëdhjetë mijë vjet.');
INSERT INTO "hasan_efendi" VALUES(70,5,'70:5','Prandaj, duro, me durim të hijshëm.');
INSERT INTO "hasan_efendi" VALUES(70,6,'70:6','Ata e mendojnë atë (dënim) të largët,');
INSERT INTO "hasan_efendi" VALUES(70,7,'70:7','kurse Ne e shohim të afërt.');
INSERT INTO "hasan_efendi" VALUES(70,8,'70:8','Atë Ditë qielli do të bëhet si llumi i vajit');
INSERT INTO "hasan_efendi" VALUES(70,9,'70:9','e malet do të bëhen si lesh i shprishur,');
INSERT INTO "hasan_efendi" VALUES(70,10,'70:10','Shoku s’do të pyesë për të shokun,');
INSERT INTO "hasan_efendi" VALUES(70,11,'70:11','edhe pse do të vihen të shohin njëri-tjetrin. Për të liruar veten nga dënimi i asaj dite, fajtori do të sakrifikonte me gjithë qejf fëmijët e vet,');
INSERT INTO "hasan_efendi" VALUES(70,12,'70:12','edhe gruan, edhe vëllanë e vet,');
INSERT INTO "hasan_efendi" VALUES(70,13,'70:13','edhe farefisin, që i gjendej pranë (ne jetën e Tokës),');
INSERT INTO "hasan_efendi" VALUES(70,14,'70:14','si dhe të gjithë ata që gjenden në faqe të Tokës, nëse kjo do të mund ta shpëtonte.');
INSERT INTO "hasan_efendi" VALUES(70,15,'70:15','Por jo, (fajtori do të hidhet në zjarr)! Ai është zjarr flakërues,');
INSERT INTO "hasan_efendi" VALUES(70,16,'70:16','që e shkul (dhe e djeg) krejtësisht skalpin e kokës.');
INSERT INTO "hasan_efendi" VALUES(70,17,'70:17','Ai thërret këdo që (i) ka kthyer shpinën e (i) është larguar (besimit),');
INSERT INTO "hasan_efendi" VALUES(70,18,'70:18','dhe që ka grumbulluar (pasuri) dhe e ka fshehur.');
INSERT INTO "hasan_efendi" VALUES(70,19,'70:19','Vërtet që njeriu është krijuar i paqëndrueshëm:');
INSERT INTO "hasan_efendi" VALUES(70,20,'70:20','kur atë e godet e keqja, ai ligështohet fort,');
INSERT INTO "hasan_efendi" VALUES(70,21,'70:21','por, kur i bie e mira, ai bëhet koprrac.');
INSERT INTO "hasan_efendi" VALUES(70,22,'70:22','Përjashtim bëjnë ata që falen,');
INSERT INTO "hasan_efendi" VALUES(70,23,'70:23','e që janë të vazhdueshëm në faljet e tyre;');
INSERT INTO "hasan_efendi" VALUES(70,24,'70:24','e që caktojnë një pjesë të pasurisë së tyre');
INSERT INTO "hasan_efendi" VALUES(70,25,'70:25','për lypësin dhe për nevojtarin;');
INSERT INTO "hasan_efendi" VALUES(70,26,'70:26','e që besojnë në Ditën e Llogarisë (Gjykimit);');
INSERT INTO "hasan_efendi" VALUES(70,27,'70:27','e që frikësohen nga dënimi i Zotit të tyre,');
INSERT INTO "hasan_efendi" VALUES(70,28,'70:28','- vërtet që askush nuk është i sigurt prej dënimit të Zotit të vet -;');
INSERT INTO "hasan_efendi" VALUES(70,29,'70:29','e që e ruajnë nderin e tyre (nga marrëdhëniet e jashtëligjshme),');
INSERT INTO "hasan_efendi" VALUES(70,30,'70:30','përveçse ndaj bashkëshorteve dhe skllaveve të veta - në këtë rast nuk meritojnë qortim,');
INSERT INTO "hasan_efendi" VALUES(70,31,'70:31','por kush kërkon jashtë këtyre caqeve, pikërisht ata janë shkelësit -;');
INSERT INTO "hasan_efendi" VALUES(70,32,'70:32','e që i ruajnë amanetet dhe i plotësojnë besëlidhjet e tyre;');
INSERT INTO "hasan_efendi" VALUES(70,33,'70:33','e që gjithnjë dëshmojnë drejt;');
INSERT INTO "hasan_efendi" VALUES(70,34,'70:34','e që i falin namazet rregullisht.');
INSERT INTO "hasan_efendi" VALUES(70,35,'70:35','Pikërisht ata do të jenë të nderuar në kopshtet e Xhenetit.');
INSERT INTO "hasan_efendi" VALUES(70,36,'70:36','Ç’është kështu me jobesimtarët, që nxitojnë drejt teje');
INSERT INTO "hasan_efendi" VALUES(70,37,'70:37','djathtas e majtas, grupe-grupe?!');
INSERT INTO "hasan_efendi" VALUES(70,38,'70:38','A mos çdonjëri prej tyre shpreson të hyjë në Kopshtin e Lumturisë?');
INSERT INTO "hasan_efendi" VALUES(70,39,'70:39','Kurrsesi! Vërtet që ata e dinë nga çfarë i kemi krijuar!');
INSERT INTO "hasan_efendi" VALUES(70,40,'70:40','Betohem për Zotin e lindjeve dhe të perëndimeve, që Ne jemi të Zotë');
INSERT INTO "hasan_efendi" VALUES(70,41,'70:41','t’i ndërrojmë ata me njerëz më të mirë se ata dhe askush nuk na pengon dot.');
INSERT INTO "hasan_efendi" VALUES(70,42,'70:42','Andaj, lëri ata të zbaviten me gjëra të kota dhe të luajnë derisa të përballen me Ditën e tyre që u është premtuar,');
INSERT INTO "hasan_efendi" VALUES(70,43,'70:43','Ditën, në të cilën do të dalin nga varrezat duke vrapuar, sikur të ishin duke nxituar drejt një altari,');
INSERT INTO "hasan_efendi" VALUES(70,44,'70:44','me shikim të frikësuar e të kapluar nga poshtërimi. Ajo është Dita që u është premtuar atyre!');
INSERT INTO "hasan_efendi" VALUES(71,1,'71:1','Ne e dërguam Nuhun te populli i tij dhe i thamë: “Paralajmëroje popullin tënd para se t’i vijë një dënim i dhembshëm!”');
INSERT INTO "hasan_efendi" VALUES(71,2,'71:2','Ai tha: “O populli im! Në të vërtetë, unë jam për ju një paralajmërues i qartë:');
INSERT INTO "hasan_efendi" VALUES(71,3,'71:3','që të adhuroni Allahun, t’i frikësoheni Atij e të më bindeni mua,');
INSERT INTO "hasan_efendi" VALUES(71,4,'71:4','me qëllim që Ai të falë disa nga gjynahet tuaja dhe t’ju lërë (të jetoni) deri në një afat të caktuar. Dhe, kur t’ju vijë çasti i caktuar i Allahut, askush nuk do të mund ta shtyjë atë, veç sikur ta dinit”!');
INSERT INTO "hasan_efendi" VALUES(71,5,'71:5','Ai tha: “O Zoti im, unë e thirra vërtet popullin tim natë e ditë,');
INSERT INTO "hasan_efendi" VALUES(71,6,'71:6','por thirrja ime edhe më tepër i largoi (nga e vërteta).');
INSERT INTO "hasan_efendi" VALUES(71,7,'71:7','Sa herë, që i thirrja për t’i falur Ti, ata shtinin gishtërinjtë në veshë dhe mbuloheshin me petkat e tyre, duke këmbëngulur me mendjemadhësi (në mosbesim).');
INSERT INTO "hasan_efendi" VALUES(71,8,'71:8','Pastaj i thirra me zë të lartë.');
INSERT INTO "hasan_efendi" VALUES(71,9,'71:9','Më pas ua shpalla edhe botërisht, edhe fshehurazi');
INSERT INTO "hasan_efendi" VALUES(71,10,'71:10','e u thashë: “Kërkoni falje nga Zoti juaj, i Cili është Falës i madh,');
INSERT INTO "hasan_efendi" VALUES(71,11,'71:11','që Ai t’ju dërgojë shi të bollshëm,');
INSERT INTO "hasan_efendi" VALUES(71,12,'71:12','t’ju shtojë pasurinë dhe fëmijët tuaj dhe të bëjë për ju kopshte e lumenj!');
INSERT INTO "hasan_efendi" VALUES(71,13,'71:13','Ç’është kështu me ju, që nuk e madhëroni Allahun siç duhet (duke e pasur frikë),');
INSERT INTO "hasan_efendi" VALUES(71,14,'71:14','ndërkohë që Ai ju ka krijuar në etapa (etapat e embrionit)?');
INSERT INTO "hasan_efendi" VALUES(71,15,'71:15','A nuk e shihni se si Allahu i ka krijuar shtatë qiejt njëri mbi tjetrin,');
INSERT INTO "hasan_efendi" VALUES(71,16,'71:16','dhe në ta e ka bërë Hënën dritë, kurse Diellin e ka bërë fener?');
INSERT INTO "hasan_efendi" VALUES(71,17,'71:17','Allahu ju ka krijuar nga toka, ashtu si bimët,');
INSERT INTO "hasan_efendi" VALUES(71,18,'71:18','pastaj ju kthen tek ajo dhe prej saj sërish do t’ju nxjerrë.');
INSERT INTO "hasan_efendi" VALUES(71,19,'71:19','Allahu për ju e ka bërë Tokën shtrojë të gjerë,');
INSERT INTO "hasan_efendi" VALUES(71,20,'71:20','që të ecni nëpër të, nëpër rrugët e gjera.”');
INSERT INTO "hasan_efendi" VALUES(71,21,'71:21','Nuhu tha: “O Zoti im, ata më kundërshtuan mua dhe u dhanë pas atyre njerëzve, pasuria dhe fëmijët e të cilëve nuk kanë sjellë gjë tjetër, veç shkatërrimit.');
INSERT INTO "hasan_efendi" VALUES(71,22,'71:22','Ata kurdisën një kurth të madh,');
INSERT INTO "hasan_efendi" VALUES(71,23,'71:23','dhe thanë: ‘Mos i lini kurrsesi zotat tuaj! Mos e lini kurrsesi as Veddin, as Suvain, as Jeguthin, as Jaukën, as Nesrin’!');
INSERT INTO "hasan_efendi" VALUES(71,24,'71:24','E kështu kanë futur në rrugë të gabuar shumë (njerëz). Prandaj, keqbërësve mos u shto gjë tjetër, veç humbjes!”');
INSERT INTO "hasan_efendi" VALUES(71,25,'71:25','Për shkak të gjynaheve të tyre, ata u përmbytën dhe u hodhën në zjarr. Ata nuk gjetën mbrojtës tjetër për veten në vend të Allahut.');
INSERT INTO "hasan_efendi" VALUES(71,26,'71:26','Dhe Nuhu tha: “O Zoti im, mos lër mbi tokë asnjë femohues,');
INSERT INTO "hasan_efendi" VALUES(71,27,'71:27','sepse, nëse Ti i lë, ata do t’i shpien në humbje robërit e Tu dhe do të lindin vetëm kriminelë dhe jobesimtarë!');
INSERT INTO "hasan_efendi" VALUES(71,28,'71:28','O Zoti im, falmë mua dhe prindërit e mi, si dhe çdo besimtar, që kërkon strehim në shtëpinë time! Fali të gjithë besimtarët e besimtaret, ndërsa keqbërësve shtoju vetëm shkatërrim!”');
INSERT INTO "hasan_efendi" VALUES(72,1,'72:1','Thuaj (o Muhamed): “Mua më është shpallur se disa prej xhindeve kanë dëgjuar (Kuranin) dhe kanë thënë: ‘Me të vërtetë, kemi dëgjuar një Kuran të mrekullueshëm,');
INSERT INTO "hasan_efendi" VALUES(72,2,'72:2','i cili udhëzon në rrugën e drejtë dhe kemi besuar në të, prandaj kurrsesi më nuk do t’i shoqërojmë askënd Zotit tonë (në adhurim)!');
INSERT INTO "hasan_efendi" VALUES(72,3,'72:3','Qoftë lartësuar Madhëria e Zotit tonë! Ai nuk ka zgjedhur as bashkëshorte, as bir.');
INSERT INTO "hasan_efendi" VALUES(72,4,'72:4','Por disa të marrë nga ne flisnin gënjeshtra për Allahun,');
INSERT INTO "hasan_efendi" VALUES(72,5,'72:5','kurse ne mendonim se as njerëzit, as xhindet nuk flasin gënjeshtra për Allahun.');
INSERT INTO "hasan_efendi" VALUES(72,6,'72:6','Në të vërtetë, ka pasur njerëz që kërkonin mbrojtje nga xhindet, por kjo vetëm sa i çoi më tej në gabime.');
INSERT INTO "hasan_efendi" VALUES(72,7,'72:7','Edhe ata mendonin siç mendonit ju, se Allahu nuk do të ringjallte askënd.');
INSERT INTO "hasan_efendi" VALUES(72,8,'72:8','Ne jemi ata që kemi synuar qiellin, por kemi hasur në plot roje të forta (engjëj të fuqishëm) dhe shigjeta të zjarrta (meteorë).');
INSERT INTO "hasan_efendi" VALUES(72,9,'72:9','Dhe uleshim rreth tij, nëpër vende të përshtatshme për të dëgjuar diçka, por ai që përgjon tani, do të hasë në shigjeta të zjarrta (meteorë) në pritë.');
INSERT INTO "hasan_efendi" VALUES(72,10,'72:10','Ne nuk e dimë nëse (me këto roja të qiellit) është synuar e keqja (zbritja e dënimit) për ata që janë në Tokë apo Zoti i tyre kërkon t’i udhëzojë në rrugë të drejtë?!');
INSERT INTO "hasan_efendi" VALUES(72,11,'72:11','E midis nesh ka të mirë, por ka edhe që nuk janë të mirë; jemi ndarë në grupime të ndryshme.');
INSERT INTO "hasan_efendi" VALUES(72,12,'72:12','Ne e dimë se nuk mund t’i shpëtojmë Allahut në Tokë dhe nuk mund t’i shmangemi Atij duke ikur (në qiell).');
INSERT INTO "hasan_efendi" VALUES(72,13,'72:13','Posa dëgjuam udhëzimin (Kuranin), Ne besuam në të; e kush beson në Zotin e vet, nuk ka përse të frikësohet se mos dëmtohet, as se mos i bëhet padrejtësi.');
INSERT INTO "hasan_efendi" VALUES(72,14,'72:14','Ka prej nesh që janë myslimanë, por ka edhe të atillë që janë jobesimtarë. Kush ka përqafuar Islamin, ka zgjedhur rrugën e drejtë,');
INSERT INTO "hasan_efendi" VALUES(72,15,'72:15','kurse jobesimtarët do të jenë lëndë djegëse të Xhehenemit.’');
INSERT INTO "hasan_efendi" VALUES(72,16,'72:16','Sikur të qëndronin (banorët e Mekës) në rrugën e vërtetë, Ne do t’u jepnim ujë të bollshëm,');
INSERT INTO "hasan_efendi" VALUES(72,17,'72:17','për t’i vënë ata me këtë në sprovë. Por këdo që largohet nga këshillat e Zotit të tij, Ai do ta hedhë në një dënim të ashpër.');
INSERT INTO "hasan_efendi" VALUES(72,18,'72:18','Xhamitë i janë kushtuar vetëm Allahut, prandaj mos iu lutni askujt krahas Allahut!');
INSERT INTO "hasan_efendi" VALUES(72,19,'72:19','Kur robi i Allahut (Muhamedi), u ngrit t’i lutej Atij, ata (xhindet) u tubuan rreth tij si shtëllungë.”');
INSERT INTO "hasan_efendi" VALUES(72,20,'72:20','Thuaj (o Muhamed): “Unë i lutem vetëm Zotit tim dhe nuk i shoqëroj Atij askënd (në adhurim)”.');
INSERT INTO "hasan_efendi" VALUES(72,21,'72:21','Thuaj: “Unë nuk mund t’ju largoj ndonjë dëm e as t’ju sjell ndonjë dobi”.');
INSERT INTO "hasan_efendi" VALUES(72,22,'72:22','Thuaj: “Edhe mua askush nuk mund të më mbrojë nga dënimi i Allahut; dhe nuk mund të gjej strehim tjetërkund, përveçse tek Ai.');
INSERT INTO "hasan_efendi" VALUES(72,23,'72:23','Unë kam për detyrë veç t’ju përcjell atë që më vjen nga Allahu dhe mesazhet e Tij. Këdo që s’dëgjon Allahun dhe të Dërguarin e Tij, e pret zjarri i Xhehenemit, në të cilin do të mbetet përgjithmonë.”');
INSERT INTO "hasan_efendi" VALUES(72,24,'72:24','Kur ata të shohin atë që u është premtuar, do ta dinë se kush është më i dobët dhe më i paktë në numër.');
INSERT INTO "hasan_efendi" VALUES(72,25,'72:25','Thuaj (o Muhamed!): “Unë nuk e di, a është afër ajo që ju është premtuar apo Zoti im do ta vonojë.”');
INSERT INTO "hasan_efendi" VALUES(72,26,'72:26','Vetëm Ai e di të padukshmen dhe nuk ia zbulon atë askujt,');
INSERT INTO "hasan_efendi" VALUES(72,27,'72:27','përveç të dërguarve që ka zgjedhur. Para dhe pas tyre Ai vë mbikëqyrës (engjëj),');
INSERT INTO "hasan_efendi" VALUES(72,28,'72:28','në mënyrë që ta dijë nëse i përcjellin mesazhet e Zotit të tyre. Ai përfshin me dijen e Vet gjithçka që ata kanë dhe çdo gjë e ka llogaritur imtësisht.');
INSERT INTO "hasan_efendi" VALUES(73,1,'73:1','O ti i mbuluar (o Muhamed)!');
INSERT INTO "hasan_efendi" VALUES(73,2,'73:2','Çohu (të falesh) gjithë natën, përveç një pjese të saj,');
INSERT INTO "hasan_efendi" VALUES(73,3,'73:3','që është gjysma ose diçka më pak');
INSERT INTO "hasan_efendi" VALUES(73,4,'73:4','a diçka më shumë se ajo dhe lexoje Kuranin ngadalë dhe qartë,');
INSERT INTO "hasan_efendi" VALUES(73,5,'73:5','sepse Ne do të të dërgojmë vërtet një fjalë me peshë të rëndë.');
INSERT INTO "hasan_efendi" VALUES(73,6,'73:6','Natyrisht, adhurimi natën vepron më fuqishëm dhe fjala është më shprehëse,');
INSERT INTO "hasan_efendi" VALUES(73,7,'73:7','ngaqë ti ditën je vërtet shumë i zënë.');
INSERT INTO "hasan_efendi" VALUES(73,8,'73:8','Prandaj, përmende emrin e Zotit tënd dhe përkushtoju krejtësisht Atij!');
INSERT INTO "hasan_efendi" VALUES(73,9,'73:9','Ai është Zot i lindjes dhe i perëndimit, s’ka zot tjetër të vërtetë përveç Tij, drejtoju Atij si rregullues i punëve të tua!');
INSERT INTO "hasan_efendi" VALUES(73,10,'73:10','Duroji llomotitjet e atyre dhe largohu nga ata në mënyrë të hijshme!');
INSERT INTO "hasan_efendi" VALUES(73,11,'73:11','Lërmi Mua ata që të quajnë gënjeshtar e që jetojnë në luks dhe jepu pak afat atyre,');
INSERT INTO "hasan_efendi" VALUES(73,12,'73:12','se, pa dyshim, Ne kemi përgatitur për ata pranga, zjarr flakërues,');
INSERT INTO "hasan_efendi" VALUES(73,13,'73:13','ushqime që ngecin në fyt dhe dënime të dhembshme');
INSERT INTO "hasan_efendi" VALUES(73,14,'73:14','në Ditën, kur Toka dhe malet dridhen e bëhen si pirgje rëre të lëvizshme.');
INSERT INTO "hasan_efendi" VALUES(73,15,'73:15','Ne, me të vërtetë, ju kemi sjellë një të Dërguar dëshmitar mbi ju, ashtu siç ia çuam një të dërguar Faraonit,');
INSERT INTO "hasan_efendi" VALUES(73,16,'73:16','por Faraoni nuk e dëgjoi të Dërguarin, andaj i çuam dënim të rëndë.');
INSERT INTO "hasan_efendi" VALUES(73,17,'73:17','Qysh do të mbroheni ju, nëse mbeteni jobesimtarë, Ditën në të cilën fëmijët do të thinjen?');
INSERT INTO "hasan_efendi" VALUES(73,18,'73:18','Atë ditë qielli do të çahet e premtimi i Zotit do të përmbushet.');
INSERT INTO "hasan_efendi" VALUES(73,19,'73:19','Në të vërtetë, kjo është këshillë, prandaj, kush dëshiron, le të zgjedhë rrugën e drejtë që e çon te Zoti i tij.');
INSERT INTO "hasan_efendi" VALUES(73,20,'73:20','Natyrisht, Zoti yt e di se ti (o Muhamed) falesh më pak se dy të tretat e natës, ndonjëherë gjysmën ose një të tretën e saj, si edhe disa prej atyre që janë me ty. Allahu e përcakton gjatësinë e ditës dhe të natës dhe e di se ju nuk mund ta llogarisni atë saktësisht, prandaj kthehet tek ju me mëshirë. Kështu që lexoni Kuran aq sa mundeni! Ai e di se disa prej jush do të jenë të sëmurë, disa do të udhëtojnë nëpër botë, duke kërkuar mirësitë e Tij (për jetesë) dhe disa të tjerë do të jenë duke luftuar në rrugën e Tij, prandaj lexoni sa keni mundësi nga Kurani! Faleni namazin, jepeni zekatin dhe jepini Allahut një hua të mirë (që Ai t’jua kthejë shumëfish). Çfarëdo mirësie që bëni për shpirtin tuaj, me siguri që do ta gjeni tek Allahu. Ajo do të jetë më e mirë dhe më e shpërblyer. Kërkoni faljen e Allahut, sepse Allahu është vërtet Falës i madh dhe Mëshirëplotë.');
INSERT INTO "hasan_efendi" VALUES(74,1,'74:1','O ti i mbështjellë (o Muhamed)!');
INSERT INTO "hasan_efendi" VALUES(74,2,'74:2','Çohu dhe paralajmëro!');
INSERT INTO "hasan_efendi" VALUES(74,3,'74:3','Zotin tënd madhëroje,');
INSERT INTO "hasan_efendi" VALUES(74,4,'74:4','petkat e tua pastroji');
INSERT INTO "hasan_efendi" VALUES(74,5,'74:5','dhe largohu nga ndyrësia (adhurimi i idhujve)!');
INSERT INTO "hasan_efendi" VALUES(74,6,'74:6','Mos jep gjë me synimin që të marrësh më shumë');
INSERT INTO "hasan_efendi" VALUES(74,7,'74:7','dhe për hir të Zotit tënd duro,');
INSERT INTO "hasan_efendi" VALUES(74,8,'74:8','se, kur të fryhet në Sur,');
INSERT INTO "hasan_efendi" VALUES(74,9,'74:9','ajo do të jetë një ditë e rëndë.');
INSERT INTO "hasan_efendi" VALUES(74,10,'74:10','Për jobesimtarët nuk do të jetë aspak e lehtë.');
INSERT INTO "hasan_efendi" VALUES(74,11,'74:11','Më lër Vetëm (të merrem) me atë që e krijova');
INSERT INTO "hasan_efendi" VALUES(74,12,'74:12','dhe i dhashë pasuri të pamasë');
INSERT INTO "hasan_efendi" VALUES(74,13,'74:13','e djem pranë vetes!');
INSERT INTO "hasan_efendi" VALUES(74,14,'74:14','Unë ia bëra kamjen të lehtë e të shtruar,');
INSERT INTO "hasan_efendi" VALUES(74,15,'74:15','e, megjithatë, ai lakmon që t’i jap më tepër.');
INSERT INTO "hasan_efendi" VALUES(74,16,'74:16','Jo, kurrsesi! Vërtet, ai i mohoi me kryeneçësi shpalljet Tona,');
INSERT INTO "hasan_efendi" VALUES(74,17,'74:17','prandaj Unë do ta ngarkoj atë me dënime të rënda.');
INSERT INTO "hasan_efendi" VALUES(74,18,'74:18','Ai mendoi dhe kurdisi (kundër Kuranit),');
INSERT INTO "hasan_efendi" VALUES(74,19,'74:19','dhe qoftë mallkuar, si kurdisi!');
INSERT INTO "hasan_efendi" VALUES(74,20,'74:20','Dhe sërish qoftë mallkuar, si kurdisi!');
INSERT INTO "hasan_efendi" VALUES(74,21,'74:21','Pastaj vështroi rreth vetes,');
INSERT INTO "hasan_efendi" VALUES(74,22,'74:22','mblodhi vetullat e u vrenjt,');
INSERT INTO "hasan_efendi" VALUES(74,23,'74:23','pastaj u kthye i kapardisur');
INSERT INTO "hasan_efendi" VALUES(74,24,'74:24','dhe tha: “Ky (Kuran) nuk është gjë tjetër, veçse magji e trashëguar!');
INSERT INTO "hasan_efendi" VALUES(74,25,'74:25','Këto janë vetëm fjalë të një vdekatari!”');
INSERT INTO "hasan_efendi" VALUES(74,26,'74:26','Do ta hedh në Sekar (Xhehenem)!');
INSERT INTO "hasan_efendi" VALUES(74,27,'74:27','E si mund ta dish ti tamam se çfarë është Sekari (zjarri përvëlues)?');
INSERT INTO "hasan_efendi" VALUES(74,28,'74:28','Ai nuk lë gjë dhe nuk kursen asgjë,');
INSERT INTO "hasan_efendi" VALUES(74,29,'74:29','lëkurat i shkrumbon.');
INSERT INTO "hasan_efendi" VALUES(74,30,'74:30','Atij i rrinë roje nëntëmbëdhjetë (engjëj).');
INSERT INTO "hasan_efendi" VALUES(74,31,'74:31','Ne kemi bërë rojtarë të zjarrit vetëm engjëjt dhe numrin e tyre e kemi bërë sprovë (objekt polemikash) për jobesimtarët, me qëllim që të binden ata që u është dhënë Libri e t’u shtohet besimi atyre që besojnë; që të mos dyshojnë ata që u është dhënë Libri dhe besimtarët e vërtetë dhe që të thonë ata që kanë sëmundjen e dyshimit në zemër dhe jobesimtarët: “Ç’ka dashur të thotë Allahu me këtë shembull?” Kështu, Allahu lë në humbje atë që do dhe çon në rrugë të drejtë atë që do. Askush tjetër, përveç Tij, nuk e di numrin e ushtrisë së Zotit tënd. Ky është vetëm një përkujtesë për njerëzit.');
INSERT INTO "hasan_efendi" VALUES(74,32,'74:32','Kurrsesi! Betohem për Hënën,');
INSERT INTO "hasan_efendi" VALUES(74,33,'74:33','për natën që largohet');
INSERT INTO "hasan_efendi" VALUES(74,34,'74:34','e për agimin që zbardhëllon');
INSERT INTO "hasan_efendi" VALUES(74,35,'74:35','se ai (Xhehenemi) është vërtet një nga fatkeqësitë më të mëdha,');
INSERT INTO "hasan_efendi" VALUES(74,36,'74:36','paralajmërim për njerëzit,');
INSERT INTO "hasan_efendi" VALUES(74,37,'74:37','për ata nga ju që dëshirojnë të ecin përpara (me vepra të mira) ose të kthehen mbrapsht (me vepra të këqija).');
INSERT INTO "hasan_efendi" VALUES(74,38,'74:38','Çdo njeri është peng i veprave të veta,');
INSERT INTO "hasan_efendi" VALUES(74,39,'74:39','përveç njerëzve të krahut të djathtë;');
INSERT INTO "hasan_efendi" VALUES(74,40,'74:40','të cilët në kopshtet e Xhenetit pyesin njëri-tjetrin');
INSERT INTO "hasan_efendi" VALUES(74,41,'74:41','për keqbërësit:');
INSERT INTO "hasan_efendi" VALUES(74,42,'74:42','“Çfarë ju ka sjellë në Sekar?”');
INSERT INTO "hasan_efendi" VALUES(74,43,'74:43','Ata do të përgjigjen: “Nuk ishim nga ata që falnin namaz');
INSERT INTO "hasan_efendi" VALUES(74,44,'74:44','dhe as prej atyre që i ushqenin të varfrit.');
INSERT INTO "hasan_efendi" VALUES(74,45,'74:45','Ne lëshoheshim në biseda të kota me llafazanët');
INSERT INTO "hasan_efendi" VALUES(74,46,'74:46','dhe mohonim Ditën e Llogarisë,');
INSERT INTO "hasan_efendi" VALUES(74,47,'74:47','derisa na erdhi vdekja.”');
INSERT INTO "hasan_efendi" VALUES(74,48,'74:48','Prandaj, kurrfarë ndërmjetësimi i cilitdo ndërmjetësuesi nuk do t’i ndihmojë ata.');
INSERT INTO "hasan_efendi" VALUES(74,49,'74:49','Atëherë, përse shmangen ata nga këshillimi,');
INSERT INTO "hasan_efendi" VALUES(74,50,'74:50','sikur të ishin gomarë të egër');
INSERT INTO "hasan_efendi" VALUES(74,51,'74:51','e të frikësuar që ikin prej luanit?!');
INSERT INTO "hasan_efendi" VALUES(74,52,'74:52','Vërtet, çdonjëri prej tyre kërkon që t’i jepen shkrime të hapura (nga Zoti).');
INSERT INTO "hasan_efendi" VALUES(74,53,'74:53','Kurrsesi, pasi ata nuk i frikësohen jetës tjetër!');
INSERT INTO "hasan_efendi" VALUES(74,54,'74:54','Në të vërtetë, ky (Kuran) është këshillë,');
INSERT INTO "hasan_efendi" VALUES(74,55,'74:55','prandaj, kush të dojë, le t’ia vërë veshin!');
INSERT INTO "hasan_efendi" VALUES(74,56,'74:56','Por atij ia vënë veshin vetëm ata që do Allahu. Vetëm Ai është i Denjë për t’u pasur frikë dhe vetëm Ai është i Denjë për të falur.');
INSERT INTO "hasan_efendi" VALUES(75,1,'75:1','Betohem në Ditën e Kiametit!');
INSERT INTO "hasan_efendi" VALUES(75,2,'75:2','Betohem në shpirtin qortues!');
INSERT INTO "hasan_efendi" VALUES(75,3,'75:3','A mendon njeriu se nuk do të mund t’i mbledhim eshtrat e tij?!');
INSERT INTO "hasan_efendi" VALUES(75,4,'75:4','Po, si jo! Ne jemi të Zotë t’ia ribëjmë bashkë me përsosmëri edhe majat e gishtave të tij.');
INSERT INTO "hasan_efendi" VALUES(75,5,'75:5','Por njeriu dëshiron të gabojë sa të jetë gjallë');
INSERT INTO "hasan_efendi" VALUES(75,6,'75:6','dhe pyet: “Kur do të jetë Dita e Kiametit?”');
INSERT INTO "hasan_efendi" VALUES(75,7,'75:7','Por, kur sytë t’i verbohen');
INSERT INTO "hasan_efendi" VALUES(75,8,'75:8','dhe të zihet Hëna');
INSERT INTO "hasan_efendi" VALUES(75,9,'75:9','dhe Dielli e Hëna të bashkohen,');
INSERT INTO "hasan_efendi" VALUES(75,10,'75:10','atë ditë njeriu do të thotë: “Ku të iki?”');
INSERT INTO "hasan_efendi" VALUES(75,11,'75:11','Askund! S’ka strehë!');
INSERT INTO "hasan_efendi" VALUES(75,12,'75:12','Atë ditë, streha është te Zoti yt.');
INSERT INTO "hasan_efendi" VALUES(75,13,'75:13','Atë ditë, njeriu do të vihet në dijeni për veprat që ka bërë dhe për ato që ka lënë pa bërë.');
INSERT INTO "hasan_efendi" VALUES(75,14,'75:14','Po, vërtet që njeriu do të dëshmojë kundër vetvetes,');
INSERT INTO "hasan_efendi" VALUES(75,15,'75:15','edhe sikur të paraqesë shfajësimet e veta.');
INSERT INTO "hasan_efendi" VALUES(75,16,'75:16','Mos nxito (o Muhamed) në leximin e Kuranit me gjuhën tënde! ');
INSERT INTO "hasan_efendi" VALUES(75,17,'75:17','Ne e kemi për detyrë t’i mbledhim pjesët e tij e ta lexojmë atë.');
INSERT INTO "hasan_efendi" VALUES(75,18,'75:18','Dhe, kur ta lexojmë atë, ti përcille leximin e tij!');
INSERT INTO "hasan_efendi" VALUES(75,19,'75:19','Pastaj, e kemi Ne për detyrë ta shpjegojmë.');
INSERT INTO "hasan_efendi" VALUES(75,20,'75:20','Por ju doni këtë jetë (kalimtare)');
INSERT INTO "hasan_efendi" VALUES(75,21,'75:21','e për jetën tjetër nuk kujtoheni!');
INSERT INTO "hasan_efendi" VALUES(75,22,'75:22','Atë ditë, disa fytyra do të shkëlqejnë');
INSERT INTO "hasan_efendi" VALUES(75,23,'75:23','dhe Zotin e tyre do të shohin.');
INSERT INTO "hasan_efendi" VALUES(75,24,'75:24','Kurse disa fytyra, atë ditë, do të jenë të zymta,');
INSERT INTO "hasan_efendi" VALUES(75,25,'75:25','duke e ditur se do të pësojnë një fatkeqësi, që të thyen në mes.');
INSERT INTO "hasan_efendi" VALUES(75,26,'75:26','Por, kur shpirti të vijë në grykë');
INSERT INTO "hasan_efendi" VALUES(75,27,'75:27','e të thërrasin “A ka shërues”,');
INSERT INTO "hasan_efendi" VALUES(75,28,'75:28','atëherë, ai (që po vdes) do të bindet se ky është çasti i ndarjes');
INSERT INTO "hasan_efendi" VALUES(75,29,'75:29','dhe këmbët do të ngrijnë .');
INSERT INTO "hasan_efendi" VALUES(75,30,'75:30','Atë ditë, ai do të sillet te Zoti yt.');
INSERT INTO "hasan_efendi" VALUES(75,31,'75:31','Ai as nuk besoi dhe as nuk u fal,');
INSERT INTO "hasan_efendi" VALUES(75,32,'75:32','por e mohoi të vërtetën dhe ia ktheu kurrizin,');
INSERT INTO "hasan_efendi" VALUES(75,33,'75:33','e pastaj shkoi te njerëzit e vet, duke u krenuar.');
INSERT INTO "hasan_efendi" VALUES(75,34,'75:34','Mjerë për ty! Mjerë për ty!');
INSERT INTO "hasan_efendi" VALUES(75,35,'75:35','Sërish: Mjerë për ty! Mjerë për ty!');
INSERT INTO "hasan_efendi" VALUES(75,36,'75:36','Vallë, a mendon njeriu se do të mbetet pa përgjegjshmëri?');
INSERT INTO "hasan_efendi" VALUES(75,37,'75:37','A s’ka qenë një pikë fare që derdhej,');
INSERT INTO "hasan_efendi" VALUES(75,38,'75:38','pastaj u bë droçkë gjaku e më pas Allahu e krijoi dhe i dha formë e përmasa të caktuara,');
INSERT INTO "hasan_efendi" VALUES(75,39,'75:39','e prej tij bëri çiftin: mashkullin e femrën?!');
INSERT INTO "hasan_efendi" VALUES(75,40,'75:40','Vallë, Ai Krijues, a nuk ka fuqi që t’i ngjallë të vdekurit?!');
INSERT INTO "hasan_efendi" VALUES(76,1,'76:1','Vallë, a nuk ka kaluar një periudhë kohore, kur njeriu nuk ka qenë qenie e denjë për t’u përmendur?! ');
INSERT INTO "hasan_efendi" VALUES(76,2,'76:2','Në të vërtetë, Ne e krijuam njeriun nga një pikë fare e përzier, për ta sprovuar dhe e bëmë që të dëgjojë dhe të shohë.');
INSERT INTO "hasan_efendi" VALUES(76,3,'76:3','Natyrisht që Ne i treguam atij rrugën e drejtë, që ai të jetë ose mirënjohës, ose mosmirënjohës.');
INSERT INTO "hasan_efendi" VALUES(76,4,'76:4','Pa dyshim, Ne kemi përgatitur për jobesimtarët zinxhirë, pranga dhe zjarr flakërues.');
INSERT INTO "hasan_efendi" VALUES(76,5,'76:5','Sigurisht, të ndershmit besimtarë do të pinë (në Xhenet) kupa me pije të përzier me Kafur, ');
INSERT INTO "hasan_efendi" VALUES(76,6,'76:6','nga burimi që vetëm robtë e Allahut pinë dhe të cilin mund ta çojnë nga të duan.');
INSERT INTO "hasan_efendi" VALUES(76,7,'76:7','Ata e zbatojnë premtimin dhe i frikësohen Ditës (së Kiametit), tmerri i së cilës përhapet gjithkund.');
INSERT INTO "hasan_efendi" VALUES(76,8,'76:8','Ata i ushqejnë të varfrit, jetimët dhe të zënët robër edhe pse vetë janë nevojtarë,');
INSERT INTO "hasan_efendi" VALUES(76,9,'76:9','duke thënë: “Ne ju ushqejmë vetëm për hir të Zotit. Për këtë, nuk duam shpërblim e as falënderim!');
INSERT INTO "hasan_efendi" VALUES(76,10,'76:10','Ne i frikësohemi Zotit tonë në një Ditë të ashpër, që do t’i bëjë fytyrat të jenë të vrenjtura.”');
INSERT INTO "hasan_efendi" VALUES(76,11,'76:11','Por Allahu do t’i ruajë ata nga tmerret e asaj Dite e do t’u japë shkëlqim dhe gëzim');
INSERT INTO "hasan_efendi" VALUES(76,12,'76:12','dhe, për durimin e tyre, Ai do t’i shpërblejë me Xhenet dhe petka të mëndafshta.');
INSERT INTO "hasan_efendi" VALUES(76,13,'76:13','Të mbështetur në divane, ata nuk do të kenë as vapë dhe as të ftohtë.');
INSERT INTO "hasan_efendi" VALUES(76,14,'76:14','Hija e pemëve të tij (Xhenetit) është afër mbi kokat e tyre, ndërsa frutat e tyre do t’i kenë afër sa t’i arrijnë me dorë.');
INSERT INTO "hasan_efendi" VALUES(76,15,'76:15','Aty do t’u shërbehet me enë të argjendta dhe kupa kristali,');
INSERT INTO "hasan_efendi" VALUES(76,16,'76:16','kupa të tejdukshme argjendi, madhësinë e të cilave (do ta zgjedhin) sipas dëshirës.');
INSERT INTO "hasan_efendi" VALUES(76,17,'76:17','Aty do të pinë gota me pije të përzier me xhenxhefil,');
INSERT INTO "hasan_efendi" VALUES(76,18,'76:18','prej një burimi të quajtur Selsebil.');
INSERT INTO "hasan_efendi" VALUES(76,19,'76:19','Atyre do t’u shërbejnë djem përherë të rinj, që, kur i shikon, të duken si margaritarë të shpërndarë.');
INSERT INTO "hasan_efendi" VALUES(76,20,'76:20','Ngado që të hedhësh sytë, do të shohësh lumni dhe një mbretëri të madhe.');
INSERT INTO "hasan_efendi" VALUES(76,21,'76:21','Ata do të jenë të veshur me petka të holla mëndafshi të gjelbër dhe me armaç mëndafshi të trashë, të stolisur me byzylykë të argjendtë. Zoti i tyre do t’u japë të pinë pije të pastër.');
INSERT INTO "hasan_efendi" VALUES(76,22,'76:22','Ky do të jetë me të vërtetë shpërblimi juaj; përpjekjet tuaja do të jenë të pranuara.');
INSERT INTO "hasan_efendi" VALUES(76,23,'76:23','Në të vërtetë, Ne ta kemi shpallur Kuranin ty hap pas hapi.');
INSERT INTO "hasan_efendi" VALUES(76,24,'76:24','Prandaj ki durim për vendimin e Zotit tënd e mos dëgjo asnjë gjynahqar apo jobesimtar prej tyre!');
INSERT INTO "hasan_efendi" VALUES(76,25,'76:25','Përmende emrin e Zotit tënd në mëngjes dhe në mbrëmje.');
INSERT INTO "hasan_efendi" VALUES(76,26,'76:26','Në një pjesë të natës bëji sexhde Atij dhe, natën falju gjatë Atij.');
INSERT INTO "hasan_efendi" VALUES(76,27,'76:27','Vërtet që jobesimtarët e duan jetën e këtushme dhe nuk e përfillin një Ditë të vështirë, që do t’u vijë.');
INSERT INTO "hasan_efendi" VALUES(76,28,'76:28','Ne i krijuam ata dhe ua forcuam strukturën, por, nëse duam, i ndërrojmë plotësisht me të tjerë si ata.');
INSERT INTO "hasan_efendi" VALUES(76,29,'76:29','Në të vërtetë, kjo është këshillë, prandaj, kush të dojë, le të ndjekë rrugën që e shpie te Zoti i tij.');
INSERT INTO "hasan_efendi" VALUES(76,30,'76:30','Por ju nuk mund të dëshironi, përveçse me vullnetin e Allahut! Ai është vërtet i Gjithëdijshëm e i Urtë.');
INSERT INTO "hasan_efendi" VALUES(76,31,'76:31','Ai pranon në mëshirën e Vet kë të dojë, ndërsa për keqbërësit ka përgatitur një dënim të dhembshëm.');
INSERT INTO "hasan_efendi" VALUES(77,1,'77:1','Për ato që dërgohen njëra pas tjetrës, ');
INSERT INTO "hasan_efendi" VALUES(77,2,'77:2','për ato që shpejtojnë shtrëngatën,');
INSERT INTO "hasan_efendi" VALUES(77,3,'77:3','për ato që shpërndajnë shiun,');
INSERT INTO "hasan_efendi" VALUES(77,4,'77:4','për ato që ndajnë saktësisht');
INSERT INTO "hasan_efendi" VALUES(77,5,'77:5','dhe për ato që përcjellin këshilla,');
INSERT INTO "hasan_efendi" VALUES(77,6,'77:6','si shfajësim apo paralajmërim!');
INSERT INTO "hasan_efendi" VALUES(77,7,'77:7','Në të vërtetë, ajo që ju është premtuar, do të bëhet!');
INSERT INTO "hasan_efendi" VALUES(77,8,'77:8','Kur yjet të shuhen');
INSERT INTO "hasan_efendi" VALUES(77,9,'77:9','dhe, kur qielli të çahet');
INSERT INTO "hasan_efendi" VALUES(77,10,'77:10','dhe, kur malet të thërrmohen si pluhur');
INSERT INTO "hasan_efendi" VALUES(77,11,'77:11','dhe, kur të dërguarit të tubohen në kohën e caktuar');
INSERT INTO "hasan_efendi" VALUES(77,12,'77:12','- në cilën ditë do të ndodhin të gjitha këto?');
INSERT INTO "hasan_efendi" VALUES(77,13,'77:13','Në Ditën e Ndarjes vendimtare!');
INSERT INTO "hasan_efendi" VALUES(77,14,'77:14','Kush vallë do të ta shpjegojë se ç’është Dita e Ndarjes vendimtare?');
INSERT INTO "hasan_efendi" VALUES(77,15,'77:15','Mjerë mohuesit në atë ditë!');
INSERT INTO "hasan_efendi" VALUES(77,16,'77:16','A nuk i kemi zhdukur të lashtët');
INSERT INTO "hasan_efendi" VALUES(77,17,'77:17','e pastaj do t’i bëjmë të tjerët t’i pasojnë?');
INSERT INTO "hasan_efendi" VALUES(77,18,'77:18','Kështu do të veprojmë me të gjithë keqbërësit!');
INSERT INTO "hasan_efendi" VALUES(77,19,'77:19','Mjerë mohuesit në atë ditë!');
INSERT INTO "hasan_efendi" VALUES(77,20,'77:20','A nuk ju kemi krijuar nga një pikë lëngu të shpërfillur,');
INSERT INTO "hasan_efendi" VALUES(77,21,'77:21','të cilën e vumë në një vend të sigurt (mitër),');
INSERT INTO "hasan_efendi" VALUES(77,22,'77:22','deri në një kohë të caktuar?!');
INSERT INTO "hasan_efendi" VALUES(77,23,'77:23','Kështu e kemi rregulluar; sa Rregullues të shkëlqyer jemi Ne!');
INSERT INTO "hasan_efendi" VALUES(77,24,'77:24','Mjerë mohuesit në atë ditë!');
INSERT INTO "hasan_efendi" VALUES(77,25,'77:25','A nuk e kemi bërë Tokën vendqëndrim');
INSERT INTO "hasan_efendi" VALUES(77,26,'77:26','të të gjallëve dhe të të vdekurve?');
INSERT INTO "hasan_efendi" VALUES(77,27,'77:27','A nuk kemi vënë në të male të larta e të patundshme? A nuk ju kemi dhënë të pini me bollëk ujë të ëmbël?');
INSERT INTO "hasan_efendi" VALUES(77,28,'77:28','Mjerë mohuesit në atë ditë!');
INSERT INTO "hasan_efendi" VALUES(77,29,'77:29','(Do t’u thuhet): Nisuni drejt atij (dënimi) që e mohuat!');
INSERT INTO "hasan_efendi" VALUES(77,30,'77:30','Shkoni drejt një hijeje me tri kolona (tymi të Xhehenemit),');
INSERT INTO "hasan_efendi" VALUES(77,31,'77:31','e cila nuk bën hije (freskuese) dhe as nga flaka nuk të shpëton!');
INSERT INTO "hasan_efendi" VALUES(77,32,'77:32','Ajo hedh shkëndija të mëdha sa një kala,');
INSERT INTO "hasan_efendi" VALUES(77,33,'77:33','sikur të ishin deve të verdha!');
INSERT INTO "hasan_efendi" VALUES(77,34,'77:34','Mjerë mohuesit në atë ditë!');
INSERT INTO "hasan_efendi" VALUES(77,35,'77:35','Kjo është një ditë, kur ata nuk do të mund të flasin');
INSERT INTO "hasan_efendi" VALUES(77,36,'77:36','dhe nuk do t’u jepet leje për t’u shfajësuar.');
INSERT INTO "hasan_efendi" VALUES(77,37,'77:37','Mjerë mohuesit në atë ditë!');
INSERT INTO "hasan_efendi" VALUES(77,38,'77:38','Kjo është Dita e Ndarjes vendimtare, në të cilën do të tuboheni ju dhe popujt e lashtë,');
INSERT INTO "hasan_efendi" VALUES(77,39,'77:39','prandaj, nëse zotëroni ndonjë dinakëri, përdoreni kundër Meje!');
INSERT INTO "hasan_efendi" VALUES(77,40,'77:40','Mjerë mohuesit në atë ditë!');
INSERT INTO "hasan_efendi" VALUES(77,41,'77:41','Punëdrejtët do të jenë mes hijesh e burimesh,');
INSERT INTO "hasan_efendi" VALUES(77,42,'77:42','si dhe frutash nga ato që dëshirojnë.');
INSERT INTO "hasan_efendi" VALUES(77,43,'77:43','(Do t’u thuhet) “Hani e pini e ju bëftë mirë, (si shpërblim) për atë që keni punuar.”');
INSERT INTO "hasan_efendi" VALUES(77,44,'77:44','Kështu i shpërblejmë Ne ata, që bëjnë vepra të mira.');
INSERT INTO "hasan_efendi" VALUES(77,45,'77:45','Mjerë mohuesit në atë ditë!');
INSERT INTO "hasan_efendi" VALUES(77,46,'77:46','Hani dhe kënaquni për pak kohë! Ju jeni keqbërës!');
INSERT INTO "hasan_efendi" VALUES(77,47,'77:47','Mjerë mohuesit në atë ditë!');
INSERT INTO "hasan_efendi" VALUES(77,48,'77:48','Kur atyre u thuhej: “Përkuluni (në namaz para Allahut)!” ata nuk përkuleshin.');
INSERT INTO "hasan_efendi" VALUES(77,49,'77:49','Mjerë mohuesit në atë ditë!');
INSERT INTO "hasan_efendi" VALUES(77,50,'77:50','Në çfarë shpallje tjetër do të besojnë ata, pas këtij (Kurani)?!');
INSERT INTO "hasan_efendi" VALUES(78,1,'78:1','Për çfarë e pyesin ata njëri-tjetrin?');
INSERT INTO "hasan_efendi" VALUES(78,2,'78:2','Për Lajmin e madh,');
INSERT INTO "hasan_efendi" VALUES(78,3,'78:3','rreth të cilit ata kanë kundërshtime.');
INSERT INTO "hasan_efendi" VALUES(78,4,'78:4','Ani, shpejt do ta marrin vesh!');
INSERT INTO "hasan_efendi" VALUES(78,5,'78:5','Sigurisht që shpejt do ta marrin vesh!');
INSERT INTO "hasan_efendi" VALUES(78,6,'78:6','A nuk jua kemi bërë Tokën si shtrat');
INSERT INTO "hasan_efendi" VALUES(78,7,'78:7','e malet si shtylla?!');
INSERT INTO "hasan_efendi" VALUES(78,8,'78:8','Ne ju kemi krijuar në çifte');
INSERT INTO "hasan_efendi" VALUES(78,9,'78:9','dhe gjumin jua kemi bërë për prehje.');
INSERT INTO "hasan_efendi" VALUES(78,10,'78:10','Natën jua kemi bërë mbulesë,');
INSERT INTO "hasan_efendi" VALUES(78,11,'78:11','ndërsa ditën e kemi caktuar për të punuar.');
INSERT INTO "hasan_efendi" VALUES(78,12,'78:12','Mbi ju kemi ndërtuar shtatë qiej të fortë');
INSERT INTO "hasan_efendi" VALUES(78,13,'78:13','dhe kemi vënë kandil (diell) të shndritshëm!');
INSERT INTO "hasan_efendi" VALUES(78,14,'78:14','Nga retë Ne ju lëshojmë ujëra të bollshme');
INSERT INTO "hasan_efendi" VALUES(78,15,'78:15','për rritjen e drithërave, bimëve');
INSERT INTO "hasan_efendi" VALUES(78,16,'78:16','dhe të kopshteve të dendura.');
INSERT INTO "hasan_efendi" VALUES(78,17,'78:17','Sigurisht, Dita e Ndarjes vendimtare është caktuar.');
INSERT INTO "hasan_efendi" VALUES(78,18,'78:18','Atë Ditë do të fryhet në Sur dhe ju do të vini grupe-grupe.');
INSERT INTO "hasan_efendi" VALUES(78,19,'78:19','Dyert e qiellit do të hapen');
INSERT INTO "hasan_efendi" VALUES(78,20,'78:20','dhe malet do të shtyhen tutje e do të bëhen si mirazh.');
INSERT INTO "hasan_efendi" VALUES(78,21,'78:21','Me të vërtetë, Xhehenemi do të bëhet pusi,');
INSERT INTO "hasan_efendi" VALUES(78,22,'78:22','vendbanim i caktuar për keqbërësit,');
INSERT INTO "hasan_efendi" VALUES(78,23,'78:23','që aty të mbeten përgjithmonë.');
INSERT INTO "hasan_efendi" VALUES(78,24,'78:24','Aty nuk do të shijojnë as freski, as pije,');
INSERT INTO "hasan_efendi" VALUES(78,25,'78:25','përpos ujit të valuar dhe qelbësirave,');
INSERT INTO "hasan_efendi" VALUES(78,26,'78:26','si shpërblim përkatës (të veprave të tyre).');
INSERT INTO "hasan_efendi" VALUES(78,27,'78:27','Në të vërtetë, ata as që e çonin nëpër mend se do të jepnin llogari');
INSERT INTO "hasan_efendi" VALUES(78,28,'78:28','dhe shpalljet Tona i mohonin prerazi.');
INSERT INTO "hasan_efendi" VALUES(78,29,'78:29','Por Ne çdo gjë e kemi shënuar në një libër.');
INSERT INTO "hasan_efendi" VALUES(78,30,'78:30','Prandaj do t’u themi: “Shijojeni këtë! Ne nuk do t’ju shtojmë gjë tjetër përveçse dënim!”');
INSERT INTO "hasan_efendi" VALUES(78,31,'78:31','Vërtet, për punëdrejtët është përgatitur e mira (Xheneti):');
INSERT INTO "hasan_efendi" VALUES(78,32,'78:32','kopshte dhe vreshta,');
INSERT INTO "hasan_efendi" VALUES(78,33,'78:33','vajza të reja, të së njëjtës moshë,');
INSERT INTO "hasan_efendi" VALUES(78,34,'78:34','dhe kupa të plota.');
INSERT INTO "hasan_efendi" VALUES(78,35,'78:35','Atje nuk do të dëgjojnë biseda të kota e as gënjeshtra.');
INSERT INTO "hasan_efendi" VALUES(78,36,'78:36','Kështu është shpërblimi nga Zoti yt - dhuratë sipas meritës');
INSERT INTO "hasan_efendi" VALUES(78,37,'78:37','Zoti i qiejve dhe i Tokës dhe i gjithçkaje që gjendet midis tyre, i Gjithëmëshirshmi, të Cilit askush nuk do të mund t’i flasë (nga frika).');
INSERT INTO "hasan_efendi" VALUES(78,38,'78:38','Në ditën, kur Shpirti (Xhebraili) dhe engjëjt do të jenë të rreshtuar, nuk do të flasë askush, përveç atij që do t’i japë leje i Gjithëmëshirshmi e që do të thotë të vërtetën.');
INSERT INTO "hasan_efendi" VALUES(78,39,'78:39','Ajo është Dita e Vërtetë, prandaj kush të dojë, le të kthehet te Zoti i vet (që në këtë jetë).');
INSERT INTO "hasan_efendi" VALUES(78,40,'78:40','Me të vërtetë, Ne ju kemi paralajmëruar se është i afërt dënimi, Ditën, kur njeriu do të shohë se ç’kanë bërë duart e tij dhe, kur jobesimtari do të thotë: “Ah, sikur të isha pluhur!”');
INSERT INTO "hasan_efendi" VALUES(79,1,'79:1','Për ata (engjëj) që marrin me forcë, ');
INSERT INTO "hasan_efendi" VALUES(79,2,'79:2','për ata që marrin butësisht, ');
INSERT INTO "hasan_efendi" VALUES(79,3,'79:3','për ata që notojnë në hapësirë,');
INSERT INTO "hasan_efendi" VALUES(79,4,'79:4','për ata që nxitojnë, ');
INSERT INTO "hasan_efendi" VALUES(79,5,'79:5','dhe për ata që i rregullojnë punët! ');
INSERT INTO "hasan_efendi" VALUES(79,6,'79:6','Ditën kur do të dridhet gjithçka nga ushtima (që lajmëron Kiametin),');
INSERT INTO "hasan_efendi" VALUES(79,7,'79:7','e cila do ta ndiqet nga një e dytë,');
INSERT INTO "hasan_efendi" VALUES(79,8,'79:8','disa zemra do të jenë të mbushura me tmerr');
INSERT INTO "hasan_efendi" VALUES(79,9,'79:9','e shikimet e tyre do të bien përdhe.');
INSERT INTO "hasan_efendi" VALUES(79,10,'79:10','Ata thonë: “Vallë, vërtet do të rikthehemi në jetë?!');
INSERT INTO "hasan_efendi" VALUES(79,11,'79:11','A thua, edhe kur të bëhemi eshtra të thërrmuara?!”');
INSERT INTO "hasan_efendi" VALUES(79,12,'79:12','Dhe thonë: “Po qe kështu, atëherë ky kthim do të jetë humbje (për ne)!”');
INSERT INTO "hasan_efendi" VALUES(79,13,'79:13','Por, ajo do të jetë veç një fryrje e vetme (e Surit)');
INSERT INTO "hasan_efendi" VALUES(79,14,'79:14','dhe ata përnjëherësh do të brofin (të ringjallur).');
INSERT INTO "hasan_efendi" VALUES(79,15,'79:15','A e ke dëgjuar (o Muhamed) historinë e Musait?');
INSERT INTO "hasan_efendi" VALUES(79,16,'79:16','Zoti i tij e thirri në luginën e shenjtë të Tuvas dhe i tha:');
INSERT INTO "hasan_efendi" VALUES(79,17,'79:17','“Shko te Faraoni, se ai, me të vërtetë, ka kaluar çdo kufi;');
INSERT INTO "hasan_efendi" VALUES(79,18,'79:18','dhe thuaji: “A do ta pastrosh veten,');
INSERT INTO "hasan_efendi" VALUES(79,19,'79:19','dhe të të udhëzoj te Zoti yt, që t’i frikësohesh?”');
INSERT INTO "hasan_efendi" VALUES(79,20,'79:20','Musai ia tregoi mrekullinë më të madhe,');
INSERT INTO "hasan_efendi" VALUES(79,21,'79:21','por ai (Faraoni) e mohoi dhe e kundërshtoi.');
INSERT INTO "hasan_efendi" VALUES(79,22,'79:22','Pastaj i ktheu shpinën, duke u përpjekur (kundër së vërtetës)');
INSERT INTO "hasan_efendi" VALUES(79,23,'79:23','dhe tuboi njerëzit e u deklaroi:');
INSERT INTO "hasan_efendi" VALUES(79,24,'79:24','“Unë jam zoti juaj më i lartë!”');
INSERT INTO "hasan_efendi" VALUES(79,25,'79:25','Prandaj Allahu e goditi atë me dënim në jetën tjetër dhe në këtë jetë.');
INSERT INTO "hasan_efendi" VALUES(79,26,'79:26','Kjo është këshillë për ata që i frikësohen Allahut.');
INSERT INTO "hasan_efendi" VALUES(79,27,'79:27','Cili është më i vështirë: krijimi i juaj apo i qiellit, të cilin Ai e ndërtoi,');
INSERT INTO "hasan_efendi" VALUES(79,28,'79:28','ia ngriti lart kupën dhe e përsosi.');
INSERT INTO "hasan_efendi" VALUES(79,29,'79:29','Netët i bëri të errëta, kurse ditët të ndritshme.');
INSERT INTO "hasan_efendi" VALUES(79,30,'79:30','Pastaj e shtriu rrafsh Tokën,');
INSERT INTO "hasan_efendi" VALUES(79,31,'79:31','dhe, nga ajo nxori ujërat dhe bëri kullotat,');
INSERT INTO "hasan_efendi" VALUES(79,32,'79:32','kurse malet i bëri të patundshme,');
INSERT INTO "hasan_efendi" VALUES(79,33,'79:33','që t’i gëzoni ju dhe bagëtitë tuaja.');
INSERT INTO "hasan_efendi" VALUES(79,34,'79:34','Kur të arrijë gjëma më e madhe,');
INSERT INTO "hasan_efendi" VALUES(79,35,'79:35','atë Ditë njeriu do të kujtojë gjithçka që ka bërë');
INSERT INTO "hasan_efendi" VALUES(79,36,'79:36','dhe Zjarri flakërues do t’i tregohet çdokujt që sheh.');
INSERT INTO "hasan_efendi" VALUES(79,37,'79:37','Atëherë, ai që ka shkelur gjithçka');
INSERT INTO "hasan_efendi" VALUES(79,38,'79:38','dhe ka parapëlqyer jetën e kësaj bote,');
INSERT INTO "hasan_efendi" VALUES(79,39,'79:39','sigurisht që do të ketë për strehë Xhehenemin.');
INSERT INTO "hasan_efendi" VALUES(79,40,'79:40','Kurse ai që i është frikësuar madhërisë së Zotit të tij dhe e ka frenuar veten nga dëshirat,');
INSERT INTO "hasan_efendi" VALUES(79,41,'79:41','pa dyshim që do të ketë për strehë Xhenetin.');
INSERT INTO "hasan_efendi" VALUES(79,42,'79:42','Të pyesin ty për Orën (e Kiametit) se kur do të ndodhë.');
INSERT INTO "hasan_efendi" VALUES(79,43,'79:43','Çfarë rëndësie ka për ty që ta përmendësh atë?');
INSERT INTO "hasan_efendi" VALUES(79,44,'79:44','Tek Zoti yt është fundi i (dijenisë rreth) saj.');
INSERT INTO "hasan_efendi" VALUES(79,45,'79:45','Ti je vetëm paralajmërues i atyre që i frikësohen asaj.');
INSERT INTO "hasan_efendi" VALUES(79,46,'79:46','Ditën kur do ta shohin atë, (njerëzve) do t’u duket se kanë qëndruar (në Tokë) veç një mbrëmje apo një mëngjes.');
INSERT INTO "hasan_efendi" VALUES(80,1,'80:1','Ai u vrenjt dhe ktheu kurrizin,');
INSERT INTO "hasan_efendi" VALUES(80,2,'80:2','kur i erdhi pranë i verbri.');
INSERT INTO "hasan_efendi" VALUES(80,3,'80:3','E ku e di ti, ndoshta ai dëshironte të pastrohej (nga gjynahet)');
INSERT INTO "hasan_efendi" VALUES(80,4,'80:4','ose të merrte këshillë, e këshilla t’i sillte dobi atij?!');
INSERT INTO "hasan_efendi" VALUES(80,5,'80:5','Sa për atë që është i pasur,');
INSERT INTO "hasan_efendi" VALUES(80,6,'80:6','ti atij po i kushton vëmendje,');
INSERT INTO "hasan_efendi" VALUES(80,7,'80:7','megjithëse nuk ke përgjegjësi, nëse ai nuk dëshiron të pastrohet.');
INSERT INTO "hasan_efendi" VALUES(80,8,'80:8','Sa për atë që të vjen duke nxituar');
INSERT INTO "hasan_efendi" VALUES(80,9,'80:9','dhe ndien frikë (nga Allahu),');
INSERT INTO "hasan_efendi" VALUES(80,10,'80:10','ti atij nuk po ia vë veshin.');
INSERT INTO "hasan_efendi" VALUES(80,11,'80:11','Mos vepro ashtu! Në të vërtetë, ky (Kuran) është këshillë,');
INSERT INTO "hasan_efendi" VALUES(80,12,'80:12','pra, kush të dojë, le të marrë këshillë!');
INSERT INTO "hasan_efendi" VALUES(80,13,'80:13','Ai ndodhet në Faqe të nderuara, ');
INSERT INTO "hasan_efendi" VALUES(80,14,'80:14','të lartësuara, të pastra,');
INSERT INTO "hasan_efendi" VALUES(80,15,'80:15','në duart e shkruesve (engjëj)');
INSERT INTO "hasan_efendi" VALUES(80,16,'80:16','të ndershëm dhe të drejtë.');
INSERT INTO "hasan_efendi" VALUES(80,17,'80:17','Mallkuar qoftë njeriu! Sa mosmirënjohës që është ai!');
INSERT INTO "hasan_efendi" VALUES(80,18,'80:18','Prej çfarë gjëje e krijon Allahu atë?');
INSERT INTO "hasan_efendi" VALUES(80,19,'80:19','Prej pikës së farës e krijon dhe i jep përmasa të rregullta.');
INSERT INTO "hasan_efendi" VALUES(80,20,'80:20','Më pas ia lehtëson rrugën (e lindjes)');
INSERT INTO "hasan_efendi" VALUES(80,21,'80:21','e pastaj e vdes dhe i cakton një varr,');
INSERT INTO "hasan_efendi" VALUES(80,22,'80:22','dhe, kur të dojë, Ai e ringjall.');
INSERT INTO "hasan_efendi" VALUES(80,23,'80:23','E megjithatë, njeriu ende nuk i ka plotësuar ato që i ka urdhëruar Ai!');
INSERT INTO "hasan_efendi" VALUES(80,24,'80:24','Le të mendojë njeriu për ushqimin e vet:');
INSERT INTO "hasan_efendi" VALUES(80,25,'80:25','Jemi Ne që lëshojmë ujë me bollëk,');
INSERT INTO "hasan_efendi" VALUES(80,26,'80:26','pastaj e çajmë tokën në thellësi');
INSERT INTO "hasan_efendi" VALUES(80,27,'80:27','dhe nxjerrim prej saj drithërat,');
INSERT INTO "hasan_efendi" VALUES(80,28,'80:28','rrushin e perimet,');
INSERT INTO "hasan_efendi" VALUES(80,29,'80:29','ullirin e palmat,');
INSERT INTO "hasan_efendi" VALUES(80,30,'80:30','kopshtet e dendura,');
INSERT INTO "hasan_efendi" VALUES(80,31,'80:31','pemët dhe gjithë barërat,');
INSERT INTO "hasan_efendi" VALUES(80,32,'80:32','që t’i gëzoni ju dhe bagëtitë tuaja.');
INSERT INTO "hasan_efendi" VALUES(80,33,'80:33','Por, kur të vijë Ushtima,');
INSERT INTO "hasan_efendi" VALUES(80,34,'80:34','atë Ditë njeriu do të ikë nga i vëllai,');
INSERT INTO "hasan_efendi" VALUES(80,35,'80:35','nga e ëma dhe nga i ati,');
INSERT INTO "hasan_efendi" VALUES(80,36,'80:36','nga e shoqja dhe nga fëmijët e vet.');
INSERT INTO "hasan_efendi" VALUES(80,37,'80:37','Atë Ditë, secili prej tyre do të ketë aq shqetësim për veten, sa do t’i mjaftojë.');
INSERT INTO "hasan_efendi" VALUES(80,38,'80:38','Atë Ditë do të ketë fytyra rrëzëllore,');
INSERT INTO "hasan_efendi" VALUES(80,39,'80:39','të qeshura e të gëzuara.');
INSERT INTO "hasan_efendi" VALUES(80,40,'80:40','Atë Ditë do të ketë (edhe) fytyra të pluhurosura,');
INSERT INTO "hasan_efendi" VALUES(80,41,'80:41','që do t’i mbulojë errësira.');
INSERT INTO "hasan_efendi" VALUES(80,42,'80:42','Këta janë jobesimtarët, gjynahqarët.');
INSERT INTO "hasan_efendi" VALUES(81,1,'81:1','Kur Dielli të mblidhet e të humbë shkëlqimin;');
INSERT INTO "hasan_efendi" VALUES(81,2,'81:2','kur yjet të rrëzohen');
INSERT INTO "hasan_efendi" VALUES(81,3,'81:3','dhe malet të lëvizin tutje;');
INSERT INTO "hasan_efendi" VALUES(81,4,'81:4','kur devetë barrse të braktisen');
INSERT INTO "hasan_efendi" VALUES(81,5,'81:5','dhe egërsirat të tubohen;');
INSERT INTO "hasan_efendi" VALUES(81,6,'81:6','kur detet të vlojnë');
INSERT INTO "hasan_efendi" VALUES(81,7,'81:7','dhe shpirtrat të bashkohen (me trupat);');
INSERT INTO "hasan_efendi" VALUES(81,8,'81:8','kur foshnja e varrosur për së gjalli të pyetet');
INSERT INTO "hasan_efendi" VALUES(81,9,'81:9','për çfarë faji ishte vrarë;');
INSERT INTO "hasan_efendi" VALUES(81,10,'81:10','kur fletët të shpërndahen ');
INSERT INTO "hasan_efendi" VALUES(81,11,'81:11','dhe qielli të zhvishet;');
INSERT INTO "hasan_efendi" VALUES(81,12,'81:12','kur Xhehenemi të flakërojë');
INSERT INTO "hasan_efendi" VALUES(81,13,'81:13','dhe Xheneti të afrohet:');
INSERT INTO "hasan_efendi" VALUES(81,14,'81:14','atëherë çdokush do të marrë vesh çfarë kishte përgatitur (për këtë Ditë).');
INSERT INTO "hasan_efendi" VALUES(81,15,'81:15','Betohem për ata (planetë) që tërhiqen,');
INSERT INTO "hasan_efendi" VALUES(81,16,'81:16','që lindin e perëndojnë,');
INSERT INTO "hasan_efendi" VALUES(81,17,'81:17','për natën kur bie terri');
INSERT INTO "hasan_efendi" VALUES(81,18,'81:18','e për agimin kur nis të zbardhë,');
INSERT INTO "hasan_efendi" VALUES(81,19,'81:19','se ky (Kuran) është Fjalë e (kumtuar prej) një të Dërguari fisnik, ');
INSERT INTO "hasan_efendi" VALUES(81,20,'81:20','që zotëron fuqi dhe pozitë të lartë te Zoti i Fronit');
INSERT INTO "hasan_efendi" VALUES(81,21,'81:21','e që është i respektuar atje dhe besnik.');
INSERT INTO "hasan_efendi" VALUES(81,22,'81:22','Shoku juaj nuk është i marrë.');
INSERT INTO "hasan_efendi" VALUES(81,23,'81:23','Ai e ka parë atë në horizontin e qartë');
INSERT INTO "hasan_efendi" VALUES(81,24,'81:24','dhe nuk mbulon asnjë të fshehtë prej së Padukshmes.');
INSERT INTO "hasan_efendi" VALUES(81,25,'81:25','As ky (Kuran) nuk është fjalë e djallit të mallkuar.');
INSERT INTO "hasan_efendi" VALUES(81,26,'81:26','Prandaj, nga po ia mbani?');
INSERT INTO "hasan_efendi" VALUES(81,27,'81:27','Ai është veçse këshillë për botët:');
INSERT INTO "hasan_efendi" VALUES(81,28,'81:28','për secilin nga ju që dëshiron të ndjekë rrugën e drejtë.');
INSERT INTO "hasan_efendi" VALUES(81,29,'81:29','Por ju nuk mund të dëshironi, përveçse me vullnetin e Allahut, Zotit të botëve!');
INSERT INTO "hasan_efendi" VALUES(82,1,'82:1','Kur qielli të çahet');
INSERT INTO "hasan_efendi" VALUES(82,2,'82:2','dhe yjet të derdhen;');
INSERT INTO "hasan_efendi" VALUES(82,3,'82:3','kur detet të përzihen');
INSERT INTO "hasan_efendi" VALUES(82,4,'82:4','dhe varret të rrotullohen,');
INSERT INTO "hasan_efendi" VALUES(82,5,'82:5','atëherë çdokush do të mësojë për veprat që ka bërë dhe që ka lënë pa bërë.');
INSERT INTO "hasan_efendi" VALUES(82,6,'82:6','O njeri, ç’të mashtroi ty që nuk i besove Zotit tënd bujar,');
INSERT INTO "hasan_efendi" VALUES(82,7,'82:7','i Cili të krijoi ty, të dha përmasa të rregullta dhe të harmonizoi');
INSERT INTO "hasan_efendi" VALUES(82,8,'82:8','dhe, që të ka mbrujtur në çfarëdo forme që ka dashur?!');
INSERT INTO "hasan_efendi" VALUES(82,9,'82:9','Por jo! Ju e quani të gënjeshtërt Gjykimin (pas ringjalljes),');
INSERT INTO "hasan_efendi" VALUES(82,10,'82:10','ndonëse mbi ju ka rojtarë (që ju vëzhgojnë),');
INSERT INTO "hasan_efendi" VALUES(82,11,'82:11','shënues të nderuar,');
INSERT INTO "hasan_efendi" VALUES(82,12,'82:12','që dinë gjithçka që bëni.');
INSERT INTO "hasan_efendi" VALUES(82,13,'82:13','Vërtet, punëdrejtët do të jenë në Xhenet,');
INSERT INTO "hasan_efendi" VALUES(82,14,'82:14','kurse fajtorët, me siguri, do të jenë në Zjarrin flakërues,');
INSERT INTO "hasan_efendi" VALUES(82,15,'82:15','në të cilin do të digjen në Ditën e Gjykimit,');
INSERT INTO "hasan_efendi" VALUES(82,16,'82:16','pa mundur t’i shmangen atij.');
INSERT INTO "hasan_efendi" VALUES(82,17,'82:17','E kush do të ta shpjegojë ty se ç’është Dita e Gjykimit?');
INSERT INTO "hasan_efendi" VALUES(82,18,'82:18','Edhe njëherë: Kush do të ta shpjegojë ty se ç’është Dita e Gjykimit?');
INSERT INTO "hasan_efendi" VALUES(82,19,'82:19','Ajo është Dita, kur askush nuk do të ketë fuqi për të bërë gjë për ndonjë tjetër dhe, kur pushteti do të jetë vetëm i Allahut.');
INSERT INTO "hasan_efendi" VALUES(83,1,'83:1','Mjerë për ata që hanë në peshë (dhe matje),');
INSERT INTO "hasan_efendi" VALUES(83,2,'83:2','që, kur masin për veten, e marrin masën të plotë,');
INSERT INTO "hasan_efendi" VALUES(83,3,'83:3','ndërsa, kur u masin të tjerëve me litër ose me kandar, ua pakësojnë.');
INSERT INTO "hasan_efendi" VALUES(83,4,'83:4','A nuk mendojnë ata se do të ringjallen');
INSERT INTO "hasan_efendi" VALUES(83,5,'83:5','në një Ditë të madhe,');
INSERT INTO "hasan_efendi" VALUES(83,6,'83:6','Ditën, kur të gjithë njerëzit do të dalin para Zotit të botëve?!');
INSERT INTO "hasan_efendi" VALUES(83,7,'83:7','Sigurisht (që do të ringjallen), dhe Libri i punëmbrapshtëve do të jetë në Sixh-xhîn!');
INSERT INTO "hasan_efendi" VALUES(83,8,'83:8','E kush do të ta shpjegojë ty se ç’është Sixh-xhîn?!');
INSERT INTO "hasan_efendi" VALUES(83,9,'83:9','Është një libër i shkruar!');
INSERT INTO "hasan_efendi" VALUES(83,10,'83:10','Mjerë mohuesit atë ditë,');
INSERT INTO "hasan_efendi" VALUES(83,11,'83:11','të cilët mohuan Ditën e Gjykimit!');
INSERT INTO "hasan_efendi" VALUES(83,12,'83:12','Askush nuk e mohon atë, përveç gjynahqarëve që shkelin çdo kufi');
INSERT INTO "hasan_efendi" VALUES(83,13,'83:13','e të cilët, kur u lexohen shpalljet Tona, thonë: “Këto janë përralla të popujve të lashtë!”');
INSERT INTO "hasan_efendi" VALUES(83,14,'83:14','Nuk është ashtu! Por veprat e tyre (të këqija) ua kanë mbuluar zemrat.');
INSERT INTO "hasan_efendi" VALUES(83,15,'83:15','Nuk është ashtu! Në të vërtetë, atë ditë një vel do t’i pengojë ata që të shohin Zotin e tyre,');
INSERT INTO "hasan_efendi" VALUES(83,16,'83:16','pastaj, me siguri, do të hyjnë në Zjarrin e Xhehenemit');
INSERT INTO "hasan_efendi" VALUES(83,17,'83:17','e do t’u thuhet: “Ja, kjo është ajo që e quanit të gënjeshtërt!”');
INSERT INTO "hasan_efendi" VALUES(83,18,'83:18','Ndërsa Libri i punëdrejtëve me siguri që do të jetë në Il-lijûn.');
INSERT INTO "hasan_efendi" VALUES(83,19,'83:19','E kush do të ta shpjegojë ty se ç’është Il-lijjûn?');
INSERT INTO "hasan_efendi" VALUES(83,20,'83:20','Është një libër i shkruar!');
INSERT INTO "hasan_efendi" VALUES(83,21,'83:21','Atë e dëshmojnë të afërmit (e Allahut).');
INSERT INTO "hasan_efendi" VALUES(83,22,'83:22','Me të vërtetë, punëdrejtët do të gjenden në Xhenet,');
INSERT INTO "hasan_efendi" VALUES(83,23,'83:23','duke shikuar nga divanet.');
INSERT INTO "hasan_efendi" VALUES(83,24,'83:24','Në fytyrat e tyre do të njohësh shkëlqimin e Lumturisë.');
INSERT INTO "hasan_efendi" VALUES(83,25,'83:25','Atyre do t’u jepet të pinë musht të pastër të ruajtur mirë,');
INSERT INTO "hasan_efendi" VALUES(83,26,'83:26','fundi i të cilit është myshku. Për këtë, le të përpiqen ata që përpiqen.');
INSERT INTO "hasan_efendi" VALUES(83,27,'83:27','Dhe ai (musht) do të përzihet me ujin e Tesnimit,');
INSERT INTO "hasan_efendi" VALUES(83,28,'83:28','nga burimi i të cilit do të pinë më të afërmit (e Allahut).');
INSERT INTO "hasan_efendi" VALUES(83,29,'83:29','Vërtet, keqbërësit qeshnin me ata që besonin,');
INSERT INTO "hasan_efendi" VALUES(83,30,'83:30','duke ia shkelur syrin njëri-tjetrit, sa herë që ata kalonin pranë tyre.');
INSERT INTO "hasan_efendi" VALUES(83,31,'83:31','Kur ktheheshin te njerëzit e vet, ata talleshin (me besimtarët)');
INSERT INTO "hasan_efendi" VALUES(83,32,'83:32','dhe, kur i shihnin ata, thoshin: “Këta janë të humbur”,');
INSERT INTO "hasan_efendi" VALUES(83,33,'83:33','ndonëse nuk qenë dërguar për kujdestarë të tyre.');
INSERT INTO "hasan_efendi" VALUES(83,34,'83:34','Por sot, ata që kanë besuar, do t’i përqeshin jobesimtarët,');
INSERT INTO "hasan_efendi" VALUES(83,35,'83:35','e nga divanet e ngritura do t’i shikojnë.');
INSERT INTO "hasan_efendi" VALUES(83,36,'83:36','A janë shpërblyer meritueshëm mohuesit për atë që kanë bërë?!');
INSERT INTO "hasan_efendi" VALUES(84,1,'84:1','Kur qielli të çahet,');
INSERT INTO "hasan_efendi" VALUES(84,2,'84:2','duke dëgjuar me bindje Zotin e vet, siç e ka për detyrë;');
INSERT INTO "hasan_efendi" VALUES(84,3,'84:3','kur Toka të sheshohet (nga rrënimi i maleve)');
INSERT INTO "hasan_efendi" VALUES(84,4,'84:4','dhe të flakë atë që ka brenda e të zbrazet,');
INSERT INTO "hasan_efendi" VALUES(84,5,'84:5','duke dëgjuar me bindje Zotin e vet, siç e ka për detyrë,');
INSERT INTO "hasan_efendi" VALUES(84,6,'84:6','atëherë ti, o njeri, që përpiqesh shumë, mundin tënd do ta gjesh te Zoti yt.');
INSERT INTO "hasan_efendi" VALUES(84,7,'84:7','Ai, të cilit do t’i jepet libri i tij në dorën e djathtë,');
INSERT INTO "hasan_efendi" VALUES(84,8,'84:8','do ta ketë të lehtë llogarinë');
INSERT INTO "hasan_efendi" VALUES(84,9,'84:9','dhe do të kthehet i gëzuar te të afërmit e tij.');
INSERT INTO "hasan_efendi" VALUES(84,10,'84:10','Kurse ai, të cilit do t’i jepet libri i tij pas shpinës,');
INSERT INTO "hasan_efendi" VALUES(84,11,'84:11','do të lutet për të vdekur');
INSERT INTO "hasan_efendi" VALUES(84,12,'84:12','dhe në zjarr do të digjet,');
INSERT INTO "hasan_efendi" VALUES(84,13,'84:13','sepse ai jetonte i shkujdesur në familjen e tij,');
INSERT INTO "hasan_efendi" VALUES(84,14,'84:14','duke menduar se kurrë nuk do të kthehej (për të dhënë llogari).');
INSERT INTO "hasan_efendi" VALUES(84,15,'84:15','Po, Zoti i tij, sigurisht që i ka ndjekur të gjitha punët e tij!');
INSERT INTO "hasan_efendi" VALUES(84,16,'84:16','Betohem në kuqërrimin e muzgut,');
INSERT INTO "hasan_efendi" VALUES(84,17,'84:17','në natën e në gjithçka që ajo kaplon me errësirën e saj,');
INSERT INTO "hasan_efendi" VALUES(84,18,'84:18','si dhe në hënën e plotë,');
INSERT INTO "hasan_efendi" VALUES(84,19,'84:19','se ju, me siguri, do të kaloni nga e keqja te më e keqja!');
INSERT INTO "hasan_efendi" VALUES(84,20,'84:20','Çfarë kanë ata që nuk duan të besojnë?!');
INSERT INTO "hasan_efendi" VALUES(84,21,'84:21','E përse, kur u lexohet Kurani, nuk përulen në sexhde?!');
INSERT INTO "hasan_efendi" VALUES(84,22,'84:22','Përkundrazi, ata që nuk besojnë e quajnë atë gënjeshtër.');
INSERT INTO "hasan_efendi" VALUES(84,23,'84:23','Por Allahu e di mirë çfarë fshehin ata,');
INSERT INTO "hasan_efendi" VALUES(84,24,'84:24','prandaj shpallu atyre dënimin e dhembshëm (që i pret të gjithë),');
INSERT INTO "hasan_efendi" VALUES(84,25,'84:25','përveç atyre, që besojnë dhe bëjnë vepra të mira; ata do të shpërblehen pandërprerë.');
INSERT INTO "hasan_efendi" VALUES(85,1,'85:1','Për qiellin me yjësi,');
INSERT INTO "hasan_efendi" VALUES(85,2,'85:2','për Ditën e premtuar ,');
INSERT INTO "hasan_efendi" VALUES(85,3,'85:3','për dëshmitarin dhe për atë që dëshmohet!');
INSERT INTO "hasan_efendi" VALUES(85,4,'85:4','Mallkuar qofshin njerëzit e Hendekut, ');
INSERT INTO "hasan_efendi" VALUES(85,5,'85:5','zjarrit (të ushqyer) me lëndë djegëse,');
INSERT INTO "hasan_efendi" VALUES(85,6,'85:6','kur rrinin ulur rreth tij,');
INSERT INTO "hasan_efendi" VALUES(85,7,'85:7','duke dëshmuar atë që po i bënin besimtarëve!');
INSERT INTO "hasan_efendi" VALUES(85,8,'85:8','Ata i munduan besimtarët, vetëm pse këta i besuan Allahut, të Plotfuqishmit, të Denjit për çdo lavd,');
INSERT INTO "hasan_efendi" VALUES(85,9,'85:9','të Cilit i përket pushteti i qiejve dhe i Tokës dhe që është Dëshmitar i çdo gjëje.');
INSERT INTO "hasan_efendi" VALUES(85,10,'85:10','Pa dyshim, ata që i përndjekin besimtarët dhe besimtaret e nuk pendohen, i pret dënimi i Xhehenemit dhe dënimi me djegie.');
INSERT INTO "hasan_efendi" VALUES(85,11,'85:11','Kurse për ata, që besojnë dhe bëjnë vepra të mira, do të ketë kopshte, nëpër të cilat rrjedhin lumenj. Kjo është fitorja madhështore.');
INSERT INTO "hasan_efendi" VALUES(85,12,'85:12','Vërtet, ndëshkimi i Zotit tënd është i ashpër!');
INSERT INTO "hasan_efendi" VALUES(85,13,'85:13','Vërtet, Ai e zë fill krijimin dhe e përsërit atë.');
INSERT INTO "hasan_efendi" VALUES(85,14,'85:14','Ai është Falësi i madh, i Dhembshuri,');
INSERT INTO "hasan_efendi" VALUES(85,15,'85:15','Zotëruesi i Fronit të Lavdishëm,');
INSERT INTO "hasan_efendi" VALUES(85,16,'85:16','Vepruesi i gjithçkaje që dëshiron.');
INSERT INTO "hasan_efendi" VALUES(85,17,'85:17','A e ke dëgjuar ndodhinë e ushtrive');
INSERT INTO "hasan_efendi" VALUES(85,18,'85:18','të Faraonit dhe të fisit Themud?');
INSERT INTO "hasan_efendi" VALUES(85,19,'85:19','E megjithatë, ata që nuk besojnë, vazhdojnë ta quajnë atë (Kuranin) të rremë,');
INSERT INTO "hasan_efendi" VALUES(85,20,'85:20','ndonëse Allahu është pas tyre e i ka nën kontroll.');
INSERT INTO "hasan_efendi" VALUES(85,21,'85:21','Përkundrazi, ai është një Kuran i lavdishëm,');
INSERT INTO "hasan_efendi" VALUES(85,22,'85:22','në Pllakën e Ruajtur mirë (Leuhi Mahfûdh).');
INSERT INTO "hasan_efendi" VALUES(86,1,'86:1','Për qiellin dhe për vizitorin e natës!');
INSERT INTO "hasan_efendi" VALUES(86,2,'86:2','E kush mund të ta shpjegojë ty se ç’është vizitori i natës?');
INSERT INTO "hasan_efendi" VALUES(86,3,'86:3','Ai është ylli që shpon terrin.');
INSERT INTO "hasan_efendi" VALUES(86,4,'86:4','Për çdo njeri ka mbikëqyrës.');
INSERT INTO "hasan_efendi" VALUES(86,5,'86:5','Le të meditojë njeriu se prej çfarë është krijuar:');
INSERT INTO "hasan_efendi" VALUES(86,6,'86:6','është krijuar prej një lëngu që hidhet');
INSERT INTO "hasan_efendi" VALUES(86,7,'86:7','e që del ndërmjet shtyllës kurrizore dhe brinjëve.');
INSERT INTO "hasan_efendi" VALUES(86,8,'86:8','Me të vërtetë, Ai është i Fuqishëm për ta kthyer (në jetë) atë');
INSERT INTO "hasan_efendi" VALUES(86,9,'86:9','ditën, kur do të zbulohen të fshehtat,');
INSERT INTO "hasan_efendi" VALUES(86,10,'86:10','kur njeriu nuk do të ketë as fuqi, as ndihmës.');
INSERT INTO "hasan_efendi" VALUES(86,11,'86:11','Për qiellin që kthehet pareshtur ');
INSERT INTO "hasan_efendi" VALUES(86,12,'86:12','dhe për Tokën që plasaritet, ');
INSERT INTO "hasan_efendi" VALUES(86,13,'86:13','që kjo është një Fjalë ndarëse, ');
INSERT INTO "hasan_efendi" VALUES(86,14,'86:14','dhe nuk është aspak shaka!');
INSERT INTO "hasan_efendi" VALUES(86,15,'86:15','Në të vërtetë, ata ngrenë kurthe,');
INSERT INTO "hasan_efendi" VALUES(86,16,'86:16','por dhe Unë ua pres me kurth.');
INSERT INTO "hasan_efendi" VALUES(86,17,'86:17','Prandaj, jepu pak afat jobesimtarëve, lëri të qetë edhe për pak.');
INSERT INTO "hasan_efendi" VALUES(87,1,'87:1','Lavdëro emrin e Zotit tënd, më të Lartit,');
INSERT INTO "hasan_efendi" VALUES(87,2,'87:2','që ka krijuar gjithçka dhe e ka përpjesëtuar drejt;');
INSERT INTO "hasan_efendi" VALUES(87,3,'87:3','që ka caktuar fatin e çdo gjëje dhe ia ka rrëfyer rrugën;');
INSERT INTO "hasan_efendi" VALUES(87,4,'87:4','që bën të rriten kullotat');
INSERT INTO "hasan_efendi" VALUES(87,5,'87:5','dhe pastaj të shndërrohen, të thahen e të vyshken.');
INSERT INTO "hasan_efendi" VALUES(87,6,'87:6','Ne do të të bëjmë ty (Muhamed) të lexosh (Shpalljet Tona), kështu që nuk ke për të harruar,');
INSERT INTO "hasan_efendi" VALUES(87,7,'87:7','përveç asaj që do Allahu. Vërtet, Ai e di të dukshmen dhe çka është e fshehtë.');
INSERT INTO "hasan_efendi" VALUES(87,8,'87:8','Do të ta lehtësojmë ty rrugën e drejtë,');
INSERT INTO "hasan_efendi" VALUES(87,9,'87:9','prandaj këshillo, nëse këshilla do të jetë e dobishme!');
INSERT INTO "hasan_efendi" VALUES(87,10,'87:10','Asaj do t’ia vërë veshin ai që (i) frikësohet (Allahut),');
INSERT INTO "hasan_efendi" VALUES(87,11,'87:11','e do t’i shmanget vetëm më i keqi,');
INSERT INTO "hasan_efendi" VALUES(87,12,'87:12','i cili do të digjet në zjarrin e madh,');
INSERT INTO "hasan_efendi" VALUES(87,13,'87:13','ku as nuk do të vdesë, as nuk do të jetojë.');
INSERT INTO "hasan_efendi" VALUES(87,14,'87:14','I shpëtuar është ai që pastrohet,');
INSERT INTO "hasan_efendi" VALUES(87,15,'87:15','që e përmend emrin e Zotit të vet dhe që fal namaz!');
INSERT INTO "hasan_efendi" VALUES(87,16,'87:16','Por ju, përkundrazi, më shumë po e parapëlqeni jetën e kësaj bote,');
INSERT INTO "hasan_efendi" VALUES(87,17,'87:17','ndonëse jeta tjetër është më e mirë dhe e amshuar.');
INSERT INTO "hasan_efendi" VALUES(87,18,'87:18','Kjo, me të vërtetë, ndodhet në Fletët e lashta,');
INSERT INTO "hasan_efendi" VALUES(87,19,'87:19','në Fletët e Ibrahimit dhe Musait.');
INSERT INTO "hasan_efendi" VALUES(88,1,'88:1','A ke dëgjuar për Ngjarjen Gjithëpërfshirëse (Kiametin)?');
INSERT INTO "hasan_efendi" VALUES(88,2,'88:2','Atë Ditë do të poshtërohen fytyra njerëzish,');
INSERT INTO "hasan_efendi" VALUES(88,3,'88:3','që kanë punuar e janë rraskapitur (duke bërë të këqija në këtë botë).');
INSERT INTO "hasan_efendi" VALUES(88,4,'88:4','Ata do të digjen në zjarr të nxehtë');
INSERT INTO "hasan_efendi" VALUES(88,5,'88:5','e do të pinë nga burime të valuara.');
INSERT INTO "hasan_efendi" VALUES(88,6,'88:6','Për ata nuk do të ketë tjetër ushqim, përveç ferrave helmuese,');
INSERT INTO "hasan_efendi" VALUES(88,7,'88:7','të cilat as nuk do t’i mbajnë, as nuk do t’ua shuajnë urinë.');
INSERT INTO "hasan_efendi" VALUES(88,8,'88:8','Atë Ditë, fytyrat e disave do të jenë të gëzuara,');
INSERT INTO "hasan_efendi" VALUES(88,9,'88:9','të kënaqura për punët e bëra.');
INSERT INTO "hasan_efendi" VALUES(88,10,'88:10','Ata do të jenë në kopshtin e lartë,');
INSERT INTO "hasan_efendi" VALUES(88,11,'88:11','në të cilin nuk do të dëgjojnë fjalë boshe.');
INSERT INTO "hasan_efendi" VALUES(88,12,'88:12','Atje ka një burim rrjedhës uji');
INSERT INTO "hasan_efendi" VALUES(88,13,'88:13','dhe divane të ngritura,');
INSERT INTO "hasan_efendi" VALUES(88,14,'88:14','ka kupa të vëna (për t’u mbajtur në dorë)');
INSERT INTO "hasan_efendi" VALUES(88,15,'88:15','dhe jastëkë të renditur,');
INSERT INTO "hasan_efendi" VALUES(88,16,'88:16','si dhe shumë qilima të shtruar.');
INSERT INTO "hasan_efendi" VALUES(88,17,'88:17','Vallë, a nuk i shohin devetë, se si janë krijuar?!');
INSERT INTO "hasan_efendi" VALUES(88,18,'88:18','Po qiellin, se si është ngritur?!');
INSERT INTO "hasan_efendi" VALUES(88,19,'88:19','Po malet, se si janë vënë të patundur?!');
INSERT INTO "hasan_efendi" VALUES(88,20,'88:20','Po Tokën, se si është shtrirë?!');
INSERT INTO "hasan_efendi" VALUES(88,21,'88:21','Prandaj këshilloji (njerëzit o Muhamed), se ti je vetëm këshillues');
INSERT INTO "hasan_efendi" VALUES(88,22,'88:22','dhe nuk je ngarkuar t’i detyrosh ata me forcë!');
INSERT INTO "hasan_efendi" VALUES(88,23,'88:23','Sa për ata që shmangen dhe nuk besojnë,');
INSERT INTO "hasan_efendi" VALUES(88,24,'88:24','Allahu do t’i ndëshkojë me dënimin më të madh.');
INSERT INTO "hasan_efendi" VALUES(88,25,'88:25','Pa dyshim, ata do të kthehen të gjithë tek Ne');
INSERT INTO "hasan_efendi" VALUES(88,26,'88:26','dhe patjetër që do të japin llogari para Nesh!');
INSERT INTO "hasan_efendi" VALUES(89,1,'89:1','Betohem për muzgun e agimit');
INSERT INTO "hasan_efendi" VALUES(89,2,'89:2','dhe për dhjetë netët (e para të muajit Dhul Hixhxhe)!');
INSERT INTO "hasan_efendi" VALUES(89,3,'89:3','Betohem për çiftin dhe tekun');
INSERT INTO "hasan_efendi" VALUES(89,4,'89:4','dhe për natën kur largohet!');
INSERT INTO "hasan_efendi" VALUES(89,5,'89:5','A nuk janë këto betime të mëdha për njerëzit e mençur?!');
INSERT INTO "hasan_efendi" VALUES(89,6,'89:6','Vallë, a nuk e di ti se çfarë ka bërë Zoti yt me fisin Ad,');
INSERT INTO "hasan_efendi" VALUES(89,7,'89:7','banorë të Iremit me pallate shumështyllëshe,');
INSERT INTO "hasan_efendi" VALUES(89,8,'89:8','të atillë që nuk janë krijuar askund tjetër në tokë si ata?!');
INSERT INTO "hasan_efendi" VALUES(89,9,'89:9','Dhe me fisin Themud, që gdhendi shkëmbinjtë në luginë (për të bërë shtëpi)?');
INSERT INTO "hasan_efendi" VALUES(89,10,'89:10','Dhe me Faraonin, njeriun e hunjve (ku torturonte njerëzit)?');
INSERT INTO "hasan_efendi" VALUES(89,11,'89:11','Të gjithë këta shkelën çdo kufi në vendet e tyre,');
INSERT INTO "hasan_efendi" VALUES(89,12,'89:12','duke shtuar atje shthurjen dhe korrupsionin,');
INSERT INTO "hasan_efendi" VALUES(89,13,'89:13','prandaj Zoti yt lëshoi mbi ata kamxhikun e dënimit.');
INSERT INTO "hasan_efendi" VALUES(89,14,'89:14','Vërtet, Zoti yt është gjithnjë në pritë!');
INSERT INTO "hasan_efendi" VALUES(89,15,'89:15','Për sa i përket njeriut, kur Zoti i tij e sprovon atë, duke e ngritur lart dhe duke i dhënë mirësi, ai thotë: “Zoti im më ka nderuar.”');
INSERT INTO "hasan_efendi" VALUES(89,16,'89:16','Por, kur e sprovon atë, duke ia ngushtuar mjetet e jetesës, ai thotë: “Zoti im më ka poshtëruar”.');
INSERT INTO "hasan_efendi" VALUES(89,17,'89:17','Nuk është kështu! Por ju nuk silleni mirë me jetimët');
INSERT INTO "hasan_efendi" VALUES(89,18,'89:18','dhe nuk e nxitni njëri-tjetrin për t’i ushqyer të varfrit.');
INSERT INTO "hasan_efendi" VALUES(89,19,'89:19','Trashëgiminë (e të dobëtëve) e zhvatni me lakmi të pangopur,');
INSERT INTO "hasan_efendi" VALUES(89,20,'89:20','dhe e doni pasurinë me një dëshirë të pakufishme.');
INSERT INTO "hasan_efendi" VALUES(89,21,'89:21','Por ani! Kur Toka të bëhet copë e thërrime,');
INSERT INTO "hasan_efendi" VALUES(89,22,'89:22','kur të vijë Zoti yt (për të ndarë gjykimin) me engjëjt të rreshtuar radhë pas radhe,');
INSERT INTO "hasan_efendi" VALUES(89,23,'89:23','Dhe, kur të sillet Xhehenemi, atë Ditë njeriu do të kujtohet por ç’i duhet se u kujtua!');
INSERT INTO "hasan_efendi" VALUES(89,24,'89:24','Ai do të thotë: “Ah, sikur të kisha bërë diçka të mirë për jetën time!”');
INSERT INTO "hasan_efendi" VALUES(89,25,'89:25','Atë Ditë, askush nuk do të dënojë siç dënon Ai,');
INSERT INTO "hasan_efendi" VALUES(89,26,'89:26','e askush nuk do të lidhë në pranga siç lidh Ai!');
INSERT INTO "hasan_efendi" VALUES(89,27,'89:27','O ti shpirt i qetësuar!');
INSERT INTO "hasan_efendi" VALUES(89,28,'89:28','Kthehu te Zoti yt, ti i kënaqur me Atë dhe Ai kënaqur me ty!');
INSERT INTO "hasan_efendi" VALUES(89,29,'89:29','Bashkohu me robërit e Mi');
INSERT INTO "hasan_efendi" VALUES(89,30,'89:30','dhe hyr në Xhenetin Tim!');
INSERT INTO "hasan_efendi" VALUES(90,1,'90:1','Betohem për këtë vend, ');
INSERT INTO "hasan_efendi" VALUES(90,2,'90:2','në të cilin ti je i lirë (o Muhamed), ');
INSERT INTO "hasan_efendi" VALUES(90,3,'90:3','dhe për atë që lind e për atë që është i lindur,');
INSERT INTO "hasan_efendi" VALUES(90,4,'90:4','se, në të vërtetë, Ne e kemi krijuar njeriun për të luftuar në vështirësi! ');
INSERT INTO "hasan_efendi" VALUES(90,5,'90:5','A mendon ai se askush nuk ka pushtet mbi atë?');
INSERT INTO "hasan_efendi" VALUES(90,6,'90:6','Ai thotë: “Kam shpenzuar pasuri të pamasë!”');
INSERT INTO "hasan_efendi" VALUES(90,7,'90:7','Vallë, a mendon ai se nuk e ka parë askush?');
INSERT INTO "hasan_efendi" VALUES(90,8,'90:8','A nuk i kemi dhënë Ne atij dy sy,');
INSERT INTO "hasan_efendi" VALUES(90,9,'90:9','edhe gjuhë, edhe dy buzë,');
INSERT INTO "hasan_efendi" VALUES(90,10,'90:10','dhe ia kemi bërë të qarta të dyja rrugët? ');
INSERT INTO "hasan_efendi" VALUES(90,11,'90:11','Atëherë, le të ndjekë rrugën e vështirë!');
INSERT INTO "hasan_efendi" VALUES(90,12,'90:12','E kush mund të ta shpjegojë ty se ç’është rruga e vështirë?');
INSERT INTO "hasan_efendi" VALUES(90,13,'90:13','Ajo është të lirosh një skllav');
INSERT INTO "hasan_efendi" VALUES(90,14,'90:14','ose të ushqesh në një ditë zie');
INSERT INTO "hasan_efendi" VALUES(90,15,'90:15','jetimin e afërm');
INSERT INTO "hasan_efendi" VALUES(90,16,'90:16','ose të varfrin e mjerë.');
INSERT INTO "hasan_efendi" VALUES(90,17,'90:17','Veç kësaj, (njeriu duhet) të jetë nga ata që besojnë, që e këshillojnë njëri-tjetrin për durim dhe e këshillojnë për mëshirë.');
INSERT INTO "hasan_efendi" VALUES(90,18,'90:18','Këta janë njerëzit e krahut të djathtë.');
INSERT INTO "hasan_efendi" VALUES(90,19,'90:19','Ndërsa ata që i mohojnë shpalljet Tona, ata janë njerëzit e krahut të majtë.');
INSERT INTO "hasan_efendi" VALUES(90,20,'90:20','Mbi ata do të jetë zjarri i mbyllur.');
INSERT INTO "hasan_efendi" VALUES(91,1,'91:1','Për Diellin dhe shkëlqimin e tij');
INSERT INTO "hasan_efendi" VALUES(91,2,'91:2','dhe për Hënën që e përcjell atë!');
INSERT INTO "hasan_efendi" VALUES(91,3,'91:3','Për ditën që ia zbulon shkëlqimin atij (diellit)');
INSERT INTO "hasan_efendi" VALUES(91,4,'91:4','dhe për natën kur e mbulon atë!');
INSERT INTO "hasan_efendi" VALUES(91,5,'91:5','Për qiellin dhe për Atë që e ka ndërtuar!');
INSERT INTO "hasan_efendi" VALUES(91,6,'91:6','Për Tokën dhe për Atë që e ka shtruar!');
INSERT INTO "hasan_efendi" VALUES(91,7,'91:7','Për shpirtin dhe për Atë që e ka përsosur,');
INSERT INTO "hasan_efendi" VALUES(91,8,'91:8','duke ia bërë të njohur atij të keqen e të mirën!');
INSERT INTO "hasan_efendi" VALUES(91,9,'91:9','Vërtet, kushdo që e pastron shpirtin, do të shpëtojë,');
INSERT INTO "hasan_efendi" VALUES(91,10,'91:10','ndërsa kushdo që e shtyp atë (me punë të këqija), do të dështojë.');
INSERT INTO "hasan_efendi" VALUES(91,11,'91:11','Fisi i Themudit, nga shfrenimi i vet - përgënjeshtroi;');
INSERT INTO "hasan_efendi" VALUES(91,12,'91:12','kur u ngrit më i keqi i tij (për të therur devenë).');
INSERT INTO "hasan_efendi" VALUES(91,13,'91:13','I dërguari i Allahut (Salihu), u tha atyre : “Kjo është deveja e Allahut! Lëreni atë të pijë pjesën e vet!”');
INSERT INTO "hasan_efendi" VALUES(91,14,'91:14','Por ata nuk i besuan atij dhe e therën atë. Prandaj Zoti i tyre i shkatërroi të gjithë për gjynahet e tyre, nga i pari tek i fundit, ');
INSERT INTO "hasan_efendi" VALUES(91,15,'91:15','pa pasur frikë prej pasojave të kësaj.');
INSERT INTO "hasan_efendi" VALUES(92,1,'92:1','Për natën që mbulon (me terr),');
INSERT INTO "hasan_efendi" VALUES(92,2,'92:2','për ditën që agon');
INSERT INTO "hasan_efendi" VALUES(92,3,'92:3','dhe për Atë që ka krijuar mashkullin dhe femrën!');
INSERT INTO "hasan_efendi" VALUES(92,4,'92:4','Vërtet, veprat tuaja janë të ndryshme.');
INSERT INTO "hasan_efendi" VALUES(92,5,'92:5','Sa për atë që jep (për hir të Allahut), ka frikë (nga Ai)');
INSERT INTO "hasan_efendi" VALUES(92,6,'92:6','dhe dëshmon për vërtetësinë e më të bukurës (besimit),');
INSERT INTO "hasan_efendi" VALUES(92,7,'92:7','Ne do t’ia lehtësojmë atij rrugën drejt shpëtimit.');
INSERT INTO "hasan_efendi" VALUES(92,8,'92:8','Kurse atij që është koprrac, ndihet i vetëmjaftueshëm,');
INSERT INTO "hasan_efendi" VALUES(92,9,'92:9','dhe e quan gënjeshtër më të bukurën (besimin),');
INSERT INTO "hasan_efendi" VALUES(92,10,'92:10','Ne do t’ia lehtësojmë rrugën drejt dënimit,');
INSERT INTO "hasan_efendi" VALUES(92,11,'92:11','e, kur të hidhet (në zjarr), pasuria e tij nuk do t’i bëjë dobi.');
INSERT INTO "hasan_efendi" VALUES(92,12,'92:12','Neve na përket që t’ua tregojmë rrugën e drejtë,');
INSERT INTO "hasan_efendi" VALUES(92,13,'92:13','dhe, me të vërtetë, e Jona është ajo botë dhe kjo botë!');
INSERT INTO "hasan_efendi" VALUES(92,14,'92:14','Prandaj Unë ju kam tërhequr vërejtjen me një zjarr që flakëron,');
INSERT INTO "hasan_efendi" VALUES(92,15,'92:15','ku do të hidhet vetëm më i keqi,');
INSERT INTO "hasan_efendi" VALUES(92,16,'92:16','që ka mohuar dhe është larguar nga besimi.');
INSERT INTO "hasan_efendi" VALUES(92,17,'92:17','E do të mbahet larg tij (zjarrit) më i devotshmi (që ruhet nga gjynahet),');
INSERT INTO "hasan_efendi" VALUES(92,18,'92:18','që e jep pasurinë e tij, duke pastruar veten');
INSERT INTO "hasan_efendi" VALUES(92,19,'92:19','dhe duke mos pritur shpërblim prej dikujt tjetër për këtë,');
INSERT INTO "hasan_efendi" VALUES(92,20,'92:20','por vetëm për të kërkuar Fytyrën (kënaqësinë) e Zotit të tij, më të Lartit.');
INSERT INTO "hasan_efendi" VALUES(92,21,'92:21','Dhe ai, me siguri, do të jetë i kënaqur!');
INSERT INTO "hasan_efendi" VALUES(93,1,'93:1','Betohem në shkëlqimin e paradites');
INSERT INTO "hasan_efendi" VALUES(93,2,'93:2','dhe në natën kur erret e qetësohet,');
INSERT INTO "hasan_efendi" VALUES(93,3,'93:3','se Zoti yt nuk të ka braktisur e as nuk të urren (o Muhamed)! ');
INSERT INTO "hasan_efendi" VALUES(93,4,'93:4','Natyrisht, jeta e ardhshme për ty është më e mirë se kjo jetë,');
INSERT INTO "hasan_efendi" VALUES(93,5,'93:5','e, sigurisht që Zoti yt do të të japë ty, sa të jesh i kënaqur.');
INSERT INTO "hasan_efendi" VALUES(93,6,'93:6','A nuk ishe jetim e Ai të dha strehë?');
INSERT INTO "hasan_efendi" VALUES(93,7,'93:7','A nuk të gjeti të paditur (për Islamin) e të udhëzoi?');
INSERT INTO "hasan_efendi" VALUES(93,8,'93:8','A nuk ishe nevojtar e të bëri të mos ia kesh nevojën askujt?');
INSERT INTO "hasan_efendi" VALUES(93,9,'93:9','Prandaj, mos e shtyp jetimin,');
INSERT INTO "hasan_efendi" VALUES(93,10,'93:10','lypësin mos e shtyj me ashpërsi');
INSERT INTO "hasan_efendi" VALUES(93,11,'93:11','dhe shpalli dhuntitë e Zotit tënd! ');
INSERT INTO "hasan_efendi" VALUES(94,1,'94:1','Vallë, a nuk ta hapëm ty (Muhamed) kraharorin (për të pranuar besimin)');
INSERT INTO "hasan_efendi" VALUES(94,2,'94:2','dhe ta hoqëm barrën ,');
INSERT INTO "hasan_efendi" VALUES(94,3,'94:3','e cila ta rëndonte tepër kurrizin,');
INSERT INTO "hasan_efendi" VALUES(94,4,'94:4','dhe ta ngritëm lart emrin?! ');
INSERT INTO "hasan_efendi" VALUES(94,5,'94:5','Me të vërtetë, pas vështirësisë - vjen lehtësimi!');
INSERT INTO "hasan_efendi" VALUES(94,6,'94:6','Me të vërtetë, pas vështirësisë - vjen lehtësimi!');
INSERT INTO "hasan_efendi" VALUES(94,7,'94:7','Prandaj, kur të çlirohesh (nga punët e ndryshme), përpiqu fort (në adhurim)');
INSERT INTO "hasan_efendi" VALUES(94,8,'94:8','dhe vetëm ndaj Zotit tënd përkushtohu!');
INSERT INTO "hasan_efendi" VALUES(95,1,'95:1','Për fikun dhe ullirin,');
INSERT INTO "hasan_efendi" VALUES(95,2,'95:2','për malin e Sinait');
INSERT INTO "hasan_efendi" VALUES(95,3,'95:3','dhe për këtë Qytet të sigurt (Mekën) ,');
INSERT INTO "hasan_efendi" VALUES(95,4,'95:4','Në të vërtetë, Ne e krijuam njeriun në trajtën më të përkryer,');
INSERT INTO "hasan_efendi" VALUES(95,5,'95:5','pastaj e çuam atë (kur nuk beson) në gjendjen më të ulët,');
INSERT INTO "hasan_efendi" VALUES(95,6,'95:6','përveç atyre që besojnë dhe bëjnë vepra të mira: ata do të kenë shpërblime të pandërprera.');
INSERT INTO "hasan_efendi" VALUES(95,7,'95:7','Atëherë, çfarë të shtyn ty (o njeri), që ta mohosh Ditën e Kiametit?');
INSERT INTO "hasan_efendi" VALUES(95,8,'95:8','A nuk është Allahu Gjykatësi më i drejtë?!');
INSERT INTO "hasan_efendi" VALUES(96,1,'96:1','Lexo me emrin e Zotit tënd, i Cili krijoi (gjithçka),');
INSERT INTO "hasan_efendi" VALUES(96,2,'96:2','e krijoi njeriun nga një droçkë gjaku!');
INSERT INTO "hasan_efendi" VALUES(96,3,'96:3','Lexo! Zoti yt është Bujari më i madh,');
INSERT INTO "hasan_efendi" VALUES(96,4,'96:4','i Cili, me anë të penës ia mësoi,');
INSERT INTO "hasan_efendi" VALUES(96,5,'96:5','ia mësoi njeriut ato që nuk i dinte.');
INSERT INTO "hasan_efendi" VALUES(96,6,'96:6','Por vërtet që njeriu shkel çdo cak,');
INSERT INTO "hasan_efendi" VALUES(96,7,'96:7','kur mendon se është i vetëmjaftueshëm.');
INSERT INTO "hasan_efendi" VALUES(96,8,'96:8','Në të vërtetë, kthimi është te Zoti yt!');
INSERT INTO "hasan_efendi" VALUES(96,9,'96:9','A e ke parë ti (Muhamed) atë që pengon');
INSERT INTO "hasan_efendi" VALUES(96,10,'96:10','robin (Tonë) kur falet? ');
INSERT INTO "hasan_efendi" VALUES(96,11,'96:11','A nuk është ai (robi Ynë) në rrugë të drejtë');
INSERT INTO "hasan_efendi" VALUES(96,12,'96:12','dhe që urdhëron për devotshmëri?!');
INSERT INTO "hasan_efendi" VALUES(96,13,'96:13','Mendo, nëse ai (penguesi) mohon dhe kthen kurrizin,');
INSERT INTO "hasan_efendi" VALUES(96,14,'96:14','a nuk e di ai se Allahu i sheh të gjitha?!');
INSERT INTO "hasan_efendi" VALUES(96,15,'96:15','Jo, jo! Nëse ai nuk tërhiqet nga kjo, Ne do ta kapim për ballukesh (për ta hedhur në zjarrin e Xhehenemit),');
INSERT INTO "hasan_efendi" VALUES(96,16,'96:16','për ballukesh të gënjeshtarit dhe fajtorit.');
INSERT INTO "hasan_efendi" VALUES(96,17,'96:17','Atëherë ai le ta thërrasë shoqërinë e vet, ');
INSERT INTO "hasan_efendi" VALUES(96,18,'96:18','Ne do t’i thërrasim Zebanijet (rojet e Xhehenemit)!');
INSERT INTO "hasan_efendi" VALUES(96,19,'96:19','Kurrsesi mos iu nënshtro atij (o Muhamed), por bëj sexhde dhe afroju Zotit tënd!');
INSERT INTO "hasan_efendi" VALUES(97,1,'97:1','Ne e kemi zbritur (Kuranin) në Natën e Kadrit.');
INSERT INTO "hasan_efendi" VALUES(97,2,'97:2','E kush mund të ta shpjegojë ty se ç’është nata e Kadrit?');
INSERT INTO "hasan_efendi" VALUES(97,3,'97:3','Nata e Kadrit është më e mirë se një mijë muaj.');
INSERT INTO "hasan_efendi" VALUES(97,4,'97:4','Engjëjt dhe Shpirti (Xhebraili), me lejen e Zotit të tyre, zbresin në këtë natë, me të gjitha vendimet. ');
INSERT INTO "hasan_efendi" VALUES(97,5,'97:5','Paqe është ajo deri në lindjen e agimit.');
INSERT INTO "hasan_efendi" VALUES(98,1,'98:1','Ata që nuk besojnë prej ithtarëve të Librit dhe idhujtarëve, nuk do të largoheshin (nga mosbesimi i tyre), derisa t’u vinte Dëshmia e qartë:');
INSERT INTO "hasan_efendi" VALUES(98,2,'98:2','Një i Dërguar nga Allahu, i cili do t’u lexonte faqe të pastra (Kuranin),');
INSERT INTO "hasan_efendi" VALUES(98,3,'98:3','të cilat përmbanin rregulla të drejta.');
INSERT INTO "hasan_efendi" VALUES(98,4,'98:4','Ata, që u ishte dhënë Libri, u përçanë vetëm atëherë, kur u erdhi Dëshmia e qartë.');
INSERT INTO "hasan_efendi" VALUES(98,5,'98:5','E megjithatë, ata qenë urdhëruar vetëm që të adhuronin Allahun me përkushtim të sinqertë, duke qenë në fenë e pastër (të Ibrahimit), si dhe të falnin namazin e të jepnin zekatin. Kjo është feja e drejtë.');
INSERT INTO "hasan_efendi" VALUES(98,6,'98:6','Në të vërtetë, ata që nuk besojnë prej ithtarëve të Librit dhe idhujtarëve, do të hyjnë në zjarrin e Xhehenemit, në të cilin do të mbeten përgjithmonë. Ata janë krijesat më të këqija.');
INSERT INTO "hasan_efendi" VALUES(98,7,'98:7','Pa dyshim, ata që besojnë dhe punojnë vepra të mira - pikërisht ata janë krijesat më të mira.');
INSERT INTO "hasan_efendi" VALUES(98,8,'98:8','Shpërblimi i tyre është te Zoti i tyre: kopshtet e Adnit, nëpër të cilat rrjedhin lumenj e ku do të banojnë përherë. Allahu është i kënaqur me ata, por dhe ata janë të kënaqur me Atë. Kjo është dhurata për ata që e kanë frikë Zotin e vet.');
INSERT INTO "hasan_efendi" VALUES(99,1,'99:1','Kur të dridhet Toka me tronditjen e saj (të fundit),');
INSERT INTO "hasan_efendi" VALUES(99,2,'99:2','kur të nxjerrë barrët nga brendia e saj ');
INSERT INTO "hasan_efendi" VALUES(99,3,'99:3','e njeriu të thotë: “Ç’po i ndodh asaj?!”');
INSERT INTO "hasan_efendi" VALUES(99,4,'99:4','Atë Ditë ajo do të tregojë lajmet e saj, ');
INSERT INTO "hasan_efendi" VALUES(99,5,'99:5','sepse Zoti yt e ka frymëzuar atë.');
INSERT INTO "hasan_efendi" VALUES(99,6,'99:6','Atë Ditë njerëzit do të paraqiten grupe-grupe, që t’u tregohen veprat e tyre:');
INSERT INTO "hasan_efendi" VALUES(99,7,'99:7','kush ka bërë ndonjë të mirë, qoftë sa një thërrmijë, do ta shohë atë,');
INSERT INTO "hasan_efendi" VALUES(99,8,'99:8','e kush ka bërë ndonjë të keqe, qoftë sa një thërrmijë, do ta shohë atë. ');
INSERT INTO "hasan_efendi" VALUES(100,1,'100:1','Betohem për ata (kuaj) që vrapojnë duke dihatur,');
INSERT INTO "hasan_efendi" VALUES(100,2,'100:2','që nxjerrin xixa nga thundrat ');
INSERT INTO "hasan_efendi" VALUES(100,3,'100:3','dhe trokojnë për sulm në mëngjes,');
INSERT INTO "hasan_efendi" VALUES(100,4,'100:4','duke çuar re pluhuri,');
INSERT INTO "hasan_efendi" VALUES(100,5,'100:5','ndërsa çajnë armikun në dysh,');
INSERT INTO "hasan_efendi" VALUES(100,6,'100:6','se njeriu është vërtet mosmirënjohës ndaj Zotit të vet,');
INSERT INTO "hasan_efendi" VALUES(100,7,'100:7','dhe ai, pa dyshim, këtë e dëshmon.');
INSERT INTO "hasan_efendi" VALUES(100,8,'100:8','Vërtet, ai është i babëzitur për pasuri.');
INSERT INTO "hasan_efendi" VALUES(100,9,'100:9','Vallë, a nuk e di ai se kur të nxirren jashtë ata që gjenden në varre ');
INSERT INTO "hasan_efendi" VALUES(100,10,'100:10','dhe, kur të dalë në shesh ajo që është në gjokse,');
INSERT INTO "hasan_efendi" VALUES(100,11,'100:11','Zoti i tyre, me siguri, atë Ditë di çdo gjë për ta?!');
INSERT INTO "hasan_efendi" VALUES(101,1,'101:1','Gjëmimi i tmerrshëm! ');
INSERT INTO "hasan_efendi" VALUES(101,2,'101:2','Ç’është Gjëmimi i tmerrshëm?');
INSERT INTO "hasan_efendi" VALUES(101,3,'101:3','Kush mund të ta shpjegojë ty se çfarë është Gjëmimi i tmerrshëm?');
INSERT INTO "hasan_efendi" VALUES(101,4,'101:4','Atë Ditë njerëzit do të jenë si fluturat e shpërndara,');
INSERT INTO "hasan_efendi" VALUES(101,5,'101:5','kurse malet si leshi i shprishur.');
INSERT INTO "hasan_efendi" VALUES(101,6,'101:6','Sa i përket atij, veprat e (mira të) të cilit do të rëndojnë në peshore,');
INSERT INTO "hasan_efendi" VALUES(101,7,'101:7','ai do të ketë një jetë të kënaqur (në Xhenet).');
INSERT INTO "hasan_efendi" VALUES(101,8,'101:8','E sa i përket atij, veprat e (mira të) të cilit nuk do të rëndojnë në peshore,');
INSERT INTO "hasan_efendi" VALUES(101,9,'101:9','Ai do të ketë si strehim Humnerën (e Xhehenemit). ');
INSERT INTO "hasan_efendi" VALUES(101,10,'101:10','Kush mund të ta shpjegojë ty se ç’është Humnera (e Xhehenemit)?');
INSERT INTO "hasan_efendi" VALUES(101,11,'101:11','Ajo është një Zjarr i vrullshëm.');
INSERT INTO "hasan_efendi" VALUES(102,1,'102:1','Ju ka hutuar rivaliteti për të shtuar ,');
INSERT INTO "hasan_efendi" VALUES(102,2,'102:2','derisa të shkoni në varre.');
INSERT INTO "hasan_efendi" VALUES(102,3,'102:3','Gjithsesi, do ta merrni vesh!');
INSERT INTO "hasan_efendi" VALUES(102,4,'102:4','Madje, së shpejti do ta merrni vesh!');
INSERT INTO "hasan_efendi" VALUES(102,5,'102:5','Kujdes! Ah, sikur ta dinit të vërtetën me siguri!');
INSERT INTO "hasan_efendi" VALUES(102,6,'102:6','Sigurisht që do ta shihni Zjarrin flakërues!');
INSERT INTO "hasan_efendi" VALUES(102,7,'102:7','Pastaj, me të vërtetë, do ta shihni atë me syrin e sigurisë.');
INSERT INTO "hasan_efendi" VALUES(102,8,'102:8','Pastaj, atë Ditë, do të pyeteni për kënaqësitë (e kësaj bote)!');
INSERT INTO "hasan_efendi" VALUES(103,1,'103:1','Për kohën! ');
INSERT INTO "hasan_efendi" VALUES(103,2,'103:2','Me të vërtetë, njeriu është në humbje,');
INSERT INTO "hasan_efendi" VALUES(103,3,'103:3','përveç atyre që besojnë dhe bëjnë vepra të mira, i këshillojnë njëri-tjetrit të vërtetën e i këshillojnë njëri-tjetrit durimin. ');
INSERT INTO "hasan_efendi" VALUES(104,1,'104:1','Mjerë për çdo përqeshës - shpifës,');
INSERT INTO "hasan_efendi" VALUES(104,2,'104:2','që grumbullon pasuri dhe e ruan,');
INSERT INTO "hasan_efendi" VALUES(104,3,'104:3','duke menduar se ajo pasuri do ta bëjë të pavdekshëm!');
INSERT INTO "hasan_efendi" VALUES(104,4,'104:4','Jo, aspak! Në të vërtetë, ai do të hidhet në torturën shkatërruese.');
INSERT INTO "hasan_efendi" VALUES(104,5,'104:5','E kush mund të ta shpjegojë ty se çfarë është tortura shkatërruese?');
INSERT INTO "hasan_efendi" VALUES(104,6,'104:6','Ajo është zjarri i Allahut i ndezur fort,');
INSERT INTO "hasan_efendi" VALUES(104,7,'104:7','i cili depërton deri në zemrën e njeriut.');
INSERT INTO "hasan_efendi" VALUES(104,8,'104:8','Ai do t’i rrethojë ata nga çdo anë,');
INSERT INTO "hasan_efendi" VALUES(104,9,'104:9','në shtylla të gjata flakëruese. ');
INSERT INTO "hasan_efendi" VALUES(105,1,'105:1','Vallë, a nuk ke dëgjuar ti se si bëri Zoti yt me njerëzit e elefantit? ');
INSERT INTO "hasan_efendi" VALUES(105,2,'105:2','A nuk ua prishi atyre kurthin,');
INSERT INTO "hasan_efendi" VALUES(105,3,'105:3','duke dërguar kundër atyre tufa zogjsh,');
INSERT INTO "hasan_efendi" VALUES(105,4,'105:4','të cilët i gjuajtën me gurë prej baltës së pjekur?');
INSERT INTO "hasan_efendi" VALUES(105,5,'105:5','Dhe kështu, i bëri si byk i ngrënë.');
INSERT INTO "hasan_efendi" VALUES(106,1,'106:1','(Shkatërrimin e njerëzve të elefantit Allahu e bëri) për të mbrojtur fisin Kurejsh, ');
INSERT INTO "hasan_efendi" VALUES(106,2,'106:2','për t’i mbrojtur ata në udhëtimin që bëjnë dimër e verë. ');
INSERT INTO "hasan_efendi" VALUES(106,3,'106:3','Prandaj le të adhurojnë Zotin e kësaj shtëpie (Qabes),');
INSERT INTO "hasan_efendi" VALUES(106,4,'106:4','i Cili i ushqen ata në ditë urie dhe i siguron në ditë frike.');
INSERT INTO "hasan_efendi" VALUES(107,1,'107:1','A e ke parë atë që mohon Ditën e Llogarisë?');
INSERT INTO "hasan_efendi" VALUES(107,2,'107:2','Ky është ai që i dëbon ashpër jetimët');
INSERT INTO "hasan_efendi" VALUES(107,3,'107:3','dhe nuk nxit që t’i jepet ushqim të varfrit.');
INSERT INTO "hasan_efendi" VALUES(107,4,'107:4','Mjerë për ata, të cilët kur falen,');
INSERT INTO "hasan_efendi" VALUES(107,5,'107:5','janë të pakujdesshëm për namazet, ');
INSERT INTO "hasan_efendi" VALUES(107,6,'107:6','të cilët duan vetëm që të duken');
INSERT INTO "hasan_efendi" VALUES(107,7,'107:7','dhe nuk u japin ndihmë nevojtarëve.');
INSERT INTO "hasan_efendi" VALUES(108,1,'108:1','Me të vërtetë që Ne të kemi dhënë ty shumë të mira, ');
INSERT INTO "hasan_efendi" VALUES(108,2,'108:2','andaj, falu (vetëm) për Zotin tënd dhe ther kurban! ');
INSERT INTO "hasan_efendi" VALUES(108,3,'108:3','Sigurisht, ai që të urren ty, ai vetë është fatprerë. ');
INSERT INTO "hasan_efendi" VALUES(109,1,'109:1','Thuaj: “O ju jobesimtarë!');
INSERT INTO "hasan_efendi" VALUES(109,2,'109:2','Unë nuk adhuroj çfarë ju adhuroni');
INSERT INTO "hasan_efendi" VALUES(109,3,'109:3','dhe ju nuk jeni adhurues të Atij që unë adhuroj.');
INSERT INTO "hasan_efendi" VALUES(109,4,'109:4','Unë nuk do të jem kurrë adhurues i atyre që ju i adhuroni');
INSERT INTO "hasan_efendi" VALUES(109,5,'109:5','dhe as ju nuk do të jeni adhurues të Atij që unë adhuroj! ');
INSERT INTO "hasan_efendi" VALUES(109,6,'109:6','Ju keni fenë tuaj, ndërsa unë kam fenë time!”');
INSERT INTO "hasan_efendi" VALUES(110,1,'110:1','Kur të vijë ndihma e Allahut dhe çlirimi (i Mekës) ');
INSERT INTO "hasan_efendi" VALUES(110,2,'110:2','dhe t’i shohësh njerëzit duke hyrë në fenë e Allahut grupe-grupe,');
INSERT INTO "hasan_efendi" VALUES(110,3,'110:3','atëherë madhëroje me falënderim Zotin tënd dhe kërko falje nga Ai; se Ai është vërtet Pranues i madh i pendimeve.');
INSERT INTO "hasan_efendi" VALUES(111,1,'111:1','Iu thafshin duart Ebu Lehebit dhe u thaftë në gjithçka!');
INSERT INTO "hasan_efendi" VALUES(111,2,'111:2','Nuk do ta ndihmojnë atë as pasuria dhe as fitimet e tjera. ');
INSERT INTO "hasan_efendi" VALUES(111,3,'111:3','Ai, me siguri, do të hidhet në zjarrin me flakë të tërbuara,');
INSERT INTO "hasan_efendi" VALUES(111,4,'111:4','po ashtu edhe gruaja e tij , që mbart drutë e zjarrit,');
INSERT INTO "hasan_efendi" VALUES(111,5,'111:5','në qafën e saj do të ketë një litar prej fijesh palme.');
INSERT INTO "hasan_efendi" VALUES(112,1,'112:1','Thuaj: “Ai është Allahu, Një dhe i Vetëm!');
INSERT INTO "hasan_efendi" VALUES(112,2,'112:2','Allahu është Absoluti, të Cilit i përgjërohet gjithçka në amshim. ');
INSERT INTO "hasan_efendi" VALUES(112,3,'112:3','Ai as nuk lind, as nuk është i lindur.');
INSERT INTO "hasan_efendi" VALUES(112,4,'112:4','Dhe askush nuk është i barabartë (a i krahasueshëm) me Atë!”');
INSERT INTO "hasan_efendi" VALUES(113,1,'113:1','Thuaj: “Kërkoj mbështetje te Zoti i agimit,');
INSERT INTO "hasan_efendi" VALUES(113,2,'113:2','që të më mbrojë nga sherri i gjithçkaje që Ai ka krijuar');
INSERT INTO "hasan_efendi" VALUES(113,3,'113:3','dhe nga sherri i natës, kur kaplon terri');
INSERT INTO "hasan_efendi" VALUES(113,4,'113:4','dhe nga sherri i falltarëve, që fryjnë në nyje (duke bërë magji) ');
INSERT INTO "hasan_efendi" VALUES(113,5,'113:5','dhe nga sherri i smirëziut, kur vepron me smirë.” ');
INSERT INTO "hasan_efendi" VALUES(114,1,'114:1','Thuaj: “Kërkoj mbështetje te Zoti i njerëzve, ');
INSERT INTO "hasan_efendi" VALUES(114,2,'114:2','Sundimtari i njerëzve,');
INSERT INTO "hasan_efendi" VALUES(114,3,'114:3','i Adhuruari (i vetëm me të drejtë) i njerëzve,');
INSERT INTO "hasan_efendi" VALUES(114,4,'114:4','nga sherri i djallit cytës që fshihet (pasi cyt)');
INSERT INTO "hasan_efendi" VALUES(114,5,'114:5','e që hedh dyshime në gjokset e njerëzve,');
INSERT INTO "hasan_efendi" VALUES(114,6,'114:6','(qoftë ai djall) prej xhindeve apo njerëzve!”');
